"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[39814],{39988:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u5251\u65e0\u75d5",content:(0,t.jsx)(t.Fragment,{children:"\u7b2c \u4e00 \u56de\u3000\u7389\u5170\u53cc\u59dd \u3000 \u3000\u3000\u4e8b\u5982\u6625\u98ce\u4e86\u65e0\u75d5\u3002 \u3000\u3000\u5c71\u8272\u4f9d\u65e7\u7eff\uff0c\u6c5f\u6c34\u4f9d\u65e7\u6d41\u3002 \u3000\u3000\u591a\u5c11\u82f1\u96c4\u6210\u9752\u51a2\uff0c\u591a\u5c11\u843d\u65e5\u5411\u9ec4\u660f\u3002 \u3000\u3000\u6c5f\u6e56\u4ee3\u6709\u624d\u4eba\u51fa\uff0c\u5404\u9886\u98ce\u9a9a\u4e09\u5341\u6625\u3002 \u3000\u3000\u65e7\u4e00\u4ee3\u7684\u6c5f\u6e56\u5f80\u4e8b\uff0c\u5df2\u968f\u6625\u98ce\u800c\u53bb\uff0c\u65b0\u4e00\u4ee3\u7684\u6c5f\u6e56\u624d\u4eba\uff0c\u53c8\u5728\u8301\u58ee\u6210\u957f\u3002 \u3000\u3000\u6eda\u6eda\u4e1c\u6d41\u957f\u6c5f\u6c34\uff0c\u540e\u6d6a\u6c38\u8fdc\u63a8\u524d\u6d6a\u3002 \u3000\u3000 \u3000\u3000\u6625\u5bd2\u6599\u5ced\uff0c\u5fae\u660e\u65f6\u5206\uff0c\u4ecd\u7136\u662f\u5bd2\u6c14\u4fb5\u4eba\u3002 \u3000\u3000\u592a\u6e56\u4e09\u4e07\u516d\u5e72\u9877\uff0c\u4e00\u9762\u98ce\u6d6a\u4e00\u9762\u5e73\u3002 \u3000\u3000\u90a3\u662f\u5f62\u5bb9\u592a\u6e56\u7684\u535a\u5927\uff0c\u540c\u4e00\u5ea7\u6e56\u5185\uff0c\u4e00\u9762\u98ce\u8d77\u6d6a\u6d8c\uff0c\u4e00\u9762\u6c34\u5e73\u5982\u955c\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u6625\u5bd2\u72b9\u6d53\u7684\u65e9\u6668\uff0c\u6c34\u5e73\u5982\u955c\u7684\u6e56\u9762\u4e0a\uff0c\u505c\u7740\u4e00\u8258\u753b\u822b\u3002 \u3000\u3000\u4e00\u4e2a\u767d\u8863\u5982\u96ea\uff0c\u957f\u53d1\u62ab\u5782\u7684\u5c11\u5973\uff0c\u7ad9\u5728\u7532\u677f\u4e0a\uff0c\u671b\u7740\u4e1c\u65b9\u5929\u9645\u51fa\u795e\u3002 \u3000\u3000\u592a\u6e56\u770b\u65e5\u51fa\u3002 \u3000\u3000\u4e00\u7247\u9c7c\u809a\u767d\u8272\u7684\u5929\u9645\uff0c\u7a81\u7136\u6cdb\u8d77\u4e00\u70b9\u91d1\u8292\uff0c\u77ac\u606f\u95f4\uff0c\u91d1\u8292\u6269\u5c55\uff0c\u5316\u6210\u4e86\u4e07\u9053\u91d1\u5149\uff0c\u8000\u7167\u5929\u9645\u3002 \u3000\u3000\u91d1\u5149\u5e7b\u5f71\u5b50\u6c34\u6ce2\u4e4b\u4e2d\uff0c\u66f4\u663e\u51fa\u65e5\u51fa\u7684\u7eee\u4e3d\u666f\u8272\u3002 \u3000\u3000\u671b\u7740\u90a3\u5347\u8d77\u7684\u592a\u9633\uff0c\u767d\u8863\u5c11\u5973\u8138\u4e0a\u6cdb\u8d77\u4e86\u6b22\u6109\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u5927\u59d0\uff0c\u592a\u9633\u51fa\u5c71\u4e86\u3002\u201d\u753b\u822b\u4e2d\u4f20\u51fa\u4e86\u4e00\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4eba\u5462\uff1f\u201d\u767d\u8863\u5c11\u5973\u8e59\u8d77\u4e86\u67f3\u7709\u513f\uff0c\u9053\uff1a\u201c\u4eba\u8fd8\u672a\u89c1\u5230\u3002\u201d \u3000\u3000\u6562\u60c5\uff0c\u5979\u4eec\u5728\u7b49\u4eba\u3002\u4e00\u8258\u68ad\u5f62\u5feb\u821f\uff0c\u79bb\u5f26\u4e4b\u7bad\u822c\uff0c\u75be\u9a70\u800c\u6765\u3002\u8239\u5934\u4e0a\u7ad9\u7740\u4e00\u4e2a\u4eba\u3002\u4e00\u4e2a\u9752\u886b\u4f69\u5251\u7684\u5e74\u8f7b\u4eba\u3002\u64cd\u821f\u7684\u662f\u4e00\u4e2a\u77ed\u9aef\u5982\u621f\u7684\u5927\u6c49\u3002\u4ed6\u4e0d\u4f46\u6709\u7740\u8fc7\u4eba\u7684\u81c2\u529b\uff0c\u800c\u4e14\u4e5f\u6709\u7740\u7eaf\u719f\u7684\u64cd\u821f\u6280\u672f\uff0c\u624d\u80fd\u628a\u90a3\u8258\u5feb\u821f\u64cd\u7eb5\u7684\u75be\u5982\u6d41\u661f\u3002\u5feb\u821f\u63a0\u7740\u753b\u822b\u800c\u8fc7\uff0c\u901f\u5ea6\u4e0d\u51cf\uff0c\u5728\u5e73\u9759\u7684\u6e56\u6c34\u4e0a\uff0c\u5212\u6210\u4e86\u4e00\u8272\u7684\u6c34\u75d5\u3002\u9752\u886b\u4eba\u7a81\u7136\u98de\u8eab\u800c\u8d77\uff0c\u843d\u5728\u753b\u822b\u4e4b\u4e0a\u3002\u4ed6\u8f7b\u529f\u7edd\u4f73\uff0c\u8f7b\u5982\u98d8\u7d6e\uff0c\u843d\u5728\u753b\u822b\u4e0a\uff0c\u753b\u822b\u8fde\u52a8\u4e5f\u672a\u52a8\u4e00\u73b0\u5728\uff0c\u9752\u886b\u4eba\u548c\u90a3\u767d\u8863\u5c11\u5973\uff0c\u6b63\u76f8\u5bf9\u800c\u7acb\u3002\u767d\u8863\u5c11\u5973\u76ee\u5149\u51b7\u5389\uff0c\u671b\u7740\u90a3\u9752\u886b\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u5f88\u5b88\u7ea6\u3002\u201d\u9752\u886b\u4eba\u9053\uff1a\u201c\u59d3\u53f6\u7684\u4ece\u6765\u5c31\u6ca1\u6709\u5931\u8fc7\u7ea6\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u4f60\u5f88\u81ea\u8d1f\u3002\u201d\u9752\u886b\u4eba\u53cc\u76ee\u4e2d\u68f1\u8292\u4e00\u95ea\uff0c\u9053\uff1a\u201c\u53f6\u67d0\u4eba\u5bf9\u81ea\u5df1\u4e00\u652f\u957f\u5251\u3002\u4e00\u5411\u6709\u4fe1\u5fc3\u3002\u201d\u767d\u8863\u5c11\u5973\u51b7\u54fc\u4e00\u58f0\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8c26\u53d7\u76ca\uff0c\u8fd9\u53e5\u8bdd\u4f60\u61c2\u4e0d\u5417\uff1f\u201d\u9752\u886b\u4eba\u9053\uff1a\u201c\u53f6\u67d0\u4eba\u8bfb\u7684\u4e66\u867d\u7136\u4e0d\u7b97\u592a\u591a\uff0c\u4f46\u8fd9\u53e5\u8bdd\u7684\u610f\u601d\uff0c\u8fd8\u662f\u660e\u767d\u3002\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u6211\u5927\u59d0\u6700\u8ba8\u538c\u9a84\u50b2\u7684\u4eba\uff0c\u4f60\u6700\u597d\u5c0f\u5fc3\u4e00\u4e9b\u3002\u201d\u9752\u886b\u4eba\u4e00\u626c\u5251\u7709\uff0c\u4f3c\u662f\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u4ed6\u7ec8\u4e8e\u5fcd\u4e86\u4e0b\u53bb\u3002\u767d\u8863\u5c11\u5973\u5df2\u8f6c\u8fc7\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u8bf7\u5165\u8231\u4e2d\uff0c\u6211\u66ff\u4f60\u5e26\u8def\u3002\u201d\u9752\u886b\u4eba\u884c\u5165\u4e86\u8231\u4e2d\u3002\u753b\u822b\u4e0d\u592a\u5927\uff0c\u8239\u8231\u81ea\u7136\u4e5f\u4e0d\u4f1a\u592a\u5bbd\u9614\u3002\u4f46\u7cbe\u5de7\u7684\u5e03\u7f6e\uff0c\u5374\u4f7f\u4eba\u4e0d\u89c9\u72ed\u5c0f\u3002 \u3000\u3000\u4e00\u5f20\u5c0f\u5de7\u7684\u6728\u51e0\uff0c\u56f4\u7740\u56db\u5f20\u5c0f\u5de7\u7684\u9526\u58a9\u3002 \u3000\u3000\u4e3b\u4f4d\u4e0a\uff0c\u65e9\u5df2\u5750\u4e00\u4e2a\u5168\u8eab\u9ed1\u8863\u7684\u5c11\u5973\uff0c\u548c\u90a3\u767d\u8863\u5c11\u5973\u4e00\u6837\uff0c\u62ab\u7740\u4e00\u80a9\u957f\u53d1\u3002 \u3000\u3000\u4e0d\u77e5\u5979\u662f\u6709\u5fc3\uff0c\u6216\u662f\u65e0\u610f\uff0c\u5fae\u5fae\u5782\u5934\uff0c\u957f\u53d1\u906e\u53bb\u5979\u7684\u534a\u4e2a\u8138\u513f\u3002 \u3000\u3000\u4e0d\u62b1\u7435\u7436\u5374\u906e\u9762\uff0c\u501f\u7528\u4e86\u90a3\u4e00\u5934\u53d1\u3002\u8fd9\u5c31\u53eb\u4eba\u770b\u4e0d\u771f\u5207\uff0c\u9690\u89c1\u534a\u988a\u900f\u80ed\u8102\uff0c\u7ed9\u4eba\u4e00\u79cd\u8ff7\u8499\u7684\u7f8e\u3002 \u3000\u3000\u5979\u7684\u58f0\u97f3\u5374\u5982\u51fa\u8c37\u9ec4\u8425\u4e00\u822c\uff0c\u5a49\u8f6c\u53d1\u51fa\u4e86\u4e00\u7f15\u6e05\u97f3\uff0c\u9053\u3002\u201c\u53f6\u516c\u5b50\u8bf7\u5750\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u7f13\u7f13\u5750\u4e0b\uff0c\u9053\uff1a\u201c\u7389\u5170\u53cc\u59dd\u98de\u7b3a\u76f8\u53ec\uff0c\u53f6\u67d0\u4eba\u5982\u7ea6\u800c\u6765\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u6b63\u662f\u611a\u59ca\u59b9\u5949\u51fd\u9080\u7ea6\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u9053\uff1a\u201c\u54e6\uff01\u59d1\u5a18\u662f\u2026\u2026\u201d \u3000\u3000\u663e\u7136\uff0c\u4ed6\u4eec\u8fc7\u53bb\u5e76\u4e0d\u76f8\u8bc6\u3002\u751a\u81f3\u89c1\u4e86\u9762\uff0c\u4e5f\u8ba4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u9ed1\u8863\u5973\u9053\uff1a\u201c\u6211\u662f\u7389\u7fe0\uff0c\u820d\u59b9\u5170\u767d\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u9053\uff1a\u201c\u5e78\u4f1a\uff0c\u5e78\u4f1a\uff0c\u4e0d\u77e5\u59d1\u5a18\u9080\u7ea6\u53f6\u67d0\uff0c\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5973\u4e0d\u7b54\u95ee\u8bdd\uff0c\u5374\u4e3e\u624b\u4e92\u51fb\u4e00\u638c\u3002 \u3000\u3000\u4e00\u4e2a\u5782\u9aef\u5973\u5a62\uff0c\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u624b\u4e2d\u6258\u7740\u4e00\u4e2a\u94f6\u76d8\u3002 \u3000\u3000\u94f6\u76d8\u4e2d\uff0c\u4e00\u4e2a\u7ec6\u74f7\u8336\u676f\uff0c\u8336\u4e2d\u9999\u8317\uff0c\u6563\u53d1\u51fa\u6251\u9f3b\u7684\u6e05\u9999\u3002 \u3000\u3000\u5973\u5a62\u653e\u4e0b\u4e86\u94f6\u76d8\u3002 \u3000\u3000\u9ed1\u8863\u5973\u9053\uff1a\u201c\u53f6\u5144\uff0c\u8bf7\u7528\u8336\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u4f4e\u5934\u770b\uff0c\u624d\u53d1\u73b0\u90a3\u7ec6\u74f7\u8336\u676f\u4e0b\u9762\uff0c\u538b\u7740\u4e00\u5c01\u4fe1\u3002 \u3000\u3000\u8981\u53d6\u90a3\u5c01\u4fe1\uff0c\u975e\u5f97\u7aef\u8d77\u8336\u676f\u4e0d\u53ef\u3002 \u3000\u3000\u9752\u886b\u4eba\u9053\uff1a\u201c\u59d1\u5a18\u53ec\u53f6\u67d0\u5230\u6b64\uff0c\u4e0d\u4f1a\u662f\u8981\u6211\u6765\u54c1\u8317\u5427\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5973\u9053\uff1b\u201c\u597d\u9152\u4ee4\u4eba\u9189\uff0c\u597d\u8336\u4f7f\u4eba\u96c5\uff0c\u53f6\u5144\u540d\u6ee1\u6c5f\u6e56\uff0c\u7389\u7fe0\u600e\u6562\u4ee5\u4fd7\u4eba\u76f8\u5f85\uff0c\u8bf7\u5c3d\u4e00\u676f\u9999\u8317\uff0c\u804a\u5c3d\u5fc3\u610f\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u9053\uff1b\u201c\u65e0\u529f\u4e0d\u53d7\u7984\u3002\u201d\u9ed1\u8863\u5973\u9053\uff1a\u201c\u53f6\u5144\u5feb\u4eba\u5feb\u8bed\uff0c\u65e2\u7136\u6e05\u8336\u4e0d\u5165\u6cd5\u773c\uff0c\u90a3\u8bf7\u53f6\u5144\u770b\u4fe1\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u9053\uff1a\u201c\u8fd9\u4fe1\u53ef\u662f\u7ed9\u6211\u53f6\u67d0\u4eba\u770b\u7684\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5973\u5b50\u9053\uff1a\u201c\u6b63\u662f\u8981\u53f6\u5144\u8fc7\u76ee\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u7a81\u7136\u4f38\u51fa\u5de6\u624b\uff0c\u6309\u5728\u94f6\u76d8\u4e4b\u4e0a\u3002\u94f6\u76d8\u4e2d\u7684\u8336\u676f\u7f13\u7f13\u5347\u8d77\u3002\u9752\u886b\u4eba\u53f3\u624b\u53d6\u8fc7\u4fe1\u7b3a\u3002 \u3000\u3000\u8336\u676f\u53c8\u843d\u56de\u539f\u5904\u3002 \u3000\u3000\u90a3\u4fe1\u5c01\u4e0a\uff0c\u5199\u4e86\u201c\u673a\u5bc6\u201d\u4e8c\u5b57\u3002 \u3000\u3000\u4fe1\u672a\u5c01\u53e3\uff0c\u9752\u886b\u4eba\u5fae\u5fae\u4e00\u76b1\u7709\u5934\uff0c\u53d6\u51fa\u4fe1\u7b3a\u3002 \u3000\u3000\u53ea\u770b\u4e86\u7b2c\u4e00\u53e5\uff0c\u9752\u886b\u4eba\u7684\u8138\u8272\u5df2\u7ecf\u53d8\u5f97\u5f88\u96be\u770b\u3002\u4f46\u4ed6\u8fd8\u662f\u5fcd\u8010\u7740\u628a\u4fe1\u770b\u5b8c\u3002 \u3000\u3000\u770b\u5b8c\u4e86\u4fe1\uff0c\u9752\u886b\u4eba\u7684\u795e\u8272\u4e5f\u6062\u590d\u4e86\u5e73\u9759\uff0c\u5c45\u7136\u628a\u4fe1\u7b3a\u6298\u53e0\u5f97\u5f88\u597d\uff0c\u53c8\u653e\u56de\u4e86\u539f\u5904\u3002 \u3000\u3000\u9ed1\u8863\u5973\u9053\uff1a\u201c\u53f6\u5144\u770b\u5b8c\u4e86\uff1f\u201d \u3000\u3000\u9752\u886b\u4eba\u9053\uff1a\u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u5728\u4e0b\u8bfb\u4e66\u4e0d\u591a\uff0c\u4f46\u8fd9\u5c01\u4fe1\uff0c\u6211\u8fd8\u662f\u770b\u5f97\u660e\u767d\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u9053\uff1a\u201c\u53f6\u5144\u5c31\u662f\u4ed6\u4eec\u8bf4\u7684\u4eba\u4e86\uff1f\u201d \u3000\u3000\u9752\u886b\u4eba\u9053\uff1a\u201c\u59d1\u5a18\u6ca1\u6709\u627e\u9519\u4eba\uff0c\u5728\u4e0b\u4e5f\u786e\u6709\u4e00\u4e2a\u7ef0\u53f7\u53eb\u505a\u4e03\u5251\u8ffd\u9b42\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u90a3\u662f\u8bf4\u53f6\u5144\u5230\u73b0\u5728\u5df2\u6210\u540d\u6c5f\u6e56\uff0c\u8fd8\u6ca1\u6709\u7528\u8fc7\u7b2c\u516b\u62db\u6740\u4eba\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u53ea\u7528\u8fc7\u4e94\u62db\uff0c\u7b2c\u516d\u3001\u4e03\u4e24\u62db\uff0c\u8fd8\u6ca1\u6709\u673a\u4f1a\u7528\u8fc7\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u9053\uff1a\u201c\u5509\uff01\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u5b9e\u5728\u662f\u4e00\u4e2a\u5927\u6570\u76ee\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u9053\uff1a\u201c\u786e\u4e5f\u503c\u5f97\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u9053\uff1a\u201c\u542c\u53f6\u5144\u7684\u53e3\u6c14\uff0c\u4e0d\u51c6\u5907\u6210\u5168\u6211\u4eec\u59ca\u59b9\u4e86\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u738b\u7fe0\u59d1\u5a18\u8981\u5728\u4e0b\u6210\u5168\u7684\u610f\u601d\uff0c\u5c31\u662f\u5728\u4e0b\u675f\u624b\u5c31\u622e\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4e00\u4e2a\u4eba\u6d3b\u8fc7\u767e\u5c81\uff0c\u4e5f\u662f\u96be\u514d\u4e00\u6b7b\uff0c\u65e9\u6b7b\u4e0e\u665a\u6b7b\u51e0\u5e74\uff0c\u5176\u5b9e\u4e5f\u6ca1\u6709\u4ec0\u4e48\u592a\u5927\u7684\u5206\u522b\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u9053\uff1a\u201c\u7389\u7fe0\u59d1\u5a18\u8bf4\u7684\u5f88\u6709\u9053\u7406\uff0c\u53ea\u53ef\u501f\uff0c\u5728\u4e0b\u8fd8\u60f3\u591a\u6d3b\u51e0\u5e74\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6b7b\u6709\u4e09\u79cd\uff0c\u4e00\u79cd\u662f\u81ea\u7136\u7684\u6b7b\u4e00\u79cd\u662f\u88ab\u4eba\u6740\u6b7b\uff0c\u8fd8\u6709\u4e00\u79cd\u662f\u81ea\u7edd\u800c\u6b7b\uff0c\u53f6\u5144\u6b63\u503c\u76db\u5e74\uff0c\u53ea\u6015\u884c\u96be\u81ea\u7136\u6b7b\u4e86\uff0c\u4f59\u4e0b\u4e24\u79cd\u6b7b\u6cd5\u3002\u4e0d\u77e5\u53f6\u5144\u611f\u89c9\u5230\u54ea\u4e00\u79cd\u597d\uff1f\u201d \u3000\u3000\u9752\u886b\u4eba\u9053\uff1a\u201c\u4e24\u79cd\u90fd\u4e0d\u597d\uff0c\u5982\u662f\u7389\u7fe0\u59d1\u5a18\u4e00\u5b9a\u8981\u5728\u4e0b\u9009\u4e00\u79cd\u6211\u5012\u5b81\u53ef\u662f\u9009\u62e9\u88ab\u4eba\u6740\u6b7b\u7684\u597d\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u9053\uff1a\u201c\u90a3\u771f\u662f\u4e00\u4ef6\u5f88\u9057\u61be\u7684\u4e8b\uff0c\u56e0\u4e3a\uff0c\u6211\u548c\u5408\u59b9\uff0c\u90fd\u4e0d\u592a\u559c\u6b22\u6740\u4eba\uff0c\u53ea\u4e0d\u8fc7\uff0c\u5f62\u52bf\u8feb\u4eba\u65f6\uff0c\u53ea\u597d\u52c9\u5f3a\u52a8\u624b\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u6740\u4eba\u7684\u4eba\uff0c\u5fc5\u987b\u8981\u5192\u4e00\u4e2a\u5f88\u5927\u7684\u5371\u9669\uff0c\u90a3\u5c31\u662f\u4e5f\u6709\u88ab\u4eba\u6740\u7684\u53ef\u80fd\uff01\u201d \u3000\u3000\u9ed1\u8863\u5973\u9053\uff1a\u201c\u53f6\u5144\u7684\u610f\u601d\u662f\u60f3\u6740\u6211\u4eec\u59ca\u59b9\u4e86\uff1f\u201d \u3000\u3000\u9752\u886b\u4eba\u9053\uff1a\u201c\u5200\u5251\u672c\u6765\u65e0\u773c\uff0c\u5982\u662f\u52a8\u8d77\u624b\u6765\uff0c\u5b9e\u5728\u5f88\u96be\u4fdd\u8bc1\u4e00\u4e2a\u4eba\u4e0d\u53d7\u4f24\u5bb3\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u662f\u5426\u77e5\u9053\uff0c\u4f60\u5982\u51b3\u5b9a\u6210\u5168\u6211\u4eec\u59ca\u59b9\u65f6\uff0c\u4f60\u4f1a\u6709\u5f88\u5927\u7684\u6536\u83b7\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u51fa\uff0c\u4ec0\u4e48\u6536\u83b7\u4f1a\u6bd4\u4e00\u4e2a\u4eba\u7684\u751f\u547d\u66f4\u91cd\u8981\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u53f6\u5144\u662f\u5426\u542c\u8bf4\u8fc7\u2018\u5b81\u5728\u82b1\u4e0b\u6b7b\uff0c\u505a\u9b3c\u4e5f\u98ce\u6d41\u2019\u8fd9\u53e5\u8bdd\uff0c\u7389\u5170\u53cc\u59dd\uff0c\u90fd\u5177\u6709\u4eba\u95f4\u6b8a\u8272\u3002\u201d \u3000\u3000\u5979\u58f0\u97f3\u67d4\u7f8e\uff0c\u542c\u8d77\u6765\uff0c\u5a07\u8106\u52a8\u4eba\uff0c\u7eb5\u7136\u662f\u6740\u4eba\u53d6\u547d\u7684\u4e8b\uff0c\u4f46\u5728\u5979\u53e3\u4e2d\u8bf4\u8d77\u6765\uff0c\u4e5f\u662f\u90a3\u4e48\u5a49\u8f6c\u52a8\u542c\u3002 \u3000\u3000\u9752\u886b\u4eba\u671b\u671b\u4e00\u76f4\u5750\u5728\u65c1\u8fb9\u7684\u767d\u8863\u5c11\u5973\uff0c\u9053\uff1a\u201c\u5170\u767d\u59d1\u5a18\u5b9e\u5728\u5f88\u7f8e\u4e3d\u2026\u2026\u201d \u3000\u3000\u9ed1\u8863\u5973\u63a5\u9053\uff1a\u201c\u5979\u53ef\u4ee5\u548c\u4f60\u5bf9\u6708\u8c08\u60c5\uff0c\u966a\u4f60\u559d\u51e0\u676f\u6700\u597d\u7684\u9152\uff0c\u5979\u5f39\u7684\u4e00\u624b\u597d\u7435\u7436\uff0c\u4e5f\u53ef\u4ee5\u4e3a\u4f60\u9ad8\u6b4c\u4e00\u66f2\uff0c\u5979\u7684\u6b4c\u5589\u4e5f\u8fd8\u4e0d\u9519\uff0c\u6e56\u5fc3\u9a76\u821f\uff0c\u5bf9\u6708\u60c5\u8bdd\uff0c\u90a3\u771f\u662f\u4eba\u95f4\u7f8e\u4e8b\uff0c\u53f6\u5144\u4f55\u4e50\u4e0d\u4e3a\u5462\uff1f\u201d\u9752\u886b\u4eba\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u5170\u767d\u59d1\u5a18\u4e4b\u7f8e\uff0c\u8fd8\u65e0\u6cd5\u4f7f\u5728\u4e0b\u9676\u9189\u5230\u4e0d\u8981\u547d\u7684\u7a0b\u5ea6\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u4ed4\u7ec6\u7684\u770b\u770b\u6211\u5462\uff1f\u6211\u548c\u820d\u59b9\u6709\u5f88\u591a\u7684\u4e0d\u540c\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u5b9e\u5728\u77a7\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5ffd\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u51a5\u987d\u4e0d\u7075\u3002\u4e0d\u7528\u548c\u4ed6\u8c08\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u53f6\u516c\u5b50\u662f\u6c5f\u6e56\u4e0a\u6709\u540d\u7684\u5251\u624b\uff0c\u771f\u8981\u52a8\u8d77\u624b\u6765\uff0c\u53ea\u6015\u6211\u4eec\u90fd\u672a\u5fc5\u662f\u4ed6\u7684\u654c\u624b\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u9053\uff1a\u201c\u738b\u7fe0\u59d1\u5a18\u5982\u6b64\u8c26\u865a\uff0c\u4f7f\u5728\u4e0b\u89c9\u7740\u5947\u602a\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u59b9\u8bf4\u7684\u90fd\u662f\u80ba\u8151\u4e4b\u767e\u6211\u4e0d\u559c\u5200\u6765\u5251\u5f80\u7684\u62fc\u6740\uff0c\u90a3\u573a\u9762\u5927\u6b8b\u5fcd\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u770b\u5979\u8bf4\u7684\u5341\u5206\u8ba4\u771f\uff0c\u5b9e\u6709\u7740\u557c\u7b11\u7686\u975e\u4e4b\u611f\uff0c\u8fd9\u4e24\u4e2a\u5973\u98de\u51fd\u76f8\u53ec\uff0c\u628a\u4ed6\u7ea6\u6765\u592a\u6e56\u753b\u822b\u4e4b\u4e0a\uff0c\u76ee\u7684\u53ea\u662f\u8981\u53d6\u4ed6\u7684\u6027\u547d\uff0c\u5206\u660e\u662f\u4e00\u573a\u8840\u8165\u640f\u6740\uff0c\u5979\u5374\u504f\u504f\u53c8\u628a\u5b83\u8bf4\u6210\u4e86\u65e0\u8fb9\u98ce\u6708\u3002 \u3000\u3000\u4f3c\u4e4e\u662f\u7389\u7fe0\u59d1\u5a18\u65e9\u5df2\u770b\u900f\u4e86\u9752\u886b\u4eba\u5fc3\u4e2d\u7684\u9690\u5bc6\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6740\u4eba\uff0c\u4e5f\u662f\u4e00\u79cd\u827a\u672f\uff0c\u8981\u6740\u5f97\u4e0d\u5e26\u8840\u8165\u6c14\uff0c\u8981\u6b7b\u5f97\u8fc5\u901f\uff0c\u5c0f\u59b9\u4e00\u5411\u53cd\u5bf9\u52a8\u5200\u52a8\u67aa\u7684\u786c\u6027\u640f\u6740\uff0c\u5f3a\u5b58\u5f31\u4ea1\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u51b7\u7b11\u4e00\u58f0\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u5728\u4e0b\u5df2\u7ecf\u5728\u6c5f\u6e56\u4e0a\u95ef\u8361\u4e86\u4e0d\u5c11\u65f6\u5019\uff0c\u4e5f\u6709\u4e0d\u5c11\u4eba\u82b1\u94b1\u96c7\u8bf7\u4eb2\u624b\uff0c\u8981\u4e70\u6211\u53f6\u957f\u9752\u4e00\u6761\u547d\uff0c\u4e0d\u8fc7\u3002\u53f6\u67d0\uff0c\u4ece\u5374\u4ece\u672a\u9047\u4e0a\u8fc7\u59d1\u5a18\u8fd9\u6837\u7684\u6740\u4eba\u65b9\u6cd5\uff0c\u7389\u5170\u53cc\u59dd\u672c\u662f\u6c5f\u6e56\u4e0a\u6709\u540d\u7684\u6740\u624b\uff0c\u73b0\u5728\u597d\u50cf\u662f\u5728\u5411\u6211\u53f6\u67d0\u4eba\u6c42\u547d\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u7a81\u7136\u4e00\u62c2\u5782\u5728\u8138\u4e0a\u7684\u79c0\u53d1\uff0c\u76ee\u5149\u6295\u6ce8\u53f6\u957f\u9752\u7684\u8138\u4e0a\uff0c\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u8bba\u53f6\u5144\u5982\u4f55\u60f3\u6cd5\uff0c\u4f46\u5c0f\u59b9\u603b\u89c9\u7740\uff0c\u7528\u5200\u5251\u53bb\u6740\u4eba\uff0c\u5b9e\u5728\u662f\u6700\u4e0d\u827a\u672f\u7684\u65b9\u6cd5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u5728\u4e0b\u4e0d\u60f3\u518d\u8c08\u8fd9\u4ef6\u4e8b\u4e86\uff0c\u5982\u679c\u4f60\u4eec\u7389\u5170\u53cc\u59dd\uff0c\u6709\u4fe1\u5fc3\u80dc\u5f97\u8fc7\u6211\u53f6\u67d0\u4eba\u624b\u4e2d\u4e4b\u5251\uff0c\u90a3\u5c31\u8bf7\u7acb\u523b\u51fa\u624b\uff0c\u5982\u662f\u4f60\u4eec\u6ca1\u6709\u80dc\u6211\u7684\u4fe1\u5fc3\uff0c\u4e0d\u80af\u51fa\u624b\uff0c\u533a\u533a\u5c31\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1b\u201c\u53f6\u5144\u60f3\u8d70\u4e86\u3002\u201d \u3000\u3000\u76f4\u5230\u6b64\u523b\uff0c\u53f6\u957f\u9752\u624d\u7b97\u771f\u6b63\u7684\u770b\u60c5\u695a\u90a3\u9ed1\u8863\u5973\u5b50\u7684\u6a21\u6837\u3002 \u3000\u3000\u5979\u4e0d\u7b97\u592a\u7f8e\uff0c\u751a\u81f3\u6ca1\u6709\u5170\u767d\u7f8e\uff0c\u4f46\u5979\u786e\u548c\u5170\u767d\u4e0d\u540c\u3002 \u3000\u3000\u5979\u5168\u8eab\u90fd\u6563\u53d1\u7740\u4e00\u79cd\u5a9a\u6001\uff0c\u5c24\u5176\u662f\u90a3\u4e00\u5bf9\u5706\u5706\u7684\u5927\u773c\u5374\u4f3c\u6709\u7740\u65e0\u9650\u7684\u60c5\u610f\u3002 \u3000\u3000\u53f6\u957f\u9752\u63a5\u89e6\u5230\u4e86\u90a3\u79cd\u76ee\u5149\uff0c\u7acb\u523b\u6709\u7740\u83ab\u540d\u7684\u5947\u5f02\u611f\u89c9\u3002 \u3000\u3000\u53ea\u60f3\u6251\u4e0a\u524d\u53bb\u3001\u628a\u5979\u63fd\u5165\u6000\u4e2d\u3002 \u3000\u3000\u4f46\u4ed6\u5fcd\u4f4f\u4e86\u3002\u5fcd\u4e0b\u4e86\u90a3\u80a1\u51b2\u52a8\u3002 \u3000\u3000\u53f6\u957f\u9752\u7a76\u7adf\u662f\u4e45\u8d70\u6c5f\u6e56\u7684\u4eba\u3001\u7acb\u523b\u8b66\u89c9\u5230\u90a3\u6837\u8bf1\u60d1\u7684\u76ee\u5149\u5145\u6ee1\u7740\u5438\u529b\u3002 \u3000\u3000\u4ed6\u95ed\u4e0a\u4e86\u773c\u775b\uff0c\u5438\u4e00\u53e3\u6c14\uff0c\u5ffd\u7136\u95f4\u89c9\u7740\u54bd\u5589\u4e4b\u4e0a\u4e00\u51c9\u3002 \u3000\u3000\u7b49\u4ed6\u7741\u773c\u775b\u65f6\uff0c\u4e00\u628a\u950b\u5229\u7684\u77ed\u5200\u5df2\u7136\u903c\u4e0a\u4e86\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u73b0\u5728\uff0c\u53f6\u957f\u9752\u76f8\u4fe1\u4e86\u3002\u53ea\u53ef\u60dc\u76f8\u4fe1\u5f97\u665a\u4e86\u4e00\u4e9b\u3002 \u3000\u3000\u53f6\u957f\u9752\u611f\u89c9\u90a3\u950b\u5229\u7684\u5bd2\u5203\uff0c\u7d27\u8d34\u54bd\u5589\u4e4b\u4e0a\u3002\u53ea\u8981\u5bf9\u65b9\u8f7b\u8f7b\u4e00\u6325\uff0c\u7acb\u523b\u53ef\u4ee5\u5272\u4e86\u4ed6\u9879\u4e0a\u7684\u4eba\u5934\u3002\u81f3\u5c11\uff0c\u53ef\u4ee5\u5272\u65ad\u4e86\u4ed6\u7684\u5589\u7ba1\u3002 \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u53f6\u5144\uff0c\u8fd9\u5c31\u662f\u4f60\u6240\u6307\u7684\u6740\u4eba\u4e86\u6cd5\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\u3002\u201c\u5728\u4e0b\u65e2\u7136\u5df2\u7ecf\u4e2d\u8ba1\u88ab\u64d2\uff0c\u6740\u5250\u6089\u51ed\u5c0a\u4fbf\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u8981\u6740\u4f60\uff0c\u4e5f\u6ca1\u6709\u4eba\u6562\u6740\u4f60\uff0c\u5c0f\u59b9\u53ea\u662f\u60f3\u5411\u4f60\u8bc1\u660e\u4e00\u4e0b\uff0c\u4f60\u7684\u60f3\u6cd5\uff0c\u672a\u5fc5\u5c31\u662f\u5bf9\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u8bba\u4ec0\u4e48\u539f\u56e0\uff0c\u603b\u662f\u88ab\u4e00\u5200\u62b5\u5728\u54bd\u5589\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u7a81\u7136\u51fa\u624b\u52a0\u7535\uff0c\u70b9\u4e86\u53f6\u957f\u9752\u4e24\u5904\u7a74\u9053\uff0c\u6536\u8d77\u4e86\u77ed\u5200\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u73b0\u5728\uff0e\u4f60\u60f3\u600e\u4e48\u6b7b\uff1f\u201d \u3000\u3000\u8fd9\u8258\u753b\u822b\u3002\u505c\u5728\u6e56\u6c34\u4e4b\u4e2d\uff0c\u653e\u773c\u671b\uff0c\u76ee\u529b\u6240\u53ca\u5904\uff0c\u4e0d\u89c1\u4eba\u8e2a\u3001\u8239\u5f71\u3002 \u3000\u3000\u53f6\u957f\u9752\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u60f3\u8bf7\u59d1\u5a18\u65e9\u4e9b\u4e0b\u624b\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u9053\uff1a\u201c\u4f60\u5982\u6b64\u56fa\u6267\uff0c\u6211\u4eec\u4e4b\u95f4\uff0c\u5f88\u96be\u6709\u8f6c\u5708\u4e4b\u4f59\u5730\uff0c\u4f60\u65e2\u7136\u6025\u7740\u8981\u6b7b\uff0c\u5c0f\u59b9\u53ea\u597d\u6210\u5168\u4f60\u7684\u5fc3\u613f\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53cc\u76ee\u4e00\u95ed\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u53ea\u7ba1\u51fa\u624b\uff0c\u6211\u53f6\u957f\u9752\u8ba4\u547d\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u5973\u5b50\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6211\u6015\u770b\u5230\u8840\uff0c\u6740\u4e00\u4e2a\u4eba\uff0c\u6740\u7684\u8840\u6dcb\u6dcb\u7684\uff0c\u5b9e\u5728\u662f\u4e00\u4ef6\u5f88\u4e0d\u827a\u672f\u7684\u4e8b\u60c5\uff0c\u6240\u4ee5\uff0c\u5c0f\u59b9\u60f3\u8bf7\u53f6\u5144\u559d\u4e00\u676f\u6bd2\u9152\u5982\u4f55\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6bd2\u9152\uff1f\u201d \u3000\u3000\u9ed1\u8863\u5973\u5b50\u9053\uff1a\u201c\u4e00\u676f\u5f88\u6bd2\u7684\u9152\uff0c\u559d\u4e0b\u53bb\u7acb\u523b\u53ef\u4ee5\u81f4\u547d\u3002\u201d \u3000\u3000\u9762\u5bf9\u7740\u751f\u6b7b\u5927\u4e8b\uff0c\u53f6\u957f\u9752\u8fd8\u7b11\u5f97\u51fa\u6765\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u8981\u662f\u6b7b\u4ea1\uff0c\u4e0d\u8bba\u600e\u4e48\u6837\u6b7b\u53bb\u90fd\u597d\u3002\u201d \u3000\u3000\u4ed6\u5bf9\u843d\u4e8e\u4e24\u4e2a\u5973\u5b50\u4e4b\u624b\uff0c\u5fc3\u4e2d\u5341\u5206\u96be\u8fc7\uff0c\u53ea\u6c42\u4e00\u6b7b\u89e3\u8131\u3002 \u3000\u3000\u5170\u767d\u59d1\u5a18\uff0c\u65e9\u5df2\u7ecf\u7aef\u6765\u4e86\u4e00\u676f\u9152\u3002\u78a7\u7eff\u8272\u7684\u9152\uff0c\u6709\u4e00\u80a1\u6d53\u70c8\u7684\u6251\u9f3b\u9999\u6c14\u3002 \u3000\u3000\u9ed1\u8863\u5973\u9053\uff1a\u201c\u559d\u4e0b\u53bb\u8fd9\u676f\u9152\uff0c\u4f60\u4f1a\u4e0d\u77e5\u4e0d\u89c9\u7684\u6b7b\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u676f\u9152\u6e05\u9999\u6251\u9f3b\uff0c\u4f3c\u4e4e\u4e0d\u50cf\u6bd2\u9152\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u59b9\u4e00\u5411\u662f\u5fc3\u6000\u6148\u60b2\uff0c\u5c31\u7b97\u662f\u5728\u6740\u4eba\u7684\u65f6\u5019\uff0c\u4e5f\u662f\u6740\u7684\u6587\u6587\u660e\u660e\uff0c\u4f7f\u6b7b\u8005\u5fc3\u4e2d\u5f88\u8212\u773c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u4e00\u70b9\u4e5f\u4e0d\u89c9\u5f97\u8212\u670d\uff0c\u6211\u5e0c\u671b\u7684\u6b7b\u6cd5\uff0c\u662f\u62d4\u5251\u800c\u6597\uff0c\u5982\u679c\u59d1\u5a18\u80fd\u51ed\u6b66\u529f\u6740\u4e86\u6211\uff0c\u5728\u4e0b\u624d\u6b7b\u7684\u5fc3\u773c\u53e3\u670d\u3002\u201d \u3000\u3000\u5170\u767d\u5df2\u628a\u90a3\u676f\u6bd2\u9152\u9001\u5230\u4e86\u53f6\u957f\u9752\u7684\u53e3\u8fb9\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e00\u5f20\u5634\u559d\u4e0b\u4e86\u6ee1\u676f\u78a7\u7eff\u8272\u7684\u9152\u3002 \u3000\u3000\u9152\u5473\u5f88\u82ac\u82b3\uff0c\u4f46\u5374\u7acb\u523b\u4f7f\u53f6\u957f\u9752\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u653e\u51fa\u4fe1\u53f7\uff0c\u53eb\u4ed6\u4eec\u6765\u5e26\u4eba\u3002\u201d \u3000\u3000\u5170\u767d\u653e\u51fa\u4e86\u4fe1\u53f7\u3002 \u3000\u3000\u90a3\u662f\u4e00\u679a\u9ad8\u7a7a\u65d7\u82b1\u706b\u70ae\u3002 \u3000\u3000\u4e00\u9053\u84dd\u70df\uff0c\u76f4\u51b2\u9704\u6c49\uff0c\u5347\u9ad8\u4e86\u4e94\u516d\u4e08\u540e\uff0c\u7a81\u7136\u54cd\u8d77\u4e86\u4e00\u58f0\u7206\u9707\u3002 \u3000\u3000\u54cd\u58f0\u8fc7\u540e\u4e0d\u4e45\uff0c\u4e00\u8258\u9c7c\u821f\u75be\u9a70\u800c\u81f3\u3002 \u3000\u3000\u9c7c\u821f\u7d27\u9760\u7740\u753b\u822b\uff0c\u505c\u4e86\u4e0b\u6765\u3002\u4e00\u4e2a\u8eab\u7740\u7070\u5e03\u88e4\u8902\u7684\u5927\u6c49\uff0c\u7a81\u7136\u8df3\u4e0a\u4e86\u753b\u822b\u3002 \u3000\u3000\u7070\u8863\u5927\u6c49\u56de\u987e\u4e86\u4e00\u773c\uff0c\u4e3e\u6b65\u884c\u5165\u8231\u4e2d\u3002 \u3000\u3000\u7389\u7fe0\u5df2\u7136\u5750\u56de\u539f\u4f4d\uff0c\u957f\u53d1\u5782\u4e0b\uff0c\u906e\u4f4f\u4e86\u5927\u534a\u5f20\u8138\u3002 \u3000\u3000\u5170\u767d\u7ad9\u5728\u8231\u95e8\u5904\uff0c\u5f90\u6765\u7684\u5fae\u98ce\uff0c\u8f7b\u8f7b\u7684\u98d8\u8d77\u5979\u5782\u80a9\u957f\u53d1\u3002 \u3000\u3000\u7389\u7fe0\u59d1\u5a18\u5a07\u8106\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u5468\u603b\u7ba1\uff0c\u770b\u770b\u662f\u662f\u4f60\u4eec\u8981\u7684\u53f6\u957f\u9752\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u4f4e\u4e0b\u5934\u53bb\uff0c\u770b\u7684\u5f88\u4ed4\u7ec6\uff0c\u800c\u4e14\uff0c\u4f38\u51fa\u4e86\u53f3\u624b\uff0c\u5728\u53f6\u9752\u7684\u8138\u4e0a\u6ed1\u52a8\u4e86\u4e24\u4e0b\uff0c\u786e\u5b9a\u4ed6\u6ca1\u6709\u6234\u9762\u5177\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u4ed6\u53f6\u957f\u9752\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5468\u603b\u7ba1\u770b\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u770b\u6e05\u695a\u4e86\uff0c\u59d1\u5a18\u4ea4\u7ed9\u5728\u4e0b\u7684\u662f\u8d27\u771f\u4ef7\u5b9e\u7684\u4e03\u5251\u8feb\u9b42\u3002\u201d \u3000\u3000\u7389\u7fe0\u59d1\u5a18\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e78\u672a\u8fb1\u547d\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u7389\u5170\u53cc\u59dd\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u5728\u4e0b\u597d\u751f\u4f69\u670d\uff0c\u4e0d\u77e5\u5728\u4e0b\u53ef\u5426\u628a\u4eba\u5e26\u8d70\u3002\u201d \u3000\u3000\u7389\u7fe0\u59d1\u5a18\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5468\u603b\u7ba1\uff0c\u5e26\u6765\u94f6\u5b50\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u94f6\u5b50\u4e0d\u662f\u65e9\u5df2\u4ed8\u6e05\u4e86\u5417\uff1f\u201d \u3000\u3000\u7389\u7fe0\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5468\u603b\u7ba1\u7adf\u7136\u4e0d\u6e05\u695a\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u5c31\u5728\u4e0b\u6240\u77e5\uff0c\u54b1\u4eec\u597d\u50cf\u5df2\u4ed8\u59d1\u5a18\u5341\u4e07\u94f6\u7968\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u90a3\u5341\u4e07\u94f6\u5b50\uff0c\u662f\u53f6\u957f\u9752\u7684\u4eba\u5934\uff0c\u4f46\u6211\u73b0\u5728\u7ed9\u4f60\u7684\u662f\u4e00\u4e2a\u6d3b\u4eba\uff0c\u6d3b\u7684\u4e03\u5251\u8ffd\u9b42\u53f6\u957f\u9752\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u53f6\u957f\u9752\u7684\u751f\u6b7b\u5e76\u4e0d\u91cd\u8981\uff0c\u91cd\u8981\u7684\u662f\u6211\u4eec\u8981\u5f97\u5230\u4ed6\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5468\u603b\u7ba1\uff0c\u8fd9\u4e48\u8bf4\uff0c\u54b1\u4eec\u8fd9\u751f\u610f\u5c31\u5f88\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u7b80\u5355\u5f97\u5f88\uff0c\u59d1\u5a18\u5272\u4e0b\u53f6\u957f\u9752\u7684\u4eba\u5934\uff0c\u4ea4\u7ed9\u5468\u67d0\u4eba\u5e26\u56de\u53bb\u5c31\u662f\u3002\u201d \u3000\u3000\u7389\u7fe0\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u64d2\u5230\u4e86\u6d3b\u7684\u53f6\u957f\u9752\uff0c\u53ef\u4ee5\u591a\u5356\u4e24\u4e07\u4e24\u94f6\u5b50\uff0c\u4e3a\u4ec0\u4e48\u8981\u628a\u4ed6\u6740\u6b7b\u5462\uff1f\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u4f46\u5728\u4e0b\u6765\u6b64\u4e4b\u65f6\uff0c\u5e76\u672a\u5e26\u94b1\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u63a5\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u90a3\u4e5f\u5f88\u5bb9\u6613\u529e\uff0c\u4f60\u56de\u53bb\u53d6\u4e86\u94f6\u5b50\u518d\u6765\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u53ef\u5426\u5148\u628a\u4eba\u4ea4\u7ed9\u5728\u4e0b\u5e26\u8d70\uff0c\u4e09\u65e5\u4e4b\u5185\uff0c\u5728\u4e0b\u4e00\u5b9a\u628a\u94f6\u7968\u9001\u5230\uff1f\u201d\u7389\u7fe0\u9053\uff1a\u201c\u5468\u603b\u7ba1\u7684\u610f\u601d\u4e0d\u9519\uff0c\u4e0d\u8fc7\u4e24\u4e07\u94f6\u5b50\uff0c\u4e0d\u662f\u4e2a\u5c0f\u6570\u76ee\uff0c\u6211\u4e0d\u60f3\u5192\u8fd9\u4e2a\u9669\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u5192\u9669\uff1f\u96be\u9053\u59d1\u5a18\u6015\u5728\u4e0b\u8d56\u5e10\uff1f\u201d \u3000\u3000\u7389\u7fe0\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u5012\u4e0d\u6015\uff0c\u6211\u60f3\u4f60\u5468\u603b\u7ba1\u4e5f\u4e0d\u6562\u8d56\u5e10\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u4e16\u4e0a\u5982\u82e5\u6709\u4eba\u6562\u8d56\u7389\u5170\u53cc\u59dd\u7684\u5e10\uff0c\u90a3\u4e2a\u4eba\u4e00\u5b9a\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u62c5\u5fc3\u7684\u662f\u6050\u6015\u4f60\u5468\u603b\u7ba1\u6ca1\u6709\u4fdd\u62a4\u8fd9\u4f4d\u53f6\u957f\u9752\u7684\u80fd\u529b\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u5728\u4e8e\u5e0c\u671b\u4f60\u518d\u8003\u8651\u4e00\u4e0b\u3002\u201d \u3000\u3000\u7389\u7fe0\u59d1\u5a18\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u5468\u603b\u7ba1\u8fd8\u662f\u62ff\u94f6\u5b50\u6765\u518d\u8bf4\u5427\uff01\u201d \u3000\u3000\u5468\u603b\u7ba1\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u6211\u60f3\uff0c\u8fd9\u59d3\u53f6\u7684\u6709\u4e0d\u5c11\u7684\u670b\u53cb\uff0c\u5982\u82e5\u628a\u4ed6\u7559\u5728\u8fd9\u753b\u822b\u4e4b\u4e0a\uff0c\u53ea\u6015\u662f\u4e0d\u592a\u59a5\u5f53\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f60\u5468\u603b\u7ba1\u65e2\u7136\u77e5\u9053\u53f6\u957f\u9752\u6709\u4e0d\u5c11\u7684\u670b\u53cb\uff0c\u81ea\u7136\u4e5f\u8be5\u660e\u767d\uff0c\u4ed6\u90a3\u4e9b\u670b\u53cb\uff0c\u4e00\u5b9a\u4f1a\u66ff\u4ed6\u62a5\u4ec7\uff1f\u201d \u3000\u3000\u5468\u603b\u7ba1\u70b9\u70b9\u5934\u3002 \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u4eec\u9080\u7ea6\u53f6\u957f\u9752\u7684\u4e8b\uff0c\u5e76\u975e\u9690\u79d8\uff0c\u6211\u60f3\u4e00\u5b9a\u6709\u4e0d\u5c11\u4eba\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u53f6\u957f\u9752\u7684\u670b\u53cb\uff0c\u2014\u5b9a\u4f1a\u8ffd\u903c\u6211\u4eec\u8bf4\u51fa\u4ed6\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u7389\u7fe0\uff0c\u884c\u6709\u884c\u89c4\uff0c\u54b1\u4eec\u5df2\u4ed8\u4e86\u5341\u4e07\u4e24\u767d\u82b1\u82b1\u7684\u94f6\u5b50\uff0c\u6211\u4eec\u4e4b\u95f4\u7ea6\u5b9a\uff0c\u5927\u5bb6\u81ea\u7136\u90fd\u5e94\u9075\u5b88\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u662f\u6211\u4eec\u4e0d\u5b88\u7ea6\uff0c\u8fd8\u662f\u4f60\u4eec\u4e0d\u5b88\u7ea6\uff1f\u201d \u3000\u3000\u5468\u603b\u7ba1\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8981\u6b7b\u4eba\uff0c\u5272\u4e0b\u53f6\u957f\u9752\u7684\u4eba\u5934\uff0c\u6211\u5e26\u8d70\u3002\u201d \u3000\u3000\u8bf4\u52a8\u5c31\u52a8\uff0c\u8bdd\u843d\u53e3\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u53e3\u950b\u5229\u7684\u5315\u9996\u3002 \u3000\u3000\u5200\u950b\u5df2\u5207\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e00\u53cc\u624b\u3002\u96ea\u767d\u6ed1\u817b\u7684\u624b\uff0c\u65e0\u58f0\u65e0\u606f\u7684\u4f38\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e94\u6839\u7ea4\u7ea4\u7684\u7389\u6307\uff0c\u6293\u5728\u5468\u603b\u7ba1\u63e1\u529b\u7684\u53f3\u8155\u4e4b\u4e0a\u3002 \u3000\u3000\u5200\u950b\u8ddd\u79bb\u53f6\u957f\u9752\u7684\u9888\u5b50\uff0c\u8fd8\u6709\u4e09\u5bf8\u3002 \u3000\u3000\u5468\u603b\u7ba1\u56de\u8fc7\u5934\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5170\u767d\u59d1\u5a18\uff0c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u6211\u4eec\u6293\u7684\u662f\u6d3b\u4eba\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6740\u4ed6\uff1f\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u6211\u4eec\u4ed8\u4e86\u5341\u4e07\u94f6\u5b50\uff0c\u4e70\u7684\u662f\u53f6\u957f\u9752\u7684\u4eba\u5934\u3002\u201d \u3000\u3000\u5170\u767d\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u7684\u7ea6\u5b9a\u4e0a\u6709\u4e00\u6761\uff0c\u5982\u662f\u4ea4\u6d3b\u4eba\u8981\u591a\u6536\u4e24\u4e07\u94f6\u5b50\uff0c\u4e3a\u4e86\u8fd9\u4e24\u4e07\u94f6\u5b50\uff0c\u6211\u4eec\u8d39\u4e86\u4e0d\u5c11\u7684\u5de5\u592b\uff0c\u5468\u603b\u7ba1\u4e0d\u89c9\u5f97\u8fd9\u6837\u5927\u4fbf\u5b9c\u4e86\uff1f\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u5728\u4e0b\u65e2\u7136\u672a\u5e26\u94f6\u5b50\uff0c\u81ea\u7136\u4e5f\u4e0d\u80fd\u6cbe\u4f60\u59d1\u5a18\u7684\u5149\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u559d\u4e86\u4e00\u676f\u4e94\u65e5\u9189\uff0c\u4e94\u5929\u4e4b\u5185\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u9192\u8fc7\u6765\uff0c\u53bb\u53d6\u94f6\u5b50\u3002\u6211\u4eec\u5728\u8fd9\u91cc\u7b49\u4f60\u4eec\u4e09\u5929\uff0c\u4e09\u5929\u4e4b\u540e\u3002\u6211\u4eec\u5c31\u4e0d\u7b49\u4e86\uff0c\u90a3\u65f6\u5019\uff0c\u6211\u4eec\u53ef\u4ee5\u653e\u4e86\u53f6\u957f\u9752\u2026\u2026\u201d \u3000\u3000\u5468\u603b\u7ba1\u6025\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4eec\u5df2\u6536\u8fc7\u4e86\u5341\u4e07\u94f6\u5b50\uff0c\u600e\u80fd\u5982\u6b64\u4e0d\u8bb2\u4fe1\u7ea6\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5468\u603b\u7ba1\uff0c\u4e0d\u5b88\u7ea6\u5b9a\u7684\u662f\u4f60\uff0c\u4e3a\u4ec0\u4e48\u660e\u660e\u4e00\u4e2a\u5927\u6d3b\u4eba\uff0c\u4f60\u5374\u5f53\u4f5c\u6b7b\u4eba\u6536\uff1f\u201d \u3000\u3000\u5170\u767d\u53f3\u624b\u4e94\u6307\u4e00\u6536\u3001\u5468\u603b\u7ba1\u624b\u4e2d\u7684\u5200\uff0c\u6389\u5728\u8239\u677f\u4e0a\u3002 \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\uff0c\u4f60\u4eec\u4e0d\u80fd\u8fd9\u6837\u5bf9\u5f85\u4e3b\u987e\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1b\u201c\u7389\u5170\u53cc\u59dd\uff0c\u4ece\u6765\u6ca1\u6709\u9047\u4e0a\u4f60\u8fd9\u4e48\u7f57\u55e6\u7684\u4e3b\u987e\u3002\u201d \u3000\u3000\u53f3\u624b\u5ffd\u7136\u52a0\u529b\u4e00\u9001\uff0c\u5468\u603b\u7ba1\u8eab\u5b50\u4e0d\u7531\u81ea\u4e3b\u8f6c\u5230\u8231\u95e8\u53e3\u5904\u3002 \u3000\u3000\u8fd9\u8258\u753b\u822b\u3001\u672c\u6765\u4e0d\u5927\u3002 \u3000\u3000\u5170\u767d\u98de\u8d77\u4e86\u4e00\u811a\uff0c\u8e22\u5728\u5468\u603b\u7ba1\u7684\u81c0\u90e8\u4e4b\u4e0a\u3002 \u3000\u3000\u5e78\u597d\uff0c\u4ed6\u4e58\u6765\u7684\u6e14\u821f\uff0c\u5c31\u5728\u65c1\u8fb9\u3001\u7acb\u523b\u88ab\u6551\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u753b\u822b\u4e2d\u4f20\u51fa\u6765\u7389\u7fe0\u7684\u58f0\u97f3\uff1b\u9053\uff1a\u201c\u5468\u603b\u7ba1\uff0c\u4e09\u5929\uff0c\u7531\u73b0\u5728\u7b97\uff0c\u540e\u5929\u65e5\u843d\u65f6\u5206\uff0c\u4f60\u5982\u679c\u8fd8\u4e0d\u6765\uff0c\u6211\u4eec\u5c31\u4e0d\u7b49\u4e86\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u7684\u540e\u8170\uff0c\u75db\u7684\u5f88\u5389\u5bb3\uff0c\u5170\u767d\u90a3\u4e00\u811a\uff0c\u5b9e\u5728\u8e22\u5f97\u4e0d\u8f7b\u3002 \u3000\u3000\u5468\u603b\u7ba1\u5fcd\u7740\u75db\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\uff0c\u4e0d\u8bba\u5982\u4f55\uff0c\u4e09\u65e5\u4e4b\u7ea6\uff0c\u5728\u4e0b\u4e00\u5b9a\u8d76\u5230\uff0c\u53ea\u662f\u592a\u6e56\u5982\u6b64\u5e7f\u5927\uff0c\u4f60\u4eec\u6cdb\u821f\u592a\u6e56\uff0c\u6211\u4eec\u8981\u5230\u54ea\u91cc\u53bb\u627e\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f60\u77e5\u9053\u82a6\u82c7\u6797\u5427\uff01\u8ddd\u79bb\u6b64\u5904\u4e0d\u8fdc\uff0c\u6211\u4eec\u5728\u82a6\u82c7\u6797\u76f8\u89c1\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u597d\uff0c\u4e00\u8a00\u4e3a\u5b9a\u3002\u201d \u3000\u3000\u6e14\u821f\u6389\u8fc7\u8239\u5934\uff0c\u6025\u9a70\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u6025\u53bb\u7684\u6e14\u821f\uff0c\u5170\u767d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5927\u59d0\uff0c\u54b1\u4eec\u771f\u7684\u8981\u7b49\u4ed6\u5417\uff1f\u201d \u3000\u3000\u7389\u7fe0\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e24\u4e07\u94f6\u5b50\uff0c\u4e0d\u662f\u4e2a\u5c0f\u6570\u5b57\uff0c\u81ea\u7136\u8981\u7b49\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u8fd9\u59d3\u53f6\u7684\u5462\uff1f\u96be\u9053\u54b1\u4eec\u5c31\u628a\u4ed6\u6446\u5728\u8fd9\u753b\u822b\u4e4b\u4e0a\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4ed6\u559d\u4e86\u4e00\u676f\u4e94\u65e5\u9189\uff0c\u4e94\u65e5\u4e4b\u5185\u4e0d\u4f1a\u9192\u8fc7\u6765\uff0c\u5148\u628a\u4ed6\u6346\u8d77\u6765\uff0c\u4e22\u5728\u8231\u4e2d\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u95f4\uff0c\u7389\u7fe0\u95ed\u4e0a\u4e86\u5634\u5df4\uff01\u5170\u767d\u4e5f\u9732\u51fa\u4e86\u5f88\u5403\u60ca\u7684\u795e\u60c5\u3002 \u3000\u3000\u539f\u6765\uff0c\u8eba\u5728\u8231\u4e2d\u7684\u53f6\u957f\u9752\uff0c\u7a81\u7136\u5750\u4e86\u8d77\u6765\uff0c\u800c\u4e14\uff0c\u987a\u624b\u6293\u8fc7\u8eab\u4fa7\u7684\u957f\u5251\u3002 \u3000\u3000\u53f6\u957f\u9752\u8138\u4e0a\u5e26\u7740\u5fae\u7b11\uff0c\u9053\uff1a\u201c\u8d35\u59ca\u59b9\u7684\u4e94\u65e5\u9189\uff0c\u5e76\u4e0d\u592a\u597d\uff0c\u6240\u4ee5\uff0c\u6211\u5f88\u5feb\u7684\u9192\u4e86\u8fc7\u6765\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u559d\u4e86\u4e94\u65e5\u9189\u7684\u4eba\uff0c\u4e0d\u9189\u4e94\u5929\uff0c\u81f3\u5c11\u4e5f\u8981\u4e09\u5929\uff0c\u4f60\u5f88\u5feb\u9192\u8fc7\u6765\uff0c\u90a3\u662f\u56e0\u4e3a\u4f60\u6839\u672c\u6ca1\u6709\u559d\u4e0b\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fae\u4e00\u633a\u8170\uff0c\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u5b9e\u5728\u5f88\u806a\u660e\uff0c\u4f60\u4eec\u90a3\u676f\u4e94\u65e5\u9189\uff0c\u6211\u5df2\u7ecf\u5410\u4e86\u51fa\u6765\uff0c\u800c\u4e14\uff0c\u59d1\u5a18\u7684\u70b9\u7a74\u624b\u6cd5\u4e5f\u4e0d\u592a\u597d\uff0c\u6240\u4ee5\uff0c\u6211\u8fd8\u80fd\u542c\u5230\u4f60\u4eec\u7684\u8c08\u8bdd\uff0c\u5176\u5b9e\uff0c\u4f60\u4eec\u5e94\u8be5\u628a\u6211\u4ea4\u7ed9\u5468\u603b\u7ba1\u7684\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u4e00\u76f4\u5728\u5947\u602a\uff0c\u4e03\u5251\u8feb\u9b42\u600e\u4e48\u4f1a\u8fd9\u6837\u5bb9\u6613\u5bf9\u4ed8\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u4f60\u4eec\u5df2\u7ecf\u6536\u8fc7\u4e86\u522b\u4eba\u5341\u4e07\u94f6\u5b50\uff0c\u90a3\u4e0d\u662f\u4e00\u4e2a\u5c0f\u6570\u76ee\uff0c\u4f55\u5fc5\u53c8\u8d2a\u5fc3\u591a\u8981\u4e24\u4e07\u94f6\u5b50\u5462\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u53f6\u5927\u4fa0\u51c6\u5907\u5bf9\u4ed8\u6211\u4eec\u59ca\u59b9\u4e86\uff1f\u201d \u3000\u3000\u5170\u767d\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4eec\u65e2\u7136\u6562\u505a\uff0c\u5c31\u4e0d\u4f1a\u5bb3\u6015\uff0c\u5927\u4e0d\u4e86\u653e\u624b\u4e00\u62fc\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u9053\uff1a\u201c\u5c0f\u59b9\u603b\u89c9\u5f97\u62d4\u5200\u800c\u6597\uff0c\u8840\u6d41\u4e94\u6b65\uff0c\u6709\u70b9\u5339\u592b\u4e4b\u52c7\uff0c\u4e0d\u662f\u667a\u8005\u6240\u4e3a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u4eec\u52a0\u5bb3\u6211\u53f6\u67d0\u4eba\u7684\u4e8b\uff0c\u6211\u53f6\u67d0\u4eba\u53ef\u4ee5\u4e0d\u8ffd\u7a76\u4e0d\u8fc7\u201d \u3000\u3000\u7389\u7fe0\u63a5\u9053\uff1a\u201c\u8981\u6709\u6761\u4ef6\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6709\u6761\u4ef6\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5e0c\u671b\u90a3\u6761\u4ef6\u4e0d\u4f1a\u592a\u56f0\u96be\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5bf9\u4f60\u4eec\u7389\u5170\u53cc\u59dd\u6765\u8bf4\uff0c\u90a3\u8be5\u662f\u4e00\u4ef6\u8f7b\u800c\u6613\u4e3e\u7684\u4e8b\uff0c\u53ea\u8981\u4f60\u4eec\u544a\u8bc9\u6211\u90a3\u5468\u603b\u7ba1\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u884c\u6709\u884c\u89c4\uff0c\u8fd9\u4e00\u70b9\uff0c\u5b9e\u5728\u5f88\u62b1\u6b49\uff0c\u5c0f\u59b9\u4e0d\u80fd\u6cc4\u9732\u51fa\u96c7\u4e3b\u7684\u673a\u5bc6\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u53ea\u597d\u6c42\u5176\u6b21\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u628a\u6211\u4ea4\u7ed9\u90a3\u5468\u603b\u7ba1\u5e26\u8d70\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5c31\u8fd9\u6837\u5b50\u4ea4\u7ed9\u4ed6\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1b\u201c\u96be\u9053\u8fd8\u8981\u6211\u518d\u559d\u4e00\u676f\u4e94\u65e5\u9189\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e0d\u559d\u4e94\u65e5\u9189\uff0c\u81f3\u5c11\uff0c\u4e5f\u8be5\u628a\u4f60\u7ed1\u8d77\u6765\uff0c\u5468\u603b\u7ba1\u662f\u4e00\u4e2a\u5f88\u7cbe\u660e\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7389\u7fe0\u59d1\u5a18\u5b9e\u5728\u662f\u4e00\u4e2a\u5f88\u6709\u667a\u6167\u7684\u4eba\uff0c\u5728\u4e0b\u662f\u671b\u5c18\u83ab\u53ca\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u5728\u4e0b\u662f\u5f88\u96be\u540c\u610f\u3002\u201d \u3000\u3000\u738b\u7fe0\u59d1\u5a18\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53f6\u5927\u4fa0\uff0c\u8fd9\u5c31\u4f7f\u6211\u4eec\u5f88\u4e3a\u96be\u4e86\uff0c\u603b\u4e0d\u80fd\u8ba9\u6211\u4eec\u628a\u6536\u5230\u7684\u5341\u4e07\u94f6\u5b50\u9000\u56de\u53bb\u5427\uff1f\u201d\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u662f\u59d1\u5a18\u7684\u4e8b\u4e86\uff0c\u59d1\u5a18\u5982\u662f\u4e00\u5b9a\u8981\u628a\u5728\u4e0b\u7ed1\u8d77\u6765\uff0c\u53ea\u6015\u8981\u59d1\u5a18\u62ff\u51fa\u4e00\u70b9\u771f\u5b9e\u7684\u672c\u9886\u6765\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u59d1\u5a18\u9053\uff1a\u201c\u52a8\u624b\u4e00\u6218\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u6211\u60f3\u4e0d\u51fa\u4ec0\u4e48\u66f4\u597d\u7684\u529e\u6cd5\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5927\u59d0\uff0c\u8fd9\u4e2a\u4eba\u6840\u9a9c\u4e0d\u9a6f\uff0c\u4ed6\u8fd8\u8ba4\u4e3a\u54b1\u4eec\u771f\u7684\u6015\u4ed6\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5170\u767d\u59d1\u5a18\u8bf4\u7684\u4e0d\u9519\uff0c\u4e24\u4f4d\u59d1\u5a18\u5982\u662f\u4e0d\u62ff\u4e00\u70b9\u672c\u9886\u51fa\u6765\uff0c\u53ea\u597d\u6709\u52b3\u4e24\u4f4d\u9001\u5728\u4e0b\u4e00\u8d9f\u4e86\u3002\u5728\u4e0b\u603b\u4e0d\u80fd\u957f\u7559\u5728\u4e24\u4f4d\u59d1\u5a18\u8fd9\u753b\u822b\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u7389\u7fe0\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53f6\u5144\u4e00\u5b9a\u8981\u8d70\uff0c\u5c0f\u59b9\u53ea\u597d\u7559\u5ba2\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\u4e00\u4e2a\u8f6c\u8eab\uff0c\u4e00\u6307\u70b9\u5411\u524d\u80f8\u3002 \u3000\u3000\u53f6\u957f\u9752\u6325\u624b\u4e00\u5c01\uff0c\u8e22\u51fa\u4e00\u811a\u3002 \u3000\u3000\u5170\u767d\u8fc5\u5feb\u7684\u79fb\u52a8\u8eab\u5b50\uff0c\u7ad9\u5728\u8231\u95e8\u53e3\u5904\u3002 \u3000\u3000\u7389\u7fe0\u67f3\u8170\u8f7b\u6446\uff0c\u6b3a\u5230\u53f6\u957f\u9752\u7684\u8eab\u524d\uff0c\u73b0\u624b\u8fde\u73af\u62cd\u51fa\uff0c\u4e00\u8fde\u516b\u638c\u3002 \u3000\u3000\u8fd9\u5ea7\u753b\u822b\u8231\u4e2d\u7684\u5730\u65b9\uff0c\u5b9e\u5728\u5f88\u5c0f\uff0c\u53ea\u9002\u5408\u4e00\u4e9b\u5c0f\u5de7\u7684\u5de5\u592b\u3002 \u3000\u3000\u53f6\u957f\u9752\u5de6\u624b\u63d0\u7740\u957f\u5251\uff0c\u53ea\u80fd\u7528\u53f3\u624b\u5c01\u6321\u7389\u7fe0\u7684\u653b\u52bf\u3002 \u3000\u3000\u7389\u7fe0\u516b\u638c\u51cc\u5389\u7684\u653b\u52bf\uff0c\u628a\u53f6\u957f\u9752\u903c\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u9000\u4e24\u6b65\uff0c\u5df2\u5230\u4e86\u8231\u58c1\u4e4b\u524d\u3002 \u3000\u3000\u4f46\u4ed6\u603b\u7b97\u628a\u7389\u7fe0\u7684\u516b\u62db\u4e00\u6c14\u5475\u6210\u7684\u653b\u52bf\u7ed9\u5c01\u5f00\u4e86\u3002 \u3000\u3000\u7389\u7fe0\u53ea\u8981\u518d\u653b\u4e24\u62db\uff0c\u53f6\u957f\u9752\u5c31\u65e0\u53ef\u9000\u4e4b\u5904\uff0c\u53ea\u6709\u786c\u884c\u5c01\u6321\u7389\u7fe0\u59d1\u5a18\u7684\u653b\u52bf\u4e86\u3002 \u3000\u3000\u4f46\u7389\u7fe0\u5374\u505c\u624b\u6765\u653b\uff0c\u53cd\u800c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5927\u4fa0\uff0c\u8fd9\u5730\u65b9\u65b9\u72ed\u5c0f\u4e86\u4e9b\uff0c\u4f60\u867d\u6709\u7edd\u4e16\u5251\u672f\uff0c\u4f46\u5374\u65e0\u6cd5\u65bd\u5c55\uff0c\u5f62\u52bf\u53d7\u4e86\u5f88\u5927\u7684\u9650\u5236\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u8239\u8231\u867d\u7136\u4e0d\u5927\uff0c\u4f46\u5374\u672a\u5fc5\u80fd\u591f\u771f\u7684\u56f0\u5f97\u4f4f\u6211\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u6211\u4e5f\u8bc1\u660e\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u7389\u7fe0\u59d1\u5a18\u9053\uff1a\u201c\u5c0f\u59b9\u5982\u82e5\u653e\u5f00\u624b\uff0c\u4e5f\u53ef\u4ee5\u548c\u53f6\u5927\u4fa0\u4e00\u6218\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u59d1\u5a18\u8ba4\u4e3a\u521a\u624d\u90a3\u51e0\u62db\u5360\u4e86\u4f18\u52bf\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u521a\u624d\u53ea\u6709\u6211\u4e00\u4e2a\u4eba\u51fa\u624b\uff0c\u800c\u820d\u59b9\u7684\u6b66\u529f\uff0c\u51b3\u4e0d\u5728\u6211\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7389\u5170\u53cc\u59dd\u8981\u501a\u591a\u4e3a\u80dc\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u63a5\u9053\uff1a\u201c\u662f\u554a\uff01\u6211\u4eec\u4e0d\u4f46\u8981\u8054\u5408\u51fa\u624b\uff0c\u800c\u4e14\uff0c\u5c31\u5728\u8fd9\u8239\u8231\u4e4b\u4e2d\u548c\u4f60\u52a8\u624b\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u9664\u975e\u4f60\u7684\u6c34\u4e2d\u5de5\u592b\uff0c\u548c\u6211\u4eec\u4e00\u6837\u597d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u4e00\u9707\uff0c\u8138\u4e0a\u95ea\u63a0\u8fc7\u4e00\u62b9\u60ca\u8bb6\u4e4b\u8272\u3002 \u3000\u3000\u5c31\u662f\u90a3\u4e00\u62b9\u60ca\u8bb6\uff0c\u5df2\u88ab\u7389\u7fe0\u770b\u51fa\u4e8e\u7834\u7efd\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u516c\u5b50\u53ef\u662f\u6ca1\u6709\u5b66\u8fc7\u6c34\u4e2d\u5de5\u592b\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c31\u7b97\u5728\u4e0b\u4e0d\u4f1a\u6c34\u4e2d\u5de5\u592b\uff0c\u4e5f\u4e0d\u89c1\u5f97\u5c31\u4f1a\u88ab\u6c34\u6df9\u6b7b\uff0c\u54b1\u4eec\u73b0\u5728\u6b63\u5728\u4e00\u8258\u753b\u822b\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53ef\u662f\u8239\u5728\u6c34\u9762\u4e0a\uff0c\u53ea\u8981\u8fd9\u8258\u8239\u7834\u4e86\uff0c\u6c34\u5c31\u53ef\u4ee5\u8fdb\u5165\u8239\u8231\u4e2d\uff0c\u6709\u4e00\u53e5\u8bdd\uff0c\u6c34\u53ef\u4ee5\u8f7d\u821f\uff0c\u4e5f\u53ef\u4ee5\u8986\u821f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u51c6\u5907\u6c89\u8239\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u8fd9\u8258\u753b\u822b\uff0c\u503c\u4e00\u767e\u4e24\u94f6\u5b50\uff0c\u5982\u679c\u4e0d\u5230\u975e\u6c89\u4e0d\u53ef\u7684\u65f6\u5019\uff0c\u5c0f\u59b9\u4e0d\u4f1a\u820d\u5f97\u6c89\u4e0b\u5b83\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7f13\u7f13\u62bd\u51fa\u957f\u5251\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\uff0c\u8bf7\u4eae\u5175\u5203\uff0c\u5982\u662f\u4f60\u4eec\u4ecd\u8981\u6c89\u8239\uff0c\u6211\u4f1a\u5728\u753b\u822b\u672a\u6c89\u4e4b\u524d\uff0c\u5148\u6740\u4e86\u4e24\u4f4d\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5927\u59d0\uff0c\u7ed9\u4ed6\u4e09\u5206\u989c\u8272\uff0c\u4ed6\u5c31\u5f00\u8d77\u67d3\u623f\u4e86\uff0c\u6211\u770b\uff0c\u8981\u7ed9\u4ed6\u4e00\u70b9\u6559\u8bad\u624d\u6210\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e8c\u59d1\u5a18\u6307\u6559\uff0c\u5728\u4e0b\u7b49\u5019\u3002\u201d \u3000\u3000\u5170\u767d\u7a81\u7136\u4e00\u63a2\u8170\u9645\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u53cc\u77ed\u5200\u3002 \u3000\u3000\u4f46\u662f\uff0c\u90a3\u5e76\u4e0d\u662f\u4e24\u628a\u77ed\u5200\uff0c\u5c16\u7aef\uff0c\u6709\u4e00\u4e2a\u5f62\u5982\u9e26\u53e3\u7684\u5c16\u523a\u3002 \u3000\u3000\u8fd9\u5bf9\u5175\u5203\uff0c\u5947\u602a\u5230\u5bb6\uff0c\u53f6\u957f\u9752\u8fd9\u6837\u957f\u5e74\u95ef\u8361\u6c5f\u6e56\u7684\u4eba\uff0c\u4e5f\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u7684\u602a\u5175\u5203\u3002 \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u5c0f\u5fc3\u4e9b\uff0c\u6211\u53bb\u6c89\u8239\u3002\u201d \u3000\u3000\u4e3e\u6b65\u8f6c\u5165\u5185\u8231\u3002 \u3000\u3000\u53f6\u957f\u9752\u5927\u6025\uff0c\u957f\u5251\u4e00\u63a2\uff0c\u523a\u5411\u7389\u7fe0\u7684\u80cc\u5fc3\u3002 \u3000\u3000\u5170\u767d\u53f3\u624b\u7684\u9e26\u53e3\u5200\u4e00\u62ac\uff0c\u5c01\u5f00\u4e86\u957f\u5251\u3002\u5de6\u624b\u4e00\u5200\uff0c\u523a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u53f7\u79f0\u4e03\u5251\u8ffd\u9b42\uff0c\u4ed6\u7684\u5251\u6cd5\uff0c\u81ea\u7136\u662f\u6709\u72ec\u5230\u4e4b\u5904\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u8fd9\u8239\u8231\u592a\u8fc7\u72ed\u5c0f\uff0c\u957f\u5251\u5904\u5904\u53d7\u5236\uff0c\u771f\u7684\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u5170\u767d\u59d1\u5a18\u624b\u4e2d\u7684\u9e26\u5200\uff0c\u53ea\u6709\u4e00\u5c3a\u4e8c\u5bf8\u5de6\u53f3\uff0c\u518d\u52a0\u4e0a\u5979\u8eab\u8eaf\u5a07\u5c0f\u73b2\u73d1\uff0c\u8fd0\u8f6c\u7075\u6d3b\uff0c\u65bd\u5c55\u5f00\u6765\uff0c\u653b\u52bf\u6781\u4e3a\u51cc\u5389\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u53f6\u957f\u9752\u7a7a\u6709\u4e03\u5251\u8ffd\u9b42\u4e4b\u540d\uff0c\u5374\u662f\u4e00\u70b9\u53d1\u6325\u4e0d\u51fa\u3002 \u3000\u3000\u8239\u8231\u4e2d\u89c1\u4e86\u6c34\uff0c\u7247\u523b\u5de5\u592b\uff0c\u6c34\u5df2\u6d78\u5230\u4e86\u53cc\u811a\u3002 \u3000\u3000\u5170\u767d\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u59ca\uff0c\u59d3\u53f6\u7684\u865a\u6709\u5176\u540d\uff0c\u6211\u5df2\u63a7\u5236\u4e86\u5168\u5c40\uff0c\u518d\u6709\u4e09\u5341\u62db\uff0c\u6211\u53ef\u4ee5\u903c\u4ed6\u5f03\u5251\uff0c\u7528\u4e0d\u7740\u653e\u6c34\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u8fd9\u59d3\u53f6\u7684\u72e1\u733e\u7684\u5f88\uff0c\u4f60\u8981\u5c0f\u5fc3\u4e86\uff0c\u522b\u4e2d\u4e86\u4e5f\u8bf1\u654c\u4e4b\u8ba1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u6765\uff0c\u6211\u53f6\u957f\u9752\u4eca\u65e5\u771f\u8981\u683d\u5728\u8fd9\u4e24\u4e2a\u4e2b\u5934\u624b\u4e2d\u4e86\u3002\u201d \u3000\u3000\u5bf9\u5170\u767d\u6025\u653b\u7684\u53cc\u5200\uff0c\u4ed6\u5e76\u672a\u653e\u5728\u5fc3\u4e0a\uff0c\u4ed6\u76f8\u4fe1\u53ea\u8981\u80fd\u51b2\u51fa\u8231\u95e8\uff0c\u4e0d\u53d7\u8231\u4e2d\u7684\u5f62\u52bf\u9650\u5236\uff0c\u4ee5\u4ed6\u51cc\u5389\u7684\u5251\u62db\uff0c\u5fc5\u53ef\u53cd\u51fb\u5f97\u624b\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u5f88\u6015\u6c34\u3002 \u3000\u3000\u5170\u767d\u7684\u9e26\u53e3\u5200\uff0c\u4e0d\u4f46\u51cc\u5389\u800c\u4e14\u5f88\u51b7\u9177\uff0c\u51fa\u624b\u5c3d\u5411\u53f6\u957f\u9752\u81f4\u547d\u7684\u5730\u65b9\u62db\u547c\uff0c\u5927\u6709\u6740\u6b7b\u53f6\u957f\u9752\u7684\u7528\u5fc3\u3002 \u3000\u3000\u6c34\u6df1\u8fc7\u819d\uff0c\u53f6\u957f\u9752\u7684\u611f\u89c9\u4e2d\u6574\u5ea7\u753b\u822b\uff0c\u4f3c\u5df2\u5411\u6c34\u4e2d\u6c89\u53bb\u3002 \u3000\u3000\u4ed6\u5df2\u611f\u89c9\u8fd0\u8f6c\u7684\u957f\u5251\uff0c\u53d7\u5230\u4e86\u5f71\u54cd\u3002 \u3000\u3000\u4f46\u5170\u767d\u7684\u653b\u52bf\uff0c\u4ecd\u7136\u662f\u90a3\u4e48\u7684\u6fc0\u70c8\uff0c\u800c\u8863\u8896\u5e26\u8d77\u7684\u6c34\u73e0\uff0c\u98de\u6e85\u4e86\u53f6\u957f\u9752\u6ee1\u8eab\u6ee1\u8138\u3002 \u3000\u3000\u7389\u7fe0\u59d1\u5a18\u7a81\u7136\u51fa\u73b0\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u4f4f\u624b\u3002\u201d \u3000\u3000\u5170\u767d\u6536\u4f4f\u4e86\u5200\u52bf\u3002 \u3000\u3000\u7389\u7fe0\u8138\u4e0a\u5e26\u7740\u5a07\u5a9a\u7684\u7b11\u610f\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6536\u8d77\u4f60\u7684\u957f\u5251\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1b\u201c\u5c31\u7b97\u6211\u4e00\u5b9a\u8981\u6b7b\uff0c\u4e5f\u6b7b\u7684\u58ee\u70c8\u4e00\u4e9b\u3002\u201d \u3000\u3000\u8fc7\u819d\u7684\u6e56\u6c34\uff0c\u5df2\u4f7f\u5f97\u53f6\u957f\u9752\u5b8c\u5168\u5931\u53bb\u53d6\u80dc\u7684\u4fe1\u5fc3\u3002 \u3000\u3000\u5bf9\u4e00\u4e2a\u4e0d\u4f1a\u6c34\u7684\u4eba\uff0c\u6c34\u7ed9\u4e88\u4ed6\u7684\u5a01\u80c1\u592a\u5927\u4e86\u3002 \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u60f3\u6740\u4f60\uff0c\u518d\u8bf4\uff0c\u76ee\u524d\u4f60\u5df2\u7ecf\u6ca1\u6709\u6297\u62d2\u7684\u80fd\u529b\uff0c\u8231\u4e2d\u7684\u79ef\u6c34\uff0c\u5982\u662f\u518d\u6df1\u4e00\u4e9b\uff0c\u4f60\u5fc5\u4f1a\u4f24\u5728\u5170\u59b9\u7684\u5200\u4e0b\uff0c\u6211\u77e5\u9053\u4f60\u4e0d\u6015\u6b7b\uff0c\u4f46\u5982\u679c\u88ab\u4eba\u5728\u8138\u4e0a\u5212\u51e0\u5200\uff0c\u603b\u662f\u4e0d\u592a\u597d\u770b\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u4f60\u5df2\u7ecf\u5b8c\u5168\u660e\u767d\u4e8b\u60c5\u7684\u5185\u60c5\uff0c\u4f60\u6b7b\u4e86\uff0c\u6211\u53ea\u4e0d\u8fc7\u662f\u5c11\u6536\u4e86\u4e24\u4e07\u94f6\u5b50\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e24\u4e07\u94f6\u5b50\uff0c\u5bf9\u4f60\u4eec\u7389\u5170\u53cc\u59dd\u6765\u8bf4\uff0c\u4e5f\u662f\u4e00\u79cd\u635f\u5931\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u53ef\u660e\u767d\u7559\u5f97\u9752\u5c71\u5728\u8fd9\u53e5\u8bdd\u5417\uff1f\u4f60\u53ea\u8981\u4e0d\u6b7b\uff0c\u5c31\u53ef\u80fd\u6709\u8131\u9669\u7684\u673a\u4f1a\uff0c\u4e0d\u7b97\u662f\u8f93\u5b9a\u4e86\uff0c\u4f46\u5982\u4f60\u6b7b\u4e86\uff0c\u90a3\u5c31\u6c38\u65e0\u7ffb\u8eab\u7684\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e3a\u4e86\u4e24\u4e07\u94f6\u5b50\uff0c\u59d1\u5a18\u5b9e\u5728\u662f\u8d39\u5c3d\u4e86\u53e3\u820c\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e3b\u8981\u7684\u662f\u6211\u4eec\u59ca\u59b9\uff0c\u4e0d\u559c\u6b22\u770b\u5230\u6d41\u8840\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5bf9\u59d1\u5a18\u8fd9\u4efd\u865a\u4f2a\uff0c\u5b9e\u5728\u662f\u53eb\u4eba\u538c\u6076\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u8bba\u662f\u5426\u4ee4\u4f60\u611f\u5230\u538c\u6076\uff0c\u4f46\u5374\u5f88\u771f\u5b9e\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u4eec\u59ca\u59b9\u4e3a\u7978\u6c5f\u6e56\uff0c\u6211\u53f6\u67d0\u4eba\u6068\u81ea\u5df1\u65e0\u80fd\u4e3a\u6c5f\u6e56\u9664\u5bb3\uff0c\u4f46\u81f3\u5c11\u53ef\u4ee5\u8ba9\u4f60\u4eec\u89c1\u8bc6\u4e00\u4e0b\uff0c\u4ec0\u4e48\u662f\u82f1\u96c4\u4eba\u7269\uff0c\u5927\u4e49\u6240\u5728\uff0c\u6b7b\u800c\u65e0\u61be\u3002\u201d \u3000\u3000\u7a81\u7136\u4e3e\u5251\uff0c\u5411\u8116\u5b50\u4e0a\u62b9\u53bb\u3002 \u3000\u3000\u4ed6\u751f\u6027\u521a\u70c8\uff0c\u5b81\u613f\u81ea\u520e\u4e00\u6b7b\uff0c\u4e5f\u4e0d\u613f\u6d3b\u7740\u53d7\u7f6a\u3002 \u3000\u3000\u5bd2\u5149\u95ea\u52a8\uff0c\u5f53\u7684\u51fb\u5728\u957f\u5251\u4e0a\u3002 \u3000\u3000\u5170\u767d\u624b\u4e2d\u7684\u9e26\u53e3\u5200\uff0c\u98de\u4e86\u8fc7\u53bb\uff0c\u5c16\u5389\u7684\u9e26\u53e3\uff0c\u7adf\u7136\u80fd\u591f\u5f20\u5f00\uff0c\u54ac\u4f4f\u4e86\u53f6\u957f\u9752\u624b\u4e2d\u7684\u957f\u5251\u3002 \u3000\u3000\u73b0\u5728\uff0c\u53f6\u957f\u9752\u5df2\u5931\u53bb\u4e86\u81ea\u7edd\u7684\u80fd\u529b\u3002 \u3000\u3000\u5170\u767d\u624b\u4e2d\u7684\u9e26\u53e3\u5200\uff0c\u5e26\u7740\u7ec6\u5c0f\u7684\u94a2\u7d22\uff0c\u53ea\u89c1\u5979\u53f3\u624b\u6296\u4e86\u4e00\u6296\uff0c\u9e26\u53e3\u5200\uff0c\u91cd\u53c8\u98de\u56de\u4e86\u5170\u767d\u7684\u624b\u4e2d\u3002 \u3000\u3000\u7389\u7fe0\u7b11\u9053\uff1a\u201c\u53f6\u5144\uff0c\u611a\u59ca\u59b9\u5f88\u5e0c\u671b\u53f6\u5144\u5408\u4f5c\uff0c\u4e0d\u8981\u518d\u4f5c\u65e0\u8c13\u7684\u4e89\u6267\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4e5f\u660e\u767d\uff0c\u6b64\u523b\u5df2\u5b8c\u5168\u53d7\u5236\uff0c\u53f9\u606f\u4e00\u58f0\uff0c\u677e\u53bb\u624b\u4e2d\u7684\u957f\u5251\uff0c\u95ed\u4e0a\u53cc\u76ee\u3002 \u3000\u3000\u5170\u767d\u7f13\u6b65\u884c\u4e86\u8fc7\u53bb\uff0c\u843d\u6307\u5982\u96e8\uff0c\u8fde\u70b9\u4e86\u53f6\u957f\u9752\u516b\u5904\u7a74\u9053\u3002 \u3000\u3000\u4ed6\u4f3c\u662f\u7ec3\u8fc7\u79fb\u7a74\u6362\u4f4d\u7684\u529f\u592b\uff0c\u5170\u767d\u5df1\u4e0d\u6562\u5927\u610f\u3002 \u3000\u3000\u53f6\u957f\u9752\u5411\u6c34\u4e2d\u5012\u53bb\uff0c\u5170\u767d\u53ca\u65f6\u6276\u4f4f\u4e86\u4ed6\u3002 \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6276\u4f4f\u4ed6\uff0c\u6211\u653e\u53bb\u8231\u4e2d\u79ef\u6c34\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u8fd9\u8258\u753b\u822b\uff0c\u7adf\u7136\u662f\u6709\u7740\u6d3b\u52a8\u7684\u95f8\u95e8\uff0c\u53ef\u4ee5\u653e\u6c34\uff0c\u4e5f\u53ef\u4ee5\u6392\u6c34\u3002 \u3000\u3000\u8231\u4e2d\u7684\u6c34\uff0c\u5f88\u5feb\u6392\u5b8c\uff0c\u592a\u6e56\u7684\u6c34\u8d28\u6e05\u7eaf\uff0c\u62ed\u53bb\u4e86\u6c34\u75d5\u4e4b\u540e\uff0c\u6c57\u6ca1\u6709\u7559\u4e0b\u4ec0\u4e48\u6c61\u8ff9\u3002 \u3000\u3000\u8fd9\u8258\u5c0f\u5c0f\u7684\u753b\u822b\uff0c\u7adf\u7136\u7cbe\u5bc6\u7279\u5236\uff0c\u53ef\u60dc\uff0c\u53f6\u957f\u9752\u5df2\u771f\u7684\u6655\u4e86\u8fc7\u53bb\uff0c\u65e0\u6cd5\u770b\u5230\u8fd9\u4e9b\u9690\u79d8\u3002 \u3000\u3000\u53e3\u53e3\u53e3 \u3000\u3000\u5170\u767d\u8131\u6389\u4e86\u6c34\u6dcb\u6dcb\u7684\u8863\u670d\uff0c\u800c\u4e14\uff0c\u4e00\u4e0b\u5b50\u8131\u7684\u5f88\u5f7b\u5e95\u3002 \u3000\u3000\u78a7\u6c34\u753b\u822b\uff0c\u7389\u4f53\u63a9\u6620\uff0c\u597d\u4e00\u4e2a\u79c0\u81f4\u7f8e\u4e3d\u7684\u8eab\u4f53\u3002 \u3000\u3000\u7389\u7fe0\u9759\u9759\u7684\u6b23\u8d4f\u7740\uff0c\u5170\u767d\u626d\u52a8\u4e00\u4e0b\u8170\u80a2\uff0c\u8138\u4e0a\u6cdb\u8d77\u4e00\u80a1\u81ea\u601c\u81ea\u60dc\u7684\u795e\u60c5\u3002 \u3000\u3000\u7389\u7fe0\u629b\u8fc7\u6765\u4e00\u4ef6\u8863\u773c\uff0c\u7b11\u9053\uff1a\u201c\u4e2b\u5934\u7a7f\u4e0a\u5427\uff01\u201d \u3000\u3000\u4e00\u4ef6\u767d\u8863\uff0c\u96ea\u4e00\u822c\u7684\u767d\u8863\u3002 \u3000\u3000\u7389\u5170\u53cc\u59dd\uff0c\u5c31\u662f\u8fd9\u6837\u7684\u4eba\uff0c\u653e\u7eb5\u800c\u7f8e\u4e3d\u7684\u5973\u4eba\u3002 \u3000\u3000\u5170\u767d\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u59ca\uff0c\u54b1\u4eec\u8981\u5982\u4f55\u5904\u7f6e\u4ed6\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f60\u8bf4\u8bf4\uff1f\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u54b1\u4eec\u521a\u624d\u5e94\u8be5\u628a\u4ed6\u4ea4\u7ed9\u5468\u603b\u7ba1\u7684\u3002\u201d \u3000\u3000\u7389\u7fe0\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e78\u597d\uff0c\u54b1\u4eec\u6ca1\u6709\u4ea4\u7ed9\u4ed6\uff0c\u53f6\u957f\u9752\u53ea\u6015\u5f88\u96be\u5fd8\u8bb0\u54b1\u4eec\u751f\u64d2\u51fa\u5356\u4ed6\u7684\u4ec7\u6068\uff0c\u4ed6\u5982\u679c\u6740\u4e01\u5468\u603b\u7ba1\uff0c\u5b9a\u7136\u4f1a\u6765\u627e\u54b1\u4eec\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5927\u59ca\uff0c\u54b1\u4eec\u662f\u4e0d\u662f\u771f\u7684\u505a\u5b8c\u8fd9\u4e00\u7968\uff0c\u5c31\u6d17\u624b\u4e0d\u5e72\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u662f\u771f\u7684\uff0c\u4e8c\u59b9\uff0c\u54b1\u4eec\u867d\u7136\u662f\u5e08\u59ca\u59b9\uff0c\u4f46\u60c5\u80dc\u9aa8\u8089\uff0c\u53ef\u662f\u505a\u4e86\u8fd9\u7968\u751f\u610f\uff0c\u54b1\u4eec\u5c31\u8981\u5206\u624b\uff0c\u9690\u540d\u6c5f\u6e56\u91cd\u65b0\u505a\u4eba\uff0c\u7389\u5170\u53cc\u59dd\uff0c\u4e5f\u5c31\u4ece\u6b64\u6d88\u5931\u4e8e\u4eba\u95f4\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5927\u59ca\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u54b1\u4eec\u65e6\u5915\u76f8\u5904\uff0c\u4e00\u65e6\u5206\u624b\uff0c\u8fd9\u4e00\u4efd\u76f8\u601d\u4e4b\u60c5\uff0c\u53ea\u6015\u662f\u5f88\u96be\u71ac\u53d7\uff0c\u5c0f\u59b9\u89c9\u5f97\uff0c\u54b1\u4eec\u629b\u5f03\u4e86\u6c5f\u6e56\u6740\u624b\u751f\u6daf\uff0c\u5374\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u5206\u5f00\u5462\uff1f\u201d \u3000\u3000\u7389\u7fe0\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5170\u59b9\uff0c\u8fd9\u51e0\u5e74\uff0c\u54b1\u4eec\u4e24\u4e2a\u4eba\u505a\u4e86\u4e0d\u5c11\u9707\u52a8\u6c5f\u6e56\u7684\u5927\u4e8b\uff0c\u4e5f\u8d5a\u591f\u4e86\u54b1\u4eec\u4e00\u8f88\u5b50\u4eab\u7528\u4e0d\u5c3d\u7684\u94f6\u5b50\uff0c\u6b64\u540e\u534a\u751f\uff0c\u54b1\u4eec\u8981\u91cd\u65b0\u505a\u4eba\uff0c\u5982\u679c\u54b1\u4eec\u4e0d\u5206\u5f00\uff0c\u5c31\u5f88\u53ef\u80fd\u91cd\u4f5c\u51af\u5987\uff0c\u518d\u51fa\u6c5f\u6e56\uff0c\u800c\u4e14\uff0c\u4e5f\u7559\u4e0b\u4e86\u7ed9\u522b\u4eba\u8ffd\u7d22\u7684\u75d5\u8ff9\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u7389\u5170\u53cc\u59dd\uff0c\u4e00\u4e2a\u4f4f\u5929\u5357\uff0c\u4e00\u4e2a\u4f4f\u5730\u5317\uff0c\u5c31\u7b97\u4ed6\u4eec\u627e\u5230\u4e86\u54b1\u4eec\u5176\u4e2d\u4e00\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u6562\u80af\u5b9a\u54b1\u4eec\u5c31\u662f\u7389\u5170\u53cc\u59dd\u3002\u201d \u3000\u3000\u7389\u7fe0\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5170\u59b9\uff0c\u54b1\u4eec\u51fa\u9053\u51e0\u5e74\u4e86\uff1f\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4e09\u5e74\u53e6\u5341\u4e2a\u6708\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u75db\u6094\uff0c\u4e5f\u60ed\u6127\uff0c\u4f60\u5c0f\u6211\u4e09\u5c81\uff0c\u4e09\u5e74\u524d\uff0c\u8fd8\u662f\u4e2a\u4e0d\u61c2\u4e8b\u7684\u5b69\u5b50\uff0c\u4f46\u6211\u5374\u6ca1\u6709\u5e26\u7740\u4f60\u5b66\u597d\uff0c\u7ade\u8d70\u4e0a\u6c5f\u6e56\u6740\u624b\u4e4b\u9014\uff0c\u5e08\u7236\u4e5d\u6cc9\u6709\u77e5\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u653e\u8fc7\u6211\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5927\u59ca\uff0c\u6211\u4e00\u70b9\u4e5f\u4e0d\u540e\u6094\uff0c\u6211\u4eec\u6ca1\u6709\u4eb2\u624b\u6740\u8fc7\u4e00\u4e2a\u4eba\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u63a5\u9053\uff1a\u201c\u50bb\u4e2b\u5934\uff0c\u6211\u4eec\u628a\u4ed6\u6446\u5e03\u5f97\u5b8c\u5168\u6ca1\u6709\u53cd\u6297\u4e4b\u529b\uff0c\u770b\u7740\u522b\u4eba\u6740\u4e01\u4ed6\uff0c\u8fd9\u548c\u6211\u4eec\u6740\u4e86\u4ed6\u6709\u4f55\u4e0d\u540c\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u59ca\u59ca\uff0c\u6211\u8bb0\u5f97\u54b1\u4eec\u505a\u4e86\u5341\u7968\u751f\u610f\uff0c\u4e5d\u4e2a\u90fd\u662f\u56e0\u4e3a\u559c\u7231\u4e86\u54b1\u4eec\u7684\u7f8e\u8272\u624d\u88ab\u54b1\u4eec\u5236\u4f4f\u7684\uff0c\u90a3\u5982\u4f55\u80fd\u602a\u54b1\u4eec\u5462\uff1f\u201d \u3000\u3000\u7389\u7fe0\u671b\u4e86\u671b\u8eba\u5728\u8231\u4e2d\u7684\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u5462\uff1f\u5e76\u6ca1\u6709\u88ab\u54b1\u4eec\u7684\u7f8e\u8272\u6240\u9189\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u63a5\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u6211\u4e0d\u4fe1\u4ed6\u771f\u80fd\u628a\u6301\u5f97\u4f4f\uff0c\u53ea\u4e0d\u8fc7\uff0c\u54b1\u4eec\u6ca1\u6709\u771f\u6b63\u8bf1\u60d1\u4ed6\u7f62\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5170\u59b9\uff0c\u522b\u8ba4\u4e3a\u5929\u4e0b\u7684\u7537\u4eba\u90fd\u4e00\u6837\uff0c\u8fd9\u4e2a\u53f6\u957f\u9752\u548c\u522b\u4eba\u6709\u4e9b\u4e0d\u540c\u3002\u201d \u3000\u3000\u5170\u767d\u671b\u7740\u6a2a\u5367\u5728\u5730\u4e0a\u7684\u53f6\u957f\u9752\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6211\u77a7\u4e0d\u51fa\u4ed6\u6709\u4ec0\u4e48\u548c\u522b\u4eba\u4e0d\u540c\u7684\u5730\u65b9\uff0c\u5c0f\u59b9\u5c31\u4e0d\u76f8\u4fe1\uff0c\u54b1\u4eec\u5f81\u670d\u4e0d\u4e86\u4ed6\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5170\u59b9\uff0c\u4f60\u5b9e\u5728\u5f88\u7f8e\uff0c\u4e0a\u5929\u4e0d\u4f46\u7ed9\u4e86\u4f60\u4e00\u5f20\u59e3\u597d\u7684\u9762\u5bb9\uff0c\u4e5f\u7ed9\u4e86\u4f60\u4e00\u4e2a\u7f8e\u597d\u7684\u8eab\u6bb5\uff0c\u6240\u4ee5\uff0c\u4f60\u51e0\u4e4e\u65e0\u5f80\u4e0d\u5229\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u53f9\u606f\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6211\u5fc3\u91cc\u8fd8\u6709\u4e00\u4e2a\u5f88\u5947\u602a\u7684\u8d1f\u62c5\uff01\uff0c\u8fd9\u51e0\u5e74\u4e2d\uff0c\u6211\u4eec\u4f24\u5bb3\u7684\u4eba\uff0c\u4e0d\u7ba1\u662f\u6b66\u6797\u4fa0\u58eb\uff0c\u6216\u662f\u7eff\u6797\u5927\u76d7\uff0c\u4f46\u4ed6\u4eec\u90fd\u662f\u60f3\u4fb5\u72af\u6211\uff0c\u4e0d\u7ba1\u54b1\u4eec\u5982\u4f55\u6574\u4ed6\u4eec\uff0c\u6211\u5fc3\u4e2d\u90fd\u6ca1\u6709\u4e00\u70b9\u9057\u61be\uff0c\u4f46\u5bf9\u8fd9\u4e2a\u59d3\u53f6\u7684\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u7b11\u4e00\u7b11\u3002\u9053\uff1a\u201c\u5170\u59b9\uff0c\u505a\u5b8c\u4e86\u8fd9\u4e00\u7968\u751f\u610f\uff0c\u54b1\u4eec\u5c31\u51b3\u5fc3\u9000\u9690\u4e86\uff0c\u4f55\u59a8\u5728\u5fc3\u4e2d\u7559\u4e00\u70b9\u61be\u4e8b\uff1f\u201d \u3000\u3000\u5170\u767d\u5fae\u5fae\u4e00\u7b11\uff0c\u672a\u518d\u591a\u8a00\u3002\uff0c \u3000\u3000\u753b\u822b\uff0c\u53c8\u6062\u590d\u4e86\u539f\u6709\u7684\u5b81\u9759\u3002\u6a79\u58f0\u6b59\u4e43\uff0c\u884c\u9a76\u5728\u5e73\u9759\u7684\u6e56\u6c34 \u3000\u3000\u665a\u971e\u67d3\u7ea2\u4e86\u534a\u8fb9\u5929\uff0c\u53c8\u662f\u5915\u9633\u5411\u9ec4\u660f\u7684\u65f6\u523b\u3002 \u3000\u3000\u6ce2\u6d6a\u88c2\u5f00\u6c34\u4e2d\u5929\uff0c\u4e00\u8258\u5feb\u821f\uff0c\u6012\u9a6c\u5954\u9a76\u822c\u51b2\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5212\u8d77\u4e86\u4e00\u9053\u767d\u6d6a\uff0c\u4e5f\u5212\u7834\u4e86\u6c34\u4e2d\u7684\u5f69\u4e91\u3001\u84dd\u5929\uff1a \u3000\u3000\u5feb\u821f\u9a70\u8fd1\u82a6\u82b1\u98de\u81ea\u7684\u82c7\u6797\uff0c\u7a81\u7136\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4e00\u4e2a\u957f\u886b\u9a6c\u8902\u7684\u4e2d\u5e74\u4eba\uff0c\u51fa\u73b0\u4e8e\u8239\u5934\uff0c\u6b63\u662f\u5468\u603b\u7ba1\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u7389\u59d1\u5a18\uff0c\u5468\u67d0\u4eba\u62ab\u661f\u6234\u6708\uff0c\u98de\u9a6c\u6025\u821f\u800c\u4e5f\u5e26\u6765\u4e86\u7ea6\u5b9a\u4e4b\u7269\uff0c\u8bf7\u59d1\u5a18\u73b0\u8eab\u4e00\u6664\u3002\u201d \u3000\u3000\u82a6\u82c7\u6df1\u5904\uff0c\u4f20\u51fa\u4e86\u6c34\u6ce2\u8361\u6f3e\u4e4b\u58f0\u3002 \u3000\u3000\u4e00\u8258\u5c0f\u5de7\u7684\u753b\u822b\uff0c\u7f13\u7f13\u7531\u82c7\u6797\u4e2d\u8f6c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ecd\u7136\u662f\u90a3\u4e00\u8258\u753b\u822b\uff0c\u4ecd\u7136\u662f\u90a3\u4e24\u4e2a\u62e5\u6709\u753b\u822b\u7684\u5973\u4e3b\u4eba\u3002 \u3000\u3000\u5170\u767d\u767d\u8863\u98d8\u98ce\uff0c\u7ad9\u5728\u8239\u5934\u3002 \u3000\u3000\u753b\u822b\u8f6c\u51fa\u82a6\u82c7\u6797\uff0c\u548c\u5feb\u821f\u4fdd\u6301\u4e86\u4e24\u4e08\u5de6\u53f3\u7684\u8ddd\u79bb\uff0c\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5468\u603b\u7ba1\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u5170\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u51b7\u6f20\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u6765\u7684\u5f88\u5feb\uff0c\u65e9\u4e86\u4e00\u5929\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f46\u5728\u4e0b\u5df2\u5e26\u6765\u4e86\u7ea6\u5b9a\u4e4b\u7269\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u94f6\u7968\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u6210\u8272\u5341\u8db3\u7684\u91d1\u53f6\u5b50\u4e24\u5343\u4e94\u767e\u4e24\uff0c\u542c\u8bf4\u4e24\u4f4d\u59d1\u5a18\u505a\u4e86\u8fd9\u4e00\u7968\u751f\u610f\u4e4b\u540e\uff0c\u5c31\u8981\u6d17\u624b\u5f52\u9690\uff0c\u655d\u5e84\u4e3b\u624d\u51d1\u8db3\u4e86\u91d1\u53f6\u5b50\uff0c\u4e00\u624b\u4ea4\u73b0\u91d1\uff0c\u4e00\u624b\u4ea4\u4eba\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u597d\u50cf\u591a\u4e86\u4e94\u767e\u4e24\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e24\u5e72\u4e24\u9ec4\u91d1\uff0c\u662f\u5e94\u8be5\u4ed8\u7ed9\u4e24\u4f4d\u59d1\u5a18\u916c\u52b3\uff0c\u53e6\u5916\u4e94\u767e\u4e24\u662f\u655d\u5e84\u4e3b\u9001\u7ed9\u4e24\u4f4d\u59d1\u5a18\u7684\u7a0b\u4eea\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u8d35\u5e84\u4e3b\u60f3\u5f97\u5f88\u5468\u5230\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u655d\u5e84\u4e3b\u901a\u60c5\u8fbe\u7406\uff0c\u77e5\u4eba\u5584\u4efb\uff0c\u5bf9\u9053\u4e0a\u670b\u53cb\uff0c\u4e00\u5411\u662f\u987e\u8651\u5468\u8be6\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u4e0d\u77e5\u53f6\u957f\u9752\u7684\u4eba\u662f\u5426\u8fd8\u5728\uff1f\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5f53\u7136\u5728\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u597d\uff0c\u5728\u4e0b\u9001\u4e0a\u9ec4\u91d1\u3002\u201d \u3000\u3000\u8f6c\u8eab\u5165\u8231\uff0c\u6367\u51fa\u4e86\u4e00\u53ea\u6728\u7bb1\u3002 \u3000\u3000\u5feb\u821f\u79fb\u52a8\uff0c\u63a5\u8fd1\u753b\u822b\u3002 \u3000\u3000\u5468\u603b\u7ba1\u767b\u4e0a\u753b\u822b\uff0c\u76f4\u5165\u8231\u4e2d\uff0c\u653e\u4e0b\u4e86\u6728\u7bb1\uff0c\u6253\u5f00\u7bb1\u76d6\u3002 \u3000\u3000\u53ea\u89c1\u91d1\u5149\u8000\u773c\uff0c\u4e00\u6392\u6392\u7684\u91d1\u53f6\u5b50\uff0c\u6392\u5217\u5f97\u5341\u5206\u6574\u9f50\u3002 \u3000\u3000\u5468\u603b\u7ba1\u770b\u5230\u4e86\u53f6\u957f\u9752\uff0c\u9759\u9759\u7684\u5750\u5728\u4e00\u5f20\u9526\u58a9\u4e0a\uff0c\u80cc\u9760\u8231\u58c1\uff0c\u5374\u4e5f\u4e0d\u52a8\u4e00\u4e0b\u3002 \u3000\u3000\u4e00\u773c\u95f4\uff0c\u5c31\u53ef\u4ee5\u770b\u51fa\u6765\uff0c\u90a3\u662f\u88ab\u70b9\u4e86\u7a74\u9053\u3002 \u3000\u3000\u7389\u7fe0\u4e00\u76f4\u672a\u51fa\u9762\uff0c\u5170\u767d\u5374\u6307\u6307\u53f6\u957f\u9752\uff0c\u9053\uff1a\u201c\u4eba\u5728\u90a3\u91cc\uff0c\u4f60\u5e26\u8d70\u5427\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u80cc\u8d77\u4e86\u53f6\u957f\u9752\uff0c\u884c\u51fa\u8231\u95e8\uff0c\u76f4\u767b\u5feb\u821f\u3002 \u3000\u3000\u5170\u767d\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5927\u59ca\uff0c\u6211\u770b\u53f6\u957f\u9752\u662f\u6b7b\u5b9a\u4e86\uff0c\u201d \u3000\u3000\u7389\u7fe0\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5927\u59d0\uff0c\u54b1\u4eec\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u753b\u822b\u9760\u5cb8\u4e4b\u540e\uff0c\u54b1\u4eec\u653e\u628a\u706b\uff0c\u628a\u8239\u70e7\u5382\uff0c\u7136\u540e\u5206\u4e86\u94f6\u5b50\uff0c\u4f60\u5c31\u56de\u8001\u5bb6\u53bb\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5927\u59d0\u5462\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u56fa\u5b9a\u7684\u5730\u65b9\uff0c\u4e0d\u8fc7\uff0c\u6211\u4f1a\u6162\u6162\u7684\u627e\u4e00\u4e2a\u7406\u60f3\u7684\u5730\u65b9\u4f4f\u4e0b\u6765\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u63a5\u9053\uff1a\u201c\u5927\u59d0\uff0c\u96be\u9053\u6211\u4eec\u771f\u7684\u4e0d\u80fd\u518d\u4f4f\u5728\u4e00\u8d77\u5417\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5170\u59b9\uff0c\u81f3\u5c11\u54b1\u4eec\u8981\u5206\u5f00\u4e2a\u4e24\u4e09\u5e74\uff0c\u7389\u5170\u53cc\u59dd\u5982\u662f\u4e0d\u62c6\u4f19\uff0c\u6c5f\u6e56\u7684\u4eba\uff0c\u4e0d\u4f1a\u653e\u8fc7\u54b1\u4eec\uff1a\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u54b1\u4eec\u5206\u5f00\u4e86\uff0c\u96be\u9053\u4ed6\u4eec\u5c31\u653e\u5fc3\u4e86\u5417\uff1f\u518d\u8bf4\uff0c\u5206\u5f00\u4e4b\u540e\uff0c\u5b9e\u529b\u4e5f\u5927\u4e3a\u51cf\u5f31\uff0c\u4e07\u2014\u518d\u9047\u4e0a\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5e94\u4ed8\u8d77\u6765\uff0c\u5b9e\u529b\u4e0a\uff0c\u4e5f\u8981\u5927\u6253\u6298\u6263\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5170\u59b9\uff0c\u54b1\u4eec\u4e24\u4e2a\u4eba\u8d70\u5728\u4e00\u8d77\uff0c\u5c31\u4f1a\u4f7f\u4eba\u60f3\u8d77\u7389\u5170\u53cc\u59dd\uff0c\u56e0\u4e3a\uff0c\u7389\u5170\u53cc\u59dd\uff0c\u7a76\u7adf\u662f\u4e24\u4e2a\u4eba\uff0c\u54b1\u4eec\u4e00\u8d70\u5355\uff0c\u5c31\u7b97\u662f\u88ab\u4eba\u770b\u5230\u4e86\uff0c\u4e5f\u4e0d\u4f1a\u8054\u60f3\u5230\u7389\u5170\u53cc\u59dd\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u81ea\u7136\uff0c\u6211\u8fd8\u6709\u4e00\u70b9\u522b\u7684\u5e03\u7f6e\uff0c\u4f7f\u4ed6\u4eec\u89c9\u5f97\u7389\u5170\u53cc\u59dd\uff0c\u5df2\u7ecf\u9999\u6d88\u7389\u6b92\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5927\u59d0\u65e9\u6709\u8ba1\u5212\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u7684\uff01\u6240\u4ee5\uff0c\u54b1\u4eec\u5fc5\u987b\u8981\u5206\u5f00\u4e09\u5e74\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u4e09\u5e74\u5462\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u7167\u4e00\u822c\u6c5f\u6e56\u4e0a\u7684\u4e60\u6027\uff0c\u7389\u5170\u53cc\u59dd\u51fa\u4e8b\u4e4b\u540e\uff0c\u5fc5\u7136\u4f1a\u662f\u4e00\u4ef6\u8f70\u52a8\u6c5f\u6e56\u7684\u5927\u4e8b\uff0c\u4e00\u4e24\u5e74\u5185\uff0e\u4e00\u5b9a\u4f1a\u6709\u591a\u4e8b\u7684\u4eba\u60f3\u6cd5\u5b50\u627e\u5bfb\u6211\u4eec\u7684\u8e2a\u8ff9\uff0c\u4e09\u5e74\u4e4b\u540e\uff0c\u8fd9\u4ef6\u4e8b\u5c31\u4f1a\u6de1\u4e86\u4e0b\u6765\uff0c\u90a3\u65f6\u5019\uff0c\u6211\u4f1a\u53bb\u627e\u4f60\u3002\u201d \u3000\u3000\u53e3\u53e3\u53e3 \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u592a\u6e56\u6c34\u9762\u4e0a\uff0c\u6709\u4e00\u8258\u70e7\u6b8b\u7684\u753b\u822b\u548c\u4e24\u5177\u5c38\u4f53\uff0c\u4ece\u8863\u670d\u548c\u8eab\u6750\u4e0a\u770b\u53bb\uff0c\u662f\u540d\u52a8\u6c5f\u6e56\u7684\u795e\u79d8\u5973\u6740\u624b\u7389\u5170\u53cc\u59dd\u3002 \u3000\u3000\u53ea\u53ef\u60dc\uff0c\u4e24\u4e2a\u4eba\u6b7b\u7684\u5f88\u51c4\u60e8\uff0c\u9762\u76ee\u5df2\u7ecf\u65e0\u6cd5\u8fa8\u8ba4\u4e86\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u7389\u5170\u53cc\u59dd\u88ab\u6740\u7684\u4e8b\uff0c\u7acb\u523b\u4f20\u904d\u5b50\u6c5f\u6e56\u3002 \u3000\u3000\u90a3\u8258\u70e7\u6b8b\u7684\u753b\u822b\u4e0a\uff0c\u7559\u4e0b\u4e86\u7389\u5170\u53cc\u59dd\u7684\u7968\u5e1c\u3002 \u3000\u3000\u53e3\u53e3\u53e3 \u3000\u3000\u53f6\u957f\u9752\u88ab\u5468\u603b\u7ba1\u5e26\u4e0a\u5feb\u821f\uff0c\u8231\u4e2d\uff0c\u65e9\u5df2\u5750\u4e86\u4e00\u4f4d\u56db\u5341\u5de6\u53f3\u7684\u4e2d\u5e74\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u751f\u7684\u5f88\u7626\uff0c\u5982\u82e5\u62c6\u4e0b\u4e86\u8eab\u4e0a\u7684\u9aa8\u5934\uff0c\u53ea\u6015\u5265\u4e0d\u51fa\u4e94\u65a4\u51c0\u8089\u3002 \u3000\u3000\u4f46\u5468\u603b\u7ba1\u5bf9\u4ed6\u5374\u5341\u5206\u7684\u656c\u91cd\uff0c\u5148\u653e\u4e0b\u4e01\u53f6\u957f\u9752\uff0c\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u590f\u8001\u2026\u2026\u201d \u3000\u3000\u7626\u524a\u7684\u4e2d\u5e74\u4eba\uff0c\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53f6\u957f\u9752\u8fd8\u6d3b\u7740\u5417\uff1f\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u6d3b\u7740\uff0c\u53ea\u662f\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u53ef\u80fd\u8fd8\u52a0\u4e86\u4e24\u4e2a\u4e2b\u5934\u7684\u4e00\u676f\u4e94\u65e5\u9189\u3002\u201d \u3000\u3000\u7626\u524a\u7684\u4e2d\u5e74\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e24\u4e2a\u4e2b\u5934\uff0c\u662f\u5426\u76f8\u4fe1\u6211\u4eec\u4f1a\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u5468\u603b\u7ba1\u7b11\u9053\uff1a\u201c\u5979\u4eec\u4e00\u518d\u963b\u6b62\u6211\u3002\u201d \u3000\u3000\u7626\u524a\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u628a\u4ed6\u5e26\u5165\u5e95\u8231\uff0c\u518d\u52a0\u4e00\u9053\u6d78\u8fc7\u6cb9\u7684\u725b\u7b4b\u7ed9\u6346\u8d77\u6765\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u590f\u8001\uff0c\u8fd9\u4e2a\u4eba\u5f88\u6025\u8e81\uff0c\u4e07\u4e00\u4ed6\u9192\u8fc7\u6765\uff0c\u53d1\u89c9\u81ea\u5df1\u65e0\u9003\u8131\u6cd5\u65f6\uff0c\u5f88\u53ef\u80fd\u4f1a\u81ea\u7edd\u800c\u6b7b\u3002\u201d \u3000\u3000\u7626\u524a\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u50cf\u53f6\u957f\u9752\u8fd9\u6837\u7684\u4eba\uff0c\u4e0d\u4f1a\u6015\u6b7b\uff0c\u4f46\u4ed6\u5374\u6709\u5f88\u91cd\u7684\u597d\u5947\u5fc3\uff0c\u4efb\u4f55\u4e8b\uff0c\u90fd\u5e0c\u671b\u5f04\u4e2a\u660e\u767d\u51fa\u6765\u3002\u5c31\u7b97\u4ed6\u6e05\u9192\u8fc7\u6765\uff0c\u4f46\u5fc3\u4e2d\u90a3\u4e00\u80a1\u5f3a\u70c8\u7684\u597d\u5947\uff0c\u4e00\u5b9a\u4f1a\u60f3\u6cd5\u5b50\u4fdd\u4e0b\u6027\u547d\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u590f\u8001\u9ad8\u89c1\u3002\u201d \u3000\u3000\u7626\u524a\u4eba\u9053\uff1a\u201c\u7528\u725b\u7b4b\u6346\u597d\u4e4b\u540e\uff0c\u5c31\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\uff0c\u8ba9\u4ed6\u65e9\u4e9b\u6e05\u9192\u8fc7\u6765\u3002\u201d \u3000\u3000\u5468\u603b\u7ba1\u9053\uff1a\u201c\u5728\u4e0b\u8fd9\u5c31\u53bb\u529e\u3002\u201d \u3000\u3000\u7626\u524a\u4eba\u9053\uff1a\u201c\u54e6\uff01\u8fd8\u8981\u66ff\u4ed6\u52a0\u4e0a\u4e00\u526f\u7279\u5236\u7684\u624b\u94d0\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u771f\u7684\u9192\u8fc7\u6765\u4e86\uff0c\u4f46\u4ed6\u53d1\u89c9\uff0c\u81ea\u5df1\u4e0d\u4f46\u624b\u4e0a\u6709\u4e00\u526f\u575a\u7262\u7684\u94d0\uff0c\u800c\u4e14\u5168\u8eab\u90fd\u88ab\u575a\u7262\u7684\u725b\u7b4b\u6346\u7740\u3002 \u3000\u3000\u4ed6\u6697\u4e2d\u8fd0\u6c14\u8bd5\u4e86\u4e00\u4e0b\uff0c\u5e78\u597d\uff0c\u4ed6\u7684\u6b66\u529f\u8fd8\u5728\u3002 \u3000\u3000\u8fd0\u6c14\u63a2\u52a8\u4e86\u4e00\u4e0b\uff0c\u53d1\u89c9\u6346\u7ed1\u7684\u725b\u7b4b\u5341\u5206\u575a\u7262\uff0c\u624b\u4e2d\u7684\u624b\u94d0\u4e5f\u662f\u4e0d\u6613\u6323\u8131\u7684\u767e\u70bc\u7cbe\u94a2\u7814\u5236\u3002 \u3000\u3000\u6c5f\u6e56\u4e0a\u7684\u4e30\u5bcc\u9605\u5386\uff0c\u4f7f\u4ed6\u7acb\u523b\u505c\u606f\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u660e\u767d\uff0c\u5982\u82e5\u6ca1\u6709\u5916\u529b\u7684\u652f\u63f4\uff0c\u81ea\u5df1\u5f88\u96be\u8131\u56f0\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u4e2a\u5f88\u5e7d\u6697\u7684\u5730\u65b9\uff0c\u9ed1\u5f97\u4e0d\u89c1\u4e94\u6307\u3002\u4f46\u53f6\u957f\u9752\u4ecd\u7136\u5f88\u5feb\u7684\u5206\u8fa8\u51fa\uff0c\u4ed6\u7684\u5904\u5883\uff0c\u662f\u5728\u4e00\u8258\u8239\u7684\u5e95\u8231\u4e2d\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u4e00\u9053\u706f\u5149\u5c04\u8fc7\u6765\u3002 \u3000\u3000\u90a3\u662f\u4e00\u76cf\u5b54\u660e\u706f\uff0c\u7531\u4e00\u9762\u8231\u677f\u7684\u6d1e\u53e3\u4e2d\u900f\u8fc7\u6765\u3002 \u3000\u3000\u5ba4\u4e2d\u660e\u4eae\u4e86\uff0c\u4f46\u5bf9\u53f6\u957f\u9752\u800c\u8a00\uff0c\u5374\u662f\u6beb\u65e0\u5e2e\u52a9\u3002 \u3000\u3000\u706f\u5149\uff0c\u903c\u7167\u5728\u4ed6\u7684\u53cc\u76ee\u4e0a\uff0c\u4ed6\u4ecd\u7136\u65e0\u6cd5\u770b\u5230\u522b\u7684\u666f\u7269\u3002 \u3000\u3000\u4e00\u4e2a\u6210\u540d\u7684\u5251\u5ba2\uff0c\u5fc5\u987b\u7ecf\u8fc7\u5f88\u591a\u6b21\u751f\u6b7b\u7684\u640f\u6597\uff0c\u8fd9\u5c31\u7ec3\u6210\u4e86\u4e00\u79cd\u9047\u4e8b\u4e0d\u614c\u4e71\u7684\u9547\u9759\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u53ea\u662f\u9759\u9759\u7684\u5750\u7740\uff0c\u6ca1\u6709\u60ca\u614c\uff0c\u4e5f\u6ca1\u6709\u591a\u95ee\u3002 \u3000\u3000\u4e00\u58f0\u51b7\u7b11\uff0c\u4f20\u4e86\u8fc7\u6765\uff1a\u201c\u53f6\u957f\u9752\uff0c\u4f60\u5f88\u6c89\u7740\u554a\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u53e3\u97f3\u5f88\u964c\u751f\uff0c\u8fc7\u53bb\uff0c\u54b1\u4eec\u6ca1\u6709\u89c1\u8fc7\u5427\uff1f\u201d \u3000\u3000\u90a3\u58f0\u97f3\u5e94\u9053\uff1a\u201c\u6211\u59d3\u590f\uff0c\u6625\u590f\u79cb\u51ac\u7684\u590f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u539f\u6765\u662f\u590f\u5148\u751f\uff0c\u5927\u540d\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u590f\u5148\u751f\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u544a\u8bc9\u4f60\uff0c\u4e5f\u4e0d\u8981\u7d27\uff0c\u56e0\u4e3a\uff0c\u6b64\u540e\u54b1\u4eec\u53ef\u80fd\u662f\u670b\u53cb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u590f\u5148\u751f\u9053\uff1a\u201c\u5982\u679c\u54b1\u4eec\u4e0d\u80fd\u6210\u4e3a\u670b\u53cb\u7684\u65f6\u5019\uff0c\u90a3\u5c31\u4f1a\u53d8\u6210\u4eba\u9b3c\u6b8a\u9014\uff0c\u9634\u9633\u6709\u522b\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6ca1\u6709\u4eba\u80fd\u591f\u5e2e\u52a9\u4f60\uff0c\u5fc5\u987b\u7531\u4f60\u81ea\u5df1\u53bb\u51b3\u5b9a\u2026\u2026\u201d \u3000\u3000\u91cd\u91cd\u54b3\u4e86\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u6211\u53eb\u590f\u6740\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u8868\u9762\u4e0a\u5f88\u51b7\u9759\uff0c\u5176\u5b9e\u3002\u5374\u6697\u4e2d\u628a\u58f0\u97f3\u7262\u8bb0\u8111\u9645\u3002\u4ed6\u65e0\u6cd5\u770b\u5230\u590f\u6740\u7684\u5f62\u8c8c\uff0c\u53ea\u6709\u7262\u8bb0\u4ed6\u7684\u58f0\u97f3\u4e86\u3002 \u3000\u3000\u590f\u6740\u7684\u58f0\u97f3\uff0c\u53c8\u4f20\u5165\u8033\u9645\uff0c\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u4f60\u60f3\u6e05\u695a\u4e86\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u8fd8\u4e0d\u77e5\u9053\uff0c\u54b1\u4eec\u5982\u4f55\u624d\u80fd\u6210\u4e3a\u670b\u53cb\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u6210\u670b\u53cb\u8fd9\u4ef6\u4e8b\uff0c\u5f88\u5bb9\u6613\uff0c\u4e5f\u5f88\u56f0\u96be\uff0c\u56f0\u96be\u7684\u539f\u56e0\u662f\uff1a\u50cf\u4f60\u53f6\u8001\u5f1f\u8fd9\u79cd\u4eba\uff0c\u5c11\u5e74\u6210\u540d\uff0c\u96be\u514d\u6c14\u76db\uff0c\u4e0d\u592a\u5bb9\u6613\u63a5\u53d7\u522b\u4eba\u7684\u547d\u4ee4\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5982\u662f\u4e00\u4e2a\u4eba\uff0c\u53bb\u547d\u4ee4\u2014\u4e2a\u4eba\uff0c\u90a3\u5c31\u4e0d\u80fd\u7b97\u662f\u670b\u53cb\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u4f46\u547d\u4ee4\u4f60\u7684\u4e0d\u662f\u6211\uff0c\u6240\u4ee5\u6211\u4eec\u8fd8\u662f\u670b\u53cb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\u7684\u610f\u601d\u662f\uff0c\u4f60\u6211\u7b49\u7ea7\u4e00\u6837\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u7684\u6b66\u529f\uff0c\u6211\u76f8\u4fe1\uff0c\u4e5f\u4e0d\u4f1a\u76f8\u5dee\u5f97\u5f88\u8fdc\uff0c\u65e5\u540e\u5728\u6c5f\u6e56\u4e0a\uff0c\u4e5f\u8bb8\u54b1\u4eec\u4f1a\u6709\u5f88\u591a\u5408\u4f5c\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u5982\u662f\u548c\u6211\u4e00\u6837\u7684\u8eab\u4ef7\uff0c\u53c8\u5982\u4f55\u80fd\u505a\u5f97\u4e86\u4e3b\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u6211\u662f\u5949\u547d\u6765\u6b64\uff0c\u4e13\u95e8\u5904\u7406\u4f60\u53f6\u8001\u5f1f\u7684\u4e8b\uff0c\u6211\u62e5\u6709\u5168\u6743\uff0c\u6240\u4ee5\uff0c\u4e5f\u53ef\u4ee5\u51b3\u5b9a\u4f60\u7684\u751f\u6b7b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c31\u7b97\u6211\u7b54\u5e94\u4e86\u4f60\uff0c\u4e0d\u77e5\u9053\u4f60\u4f1a\u4e0d\u4f1a\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u4f60\u4e5f\u662f\u6c5f\u6e56\u4e2d\u7684\u6210\u540d\u7684\u4eba\u7269\u4e86\uff0c\u6211\u60f3\uff0c\u54b1\u4eec\u90fd\u4e0d\u662f\u5bb9\u6613\u4e0a\u5f53\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u96be\u9053\u8fd8\u6709\u4ec0\u4e48\u6761\u4ef6\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u6709\uff01\u53ea\u8981\u4f60\u53f6\u5144\u4ea4\u51fa\u4e00\u4e2a\u4fdd\u8bc1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u5b51\u7136\u4e00\u8eab\uff0c\u80fd\u4ea4\u51fa\u4ec0\u4e48\u4fdd\u8bc1\u7ed9\u4f60\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5982\u679c\u53f6\u8001\u5f1f\u5b9e\u5728\u60f3\u4e0d\u8d77\u6765\uff0c\u6211\u5012\u53ef\u66ff\u53f6\u8001\u5f1f\u60f3\u4e00\u60f3\u3002\u201d\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\uff01\u4f60\u8bf4\u7ed9\u6211\u542c\u542c\uff0c\u4e5f\u8bb8\u771f\u7684\u80fd\u5bf9\u6211\u6709\u5f88\u5927\u7684\u5e2e\u52a9\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4fe1\u4efb\uff0c\u60f3\u4e00\u4e2a\u529e\u6cd5\uff0c\u8ba9\u6211\u4eec\u4fe1\u4efb\u4f60\uff0c\u4e5f\u5c31\u662f\u8ba9\u6211\u89c9\u5f97\u4f60\u4e0d\u4f1a\u80cc\u53db\uff0c\u5bf9\u6211\u4e2a\u4eba\u800c\u76f2\uff0c\u4f60\u4f1a\u6c38\u8fdc\u662f\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5509\uff01\u542c\u8d77\u6765\u5f88\u5bb9\u6613\uff0c\u505a\u8d77\u6765\uff0c\u53ea\u6015\u5f88\u5806\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5bb9\u6613\u5f97\u5f88\uff0c\u53ea\u8981\u628a\u6027\u547d\u4ea4\u7ed9\u4ed6\u4eec\u638c\u63e1\uff0c\u4ed6\u4eec\u5c31\u4e0d\u4f1a\u591a\u6000\u7591\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\uff0c\u5728\u4e0b\u4ece\u672a\u505a\u8fc7\uff0c\u4e5f\u4ece\u672a\u60f3\u8fc7\u3002\u590f\u5144\u65e2\u7136\u80fd\u4e3a\u6211\u501f\u7bb8\u4ee3\u7b79\uff0c\u60f3\u6765\u4e5f\u5fc5\u7136\u4e3a\u6211\u4ee3\u60f3\u4e00\u4e2a\u5b8c\u5584\u7684\u529e\u6cd5\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5c31\u5728\u4e0b\u6240\u77e5\uff0c\u670d\u7528\u4ed6\u4eec\u4ea4\u6765\u7684\u836f\u7269\uff0c\u662f\u529e\u6cd5\u4e4b\u4e00\uff0c\u90a3\u79cd\u836f\u7269\uff0c\u6709\u4e00\u79cd\u5b9a\u65f6\u53d1\u4f5c\u7684\u7279\u6027\uff0c\u4e0d\u751f\u80cc\u53db\u4e4b\u5fc3\uff0c\u90a3\u89e3\u836f\u5c31\u4f1a\u53ca\u65f6\u9001\u5230\u4f60\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u529e\u6cd5\u867d\u7136\u4e0d\u592a\u65b0\u5947\uff0c\u4f46\u5b9e\u5728\u5f88\u6709\u6548\uff0c\u4f46\u4e0d\u77e5\u590f\u5144\uff0c\u662f\u5426\u4e5f\u8981\u5e38\u5e38\u670d\u7528\u89e3\u836f\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4e00\u5e74\u4e4b\u524d\uff0c\u6211\u8fd8\u8981\u501f\u90a3\u4e9b\u89e3\u836f\u7ef4\u6301\u751f\u547d\uff0c\u4f46\u662f\u73b0\u5728\u4e0d\u7528\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u906d\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u4ed6\u4eec\u5df2\u7ecf\u5b8c\u5168\u7684\u4fe1\u4efb\u6211\uff0c\u6211\u8fd9\u51e0\u5e74\u6765\uff0c\u4e5f\u8868\u73b0\u5f97\u76f8\u5f53\u4e0d\u9519\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u5c31\u89e3\u9664\u4e86\u52a0\u8bf8\u5728\u6211\u8eab\u4e0a\u7684\u9650\u5236\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\uff0c\u4e00\u4e2a\u4eba\u7684\u6b7b\u4ea1\uff0c\u867d\u7136\u662f\u5fc5\u7ecf\u4e4b\u8def\uff0c\u4f46\u4eba\u7684\u597d\u751f\u6076\u6b7b\u4e4b\u5ff5\uff0c\u603b\u662f\u96be\u4ee5\u6da4\u9664\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u662f\u554a\uff0c\u597d\u6b7b\u4e0d\u5982\u8d56\u6d3b\uff0c\u5c24\u5176\u50cf\u4f60\u53f6\u8001\u5f1f\u8fd9\u6837\u7684\u4eba\uff0c\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u540d\u6ee1\u6c5f\u6e56\uff0c\u6765\u65e5\u6b63\u957f\uff0c\u6b7b\u4e86\u5b9e\u5728\u53ef\u60dc\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e0d\u8bed\uff0c\u5fc3\u4e2d\u5374\u5728\u6697\u4f5c\u76d8\u7b97\uff1a\u201c\u6211\u5982\u4e0d\u7b54\u5e94\u4ed6\uff0e\u5fc5\u662f\u6b7b\u5b9a\u4e86\uff0c\u7b54\u5e94\u4e86\u773c\u4e0b\u5b9a\u65f6\u53d1\u4f5c\u7684\u6bd2\u836f\uff0c\u4e5f\u662f\u5f88\u96be\u6d3b\u5f97\u4e0b\u53bb\uff0c\u4f46\u6b66\u6797\u4eba\u7269\uff0c\u5b81\u53ef\u6b7b\u4e8e\u5200\u3001\u5251\u4e4b\u4e0b\uff0c\u4e5f\u4e0d\u80fd\u6b7b\u4e8e\u6bd2\u836f\u7684\u53d1\u4f5c\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u53ea\u542c\u590f\u6740\u8bf4\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u82b1\u56fd\u53d6\u9b41\uff0c\u7ae0\u53f0\u8d70\u9a6c\uff0c\u4eba\u751f\u98ce\u6f14\u4e8b\u7269\uff0c\u591a\u4e0d\u80dc\u6570\uff0c\u8fd9\u4e9b\uff0c\u53ea\u6015\u4f60\u8001\u5f1f\u90fd\u8fd8\u6ca1\u6709\u7ecf\u5386\u8fc7\u5427\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u6240\u4ee5\u6211\u8fd8\u60f3\u591a\u6d3b\u51e0\u5e74\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u6211\u5fc5\u987b\u5148\u5f04\u6e05\u695a\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8981\u5728\u4e0b\u542c\u547d\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u5982\u82e5\u4ed6\u8fd8\u80fd\u4f7f\u5728\u4e0b\u5fc3\u4e2d\u6709\u70b9\u656c\u670d\uff0c\u4e5f\u5c31\u5c06\u5c31\u7740\u6d3b\u4e0b\u53bb\u4e86\uff0c\u5982\u82e5\u90a3\u4eba\u53eb\u5728\u4e0b\u770b\u4e0d\u8d77\uff0c\u90a3\u5c31\u751f\u4e0d\u5982\u6b7b\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u5f88\u4e45\u6ca1\u6709\u56de\u7b54\uff0c\u800c\u4e14\uff0c\u90a3\u7167\u5c04\u5728\u53f6\u957f\u9752\u8138\u4e0a\u7684\u706f\u5149\uff0c\u4e5f\u7a81\u7136\u9690\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u95ed\u4e0a\u4e86\u773c\u775b\uff0e\u5c3d\u91cf\u4f7f\u81ea\u5df1\u7684\u76ee\u5149\u9002\u5e94\u9ed1\u6697\u3002 \u3000\u3000\u518d\u7741\u5f00\u773c\u775b\u65f6\uff0c\u5df2\u7136\u9690\u9690\u53ef\u89c1\u5ba4\u4e2d\u7684\u666f\u7269\u3002 \u3000\u3000\u611f\u89c9\u4e2d\uff0c\u884c\u76f4\u7684\u5feb\u821f\uff0c\u7a81\u7136\u505c\u4e86\u4e0b\u6765\u3002\u7d27\u63a5\u7740\uff0c\u5149\u7ebf\u900f\u5165\uff0c\u5e95\u8231\u95e8\u5f00\uff0c\u4e00\u4e2a\u9ed1\u5e03\u5305\u5934\uff0c\u53ea\u9732\u51fa\u4e24\u53ea\u773c\u775b\u7684\u4eba\uff0c\u7f13\u6b65\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u867d\u7136\u65e0\u6cd5\u770b\u51fa\u4ed6\u7684\u9762\u8c8c\uff0c\u4f46\u53f6\u957f\u9752\u4ecd\u7136\u770b\u51fa\u4e86\u4ed6\u662f\u5468\u603b\u7ba1\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u53f6\u957f\u9752\u5e76\u6ca1\u6709\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u90a3\u4eba\u76f4\u884c\u5230\u53f6\u957f\u9752\u7684\u9762\u524d\uff0c\u9053\uff1a\u201c\u9601\u4e0b\uff0c\u8bf7\u559d\u4e0b\u8fd9\u4e00\u676f\u9152\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u9152\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u8ff7\u9b42\u9152\uff0c\u9601\u4e0b\u559d\u4e0b\u53bb\u4e86\u8fd9\u676f\u9152\u4e4b\u540e\uff0c\u4eba\u5c31\u7acb\u523b\u6655\u8ff7\u8fc7\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u660e\u767d\uff0c\u4e0d\u559d\u6655\u8fd9\u676f\u9152\uff0c\u53ef\u80fd\u4f1a\u7acb\u523b\u53ec\u6765\u6740\u8eab\u4e4b\u7978\uff0c\u5f53\u4e0b\u4e00\u5f20\u53e3\uff0c\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e00\u676f\u9152\u559d\u4e0b\u53bb\uff0c\u53f6\u957f\u9752\u7acb\u523b\u611f\u89c9\u5230\u90a3\u662f\u4e00\u79cd\u5f88\u5f3a\u70c8\u7684\u8ff7\u9b42\u836f\u7269\uff0c\u5fc3\u4e2d\u5ff5\u5934\u8fd8\u6765\u8f6c\u8fc7\u6765\uff1b\u4eba\u5df2\u7ecf\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000 \u3000\u3000\u53f6\u957f\u9752\u518d\u5ea6\u6e05\u9192\u65f6\uff0e\u53d1\u89c9\u8eba\u5728\u4e00\u5f20\u6728\u5e8a\u4e0a\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u95f4\u5e03\u7f6e\u5f97\u5f88\u96c5\u81f4\u7684\u5ba2\u5ba4\u3002\u4e00\u5385\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u95f4\u5367\u623f\uff0c\u5367\u623f\u5185\uff0c\u6709\u4e00\u5f20\u6728\u69bb\u3002 \u3000\u3000\u6728\u69bb\u65c1\u8fb9\uff0c\u6b63\u7ad9\u7740\u2014\u4e2a\u7f8e\u4e3d\u7edd\u4f26\u7684\u5973\u5b50\u3002 \u3000\u3000\u4eba\u751f\u5f97\u7f8e\u4e3d\uff0c\u4e00\u8eab\u8863\u670d\uff0c\u66f4\u662f\u5145\u6ee1\u7740\u2014\u79cd\u52a8\u4eba\u7684\u70ed\u60c5\u3002 \u3000\u3000\u7c89\u7ea2\u7f57\u886b\uff0c\u7c89\u7ea2\u88d9\uff0c\u7c89\u7ea2\u8272\u7684\u5e26\u5b50\uff0c\u5728\u957f\u53d1\u4e0a\u6253\u4e86\u4e00\u4e2a\u8774\u8776\u7ed3\u3002 \u3000\u3000\u5979\u624b\u4e2d\u8fd8\u6367\u7740\u4e00\u4e2a\u74f7\u76d8\uff0c\u76d8\u4e2d\u653e\u7740\u4e00\u4e2a\u7ec6\u74f7\u82b1\u676f\u3002 \u3000\u3000\u671b\u671b\u90a3\u7ea2\u8863\u5c11\u5973\uff0c\u53f6\u957f\u9752\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u95f4\u8fce\u5019\u8d35\u5bbe\u7684\u5ba2\u623f\uff0c\u6211\u662f\u4f8d\u5019\u8d35\u5bbe\u7684\u4e2b\u5934\u3002\u201d \u3000\u3000\u5979\u56de\u7b54\u7684\u8bdd\u5f88\u6e05\u695a\uff0c\u58f0\u97f3\u4e5f\u5f88\u5a07\u7f8e\u52a8\u542c\uff0c\u4f46\u5374\u5b8c\u5168\u8bf4\u7684\u662f\u5e9f\u8bdd\u3002 \u3000\u3000\u4e00\u95f4\u5367\u623f\uff0c\u7528\u4e0d\u7740\u90a3\u4e2b\u5934\u8bf4\uff0c\u53f6\u957f\u9752\u770b\u5f97\u5f88\u6e05\u695a\u4e86\u3002 \u3000\u3000\u53f6\u957f\u9752\u7f13\u7f13\u5750\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u6211\u770b\u5f97\u51fa\u8fd9\u662f\u4e00\u5ea7\u5367\u5ba4\uff0c\u6211\u60f3\u8fd9\u5730\u65b9\uff0c\u603b\u5e94\u8be5\u6709\u4e2a\u5730\u540d\u624d\u662f\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5973\u5b50\u9053\uff1a\u201c\u54e6\uff01\u8fd9\u5730\u65b9\u53eb\u9634\u9633\u5821\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9634\u9633\u5821\uff0c\u6211\u600e\u4e48\u6ca1\u6709\u542c\u8bf4\u8fc7\u8fd9\u4e48\u4e00\u4e2a\u5730\u65b9\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5973\u5b57\u7b11\u9053\uff1a\u201c\u6709\u4e9b\u5730\u529b\u5f88\u76ee\u6709\u540d\uff0c\u4f46\u5374\u662f\u865a\u540d\uff0c\u9634\u9633\u5821\u867d\u7136\u4e0d\u600e\u4e48\u6709\u540d\uff0c\u4f46\u5374\u662f\u9634\u9633\u5206\u660e\uff0c\u662f\u4e2a\u5f88\u5b9e\u9645\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f88\u5b9e\u9645\u7684\u5730\u65b9\uff0c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5973\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u8fd9\u91cc\u662f\u751f\u6b7b\u5173\uff0c\u9634\u9633\u754c\uff0c\u53ef\u4ee5\u751f\uff0c\u53ef\u4ee5\u6b7b\uff0c\u5c31\u8981\u770b\u4f60\u53f6\u5c11\u4fa0\u81ea\u5df1\u7684\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u50cf\u59d1\u5a18\u8fd9\u6837\u7684\u7f8e\u4e3d\u5973\u4eba\uff0c\u5c31\u7b97\u662f\u5f88\u53ef\u6016\u7684\u8bdd\uff0c\u4f46\u5982\u4ece\u4f60\u7684\u53e3\u4e2d\u8bf4\u51fa\u91c7\uff0c\u4e5f\u6ca1\u6709\u53ef\u6016\u7684\u611f\u89c9\u4e86\u3002\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\u8fd9\u6837\u82f1\u4fca\uff0c\u8bf4\u7684\u8bdd\uff0c\u53c8\u5982\u6b64\u751c\u7f8e\u52a8\u4eba\uff0c\u5b9e\u5728\u662f\u53eb\u4eba\u542c\u4e86\u5f88\u8212\u670d\uff0c\u5c0f\u59b9\u8877\u5fc3\u5e0c\u671b\u4f60\u80fd\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u671b\u4e86\u671b\u90a3\u82b1\u74f7\u8336\u676f\uff0e\u9053\uff1a\u201c\u90a3\u8336\u676f\u91cc\u9762\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u9053\uff1a\u201c\u8336\uff01\u4e00\u676f\u65e0\u5fe7\u8336\uff0c\u5403\u4e0b\u53bb\uff0c\u4f60\u5c31\u4e0d\u7528\u518d\u62c5\u5fc3\u751f\u6b7b\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6697\u4e2d\u8fd0\u6c14\u2014\u8bd5\uff0c\u53d1\u89c9\u7adf\u7136\u65e0\u6cd5\u63d0\u805a\u771f\u6c14\uff0c\u6697\u6697\u53f9\u606f\u4e00\u58f0\uff0c\u5fc3\u4e2d\u660e\u767d\u5df1\u7136\u88ab\u4eba\u52a8\u4e86\u624b\u811a\u3002 \u3000\u3000\u4f46\u4ed6\u4e5f\u660e\u767d\uff0c\u6b64\u523b\uff0c\u6b63\u662f\u751f\u6b7b\u5173\u5934\uff0c\u5fc5\u987b\u4fdd\u6301\u51b7\u9759\u3002 \u3000\u3000\u4ed6\u5c3d\u91cf\u4f7f\u58f0\u97f3\u53d8\u7684\u5f88\u5e73\u548c\uff0c\u9053\uff1a\u201c\u65e0\u5fe7\u8336\uff0c\u5b9e\u5728\u662f\u5f88\u597d\u542c\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u7ea2\u8863\u5973\u5b50\u9053\uff1a\u201c\u540d\u5b57\u867d\u7136\u597d\u542c\uff0c\u4f46\u5374\u662f\u4e00\u676f\u6bd2\u8336\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u559d\u4e0b\u53bb\uff0c\u7acb\u523b\u4f1a\u6b7b\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5973\u5b50\u9053\uff1a\u201c\u90a3\u770b\u4f60\u7684\u60f3\u6cd5\u4e86\uff1f\u5982\u82e5\u4f60\u6e29\u987a\u4e00\u4e9b\uff1b\u559d\u4e0b\u53bb\uff0c\u5c31\u4e0d\u4f1a\u6b7b\u4e86\uff0c\u5982\u82e5\u4f60\u662f\u90a3\u79cd\u5f88\u56fa\u6267\u7684\u4eba\uff0c\u4e0d\u77e5\u9053\u89c1\u98ce\u8f6c\u8235\uff0c\u90a3\u5c31\u751f\u4e0d\u5982\u6b7b\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4f4e\u58f0\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u5982\u6d3b\u4e0b\u53bb\uff0c\u80fd\u4e0d\u80fd\u63d0\u51fa\u2014\u4e2a\u8981\u6c42\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5973\u5b50\u9053\uff1a\u201c\u770b\u6837\u5b50\uff0c\u4f60\u5982\u80af\u5408\u4f5c\uff0c\u4f1a\u53d7\u5230\u76f8\u5f53\u7684\u656c\u91cd\uff0c\u5982\u679c\u4e0d\u592a\u8fc7\u5206\u7684\u8981\u6c42\uff0c\u4e5f\u8bb8\u5821\u4e3b\u4f1a\u7b54\u5e94\u4f60\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u5c31\u597d\u3002\u201d\u4f38\u624b\u53d6\u8fc7\u8336\u676f\uff0c\u4e00\u4ef0\u8116\u5b50\uff0c\u559d\u5e72\u4e86\u8336\u3002 \u3000\u3000\u7ea2\u8863\u5973\u5b50\u5012\u662f\u672a\u60f3\u5230\u4ed6\u5982\u6b64\u5e72\u51c0\u5229\u843d\uff0c\u4e0d\u7981\u4e00\u5446\u3002 \u3000\u3000\u53f6\u957f\u9752\u5df2\u7136\u4e0b\u4e86\u5e8a\uff0c\u9053\uff1a\u201c\u8fd9\u676f\u8336\u4e2d\u7684\u6bd2\u836f\uff0c\u51e0\u65f6\u4f1a\u53d1\u4f5c\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u9053\uff1a\u201c\u81f3\u5c11\u8fd8\u6709\u4e09\u4e2a\u65f6\u8fb0\uff0c\u4f60\u8fd8\u6709\u8db3\u591f\u7684\u65f6\u95f4\uff0c\u529e\u5b8c\u4f60\u7684\u540e\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u6211\u80fd\u4e0d\u80fd\u5f88\u5feb\u7684\u89c1\u5230\u5821\u4e3b\u3002\u201d \u3000\u3000\u7ea2\u8863\u5973\u5b50\u9053\uff1a\u201c\u8bf7\u5230\u5385\u4e2d\u5750\u5427\uff01\u201d \u3000\u3000\u5385\u4e0d\u5927\uff0c\u4f46\u5374\u5e03\u7f6e\u5f97\u5f88\u96c5\u81f4\u3002 \u3000\u3000\u7ea2\u8863\u5973\u5b50\u4f38\u51fa\u5979\u7ea4\u5de7\u7684\u7389\u624b\uff0c\u8f7b\u8f7b\u6309\u4f4f\u4e86\u53f6\u957f\u9752\u7684\u80a9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u5fc3\u5b58\u5984\u56fe\uff0c\u90a3\u4f1a\u4f7f\u4f60\u6b7b\u7684\u5f88\u60b2\u60e8\uff0c\u800c\u4e14\uff0c\u4e00\u70b9\u4e5f\u4e0d\u58ee\u70c8\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u6307\u6559\u3002\u201d \u3000\u3000\u7ea2\u8863\u5973\u5b50\u9053\uff1a\u201c\u4e56\u4e56\u7684\u7a0d\u5750\u7247\u523b\uff0c\u6211\u53bb\u8bf7\u655d\u5821\u4e3b\u6765\u3002\u201d \u3000\u3000 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u7ea2\u8863\u5973\u5b50\u5e26\u4e86\u4e24\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u4e00\u4e2a\u957f\u7709\u5584\u76ee\uff0c\u8eab\u7740\u9752\u7ef8\u5b50\u957f\u888d\u7684\u4e2d\u5e74\uff0c\u8eab\u540e\uff0c\u5374\u7d27\u968f\u7740\u4e00\u4e2a\u7626\u9aa8\u5d99\u5ccb\u7684\u4eba\u3002 \u3000\u3000\u9752\u8863\u4eba\u5c31\u5728\u53f6\u957f\u9752\u5bf9\u9762\u4e00\u5f20\u6728\u6905\uff0d\u5315\u5750\u4e0b\u3002\u90a3\u7626\u524a\u4eba\u5374\u8083\u7acb\u5728\u957f\u8863\u4eba\u7684\u8eab\u540e\u3002 \u3000\u3000\u7ea2\u8863\u5973\u5b50\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u8fd9\u5c31\u662f\u4f60\u6c42\u89c1\u7684\u5821\u4e3b\uff0c\u4ed6\u80af\u79fb\u9a7e\u6765\u6b64\u770b\u4f60\uff0c\u8fd8\u4e0d\u5feb\u4e9b\u62dc\u89c1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6ca1\u6709\u52a8\uff0c\u53ea\u662f\u671b\u7740\u90a3\u9752\u8863\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u53eb\u53f6\u957f\u9752\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4e03\u5251\u8ffd\u9b42\uff0c\u542c\u8bf4\u4f60\u51fa\u9053\u6c5f\u6e56\u4ee5\u6765\uff0c\u8fd8\u6ca1\u6709\u7528\u8fc7\u4e03\u5251\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u5938\u5f20\uff0c\u6211\u6740\u6b7b\u6700\u9ad8\u660e\u7684\u4e00\u4e2a\u4eba\u65f6\uff0c\u53ea\u7528\u4e86\u4e94\u5251\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u4eec\u4e00\u5b9a\u8981\u6740\u6389\u4f60\uff0c\u6211\u4e0d\u60f3\u5728\u6c5f\u6e56\u4e0a\uff0c\u66ff\u81ea\u5df1\u7559\u4e0b\u4e00\u4e2a\u5f3a\u654c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u559d\u4e0b\u4e86\u90a3\u4e00\u676f\u65e0\u5fe7\u8336\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u624d\u80fd\u591f\u89c1\u5230\u6211\uff0c\u800c\u4e14\u662f\u4ee5\u771f\u6b63\u7684\u9762\u76ee\u548c\u4f60\u89c1\u9762\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u53ef\u5426\u63d0\u51fa\u4e00\u4e2a\u8981\u6c42\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u6c89\u541f\u4e86\u4e00\u9635\u906d\uff1a\u201c\u53ef\u4ee5\uff0c\u4e0d\u8fc7\uff0c\u8981\u4f7f\u6211\u80fd\u591f\u63a5\u53d7\u7684\u8981\u6c42\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u867d\u7136\u81ea\u5df1\u77e5\u9053\u4e86\u81ea\u5df1\u7684\u547d\u8fd0\uff0c\u4f46\u6211\u8fd8\u662f\u5e0c\u671b\u81ea\u5df1\u4f1a\u88ab\u4eba\u91cd\u89c6\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u54e6\uff01\u8bf4\u5427\uff0c\u4f60\u8981\u6c42\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u53ea\u662f\u60f3\u4e3a\u81ea\u5df1\u627e\u4e00\u4e2a\u559c\u6b22\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9\u4e48\u4e00\u4ef6\u4e8b\uff0c\u8bf4\u8bf4\u770b\uff0c\u4f60\u60f3\u8981\u4ec0\u4e48\u6837\u5b50\u7684\u5973\u4eba\uff1f\u201d \u3000\u3000\u201c\u5979\uff01\u201d\u53f6\u957f\u9752\u4f38\u624b\u6307\u7740\u90a3\u7ad9\u5728\u4e00\u4fa7\u7684\u7ea2\u8863\u5973\u4eba\u3002 \u3000\u3000\u9752\u8863\u4eba\u5fae\u5fae\u4e00\u6014\uff0e\u9053\uff1a\u201c\u6021\u7ea2\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u539f\u6765\u5979\u53eb\u6021\u7ea2\uff0c\u5f88\u52a8\u4eba\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\u4f3c\u4e4e\u4e0d\u662f\u4e2a\u81ea\u547d\u98ce\u6d41\u7684\u4eba\u7269\uff0c\u600e\u4e48\u4f1a\u7a81\u7136\u6709\u4e86\u6539\u53d8\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5728\u4e0b\u8fc7\u53bb\u786e\u7136\u4e0d\u662f\uff0c\u4f46\u81ea\u4ece\u996e\u4e0b\u4e86\u90a3\u4e00\u676f\u65e0\u5fe7\u8336\u4e4b\u540e\uff0c\u6211\u5f00\u59cb\u6709\u4e86\u6539\u53d8\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u9053\uff1a\u201c\u6539\u53d8\u5f97\u5f88\u5927\uff0c\u4e00\u4e2a\u5b8c\u5168\u4e0d\u60f3\u5973\u4eba\u7684\u4eba\uff0c\u5ffd\u7136\u95f4\u60f3\u8d77\u5973\u4eba\u6765\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5bf9\uff01\u56e0\u4e3a\u6211\u5df2\u7ecf\u660e\u767d\uff0c\u4ece\u996e\u4e0b\u90a3\u4e00\u676f\u65e0\u5fe7\u8336\u8d77\uff0c\u6211\u5df2\u7ecf\u8981\u5f00\u59cb\u8fc7\u53e6\u4e00\u79cd\u751f\u6d3b\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u9053\uff1a\u201c\u4f60\u60f3\u5f97\u5f88\u591a\uff0c\u4e0d\u8fc7\u5f88\u6b63\u786e\u3002\u770b\u6765\uff0c\u4f60\u4e0d\u4f46\u6b66\u529f\u5f88\u9ad8\uff0c\u800c\u4e14\u4eba\u4e5f\u5f88\u806a\u660e\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u80fd\u5728\u6c5f\u6e56\u4e0a\u95ef\u51fa\u8fd9\u70b9\u58f0\u8a89\uff0c\u81ea\u7136\u4e0d\u662f\u4e00\u4e2a\u7b80\u5355\u7684\u4eba\u7269\u4e86\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u7269\u7ade\u5929\u62e9\uff0c\u70bc\u91d1\u706b\u70c8\uff0c\u4e00\u4e2a\u4eba\u80fd\u5728\u6c5f\u6e56\u4e0a\u95ef\u51fa\u540d\u6c14\uff0c\u90fd\u6709\u4ed6\u7684\u8fc7\u4eba\u4e4b\u5904\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u90a3\u676f\u65e0\u5fe7\u8336\uff0c\u6709\u4e9b\u4ec0\u4e48\u4f5c\u7528\uff0c\u4f46\u6211\u5374\u77e5\u9053\uff0c\u53f6\u957f\u9752\u5373\u5c06\u6d88\u5931\u4e8e\u625b\u6e56\u4e4b\u4e2d\uff0c\u5b58\u5728\u6c5f\u6e56\u4e0a\u7684\u3002\u53ea\u662f\u4e03\u5251\u8ffd\u9b42\u3002\u201d \u3000\u3000\u7ad9\u5728\u90a3\u9752\u886b\u4eba\u8eab\u540e\u7684\u7626\u524a\u4eba\uff0c\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u4e03\u5251\u8ffd\u9b42\u548c\u53f6\u957f\u9752\uff0c\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u7acb\u523b\u7531\u58f0\u97f3\u4e2d\u5206\u8fa8\u51fa\uff0c\u90a3\u4eba\u6b63\u662f\u590f\u6740\u3002 \u3000\u3000\u9752\u8863\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u540c\uff0c\u4e0d\u540c\uff0c\u5f88\u5927\u7684\u4e0d\u540c\uff0c\u53f6\u957f\u9752\u662f\u4e00\u4ee3\u5927\u4fa0\uff0c\u4e03\u5251\u8ffd\u9b42\uff0c\u53ea\u662f\u4e00\u4e2a\u51cc\u5389\u7684\u5251\u624b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u4fa0\u5ba2\uff0c\u9664\u4e86\u4ed6\u7684\u5251\u672f\u4e4b\u5916\uff0c\u5fc5\u987b\u8fd8\u8981\u5177\u6709\u4f18\u826f\u7684\u54c1\u5fb7\uff0e\u4f46\u4e00\u4e2a\u5251\u624b\uff0c\u90a3\u5c31\u4e0d\u7528\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u55ef\uff0c\u98df\u8272\u8005\uff0c\u6027\u4e5f\uff0c\u8fc7\u53bb\uff0c\u53f6\u5c11\u4fa0\u4e3a\u4e86\u6210\u4e3a\u4e00\u540d\u5927\u4fa0\uff0c\u6240\u4ee5\uff0c\u4f60\u5fc5\u9700\u4f7f\u81ea\u5df1\u7684\u54c1\u5fb7\u5f88\u5b8c\u83ab\uff0c\u6240\u4ee5\uff0c\u4ed6\u4e00\u76f4\u5728\u514b\u5236\u7740\u81ea\u5df1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4ed6\u4e00\u65e6\u51b3\u5b9a\u653e\u5f03\u4e86\u4fa0\u5ba2\u7684\u8eab\u5206\uff0c\u505a\u4e00\u4e2a\u6740\u624b\u65f6\uff0c\u5c31\u51c6\u5907\u8fc7\u4e00\u70b9\u653e\u8361\u7684\u751f\u6d3b\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\u89c2\u5bdf\u4eba\u751f\uff0c\u6d1e\u5f7b\u7ec6\u81f4\u3002\u679c\u7136\u662f\u65e0\u6240\u4e0d\u80fd\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u6de1\u7ebf\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u4e00\u773c\u5c31\u770b\u4e0a\u4e86\u6021\u7ea2\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5979\u5b9e\u5728\u662f\u4e00\u4e2a\u5f88\u7f8e\u7684\u5973\u5b69\u5b50\uff0c\u6709\u7740\u90a3\u79cd\u4f7f\u4eba\u9676\u9189\u7684\u8273\u4e3d\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u5979\u5f53\u7136\u5f88\u7f8e\uff0c\u56e0\u4e3a\u5979\u662f\u4ece\u5f88\u591a\u7f8e\u5973\u4e2d\u9009\u51fa\u6765\u7684\u7f8e\u5973\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u8fd9\u5c31\u96be\u602a\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u5979\u662f\u6211\u7684\u4ec0\u4e48\u4eba\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u53ea\u770b\u5230\u4e86\u5979\u7684\u7f8e\u4e3d\uff0c\u6ca1\u6709\u60f3\u5230\u5979\u7684\u8eab\u5206\uff0c\u4e0d\u8fc7\uff0c\u5821\u4e3b\u80af\u628a\u5979\u653e\u5230\u6211\u7684\u8eab\u8fb9\u6765\uff0c\u60f3\u6765\uff0c\u4e0d\u4f1a\u592a\u8fc7\u91cd\u8981\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u9053\uff1a\u201c\u5bf9\u6211\u800c\u8a00\uff0c\u975e\u5e38\u91cd\u8981\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u4e3a\u4e86\u4e89\u53d6\u50cf\u4e03\u5251\u8ffd\u9b42\u8fd9\u6837\u7684\u6740\u624b\uff0c\u6211\u4e0d\u5f97\u4e0d\u8868\u793a\u51fa\u4e00\u70b9\u98ce\u5ea6\u548c\u727a\u7272\u201d \u3000\u3000\u76ee\u5149\u4e13\u6ce8\u5230\u6021\u7ea2\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u7531\u6b64\u523b\u8d77\uff0c\u4f60\u5c31\u662f\u957f\u9752\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u53e3\u6c14\u575a\u5b9a\uff0c\u4e0d\u5bb9\u6021\u7ea2\u6709\u56de\u7edd\u7684\u4f59\u5730\u3002 \u3000\u3000\u6021\u7ea2\u7728\u52a8\u4e86\u4e00\u4e0b\u7f8e\u4e3d\u7684\u5927\u773c\u775b\uff0c\u7f13\u7f13\u5e94\u9053\uff1a\u201c\u5a62\u5b50\u9075\u547d\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7f13\u7f13\u7531\u8eab\u4e0a\u53d6\u51fa\u4e86\u4e00\u7c92\u4e39\u4e38\u3001\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u8fd9\u4e00\u7c92\u836f\u4e38\uff0c\u53ef\u4ee5\u4f7f\u4f60\u4fdd\u6301\u4e09\u5929\u7684\u6e05\u9192\uff0c\u6211\u7559\u4e0b\u6021\u7ea2\u966a\u7740\u4f60\u3001\u4f60\u53ef\u4ee5\u75db\u75db\u5feb\u5feb\u7684\u73a9\u4e09\u5929\u3001\u4e09\u5929\u4e4b\u540e\u3002\u4f60\u5c06\u53bb\u5b8c\u6210\u52a0\u5165\u672c\u5821\u540e\u7684\u7b2c\u4e00\u4e2a\u4efb\u52a1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u6211\u8981\u53bb\u505a\u4e9b\u4ec0\u4e48\uff1f\u4e0d\u8fc7\uff0e\u6211\u60f3\uff0c\u90a3\u4e00\u5b9a\u662f\u7b2c\u4e00\u6b21\u7684\u4e8b\u60c5\uff0c\u603b\u4e0d\u4f1a\u662f\u6211\u4e00\u4e2a\u4eba\u5427\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u590f\u6740\u966a\u4f60\u53bb\u2026\u2026\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u5b50\uff0c\u63a5\u7740\uff1a\u201c\u4e09\u5929\u7684\u65f6\u95f4\uff0c\u4e0d\u7b97\u957f\uff0c\u4f60\u597d\u597d\u7684\u4eab\u53d7\u4e09\u5929\u5427\u2014\u2014\u6211\u4f1a\u8981\u4ed6\u4eec\u9001\u6765\u6700\u597d\u7684\u9152\uff0c\u6700\u597d\u7684\u83dc\uff0c\u4e0d\u8fc7\uff0c\u6211\u8fd8\u662f\u5e0c\u671b\u4f60\u5728\u4eab\u53d7\u6b22\u4e50\u7684\u8fc7\u7a0b\u4e2d\uff0c\u80fd\u73cd\u60dc\u81ea\u5df2\uff0c\u4fdd\u7559\u4e0b\u9002\u5f53\u7684\u4f53\u80fd\uff0c\u80fd\u63e1\u7d27\u4f60\u624b\u4e2d\u7684\u5251\u3002\u201d \u3000\u3000\u535f\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\uff0c\u6211\u73b0\u5728\u4e0d\u60f3\u6b7b\u4e86\uff0c\u81ea\u7136\u662f\u5e0c\u671b\u80fd\u597d\u597d\u7684\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u9752\u886b\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5bf9\uff01\u5bf9\uff01\u7559\u5f97\u9752\u5c71\u5728\uff0c\u4e0d\u6015\u6ca1\u67f4\u70e7\uff0c\u5929\u4e0b\u7f8e\u5973\uff0c\u4f55\u6b62\u5343\u4e07\uff0c\u53c8\u4f55\u53ea\u6021\u7ea2\u4e00\u4eba\u3002\u201d \u3000\u3000\u4e5f\u4e0d\u5f85\u53f6\u957f\u9752\u7b54\u8bdd\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u5385\u4e2d\uff0c\u53ea\u4f59\u4e0b\u6021\u7ea2\u548c\u53f6\u957f\u9752\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u6021\u7ea2\u4e3e\u624b\u7406\u4e00\u4e0b\u6563\u62ab\u7684\u957f\u53d1\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u4f60\u7adf\u7136\u4f1a\u770b\u4e0a\u6211\uff0c\u6211\u53ea\u4e0d\u8fc7\u662f\u4e00\u4e2a\u4e2b\u73af\u7f62\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u521a\u597d\u5417\uff1f\u6211\u53f6\u957f\u9752\u5c31\u8981\u6ca6\u4e3a\u4e00\u4e2a\u6740\u624b\uff0c\u6740\u624b\u914d\u4e2b\u5934\uff0c\u90a3\u624d\u662f\u95e8\u5f53\u6237\u5bf9\u554a\uff01\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u6211\u8fd9\u4e2a\u4e2b\u5934\uff0c\u53ea\u6015\u8fd8\u672a\u5fc5\u613f\u610f\u4f5c\u4f60\u6740\u624b\u7684\u59bb\u5b50\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\u59d1\u5a18\uff0c\u6211\u4e5f\u4e0d\u4f1a\u5a36\u4f60\uff0c\u6740\u624b\u751f\u6daf\u672c\u5982\u68a6\uff0c\u5728\u4e0b\u4e5f\u4e0d\u613f\u591a\u4e00\u4e2a\u8d1f\u62c5\u3002\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u6021\u7ea2\u7684\u8138\u4e0a\uff0c\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u4f60\u4e0d\u8fc7\u662f\u4e2a\u5f88\u597d\u770b\u7684\u5973\u4eba\uff0c\u4e0d\u4f1a\u662f\u8d24\u59bb\uff0c\u4e5f\u4e0d\u4f1a\u662f\u826f\u6bcd\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u4f60\u8981\u6211\u7559\u4e0b\u6765\uff0c\u53ea\u4e3a\u4e86\u8981\u7f9e\u8fb1\u6211\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5012\u4e0d\u662f\u5168\u7136\u5982\u6b64\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u522b\u7684\u4f5c\u7528\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6021\u7ea2\u59d1\u5a18\uff0c\u50cf\u6211\u4e03\u5251\u8ffd\u9b42\u53f6\u957f\u9752\uff0c\u5728\u6b66\u6797\u4e2d\uff0c\u5c0f\u6709\u540d\u671b\u7684\u4eba\uff0c\u4f46\u6211\u7adf\u7136\u4f1a\u6ca6\u4e3a\u6740\u624b\uff0c\u6709\u4e9b\u4e8b\uff0c\u597d\u50cf\u662f\u53ea\u6709\u8ba4\u547d\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u5982\u662f\u6211\u4e0d\u7b54\u5e94\u5462\uff1f\u201d\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\u59d1\u5a18\uff0c\u96be\u9053\u4f60\u5fd8\u4e86\uff0c\u5821\u4e3b\u5df2\u7ecf\u628a\u4f60\u8d50\u7ed9\u4e86\u6211\uff1f\u201d\u6021\u7ea2\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u6211\u4e0d\u80fd\u6297\u62d2\u5821\u4e3b\u4e4b\u547d\uff0c\u96be\u9053\u4e5f\u4e0d\u80fd\u6297\u62d2\u4f60\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u624b\u4e2d\u6709\u5251\uff0c\u5251\u53ef\u4ee5\u6740\u4eba\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u8fd8\u6ca1\u6709\u4e00\u4e2a\u4eba\uff0c\u80fd\u9003\u8fc7\u6211\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u96be\u9053\u4f60\u4f1a\u6740\u6211\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f88\u53ef\u80fd\uff0c\u6021\u7ea2\u59d1\u5a18\uff0c\u8fc7\u53bb\uff0c\u6211\u53f6\u67d0\u4eba\u624b\u4e2d\u4e4b\u5251\uff0c\u662f\u6b63\u4e49\u4e4b\u5251\uff0c\u4e0d\u4f1a\u8f7b\u6613\u6740\u4eba\u2026\u2026\u201d \u3000\u3000\u6021\u7ea2\u63a5\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u4e0d\u662f\u6b63\u4e49\u4e4b\u5251\u4e86\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u73b0\u5728\uff0c\u53f6\u67d0\u4eba\u624b\u4e2d\u4e4b\u5251\uff0c\u5df2\u6ca6\u4e3a\u6740\u624b\u4e4b\u5251\uff0c\u6325\u52a8\u6211\u624b\u4e2d\u4e4b\u5251\u65f6\uff0c\u4f3c\u4e4e\u662f\u7528\u4e0d\u7740\u8bb2\u4ec0\u4e48\u9053\u7406\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u7684\u6ca6\u843d\uff0c\u7adf\u7136\u662f\u5982\u6b64\u7684\u5feb\u901f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u6c89\u6ca6\u5f97\u5f88\u5feb\uff0c\u53ea\u4e0d\u8fc7\u51e0\u5929\u7684\u5de5\u592b\uff0c\u4f60\u5df2\u7ecf\u5b8c\u5168\u53d8\u4e86\u53e6\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u906d\uff1a\u201c\u6021\u7ea2\u59d1\u5a18\uff0c\u8fd9\u91cc\u662f\u86c7\u7a74\u72fc\u7a9f\uff0c\u5728\u8fd9\u91cc\uff0c\u5e94\u8be5\u662f\u5f31\u8089\u5f3a\u98df\uff0c\u59d1\u5a18\uff0c\u4f60\u9664\u4e86\u6709\u4e00\u4e2a\u7f8e\u4e3d\u7684\u8eab\u4f53\u4e4b\u5916\uff0c\u6211\u5b9e\u5728\u77a7\u4e0d\u51fa\u4f60\u8fd8\u6709\u522b\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6021\u7ea2\u7a81\u7136\u54af\u54af\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u671b\u7740\u5927\u7b11\u7684\u6021\u7ea2\uff0c\u5185\u5fc3\u4e2d\u5374\u6cdb\u8d77\u4e86\u5bf9\u7389\u5170\u53cc\u59dd\u7684\u4ec7\u6068\u3002 \u3000\u3000\u4e00\u80a1\u5f3a\u70c8\u7684\u6fc0\u52a8\uff0c\u7531\u5fc3\u5e95\u76f4\u6cdb\u4e0a\u6765\u3002 \u3000\u3000\u7a81\u7136\uff0c\u53f6\u957f\u9752\u4f38\u51fa\u4e86\u53f3\u624b\uff0c\u5288\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6021\u7ea2\u6ca1\u60f3\u5230\u4ed6\u4f1a\u7a81\u7136\u51fa\u624b\uff0c\u60f3\u95ea\u907f\u5df2\u81ea\u4e0d\u53ca\u3002 \u3000\u3000\u4f46\u95fb\u7830\u7684\u4e00\u58f0\uff0c\u6021\u7ea2\u88ab\u51fb\u4e2d\u4e00\u638c\u3002\u5f88\u91cd\u7684\u4e00\u51fb\u3002 \u3000\u3000\u6021\u7ea2\u4e00\u4e2a\u8eab\u8eaf\uff0c\u6253\u4e86\u4e24\u4e2a\u65cb\u8f6c\u3002\u4f46\u4ecd\u7136\u65e0\u6cd5\u4fdd\u6301\u4f4f\u8eab\u4f53\u7684\u5e73\u8861\uff0c\u4e00\u8de4\u8dcc\u6454\u5728\u5730\u4e0a\u3002\u79c0\u4e3d\u3001\u5ae9\u7ea2\u7684\u8138\u4e0a\uff0c\u7acb\u523b\u6cdb\u8d77\u4e86\u4e94\u6761\u7ea2\u8272\u7684\u6307\u75d5\uff0c\u5634\u89d2\u95f4\uff0c\u4e5f\u6d41\u51fa\u4e86\u9c9c\u8840\u3002 \u3000\u3000\u5979\u6ca1\u6709\u7acb\u523b\u7ad9\u8d77\u6765\u3002\u4e5f\u8bb8\u8fd9\u4e00\u62f3\u6253\u5f97\u592a\u91cd\u4e86\u4e00\u4e9b\uff0c\u4f7f\u5979\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u6ca1\u6709\u53bb\u6400\u6276\uff0c\u5374\u7f13\u7f13\u5750\u4e0b\u6765\u3002 \u3000\u3000\u4e0d\u77e5\u9053\u8fc7\u4e86\u591a\u5c11\u7684\u65f6\u95f4\uff0c\u6021\u7ea2\u7f13\u7f13\u7ad9\u8d77\u4e86\u8eab\u5b50\uff0c\u62ed\u62ed\u5634\u89d2\u7684\u9c9c\u8840\uff0c\u7406\u4e00\u7406\u6563\u4e71\u7684\u957f\u53d1\uff0c\u6021\u7ea2\u7f13\u6b65\u884c\u5230\u4e86\u53f6\u957f\u9752\u7684\u8eab\u4fa7\u3002 \u3000\u3000\u4e5f\u8bb8\u8fd9\u6beb\u4e0d\u601c\u9999\u60dc\u7389\u7684\u4e00\u638c\uff0c\u6253\u5f97\u6021\u7ea2\u751f\u4e86\u6050\u60e7\uff0c\u6253\u51fa\u4e86\u5979\u5973\u4eba\u7684\u6e29\u67d4\u3002 \u3000\u3000\u5979\u6709\u4e9b\u7f9e\u602f\u7684\u884c\u5230\u4e86\u53f6\u957f\u9752\u7684\u8eab\u4fa7\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u8fd9\u4e00\u638c\u6253\u5f97\u5f88\u91cd\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u8e2a\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u6ca1\u6709\u6655\u8fc7\u53bb\u5417\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u6b7b\uff0c\u6655\u8fc7\u53bb\uff0c\u53c8\u9192\u8fc7\u6765\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1b\u201c\u6021\u7ea2\u59d1\u5a18\uff0c\u6211\u7684\u813e\u6c14\u4e0d\u592a\u597d\uff0c\u6240\u4ee5\u5e0c\u671b\u4f60\u80fd\u5c0f\u5fc3\u4e00\u4e9b\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u9886\u6559\u4e86\u53f6\u5c11\u4fa0\u7684\u5389\u5bb3\u3002\u6211\u7684\u4e0d\u613f\u518d\u88ab\u4f60\u6253\u4e00\u987f\uff0c\u597d\u50cf\u53ea\u6709\u53d8\u5f97\u6e29\u67d4\u4e00\u4e9b\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u5230\uff0c\u4f60\u6021\u7ea2\u59d1\u5a18\uff0c\u7adf\u7136\u4f1a\u662f\u4e00\u4e2a\u8fd9\u6837\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u6211\u53ef\u4e0d\u53ef\u4ee5\u53bb\u68b3\u5986\u4e00\u4e0b\uff0c\u628a\u81ea\u5df1\u6253\u626e\u7684\u597d\u770b\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u770b\u4e0d\u7528\u4e86\uff0c\u6211\u5df2\u7ecf\u89c1\u8bc6\u8fc7\u4f60\u7684\u7f8e\u4e3d\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u5973\u4e3a\u60a6\u5df1\u8005\u5bb9\uff0c\u6211\u8fd9\u6837\u96be\u770b\uff0c\u4f60\u5982\u4f55\u80fd\u548c\u6211\u5904\u4e0a\u4e09\u5929\u5462\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5982\u679c\u6211\u8ba8\u538c\u4f60\uff0c\u4f60\u5c31\u53ef\u4ee5\u7acb\u523b\u8d70\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4f60\u628a\u6211\u7559\u4e0b\u6765\u3002\u53c8\u8981\u64b5\u6211\u8d70\uff01\u6211\u4e3a\u4ec0\u4e48\u4e8b\u4e8b\u90fd\u8981\u542c\u4f60\u7684\uff1f\u201d\u53f6\u957f\u9752\u7f13\u7f13\u62ac\u8d77\u5934\u6765\uff0c\u671b\u4e86\u6021\u7ea2\u2014\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8d70\uff1f\u201d \u3000\u3000\u6021\u7ea2\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u6253\u6b7b\u627e\uff0c\u6211\u4e5f\u4e0d\u8d70\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u539f\u6765\u5973\u4eba\u662f\u5982\u6b64\u5bb9\u6613\u5f81\u670d\u7684\u3002\u201d\u6021\u7ea2\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u88ab\u7389\u5170\u53cc\u59dd\u6240\u64d2\uff0c\u6240\u4ee5\uff0c\u8ba8\u5973\u4eba\u7279\u522b\u7684\u6028\u6068\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u77e5\u9053\u7684\u4e8b\u60c5\u4e0d\u5c11\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6211\u662f\u5821\u4e3b\u7684\u4eb2\u4fe1\u5973\u5a62\uff0c\u6709\u5f88\u591a\u4e8b\uff0c\u90fd\u662f\u7ecf\u6211\u624b\u5904\u7406\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u662f\u5821\u4e3b\u7684\u60c5\u4eba\uff0c\u73a9\u7269\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u522b\u8bf4\u7684\u90a3\u6837\u96be\u542c\uff0c\u4e00\u4e2a\u5f31\u5973\u5b50\uff0c\u6709\u4ec0\u4e48\u53cd\u6297\u7684\u80fd\u529b\uff0c\u5c31\u50cf\u521a\u624d\u4f60\u6253\u6211\u4e00\u6837\uff0c\u6211\u9664\u4e86\u5c48\u670d\u4e4b\u5916\uff0c\u53ea\u6709\u88ab\u4f60\u6d3b\u6d3b\u6253\u6b7b\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u5f88\u6015\u5417\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6b7b\u4ea1\u5bf9\u4e00\u4e2a\u4eba\u6700\u5927\u7684\u5a01\u80c1\u662f\u4e00\u5207\u5316\u4f5c\u4e4c\u6709\uff0c\u6240\u4ee5\uff0c\u5f88\u591a\u4eba\u90fd\u6015\u6b7b\u4ea1\uff0c\u5012\u4e0d\u4e00\u5b9a\u662f\u6015\u751f\u547d\u7684\u7ed3\u675f\uff0c\u800c\u662f\u5f88\u591a\u4e8b\uff0c\u90fd\u56e0\u6b7b\u4ea1\u800c\u65e0\u6cd5\u518d\u4e86\u89e3\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u59d1\u5a18\u662f\u4e00\u4e2a\u5f88\u6709\u89c1\u89e3\u7684\u4eba\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u591a\u8c22\u5938\u5956\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\u559c\u6b22\u4f60\uff0c\u770b\u6765\uff0c\u5e76\u4e0d\u53ea\u662f\u56e0\u4e3a\u4f60\u7684\u7f8e\u4e3d\uff0c\u6021\u7ea2\u9053\uff1a\u201c\u7f8e\u4e3d\u52a0\u4e0a\u806a\u660e\uff0c\u624d\u80fd\u6210\u4e3a\u5821\u4e3b\u7684\u5fc3\u8179\uff0c\u53d7\u5230\u5ba0\u7231\u3002\u6709\u65f6\uff0c\u6211\u4e5f\u6709\u9519\u8bef\uff0e\u4f46\u5ba0\u7231\u4f1a\u4f7f\u4ed6\u5bf9\u6211\u6709\u8f83\u5927\u7684\u5bb9\u5fcd\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4ed6\u5b9e\u5728\u5f88\u559c\u6b22\u4f60\u4e86\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u50cf\u6211\u8fd9\u6837\u7f8e\u4e3d\u53c8\u806a\u660e\u7684\u5973\u5b69\u5b50\uff0c\u4e16\u4e0a\u5b9e\u5728\u4e5f\u4e0d\u591a\uff0c\u53d7\u5230\u4e00\u70b9\u7537\u4eba\u7684\u5ba0\u7231\uff0c\u4e5f\u662f\u5e94\u8be5\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u5728\u4e0b\u60f3\u4e0d\u901a\u7684\u662f\uff0c\u5821\u4e3b\u5982\u6b64\u559c\u7231\u4f60\uff0c\u7adf\u7136\u4f1a\u628a\u4f60\u8d50\u7ed9\u4e86\u6211\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u90a3\u662f\u56e0\u4e3a\u4ed6\u5f88\u770b\u91cd\u4f60\uff0c\u6240\u4ee5\u4f60\u4e00\u5f00\u53e3\uff0c\u4ed6\u5c31\u7b54\u5e94\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\u59d1\u5a18\uff0c\u5728\u4e0b\u5ffd\u7136\u89c9\u5f97\u60c5\u5f62\u6709\u4e9b\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5982\u82e5\u4ed6\u771f\u7684\u5f88\u559c\u6b22\u4f60\uff0c\u600e\u4e48\u4f1a\u524d\u628a\u4f60\u9001\u7ed9\u6211\u5462\uff1f\u5982\u82e5\u662f\u6211\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u628a\u81ea\u5df1\u559c\u6b22\u7684\u5973\u4eba\u9001\u7ed9\u522b\u4eba\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u6c38\u8fdc\u4e0d\u80fd\u505a\u4e00\u4e2a\u9886\u5bfc\u7fa4\u96c4\u7684\u5821\u4e3b\uff0c\u4e3b\u6301\u5927\u5c40\u7684\u4eba\u7269\uff0c\u4f60\u53ef\u80fd\u6210\u4e3a\u4e00\u4e2a\u540d\u524d\u6c5f\u6e56\u7684\u5927\u4fa0\uff0c\u6216\u8005\u505a\u4e00\u4e2a\u4e00\u6d41\u7684\u6740\u624b\uff0c\u56e0\u4e3a\uff0c\u4f60\u6709\u6027\u683c\uff0c\u4e0d\u591f\u9634\u6c89\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5509\uff01\u4f60\u5b9e\u5728\u5f88\u806a\u660e\u3001\u5bf9\u90a3\u4f4d\u5821\u4e3b\u7684\u5e2e\u52a9\u4e5f\u4e00\u5b9a\u5f88\u5927\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u76f8\u5904\u7684\u4e45\u4e00\u70b9\uff0c\u4f60\u4f1a\u53d1\u89c9\u6211\u6709\u66f4\u591a\u7684\u4f18\u70b9\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6de1\u6de1\u4e00\u7b11\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6211\u559c\u4f3a\u4eba\u610f\uff0c\u4f53\u8d34\u5165\u5fae\uff0c\u53ea\u8981\u4f60\u80af\u7ed9\u6211\u673a\u4f1a\uff0c\u6211\u5c31\u80fd\u4f7f\u4f60\u751f\u6d3b\u7684\u5f88\u5feb\u4e50\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u6837\u591a\u7684\u4f18\u70b9\uff0c\u542c\u8d77\u6765\uff0c\u5b9e\u5728\u5f88\u52a8\u4eba\uff0c\u4e0d\u8fc7\uff0c\u6211\u90a3\u4e00\u8033\u5149\uff0c\u6253\u7684\u91cd\u4e86\u4e00\u4e9b\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u7537\u4eba\u90fd\u559c\u6b22\u6253\u626e\u5f97\u5f88\u96c5\u4e3d\u7684\u5973\u4eba\uff0c\u6211\u53bb\u6362\u4ef6\u8863\u670d\uff0c\u68b3\u4f83\u5934\uff0c\u518d\u6765\u966a\u4f60\u559d\u4e00\u70b9\u9152\u3002\u201d \u3000\u3000\u5979\u5fae\u7b11\u7740\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u671b\u7740\u90a3\u67f3\u8170\u8f7b\u626d\uff0c\u7f13\u6b65\u800c\u53bb\u7684\u80cc\u5f71\uff0e\u624d\u53d1\u89c9\uff0c\u8fd9\u5973\u4eba\u4e0d\u4f46\u957f\u7684\u5f88\u7f8e\uff0c\u800c\u4e14\uff0c\u5979\u8d70\u8def\u7684\u59ff\u52bf\u3001\u7adf\u4e5f\u662f\u90a3\u6837\u7684\u52a8\u4eba\u3002 \u3000\u3000\u6021\u7ea2\u5f88\u5feb\u7684\u56de\u6765\uff0c\u6362\u7ecf\u4e86\u4e00\u8eab\u6de1\u7ea2\u7684\u8863\u670d\u3002\u5979\u7684\u8138\u4e0a\u3002\u4e5f\u7ecf\u8fc7\u4e86\u4e00\u756a\u5316\u5986\uff0c\u8584\u65bd\u8102\u7c89\uff0c\u8c08\u626b\u5ce8\u7709\uff0e\u63a9\u53bb\u4e86\u8138\u4e0a\u7684\u4f24\u75d5\u3002 \u3000\u3000\u4e24\u4e2a\u5973\u5a62\u9001\u4e0a\u4e86\u9152\u83dc\uff0c\u5f88\u4e30\u76db\u7684\u83dc\u80b4\u3002 \u3000\u3000\u6021\u7ea2\u5f88\u7ec6\u5fc3\u7684\u62ed\u8fc7\u9152\u676f\u3002\u659f\u6ee1\u4e86\u4e00\u676f\uff0c\u624d\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u8bf7\u5165\u5e2d\u3002\u201d \u3000\u3000\u9152\u83dc\u5c31\u6446\u5728\u5385\u4e2d\u5c0f\u6728\u684c\u4e0a\u3002 \u3000\u3000\u6021\u7ea2\u5728\u5bf9\u9762\u5750\u4e0b\u6765\uff0c\u4e5f\u66ff\u81ea\u5df1\u659f\u4e86\u4e00\u676f\u9152\uff0c\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u5a62\u5b50\u656c\u4f60\u4e00\u676f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u73b0\u5728\u5b9e\u5728\u5f88\u540e\u6094\uff0c\u7adf\u7136\u4f1a\u51fa\u624b\u6253\u4f60\u3002\u201d\u6021\u7ea2\u9053\uff1a\u201c\u4e0d\u8981\u60f3\u8fc7\u53bb\u7684\u4e8b\uff0c\u8fc7\u53bb\u7684\u662f\u662f\u975e\u975e\uff0c\u5df2\u6210\u8fc7\u53bb\uff0c\u6700\u91cd\u8981\u7684\u662f\u73b0\u5728\u548c\u660e\u5929\uff0c\u73b0\u5728\u8981\u505a\u4ec0\u4e48\uff1f\u660e\u5929\u8be5\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e5f\u5e72\u4e86\u9762\u524d\u7684\u9152\u676f\uff0c\u5fc3\u4e2d\u5374\u6697\u6697\u5fd6\u9053\u201c\u8fd9\u4e2b\u5934\uff0c\u5b9e\u5728\u4e0d\u7b80\u5355\uff0c\u8bed\u8bcd\u5e38\u542b\u7384\u673a\uff0c\u96be\u9053\uff0c\u5979\u7528\u5fc3\u5728\u8bd5\u63a2\u6211\u5417\uff1f\u201d \u3000\u3000\u6709\u6b64\u4e00\u5ff5\uff0c\u7acb\u523b\u63d0\u9ad8\u4e86\u8b66\u89c9\u3002 \u3000\u3000\u6021\u7ea2\u5939\u4e86\u4e00\u5757\u9e21\u8089\uff0c\u800c\u4e14\uff0c\u719f\u7ec3\u53c8\u7075\u5de7\u7684\u9664\u53bb\u4e86\u9e21\u9aa8\uff0c\u653e\u5728\u957f\u9752\u9762\u524d\u7684\u74f7\u76d8\u4e2d\u3002 \u3000\u3000\u9e21\u8089\u70e7\u5f97\u5f88\u9c9c\u7f8e\u3002 \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u957f\u9752\uff0c\u4f60\u53ea\u6709\u4e09\u5929\u7684\u65f6\u95f4\uff0c\u6211\u4e0d\u77e5\u9053\u4f60\u8fd9\u4e09\u5929\u4e2d\u5982\u4f55\u5b89\u6392\u81ea\u5df1\uff0c\u662f\u4e0d\u662f\u90fd\u548c\u6211\u6709\u5173\uff1f\u201d \u3000\u3000\u5979\u95ee\u5f97\u5f88\u6280\u5de7\uff0c\u4e5f\u5f88\u542b\u84c4\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u6211\u4e0d\u660e\u767d\u6211\u8fd8\u80fd\u5982\u4f55\u5b89\u6392\u81ea\u5df1\u3002\u201d \u3000\u3000\u6021\u7ea2\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u957f\u9752\uff0c\u4f60\u662f\u6211\u6240\u89c1\u6700\u53d7\u5821\u4e3b\u5173\u5fc3\u7684\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u5821\u4e3b\u66ff\u4f60\u9009\u62e9\u4e86\u4e94\u4e2a\u7f8e\u5973\uff0c\u4e94\u79cd\u4e0d\u540c\u7684\u5178\u578b\uff0c\u6240\u4ee5\uff0c\u9664\u4e86\u6211\u4e4b\u5916\uff0c\u4f60\u8fd8\u6709\u4e94\u4e2a\u7f8e\u5973\u53ef\u4ee5\u9009\u62e9\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u5230\u8fc7\u5f88\u591a\u7684\u5730\u65b9\uff0c\u4e5f\u89c1\u8fc7\u5f88\u591a\u7684\u7f8e\u5973\uff0c\u4f46\u6211\u770b\u4e0a\u7684\uff0c\u53ea\u6709\u59d1\u5a18\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u6021\u7ea2\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u542c\u8d77\u6765\u5f88\u52a8\u4eba\uff0c\u4e0d\u8fc7\uff0c\u6211\u5f88\u4e86\u89e3\u81ea\u5df1\uff0c\u53ea\u662f\u4f60\u7531\u4fa0\u5ba2\u53d8\u4f5c\u4e00\u4e2a\u5251\u624b\u8fc7\u7a0b\u4e2d\u7684\u73a9\u7269\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u600e\u53ef\u5982\u6b64\u7684\u8f7b\u94b1\u81ea\u5df1\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4eba\u8d35\u81ea\u77e5\uff0c\u6211\u77e5\u9053\uff0c\u81ea\u5df1\u4e0d\u8fc7\u662f\u4e00\u4e2a\u6b8b\u82b1\u8d25\u67f3\u7684\u5973\u5b50\u3002\u5821\u4e3b\u8fc7\u53bb\u5bf9\u6211\u7684\u5ba0\u7231\u3002\u4f7f\u6211\u6709\u70b9\u9676\u9189\uff0c\u4f46\u4ed6\u4eca\u5929\u628a\u6211\u9001\u4f60\uff0c\u4f7f\u6211\u5b8c\u5168\u7684\u6e05\u9192\u8fc7\u6765\uff0e\u4e5f\u662f\u6211\u77e5\u9053\u4e86\u81ea\u5df1\u7684\u4ef7\u503c\uff0c\u8bf4\u4e0a\u5929\uff0c\u6211\u4e0d\u8fc7\u662f\u4f60\u4eec\u5fc3\u76ee\u4e2d\u7684\u73a9\u7269\u7f62\u4e86\uff0c\u4f60\u4eec\u559c\u7231\u7684\u53ea\u662f\u6211\u7f8e\u4e3d\uff01\u5bb9\u8272\u3001\u59e3\u597d\u7684\u8eab\u6bb5\uff0c\u6211\u60f3\u4e0d\u51fa\u6765\u6211\u80fd\u4f7f\u4f60\u4eec\u7559\u4e0b\u4ec0\u4e48\u8bb0\u5fc6\uff0e\u66f4\u4e0d\u6562\u5b58\u767d\u9996\u5055\u8001\uff0c\u7ec8\u8eab\u53ae\u5b88\u7684\u5ff5\u5934\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u6021\u7ea2\u59d1\u5a18\u3002\u542c\u8d77\u6765\uff0c\u786e\u5b9e\u662f\u4e00\u4ef6\u60b2\u54c0\u7684\u4e8b\uff0c\u4e0d\u8fc7\uff0c\u4f60\u5982\u60f3\u4e00\u60f3\u6211\u4eec\uff0c\u4e5f\u5c31\u5fc3\u5e73\u6c14\u548c\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e00\u4e2a\u53d7\u547d\u6740\u4eba\u7684\u4eb2\u624b\uff0c\u4ed6\u7684\u751f\u4e0e\u6b7b\uff0c\u597d\u50cf\u5b8c\u5168\u638c\u63e1\u5728\u522b\u4eba\u7684\u624b\u4e2d\uff0c\u5982\u679c\u4ed6\u771f\u7684\u5f88\u559c\u7231\u4f60\u4e86\uff0c\u5c82\u4e0d\u662f\u5bb3\u4e86\u4f60\u3002\u201d \u3000\u3000\u6021\u7ea2\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u957f\u9752\uff0c\u4e00\u4e2a\u5973\u4eba\u3002\u5f88\u671f\u671b\u5f97\u5230\u771f\u5b9e\u7684\u60c5\u7231\uff0c\u5c31\u7b97\u662f\u5f88\u77ed\u6682\uff0c\u4e5f\u6bd4\u6ca1\u6709\u597d\u4e00\u4e9b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u4e0d\u8c08\u8fd9\u4e9b\u4e86\uff0e\u6211\u53ea\u6709\u4e09\u5929\u7684\u65f6\u95f4\uff0c\u4e09\u5929\u4e4b\u540e\uff0c\u4f60\u53ef\u80fd\u4ecd\u662f\u5821\u4e3b\u7684\u5ba0\u59ec\uff0c\u6211\u5462\uff1f\u4e5f\u8bb8\u5df2\u7ecf\u4ed7\u5251\u5343\u91cc\uff0c\u98ce\u5c18\u4ec6\u4ec6\uff0c\u8981\u53bb\u6740\u4e00\u4e2a\u6c5f\u6e56\u4e0a\u6700\u6709\u540d\u6c14\u7684\u4eba\u3002\u201d \u3000\u3000\u6021\u7ea2\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\uff01\u4e5f\u8bb8\u4f7f\u4f60\u4e0d\u5feb\uff0c\u6211\u672c\u6765\u53ea\u662f\u4e00\u4e2a\u4e2b\u5934\uff0c\u662f\u6709\u4e9b\u4e0d\u914d\u548c\u4eba\u5bb6\u8c08\u8fd9\u4e9b\u5927\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u59d1\u5a18\u3002\u4f73\u80b4\u7f8e\u9152\uff0e\u53ea\u5b9c\u8c08\u98ce\u6708\uff0c\u54b1\u4eec\u518d\u5e72\u4e00\u676f\u5427\uff1f\u201d \u3000\u3000 \u3000\u3000\u53f6\u957f\u9752\u559d\u9189\u4e86\uff0c\u9189\u7684\u4eba\u4e8b\u4e0d\u9192\u3002 \u3000\u3000\u9192\u6765\u65f6\uff0c\u53d1\u89c9\u5367\u5728\u7f8e\u4eba\u7684\u6000\u4e2d\u3002 \u3000\u3000\u53f6\u957f\u9752\u8863\u670d\u5c3d\u53bb\uff0c\u4f46\u6021\u7ea2\u5374\u8fd8\u7a7f\u7740\u7d27\u8eab\u7684\u7f57\u886b\u3002 \u3000\u3000\u5c31\u662f\u90a3\u4e48\u7684\u5584\u89e3\u4eba\u610f\uff0c\u53f6\u957f\u9752\u4e00\u7741\u5f00\u773c\uff0c\u6021\u7ea2\u5df2\u7ecf\u4f38\u51fa\u96ea\u767d\u7684\u7389\u81c2\uff0c\u53d6\u8fc7\u6765\u4e00\u7897\u9192\u9152\u7684\u6d53\u8336\u3002 \u3000\u3000\u5df2\u7ecf\u653e\u51c9\u7684\u8336\u3002 \u3000\u3000\u559d\u4e0b\u4e00\u7897\u8336\u3002\u53f6\u957f\u9752\u6447\u6447\u5934\uff0c\u6062\u590d\u4e86\u6e05\u9192\u7684\u795e\u5fd7\uff0c\u9053\uff1a\u201c\u6211\u9189\u4e86\u591a\u4e45\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4e24\u591c\u4e00\u5929\uff0c\u73b0\u5728\u5927\u7ea6\u662f\u4e09\u66f4\u65f6\u5206\uff0c\u4f60\u4e09\u5929\u7684\u65f6\u95f4\uff0c\u5df2\u7ecf\u8fc7\u4e86\u4e00\u534a\u3002\u201d\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u5c31\u8fd9\u6837\u966a\u4e86\u6211\u4e24\u591c\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u53ea\u6709\u4e00\u591c\u534a\uff0c\u56e0\u4e3a\uff0c\u7b2c\u4e00\u591c\uff0c\u4f60\u9189\u7684\u592a\u5389\u5bb3\uff0c\u6211\u4e00\u76f4\u5728\u5e8a\u65c1\u5b88\u7740\u4f60\uff0c\u8fd9\u4e00\u591c\uff0c\u4f60\u5168\u6ca1\u6709\u9192\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u5401\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u9152\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u90a3\u662f\u5c01\u5b58\u4e86\u4e8c\u5341\u5e74\u7684\u5973\u513f\u7ea2\uff0c\u9152\u5473\u9999\u9187\uff0c\u9189\u8d77\u6765\uff0c\u53ef\u4ee5\u9189\u4e0a\u4e09\u5929\u4e09\u591c\uff0c\u4f60\u7684\u5e95\u5b50\u597d\uff0c\u6240\u4ee5\u9192\u7684\u65e9\u4e00\u4e9b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e5f\u8bb8\uff0c\u5821\u4e3b\u65e9\u5df2\u7ecf\u7b97\u597d\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u521d\u66f4\u65f6\u5206\uff0c\u4ed6\u6765\u770b\u8fc7\u4f60\u3002\u201d\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u5982\u662f\u9189\u4e0a\u4e86\u4e09\u5929\u4e09\u591c\uff0c\u90a3\u4e0d\u662f\u4fdd\u5168\u4e01\u4ed6\u7684\u5ba0\u59ec\u5417\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u5509\uff01\u8fd9\u548c\u4f60\u5f97\u5230\u6211\uff0c\u6709\u4ec0\u4e48\u4e0d\u540c\uff0c\u7f57\u8966\u534a\u89e3\uff0c\u5e76\u6795\u540c\u7720\uff0c\u6211\u5fc3\u5e95\u5df2\u7ecf\u627f\u8ba4\u4f8d\u5019\u8fc7\u4f60\uff0c\u201d\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\uff01\u6211\u8981\u5f97\u5230\uff0c\u5b8c\u5b8c\u5168\u5168\u7684\u5f97\u5230\uff0c\u5982\u82e5\u53ea\u662f\u5047\u51e4\u865a\u51f0\uff0c\u6211\u53c8\u4f55\u5fc5\u8981\u5821\u4e3b\u628a\u4eba\u8d50\u7ed9\u6211\u5462\uff1f\u201d \u3000\u3000\u6021\u7ea2\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u73b0\u6211\u5c31\u5728\u4f60\u7684\u8eab\u8fb9\uff0c\u4f60\u4e00\u52a8\u624b\uff0c\u5c31\u53ef\u4ee5\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\uff01\u6211\u8981\u4f60\u81ea\u5df1\u8131\u4e0b\u7f57\u886b\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4f60\u597d\u50cf\u4ecd\u7136\u5b58\u5fc3\u5728\u6298\u817e\u6211\u3002\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\uff0c\u53cc\u624b\u5374\u672a\u505c\u4e0b\u5c3d\u9664\u8eab\u4e0a\u7684\u8863\u670d\u3002 \u3000\u3000\u4e00\u4e2a\u7f8e\u4e3d\u7edd\u4f26\u7684\u80f4\u4f53\uff0c\u5448\u73b0\u5728\u53f6\u957f\u9752\u7684\u9762\u524d\u3002 \u3000\u3000\u5979\u8138\u4e0a\u6709\u4e00\u80a1\u65e0\u53ef\u5948\u4f55\u7684\u54c0\u6028\uff0c\u518d\u52a0\u4e0a\u4e09\u5206\u5a07\u7f9e\uff0c\u4f7f\u4eba\u751f\u51fa\u4e00\u79cd\u51c4\u695a\u7684\u601c\u60dc\u3002 \u3000\u3000\u53f6\u957f\u9752\u671b\u7740\u5979\uff0c\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u4f60\u662f\u4e0d\u662f\u89c9\u5f97\u5f88\u59d4\u5c48\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4e0d\uff01\u6211\u6ca1\u6709\u59d4\u5c48\u7684\u611f\u89c9\uff0c\u53ea\u662f\uff0c\u89c9\u5f97\u4f60\u7684\u4f5c\u6cd5\u592a\u751f\u786c\uff0c\u55ef\uff0c\u4f60\u522b\u751f\u6c14\uff0c\u5a62\u5b50\u624d\u6562\u76f4\u8bf4\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u597d\uff01\u4f60\u8bf4\u5427\uff01\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u90a3\u79cd\u601c\u9999\u60dc\u738b\u7684\u4eba\uff0e\u770b\u8d77\u6765\u3001\u4f60\u597d\u50cf\u4e00\u70b9\u4e5f\u4e0d\u89e3\u98ce\u60c5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\u59d1\u5a18\uff0c\u8fc7\u53bb\u6211\u672c\u5c31\u4e0d\u662f\u4e2a\u98ce\u6d41\u4eba\u7269\uff0c\u6240\u4ee5\uff0c\u662f\u6709\u4e9b\u4e0d\u89e3\u98ce\u60c5\u3002\u201d \u3000\u3000\u6021\u7ea2\u9010\u6e10\u7684\u653e\u5f00\u4e86\u81ea\u5df1\uff0c\u7f13\u7f13\u4f38\u51fa\u53cc\u81c2\uff0c\u62b1\u4f4f\u4e86\u53f6\u957f\u9752\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e00\u626c\u624b\uff0c\u7184\u53bb\u4e86\u684c\u6848\u7684\u70db\u706b\u3002 \u3000\u3000 \u3000\u3000\u53f6\u957f\u9752\u9192\u6765\u65f6\uff0c\u5929\u5df2\u5927\u4eae\uff0c\u6021\u7ea2\u5df2\u7ecf\u66ff\u53f6\u957f\u9752\u51c6\u5907\u597d\u4e86\u6f31\u6d17\u7684\u6c34\uff0c\u548c\u4e30\u5bcc\u7684\u65e9\u9910\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u518d\u6765\u6253\u6405\u4ed6\u4eec\u3002\u4ed6\u4eec\u5e73\u9759\u7684\u751f\u6d3b\u4e86\u4e24\u5929\u3002 \u3000\u3000\u7b2c\u4e09\u5929\u4e2d\u5348\u65f6\u5206\uff0c\u590f\u6740\u8d76\u5230\u4e86\u8fd9\u5ea7\u5145\u6ee1\u6625\u610f\u7684\u5c0f\u5ba4\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u660e\u767d\uff0c\u53e6\u4e00\u4e2a\u65b0\u7684\u751f\u6d3b\u5373\u5c06\u5f00\u59cb\u3002 \u3000\u3000\u590f\u6740\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u5144\uff0c\u5144\u5f1f\u66ff\u4f60\u591a\u7559\u4e86\u534a\u5929\u65f6\u95f4\uff0c\u4e0d\u8fc7\uff0c\u54b1\u4eec\u4eca\u591c\u4e00\u5b9a\u8981\u5230\u6307\u5b9a\u7684\u5730\u65b9\u62a5\u5230\uff0c\u518d\u665a\uff0c\u5929\u9ed1\u4e4b\u524d\u5c31\u8d76\u4e0d\u5230\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u671b\u671b\u6302\u5728\u58c1\u4e0a\u7684\u957f\u5251\uff0c\u9053\uff1a\u201c\u591a\u8c22\u590f\u5144\uff0c\u54b1\u4eec\u51e0\u65f6\u52a8\u8eab\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u73b0\u5728\uff0c\u5feb\u9a6c\u5df2\u5728\u5916\u9762\u7b49\u5019\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7f13\u7f13\u53d6\u4e0b\u58c1\u4e0a\u7684\u957f\u5251\uff0c\u56de\u987e\u4e86\u6021\u7ea2\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u8c22\u8c22\u4f60\u8fd9\u51e0\u5929\u5bf9\u6211\u7167\u987e\uff0c\u4e5f\u62b1\u6b49\u3002\u90a3\u4e00\u5929\u6253\u4f24\u4e86\u4f60\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u7237\uff0c\u4e0d\u8981\u8fd9\u6837\u8bf4\uff0c\u8fd9\u51e0\u5929\uff0c\u6211\u6ca1\u6709\u592a\u5c3d\u5fc3\u610f\u3001\u6709\u4ec0\u4e48\u7167\u987e\u4e0d\u5230\u7684\u5730\u65b9\uff0c\u7237\u4e0d\u8981\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u4eca\u65e5\u4e00\u522b\u3002\u4e0d\u77e5\u54b1\u4eec\u4f55\u65e5\u624d\u80fd\u518d\u89c1\uff0c\u4f60\u73cd\u91cd\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u53f9\u606f\u4e00\u58f0\uff0e\u9053\uff1a\u201c\u7237\uff0c\u4f46\u613f\u518d\u6709\u76f8\u89c1\u65e5\uff0c\u6055\u5a62\u5b50\u4e0d\u9001\u4f60\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u7adf\u662f\u5982\u6b64\u591a\u60c5\u7684\u79cd\u5b50\uff0c\u8d70\uff01\u529e\u5b8c\u4e86\u8fd9\u6863\u4e8b\u540e\uff0c\u60f3\u6cd5\u5b50\u2026\u2026\u201d \u3000\u3000\u6021\u7ea2\u8f7b\u8f7b\u53f9\u81ea\u4e00\u58f0\uff0c\u5782\u4e0b\u4e86\u5934\u3002 \u3000\u3000 \u3000\u3000\u8fd9\u662f\u4e00\u7247\u6d53\u5bc6\u7684\u6811\u6797\uff0c\u53f6\u957f\u9752\u8fd9\u4e09\u5929\u4e00\u76f4\u6ca1\u6709\u79bb\u5f00\u8fc7\u90a3\u5ea7\u96c5\u81f4\u7684\u623f\u820d\uff0c\u90a3\u623f\u820d\u5c31\u5efa\u5728\u6797\u8fb9\u5c71\u5761\u4e0a\u3002 \u3000\u3000\u5728\u4ed6\u7684\u60f3\u50cf\u4e2d\uff0c\u81ea\u5df1\u5e94\u8be5\u4f4f\u5728\u4e00\u5ea7\u5927\u5821\u4e2d\uff0c\u90a3\u8be5\u662f\u4e00\u7247\u5e7f\u5927\u76f8\u8fde\u7684\u623f\u820d\uff1b\u6212\u5907\u68ee\u4e25\uff0c\u4f46\u5374\u672a\u60f3\u5230\u4ed6\u5728\u7684\u5730\u65b9\uff0c\u7adf\u7136\u662f\u4e00\u5ea7\u5b64\u7acb\u5c71\u5761\u4e0a\u7684\u5c0f\u5de7\u77f3\u5c4b\u3002 \u3000\u3000\u590f\u6740\u5e26\u8def\uff0c\u7a7f\u8fc7\u6811\u6797\uff0c\u5230\u4e86\u4e00\u6761\u5e7f\u9614\u7684\u5b98\u9053\u4e0a\u3002 \u3000\u3000\u5b98\u9053\u4e0a\uff0c\u65e9\u5df2\u6709\u4e24\u5339\u5907\u597d\u7684\u5065\u9a6c\u5728\u7b49\u5019\u3002 \u3000\u3000\u4e24\u5339\u5065\u9a6c\u90fd\u6ca1\u6709\u4eba\u7275\u62c9\uff0c\u4e24\u5339\u9a6c\u5374\u90fd\u62f4\u5728\u4e00\u68f5\u5c0f\u6811\u4e0a\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u5ffd\u7136\u5347\u8d77\u4e86\u4e00\u7f15\u5f88\u9ad8\u7684\u8b66\u89c9\uff0c\u5fc3\u4e2d\u6697\u6697\u5fd6\u9053\uff1a\u201c\u597d\u4e25\u5bc6\u7684\u7ec4\u5408\uff0c\u6211\u89c1\u7684\u90a3\u4f4d\u5821\u4e3b\uff0c\u6015\u4e5f\u4e0d\u662f\u771f\u7684\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u89e3\u5f00\u4e86\u62f4\u5728\u6811\u4e0a\u7684\u9a6c\u7f30\uff0c\u7b11\u9053\uff1a\u201c\u8d70\u5427\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u590f\u5144\uff0c\u8fd9\u4e24\u5339\u9a6c\u662f\u4e0d\u662f\u7ed9\u54b1\u4eec\u51c6\u5907\u7684\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4e0d\u60dc\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u89c1\u9001\u9a6c\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u9a6c\u5df2\u7ecf\u9001\u5230\u4e86\uff0c\u4eba\u53c8\u4f55\u5fc5\u7559\u4e0b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u98de\u8eab\u4e0a\u9a6c\uff0c\u9053\uff1a\u201c\u590f\u5144\uff0c\u54b1\u4eec\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4e0d\u592a\u8fdc\uff0c\u4ee5\u8fd9\u4e24\u5339\u5065\u9a6c\u7684\u811a\u7a0b\uff0c\u5927\u7ea6\u5929\u9ed1\u8fd9\u524d\u5c31\u53ef\u4ee5\u8d76\u5230\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u4e86\u70b9\u5934\uff0c\u672a\u518d\u591a\u95ee\u3002 \u3000 \u7b2c \u4e8c \u56de\u3000\u5723\u6cc9\u9547 \u3000 \u3000\u3000\u4e24\u5339\u5065\u9a6c\u5954\u9a70\u5728\u5927\u9053\u4e0a\u3002 \u3000\u3000\u5929\u5230\u66ae\u8272\u65f6\u5206\uff0c\u4e24\u4eba\u5230\u4e86\u4e00\u5ea7\u5c0f\u9547\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e00\u8def\u7559\u5fc3\uff0c\u8bb0\u4e0b\u4e86\u7ecf\u8fc7\u7684\u5730\u65b9\u5f62\u52bf\u3002 \u3000\u3000\u5065\u9a6c\u5982\u98de\uff0c\u4e00\u8def\u5954\u9a70\uff0c\u7adf\u7136\u672a\u66fe\u505c\u8fc7\u3002 \u3000\u3000\u8fd9\u4e24\u5339\u9a6c\u867d\u7136\u662f\u5065\u58ee\u5f02\u5e38\uff0c\u4f46\u4e00\u4e2a\u4e0b\u5348\u4e0d\u505c\u7684\u5954\u8dd1\uff0c\u5230\u4e86\u5c0f\u9547\u65f6\uff0c\u4e5f\u5df2\u7ecf\u5927\u6c57\u6dcb\u6f13\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e00\u76f4\u5fcd\u7740\u6ca1\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u591a\u8bf4\u4e00\u53e5\u8bdd\uff0c\u5c31\u53ef\u80fd\u4f7f\u590f\u6740\u5bf9\u81ea\u5df1\u591a\u4e00\u5206\u4e86\u89e3\u3002 \u3000\u3000\u4f46\u8fdb\u4e01\u8fd9\u5ea7\u5c0f\u9547\u540e\uff0c\u53f6\u957f\u9752\u5b9e\u5728\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u590f\u5144\uff0c\u54b1\u4eec\u662f\u4e0d\u662f\u5e94\u8be5\u5728\u8fd9\u91cc\u4f11\u606f\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff01\u56e0\u4e3a\u54b1\u4eec\u5df2\u7ecf\u5230\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u6a2a\u5728\u57ce\u95e8\u4e0a\u7684\u4e00\u5757\u6a2a\u533e\u4e0a\uff0c\u5199\u7740\u201c\u5723\u6cc9\u9547\u201d\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u7acb\u523b\u9707\u52a8\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u5ea7\u5c0f\u9547\uff0c\u4e00\u5ea7\u9ed8\u9ed8\u65e0\u95fb\u7684\u5c0f\u9547\uff0c\u4f46\u56e0\u4e3a\u8fd9\u91cc\u6709\u4e00\u9053\u5723\u6cc9\uff0c\u800c\u540d\u566a\u5929\u4e0b\uff0c\u5929\u4e0b\u95fb\u540d\u7684\u5723\u6cc9\u9547\u3002 \u3000\u3000\u90a3\u662f\u4e00\u9053\u5145\u6ee1\u7740\u4f20\u5947\u7684\u6cc9\u6c34\uff0c\u8bba\u65a4\u8ba1\u4ef7\uff0c\u4f46\u5374\u53d6\u4e4b\u4e0d\u6613\u3002 \u3000\u3000\u5723\u6cc9\u53ec\u6765\u4e86\u5f88\u591a\u7684\u4eba\uff0c\u4e5f\u66ff\u8fd9\u5ea7\u5c0f\u9547\u5e26\u6765\u4e86\u540d\u6c14\u3001\u7e41\u8363\u3002 \u3000\u3000\u8fd9\u91cc\u672c\u6765\u662f\u4e2a\u53ea\u6709\u56db\u4e94\u5bb6\u4eba\u7684\u5c0f\u6751\u843d\uff0c\u4f46\u5374\u56e0\u4e3a\u6709\u4e86\u5723\u6cc9\uff0c\u9010\u6e10\u7684\u53d8\u6210\u4e86\u4e00\u5ea7\u5c0f\u9547\u3002 \u3000\u3000\u56db\u4e94\u6237\u7684\u4eba\u5bb6\uff0c\u4e5f\u4e91\u96c6\u6210\u4e09\u767e\u591a\u6237\u7684\u5c45\u6c11\u3002 \u3000\u3000\u8fd9\u4e09\u767e\u591a\u6237\u7684\u5c45\u6c11\uff0c\u5c45\u7136\u6709\u4e8c\u5341\u5bb6\u5ba2\u6808\uff0c\u56db\u5341\u5bb6\u9152\u9986\u3002 \u3000\u3000\u8fd9\u91cc\u7684\u5ba2\u6808\uff0c\u4e0d\u4f46\u8bbe\u5907\u5f88\u597d\uff0c\u800c\u4e14\u4ef7\u94b1\u4e5f\u76f8\u5f53\u7684\u597d\u3002 \u3000\u3000\u66f4\u5947\u602a\u7684\u662f\uff0c\u8fd9\u91cc\u7684\u5ba2\u6808\uff0c\u90fd\u4e0d\u5f88\u5927\u3001\u6bcf\u4e00\u5bb6\u5ba2\u6808\uff0c\u53ea\u6709\u4e09\u56db\u95f4\u5ba2\u623f\uff0c\u4f46\u6bcf\u4e00\u4e2a\u5ba2\u623f\uff0c\u90fd\u5e03\u7f6e\u5f97\u5f88\u8c6a\u534e\u3002 \u3000\u3000\u6bcf\u4e00\u5bb6\u7684\u9152\u9986\u4e5f\u90fd\u4e0d\u592a\u5927\uff0c\u53ea\u6709\u4e00\u95f4\u5c0f\u578b\u7684\u5385\u623f\uff0c\u6446\u4e86\u4e24\u4e09\u5f20\u684c\u5b50\u3002\u53e6\u5916\uff0c\u6709\u4e00\u95f4\u96c5\u5ba4\u3002 \u3000\u3000\u5c0f\u5385\u4e0a\u7684\u51e0\u5f20\u684c\u5b50\u662f\u5356\u7ed9\u4e00\u822c\u5ba2\u4eba\u5403\u7684\uff0c\u90a3\u95f4\u8c6a\u534e\u7684\u96c5\u5ba4\uff0c\u5374\u662f\u5e03\u7f6e\u7684\u5341\u5206\u9ad8\u8d35\u3002 \u3000\u3000\u8fd9\u5c0f\u9547\u4e0a\u7684\u4eba\u5927\u90fd\u5728\u5385\u4e2d\u968f\u4fbf\u559d\u4e24\u676f\uff0c\u5f88\u5c11\u5230\u90a3\u95f4\u96c5\u5ba4\u4e2d\u53bb\uff0c\u56e0\u4e3a\uff0c\u90a3\u95f4\u96c5\u5ba4\uff0c\u6536\u8d39\u5f88\u6602\u8d35\u3002 \u3000\u3000\u8fd9\u5ea7\u5723\u6cc9\u9547\uff0c\u5c31\u662f\u8fd9\u4e48\u4e00\u5ea7\u5947\u602a\u7684\u5c0f\u9547\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5723\u6cc9\u9547\u7684\u751f\u610f\uff0c\u5e76\u4e0d\u600e\u4e48\u597d\u3002\u5927\u90e8\u5206\u7684\u5ba2\u6808\uff0c\u90fd\u8fd8\u6302\u7740\u706f\u7b3c\u3002 \u3000\u3000\u90a3\u662f\u5728\u62db\u63fd\u987e\u5ba2\uff0c\u81f3\u5c11\u662f\u4ed6\u4eec\u7684\u5ba2\u6808\uff0c\u8fd8\u53ef\u4ee5\u63a5\u7eb3\u5ba2\u4eba\u3002 \u3000\u3000\u56db\u5341\u5bb6\u9152\u9986\u7684\u751f\u610f\u4e5f\u4e0d\u592a\u597d\uff0c\u53ea\u6709\u4e03\u516b\u5bb6\u7684\u9152\u9986\u6709\u751f\u610f\u3002 \u3000\u3000\u53f6\u957f\u9752\u542c\u8fc7\u5723\u6cc9\u9547\u7684\u4f20\u8bf4\uff0c\u53ef\u60dc\uff0c\u4ed6\u6ca1\u6709\u5230\u8fc7\u8fd9\u4e2a\u5730\u65b9\u3002 \u3000\u3000\u9152\u9986\u548c\u5ba2\u6808\u7684\u524d\u9762\uff0c\u90fd\u9ad8\u6311\u7740\u706f\u7b3c\uff0c\u6240\u4ee5\uff0c\u5165\u591c\u4e4b\u540e\uff0c\u8fd9\u5ea7\u5c0f\u9547\u8857\u4e0a\u7684\u706f\u5149\uff0c\u5012\u662f\u4e00\u7247\u8f89\u714c\u3002 \u3000\u3000\u590f\u6740\u5e26\u7740\u53f6\u957f\u9752\uff0c\u4e5f\u5728\u4e00\u95f4\u5ba2\u6808\u524d\u505c\u4e0b\u3002 \u3000\u3000\u53f6\u957f\u9752\u62ac\u5934\u770b\u4e86\u4e00\u773c\uff0c\u53d1\u89c9\u8fd9\u5bb6\u5ba2\u6808\u5e97\u540d\u53eb\u4f5c\u5e73\u5b89\u3002 \u3000\u3000\u4e00\u4e2a\u5e97\u5c0f\u4e8c\u8fce\u51fa\u6765\uff0c\u63a5\u53bb\u4e24\u5339\u9a6c\u3002 \u3000\u3000\u8fd9\u5bb6\u5ba2\u4e0d\u5927\uff0c\u4f46\u8bbe\u5907\u5f88\u9f50\u5168\uff0c\u8fd9\u6837\u5c0f\u7684\u5ba2\u6808\uff0c\u7adf\u7136\u8fd8\u6709\u9a6c\u623f\u3002 \u3000\u3000\u53f6\u957f\u9752\u548c\u590f\u6740\u88ab\u5e26\u5165\u4e24\u95f4\u5e76\u5217\u7684\u5ba2\u623f\u5185\u3002 \u3000\u3000\u5ba2\u623f\u76f8\u5f53\u5bbd\u5927\uff0c\u800c\u4e14\u6a80\u6728\u96d5\u82b1\u5e8a\uff0c\u767d\u7eb1\u5782\u5e10\uff0c\u8fd8\u6709\u4e00\u4e2a\u5f88\u5927\u7684\u5986\u53f0\u3002 \u3000\u3000\u53f6\u957f\u9752\u53d1\u89c9\u4e86\u53e6\u4e00\u4ef6\u5947\u602a\u7684\u4e8b\u60c5\uff0c\u5c31\u662f\u4ed6\u548c\u590f\u6740\u8fdb\u5165\u5ba2\u6808\u4e4b\u540e\uff0c\u5ba2\u6808\u53d6\u4e0b\u4e86\u706f\u7b3c\uff0c\u63a9\u4e0a\u4e86\u5e97\u95e8\uff0c\u4ed6\u4eec\u5bf9\u63a5\u4e0b\u8fd9\u4e24\u4e2a\u5ba2\u4eba\uff0c\u4f3c\u662f\u5df2\u5f88\u7792\u8db3\uff0c\u4e0d\u518d\u63a5\u522b\u7684\u5ba2\u4eba\u3002 \u3000\u3000\u590f\u6740\u56de\u987e\u4e86\u5e26\u8def\u7684\u5e97\u4f19\u8ba1\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5c0f\u4e8c\uff0c\u66ff\u6211\u4eec\u51c6\u5907\u5403\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u6b20\u8eab\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u5403\u7684\u4e1c\u897f\u5012\u6709\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u4e0d\u5356\u9152\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6709\u83dc\u65e0\u9152\uff0c\u8fd9\u662f\u54ea\u91cc\u7684\u89c4\u77e9\uff1f\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u8fd9\u5723\u6cc9\u9547\u7684\u89c4\u77e9\uff0c\u548c\u522b\u5904\u4e0d\u540c\uff0c\u6211\u4eec\u8fd9\u91cc\u662f\u4e0d\u5356\u9152\uff0c\u8fd9\u91cc\u6709\u56db\u5341\u5bb6\u9152\u9986\uff0c\u6211\u4eec\u4e4b\u95f4\uff0c\u6709\u4e00\u79cd\u534f\u8bae\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u7b11\u4e00\u58f0\uff0c\u6b63\u5f85\u53d1\u4f5c\uff0c\u590f\u6740\u5374\u5df2\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u9ec4\u6cc9\u8def\u4e0a\u5948\u6cb3\u6865\u2026\u2026\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u671b\u4e61\u53f0\u4e0b\u9633\u5173\u9053\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u6211\u4eec\u5148\u5728\u8fd9\u95f4\u5ba2\u623f\u91cc\u5750\uff0c\u53bb\u53eb\u4f60\u4eec\u638c\u67dc\u7684\u6765\u3002\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u4e00\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u662f\uff01\u5c0f\u7684\u8fd9\u5c31\u53bb\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u66ff\u6211\u4eec\u51c6\u5907\u516d\u4e2a\u4e0b\u9152\u7684\u597d\u83dc\uff0c\u987a\u4fbf\u627e\u4e00\u58f6\u9152\u3001\u4e8c\u5341\u5f20\u6cb9\u997c\u3002\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u5c0f\u7684\u8fd9\u5c31\u53bb\u51c6\u5907\uff0c\u4e24\u4f4d\u8bf7\u5750\u3002\u201d \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u4e00\u4e2a\u8eab\u7740\u9752\u886b\u7684\u6c49\u5b50\uff0c\u7f13\u6b65\u884c\u4e86\u8fdb\u6765\uff0c\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u627e\u6211\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4f60\u662f\u638c\u67dc\u7684\u3002\u201d \u3000\u3000\u9752\u886b\u4e2d\u5e74\u6c49\u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u5723\u6cc9\u9547\u7684\u638c\u67dc\u7684\u3002\u201d \u3000\u3000\u9152\u83dc\u9001\u4e0a\u6765\u4e86\uff0c\u516d\u4e2a\u83dc\uff0c\u4e00\u58f6\u4e0a\u597d\u6c7e\u9152\u3002 \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u638c\u67dc\u7684\u63a5\u5230\u4e86\u4ee4\u8c15\u5417\uff1f\u201d \u3000\u3000\u9752\u886b\u4eba\u70b9\u70b9\u5934\uff0c\u4f38\u624b\u4ece\u8170\u4e2d\u638f\u51fa\u4e00\u4e2a\u94dc\u724c\uff0c\u90a3\u94dc\u724c\u53ea\u6709\u534a\u4e2a\uff0c\u800c\u4e14\u4e0a\u9762\u8fd8\u6709\u53f7\u7801\u3002 \u3000\u3000\u590f\u6740\u4e5f\u4ece\u53e3\u888b\u4e2d\u53d6\u51fa\u4e00\u4e2a\u94dc\u724c\u3002 \u3000\u3000\u4e24\u5757\u94a2\u724c\u62fc\u5728\u4e00\u5904\uff0c\u4e1d\u4e1d\u5165\u6263\uff0c\u7adf\u7136\u662f\u4e00\u5757\u94dc\u724c\u5207\u6210\u4e86\u4e24\u7247\u3002 \u3000\u3000\u5bf9\u5728\u4e00\u5904\uff0c\u5b57\u8ff9\u5b9b\u7136\uff0c\u94dc\u724c\u4e0a\u5199\u7684\u662f\u5341\u4e8c\u53f7\u3002 \u3000\u3000\u9752\u886b\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u53eb\u5f20\u73de\u2026\u2026\u201d\u76ee\u5149\u4e00\u63a0\u53f6\u957f\u9752\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e00\u4f4d\u662f\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e03\u5251\u8ffd\u9b42\u3002\u201d \u3000\u3000\u5f20\u73de\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e03\u5251\u8ffd\u9b42\u53f6\u957f\u9752\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u8981\u53eb\u6211\u53f6\u957f\u9752\uff0c\u53f6\u957f\u9752\u5df2\u7ecf\u6b7b\u4e86\uff0c\u73b0\u5728\u6d3b\u7740\u7684\u662f\u4e03\u5251\u8ffd\u9b42\uff0c\u4e00\u4e2a\u6740\u624b\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u597d\uff01\u6211\u4eec\u4e5f\u4e0d\u9700\u8981\u53f6\u957f\u9752\uff0c\u6211\u4eec\u8981\u7684\u662f\u4e03\u5251\u8ffd\u9b42\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5f20\u5144\uff0c\u80fd\u4e0d\u80fd\u5148\u544a\u8bc9\u6211\u4eec\uff0c\u8981\u6740\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u4e24\u4f4d\u5148\u559d\u9152\uff0c\u54b1\u4eec\u6162\u6162\u7684\u8c08\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u6700\u597d\u5148\u8bf4\u660e\u767d\u3002\u201d \u3000\u3000\u5f20\u73de\u53cc\u76ee\u76ef\u6ce8\u53f6\u957f\u9752\u7684\u8138\uff0c\u770b\u4e86\u4e00\u9635\u624d\u9053\uff1a\u201c\u4e03\u5251\u8ffd\u9b42\uff0c\u4f60\u5e94\u8be5\u5148\u660e\u767d\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u5723\u6cc9\u9547\u662f\u4e00\u4e2a\u5145\u6ee1\u7740\u795e\u79d8\uff0c\u5f88\u6709\u4e9b\u602a\u89c4\u77e9\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u8fd9\u5730\u65b9\u867d\u7136\u662f\u6709\u4e9b\u8be1\u5f02\uff0c\u4e0d\u8fc7\uff0c\u5982\u679c\u9601\u4e0b\u80fd\u7ed9\u54b1\u4eec\u89e3\u91ca\u4e00\u4e0b\uff0c\u4e5f\u53ef\u4ee5\u4f7f\u54b1\u4eec\u4e00\u5e7f\u89c1\u95fb\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u5f88\u591a\u7684\u5ba2\u6808\uff0c\u4f46\u6bcf\u4e00\u5bb6\u90fd\u5f88\u5c0f\uff0c\u5e03\u7f6e\u5374\u53c8\u5341\u5206\u8c6a\u534e\uff0c\u4f60\u662f\u4e0d\u662f\u89c9\u5f97\u5f88\u5947\u602a\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u8fd8\u6709\u5f88\u591a\u7684\u5c0f\u9152\u9986\uff0c\u5e03\u7f6e\u7684\u4e5f\u5f88\u8c6a\u534e\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5bf9\uff01\u8fd9\u6837\u7684\u5ba2\u6808\u3001\u9152\u9986\uff0c\u9664\u4e86\u5723\u6cc9\u9547\uff0c\u53ea\u6050\u6015\u522b\u7684\u5730\u65b9\uff0c\u518d\u4e5f\u627e\u4e0d\u5230\u4e86\u3002\u201d \u3000\u3000\u5f20\u73de\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u4e03\u5251\u8ffd\u9b42\u8d70\u8fc7\u4e86\u4e0d\u5c11\u7684\u5730\u65b9\uff0c\u5927\u6982\u4e5f\u660e\u767d\uff0c\u8fd9\u4e9b\u60c5\u5f62\u90a3\u662f\u56e0\u4e3a\u6709\u6b64\u9700\u8981\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u5b50\u7684\u5ba2\u4eba\uff0c\u624d\u4f1a\u4f4f\u8fd9\u6837\u7684\u5ba2\u6808\u5462\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u4eba\uff0c\u800c\u4e14\u662f\u5404\u6000\u9b3c\u80ce\u7684\u6b66\u6797\u4e2d\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u4eec\u4e3a\u4e86\u8981\u4fdd\u62a4\u81ea\u5df1\u7684\u9690\u79d8\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u5bf9\uff01\u5230\u8fd9\u91cc\u7684\u4eba\uff0c\u90fd\u6709\u81ea\u5df1\u7684\u9690\u79d8\uff0c\u800c\u4e14\uff0c\u4ed6\u4eec\u8fd9\u4e9b\u9690\u79d8\uff0c\u90fd\u4e0d\u5e0c\u671b\u522b\u7684\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6765\u5230\u8fd9\u5723\u6cc9\u9547\u7684\u4eba\uff0c\u96be\u9053\u6bcf\u4e00\u4e2a\u4eba\u90fd\u6709\u4e00\u4e9b\u9690\u79d8\u5417\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1b\u201c\u81f3\u5c11\uff0c\u4ed6\u4eec\u81ea\u5df1\u7684\u611f\u89c9\u662f\u5982\u6b64\uff0c\u5230\u8fd9\u91cc\u7684\u4eba\uff0c\u6bcf\u4e00\u4e2a\u4eba\uff0c\u90fd\u89c9\u5f97\u81ea\u5df1\u6709\u4e00\u4e2a\u522b\u4eba\u4e0d\u77e5\u9053\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c31\u8fd9\u6837\uff0c\u9020\u6210\u4e86\u8fd9\u5730\u65b9\u7684\u795e\u79d8\u2026\u2026\u201d \u3000\u3000\u5f20\u73de\u63a5\u9053\uff1a\u201c\u8fd9\u53ea\u662f\u539f\u56e0\u4e4b\u4e00\uff0c\u6700\u91cd\u8981\u7684\u662f\uff0c\u8fd9\u5723\u6c34\u7684\u4ea4\u6613\uff0c\u4e5f\u5145\u6ee1\u7740\u795e\u79d8\uff1b\u8fd9\u5723\u6c34\u6bcf\u6b21\u53ea\u51fa\u552e\u4e00\u6876\uff0c\u5374\u6709\u5f88\u591a\u4eba\u63a5\u5230\u4e86\u901a\u77e5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u90fd\u8d76\u5230\u4e86\u8fd9\u5ea7\u5c0f\u9547\u4e0a\u6765\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u5bf9\uff01\u4ed6\u4eec\u90fd\u63a5\u5230\u4e86\u901a\u77e5\uff0c\u800c\u4e14\uff0c\u5927\u90fd\u5728\u4e00\u4e2a\u6708\u524d\u5230\u4e86\u8fd9\u91cc\uff0c\u90a3\u65f6\uff0c\u8fd9\u5ea7\u5c0f\u9547\u624d\u771f\u6b63\u89c1\u5230\u7e41\u8363\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u5723\u6cc9\u9547\u7684\u5723\u6c34\uff0c\u591a\u4e45\u4ea4\u6613\u4e00\u6b21\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u4e09\u4e2a\u6708\uff0c\u6bcf\u5e74\u56db\u6b21\uff0c\u6bcf\u6b21\u4e00\u6876\uff0c\u8fd9\u5ea7\u5c0f\u9547\u6bcf\u4eba\u6709\u56db\u6b21\u4ea4\u6613\uff0c\u6bcf\u4e00\u6b21\u4ea4\u6613\uff0c\u53ef\u4ee5\u7ed9\u8fd9\u4e2a\u5c0f\u9547\u5e26\u6765\u4e00\u4e2a\u591a\u6708\u7684\u7e41\u8363\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6bcf\u4e00\u5e74\uff0c\u4ed6\u4eec\u53ea\u505a\u56db\u4e2a\u591a\u6708\u7684\u751f\u610f\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u90a3\u5df2\u7ecf\u5f88\u591f\u4e86\uff0c\u4ed6\u4eec\u56db\u4e2a\u6708\u6240\u8d5a\u5230\u7684\u94f6\u5b50\uff0c\u8db3\u591f\u4ed6\u4eec\u4e00\u5e74\u5f00\u9500\u6709\u4f59\u4e86\uff0c\u56e0\u4e3a\uff0c\u6765\u8fd9\u91cc\u7684\u4eba\uff0c\u90fd\u5f88\u6709\u94b1\uff0c\u4ed6\u4eec\u5305\u4e0b\u6574\u5ea7\u5ba2\u6808\uff0c\u4e00\u5ea7\u9152\u9986\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u73b0\u5728\u624d\u660e\u767d\uff0c\u8fd9\u91cc\u7684\u5ba2\u6808\u548c\u9152\u9986\uff0c\u4e3a\u4ec0\u4e48\u90a3\u4e48\u591a\u4e86\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u6bcf\u4e00\u4ea4\u6613\u5b8c\u6210\u4e4b\u540e\uff0c\u8fd9\u4e9b\u4eba\u5c31\u6084\u7136\u79bb\u5f00\uff0c\u4ed6\u4eec\u90fd\u5f88\u5bcc\u6709\uff0c\u6bcf\u4e00\u6b21\u6765\u8fd9\u91cc\uff0c\u90fd\u5e26\u4e86\u5927\u6279\u7684\u91d1\u94f6\u3001\u73e0\u5b9d\uff0c\u5e0c\u671b\u80fd\u6362\u5230\u90a3\u4e00\u6876\u5723\u6c34\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f20\u638c\u67dc\uff0c\u6211\u4eec\u73b0\u5728\u662f\u4e0d\u662f\u8981\u6253\u4e3b\u610f\u52ab\u593a\u90a3\u4e9b\u73e0\u5b9d\u3001\u91d1\u94f6\uff1f\u201d \u3000\u3000\u5f20\u73de\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u5982\u53ea\u4e3a\u62a2\u52ab\u4e00\u4e9b\u73e0\u5b9d\uff0c\u5821\u4e3b\u4e0d\u4f1a\u6d3e\u4f60\u4eec\u8fd9\u6837\u7684\u9ad8\u624b\u6765\u6b64\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u6211\u4eec\u6765\u6b64\u7528\u610f\u4f55\u5728\u5462\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u672c\u5821\u7684\u89c4\u77e9\u5f88\u4e25\uff0c\u6211\u4e0d\u80fd\u544a\u8bc9\u4f60\u4eec\u592a\u591a\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u8981\u6211\u4eec\u505a\u4e9b\u4ec0\u4e48\u4e8b\uff0c\u603b\u5f97\u5bf9\u6211\u4eec\u8bf4\u660e\u767d\u5427\uff01\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u8981\u4f60\u4eec\u6740\u4e00\u4e2a\u4eba\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u8fd9\u4e2a\u4f60\u4eec\u4e0d\u7528\u95ee\u4e86\uff0c\u6211\u66ff\u4f60\u4eec\u5b89\u6392\u3002\u73b0\u5728\uff0c\u4e24\u4f4d\u53ef\u4ee5\u5b89\u8be6\u7684\u4f4f\u5728\u8fd9\u91cc\uff0c\u9152\u3001\u5973\u4eba\uff0c\u6211\u90fd\u53ef\u4ee5\u4f9b\u5e94\uff0c\u4e0d\u8fc7\uff0c\u4e24\u4f4d\u6700\u597d\u662f\u4e0d\u8981\u79bb\u5f00\u8fd9\u91cc\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u6837\u7b97\u662f\u88ab\u56da\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u53f6\u5144\u4e0d\u8981\u8bef\u4f1a\uff0c\u4f60\u4eec\u53ea\u4e0d\u8fc7\u5728\u6267\u884c\u4e00\u4e2a\u4ee4\u8c15\uff0c\u4e0d\u80fd\u66b4\u9732\u8eab\u4efd\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u54b1\u4eec\u65e2\u7136\u662f\u5949\u547d\u800c\u6765\uff0c\u53ea\u6709\u542c\u547d\u884c\u4e8b\u4e86\u3002\u4e0d\u8fc7\uff0c\u5f20\u638c\u67dc\uff0c\u6765\u65e5\u65b9\u957f\uff0c\u6211\u4e03\u5251\u8ffd\u9b42\u53ea\u8981\u8fd9\u4e00\u6b21\u4e0d\u6b7b\uff0c\u6211\u4eec\u65e5\u540e\u76f8\u5904\u7684\u65f6\u95f4\u8fd8\u591a\uff0c\u5927\u5bb6\u7559\u4e00\u70b9\u65e5\u540e\u76f8\u5904\u7684\u60c5\u5206\u3002\u201d \u3000\u3000\u5f20\u73de\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ee5\u53f6\u5144\u4e4b\u80fd\uff0c\u6211\u60f3\u5b9a\u7136\u4f1a\u5728\u672c\u7ec4\u5408\u4e2d\uff0c\u4e89\u5f97\u4e00\u5e2d\u4e4b\u5730\u3002\u90a3\u65f6\uff0c\u5144\u5f1f\u8fd8\u800d\u53f6\u5144\u591a\u591a\u7167\u987e\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e00\u62a5\u8fd8\u4e00\u62a5\uff0c\u5982\u679c\u5f20\u5144\u771f\u7684\u770b\u597d\u6211\u4ee5\u540e\u7684\u6210\u5c31\uff0c\u5e0c\u671b\u5f20\u5144\u73b0\u5728\u80fd\u653e\u70b9\u4ea4\u60c5\u8fdb\u53bb\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u4e00\u5b9a\uff0c\u2014\u5b9a\uff0c\u82e5\u662f\u5144\u5f1f\u80fd\u7ed9\u4e24\u4f4d\u65b9\u4fbf\u7684\u5730\u65b9\uff0c\u7edd\u5bf9\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e8b\u60c5\u968f\u65f6\u53ef\u80fd\u6709\u53d8\u5316\uff0c\u4e24\u4f4d\u65c5\u9014\u52b3\u7d2f\uff0c\u4e5f\u8be5\u597d\u597d\u7684\u4f11\u606f\u4e00\u4e0b\u4e86\uff0c\u6211\u8fd8\u6709\u4e8b\uff0c\u5931\u966a\u4e86\u3002\u201d \u3000\u3000\u8d77\u8eab\u544a\u9000\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u5f20\u73de\u7684\u80cc\u5f71\uff0c\u590f\u6740\u8f7b\u8f7b\u53f9\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6211\u5728\u6c5f\u6e56\u4e0a\u6df7\u4e86\u51e0\u5341\u5e74\uff0c\u770b\u6765\uff0c\u8fd8\u662f\u4e0d\u5982\u4f60\u53f6\u5144\u9ad8\u660e\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\u62ac\u4e3e\u5144\u5f1f\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5144\u5f1f\u662f\u7531\u8877\u4e4b\u8a00\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u6211\u8981\u5fcd\u8fb1\u8d1f\u91cd\u7684\u6d3b\u4e0b\u53bb\uff0c\u5fc5\u987b\u8981\u548c\u8fd9\u4e9b\u4eba\u591a\u591a\u8054\u7edc\u4e00\u4e0b\u624d\u884c\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u6c5f\u6e56\u4e0a\u7ecf\u9a8c\u4e0d\u591a\uff0c\u813e\u6c14\u53c8\u4e0d\u597d\uff0c\u65e5\u540e\uff0c\u8fd8\u5e0c\u671b\u590f\u5144\u591a\u6307\u70b9\u3002\u201d \u3000\u3000\u590f\u6740\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\u5fc3\u4e2d\u5982\u6709\u4ec0\u4e48\u7591\u95ee\uff0c\u53ea\u7ba1\u8bf7\u8bf4\uff0c\u5144\u5f1f\u662f\u77e5\u65e0\u4e0d\u8a00\u3002\u201d\u53f6\u957f\u9752\u9053\uff1a\u201c\u65e2\u7136\u590f\u5144\u80af\u628a\u5144\u5f1f\u5f15\u4e3a\u77e5\u5df1\uff0c\u5144\u5f1f\u5fc3\u4e2d\u51e0\u70b9\u7591\u95ee\uff0c\u90a3\u5c31\u6597\u80c6\u51fa\u53e3\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u8bf7\u8bf4\uff0c\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\u5e72\u8fd9\u79cd\u5de5\u4f5c\uff0c\u6709\u591a\u5c11\u5e74\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5c06\u8fd1\u4e03\u5e74\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e03\u5e74\u4e86\uff0c\u4e03\u5e74\u7684\u65e5\u5b50\uff0c\u867d\u7136\u662f\u4e0d\u592a\u957f\uff0c\u4f46\u4e5f\u4e0d\u592a\u77ed\uff0c\u590f\u5144\u7684\u65e5\u5b50\uff0c\u8fc7\u5f97\u8fd8\u6109\u5feb\u5427\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u8c08\u4e0d\u4e0a\u6109\u5feb\uff0c\u4f46\u4e5f\u6ca1\u6709\u4ec0\u4e48\u70e6\u607c\uff0c\u53ea\u4e0d\u8fc7\u751f\u6b7b\u4e4b\u95f4\uff0c\u5f88\u96be\u628a\u63e1\u5f97\u4f4f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\uff0c\u54b1\u4eec\u8fd9\u79cd\u751f\u6d3b\uff0c\u53ef\u6709\u4ec0\u4e48\u5feb\u6d3b\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u6709\uff01\u6211\u4eec\u5f88\u7a7a\u95f2\uff0c\u800c\u4e14\uff0c\u6211\u4eec\u6709\u4e00\u4efd\u5f88\u4e30\u539a\u7684\u85aa\u4ff8\uff0c\u7a7a\u95f2\u7684\u65f6\u5019\uff0c\u6211\u4eec\u53ef\u4ee5\u559d\u559d\u9152\u3001\u53ef\u4ee5\u8d4c\u8d4c\u94b1\uff0c\u4e5f\u53ef\u4ee5\u627e\u5973\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u4eec\u751f\u6d3b\u7684\u5168\u90e8\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5bf9\uff01\u6740\u4eba\u751f\u6daf\u672c\u5982\u68a6\uff0c\u54b1\u4eec\u8fd9\u79cd\u751f\u6d3b\u5c31\u662f\u9189\u751f\u68a6\u6b7b\u7684\u751f\u6d3b\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u8fc7\u5f97\u8fd8\u4e60\u60ef\u5427\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u6162\u6162\u5c31\u4e60\u60ef\u4e86\uff0c\u5728\u5f00\u59cb\u7684\u65f6\u5019\uff0c\u6211\u4e5f\u548c\u53f6\u5144\u4e00\u6837\uff0c\u4e0d\u77e5\u9053\u4eca\u540e\u8be5\u5982\u4f55\u81ea\u5904\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\uff0c\u5821\u4e3b\u82e6\u5fc3\u8bbe\u8ba1\uff1b\u4e0d\u60dc\u82b1\u53bb\u4e86\u5927\u7b14\u94f6\u5b50\uff0c\u627e\u5230\u7389\u5170\u53cc\u59dd\uff0c\u628a\u5144\u5f1f\u751f\u64d2\u8fc7\u6765\uff0c\u76ee\u7684\u5c31\u5728\u5723\u6cc9\u9547\u8fd9\u4e00\u51fb\u5417\uff1f\u201d \u3000\u3000\u590f\u6740\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u53f6\u5144\u65e2\u7136\u5982\u6b64\u8bda\u631a\u7684\u76f8\u95ee\uff0c\u5144\u5f1f\u5c31\u53ea\u597d\u5b9e\u8bdd\u5b9e\u8bf4\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u54b1\u4eec\u547d\u8fd0\u76f8\u8fde\uff0c\u590f\u5144\u4e5f\u5e94\u8be5\u8bf4\u5b9e\u8bdd\u624d\u5bf9\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u672c\u6765\uff0c\u5723\u6cc9\u9547\u8fd9\u4e00\u8d9f\u4efb\u52a1\uff0c\u4e0d\u662f\u4ef0\u4ed7\u53f6\u5144\u7684\uff0c\u540e\u6765\uff0c\u7389\u5170\u53cc\u59dd\u628a\u53f6\u5144\u751f\u64d2\u8fc7\u6765\u4e4b\u540e\uff0c\u8fd9\u4ef6\u4e8b\u5c31\u843d\u5728\u53f6\u5144\u7684\u5934\u4e0a\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\uff0c\u5f53\u65f6\uff0c\u4f60\u4eec\u786e\u5b9e\u51c6\u5907\u8981\u6740\u5144\u5f1f\u4e86\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5bf9\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5f53\u521d\uff0c\u662f\u51c6\u5907\u6d3e\u54ea\u4e00\u4f4d\u6765\u8fd9\u8d9f\u5723\u6cc9\u9547\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u53f6\u5144\uff0c\u8fd9\u4e2a\uff0c\u5c31\u4e0d\u662f\u5144\u5f1f\u7684\u8eab\u4efd\u6240\u77e5\u6653\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\uff0c\u6211\u6709\u4e00\u70b9\u60f3\u4e0d\u660e\u767d\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u4f4e\u58f0\u9053\uff1a\u201c\u5821\u4e3b\u53ef\u4ee5\u82b1\u53bb\u5927\u628a\u7684\u94f6\u5b50\uff0c\u96c7\u7528\u7389\u5170\u53cc\u59dd\uff0c\u5bf9\u4ed8\u5144\u5f1f\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u96c7\u7528\u7389\u5170\u53cc\u59dd\u4f5c\u5723\u6cc9\u9547\u8fd9\u4e00\u51fb\u5462\uff1f\u201d \u3000\u3000\u590f\u6740\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u5144\uff0c\u8fd9\u4e00\u70b9\uff0c\u5144\u5f1f\u4e5f\u4e0d\u4e86\u89e3\uff0c\u5c31\u5144\u5f1f\u731c\u60f3\uff0c\u8fd9\u53ef\u80fd\u662f\u4e00\u6869\u5f88\u9690\u79d8\u7684\u4ea4\u6613\uff0c\u7389\u5170\u53cc\u59dd\u7a76\u7adf\u8fd8\u4e0d\u662f\u54b1\u4eec\u7684\u4eba\uff0c\u5979\u4eec\u53ea\u662f\u88ab\u96c7\u7528\u7684\u6740\u624b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\u89c1\u8fc7\u7389\u5170\u53cc\u59dd\u5417\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u95fb\u540d\u5df2\u4e45\uff0c\u76f4\u5230\u5144\u5f1f\u5949\u6d3e\u53bb\u63a5\u53f6\u5144\u65f6\uff0c\u624d\u7b2c\u4e00\u6b21\u89c1\u5230\u5979\u4eec\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5509\uff01\u7389\u5170\u53cc\u59dd\uff0c\u90fd\u751f\u5f97\u5f88\u7f8e\uff0e\u4f46\u7adf\u7136\u6df7\u8ff9\u5728\u6c5f\u6e56\u4e4b\u4e2d\u4f5c\u4e3a\u6740\u624b\uff0c\u60f3\u8d77\u6765\uff0c\u4e5f\u662f\u4e00\u4ef6\u5f88\u53ef\u60b2\u7684\u4e8b\u60c5\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1b\u201c\u8bf4\u8d77\u6765\uff0c\u8fd9\u4e24\u4e2a\u4e2b\u5934\u7684\u906d\u9047\u5012\u4e5f\u662f\u4e0b\u573a\u51c4\u51c9\uff0c\u4e0d\u77e5\u9053\u662f\u4e0d\u662f\u4f60\u53f6\u5144\u7684\u670b\u53cb\u627e\u4e0a\u4e86\u5979\u4eec\uff0c\u5c31\u5728\u5979\u4eec\u751f\u64d2\u4e86\u53f6\u5144\u7684\u7b2c\u4e8c\u5929\uff0c\u7389\u5170\u53cc\u59dd\u4e5f\u5728\u592a\u6e56\u6c34\u57df\u4e2d\u9047\u5bb3\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u600e\u4e48\uff0c\u7389\u5170\u53cc\u59dd\u88ab\u4eba\u6740\u4e86\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5bf9\uff01\u4e24\u59ca\u59b9\u53cc\u53cc\u4f0f\u8bdb\uff0c\u6c89\u5c38\u8239\u5934\uff0c\u800c\u4e14\uff0e\u6b7b\u72b6\u751a\u60e8\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e24\u4e2a\u4eba\u90fd\u6b7b\u4e86\u5417\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4e24\u59ca\u59b9\u53cc\u53cc\u4f0f\u5c38\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\uff0c\u662f\u4e0d\u662f\u54b1\u4eec\u6d3e\u4eba\u6740\u7684\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u7b2c\u4e00\uff0c\u8fd9\u4e24\u4e2a\u5973\u5a03\u513f\u4e0d\u597d\u5bf9\u4ed8\uff0c\u5979\u4eec\u4e0d\u4f46\u6b66\u529f\u9ad8\u5f3a\uff0c\u800c\u4e14\uff0c\u4eba\u4e5f\u7cbe\u660e\u5f97\u5f88\uff0c\u54b1\u4eec\u65e2\u7136\u82b1\u4e86\u94f6\u5b50\uff0c\u5979\u4eec\u4ea4\u51fa\u4e86\u4f60\u53f6\u5144\uff0c\u5f7c\u6b64\u4e4b\u95f4\uff0c\u4ea4\u6613\u7684\u5f88\u6e05\u695a\uff0c\u4f3c\u4e4e\u662f\u7528\u4e0d\u7740\u518d\u6d3e\u4eba\u6740\u5bb3\u5979\u4eec\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u89c1\u8fc7\u7389\u5170\u53cc\u59dd\uff0c\u5bf9\u5979\u4eec\u7684\u4e00\u5207\u90fd\u662f\u5f88\u719f\u6089\uff0c\u6240\u4ee5\uff0c\u4ed6\u660e\u767d\u6740\u5bb3\u7389\u5170\u53cc\u59dd\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u7389\u5170\u53cc\u59b9\u6bd5\u7adf\u8fd8\u662f\u88ab\u4eba\u6740\u4e86\u3002\u8fd9\u4e24\u4e2a\u7f8e\u4e3d\u7684\u5973\u523a\u5ba2\uff0c\u4ece\u6b64\u9664\u540d\u6c5f\u6e56\uff0c\u6d88\u5931\u4eba\u95f4\u3002 \u3000\u3000\u53ea\u542c\u590f\u6740\u8bf4\u9053\uff1a\u201c\u53f6\u5144\uff0c\u60f3\u60f3\u770b\uff0c\u662f\u4e0d\u662f\u4f60\u7684\u670b\u53cb\u4e0b\u7684\u624b\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u6211\u8d74\u7ea6\u592a\u6e56\uff0c\u6211\u7684\u670b\u53cb\u90fd\u4e0d\u77e5\u9053\uff0c\u4ed6\u4eec\u4e0d\u77e5\u9053\u6211\u53bb\u4f1a\u7389\u5170\u53cc\u59dd\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u7389\u5170\u53cc\u59dd\u5728\u6c5f\u6e56\u4e0a\u505a\u7684\u6848\u5b50\u4e0d\u7b97\u592a\u591a\uff0c\u4e0d\u8fc7\uff0c\u5979\u4eec\u505a\u7684\u6848\u5b50\uff0c\u90fd\u662f\u5927\u6848\u5b50\uff0c\u6211\u60f3\u3002\u8fd9\u4e00\u6b21\uff0c\u4e3a\u4e86\u53f6\u5144\u7684\u4e8b\uff0c\u5979\u76ef\u5728\u5927\u6e56\u7559\u5c45\u4e86\u6570\u65e5\u4e4b\u4e45\uff0c\u5fc5\u7136\u662f\u88ab\u4eba\u53d1\u73b0\u4e86\u884c\u8e2a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53cc\u76ee\u51dd\u6ce8\u5728\u590f\u6740\u7684\u8138\u4e0a\uff0c\u7b11\u2014\u7b11\u9053\uff1a\u201c\u590f\u5144\uff0c\u6211\u4eec\u6bd4\u8d77\u7389\u5170\u53cc\u59dd\u3002\u53ea\u6015\u8fd8\u4f4e\u4e86\u4e00\u7b49\u3002\u201d \u3000\u3000\u590f\u6740\u9ed8\u9ed8\u4e0d\u8bed\u3002\u9ed8\u9ed8\u4e0d\u8bed\uff0c\u5c31\u662f\u627f\u8ba4\u4e86\u4e8b\u5b9e\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u633a\u4e4b\u8fc7\u6025\uff0c\u8fd9\u590f\u6740\u5fc3\u4e2d\u7a76\u7adf\u60f3\u4e9b\u4ec0\u4e48\uff1f\u6211\u8fd8\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u4ed6\u660e\u767d\u81ea\u5df1\u7684\u751f\u6b7b\uff0c\u8fd8\u5728\u522b\u4eba\u7684\u638c\u63e1\u4e4b\u4e2d\u3002 \u3000\u3000\u5ba4\u4e2d\u7a81\u7136\u95f4\u6c89\u9759\u4e0b\u6765\uff0c\u4e24\u4e2a\u4eba\u5bf9\u7740\u559d\u95f7\u9152\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u5fc3\u4e2d\uff0c\u90fd\u597d\u50cf\u6709\u5f88\u591a\u7684\u8bdd\u8bf4\uff0c\u4f46\u4e00\u65f6\u95f4\uff0c\u8c01\u90fd\u60f3\u4e0d\u51fa\u8be5\u5982\u4f55\u5f00\u53e3\u3002 \u3000\u3000\u5e78\u597d\uff0c\u5f20\u73de\u53ca\u65f6\u800c\u6765\u3002 \u3000\u3000\u4ed6\u624b\u4e2d\u6367\u7740\u4e00\u4e2a\u4e94\u5bf8\u89c1\u65b9\u7684\u6728\u76d2\u3002 \u3000\u3000\u590f\u6740\u7684\u8138\u8272\u50b2\u5fae\u4e00\u53d8\uff0c\u4f46\u5f88\u5feb\u6062\u590d\u4e86\u6b63\u5e38\u3002 \u3000\u3000\u5f20\u73de\u5374\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u7684\u8fd0\u6c14\u4e0d\u9519\uff0c\u660e\u5929\u5c31\u6709\u2014\u4e2a\u5f88\u597d\u7684\u4e0b\u624b\u673a\u4f1a\uff0c\u6211\u628a\u4e24\u4f4d\u7528\u7684\u4e1c\u897f\uff0c\u90fd\u5e26\u6765\u4e86\uff0c\u5e0c\u671b\u4e24\u4f4d\u80fd\u4e00\u51fb\u6210\u529f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u8f7b\u8f7b\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5f20\u5144\uff0c\u6211\u4e03\u5251\u8ffd\u9b42\u5728\u6c5f\u6e56\u4e0a\u4e5f\u8d70\u4e86\u4e0d\u5c11\u7684\u65f6\u95f4\uff0c\u603b\u5f52\u4e5f\u6709\u51e0\u4e2a\u597d\u670b\u53cb\uff0c\u4e07\u4e00\u6740\u7684\u662f\u6211\u7684\u670b\u53cb\uff0c\u90a3\u5c06\u5982\u4f55\u662f\u597d\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u4e07\u4e00\u662f\u4f60\u7684\u670b\u53cb\uff0c\u90a3\u4e5f\u53ea\u597d\u628a\u4ed6\u6740\u4e86\uff0c\u4f60\u4e8b\u5148\u4e0d\u77e5\u9053\u5c82\u4e0d\u662f\u66f4\u597d\u4e00\u4e9b\uff0c\u4fd7\u8bed\u8bf4\u7684\u597d\uff0c\u4e0d\u77e5\u8005\u4e0d\u7f6a\u3002\u201d \u3000\u3000\u4ed6\u56de\u7b54\u7684\u867d\u7136\u5341\u5206\u548c\u6c14\uff0c\u4f46\u8bed\u8bcd\u4e4b\u4e2d\u5f02\u5e38\u575a\u51b3\uff0c\u53f6\u957f\u9752\u5fc3\u4e2d\u660e\u767d\uff0c\u65e0\u6cd5\u518d\u95ee\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5f20\u73de\u6253\u5f00\u76d2\u76d6\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e24\u4e2a\u4eba\u6bcf\u4eba\u4e00\u4e2a\u3002\u201d \u3000\u3000\u590f\u6740\u5f53\u5148\u4f38\u624b\uff0c\u53d6\u8fc7\u4e00\u4e2a\u3002 \u3000\u3000\u53f6\u957f\u9752\u53ea\u597d\u53d6\u8fc7\u4e86\u53e6\u4e00\u4e2a\u3002\u5f20\u73de\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u79cd\u5f88\u9738\u9053\u7684\u6697\u5668\uff0c\u636e\u8bf4\u5728\u4e00\u4e08\u4e4b\u5185\uff0c\u6ca1\u6709\u4eba\u80fd\u591f\u9003\u907f\u5f97\u8fc7\uff0c\u51f6\u5389\u662f\u591f\u51f6\u5389\u7684\uff0c\u4e0d\u8fc7\u5b83\u4e5f\u6709\u4e00\u4e2a\u5f88\u5927\u7684\u7f3a\u70b9\uff0c\u90a3\u5c31\u662f\u53ea\u80fd\u7528\u4e00\u6b21\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u53ea\u80fd\u7528\u4e00\u6b21\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u5bf9\uff01\u800c\u4e14\u5236\u9020\u8d77\u6765\u5341\u5206\u9ebb\u70e6\uff0c\u6240\u4ee5\u4e24\u4f4d\u80fd\u591f\u4e0d\u7528\u7684\u65f6\u5019\u6700\u597d\u4e0d\u8981\u7528\uff0c\u53ef\u662f\uff0c\u4e0d\u5bb9\u654c\u4eba\u9003\u8131\uff0c\u5982\u679c\u654c\u4eba\u4e00\u5b9a\u8981\u9003\u8d70\u7684\u65f6\u5019\uff0c\u6216\u662f\u4f60\u4eec\u96be\u4ee5\u6297\u62d2\u4e4b\u65f6\uff0c\u90a3\u5c31\u53ef\u4ee5\u65bd\u7528\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5982\u4f55\u7528\u6cd5\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u7b80\u5355\u5f97\u5f88\uff0c\u5706\u7b52\u4e24\u7aef\u867d\u90fd\u662f\u5c01\u6b7b\u7684\uff0c\u4f46\u4e24\u9762\u7684\u989c\u8272\u4e0d\u540c\uff0c\u4e00\u767d\u4e00\u9ed1\uff0c\u9ed1\u7684\u5bf9\u7740\u654c\u4eba\uff0c\u53f3\u624b\u4e00\u6307\uff0c\u7528\u529b\u4e00\u9876\u767d\u8272\u7684\u4e00\u7aef\uff0c\u5373\u53ef\u53d1\u6325\u5999\u7528\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u5b50\u7684\u5999\u7528\uff1f\u201d \u3000\u3000\u5f20\u73de\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u53ea\u662f\u542c\u8fc7\u5374\u672a\u89c1\u8fc7\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e00\u70b9\u53ef\u4ee5\u80af\u5b9a\uff0c\u53ea\u8981\u654c\u4eba\u5728\u4e00\u4e08\u4e4b\u5185\uff0c\u7edd\u5bf9\u9003\u4e0d\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u8fd9\u4e1c\u897f\u5f88\u7ec6\u5c0f\uff0c\u4e00\u53ea\u624b\u5c31\u53ef\u4ee5\u7528\u4e86\uff0c\u4e24\u4f4d\u6536\u8d77\u6765\u5427\uff01\u201d \u3000\u3000\u590f\u6740\u7a81\u7136\u63a5\u9053\uff1a\u201c\u638c\u67dc\u7684\uff0c\u8fd9\u4e1c\u897f\u6709\u4e2a\u540d\u5b57\u5427\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u597d\u50cf\u53eb\u4ec0\u4e48\u4e09\u7edd\u7b52\uff0c\u4e0d\u8fc7\uff0c\u5b83\u7684\u540d\u5b57\u5e76\u4e0d\u91cd\u8981\uff0c\u91cd\u8981\u7684\u662f\u5b83\u7684\u5a01\u529b\u3002\u6211\u4eec\u8fd9\u79cd\u4e1c\u897f\u5e76\u4e0d\u591a\uff0c\u542c\u8bf4\u90a3\u5236\u9020\u7684\u5de5\u5320\u5df2\u7ecf\u901d\u53bb\uff0c\u7cbe\u5de7\u7684\u624b\u827a\u5e76\u672a\u6d41\u4f20\u4e0b\u6765\uff0c\u7528\u4e00\u4e2a\u5c11\u4e00\u4e2a\uff0c\u8fd9\u4e00\u6b21\uff0c\u5821\u4e3b\u5206\u7ed9\u4e24\u4f4d\u6bcf\u4eba\u4e00\u53ea\uff0c\u8db3\u89c1\u5bf9\u8fd9\u6b21\u7684\u4efb\u52a1\u7684\u91cd\u89c6\uff0c\u5e0c\u671b\u4e24\u4f4d\u80fd\u591f\u4e0d\u8d1f\u5821\u4e3b\u6240\u671b\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u987f\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4e24\u4f4d\u53ef\u4ee5\u597d\u597d\u4f11\u606f\u4e00\u4e0b\uff0c\u6211\u4e0d\u6253\u6270\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f20\u5144\u8bf7\u4fbf\u3002\u201d \u3000\u3000\u5f20\u73de\u7b11\u4e00\u7b11\uff0c\u8d77\u8eab\u79bb\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u76ee\u7779\u5f20\u73de\u79bb\u53bb\uff0c\u624d\u4f4e\u58f0\u9053\uff1a\u201c\u590f\u5144\uff0c\u8fd9\u4e09\u7edd\u7b52\u4e2d\uff0c\u7a76\u7adf\u662f\u88c5\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u6211\u542c\u8bf4\u5821\u4e3b\u62e5\u6709\u4e00\u79cd\u9738\u9053\u7684\u5229\u5668\uff0c\u5927\u6982\u662f\u8fd9\u4e2a\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u4e5f\u662f\u7b2c\u4e00\u6b21\u65bd\u7528\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u7b2c\u4e00\u6b21\u770b\u5230\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e1c\u897f\uff0c\u5907\u800c\u4e0d\u7528\uff0c\u5e26\u5728\u8eab\u8fb9\uff0c\u603b\u4e0d\u59a8\u4e8b\u3002\u201d \u3000\u3000\u590f\u6740\u82e6\u7b11\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u660e\u5929\u665a\u4e0a\uff0c\u597d\u50cf\u5c31\u662f\u6211\u670d\u7528\u89e3\u836f\u7684\u65f6\u95f4\u4e86\uff0c\u5982\u679c\u660e\u5929\u4e2d\u5348\u65f6\u5206\uff0c\u6211\u4eec\u6740\u4e0d\u4e86\u90a3\u4e2a\u4eba\uff0c\u5144\u5f1f\u5c31\u6d3b\u4e0d\u5230\u660e\u665a\u4e09\u66f4\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u53f6\u5144\uff0c\u8fd9\u4e00\u70b9\u4e0d\u7528\u987e\u8651\uff0c\u54b1\u4eec\u6740\u4e0d\u4e86\u90a3\u4e2a\u4eba\u7684\u65f6\u5019\uff0c\u8eab\u4e0a\u662f\u5426\u4e2d\u6bd2\uff0c\u90fd\u662f\u4e00\u6837\u7684\u7ed3\u679c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u54b1\u4eec\u6740\u4e0d\u4e0d\u4e86\u90a3\u4e2a\u4eba\uff0c\u5c31\u53ef\u80fd\u88ab\u4eba\u6740\u6b7b\uff0c\u662f\u5426\u4e2d\u4e86\u6bd2\uff0c\u4e5f\u5c31\u65e0\u5173\u91cd\u8981\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\uff0e\u53cc\u65b9\u640f\u6740\uff0c\u4e5f\u5e76\u975e\u4e00\u5b9a\u8981\u6b7b\uff0c\u54b1\u4eec\u4e5f\u53ef\u80fd\u6709\u9003\u751f\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u53f6\u5144\uff0c\u5982\u82e5\u5408\u54b1\u4eec\u4e24\u4eba\u4e4b\u529b\u8fd8\u65e0\u6cd5\u5bf9\u4ed8\u7684\u654c\u4eba\uff0c\u9003\u8d70\u7684\u673a\u4f1a\uff0c\u5c31\u5b9e\u5728\u4e0d\u5927\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\uff0c\u5982\u679c\u6211\u4eec\u4ecd\u6740\u4e0d\u4e86\u5bf9\u65b9\uff0c\u90a3\u4f1a\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u4e8e\u7684\u7ed3\u679c\uff1f\u201d \u3000\u3000\u590f\u6740\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u6740\u4e0d\u4e86\u5bf9\u65b9\uff0c\u54b1\u4eec\u6d3b\u4e0b\u53bb\uff0c\u4e5f\u662f\u65e0\u5473\u7684\u5f88\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u5f88\u5a49\u8f6c\uff0c\u53f6\u957f\u9752\u8fd8\u662f\u542c\u61c2\u4e86\u5f26\u5916\u4e4b\u97f3\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u590f\u5144\uff0c\u90a3\u662f\u8bf4\uff0c\u54b1\u4eec\u5982\u679c\u4e0d\u80fd\u72d9\u6740\u4e86\u5bf9\u65b9\uff0c\u4e5f\u662f\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u54b1\u4eec\u5fc5\u987b\u8981\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u672a\u518d\u591a\u95ee\uff0c\u53ea\u662f\u7528\u76ee\u5149\u671b\u7740\u590f\u6740\u3002\u4ed6\u8981\u770b\u590f\u6740\u7684\u53cd\u5e94\uff0c\u60f3\u542c\u542c\u590f\u6740\u7684\u610f\u89c1\u3002 \u3000\u3000\u590f\u6740\u5374\u7b11\u4e00\u7b11\uff0c\u4e0d\u518d\u591a\u8a00\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\uff0c\u5fc3\u4e2d\u4f3c\u662f\u90fd\u6709\u67d0\u4e00\u4e9b\u987e\u5fcc\uff0c\u4e0d\u80fd\u7545\u6240\u6b32\u8a00\u3002 \u3000\u3000\u8fd9\u4e00\u591c\uff0c\u5f88\u5b89\u9759\uff0c\u81f3\u5c11\u662f\u8868\u9762\u4e0a\u5f88\u5b89\u9759\u3002\u4f46\u4e8b\u5b9e\u4e0a\uff0c\u590f\u6740\u548c\u53f6\u957f\u9752\uff0c\u90fd\u6ca1\u6709\u7761\u597d\u3002 \u3000\u3000\u6f2b\u6f2b\u957f\u591c\uff0c\u7ec8\u4e8e\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u76f4\u5230\u65e5\u5347\u4e09\u7aff\u65f6\u5206\uff0c\u5f20\u73de\u624d\u8d76\u4e86\u6765\u3002 \u3000\u3000\u5f20\u73de\u5e26\u4e86\u4e24\u5957\u8863\u670d\u3002\u4e24\u5957\u5e97\u5c0f\u4e8c\u7684\u8863\u670d\u3002 \u3000\u3000\u590f\u6740\u6ca1\u6709\u591a\u95ee\uff0c\u7acb\u523b\u6362\u4e0a\u4e86\u8863\u670d\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\u52a8\u8eab\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u4e5f\u6362\u597d\u4e86\u8863\u670d\u3002 \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u8fd8\u6709\u534a\u4e2a\u65f6\u8fb0\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u53f6\u957f\u9752\uff0c\u63a5\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u592a\u82f1\u4fca\u4e86\uff0c\u770b\u6765\uff0c\u8138\u4e0a\u8fd8\u8981\u64e6\u4e00\u70b9\u4ec0\u4e48\uff0c\u5e78\u597d\uff0c\u6211\u5df2\u66ff\u53f6\u5144\u51c6\u5907\u597d\u4e86\u3002\u201d \u3000\u3000\u5f20\u73de\u9012\u7ed9\u4e86\u53f6\u957f\u9752\u4e00\u76d2\u9ed1\u8272\u7684\u989c\u6599\uff0e\u63a5\u9053\uff1a\u201c\u6709\u5f88\u591a\u7684\u5927\u4e8b\uff0c\u5e38\u5e38\u4f1a\u574f\u5728\u5c0f\u8282\u7684\u758f\u5ffd\u4e0a\u3002\u201d \u3000\u3000 \u3000\u3000\u5f20\u73de\u5e26\u7740\u4e24\u4eba\uff0c\u7531\u540e\u95e8\u51fa\u53bb\uff0c\u90a3\u662f\u4e00\u6761\u50fb\u9759\u7684\u5c0f\u5df7\u5b50\u3002 \u3000\u3000\u4e00\u8f86\u7bf7\u8f66\u65e9\u5df3\u5728\u7b49\u5019\u3002 \u3000\u3000\u5f20\u73de\u5e26\u4e24\u4eba\u767b\u4e0a\u4e86\u7bf7\u8f66\u3002 \u3000\u3000\u653e\u4e0b\u4e86\u5782\u5e18\uff0c\u5f20\u73de\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u795d\u4e24\u4f4d\u4e00\u51fb\u6210\u529f\u3002\u201d \u3000\u3000\u9a6c\u8f66\u7f13\u7f13\u5411\u524d\u9a70\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u73b0\u5728\uff0c\u53ef\u4e0d\u53ef\u4ee5\u544a\u8bc9\u6211\u4eec\u8981\u6740\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u4e0d\u8bba\u6740\u7684\u662f\u4ec0\u4e48\u4eba\uff0c\u90fd\u4e0d\u91cd\u8981\uff0c\u4f46\u5144\u5f1f\u53ef\u4ee5\u63d0\u4f9b\u4e00\u4e9b\u91cd\u8981\u7684\u4e8b\u60c5\u7ed9\u4e24\u4f4d\u53c2\u8003\u3002\u4f60\u4eec\u8981\u6740\u7684\u5bf9\u8c61\u6b66\u529f\u5f88\u9ad8\uff0c\u800c\u4e14\uff0c\u6709\u4e24\u4e2a\u4f8d\u536b\u4e00\u76f4\u4e0d\u5bd3\u4ed6\u7684\u5de6\u53f3\uff0c\u6240\u4ee5\u52a8\u624b\u65f6\uff0c\u4e00\u5b9a\u8981\u6211\u51fa\u4e2a\u9002\u5f53\u7684\u673a\u4f1a\uff0c\u52a1\u6c42\u4e00\u51fb\u800c\u4e2d\uff0c\u56e0\u4e3a\uff0c\u4f60\u4eec\u4e0d\u4f1a\u6709\u7b2c\u4e8c\u6b21\u51fa\u624b\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5f20\u5144\uff0c\u90a3\u4e2a\u4eba\u957f\u5f97\u4ec0\u4e48\u6837\u5b50\uff0c\u603b\u53ef\u4ee5\u544a\u8bc9\u6211\u4eec\u5427\uff1f\u201d \u3000\u3000\u5f20\u73de\u70b9\u5934\u9053\uff1a\u201c\u4ed6\u5f88\u5e74\u8f7b\uff0c\u800c\u4e14\u957f\u5f97\u5f88\u82f1\u4fca\uff0e\u5e74\u7eaa\u5927\u7ea6\u6709\u4e8c\u5341\u591a\u5c81\uff0e\u4ed6\u559c\u6b22\u7a7f\u7740\u4e00\u8eab\u767d\u8863\uff0c\u8fd9\u4e2a\u76ee\u6807\uff0c\u5df2\u7ecf\u5f88\u660e\u663e\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u662f\u5426\u5df2\u7ecf\u77e5\u9053\uff0c\u6211\u4eec\u8981\u6740\u4ed6\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u53ef\u80fd\u4e0d\u77e5\u9053\uff0c\u4e0d\u8fc7\uff0e\u542c\u8bf4\u4ed6\u662f\u4e2a\u5f88\u673a\u8b66\u7684\u4eba\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1b\u201c\u4f60\u4eec\u73b0\u5728\u662f\u5ba2\u6808\u7684\u5e97\u5c0f\u4e8c\uff0c\u6240\u4ee5\uff0c\u53ea\u8981\u4f60\u4eec\u884c\u52a8\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u80fd\u9690\u79d8\u8d77\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u5c31\u53ef\u4ee5\u6709\u8db3\u591f\u7684\u65f6\u95f4\uff0c\u9009\u62e9\u6700\u597d\u7684\u51fa\u624b\u65f6\u673a\uff0c\u81f3\u4e8e\u5982\u4f55\u9690\u8d77\u4f60\u4eec\u7684\u5175\u5203\uff0c\u8981\u4e24\u4f4d\u81ea\u5df1\u53bb\u52a8\u8111\u7b4b\u4e86\u3002\u201d \u3000\u3000\u7bf7\u8f66\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5f20\u73de\u70b9\u70b9\u5934\uff0c\u9053\uff1b\u201c\u4e24\u4f4d\u53ef\u4ee5\u4e0b\u8f66\u4e86\uff0c\u6709\u4eba\u4f1a\u63a5\u5e94\u4f60\u4eec\u3002\u201d \u3000\u3000\u8fd9\u4e5f\u662f\u4e00\u6761\u50fb\u9759\u7684\u5c0f\u5df7\uff0c\u4e5f\u662f\u8d70\u7684\u540e\u95e8\uff0c\u7bf7\u8f66\u5c31\u505c\u5728\u95e8\u53e3\u5904\uff0c\u95e8\u5df2\u6253\u5f00\uff0c\u4e00\u4e2a\u56db\u5341\u591a\u5c81\u7684\u4e2d\u5e74\u4eba\u7ad9\u5728\u95e8\u53e3\u5411\u4e24\u4eba\u62db\u624b\u3002 \u3000\u3000\u53f6\u957f\u9752\u548c\u590f\u6740\u8fdb\u4e86\u95e8\uff0c\u6728\u95e8\u7acb\u523b\u5173\u4e0a\uff0c\u7bf7\u8f66\u9a70\u8d70\u3002 \u3000\u3000\u63a5\u5e94\u4e24\u4eba\u7684\u4f3c\u4e4e\u662f\u4e00\u4e2a\u53a8\u5e08\uff0c\u624b\u4e0a\u8fd8\u6cbe\u7740\u6cb9\u817b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5ba2\u4eba\u4e2d\u5348\u65f6\u6765\uff0c\u8fd9\u5e97\u91cc\u53ea\u6709\u4f60\u4eec\u4e24\u4e2a\u5e97\u4f19\u8ba1\uff0c\u5feb\u4e9b\u53bb\u6253\u626b\u4e00\u4e0b\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\uff0c\u8f6c\u5934\u5c31\u8d70\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u5bb6\u9152\u9986\u3002 \u3000\u3000\u9664\u4e86\u53a8\u623f\u4e4b\u5916\uff0c\u8fd9\u5bb6\u5c0f\u5de7\u7684\u9152\u9986\u771f\u7684\u4f3c\u4e4e\u5189\u65e0\u522b\u4eba\u3002 \u3000\u3000\u590f\u6740\u548c\u53f6\u957f\u9752\u53ea\u597d\u771f\u7684\u4ee3\u505a\u4e86\u5e97\u5c0f\u4e8c\u7684\u5de5\u4f5c\uff0c\u62b9\u684c\u3001\u6446\u501a\uff0c\u7136\u540e\uff0c\u6253\u5f00\u4e86\u5e97\u95e8\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\uff1b\u597d\u50cf\u662f\u5f20\u73de\u5df2\u7ecf\u4e70\u4e0b\u4e86\u8fd9\u5ea7\u9152\u9986\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1b\u201c\u5f20\u73de\u4e00\u5411\u505a\u4e8b\u7f1c\u5bc6\uff0c\u662f\u5821\u4e3b\u5f88\u4fe1\u4efb\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u5bf9\u5e97\u5c0f\u4e8c\u7684\u5de5\u4f5c\uff0c\u5b9e\u5728\u4e0d\u592a\u719f\u7ec3\uff0c\u6240\u4ee5\uff0c\u73b0\u5728\u4e0d\u77e5\u518d\u505a\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u7b49\u5f85\uff0c\u7b49\u5ba2\u4eba\u4e0a\u95e8\u3002\u201d \u3000\u3000\u4e2d\u5348\u65f6\u5206\uff0c\u95e8\u5916\u679c\u7136\u6765\u4e86\u5ba2\u4eba\u3002 \u3000\u3000\u8fd9\u5723\u6cc9\u9547\u867d\u6709\u6570\u5341\u5bb6\u7684\u5ba2\u6808\u548c\u9152\u9986\uff0e\u4f46\u7edd\u5bf9\u6ca1\u6709\u5e97\u5c0f\u4e8c\u5728\u95e8\u53e3\u62c9\u751f\u610f\uff0c\u613f\u610f\u4e0a\u54ea\u4e00\u5bb6\u5ba2\u6808\u548c\u9152\u9986\uff0c\u5b8c\u5168\u662f\u5ba2\u4eba\u81ea\u5df1\u7684\u4e3b\u610f\u3002 \u3000\u3000\u4e24\u4e2a\u7070\u8272\u52b2\u88c5\u7684\u5927\u6c49\uff0c\u966a\u7740\u767d\u8863\u957f\u886b\u5c11\u5e74\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u7f13\u6b65\u8fce\u4e86\u4e0a\u53bb\uff0c\u4e00\u9762\u6253\u91cf\u90a3\u767d\u8863\u957f\u886b\u4eba\u4e00\u773c\u3002 \u3000\u3000\u90a3\u4eba\u5f88\u5e74\u8f7b\uff0c\u4e00\u88ad\u96ea\u767d\u7684\u676d\u7ef8\u957f\u886b\uff0c\u5251\u7709\u661f\u76ee\uff0c\u8702\u8170\u3001\u733f\u81c2\u3002 \u3000\u3000\u5306\u5306\u4e00\u773c\u4e4b\u4e0b\uff0c\u53f6\u957f\u9752\u5ffd\u7136\u53d1\u89c9\u8fd9\u662f\u6709\u4e00\u79cd\u5f3a\u70c8\u5438\u5f15\u529b\u7684\u7537\u4eba\u3002 \u3000\u3000\u4e24\u4e2a\u968f\u4f8d\u5de6\u53f3\u7684\u7070\u8863\u52b2\u88c5\u5927\u6c49\uff0c\u4e5f\u4e0d\u8fc7\u4e09\u5341\u5de6\u53f3\u7684\u5e74\u7eaa\u3002 \u3000\u3000\u53f6\u957f\u9752\u884c\u8fd1\u767d\u8863\u5c11\u5e74\u65f6\uff0c\u5de6\u9996\u7684\u7070\u8863\u5927\u6c49\u7a81\u7136\u5411\u524d\u5feb\u884c\uff0c\u62e6\u4f4f\u4e86\u53f6\u957f\u9752\uff0c\u9053\uff1a\u201c\u51c6\u5907\u9152\u83b1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5e94\u4e86\u4e00\u58f0\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u590f\u6740\u7ad9\u5728\u4e00\u4fa7\u3002 \u3000\u3000\u5de6\u9996\u7070\u8863\u5927\u6c49\u7684\u76ee\u5149\uff0c\u8f6c\u6ce8\u5230\u590f\u6740\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u96c5\u5ba4\u6253\u626b\u597d\u4e86\u5417\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u6253\u626b\u597d\u4e86\uff0c\u606d\u5019\u8d35\u5ba2\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u76ee\u5149\u7a81\u7136\u76ef\u6ce8\u5728\u590f\u6740\u8eab\u4e0a\uff0c\u77a7\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4f60\u662f\u5e97\u4f19\u8ba1\uff1f\u201d \u3000\u3000\u590f\u6740\u2014\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u662f\uff01\u516c\u5b50\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u597d\u5b88\u7d27\u95e8\u6237\uff0c\u4e8b\u6210\u4e4b\u540e\uff0c\u6211\u6709\u91cd\u8d4f\u3002\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u590f\u6740\u6839\u672c\u4e0d\u77e5\u9053\u8fd9\u5ea7\u9152\u9986\uff0c\u6b63\u8981\u8fdb\u884c\u4e9b\u4ec0\u4e48\u4e8b\uff1f \u3000\u3000\u4f46\u4ed6\u4eec\u5df2\u7ecf\u8ba4\u51fa\u4e86\u8981\u6740\u7684\u5bf9\u8c61\uff0c\u5c31\u662f\u90a3\u767d\u8863\u5c11\u5e74\u3002 \u3000\u3000\u4e00\u773c\u95f4\uff0c\u5c31\u770b\u51fa\u4e86\u8fd9\u4e24\u4e2a\u7070\u8863\u5927\u6c49\uff0c\u4e0d\u662f\u5bb9\u6613\u5bf9\u4ed8\u7684\u4eba\uff0c\u90a3\u767d\u8863\u5c11\u5e74\u4f3c\u4e4e\u662f\u66f4\u96be\u5bf9\u4ed8\u3002 \u3000\u3000\u6bcf\u4e00\u5bb6\u9152\u9986\uff0c\u90fd\u6709\u4e00\u95f4\u5bc6\u5ba4\u3002 \u3000\u3000\u90a3\u662f\u4e00\u95f4\u5e03\u7f6e\u5f97\u5f88\u9ad8\u96c5\u7684\u623f\u95f4\uff0c\u4fbf\u5229\u9152\u9986\u7684\u5ba2\u4eba\u4ea4\u6613\u3002 \u3000\u3000\u767d\u8863\u4eba\u5728\u5927\u6c49\u7684\u62a4\u4f8d\u4e0b\uff0c\u884c\u5165\u4e86\u5bc6\u5ba4\uff0c\u800c\u4e14\uff0c\u623f\u95e8\u7acb\u523b\u88ab\u63a9\u4e0a\u3002 \u3000\u3000\u5723\u6cc9\u9547\u7684\u89c4\u77e9\uff0c\u5e97\u5c0f\u4e8c\uff0c\u7edd\u5bf9\u4e0d\u95ee\u5ba2\u4eba\u7684\u4e8b\u3002 \u3000\u3000\u590f\u6740\u548c\u53f6\u957f\u9752\uff0c\u88ab\u9694\u7edd\u4e8e\u5bc6\u5ba4\u95e8\u5916\u3002\u4f46\u8fd9\u4e5f\u7ed9\u4e88\u4e86\u590f\u6740\u548c\u53f6\u957f\u9752\u5f88\u597d\u7684\u8ba1\u8bae\u673a\u4f1a\u3002 \u3000\u3000\u53f6\u957f\u9752\u4f4e\u58f0\u9053\uff1a\u201c\u590f\u5144\uff0c\u90a3\u767d\u8863\u4eba\u5c31\u662f\u54b1\u4eec\u8981\u6740\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u6211\u8fd8\u60f3\u4e0d\u51fa\u5982\u4f55\u4e0b\u624b\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4e58\u5176\u4e0d\u5907\uff0c\u7a81\u7136\u4e0b\u624b\uff0c\u90a3\u662f\u6700\u597d\u7684\u65f6\u673a\uff0c\u4e0d\u8fc7\uff0c\u6211\u770b\u8fd9\u4e09\u4e2a\u4eba\uff0c\u90fd\u6709\u5f88\u9ad8\u7684\u8b66\u89c9\u5fc3\uff0c\u53ea\u6015\u5f88\u96be\u6709\u8fd9\u4e2a\u673a\u4f1a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54b1\u4eec\u8fdb\u5165\u90a3\u5bc6\u5ba4\u7684\u673a\u4f1a\uff0c\u53ea\u6709\u5728\u9001\u9152\u83dc\u7684\u65f6\u5019\uff0c\u5982\u82e5\u54b1\u4eec\u4e0d\u80fd\u5728\u90a3\u65f6\u523b\u4e0b\u624b\uff0c\u90a3\u5c31\u53ea\u6709\u51fa\u624b\u786c\u62fc\u4e00\u9014\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u673a\u4f1a\uff0c\u90a3\u5c31\u662f\u4ed6\u4eec\u79bb\u5f00\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\uff0c\u6211\u770b\u8fd9\u767d\u8863\u4eba\u5f88\u6709\u6c14\u52bf\uff0c\u5b9e\u5728\u662f\u4e00\u4e2a\u4e0d\u5bb9\u6613\u5bf9\u4ed8\u7684\u4eba\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5982\u82e5\u662f\u5f88\u5bb9\u6613\u5bf9\u4ed8\u7684\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u6d3e\u4f60\u53f6\u5144\u51fa\u624b\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u5f88\u5389\u5bb3\uff0c\u4e00\u5f00\u53e3\u5c31\u628a\u8d23\u4efb\u5957\u5728\u53f6\u957f\u9752\u7684\u5934\u4e0a\uff0c\u597d\u50cf\u53f6\u957f\u9752\u662f\u4e3b\u529b\uff0c\u4ed6\u53ea\u662f\u4e00\u4e2a\u526f\u624b\u3002 \u3000\u3000\u53f6\u957f\u9752\u6de1\u8c08\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u82e5\u54b1\u4eec\u6ca1\u6709\u628a\u63e1\u80fd\u6740\u4e86\u5bf9\u65b9\uff0c\u590f\u5144\uff0c\u89c9\u5f97\u5e94\u8be5\u5982\u4f55\u5e94\u4ed8\uff1f\u201d \u3000\u3000\u590f\u6740\u4e00\u76f4\u62c5\u5fc3\u53f6\u957f\u9752\u4f1a\u7ffb\u8138\u76f8\u5411\uff0c\u548c\u654c\u4eba\u8054\u624b\uff0c\u81ea\u5df1\u5c31\u975e\u6b7b\u4e0d\u53ef\u3002 \u3000\u3000\u654c\u5bf9\u7684\u529b\u91cf\uff0c\u518d\u52a0\u4e0a\u53f6\u957f\u9752\u65f6\uff0c\u590f\u6740\u5fc3\u4e2d\u660e\u767d\uff0c\u81ea\u5df1\u8fde\u2014\u62db\u4e5f\u6321\u4e0d\u8fc7\u53bb\u3002 \u3000\u3000\u590f\u6740\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4eba\u5728\u9762\u5bf9\u7740\u6b7b\u4ea1\u65f6\uff0c\u603b\u5e94\u8be5\u66ff\u81ea\u5df1\u6253\u7b97\u4e00\u4e0b\uff0c\u53f6\u5144\u6709\u4ec0\u4e48\u81ea\u4fdd\u7684\u9ad8\u89c1\uff0c\u8fd8\u671b\u6307\u6559\u5144\u5f1f\u2014\u4e8c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u662f\u62c5\u5fc3\u54b1\u4eec\u4e00\u51fb\u4e0d\u4e2d\u5fc5\u5c06\u4f1a\u62db\u81f4\u5f3a\u70c8\u7684\u53cd\u51fb\uff0c\u90a3\u65f6\uff0c\u54b1\u4eec\u662f\u5426\u4f1a\u6709\u63f4\u624b\u76f8\u52a9\uff1f\u201d \u3000\u3000\u590f\u6740\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u4e24\u4e2a\u4eba\u6765\uff0c\u751f\u4e5f\u662f\u54b1\u4eec\u4e24\u4e2a\uff0c\u6b7b\u4e5f\u662f\u54b1\u4eec\u4e24\u4e2a\u3002\u201d \u3000\u3000\u5bc6\u5ba4\u7684\u95e8\u7a81\u7136\u6253\u5f00\uff0c\u4e00\u4e2a\u7a7f\u7740\u7070\u8863\u7684\u5927\u6c49\uff0c\u51fa\u73b0\u5728\u95e8\u53e3\uff0c\u9053\uff1a\u201c\u9152\u83dc\u5907\u597d\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u51c6\u5907\u597d\u4e86\uff0c\u53ef\u8981\u9001\u5165\u5ba4\u4e2d\u5417\uff1f\u201d \u3000\u3000\u7070\u8863\u5927\u6c49\u7a81\u7136\u6539\u53d8\u4e86\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u73b0\u5728\u4ec0\u4e48\u65f6\u523b\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5348\u65f6\u4e09\u523b\u3002\u201d \u3000\u3000\u7070\u8863\u5927\u6c49\u9053\uff1a\u201c\u597d\uff01\u5feb\u4e9b\u9001\u4e0a\u9152\u83dc\uff0c\u54b1\u4eec\u7b49\u5019\u7684\u5ba2\u4eba\u5c31\u8981\u5230\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u5e94\u4e86\u4e00\u58f0\uff0c\u8f6c\u8eab\u884c\u53bb\u3002 \u3000\u3000\u7070\u8863\u5927\u6c49\u63a5\u9053\uff1a\u201c\u7b49\u4e00\u4e0b\uff0c\u6709\u4e24\u4f4d\u5ba2\u4eba\u6765\u627e\u54b1\u4eec\uff0c\u53ef\u4ee5\u653e\u4ed6\u4eec\u8fdb\u6765\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u662f\u4e24\u4e2a\u4ec0\u4e48\u6837\u5b50\u7684\u5ba2\u4eba\uff1f\u201d \u3000\u3000\u7070\u8863\u5927\u6c49\u9053\uff1a\u201c\u4e00\u4e2a\u59d3\u7f57\u7684\uff0c\u4f60\u4eec\u53ea\u8981\u62e6\u4f4f\u95ee\u4ed6\u4eec\u4e00\u58f0\uff0c\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c0f\u7684\u9075\u547d\u3002\u201d \u3000\u3000\u590f\u6740\u6367\u4e86\u9152\u83dc\u3002 \u3000\u3000\u53f6\u957f\u9752\u5374\u7a81\u7136\u8f6c\u8eab\uff0c\u884c\u5230\u4e86\u5927\u95e8\u53e3\u5904\u3002 \u3000\u3000\u5f62\u52bf\u7684\u53d8\u5316\uff0c\u4f7f\u5f97\u590f\u6740\u4e0d\u5f97\u4e0d\u786c\u7740\u5934\u76ae\uff0c\u628a\u9152\u83dc\u9001\u5165\u4e86\u5bc6\u5ba4\u3002 \u3000\u3000\u542c\u5230\u5f00\u95e8\u7684\u58f0\u97f3\uff0c\u53f6\u957f\u9752\u624d\u56de\u671b\u4e86\u4e00\u773c\u3002 \u3000\u3000\u590f\u6740\u5c3d\u91cf\u907f\u514d\u522b\u4eba\u7684\u7591\u5fc3\uff0c\u653e\u4e0b\u9152\u83dc\u4e4b\u540e\uff0c\u7acb\u523b\u8f6c\u8eab\u800c\u51fa\u3002 \u3000\u3000\u5bc6\u5ba4\u7684\u95e8\u91cd\u53c8\u5173\u95ed\uff0c\u590f\u6740\u5374\u6084\u7136\u884c\u5230\u53f6\u957f\u9752\u7684\u8eab\u4fa7\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u53f6\u5144\uff0c\u60c5\u5f62\u6709\u4e9b\u4e0d\u5bf9\u4e86\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5144\u5f1f\u53d1\u89c9\u4e86\uff0c\u4ed6\u4eec\u5bf9\u54b1\u4eec\u4e00\u76f4\u6709\u7740\u5f88\u6df1\u7684\u6212\u5fc3\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u96be\u9053\uff0c\u4ed6\u4eec\u5df2\u53d1\u89c9\u4e86\u54b1\u4eec\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5144\u5f1f\u4e00\u76f4\u5728\u4e3a\u8fd9\u4ef6\u4e8b\u62c5\u5fe7\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u662f\u798f\u4e0d\u662f\u7978\uff0c\u662f\u7978\u8eb2\u4e0d\u8fc7\uff0c\u54b1\u4eec\u5df2\u7ecf\u9519\u8fc7\u4e86\u4e24\u6b21\u4e0b\u624b\u7684\u673a\u4f1a\uff0c\u4e00\u6b21\u662f\u4ed6\u4eec\u8fdb\u5165\u9152\u9986\u7684\u65f6\u5019\uff0c\u4e00\u6b21\u662f\u4f60\u9001\u5165\u9152\u83dc\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u9001\u9152\u83dc\u7684\u53ea\u5144\u5f1f\u4e00\u4e2a\u4eba\uff0c\u4ed6\u4eec\u867d\u6709\u6212\u5fc3\uff0c\u4f46\u90a3\u5730\u65b9\uff0c\u4ecd\u7136\u662f\u4e00\u4e2a\u4e0b\u624b\u7684\u597d\u673a\u4f1a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\uff0c\u73b0\u5728\u54b1\u4eec\u8fd8\u6709\u4e24\u4e2a\u673a\u4f1a\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4ec0\u4e48\u673a\u4f1a\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e00\u4e2a\u662f\u4ed6\u4eec\u5ba2\u4eba\u6765\u7684\u65f6\u5019\uff0c\u54b1\u4eec\u53ef\u4ee5\u6df7\u6c34\u6478\u9c7c\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u8fd8\u6709\u5462\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u4eec\u4ea4\u6613\u5b8c\u6210\uff0c\u79bb\u5f00\u7684\u65f6\u5019\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u8bf4\u4e86\u5f88\u591a\u7684\u8bdd\uff0c\u5e0c\u671b\u590f\u6740\u80fd\u8bf4\u51fa\u81ea\u5df1\u7684\u4e3b\u610f\uff0c\u4f46\u590f\u6740\u4e00\u76f4\u552f\u552f\u8bfa\u8bfa\uff0c\u8bf4\u4e0d\u51fa\u4e00\u4e2a\u5b8c\u7f8e\u7684\u8ba1\u5212\u51fa\u6765\u3002 \u3000\u3000 \u3000\u3000\u4e2d\u5348\u8fc7\u540e\u4e0d\u4e45\uff0c\u679c\u7136\u6709\u4e24\u4e2a\u4eba\u76f4\u5411\u9152\u9986\u884c\u4e86\u8fc7\u6765\uff0c\u4e24\u4e2a\u7a7f\u7740\u9ed1\u8863\u7684\u4eba\u3002 \u3000\u3000\u5f53\u5148\u4e00\u4eba\uff0c\u5e74\u7ea6\u4e09\u5341\u5de6\u53f3\uff0c\u8eab\u6750\u5f88\u7626\uff0c\u4f46\u8138\u8272\u7ea2\u6da6\uff0c\u76ee\u5149\u795e\u91c7\u5955\u5955\uff0c\u4e00\u671b\u5373\u77e5\u56db\u5c5e\u4e8e\u5185\u529f\u7cbe\u6df1\u7684\u4eba\u7269\u3002 \u3000\u3000\u540e\u9762\u4e00\u4e2a\u9ed1\u8863\u4eba\uff0c\u6234\u4e86\u9ed1\u8272\u7684\u7ed2\u5e3d\uff0c\u6574\u4e2a\u4eba\u53ea\u663e\u9732\u51fa\u4e24\u53ea\u773c\u775b\u3002 \u3000\u3000\u800c\u4e14\uff0c\u662f\u4e24\u53ea\u534a\u7741\u534a\u95ed\u7684\u773c\u775b\u3002 \u3000\u3000\u90a3\u5f53\u5148\u7684\u9ed1\u8863\u4eba\u884c\u5230\u5e97\u95e8\u53e3\u5904\uff0c\u505c\u4e0b\u811a\u6b65\uff0c\u9053\uff1a\u201c\u6211\u59d3\u7f57\uff0c\u548c\u4eba\u6709\u7ea6\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u3002\u201d \u3000\u3000\u5f53\u5148\u9ed1\u8863\u5165\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8c22\u4e86\u3002\u201d\u4e3e\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u590f\u6740\u968f\u5728\u7b2c\u4e8c\u4e2a\u9ed1\u8863\u5165\u7684\u8eab\u540e\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u521a\u5230\u5bc6\u5ba4\u95e8\u53e3\uff0c\u5ba4\u95e8\u5374\u7a81\u7136\u5927\u5f00\u3002 \u3000\u3000\u4e00\u4e2a\u7070\u8863\u5927\u6c49\u7f13\u7f13\u884c\u4e86\u51fa\u6765\uff0c\u4e00\u6307\u590f\u6740\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u4f60\u4eec\u7167\u987e\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u5e94\u4e86\u4e00\u58f0\uff0c\u8f6c\u8eab\u800c\u53bb\uff0c \u3000\u3000\u4e24\u4e2a\u9ed1\u8863\u4eba\u7f13\u7f13\u6b65\u5165\u5bc6\u5ba4\u4e4b\u4e2d\u3002 \u3000\u3000\u5bc6\u5ba4\u7684\u95e8\uff0c\u53c8\u6025\u6025\u5173\u4e0a\u3002 \u3000\u3000\u590f\u6740\u884c\u5230\u4e86\u53f6\u957f\u9752\u7684\u8eab\u4fa7\uff0c\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6211\u770b\uff0c\u4ed6\u4eec\u7684\u9632\u8303\u5f88\u4e25\u5bc6\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u53ea\u6709\u786c\u62fc\u4e86\uff0c\u590f\u5144\u7ecf\u9a8c\u4e30\u5bcc\u4e00\u4e9b\uff0c\u4f60\u5148\u51fa\u624b\u5982\u4f55\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u624d\u662f\u8fd9\u4e00\u6b21\u884c\u52a8\u7684\u4e3b\u529b\uff0c\u5144\u5f1f\u662f\u5e2e\u5fd9\u6253\u6742\u7684\uff0c\u800c\u4e14\uff0c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u5c31\u8981\u51ed\u6b66\u529f\u7f20\u6597\u4e86\uff0c\u5509\uff01\u5144\u5f1f\u505a\u4e86\u4e0d\u5c11\u5e74\u7684\u6740\u624b\uff0c\u8fd9\u4e00\u6b21\uff0c\u662f\u6211\u9047\u4e0a\u6700\u56f0\u96be\u7684\u4e00\u6b21\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\u5427\uff01\u590f\u5144\u5982\u6b64\u770b\u91cd\u5144\u5f1f\uff0c\u5144\u5f1f\u53ea\u597d\u52c9\u4e3a\u5176\u96be\u4e86\uff0c\u4e0d\u8fc7\uff0c\u5236\u9020\u5144\u5f1f\u51fa\u624b\u7684\u673a\u4f1a\uff0c\u8fd8\u8981\u590f\u5144\u8d39\u70b9\u5fc3\u601d\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5144\u5f1f\u7406\u5e94\u7b79\u8c0b\u3002\u201d \u3000\u3000\u5927\u7ea6\u6709\u4e00\u987f\u996d\u7684\u5de5\u592b\uff0c\u5bc6\u5ba4\u95e8\u6237\u7a81\u7136\u5927\u5f00\uff0c\u4e24\u4e2a\u9ed1\u8863\u4eba\u5306\u5306\u800c\u53bb\u3002 \u3000\u3000\u5bc6\u5ba4\u6728\u95e8\uff0c\u91cd\u53c8\u95ed\u4e0a\u3002 \u3000\u3000\u4e24\u4eba\u8981\u6740\u7684\u5bf9\u8c61\uff0c\u65e2\u662f\u90a3\u767d\u8863\u5c11\u5e74\uff0c\u4e3a\u4e86\u6015\u6253\u8349\u60ca\u86c7\uff0c\u53ea\u6709\u773c\u770b\u770b\u90a3\u9ed1\u8863\u4eba\u79bb\u5e97\u800c\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u5df2\u7ecf\u5b8c\u6210\u4e86\u4e00\u7b14\u4ea4\u6613\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u4ea4\u6613\u8fdb\u884c\u7684\u5f88\u987a\u5229\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54b1\u4eec\u73b0\u5728\u53ea\u6709\u4e00\u4e2a\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5e0c\u671b\u4ed6\u4eec\u80fd\u65e9\u4e00\u4e9b\u79bb\u5f00\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u4ed6\u4eec\u5982\u82e5\u591a\u559d\u4e9b\u9152\uff0c\u628a\u65f6\u95f4\u62d6\u957f\u4e86\uff0c\u53f6\u5144\u7684\u6bd2\u6027\u53d1\u4f5c\uff0c\u5728\u4e0b\u4e00\u4eba\u66f4\u65e0\u6cd5\u5e94\u4ed8\u8fd9\u4e2a\u5c40\u9762\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u7528\u4e0d\u7740\u590f\u5144\u7684\u63d0\u9192\uff0c\u6211\u4e00\u76f4\u5728\u60f3\u7740\u6bd2\u6027\u5c06\u53d1\u7684\u4e8b\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5f20\u638c\u67dc\u5e94\u8be5\u6709\u89e3\u836f\u624d\u662f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c31\u4ed6\u6709\u89e3\u836f\uff0c\u4ed6\u4e5f\u505a\u4e0d\u4e86\u4e3b\u3002\u201d \u3000\u3000\u4e24\u4e2a\u7070\u8863\u5927\u6c49\uff0c\u7a81\u7136\u884c\u51fa\u4e86\u5bc6\u5ba4\u3002 \u3000\u3000\u590f\u6740\u7cbe\u795e\u4e00\u9707\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff0c\u8bf8\u4f4d\u8981\u8d70\u4e86\uff1f\u201d \u3000\u3000\u5de6\u9996\u7684\u7070\u8863\u5927\u6c49\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u770b\u6765\u4e24\u4f4d\u90fd\u4e0d\u50cf\u9152\u9986\u7684\u4f19\u8ba1\u3002\u201d \u3000\u3000\u590f\u6740\u5fc3\u5934\u4e00\u9707\uff0c\u7b11\u9053\uff1a\u201c\u96be\u9053\u5e97\u4f19\u8ba1\u8981\u5728\u5934\u4e0a\u523b\u4e2a\u5b57\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5de6\u9996\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u523b\u5b57\u5012\u4e0d\u7528\uff0c\u4f46\u4f60\u4eec\u7ba1\u7684\u4e8b\u60c5\u592a\u591a\uff0c\u90a3\u5c31\u4f1a\u9732\u51fa\u9a6c\u811a\u3002\u201d \u3000\u3000\u590f\u6740\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u3002\u505a\u4e00\u4e2a\u5e97\u4f19\u8ba1\uff0c\u7adf\u7136\u4e5f\u6709\u8fd9\u4e48\u591a\u7684\u56f0\u96be\u3002\u201d \u3000\u3000\u7070\u8863\u4eba\u9053\uff1a\u201c\u5e97\u4f19\u8ba1\u5e76\u4e0d\u56f0\u96be\uff0c\u56f0\u96be\u662f\u5192\u5145\u5e97\u4f19\u8ba1\u7684\u4eba\uff0c\u800c\u4e14\uff0c\u53c8\u5192\u5145\u7684\u4e0d\u592a\u50cf\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u54b1\u4eec\u672c\u6765\u4e5f\u4e0d\u662f\u5e72\u5e97\u5c0f\u4e8c\u7684\u4eba\u3002\u201d \u3000\u3000\u7070\u8863\u4eba\u9053\uff1a\u201c\u54e6\uff01\u4e24\u4e2a\u65e2\u7136\u4e0d\u662f\u5e97\u4f19\u8ba1\uff0c\u4e3a\u4ec0\u4e48\u5374\u7a7f\u4e86\u5e97\u4f19\u8ba1\u7684\u8863\u670d\uff0c\u88c5\u4f5c\u70b9\u4f19\u8ba1\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4e01\u638c\u67dc\u82b1\u4e86\u2014\u4e9b\u94f6\u5b50\uff0c\u8981\u6211\u4eec\u88c5\u4f5c\u5e97\u4f19\u8ba1\uff0c\u597d\u50cf\u662f\u4e3a\u4e86\u4fdd\u62a4\u4e09\u4f4d\u5427\uff1f\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u7a81\u7136\u51fa\u73b0\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4e01\u638c\u67dc\u8fd8\u4ea4\u4ee3\u4f60\u4eec\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4e01\u638c\u67dc\u8bf4\uff0c\u8fd9\u91cc\u53ef\u80fd\u4f1a\u53d1\u751f\u4e00\u70b9\u9ebb\u70e6\uff0c\u5e0c\u671b\u6211\u4eec\u80fd\u591f\u5e2e\u4f60\u4e00\u70b9\u5fd9\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4e01\u638c\u67dc\u5b9e\u5728\u662f\u5f88\u7ec6\u5fc3\u7684\u4eba\uff0c\u6211\u4eec\u5728\u8fd9\u91cc\u82b1\u4e86\u4e0d\u5c11\u7684\u94f6\u5b50\uff0c\u603b\u7b97\u4e70\u5230\u4e86\u4e00\u70b9\u4ea4\u60c5\uff0c\u4f46\u4e0d\u77e5\u4e0b\u638c\u67dc\u544a\u8bc9\u4f60\u4eec\u6ca1\u6709\uff1f\u201d \u3000\u3000\u590f\u6740\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u544a\u8bc9\u4eec\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4e01\u638c\u67dc\u6ca1\u6709\u544a\u8bc9\u4f60\u6211\u662f\u8c01\uff1f\u6211\u7684\u59d3\u540d\uff1f\u201d \u3000\u3000\u590f\u6740\u906d\uff1a\u201c\u6ca1\u6709\uff0c\u4e01\u638c\u67dc\u662f\u4e00\u4e2a\u5f88\u7ec6\u5fc3\u7684\u4eba\uff0c\u6240\u4ee5\uff0c\u4ed6\u53ea\u544a\u8bc9\u6211\u4eec\u5e2e\u52a9\u4e00\u4e2a\u5e74\u8f7b\u4eba\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u7a7f\u7740\u767d\u8863\u7684\u5e74\u8f7b\u4eba\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5bf9\uff01\u4e01\u638c\u67dc\u662f\u8fd9\u4e48\u8bf4\u7684\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u7f13\u6b65\u884c\u5165\u5385\u4e2d\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u4e01\u638c\u67dc\u771f\u662f\u8fd9\u4e48\u544a\u8bc9\u4e24\u4f4d\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u4e00\u76f4\u6ca1\u6709\u8bf4\u8bdd\uff0c\u53ea\u662f\u9759\u9759\u7684\u7ad9\u5728\u2014\u4fa7\u3002 \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff01\u4e01\u638c\u67dc\u786e\u7136\u662f\u8fd9\u4e48\u8bf4\u7684\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ef\u60dc\u5440\uff01\u53ef\u60dc\uff01\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u6211\u6839\u672c\u4e0d\u8ba4\u8bc6\u4e01\u638c\u67dc\uff0c\u6211\u4e5f\u662f\u7b2c\u4e00\u6b21\u5230\u5723\u6cc9\u9547\u6765\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u9601\u4e0b\u5982\u82e5\u80fd\u6c89\u5f97\u4f4f\u6c14\uff0c\u4e5f\u8bb8\uff0c\u6211\u4eec\u8fd8\u5f04\u4e0d\u6e05\u695a\u4e24\u4f4d\u7684\u8eab\u4efd\uff0c\u5bf9\u4e00\u4e2a\u6740\u624b\u6765\u8bf4\uff0c\u9601\u4e0b\u6709\u4e9b\u5931\u4e4b\u8f7b\u7387\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u9762\u8272\u60e8\u767d\uff0c\u56de\u987e\u4e86\u53f6\u957f\u9752\u4e00\u773c\u3002 \u3000\u3000\u4e8b\u60c5\u7684\u53d8\u5316\uff0c\u5df2\u4f7f\u590f\u6740\u548c\u53f6\u957f\u9752\u5b8c\u5168\u6ca1\u6709\u4e86\u7a81\u7136\u51fa\u624b\u7684\u673a\u4f1a\u3002 \u3000\u3000\u53f6\u957f\u9752\u7f13\u7f13\u8f6c\u8fc7\u8eab\u5b50\uff0c\u9762\u5bf9\u7740\u767d\u8863\u5c11\u5e74\uff0c\u9053\uff1a\u201c\u6740\u624b\u5fc5\u6709\u4e24\u79cd\u6740\u4eba\u7684\u65b9\u6cd5\uff0c\u4e00\u79cd\u662f\u6697\u6740\uff0c\u4e00\u79cd\u662f\u660e\u6740\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u627f\u6559\uff0c\u627f\u6559\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u770b\u6765\uff0c\u6211\u4eec\u4f3c\u4e4e\u662f\u65e0\u6cd5\u6697\u6740\u9601\u4e0b\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u4e24\u4f4d\u5982\u82e5\u4e0d\u662f\u5728\u9152\u83dc\u4e4b\u4e2d\u4e0b\u6bd2\uff0c\u6697\u6740\u548c\u660e\u6740\uff0c\u4e5f\u6ca1\u6709\u5f88\u5927\u7684\u533a\u522b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5bf9\u4e00\u4e2a\u6c5f\u6e56\u9ad8\u624b\u800c\u8a00\uff0c\u51fa\u624b\u7684\u6697\u88ad\uff0c\u672a\u5fc5\u5c31\u80fd\u4f24\u5f97\u4e86\u4ed6\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u8138\u8272\u7a81\u7136\u4e25\u8083\u8d77\u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u53eb\u53f6\u957f\u9752\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u8138\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u4f46\u7acb\u523b\u53c8\u6062\u590d\u9547\u9759\u3002\u6700\u611f\u9707\u60ca\u7684\u662f\u590f\u6740\uff0c\u60f3\u4e0d\u5230\uff0c\u5bf9\u65b9\u7adf\u7136\u628a\u81ea\u5df1\u5f04\u5f97\u8fd9\u6837\u6e05\u695a\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u53f6\u957f\u9752\u5df2\u7ecf\u6b7b\u4e86\uff0c\u6211\u662f\u4e03\u5251\u8ffd\u9b42\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4e03\u5251\u8ffd\u9b42\uff0c\u662f\u4e0d\u662f\u53f6\u957f\u9752\u7684\u5916\u53f7\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u53f3\u624b\u4e00\u626c\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u67c4\u5251\u3002 \u3000\u3000\u539f\u6765\uff0c\u4ed6\u7684\u5251\u7adf\u7136\u85cf\u5728\u8896\u4e2d\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u51fa\u9053\u4ee5\u6765\uff0c\u8fd8\u6ca1\u6709\u4eba\u80fd\u63a5\u4e0b\u4f60\u4e03\u5251\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\u50cf\u5982\u6b64\u3002\u201d \u3000\u3000\u81ea\u8863\u5c11\u5e74\u9053\uff1a\u201c\u76db\u540d\u5f97\u6765\u4e0d\u6613\uff0c\u6211\u5c31\u60f3\u4e0d\u901a\uff0c\u4f60\u4e3a\u4f55\u7adf\u7136\u4e0d\u73cd\u60dc\uff0c\u5802\u5802\u7684\u53f6\u5927\u4fa0\uff0c\u7adf\u7136\u4f1a\u6ca6\u4e3a\u6740\u624b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u6700\u597d\u662f\u4eae\u51fa\u4f60\u7684\u5175\u5203\uff0c\u56e0\u4e3a\uff0c\u6211\u5c31\u8981\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u5251\u5728\u4f60\u7684\u624b\u4e2d\uff0c\u51fa\u624b\u4e0e\u5426\uff0c\u6089\u542c\u5c0a\u4fbf\u4e86\u3002\u201d \u3000\u3000\u6709\u8fd9\u4e48\u4e00\u4e2a\u5251\u624b\u5728\u4fa7\uff0c\u4f7f\u5f97\u590f\u6740\u7684\u5fc3\u60c5\u5f00\u6717\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u56de\u987e\u4e86\u4e24\u4e2a\u7070\u8863\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u9000\u5f00\u53bb\u3002\u201d \u3000\u3000\u4e24\u4e2a\u7070\u8863\u4eba\u5e94\u4e86\u4e00\u58f0\uff0c\u9000\u5230\u767d\u8863\u5c11\u5e74\u7684\u8eab\u540e\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u76ee\u5149\u8f6c\u6ce8\u5230\u53f6\u957f\u9752\u7684\u8eab\u4e0a\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8ffd\u9b42\u4e03\u5251\uff0c\u679c\u7136\u662f\u540d\u4e0d\u865a\u4f20\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9601\u4e0b\u53ef\u4ee5\u4eae\u51fa\u5175\u5203\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u5c3d\u7ba1\u51fa\u624b\uff0c\u8be5\u4eae\u5175\u5203\u7684\u65f6\u5019\uff0c\u6211\u81ea\u4f1a\u4eae\u51fa\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u770b\u6765\uff0c\u9601\u4e0b\u5b8c\u5168\u4e0d\u628a\u5728\u4e0b\u653e\u5728\u773c\u4e2d\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5982\u8bf4\uff0c\u4f60\u4e03\u5251\u8ffd\u9b42\u662f\u6b66\u6797\u4e2d\u624d\u4fca\u4e4b\u58eb\uff0c\u96be\u9053\u4f60\u5c31\u53ef\u4ee5\u4e0d\u6740\u6211\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u81ea\u7136\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u65e2\u7136\u662f\u96be\u514d\u4e00\u6218\uff0c\u5728\u4e0b\u603b\u4e0d\u80fd\u5148\u5938\u8d5e\u4f60\u4e03\u5251\u8ffd\u9b42\u4e00\u9635\u5427\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u8bf4\u4ec0\u4e48\uff0c\u5728\u4e0b\u4eca\u65e5\u975e\u8981\u6740\u4e86\u4f60\u4e0d\u53ef\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u54b1\u4eec\u5f80\u65e5\u65e0\u6028\uff0c\u8fd1\u65e5\u65e0\u4ec7\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u65e2\u7136\u51b3\u5fc3\u8981\u6740\u4f60\uff0c\u4f3c\u4e4e\u662f\u7528\u4e0d\u7740\u8bf4\u51fa\u4e00\u756a\u7406\u7531\u5427\uff01\u201d \u3000\u3000\u7a81\u7136\u4e00\u626c\u624b\uff0c\u2014\u5251\u523a\u51fa\u3002\u4ed6\u53f7\u79f0\u4e03\u5251\u8ffd\u9b42\uff0c\u653b\u51fa\u7684\u5251\u52bf\uff0c\u81ea\u6709\u975e\u540c\u5c0f\u53ef\u7684\u5a01\u52bf\u3002\u4e00\u5251\u76f4\u523a\u767d\u8863\u5c11\u5e74\u7684\u5fc3\u810f\u8981\u5bb3\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u8eab\u5b50\u5fae\u5fae\u4e00\u4fa7\uff0c\u659c\u9000\u4e24\u6b65\uff0c\u907f\u8fc7\u4e00\u5251\u3002 \u3000\u3000\u4e24\u4e2a\u7070\u8863\u4eba\u7a81\u7136\u5c71\u624b\u3002\u4e24\u628a\u7f05\u94c1\u8f6f\u5200\uff0c\u5206\u7531\u4e24\u4fa7\uff0c\u653b\u5411\u4e86\u53f6\u957f\u9752\u3002 \u3000\u3000\u53f6\u957f\u9752\u957f\u5251\u2014\u7ede\uff0c\u624b\u4e2d\u4e4b\u5251\uff0c\u5ffd\u7136\u95ea\u8d77\u4e86\u4e00\u7247\u5bd2\u8292\u3002 \u3000\u3000\u4f46\u95fb\u9535\u9535\u4e24\u58f0\uff0c\u4e24\u67c4\u7f05\u94c1\u8f6f\u5200\uff0c\u7adf\u7136\u88ab\u2014\u5251\u9707\u5f00\u3002 \u3000\u3000\u53f6\u957f\u9752\u957f\u5251\u53cd\u523a\uff0c\u5237\u5237\u4e24\u5251\uff0c\u4ee5\u7535\u5149\u77f3\u706b\u822c\u7684\u901f\u5ea6\uff0c\u53cd\u523a\u8fc7\u53bb\u3002 \u3000\u3000\u5251\u5982\u95ea\u7535\uff0c\u4e24\u4e2a\u7070\u8863\u5927\u6c49\uff0c\u540c\u65f6\u4e22\u5f03\u4e86\u624b\u4e2d\u7684\u7f05\u5200\u3002 \u3000\u3000\u539f\u6765\uff0c\u4e24\u4e2a\u7070\u8863\u5927\u6c49\u6267\u5200\u7684\u6709\u8155\uff0c\u5404\u4e2d\u4e86\u4e00\u5251\uff0c\u9c9c\u8840\u6dcb\u6f13\u800c\u4e0b\u3002\u5251\u5c16\u7531\u624b\u80cc\uff0c\u76f4\u900f\u624b\u638c\uff0c\u518d\u4e5f\u65e0\u6cd5\u63e1\u5f97\u4f4f\u7f05\u5200\u3002 \u3000\u3000\u4e24\u4e2a\u7070\u8863\u4eba\u7684\u8138\u8272\u53d8\u4e86\uff0c\u590f\u6740\u7684\u8138\u4e0a\uff0c\u5374\u6cdb\u8d77\u4e86\u4e00\u7247\u7b11\u5bb9\u3002 \u3000\u3000\u4e45\u95fb\u8ffd\u9b42\u4e03\u5251\u7684\u5389\u5bb3\uff0c\u4eca\u65e5\u603b\u7b97\u662f\u5f00\u4e86\u773c\u754c\u3002 \u3000\u3000\u90a3\u771f\u662f\u95ea\u7535\u5954\u96f7\u4e00\u822c\u7684\u5feb\u901f\u5251\u52bf\uff0c\u4ee5\u590f\u6740\u7684\u6b66\u529f\uff0c\u7adf\u7136\u672a\u77a7\u51fa\u90a3\u4e24\u5251\u662f\u5982\u4f55\u523a\u4e2d\u4e86\u4e24\u4e2a\u7070\u8863\u4eba\u7684\u53f3\u624b\u3002 \u3000\u3000\u679c\u7136\u975e\u540c\u51e1\u54cd\u7684\u5251\u6cd5\u3002 \u3000\u3000\u53f6\u957f\u9752\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u72c2\u3002\u201d \u3000\u3000\u5ffd\u7136\u4e00\u5251\uff0c\u523a\u5411\u524d\u5fc3\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u53f3\u624b\u4e00\u62ac\uff0c\u7adf\u7136\u7528\u53f3\u8155\u76f4\u8fce\u5229\u5251\u3002 \u3000\u3000\u53f6\u957f\u9752\u51b7\u54fc\u4e00\u58f0\uff0c\u5b9d\u5251\u4e00\u504f\uff0c\u5251\u4fa7\u8f6c\uff0c\u524a\u5411\u624b\u8155\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u7684\u53f3\u8155\uff0c\u649e\u5728\u5251\u950b\u4e4b\u4e0a\u3002 \u3000\u3000\u4f46\u95fb\u5f53\u7684\u4e00\u58f0\uff0c\u957f\u5251\u7adf\u88ab\u767d\u8863\u5c11\u5e74\u7684\u53f3\u8155\u9707\u5f00\u3002 \u3000\u3000\u53f6\u957f\u9752\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u4e3e\u8d77\u53f3\u624b\uff0c\u9732\u51fa\u4e86\u8863\u8896\uff0c\u53ea\u89c1\u4ed6\u624b\u8155\u4e4b\u4e0a\uff0c\u5e26\u7740\u4e00\u4e2a\u4e00\u5bf8\u591a\u5bbd\u7684\u94f6\u8272\u7684\u62a4\u8155\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4f60\u7684\u5175\u5203\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u52c9\u5f3a\u7b97\u5f97\u4e0a\u662f\u5175\u5203\u5427\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u53ea\u6015\u5b83\u4fdd\u4e0d\u4f4f\u4f60\u7684\u547d\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u53f6\u5927\u4fa0\uff0c\u8bd5\u8bd5\u770b\u5427\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u8981\u53eb\u6211\u53f6\u5927\u4fa0\uff0c\u53f6\u957f\u9752\u5df2\u7ecf\u6b7b\u4e86\uff0c\u6d3b\u7740\u7684\u662f\u4e03\u5251\u8ffd\u9b42\u3002\u201d \u3000\u3000\u5ffd\u7136\u5b9d\u5251\u4e00\u626c\uff0c\u7f13\u7f13\u523a\u4e86\u8fc7\u53bb\u3002\u5251\u5c16\u98a4\u52a8\uff0c\u95ea\u8d77\u4e86\u6735\u6735\u5251\u82b1\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u8138\u8272\u7a81\u7136\u95ee\u53d8\u7684\u5341\u5206\u51dd\u91cd\uff0c\u53cc\u624b\u5e73\u4e3e\uff0c\u4ea4\u653e\u80f8\u524d\u3002 \u3000\u3000\u90a3\u672c\u6765\u53ea\u662f\u4e00\u652f\u5251\uff0c\u4f46\u4eba\u7684\u76ee\u5149\u4e2d\u671b\u53bb\uff0c\u90a3\u4e00\u652f\u5251\uff0c\u5374\u5316\u6210\u4e86\u5343\u767e\u652f\u5251\u3002 \u3000\u3000\u593a\u9b42\u4e03\u5251\u7684\u7b2c\u4e00\u5251\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u7a81\u7136\u53cc\u624b\u6325\u51fa\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u770b\u6e05\u695a\u90a3\u4e00\u62db\u5251\u52bf\u53d8\u5316\uff0c\u53ea\u89c9\u90a3\u767d\u7070\u5c11\u5e74\u6574\u4e2a\u7684\u4eba\uff0c\u88ab\u88f9\u5728\u90a3\u4e00\u7247\u5251\u5149\u4e2d\u4e86\u3002 \u3000\u3000\u4e24\u80a1\u52b2\u98ce\uff0c\u7531\u4e00\u7247\u5251\u5149\u4e2d\u98de\u51fa\u3002 \u3000\u3000\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\u3002 \u3000\u3000\u90a3\u5e7b\u8d77\u7684\u5343\u767e\u5251\u950b\uff0c\u5ffd\u7136\u95f4\uff0c\u53c8\u53d8\u56de\u4e00\u5251\uff0c\u4e00\u652f\u7cbe\u94a2\u5b9d\u5251\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u53cc\u81c2\u4e0a\u4e00\u534a\u8863\u8896\uff0c\u5df2\u7136\u4e0d\u89c1\uff0c\u5316\u4f5c\u7247\u7247\u788e\u5c51\uff0c\u6d12\u843d\u4e00\u5730\u3002 \u3000\u3000\u539f\u6765\uff0c\u90a3\u53cc\u8863\u8896\uff0c\u5df2\u88ab\u4ed6\u51cc\u5389\u7684\u5251\u6c14\u7ede\u788e\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\uff0c\u9732\u51fa\u4e86\u4e24\u6761\u624b\u81c2\uff0c\u4e24\u6761\u5145\u6ee1\u529b\u91cf\u7684\u624b\u81c2\u3002 \u3000\u3000\u4ed6\u7684\u53cc\u8155\u4e4b\u4e0a\uff0c\u5404\u5e26\u7740\u4e00\u5bf8\u5de6\u53f3\u7684\u767d\u8272\u62a4\u8155\uff0c\u5c31\u662f\u90a3\u4e24\u53ea\u62a4\u8155\uff0c\u6321\u5f00\u4e86\u8fd9\u4e00\u5251\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u518d\u63a5\u6211\u4e00\u5251\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u795e\u8272\u8083\u7136\u9053\uff1a\u201c\u4e03\u5251\u8ffd\u9b42\uff0c\u51fa\u9053\u6c5f\u6e56\u4ee5\u6765\uff0c\u4ece\u6765\u51fa\u8fc7\u7b2c\u4e03\u5251\u3002\u201d \u3000\u3000\u53f6\u957f\u80af\u9053\uff1a\u201c\u5728\u4e0b\u53ea\u51fa\u8fc7\u4e94\u5251\u3002\u201d \u3000\u3000\u5b9d\u5251\u53cd\u62cd\u800c\u51fa\uff0c\u8ffd\u9b42\u7b2c\u4e8c\u5251\u3002 \u3000\u3000\u95ea\u8000\u7684\u5251\u5149\uff0c\u5e7b\u8d77\u4e00\u9053\u6597\u5927\u7684\u5149\u5708\u3002 \u3000\u3000\u4e00\u70b9\u5bd2\u8292\uff0c\u7a81\u73b0\u4e8e\u5149\u5708\u7684\u4e2d\u5fc3\uff0c\u5c04\u5411\u4e86\u767d\u8863\u5c11\u5e74\u7684\u7709\u5fc3\u3002 \u3000\u3000\u201c\u4e00\u8f6e\u660e\u6708\u7167\u5e7d\u9b42\u201d\uff0c\u8ffd\u9b42\u4e03\u5251\u7b2c\u4e8c\u62db\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u7684\u53cc\u624b\uff0c\u7a81\u7136\u5411\u4e0a\u4e3e\u8d77\uff0c\u6295\u5165\u4e86\u90a3\u5149\u5708\u4e4b\u4e2d\u3002 \u3000\u3000\u53cc\u81c2\u5ffd\u5206\uff0c\u5149\u5f71\u6d41\u52a8\u4e2d\uff0c\u53c8\u54cd\u8d77\u4e86\u4e00\u9635\u91d1\u94c1\u76f8\u51fb\u4e4b\u58f0\u3002 \u3000\u3000\u4e0d\u77e5\u90a3\u767d\u8863\u5c11\u5e74\u7528\u7684\u4ec0\u4e48\u624b\u6cd5\uff0c\u7adf\u7136\u53c8\u628a\u90a3\u4e00\u5251\u6321\u5f00\u3002 \u3000\u3000\u53f6\u957f\u9752\u51b7\u7b11\u4e00\u58f0\uff0c\u7b2c\u4e09\u5251\uff0c\u8fde\u7ef5\u51fb\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u95ea\u70c1\u4e0d\u5b9a\uff0c\u7f13\u7f13\u523a\u6765\uff0c\u5c31\u662f\u65c1\u89c2\u7684\u590f\u6740\uff0c\u4e5f\u77a7\u4e0d\u51fa\uff0c\u8fd9\u4e00\u5251\uff0c\u8981\u523a\u5411\u4f55\u5904\uff1f \u3000\u3000\u201c\u5904\u5904\u65e0\u5bb6\u5904\u5904\u5bb6\u201d\uff0c\u8fd9\u4e00\u5251\u7b3c\u7f69\u4e86\u767d\u8863\u5c11\u5e74\u5168\u8eab\u4e00\u5341\u4e09\u5904\u5927\u7a74\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u4e5f\u6c89\u5f97\u4f4f\u6c14\uff0c\u53cc\u76ee\u76ef\u4f4f\u5728\u90a3\u523a\u6765\u7684\u5251\u8eab\u4e4b\u4e0a\uff0c\u53cc\u81c2\u5fae\u66f2\u524d\u4f38\uff0c\u4f7f\u4e24\u624b\u548c\u8eab\u8eaf\uff0c\u4fdd\u6301\u4e86\u534a\u5c3a\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u7684\u5251\u52bf\u7a81\u7136\u52a0\u5feb\uff0c\u8fde\u7eed\u4e03\u5251\uff0c\u5206\u523a\u4e03\u5904\u5927\u7a74\u3002 \u3000\u3000\u4e03\u5904\uff0c\u90fd\u662f\u81f4\u547d\u7684\u5730\u65b9\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u53cc\u624b\u5ffd\u7136\u5316\u73b0\u51fa\u4e00\u5341\u56db\u6761\u624b\u81c2\u3001\u5c01\u5f00\u4e86\u4e03\u5251\u3002 \u3000\u3000\u53f6\u957f\u9752\u81ea\u51fa\u9053\u4ee5\u6765\uff1b\u4ece\u6ca1\u6709\u9047\u8fc7\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u53ea\u6709\u624b\u8155\u4e0a\u4e00\u5bf9\u62a4\u8155\uff0c\u6321\u4e86\u8ffd\u9b42\u4e03\u5251\u4e2d\u7684\u4e09\u62db\uff0c\u4e0d\u7981\u5fc3\u5934\u4e00\u9707\u3002 \u3000\u3000\u8fd9\u4e5f\u6fc0\u8d77\u4e86\u5730\u5fc3\u4e2d\u7684\u6012\u706b\u3002\u8ffd\u9b42\u7b2c\u56db\u5251\uff0c\u7b2c\u4e94\u5251\uff0c\u8fde\u7eed\u653b\u51fa\u3002 \u3000\u3000\u5f3a\u5927\u7684\u5251\u52bf\u3002\u5377\u8d77\u4e86\u4e00\u7247\u51b7\u5389\u7684\u5251\u6c14\uff0c\u76f4\u538b\u8fc7\u53bb\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u53cc\u624b\u5316\u767e\u638c\uff0c\u6709\u5982\u6570\u5341\u6761\u624b\u81c2\uff0c\u5728\u5c01\u6321\u5251\u62db\u4e00\u822c\u3002 \u3000\u3000\u53f6\u957f\u9752\u51fa\u9053\u6c5f\u6e56\u4ee5\u6765\uff0e\u7528\u7684\u4e94\u5251\uff0c\u5168\u6570\u88ab\u5c01\u5f00\u4e86\u3002 \u3000\u3000\u4e03\u5251\u8ffd\u9b42\u5b9a\u5b9a\u795e\uff0c\u5ffd\u7136\u53d1\u89c9\uff0c\u81ea\u5df1\u653b\u51fa\u7684\u4e94\u5251\uff0c\u8ffd\u9b42\u53d6\u547d\u7684\u4e94\u5251\uff0c\u7adf\u7136\u65e0\u6cd5\u628a\u5bf9\u65b9\u903c\u9000\u4e00\u6b65\u3002 \u3000\u3000\u4f46\u90a3\u767d\u8863\u5c11\u5e74\u8eab\u4e0a\u7684\u8863\u886b\uff0c\u53c8\u591a\u4e86\u56db\u4e94\u5904\u88c2\u53e3\u3002\u5251\u6c14\u5212\u7834\u7684\u88c2\u53e3\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u9601\u4e0b\u975e\u8981\u7528\u51fa\u7b2c\u516d\u3001\u7b2c\u4e03\u62db\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7684\u7751\u8272\u5f88\u5947\u602a\uff0c\u4e0d\u662f\u6124\u6012\uff0e\u4e0d\u662f\u60ca\u5947\uff0c\u800c\u662f\u4e00\u62b9\u5fae\u5fae\u7684\u7b11\u610f\u3002\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u5fc3\u4e2d\u5728\u60f3\u4ec0\u4e48\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u4e5f\u88ab\u53f6\u957f\u9752\u8fd9\u5947\u602a\u7684\u795e\u60c5\uff0c\u5f15\u8d77\u4e86\u5f3a\u70c8\u7684\u597d\u5947\u3002 \u3000\u3000\u53f6\u957f\u9752\u7ec8\u4e8e\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u6211\u6700\u540e\u4e24\u5251\u4f7f\u51fa\u7684\u540e\u679c\u5417\uff1f\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u8ffd\u9b42\u4e03\u5251\uff0e\u679c\u7136\u662f\u4e00\u5251\u6bd4\u4e00\u5251\u9ad8\u660e\uff1b\u6211\u60f3\u4f60\u51fa\u9053\u4ee5\u6765\uff0c\u4ece\u672a\u7528\u8fc7\u7684\u4e24\u5251\uff0c\u81ea\u7136\u66f4\u4e3a\u7cbe\u5389\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u4e03\u5251\u5c3d\u51fa\uff1b\u5982\u679c\u8fd8\u6740\u4e0b\u4e86\u4f60\uff0c\u90a3\u6700\u540e\u53ea\u6709\u51fa\u7b2c\u516b\u5251\u4e86\u3002\u201d\u767d\u8863\u5c11\u5e74\u9053\uff1a \u3000\u3000\u201c\u539f\u6765\uff0c\u9601\u4e0b\u8fd8\u6709\u7b2c\u516b\u62db\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7b2c\u516b\u5251\u4e0d\u662f\u6740\u4eba\uff0c\u800c\u662f\u81ea\u520e\uff0c\u6211\u4e03\u5251\u8fd8\u6740\u4e0d\u4e86\u4f60\uff0c\u4e5f\u53ea\u6709\u81ea\u7edd\u4e00\u9014\u53ef\u5faa\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1b\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u4f60\u5c31\u7b97\u6740\u4e0d\u4e86\u6211\uff0c\u81f3\u5c11\uff0c\u8fd8\u53ef\u4ee5\u81ea\u4fdd\u554a\uff01\u4f55\u51b5\u3002\u6211\u80fd\u5426\u518d\u5c01\u5f00\u4f60\u4e24\u5251\uff0c\u8fde\u6211\u81ea\u5df1\u4e5f\u6ca1\u6709\u628a\u63e1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e00\u53f6\u77e5\u79cb\uff0c\u6211\u8fde\u51fa\u4e86\u4e94\u5251\uff0c\u90fd\u65e0\u6cd5\u4f24\u5f97\u4e86\u9601\u4e0b\uff0c\u6211\u5df2\u7ecf\u65e0\u6cd5\u9884\u6599\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u9601\u4e0b\u7684\u6218\u5fd7\uff0c\u5df2\u7ecf\u6d88\u6563\uff0c\u8fd9\u6740\u4eba\u7684\u6c14\u52bf\uff0c\u4e5f\u968f\u7740\u51cf\u5f31\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e94\u62db\u653b\u88ad\uff0c\u7adf\u7136\u672a\u80fd\u628a\u9601\u4e0b\u8feb\u9000\u4e00\u6b65\uff0c\u5b9e\u5728\u662f\u4ece\u672a\u6709\u8fc7\u7684\u4e8b\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u4e00\u4eba\u4e0d\u77e5\u4e00\u4eba\u96be\uff0c\u6211\u63a5\u4e0b\u4f60\u4e94\u5251\uff0c\u672c\u8eab\u53d7\u5230\u7684\u75db\u82e6\u6709\u591a\u5c11\uff0c\u4f60\u53c8\u5982\u4f55\u77e5\u6653\uff1f \u3000\u3000\u5f7c\u6b64\u8fc7\u62db\uff0c\u90fd\u4ee5\u6c14\u52bf\u51cc\u4eba\u53d6\u80dc\uff0c\u4f46\u8fd9\u4e24\u4eba\u52a8\u53e3\uff0c\u5374\u662f\u5f7c\u6b64\u4e92\u76f8\u5b89\u6170\uff0c\u4f3c\u4e4e\u662f\u8001\u53cb\u53d9\u65e7\u4e00\u80a1\u3002 \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u770b\u6765\uff0c\u9601\u4e0b\u662f\u51c6\u5907\u548c\u6211\u51b3\u4e00\u6b7b\u6218\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4e03\u5251\u8ffd\u9b42\uff0c\u4f60\u5982\u6740\u4e0d\u4e86\u6211\uff0c\u5728\u4e0b\u8fd8\u53ef\u8fd8\u51fb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u521a\u624d\u4e0d\u51fa\u624b\u62a2\u653b\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u8ffd\u9b42\u4e03\u5251\uff0c\u5341\u5206\u51cc\u5389\uff0c\u51e0\u4e4e\u662f\u6ca1\u6709\u6211\u62a2\u653b\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u51c6\u5907\u5728\u6211\u51fa\u8fc7\u4e03\u5251\u4e4b\u540e\uff0c\u518d\u884c\u53cd\u51fb\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u5bf9\uff01\u5e0c\u671b\u4f60\u80fd\u591f\u7ed9\u6211\u8fd9\u4e2a\u673a\u4f1a\uff0c\u4f60\u5982\u628a\u6211\u6740\u4e86\uff0c\u4e5f\u5c31\u7b97\u4e86\uff0c\u5982\u679c\u6740\u4e0d\u4e86\u6211\uff0c\u90a3\u5c31\u7ed9\u6211\u6740\u4f60\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53cc\u76ee\u795e\u5149\u4e00\u95ea\uff0c\u9053\uff1a\u201c\u7528\u5b8c\u8ffd\u9b42\u4e03\u5251\uff0c\u4ecd\u7136\u662f\u6740\u4e0d\u4e86\u4f60\uff0c\u4f46\u90a3\u5e76\u975e\u662f\u8bf4\u6211\u5df2\u7ecf\u5b8c\u5168\u6ca1\u6709\u4e86\u6297\u62d2\u7684\u80fd\u529b\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u81ea\u7136\u4f60\u4ecd\u6709\u4fdd\u536b\u81ea\u5df1\u7684\u80fd\u529b\u4e86\uff0c\u4e0d\u8fc7\uff0c\u4f60\u5982\u6a2a\u5251\u81ea\u520e\u4e00\u6b7b\uff0c\u5728\u4e0b\u5c82\u4e0b\u662f\u6ca1\u6709\u53cd\u4ee5\u7684\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5446\u4e86\u4e00\u5446\uff0c\u7a81\u7136\u653e\u58f0\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u6709\u8fd9\u6837\u4e00\u4e2a\u52b2\u654c\uff0c\u53cd\u4f7f\u53f6\u957f\u9752\u5fc3\u4e2d\u6709\u4e00\u4efd\u83ab\u540d\u7684\u5feb\u4e50\u3002 \u3000\u3000\u4ed6\u89c9\u7740\u81ea\u5df1\u5e76\u975e\u6b66\u6797\u4e2d\u7b2c\u4e00\u6d41\u7684\u4eba\u7269\uff0c\u7eb5\u7136\u81ea\u5df1\u6b7b\u53bb\uff0c\u81f3\u5c11\uff0c\u4ecd\u6709\u4e00\u4e2a\u53ef\u4ee5\u548c\u8fd9\u4e2a\u6740\u624b\u7ec4\u5408\u6297\u62d2\u7684\u4eba\u7269\u3002 \u3000\u3000\u4ed6\u60f3\u628a\u77e5\u9053\u7684\u9690\u79d8\uff0c\u544a\u8bc9\u8fd9\u767d\u8863\u5c11\u5e74\uff0c\u7136\u540e\uff0c\u6a2a\u5251\u81ea\u7edd\u3002 \u3000\u3000\u4f46\u8fd9\u767d\u8863\u5c11\u5e74\u4e09\u756a\u4e24\u6b21\u7684\u51fa\u8a00\u76f8\u6fc0\uff0c\u4f7f\u5f97\u53f6\u957f\u9752\u6d88\u6c89\u7684\u610f\u5fd7\uff0c\u5ffd\u7136\u53c8\u52c3\u52c3\u800c\u8d77\u3002 \u3000\u3000\u4ed6\u662f\u82f1\u96c4\u4eba\u7269\uff0c\u9884\u77e5\u81ea\u5df1\u6b7b\u671f\u7684\u82f1\u96c4\u4eba\u7269\u3002 \u3000\u3000\u5982\u662f\u8fd9\u767d\u8863\u5c11\u5e74\u6b66\u529f\u5e73\u5eb8\uff0c\u53f6\u957f\u9752\u786e\u51c6\u5907\u6740\u4e86\u4ed6\uff0c\u4fdd\u5168\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u5fc5\u987b\u7559\u4e0b\u81ea\u5df1\u6709\u7528\u7684\u751f\u547d\uff0c\u4f46\u89c1\u8fc7\u8fd9\u767d\u8863\u4eba\u7684\u6b66\u529f\u4e4b\u540e\uff0c\u82f1\u96c4\u5e94\u8be5\u6b7b\u4e8e\u5200\u5251\u4e4b\u4e0b\uff0c\u4e0d\u80fd\u53d7\u6bd2\u53d1\u800c\u6b7b\uff0c\u53f6\u957f\u9752\u81ea\u89c9\u662f\u82f1\u96c4\uff0c\u6240\u4ee5\u4ed6\u8981\u6a2a\u5251\u81ea\u7edd\u3002 \u3000\u3000\u4f46\u8fd9\u767d\u8863\u5c11\u5e74\uff0c\u4e09\u756a\u4e24\u6b21\u7684\u8a00\u8bed\u76f8\u6fc0\uff0c\u4f7f\u5f97\u53f6\u957f\u9752\u7531\u6d88\u6c89\u4e2d\u632f\u4f5c\u8d77\u6765\uff0c\u63e1\u5251\u957f\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u597d\uff01\u9601\u4e0b\u65e2\u7136\u60f3\u8981\u6211\u5c3d\u51fa\u4e03\u5251\uff0c\u5728\u4e0b\u5c31\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u5bf9\uff01\u4eba\u751f\u4e00\u4e16\uff0c\u96be\u5f97\u9047\u4e00\u52b2\u654c\uff0c\u533a\u533a\u4e0d\u624d\uff0c\u6709\u5e78\u5f97\u9047\u53f6\u5144\uff0c\u5982\u679c\u6211\u6b7b\u4e8e\u8ffd\u9b42\u4e03\u5251\u4e4b\u4e0b\uff0c\u90a3\u4e5f\u662f\u4e00\u4ef6\u5feb\u4e8b\uff0c\u5982\u662f\u5e78\u5f97\u9003\u8fc7\uff0c\u5144\u5f1f\u4e5f\u60f3\u4ee5\u51e0\u62db\u5fc3\u5f97\u4e4b\u5b66\uff0c\u5411\u53f6\u5144\u9886\u6559\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f46\u613f\u9601\u4e0b\u80fd\u591f\u5c01\u5f00\u6211\u8ffd\u9b42\u4e03\u5251\uff0c\u4e5f\u597d\u4f7f\u5728\u4e0b\u77bb\u4ef0\u4e00\u4e0b\u9601\u4e0b\u8eab\u6000\u7684\u5947\u6280\u3002\u201d \u3000\u3000\u624b\u4e2d\u5b9d\u5251\uff0c\u7f13\u7f13\u626c\u8d77\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u53f6\u5144\u4e14\u6162\u3002\u201d \u3000\u3000\u4e24\u4eba\u51e0\u62db\u76f8\u4ea4\uff0c\u5f7c\u6b64\u4e4b\u95f4\uff0c\u5df2\u751f\u51fa\u4e86\u60fa\u60fa\u76f8\u60dc\u4e4b\u5fc3\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9601\u4e0b\u8fd8\u6709\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u9053\uff1a\u201c\u4eba\u6b7b\u7559\u540d\uff0c\u96c1\u8fc7\u7559\u58f0\uff0c\u53f6\u5144\u8fde\u533a\u533a\u59d3\u540d\u4e5f\u4e0d\u60f3\u95ee\u95ee\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u95ee\u95ee\u53c8\u5982\u4f55\uff1f\u76f8\u9022\u4f55\u5fc5\u66fe\u76f8\u8bc6\uff0c\u4f55\u51b5\uff0c\u4f60\u6211\u751f\u6b7b\u672a\u535c\uff0c\u7559\u4e0b\u59d3\u540d\uff0c\u5f92\u4e71\u4eba\u610f\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5e74\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\u4e4b\u540d\uff0c\u5144\u5f1f\u5df2\u7ecf\u77e5\u9053\uff0c\u6211\u5982\u4e0d\u7559\u4e0b\u59d3\u540d\uff0c\u5c82\u4e0d\u662f\u592a\u5403\u4e8f\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u533a\u533a\u4f55\u5bd2\u8863\u3002\u201d \u3000\u3000\u590f\u6740\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u8fd9\u540d\u5b57\uff0c\u5374\u662f\u4ece\u672a\u542c\u4eba\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u770b\u5251\u3002\u201d \u3000\u3000\u957f\u5251\u4e00\u632f\uff0c\u76f4\u523a\u800c\u51fa\u3002 \u3000\u3000\u8ffd\u9b42\u4e03\u5251\u7b2c\u516d\u62db\u201c\u70b9\u70b9\u9b3c\u706b\u7167\u4eba\u6765\u201d\u3002 \u3000\u3000\u5251\u52bf\u5316\u4f5c\u4e86\u70b9\u70b9\u5bd2\u661f\uff0c\u7b3c\u7f69\u4e01\u4f55\u5bd2\u8863\uff0c\u6ca1\u6709\u4eba\u77e5\u9053\u8fd9\u4e00\u5251\uff0c\u7a76\u7adf\u523a\u5411\u4ec0\u4e48\u5730\u65b9\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4f3c\u4e4e\u662f\u65e0\u6cd5\u5c01\u6321\u8fd9\u4e00\u5251\uff0c\u5ffd\u7136\u5411\u540e\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u8fd9\u4e00\u5251\uff0c\u53bb\u52bf\u5f88\u7f13\uff0c\u4f46\u5374\u98d8\u5ffd\u4e0d\u5b9a\uff0c\u4f7f\u4eba\u65e0\u6cd5\u6349\u6478\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u8fde\u7eed\u9000\u540e\u4e09\u6b21\uff0c\u4ecd\u7136\u662f\u6ca1\u6709\u529e\u6cd5\u907f\u5f00\u8fd9\u4e00\u5251\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u7684\u5947\u5999\uff0c\u5c31\u5728\u5b83\u4e00\u76f4\u662f\u5982\u5f71\u968f\u5f62\u4e00\u822c\uff0c\u4e00\u76f4\u7b3c\u7f69\u7740\u4eba\u8eab\u5341\u51e0\u5904\u5927\u7a74\uff0c\u4e0d\u77e5\u5b83\u523a\u5411\u4ec0\u4e48\u5730\u65b9\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7a81\u7136\u53cc\u81c2\u4e00\u632f\uff0c\u4e00\u7247\u5bd2\u8292\uff0c\u7a81\u7136\u98de\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6574\u4e2a\u7684\u8eab\u8eaf\uff0c\u90fd\u7b3c\u7f69\u5728\u90a3\u7247\u5bd2\u8292\u4e4b\u4e2d\u3002 \u3000\u3000\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\uff0c\u7206\u6563\u51fa\u4e00\u7247\u706b\u661f\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u53cc\u5251\u4ea4\u51fb\uff0c\u53cc\u65b9\u90fd\u7528\u51fa\u4e86\u5185\u529b\u3002 \u3000\u3000\u7206\u6563\u7684\u706b\u661f\u3001\u5251\u8292\u4e2d\uff0c\u4e24\u4e2a\u4ea4\u63a5\u5728\u4e00\u8d77\u7684\u4eba\uff0c\u7a81\u7136\u5206\u5f00\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u8131\u51fa\u4e86\u90a3\u5982\u5f71\u968f\u5f62\u7684\u5251\u8292\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u624b\u4e2d\uff0c\u5374\u591a\u4e86\u4e24\u628a\u4e00\u5c3a\u4e03\u5bf8\u7684\u77ed\u5251\u3002 \u3000\u3000\u91d1\u67c4\u94f6\u8292\u7684\u77ed\u5251\u3002 \u3000\u3000\u590f\u6740\u5446\u5728\u4e00\u4fa7\uff0c\u4ed6\u7ec8\u4e8e\u4e86\u89e3\u4e86\u8fd9\u767d\u8863\u5c11\u5e74\u7684\u8eab\u4efd\u3002 \u3000\u3000\u90a3\u4e24\u652f\u91d1\u67c4\u77ed\u5251\uff0c\u5c31\u662f\u4ed6\u7684\u6807\u5e1c\u2014\u2014\u91d1\u5251\u98de\u8f6e\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7684\u540d\u5b57\uff0c\u6c5f\u6e56\u4e0a\u77e5\u6653\u7684\u4eba\uff0c\u4e5f\u8bb8\u4e0d\u591a\uff0c\u4f46\u91d1\u5251\u98de\u8f6e\u7684\u5927\u540d\uff0c\u6c5f\u6e56\u4e0a\u5374\u662f\u6709\u5f88\u591a\u4eba\u77e5\u6653\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u4eba\u540d\u53ea\u662f\u5728\u6c5f\u6e56\u4e00\u6d41\u9ad8\u624b\u4e2d\u4f20\u8bf5\u3002 \u3000\u3000\u4e00\u822c\u7684\u6c5f\u6e56\u6b66\u5e08\uff0c\u5e76\u4e0d\u77e5\u6653\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u5f88\u5bb3\u6015\uff0c\u4e0b\u610f\u8bc6\u7684\u5411\u540e\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\uff0c\u8d5e\u7f8e\u5730\u8bf4\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff0c\u7b2c\u516d\u5251\u5df2\u5982\u6b64\u51f6\u5389\uff0c\u60f3\u6765\u90a3\u7b2c\u4e03\u5251\uff0c\u5fc5\u7136\u66f4\u4e3a\u5389\u5bb3\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8bd5\u8bd5\u5427\uff01\u4f60\u80fd\u8fde\u5c01\u516d\u5251\uff0c\u4e5f\u8bb8\u7b2c\u4e03\u5251\u4e5f\u4f24\u4e0d\u5230\u4f60\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5438\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\uff01\u53f6\u5144\u8d50\u6559\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9601\u4e0b\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u7f13\u7f13\u5411\u540e\u9000\u4e86\u4e03\u6b65\uff0c\u957f\u5251\u659c\u659c\u4e3e\u8d77\uff0c\u6307\u5411\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u53f6\u957f\u9752\u5e76\u6ca1\u6709\u7acb\u523b\u653b\u5411\u4f55\u5bd2\u8863\uff0c\u4ed6\u53ea\u662f\u4e3e\u8d77\u4e86\u624b\u4e2d\u7684\u957f\u5251\u3002\u957f\u5251\u5bf9\u51c6\u7740\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7684\u80a1\u8272\uff0c\u5374\u663e\u5f97\u66f4\u4e3a\u51dd\u91cd\u3002 \u3000\u3000\u4ed6\u5c01\u5f00\u4e86\u53f6\u957f\u9752\u516d\u62db\u653b\u52bf\u867d\u7136\u53cc\u8896\u88ab\u5251\u950b\u524a\u53bb\uff0c\u8863\u670d\u4e0a\uff0c\u4e5f\u6709\u6761\u6761\u88c2\u53e3\uff0c\u4f46\u4ed6\u7684\u795e\u8272\u4e2d\u5374\u4e00\u76f4\u4fdd\u6301\u7740\u76f8\u5f53\u7684\u9547\u9759\u3002 \u3000\u3000\u4f46\u53f6\u957f\u9752\u8fd9\u4e00\u62db\u4e3e\u5251\u7684\u59ff\u6001\uff0c\u5374\u4f7f\u4ed6\u5f88\u7d27\u5f20\uff0c\u7d27\u5f20\u5f97\u9876\u95e8\u4e0a\u5192\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e5f\u6709\u4e9b\u7d27\u5f20\uff0c\u7d27\u5f20\u5f97\u8eab\u4f53\u4e5f\u5fae\u5fae\u6709\u4e9b\u98a4\u52a8\u3002 \u3000\u3000\u4f46\u4ed6\u63e1\u5251\u7684\u53f3\u624b\uff0c\u4ecd\u7136\u662f\u90a3\u4e48\u7a33\u3002 \u3000\u3000\u597d\u50cf\u90a3\u53ea\u63e1\u5251\u7684\u624b\uff0c\u548c\u4ed6\u7684\u8eab\u4f53\uff0c\u5df2\u7136\u5206\u5f00\uff0c\u4e0d\u53d7\u5f71\u54cd\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e24\u652f\u91d1\u67c4\u94f6\u5251\uff0c\u63a8\u79bb\u524d\u80f8\u534a\u5c3a\u5de6\u53f3\uff0c\u53cc\u8098\u5fae\u5c48\uff0c\u4fdd\u6301\u7740\u53cc\u624b\u6d3b\u52a8\u7684\u4f59\u5730\u3002 \u3000\u3000\u590f\u6740\u5185\u5fc3\u7684\u7d27\u5f20\uff0c\u5b9e\u4e0d\u5728\u53f6\u957f\u9752\u3001\u4f55\u5bd2\u8863\u4e4b\u4e0b\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u53f6\u957f\u9752\u8fd9\u4e00\u51fb\uff0c\u5982\u679c\u6740\u4e0d\u4e86\u4f55\u5bd2\u8863\uff0c\u90a3\u540e\u679c\u5c31\u5f88\u96be\u9884\u6599\u4e86\u3002 \u3000\u3000\u53f6\u957f\u9752\u7ec8\u4e8e\u51fa\u5251\u4e86\uff0c\u4eba\u5251\u5408\u4e00\u7684\u4e00\u51fb\u3002 \u3000\u3000\u5c0f\u5c0f\u7684\u9152\u9986\uff0c\u95ea\u52a8\u8d77\u6ee1\u5ba4\u68ee\u5bd2\u7684\u5251\u5149\u3002 \u3000\u3000\u5f3a\u70c8\u7684\u5251\u6c14\uff0c\u903c\u5f97\u590f\u6740\u548c\u4e24\u4e2a\u7070\u8863\u4eba\uff0c\u90fd\u5411\u540e\u9000\u907f\u3002 \u3000\u3000\u95ea\u52a8\u7684\u5f3a\u70c8\u7684\u5149\u534e\uff0c\u5ffd\u7136\u95f4\uff0c\u655b\u6536\u4e0d\u89c1\u3002 \u3000\u3000\u4f46\u73b0\u573a\u7684\u60c5\u5f62\uff0c\u5df2\u6709\u4e86\u5f88\u5927\u7684\u6539\u53d8\u3002 \u3000\u3000\u53f6\u957f\u9752\u957f\u5251\u6491\u5730\uff0c\u8eab\u8eaf\u5fae\u8eac\uff0c\u4f3c\u4e4e\u662f\u8981\u501f\u90a3\u5b9d\u5251\u4e4b\u529b\uff0c\u7a33\u4f4f\u8eab\u5b50\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u524d\u80f8\u7684\u8863\u670d\u4e0a\uff0c\u53c8\u6709\u4e00\u9053\u53e3\u5b50\uff0c\u9c9c\u8840\u6b63\u7531\u88c2\u53e3\u4e2d\u6d41\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4f46\u4ed6\u624b\u4e2d\u4ecd\u7136\u6267\u7740\u53cc\u5251\uff0c\u795e\u60c5\u95f4\uff0c\u8fd8\u4fdd\u6301\u7740\u4e00\u7247\u5e73\u9759\u3002 \u3000\u3000\u590f\u6740\u7a81\u7136\u4e00\u4e0a\u6b65\uff0c\u6276\u4f4f\u4e86\u53f6\u957f\u9752\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5feb\u4e9b\u8d70\uff01\u4f55\u5bd2\u8863\u73b0\u5728\u8fd8\u6ca1\u6709\u51fa\u624b\u7684\u529b\u91cf\uff0c\u518d\u8fc7\u7247\u523b\uff0c\u53ea\u6015\u4f60\u8d70\u4e0d\u6210\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u8981\u6211\u8d70\uff1f\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5bf9\uff01\u4f60\u8d70\uff0c\u5c3d\u5feb\u7684\u8d70\u3002\u201d \u3000\u3000\u590f\u6740\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u5982\u4f55\u80fd\u591f\u4e22\u4e0b\u4f60\u4e00\u4e2a\u4eba\u8d70\u5462\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u6211\u4e00\u4e2a\u4eba\u79bb\u5f00\u8fd9\u91cc\uff0c\u53ea\u6015\u4e5f\u662f\u96be\u514d\u4e00\u6b7b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u5c31\u9003\u547d\u53bb\u5427\uff01\u7559\u8fd9\u91cc\u5fc5\u6b7b\u65e0\u7591\u2026\u2026\u201d \u3000\u3000\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u590f\u5144\uff0c\u6211\u7edd\u5bf9\u6ca1\u6709\u529e\u6cd5\u63a5\u4e0b\u5bf9\u65b9\u7684\u53cd\u51fb\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u4f4e\u58f0\u6025\u4fc3\u5730\u63a5\u9053\uff1a\u201c\u5feb\u8d70\uff01\u5feb\u8d70\uff01\u201d \u3000\u3000\u590f\u6740\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u8ba4\u4e3a\u6211\u79bb\u5f00\u4e86\u6b64\u5730\uff0c\u8fd8\u80fd\u591f\u6d3b\u5f97\u4e0b\u53bb\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u590f\u6740\uff0c\u6211\u5df2\u7ecf\u65e0\u80fd\u4e3a\u529b\u4e86\uff0c\u6211\u81ea\u77e5\u6321\u4e0d\u4f4f\u4f55\u5bd2\u8863\u7684\u53cd\u51fb\uff0c\u4f60\u5982\u575a\u6301\u7559\u5728\u6b64\u5730\uff0c\u90a3\u4e5f\u53ea\u6709\u6b7b\u8def\u2014\u6761\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u6a2a\u7ad6\u90fd\u662f\u4e00\u6b7b\uff0c\u4f55\u4e0d\u6b7b\u5f97\u5149\u5f69\u4e00\u4e9b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5509\uff01\u590f\u5144\u5982\u6b64\u7684\u591f\u670b\u53cb\uff0c\u5b9e\u5728\u53eb\u5144\u5f1f\u6709\u4e9b\u611f\u6fc0\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u611f\u6fc0\u5012\u4e0d\u6562\u5f53\uff0c\u4e0d\u8fc7\uff0c\u5144\u5f1f\u8fd9\u6b21\u65e2\u7136\u8ddf\u7740\u53f6\u5144\u6765\u4e86\uff0c\u90a3\u5c31\u53ea\u6709\u8ddf\u7740\u53f6\u5144\u53bb\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u597d\uff01\u590f\u5144\u65e2\u7136\u5982\u6b64\u6709\u4e49\u6c14\uff0c\u5144\u5f1f\u4e5f\u5c31\u4e0d\u4fbf\u8bf4\u4ec0\u4e48\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u53f9\u606f\u4e00\u58f0\uff0c\u7a81\u7136\u62bd\u51fa\u56f4\u5728\u8170\u4e2d\u7684\u8f6f\u5200\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6211\u4eec\u8054\u624b\u6321\u63a7\u4ed6\u7684\u653b\u52bf\uff0c\u5982\u662f\u4f4e\u6321\u4e0d\u4f4f\u3002\u90a3\u53ea\u6709\u4e00\u8d77\u79bb\u53bb\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e00\u8d77\u9000\u8d70\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5bf9\uff01\u8981\u8d70\uff0c\u54b1\u4eec\u4e00\u8d77\u8d70\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5509\uff01\u590f\u5144\u8ba4\u4e3a\u54b1\u95e8\u8fd8\u80fd\u8d70\u4e86\u5417\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5982\u679c\u54b1\u4eec\u80fd\u591f\u8d70\u5f97\u4e86\uff0c\u6211\u76f8\u4fe1\uff0c\u54b1\u4eec\u8fd8\u6709\u6d3b\u4e0b\u53bb\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1b\u201c\u590f\u5144\u65e2\u7136\u89c9\u5f97\u8fd8\u6709\u6d3b\u547d\u7684\u673a\u4f1a\uff0c\u4e3a\u4f55\u4e0d\u8d70\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u6d3b\u547d\u7684\u673a\u4f1a\u867d\u6709\uff0c\u4f46\u5fc5\u987b\u8981\u53f6\u89c1\u548c\u5144\u5f1f\u540c\u884c\u624d\u6709\u673a\u4f1a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u53c8\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u53f6\u5144\uff0c\u76ee\u4e0b\u53cc\u65b9\u5bf9\u9635\uff0c\u968f\u65f6\u53ef\u80fd\u52a8\u624b\uff0c\u5b9e\u5728\u65e0\u6687\u591a\u8c08\uff0c\u6b64\u95f4\u4e8b\u4e86\u4e4b\u540e\u3001\u54b1\u4eec\u518d\u8c08\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u4e24\u4f4d\u6162\u6162\u7684\u8c08\u3001\u5728\u4e0b\u4e48\uff0c\u662f\u4e00\u4e2a\u5f88\u6709\u8010\u5fc3\u7684\u4eba\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u54b1\u4eec\u7684\u7528\u5fc3\uff0c\u662f\u8981\u6740\u4e86\u9601\u4e0b\uff0c\u4f46\u9601\u4e0b\u5bf9\u6211\u4eec\u4f3c\u4e4e\u662f\u5e76\u65e0\u6068\u610f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u6211\u8fd8\u6ca1\u6709\u6740\u4f60\u4eec\u7684\u628a\u63e1\u3002\u56e0\u4e3a\uff0c\u53f6\u957f\u9752\u7684\u5251\u672f\uff0c\u4f3c\u4e4e\u4e0d\u5728\u6211\u4e4b\u4e0b\uff0c\u6709\u5f88\u5f3a\u70c8\u653b\u52bf\u7684\u4eba\uff0c\u6211\u76f8\u4fe1\uff0c\u4e5f\u53ef\u80fd\u6709\u5f88\u597d\u7684\u9632\u5b88\u4e4b\u6cd5\uff1b\u7b2c\u4e8c\uff0c\u6211\u60f3\u77e5\u9053\uff0c\u4f60\u4eec\u7a76\u7adf\u662f\u8981\u653e\u624b\u4e00\u6218\uff1f\u8fd8\u662f\u51c6\u5907\u9000\u8d70\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u653e\u624b\u4e00\u6218\u5982\u4f55\uff1f\u51c6\u5907\u9000\u8d70\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u653e\u624b\u4e00\u6218\u5417\uff1f\u5728\u4e0b\u5c31\u975e\u51fa\u624b\u4e0d\u53ef\u4e86\uff0c\u5982\u662f\u51c6\u5907\u64a4\u8d70\uff0c\u4e24\u4f4d\u73b0\u5728\u5c31\u53ef\u4ee5\u52a8\u8eab\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u4e0d\u53cd\u51fb\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u5f88\u60f3\u53cd\u51fb\uff0c\u53ef\u60dc\u6211\u89c9\u7740\u81ea\u4e0b\u5f88\u56f0\u4e4f\uff0c\u4e0d\u77e5\u9053\u662f\u5426\u8fd8\u6709\u80fd\u529b\u8fd8\u51fb\uff0c\u6211\u53c8\u662f\u4e00\u4e2a\u4e0d\u592a\u559c\u6b22\u5192\u9669\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u56de\u987e\u4e86\u590f\u6740\u4e00\u773c\uff0c\u9053\uff1a\u201c\u590f\u5144\uff0c\u54b1\u4eec\u2026\u2026\u201d \u3000\u3000\u590f\u6740\u63a5\u9053\uff1a\u201c\u8d70\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f88\u53ef\u60dc\uff0c\u6211\u7684\u8ffd\u9b42\u5251\u6cd5\u53ea\u6709\u4e03\u62db\uff0c\u5982\u662f\u80fd\u518d\u591a\u4e00\u62db\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a\u201c\u5e78\u597d\u4f60\u53ea\u6709\u4e03\u62db\uff0c\u5982\u662f\u518d\u591a\u4e24\u62db\uff0c\u53ea\u6015\u5728\u4e0b\u8981\u6b7b\u4e8e\u4f60\u7684\u5251\u4e0b\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5176\u5b9e\u4e24\u4f4d\u7684\u5251\u672f\uff0c\u90fd\u5f88\u9ad8\u660e\uff0c\u4e8c\u864e\u76f8\u6597\uff0c\u5fc5\u6709\u4e00\u4f24\uff0c\u4e24\u4f4d\u4eca\u65e5\u6597\u4e00\u4e2a\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u90a3\u8be5\u662f\u4e0d\u5e78\u4e2d\u7684\u5927\u5e78\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6536\u8d77\u957f\u5251\uff0c\u4e3e\u6b65\u51fa\u5e97\u3002\u590f\u6740\u7d27\u968f\u5728\u4ed6\u7684\u8eab\u540e\u3002 \u3000\u3000\u53f6\u957f\u9752\u56de\u987e\u4e86\u4e00\u773c\uff0c\u53c8\u53d1\u89c9\u4e86\u53e6\u5916\u4e00\u4ef6\u5947\u602a\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u8fd9\u5723\u6cc9\u9547\u7684\u4eba\uff0c\u4f3c\u4e4e\u662f\u90fd\u4e0d\u5927\u5173\u5fc3\u522b\u7684\u4eba\uff0e\u8fd9\u5ea7\u5c0f\u9986\u4e2d\u6253\u5f97\u6b64\u6fc0\u70c8\uff0c\u7adf\u7136\u6ca1\u6709\u4e00\u4e2a\u6765\u770b\u70ed\u95f9\u3002 \u3000\u3000\u751a\u81f3\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u591a\u671b\u4ed6\u4eec\u4e00\u773c\u3002 \u3000\u3000\u53f6\u957f\u9752\u4ef0\u5929\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u590f\u98ce\uff0c\u54b1\u4eec\u73b0\u5728\u8981\u5230\u54ea\u91cc\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1b\u201c\u81ea\u7136\u53bb\u89c1\u5f20\u73de\u3002\u201d \u3000\u3000\u5f20\u73de\u4f3c\u662f\u65e9\u5df2\u7ecf\u77e5\u9053\u4e86\u8fd9\u4e2a\u7ed3\u679c\uff0c\u7adf\u7136\u5728\u95e8\u53e3\u606d\u8fce\u4e24\u4eba\u3002 \u3000\u3000\u5ba2\u6808\u4e2d\uff0c\u4e00\u95f4\u534e\u4e3d\u7684\u5ba2\u623f\u4e2d\uff0c\u5df2\u6446\u597d\u4e86\u9152\u83dc\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f20\u5144\u60f3\u5f97\u5f88\u5468\u5230\uff0c\u65e9\u5df2\u66ff\u6211\u51c6\u5907\u4e86\u9001\u6b7b\u5bb4\u3002\u201d \u3000\u3000\u5f20\u73de\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u606d\u559c\u53f6\u5144\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u5bf9\uff01\u6211\u6d3b\u4e0d\u8fc7\u4eca\u591c\u4e09\u66f4\u3002\u201d \u3000\u3000\u4e3e\u676f\u8fde\u996e\uff0c\u4e00\u53e3\u6c14\u559d\u4e86\u516d\u5927\u676f\u9152\u3002 \u3000\u3000\u590f\u6740\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5f20\u638c\u67dc\uff0c\u4f60\u5f88\u8f7b\u677e\u3002\u201d \u3000\u3000\u5f20\u73de\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u8bdd\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u6839\u636e\u4ee5\u5f80\u7684\u60c5\u5f62\uff0c\u6211\u4eec\u5931\u8d25\u4e4b\u540e\uff0e\u597d\u50cf\u4e0d\u53ef\u80fd\u518d\u5230\u5982\u6b64\u7684\u793c\u9047\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u590f\u5144\uff0c\u8fd9\u8981\u770b\u4ec0\u4e48\u4eba\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u54e6\uff01\u60f3\u6765\uff0c\u8fd9\u662f\u770b\u5728\u53f6\u5144\u7684\u4efd\u4e0a\u4e86\uff0c\u6211\u590f\u67d0\u4eba\u662f\u79c3\u5b50\u8ddf\u7740\u6708\u4eae\u8d70\uff0c\u6cbe\u4e86\u53f6\u5144\u7684\u5149\u4e86\u3002\u201d \u3000\u3000\u5f20\u73de\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff0c\u4f60\u590f\u5144\uff0c\u4e5f\u662f\u5f88\u53d7\u7ec4\u7ec7\u91cd\u89c6\u7684\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u590f\u6740\u82e6\u7b11\u4e00\u4e0b\u9053\uff1a\u201c\u5f20\u638c\u67dc\uff0c\u8fd9\u4e00\u4e2a\uff0c\u6211\u660e\u767d\uff0c\u6211\u590f\u6740\u6709\u591a\u5c11\u4efd\u91cf\uff0c\u6211\u5fc3\u4e2d\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u5f20\u73de\u672a\u518d\u7406\u4f1a\u590f\u6740\uff0c\u5374\u671b\u7740\u53f6\u957f\u9752\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u5728\u4e0b\u7684\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u5e0c\u671b\u53f6\u5144\u80fd\u542c\u5144\u5f1f\u628a\u8bdd\u8bf4\u5b8c\u5982\u4f55\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u53c8\u559d\u4e86\u4e00\u676f\u9152\uff0c\u9053\uff1a\u201c\u597d\uff01\u6211\u5728\u542c\uff0c\u4f60\u8bf4\u5427\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u53f6\u5144\u867d\u7136\u6ca1\u6709\u6740\u6b7b\u90a3\u4eba\uff0c\u4f46\u5df2\u5c3d\u4e86\u5168\u529b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u5821\u4e3b\u7684\u795e\u76ee\u5982\u7535\uff0c\u6d1e\u67e5\u7ec6\u5fae\uff0c\u4ed6\u5df2\u7ecf\u77e5\u9053\u4f60\u5bf9\u4ed6\u7684\u5fe0\u5fc3\uff0c\u6240\u4ee5\uff0c\u7279\u5730\u6d3e\u4eba\u9001\u6765\u4e86\u89e3\u836f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u89e3\u836f\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u5bf9\uff01\u89e3\u836f\uff0c\u4e00\u670d\u4e4b\u540e\uff0c\u53f6\u5144\u8eab\u4e2d\u4e4b\u6bd2\uff0c\u5c31\u53ef\u4ee5\u5b8c\u5168\u89e3\u53bb\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u53f6\u67d0\u5bf8\u529f\u672a\u7acb\uff0c\u5821\u4e3b\u600e\u4f1a\u5bf9\u6211\u5982\u6b64\u770b\u91cd\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u53f6\u5144\u554a\uff01\u4eba\u624d\u96be\u5f97\uff0c\u50cf\u53f6\u5144\u8fd9\u6837\u7684\u4eba\uff0c\u6c5f\u6e56\u4e0a\u96be\u5f97\u4e00\u89c1\uff0c\u5821\u4e3b\u53c8\u600e\u4f1a\u5982\u6b64\u8f7b\u6613\u7684\u820d\u5f03\u53f6\u5144\u5462\uff1f\u201d \u3000\u3000\u4e00\u9762\u4f38\u624b\uff0c\u53d6\u51fa\u4e86\u836f\u7269\uff0c\u606d\u606d\u656c\u656c\u7684\u5949\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u4f38\u624b\u63a5\u8fc7\u3002 \u3000\u3000\u90a3\u662f\u4e00\u4e2a\u7cbe\u81f4\u7684\u7389\u76d2\u3002 \u3000\u3000\u6380\u5f00\u76d2\u76d6\uff0c\u91cc\u9762\u662f\u4e00\u9897\u7ea2\u8272\u7684\u4e39\u4e38\u3002 \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u7c92\u836f\u7269\uff0c\u5982\u662f\u6bd2\u836f\uff0c\u90a3\u5fc5\u7136\u662f\u4e00\u7c92\u5341\u5206\u5f3a\u70c8\u7684\u6bd2\u7269\uff0c\u670d\u4e0b\u53bb\u7acb\u523b\u81f4\u547d\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u597d\u7684\u662f\uff0c\u5b83\u7edd\u4e0d\u662f\u6bd2\u836f\uff0c\u800c\u662f\u4e00\u7c92\u89e3\u6bd2\u795e\u4e39\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u5c31\u662f\u70c8\u6027\u7684\u6bd2\u836f\uff0c\u6211\u4e5f\u4e00\u5b9a\u8981\u670d\u4e0b\uff0c\u8ffd\u9b42\u4e03\u5251\u5c3d\u51fa\uff0c\u4ecd\u7136\u4e0d\u80fd\u4f24\u4eba\uff0c\u6211\u6d3b\u7740\uff0c\u4e5f\u662f\u65e0\u5473\u5f97\u5f88\u3002\u201d \u3000\u3000\u5f20\u73de\u7b11\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\u7684\u5403\u4e0b\u53bb\uff0c\u5728\u4e0b\u5949\u5230\u7684\u4ee4\u8aed\u662f\u8981\u597d\u597d\u7684\u4fdd\u62a4\u9601\u4e0b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u7f13\u7f13\u628a\u836f\u7269\u541e\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u53c8\u767d\u659f\u81ea\u996e\u7684\u5e72\u4e86\u4e24\u676f\u9152\uff0c\u76d8\u819d\u5750\u5728\u90a3\u91cc\u3002 \u3000\u3000\u590f\u6740\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5821\u4e3b\u6765\u4e86\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u662f\uff01\u5821\u4e3b\u6765\u4e86\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u5728\u4e0b\u662f\u4e0d\u662f\u53ef\u4ee5\u89c1\u5230\u5821\u4e3b\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u53ef\u4ee5\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u51e0\u65f6\u53bb\u89c1\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u7b49\u53f6\u5144\u7684\u836f\u529b\u884c\u5f00\uff0c\u5185\u8151\u4e2d\u5947\u836f\u89e3\u53bb\u4e4b\u540e\uff0c\u518d\u53bb\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u590f\u6740\u70b9\u4e86\u70b9\u5934\uff0c\u672a\u518d\u591a\u8a00\u3002 \u3000\u3000\u53f6\u957f\u9752\u5f88\u5feb\u7684\u611f\u89c9\u5230\u4e39\u7530\u5185\u4e00\u80a1\u70ed\u6c14\u76f4\u5411\u4e0a\u9762\u51b2\u6765\u3002 \u3000\u3000\u5f3a\u70c8\u7684\u836f\u529b\uff0c\u76f4\u900f\u56db\u80a2\u767e\u9ab8\u3002 \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u5982\u611f\u89c9\u5230\u6709\u4e00\u80a1\u70ed\u529b\u5411\u56db\u9762\u6269\u5c55\uff0c\u90a3\u5c31\u60f3\u529e\u6cd5\u8fd0\u6c14\u5e2e\u52a9\u836f\u529b\u884c\u5f00\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4f9d\u8a00\uff0c\u7acb\u523b\u51fa\u4e86\u4e00\u8eab\u5927\u6c57\u3002 \u3000\u3000\u770b\u5230\u53f6\u957f\u9752\u9876\u95e8\u4e0a\uff0c\u6c57\u6c34\u6dcb\u6f13\u800c\u4e0b\uff0c\u5f20\u73de\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u6210\u4e86\uff0c\u53f6\u5144\uff0c\u6d17\u4e00\u4e2a\u6fa1\uff0c\u6362\u4ef6\u8863\u670d\uff0c\u4e00\u8d77\u53bb\u89c1\u5821\u4e3b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5185\u529f\u7cbe\u6e5b\uff0c\u8fd9\u4e00\u8fd0\u6c14\u903c\u6bd2\uff0c\u7acb\u523b\u611f\u5230\u6c57\u6c34\u4e2d\u6709\u4e00\u80a1\u6de1\u6de1\u8165\u81ed\u4e4b\u5473\u3002\u201d \u3000\u3000\u6c90\u6d74\u66f4\u8863\u4e4b\u540e\uff0c\u53f6\u957f\u9752\u624d\u8f7b\u8f7b\u5401\u2014\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5f20\u638c\u67dc\uff0c\u5821\u4e3b\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u5723\u6cc9\u9547\u5916\u4e0d\u8fdc\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f20\u5144\uff0c\u5728\u4e0b\u548c\u4f55\u5bd2\u8863\u52a8\u624b\u65f6\uff0c\u9601\u4e0b\u53ef\u5728\u73b0\u573a\uff1f\u201d \u3000\u3000\u5f20\u73de\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u4e0d\u5728\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u4e48\u5821\u4e3b\u5728\u73b0\u573a\u4e86\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\uff0c\u5144\u5f1f\u4e5f\u4e0d\u660e\u767d\uff0c\u53f6\u5144\u5f88\u5feb\u5c31\u53ef\u89c1\u5230\u5821\u4e3b\u4e86\uff0c\u4f55\u4e0d\u5f53\u9762\u95ee\u8fc7\uff1f\u201d\uff0c\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u591a\u8c22\u5f20\u5144\u6307\u70b9\u3002\u201d \u3000\u3000\u5f20\u73de\u56de\u987e\u4e86\u590f\u6740\u4e00\u773c\uff0c\u9053\uff1a\u201c\u590f\u5144\uff0c\u8981\u4e0d\u8981\u6c90\u6d74\u66f4\u8863\uff1f\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u8fd9\u4e00\u573a\u6fc0\u70c8\u7684\u640f\u6740\uff0c\u90fd\u662f\u53f6\u5144\u4e00\u4eba\u5e94\u4ed8\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u54b1\u4eec\u53ef\u4ee5\u52a8\u8eab\u4e86\u3002\u201d \u3000\u3000\u5e97\u5916\u9762\u65e9\u5df3\u505c\u7740\u4e00\u8f86\u5bc6\u5c01\u7684\u7bf7\u8f66\uff0c\u5f20\u73de\u5e26\u5b50\u4e24\u4eba\u76f4\u767b\u8f66\u5185\u3002 \u3000\u3000\u7bf7\u8f66\u9a70\u52a8\uff0c\u5411\u524d\u5954\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u65e0\u6cd5\u770b\u5230\u8f66\u5916\u7684\u666f\u7269\uff0c\u53ea\u80fd\u51ed\u611f\u89c9\u7684\u611f\u53d7\u6d4b\u65ad\u884c\u8f66\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u8f66\u884c\u5927\u7ea6\u6709\u534a\u4e2a\u65f6\u8fb0\uff0c\u7a81\u7136\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8f66\u5e18\u542f\u52a8\uff0c\u53d1\u89c9\u4e86\u7bf7\u8f66\u7adf\u7136\u505c\u5728\u4e00\u5ea7\u5b85\u9662\u4e4b\u4e2d\u3002 \u3000\u3000\u4e0b\u4e86\u7bf7\u8f66\uff0c\u6b63\u597d\u53ef\u4ee5\u767b\u4e0a\u8fdb\u5165\u5385\u4e2d\u7684\u77f3\u9636\u3002 \u3000\u3000\u7ad9\u5728\u5385\u524d\u77f3\u9636\u4e0a\u8fce\u63a5\u5ba2\u4eba\u7684\uff0c\u7adf\u7136\u662f\u8273\u5a62\u6021\u7ea2\u3002 \u3000\u3000\u51e0\u65e5\u4e0d\u89c1\uff0c\u6021\u7ea2\u4f3c\u4e4e\u662f\u51fa\u843d\u5f97\u66f4\u7f8e\u4e3d\u4e86\u3002 \u3000\u3000\u5979\u8138\u4e0a\u5e26\u7740\u5fae\u7b11\uff0c\u5fae\u7b11\u5982\u82b1\u3002 \u3000\u3000\u53f6\u957f\u9752\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u4f60\u600e\u4e48\u4e5f\u6765\u4e86\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u96be\u9053\uff0c\u6211\u4e0d\u5e94\u8be5\u6765\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e8b\u5982\u6625\u98ce\u4e86\u65e0\u75d5\uff0c\u60f3\u4e0d\u5230\u6211\u4f1a\u518d\u89c1\u4f60\u3002\u201d \u3000\u3000\u6021\u7ea2\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u75f4\u5fc3\u5973\u5b50\u8d1f\u5fc3\u90ce\uff0c\u4f60\u4eec\u7537\u4eba\u4e00\u5411\u8584\u60c5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u91cd\u8981\u7684\u662f\uff0c\u6211\u60f3\u4e0d\u5230\u81ea\u5df1\u7adf\u7136\u80fd\u6d3b\u4e86\u4e0b\u6765\uff0c\u800c\u4e14\u53c8\u89c1\u5230\u4f60\u3002\u201d \u3000\u3000\u6021\u7ea2\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u5f88\u610f\u5916\uff0c\u5821\u4e3b\u7528\u5feb\u9a6c\u628a\u6211\u9001\u5230\u8fd9\u91cc\uff0c\u60f3\u4e0d\u5230\uff0c\u7adf\u7136\u662f\u6765\u63a5\u5f85\u4f60\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u56de\u987e\u4e86\u590f\u6740\u3001\u5f20\u73de\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u5821\u4e3b\u5462\uff1f\u201d \u3000\u3000\u6021\u7ea2\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5148\u8bf7\u5165\u5385\u4e2d\u5750\u5427\uff01\u201d \u3000\u3000\u5979\u907f\u5f00\u4e86\u6b63\u9762\u7684\u95ee\u9898\uff0c\u5374\u628a\u53f6\u957f\u9752\u8ba9\u5165\u5927\u5385\u3002 \u3000\u3000\u5385\u4e2d\u7684\u5e03\u7f6e\u5f88\u8c6a\u534e\u3002 \u3000\u3000\u590f\u6740\u867d\u7136\u662f\u6bd4\u53f6\u957f\u9752\u65e9\u5165\u8fd9\u4e00\u4e2a\u6740\u624b\u7ec4\u5408\u3002 \u3000\u3000\u4f46\u6c5f\u6e56\u4e0a\u5f88\u73b0\u5b9e\uff0c\u53f6\u957f\u9752\u7684\u6b66\u529f\u3001\u58f0\u8a89\u90fd\u6bd4\u590f\u6740\u9ad8\u660e\u4e86\u5f88\u591a\u3002 \u3000\u3000\u5728\u8fd9\u4e2a\u7ec4\u7ec7\u4e2d\uff0c\u6b66\u529f\u6108\u5f3a\u7684\u4eba\uff0c\u6108\u53d7\u5c0a\u91cd\u3002 \u3000\u3000\u5348\u65f6\u4e00\u6218\uff0c\u590f\u6740\u76ee\u7779\u4e86\u53f6\u957f\u9752\u7684\u6b66\u529f\u3002 \u3000\u3000\u540d\u6ee1\u5929\u4e0b\u7684\u8ffd\u9b42\u4e03\u5251\uff0c\u90a3\u5b9e\u5728\u662f\u5f88\u9ad8\u660e\u7684\u5251\u6cd5\u3002 \u3000\u3000\u590f\u6740\u6697\u4e2d\u7b97\u8fc7\uff0c\u4ed6\u81ea\u5df1\u7edd\u5bf9\u63a5\u4e0d\u4e0b\u4e09\u5251\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u53f6\u957f\u9752\u867d\u7136\u53d7\u5230\u4e86\u8d85\u8fc7\u81ea\u5df1\u5f88\u591a\u7684\u5c0a\u91cd\uff0c\u4f46\u590f\u6740\u5fc3\u4e2d\u7edd\u5bf9\u6ca1\u6709\u4e00\u70b9\u5992\u5fcc\u7684\u611f\u89c9\u3002 \u3000\u3000\u5f20\u73de\u4e5f\u8868\u73b0\u51fa\u4e86\u76f8\u5f53\u7684\u62d8\u8c28\u3002 \u3000\u3000\u53f6\u957f\u9752\u53d1\u89c9\u81ea\u5df1\u6210\u4e86\u771f\u6b63\u7684\u8d35\u5bbe\u3002 \u3000\u3000\u6021\u7ea2\u7684\u5fc3\u76ee\u4e2d\uff0c\u4f3c\u4e4e\u662f\u4e5f\u53ea\u6709\u53f6\u957f\u9752\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u5979\u5168\u5fc3\u5168\u610f\u7684\u7167\u987e\u4ed6\uff0c\u6b22\u8fce\u4ed6\uff0c\u4f3c\u4e4e\u662f\u6839\u672c\u6ca1\u6709\u6ce8\u610f\u5230\u5f20\u73de\u548c\u590f\u6740\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6709\u4e9b\u5947\u602a\uff0c\u4e5f\u4f7f\u4ed6\u6697\u4e2d\u63d0\u9ad8\u4e86\u8b66\u89c9\u3002 \u3000\u3000\u4e24\u4e2a\u5973\u5a62\uff0c\u5949\u4e0a\u4e01\u9999\u8317\u3002 \u3000\u3000\u6021\u7ea2\u4eb2\u624b\u53d6\u8fc7\u4e00\u676f\uff0c\u653e\u5728\u53f6\u957f\u9752\u9762\u524d\u7684\u8336\u51e0\u4e0a\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53f6\u957f\u9752\u624d\u53d1\u89c9\uff0c\u81ea\u5df1\u5df2\u88ab\u62a4\u62e5\u5728\u4e0a\u4f4d\u3002 \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u6021\u7ea2\u59d1\u5a18\uff0c\u5821\u4e3b\u7684\u5927\u9a7e\uff0c\u73b0\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4ed6\u5f88\u5feb\u4f1a\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u8f9b\u82e6\u4e86\uff0c\u957f\u9752\u5144\u3002\u201d\u4e00\u4e2a\u9752\u8863\u4eba\u7f13\u6b65\u884c\u4e0b\u8fc7\u6765\uff0c\u5750\u5728\u53f6\u957f\u9752\u7684\u5bf9\u9762\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u2026\u2026\u201d \u3000\u3000\u8bf4\u4e86\u4e00\u4e2a\u5b57\uff0c\u7acb\u523b\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u4ed6\u89c1\u5821\u4e3b\uff0c\u867d\u7136\u662f\u5306\u5306\u4e00\u9762\uff0c\u4f46\u90a3\u65e5\u5b50\u5e76\u4e0d\u5f88\u4e45\uff0c\u8bb0\u5fc6\u5f88\u9c9c\u660e\uff0c\u8fd9\u4e2a\u4eba\u7edd\u5bf9\u4e0d\u662f\u4ed6\u89c1\u8fc7\u7684\u5821\u4e3b\u3002 \u3000\u3000\u4e0d\u662f\u5821\u4e3b\uff0c\u8fd9\u9752\u8863\u4eba\u53c8\u662f\u4f55\u8bb8\u4eba\u7269\uff1f \u3000\u3000\u6021\u7ea2\u5374\u5f88\u606d\u656c\u7684\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u5a62\u5b50\u89c1\u8fc7\u5821\u4e3b\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u6ca1\u6709\u7406\u4f1a\u6021\u7ea2\uff0c\u5374\u5bf9\u53f6\u957f\u9752\u9894\u9996\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff1f\u957f\u9752\u5144\u4e0d\u8ba4\u8bc6\u6211\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4e24\u9053\u76ee\u5149\uff0c\u51dd\u6ce8\u5728\u9752\u8863\u4eba\u7684\u8138\u4e0a\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u5821\u4e3b\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u662f\u4e0d\u662f\u6709\u4e00\u70b9\u4e0d\u50cf\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u56de\u987e\u590f\u6740\u3002 \u3000\u3000\u590f\u6740\u8fde\u8fde\u70b9\u5934\u3002 \u3000\u3000\u53f6\u957f\u9752\u5374\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u53ea\u662f\u6709\u4e00\u70b9\u4e0d\u50cf\uff0c\u800c\u4e14\u662f\u5b8c\u5168\u4e0d\u50cf\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4e5f\u8bb8\u6211\u4eec\u89c1\u9762\u7684\u6b21\u6570\u5c11\u4e00\u4e9b\uff0c\u6240\u4ee5\uff0c\u4f60\u8ba4\u7684\u4e0d\u592a\u6e05\u695a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u867d\u7136\uff0c\u6211\u4eec\u53ea\u89c1\u8fc7\u4e00\u6b21\uff0c\u4f46\u6211\u5bf9\u81ea\u5df1\u7684\u8bb0\u5fc6\u529b\uff0c\u5f88\u6709\u4fe1\u5fc3\uff0c\u4f60\u7edd\u5bf9\u4e0d\u662f\u5821\u4e3b\uff0c\u4f60\u5982\u662f\u5821\u4e3b\uff0c\u6211\u4eec\u7b2c\u4e00\u6b21\u89c1\u5230\u7684\u5821\u4e3b\u5fc5\u662f\u6709\u4eba\u5192\u5145\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u8c01\u6709\u90a3\u4e48\u5927\u7684\u80c6\u5b50\uff0c\u6562\u5192\u5145\u9634\u9633\u5821\u7684\u5821\u4e3b\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u653e\u58f0\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u7b11\u58f0\u505c\u4e0b\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u9634\u9633\u5821\u53c8\u5728\u4f55\u5904\uff1f\u9634\u9633\u5821\u4e3b\u4e5f\u4e0d\u8fc7\u662f\u4e00\u4e2a\u540d\u8bcd\u7f62\u4e86\uff0c\u5982\u82e5\u4e16\u4e0a\u771f\u6709\u4e00\u4e2a\u9634\u9633\u5821\u4e3b\uff0c\u90a3\u4e2a\u4eba\u53c8\u4f55\u5fc5\u4e00\u5b9a\u662f\u6211\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u4f60\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u9053\uff1a\u201c\u957f\u9752\u5144\uff0c\u8fd9\u610f\u601d\u5f88\u660e\u767d\uff0c\u4e8b\u5728\u4eba\u4e3a\uff0c\u9634\u9633\u5821\u5c06\u6765\u5982\u80fd\u5728\u6c5f\u6e56\u4e0a\u5360\u4e00\u5e2d\u4e4b\u5730\uff0c\u90a3\u5e76\u4e0d\u662f\u56e0\u4e3a\u9634\u9633\u5821\u6709\u4e00\u4e2a\u6770\u51fa\u7684\u5821\u4e3b\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u8fd9\u53c8\u4e3a\u4ec0\u4e48\uff1f\u201d\u9752\u8863\u4eba\u9053\uff1a\u201c\u72ec\u6728\u96be\u652f\u5927\u53a6\uff0c\u9634\u9633\u5821\u6709\u4ec0\u4e48\u6210\u5c31\uff0c\u90a3\u662f\u56e0\u4e3a\u4ed6\u4eec\u6709\u4e00\u4e2a\u5f88\u5065\u5168\u7684\u7ec4\u7ec7\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u5982\u82e5\u6709\u4e00\u4e2a\u5f88\u5065\u5168\u7684\u7ec4\u7ec7\uff0c\u4e0d\u8bba\u4ec0\u4e48\u4eba\u505a\u5821\u4e3b\u90fd\u662f\u4e00\u6837\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5728\u4e0b\u4e5f\u6709\u4e00\u4e2a\u5f53\u5821\u4e3b\u7684\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6709\uff01\u800c\u4e14\u5f88\u5927\uff0c\u6240\u4ee5\u957f\u9752\u5144\u4e0d\u7528\u6000\u7591\u6211\u5821\u4e3b\u7684\u8eab\u4efd\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u51b7\u9053\uff1a\u201c\u86c7\u65e0\u5934\u4e0d\u884c\uff0c\u9e1f\u65e0\u7fc5\u4e0d\u98de\uff0c\u4f60\u5982\u4e0d\u662f\u9634\u9633\u5821\u4e3b\uff0c\u5728\u4e0b\u4f3c\u4e4e\u662f\u7528\u4e0d\u7740\u542c\u4f60\u7684\u9ad8\u8c08\u9614\u8bba\u4e86\u3002\u201d \u3000\u3000\u970d\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u73b0\u5728\u8fd8\u6709\u4e00\u4e2a\u529e\u6cd5\uff0c\u80fd\u591f\u4f7f\u5728\u4e0b\u542c\u4ece\u4f60\u7684\u602a\u8bba\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u529e\u6cd5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4f38\u624b\u63e1\u4f4f\u4e86\u5251\u67c4\uff0c\u9053\uff1a\u201c\u6253\u8d25\u4e86\u5728\u4e0b\u624b\u4e2d\u8fd9\u67c4\u5251\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u80fd\u8bc1\u660e\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8bc1\u660e\u4f60\u6b66\u529f\u6bd4\u6211\u9ad8\u660e\uff0c\u5728\u4e0b\u53d7\u547d\u884c\u4e8b\uff0c\u624d\u80fd\u5fc3\u670d\u3002\u201d \u3000\u3000\u5ffd\u7136\u4e00\u5251\uff0c\u523a\u4e86\u8fc7\u53bb\u3002\u5feb\u5982\u95ea\u7535\u7684\u4e00\u5251\u3002 \u3000\u3000\u590f\u6740\u3001\u5f20\u73de\uff0c\u90fd\u7ad9\u5728\u65c1\u4fa7\uff0c\u660e\u770b\u7740\u4e00\u5251\u523a\u51fa\uff0c\u5374\u662f\u65e0\u6cd5\u6551\u63f4\u3002 \u3000\u3000\u9752\u8863\u4eba\u8eab\u5b50\u4e00\u4ef0\uff0c\u8fde\u4eba\u5e26\u6905\u5b50\uff0c\u7a81\u7136\u5728\u7a7a\u4e2d\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\u3002 \u3000\u3000\u6905\u5b50\u98de\u843d\u5230\u516d\u4e03\u5c3a\u5916\uff0c\u4eba\u4ecd\u7136\u5750\u5728\u6905\u5b50\u4e0a\u3002\u4ecd\u7136\u4fdd\u6301\u7740\u539f\u6765\u7684\u59ff\u52bf\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e00\u4e2a\u65cb\u8eab\uff0c\u7ed5\u8fc7\u4e86\u6021\u7ea2\uff0c\u53c8\u903c\u5230\u4e86\u9752\u8863\u4eba\u7684\u8eab\u5b50\u524d\u3002 \u3000\u3000\u590f\u6740\u3001\u5f20\u73de\uff0c\u5df2\u7136\u53cc\u53cc\u5954\u5230\u9752\u8863\u4eba\u7684\u8eab\u4fa7\u3002\u4e24\u4e2a\u4eba\uff0c\u4e5f\u4eae\u51fa\u4e86\u5175\u5203\u3002 \u3000\u3000\u5f20\u73de\u5374\u662f\u4e24\u6839\u4e0d\u5230\u4e24\u5c3a\u7684\u94dc\u68d2\u3002 \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u4f60\u600e\u53ef\u5bf9\u5821\u4e3b\u5982\u6b64\u4e0d\u656c\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u4f60\u53ea\u8981\u770b\u6211\u4eec\u5bf9\u4ed6\u7684\u5c0a\u656c\u4e0a\uff0c\u4e5f\u8be5\u77a7\u51fa\u6765\uff0c\u4ed6\u7edd\u5bf9\u4e0d\u662f\u5192\u5145\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u590f\u6740\uff0c\u4f60\u8bf4\uff0c\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u590f\u6740\u671b\u671b\u90a3\u9752\u8863\u4eba\uff0c\u4e0d\u6562\u591a\u8a00\u3002 \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u590f\u6740\uff0c\u544a\u8bc9\u4ed6\uff0c\u53f6\u5144\u662f\u6027\u60c5\u4e2d\u4eba\uff0c\u4e0d\u7ed9\u4ed6\u8bf4\u4e2a\u660e\u767d\uff0c\u53ea\u6015\u4ed6\u4e0d\u4f1a\u7f62\u4f11\u3002\u201d \u3000\u3000\u590f\u6740\u5bf9\u90a3\u9752\u8863\u4eba\u8868\u73b0\u51fa\u65e0\u6bd4\u7684\u606d\u8c28\u795e\u6001\uff0c\u8eac\u8eab\u884c\u4e86\u4e00\u793c\uff0c\u624d\u56de\u987e\u53f6\u957f\u9752\uff0c\u7b11\u9053\uff1a\u201c\u53f6\u5144\uff0c\u54b1\u4eec\u5821\u4e3b\u6709\u4e00\u4e2a\u5916\u53f7\uff0c\u53eb\u4f5c\u5316\u8eab\u4e66\u751f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5316\u8eab\u4e66\u751f\uff0c\u5144\u5f1f\u5012\u662f\u542c\u4eba\u8bf4\u8fc7\uff0c\u88ab\u79f0\u4e3a\u6c5f\u6e56\u4e0a\u7b2c\u4e00\u53f7\u795e\u79d8\u4eba\u7269\u3002\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u5821\u4e3b\u7684\u5927\u540d\uff0c\u5728\u6c5f\u6e56\u4e0a\u5341\u5206\u54cd\u4eae\uff0c\u4f46\u89c1\u8fc7\u4ed6\u7684\u4eba\uff0c\u5374\u662f\u6ca1\u6709\u51e0\u4e2a\u3002\u201d \u3000\u3000\u5f20\u73de\u63a5\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u5c31\u7b97\u54b1\u4eec\u7684\u5821\u4e3b\uff0c\u7ad9\u5728\u4ed6\u7684\u8eab\u4fa7\uff0c\u4ed6\u4eec\u4e5f\u662f\u8ba4\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u79cd\u6613\u5bb9\u672f\uff0c\u786e\u7136\u9ad8\u660e\u81f3\u6781\uff0c\u5728\u4e0b\u89c1\u8fc7\u4e0d\u4e45\uff0c\u4f46\u7b2c\u4e8c\u6b21\u76f8\u89c1\uff0c\u7adf\u7136\u662f\u4e00\u70b9\u4e5f\u8ba4\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u76f8\u4fe1\u4e86\u6211\u7684\u8eab\u4efd\u5427\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7531\u4ed6\u4eec\u606d\u656c\u795e\u60c5\u4e4b\u4e2d\uff0c\u5c31\u7b97\u6211\u4e0d\u76f8\u4fe1\uff0c\u4e5f\u5f97\u76f8\u4fe1\u4e86\u3002\u201d \u3000\u3000\u2014\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5728\u4e0b\u8fd8\u6709\u4e00\u70b9\u5947\u602a\u4e4b\u5904\uff0c\u50cf\u8fd9\u79cd\u6613\u5bb9\u4e4b\u672f\uff0c\u5df2\u5230\u4e86\u795e\u79d8\u65e0\u6bd4\u4e4b\u5883\uff0c\u4ed6\u4eec\u53c8\u600e\u4f1a\u77e5\u9053\u662f\u4f60\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u5f88\u591a\u79cd\u65b9\u6cd5\uff0c\u4e0d\u8bba\u6211\u662f\u4ec0\u4e48\u6837\u7684\u5f62\u8c8c\u51fa\u73b0\uff0c\u4ed6\u4eec\u90fd\u53ef\u4ee5\u8ba4\u5f97\u51fa\u6765\u3002\u201d\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e2a\u65b9\u6cd5\u4e00\u5b9a\u5f88\u5947\u5999\u3001\u7b80\u5355\uff0c\u56e0\u4e3a\uff0c\u6211\u6ca1\u6709\u77a7\u51fa\u4f60\u6253\u51fa\u6697\u8bb0\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u4e00\u79cd\u6807\u8bc6\uff0c\u4ed6\u4eec\u77a7\u5230\u4e86\u90a3\u6807\u8bc6\uff0c\u5c31\u77e5\u9053\u662f\u6211\u6765\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u679c\u7136\u662f\u5f88\u7b80\u5355\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u73b0\u5728\uff0c\u4f60\u5df2\u7ecf\u5165\u9009\u4e86\u4e94\u5927\u5251\u4f7f\u4e4b\u4e00\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u5728\u6211\u4e4b\u524d\uff0c\u4f60\u624b\u4e0b\u6709\u51e0\u4e2a\u5251\u4f7f\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4e94\u4e2a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u628a\u53e6\u5916\u4e00\u4e2a\u4eba\u6324\u8d70\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4e94\u4e2a\u5251\u4f7f\uff0c\u73b0\u5728\u8fd8\u6709\u56db\u4e2a\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u4e00\u4e2a\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6b7b\u4e86\uff0c\u8fd9\u4e00\u7a7a\u4f4d\uff0c\u4e00\u76f4\u5728\u865a\u60ac\u7740\uff0c\u6211\u82b1\u4e86\u5f88\u591a\u7684\u94f6\u5b50\uff0c\u5c31\u662f\u8981\u627e\u4e00\u4e2a\u50cf\u4f60\u8fd9\u6837\u7684\u4eba\uff0c\u63a5\u4e0b\u8fd9\u4e00\u4f4d\u7a7a\u7f3a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u7684\u5b9e\u529b\uff0c\u6bd4\u7389\u5170\u53cc\u59dd\u5f3a\u5927\u4e86\u5f88\u591a\uff0c\u6211\u4e0d\u660e\u767d\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u80af\u81ea\u5df1\u51fa\u624b\uff0c\u5374\u501f\u7528\u7389\u5170\u53cc\u59dd\u7684\u529b\u91cf\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6ca1\u6709\u7ecf\u8fc7\u4e00\u79cd\u5f88\u60e8\u70c8\u7684\u8003\u9a8c\uff0c\u6211\u4e0d\u77e5\u9053\u4f60\u80fd\u5426\u63a5\u4e0b\u8fd9\u4e2a\u804c\u4f4d\uff0c\u800c\u4e14\uff0c\u6211\u4eec\u4e5f\u4e0d\u613f\u610f\u7559\u4e0b\u53ef\u4f9b\u4eba\u8ffd\u7d22\u7684\u75d5\u8ff9\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\u7684\u601d\u8651\uff0c\u679c\u7136\u5341\u5206\u5468\u5bc6\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f60\u7684\u8ffd\u9b42\u4e03\u5251\uff0c\u679c\u7136\u662f\u5f88\u9ad8\u660e\u7684\u5251\u6cd5\uff0c\u8fd9\u4e9b\u6210\u5c31\uff0c\u8db3\u4ee5\u62c5\u5f53\u5251\u4f7f\u7684\u804c\u4f4d\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e00\u4e2a\u95ee\u9898\uff0c\u6211\u8fd8\u5728\u8003\u8651\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u95ee\u9898\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f60\u7684\u5fe0\u8bda\uff0c\u6211\u8981\u5982\u4f55\u624d\u80fd\u76f8\u4fe1\u4f60\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u5982\u662f\u5821\u4e3b\u6ca1\u6709\u66f4\u597d\u7684\u529e\u6cd5\uff0c\u770b\u6765\uff0c\u4e5f\u53ea\u6709\u4fe1\u4efb\u6211\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u89c1\u4f60\u7684\u7b2c\u4e8c\u4ef6\u91cd\u8981\u7684\u4e8b\uff0c\u800c\u4e14\uff0c\u7acb\u523b\u5c31\u8981\u786e\u5b9a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7b2c\u4e00\u4ef6\u91cd\u8981\u4e8b\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u544a\u8bc9\u4f60\u5165\u9009\u4e86\u4e94\u5927\u5251\u4f7f\u7684\u51b3\u5b9a\uff0c\u4e94\u5927\u5251\u4f7f\uff0c\u5728\u6211\u4eec\u8fd9\u4e2a\u7ec4\u5408\u4e2d\uff0c\u662f\u5f88\u9ad8\u7684\u804c\u4f4d\uff0c\u4ed6\u4e0d\u4f46\u8981\u6709\u5f88\u9ad8\u660e\u7684\u6b66\u529f\uff0c\u800c\u4e14\uff0c\u8fd8\u8981\u6709\u5e94\u53d8\u7684\u624d\u667a\uff0c\u7edd\u5bf9\u7684\u5fe0\u8bda\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u6211\u60f3\u5148\u77e5\u9053\uff0c\u6211\u4eec\u8fd9\u7b97\u4e00\u4e2a\u4ec0\u4e48\u6837\u5b50\u7684\u7ec4\u5408\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u6709\u4e09\u4ef6\u6212\u5f8b\uff0c\u7b49\u4f60\u5c31\u4efb\u5251\u4f7f\u4e4b\u4f4d\u65f6\uff0c\u81ea\u4f1a\u544a\u8bc9\u4f60\uff0c\u90a3\u65f6\uff0c\u4f60\u8fd8\u6709\u9009\u62e9\u7684\u4f59\u5730\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u8fd8\u6709\u5c31\u4efb\u5251\u4f7f\u7684\u4eea\u5f0f\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8981\u628a\u53e6\u5916\u56db\u4f4d\u5251\u4f7f\uff0c\u548c\u51e0\u4e2a\u7ec4\u5408\u4e2d\u91cd\u8981\u7684\u4eba\uff0c\u90fd\u7ed9\u4f60\u5f15\u89c1\u8ba4\u8bc6\uff0c\u6211\u4eec\u5bf9\u5916\u9762\u662f\u4e00\u4e2a\u8c1c\uff0c\u4e0d\u53ef\u89e3\u7684\u795e\u79d8\u4e4b\u8c1c\uff0c\u4f46\u5bf9\u5185\uff0c\u5374\u662f\u5927\u90e8\u5206\u516c\u5f00\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u548c\u6211\u4eca\u5929\u51b3\u6218\u7684\u4f55\u5bd2\u8863\uff0c\u5251\u672f\u7684\u9ad8\u660e\uff0c\u7edd\u5bf9\u4e0d\u5728\u6211\u4e4b\u4e0b\uff0c\u53ef\u60dc\u54b1\u4eec\u53ea\u6709\u4e94\u5927\u5251\u4f7f\uff0c\u5982\u679c\u6709\u516d\u5927\u5251\u4f7f\uff0c\u4ed6\u5e94\u8be5\u662f\u4e00\u4e2a\u5f88\u597d\u7684\u4eba\u9009\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u4ed6\u672c\u6765\u5c31\u662f\u4e94\u5927\u5251\u4f7f\u4e4b\u9996\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5446\u4f4f\u4e86\u3002\u5fc3\u4e2d\u6697\u6697\u5fd6\u9053\uff1a\u6211\u5bf9\u4ed6\u6697\u793a\u4e86\u4e0d\u5c11\u7684\u8bdd\uff0c\u4ed6\u5982\u679c\u662f\u628a\u8fd9\u4e9b\u8bdd\uff0c\u544a\u8bc9\u4e86\u5821\u4e3b\uff0c\u90a3\u5c31\u52ff\u602a\u4ed6\u5bf9\u6211\u6000\u7591\u4e86\u3002 \u3000\u3000\u9752\u8863\u4eba\u63a5\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u5c5e\u4e0b\u3001\u540c\u65f6\u4e5f\u662f\u6211\u5f88\u597d\u7684\u670b\u53cb\uff0c\u4f60\u5982\u80fd\u5b89\u5fc3\u4e8e\u5251\u4f7f\u4e4b\u4f4d\uff0c\u4ee5\u540e\uff0c\u4f60\u4e5f\u548c\u4ed6\u4e00\u6837\uff0c\u79c1\u4eba\u4e4b\u95f4\uff0c\u4f60\u4e5f\u662f\u6211\u7684\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u91d1\u5251\u98de\u8f6e\uff0c\u5728\u6c5f\u6e56\u4e0a\u7684\u540d\u6c14\uff0c\u5c24\u5728\u6211\u53f6\u67d0\u4eba\u4e4b\u4e0a\u2026\u2026\u201d \u3000\u3000\u9752\u8863\u4eba\u63a5\u9053\uff1a\u201c\u4f46\u4ed6\u5374\u548c\u6211\u5904\u5f97\u5f88\u597d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u5df2\u7ecf\u5165\u9009\u4e86\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u6211\u8fd9\u4e00\u65b9\u9762\uff0c\u4f60\u5df2\u7ecf\u5165\u9009\u4e86\uff0c\u4f46\u5251\u4f7f\u4e4b\u4f4d\u975e\u540c\u5c0f\u53ef\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u8fd8\u8981\u95ee\u4e00\u4e0b\u4f60\u53f6\u5144\u7684\u610f\u89c1\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u597d\u50cf\u6ca1\u6709\u9009\u62e9\u7684\u4f59\u5730\u4e86\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u4e0d\u5c3d\u7136\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u662f\u8bf4\uff0c\u6211\u53ef\u4ee5\u9009\u62e9\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u53ef\u4ee5\uff0c\u5bf9\u4e8e\u5251\u4f7f\u8eab\u4efd\u7684\u4eba\uff0c\u6211\u4eec\u4e0d\u4f1a\u5f3a\u8feb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u601d\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6211\u9009\u62e9\u7684\u5c3a\u5ea6\uff0c\u6709\u591a\u5927\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\u9650\u5236\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6ca1\u6709\u9650\u5236\uff1f\u5982\u662f\u6211\u8981\u79bb\u5f00\u8fd9\u91cc\u5462\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4e5f\u53ef\u4ee5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6ca1\u6709\u4efb\u4f55\u56f0\u96be\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u770b\u8d77\u6765\uff0c\u4f60\u597d\u50cf\u77e5\u9053\u4e86\u4e0d\u5c11\u7684\u4e8b\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4f60\u4ec0\u4e48\u4e5f\u4e0d\u77e5\u9053\uff0c\u4f60\u4ed4\u7ec6\u60f3\u4e00\u60f3\uff0c\u5c31\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u6211\u77e5\u9053\u4e86\u4e09\u4ef6\u4e8b\uff0c\u7b2c\u4e00\uff0c\u5316\u8eab\u4e66\u751f\u662f\u8fd9\u4e2a\u7ec4\u7ec7\u7684\u9996\u8111\uff0c\u7b2c\u4e8c\uff0c\u4f55\u5bd2\u8863\u662f\u8fd9\u4e2a\u7ec4\u7ec7\u4e2d\u4e94\u5927\u5251\u4f7f\u4e4b\u4e00\uff0c\u7b2c\u4e09\uff0c\u590f\u6740\u662f\u8fd9\u4e2a\u7ec4\u7ec7\u4e2d\u7684\u6740\u624b\u4e4b\u4e00\uff0c\u7b2c\u56db\uff0c\u8fd9\u91cc\u6709\u4e00\u4e2a\u8273\u5a62\u6021\u7ea2\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u8fd9\u4e00\u4e9b\uff0c\u90fd\u4e0d\u592a\u91cd\u8981\uff0c\u5c31\u7b97\u4f60\u771f\u7684\u8bb2\u51fa\u53bb\uff0c\u4e5f\u4e0d\u4f1a\u5bf9\u6211\u4eec\u6709\u592a\u5927\u7684\u59a8\u5bb3\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u6211\u5012\u60f3\u8bf4\u660e\u767d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f60\u79bb\u5f00\u8fd9\u91cc\u4e4b\u540e\uff0c\u4f60\u5c31\u5931\u53bb\u4e86\u5f88\u591a\u7684\u597d\u670b\u53cb\uff0c\u4e5f\u5931\u53bb\u4e86\u6021\u7ea2\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7684\u76ee\u5149\uff0c\u7a81\u7136\u8f6c\u6ce8\u5230\u6021\u7ea2\u7684\u8eab\u4e0a\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u8fd9\u662f\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u96be\u9053\u4f60\u4e0d\u80fd\u8ddf\u6211\u4e00\u8d77\u8d70\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4e0d\u80fd\uff0c\u56e0\u4e3a\uff0c\u6211\u662f\u5c5e\u4e8e\u8fd9\u91cc\u7684\u4eba\uff0c\u6c38\u8fdc\u8981\u7559\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u56e0\u4e3a\u5821\u4e3b\u5bf9\u6211\u5f88\u5173\u5fc3\uff0c\u4e5f\u5f88\u7167\u987e\uff0c\u6211\u4e0d\u613f\u79bb\u5f00\u8fd9\u91cc\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u770b\u6765\uff0c\u5821\u4e3b\u7edf\u6cbb\u7684\u624b\u6cd5\uff0c\u4e0d\u4f46\u7edf\u6cbb\u4ed6\u4eec\u7684\u4eba\uff0c\u800c\u4e14\uff0c\u4e5f\u7edf\u6cbb\u4e86\u4ed6\u4eec\u7684\u5fc3\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u5ffd\u7136\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6211\u6709\u5f88\u591a\u8bdd\u60f3\u8bf4\u660e\u767d\uff0c\u4e0d\u8fc7\uff0c\u56e0\u4e3a\uff0c\u6709\u4e00\u4e9b\u987e\u8651\uff0c\u4f7f\u6211\u65e0\u6cd5\u8bf4\u51fa\u53e3\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u987e\u8651\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u662f\u6211\u4eec\u7684\u4eba\uff0c\u6211\u5982\u544a\u8bc9\u4f60\u592a\u591a\uff0c\u5bf9\u4f60\u5f88\u4e0d\u5229\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5982\u679c\u6211\u73b0\u5728\u79bb\u5f00\u5462\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u53f6\u5144\u5c3d\u5feb\u8bf7\u4fbf\uff0c\u8fd9\u5ea7\u9662\u843d\u4e4b\u5916\uff0c\u5df2\u7ecf\u6709\u7740\u4e09\u5339\u4e0a\u4e86\u978d\u7684\u5feb\u9a6c\uff0c\u4f60\u53ef\u4ee5\u968f\u4fbf\u9009\u62e9\u4e00\u5339\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u53e6\u5916\uff0c\u5821\u4e3b\u8fd8\u4f1a\u81f4\u9001\u7a0b\u4eea\u9ec4\u91d1\u767e\u4e24\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u6697\u5fd6\u9053\uff1a\u8fd9\u4e2a\u7ec4\u7ec7\uff0c\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u597d\u50cf\u4e00\u70b9\u4e5f\u4e0d\u5728\u4e4e\u6211\u7684\u53bb\u7559\uff0c\u4f46\u611f\u89c9\u4e2d\uff0c\u4ed6\u4eec\u5bf9\u6211\u4e49\u5f88\u91cd\u89c6\u3002\u8fd9\u83ab\u975e\u662f\u88c5\u4f5c\uff1f\u83ab\u975e\u6709\u4ec0\u4e48\u9634\u8c0b\uff1f \u3000\u3000\u53f6\u957f\u9752\u53c8\u4ed4\u7ec6\u7684\u6253\u91cf\u4e86\u90a3\u9752\u8863\u4eba\u4e00\u773c\u3002 \u3000\u3000\u7cbe\u5de7\u7684\u9762\u5177\u548c\u6613\u5bb9\u672f\uff0c\u63a9\u906e\u4e86\u4ed6\u7684\u8138\uff0c\u4f46\u5374\u65e0\u6cd5\u63a9\u906e\u4ed6\u7684\u773c\u795e\u3002 \u3000\u3000\u90a3\u662f\u4e00\u5bf9\u795e\u91c7\u7684\u773c\u795e\uff0c\u4f3c\u662f\u8574\u85cf\u7740\u65e0\u6bd4\u7684\u667a\u6167\u7684\u773c\u795e\u3002 \u3000\u3000\u53f6\u957f\u9752\u4ee5\u4ed6\u5728\u6c5f\u6e56\u4e0a\u7684\u9605\u4eba\u7ecf\u9a8c\uff0c\u5374\u770b\u4e0d\u51fa\u4efb\u4f55\u90aa\u6076\u3002 \u3000\u3000\u96be\u9053\u8fd9\u4e2a\u4eba\uff0c\u5e76\u975e\u662f\u4ec0\u4e48\u574f\u4eba\uff1f \u3000\u3000\u5bf9\u8fd9\u4e48\u4e00\u4e2a\u5145\u6ee1\u7740\u795e\u79d8\u7684\u4eba\u7269\uff0c\u53f6\u957f\u9752\u5b8c\u5168\u5931\u53bb\u4e86\u4f30\u7b97\u3002 \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u662f\u4e0d\u662f\u6709\u4e9b\u72b9\u8c6b\u4e86\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u82b1\u4e86\u5f88\u591a\u7684\u94b1\uff0c\u539f\u672c\u4e70\u6211\u4e00\u6761\u547d\uff0c\u5982\u4eca\uff0c\u5374\u53c8\u8981\u8f7b\u8f7b\u7684\u653e\u6211\u79bb\u53bb\uff0c\u635f\u5931\u4e86\u90a3\u4e48\u4e00\u5927\u7b14\u91d1\u94b1\uff0c\u96be\u9053\u4e00\u70b9\u4e5f\u4e0d\u5fc3\u75bc\u5417\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6211\u4eec\u76f8\u8bc6\u4e0d\u4e45\uff0c\u5b8c\u5168\u8c08\u4e0d\u5230\u4e86\u89e3\uff0c\u4e0d\u8fc7\uff0c\u81f3\u5c11\uff0c\u6211\u4eec\u5728\u8fd9\u51e0\u5929\u76f8\u5904\u4e2d\uff0c\u6211\u611f\u89c9\u5230\u6211\u4eec\u4e0d\u4f1a\u6210\u4e3a\u654c\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u53f6\u5144\u53bb\u610f\u65e2\u51b3\u65f6\uff0c\u6211\u4e0d\u4fbf\u5f3a\u7559\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5509\uff01\u6211\u4e0d\u77e5\u9053\u8be5\u4e0d\u8be5\u79f0\u4f60\u4e00\u58f0\u5821\u4e3b\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6211\u59d3\u9ec4\uff0c\u5176\u5b9e\uff0c\u600e\u4e48\u79f0\u547c\u6211\u90fd\u4e0d\u8981\u7d27\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f88\u5947\u602a\uff0c\u5bf9\u8fd9\u4e2a\u5145\u6ee1\u795e\u79d8\u7684\u4eba\u4eba\u4e8b\u4e8b\uff0c\u6211\u5ffd\u7136\u52a8\u4e86\u5f3a\u70c8\u7684\u597d\u5947\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u81ea\u5df1\u7684\u8bf7\u6c42\uff0c\u662f\u5426\u6709\u4e9b\u8fc7\u5206\uff0c\u6211\u60f3\u89c1\u89c1\u4f55\u5bd2\u8863\uff0c\u81ea\u4ece\u6211\u827a\u6ee1\u79bb\u5e08\u4e4b\u540e\uff0c\u4ed6\u662f\u552f\u4e00\u80fd\u63a5\u4e0b\u6211\u8ffd\u9b42\u4e03\u5251\u7684\u4eba\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u7531\u6765\u82f1\u96c4\u6700\u76f8\u60dc\u2026\u2026\u201d \u3000\u3000\u56de\u987e\u4e86\u6021\u7ea2\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u5c31\u50cf\u6021\u7ea2\u2014\u6837\uff0c\u81ea\u4ece\u548c\u4f60\u53f6\u5144\u76f8\u5904\u4e86\u6570\u65e5\u4e4b\u540e\uff0c\u5c0f\u59ae\u5b50\u7adf\u7136\u52a8\u4e86\u771f\u60c5\uff0c\u7ec8\u65e5\u6101\u7709\u4e0d\u5c55\uff0c\u53f6\u5144\u5982\u679c\u771f\u7684\u53bb\u4e86\uff0c\u4f60\u5c31\u5e26\u8d70\u4e86\u5979\u4e00\u9897\u82b3\u5fc3\u3002\u201d \u3000\u3000\u6021\u7ea2\u7684\u8138\u4e0a\uff0c\u6cdb\u6ee1\u5a07\u7f9e\uff0c\u7f13\u7f13\u5782\u4e0b\u4e86\u5934\uff0c \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u5728\u5185\u5fc3\u5df2\u7ecf\u4f5c\u4e86\u4e00\u4e2a\u51b3\u5b9a\uff0c\u672c\u6765\uff0c\u6211\u4e0d\u60f3\u8bf4\u51fa\u6765\uff0c\u73b0\u5728\uff0c\u6211\u770b\u8fd8\u662f\u65e9\u4e9b\u544a\u8bc9\u4f60\u7684\u597d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u51b3\u5b9a\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u5b9d\u5251\u8d60\u4fa0\u58eb\uff0c\u7ea2\u7c89\u8d60\u4f73\u4eba\uff0c\u5c31\u50cf\u7559\u4e0b\u53f6\u5144\u7684\u4e8b\uff0c\u5982\u679c\u7559\u4f4f\u4e86\u4f60\u7684\u4eba\uff0c\u7559\u4e0d\u4f4f\u4f60\u7684\u5fc3\uff0c\u7559\u4e0b\u4f60\uff0c\u53c8\u6709\u4f55\u76ca\uff1f\u6021\u7ea2\u65e2\u662f\u82b3\u5fc3\u6240\u5c5e\uff0c\u6211\u53c8\u4f55\u5fc5\u5f3a\u7559\u4e0b\u5979\uff1f\u53f6\u5144\u51b3\u5b9a\u79bb\u53bb\u65f6\uff0c\u6211\u4f1a\u8981\u5979\u4f34\u541b\u540c\u884c\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\u63a5\u9053\uff1a\u201c\u5821\u4e3b\u5272\u7231\u539a\u8d50\u2026\u2026\u201d \u3000\u3000\u9752\u8863\u4eba\u63a5\u9053\uff1a\u201c\u4e0d\uff01\u6211\u548c\u6021\u7ea2\u4e4b\u95f4\uff0c\u5341\u5206\u6e05\u767d\uff0c\u4f46\u5979\u7684\u8eab\u4e16\u582a\u601c\uff0c\u53f6\u5144\u5e26\u5728\u8eab\u4fa7\uff0c\u4e5f\u53ea\u80fd\u2026\u2026\u201d \u3000\u3000\u6021\u7ea2\u9eef\u7136\u63a5\u9053\uff1a\u201c\u6211\u77e5\u9053\u81ea\u5df1\uff0c\u53ea\u80fd\u4f5c\u4e00\u4e2a\u4f8d\u59be\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7531\u6765\u4fa0\u5973\u51fa\u98ce\u5c18\uff0c\u8fc7\u53bb\u7684\u4e8b\uff0c\u6211\u4e0d\u60f3\u77e5\u9053\uff0c\u4e5f\u4e0d\u613f\u77e5\u9053\u3002\u201d \u3000\u3000\u6562\u60c5\uff0c\u90a3\u6570\u65e5\u76f8\u5904\uff0c\u6021\u7ea2\u7684\u5a49\u8f6c\u53ef\u4eba\uff0c\u4e5f\u4f7f\u5f97\u53f6\u957f\u9752\u52a8\u4e86\u771f\u60c5\u3002 \u3000\u3000\u9752\u8863\u4eba\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u7531\u6765\u4fa0\u5973\u51fa\u98ce\u5c18\uff0c\u7ea2\u62c2\u3001\u674e\u9756\u3001\u866c\u9aef\u5ba2\uff0c\u7ea2\u62c2\u51fa\u8eab\u4e5f\u53ea\u662f\u6768\u5e9c\u4e2d\u4e00\u4e2a\u6b4c\u59ec\u2026\u2026\u201d \u3000\u3000\u6021\u7ea2\u63a5\u9053\uff1a\u201c\u7237\uff0c\u6e05\u767d\u73b7\u6c61\u7ec8\u6068\u4e8b\uff0c\u56de\u5934\u660e\u73e0\u66fe\u8499\u5c18\uff0c\u6b8b\u82b1\u4f55\u582a\u5e38\u4f8d\u541b\u3002\u6211\u8fd8\u662f\u7559\u5728\u7237\u7684\u8eab\u4fa7\uff0c\u4f5c\u4e00\u4e2a\u4e2b\u5934\u7684\u597d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u4f60\u771f\u7684\u4e0d\u613f\u8ddf\u6211\u5417\uff1f\u201d \u3000\u3000\u6021\u7ea2\u53cc\u76ee\u4e2d\u6d41\u4e0b\u4e24\u884c\u6e05\u6cea\uff0c\u9053\uff1a\u201c\u6211\u51fa\u8eab\u9752\u697c\uff0c\u8eab\u8eaf\u7ecf\u8fc7\u5343\u4eba\u62b1\uff0c\u6731\u5507\u66fe\u7ecf\u767e\u5ba2\u5c1d\uff0c\u4f60\u534a\u751f\u82f1\u540d\uff0c\u5251\u827a\u65e0\u53cc\uff0c\u600e\u80fd\u8981\u6211\u8fd9\u6837\u4e00\u4e2a\u4eba\uff0c\u5e38\u4f34\u8eab\u4fa7\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u80fd\u4e0d\u80fd\u8ba9\u6211\u89c1\u89c1\u4f55\u5bd2\u8863\uff0c\u5982\u662f\u6709\u788d\u96be\u4e4b\u5904\uff0c\u6211\u5c31\u5e26\u6021\u7ea2\u7acb\u523b\u4e0a\u8def\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u53ef\u4ee5\uff0c\u4e0d\u8fc7\uff0c\u8981\u7b49\u4e00\u65e5\u65f6\u95f4\uff0c\u6211\u7528\u98de\u9e3d\u53ec\u4ed6\u56de\u6765\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u5df2\u7ecf\u79bb\u5f00\u4e86\u5723\u6cc9\u9547\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u662f\uff01\u6b64\u523b\uff0c\u6050\u65e9\u5df3\u5728\u767e\u91cc\u4e4b\u5916\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u884c\u8272\u5982\u6b64\u5306\u6025\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6211\u8d76\u6765\u6b64\u5730\u4f1a\u4f60\uff0c\u4e5f\u662f\u5728\u5f88\u5306\u6025\u4e4b\u4e0b\u8d76\u6765\u7684\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u8981\u6025\u7740\u79bb\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6709\u6025\u4e8b\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u5bf9\uff01\u5341\u5206\u7d27\u6025\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u662f\u4e0d\u662f\u548c\u4f60\u4eec\u6574\u4e2a\u7ec4\u7ec7\u6709\u5173\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u5173\u7cfb\u5341\u5206\u91cd\u5927\uff0c\u7b49\u53f6\u5144\u6709\u4e00\u4e2a\u51b3\u5b9a\u4e4b\u540e\uff0c\u5728\u4e0b\u5c31\u8981\u79bb\u5f00\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9ec4\u5144\uff0c\u5728\u4e0b\u662f\u5426\u5c31\u4efb\u5251\u4f7f\u7684\u4e8b\uff0e\u53ef\u5426\u6682\u884c\u6309\u4e0b\u4e0d\u8c08\uff0c\u5bb9\u6211\u53f6\u67d0\u4eba\u4ee5\u4e00\u4e2a\u670b\u53cb\u7684\u7acb\u573a\uff0c\u7ed9\u6211\u4e00\u4e2a\u5e2e\u5fd9\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u53f6\u5144\u7684\u597d\u610f\uff0c\u5728\u4e0b\u5fc3\u9886\u4e86\uff0c\u6211\u4eec\u76ee\u524d\uff0c\u867d\u7136\u6709\u5f88\u5927\u7684\u5371\u673a\uff0c\u4f46\u8fd8\u7528\u4e0d\u5230\u5916\u4eba\u5e2e\u5fd9\uff0c\u53f6\u5144\uff0c\u5982\u662f\u4e0d\u613f\u52a0\u5165\u6211\u4eec\u8fd9\u4e2a\u7ec4\u7ec7\uff0c\u5b9e\u5728\u4e0d\u6562\u6709\u52b3\u3002\u201d \u3000\u3000\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4ed6\u4eec\u5df2\u653e\u51fa\u98de\u9e3d\uff0c\u53bb\u8ffd\u4f55\u5bd2\u8863\uff0c\u5982\u662f\u6ca1\u6709\u5176\u4ed6\u7684\u53d8\u5316\uff0c\u6211\u60f3\u660e\u6668\uff0c\u53f6\u5144\u5c31\u53ef\u4ee5\u89c1\u5230\u4ed6\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u4e00\u70b9\u4e5f\u6ca1\u6709\u903c\u8feb\u4ec0\u4e48\u7684\u610f\u601d\uff0c\u4f46\u8fd9\u79cd\u60c5\u4e49\u4e0a\u7684\u538b\u529b\uff0c\u8fdc\u6bd4\u903c\u8feb\u66f4\u4e3a\u5f3a\u5927\u3002 \u3000 \u7b2c \u4e09 \u56de\u3000\u662f\u662f\u975e\u975e \u3000 \u3000\u3000\u53f6\u957f\u9752\u51e0\u4e4e\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\u7684\u60c5\u7eea\uff0c\u627f\u8bfa\u51fa\u4efb\u5251\u4f7f\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u662f\u5fcd\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u4e9b\u5e74\uff0c\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u4f7f\u4ed6\u8001\u7ec3\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u4f46\u9752\u8863\u4eba\u5374\u8868\u73b0\u51fa\u4e86\u52a8\u4eba\u7684\u6c14\u5ea6\uff0c\u7b11\u2014\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6021\u7ea2\u966a\u4f60\u804a\u804a\uff0c\u6211\u5148\u79bb\u5f00\u4e00\u4e0b\uff0c\u4f55\u5bd2\u8863\u5982\u679c\u660e\u65e5\u8fd8\u65e0\u6cd5\u56de\u6765\uff0c\u6211\u4eec\u5c31\u4e0d\u80fd\u7b49\u4ed6\u5382\uff0c\u6700\u8fdf\u4e2d\u5348\u4e4b\u540e\uff0c\u6211\u4e5f\u8981\u79bb\u5f00\u8fd9\u91cc\uff0c\u90a3\u65f6\uff0c\u5982\u679c\u53f6\u5144\u8fd8\u65e0\u6cd5\u51b3\u5b9a\u4ec0\u4e48\uff0c\u4f60\u5c31\u5e26\u7740\u6021\u7ea2\u79bb\u5f00\u8fd9\u91cc\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u9752\u8863\u4eba\u6325\u6325\u624b\uff0c\u4e0d\u8ba9\u53f6\u957f\u9752\u518d\u8bf4\u4e0b\u53bb\uff0c\u5e26\u7740\u590f\u6740\u548c\u5f20\u73de\u800c\u53bb\u3002 \u3000\u3000\u5c0f\u5de7\u7684\u5385\uff0c\u53ea\u4f59\u4e0b\u4e86\u6021\u7ea2\u548c\u53f6\u957f\u9752\u3002 \u3000\u3000\u53f6\u957f\u9752\u671b\u671b\u6021\u7ea2\uff0c\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u867d\u7136\u6709\u4e9b\u795e\u79d8\uff0c\u65e0\u6cd5\u548c\u4eba\u4ee5\u771f\u9762\u76ee\u76f8\u89c1\uff0c\u4f46\u4ed6\u7684\u6c14\u5ea6\u5b9e\u5728\u53eb\u4eba\u6298\u670d\u3002\u201d \u3000\u3000\u6021\u7ea2\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u50cf\u4e91\u4e2d\u9f99\uff0c\u96fe\u91cc\u82b1\uff0c\u4f7f\u4eba\u6709\u4e9b\u6349\u6478\u4e0d\u6e05\uff0c\u4f46\u662f\u548c\u4ed6\u76f8\u5904\u8fc7\u7684\u4eba\uff0c\u65e0\u4e0d\u5bf9\u4ed6\u5fc3\u751f\u656c\u670d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u6709\u4e9b\u8bdd\uff0c\u4e5f\u8bb8\u6211\u4e0d\u8be5\u95ee\u2026\u2026\u201d \u3000\u3000\u6021\u7ea2\u63a5\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u5fc3\u4e2d\u60f3\u4ec0\u4e48\uff0c\u5c31\u7b97\u8bf4\u9519\u4e86\u4e5f\u4e0d\u8981\u7d27\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8981\u8c08\u7684\uff0c\u53ea\u662f\u9ec4\u5821\u4e3b\u4e00\u4e2a\u4eba\u2026\u2026\u201d \u3000\u3000\u6021\u7ea2\u63a5\u9053\uff1a\u201c\u4f60\u60f3\u77e5\u9053\u4ec0\u4e48\uff1f\u5c3d\u7ba1\u95ee\u5427\uff01\u4ed6\u5e26\u6211\u6765\u8fd9\u91cc\uff0c\u7559\u4e0b\u6211\u79bb\u5f00\uff0c\u5c31\u662f\u6709\u4e24\u4e2a\u7528\u5fc3\uff0c\u4e00\u4e2a\u662f\u8981\u6211\u7167\u987e\u4f60\uff0c\u4e00\u4e2a\u5c31\u662f\u8981\u6211\u89e3\u7b54\u4e00\u4e9b\u4ed6\u4e0d\u4fbf\u76f4\u63a5\u56de\u7b54\u7684\u95ee\u9898\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u5bf9\u4ed6\u77e5\u9053\u7684\u5f88\u591a\u5417\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4e0d\u592a\u591a\uff0c\u4f46\u4e5f\u4e0d\u5c11\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u5bf9\u4ed6\u4e86\u89e3\u591a\u5c11\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u4ed6\u4e2a\u4eba\uff0c\u8fd8\u662f\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u8fd8\u662f\u5148\u4ece\u4ed6\u4e2a\u4eba\u8c08\u8d77\u5427\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u5148\u8bf4\u4ed6\u5bf9\u6211\u7684\u4e8b\uff0c\u4ed6\u82b1\u8d39\u91cd\u91d1\uff0c\u628a\u6211\u7531\u9752\u697c\u4e2d\u8d4e\u51fa\u6765\u4e4b\u540e\uff0c\u4e00\u76f4\u7559\u6211\u5728\u4ed6\u8eab\u4fa7\uff0c\u7167\u987e\u4ed6\u7684\u751f\u6d3b\uff0c\u4f46\u4ed6\u5f88\u5fd9\uff0c\u4e00\u5e74\u4e4b\u4e2d\uff0c\u8981\u6211\u7167\u987e\u4ed6\u7684\uff0c\u4e5f\u4e0d\u8fc7\u662f\u51e0\u5341\u5929\u800c\u5df2\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\u3002 \u3000\u3000\u6021\u7ea2\u63a5\u9053\uff1a\u201c\u4ed6\u4e0d\u62d8\u5f62\u8ff9\uff0c\u4e5f\u4e0d\u4e3a\u4e16\u4fd7\u793c\u6cd5\u6240\u7f1a\uff0c\u4f46\u4ed6\u884c\u4e8b\uff0c\u5374\u662f\u6d01\u8eab\u81ea\u7231\uff0c\u6211\u7684\u59ff\u8272\u4e0d\u592a\u574f\uff0c\u800c\u4e14\uff0c\u4e5f\u6709\u732e\u8eab\u4e4b\u610f\uff0c\u4f46\u4ed6\u603b\u662f\u9002\u53ef\u800c\u6b62\uff0c\u6709\u51e0\u6b21\uff0c\u6211\u81ea\u5df1\u7559\u5728\u623f\u4e2d\u966a\u4ed6\uff0c\u76f4\u5230\u6df1\u591c\uff0c\u4f46\u4ed6\u4e00\u76f4\u6ca1\u6709\u8fc7\u4efb\u4f55\u903e\u8d8a\u7684\u4e3e\u52a8\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u53f6\u67d0\u4eba\u6bd4\u8d77\u9ec4\u5821\u4e3b\uff0c\u90a3\u662f\u6709\u7740\u9704\u58e4\u4e4b\u522b\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4e0d\u8981\u8fd9\u6837\u8bf4\uff0c\u6211\u5bf9\u4f60\u4e00\u7247\u771f\u60c5\uff0c\u624d\u8fd9\u6837\u6ca1\u6709\u4fdd\u7559\uff0c\u4ec0\u4e48\u4e8b\u90fd\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\uff01\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6211\u867d\u7136\u8ffd\u968f\u4ed6\u5df2\u5feb\u4e24\u5e74\uff0c\u8fd9\u76f8\u805a\u7684\u65e5\u5b50\uff0c\u5e76\u4e0d\u592a\u591a\uff0c\u4f46\u4ed6\u5bf9\u6211\uff0c\u5e76\u6ca1\u6709\u84c4\u610f\u8981\u4fdd\u5b88\u4ec0\u4e48\u9690\u79d8\u3002\u6240\u4ee5\uff0c\u6211\u624d\u77e5\u6653\u4e86\u4e0d\u5c11\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u4f60\u89c1\u8fc7\u4ed6\u7684\u771f\u6b63\u7684\u9762\u76ee\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6021\u7ea2\u6c89\u601d\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5e94\u8be5\u662f\u6709\uff0c\u6709\u65f6\u5019\uff0c\u4ed6\u6c90\u6d74\u66f4\u8863\uff0c\u5927\u6982\u603b\u4e0d\u80fd\u8fd8\u5e26\u7740\u7cbe\u5de7\u7684\u9762\u5177\u5427\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u5728\u6211\u7684\u8bb0\u5fc6\u4e4b\u4e2d\uff0c\u6709\u4e24\u4e09\u526f\u9762\u5b54\u63a5\u8fd1\u771f\u5b9e\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u4e5f\u65e0\u6cd5\u80af\u5b9a\uff0c\u54ea\u4e00\u526f\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u4f60\u8ba4\u8bc6\u5417\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u8ba4\u8bc6\uff0c\u79c1\u5e95\u4e0b\uff0c\u4ed6\u4eec\u4e24\u4e2a\u786e\u662f\u5f88\u597d\u7684\u670b\u53cb\uff0c\u5e38\u5e38\u80fd\u6e05\u8336\u4e00\u58f6\uff0c\u6df1\u5bb5\u957f\u53d9\uff0c\u4e0d\u77e5\u4e1c\u65b9\u5df2\u767d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9664\u4e86\u4f55\u5bd2\u8863\u4e4b\u5916\uff0c\u4f60\u662f\u5426\u8fd8\u89c1\u8fc7\u5176\u4ed6\u7684\u51e0\u4f4d\u5251\u4f7f\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u89c1\u8fc7\uff0c\u90fd\u662f\u6c5f\u6e56\u4e0a\u4e00\u65f6\u4fca\u5f66\u4e4b\u58eb\uff0c\u9664\u4e86\u4e00\u4f4d\u9ec4\u886b\u8001\u4eba\u4e4b\u5916\uff0c\u5176\u4ed6\u51e0\u4f4d\u90fd\u5f88\u5e74\u8f7b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u6837\u4e00\u4e2a\u7ec4\u7ec7\u5462\uff1f\u6211\u6709\u4e9b\u7cca\u6d82\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u4e00\u95ee\uff0c\u6211\u4e5f\u6709\u65e0\u4ece\u56de\u7b54\u7684\u611f\u89c9\uff0c\u5c31\u4e00\u822c\u6c5f\u6e56\u4e0a\u7684\u95e8\u6d3e\u770b\u4ed6\u4eec\uff0c\u5374\u548c\u522b\u4eba\u6709\u5f88\u591a\u7684\u4e0d\u540c\uff0c\u4e0d\u9519\uff0c\u4ed6\u4eec\u6709\u4e2a\u660e\u663e\u7684\u5730\u65b9\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u662f\u4e0d\u662f\u53eb\u4f5c\u9634\u9633\u5821\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u662f\uff01\u4f46\u771f\u6b63\u7559\u5728\u90a3\u91cc\u5904\u7406\u4e8b\u52a1\u7684\uff0c\u53ea\u662f\u51e0\u4e2a\u603b\u7ba1\uff0c\u4ed6\u4e2a\u4eba\u5374\u5f88\u5c11\u7559\u5728\u5821\u4e2d\uff0c\u90a3\u53ea\u662f\u4e00\u5ea7\u540d\u4e49\u4e0a\u7684\u603b\u5be8\uff0c\u8054\u7edc\u4e8b\u60c5\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u4ed6\u4eec\u8fd9\u4e48\u5306\u5fd9\uff0c\u4e91\u96c6\u4e0b\u8fd9\u4e48\u591a\u9ad8\u624b\u3001\u4eba\u624d\uff0c\u7a76\u7adf\u5728\u505a\u4e9b\u4ec0\u4e48\u4e8b\u5462\uff1f\u201d \u3000\u3000\u6021\u7ea2\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u6211\u5c31\u8bf4\u4e0d\u4e0a\u6765\uff0c\u4ed6\u597d\u50cf\u6709\u5f88\u591a\u7684\u4ea7\u4e1a\uff0c\u4e5f\u6709\u5f88\u591a\u7684\u751f\u610f\uff0c\u6709\u53d6\u4e4b\u4e0d\u5c3d\u7684\u8d22\u6e90\uff0c\u4e5f\u6709\u5f88\u591a\u5f88\u591a\u529e\u4e8b\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u4f60\u770b\u4ed6\u4eec\u505a\u7684\u4e8b\uff0c\u662f\u597d\uff0c\u662f\u574f\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u770b\u4ed6\u7684\u4eba\uff0c\u4e0d\u50cf\u574f\u4eba\uff0c\u6211\u66fe\u4e00\u5ea6\u6ca6\u843d\u98ce\u5c18\uff0c\u5bf9\u4e8b\u60c5\u597d\u574f\u7684\u770b\u6cd5\uff0c\u548c\u522b\u4eba\u6709\u4e9b\u4e0d\u540c\uff0c\u81f3\u5c11\uff0c\u6211\u89c9\u5f97\uff0c\u4ed6\u8fd9\u6837\u7684\u4eba\uff0c\u4e0d\u4f1a\u505a\u4ec0\u4e48\u574f\u4e8b\u51fa\u6765\uff0c\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u624b\u4e0b\uff0c\u6709\u4e94\u5927\u5251\u4f7f\uff0c\u90a3\u8bf4\u660e\u4e86\uff0c\u662f\u4e00\u4e2a\u7eaf\u6c5f\u6e56\u7684\u95e8\u6d3e\u7ec4\u7ec7\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u7237\uff0c\u5bf9\u6c5f\u6e56\u4e2d\u7684\u4e8b\u4e0e\u4eba\uff0c\u6211\u5c31\u77e5\u9053\u7684\u4e0d\u591a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u5e2e\u6211\u5b9a\u4e2a\u4e3b\u610f\uff0c\u6211\u5e94\u4e0d\u5e94\u6f14\u7559\u5728\u8fd9\u91cc\uff1f\u7167\u4ed6\u4eec\u63b3\u6211\u6765\u6b64\u7684\u624b\u6bb5\u3001\u65b9\u6cd5\uff0c\u5341\u5206\u6076\u6bd2\u3001\u9634\u9669\uff0c\u5728\u672a\u89c1\u8fc7\u9ec4\u5821\u4e3b\u4e4b\u524d\uff0c\u6211\u5bf9\u4ed6\u4eec\u8fd9\u4e2a\u7ec4\u7ec7\uff0c\u4e00\u76f4\u6ca1\u6709\u597d\u611f\uff0c\u60f3\u4e0d\u5230\u548c\u4ed6\u4e00\u5e2d\u4ea4\u8c08\uff0c\u7adf\u7136\u662f\u5370\u8c61\u5927\u53d8\u3002\u201d \u3000\u3000\u6021\u7ea2\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u662f\u771f\u7684\u60f3\u7559\u4e0b\u4f60\uff0c\u800c\u4e14\uff0c\u4e5f\u4ea4\u4ee3\u8fc7\u6211\u597d\u597d\u7684\u529d\u529d\u4f60\u3002\u4ed6\u8bf4\uff0c\u4ed6\u5e0c\u671b\u4f60\u7559\u4e0b\u6765\uff0c\u6240\u4ee5\u4ed6\u521a\u624d\u8bf4\uff0c\u4f60\u4e00\u5b9a\u8981\u8d70\uff0c\u4f60\u4f1a\u5931\u53bb\u5f88\u591a\u7684\u670b\u53cb\uff0c\u5305\u62ec\u6211\u4e5f\u5728\u5185\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u4f46\u4ed6\u540e\u6765\uff0c\u53c8\u8981\u6211\u5e26\u7740\u4f60\u8d70\uff01\u4e3a\u4ec0\u4e48\u4ed6\u53c8\u6539\u53d8\u4e86\u4e3b\u610f\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u957f\u9752\uff0c\u6211\u4e0d\u662f\u6545\u610f\u5e2e\u9ec4\u7237\u8bb2\u8bdd\uff0c\u4ed6\u5b9e\u5728\u662f\u4e00\u4e2a\u5f88\u6709\u6c14\u5ea6\u7684\u4eba\uff0c\u4ed6\u53d1\u89c9\u6211\u4e0d\u80fd\u4f7f\u4f60\u7559\u604b\u7684\u65f6\u5019\uff0c\u7acb\u523b\u6539\u53d8\u4e86\u4e3b\u610f\u53c8\u8ba9\u4f60\u5e26\u7740\u6211\u79bb\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u5df2\u7ecf\u5f00\u59cb\u5bf9\u4ed6\u4f5c\u4e86\u53e6\u4e00\u79cd\u4f30\u4ef7\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u4f30\u4ef7\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u4e0d\u4f46\u662f\u4e00\u4e2a\u6b66\u6797\u9ad8\u624b\uff0c\u4e5f\u662f\u4e00\u4f4d\u5929\u751f\u7684\u9886\u8896\u4eba\u7269\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u5bf9\u9634\u9633\u5821\u8fd9\u4e2a\u7ec4\u7ec7\uff0c\u5fc3\u4e2d\u8fd8\u6709\u5f88\u591a\u7684\u4e0d\u89e3\u4e4b\u5904\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u7237\uff0c\u5a62\u5b50\u77e5\u9053\u7684\uff0c\u6211\u4f1a\u5c3d\u91cf\u7684\u544a\u8bc9\u4f60\uff0c\u5982\u662f\u5a62\u5b50\u4e0d\u77e5\u9053\u7684\uff0c\u90a3\u5c31\u6055\u6211\u65e0\u80fd\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u8ffd\u968f\u5821\u4e3b\u5df2\u8fd1\u4e24\u5e74\uff0c\u4ed6\u53c8\u662f\u628a\u4f60\u6551\u51fa\u98ce\u5c18\u7684\u6069\u4eba\uff0c\u6709\u4e9b\u8bdd\uff0c\u53ea\u6015\u4f60\u4e5f\u4e0d\u65b9\u4fbf\u8bb2\u3002\u201d \u3000\u3000\u6021\u7ea2\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5c31\u5a62\u5b50\u4e24\u5e74\u89c1\u95fb\uff0c\u9ec4\u7237\u5e76\u6ca1\u6709\u505a\u51fa\u4ec0\u4e48\u89c1\u4e0d\u5f97\u5929\u65e5\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u8fd9\u4e2a\u7ec4\u7ec7\u5728\u6c5f\u6e56\u4e0a\u4e00\u76f4\u4fdd\u6301\u4e86\u76f8\u5f53\u7684\u795e\u79d8\u611f\uff0c\u4f60\u53ef\u77e5\u9053\u7528\u5fc3\u4f55\u5728\u5417\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6211\u542c\u9ec4\u7237\u8bf4\u8fc7\uff0c\u4fdd\u6301\u4e00\u4e9b\u795e\u79d8\u611f\uff0c\u884c\u52a8\u4f1a\u65b9\u4fbf\u76f8\u591a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u6653\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4ed6\u4eec\u5306\u5306\u5fd9\u5fd9\uff0c\u90fd\u662f\u529e\u4e9b\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u529e\u4e9b\u4ec0\u4e48\u4e8b\uff0c\u5a62\u5b50\u662f\u4e0d\u592a\u6e05\u695a\uff0c\u4e0d\u8fc7\uff0c\u5a62\u5b50\u786e\u77e5\u4ed6\u4eec\u662f\u5728\u529e\u4e00\u4ef6\u5927\u4e8b\uff0c\u548c\u5723\u6c34\u6709\u5173\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u771f\u6709\u5723\u6c34\u5417\uff1f\u201d \u3000\u3000\u6021\u7ea2\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5a62\u5b50\u5df2\u7ecf\u542c\u4ed6\u4eec\u6570\u6b21\u63d0\u5230\u5723\u6c34\uff0c\u5927\u6982\u4e0d\u4f1a\u662f\u5047\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c31\u7b97\u786e\u6709\u5723\u6c34\uff0c\u90a3\u4e5f\u4e0d\u8fc7\u662f\u4e00\u79cd\u6cc9\u6c34\u7f62\u4e86\uff0c\u600e\u4e48\u4f1a\u4f7f\u5f97\u6b66\u6797\u4e2d\u4eba\uff0c\u5982\u6b64\u7684\u91cd\u89c6\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5a62\u5b50\u5c31\u4e0d\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u5916\u5462\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u5a62\u5b50\u8fd8\u542c\u4ed6\u4eec\u8c08\u8bba\u8fc7\u4e10\u5e2e\u548c\u5c11\u6797\u5bfa\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u5934\u4e00\u9707\uff0c\u9053\uff1a\u201c\u4e10\u5e2e\u548c\u5c11\u6797\u5bfa\uff0c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u6021\u7ea2\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u5a62\u5b50\u6ca1\u6709\u542c\u5f97\u8be6\u60c5\u5185\u5bb9\uff0c\u53ea\u542c\u9ec4\u7237\u5f88\u4e25\u8083\u5730\u8bf4\uff0c\u5179\u4e8b\u4f53\u5927\uff0c\u4e0d\u53ef\u83bd\u649e\u884c\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u4ed6\u8fd8\u63d0\u5230\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u9ec4\u7237\u548c\u51e0\u4f4d\u597d\u53cb\u4f1a\u5546\uff0c\u90fd\u7531\u5a62\u5b50\u5949\u8336\uff0c\u5a62\u5b50\u7a76\u7adf\u662f\u4e0b\u4eba\u8eab\u4efd\uff0c\u4e0d\u4fbf\u7ad9\u5728\u4e00\u65c1\u5077\u542c\uff0c\u5076\u5c14\u542c\u5f97\u4e09\u4e94\u53e5\u5bb9\u6613\u8bb0\u4e0b\u7684\u8bdd\uff0c\u624d\u80fd\u8bb0\u5728\u5fc3\u4e2d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u60f3\u60f3\u770b\uff0c\u4f60\u8fd8\u542c\u5230\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u5a62\u5b50\u8fd8\u542c\u5230\u4ed6\u4eec\u63d0\u8fc7\u4e00\u53e5\u957f\u6625\u5bab\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u957f\u6625\u5bab\uff0c\u6021\u7ea2\uff0c\u4f60\u6ca1\u6709\u542c\u9519\u5427\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6ca1\u6709\u542c\u9519\uff0c\u5a62\u5b50\u8bb0\u5f97\u5f88\u6e05\u695a\uff0c\u4ed6\u4eec\u8c08\u8fc7\u957f\u6625\u5bab\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4f4e\u58f0\u81ea\u8bed\uff0c\u9053\uff1a\u201c\u957f\u6625\u5bab\uff0c\u957f\u6625\u5bab\uff0c\u8fd9\u540d\u5b57\u597d\u751f\u719f\u6089\uff0c\u6c5f\u6e56\u4e0a\u597d\u50cf\u6709\u4eba\u63d0\u8fc7\uff0c\u600e\u7684\u6211\u7adf\u7136\u60f3\u5b83\u4e0d\u8d77\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u7237\uff01\u5a62\u5b50\u542c\u8bf4\uff0c\u90a3\u957f\u6625\u5bab\u5185\uff0c\u9752\u6625\u6c38\u9a7b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5bf9\uff01\u5bf9\uff01\u4f60\u8fd9\u4e48\u4e00\u63d0\uff0c\u6211\u5012\u662f\u60f3\u8bb0\u6765\u4e86\uff0c\u957f\u6625\u5bab\uff0c\u4e0d\u9519\uff0c\u90a3\u4e5f\u662f\u4e00\u5904\u5145\u6ee1\u7740\u795e\u79d8\u7684\u5730\u65b9\uff0c\u6021\u7ea2\uff0c\u60f3\u60f3\u770b\uff0c\u8fd8\u542c\u5230\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6021\u7ea2\u51dd\u76ee\u6c89\u601d\u4e86\u5f88\u4e45\uff0c\u9053\uff1a\u201c\u7237\uff0c\u6211\u771f\u7684\u60f3\u4e0d\u51fa\u4e86\uff0c\u4ed6\u4eec\u8c08\u4e86\u5f88\u591a\u6b21\uff0c\u6211\u4e5f\u542c\u5230\u8fc7\u5f88\u591a\u6b21\uff0c\u4f46\u5a62\u5b50\u5e76\u6ca1\u6709\u5fc3\u5077\u542c\uff0c\u6240\u4ee5\uff0c\u53ea\u8bb0\u4e86\u4e00\u4e9b\u5bb9\u6613\u8bb0\u7684\u4e8b\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u4f4f\u53e3\uff0c\u51dd\u76ee\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u63a5\u9053\uff1a\u201c\u8fd8\u6709\u51e0\u53e5\u8bdd\uff0c\u5a76\u5b50\u542c\u4e0d\u61c2\u4ec0\u4e48\u610f\u601d\uff0c\u4f46\u662f\u4e5f\u8bb0\u5728\u5fc3\u4e2d\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u72c2\u9f99\u3001\u98de\u9e70\u3001\u4e94\u9b3c\u8239\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7684\u8138\u8272\u53d8\u4e86\uff0c\u957f\u957f\u5618\u4e00\u53e3\u6c14\uff0c\u5e73\u590d\u4e00\u4e0b\u5fc3\u4e2d\u7684\u6fc0\u52a8\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u6211\u8fd8\u80fd\u4e0d\u80fd\u89c1\u5230\u5821\u4e3b\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u5927\u6982\u4f1a\u5427\uff01\u4ed6\u4e0d\u662f\u8fd8\u8981\u89c1\u4f60\u4e00\u9762\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\uff01\u6021\u7ea2\uff0c\u60f3\u6cd5\u5b50\uff0c\u6211\u73b0\u5728\u5c31\u8981\u89c1\u4ed6\u3002\u201d \u3000\u3000\u6021\u7ea2\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\uff01\u6211\u53bb\u627e\u627e\u770b\u3002\u201d\u5feb\u6b65\u884c\u51fa\u5927\u5385\u3002 \u3000\u3000\u8db3\u8db3\u6709\u4e00\u987f\u996d\u5de5\u592b\uff0c\u6021\u7ea2\u56de\u6765\u4e86\uff0c\u4f46\u5979\u5374\u6ca1\u6709\u5e26\u6765\u5821\u4e3b\u3002 \u3000\u3000\u6021\u7ea2\u540e\u9762\uff0c\u8ddf\u7740\u4e00\u4e2a\u4eba\u8fdb\u6765\uff0c\u662f\u5f20\u73de\u3002 \u3000\u3000\u5f20\u73de\u7684\u795e\u60c5\u6709\u4e9b\u6025\u8feb\u3002 \u3000\u3000\u4f46\u53f6\u957f\u9752\u6bd4\u4ed6\u66f4\u6025\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u5f20\u5144\uff0c\u5821\u4e3b\u5462\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u6709\u6025\u4e8b\u5148\u8d70\u4e86\u4e00\u6b65\uff0c\u4e34\u53bb\u5429\u5490\u5728\u4e0b\uff0c\u53f6\u5144\u6709\u4ec0\u4e48\u9700\u8981\uff0c\u53ea\u7ba1\u4ea4\u4ee3\u5f20\u67d0\uff0c\u7a0b\u4eea\u3001\u5feb\u9a6c\uff0c\u90fd\u4e3a\u53f6\u5144\u51c6\u5907\u597d\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8981\u8d70\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u90a3\u53f6\u5144\u662f\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u6211\u53ea\u60f3\u5c3d\u5feb\u89c1\u89c1\u9ec4\u5821\u4e3b\uff0c\u6211\u6709\u8bdd\u8981\u8bf4\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u5821\u4e3b\u63a5\u5230\u4e86\u2014\u5c01\u98de\u9e3d\u4f20\u4e66\uff0c\u53bb\u5f97\u5f88\u5306\u5fd9\uff0c\u53ea\u4ea4\u4ee3\u5728\u4e0b\u51e0\u53e5\u8bdd\uff0c\u6700\u91cd\u8981\u7684\u4e00\u70b9\uff0c\u53f6\u5144\u8981\u8d70\u65f6\uff0c\u4e00\u5b9a\u8981\u5e26\u8d70\u6021\u7ea2\u3002\u201d\u53f6\u957f\u9752\u6413\u6413\u53cc\u624b\uff0c\u9053\uff1a\u201c\u5f20\u5144\uff0c\u544a\u8bc9\u6211\uff0c\u5728\u4e0b\u5230\u54ea\u91cc\u53ef\u4ee5\u89c1\u5230\u5821\u4e3b\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u76ee\u4e0b\u5821\u4e3b\u884c\u65b9\u4e0d\u660e\uff0c\u53f6\u5144\u4e00\u5b9a\u8981\u89c1\u5821\u4e3b\uff0c\u6211\u770b\u5012\u4e0d\u5982\u5728\u8fd9\u91cc\u7b49\u4ed6\u7684\u6d88\u606f\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5f20\u73de\uff0c\u6211\u5fc3\u6025\u5982\u711a\uff0c\u5b9e\u5728\u7b49\u4e0d\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u54e6\uff01\u53f6\u5144\u5982\u6b64\u7126\u6025\uff0c\u5144\u5f1f\u60f3\u6cd5\u5b50\u53bb\u627e\u627e\u770b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u53ea\u5e0c\u671b\u80fd\u65e9\u4e00\u70b9\u89c1\u5230\u5821\u4e3b\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u5728\u4e0b\u5c3d\u529b\u800c\u4e3a\u3002\u201d \u3000\u3000\u5f20\u73de\u8f6c\u8eab\u884c\u4e86\u51fa\u53bb\uff0c \u3000\u3000\u53f6\u957f\u9752\u4e0d\u505c\u5730\u5728\u5ba4\u4e2d\u8d70\u52a8\u3002 \u3000\u3000\u7126\u8651\u4e4b\u60c5\uff0c\u5f62\u8bf8\u4e8e\u795e\u8272\u4e4b\u95f4\u3002 \u3000\u3000\u6021\u7ea2\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u65e0\u9650\u6e29\u67d4\u5730\u8bf4\u9053\uff1a\u201c\u8010\u5fc3\u4e00\u4e9b\uff0c\u6211\u60f3\u4e0d\u8bba\u4ec0\u4e48\u91cd\u8981\u7684\u4e8b\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\uff0c\u4ed6\u2014\u5b9a\u4f1a\u56de\u6765\u89c1\u4f60\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6211\u5fc5\u987b\u8981\u65e9\u4e9b\u89c1\u5230\u4ed6\uff0c\u6021\u7ea2\uff0c\u6211\u5fc3\u4e2d\u5f88\u591a\u7684\u4e8b\uff0c\u5fc5\u987b\u8981\u544a\u8bc9\u4f60\uff0c\u4f46\u6700\u91cd\u7684\u662f\u8981\u6551\u4ed6\u6027\u547d\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6551\u4ed6\u6027\u547d\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5bf9\uff01\u6211\u6697\u7b97\u4e86\u4ed6\u3002\u201d \u3000\u3000\u6021\u7ea2\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u6697\u7b97\u4e01\u4ed6\uff0c\u8fd9\u4e0d\u592a\u53ef\u80fd\u5427\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u5df2\u7ecf\u6697\u7b97\u4e86\u4ed6\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6211\u542c\u8bf4\u5821\u4e3b\u7684\u6b66\u529f\u5f88\u9ad8\uff0c\u4e94\u4e08\u5185\uff0c\u53ef\u4ee5\u542c\u5230\u843d\u53f6\u7684\u58f0\u97f3\uff0c\u4f60\u5982\u4f55\u80fd\u591f\u6697\u7b97\u5f97\u4e86\u4ed6\uff0c\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u539f\u6765\u53ea\u8ba4\u4e3a\u4ed6\u662f\u4e00\u4e2a\u6740\u4eba\u7ec4\u7ec7\u7684\u9996\u8111\uff0c\u6240\u4ee5\u6211\u6697\u7b97\u4e86\u4ed6\uff0c\u800c\u4e14\uff0c\u662f\u4e00\u4e2a\u5f88\u5351\u4e0b\u624b\u6bb5\u7684\u6697\u7b97\u3002\u201d \u3000\u3000\u6021\u7ea2\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u7d27\u5f20\uff0c\u4f60\u6697\u7b97\u4ed6\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u77e5\u9053\uff0c\u4ed6\u6ca1\u6709\u8bf4\u660e\u767d\uff0c\u90a3\u662f\u4ed6\u6839\u672c\u5c31\u6ca1\u6709\u628a\u8fd9\u4ef6\u4e8b\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u8083\u5730\u8bf4\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u5929\u4e0b\u6ca1\u6709\u4eba\u80fd\u591f\u907f\u8fc7\u90a3\u79cd\u6697\u7b97\uff0c\u6211\u53ea\u662f\u6697\u6740\u4e00\u4e2a\u4e13\u95e8\u6740\u4eba\u7ec4\u7ec7\u9996\u8111\uff0c\u6240\u4ee5\uff0e\u6211\u4e0d\u8ba1\u8f83\u624b\u6bb5\u5982\u4f55\uff0c\u521a\u624d\uff0c\u6211\u542c\u5230\u4e86\u4f60\u7684\u4e00\u756a\u8bdd\uff0c\u624d\u77e5\u9053\uff0c\u4ed6\u53ef\u80fd\u4e0d\u662f\u4e00\u4e2a\u574f\u4eba\uff0c\u4f55\u5bd2\u8863\u4e5f\u4e0d\u50cf\u3002\u201d \u3000\u3000\u6021\u7ea2\u63a5\u9053\uff1a\u201c\u7237\uff0c\u4f60\u548c\u4ed6\u8c08\u4e86\u90a3\u6837\u4e45\u7684\u8bdd\uff0c\u96be\u9053\u5c31\u77a7\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u77a7\u4e0d\u51fa\u6765\uff0c\u4e00\u70b9\u4e5f\u77a7\u4e0d\u51fa\u6765\uff0c\u800c\u4e14\uff0c\u6c5f\u6e56\u4e0a\u6709\u5f88\u591a\u5047\u5192\u4f2a\u5584\uff0c\u7b11\u91cc\u85cf\u5200\u7684\u4eba\uff0c\u6211\u4e0d\u80fd\u88ab\u4ed6\u4eec\u9a97\u8fc7\u53bb\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u534a\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u5982\u679c\u8fd8\u65e0\u6cd5\u8ffd\u4ed6\u56de\u6765\uff0c\u5c31\u7b97\u4ed6\u518d\u56de\u6765\uff0c\u6211\u4e5f\u65e0\u80fd\u4e3a\u529b\u3002\u201d \u3000\u3000\u6021\u7ea2\u4e5f\u6709\u4e9b\u7d27\u5f20\u4e86\uff0c\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u5feb\u4e9b\u8d70\u5427\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8d70\uff01\u5230\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u9003\u547d\u53bb\u5427\uff01\u9ec4\u7237\u5982\u679c\u6b7b\u4e86\uff0c\u4ed6\u4eec\u7edd\u5bf9\u4e0d\u4f1a\u653e\u8fc7\u4f60\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u82e6\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u6211\u5012\u4e0d\u5728\u4e4e\u81ea\u5df1\u7684\u751f\u6b7b\uff0c\u6211\u53ea\u662f\u6068\u81ea\u5df1\u6740\u4e86\u4e00\u4e2a\u6c5f\u6e56\u9ad8\u4eba\u3001\u4fa0\u58eb\u2026\u2026\u201d \u3000\u3000\u9eef\u7136\u4e00\u53f9\uff0c\u63a5\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u6211\u8d70\u4e86\uff0c\u4ed6\u4eec\u4e00\u5b9a\u8fc1\u6012\u5230\u4f60\u8eab\u4e0a\u3002\u201d \u3000\u3000\u6021\u7ea2\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u8fc1\u6012\u4e8e\u6211\uff0c\u5f88\u96be\u8bf4\uff0c\u4e0d\u8fc7\uff0c\u9ec4\u7237\u6b7b\u4e86\uff0c\u6211\u5c11\u4e86\u4e00\u4e2a\u53ef\u4ee5\u66f2\u5bb9\u6211\u7684\u4eba\uff0c\u4f46\u4f60\u53ef\u4ee5\u653e\u5fc3\uff0c\u6211\u4e0d\u4f1a\u7ed9\u4ed6\u4eec\u62a5\u590d\u7684\u673a\u4f1a\uff0c\u4f60\u8d70\u4e86\uff0c\u6211\u4e5f\u4f1a\u4e86\u65ad\u81ea\u5df1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e2a\u66f4\u4e0d\u884c\u4e86\uff0c\u6211\u5982\u4f55\u80fd\u591f\u518d\u5bb3\u4f60\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u54b1\u4eec\u4e24\u4e2a\u6bd4\u8d77\u6765\uff0c\u4f60\u6bd4\u6211\u91cd\u8981\u591a\u4e86\uff0c\u6211\u4e0d\u8fc7\u662f\u4e00\u4e2a\u51fa\u8eab\u9752\u697c\u7a91\u5b50\u7684\u59d1\u5a18\uff0c\u6211\u7684\u751f\u6b7b\uff0c\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u5982\u679c\u771f\u7684\u9519\u6740\u4e86\u9ec4\u5821\u4e3b\uff0c\u6211\u4e5f\u4e0d\u4f1a\u539f\u8c05\u81ea\u5df1\u3002\u201d \u3000\u3000\u6021\u7ea2\u6c89\u541f\u4e0d\u8bed\u3002\u4f46\u4e24\u884c\u6cea\u6c34\uff0c\u5374\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u4e00\u4e2a\u662f\u5979\u7684\u6069\u4eba\uff0c\u4e00\u4e2a\u662f\u5979\u7684\u60c5\u4eba\uff0c\u5979\u5728\u8fd9\u4e24\u8005\u4e4b\u95f4\uff0c\u65e2\u4e0d\u80fd\u62b1\u6028\u54ea\u4e2a\uff0c\u4e5f\u65e0\u6cd5\u6df1\u8c08\u4e0b\u53bb\uff0c\u4e00\u4e2a\u5973\u4eba\uff0c\u5904\u6b64\u60c5\u666f\uff0c\u9664\u4e86\u6d41\u6cea\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u522b\u7684\u529e\u6cd5\u3002 \u3000\u3000\u6021\u7ea2\u6697\u6697\u7684\u6d41\u6cea\uff0c\u53f6\u957f\u9752\u5374\u5750\u5728\u4e00\u4fa7\u53d1\u5446\u3002 \u3000\u3000\u76f4\u5230\u5f20\u73de\u56de\u5230\u5385\u4e2d\uff0c\u624d\u6253\u7834\u4e86\u6c89\u5bc2\u3002 \u3000\u3000\u53f6\u957f\u9752\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5f20\u5144\uff0c\u9ec4\u5821\u4e3b\u4ed6\u2026\u2026\u201d \u3000\u3000\u5f20\u73de\u7b11\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u8bf7\u5750\uff0c\u5821\u4e3b\u7acb\u523b\u5c31\u5230\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u671b\u671b\u5929\u8272\uff0c\u9053\uff1a\u201c\u5f20\u5144\uff0c\u534a\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u80fd\u4e0d\u80fd\u8d76\u56de\u6765\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5c31\u4e0d\u6e05\u695a\u4e86\uff0c\u4e0d\u8fc7\uff0c\u592a\u9633\u4e0b\u5c71\u4ee5\u524d\uff0c\u4e00\u5b9a\u53ef\u4ee5\u8d76\u56de\u6765\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u8d85\u8fc7\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u53ea\u6015\u5c31\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u53f6\u5144\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u975e\u8981\u5728\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u5185\u529e\u59a5\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f20\u5144\uff0c\u5982\u82e5\u5728\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u9ec4\u5821\u4e3b\u8fd8\u4e0d\u80fd\u56de\u6765\uff0c\u90a3\u5c31\u5f88\u9ebb\u70e6\u4e86\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u5173\u7cfb\u5f88\u5927\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u81f3\u5c11\u662f\u4e00\u6761\u4eba\u547d\uff0c\u4f60\u60f3\u662f\u4e0d\u662f\u91cd\u8981\u5462\uff1f\u201d \u3000\u3000\u5f20\u73de\uff1a\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4eba\u547d\uff1f\uff01\u4ec0\u4e48\u610f\u601d\uff1f\u96be\u9053\u6709\u4eba\u8981\u6740\u4f60\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6b7b\u7684\u5982\u82e5\u662f\u5728\u4e0b\uff0c\u4e5f\u8bb8\u6211\u4e0d\u4f1a\u8fd9\u6837\u7d27\u5f20\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u4e0d\u662f\u4f60\uff0c\u96be\u9053\u662f\u5821\u4e3b\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5bf9\uff01\u662f\u5821\u4e3b\uff0c\u5982\u82e5\u4ed6\u65e0\u6cd5\u5728\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u5185\u8d76\u56de\u6765\uff0c\u5c31\u7b97\u662f\u534e\u9640\u91cd\u751f\uff0c\u6241\u9e4a\u8fd8\u9b42\uff0c\u53ea\u6015\u4e5f\u65e0\u6cd5\u533b\u5f97\u597d\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u5821\u4e3b\u600e\u4f1a\u53d7\u5230\u4f24\u5bb3\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f20\u5144\uff0c\u662f\u771f\u7684\uff0c\u4f60\u5c3d\u5feb\u60f3\u6cd5\u5b50\u544a\u8bc9\u4ed6\uff0c\u8981\u4ed6\u8d76\u56de\u6765\u3002\u201d \u3000\u3000\u5f20\u73de\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u6ca1\u6709\u6bdb\u75c5\u5427\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u6b63\u5e38\u5f97\u5f88\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u533a\u533a\u5012\u662f\u60f3\u4e0d\u901a\uff0c\u4ec0\u4e48\u4eba\u80fd\u4f24\u5230\u5821\u4e3b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c31\u662f\u533a\u533a\u5728\u4e0b\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u4f60\u771f\u7684\u6697\u7b97\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u5f20\u73de\u795e\u8272\u51dd\u91cd\u8d77\u6765\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u7528\u4ec0\u4e48\u529e\u6cd5\u6697\u7b97\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5929\u5c71\u5bd2\u94c1\u65e0\u76f8\u9488\u3002\u201d \u3000\u3000\u5f20\u73de\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u7528\u8fd9\u79cd\u6b79\u6bd2\u4e4b\u7269\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u4e0d\u662f\u4e00\u65f6\u4e4b\u95f4\uff0c\u5c31\u53ef\u4ee5\u8bf4\u5f97\u6e05\u695a\u7684\u76ee\u4e0b\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\u662f\u5148\u627e\u56de\u9ec4\u5821\u4e3b\u6765\uff0c\u6551\u4eba\u8981\u7d27\u3002\u201d \u3000\u3000\u5f20\u73de\u77e5\u9053\u5929\u5c71\u5bd2\u94c1\u65e0\u76f8\u9488\u7684\u5389\u5bb3\uff0c\u5f53\u4e0b\u4e00\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u533a\u533a\u8fd9\u5c31\u53bb\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u8f6c\u8eab\u75be\u5954\u800c\u53bb\u3002 \u3000\u3000\u6021\u7ea2\u53f9\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u7237\uff0c\u4f60\u770b\u5230\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u770b\u5230\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u770b\u5230\u4ed6\u4eec\u5bf9\u5821\u4e3b\u7684\u62e5\u6234\uff0c\u90a3\u7edd\u4e0d\u662f\u6743\u529b\u548c\u91d1\u94b1\u6240\u80fd\u4f5c\u5230\uff0c\u90a3\u662f\u51fa\u81ea\u5185\u5fc3\u7684\u5fe0\u8bda\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u6021\u7ea2\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u7136\u8bf4\u660e\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u53ea\u6015\u5f88\u96be\u6709\u673a\u4f1a\u8d70\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u6211\u4e0d\u4f1a\u8d70\u7684\uff0c\u9ec4\u5821\u4e3b\u6ca1\u6709\u56de\u6765\u4ee5\u524d\uff0c\u5c31\u7b97\u6211\u6709\u6700\u597d\u7684\u673a\u4f1a\uff0c\u6211\u4e5f\u4e0d\u4f1a\u79bb\u5f00\u3002\u201d \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u5f20\u73de\u91cd\u53c8\u884c\u4e86\u56de\u6765\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u8d70\u5f97\u5fae\u5fae\u53f9\u606f\uff0c\u800c\u4e14\uff0c\u8138\u4e0a\u4e5f\u6cdb\u8d77\u4e86\u6d53\u91cd\u7684\u6740\u673a\u3002 \u3000\u3000\u663e\u7136\uff0c\u8fd9\u4e00\u6bb5\u65f6\u95f4\u4e4b\u4e2d\uff0c\u4ed6\u4e0d\u4f46\u8dd1\u5f88\u591a\u7684\u8def\uff0c\u800c\u4e14\uff0c\u4e5f\u6709\u4e86\u5e94\u53d8\u7684\u51c6\u5907\u3002 \u3000\u3000\u6021\u7ea2\u56de\u987e\u4e86\u5f20\u73de\u4e00\u773c\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u5f20\u73de\u5374\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u5728\u5821\u4e3b\u6ca1\u6709\u56de\u6765\u4e4b\u524d\uff0c\u9601\u4e0b\u6700\u597d\u662f\u4e0d\u8981\u79bb\u5f00\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u5728\u4e0b\u4e5f\u672a\u6253\u7b97\u8d70\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u53f6\u5144\uff0c\u5821\u4e3b\u5bf9\u4f60\u5f88\u5668\u91cd\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6697\u7b97\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u6025\u7740\u8981\u627e\u4ed6\u7684\u539f\u56e0\uff0c\u4ed6\u5982\u679c\u4e0d\u5668\u91cd\u6211\u53f6\u67d0\u4eba\uff0c\u5728\u4e0b\u5c31\u4e0d\u4f1a\u7ba1\u4ed6\u7684\u751f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4e0d\u7ba1\u4f60\u600e\u4e48\u60f3\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u6211\u60f3\u5148\u544a\u8bc9\u4f60\uff0c\u514d\u5f97\u5f15\u8d77\u4e0d\u5fc5\u8981\u7684\u8bef\u4f1a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u8fd9\u5927\u5385\u5916\u9762\uff0c\u6709\u56db\u4e2a\u4eba\u5728\u5b88\u7740\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u9053\uff1a\u201c\u5f20\u5144\uff0c\u5728\u4e0b\u5982\u82e5\u662f\u51c6\u5907\u79bb\u5f00\uff0c\u4e5f\u4e0d\u4f1a\u544a\u8bc9\u4f60\u8fd9\u4ef6\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6211\u4e0d\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u4e5f\u5c31\u7b97\u4e86\uff0c\u6211\u65e2\u7136\u77e5\u9053\u4e86\uff0c\u90a3\u5c31\u4e0d\u5f97\u4e0d\u9884\u9632\u4e00\u4e0b\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u773c\u4e0b\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u5c31\u662f\u5148\u8981\u60f3\u6cd5\u5b50\u627e\u56de\u5821\u4e3b\uff0c\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6211\u53ea\u80fd\u5c3d\u529b\u60f3\u6cd5\u5b50\uff0c\u5e0c\u671b\u80fd\u5c3d\u5feb\u7684\u627e\u5230\u4ed6\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u81f3\u591a\u4e00\u4e2a\u65f6\u8fb0\uff0c\u8d85\u8fc7\u4e86\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u4ed6\u56de\u6765\u4e86\uff0c\u5728\u4e0b\u4e5f\u65e0\u80fd\u4e3a\u529b\u3002\u201d \u3000\u3000\u5f20\u73de\u672a\u518d\u7b54\u8bdd\u3002 \u3000\u3000\u53f6\u957f\u9752\u82e6\u7b11\u4e00\u4e0b\uff0c\u7f13\u7f13\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5385\u4e2d\u7684\u4eba\uff0c\u4ece\u6765\u6ca1\u6709\u611f\u89c9\u5230\u65f6\u95f4\u662f\u5982\u6b64\u7684\u96be\u8fc7\u3002 \u3000\u3000\u534a\u4e2a\u65f6\u8fb0\u7684\u8fb0\u5149\uff0c\u6709\u5982\u8fc7\u4e86\u4e00\u5e74\u4f3c\u7684\u3002 \u3000\u3000\u5e78\u597d\uff0c\u9ec4\u5821\u4e3b\u53ca\u65f6\u8d76\u4e86\u56de\u6765 \u3000\u3000\u4ed6\u4ecd\u7136\u7a7f\u7740\u90a3\u4e00\u88ad\u9752\u8863\uff0c\u8138\u4e0a\u5e26\u6709\u5fae\u5fae\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u5f20\u73de\u5f88\u606d\u656c\u7684\u8fce\u4e86\u4e0a\u53bb\uff0c\u8eac\u8eab\u4e00\u793c\u3002 \u3000\u3000\u9752\u8863\u4eba\u6325\u7740\u624b\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff0c\u5982\u6b64\u6025\u8feb\u7684\u627e\u6211\u56de\u6765\uff1f\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u662f\u53f6\u5144\u8981\u627e\u5821\u4e3b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5df2\u7ecf\u7ad9\u4e86\u8d77\u6765\uff0c\u4e24\u9053\u76ee\u5149\uff0c\u4e0d\u505c\u5730\u5728\u9752\u8863\u4eba\u8eab\u4e0a\u6253\u91cf\u7740\u3002 \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u5144\u627e\u5728\u4e0b\uff0c\u4f3c\u4e4e\u662f\u5341\u5206\u6025\u8feb\uff0c\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u662f\u4e0d\u662f\u611f\u89c9\u5230\u6709\u4e9b\u4e0d\u8212\u670d\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u4f60\u53d7\u4e86\u6697\u7b97\uff0c\u81ea\u5df1\u4e00\u70b9\u4e5f\u4e0d\u89c9\u5f97\u5417\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5728\u4e0b\u5012\u662f\u6ca1\u6709\u611f\u89c9\uff0c\u4ec0\u4e48\u4eba\u6697\u7b97\u4e86\u6211\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\uff01\u6240\u4ee5\uff0c\u6211\u81ea\u5df1\u5341\u5206\u6e05\u695a\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u54e6\uff01\u4f46\u4e0d\u77e5\u53f6\u5144\u7528\u7684\u4ec0\u4e48\u624b\u6bb5\u5bf9\u4ed8\u5728\u4e0b\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5929\u5c71\u5bd2\u94c1\u65e0\u76f8\u9488\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u65e0\u76f8\u9488\uff0c\u5012\u662f\u542c\u4eba\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u662f\u5929\u5c71\u5343\u5e74\u5bd2\u94c1\u7422\u78e8\u800c\u6210\uff0c\u7ec6\u5982\u725b\u6bdb\uff0c\u4e0d\u8bba\u591a\u4e48\u597d\u7684\u5185\u529b\uff0c\u4e5f\u65e0\u6cd5\u6297\u62d2\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u7528\u4e00\u679a\u5bd2\u94c1\u65e0\u76f8\u9488\uff0c\u523a\u5165\u5821\u4e3b\u7684\u8eab\u4e0a\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u662f\u771f\u7684\uff0c\u90a3\u9488\u592a\u8fc7\u7ec6\u5c0f\uff0c\u523a\u5165\u4eba\u8eab\u4e4b\u540e\uff0c\u968f\u7740\u8840\u6db2\u8fd0\u884c\uff0c\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u968f\u7740\u884c\u8840\u523a\u5165\u5fc3\u810f\u4e4b\u4e0a\uff0c\u90a3\u65f6\uff0c\u5c31\u7b97\u534e\u9640\u91cd\u751f\uff0c\u4e5f\u65e0\u6cd5\u6551\u6cbb\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u4e0d\u662f\u8fd9\u4e2a\uff1f\u201d \u3000\u3000\u4ed6\u7a81\u7136\u4f38\u51fa\u4e86\u624b\u638c\u3002 \u3000\u3000\u53f6\u957f\u9752\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u9752\u8863\u4eba\u7684\u624b\u5fc3\u4e0a\uff0c\u6258\u7740\u4e00\u679a\u7ec6\u5982\u725b\u6bdb\u7684\u7ec6\u5c0f\u5c16\u9488\uff0c\u4e0d\u7981\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u65e9\u5df2\u77e5\u9053\u6211\u6697\u7b97\u4f60\u4e86\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\uff01\u5e78\u597d\uff0c\u6211\u6ca1\u6709\u53d7\u4f24\uff0c\u4f46\u53f6\u5144\u8fd9\u7b49\u5173\u5fc3\u4e4b\u60c5\uff0c\u5728\u4e0b\u4e00\u6837\u611f\u6fc0\u2026\u2026\u201d \u3000\u3000\u6325\u6325\u624b\uff0c\u793a\u610f\u5f20\u73de\u7b49\u79bb\u53bb\u3002 \u3000\u3000\u6021\u7ea2\u4e5f\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u9752\u8863\u4eba\u5374\u7b11\u4e00\u7b11\uff0c\u53eb\u4f4f\u4e86\u6021\u7ea2\uff0c\u9053\uff1a\u201c\u53bb\uff0c\u505a\u4e00\u4e9b\u70b9\u5fc3\u9001\u6765\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u7237\uff01\u60f3\u5403\u70b9\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u968f\u4fbf\uff0c\u522b\u5fd8\u4e86\u4f60\u6700\u62ff\u624b\u7684\u51b0\u7cd6\u83b2\u85d5\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u4e00\u7b11\u800c\u53bb\uff0c\u5927\u5385\u4e2d\u53ea\u4f59\u4e0b\u4e86\u53f6\u957f\u9752\u548c\u9752\u8863\u4eba\u3002 \u3000\u3000\u53f6\u957f\u9752\u795e\u60c5\u5f88\u5c34\u5c2c\uff0c\u4e03\u5206\u60ca\u6115\u6df7\u5408\u7740\u4e09\u5206\u60ed\u6127\uff0c\u8f7b\u8f7b\u5618\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5821\u4e3b\u600e\u80fd\u907f\u5f00\u8fd9\u65e0\u76f8\u9488\u7684\u4e00\u51fb\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4e03\u5206\u8fd0\u6c14\uff0c\u52a0\u4e0a\u4e09\u5206\u673a\u667a\uff0c\u4f7f\u6211\u907f\u5f00\u4e86\u8fd9\u4e00\u51fb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6211\u77a7\u5230\u4e86\u53f6\u5144\u624b\u6307\u8f7b\u5f39\u65f6\uff0c\u7acb\u523b\u5fae\u4e00\u4fa7\u8eab\uff0c\u4f46\u4ecd\u7136\u672a\u80fd\u907f\u5f00\u53f6\u5144\u8fd9\u4e00\u51fb\uff0c\u5e78\u597d\u6211\u7684\u8863\u888b\u4e4b\u4e2d\u6709\u4e00\u5757\u6563\u788e\u7684\u94f6\u5b50\uff0c\u662f\u8fd9\u5757\u94f6\u5b50\u6551\u4e86\u6211\u7684\u547d\uff0c\u5bd2\u94c1\u65e0\u76f8\u9488\uff0c\u76f8\u5f53\u7684\u5c16\u5389\uff0c\u6df1\u5165\u94f6\u5b50\uff0c\u5982\u82e5\u8fd9\u5757\u94f6\u5b50\u8584\u4e00\u4e9b\uff0c\u53ea\u6015\u6211\u8fd8\u662f\u8981\u53d7\u5230\u4f24\u5bb3\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1b\u201c\u5821\u4e3b\u77e5\u9053\u4e86\uff0c\u4f46\u5374\u6ca1\u6709\u95ee\u8fc7\u5728\u4e0b\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u6ca1\u6709\u4f24\u5230\u6211\uff0c\u800c\u4e14\uff0c\u8ba9\u6211\u89c1\u8bc6\u5230\u4e86\u5bd2\u94c1\u65e0\u76f8\u9488\u7684\u5389\u5bb3\uff0c\u5bf9\u6211\u5728\u6c5f\u6e56\u4e0a\u7684\u884c\u52a8\u5e2e\u52a9\u4e0d\u5c0f\uff0c\u6b64\u4e8b\u5df2\u6210\u8fc7\u53bb\uff0c\u4e0d\u8c08\u4e5f\u7f62\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u53f6\u5144\u6025\u6025\u627e\u6211\u56de\u6765\uff0c\u60f3\u6765\uff0c\u8fd8\u6709\u522b\u7684\u4e8b\u5427\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u957f\u9752\u95f7\u5728\u5fc3\u4e2d\u7684\u4e8b\u60c5\uff0c\u76f4\u8a00\u51fa\u53e3\uff0c\u5e0c\u671b\u5821\u4e3b\u4e0d\u8981\u89c1\u602a\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u597d\u4f60\u53ea\u7ba1\u8bf4\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\u544a\u8bc9\u4e86\u6211\u4e00\u4e9b\u4e8b\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u4e00\u76f4\u6ca1\u6709\u628a\u5979\u5f53\u5916\u4eba\u770b\uff0c\u6709\u4e9b\u4e8b\uff0c\u4e5f\u6ca1\u6709\u9a97\u5979\uff0c\u53ea\u6050\u5979\u542c\u5230\u7684\uff0c\u90fd\u662f\u4e9b\u4e00\u9cde\u534a\u722a\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u5bf9\uff01\u5979\u77e5\u9053\u7684\u662f\u4e0d\u5b8c\u5168\uff0c\u6240\u4ee5\uff0c\u957f\u9752\u6597\u80c6\u8981\u5411\u5821\u4e3b\u6c42\u8bc1\u4e00\u4e0b\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u70b9\u70b9\u5934\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\u8bf4\u5230\u4e10\u5e2e\u548c\u5c11\u6797\u5bfa\u7684\u4e8b\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u8981\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0e\u53ea\u6015\u90fd\u5f88\u96be\u548c\u8fd9\u4e00\u5e2e\u3001\u4e00\u6d3e\uff0c\u8131\u79bb\u5173\u7cfb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\u9886\u5bfc\u7684\u7ec4\u7ec7\uff0c\u548c\u4ed6\u4eec\u6709\u51b2\u7a81\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f60\u5bf9\u5c11\u6797\u3001\u4e10\u5e2e\u8fd9\u4e24\u5927\u5e2e\u6d3e\uff0c\u77e5\u9053\u591a\u5c11\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u662f\u6b66\u6797\u4e2d\u7684\u6cf0\u5c71\u3001\u5317\u6597\uff0c\u4e10\u5e2e\u5fe0\u4e49\u76f8\u4f20\uff0c\u5f88\u53d7\u6b66\u6797\u540c\u9053\u4eec\u656c\u91cd\u7684\u4e24\u4e2a\u95e8\u6237\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e00\u6d3e\u4e00\u5e2e\uff0c\u90fd\u662f\u53d7\u4eba\u656c\u91cd\u7684\u6b66\u6797\u95e8\u6237\uff0c\u53ef\u662f\uff0c\u4ed6\u4eec\u592a\u5e9e\u5927\u4e86\uff0c\u4eba\u6570\u592a\u591a\uff0c\u5c31\u96be\u514d\u9f99\u86c7\u6df7\u6742\uff0c\u826f\u83a0\u4e0d\u9f50\uff0c\u542c\u8bf4\uff0c\u8fd9\u4e00\u5e2e\u4e00\u6d3e\u4e2d\u6709\u9ad8\u4eba\uff0c\u6df7\u5165\u4e86\u957f\u6625\u5bab\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u7a7a\u7a74\u81ea\u7136\u4e0d\u4f1a\u6765\u98ce\uff0c\u53ea\u4e0d\u8fc7\uff0c\u6211\u4eec\u8fd8\u6ca1\u627e\u5230\u786e\u5207\u7684\u8bc1\u636e\uff0c\u6240\u4ee5\uff0c\u6211\u8981\u4ed6\u4eec\u591a\u5fcd\u8010\uff0c\u4e0d\u53ef\u8f7b\u4e3e\u3001\u5984\u52a8\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u72c2\u9f99\u3001\u98de\u9e70\u3001\u4e94\u9b3c\u8239\uff0c\u5821\u4e3b\u4e5f\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u65b0\u8fd1\u5d1b\u8d77\u7684\u4e09\u4e2a\u795e\u79d8\u4eba\u7269\uff0c\u53f6\u5144\uff0c\u4e5f\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u542c\u4eba\u8bf4\u8fc7\uff0c\u800c\u4e14\uff0c\u4e5f\u542c\u8fc7\u9634\u9633\u5821\u8fd9\u4e2a\u7ec4\u7ec7\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5728\u4fa0\u4e49\u9053\u4e0a\u7684\u4eba\uff0c\u5bf9\u9634\u9633\u5821\uff0c\u6709\u4ec0\u4e48\u770b\u6cd5\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1b\u201c\u6076\u540d\u751a\u5927\uff0c\u4f46\u5374\u6ca1\u6709\u4ec0\u4e48\u4e8b\u8ff9\u4f20\u8bf4\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6709\uff01\u50cf\u63b3\u63a0\u4f60\u53f6\u5144\uff0c\u5c31\u662f\u4e8b\u8ff9\u4e4b\u4e00\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u82e5\u6709\u6240\u609f\u5730\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u4e0d\u77e5\u4e94\u5927\u5251\u4f7f\u7684\u7a7a\u4f4d\uff0c\u662f\u5426\u8fd8\u6709\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u9053\uff1a\u201c\u4e00\u76f4\u5728\u7b49\u5f85\u7740\u53f6\u5144\u7684\u51b3\u5b9a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5982\u662f\u5821\u4e3b\u89c9\u5f97\u5728\u4e0b\uff0c\u8fd8\u80dc\u4efb\u5251\u4f7f\u4e4b\u804c\uff0c\u5144\u5f1f\u5012\u662f\u60f3\u5c31\u4efb\u5251\u4f7f\u4e4b\u4f4d\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u597d\uff01\u597d\u6781\u4e86\uff01\u6211\u7acb\u523b\u5429\u5490\u4ed6\u4eec\u51c6\u5907\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u51c6\u5907\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u51c6\u5907\u4f60\u7684\u5c31\u4efb\u5251\u4f7f\u5927\u5178\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u957f\u9752\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u5f88\u62b1\u6b49\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1b\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4eca\u591c\u3002\u4f60\u5c31\u4f4f\u5251\u4f7f\u4e4b\u4f4d\uff0c\u4f46\u53e6\u5916\u56db\u4f4d\u5251\u4f7f\uff0c\u53ea\u6015\u6709\u4e24\u4f4d\u4e0d\u80fd\u5230\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4ed6\u4eec\u5f88\u5fd9\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u957f\u9752\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u4eec\u90fd\u5728\u767e\u91cc\u4ee5\u5916\u3002\u201d\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f55\u5144\u5462\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f55\u5bd2\u8863\u4f1a\u8d76\u6765\uff0c\u4e00\u5219\u662f\u56e0\u4e3a\u4ed6\u5bf9\u53f6\u5144\u6709\u7740\u5f88\u6df1\u539a\u7684\u6000\u5ff5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u6ca1\u6709\u7b54\u8bdd\u3002 \u3000\u3000\u9752\u8863\u4eba\u7a81\u7136\u4e3e\u624b\u4e92\u51fb\u4e24\u638c\u3002\u5f20\u73de\u5e94\u58f0\u800c\u6765\u3002 \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u98de\u9e3d\u5feb\u9a6c\uff0c\u4f20\u4ee4\u4e0b\u53bb\uff0c\u5c31\u8bf4\uff0c\u628a\u4e8b\u60c5\u5ef6\u540e\u4e24\u5929\u3002\u201d \u3000\u3000\u5f20\u73de\u5e94\u4e86\u4e00\u58f0\uff0c\u8f6c\u8eab\u6b32\u53bb\u3002 \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6162\u4e00\u70b9\uff0c\u8981\u4f55\u5bd2\u8863\u5251\u4f7f\u65e0\u8bba\u5982\u4f55\uff0c\u5728\u521d\u66f4\u4e4b\u524d\u8d76\u56de\u6765\u3002\u201d \u3000\u3000\u5f20\u73de\u9053\uff1a\u201c\u5c5e\u4e0b\u9075\u547d\u3002\u201d \u3000\u3000\u6021\u7ea2\u9001\u4e0a\u4e86\u9999\u8317\u7ec6\u70b9\u3002 \u3000\u3000\u4f46\u5979\u5f88\u8bc6\u8da3\uff0c\u9001\u4e0a\u70b9\u5fc3\uff0c\u7acb\u523b\u9000\u4e0b\u53bb\u3002 \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u53f6\u5144\u4e00\u8bfa\u5343\u91d1\uff0c\u4f60\u5fc3\u4e2d\uff0c\u5fc5\u7136\u8fd8\u6709\u5f88\u591a\u7684\u7591\u95ee\uff0c\u73b0\u5728\uff0c\u4f60\u53ef\u4ee5\u95ee\u95ee\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5728\u4e0b\u60f3\u5148\u786e\u5b9a\u6211\u5bf9\u4f60\u7684\u79f0\u547c\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u968f\u4fbf\u5427\uff01\u53eb\u6211\u5821\u4e3b\u4e5f\u597d\uff0c\u79f0\u6211\u9ec4\u5144\u4e5f\u597d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\uff01\u5728\u4e0b\u65e2\u7136\u786e\u5b9a\u4e86\u8981\u5c31\u4efb\u5251\u4f7f\u4e4b\u4f4d\uff0c\u5e0c\u671b\u80fd\u80af\u5b9a\u5728\u4e0b\u548c\u9ec4\u5144\u4e4b\u95f4\u7684\u8eab\u4efd\u3001\u5173\u7cfb\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u597d\u5427\uff01\u90a3\u5c31\u516c\u4e8b\u4e0a\u79f0\u6211\u5821\u4e3b\uff0e\u79c1\u5e95\u4e0b\u53eb\u6211\u9ec4\u5144\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u54b1\u4eec\u8fd9\u4e2a\u7ec4\u7ec7\uff0c\u7a76\u7adf\u662f\u600e\u6837\u7684\u4e00\u4e2a\u7ec4\u7ec7\uff0c\u6211\u4eec\u8981\u505a\u4e9b\u4ec0\u4e48\u4e8b\uff1f\u6709\u4e9b\u4ec0\u4e48\u7279\u522b\u4e25\u5389\u7684\u6212\u5f8b\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u9996\u5148\u6211\u8981\u8bf4\u660e\u767d\u7684\u662f\uff0c\u54b1\u4eec\u8fd9\u4e2a\u7ec4\u7ec7\u4e2d\u9f99\u86c7\u6df7\u6742\uff0c\u9ed1\u9053\u4e2d\u4eba\u7269\u5360\u7684\u6bd4\u7387\u5f88\u9ad8\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u884c\u4e8b\u7684\u65b9\u6cd5\uff0c\u4e5f\u9887\u4f3c\u9ed1\u9053\u4e2d\u4eba\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u5bf9\uff01\u8fd9\u65b9\u6cd5\u4e0d\u4f46\u6709\u6548\uff0c\u800c\u4e14\u5f88\u8fc5\u901f\uff0c\u6bd4\u8d77\u4fa0\u4e49\u9053\u4e0a\u4eba\u7269\uff0c\u90a3\u5927\u96e8\u672a\u5230\u98ce\u5148\u81f3\u7684\u4f5c\u6cd5\u6709\u7528\u591a\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6211\u867d\u7136\u7528\u4e86\u4e0d\u5c11\u7eff\u6797\u9053\u4e0a\u7684\u4eba\u7269\uff0c\u4f46\u6211\u4eec\u7684\u4f5c\u4e3a\uff0c\u5374\u662f\u4ef0\u4e0d\u6127\u5929\uff0c\u4fef\u4e0d\u600d\u5730\uff0c\u5982\u679c\u6211\u4eec\u6709\u53ef\u8bae\u4e4b\u5904\uff0c\u90a3\u5c31\u662f\u6211\u4eec\u7684\u65b9\u6cd5\uff0c\u5728\u5f88\u591a\u6b63\u6d3e\u4eba\u58eb\u7684\u773c\u4e2d\uff0c\u6709\u4e9b\u4e0d\u767b\u5927\u96c5\u4e4b\u5802\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9ec4\u5144\uff0c\u79c1\u4eba\u8bf7\u6559\uff0c\u6211\u4eec\u5982\u6b64\u5306\u5fd9\uff0c\u5728\u5bf9\u4ed8\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u76ee\u4e0b\uff0c\u6211\u4e5f\u65e0\u6cd5\u80af\u5b9a\u544a\u8bc9\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u56e0\u4e3a\uff0c\u6211\u4eec\u53ea\u662f\u53d1\u89c9\u4e86\u4e00\u4e2a\u95ee\u9898\uff0c\u4e00\u4e2a\u5927\u9634\u8c0b\u6b63\u5728\u6c5f\u6e56\u4e0a\u8fdb\u884c\u4e2d\uff0c\u6211\u4eec\u5728\u8ffd\u67e5\u8fd9\u4ef6\u4e8b\uff0c\u4f3a\u673a\u7834\u574f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9ec4\u5144\uff0c\u96be\u9053\u5c31\u6ca1\u6709\u4e00\u70b9\u7ebf\u7d22\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6709\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u7684\u7ebf\u7d22\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u53f6\u5144\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u5b9e\u5728\u4e0d\u4fbf\u8bf4\u51fa\u6765\uff0c\u56e0\u4e3a\uff0c\u6211\u4eec\u8fd8\u65e0\u6cd5\u80af\u5b9a\u3002\u6211\u4eec\u4e0d\u80fd\u8f7b\u4fae\u4e00\u4e2a\u4eba\u7684\u58f0\u8a89\uff0c\u4f55\u51b5\uff0c\u6c5f\u6e56\u4e0a\u7684\u9b3c\u872e\u4f0e\u4fe9\uff0c\u5b9e\u5728\u5f88\u6015\u4eba\uff0c\u4e07\u4e00\u8981\u4f24\u4e86\u522b\u4eba\uff0c\u4e0d\u4f46\u8981\u5f15\u8d77\u4e00\u573a\u4e0d\u5fc5\u8981\u7684\u98ce\u6ce2\uff0c\u800c\u4e14\uff0c\u8fd8\u4f1a\u4f24\u5bb3\u522b\u4eba\u7684\u540d\u8282\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9ec4\u5144\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u9752\u8863\u4eba\u63a5\u5230\uff1a\u201c\u884c\u52a8\uff0c\u8ffd\u67e5\u5230\u6c34\u843d\u77f3\u51fa\u4e3a\u6b62\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u672a\u518d\u591a\u95ee\u3002 \u3000\u3000\u5c31\u4efb\u5251\u4f7f\u7684\u5927\u5178\u5e84\u4e25\uff0c\u4f46\u53c2\u52a0\u7684\u4eba\u6570\u5e76\u4e0d\u591a\u3002 \u3000\u3000\u5178\u793c\u8fdb\u884c\u7684\u5f88\u5feb\u901f\uff0c\u524d\u540e\u53ea\u4e0d\u8fc7\u4e00\u523b\u529f\u592b\uff0c\u53f6\u957f\u9752\u5f88\u7559\u5fc3\u53c2\u4e0e\u7684\u4eba\uff0c\u9664\u4e86\u9ec4\u5821\u4e3b\uff0c\u4f55\u5bd2\u8863\u4e4b\u5916\uff0c\u8fd8\u6709\u4e24\u4e2a\u5973\u7684\uff0c\u4e03\u516b\u4e2a\u4e2d\u5e74\u5927\u6c49\uff0c\u548c\u4e00\u4e2a\u957f\u886b\u8001\u4eba\u3002 \u3000\u3000\u53f6\u957f\u9752\u5728\u4e00\u4e2a\u706f\u706b\u8f89\u714c\u7684\u6728\u53f0\u4e0a\uff0c\u5ba3\u8bfb\u4e86\u8a93\u8a00\u3002 \u3000\u3000\u5f3a\u70c8\u7684\u706f\u5149\uff0c\u4f7f\u5f97\u53f6\u548c\u957f\u9752\u5f88\u96be\u770b\u6e05\u695a\u53f0\u4e0b\u4eba\u7684\u9762\u8c8c\uff0c\u4f46\u522b\u4eba\uff0c\u5374\u628a\u4ed6\u770b\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u53f6\u957f\u9752\u6b65\u4e0b\u6728\u53f0\u65f6\uff0c\u5385\u4e2d\u4eba\u5df2\u7136\u5927\u90e8\u6563\u53bb\u3002 \u3000\u3000\u7559\u5728\u5927\u5385\u4e2d\u7684\u53ea\u6709\u9ec4\u5821\u4e3b\uff0c\u4f55\u5bd2\u8863\uff0c\u9752\u8863\u8001\u4eba\uff0c\u53ca\u4e00\u4e2a\u4e8c\u5341\u4e03\u516b\u5c81\u7684\u5987\u4eba\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4f38\u51fa\u624b\u53bb\uff0c\u7d27\u63e1\u7740\u53f6\u957f\u9752\u7684\u624b\uff0c\u65e0\u9650\u70ed\u60c5\u5730\u8bf4\u9053\uff1a\u201c\u53f6\u5144\uff0c\u9ec4\u5821\u4e3b\u7ec8\u4e8e\u8bf4\u670d\u4e86\u53f6\u5144\uff0c\u4f7f\u4f60\u5c31\u4e86\u5251\u4f7f\u4e4b\u4f4d\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u5374\u7ad9\u8d77\u8eab\u5b50\u9053\uff1a\u201c\u8d70\uff01\u96c5\u5ba4\u4e2d\uff0c\u6021\u7ea2\u4eb2\u624b\u4e3a\u53f6\u5251\u4f7f\u51c6\u5907\u4e86\u4e00\u5e2d\u9152\u5e2d\uff0c\u54b1\u4eec\u8fb9\u5403\u8fb9\u8c08\u5427\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u539f\u60f3\u5c31\u4efb\u5251\u4f7f\u4e4b\u540e\uff0c\u53ef\u4ee5\u4e86\u89e3\u4e0d\u5c11\u7684\u9690\u79d8\uff0c\u5374\u6ca1\u6709\u60f3\u5230\uff0c\u5c31\u4efb\u5251\u4f7f\u4e4b\u540e\uff0c\u5fc3\u4e2d\u7adf\u7559\u4e0b\u4e86\u66f4\u591a\u7684\u7591\u95ee\u3002 \u3000\u3000\u4e00\u95f4\u5c0f\u5ba4\uff0c\u4e00\u684c\u9152\u5e2d\uff0c\u5f88\u7cbe\u81f4\u7684\u83dc\u80b4\uff0c\u5f88\u9999\u9187\u7684\u9152\u3002 \u3000\u3000\u4f46\u4e00\u684c\u53ea\u6709\u51e0\u4e2a\u4eba\uff0c\u9ec4\u5821\u4e3b\uff0c\u4f55\u5bd2\u8863\uff0c\u9752\u8863\u8001\u4eba\uff0c\u548c\u90a3\u4e2a\u7a7f\u7384\u8863\u7684\u5987\u4eba\u3002 \u3000\u3000\u53f6\u957f\u9752\u867d\u548c\u9ec4\u5821\u4e3b\u8c08\u4e86\u4e0d\u5c11\u7684\u8bdd\uff0c\u4f46\u4ed6\u5bf9\u9ec4\u5821\u4e3b\u5e76\u975e\u771f\u6b63\u7684\u8ba4\u8bc6\uff0c\u4ed6\u6000\u7591\u81ea\u5df1\u6240\u770b\u5230\u7684\uff0c\u53ea\u662f\u4e00\u5f20\u6234\u7740\u4eba\u76ae\u9762\u5177\uff0c\u6216\u662f\u7ecf\u8fc7\u5de7\u5999\u4f2a\u88c5\u7684\u8138\u3002 \u3000\u3000\u8fd9\u91cc\u7684\u4e00\u5207\uff0c\u90fd\u592a\u8fc7\u795e\u79d8\u3002 \u3000\u3000\u53f6\u957f\u9752\u5185\u5fc3\u4e2d\u4e0d\u592a\u559c\u6b22\u795e\u79d8\uff0c\u4e00\u4e2a\u5802\u5802\u6b63\u6b63\u7684\u4eba\uff0c\u90fd\u4e0d\u592a\u559c\u6b22\u795e\u79d8\u3002 \u3000\u3000\u795e\u79d8\u6709\u65f6\u8fd1\u4e4e\u8be1\u5f02\u3002 \u3000\u3000\u8fd9\u95f4\u96c5\u5ba4\u4e2d\uff0c\u53f6\u957f\u9752\u771f\u6b63\u8ba4\u8bc6\u7684\u4eba\uff0c\u53ea\u6709\u4e24\u4e2a\u3002 \u3000\u3000\u4e00\u4e2a\u662f\u6021\u7ea2\uff0c\u4e00\u4e2a\u662f\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4ecd\u7136\u662f\u4e00\u88ad\u767d\u886b\uff0c\u6f47\u6d12\uff0c\u82f1\u4fca\u3002 \u3000\u3000\u8fd9\u5df2\u662f\u5165\u591c\u65f6\u5206\uff0c\u5ba4\u5185\u7684\u706f\u5149\u5f88\u67d4\u548c\u3002 \u3000\u3000\u53f6\u957f\u9752\u56de\u987e\u4e86\u90a3\u9752\u8863\u8001\u4eba\u4e00\u773c\u4e4b\u540e\uff0c\u7a81\u7136\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u90a3\u662f\u4e00\u5f20\u4e0d\u5f88\u597d\u770b\u7684\u8138\uff0c\u773c\u4e0a\u6709\u4e94\u4e2a\u94dc\u94b1\u822c\u7684\u5927\u9ebb\u5b50\u3002 \u3000\u3000\u90a3\u662f\u5f20\u6807\u5fd7\u660e\u663e\u7684\u8138\uff0c\u4e5f\u662f\u4ee3\u8868\u7740\u7f6a\u6076\u7684\u8138\uff0c\u4e8b\u5b9e\u4e0a\u53f6\u957f\u9752\u5e76\u6ca1\u6709\u89c1\u8fc7\u8fd9\u4e2a\u4eba\uff0c\u4f46\u4ed6\u542c\u8fc7\u8fd9\u5f20\u8138\u548c\u8fd9\u4e2a\u4eba\u7684\u540d\u5b57\u3002\u90a3\u662f\u6c5f\u6e56\u4fa0\u4e49\u9053\u4e0a\uff0c\u4eba\u4eba\u90fd\u60f3\u535a\u6740\u7684\u5927\u51f6\u624b\uff0c\u9ebb\u9762\u8840\u624b\u5c60\u65e0\u65b9\u3002 \u3000\u3000\u53f6\u957f\u9752\u60ca\u6115\u7684\u795e\u8272\uff0c\u5df2\u8868\u793a\u4ed6\u5185\u5fc3\u7684\u4e0d\u6ee1\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u662f\u4e0d\u662f\u770b\u5230\u4e86\u6211\u8fd9\u5f20\u8138\uff0c\u5fc3\u91cc\u6709\u4e9b\u4e0d\u8212\u670d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u662f\u9ebb\u9762\u8840\u624b\u5c60\u65e0\u65b9\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6b63\u662f\u533a\u533a\uff0c\u6211\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u8a89\u4e0d\u592a\u597d\uff0c\u5982\u82e5\u4e0d\u662f\u5821\u4e3b\u548c\u4f55\u8001\u5f1f\u5728\u5ea7\uff0c\u53f6\u8001\u5f1f\u53ef\u80fd\u5df2\u62d4\u5251\u76f8\u5411\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u8fd9\u4e2a\u7ec4\u7ec7\u4e2d\u4eba\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5bf9\uff01\u4e94\u5927\u5251\u4f7f\u4e4b\u4e00\uff0c\u53f6\u8001\u5f1f\u6709\u4e9b\u60f3\u4e0d\u5230\u5427\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u5b8c\u5168\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\u3002\u4eba\u4e0d\u53ef\u8c8c\u76f8\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u5230\uff1a\u201c\u4e0d\u662f\u4ed6\u7684\u8c8c\uff0c\u800c\u662f\u4ed6\u7684\u51f6\u540d\uff0c\u6c5f\u6e56\u4e0a\uff0c\u7b2c\u4e00\u53f7\u51b7\u8840\u6740\u624b\u3002\u201d \u3000\u3000\u4f55\u7a7f\u8863\u5fae\u5fae\u4e00\u7b11\uff0c\u672a\u518d\u591a\u53e3\u3002 \u3000\u3000\u9ec4\u5821\u4e3b\uff0c\u548c\u90a3\u7384\u8863\u5987\u4eba\uff0c\u90fd\u5750\u5728\u4e00\u4fa7\uff0c\u5fae\u7b11\u4e0d\u8a00\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u5145\u6ee1\u4e86\u6c14\u6012\uff0c\u957f\u957f\u5618\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u5c60\u65e0\u65b9\uff0c\u54b1\u4eec\u5b9e\u5728\u4e0d\u5e94\u8be5\u5728\u8fd9\u4e2a\u573a\u5408\u89c1\u9762\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u597d\u4fee\u517b\uff0c\u548c\u4ed6\u76db\u4f20\u4e8e\u6c5f\u6e56\u4e0a\u7684\u51f6\u540d\u5b8c\u5168\u4e0d\u540c\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u7aef\u8d77\u9762\u524d\u7684\u9152\u676f\u3002\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u6211\u5148\u656c\u4f60\u4e00\u676f\uff0c\u54b1\u4eec\u518d\u6162\u6162\u7684\u8c08\u3002\u201d \u3000\u3000\u4e00\u53e3\u559d\u5e72\u4e86\u676f\u4e2d\u4e4b\u9152\u3002 \u3000\u3000\u53f6\u957f\u9752\u76b1\u76b1\u7709\u5934\u3002\u4e5f\u53ea\u597d\u5e72\u676f\u9053\uff1a\u201c\u5c60\u65e0\u65b9\uff0c\u4f60\u7a76\u7adf\u6740\u4e86\u591a\u5c11\u4eba\uff0c\u4f60\u5fc3\u4e2d\u662f\u5426\u6709\u4e2a\u6570\u76ee\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e09\u767e\u4e8c\u5341\u4e5d\u4e2a\uff0c\u4e0d\u7b97\u592a\u591a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6709\u4e9b\u4eba\uff0c\u6740\u4eba\u6bd4\u4f60\u591a\uff0c\u4f46\u5374\u6ca1\u6709\u4eba\u89c9\u5f97\u4ed6\u662f\u51f6\u624b\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u4ed6\u4eec\u6740\u7684\u4eba\uff0c\u90fd\u662f\u8be5\u6740\u7684\u4eba\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u70b9\u70b9\u5934\uff0e\u9053\uff1a\u201c\u53f6\u8001\u5f1f\u8bf4\u7684\u662f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5f88\u60f3\u53d1\u4f5c\uff0c\u4ed6\u89c9\u5f97\u628a\u81ea\u5df1\u548c\u5c60\u65e0\u65b9\u8fd9\u4e2a\u4eba\u805a\u96c6\u5728\u4e00\u8d77\uff0c\u5bf9\u4ed6\u662f\u4e00\u79cd\u7f9e\u8fb1\u3002\u4f46\u5c60\u65e0\u529b\u4ecd\u662f\u4e0d\u7ed9\u4ed6\u53d1\u4f5c\u7684\u673a\u4f1a\uff0c\u8138\u4e0a\u4e00\u76f4\u5e26\u7740\u7b11\u5bb9\u3002 \u3000\u3000\u867d\u7136\u662f\u521a\u521a\u7acb\u8fc7\u91cd\u8a93\uff0c\u5c31\u4efb\u4e86\u5251\u4f7f\u4e4b\u4f4d\u3002\u4f46\u53f6\u957f\u9752\u4ecd\u65e0\u6cd5\u6309\u4e0b\u9010\u6e10\u9ad8\u5347\u7684\u6012\u706b\uff0c\u8fde\u559d\u897f\u676f\u9152\uff0c\u56de\u987e\u7740\u9ec4\u5821\u4e3b\uff0c\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u5982\u82e5\u5728\u4e0b\u8981\u8f9e\u53bb\u8fd9\u5251\u4f7f\u4e4b\u4f4d\u4e0d\u77e5\u5e94\u8be5\u5982\u4f55\uff1f\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u9053\uff1a\u201c\u53ea\u8981\u53f6\u5144\u80fd\u8bf4\u51fa\u7406\u7531\uff0c\u7acb\u5373\u53ef\u4ee5\u79bb\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u56de\u671b\u4e86\u5c60\u65e0\u65b9\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u662f\u6c5f\u6e56\u4e0a\u7684\u6709\u540d\u6740\u624b\u3002\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u4ed6\u6740\u7684\u4eba\u4e5f\u4e0d\u7b97\u592a\u591a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u6709\u4e9b\u4eba\u6740\u4e0d\u5f97\uff0c\u6740\u4e00\u4e2a\u5c31\u4f1a\u611f\u89c9\u5230\u5f88\u96be\u8fc7\uff0c\u6709\u4e9b\u4eba\u6740\u7684\u6108\u591a\u6108\u597d\uff0c\u5c31\u50cf\u9ebb\u9762\u8840\u624b\u8fd9\u79cd\u4eba\u3002\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u5251\u4f7f\u53ef\u662f\u770b\u5230\u4ed6\u6740\u4eba\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u6ca1\u6709\u770b\u8fc7\uff0c\u4f46\u6211\u542c\u5230\u8fc7\u4ed6\u7684\u51f6\u540d\u3002\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\uff0c\u5185\u60c5\u66f2\u6298\uff0c\u9152\u5e2d\u4e0a\u6ca1\u6709\u5916\u4eba\uff0c\u53f6\u5144\u4e3a\u4ec0\u4e48\u4e0d\u591a\u548c\u5c60\u5251\u4f7f\u8c08\u8c08\u5462\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u6014\u4e86\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u55dc\u6740\u4e4b\u4eba\uff0c\u5927\u90fd\u51f6\u6b8b\u6210\u6027\uff0c\u6700\u662f\u53d7\u4e0d\u5f97\u4eba\u7684\u65a5\u8d23\uff0c\u8fd9\u5c60\u65e0\u65b9\u600e\u4f1a\u6709\u8fd9\u6837\u597d\u7684\u4fee\u517b\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u6c14\u4e2d\u4ecd\u7136\u5e26\u7740\u51b7\u5389\uff0c\u9053\uff1a\u201c\u5c60\u5144\uff0c\u8fd9\u51e0\u5e74\u5f88\u5c11\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u554a\uff01\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u54ea\u91cc\uff0c\u5176\u5b9e\uff0c\u6211\u8fd8\u662f\u5e38\u5728\u6c5f\u6e56\u4e0a\u51fa\u73b0\u3002\u4e0d\u8fc7\uff0c\u6709\u65f6\u4e3a\u4e86\u907f\u514d\u4e9b\u610f\u5916\u7684\u9ebb\u70e6\uff0c\u6211\u5c31\u628a\u81ea\u5df1\u6539\u626e\u4e86\u4e00\u4e0b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u8bf4\u5462\uff1f\u6709\u5f88\u591a\u4eba\uff0c\u8981\u627e\u4f60\u5c60\u5144\uff0c\u5374\u662f\u5f88\u96be\u89c1\u5230\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4f60\u53f6\u8001\u5f1f\uff0c\u4e5f\u662f\u5176\u4e2d\u4e4b\u4e00\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5bf9\uff01\u6240\u4ee5\u6211\u611f\u89c9\u54b1\u4eec\u89c1\u7684\u5730\u65b9\u5f88\u4e0d\u5bf9\uff0c\u5982\u679c\u6362\u4e00\u4e2a\u5730\u65b9\uff0c\u6211\u60f3\uff0c\u54b1\u4eec\u65e9\u5c31\u52a8\u4e0a\u624b\u4e86\uff0c\u201d \u3000\u3000\u5c60\u65e0\u65b9\u81ea\u659f\u81ea\u996e\u7684\u559d\u4e86\u4e24\u676f\u9152\uff0c\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0e\u5bf9\u6211\u5c60\u67d0\u4eba\u5982\u6b64\u7684\u4e0d\u6ee1\uff0c\u4e0d\u77e5\u662f\u5426\u6d89\u53ca\u4e86\u79c1\u4eba\u7684\u6069\u6028\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u4e0d\u662f\u79c1\u4eba\u7684\u6069\u6028\uff0c\u800c\u662f\u516c\u6124\uff0c\u5728\u4e0b\u89c9\u5f97\uff0c\u50cf\u4f60\u5c60\u5144\u8fd9\u79cd\u4eba\u6d3b\u5728\u4e16\u4e0a\uff0c\u5b9e\u5728\u662f\u6709\u5bb3\u65e0\u76ca\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7b11\u9053\uff1a\u201c\u5982\u82e5\u6ca1\u6709\u79c1\u4eba\u6069\u6028\uff0c\u54b1\u4eec\u5c31\u597d\u8c08\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\u8c08\u4e86\uff0c\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53f6\u5144\u89c9\u5f97\u6211\u5c60\u67d0\u6740\u4eba\u592a\u591a\u4e86\uff0c\u8fd8\u662f\u89c9\u5f97\u6211\u6740\u9519\u4eba\u4e86\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u6740\u7684\u4eba\u867d\u7136\u4e0d\u5c11\uff0c\u53ef\u662f\u4e5f\u4e0d\u7b97\u592a\u591a\uff0c\u4f46\u6700\u91cd\u8981\u7684\uff0c\u4f60\u6740\u7684\u4eba\uff0c\u90fd\u662f\u4e0d\u8be5\u6740\u7684\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6765\uff01\u53f6\u8001\u5f1f\uff0c\u54b1\u4eec\u518d\u5e72\u4e00\u676f\uff0c\u6211\u60f3\u8bf4\u4e24\u4ef6\u6740\u4eba\u7684\u4e8b\u60c5\uff0c\u7ed9\u53f6\u8001\u5f1f\u542c\u542c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u76b1\u7740\u7709\u5934\uff0c\u548c\u5c60\u65e0\u65b9\u5bf9\u5e72\u4e86\u4e00\u676f\u3002 \u3000\u3000\u9ec4\u5821\u4e3b\uff0c\u4f55\u5bd2\u8863\uff0c\u7384\u8863\u5987\u4eba\uff0c\u5b8c\u5168\u50cf\u4e2a\u5c40\u5916\u4eba\u4e00\u6837\uff0c\u51b7\u773c\u65c1\u89c2\u7740\u4e8b\u60c5\u53d1\u5c55\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u5618\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u53f6\u8001\u5f1f\u662f\u5426\u542c\u8fc7\uff0c\u6211\u6740\u6b7b\u4e03\u5b9d\u94f6\u53f7\u5f20\u5bb6\u7684\u4e8b\uff0c\u90a3\u4e00\u6b21\uff0c\u6211\u4e00\u591c\u6740\u4e86\u4e8c\u5341\u4e03\u4e2a\u4eba\u4e4b\u591a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u6869\u5f88\u6709\u540d\u7684\u8840\u6848\uff0c\u5e38\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u7684\u4eba\uff0c\u53ea\u6015\u5927\u90fd\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8868\u9762\u4e0a\uff0c\u90a3\u662f\u4e00\u5ea7\u94f6\u53f7\uff0c\u4f46\u9aa8\u5b50\u91cc\uff0c\u4ed6\u5374\u662f\u4e00\u4e2a\u5f88\u4f1a\u8d5a\u94b1\u7684\u4eba\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f1a\u8d5a\u94b1\uff0c\u5e76\u6ca1\u6709\u9519\uff0c\u5c06\u672c\u6c42\u5229\u662f\u541b\u5b50\u7231\u8d22\u4e4b\u9053\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8d5a\u94b1\u662f\u6ca1\u6709\u9519\uff0c\u4e0d\u8fc7\uff0c\u4e03\u5b9d\u94f6\u53f7\u5f20\u5bb6\u7684\u8d5a\u94b1\u65b9\u6cd5\u4e0d\u592a\u597d\uff0c\u8d5a\u7684\u94b1\uff0c\u5e26\u7740\u8840\u8165\u5473\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u5c60\u5144\u53ef\u5426\u8bf4\u5f97\u6e05\u695a\u4e00\u4e9b\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4ed6\u653e\u9ad8\u5229\u8d37\uff0c\u800c\u4e14\uff0c\u8fd8\u517b\u5b50\u4e0d\u5c11\u6253\u624b\uff0c\u4e13\u4ee5\u5411\u4eba\u8ba8\u503a\uff0c\u52fe\u7ed3\u5b98\u5e9c\uff0c\u9c7c\u8089\u826f\u6c11\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u5728\u4e0b\u5012\u672a\u542c\u8fc7\uff0c\u518d\u8bf4\uff0c\u5c31\u7b97\u771f\u6709\u4e86\u8fd9\u79cd\u4e8b\uff0c\u4e5f\u4e0d\u503c\u5f97\u4f60\u4e00\u6740\u4e8c\u5341\u4e03\u6761\u4eba\u547d\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e03\u5b9d\u94f6\u53f7\u4e3b\u8981\u7684\u8d5a\u94b1\u6765\u6e90\uff0c\u662f\u4e00\u767e\u4e00\u5341\u4e00\u5bb6\u5993\u9662\uff0c\u4e00\u767e\u4f59\u5bb6\u7684\u5993\u9662\uff0c\u904d\u5e03\u4e86\u6c5f\u5357\uff0c\u4e2d\u539f\uff0c\u90a3\u4e9b\u53ef\u601c\u5993\u5973\uff0c\u90fd\u662f\u9ad8\u5229\u8d37\u4e0b\u7684\u727a\u7272\u8005\uff0c\u5c31\u5728\u4e0b\u6240\u77e5\uff0c\u4ed6\u4eec\u6d3b\u6d3b\u62c6\u6563\u4e86\u56db\u4e94\u767e\u4e2a\u5bb6\u5ead\uff0c\u6709\u4e00\u4e2a\u8fc7\u95e8\u4e09\u5929\u7684\u65b0\u5a18\u5b50\uff0c\u4e5f\u88ab\u4ed6\u4eec\u903c\u503a\u7684\u4eba\u7ed9\u62a2\u4e86\u53bb\uff0c\u5bb3\u5f97\u90a3\u4f4d\u65b0\u90ce\u5b98\u81ea\u7edd\u800c\u6b7b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5343\u771f\u4e07\u786e\uff0c\u6211\u521a\u521a\u542c\u5230\u65f6\uff0c\u4e5f\u4e0d\u592a\u76f8\u4fe1\uff0c\u4e03\u5b9d\u94f6\u53f7\u7528\u7684\u662f\u79fb\u82b1\u63a5\u6728\u624b\u6cd5\uff0c\u81ea\u5df1\u5e76\u4e0d\u51fa\u9762\u903c\u503a\uff0c\u7b49\u6211\u591c\u5165\u4e03\u5b9d\u94f6\u53f7\uff0c\u627e\u5230\u4e86\u5e10\u518c\uff0c\u8bc1\u660e\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u624d\u627e\u51fa\u90a3\u5f20\u5927\u4e1c\u4e3b\u5f53\u9762\u8ffd\u95ee\uff0c\u4f46\u4e03\u5b9d\u94f6\u53f7\u7684\u6253\u624b\uff0c\u5df2\u7ecf\u8d76\u5230\uff0c\u53cc\u65b9\u52a8\u624b\u7684\u7ed3\u679c\uff0c\u5c31\u9020\u6210\u4e86\u4e8c\u5341\u4e03\u6761\u4eba\u547d\u6848\u5b50\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u6740\u4e86\u4e8c\u5341\u4e03\u4e2a\uff0c\u5168\u90fd\u662f\u4e03\u5b9d\u94f6\u53f7\u7684\u4eba\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5bf9\uff01\u5168\u662f\u4e03\u5b9d\u94f6\u53f7\u7684\u5e10\u623f\u6253\u624b\uff0c\u4e0d\u8fc7\u5728\u4e0b\u4e5f\u6709\u7406\u4e8f\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6211\u53d6\u8d70\u4e86\u4e03\u5b9d\u94f6\u53f7\u7684\u94f6\u7968\uff0c\u73e0\u5b9d\u3002\u4ef7\u503c\u4e09\u767e\u4e07\u4e24\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u9ec4\u5821\u4e3b\u9053\uff1a\u201c\u4e0d\u4e49\u4e4b\u8d22\uff0c\u53d6\u4e0d\u4f24\u5ec9\uff0c\u7136\u540e\uff0c\u518d\u628a\u8fd9\u4e9b\u94b1\uff0c\u7528\u5728\u6709\u7528\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5f20\u5bb6\u7684\u5987\u5b7a\uff0c\u6beb\u53d1\u65e0\u4f24\uff0c\u800c\u4e14\uff0c\u4ed6\u4eec\u7684\u4ea7\u4e1a\uff0c\u4e5f\u8db3\u591f\u4f59\u751f\u4e4b\u7528\uff0c\u8fc7\u4e00\u8f88\u5b50\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5982\u82e5\u4f60\u8bf4\u7684\u90fd\u662f\u5b9e\u8bdd\uff0c\u90a3\u5c31\u662f\u6c5f\u6e56\u4e0a\u7684\u4f20\u8a00\u5931\u771f\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6211\u51fa\u8eab\u7eff\u6797\uff0c\u521a\u5165\u6c5f\u6e56\u65f6\uff0c\u786e\u6709\u5f88\u591a\u9519\u8bef\uff0c\u4e5f\u6709\u8fc7\u8bef\u4f24\u4eba\u547d\u7684\u4e8b\uff0c\u4f46\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u90fd\u4e00\u76f4\u5f88\u8c28\u614e\uff0c\u5982\u4e0d\u662f\u8c03\u67e5\u7684\u8bc1\u636e\u660e\u786e\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u8f7b\u6613\u6740\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e24\u5e74\u524d\u9102\u5dde\u8840\u6848\uff0c\u5be1\u6bcd\u4e03\u5973\uff0c\u5168\u6570\u88ab\u6740\uff0c\u542c\u8bf4\u4e5f\u662f\u4f60\u4e0b\u7684\u624b\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5bf9\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u53c8\u662f\u4e3a\u4ec0\u4e48\uff1f\u96be\u9053\u90a3\u4e00\u6bcd\u4e03\u5973\uff0e\u4e5f\u662f\u653e\u9ad8\u5229\u903c\u4eba\u4e3a\u5a3c\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u4e0d\u8fc7\uff0c\u5979\u4eec\u7ecf\u8425\u4e86\u4e00\u5341\u4e8c\u5bb6\u9ed1\u5e97\uff0c\u5750\u6536\u539a\u5229\uff0c\u8fc7\u5f80\u884c\u5546\uff0c\u6b7b\u5728\u5979\u4eec\u516b\u4eba\u624b\u4e2d\u7684\u4e0d\u4e0b\u4e00\u767e\u4eba\u53f6\u8001\u5f1f\uff0c\u89c9\u5f97\u5979\u4eec\u662f\u5426\u8be5\u6740\u5462\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u771f\u6709\u5176\u4e8b\uff0c\u90a3\u662f\u6b7b\u6709\u4f59\u8f9c\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5341\u5e74\u4ee5\u6765\uff0c\u5728\u4e0b\u505a\u7684\u6848\u5b50\uff0c\u5821\u4e3b\u90a3\u8fb9\uff0c\u90fd\u6709\u8be6\u7ec6\u7684\u8bb0\u5f55\uff0c\u53f6\u8001\u5f1f\u5982\u82e5\u6709\u6687\uff0c\u4e0d\u59a8\u770b\u4e00\u904d\u3002\u201d \u3000\u3000\u4e00\u76f4\u6ca1\u6709\u5f00\u53e3\u7684\u4f55\u5bd2\u8863\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u5c60\u5144\u867d\u662f\u540d\u52a8\u6c5f\u6e56\uff0c\u4f46\u4ed6\u505a\u4e8b\u7684\u7ec6\u5fc3\uff0c\u6bd4\u5144\u5f1f\u8981\u5f3a\u5341\u500d\uff0c\u5728\u5144\u5f1f\u7684\u5251\u4e0b\u4e5f\u8bb8\u8fd8\u6709\u6789\u6b7b\u4e4b\u4eba\uff0c\u4f46\u5c60\u5144\u4e0d\u4f1a\uff0c\u4ed6\u6bcf\u505a\u4e00\u4ef6\u4e8b\uff0c\u90fd\u8c03\u67e5\u7684\u6781\u4e3a\u8be6\u5c3d\uff0c\u7136\u540e\u518d\u4e0b\u624b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u6c5f\u6e56\u4e0a\u7684\u4f20\u8bf4\uff0c\u5b8c\u5168\u5931\u771f\u4e86\u3002\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u9053\uff1a\u201c\u90a3\u662f\u56e0\u4e3a\uff0c\u4ed6\u4eec\u5927\u90fd\u4e0d\u660e\u771f\u76f8\uff0c\u6709\u4e9b\u662f\u4ec7\u5bb6\u6545\u610f\u6563\u5e03\u7684\u8c23\u8a00\uff0c\u4ee5\u60d1\u4eba\u8033\u76ee\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5509\uff01\u6700\u91cd\u8981\u7684\u8fd8\u662f\u6211\u51fa\u8eab\u7eff\u6797\uff0c\u800c\u4e14\uff0c\u521d\u5165\u6c5f\u6e56\u65f6\uff0c\u5e74\u8f7b\u6c14\u76db\uff0c\u7ed3\u4ec7\u592a\u591a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4f3c\u4e4e\u662f\u5bf9\u5c60\u65e0\u65b9\u7684\u6210\u89c1\u5f88\u6df1\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u5c31\u6740\u4e86\u5f88\u591a\u7684\u4eba\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7b11\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u51fa\u8eab\u4e0d\u597d\uff0c\u5c31\u88ab\u81ea\u547d\u767d\u9053\u7684\u4fa0\u4e49\u4e2d\u4eba\uff0c\u6709\u4e9b\u770b\u4e0d\u8d77\uff0c\u5c31\u8c61\u4f60\u53f6\u8001\u5f1f\u4e00\u6837\u5bf9\u6211\u6709\u7740\u5f88\u6df1\u7684\u6210\u89c1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u548c\u4f60\u6210\u89c1\u5f88\u6df1\u7684\u4eba\u4e0d\u662f\u6211\uff0c\u800c\u662f\u6574\u4e2a\u6c5f\u6e56\u7684\u540c\u9053\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6574\u4e2a\u7684\u6c5f\u6e56\u540c\u9053\uff0c\u5bf9\u6211\u5c60\u65e0\u65b9\u6709\u6240\u8bef\u89e3\uff0c\u90a3\u662f\u56e0\u4e3a\u4ed6\u4eec\u6ca1\u6709\u542c\u8fc7\u6211\u7684\u89e3\u91ca\uff0c\u4f46\u662f\u4f60\u53f6\u8001\u5f1f\u542c\u8fc7\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u662f\u4e0d\u80af\u539f\u8c05\u5462\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u662f\u56e0\u4e3a\u5728\u4e0b\u8fd8\u672a\u88ab\u4f60\u8bf4\u670d\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7b11\u9053\uff1a\u201c\u5bf9\u4e8e\u6c5f\u6e56\u540c\u9053\u5bf9\u6211\u7684\u4ec7\u6068\uff0c\u6211\u5012\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u56e0\u4e3a\uff0c\u5728\u4e0b\u521d\u51fa\u6c5f\u6e56\u4e4b\u65f6\uff0c\u786e\u4e5f\u66fe\u72af\u8fc7\u4e0d\u5c11\u7684\u9519\u5931\uff0c\u8bef\u6740\u8fc7\u4e00\u4e9b\u4eba\uff0c\u6240\u4ee5\uff0c\u81ea\u5df1\u60f3\u8d77\u6765\u4e5f\u6709\u4e9b\u4e0d\u5b89\uff0c\u81ea\u7136\u4e0d\u80fd\u602a\u522b\u4eba\u5bf9\u6211\u7684\u8bef\u89e3\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u4eba\u975e\u5723\u8d24\uff0c\u5b70\u80fd\u65e0\u8fc7\uff0c\u4f5b\u5bb6\u4e5f\u8bf4\u8fc7\u56de\u5934\u662f\u5cb8\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u81ea\u5df1\u89c9\u5f97\uff0c\u505a\u7684\u597d\u4e8b\uff0c\u6bd4\u8fc7\u53bb\u505a\u7684\u574f\u4e8b\u591a\u8fc7\u5341\u500d\uff0c\u4e5f\u8bb8\uff0c\u6211\u7684\u624b\u6bb5\u592a\u6fc0\u70c8\u4e86\uff0c\u6240\u4ee5\u8fd9\u4e2a\u6076\u540d\u4e00\u76f4\u6ca1\u6cd5\u5b50\u6d17\u5237\u6389\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u987e\u4e86\u4e00\u773c\uff0c\u53d1\u89c9\u9ec4\u5821\u4e3b\uff0c\u4f55\u5bd2\u8863\uff0c\u4ee5\u53ca\u90a3\u7384\u8863\u5987\u4eba\uff0c\u795e\u8272\u90fd\u5f88\u5e73\u9759\uff0c\u5bf9\u4e24\u4eba\u7684\u4e89\u6267\uff0c\u8868\u73b0\u51fa\u4e86\u4e00\u526f\u53e4\u4e95\u4e0d\u6ce2\u7684\u5473\u9053\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u53c8\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u8981\u4e0d\u8981\u6211\u518d\u4e00\u4ef6\u4e00\u4ef6\u7684\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u662f\u5426\u5df2\u7ecf\u76f8\u4fe1\u4e86\u6211\u8bf4\u7684\u8bdd\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u6211\u8fd8\u65e0\u6cd5\u5b8c\u5168\u76f8\u4fe1\uff0c\u6240\u4ee5\uff0c\u5728\u4e0b\u8fd8\u2026\u2026\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd8\u65e0\u6cd5\u5b8c\u5168\u6d88\u5931\u5bf9\u6211\u7684\u4ec7\u89c6\uff0c\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5982\u82e5\u4f60\u8bf4\u7684\u90fd\u662f\u5b9e\u8a00\uff0c\u6211\u4e0d\u4f46\u4e0d\u4ec7\u89c6\u5c60\u5144\uff0c\u800c\u4e14\uff0c\u5bf9\u5c60\u5144\u8fd8\u6709\u65e0\u9650\u7684\u5c0a\u656c\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u53ea\u6015\u8fd8\u8981\u4f60\u53f6\u5144\u8bc1\u5b9e\u4e86\u4e4b\u540e\u624d\u884c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5bf9\uff01\u201d\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u4e0d\u77e5\u8981\u591a\u5c11\u65f6\u95f4\u624d\u884c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u5144\u76f8\u4fe1\u5144\u5f1f\uff1f\u201d \u3000\u3000\u5bf9\u4f55\u5bd2\u8863\uff0c\u53f6\u957f\u9752\u786e\u6709\u7740\u65e0\u9650\u7684\u656c\u91cd\uff0c\u70b9\u70b9\u5934\u8bf4\u9053\uff1a\u201c\u76f8\u4fe1\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d\uff0c\u6211\u4fdd\u8bc1\u5c60\u5144\u8bf4\u7684\u540d\u53e5\u53e5\u771f\u5b9e\uff0c\u4e8c\u5341\u5e74\u524d\uff0c\u5c60\u5144\u505a\u4e86\u4ec0\u4e48\uff1f\u5144\u5f1f\u4e0d\u613f\u62c5\u4fdd\uff0c\u4f46\u8fd1\u4e8c\u5341\u5e74\uff0c\u5c60\u5144\u6240\u4f5c\u6240\u4e3a\uff0c\u65e0\u4e00\u4e0d\u662f\u5927\u5feb\u4eba\u5fc3\u7684\u4e8b\uff0c\u65e5\u540e\uff0c\u53f6\u5144\u5982\u82e5\u60f3\u51fa\u4ec0\u4e48\u4e0d\u7b26\u4e4b\u5904\uff0c\u552f\u5144\u5f1f\u662f\u95ee\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7a81\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u5bf9\u7740\u5c60\u65e0\u65b9\u606d\u656c\u884c\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u5c60\u5144\uff0c\u5144\u5f1f\u5c11\u4e0d\u66f4\u4e8b\uff0c\u9002\u624d\u591a\u6709\u5f97\u7f6a\u4e4b\u5904\uff0c\u8fd8\u671b\u5c60\u5144\u539f\u8c05\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u539f\u8c05\u5012\u4e0d\u6562\u5f53\uff0c\u53ea\u671b\u53f6\u8001\u5f1f\uff0c\u76f8\u4fe1\u6211\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u76ee\u5149\u8f6c\u5230\u90a3\u7384\u8863\u5987\u4eba\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u4f4d\u662f\u2026\u2026\u201d \u3000\u3000\u7384\u8863\u5987\u4eba\u63a5\u9053\uff1a\u201c\u8d31\u59be\u7684\u6076\u540d\uff0c\u53ea\u6015\u4e0d\u5728\u5c60\u65e0\u65b9\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u4e00\u9707\u9053\uff1a\u201c\u592b\u4eba\u662f\u2026\u2026\u201d \u3000\u3000\u7384\u8863\u5987\u4eba\u9053\uff1a\u201c\u522b\u53eb\u6211\u592b\u4eba\uff0c\u6211\u8fd8\u672a\u66fe\u5ac1\u8fc7\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u7384\u8863\u5973\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u8fd9\u4e2a\u4e16\u754c\u4e0a\uff0c\u6562\u5a36\u6211\u7684\u4eba\uff0c\u6211\u8fd8\u6ca1\u6709\u9047\u4e0a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8bf7\u6559\u540d\u53f7\u3002\u201d \u3000\u3000\u7384\u8863\u5973\u5b50\u9053\uff1a\u201c\u534e\u5999\u771f\u3002\u201d\u9ec4\u5821\u4e3b\u3001\u4f55\u5bd2\u8863\uff0c\u751a\u81f3\u5c60\u65e0\u65b9\uff0c\u90fd\u4e0d\u518d\u63a5\u53e3\u4e86\uff0c\u53f6\u957f\u9752\u5fc3\u4e2d\u660e\u767d\uff0c\u53c8\u9047\u4e0a\u4e86\u4e00\u6869\u9ebb\u70e6\u4e8b\u60c5\u3002 \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u679c\u7136\uff0c\u4e5f\u662f\u4e00\u4f4d\u6c5f\u6e56\u4e0a\u5927\u5927\u6709\u540d\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u7384\u8863\u5973\u5b50\u9053\uff1a\u201c\u4e00\u4e2a\u51f6\u540d\u5353\u8457\u7684\u6076\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u534e\u59d1\u5a18\u4e5f\u662f\u6539\u90aa\u5f52\u6b63\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u6211\u770b\uff0c\u6211\u7528\u53e3\u820c\u89e3\u8bf4\uff0c\u53ea\u6015\u4e5f\u662f\u65e0\u6548\uff0c\u4e0d\u5982\u627e\u4e00\u4f4d\u62c5\u4fdd\u4eba\u62c5\u4fdd\u4e00\u4e0b\u5427\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u53ea\u8981\u4f55\u5144\u4e00\u53e5\u8bdd\uff0c\u5144\u5f1f\u51b3\u5b9a\u4e0d\u6000\u7591\u4ec0\u4e48\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d\uff01\u53f6\u5144\u8fd9\u4e48\u7ed9\u5144\u5f1f\u9762\u5b50\uff0c\u5144\u5f1f\u5c31\u80c6\u5927\u7684\u627f\u62c5\u4e0b\u6765\uff0c\u534e\u59d1\u5a18\u4ea6\u975e\u6614\u5e74\u7684\u6c5f\u6e56\u6740\u661f\uff0c\u800c\u662f\u4e00\u4f4d\u771f\u771f\u6b63\u6b63\u7684\u6551\u4e16\u89c2\u97f3\u4e86\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e24\u4e2a\u6781\u7aef\uff0c\u4e00\u4e2a\u51f6\u540d\u6ee1\u6c5f\u6e56\u7684\u4eba\uff0c\u53d8\u6210\u4e86\u6551\u4e16\u89c2\u97f3\uff0c\u53f6\u957f\u9752\u5b9e\u5728\u96be\u4fe1\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u5982\u4e0d\u8bf4\u660e\uff0c\u672a\u514d\u6709\u4e9b\u542b\u7cca\u7b3c\u7edf\uff0c\u53ea\u4e3e\u4e00\u4f8b\uff0c\u53f6\u5144\u81ea\u5df1\u60f3\u60f3\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5144\u5f1f\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e94\u5e74\u524d\uff0c\u9c81\u897f\u8c6b\u4e1c\uff0c\u53d1\u751f\u4e86\u4e00\u6b21\u761f\u75ab\uff0c\u6b7b\u4e86\u5341\u4f59\u4e07\u4eba\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u90a3\u771f\u662f\u4e00\u6b21\u5927\u52ab\u96be\uff0c\u5144\u5f1f\u4e5f\u542c\u8bf4\u8fc7\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u534e\u59d1\u5a18\u91c7\u96c6\u4e86\u5927\u6279\u836f\u7269\uff0c\u7ec3\u6210\u4e39\u4e38\uff0c\u4eb2\u7387\u56db\u5341\u516b\u540d\u52a9\u624b\u6df1\u5165\u75ab\u533a\uff0c\u6551\u4eba\u4e09\u5341\u4e07\uff0c\u56db\u5341\u516b\u4f4d\u52a9\u624b\u4e2d\uff0c\u6709\u4e00\u534a\u53d7\u75ab\u6bd2\u611f\u67d3\u4e0d\u5e78\u4ea1\u6545\uff0c\u534e\u59d1\u5a18\u4ea6\u4e3a\u75ab\u6bd2\u6240\u4fb5\u5927\u75c5\u4e00\u573a\uff0c\u7597\u6cbb\u4e09\u4e2a\u6708\u624d\u5f97\u590d\u5143\u2026\u2026\u201d \u3000\u3000\u8f7b\u8f7b\u5618\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u4ee5\u5979\u6df1\u539a\u7684\u5185\u529f\uff0c\u672c\u6765\u75ab\u6bd2\u96be\u4fb5\uff0c\u53ea\u56e0\u4e3a\u4e86\u6551\u4eba\uff0c\u52b3\u7d2f\u8fd1\u6708\u672a\u5f97\u4f11\u606f\uff0c\u4f53\u80fd\u5927\u51cf\uff0c\u624d\u4f7f\u75ab\u6bd2\u4e58\u865a\u800c\u5165\uff0c\u4f46\u534e\u59d1\u5a18\u4ecd\u7136\u62b1\u75c5\u65bd\u836f\u5954\u8d70\u6570\u767e\u91cc\uff0c\u76f4\u5230\u75c5\u52bf\u53d1\u4f5c\u4e0d\u652f\u5012\u5730\uff0c\u624d\u88ab\u4eba\u6551\u51fa\u7597\u6cbb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u539f\u6765\u65bd\u836f\u75ab\u533a\uff0c\u5954\u8d70\u534a\u8f7d\uff0c\u88ab\u8a89\u4e3a\u6551\u4e16\u89c2\u97f3\u7684\uff0c\u5c31\u662f\u534e\u59d1\u5a18\uff0c\u5728\u4e0b\u5931\u656c\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u7b11\u9053\uff1a\u201c\u6211\u8fc7\u53bb\u6740\u4eba\u592a\u591a\uff0c\u800c\u4e14\u662f\u4e0d\u5206\u597d\u574f\uff0c\u5b8c\u5168\u4ee5\u4e00\u5df1\u7684\u559c\u6012\u51b3\u5b9a\uff0c\u90a3\u4e00\u6b21\uff0c\u867d\u7136\u6551\u6d3b\u4e86\u4e0d\u5c11\uff0c\u4e5f\u4e0d\u8fc7\u7a0d\u8d4e\u524d\u884d\uff0c\u800c\u4e14\uff0c\u6b7b\u4e86\u4e8c\u5341\u56db\u4f4d\u5e2e\u6211\u5fd9\u7684\u4eba\uff0c\u81f3\u4eca\u60f3\u6765\u4ecd\u662f\u4e0d\u5b89\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u534e\u59d1\u5a18\u5982\u6b64\u5584\u4e3e\uff0c\u653e\u773c\u5929\u4e0b\u65e0\u4eba\u80fd\u53ca\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u5c31\u6ca1\u6709\u4f20\u51fa\u6765\u5417\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u534e\u59d1\u5a18\u4e3a\u5584\u4e0d\u6b32\u4eba\u77e5\uff0c\u6240\u4ee5\u5979\u5728\u5954\u8d70\u6551\u4eba\u4e4b\u65f6\uff0c\u4e00\u76f4\u662f\u9ed1\u7eb1\u8499\u9762\uff0c\u8eab\u7740\u767d\u8863\uff0c\u6240\u4ee5\u624d\u6709\u89c2\u97f3\u964d\u4e16\uff0c\u89e3\u75ab\u6551\u96be\u7684\u4f20\u8bf4\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u60ed\u6127\u554a\uff01\u60ed\u6127\u3002\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u9053\uff1a\u201c\u53f6\u5144\u65e2\u80fd\u4e86\u7136\u5185\u60c5\uff0c\u5927\u5bb6\u90fd\u4e0d\u518d\u63d0\u8fd9\u4ef6\u4e8b\uff0c\u54b1\u4eec\u597d\u597d\u5403\u4e00\u987f\uff0c\u6211\u8fd8\u6709\u4e8b\u544a\u8bc9\u8bf8\u4f4d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7adf\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u7980\u5821\u4e3b\uff0c\u5728\u4e0b\u53ea\u6015\u65e0\u6687\u8fdb\u9910\uff0c\u90a3\u8fb9\u4e8b\u52a1\u7d27\u6025\uff0c\u5728\u4e0b\u8fd9\u5c31\u8981\u52a8\u8eab\u4e86\u3002\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u654c\u52bf\u53ef\u80fd\u6709\u53d8\uff0c\u54b1\u4eec\u7684\u884c\u52a8\uff0c\u4e5f\u8981\u6539\u53d8\u4e00\u4e0b\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6539\u53d8\uff0c\u4ec0\u4e48\u6539\u53d8\uff1f\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u76ee\u4e0b\u8fd8\u6ca1\u6709\u4ec0\u4e48\u6d88\u606f\u4f20\u6765\uff0c\u81f3\u5c11\uff0c\u6211\u4eec\u8981\u5148\u6478\u51fa\u6765\u4ed6\u4eec\u7684\u884c\u8e2a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u4e00\u8f86\u534e\u4e3d\u7684\u7bf7\u8f66\uff0c\u4e00\u76f4\u5728\u6211\u4eec\u7684\u76d1\u89c6\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u9053\uff1a\u201c\u90a3\u662f\u91d1\u8749\u8131\u58f3\u4e4b\u8ba1\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u4eec\u5df2\u7ecf\u65e9\u628a\u4e1c\u897f\u79fb\u5f80\u522b\u5904\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u9053\uff1a\u201c\u6709\uff01\u800c\u4e14\uff0c\u5343\u771f\u4e07\u786e\u2026\u2026\u201d \u3000\u3000\u7b11\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5bd2\u8863\uff0c\u4ed6\u4eec\u6709\u5e72\u53d8\u4e07\u5316\uff0c\u6211\u4eec\u6709\u4e00\u5b9a\u4e4b\u89c4\uff0c\u5929\u4eae\u4e4b\u524d\uff0c\u4e00\u5b9a\u6709\u6d88\u606f\u4f20\u6765\uff0c\u4f60\u5c31\u653e\u677e\u5fc3\u60c5\uff0c\u597d\u597d\u7684\u5403\u559d\u4e00\u987f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u4e00\u5411\u6599\u4e8b\u5982\u795e\uff0c\u60f3\u6765\u4e0d\u4f1a\u6709\u9519\u4e86\u3002\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u5173\u7cfb\u91cd\u5927\uff0c\u8868\u9762\u4e0a\u770b\u53bb\u867d\u7136\u5341\u5206\u5355\u7eaf\uff0c\u4f46\u9aa8\u5b50\u91cc\u5374\u662f\u7275\u626f\u5f88\u591a\uff0c\u6211\u4e5f\u662f\u4eca\u5929\u624d\u77e5\u9053\u4e00\u90e8\u5206\u5185\u60c5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u90fd\u7275\u626f\u4e0a\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u9053\uff1a\u201c\u542c\u8bf4\uff0c\u72c2\u9f99\uff0c\u98de\u9e70\u90fd\u7275\u5165\u4e86\u8fd9\u4ef6\u4e8b\u4e2d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\uff0c\u4e00\u4e2a\u95ed\u5173\u81ea\u5b88\uff0c\u4e0d\u592a\u559c\u6b22\u591a\u7ba1\u6c5f\u6e56\u4e2d\u4e8b\uff0c\u600e\u4e48\u7adf\u4f1a\u5377\u5165\u8fd9\u4ef6\u6f29\u6da1\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u9053\uff1a\u201c\u6700\u91cd\u8981\u7684\u4e00\u6212\uff0c\u5c31\u662f\u5fc3\u6212\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5fc3\u6212\uff0c\u8fd9\u5012\u662f\u4ece\u672a\u542c\u8fc7\u7684\u6212\u5f8b\u3002\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u7684\u6240\u884c\u6240\u4e3a\uff0c\u53ea\u8981\u4e8e\u5fc3\u65e0\u6127\uff0c\u53c8\u4f55\u5fc5\u7528\u5f88\u591a\u7684\u6212\u5f8b\u6765\u675f\u7f1a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\u8bf4\u7684\u4e5f\u662f\u3002\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u4eec\u65e2\u662f\u4e00\u4e2a\u7ec4\u7ec7\uff0c\u4eba\u6570\u4f17\u591a\uff0c\u5206\u5b50\u5e9e\u6742\uff0c\u5982\u662f\u6ca1\u6709\u51e0\u6761\u89c4\u6212\u675f\u7f1a\uff0c\u53ea\u6015\u6709\u4e9b\u4eba\u96be\u514d\u6709\u65e0\u6240\u9002\u4ece\u4e4b\u611f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\u8bf4\u7684\u662f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53f6\u5144\uff0c\u672c\u7ec4\u7ec7\u7684\u6212\u5f8b\u5f88\u7b80\u5355\uff0c\u53ea\u6709\u4e09\u6761\uff0c\u4e00\u662f\u4e0d\u80fd\u5f3a\u4eba\u6240\u96be\uff0c\u4e8c\u662f\u4e0d\u8bb8\u6740\u4f24\u65e0\u8f9c\uff0c\u4e09\u662f\u884c\u4e8b\u65e0\u6127\u5929\u5730\uff0c\u8fd9\u4e09\u5927\u620e\u5f8b\uff0c\u867d\u7136\u7b80\u5355\uff0c\u4f46\u5374\u5305\u7f57\u4e86\u5f88\u591a\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u7b11\u4e00\u7b11\uff0c\u906d\uff1a\u201c\u53f6\u5144\uff0c\u4e00\u4e2a\u4eba\uff0c\u53ea\u8981\u80fd\u505a\u5230\u4ee5\u4e0a\u4e09\u6212\u6240\u9650\uff0c\u5c31\u7b97\u4e0d\u662f\u5927\u5584\u4e4b\u4eba\uff0c\u4f46\u4e5f\u4e0d\u4f1a\u662f\u6019\u6076\u4e4b\u5f92\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u6267\u5927\u4e49\uff0c\u4e0d\u62d8\u5c0f\u8282\uff0c\u6240\u4ee5\u6212\u5f8b\u7b80\u660e\uff0c\u4f46\u5374\u6267\u884c\u7684\u5341\u5206\u4e25\u5389\uff0c\u534e\u59d1\u5a18\u5c31\u662f\u6267\u884c\u5251\u4f7f\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u6267\u6cd5\u867d\u4e25\uff0c\u4f46\u4e00\u5411\u517c\u987e\u60c5\u7406\uff0c\u660e\u77e5\u6545\u72af\u548c\u65e0\u610f\u4e4b\u5931\uff0c\u6709\u7740\u5f88\u5927\u7684\u5dee\u8ddd\uff0c\u65e0\u610f\u800c\u9519\u7684\u4eba\uff0c\u6211\u7684\u5904\u5206\u624b\u6bb5\u5c31\u8f7b\u4e86\u5f88\u591a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1b\u201c\u6267\u6cd5\u5728\u4eba\uff0c\u5206\u8fa8\u51fa\u662f\u6545\u610f\u8fd8\u662f\u6709\u610f\uff0e\u53ea\u6015\u8981\u5f88\u7cbe\u5bc6\u7684\u5224\u65ad\u624d\u5bf9\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u9053\uff1a\u201c\u534e\u59d1\u5a18\u6267\u6cd5\u4e25\u660e\uff0c\u7ec4\u7ec7\u4e2d\u4eba\uff0c\u65e0\u4e0d\u656c\u754f\uff0c\u4e00\u63d0\u6267\u6cd5\u5251\u4f7f\uff0c\u65e0\u4e0d\u5fc3\u670d\u53e3\u773c\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u5c0f\u59b9\u8981\u8bf4\u660e\u767d\uff0c\u6267\u6cd5\u5251\u4f7f\u7684\u516c\u6b63\uff0c\u53f6\u5144\u65e5\u540e\u81ea\u4f1a\u77e5\u6653\uff0c\u4e0d\u662f\u5c0f\u59b9\u5938\u53e3\uff0c\u7eb5\u7136\u6709\u4ec0\u4e48\u9519\u5931\uff0c\u5176\u9519\u4e5f\u662f\u5076\u6709\u7684\u758f\u5ffd\uff0c\u4e0d\u8fc7\uff0c\u7ec4\u7ec7\u4e2d\u4eba\uff0c\u90fd\u77e5\u9053\u6267\u6cd5\u5251\u4f7f\uff0c\u5374\u4e0d\u77e5\u6267\u6cd5\u5251\u4f7f\u5c31\u662f\u5c0f\u59b9\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53f6\u5144\uff0c\u5821\u4e3b\u521a\u624d\u5df2\u7ecf\u8bf4\u660e\uff0c\u6211\u4eec\u8fd9\u4e2a\u7ec4\u7ec7\u4e2d\uff0c\u4eba\u7269\u5341\u5206\u6df7\u6742\u3001\u6709\u5f88\u591a\u7684\u7eff\u6797\u9053\u4e0a\u4eba\u7269\u4ed6\u4eec\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u8a89\uff0c\u53ef\u80fd\u90fd\u4e0d\u592a\u597d\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u4e00\u76b1\u7709\u5934\uff0c\u63a5\u9053\uff1a\u201c\u4f55\u5144\uff0c\u4ed6\u4eec\u52a0\u5165\u4e86\u6211\u4eec\u8fd9\u4e2a\u7ec4\u7ec7\u4e4b\u540e\uff0c\u90fd\u53d8\u597d\u4e86\uff0c\u80fd\u9075\u5b88\u4e09\u5927\u6212\u5f8b\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4ed6\u4eec\u77e5\u9053\u8fd9\u4e09\u5927\u6212\u5f8b\uff0c\u90a3\u4e0d\u662f\u8981\u4ed6\u4eec\u9075\u5b88\u7684\u539f\u5219\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f55\u5144\uff0c\u5728\u4e0b\u662f\u8d8a\u542c\u8d8a\u7cca\u6d82\u4e0b\uff1a\u201d \u3000\u3000\u4f3a\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u9aa4\u7136\u95f4\u95ee\u8d77\u6765\uff0c\u4f3c\u662f\u590d\u6742\u7684\u5f88\uff0c\u4f46\u5144\u5f1f\u5982\u82e5\u89e3\u91ca\u4e00\u4e0b\uff0c\u8fd9\u5c31\u4e0d\u662f\u4e3a\u5947\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\uff01\u5144\u5f1f\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u4e2a\u7ec4\u7ec7\uff0c\u5206\u4e24\u79cd\u540d\u4e49\u7edf\u7387\u4e0b\u5c5e\uff0c\u4e00\u4e2a\u9634\u9633\u5821\uff0c\u90a3\u662f\u4e13\u95e8\u7edf\u7387\u7eff\u6797\u4eba\u7269\u7684\u540d\u4e49\uff0c\u7fa4\u6076\u6c47\u96c6\uff0c\u6211\u4eec\u5bf9\u4ed6\u4eec\u7684\u7ea6\u675f\u6212\u5f8b\u81ea\u4e5f\u4e0d\u76f8\u540c\uff0c\u5176\u4e2d\u6700\u91cd\u8981\u7684\u4e24\u6761\uff0c\u5c31\u662f\u4e0d\u5f97\u6b3a\u4e0a\uff0c\u8981\u4e25\u683c\u6267\u884c\u4ee4\u8c15\uff0c\u8fd9\u5c31\u662f\u6211\u4eec\u4e0e\u522b\u7684\u95e8\u6d3e\u4e0d\u540c\u4e4b\u5904\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5618\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u79cd\uff0c\u5c31\u662f\u6211\u4eec\u8fd9\u4e00\u73ed\u4eba\u4e86\uff0c\u5fd7\u540c\u9053\u5408\uff0c\u809d\u80c6\u76f8\u7167\uff0c\u6211\u4eec\u6709\u7406\u60f3\u4e5f\u5b58\u5fc3\u7ef4\u62a4\u6c5f\u6e56\u9053\u4e49\uff0c\u5927\u5bb6\u76f8\u5904\uff0c\u9053\u4e49\u4e3a\u5148\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u539f\u6765\u90a3\u4e48\u590d\u6742\u3002\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u9053\uff1a\u201c\u957f\u9752\uff0c\u8fd9\u662f\u6ca1\u6709\u529e\u6cd5\u7684\u4e8b\uff0c\u6211\u4eec\u5fc5\u987b\u8981\u501f\u91cd\u4ed6\u4eec\uff0c\u8fd9\u662f\u4e00\u4e2a\u8fc7\u7a0b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u4f60\u73b0\u5728\u662f\u771f\u6b63\u9762\u76ee\u5462\uff1f\u8fd8\u662f\u6234\u7740\u9762\u5177\uff1f\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\u4ed4\u7ec6\u77a7\u77a7\uff0c\u6211\u662f\u5426\u6234\u6709\u9762\u5177\uff1f\u201d \u3000\u3000\u53ea\u89c1\u4f55\u5bd2\u8863\uff0c\u534e\u5999\u771f\uff0c\u5c60\u65e0\u65b9\u6bcf\u4e2a\u4eba\u90fd\u5fae\u5fae\u800c\u7b11\u3002 \u3000\u3000\u4f3c\u662f\u5bf9\u53f6\u957f\u9752\u8fd9\u4e2a\u95ee\u6cd5\uff0c\u611f\u89c9\u5230\u5341\u5206\u5947\u602a\u3002 \u3000\u3000\u53f6\u957f\u9752\u731b\u7136\u95f4\u611f\u89c9\uff0c\u8fd9\u4e9b\u4eba\uff0c\u4e0d\u4f46\u90fd\u6709\u7740\u5f88\u5927\u7684\u5ea6\u91cf\uff0c\u800c\u4e14\uff0c\u4e5f\u6709\u7740\u8d85\u4eba\u7684\u80f8\u895f\uff0c\u65e0\u7269\u4e0d\u5bb9\u3002 \u3000\u3000\u9ec4\u5821\u4e3b\u7a81\u7136\u4e3e\u624b\u5728\u80f8\u4e0a\u4e00\u62b9\uff0c\u987f\u65f6\u6362\u4e86\u4e00\u526f\u9762\u5bb9\uff0c\u9053\uff1a\u201c\u957f\u9752\uff0c\u4ed4\u7ec6\u770b\u770b\uff0c\u8fd9\u5c31\u662f\u6211\u7684\u771f\u9762\u76ee\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51dd\u76ee\u671b\u53bb\u3002 \u3000\u3000\u90a3\u662f\u4e00\u5f20\u8f6e\u5ed3\u5f88\u7f8e\u7684\u8138\uff0c\u53ef\u60dc\u7684\u662f\uff0c\u8138\u4e0a\u5374\u4ea4\u9519\u7740\u5f88\u591a\u7684\u75a4\u75d5\u3002 \u3000\u3000\u53f6\u957f\u9752\u5446\u4f4f\u4e86\uff0c\u957f\u4e45\u4e4b\u540e\uff0c\u624d\u9eef\u7136\u5730\u8bf4\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u63a5\u9053\uff1a\u201c\u6211\u53eb\u9ec4\u7075\uff0c\u8fd9\u5f20\u8138\uff0c\u88ab\u4eba\u5212\u4e86\u5341\u516b\u5200\uff0c\u5f53\u65f6\u6211\u5f88\u60f3\u53bb\u6b7b\uff0c\u4f46\u4ee5\u540e\uff0c\u6211\u5374\u52c7\u6562\u5730\u6d3b\u4e86\u4e0b\u6765\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u6bc1\u4e86\u4f60\u7684\u5bb9\u8c8c\uff0c\u5821\u4e3b\u53ef\u5df2\u7ecf\u62a5\u4e86\u4ec7\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6ca1\u6709\u4ec7\u4eba\uff0c\u90a3\u4e9b\u4eba\uff0c\u76ee\u4e0b\u90fd\u8fd8\u597d\u597d\u5730\u6d3b\u7740\uff0c\u957f\u9752\uff0c\u6216\u8bb8\u4f60\u4e0d\u592a\u76f8\u4fe1\uff0c\u73b0\u5728\uff0c\u6211\u4e00\u70b9\u4e5f\u4e0d\u6068\u4ed6\u4eec\uff0c\u4ed6\u4eec\u6bc1\u4e86\u6211\u7684\u8138\uff0c\u4f46\u5374\u4f7f\u7075\u667a\u590d\u660e\uff0c\u4f7f\u6211\u7531\u90aa\u5f52\u6b63\u3002\u201d \u3000\u3000\u5316\u8eab\u4e66\u751f\uff0c\u539f\u6765\u662f\u4e00\u4e2a\u4ecb\u4e8e\u6b63\u90aa\u4e4b\u95f4\u7684\u4eba\u7269\uff0c\u4ed6\u592a\u806a\u660e\u4e86\uff0c\u806a\u660e\u5230\u628a\u4efb\u4f55\u4eba\u90fd\u4e0d\u653e\u5728\u773c\u4e2d\u3002 \u3000\u3000\u4ed6\u6709\u4e00\u8eab\u7edd\u9ad8\u7684\u6b66\u529f\uff0c\u548c\u4eba\u6240\u96be\u53ca\u7684\u667a\u6167\u53ca\u4e00\u5f20\u82f1\u4fca\u52a8\u4eba\u7684\u8138\uff0c\u548c\u7528\u4e0d\u5b8c\u7684\u8d22\u5bcc\uff0c\u4e00\u5f20\u80fd\u8bf4\u4f1a\u9053\u7684\u5634\u5df4\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u4e2a\u4eba\uff0c\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u4e0d\u559c\u6b22\u4ed6\u5462\uff1f\u4ec0\u4e48\u6837\u7684\u5973\u4eba\u4e0d\u7231\u4ed6\u5462\uff1f \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u65e0\u5f80\u4e0d\u5229\uff0c\u65e0\u8bba\u662f\u7537\u4eba\uff0c\u5973\u4eba\uff0c\u90fd\u4f1a\u88ab\u4ed6\u73a9\u5f04\u4e8e\u80a1\u638c\u4e4b\u4e0a\u3002 \u3000\u3000\u4ed6\u591a\u60c5\uff0c\u4f46\u7edd\u4e0d\u4e13\u60c5\uff0c\u4ed6\u4e0d\u4f46\u5360\u6709\u4e86\u5f88\u591a\u5c11\u5973\u7684\u6e05\u767d\u4e4b\u8eaf\uff0c\u4e5f\u5e26\u8d70\u4e86\u5979\u4eec\u7684\u5fc3\u3002 \u3000\u3000\u65e0\u6570\u5c11\u5973\u82b3\u5fc3\uff0c\u88ab\u4ed6\u63c9\u788e\u3002 \u3000\u3000\u4ed6\u867d\u7136\u806a\u660e\u7edd\u9876\uff0c\u4f46\u591c\u8def\u8d70\u5f97\u592a\u591a\u4e86\uff0c\u6709\u4e00\u5929\u7ec8\u4e8e\u9047\u4e0a\u9b3c\u4e86\u3002 \u3000\u3000\u5728\u4e00\u4e2a\u7f8e\u4e3d\u5c11\u5973\u7684\u95fa\u623f\u4e2d\uff0c\u88ab\u4eba\u7528\u836f\u7269\u8ff7\u5012\u3002 \u3000\u3000\u88ab\u4eba\u5728\u8138\u4e0a\u5212\u4e86\u4e00\u5341\u516b\u5200\uff0c\u5207\u53bb\u4e86\u90a3\u4e00\u5f20\u4f7f\u5973\u4eba\u7740\u8ff7\u7684\u8138\u513f\u3002 \u3000\u3000\u672c\u6765\uff0c\u4ed6\u8981\u88ab\u4e71\u5200\u5206\u5c38\u7684\uff0c\u8fd8\u662f\u90a3\u4f4d\u88ab\u4eba\u5229\u7528\u8bbe\u8ba1\u8bf1\u4ed6\u4e0a\u94a9\u7684\u5c11\u5973\uff0c\u820d\u6b7b\u62fc\u547d\u6551\u4e86\u4ed6\u3002 \u3000\u3000\u90a3\u4f4d\u7f8e\u4e3d\u7684\u59d1\u5a18\uff0c\u8eab\u53d7\u56db\u5904\u5200\u4f24\uff0c\u7ec8\u4e8e\u51fb\u9000\u4e86\u5f3a\u654c\uff0c\u7528\u89e3\u836f\u6551\u9192\u4e86\u4ed6\uff0c\u7136\u540e\uff0c\u5728\u4ed6\u9762\u524d\u81ea\u7edd\u800c\u6b7b\u3002 \u3000\u3000\u9ec4\u7075\u5728\u8fd9\u6b21\u5927\u53d8\u4e4b\u540e\uff0c\u9762\u5bf9\u8840\u75d5\u6591\u6591\u7684\u602a\u8138\uff0c\u60f3\u5230\u4e86\u8fc7\u53bb\u52a8\u4eba\u7684\u82f1\u4fca\uff0c\u671b\u7740\u6a2a\u5728\u8eab\u524d\u7684\u7f8e\u4e3d\u5c11\u5973\u5c38\u4f53\uff0c\u5ffd\u7136\u95f4\u611f\u5230\u4e86\u81ea\u5df1\u7684\u7f6a\u6076\uff0c\u60f3\u5230\u4e86\u8fc7\u53bb\u7684\u9519\u5931\u3002 \u3000\u3000\u6709\u4e9b\u4e8b\uff0c\u4e00\u4e2a\u4eba\u60f3\u4e0a\u4e09\u4e94\u5e74\uff0c\u4e5f\u60f3\u4e0d\u901a\uff0c\u4f46\u5374\u80fd\u5728\u4e00\u77ac\u95f4\uff0c\u60f3\u901a\u5341\u5e74\u60f3\u4e0d\u901a\u7684\u4e8b\u3002 \u3000\u3000\u73b0\u5728\uff0c\u9ec4\u7075\u60f3\u901a\u4e86\uff0c\u8840\u6dcb\u6dcb\u7684\u666f\u8c61\uff0c\u4f7f\u4ed6\u60f3\u8d77\u4e86\u5f80\u4e8b\u3002 \u3000\u3000\u5f80\u4e8b\u90fd\u662f\u7f6a\u6076\u3002 \u3000\u3000\u7531\u90a3\u4e00\u5929\u5f00\u59cb\uff0c\u9ec4\u7075\u53d8\u4e86\u3002 \u3000\u3000\u4ed6\u629b\u53bb\u79c1\u4eba\u7684\u4ec7\u6068\uff0c\u51b3\u5fc3\u6539\u8fc7\u5411\u5584\uff0c\u505a\u4e00\u4e9b\u6709\u76ca\u4e8e\u4eba\u95f4\u548c\u6c5f\u6e56\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u51ed\u4ed7\u65e0\u6bd4\u7684\u806a\u660e\uff0c\u548c\u52ab\u53d6\u7684\u8d22\u5bcc\uff0c\u7ecf\u8425\u4e86\u5f88\u591a\u7684\u751f\u610f\u3002 \u3000\u3000\u4ee5\u9ec4\u7075\u7684\u806a\u660e\uff0c\u5f88\u5feb\u5730\u4e86\u89e3\u5230\uff0c\u4e00\u4e2a\u4eba\u7684\u6b66\u529f\u518d\u9ad8\u5f3a\uff0c\u529b\u91cf\u7ec8\u5c5e\u6709\u9650\uff0c\u771f\u8981\u5728\u6c5f\u6e56\u4e0a\u6709\u4e00\u756a\u4f5c\u4e3a\uff0c\u5fc5\u8981\u6709\u4e00\u4e2a\u7ec4\u7ec7\u3002\u7ec4\u5408\u8d8a\u5e9e\u5927\uff0c\u6548\u7528\u4e5f\u8d8a\u5927\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u7ec4\u6210\u4e86\u9634\u9633\u5821\u3002 \u3000\u3000\u4e3a\u4e86\u8fd9\u4e2a\u7ec4\u7ec7\u80fd\u5f88\u5feb\u53d1\u6325\u51fa\u5f3a\u5927\u7684\u529b\u91cf\uff0c\u6240\u4ee5\u4ed6\u5f00\u59cb\u5229\u7528\u7eff\u6797\u9053\u4e0a\u7684\u4eba\u7269\u3002 \u3000\u3000\u9ed1\u9053\u4eba\u7269\u7684\u624b\u6bb5\uff0c\u867d\u7136\u6b8b\u9177\u4e00\u4e9b\uff0c\u4f46\u4ed6\u4eec\u6548\u7387\u5374\u5341\u5206\u5f3a\u5927\u3002 \u3000\u3000\u9ec4\u7075\u8fc7\u53bb\u6700\u559c\u7231\u5973\u8272\uff0c\u4f46\u4ed6\u7ecf\u8fc7\u90a3\u6b21\u5927\u53d8\u4e4b\u540e\uff0c\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u5c31\u662f\u6212\u53bb\u4e86\u597d\u8272\u4e4b\u5fc3\u3002 \u3000\u3000\u9ec4\u7075\u6234\u4e0a\u4e86\u4eba\u76ae\u9762\u5177\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u957f\u9752\u5144\u3001\u534e\u59d1\u5a18\u3001\u5c60\u5144\uff0c\u548c\u533a\u533a\u5728\u4e0b\uff0c\u90fd\u6709\u7740\u4e00\u6bb5\u4e0d\u8db3\u4e3a\u5916\u4eba\u77e5\u9053\u7684\u5f80\u4e8b\uff0c\u4f46\u4e5f\u4f7f\u6211\u4eec\u629b\u53bb\u4e01\u540d\u5229\u7684\u67b7\u9501\uff0c\u6211\u4eec\u53ea\u6c42\u4e8b\u60c5\u5b8c\u7f8e\uff0c\u4e0d\u6c42\u95fb\u8fbe\u6c5f\u6e56\uff0c\u6211\u4eec\u6ca1\u6709\u540d\u95e8\u5927\u6d3e\u90a3\u4e9b\u91cd\u91cd\u95e8\u89c4\u7684\u675f\u7f1a\uff0c\u505a\u4e8b\u7684\u6548\u7387\u4e5f\u5341\u5206\u5f3a\u5927\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u73b0\u5728\u6211\u603b\u7b97\u660e\u767d\u4e86\uff0c\u4f60\u4eec\u624d\u662f\u6700\u53ef\u656c\u7684\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u8bf4\u8d77\u4e86\u9ec4\u5144\u7684\u4f5c\u4e3a\uff0c\u90a3\u771f\u4ee4\u6c5f\u6e56\u540d\u95e8\u5927\u6d3e\u60ed\u6127\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e5f\u5e78\u597d\u6c5f\u6e56\u4e0a\u6709\u8fd9\u4e9b\u4eba\u7269\u548c\u529b\u91cf\u5b58\u5728\uff0c\u624d\u4fdd\u6709\u6b63\u4e49\u4e4b\u706b\u4e0d\u7184\uff0c\u53f6\u67d0\u4e0d\u624d\uff0e\u613f\u4ee5\u5168\u529b\u6548\u547d\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u9053\uff1a\u201c\u6211\u4eec\u6700\u9700\u8981\u53f6\u5144\u8fd9\u6837\u7684\u4eba\u624d\uff0c\u5e74\u5c11\u6709\u4e3a\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8fd1\u5e74\u6765\uff0c\u6c5f\u6e56\u4e0a\u4f3c\u662f\u6709\u51e0\u80a1\u795e\u79d8\u7684\u529b\u91cf\uff0c\u5728\u9690\u9690\u4e2d\u6210\u957f\uff0c\u6211\u5df2\u7ecf\u628a\u8fd9\u4e9b\u6d88\u606f\uff0c\u9001\u5165\u4e86\u5c11\u6797\u3001\u6b66\u5f53\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u4eec\u6709\u4e9b\u4ec0\u4e48\u53cd\u5e94\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u6d3e\u5a01\u671b\u4e4b\u91cd\uff0c\u5b9e\u529b\u4e4b\u5f3a\uff0c\u5982\u82e5\u80fd\u65e9\u4e9b\u7740\u624b\u8ffd\u67e5\u5185\u60c5\uff0c\u6216\u8bb8\u53ef\u4ee5\u9632\u60a3\u672a\u7136\uff0c\u4f46\u4ed6\u4eec\u5374\u7f6e\u82e5\u7f54\u95fb\u3002\u201d\u534e\u5999\u771f\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u4eec\u53ea\u597d\u81ea\u5df1\u8ffd\u67e5\u4e86\uff0c\u5148\u627e\u51fa\u4e86\u5723\u6c34\u7684\u9690\u79d8\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u5723\u6c34\uff0c\u4f1a\u6709\u4ec0\u4e48\u9690\u79d8\u5462\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u5723\u6c34\u8fd9\u79d8\uff0c\u8fd8\u6ca1\u6709\u4f20\u904d\u6c5f\u6e56\uff0c\u4e5f\u8bb8\u5404\u6b63\u5927\u95e8\u6d3e\uff0c\u8fd8\u4e0d\u592a\u6e05\u695a\uff0c\u53ef\u662f\u8fd9\u4ef6\u4e8b\uff0c\u5728\u9ed1\u9053\u4e2d\uff0c\u5374\u5df2\u6380\u8d77\u4e86\u5de8\u5927\u7684\u98ce\u6d6a\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u65e2\u79f0\u4e4b\u4e3a\u6c34\uff0c\u4e0d\u8fc7\u662f\u5730\u4e0b\u6216\u6cc9\u6c34\u4e2d\u53d6\u5f97\u4e4b\u7269\uff0c\u7eb5\u6709\u5947\u5f02\u4e4b\u5904\uff0c\u5de6\u53f3\u4e5f\u4e0d\u8fc7\u662f\u6c34\u7f62\u4e86\uff0c\u600e\u4f1a\u5f15\u8d77\u9ed1\u9053\u4eba\u7269\u4e2d\u5982\u6b64\u5173\u6ce8\u3001\u4e89\u593a\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c60\u5144\uff0c\u90a3\u5723\u6c34\u6709\u4ec0\u4e48\u5947\u602a\u4e4b\u5904\uff0c\u7adf\u80fd\u5f15\u8d77\u6c5f\u6e56\u9053\u4e0a\u5982\u6b64\u7684\u91cd\u89c6\u4e0e\u4e89\u593a\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u542c\u8bf4\uff0c\u90a3\u5723\u6c34\u51fa\u81ea\u4e24\u9053\u5947\u6cc9\u4e4b\u4e2d\uff0c\u4e00\u79cd\u53ef\u4ee5\u7597\u6cbb\u767e\u75c5\uff0c\u53e6\u4e00\u79cd\u53ef\u5236\u6210\u4e89\u9738\u6c5f\u6e56\u7684\u5229\u5668\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5c60\u5144\u4e5f\u6ca1\u6709\u89c1\u8fc7\u5723\u6c34\u4e86\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5723\u6c34\u5728\u6b64\u4ea4\u6613\uff0c\u8fd9\u4e2a\u5c0f\u9547\u4e5f\u56e0\u5723\u6c34\u800c\u5f97\u540d\uff0c\u4e0d\u8fc7\uff0c\u7a76\u7adf\u5723\u6c34\u662f\u4ec0\u4e48\u6837\u5b50\uff0c\u5728\u4e0b\u6ca1\u6709\u89c1\u8fc7\u3002\u6bcf\u4e00\u5e74\uff0c\u8fd9\u91cc\u6709\u4e00\u6b21\u4ea4\u6613\uff0c\u6709\u5f88\u591a\u9ed1\u9053\u4eba\u7269\u96c6\u5c45\u4e8e\u6b64\uff0c\u6709\u65f6\u2014\u4f4f\u6570\u6708\u4e4b\u4e45\uff0c\u53ea\u77e5\u4ed6\u4eec\u5728\u5723\u6c34\u9547\u4e2d\u4ea4\u6613\uff0c\u4f46\u5374\u4e00\u76f4\u89c1\u4e0d\u5230\u4ed6\u4eec\u548c\u4f55\u4eba\u4ea4\u6613\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u5c0f\u5c0f\u7684\u5723\u6c34\u9547\uff0c\u7adf\u7136\u5982\u6b64\u795e\u79d8\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u5618\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u522b\u5c0f\u770b\u8fd9\u4e00\u5ea7\u5c0f\u5c0f\u7684\u5723\u6c34\u9547\uff0c\u6bcf\u5e74\u5728\u4ea4\u6613\u4e4b\u671f\uff0c\u4e91\u96c6\u4e86\u5f88\u591a\u7684\u9ed1\u9053\u5de8\u64d8\uff0c\u4e0d\u8bba\u4f55\u4eba\uff0c\u5982\u679c\u60f3\u5728\u6b64\u5730\u4e71\u95ef\uff0c\u52bf\u5fc5\u4f1a\u5f15\u8d77\u6781\u5927\u7684\u7eb7\u4e89\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u8fd9\u4e48\u591a\u9ad8\u4eba\u96c6\u5c45\u4e8e\u5723\u6c34\u9547\uff0c\u96be\u9053\u8fd9\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5730\u65b9\uff0c\u5c31\u67e5\u4e0d\u51fa\u4e00\u70b9\u7709\u76ee\u5417\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5c31\u662f\u67e5\u4e0d\u51fa\u6765\uff0c\u795e\u79d8\u5904\u4e5f\u5c31\u5728\u6b64\u4e86\uff0c\u6bcf\u5e74\u4ea4\u6613\u4e4b\u671f\uff0c\u6211\u4eec\u56fa\u7136\u6d3e\u51fa\u4e86\u5f88\u591a\u4eba\u5728\u6697\u67e5\u8bbf\uff0c\u5c31\u662f\u90a3\u4e9b\u96c6\u4e2d\u4e8e\u6b64\u7684\u9ed1\u9053\u5de8\u64d8\u4e5f\u90fd\u6d3e\u4e86\u8033\u76ee\uff0c\u5e0c\u671b\u80fd\u67e5\u51fa\u4e00\u70b9\u7709\u76ee\u6765\uff0c\u4f46\u4ed6\u4eec\u4ecd\u7136\u6bcf\u5e74\u5728\u79d8\u5bc6\u4e2d\u5b8c\u6210\u4ea4\u6613\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\uff0c\u4ed6\u4eec\u65e2\u7136\u5728\u79d8\u871c\u4e2d\u5b8c\u6210\u4e86\u4ea4\u6613\uff0c\u522b\u4eba\u53c8\u5982\u4f55\u77e5\u6653\u5462\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5947\u602a\u7684\u662f\uff0c\u4ed6\u4eec\u7684\u4ea4\u6613\uff0c\u662f\u90a3\u4e48\u9690\u79d8\uff0c\u4f46\u4e8b\u540e\u5374\u6cc4\u6f0f\u51fa\u6765\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\uff0c\u4ed6\u4eec\u8fd9\u4e9b\u4ea4\u6613\uff0c\u662f\u4e0d\u662f\u6bcf\u5e74\u90fd\u6709\u56fa\u5b9a\u7684\u7528\u6237\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u6211\u5df2\u7ecf\u6253\u542c\u5f97\u5f88\u6e05\u695a\u4e86\uff0c\u4ed6\u4eec\u5e76\u975e\u662f\u5356\u7ed9\u4e00\u4e2a\u4eba\uff0c\u5982\u662f\u56fa\u5b9a\u7684\u4e00\u4e2a\u4eba\uff0c\u81ea\u7136\u4e5f\u4e0d\u4f1a\u62db\u63fd\u8fd9\u4e48\u591a\u4eba\u5230\u5723\u6c34\u9547\u6765\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\uff0c\u6709\u4e00\u70b9\uff0c\u5144\u5f1f\u6709\u4e9b\u60f3\u4e0d\u901a\uff0c\u8fd9\u5723\u6c34\u4ea4\u6613\u5982\u6b64\u795e\u79d8\uff0c\u600e\u4f1a\u6709\u8fd9\u4e48\u591a\u4eba\u8d76\u6765\u5462\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u53ef\u4ee5\u5206\u4e24\u65b9\u9762\u89e3\u8bf4\uff0c\u4e00\u662f\uff0c\u90a3\u5723\u6c34\u786e\u6709\u4e0d\u540c\u51e1\u54cd\u7684\u4ef7\u503c\uff1b\u4e8c\u662f\uff0c\u4ed6\u4eec\u6709\u4e00\u5957\u5f88\u4e25\u5bc6\u7684\u4ea4\u6613\u5b89\u6392\uff0c\u6240\u4ee5\uff0c\u867d\u7136\u5f88\u591a\u4eba\u5f97\u5230\u901a\u77e5\uff0c\u4f46\u5374\u5341\u4e4b\u516b\u4e5d\u843d\u7a7a\uff0c\u800c\u4e14\uff0c\u8fd8\u6ca1\u6709\u60f9\u8d77\u4e89\u5723\u6c34\u7684\u706b\u5e76\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\uff0c\u6211\u770b\u6700\u597d\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u60f3\u6cd5\u5b50\uff0c\u53c2\u4e0e\u8fd9\u573a\u4ea4\u6613\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u6211\u4eec\u4e5f\u8bd5\u8fc7\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u901a\u77e5\u8fc7\u54b1\u4eec\u4ea4\u6613\u7684\u65b9\u6cd5\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u63a5\u5230\u8fc7\u4e00\u6b21\u901a\u77e5\uff0c\u90a3\u662f\u4e00\u4efd\u5f88\u5947\u602a\u7684\u901a\u77e5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u901a\u77e5\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u90a3\u4e00\u4efd\u901a\u77e5\u4e0a\u8bf4\uff0c\u53c2\u4e0e\u4e4b\u4eba\uff0c\u5fc5\u987b\u8981\u6709\u6052\u5fc3\uff0c\u4ed6\u4eec\u8981\u53c2\u4e0e\u4e09\u5e74\u4e4b\u540e\uff0c\u624d\u80fd\u8c08\u5230\u771f\u6b63\u7684\u4ea4\u6613\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u8fd8\u6709\u8fd9\u4e48\u591a\u4e25\u683c\u7684\u9650\u5236\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6211\u4eec\u53c2\u4e0e\u6b64\u4e8b\uff0c\u5df2\u7ecf\u4e09\u5e74\u4e86\uff0c\u4eca\u5e74\u7ec8\u4e8e\u63a5\u5230\u4e86\u4e00\u4efd\u901a\u77e5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u901a\u77e5\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e00\u4efd\u51fa\u4ef7\u7684\u5355\u5b50\uff0c\u8981\u6211\u4eec\u586b\u4e0a\u94f6\u94b1\u6570\u76ee\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u5c60\u5144\u586b\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u586b\u8fc7\u4e86\uff0c\u6211\u60f3\u8fd9\u5c31\u662f\u4ed6\u4eec\u79d8\u5bc6\u4ea4\u6613\u7684\u7b2c\u4e00\u6b65\uff0c\u54ea\u4e00\u4e2a\u586b\u7684\u6570\u76ee\u6700\u9ad8\uff0c\u4ed6\u4eec\u5c31\u53ef\u80fd\u548c\u4ed6\u63a5\u5934\u6d3d\u5546\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u5c60\u5144\uff0c\u54b1\u4eec\u4f55\u59a8\u586b\u5f97\u591a\u4e00\u4e9b\uff0c\u8fd9\u4e00\u7b14\u94b1\uff0c\u53cd\u6b63\uff0c\u54b1\u4eec\u4e5f\u4e0d\u51c6\u5907\u4ed8\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u95ee\u9898\u662f\uff0c\u8fd8\u4e0d\u80fd\u80e1\u4e71\u586b\u4ef7\uff0c\u56e0\u4e3a\u968f\u7740\u90a3\u4e00\u5f20\u4ef7\u76ee\u5355\uff0c\u8981\u9644\u4e0a\u5341\u5206\u4e4b\u4e00\u7684\u94f6\u7968\uff0c\u4e00\u65e6\u4ed6\u4eec\u51b3\u5b9a\u4e86\uff0c\u5c31\u8981\u6309\u90a3\u586b\u4e0a\u7684\u5355\u5b50\u4ed8\u94b1\uff0c\u5982\u679c\u4e0d\u80fd\u4ed8\u6b3e\u3002\u90a3\u5341\u5206\u4e4b\u4e00\u7684\u94b1\uff0c\u5c31\u6ce1\u6c64\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u597d\u8fa3\u7684\u624b\u6bb5\uff0c\u5c60\u5144\uff0c\u5982\u662f\u4ed6\u4eec\u9009\u4e0d\u4e0a\u6211\u4eec\u5462\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u9009\u4e0d\u4e0a\uff0c\u4ed6\u4eec\u4f1a\u9000\u56de\u94f6\u5b50\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u53ef\u6709\u8981\u5144\u5f1f\u6548\u52b3\u7684\u5730\u65b9\u5417\uff1f\u6211\u5c31\u4e0d\u4fe1\u8fd9\u5c0f\u5c0f\u7684\u5723\u6c34\u9547\uff0c\u96be\u9053\u4f1a\u67e5\u4e0d\u51fa\u90a3\u4e2a\u4ea4\u6613\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u4e00\u5b9a\u8981\u501f\u91cd\u5927\u529b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u501f\u91cd\u5012\u4e0d\u6562\u5f53\uff0c\u5982\u662f\u6709\u8981\u5144\u5f1f\u6548\u52b3\u4e4b\u5904\uff0c\u5144\u5f1f\u5b9a\u4f1a\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u957f\u9752\uff0c\u8fd8\u6709\u4e00\u4e2a\u5251\u4f7f\u56e0\u4e8b\u7f20\u8eab\uff0c\u4e0d\u80fd\u8d76\u5230\uff0c\u4e0d\u8fc7\uff0c\u90a3\u4e00\u4f4d\uff0c\u4e5f\u7528\u4e0d\u7740\u548c\u4f60\u89c1\u9762\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u662f\u2026\u2026\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5929\u5e73\u5200\u4e07\u80dc\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u539f\u6765\u662f\u4e07\u8001\u82f1\u96c4\uff0c\u90a3\u53ef\u662f\u540d\u95fb\u5927\u6c5f\u5357\u5317\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e07\u8001\u662f\u6c5f\u6e56\u9053\u4e0a\u516c\u8ba4\u7684\u4e00\u4ee3\u4fa0\u58eb\uff0c\u53f6\u5144\u4e0d\u4f1a\u5bf9\u4ed6\u53d1\u751f\u8bef\u4f1a\u5427\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u8138\u4e00\u7ea2\u9053\uff1a\u201c\u5144\u5f1f\u5c11\u4e0d\u66f4\u4e8b\uff0c\u5982\u6709\u5f00\u7f6a\u5404\u4f4d\u4e4b\u5904\uff0c\u8fd8\u671b\u5404\u4f4d\u591a\u591a\u539f\u8c05\u3002\u201d \u3000\u3000\u9ec4\u7075\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e8b\u60c5\u5982\u679c\u4e0d\u8bf4\u4e2a\u6e05\u695a\uff0c\u96be\u514d\u53f6\u5144\u8bef\u4f1a\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e00\u70b9\uff0c\u6211\u60f3\u8fd8\u8981\u5bf9\u53f6\u5144\u8bf4\u4e2a\u660e\u767d\uff0c\u6211\u4eec\u5fc3\u5b58\u6b63\u4e49\uff0c\u5929\u795e\u53ef\u9274\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u505a\u4e8b\u7684\u65b9\u6cd5\u3001\u624b\u6bb5\uff0c\u53ef\u548c\u90a3\u4e9b\u6b63\u5927\u95e8\u6d3e\u5b8c\u5168\u4e0d\u540c\u3002\u201d\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4eec\u884c\u4e8b\uff0c\u6548\u7387\u7b2c\u4e00\uff0c\u6709\u4e9b\u5730\u65b9\uff0c\u96be\u514d\u4f1a\u8fc7\u6fc0\u4e00\u4e9b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9634\u9633\u5821\u7edf\u7387\u4e4b\u4e0b\uff0c\u5927\u90fd\u662f\u7eff\u6797\u9053\u4e2d\u4eba\u7269\uff0c\u54c1\u6d41\u6df7\u6742\uff0c\u9ec4\u5821\u4e3b\u5fc3\u5b58\u5927\u613f\uff0c\u5e0c\u671b\u628a\u8fd9\u4e9b\u51f6\u795e\u6076\u715e\uff0c\u9ed8\u5316\u6f5c\u79fb\u6210\u6709\u7528\u4e4b\u4eba\uff0c\u8d39\u5c3d\u5fc3\u673a\uff0c\u60f3\u4e86\u4e0d\u5c11\u529e\u6cd5\uff0c\u8fd9\u4e9b\u4eba\u4e2d\uff0c\u4e5f\u786e\u6709\u4e0d\u5c11\u53d8\u5316\u4e86\u6c14\u8d28\uff0c\u771f\u662f\u51a5\u987d\u4e0d\u5316\u4e4b\u4eba\uff0c\u81ea\u7136\u4e5f\u4e0d\u4f1a\u59d1\u606f\uff0c\u4f46\u9634\u9633\u5821\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u8a89\uff0c\u5374\u662f\u4e0d\u600e\u4e48\u597d\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u5728\u9634\u9633\u5821\u4e2d\u51fa\u73b0\uff0c\u5b8c\u5168\u662f\u5821\u4e3b\u8eab\u4efd\u2026\u2026\u201d \u3000\u3000\u7b11\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u4f46\u6c14\u6307\u9890\u4f7f\uff0c\u800c\u4e14\uff0c\u51f6\u5389\u975e\u5e38\uff0c\u6709\u4e00\u6b21\uff0c\u6211\u66fe\u7ecf\u4e0b\u4ee4\u5904\u7f6e\u4e86\u4e00\u5341\u516b\u4e2a\u5821\u4e2d\u7684\u6b66\u5e08\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u81ea\u7136\u662f\u56e0\u4e3a\u90a3\u5341\u516b\u4e2a\u4eba\u51a5\u987d\u4e0d\u5316\uff0c\u5b8c\u5168\u6ca1\u6709\u6539\u9020\u7684\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\u4e0b\u4ee4\u8fde\u7eed\u5904\u7f6e\u4e86\u4e00\u5341\u516b\u4eba\uff0c\u90a3\u5c82\u4e0d\u662f\u4f7f\u5f97\u6574\u5ea7\u7684\u9634\u9633\u5821\u4e3a\u4e4b\u9707\u52a8\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5bf9\uff01\u6574\u4e2a\u7684\u9634\u9633\u5821\u4e3a\u4e4b\u9707\u52a8\uff0c\u4e0d\u8fc7\uff0c\u5821\u4e3b\u662f\u4e00\u4f4d\u5f88\u7cbe\u7ec6\u7684\u4eba\uff0c\u4ed6\u627e\u5230\u4e86\u4e00\u4e2a\u5f88\u597d\u7684\u7406\u7531\uff0c\u5904\u7f6e\u4ed6\u4eec\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u7559\u7ed9\u522b\u4eba\u7684\u6000\u7591\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9634\u9633\u5821\u6709\u4e00\u79cd\u91d1\u661f\u4ee4\u724c\uff0c\u5c31\u4ee3\u8868\u4e86\u5821\u4e3b\uff0c\u91d1\u661f\u4ee4\u724c\u4e00\u65e6\u4f20\u51fa\uff0c\u4e0d\u8bba\u4f55\u4eba\uff0c\u90fd\u5fc5\u987b\u9075\u5b88\u4ee4\u8c15\uff0c\u9650\u671f\u4e4b\u5185\uff0c\u5b8c\u6210\u4ee4\u8c15\u6240\u793a\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u90a3\u662f\u9634\u9633\u5821\u4e2d\uff0c\u6700\u5177\u6743\u5a01\u7684\u4e00\u79cd\u4ee4\u8c15\uff0c\u82e5\u4e0d\u80fd\u5b8c\u6210\u8fd9\u4ee4\u8c15\u4ea4\u529e\u7684\u4e8b\uff0c\u53ea\u6709\u4e00\u6761\u6b7b\u8def\u53ef\u884c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5e2e\u4e3b\u8bf7\u6055\u5728\u4e0b\u6597\u80c6\u76f4\u8a00\uff0c\u9634\u9633\u5821\u4e3b\uff0c\u5982\u662f\u7fa4\u96c6\u4e86\u9ed1\u9053\u715e\u661f\uff0c\u90a3\u4e9b\u4eba\uff0c\u90fd\u4e0d\u662f\u7b80\u5355\u4eba\u7269\uff0c\u4ed6\u4eec\u96be\u9053\u5c31\u4e0d\u4f1a\u52a8\u7591\u5417\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2d\u95f4\uff0c\u6709\u4e00\u4e2a\u5f88\u5927\u7684\u539f\u56e0\uff0c\u90a3\u5c31\u662f\u5821\u4e3b\u5728\u4ed6\u4eec\u5fc3\u76ee\u4e2d\u7684\u58f0\u671b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u539f\u56e0\u662f\uff0c\u5821\u4e3b\u96be\u5f97\u548c\u4ed6\u4eec\u89c1\u4e00\u6b21\u9762\uff0c\u4ed6\u4eec\u5fc3\u76ee\u4e2d\u7684\u5821\u4e3b\uff0c\u5341\u5206\u795e\u79d8\uff0c\u8fd9\u79cd\u795e\u79d8\uff0c\u5728\u4ed6\u4eec\u5fc3\u76ee\u4e2d\u4ea7\u751f\u51fa\u4e86\u4e00\u79cd\u83ab\u53ef\u6bd4\u62df\u7684\u529b\u91cf\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5f97\u8bf8\u4f4d\u6307\u70b9\uff0c\u4f7f\u5728\u4e0b\u53c8\u5f00\u4e86\u4e0d\u5c11\u7684\u8305\u585e\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u53f6\u5144\uff0c\u8fd8\u6709\u4ec0\u4e48\u9700\u8981\u95ee\u660e\u767d\u7684\u5730\u65b9\uff0c\u53ea\u7ba1\u5f00\u53e3\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5728\u4e0b\u5df2\u7ecf\u77e5\u9053\u4e86\u4e00\u4e2a\u5927\u6982\u7684\u60c5\u5f62\u4e0b\uff0c\u6b64\u540e\uff0c\u76f8\u5904\u7684\u65f6\u95f4\u8fd8\u957f\uff0c\u5728\u4e0b\u5982\u6709\u4e0d\u89e3\uff0c\u518d\u5411\u8bf8\u4f4d\u8bf7\u6559\uff1a\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff01\u53f6\u5144\uff0c\u65e2\u7136\u5fc3\u4e2d\u5df2\u518d\u65e0\u6000\u7591\u4e4b\u5904\uff0c\u7531\u6b64\u523b\u8d77\uff0c\u4f60\u5185\u5fc3\u4e4b\u4e2d\u662f\u5426\u5df2\u7ecf\u627f\u8ba4\u63a5\u4e0b\u4e86\u5251\u4f7f\u4e4b\u4f4d\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u8981\u4e0b\u7b2c\u4e00\u9053\u4ee4\u8c15\u7ed9\u4f60\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u970d\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u957f\u9752\u606d\u5019\u4ee4\u4e0b\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u9053\uff1a\u201c\u4f60\u5750\u5427\uff01\u5bf9\u5728\u5ea7\u8bf8\u4f4d\uff0c\u6211\u90fd\u4ee5\u670b\u53cb\u770b\u5f85\uff0c\u6211\u8fd9\u4efd\u5fc3\u613f\uff0c\u80fd\u5426\u5b9e\u73b0\uff0c\u5c31\u8981\u4ed7\u51ed\u8bf8\u4f4d\u8f9b\u82e6\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ec4\u5144\uff0c\u6211\u4eec\u90fd\u4f1a\u5c3d\u529b\u800c\u4e3a\uff0c\u4f60\u6709\u4ec0\u4e48\u4e8b\uff0c\u53ea\u7ba1\u5429\u5490\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5bf9\uff01\u5821\u4e3b\u53ea\u8981\u5429\u5490\u4e00\u58f0\uff0c\u6211\u7b49\u5fc5\u5c06\u5168\u529b\u4ee5\u8d74\uff0c\u867d\u6b7b\u65e0\u61be\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u957f\u9752\uff0c\u4f60\u548c\u5c60\u5144\u7559\u5728\u5723\u6c34\u9547\uff0c\u5728\u8fd9\u91cc\u6211\u4eec\u4e5f\u6709\u4e00\u4e9b\u90e8\u7f72\uff0c\u4f60\u548c\u5c60\u5144\u8d1f\u8d23\u7684\uff0c\u5c31\u662f\u62e6\u622a\u5723\u6c34\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u5148\u4e0b\u624b\u62a2\uff1f\u201d \u3000\u3000\u9ec4\u7075\u7b11\u9053\uff1a\u201c\u53ef\u4ee5\uff0c\u4e0d\u8bba\u7528\u4ec0\u4e48\u65b9\u6cd5\uff0c\u4f60\u4eec\u7684\u76ee\u7684\uff0c\u5c31\u662f\u8981\u53d6\u5f97\u5723\u6c34\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53d6\u5230\u5723\u6c34\uff0c\u6700\u5927\u7684\u56f0\u96be\uff0c\u5c31\u662f\u4e0d\u77e5\u9053\u5723\u6c34\u5728\u4f55\u5904\uff0c\u54ea\u4e00\u5929\u5230\u5723\u6c34\u9547\uff0c\u5982\u4f55\u8fd0\u9001\u6765\uff0c\u53c8\u5982\u4f55\u4ea4\u7ed9\u4eba\u8fd0\u8d70\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\uff0c\u54b1\u4eec\u96be\u9053\u4e0d\u4f1a\u5b88\u4f4f\u56db\u95e8\uff0c\u6211\u5c31\u4e0d\u76f8\u4fe1\u4f1a\u77a7\u4e0d\u51fa\u4e00\u70b9\u86db\u4e1d\u9a6c\u8ff9\u6765\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u56f0\u96be\u7684\u539f\u56e0\uff0c\u5c31\u662f\u6211\u4eec\u8fd8\u4e0d\u80fd\u505a\u5f97\u592a\u9732\u9aa8\uff0c\u7834\u574f\u4e86\u5723\u6c34\u4ea4\u6613\uff0c\u5f88\u53ef\u80fd\u4f1a\u5f15\u8d77\u5723\u6c34\u9547\u4e2d\u9690\u85cf\u7684\u9ad8\u624b\uff0c\u5168\u9762\u653b\u51fb\uff0c\u90a3\u5c31\u53d8\u6210\u4e86\u4f17\u77e2\u4e4b\u7684\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u957f\u9752\uff0c\u4f60\u7559\u4e0b\u548c\u5c60\u5251\u4f7f\u597d\u597d\u7684\u8c08\u8c08\uff0c\u7814\u7a76\u51fa\u4e2a\u529e\u6cd5\u6765\uff0c\u5927\u6982\u5341\u5929\u5de6\u53f3\u6211\u518d\u56de\u6b64\u5730\uff0c\u6211\u8981\u5148\u8d70\u4e00\u6b65\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u3001\u534e\u5999\u771f\u4e00\u8d77\u7ad9\u4e01\u8d77\u6765\uff0c\u968f\u9ec4\u7075\u8eab\u540e\u800c\u53bb\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5723\u6c34\u7684\u9690\u79d8\uff0c\u4e0d\u4f46\u5728\u5821\u4e3b\u5185\u5fc3\u4e2d\u6784\u6210\u4e86\u4e00\u4e2a\u8d1f\u62c5\uff0c\u4e5f\u6210\u4e86\u6c5f\u6e56\u4e0a\u4e00\u4e2a\u79d8\u5bc6\uff0c\u6211\u4eec\u73b0\u5728\u8981\u4f5c\u7684\uff0c\u5c31\u662f\u89e3\u5f00\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u9664\u53bb\u5821\u4e3b\u5fc3\u4e0a\u7684\u8d1f\u62c5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u54b1\u4eec\u5f97\u597d\u597d\u7684\u60f3\u4e2a\u6cd5\u5b50\uff0c\u52a1\u6c42\u4e00\u51fb\u800c\u4e2d\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5bf9\uff01\u6211\u4eec\u5f97\u597d\u597d\u7684\u5546\u91cf\u4e00\u4e0b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\uff0c\u90a3\u5723\u6c34\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u5462\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u6211\u8fd8\u6ca1\u89c1\u5230\u5723\u6c34\u2026\u2026\u201d \u3000\u3000\u51dd\u76ee\u6c89\u601d\u4e86\u7247\u523b\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u867d\u7136\u6ca1\u6709\u89c1\u8fc7\u5723\u6c34\uff0c\u4f46\u6211\u542c\u5230\u8fc7\u8fd9\u5723\u6c34\u7684\u8bf8\u822c\u795e\u5947\u4f20\u8bf4\uff0c\u800c\u5404\u6b63\u5927\u95e8\u6237\u53ea\u6015\u8fde\u8fd9\u5723\u6c34\u7684\u4f20\u8bf4\uff0c\u4e5f\u672a\u66fe\u542c\u8fc7\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8fd9\u5723\u6c34\u5728\u6c5f\u6e56\u4e0a\u7684\u4f20\u8bf5\uff0c\u5e76\u4e0d\u592a\u5e7f\u6cdb\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5f88\u5e7f\u6cdb\uff0c\u53ea\u4e0d\u8fc7\uff0c\u5b83\u6d41\u4f20\u5e72\u9ed1\u9053\u4e4b\u4e2d\uff0c\u53f6\u8001\u5f1f\u8fc7\u53bb\u53ef\u66fe\u542c\u8fc7\u8fd9\u5723\u6c34\u7684\u4f20\u8bf4\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51dd\u76ee\u6c89\u601d\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u597d\u50cf\u662f\u542c\u8fc7\u4e00\u6b21\uff0c\u4e0d\u8fc7\uff0c\u6ca1\u6709\u592a\u6ce8\u610f\u5b83\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u9ed1\u767d\u4e24\u9053\u4e2d\u4e0d\u540c\u7684\u5730\u65b9\uff0c\u767d\u9053\u4e2d\u4eba\uff0c\u4e0d\u592a\u559c\u6b22\u8fc7\u4e8e\u795e\u5947\u7684\u4e8b\u60c5\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u5bf9\u5723\u6c34\u7684\u4f20\u8bf4\uff0c\u7eb5\u7136\u662f\u542c\u5230\u4e86\uff0c\u4e5f\u4e0d\u4f1a\u653e\u5728\u5fc3\u4e0a\uff1b\u4f46\u9ed1\u9053\u4e2d\u4eba\u5c31\u4e0d\u540c\u4e86\uff0c\u4ed6\u4eec\u6700\u559c\u6b22\u6253\u542c\u90a3\u4e9b\u795e\u795e\u79d8\u79d8\uff0c\u7a00\u5947\u53e4\u602a\u7684\u4e8b\u60c5\uff0c\u5355\u662f\u5723\u6c34\u8fd9\u4e24\u4e2a\u5b87\uff0c\u4f20\u5165\u4ed6\u4eec\u7684\u8033\u6735\u4e2d\uff0c\u5c31\u8be5\u5438\u5f15\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\u4e0d\u4f46\u719f\u6089\u6c5f\u6e56\u4e0a\u7684\u4e8b\u52a1\uff0c\u800c\u4e14\uff0c\u4e5f\u5f88\u719f\u6089\u6c5f\u6e56\u4eba\u7684\u5fc3\u7406\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u54b1\u4eec\u8fd9\u4e2a\u7ec4\u7ec7\uff0c\u4e00\u5411\u505a\u4e8b\u53ea\u6c42\u6548\u7387\u4e0d\u6c42\u95fb\u8fbe\uff0c\u800c\u4e14\uff0c\u4f60\u53f6\u8001\u5f1f\u662f\u6c5f\u6e56\u4e0a\u6709\u540d\u7684\u5251\u624b\uff0c\u4e3a\u4e86\u907f\u514d\u4e00\u4e9b\u4e0d\u5fc5\u8981\u7684\u9ebb\u70e6\uff0c\u6050\u6015\u4f60\u8001\u5f1f\u8fd8\u5f97\u6613\u5bb9\u4e00\u756a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\uff01\u5c60\u5144\u8981\u6211\u4f5c\u4ec0\u4e48\uff1f\u53ea\u7ba1\u5429\u5490\u5c31\u662f\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u4f38\u624b\u7531\u6000\u4e2d\u53d6\u51fa\u2014\u4e2a\u4eba\u76ae\u9762\u5177\uff0c\u9012\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u8fd9\u4e2a\u9762\u5177\u4f60\u6234\u4e0a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c60\u5144\u65e9\u5df2\u51c6\u5907\u4e86\u4e00\u4e2a\u9762\u5177\uff0c\u8fd9\u4e2d\u95f4\u662f\u5426\u6709\u4e00\u4e9b\u9053\u7406\u5462\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6709\uff01\u8fd9\u526f\u9762\u5177\u5f88\u50cf\u2014\u4e2a\u4eba\uff0c\u8fd9\u4e2a\u4eba\uff0c\u8fc7\u53bb\u5e38\u548c\u6211\u5728\u4e00\u5904\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u9b54\u624b\u94c1\u98de\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8840\u624b\u3001\u9b54\u624b\uff0c\u539f\u5949\u662f\u4e00\u5bf9\u642d\u6863\uff0c\u6211\u7adf\u7136\u60f3\u4e0d\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u94c1\u98de\u5728\u4e00\u573a\u6fc0\u70c8\u7684\u640f\u6740\u4e2d\u53d7\u4e86\u91cd\u4f24\uff0c\u4e34\u6b7b\u4e4b\u524d\uff0c\u628a\u4ed6\u5e38\u5e26\u7684\u4e00\u5f20\u4eba\u76ae\u9762\u5177\u4ea4\u7ed9\u4e86\u6211\uff0c\u8981\u6211\u627e\u4e00\u4e2a\u4eba\u4ee3\u8868\u4ed6\uff0c\u4ed6\u8bf4\uff0c\u8840\u624b\u3001\u9b54\u624b\uff0c\u5931\u53bb\u4e00\u4e2a\uff0c\u5c31\u597d\u50cf\u662f\u4e00\u4e2a\u4eba\u65ad\u4e86\u4e00\u6761\u624b\u81c2\u4e00\u6837\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9b54\u624b\u94c1\u98de\u5f53\u5e74\u5c31\u662f\u8fd9\u6837\u6234\u7740\u4eba\u76ae\u9762\u5177\u95ef\u8361\u6c5f\u6e56\u7684\u5417\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\uff0c\u8bf7\u6055\u5728\u4e0b\u591a\u95ee\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u7ec8\u751f\u6234\u7740\u4eba\u76ae\u9762\u5177\u5462\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u5f20\u9762\u5177\uff0c\u5df2\u7ecf\u4e0d\u592a\u597d\u770b\uff0c\u4f46\u4ed6\u7684\u8138\uff0c\u6bd4\u8fd9\u9762\u5177\uff0c\u66f4\u96be\u770b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4ed6\u5012\u4e0d\u662f\u6015\u96be\u770b\uff0c\u53ea\u662f\u6015\u6709\u4e9b\u60ca\u4e16\u9a87\u4fd7\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u76ee\u4e0b\u8ddd\u79bb\u5723\u6c34\u4ea4\u6613\u4e4b\u671f\uff0c\u8fd8\u6709\u4e24\u4e2a\u6708\uff0c\u4f46\u5341\u5929\u4e4b\u540e\uff0c\u5c31\u4f1a\u6709\u5404\u8def\u4eba\u9a6c\uff0c\u6c47\u805a\u5230\u5723\u6c34\u9547\u6765\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u6765\u5f97\u8fd9\u4e48\u65e9\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u8981\u6709\u4e00\u4e2a\u591a\u6708\u7684\u65f6\u95f4\u6298\u51b2\u3001\u6bd4\u4ef7\uff0c\u51b3\u5b9a\u5723\u6c34\u5c5e\u8c01\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u8fd8\u6709\u5341\u5929\u597d\u7b49\uff0c\u5341\u5929\u4e4b\u540e\uff0c\u5f88\u53ef\u80fd\u4f1a\u63a5\u5230\u4e00\u4efd\u901a\u77e5\uff0c\u53bb\u8c08\u4ea4\u6613\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5982\u662f\u6ca1\u6709\u8fd9\u4e00\u4efd\u901a\u77e5\uff0c\u90a3\u5c31\u662f\u628a\u6211\u4eec\u6452\u5f03\u5230\u5723\u6c34\u4ea4\u6613\u4e4b\u5916\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5bf9\uff01\u90a3\u5c31\u8981\u5927\u8d39\u5468\u6298\uff0c\u5821\u4e3b\u5728\u5341\u65e5\u4e4b\u540e\uff0c\u8981\u56de\u6b64\u5730\u4e00\u8d9f\uff0c\u5927\u6982\u4e5f\u548c\u6b64\u4e8b\u6709\u5173\uff0c\u5821\u4e3b\u4f3c\u4e4e\u662f\u5df2\u7ecf\u4e0b\u4e86\u51b3\u5fc3\uff0c\u8fd9\u4e00\u6b21\uff0c\u4e00\u5b9a\u8981\u53d6\u5230\u5723\u6c34\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\u8eab\u8d1f\u5927\u4efb\uff0c\u5e0c\u671b\u80fd\u66ff\u5821\u4e3b\u5206\u5fe7\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u672c\u6765\uff0c\u6211\u662f\u6709\u4e9b\u60f6\u6050\uff0c\u4e00\u518d\u8bf7\u6c42\u5821\u4e3b\uff0c\u7559\u4e0b\u4f55\u5bd2\u8863\u6216\u662f\u534e\u5999\u771f\uff0c\u52a9\u6211\u4e00\u81c2\uff0c\u4f46\u4ed6\u4eec\u4e24\u4f4d\u90fd\u6709\u5927\u4efb\u5728\u8eab\u65e0\u6cd5\u7559\u4e0b\uff0c\u5374\u672a\u60f3\u5230\u4f1a\u6709\u4e86\u4f60\u53f6\u8001\u5f1f\u8fd9\u4e48\u4e00\u4e2a\u597d\u5e2e\u624b\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u53ea\u6015\u5728\u4e0b\u7684\u80fd\u529b\u6709\u9650\uff0c\u5bf9\u5c60\u5144\u6ca1\u6709\u5927\u52a9\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53f6\u8001\u5f1f\u4e0d\u7528\u5ba2\u6c14\uff0c\u5821\u4e3b\u770b\u4e0a\u4f60\uff0c\u4f55\u8001\u5f1f\u548c\u4f60\u52a8\u8fc7\u624b\uff0c\u4ed6\u5bf9\u4f60\u7684\u8bc4\u4ef7\u5f88\u9ad8\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5c60\u5144\uff0c\u6211\u8d25\u5728\u4f55\u5bd2\u8863\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7b11\u9053\uff1a\u201c\u4f55\u8001\u5f1f\u6240\uff0c\u4f60\u5982\u518d\u591a\u51e0\u5206\u6597\u5fd7\uff0c\u9e7f\u6b7b\u8c01\u624b\u5f88\u96be\u9884\u6599\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u597d\u597d\u4f11\u606f\u4e00\u4e0b\uff0c\u660e\u5929\uff0c\u6211\u5e26\u4f60\u5230\u4e00\u5904\u5730\u65b9\u77a7\u77a7\uff0c\u89c1\u51e0\u4e2a\u4eba\u3002\u201d \u3000\u3000 \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u53f6\u957f\u9752\u4e00\u76f4\u5728\u7b49\uff0c\u7b49\u5230\u4e0b\u592a\u9633\u5feb\u8981\u4e0b\u5c71\u7684\u65f6\u5019\uff0c\u5c60\u65e0\u65b9\u624d\u627e\u4e0a\u95e8\u6765\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f11\u606f\u597d\u5382\u6ca1\u6709\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5144\u5f1f\u5df2\u606d\u5019\u4e0b\u5927\u534a\u5929\u4e0b\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u6628\u5929\uff0c\u6211\u5012\u5fd8\u8bb0\u544a\u8bc9\u4f60\u4e86\uff0c\u54b1\u4eec\u665a\u4e0a\u884c\u52a8\u4f1a\u65b9\u4fbf\u4e00\u4e9b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u73b0\u5728\uff0c\u54b1\u4eec\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\uff01\u8981\u4e0d\u8981\u6234\u4e0a\u9b54\u624b\u94c1\u98de\u7684\u9762\u5177\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6700\u597d\u6234\u4e0a\uff0c\u54b1\u4eec\u4eca\u5929\u89c1\u7684\u4eba\uff0c\u90fd\u662f\u9ed1\u9053\u4e2d\u4eba\u53f6\u8001\u5f1f\uff0c\u94c1\u98de\u5728\u9ed1\u9053\u4e2d\uff0c\u7a0d\u5177\u540d\u671b\uff0c\u5343\u4e07\u522b\u5fd8\u4e86\u4f60\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u8ddd\u79bb\u5723\u6c34\u9547\u4e03\u516b\u91cc\u5904\uff0c\u4e00\u5ea7\u5c0f\u8305\u68da\u5185\uff0c\u71c3\u7740\u4e00\u76cf\u6cb9\u706f\u3002 \u3000\u3000\u56db\u4e2a\u7a7f\u7740\u571f\u5e03\u8863\u670d\u7684\u4e2d\u5e74\u4eba\uff0c\u56f4\u5750\u5728\u4e00\u684c\u4e0a\u3002 \u3000\u3000\u684c\u4e0a\u6446\u4e86\u56db\u4e2a\u571f\u5236\u7684\u5927\u9ed1\u7897\uff0c\u7897\u91cc\u7684\u83dc\u80b4\uff0c\u5374\u5f88\u7cbe\u7f8e\u3002 \u3000\u3000\u4e00\u5927\u7897\u9152\uff0c\u4e5f\u5df2\u559d\u4e86\u4e00\u5927\u534a\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u4f3c\u4e4e\u662f\u6781\u53d7\u5c0a\u91cd\uff0c\u56db\u4e2a\u4eba\u7acb\u523b\u7ad9\u4e86\u8d77\u6765\uff0c\u6b20\u8eab\u4e00\u793c\u9053\uff1a\u201c\u89c1\u8fc7\u603b\u62a4\u6cd5\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u5c60\u65e0\u65b9\u6709\u4e24\u4e2a\u8eab\u4efd\uff0c\u4e00\u4e2a\u662f\u4e94\u5927\u5251\u4f7f\u4e2d\u4e00\u540d\u5251\u4f7f\uff0c\u5728\u9634\u9633\u5821\u4e2d\uff0c\u4ed6\u5374\u662f\u603b\u62a4\u6cd5\u7684\u8eab\u5206\u3002 \u3000\u3000\u53f6\u957f\u9752\u76ee\u5149\u8f6c\u52a8\uff0c\u53d1\u89c9\u56db\u4e2a\u4eba\uff0c\u4ed6\u7adf\u7136\u8ba4\u8bc6\u4e86\u4e24\u4e2a\u3002 \u3000\u3000\u4e00\u4e2a\u662f\u6d3b\u50f5\u5c38\u9648\u6c9b\uff0c\u4e00\u4e2a\u662f\u6bd2\u8702\u5f6d\u9707\u3002 \u3000\u3000\u53e6\u5916\u4e24\u4e2a\u4eba\uff0c\u53f6\u957f\u9752\u867d\u7136\u4e0d\u8ba4\u8bc6\uff0c\u4f46\u548c\u9648\u6c9b\u3001\u5f6d\u9707\u5728\u4e00\u8d77\u7684\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u662f\u4ec0\u4e48\u597d\u4e1c\u897f\u4e86\u3002 \u3000\u3000\u5e78\u597d\uff0c\u53f6\u957f\u9752\u6234\u4e0a\u4e86\u4eba\u76ae\u9762\u5177\uff0c\u5426\u5219\u7acb\u523b\u5c31\u4f1a\u5f15\u8d77\u4e00\u573a\u7eb7\u4e89\u6c5f\u6e56\u4e0a\uff0c\u6709\u4e9b\u4eba\u4e0e\u4eba\u4e4b\u95f4\u5f62\u540c\u6c34\u706b\uff0c\u6c34\u706b\u96be\u5e76\u5b58\u3002 \u3000\u3000\u8fd9\u4e9b\u5165\u4e4b\u95f4\uff0c\u4e0d\u4e00\u5b9a\u4f1a\u6709\u4ec0\u4e48\u4ec7\u6068\uff0c\u4f46\u4e24\u79cd\u4eba\u5374\u5728\u6c5f\u6e56\u4e0a\u4ee3\u8868\u4e86\u4e24\u4e2a\u6781\u7aef\uff0c\u53ea\u8981\u8fd9\u4e24\u79cd\u4eba\u4e00\u78b0\u4e0a\uff0c\u53ea\u6709\u4e24\u4e2a\u7ed3\u679c\uff0c\u4e00\u79cd\u662f\u7acb\u523b\u706b\u5e76\uff0c\u4e00\u79cd\u662f\u4e00\u65b9\u9003\u907f\u3002 \u3000\u3000\u8fd9\u4e24\u79cd\u4eba\u5728\u8fd9\u5ea7\u8305\u68da\u4e2d\u90fd\u6709\uff0c\u4e00\u79cd\u662f\u53f6\u957f\u9752\uff0c\u4e00\u79cd\u662f\u9648\u6c9b\u3001\u5f6d\u9707\u3002 \u3000\u3000\u5e78\u597d\uff0c\u90a3\u526f\u9762\u5177\uff0c\u906e\u4f4f\u4e86\u53f6\u957f\u9752\u7684\u8138\u3002 \u3000\u3000\u53f6\u957f\u9752\u73b0\u5728\u7684\u8eab\u4efd\u662f\u9b54\u624b\u94c1\u98de\uff0c\u94c1\u98de\u662f\u5f88\u53d7\u9ed1\u9053\u4eba\u7269\u5c0a\u91cd\u7684\u4eba\uff0c\u6240\u4ee5\uff0c\u56db\u4e2a\u9ed1\u8863\u4eba\uff0c\u90fd\u5bf9\u94c1\u98de\u5f88\u656c\u91cd\uff0c\u9f50\u9f50\u8eac\u8eab\u4f5c\u793c\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e0d\u6562\u5f00\u53e3\uff0c\u4ed6\u6839\u672c\u4e0d\u77e5\u9053\uff0c\u94c1\u98de\u7684\u58f0\u97f3\u662f\u4ec0\u4e48\u6837\u5b50\u3002 \u3000\u3000\u4e0d\u5f00\u53e3\uff0c\u53ea\u597d\u70b9\u70b9\u5934\uff0c\u4ee3\u8868\u4e86\u7b54\u590d\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u5927\u9ebb\u8138\u4e0a\uff0c\u662f\u4e00\u7247\u51b7\u8083\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u63a2\u542c\u4e86\u4ec0\u4e48\u6d88\u606f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5f6d\u9707\u9053\uff1a\u201c\u5c60\u8001\uff0c\u6211\u4eec\u4eca\u5929\u4e00\u5927\u65e9\u51fa\u53bb\uff0c\u76f4\u5230\u592a\u9633\u4e0b\u5c71\u624d\u56de\u6765\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5954\u8d70\u4e86\u4e00\u5929\uff0c\u63a2\u542c\u5230\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f6d\u9707\u9053\uff1a\u201c\u53d1\u73b0\u4e86\u4e00\u4e9b\u4e8b\uff0c\u4e0d\u8fc7\uff0c\u662f\u4e0d\u662f\u548c\u5723\u6c34\u6709\u5173\uff0c\u5c31\u65e0\u6cd5\u9884\u6599\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8bf4\u8bf4\u770b\uff0c\u90fd\u53d1\u73b0\u4e9b\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5f6d\u9707\u9053\uff1a\u201c\u597d\u50cf\u6709\u51e0\u4f4d\u6b63\u5927\u95e8\u6d3e\u4e2d\u4eba\uff0c\u4e5f\u8d76\u6765\u4e86\u5723\u6c34\u9547\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6b63\u5927\u95e8\u6237\u4e2d\u4eba\uff0c\u96be\u9053\u662f\u5c11\u6797\u5bfa\u4e2d\u4eba\uff1f\u201d \u3000\u3000\u5f6d\u9707\u9053\uff1a\u201c\u5c11\u6797\u6d3e\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u4e5f\u662f\u9886\u8896\u9c81\u8c6b\u9053\u4e0a\u7684\u767d\u9053\u9996\u8111\u845b\u5e73\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u845b\u5e73\u6765\u4e86\u5723\u6c34\u9547\uff01\u201d \u3000\u3000\u5f6d\u9707\u9053\uff1a\u201c\u4e0d\u4f46\u6765\u4e86\u5723\u6c34\u9547\uff0c\u800c\u4e14\u8fd8\u5e26\u4e86\u56db\u4e2a\u4eba\u6765\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u5f6d\u9707\u5618\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u845b\u5e73\u6765\u7684\u5f88\u7a81\u7136\uff0c\u800c\u4e14\uff0c\u4ed6\u597d\u50cf\u662f\u7ecf\u8fc7\u7b80\u5355\u7684\u6613\u5bb9\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u6613\u5bb9\u7684\u624b\u6cd5\u5f88\u6076\u52a3\uff0c\u6211\u4e00\u773c\u5c31\u77a7\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u845b\u5e73\u8dd1\u5230\u4e86\u5723\u6c34\u9547\u6765\uff0c\u7528\u5fc3\u4f55\u5728\u5462\uff1f\u201d \u3000\u3000\u5f6d\u9707\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5c5e\u4e0b\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4f60\u4eec\u67e5\u5230\u4ed6\u843d\u8db3\u4e4b\u5904\u5417\uff1f\u201d \u3000\u3000\u5f6d\u9707\u9053\uff1a\u201c\u845b\u5e73\u662f\u4e2a\u5f88\u7cbe\u660e\u7684\u4eba\u7269\uff0c\u5c5e\u4e0b\u867d\u7136\u7ecf\u8fc7\u4e86\u6539\u626e\uff0c\u4f46\u4ecd\u7136\u5f88\u62c5\u5fc3\u88ab\u4ed6\u77a7\u51fa\u6765\uff0c\u6240\u4ee5\uff0c\u4e0d\u6562\u8ddf\u5f97\u592a\u7d27\uff0c\u53ea\u770b\u5230\u4ed6\u4eec\u8fdb\u5165\u4e86\u5723\u6c34\u9547\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u597d\uff01\u845b\u5e73\u7684\u4e8b\uff0c\u6211\u4f1a\u53bb\u67e5\uff0c\u8fd8\u6709\u522b\u7684\u4e8b\u5417\uff1f\u201d \u3000\u3000\u9648\u6c9b\u9053\uff1a\u201c\u6709\uff01\u4eca\u6668\uff0c\u6211\u4eec\u63a5\u5230\u4e86\u2014\u5c01\u4fe1\uff0c\u8981\u4e94\u65e5\u4e4b\u540e\uff0c\u53bb\u53c2\u4e0e\u5723\u6c34\u6bd4\u4ef7\u2026\u2026\u201d \u3000\u3000\u5c60\u65e0\u65b9\u63a5\u9053\uff1a\u201c\u4e94\u5929\u4e4b\u540e\uff1f\u201d \u3000\u3000\u9648\u6c9b\u9053\uff1a\u201c\u5bf9\uff01\u4e94\u5929\u4e4b\u540e\uff0c\u800c\u4e14\u662f\u591c\u95f4\uff0c\u4e8c\u66f4\u65f6\u5206\uff0c\u4ed6\u4eec\u6765\u8fd9\u91cc\u63a5\u4eba\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd8\u6709\u4e9b\u4ec0\u4e48\u9650\u5236\uff1f\u201d \u3000\u3000\u9648\u6c9b\u9053\uff1a\u201c\u6709\uff01\u81f3\u591a\u4e0d\u80fd\u8d85\u8fc7\u4e24\u4e2a\u4eba\u53bb\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u597d\uff01\u660e\u5929\u7ee7\u7eed\u6697\u4e2d\u884c\u52a8\uff0c\u770b\u770b\u8fd8\u6709\u4e9b\u4ec0\u4e48\u4eba\u8d76\u6765\u6b64\u5730\uff0c\u845b\u5e73\u65e2\u7136\u6765\u4e86\uff0c\u53ea\u6015\u8fd8\u6709\u522b\u7684\u5f88\u591a\u4eba\u4f1a\u6765\u3002\u201d \u3000\u3000\u9648\u6c9b\u3001\u5f6d\u9707\u9f50\u9f50\u8eac\u8eab\u5e94\u662f\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u5e26\u7740\u53f6\u957f\u9752\u79bb\u5f00\u4e86\u8305\u820d\uff0c \u3000\u3000\u4e00\u76f4\u884c\u51fa\u4e94\u91cc\u4e4b\u5916\uff0c\u53f6\u957f\u9752\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5c60\u5144\uff0c\u9648\u6c9b\u3001\u5f6d\u9707\u8fd9\u4e24\u4e2a\u4eba\u4e5f\u6295\u5165\u4e86\u9634\u9633\u5821\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u800c\u4e14\uff0c\u4ed6\u4eec\u90fd\u5728\u5144\u5f1f\u624b\u4e0b\u542c\u547d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\u7684\u6076\u8ff9\uff0c\u6c5f\u6e56\u4e0a\u4eba\u4eba\u7686\u77e5\uff0c\u4e0d\u77e5\u4ed6\u4eec\u662f\u5426\u4e5f\u548c\u5c60\u5144\u4e00\u6837\uff0c\u540d\u6076\u884c\u5b9e\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e0d\uff01\u4ed6\u4eec\u662f\u771f\u771f\u6b63\u6b63\u7684\u6076\u4eba\uff0c\u65e0\u6076\u4e0d\u4f5c\u7684\u574f\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5144\u5f1f\u4e24\u5e74\u524d\uff0c\u66fe\u7ecf\u7b49\u4e86\u4ed6\u4eec\u4e09\u4e2a\u6708\uff0c\u60f3\u4e0d\u5230\u4ed6\u4eec\u6295\u5165\u4e86\u9634\u9633\u5821\u4e3b\u3002\u201d \u3000\u3000\u5c60\u65e0\u529b\u7b11\u9053\uff1a\u201c\u9634\u9633\u5821\u4e2d\uff0c\u4e5f\u6709\u4e00\u4e9b\u6212\u5f8b\uff0c\u867d\u7136\u662f\u4e9b\u6781\u6076\u7684\u4eba\uff0c\u4e00\u4f46\u8fdb\u5165\u4e86\u9634\u9633\u5821\uff0c\u4e5f\u4f1a\u7a0d\u6709\u6539\u53d8\u2026\u2026\u201d \u3000\u3000\u53f6\u6c0f\u9752\u63a5\u9053\uff1a\u201c\u96be\u9053\u9634\u9633\u5821\u4e2d\uff0c\u4e5f\u6709\u6e05\u660e\u6212\u5f8b\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1b\u201c\u6ca1\u6709\uff0c\u4e0d\u8fc7\uff0c\u5821\u4e3b\u4f1a\u628a\u4ed6\u4eec\u6d3e\u4e9b\u65e0\u6cd5\u4e3a\u6076\u7684\u5de5\u4f5c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u54e6\u4e86\u4e00\u58f0\uff0c\u4f46\u795e\u8272\u4e4b\u95f4\u3002\u4ecd\u7136\u6709\u7740\u4e00\u4e9b\u56f0\u60d1\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u4e9b\u5730\u65b9\uff0c\u9664\u4e86\u90a3\u4e9b\u9152\u5e97\u4e4b\u5916\uff0c\u5f88\u5c11\u4eba\u5bb6\uff0c\u5c31\u7b97\u4ed6\u4eec\u60f3\u505a\u70b9\u4ec0\u4e48\u574f\u4e8b\uff0c\u53ea\u6015\u8fd9\u5730\u65b9\u4e5f\u4e0d\u5141\u8bb8\u4ed6\u4eec\u80e1\u4f5c\u975e\u4e3a\u3002\u518d\u8bf4\uff0c\u5821\u4e3b\u7684\u4ee4\u8c15\uff0c\u5341\u5206\u68ee\u4e25\uff0c\u6211\u7684\u813e\u6c14\u4e5f\u4e0d\u592a\u597d\uff0c\u53f6\u8001\u5f1f\u60f3\u4e00\u60f3\u5c31\u53ef\u4ee5\u660e\u767d\u4e86\uff0c\u4ed6\u4eec\u8eb2\u5728\u90a3\u8305\u68da\u91cc\u9762\u559d\u9152\uff0c\u4ee5\u89e3\u9664\u5fc3\u4e2d\u7684\u5bc2\u5bde\uff0c\u8fd9\u4e5f\u8bc1\u660e\u4e86\u4ed6\u4eec\u5fc3\u4e2d\u6709\u754f\u60e7\uff0c\u6211\u4eec\u7684\u7ea6\u675f\u8fd8\u6709\u6548\u7528\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u70b9\u70b9\u5934\uff0c\u6d3b\u9898\u4e00\u8f6c\uff0e\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u4f60\u521a\u624d\u542c\u5230\u4e86\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u542c\u5230\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u5723\u6c34\u7684\u4ea4\u6613\uff0c\u5b9e\u5728\u662f\u5145\u6ee1\u7740\u795e\u79d8\uff0c\u4e00\u5207\u90fd\u5728\u4f60\u610f\u6599\u4e4b\u5916\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c0f\u5f1f\u8fd8\u662f\u4e0d\u592a\u660e\u767d\u5c60\u5144\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5728\u4e0b\u66fe\u548c\u5821\u4e3b\u3001\u4f55\u8001\u5f1f\u7b49\uff0c\u5546\u91cf\u8fc7\u8fd9\u4ef6\u4e8b\uff0c\u60f3\u4ed6\u4eec\u7ea6\u6664\u54b1\u4eec\u7684\u65f6\u95f4\uff0c\u6700\u5feb\u4e5f\u8be5\u5728\u534a\u4e2a\u6708\u4e4b\u540e\uff0c\u4f46\u5374\u60f3\u4e0d\u5230\u4ed6\u4eec\u7adf\u7136\u4f1a\u7ea6\u6211\u4eec\u4e94\u5929\u4e4b\u540e\u5c31\u53bb\u89c1\u9762\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e9b\u7ea6\u54b1\u4eec\u89c1\u9762\u7684\u4eba\uff0c\u5c31\u662f\u628a\u6301\u5723\u6c34\u7684\u4eba\u4e86\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4e0d\u662f\u628a\u6301\u5723\u6c34\u7684\u4eba\uff0c\u5728\u4e0b\u4e5f\u4e0d\u6e05\u695a\uff0c\u4e0d\u8fc7\uff0c\u4e00\u5b9a\u548c\u5723\u6c34\u6709\u5173\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\u51c6\u5907\u5e26\u4ec0\u4e48\u4eba\u53bb\u53c2\u4e0e\u5723\u6c34\u6bd4\u4ef7\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4f60\uff01\u53f6\u8001\u5f1f\u548c\u6211\u4e24\u4e2a\u4eba\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u6709\u4e94\u5929\u7684\u65f6\u95f4\u51c6\u5907\u3002\u201d \u3000\u3000\u5c60\u65e0\u529b\u9053\uff1a\u201c\u5bf9\uff01\u8fd9\u4e94\u5929\uff0c\u53f6\u8001\u5f1f\uff0c\u5e0c\u671b\u4f60\u597d\u597d\u5229\u7528\uff0c\u800c\u4e14\uff0c\u8981\u4fdd\u6301\u6700\u597d\u7684\u4f53\u80fd\uff0c\u56e0\u4e3a\uff0c\u53c2\u4e0e\u5723\u6c34\u4e4b\u4f1a\uff0c\u5f88\u53ef\u80fd\u95f9\u51fa\u4e8b\u60c5\uff1b\u8bf4\u4e0d\u5b9a\u8fd8\u8981\u52a8\u624b\u4e00\u6218\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5728\u4e0b\u7684\u9605\u5386\u4e0d\u591f\uff0c\u8fd9\u4e9b\u4e8b\u5e94\u8be5\u5982\u4f55\u5e94\u4ed8\uff0c\u5168\u8981\u770b\u4f60\u5c60\u5144\u7684\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u906d\uff1a\u201c\u5982\u4f55\u5e94\u4ed8\u662f\u6211\u7684\u4e8b\uff0c\u4e0d\u7528\u53f6\u8001\u5f1f\u4f60\u8d39\u5fc3\uff0c\u91cd\u8981\u7684\u662f\u4f60\u8981\u60f3\u4e2a\u529e\u6cd5\uff0c\u4e07\u4e00\u52a8\u4e0a\u624b\u65f6\uff0c\u6700\u597d\u522b\u7528\u4f60\u7684\u8ffd\u9b42\u4e03\u5251\uff0c\u4f7f\u522b\u4eba\u4e00\u4e0b\u5b50\u5c31\u77a7\u51fa\u4e86\u4f60\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u771f\u5230\u4e86\u6027\u547d\u76f8\u640f\u7684\u65f6\u95f4\uff0c\u53ea\u6015\u5f88\u96be\u80fd\u63a9\u9970\u5f97\u4f4f\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u70b9\u70b9\u5934\u3002 \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u4f1a\u5c3d\u91cf\u5c0f\u5fc3\u2026\u2026\u201d \u3000\u3000 \u3000\u3000\u4e94\u5929\u540e\u521d\u66f4\u65f6\u5206\uff0c\u5c60\u65e0\u65b9\u5e26\u7740\u53f6\u957f\u9752\u53c8\u5230\u4e86\u90a3\u8305\u68da\u4e4b\u4e2d\u3002 \u3000\u3000\u9648\u6c9b\u3001\u5f6d\u9707\uff0c\u65e9\u5df3\u5728\u8305\u820d\u4e2d\u7b49\u5019\u3002 \u3000\u3000\u4e8c\u66f4\u65f6\u5206\uff0c\u4e00\u8f86\u7bf7\u8f66\u9a70\u5230\u4e86\u8305\u820d\u5916\u9762\uff1a \u3000\u3000\u8d76\u8f66\u7684\u7a7f\u7740\u4e00\u8eab\u9ed1\u8863\uff0c\u6234\u4e86\u2014\u9876\u9ed1\u5e3d\u5b50\uff0c\u7531\u5934\u9876\u81ea\u5782\u5230\u4e0b\u989a\u7684\u5e3d\u5b50\u3002 \u3000\u3000\u9664\u4e86\u4e24\u53ea\u773c\u775b\u4e4b\u5916\uff0c\u6574\u4e2a\u7684\u5934\u8138\u90fd\u5728\u90a3\u5e3d\u5b50\u7684\u63a9\u906e\u4e4b\u4e0b\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u8f6c\u8fc7\u8138\u6765\uff0c\u6253\u91cf\u4e86\u5c60\u65e0\u65b9\u7b49\u2014\u9635\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u53ea\u80fd\u53bb\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u597d\uff01\u201d\u4e00\u9762\u62c9\u53f6\u957f\u9752\u767b\u4e0a\u4e86\u7bf7\u8f66\uff1b\u53f6\u957f\u9752\u73b0\u5728\u662f\u9b54\u624b\u94c1\u98de\uff0c\u7a7f\u7740\u94c1\u98de\u559c\u7231\u7684\u8863\u773c\uff0c\u4e5f\u5e26\u4e86\u94c1\u98de\u5e38\u7528\u7684\u5175\u5203\u3002 \u3000\u3000\u7bf7\u8f66\u4e2d\u7684\u5e03\u7f6e\uff0c\u8bf4\u4e0d\u4e0a\u8c6a\u534e\uff0c\u4f46\u5374\u5f88\u5b9e\u7528\u3001\u8212\u9002\u3002 \u3000\u3000\u9ed1\u8863\u8f66\u592b\u653e\u4e0b\u4e86\u5782\u5e18\uff0c\u7bf7\u8f66\u7acb\u523b\u5411\u524d\u9a70\u53bb\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\uff0c\u5230\u4e86\u5730\u65b9\u6211\u81ea\u4f1a\u62db\u547c\u4e24\u4f4d\u4e0b\u8f66\uff0c\u5982\u82e5\u4f60\u4eec\u5728\u9014\u4e2d\u5411\u5916\u63a2\u89c6\uff0c\u6216\u662f\u6709\u4ec0\u4e48\u4e0d\u8be5\u6709\u7684\u4e3e\u52a8\uff0c\u4e24\u4f4d\u8981\u7acb\u523b\u4e0b\u8f66\uff0c\u4e5f\u4e0d\u7528\u53bb\u8c08\u4ec0\u4e48\u4ea4\u6613\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5982\u679c\u4f60\u5728\u9014\u4e2d\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4e5f\u4e0d\u8bb8\u54b1\u4eec\u77a7\u7740\u5417\uff1f\u201d \u3000\u3000\u8d76\u8f66\u7684\u9053\uff1a\u201c\u5982\u679c\u771f\u7684\u9047\u4e0a\u4e86\u4ec0\u4e48\u9ebb\u70e6\uff0c\u6211\u81ea\u4f1a\u62db\u547c\u4f60\u4eec\u3002\u201d\u53f6\u957f\u9752\u9053\uff1a\u201c\u9601\u4e0b\u7684\u610f\u601d\u662f\uff0c\u54b1\u4eec\u542c\u4e0d\u5230\u62db\u547c\uff0c\u5c31\u7b97\u9601\u4e0b\u6b7b\u4e86\uff0c\u4e5f\u7528\u4e0d\u7740\u54b1\u4eec\u63d2\u624b\u4e86\uff1f\u201d \u3000\u3000\u8d76\u8f66\u7684\u9053\uff1a\u201c\u5bf9\uff01\u5982\u6211\u6ca1\u6709\u62db\u547c\u4f60\u4eec\uff0c\u5c31\u7b97\u6211\u6b7b\u4e86\uff0c\u4f60\u4eec\u4e5f\u4e0d\u7528\u77a7\u3002\u201d \u3000\u3000\u7bf7\u8f66\u5f00\u59cb\u8df3\u52a8\uff0c\u4f3c\u4e4e\u6b63\u9a70\u5728\u5d0e\u5c96\u4e0d\u5e73\u7684\u5c0f\u9053\u4e0a\u3002\u8fd9\u65f6\uff0c\u53f6\u957f\u9752\u5ffd\u7136\u53d1\u89c9\u4e86\u8fd9\u7bf7\u8f66\u6709\u4e00\u70b9\u7279\u6b8a\u7684\u5730\u65b9\uff0c\u5b83\u5f88\u575a\u7262\uff0c\u5982\u679c\u666e\u901a\u7684\u7bf7\u8f66\uff0c\u8fd9\u7b49\u884c\u9a70\u4e4b\u6cd5\uff0c\u8f66\u5b50\u53ef\u80fd\u65e9\u5df2\u7ecf\u88ab\u9707\u6563\u4e86\u3002 \u3000\u3000\u7bf7\u8f66\u7a81\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u9759\u6b62\u672a\u52a8\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u4e3a\u4ec0\u4e48\u505c\u4e86\u4e0b\u6765\u4e86\uff1f\u201d \u3000\u3000\u8d76\u8f66\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7a0d\u5b89\u52ff\u8e81\uff0c\u4e5f\u8bb8\u4e24\u4f4d\u5f88\u5feb\u5c31\u53ef\u4ee5\u4e0b\u8f66\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u7ec6\u58f0\u7ec6\u6c14\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u4e0d\u5f97\u5bf9\u8d35\u5bbe\u65e0\u793c\u201d \u3000\u3000\u7d27\u63a5\u7740\u8f66\u5e18\u542f\u52a8\uff0c\u4e00\u9053\u706f\u5149\u7167\u5c04\u8fc7\u6765\u3002 \u3000\u3000\u4e00\u4e2a\u7a7f\u7740\u5929\u84dd\u957f\u886b\uff0c\u624b\u4e2d\u9ad8\u4e3e\u706f\u7b3c\uff0c\u9762\u4e5f\u5e26\u7740\u5fae\u7b11\u7684\u4e2d\u5e74\u4eba\u7ad9\u5728\u7bf7\u8f66\u524d\u9762\u3002 \u3000\u3000\u706f\u5149\u7167\u7740\u5c60\u65e0\u65b9\u7684\u5927\u9ebb\u8138\uff0c\u7167\u7740\u84dd\u886b\u4eba\u4e00\u8eab\u7ec6\u76ae\u767d\u8089\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u662f\u5c5e\u4e8e\u90a3\u79cd\u5929\u751f\u53ef\u4eb2\u7684\u4eba\u7269\uff0c\u80d6\u80d6\u7684\u8eab\u6750\uff0c\u7ec6\u7ec6\u7684\u957f\u7709\uff0c\u5e26\u7740\u4e00\u7247\u7965\u548c\u4e4b\u6c14\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u53ef\u4ee5\u4e0b\u8f66\u4e86\uff1f\u201d\u84dd\u886b\u4eba\u9053\uff1a\u201c\u53ef\u4ee5\uff0c\u53ef\u4ee5\uff0c\u5c0f\u9053\u5d0e\u5c96\uff0c\u8f66\u5b50\u8df3\u52a8\u7684\u5389\u5bb3\uff0c\u53ea\u6015\u4e24\u4f4d\u5f88\u8f9b\u82e6\u554a\uff01\u201d \u3000\u3000\u5c60\u65e0\u65b9\u5f53\u5148\u4e0b\u4e86\u7bf7\u8f66\uff0c\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u8fd8\u597d\uff0c\u53cd\u6b63\uff0c\u6211\u4eec\u4e5f\u4e0d\u662f\u7eb8\u624e\u7684\u4eba\uff0c\u8fd9\u4e00\u70b9\u8f9b\u82e6\u8fd8\u627f\u53d7\u5f97\u4f4f\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u7b11\u9053\uff1a\u201c\u5148\u82e6\u540e\u7518\uff0c\u54b1\u4eec\u66ff\u4e24\u4f4d\u51c6\u5907\u4e86\u9152\u83dc\uff0c\u53bb\u559d\u4e00\u676f\uff0c\u6d88\u6d88\u75b2\u52b3\uff0c\u5728\u4e0b\u7ed9\u4e24\u4f4d\u5e26\u8def\u3002\u201d \u3000\u3000\u8f6c\u8eab\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u4e0b\u6253\u91cf\u4e86\u4e00\u9635\uff0c\u53d1\u89c9\u7bf7\u8f66\u6b63\u505c\u5728\u4e00\u7247\u6811\u6797\u7684\u65c1\u8fb9\uff0c\u4e00\u9762\u9760\u5c71\uff0c\u4e00\u9762\u501a\u6797\u3002 \u3000\u3000\u591c\u8272\u6d53\u9ed1\uff0c\u5c71\u98ce\u547c\u5578\uff0c\u90a3\u84dd\u8863\u4eba\u6267\u706f\u5e26\u8def\uff0c\u987a\u7740\u4e00\u6761\u5c0f\u5f84\u5411\u6797\u4e2d\u884c\u53bb\u3002 \u3000\u3000\u6811\u6797\u4e2d\u66f4\u662f\u9ed1\u6697\uff0c\u5982\u6ca1\u6709\u90a3\u4e00\u76cf\u5e26\u8def\u7684\u706f\u7b3c\uff0c\u9ed1\u5f97\u5df2\u7ecf\u5230\u4e86\u4f38\u624b\u96be\u89c1\u4e94\u6307\u3002 \u3000\u3000\u6797\u4e2d\u6811\u6728\u8302\u5bc6\uff0c\u66f2\u5f84\u901a\u5e7d\uff0c\u53f6\u957f\u9752\u7d27\u968f\u5728\u90a3\u6267\u706f\u4eba\u7684\u8eab\u540e\uff0c\u6697\u4e2d\u8fd0\u529f\u6212\u5907\uff0c\u82e5\u4e8b\u60c5\u7a0d\u6709\u53d8\u5316\uff0c\u7acb\u523b\u51fa\u624b\u653b\u5411\u90a3\u4eba\u7684\u80cc\u5fc3\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u8d70\u5728\u53f6\u957f\u9752\u7684\u8eab\u540e\uff0c\u4e5f\u662f\u5168\u795e\u6212\u5907\uff0c\u968f\u65f6\u51c6\u5907\u5e94\u53d8\u3002 \u3000\u3000\u90a3\u6267\u706f\u4eba\u5e26\u7740\u513f\u4eba\u884c\u8fd1\u5b50\u4e00\u5ea7\u8305\u820d\u4e4b\u5916\uff0c\u505c\u4e86\u4e0b\u6765\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u91cc\u5c31\u662f\u4e86\uff0c\u4e24\u4f4d\u8bf7\u8fdb\u5427\uff01\u201d \u3000\u3000\u8305\u820d\u7684\u6728\u95e8\uff0c\u5440\u7136\u800c\u5f00\u3002 \u3000\u3000\u84dd\u8863\u4eba\u95ea\u8eab\u9000\u5230\u4e00\u4fa7\u3002 \u3000\u3000\u53f6\u957f\u9752\u56de\u987e\u4e86\u4e00\u773c\uff0c\u7f13\u6b65\u884c\u5165\u4e86\u8305\u820d\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u6df1\u6797\u8305\u820d\uff0c\u770b\u4e0a\u53bb\u5b9e\u5728\u4e0d\u8d77\u773c\uff0c\u4f46\u8305\u820d\u5185\uff0c\u5e03\u7f6e\u7684\u5374\u5341\u5206\u8c6a\u534e\u3002 \u3000\u3000\u6574\u4e2a\u8305\u820d\uff0c\u4e00\u7247\u5929\u84dd\u989c\u8272\uff0c\u5730\u4e0a\u94fa\u7740\u767d\u8272\u7684\u6bdb\u6be1\u3002 \u3000\u3000\u5916\u9762\u77a7\u4e0d\u51fa\u6765\uff0c\u4f46\u8fdb\u4e86\u95e8\uff0c\u5374\u53d1\u89c9\u8fd9\u8305\u820d\u76f8\u5f53\u7684\u5927\u3002 \u3000\u3000\u4e00\u5ea7\u5bbd\u655e\u7684\u5927\u5385\u4e2d\uff0c\u6446\u7740\u5f20\u91d1\u4ea4\u6905\uff0c\u4e0a\u9762\u5750\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u5973\u4eba\uff0c\u5f88\u7f8e\u4e3d\u7684\u5973\u4eba\u3002 \u3000\u3000\u4e24\u4e2a\u8eab\u7740\u52b2\u88c5\u7684\u4e2b\u5934\uff0c\u5206\u522b\u7ad9\u5728\u90a3\u5973\u4eba\u8eab\u4fa7\u3002 \u3000\u3000\u5927\u5385\u56db\u89d2\uff0c\u71c3\u8d77\u4e86\u516d\u652f\u7c97\u5982\u624b\u81c2\u7684\u5de8\u70db\uff0c\u5ba4\u4e2d\u76f8\u5f53\u7684\u4eae\uff0c\u6240\u4ee5\uff0c\u4e00\u5207\u666f\u7269\u90fd\u770b\u7684\u5f88\u6e05\u695a\u3002 \u3000\u3000\u90a3\u5973\u4eba\u7a7f\u7740\u4e00\u8eab\u767d\u989c\u8272\u7684\u8863\u670d\uff0c\u5728\u91d1\u4ea4\u6905\u524d\u5374\u653e\u7740\u4e24\u5341\u4e2a\u5750\u58a9\u3002 \u3000\u3000\u9760\u5728\u5385\u95e8\u540e\u9762\u4e5f\u7ad9\u7740\u4e24\u4e2a\u4e2b\u5934\uff0c\u4e00\u4e2a\u8fce\u4e0a\u6765\u63a5\u8fc7\u84dd\u886b\u4eba\u624b\u4e2d\u7684\u706f\u7b3c\u3002 \u3000\u3000\u767d\u8863\u5973\u5b50\u5fae\u5fae\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u5750\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u548c\u53f6\u957f\u9752\uff0c\u8001\u5b9e\u4e0d\u5ba2\u6c14\u5730\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u767d\u8863\u5973\u5b50\u4e24\u9053\u7f8e\u76ee\uff0c\u5728\u4e24\u4eba\u8138\u4e0a\u6253\u91cf\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u54ea\u4e00\u4f4d\u662f\u80fd\u591f\u505a\u4e3b\u7684\u4eba\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6211\uff01\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u6211\u770b\u5230\u4e86\u4f60\u4eec\u51fa\u7684\u4ef7\u94b1\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6211\u4eec\u5bf9\u8d2d\u4e70\u5723\u6c34\u4e00\u4e8b\uff0c\u4e00\u76f4\u5f88\u8ba4\u771f\uff0c\u6240\u4ee5\u4e0d\u60dc\u5de8\u8d44\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4f60\u4eec\u51fa\u7684\u4ef7\u94b1\u867d\u5927\uff0c\u4f46\u4e0d\u662f\u8bf4\uff0c\u6211\u4eec\u4e00\u5b9a\u5c31\u5356\u7ed9\u4f60\u4eec\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u539f\u56e0\u662f\uff0c\u6709\u4eba\u6bd4\u4f60\u4eec\u51fa\u4ef7\u66f4\u9ad8\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5982\u679c\u53ea\u662f\u4ef7\u94b1\u95ee\u9898\uff0c\u6211\u4eec\u518d\u63d0\u9ad8\u4e00\u70b9\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u9664\u4e86\u94b1\u4e4b\u5916\uff0c\u6211\u4eec\u8fd8\u6709\u522b\u7684\u6761\u4ef6\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd8\u6709\u522b\u7684\u6761\u4ef6\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u5bf9\uff01\u6211\u4eec\u5e0c\u671b\u77e5\u9053\uff0c\u4f60\u4eec\u4e70\u53bb\u5723\u6c34\u7684\u7528\u5904\u4e3a\u4f55\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u4e5f\u8981\u8bf4\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u5bf9\uff01\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5728\u4e0b\u5c31\u4e0d\u592a\u6e05\u695a\u4e86\uff0c\u6211\u53ea\u662f\u4e70\u529e\u5723\u6c34\u7684\u4eba\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u90a3\u4ef7\u94b1\u4f60\u65e9\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u77e5\u9053\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u5723\u6c34\u4ea4\u6613\u7684\u89c4\u77e9\u5462\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e5f\u77e5\u9053\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u597d\uff01\u4f60\u8fd8\u8bb0\u5f97\u90a3\u4e2a\u4ef7\u94b1\u4e48\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8bb0\u5f97\uff0c\u4e00\u5171\u662f\u5341\u516b\u4e07\u516d\u5e72\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u4eec\u80fd\u4e0d\u80fd\u6b63\u5f0f\u5546\u91cf\u4e00\u4e0b\u8fd9\u7b14\u4ea4\u6613\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u6211\u627e\u4e24\u4f4d\u6765\uff0c\u5c31\u662f\u8981\u548c\u4f60\u4eec\u8c08\u8c08\u8fd9\u4ef6\u4e8b\u7684\u7ec6\u8282\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u54b1\u4eec\u5df2\u53c2\u4e0e\u4e86\u6bd4\u4ef7\u4e24\u5e74\uff0c\u8fd9\u4e00\u6b21\uff0c\u5fd7\u5728\u5fc5\u5f97\uff0c\u591a\u82b1\u4e00\u4e9b\u94f6\u94b1\uff0c\u5728\u6240\u4e0d\u60dc\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u8d35\u59d3\u554a\uff01\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5728\u4e0b\u5c60\u65e0\u65b9\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u5c60\u5144\uff0c\u597d\u50cf\u662f\u6709\u4e00\u4e2a\u5916\u53f7\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u6709\u4e00\u4e2a\u5916\u53f7\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u5c60\u5144\u7684\u5916\u53f7\u662f\u2026\u2026\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u975e\u5e38\u7684\u96be\u542c\uff0c\u8bf4\u51fa\u6765\uff0c\u4e0d\u597d\u610f\u601d\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u548c\u6211\u4eec\u4ea4\u6613\uff0c\u6709\u4e00\u4e2a\u5f88\u91cd\u8981\u7684\u89c4\u77e9\uff0c\u4e0d\u77e5\u5c60\u5144\u662f\u5426\u77e5\u9053\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u542c\u662f\u542c\u8bf4\u8fc7\u4e00\u4e9b\uff0c\u4e0d\u8fc7\uff0c\u8fd8\u662f\u4e0d\u592a\u6e05\u695a\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u5176\u4e2d\u6709\u4e00\u6761\uff0c\u5c31\u662f\u4e0d\u53ef\u9690\u7792\u8eab\u4efd\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u662f\uff01\u662f\uff0c\u5728\u4e0b\u7684\u5916\u53f7\u53eb\u4f5c\u9ebb\u9762\u8840\u624b\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5012\u662f\u5207\u5408\u4f60\u7684\u8eab\u4efd\u554a\uff01\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5355\u51ed\u9ebb\u9762\u8840\u624b\u8fd9\u56db\u4e2a\u5b57\uff0c\u5c31\u662f\u4e00\u4e2a\u5f88\u597d\u7684\u4ea4\u6613\u5bf9\u8c61\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u79fb\u53f6\u957f\u9752\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4f4d\u60f3\u6765\u5fc5\u662f\u4f60\u7684\u642d\u6863\u9b54\u624b\u94c1\u98de\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u9053\uff1a\u539f\u6765\uff0c\u4ed6\u4eec\u65e9\u5df2\u7ecf\u77e5\u9053\u4e86\u3002 \u3000\u3000\u53ea\u542c\u9ebb\u9762\u8840\u624b\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u539f\u6765\u65e9\u5df3\u77e5\u6653\u6211\u4eec\u7684\u8eab\u4efd\u3002\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u5bf9\u4ea4\u6613\u7684\u5bf9\u8c61\uff0e\u6211\u4eec\u4e0d\u5f97\u4e0d\u5f04\u6e05\u695a\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u59d1\u5a18\u65e2\u662f\u65e9\u77e5\u6211\u4eec\u7684\u8eab\u4efd\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u591a\u6b64\u4e00\u95ee\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u6211\u8981\u770b\u4f60\u4eec\u4f1a\u4e0d\u4f1a\u8bf4\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7b11\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u8bf4\u4e86\u5b9e\u8bdd\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u5bf9\uff01\u5c60\u5144\uff0c\u8fd9\u4e00\u70b9\u5c1a\u8bf7\u4f60\u539f\u5ba5\uff0c\u6211\u4eec\u5bf9\u4ea4\u6613\u5bf9\u8c61\uff0c\u4e00\u5411\u5f88\u4e25\uff0c\u5982\u662f\u4eba\u5934\u4e0d\u5bf9\uff0c\u5c31\u7b97\u4f60\u591a\u51fa\u4e86\u4e8c\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u4e5f\u4e0d\u4e00\u5b9a\u80fd\u4e70\u5230\u5723\u6c34\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5509\uff01\u539f\u6765\u8fd8\u6709\u8fd9\u4e48\u591a\u7684\u9650\u5236\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8fd9\u4e00\u70b9\uff0c\u4f60\u4eec\u53ef\u4ee5\u653e\u5fc3\u4e86\uff0c\u56e0\u4e3a\uff0c\u4f60\u4eec\u5df2\u7ecf\u901a\u8fc7\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u90a3\u771f\u8981\u591a\u8c22\u59d1\u5a18\u5e2e\u5fd9\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u6211\u6ca1\u6709\u5e2e\u52a9\u4f60\u4eec\uff0c\u800c\u662f\u4f60\u4eec\u672c\u8eab\u5177\u6709\u4e86\u8fd9\u4e2a\u6761\u4ef6\u3002\u201d \u3000\u3000\u5c60\u65e0\u4e3b\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u4eec\u8fd9\u7b14\u4ea4\u6613\uff0c\u7b97\u4e0d\u7b97\u6210\u529f\u4e86\u5462\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\uff0c\u4f60\u4eec\u53ea\u662f\u901a\u8fc7\u4e86\u4ea4\u6613\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fcd\u4e86\u53c8\u5fcd\uff0c\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u5fc3\u4e2d\u7684\u6012\u706b\uff0c\u9053\uff1a\u201c\u8981\u5230\u4ec0\u4e48\u6837\u4e86\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u6211\u4eec\u624d\u80fd\u53c2\u4e0e\u8fd9\u7b14\u4ea4\u6613\u5462\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4f1a\u6709\u5f88\u591a\u4eba\u88ab\u901a\u8fc7\u4ea4\u6613\u7684\u8eab\u4efd\uff0c\u90a3\u5c31\u8981\u770b\u8c01\u51fa\u7684\u4ef7\u94b1\u9ad8\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u751f\u6050\u53f6\u957f\u9752\u5fcd\u4e0d\u4e0b\u80f8\u4e2d\u6012\u6c14\uff0c\u4e71\u4e86\u5927\u8c0b\uff0c\u6025\u6025\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u80fd\u5426\u7ed9\u6211\u4e00\u70b9\u6307\u793a\uff0c\u544a\u8bc9\u6211\u4eec\u4e00\u4e2a\u4ef7\u7801\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6709\u4e9b\u4eba\u51fa\u4ef7\u94b1\u532a\u5937\u6240\u601d\uff0c\u6211\u8981\u7ed9\u4f60\u4eec\u4e00\u4e2a\u660e\u793a\uff0c\u53ef\u80fd\u4f1a\u5bb3\u4e86\u4f60\u4eec\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u5c31\u8981\u5404\u51ed\u8fd0\u6c14\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u597d\uff01\u8fd9\u4e00\u70b9\uff0c\u6211\u4e5f\u8bb8\u53ef\u4ee5\u5e2e\u5fd9\uff0c\u4e0d\u8fc7\uff0c\u516c\u5f00\u558a\u4ef7\uff0c\u53ef\u80fd\u4f1a\u628a\u4ef7\u7801\u62ac\u5f97\u5f88\u9ad8\uff0c\u90a3\u5c31\u6709\u4e9b\u9ebb\u70e6\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u81f3\u5c11\uff0c\u4e92\u62fc\u8d22\u529b\uff0c\u53ef\u4ee5\u4f7f\u6211\u4eec\u77e5\u9053\u4e3a\u4ec0\u4e48\u4e70\u4e0d\u5230\u5723\u6c34\uff0c\u8fd9\u4e00\u70b9\uff0c\u4f7f\u5728\u4e0b\u5bf9\u4e0a\u8fb9\u4e5f\u7b97\u6709\u4e00\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u597d\u5427\uff01\u6211\u8bd5\u8bd5\u770b\uff0c\u6211\u60f3\uff0c\u8fd9\u4e2a\u673a\u4f1a\u5f88\u5927\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u5de6\u987e\u53f3\u76fc\u4e86\u4e00\u9635\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u5c60\u65e0\u529b\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u77e5\u9053\u8fd9\u4e9b\u8bdd\uff0c\u8bf4\u51fa\u6765\u662f\u4e0d\u662f\u65b9\u4fbf\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u65b9\u4fbf\uff0c\u4f60\u5c3d\u7ba1\u8bf4\u5427\uff01\u201d \u3000\u3000\u5c60\u65e0\u529b\u9053\uff1a\u201c\u5982\u8db3\u59d1\u5a18\u80fd\u591f\u5e2e\u5fd9\uff0c\u5728\u4e0b\u81ea\u7136\u4e5f\u4e0d\u80fd\u767d\u767d\u52b3\u52a8\u5927\u9a7e\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u4e0d\u5fc5\u4e86\uff0c\u4e07\u4e00\u4f60\u4eec\u5f97\u4e0d\u5230\u8fd9\u4e00\u6279\u5723\u6c34\u6587\u6613\uff0c\u5c82\u4e0d\u662f\u8ba9\u4f60\u4eec\u767d\u767d\u82b1\u4e86\u94f6\u5b50\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53ea\u8981\u59d1\u5a18\u80fd\u591f\u5b89\u6392\u516c\u5f00\u558a\u4ef7\uff0c\u5c31\u7b97\u5e2e\u4e86\u6211\u4eec\u7684\u5927\u5fd9\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5e7f\u9053\uff1a\u201c\u597d\uff01\u90a3\u5c31\u7b49\u4f60\u4eec\u5f97\u5230\u4e86\u5723\u6c34\u4e4b\u540e\uff0c\u518d\u9001\u6211\u4e00\u70b9\u94f6\u5b50\u4e5f\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u63a5\u9053\uff1a\u201c\u4e24\u4f4d\u5df2\u7ecf\u901a\u8fc7\u4e86\u6211\u8fd9\u4e00\u5173\uff0c\u56de\u53bb\u9759\u5019\u4f73\u97f3\uff0c\u4e0d\u8fc7\uff0c\u6709\u51e0\u4ef6\u4e8b\uff0c\u6211\u8981\u4e8b\u5148\u5411\u4e8c\u4f4d\u8bf4\u660e\u4e00\u4e0b\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u542c\u8bf4\u6709\u51e0\u4e2a\u6b66\u6797\u9053\u4e0a\u5f88\u6709\u540d\u7684\u4eba\u7269\uff0c\u4e5f\u542c\u5230\u4e86\u5723\u6c34\u7684\u4f20\u8bf4\uff0c\u5e26\u4e86\u4e00\u4e9b\u4eba\u6613\u5bb9\u800c\u6765\uff0c\u800c\u4e14\uff0c\u5df2\u7ecf\u4f4f\u5165\u4e86\u5723\u6c34\u9547\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u9053\uff1a\u539f\u6765\uff0c\u4ed6\u4eec\u7684\u6d88\u606f\u4e5f\u8fd9\u4e48\u7075\u901a\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u6545\u4f5c\u60ca\u8bb6\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u662f\u8bf4\uff0c\u6709\u4eba\u8981\u963b\u6321\u5723\u6c34\u4ea4\u6613\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4ed6\u4eec\u65e2\u7136\u6765\u4e86\uff0c\u81ea\u7136\u6709\u8fd9\u4e2a\u7528\u5fc3\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u662f\u5426\u6709\u80fd\u529b\u963b\u6321\u8fd9\u4ef6\u4e8b\u60c5\uff0e\u90a3\u5c31\u53c8\u53e6\u5f53\u522b\u6ca6\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u80fd\u4e0d\u80fd\u544a\u8bc9\u5728\u4e0b\u2014\u58f0\uff0c\u6765\u7684\u662f\u4ec0\u4e48\u4eba\u7269\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u5fd6\u9053\uff1a\u4ed6\u660e\u660e\u77e5\u6653\u7684\u5341\u5206\u6e05\u695a\uff0c\u5374\u88c5\u5f97\u5982\u6b64\u4e4b\u50cf\uff0c\u770b\u8d77\u6765\uff0c\u8fd9\u6c5f\u6e56\u4e0a\u7684\u7ecf\u9a8c\uff0c\u6211\u5b9e\u5728\u5dee\u4e86\u4e00\u70b9\u3002 \u3000\u3000\u53ea\u542c\u90a3\u767d\u8863\u5973\u5b50\u8bf4\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\u597d\u50cf\u53eb\u845b\u5e73\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u9886\u8896\u9c81\u3001\u8c6b\u9053\u4e0a\u7684\u6d41\u661f\u5200\u845b\u5e73\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u55ef\uff01\u5c31\u662f\u4ed6\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u5728\u4e2d\u539f\u5f88\u6709\u70b9\u52bf\u529b\uff0c\u542c\u8bf4\u662f\u51fa\u8eab\u5c11\u6797\u7684\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u5200\u4e0a\u9020\u8be3\u6781\u6df1\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4f60\u5bf9\u6d41\u661f\u5200\u845b\u5e73\u8fd9\u4e2a\u4eba\uff0c\u8fd8\u77e5\u9053\u591a\u5c11\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5728\u4e0b\u542c\u5230\u7684\uff0c\u5168\u90fd\u8bf4\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u6211\u6240\u77e5\u9053\u7684\uff0c\u845b\u5e73\u8fd9\u4e2a\u4eba\uff0c\u4e00\u76f4\u548c\u5c11\u6797\u5bfa\u4fdd\u6301\u5f88\u5bc6\u5207\u7684\u5173\u7cfb\uff0c\u6211\u60f3\uff0c\u9664\u4e86\u4ed6\u5728\u8c6b\u5357\u4fe1\u9633\u5dde\uff0c\u845b\u5bb6\u5821\u7684\u5b9e\u529b\u4e4b\u5916\uff0c\u8fd8\u6709\u5c11\u6797\u5bfa\u7684\u8001\u548c\u5c1a\u6491\u8170\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u845b\u5e73\u8fd9\u4e00\u6b21\u8fd8\u4e13\u4e3a\u5723\u6c34\u4ea4\u6613\u800c\u6765\u7684\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4e0d\uff01\u8fd8\u6709\u53e6\u5916\u4e00\u4ef6\u4e8b\uff0c\u542c\u8bf4\u662f\u8ffd\u67e5\u4e03\u5251\u8ffd\u9b42\u53f6\u957f\u9752\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53f6\u957f\u9752\u548c\u845b\u5e73\u4e5f\u6709\u5173\u8fde\u5417\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u8fd9\u5c31\u4e0d\u592a\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5c31\u6211\u4eec\u6240\u77e5\uff0c\u53f6\u957f\u9752\u548c\u845b\u5e73\uff0c\u5e94\u8be5\u6ca1\u6709\u89c1\u8fc7\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u9053\uff1a\u4e0d\u9519\uff0c\u6211\u4eec\u53ea\u662f\u5f7c\u6b64\u95fb\u540d\uff0c\u5374\u662f\u7f18\u94ff\u4e00\u9762\u3002 \u3000\u3000\u767d\u8863\u5973\u5b50\u7b11\u9053\uff1a\u201c\u845b\u5e73\u548c\u53f6\u957f\u9752\uff0c\u4e0d\u4e00\u5b9a\u8981\u6709\u4ea4\u60c5\uff0c\u4ed6\u4eec\u81ea\u89c9\u662f\u4e49\u6c14\u76f8\u6295\u7684\u4eba\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e03\u5251\u8ffd\u9b42\u8fd9\u51e0\u5e74\u5728\u6c5f\u5357\u9053\u4e0a\uff0c\u4e5f\u7684\u786e\u51fa\u4e86\u4e00\u70b9\u98ce\u5934\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c60\u8001\u5144\uff0c\u5bf9\u53f6\u957f\u9752\u5931\u8e2a\u4e00\u4e8b\uff0c\u96be\u9053\u4e00\u70b9\u4e5f\u4e0d\u6e05\u695a\u5417\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u4f20\u8bf4\u4ed6\u53bb\u4e86\u592a\u6e56\uff0c\u5c31\u50cf\u6295\u5165\u5728\u592a\u6e56\u4e2d\u7684\u6ce5\u6c99\u4e00\u6837\uff0c\u4e00\u4e0b\u5b50\uff0c\u5c31\u5931\u53bb\u4e86\u8e2a\u5f71\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4e24\u4e2a\u6c5f\u6e56\u4e0a\u65b0\u8d77\u4e4b\u79c0\u7684\u5973\u6740\u624b\uff0c\u4e5f\u5728\u592a\u6e56\u6b7b\u4ea1\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u4f3c\u4e4e\u662f\u6709\u4e9b\u795e\u79d8\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4f60\u8bf4\u7389\u5170\u53cc\u59dd\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4e0d\u9519\u554a\uff01\u4f60\u4eec\u5bf9\u6b64\u4e8b\u3001\u6709\u4f55\u770b\u6cd5\uff1f\u201d\u5c60\u65e0\u65b9\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e08\u4e8c\u91d1\u521a\uff0c\u6709\u4e9b\u6478\u4e0d\u7740\u5934\u8111\uff0c\u4e0d\u8fc7\uff0c\u655d\u5821\u4e5f\u6b63\u5728\u8c03\u67e5\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4f60\u4ee3\u8868\u9634\u9633\u5821\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5bf9\uff01\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u9634\u9633\u5821\u5728\u6c5f\u6e56\u9053\u4e0a\uff0c\u662f\u9996\u5c48\u4e00\u6307\u7684\u5927\u7ec4\u7ec7\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u96be\u9053\u8fd8\u80fd\u7792\u4f4f\u4f60\u4eec\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6b63\u56e0\u7ec4\u7ec7\u5f88\u5927\uff0c\u6240\u4ee5\u624d\u5404\u6709\u4e13\u53f8\uff0c\u533a\u533a\u5949\u547d\uff0c\u6765\u8c08\u5723\u6c34\u4ea4\u6613\uff0c\u5bf9\u53f6\u957f\u9752\u7684\u4e8b\u60c5\uff0c\u77e5\u6653\u4e0d\u591a\uff0c\u4f46\u6211\u77e5\u9053\uff0c\u655d\u5821\u786e\u5728\u8c03\u67e5\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0e\u6c5f\u5357\u7eff\u6797\u9053\u4e0a\uff0c\u5bf9\u53f6\u957f\u9752\u8fd9\u4e2a\u4eba\uff0c\u90fd\u6709\u4e9b\u5934\u75db\uff0c\u4ed6\u5982\u679c\u5c31\u6b64\u5931\u8e2a\uff0c\u6c38\u4e0d\u518d\u51fa\u73b0\u6c5f\u6e56\uff0c\u4e5f\u7b97\u5c11\u4e86\u4e2a\u6363\u4e71\u7684\u4eba\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u8bf4\u7684\u662f\uff0c\u655d\u5821\u4e5f\u66fe\u6d3e\u4eba\u8ddf\u8e2a\u8fc7\u4ed6\uff0c\u4f46\u5374\u88ab\u4ed6\u6446\u8131\u4e86\uff0c\u8fd9\u4e2a\u4eba\uff0c\u975e\u4f46\u5251\u6cd5\u8f9b\u8fa3\uff0c\u800c\u4e14\u4e5f\u5341\u5206\u673a\u8b66\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5982\u679c\u522b\u65e0\u5429\u5490\uff0c\u6211\u4eec\u60f3\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u597d\uff01\u591a\u5219\u5341\u5929\uff0c\u5c11\u5219\u4e94\u65e5\uff0c\u4f60\u4eec\u5c31\u4f1a\u63a5\u5230\u901a\u77e5\u4e86\uff0c\u4eca\u5e74\u7684\u5723\u6c34\u4ea4\u6613\uff0c\u4e5f\u8bb8\u4f1a\u56e0\u845b\u5e73\u4e4b\u6765\uff0c\u63d0\u524d\u4e00\u4e9b\u65f6\u95f4\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u597d\uff01\u6211\u4eec\u606d\u5019\u901a\u77e5\uff0c\u201d \u3000\u3000 \u3000\u3000\u4e24\u4e2a\u4eba\uff0c\u4ecd\u5750\u7740\u539f\u6765\u7684\u7bf7\u8f66\uff0c\u6765\u5230\u4e86\u8305\u68da\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5929\u8fd8\u6ca1\u6709\u4eae\u3002 \u3000\u3000\u9648\u6c9b\u3001\u5f6d\u9707\uff0c\u4ecd\u5728\u8305\u68da\u4e2d\u5bf9\u5750\u559d\u9152\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u4e0b\u9152\u7684\u83dc\u5f88\u7b80\u5355\uff0c\u4e00\u76d8\u8c46\u5e72\uff0c\u4e00\u76d8\u82b1\u751f\uff0c\u548c\u4e00\u76d8\u5364\u5473\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u5728\u6c5f\u6e56\u4e0a\u65e0\u6076\u4e0d\u4f5c\uff0c\u6574\u65e5\u5927\u9c7c\u3001\u5927\u8089\u7684\u5403\uff0c\u5343\u91d1\u4e70\u7b11\uff0c\u6325\u970d\u65e0\u5ea6\uff0c\u8981\u4ed6\u4eec\u8eb2\u5728\u8fd9\u8305\u68da\u4e2d\uff0c\u5403\u7740\u8c46\u8150\u5e72\u4e0b\u9152\uff0c\u5bf9\u8fd9\u4e24\u4eba\u5b9e\u5728\u662f\u4e00\u79cd\u5927\u6298\u78e8\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u671b\u4e86\u4e24\u4eba\u4e00\u773c\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u6ca1\u6709\u4f11\u606f\uff1f\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u9f50\u9f50\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u5728\u7b49\u5019\u5c60\u8001\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u6708\u6765\uff0c\u4f60\u4eec\u5f88\u82e6\u3002\u201d \u3000\u3000\u9648\u6c9b\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u5dee\u4e8b\uff0c\u5b9e\u5728\u662f\u5f88\u96be\u5fcd\u53d7\u3002\u201d \u3000\u3000\u5f6d\u9707\u9053\uff1a\u201c\u5403\u7684\u4e0d\u597d\uff0e\u7761\u7684\u4e0d\u597d\uff0c\u6700\u96be\u8fc7\u7684\u662f\uff0c\u4e24\u4e2a\u6708\u6765\u672a\u78b0\u8fc7\u5973\u4eba\uff0c\u8fd9\u5473\u9053\uff0c\u5c31\u50cf\u5728\u706b\u4e0a\u70e4\u7684\u4e00\u6837\uff0c\u70e4\u7684\u53eb\u4eba\u96be\u8fc7\u3002\u201d \u3000\u3000\u9648\u6c9b\u9053\uff1a\u201c\u5c60\u8001\uff0c\u4f60\u80fd\u4e0d\u80fd\u5e2e\u4e2a\u5fd9\uff0c\u7ed9\u54b1\u4eec\u8c03\u4e2a\u5dee\u4e8b\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e24\u4f4d\u6c5f\u6e56\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u6b66\u529f\u9ad8\u660e\uff0c\u624d\u5f97\u5821\u4e3b\u8d4f\u8bc6\u8c03\u6765\u6b64\u5730\uff0c\u5e2e\u52a9\u5b8c\u6210\u5723\u6c34\u4ea4\u6613\uff0c\u600e\u53ef\u8f7b\u6613\u8a00\u9000\u3002\u201d \u3000\u3000\u9648\u6c9b\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u751f\u6d3b\uff0c\u8fd8\u53ef\u71ac\u53d7\u5f97\u4f4f\uff0c\u4f46\u6211\u4eec\u8fd9\u4e9b\u65e5\u5b50\u91cc\uff0c\u8fde\u5973\u4eba\u90fd\u65e0\u6cd5\u78b0\u4e00\u4e0b\uff0c\u5b9e\u5728\u662f\u96be\u8fc7\u7684\u5f88\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\uff0c\u501f\u91cd\u4e24\u4f4d\uff0c\u4e5f\u59d4\u5c48\u4e86\u4e24\u4f4d\uff0c\u6211\u770b\uff0c\u8fd9\u5723\u6c34\u4ea4\u6613\u5f88\u5feb\u7684\u5c31\u8981\u6210\u4e86\uff0c\u4e24\u4f4d\u518d\u71ac\u53d7\u4e00\u6bb5\u65f6\u95f4\uff0c\u4e00\u65e6\u5723\u6c34\u4ea4\u6613\u5b8c\u6210\uff0c\u5fc5\u7136\u662f\u5927\u529f\u4e00\u4ef6\uff0c\u90a3\u65f6\uff0c\u5821\u4e3b\u53ef\u4ee5\u7ed9\u4e24\u4f4d\u51e0\u65e5\u5047\u671f\uff0c\u4e24\u4f4d\u4e5f\u53ef\u4ee5\u597d\u597d\u7684\u73a9\u4e00\u73a9\u4e86\u3002\u201d \u3000\u3000\u9648\u6c9b\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u90a3\u4e5f\u53ea\u597d\u5982\u6b64\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u4f4e\u58f0\u9053\uff1a\u201c\u4e24\u4f4d\u597d\u597d\u5b88\u5728\u8fd9\u91cc\uff0c\u6709\u4ec0\u4e48\u6d88\u606f\uff0c\u7acb\u523b\u901a\u77e5\u6211\u3002\u201d \u3000\u3000\u5e26\u7740\u53f6\u957f\u9752\u79bb\u5f00\u4e86\u8305\u68da\u3002 \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u5c60\u5144\uff0c\u770b\u6837\u5b50\uff0c\u4ed6\u4eec\u4e24\u4eba\u786e\u5b9e\u89c9\u5f97\u4e07\u5206\u75db\u82e6\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u8083\u7136\u8bf4\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u4f60\u4e0d\u660e\u767d\uff0c\u8fd9\u4e9b\u4eba\u653e\u8361\u60ef\u4e86\uff0c\u6574\u65e5\u548c\u9152\u8089\u5973\u4eba\u4e3a\u4f0d\uff0c\u4e00\u65e6\u8981\u4ed6\u4eec\u8fc7\u8fd9\u79cd\u6751\u5c45\u751f\u6d3b\uff0c\u771f\u6bd4\u6740\u4e86\u4ed6\u4eec\u8fd8\u96be\u8fc7\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u9053\uff1a\u201c\u50cf\u9648\u6c9b\u3001\u5f6d\u9707\u8fd9\u4e24\u4e2a\u4eba\u653e\u8361\u4e3a\u6076\u4e4b\u4eba\uff0c\u8fd9\u4e00\u6b21\u51e0\u4e2a\u6708\u7684\u5b89\u5206\u5b88\u5df1\uff0c\u4e5f\u591f\u4ed6\u4eec\u53d7\u4e86\u3002\u201d \u3000 \u7b2c \u56db \u56de\u3000\u5723\u6c34\u98ce\u6ce2 \u3000 \u3000\u3000\u53f6\u957f\u9752\u5c31\u8fd9\u6837\u4f4f\u4e86\u4e0b\u6765\uff0c\u4ed6\u6bcf\u65e5\u82e6\u7ec3\u6b66\u529f\u3002 \u3000\u3000\u7ecf\u8fc7\u4e86\u8fd9\u4e00\u756a\u632b\u6298\uff0c\u4ed6\u77e5\u9053\u6c5f\u6e56\u4e0a\u7684\u9ad8\u4eba\u5b9e\u5728\u5f88\u591a\u3002 \u3000\u3000\u6021\u7ea2\u4e00\u76f4\u966a\u7740\u4ed6\uff0c\u5251\u6c14\u7f8e\u4eba\uff0c\u7ea2\u8896\u6dfb\u9999\uff0c\u65e5\u5b50\u8fc7\u5f97\u5b9e\u5728\u5f88\u8212\u9002\u3002 \u3000\u3000\u4f46\u8212\u9002\u7684\u65e5\u5b50\uff0c\u4e00\u5411\u90fd\u4e0d\u4f1a\u592a\u4e45\u3002 \u3000\u3000\u7b2c\u516b\u5929\uff0c\u5c60\u65e0\u65b9\u6765\u4e86\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u672c\u6765\u4e5f\u4f4f\u8fd9\u91cc\uff0c\u4f46\u5f88\u5c11\u56de\u6765\u8fc7\uff0c\u8fd9\u516b\u5929\u4e2d\uff0c\u53ea\u56de\u6765\u8fc7\u4e00\u6b21\uff0c\u90a3\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u665a\u9910\u7684\u65f6\u523b\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u5374\u662f\u4e2d\u5348\u3002 \u3000\u3000\u679c\u7136\uff0c\u5c60\u65e0\u65b9\u5e26\u6765\u4e86\u5f88\u91cd\u8981\u7684\u6d88\u606f\u3002\u5723\u6c34\u7684\u6bd4\u4ef7\u65f6\u523b\uff0c\u5df2\u7ecf\u51b3\u5b9a\u4e86\uff0c\u5c31\u5728\u4eca\u591c\u4e8c\u66f4\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u6b63\u5728\u5348\u996d\uff0c\u53f6\u957f\u9752\u5374\u7acb\u523b\u653e\u4e0b\u4e86\u7897\u7b77\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u671b\u671b\u5929\u8272\u63a5\u9053\uff1a\u201c\u521d\u66f4\u65f6\u6211\u5e26\u4f60\u53bb\u90a3\u4e2a\u5730\u65b9\uff0c\u73b0\u5728\u8fd8\u6709\u5927\u534a\u5929\u65f6\u95f4\uff0c\u6211\u4eec\u5f97\u4f11\u606f\u4e00\u4e0b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5982\u679c\u4ed6\u4eec\u518d\u665a\u4e24\u5929\uff0c\u5821\u4e3b\u4e5f\u8bb8\u4f1a\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e16\u4e0a\u4e8b\uff0c\u603b\u6709\u5f88\u591a\u4e0d\u5982\u610f\u7684\u5730\u65b9\uff0c\u8fd9\u4e00\u6b21\uff0c\u5723\u6c34\u4ea4\u6613\u8981\u4ed7\u51ed\u4f60\u53f6\u8001\u5f1f\u5f88\u591a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\uff0c\u6211\u4f1a\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6211\u76f8\u4fe1\uff0c\u6211\u4eec\u673a\u4f1a\u5f88\u5927\uff0c\u4e0d\u8fc7\uff0c\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\u5c31\u662f\u8981\u5fcd\u8010\uff0c\u6211\u60f3\u4e0d\u51fa\uff0c\u4ed6\u4eec\u5982\u4f55\u5b89\u6392\u5723\u6c34\u4ea4\u6613\uff0c\u4f46\u4e00\u5b9a\u6709\u5f88\u591a\u4eba\u53c2\u4e0e\uff0c\u4e00\u4e2a\u4e0d\u597d\uff0c\u5f88\u53ef\u80fd\u4f1a\u9020\u6210\u4f17\u77e2\u4e4b\u7684\u3002\u201d \u3000\u3000 \u3000\u3000\u521d\u66f4\u65f6\u5206\uff0c\u5c60\u65e0\u65b9\u548c\u53f6\u957f\u9752\u90fd\u6362\u4e86\u4e00\u8eab\u9ed1\u8272\u7684\u957f\u886b\u3002 \u3000\u3000\u90a3\u662f\u5723\u6c34\u4ea4\u6613\u6307\u5b9a\u7684\u7a7f\u7740\u7684\u8863\u670d\u3002 \u3000\u3000\u53f6\u957f\u9752\u65e0\u6cd5\u5e26\u4e0a\u60ef\u7528\u7684\u957f\u5251\uff0c\u53ea\u5f97\u6613\u4e86\u4e00\u628a\u77ed\u5251\uff0c\u6697\u85cf\u5728\u8eab\u4e0a\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u624b\u4e2d\u63d0\u4e86\u4e00\u4eca\u63cf\u91d1\u5c0f\u6728\u7bb1\u5b50\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u6ca1\u6709\u884c\u5411\u9547\u4e2d\u7684\u9152\u5e97\uff0c\u5374\u5230\u4e86\u9547\u5916\u90ca\u91ce\u3002 \u3000\u3000\u90a3\u662f\u4e00\u5904\u6d45\u5c71\u5761\u4e0b\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u4f3c\u4e4e\u65e9\u5df2\u80f8\u6709\u6210\u7af9\uff0c\u884c\u5230\u4e00\u682a\u5927\u6995\u6811\u4e0b\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u521a\u521a\u505c\u4e0b\u811a\u6b65\uff0c\u6995\u6811\u4e0a\u5df2\u98d8\u4e0b\u6765\u4e00\u4e2a\u6e05\u51b7\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u662f\u9634\u9633\u5821\u7684\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u662f\uff01\u5728\u4e0b\u5c60\u65e0\u65b9\u3002\u201d \u3000\u3000\u6811\u4e0a\u4eba\u9053\uff1a\u201c\u5e26\u6709\u94f6\u94b1\u5417\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u90fd\u5728\u8fd9\u6728\u7bb1\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u6811\u4e0a\u4eba\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u4e24\u9876\u7279\u5236\u5e3d\u5b50\uff0c\u4e24\u4f4d\u53ef\u4ee5\u6234\u4e0a\uff0c\u53ea\u9732\u51fa\u4e24\u53ea\u773c\u775b\uff0c\u5723\u6c34\u4ea4\u6613\uff0c\u4e00\u5411\u79d8\u5bc6\uff0c\u4e24\u4f4d\u8981\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u6811\u4e0a\u4e22\u4e0b\u6765\u4e24\u9876\u5e3d\u5b50\u3002 \u3000\u3000\u90a3\u662f\u5f88\u539a\u7684\u9ed1\u5e03\u505a\u6210\u7684\u5e3d\u5b50\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u548c\u53f6\u957f\u9752\uff0c\u90fd\u4f9d\u8a00\u6234\u4e0a\uff0c \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u9053\uff1a\u770b\u6765\uff0c\u4e3b\u6301\u5723\u6c34\u4ea4\u6613\u7684\u4eba\uff0c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u597d\u4eba\uff0c\u5355\u662f\u8fd9\u5206\u8be1\u79d8\uff0c\u5c31\u4e0d\u50cf\u6b63\u5f53\u4eba\u7269\u3002 \u3000\u3000\u8fd9\u672c\u662f\u4e00\u79cd\u4e70\u5356\uff0c\u4e0d\u77e5\u4f55\u6545\uff0c\u7adf\u7136\u5b89\u6392\u5f97\u5982\u6b64\u5947\u79d8\u3001\u8be1\u5f02\u3002 \u3000\u3000\u53ea\u542c\u6811\u4e0a\u4eba\u8bf4\u9053\uff1a\u201c\u4e3a\u4e86\u4fdd\u6301\u4f60\u4eec\u7684\u8eab\u4efd\uff0c\u7531\u73b0\u5728\u5f00\u59cb\uff0c\u4f60\u4eec\u8981\u8bb0\u7740\u4e00\u4e2a\u4ee3\u53f7\uff0c\u4e24\u4f4d\u7684\u4ee3\u53f7\u662f\u5341\u4e03\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e24\u4e2a\u4eba\uff0c\u90fd\u662f\u5341\u4e03\u53f7\u5417\uff1f\u201d \u3000\u3000\u6811\u4e0a\u4eba\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u7ba1\u4eba\u6570\uff0c\u5341\u4e03\u53f7\u5c31\u662f\u8d35\u5821\u7684\u4ee3\u53f7\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u4eec\u7531\u6b64\u5f80\u5357\u8d70\uff0c\u770b\u5230\u4e00\u5ea7\u519c\u820d\u65f6\uff0c\u81ea\u4f1a\u6709\u4eba\u62db\u547c\u4f60\u4eec\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u591c\u8272\u5e7d\u6697\uff0c\u4e0d\u4f1a\u8d70\u9519\u4e86\u8def\u5427\uff1f\u201d \u3000\u3000\u6811\u4e0a\u4eba\u9053\uff1a\u201c\u53ea\u6709\u2014\u6761\u5c0f\u5f84\u53ef\u7ecf\u519c\u820d\uff0c\u4f60\u4eec\u76f4\u8d70\u8fc7\u53bb\u5c31\u662f\u3002\u201d \u3000\u3000\u4e24\u4eba\u4f9d\u8a00\u5411\u5357\u884c\u53bb\u3002 \u3000\u3000\u679c\u7136\u53ea\u6709\u4e00\u6761\u5c0f\u5f84\uff0c\u4e24\u4fa7\u90fd\u662f\u6c34\u7530\u3002 \u3000\u3000\u884c\u7ea6\u4e00\u91cc\uff0c\u591c\u8272\u4e2d\uff0c\u679c\u7136\u77d7\u7acb\u7740\u4e00\u5ea7\u519c\u820d\u3002 \u3000\u3000\u8ddd\u519c\u820d\u8fd8\u6709\u4e08\u8bb8\uff0c\u4e00\u4e2a\u51b7\u51b7\u7684\u58f0\u97f3\uff0c\u5df2\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u505c\u6b65\uff0c\u62a5\u4e0a\u7f16\u53f7\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5341\u4e03\u53f7\u3002\u201d \u3000\u3000\u90a3\u51b7\u51b7\u7684\u58f0\u97f3\u9053\uff1a\u201c\u8fd9\u8305\u820d\u4e4b\u524d\u7684\u5ead\u9662\u4e4b\u4e2d\uff0c\u6709\u4e00\u8f86\u7b49\u5019\u8bf8\u4f4d\u7684\u7bf7\u8f66\uff0c\u4f60\u4eec\u4e0a\u8f66\u53bb\u5427\uff01\u201d \u3000\u3000\u5c60\u65e0\u65b9\u4f9d\u8a00\u884c\u53bb\uff0c\u679c\u89c1\u8305\u820d\u524d\u9762\u7684\u5c0f\u5e7f\u573a\u4e0a\uff0c\u505c\u4e86\u4e00\u8f86\u7bf7\u8f66\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u7bf7\u8f66\u4e0a\u5df2\u7ecf\u5750\u4e86\u4e0d\u5c11\u7684\u4eba\u3002 \u3000\u3000\u90a3\u662f\u7ecf\u8fc7\u7279\u522b\u8bbe\u8ba1\u7684\u7bf7\u8f66\uff0c\u8f66\u4e0a\u5171\u6709\u5341\u4e2a\u5ea7\u4f4d\uff0c\u73b0\u5728\u5df2\u7ecf\u5750\u4e86\u516b\u4e2a\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\uff0c\u90fd\u662f\u4e00\u6837\u7684\u88c5\u675f\uff0c\u9ed1\u8272\u7684\u8863\u670d\uff0c\u7f69\u7740\u5934\u7751\u7684\u5e3d\u5b50\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\uff0c\u90fd\u53ea\u9732\u51fa\u4e24\u53ea\u773c\u775b\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u3001\u53f6\u957f\u9752\uff0c\u767b\u4e0a\u4e86\u7bf7\u8f66\u4e4b\u540e\uff0c\u7bf7\u8f66\u7acb\u523b\u5411\u524d\u884c\u9a70\u800c\u53bb\u3002 \u3000\u3000\u8f66\u95e8\u5173\u4e0a\u4e86\uff0c\u8f66\u4e2d\u4e00\u7247\u9ed1\u3002 \u3000\u3000\u9664\u4e86\u53ef\u89c1\u4e8c\u5341\u53ea\u95ea\u5149\u7684\u773c\u775b\u4e4b\u5916\uff0c\u542c\u4e0d\u5230\u4e00\u70b9\u58f0\u606f\u3002 \u3000\u3000\u8f6e\u58f0\u8f98\u8f98\uff0c\u5212\u7834\u4e86\u6c89\u5bc2\uff0c\u4e5f\u5212\u7834\u4e86\u591c\u8272\u7684\u5e7d\u79d8\u3002 \u3000\u3000\u7bf7\u8f66\u7ec8\u4e8e\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4f46\u5750\u5728\u8f66\u5185\u7684\u4eba\uff0c\u90fd\u5f88\u6c89\u7740\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u5f00\u53e3\u8bf4\u8bdd\uff0c\u4f3c\u4e4e\u662f\u4e00\u5207\u90fd\u5728\u7b49\u5f85\u5bf9\u65b9\u7684\u62db\u547c\u3002 \u3000\u3000\u679c\u7136\uff0c\u4e00\u76cf\u70ed\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u7bf7\u8f66\u7684\u95e8\u6237\u5927\u5f00\u3002 \u3000\u3000\u4e00\u4e2a\u6e05\u51b7\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u4e0b\u8f66\uff0c\u8bf7\u5165\u5927\u5385\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e00\u5ea7\u5f88\u5927\u7684\u56db\u5408\u9662\uff0c\u548c\u4e00\u822c\u7684\u6c11\u623f\u5927\u81f4\u7c7b\u4f3c\uff0c\u53ea\u662f\u5927\u4e00\u4e9b\u3002 \u3000\u3000\u6d6e\u4e91\u63a9\u7a7a\uff0c\u8fde\u4e00\u9897\u661f\u661f\u4e5f\u770b\u4e0d\u5230\u3002 \u3000\u3000\u9a6c\u8f66\u5c31\u505c\u5728\u9662\u4e2d\u3002 \u3000\u3000\u53f6\u957f\u9752\u8fd0\u8db3\u76ee\u529b\u770b\u53bb\uff0c\u53d1\u89c9\u9662\u4e2d\u4e00\u5171\u505c\u4e86\u4e09\u8f86\u7bf7\u8f66\u3002 \u3000\u3000\u6b63\u5385\u7684\u6728\u95e8\uff0c\u65e9\u5df2\u5927\u5f00\uff0c\u4f46\u5385\u4e2d\u4e00\u7247\u6f06\u9ed1\uff0c\u4e0d\u89c1\u706f\u5149\u3002 \u3000\u3000\u90a3\u6e05\u51b7\u7684\u58f0\u97f3\uff0c\u53c8\u4ece\u5927\u5385\u4e2d\u4f20\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u8bf7\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7b49\u4eba\uff0c\u5927\u6982\u662f\u6700\u540e\u4e00\u8f66\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e00\u76f4\u7d27\u968f\u5728\u5c60\u65e0\u65b9\u7684\u8eab\u540e\uff0c\u4e0d\u6562\u7a0d\u6709\u8ddd\u79bb\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u6240\u6709\u7684\u4eba\uff0c\u90fd\u7a7f\u7740\u4e00\u6837\u7684\u8863\u670d\uff0c\u6234\u7740\u4e00\u6837\u7684\u5e3d\u5b50\uff0c\u751a\u81f3\u8fde\u624b\u4e2d\u63d0\u7684\u7bb1\u5b50\uff0c\u90fd\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u5982\u662f\u7a0d\u4e0d\u5c0f\u5fc3\uff0c\u518d\u60f3\u627e\u5230\u5c60\u65e0\u65b9\uff0c\u5b9e\u5728\u4e0d\u592a\u5bb9\u6613\u3002 \u3000\u3000\u5341\u4e2a\u9ed1\u8863\u4eba\uff0c\u9c7c\u8d2f\u884c\u5165\u5385\u4e2d\u3002 \u3000\u3000\u6bcf\u4e00\u7ec4\u4e24\u4e2a\u4eba\uff0c\u540c\u6837\u7684\u5fc3\u7406\uff0c\u90fd\u62c5\u5fc3\u548c\u540c\u4f34\u8d70\u6563\u4e86\uff0c\u4e3e\u6b65\u90fd\u5f88\u8c28\u614e\u3002 \u3000\u3000\u51e0\u4eba\u884c\u5165\u5385\u4e2d\u4e4b\u540e\uff0c\u5385\u95e8\u7a81\u7136\u5173\u4e86\u8d77\u6765\u3002 \u3000\u3000\u7136\u540e\uff0c\u5927\u5385\u4e2d\u7a81\u7136\u95ea\u8d77\u4e00\u9053\u706b\u5149\uff0c\u7d27\u63a5\u7740\u5927\u653e\u5149\u660e\u3002 \u3000\u3000\u56db\u76cf\u9ad8\u706f\uff0c\u516d\u53ea\u706b\u70db\uff0c\u4e00\u8d77\u71c3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u76ee\u5149\u8f6c\u52a8\uff0c\u770b\u6e05\u695a\u4e86\u5385\u4e2d\u7684\u5e03\u7f6e\u3002 \u3000\u3000\u53ea\u89c1\u4e0a\u9996\u4e00\u5f20\u957f\u5f62\u7684\u6728\u6848\uff0c\u6728\u6848\u540e\u9762\uff0c\u5750\u7740\u4e09\u4e2a\u4eba\uff0c\u53e6\u5916\u6709\u5341\u4e8c\u5f20\u5c0f\u5f62\u65b9\u684c\uff0c\u4e0a\u9762\u90fd\u6446\u597d\u4e86\u53f7\u724c\u3002 \u3000\u3000\u53f6\u957f\u9752\u3001\u5c60\u65e0\u65b9\uff0c\u884c\u5411\u4e86\u5341\u4e03\u53f7\u684c\u4f4d\u3002 \u3000\u3000\u4e8b\u60c5\u867d\u7136\u5f88\u795e\u79d8\uff0c\u5982\u51b7\u9759\u7684\u89c2\u5bdf\uff0c\u4ecd\u6709\u8109\u7edc\u53ef\u5bfb\u3002 \u3000\u3000\u5341\u4e8c\u5f20\u5c0f\u684c\u5b50\u4e0a\uff0c\u90fd\u5750\u4e86\u4eba\u3002\u800c\u4e14\uff0c\u6bcf\u4e00\u5f20\u5c0f\u6728\u684c\u4e0a\uff0c\u90fd\u662f\u5750\u4e86\u4e24\u4e2a\u4eba\uff0c\u5176\u4e2d\u4e00\u4eba\uff0c\u63d0\u7740\u6728\u7bb1\uff0c\u8bf4\u660e\u4e86\uff0c\u6709\u5341\u4e8c\u4e2a\u7ec4\u7ec7\uff0c\u53c2\u4e0e\u4e86\u4eca\u5e74\u7684\u5723\u6c34\u4ea4\u6613\u3002 \u3000\u3000\u6709\u4e00\u70b9\u4f7f\u53f6\u957f\u9752\u4e0d\u660e\u767d\uff0c\u660e\u660e\u53ea\u6709\u5341\u4e8c\u5f20\u684c\u5b50\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u6709\u5341\u4e03\u4e2a\u7f16\u53f7\u3002 \u3000\u3000\u4f46\u53f6\u957f\u9752\u5f88\u5feb\u53c8\u4e86\u89e3\u4e0b\u4e2a\u4e2d\u7684\u5185\u60c5\u3002 \u3000\u3000\u539f\u6765\u90a3\u5341\u4e8c\u5f20\u684c\u5b50\u4e0a\u7684\u7f16\u53f7\uff0c\u5e76\u975e\u7531\u4e00\u81f3\u5341\u4e8c\uff0c\u800c\u662f\u7f16\u53f7\u4e0d\u540c\uff0c\u6700\u540e\u4e00\u4e2a\u53f7\u7801\uff0c\u7adf\u7136\u662f\u4e8c\u5341\u4e5d\u53f7\u3002 \u3000\u3000\u90a3\u662f\u8bf4\uff0c\u53c2\u4e0e\u8fd9\u573a\u751f\u610f\u7684\uff0c\u81f3\u5c11\u6709\u4e8c\u5341\u4e5d\u4e2a\u4eba\uff0c\u4f46\u5374\u4ece\u4e8c\u5341\u4e5d\u4eba\u4e2d\u9009\u62e9\u4e86\u5341\u4e8c\u4e2a\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5750\u5728\u90a3\u957f\u684c\u540e\u9762\u7684\u4e09\u4e2a\u9ed1\u8863\u4eba\u5c45\u4e2d\u7684\u4e00\u4e2a\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\uff0c\u8fd9\u4e00\u6b21\u5723\u6c34\u4ea4\u6613\uff0c\u6709\u5341\u4e8c\u5bb6\u5165\u9009\uff0c\u5c31\u662f\u5728\u5ea7\u5217\u4f4d\uff0c\u5723\u6c34\u4e24\u6876\uff0c\u4e00\u9ed1\u4e00\u767d\uff0c\u6bcf\u4e00\u6876\u6709\u516d\u5341\u65a4\uff0c\u662f\u5386\u6765\u6570\u91cf\u6700\u591a\u7684\u2014\u6b21\uff0c\u4e3a\u4e86\u6c42\u5f97\u516c\u5e73\uff0c\u6211\u4eec\u628a\u8bf8\u4f4d\u5341\u4e8c\u4e2a\u7ec4\u7ec7\u7684\u4ee3\u8868\uff0c\u5168\u90fd\u8bf7\u5230\u4e86\u6b64\u5730\uff0c\u7531\u8bf8\u4f4d\u516c\u5f00\u51fa\u9636\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u9707\u52a8\u4e00\u4e0b\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u6bcf\u4e24\u4e2a\u4eba\u4ee3\u8868\u4e00\u4e2a\u9ed1\u9053\u7ec4\u7ec7\uff0c\u5355\u662f\u5728\u5ea7\u7684\u5c31\u6709\u4e00\u5341\u4e8c\u4e2a\u4e4b\u591a\uff0c\u8fde\u540c\u5254\u9664\u7684\u7ec4\u7ec7\uff0c\u81f3\u5c11\u6709\u4e8c\u5341\u4e5d\u4e2a\u4e4b\u591a\uff0c\u600e\u7684\u6211\u5e38\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u7adf\u7136\u672a\u66fe\u542c\u8fc7\u3002 \u3000\u3000\u4e5d\u53f7\u684c\u4f4d\u4e0a\u7684\u4e00\u4e2a\u9ed1\u8863\u4eba\uff0c\u7a81\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u4eec\u51fa\u4ef7\u4e8c\u5341\u4e07\uff0c\u94f6\u7968\u5728\u4e0b\u5df2\u7ecf\u5e26\u6765\uff0c\u6216\u7531\u73b0\u503c\u7684\u73e0\u5b9d\u62b5\u4ef7\u90fd\u597d\u3002\u201d \u3000\u3000\u4e0a\u9996\u5c45\u4e2d\u7684\u9ed1\u8863\u4eba\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u94f6\u7968\u4e5f\u597d\uff0c\u73b0\u503c\u73e0\u5b9d\u62b5\u4ef7\u4e5f\u597d\uff0c\u6211\u4eec\u4ece\u6765\u6ca1\u6709\u5403\u8fc7\u5012\u5e10\uff0c\u4f60\u4ed8\u94f6\u7968\uff0c\u6211\u4eec\u5fc5\u987b\u94f6\u7968\u5151\u73b0\u4e4b\u540e\uff0c\u624d\u4f1a\u4ea4\u51fa\u5723\u6c34\uff0c\u4f60\u5982\u662f\u4ed8\u73e0\u5b9d\uff0c\u4e5f\u5fc5\u987b\u7b49\u6211\u4eec\u9274\u5b9a\u8fc7\u4e4b\u540e\uff0c\u624d\u4f1a\u4ea4\u51fa\u5723\u6c34\u3002\u201d \u3000\u3000\u4e5d\u53f7\u9ed1\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u8bf4\u7684\u662f\uff0c\u5723\u6c34\u4ea4\u6613\uff0c\u5728\u4e0b\u76f8\u4fe1\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4eba\u4f7f\u7528\u8bc8\u672f\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5341\u4e8c\u53f7\u684c\u4f4d\u4e0a\u7684\u9ed1\u8863\u4eba\uff0c\u7a81\u7136\u7ad9\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u5341\u4e8c\u53f7\u51fa\u4ef7\u4e8c\u5341\u4e94\u4e07\u3002\u201d \u3000\u3000\u5341\u4e94\u53f7\u684c\u4f4d\u4e0a\u7684\u9ed1\u8863\u4eba\uff0c\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u5341\u4e94\u53f7\u51fa\u4ef7\u4e09\u5341\u4e07\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5341\u4e03\u53f7\u51fa\u4ef7\u4e09\u5341\u4e94\u4e07\u3002\u201d \u3000\u3000\u5eff\u4e5d\u53f7\u684c\u4f4d\u4e0a\u7684\u9ed1\u8863\u4eba\uff0c\u9053\uff1a\u201c\u4e8c\u5341\u4e5d\u53f7\u51fa\u4ef7\u56db\u5341\u4e07\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5927\u4e3a\u9707\u52a8\uff0c\u5fd6\u9053\uff1a\u8fd9\u4e9b\u4eba\uff0c\u600e\u4f1a\u5982\u6b64\u7684\u6709\u94b1\uff0c\u60f3\u60f3\u51e0\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u4e0d\u662f\u4e2a\u5c0f\u6570\u5b57\uff0c\u4ed6\u4eec\u8bf4\u6765\uff0c\u7adf\u7136\u662f\u5982\u6b64\u7684\u8f7b\u677e\u3002 \u3000\u3000\u4e00\u4e0b\u5b50\u628a\u4ef7\u94b1\u54c4\u62ac\u5982\u6b64\u4e4b\u9ad8\uff0c\u5385\u4e2d\u7acb\u65f6\u6c89\u5bc2\u4e0b\u6765\u3002 \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5982\u662f\u6ca1\u6709\u4eba\u518d\u51fa\u9ad8\u4ef7\uff0c\u5723\u6c34\u5c31\u662f\u5eff\u4e5d\u53f7\u7684\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u4e00\u54ac\u7259\u9053\uff1a\u201c\u5341\u4e03\u53f7\u51fa\u4ef7\u56db\u5341\u4e94\u4e07\u3002\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u5168\u573a\u90fd\u4e3a\u4e4b\u9e26\u96c0\u65e0\u58f0\u4e86\uff0c\u56db\u5341\u4e94\u4e07\u94f6\u5b50\uff0c\u662f\u4e2a\u5f88\u5927\u7684\u6570\u76ee\u3002 \u3000\u3000\u53f6\u957f\u9752\u5618\u4e86\u4e00\u53e3\u6c14\uff0c\u5fc3\u60f3\uff1a\u56db\u5341\u4e94\u4e07\u94f6\u5b50\u5356\u4e86\u4e24\u6876\u5723\u6c34\uff0c\u90a3\u5723\u6c34\u7a76\u7adf\u80fd\u505a\u4ec0\u4e48\u7528\u5462\uff1f \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u4eba\u518d\u52a0\u94f6\u5b50\u5417\uff1f\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u684c\u4f4d\u4e0a\u7684\u9ed1\u8863\u4eba\u7ad9\u8d77\u6765\uff0c\u518d\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u663e\u7136\uff0c\u4ed6\u5f88\u60f3\u518d\u52a0\uff0c\u4e5f\u8bb8\u662f\u8d22\u529b\u4e0d\u8db3\uff0c\u4e5f\u53ef\u80fd\u662f\u6743\u529b\u4e0d\u591f\u3002 \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4e2d\u7a81\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u597d\uff0c\u65e2\u7136\u65e0\u4eba\u518d\u52a0\u4ef7\uff0c\u5723\u6c34\u6210\u4ea4\u4e86\u3002\u201d \u3000\u3000\u4f46\u5750\u5728\u5927\u5385\u4e2d\u7684\u4eba\uff0c\u6ca1\u6709\u6563\u53bb\uff0c\u4e5f\u6ca1\u6709\u4eba\u7ad9\u8d77\u8eab\u5b50\u3002 \u3000\u3000\u4f3c\u4e4e\u662f\uff0c\u8fd9\u4e9b\u4eba\u5bf9\u5723\u6c34\u6210\u4ea4\u4e00\u4e8b\uff0c\u6709\u4e9b\u4e0d\u670d\u3002 \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u53c8\u9053\uff1a\u201c\u5341\u4e03\u53f7\uff0c\u4f60\u5e26\u7684\u94f6\u7968\uff0c\u8fd8\u662f\u73e0\u5b9d\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4f60\u4eec\u8981\u94f6\u7968\uff0c\u8fd8\u662f\u8981\u73e0\u5b9d\uff1f\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u90fd\u884c\uff0c\u4f60\u4ea4\u51fa\u94f6\u7968\u6216\u73e0\u5b9d\uff0c\u4e09\u65e5\u4e4b\u540e\uff0c\u6211\u4eec\u624d\u80fd\u4ea4\u51fa\u5723\u6c34\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u56db\u5341\u4e94\u4e07\u94f6\u5b50\uff0c\u6570\u76ee\u5f88\u5927\uff0c\u4e07\u4e00\u5723\u6c34\u4e0d\u80fd\u5982\u671f\u4ea4\u8d37\uff0c\u655d\u4e0a\u51b3\u4e0d\u4f1a\u539f\u8c05\u6211\u3002\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5723\u6c34\u4ea4\u6613\uff0c\u4ece\u672a\u5931\u8fc7\u7ea6\uff0c\u4f60\u5fc5\u987b\u4fe1\u4efb\u6211\u4eec\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5728\u4e0b\u73b0\u5728\u4ed8\u6b3e\uff0c\u4f46\u4e0d\u77e5\u51e0\u65f6\u53ef\u4ee5\u6536\u5230\u5723\u6c34\u3002\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u73b0\u5728\u4ed8\u6b3e\uff0c\u4e09\u65e5\u540e\u53ef\u4ee5\u6536\u5230\u5723\u6c34\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e09\u65e5\u4e4b\u540e\uff0c\u5728\u4e0b\u5230\u54ea\u91cc\u53bb\u53d6\u5723\u6c34\u3002\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4ed8\u6b3e\u4e4b\u540e\uff0c\u4f60\u53ef\u4ee5\u5f97\u5230\u9526\u56ca\u4e00\u53ea\uff0c\u8fd9\u9526\u56ca\u4e4b\u5185\uff0c\u4f1a\u8bf4\u660e\u4ea4\u8d27\u5730\u65b9\uff0c\u53d6\u8d37\u65b9\u6cd5\uff0c\u5723\u6c34\u4ea4\u6613\uff0c\u6709\u4e00\u5b9a\u7684\u79d8\u5bc6\u65b9\u6cd5\uff0c\u4e5f\u6709\u4e00\u5957\u5f88\u5b8c\u6574\u7684\u529e\u6cd5\uff0c\u8fd9\u4e2a\u529e\u6cd5\uff0c\u662f\u7ecf\u8fc7\u4e86\u6211\u4eec\u5f88\u957f\u65f6\u95f4\u7814\u7a76\u51fa\u6765\u7684\u529e\u6cd5\uff0c\u4e07\u65e0\u4e00\u5931\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4f60\u4eec\u662f\u8981\u94f6\u7968\u5462\uff1f\u8fd8\u662f\u73e0\u5b9d\u3002\u201d \u3000\u3000\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u51c6\u5907\u6253\u5f00\u6728\u7bb1\u3002 \u3000\u3000\u53ef\u662f\u5c45\u4e2d\u9ed1\u8863\u4eba\u6447\u6447\u624b\uff0c\u62e6\u963b\u4e86\u5c60\u65e0\u65b9\uff0c\u9053\uff1a\u201c\u6162\u7740\uff0c\u8d22\u4e0d\u9732\u767d\uff0c\u4e14\u4e0d\u53ef\u62ff\u51fa\u8d22\u7269\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u4ea4\u6613\u5df2\u5b8c\uff0c\u5982\u662f\u6ca1\u6709\u522b\u7684\u4e8b\uff0c\u8bf8\u4f4d\u53ef\u4ee5\u5148\u8d70\u4e00\u6b65\u4e86\u3002\u201d \u3000\u3000\u5927\u90e8\u5206\u7684\u4eba\uff0c\u90fd\u7f13\u7f13\u7ad9\u8d77\u4e86\u8eab\u3002 \u3000\u3000\u7531\u4ed6\u4eec\u61d2\u6563\u7684\u52a8\u4f5c\u4e2d\uff0c\u53ef\u4ee5\u770b\u51fa\u6765\uff0c\u5bf9\u8fd9\u6b21\u672a\u53d6\u5f97\u5723\u6c34\u7684\u5931\u671b\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5eff\u4e5d\u53f7\u684c\u4f4d\u4e0a\u7684\u9ed1\u8863\u4eba\u7a81\u7136\u53c8\u7ad9\u4e0b\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4e14\u6162\uff0c\u5728\u4e0b\u8fd8\u6709\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u3002\u201d \u3000\u3000\u5eff\u4e5d\u53f7\u9053\uff1a\u201c\u5728\u4e0b\u613f\u51fa\u4e94\u5341\u4e07\u94f6\u5b50\uff0c\u53d6\u5f97\u5723\u6c34\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u597d\u554a\uff01\u6405\u5c40\u7684\u6765\u4e86\uff0c\u4e14\u770b\u4ed6\u4eec\u5982\u4f55\u5904\u7f6e\u3002\u201d \u3000\u3000\u53ea\u542c\u90a3\u5c45\u4e2d\u7684\u9ed1\u8863\u4eba\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u4ea4\u6613\u5df2\u6210\uff0c\u9601\u4e0b\u65e2\u7136\u51fa\u5f97\u8d77\u4e94\u5341\u4e07\u94f6\u5b50\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u4e00\u4e9b\u558a\u4ef7\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9053\uff1a\u201c\u90a3\u662f\u56e0\u4e3a\u5728\u4e0b\u968f\u8eab\u6240\u5e26\u94f6\u4e24\u4e0d\u8db3\u3002\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u73b0\u5728\u5c31\u591f\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9053\uff1a\u201c\u591f\u4e86\uff0c\u5728\u4e0b\u521a\u521a\u501f\u5f97\u5341\u4e07\u94f6\u7968\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u5f88\u53ef\u60dc\uff0c\u5723\u6c34\u5df2\u7ecf\u6210\u4ea4\u4e86\uff0c\u660e\u5e74\u8bf7\u65e9\u5427\uff01\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u5728\u4e0b\u5949\u547d\uff0c\u5fc5\u987b\u53d6\u5f97\u5723\u6c34\uff0c\u5426\u5219\u63d0\u5934\u590d\u547d\u3002\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u670b\u53cb\u786e\u6709\u788d\u96be\u4e4b\u5904\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u4e0d\u80fd\u51fa\u5c14\u53cd\u5c14\uff0c\u4e5f\u4e0d\u80fd\u6539\u53d8\u5df2\u6210\u7684\u4ea4\u6613\uff0c\u6211\u4eec\u5fc5\u987b\u7ef4\u6301\u4fe1\u7528\uff0c\u5c31\u7b97\u9601\u4e0b\u80af\u51fa\u767e\u4e07\u94f6\u5b50\uff0c\u90a3\u4e5f\u662f\u660e\u5e74\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9053\uff1a\u201c\u6211\u5982\u53d6\u4e0d\u5230\u5723\u6c34\uff0c\u56de\u53bb\u96be\u514d\u4e00\u6b7b\u3002\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u90a3\u662f\u4f60\u4e2a\u4eba\u4e4b\u4e8b\uff0c\u6211\u4eec\u7231\u83ab\u80fd\u52a9\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9053\uff1a\u201c\u4eca\u5e74\u5723\u6c34\u4ea4\u6613\uff0e\u7a81\u53d8\u5f97\u5927\u5f02\u5f80\u6614\uff0c\u6240\u4ee5\uff0c\u4e5f\u4e0d\u80fd\u5168\u602a\u6211\u4eec\u3002\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u670b\u53cb\uff0c\u8981\u53cd\u5bf9\u8fd9\u79cd\u4ea4\u6613\u529e\u6cd5\uff0c\u5e94\u8be5\u5165\u573a\u4e4b\u540e\u5c31\u63d0\u51fa\u53cd\u5bf9\uff0c\u73b0\u5728\u662f\u4e0d\u662f\u592a\u8fdf\uff01\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9053\uff1a\u201c\u9601\u4e0b\u5982\u80af\u5e2e\u5fd9\uff0c\u65f6\u72b9\u672a\u665a\uff0c\u4f46\u5982\u9601\u4e0b\u4e0d\u80af\u5e2e\u5fd9\uff0c\u90a3\u5c31\u662f\u628a\u5728\u4e0b\u7f6e\u4e8e\u6b7b\u5730\u4e86\u3002\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4f60\u80e1\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u6211\u4eec\u4ea4\u6613\u6709\u4ea4\u6613\u7684\u89c4\u77e9\uff0c\u5c82\u53ef\u56e0\u4e00\u5df2\u4e4b\u79c1\uff0c\u7834\u574f\u5927\u5c40\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u53d6\u56de\u5723\u6c34\uff0c\u96be\u514d\u4e00\u6b7b\uff0c\u90a3\u4e0d\u5982\u73b0\u5728\u6b7b\u4e86\u7b97\u3002\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u9601\u4e0b\u8bf4\u8fd9\u4e9b\u8bdd\uff0c\u90a3\u662f\u8bda\u5fc3\u5a01\u80c1\u6211\u4eec\u4e86\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9053\uff1a\u201c\u5982\u82e5\u5728\u4e0b\u80fd\u591f\u5546\u8bf7\u5341\u4e03\u53f7\u8f6c\u8ba9\uff0c\u8fd9\u5c31\u548c\u5723\u6c34\u4ea4\u6613\u65e0\u5173\u4e86\u3002\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8fd9\u5012\u53ef\u4ee5\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u80fd\u7528\u5f3a\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u7a81\u7136\u4e3e\u6b65\u884c\u5230\u5c60\u65e0\u65b9\u7684\u8eab\u524d\uff0c\u62b1\u62f3\u4e00\u4e2a\u957f\u63d6\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u5144\u53f0\uff0c\u533a\u533a\u82e6\u8877\uff0c\u8c05\u5df2\u77e5\u6653\uff0c\u4f46\u4e0d\u77e5\u9601\u4e0b\u662f\u5426\u80af\u4f53\u8c05\uff0c\u8f6c\u8ba9\u5723\u6c34\uff0c\u533a\u533a\u613f\u4ee5\u4e94\u4e07\u94f6\u5b50\u916c\u8c22\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6211\u7684\u5904\u5883\uff0c\u548c\u4f60\u76f8\u4f3c\uff0c\u5bf9\u5723\u6c34\u4e5f\u662f\u5fd7\u5728\u5fc5\u5f97\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9053\uff1a\u201c\u4f60\u5982\u4e0d\u5f97\u5723\u6c34\uff0c\u603b\u4e0d\u81f3\u4e8e\u8981\u4e86\u6027\u547d\u5427\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5f7c\u6b64\uff0c\u5f7c\u6b64\uff0c\u6211\u5982\u4e0d\u5f97\u5723\u6c34\uff0c\u4e5f\u5f88\u96be\u4ea4\u5dee\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u8138\u4e0a\u6709\u9ed1\u5e03\u63a9\u906e\uff0c\u4f46\u53ef\u4ece\u4ed6\u76ee\u5149\u4e2d\u53d1\u89c9\u4ed6\u7684\u7126\u8651\u548c\u6124\u6012\uff0c\u4f46\u4ed6\u4ecd\u6781\u529b\u5730\u5fcd\u8010\u7740\uff0c\u9053\uff1a\u201c\u79bb\u6b64\u4e4b\u540e\uff0c\u5728\u4e0b\u5f53\u4e13\u7a0b\u62dc\u8bbf\u916c\u8c22\u5927\u6069\uff0c\u800c\u4e14\uff0c\u65e5\u540e\u5fc5\u6709\u4e00\u62a5\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6211\u5982\u6551\u4f60\uff0c\u8c01\u53c8\u80fd\u6551\u6211\u5462\uff1f\u9601\u4e0b\u4e4b\u8bf7\uff0c\u6055\u96be\u5e94\u547d\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u6012\u9053\uff1a\u201c\u670b\u53cb\u5982\u6b64\u4e0d\u901a\u60c5\u7406\uff0c\u4eca\u65e5\u4ea4\u6613\uff0c\u53ea\u6015\u5f88\u96be\u6210\u529f\u4e86\u3002\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u7a81\u7136\u63a5\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u6211\u5df2\u8bf4\u8fc7\uff0c\u4e0d\u8bb8\u5a01\u80c1\uff0c\u522b\u4eba\u65e2\u7136\u4e0d\u540c\u610f\uff0c\u4f60\u4f3c\u4e4e\u7528\u4e0d\u7740\u518d\u8bf4\u4e86\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u4f3c\u662f\u5df2\u65e0\u6cd5\u63a7\u5236\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u5723\u6c34\u5df2\u7ecf\u5356\u51fa\uff0c\u90a3\u5df1\u548c\u9601\u4e0b\u65e0\u5173\uff0c\u7528\u4e0d\u7740\u4f60\u518d\u591a\u53e3\u820c\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u4e8c\u5341\u4e5d\u53f7\uff0c\u4f60\u662f\u8bda\u5fc3\u6765\u6363\u86cb\u4e86\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9053\uff1a\u201c\u7b11\u8bdd\uff0c\u6211\u5e26\u4e86\u6570\u5341\u4e07\u5f20\u771f\u5b9e\u7684\u94f6\u7968\uff0c\u5c82\u662f\u6765\u6363\u86cb\u7684\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u50cf\u9601\u4e0b\u8fd9\u79cd\u4e0d\u901a\u60c5\u7406\u7684\u4eba\uff0c\u6211\u5df2\u89c1\u8fc7\u5f88\u591a\uff0c\u4e0d\u7528\u5728\u6211\u9762\u524d\u800d\u4ec0\u4e48\u82b1\u62db\u4e86\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u7f13\u7f13\u8f6c\u8fc7\u4e86\u8eab\u5b50\uff0c\u76f4\u5bf9\u90a3\u957f\u684c\u884c\u53bb\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5982\u6b64\u76f8\u903c\uff0c\u5728\u4e0b\u4e5f\u53ea\u597d\u653e\u624b\u4e00\u62fc\u4e86\u3002\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u597d\uff01\u4f60\u8981\u6253\u67b6\uff0c\u6211\u5c31\u627e\u4e2a\u4eba\u966a\u4f60\u6253\u4e00\u6b21\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5df2\u51c6\u5907\u51fa\u624b\uff0c\u53ea\u8981\u90a3\u9ed1\u8863\u4eba\u518d\u5411\u5c60\u65e0\u65b9\u903c\u8fd1\u4e00\u6b65\u3002 \u3000\u3000\u4f46\u90a3\u4e3b\u6301\u5723\u6c34\u7684\u9ed1\u8863\u4eba\uff0c\u5374\u628a\u8fd9\u4ef6\u4e8b\u63fd\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u65e2\u7136\uff0c\u4e8b\u60c5\u5c94\u4e86\u9053\uff0c\u53f6\u957f\u9752\u81ea\u5df1\u4e5f\u61d2\u5f97\u591a\u7ba1\u95f2\u4e8b\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53c2\u4e0e\u5723\u6c34\u4ea4\u6613\u7684\u4eba\uff0c\u5927\u90fd\u8fd8\u672a\u79bb\u53bb\uff0c\u773c\u770b\u6709\u4e86\u9ebb\u70e6\uff0c\u7acb\u523b\u90fd\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u6697\u4e2d\u6570\u4e86\u4e00\u4e0b\uff0c\u7559\u5728\u8fd9\u91cc\u7684\uff0c\u8fd8\u6709\u4e5d\u7ec4\uff0c\u6bcf\u7ec4\u4e8c\u4eba\uff0c\u4e8c\u4e5d\u4e00\u5341\u516b\uff0c\u52a0\u4e0a\u51fa\u552e\u5723\u6c34\u4e00\u65b9\u7684\u4e09\u4e2a\u4eba\uff0c\u5408\u8ba1\u6709\u4e8c\u5341\u4e00\u4eba\u3002 \u3000\u3000\u4eba\u6570\u867d\u7136\u4e0d\u592a\u591a\uff0c\u4f46\u80fd\u53c2\u4e0e\u5723\u6c34\u4ea4\u6613\u7684\u4eba\u7269\uff0c\u90fd\u4e0d\u662f\u6613\u4e0e\u4e4b\u8f88\u3002 \u3000\u3000\u8fd9\u4e8c\u5341\u51e0\u4e2a\u4eba\uff0c\u5982\u662f\u771f\u7684\u8981\u653e\u624b\u6253\u4e86\u8d77\u6765\uff0c\u5fc5\u662f\u4e00\u573a\u6c5f\u6e56\u4e0a\u6781\u4e3a\u5c11\u89c1\u7684\u640f\u6740\u3002 \u3000\u3000\u53f6\u957f\u9752\u867d\u7136\u5e0c\u671b\u5723\u6c34\u4ea4\u6613\u80fd\u591f\u987a\u5229\u7684\u6210\u529f\uff0c\u89c1\u8bc6\u4e00\u4e0b\uff0c\u90a3\u5723\u6c34\u7a76\u7adf\u662f\u4ec0\u4e48\u6837\u5b50\uff1f \u3000\u3000\u4f46\u4ed6\u4e5f\u5e0c\u671b\u8fd9\u51e0\u4e2a\u4eba\u6253\u4e86\u8d77\u6765\uff0c\u95f9\u4e2a\u5929\u7ffb\u5730\u8986\uff0c\u5927\u5bb6\u6495\u4e0b\u90a3\u8138\u4e0a\u9ed1\u5e03\uff0c\u77a7\u77a7\u770b\uff0c\u90fd\u662f\u4e9b\u4ec0\u4e48\u4eba\u7269\uff1f \u3000\u3000\u8fd9\u4e24\u79cd\u5fc3\u7406\uff0c\u76f8\u4e92\u77db\u76fe\uff0c\u4e5f\u76f8\u4e92\u51b2\u7a81\u3002 \u3000\u3000\u5c45\u4e2d\u7684\u9ed1\u8863\u4eba\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u51c6\u5907\u79c1\u4eba\u548c\u6211\u6253\u4e00\u67b6\uff1f\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9053\uff1a\u201c\u597d\uff01\u8fd9\u6b21\u5723\u6c34\u4ea4\u6613\u4e0d\u6210\uff0c\u5728\u4e0b\u8fd9\u6761\u547d\uff0c\u516b\u6210\u662f\u4fdd\u4e0d\u4f4f\u4e86\uff0c\u80fd\u548c\u9601\u4e0b\u653e\u624b\u4e00\u6218\uff0c\u6b7b\u4ea6\u65e0\u61be\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u8bf4\u6253\u5c31\u6253\uff0c\u7acb\u523b\u52a8\u4e0a\u4e86\u624b\u3002 \u3000\u3000\u800c\u4e14\uff0c\u4e00\u52a8\u624b\uff0c\u5c31\u662f\u62fc\u547d\u7684\u6253\u6cd5\u3002 \u3000\u3000\u4f46\u89c1\u62f3\u5f71\u70b9\u70b9\uff0c\u53cc\u65b9\u6253\u6597\u5f97\u5341\u5206\u6fc0\u70c8\u3002 \u3000\u3000\u53f6\u957f\u9752\u51b7\u773c\u65c1\u89c2\uff0c\u53d1\u89c9\u53cc\u65b9\u4e3e\u624b\u6295\u8db3\u4e4b\u95f4\uff0c\u51cc\u5389\u4e2d\uff0c\u542b\u6709\u7740\u6781\u4e3a\u7cbe\u5965\u7684\u53d8\u5316\uff0c\u4e0d\u7981\u4e00\u5446\uff0c\u6697\u9053\uff1a\u770b\u8d77\u6765\u8fd9\u4e24\u4eba\uff0c\u4e5f\u7b97\u5f97\u6b66\u6797\u4e2d\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\u4e86\u3002 \u3000\u3000\u53cc\u65b9\u6076\u6597\u4e86\u767e\u62db\u5de6\u53f3\uff0c\u4ecd\u7136\u4fdd\u6301\u4e2a\u4e0d\u80dc\u4e0d\u8d25\u4e4b\u5c40\u3002 \u3000\u3000\u53f6\u957f\u9752\u56de\u987e\u4e86\u5c60\u65e0\u65b9\u4e00\u773c\uff0c\u5c60\u65e0\u65b9\u793a\u610f\u4ed6\u4e0d\u53ef\u63d2\u624b\u3002 \u3000\u3000\u8fd9\u5b9e\u5728\u662f\u4e00\u573a\u5f88\u6fc0\u70c8\u7684\u640f\u6597\u3002 \u3000\u3000\u4f46\u53cc\u65b9\u90fd\u8fd8\u6709\u76f8\u5f53\u7684\u6291\u5236\uff0c\u56e0\u4e3a\uff0c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u53cc\u65b9\u90fd\u8fd8\u6ca1\u6709\u4eae\u51fa\u5175\u5203\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u54cd\u8d77\u4e86\u4e24\u58f0\u5927\u9707\u3002 \u3000\u3000\u539f\u6765\u4e24\u4e2a\u4eba\u4e45\u6218\u4e0d\u4e0b\uff0c\u90fd\u6253\u5f97\u6027\u8d77\uff0c\u7a81\u7136\u95f4\u786c\u63a5\u4e86\u4e24\u62db\u3002 \u3000\u3000\u8fd9\u4e24\u62db\u786c\u63a5\uff0c\u53cc\u65b9\u5404\u81ea\u88ab\u9707\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u4ecd\u7136\u662f\u4e00\u4e2a\u534a\u65a4\u516b\u4e24\u4e4b\u5c40\u3002 \u3000\u3000\u4e24\u4eba\u8138\u4e0a\uff0c\u90fd\u6709\u7740\u9ed1\u5e03\u906e\u4f4f\uff0c\u65e0\u6cd5\u770b\u5230\u4ed6\u4eec\u7684\u8868\u60c5\u3002 \u3000\u3000\u4f46\u53ea\u89c1\u4e24\u4eba\u773c\u4e2d\uff0c\u4ecd\u7136\u795e\u5149\u70af\u70af\uff0c\u4f3c\u4e4e\u662f\u90fd\u6ca1\u6709\u53d7\u5230\u4f24\u5bb3\u3002 \u3000\u3000\u81f3\u5c11\uff0c\u4e24\u4e2a\u4eba\u8fd8\u6709\u518d\u6218\u4e4b\u80fd\u529b\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4e24\u4e2a\u4eba\u672a\u518d\u52a8\u624b\u3002 \u3000\u3000\u53ea\u662f\u56db\u65e5\u4ea4\u6295\uff0c\u76f8\u4e92\u51dd\u671b\u7740\uff0c\u4e24\u4e2a\u4eba\u5185\u5fc3\u4e2d\uff0c\u90fd\u6709\u7740\u9707\u52a8\u7684\u611f\u89c9\u3002 \u3000\u3000\u8fd9\u767e\u62db\u6076\u6597\uff0c\u548c\u4e24\u638c\u786c\u62fc\u4e4b\u540e\uff0c\u53cc\u65b9\u5fc3\u4e2d\u90fd\u6709\u4e86\u6570\u3002 \u3000\u3000\u518d\u6253\u4e0b\u53bb\uff0c\u4e24\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u662f\u4e09\u4e24\u767e\u62db\u5185\uff0c\u53ef\u4ee5\u5206\u51fa\u80dc\u8d1f\u3002 \u3000\u3000\u9664\u975e\u6709\u4e00\u79cd\u7279\u6b8a\u7684\u6b66\u529f\uff0c\u65bd\u5c55\u51fa\u6765\uff0c\u4e00\u4e3e\u514b\u654c\u3002 \u3000\u3000\u8fbe\u4e24\u4e2a\u4eba\u6ca1\u6709\u4ec0\u4e48\u4ec7\u6068\uff0c\u771f\u8981\u4ee5\u547d\u76f8\u62fc\u65f6\uff0c\u5fc3\u4e2d\u53cd\u800c\u90fd\u6709\u4e9b\u72b9\u8c6b\u8d77\u6765\u3002 \u3000\u3000\u5c24\u5176\u90a3\u4e3b\u6301\u5723\u6c34\u4e70\u5356\u7684\u9ed1\u8863\u4eba\uff0c\u66f4\u65e0\u62fc\u547d\u4e4b\u5fc3\u3002 \u3000\u3000\u81ea\u7136\uff0c\u4ed6\u4eec\u8138\u4e0a\u6234\u7684\u9ed1\u8272\u5e3d\u5b50\u63a9\u53bb\u4e86\u672c\u6765\u9762\u76ee\uff0c\u4e5f\u4f7f\u4ed6\u4eec\u6709\u7740\u9690\u853d\u7684\u611f\u89c9\uff0c\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u4eec\u662f\u8c01\uff0c\u4e5f\u4e0d\u7528\u4e3a\u4e86\u4fdd\u62a4\u81ea\u5df1\u7684\u540d\u6c14\uff0c\u820d\u547d\u4e00\u62fc\u4e86\u3002 \u3000\u3000\u5fc3\u4e2d\u5148\u6709\u4e86\u64a4\u9000\u7684\u6253\u7b97\uff0c\u76ee\u5149\u4e2d\u7684\u795e\u5149\uff0c\u4e5f\u7acb\u523b\u655b\u5931\u3002 \u3000\u3000\u4f46\u90a3\u4e8c\u5341\u4e5d\u53f7\u7684\u9ed1\u8863\u4eba\uff0c\u4ecd\u6709\u65fa\u76db\u7684\u6597\u5fd7\uff0c\u76ee\u4e2d\u795e\u5149\u8fde\u8fde\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u5f88\u9ad8\u660e\uff0c\u4e0d\u8fc7\uff0c\u54b1\u4eec\u975e\u5f97\u5206\u4e2a\u80dc\u8d1f\u4e0d\u53ef\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u90a3\u5c45\u4e2d\u7684\u9ed1\u8863\u4eba\uff0e\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u4eca\u53e3\u662f\u5723\u6c34\u4ea4\u6613\u4e4b\u671f\uff0c\u5728\u4e0b\u4e0d\u613f\u610f\u95f9\u51fa\u6d41\u8840\u4e4b\u4e8b\uff0c\u5e0c\u671b\u4f60\u4e0d\u8981\u5f97\u5bf8\u8fdb\u5c3a\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u5728\u4e0b\u5df2\u4e0d\u51c6\u5907\u751f\u79bb\u6b64\u5730\u4e86\u3002\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u987e\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u7b97\u4e86\uff0c\u751f\u610f\u4e0d\u6210\u4ec1\u4e49\u5728\uff0c\u4f60\u4eec\u4eca\u5e74\u4e70\u4e0d\u5230\u5723\u6c34\uff0c\u660e\u5e74\u53ef\u80fd\u4e70\u6210\uff0c\u4eca\u5929\uff0c\u5c31\u7b97\u4f60\u62fc\u4e86\u547d\uff0c\u8840\u6e85\u4e8e\u6b64\uff0c\u5bf9\u5723\u6c34\u4ea4\u6613\uff0c\u4e5f\u662f\u4e8e\u4e8b\u65e0\u8865\u4e86\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9053\uff1a\u201c\u4e0d\u7ba1\u662f\u5426\u4e8e\u4e8b\u6709\u8865\uff0c\u5728\u4e0b\u7684\u906d\u9047\u90fd\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5723\u6c34\u4ea4\u6613\u548c\u6574\u4e2a\u6b66\u6797\u6709\u5173\uff0c\u4f60\u8fd9\u4e48\u4e00\u95f9\uff0c\u5c82\u4e0d\u628a\u6574\u4e2a\u5723\u6c34\u4ea4\u6613\uff0c\u90fd\u95f9\u57ae\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u628a\u8fd9\u4ef6\u4e8b\u95f9\u4e2a\u5929\u7ffb\u5730\u8986\uff0c\u5728\u4e0b\u4e5f\u662f\u5728\u6240\u4e0d\u60dc\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u6e05\u51b7\u7684\u5973\u5b50\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u592a\u8fc7\u65e0\u793c\u4e86\u3002\u201d \u3000\u3000\u968f\u7740\u90a3\u547c\u53eb\u4e4b\u58f0\uff0c\u4e00\u4e2a\u7a7f\u7740\u767d\u8863\u7684\u5973\u5b50\uff0c\u7f13\u6b65\u884c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u8bb0\u5f97\u90a3\u5973\u5b50\u7684\u58f0\u97f3\uff0c\u6b63\u662f\u90a3\u66fe\u548c\u4ed6\u4eec\u4e24\u4eba\u89c1\u9762\u7684\u767d\u8863\u5c11\u5973\u3002 \u3000\u3000\u5979\u4ecd\u7a7f\u7740\u90a3\u4e00\u8eab\u767d\u8863\uff0c\u53ea\u662f\u8138\u4e0a\u591a\u4e86\u4e00\u4e2a\u9ed1\u8272\u7684\u9762\u7f69\u3002\u4f46\u5979\u7684\u58f0\u97f3\uff0c\u4ecd\u7136\u53ef\u4ee5\u542c\u5f97\u51fa\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u9053\uff1a\u770b\u6765\uff0c\u8fd9\u5973\u4eba\u7684\u8eab\u4efd\uff0c\u5728\u8fd9\u4e2a\u7ec4\u7ec7\u4e2d\uff0c\u8fd8\u4e0d\u592a\u4f4e\u3002 \u3000\u3000\u767d\u8863\u5973\u5b50\u9762\u5bf9\u4e8c\u5341\u4e5d\u53f7\u884c\u4e86\u8fc7\u53bb\uff0c\u4e00\u9762\u63a5\u9053\uff1a\u201c\u5723\u6c34\u4ea4\u6613\u4e0d\u5141\u8bb8\u7834\u574f\uff0c\u4e0d\u8bba\u4f60\u6709\u591a\u5c11\u7406\u7531\uff0c\u591a\u5c11\u59d4\u5c48\uff0c\u6211\u4eec\u4e0d\u63a5\u53d7\u4efb\u4f55\u5a01\u80c1\uff0c\u4e5f\u4e0d\u613f\u6740\u4eba\uff0c\u4f46\u5982\u5728\u4e24\u8005\u4e4b\u95f4\u4e00\u5b9a\u8981\u6211\u4eec\u9009\u62e9\u4e00\u6837\u65f6\uff0c\u6211\u5b81\u53ef\u9009\u62e9\u6740\u4eba\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5982\u679c\u5723\u6c34\u4ea4\u6613\u771f\u6709\u4e25\u683c\u89c4\u5b9a\uff0c\u59d1\u5a18\u662f\u7b2c\u4e00\u4e2a\u8fdd\u72af\u89c4\u5b9a\u7684\u4eba\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u54e6\uff01\u6211\u72af\u4e86\u4ec0\u4e48\u89c4\u5b9a\uff1f\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9053\uff1a\u201c\u5c31\u5728\u4e0b\u6240\u77e5\uff0c\u9664\u4e86\u53c2\u4e0e\u5723\u6c34\u4ea4\u6613\u7684\u4eba\u4e4b\u5916\uff0c\u4efb\u4f55\u4eba\u4e0d\u5f97\u8fdb\u5165\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u90a3\u662f\u5bf9\u4f60\u4eec\u7684\u9650\u5236\uff0c\u5bf9\u6211\u4eec\u5e76\u6ca1\u6709\u8fd9\u4e2a\u89c4\u5b9a\uff0c\u4f55\u51b5\u2026\u2026\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4f55\u51b5\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u6211\u7684\u8eab\u4efd\u662f\u76d1\u7763\u5723\u6c34\u4ea4\u6613\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5982\u82e5\u4f60\u542c\u5230\u4e86\u6211\u8bf4\u7684\u8bdd\uff0c\u4f60\u8be5\u660e\u767d\uff0c\u6211\u5df2\u7ecf\u662f\u6b7b\u5b9a\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u5a01\u80c1\uff1f\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u662f\u5a01\u80c1\uff0c\u662f\u4e8b\u5b9e\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u8981\u6211\u6740\u4e86\u4f60\uff0c\u8fd8\u662f\u5927\u5bb6\u653e\u624b\u4e00\u640f\uff1f\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5927\u5bb6\u653e\u624b\u4e00\u640f\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u60f3\u6b7b\uff0c\u53ea\u662f\u60f3\u64a4\u8d56\u2026\u2026\u201d \u3000\u3000\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u5728\u573a\u8bf8\u4f4d\uff0c\u90fd\u662f\u6211\u4eec\u7684\u987e\u5ba2\uff0c\u5c0f\u59b9\u5b9e\u5728\u4e0d\u613f\u5bf9\u8bf8\u4f4d\u65e0\u793c\uff0c\u4e0d\u8fc7\u6211\u4eec\u4e3a\u4e86\u8981\u7ef4\u62a4\u5723\u6c34\u4ea4\u6613\u7684\u5c0a\u4e25\uff0c\u4e0d\u5f97\u4e0d\u653e\u624b\u4e00\u640f\u4e86\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9ed1\u8863\u4eba\u53cc\u76ee\u795e\u5149\u4e00\u95ea\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u653e\u5fc3\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u5e2e\u52a9\u6211\uff0c\u6211\u4e5f\u4e0d\u9700\u8981\u4ed6\u4eec\u5e2e\u52a9\uff0c\u751a\u81f3\u548c\u6211\u540c\u6765\u7684\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u51fa\u624b\uff0c\u59d1\u5a18\u53ea\u8981\u6740\u4e86\u6211\uff0c\u4ed6\u4f1a\u5e26\u7740\u6211\u7684\u5c38\u4f53\u548c\u94f6\u7968\u79bb\u53bb\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u5982\u6b64\u5c31\u597d\uff0c\u4f60\u8bf7\u51fa\u624b\u5427\uff0c\u6211\u8ba9\u4f60\u5148\u673a\u3002\u201d \u3000\u3000\u4e8c\u5341\u53f7\u9ed1\u8863\u4eba\u4f3c\u4e4e\u662f\u65e9\u5c31\u7b49\u4e0d\u53ca\u4e86\uff0c\u4e00\u542c\u767d\u8863\u5973\u5b50\u7684\u8bdd\uff0c\u7acb\u523b\u51fa\u624b\uff0c\u4e00\u62f3\u6363\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u767d\u8863\u5973\u5b50\u4e00\u4fa7\u8eab\uff0c\u907f\u8fc7\u4e00\u51fb\u3002 \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u7684\u9ed1\u8863\u4eba\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u8fde\u73af\u62cd\u51fa\uff0c\u6025\u6025\u62a2\u653b\u800c\u6765\u3002 \u3000\u3000\u767d\u8863\u5973\u5b50\u7acb\u523b\u53cd\u51fb\uff0c\u6307\u70b9\u8098\u649e\uff0c\u786c\u628a\u5bf9\u65b9\u7684\u653b\u52bf\u7ed9\u5c01\u4f4f\u3002 \u3000\u3000\u4e24\u4eba\u5c55\u5f00\u4e86\u4e00\u573a\u6fc0\u70c8\u7edd\u4f26\u7684\u6076\u6597\u3002 \u3000\u3000\u90a3\u767d\u8863\u5973\u5b50\u8eab\u6750\u5a07\u5c0f\uff0c\u4f46\u51fa\u638c\uff0c\u98de\u8db3\uff0c\u8fc5\u5982\u96f7\u706b\u3002 \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9ed1\u8863\u4eba\uff0c\u63a5\u8fc7\u4e86\u4e8c\u5341\u62db\u4e4b\u540e\uff0c\u5c31\u6709\u4e9b\u5e94\u4ed8\u4e0d\u6687\u4e86\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u767d\u8863\u5973\u5b50\u638c\u6307\u6d41\u52a8\uff0c\u5e7b\u8d77\u4e86\u6307\u5f62\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u770b\u6e05\u695a\uff0c\u5979\u5982\u4f55\u7528\u51fa\u8fd9\u5947\u5389\u5982\u5e7b\u7684\u4e00\u62db\u3002 \u3000\u3000\u4f46\u5374\u542c\u5f97\u90a3\u4e8c\u5341\u4e5d\u53f7\u9ed1\u8863\u4eba\u5927\u53eb\u4e00\u58f0\uff0c\u75be\u5feb\u5730\u5411\u540e\u9000\u53bb\u3002 \u3000\u3000\u4ed6\u53cc\u624b\u629a\u9762\uff0c\u9c9c\u8840\u4ece\u6307\u7f1d\u4e2d\u6d41\u4e86\u51fa\u6765\u3002 \u3000\u3000\u968f\u6765\u7684\u9ed1\u8863\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u6b65\u884c\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u8001\u5927\u4f60\u2026\u2026\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9ed1\u8863\u4eba\u63a5\u9053\uff1a\u201c\u6211\u88ab\u5979\u523a\u778e\u4e86\u4e24\u53ea\u773c\u775b\uff0c\u4e0d\u53ef\u4e3a\u6211\u62a5\u4ec7\uff0c\u5feb\u4e9b\u9003\u53bb\u5427\uff01\u201d \u3000\u3000\u968f\u6765\u9ed1\u8863\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8001\u5927\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6211\u5df2\u6210\u4e86\u778e\u5b50\uff0c\u5feb\u4e9b\u53bb\u5427\uff01\u522b\u4e3a\u6211\u62c5\u5fc3\uff0c\u6211\u4f1a\u81ea\u4f5c\u4e86\u65ad\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u7ad9\u5728\u4e00\u4fa7\u770b\u70ed\u95f9\u7684\u4eba\uff0c\u7acb\u523b\u7eb7\u7eb7\u8f6c\u8eab\u800c\u53bb\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u8d70\u6563\u4e86\u5927\u90e8\u5206\uff0c\u53ea\u4f59\u4e0b\u968f\u6765\u7684\u9ed1\u8863\u4eba\uff0c\u548c\u5c60\u65e0\u65b9\u53ca\u53f6\u957f\u9752\u3002 \u3000\u3000\u539f\u6765\uff0c\u5927\u5bb6\u90fd\u62c5\u5fc3\uff0c\u8fd9\u4e8c\u5341\u4e5d\u53f7\u7684\u9ed1\u8863\u4eba\uff0c\u5982\u82e5\u88ab\u6740\u4e86\uff0c\u53ef\u80fd\u4f1a\u5f15\u8d77\u5168\u573a\u51f6\u5389\u7684\u6597\u4e89\uff0c\u4f46\u5374\u6ca1\u6709\u60f3\u5230\uff0c\u767d\u8863\u5973\u5b50\u8fd9\u4e00\u51fb\uff0c\u7adf\u7136\u4f1a\u628a\u5728\u573a\u4e2d\u6240\u6709\u7684\u4eba\uff0c\u7ed9\u60ca\u9000\u800c\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u9053\uff1a\u9ed1\u9053\u4e2d\u4eba\uff0c\u548c\u767d\u9053\u4e2d\u4eba\uff0c\u6700\u5927\u7684\u4e0d\u540c\uff0c\u5927\u6982\u5c31\u5728\u8fd9\u4e2a\u5730\u65b9\u4e86\u3002 \u3000\u3000\u767d\u9053\u4e2d\u4eba\uff0c\u5982\u662f\u9047\u4e0a\u4e86\u4ec0\u4e48\u9ebb\u70e6\uff0c\u5927\u5bb6\u9053\u4e49\u76f8\u5171\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u8fd9\u4e48\u5f03\u4e4b\u4e0d\u987e\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u968f\u6765\u7684\u9ed1\u8863\u4eba\u63d0\u4e86\u5c0f\u6728\u7bb1\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u4f46\u95fb\u90a3\u767d\u8863\u5973\u5b50\u559d\u9053\uff1a\u201c\u7ad9\u4f4f\u3002\u201d \u3000\u3000\u7acb\u65f6\u6709\u4eba\u5173\u4e0a\u4e86\u5927\u95e8\uff0c\u62e6\u5728\u95e8\u524d\u3002 \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u653e\u4ed6\u8d70\uff01\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u665a\u4e86\uff0c\u521a\u624d\u4f60\u4eec\u4e0d\u8d70\uff0c\u73b0\u5728\u60f3\u8d70\u4e5f\u4e0d\u6210\u4e86\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9ed1\u8863\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5982\u6b64\u4e0d\u8bb2\u9053\u4e49\uff0c\u6b64\u540e\uff0c\u8c01\u8fd8\u6562\u53c2\u52a0\u5723\u6c34\u4ea4\u6613\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u9664\u975e\u4f60\u81ea\u6740\uff0c\u6211\u4eec\u4e0d\u4f1a\u6740\u4f60\uff0c\u6211\u8981\u627e\u4f60\u4eec\u8fd9\u4e2a\u7ec4\u7ec7\u7684\u5934\u76ee\u6765\u95ee\u4e2a\u6e05\u695a\uff0c\u8981\u4ed6\u6765\u5e26\u4f60\u4eec\u79bb\u5f00\u3002\u201d \u3000\u3000\u7a81\u7136\u51fa\u624b\u70b9\u4e86\u4e8c\u5341\u4e5d\u53f7\u9ed1\u8863\u4eba\u7684\u7a74\u9053\uff0c\u9053\uff1a\u201c\u66ff\u4ed6\u7597\u4f24\u6b62\u8840\u3002\u201d \u3000\u3000\u76ee\u5149\u8f6c\u5230\u90a3\u968f\u6765\u9ed1\u8863\u4eba\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u7559\u4e0b\u5462\uff1f\u8fd8\u662f\u8981\u6211\u52a8\u624b\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u5982\u8981\u4e00\u4e2a\u4eba\u901a\u98ce\u62a5\u4fe1\uff0c\u6211\u56de\u53bb\u5c82\u4e0d\u662f\u6b63\u597d\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u7684\u5f88\u597d\uff0c\u6211\u4e0d\u4f1a\u8981\u4f60\u56de\u53bb\uff0c\u4f60\u56de\u53bb\uff0c\u53ef\u4ee5\u968f\u4fbf\u8bf4\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6211\uff01\u6211\u2026\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u7a81\u7136\u6b3a\u8eab\u800c\u4e0a\uff0c\u70b9\u51fa\u4e00\u6307\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u81ea\u77e5\u4e0d\u662f\u654c\u624b\uff0c\u53cd\u6297\u5f92\u62db\u7f9e\u8fb1\uff0c\u6240\u4ee5\u6839\u672c\u5c31\u6ca1\u6709\u53cd\u6297\uff0c\u4e5f\u672a\u95ea\u907f\uff0c\u4efb\u51ed\u767d\u8863\u5973\u5b50\u4e00\u6307\uff0c\u70b9\u4e2d\u4e86\u7a74\u9053\u3002 \u3000\u3000\u767d\u8863\u5973\u5b50\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8bc6\u65f6\u52a1\u624d\u4e3a\u4fca\u6770\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u3001\u53f6\u957f\u9752\u4e00\u76f4\u51b7\u773c\u65c1\u89c2\uff0c\u6ca1\u6709\u63a5\u53e3\u3002 \u3000\u3000\u767d\u8863\u5973\u5b50\u76ee\u5149\u4e00\u63a0\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4f60\u4eec\u662f\u5341\u4e03\u53f7\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u662f\uff01\u4e5f\u662f\u8fd9\u4e00\u6b21\u6807\u4e2d\u5723\u6c34\u7684\u4eba\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u5723\u6c34\u4ea4\u6613\uff0e\u7ee7\u7eed\u8fdb\u884c\uff0c\u4f60\u4eec\u8be5\u529e\u4ec0\u4e48\uff0c\u53ef\u4ee5\u529e\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u6253\u5f00\u6728\u7bb1\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u94f6\u7968\uff0c\u4e5f\u6709\u73e0\u5b9d\uff0c\u4e0d\u77e5\u8bf8\u4f4d\u8981\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5c45\u4e2d\u7684\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u94f6\u7968\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u53d6\u51fa\u4e00\u53e0\u94f6\u7968\uff0c\u70b9\u4e86\u4e00\u70b9\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u56db\u5341\u4e94\u4e07\uff0c\u9601\u4e0b\u8bf7\u70b9\u4e00\u70b9\u3002\u201d \u3000\u3000\u5c45\u4e2d\u9ed1\u8863\u4eba\u63a5\u8fc7\u94f6\u7968\uff0c\u6570\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u597d\uff01\u4e09\u65e5\u4e4b\u540e\uff0c\u6211\u4eec\u4f1a\u901a\u77e5\u9645\u63a5\u6536\u5723\u6c34\u7684\u5730\u70b9\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u5408\u4e0a\u6728\u7bb1\uff0c\u5411\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u5e76\u672a\u79bb\u5f00\u73b0\u573a\u3002 \u3000\u3000\u4ed6\u8981\u770b\u90a3\u767d\u8863\u5973\u5b50\uff0c\u5982\u4f55\u5904\u7f6e\u8fd9\u4ef6\u4e8b\u60c5\uff0c \u3000\u3000\u767d\u8863\u5973\u5b50\u4e5f\u6ca1\u6709\u62db\u547c\u4e24\u4eba\u79bb\u53bb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e8c\u5341\u4e5d\u53f7\uff0c\u8981\u6211\u53d6\u4e0a\u4f60\u8138\u4e0a\u7684\u9762\u7eb1\u5462\uff1f\u8fd8\u662f\u5c31\u8fd9\u6837\u56de\u7b54\u6211\u7684\u95ee\u8bdd\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u53cc\u76ee\u88ab\u523a\u778e\uff0c\u5df2\u6709\u4eba\u4e3a\u4ed6\u6b62\u8840\u4e86\uff0c\u4f46\u4ed6\u6234\u7684\u62a4\u5934\u5e3d\u5b50\uff0c\u5e76\u672a\u9664\u53bb\u3002 \u3000\u3000\u663e\u7136\uff0c\u8fd9\u91cc\u5f88\u5c0a\u91cd\u4e2a\u4eba\u7684\u9690\u79d8\u3002 \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9053\uff1a\u201c\u5509\uff01\u6211\u53cc\u76ee\u5df2\u778e\uff0c\u4e0d\u5728\u4e4e\u4f60\u662f\u5426\u8ba4\u51fa\u6211\u662f\u8c01\u4e86\uff0c\u4e0d\u8fc7\uff0c\u4f60\u5982\u80fd\u5c0a\u91cd\u6211\u4eec\u7ea6\u5b9a\uff0c\u6700\u597d\u8fd8\u662f\u8fd9\u6837\u95ee\u8bdd\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u6211\u4eec\u5982\u4f55\u548c\u4f60\u4ee3\u8868\u7684\u7ec4\u7ec7\u8054\u7cfb\uff0c\u8981\u4ed6\u4eec\u6d3e\u4eba\u6765\u6b64\u63a5\u4f60\u4eec\u79bb\u5f00\uff1f\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u9053\uff1a\u201c\u4f60\u6d3e\u4eba\u5230\u5723\u6c34\u9547\uff0c\u6843\u82b1\u9152\u9986\uff0c\u901a\u77e5\u4e00\u58f0\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u597d\uff01\u6211\u4eec\u4f1a\u6d3e\u4eba\u53bb\uff0c\u8981\u8d35\u7ec4\u7ec7\u6d3e\u5165\u63a5\u4e24\u4f4d\u3002\u201d \u3000\u3000\u4e8c\u5341\u4e5d\u53f7\u53f9\u606f\u4e00\u58f0\uff0c\u6765\u518d\u7b54\u8bdd\u3002 \u3000\u3000\u767d\u8863\u5973\u5b50\u56de\u987e\u5c60\u65e0\u65b9\u4e00\u773c\u9053\uff1a\u201c\u4e24\u4f4d\u8fd8\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u4eec\u5bf9\u5723\u6c34\u4ea4\u6613\u7684\u5c0a\u4e25\uff0c\u5df2\u7ecf\u6709\u4e86\u4fe1\u5fc3\uff0c\u73b0\u5728\uff0c\u53ef\u4ee5\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4e24\u4f4d\u53ef\u4ee5\u653e\u5fc3\uff0c\u5723\u6c34\u7684\u4ea4\u6613\uff0c\u5e76\u975e\u662f\u4eca\u5e74\u505a\u5b8c\u4e86\u5c31\u4e0d\u518d\u505a\uff0c\u6211\u4eec\u7684\u6280\u5de7\u8d8a\u6765\u8d8a\u719f\uff0c\u4ea7\u91cf\u4e5f\u8d8a\u6765\u8d8a\u5927\uff0e\u6240\u4ee5\u6211\u5e0c\u671b\u660e\u5e74\u7684\u4ea4\u6613\u66f4\u987a\u5229\u4e00\u4e9b\uff0c\u5bf9\u4e8e\u4efb\u4f55\u7834\u574f\u5723\u6c34\u4ea4\u6613\u7684\u4eba\uff0c\u6211\u4eec\u90fd\u4e0d\u80fd\u5fcd\u53d7\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u80fd\u6709\u59d1\u5a18\u8fd9\u51e0\u53e5\u8bdd\uff0c\u54b1\u4eec\u5c31\u66f4\u653e\u5fc3\u4e86\uff0c\u54b1\u4eec\u606d\u5019\u901a\u77e5\u4e86\u3002\u201d \u3000\u3000\u767d\u8863\u5973\u5b50\u9053\uff1a\u201c\u4e24\u4f4d\u597d\u8d70\uff01\u6055\u6211\u4e0d\u9001\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u591c\u8272\u5df2\u6df1\uff0c\u4f46\u5927\u5385\u5916\u9762\uff0c\u4ecd\u7136\u505c\u7740\u4e00\u8f86\u7bf7\u8f66\u3002 \u3000\u3000\u5e7f\u5927\u7684\u5ead\u4e2d\uff0c\u5df2\u4e0d\u89c1\u6709\u9ed1\u8863\u4eba\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u672a\u591a\u95ee\u8bdd\uff0c\u5306\u5306\u767b\u4e0a\u4e86\u7bf7\u8f66\u3002 \u3000\u3000\u4e24\u4eba\u8fd8\u672a\u5750\u597d\uff0c\u7bf7\u8f66\u5df2\u5411\u524d\u9a70\u53bb\u3002 \u3000\u3000\u884c\u7ea6\u5927\u534a\u4e2a\u65f6\u8fb0\u5de6\u53f3\uff0c\u7bf7\u8f66\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8033\u9645\u95f4\uff0c\u54cd\u8d77\u4e86\u8d76\u8f66\u4eba\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u53ef\u4ee5\u4e0b\u8f66\u4e86\uff0c\u90a3\u4e24\u9876\u63a9\u906e\u7684\u5e3d\u5b50\uff0c\u8bf7\u4e24\u4f4d\u8131\u4e0b\u6765\u653e\u5728\u8f66\u4e0a\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u3001\u53f6\u957f\u9752\uff0c\u4e00\u5207\u90fd\u7167\u7740\u5429\u5490\u884c\u4e8b\uff0c\u8131\u4e86\u5e3d\u5b50\uff0c\u4e0b\u8f66\u800c\u53bb\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u7565\u4e00\u8fa8\u8bc6\uff0c\u5df2\u8ba4\u51fa\u4e86\u7acb\u8eab\u4e4b\u5730\uff0c\u53c8\u56de\u5230\u4e86\u5723\u6cc9\u9547\u5916\u3002 \u3000\u3000\u53f6\u957f\u9752\u5618\u4e00\u53e3\u6c14\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c60\u5144\uff0c\u4e0d\u77e5\u4ed6\u4eec\u662f\u5426\u6709\u76ef\u68a2\u7684\u4eba\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5c31\u7b97\u6709\u5427\uff01\u54b1\u4eec\u4e5f\u4e0d\u7528\u62c5\u5fc3\uff0c\u54b1\u4eec\u7684\u4f4f\u5904\uff0c\u65e9\u65e5\u5728\u4ed6\u4eec\u7684\u76d1\u89c6\u4e4b\u4e0b\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff01\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u53f6\u5144\u53ef\u4ee5\u653e\u5fc3\uff0c\u4ed6\u4eec\u53ea\u662f\u77e5\u9053\u4e86\u54b1\u4eec\u7684\u4f4f\u5904\uff0c\u5374\u6ca1\u6709\u4fb5\u5165\u5b85\u5185\uff0c\u4e3b\u6301\u5723\u6c34\u4ea4\u6613\u7684\u8fd9\u4e00\u6279\u4eba\u7269\uff0c\u4e5f\u4e0d\u662f\u7b80\u5355\u4eba\u7269\uff0c\u867d\u7136\u662f\u6545\u4f5c\u795e\u79d8\uff0c\u4f46\u5374\u6781\u5c3d\u66f2\u6298\u3001\u73b2\u73d1\u4e4b\u5999\uff0c\u9886\u5bfc\u8fd9\u4e00\u4e2a\u7ec4\u7ec7\u7684\u4eba\uff0c\u5fc5\u662f\u4e00\u4e2a\u72e1\u8bc8\u3001\u591a\u53d8\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u56de\u5934\u77a7\u4e01\u4e00\u9635\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c60\u5144\uff0c\u54b1\u4eec\u6b64\u523b\u8981\u4f5c\u4f55\u6253\u7b97\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5e0c\u671b\u5821\u4e3b\u80fd\u591f\u53ca\u65f6\u8d76\u56de\u6765\u4eb2\u81ea\u9886\u5bfc\u5904\u7f6e\u6b64\u4e8b\uff0c\u5982\u679c\u4e0d\u80fd\uff0c\u54b1\u4eec\u4e5f\u53ea\u6709\u89c1\u673a\u884c\u4e8b\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\uff0c\u54b1\u4eec\u7684\u7528\u5fc3\uff0c\u53ea\u662f\u53d6\u5230\u5723\u6c34\u5462\uff1f\u8fd8\u662f\u53d6\u56de\u94f6\u7968\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5821\u4e3b\u7684\u610f\u601d\u662f\u5148\u8981\u53d6\u5230\u5723\u6c34\uff0c\u7136\u800c\u5728\u4e0b\u89c9\u5f97\u82b1\u4e86\u51e0\u5341\u4e07\u94f6\u5b50\uff0c\u4e70\u4e86\u4e24\u6876\u5723\u6c34\u6765\uff0c\u5b9e\u5728\u662f\u4e00\u4ef6\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u672c\u6765\u4e00\u76f4\u5bf9\u5723\u6c34\u4ea4\u6613\uff0c\u5b58\u7740\u6000\u7591\uff0c\u51e0\u5341\u4e07\u4e24\u94f6\uff0c\u7528\u8f66\u62c9\uff0c\u4e5f\u8981\u5341\u51e0\u8f86\u8f66\uff0c\u4f46\u89c1\u4e86\u4eca\u591c\u7684\u4ea4\u6613\u573a\u9762\uff0c\u5012\u662f\u4e0d\u5bb9\u4f60\u4e0d\u4fe1\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u6211\u4e5f\u662f\u5728\u4eca\u591c\u7684\u89c1\u8bc6\u4e4b\u4e0b\uff0c\u624d\u53d1\u89c9\u5723\u6c34\u771f\u7684\u5982\u6b64\u62a2\u624b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4f4e\u58f0\u9053\uff1a\u201c\u5c60\u5144\uff0c\u90a3\u5723\u6c34\uff0c\u7a76\u7adf\u6709\u4ec0\u4e48\u7528\u5904\uff0c\u7adf\u7136\u8d35\u5f97\u5982\u6b64\u9a87\u4eba\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u6211\u5b9e\u5728\u4e5f\u4e0d\u592a\u6e05\u695a\u5723\u6c34\u6709\u4e9b\u4ec0\u4e48\u7528\u5904\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54b1\u4eec\u82b1\u4e86\u8fd9\u4e48\u591a\u94b1\uff0c\u4e70\u4e0b\u4e24\u6876\u5723\u6c34\uff0c\u65e2\u7136\u4e0d\u77e5\u9053\u5b83\u7684\u7528\u5904\u4e5f\u4e0d\u77e5\u9053\u5b83\u7684\u771f\u5047\uff0c\u5929\u4e0b\u4e4b\u51a4\u4e8b\uff0e\u83ab\u8fc7\u5982\u6b64\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u8fd9\u5723\u6c34\u7684\u4ef7\u503c\uff0c\u76ee\u4e0b\u5df2\u7ecf\u786e\u5b9a\u4e86\uff0c\u65e2\u7136\u6709\u8fd9\u4e48\u591a\u4eba\u80af\u82b1\u53bb\u5927\u628a\u94f6\u5b50\u6765\u4e70\u8fd9\u5723\u6c34\uff0c\u60f3\u6765\uff0c\u8fd9\u5723\u6c34\u81ea\u6709\u73cd\u8d35\u4e4b\u5904\u4e86\uff0c\u53ea\u53ef\u60dc\uff0c\u54b1\u4eec\u4e0d\u77e5\u9053\u5b83\u7684\u7528\u5904\u4f55\u5728\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5618\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5c60\u5144\uff0c\u5c0f\u5f1f\u867d\u7136\u4e0d\u77e5\u9053\u8fd9\u5723\u6c34\u7684\u4f5c\u7528\uff0c\u4f46\u6211\u76f8\u4fe1\u8fd9\u5723\u6c34\u4e00\u5b9a\u548c\u6b66\u6797\u7684\u5c40\u52bf\u6709\u5173\uff0c\u6240\u4ee5\uff0c\u624d\u4f1a\u6380\u8d77\u5982\u6b64\u6fc0\u70c8\u7684\u7ade\u4e89\uff0c\u4e0d\u60dc\u82b1\u8d39\u5927\u91cf\u91d1\u94b1\u3002\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u5df2\u7136\u884c\u5165\u4e86\u5e84\u9662\u4e4b\u4e2d\u3002 \u3000\u3000\u6021\u7ea2\u8fd8\u6ca1\u6709\u7761\uff0c\u8fd9\u4f4d\u7f8e\u4e3d\u53ef\u4eba\u7684\u59d1\u5a18\uff0c\u66ff\u4e24\u4eba\u51c6\u5907\u597d\u4e86\u5bb5\u591c\uff0c\u4e5f\u51c6\u5907\u4e86\u9152\u3002 \u3000\u3000\u96c5\u5ba4\u7684\u706f\u5149\u5f88\u67d4\u548c\uff0c\u5c60\u65e0\u65b9\u548c\u53f6\u957f\u9752\u5bf9\u5750\u5c0f\u914c\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u56de\u987e\u4e86\u6021\u7ea2\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u91cc\u7684\u5b88\u62a4\u5982\u4f55\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u52a0\u6d3e\u4e86\u5f88\u591a\u4eba\u624b\uff0c\u76f8\u4fe1\u6bd4\u6628\u5929\u4e25\u5bc6\u591a\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u597d\uff01\u8f9b\u82e6\u4f60\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u5f88\u8bc6\u76f8\uff0c\u53f6\u957f\u9752\u65e2\u6ca1\u6709\u7559\u5979\u5c0f\u5750\uff0c\u7acb\u523b\u6084\u7136\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u5e72\u4e86\u4e00\u676f\u9152\uff0c\u7b11\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u5bf9\u5723\u6c34\u7684\u4e8b\uff0c\u6211\u77e5\u9053\u7684\u4e5f\u4e0d\u592a\u591a\uff0c\u5c31\u7b97\u6bd4\u4f60\u591a\u4e00\u70b9\uff0c\u4e5f\u5c5e\u6709\u9650\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u5723\u6c34\u5df2\u5f15\u8d77\u4e86\u6211\u5f3a\u70c8\u7684\u597d\u5947\uff0c\u6211\u80fd\u591a\u77e5\u9053\u4e00\u70b9\uff0c\u5c31\u60f3\u591a\u77e5\u9053\u4e00\u4e9b\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u542c\u8bf4\uff0c\u9ed1\u9053\u4e0a\u4e89\u53d6\u5723\u6c34\uff0c\u786e\u548c\u6b66\u6797\u9738\u6743\u6709\u5173\uff0c\u4e0d\u8fc7\u7a76\u7adf\u6709\u591a\u5927\u5173\u7cfb\uff0c\u5c31\u65e0\u6cd5\u77e5\u6653\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\uff0c\u770b\u6837\u5b50\u5723\u6c34\u7684\u4ea7\u91cf\uff0c\u6bcf\u5e74\u5728\u589e\u52a0\uff0c\u5982\u679c\u5723\u6c34\u548c\u6b66\u6797\u7684\u9738\u6743\u6709\u5173\uff0c\u90a3\u95ee\u9898\u5c82\u4e0d\u662f\u8d8a\u6765\u8d8a\u4e25\u91cd\u4e86\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u5821\u4e3b\u4e0d\u60dc\u4ee3\u4ef7\uff0c\u8981\u5f97\u5230\u5723\u6c34\u7684\u4e3b\u8981\u539f\u56e0\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4eca\u5e74\u54b1\u4eec\u8d2d\u5f97\u5723\u6c34\uff0c\u4f46\u660e\u5e74\u5462\uff1f\u662f\u4e0d\u662f\u4ecd\u6709\u628a\u63e1\u5f97\u5230\uff0c\u5c31\u7b97\u80fd\u5f97\u5230\uff0c\u4e5f\u8981\u82b1\u6570\u5341\u4e07\u4e24\u94f6\u5b50\uff0c\u9634\u9633\u5821\u7684\u8d22\u529b\uff0c\u80fd\u591f\u8d1f\u62c5\u5417\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u50cf\u8fd9\u6837\u6bcf\u5e74\u4e70\u5356\u5723\u6c34\uff0c\u9634\u9633\u5821\u518d\u5982\u4f55\u5bcc\u6709\uff0c\u4e5f\u65e0\u6cd5\u8d1f\u62c5\u8fd9\u7b14\u8d39\u7528\uff0c\u5821\u4e3b\u7a76\u7adf\u8981\u4f5c\u4f55\u5904\u7f6e\uff0c\u5927\u6982\u8fd8\u6ca1\u6709\u51b3\u5b9a\uff0c\u6211\u60f3\uff0c\u5821\u4e3b\u7684\u7528\u5fc3\uff0c\u5927\u6982\u662f\u5148\u8981\u77e5\u9053\u8fd9\u5723\u6c34\u7a76\u7adf\u6709\u4e9b\u4ec0\u4e48\u7528\u5904\uff0c\u7136\u540e\uff0c\u518d\u4f5c\u51b3\u5b9a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6211\u60f3\u4eca\u5929\u8fd9\u4e2a\u5c40\u9762\uff0c\u5927\u6982\u662f\u5723\u6c34\u4ea4\u6613\u4ee5\u6765\u6700\u574f\u7684\u5c40\u9762\u4e86\uff0c\u6240\u4ee5\u6211\u5f88\u62c5\u5fc3\u4ed6\u4eec\u4f1a\u62a2\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u4f1a\u62a2\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5728\u573a\u53c2\u4e0e\u4ea4\u6613\u7684\u4eba\uff0c\u90fd\u53ef\u80fd\u4f1a\u4e0b\u624b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6709\u5907\u65e0\u60a3\uff0c\u54b1\u4eec\u8981\u628a\u4eba\u624b\u91cd\u65b0\u8c03\u914d\u4e00\u4e0b\uff0c\u4e07\u4e00\u6709\u4e86\u4ec0\u4e48\u53d8\u5316\uff0c\u4e5f\u597d\u5e94\u4ed8\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u65b9\u9762\u5144\u5f1f\u53ea\u6015\u5e2e\u4e0d\u4e0a\u5fd9\u3002\u201d \u3000\u3000\u5c60\u65e0\u529b\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u662f\u4e3b\u529b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u4f1a\u5168\u529b\u4ee5\u8d74\uff0c\u5c60\u5144\u4f46\u8bf7\u653e\u5fc3\uff0c\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5821\u4e3b\u4e34\u53bb\u4e4b\u9645\uff0c\u4ea4\u4ee3\u5728\u4e0b\uff0c\u6709\u4e8b\u60c5\u54b1\u4eec\u5546\u91cf\u7740\u529e\uff0c\u6211\u60f3\u5148\u628a\u8fd9\u91cc\u7684\u5b9e\u529b\u7ed9\u53f6\u8001\u5f1f\u8bf4\u660e\u4e00\u4e0b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\u5982\u662f\u770b\u91cd\u533a\u533a\uff0c\u6211\u5c31\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\uff0c\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5728\u8fd9\u5723\u6c34\u9547\u4e2d\uff0c\u54b1\u4eec\u5927\u7ea6\u6709\u516d\u5341\u4e2a\u4eba\uff0c\u5176\u4e2d\u7b97\u5f97\u9ad8\u624b\u7684\uff0c\u5927\u7ea6\u6709\u5341\u4e94\u4e2a\u4eba\uff0c\u5176\u4f59\u7684\uff0c\u867d\u7136\u62fc\u547d\u4ee5\u8d74\uff0c\u4f46\u4e5f\u522b\u6307\u671b\u4ed6\u4eec\u80fd\u6d3e\u5927\u7528\u573a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\uff0c\u51c6\u5907\u600e\u4e48\u5e03\u7f6e\u5462\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5929\u8272\u4e0d\u65e9\uff0c\u53f6\u8001\u5f1f\u4e5f\u8be5\u4f11\u606f\u4e00\u4e0b\uff0c\u7b49\u6211\u60f3\u51fa\u4e86\u4e00\u4e2a\u5e03\u7f6e\u7684\u65b9\u6cd5\uff0c\u518d\u548c\u53f6\u8001\u5f1f\u4ed4\u7ec6\u7684\u5546\u91cf\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u56de\u5230\u5367\u5ba4\uff0c\u6021\u7ea2\u65e9\u5df2\u5728\u5ba4\u4e2d\u606d\u5019\uff0c\u7b11\u9053\uff1a\u201c\u7237\uff0c\u8dd1\u4e86\u6574\u4e2a\u4e00\u591c\uff0c\u53ef\u67e5\u51fa\u4e00\u70b9\u7709\u76ee\u4e86\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e00\u5207\u4e8b\u52a1\uff0c\u90fd\u6709\u5c60\u5144\u4e3b\u6301\uff0c\u6211\u4e0d\u8fc7\u662f\u8ddf\u7740\u8dd1\u8dd1\u817f\u7f62\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u7237\uff0c\u5f88\u7d2f\u5417\uff1f\u8981\u4e0d\u8981\u6211\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e24\u5929\u968f\u65f6\u5c31\u53ef\u80fd\u51fa\u52a8\uff0c\u4f60\u4e0d\u7528\u7559\u8fd9\u91cc\u4e86\uff0c\u56de\u53bb\u4f11\u606f\u4e00\u4e0b\uff0c\u6ce8\u610f\u4eba\u5bb6\u6d3e\u6765\u7684\u6697\u63a2\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u8fd9\u91cc\u6709\u4e0d\u5c11\u7684\u9632\u536b\u4eba\u624b\uff0c\u4ed6\u4eec\u7684\u6b66\u529f\uff0c\u867d\u7136\u4e0d\u5982\u7237\uff0c\u4f46\u4ed6\u4eec\u4e5f\u4e0d\u662f\u5eb8\u624b\uff0c\u4e0d\u8bba\u6765\u4eba\u7684\u6b66\u529f\u5982\u4f55\u9ad8\u660e\uff0c\u4f46\u4ed6\u4eec\u81f3\u5c11\u53ef\u4ee5\u6321\u4ed6\u4eec\u4e00\u9635\uff0c\u548c\u4f20\u51fa\u8b66\u8baf\u3002\u201d \u3000\u3000\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u5f88\u5e73\u5b89\u7684\u8fc7\u4e86\u4e00\u591c\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e2d\u5348\u4e4b\u540e\uff0c\u5c60\u65e0\u65b9\u5e26\u6765\u4e86\u4ed6\u7684\u5e03\u7f6e\u8ba1\u5212\u3002 \u3000\u3000\u8ba1\u5212\u5f88\u8be6\u660e\uff0c\u4e5f\u8bf4\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u53f6\u957f\u9752\u60f3\u63d0\u4e00\u70b9\u4fee\u6b63\u7684\u610f\u89c1\uff0c\u4f46\u60f3\u6765\u60f3\u53bb\uff0c\u7adf\u7136\u60f3\u4e0d\u51fa\u7834\u7efd\u3002 \u3000\u3000\u592a\u9633\u4e0b\u5c71\u7684\u65f6\u5206\uff0c\u6bd2\u8702\u5f6d\u9707\uff0c\u9001\u6765\u4e86\u4e00\u4efd\u5bc6\u7b80\u3002 \u3000\u3000\u81ea\u7136\uff0c\u90a3\u5bc6\u7b80\u4e0a\uff0c\u4e5f\u8bf4\u660e\u4e86\u8981\u53bb\u7684\u5730\u65b9\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u770b\u5b8c\u5bc6\u7b80\uff0c\u6325\u6325\u624b\uff0c\u5bf9\u5f6d\u9707\u8bf4\u9053\uff1a\u201c\u53bb\u53ec\u96c6\u6240\u6709\u4eba\uff0c\u96c6\u4e2d\u90a3\u8305\u68da\u4e2d\u7b49\u4faf\uff0c\u521d\u66f4\u65f6\u5206\uff0c\u4e00\u5b9a\u8981\u4eba\u624b\u9f50\u5168\u3002\u201d \u3000\u3000\u5f6d\u9707\u671b\u671b\u5929\u8272\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u671b\u7740\u5f6d\u9707\u53bb\u8fdc\uff0c\u624d\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6765\u5f97\u597d\u5feb\uff0c\u770b\u6765\uff0c\u662f\u7b49\u4e0d\u53ca\u5821\u4e3b\u56de\u6765\u4e86\uff0c\u53f6\u8001\u5f1f\uff0c\u8fd9\u4ef6\u4e8b\u8981\u9760\u4f60\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\u7684\u7ecf\u9a8c\u3001\u6b66\u529f\uff0c\u90fd\u5728\u5144\u5f1f\u4e4b\u4e0a\uff0c\u5144\u5f1f\u4e00\u5207\u542c\u547d\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u53c8\u53ec\u6765\u6021\u7ea2\uff0c\u5429\u5490\u5979\u51c6\u5907\u7684\u4e8b\u60c5\uff0c\u7136\u540e\uff0c\u5e26\u7740\u53f6\u957f\u9752\u79bb\u53bb\u3002 \u3000\u3000\u5230\u4e86\u5723\u6c34\u9547\u5916\u7684\u8305\u68da\u6240\u5728\uff0c\u5f6d\u9707\u679c\u7136\u5df2\u53ec\u6765\u4e86\u4e0d\u5c11\u7684\u4eba\u624b\u3002 \u3000\u3000\u53f6\u957f\u9752\u6697\u4e2d\u4e00\u6570\uff0c\u5305\u62ec\u6d3b\u50f5\u5c38\u9648\u6c9b\u5728\u5185\uff0c\u6709\u5341\u4e09\u4e2a\u4eba\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4eba\u6570\u90fd\u9f50\u4e86\u5417\uff1f\u201d \u3000\u3000\u5f6d\u9707\u9053\uff1a\u201c\u5dee\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5dee\u4e24\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u5f6d\u9707\u9053\uff1a\u201c\u4ed6\u4eec\u6b7b\u4e01\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6b7b\u5728\u4eba\u6697\u7b97\u8fd8\u662f\u5175\u5203\u4e4b\u4e0b\uff1f\u201d \u3000\u3000\u5f6d\u9707\u5230\uff1a\u201c\u6b7b\u5728\u4e00\u79cd\u5185\u5bb6\u638c\u529b\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u6740\u7684\u5462\uff1f\u201d \u3000\u3000\u5f6d\u9707\u9053\uff1a\u201c\u73b0\u5728\uff0c\u8fd8\u6ca1\u6709\u627e\u51fa\u5934\u7eea\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u4f4e\u58f0\u5429\u5490\u4e01\u5f6d\u9707\u548c\u9648\u6c9b\u51e0\u53e5\uff0c\u4e24\u4e2a\u4eba\u5e94\u4e86\u4e00\u58f0\uff0c\u7387\u4eba\u800c\u53bb\uff0c\u53f6\u957f\u9752\u4f4e\u58f0\u9053\uff1a\u201c\u5c60\u5144\uff0c\u54b1\u4eec\u8fd8\u662f\u5728\u8fd9\u513f\u7b49\u4ed6\u4eec\u6765\u63a5\u5417\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u7b49\u4e00\u4f1a\uff0c\u4ed6\u4eec\u4f1a\u6d3e\u4eba\u6765\u8ddf\u54b1\u4eec\u8054\u7edc\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u672a\u518d\u591a\u8a00\u3002 \u3000\u3000\u5927\u7ea6\u4e8c\u66f4\u8fc7\u540e\uff0c\u4e00\u9635\u6b65\u5c65\u4e4b\u58f0\uff0c\u7531\u8fdc\u800c\u8fd1\uff0c\u76f4\u5411\u51e0\u4eba\u505c\u8eab\u4e4b\u5904\u884c\u6765\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u51dd\u76ee\u671b\u53bb\uff0c\u8fd8\u672a\u770b\u5230\u4eba\u5f71\uff0c\u4f46\u90a3\u6b65\u5c65\u4e4b\u58f0\uff0c\u5374\u662f\u54cd\u5f97\u5341\u5206\u6e05\u6670\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u8fd9\u662f\u4ec0\u4e48\u6b66\u529f\uff0c\u4eba\u8fd8\u672a\u770b\u5230\uff0c\u4f46\u5374\u542c\u5230\u4e86\u811a\u6b65\u4e4b\u58f0\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u4e5f\u662f\u6d41\u76ee\u56db\u987e\uff0c\u4f46\u5374\u4e00\u76f4\u89c1\u4e0d\u5230\u4eba\u5f71\uff1a \u3000\u3000\u4ed6\u884c\u8d70\u6c5f\u6e56\u6570\u5341\u5e74\uff0c\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u4f46\u4ed6\u7ec8\u4e8e\u5fcd\u8010\u4e0d\u4f4f\u4e86\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u90a3\u4e00\u4f4d\u9ad8\u4eba\uff0c\u65e2\u7136\u6765\u4e86\uff0c\u4e3a\u4f55\u8fd8\u4e0d\u89c2\u8eab\u51fa\u6765\u3002\u201d \u3000\u3000\u6b65\u5c65\u4e4b\u58f0\uff0c\u7a81\u7136\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u7d27\u63a5\u7740\u4e00\u4e2a\u51b7\u51b7\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u8fc7\u6765\u5427\uff01\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4f60\u5728\u54ea\u91cc\u3002\u201d \u3000\u3000\u90a3\u51b7\u51b7\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u5411\u6b63\u5357\u65b9\u884c\u4e94\u767e\u6b65\uff0c\u5c31\u53ef\u4ee5\u770b\u5230\u6211\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5446\u4e86\u4e00\u5446\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c60\u5144\uff0c\u4ed6\u81f3\u5c11\u5728\u4e94\u767e\u6b65\u4e4b\u5916\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u82e6\u7b11\u4e00\u4e0b\uff0c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u6211\u4eec\u73b0\u5728\u53bb\u5427\uff01\u201d \u3000\u3000\u4e24\u4eba\u5411\u6b63\u5357\u65b9\u884c\u4e86\u4e94\u767e\u6b65\uff0c\u679c\u7136\u89c1\u4e00\u4e2a\u9ed1\u8863\u4eba\uff0c\u633a\u76f4\u7684\u7ad9\u5728\u8352\u91ce\u4e4b\u4e2d\u3002 \u3000\u3000\u90a3\u662f\u4e2a\u5168\u8eab\u9ed1\u8863\u7684\u4e2d\u5e74\u4eba\uff0c\u7559\u7740\u5f88\u957f\u7684\u80e1\u5b50\uff0c\u4f46\u5374\u6709\u4e00\u5757\u9ed1\u7eb1\u906e\u4f4f\u4e86\u8138\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u884c\u8fd1\u4e86\u9ed1\u8863\u4eba\u4e00\u62f1\u624b\u9053\uff1a\u201c\u670b\u53cb\uff0c\u521a\u624d\u53ef\u662f\u4f60\u62db\u547c\u6211\u4eec\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u4eec\u53ef\u662f\u5f97\u4e86\u5723\u6c34\u7684\u4eba\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e24\u4f4d\u5c31\u662f\u63a5\u6536\u5723\u6c34\u7684\u4eba\u4e86\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u597d\uff01\u4f60\u4eec\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u4e3e\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u3001\u5c60\u65e0\u65b9\uff0c\u7d27\u968f\u5728\u90a3\u4eba\u8eab\u540e\uff0c\u5feb\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u53c8\u884c\u7ea6\u4e00\u987f\u996d\u5de5\u592b\u4e4b\u4e45\uff0c\u5230\u4e86\u4e00\u68f5\u5927\u6811\u4e4b\u4e0b\u3002 \u3000\u3000\u90a3\u91cc\u7ad9\u7740\u516b\u4e2a\u7070\u8863\u4eba\uff0c\u624b\u4e2d\u5168\u6267\u7740\u5175\u5203\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u6307\u6307\u90a3\u4e24\u4e2a\u653e\u5728\u6811\u4e0b\u7684\u5bc6\u5c01\u6728\u6876\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u5723\u6c34\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u54e6\u4e86\u4e00\u58f0\uff0c\u5feb\u6b65\u884c\u5411\u524d\u53bb\uff0c\u4f4e\u5934\u67e5\u770b\u3002 \u3000\u3000\u53ea\u89c1\u4e24\u4e2a\u6728\u6876\uff0c\u90fd\u52a0\u4e0a\u4e86\u9ec4\u8721\u5bc6\u5c01\uff0c\u6bcf\u4e2a\u6728\u6876\uff0c\u5c31\u548c\u4e00\u822c\u7684\u6c34\u6876\u76f8\u82e5\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u54e6\uff01\u8fd9\u4e24\u6876\u5723\u6c34\uff0c\u6211\u4eec\u53ef\u4e0d\u53ef\u4ee5\u6253\u5f00\u77a7\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u7b7e\u4e86\u6536\u6761\u4e4b\u540e\uff0c\u968f\u4fbf\u5904\u7f6e\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u670b\u53cb\uff0c\u8c08\u5224\u5723\u6c34\u4ea4\u6613\u65f6\uff0c\u51fa\u4e86\u70b9\u9ebb\u70e6\uff0c\u4e0d\u77e5\u670b\u53cb\u662f\u5426\u77e5\u6653\u3002\u201d \u3000\u3000\u4e00\u9762\u53cc\u76ee\u795e\u51dd\uff0c\u4e0d\u505c\u5728\u90a3\u9ed1\u8863\u4eba\u7684\u8eab\u4e0a\u6253\u91cf\uff0c\u5e0c\u671b\u80fd\u627e\u51fa\u4ed6\u7684\u7279\u5f81\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u7279\u522b\u4e4b\u5904\uff0c\u5c31\u662f\u80e1\u5b50\u3002 \u3000\u3000\u5f88\u7f8e\u5f88\u7f8e\u7684\u4e94\u7efa\u957f\u9aef\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5f00\u4e86\u53e3\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u8001\u592b\u53ea\u7ba1\u62bc\u8fd0\u5723\u6c34\uff0c\u8c08\u4ea4\u6613\u548c\u6211\u65e0\u5173\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u54e6\uff01\u5982\u679c\u8fd9\u5723\u6c34\u88ab\u4eba\u62a2\u8d70\u4e86\u5462\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5728\u4f60\u4eec\u6ca1\u6709\u7b7e\u6536\u4e4b\u524d\uff0c\u8fd9\u5723\u6c34\u5982\u6709\u70b9\u6ef4\u635f\u5931\uff0c\u90a3\u5c31\u5f52\u6211\u4eec\u8d54\u4ef7\uff0c\u5982\u662f\u4f60\u4eec\u7b7e\u6536\u4e86\uff0c\u8fd9\u5723\u6c34\u5c31\u7b97\u5168\u90e8\u5931\u53bb\u4e86\uff0c\u4e0e\u6211\u4eec\u4e5f\u6ca1\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4ea4\u6613\u5012\u662f\u5e72\u8106\u5f97\u5f88\uff0c\u5b8c\u5168\u6ca1\u6709\u4e00\u70b9\u4eba\u60c5\u5473\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u4f38\u624b\u53d6\u51fa\u4e00\u5f20\u767d\u7b3a\uff0c\u9053\uff1a\u201c\u670b\u53cb\uff0c\u5728\u4e0a\u9762\u5212\u62bc\u5427\uff01\u201d \u3000\u3000\u5c60\u65e0\u65b9\u77a7\u4e86\u2014\u773c\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u9053\uff1a \u3000\u3000\u201c\u6536\u5230\u4e24\u6876\u5bc6\u5c01\u5723\u6c34\u3002\u201d \u3000\u3000\u4e0b\u9762\u662f\u7ec4\u7ec7\u548c\u4eba\u540d\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u63a5\u9053\uff1a\u201c\u4f60\u5982\u80fd\u5199\u5b57\uff0c\u6700\u597d\u662f\u5199\u4e0a\u4e86\u4f60\u4eec\u7684\u7ec4\u7ec7\u540d\u79f0\uff0c\u5982\u662f\u4e0d\u80fd\u5199\u5b57\uff0c\u90a3\u5c31\u5728\u4e0a\u9762\u5212\u4e2a\u62bc\u7b97\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6211\u770b\uff0c\u5728\u4e0b\u8fd8\u662f\u5212\u4e2a\u62bc\u5427\uff01\u201d \u3000\u3000\u4e00\u4e2a\u7070\u8863\u4eba\uff0c\u7acb\u523b\u9012\u4e0a\u4e86\u4e00\u652f\u7b14\u6765\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u5212\u4e86\u62bc\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u63a5\u8fc7\u6761\u636e\uff0c\u8c22\u4e5f\u672a\u8bf4\u4e00\u58f0\uff0c\u4e00\u6325\u624b\uff0c\u5b88\u5728\u56db\u5468\u7684\u7070\u8863\u4eba\u7acb\u523b\u5411\u5916\u9038\u53bb\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u8d70\u5f97\u4eba\u8e2a\u987f\u6773\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\u73b0\u5b9e\u7684\u4ea4\u6613\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u957f\u9752\uff0c\u4e5f\u4e0d\u80fd\u602a\u4ed6\u4eec\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u8fd9\u4e9b\u9ed1\u9053\u7ec4\u7ec7\uff0c\u6839\u672c\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u4eba\u60c5\u9053\u4e49\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u56db\u5468\u51fa\u73b0\u4e86\u5e62\u5e62\u4eba\u5f71\uff0c\u56f4\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u6211\uff0c\u201d\u9648\u6c9b\u6025\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u8f7b\u8f7b\u5618\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90fd\u6765\u4e86\u3002\u201d \u3000\u3000\u9648\u6c9b\u9053\uff1a\u201c\u6211\u4eec\u656c\u9075\u4ee4\u8c15\uff0c\u4e0d\u6562\u903c\u5f97\u592a\u8fd1\uff0c\u8fdc\u8fdc\u7684\u8ffd\u8ff9\u800c\u6765\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u9648\u6c9b\uff0c\u627e\u4e24\u4e2a\u4eba\u5e26\u7740\u6728\u6876\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u4ed6\u4e0d\u80af\u8bf4\u660e\u5723\u6c34\uff0c\u53ea\u8bf4\u662f\u4e24\u4e2a\u6728\u6876\uff0c\u663e\u7136\u8fd9\u53d6\u5f97\u5723\u6c34\u4e00\u4e8b\uff0c\u8fd8\u662f\u4e00\u6869\u79d8\u5bc6\u3002\u201d \u3000\u3000\u9664\u4e86\u5177\u6709\u67d0\u79cd\u7b49\u7ea7\u8eab\u4efd\u7684\u4eba\uff0c\u5176\u4ed6\u7684\u4eba\uff0c\u5e76\u4e0d\u77e5\u6653\u3002 \u3000\u3000\u9648\u6c9b\u70b9\u70b9\u5934\uff0c\u62db\u624b\u5524\u8fc7\u6765\u516d\u4e2a\u5927\u6c49\u3002\u9053\uff1a\u201c\u5f20\u516d\u3001\u738b\u4e5d\uff0c\u4f60\u4eec\u4e24\u4e2a\u5404\u62b1\u4e00\u4e2a\u6728\u6876\uff0c\u5176\u4f59\u56db\u4e2a\u4eba\uff0c\u53ea\u7ba1\u4fdd\u62a4\u4eba\u548c\u6728\u6876\uff0c\u4e0d\u8bba\u53d1\u751f\u4ec0\u4e48\u4e8b\uff0c\u4f60\u4eec\u90fd\u4e0d\u8bb8\u79bb\u5f00\u8fd9\u4e24\u4e2a\u6728\u6876\uff0c\u6876\u5728\u4eba\u5728\uff0c\u6876\u5931\u4eba\u4ea1\u3002\u201d \u3000\u3000\u516d\u4e2a\u4eba\u5e94\u4e86\u4e00\u58f0\uff0c\u884c\u4e86\u8fc7\u53bb\uff0c\u5176\u4e2d\u4e24\u4e2a\u4eba\uff0c\u5404\u81ea\u62b1\u8d77\u4e00\u4e2a\u6728\u6876\u3002 \u3000\u3000\u53e6\u5916\u56db\u4e2a\u4eba\uff0c\u5374\u7a81\u7136\u5237\u7684\u4e00\u58f0\uff0c\u4eae\u51fa\u4e86\u5175\u5203\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u7528\u5200\uff0c\u4e00\u4e2a\u624b\u6267\u7740\u5224\u5b98\u7b14\uff0c\u4e00\u4e2a\u624b\u6267\u7740\u6781\u5c11\u89c1\u7684\u5916\u95e8\u5175\u5203\uff0c\u9752\u94a2\u8f6e\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8d70\uff01\u628a\u4e1c\u897f\u9001\u5230\u4e07\u5bb6\u5927\u9662\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u77e5\u9053\u4e07\u5bb6\u5927\u9662\uff0c\u90a3\u5c31\u662f\u6021\u7ea2\u4f4f\u5b88\u7684\u5730\u65b9\uff0c\u8fd9\u5730\u65b9\u672c\u662f\u4e00\u5bb6\u59d3\u4e07\u7684\u4eba\u5bb6\u6240\u6709\uff0c\u88ab\u9634\u9633\u5821\u4e70\u4e86\u8fc7\u6765\uff0c\u4f5c\u4e3a\u4e00\u4e2a\u5206\u8235\u3002 \u3000\u3000\u8fd9\u672c\u662f\u4e0d\u91cd\u8981\u7684\u5206\u8235\uff0c\u4f46\u56e0\u5723\u6c34\u4ea4\u6613\uff0c\u4f7f\u5b83\u7684\u8eab\u4ef7\u5927\u589e\u3002 \u3000\u3000\u8fd9\u5ea7\u4e07\u5bb6\u5b85\u9662\uff0c\u53d8\u6210\u4e86\u9634\u9633\u5821\u7684\u91cd\u8981\u5206\u8235\u4e86\u3002\u5f6d\u9707\u3001\u9648\u6c9b\u5f53\u5148\u5e26\u8def\uff0c\u53e6\u5916\u8fd8\u6709\u4e94\u4e2a\u4eba\uff0c\u5206\u6563\u5728\u4e24\u4fa7\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u548c\u53f6\u957f\u9752\uff0c\u8d70\u5728\u540e\u9762\u3002 \u3000\u3000\u5f20\u516d\u3001\u738b\u4e5d\uff0c\u62b1\u7740\u5723\u6c34\uff0c\u5728\u56db\u4e2a\u624b\u6267\u5175\u5203\u7684\u5927\u6c49\u56f4\u7ed5\u4e0b\uff0c\u8d70\u5728\u4e2d\u95f4\u3002 \u3000\u3000\u51fa\u4eba\u610f\u5916\u7684\u987a\u5229\uff0c\u4e00\u8def\u4e0a\u98ce\u5e73\u6d6a\u9759\uff0c\u5230\u8fbe\u4e86\u4e07\u5bb6\u5927\u9662\u3002 \u3000\u3000\u6070\u7ea2\u65e9\u5df2\u5728\u95e8\u53e3\u5904\u606d\u5019\uff0c\u540c\u65f6\uff0c\u4e07\u5bb6\u5927\u9662\u4e2d\u7684\u4eba\uff0c\u4e5f\u6709\u4e86\u5f88\u4e25\u5bc6\u7684\u6212\u5907\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u5e26\u4eba\u884c\u5165\u5927\u5385\uff0c\u653e\u4e0b\u5723\u6c34\uff0c\u624d\u5bf9\u9648\u6c9b\u3001\u5f6d\u9707\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u6708\u6765\uff0c\u4e24\u4f4d\u6700\u662f\u8f9b\u82e6\uff0c\u4eca\u65e5\u603b\u7b97\u5927\u4e8b\u5b8c\u6210\uff0c\u4e24\u4f4d\u672c\u6765\u5e94\u8be5\u597d\u597d\u4f11\u606f\uff0c\u53ef\u662f\uff0c\u5821\u4e3b\u5927\u9a7e\u8fd8\u672a\u8d76\u5230\u2026\u2026\u201d \u3000\u3000\u9648\u6c9b\u63a5\u9053\uff1a\u201c\u600e\u4e48\uff1f\u5821\u4e3b\u8981\u6765\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5bf9\uff01\u5821\u4e3b\u8981\u5e26\u4e00\u6279\u65b0\u7684\u4eba\u624b\u6765\u66ff\u6362\u4f60\u4eec\uff0c\u4e5f\u597d\u8ba9\u4f60\u4eec\u4f11\u606f\u4e00\u4e0b\u3002\u201d \u3000\u3000\u5f6d\u9707\u9053\uff1a\u201c\u5821\u4e3b\u8fd8\u80fd\u60f3\u5230\u6211\u4eec\u8f9b\u82e6\uff0c\u90a3\u5b9e\u5728\u662f\u53eb\u4eba\u611f\u6fc0\u5f97\u5f88\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5821\u4e3b\u5bf9\u6211\u4eec\u90fd\u6781\u5173\u6ce8\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5728\u5821\u4e3b\u672a\u5230\u4e4b\u524d\uff0c\u4e24\u4f4d\u8fd8\u8981\u591a\u591a\u8d39\u5fc3\u4e00\u4e9b\u3002\u201d \u3000\u3000\u9648\u6c9b\u9053\uff1a\u201c\u90a3\u662f\u5e94\u8be5\u7684\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u54b1\u4eec\u5821\u4e2d\u4e24\u4f4d\u6b66\u58eb\u5931\u8e2a\uff0c\u90a3\u662f\u8bf4\u660e\u4e86\u4e00\u573a\u5927\u98ce\u66b4\u7684\u524d\u594f\u5df2\u7ecf\u5f00\u59cb\uff0c\u4ed6\u4eec\u6ca1\u6709\u5728\u8def\u4e0a\u52a8\u624b\uff0c\u5927\u6982\u662f\u53d1\u73b0\u4e86\u54b1\u4eec\u5b9e\u529b\u76f8\u5f53\u5f3a\u5927\uff0c\u77e5\u96be\u800c\u9000\uff0c\u4f46\u4ed6\u4eec\u7edd\u5bf9\u4e0d\u4f1a\u653e\u624b\uff0c\u6211\u60f3\uff0c\u53ef\u80fd\u5728\u8c03\u96c6\u4eba\u624b\uff0c\u4e00\u65e6\u5bf9\u4e07\u5bb6\u5927\u9662\u53d1\u52a8\u4e86\u653b\u51fb\uff0c\u5fc5\u7136\u662f\u52bf\u9053\u51cc\u5389\u65e0\u5339\u3002\u201d \u3000\u3000\u5f6d\u9707\u9053\uff1a\u201c\u603b\u62a4\u6cd5\u7684\u610f\u601d\uff0c\u53ef\u662f\u8ba9\u6211\u4eec\u4ecd\u7136\u5404\u56de\u539f\u4f4d\u2026\u2026\u201d \u3000\u3000\u5c60\u65e0\u65b9\u63a5\u9053\uff1a\u201c\u4e0d\u662f\uff01\u54b1\u4eec\u4e1c\u897f\u5df2\u7ecf\u5230\u624b\uff0c\u773c\u4e0b\u6700\u91cd\u8981\u7684\u4e8b\uff0c\u662f\u8981\u4fdd\u62a4\u8fd9\u6279\u4e1c\u897f\uff0c\u8bf8\u4f4d\u7531\u73b0\u5728\u5f00\u59cb\uff0c\u5c31\u7559\u5728\u4e07\u5bb6\u5927\u9662\u4e2d\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u66ff\u8bf8\u4f4d\u51c6\u5907\u597d\u4e86\u9152\u83dc\u3001\u8863\u670d\uff0c\u8bf8\u4f4d\u8bf7\u9971\u9910\u4e00\u987f\uff0c\u6c90\u6d74\u66f4\u8863\uff0c\u7136\u540e\u518d\u597d\u597d\u7684\u4f11\u606f\u4e00\u4e0b\uff1a\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7b11\u9053\uff1a\u201c\u8fd9\u4e07\u5bb6\u5927\u9662\u4e2d\u539f\u6709\u4e00\u90e8\u5206\u4eba\u624b\uff0c\u90fd\u5f52\u6021\u7ea2\u59d1\u5a18\u7387\u9886\uff0c\u8bf8\u4f4d\u53ef\u4ee5\u5b89\u5fc3\u5403\u559d\u4e00\u987f\uff0c\u6211\u60f3\u4ed6\u4eec\u5c31\u662f\u4e0b\u624b\uff0c\u4e5f\u4e0d\u4f1a\u5728\u4eca\u5929\u4e0b\u5348\u3002\u201d \u3000\u3000 \u3000\u3000\u4e09\u65e5\u540e\uff0c\u591c\u95f4\u4e8c\u66f4\u3002 \u3000\u3000\u53f6\u957f\u9752\u521a\u521a\u5750\u606f\u9192\u6765\u3002 \u3000\u3000\u8fd9\u51e0\u5929\uff0c\u6574\u4e2a\u7684\u4e07\u5bb6\u5927\u9662\uff0c\u90fd\u5728\u68ee\u4e25\u7684\u6212\u5907\u4e4b\u4e2d\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u4e0b\u4e86\u4e00\u9053\u4ee4\u8c15\uff0c\u767d\u663c\u53ea\u8981\u51e0\u4e2a\u4eba\u8b66\u6212\uff0c\u4e86\u671b\uff0c\u91cd\u8981\u6212\u5907\u4ee5\u665a\u4e0a\u4e3a\u4e3b\u3002 \u3000\u3000\u53f6\u957f\u9752\u51fa\u6765\u5de1\u89c6\uff0c\u884c\u8fc7\u5927\u5385\uff0c\u6b63\u542c\u5230\u9648\u6c9b\u548c\u5f6d\u9707\u5728\u8c08\u5973\u513f\u53f6\u957f\u9752\u6b63\u60f3\u6084\u7136\u800c\u9000\uff0c\u7a81\u7136\u4e00\u58f0\u559d\u53f1\u4f20\u4e0b\u8fc7\u6765\u3002 \u3000\u3000\u9648\u6c9b\u5f53\u5148\u98de\u51fa\u5927\u5385\u3002 \u3000\u3000\u5f6d\u9707\u4e00\u53e3\u5439\u606f\u4e86\u706f\u706b\uff0c\u8ddf\u7740\u8d76\u4e86\u51fa\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e00\u5438\u6c14\uff0c\u8eab\u5b50\u98d8\u7136\u800c\u8d77\uff0c\u5168\u8eab\u8d34\u5728\u5eca\u6a90\u4e0b\u7684\u9634\u6697\u4e4b\u5904\u3002 \u3000\u3000\u53ea\u542c\u4e00\u9635\u5175\u5203\u4ea4\u51fb\uff0c\u7d27\u63a5\u7740\u54cd\u8d77\u4e00\u58f0\u60e8\u53eb\u3002 \u3000\u3000\u679c\u7136\u6709\u4eba\u6478\u8fdb\u4e86\u4e07\u5bb6\u5927\u9662\uff0c\u800c\u4e14\uff0c\u6765\u4eba\u7684\u6b66\u529f\u8fd8\u76f8\u5f53\u9ad8\u3002 \u3000\u3000\u9759\u591c\u4e2d\uff0c\u4e00\u58f0\u559d\u53f1\uff0c\u4e00\u58f0\u60e8\u53eb\uff0c\u60ca\u52a8\u4e86\u6574\u5ea7\u7684\u4e07\u5bb6\u5927\u9662\uff0c\u6709\u51e0\u5904\u539f\u672c\u4eae\u7740\u706f\u706b\u7684\u5730\u65b9\uff0c\u6b64\u65f6\u7acb\u523b\u7184\u53bb\u3002 \u3000\u3000\u4e07\u5bb6\u5927\u9662\u4e2d\u7684\u6212\u5907\uff0c\u65e9\u5df2\u5b89\u6392\uff0c\u867d\u7136\u53d1\u73b0\u4e86\u654c\u8ff9\uff0c\u4f46\u5374\u4e00\u4e1d\u4e0d\u4e71\u3002 \u3000\u3000\u4eca\u591c\u65e0\u6708\uff0c\u4f46\u4ea6\u65e0\u4e91\uff0c\u661f\u5149\u95ea\u70c1\uff0c\u666f\u7269\u9690\u9690\u53ef\u89c1\u3002 \u3000\u3000\u53f6\u957f\u9752\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u9648\u6c9b\u3001\u5f6d\u9707\u8f7b\u8f7b\u5411\u4e24\u4fa7\u79fb\u52a8\uff0c\u5206\u522b\u7ad9\u5728\u5385\u524d\u4e24\u5904\u5c4b\u89d2\u6240\u5728\u3002 \u3000\u3000\u90a3\u4e00\u58f0\u60e8\u53eb\u4e4b\u540e\uff1b\u4e5f\u672a\u89c1\u654c\u4eba\u653b\u5165\u3002 \u3000\u3000\u663e\u7136\uff0c\u6765\u7684\u4eba\u4e5f\u5f88\u6c89\u7740\u3002 \u3000\u3000\u53cc\u65b9\u5bf9\u5cd9\u4e86\u5927\u7ea6\u4e00\u76cf\u70ed\u8336\u5de5\u592b\uff0c\u9648\u6c9b\u624d\u7f13\u6b65\u884c\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u670b\u53cb\uff0c\u8bf7\u73b0\u8eab\u76f8\u89c1\uff0c\u54b1\u4eec\u5df2\u606d\u5019\u591a\u65f6\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u4e00\u58f0\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u6211\u8fd8\u8ba4\u4e3a\uff0c\u4e07\u5bb6\u5927\u9662\u4e2d\u53ea\u6709\u4e00\u4e2a\u5b88\u95e8\u7684\uff0c\u539f\u6765\uff0c\u8fd8\u6709\u4eba\u7a9d\u5728\u91cc\u9762\u3002\u201d \u3000\u3000\u9648\u6c9b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u5403\u4ec0\u4e48\u957f\u5927\u7684\uff0c\u600e\u4e48\u8bf4\u4e0d\u51fa\u4e00\u53e5\u4eba\u8bdd\uff0c\u4f60\u6709\u79cd\uff0c\u7ed9\u6211\u7ad9\u51fa\u6765\uff0c\u54b1\u4eec\u4e00\u4e2a\u62fc\u4e00\u4e2a\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u4f60\u8bf4\u8bdd\u53ef\u8981\u7b97\u6570\u3002\u201d \u3000\u3000\u968f\u7740\u8bed\u58f0\uff0c\u98de\u8d77\u4e00\u6761\u4eba\u5f71\uff0c\u5929\u9a6c\u884c\u7a7a\u4e00\u822c\uff0c\u7531\u95e8\u697c\u4e0a\u75be\u98de\u800c\u843d\uff0c\u4e00\u63a0\u56db\u4e08\u591a\u8fdc\uff0c\u843d\u5728\u4e86\u9648\u6c9b\u7684\u9762\u524d\u3002 \u3000\u3000\u53ea\u770b\u773c\u524d\u5f62\u52bf\uff0c\u53f6\u957f\u9752\u4e5f\u660e\u767d\uff0c\u5b88\u5728\u5927\u95e8\u697c\u4e0a\u7684\u6697\u6869\uff0c\u7ed9\u4eba\u62d4\u4e86\u3002 \u3000\u3000\u6765\u4eba\u7a7f\u7740\u4e00\u8eab\u9ed1\u8272\u591c\u884c\u52b2\u88c5\uff0c\u8138\u4e0a\u6234\u7740\u9ed1\u8272\u7684\u5957\u5934\u5e3d\u5b50\uff0c\u53ea\u9732\u51fa\u4e24\u53ea\u773c\u775b\u3002 \u3000\u3000\u9648\u6c9b\u6253\u91cf\u4e86\u9ed1\u8863\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u670b\u53cb\uff0c\u6709\u79cd\u6765\u4e07\u5bb6\u5927\u9662\uff0c\u600e\u4e48\u4e0d\u6562\u9732\u51fa\u672c\u6765\u9762\u76ee\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u6ca1\u6709\u5de5\u592b\u8ddf\u4f60\u78e8\u7259\uff0c\u770b\u638c\u3002\u201d \u3000\u3000\u547c\u7684\u4e00\u58f0\uff0c\u5288\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u9ed1\u8863\u4eba\u638c\u52bf\u5341\u5206\u5f3a\u5927\uff0c\u4e00\u638c\u5288\u6765\uff0c\u5e26\u7740\u5f3a\u52b2\u7684\u547c\u5578\u4e4b\u58f0\u3002 \u3000\u3000\u9648\u6c9b\u8fd9\u4eba\u5341\u5206\u5269\u608d\uff0c\u53f3\u624b\u4e00\u62db\uff0c\u7adf\u7136\u786c\u63a5\u4e0b\u4e00\u638c\u3002 \u3000\u3000\u53cc\u638c\u63a5\u5b9e\uff0c\u54cd\u8d77\u4e00\u58f0\u5927\u9707\u3002 \u3000\u3000\u4e00\u51fb\u4e4b\u4e0b\uff0c\u53cc\u65b9\u7acb\u65f6\u5206\u51fa\u4e86\u5f3a\u5f31\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u7eb9\u98ce\u672a\u52a8\uff0c\u4f46\u9648\u6c9b\u5374\u88ab\u9707\u5f97\u5411\u540e\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u9053\uff1a\u8fd9\u4eba\u6b66\u529f\u4e0d\u5f31\u3002 \u3000\u3000\u6b63\u5f85\u98d8\u8eab\u800c\u4e0b\uff0c\u7a81\u89c1\u4eba\u5f71\u95ea\u52a8\uff0c\u5f6d\u9707\u4ee5\u8fc5\u82e5\u7535\u706b\u7684\u901f\u5ea6\uff0c\u51b2\u5165\u4e86\u573a\u4e2d\u3002 \u3000\u3000\u53f6\u957f\u9752\u53c8\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u4eec\u51c6\u5907\u7fa4\u6bb4\u3002\u201d \u3000\u3000\u5f6d\u9707\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5149\u660e\u6b63\u5927\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u906d\uff1a\u201c\u5f6d\u9707\uff0c\u8fd9\u4e07\u5bb6\u5927\u9662\u4e2d\uff0c\u4e0d\u81f3\u4e8e\u53ea\u6709\u4f60\u4eec\u4e24\u4e2a\u4eba\u5427\uff01\u201d \u3000\u3000\u5f6d\u9707\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u8ba4\u8bc6\u6211\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6211\u4e0d\u4f46\u8ba4\u8bc6\u4f60\uff0c\u800c\u4e14\uff0c\u4e5f\u8ba4\u8bc6\u4ed6\uff0c\u4ed6\u53eb\u6d3b\u50f5\u5c38\u9648\u6c9b\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u9648\u6c9b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u53eb\u9648\u6c9b\uff0c\u9601\u4e0b\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u914d\u77e5\u9053\u8001\u592b\u7684\u59d3\u540d\u3002\u201d \u3000\u3000\u9648\u6c9b\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff1f\u201d \u3000\u3000\u98de\u8d77\u4e00\u811a\uff0c\u8e22\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5f6d\u9707\u548c\u9648\u6c9b\u4e24\u4e2a\u4eba\uff0c\u5df2\u7ecf\u642d\u6863\u591a\u5e74\uff0c\u9648\u6c9b\u4e00\u811a\u8e22\u51fa\uff0c\u5f6d\u9707\u7acb\u65f6\u8fce\u9762\u51fb\u51fa\u4e00\u62f3\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u5de6\u624b\u6a2a\u683c\uff0c\u5c01\u4f4f\u4e86\u9648\u6c9b\u4e00\u811a\uff0c\u53f3\u624b\u4e00\u62ac\uff0c\u8fce\u5411\u5f6d\u9707\u7684\u62f3\u52bf\u3002 \u3000\u3000\u9648\u6c9b\u5438\u4e00\u53e3\u6c14\uff0c\u5411\u540e\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u5f6d\u9707\u5374\u662f\u786c\u78b0\u786c\u7684\u548c\u9ed1\u8863\u4eba\u62fc\u4e86\u4e00\u62f3\u3002 \u3000\u3000\u53ea\u89c9\u8fd9\u4e00\u62f3\u6709\u5982\u649e\u4e0a\u4e86\u94c1\u677f\u4e00\u6837\uff0c\u4e00\u9635\u9aa8\u75bc\u5982\u88c2\uff0c\u8eab\u4e0d\u7531\u4e3b\u7684\u5411\u540e\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u9648\u6c9b\u9000\u53c8\u4e0a\uff0c\u811a\u62f3\u9f50\u65bd\uff0c\u653b\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5f6d\u9707\u4e00\u54ac\u7259\uff0c\u5fcd\u7740\u53f3\u638c\u7684\u75bc\u82e6\uff0c\u914d\u5408\u7740\u9648\u6c9b\u7684\u653b\u52bf\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5927\u559d\u4e00\u58f0\uff0c\u8eab\u5982\u9640\u87ba\u4e00\u8f6c\uff0e\u53cc\u62f3\u9f50\u98de\uff0c\u8fce\u5411\u4e86\u4e24\u4eba\u7684\u653b\u52bf\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\uff0c\u5c55\u5f00\u4e86\u4e00\u573a\u6fc0\u70c8\u7edd\u4f26\u7684\u6076\u6597\u3002 \u3000\u3000\u4e8c\u5341\u4e2a\u56de\u5408\u4e4b\u540e\uff0c\u9ed1\u8863\u4eba\u5df2\u7ecf\u63a7\u5236\u4e86\u5168\u5c40\u3002 \u3000\u3000\u5f6d\u9707\u3001\u9648\u6c9b\u7684\u653b\u52bf\uff0c\u5b8c\u5168\u53d7\u5236\u4e8e\u9ed1\u8863\u4eba\uff0c\u4e24\u4e2a\u4eba\u62f3\u3001\u811a\u653b\u52bf\uff0c\u5168\u7531\u5bf9\u65b9\u62f3\u3001\u811a\u6240\u8bf1\u5bfc\uff0c\u770b\u4e0a\u53bb\u5341\u5206\u72fc\u72c8\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u9ed1\u8863\u4eba\u5df2\u7ecf\u968f\u65f6\u53ef\u4ee5\u5236\u670d\u4e24\u4eba\uff0c\u4f46\u662f\u4ed6\u5374\u4e00\u76f4\u4e0d\u80af\u4e0b\u624b\uff0c\u50cf\u732b\u800d\u8001\u9f20\u4e00\u6837\uff0c\u9017\u7740\u4e24\u4eba\u73a9\u800d\u73a9\u800d\u3002 \u3000\u3000\u53f6\u957f\u9752\u9690\u8eab\u4e4b\u5904\uff0c\u548c\u6597\u573a\u4e0d\u8fdc\uff0c\u501f\u9690\u9690\u661f\u5149\uff0c\u770b\u5f97\u5341\u5206\u6e05\u695a\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u5341\u5206\u9707\u60ca\u8fd9\u9ed1\u8863\u4eba\u6b66\u529f\u4e4b\u9ad8\uff0c\u4f46\u53c8\u6068\u9648\u6c9b\u3001\u5f6d\u9707\u8fd9\u4e24\u4eba\u7684\u5fc3\u5730\u574f\u6bd2\uff0c\u5fc3\u4e2d\u6697\u4f5c\u76d8\u7b97\uff0c\u8fd9\u4e24\u4eba\u5982\u82e5\u88ab\u90a3\u9ed1\u8863\u4eba\u6740\u4e86\uff0c\u81ea\u5df1\u518d\u73b0\u8eab\u66ff\u4ed6\u4eec\u62a5\u4ec7\uff0c\u90a3\u5c82\u4e0d\u662f\u4e00\u4e3e\u4e24\u5f97\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u5206\u660e\u6709\u80fd\u529b\u51fb\u6bd9\u4e24\u4eba\uff0c\u5374\u8fdf\u8fdf\u4e0d\u80af\u4e0b\u624b\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fcd\u4e86\u53c8\u5fcd\uff0c\u5b9e\u5728\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u6b63\u5f85\u98d8\u8eab\u800c\u4e0b\uff0c\u7a81\u95fb\u8863\u8882\u98d8\u98ce\u4e4b\u58f0\uff0c\u4e00\u6761\u4eba\u5f71\uff0c\u7834\u7a7a\u800c\u81f3\u3002 \u3000\u3000\u76f4\u5f85\u5bf9\u65b9\u843d\u7740\u5b9e\u5730\uff0c\u53f6\u957f\u9752\u624d\u770b\u6e05\u695a\u6765\u4eba\u662f\u5c60\u65e0\u65b9\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u773c\u770b\u5bf9\u65b9\u63f4\u624b\u5df2\u5230\uff0c\u53cc\u638c\u7a81\u7136\u4e00\u7d27\uff0c\u9648\u6c9b\u95f7\u54fc\u4e00\u58f0\uff0c\u8dcc\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5f6d\u9707\u5374\u8eab\u5b50\u901f\u95ea\uff0c\u907f\u5f00\u4e86\u4e00\u638c\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u51b7\u7b11\u4e00\u58f0\uff0c\u6b3a\u8eab\u800c\u4e0a\uff0c\u53cc\u65b9\u62f3\u638c\u76f8\u63a5\uff0c\u786c\u78b0\u786c\u7684\u63a5\u4e86\u56db\u62db\u3002 \u3000\u3000\u8fd9\u56db\u62db\u4e00\u63a5\uff0c\u5c31\u663e\u51fa\u4e86\u5c60\u65e0\u65b9\u548c\u9648\u6c9b\u3001\u5f6d\u9707\uff0c\u6709\u7740\u5f88\u5927\u7684\u4e0d\u540c\u3002 \u3000\u3000\u786c\u78b0\u56db\u638c\uff0c\u5c60\u65e0\u65b9\u7eb9\u98ce\u672a\u52a8\uff0c\u5012\u662f\u90a3\u9ed1\u8863\u4eba\uff0c\u53cd\u800c\u88ab\u638c\u529b\u9707\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u9690\u8eab\u5728\u6697\u4e2d\u7684\u53f6\u957f\u9752\uff0c\u867d\u7136\u60f3\u4e0b\u6765\uff0c\u53cd\u800c\u89c9\u5f97\u6709\u4e00\u4e9b\u4e0d\u597d\u610f\u601d\u73b0\u8eab\uff0c\u53ea\u597d\u786c\u7740\u5934\u76ae\u7ee7\u7eed\u85cf\u8eab\u4e0d\u52a8\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u53cc\u76ee\u4e2d\u66b4\u5c04\u51fa\u51b7\u5389\u7684\u795e\u5149\uff0c\u76ef\u5728\u5c60\u65e0\u65b9\u8eab\u4e0a\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8840\u624b\u4e4b\u540d\uff0c\u679c\u4e0d\u865a\u4f20\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u670b\u53cb\uff0c\u65e2\u7136\u8ba4\u8bc6\u5c60\u67d0\uff0c\u4e3a\u4f55\u4e0d\u4ee5\u771f\u6b63\u9762\u76ee\u76f8\u89c1\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5728\u4e0b\u65e2\u7136\u8499\u9762\u800c\u6765\uff0c\u5c31\u672a\u6253\u7b97\u548c\u4f60\u4ee5\u771f\u6b63\u9762\u76ee\u76f8\u89c1\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u4eec\u53ea\u6709\u5728\u6b66\u529f\u4e0a\u5206\u4e2a\u9ad8\u4e0b\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u9601\u4e0b\u65e2\u7136\u4e0d\u613f\u8868\u660e\u8eab\u5206\uff0c\u4e5f\u4e0d\u613f\u4ee5\u771f\u6b63\u9762\u76ee\u76f8\u89c1\uff0c\u4f46\u6765\u6b64\u7528\u5fc3\u603b\u8be5\u8bf4\u4e2a\u660e\u767d\u5427\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c60\u65e0\u65b9\uff0c\u8fd9\u4e0d\u662f\u660e\u77e5\u6545\u95ee\u5417\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53ef\u662f\u4e3a\u4e86\u5723\u6c34\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u662f\u5723\u6c34\uff0c\u54b1\u4eec\u4e5f\u72af\u4e0d\u7740\u62db\u60f9\u4f60\u9ebb\u9762\u8840\u624b\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5723\u6c34\u786e\u5728\u4e07\u5bb6\u5927\u9662\u4e4b\u4e2d\uff0c\u4e0d\u8fc7\uff0e\u8981\u8bf8\u4f4d\u51ed\u672c\u9886\u624d\u80fd\u53d6\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u65e2\u7136\u6765\u4e86\uff0c\u82e5\u662f\u6ca1\u6709\u4e00\u70b9\u628a\u63e1\uff0c\u4e5f\u4e0d\u4f1a\u767d\u8d39\u5de5\u592b\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u53f3\u624b\u4e00\u6325\uff0c\u62cd\u51fa\u4e00\u638c\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u5148\u80dc\u4e86\u5c60\u67d0\u4eba\uff0c\u518d\u8c08\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u53cc\u624b\u8fde\u6325\uff0c\u4e00\u8fde\u653b\u51fa\u4e86\u516b\u638c\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u63a5\u4e0b\u4e86\u516b\u638c\uff0c\u5411\u540e\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u4f46\u4e00\u9000\u5373\u4e0a\uff0c\u7acb\u523b\u5c55\u5f00\u4e86\u53cd\u51fb\u3002 \u3000\u3000\u53cc\u65b9\u8fd9\u4e00\u52a8\u4e0a\u624b\u60c5\u52bf\u5c31\u706b\u66b4\u5f02\u5e38\uff0c\u4e24\u4eba\u7684\u638c\u52bf\uff0c\u90fd\u5e26\u7740\u5f3a\u70c8\u7684\u52b2\u9053\uff0c\u6697\u52b2\uff0c\u9707\u98d8\u8d77\u8fdc\u7acb\u5728\u4e00\u4e08\u5916\u7684\u9648\u6c9b\u548c\u5f6d\u9707\u7684\u8863\u8882\uff1f \u3000\u3000\u53cc\u65b9\u4e0d\u4f46\u638c\u529b\u96c4\u6d51\uff0c\u62fc\u6597\u6fc0\u70c8\uff0c\u800c\u4e14\uff0c\u90fd\u5411\u5bf9\u65b9\u81f4\u547d\u7684\u5730\u65b9\u4e0b\u624b\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u7684\u6b66\u529f\uff0c\u4f3c\u4e4e\u662f\u7a0d\u900a\u4e00\u7b79\uff0c\u4f46\u4ed6\u62fc\u6597\u7684\u610f\u5fd7\u5341\u5206\u5f3a\u70c8\uff0c\u4e00\u76f4\u82e6\u7f20\u4e0d\u820d\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u9053\uff1a\u8fd9\u9ed1\u8863\u4eba\u4e0d\u77e5\u662f\u4ec0\u4e48\u4eba\u7269\uff0c\u6b66\u529f\u7adf\u7136\u5982\u6b64\u9ad8\u5f3a\u3002 \u3000\u3000\u8f6c\u773c\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u7136\u62fc\u5230\u4e86\u767e\u62db\u4ee5\u4e0a\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u529f\u529b\u6df1\u539a\uff0c\u767e\u62db\u4e4b\u540e\uff0c\u9010\u6e10\u53d6\u5f97\u4f18\u52bf\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u53c8\u52c9\u5f3a\u652f\u6301\u4e86\u4e8c\u5341\u62db\uff0c\u5c60\u65e0\u65b9\u7a81\u5c55\u7edd\u5b66\uff0c\u4e00\u628a\u6263\u4f4f\u9ed1\u8863\u4eba\u53f3\u8155\uff0c\u5de6\u624b\u4e00\u62ac\uff0c\u53d6\u4e0b\u4e86\u9ed1\u8863\u4eba\u7684\u9ed1\u8272\u9762\u7f69\u3002 \u3000\u3000\u661f\u5149\u4e0b\uff0c\u5c60\u65e0\u65b9\u770b\u6e05\u695a\u4e86\u5bf9\u65b9\u7684\u9762\u8c8c\uff0c\u7acb\u523b\u677e\u5f00\u4e86\u90a3\u9ed1\u8863\u7684\u53f3\u8155\uff0c\u9a87\u7136\u5411\u540e\u9000\u4e01\u4e09\u6b65\uff0c\u9053\uff1a\u201c\u662f\u4f60\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5c60\u5144\u60f3\u4e0d\u5230\u5427\uff01\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u54b1\u4eec\u4e8c\u5341\u4f59\u5e74\u6765\u89c1\u9762\u4e86\uff0c\u6664\u9762\u65f6\u523b\u7adf\u7136\u662f\u751f\u6b7b\u76f8\u640f\u7684\u5c40\u9762\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e5f\u662f\u6ca1\u6709\u6cd5\u5b50\u7684\u4e8b\u4e86\uff0c\u6211\u5949\u547d\u800c\u6765\uff0c\u5fc5\u987b\u8981\u53d6\u5f97\u5723\u6c34\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e8c\u5341\u4f59\u5e74\u6765\uff0c\u4e54\u8d24\u5f1f\u867d\u7136\u6709\u4e86\u4e0d\u5c11\u7684\u8fdb\u5883\uff0c\u4f46\u5c0f\u5144\u4e5f\u6ca1\u6709\u95f2\u7740\uff0c\u54b1\u4eec\u5df2\u7ecf\u52a8\u8fc7\u624b\u4e86\uff0c\u4f60\u8fd8\u975e\u5c0f\u5144\u4e4b\u654c\uff0c\u6545\u4ea4\u60c5\u6df1\uff0c\u6211\u4e5f\u4e0d\u613f\u610f\u4f24\u5bb3\u4f60\uff0c\u4f60\u8bf7\u4fbf\u5427\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5509\uff0c\u5c60\u5144\uff0c\u76ee\u4e0b\u6211\u4eec\u4e4b\u95f4\uff0c\u53ea\u6709\u4e24\u4e2a\u529e\u6cd5\u89e3\u51b3\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u906d\uff1a\u201c\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e00\u4e2a\u662f\u4f60\u4ea4\u51fa\u5723\u6c34\uff0c\u4e00\u4e2a\u662f\u4f60\u628a\u5144\u5f1f\u6740\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u906d\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u5982\u6b64\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u4f60\u5982\u4e0d\u4ea4\u51fa\u5723\u6c34\uff0c\u6211\u4e5f\u5f88\u96be\u6d3b\u5f97\u4e0b\u53bb\u5c60\u5144\u4e5f\u4e0d\u4f1a\u597d\u8fc7\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5c60\u5144\u5e94\u8be5\u60f3\u5230\uff0c\u6765\u8005\u4e0d\u5584\uff0c\u5584\u8005\u4e0d\u6765\uff0c\u5c0f\u5f1f\u6211\u53ea\u662f\u4e00\u4e2a\u9a6c\u524d\u5352\uff0c\u5927\u4eba\u7269\u90fd\u5728\u540e\u9762\u2026\u2026\u201d \u3000\u3000\u5c60\u65e0\u65b9\u51b7\u7b11\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u4e54\u8d24\u5f1f\uff0c\u4f60\u5728\u5a01\u80c1\u6211\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u662f\u5a01\u80c1\uff0c\u662f\u4e8b\u5b9e\uff0c\u5c60\u5144\uff0c\u5c0f\u5f1f\u6839\u60ed\u6127\uff0c\u6240\u4ee5\u5e0c\u671b\u4f60\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6740\u4e86\u4f60\uff0c\u80fd\u591f\u963b\u6b62\u4ed6\u4eec\u6765\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u80fd\uff0c\u6740\u4f60\u4f55\u76ca\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6740\u4e86\u6211\uff0c\u5c0f\u5f1f\u81f3\u5c11\u53ef\u4ee5\u773c\u4e0d\u89c1\u5fc3\u4e0d\u70e6\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u770b\u5c60\u5144\uff0c\u4e3a\u4e86\u4fdd\u62a4\u5723\u6c34\uff0c\u6e85\u8840\u4e07\u5bb6\u5927\u9662\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u8138\u4e0a\u6cdb\u8d77\u4e86\u6012\u5bb9\uff0c\u4f46\u5f88\u5feb\u53c8\u5e73\u606f\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u4e54\u8d24\u5f1f\uff0c\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\uff0c\u6765\u7684\u90fd\u662f\u4e9b\u4ec0\u4e48\u4eba\u7269\uff0c\u4e5f\u8bb8\u6211\u53ef\u4ee5\u5bf9\u4ed8\uff0c\u4f60\u5c31\u7559\u5728\u8fd9\u91cc\uff0c\u54b1\u4eec\u5144\u5f1f\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6295\u5165\u9634\u9633\u5821\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6295\u5165\u9634\u9633\u5821\u6709\u4ec0\u4e48\u4e0d\u597d\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u9634\u9633\u5821\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u8a89\uff0c\u5e76\u975e\u5f88\u597d\uff0c\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4f60\u4eec\u90a3\u4e2a\u7ec4\u7ec7\uff0c\u80fd\u52a8\u624b\u6765\u62a2\u522b\u4eba\u82b1\u4e86\u5927\u628a\u94f6\u5b50\u4e70\u6765\u7684\u5723\u6c34\uff0c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u6b63\u5f53\u7684\u7ec4\u7ec7\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u662f\u9ed1\u5403\u9ed1\u7684\u706b\u5e76\uff0c\u5c60\u5144\uff0c\u800c\u4f60\u4eec\u5374\u662f\u5168\u65e0\u80dc\u7b97\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u542c\u4f60\u53e3\u6c14\uff0e\u4f3c\u4e4e\u662f\u4eca\u665a\u6765\u5230\u4e07\u5bb6\u5927\u9662\u7684\u4eba\uff0c\u5403\u5b9a\u4e86\u6211\u4eec\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c60\u5144\uff0c\u6211\u53ea\u63d0\u4e24\u4e2a\u4eba\uff0c\u4f60\u5fc3\u4e2d\u5c31\u6709\u6570\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u597d\uff01\u5c0f\u5144\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u8ffd\u98ce\u53df\u548c\u5f2f\u6708\u5200\uff0c\u8fd9\u4e24\u4e2a\u4eba\uff0c\u5c60\u5144\u542c\u8fc7\u5417\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u5446\u4f4f\u4e86\uff0c\u534a\u664c\u4e4b\u540e\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u6765\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u662f\uff01\u4ed6\u4eec\u5c31\u5728\u4e07\u5bb6\u5927\u9662\u5916\u9762\uff0c\u7b49\u5c0f\u5f1f\u7684\u56de\u97f3\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\uff0c\u600e\u4f1a\u6765\u62a2\u522b\u4eba\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4ed6\u4eec\u548c\u5c0f\u5f1f\u4e00\u6837\uff0c\u4e5f\u662f\u8eab\u4e0d\u7531\u5df1\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u66f4\u9707\u52a8\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u4ed6\u4eec\uff0c\u4e0d\u662f\u81ea\u613f\u6765\u7684\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\uff0c\u4e0d\u592a\u7231\u94b1\uff0c\u5982\u679c\u5403\u4e0d\u4f4f\u4ed6\u4eec\u7684\u4eba\uff0c\u5f88\u96be\u4f7f\u4ed6\u4eec\u5c31\u8303\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5509\uff01\u4e54\u8d24\u5f1f\uff0c\u60f3\u4e0d\u5230\uff0c\u4f60\u7adf\u7136\u8fd9\u4e48\u5bf9\u5f85\u611a\u5144\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u8981\u4f60\u6740\u4e86\u6211\uff0c\u4e00\u6b7b\u767e\u4e86\uff0c\u770b\u4e0d\u5230\u4f60\u7684\u60e8\u70c8\u640f\u6740\uff0c\u518d\u4e0d\u7136\uff0c\u8bc6\u65f6\u52a1\u4e00\u4e9b\uff0c\u4ea4\u51fa\u5723\u6c34\uff0c\u514d\u53bb\u8fd9\u4e00\u573a\u640f\u6740\u3002\u201d \u3000\u3000\u8fd9\u65f6\u53f6\u957f\u9752\u5df2\u98d8\u8eab\u843d\u5730\uff0c\u7531\u6697\u5f71\u4e2d\u884c\u4e01\u51fa\u6765\uff0c\u7ad9\u5728\u5c60\u65e0\u65b9\u7684\u8eab\u540e\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u4ecd\u7136\u5e26\u7740\u9b54\u624b\u94c1\u98de\u7559\u4e0b\u6765\u7684\u9762\u5177\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u76ee\u5149\u4e00\u8f6c\uff0c\u770b\u5230\u53f6\u957f\u9752\uff0c\u9a87\u7136\u53eb\u9053\uff1a\u201c\u94c1\u5144\uff0c\u4f60\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u8fd9\u4e00\u903c\uff0c\u903c\u5f97\u53f6\u957f\u9752\u4e0d\u80fd\u4e0d\u5f00\u53e3\u4e86\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8fd8\u6ca1\u6709\u6b7b\uff01\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u94c1\u5144\u5728\u6b64\uff0c\u4e8b\u60c5\u5c31\u597d\u529e\u591a\u4e86\uff0c\u8bf7\u529d\u529d\u5c60\u5144\uff0c\u4ea4\u51fa\u5723\u6c34\u7b97\u4e86\uff0c\u5355\u662f\u8ffd\u98ce\u53df\u548c\u5f2f\u6708\u5200\uff0c\u5df2\u7ecf\u975e\u4f60\u4eec\u6240\u80fd\u5bf9\u4ed8\uff0c\u4f55\u51b5\uff0c\u8fd8\u6709\u522b\u7684\u9ad8\u624b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6545\u610f\u522b\u7740\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u6211\u4e0d\u4f1a\u529d\u5c60\u5144\u7b54\u5e94\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u94c1\u5144\uff0c\u4f60\u548c\u5c60\u5144\u7684\u4ea4\u60c5\u8fc7\u547d\uff0c\u96be\u9053\u4f60\u8981\u770b\u7740\u5c60\u5144\u9001\u4e86\u8fd9\u4e00\u6761\u8001\u547d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u867d\u7136\u548c\u4eba\u8c08\u4e86\u5f88\u591a\u8bdd\uff0c\u4f46\u5374\u4e00\u76f4\u4e0d\u77e5\u90a3\u4eba\u662f\u8c01\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u7531\u5c60\u65e0\u65b9\u7684\u53e3\u4e2d\uff0c\u77e5\u9053\u4ed6\u59d3\u4e54\u3002 \u3000\u3000\u5f53\u4e0b\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e54\u5144\u4e5f\u662f\u5c60\u5144\u7684\u597d\u53cb\uff0c\u4f46\u5374\u5e26\u4e86\u4eba\u6765\u62a2\u4ed6\u7684\u5723\u6c34\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u53f9\u606f\u4e00\u58f0\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u7b54\u8bdd\uff0c\u4e00\u58f0\u9634\u68ee\u7684\u51b7\u7b11\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u4e54\u4e00\u9f99\uff0c\u4f60\u662f\u548c\u6545\u53cb\u53d9\u65e7\u5462\uff1f\u8fd8\u662f\u6765\u8c08\u5723\u6c34\u7684\u4e8b\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u670b\u53cb\u65e2\u7136\u6562\u6253\u9634\u9633\u5821\u7684\u4e3b\u610f\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6562\u73b0\u8eab\u4e00\u89c1\u3002\u201d \u3000\u3000\u6697\u5f71\u4e2d\u53c8\u662f\u4e00\u58f0\u9634\u68ee\u68ee\u7684\u51b7\u7b11\uff0c\u7f13\u6b65\u884c\u51fa\u4e00\u4e2a\u767d\u9aef\u5782\u80f8\u7684\u8001\u8005\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8ffd\u98ce\u53df\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u8001\u592b\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u9601\u4e0b\u548c\u9634\u9633\u5821\u7d20\u6765\u4e95\u6c34\u4e0d\u72af\u6cb3\u6c34\uff0c\u4e3a\u4ec0\u4e48\u62a2\u9634\u9633\u5821\u7684\u5723\u6c34\uff01\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u9634\u9633\u5821\u4e0d\u662f\u4ec0\u4e48\u6b63\u5927\u7ec4\u7ec7\uff0c\u54b1\u4eec\u5403\u4e86\u8d35\u5821\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u60ed\u6127\u4e4b\u5904\uff0c\u9601\u4e0b\u5047\u5982\u4e0d\u4ea4\u51fa\u5723\u6c34\uff0c\u8001\u592b\u4eca\u5bb5\u53ea\u597d\u5927\u5f00\u6740\u6212\u4e86\uff0c\u201d \u3000\u3000\u4e54\u4e00\u9f99\u9053\uff1a\u201c\u5c60\u5144\uff0c\u4f60\u7eb5\u7136\u62fc\u4e01\u8001\u547d\uff0c\u4ecd\u65e0\u6cd5\u4fdd\u62a4\u4f4f\u5723\u6c34\uff0c\u5c60\u5144\u4e09\u601d\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u662f\u6362\u8fc7\u91d1\u5170\u8c31\u7684\u670b\u53cb\uff0c\u4f60\u5982\u8fd8\u8ba4\u6211\u8fd9\u4e2a\u5144\u957f\uff0c\u5c31\u7acb\u523b\u9000\u51fa\u4e07\u5bb6\u5927\u9662\uff0c\u81f3\u4e8e\u4f60\u5e26\u4eba\u6765\u6b64\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4e5f\u4e0d\u8ffd\u7a76\u4e86\uff0c\u4f60\u8bf7\u5427\uff01\u201d \u3000\u3000\u8ffd\u98ce\u53df\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e54\u4e00\u9f99\uff0c\u4f60\u4e5f\u8be5\u4f5c\u4e00\u4e2a\u51b3\u5b9a\u4e86\uff0c\u4e0d\u8fc7\uff0c\u8001\u592b\u7edd\u5bf9\u4e0d\u52c9\u5f3a\u4f60\uff0c\u4f60\u89c9\u7740\u5e94\u8be5\u5e2e\u8c01\uff1f\u7559\u4e0b\u6765\uff0c\u8fd8\u662f\u5c31\u6b64\u79bb\u53bb\u3002\u201d \u3000\u3000\u4e54\u4e00\u9f99\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u5509\uff01\u5c60\u5144\uff0c\u54b1\u4eec\u867d\u5c5e\u91d1\u5170\u4e4b\u4ea4\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u5c0f\u5f1f\u4e00\u76f4\u662f\u4f9d\u9644\u522b\u4eba\u9ebe\u4e0b\uff0c\u8fd9\u4e00\u70b9\u5c60\u5144\u60f3\u6765\u5b9a\u80fd\u9274\u8c05\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e54\u8d24\u5f1f\uff0c\u654c\u6211\u4e00\u5b9a\u5f97\u5206\u660e\uff0c\u4f60\u5982\u771f\u6709\u82e6\u8877\uff0c\u54b1\u4eec\u53ea\u597d\u62d4\u4e86\u9999\u5934\uff0c\u5212\u5730\u7edd\u4ea4\u3002\u201d \u3000\u3000\u4e54\u4e00\u9f99\u9053\uff1a\u201c\u5c60\u5144\u575a\u51b3\u5982\u6b64\uff0c\u5144\u5f1f\u4e5f\u53ea\u597d\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e54\u4e00\u9f99\u660e\u77e5\u4f60\u8fd9\u4f4d\u5c60\u5927\u54e5\u5728\u6b64\u4e3b\u4e8b\uff0c\u5374\u5e26\u4e86\u4eba\u6765\u6b64\uff0c\u52ab\u53d6\u5723\u6c34\uff0c\u8fd9\u54ea\u91cc\u8fd8\u6709\u4ec0\u4e48\u5144\u5f1f\u4e4b\u60c5\uff0c\u8fd9\u79cd\u670b\u53cb\u4e0d\u4ea4\u4e5f\u7f62\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u6211\u8fd9\u4e2a\u655d\u5927\u54e5\u7684\uff0c\u603b\u4e0d\u80fd\u5148\u884c\u7edd\u60c5\u65a9\u4e49\uff0c\u6211\u8981\u4ed6\u4eb2\u53e3\u8bf4\u51fa\u6765\uff0c\u6211\u4eec\u662f\u654c\u662f\u53cb\u3002\u201d \u3000\u3000\u4e54\u2014\u9f99\u9053\uff1a\u201c\u5c60\u8001\u5927\uff0c\u8bdd\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff0c\u5c0f\u5f1f\u65e2\u662f\u53e6\u4e00\u4e2a\u7ec4\u7ec7\u4e2d\u4eba\uff0c\u5c31\u8be5\u5bf9\u90a3\u4e2a\u7ec4\u7ec7\u5fe0\u5b9e\uff0c\u5c31\u50cf\u4f60\u5c60\u5927\u54e5\u4e00\u6837\uff0c\u660e\u77e5\u4e0d\u53ef\u4e3a\uff0c\u800c\u4e3a\u4e4b\uff0c\u90a3\u8868\u793a\u4e86\u4f60\u5bf9\u9634\u9633\u5821\u7684\u5fe0\u8bda\uff0c\u8fd9\u662f\u5404\u4e3a\u5176\u4e3b\uff0c\u5982\u4f55\u80fd\u602a\u5c0f\u5f1f\u4e0d\u4e49\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e54\u8d24\u5f1f\uff0c\u6211\u6ca1\u6709\u602a\u4f60\uff0c\u53ea\u662f\uff0c\u4e8b\u5df2\u903c\u5230\u5934\u4e0a\uff0c\u54b1\u4eec\u4e0d\u5f97\u4e0d\u628a\u8bdd\u8bf4\u4e2a\u6e05\u695a\u3002\u201d \u3000\u3000\u4e54\u4e00\u9f99\u9053\uff1a\u201c\u5c60\u8001\u5927\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u8ffd\u98ce\u53df\u3001\u5f2f\u6708\u5200\uff0c\u4e24\u4f4d\u9ad8\u624b\uff0c\u90fd\u5df2\u6765\u6b64\uff0c\u4e5f\u662f\u60f3\u4e00\u5c3d\u5144\u5f1f\u60c5\u4e49\uff0c\u8bdd\u5df2\u8bf4\u7684\u5f88\u660e\u767d\u4e86\uff0c\u4f60\u5c60\u8001\u5927\u4f5c\u4f55\u533a\u5904\uff0c\u90a3\u662f\u4f60\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e54\u8d24\u5f1f\uff0c\u8fd9\u4e2d\u95f4\uff0c\u6709\u4e00\u70b9\u4e0d\u540c\uff0c\u662f\u4f60\u4e54\u8d24\u5f1f\u5e26\u7740\u4eba\u627e\u4e0a\u4e07\u5bb6\u5927\u9662\u7684\uff0c\u65e2\u662f\u5fe0\u4e49\u96be\u4e24\u5168\uff0c\u54b1\u4eec\u4e5f\u53ea\u597d\u65ad\u4e86\u4e49\u6c14\uff0c\u5144\u5f1f\u4eec\u7ffb\u8138\u6210\u4ec7\uff0c\u5927\u5bb6\u5728\u6b66\u529f\u4e0a\u89c1\u4e2a\u9ad8\u4e0b\u4e86\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u7eb5\u58f0\u5927\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u597d\uff01\u4e54\u4e00\u9f99\uff0c\u4f60\u65e2\u7136\u5df2\u8868\u660e\u4e86\u5fc3\u5fd7\uff0c\u8001\u592b\u4e5f\u4e0d\u4f1a\u4e3a\u96be\u4f60\u4e86\uff0c\u5bf9\u4ed8\u5c60\u65e0\u65b9\u7528\u4e0d\u7740\u4f60\u51fa\u624b\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u8f6c\u6ce8\u5230\u5c60\u65e0\u65b9\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u662f\u8981\u4ea4\u51fa\u5723\u6c34\u5462\uff1f\u8fd8\u662f\u8981\u653e\u624b\u4e00\u6218\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u89c9\u7740\uff0c\u4f60\u8fd9\u6837\u5c31\u53ef\u4ee5\u8feb\u4f7f\u5728\u4e0b\u4ea4\u51fa\u5723\u6c34\u4e86\uff1f\u201d \u3000\u3000\u8ffd\u98ce\u53df\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5c60\u65e0\u65b9\uff0c\u8001\u592b\u53ea\u4e0d\u8fc7\u7ed9\u4f60\u4e00\u4e2a\u673a\u4f1a\u7f62\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8ffd\u98ce\u53df\uff0c\u4ee5\u9601\u4e0b\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u8a89\u5730\u4f4d\uff0c\u5c60\u67d0\u4eba\u5fc3\u4e2d\u5341\u5206\u656c\u91cd\uff0c\u4f46\u5982\u8bf4\u9634\u9633\u5821\u6015\u4e86\u4f60\uff0c\u8001\u5b9e\u8bf4\uff0c\u90a3\u5012\u672a\u5fc5\u89c1\u5f97\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u5c60\u65e0\u65b9\uff0c\u4f60\u597d\u72c2\u7684\u53e3\u6c14\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e0d\u8bba\u4f60\u600e\u4e48\u60f3\uff0c\u6211\u89c9\u7740\uff0c\u5728\u4e0b\u8981\u8bf4\u7684\u8bdd\uff0c\u8fd8\u662f\u5e94\u8be5\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u5c31\u51ed\u4f60\u4eec\u9634\u9633\u5821\u4e2d\u51e0\u6839\u8471\uff0c\u8fd8\u80fd\u5bf9\u8001\u592b\u6784\u6210\u4ec0\u4e48\u5a01\u80c1\u5417\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5982\u679c\u9601\u4e0b\u81ea\u89c9\u8eab\u4efd\u5f88\u9ad8\uff0c\u6280\u538b\u7fa4\u4f26\uff0c\u54b1\u4eec\u5e94\u8be5\u675f\u624b\u542c\u547d\uff0c\u54b1\u4eec\u5c31\u8c08\u4e0d\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5c60\u65e0\u65b9\uff0c\u5ff5\u5728\u6b66\u6797\u540c\u9053\u7684\u4efd\u4e0a\uff0c\u8001\u592b\u5df2\u7ecf\u7ed9\u4e86\u4f60\u673a\u4f1a\uff0c\u5148\u793c\u540e\u5175\uff0c\u65e5\u540e\u4f20\u8a00\u5230\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u8001\u592b\u6b3a\u4fae\u4f60\u4e86\u3002\u201d \u3000\u3000\u5ffd\u7136\u4e00\u4e3e\u6b65\uff0c\u4eba\u5df2\u8de8\u524d\u4e24\u4e09\u5c3a\u8fdc\uff0c\u4f38\u624b\u4e00\u628a\uff0c\u6293\u5411\u5c60\u65e0\u65b9\u3002 \u3000\u3000\u4ed6\u4e3e\u6b62\u6f47\u6d12\uff0c\u770b\u4e0a\u53bb\u8f7b\u63cf\u6de1\u5199\uff0c\u4e0d\u50cf\u4e00\u822c\u6c5f\u6e56\u4eba\u7269\u52a8\u624b\u4e4b\u524d\uff0c\u6709\u4e00\u80a1\u8083\u715e\u4e4b\u6c14\u3002 \u3000\u3000\u4f46\u90a3\u6325\u624b\u4e00\u6293\uff0c\u5374\u662f\u5feb\u901f\u81f3\u6781\u3002 \u3000\u3000\u5728\u5c60\u65e0\u65b9\u7684\u611f\u53d7\u4e4b\u4e2d\uff0c\u53d1\u89c9\u4ed6\u4e00\u4e3e\u624b\u95f4\uff0c\u4e94\u6307\u5df2\u7ecf\u7b3c\u7f69\u4e01\u81ea\u5df1\u8eab\u4e0a\u4e03\u516b\u5904\u5927\u7a74\u3002 \u3000\u3000\u4e00\u65f6\u95f4\uff0c\u7adf\u7136\u4e0d\u77e5\u5982\u4f55\u95ea\u907f\uff0c\u624d\u80fd\u9003\u8fc7\u5bf9\u65b9\u8fd9\u4e00\u62db\u3002 \u3000\u3000\u8fd9\u4e00\u77ac\u95f4\u4ed6\u611f\u89c9\u5230\u4e86\u5a01\u80c1\uff0c\u4e5f\u611f\u89c9\u5230\u4e86\u5f3a\u5927\u538b\u529b\u3002 \u3000\u3000\u8fd9\u662f\u5c60\u65e0\u65b9\u51fa\u9053\u6c5f\u6e56\u4ee5\u6765\uff0c\u7b2c\u4e00\u6b21\u9047\u4e0a\u4e86\u5982\u6b64\u5f3a\u5927\u7684\u654c\u52bf\u3002 \u3000\u3000\u53ea\u89c9\u5bf9\u65b9\u84c4\u52b2\u672a\u53d1\uff0c\u968f\u65f6\u53ef\u4ee5\u653b\u6765\uff0c\u4f46\u5374\u53c8\u6599\u4e0d\u51c6\u5bf9\u65b9\u653b\u5411\u4f55\u5904\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u5bd2\u5149\u4e00\u95ea\uff0c\u4e00\u652f\u957f\u5251\u4f38\u4e86\u8fc7\u6765\uff0c\u5251\u82b1\u8000\u76ee\uff0c\u5c01\u4f4f\u4e86\u8ffd\u98ce\u53df\u7684\u653b\u52bf\u3002 \u3000\u3000\u51fa\u624b\u7684\u662f\u53f6\u957f\u9752\u3002 \u3000\u3000\u4ed6\u4ecd\u7136\u6234\u7740\u9b54\u624b\u94c1\u98de\u7684\u9762\u5177\uff0c\u4e0d\u5e94\u8be5\u52a8\u5251\u3002 \u3000\u3000\u4f46\u4ed6\u770b\u5230\u4e86\u8ffd\u98ce\u53df\u7684\u6b66\u529f\uff0c\u90a3\u662f\u6c5f\u6e56\u4e0a\u6781\u5c11\u89c1\u7684\u771f\u6b63\u9ad8\u624b\u3002 \u3000\u3000\u81ea\u5df1\u7684\u7279\u957f\u662f\u5251\u6cd5\uff0c\u5982\u82e5\u820d\u5251\u5bf9\u654c\uff0c\u90a3\u662f\u4ee5\u5df1\u4e4b\u77ed\uff0c\u5bf9\u4eba\u4e4b\u957f\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u4eae\u4e86\u5251\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u88ab\u90a3\u4e00\u5251\u628a\u653b\u52bf\u7ed9\u903c\u4f4f\uff0c\u5fc3\u4e2d\u4ea6\u4e0d\u7981\u6709\u4e9b\u9a87\u7136\u3002 \u3000\u3000\u4ed6\u4eec\u5df2\u7ecf\u628a\u4e07\u5bb6\u5927\u9662\u4e2d\u5e95\u7ec6\u7ed9\u6478\u6e05\u695a\u4e86\uff0c\u8fd9\u5730\u65b9\u6b66\u529f\u6700\u9ad8\u7684\u662f\u5c60\u65e0\u65b9\u3002 \u3000\u3000\u4f46\u5374\u672a\u6599\u5230\uff0c\u5ffd\u7136\u95f4\u5192\u51fa\u8fd9\u4e48\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u53cc\u76ee\u51dd\u795e\u903c\u89c6\u4f4f\u53f6\u957f\u9752\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e54\u4e00\u9f99\u9053\uff1a\u201c\u4ed6\u662f\u9b54\u624b\u94c1\u98de\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\uff0c\u9b54\u624b\u94c1\u98de\u4e0d\u7528\u5251\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9063\uff1a\u201c\u4e0d\u8bba\u6211\u662f\u8c01\uff0c\u4f3c\u4e4e\u662f\u5e76\u4e0d\u91cd\u8981\uff0c\u91cd\u8981\u7684\u662f\u6211\u624b\u4e2d\u8fd9\u4e00\u652f\u5251\uff0c\u662f\u5426\u80fd\u5bf9\u4ed8\u4f60\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u7eb5\u58f0\u5927\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u662f\u89c9\u7740\u80fd\u548c\u8001\u592b\u4e00\u6218\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5bf9\u81ea\u5df1\u5251\u4e0a\u9020\u8be3\u7684\u4fe1\u5fc3\uff0c\u4e00\u5411\u5f88\u5f3a\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u822c\u82f1\u96c4\u8c6a\u6770\uff0c\u90a3\u5c31\u8be5\u62a5\u4e0a\u771f\u5b9e\u59d3\u540d\uff0c\u53d6\u4e0b\u4f60\u8138\u4e0a\u9762\u5177\uff0c\u8ba9\u8001\u592b\u770b\u770b\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u770b\u8fd9\u4e5f\u7528\u4e0d\u7740\u4e86\uff0c\u4f60\u5982\u80fd\u6740\u4e86\u6211\uff0c\u53c8\u4f55\u7528\u7ba1\u6211\u662f\u4ec0\u4e48\u4eba\uff1f\u9601\u4e0b\u5982\u6740\u4e0d\u4e86\u6211\uff0c\u6211\u662f\u8c01\uff0c\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u80e1\u626f\u4ec0\u4e48\uff1f\u8001\u592b\u624b\u4e0b\uff0c\u4e0d\u6b7b\u65e0\u540d\u4e4b\u5352\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4e00\u6325\u5251\uff0c\u523a\u4e86\u8fc7\u53bb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u80dc\u4e86\u6211\u518d\u5938\u53e3\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u9002\u6728\u8ffd\u98ce\u53df\u548c\u5c60\u65e0\u65b9\u52a8\u624b\u7684\u60c5\u5f62\uff0c\u53f6\u957f\u9752\u770b\u7684\u5341\u5206\u4ed4\u7ec6\uff0c\u8ffd\u98ce\u53df\u7684\u6b66\u529f\u6781\u9ad8\uff0c\u5c60\u65e0\u65b9\u5c31\u662f\u5728\u5bf9\u65b9\u62a2\u4e86\u5148\u673a\uff0c\u4e00\u62db\u5c31\u903c\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u81ea\u5df1\u4e0d\u80fd\u91cd\u8e48\u8986\u8f99\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4e24\u4eba\u5728\u8c08\u8bdd\u7684\u65f6\u5019\uff0c\u53f6\u957f\u9752\u5c31\u4e00\u76f4\u6697\u4f5c\u6212\u5907\uff0c\u968f\u65f6\uff0c\u51c6\u5907\u62a2\u5148\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u5df2\u611f\u89c9\u5230\u8fd9\u4e00\u6218\uff0c\u65e0\u6cd5\u907f\u514d\u4e86\uff0c\u7acb\u523b\u6325\u5251\u653b\u51fa\u3002 \u3000\u3000\u53f6\u957f\u9752\u7684\u5251\u6cd5\uff0c\u672c\u6765\u4ee5\u8fc5\u5feb\u89c1\u957f\uff0c\u4e00\u5251\u653b\u51fa\u4e4b\u540e\uff0c\u8fd9\u65f6\u8fde\u7ef5\u4e0d\u7edd\u7684\u8fde\u73af\u51fb\u51fa\uff0c\u5251\u52bf\u5982\u957f\u6c5f\u5927\u6cb3\u4e00\u822c\uff0c\u7740\u7740\u903c\u8fdb\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u7adf\u88ab\u53f6\u957f\u9752\u4e00\u8f6e\u6025\u5251\u5feb\u653b\uff0c\u95f9\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u4e00\u8fde\u9000\u540e\u4e86\u4e03\u516b\u6b65\uff0c\u624d\u628a\u9635\u52bf\u7a33\u4f4f\u3002 \u3000\u3000\u53f6\u957f\u9752\u653b\u51fa\u4e86\u7b2c\u4e8c\u5341\u516b\u5251\u65f6\uff0c\u8ffd\u98ce\u66fc\u624d\u5c55\u5f00\u4e86\u53cd\u51fb\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u6307\u70b9\u3001\u638c\u5288\uff0c\u4e0d\u5230\u5341\u62db\u5df2\u628a\u53f6\u957f\u9752\u653b\u52bf\u7ed9\u6321\u4f4f\u3002 \u3000\u3000\u53f6\u957f\u9752\u53ea\u89c9\u5bf9\u65b9\u653b\u6765\u7684\u638c\u3001\u6307\u4e4b\u4e0a\uff0c\u5e26\u7740\u4e00\u80a1\u5f3a\u5927\u7684\u6697\u52b2\u3001\u6f5c\u529b\uff0c\u903c\u4f4f\u5251\u52bf\uff0c\u4f7f\u5f97\u624b\u4e2d\u5251\u62db\uff0c\u65e0\u6cd5\u53d1\u6325\u51fa\u5a01\u529b\u3002 \u3000\u3000\u4ed6\u7684\u5251\u6cd5\uff0c\u672c\u4ee5\u5feb\u901f\u89c1\u79f0\u3002 \u3000\u3000\u4f46\u8ffd\u98ce\u53df\u4f3c\u4e4e\u662f\u6bd4\u4ed6\u66f4\u5feb\u3002 \u3000\u3000\u53f6\u957f\u9752\u539f\u672c\u62a2\u5f97\u7684\u5148\u673a\uff0c\u4e5f\u88ab\u8ffd\u98ce\u53df\u53cd\u51fb\u903c\u5f97\u9010\u6e10\u5931\u53bb\u3002 \u3000\u3000\u53ea\u542c\u8ffd\u98ce\u53df\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff0c\u201c\u4f60\u5c0f\u4e8e\u53e3\u6c14\u5f88\u5927\uff0c\u6211\u8fd8\u9053\u771f\u6709\u51e0\u4e0b\u4e8e\u73a9\u827a\u7684\uff0c\u60f3\u4e0d\u5230\u7adf\u7136\u53ea\u662f\u4e00\u5f20\u5634\u5df4\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4e5f\u6b63\u5728\u6697\u81ea\u76d8\u7b97\uff0c\u8fd9\u6837\u6253\u4e0b\u53bb\uff0c\u4e0d\u4f46\u96be\u6709\u80dc\u7b97\uff0c\u800c\u4e14\u5f88\u5feb\u4f1a\u8d25\u5728\u5bf9\u65b9\u624b\u4e2d\u3002 \u3000\u3000\u662f\u4e0d\u662f\u5e94\u8be5\u65bd\u5c55\u51fa\u8ffd\u9b42\u4e03\u5251\u3002 \u3000\u3000\u5929\u4e0b\u4f1a\u6b64\u5251\u62db\u7684\uff0c\u53ea\u6709\u4ed6\u53f6\u957f\u9752\u5e08\u5f92\u4e24\u4eba\uff0c \u3000\u3000\u4ee5\u8ffd\u98ce\u53df\u7684\u6b66\u529f\u9020\u8be3\uff0c\u548c\u6c5f\u6e56\u4e0a\u7684\u6e0a\u535a\u89c1\u8bc6\uff0c\u8ffd\u9b42\u4e03\u5251\u4e00\u51fa\u624b\uff0c\u5fc5\u7136\u4f1a\u88ab\u5bf9\u65b9\u770b\u51fa\u6765\u5386\u3002 \u3000\u3000\u5fc3\u4e2d\u6b63\u5728\u72b9\u8c6b\u4e0d\u51b3\uff0c\u518d\u88ab\u8feb\u98ce\u53df\u62ff\u6d3b\u4e00\u6fc0\uff0c\u5251\u62db\u7acb\u523b\u4e00\u53d8\u3002 \u3000\u3000\u8ffd\u9b42\u4e03\u5251\u7b2c\u4e00\u62db\uff0c\u7acb\u523b\u51fa\u624b\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u5df2\u5b8c\u5168\u638c\u63e1\u4e86\u4e3b\u52a8\uff0c\u773c\u770b\u80dc\u7b97\u5df2\u7ecf\u5728\u63e1\uff0c\u5374\u4e0d\u6599\u5bf9\u65b9\u7a81\u51fa\u5947\u62db\u3002 \u3000\u3000\u5251\u51dd\u51b7\u971c\uff0c\u5bd2\u8292\u6253\u95ea\uff0c\u51b2\u7834\u4e86\u8ffd\u98ce\u53df\u62a4\u8eab\u7684\u771f\u6c14\uff0c\u76f4\u903c\u524d\u80f8\u8981\u5bb3\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u5fc3\u5934\u9a87\u7136\uff0c\u4e00\u5438\u6c14\uff0c\u98d8\u9000\u4e86\u4e03\u5c3a\uff1f\u5389\u58f0\u559d\u9053\uff1a\u201c\u53f6\u957f\u9752\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6014\u4e86\u4e00\u6014\uff0c\u53d6\u4e0b\u8138\u4e0a\u7684\u9762\u5177\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u554a\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u7adf\u7136\u6ca6\u5165\u4e86\u9634\u9633\u5821\u4e2d\uff0c\u8ba7\u6e56\u4e0a\u76db\u4f20\u4f60\u5728\u592a\u6e56\u9047\u5bb3\uff0c\u539f\u6765\u662f\u91d1\u8749\u8131\u58f3\u4e4b\u8ba1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53ea\u89c9\u6b64\u4e2d\u5f88\u591a\u7684\u4e8b\u60c5\u65e0\u6cd5\u89e3\u8bf4\uff0c\u4e5f\u4e0d\u80fd\u89e3\u8bf4\uff0c\u53ea\u597d\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u5bf9\u6211\u7684\u4e8b\uff0c\u597d\u50cf\u5341\u5206\u5173\u5fc3\uff0c\u4f46\u5728\u4e0b\u7684\u8bb0\u5fc6\u4e4b\u4e2d\uff0c\u6211\u4eec\u4f3c\u4e4e\u662f\u6ca1\u6709\u89c1\u8fc7\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u8001\u592b\u5173\u5fc3\u7684\u4e0d\u662f\u4f60\uff0c\u6211\u662f\u5173\u5fc3\u78e8\u5251\u8001\u4eba\uff0c\u4ed6\u600e\u4e48\u6559\u51fa\u4e86\u8fd9\u4e48\u4e00\u4e2a\u6ca1\u6709\u51fa\u606f\uff0c\u66ff\u4ed6\u4e22\u4eba\u7684\u5f92\u5f1f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8001\u524d\u8f88\u548c\u5bb6\u5e08\u2026\u2026\u201d \u3000\u3000\u8ffd\u98ce\u53df\u63a5\u9053\uff1a\u201c\u6211\u4eec\u662f\u4e94\u5341\u5e74\u7684\u8001\u670b\u53cb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u665a\u8f88\u8ffd\u968f\u5bb6\u5e08\u5341\u5e74\uff0c\u5374\u6ca1\u6709\u89c1\u8fc7\u8001\u524d\u8f88\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u6211\u4eec\u89c1\u9762\u7684\u673a\u4f1a\u662f\u4e0d\u591a\uff0c\u4f46\u5374\u53cb\u8c0a\u957f\u9752\uff0c\u4e09\u5e74\u524d\uff0c\u8001\u592b\u5728\u5357\u8352\u9047\u4e0a\u4ed6\uff0c\u4ed6\u8fd8\u6258\u6211\u7167\u987e\u4f60\uff0c\u54fc\uff01\u60f3\u4e0d\u5230\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u7684\u8eab\u4f53\u597d\u5427\uff1f\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u8eab\u4f53\u5f88\u597d\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u7684\u5f92\u5f1f\u592a\u4e0d\u4e89\u6c14\uff0c\u7adf\u7136\u6295\u5165\u4e86\u9634\u9633\u5821\u4e2d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u56de\u987e\u4e86\u5c60\u65e0\u65b9\u4e00\u773c\uff0c\u5c60\u65e0\u65b9\u5374\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5c60\u65e0\u65b9\u4e5f\u65e0\u6cd5\u5e2e\u5f97\u4e0a\u5fd9\u3002 \u3000\u3000\u4ed6\u7684\u58f0\u8a89\uff0c\u4e00\u5411\u4e0d\u597d\u3002 \u3000\u3000\u53f6\u957f\u9752\u8f7b\u8f7b\u5618\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4f60\u662f\u5bb6\u5e08\u7684\u670b\u53cb\uff0c\u81ea\u7136\u77e5\u6653\u5bb6\u5e08\u7684\u4e3a\u4eba\u4e86\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u5bf9\u4ed6\u8ffd\u9b42\u4e03\u5251\uff0c\u719f\u6089\u7684\u5f88\uff0c\u5f53\u4f60\u7528\u51fa\u6765\u7b2c\u4e00\u5251\u65f6\uff0c\u6211\u5df2\u7ecf\u77e5\u9053\u4e86\u4f60\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u665a\u8f88\u7684\u610f\u601d\u662f\uff0c\u5bb6\u5e08\u4e3a\u4eba\u5f88\u65b9\u6b63\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u78e8\u5251\u8001\u4eba\u4ed6\u4e00\u751f\u803f\u76f4\uff0c\u6c5f\u6e56\u4e0a\u4eba\u4eba\u5d07\u656c\uff0c\u771f\u60f3\u4e0d\u5230\u4ed6\u7684\u5f1f\u5b50\u5374\u7834\u574f\u4e86\u4ed6\u5728\u6c5f\u6e56\u4e0a\u7684\u803f\u76f4\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u5012\u672a\u5fc5\uff0c\u53f6\u957f\u9752\u662f\u78e8\u5251\u8001\u4eba\u7684\u5f1f\u5b50\uff0c\u6211\u4e0d\u4f1a\u4e22\u4ed6\u8001\u4eba\u5bb6\u7684\u4eba\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u5389\u58f0\u559d\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u9634\u9633\u5821\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u7ec4\u7ec7\u5417\uff1f\u9ebb\u9762\u8840\u624b\uff0c\u53c8\u662f\u4ec0\u4e48\u6837\u7684\u4eba\u7269\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u6487\u5f00\u5bb6\u5e08\u4e0d\u8c08\uff0c\u8001\u524d\u8f88\u7387\u9886\u4eba\u624b\uff0c\u6765\u62a2\u522b\u4eba\u7684\u5723\u6c34\uff0c\u96be\u9053\u5c31\u662f\u6b63\u5927\u5149\u660e\u7684\u4e8b\u5417\uff1f\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u5c11\u4e0d\u66f4\u4e8b\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u8fd9\u4e00\u6279\u5723\u6c34\uff0c\u5982\u662f\u843d\u5728\u9634\u9633\u5821\u7684\u624b\u4e2d\uff0c\u4f1a\u6380\u8d77\u591a\u5927\u7684\u98ce\u6ce2\u5417\uff1f\u4f1a\u66ff\u6c5f\u6e56\u5236\u9020\u591a\u5927\u7684\u9ebb\u70e6\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u8fd9\u5723\u6c34\u5df2\u6709\u5f88\u591a\u5e74\u4e86\uff0c\u5982\u679c\u5723\u6c34\u4f1a\u5728\u6c5f\u6e56\u4e0a\u6380\u8d77\u5f88\u5927\u7684\u98ce\u6ce2\uff0c\u5723\u6c34\u5df2\u7ecf\u6d41\u5165\u6c5f\u6e56\u4e0d\u5c11\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u5389\u58f0\u559d\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u4f60\u8fd9\u822c\u8892\u62a4\u9634\u9633\u5821\uff0c\u4f3c\u662f\u5bf9\u4ed6\u4eec\u5341\u5206\u5fe0\u8bda\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u5728\u4e0b\u81ea\u6709\u89e3\u91ca\uff0c\u5982\u679c\u8001\u524d\u8f88\u548c\u5bb6\u5e08\u771f\u662f\u77e5\u5df2\u4e4b\u4ea4\uff0c\u65e5\u540e\uff0c\u5728\u4e0b\u81ea\u4f1a\u5bf9\u5bb6\u5e08\u6709\u4e2a\u4ea4\u4ee3\uff0c\u8fd8\u671b\u8001\u524d\u8f88\u770b\u5728\u5bb6\u5e08\u7684\u4efd\u4e0a\uff0c\u6492\u624b\u4eca\u591c\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u4f60\u8981\u8001\u592b\u6492\u624b\u4e0d\u7ba1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u8001\u592b\u53d7\u4eba\u4e4b\u6258\u800c\u6765\uff0c\u5c82\u80fd\u4e2d\u9014\u7f62\u624b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u53ef\u66fe\u53c2\u52a0\u8fc7\u5723\u6c34\u4ea4\u6613\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u665a\u8f88\u53c2\u52a0\u8fc7\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u90a3\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u51e1\u662f\u53c2\u4e0e\u5723\u6c34\u4ea4\u6613\u7684\u4eba\uff0c\u5168\u4e0d\u662f\u4ec0\u4e48\u597d\u4eba\uff0c\u8001\u524d\u8f88\u4e0d\u67e5\u7fa4\u60c5\uff0c\u5f88\u53ef\u80fd\u662f\u88ab\u4eba\u5bb6\u5229\u7528\u4e86\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u592b\u8fd9\u4e00\u628a\u5e74\u7eaa\uff0c\u5c82\u4f1a\u8f7b\u6613\u88ab\u4eba\u5229\u7528\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u9ed8\u4e86\uff0c\u4ed6\u5df2\u611f\u89c9\u5230\u65e0\u6cd5\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u5982\u679c\u5f3a\u884c\u89e3\u91ca\u4e0b\u53bb\uff0c\u5f88\u53ef\u80fd\u8981\u6cc4\u6f0f\u4e86\u9634\u9633\u5821\u4e2d\u5927\u90e8\u5206\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u5230\uff0c\u5584\u4e3a\u4eba\u89c1\uff0c\u975e\u5584\u4e5f\uff0c\u4e00\u4e2a\u771f\u6b63\u7684\u4fa0\u5ba2\uff0c\u4e0d\u6c42\u540d\u5229\uff0c\u4e0d\u6c42\u95fb\u8fbe\uff0c\u53ea\u662f\u9ed8\u9ed8\u505a\u7740\uff0c\u4ed6\u8ba4\u4e3a\u5e94\u8be5\u505a\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u8981\u505a\u4e00\u4e2a\u771f\u6b63\u7684\u4fa0\u5ba2\u3002 \u3000\u3000\u53e3\u820c\u4e0a\u9690\u7ea6\u7684\u6697\u793a\uff0c\u5df2\u7136\u65e0\u6548\uff0c\u9664\u975e\u628a\u4e8b\u60c5\u644a\u660e\u4e86\u8bb2\uff0c\u5f88\u96be\u6709\u89e3\u51b3\u7684\u529e\u6cd5\u4e86\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u795e\u60c5\u51b7\u8083\u5730\u63a5\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u4f60\u600e\u4e48\u5bf9\u8001\u592b\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\u4ea4\u4ee3\u4e86\uff0c\u5c31\u7b97\u5728\u4e0b\u627f\u8ba4\u4f60\u662f\u5bb6\u5e08\u7684\u670b\u53cb\uff0c\u4f46\u4e5f\u65e0\u6cd5\u5bf9\u4f60\u4ea4\u4ee3\u4ec0\u4e48\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5a03\u513f\uff0c\u82e6\u6d77\u65e0\u8fb9\uff0c\u56de\u5934\u662f\u5cb8\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u665a\u8f88\u5e76\u672a\u611f\u89c9\u5230\u81ea\u5df1\u5728\u82e6\u6d77\u4e4b\u4e2d\uff0c\u5012\u662f\u8001\u524d\u8f88\u5728\u6c5f\u6e56\u4e0a\u7684\u8eab\u4efd\u3001\u58f0\u671b\uff0c\u5924\u591c\u4e4b\u4e2d\uff0c\u5e26\u4eba\u5230\u6b64\u5730\u6765\uff0c\u62a2\u4eba\u4e4b\u7269\uff0c\u672a\u514d\u6709\u8fb1\u6e05\u8a89\u4e86\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u6012\u9053\uff1a\u201c\u6267\u5927\u4e49\u4e0d\u62d8\u5c0f\u8282\uff0c\u8001\u592b\u8fd9\u4e3e\u52a8\uff0c\u7eb5\u7136\u5728\u793c\u6570\u4e0a\u662f\u6709\u4e8f\u635f\u4e4b\u5904\uff0e\u4f46\u5fc3\u4e2d\u5766\u8361\uff0c\u53c8\u4f55\u4f24\u4e4b\u6709\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u770b\u6cd5\u4e0a\u89c1\u4ec1\u89c1\u667a\uff0c\u5404\u6709\u4e0d\u540c\uff0c\u8fd8\u671b\u8001\u524d\u8f88\u770b\u5728\u548c\u5bb6\u5e08\u76f8\u8bc6\u4e00\u573a\u4efd\u4e0a\uff0c\u8d4f\u7ed9\u665a\u8f88\u4e00\u4e2a\u8584\u9762\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u8d4f\u4f60\u4ec0\u4e48\u9762\u5b50\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9ad8\u62ac\u8d35\u624b\uff0c\u9000\u51fa\u4e07\u5bb6\u5927\u9662\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u597d\u554a\uff01\u8fd9\u662f\u5012\u6253\u4e00\u9489\u8019\uff0c\u8001\u592b\u8fd8\u672a\u628a\u4f60\u529d\u670d\uff0c\u4f60\u5012\u53cd\u529d\u8001\u592b\u5b50\uff0c\u770b\u6765\uff0c\u4eca\u591c\u8001\u592b\u975e\u5f97\u597d\u597d\u7684\u6559\u8bad\u4f60\u4e00\u987f\uff0c\u7eb5\u7136\u56e0\u6b64\u5f00\u7f6a\u4e86\u78e8\u5251\u8001\u4eba\uff0c\u4e5f\u662f\u5728\u6240\u4e0d\u60dc\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8005\u524d\u8f88\u5982\u6b64\u6267\u7740\uff0c\u665a\u8f88\u5374\u8d1f\u6709\u5b88\u536b\u8fd9\u4e07\u5bb6\u5927\u9662\u4e4b\u8d23\uff0c\u8fd9\u76f8\u5904\u4e4b\u95f4\uff0c\u5f88\u96be\u6709\u5b8c\u7f8e\u7684\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u5b8c\u7f8e\u7684\u673a\u4f1a\uff1f\u4f60\u5c0f\u5b50\u8fd9\u6837\u6267\u8ff7\u4e0d\u609f\uff0c\u6211\u5e94\u8be5\u7ed9\u4f60\u4e00\u6b21\u597d\u597d\u7684\u6559\u8bad\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u665a\u8f88\u804c\u53f8\u5728\u8eab\uff0c\u8001\u524d\u8f88\u82e5\u5b9a\u8981\u51fa\u624b\uff0c\u665a\u8f88\u5c31\u53ea\u597d\u8fd8\u51fb\u4e86\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u53cc\u76ee\u4e00\u77aa\uff0c\u80e1\u5b50\u4e5f\u5439\u8d77\u8001\u9ad8\uff0c\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u4f60\u5c0f\u5b50\u6562\u8fd8\u624b\uff0c\u8001\u592b\u4e5f\u7b97\u5bf9\u78e8\u5251\u8001\u4eba\u6709\u4e2a\u4ea4\u4ee3\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\u4e00\u4f38\u53f3\u624b\uff0c\u75be\u5411\u53f6\u957f\u9752\u53f3\u8155\u6293\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u53f3\u624b\u4e00\u6c89\uff0c\u907f\u5f00\u4e00\u51fb\uff0c\u957f\u5251\u5ffd\u7ffb\uff0c\u75be\u5982\u661f\u5149\uff0c\u70b9\u5411\u8ffd\u98ce\u53df\u7684\u524d\u80f8\u3002 \u3000\u3000\u679c\u7136\u662f\u4e00\u62db\u4e5f\u4e0d\u76f8\u8ba9\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u957f\u7b11\u4e00\u58f0\uff0c\u638c\u52bf\u5ffd\u7136\u4e00\u53d8\uff0c\u6f5c\u529b\u968f\u7740\u638c\u52bf\uff0c\u6d8c\u4e86\u51fa\u6765\uff0c\u903c\u4f4f\u4e86\u53f6\u957f\u9752\u7684\u5251\u52bf\u3002 \u3000\u3000\u53f6\u957f\u9752\u548c\u4e0d\u5c11\u9ad8\u624b\u8fc7\u62db\uff0c\u4f46\u5374\u4ece\u6765\u6ca1\u6709\u9047\u4e0a\u8fc7\u8fd9\u6837\u7684\u538b\u529b\uff0c\u5f3a\u5927\u7684\u6697\u52b2\u3001\u6f5c\u529b\uff0c\u7b80\u76f4\u903c\u7684\u4eba\u6709\u4e9b\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u5927\u7ea6\u662f\u5bf9\u53f6\u957f\u9752\u7684\u8ffd\u9b42\u4e03\u5251\uff0c\u4e5f\u6709\u4e00\u4e9b\u987e\u8651\uff0c\u6240\u4ee5\uff0c\u53cc\u638c\u529b\u9053\uff0c\u6108\u6253\u6108\u731b\uff0c\u5f3a\u5927\u7684\u6f5c\u529b\u3001\u6697\u52b2\uff0c\u5b8c\u5168\u628a\u53f6\u957f\u9752\u7684\u5251\u8def\u5c01\u6b7b\u3002 \u3000\u3000\u624b\u4e2d\u7a7a\u6709\u5229\u5203\uff0c\u4f46\u5374\u6709\u4e9b\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u7a81\u7136\u95f4\u706b\u5149\u95ea\u52a8\uff0c\u4e24\u76cf\u706f\u7b3c\uff0c\u7a81\u7136\u4eae\u8d77\u3002 \u3000\u3000\u706f\u7b3c\u5206\u6267\u5728\u4e24\u4e2a\u7a7f\u7740\u9752\u8863\u7684\u5973\u5a62\u624b\u4e2d\uff0c\u5f15\u5bfc\u7740\u6021\u7ea2\uff0c\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5728\u6021\u7ea2\u7684\u8eab\u540e\uff0c\u8ddf\u7740\u4e24\u4e2a\u9ed1\u8863\u5927\u6c49\u3002 \u3000\u3000\u4e24\u4e2a\u5927\u6c49\u62ac\u7740\u4e00\u5ea7\u8f6f\u69bb\u3002\u8f6f\u69bb\u4e0a\u8eba\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u597d\u50cf\u6709\u75c5\uff0c\u76d6\u7740\u4e00\u5e8a\u88ab\u5b50\uff0c\u8fde\u5934\u90fd\u8499\u5728\u88ab\u5b50\u4e2d\u3002 \u3000\u3000\u6021\u7ea2\u542f\u52a8\u6a31\u5507\uff0c\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u4f4f\u624b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5168\u529b\u6325\u51fa\u4e00\u5251\uff0c\u4e00\u963b\u8ffd\u98ce\u53df\u7684\u653b\u52bf\uff0c\u5411\u540e\u8dc3\u9000\u4e03\u5c3a\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u76ee\u5149\u4e00\u63a0\u5c60\u65e0\u65b9\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff1f\u8fde\u5987\u4eba\u3001\u5b7a\u5b50\u4e5f\u8981\u51fa\u52a8\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u524d\u8f88\u662f\u4ec0\u4e48\u4eba\uff0c\u600e\u4e48\u8bf4\u8bdd\u5982\u6b64\u65e0\u793c\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u8001\u592b\u8ffd\u98ce\u53df\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u5f88\u6709\u540d\u6c14\u7684\u5927\u4eba\u7269\uff0c\u4f46\u8bf4\u8bdd\uff0c\u5374\u4f3c\u662f\u4e00\u4e2a\u4e0d\u89e3\u793c\u4eea\u7684\u7c97\u4eba\u3002\u201d \u3000\u3000\u9a82\u7684\u65f6\u673a\u6070\u5f53\uff0c\u800c\u4e14\u5c16\u9178\u3001\u523b\u8584\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u4e00\u5f20\u8001\u8138\uff0c\u7acb\u523b\u7ea2\u5230\u4e86\u8033\u6839\u540e\u9762\u3002 \u3000\u3000\u6021\u7ea2\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8ffd\u98ce\u8001\u524d\u8f88\u5f88\u770b\u4e0d\u8d77\u6211\u4eec\u8fd9\u5987\u9053\u4eba\u5bb6\u4e86\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u79c1\u5ba4\u5e8a\u7b2b\u4e4b\u95f4\uff0c\u8fd9\u6021\u7ea2\u6709\u7740\u65e0\u6bd4\u7684\u6e29\u67d4\uff0c\u60f3\u4e0d\u5230\u7adf\u7136\u662f\u5982\u6b64\u7684\u5229\u53e3\u5982\u5200\u3002\u201d \u3000\u3000\u53ea\u542c\u8ffd\u98ce\u53df\u5927\u58f0\u543c\u9053\uff1a\u201c\u4f60\u8fd9\u6bdb\u4e2b\u5934\uff0c\u4e0d\u770b\u4f60\u662f\u4e2a\u5973\u5a03\u513f\uff0c\u8001\u592b\u5c31\u4e00\u638c\u5288\u4e86\u4f60\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u6211\u53ea\u4e0d\u8fc7\u662f\u4e00\u4e2a\u4e2b\u5934\uff0c\u4e0d\u914d\u548c\u4f60\u8fd9\u4e2a\u5927\u540d\u9f0e\u9f0e\u7684\u4eba\u7269\u52a8\u624b\uff0c\u90a3\u5c31\u53ea\u597d\u8bf7\u522b\u4eba\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u53f3\u624b\u8f7b\u8f7b\u4e00\u6325\u3002 \u3000\u3000\u4e24\u4e2a\u62ac\u7740\u8f6f\u69bb\u7684\u5927\u6c49\uff0c\u7a81\u7136\u5feb\u6b65\u5411\u524d\uff0c\u628a\u62fe\u7684\u8f6f\u69bb\uff0c\u8fde\u540c\u69bb\u4e0a\u7684\u4eba\uff0c\u4e00\u8d77\u653e\u4e0b\u3002 \u3000\u3000\u90a3\u8f6f\u69bb\u5c31\u5728\u8ffd\u98ce\u53df\u7684\u9762\u524d\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u8ffd\u98ce\u53df\u6123\u4f4f\u4e86\uff0c\u671b\u4e86\u90a3\u8f6f\u69bb\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4eba\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u662f\u6b7b\u4eba\u8fd8\u662f\u6d3b\u4eba\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6d3b\u4eba\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u65e2\u7136\u8fd8\u6d3b\u7740\uff0c\u90a3\u5c31\u5feb\u4e9b\u7ed9\u6211\u6eda\u8fdc\u4e00\u4e9b\uff0c\u60f9\u8d77\u8001\u592b\u6012\u706b\uff0c\u4e00\u6837\u5288\u4e86\u4f60\u3002\u201d \u3000\u3000\u8f6f\u69bb\u4e0a\u8eba\u7740\u7684\u4eba\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u5927\u7684\u53e3\u6c14\uff0c\u6211\u8001\u4eba\u5bb6\u75c5\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u5c31\u662f\u54bd\u4e0d\u4e00\u4e0b\u6700\u540e\u4e00\u53e3\u6c14\uff0c\u4f60\u8001\u5c0f\u5b50\uff0c\u4f55\u4e0d\u5288\u51fa\u4e00\u638c\u8bd5\u8bd5\uff1f\u201d \u3000\u3000\u8ffd\u98ce\u53df\u6c14\u5f97\u5168\u8eab\u76f4\u6296\uff0c\u6012\u9053\uff1a\u201c\u597d\uff01\u597d\uff01\u4f60\u53ef\u8ba4\u4e3a\u8001\u592b\u4e0d\u6562\u3002\u201d \u3000\u3000\u5ffd\u7136\u626c\u624b\u4e00\u638c\uff0c\u5288\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u542b\u6124\u51fa\u624b\uff0c\u51dd\u805a\u4e86\u5168\u8eab\u529f\u529b\uff0c\u638c\u52bf\u7684\u5947\u5389\u5a01\u731b\uff0c\u76f4\u5982\u6392\u5c71\u5012\u6d77\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u4e00\u638c\uff0c\u53ef\u4ee5\u4f7f\u5341\u4e2a\u5e74\u8f7b\u5c0f\u4f19\u5b50\u8054\u624b\u96be\u652f\uff0c\u4f46\u5374\u6ca1\u6709\u6253\u5012\u75c5\u69bb\u4e0a\u75c5\u4eba\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u8fde\u75c5\u69bb\u4e5f\u6ca1\u6709\u78b0\u5230\u3002 \u3000\u3000\u539f\u6765\uff0c\u8ffd\u98ce\u53df\u638c\u52bf\u62cd\u51fa\u7684\u540c\u65f6\uff0c\u90a3\u8f6f\u69bb\u7a81\u7136\u98de\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5355\u662f\u8fd9\u4e00\u624b\uff0c\u5df2\u7ecf\u4f7f\u4eba\u60ca\u9a87\u4e0d\u5df2\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u8eba\u7740\u4e0d\u52a8\u98de\u8d77\u6765\uff0c\u5df2\u7ecf\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\uff0c\u518d\u8981\u628a\u4e00\u5f20\u8f6f\u69bb\u62d6\u8d77\u6765\uff0c\u5b9e\u5728\u662f\u56f0\u96be\u4e07\u5206\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u81ea\u89c9\u5c31\u65e0\u6cd5\u529e\u5230\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u626c\u8d77\u4e86\u638c\u52bf\uff0c\u5c31\u672a\u518d\u5288\u51fa\u3002 \u3000\u3000\u4ed6\u88ab\u5bf9\u65b9\u7684\u6b66\u529f\u9707\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e07\u5bb6\u5927\u9662\u95e8\u5916\uff0c\u53c8\u884c\u8fdb\u6765\u516d\u4e2a\u9ed1\u8863\u8499\u9762\u4eba\u3002 \u3000\u3000\u8fd9\u516d\u4e2a\u4eba\uff0c\u624b\u4e2d\u90fd\u62ff\u7740\u5175\u5203\uff0c\u4e00\u8272\u7684\u957f\u5251\u3002 \u3000\u3000\u8fd9\u516d\u4eba\u4e00\u5165\u573a\u4e2d\uff0c\u7acb\u523b\u6325\u5251\u63a9\u6740\u8fc7\u6765\u3002 \u3000\u3000\u6021\u7ea2\u8f7b\u58f0\u559d\u9053\uff1a\u201c\u90fd\u7ed9\u6211\u9000\u4e0b\u6765\u3002\u201d \u3000\u3000\u5305\u62ec\u53f6\u957f\u9752\u548c\u5c60\u65e0\u65b9\u5728\u5185\uff0c\u90fd\u51c6\u5907\u6325\u5251\u8fce\u4e0a\u53bb\uff0c\u542c\u5f97\u6021\u7ea2\u4e00\u53eb\uff0c\u7a81\u7136\u5411\u540e\u9000\u53bb\u3002 \u3000\u3000\u6070\u7ea2\u7a81\u7136\u8f6c\u52a8\u5a07\u8eaf\uff0c\u53cc\u624b\u4e00\u626c\u3002 \u3000\u3000\u516d\u4e2a\u9ed1\u8863\u6267\u5251\u4eba\uff0c\u7a81\u7136\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fae\u5fae\u2014\u6014\uff0c\u6697\u9053\uff1a\u597d\u6076\u6bd2\u7684\u6697\u5668\uff0c\u65e0\u58f0\u65e0\u606f\uff0c\u89c1\u8840\u5c01\u5589\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u597d\u72e0\u7684\u4e2b\u5934\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u5bf9\u4e8e\u5e94\u8be5\u5c0a\u656c\u7684\u4eba\uff0c\u6211\u5341\u5206\u5c0a\u656c\uff0c\u4f46\u8be5\u6740\u7684\u4eba\uff0c\u6211\u4e5f\u4e0d\u4f1a\u653e\u8fc7\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u4f60\u7528\u7684\u4ec0\u4e48\u6697\u5668\u6740\u4e86\u4ed6\u4eec\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u4e0d\u4f1a\u770b\u5417\uff1f\u201d \u3000\u3000\u8ffd\u98ce\u53df\u4e3a\u4e4b\u8bed\u585e\u3002 \u3000\u3000\u4ed6\u867d\u7136\u76ee\u5149\u8fc7\u4eba\uff0c\u4e5f\u53ea\u770b\u5230\u4e00\u7247\u94f6\u8292\uff0c\u4e00\u95ea\u800c\u6ca1\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u7a81\u7136\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u8ffd\u98ce\u53df\uff0c\u4f60\u53ef\u4ee5\u53bb\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u63a5\u9053\uff1a\u201c\u4e3a\u4e86\u4fdd\u62a4\u5723\u6c34\uff0c\u6211\u4eec\u4ec0\u4e48\u624b\u6bb5\u90fd\u7528\u5f97\u51fa\u6765\uff0c\u5982\u662f\u8fd8\u6709\u4eba\u4e0d\u4fe1\uff0c\u4e0d\u59a8\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u7a81\u7136\u4e00\u8e29\u811a\uff0c\u9053\uff1a\u201c\u662f\u4f60\uff0c\u8001\u75c5\u9b3c\uff0c\u8fd8\u4e0d\u7ad9\u8d77\uff0c\u76d6\u7740\u88ab\u5b50\uff0c\u88c5\u4ec0\u4e48\u72d7\u718a\u3002\u201d \u3000\u3000\u54ea\u77e5\u8eba\u5728\u8f6f\u69bb\u4e0a\u7684\u4eba\uff0c\u65e2\u4e0d\u56de\u7b54\uff0c\u4e5f\u6ca1\u6709\u7ad9\u8d77\u6765\uff0c\u597d\u50cf\u6839\u672c\u5c31\u6ca1\u628a\u8ffd\u98ce\u53df\u7684\u8bdd\u542c\u5728\u8033\u4e2d\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u987f\u65f6\u89c9\u5f97\u6709\u7740\u65e0\u6cd5\u4e0b\u53f0\u7684\u611f\u89c9\uff0c\u6697\u4e2d\u63d0\u805a\u771f\u6c14\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8001\u75c5\u9b3c\uff0c\u4f60\u8fd9\u4e48\u88c5\u7cca\u6d82\uff0c\u522b\u602a\u6211\u51fa\u624b\u65e0\u60c5\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\u4e00\u626c\u624b\uff0c\u5288\u7a7a\u638c\u529b\uff0c\u75be\u51fb\u800c\u51fa\u3002 \u3000\u3000\u4e00\u80a1\u5f3a\u5927\u7684\u6697\u52b2\u76f4\u6d8c\u8fc7\u53bb\u3002 \u3000\u3000\u4e00\u53ea\u624b\uff0c\u7a81\u7136\u7531\u8986\u76d6\u7684\u68c9\u88ab\u4e2d\u4f38\u4e86\u51fa\u6765\uff0c\u63a5\u4e0b\u4e86\u8ffd\u98ce\u53df\u51fb\u51fa\u7684\u6697\u52b2\uff0c\u9053\uff1a\u201c\u8001\u5c0f\u5b50\uff0c\u4e0d\u9519\u554a\uff01\u5288\u7a7a\u638c\uff0c\u53c8\u52a0\u51e0\u5206\u706b\u5019\u3002\u201d \u3000\u3000\u4e00\u4e2a\u6ee1\u9762\u75c5\u5bb9\u7684\u8001\u4eba\uff0c\u5ffd\u7136\u7531\u8f6f\u69bb\u4e0a\u5750\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u679c\u7136\u662f\u4f60\uff0c\u8001\u75c5\u9b3c\uff0c\u4f60\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u90a3\u4eba\u7406\u4e00\u7406\u5934\u4e0a\u7684\u4e71\u53d1\uff0c\u7b11\u9053\uff1a\u201c\u6b66\u5144\u4e0d\u6b7b\uff0c\u5144\u5f1f\u600e\u6562\u5148\u6b7b\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u7136\u6ca1\u6709\u6b7b\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u627e\u4e00\u4e2a\u6e05\u9759\u5730\u65b9\uff0c\u597d\u597d\u517b\u75c5\uff0c\u7adf\u7136\u6295\u5165\u4e86\u9634\u9633\u5821\u4e2d\u3002\u201d \u3000\u3000\u8f6f\u69bb\u8001\u4eba\u7a81\u7136\u4e00\u6b63\u75c5\u5bb9\uff0c\u9053\uff1a\u201c\u8001\u6b66\uff0c\u54b1\u4eec\u51e0\u5341\u5e74\u4e0d\u89c1\u4e86\uff0c\u4f60\u8fd8\u80fd\u4e00\u4e0b\u5b50\u8ba4\u51fa\u6211\u8001\u75c5\u9b3c\uff0c\u8db3\u89c1\u4f60\u5bf9\u6211\u8fd8\u6709\u4e00\u4efd\u6000\u5ff5\u4ea4\u60c5\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u63a5\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u6b7b\u4e86\uff0c\u6211\u4e5f\u80fd\u4e00\u773c\u8ba4\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u8f6f\u69bb\u8001\u4eba\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u8981\u529d\u4f60\u51e0\u53e5\u8bdd\uff0c\u54b1\u4eec\u90fd\u4e00\u5927\u628a\u5e74\u7eaa\u4e86\uff0c\u72af\u4e0d\u7740\u62fc\u8001\u547d\uff0c\u73b0\u5728\uff0c\u53c8\u4e0d\u662f\u54b1\u4eec\u8001\u54e5\u4fe9\u676f\u9152\u53d9\u65e7\u7684\u65f6\u523b\uff0c\u542c\u6211\u4e00\u53e5\u8bdd\uff0c\u65e9\u4e9b\u79bb\u53bb\u5427\uff01\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u4f60\u53c8\u4e3a\u4ec0\u4e48\u4e0d\u8d70\uff01\u201d \u3000\u3000\u8f6f\u69bb\u8001\u4eba\u9053\uff1a\u201c\u6211\u8001\u4eba\u5bb6\u4e00\u8eab\u75c5\uff0c\u884c\u52a8\u4e0d\u4fbf\uff0c\u5982\u4f55\u80fd\u8d70\u5f97\u4e86\uff0c\u518d\u8bf4\u8fd9\u5730\u65b9\u6b63\u662f\u6211\u517b\u75c5\u4e4b\u5904\uff0c\u4f60\u8bf4\u8981\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u4f60\u65e2\u7136\u4e0d\u80fd\uff0c\u4e3a\u4ec0\u4e48\u8981\u6211\u8d70\u3002\u201d \u3000\u3000\u8f6f\u69bb\u8001\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u6b66\u5144\uff0c\u4f60\u867d\u7136\u5e74\u7eaa\u5927\u4e00\u4e9b\uff0c\u4f46\u603b\u4e0d\u81f3\u4e8e\u8001\u7cca\u6d82\u5427\uff01\u4f60\u4eec\u6765\u4e86\u4e0d\u5c11\u7684\u4eba\uff0c\u5374\u8981\u4f60\u4e00\u4e2a\u4eba\u51fa\u9762\uff0c\u522b\u4eba\u90fd\u6682\u65f6\u8eb2\u7740\u4e0d\u51fa\u6765\u2026\u2026\u201d \u3000\u3000\u8ffd\u98ce\u53df\u63a5\u9053\uff1a\u201c\u8c01\u8bf4\u7684\uff0c\u4f60\u4eec\u4e0d\u662f\u5df2\u7ecf\u6740\u4e86\u5f88\u591a\u4eba\u4e86\u5417\uff1f\u201d \u3000\u3000\u8f6f\u69bb\u8001\u4eba\u53f9\u606f\u4e00\u58f0\uff0c\u906d\uff1a\u201c\u6b66\u5144\u6267\u8ff7\u4e0d\u609f\uff0c\u5144\u5f1f\u4e5f\u5b9e\u5728\u6ca1\u6709\u6cd5\u5b50\u4e86\uff0c\u4e0d\u8fc7\uff0c\u4f60\u8981\u77e5\u9053\u4f60\u5982\u4e00\u5b9a\u4e0d\u8d70\uff0c\u4eca\u665a\u4e0a\uff0c\u54b1\u4eec\u8001\u54e5\u4fe9\uff0c\u5c31\u8981\u6709\u4e00\u4e2a\u4eba\u8eba\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u8001\u75c5\u9b3c\uff0c\u521a\u624d\u4f60\u70ab\u9732\u4e86\u4e00\u624b\u8f7b\u529f\uff0c\u4e5f\u548c\u6211\u5bf9\u4e86\u4e00\u638c\uff0c\u4f60\u8001\u75c5\u9b3c\u786e\u6709\u4e9b\u8fdb\u6b65\uff0c\u4e0d\u8fc7\uff0c\u771f\u8981\u62fc\u8d77\u6765\uff0c\u9e7f\u6b7b\u8c01\u624b\uff0c\u8fd8\u5f88\u96be\u8bf4\u3002\u201d \u3000\u3000\u8f6f\u69bb\u8001\u4eba\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b66\u5144\uff0c\u4f60\u771f\u7684\u8981\u60f3\u62fc\u4e00\u573a\u5417\uff1f\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u8fd9\u8981\u770b\u4f60\u4e86\uff0c\u8001\u592b\u65e2\u7136\u6765\u4e86\uff0c\u603b\u8981\u5bf9\u4eba\u5bb6\u6709\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u6021\u7ea2\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u66ae\u9f13\u6668\u949f\uff0c\u60ca\u4e0d\u9192\u4ed6\u5357\u67ef\u4e4b\u68a6\uff0c\u4f60\u5df2\u7ecf\u5c3d\u4e86\u5fc3\u529b\uff0c\u4e0d\u7528\u518d\u62b1\u6127\u81ea\u759a\u4e86\u3002\u201d \u3000\u3000\u8f6f\u69bb\u8001\u4eba\u70b9\u70b9\u5934\uff0c\u7f13\u7f13\u4e0b\u4e86\u8f6f\u69bb\uff0c\u5411\u8ffd\u98ce\u53df\u884c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u8d70\u5f97\u5f88\u6162\u3001\u5f88\u6162\uff0c\u6bcf\u4e00\u6b65\u53ea\u79fb\u52a8\u534a\u5c3a\u5de6\u53f3\uff0c\u6d3b\u4f3c\u4e00\u4e2a\u75c5\u69bb\u4e0a\u6298\u78e8\u4e86\u5f88\u4e45\u7684\u4eba\uff0c\u4f53\u529b\u52c9\u5f3a\u8d1f\u8377\u7740\u8eab\u91cd\uff0c\u8d70\u5f97\u662f\u90a3\u6837\u8270\u82e6\u3002 \u3000\u3000\u4f46\u4ed6\u67af\u7626\u72b9\u5e26\u75c5\u5bb9\u7684\u8138\u4e0a\uff0c\u5374\u662f\u4e00\u7247\u4e25\u8083\u3002 \u3000\u3000\u4e24\u9053\u70af\u70af\u7684\u76ee\u5149\uff0c\u66b4\u5c04\u51fa\u51b7\u5389\u7684\u5bd2\u8292\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u5ffd\u7136\u95f4\u5185\u5fc3\u5e95\u751f\u51fa\u4e86\u4e00\u80a1\u5bd2\u610f\uff0c\u9053\uff1a\u201c\u8001\u75c5\u9b3c\uff0c\u4f60\u771f\u8981\u62fc\u547d\u5417\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u53f6\u957f\u9752\u6084\u7136\u884c\u5230\u4e86\u5c60\u65e0\u65b9\u7684\u8eab\u4fa7\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c60\u5144\uff0c\u8fd9\u4e00\u8138\u75c5\u5bb9\u7684\u8001\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u75c5\u53df\u5468\u7eb6\u3002\u201d \u3000\u3000\u706f\u5149\u4e0b\uff0c\u53ea\u89c1\u4ed6\u8138\u8272\u82cd\u767d\uff0c\u914d\u7740\u4e00\u8eab\u5d99\u5ccb\u7626\u9aa8\uff0c\u7ed9\u4eba\u4e00\u79cd\u652f\u79bb\u4eba\u6837\u7684\u611f\u89c9\u3002 \u3000\u3000\u4f46\u76ee\u5149\u5982\u7535\uff0c\u51b7\u8083\u7684\u795e\u60c5\u4e2d\uff0c\u6563\u53d1\u7740\u6d53\u70c8\u7684\u6740\u6c14\uff0c\u6bcf\u5411\u524d\u884c\u4e00\u6b65\uff0c\u8fd9\u6740\u6c14\u5c31\u589e\u5f3a\u4e86\u51e0\u5206\u3002 \u3000\u3000\u53ea\u542c\u75c5\u53df\u5468\u7eb6\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b66\u5144\uff0c\u597d\u53cb\u7ffb\u76ee\uff0c\u751f\u6b7b\u4e00\u6218\uff0c\u8fd9\u5927\u6982\u662f\u4eba\u751f\u6700\u5927\u7684\u60e8\u4e8b\u4e4b\u4e00\uff0c\u6b66\u5144\u6267\u8457\u5df1\u89c1\u4e0d\u80af\u79bb\u53bb\uff0c\u54b1\u4eec\u8001\u54e5\u4fe9\uff0c\u4eca\u591c\u5fc5\u6709\u4e00\u4e2a\u4eba\u8981\u6e85\u8840\u6a2a\u5c38\u4e8e\u4e07\u5bb6\u5927\u9662\u4e86\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u5438\u4e00\u53e3\u6c14\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u6b7b\u7684\u672a\u5fc5\u5c31\u662f\u6211\u6b66\u5fd7\u8fdc\uff1f\u201d \u3000\u3000\u5468\u7eb6\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u7ba1\u662f\u6b66\u5144\u6740\u4e86\u5144\u5f1f\uff0c\u6216\u662f\u5144\u5f1f\u4f24\u4e86\u6b66\u5144\uff0c\u90fd\u662f\u4eba\u95f4\u60e8\u4e8b\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u4f60\u65e2\u77e5\u5982\u6b64\uff0c\u53c8\u4f55\u5fc5\u4e00\u5b9a\u8981\u51fa\u624b\u5462\uff1f\u201d \u3000\u3000\u75c5\u53df\u5468\u7eb6\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b66\u5144\uff0c\u522b\u5fd8\u4e86\uff0c\u662f\u4f60\u5e26\u5165\u4fb5\u72af\u4e07\u5bb6\u5927\u9662\uff0c\u5144\u5f1f\u5374\u6b63\u5728\u8fd9\u91cc\u517b\u75c5\uff1a\u201d \u3000\u3000\u4ed6\u8d70\u5f97\u867d\u7136\u5f88\u6162\uff0c\u4f46\u53cc\u65b9\u7684\u8ddd\u79bb\u5f88\u8fd1\uff0c\u8bf4\u5b8c\u4e86\u51e0\u53e5\u8bdd\uff0c\u5f7c\u6b64\u5df2\u7ecf\u5bf9\u9762\u800c\u7acb\u4e86\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u6b66\u5fd7\u8fdc\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8001\u75c5\u9b3c\uff0c\u4f60\u53ef\u4ee5\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u6b66\u5144\u662f\u5ba2\u4eba\uff0c\u8fd8\u662f\u8bf7\u6b66\u5144\u5148\u51fa\u624b\u5427\uff01\u201d \u3000\u3000\u4e8b\u60c5\u6324\u4e0a\u4e86\u5934\uff0c\u8ffd\u98ce\u53df\u5c31\u7b97\u4e0d\u60f3\u52a8\u624b\uff0c\u4e5f\u5df2\u7ecf\u6ca1\u6709\u6cd5\u5b50\u4e0b\u53f0\u3002 \u3000\u3000\u65e2\u662f\u8fd9\u573a\u67b6\u975e\u6253\u4e0d\u53ef\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u62a2\u5148\u673a\uff0e\u5f53\u4e0b\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u5468\u5144\u5982\u6b64\u76f8\u8ba9\uff0c\u5728\u4e0b\u5c31\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d \u3000\u3000\u5ffd\u7136\u626c\u624b\u4e00\u638c\uff0c\u5288\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u867d\u7136\u968f\u968f\u4fbf\u4fbf\u7684\u5288\u51fa\u4e86\u4e00\u638c\uff0c\u4f46\u5f3a\u731b\u7684\u638c\u529b\uff0c\u631f\u8d77\u4e86\u4e00\u80a1\u5f3a\u70c8\u7684\u5578\u98ce\u4e4b\u58f0\u3002 \u3000\u3000\u597d\u51cc\u5389\u7684\u4e00\u638c\u3002 \u3000\u3000\u75c5\u53df\u5468\u7eb6\u7adf\u7136\u4e0d\u95ea\u4e0d\u907f\uff0c\u53f3\u624b\u4e00\u62ac\uff0c\u786c\u78b0\u786c\u7684\u63a5\u4e86\u4e00\u638c\u3002 \u3000\u3000\u7830\u7136\u2014\u58f0\uff0c\u53cc\u638c\u63a5\u5b9e\u3002 \u3000\u3000\u8868\u9762\u4e0a\u770b\u53bb\uff0c\u4e24\u4e2a\u4eba\u90fd\u7eb9\u98ce\u672a\u52a8\uff0c\u662f\u4e00\u4e2a\u5e73\u5206\u79cb\u8272\u4e4b\u5c40\u3002 \u3000\u3000\u4f46\u8ffd\u98ce\u53df\u5374\u611f\u89c9\u5230\u5bf9\u65b9\u638c\u52bf\u7684\u53cd\u5f39\u4e4b\u529b\uff0c\u9707\u5f97\u624b\u81c2\u5fae\u5fae\u9ebb\u6728\u3002 \u3000\u3000\u53ea\u542c\u5468\u7eb6\u8bf4\u9053\uff1a\u201c\u6b66\u5144\u4e5f\u63a5\u5144\u5f1f\u4e00\u638c\u3002\u201d \u3000\u3000\u53cc\u638c\u8fde\u73af\uff0c\u5148\u540e\u5288\u51fa\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u81ea\u662f\u4e0d\u7518\u793a\u5f31\uff0c\u5de6\u53f3\u53cc\u624b\uff0c\u8fde\u73af\u8fce\u51fb\u3002 \u3000\u3000\u7830\u7830\u4e24\u58f0\uff0c\u53c8\u662f\u4e24\u638c\u78b0\u62fc\u3002 \u3000\u3000\u638c\u52bf\u63a5\u5b9e\uff0c\u5f3a\u731b\u7684\u52b2\u98ce\uff0c\u56db\u4e0b\u6ea2\u8361\u3002 \u3000\u3000\u53f6\u957f\u9752\u4f4e\u58f0\u9053\uff1a\u201c\u5c60\u5144\uff0c\u8fd9\u4e09\u62db\u786c\u62fc\uff0c\u62db\u62db\u529b\u903e\u5343\u65a4\uff0c\u770b\u6837\u5b50\uff0c\u5144\u5f1f\u4e00\u638c\u4e5f\u63a5\u4e0d\u4e0b\u6765\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u4e0a\u4e00\u4ee3\u6c5f\u6e56\u4e0a\u6210\u540d\u7684\u6709\u6570\u9ad8\u624b\uff0c\u5728\u529f\u529b\u4e0a\uff0c\u81ea\u975e\u54b1\u4eec\u80fd\u671b\u5176\u9879\u80cc\u3002\u201d \u3000\u3000\u53ea\u89c1\u8ffd\u98ce\u53df\u7a81\u7136\u5411\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u5f20\u5634\u5410\u51fa\u4e00\u53e3\u9c9c\u8840\u3002 \u3000\u3000\u4e54\u4e00\u9f99\u4f38\u624b\u6276\u7740\u4e86\u8ffd\u98ce\u53df\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4f24\u52bf\u5982\u4f55\uff1f\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u9707\u52a8\u4e86\u5185\u8151\u3002\u201d \u3000\u3000\u75c5\u53df\u5468\u7eb6\u8f7b\u8f7b\u5618\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6b66\u5144\uff0c\u627f\u8ba9\u4e86\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u7a81\u7136\u63a8\u5f00\u4e86\u4e54\u4e00\u9f99\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8001\u75c5\u9b3c\uff0c\u6211\u867d\u7136\u5185\u8151\u53d7\u4f24\uff0c\u4f46\u8fd8\u6709\u518d\u6218\u4e4b\u80fd\u3002\u201d \u3000\u3000\u5927\u6b65\u5411\u524d\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5468\u7eb6\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6b66\u5144\uff0c\u54b1\u4eec\u51e0\u5341\u5e74\u7684\u670b\u53cb\uff0c\u96be\u9053\u4e00\u5b9a\u8981\u628a\u8001\u547d\u62fc\u4e86\u5417\uff1f\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4eca\u5929\u4e0d\u662f\u4f60\u6b7b\uff0c\u5c31\u662f\u6211\u4ea1\u3002\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u6b66\u5144\uff0c\u54b1\u4eec\u4e24\u4e2a\u62fc\u4e86\uff0c\u672a\u5fc5\u5c31\u4f1a\u628a\u95ee\u9898\u89e3\u51b3\u4e86\uff0c\u4f60\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u6211\u4eec\u6b7b\u4e86\uff0c\u81ea\u7136\u65e0\u6cd5\u518d\u7ba1\u8fd9\u4e9b\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u8001\u6b66\uff0c\u4f60\u5012\u8bf4\u8bf4\u770b\uff0c\u54b1\u4eec\u65e2\u7136\u89e3\u51b3\u4e0d\u4e86\u4e8b\u60c5\uff0c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u62fc\u4e2a\u6b7b\u6d3b\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u53ef\u4ee5\u843d\u4e00\u4e2a\u773c\u4e0d\u89c1\uff0c\u5fc3\u4e0d\u70e6\u3002\u201d \u3000\u3000\u5468\u7eb6\u8138\u4e0a\u6709\u4e86\u6012\u5bb9\uff0c\u9053\uff1a\u201c\u8001\u6b66\uff0c\u4f60\u8fd9\u662f\u65e0\u7406\u53d6\u95f9\u4e86\uff0c\u5bf9\u65b9\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff0c\u548c\u4f60\u6709\u591a\u539a\u7684\u4ea4\u60c5\uff0c\u4f60\u7adf\u7136\u4e0d\u60dc\u4e00\u6218\uff0c\u786c\u8981\u548c\u8001\u670b\u53cb\u62fc\u547d\uff1f\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u627e\u6211\u7684\u4eba\uff0c\u4e0d\u662f\u6211\u5f88\u597d\u7684\u670b\u53cb\uff0c\u4e5f\u548c\u6211\u6ca1\u6709\u4ec0\u4e48\u4ea4\u60c5\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e0d\u5e0c\u671b\u770b\u7740\u9634\u9633\u5821\u62e5\u6709\u90a3\u4e48\u591a\u7684\u5723\u6c34\u3002\u201d \u3000\u3000\u75c5\u53df\u5468\u7eb6\u9053\uff1a\u201c\u5723\u6c34\u843d\u5165\u4e86\u9634\u9633\u5821\uff0c\u548c\u843d\u4eba\u522b\u5904\uff0c\u6709\u4ec0\u4e48\u4e0d\u540c\uff0c\u518d\u8bf4\uff0c\u5723\u6c34\u4ea4\u6613\u53c8\u975e\u81ea\u4eca\u5e74\u59cb\uff0c\u4f60\u5982\u8ffd\u67e5\u5723\u6c34\uff0c\u5c31\u8be5\u60f3\u6cd5\u5b50\u963b\u6b62\u8fd9\u79cd\u4ea4\u6613\uff0c\u4f46\u4f60\u820d\u672c\u9010\u672b\uff0c\u5374\u627e\u9634\u9633\u5821\u7684\u9ebb\u70e6\uff0c\u5355\u662f\u8fd9\u4e00\u6869\uff0c\u5c31\u4e0d\u8db3\u8a00\u516c\u5e73\u4e8c\u5b57\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fc7\u53bb\u6709\u5723\u6c34\u4ea4\u6613\uff0e\u4f46\u6211\u4e0d\u77e5\u9053\uff0c\u6211\u5f97\u5230\u6d88\u606f\uff0c\u5306\u5306\u8d76\u6765\uff0c\u5c31\u662f\u60f3\u963b\u6b62\u8fd9\u4e00\u6b21\u7684\u5723\u6c34\u4ea4\u6613\uff0c\u60f3\u4e0d\u5230\uff0c\u6211\u7adf\u7136\u665a\u5230\u4e86\u4e00\u6b65\uff0c\u5723\u6c34\u4ea4\u6613\uff0c\u5df2\u7ecf\u5b8c\u6210\u4e86\uff0c\u5723\u6c34\u5df2\u7136\u843d\u5165\u4e86\u4f60\u4eec\u624b\u4e2d\u3002\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u5c31\u8981\u8ffd\u6211\u4eec\u4ea4\u51fa\u5723\u6c34\u4e86\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u8001\u75c5\u9b3c\uff0c\u4f60\u77e5\u9053\u9634\u9633\u5821\u662f\u4e2a\u4ec0\u4e48\u6837\u5b50\u7684\u7ec4\u7ec7\u5417\uff1f\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u5b50\u7684\u7ec4\u7ec7\uff1f\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u4e00\u4e2a\u9634\u9669\u3001\u6bd2\u8fa3\u7684\u9ed1\u9053\u7ec4\u7ec7\uff0c\u8fd9\u6279\u5723\u6c34\uff0c\u5982\u82e5\u843d\u5728\u4e86\u4ed6\u4eec\u7684\u624b\u4e2d\uff0c\u6574\u4e2a\u6c5f\u6e56\u90fd\u4f1a\u53d7\u5230\u5f88\u5927\u7684\u4f24\u5bb3\uff0c\u8001\u592b\u4e2a\u4eba\u7684\u751f\u6b7b\uff0c\u53c8\u7b97\u5f97\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u6b66\u5144\uff0c\u4f60\u4e5f\u5728\u6c5f\u6e56\u4e0a\u8d70\u4e86\u51e0\u5341\u5e74\uff0c\u60f3\u4e0d\u5230\uff0c\u4f60\u7adf\u7136\u5341\u5206\u7cca\u6d82\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u7cca\u6d82\u7684\u662f\u4f60\u8001\u75c5\u9b3c\uff0c\u4f60\u867d\u7136\u4e0d\u5e38\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u4f46\u58f0\u8a89\u5e76\u4e0d\u574f\uff0c\u6211\u5374\u60f3\u4e0d\u5230\uff0c\u4f60\u8001\u4e86\u4e4b\u540e\uff0c\u7adf\u7136\u662f\u5982\u6b64\u7684\u6ca1\u6709\u9aa8\u6c14\uff0c\u6295\u8eab\u5728\u9634\u9633\u5821\u4e2d\u3002\u201d \u3000\u3000\u5468\u7eb6\u6012\u9053\uff1a\u201c\u8001\u6b66\uff0c\u5982\u82e5\u9634\u9633\u5821\u5982\u4f60\u6240\u8a00\uff0c\u65e9\u5c31\u8981\u4e86\u4f60\u7684\u547d\u4e86\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u4e00\u9635\u54c8\u54c8\u5927\u7b11\u4e4b\u58f0\uff0e\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u6b66\u5144\uff0c\u4e0d\u8981\u4e3a\u4ed6\u4eec\u6311\u62e8\u6240\u4f24\u3002\u201d \u3000\u3000\u968f\u7740\u90a3\u8bf4\u8bdd\u4e4b\u58f0\uff0c\u4e00\u6761\u4eba\u5f71\uff0c\u75be\u98de\u800c\u81f3\uff0c\u98d8\u843d\u5728\u8ffd\u98ce\u53df\u7684\u8eab\u524d\u3002 \u3000\u3000\u6765\u4eba\u8eab\u7740\u9752\u8272\u957f\u886b\uff0c\u7559\u7740\u4e94\u7efa\u957f\u9aef\uff0c\u8eab\u6750\u7626\u957f\uff0c\u65b9\u9762\u5927\u8033\uff0c\u50cf\u8c8c\u5802\u5802\u3002 \u3000\u3000\u53f6\u957f\u9752\u53ea\u89c9\u6b64\u4eba\u5341\u5206\u9762\u5584\uff0c\u4f3c\u4e4e\u662f\u5728\u54ea\u91cc\u89c1\u8fc7\uff0c\u81f3\u5c11\uff0c\u4e5f\u542c\u4eba\u8bf4\u8fc7\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u90a3\u9752\u8863\u4eba\u56de\u987e\u4e86\u8ffd\u98ce\u53df\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6b66\u5144\uff0c\u4f60\u53d7\u4f24\u4e86\u3002\u201d \u3000\u3000\u4f38\u624b\u53d6\u51fa\u4e00\u7c92\u4e39\u4e38\uff0c\u63a5\u9053\uff1a\u201c\u541e\u4e0b\u53bb\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u5bf9\u8fd9\u9752\u8863\u4eba\uff0c\u4f3c\u4e4e\u662f\u5341\u5206\u656c\u91cd\uff0c\u4e5f\u4e0d\u95ee\u4ec0\u4e48\u836f\u7269\uff0c\u63a5\u7740\u5c31\u541e\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5468\u7eb6\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u9ad8\u5927\u4fa0\uff0c\u5931\u656c\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u957f\u9aef\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u75c5\u53df\u5468\u7eb6\uff0c\u5468\u5927\u4fa0\uff0c\u62b1\u75c5\u884c\u9053\u6c5f\u6e56\uff0c\u5728\u4e0b\u5fc3\u6155\u5df2\u7ecf\u5f88\u4e45\u4e86\uff0c\u4eca\u65e5\u6709\u5e78\u5f97\u6664\u3002\u201d \u3000\u3000\u5468\u7eb6\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\u671b\u91cd\u6c5f\u6e56\uff0c\u8001\u75c5\u9b3c\u4eca\u5929\u80fd\u5f97\u4e00\u53e5\u8d5e\u7f8e\uff0c\u5f53\u771f\u662f\u6b7b\u800c\u65e0\u61be\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c60\u5144\uff0c\u8fd9\u4f4d\u9752\u886b\u4eba\u53ef\u662f\u9ad8\u5929\u5065\u9ad8\u5927\u4fa0\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u4ed6\uff0c\u4e2d\u5dde\u7b2c\u4e00\u5200\u9ad8\u5929\u5065\uff0c\u4ed6\u662f\u6c5f\u6e56\u4e0a\u6700\u53d7\u4eba\u656c\u91cd\u7684\u5927\u4fa0\uff0c\u542c\u8bf4\uff0c\u6b66\u6797\u9053\u4e0a\u7684\u670b\u53cb\uff0c\u4e00\u7ecf\u9ad8\u5927\u4fa0\u7684\u54c1\u8bc4\uff0c\u7acb\u523b\u8eab\u4ef7\u767e\u500d\uff0c\u8fd9\u4e2a\u4eba\u5e7f\u5f97\u6c5f\u6e56\u540c\u9053\u4fe1\u4efb\uff0c\u636e\u8bf4\uff0c\u8fde\u5c11\u6797\u3001\u6b66\u5f53\u7684\u638c\u95e8\u4eba\uff0c\u90fd\u5bf9\u4ed6\u656c\u91cd\u5f02\u5e38\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u5618\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u6c5f\u6e56\u4e0a\u7684\u4e8b\uff0c\u4e0d\u53ef\u7eaf\u4ee5\u5916\u8c8c\u53d6\u820d\uff0c\u8fd9\u4f4d\u9ad8\u5927\u4fa0\u786e\u5b9e\u540d\u52a8\u56db\u6d77\uff0c\u4e00\u5ea6\u8fde\u6211\u4e5f\u5bf9\u4ed6\u751f\u51fa\u4e86\u6781\u5ea6\u7684\u656c\u4f69\u2026\u2026\u201d \u3000\u3000\u53f6\u5b50\u9752\u63a5\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u4eca\u665a\uff0c\u6211\u7adf\u7136\u4f1a\u5728\u8fd9\u91cc\u89c1\u5230\u4e86\u6211\u6700\u5fc3\u6155\u7684\u4eba\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u5821\u4e3b\u8fc7\u53bb\u4e5f\u5f88\u656c\u4f69\u9ad8\u5927\u4fa0\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u73b0\u5728\uff0c\u5821\u4e3b\u89c9\u7740\u4ed6\u5f88\u53ef\u7591\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u53ef\u7591\uff0c\u53ef\u7591\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53ef\u7591\u4ec0\u4e48\uff1f\u5728\u4e0b\u4e0d\u592a\u6e05\u695a\uff0c\u7b49\u65e5\u540e\u89c1\u7740\u5821\u4e3b\u65f6\uff0c\u4f60\u518d\u95ee\u95ee\uff0c\u4e0d\u8fc7\uff0c\u6211\u8981\u5949\u529d\u4f60\u8001\u5f1f\u4e00\u53e5\u6d3b\u3001\u6709\u4e9b\u4e8b\uff0c\u4e0d\u53ef\u62b1\u592a\u9ad8\u7684\u5e0c\u671b\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u5f88\u542b\u84c4\uff0c\u4f46\u53f6\u957f\u9752\u5374\u542c\u5f97\u5fc3\u4e2d\u4e00\u7247\u7d0a\u4e71\u3002 \u3000\u3000\u4ed6\u660e\u767d\u5c60\u65e0\u65b9\u8bed\u4e2d\u7684\u542b\u610f\uff0c\u4f46\u4ed6\u7edd\u5bf9\u4e0d\u613f\u76f8\u4fe1\u8fd9\u4e9b\u4e8b\u3002 \u3000\u3000\u53ea\u542c\u75c5\u53df\u5468\u7eb6\u8bf4\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\u591c\u6765\u4e07\u5bb6\u5927\u9662\uff0c\u4e0d\u77e5\u6709\u4f55\u6307\u6559\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u542c\u8bf4\uff0c\u8d35\u7ec4\u7ec7\u5f97\u5230\u4e86\u4e00\u6279\u5723\u6c34\uff1f\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5468\u5144\u53ef\u77e5\u9053\uff0c\u90a3\u5723\u6c34\u7684\u7528\u9014\u5417\uff1f\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u4e0d\u592a\u6e05\u695a\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u5c31\u96be\u602a\u4e86\uff0c\u5468\u5144\u5982\u662f\u77e5\u6653\u4e86\u90a3\u5723\u6c34\u7684\u7528\u9014\u5b9a\u7136\u4e0d\u4f1a\u90a3\u4e48\u5168\u5fc3\u4fdd\u62a4\u5b83\u4e86\u3002\u201d \u3000\u3000\u5468\u7eb6\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\u77e5\u6653\u90a3\u5723\u6c34\u7684\u7528\u9014\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u7565\u77e5\u4e00\u4e8c\u3002\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\uff0c\u53ef\u5426\u89c1\u793a\u5462\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5c31\u5728\u4e0b\u6240\u77e5\u9053\u7684\uff0c\u6240\u8c13\u5723\u6c34\uff0c\u53ea\u662f\u51fa\u81ea\u5730\u4e0b\u7684\u7279\u6b8a\u77ff\u7269\uff0c\u7ecf\u8fc7\u4e86\u4e00\u79cd\u7279\u6b8a\u7684\u79d8\u65b9\u8c03\u5236\u4e4b\u540e\uff0c\u53ef\u4ee5\u6210\u4e3a\u4e00\u79cd\u9510\u5229\u7684\u6740\u4eba\u6b66\u5668\uff0c\u5c31\u5728\u4e0b\u6240\u77e5\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u8fd8\u6ca1\u6709\u4e00\u79cd\u6b66\u529f\uff0c\u53ef\u4ee5\u963b\u6b62\u5b83\u3002\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\u662f\u8ffd\u53d6\u8fd9\u6279\u5723\u6c34\u800c\u6765\u4e86\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\uff0e\u53ef\u77e5\u9053\u5723\u6c34\u4ea4\u6613\uff0c\u5df2\u7ecf\u6709\u4e86\u5f88\u591a\u5e74\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u77e5\u9053\uff0c\u4e0d\u8fc7\u3001\u77e5\u9053\u7684\u665a\u4e86\u4e00\u4e9b\u3002\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u5723\u6c34\u4ea4\u6613\u6570\u5e74\uff0c\u5df2\u6709\u4e0d\u5c11\u6d41\u5165\u6c5f\u6e56\uff0c\u5c31\u7b97\u9ad8\u5927\u4fa0\u8ffd\u56de\u8fd9\u4e00\u6279\u5723\u6c34\uff0c\u53ea\u6015\u4e5f\u662f\u6ca1\u6709\u529e\u6cd5\u963b\u6b62\u5723\u6c34\u4e3a\u5bb3\u6c5f\u6e56\uff0c\u518d\u8bf4\uff0c\u9ad8\u5927\u4fa0\u5355\u5355\u53d6\u8d70\u6211\u4eec\u82b1\u4e86\u6570\u5341\u4e07\u94f6\u5b50\uff0c\u4e70\u5f97\u7684\u5723\u6c34\uff0c\u4e5f\u5f88\u96be\u4f7f\u4eba\u4fe1\u670d\uff0c\u4f17\u53e3\u94c4\u91d1\uff0c\u9ad8\u5927\u4fa0\u96be\u9053\u5c31\u4e0d\u6015\u6709\u4f24\u6e05\u8a89\u5417\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u9ad8\u67d0\u5982\u82e5\u4e0d\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u90a3\u4e5f\u662f\u6ca1\u6709\u6cd5\u5b50\u7684\u4e8b\u5b50\uff0c\u65e2\u7136\u8ba9\u6211\u77e5\u9053\u4e86\uff0c\u603b\u4e0d\u80fd\u591f\u5750\u89c6\u4e0d\u7406\uff0c\u5468\u5927\u4fa0\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u8a89\u4e00\u5411\u5f88\u597d\uff0c\u60f3\u6765\u8fd8\u4e0d\u81f3\u4e8e\u4e3a\u6b64\u4e8b\u548c\u9ad8\u67d0\u4f5c\u5bf9\u4e86\u3002\u201d \u3000\u3000\u8bed\u8bcd\u542c\u8d77\u6765\u5f88\u67d4\u548c\uff0c\u4f46\u8bdd\u4e2d\u7684\u542b\u610f\u5374\u662f\u6781\u5c3d\u51b7\u5389\uff0c\u90a3\u65e0\u7591\u662f\u8bf4\uff0c\u4f60\u5468\u7eb6\u5982\u662f\u8981\u548c\u9ad8\u67d0\u4eba\u4f5c\u5bf9\uff0c\u65e0\u7591\u5c31\u662f\u9634\u9633\u5821\u4e2d\u4e00\u4e18\u4e4b\u8c89\u3002 \u3000\u3000\u5728\u573a\u4e4b\u4eba\uff0c\u5fc3\u4e2d\u6ce2\u52a8\u6700\u5927\u7684\u81ea\u7136\u662f\u53f6\u957f\u9752\u4e86\uff0c\u8ffd\u98ce\u53df\u3001\u9ad8\u5929\u5065\u7684\u7a81\u7136\u51fa\u73b0\uff0c\u4f7f\u4ed6\u5bf9\u9634\u9633\u5821\u8fd9\u4e2a\u7ec4\u7ec7\u7684\u4fe1\u4efb\uff0c\u5f00\u59cb\u4e86\u52a8\u6447\uff0c\u9ad8\u5929\u5065\u5728\u6c5f\u6e56\u4e0a\u7684\u4fe1\u7528\u592a\u597d\u4e86\uff0c\u597d\u5230\u4ee3\u8868\u4e86\u6c5f\u6e56\u4e0a\u7684\u6b63\u4e49\u6807\u5e1c\uff0c\u53ea\u8981\u548c\u9ad8\u5929\u5065\u6709\u8fc7\u51b2\u7a81\u7684\u4eba\uff0c\u7edd\u5bf9\u4e0d\u662f\u597d\u4eba\u3002 \u3000\u3000\u9ad8\u5929\u5065\u4e00\u5411\u4e5f\u5f88\u73cd\u60dc\u4ed6\u7684\u91d1\u5b57\u62db\u724c\uff0c\u4ed6\u4ece\u4e0b\u8f7b\u6613\u51fa\u624b\uff0c\u4e5f\u5f88\u5c11\u5411\u4eba\u5174\u5e08\u95ee\u7f6a\u3002 \u3000\u3000\u4ed6\u4ece\u6765\u6ca1\u6709\u4e3a\u4e2a\u4eba\u7684\u6069\u6028\uff0c\u5411\u4eba\u5bfb\u8fc7\u4ec7\uff0c\u6bcf\u4e00\u6b21\uff0c\u90fd\u662f\u4e3a\u4e86\u6b66\u6797\u7684\u6b63\u4e49\u3002 \u3000\u3000\u795e\u79d8\u7684\u5723\u6c34\u4ea4\u6613\uff0c\u7ec8\u4e8e\u5f15\u8d77\u4e86\u4ed6\u7684\u5173\u5fc3\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7ec8\u4e8e\u51fa\u9762\u4e86\u3002 \u3000\u3000\u5bf9\u8fd9\u4f4d\u4eab\u540d\u6c5f\u6e56\uff0c\u5fc3\u4eea\u5df2\u4e45\u7684\u5927\u4fa0\u5ba2\uff0c\u53f6\u957f\u9752\u5185\u5fc3\u4e4b\u4e2d\uff0c\u5b9e\u6709\u7740\u65e0\u6bd4\u5d07\u656c\u3002 \u3000\u3000\u4f46\u9ec4\u7075\u548c\u4ed6\u7684\u4e00\u5915\u957f\u8c08\uff0c\u4e5f\u7ed9\u4e86\u4ed6\u5f88\u5927\u7684\u4fe1\u5fc3\u3002 \u3000\u3000\u73b0\u5728\uff0c\u53f6\u957f\u9752\u6709\u4e9b\u8ff7\u60d1\u4e86\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6697\u4f5c\u76d8\u7b97\u9053\uff1a\u4e0d\u662f\u9ec4\u7075\u6b3a\u9a97\u4e86\u4ed6\uff0c\u5c31\u662f\u8fd9\u4f4d\u9ad8\u5927\u4fa0\u4e0d\u660e\u5185\u60c5\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u9634\u9633\u5821\u5728\u6c5f\u6e56\u4e0a\uff0c\u672c\u6765\u5c31\u662f\u4e00\u4e2a\u58f0\u540d\u72fc\u85c9\u7684\u5730\u65b9\u3002 \u3000\u3000\u4f46\u9ec4\u7075\u90a3\u4e48\u5766\u7136\u76f8\u544a\uff0c\u5b9e\u5728\u4e0d\u50cf\u662f\u6b3a\u9a97\u4e4b\u610f\u3002 \u3000\u3000\u5c24\u5176\u662f\u4f55\u5bd2\u8863\uff0c\u90a3\u662f\u767e\u5206\u4e4b\u767e\u7684\u597d\u4eba\uff0c\u4ed6\u53c8\u600e\u80fd\u7518\u613f\u52a9\u7ea3\u4e3a\u8650\uff0c\u4e3a\u9ec4\u7075\u5356\u547d\u5462\uff1f \u3000\u3000\u4f46\u9ad8\u5929\u5065\u662f\u7edd\u5bf9\u53ef\u4ee5\u76f8\u4fe1\u7684\u4eba\u3002 \u3000\u3000\u53f6\u957f\u9752\u8ff7\u60d1\u4e86\u3002 \u3000\u3000\u5468\u7eb6\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\uff0c\u5821\u4e3b\u4e0d\u5728\u6b64\u5730\uff0c\u4e0d\u8fc7\uff0c\u4e09\u4e24\u5929\uff0c\u5c31\u56de\u6765\uff0c\u9ad8\u5927\u4fa0\u4e00\u5b9a\u8981\u8ffd\u51fa\u5723\u6c34\u7684\u5382\u843d\uff0c\u90a3\u5c31\u665a\u51e0\u5929\u518d\u6765\u5427\uff01\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5468\u5927\u5feb\uff0c\u90a3\u4e00\u4f4d\u662f\u9ebb\u9762\u8840\u624b\u5c60\u65e0\u65b9\u3002\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u662f\u4ed6\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u90a3\u4e24\u4f4d\u53ef\u662f\u6d3b\u50f5\u5c38\u9648\u6c9b\uff0c\u6bd2\u8702\u5f6d\u9707\u3002\u201d \u3000\u3000\u5468\u7eb6\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5468\u5927\u4fa0\u5728\u6c5f\u6e56\u4e0a\uff0c\u53ef\u4e5f\u662f\u6709\u5f88\u5927\u540d\u671b\u7684\u4eba\uff0c\u600e\u4e48\u4f1a\u548c\u8fd9\u4e9b\u4eba\u5728\u4e00\u8d77\uff0c\u5509\u2026\u2026\u5728\u4e0b\u65e9\u542c\u8bf4\u8fc7\u9634\u9633\u5821\u8fd9\u4e2a\u5730\u65b9\uff0c\u5145\u6ee1\u7740\u795e\u79d8\uff0c\u4f46\u5374\u6ca1\u6709\u60f3\u5230\uff0c\u8fd9\u4e00\u4e2a\u7ec4\u7ec7\uff0c\u7adf\u7136\u662f\u4e13\u95e8\u6536\u7f57\u6c5f\u6d0b\u5927\u76d7\uff0c\u548c\u4ea1\u547d\u532a\u5f92\uff0c\u8fd9\u4e09\u5927\u6076\u4eba\uff0c\u867d\u7136\u662f\u4e24\u4e2a\u7b49\u7ea7\uff0c\u4f46\u5374\u90fd\u88ab\u6536\u5728\u9634\u9633\u5821\u4e2d\u3002\u201d \u3000\u3000\u5468\u7eb6\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u655d\u4e86\u51e0\u4ef6\u574f\u4e8b\uff0c\u5e76\u4e0d\u80fd\u8bf4\u4ed6\u4eec\u4e00\u8f88\u5b50\u5c31\u662f\u574f\u4eba\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5468\u5927\u4fa0\uff0c\u4f60\u548c\u8fd9\u4e9b\u58f0\u540d\u72fc\u85c9\u7684\u4eba\uff0c\u65e5\u4e45\u76f8\u5904\uff0c\u4e0d\u89c9\u7740\u5f88\u96be\u8fc7\u5417\uff1f\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u5728\u4e0b\u5012\u6ca1\u6709\u8fd9\u79cd\u611f\u89c9\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5468\u5144\uff0c\u9ad8\u67d0\u4e00\u5411\u4e0d\u613f\u51fa\u53e3\u4f24\u4eba\uff0c\u6211\u5df2\u7ecf\u8bf4\u7684\u5f88\u660e\u767d\u4e86\uff0c\u4f46\u66ae\u9f13\u6668\u949f\uff0c\u60ca\u4e0d\u9192\u68a6\u4e2d\u4eba\uff0c\u90a3\u4e5f\u662f\u6ca1\u6709\u6cd5\u5b50\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5468\u7eb6\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\uff0c\u4e0d\u8fc7\uff0c\u6bcf\u4e2a\u4eba\u7684\u770b\u6cd5\u4e0d\u540c\uff0c\u6211\u89c9\u7740\u9634\u9633\u5821\u5012\u4e0d\u662f\u5916\u4f20\u7684\u90a3\u4e48\u4e11\u6076\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5468\u5927\u4fa0\uff0c\u5355\u662f\u9634\u9633\u5821\u8fd9\u4e2a\u540d\u5b57\uff0c\u5df2\u7ecf\u591f\u574f\u4e86\uff0c\u60f3\u4e0d\u5230\uff0c\u8fd8\u6536\u7f57\u4e01\u8fd9\u4e48\u591a\u7684\u6076\u4eba\uff0c\u5509\uff01\u5468\u5927\u4fa0\uff0c\u86c7\u3001\u9f20\u4e91\u96c6\u4e4b\u7a74\uff0c\u8fd8\u80fd\u5bb9\u5f97\u4e0b\u9f99\u6f5c\u864e\u85cf\u5417\uff1f\u201d \u3000\u3000\u5468\u7eb6\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u5468\u5927\u4fa0\uff0c\u826f\u79bd\u62e9\u6728\u800c\u6816\uff0c\u8fd9\u53e5\u8bdd\u2026\u2026\u201d \u3000\u3000\u5468\u7eb6\u63a5\u9053\uff1a\u201c\u6211\u660e\u767d\uff0c\u4e0d\u8fc7\uff0c\u4eba\u5404\u6709\u5fd7\uff0c\u52c9\u5f3a\u4e0d\u5f97\uff0c\u9ad8\u5927\u4fa0\u7684\u597d\u610f\u5728\u4e0b\u5fc3\u9886\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u5df3\u5c3d\u4e86\u5fc3\uff0c\u5468\u5927\u4fa0\u5982\u4f55\u9009\u62e9\uff0c\u90a3\u662f\u4f60\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5468\u7eb6\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\uff0c\u6709\u4e9b\u4e8b\u8005\u673d\u4e0d\u4fbf\u505a\u4e3b\uff0c\u8fc7\u51e0\u5929\u4f60\u518d\u6765\uff0c\u6211\u76f8\u4fe1\u5821\u4e3b\u56de\u6765\u4e4b\u540e\uff0c\u5b9a\u4f1a\u5bf9\u4f60\u9ad8\u5927\u4fa0\u6709\u4e2a\u6ee1\u610f\u7684\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u5618\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8c01\u662f\u9634\u9633\u5821\u4e3b\u3002\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\u4f55\u4e0d\u7b49\u4e09\u4e94\u5929\u540e\uff0c\u89c1\u7740\u4ed6\u5f53\u9762\u95ee\u8fc7\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u597d\uff01\u90a3\u6211\u518d\u8bf7\u6559\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u5723\u6c34\u662f\u4e0d\u662f\u5728\u8fd9\u4e07\u5bb6\u5927\u9662\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u8ffd\u98ce\u53df\u63a5\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u4e0d\u7528\u95ee\u4e86\uff0c\u6211\u77e5\u9053\uff0c\u5723\u6c34\u5c31\u653e\u5728\u4e07\u5bb6\u5927\u9662\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5468\u5927\u4fa0\uff0c\u8bf7\u9000\u4e00\u8fb9\uff0c\u6211\u8981\u4ed6\u4eec\u4ea4\u51fa\u6765\u3002\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\uff0c\u4f60\u5927\u4ec1\u5927\u4e49\uff0c\u6c5f\u6e56\u4e0a\u65e0\u4e0d\u79f0\u9053\uff0c\u6211\u8001\u75c5\u9b3c\u5411\u4f60\u4fdd\u8bc1\uff0c\u8fdf\u5219\u4e09\u65e5\uff0c\u591a\u5219\u4e94\u5929\uff0e\u6211\u8981\u5821\u4e3b\u5728\u6b64\u5730\u606d\u5019\u5927\u9a7e\uff0c\u90a3\u65f6\u9ad8\u5927\u4fa0\u518d\u4f5c\u51b3\u5b9a\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u6211\u4e0d\u80fd\u4fe1\u4efb\u9634\u9633\u5821\u4e2d\u4eba\u3002\u201d \u3000\u3000\u5468\u7eb6\u9053\uff1a\u201c\u6211\u8001\u75c5\u9b3c\u5462\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1b\uff1a\u201c\u5468\u5927\u4fa0\u5728\u6c5f\u6e56\u4e0a\u8bf4\u4ec0\u4e48\uff0c\u4eba\u4eba\u90fd\u4f1a\u76f8\u4fe1\uff0c\u4e0d\u8fc7\uff0c\u4f60\u73b0\u5728\u6295\u5165\u4e86\u9634\u9633\u5821\uff0c\u9634\u9633\u5821\u4e2d\u4eba\uff0c\u65e0\u6cd5\u4f7f\u6c5f\u6e56\u4e0a\u4efb\u4f55\u4eba\u76f8\u4fe1\u3002\u201d \u3000\u3000\u4ed6\u8138\u8272\u7a81\u7136\u95f4\u53d8\u5f97\u5341\u5206\u51b7\u8083\uff0c\u53cc\u76ee\u4e2d\u4e5f\u6cdb\u51fa\u4e86\u6d53\u91cd\u7684\u6740\u673a\uff0c\u7f13\u6b65\u903c\u8fd1\u4e86\u5468\u7eb6\uff0c\u9053\uff1a\u201c\u5468\u7eb6\uff0c\u6211\u5df2\u5341\u5e74\u6ca1\u6709\u51fa\u5200\uff0c\u4e0d\u8981\u903c\u6211\u51fa\u5200\u3002\u201d \u3000\u3000\u5468\u7eb6\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\u3001\u6c5f\u6e56\u4e2d\u4eba\uff0c\u90fd\u4e0d\u613f\u548c\u4f60\u4e3a\u654c\uff0c\u9634\u9633\u5821\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u9ad8\u5929\u5efa\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u6211\u8981\u5e26\u8d70\u5723\u6c34\u3002\u201d \u3000\u3000\u7a81\u7136\u8f6c\u8eab\u903c\u8fd1\u5f6d\u9707\u3001\u9648\u6c9b\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\uff0c\u5723\u6c34\u5b58\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u9648\u6c9b\u3001\u5f6d\u9707\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u7a81\u7136\u2014\u62ac\u624b\uff0c\u62cd\u62cd\u4e24\u58f0\uff0c\u9648\u6c9b\u548c\u5f6d\u9707\u6bcf\u4e2a\u4eba\u8138\u4e0a\uff0c\u90fd\u88ab\u6253\u4e86\u4e00\u4e2a\u8033\u62ec\u5b50\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u51f6\u608d\u6210\u6027\uff0c\u8fd9\u4e24\u638c\uff0c\u6253\u51fa\u4e86\u4e24\u4eba\u7684\u6012\u706b\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u5404\u6301\u5175\u5203\uff0c\u6328\u5411\u4e86\u9ad8\u5929\u5065\u3002 \u3000\u3000\u9ad8\u5929\u5efa\u51b7\u7b11\u4e00\u58f0\uff0c\u7a81\u7136\u628a\u5200\u4e00\u6325\uff0c \u3000\u3000\u6ca1\u6709\u770b\u6e05\u695a\u8fd9\u4e00\u5200\u7684\u6765\u9f99\u53bb\u8109\uff0c\u770b\u5230\u7684\u53ea\u662f\u4e24\u5177\u6ca1\u6709\u4eba\u5934\u7684\u5c38\u4f53\uff0c\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4e24\u9897\u4eba\u5934\uff0c\u98de\u8d77\u5f88\u9ad8\u3002 \u3000\u3000\u9ad8\u5929\u5065\u51fa\u5200\u5feb\uff0c\u6536\u5200\u66f4\u5feb\u3002 \u3000\u3000\u4e24\u5177\u5c38\u4f53\u8fd8\u672a\u5012\u4e0b\uff0c\u5200\u5df2\u8fd8\u9798\uff0c\u4eba\u5df2\u5230\u4e86\u75c5\u9b3c\u5468\u7eb6\u7684\u8eab\u524d\u3002 \u3000\u3000\u624d\u51fa\u7b2c\u4e00\u5200\uff0c\u679c\u7136\u77f3\u7834\u5929\u60ca\uff0c\u9707\u9a87\u4eba\u5fc3\u3002 \u3000\u3000\u5468\u7eb6\u8f7b\u8f7b\u5618\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u6740\u4e86\u4ed6\u4eec\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5468\u7eb6\uff0c\u4ea4\u51fa\u5723\u6c34\uff0c\u4e0d\u8981\u903c\u6211\u5927\u5f00\u6740\u6212\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u770b\u5230\u4e86\u90a3\u4e00\u5200\uff0c\u5947\u5e7b\u83ab\u6d4b\u7684\u4e00\u5200\u3002 \u3000\u3000\u5468\u7eb6\u6447\u6447\u6709\uff0c\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\uff0c\u4f60\u5c31\u6740\u4e86\u533a\u533a\uff0c\u6211\u4e5f\u4e0d\u80fd\u4ea4\u5723\u6c34\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u597d\uff01\u4f60\u51b3\u5fc3\u5982\u6b64\uff0c\u90a3\u5c31\u4eae\u5175\u5203\u5427\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u5446\u4e01\u4e00\u5446\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c60\u5144\uff0c\u9ad8\u5927\u4fa0\u5982\u679c\u5b9a\u8981\u51fa\u624b\u54b1\u4eec\u2026\u2026\u201d \u3000\u3000\u5c60\u65e0\u65b9\u63a5\u9053\uff1a\u201c\u75c5\u7535\u5468\u7eb6\u4e5f\u8bb8\u53ef\u4ee5\u63a5\u4e0b\u4ed6\u51e0\u62db\u3002\u201d \u3000\u3000\u54ea\u77e5\u5468\u7eb6\u53cc\u624b\u4e00\u62b1\u3002\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\u8bf7\u5feb\u52a8\u624b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5468\u7eb6\uff0c\u8fd9\u7b49\u82e6\u8089\u8ba1\uff0c\u5c11\u5728\u6211\u9762\u524d\u65bd\u7528\uff0c\u6211\u5df2\u8bf4\u7684\u5f88\u6e05\u695a\u4e86\uff0c\u4e3a\u4e86\u5723\u6c34\uff0c\u6211\u53ef\u8981\u5927\u5f00\u6740\u6212\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u6740\u5c31\u6740\uff0c\u5200\u5149\u4e00\u95ea\uff0c\u75c5\u9b3c\u5468\u7eb6\u751f\u751f\u88ab\u65a9\u4f5c\u4e86\u4e24\u6bb5\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u6740\u5f97\u5feb\u901f\u5982\u7535\uff0c\u4e5f\u6740\u5f97\u4eba\u5fc3\u751f\u5bd2\u610f\u3002 \u3000\u3000\u8ffd\u98ce\u53df\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u624b\u4e2d\u6267\u7740\u4e03\u661f\u5b9d\u5200\uff0c\u671b\u7740\u88ab\u4ed6\u4e00\u5200\u62e6\u8170\u65a9\u65ad\u7684\u5c38\u4f53\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4ed6\u5e94\u8be5\u53ef\u4ee5\u907f\u5f00\u8fd9\u4e00\u5200\u7684\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u95ea\u907f\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u6ca1\u6709\u60f3\u5230\u9ad8\u5927\u4fa0\u771f\u7684\u4f1a\u6740\u4ed6\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u6012\u9053\uff1a\u201c\u5c60\u65e0\u65b9\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u5c60\u65e0\u65b9\u63a5\u9053\uff1a\u201c\u4ed6\u66f4\u60f3\u4e0d\u5230\u5927\u540d\u9f0e\u9f0e\u7684\u9ad8\u5927\u4fa0\uff0c\u7adf\u7136\u4f1a\u8bf4\u5c31\u6740\uff0c\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5200\u5df2\u51fa\u624b\uff0c\u56e0\u4e3a\uff0c\u8fd9\u4e0d\u50cf\u4e00\u4e2a\u5927\u4fa0\u505a\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u662f\u8bef\u6740\u4e86\u5468\u7eb6\uff0c\u4f46\u5982\u6740\u4e86\u4f60\u5c60\u65e0\u65b9\uff0c\u90a3\u5c31\u4e0d\u7b97\u8bef\u6740\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5982\u9ad8\u5927\u4fa0\u7b2c\u4e00\u6b21\u5bf9\u6211\u5c60\u67d0\u4eba\uff0c\u7528\u51fa\u8fd9\u7b49\u624b\u6bb5\u6211\u76f8\u4fe1\uff0c\u5728\u4e0b\u4e5f\u4f1a\u6b7b\u5728\u9ad8\u5927\u4fa0\u7684\u5feb\u5200\u4e4b\u4e0b\uff0c\u53ef\u662f\uff0c\u73b0\u5728\u4e0d\u4f1a\u4e86\u56e0\u4e3a\uff0c\u6211\u770b\u5230\u4e86\u4f60\u9ad8\u5927\u4fa0\u7684\u624b\u6bb5\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5c60\u65e0\u65b9\uff0c\u9ad8\u67d0\u4eba\u65e2\u7136\u5f00\u4e86\u6740\u6212\uff0c\u51fa\u4e86\u5200\uff0c\u4eca\u591c\u5c31\u5fc5\u987b\u628a\u4e8b\u60c5\u5f04\u4e00\u4e2a\u6c34\u843d\u77f3\u51fa\uff0c\u4f60\u5c60\u65e0\u65b9\u662f\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\uff0c\u4f60\u8868\u73b0\u51fa\u6765\u7684\u4fa0\u6c14\u3001\u82f1\u98ce\uff0c\u6211\u4eec\u5df2\u7ecf\u9886\u6559\u4e86\uff0c\u82e5\u6c5f\u6e56\u9053\u4e0a\u7684\u4fa0\u4e49\u4eba\u7269\uff0c\u90fd\u548c\u9ad8\u5927\u4fa0\u4e00\u6837\uff0c\u54b1\u4eec\u5b81\u4e3a\u7eff\u6797\u4e2d\u4e00\u540d\u76d7\u532a\uff0c\u4e5f\u4e0d\u4e3a\u4fa0\u4e49\u95e8\u4e2d\u4eba\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\uff0c\u8001\u75c5\u9b3c\u6295\u5165\u4e86\u9634\u9633\u5821\u4e2d\uff0c\u60f3\u5fc5\u6709\u4ed6\u7684\u82e6\u8877\uff0c\u81f3\u5c11\uff0c\u4ed6\u7f6a\u4e0d\u81f3\u6b7b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u65e0\u610f\u6740\u4ed6\uff0c\u4ed6\u7684\u6b7b\uff0c\u8981\u602a\u4ed6\u592a\u8fc7\u81ea\u4fe1\u2026\u2026\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u592a\u8fc7\u81ea\u4fe1\uff0c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u5c60\u65e0\u4e07\u9053\uff1a\u201c\u75c5\u53df\u5468\u7eb6\u81ea\u4fe1\u9ad8\u5927\u4fa0\u4e0d\u4f1a\u5bf9\u6c60\u65bd\u6bd2\u624b\uff0c\u4ed6\u5168\u65e0\u65b9\u5907\uff0c\u6240\u4ee5\uff0c\u9ad8\u5927\u4fa0\u624d\u80fd\u4e00\u51fb\u5f97\u624b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u4e3e\u6b65\u76f4\u5bf9\u5c60\u65e0\u65b9\u884c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u624b\u4e2d\u4ecd\u63d0\u7740\u4e03\u661f\u5b9d\u5200\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u5438\u4e00\u53e3\u6c14\uff0e\u4e25\u4f5c\u6212\u5907\u3002 \u3000\u3000\u53f6\u957f\u9752\u7a81\u7136\u5411\u524d\u884c\u4e86\u4e24\u6b65\uff0c\u9053\uff1a\u201c\u5c60\u5144\uff0c\u591a\u5c0f\u5fc3\u554a\uff01\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6211\u4f1a\u5168\u529b\u6212\u5907\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u903c\u8fd1\u5230\u5c60\u65e0\u65b9\u8eab\u524d\u56db\u4e94\u5c3a\u5904\uff0c\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u8ffd\u98ce\u53df\u5df2\u7136\u8fdf\u540e\u4e86\u516b\u6b65\uff0c\u9759\u9759\u7684\u7ad9\u7740\u3002 \u3000\u3000\u4e24\u9053\u76ee\u5149\u671b\u7740\u75c5\u53df\u5468\u7eb6\u5c38\u4f53\u51fa\u795e\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u624b\u4e2d\u6267\u7740\u4e00\u6839\u4e00\u5c3a\u516b\u5bf8\u7684\u94f6\u68d2\u3002 \u3000\u3000\u90a3\u5c31\u662f\u5c60\u65e0\u65b9\u7684\u5175\u5203\uff0c\u53eb\u505a\u5982\u610f\u94f6\u68d2\u3002 \u3000\u3000\u8fd9\u6839\u5982\u610f\u68d2\u8ddf\u968f\u7740\u5c60\u65e0\u65b9\uff0c\u6570\u5341\u5e74\u6765\uff0c\u4f1a\u8fc7\u4e0d\u5c11\u6b66\u6797\u9ad8\u624b\u3002\u5b83\u5185\u85cf\u673a\u7c27\uff0c\u53ef\u4f38\u53ef\u7f29\uff0c\u957f\u80fd\u5230\u4e94\u5c3a\u56db\u5bf8\uff0c\u77ed\u80fd\u5230\u4e00\u5c3a\u516b\u5bf8\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u51fa\u624b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5c60\u65e0\u65b9\uff0c\u4f60\u771f\u8981\u627e\u6b7b\uff01\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u75c5\u53df\u5468\u7eb6\u5e76\u672a\u627e\u6b7b\uff0c\u4f46\u4ed6\u8fd8\u662f\u6b7b\u5728\u4e86\u4f60\u7684\u4e03\u661f\u5200\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u54fc\u4e00\u58f0\uff0c\u7a81\u7136\u51fa\u5200\u3002 \u3000\u3000\u4eca\u591c\u4ed6\u5df2\u51fa\u4e86\u4e24\u6b21\u5200\uff0c\u4e00\u6b21\uff0c\u6740\u6b7b\u4e86\u9648\u6c9b\u3001\u5f6d\u9707\uff0c\u7b2c\u4e8c\u6b21\uff0c\u6740\u6b7b\u4e86\u75c5\u53df\u5468\u7eb6\u3002 \u3000\u3000\u4f46\u7b2c\u4e09\u6b21\u51fa\u5200\uff0c\u5374\u6ca1\u6709\u6740\u6b7b\u5c60\u65e0\u65b9\u3002 \u3000\u3000\u5e76\u975e\u4ed6\u7684\u5200\u4e0b\u7559\u60c5\uff0c\u800c\u662f\u5c60\u65e0\u65b9\u2014\u76f4\u8c28\u614e\u7684\u6212\u5907\u3002 \u3000\u3000\u5f53\u7684\u4e00\u58f0\uff0c\u5982\u610f\u68d2\u7a81\u7136\u4f38\u957f\u4e09\u5c3a\uff0c\u5728\u54bd\u5589\u524d\u9762\u4e09\u5bf8\u5904\uff0c\u5c01\u4f4f\u4e86\u9ad8\u5929\u5065\u7684\u5200\u52bf\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7684\u5200\u6cd5\uff0c\u5b9e\u5728\u5f88\u5feb\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u6570\u5341\u5e74\u6765\uff0c\u4f55\u6b62\u8eab\u7ecf\u767e\u6218\uff0c\u8fd9\u662f\u4ed6\u7b2c\u4e00\u6b21\u9047\u4e0a\u4e86\u5982\u6b64\u51cc\u5389\u3001\u5feb\u901f\u7684\u5200\u6cd5\u3002 \u3000\u3000\u9ad8\u5929\u5065\u4e00\u51fb\u843d\u7a7a\uff0c\u4e03\u661f\u5200\u500f\u7136\u6536\u56de\uff0c\u5200\u968f\u52bf\u52a8\uff0c\u53c8\u9000\u56de\u4e86\u516d\u5c3a\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01 \u3000\u3000\u770b\u6765\u9ebb\u9762\u8840\u624b\uff0c\u679c\u7136\u662f\u540d\u4e0d\u865a\u4f20\uff0c\u4f60\u80fd\u5c01\u4f4f\u6211\u7b2c\u4e00\u5200\uff0e\u6bd4\u75c5\u53df\u5468\u7eb6\uff0e\u4f3c\u662f\u5c24\u5f3a\u8fc7\u51e0\u5206\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5728\u4e0b\u6bd4\u5468\u7eb6\u76f8\u5dee\u5341\u5206\u7684\u591a\uff0c\u53ea\u4e0d\u8fc7\uff0c\u5728\u4e0b\u5df2\u7ecf\u77e5\u9053\u9ad8\u5927\u4fa0\u51fa\u5200\u5982\u6697\u7bad\uff0c\u9632\u5907\u7684\u5341\u5206\u5c0f\u5fc3\uff0c\u800c\u5468\u7eb6\u5374\u5ffd\u7565\u4e86\u8fd9\u4e00\u70b9\uff0c\u5468\u7eb6\u4e4b\u6b7b\uff0c\u5982\u82e5\u8bf4\u662f\u6b7b\u5728\u4f60\u9ad8\u5927\u4fa0\u7684\u5feb\u5200\u4e4b\u4e0b\uff0c\u5012\u4e0d\u5982\u8bf4\u4ed6\u662f\u6b7b\u5728\u4f60\u7684\u4fa0\u540d\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c60\u65e0\u65b9\uff0c\u518d\u63a5\u6211\u51e0\u5200\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u7a81\u7136\u6325\u5200\u5288\u51fa\u3002 \u3000\u3000\u4f46\u89c1\u5200\u5149\u95ea\u95ea\uff0c\u8fde\u653b\u56db\u5200\u3002 \u3000\u3000\u8bf4\u662f\u56db\u5200\uff0c\u4e8b\u5b9e\u4e0a\u53ea\u662f\u4e00\u62db\u4e2d\uff0c\u56db\u4e2a\u53d8\u5316\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u5c01\u5f00\u4e86\u4e09\u5200\uff0c\u4f46\u5374\u65e0\u6cd5\u5c01\u5f00\u7b2c\u56db\u5200\u3002 \u3000\u3000\u68ee\u68ee\u7684\u5bd2\u8292\uff0c\u5df2\u7136\u903c\u5230\u4e86\u5c60\u65e0\u65b9\u7684\u524d\u80f8\u3002 \u3000\u3000\u4e00\u652f\u5251\u659c\u91cc\u4f38\u4e86\u8fc7\u6765\u3002\u5c01\u4f4f\u4e86\u7b2c\u56db\u5200\u3002 \u3000\u3000\u53f6\u957f\u9752\u7684\u5251\uff0c\u5feb\u5982\u95ea\u7535\u7684\u4e00\u5251\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7684\u653b\u52bf\u4e2d\uff0c\u4ece\u6765\u4e0d\u8ba9\u4eba\u6709\u8fd8\u624b\u7684\u673a\u4f1a\uff0c\u5f53\u4ed6\u7684\u653b\u52bf\u53d7\u963b\u65f6\uff0c\u7acb\u523b\u6536\u5200\u800c\u9000\u3002 \u3000\u3000\u73b0\u5728\uff0c\u9ad8\u5929\u5065\u53c8\u6536\u5200\u9000\u540e\u4e86\u516b\u5c3a\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u8f7b\u8f7b\u5618\u4e00\u53e3\u6c14\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8c22\u4e86\uff0c\u5144\u5f1f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u795e\u60c5\u51b7\u8083\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u4e00\u4e2a\u4eba\uff0c\u6709\u5982\u6b64\u5feb\u901f\u7684\u5200\u6cd5\uff0c\u5c60\u5144\u80fd\u6309\u4ed6\u4e09\u5200\uff0c\u5728\u4e0b\u53ea\u6015\u8fde\u8fd9\u4e09\u5200\u4e5f\u63a5\u4e0d\u4e0b\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4ed6\u7684\u76db\u540d\uff0c\u6bd4\u4ed6\u7684\u5200\u6cd5\u66f4\u53ef\u6015\uff0c\u4f20\u8bf4\u4e2d\uff0c\u4e2d\u5dde\u7b2c\u4e00\u5200\uff0c\u6740\u4eba\u4e0d\u51fa\u7b2c\u4e09\u5200\uff0c\u6700\u591a\u53ea\u6709\u4e8c\u5200\uff0c\u4f46\u6211\u63a5\u4e86\u4ed6\u4e24\u62db\u56db\u53d8\u4e4b\u540e\uff0c\u5fc3\u4e2d\u53cd\u800c\u6709\u4e86\u53e6\u4e00\u79cd\u611f\u53d7\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u611f\u53d7\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4ed6\u7684\u5200\u5c16\uff0c\u5168\u662f\u653b\u52bf\uff0c\u5982\u82e5\u54b1\u4eec\u4e0d\u7ed9\u4ed6\u653b\u5165\u7684\u673a\u4f1a\u5462\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u59a8\u2014\u8bd5\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u53f3\u624b\u4e00\u9707\uff0c\u624b\u4e2d\u5982\u610f\u68d2\u4f38\u957f\u5230\u4e94\u5c3a\u56db\u5bf8\uff0c\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\u5c0f\u5fc3\uff0c\u5c60\u67d0\u4eba\u8fd8\u51fb\u4e86\u3002\u201d \u3000\u3000\u53cc\u624b\u6325\u68d2\uff0c\u6a2a\u91cc\u626b\u53bb\u3002 \u3000\u3000\u9ad8\u5929\u5065\u4e03\u661f\u5200\u5200\u80cc\u6a2a\u67b6\uff0c\u5f53\u7684\u2014\u58f0\uff0c\u5c01\u6765\u2014\u68d2\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5c60\u65e0\u65b9\u7684\u4e2d\u95e8\uff0c\u5e94\u8be5\u662f\u95e8\u6237\u5927\u5f00\u3002\u9ad8\u5929\u5065\u53ef\u4ee5\u6325\u5200\u76f4\u653b\u3002 \u3000\u3000\u4ee5\u4ed6\u5200\u6cd5\u4e4b\u5feb\uff0c\u4efb\u4f55\u4eba\u53ea\u8981\u7ed9\u4e86\u4ed6\u8fd9\u4e00\u673a\u4f1a\uff0c\u5c31\u4f1a\u6b7b\u4e8e\u5200\u4e0b\u3002 \u3000\u3000\u4f46\u5c60\u5143\u65b9\u65e9\u6709\u601d\u8651\uff0c\u5982\u610f\u68d2\u4e00\u8f6c\uff0c\u53e6\u2014\u9762\u547c\u7684\u5411\u4e0a\u6311\u6765\u3002 \u3000\u3000\u4ed6\u53d1\u52a8\u5f88\u5feb\uff0c\u4e8b\u5148\u65e9\u5df2\u60f3\u597d\uff0c\u4e24\u4e2a\u52a8\u4f5c\uff0e\u6709\u5982\u8fde\u73af\u7684\u2014\u62db\u4e00\u6837\u3002 \u3000\u3000\u9ad8\u5929\u5065\u88ab\u8fd9\u4e00\u6311\u7684\u653b\u52bf\uff0c\u5c01\u4f4f\u8fdb\u653b\u7684\u673a\u4f1a\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u2014\u62db\u987a\u624b\uff0c\u5982\u610f\u68d2\u5e7b\u8d77\u4e86\u4e00\u7247\u68d2\u5f71\uff0c\u8fde\u653b\u2014\u5341\u516b\u68d2\u3002 \u3000\u3000\u9ad8\u5929\u5065\u786e\u662f\u4e0d\u51e1\uff0c\u8fd9\u4e00\u5341\u516b\u6367\uff0c\u5e76\u6ca1\u6709\u628a\u4ed6\u8feb\u9000\u4e00\u6b65\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u624b\u4e2d\u4e03\u661f\u5b9d\u5200\uff0c\u5de6\u6a2a\u53f3\u7ad6\uff0c\u4e5f\u5e7b\u8d77\u4e86\u4e00\u7247\u5200\u5f71\u3002 \u3000\u3000\u4e00\u8fde\u4e32\u91d1\u94c1\u4ea4\u9e23\u58f0\u4e2d\uff0c\u5c01\u5f00\u4e86\u5c60\u65e0\u65b9\u5341\u516b\u68d2\u3002 \u3000\u3000\u4e00\u5341\u516b\u68d2\u653b\u5b8c\uff0c\u5c60\u65e0\u65b9\u4e5f\u75be\u5feb\u7684\u5411\u540e\u9000\u4e86\u516b\u6b65\u3002 \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u68d2\u6cd5\uff0c\u597d\u68d2\u6cd5\uff0c\u4e0d\u8fc7\uff0c\u5728\u4e0b\u6709\u4e9b\u5947\u602a\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u8eab\u7ecf\u767e\u6218\uff0c\u4ece\u6765\u6ca1\u6709\u9762\u5bf9\u8fc7\u8fd9\u6837\u4e00\u4e2a\u4eba\uff0c\u6709\u751f\u4ee5\u6765\uff0c\u4ed6\u7b2c\u4e00\u6b21\u611f\u89c9\u5230\u9762\u5bf9\u7684\u654c\u4eba\u3002\u662f\u5982\u6b64\u53ef\u6015\uff0c\u2014\u5ea7\u61be\u4e0d\u52a8\u7684\u5c71\u3002 \u3000\u3000\u4f46\u4ed6\u5c3d\u91cf\u4f7f\u81ea\u5df1\u4fdd\u6301\u7740\u5e73\u9759\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5947\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u53f7\u79f0\u8840\u624b\uff0c\u542c\u8bf4\u5728\u53cc\u624b\u4e0a\u7ec3\u6709\u4e00\u79cd\u8d64\u715e\u638c\uff0c\u60f3\u4e0d\u5230\uff0c\u4f60\u89c9\u7528\u94c1\u68d2\u5bf9\u654c\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u7ec3\u8fc7\u8d64\u715e\u638c\uff0c\u4f60\u53ea\u8981\u80fd\u591f\u903c\u5f97\u4e0d\u5f97\u4e0d\u7528\u65f6\uff0c\u5728\u4e0b\u81ea\u4f1a\u51fa\u624b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u5c60\u65e0\u65b9\uff0c\u4f60\u4e00\u751f\u4e3a\u6076\uff0c\u6740\u4eba\u4e0d\u5c11\uff0c\u4eca\u5929\u6211\u6740\u4e86\u4f60\uff0c\u4e5f\u662f\u4e3a\u6c5f\u6e56\u9664\u5bb3\u4e86\u3002\u201d \u3000\u3000\u53f3\u624b\u4e00\u6325\uff0c\u5ffd\u7136\u4e00\u5200\u5288\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u62db\u5f88\u666e\u901a\u7684\u201c\u529b\u5288\u6cf0\u5c71\u201d\uff0c\u4f46\u5728\u5c60\u65e0\u65b9\u7684\u611f\u89c9\u4e2d\uff0c\u8fd9\u4e00\u5200\u5374\u6709\u7740\u5f88\u591a\u7684\u53d8\u5316\u3002 \u3000\u3000\u4ed6\u5bf9\u654c\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u60f3\u5230\u8fd9\u53ef\u80fd\u662f\u4e00\u5f0f\u865a\u62db\uff0c\u6240\u4ee5\uff0c\u4e00\u76f4\u51dd\u52b2\u4e0d\u53d1\uff0c\u51c6\u5907\u5e94\u53d8\u3002 \u3000\u3000\u76f4\u5f85\u90a3\u4e00\u5200\u8ddd\u79bb\u5934\u9876\u4e0d\u8fdc\u65f6\uff0c\u5c60\u65e0\u65b9\u624d\u7a81\u7136\u4e00\u6a2a\u624b\u4e2d\u5982\u610f\u68d2\uff0c\u6a2a\u91cc\u67b6\u53bb\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7684\u5200\u52bf\u3001\u63a0\u7740\u94c1\u68d2\uff0c\u7a81\u7136\u5411\u4e0b\u6ed1\u843d\uff0c\u5200\u4e0a\u67d4\u82e5\u65e0\u529b\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u5200\u6cd5\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u662f\u4ec0\u4e48\u5200\u6cd5\uff0c\u53ea\u662f\u4e00\u79cd\u529f\u529b\u8fd0\u7528\u3002\u201d \u3000\u3000\u5200\u5df2\u5212\u4e0a\u4e86\u5c60\u65e0\u65b9\u7684\u524d\u80f8\u3002 \u3000\u3000\u53c8\u662f\u90a3\u4e00\u652f\u5251\uff0c\u659c\u91cc\u4f38\u4e86\u8fc7\u6765\uff0c\u67b6\u4f4f\u4e86\u4e03\u661f\u5200\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u75be\u5feb\u7684\u5411\u540e\u9000\uff0c\u524d\u80f8\u7684\u8863\u670d\uff0c\u5df2\u7ecf\u7834\u6765\u4e86\u4e00\u9053\u53e3\u5b50\u3002\u9c9c\u8840\u7531\u90a3\u9053\u53e3\u5b50\u4e2d\uff0c\u6e17\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5982\u4e0d\u662f\u53f6\u957f\u9752\u8fd9\u4e00\u5251\u591f\u8fc5\u5feb\uff0c\u5c60\u65e0\u65b9\u52bf\u5fc5\u5df2\u6b7b\u5728\u4e86\u9ad8\u5929\u5065\u7684\u5200\u4e0b\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u8c22\u8c22\u4f60\uff0c\u4e24\u5ea6\u6c42\u4e86\u6211\u7684\u8001\u547d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u80fd\u628a\u5200\u9053\u5316\u82e5\u67d4\u4e1d\uff0c\u4f7f\u5200\u52bf\u6ed1\u843d\u3001\u5728\u4e0b\u4e5f\u662f\u521d\u6b21\u89c1\u5230\uff0c\u5c60\u5144\u7684\u4f24\u52bf\u5982\u4f55\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5f1f\u7684\u5251\u52bf\u591f\u5feb\uff0c\u6240\u4ee5\uff0c\u6211\u4f24\u7684\u8fd8\u4e0d\u592a\u91cd\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd8\u80fd\u4e0d\u80fd\u52a8\u624b\uff1f\u201d\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53ef\u4ee5\uff0c\u8fd9\u4e00\u70b9\u76ae\u8089\u4e4b\u4f24\uff0c\u6211\u8fd8\u53ef\u4ee5\u6491\u5f97\u4f4f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u7684\u5200\u6cd5\u5927\u6df1\u5965\uff0c\u4e00\u4e2a\u4eba\u51b3\u975e\u5176\u654c\uff0c\u54b1\u4eec\u4e24\u4e2a\u8054\u624b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u76ee\u5149\u8f6c\u6ce8\u5230\u53f6\u957f\u9752\u7684\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u53c8\u662f\u4f60\u6551\u4e86\u4ed6\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5728\u4e0b\u4e00\u76f4\u5728\u65c1\u8fb9\u7b49\u5f85\uff0c\u81ea\u7136\u51fa\u624b\u65b9\u4fbf\u4e86\u4e9b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u53eb\u53f6\u957f\u9752\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u662f\u78e8\u5251\u8001\u4eba\u7684\u5f92\u5f1f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u662f\u5bb6\u5e08\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u78e8\u5251\u8001\u4eba\u4e00\u751f\u803f\u76f4\uff0c\u600e\u4e48\u4f1a\u6536\u4e86\u4f60\u8fd9\u4e48\u4e00\u4e2a\u5f1f\u5b50\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u81f3\u5c11\uff0c\u6211\u884c\u4e8b\u4e3a\u4eba\uff0c\u4e8e\u5fc3\u65e0\u6127\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u548c\u9634\u9633\u5821\u8fd9\u4e00\u6279\u51f6\u795e\u6076\u52bf\uff0c\u725b\u9b3c\u86c7\u795e\uff0c\u6df7\u5728\u4e00\u8d77\uff0c\u8fd8\u80fd\u95ee\u5fc3\u65e0\u6127\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\uff0c\u4eca\u5bb5\u4e4b\u524d\uff0c\u5728\u4e0b\u5bf9\u4f60\u9ad8\u5927\u4fa0\u7684\u786e\u5341\u5206\u656c\u6155\uff0c\u4e0d\u8fc7\uff0c\u7531\u73b0\u5728\u5f00\u59cb\uff0c\u5bf9\u4f60\u9ad8\u5927\u4fa0\u4e48\uff1f\u5728\u4e0b\u5185\u5fc3\u4e2d\u6709\u4e86\u4e00\u70b9\u7591\u95ee\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4ec0\u4e48\u7591\u95ee\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u770b\u5230\u4e86\u4f60\u7684\u6740\u4eba\u624b\u6cd5\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u5982\u662f\u90a3\u4e2a\u4eba\u8be5\u6b7b\uff0c\u6211\u600e\u4e48\u6740\u4ed6\uff0c\u4f3c\u4e4e\u662f\u5e76\u4e0d\u91cd\u8981\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u81f3\u5c11\u75c5\u53df\u5468\u7eb6\uff0c\u6b7b\u5f97\u5f88\u51a4\u6789\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6b7b\u7684\u662f\u51a4\u4e86\u4e00\u4e9b\uff0c\u90a3\u4e00\u5200\u5730\u53ef\u4ee5\u8eb2\u5f00\u7684\uff0c\u4f46\u4ed6\u6ca1\u6709\u8eb2\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u592a\u4fe1\u4efb\u4f60\u7684\u8eab\u5206\uff0c\u60f3\u4e0d\u5230\u4f60\u4f1a\u7a81\u7136\u51fa\u624b\uff0c\u6240\u4ee5\uff0c\u4f60\u624d\u80fd\u4e00\u5200\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u5c31\u7b97\u5468\u7eb6\u4e0d\u8be5\u6b7b\uff0c\u4f46\u8fd9\u4e2a\u8840\u624b\u5c60\u65e0\u65b9\u5462\uff1f\u4ed6\u8be5\u4e0d\u8be5\u6b7b\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u4e5f\u4e0d\u8be5\u6b7b\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\uff0c\u4e0d\u5206\u7682\u767d\uff0c\u4e0d\u5206\u662f\u975e\uff0c\u78e8\u5251\u8001\u4eba\u600e\u4e48\u4f1a\u6559\u51fa\u4f60\u8fd9\u6837\u4e00\u4e2a\u5f92\u5f1f\uff0c\u65e5\u540e\uff0c\u6211\u518d\u89c1\u4ed6\u4e4b\u65f6\uff0c\u5b9a\u8981\u95ee\u4ed6\u4e00\u4e2a\u6559\u5f92\u4e0d\u4e25\u4e4b\u7f6a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u5df2\u79bb\u5e08\uff0c\u5728\u4e0b\u5728\u6c5f\u6e56\u4e0a\u7684\u4f5c\u4e3a\uff0c\u90fd\u7531\u6211\u81ea\u5df1\u627f\u62c5\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u4f60\u95ef\u51fa\u4e03\u5251\u8ffd\u9b42\uff0c\u8fd9\u70b9\u4fa0\u540d\u5f97\u4e4b\u4e0d\u6613\uff0c\u4e0d\u8981\u81ea\u5df1\u6bc1\u4e86\u5b83\uff0c\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u867d\u6709\u4e03\u5251\u8ffd\u9b42\u4e4b\u540d\uff0c\u4f46\u6740\u4eba\u4e5f\u548c\u4f60\u6709\u6240\u4e0d\u540c\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u8a00\u597d\u8bed\uff0c\u529d\u4e0d\u9192\u4f60\u8fd9\u68a6\u4e2d\u4e4b\u4eba\uff0c\u770b\u6765\uff0c\u6211\u4e5f\u53ea\u6709\u6210\u5168\u4f60\u4e86\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u56de\u5934\uff0c\u671b\u4e86\u8ffd\u98ce\u53df\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u6b66\u5144\uff0c\u4f60\u90fd\u770b\u5230\u4e86\uff0c\u542c\u5230\u4e86\uff0c\u65e5\u540e\u89c1\u7740\u78e8\u5251\u8001\u4eba\u65f6\uff0c\u4e5f\u597d\u7ed9\u5144\u5f1f\u505a\u4e2a\u8bc1\u4eba\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u6ca1\u6709\u7b54\u8bdd\uff0c\u53ea\u5fae\u5fae\u70b9\u5934\u3002 \u3000\u3000\u5934\u70b9\u7684\u5f88\u52c9\u5f3a\uff0c\u663e\u7136\u5185\u5fc3\u4e4b\u4e2d\u5e76\u4e0d\u662f\u5f88\u540c\u610f\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5374\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6b66\u5144\u613f\u610f\u505a\u8bc1\uff0c\u5144\u5f1f\u4e5f\u7b97\u6709\u4e2a\u4ea4\u4ee3\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u957f\u5251\u4e00\u4e3e\uff0c\u5e73\u80f8\u800c\u7acb\uff0c\u5c60\u65e0\u65b9\u4e5f\u5e73\u4e3e\u624b\u4e2d\u5982\u610f\u68d2\u3002 \u3000\u3000\u4e3a\u4e86\u548c\u53f6\u957f\u9752\u914d\u5408\u65b9\u4fbf\uff0c\u5c60\u65e0\u65b9\u53c8\u628a\u624b\u4e2d\u7684\u5982\u610f\u68d2\u7f29\u6210\u4e09\u5c3a\u5de6\u53f3\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u80f8\u524d\u7684\u4f24\u52bf\uff0c\u5b9e\u5728\u5e76\u4e0d\u8f7b\uff0c\u9c9c\u8840\u4ecd\u7136\u4e0d\u65ad\u7684\u6d41\u51fa\u6765\u3002 \u3000\u3000\u8840\u6c34\uff0c\u6e7f\u900f\u534a\u8eab\u7684\u8863\u886b\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u6d51\u5982\u4e0d\u89c9\uff0c\u53ea\u662f\u5168\u795e\u8d2f\u6ce8\u5728\u9ad8\u5929\u5065\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u9ad8\u5929\u5065\u76ee\u5149\u4e00\u63a0\u53f6\u957f\u9752\uff0c\u9053\uff1a\u201c\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\u4e00\u5200\uff0c\u523a\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5200\u81f3\u4e2d\u9014\uff0c\u5ffd\u7136\u4e00\u8f6c\uff0c\u5288\u5411\u4e86\u5c60\u65e0\u65b9\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u5982\u610f\u68d2\u659c\u659c\u4e00\u5c01\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u5c01\u4f4f\u4e86\u5200\u52bf\u3002 \u3000\u3000\u53f6\u957f\u9752\u5927\u559d\u4e00\u4e8b\uff0c\u4e00\u5251\u523a\u51fa\u3002 \u3000\u3000\u4e00\u52a8\u624b\uff0c\u5c31\u7528\u51fa\u4e86\u8ffd\u9b42\u4e03\u5251\u3002 \u3000\u3000\u5f3a\u5389\u7684\u5251\u98ce\uff0e\u5e26\u8d77\u4e01\u91d1\u5203\u7834\u7a7a\u4e4b\u58f0\u3002 \u3000\u3000\u9ad8\u5929\u5065\u4e03\u661f\u5b9d\u5200\u4e00\u8f6c\uff0c\u5ffd\u7136\u95f4\uff0c\u5e7b\u8d77\u4e86\u4e00\u7247\u5200\u5f71\u3002 \u3000\u3000\u5200\u5251\u76f8\u89e6\uff0c\u53f6\u957f\u9752\u7a81\u7136\u95f4\uff0c\u611f\u89c9\u5230\u624b\u4e2d\u4e00\u9707\uff0c\u957f\u5251\u51e0\u4e4e\u88ab\u90a3\u4e00\u5200\u9707\u98de\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u5982\u610f\u68d2\u4e00\u7f29\u4e00\u4f38\uff0c\u70b9\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u529b\u9053\u5f3a\u731b\uff0c\u4e00\u8fde\u4e32\u91d1\u94c1\u5927\u9707\u58f0\u4e2d\uff0c\u7adf\u7136\u51b2\u7834\u4e86\u9ad8\u5929\u5065\u7684\u62a4\u8eab\u5200\u8292\u3002 \u3000\u3000\u54ea\u77e5\u9ad8\u5929\u5065\u5200\u52bf\u7a81\u7136\u4e00\u8f6c\uff0c\u631f\u4e00\u7247\u51cc\u5389\u7684\u5200\u98ce\uff0c\u7adf\u7136\u6b3a\u5165\u4e86\u4e24\u4eba\u4e4b\u95f4\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u4e03\u661f\u5200\u5de6\u8f6c\u53f3\u51fb\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u8fde\u653b\u4e86\u56db\u5200\u3002 \u3000\u3000\u56db\u5200\u5206\u88ad\u4e24\u4e2a\u4eba\uff0c\u628a\u4e24\u4eba\u90fd\u8feb\u5f97\u53e5\u540e\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u53f6\u957f\u9752\u7684\u8ffd\u9b42\u4e03\u5251\uff0c\u672c\u662f\u4e00\u79cd\u5f88\u9738\u9053\u7684\u5251\u52bf\uff0c\u65bd\u5c55\u5f00\u65f6\uff0c\u6709\u7740\u62db\u62db\u8ffd\u9b42\uff0c\u5251\u5251\u53d6\u547d\u7684\u6c14\u52bf\u3002 \u3000\u3000\u4f46\u4ed6\u548c\u5c60\u65e0\u65b9\u8054\u624b\u5408\u653b\u65f6\uff0c\u5374\u6709\u7740\u65bd\u5c55\u4e0d\u5f00\u7684\u611f\u89c9\uff0c\u751f\u6050\u90a3\u6269\u5c55\u653b\u654c\u7684\u5251\u52bf\uff0c\u4f24\u5230\u4e86\u5c60\u65e0\u65b9\u3002 \u3000\u3000\u9ad8\u5929\u5065\u628a\u4e24\u4eba\u5206\u4e3a\u5f00\uff0c\u53cd\u800c\u5e2e\u4e86\u53f6\u957f\u9752\u7684\u5fd9\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6ca1\u6709\u4e86\u987e\u8651\uff0c\u5c55\u5f00\u4e86\u8ffd\u9b42\u4e03\u5251\u3002 \u3000\u3000\u4f46\u89c1\u5bd2\u8292\u5982\u7535\uff0c\u5251\u5f71\u91cd\u91cd\uff0c\u6ce2\u7ffb\u6d6a\u6d8c\u4e00\u822c\uff0c\u76f4\u5377\u8fc7\u53bb\u3002 \u3000\u3000\u9ad8\u5929\u5065\u867d\u7136\u6709\u2014\u8eab\u7edd\u4e16\u7684\u6b66\u529f\uff0c\u4f46\u4e5f\u65e0\u6cd5\u7834\u89e3\u8fd9\u51cc\u5389\u7edd\u4f26\u7684\u8ffd\u9b42\u4e03\u5251\u3002 \u3000\u3000\u4f46\u9ad8\u5929\u5065\u5b9e\u5728\u6709\u7740\u8fc7\u4eba\u4e4b\u80fd\uff0c\u5b9d\u5200\u8fde\u6325\uff0c\u5200\u5149\u62a4\u8eab\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u7adf\u7136\u51b2\u7834\u4e86\u5251\u8292\u800c\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u53f6\u957f\u9752\u7684\u51cc\u5389\u5251\u52bf\uff0c\u5168\u90fd\u6307\u5411\u4e0b\u5c60\u65e0\u65b9\u3002 \u3000\u3000\u5e78\u597d\uff0c\u5c60\u65e0\u65b9\u4e5f\u6709\u4e86\u5f88\u4e25\u5bc6\u7684\u6212\u5907\uff0c\u68d2\u5f71\u62a4\u8eab\uff0c\u5c01\u5f00\u4e86\u4e24\u5251\u3002 \u3000\u3000\u53f6\u957f\u9752\u53ca\u65f6\u6536\u4f4f\u4e86\u53f3\u624b\u3002 \u3000\u3000\u4f46\u8fd9\u4e00\u77ac\u529f\u592b\uff0c\u5374\u7ed9\u9ad8\u5929\u5065\u53ef\u4e58\u4e4b\u673a\u3002 \u3000\u3000\u8bba\u529f\u529b\uff0c\u4e5f\u8bb8\u5c60\u65e0\u65b9\u4f1a\u6bd4\u53f6\u957f\u9752\u6df1\u539a\uff0c\u5c01\u654c\u7684\u7ecf\u9a8c\uff0c\u4e5f\u6bd4\u53f6\u957f\u9752\u4e30\u5bcc\u3002 \u3000\u3000\u4f46\u53f6\u957f\u9752\u7684\u8ffd\u9b42\u4e03\u5251\uff0c\u5b9e\u5728\u5f88\u96be\u5bf9\u4ed8\u3002 \u3000\u3000\u9ad8\u5929\u5065\u51b3\u5fc3\u5148\u6740\u4e86\u53f6\u957f\u9752\u3002 \u3000\u3000\u4ed6\u7684\u5200\uff0c\u6709\u5982\u9b54\u5200\u4e00\u6837\uff0c\u5c31\u5728\u53f6\u957f\u9752\u4e00\u6536\u5251\u52bf\uff0c\u5df2\u5230\u4e86\u53f6\u957f\u9752\u7684\u524d\u80f8\u3002 \u3000\u3000\u53f6\u957f\u9752\u7684\u5251\u52bf\uff0c\u5df2\u88ab\u5c01\u5230\u4e86\u95e8\u5916\uff0c\u518d\u60f3\u6536\u56de\uff0c\u5df2\u81ea\u4e0d\u53ca\u3002 \u3000\u3000\u5e78\u597d\u5c60\u65e0\u65b9\u7684\u5982\u610f\u68d2\uff0c\u5feb\u70b9\u4e86\u8fc7\u53bb\uff0c\u70b9\u5411\u9ad8\u5929\u5065\u7684\u53f3\u80a9\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7684\u4e00\u5200\uff0c\u53ef\u80fd\u6d3b\u5288\u4e86\u53f6\u957f\u9752\uff0c\u4f46\u5374\u6ca1\u6709\u673a\u4f1a\u518d\u907f\u5f00\u8fd9\u4e00\u68d2\u3002 \u3000\u3000\u4ed6\u8981\u7528\u4e00\u6761\u53f3\u81c2\uff0c\u6362\u53d6\u53f6\u957f\u9752\u7684\u4e00\u6761\u547d\u3002 \u3000\u3000\u9ad8\u5929\u5065\u8fd9\u4e2a\u4eba\u7edd\u5bf9\u4e0d\u4f1a\uff0c\u56e0\u4e3a\u6740\u6b7b\u53f6\u957f\u9752\u800c\u635f\u4f24\u2014\u81c2\uff0c\u751a\u81f3\u6389\u4e00\u6839\u624b\u6307\uff0c\u4ed6\u4e5f\u4e0d\u5e72\uff0c\u4ed6\u6709\u4fe1\u5fc3\uff0c\u53ef\u4ee5\u518d\u5236\u9020\u51fa\u6740\u6b7b\u53f6\u957f\u9752\u7684\u673a\u4f1a\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u6536\u5200\u800c\u9000\uff0c\u4e00\u9000\u5c31\u662f\u4e00\u4e08\u591a\u8fdc\u3002 \u3000\u3000\u53f6\u957f\u9752\u5618\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\u5200\u6cd5\uff0c\u7b80\u76f4\u50cf\u9b54\u672f\u4e00\u6837\uff0c\u5ffd\u7136\u95f4\uff0c\u5c31\u5230\u4e86\u81f4\u547d\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u6211\u4eec\u5c31\u7b97\u8054\u624b\uff0c\u4e5f\u6ca1\u6709\u529e\u6cd5\u6253\u4e0b\u53bb\uff0c\u4e5f\u8bb8\u4ea6\u8001\u5f1f\u7684\u8feb\u9b42\u4e03\u5251\u53ef\u4ee5\u6321\u4ed6\u4e00\u9635\uff0c\u4f46\u5374\u5f00\u6cd5\u548c\u4ed6\u957f\u671f\u6301\u6218\u4e0b\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4ed6\u7684\u5200\u6cd5\uff0c\u5df2\u5230\u5fc3\u5200\u5408\u4e00\u4e4b\u5883\uff0c\u6211\u867d\u7136\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u7684\u5200\u6cd5\uff0c\u4f46\u5374\u542c\u4eba\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u5c60\u65e0\u65b9\uff0c\u4f60\u5012\u4e5f\u6709\u4e9b\u89c1\u8bc6\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u7ec3\u7684\u4ec0\u4e48\u5200\u6cd5\uff0c\u7adf\u6709\u5982\u6b64\u4e4b\u5a01\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4ed6\u7684\u5200\u6cd5\u5df2\u5230\u62db\u968f\u5ff5\u751f\u7684\u5883\u754c\uff0c\u65e0\u62db\u65e0\u5f0f\uff0c\u65e0\u5f62\u65e0\u76f8\uff0c\u968f\u624b\u6325\u5200\uff0c\u5c31\u6709\u7740\u6740\u4eba\u53d6\u547d\u7684\u529b\u91cf\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c31\u7b97\u5982\u6b64\uff0c\u54b1\u4eec\u4e5f\u5f97\u62fc\u547d\u4e00\u6218\uff0c\u4eca\u5bb5\u4e07\u5bb6\u5927\u9662\u4e4b\u4e2d\uff0c\u53ea\u6709\u4f60\u6211\u4e24\u4eba\uff0c\u53ef\u4ee5\u652f\u6491\u51e0\u4e2a\u56de\u5408\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u5341\u5e74\u4e0d\u7528\u5200\u4e86\uff0c\u5341\u5e74\u672a\u6740\u4eba\uff0c\u4eca\u5bb5\u5b9d\u5200\u51fa\u4e86\u9798\uff0c\u4e5f\u6740\u4e86\u4eba\uff0c\u591a\u6740\u4e24\u4eba\uff0c\u53c8\u6709\u4f55\u59a8\uff0c\u5341\u4e2a\u56de\u5408\u4e4b\u5185\uff0c\u5fc5\u53d6\u4f60\u4e24\u4eba\u4e4b\u547d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ee5\u4f60\u5200\u6cd5\u7684\u51cc\u5389\uff0e\u8fd9\u4e5f\u4e0d\u7b97\u5439\u725b\uff0c\u5c60\u5144\uff0c\u8bf7\u6682\u9000\u4e0b\uff0c\u6211\u5148\u72ec\u62fc\u4e00\u9635\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u54b1\u4eec\u4e24\u4eba\u8054\u624b\uff0c\u5c1a\u4e14\u975e\u4ed6\u4e4b\u654c\uff0c\u4f60\u4e00\u4eba\u5982\u4f55\u662f\u4ed6\u654c\u624b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8ffd\u9b42\u4e03\u5251\uff0c\u4ea6\u6709\u6240\u957f\uff0c\u5c0f\u5f1f\u4e00\u4eba\u72ec\u62fc\uff0c\u53ef\u628a\u5251\u62db\u4e4b\u5a01\uff0c\u53d1\u6325\u5230\u6781\u81f4\uff0c\u5c31\u7b97\u6b7b\u5728\u4ed6\u7684\u5200\u4e0b\uff0c\u4e5f\u53ef\u4ee5\u524a\u4ed6\u4e00\u5757\u76ae\u8089\u4e0b\u6765\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u662f\u8001\u6c5f\u6e56\uff0c\u8001\u6c5f\u6e56\u4e00\u70b9\u5c31\u900f\uff0c\u4ed6\u5df2\u542c\u51fa\u4e86\u53f6\u957f\u9752\u7684\u5f26\u5916\u4e4b\u97f3\u3002 \u3000\u3000\u90a3\u662f\u8bf4\uff0c\u4ed6\u7684\u5251\u6cd5\u4e4b\u4e2d\uff0c\u53e6\u6709\u5947\u62db\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u548c\u654c\u4eba\u540c\u5f52\u4e8e\u5c3d\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u4f24\u4e86\u5bf9\u65b9\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u8bf4\u7684\u4e5f\u662f\uff0c\u4f60\u652f\u5251\u72ec\u62fc\u4e2d\u6d32\u7b2c\u4e00\u5200\uff0c\u6b7b\u800c\u4f55\u61be\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u8c6a\u58ee\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u5c60\u5144\u66ff\u6211\u63a0\u9635\u3002\u201d \u3000\u3000\u957f\u5251\u4e00\u6325\uff0c\u6d12\u51fa\u4e00\u7247\u5251\u82b1\uff0c\u5e73\u62a4\u80f8\u524d\u3002 \u3000\u3000\u8ffd\u9b42\u4e03\u5251\uff0c\u672c\u5e94\u62a2\u5148\u673a\u653b\u654c\uff0c\u4f46\u8fd9\u4e00\u6b21\uff0c\u5374\u91c7\u53d6\u7684\u5b88\u52bf\uff0c\u6a2a\u5251\u4e0d\u51fa\u3002 \u3000\u3000\u9ad8\u5929\u5065\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ee4\u5e08\u78e8\u5251\u8001\u4eba\uff0e\u4e09\u5341\u5e74\u524d\u66fe\u548c\u6211\u54c1\u8317\u8bba\u5251\uff0c\u56db\u5341\u62db\u540e\uff0c\u4ed6\u81ea\u7518\u670d\u8f93\uff0c\u6211\u8bb0\u5f97\uff0c\u4ed6\u66fe\u7528\u8fc7\u2014\u62db\u2018\u8bf1\u654c\u6df1\u5165\u2019\uff0c\u597d\u50cf\u5c31\u662f\u4f60\u8fd9\u4e48\u4e00\u4e2a\u67b6\u5f0f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5bb6\u5e08\u751f\u5e73\u53ea\u8d25\u8fc7\u4e24\u6b21\uff0c\u4e00\u6b21\u7adf\u7136\u662f\u8d25\u5728\u4f60\u7684\u624b\u4e2d\uff01\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4e00\u6b21\uff0c\u6211\u4eec\u662f\u53e3\u8ff0\u6bd4\u62db\uff0c\u867d\u7136\u662f\u6218\u4e91\u5bc6\u5e03\uff0c\u4f46\u5374\u4e0d\u4f1a\u4f24\u4eba\uff0c\u8fd9\u4e00\u6b21\uff0c\u771f\u5200\u771f\u5251\uff0c\u53ef\u662f\u6709\u6027\u547d\u4e4b\u5371\u7684\u640f\u6597\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u665a\u8fdb\u5fc3\u4e2d\u6218\u5fd7\u6602\u626c\uff0c\u9601\u4e0b\u5c3d\u7ba1\u51fa\u5200\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\u7684\u5c0f\u4e8e\uff0c\u6211\u9002\u624d\u624b\u4e0b\u7559\u60c5\uff0c\u5b8c\u5168\u662f\u770b\u5728\u78e8\u5251\u8001\u4eba\u7684\u4efd\u4e0a\uff0c\u4f60\u5982\u6267\u610f\u627e\u6b7b\uff0c\u90a3\u5c31\u522b\u602a\u6211\u5200\u4e0b\u7edd\u60c5\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u6740\u673a\u65e9\u751f\uff0c\u7528\u4e0d\u7740\u5047\u6148\u60b2\uff0c\u6ee1\u53e3\u4ec1\u4e49\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u626c\u4e86\u4e00\u626c\u6d53\u7709\uff0c\u6cdb\u8d77\u4e01\u6ee1\u8138\u6740\u673a\uff0c\u9053\uff1a\u201c\u597d\uff01\u63a5\u5200\u3002\u201d \u3000\u3000\u53f3\u624b\u8f7b\u6325\uff0c\u4e03\u661f\u5200\u5316\u4e00\u5c61\u5bd2\u8292\uff0c\u6a2a\u91cc\u626b\u53bb\u3002 \u3000\u3000\u4ed6\u5200\u4e0a\u9020\u8be3\u5df2\u81f3\u7edd\u5883\uff0c\u968f\u624b\u6325\u6d12\u7686\u6210\u6587\u7ae0\uff0c\u53ea\u89c1\u95ea\u52a8\u5200\u5149\u4e2d\uff0c\u6ce2\u6ce2\u91cd\u91cd\u5bd2\u5f71\uff0c\u72b9\u5982\u5341\u6570\u628a\u5200\u4e00\u9f50\u653b\u6765\u4e00\u822c\u3002 \u3000\u3000\u53f6\u957f\u9752\u7684\u5251\u8d2f\u6ce8\u4e86\u4ed6\u5168\u90e8\u7684\u529f\u529b\uff0c\u654c\u4e0d\u52a8\u6211\u4e0d\u52a8\uff0c\u654c\u5df2\u52a8\uff0c\u6211\u5148\u52a8\uff0c\u672c\u662f\u5251\u4e2d\u5947\u5b66\uff0c\u53ef\u60dc\uff0c\u4ed6\u9047\u4e0a\u4e86\u9ad8\u5929\u5065\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u53f6\u957f\u9752\u867d\u7136\u6781\u5feb\u7684\u53d1\u51fa\u4e00\u5251\uff0c\u4f46\u4ecd\u65e0\u6cd5\u62a2\u5230\u5148\u673a\u3002 \u3000\u3000\u5251\u5982\u6d41\u661f\uff0c\u4f46\u5374\u6ca1\u5165\u90a3\u6ce2\u52a8\u91cd\u91cd\u7684\u5200\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u5f53\u7684\u4e00\u58f0\uff0c\u91d1\u94c1\u4ea4\u545c\uff0c\u53f6\u957f\u9752\u75be\u5411\u540e\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u4e0d\u9519\uff0c\u8fd9\u5c0f\u5b50\u7adf\u80fd\u5316\u89e3\u53bb\u6211\u8fd9\u4e00\u5200\uff0c\u770b\u6765\uff0c\u78e8\u5251\u8001\u4eba\u7684\u5251\u6cd5\uff0c\u53c8\u6709\u4e86\u5f88\u5927\u7684\u8fdb\u6b65\u3002 \u3000\u3000\u53f6\u957f\u9752\u975e\u9000\u4e0d\u53ef\uff0c\u5728\u4ed6\u7684\u611f\u89c9\u4e2d\uff0c\u5168\u8eab\u90fd\u8981\u88ab\u90a3\u91cd\u91cd\u7684\u5200\u5149\u6df9\u6ca1\uff0c\u5c31\u8981\u88ab\u56f4\u62e2\u7684\u5200\u5149\u6240\u4f24\u3002 \u3000\u3000\u4f46\u4ed6\u6bd5\u7adf\u8fd8\u662f\u9000\u51fa\u6765\u4e86\u3002 \u3000\u3000\u9ad8\u5929\u5065\u6545\u4f5c\u5e73\u6de1\uff0c\u6536\u5200\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u5982\u82e5\u8ba9\u6211\u5148\u51fa\u5200\uff0c\u4f60\u8fde\u4e09\u5200\u4e5f\u6491\u4e0d\u5230\uff0c\u8001\u592b\u8ba9\u4f60\u5148\u673a\uff0c\u7eb5\u7136\u65a9\u4f60\u4e8e\u8001\u592b\u7684\u5b9d\u5200\u4e4b\u4e0b\uff0c\u4e5f\u8981\u8ba9\u4f60\u6b7b\u7684\u5fc3\u670d\u53e3\u670d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5438\u4e00\u53e3\u6c14\uff0c\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u6325\u5251\u51fb\u51fa\u3002 \u3000\u3000\u8eab\u968f\u5251\u8d70\uff0c\u5251\u5316\u9752\u8679\uff0c\u8fde\u4eba\u5e26\u5251\u7684\u76f4\u51b2\u8fc7\u53bb\u3002 \u3000\u3000\u5f88\u6c14\u6d3e\u7684\u4e00\u5251\uff0c\u5145\u6ee1\u6740\u673a\u7684\u4e00\u5251\u3002 \u3000\u3000\u9ad8\u5929\u5065\u4e3e\u5200\u4e00\u6325\uff0c\u5ffd\u7136\u95f4\u5728\u8eab\u524d\u6d8c\u8d77\u4e86\u4e00\u7247\u5200\u5149\u3002 \u3000\u3000\u53f6\u957f\u9752\u5c31\u649e\u5728\u90a3\u5200\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u53c8\u662f\u4e00\u58f0\u91d1\u94c1\u76f8\u51fb\u3002 \u3000\u3000\u53f6\u957f\u9752\u5ffd\u7136\u95f4\u62e5\u6eda\u800c\u51fa\uff0c\u76f4\u6eda\u4e8c\u4e08\u4ee5\u5916\uff0c\u624d\u7ad9\u8d77\u4e86\u8eab\u5b50\uff0c\u4ed6\u7684\u5934\u53d1\u6563\u4e86\uff0c\u524d\u80f8\u4e0a\u7684\u8863\u670d\u7834\u4e86\uff0c\u53f3\u817f\u7684\u88e4\u5b50\u88c2\u4e86\uff0c\u80f8\u524d\u548c\u817f\u4e0a\uff0c\u4e24\u5904\u4f24\u75d5\uff0c\u4e0d\u505c\u7684\u6d8c\u51fa\u9c9c\u8840\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u5feb\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u54b1\u4eec\u8fd8\u662f\u8054\u624b\u62d2\u654c\u5427\uff01\u201d \u3000\u3000\u6021\u7ea2\u7a81\u7136\u5c16\u5fc3\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u7ed9\u6211\u95ea\u5f00\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u53c8\u76ee\u4e2d\u5947\u5149\u66b4\u5c04\uff0e\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8001\u592b\u5b9d\u5200\u4e4b\u4e0b\uff0c\u2014\u5411\u4e0d\u6740\u5987\u4eba\u5b7a\u5b50\uff0c\u4f46\u5982\u4f60\u65bd\u7528\u6697\u5668\uff0c\u90a3\u5c31\u53c8\u5f53\u522b\u8bba\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4f60\u80fd\u6740\u5468\u7eb6\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u4f60\u4e0d\u80fd\u6740\u7684\uff1f\u6211\u7528\u4e0d\u7528\u6697\u5668\uff0c\u90fd\u662f\u4e00\u6837\uff0c\u4f60\u53ea\u8981\u6709\u6740\u6211\u7684\u673a\u4f1a\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u653e\u8fc7\u6211\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u8fdb\u9000\u7684\u4e2b\u5934\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4f60\u4e5f\u53ef\u80fd\u4e0d\u6740\u6211\uff0c\u4e0d\u8fc7\uff0c\u90a3\u5e76\u975e\u4f60\u5fc3\u5b58\u601c\u60af\uff0c\u5bf9\u6211\u6148\u60b2\uff0c\u800c\u662f\u8981\u505a\u7ed9\u6d3b\u7684\u4eba\u770b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u4ec1\u6148\u5e76\u4e0d\u80fd\u4f7f\u6076\u4eba\u56de\u5934\uff0c\u65e2\u7136\u8001\u592b\u65e0\u6cd5\u5ea6\u5316\u4f60\u4eec\uff0c\u90a3\u53ea\u6709\u4ee5\u6740\u6b62\u6740\uff0c\u65a9\u8349\u9664\u6839\u4e86\uff0c\u201d \u3000\u3000\u6021\u7ea2\u8f7b\u8f7b\u5618\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u624b\u4e2d\u662f\u673a\u7c27\u63a7\u5236\u7684\u6bd2\u9488\uff0c\u529b\u52b2\u5f88\u5f3a\uff0c\u4e5f\u5c31\u662f\u4f60\u53e3\u4e2d\u6240\u8c13\u7684\u6b79\u6bd2\u6697\u5668\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u8bd5\u8bd5\u770b\u5427\uff01\u80fd\u4e0d\u80fd\u4f24\u4e86\u8001\u592b\uff0c\u6211\u7ed9\u4f60\u4eec\u8fd9\u4e2a\u673a\u4f1a\u3002\u201d \u3000\u3000\u5200\u6a2a\u53f3\u624b\uff0c\u7f13\u6b65\u5411\u524d\u884c\u6765\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u7a81\u7136\u4f20\u8fc7\u6765\u4e00\u4e2a\u6025\u4fc3\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u6021\u7ea2\u4e0d\u53ef\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5e2e\u4e3b\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u6536\u8d77\u4e86\u624b\u4e2d\u7684\u9489\u7b52\u3002 \u3000\u3000\u4f46\u89c1\u4e24\u6761\u4eba\u5f71\uff0c\u75be\u5954\u800c\u81f3\u3002 \u3000\u3000\u5f53\u5148\u4e00\u4eba\u8eab\u7740\u9752\u886b\uff0c\u6b63\u662f\u9634\u9633\u5821\u4e3b\u9ec4\u7075\u3002 \u3000\u3000\u7d27\u968f\u7740\u9ec4\u7075\u8eab\u540e\u4e00\u4eba\uff0c\u52b2\u88c5\u4f69\u5251\uff0c\u82f1\u4fca\u6f47\u6d12\uff0c\u4e43\u662f\u9634\u9633\u5821\u4e2d\u4e94\u5927\u5251\u4f7f\u4e2d\u7684\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u9ad8\u5929\u5065\u8fd8\u5200\u5165\u9798\uff0c\u80cc\u624b\u800c\u7acb\uff0c\u9053\uff1a\u201c\u6765\u7684\u53ef\u662f\u9634\u9633\u5821\u4e3b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6b63\u662f\u5728\u4e0b\uff0c\u9ad8\u5927\u4fa0\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u53d1\u73b0\u4e86\u5468\u7eb6\u7684\u5c38\u4f53\uff0c\u53cc\u76ee\u4e2d\u7acb\u65f6\u66b4\u5c04\u51fa\u4e24\u9053\u795e\u5149\uff0c\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u6740\u4e86\u5468\u7eb6\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u4e00\u8eac\u8eab\u9053\uff1a\u201c\u9ad8\u5929\u5065\u3002\u201d \u3000\u3000\u9ec4\u7075\u76ee\u5149\u4e00\u63a0\u9648\u6c9b\u3001\u5f6d\u9707\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4e2a\uff0c\u4e5f\u662f\u6b7b\u5728\u9ad8\u5927\u4fa0\u5200\u4e0b\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u9ec4\u7075\u7f13\u7f13\u5410\u51fa\u4e00\u53e3\u957f\u6c14\uff0c\u9053\uff1a\u201c\u6740\u9648\u6c9b\u3001\u5f6d\u9707\uff0c\u60c5\u6709\u53ef\u8bf4\uff0c\u9ad8\u5927\u4fa0\u5200\u5288\u5468\u7eb6\uff0c\u4e0d\u89c9\u7740\u592a\u8fc7\u5206\u5417\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4ed6\u672c\u53ef\u8eb2\u8fc7\u90a3\u4e00\u5200\u7684\uff0c\u4f46\u4ed6\u7adf\u7136\u4e0d\u80af\u95ea\u907f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e2d\u5dde\u7b2c\u4e00\u5200\uff0c\u5200\u52bf\u5df2\u5230\u4e86\u5200\u968f\u5fc3\u52a8\u7684\u5883\u754c\uff0c\u4f60\u5982\u65e0\u6740\u4ed6\u4e4b\u5fc3\uff0c\u5c82\u4f1a\u6536\u4e0d\u4f4f\u624b\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u53f9\u606f\u9053\uff1a\u201c\u5468\u7eb6\u4e00\u76f4\u4e0d\u76f8\u4fe1\u9ad8\u5927\u4fa0\u4f1a\u6740\u4ed6\uff0c\u6240\u4ee5\uff0c\u5168\u65e0\u6212\u5907\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\uff0c\u662f\u8fd9\u6837\u5417\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u906d\uff1a\u201c\u9634\u9633\u5821\u4e3b\uff0c\u4f60\u6562\u8d28\u95ee\u8001\u592b\uff01\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u6562\uff0c\u5468\u7eb6\u662f\u9634\u9633\u5821\u4e2d\u7684\u4eba\uff0c\u6211\u662f\u9634\u9633\u5821\u7684\u5821\u4e3b\uff0c\u5821\u4e2d\u5c5e\u4e0b\u88ab\u4eba\u5bb6\u6740\u4e86\uff0c\u6211\u5c82\u80fd\u4e0d\u95fb\u4e0d\u95ee\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u80fd\u95ee\u5f97\u4e86\u5417\uff1f\u201d \u3000\u3000\u9ec4\u7075\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8fc7\u662f\u4e2d\u5dde\u7b2c\u4e00\u5200\uff0c\u5e76\u975e\u5929\u4e0b\u7b2c\u4e00\u5200\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u9886\u5bfc\u9634\u9633\u5821\uff0c\u641e\u5f97\u6c5f\u6e56\u4e0a\u4e4c\u70df\u7634\u6c14\uff0c\u5374\u53c8\u9690\u8eab\u5e55\u540e\uff0c\u63a9\u85cf\u4f4f\u771f\u6b63\u9762\u76ee\uff0c\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u662f\u4ec0\u4e48\u4eba\uff0c\u5e76\u4e0d\u91cd\u8981\uff0c\u4f60\u9ad8\u5929\u5065\u624b\u4e2d\u7684\u4e03\u661f\u5b9d\u5200\uff0c\u5982\u679c\u628a\u6211\u7ed9\u6740\u4e86\uff0c\u6211\u662f\u8c01\uff0c\u90fd\u662f\u4e00\u6837\uff0c\u4f60\u82e5\u662f\u6740\u4e0d\u4e86\u6211\uff0c\u6211\u662f\u8c01\u4f60\u4e5f\u4e0d\u5fc5\u95ee\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u7eb5\u58f0\u800c\u7b11\uff0c\u9053\uff1a\u201c\u5899\u65e0\u767e\u53e3\u4e0d\u900f\u98ce\uff0c\u4e16\u4e0a\u5c82\u771f\u6709\u7edd\u5bf9\u9690\u79d8\u7684\u4e8b\uff0c\u4f60\u8ba4\u4e3a\uff0c\u4f60\u6545\u4f5c\u795e\u79d8\uff0c\u9020\u6210\u70df\u5e55\uff0c\u5c31\u771f\u7684\u80fd\u63a9\u76d6\u5929\u4e0b\u7684\u8033\u76ee\u5417\uff1f\u5176\u5b9e\uff0c\u77e5\u4f60\u8eab\u4efd\u7684\uff0c\u6c5f\u6e56\u53c8\u4f55\u6b62\u8001\u592b\u4e00\u4eba\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u65e2\u7136\u77e5\u9053\u6211\u662f\u8c01\uff0c\u53c8\u4f55\u7528\u591a\u95ee\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u662f\u5316\u8eab\u4e66\u751f\uff0c\u4e03\u5e74\u524d\u6c5f\u6e56\u4fa0\u4e49\u95e8\u4e2d\uff0c\u5927\u4e3e\u56f4\u653b\u7684\u91c7\u82b1\u5927\u76d7\u9ec4\u7075\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\u771f\u662f\u6709\u5fc3\u4eba\uff0c\u4f60\u8fd9\u4e00\u8bf4\uff0c\u5012\u4f7f\u5728\u4e0b\u660e\u767d\u4e86\u6c5f\u6e56\u4e0a\u4e0d\u5c11\u9690\u79d8\u3001\u5185\u60c5\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u660e\u767d\u4ec0\u4e48\u9690\u79d8\u3001\u5185\u60c5\uff0c\u90fd\u548c\u8001\u592b\u65e0\u5173\uff0c\u6211\u4e00\u751f\u884c\u7684\u7aef\uff0c\u7acb\u7684\u6b63\uff0c\u4ef0\u4e0d\u6127\u5929\uff0c\u4fef\u4e0d\u4f5c\u5730\uff0c\u6ca1\u6709\u4ec0\u4e48\u4e0d\u53ef\u544a\u4eba\u7684\u4e8b\uff0c\u5012\u662f\u4eca\u5929\uff0c\u4f60\u66b4\u9732\u4e86\u8eab\u5206\uff0c\u8001\u592b\u53ef\u4ee5\u653e\u5fc3\u7684\u5927\u5f00\u6740\u6212\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\u4e0d\u4f46\u5200\u6cd5\u7cbe\u7edd\uff0c\u5fc3\u673a\u7684\u6df1\u6c89\uff0c\u4e5f\u975e\u5e38\u4eba\u80fd\u53ca\uff0c\u4e0d\u9519\uff0c\u6211\u662f\u9ec4\u7075\uff0c\u8fc7\u53bb\uff0c\u6211\u786e\u6709\u55dc\u8272\u4e4b\u7656\uff0c\u4e0d\u8bba\u6211\u81ea\u5df1\u7684\u770b\u6cd5\u5982\u4f55\uff0c\u4f46\u6211\u786e\u5b9e\u4f24\u4e86\u5f88\u591a\u5c11\u5973\u7684\u5fc3\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e5f\u5f97\u5230\u4e86\u62a5\u5e94\uff0c\u4f60\u4eec\u780d\u4e86\u6211\u6570\u5341\u5200\uff0c\u53c8\u6bc1\u4e86\u6211\u7684\u5bb9\u8c8c\uff0c\u4f46\u6211\u4e00\u70b9\u4e5f\u4e0d\u4f1a\u8bb0\u6068\u4f60\u4eec\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u4f60\u5982\u6ca1\u6709\u62a5\u590d\u4e4b\u610f\uff0c\u4e3a\u5565\u4f1a\u6210\u7acb\u9634\u9633\u5821\uff0c\u6536\u7f57\u6c5f\u6d0b\u5927\u76d7\uff0c\u62db\u52df\u4ea1\u547d\u4e4b\u5f92\uff0c\u7528\u5fc3\u4f55\u5728\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u6211\u9ec4\u7075\u505a\u4ec0\u4e48\uff0c\u53ea\u8981\u81ea\u89c9\u95ee\u5fc3\u65e0\u6127\uff0c\u7528\u4e0d\u7740\u5411\u8c01\u89e3\u91ca\uff0c\u4f46\u4e8b\u60c5\u65e2\u7136\u6311\u660e\u4e86\uff0c\u6211\u5012\u8981\u548c\u4f60\u8fa8\u4e2a\u662f\u975e\u9ed1\u767d\u51fa\u6765\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u9634\u9633\u5821\u6210\u7acb\u4e86\u5f88\u591a\u5e74\uff0c\u800c\u4e14\uff0c\u4e5f\u5728\u6c5f\u6e56\u4e0a\u95ef\u51fa\u4e86\u76f8\u5f53\u7684\u5b57\u53f7\uff0c\u4f46\u6211\u5411\u4ec0\u4e48\u4eba\u62a5\u590d\u8fc7\uff1f\u6614\u5e74\u56f4\u6740\u6211\u7684\u4eba\uff0c\u6211\u77e5\u9053\u5341\u4e4b\u516b\u4e5d\uff0c\u75c5\u53df\u5468\u7eb6\u3001\u8ffd\u98ce\u53df\u3001\u548c\u4f60\u9ad8\u5927\u4fa0\uff0c\u90fd\u662f\u6614\u65e5\u53c2\u4e0e\u56f4\u6740\u6211\u7684\u4eba\uff0c\u4f46\u5468\u7eb6\u73b0\u5728\u662f\u6211\u7684\u670b\u53cb\uff0c\u4ed6\u4e14\u6b7b\u5728\u4f60\u7684\u5200\u4e0b\uff0c\u8ffd\u98ce\u53df\u9677\u8eab\u5728\u5927\u5df4\u5ddd\u4e1c\u4e09\u51f6\u624b\u4e2d\u65f6\uff0e\u5728\u4e0b\u6697\u4f38\u63f4\u624b\uff0c\u6551\u4ed6\u51fa\u9669\uff0c\u8fde\u59d3\u540d\u4e5f\u672a\u7559\u4e0b\u2026\u2026\u201d \u3000\u3000\u8ffd\u98ce\u53df\u63a5\u9053\uff1a\u201c\u4e09\u5e74\u524d\u5728\u5927\u5df4\u662f\u9601\u4e0b\u7684\u63f4\u624b\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5584\u4e8e\u4eba\u77e5\uff0c\u4e0d\u662f\u771f\u5584\uff0c\u5982\u8bf4\u884c\u4fa0\u4ed7\u4e49\uff0c\u6d4e\u56f0\u6276\u5371\uff0c\u8001\u592b\u8fd9\u6570\u5341\u5e74\u4f55\u81f3\u6570\u767e\u4ef6\uff0c\u4f46\u8001\u592b\u4ece\u672a\u5728\u4eba\u524d\u63d0\u8fc7\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\uff0c\u5927\u5df4\u5c71\u6211\u8bef\u4e2d\u4e86\u5ddd\u4e1c\u4e09\u51f6\u7684\u8be1\u8ba1\uff0c\u9677\u8eab\u706b\u7a9f\uff0c\u4eba\u5df3\u5feb\u88ab\u6d53\u70df\u85b0\u5012\uff0c\u90a3\u4eba\u5192\u9669\u51b2\u5165\u706b\u6d77\uff0c\u643a\u6211\u800c\u51fa\uff0c\u4e3a\u6551\u6211\u800c\u8863\u886b\u5c3d\u71c3\u3002\u9ad8\u5927\u4fa0\uff0c\u8fd9\u662f\u6551\u547d\u6069\u4eba\uff0c\u60c5\u540c\u518d\u9020\uff0c\u54b1\u4eec\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u4e4b\u4eba\uff0c\u8fd9\u7b49\u5927\u6069\uff0c\u5c82\u53ef\u4e0d\u62a5\uff1f\u4f55\u51b5\uff0c\u6211\u8fd8\u662f\u6614\u5e74\u4f24\u5bb3\u4ed6\u7684\u4ec7\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6b66\u5144\uff0c\u6211\u8bf4\u51fa\u6b64\u4e8b\uff0c\u53ea\u662f\u60f3\u5728\u9ad8\u5927\u4fa0\u7684\u9762\u524d\uff0c\u8bc1\u5b9e\u6211\u6ca1\u6709\u62a5\u590d\u4ec7\u6068\u4e4b\u5fc3\uff0c\u800c\u6b66\u5144\u53c8\u662f\u5728\u573a\u7684\u6700\u597d\u8bc1\u4eba\uff0c\u81f3\u4e8e\u5468\u7eb6\u5982\u4f55\u548c\u6211\u9ec4\u7075\u4ea4\u4e0a\u4e86\u670b\u53cb\uff0c\u8fd9\u4e2d\u95f4\u5185\u60c5\u4e0d\u8bf4\u4e5f\u7f62\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6b66\u5144\uff0c\u6267\u5927\u4e49\u4e0d\u62d8\u5c0f\u8282\uff0c\u5c31\u7b97\u662f\u9ec4\u7075\u771f\u7684\u6551\u8fc7\u4f60\uff0c\u4f46\u4ed6\u53ea\u6551\u4f60\u4e00\u6761\u547d\uff0c\u4ed6\u5728\u6c5f\u6e56\u4e0a\u9020\u6210\u7684\u7978\u5bb3\uff0c\u5374\u635f\u4f24\u5230\u5343\u767e\u4e07\u4eba\uff0c\u54b1\u4eec\u8eab\u4e3a\u4fa0\u4e49\u4e2d\u4eba\uff0c\u603b\u4e0d\u80fd\u56e0\u4e00\u5df1\u4e4b\u6069\u60c5\uff0c\u7d2f\u53ca\u4f17\u751f\u3002\u201d \u3000\u3000\u9ec4\u7075\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4eca\u5bb5\uff0c\u6211\u624d\u771f\u7684\u9886\u6559\u4e86\u4f60\u7684\u5389\u5bb3\uff0c\u4f60\u624b\u4e2d\u5b9d\u5200\u950b\u5229\uff0c\u4f46\u8fd8\u4e0d\u53ca\u4f60\u7684\u53e3\u820c\uff0c\u4f60\u6ee1\u53e3\u7684\u4ec1\u4e49\u3001\u9053\u5fb7\uff0c\u4f46\u4f60\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u5374\u5b8c\u5168\u4e0d\u662f\u90a3\u4e48\u56de\u4e8b\uff0c\u53e3\u662f\u5fc3\u975e\uff0c\u52a0\u5728\u4f60\u9ad8\u5927\u4fa0\u8eab\u4e0a\uff0c\u771f\u662f\u4e1d\u4e1d\u5165\u6263\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u7eb5\u58f0\u800c\u7b11\uff0c\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u8fd9\u4e0d\u662f\u4f60\u6211\u53e3\u820c\u4e0a\u7684\u4e89\u8fa9\uff0c\u5c31\u53ef\u4ee5\u5b9a\u8bba\u7684\u4e8b\uff0c\u8fd9\u8981\u5929\u4e0b\u516c\u8bba\uff0c\u8fd9\u8981\u4e8b\u5b9e\u8bc1\u660e\u3002\u201d \u3000\u3000\u9ec4\u7075\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u5343\u65b9\u767e\u8ba1\u5728\u6316\u6211\u7684\u6839\uff0c\u6211\u4e5f\u5728\u6478\u4f60\u7684\u5e95\u5b50\uff0c\u4f60\u77e5\u9053\u6211\u662f\u9ec4\u7075\uff0c\u6211\u4e5f\u627e\u51fa\u4e86\u4f60\u4e0d\u5c11\u52a3\u8ff9\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u8001\u592b\u6709\u4ec0\u4e48\u52a3\u8ff9\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8868\u9762\u4e0a\uff0c\u4f60\u662f\u4e2d\u539f\u7b2c\u4e00\u5927\u4fa0\uff0c\u5c31\u662f\u5f53\u4ee3\u5c11\u6797\u3001\u6b66\u5f53\u7684\u638c\u95e8\u4eba\uff0c\u4e10\u5e2e\u5e2e\u4e3b\uff0c\u4e5f\u90fd\u5bf9\u4f60\u656c\u754f\u51e0\u5206\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4f60\u6697\u4e2d\u7edf\u8f96\u4e86\u4e5d\u4e2a\u9ed1\u9053\u7ec4\u7ec7\uff0c\u5728\u6c5f\u6e56\u4e0a\u5174\u98ce\u4f5c\u6d6a\uff0c\u4f60\u7684\u52bf\u529b\uff0c\u5df2\u7ecf\u5ef6\u4f38\u5230\u6c5f\u5357\uff0c\u585e\u4e0a\u4ee5\u56db\u5bb6\u9556\u5c40\u4e3a\u63a9\u62a4\uff0c\u771f\u662f\u806a\u660e\u7edd\u4f26\u7684\u529e\u6cd5\uff0c\u601d\u8651\u7684\u5468\u5bc6\uff0c\u5b9e\u5728\u53eb\u4eba\u4f69\u670d\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8001\u592b\u5982\u8981\u5f00\u8bbe\u4e00\u5bb6\u9556\u5c40\u3002\u4ee5\u7ef4\u6301\u5546\u65c5\u5b89\u5168\uff0c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\u9519\uff0c\u53ea\u53ef\u60dc\u8001\u592b\u8fd8\u4e0d\u5c51\u4e3a\u4e4b\u5462\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\uff0c\u8fd9\u51e0\u53e5\u8bdd\uff0c\u8001\u673d\u4e5f\u4e0d\u6562\u82df\u540c\uff0c\u9ad8\u5927\u4fa0\u5982\u8981\u652f\u6301\u9556\u5c40\uff0c\u53ea\u8981\u652f\u6301\u4e00\u5bb6\u5c31\u884c\uff0c\u90a3\u624d\u80fd\u505a\u72ec\u95e8\u751f\u610f\uff0c\u4e3a\u4ec0\u4e48\u8981\u4e00\u4e0b\u5b50\u652f\u6301\u56db\u5bb6\u5462\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u541b\u5b50\u6b3a\u4e4b\u4ee5\u65b9\uff0c\u6b66\u5144\u662f\u541b\u5b50\u3001\u6240\u4ee5\u60f3\u4e0d\u5230\u9ad8\u5929\u5065\u7684\u5e03\u5c40\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u613f\u95fb\u9ec4\u5821\u4e3b\u7684\u9ad8\u8bba\uff01\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5929\u5065\u5982\u53ea\u652f\u6491\u4e00\u5bb6\u9556\u5c40\uff0e\u4f7f\u8fd9\u4e00\u5bb6\u9556\u5c40\u65e0\u5f80\u4e0d\u5229\uff0c\u5fc5\u4f1a\u5f15\u8d77\u522b\u4eba\u7684\u6000\u7591\uff0c\u4e5f\u4f1a\u5f15\u8d77\u6b66\u6797\u540c\u9053\u7684\u6ce8\u610f\u3002\u4f46\u56db\u5bb6\u5c31\u4e0d\u540c\u4e86\uff0c\u56db\u5bb6\u4e92\u76f8\u7ade\u4e89\uff0c\u5404\u6709\u6210\u5c31\uff0c\u8fd9\u4e2d\u95f4\u7684\u9053\u7406\uff0c\u6781\u5c3d\u66f2\u5f84\u901a\u5e7d\u4e4b\u5999\u3002\u8bd5\u60f3\u5357\u4e03\u5317\u516d\u2014\u5341\u4e09\u7701\uff0c\u4f55\u6b62\u6570\u767e\u5bb6\u9556\u5c40\uff0e\u9664\u4e86\u9ad8\u5929\u5065\u652f\u6301\u56db\u5bb6\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\uff0c\u90fd\u88ab\u9ad8\u5927\u4fa0\u7edf\u9886\u4e5d\u4e2a\u9ed1\u9053\u7ec4\u7ec7\u52ab\u53bb\u9556\u94f6\uff0c\u957f\u6b64\u4e0b\u53bb\uff0c\u6570\u767e\u5bb6\u9556\u5c40\uff0c\u90fd\u8981\u5173\u95e8\uff0c\u5929\u4e0b\u7684\u4fdd\u9556\u751f\u610f\uff0c\u90fd\u843d\u5728\u4e86\u90a3\u56db\u5bb6\u9556\u5c40\u624b\u4e2d\uff0c\u65e5\u540e\uff0c\u4ed6\u4eec\u53ea\u8981\u5212\u533a\u5206\u4fdd\uff0c\u5c31\u53d8\u6210\u4e86\u72ec\u5bb6\u751f\u610f\uff0c\u90a3\u65f6\u4ed6\u4eec\u5c31\u53ef\u4ee5\u4e88\u53d6\u4e88\u6c42\u4e86\u3002\u81f3\u4e8e\u90a3\u4e9b\u6b66\u529f\u9ad8\u5f3a\u7684\u9556\u5e08\uff0c\u9ad8\u5927\u4fa0\u4e5f\u4f1a\u793c\u8058\u4ed6\u4eec\u5206\u522b\u7684\u52a0\u5165\u90a3\u56db\u5bb6\u9556\u5c40\uff0c\u56db\u5bb6\u9556\u5c40\uff0c\u4e00\u4e2a\u4e3b\u4eba\uff0c\u5c40\u91cc\u7684\u9556\u5e08\uff0c\u4e5f\u5c31\u6210\u4e86\u9ad8\u5927\u4fa0\u7684\u5c5e\u4e0b\u4e86\uff0c\u9664\u4e86\u4fdd\u9556\u4e4b\u5916\uff0c\u8fd8\u53ef\u4ee5\u66ff\u9ad8\u5927\u4fa0\u529e\u4efb\u4f55\u4e8b\u60c5\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u529e\u6cd5\u5b9e\u5728\u9ad8\u660e\uff1a\u201d \u3000\u3000\u9ad8\u5929\u5065\u7684\u795e\u8272\u9634\u6674\u4e0d\u5b9a\uff0c\u4f46\u4ed6\u5e76\u672a\u53d1\u4f5c\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u8bf4\u8001\u592b\u64cd\u7eb5\u56db\u5bb6\u9556\u5c40\uff0c\u662f\u54ea\u56db\u5bb6\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u5982\u679c\u8fde\u8fd9\u4e00\u70b9\u4e5f\u672a\u67e5\u51fa\u6765\uff0c\u5c82\u4e0d\u662f\u542b\u8840\u55b7\u4eba\u4e86\uff0c\u90a3\u56db\u5bb6\u9556\u5c40\u5b50\u7684\u540d\u5b57\uff0c\u8d77\u7684\u5f88\u5999\uff0c\u540d\u5b57\u4e5f\u4e92\u4e0d\u5173\u8fde\uff0c\u4efb\u4f55\u4eba\uff0c\u5355\u4ece\u540d\u5b57\u4e0a\u770b\uff0c\u8c01\u4e5f\u77a7\u4e0d\u51fa\u5fc6\u4eec\u6709\u5173\u7cfb\uff1b\u8fd9\u56db\u5bb6\u5c31\u662f\u516b\u65b9\u3001\u632f\u6b66\u3001\u96c4\u98ce\u3001\u795e\u5a01\u3002\u201d \u3000\u3000\u8ffd\u98ce\u53df\u9053\uff1a\u201c\u55ef\uff0c\u8fd9\u56db\u5bb6\u9556\u5c40\u5b50\uff0c\u5d1b\u8d77\u4e0d\u8fc7\u4e94\u516d\u5e74\uff0c\u4f46\u751f\u610f\u90fd\u5f88\u5174\u9686\uff0c\u800c\u4e14\u90fd\u5f00\u8bbe\u4e86\u4e0d\u5c11\u5206\u5c40\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u5730\u770b\u4e86\u8ffd\u98ce\u53df\u4e00\u773c\uff0c\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u8fd9\u56db\u5bb6\u9556\u5c40\u5b50\uff0c\u4f60\u786c\u628a\u4ed6\u4eec\u548c\u8001\u592b\u626f\u4e86\u5173\u7cfb\u3002\u4f46\u4f60\u6709\u4ec0\u4e48\u8bc1\u660e\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u73b0\u5728\uff0c\u8fd8\u4e0d\u662f\u8bf4\u51fa\u6765\u7684\u65f6\u5019\uff0c\u6211\u4e0d\u80fd\u8981\u4f60\u53bb\u6bc1\u706d\u8bc1\u636e\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u54e6\uff01\u4ec0\u4e48\u65f6\u5019\uff0c\u624d\u662f\u4f60\u8bf4\u51fa\u6765\u7684\u65f6\u5019\u5462\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u660e\u5e74\u4e8c\u6708\u4e8c\u65e5\uff0c\u5357\u5cb3\u8861\u5c71\u8fe5\u5cf0\u4e0a\uff0c\u5929\u4e0b\u82f1\u96c4\u5927\u4f1a\uff0c\u5404\u5927\u95e8\u6d3e\u7684\u91cd\u8981\u4eba\u7269\u90fd\u8981\u53c2\u4e0e\uff0c\u4f60\u9ad8\u5927\u4fa0\u81ea\u7136\u662f\u5927\u4f1a\u8d35\u5bbe\uff0c\u90a3\u65f6\uff0c\u6211\u81ea\u4f1a\u62ff\u51fa\u8bc1\u636e\uff0c\u8ba9\u4f60\u5fc3\u670d\u53e3\u670d\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u53ef\u60dc\u4f60\u4eca\u591c\u975e\u8bf4\u4e0d\u53ef\uff0c\u5426\u5219\u4f60\u5c31\u6c38\u8fdc\u6ca1\u6709\u673a\u4f1a\u8bf4\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\u53ef\u662f\u60f3\u6740\u4eba\u706d\u53e3\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u4e3a\u6b66\u6797\u9664\u5bb3\u3002\u201d \u3000\u3000\u4ed6\u7684\u4e3e\u6b62\u5f88\u6f47\u6d12\uff0c\u5e76\u6765\u4e3a\u9ec4\u7075\u7684\u8a00\u8bed\u6240\u6fc0\u52a8\uff0c\u624b\u63e1\u4e03\u661f\u5200\u67c4\uff0c\u7f13\u7f13\u5411\u524d\u5730\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7a81\u7136\u4e00\u4e0a\u6b65\uff0c\u62e6\u5728\u4e86\u9ec4\u7075\u7684\u8eab\u524d\uff0c\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\uff0c\u8ba4\u8bc6\u533a\u533a\u5728\u4e0b\u5417\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u6253\u91cf\u4e86\u4f55\u5bd2\u8863\u4e00\u9635\uff0c\u6447\u6447\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\u4e0d\u8ba4\u8bc6\u5728\u4e0b\u4e5f\u597d\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\u8ba4\u4e0d\u8ba4\u8bc6\u5728\u4e0b\uff0c\u90fd\u662f\u4e00\u6837\uff0c\u4f60\u8981\u6740\u7684\u4eba\uff0c\u4e0d\u8bba\u4f60\u662f\u5426\u8ba4\u8bc6\uff0c\u90fd\u8981\u6740\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u9ad8\u67d0\u5200\u4e0b\u6a2a\u5c38\u7684\u4eba\uff0c\u90fd\u662f\u6b66\u6797\u8d25\u7c7b\uff0c\u6c5f\u6e56\u76d7\u532a\uff0c\u4ece\u6765\u4e0d\u6740\u516c\u6b63\u3001\u6e05\u660e\u7684\u6b66\u6797\u540c\u9053\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u75c5\u53df\u5468\u7eb6\u4e0d\u662f\u6b66\u6797\u4e2d\u516c\u8ba4\u8d25\u7c7b\uff0c\u4ed6\u53f7\u79f0\u5927\u4fa0\uff0c\u5728\u6c5f\u6e56\u540c\u9053\u7684\u5fc3\u76ee\u4e2d\uff0c\u51b3\u4e0d\u662f\u6b66\u6797\u8d25\u7c7b\uff0c\u4f46\u4f60\u9ad8\u5927\u4fa0\u60f3\u6740\u4ed6\uff0c\u8fd8\u4e0d\u662f\u4e00\u5200\u6740\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5175\u5203\u65e0\u773c\uff0c\u96be\u514d\u8bef\u4f24\uff0c\u4ed6\u6295\u5165\u9634\u9633\u5821\uff0c\u4e5f\u6709\u8be5\u6b7b\u4e4b\u9053\uff0c\u4e00\u4e2a\u4eba\u665a\u8282\u4e0d\u4fdd\uff0c\u906d\u6b64\u62a5\u5e94\uff0c\u867d\u7136\u4e0d\u65e0\u9057\u61be\uff0c\u4f46\u4e5f\u7b97\u662f\u548e\u7531\u81ea\u53d6\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\u7ec8\u4e8e\u9732\u51fa\u4f60\u771f\u6b63\u9762\u76ee\u4e86\uff0c\u4e5f\u8bf4\u51fa\u4e86\u4f60\u8981\u8bf4\u7684\u8bdd\uff0c\u8fd9\u4e9b\u5e74\uff0c\u54b1\u4eec\u4e00\u76f4\u5728\u8ffd\u5bfb\u6c5f\u6e56\u4e0a\u7684\u4e71\u6e90\u4f55\u5728\uff1f\u8d39\u4e86\u6570\u5e74\u82e6\u5fc3\uff0c\u624d\u67e5\u51fa\u4e86\u4e00\u70b9\u7709\u76ee\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8001\u592b\u65e0\u6687\u548c\u4f60\u4eec\u591a\u8d39\u53e3\u820c\uff0c\u4f60\u4e00\u4e2a\u65e0\u540d\u5c0f\u5352\uff0c\u8001\u592b\u4e0d\u613f\u6740\u4f60\uff0c\u8bf7\u8ba9\u5f00\u4e00\u6b65\uff0c\u8001\u592b\u8981\u6740\u9ec4\u7075\uff0c\u4ee5\u6170\u5468\u7eb6\u4e4b\u7075\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u60f3\u6740\u9ec4\u7075\uff0c\u81f3\u5c11\u5148\u5f97\u6740\u4e86\u5728\u4e0b\u3002\u201d \u3000\u3000\u9ec4\u7075\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u4f60\u95ea\u5f00\uff0c\u4ed6\u5fd7\u5728\u6740\u6211\uff0c\u5982\u82e5\u4e0d\u8ba9\u4ed6\u8bd5\u8bd5\uff0c\u53ea\u6015\u4ed6\u6c38\u4e0d\u7518\u5fc3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ed6\u7684\u5200\uff0c\u542c\u8bf4\u5df2\u5230\u4e86\u65e0\u62db\u65e0\u5f0f\uff0c\u968f\u5fc3\u6240\u6b32\u4e4b\u5883\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4ed6\u6740\u4e0d\u4e86\u6211\uff0c\u4ed6\u4f1a\u98df\u4e0d\u7518\u5473\uff0c\u5e2d\u4e0d\u5b89\u6795\uff0c\u56e0\u4e3a\uff0c\u6211\u90a3\u51e0\u53e5\u8bdd\uff0c\u89e6\u75db\u4e86\u4ed6\u7684\u5fc3\uff0c\u9ad8\u5927\u4fa0\u8981\u5229\u4e5f\u8981\u540d\uff0c\u4ed6\u8981\u5e7f\u805a\u5929\u4e0b\u7684\u8d22\u5bcc\uff0c\u4ed6\u8981\u7edf\u6cbb\u6c5f\u6e56\uff0c\u53f7\u4ee4\u6b66\u6797\uff0c\u4e5f\u8981\u4fdd\u6301\u4ed6\u5728\u6b66\u6797\u4e2d\u7684\u8eab\u4efd\u3001\u5730\u4f4d\uff0c\u5982\u82e5\u6211\u5728\u6765\u5e74\u4e8c\u6708\u4e8c\u65e5\u7684\u8861\u5c71\u82f1\u96c4\u5927\u4f1a\u4e0a\u63ed\u7a7f\u4e86\u9ad8\u5927\u4fa0\u7684\u9690\u79d8\uff0c\u90a3\u5c82\u4e0d\u662f\u5927\u6740\u98ce\u666f\u7684\u4e8b\u4e48\uff0c\u4ed6\u5982\u4e0d\u6740\u4e86\u6211\uff0c\u5c82\u80fd\u5b89\u5f97\u4e0b\u5fc3\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u7a81\u7136\u7eb5\u58f0\u800c\u7b11\uff0c\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u9634\u9633\u5821\u4e3b\uff0c\u4f60\u4e0d\u89c9\u7740\u592a\u8fc7\u9ad8\u62ac\u81ea\u5df1\u4e86\u5417\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\uff0c\u6211\u9ec4\u7075\u52ab\u540e\u4f59\u751f\uff0c\u51b3\u5fc3\u8981\u628a\u540e\u534a\u4e16\u7684\u6027\u547d\uff0c\u6295\u6ce8\u5728\u4e00\u4ef6\u5f88\u6709\u610f\u4e49\u7684\u5de5\u4f5c\u4e0a\uff0c\u603b\u7b97\u7687\u5929\u4e0d\u8d1f\u82e6\u5fc3\u4eba\uff0c\u88ab\u6211\u627e\u51fa\u4e86\u8fd9\u4e8c\u5341\u5e74\u6765\u6c5f\u6e56\u4e0a\u7684\u771f\u6b63\u4e71\u6e90\u4e86\uff0c\u6211\u9ec4\u7075\u5c31\u7b97\u4e3a\u6b64\u800c\u6b7b\uff0c\u4e5f\u6b7b\u5f97\u7791\u76ee\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u8ba4\u4e3a\u4f60\u8fd9\u4e00\u756a\u5531\u505a\u4ff1\u4f73\u7684\u8868\u6f14\uff0c\u771f\u80fd\u4f7f\u6c5f\u6e56\u670b\u53cb\u4eec\u76f8\u4fe1\u5417\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e8b\u5b9e\u4ff1\u5728\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u76f8\u4fe1\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u5982\u4eca\u591c\u4e4b\u4e2d\uff0c\u575a\u6301\u6740\u4f60\uff0c\u4f7f\u522b\u4eba\u8bef\u8ba4\u6211\u9ad8\u67d0\u4eba\u771f\u7684\u6015\u4f60\u5728\u4e8c\u6708\u4e8c\u65e5\u7684\u82f1\u96c4\u5927\u4f1a\u4e4b\u4e0a\uff0c\u63ed\u7a7f\u6211\u4ec0\u4e48\u9690\u79d8\uff1b\u771f\u91d1\u4e0d\u6015\u706b\uff0c\u5c31\u7b97\u4f60\u4eec\u6709\u4ec0\u4e48\u8bbe\u8ba1\u597d\u7684\u8bec\u9677\uff0c\u6211\u4e0d\u4fe1\uff0c\u51ed\u4f60\u9ec4\u7075\u5c31\u80fd\u53cc\u624b\u906e\u5929\uff01\u4e0d\u8fc7\uff0c\u660e\u5e74\u4e8c\u6708\u4e8c\u65e5\uff0c\u4f60\u4e00\u5b9a\u5f97\u5230\u8861\u5c71\u82f1\u96c4\u5927\u4f1a\u4e0a\u53bb\uff0c\u8001\u592b\u8981\u548c\u4f60\u5f53\u9762\u5bf9\u8d28\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d\uff01\u5e0c\u671b\u4f60\u9ad8\u5927\u4fa0\u5c4a\u65f6\u80fd\u591f\u5982\u7ea6\u800c\u81f3\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e00\u8a00\u4e3a\u5b9a\u3002\u201d \u3000\u3000\u56de\u987e\u4e86\u8ffd\u98ce\u53df\u4e00\u773c\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8d70\uff01\u201d \u3000\u3000\u9ec4\u7075\u8f7b\u8f7b\u5618\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u9ad8\u5927\u4fa0\u597d\u8d70\uff01\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u4e0d\u7528\u505a\u4f5c\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u672a\u518d\u591a\u8a00\uff0c\u76ee\u7779\u9ad8\u5929\u5065\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u5728\u4e0b\u89c9\u7740\uff0c\u521a\u624d\uff0c\u8981\u5c5e\u4e0b\u63a5\u4ed6\u4e24\u62db\u5200\u6cd5\uff0c\u518d\u653e\u4ed6\u79bb\u5f00\u624d\u662f\u3002\u201d \u3000\u3000\u9ec4\u7075\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u6211\u4eec\u867d\u7136\u53ef\u4ee5\u548c\u4ed6\u4e00\u6218\uff0c\u4f46\u5b8c\u5168\u6ca1\u6709\u628a\u63e1\u7684\u4e8b\uff0c\u505a\u8d77\u6765\uff0c\u4e0d\u4f46\u6ca1\u6709\u610f\u4e49\uff0c\u800c\u4e14\uff0c\u4e5f\u5f88\u5bb9\u6613\u7834\u574f\u81ea\u5df1\u7684\u8ba1\u5212\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u679c\u521a\u624d\u9ad8\u5929\u5065\u4e00\u5b9a\u8981\u6325\u5200\u4e00\u6218\u5462\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u90a3\u4ed6\u5c31\u4e0d\u662f\u9ad8\u5929\u5065\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\u548c\u4f55\u5144\u672a\u5230\u4e4b\u524d\uff0c\u9ad8\u5929\u5065\u4f3c\u4e4e\u662f\u5df2\u52a8\u4e86\u6740\u5bb3\u6211\u4eec\u7684\u7528\u5fc3\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bf9\uff01\u90a3\u65f6\u523b\uff0c\u4ed6\u6709\u7740\u5fc5\u80dc\u7684\u628a\u63e1\uff0c\u4f46\u6211\u548c\u5bd2\u8863\u73b0\u8eab\u4e4b\u540e\uff0c\u4ed6\u5ffd\u7136\u611f\u89c9\u5230\u6ca1\u6709\u628a\u63e1\u4e86\uff0c\u9ad8\u5929\u5065\u7edd\u5bf9\u4e0d\u505a\u6ca1\u6709\u628a\u63e1\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9ad8\u5929\u5065\u5982\u679c\u62c5\u5fc3\u5821\u4e3b\u4f1a\u5728\u6765\u5e74\u4e8c\u6708\u4e8c\u65e5\uff0c\u63ed\u7a7f\u4e86\u4ed6\u7684\u9690\u79d8\uff0c\u8fd9\u4e00\u5e74\u4e4b\u5185\uff0c\u5fc5\u7136\u4f1a\u60f3\u529e\u6cd5\uff0c\u5bf9\u4ed8\u54b1\u4eec\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u624d\u662f\u8981\u8ba1\u5212\u9632\u5907\u7684\u4e8b\u60c5\uff0c\u6211\u4eec\u56de\u53bb\u5546\u91cf\u5427\uff01\u201d \u3000\u3000 \u3000\u3000\u53f6\u957f\u9752\u7531\u6c89\u7761\u4e2d\u9192\u6765\uff0c\u8eab\u8fb9\u7684\u4f24\u75bc\uff0c\u5df2\u7ecf\u597d\u4e86\u5341\u4e4b\u516b\u4e5d\uff0c\u7f13\u7f13\u5750\u8d77\u8eab\u5b50\u65f6\uff0c\u53d1\u89c9\u6021\u7ea2\u5728\u5e8a\u4fa7\u5750\u7740\uff0c\u8138\u4e0a\u6cdb\u7740\u5fae\u5fae\u7684\u7b11\u610f\uff0c\u9053\uff1a\u201c\u7237\uff0c\u4f60\u6e05\u9192\u8fc7\u6765\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5618\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u5821\u4e3b\u5462\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4eca\u5929\u4e00\u5927\u65e9\u5c31\u8d70\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8d70\u4e86\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u662f\uff01\u4ed6\u4eec\u770b\u516c\u5b50\u7761\u5f97\u5341\u5206\u9999\u751c\uff0c\u4e0d\u4fbf\u60ca\u6270\uff0c\u6240\u4ee5\uff0c\u5148\u8d70\u4e86\u4e00\u6b65\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u5462\uff1f\u600e\u4e48\u80fd\u628a\u6211\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u7237\u4e0d\u7528\u7126\u6025\uff0c\u5821\u4e3b\u5df2\u7ecf\u4ea4\u4ee3\u8fc7\u4e86\u4f60\u7684\u53bb\u5904\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u6211\u5230\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u516c\u5b50\uff0c\u518d\u8eba\u4e00\u4f1a\uff0c\u63a5\u4f60\u7684\u4eba\uff0c\u5927\u6982\u8981\u5230\u665a\u4e0a\u624d\u6765\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u665a\u4e0a\uff0c\u8fd8\u6709\u4eba\u6765\u63a5\u6211\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u662f\uff01\u5821\u4e3b\u4f30\u7b97\u8fc7\u4e86\u4ed6\u7684\u836f\u529b\uff0e\u4e5f\u8bb8\u7b97\u8fc7\u4f60\u7684\u4f24\u52bf\uff0c\u5927\u6982\u5230\u5929\u8272\u5165\u591c\u7684\u65f6\u5206\uff0c\u624d\u80fd\u5b8c\u5168\u6536\u53e3\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f55\u5251\u4f7f\u5462\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u8ddf\u5821\u4e3b\u4e00\u8d77\u8d70\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5251\u4f7f\u5462\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u5c60\u5251\u4f7f\u4f24\u52bf\u6bd4\u4f60\u8fd8\u91cd\u4e9b\uff0c\u5927\u6982\u8fd8\u8981\u4e2a\u4e24\u4e09\u5929\u624d\u80fd\u590d\u5143\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u6765\u56de\u8d70\u52a8\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u5c60\u5251\u4f7f\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4f60\u8981\u89c1\u89c1\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u5ffd\u7136\u5bf9\u6021\u7ea2\u52a8\u4e86\u6000\u7591\uff0c\u89c9\u7740\u5f88\u591a\u4e8b\u5e94\u8be5\u4fdd\u7559\u4e00\u4e9b\u3002\u7b11\u9053\uff1a\u201c\u662f\uff01\u54b1\u4eec\u53bb\u770b\u770b\u4ed6\u3002\u201d \u3000\u3000\u6021\u7ea2\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff01\u6211\u7ed9\u7237\u5e26\u8def\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7684\u4f24\u52bf\u8fd8\u6ca1\u6709\u75ca\u6108\uff0c\u4ecd\u7136\u9759\u9759\u7684\u8eba\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c60\u5144\uff0c\u4f24\u5f97\u5f88\u91cd\u5417\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u597d\u591a\u4e86\uff0c\u5144\u5f1f\u7684\u4f24\u52bf\u5168\u597d\u4e86\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\u4e86\u5341\u4e4b\u516b\u4e5d\uff0c\u5c60\u5144\uff0c\u5821\u4e3b\u548c\u4f55\u5144\u90fd\u8d70\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u4f60\u5fc3\u4e2d\u6709\u5f88\u591a\u7684\u7591\u95ee\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u662f\uff01\u5c60\u5144\uff0c\u5144\u5f1f\u5fc3\u4e2d\uff0c\u786e\u6709\u5f88\u591a\u4e0d\u89e3\u4e4b\u5904\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u5821\u4e3b\u65e9\u5df2\u60f3\u5230\u4e86\uff0c\u8001\u5f1f\uff0c\u6709\u4ec0\u4e48\u4e8b\uff0c\u4f60\u5c3d\u7ba1\u95ee\u6211\uff0c\u4ed6\u8981\u6211\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\u201c\u5c60\u5144\uff0c\u5821\u4e3b\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e3a\u4e86\u5bf9\u4ed8\u9ad8\u5929\u5065\uff0c\u65e9\u4f5c\u5e03\u7f6e\u53bb\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\u5462\uff1f\u201d \u3000\u3000\u5c51\u65e0\u65b9\u906d\uff1a\u201c\u5821\u4e3b\u5df2\u7ecf\u5206\u6d3e\u4e86\u5144\u5f1f\u7684\u5de5\u4f5c\uff0c\u9634\u9633\u5821\u7531\u6b64\u523b\u8d77\uff0c\u8981\u7531\u660e\u5165\u6697\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u90a3\u4e48\u5144\u5f1f\u8981\u5e72\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5821\u4e3b\u5f88\u770b\u91cd\u4f60\uff0c\u8fd9\u4e00\u6b21\uff0c\u542c\u8bf4\u5206\u6d3e\u4f60\u8001\u5f1f\u4e00\u4e2a\u5f88\u91cd\u8981\u7684\u804c\u53f8\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\uff0c\u53ef\u77e5\u9053\u5821\u4e3b\u6d3e\u7ed9\u5144\u5f1f\u7684\u804c\u53f8\u5417\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8be6\u7ec6\u7684\u60c5\u5f62\uff0c\u5728\u4e0b\u5c31\u4e0d\u5f88\u6e05\u695a\u4e86\uff0c\u4e0d\u8fc7\uff0c\u4f55\u8001\u5f1f\u9690\u9690\u7ea6\u7ea6\u900f\u9732\u8fc7\uff0c\u597d\u50cf\u8981\u4f60\u8001\u5f1f\u9886\u5bfc\u4e00\u6279\u4eba\u624b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u5c60\u5144\uff0c\u53ef\u77e5\u9053\u8981\u5144\u5f1f\u9886\u5bfc\u4e00\u6279\u4ec0\u4e48\u6837\u7684\u4eba\u4e48\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5821\u4e3b\u624d\u6c14\u7eb5\u6a2a\uff0c\u6709\u4e00\u4e9b\u5e03\u7f6e\uff0c\u54b1\u4eec\u5f88\u96be\u5b8c\u5168\u731c\u5230\uff0c\u800c\u4e14\uff0c\u4ed6\u4e0d\u628a\u4e8b\u60c5\u8bb2\u5f97\u5f88\u6e05\u695a\uff0c\u60f3\u5fc5\u6709\u4ed6\u7684\u9053\u7406\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u53f6\u5144\u5fc3\u4e2d\u8fd8\u6709\u4ec0\u4e48\u4e0d\u6ee1\u610f\u7684\u5730\u65b9\uff0c\u5c3d\u7ba1\u544a\u8bc9\u5728\u4e0b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u5728\u60f3\uff0c\u54b1\u4eec\u6240\u4f5c\u6240\u4e3a\uff0c\u7a76\u7adf\u662f\u5bf9\u662f\u9519\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u5f1f\u5fc3\u4e2d\u53ef\u662f\u5bf9\u5821\u4e3b\u6709\u4e86\u6000\u7591\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u662f\u5728\u60f3\u54b1\u4eec\u53d6\u5f97\u90a3\u4e9b\u5723\u6c34\uff0c\u5230\u5e95\u80fd\u505a\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u5821\u4e3b\u7559\u8fc7\u8bdd\uff0c\u4ed6\u8bf4\uff0c\u4f60\u5982\u5bf9\u4ed6\u5931\u53bb\u4e86\u4fe1\u4efb\uff0c\u8fd9\u91cc\u7edd\u4e0d\u5f3a\u7559\u4e0b\u4f60\uff0c\u597d\u5728\u6765\u5e74\u4e8c\u6708\u4e8c\u65e5\uff0c\u771f\u8c61\u5373\u53ef\u5927\u767d\uff0c\u90a3\u65f6\u53f6\u8001\u5f1f\u53ef\u4ee5\u660e\u767d\u771f\u8c61\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5618\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5c60\u5144\uff0c\u4f60\u5bf9\u54b1\u4eec\u9634\u9633\u5821\u7684\u4f5c\u6cd5\uff0c\u662f\u5426\u6709\u4e9b\u6000\u7591\u5462\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4e0d\uff01\u5728\u4e0b\u5bf9\u5821\u4e3b\u7edd\u5bf9\u7684\u4fe1\u4efb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\uff01\u5bf9\u5821\u4e3b\uff0c\u5728\u4e0b\u4e86\u5e76\u975e\u4e0d\u4fe1\u4efb\uff0c\u53ea\u662f\u89c9\u7740\u6709\u4e9b\u4e8b\uff0c\u54b1\u4eec\u5e94\u8be5\u660e\u767d\u5185\u60c5\uff0c\u5e94\u5bf9\u4e3e\u63aa\u4e4b\u95f4\uff0c\u4e5f\u597d\u65e9\u4e9b\u6709\u4e2a\u51c6\u5907\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u8001\u5f1f\uff0c\u9634\u9633\u5821\u6709\u4e00\u4e2a\u5f88\u5927\u7684\u53d8\u5316\uff0c\u5821\u4e3b\u5fc5\u987b\u8981\u4f5c\u5f88\u591a\u5e94\u53d8\u63aa\u65bd\uff0c\u6240\u4ee5\uff0c\u4ed6\u65e0\u6cd5\u7559\u4e0b\u6765\u7b49\u4f60\uff0c\u6240\u4ee5\u5148\u8d70\u4e86\u4e00\u6b65\uff0c\u4e0d\u8fc7\uff0c\u6211\u60f3\u8fd1\u65e5\u4e4b\u5185\uff0c\u5927\u4e8b\u5fd9\u5b8c\uff0c\u5b9a\u4f1a\u548c\u4f60\u8001\u5f1f\u78b0\u4e2a\u9762\uff0c\u4f60\u5fc3\u4e2d\u6709\u4ec0\u4e48\u7591\u95ee\uff0c\u53ef\u4ee5\u5f53\u9762\u95ee\u95ee\u4ed6\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u73b0\u5728\u4e5f\u53ea\u6709\u5982\u6b64\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u542c\u6021\u7ea2\u8bf4\uff0c\u6211\u4eca\u591c\u5c31\u8981\u52a8\u8eab\u4e86\uff0c\u6211\u4e00\u8d70\uff0c\u8fd9\u4e07\u5bb6\u5927\u9662\u4e4b\u5185\uff0c\u53ea\u4f59\u4e0b\u4f60\u5c60\u5144\u4e00\u4e2a\u4eba\u4e86\uff0c\u4e07\u4e00\u6709\u4ec0\u4e48\u4eba\u627e\u4e0a\u4e07\u5bb6\u5927\u9662\u6765\uff0c\u90a3\u5c06\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u4f60\u8001\u5f1f\u53ef\u4ee5\u653e\u5fc3\uff0c\u6211\u5bf9\u5821\u4e3b\u6709\u7740\u7edd\u5bf9\u7684\u4fe1\u5fc3\uff0c\u5821\u4e3b\u4e00\u5411\u662f\u5bf9\u5c5e\u4e0b\u987e\u8651\u5468\u5230\uff0c\u4ed6\u8bf4\u4e09\u65e5\u5185\u6ca1\u6709\u5371\u9669\uff0c\u5fc5\u7136\u5df2\u7ecf\u8fc7\u4e86\u5f88\u7cbe\u5bc6\u7684\u7b97\u8ba1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c60\u5144\u5bf9\u5821\u4e3b\u4fe1\u5fc3\u5982\u6b64\u4e4b\u5f3a\uff0c\u5728\u4e0b\u5012\u662f\u6ca1\u6709\u8bdd\u8bf4\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8001\u5f1f\u5c3d\u7ba1\u5148\u8d70\uff0c\u5e0c\u671b\u4f60\u80fd\u542c\u5821\u4e3b\u7684\u5b89\u6392\u3002\u201d \u3000\u3000\u7ecf\u8fc7\u8fd9\u4e00\u756a\u8c08\u8bdd\uff0c\u53f6\u957f\u9752\u7684\u5fc3\u4e2d\u5b89\u9759\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u6021\u7ea2\u65e9\u5df3\u66ff\u53f6\u957f\u9752\u51c6\u5907\u597d\u4e86\u51e0\u76d8\u7cbe\u81f4\u7684\u5c0f\u83dc\uff0c\u76f8\u4e00\u58f6\u597d\u9152\uff0c\u800c\u4e14\u966a\u7740\u53f6\u957f\u9752\u5403\u3002 \u3000\u3000\u5979\u4e00\u76f4\u4fdd\u6301\u7740\u6de1\u6de1\u7684\u5fae\u7b11\uff0c\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u5a34\u9759\u3001\u6e29\u67d4\u3002 \u3000\u3000\u4f46\u53f6\u957f\u9752\u5185\u5fc3\u4e4b\u4e2d\uff0c\u5374\u5f88\u4f69\u670d\u5979\u7684\u51b7\u9759\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u8fd9\u4e2b\u5934\u96be\u9053\u662f\u4e00\u4f4d\u6df1\u85cf\u4e0d\u9732\u7684\u9ad8\u4eba\uff1f\u518d\u4ed4\u7ec6\u7684\u60f3\u4e00\u4e8b\u60c5\u7ecf\u8fc7\uff0c\u9ec4\u7075\u79bb\u53bb \u3000\u3000\u4e4b\u540e\uff0c\u6021\u7ea2\u624d\u662f\u8fd9\u4e07\u5bb6\u5927\u9662\u7684\u9996\u8111\u4eba\u7269\u3002 \u3000\u3000\u60f3\u4e0d\u901a\u7684\u662f\u5979\u600e\u4f1a\u8f7b\u6613\u5bf9\u81ea\u5df1\u732e\u8eab\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u76f8\u5bf9\u800c\u5750\uff0c\u4f4e\u659f\u6d45\u914c\uff0c\u4f46\u5374\u4e00\u76f4\u90fd\u672a\u4ea4\u8c08\u3002 \u3000\u3000\u53f6\u957f\u9752\u6765\u518d\u591a\u95ee\uff0c\u6021\u7ea2\u4e5f\u672a\u518d\u89e3\u8bf4\u3002 \u3000\u3000\u76f4\u5230\u53f6\u957f\u9752\u9152\u8db3\u3001\u996d\u9971\u624d\u5618\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u6211\u4eca\u591c\u8981\u52a8\u8eab\u4e86\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u662f\uff0c\u4e8c\u66f4\u65f6\u5206\uff0c\u6709\u4eba\u6765\u63a5\u4f60\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u906d\uff1a\u201c\u4f60\u5462\uff1f\u8fd8\u8981\u7559\u5728\u4e07\u5bb6\u5927\u9662\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6211\u8981\u7b49\u5c60\u5251\u4f7f\u79bb\u5f00\u7684\u65f6\u5019\uff0c\u624d\u80fd\u8d70\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u4f60\u597d\u50cf\u4e00\u70b9\u4e5f\u4e0d\u5bb3\u6015\uff0c\u4e0d\u62c5\u5fc3\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6211\u4ece\u6b7b\u4ea1\u4e2d\u56de\u6765\uff0c\u6240\u4ee5\uff0c\u5bf9\u6b7b\u4ea1\u6211\u5df2\u7ecf\u6ca1\u6709\u754f\u60e7\u3002\u201d\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e86\u4e0d\u8d77\uff0c\u6021\u7ea2\uff0c\u73b0\u5728\uff0c\u6211\u624d\u53d1\u89c9\u4f60\u662f\u4e2a\u4e86\u4e0d\u8d77\u7684\u4eba\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u7237\u592a\u5938\u5956\u4e86\uff0c\u6211\u53ea\u662f\u5fc3\u60c5\u6bd4\u522b\u4eba\u5e73\u9759\u4e00\u4e9b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\uff0c\u4e34\u5371\u4e0d\u4e71\uff0c\u5904\u53d8\u4e0d\u60ca\uff0c\u5927\u90fd\u662f\u56e0\u4e3a\u4ed6\u5bf9\u672c\u8eab\u7684\u827a\u4e1a\uff0c\u6709\u7740\u5f88\u5145\u5206\u7684\u81ea\u4fe1\uff0c\u4f46\u4f60\u8bf4\u8fc7\uff0c\u4f60\u4e0d\u4f1a\u6b66\u529f\u3002\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u6211\u624e\u57fa\u592a\u665a\uff0c\u867d\u7136\u5e38\u548c\u4f60\u4eec\u8fd9\u4e9b\u7b2c\u4e00\u6d41\u4eba\u7269\u76f8\u5904\uff0c\u4f46\u5374\u96be\u83b7\u6559\u76ca\uff0c\u4e0d\u8fc7\uff0c\u6211\u6709\u5f88\u5229\u5bb3\u7684\u6697\u5668\uff0c\u5bf9\u8fd9\u6697\u5668\uff0c\u6211\u4e0b\u4e86\u4e0d\u5c11\u7684\u529f\u592b\uff0c\u8fd8\u6709\u4e00\u70b9\uff0c\u5c31\u662f\u6211\u6709\u7740\u968f\u65f6\u5904\u7f6e\u81ea\u5df1\u7684\u80fd\u529b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6021\u7ea2\uff0c\u8fd9\u8bdd\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u6021\u7ea2\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u7237\uff0c\u6211\u968f\u65f6\u643a\u5e26\u7740\u4e00\u9897\u6bd2\u836f\uff0c\u968f\u65f6\u53ef\u4ee5\u628a\u5b83\u541e\u4e0b\u53bb\uff0c\u90a3\u662f\u4e00\u79cd\u5f88\u5f3a\u70c8\u7684\u6bd2\u836f\uff0c\u541e\u4e0b\u53bb\u7acb\u523b\u81f4\u547d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u539f\u6765\uff0c\u59d1\u5a18\u5df2\u7ecf\u52d8\u7834\u4e86\u751f\u6b7b\u4e4b\u5173\u3002\u201d \u3000\u3000\u6021\u7ea2\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u7237\uff0c\u8fd9\u4e0d\u7b97\u4ec0\u4e48\u3002\u5821\u4e3b\u62ef\u6211\u51fa\u98ce\u5c18\u65f6\uff0c\u6211\u5df2\u7ecf\u6709\u4e86\u8fd9\u79cd\u51b3\u5fc3\uff0c\u597d\u597d\u7684\u4f11\u606f\u7f62\uff01\u4e8c\u66f4\u65f6\u5206\u4f60\u5c31\u8981\u79bb\u5f00\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5ffd\u7136\u95f4\u611f\u89c9\u7740\u81ea\u5df1\u7adf\u7136\u4e0d\u5982\u6021\u7ea2\uff0c\u5979\u4e00\u4e2a\u5973\u5b69\u4e8e\uff0c\u4f46\u5979\u9047\u4e8b\u7684\u9547\u9759\uff0c\u575a\u5f3a\uff0c\u5374\u5f3a\u8fc7\u81ea\u5df1\u5341\u500d\u3002 \u3000\u3000\u6021\u7ea2\u4f38\u624b\u7531\u6000\u4e2d\u53d6\u51fa\u4e00\u4e2a\u7389\u74f6\uff0c\u5012\u51fa\u4e00\u7c92\u4e39\u836f\uff0c\u9053\uff1a\u201c\u7237\uff0c\u628a\u8fd9\u7c92\u4e39\u4e38\u5403\u4e0b\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u628a\u4e39\u4e5d\u541e\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6021\u7ea2\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7237\uff0c\u8eba\u4e00\u8eba\uff0c\u4e8c\u66f4\u65f6\u5206\uff0c\u6211\u518d\u6765\u53eb\u4f60\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\u8eba\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u6709\u7740\u8ba4\u547d\u7684\u611f\u89c9\uff0c\u4f9d\u8a00\u8eba\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6021\u7ea2\u8f7b\u8f7b\u5618\u4e00\u53e3\u6c14\uff0c\u66ff\u53f6\u957f\u9752\u76d6\u4e0a\u68c9\u88ab\uff0c\u6084\u7136\u800c\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u773c\u4e0b\u90a3\u7c92\u4e39\u836f\u4e4b\u540e\uff0c\u5ffd\u7136\u89c9\u7740\u4e39\u7530\u6709\u4e00\u80a1\u70ed\u529b\u5728\u6d41\u52a8\u3002 \u3000\u3000\u4ed6\u5185\u529f\u57fa\u7840\u5f88\u6df1\u539a\uff0c\u7acb\u523b\u6253\u5750\u8c03\u606f\uff0c\u53ea\u89c9\u70ed\u529b\u88ab\u771f\u6c14\u5f15\u52a8\uff0c\u6563\u884c\u56db\u80a2\u767e\u9ab8\uff0c\u4e0d\u89c9\u4e4b\u95f4\uff0c\u8fdb\u5165\u4e86\u7269\u6211\u4e24\u5fd8\u4e4b\u5883\u3002 \u3000\u3000\u9192\u6765\u4e4b\u540e\uff0c\u53ea\u89c1\u6021\u7ea2\u7ad9\u5728\u8eab\u4fa7\uff0c\u8138\u4e0a\u5e26\u7740\u5fae\u5fae\u7684\u7b11\u610f\u3002\u9053\uff1a\u201c\u7237\uff0c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u521a\u624d\u90a3\u7c92\u4e39\u836f\uff0c\u662f\u4ec0\u4e48\u836f\u7269\uff1f\u201d \u3000\u3000\u6021\u7ea2\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u76ca\u795e\u8865\u6c14\u4e39\uff0e\u542c\u8bf4\uff0e\u5bf9\u4f60\u4eec\u7ec3\u6b66\u7684\u4eba\uff0c\u6709\u5f88\u5927\u7684\u5e2e\u52a9\uff0c\u73b0\u5728\uff0c\u4f60\u611f\u89c9\u5982\u4f55\uff1f\u201d \u3000\u3000\u53f6\u957f\u80af\u9053\uff1a\u201c\u611f\u89c9\u7cbe\u795e\u5f88\u597d\u3002\u201d \u3000\u3000\u6021\u7ea2\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d77\u6765\u5427\uff01\u63a5\u4f60\u7684\u4eba\uff0e\u5df2\u7ecf\u5230\u4e86\u95e8\u5916\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7ad9\u8d77\u8eab\u5b50\uff0c\u53d6\u8fc7\u5b9d\u5251\uff0c\u9053\uff1a\u201c\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u6021\u7ea2\u9053\uff1a\u201c\u4ed6\u4eec\u5df2\u7ecf\u5728\u5385\u4e2d\u606d\u5019\u4f60\u4e86\u3002\u201d \u3000\u3000\u5927\u5385\u4e2d\u5750\u7740\u4e24\u4e2a\u4eba\uff0c\u90fd\u7a7f\u7740\u4e00\u8eab\u9ed1\u8272\u7684\u8863\u670d\u3002 \u3000\u3000\u4e24\u4e2a\u9ed1\u8863\u4eba\u5bf9\u6021\u7ea2\uff0c\u4f3c\u4e4e\u662f\u5341\u5206\u656c\u91cd\uff0c\u9f50\u9f50\u8eac\u8eab\u600d\u793c\uff0c\u9053\uff1a\u201c\u89c1\u8fc7\u59d1\u5a18\u3002\u201d \u3000\u3000\u6021\u7ea2\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u4f4d\u5c31\u662f\u53f6\u5251\u4f7f\uff0c\u4f60\u4eec\u5e26\u4ed6\u53bb\u5427\uff01\u201d \u3000\u3000\u4e24\u4e2a\u9ed1\u8863\u4eba\u56de\u8eab\u5bf9\u53f6\u957f\u9752\u8eac\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u89c1\u8fc7\u5251\u4f7f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u76ee\u5149\u8f6c\u52a8\uff0c\u6253\u91cf\u4e86\u4e24\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u5de6\u9996\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5728\u4e0b\u4e01\u5cf0\u3002\u201d \u3000\u3000\u53f3\u9996\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6211\u53eb\u9a6c\u826f\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u6211\u4eec\u662f\u5251\u4f7f\u7684\u8fd1\u536b\u3002\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u7531\u6b64\u523b\u5f00\u59cb\uff0c\u6211\u4eec\u8981\u4fdd\u62a4\u53f6\u5251\u4f7f\uff0c\u4efb\u4f55\u4eba\uff0c\u53ea\u8981\u60f3\u4f24\u5bb3\u5230\u5251\u4f7f\uff0c\u5fc5\u987b\u5148\u628a\u6211\u4eec\u4e24\u4e2a\u4eba\u7ed9\u6740\u6389\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4ed4\u7ec6\u626b\u6253\u91cf\u4e86\u4e24\u4eba\u4e00\u773c\uff0c\u53d1\u89c9\u4e24\u4eba\u90fd\u751f\u7684\u7aef\u7aef\u58ee\u58ee\uff0c\u4e0d\u50cf\u662f\u4ec0\u4e48\u574f\u4eba\u3002 \u3000\u3000\u800c\u4e14\uff0c\u8fd9\u4e24\u4e2a\u4eba\u7684\u540d\u5b57\uff0c\u4e5f\u4ece\u6765\u6ca1\u6709\u542c\u4eba\u8bf4\u8fc7\u3002 \u3000\u3000\u90a3\u662f\u8bf4\uff0c\u4ed6\u4eec\u5728\u6c5f\u6e56\u4e0a\u662f\u4e24\u4e2a\u9ed8\u9ed8\u65e0\u95fb\u7684\u4eba\u3002 \u3000\u3000\u53f6\u957f\u9752\u8f7b\u8f7b\u5618\u4e00\u53e3\u6c14\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u53ea\u6709\u4f60\u4eec\u4e24\u4e2a\u8ddf\u6211\u5417\uff1f\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u4e0d\uff01\u54b1\u4eec\u662f\u5251\u4f7f\u8fd1\u8eab\u4f8d\u536b\uff0c\u9664\u4e86\u6211\u4eec\u4e24\u4e2a\u4eba\u5916\uff0c\u8fd8\u6709\u4e00\u6279\u4eba\u624b\uff0c\u4f9d\u9760\u5251\u4f7f\u7684\u9886\u5bfc\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u4e00\u5171\u6709\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u4e0d\u8fc7\u4e09\u5341\u4f59\u4eba\uff0c\u4f46\u5982\u6709\u9700\u8981\u7684\u65f6\u5019\uff0c\u53ef\u4ee5\u8c03\u52a8\u66f4\u591a\u7684\u4eba\u76f8\u52a9\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u5f88\u719f\u6089\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5c0f\u7684\u8ffd\u968f\u5251\u4f7f\uff0c\u9664\u4e86\u4fdd\u62a4\u5251\u4f7f\u4e4b\u5916\uff0c\u8fd8\u8981\u5907\u8be2\uff0c\u6240\u4ee5\uff0c\u5bf9\u5982\u4f55\u6c42\u63f4\uff0c\u5982\u4f55\u8054\u7edc\u7684\u4e8b\uff0c\u90fd\u5df2\u51c6\u5907\u7684\u5341\u5206\u8be6\u5c3d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u54b1\u4eec\u6709\u6ca1\u6709\u4e00\u4e2a\u76ee\u7684\uff1f\u201d \u3000\u3000\u4e01\u5cf0\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u56fa\u5b9a\u7684\u76ee\u7684\uff0c\u53ea\u4e0d\u8fc7\u9634\u9633\u5821\u5df2\u7ecf\u6563\u53bb\uff0c\u8fd9\u4e9b\u4eba\uff0c\u5206\u6210\u4e86\u5f88\u591a\u6279\uff0c\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\uff0c\u4e00\u5219\uff0c\u53ef\u4ee5\u673a\u52a8\u5bf9\u654c\uff0c\u4f46\u6700\u91cd\u8981\u7684\u662f\u4fdd\u5b58\u4e0b\u8fd9\u4e9b\u5b9e\u529b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9632\u5907\u9ad8\u5929\u5065\uff1f\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u9632\u5907\u4ec0\u4e48\u4eba\uff0c\u5c0f\u7684\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u5df2\u7ecf\u96c6\u5408\u5728\u4e07\u5bb6\u5927\u9662\u4e4b\u5916\uff0c\u606d\u5019\u5251\u4f7f\uff0c\u4e5f\u7b49\u5f85\u5251\u4f7f\u51b3\u5b9a\u884c\u6b62\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7a81\u7136\u95f4\u89c9\u5f97\u80a9\u4e0a\u52a0\u4e86\u4e00\u4e2a\u62c5\u5b50\u3002 \u3000\u3000\u8fd9\u62c5\u5b50\uff0c\u53c8\u662f\u4ed6\u4ece\u672a\u62c5\u8d77\u8fc7\u7684\u8d23\u4efb\uff0c\u987f\u6709\u7740\u538b\u529b\u5343\u94a7\u4e4b\u611f\u3002 \u3000\u3000\u4e00\u4e0b\u5b50\uff0c\u53f6\u957f\u9752\u7684\u8138\u8272\uff0c\u53d8\u5f97\u4e25\u8083\u8d77\u6765\uff0c\u56de\u987e\u4e86\u6021\u7ea2\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u53bb\u4e86\u3002\u201d \u3000\u3000\u6070\u7ea2\u9053\uff1a\u201c\u653e\u5fc3\u7684\u53bb\u5427\uff01\u5821\u4e3b\u4f1a\u968f\u65f6\u548c\u4f60\u4eec\u8054\u7edc\u3002\u201d \u3000\u3000\u4e07\u5bb6\u5927\u9662\u4e4b\u5916\uff0c\u679c\u7136\u4e91\u96c6\u4e86\u4e00\u7fa4\u4eba\uff0c\u7ad9\u7684\u5f88\u6709\u79e9\u5e8f\uff0c\u800c\u4e14\u9759\u5f97\u4e0d\u95fb\u4e00\u70b9\u58f0\u606f\u3002 \u3000\u3000\u4e01\u5cf0\u66ff\u7fa4\u8c6a\u5f15\u89c1\u4e86\u53f6\u957f\u9752\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\uff0c\u5bf9\u53f6\u957f\u9752\u90fd\u8868\u73b0\u4e86\u5341\u5206\u606d\u656c\uff0c\u9f50\u9f50\u8eac\u8eab\u4f5c\u793c\u3002 \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5c5e\u4e0b\u7684\u610f\u601d\u662f\uff0c\u54b1\u4eec\u5230\u592a\u6e56\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5230\u592a\u6e56\uff0c\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u6709\u4e00\u6bb5\u516c\u6848\uff0c\u5251\u4f7f\u4e00\u5b9a\u5341\u5206\u5173\u5fc3\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u516c\u6848\uff1f\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u542c\u8bf4\u6b7b\u5728\u592a\u6e56\u7684\u7389\u5170\u53cc\u59dd\uff0c\u8fd1\u65e5\u5ffd\u7136\u53c8\u6d3b\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u5462\uff1f\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5979\u4eec\u7531\u5251\u4f7f\u7684\u8eab\u4e0a\u8d5a\u4e86\u5341\u4e07\u94f6\u5b50\uff0c\u7136\u540e\u88c5\u4f5c\u6b7b\u4ea1\uff0c\u8fd9\u7792\u5929\u8fc7\u6d77\u4e4b\u8ba1\uff0c\u672c\u6765\u662f\u5929\u8863\u65e0\u7f1d\uff0e\u8fde\u6211\u4eec\u90fd\u76f8\u4fe1\u4e86\uff0c\u56e0\u4e3a\uff0c\u5728\u4e24\u4eba\u6c89\u5c38\u4e4b\u5904\uff0c\u8fd8\u53d1\u73b0\u4e86\u4e24\u7247\u91d1\u53f6\u5b50\uff0c\u90a3\u6b63\u662f\u6253\u6709\u6211\u4eec\u6697\u8bb0\u4e4b\u7269\uff0c\u770b\u4e0a\u53bb\uff0c\u5c31\u50cf\u7389\u5170\u53cc\u59dd\u6000\u74a7\u5176\u7f6a\uff0c\u88ab\u4eba\u62a2\u4e86\u8d22\u7269\uff0c\u7136\u540e\u6740\u6b7b\u6c89\u5c38\u6e56\u4e2d\uff0c\u4f46\u5374\u6ca1\u6709\u6599\u5230\uff0c\u8fd9\u4e24\u4e2a\u5973\u6740\u624b\uff0c\u5ffd\u7136\u53c8\u5728\u592a\u6e56\u51fa\u73b0\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5979\u4eec\u6709\u4ec0\u4e48\u4f5c\u7528\u5462\uff1f\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u4eec\u8981\u67e5\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u5979\u4eec\u7684\u7528\u5fc3\uff0c\u4f1a\u4e0d\u4f1a\u5f15\u6211\u51fa\u73b0\uff1f\u201d \u3000\u3000\u4e01\u5cf0\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5f88\u6709\u53ef\u80fd\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u8fd9\u4e00\u6b21\u63a5\u8fd1\u5979\u4eec\uff0c\u7528\u7684\u65b9\u6cd5\u4e0d\u540c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u8fc7\u53bb\uff0c\u5979\u4eec\u5728\u6697\u4e2d\uff0c\u5251\u4f7f\u5728\u660e\u5904\uff0c\u8fd9\u4e00\u6b21\uff0c\u6211\u4eec\u8981\u5728\u6697\u4e2d\u73b0\u8eab\uff0c\u5979\u4eec\u5728\u660e\u5904\uff0c\u54b1\u4eec\u53ef\u4ee5\u67e5\u67e5\u5979\u4eec\u7684\u76ee\u7684\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\uff01\u65e2\u7136\u5982\u6b64\uff0c\u54b1\u4eec\u5c31\u8d76\u5230\u592a\u6e56\u53bb\u5427\uff01\u201d \u3000\u3000\u4e01\u5cf0\u63a5\u9053\uff1a\u201c\u5c5e\u4e0b\u4e4b\u610f\uff0c\u54b1\u4eec\u6700\u597d\u662f\u60f3\u6cd5\u5b50\u5206\u6210\u6570\u6279\uff0c\u5f7c\u6b64\u884c\u52a8\u4e4b\u95f4\uff0c\u4e5f\u597d\u6709\u4e2a\u7167\u5e94\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u5206\u6210\u4e09\u6279\u884c\u52a8\uff0c\u4f60\u548c\u9a6c\u826f\uff0c\u5404\u81ea\u7387\u9886\u4e00\u6279\u3002\u201d \u3000\u3000\u9a6c\u826f\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u9075\u547d\uff0c\u4e0d\u8fc7\uff0c\u5c5e\u4e0b\u6709\u4e00\u70b9\u6d45\u89c1\uff0c\u8fd8\u671b\u5251\u4f7f\u5353\u88c1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff0c\u4f60\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u5c5e\u4e0b\u89c9\u7740\uff0c\u6211\u4eec\u7684\u884c\u52a8\u65e2\u662f\u8981\u9690\u79d8\u8eab\u4efd\uff0c\u6700\u597d\u80fd\u591f\u6539\u626e\u4e00\u4e0b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\u5427\uff01\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u4ed6\u4eec\u4e00\u5171\u6709\u4e09\u5341\u4e8c\u4eba\uff0c\u5206\u6210\u56db\u7ec4\uff0c\u6bcf\u7ec4\u516b\u4e2a\uff0c\u6bcf\u7ec4\u6709\u4e00\u4e2a\u9886\u961f\uff0c\u90fd\u6709\u7740\u5f88\u4e30\u5bcc\u7684\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u800c\u4e14\u56e0\u65f6\u56e0\u5730\uff0c\u4f1a\u7ecf\u5e38\u6539\u53d8\u4ed6\u4eec\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u5f88\u597d\u3002\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u81f3\u4e8e\u6211\u548c\u4e01\u5cf0\uff0c\u8d1f\u8d23\u4f8d\u5019\u5251\u4f7f\uff0c\u4e0d\u80fd\u548c\u4ed6\u4eec\u4e00\u8d77\u884c\u52a8\uff0c\u5fc5\u987b\u8ffd\u968f\u5728\u5251\u4f7f\u7684\u8eab\u65c1\uff0c\u4e5f\u597d\u968f\u65f6\u6548\u547d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u3002\u201d \u3000 \u7b2c \u4e94 \u56de\u3000\u72c2\u9f99\u4e0e\u98de\u9e70 \u3000 \u3000\u3000\u53f6\u957f\u9752\u7b2c\u4e8c\u5ea6\u8fdb\u5165\u4e86\u592a\u6e56\uff0c\u4e0d\u8fc7\u8fd9\u4e00\u6b21\u4ed6\u7684\u8eab\u4efd\u4e0d\u540c\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u592a\u5927\u7684\u6539\u53d8\uff0c\u53ea\u662f\u8138\u4e0a\u6d82\u4e0a\u4e86\u4e00\u79cd\u836f\u7269\uff0c\u663e\u5f97\u6709\u4e9b\u82cd\u767d\uff0c\u865a\u5f31\uff0c\u518d\u52a0\u4e0a\u8863\u670d\u4e0a\u7684\u6539\u53d8\uff0c\u770b\u4e0a\u53bb\u5c31\u5b8c\u5168\u6362\u4e86\u4e00\u4e2a\u4eba\u6837\uff0c \u3000\u3000\u4e01\u5cf0\u6362\u4e0a\u4e86\u957f\u886b\uff0c\u7565\u7ecf\u6539\u53d8\u5bb9\u8c8c\uff0c\u626e\u4f5c\u4e86\u4e00\u4e2a\u968f\u884c\u7684\u6587\u6848\u3002 \u3000\u3000\u9a6c\u826f\u4e00\u526f\u957f\u968f\u7684\u88c5\u626e\u3002 \u3000\u3000\u4ed6\u4eec\u96c7\u4e86\u4e00\u8258\u753b\u822b\u2014\u2014\u592a\u6e56\u6700\u8c6a\u534e\u7684\u6e38\u8239\u4e4b\u4e00\uff0c\u8239\u4e0a\u6709\u4e24\u4e2a\u8239\u5bb6\u5973\uff0c\u4e00\u4e2a\u8001\u5988\u5b50\uff0c\u4e00\u4e2a\u53a8\u5e08\uff0c\u548c\u4e00\u4e2a\u638c\u8235\u7684\u8239\u592b\u3002 \u3000\u3000\u4e01\u5cf0\u4e09\u4e94\u53e5\u8bdd\uff0c\u628a\u53f6\u957f\u9752\u7684\u8eab\u4efd\uff0c\u7acb\u523b\u6258\u886c\u5f97\u5341\u5206\u5c0a\u8d35\u3002 \u3000\u3000\u8239\u5bb6\u90fd\u77e5\u9053\u91d1\u9675\u7684\u4e00\u4f4d\u8d35\u516c\u5b50\u6765\u6b64\u6e38\u6e56\uff0c\u4e0d\u8ba1\u8239\u79df\u91d1\uff0c\u4f46\u5374\u9700\u4f8d\u5019\u516c\u5b50\u5fc3\u4e2d\u8212\u670d\u3002 \u3000\u3000\u8fd9\u5c31\u5fd9\u574f\u4e86\u4e24\u4f4d\u8239\u5bb6\u59d1\u5a18\u3002 \u3000\u3000\u4e00\u4f1a\u513f\u9001\u8336\uff0c\u4e00\u4f1a\u513f\u9001\u4e0a\u70ed\u6bdb\u5dfe\u3002 \u3000\u3000\u504f\u662f\u8fd9\u4f4d\u516c\u5b50\u7684\u8eab\u4f53\u4e0d\u592a\u597d\uff0c\u4e00\u76f4\u8eb2\u5728\u5185\u8231\u4e2d\u7684\u5e8a\u4e0a\u3002 \u3000\u3000\u4e01\u5cf0\u5429\u5490\u4e24\u4f4d\u8239\u5bb6\u59d1\u5a18\uff0c\u4e0d\u53ef\u5165\u5185\u8231\u6253\u6405\u516c\u5b50\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u4e24\u4f4d\u8239\u5bb6\u5973\uff0c\u867d\u7136\u662f\u5fd9\u7684\u5229\u5bb3\uff0c\u4f46\u5979\u4eec\u5374\u5f88\u5c11\u770b\u5f97\u5230\u516c\u5b50\u3002 \u3000\u3000\u5929\u8272\u8fd1\u5348\u4e0a\u8239\uff0c\u76f4\u5230\u65e5\u843d\u897f\u5c71\uff0c\u53f6\u957f\u9752\u4e00\u76f4\u8eb2\u5728\u5185\u8231\u4e2d\u6ca1\u6709\u51fa\u6765\u8fc7\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u8eb2\u5728\u5185\u8231\u7684\u53f6\u957f\u9752\u5e76\u672a\u8eba\u5728\u5e8a\u4e0a\u4f11\u606f\uff0c\u4ed6\u652f\u8d77\u4e86\u8231\u4e2d\u4e00\u6247\u7a97\u5b50\uff0c\u7559\u5fc3\u7740\u56db\u5468\u7684\u666f\u7269\u3002 \u3000\u3000\u53f6\u957f\u9752\u51ed\u7a97\u773a\u671b\uff0c\u53d1\u89c9\u90a3\u4e9b\u6d69\u701a\u6e56\u9762\u4e0a\uff0c\u4f3c\u4e4e\u662f\u51e0\u8258\u6e38\u8239\u9690\u9690\u95f4\uff0c\u8ffd\u968f\u5728\u83b2\u82b1\u53f7\u7684\u5de6\u53f3\u3002 \u3000\u3000\u81ea\u7136\uff0c\u5982\u662f\u5b8c\u5168\u4e0d\u660e\u5185\u60c5\u7684\u4eba\uff0c\u6839\u672c\u4e0d\u4f1a\u60f3\u5230\uff0c\u8fd9\u4e9b\u6e38\u8239\u662f\u6709\u610f\u7684\u8ffd\u968f\u83b2\u82b1\u53f7\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u8231\u95e8\u5440\u7136\uff0c\u4e01\u5cf0\u7f13\u6b65\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u56de\u987e\u4e86\u4e01\u5cf0\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6709\u4e8b\u60c5?\u201d \u3000\u3000\u4e01\u5cf0\u4e00\u76f4\u4fdd\u6301\u7740\u606d\u8c28\u7684\u6001\u5ea6\uff0c\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u7389\u5170\u53cc\u59dd\u4e58\u5750\u7684\u753b\u822b\uff0c\u505c\u5728\u5317\u6e56\u82a6\u82c7\u6797\u8fb9\uff0c\u5982\u679c\u54b1\u4eec\u7684\u65b9\u5411\u4e0d\u53d8\uff0c\u665a\u4e0a\uff0c\u5c31\u53ef\u4ee5\u548c\u5979\u4eec\u78b0\u4e0a\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d!\u63a5\u8fd1\u5979\u4eec\u4e4b\u540e\uff0c\u5148\u770b\u56db\u5468\u6709\u6ca1\u6709\u4ec0\u4e48\u57cb\u4f0f\uff0c\u5e03\u7f6e\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5c5e\u4e0b\u9075\u547d\u3002\u201d \u3000\u3000\u53e3 \u53e3 \u53e3 \u3000\u3000\u65e5\u843d\u897f\u5c71\u65f6\u5206\uff0c\u665a\u971e\u5728\u6e56\u9762\u4e0a\u6cdb\u8d77\u4e86\u4e07\u9053\u5149\u5f69\u3002 \u3000\u3000\u53f6\u957f\u9752\u51ed\u7a97\u800c\u671b\uff0c\u5df2\u53ef\u89c1\u4e00\u7247\u5e7f\u5927\u7684\u82a6\u82c7\u6797\u3002 \u3000\u3000\u4e5f\u770b\u5230\u4e86\u4e00\u8258\u5f88\u7cbe\u81f4\u7684\u753b\u822b\uff0c\u6cca\u5728\u82a6\u82c7\u6797\u65c1\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53f6\u957f\u9752\u5ffd\u7136\u95f4\u611f\u89c9\u5230\u4e00\u4e2a\u7ec4\u7ec7\u529b\u91cf\u7684\u5f3a\u5927\u3002 \u3000\u3000\u53ea\u6709\u7ed3\u5408\u4e86\u5f88\u591a\u7684\u529b\u91cf\uff0c\u624d\u80fd\u4f7f\u8033\u76ee\u7075\u654f\uff0c\u5b89\u6392\u7684\u5982\u6b64\u81ea\u7136\uff0c\u987a\u5229\u3002 \u3000\u3000\u5915\u9633\u65e0\u9650\u597d\uff0c\u53ea\u662f\u8fd1\u9ec4\u660f\u3002 \u3000\u3000\u8f6c\u773c\u95f4\uff0c\u7eda\u70c2\u7684\u665a\u971e\uff0c\u5df2\u88ab\u56db\u5408\u7684\u66ae\u8272\u541e\u6ca1\u3002\u6e56\u9762\u4e0a\u4e5f\u9010\u6e10\u5730\u6697\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8239\u5bb6\u5f00\u4e86\u665a\u9910\u3002 \u3000\u3000\u53f6\u957f\u9752\u79bb\u5f00\u4e86\u5185\u8231\u3002 \u3000\u3000\u4e24\u4e2a\u8239\u5bb6\u5973\uff0c\u7ec8\u4e8e\u6709\u673a\u4f1a\u770b\u6e05\u695a\u4e86\u5979\u4eec\u6e34\u671b\u4e00\u89c1\u7684\u91d1\u9675\u8d35\u516c\u5b50\u3002 \u3000\u3000\u770b\u5230\u4e86\u5979\u4eec\u7acb\u523b\u6709\u7740\u5931\u671b\u7684\u611f\u89c9\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u90a3\u5f20\u8138\uff0c\u4e0d\u662f\u5973\u5b69\u5b50\u559c\u6b22\u7684\u8138\u3002\u4e00\u5f20\u59dc\u9ec4\u7684\u8138\u3002 \u3000\u3000\u4e24\u4e2a\u8239\u5bb6\u5973\uff0c\u867d\u7136\u6709\u4e9b\u5931\u671b\uff0c\u8fd9\u4f4d\u8d35\u516c\u5b50\u548c\u5979\u4eec\u60f3\u7684\u4e0d\u4e00\u6837\uff0c\u4f46\u4ed6\u4ecd\u7136\u53d7\u5230\u76f8\u5f53\u7684\u656c\u91cd\u3002 \u3000\u3000\u4ed6\u662f\u6709\u8eab\u4efd\u7684\u4eba\uff0c\u6709\u94b1\uff0c\u6709\u52bf\uff0c\u800c\u4e14\uff0c\u9664\u4e86\u4ed6\u7684\u8138\u8272\u4e4b\u5916\uff0c\u8eab\u6750\uff0c\u4e3e\u6b62\uff0c\u90fd\u5f88\u6807\u51c6\uff0c\u6f47\u6d12\u3002 \u3000\u3000\u83b1\u4e0d\u591a\uff0c\u4f46\u5f88\u7cbe\u81f4\uff0c\u5c24\u5176\u90a3\u4e00\u9053\u6e05\u84b8\u9c7c\uff0c\u662f\u521a\u7531\u592a\u6e56\u4e2d\u635e\u51fa\u6765\u7684\u6d3b\u9c7c\uff0c\u5473\u9053\u7279\u522b\u9c9c\u7f8e\u3002 \u3000\u3000\u4e01\u5cf0\u6ca1\u6709\u540c\u684c\u8fdb\u9910\uff0c\u53ea\u662f\u5782\u624b\u7ad9\u5728\u53f6\u957f\u9752\u7684\u8eab\u540e\uff0c\u8fd9\u5c31\u66f4\u886c\u6258\u51fa\u4ed6\u7684\u8eab\u4efd\u7684\u5c0a\u8d35\u3002 \u3000\u3000\u4e24\u4e2a\u8239\u5bb6\u5973\uff0c\u4e5f\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u4f8d\u5019\u7740\uff0c\u6dfb\u996d\uff0c\u5e03\u83dc\uff0c\u65e0\u5fae\u4e0d\u81f3\u3002 \u3000\u3000\u53e3 \u53e3 \u53e3 \u3000\u3000\u4e0b\u5f26\u6708\uff0c\u7167\u4eae\u4e86\u5e73\u9759\u7684\u6e56\u9762\u3002 \u3000\u3000\u5929\u8272\u5df2\u7ecf\u662f\u4e8c\u66f4\u65f6\u5206\u3002 \u3000\u3000\u53f6\u957f\u9752\u6362\u8fc7\u4e86\u4e00\u8eab\u52b2\u88c5\uff0c\u6084\u7136\u884c\u51fa\u8231\u95e8\uff0c\u884c\u4e0a\u7532\u677f\u3002 \u3000\u3000\u90a3\u7cbe\u5de7\u7684\u753b\u822b\u8ddd\u79bb\u53f6\u957f\u9752\u7684\u6e38\u8239\uff0c\u5927\u7ea6\u6709\u4e03\u516b\u4e08\u8fdc\uff0c\u53f6\u957f\u9752\u4f30\u7b97\u81ea\u5df1\u7684\u8f7b\u529f\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u5f88\u96be\u4e00\u4e0b\u8dc3\u767b\u4e0a\u5bf9\u65b9\u7684\u8239\u4e0a\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4eba\u5f71\u4e00\u95ea\uff0c\u4e01\u5cf0\u6084\u7136\u884c\u4e86\u8fc7\u6765\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u5251\u4f7f\uff0c\u53ef\u662f\u8981\u8fc7\u53bb\u77a7\u77a7\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a \u201c\u662f!\u5982\u679c\u54b1\u4eec\u8981\u4e86\u89e3\u5979\u4eec\u7684\u7528\u5fc3\uff0c\u90a3\u5c31\u53ea\u6709\u8fc7\u53bb\u770b\u770b\u4e86\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a \u201c\u8fd9\u56db\u5468\u5c5e\u4e0b\u5df2\u7ecf\u67e5\u8fc7\u4e86\uff0c\u4e0d\u89c1\u6709\u57cb\u4f0f\u4e4b\u4eba\uff0c\u552f\u4e00\u53ef\u4ee5\u8bbe\u4e0b\u57cb\u4f0f\u7684\uff0c\u5c31\u662f\u9760\u8fd1\u82a6\u82c7\u6797\u4e2d\u4e00\u9762\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u7389\u5170\u53cc\u59dd\u7684\u753b\u822b\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5251\u4f7f\u660e\u9274\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u7389\u5170\u53cc\u59dd\uff0c\u91cd\u51fa\u6c5f\u6e56\u5fc5\u7136\u6709\u76ee\u7684\uff0c\u65e2\u7136\u6c89\u821f\u9690\u8e2a\uff0c\u8fd9\u4e00\u6b21\u91cd\u884c\u51fa\u5c71\uff0c\u672a\u5fc5\u662f\u51fa\u4e8e\u5979\u4eec\u7684\u672c\u5fc3\uff0c\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u76ee\u4e0b\u6c5f\u6e56\u4e0a\uff0c\u53ef\u80fd\u6709\u4e00\u80a1\u6216\u8005\u5f88\u591a\u80a1\u7684\u529b\u91cf\u5728\u5bfb\u8bbf\u54b1\u4eec\uff0c\u54b1\u4eec\u65e9\u665a\u90fd\u4f1a\u548c\u4ed6\u4eec\u649e\u4e0a\uff0c\u95ee\u9898\u5728\u907f\u91cd\u5c31\u8f7b\uff0c\u4ed6\u4eec\u529b\u91cf\u5f31\u5c0f\u7684\u65f6\u5019\uff0c\u54b1\u4eec\u5c31\u4e00\u9f13\u4f5c\u6c14\u628a\u4ed6\u4eec\u6b7c\u706d\uff0c\u51fb\u6e83\uff0c\u5982\u662f\u5bf9\u65b9\u5f3a\u5927\u65f6\uff0c\u54b1\u4eec\u5c31\u60f3\u6cd5\u5b50\u907f\u5f00\uff0c\u4e0d\u548c\u4ed6\u4eec\u76f4\u63a5\u7684\u51b2\u7a81\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u6211\u8fd8\u662f\u8fc7\u53bb\u77a7\u77a7\u7684\u597d\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u8981\u4e0d\u8981\u5c5e\u4e0b\u548c\u9a6c\u826f\u76f8\u968f?\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u8fc7\u53bb\uff0c\u9a6c\u826f\u7559\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5c5e\u4e0b\u9075\u547d\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u6e38\u8239\u4e0a\u4e24\u8258\u5c0f\u821f\uff0c\u5c5e\u4e0b\u5df2\u653e\u4e0b\u4e86\u6c34\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5c0f\u8239\u9760\u8fd1\u753b\u822b\u65f6\uff0c\u753b\u822b\u4e0a\u4ecd\u7136\u4e0d\u89c1\u52a8\u9759\u3002 \u3000\u3000\u4ed6\u4e00\u70b9\u6ca1\u6709\u5927\u610f\uff0c\u4ed6\u660e\u767d\uff0c\u4ee5\u7389\u5170\u53cc\u59dd\u7684\u8033\u76ee\uff0c\u4e00\u5b9a\u65e9\u5df3\u542c\u5230\u5c0f\u821f\u5212\u52a8\u7684\u6c34\u58f0\u3002 \u3000\u3000\u679c\u7136\uff0c\u53f6\u957f\u9752\u8dc3\u767b\u4e0a\u7532\u677f\u65f6\uff0c\u8231\u4e2d\u5df2\u4f20\u51fa\u4e00\u4e2a\u5973\u5b50\u58f0\u97f3\uff0c\u9053\uff1b\u201c\u54ea\u4e00\u4f4d\u6c5f\u6e56\u670b\u53cb\uff0c\u5924\u591c\u767b\u821f\uff0c\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6545\u4eba\u6765\u8bbf\uff0c\u4e0d\u77e5\u53ef\u5426\u5165\u8231\u4e00\u53d9\uff1f\u201d \u3000\u3000\u8231\u4e2d\u4eba\u6709\u4e9b\u610f\u5916\uff0c\u9053\uff1a\u201c\u4f60\u662f\u53f6\u5c11\u4fa0?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u59d1\u5a18\uff0c\u4f60\u662f\u5170\u767d\uff0c\u8fd8\u662f\u7389\u7fe0?\u201d \u3000\u3000\u201c\u5c0f\u59b9\u5170\u767d\u201d\uff0c\u8231\u95e8\u7a81\u7136\u5927\u5f00\uff0c\u5170\u767d\u4e5f\u51fa\u73b0\u5728\u8231\u95e8\u53e3\u5904\u3002 \u3000\u3000\u5979\u6839\u672c\u6ca1\u6709\u7761\uff0c\u8863\u7740\u6574\u9f50\uff0c\u8fd8\u5e26\u7740\u5175\u5203\u3002 \u3000\u3000\u4f3c\u4e4e\u662f\uff0c\u5979\u4eec\u4e00\u76f4\u5728\u8231\u4e2d\u5750\u7740\u7b49\u4eba\u3002 \u3000\u3000\u5170\u767d\u6253\u91cf\u4e86\u53f6\u957f\u9752\u4e00\u773c\uff0c\u9053\uff1a \u201c\u53f6\u5c11\u4fa0\u6234\u4e86\u9762\u5177\u8fd8\u662f\u8138\u4e0a\u6d82\u4e86\u836f\u7269?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6c5f\u6e56\u9669\u6076\uff0c\u5728\u4e0b\u4e5f\u4e0d\u5f97\u4e0d\u5c0f\u5fc3\u4e00\u4e9b\u2026\u2026\u201d \u3000\u3000\u5185\u8231\u4e2d\u4f20\u8fc7\u6765\u7389\u7fe0\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u8d35\u5ba2\u767b\u821f\uff0c\u600e\u4e0d\u8bf7\u4eba\u5165\u8231\u5c0f\u5750\uff0c\u5168\u4e0d\u77e5\u5f85\u5ba2\u793c\u6570\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a \u201c\u53f6\u5c11\u4fa0\u4e00\u671d\u88ab\u86c7\u54ac\uff0c\u5341\u5e74\u6015\u4e95\u7ef3\uff0c\u53ea\u6015\u4ed6\u4e0d\u6562\u9886\u53d7\u59ca\u59ca\u7684\u76db\u60c5\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a \u201c\u5170\u767d\u59d1\u5a18\u7528\u4e0d\u7740\u4f7f\u7528\u6fc0\u5c06\u4e4b\u6cd5\uff0c\u6211\u65e2\u7136\u6765\u4e86\uff0c\u5c31\u7b97\u9f99\u6f6d\u864e\u7a74\uff0c\u5728\u4e0b\u4e5f\u8981\u518d\u8bd5\u4e00\u6b21\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\u8bf7\u653e\u5fc3\uff0c\u5c0f\u821f\u4e2d\u65e0\u57cb\u4f0f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u5c31\u7b97\u6709\u57cb\u4f0f\uff0c\u53f6\u67d0\u4eba\u4e5f\u53ea\u6709\u8ba4\u4e86\uff0c\u5170\u767d\u59d1\u5a18\u8bf7\u8ba9\u8ba9\u8def\u5427\uff01\u201d \u53f6\u957f\u9752\u8fd0\u8db3\u4e86\u76ee\u529b\uff0c\u7d27\u968f\u5728\u5170\u767d\u7684\u8eab\u540e\u800c\u5165\u3002 \u3000\u3000\u8231\u4e2d\u672a\u70b9\u706f\u706b\uff0c\u4f46\u7389\u7fe0\u5374\u62c9\u5f00\u4e86\u7a97\u5e37\uff0c \u6708\u5149\u7531\u7a97\u4e2d\u900f\u5c04\u8fdb\u6765\u3002 \u4ee5\u53f6\u957f\u9752\u8d85\u8fc7\u5e38\u4eba\u7684\u76ee\u529b\uff0c \u53ef\u4ee5\u770b\u6e05\u695a\u8231\u4e2d\u7684\u666f\u7269\u3002 \u7389\u7fe0\u51ed\u7a97\u800c\u5750\uff0c\u9762\u524d\u4e00\u5f20\u5c0f\u8336\u51e0\u4e0a\u653e\u7740\u4e00\u676f\u9999\u8317\u3002 \u3000\u3000\u5170\u767d\u7d27\u4f9d\u5728\u7389\u7fe0\u7684\u8eab\u4fa7\u5750\u4e0b\u3002 \u3000\u3000\u7389\u7fe0\u6307\u6307\u5bf9\u9762\u4e00\u5f20\u6728\u6905\uff0c\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u8bf7\u5750\u5427!\u201d \u3000\u3000\u53f6\u957f\u9752\u672a\u7acb\u523b\u5750\u4e0b\uff0c\u5f88\u4ed4\u7ec6\u7684\u770b\u8fc7\u90a3\u5f20\u6728\u6905\u4e4b\u540e\uff0c\u624d\u7f13\u7f13\u843d\u5ea7\uff0c\u9053\uff1a\u201c\u7389\u5170\u53cc\u59dd\u6740\u4eba\u65b9\u6cd5\u5f88\u591a\uff0c\u5728\u4e0b\u5403\u8fc7\u4e00\u6b21\u4e8f\uff0c\u4e0d\u5f97\u4e0d\u8c28\u614e\u4e00\u4e9b\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\uff0c\u53f6\u5144\u4e0d\u662f\u4e00\u4e2a\u4eba\u6765\u5427?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u8fd8\u6709\u63a5\u5e94\uff0c\u6240\u4ee5\u8d35\u59ca\u59b9\u5c31\u7b97\u6709\u4ec0\u4e48\u5e03\u7f6e\uff0c\u53ea\u6015\u4e5f\u672a\u5fc5\u80fd\u4f24\u5f97\u4f4f\u5728\u4e0b\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u8fd9\u4e00\u6b21\uff0c\u53f6\u5c11\u4fa0\u6df1\u591c\u6765\u8bbf\uff0c\u5c31\u662f\u70ab\u8000\u4f60\u7684\u5b9e\u529b\u4e86\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u6700\u91cd\u8981\u7684\u662f\u5728\u4e0b\u5e0c\u671b\u8d35\u59ca\u59b9\u5bf9\u533a\u533a\u6709\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u4eec\u51fa\u5356\u4f60\u7684\u4e8b?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\u5728\u533a\u533a\u7684\u8fd0\u6c14\u4e0d\u9519\uff0c\u5426\u5219\uff0c \u5c82\u4e0d\u662f\u8981\u6b7b\u5728\u4f60\u4eec\u7684\u624b\u4e0b\u3002\u201d \u7389\u7fe0\u9053\uff1a\u201c\u4f46\u4f60\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u5728\u4e0b\u624d\u80fd\u627e\u5230\u8d35\u59ca\u59b9\uff0c\u95ee\u4e2a\u660e\u767d\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f60\u662f\u8981\u62a5\u590d\uff0c\u6216\u662f\u6765\u8981\u6c42\u8865\u507f?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u8fd9\u8981\u770b\u59d1\u5a18\u7684\u7b54\u590d\u4e86\uff0c\u5982\u82e5\u59d1\u5a18\u7684\u7b54\u590d\uff0c\u80fd\u4f7f\u5728\u4e0b\u6ee1\u610f\uff0c\u8fd9\u62a5\u590d\u7684\u4e8b\uff0c\u53ef\u4ee5\u514d\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5982\u679c\u4e0d\u80fd\u6ee1\u8db3\u4f60\u5462?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u522b\u592a\u8fc7\u5206\u4e86\uff0c\u5728\u4e0b\u662f\u53d7\u5bb3\u4eba\uff0c\u6211\u5e0c\u671b\u4f60\u6709\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u4e8b\u60c5\u5df2\u7ecf\u5f88\u660e\u663e\uff0c\u6211\u4eec\u8fd8\u80fd\u6709\u4ec0\u4e48\u4ea4\u4ee3\uff0c\u6211\u4eec\u59ca\u59b9\u4e24\u4e2a\u4eba\u662f\u6740\u624b\uff0c\u53d7\u4eba\u96c7\u7528\uff0c\u6211\u4eec\u5bf9\u4f60\u4e5f\u662f\u4e00\u6837\uff0c\u9634\u9633\u5821\u82b1\u4e86\u5341\u4e07\u94f6\u5b50\u4e70\u4f60\u4e00\u6761\u547d\uff0c\u4f46\u6211\u4eec\u5356\u7ed9\u4ed6\u4e00\u4e2a\u6d3b\u4eba\uff0e\u591a\u5acc\u4e86\u4e24\u4e07\u94f6\u5b50\uff0c\u6709\u4e86\u8fd9\u7b14\u94b1\uff0c\u6211\u4eec\u59ca\u59b9\u672c\u6765\u60f3\u6d17\u624b\u4e0d\u5e72\u4e86\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u4f46\u4f60\u4eec\u4e3a\u4ec0\u4e48\u91cd\u51fa\u6c5f\u6e56\uff0c\u800c\u4e14\uff0c\u53c8\u5230\u5b50\u592a\u6e56\u4e2d\u6765?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5f88\u7b80\u5355\uff0c\u6211\u4eec\u5728\u7b49\u4eba\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u7b49\u6211\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u4f60\u7684\u51fa\u73b0\uff0c\u5bf9\u6211\u4eec\u800c\u8a00\uff0c\u6709\u4e9b\u610f\u5916\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u544a\u8bc9\u4f60\u53f6\u5c11\u4fa0\u51e0\u53e5\u771f\u5b9e\u7684\u8bdd\uff0c\u6211\u4eec\u8fd9\u4e00\u6b21\u91cd\u51fa\u6c5f\u6e56\u5e76\u975e\u672c\u610f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u53d7\u4eba\u80c1\u8feb?\u201d \u3000\u3000\u7389\u7fe0\u70b9\u70b9\u5934\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u60f3\u8fd9\u4e2d\u95f4\u4e00\u5b9a\u6709\u66f2\u6298\u7684\u5185\u60c5\uff0c\u4f46\u90a3\u65e0\u5173\u7d27\u8981\uff0c\u6211\u60f3\u77e5\u9053\u80c1\u8feb\u4f60\u4eec\u7684\u662f\u4e9b\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u7389\u7fe0\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u544a\u8bc9\u4f60\uff0c\u53c8\u80fd\u600e\u4e48\u6837?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e5f\u8bb8\uff0c\u6211\u53ef\u4ee5\u5e2e\u52a9\u4f60\u4eec\u3002\u201d \u3000\u3000\u7389\u7fe0\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u53f6\u5c11\u4fa0\u786e\u6709\u80fd\u529b\u5e2e\u52a9\u6211\u4eec\uff0c\u53ea\u662f\u4e0d\u77e5\u9053\u53f6\u5c11\u4fa0\uff0c\u613f\u4e0d\u613f\u610f\u4ed8\u51fa\u8fd9\u4e9b\u727a\u7272?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u55ef\uff01\u4ec0\u4e48\u6837\u7684\u727a\u7272\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u53f6\u5c11\u4fa0\u5927\u6982\u4e0d\u80af\u675f\u624b\u5c31\u7f1a\uff0c\u8ba9\u6211\u4eec\u518d\u628a\u4f60\u751f\u64d2\u4e00\u6b21\u5427!\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5389\u5bb3\u5440!\u7389\u7fe0\u59d1\u5a18\uff0c\u5728\u4e0b\u662f\u95ee\u7f6a\u800c\u6765\uff0c\u60f3\u4e0d\u5230\uff0c\u4f60\u59d1\u5a18\u7adf\u7136\u53bb\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u5148\u5012\u6253\u4e00\u8019\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u6211\u77e5\u9053\u53f6\u5c11\u4fa0\u4e0d\u4f1a\u540c\u610f\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u59ca\u59b9\u4e5f\u672a\u4f5c\u592a\u5982\u610f\u7684\u60f3\u6cd5\uff0c\u4e0d\u8fc7\uff0c\u53f6\u5c11\u4f7f\u95ee\u4e86\uff0c\u54b1\u4eec\u5012\u4e0d\u80fd\u4e0d\u8bf4\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u9053\uff1a \u201c\u8fd9\u4e2b\u5934\u53e3\u9f7f\u4f36\u4fd0\uff0c\u8be1\u8ba1\u591a\u7aef\uff0c\u5012\u662f\u4e0d\u80fd \u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374\u63a5\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u5728\u4e0b\u6df1\u591c\u9020\u8bbf\uff0c\u7528\u5fc3\u5728\u8981\u8d35\u59ca\u59b9\u7ed9\u6211\u4e00\u4e2a\u4ea4\u4ee3\u3002\u201d \u7389\u7fe0\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u5982\u662f\u53f6\u5c11\u4fa0\u5bf9\u524d\u5acc\u8bb0\u6068\u4e0d\u6df1\uff0c\u62a5\u590d\u624b\u6bb5\u4e0d\u592a\u6fc0\u70c8\uff0c\u6211\u4eec\u59ca\u59b9\u613f\u675f\u624b\u5c31\u7f1a\uff0c\u4e0d\u4e88\u8fd8\u624b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u624b\u6bb5\u624d\u7b97\u4e0d\u6fc0\u70c8\u5728\u4e0b\u5012\u662f\u88ab\u59d1\u5a18\u96be\u4f4f\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u4ec0\u4e48\u5f88\u96be\u5212\u5206\u7684\u4e8b\uff0c\u8b6c\u5982\u4f60\u8981\u94b1\uff0c\u54b1\u4eec\u59ca\u59b9\u51fa\u5356\u4f60\u53f6\u5c11\u4fa0\u7684\u5341\u4e07\u94f6\u5b50\u8fd8\u5728\uff0c \u53ef\u4ee5\u8fd8\u7ed9\u4f60\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u90a3\u94f6\u5b50\u867d\u7136\u548c\u6211\u6709\u5173\uff0c\u4f46\u5b83\u7a76\u7adf\u4e0d\u662f\u6211\u7684\u4e1c\u897f\u3002\u201d \u7389\u7fe0\u9053\uff1a\u201c\u4e0d\u8981\u94b1\uff0c\u8981\u4eba\u4e5f\u884c\uff0c\u820d\u59b9\u5170\u767d\uff0c\u8fd8\u662f\u6e05\u767d\u513f\u5973\u4e4b\u8eab\uff0c\u8981\u5979\u5949\u4f8d\u6795\u5e2d\uff0c\u4ee5\u4f5c\u8865\u507f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u5728\u4e0b\u627f\u53d7\u4e0d\u8d77\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e0d\u8981\u94b1\uff0c\u4e0d\u8981\u4eba\uff0c\u90a3\u662f\u8981\u547d\u4e86?\u201d \u3000\u3000\u201c\u5982\u662f\u8981\u547d\uff0c\u59d1\u5a18\u53c8\u4f5c\u4f55\u6253\u7b97\u5462?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5343\u53e4\u8270\u96be\u552f\u4e00\u6b7b\uff0c\u6211\u4eec\u59ca\u59b9\u8fd8\u4e0d\u60f3\u6b7b\uff0c\u53f6\u5c11\u4fa0\u5982\u679c\u975e\u8981\u54b1\u4eec\u59ca\u59b9\u7684\u6027\u547d\u4e0d\u53ef\uff0c\u8bf4\u4e0d\u5b9a\u54b1\u4eec\u8981\u594b\u8d77\u4e00\u6218\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53f9\u606f\u9053\uff1a \u201c\u8d35\u59ca\u59b9\u59ff\u8272\uff0c\u624d\u667a\uff0c\u90fd\u5c5e\u4e0a\u9009\uff0c\u5982\u53ea\u60f3\u8c0b\u4e00\u53e3\u5e73\u5b89\u996d\u5403\uff0c\u4f55\u5904\u4e0d\u53ef\u7acb\u8db3\uff0c\u4e3a\u4ec0\u4e48\u8981\u4f5c\u6740\u624b\u5462?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\u8fd9\u756a\u8bdd\u662f\u597d\u610f\uff0c\u53ea\u53ef\u60dc\u592a\u665a\u5e7f\u2014\u4e9b\uff0c\u54b1\u4eec\u59ca\u59b9\uff0c\u672c\u6765\u5df2\u51c6\u5907\u9000\u51fa\u6c5f\u6e56\uff0c\u4f46\u4ecd\u7136\u88ab\u4eba\u5bb6\u7ed9\u903c\u4e86\u51fa\u6765\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u9053\uff1a\u8fd9\u624d\u5f15\u5165\u6b63\u9898\u4e86\u3002\u751f\u6050\u6b63\u9762\u76f8\u95ee\uff0c\u7389\u7fe0\u53c8\u907f\u5f00\u6b63\u9898\uff0c\u53ea\u597d\u65c1\u6572\u4fa7\u51fb\u5730\u8bf4\u9053\uff1a \u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u8001\u5b9e\u8bf4\uff0c\u5728\u4e0b\u5bf9\u4e24\u4f4d\u7684\u52a0\u5bb3\u4e4b\u4e8b\uff0c\u5e76\u672a\u592a\u8ba1\u8f83\uff0c\u4ec7\u6068\u4e4b\u5fc3\u4e5f\u4e0d\u5f88\u6df1\uff0c\u4e5f\u8bb8\u4f60\u4eec\u7389\u5170\u53cc\u59dd\u7ed9\u4eba\u7684\u5370\u8c61\uff0c\u5e76\u4e0d\u592a\u574f\u3002\u201d \u3000\u3000\u7389\u7fe0\u53f9\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u4e5f\u6709\u4eba\u8fd9\u4e48\u8bf4\u8fc7\uff0c\u4ed6\u4eec\u6ee1\u6000\u4ec7\u6068\u800c\u6765\uff0c \u4f46\u89c1\u5230\u4e86\u6211\u4eec\u59ca\u59b9\u4e4b\u540e\uff0c\u8fd9\u4ec7\u89c6\u4e4b\u5fc3\uff0c\u5ffd\u7136\u95f4\u6de1\u4e86\u4e0b\u6765\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4eba\u4e4b\u5e38\u60c5\uff0c\u4e5f\u662f\u4eba\u7684\u7f3a\u70b9\u3002\u5bf9\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u591a\u5c11\u90fd\u6709\u5bb9\u5fcd\u4e4b\u610f\u3002\u201d \u3000\u3000\u8138\u8272\u7a81\u7136\u95f4\u8f6c\u53d8\u7684\u5341\u5206\u4e25\u8083\uff0c\u7f13\u7f13\u63a5\u9053\uff1b\u201c\u59d1\u5a18\uff0c\u8fc7\u5f80\u4e4b\u4e8b\uff0c\u5728\u4e0b\u53ef\u4ee5\u4e0d\u8ffd\uff0c\u4e0d\u8fc7\uff0c\u4e24\u4f4d\u518d\u884c\u51fa\u73b0\u6c5f\u6e56\u7684\u7528\u5fc3\uff0c\u5728\u4e0b\u5c31\u4e0d\u80fd\u4e0d\u67e5\u4e2a\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u8bf4\u8fc7\u4e86\uff0c\u6211\u4eec\u59ca\u59b9\u662f\u88ab\u4eba\u903c\u51fa\u6765\u7684\uff0c\u5728\u6b64\u4f5c\u9975\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u4eec\u4e24\u59ca\u59b9\u5fc5\u9700\u8981\u4f5c\u4e00\u4e2a\u6289\u62e9\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6289\u62e9\u4ec0\u4e48?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u548c\u5728\u4e0b\u5408\u4f5c\uff0c\u8fd8\u662f\u548c\u5728\u4e0b\u654c\u5bf9\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u6211\u4eec\u5f88\u60f3\u548c\u4f60\u5408\u4f5c\uff0c\u4f46\u53ef\u60dc\uff0c\u6211\u4eec\u5df2\u8eab\u96be\u81ea\u4e3b\u4e86\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u600e\u4e48?\u4f60\u4eec\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e00\u79cd\u5947\u602a\u7684\u836f\uff0c\u5341\u4e8c\u4e2a\u65f6\u8fb0\u5185\u5fc5\u987b\u670d\u7528\u4e00\u6b21\u89e3\u836f\uff0c\u6211\u4eec\u59ca\u59b9\u5c31\u5b88\u5728\u6b64\u5730\uff0c\u4ed6\u4eec\u6309\u65f6\u9001\u4e0a\u89e3\u836f\uff0c\u4ed6\u4eec\u4e0d\u7528\u7ed9\u6211\u4eec\u4ec0\u4e48\u7ea6\u675f\uff0c\u6211\u4eec\u4e5f\u4e0d\u4f1a\u8dd1\uff0c\u56e0\u4e3a\u4ed6\u4eec\u62f4\u4f4f\u4e86\u6211\u4eec\u59ca\u59b9\u7684\u547d\uff0c\u4ed6\u4eec\u4ea4\u4ee3\u4e86\u51e0\u4ef6\u4e8b\uff0c\u6211\u4eec\u53ea\u597d\u7167\u7740\u505a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u77e5\u4ec0\u4e48\u4eba\u53d1\u660e\u7528\u6bd2\uff0c\u7adf\u7136\u5728\u6c5f\u6e56\u4e0a\u5f62\u6210\u4e86\u4e00\u79cd\u4e13\u95e8\u7684\u6bd2\u6280\uff0c \u65e5\u6709\u8fdb\u5883\uff0c\u5927\u6709\u51cc\u5374\u6b66\u529f\u4e4b\u4e0a\u7684\u7528\u9014\u3002\u201d \u3000\u3000\u7389\u7fe0\u82e6\u7b11\u4e00\u4e0b\u9053\uff1a \u201c\u8fd9\u5c31\u662f\u6211\u4eec\u59ca\u59b9\u7684\u906d\u9047\uff0c\u5982\u82e5\u4ed6\u4eec\u65ad\u7edd\u4e86\u89e3\u836f\u7684\u4f9b\u5e94\uff0c\u660e\u65e5\u5348\u65f6\uff0c\u6211\u4eec\u5c31\u6bd2\u53d1\u6b7b\u4ea1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7389\u7fe0\uff0c\u4f60\u662f\u5426\u77e5\u9053\uff0c\u4ec0\u4e48\u4eba\u63a7\u5236\u7740\u4f60\u4eec?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u4e0d\u77e5\u9053\uff0c\u6211\u53ea\u6653\u5f97\uff0c\u4ed6\u4eec\u662f\u4e00\u4e2a\u5f88\u5e9e\u5927\u7684\u7ec4\u7ec7\uff0c\u6211\u4e00\u76f4\u5728\u731c\u60f3\uff0c\u4f1a\u4e0d\u4f1a\u662f\u9634\u9633\u5821?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u544a\u6c82\u4f60\uff0c\u4e0d\u662f\u9634\u9633\u5821\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u54e6! \u4f60\u73b0\u5728\u662f\u9634\u9633\u5821\u4e2d\u7684\u4eba\u4e86\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u6211\u662f\u9634\u9633\u5821\u4e2d\u7684\u4eba\u8fd9\u8fd8\u662f\u8d35\u59ca\u59b9\u7684\u5e2e\u5fd9\u3002\u201d \u7389\u7fe0\u9053\uff1a \u201c\u5927\u540d\u9f0e\u9f0e\u7684\u8ffd\u9b42\u4e03\u5251\uff0c\u5728\u9634\u9633\u5821\u4e2d\uff0c\u5b9a\u7136\u662f\u8eab\u4efd\u4e0d\u4f4e\u4e86\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd8\u8fc7\u5f97\u53bb\u2026\u2026\u201d \u3000\u3000\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u8d35\u59ca\u59b9\u73b0\u5728\u4f5c\u4f55\u6253\u7b97\u5462?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u4eec\u53ef\u4ee5\u6d3b\u4e0b\u53bb\uff0c\u90a3\u5c31\u662f\u7167\u4ed6\u4eec\u7684\u5429\u5490\u529e\u4e8b\uff0c\u6211\u73b0\u5728\u628a\u5185\u60c5\u90fd\u544a\u8bc9\u4e86\u4f60\uff0c\u90a3\u5c31\u662f\u6ca1\u6709\u51c6\u5907\u7167\u4ed6\u4eec\u7684\u5429\u5490\u529e\u4e8b\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u4f60\u81ea\u5df1\u5bb3\u4e86\u81ea\u5df1\u5417?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u51a5\u51a5\u4e4b\u4e2d\uff0c\u4e5f\u8bb8\u771f\u6709\u4e00\u79cd\u62a5\u5e94\u7684\u529b\u91cf\u5b58\u5728\uff0c\u6211\u4eec\u59ca\u59b9\uff0c\u5e74\u7eaa\u4e0d\u5927\uff0c\u51fa\u9053\u6c5f\u6e56\u4e0d\u4e45\u3002\u4f46\u5374\u6ca1\u6709\u505a\u4e00\u4ef6\u597d\u4e8b\uff0c\u6211\u539f\u60f3\u5728\u51e0\u5e74\u5185\uff0c\u635e\u591f\u4e86\u4e00\u751f\u6240\u9700\u4e4b\u94b1\u8d22\uff0c\u4ece\u5934\u505a\u8d77\uff0c\u79ef\u4fee\u4e00\u4e9b\u5584\u529f\uff0c\u4ee5\u8865\u507f\u6211\u4eec\u7684\u7f6a\u8fc7\uff0c\u60f3\u4e0d\u5230\u4e0a\u5929\u7adf\u7136\u4e0d\u7ed9\u6211\u4eec\u8fd9\u673a\u4f1a\uff0c\u6211\u89c9\u5f97\u5f88\u5b8c\u7f8e\u7684\u8ba1\u5212\uff0c\u5012\u4f1a\u767e\u5bc6\u4e00\u758f\uff0c\u9020\u6210\u4e86\u5f88\u5927\u7684\u635f\u5931\uff0c\u843d\u6b64\u4e0b\u573a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u5728\u4e0b\u5f88\u611f\u6fc0\u4f60\u8fd9\u756a\u5766\u8bda\u76f8\u544a\uff0c\u4e0d\u8fc7\uff0c\u6211\u603b\u89c9\u5f97\u4f60\u8bf4\u7684\u8fd8\u7f3a\u4e4f\u91cd\u70b9\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u60f3\u77e5\u9053\u64cd\u7eb5\u6211\u4eec\u7684\u662f\u4e00\u4e2a\u4ec0\u4e48\u4f6f\u7684\u7ec4\u7ec7\uff0c\u4ed6\u4eec\u7684\u9886\u5934\u662f\u8c01\uff0c\u53ef\u662f\u8fd9\u4e9b\u6211\u90fd\u4e0d\u77e5\u9053\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u4e0d\u4e3a\u94b1\uff0c\u6211\u4eec\u59ca\u59b9\u7684\u6240\u6709\u7684\u8d22\u7269\uff0c\u4ed6\u4eec\u90fd\u6ca1\u6709\u52a8\uff0c\u8fd9\u8258\u8239\u4e5f\u662f\u4ed6\u4eec\u7684\uff0c\u6211\u4eec\u539f\u6765\u90a3\u8258\u8239\uff0c\u65e9\u5df2\u6c89\u5728\u592a\u6e56\u4e86\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u8fd9\u4ef6\u4e8b\u6682\u4e14\u4e0d\u8c08\uff0c\u5728\u4e0b\u5012\u60f3\u548c\u59d1\u5a18\u8c08\u8c08\u751f\u6b7b\u5927\u4e8b\u4e86\u3002\u201d \u7389\u7fe0\u9053\uff1a\u201c\u6211\u4eec\u53ea\u6709\u4e00\u65e5\u597d\u6d3b\uff0c\u8fd8\u6709\u4ec0\u4e48\u8c08\u7684?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5728\u4e0b\u60f3\uff0c\u59d1\u5a18\u5e94\u8be5\u6709\u4e00\u4e2a\u751f\u5b58\u7684\u673a\u4f1a\u624d\u662f\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6ca1\u6709\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u6709\u7684\uff0c\u4f60\u4ed4\u7ec6\u60f3\u60f3\u770b\uff0c\u8b6c\u5982\u54b1\u4eec\u5408\u4f5c\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5408\u4f5c\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5bf9!\u6211\u548c\u4f60\u5408\u4f5c\uff0c\u662f\u4e0d\u662f\u6709\u4e00\u4e2a\u6c42\u751f\u4e4b\u9053\u5462\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5982\u82e5\u53f6\u516c\u5b50\u771f\u80af\u548c\u6211\u4eec\u5408\u4f5c\u3002\u5012\u662f\u6709\u2014\u4e2a\u53ef\u4ee5\u6c42\u751f\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u55ef!\u8bf4\u8bf4\u770b\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u4ed6\u4eec\u6307\u5b9a\u8981\u6211\u4eec\u751f\u64d2\u7684\u4eba\u4e4b\u4e00\uff0c\u5982\u82e5\u4f60\u80af\u7559\u5728\u8fd9\u91cc\uff0c\u4e5f\u8bb8\u6211\u4eec\u8fd8\u53ef\u4ee5\u548c\u4ed6\u4eec\u8c08\u8c08\u6761\u4ef6\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\uff0c\u957f\u957f\u53f9\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u5176\u5b9e\uff0c\u8fd9\u673a\u4f1a\u4e0d\u5927\uff0c\u4f60\u4eec\u7684\u8033\u76ee\u867d\u7136\u5f88\u7075\u654f\uff0c\u4f46\u4ed6\u4eec\u4e5f\u4e0d\u5f31\uff0c\u4f60\u4f1a\u627e\u5230\u8fd9\u91cc\u7684\u4e8b\uff0c\u4ed6\u4eec\u65e9\u90fd\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a \u201c\u65e9\u77e5\u9053\u4e86\uff0c\u4f60\u662f\u8bf4\uff0c\u4ed6\u4eec\u65e9\u5c31\u6599\u5b9a\u6211\u8981\u6765\u4e86\u3002\u201d \u7389\u7fe0\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u5bf9!\u4ed6\u4eec\u4e5f\u65e9\u5c31\u6599\u5b9a\u2026\u2026\u6240\u4ee5\uff0c\u5982\u8bf4\u7b97\u8ba1\u4ed6\u4eec\u51b3\u4e0d\u5728\u4f60\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c31\u7b97\u662f\u6ca1\u6709\u529e\u6cd5\uff0c\u4f60\u4eec\u4e5f\u6700\u597d\u8bd5\u4e00\u8bd5\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u53f6\u5c11\u4fa0\u7684\u597d\u610f\uff0c\u6211\u4eec\u5f88\u611f\u6fc0\uff0c\u4e0d\u8bba\u6210\u8d25\uff0c\u6211\u4eec\u90fd\u8bd5\u8bd5\u8fd0\u6c14\uff0c\u800c\u4e14\uff0c\u4e5f\u53ef\u4ee5\u4f7f\u53f6\u5c11\u4fa0\u770b\u4e2a\u7ed3\u679c\u51fa\u6765\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u4f60\u597d\u8c61\u4e00\u76f4\u5bf9\u6211\u6709\u7740\u5f88\u6df1\u7684\u6210\u89c1\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\u6700\u597d\u4e0d\u8981\u8bef\u4f1a\uff0c\u6211\u8bf4\u7684\u90fd\u662f\u5f88\u771f\u5b9e\u7684\u8bdd\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d!\u6211\u53bb\u62db\u547c\u4ed6\u4eec\u4e00\u58f0\u3002\u201d \u3000\u3000\u8f6c\u8eab\u51fa\u8231\u800c\u53bb\u3002 \u3000\u3000\u91cd\u56de\u8231\u4e2d\u4e4b\u65f6\uff0c\u5170\u767d\u5df2\u56de\u5165\u5185\u8231\uff0c\u8231\u4e2d\u53ea\u4f59\u4e0b\u7389\u7fe0\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e3e\u624b\u7406\u4e00\u7406\u62ab\u6563\u5728\u80a9\u540e\u7684\u957f\u53d1\uff0c\u7389\u7fe0\u51c4\u51c9\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5170\u767d\u5728\u5185\u8231\uff0c\u53bb\u966a\u966a\u5979\u5427!\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u662f\u8865\u507f?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u4e0d\u5b8c\u5168\u662f\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u4eec\u5e0c\u671b\u4f60\u80fd\u6709\u4e00\u70b9\u60c5\u610f\uff0c\u660e\u5929\u66ff\u6211\u4eec\u6536\u5c38\uff0c\u518d\u8bf4\uff0c\u5979\u4eba\u95f4\u8d70\u4e00\u906d\uff0c\u6765\u53bb\u4e4b\u95f4\uff0c\u603b\u5e94\u7559\u4e0b\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u8001\u5b9e\u8bf4\uff0c \u4f60\u4eec\u59ca\u59b9\uff0c\u82b1\u6735\u4e00\u6837\u7684\u4eba\u513f\uff0c\u4eba\u89c1\u4eba\u7231\uff0c\u4f46\u8fd9\u662f\u4e58\u4eba\u4e4b\u5371\uff0c\u6211\u53f6\u67d0\u4e0d\u80fd\u505a\u8fd9\u79cd\u4e8b\uff0c\u518d\u8bf4\uff0c\u660e\u5929\u53ea\u8981\u4ed6\u4eec\u6709\u4eba\u6765\uff0c\u6211\u4fdd\u8bc1\u4f60\u4eec\u59ca\u59b9\u4e0d\u4f1a\u6bd2\u53d1\u6b7b\u4ea1\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u89c1\u5f97\u4f1a\u4ea4\u51fa\u89e3\u836f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u54b1\u4eec\u53ef\u4ee5\u62a2\uff0c\u53ea\u8981\u4ed6\u4eec\u5e26\u7684\u6709\uff0c\u6211\u76f8\u4fe1\u4ed6\u5e26\u4e0d\u8d70\u3002\u201d \u7389\u7fe0\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u8bf4\u7684\u4e0d\u9519\uff0c\u90a3\u4e5f\u4e0d\u8fc7\u662f\u591a\u6d3b\u4e00\u5929\u7f62\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u90a3\u8981\u770b\u4ed6\u4eec\u8eab\u4e0a\u5e26\u6709\u591a\u5c11\u89e3\u836f\u4e86\uff0c\u5982\u82e5\u591f\u591a\uff0c\u4f60\u4eec\u4e5f\u8bb8\u5c31\u6709\u4fdd\u547d\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u6447\u6447\u5934\uff0c\u9053\uff1a \u201c\u4e0d\u53ef\u80fd\uff0c\u4e0d\u8fc7\uff0c\u53f6\u5c11\u4fa0\u8981\u5c3d\u4eba\u4e8b\uff0c\u54b1\u4eec\u59ca\u59b9\u53ea\u597d\u542c\u547d\u884c\u4e8b\u4e86\u3002\u201d \u53f6\u957f\u9752\u5fc3\u4e2d\u4e0d\u7981\u4e3a\u4e4b\u9eef\u7136\uff0c\u60f3\u60f3\u8fd9\u7389\u5170\u53cc\u59dd\uff0c\u672c\u662f\u6c5f\u6e56\u6709\u540d\u7684\u6740\u624b\uff0c\u5982\u4eca\u7adf\u843d\u5f97\u5982\u6b64\u4e0b\u573a\uff0c\u5982\u6b64\u7684\u51c4\u51c9\uff0c\u4e5f\u7b97\u662f\u62a5\u5e94\u4e34\u5934\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u660e\u660e\u77e5\u6653\u6b7b\u671f\uff0c\u4f46\u5374\u53c8\u65e0\u53ef\u5948\u4f55\uff0c\u90a3\u4efd\u75db\u82e6\uff0c\u5b9e\u5728\u662f\u76f8\u5f53\u7684\u6df1\u523b\u4e86\u3002 \u3000\u3000\u806a\u660e\u7075\u6d3b\uff0c\u7f8e\u4e3d\u679c\u6562\u7684\u7389\u7fe0\uff0c\u6b64\u523b\u597d\u8c61\u662f\u5b8c\u5168\u53d8\u4e86\u4e00\u4e2a\u4eba\u4f3c\u7684\uff0c\u4e00\u4e2a\u548c\u8fc7\u53bb\u5b8c\u5168\u4e0d\u540c\u7684\u8f6f\u5f31\u7684\u5973\u4eba\u3002 \u3000\u3000\u4e0d\u77e5\u5979\u662f\u771f\u7684\u8981\u5728\u4e34\u6b7b\u4e4b\u524d\uff0c\u56de\u987e\u2014\u4e0b\u4eba\u751f\u5462\uff0c\u8fd8\u662f\u771f\u7684\u8981\u8865\u507f\u4ec0\u4e48?\u5618\u4e00\u53e3\u6c14\u9053\uff1a \u3000\u3000\u201c\u53f6\u5c11\u4fa0\uff0c \u4f60\u4e0d\u613f\u73b7\u6c61\u4e86\u5170\u767d\u7684\u6e05\u767d\uff0c\u90a3\u662f\u4fa0\u4e49\u884c\u5f84\uff0c\u6211\u4e5f\u4e0d\u4fbf\u591a\u529d\u4f60\u4e86\uff0c\u4f46\u4f60\u5982\u4e0d\u5acc\u6211\u6b8b\u82b1\u8d25\u67f3\uff0c\u6211\u53ef\u4ee5\u966a\u4f60\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u4e0d\u4e0d\u4e0d\uff0c\u5728\u4e0b\u89c9\u7740\uff0c \u54b1\u4eec\u90e8\u5e94\u8be5\u7559\u4e9b\u7cbe\u795e\uff0c\u5e94\u4ed8\u660e\u5929\u7684\u5927\u4e8b\u3002\u201d \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u65e2\u7136\u5982\u6b64\uff0c\u53f6\u5c11\u4fa0\u8bf7\u5230\u5185\u8231\u4f11\u606f\uff0c\u6211\u62db\u547c\u4e8c\u59b9\u51fa\u6765\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a \u201c\u5c82\u53ef\u55a7\u5bbe\u593a\u4e3b\uff0c\u7389\u7fe0\u59d1\u5a18\u8bf7\u5165\u5185\u8231\u4f11\u606f\uff0c\u5728\u4e0b \u5728\u5916\u8231\u6253\u5750\u4e00\u9635\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u4e0d\u592a\u597d\u5427\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u5fae\u5fae\u2014\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u8bf7!\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u5144\u65e2\u5982\u6b64\u8bf4\uff0c\u5c0f\u59b9\u4e5f\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u5b50\u5411\u5185\u8231\u884c\u53bb\u3002 \u3000\u3000\u53e3 \u53e3 \u53e3 \u3000\u3000\u7b2c\u4e8c\u5929\u4e2d\u5348\u65f6\u5206\uff0c\u679c\u7136\u6709\u4e00\u8258\u5c0f\u821f\u884c\u8fd1\u4e86\u753b\u822b\u3002 \u3000\u3000\u4e00\u4e2a\u8eab\u7740\u9752\u8863\u7684\u4e2d\u5e74\u4eba\uff0c\u767b\u4e0a\u4e86\u753b\u822b\uff0c\u7f13\u6b65\u884c\u5165\u8231\u4e2d\u3002 \u3000\u3000\u53f6\u957f\u9752\u5750\u5728\u4e00\u5f20\u6728\u6905\u4e0a\uff0c\u53ea\u4e0d\u8fc7\u8eab\u4e0a\u52a0\u4e86\u4e00\u6761\u7ef3\u7d22\u3002 \u3000\u3000\u660e\u767d\u70b9\u8bf4\uff0c\u4ed6\u662f\u88ab\u6346\u5728\u6905\u5b50\u4e0a\u3002 \u3000\u3000\u7389\u7fe0\u7a7f\u7740\u4e00\u8eab\u6c34\u7eff\u8272\u7684\u8863\u88d9\uff0c\u4f46\u5170\u767d\u4ecd\u7136\u7a7f\u7740\u4e00\u8eab\u52b2\u88c5\uff0c\u800c\u4e14\uff0c\u4f69\u5e26\u7740\u5979\u5f88\u7279\u522b\u7684\u5175\u5203\uff0c\u9e26\u53e3\u5200\u3002 \u3000\u3000\u7389\u7fe0\u8f7b\u8f7b\u5618\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u624d\u6765\uff0c\u6211\u7684\u836f\u6027\u5feb\u8981\u53d1\u4f5c\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u5728\u4e0b\u4e00\u5411\u5bf9\u65f6\u523b\u628a\u63e1\u5f97\u5f88\u51c6\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9752\u8863\u4eba\u671b\u671b\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u662f\u8c01?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u957f\u9752\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u62ff\u6765\u5427!\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4ec0\u4e48?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u89e3\u836f\uff0c\u4f60\u77e5\u9053\u4e48\uff0c\u518d\u8fc7\u7247\u523b\uff0c\u6211\u7684\u6bd2\u6027\u5c31\u8981\u53d1\u4f5c\u4e86\u3002\u201d \u9752\u8863\u4eba\u9053\uff1a\u201c\u4f38\u624b\u5165\u6000\uff0c\u53d6\u51fa\u4e24\u7c92\u4e39\u4e38\uff0c\u201d\u4ea4\u7ed9\u4e86\u5170\u767d\u3002 \u3000\u3000\u5170\u767d\u81ea\u5df1\u541e\u4e0b\u4e00\u7c92\uff0c\u628a\u53e6\u4e00\u7c92\u4ea4\u7ed9\u4e86\u7389\u7fe0\u3002 \u3000\u3000\u7389\u7fe0\u5374\u628a\u90a3\u7c92\u4e39\u836f\u6536\u4e86\u8d77\u6765\uff0c\u6ca1\u6709\u5403\u4e0b\u3002 \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u5403\u4e0b\u53bb\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u6211\u8981\u8bd5\u8bd5\u770b\uff0c\u6211\u4eec\u8eab\u4e2d\u4e4b\u6bd2\uff0c\u8981\u51e0\u65f6\u53d1\u4f5c\uff0c\u6211\u73b0\u5728\u89c9\u5f97\u5f88\u597d\uff0c\u7b49\u6bd2\u6027\u53d1\u4f5c\u4e4b\u65f6\u518d\u5403\u4e0b\u53bb\u4e5f\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u5e0c\u671b\u59d1\u5a18\u80fd\u591f\u8d76\u53ca\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u5509!\u8fd9\u89e3\u836f\u592a\u73cd\u8d35\u4e86\uff0c\u6211\u4eec\u6bcf\u5929\u90fd\u8981\u7b49\u5f85\uff0c\u5b9e\u5728\u4e5f\u662f\u4e00\u4ef6\u5f88\u82e6\u7684\u4e8b\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u53ea\u8981\u4e24\u4f4d\u5408\u4f5c\uff0c\u6211\u4eec\u4e00\u5b9a\u4f1a\u6309\u65f6\u9001\u4e0a\u89e3\u836f\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u73b0\u5728\u60c5\u52bf\u6709\u6240\u4e0d\u540c\uff0c\u8fd9\u53f6\u957f\u9752\u65e2\u7136\u5230\u4e86\u6b64\u5730\uff0c\u6211\u60f3\u4e00\u5b9a\u6709\u5f88\u591a\u4eba\u4f1a\u7ee7\u7eed\u8ffd\u8fc7\u6765\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u4eec\u59ca\u59b9\u4e00\u76f4\u62c5\u5fc3\u4f60\u88ab\u4ed6\u4eec\u6740\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u56e0\u6b64\uff0c\u6211\u4eec\u5e0c\u671b\u4f60\u80fd\u591a\u7ed9\u6211\u4eec\u51e0\u7c92\u89e3\u836f\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1b\u201c\u59d1\u5a18\uff0c\u89e3\u836f\u4e0d\u5728\u6211\u7684\u8eab\u4e0a\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u9601\u4e0b\u53ea\u662f\u4e2a\u4f20\u8fbe\u4ee4\u8c15\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u53ea\u4e0d\u8fc7\u8dd1\u8dd1\u8def\u7f62\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u6de1\u6de1\u4e00\u7b11\u9053\uff1a \u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u4eec\u53ea\u6709\u4e00\u5929\u76fc\u4e00\u5929\u4e86\u3002\u8fd9\u65e5\u5b50\u8fc7\u7684\u4e0d\u662f\u592a\u75db\u82e6\u4e86\u5417?\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u5148\u82e6\u540e\u7518\uff0c\u53ea\u8981\u4e24\u4f4d\u59d1\u5a18\u80fd\u591f\u5c65\u884c\u7ea6\u5b9a\uff0c\u628a\u4ed6\u4eec\u5f15\u4e0a\u753b\u822b\uff0c\u54b1\u4eec\u4e0d\u4f46\u4f1a\u89e3\u4e86\u4e24\u4f4d\u59d1\u5a18\u7684\u8eab\u4e2d\u4e4b\u6bd2\uff0c\u800c\u4e14\uff0c\u8fd8\u6709\u4e00\u7b14\u539a\u8d60\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u5509!\u5bf9\u4ed8\u4e00\u4e2a\u53f6\u957f\u9752\uff0c \u5df2\u4f7f\u5f97\u6211\u4eec\u59ca\u59b9\u4ed8\u4e86\u5f88\u5927\u7684\u4ee3\u4ef7\uff0c\u4f60\u77e5\u9053\u5417?\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4e03\u5251\u8ffd\u9b42\uff0c\u4e0d\u662f\u597d\u4e0e\u4eba\u7269\uff0c\u8d35\u59ca\u59b9\u81ea\u7136\u662f\u8f9b\u82e6\u4e00\u4e9b\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1b\u201c\u82e5\u4ee5\u6b66\u529f\u786c\u62fc\uff0c\u5c31\u7b97\u662f\u6211\u4eec\u59ca\u59b9\u4e00\u9f50\u51fa\u624b\uff0c\u4e5f\u4e0d\u4f1a\u662f\u4ed6\u654c\u624b\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u82f1\u96c4\u96be\u8fc7\u7f8e\u4eba\u5173\uff0c\u738b\u5170\u53cc\u86db\uff0c\u5a07\u8273\u5982\u82b1\uff0c\u5bf9\u4ed8\u90a3\u4e9b\u82f1\u96c4\u4eba\u7269\uff0c\u81ea\u7136\u662f\u8f7b\u800c\u6613\u4e3e\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u770b\u6765\uff0c \u4f60\u4eec\u5229\u7528\u6211\u4eec\u7684\uff0c \u4e5f\u5c31\u662f\u8fd9\u4e9b\u4e86\u3002\u201d \u9752\u8863\u4eba\u9053\uff1a \u201c\u4e24\u4f4d\u7684\u8eab\u624b\uff0c\u867d\u7136\u4e0d\u9519\uff0c \u4f46\u8981\u51ed\u6b64\u95ef\u8361\u6c5f\u6e56\uff0c\u6253\u5f00\u4e00\u756a\u5c40\u9762\uff0c\u53ea\u6015\u8fd8\u4e0d\u662f\u5f88\u5bb9\u6613\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u7389\u7fe0\u82e6\u7b11\u4e00\u4e0b\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u6211\u4eec\u7684\u6b66\u529f\uff0c\u4e5f\u4e0d\u7b97\u592a\u5dee\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u53ef\u662f\u4e0d\u76f8\u4fe1\u81ea\u5df1\u4e2d\u4e86\u6bd2\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u6b63\u5728\u6c42\u8bc1\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u597d!\u54b1\u4eec\u603b\u5f97\u8ba9\u59d1\u5a18\u8bc1\u5b9e\u4e86\uff0c \u624d\u80fd\u8c08\u4e0b\u53bb\u4e86\u3002\u201d \u53cc\u65b9\u7684\u4ea4\u8c08\u505c\u4e0b\u3002 \u3000\u3000\u753b\u822b\u4e2d\u4e00\u7247\u9759\uff0c\u9759\u5f97\u542c\u4e0d\u5230\u4e00\u70b9\u58f0\u606f\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u7389\u7fe0\u611f\u89c9\u8179\u4e2d\u5fae\u5fae\u4f5c\u75db\uff0c\u52fa\u7ea2\u7684\u8138\u513f\u4e0a\uff0c\u4e5f\u6cdb\u8d77\u4e86\u4e00\u80a1\u9ed1\u6c14\u3002 \u3000\u3000\u8fd9\u662f\u6bd2\u6027\u53d1\u4f5c\u4e4b\u5f81\u3002 \u3000\u3000\u7389\u7fe0\u6025\u6025\u541e\u4e0b\u4e86\u624b\u4e2d\u7684\u836f\u4e38\u3002 \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u8bc1\u5b9e\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u597d\u50cf\u662f\u771f\u4e2d\u4e86\u6bd2\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u597d\u6781\u4e86\uff0c\u59d1\u5a18\u53ef\u4ee5\u628a\u8fd9\u53f6\u957f\u9752\u4ea4\u7ed9\u5728\u4e0b\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53ef\u4ee5\uff0c\u6211\u5df2\u70b9\u4e86\u4ed6\u51e0\u5904\u7a74\uff0c\u4f60\u53ef\u4ee5\u5e26\u8d70\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7f13\u6b65\u5411\u53f6\u957f\u9752\u884c\u4e86\u8fc7\u53bb\uff0c\u4e00\u9762\u5c0f\u5fc3\u6212\u5907\u3002 \u3000\u3000\u884c\u8fd1\u53f6\u957f\u9752\u65f6\uff0c\u53c8\u7a81\u7136\u51fa\u624b\uff0c\u70b9\u4e86\u4ed6\u4e24\u5904\u7a74\u9053\u3002 \u3000\u3000\u89e3\u4e86\u6346\u7ed1\u53f6\u957f\u9752\u8eab\u4e0a\u7684\u7d22\u7ef3\uff0c\u9053\uff1a \u201c\u53f6\u957f\u9752\u6211\u5148\u5e26\u8d70\uff0c\u660e\u5929\u4e2d\u5348\uff0c\u6211\u518d\u9001\u836f\u6765\u3002\u201d \u5f88\u5c11\u8bf4\u8bdd\u7684\u5170\u767d\uff0c\u7a81\u7136\u63a5\u53e3\u8bf4\u9053\uff1a \u201c\u6211\u4eec\u8fd9\u6837\u8bda\u5fc3\u7ed9\u4f60\u4eec\u505a\u4e8b\uff0c\u4f60\u4eec\u4f3c\u4e4e\u662f\u4ecd\u7136\u4e0d\u80af\u4fe1\u4efb\u6211\u4eec\u3002\u201d \u3000\u3000\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u7f13\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u7389\u7fe0\u914d\u5408\u5f97\u5f88\u597d\uff0c\u4e5f\u4e3e\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u66ff\u6211\u4eec\u9001\u4e86\u4e0d\u5c11\u6b21\u7684\u836f\u4e86\uff0c\u6211\u4eec\u8fd8\u4e0d\u77e5\u9053\u8001\u5144\u600e\u4e48\u79f0\u547c\u3002\u201d \u3000\u3000\u79fb\u52a8\u7684\u8eab\u8eaf\uff0c\u6b63\u597d\u906e\u53bb\u4e86\u9752\u8863\u4eba\u7684\u89c6\u7ebf\u3002 \u3000\u3000\u5170\u767d\u8fc5\u5feb\u51fa\u624b\uff0c\u63a8\u6d3b\u4e86\u53f6\u957f\u9752\u7684\u7a74\u9053\uff0c\u4eba\u4e5f\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u5f53\u771f\u7684\u5f88\u5173\u5fc3\u5728\u4e0b\u5417?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u5509!\u6211\u4eec\u59ca\u59b9\u5982\u82e5\u662f\u4e0d\u60f3\u6b7b\uff0c\u8fd8\u771f\u8981\u548c\u4f60\u8001\u5144\u62c9\u4e0a\u4ea4\u60c5\u624d\u884c\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u7389\u59d1\u5a18\u5982\u4e0d\u5f00\u53e3\uff0c\u5728\u4e0b\u5012\u4e5f\u4e0d\u4fbf\u6bdb\u9042\u81ea\u8350\u4e86\u3002\u201d \u7389\u7fe0\u9053\uff1a \u201c\u6211\u4eec\u5185\u8231\u4e2d\uff0c\u5b58\u4e86\u4e0d\u5c11\u91d1\u94f6\u73e0\u5b9d\uff0c\u8001\u5144\u8981\u4ec0\u4e48?\u53ea\u7ba1\u5429\u5490\u4e00\u58f0\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6211\u867d\u7136\u4e0d\u7b97\u6709\u94b1\uff0c\u4f46\u603b\u53ef\u4fdd\u4e2a\u8863\u98df\u65e0\u5fe7\uff0c\u800c\u4e14\uff0c\u54b1\u4eec\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u7684\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u628a\u94b1\u770b\u5f97\u5f88\u91cd\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8001\u5144\u662f\u4e0d\u7231\u94b1\u4e86\uff0c\u4f46\u4e0d\u77e5\u4f60\u559c\u6b22\u4ec0\u4e48\uff1f\u201d \u9752\u8863\u4eba\u9053\uff1a\u201c\u9664\u4e86\u94b1\u4e4b\u5916\uff0c \u4f60\u4eec\u8fd8\u6709\u4ec0\u4e48?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u9664\u4e86\u94b1\uff0c\u53ea\u6709\u6211\u4eec\u59ca\u59b9\u4e24\u4e2a\u4eba\u4e86\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u597d!\u90a3\u6211\u5c31\u8981\u4eba\u5427!\u201d \u3000\u3000\u7389\u7fe0\u6487\u5634\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u8001\u5144\uff0c \u4f60\u597d\u6c89\u5f97\u4f4f\u6c14\uff0c\u65e2\u7136\u60f3\u8981\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u4e00\u4e9b\u8bf4\u5462?\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u5728\u4e0b\u89c9\u7740\u6709\u5f88\u591a\u4e0d\u4fbf\u5f00\u53e3\u4e4b\u5904\uff0c\u5982\u8d35\u59ca\u59b9\u662f\u90a3\u79cd\u89c6\u6b7b\u5982\u5f52\u7684\u4eba\uff0c\u5728\u4e0b\u5c31\u7b97\u662f\u5f00\u4e86\u53e3\uff0c\u8fd8\u4e0d\u662f\u4e00\u6837\u8981\u78b0\u9489\u5b50\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\u554a!\u8fd9\u79cd\u4e8b\uff0c\u8981\u4e24\u76f8\u60c5\u613f\u624d\u597d\u2026\u2026\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f46\u4e0d\u77e5\u4e24\u4f4d\u59d1\u5a18\u51e0\u65f6\u53ef\u4ee5\u8ba9\u5728\u4e0b\u4e00\u4eb2\u82b3\u6cfd\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u73b0\u5728\u5c31\u884c\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u53f6\u957f\u9752\uff0c\u63a5\u9053\uff1a\u201c\u73b0\u5728\uff0c\u53ea\u6015\u662f\u4e0d\u592a\u65b9\u4fbf\u5427!\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u8272\u80c6\u5305\u5929\uff0c\u4f60\u96be\u9053\u6ca1\u6709\u542c\u4eba\u8bf4\u8fc7\u8fd9\u53e5\u8bdd\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u53ef\u60dc\u8fd8\u6709\u4e00\u4f4d\u540c\u4f34\u5728\u8239\u4e0a\u7b49\u6211\u3002\u201d \u3000\u3000\u7389\u7fe0\u4f4e\u58f0\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u65b9\u4fbf\uff0c\u4f60\u80fd\u4e0d\u80fd\u665a\u4e0a\u6765\uff1a\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u9053\uff1a\u201c\u665a\u4e0a\uff0c\u884c\uff0c\u665a\u4e0a\u4ec0\u4e48\u65f6\u95f4?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u770b\u4f60\u7684\u65b9\u4fbf\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4e8c\u66f4\u65f6\u5206\u5982\u4f55?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u4eec\u59ca\u59b9\u606d\u5019\u5927\u9a7e\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u597d!\u53f6\u957f\u9752\u5148\u653e\u5728\u8fd9\u91cc\uff0c\u4e8c\u66f4\u65f6\u5206\u6211\u518d\u6765\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u826f\u5bb5\u82e6\u77ed\uff0c\u6e29\u67d4\u60c5\u957f\uff0c\u9601\u4e0b\u6765\uff0c\u5e0c\u671b\u80fd\u7ed9\u6211\u4eec\u5e26\u6765\u6b22\u4e50\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u5b81\u5728\u82b1\u4e0b\u6b7b\uff0c\u505a\u9b3c\u4e5f\u98ce\u6d41\uff0c\u5728\u4e0b\u4f1a\u5c3d\u529b\u800c\u4e3a\u3002\u201d \u3000\u3000\u8f6c\u8eab\u4e00\u8dc3\uff0c\u79bb\u4e86\u753b\u822b\u3002 \u3000\u3000\u5170\u767d\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u8be5\u6b7b\u7684\u5bb6\u4f19\uff0c\u4e5f\u4e0d\u77a7\u77a7\u81ea\u5df1\u90a3\u526f\u5fb7\u884c\u3002\u201d \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u770b\u4ed6\u665a\u4e0a\u662f\u4e0d\u662f\u771f\u7684\u4f1a\u6765\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u770b\u6837\u5b50\u4ed6\u5df2\u7ecf\u541e\u4e0b\u4e86\u9975\uff0c\u5927\u6982\u662f\u4e0d\u4f1a\u53d8\u5366\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4e0d\u77e5\u4ed6\u80fd\u5e26\u6765\u591a\u5c11\u89e3\u836f?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u5c31\u8981\u770b\u4ed6\u6709\u591a\u5927\u7684\u6743\u529b\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u4ed6\u4f1a\u5e26\u4e00\u4e9b\u6765\uff0c\u4f46\u7edd\u5bf9\u4e0d\u4f1a\u592a\u591a\uff0c\u4ed6\u662f\u4e2a\u5f88\u5c0f\u5fc3\u7684\u4eba\u3002\u201d \u5170\u767d\u9053\uff1a \u201c\u59d0\u59d0\uff0c\u6211\u770b\u4ed6\u4e00\u526f\u8272\u8ff7\u8ff7\u7684\u6837\u5b50\uff0c\u82e5\u4e0d\u8ba9\u4ed6\u5360\u5230\u4fbf\u5b9c\uff0c\u53ea\u6015\u4ed6\u4e0d\u80af\u4ea4\u51fa\u89e3\u836f\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u9053\uff1a \u201c\u8fd9\u4ef6\u4e8b\u7531\u59ca\u59ca\u6765\u5e94\u4ed8\uff0c\u4f60\u4e0d\u7528\u62c5\u5fc3\uff0c\u59d4\u66f2\u4e86\u53f6\u5c11\u4fa0\uff0c\u4f60\u597d\u597d\u7684\u966a\u966a\u4ed6\u5427\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u7ad9\u8d77\u8eab\u5b50\uff0c\u4f38\u5c55\u4e00\u4e0b\u53cc\u81c2\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u4f60\u4eec\u59ca\u59b9\u597d\u597d\u8c08\u8c08\uff0c\u5728\u4e0b\u8981\u5750\u606f\u4e00\u4e0b\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u4f60\u968f\u6765\u7684\u4eba\u624b\uff0c\u4f1a\u4e0d\u4f1a\u5728\u56db\u5468\u76d1\u89c6\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u5f88\u64c5\u4e8e\u63a9\u85cf\u81ea\u5df1\u7684\u4eba\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u53f6\u5c11\u4fa0\uff0c\u6211\u89c9\u7740\u4ed6\u7b54\u5e94\u5f97\u592a\u723d\u5feb\uff0c\u5176\u4e2d\u53ef\u80fd\u6709\u8bc8\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u8bba\u662f\u5426\u6709\u8bc8\uff0c\u6211\u4eec\u90fd\u8981\u51c6\u5907\u4e00\u4e0b\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5bf9!\u6709\u5907\u65e0\u60a3\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u53ef\u662f\u53f6\u5144\u4e0d\u80fd\u51fa\u53bb\uff0c\u8981\u5982\u4f55\u4f20\u51fa\u8baf\u606f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u8fd9\u4e2a\uff0c\u5728\u4e0b\u81ea\u6709\u65b9\u6cd5\uff0c\u4e0d\u52b3\u4e24\u4f4d\u8d39\u5fc3\uff0c\u59d1\u5a18\u53ef\u6709\u7ea2\u767d\u8272\u7684\u7ef8\u5e03\u3002\u201d \u5170\u767d\u9053\uff1a\u201c\u7ef8\u5e03\u5012\u662f\u6ca1\u6709\uff0c\u4f46\u6709\u7ee2\u5e15\u8863\u7269\uff0c\u53ef\u5426\u9002\u7528?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u53ef\u4ee5\u3002\u201d \u3000\u3000\u5170\u767d\u53d6\u6765\u7ee2\u5e15\u8863\u7269\uff0c\u7ea2\u767d\u5747\u6709\u3002 \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u53f6\u5144\uff0c\u628a\u8fd9\u4e9b\u5e03\u6302\u51fa\u53bb\uff0c\u4f60\u7684\u4eba\u56fa\u7136\u53ef\u4ee5\u770b\u5230\uff0c\u4ed6\u4eec\u5c82\u4e0d\u662f\u4e00\u6837\u53ef\u4ee5\u770b\u5230\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\u9053\uff1a \u201c\u90a3\u5012\u4e0d\u7528\uff0c\u54b1\u4eec\u5c31\u5e03\u7f6e\u5728\u753b\u822b\u4e4b\u5185\u5c31\u53ef\u4ee5\u4e86\u3002\u201d \u7389\u7fe0\u70b9\u70b9\u5934\u3002 \u3000\u3000\u53f6\u957f\u9752\u5f88\u5feb\u7684\u9762\u6302\u597d\u4e86\u3002 \u3000\u3000\u5170\u767d\u8f7b\u8f7b\u5618\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u4f60\u6446\u5728\u8fd9\u91cc\uff0c\u522b\u4eba\u5982\u4f55\u80fd\u591f\u770b\u5f97\u5230\u5462?\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u5f88\u5feb\u5c31\u4f1a\u6765\u4e86\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u4e0d\u5927\u5de5\u592b\uff0c\u4e00\u8258\u5c0f\u578b\u6e14\u821f\uff0c\u7a81\u7136\u52a0\u5feb\u4e86\u901f\u5ea6\u800c\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u6536\u8d77\u4e86\u7ea2\u5e03\u767d\u7ee2\u3002 \u3000\u3000\u7389\u7fe0\u6ca1\u6709\u591a\u95ee\u3002 \u3000\u3000\u4e8b\u60c5\u5df2\u7ecf\u5f88\u660e\u663e\u4e86\uff0c\u518d\u591a\u95ee\u5c31\u663e\u5f97\u6709\u4e9b\u611a\u8822\u4e86\u3002 \u3000\u3000\u53e3 \u53e3 \u53e3 \u3000\u3000\u4e8c\u66f4\u65f6\u5206\uff0c\u4eca\u591c\u65e0\u6708\uff0c\u5929\u8272\u7279\u522b\u7684\u9ed1\u6697\u3002 \u3000\u3000\u4e00\u4e2a\u4f4e\u6c89\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u7761\u4e86\u6ca1\u6709?\u201d \u3000\u3000\u662f\u90a3\u9752\u8863\u4eba\u7684\u58f0\u97f3\u3002 \u3000\u3000\u7389\u7fe0\u6545\u610f\u88c5\u4f5c\u672a\u542c\u51fa\u6765\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u662f\u54ea\u4e00\u4f4d?\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u5728\u4e0b\u52fe\u901a\uff0c\u548c\u59d1\u5a18\u7ea6\u597d\u4e86\u4eca\u591c\u4e8c\u66f4\u76f8\u89c1\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u5440!\u4f60\u4e00\u76f4\u4e0d\u80af\u8bf4\u540d\u9053\u59d3\uff0c \u5c0f\u59b9\u600e\u77e5\u4f60\u662f\u52fe\u901a\u2026\u2026\u201d \u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u52fe\u5144\u662f\u4e00\u4e2a\u4eba\u6765\u7684\u5417?\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u662f\u554a!\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5feb\u8bf7\u4e0a\u8239\uff0c\u542c\u8bf4\u53f6\u957f\u9752\u4e5f\u5e26\u4e86\u4eba\u6765\uff0c\u8239\u4e0a\u4e0d\u4fbf\u70b9\u706f\u3002\u201d \u3000\u3000\u52fe\u901a\u4e0a\u4e86\u753b\u822b\uff1b \u3000\u3000\u4ed6\u6362\u4e86\u4e00\u8eab\u6df1\u84dd\u8272\u7684\u52b2\u88c5\uff0c\u80cc\u4e0a\u8fd8\u63d2\u4e86\u4e00\u628a\u5355\u5200\u3002 \u3000\u3000\u7389\u7fe0\u662f\u88ad\u767d\u7eb1\u957f\u886b\uff0c\u8d77\u8eab\u7b11\u9053\uff1a\u201c\u52fe\u5144\u8fd8\u5e26\u4e86\u5175\u5203\u3002\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u672c\u4f1a\u4e2d\uff0c\u6709\u5f88\u591a\u9ad8\u624b\uff0c\u8981\u5230\u5929\u660e\u65f6\u523b\uff0c\u624d\u80fd\u8d76\u5230\uff0c\u4f46\u654c\u4eba\u5374\u5df2\u6709\u4eba\u4eca\u591c\u8d76\u5230\uff0c\u5728\u4e0b\u4e0d\u5f97\u4e0d\u5c0f\u5fc3\u4e00\u4e9b\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4ec0\u4e48\u4f1a\u5440?\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u91d1\u9f99\u4f1a\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u91d1\u9f99\u4f1a\uff0c\u4ece\u6765\u6ca1\u6709\u542c\u8fc7\u8fd9\u4e2a\u540d\u5b57\u5440\u3002\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a \u201c\u6211\u4eec\u662f\u4e2a\u9690\u5bc6\u7684\u7ec4\u7ec7\uff0c\u52bf\u529b\u5f88\u5927\uff0c\u4f46\u6c5f\u6e56\u4e0a\u5374\u5f88\u5c11\u4eba\u77e5\u9053\u3002\u201d \u4e00\u9762\u8f6c\u76ee\u56db\u987e\u3002 \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f60\u770b\u4ec0\u4e48?\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u53f6\u957f\u9752\u5462?\u201d \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u662f\u5f88\u6015\u4ed6?\u201d \u3000\u3000\u52fe\u901a\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u6015\u4e0d\u6015\u4ed6\uff0c\u5012\u662f\u53e6\u5916\u4e00\u4ef6\u4e8b\u4e86\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u7559\u8fd9\u4e48\u4e00\u4e2a\u4eba\u5728\u8239\u4e0a\uff0c\u603b\u662f\u6709\u4e9b\u4e0d\u5927\u5bf9\u52b2\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u9053\uff1a \u201c\u4f60\u653e\u5fc3\uff0c\u4ed6\u5df2\u88ab\u6211\u70b9\u4e86\u7761\u7a74\uff0c\u8ddf\u6b7b\u4e86\u5dee\u4e0d\u591a\u2026\u2026\u201d \u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u52fe\u5144\uff0c\u6211\u4eec\u59ca\u59b9\u5728\u6c5f\u6e56\u4e0a\uff0c\u867d\u7136\u662f\u5e72\u7684\u6740\u624b\u5de5\u4f5c\uff0c\u4f46\u5e76\u4e0d\u968f\u4fbf\u548c\u7537\u4eba\u4ea4\u5f80\uff0c \u76ee\u4e0b\u60c5\u666f\uff0c\u6211\u4eec\u54ea\u4f1a\u6709\u5fc3\u60c5\u548c\u4f60\u8c08\u60c5\u8bf4\u7231\uff0c\u91cd\u8981\u7684\u662f\u6211\u4eec\u8981\u5408\u4f5c\uff0c\u6211\u4eec\u59ca\u59b9\u53ef\u4ee5\u7b54\u5e94\u4f60\u4efb\u4f55\u7684\u8981\u6c42\uff0c\u4f46\u6211\u4eec\u8981\u7528\u89e3\u836f\u6765\u4ea4\u6362\uff0c\u603b\u662f\u2014\u6ca1\u9519\u3002\u201d \u3000\u3000\u52fe\u901a\u7b11\u4e00\u7b11\u9053\uff1a \u201c\u59d1\u5a18\u8bdd\u8bf4\u7684\u5f88\u660e\u767d\u4e01\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u5728\u4e0b\u5fc3\u4e2d\u4e5f\u5f88\u660e\u767d\u6211\u52fe\u67d0\u4eba\u7684\u8eab\u4efd\u3001\u8d22\u5bcc\uff0c\u5927\u6982\u4e5f\u90fd\u8fd8\u4e0d\u8db3\u4ee5\u4f7f\u8d35\u59ca\u59b9\u52a8\u5fc3\u3002\u201d \u3000\u3000\u53c8\u9053\uff1a\u201c\u89e3\u836f\u603b\u5f52\u8981\u5e26\u4e00\u70b9\u6765\uff0c\u5426\u5219\uff0c\u4e5f\u4e0d\u597d\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5e26\u591a\u5c11?\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a \u201c\u89e3\u836f\u4e0d\u662f\u5728\u4e0b\u6240\u80fd\u63a7\u5236\uff0c\u4f46\u6211\u4e3a\u4e86\u8d35\u59ca\u59b9\uff0c\u505a\u4e86\u4e00\u4ef6\u5f88\u5192\u9669\u7684\u4e8b\u3002\u201d \u7389\u7fe0\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u5077\u4e86\u89e3\u836f\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u8fd9\u624d\u7b97\u8868\u73b0\u51fa\u4e00\u70b9\u591a\u60c5\u7684\u5730\u65b9\uff0c\u4f46\u4e0d\u77e5\u5077\u4e86\u591a\u5c11?\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u6211\u968f\u624b\u4e00\u6293\uff0c\u591a\u5c11\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u90a3\u4e00\u628a\u6293\uff0c\u603b\u8be5\u6709\u4e2a\u56db\u4e94\u5341\u7c92\u4e86\u3002\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u90a3\u6574\u4e2a\u836f\u74f6\u5185\uff0c\u597d\u50cf\u4e5f\u6ca1\u6709\u56db\u4e94\u5341\u7c92\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u7a76\u7adf\u662f\u591a\u5c11\u561b?\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u5170\u767d\u59d1\u5a18\uff0c\u8bf4\u51fa\u6765\uff0c\u8981\u4f60\u4eec\u4e24\u59ca\u59b9\u5931\u671b\uff0c\u5927\u6982\u53ea\u6709\u56db\u4e94\u7c92\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u54fc\uff01\u80c6\u5c0f\u9b3c\uff0c\u65e2\u7136\u5077\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u591a\u5077\u4e00\u4e9b\u3002\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u6211\u8fd8\u4e0d\u60f3\u88ab\u4ed6\u4eec\u53d1\u89c9\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u90a3\u4e5f\u7b97\u804a\u80dc\u4e8e\u65e0\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u89e3\u836f\u5462?\u62ff\u6765\u7ed9\u6211\u3002\u201d \u3000\u3000\u52fe\u901a\u7b11\u9053\uff1a \u201c\u4e0d\u8981\u8868\u73b0\u5f97\u592a\u53eb\u5728\u4e0b\u4f24\u5fc3\uff0c\u89e3\u836f\u6211\u53ef\u4ee5\u7ed9\u4f60\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u662f\u73b0\u5728\u3002\u201d \u5170\u767d\u9053\uff1a\u201c\u4e0d\u662f\u73b0\u5728\uff0c\u90a3\u8981\u4ec0\u4e48\u65f6\u523b?\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u5170\u767d\u59d1\u5a18\uff0c\u5927\u6982\u6ca1\uff0c\u5fd8\u8bb0\uff0c \u4ee4\u59ca\u8981\u5728\u4e0b\u6765\u6b64\u7684\u7528\u5fc3\u4e86\u3002\u201d \u7389\u7fe0\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4e0d\u8fc7\uff0c\u6211\u60f3\u5148\u770b\u770b\u89e3\u836f\u3002\u201d \u3000\u3000\u52fe\u901a\u6447\u6447\u5934\u9053\uff1a \u201c\u770b\u6765\uff0c\u54b1\u4eec\u4e4b\u95f4\u7684\u8ddd\u79bb\u592a\u8fdc\uff0c\u5728\u4e0b\u544a\u8f9e\u4e86\u3002 \u201d \u3000\u3000\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u4ed6\u51fa\u4e86\u8231\u95e8\uff0c\u7acb\u523b\u5fc3\u5934\u4e00\u9707\u3002 \u3000\u3000\u539f\u6765\uff0c\u4ed6\u4e58\u5750\u800c\u6765\u7684\u5c0f\u821f\uff0c\u5374\u5df2\u4e0d\u77e5\u53bb\u5411\u3002 \u3000\u3000\u970d\u7136\u95f4\u8f6c\u8fc7\u8eab\u5b50\uff0c\u52fe\u901a\u7684\u53f3\u624b\uff0c \u5df2\u63e1\u4f4f\u4e86\u5200\u67c4\u4e4b\u4e0a\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u539f\u6765\uff0c\u8fd9\u53ea\u662f\u4e00\u4e2a\u5708\u5957\uff0c\u6211\u7684\u5c0f\u821f\u5462\uff1f\u201d \u3000\u3000\u7389\u7fe0\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f1a\u4e0d\u4f1a\u88ab\u98ce\u5439\u8d70\u4e86\uff0c \u5982\u82e5\u662f\u6211\u4eec\u52a8\u7684\u624b\u811a\uff0c\u4f60\u81f3\u5c11\u4e5f\u5e94\u542c\u5230\u4e00\u70b9\u58f0\u606f\u3002\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u4e0d\u4f1a\uff0c\u6211\u8bb0\u5f97\uff0c\u628a\u5c0f\u821f\u7cfb\u5728\u4e86\u753b\u822b\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u7389\u7fe0\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u52fe\u901a\uff0c\u4f60\u4ed4\u7ec6\u77a7\u77a7\uff0c\u8fd9\u5468\u56f4\u6ca1\u6709\u4ec0\u4e48\u8239\u53ea\uff0c\u9664\u975e\u4f60\u6765\u65f6\u4e0d\u5c0f\u5fc3\uff0c\u88ab\u4f60\u4eec\u81ea\u5df1\u4eba\u7ed9\u8ddf\u4e0a\u4e86\u3002\u4ed6\u4eec\u5f04\u8d70\u4e86\u4f60\u7684\u5c0f\u8239?\u201d \u3000\u3000\u52fe\u901a\u5446\u4e86\u4e00\u5446\u5fd6\u9053\uff1a\u8fd9\u5012\u4e5f\u6709\u53ef\u80fd\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374\u8bf4\uff1a\u201c\u53f6\u957f\u9752\u5462?\u201d \u3000\u3000\u5170\u767d\u4f38\u624b\u4e00\u6307\uff0c\u9053\uff1a\u201c\u5728\u90a3\u91cc\uff0c\u9759\u9759\u7684\u8eba\u7740\u3002\u201d \u3000\u3000\u52fe\u901a\u51dd\u805a\u76ee\u529b\u770b\u53bb\uff0c\u679c\u7136\u53d1\u89c9\uff0c\u90a3\u91cc\u8eba\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u90a3\u4eba\u7684\u8eab\u4e0a\uff0c\u76d6\u7740\u68c9\u88ab\u3002 \u3000\u3000\u52fe\u901a\u7f13\u6b65\u884c\u4eba\u8231\u4e2d\uff0c\u9053\uff1a\u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u5982\u82e5\u771f\u662f\u5728\u4e0b\u88ab\u4eba\u76ef\u4e0a\u4e86\uff0c\u9a70\u8d70\u5c0f\u8239\uff0c\u90a3\u4f1a\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u5b50\u7684\u7ed3\u679c\u5462?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f60\u662f\u4ed6\u4eec\u7ec4\u7ec7\u4e2d\u4eba\uff0c\u5e94\u8be5\u77e5\u9053\u7684\uff0e\u6bd4\u6211\u4eec\u66f4\u591a\u3002\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u5982\u82e5\u4ed6\u518d\u67e5\u51fa\uff0c\u6211\u5077\u4e86\u89e3\u836f\uff0c\u90a3\u5c31\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u63a5\u9053\uff1a \u201c\u6240\u4ee5\uff0c\u4f60\u6700\u597d\u628a\u89e3\u836f\u4ea4\u7ed9\u6211\u4eec\uff0c\u8d35\u4e0a\u7559\u4e0b\u6211\u4eec\u4e0d\u6740\uff0c\u7528\u5fc3\u5341\u5206\u660e\u663e\uff0c\u8981\u6211\u4eec\u4e3a\u8d35\u7ec4\u7ec7\u6548\u529b\uff0c\u81f3\u5c11\uff0c\u6211\u4eec\u73b0\u5728\u8fd8\u6709\u76f8\u5f53\u7684\u4ef7\u503c\uff0c\u6211\u4eec\u7ef4\u62a4\u4f60\uff0c\u4f60\u7684\u751f\u5b58\u673a\u4f1a\u5c31\u5f88\u5927\u3002\u201d \u3000\u3000\u52fe\u901a\u4f38\u624b\u7531\u6000\u4e2d\u53d6\u51fa\u4e00\u4e2a\u7ee2\u5305\uff0c\u63a5\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u7389\u7fe0\u7684\u8bdd\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u4e00\u7b11\uff0c\u63a5\u8fc7\u7ee2\u5305\uff0c\u6253\u5f00\u770b\u53bb\uff0c\u53ea\u89c1\u90a3\u91cc\u9762\uff0c\u5305\u4e86\u5341\u4e00\u7c92\u836f\u4e38\uff0c\u4e0d\u7981\u4e00\u76b1\u7709\u5934\u3002 \u3000\u3000\u52fe\u901a\u63a5\u9053\uff1a \u201c\u5e0c\u671b\u4ed6\u4eec\u6ca1\u6709\u53d1\u89c9\u6211\u76d7\u53d6\u836f\u7269\uff0c\u660e\u5929\u5348\u65f6\uff0c\u5728\u4e0b\u53ef\u4ee5\u8fde\u7eed\u9001\u836f\u7269\u6765\u3002\u201d \u7389\u7fe0\u628a\u836f\u7269\u4ea4\u7ed9\u4e86\u5170\u767d\uff0c\u9053\uff1a \u201c\u4f60\u77a7\u77a7\uff0c\u8fd9\u662f\u4e0d\u662f\u54b1\u4eec\u5403\u7684\u836f\u4e38\u3002\u201d \u5170\u767d\u9053\uff1a\u201c\u770b\u4e0a\u53bb\u662f\u5f88\u50cf\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6536\u8d77\u6765\u5427\uff01\u201d \u3000\u3000\u52fe\u901a\u5978\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u7684\u89e3\u836f\u4e5f\u5230\u624b\u4e2d\u4e86\uff0c\u7b54\u5e94\u5728\u4e0b\u7684\u4e8b\uff0c\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u63a5\u9053\uff1a\u201c\u5509!\u4f60\u7684\u5c0f\u8239\uff0c\u90fd\u88ab\u4ece\u4e8b\u5077\u8dd1\u4e86\uff0c\u96be\u9053\u5fc3\u4e2d\u4e00\u70b9\u4e5f\u4e0d\u7126\u6025\u5417\uff1f\u201d \u3000\u3000\u52fe\u901a\u82e6\u7b11\u4e00\u4e0b\u9053\uff1a\u201c\u5728\u4e0b\u662f\u6b7b\u5b9a\u4e86\uff0c\u6211\u60f3\u4e5f\u53ea\u6709\u5728\u4f60\u8fd9\u91cc\u8ba8\u53d6\u4e00\u70b9\u4ec0\u4e48\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u6446\u6446\u624b\uff0c\u7b11\u9053\uff1a \u201c\u52fe\u5144\uff0c\u7559\u5f97\u9752\u5c71\u5728\uff0c \u5c82\u6015\u6ca1\u67f4\u70e7\uff0c\u4eca\u65e5\u4f60\u5fc3\u4e2d\u4e0d\u5b89\uff0c\u6211\u4eec\u4e5f\u53d7\u4e86\u5f71\u54cd\uff0c\u4f55\u4e0d\u6362\u4e00\u4e2a\u65f6\u95f4\u3002\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u6211\u73b0\u5728\u7ec8\u4e8e\u660e\u767d\u4e86\uff0c\u4e0d\u8fc7\uff0c \u4f60\u4eec\u7389\u5170\u53cc\u59dd\uff0e\u4e5f\u662f\u6709\u5b57\u6709\u53f7\u7684\u4eba\u7269\uff0c\u60f3\u4e0d\u5230\u8bf4\u8bdd\u7adf\u7136\u4e0d\u7b97\u6570\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u52fe\u5144\uff0c\u522b\u5fd8\u4e86\uff0c\u6211\u4eec\u662f\u5973\u4eba\u2026\u2026\u201d \u3000\u3000\u52fe\u901a\u6012\u9053\uff1a\u201c\u5973\u4eba\u96be\u9053\u4e0d\u662f\u4eba?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u4e00\u8a00\u5982\u5c71\uff0c\u53ef\u6ca1\u6709\u5305\u62ec\u5973\u4eba\u5728\u5185\u3002\u201d \u52fe\u901a\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u4eec\u662f\u800d\u4e86\u52fe\u5927\u7237\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u4e5f\u4e0d\u662f\u800d\u4f60\u3002\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u4f60\u4eec\u628a\u89e3\u836f\u9a97\u5230\u4e86\u624b\u4e2d\u4e4b\u540e\uff0c\u5c31\u7ffb\u8138\u4e0d\u8ba4\u4eba\uff0c\u96be\u9053\u8fd8\u4e0d\u662f\u800d\u6211\u5417?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u7b2c\u4e00\uff0c\u4f60\u5e26\u6765\u7684\u89e3\u836f\u592a\u5c11\uff0c \u53ea\u591f\u6211\u4eec\u59ca\u59b9\u4e94\u5929\u670d\u7528\u3002\u8001\u5b9e\u8bf4\uff0c\u8fd9\u4e00\u70b9\uff0c\u6211\u4eec\u76f8\u5f53\u7684\u4e0d\u6ee1\u610f\u2026\u2026\u201d \u3000\u3000\u52fe\u901a\u63a5\u9053\uff1a\u201c\u6211\u5982\u62ff\u7684\u89e3\u836f\u591a\u4e00\u4e9b\uff0c\u90a3\u4e49\u5982\u4f55?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u89e3\u836f\u591a\u4e00\u4e9b\uff0c\u90a3\u5c31\u4e0d\u540c\u4e86\uff0c\u5982\u4f60\u771f\u80fd\u89e3\u4e0b\u6211\u4eec\u59ca\u59b9\u8eab\u4e0a\u4e4b\u6bd2\uff0c\u6211\u4eec\u5c31\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u8d70\u90a3\u91cc\u53bb?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u5929\u5730\u5982\u6b64\u8fbd\u9614\uff0c\u6211\u4eec\u53ea\u8981\u627e\u2014\u5e2d\u5b89\u8eab\u4e4b\u5730\uff0c\u5c31\u884c\u4e86\u3002\u5982\u679c\u4e2d\u539f\u9053\u4e0a\uff0c\u6ca1\u6709\u6211\u4eec\u7684\u5b58\u8eab\u4e4b\u5730\u3002\u6211\u4eec\u53ef\u4ee5\u51fa\u5173\u3002\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u51fa\u5173\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u6211\u4eec\u59ca\u59b9\uff0c\u90fd\u51c6\u5907\u5ac1\u7ed9\u4f60\u3001\u6211\u4eec\u84c4\u5b58\u4e86\u4e00\u5927\u7b14\u94f6\u5b50\uff0c\u8fd9\u7b14\u94b1\uff0c\u8db3\u591f\u6211\u4eec\u900d\u900d\u9065\u9065\u7684\u8fc7\u2014\u8f88\u5b50\u4e86\u3002\u201d \u3000\u3000\u52fe\u901a\u53f9\u606f\u2014\u58f0\uff0c\u9053\uff1a \u201c\u542c\u8d77\u6765\uff0c\u59a4\u50cf\u662f\u5341\u5206\u52a8\u4eba\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e0d\u662f\u4e09\u5c81\u5b69\u5b50\uff0c\u4f60\u4eec\u8fd9\u505a\u6cd5\uff0c\u770b\u6765\u50cf\u662f\u54c4\u5b69\u5b50\u2014\u6837\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u8bf4\u7684\u5f88\u8ba4\u771f\uff0c\u4fe1\u4e0d\u4fe1\u5728\u4f60\u4e86\u3002\u201d \u3000\u3000\u52fe\u901a\u7a81\u7136\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u597d\u5427!\u73b0\u5728\uff0c \u4f60\u4eec\u59ca\u59b9\u51c6\u5907\u600e\u4e48\u529e?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5c0f\u59b9\u7684\u610f\u601d\u662f\uff0c\u52fe\u5144\u73b0\u5728\u53ef\u4ee5\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a \u201c\u5bf9!\u6211\u5df2\u7ecf\u4ea4\u51fa\u4e86\u89e3\u836f\uff0c\u7559\u5728\u8fd9\u91cc\uff0c\u4e5f\u662f\u65e0\u5473\u5f97\u5f88\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u597d\uff01\u4f60\u4f11\u606f\u7247\u523b\u3002\u4e8c\u59b9\uff0c\u53bb\u66ff\u52fe\u5144\u627e\u4e00\u8258\u5c0f\u8239\u3002\u201d \u3000\u3000\u5170\u767d\u5e94\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d!\u5c0f\u59b9\u8fd9\u5c31\u53bb\u627e\u3002\u201d \u3000\u3000\u51fa\u8231\u4e00\u77a5\uff0c\u7acb\u523b\u56de\u6765\uff0c\u7b11\u4e00\u7b11\u9053\uff1a \u201c\u52fe\u5144\uff0c\u90a3\u8258\u8239\u53ea\u4e0d\u8fc7\u5411\u540e\u98d8\u4e86\u4e00\u70b9\uff0c\u52fe\u5144\u53ea\u8981\u7a0d\u5fae\u7528\u4e00\u70b9\u5fc3\uff0c\u5c31\u53ef\u4ee5\u770b\u5230\u4e86\u3002\u201d \u3000\u3000\u52fe\u901a\u9053\uff1a\u201c\u5728\u4e0b\u5b9e\u5728\u592a\u5927\u610f\u4e86\u4e00\u4e9b\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u73b0\u5728\uff0c\u53ef\u4ee5\u628a\u53f6\u957f\u9752\u4ea4\u7ed9\u6211\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u597d!\u4f60\u8fc7\u53bb\u5e26\u8d70\u5427!\u201d \u3000\u3000\u52fe\u901a\u5411\u524d\u8d70\u4e86\u4e24\u6b65\uff0c\u7a81\u7136\u53c8\u505c\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u6211\u770b\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u8fd8\u662f\u7f13\u4e00\u7f13\u5427\u3002\u201d \u3000\u3000\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u4f46\u95fb\u6a79\u58f0\u6b59\u4e43\uff0c\u5c0f\u8239\u5212\u6c34\u800c\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u5ffd\u7136\u633a\u8eab\u800c\u8d77\uff0c\u7b11\u9053\uff1a \u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u5904\u7406\u7684\u9ad8\u660e\u81f3\u6781\u3002\u201d \u7389\u7fe0\u9053\uff1a\u201c\u4f60\u770b\uff0c\u4ed6\u56de\u53bb\u4e4b\u540e\uff0c\u662f\u4e2a\u4ec0\u4e48\u6837\u5b50?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u8fd9\u4e2a\u4eba\u5f88\u6709\u57ce\u5e9c\uff0c\u800c\u4e14\uff0c\u80fd\u5c48\u80fd\u4f38\uff0c\u6211\u76f8\u4fe1\uff0c\u4ed6\u56de\u53bb\u4f1a\u6709\u4e00\u756a\u4ea4\u4ee3\u3002\u201d \u7389\u7fe0\u70b9\u70b9\u5934\uff1a \u201c\u53cd\u6b63\uff0c\u6211\u4eec\u7684\u76ee\u7684\uff0c\u53ea\u662f\u5e0c\u671b\u627e\u51fa\u4ed6\u4eec\u7684\u85cf\u8eab\u4e4b\u5730\u3002\u201d \u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u6211\u4eec\u5982\u4e0d\u5148\u52a8\u624b\uff0c\u660e\u5929\uff0c\u4e8b\u60c5\u53ef\u80fd\u5c31\u4f1a\u6709\u4e9b\u53d8\u5316\u4e86\u3002\u201d \u5170\u767d\u9053\uff1a\u201c\u4ec0\u4e48\u53d8\u5316?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u4ec0\u4e48\u53d8\u5316\u5728\u4e0b\u65e0\u6cd5\u65ad\u8a00\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e0d\u52a8\uff0c\u654c\u5fc5\u52a8\u3002\u201d \u7389\u7fe0\u9053\uff1a\u201c\u90a3\u4f60\u51c6\u5907\u600e\u4e48\u6253\u7b97?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u76ee\u4e0b\u5e0c\u671b\u80fd\u5148\u627e\u4ed6\u4eec\u7684\u5b58\u8eab\u4e4b\u5730\uff0c\u67e5\u51fa\u4ed6\u4eec\u7684\u5b9e\u529b\uff0c\u7136\u540e\uff0c\u518d\u51b3\u5b9a\u5bf9\u4ed8\u4ed6\u4eec\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u90fd\u5b89\u6392\u597d\u4e86\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5b89\u6392\u597d\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u8fd9\u4e48\u770b\u6765\uff0c\u9634\u9633\u5821\u662f\u4e00\u4e2a\u5f88\u6709\u6548\u7387\u7684\u7ec4\u5408\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u662f\uff0c\u8d35\u59ca\u59b9\u82e5\u6709\u610f\u8fdb\u5165\u9634\u9633\u5821\uff0c\u5728\u4e0b\u5012\u53ef\u4ee5\u4ee3\u4e3a\u5f15\u8350\u3002\u201d \u7389\u7fe0\u9053\uff1a\u201c\u7b97\u5566!\u53cd\u6b63\uff0c\u6211\u4eec\u4e5f\u6d3b\u4e0d\u4e86\u51e0\u5929\u5566\uff0c\u7528\u4e0d\u7740\u518d\u9ebb\u70e6\u4f60\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u53ef\u60dc\uff0c\u5728\u4e0b\u65e0\u6cd5\u89c1\u5230\u655d\u5821\u4e3b\uff0c\u5982\u82e5\u4ed6\u5728\u6b64\u5730\uff0c\u5fc5\u6709\u89e3\u6551\u8d35\u59ca\u59b9\u7684\u529e\u6cd5\u3002\u201d \u7a81\u7136\u633a\u8eab\u800c\u8d77\uff0c\u6293\u8d77\u957f\u5251\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u201c\u4f55\u5bd2\u8863\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4e00\u6014\uff0c\u9053\uff1a\u201c\u662f\u4f55\u5144\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u6ca1\u6709\u6253\u6405\u4e09\u4f4d\u5427?\u201d \u3000\u3000\u53f6\u957f\u9752\u5df2\u8fce\u4e86\u51fa\u53bb\uff0c\u9053\uff1a\u201c\u4f55\u5144\u5feb\u8bf7\u8fdb\u6765\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7f13\u6b65\u5165\u8231\uff0c\u56db\u987e\u4e86\u4e00\u773c\uff0c\u62f1\u624b\u7b11\u9053\uff1a \u201c\u8fd9\u4e24\u4f4d\u59d1\u5a18\uff0c\u5c31\u662f\u540d\u52a8\u6c5f\u6e56\u7684\u7389\u5170\u53cc\u59dd\u4e86?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5bf9\u4f55\u5bd2\u8863\u8fd9\u4e2a\u540d\u5b57\uff0c\u7389\u5170\u53cc\u59dd\u5e76\u4e0d\u592a\u719f\u3002\u4e24\u4eba\u53ea\u662f\u671b\u7740\u4f55\u5bd2\u8863\u5fae\u7b11\u3002 \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u5c31\u662f\u5927\u540d\u9f0e\u9f0e\u7684\u91d1\u5251\u98de\u8f6e\u3002\u201d \u3000\u3000\u5bf9\u91d1\u5251\u98de\u8f6e\uff0c\u7389\u5170\u53cc\u59dd\u662f\u4e45\u95fb\u5176\u540d\u4e86\uff0c\u9f50\u9f50\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u5e78\u4f1a\u4f55\u5144\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5144\u5f1f\u65e9\u5df2\u5230\u4e86\uff0c\u542c\u4e01\u5cf0\u544a\u8bc9\u6211\uff0c\u4f60\u4eec\u5df2\u653e\u7ebf\u9493\u9c7c\uff0c\u4e0d\u4fbf\u8fc7\u6765\u60ca\u6270\uff0c\u9002\u624d\u89c1\u5c0f\u821f\u5df2\u53bb\uff0c\u624d\u5192\u6627\u9020\u8bbf\uff0c\u5982\u60ca\u6270\u5230\u597d\u68a6\uff0c\u90a3\u5c31\u5341\u5206\u62b1\u6b49\u3002\u201d \u3000\u3000\u5170\u767d\u8138\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u6211\u4eec\u4e00\u76f4\u5750\u7740\u8bae\u4e8b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u53f6\u5144\uff0c\u5b9e\u56e0\u8981\u4e8b\u7d27\u6025\uff0c\u4e0d\u4fbf\u5ef6\u8bef\uff0c\u624d\u5728\u6df1\u591c\u4e2d\u4e0d\u901f\u800c\u81f3\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u5144\u5f1f\u4e5f\u60f3\u5f97\u5230\uff0c\u7389\u5170\u53cc\u59dd\u6b64\u756a\u548c\u5144\u5f1f\u5408\u4f5c\uff0c\u5012\u662f\u4e00\u7247\u771f\u60c5\uff0c\u4f55\u5144\u6709\u4ec0\u4e48\u4e8b\uff0c\u5c3d\u7ba1\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u87b3\u8782\u6355\u8749\uff1b\u9ec4\u96c0\u5728\u540e\uff0c\u53f6\u5144\u7684\u884c\u8e2a\uff0c\u65e9\u5df2\u88ab\u4eba\u76ef\u4e0a\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff0c\u5144\u5f1f\u5df2\u7ecf\u76f8\u5f53\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5509!\u54b1\u4eec\u7684\u654c\u624b\u592a\u5f3a\uff0c \u81ea\u7136\u4e0d\u80fd\u602a\u53f6\u5144\u4e86\uff0c\u5e78\u597d\u5821\u4e3b\u65e9\u6709\u51c6\u5907\uff0c\u5144\u5f1f\u5949\u547d\u8d76\u6765\uff0c\u679c\u7136\u53d1\u89c9\u4e86\u654c\u4eba\u5df2\u6709\u5e03\u7f6e\u3002\u201d \u3000\u3000\u7389\u7fe0\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u6211\u8bf4\u5462\uff0c\u4ed6\u4eec\u628a\u6211\u4eec\u4e24\u59ca\u59b9\u6446\u5728\u8fd9\u91cc\uff0c\u7adf\u7136\u4e00\u76f4\u4e0d\u95fb\u4e0d\u95ee\u4e86\u3002\u201d \u53f6\u957f\u9752\u7b80\u7565\u7684\u8bf4\u660e\u4e86\u7ecf\u8fc7\u60c5\u5f62\uff0c\u9053\uff1a\u201c\u4f55\u5144\uff0c\u53ef\u66fe\u67e5\u51fa\u4e86\u4ed6\u4eec\u8ba1\u5212?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u8be6\u7ec6\u5185\u60c5\uff0c\u65e0\u6cd5\u4e86\u89e3\uff0c\u53ea\u80fd\u4ece\u4ed6\u4eec\u7684\u5e03\u7f6e\u4e0a\uff0c\u63a8\u65ad\u51fa\u4e00\u4e9b\u4ed6\u4eec\u7684\u51c6\u5907\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u4eec\u5982\u4f55\u5e03\u7f6e\u7684?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u56db\u8258\u5927\u8239\uff0c\u505c\u6cca\u5728\u4e03\u516b\u91cc\u5916\uff0c\u53e6\u6709\u4e00\u5341\u4e8c\u8258\u5c0f\u821f\uff0c\u5206\u522b\u76ef\u7740\u53f6\u5144\u5c5e\u4e0b\u4e4b\u8239\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u626e\u6210\u6e38\u6e56\u7684\u5ba2\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u8fd8\u6709\u9c7c\u821f\uff0c\u800c\u4e14\uff0c\u4ed6\u4eec\u5e03\u7f6e\u5f88\u65e9\uff0c\u53f6\u5144\u548c\u5c5e\u4e0b\u4e00\u5165\u592a\u6e56\uff0c\u4ed6\u4eec\u5c31\u76ef\u4e0a\u4e86\uff0c\u4e0d\u8fc7\uff0c\u90a3\u56db\u8258\u5927\u8239\uff0c \u65e5\u843d\u4e4b\u524d\uff0c\u624d\u6025\u9a70\u800c\u6765\u3002\u201d \u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f55\u5144\uff0c\u5bf9\u65b9\u7a76\u7adf\u662f\u4f55\u8bb8\u4eba\u7269?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5f53\u4eca\u6b66\u6797\u4e4b\u4e2d\uff0c\u9664\u4e86\u9ad8\u5929\u5065\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u80fd\u6709\u5982\u6b64\u5927\u7684\u5b9e\u529b\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u54b1\u4eec\u65e2\u7136\u8eab\u843d\u9677\u9631\uff0c\u5e94\u5982\u4f55\u5e94\u4ed8?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u9053\uff1a \u201c\u53f6\u5144\u653e\u5fc3\uff0c\u5821\u4e3b\u5e03\u4e0b\u4e86\u5341\u4e8c\u6761\u7591\u5175\u4e4b\u8ba1\uff0c\u5206\u6563\u4e86\u4ed6\u4eec\u5f88\u591a\u5b9e\u529b\uff0c\u4f46\u4ed6\u4eec\u96c6\u5728\u592a\u6e56\u8fd9\u4e00\u80a1\u529b\u91cf\uff0c\u4f3c\u4e4e\u662f\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54b1\u4eec\u65e2\u7136\u88ab\u56f4\uff0c\u6050\u6015\u662f\u5f88\u96be\u8131\u56f0\u800c\u51fa\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6050\u6015\u662f\u96be\u514d\u4e00\u573a\u6fc0\u6218\uff0c\u4e0d\u8fc7\uff0c\u51b3\u6218\u7684\u5730\u65b9\uff0c\u54b1\u4eec\u53ef\u4ee5\u9009\u62e9\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u542c\u4f55\u5144\u53e3\u6c14\uff0c\u4f3c\u4e4e\u662f\u65e9\u5df2\u7ecf\u80f8\u6709\u6210\u7af9\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u5821\u4e3b\u7684\u5b89\u6392\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a \u201c\u4e1c\u884c\u5341\u4f59\u91cc\uff0c\u6709\u4e00\u5c0f\u5c9b\uff0c\u540d\u53eb\u5c0f\u79ef\u5c71\uff0c\u6d45\u6ee9\u4e4e\u9614\u6570\u5341\u4e08\uff0c\u662f\u4e00\u5904\u640f\u6740\u7684\u597d\u6218\u573a\u5462\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f55\u5144\u51c6\u5907\u5728\u90a3\u5730\u65b9\uff0c\u548c\u4ed6\u4eec\u4e00\u51b3\u6b7b\u6218\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5821\u4e3b\u7684\u4ee4\u8c15\u662f\uff0c\u80fd\u591f\u4e00\u4e3e\u628a\u654c\u4eba\u8fd9\u4e00\u80a1\u52bf\u529b\u6d88\u706d\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u5982\u679c\u654c\u52bf\u592a\u5f3a\uff0c\u4e0d\u8bb8\u54b1\u4eec\u786c\u62da\uff0c\u603b\u4e4b\u662f\u80fd\u6218\u5219\u6218\uff0c\u4e0d\u80fd\u6218\u5219\u8d70\uff0c\u4ee5\u4fdd\u5b58\u5b9e\u529b\u4e3a\u4e0a\uff0c\u6211\u5148\u53bb\u5e03\u7f6e\u4e00\u4e0b\uff0c\u53f6\u5144\u4e5f\u8be5\u4e0b\u4ee4\u4e01\u5cf0\uff0c\u8981\u4ed6\u901a\u4ee4\u8d35\u5c5e\uff0c\u5411\u5c0f\u79ef\u5c71\u4f1a\u96c6\uff0c\u5c0f\u79ef\u5c71\u5317\u9762\u662f\u4e00\u7247\u82a6\u82c7\u6797\uff0c\u65ad\u5d16\u7a81\u8d77\uff0c\u897f\u5357\u5904\uff0c\u4e00\u7247\u5e73\u5766\uff0c\u53f6\u5144\u5982\u4f55\u8c03\u914d\uff0c\u6089\u542c\u5c0a\u4fbf\uff0c\u5144\u5f1f\u5148\u8d70\u4e00\u6b65\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f55\u5144\uff0c\u8fde\u676f\u8336\u4e5f\u4e0d\u559d\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u65f6\u95f4\u4e0d\u591a\uff0c\u4e0d\u6562\u6709\u52b3\uff0c\u6b22\u8fce\u8d35\u59ca\u59b9\u548c\u53f6\u5144\u540c\u5f80\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u4e00\u5219\uff0c\u6211\u4eec\u5df2\u65e0\u5904\u53ef\u53bb\uff0c\u4e8c\u5219\uff0c\u6211\u4eec\u5df2\u6ca1\u6709\u51e0\u5929\u597d\u6d3b\uff0c\u81ea\u5f53\u8ffd\u968f\u53f6\u5144\uff0c\u7565\u5c3d\u7ef5\u529b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u7b49\u4e00\u4f1a\u513f\u89c1\u3002\u201d \u3000\u3000\u53cc\u80a9\u4e00\u6643\uff0c\u4eba\u5df2\u7a7f\u51fa\u8231\u5916\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u5728\u7532\u677f\u4e0a\u7565\u4e00\u501f\u529b\uff0c\u8fd4\u5411\u5916\u6e56\u4e2d\u5c04\u53bb\u3002 \u3000\u3000\u539f\u6765\uff0c\u4ed6\u4e58\u6765\u68ad\u5f62\u5feb\u821f\uff0c\u505c\u5728\u4e24\u4e08\u4ee5\u5916\uff0c\u867d\u7136\u591c\u8272\u6c89\u6c89\uff0c\u4ecd\u7136\u98de\u8dc3\u767b\u821f\uff0c\u53ea\u542c\u6728\u6868\u62e8\u6c34\uff0c\u5c0f\u821f\u5df2\u5982\u98de\u800c\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u884c\u51fa\u8231\u95e8\uff0c\u5c0f\u821f\u5df2\u5230\u4e86\u4e94\u4e08\u4e4b\u5916\u3002 \u3000\u3000\u4f46\u753b\u822b\u7684\u65c1\u4fa7\uff0c\u53e6\u5916\u505c\u7740\u4e00\u8258\u5c0f\u8239\uff0c\u4e01\u5cf0\u3001\u9a6c\u826f\uff0c\u6b63\u8083\u7acb\u5019\u547d\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7acb\u523b\u4f20\u4ee4\uff0c\u6240\u6709\u7684\u4eba\u624b\uff0c\u96c6\u4e2d\u5411\u5c0f\u5e1c\u5c71\u3002\u201d \u3000\u3000\u4e01\u5cf0\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u7acb\u523b\u4f20\u4ee4\uff0c\u5251\u4f7f\u662f\u5426\u8981\u56de\u8239\u4e0a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u4e0d\u7528\u4e86\uff0c\u8fd9\u8258\u753b\u822b\uff0c\u6700\u5f15\u4ed6\u4eec\u6ce8\u610f\uff0c\u6211\u5c31\u5728\u8fd9\u8258\u8239\u4e0a\u5427!\u201d \u3000\u3000\u4e01\u5cf0\u8f7b\u8f7b\u4e00\u8dc3\uff0c\u98de\u4e0a\u753b\u822b\u7532\u677f\uff0c\u9053\uff1a \u201c\u9a6c\u5144\u53bb\u4f20\u5251\u4f7f\u4ee4\u8c15\uff0c\u6211\u7559\u6b64\u4f3a\u5019\u5251\u4f7f\uff0c\u6700\u597d\uff0c\u518d\u6d3e\u4e24\u4e2a\u64c5\u4e8e\u64cd\u821f\u7684\u4eba\u6765\u3002\u201d \u3000\u3000\u9a6c\u826f\u70b9\u70b9\u5934\uff0c\u98de\u821f\u800c\u53bb\u3002 \u3000\u3000\u7389\u5170\u53cc\u59dd\u770b\u5f97\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u5979\u4eec\u7b2c\u4e00\u6b21\u89c1\u5230\u4e86\u4e00\u4e2a\u6c5f\u6e56\u7ec4\u5408\u7684\u884c\u52a8\uff0c\u5982\u6b64\u7684\u4e25\u5bc6\u3001\u8fc5\u901f\u3002 \u3000\u3000\u4e0d\u8fc7\u4e00\u987f\u996d\u5de5\u592b\uff0c\u9a6c\u826f\u7684\u5feb\u821f\u53c8\u9a70\u8fd1\u753b\u822b\uff0c\u4e24\u4e2a\u7cbe\u58ee\u9ed1\u8863\u5927\u6c49\uff0c\u767b\u4e0a\u4e86\u7532\u677f\u3002\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u8521\u5149\u3001\u674e\u70c8\uff0c\u89c1\u8fc7\u5251\u4f7f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u7528\u591a\u793c\uff0c\u4e24\u4f4d\u8bf7\u5165\u8231\u4e2d\u5750\u5427\uff01\u201d \u3000\u3000\u8521\u5149\u3001\u674e\u70c8\uff0c\u53e3\u4e2d\u79f0\u8c22\uff0c\u4eba\u5374\u884c\u5411\u7d22\u8235\uff0c\u6447\u6a79\u7684\u8239\u5c3e\u3002 \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u5c5e\u4e0b\u5df2\u4f20\u4e0b\u5251\u4f7f\u4ee4\u8c15\uff0c\u96c6\u4e2d\u5c0f\u79ef\u5c71\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u597d!\u4f60\u4e58\u5927\u8239\uff0c\u5e26\u4ed6\u4eec\u7acb\u523b\u542f\u884c\uff0c\u6211\u548c\u4e01\u5cf0\u65ad\u540e\u3002\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u4e00\u6325\u624b\uff0c\u63a5\u9053\uff1a\u201c\u5feb\u4e9b\u53bb\u5427\uff01\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u4fdd\u62a4\u5251\u4f7f\u3002\u201d \u3000\u3000\u4e01\u5cf0\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u53eb\u4ed6\u4eec\u5feb\u4e00\u4e9b\u3002\u201d \u3000\u3000\u9a6c\u826f\u5e94\u4e00\u58f0\uff0c\u6389\u821f\u800c\u53bb\u3002 \u3000\u3000\u83b2\u82b1\u53f7\u8c6a\u534e\u6e38\u8239\u4e0a\uff0c\u9ad8\u6311\u8d77\u4e24\u76cf\u706f\u53f7\uff0c\u9996\u5148\u8d77\u951a\u800c\u884c\u3002 \u3000\u3000\u53f6\u957f\u9752\u624b\u4e2d\u63e1\u7740\u957f\u5251\uff0c\u5168\u795e\u6ce8\u89c6\u6e56\u9762\u3002 \u3000\u3000\u4ed6\u7b2c\u4e00\u6b21\uff0c\u9886\u5bfc\u7740\u5c5e\u4e0b\u5bf9\u654c\uff0c\u4e0d\u5e0c\u671b\u6709\u4ec0\u4e48\u635f\u5931\uff0c\u5fc3\u4e2d\u96be\u514d\u6709\u4e9b\u7d27\u5f20\u3002 \u3000\u3000\u773c\u770b\u7fa4\u8239\u542f\u884c\uff0c\u6392\u6210\u4e00\u884c\u75be\u9a70\uff0c\u624d\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u4e01\u5cf0\uff0c\u597d\u50cf\u5c11\u4e86\u4e8c\u8258\u6e38\u8239\u3002\u201d \u4e01\u5cf0\u9053\uff1a \u201c\u5251\u4f7f\u660e\u5bdf\uff0c\u54b1\u4eec\u4e00\u5171\u4e5d\u8258\u6e38\u8239\uff0c\u4e24\u8258\u4e0a\u7684\u4eba\u624b\uff0c\u5df2\u7ecf\u96c6\u4e2d\u5728\u83b2\u82b1\u53f7\u4e0a\uff0c\u4ee5\u589e\u5f3a\u83b2\u82b1\u53f7\u4e0a\u5b9e\u529b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8c03\u914d\u7684\u5f88\u597d\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u6570\u767e\u4e08\u5916\uff0c\u7a81\u7136\u4e5f\u4eae\u8d77\u4e86\u706f\u53f7\uff0c\u56db\u8258\u5feb\u821f\uff0c\u6025\u9a70\u8ffd\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u4e9b\u8239\uff0c\u4e0d\u662f\u54b1\u4eec\u7684\u4e86?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u706f\u53f7\u4e0d\u540c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d!\u9a70\u8fc7\u53bb\uff0c\u622a\u67e5\u90a3\u56db\u8258\u5feb\u821f\u3002\u201d \u3000\u3000\u4e01\u5cf0\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u4f60\u8981\u7edf\u5e05\u5168\u5c40\uff0c\u4e0d\u53ef\u6d89\u9669\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u6211\u89c9\u7740\uff0c\u8fd9\u4e9b\u670b\u53cb\u65e2\u7136\u8ddf\u7740\u6211\uff0c\u6211\u5e94\u8be5\u7167\u987e\u4ed6\u4eec\uff0c\u4e5f\u5e94\u8be5\u8eab\u5148\u58eb\u5352\u3002\u201d \u3000\u3000\u4e01\u5cf0\u4f3c\u662f\u8fd8\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u5374\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8521\u5149\u3001\u674e\u70c8\uff0c\u628a\u8239\u9a70\u8fc7\u53bb\u3002\u201d \u3000\u3000\u8521\u5149\u5e94\u4e86\u4e00\u58f0\uff0c\u753b\u822b\u7a81\u7136\u5411\u524d\u98de\u9a70\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u64cd\u8239\u7684\u6280\u672f\uff0c\u9ad8\u660e\u5f02\u5e38\uff0c\u800c\u4e14\u81c2\u529b\u5947\u5927\uff0c\u90a3\u753b\u822b\u867d\u975e\u5c0f\u821f\uff0c\u4f46\u5728\u4e24\u4eba\u5408\u529b\u64cd\u7eb5\u4e4b\u4e0b\uff0c\u4ecd\u7136\u5341\u5206\u7075\u6d3b\u3002 \u3000\u3000\u753b\u822b\u5f88\u5feb\u7684\u62e6\u5728\u56db\u8258\u5feb\u821f\u7684\u524d\u9762\u3002 \u3000\u3000\u4e01\u5cf0\u6025\u884c\u4e24\u6b65\uff0c\u6321\u5728\u4e86\u53f6\u957f\u9752\u7684\u8eab\u524d\u3002 \u3000\u3000\u56db\u8258\u5c0f\u821f\uff0c\u884c\u901f\u6781\u5927\uff0c\u5f53\u5148\u4e00\u8258\uff0c\u76f4\u5411\u753b\u822b\u649e\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u53cc\u65b9\u7684\u64cd\u821f\u6280\u672f\uff0c\u90fd\u5f88\u719f\u7ec3\uff0c\u5c31\u5728\u53cc\u821f\u5c06\u8981\u63a5\u7684\u4e00\u77ac\u95f4\uff0c\u5c0f\u821f\u7a81\u7136\u4e00\u504f\u5934\uff0c\u60f3\u4ece\u753b\u822b\u7684\u4e00\u4fa7\u6ed1\u8fc7\u53bb\u3002 \u3000\u3000\u4f46\u90a3\u753b\u822b\u5374\u4f3c\u662f\u6709\u610f\u7684\u548c\u5c0f\u821f\u649e\u4e0a\uff0c\u4ee5\u5927\u5403\u5c0f\uff0c\u8239\u5c3e\u6025\u6446\uff0c\u6a2a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u7830\u7136\u4e00\u58f0\u5927\u9707\uff0c\u4f20\u5165\u8033\u9645\u3002 \u3000\u3000\u4f46\u649e\u4e0a\u7684\u4e0d\u662f\u4e24\u8258\u8239\uff0c\u800c\u662f\u674e\u70c8\u6284\u8d77\u4e86\u753b\u822b\u4e0a\u7684\u7af9\u7bd9\uff0c\u731b\u529b\u7684\u70b9\u5728\u4e86\u68ad\u5f62\u5c0f\u821f\u4e4b\u4e0a\u3002 \u3000\u3000\u674e\u70c8\u7528\u529b\u5947\u731b\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u7af9\u7bd9\u6298\u65ad\u3002 \u3000\u3000\u4f46\u90a3\u68ad\u5f62\u5feb\u821f\u7684\u8239\u8eab\uff0c\u5374\u88ab\u523a\u4e86\u4e00\u4e2a\u5927\u6d1e\u3002 \u3000\u3000\u5c0f\u821f\u4e0a\u64cd\u8239\u4eba\u7684\u6280\u672f\uff0c\u867d\u7136\u9ad8\u660e\uff0c\u4f46\u5c71\u65e0\u6cd5\u627f\u53d7\u5982\u6b64\u7684\u91cd\u51fb\u3002 \u3000\u3000\u4e0d\u4f46\u5c0f\u821f\u88ab\u649e\u7834\u4e86\uff0c\u800c\u4e14\uff0c\u6574\u4e2a\u7684\u8239\u4e5f\u5931\u53bb\u4e86\u63a7\u5236\uff0c\u5728\u6c34\u9762\uff0e\u6253\u4e86\u4e24\u4e2a\u65cb\u8f6c\u3002 \u3000\u3000\u7830\u7136\u4e00\u58f0\uff0c\u53e6\u4e00\u8258\u5c0f\u821f\uff0c\u8f6c\u53d8\u4e0d\u53ca\uff0c\u649e\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u4f46\u53e6\u5916\u4e24\u8258\u5feb\u821f\uff0c\u5374\u504f\u5934\u6ed1\u8fc7\uff0c\u4ecd\u7136\u5411\u524d\u8ffd\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u4f4e\u58f0\u9053\uff1a\u201c\u62e6\u4f4f\u4ed6\u4eec\u3002\u201d \u3000\u3000\u98de\u8eab\u800c\u8d77\uff0c\u843d\u5728\u5c0f\u821f\u4e4b\u4e0a\u3002 \u3000\u3000\u5c31\u5728\u53f6\u957f\u9752\u53d1\u52a8\u7684\u540c\u65f6\uff0c\u53e6\u5916\u4e00\u6761\u4eba\u5f71\uff0c\u5374\u7a81\u7136\u95f4\u75be\u5982\u6d41\u661f\u4e00\u822c\uff0c\u4e5f\u98de\u5411\u53e6\u4e00\u8258\u5c0f\u821f\u3002 \u3000\u3000\u90a3\u662f\u5170\u767d\u3002 \u3000\u3000\u53f6\u957f\u9752\u53cc\u8db3\u4e00\u7740\u7532\u677f\uff0c\u53f3\u624b\u4e00\u63a2\uff0c\u4e00\u80a1\u5f3a\u5927\u7684\u638c\u529b\uff0c\u5288\u4e86\u51fa\u53bb\uff0c\u51fb\u5411\u4e00\u4e2a\u6447\u6a79\u7684\u9ed1\u8863\u5927\u6c49\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u5927\u6c49\u5c31\u7b97\u4e0d\u7406\u4f1a\u4e5f\u4e0d\u884c\u4e86\uff0c\u53ea\u597d\u4e00\u626c\u53f3\u624b\uff0c\u63a5\u4e0b\u4e00\u638c\u3002 \u3000\u3000\u53f6\u957f\u9752\u5168\u529b\u53d1\u638c\uff0c\u90a3\u5927\u6c49\u88ab\u4e00\u638c\u903c\u5f97\u8eab\u5b50\u4e00\u6643\uff0c\u683d\u5165\u4e86\u6e56\u4e2d\u3002 \u3000\u3000\u753b\u822b\u53ca\u65f6\u9a70\u5230\uff0c\u53f6\u957f\u9752\u7ffb\u8eab\u843d\u5165\u753b\u822b\u3002 \u3000\u3000\u5170\u767d\u4e5f\u540c\u65f6\u98de\u8dc3\u4e0a\u753b\u822b\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u600e\u4e48\u6837?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5feb\u821f\u4e0a\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u88ab\u6211\u6740\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u4e00\u63a0\u9b13\u89d2\uff0c\u9053\uff1a\u201c\u8239\u5462?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u8239?\u4ec0\u4e48\u8239?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u90a3\u53ea\u5c0f\u821f\u5440?\u6ca1\u6709\u4eba\u64cd\u821f\u4e86\uff0c\u6f02\u8d70\u4e86\u5417?\u5e94\u8be5\u5f04\u6c89\u554a?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u6c34\u5e95\u4e0b\u6211\u4eec\u4e5f\u6709\u4eba\uff0c\u5bf9\u65b9\u7684\u65e0\u4eba\u5c0f\u821f\u6211\u4eec\u4f1a\u5b89\u6392\u7684\u3002\u201d \u3000\u3000\u7389\u7fe0\u770b\u4e86\u770b\u4e01\u5cf0\uff0c\u7b11\u4e86\u7b11\uff0c\u9053\uff1a \u201c\u53f6\u5c11\u4fa0\uff0c\u4ed6\u4eec\u90fd\u5f88\u4f1a\u529e\u4e8b\u3002\u201d \u53f6\u957f\u9752\u7b11\u9053\uff1a\u201c\u8fc7\u5956\u4e86!\u201d \u3000\u3000\u753b\u822b\u884c\u9a76\u751a\u901f\uff0c\u7728\u773c\u4e4b\u95f4\uff0c \u5df2\u9a70\u51fa\u5341\u4e08\u6709\u4f59\uff0c\u5170\u767d\u56de\u987e\u9002\u624d\u622a\u821f\u4e4b\u5904\uff0c\u5bf9\u65b9\u56db\u8258\u5c0f\u821f\uff0c\u65e9\u5df2\u6ca1\u4e86\u8e2a\u5f71\uff0c\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u597d\u5feb\u7684\u52a8\u4f5c\uff0c\u5f53\u771f\u5168\u90fd\u51ff\u6c89\u4e86?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u59d1\u5a18\uff0c\uff0c\u5c0f\u821f\u53ea\u6c89\u4e0b\u4e24\u53ea\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u8fd8\u6709\u90a3\u53e6\u5916\u7684\u4e24\u53ea\u5462?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u6211\u4eec\u7684\u4eba\u5df2\u7ecf\u4ee3\u66ff\u4e86\u5bf9\u65b9\u7684\u821f\u5b50\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u4f3c\u662f\u6709\u4e9b\u4e0d\u4fe1\uff0c\u770b\u770b\u53f6\u957f\u9752\uff0c\u9053\uff1a \u201c\u53f6\u5c11\u4fa0\uff0c \u4f60\u770b\u5230\u6c34\u9762\u4e0a\u6709\u8239\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u4e3e\u76ee\u56db\u770b\uff0c\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u5c0f\u821f\u4e0a\u706f\u53f7\u5df2\u706d\uff0c\u8ddd\u79bb\u5df2\u8fdc\uff0c\u6240\u4ee5\u770b\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u54e6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u6e56\u9762\u4e0a\u9664\u4e86\u9634\u9633\u5821\u8239\u53ea\u7684\u706f\u5149\u4e4b\u5916\uff0c\u4f3c\u4e4e\u518d\u4e5f\u77a7\u4e0d\u5230\u522b\u7684\u706f\u706b\u4e86\u3002 \u3000\u3000\u53f6\u957f\u9752\u5ffd\u7136\u89c9\u5f97\u6709\u4e9b\u4e0d\u5bf9\u52b2\uff0c\u4ee5\u521a\u624d\u5bf9\u65b9\u56db\u8258\u5feb\u821f\u7a81\u7136\u51fa\u73b0\u7684\u60c5\u5f62\u800c\u8bba\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u53ea\u6d3e\u51fa\u56db\u4eba\u56db\u821f\u5c31\u6b64\u7f62\u624b\uff0c\u5f88\u663e\u7136\uff0c\u5bf9\u65b9\u5fc5\u7136\u53e6\u6709\u8be1\u8ba1\u3002 \u3000\u3000\u4e01\u5cf0\u795e\u60c5\u8083\u7a46\u5730\u7ad9\u5728\u53f6\u957f\u9752\u8eab\u524d\uff0c\u5168\u795e\u8d2f\u6ce8\u5730\u51dd\u89c6\u7740\u6e56\u9762\u4e0a\u7684\u4e00\u5207\u52a8\u9759\uff0c\u5e76\u4e14\u4e0d\u65f6\u5411\u674e\u70c8\u3001\u8521\u5149\u4e8c\u4eba\u6253\u7740\u624b\u52bf\uff0c\u7389\u7fe0\u548c\u5170\u767d\u4eff\u4f5b\u6709\u4e9b\u8010\u4e0d\u4f4f\u8fd9\u4e00\u9635\u5b50\u6c89\u95f7\u7684\u538b\u529b\uff0c\u53cc\u53cc\u8d70\u5230\u8239\u5c3e\uff0c\u4e0d\u65f6\u5411\u6765\u8def\u7684\u6c34\u9762\u5f20\u671b\u3002 \u3000\u3000\u6c89\u5bc2\u7684\u65f6\u5149\u5e76\u4e0d\u592a\u4e45\uff0c\u753b\u822b\u8d70\u4e86\u4e0d\u5230\u4e94\u91cc\uff0c\u6e56\u9762\u4e0a\u5ffd\u7136\u4e4b\u95f4\uff0c\u4eae\u8d77\u4e86\u4e00\u5708\u706f\u706b\u3002 \u3000\u3000\u8fd9\u4e00\u5708\u706f\u5149\uff0c\u8fdc\u8fdc\u770b\u53bb\uff0c\u4eff\u4f5b\u662f\u4e00\u6761\u53d1\u5149\u7684\u7ef3\u7d22\uff0c\u73af\u7ed5\u5728\u753b\u822b\u7684\u5de6\u4fa7\u524d\u540e\uff0c\u53cc\u65b9\u76f8\u8ddd\uff0c\u4e0d\u8db3\u767e\u4e08\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u4e00\u9707\uff0c\u6697\u9053\uff1a\u201c\u679c\u7136\u6765\u4e86!\u201d \u3000\u3000\u7389\u5170\u53cc\u59dd\u4f3c\u662f\u5bf9\u8fd9\u73af\u7ed5\u5728\u753b\u822b\u5916\u56f4\u7684\u706f\u706b\uff0c\u6781\u611f\u5174\u8da3\uff0c\u77aa\u5927\u4e86\u773c\uff0c\u5446\u5446\u5730\u77a7\u7740\u3002 \u3000\u3000\u4e01\u5cf0\u5374\u795e\u8272\u7d27\u5f20\u5730\u5411\u53f6\u957f\u9752\u4f4e\u58f0\u9053\uff1a \u201c\u5251\u4f7f\uff0c\u4ed6\u4eec\u56f4\u8fc7\u6765\u4e86\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u77e5\u9053!\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u4ed6\u4eec\u4f3c\u4e4e\u8981\u903c\u6211\u4eec\u6539\u53d8\u884c\u8fdb\u65b9\u5411\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u55ef!\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u53f3\u4fa7\u8237\u5916\uff0c\u662f\u9a6c\u79ef\u5c71\uff0c\u90a3\u662f\u6e56\u4e2d\u7684\u4e00\u5ea7\u5927\u5c9b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5c0f\u79ef\u5c71\u79bb\u9a6c\u79ef\u5c71\u591a\u8fdc?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u4e0d\u8db3\u5341\u91cc\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u6211\u4eec\u6b64\u523b\u79bb\u9a6c\u79ef\u5c71\u591a\u8fdc?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u7ea6\u83ab\u5341\u4e8c\u91cc\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u770b\u4e86\u770b\u90a3\u5708\u706f\u5149\uff0c\u7b11\u4e86\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4eec\u79bb\u5c0f\u79ef\u5c71\u662f\u4e0d\u662f\u8fd8\u6709\u56db\u91cc?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u56db\u91cc\u4e0d\u5230\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7565\u4e00\u6c89\u541f\u9053\uff1b\u201c\u5982\u679c\u7ef4\u6301\u76ee\u524d\u884c\u9a76\u7684\u901f\u5ea6\uff0c\u534a\u4e2a\u65f6\u8fb0\u8d76\u5f97\u5230\u5417?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u8d76\u5f97\u5230\u2026\u2026\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u5bf9\u65b9\u7684\u8239\u53ea\u5982\u679c\u62e6\u963b\uff0c\u90a3\u5c31\u4e0d\u6613\u8d76\u5230\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a \u201c\u83b2\u82b1\u53f7\u5728\u54ea\u513f?\u80fd\u4e0d\u80fd\u8ddf\u9a6c\u826f\u8054\u7edc?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u83b2\u82b1\u53f7\u6b64\u523b\u6309\u9884\u5b9a\u7684\u884c\u7a0b\uff0c\u5e94\u8be5\u5728\u6211\u4eec\u7684\u53f3\u524d\u65b9\u4e24\u91cc\u5de6\u53f3\uff0c\u5251\u4f7f\u82e5\u8981\u8ddf\u9a6c\u826f\u8054\u7edc\uff0c\u5c5e\u4e0b\u53ef\u6d3e\u4eba\u524d\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f88\u597d\uff0c\u4f60\u53eb\u4ed6\u4eec\u5212\u4e00\u53ea\u5c0f\u8247\u8fc7\u6765\u3002\u201d \u3000\u3000\u4e01\u5cf0\u5e94\u4e86\u4e00\u58f0\uff0c\u8d70\u5411\u8239\u5c3e\uff0c\u6253\u51fa\u4e86\u4e00\u4e2a\u706f\u53f7\u3002 \u3000\u3000\u7728\u773c\u4e4b\u95f4\uff0c\u4e00\u53ea\u68ad\u5f62\u5feb\u8247\uff0c\u5982\u98de\u9a76\u81f3\u3002 \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u5c0f\u821f\u5df2\u5230\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u4e01\u5cf0\uff0c\u8981\u4ed6\u53bb\u544a\u8bc9\u9a6c\u826f\uff0c\u7acb\u5373\u628a\u83b2\u82b1\u53f7\u6389\u5934\uff0c\u8ddf\u6211\u4eec\u4f1a\u5408\u3002\u201d \u4e01\u5cf0\u7565\u4e00\u8fdf\u7591\uff0c\u4f46\u5374\u6ca1\u6709\u591a\u95ee\u4ec0\u4e48\uff0c\u4f4e\u58f0\u5411\u5c0f\u8247\u4e0a\u64cd\u821f\u7684\u58ee\u6c49\u4ea4\u4ee3\u4e86\u51e0\u53e5\uff0c\u90a3\u8258\u5c0f\u821f\uff0c\u8fd0\u6d46\u5982\u98de\uff0c\u8fc5\u901f\u5730\u6ca1\u5165\u6c34\u96fe\u4e4b\u4e2d\u3002 \u3000\u3000\u7389\u5170\u53cc\u59dd\uff0c\u65e9\u5df2\u5230\u4e86\u53f6\u957f\u9752\u8eab\u540e\uff0c\u5979\u4eec\u4e00\u76f4\u60f3\u9759\u9759\u5730\u542c\u7740\u53f6\u957f\u9752\u5429\u5490\u4e01\u5cf0\u529e\u4e8b\uff0c\u76f4\u5230\u8fd9\u65f6\uff0c\u7389\u7fe0\u624d\u7b11\u9053\uff1a \u201c\u53f6\u5c11\u4fa0\uff0c\u597d\u50cf\u4e0d\u60f3\u96c6\u4e2d\u5168\u529b\uff0c\u4e0e\u5bf9\u65b9\u4e00\u640f?\u201d \u3000\u3000\u53f6\u957f\u9752\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u7389\u7fe0\uff0c\u4f60\u8ba4\u4e3a\u5bf9\u65b9\u8fd8\u4f1a\u4f7f\u521a\u624d\u90a3\u56db\u6761\u5c0f\u821f\uff0c\u4e0d\u582a\u4e00\u51fb\u5417?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u4f1a\uff01\u201d \u3000\u3000\u5170\u767d\u63a5\u9053\uff1a\u201c\u53ea\u6015\u773c\u524d\u8fd9\u4e00\u5708\u5c0f\u8247\uff0c\u4e0d\u4e0b\u767e\u53ea\u4e4b\u591a\uff0c\u5c31\u7b97\u6211\u4eec\u5927\u5927\u5c0f\u5c0f\u7684\u4e5d\u6761\u8239\u662f\u94c1\u6253\u7684\uff0c\u8981\u786c\u62fc\u6050\u6015\u4e5f\u4e0d\u6613\u83b7\u80dc\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5170\u767d\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u4f60\u4eec\u50cf\u660e\u660e\u77e5\u9053\u5be1\u4e0d\u654c\u4f17\u561b!\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u672c\u6765\u5c31\u662f\uff01\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\u8c03\u56de\u83b2\u82b1\u53f7\uff0c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u201c\u6df7\u4e71\u5bf9\u65b9\u7684\u89c6\u542c\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5bf9\u65b9\u90a3\u767e\u6765\u53ea\u5c0f\u8247\uff0c\u5df2\u9010\u6e10\u903c\u8fd1\uff0c\u53cc\u65b9\u8ddd\u79bb\u5df2\u7f29\u77ed\u4e86\u53ea\u6709\u51e0\u5341\u4e08\u3002 \u3000\u3000\u4e01\u5cf0\u5598\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u539f\u6765\u4ed6\u4eec\u7684\u8239\u901f\u6bd4\u6211\u4eec\u5feb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8239\u8f7b\u4eba\u5c11\uff0c\u5f53\u7136\u6bd4\u6211\u4eec\u5feb!\u4e0d\u8fc7\uff0c\u7167\u76ee\u524d\u7684\u901f\u5ea6\uff0c\u4ed6\u8981\u8ffd\u5230\u6211\u4eec\u5341\u4e08\u4e4b\u5185\uff0c\u4f60\u80fd\u4e0d\u80fd\u7b97\u51fa\u8fd8\u8981\u591a\u5c11\u65f6\u5149?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u6700\u5feb\u4e5f\u5f97\u534a\u4e2a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e86\u3002 \u3000\u3000\u4e01\u5cf0\u6ca1\u6709\u7b11\uff0c\u4f4e\u58f0\u63a5\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u636e\u5c5e\u4e0b\u6240\u77e5\uff0c\u5bf9\u65b9\u76ee\u524d\u7684\u5212\u821f\u901f\u5ea6\uff0c\u5e76\u672a\u5c3d\u5168\u529b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4e00\u6014\u9053\uff1a\u201c\u672a\u5c3d\u5168\u529b?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u8fd9\u79cd\u68ad\u5f62\u5feb\u8247\uff0c\u5982\u662f\u4e24\u4eba\u5212\u6868\uff0c\u901f\u5ea6\u53ef\u6bd4\u773c\u4e0b\u589e\u52a0\u4e00\u500d\u3002\u201d \u3000\u3000\u5170\u767d\u7684\u7b11\u5bb9\u50f5\u5728\u8138\u4e0a\uff0c\u51b7\u51b7\u5730\u9053\uff1a \u201c\u53f6\u5c11\u4fa0\uff0c\u65e2\u7136\u9003\u4e0d\u4e86\uff0c\u4f55\u4e0d\u4e00\u62fc?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u7684\u5c5e\u4e0b\u4e4b\u4e2d\uff0c\u6709\u591a\u5c11\u4eba\u80fd\u5728\u6c34\u4e2d\u4f5c\u6218?\u201d \u3000\u3000\u53f6\u957f\u9752\u770b\u770b\u4e01\u5cf0\u3002 \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u4e09\u5341\u4e8c\u4eba\uff0c\u4eba\u4eba\u7686\u53ef\u51fa\u5165\u6ce2\u6d9b\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1b\u201c\u90a3\u5f88\u597d\uff0c\u4f60\u4eec\u4e4b\u4e2d\uff0c\u53ea\u6709\u53f6\u5144\u4e00\u4e2a\u662f\u65e9\u9e2d\u5b50\uff0c\u6211\u60f3\uff0c\u54b1\u4eec\u8131\u56f0\u7684\u673a\u4f1a\u5f88\u5927\u3002\u201d \u3000\u3000\u4e01\u5cf0\u5ffd\u7136\u63a5\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u83b2\u82b1\u53f7\u6765\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1b\u201c\u4f20\u4ee4\u4e0b\u53bb\uff0c\u83b2\u82b1\u53f7\u4e0a\u7684\u4eba\uff0c\u90fd\u8fc7\u6765\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a \u201c\u5c5e\u4e0b\u8fd9\u5c31\u53bb\u901a\u77e5\u4ed6\u4eec\u2026\u2026\u201d\u4e00\u4fa7\u8eab\uff0c\u8d70\u5411\u53f3\u8237\uff0c \u7eb5\u8eab\u4e00\u8df3\uff0c\u8dc3\u4e0a\u4e24\u4e08\u5916\u7684\u4e00\u53ea\u5c0f\u821f\u3002 \u3000\u3000\u7389\u7fe0\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u8981\u727a\u7272\u83b2\u82b1\u53f7?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1b\u201c\u4e0d\u4e00\u5b9a\uff0c\u662f\u5426\u727a\u7272\uff0c\u8fd8\u5f97\u770b\u5bf9\u65b9\u7684\u624b\u6bb5\u3002\u201d \u3000\u3000\u5170\u767d\u770b\u4e86\u770b\u4e8c\u4eba\u4e00\u773c\u9053\uff1a \u201c\u4f60\u4eec\u6253\u4ec0\u4e48\u54d1\u8c1c?\u8bf4\u660e\u767d\u4e0d\u884c\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5170\u767d\u59d1\u5a18\uff0c\u6211\u662f\u5728\u60f3\uff0c\u90a3\u4e9b\u654c\u65b9\u7684\u5c0f\u8239\uff0c\u4e00\u65e6\u6321\u5728\u524d\u9762\uff0c\u6211\u4eec\u5c31\u5f88\u96be\u51b2\u51fa\u91cd\u56f4\uff0c\u6240\u4ee5\uff0c\u80fd\u591f\u5206\u6563\u4e00\u4e0b\u4ed6\u4eec\u7684\u76ee\u6807\uff0c\u6211\u4eec\u5c31\u6709\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a \u201c\u600e\u4e48\u5206\u6563\u4ed6\u4eec?\u53f6\u5c11\u4fa0\uff0c\u4ed6\u4eec\u5982\u679c\u53ea\u8ddf\u5b9a\u6211\u4eec\u5462?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u4ed6\u4eec\u4e0d\u662f\u778e\u5b50!\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4eec\u4e0d\u662f\u778e\u5b50\uff0c\u53ef\u662f\uff0c\u6211\u4eec\u53ef\u4ee5\u8981\u4ed6\u4eec\u53d8\u6210\u778e\u5b50!\u201d \u3000\u3000\u5170\u767d\u6709\u4e9b\u4e0d\u89e3\uff0c\u7389\u7fe0\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u5170\u59b9\uff0c\u591c\u8272\u5df2\u6df1\uff0c\u8981\u4ed6\u4eec\u53d8\u6210\u778e\u5b50\uff0c\u5e76\u4e0d\u56f0\u96be\u3002\u201d \u3000\u3000\u5170\u767d\u54e6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u662f\u4e0d\u662f\u7184\u53bb\u706f\u5149?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5170\u767d\u59d1\u5a18\uff0c\u8fd9\u4f3c\u4e4e\u662f\u76ee\u4e0b\u552f\u4e00\u53ef\u884c\u4e4b\u6cd5\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u4f1a\u4e5f\u7184\u53bb\u706f\u706b\u5417?\u5230\u65f6\u5019\uff0c\u5927\u5bb6\u4e0d\u662f\u90fd\u53d8\u6210\u778e\u5b50\u4e86\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u9053\uff1a\u201c\u8fd9\u66f4\u597d\u4e86\uff0c\u6211\u4eec\u5b81\u53ef\u778e\u649e\uff0c\u4e5f\u4e0d\u80fd\u5728\u4eae\u5149\u4e0b\u88ab\u56f4\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u4e01\u5cf0\u3001\u9a6c\u826f\u5df2\u548c\u83b2\u82b1\u53f7\u4e0a\u7684\u56db\u540d\u58ee\u6c49\uff0c\u4e00\u540c\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u9a6c\u826f\u606d\u656c\u5730\u4e00\u62b1\u62f3\uff0c\u9053\uff1a \u201c\u5251\u4f7f\u53ec\u5524\u5c5e\u4e0b\uff0c\u5c5e\u4e0b\u5df2\u5c06\u753b\u822b\u4e0a\u7684\u5144\u5f1f\u5e26\u6765\u4e86\u3002\u201d \u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u9a6c\u826f\uff0c\u6211\u987b\u8981\u4e24\u540d\u6b7b\u58eb\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u4e5f\u8bb8\u4e0d\u4f1a\u5fc5\u6b7b\u3002\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u5251\u4f7f\u53ea\u7ba1\u6307\u5b9a\u4ed6\u4eec\u4e2d\u4efb\u4f55\u4e24\u540d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u9053\uff1a\u201c\u9a6c\u826f\uff0c\u6211\u5bf9\u4ed6\u4eec\u4e86\u89e3\u5f97\u4e0d\u591a\uff0c\u6240\u4ee5\uff0c\u8fd9\u79cd\u6bd4\u8f83\u5371\u9669\u7684\u4efb\u52a1\uff0c\u5fc5\u5f97\u8981\u9009\u51fa\u4e24\u4e2a\u673a\u8b66\u4e00\u70b9\u7684\u4eba\uff0c\u5e0c\u671b\u4f60\u80fd\u4ee3\u6211\u9009\u5b9a\u3002\u201d \u3000\u3000\u9a6c\u826f\u7b11\u4e86\u7b11\uff0c\u8f6c\u8eab\u770b\u4e86\u770b\u56db\u4e2a\u4eba\uff0c\u9053\uff1a \u201c\u5251\u4f7f\u7684\u8bdd\uff0c \u4f60\u4eec\u542c\u5230\u4e86?\u201d \u3000\u3000\u56db\u4eba\u540c\u65f6\u5e94\u58f0\u9053\uff1a\u201c\u542c\u5230\u4e86\u3002\u201d \u3000\u3000\u9a6c\u826f\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u54ea\u4e24\u4e2a\u613f\u610f\u5192\u6b7b\u62c5\u6b64\u91cd\u4efb?\u201d \u3000\u3000\u56db\u4eba\u7adf\u7136\u540c\u65f6\u5e94\u58f0\u9053\uff1a\u201c\u5c5e\u4e0b\u613f\u5f80\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u9a6c\u5144\uff0c\u4ed6\u4eec\u4e2a\u4e2a\u89c6\u6b7b\u5982\u5f52\uff0c\u8981\u4ed6\u4eec\u5192\u9669\u72af\u96be\uff0c\u81ea\u7136\u662f\u4e2a\u4e2a\u4e89\u5148\uff0c\u4e0d\u7518\u4eba\u540e\u4e86! \u3000\u3000\u6211\u770b\uff0c\u8fd8\u662f\u4f60\u6307\u5b9a\u4e24\u540d\u5427\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9887\u4e3a\u611f\u52a8\uff0c\u70b9\u5934\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u9a6c\u826f\uff0c\u4f60\u6307\u5b9a\u4e24\u4e2a\u4eba\u6700\u597d\u3002\u201d \u9a6c\u826f\u9053\uff1a \u201c\u662f\u3002\u201d\u4ed6\u8f6c\u8eab\u5411\u5176\u4e2d\u4e24\u540d\u3002\u4e00\u6307\u9053\uff1a \u201c\u5f20\u9675\u3001\u9ec4\u5b89\uff0c\u4f60\u4eec\u4e24\u4e2a\u51fa\u6765\u3002\u201d \u3000\u3000\u5f20\u3001\u9ec4\u4e8c\u4eba\u5e94\u58f0\u800c\u51fa\u3002 \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u8bf7\u6307\u793a\u4efb\u52a1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u4e86\u70b9\u5934\u9053\uff1a \u201c\u5f20\u9675\u3001\u9ec4\u5b89\uff0c\u4f60\u4eec\u9a7e\u9a76\u83b2\u82b1\u53f7\u753b\u822b\u7184\u53bb\u706f\u706b\uff0c\u5411\u76f8\u53cd\u65b9\u5411\u884c\u53bb\uff0c\u53ea\u8981\u654c\u65b9\u5c0f\u821f\u5206\u6563\u8ffd\u8d76\u4e4b\u540e\uff0c\u4f60\u4eec\u7acb\u5373\u5f03\u8239\uff0c\u6cc5\u6c34\u8df3\u4e0a\u63a5\u5e94\u4f60\u4eec\u7684\u5c0f\u821f\uff0c\u5230\u5c0f\u79ef\u5c71\u4f1a\u5408\u3002\u201d \u3000\u3000\u5f20\u3001\u9ec4\u4e24\u4eba\u62b1\u62f3\u5e94\u4e86\u58f0\uff1a \u201c\u662f!\u201d\u53cc\u53cc\u8f6c\u8eab\uff0c\u8dc3\u5411\u83b2\u82b1\u53f7\u753b\u822b\u3002 \u3000\u3000\u53f6\u957f\u9752\u770b\u770b\u4e01\u5cf0\u9053\uff1a\u201c\u8c03\u6d3e\u4e24\u6761\u5c0f\u821f\u63a5\u5e94\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5c5e\u4e0b\u8fd9\u5c31\u53bb\u529e\u3002\u201d\u5927\u6b65\u8d70\u5411\u8239\u5c3e\u3002 \u3000\u3000\u53f6\u957f\u9752\u5411\u9a6c\u826f\u4e00\u6325\u624b\uff0c\u63a5\u9053\uff1a \u201c\u9a6c\u826f\uff0c\u4f60\u548c\u4ed6\u4eec\u4e24\u4eba\uff0c\u5c31\u7559\u5728\u8fd9\u8258\u753b\u822b\u4e0a\uff0c\u5e2e\u52a9\u6211\u4eec\u50ac\u821f\u75be\u9a70\u3002\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u662f!\u201d\u7565\u4e00\u8fdf\u7591\uff0c\u53c8\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u672c\u8239\u662f\u5426\u4e5f\u8981\u7184\u53bb\u706f\u706b?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u6682\u65f6\u4e0d\u5fc5\uff0c\u7b49\u83b2\u82b1\u53f7\u5f15\u5f00\u5bf9\u65b9\u4e00\u534a\u8239\u53ea\u4e4b\u540e\uff0c\u518d\u884c\u7184\u706f\u75be\u9a70\u3002\u201d \u9a6c\u826f\u5fae\u9732\u7b11\u5bb9\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u5c5e\u4e0b\u9075\u547d\u3002\u201d\u7387\u9886\u90a3\u4e24\u540d\u5c5e\u4e0b\uff0c\u4e5f\u5411\u8239\u5c3e\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7389\u7fe0\u4e00\u76f4\u5728\u65c1\u8fb9\u51b7\u773c\u65c1\u89c2\uff0c\u8fd9\u65f6\u65b9\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u5144\u6307\u6325\u82e5\u5b9a\uff0c\u4e0d\u5931\u5927\u5c06\u98ce\u5ea6\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6df1\u6df1\u5730\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u5f62\u52bf\u903c\u4eba\uff0c\u5728\u4e0b\u4e5f\u662f\u52c9\u4e3a\u5176\u96be\uff0c\u5012\u53eb\u59d1\u5a18\u89c1\u7b11\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u6de1\u6de1\u5730\u7b11\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u6211\u59ca\u59ca\u6ca1\u8bf4\u9519\uff0c\u4f60\u8fd9\u4efd\u9547\u5b9a\u529f\u592b\uff0c\u5c31\u975e\u5e38\u4eba\u6240\u53ca\u3002\u654c\u6211\u5b9e\u529b\uff0c\u4e0d\u557b\u5341\u4e0e\u4e00\u4e4b\u6bd4\uff0c\u4f60\u80fd\u4ece\u5bb9\u4e0d\u8ffd\u7684\u5206\u914d\u4eba\u624b\uff0c\u5236\u654c\u673a\u5148\uff0c\u672c\u6765\u5c31\u79f0\u5f97\u4e0a\u5927\u5c06\u98ce\u8303\u561b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u611f\u6fc0\u7684\u4e00\u7b11\uff0c\u9053\uff1b\u201c\u8c22\u8c22\u7389\u5170\u53cc\u59dd\u7684\u5938\u8d5e\uff0c\u770b\u6765\uff0c\u54b1\u4eec\u8131\u56f4\u5230\u5c0f\u79ef\u5c71\u4e4b\u884c\uff0c\u8be5\u6709\u4e00\u534a\u6210\u529f\u7684\u673a\u4f1a\u4e86\u2026\u2026\u201d\u8bed\u97f3\u672a\u843d\uff0c\u5ffd\u7136\u53d8\u8272\u8bf4\u9053\uff1a \u201c\u4e01\u5cf0\u6ca1\u6709\u8bf4\u9519\uff0c\u5bf9\u65b9\u7684\u8239\u901f\u679c\u7136\u52a0\u5feb\u4e86\u4e00\u500d\u4e86\u3002\u201d \u3000\u3000\u5c31\u8fd9\u76cf\u8336\u65f6\u5149\uff0c\u539f\u672c\u662f\u516b\u5341\u4e08\u7684\u8ddd\u79bb\uff0c\u5df2\u7f29\u77ed\u5230\u56db\u5341\u4e08\u4e0d\u5230\uff0c\u7247\u7247\u5c0f\u821f\uff0c\u70b9\u70b9\u706f\u706b\uff0c\u5df3\u5728\u5de6\u8237\u5916\u5f62\u6210\u4e86\u4e00\u5835\u706f\u5899\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u83b2\u82b1\u53f7\u8fd9\u65f6\u5df2\u6389\u5934\u800c\u884c\uff0c\u5411\u6765\u8def\u75be\u9a70\u3002 \u3000\u3000\u5bf9\u65b9\u7684\u8239\u961f\uff0c\u4f3c\u662f\u6ca1\u6709\u6599\u5230\u83b2\u82b1\u53f7\u4f1a\u5ffd\u7136\u6389\u5934\uff0c\u5e76\u4e14\u7184\u53bb\u706f\u706b\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u8239\u961f\u534a\u5706\u5f62\u7684\u706f\u5899\u6709\u4e86\u7247\u523b\u7684\u51cc\u4e71\u3002 \u3000\u3000\u7389\u7fe0\u77a7\u7740\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u7684\u8ba1\u5212\u6210\u529f\u4e86\u4e00\u534a\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5bf9\u81ea\u5df1\u7684\u8fd9\u6761\u8bf1\u654c\u4e4b\u8ba1\uff0c\u5e76\u65e0\u7edd\u5bf9\u628a\u63e1\uff0c\u4e00\u89c1\u5bf9\u65b9\u8239\u961f\u7a81\u7136\u51cc\u4e71\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5f97\u6697\u6697\u4e00\u559c\uff0c\u4f46\u4ed6\u53e3\u4e2d\u5374\u63a5\u9053\uff1a \u201c\u7389\u7fe0\uff0c\u654c\u8239\u672a\u89c1\u884c\u52a8\uff0c\u53ea\u6015\u2026\u2026\u201d \u8bed\u97f3\u672a\u5df2\uff0c\u5170\u767d\u5df2\u54af\u54af\u7b11\u9053\uff1a \u201c\u77a7\uff0c\u4ed6\u4eec\u771f\u7684\u62e8\u4e86\u4e00\u534a\u8239\u53ea\u53bb\u8ffd\u83b2\u82b1\u53f7\u4e86\u3002\u201d \u53f6\u957f\u9752\u7b11\u4e86\u7b11\uff0c\u5411\u5df2\u7ecf\u56de\u6765\u5b88\u5728\u81ea\u5df1\u8eab\u540e\u7684\u4e01\u5cf0\u4e00\u6325\u624b\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u8981\u6211\u4eec\u7684\u8239\u53ea\u5168\u90e8\u7184\u53bb\u706f\u706b\uff0c\u731b\u529b\u6253\u6868\uff0c\u5411\u5c0f\u79ef\u5c71\u96c6\u4e2d\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u662f!\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u4ece\u8863\u895f\u4e0b\u638f\u51fa\u4e00\u6839\u5c0f\u5c0f\u7af9\u54e8\uff0c\u66f2\u6307\u4e00\u5f39\uff0c\u7af9\u54e8\u51cc\u7a7a\u98de\u53bb\uff0c\u5e26\u8d77\u4e00\u9635\u5c16\u5578\u4e4b\u58f0\u3002 \u3000\u3000\u53f6\u957f\u9752\u51dd\u76ee\u671b\u53bb\uff0c\u7af9\u54e8\u7834\u7a7a\u4e4b\u58f0\u752b\u8d77\uff0c\u81ea\u5df1\u8eab\u8fb9\u7684\u706f\u5149\u5ffd\u7136\u7184\u53bb\uff0c\u800c\u6563\u5e03\u5728\u6e56\u9762\u4e0a\u7684\u51e0\u53ea\u9634\u9633\u5821\u7684\u5c0f\u821f\u4e0a\u7684\u706f\u706b\uff0c\u4e5f\u540c\u65f6\u4e0d\u89c1\u3002 \u3000\u3000\u7389\u7fe0\u5598\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4ed6\u4eec\u53ea\u6015\u53c8\u8981\u624b\u5fd9\u811a\u4e71\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u4e00\u5b9a\uff0c\u636e\u521a\u624d\u4f55\u5bd2\u8863\u6240\u8a00\uff0c\u5bf9\u65b9\u4e43\u662f\u8c0b\u5b9a\u800c\u6765\uff0c\u5fc5\u662f\u8ba1\u5212\u5341\u5206\u5468\u5bc6\uff0c\u54b1\u4eec\u8981\u60f3\u8d81\u9ed1\u8131\u51fa\u91cd\u56f4\uff0c\u53ea\u6015\u4e0d\u6613\u3002\u201d\u4e01\u5cf0\u7b11\u9053\uff1a\u201c\u5251\u4f7f\u4e0d\u5fc5\u803d\u5fc3\u4e86\uff0c\u5c31\u5c5e\u4e0b\u6240\u77e5\uff0c\u6e56\u4e0a\u6d77\u591c\u4e8c\u66f4\u8fc7\u540e\uff0c\u5e38\u5e38\u8d77\u96fe\uff0c\u518d\u6709\u7247\u523b\uff0c\u6e56\u9762\u96fe\u6d53\uff0c\u4ed6\u4eec\u7eb5\u7136\u70b9\u8d77\u706b\u628a\uff0c\u5374\u4e5f\u5948\u4f55\u4e0d\u4e86\u6211\u4eec\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\u9a6c\u826f\u75be\u884c\u800c\u6765\uff0c\u62b1\u62f3\u9053\uff1a \u201c\u5251\u4f7f\uff0c\u5f20\u9675\u3001\u9ec4\u5b89\u5df2\u4f9d\u4ee4\u5f03\u8239\uff0c\u5b89\u5168\u4e0a\u4e86\u5c0f\u821f\u3002\u201d \u53f6\u957f\u9752\u7684\u8138\u4e0a\u9732\u51fa\u7b11\u5bb9\u9053\uff1a\u201c\u5f88\u597d!\u201d \u3000\u3000\u4e01\u5cf0\u76ee\u5149\u4e00\u8f6c\uff0c\u5411\u9a6c\u826f\u9053\uff1a\u201c\u9a6c\u5144\uff0c\u5c0f\u79ef\u5c71\u7684\u5730\u5f62\u6613\u5b88\u96be\u653b\uff0c\u9a6c\u5144\u53ef\u5426\u5148\u9a7e\u5c0f\u821f\u62a2\u5148\u767b\u5165\uff0c\u4ee5\u514d\u5bf9\u65b9\u53d1\u73b0\u6211\u4eec\u7684\u4f01\u56fe\uff0c\u963b\u65ad\u6211\u4eec\u7684\u53bb\u8def?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1b\u201c\u4e01\u5144\u597d\u597d\u4fdd\u62a4\u5251\u4f7f\u2026\u2026\u201d\u8f6c\u8eab\u6b65\u5411\u8239\u5c3e\u3002\u8df3\u5165\u4e00\u53ea\u5c0f\u821f\uff0c\u6253\u6868\u800c\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u76b1\u7709\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u9a6c\u826f\u53bb\u8fc7\u5c0f\u79ef\u5c71\u5417?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u53bb\u8fc7\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c0f\u79ef\u5c71\u6709\u6ca1\u6709\u5c45\u6c11?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u6ca1\u6709\u3002\u90a3\u53ea\u662f\u6e56\u4e2d\u7684\u4e00\u5904\u5c0f\u5c71\uff0c\u9762\u79ef\u4e0d\u5927\uff0c\u4e09\u9762\u90fd\u662f\u5ced\u58c1\uff0c\u53ea\u6709\u5411\u5357\u7684\u4e00\u9762\u662f\u767e\u4e08\u5e73\u6ee9\u6240\u4ee5\u6613\u5b88\u96be\u653b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u4e5f\u53bb\u8fc7\u5417?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1b\u201c\u5c5e\u4e0b\u53ea\u53bb\u8fc7\u4e00\u6b21\u3002\u201d\u56de\u5934\u4e00\u671b\uff0c\u63a5\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u5bf9\u65b9\u7684\u8239\u961f\u5df2\u7ecf\u5206\u6563\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4ed6\u4eec\u627e\u4e0d\u5230\u6211\u4eec\uff0c\u81ea\u7136\u53ea\u6709\u5316\u6574\u4e3a\u96f6\u7684\u622a\u51fb\u5566\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5230\u5c0f\u79ef\u5c71\u8fd8\u6709\u591a\u8fdc?\u201d \u3000\u3000\u4e01\u5cf0\u56e0\u773c\u89c1\u53f6\u957f\u9752\u6307\u6325\u82e5\u5b9a\uff0c\u5bf9\u4e8e\u8f6c\u8fdb\u5230\u5c0f\u79ef\u5c71\u884c\u52a8\u5df2\u5927\u6709\u4fe1\u5fc3\uff0c\u6545\u800c\u8138\u4e0a\u5df2\u4e0d\u4f3c\u5148\u524d\u4e25\u8083\uff0c\u7b11\u4e86\u7b11\u9053\uff1a \u201c\u56de\u5251\u4f7f\uff0c\u5c0f\u79ef\u5c71\u7684\u5c71\u5f71\u5df2\u5728\u524d\u9762\uff0c\u5728\u5bf9\u65b9\u5c0f\u8239\u8ffd\u4e0a\u6211\u4eec\u4e4b\u524d\uff0c\u4e00\u5b9a\u53ef\u4ee5\u8d76\u5230\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u9053\uff1b\u201c\u53f6\u5144\uff0c\u4f60\u770b\uff0c\u90a3\u4e0d\u5c31\u662f\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u4e3e\u76ee\u671b\u53bb\uff0c\u679c\u7136\u4e0d\u8fdc\u5904\u5df2\u9690\u9690\u7684\u9732\u51fa\u4e00\u5ea7\u5c0f\u5c71\u7684\u9ed1\u5f71\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u6697\u559c\u3002 \u3000\u3000\u5170\u767d\u5374\u5ffd\u7136\u5931\u58f0\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48?\u201d \u3000\u3000\u4f17\u4eba\u4e3e\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u6e56\u9762\u4e4b\u4e0a\u5ffd\u7136\u71c3\u8d77\u4e00\u7247\u5927\u706b\uff0c\u7167\u5f97\u6e56\u9762\u5341\u5206\u660e\u4eae\u3002 \u3000\u3000\u7389\u7fe0\u76b1\u7709\u9053\uff1a\u201c\u662f\u4e00\u6761\u8239\u5931\u706b\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u76ee\u5149\u95ea\u52a8\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u90a3\u662f\u4e0d\u662f\u6211\u4eec\u7684\u8239?\u597d\u50cf\u662f\u83b2\u82b1\u53f7\u5427?\u201d \u3000\u3000\u539f\u6765\u90a3\u706b\u5149\u5347\u8d77\u7684\u65b9\u5411\uff0c\u6b63\u662f\u83b2\u82b1\u53f7\u884c\u9a76\u7684\u65b9\u4f4d\u3002 \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u56de\u5251\u4f7f\uff0c\u90a3\u6761\u8239\u5927\u6982\u662f\u83b2\u82b1\u53f7\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u4eec\u65e2\u7136\u80fd\u706b\u711a\u83b2\u82b1\u53f7\uff0c\u5fc5\u7136\u4e5f\u4e0d\u4f1a\u653e\u8fc7\u6211\u4eec\u8fd9\u6761\u8239\u2026\u2026\u201d \u3000\u3000\u4e01\u5cf0\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a \u201c\u5251\u4f7f\uff0c\u5c5e\u5382\u53bb\u5e2e\u4ed6\u4eec\u6447\u6a79\u3002\u201d\u4e00\u8f6c\u8eab\uff0c\u5954\u5411\u8239\u5c3e\u3002 \u53f6\u957f\u9752\u770b\u4e86\u770b\u56db\u5468\uff0c\u5411\u7389\u5170\u53cc\u59dd\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u5230\u8231\u91cc\u53bb\u3002\u201d \u3000\u3000\u7389\u7fe0\u4e00\u7b11\u9053\uff1a \u201c\u4e0d\u5fc5\u4e86\uff0c\u53f6\u5144\uff0c\u5982\u679c\u4ed6\u4eec\u8d76\u6765\u653e\u706b\uff0c\u5728\u8231\u91cc\u9762\u53ef\u9003\u4e0d\u4e86\u88ab\u70e7\u6b7b\u554a\u3002\u201d \u5170\u767d\u63a5\u9053\uff1a \u201c\u53f6\u5c11\u4fa0\uff0c\u6211\u4eec\u5728\u8231\u9762\u4e0a\u5bb9\u6613\u8df3\u5165\u6c34\u4e2d\uff0c \u4f60\u4e0d\u4f1a\u6c34\uff0c\u6211\u8ddf\u59ca\u59ca\u5728\u4f60\u8eab\u8fb9\uff0c\u81f3\u5c11\u53ef\u4ee5\u5728\u6c34\u91cc\u9762\u6258\u7740\u4f60\u5440\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u76b1\u7709\u9053\uff1a\u201c\u4f60\u4eec\u672c\u6765\u4e0d\u5fc5\u4e3a\u6211\u6d89\u9669\u7684\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u5e7d\u5e7d\u4e00\u7b11\u9053\uff1a \u201c\u53cd\u6b63\u6211\u4eec\u4e5f\u6d3b\u4e0d\u4e45\u4e86\uff0c\u65e9\u6b7b\uff0c\u8fdf\u6b7b\uff0c\u4e5f\u4e0d\u8fc7\u662f\u4e94\u516d\u5929\u7684\u4e8b\uff0c\u53f6\u5144\uff0c\u4f60\u522b\u4e3a\u6211\u4eec\u803d\u5fc3\u3002\u201d \u3000\u3000\u5170\u767d\u4e5f\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u4eec\u8981\u662f\u80fd\u6551\u4e86\u4f60\uff0c\u81f3\u5c11\u4e5f\u53ef\u4ee5\u8d4e\u56de\u4e0a\u6b21\u51fa\u5356\u4f60\u7684\u7f6a\u5b7d\u5427!\u201d \u3000\u3000\u53f6\u957f\u9752\u6b63\u60f3\u8bf4\u8bdd\uff0c\u4e00\u53ea\u5c0f\u821f\uff0c\u5ffd\u7136\u51b2\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5170\u767d\u8138\u8272\u4e00\u53d8\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u627e\u6b7b\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u9e26\u53e3\u5200\u4e00\u626c\uff0c\u6b63\u8981\u51fa\u624b\uff0c\u53f6\u957f\u9752\u5df2\u770b\u6e05\u4e86\u8239\u4e0a\u7ad9\u7740\u7684\u90a3\u4eba\u4e43\u662f\u5f15\u7740\u81ea\u5df1\u8fdb\u5165\u9634\u9633\u5821\u7684\u6740\u624b\u590f\u6740\u3002 \u3000\u3000\u4ed6\u6765\u4e0d\u53ca\u559d\u963b\uff0c\u53ea\u5f97\u4e00\u4f38\u624b\u6293\u4f4f\u5170\u767d\u7684\u624b\u8155\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u662f\u6211\u4eec\u7684\u4eba\u3002\u201d \u3000\u3000\u5170\u767d\u8fd9\u65f6\u4e5f\u770b\u51fa\u6765\u4eba\u662f\u8c01\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u662f\u4ed6?\u6211\u8ddf\u59d0\u59d0\u89c1\u8fc7\u4ed6\u4e00\u9762\u3002\u201d \u3000\u3000\u590f\u6740\u5728\u8fd9\u7728\u773c\u4e4b\u95f4\uff0c\u5df2\u5230\u4e86\u753b\u822b\u7684\u53f3\u8237\u3002 \u3000\u3000\u4e01\u5cf0\u8fd9\u65f6\u7531\u8239\u5c3e\u8d70\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u590f\u5144\u4e5f\u8d76\u6765\u4e86\u5417?\u201d \u3000\u3000\u590f\u6740\u9053\uff1a \u201c\u4f55\u5251\u4f7f\u8981\u6211\u6765\u544a\u8bc9\u53f6\u5251\u4f7f\uff0c\u4ed6\u5728\u660e\u5929\u5929\u4eae\u4ee5\u524d\uff0c\u624d\u8d76\u5f97\u56de\u6765\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u590f\u5144\uff0c\u4f55\u5bd2\u8863\u73b0\u5728\u4f55\u5904?\u201d \u3000\u3000\u590f\u6740\u9053\uff1a\u201c\u5728\u5cb8\u4e0a\uff0c\u53ef\u80fd\u662f\u9047\u4e0a\u4e86\u9ad8\u5929\u5065\u90a3\u4e00\u4f19\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u9ad8\u5929\u5065?\u201d \u3000\u3000\u590f\u6740\u6b64\u523b\u5df2\u4e0a\u4e86\u753b\u822b\uff0c\u5e94\u58f0\u9053\uff1a\u201c\u4f55\u5251\u4f7f\u53ea\u662f\u8fd9\u6837\u731c\u6d4b\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u73af\u987e\u4e86\u4e00\u4e0b\u90a3\u4e9b\u6563\u5e03\u5728\u6e56\u4e0a\u9690\u9690\u7ea6\u7ea6\u7684\u5c0f\u821f\uff0c\u63a5\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u4f60\u770b\u6211\u4eec\u80fd\u8d76\u5230\u5c0f\u79ef\u5c71\u5417?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u80fd\u3002\u201d \u3000\u3000\u4ed6\u7b54\u5f97\u5341\u5206\u80af\u5b9a\uff0c\u7389\u5170\u53cc\u59dd\u4e0d\u7531\u5f97\u7b11\u4e86\u4e00\u7b11\u3002 \u3000\u3000\u53f6\u957f\u9752\u6df1\u89c9\u81ea\u5df1\u521d\u6b21\u5bf9\u654c\uff0c\u5c31\u9677\u8eab\u91cd\u56f4\uff0c\u82e5\u662f\u4e0d\u80fd\u5e73\u5b89\u51fa\u9669\uff0c\u5c82\u4e0d\u662f\u6709\u8d1f\u9ec4\u5821\u4e3b\u91cd\u6258\u4e4b\u4efb\u5417?\u662f\u6545\u4ed6\u7684\u5fc3\u60c5\u5341\u5206\u6c89\u91cd\u3002 \u3000\u3000\u5e78\u597d\u5bf9\u65b9\u706b\u711a\u8fde\u82b1\u53f7\u4e4b\u540e\uff0c\u5927\u90e8\u5206\u5c0f\u8239\u90fd\u56f4\u5728\u83b2\u82b1\u53f7\u56db\u5468\uff0c\u67e5\u770b\u83b2\u82b1\u53f7\u4e0a\u7684\u4eba\u662f\u5426\u4f1a\u8df3\u6c34\u9003\u751f\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u53f6\u957f\u9752\u8fd9\u6761\u753b\u822b\u5374\u80fd\u5e73\u5b89\u62b5\u8fbe\u5c0f\u79ef\u5c71\u7684\u5357\u9762\u6c99\u6ee9\u3002 \u3000\u3000\u53f6\u957f\u9752\u6df1\u6df1\u7684\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u7acb\u5373\u6e05\u67e5\u6211\u4eec\u7684\u8239\u53ea\uff0c\u8bbe\u6cd5\u8ba9\u4ed6\u4eec\u5e73\u5b89\u4e0a\u5cb8\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a \u201c\u5c5e\u4e0b\u8fd9\u5c31\u53bb\u529e\uff0c\u5251\u4f7f\u8bf7\u548c\u4e24\u4f4d\u59d1\u5a18\u5148\u5230\u5cb8\u4e0a\u5427\u3002\u6211\u8981\u9a6c\u826f\u6765\u66ff\u5251\u4f7f\u5f15\u8def\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\u3002\u201d \u3000\u3000\u5f53\u4e0b\u548c\u7389\u5170\u53cc\u59dd\uff0c\u4e0b\u8239\u4e0a\u5cb8\u3002 \u3000\u3000\u9a6c\u826f\u7acb\u5373\u968f\u7740\u4ed6\u4eec\u4e0a\u5cb8\u4e86\u3002 \u3000\u3000\u753b\u822b\u5728\u4e01\u5cf0\u6307\u6325\u4e0b\uff0c\u5f88\u5feb\u7684\u5411\u4e1c\u8fb9\u7684\u5c71\u51f9\u9a76\u53bb\u3002 \u3000\u3000\u53e3 \u53e3 \u53e3 \u3000\u3000\u5c0f\u79ef\u5c71\u7684\u9762\u79ef\u4e0d\u5927\uff0c\u9664\u4e86\u5357\u9762\u7684\u767e\u4e08\u6c99\u6ee9\uff0c\u5176\u4ed6\u4e09\u9762\u90fd\u662f\u5c71\u5ca9\u5ced\u58c1\u3002 \u3000\u3000\u53f6\u957f\u9752\u3001\u7389\u5170\u53cc\u59dd\u5728\u9a6c\u826f\u5f15\u5bfc\u4e0b\uff0c\u8d8a\u8fc7\u4e86\u4e00\u5ea7\u5c0f\u5c71\uff0c\u6b63\u6df1\u5165\u5c0f\u79ef\u5c71\u7684\u4e3b\u5cf0\u3002 \u3000\u3000\u6562\u60c5\uff0c\u8fd9\u5ea7\u5c0f\u79ef\u5c71\u5982\u662f\u7531\u4fa7\u9762\u6765\u770b\uff0c\u9887\u4f3c\u4e00\u53ea\u5012\u653e\u6c34\u4e0a\u7684\u846b\u82a6\uff0c\u9876\u90e8\u662f\u90a3\u7247\u6c99\u6ee9\uff0c\u5c3e\u90e8\u5c31\u662f\u4e3b\u5cf0\u3002 \u3000\u3000\u56db\u4eba\u5728\u6d53\u96fe\u6e10\u8d77\u7684\u591c\u8272\u4e2d\uff0c\u62ab\u8346\u65a9\u68d8\uff0c\u6500\u884c\u5728\u5ca9\u77f3\u8349\u6728\u4e4b\u95f4\uff0c\u82e5\u975e\u9a6c\u826f\u5f15\u8def\uff0c\u53f6\u957f\u9752\u5012\u4e0d\u4f1a\u89c9\u5f97\u4ec0\u4e48\uff0c\u7389\u5170\u53cc\u59dd\u53ef\u5c31\u8981\u5403\u82e6\u4e86\u3002 \u3000\u3000\u81f3\u5c11\uff0c\u5979\u4eec\u90a3\u4e00\u8eab\u7f57\u886b\u5c31\u65e0\u6cd5\u4fdd\u6301\u5b8c\u6574\u3002 \u3000\u3000\u8d8a\u8fc7\u4e00\u5904\u65ad\u5ca9\uff0c\u5c31\u5230\u4e86\u4e3b\u5cf0\u3002 \u3000\u3000\u53f6\u957f\u9752\u7a0d\u4e00\u6253\u91cf\u5cf0\u9876\u4e0a\u7684\u4e0d\u8db3\u4e08\u8bb8\u7684\u4e00\u5757\u5e73\u6574\u5de8\u77f3\uff0c\u9053\uff1a\u201c\u9a6c\u826f\uff0c\u8fd9\u513f\u5e73\u65f6\u6709\u6ca1\u6709\u6e14\u6c11\u505c\u7559?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u6e56\u4e0a\u6d53\u96fe\u5df2\u56db\u9762\u5347\u8d77\uff0c\u5bf9\u65b9\u7684\u5c0f\u821f\u4e0a\u706f\u706b\uff0c\u5df2\u53d8\u5f97\u660f\u6697\u3001\u8ff7\u6726\u3001\u82e5\u9690\u82e5\u73b0\u4e86\u3002 \u3000\u3000\u53f6\u957f\u9752\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a \u201c\u9a6c\u826f\uff0c\u6d53\u96fe\u5df2\u8d77\uff0c\u6211\u4eec\u7684\u4eba\u4f1a\u4e0d\u4f1a\u5728\u6e56\u4e0a\u8ff7\u9014?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff0c \u201c\u4e0d\u4f1a\u3002\u201d\u7565\u4e00\u8fdf\u7591\uff0c\u63a5\u9053\uff1a \u201c\u5251\u4f7f\uff0c\u5c71\u9876\u4e0a\u96fe\u6d53\uff0c\u5c5e\u4e0b\u8bb0\u5f97\u5728\u524d\u5cf0\u7684\u534a\u5c71\uff0c\u6709\u4e00\u5904\u8349\u68da\u53ef\u4ee5\u52c9\u5f3a\u6682\u907f\u9732\u6c34\u6e7f\u6c14\u3002\u201d \u3000\u3000\u7389\u7fe0\u63a5\u9053\uff1a\u201c\u6211\u7684\u5934\u53d1\u90fd\u6e7f\u4e86\uff0c\u6709\u4e2a\u8349\u68da\u6321\u6321\u9732\u6c34\uff0c\u4e5f\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u770b\u4e86\u7389\u5170\u53cc\u59dd\u4e00\u773c\uff0c\u7b11\u4e86\u4e00\u7b11\u3002\u5fc3\u60f3\uff1a\u5973\u4eba\u6c38\u8fdc\u662f\u5973\u4eba\uff0c\u5728\u56f0\u96be\u4e2d\uff0c\u4ecd\u7136\u5fd8\u4e0d\u4e86\u7231\u7f8e\u7684\u5929\u6027\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u53ea\u5411\u9a6c\u826f\u70b9\u4e86\u70b9\u5934\u9053\uff1a \u201c\u4e01\u5cf0\u4ed6\u4eec\u77e5\u9053\u90a3\u4e2a\u8349\u68da?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u77e5\u9053\uff0c\u90a3\u5ea7\u8349\u68da\uff0c\u662f\u4ed6\u6d3e\u4eba\u642d\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u8f6c\u8eab\u5411\u4e8c\u5973\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u8bf7\u554a!\u201d \u3000\u3000\u5170\u767d\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u5374\u5fcd\u4f4f\u4e86\u3002 \u3000\u3000\u5728\u9a6c\u826f\u7684\u5f15\u5bfc\u4e4b\u4e0b\uff0c\u4ed6\u4eec\u5faa\u7740\u539f\u8def\u4e0b\u4e86\u4e3b\u5cf0\uff0c\u8fc8\u8fc7\u524d\u5cf0\uff0c\u7ec8\u4e8e\u6765\u5230\u4e86\u4e00\u95f4\u4f9d\u7740\u5c71\u5ca9\u642d\u6210\u7684\u5c0f\u8349\u68da\u3002 \u3000\u3000\u8349\u68da\u7684\u540e\u9762\uff0c\u662f\u5ced\u58c1\u4f5c\u5899\uff0c\u5de6\u53f3\u4e24\u8fb9\uff0c\u5404\u6302\u4e86\u4e00\u7247\u6cb9\u5e03\u3002 \u3000\u3000\u5411\u7740\u90a3\u6c99\u6ee9\u7684\u4e00\u9762\uff0c\u5374\u662f\u7a7a\u7740\u7684\uff0c\u5750\u5728\u8349\u68da\u4e4b\u4e2d\uff0c\u53ef\u4ee5\u5c55\u671b\u90a3\u6c99\u6ee9\u7684\u4e00\u5207\u3002 \u3000\u3000\u6b64\u523b\u56e0\u4e3a\u6d53\u96fe\u6ee1\u5929\uff0c\u6545\u800c\u4ed6\u4eec\u5e76\u672a\u89c9\u5f97\u8fd9\u8349\u68da\u642d\u5728\u6b64\u5904\u7684\u91cd\u8981\u3002 \u3000\u3000\u56db\u4eba\u521a\u521a\u8fdb\u5165\u8349\u68da\uff0c\u4e01\u5cf0\u5c31\u5306\u5306\u7684\u9886\u7740\u56db\u540d\u5927\u6c49\uff0c\u628a\u753b\u822b\u4e0a\u7684\u98df\u7528\u4e4b\u7269\uff0c\u9001\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u60f3\u5f97\u771f\u5468\u5230\u3002\u201d \u3000\u3000\u5170\u767d\u63a5\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u4f60\u8fd9\u4e9b\u624b\u4e0b\uff0c\u771f\u4f1a\u529e\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u4eec\u8bad\u7ec3\u6709\u7d20\uff0c\u80fd\u6709\u4eca\u5929\u8fd9\u7b49\u6210\u5c31\uff0c\u5168\u662f\u9ec4\u5821\u4e3b\u7684\u624d\u667a\u3002\u201d \u3000\u3000\u4e01\u5cf0\u6307\u6325\u56db\u540d\u5927\u6c49\u653e\u4e0b\u98df\u7269\uff0c\u7b49\u4ed6\u4eec\u51fa\u4e86\u8349\u68da\uff0c\u8fd9\u624d\u4f4e\u58f0\u9053\uff1b\u201c\u56de\u5251\u4f7f\uff0c\u8fde\u590f\u6740\u5212\u6765\u7684\u8239\uff0c\u6211\u4eec\u4e00\u5171\u6709\u516b\u6761\u5c0f\u8239\uff0c\u5df2\u5168\u90e8\u5230\u4e86\u5c0f\u79ef\u5c71\uff0c\u53ea\u662f\u2026\u2026\u201d\u8fdf\u7591\u4e86\u4e00\u4e0b\u6ca1\u5f80\u4e0b\u8bf4\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u6709\u4ec0\u4e48\u8bdd\uff0c\u53ea\u7ba1\u8bf4\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a \u201c\u516b\u53ea\u5c0f\u8239\u7684\u56db\u8258\uff0c \u5df2\u7ecf\u5728\u8131\u56f4\u65f6\u649e\u5f97\u6709\u4e86\u88c2\u75d5\uff0c\u76ee\u4e0b\u5df2\u662f\u534a\u6c89\u534a\u6d6e\uff0c\u4e0d\u80fd\u518d\u7528\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u773c\u4e0b\u6211\u4eec\u8fd8\u8981\u7528\u5417?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u635f\u574f\u4e86\u5c0f\u821f\uff0c\u4e43\u662f\u5c5e\u4e0b\u758f\u5ffd\u4e4b\u7f6a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u59a8\uff0c\u6c34\u4e0a\u8131\u56f4\uff0c\u4eba\u5458\u80fd\u591f\u65e0\u4f24\uff0c\u5df2\u7ecf\u4e0d\u5bb9\u6613\u4e86\u3002\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u5bf9\u65b9\u7684\u8239\u5462?\u6709\u6ca1\u6709\u8ddf\u8fc7\u6765?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4e0d\u8fc7\u9ece\u660e\u4e4b\u524d\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u53d1\u73b0\u6211\u4eec\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u7b11\u9053\uff1a\u201c\u753b\u822b\u5462?\u90a3\u662f\u4e00\u4e2a\u5f88\u5927\u7684\u76ee\u6807\u3002\u201d\u4e01\u5cf0\u9053\uff1a \u201c\u753b\u822b\u5df2\u85cf\u5728\u5c71\u5ca9\u7684\u5ced\u58c1\u4e4b\u4e0b\uff0c\u52a0\u4e86\u8349\u6728\u4f2a\u88c5\uff0c\u4ed6\u4eec\u4e0d\u5230\u8fd1\u524d\uff0c\u53d1\u73b0\u4e0d\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u5f88\u597d\u2026\u2026\u201d\u76ee\u5149\u4e00\u8f6c\uff0c\u63a5\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u65e2\u7136\u753b\u822b\u53ef\u4ee5\u4f2a\u88c5\uff0c\u5bf9\u65b9\u600e\u4e48\u53d1\u73b0\u6211\u4eec\u884c\u8e2a?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5c5e\u4e0b\u76f8\u4fe1\uff0c\u5bf9\u65b9\u4e5f\u4f1a\u4e0a\u5cb8\u6765\u67e5\u63a2\uff0c\u5c0f\u79ef\u5c71\u65b9\u5706\u4e0d\u8fc7\u4e24\u91cc\uff0c\u6240\u4ee5\u2026\u2026\u5c51\u4e0b\u624d\u6709\u8fd9\u7b49\u60f3\u6cd5\u3002\u201d \u3000\u3000\u5170\u767d\u4e00\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u6562\u4e0a\u5cb8\u5417?\u6211\u4eec\u4e5f\u4e0d\u662f\u6b7b\u4eba\uff0c\u7b49\u7740\u4ed6\u7684\u4e0a\u6765\u627e\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u654c\u52bf\u5f3a\u5927\uff0c\u59d1\u5a18\uff0c\u4e00\u65e6\u52a8\u624b\uff0c\u5fc5\u7136\u4f24\u4ea1\u5f88\u91cd\u3002\u201d \u3000\u3000\u5170\u767d\u4e00\u6014\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u6211\u4eec\u6700\u597d\u4e0d\u8ddf\u4ed6\u4eec\u52a8\u624b?\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a \u201c\u4e01\u5cf0\uff0c\u4f60\u597d\u50cf\u8fd8\u6709\u4ec0\u4e48\u4e8b\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u662f\u5417?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a \u201c\u5c5e\u4e0b\u4e0d\u6562\u9690\u7792\u5251\u4f7f\uff0c\u590f\u6740\u6765\u6b64\u4e4b\u65f6\uff0c\u66fe\u7ecf\u53d1\u73b0\uff0c\u5bf9\u65b9\u9664\u4e86\u5c0f\u821f\u4e4b\u5916\uff0c\u8fd8\u6709\u4e24\u8258\u53cc\u6845\u8239\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6?\u8239\u4e0a\u7684\u4eba\u662f\u8c01?\u590f\u6740\u77e5\u9053\u5417?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u590f\u6740\u8bf4\uff0c\u53ef\u80fd\u662f\u72c2\u9f99\u3001\u98de\u9e70\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u542c\u5f97\u4e00\u6014\u9053\uff1a\u201c\u72c2\u9f99\u3001\u98de\u9e70?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u590f\u6740\u66fe\u770b\u5230\u90a3\u4e24\u8258\u8239\u4e0a\uff0c\u5404\u6709\u4e00\u4e2a\u6807\u8bc6\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u662f\u9f99?\u8fd8\u662f\u9e70?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u4e00\u6761\u662f\u9f99\uff0c\u4e00\u53ea\u662f\u9e70\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5982\u6b64\u770b\u6765\uff0c\u5927\u6982\u662f\u4e0d\u4f1a\u9519\u4e86\uff0c\u72c2\u9f99\u3001\u98de\u9e70\u51fa\u73b0\u5728\u592a\u6e56\uff0c\u5b9e\u5728\u662f\u2026\u2026\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u5144\uff0c\u5bf9\u65b9\u82e5\u662f\u72c2\u9f99\u3001\u98de\u9e70\uff0c\u54b1\u4eec\u53ef\u5f97\u597d\u597d\u5e03\u7f6e\u4e00\u4e0b\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u4ed6\u770b\u4e86\u770b\u4e01\u5cf0\u3001\u9a6c\u826f\uff0c\u63a5\u9053\uff1a\u201c\u6211\u4eec\u4e00\u5171\u6709\u591a\u5c11\u4eba?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u8fde\u4e24\u4f4d\u59d1\u5a18\u7b97\u4e0a\uff0c\u53ea\u6709\u5341\u516d\u4e2a\u4eba\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4eba\u591a\u4eba\u5c11\uff0c\u4e0d\u662f\u95ee\u9898\u2026\u2026\u201d\u5979\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u4f3c\u7b11\u975e\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u662f\u6211\u5c0f\u770b\u4e86\u53f6\u5144\uff0c\u4e03\u5251\u8ffd\u9b42\uff0c\u53ea\u6015\u4e0d\u5bb9\u6613\u80dc\u5f97\u4e86\u72c2\u9f99\u3001\u98de\u9e70\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7389\u7fe0\uff0c\u6211\u53f6\u67d0\u4eba\u80fd\u6d3b\u5230\u4eca\u5929\uff0c\u5c31\u662f\u5c1a\u6709\u81ea\u77e5\u4e4b\u660e\u3002\u4e00\u4e2a\u72c2\u9f99\uff0c\u6216\u662f\u4e00\u4e2a\u98de\u9e70\uff0c\u4e5f\u8bb8\u6211\u8fd8\u80fd\u6709\u673a\u4f1a\u4e00\u62fc\uff0c\u4e24\u4eba\u5168\u6765\u7684\u8bdd\uff0c\u4e8b\u60c5\u5c31\u6709\u5f88\u5927\u7684\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u4f55\u5251\u4f7f\u4f1a\u8d76\u6765\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u95ee\u9898\u662f\u4f55\u5bd2\u8863\u8d76\u5230\u4e4b\u524d\uff0c\u4ed6\u4eec\u5982\u679c\u53d1\u52a8\u653b\u52bf\u5462\uff1f\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u6211\u4eec\u4f1a\u5168\u529b\u4e00\u6218\uff0c\u5c31\u7b97\u6211\u4eec\u6218\u6b7b\uff0c\u4e5f\u8981\u4fdd\u62a4\u5251\u4f7f\u7684\u5b89\u5168\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u611f\u6fc0\u7684\u7b11\u4e86\u4e00\u7b11\u3002 \u3000\u3000\u9a6c\u826f\u63a5\u9053\uff1a\u201c\u5251\u4f7f\u653e\u5fc3\uff0c\u9634\u9633\u5821\u7684\u4eba\uff0c\u5e76\u4e0d\u6015\u6b7b\u3002\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u4eec\u4e00\u7247\u5fe0\u5fc3\uff0c\u6211\u5f88\u611f\u6fc0\u3002\u91cd\u8981\u7684\u662f\uff0c\u6211\u4e0d\u80fd\u773c\u770b\u7740\u5927\u5bb6\u6218\u6b7b\u6b64\u5730\u3002\u201d \u3000\u3000\u4ed6\u8bed\u97f3\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u4f60\u4f20\u4ee4\u4e0b\u53bb\uff0c\u6ca1\u6709\u5f97\u5230\u6211\u7684\u5141\u8bb8\uff0c\u4efb\u4f55\u4eba\u4e0d\u8bb8\u51fa\u624b\u5bf9\u654c\u3002\u201d \u3000\u3000\u4e01\u5cf0\u4e00\u6014\u9053\uff1a\u201c\u8fd9\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u8981\u4ed6\u4eec\u8eb2\u8d77\u6765\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1b\u201c\u5251\u4f7f\uff0c\u4ed6\u4eec\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u6211\u77e5\u9053\u4ed6\u4eec\u6ca1\u6709\u4eba\u6015\u6b7b\uff0c\u4e0d\u8fc7\uff0c\u9047\u5230\u4e86\u72c2\u9f99\u3001\u98de\u9e70\uff0c\u62fc\u6b7b\u662f\u6ca1\u6709\u7528\u7684\u3002\u201d \u4e01\u5cf0\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u77e5\u9053\u4e01\u5cf0\u53e3\u4e2d\u867d\u7136\u5e94\u4e86\u662f\uff0c\u4f46\u5fc3\u4e2d\u660e\u660e\u4e0d\u613f\uff0c\u7b11\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u4e0d\u4f46\u662f\u4f60\u4eec\u4e0d\u80fd\u51fa\u624b\uff0c\u4f55\u5bd2\u8863\u8d76\u5230\u4e4b\u524d\uff0c\u8fde\u6211\u4e5f\u4e0d\u613f\u9732\u9762\uff0c\u5821\u4e3b\u8981\u6211\u5e26\u4f60\u4eec\u5230\u592a\u6e56\uff0c\u6211\u8981\u4f60\u4eec\u6bcf\u4e2a\u4eba\u90fd\u80fd\u4e1d\u6beb\u65e0\u4f24\u7684\u56de\u53bb\u5440\u3002\u201d \u3000\u3000\u4e01\u5cf0\u70b9\u5934\u9053\uff1a\u201c\u5c5e\u4e0b\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u753b\u822b\u9760\u5728\u5c71\u811a\u4e0b\uff0c\u5f88\u7a33\u5f53\u5417?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u63a9\u85cf\u5f97\u5f88\u597d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u56de\u987e\u7389\u5170\u53cc\u59dd\u9053\uff1a\u201c\u753b\u822b\u7684\u8231\u623f\uff0c\u8bbe\u6709\u5e8a\u69bb\uff0c\u4f60\u4eec\u6700\u597d\u80fd\u5230\u753b\u822b\u4e2d\u4f11\u606f\u3002\u201d \u3000\u3000\u5170\u767d\u7b11\u9053\uff1a\u201c\u4f60\u5462?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u7559\u5728\u6b64\u5904\u3002\u201d \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u6211\u4eec\u8d70\u5427!\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1b\u201c\u4e01\u5cf0\uff0c\u4f60\u9001\u4e24\u4f4d\u59d1\u5a18\u4e0a\u8239\u53bb\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u662f\uff01\u201d \u5411\u4e8c\u5973\u9053\uff1a\u201c\u8bf7\uff01\u201d \u3000\u3000\u7389\u5170\u53cc\u59dd\u7f13\u7f13\u4e3e\u6b65\uff0c\u968f\u7740\u4e01\u5cf0\u884c\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u76ee\u9001\u4e01\u5cf0\u7b49\u79bb\u53bb\u4e4b\u540e\uff0c\u7acb\u5373\u548c\u9a6c\u826f\u6b65\u5411\u6c99\u6ee9\u4e4b\u4e0a\u3002 \u3000\u3000\u4e3e\u76ee\u6240\u89c1\uff0c\u6e56\u4e0a\u5c0f\u821f\u7684\u706f\u5149\uff0c\u4e5f\u8d8a\u53d1\u7684\u7a00\u5c11\uff0c\u4eff\u4f5b\u5bf9\u65b9\u5df2\u653e\u5f03\u4e86\u8ffd\u5bfb\u53f6\u957f\u9752\u7b49\u4eba\u7684\u4f01\u56fe\u3002 \u3000\u3000\u4f46\u662f\uff0c\u654c\u65b9\u7528\u5fc3\u7a76\u7adf\u662f\u4ec0\u4e48\u5462?\u53f6\u957f\u9752\u53ef\u4e0d\u6562\u6258\u5927\uff0c\u53ea\u56e0\u590f\u6740\u6240\u89c1\u5230\u72c2\u9f99\u3001\u98de\u9e70\u7684\u6807\u5fd7\uff0c\u50cf\u4e00\u5757\u5343\u65a4\u91cd\u77f3\u538b\u5728\u53f6\u957f\u9752\u7684\u5fc3\u5934\u3002 \u3000\u3000\u5982\u679c\u90a3\u4e9b\u5c0f\u821f\u662f\u72c2\u9f99\u3001\u98de\u9e70\u7684\u624b\u4e0b\uff0c\u4ed6\u4eec\u51b3\u4e0d\u4f1a\u5c31\u6b64\u7f62\u624b\u7684\u3002 \u3000\u3000\u72c2\u9f99\u3001\u98de\u9e70\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u8a89\uff0c\u53f6\u957f\u9752\u5fc3\u91cc\u6709\u6570\uff0c\u8fd9\u4e24\u4e2a\u4eba\u53ef\u662f\u50cf\u5929\u624d\u822c\u7684\u75af\u5b50\u3002 \u3000\u3000\u9664\u975e\u4ed6\u4eec\u4e0d\u60f3\u53bb\u505a\u4e00\u4ef6\u4e8b\uff0c\u5426\u5219\uff0c\u73a9\u6389\u4e86\u5c0f\u547d\uff0c\u4e5f\u4e0d\u4f1a\u9000\u7f29\u3002 \u3000\u3000\u662f\u4ee5\uff0c\u53f6\u957f\u9752\u624d\u4f1a\u89c9\u5f97\u90a3\u4e9b\u5c0f\u821f\u4e95\u672a\u903c\u8fd1\u5c0f\u79ef\u5c71\uff0c\u6b8a\u4e3a\u51fa\u4eba\u610f\u6599\u3002 \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u53f6\u957f\u9752\u4e0d\u7981\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a \u201c\u5e0c\u671b\u590f\u6740\u771f\u7684\u770b\u9519\u4e86\u624d\u597d\u3002\u201d \u9a6c\u826f\u8d70\u5728\u53f6\u957f\u9752\u8eab\u540e\uff0c\u95fb\u8a00\u63a5\u9053\uff1a \u201c\u5251\u4f7f\uff0c\u590f\u6740\u7d20\u6765\u673a\u667a\uff0c\u4e5f\u751a\u4e3a\u8c28\u614e\uff0c\u5982\u679c\u4ed6\u8bf4\u662f\u72c2\u9f99\u3001\u98de\u9e70\uff0c\u5341\u4e4b\u4e03\u516b\u662f\u4e0d\u4f1a\u9519\u7684\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9a6c\u826f\uff0c\u6211\u4e5f\u77e5\u9053\u590f\u6740\u4e0d\u4f1a\u770b\u9519\u7684\u3002\u5426\u5219\u6211\u53c8\u600e\u4e48\u4f1a\u8981\u4e01\u5cf0\u4f20\u4ee4\uff0c\u4e0d\u8bb8\u6211\u4eec\u4eba\u51fa\u624b?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u5251\u4f7f\u5173\u7231\u90e8\u5c5e\uff0c\u6211\u4eec\u2026\u2026\u90fd\u5f88\u611f\u6fc0\u3002\u201d\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u4eec\u4e5f\u4e0d\u80fd\u5750\u4ee5\u5f85\u6bd9\u554a!\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5750\u4ee5\u5f85\u6bd9?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u5c0f\u79ef\u5c71\u6ca1\u6709\u50a8\u5907\u98df\u7528\u4e4b\u7269\uff0c\u5341\u516d\u4e2a\u4eba\u600e\u4e48\u8fc7\u4e0b\u53bb\u5462?\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u9053\uff1a\u201c\u4e0d\u59a8\uff0c\u5929\u4e0b\u6ca1\u6709\u997f\u6b7b\u7684\u4eba\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u672a\u5df2\uff0c\u53ea\u89c1\u4e01\u5cf0\u5306\u5306\u8d76\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8499\u8499\u591c\u8272\u4e4b\u4e2d\uff0c\u53f6\u957f\u9752\u53d1\u89c9\u4e01\u5cf0\u8138\u8272\u751a\u4e3a\u602a\u5f02\uff0c\u5fc3\u4e2d\u6697\u6697\u4e00\u60ca\uff0c\u8131\u53e3\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\u5417?\u201d \u3000\u3000\u4e01\u5cf0\u4e0d\u5b89\u5730\u770b\u4e86\u770b\u56db\u5468\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c5e\u4e0b\u53d1\u73b0\u4e86\u654c\u4eba\u8e2a\u8ff9\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4e5f\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u4f60\u89c1\u5230\u4e86\u4ed6\u4eec?\u201d \u3000\u3000\u4e01\u5cf0\u6447\u5934\u9053\uff1a\u201c\u5c5e\u4e0b\u5e76\u672a\u89c1\u5230\u4ed6\u4eec\uff0c\u4e0d\u8fc7\u2026\u2026\u4e24\u540d\u7559\u5728\u5251\u4f7f\u90a3\u8349\u68da\u5916\u7684\u6697\u6869\uff0c\u5374\u88ab\u4eba\u70b9\u4e86\u7a74\u9053\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u54e6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b?\u90a3\u4e24\u540d\u6697\u6869\u4f55\u5728\uff0c\u5bf9\u65b9\u7684\u70b9\u7a74\u624b\u6cd5\u5982\u4f55?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1b \u201c\u70b9\u7a74\u7684\u624b\u6cd5\u6781\u4e3a\u9ad8\u660e\uff0c\u5c5e\u4e0b\u6ca1\u6562\u79fb\u52a8\uff0c\u7279\u6765\u544a\u5251\u4f7f\u3002\u201d \u53f6\u957f\u9752\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u8d70\uff01\u201d \u3000\u3000\u4e09\u4eba\u8fc5\u901f\u7684\u5954\u5411\u90a3\u8349\u68da\uff0c\u53ea\u89c1\u4e24\u540d\u9634\u9633\u5821\u7684\u5f1f\u5b50\uff0c\u5206\u522b\u8eba\u5728\u68da\u5916\u5730\u4e0a\uff0c\u660f\u8ff7\u4e0d\u9192\u3002 \u3000\u3000\u53f6\u957f\u9752\u5c0f\u5fc3\u7684\u68c0\u67e5\u4e86\u4e00\u4e0b\u4e24\u4eba\uff0c\u4f4e\u5934\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u4e01\u5cf0\u3001\u9a6c\u826f\u5168\u795e\u6212\u5907\uff0c\u9632\u7740\u6709\u4eba\u7a81\u88ad\u3002 \u3000\u3000\u53f6\u957f\u9752\u5ffd\u7136\u4e00\u4f38\u624b\uff0c\u5728\u5de6\u9762\u7684\u90a3\u540d\u9634\u9633\u5821\u5f1f\u5b50\u7389\u6795\u7a74\u4e0a\u4e00\u62cd\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u597d\u9ad8\u660e\u7684\u62c2\u7a74\u624b\u6cd5\u2026\u2026\u201d \u3000\u3000\u90a3\u540d\u5f1f\u5b50\uff0c\u5e94\u624b\u53d1\u51fa\u4e00\u58f0\u53f9\u606f\u3002 \u3000\u3000\u53f6\u957f\u9752\u5982\u6cd5\u65bd\u4e3a\uff0c\u5c06\u53f3\u9762\u8eba\u7740\u7684\u90a3\u540d\u5f1f\u5b50\u7a74\u9053\uff0c\u4e5f\u540c\u65f6\u89e3\u5f00\u3002 \u3000\u3000\u4e01\u5cf0\u3001\u9a6c\u826f\u4e92\u76f8\u770b\u4e86\u4e00\u773c\uff0c\u50b2\u6709\u7b11\u610f\u3002 \u3000\u3000\u6562\u60c5\uff0c\u4e01\u5cf0\u66fe\u7ecf\u8bd5\u8fc7\u4e86\u4e09\u79cd\u624b\u6cd5\uff0c\u90fd\u89e3\u4e0d\u5f00\u8fd9\u4e24\u540d\u5f1f\u5b50\u7684\u7a74\u9053\uff0c\u53f6\u957f\u9752\u5374\u80fd\u4e00\u62cd\u800c\u89e3\uff0c\u4f7f\u5f97\u4e01\u3001\u9a6c\u4e8c\u4eba\u5bf9\u53f6\u957f\u9752\u7684\u6b66\u529f\uff0c\u589e\u52a0\u4e86\u516b\u6210\u4fe1\u5fc3\u3002 \u3000\u3000\u53f6\u957f\u9752\u7f13\u7f13\u7684\u7ad9\u8d77\u8eab\u5b50\uff0c\u770b\u770b\u90a3\u4e24\u540d\u5df2\u7136\u6e05\u9192\u5374\u4f9d\u7136\u65e0\u6cd5\u8d77\u7acb\u7684\u5f1f\u5b50\uff0c\u6325\u4e86\u6325\u624b\u9053\uff1a \u3000\u3000\u201c\u4e01\u5cf0\uff0c\u5c06\u4ed6\u4eec\u62ac\u5165\u753b\u822b\u4f11\u606f\uff0c\u518d\u6709\u4e24\u4e2a\u65f6\u8fb0\uff0c\u5c31\u53ef\u4ee5\u6062\u590d\u4e86\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u662f!\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u5411\u5916\u4f5c\u4e86\u4e2a\u624b\u52bf\uff0c\u7acb\u5373\u6709\u56db\u540d\u9634\u9633\u5821\u5f1f\u5b50\u75be\u884c\u800c\u6765\uff0c\u5c06\u4e24\u540d\u8eba\u5728\u5730\u4e0a\u7684\u5f1f\u5b50\u62ac\u8d70\u3002 \u3000\u3000\u9752\u770b\u7684\u6697\u6697\u70b9\u5934\uff0c\u77e5\u9053\u5728\u8349\u68da\u9644\u8fd1\uff0c\u4e01\u5cf0\u4e00\u5b9a\u5b89\u6392\u4e0d\u5c11\u4eba\uff0c\u62a4\u536b\u7740\u81ea\u5df1\u7684\u5b89\u5168\u3002\u5f53\u4e0b\u76ee\u5149\u4e00\u8f6c\uff0c\u63a5\u9053\uff1a \u201c\u4e01\u5cf0\uff0c\u522b\u7684\u5730\u65b9\u6709\u6ca1\u6709\u52a8\u9759?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5c5e\u4e0b\u5c1a\u672a\u63a5\u83b7\u53d1\u73b0\u654c\u8e2a\u7684\u62a5\u544a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7565\u4e00\u6c89\u541f\uff0c\u6b65\u5165\u8349\u68da\u3002 \u3000\u3000\u4e01\u5cf0\u3001\u9a6c\u826f\u7d27\u7d27\u5730\u8ddf\u5728\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u8349\u68da\u91cc\u9762\uff0c\u4e00\u5207\u5982\u65e7\uff0c\u4f46\u53f6\u957f\u9752\u5374\u89c9\u5f97\uff0c\u6709\u4e9b\u4e8b\u513f\u4e0d\u5927\u5bf9\u52b2\u3002 \u3000\u3000\u56de\u987e\u4e86\u4e01\u5cf0\u4e00\u773c\uff0c\u9053\uff1a \u201c\u4e01\u5cf0\uff0c\u4f60\u628a\u90a3\u4e9b\u98df\u7528\u4e4b\u7269\uff0c\u53c8\u85cf\u8d77\u6765\u4e86\u5417?\u201d \u3000\u3000\u4e01\u5cf0\u6447\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u554a!\u5251\u4f7f\u8ddf\u9a6c\u5144\u6ca1\u6709\u5403\u5417?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u5251\u4f7f\u8ddf\u5144\u5f1f\u5728\u4f60\u4eec\u79bb\u5f00\u4e4b\u540e\uff0c\u5c31\u53bb\u4e86\u6ee9\u5934\u554a!\u201d \u3000\u3000\u4e01\u5cf0\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u4f1a\u662f\u4ec0\u4e48\u4eba\u62ff\u8d70\u4e86\u8fd9\u4e9b\u9152\u83dc\u5462?\u201d \u3000\u3000\u9a6c\u826f\u770b\u4e86\u53f6\u957f\u9752\u4e00\u773c\u9053\uff1a \u201c\u5251\u4f7f\uff0c\u4f1a\u4e0d\u4f1a\u662f\u90a3\u4e9b\u4e0b\u624b\u6697\u7b97\u672c\u5821\u5f1f\u5b50\u7684\u4eba\u62ff\u8d70\u4e86?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4e0b\u624b\u70b9\u4e86\u672c\u5821\u5f1f\u5b50\u7a74\u9053\u7684\u4eba\uff0c\u53ea\u662f\u4e3a\u4e86\u62ff\u8d70\u8fd9\u4e9b\u98df\u7528\u4e4b\u7269\u5417?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u5c5e\u4e0b\u53ea\u662f\u89c9\u5f97\u5947\u602a\u2026\u2026\u9664\u4e86\u90a3\u4e0b\u624b\u4e4b\u4eba\u4ee5\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u80fd\u62ff\u8d70\u8fd9\u4e9b\u98df\u7269?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u4e5f\u8bb8\uff0c\u5bf9\u65b9\u60f3\u62ff\u8d70\u98df\u7269\uff0c\u997f\u5f97\u6211\u4eec\u65e0\u529b\u5e94\u6218\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u9053\uff1a\u201c\u6e56\u4e2d\u591a\u7684\u662f\u9c7c\u867e\u6c34\u4ea7\uff0c\u60f3\u997f\u5f97\u6211\u4eec\u65e0\u529b\u5e94\u6218\uff0c\u600e\u4e48\u53ef\u80fd?\u201d \u3000\u3000\u4e01\u3001\u9a6c\u4e8c\u4eba\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u4e5f\u89c9\u5f97\u53f6\u957f\u9752\u8bf4\u7684\u6709\u7406\uff0c\u4ee5\u6765\u4eba\u6b66\u529f\u4e4b\u9ad8\uff0c\u4e0d\u4f1a\u60f3\u4e0d\u5230\u6e56\u4e2d\u7684\u6c34\u4ea7\u53ef\u4ee5\u679c\u8179\u3002 \u3000\u3000\u663e\u7136\uff0c\u6b64\u4eba\u62ff\u8d70\u4e86\u98df\u7269\uff0c\u5fc5\u662f\u53e6\u6709\u539f\u56e0\u3002 \u3000\u3000\u53f6\u957f\u9752\u8fd9\u65f6\u5df2\u8fc5\u5feb\u7684\u5728\u8349\u68da\u4e2d\u5404\u5904\u68c0\u67e5\u4e86\u4e00\u904d\u3002 \u3000\u3000\u4e01\u5cf0\u548c\u9a6c\u826f\uff0c\u4e5f\u8d70\u5230\u8349\u68da\u4e4b\u5916\uff0c\u4ed4\u7ec6\u7684\u68c0\u67e5\u4e86\u4e00\u756a\uff0c\u6ca1\u6709\u4efb\u4f55\u53ef\u7591\u7684\u4e8b\u7269\u7559\u4e0b\u3002 \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u9a6c\u826f\uff0c\u8fd9\u5c0f\u79ef\u5c71\uff0c\u4f60\u77e5\u9053\u7684\u591a\u4e0d\u591a\uff1f\u201d \u9a6c\u826f\u9053\uff1a\u201c\u5c5e\u4e0b\u4ee5\u524d\u6765\u8fc7\u4e24\u6b21\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5c5e\u4e0b\u4e5f\u6765\u8fc7\u4e00\u6b21\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u4eec\u786e\u5b9e\u77e5\u9053\u8fd9\u513f\u6ca1\u6709\u4eba\u4f4f\u5417?\u201d\u9a6c\u826f\u9053\uff1a\u201c\u5168\u5c71\u6bcf\u4e00\u4e2a\u5730\u65b9\u90fd\u52d8\u67e5\u8fc7\uff0c\u5e73\u65f6\u9664\u4e86\u5076\u7136\u6709\u907f\u98ce\u7684\u6e14\u821f\u4e4b\u5916\uff0c\u5c71\u4e0a\u4eba\u591c\u540e\u4ece\u6765\u6ca1\u6709\u4eba\u7559\u4f4f\u8fc7\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6?\u5168\u5c71\u6bcf\u4e00\u5904\u90fd\u67e5\u8fc7\u4e86\u5417?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u67e5\u8fc7\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8bed\u97f3\u4e00\u987f\uff0c\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u9664\u4e86\u2026\u2026\u9664\u4e86\u2026\u2026\u201d\u53f6\u957f\u9752\u9053\uff1b\u201c\u9664\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u662f\u4e0d\u662f\u6709\u4ec0\u4e48\u5730\u65b9\uff0c\u4f60\u4eec\u6ca1\u6709\u67e5\u5230\u5916 \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u56de\u5251\u4f7f\uff0c\u5c0f\u79ef\u5c71\u51e1\u662f\u4eba\u8ff9\u53ef\u5230\u4e4b\u5904\uff0c\u6211\u4eec\u90fd\u53bb\u8fc7\u4e86\uff01\u53ea\u6709\u540e\u5c71\u9762\u6e56\u7684\u90a3\u7247\u767e\u4e08\u5ced\u58c1\uff0c\u65e0\u6cd5\u767b\u6500\uff0e\u6ca1\u6709\u53bb\u8fc7\u3002\u201d\u4e01\u5cf0\u9053\uff1a\u201c\u5c5e\u4e0b\u66fe\u5212\u7740\u5c0f\u821f\uff0c\u5230\u8fc7\u90a3\u9762\u5ced\u58c1\u4e4b\u4e0b\uff0c\u90a3\u513f\u60ac\u5d16\u56de\u7acb\uff0c\u5bf8\u8349\u4e0d\u751f\uff0c\u4e0d\u53ef\u80fd\u6709\u4eba\u505c\u7559\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e48\u770b\u6765\uff0c\u5fc5\u662f\u6709\u4eba\u5728\u6211\u4eec\u4e4b\u524d\uff0c\u6f5c\u5165\u8fd9\u5c0f\u79ef\u5c71\u4e86\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5c5e\u4e0b\u8fd9\u5c31\u53bb\u641c\u67e5\u3002\u201d\u9a6c\u826f\u9053\uff1a\u201c\u4e01\u5144\uff0c\u6211\u4eec\u5206\u5934\u67e5\uff0c\u4f60\u53bb\u540e\u5c71\uff0c\u6211\u53bb\u505c\u821f\u4e4b\u5904\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u5fc5\u5206\u5f00\u884c\u52a8\uff0c\u4f60\u4eec\u8ddf\u6211\u4e00\u9053\u8d70\u3002\u6b64\u4eba\u6b66\u529f\u4e0d\u5f31\uff0c\u805a\u5728\u4e00\u8d77\uff0c\u4e5f\u597d\u6709\u70b9\u7167\u5e94\u3002\u201d \u3000\u3000\u4e01\u3001\u9a6c\u4e8c\u4eba\u5fc3\u4e2d\u660e\u767d\uff0c\u53f6\u957f\u9752\u662f\u6015\u4e8c\u4eba\u5bf9\u4ed8\u4e0d\u4e86\u90a3\u4e2a\u6765\u72af\u4e4b\u4eba\uff0c\u624d\u6545\u610f\u8981\u4ed6\u4eec\u8ddf\u7740\u4ed6\u805a\u5728\u4e00\u8d77\uff0c\u4ee5\u514d\u4e8c\u4eba\u9047\u4e0a\u4e86\u5bf9\u65b9\uff0c\u53d7\u5230\u4f24\u5bb3\u3002 \u3000\u3000\u4e01\u5cf0\u3001\u9a6c\u826f\u5341\u5206\u611f\u6fc0\u5730\u5e94\u58f0\u9053\uff1a\u201c\u5251\u4f7f\u5173\u7231\u5c5e\u4e0b\uff0c\u5c5e\u4e0b\u7b49\u81ea\u5f53\u9075\u547d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fae\u5fae\u4e00\u7b11\uff0c\u5f53\u5148\u8d70\u51fa\u8349\u68da\uff0c \u53e3\u4e2d\u5374\u9053\uff1a\u201c\u6211\u4eec\u5148\u53bb\u770b\u770b\u7389\u5170\u53cc\u59dd\u3002\u201d \u53e3 \u53e3 \u53e3 \u3000\u3000\u7389\u5170\u53cc\u59dd\u79bb\u5f00\u4e86\u8349\u68da\u56de\u5230\u753b\u822b\u4e4b\u540e\uff0c\u7531\u4e8e\u8863\u5c65\u5c3d\u6e7f\uff0c\u4e24\u4eba\u5306\u5306\u7684\u6362\u4e0b\u957f\u886b\uff0c\u53d6\u4e0b\u4e86\u73e0\u9970\uff0c\u62ab\u6563\u4e86\u957f\u53d1\uff0c\u7565\u4e8b\u68b3\u51bc\u4e4b\u540e\uff0c\u672c\u6765\u662f\u6253\u7b97\u7a0d\u4f5c\u4f11\u606f\uff0c\u517b\u517b\u7cbe\u795e\uff0c\u4ee5\u4fbf\u5e94\u4ed8\u5f3a\u654c\u3002 \u3000\u3000\u4e0d\u6599\uff0c\u56db\u540d\u5927\u6c49\u5728\u590f\u6740\u6307\u6325\u4e0b\uff0c\u62ac\u6765\u4e86\u4e24\u540d\u88ab\u4eba\u6697\u7b97\u7684\u9634\u9633\u5821\u5f1f\u5b50\uff0c\u653e\u5728\u524d\u8231\u3002 \u3000\u3000\u9047\u4e0a\u4e86\u8fd9\u7b49\u4e8b\uff0c\u7389\u5170\u53cc\u59dd\u54ea\u91cc\u8fd8\u80fd\u5b89\u5fc3\u4f11\u517b\u3002 \u3000\u3000\u5979\u4eec\u76d8\u95ee\u4e86\u590f\u6740\uff0c\u5374\u95ee\u4e0d\u51fa\u4efb\u4f55\u5934\u7eea\u3002 \u3000\u3000\u590f\u6740\u6839\u672c\u5c31\u4e0d\u660e\u5185\u60c5\u3002 \u3000\u3000\u5170\u767d\u7684\u6027\u5b50\u6bd4\u8f83\u6025\uff0c\u62d6\u7740\u7389\u7fe0\uff0c\u8981\u53bb\u8349\u68da\u627e\u53f6\u957f\u9752\u95ee\u3002 \u3000\u3000\u7389\u7fe0\u4e5f\u89c9\u7740\u6b64\u4e8b\u6709\u4e9b\u8e4a\u8df7\uff0c\u7b11\u4e86\u4e00\u7b11\u9053\uff1a \u201c\u59b9\u59b9\uff0c\u6211\u4eec\u5c31\u8fd9\u4e48\u8d70\u53bb\u5417?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4e0d\u8fd9\u4e48\u8d70\u53bb\uff0c\u600e\u4e48\u53bb?\u59d0\u59d0\uff0c\u4f60\u8fd8\u8981\u4eba\u7528\u56db\u62ac\u5927\u8f7f\u6765\u62ac\u4f60\u5417?\u201d \u3000\u3000\u7389\u7fe0\u4e00\u63a0\u5782\u80a9\u957f\u53d1\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u6211\u4eec\u5f97\u7c2a\u8d77\u5934\u53d1\uff0c\u522b\u8ba9\u53f6\u5144\u77a7\u4e86\u4f60\u7b11\u554a\u3002\u201d \u3000\u3000\u5170\u767d\u95fb\u8a00\u770b\u770b\u7389\u7fe0\uff0c\u4e0d\u7981\u7b11\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u6211\u5fd8\u4e86\u6211\u4eec\u2026\u2026\u201d \u3000\u3000\u4e00\u8f6c\u8eab\uff0c\u94bb\u5165\u4e86\u5185\u8231\u3002 \u3000\u3000\u7389\u7fe0\u77a7\u7740\u5170\u767d\u7684\u80cc\u5f71\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u6b63\u8981\u4e3e\u6b65\u5165\u8231\uff0c\u53ea\u542c\u5f97\u5170\u81ea\u5728\u8231\u5185\u53d1\u51fa\u4e86\u60ca\u53eb\u4e4b\u58f0\u3002 \u3000\u3000\u7389\u7fe0\u8138\u8272\u4e00\u53d8\uff0c\u95ea\u8eab\u8de8\u5165\u8231\u5185\u3002 \u3000\u3000\u987f\u65f6\uff0c\u7389\u7fe0\u4e5f\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u6562\u60c5\uff0c\u5170\u767d\u6b63\u7ad9\u5728\u5e8a\u524d\u53d1\u5446\u3002 \u3000\u3000\u7389\u7fe0\u76b1\u7709\u9053\uff1a\u201c\u600e\u4e48\u5566?\u201d \u3000\u3000\u5170\u767d\u6307\u7740\u5e8a\u4e0a\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u54b1\u4eec\u7684\u8863\u670d\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u5446\u4e86\u4e00\u5446\uff0c\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u4eec\u59d0\u59b9\u8131\u4e0b\u7684\u5916\u886b\uff0c\u539f\u672c\u653e\u5728\u5e8a\u4e0a\uff0c\u8fd9\u65f6\u679c\u7136\u662f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u7389\u7fe0\u4e00\u8f6c\u8eab\uff0c\u518d\u770b\u770b\u90a3\u5c0f\u5c0f\u7684\u5986\u53f0\uff0c\u4e0d\u7981\u8138\u4e0a\u795e\u60c5\u53c8\u662f\u4e00\u53d8\u3002 \u3000\u3000\u5170\u767d\u8fd9\u65f6\u4e5f\u8f6c\u8fc7\u8eab\u6765\uff0c\u76ee\u5149\u5c04\u5411\u5986\u53f0\uff0c\u8131\u53e3\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u54b1\u4eec\u8fd9\u73e0\u7fe0\u3001\u94c1\u73af\u5462\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6709\u4eba\u6765\u8fc7\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u6211\u4eec\u600e\u4f1a\u4e0d\u77e5\u9053\uff1f\u59d0\u59d0\u3001\u6211\u4eec\u5c31\u5728\u524d\u8231\u5440\uff1f\u201d \u3000\u3000\u7389\u7fe0\u76b1\u7709\u9053\uff1b\u201c\u5170\u767d\uff0c\u5982\u679c\u8fd9\u4e2a\u4eba\u80fd\u5728\u8fd9\u4e00\u5239\u90a3\u62ff\u8d70\u4e86\u4e1c\u897f\uff0c\u6211\u4eec\u5374\u6ca1\u53d1\u73b0\uff0c\u8fd9\u4e2a\u4eba\u4e00\u5b9a\u662f\u6c5f\u6e56\u4e0a\u7684\u7edd\u9876\u9ad8\u624b\u3002\u201d \u3000\u3000\u5170\u767d\u4e00\u6014\u9053\uff1b\u201c\u9ad8\u624b\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5982\u679c\u4e0d\u662f\u9ad8\u624b\uff0c\u4ed6\u80fd\u62ff\u5f97\u8d70\u8fd9\u4e9b\u4e1c\u897f\uff1f\u7389\u5170\u53cc\u59dd\u7684\u540d\u53f7\uff0c\u771f\u7684\u90a3\u4e48\u4e0d\u503c\u94b1\u5417\uff1f\u201d \u3000\u3000\u5170\u767d\u6c89\u541f\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u6211\u53ea\u662f\u6709\u4e00\u70b9\u4e0d\u660e\u767d\uff01\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1b\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5018\u82e5\u8fd9\u4e2a\u4eba\u6709\u8fd9\u4e48\u9ad8\u7684\u6b66\u529f\uff0c\u4ed6\u8dd1\u6765\u62ff\u8d70\u6211\u4eec\u7684\u8863\u670d\u9996\u9970\uff0c\u53c8\u4e3a\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7389\u7fe0\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u2026\u2026\u6211\u4e5f\u662f\u60f3\u4e0d\u901a\u3002\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u7b11\u4e86\u7b11\u63a5\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u8fd9\u4e2a\u4eba\u4f1a\u4e0d\u4f1a\u8fd8\u8eb2\u5728\u8239\u4e0a\uff1f\u201d \u3000\u3000\u7389\u7fe0\u88ab\u5170\u767d\u4e00\u8a00\u63d0\u9192\uff0c\u5931\u7b11\u9053\uff1a\u201c\u4f60\u77a7\uff1b\u54b1\u4eec\u53ea\u60f3\u5230\u6765\u4eba\u662f\u4e2a\u9ad8\u624b\uff0c\u5374\u5fd8\u4e86\u5148\u67e5\u67e5\u770b\u4e86\u2026\u2026\u201d \u3000\u3000\u8bdd\u97f3\u4e00\u843d\uff0c\u4e24\u4eba\u5206\u5934\u5728\u8231\u5185\u5bfb\u627e\u3002 \u3000\u3000\u8fd9\u95f4\u8231\u623f\u4e0d\u5927\uff0c\u8981\u8eb2\u4e00\u4e2a\u4eba\uff0e\u4e5f\u53ea\u6709\u5e8a\u4e0b\u9762\uff0c\u53ef\u662f\uff0c\u5e8a\u4e0b\u9762\u5374\u662f\u7a7a\u7a7a\u5982\u4e5f\u3002 \u3000\u3000\u7389\u7fe0\u82e6\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8d70\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u6b63\u9760\u5728\u7a97\u5b50\u4e00\u8fb9\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u7a97\u680f\u4e0a\u8fd8\u7559\u6709\u6c34\u6e0d\uff0c\u59d0\u59d0\uff0c\u8fd9\u4e2a\u4eba\u662f\u7531\u7a97\u5b50\u722c\u8fdb\u6765\u7684\u3002\u201d\u7389\u7fe0\u9053\uff1a\u201c\u7b97\u4e86\uff0c\u6211\u4eec\u8fd8\u662f\u5148\u53bb\u627e\u53f6\u5c11\u4fa0\u5427\uff01\u201d\u5170\u767d\u8d70\u8fc7\u6765\uff0c\u63a5\u9053\uff1a\u201c\u6211\u5f97\u68b3\u68b3\u5934\u2026\u2026\u201d\u5ffd\u7136\u53c8\u662f\u4e00\u6014\uff0c\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u68b3\u5b50\u5462\uff1f\u4f60\u2026\u2026\u653e\u5728\u90a3\u513f\uff1f\u201d \u3000\u3000\u5170\u767d\u4e24\u624b\u4e00\u644a\uff0c\u9053\uff1a\u201c\u53ef\u4e0d?\u201d \u3000\u3000\u7389\u7fe0\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a \u201c\u5947\u602a!\u8fd9\u4e2a\u4eba\u2026\u2026\u600e\u4e48\u4f1a\u8fde\u68b3\u5b50\u4e5f\u62ff\u8d70\u4e86?\u201d \u3000\u3000\u5170\u767d\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u636e\u8bf4\u5f53\u5c0f\u5077\u7684\u6700\u5fcc\u8bb3\u662f\u8eab\u5165\u4eba\u5bb6\uff0c\u7a7a\u624b\u800c\u8fd4\uff0c\u4e5f\u8bb8\uff0c\u8fd9\u4e2a\u5c0f\u5077\u53ea\u597d\u62ff\u628a\u68b3\u5b50\u5145\u6570\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u5931\u7b11\u9053\uff1a\u201c\u9664\u4e86\u68b3\u5b50\uff0c\u8fd8\u6709\u6211\u4eec\u7684\u9996\u9970\u8ddf\u8863\u670d\u5462?\u4ed6\u600e\u4e48\u4f1a\u7a7a\u624b\u800c\u8fd4?\u201d \u3000\u3000\u5170\u767d\u7b11\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u8fd9\u79cd\u4e8b\uff0c\u5927\u6982\u53ea\u6709\u6293\u5230\u90a3\u4e2a\u5c0f\u5077\u624d\u80fd\u95ee\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u4e00\u63a0\u957f\u53d1\u7684\u9b13\u89d2\uff0c\u5401\u4e00\u53e3\u6c14\u9053\uff1a \u201c\u603b\u7b97\u4ed6\u6765\u5f97\u65e9\uff0c\u5982\u679c\u662f\u7b49\u6211\u4eec\u5165\u7761\u4ee5\u540e\u2026\u2026\u5509\uff0c\u540e\u679c\u5c31\u4e0d\u582a\u8bbe\u60f3\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u7b11\u9053\uff1a\u201c\u4f1a\u6740\u4e86\u6211\u4eec?\u201d \u3000\u3000\u7389\u7fe0\u770b\u4e86\u5170\u767d\u4e00\u773c\uff0c\u63a5\u9053\uff1a \u201c\u5170\u767d\uff0c\u59d0\u59d0\u6211\u5012\u65e0\u6240\u8c13\uff0c\u5bf9\u4f60\u2026\u2026\u603b\u662f\u4e0d\u597d\u554a!\u201d \u3000\u3000\u5170\u767d\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u7389\u5170\u53cc\u59dd\uff0c\u5728\u6c5f\u6e56\u4e0a\u58f0\u540d\u4e0d\u597d\uff0c\u77e5\u9053\u6211\u662f\u6e05\u6e05\u767d\u767d\u7684\u4eba\uff0c\u5927\u6982\u53ea\u6709\u59d0\u59d0\u4f60\u4e00\u4e2a\u4eba\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u6447\u5934\u9053\uff1a\u201c\u4e0d!\u8fd8\u6709\u4e00\u4e2a\u4eba\uff01\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u8c01?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u957f\u9752\u3002\u201d \u3000\u3000\u5170\u767d\u5ffd\u7136\u4f4e\u4e0b\u5934\u53bb\uff0c\u4e45\u4e45\u4e0d\u8bed\u3002 \u3000\u3000\u53f6\u957f\u9752\uff0c\u4e0d\u8fc7\u662f\u4e00\u4e2a\u4eba\u7684\u540d\u5b57\u2026\u2026\u4f46\u662f\uff0c\u8fd9\u4e2a\u540d\u5b57\u5bf9\u5170\u767d\u7a76\u7adf\u8fd8\u6709\u7740\u4e00\u4e9b\u522b\u7684\u610f\u5473\u3002 \u3000\u3000\u54ea\u4e2a\u5c11\u5973\u4e0d\u6000\u6625?\u5170\u767d\u6d6a\u8ff9\u6c5f\u6e56\uff0c\u6210\u4e3a\u4eba\u4eba\u95fb\u540d\u4e27\u80c6\u7684\u5973\u6740\u624b\uff0c\u4f46\u5979\u4ecd\u7136\u662f\u4eba\uff0c\u662f\u4e2a\u6e05\u767d\u7eaf\u771f\u7684\u5973\u4eba\u3002 \u3000\u3000\u5979\u770b\u8fc7\u7684\u7537\u4eba\u591a\u5982\u725b\u6bdb\uff0c\u4f46\u662f\uff0c\u80fd\u50cf\u53f6\u957f\u9752\u8fd9\u6837\u88ab\u81ea\u5df1\u51fa\u5356\u4e86\uff0c\u800c\u4f9d\u7136\u80fd\u5bbd\u6055\u5979\u4eec\u7684\u4eba\uff0c\u53ef\u4ee5\u8bf4\u662f\u7edd\u65e0\u4ec5\u6709\u3002 \u3000\u3000\u5170\u767d\u4ece\u8eab\u5165\u592a\u6e56\uff0c\u88ab\u522b\u4eba\u5f53\u4f5c\u4e86\u8bf1\u9975\u6765\u5f15\u53f6\u957f\u9752\u7684\u90a3\u4e00\u77ac\u8d77\uff0c\u5979\u5fc3\u4e2d\u5c31\u5ffd\u7136\u5bf9\u53f6\u957f\u9752\u5347\u8d77\u4e86\u4e00\u4efd\u6d53\u6d53\u7684\u6b49\u610f\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u90a3\u53ea\u662f\u6b49\u610f\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4e0d\u7136\u4e86\u3002 \u3000\u3000\u7389\u7fe0\u7684\u4e00\u53e5\u8bdd\uff0c\u52fe\u9192\u4e86\u5979\u5fc3\u4e2d\u7684\u60c5\u601d\uff0c\u731b\u7136\u95f4\uff0c\u5979\u53d1\u73b0\u53f6\u957f\u9752\u4e09\u4e2a\u5b57\uff0c\u5728\u5979\u5fc3\u4e2d\u6c89\u91cd\u7684\u6709\u5982\u5343\u65a4\u3002\u5170\u767d\u5e7d\u5e7d\u5730\u62cd\u8d77\u4e86\u5934\uff0c\u5927\u5927\u7684\u773c\u775b\u91cc\uff0c\u6eda\u52a8\u7740\u6cea\u73e0\uff0c\u4f4e\u4f4e\u7684\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u59d0\u59d0\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u6402\u7740\u5170\u767d\uff0c\u9ed8\u9ed8\u5730\u6447\u7740\u5170\u767d\u53cc\u80a9\uff0c\u534a\u664c\u65b9\u9053\uff1a\u201c\u8d70\uff0c\u6211\u4eec\u5148\u53bb\u770b\u770b\u53f6\u957f\u9752!\u201d \u3000\u3000\u5170\u767d\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u5374\u6ca1\u8bf4\u3002 \u3000\u3000\u7389\u7fe0\u62c9\u7740\u5979\uff0c\u4e00\u540c\u8d70\u51fa\u8231\u95e8\u3002 \u3000\u3000\u590f\u6740\u8fd8\u5728\u4e3a\u90a3\u4e24\u540d\u9634\u9633\u5821\u7684\u5f1f\u5b50\u63a8\u62ff\uff0c\u770b\u6837\u5b50\uff0c\u5df2\u6bd4\u521a\u624d\u597d\u591a\u4e86\u3002 \u3000\u3000\u7389\u5170\u53cc\u59dd\u8de8\u5230\u8239\u5934\uff0c\u6b63\u8981\u4e0a\u5cb8\uff0c\u53ea\u89c1\u8fdc\u5904\u4eba\u5f71\u98de\u9a70\uff0c\u53f6\u957f\u9752\u5728\u4e01\u5cf0\u3001\u9a6c\u826f\u7684\u62a4\u536b\u4e4b\u4e0b\uff0c\u75be\u5954\u800c\u6765\u3002 \u3000\u3000\u7389\u7fe0\u4e00\u62c9\u5170\u767d\uff0c\u7b11\u9053\uff1a\u201c\u4ed6\u6765\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u53f9\u4e86\u53e3\u6c14\u9053\uff1b\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u7728\u773c\u4e4b\u95f4\uff0c\u53f6\u957f\u9752\u5df2\u8dc3\u4e0a\u4e86\u753b\u822b\u3002 \u3000\u3000\u4ed6\u4e00\u773c\u77a7\u89c1\u7389\u5170\u53cc\u59dd\uff0c\u53ea\u7a7f\u7740\u7d27\u8eab\u8863\u886b\uff0c\u7acb\u4e8e\u8239\u5934\uff0c\u4e0d\u7981\u5fc3\u4e2d\u4e00\u60ca\uff0c\u8131\u53e3\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u600e\u4e48\u4e0d\u5b89\u6b47?\u201d \u3000\u3000\u7389\u7fe0\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1b\u201c\u53f6\u5144\uff0c\u4f60\u60f3\u60f3\uff0c\u6211\u4eec\u80fd\u591f\u7761\u5f97\u7740\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u7565\u4e00\u6253\u91cf\u773c\u524d\u666f\u8c61\uff0c\u7b11\u4e86\u4e00\u7b11\u9053\uff1a \u201c\u655d\u5821\u4e24\u540d\u5f1f\u5b50\uff0c\u88ab\u5165\u70b9\u4e86\u7a74\u9053\uff0c\u90fd\u662f\u5728\u4e0b\u65e0\u80fd\u2026\u2026\u6253\u6270\u59d1\u5a18\u4eec\u4e0d\u80fd\u5b89\u6b47\uff0c\u5728\u4e0b\u6df1\u611f\u6b49\u759a\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u9053\uff1a\u201c\u53f6\u5144\uff0c\u5982\u4eca\u6211\u4eec\u53ef\u662f\u60a3\u96be\u4e0e\u5171\uff0c\u540c\u4ec7\u654c\u5ffe\uff0c\u4f60\u8fd8\u8bf4\u8fd9\u4e9b\u5ba2\u6c14\u8bdd\u4f5c\u751a\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u9053\uff1b\u201c\u672c\u662f\u5728\u4e0b\u987e\u8651\u4e0d\u5468\uff0c\u624d\u4f1a\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u5ffd\u7136\u63a5\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u6211\u4eec\u8fd9\u513f\u4e5f\u51fa\u4e86\u70b9\u5c0f\u4e8b\u5462!\u201d \u3000\u3000\u53f6\u957f\u9752\u4e00\u6014\u9053\uff1a\u201c\u54e6?\u201d \u3000\u3000\u4e01\u5cf0\u3001\u9a6c\u826f\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u4e2d\u5927\u4e3a\u4e0d\u5b89\u3002 \u3000\u3000\u9a6c\u826f\u5fcd\u4e0d\u4f4f\u8131\u53e3\u95ee\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u513f\u2026\u2026\u51fa\u4e86\u4ec0\u4e48\u4e8b\u554a?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4e22\u4e86\u4e1c\u897f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e22\u4e86\u4e1c\u897f?\u600e\u4e48\u4f1a?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u4e0d\u77e5\u9053\u600e\u4e48\u4f1a\u4e22\u7684\u561b?\u201d \u3000\u3000\u53f6\u957f\u9752\u770b\u4e86\u7389\u7fe0\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u7389\u7fe0\uff0c\u662f\u4f60\u4eec\u7684\u4e1c\u897f\u4e22\u4e86?\u201d \u3000\u3000\u7389\u7fe0\u7b11\u9053\uff1a\u201c\u53ef\u4e0d\u662f?\u4f60\u77a7\uff0c\u54b1\u4eec\u59ca\u59b9\u8131\u4e0b\u6765\u7684\u8863\u670d\uff0c\u88ab\u4eba\u5077\u8d70\u4e86\u3002\u201d \u3000\u3000\u8863\u670d\u88ab\u5077\uff0c\u5927\u5927\u51fa\u4e4e\u53f6\u957f\u9752\u610f\u6599\u4e4b\u5916\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8863\u670d?\u53ea\u5077\u4e86\u4f60\u4eec\u7684\u8863\u670d?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u6211\u4eec\u6253\u5934\u4e0a\u677e\u4e0b\u6765\u7684\u9996\u9970\u548c\u68b3\u5934\u7528\u7684\u68b3\u5b50\uff0c\u4e5f\u90fd\u4e22\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u54e6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u9996\u9970\u4e22\u4e86\u2026\u2026\u8fd8\u6709\u9053\u7406\uff0c\u8fd9\u68b3\u5b50\u53c8\u80fd\u503c\u51e0\u4f55?\u201d \u3000\u3000\u5170\u767d\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u5c31\u7b97\u8fde\u9996\u9970\u7b97\u5728\u5185\uff0c\u4e5f\u503c\u4e0d\u4e86\u591a\u5c11\u94b1\u5440\u2026\u2026\u201d \u3000\u3000\u4e01\u5cf0\u63a5\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u7684\u4e1c\u897f\uff0c\u662f\u51e0\u65f6\u5931\u843d\u7684?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u521a\u521a\u624d\u4e22\u7684\u3002\u201d \u3000\u3000\u4e01\u5cf0\u542c\u5f97\u4e00\u6014\u3002 \u3000\u3000\u9a6c\u826f\u8131\u53e3\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u2026\u2026\u521a\u624d\u4e0d\u5728\u8239\u4e0a\u5417?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5728\uff01\u201d \u3000\u3000\u9a6c\u826f\u770b\u4e86\u53f6\u957f\u9752\u4e00\u773c\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u53f6\u957f\u9752\u76b1\u7709\u9053\uff1a \u201c\u7389\u7fe0\uff0c\u4f60\u662f\u8bf4\u2026\u2026\u6709\u4eba\u5728\u4f60\u4eec\u8eab\u8fb9\uff0c\u62ff\u8d70\u4e86\u4f60\u4eec\u7684\u4e1c\u897f\uff0c\u4f60\u4eec\u5c45\u7136\u4e0d\u77e5\u9053?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e0d\u9519!\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u6211\u8ddf\u59d0\u59d0\u6b63\u5728\u8231\u5916\u8ddf\u590f\u6740\u4ed6\u4eec\u8bf4\u8bdd\uff0c\u6709\u4eba\u5077\u5077\u7684\u8fdb\u4e86\u5185\u8231\uff0c\u628a\u4e1c\u897f\u62ff\u8d70\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e0d\u8bed\uff0c\u4f3c\u662f\u6709\u4e9b\u4e0d\u4fe1\u3002 \u3000\u3000\u4e01\u5cf0\u63a5\u9053\uff1a \u201c\u7389\u5170\u53cc\u59dd\uff0c\u540d\u9707\u6c5f\u6e56\uff0c\u6709\u4eba\u80fd\u5728\u4e24\u4f4d\u59d1\u5a18\u4e0d\u53ca\u5341\u6b65\u4e4b\u5904\uff0c\u62ff\u8d70\u4e86\u4e1c\u897f\uff0c\u8fd9\u4ef6\u4e8b\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u62a2\u5148\u7b11\u9053\uff1a \u201c\u6211\u4eec\u5df2\u7ecf\u627e\u5230\u4e86\u6709\u4eba\u6f5c\u5165\u7684\u75d5\u8ff9\uff0c\u81ea\u7136\u4e0d\u4f1a\u9519\u4e86\u3002\u201d \u4e01\u5cf0\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u75d5\u8ff9\u4f55\u5728?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u7a97\u5b50\u4e0a\uff0c\u6c34\u6e0d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5ffd\u7136\u4e00\u76f2\u4e0d\u53d1\uff0c\u75be\u6b65\u8fdb\u5165\u5185\u8231\u3002 \u3000\u3000\u4e01\u5cf0\u3001\u9a6c\u826f\u7d27\u7d27\u8ddf\u5728\u53f6\u957f\u9752\u8eab\u540e\u3002 \u3000\u3000\u7389\u5170\u53cc\u59dd\u548c\u590f\u6740\u4e5f\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u626b\u89c6\u4e86\u8231\u5185\u4e00\u773c\uff0c\u79fb\u6b65\u8d70\u5230\u7a97\u524d\uff0c\u53d1\u73b0\u7a97\u4e0a\u679c\u7136\u8fd8\u7559\u6709\u6c34\u6e0d\uff0c\u4e0d\u7531\u5f97\u76b1\u7709\u9053\uff1a \u3000\u3000\u201c\u662f\u8fd9\u6247\u7a97\u5b50\u5417?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u63a2\u5934\u7a97\u5916\uff0c\u770b\u770b\u6697\u9ed1\u7684\u6e56\u6c34\uff0c\u957f\u53f9\u9053\uff1a \u201c\u6e56\u6c34\u832b\u832b\uff0c\u54ea\u513f\u6709\u8e2a\u8ff9\u53ef\u627e?\u201d \u3000\u3000\u5170\u767d\u63a5\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u5982\u662f\u6709\u4ec0\u4e48\u8e2a\u8ff9\u53ef\u5faa\uff0c\u6211\u4eec\u4e5f\u4e0d\u4f1a\u7b49\u5728\u8fd9\u513f\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7f13\u7f13\u8d70\u56de\u8231\u5185\uff0c\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u7acb\u5373\u4f20\u4ee4\u4e0b\u53bb\uff0c\u52a0\u5f3a\u6212\u5907\u3002\u201d \u3000\u3000\u4e01\u5cf0\u5e94\u58f0\u9053\uff1a\u201c\u5c5e\u4e0b\u9075\u547d\u3002\u201d \u3000\u3000\u8f6c\u8eab\u76f4\u5411\u8231\u5916\u884c\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u770b\u4e86\u9a6c\u826f\u4e00\u773c\uff0c\u9053\uff1a\u201c\u9a6c\u826f\uff0c\u80fd\u4e0d\u80fd\u6d3e\u4e24\u4e2a\u5144\u5f1f\uff0c\u4e0b\u6c34\u53bb\u67e5\u67e5?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u5251\u4f7f\u7684\u610f\u601d\uff0c\u53ea\u6709\u753b\u822b\u9644\u8fd1\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u9519?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u5c5e\u4e0b\u6253\u7b97\u4eb2\u81ea\u7387\u4eba\u4e0b\u6c34\u67e5\u770b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f88\u597d!\u201d \u3000\u3000\u9a6c\u826f\u53cc\u624b\u62b1\u62f3\u4e00\u793c\uff0c\u4e3e\u6b65\u8d70\u5230\u590f\u6740\u8eab\u8fb9\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u590f\u5144\uff0c\u5c0f\u5fc3\u4fdd\u62a4\u5251\u4f7f\u3002\u201d \u590f\u6740\u9053\uff1a\u201c\u9a6c\u5144\u653e\u5fc3!\u201d \u3000\u3000\u9a6c\u826f\u8eab\u5f62\u4e00\u95ea\uff0c\u51fa\u8231\u800c\u53bb\u3002 \u3000\u3000\u53e3 \u53e3 \u53e3 \u3000\u3000\u753b\u822b\u7684\u8239\u8eab\u4e4b\u4e0b\uff0c\u9a6c\u826f\u7387\u9886\u4e86\u5f20\u9675\u3001\u9ec4\u5b89\uff0c\u6b63\u5728\u6c34\u4e2d\u67e5\u770b\u3002 \u3000\u3000\u9a6c\u826f\u77e5\u9053\u53f6\u957f\u9752\u7684\u610f\u601d\uff0c\u5e76\u4e0d\u662f\u8981\u4ed6\u4eec\u4e0b\u6c34\u8ffd\u8e2a\u76d7\u7269\u4e4b\u4eba\u3002 \u3000\u3000\u8981\u5728\u6c34\u91cc\u9762\u53bb\u627e\u4ec0\u4e48\u75d5\u8ff9\uff0c\u90a3\u662f\u51b3\u4e0d\u53ef\u80fd\u3002 \u3000\u3000\u53f6\u957f\u9752\u5c82\u662f\u90a3\u7b49\u7b28\u4eba? \u3000\u3000\u56e0\u6b64\uff0c\u9a6c\u826f\u548c\u5f20\u3001\u9ec4\u4e8c\u4eba\uff0c\u5728\u6c34\u4e0b\u9762\u8981\u67e5\u7684\uff0c\u662f\u770b\u770b\u6709\u6ca1\u6709\u5728\u8231\u5e95\u65bd\u5c55\u4ec0\u4e48\u624b\u811a\u3002 \u3000\u3000\u8239\u8eab\u6c34\u7ebf\u4ee5\u4e0b\uff0c\u957f\u6ee1\u4e86\u7eff\u82d4\uff0c\u722c\u6ee1\u4e86\u87ba\u4e1d\u3002 \u3000\u3000\u4ed6\u4eec\u51e0\u4e4e\u662f\u4e00\u5bf8\u4e00\u5bf8\u7684\u5728\u627e\uff0c\u4ece\u5934\u5230\u5c3e\uff0c\u6765\u6765\u56de\u56de\u7684\u67e5\u770b\u4e86\u4e09\u904d\u3002 \u3000\u3000\u8fd8\u597d\uff0c\u6ca1\u6709\u4e1d\u6beb\u7834\u574f\u7684\u8ff9\u8c61\u3002 \u3000\u3000\u9a6c\u826f\u62db\u547c\u4e86\u5f20\u3001\u9ec4\u4e8c\u4eba\uff0c\u5347\u51fa\u6c34\u9762\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e00\u89c1\u9a6c\u826f\u51fa\u6c34\uff0c\u7acb\u5373\u95ee\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u53ef\u7591\u4e4b\u5904?\u201d \u3000\u3000\u9a6c\u826f\u817e\u8eab\u4e00\u8dc3\uff0c\u4e0a\u4e86\u753b\u822b\uff0c\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u5170\u767d\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u5176\u5b9e\uff0c\u4f60\u5927\u53ef\u4e0d\u5fc5\u8981\u4ed6\u4eec\u4e0b\u6c34\u53bb\u770b\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\u5982\u679c\u771f\u60f3\u5f04\u6c89\u753b\u822b\uff0c\u53ea\u6015\u4e0d\u7b49\u6211\u4eec\u7531\u5cb8\u4e0a\u56de\u6765\u4ee5\u524d\uff0c\u4ed6\u5c31\u51ff\u6c89\u4e86\u8fd9\u6761\u8239\u4e86\u5440\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4e0d\u7531\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u5170\u767d\uff0c\u4f60\u5f88\u806a\u660e\u3002\u201d \u3000\u3000\u5170\u767d\u88ab\u4ed6\u79f0\u8d5e\u4e86\u4e00\u53e5\uff0c\u4e0d\u7981\u5fc3\u4e2d\u6cdb\u8d77\u4e00\u80a1\u751c\u751c\u7684\u7f9e\u610f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u591a\u8c22\u53f6\u5c11\u4fa0\u5938\u5956\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u5ae3\u7136\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u53f6\u5144\uff0c\u773c\u524d\u8fd9\u4ef6\u4e8b\uff0c\u6211\u770b\u4f60\u4e5f\u7528\u4e0d\u7740\u518d\u70e6\u5fc3\u4e86\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u6447\u5934\u9053\uff1a \u201c\u7389\u7fe0\uff0c\u4f60\u4eec\u4e22\u4e86\u4e1c\u897f\uff0c \u9634\u9633\u5821\u53c8\u4f24\u4e86\u4eba\u2026\u2026\u800c\u4e14\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u53f9\u4e86\u4e00\u53e3\u6c14\u63a5\u9053\uff1a\u201c\u4e0d\u7792\u4f60\u4eec\u8bf4\uff0c\u90a3\u8349\u68da\u4e4b\u5185\u8ddf\u8fd9\u513f\u4e00\u6837\uff0c\u4e5f\u4e22\u4e86\u4e00\u70b9\u4e0d\u503c\u5f97\u4e00\u63d0\u7684\u4e1c\u897f\uff0c\u8fd9\u4ef6\u4e8b\u8981\u662f\u4e0d\u67e5\u660e\u767d\uff0c\u5728\u4e0b\u600e\u80fd\u4e0d\u5fc3\u70e6?\u201d \u3000\u3000\u7389\u7fe0\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u8349\u68da\u91cc\u4e5f\u4e22\u4e86\u4e1c\u897f\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6240\u6709\u7684\u98df\u7269\uff0c\u5168\u90fd\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u54e6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5403\u7684\u4e1c\u897f\uff0c\u5168\u90fd\u4e22\u4e86\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u82e6\u7b11\u9053\uff1a\u201c\u53ef\u4e0d\uff0c\u8fde\u4e00\u5757\u997c\u90fd\u6ca1\u6709\u7559\u4e0b\u3002\u201d \u3000\u3000\u5170\u767d\u5931\u58f0\u7b11\u9053\uff1a\u201c\u6211\u770b\uff0c\u6211\u4eec\u9047\u5230\u91ce\u4eba\u4e86\u3002\u201d \u3000\u3000\u9a6c\u826f\u63a5\u9053\uff1a\u201c\u5c0f\u79ef\u5c71\u4e0a\u4e0a\u4e0b\u4e0b\uff0c\u655d\u5821\u5df2\u7136\u6d3e\u4eba\u67e5\u660e\uff0c\u4ece\u6765\u65e0\u4eba\u5c45\u4f4f\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u6b63\u56e0\u4e3a\u6ca1\u6709\u4eba\u5c45\u4f4f\uff0c\u624d\u4f1a\u662f\u91ce\u4eba\u561b!\u4e0d\u7136\uff0c\u53c8\u600e\u4e48\u4f1a\u53eb\u91ce\u4eba\u5462?\u201d \u3000\u3000\u5979\u8bf4\u7684\u632f\u632f\u6709\u8bcd\uff0c\u9a6c\u826f\u5012\u4e00\u65f6\u4e3a\u4e4b\u8bed\u585e\u3002 \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u6211\u770b\u2026\u2026\u4e0d\u8bba\u662f\u91ce\u4eba\u4e5f\u597d\uff0c\u4e0d\u662f\u91ce\u4eba\u4e5f\u597d\uff0c\u8fd9\u5c0f\u79ef\u5c71\u4e0a\u53e6\u5916\u6709\u4e00\u4e2a\u4eba\uff0c\u5927\u6982\u662f\u4e0d\u4f1a\u9519\u7684\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u80af\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a \u201c\u56e0\u6b64\uff0c\u4e3a\u4eca\u4e4b\u8ba1\uff0c\u54b1\u4eec\u4f3c\u4e4e\u662f\u5148\u53ef\u4ee5\u786e\u5b9a\u7684\u662f\uff0c\u627e\u51fa\u8fd9\u4e2a\u4eba\uff0c\u662f\u6700\u91cd\u8981\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u9053\uff1a\u201c\u90a3\u8fd8\u7528\u8bf4\u5417?\u201d\u8bed\u97f3\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u95ee\u9898\u662f\uff0c\u54b1\u4eec\u600e\u4e48\u627e?\u90a3\u513f\u53bb\u627e?\u201d \u3000\u3000\u7389\u7fe0\u7b11\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6211\u89c9\u5f97\u2026\u2026\u8fd9\u4e2a\u4eba\u4ed6\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u600e\u4e48\u6837?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f3c\u4e4e\u5bf9\u6211\u4eec\u5e76\u6ca1\u6709\u592a\u5927\u7684\u654c\u610f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u6b64\u4eba\u5077\u8d70\u4e86\u4e24\u4f4d\u7684\u8863\u9970\uff0c\u53c8\u4f24\u4e86\u9634\u9633\u5821\u7684\u4e24\u540d\u5f1f\u5b50\uff0c\u600e\u4f1a\u6ca1\u6709\u654c\u610f?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5982\u679c\u6b64\u4eba\u771f\u6709\u654c\u610f\uff0c\u9a6c\u5144\uff0c\u9634\u9633\u5821\u7684\u4e24\u540d\u5f1f\u5b50\u53ea\u6015\u4e0d\u6b62\u4e8e\u7a74\u9053\u88ab\u5236\u5427!\u8fd8\u6709\uff0c\u4ed6\u6765\u5077\u53d6\u8863\u9970\u4e4b\u65f6\uff0c\u6211\u548c\u5170\u767d\u3001\u590f\u6740\u53ca\u53d7\u5236\u7a74\u9053\u7684\u4eba\u90fd\u5728\u8231\u9762\u4e4b\u4e0a\uff0c\u4ed6\u8981\u6697\u4e2d\u60f3\u7f6e\u6211\u7b49\u4e8e\u6b7b\u5730\uff0c\u8001\u5b9e\u8bf4\uff0c\u5b9e\u662f\u6613\u5982\u53cd\u638c\u554a!\u201d \u3000\u3000\u9a6c\u826f\u6014\u4e86\u6014\u9053\uff1a\u201c\u59d1\u5a18\u5206\u6790\u7684\u6709\u7406\uff0c\u5728\u4e0b\u2026\u2026\u89c1\u4e0d\u53ca\u6b64!\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7389\u7fe0\uff0c\u5018\u82e5\u6b64\u4eba\u4e0d\u662f\u72c2\u9f99\u3001\u98de\u9e70\u624b\u4e0b\uff0c\u4ed6\u4f3c\u662f\u4e0d\u5fc5\u4f24\u6211\u9634\u9633\u5821\u95e8\u4e0b\u5144\u5f1f\u554a!\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4ed6\u8981\u76d7\u53d6\u98df\u7269\uff0c\u4e0d\u4f24\u4eba\uff0c\u53ea\u6015\u8fdb\u4e0d\u53bb\u8349\u68da\u5427\uff1f\u201d\u5979\u7565\u4e00\u987f\u4e86\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e01\u5cf0\u3001\u9a6c\u826f\u7684\u5b89\u6392\uff0c\u4e00\u5b9a\u5341\u5206\u7cbe\u5bc6\uff0c\u4efb\u4f55\u4eba\u60f3\u63a5\u8fd1\u8349\u68da\uff0c\u5fc5\u5728\u8d35\u5821\u5c5e\u4e0b\u76d1\u89c6\u4e4b\u5185\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u770b\u4e86\u770b\u9a6c\u826f\u3002 \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u59d1\u5a18\u8bf4\u7684\u4e0d\u9519\uff0c\u4efb\u4f55\u4eba\u8981\u8fdb\u8349\u68da\uff0c\u5fc5\u987b\u901a\u8fc7\u4e24\u4e2a\u672c\u5821\u5f1f\u5b50\u7684\u76d1\u89c6\u3002\u201d \u3000\u3000\u9a6c\u826f\u7565\u4e00\u505c\u987f\uff0c\u53c8\u63a5\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u6b64\u4eba\u80fd\u4e00\u4e3e\u70b9\u4e0b\u4e24\u4eba\u7a74\u9053\uff0c\u800c\u907f\u8fc7\u53e6\u5916\u4e24\u540d\u5f1f\u5b50\u7684\u76d1\u89c6\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u53ea\u6015\u2026\u2026\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u4ed6\u8fdf\u7591\u4e86\u4e00\u4e0b\uff0c\u6ca1\u6709\u5f80\u4e0b\u518d\u8bf4\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u53ea\u6015\u600e\u4e48\u6837?\u201d \u3000\u3000\u9a6c\u826f\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a \u201c\u5251\u4f7f\u8bf7\u6055\u5c5e\u4e0b\u653e\u8086\uff0c\u5c5e\u4e0b\u7684\u770b\u6cd5\uff0c\u8fd9\u4eba\u6b66\u529f\u4e0d\u5728\u672c\u5821\u4e94\u5927\u5251\u4f7f\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u4f60\u4e0d\u8bf4\uff0c\u6211\u4e5f\u77e5\u9053\uff0c\u6b64\u4eba\u70b9\u7a74\u624b\u6cd5\u529f\u529b\u4e4b\u5f3a\uff0c\u8fde\u672c\u5ea7\u4e5f\u81ea\u53f9\u5f17\u5982\u2026\u2026\u201d \u3000\u3000\u4ed6\u5401\u4e01\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u5c0f\u79ef\u5c71\u5ffd\u7136\u51fa\u4e86\u8fd9\u6837\u4e00\u4e2a\u9ad8\u624b\uff0c\u771f\u662f\u51fa\u4eba\u610f\u6599\u4e4b\u5916\u3002\u201d \u3000\u3000\u5170\u767d\u7b11\u9053\uff1a \u201c\u4ed6\u62ff\u8d70\u4e86\u6211\u4eec\u7684\u4e1c\u897f\uff0c\u6211\u4eec\u53ef\u8981\u627e\u4ed6\u62ff\u56de\u6765 \u2026\u2026\u53f6\u5927\u4fa0\uff0c\u4f60\u8bf4\u4e86\u534a\u5929\uff0c\u6253\u4e0d\u6253\u7b97\u53bb\u4e2a\u91ce\u4eba\u627e\u51fa\u6765\u5440?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f53\u7136\u8981\u627e!\u201d \u3000\u3000\u5170\u767d\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u8d70\u554a\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u2026\u2026\u4e14\u7b49\u4e01\u5cf0\u56de\u6765\u518d\u8bf4\u5427!\u201d \u3000\u3000\u590f\u6740\u63a5\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u5c5e\u4e0b\u53bb\u627e\u4e01\u5cf0\u2026\u2026\u201d \u3000\u3000\u4e00\u62b1\u62f3\uff0c\u8dc3\u4e0b\u8239\u53bb\u3002 \u3000\u3000\u7389\u7fe0\u5ffd\u7136\u54af\u54af\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u5144\uff0c\u8d81\u7740\u4e01\u5cf0\u672a\u6765\u4e4b\u524d\uff0c\u4f60\u6700\u597d\u60f3\u4e00\u60f3\u600e\u4e48\u4e2a\u627e\u6cd5?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u59d1\u5a18\u7684\u9ad8\u89c1\u5462?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u59be\u8eab\u6709\u4ec0\u4e48\u9ad8\u89c1\u2026\u2026\u4e0d\u8fc7\uff0c\u6211\u8ba4\u4e3a\u65e2\u7136\u6b64\u4eba\u6b66\u529f\u6781\u9ad8\uff0c\u4e5f\u8bb8\u4ed6\u85cf\u8eab\u4e4b\u5904\uff0c\u4e5f\u662f\u522b\u4eba\u8ba4\u5b9a\u4e0d\u53ef\u80fd\u5c45\u4f4f\u4e4b\u5904\u2026\u2026\u3002 \u3000\u3000\u9a6c\u826f\u8131\u53e3\u63a5\u9053\uff1a\u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u4f60\u4e0d\u4f1a\u8ba4\u4e3a\u8fd9\u4eba\u80fd\u4f4f\u5728\u90a3\u76f4\u7acb\u7684\u60ac\u5d16\u4e4b\u4e0b\u5427?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd?\u4ed6\u4e0d\u662f\u6b66\u529f\u9ad8\u7edd\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u53ef\u80fd!\u5bf9!\u4e5f\u8bb8\u90a3\u60ac\u5d16\u4e4b\u4e0b\uff0c\u5c31\u662f\u6b64\u4eba\u9690\u8eab\u4e4b\u5730!\u201d \u3000\u3000\u56de\u987e\u9a6c\u826f\u4e00\u773c\uff0c\u63a5\u9053\uff1a \u201c\u9a6c\u826f\uff0c\u90a3\u5730\u65b9\u7531\u4e0a\u9762\u771f\u7684\u4e0b\u4e0d\u53bb\u5417?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u5ced\u58c1\u76f4\u7acb\uff0c\u733f\u7334\u4ea6\u65e0\u6cd5\u6500\u767b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u54e6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5982\u6709\u7ef3\u7d22\u5462?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u501f\u52a9\u7ef3\u7d22\u81ea\u7136\u53ef\u4ee5\u2026\u2026\u4e0d\u8fc7\uff0c\u5251\u4f7f\uff0c\u6211\u4eec\u67e5\u770b\u4e4b\u65f6\uff0c\u5e76\u672a\u89c1\u5230\u8fc7\u4efb\u4f55\u6500\u63f4\u7684\u75d5\u8ff9\u4ee5\u53ca\u4efb\u4f55\u53ef\u8d44\u56fa\u5b9a\u7ef3\u7d22\u4e4b\u7269\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6?\u8fd9\u662f\u8bf4\u6ca1\u6709\u4eba\u4ece\u4e0a\u9762\u4e0b\u53bb\u8fc7\u4e86?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u7389\u7fe0\u63a5\u9053\uff1a\u201c\u9a6c\u5144\uff0c\u63a5\u8fd1\u6c34\u9762\u4e4b\u5904\uff0c\u662f\u5426\u4e5f\u66fe\u4ed4\u7ec6\u67e5\u8fc7?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u67e5\u8fc7\u3002\u201d \u3000\u3000\u5170\u767d\u7b11\u9053\uff1a\u201c\u9a6c\u5144\uff0c\u6c34\u4e0b\u9762\u5462?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u6c34\u4e0b\u4e5f\u67e5\u8fc7\uff0c\u6ca1\u6709\u4ec0\u4e48\u53ef\u7591\u4e4b\u5904\u3002\u4e00\u5170\u767d\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u770b\u4e86\u9a6c\u826f\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u6f0f\u4e86\u4ec0\u4e48\u6240\u5728?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u5c5e\u4e0b\u5949\u547d\u884c\u4e8b\uff0c\u5411\u6765\u5341\u5206\u5c0f\u5fc3\uff0c\u9664\u975e\u4e0d\u662f\u4eba\u529b\u6240\u53ca\u4e4b\u5730\uff0c\u5426\u5219\uff0c\u5c0f\u79ef\u5c71\u6bcf\u4e00\u5bf8\u571f\u5730\uff0c\u5c5e\u4e0b\u7b49\u90fd\u5df2\u67e5\u660e\u3002\u201d \u3000\u3000\u7389\u7fe0\u5ffd\u7136\u5411\u9a6c\u826f\u7b11\u9053\uff1b\u201c\u9a6c\u5144\uff0c\u4f60\u4eec\u5bdf\u770b\u60ac\u5d16\u4e4b\u65f6\uff0c\u6709\u6ca1\u6709\u6d3e\u4eba\u5815\u4e0b\u53bb\u8fc7?\u201d \u3000\u3000\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u7247\u60ac\u5d16\uff0c\u7531\u6e56\u9762\u4e0a\u53ef\u4ee5\u770b\u5f97\u660e\u767d\u6e05\u695a\u2026\u2026\u662f\u4ee5\u7528\u4e0d\u7740\u6d3e\u4eba\u5815\u4e0b\u53bb\u770b\u3002\u201d \u3000\u3000\u5170\u767d\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u552f\u4e00\u53ef\u7591\u4e4b\u5904\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u80fd\u591f\u98de\u62d4\u7edd\u58c1\u4e4b\u4eba\uff0c\u4e16\u95f4\u5e76\u975e\u6ca1\u6709\uff0c\u6240\u4ee5\uff0c\u8fd9\u7247\u60ac\u5ca9\u786e\u5e94\u91cd\u65b0\u67e5\u770b\u2026\u2026\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e01\u5cf0\u3001\u590f\u6740\u75be\u884c\u56de\u5230\u8239\u4e0a\u3002 \u3000\u3000\u4e01\u5cf0\u62b1\u62f3\u9053\uff1a\u201c\u5251\u4f7f\u5524\u6211?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u5df2\u5b89\u6392\u597d\u4e86?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u4eba\u4eba\u90fd\u5df2\u5949\u547d\u8b66\u6212\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6ca1\u6709\u522b\u7684\u4e8b\u6545\u5417?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u6ca1\u6709\u2026\u2026\u201d \u3000\u3000\u4ed6\u7565\u4e00\u8fdf\u7591\uff0c\u53c8\u9053\uff1a \u201c\u53ea\u662f\uff0c\u5bf9\u65b9\u7684\u8239\u53ea\u867d\u7136\u7184\u53bb\u4e86\u706f\u706b\uff0c\u4f46\u672a\u79bb\u53bb\u3002\u201d \u53f6\u957f\u9752\u6c89\u541f\u9053\uff1a\u201c\u770b\u6765\uff0c\u4ed6\u4f3c\u662f\u8981\u7b49\u5230\u5929\u660e\u4ee5\u540e\uff0c\u518d\u884c\u653b\u51fb\u4e86\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5c5e\u4e0b\u5821\u4e2d\u5f1f\u5b50\u56db\u4eba\uff0c\u780d\u4f10\u672c\u5c71\u8f83\u7c97\u7684\u6811\u6728\uff0c\u5728\u90a3\u7247\u6ee9\u5934\u7684\u6d45\u6c34\u4e2d\u6253\u4e0b\u4e86\u4e0d\u5c11\u6728\u6869\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u9053\uff1a\u201c\u5f88\u597d\u2026\u2026\u81f3\u5c11\u5bf9\u65b9\u7684\u5c0f\u821f\uff0c\u4e0d\u80fd\u76f4\u63a5\u62d6\u5230\u5cb8\u4e0a\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u8d70!\u54b1\u4eec\u5230\u5c71\u9876\u4e0a\u770b\u770b!\u201d \u3000\u3000\u4e01\u5cf0\u4e00\u6014\u9053\uff1a\u201c\u5251\u4f7f\u8981\u53bb\u5c71\u9876\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f24\u4eba\u5077\u7269\u4e4b\u4eba\uff0c\u53ef\u80fd\u9690\u8eab\u5728\u60ac\u5d16\u4e4b\u4e2d\u7684\u67d0\u4e00\u5904\u6d1e\u7a74\uff0c\u54b1\u4eec\u5fc5\u5f97\u4e0b\u5d16\u67e5\u770b\u3002\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u5251\u4f7f\u6709\u610f\u501f\u7528\u7ef3\u7d22\u6500\u4e0b\u60ac\u5d16\u2026\u2026\u201d \u3000\u3000\u4e01\u5cf0\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u5170\u767d\u5374\u7b11\u9053\uff1a\u201d\u4e01\u5144\uff0c\u6211\u4eec\u8981\u5e26\u51e0\u6839\u7ef3\u5b50\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5728\u4e0b\u53bb\u627e\u2026\u2026\u5251\u4f7f\uff0c\u5c5e\u4e0b\u53d6\u5230\u7ef3\u7d22\u4e4b\u540e\uff0c\u5c31\u5230\u5c71\u9876\u7b49\u4faf\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u4e86\u70b9\u5934\uff0c\u4e01\u5cf0\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u9a6c\u826f\u5411\u590f\u6740\u4f4e\u58f0\u4ea4\u4ee3\u4e86\u4e00\u756a\uff0c\u8981\u4ed6\u5b88\u5728\u753b\u822b\u4e4b\u4e0a\uff0c\u6ce8\u610f\u5bf9\u65b9\u7684\u52a8\u9759\u3002 \u3000\u3000\u7389\u7fe0\u3001\u5170\u767d\u5165\u8231\u53d6\u4e86\u5175\u5203\uff0c\u8fd9\u624d\u968f\u7740\u53f6\u957f\u9752\u4e0b\u8239\u3002 \u3000\u3000\u9a6c\u826f\u4e3e\u624b\u7184\u53bb\u4e86\u753b\u822b\u8239\u5934\u7684\u4e00\u76cf\u5c0f\u706f\uff0c\u65b9\u59cb\u8dc3\u4e0a\u5cb8\u53bb\u3002 \u3000\u3000\u77a7\u4ed6\u884c\u4e8b\u7684\u7ec6\u5fc3\uff0c\u9887\u6709\u72ec\u5f53\u4e00\u9762\u7684\u6c14\u6982\u3002 \u3000\u3000\u53e3 \u53e3 \u53e3 \u3000\u3000\u5c0f\u79ef\u5c71\u7684\u5c71\u9876\u4e4b\u4e0a\u3002 \u3000\u3000\u9634\u9633\u5821\u7684\u5f1f\u5b50\u4eec\u66fe\u94f2\u51fa\u4e86\u4e08\u8bb8\u5e73\u5730\uff0c\u662f\u4ee5\uff0c\u51e0\u4e2a\u4eba\u6500\u4e0a\u6765\u4e4b\u540e\uff0c\u90fd\u89c9\u5f97\u89c6\u754c\u4e00\u5bbd\u3002 \u3000\u3000\u4e01\u5cf0\u51c6\u5907\u4e86\u56db\u6839\u7c97\u5927\u7684\u7ef3\u7d22\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9a6c\u826f\uff0c\u4f60\u628a\u7ef3\u7d22\u633d\u5728\u4e2a\u77f3\u7b0b\u4e0a\uff0c\u6211\u4eb2\u81ea\u4e0b\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u9a6c\u826f\u4e00\u6014\u9053\uff1a\u201c\u8fd9\u2026\u2026\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u8fd9\u7b49\u4e8b\u5e94\u7531\u5c5e\u4e0b\u7b49\u4e0b\u53bb\u67e5\u770b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u5982\u679c\u90a3\u4f4d\u76d7\u7269\u4f24\u4eba\u7684\u4eba\uff0c\u771f\u7684\u9690\u8eab\u60ac\u5ca9\u4e4b\u4e2d\uff0c\u4f60\u4eec\u4e0b\u53bb\uff0c\u5c82\u662f\u4ed6\u7684\u5bf9\u624b? \u3000\u3000\u5feb\u5c06\u7ef3\u7d22\u56fa\u5b9a\u2026\u2026\u201d \u3000\u3000\u4e01\u3001\u9a6c\u4e24\u4eba\u4e92\u770b\u4e00\u773c\uff0c\u62c9\u8d77\u7ef3\u7d22\uff0c\u8d70\u5411\u77f3\u7b0b\u3002 \u3000\u3000\u5170\u767d\u7b11\u9053\uff1a\u201c\u4e01\u5144\uff0c\u518d\u633d\u4e00\u6839\uff0c\u6211\u4e0b\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u59d1\u5a18\u2026\u2026\u5f88\u5371\u9669\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u6211\u624d\u4e0d\u6015\u5462!\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u9053\uff1a\u201c\u5170\u59d1\u5a18\uff0c\u4f60\u4eec\u4e0d\u5fc5\u5192\u9669\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6211\u4e5f\u6253\u7b97\u4e0b\u53bb\u5462!\u53cd\u6b63\uff0c\u6211\u8ddf\u5170\u767d\u53ea\u6709\u4e94\u5929\u597d\u6d3b\u561b\uff0c\u6709\u4ec0\u4e48\u5371\u9669\uff0c\u4e5f\u4e0d\u8fc7\u662f\u65e9\u6b7b\u4e94\u5929\uff0c\u6015\u4ec0\u4e48?\u201d \u3000\u3000\u53f6\u957f\u9752\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u4e24\u4f4d\u59d1\u5a18\u2026\u2026\u4ed6\u4eec\u600e\u4f1a\u53ea\u6709\u4e94\u5929\u597d\u6d3b\u5462?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u4ed6\u4eec\u4e2d\u4e86\u5bf9\u65b9\u7684\u6bd2\u624b\uff0c\u4e94\u5929\u540e\u5f97\u4e0d\u5230\u89e3\u836f\uff0c\u5c31\u8981\u6bd2\u53d1\u8eab\u6b7b\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u54e6\u2026\u2026\u201d \u3000\u3000\u4ed6\u8138\u8272\u6c89\u91cd\u7684\u62c9\u8d77\u4e24\u6839\u7ef3\u7d22\uff0c\u5206\u522b\u633d\u5728\u4e24\u4e2a\u77f3\u7b0b\u4e4b\u4e0a\uff0c\u6253\u597d\u4e86\u7ed3\u3002 \u3000\u3000\u9a6c\u826f\u5df2\u628a\u53e6\u4e00\u6839\u6346\u7a33\u3002 \u3000\u3000\u53f6\u957f\u9752\u62fe\u8d77\u7559\u5728\u5730\u4e0a\u53e6\u4e00\u6839\u7ef3\u7d22\uff0c\u7ed5\u6210\u4e86\u5377\uff0c\u80cc\u5728\u81ea\u5df1\u80cc\u4e0a\u3002\u7b11\u9053\uff1a\u201c\u5982\u679c\u7ef3\u5b50\u4e0d\u591f\u5782\u5230\u6c34\u9762\uff0c\u6211\u5c31\u7528\u8fd9\u4e00\u6839\u63a5\u4e0a\u3002\u201d \u3000\u3000\u7389\u5170\u53cc\u59dd\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u7389\u7fe0\u7b11\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6211\u4eec\u600e\u4e48\u529e?\u7ef3\u5b50\u8981\u662f\u4e0d\u591f\u957f\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e24\u4f4d\u5230\u4e86\u9014\u4e2d\uff0c\u5df2\u7ecf\u5f88\u591f\u4e86\uff0c\u5176\u5b9e\uff0c\u5982\u679c\u5728\u4e0b\u6599\u60f3\u4e0d\u9519\uff0c\u5bf9\u65b9\u9690\u8eab\u4e4b\u5904\uff0c\u5e94\u8be5\u4e0d\u4f1a\u592a\u63a5\u8fd1\u6c34\u9762\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u592a\u8fc7\u63a5\u8fd1\u6c34\u9762\uff0c\u672c\u5821\u5f1f\u5b50\uff0c\u5c82\u4f1a\u53d1\u73b0\u4e0d\u4e86?\u201d \u3000\u3000\u5170\u767d\u7b11\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\u2026\u2026\u59d0\u59d0\uff0c\u54b1\u4eec\u7b28\u9e1f\u5148\u98de\uff0c\u62a2\u5728\u53f6\u5c11\u4fa0\u524d\u9762\u4e00\u6b65\u5427!\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1b\u201c\u597d\u54c7!\u201d \u3000\u3000\u4e24\u4eba\u5404\u81ea\u6293\u8d77\u4e00\u6839\u7ef3\u7d22\uff0c\u6cbf\u7740\u60ac\u5ca9\uff0c\u5411\u4e0b\u6500\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fae\u5fbd\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5ca9\u77f3\u5f88\u6ed1\uff0c\u5c0f\u5fc3\u4e9b\uff01\u201d \u3000\u3000\u7389\u5170\u53cc\u59dd\uff0c\u4eba\u5df2\u6ca1\u4e86\u5f71\u5b50\u3002 \u3000\u3000\u6562\u60c5\uff0c\u5979\u4eec\u90fd\u662f\u540c\u6837\u7684\u5fc3\u601d\uff0c\u5e0c\u671b\u5728\u53f6\u957f\u9752\u4e4b\u524d\u627e\u5230\u90a3\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u6837\u7684\u8bdd\uff0c\u53cc\u65b9\u52a8\u4e0a\u4e86\u624b\uff0c\u5982\u679c\u5bf9\u65b9\u6709\u4ec0\u4e48\u715e\u624b\uff0c\u7b49\u53f6\u957f\u9752\u518d\u4e0b\u6765\u65f6\u4e5f\u5df3\u770b\u51fa\u4e86\u540d\u5802\uff0c\u53ef\u4ee5\u9884\u4e3a\u9632\u5907\u4e86\u3002 \u3000\u3000\u53f6\u957f\u9752\u53c8\u4f55\u5c1d\u4e0d\u662f\u8fd9\u4e48\u60f3\uff0c\u4f46\u4ed6\u8fd8\u8981\u4ea4\u5f85\u4e01\u3001\u9a6c\u4e8c\u4eba\u51e0\u53e5\uff0c\u662f\u4ee5\uff0c\u7ec8\u4e8e\u843d\u540e\u4e00\u6b65\uff0c\u63f4\u7740\u7ef3\u7d22\u53f6\u957f\u9752\u53cd\u7740\u8eab\u5b50\uff0c\u5411\u5ca9\u4e0b\u8dc3\u53bb\uff0c \u3000\u3000\u4e01\u3001\u9a6c\u4e8c\u4eba\u4f0f\u5728\u5730\u4e0a\uff0c\u63a2\u5934\u4e0b\u7aa5\u3002 \u3000\u3000\u8fd9\u4e00\u7728\u773c\u4e4b\u95f4\uff0c\u4e24\u4eba\u5ffd\u7136\u660e\u767d\uff0c\u5c31\u7b97\u7389\u5170\u53cc\u59dd\u62a2\u5148\u4e86\u4e00\u6b65\uff0c\u4f46\u4e0b\u964d\u7684\u901f\u5ea6\uff0c\u5374\u6bd4\u53f6\u957f\u9752\u6162\u7684\u591a\u4e86\u3002 \u3000\u3000\u53f6\u957f\u9752\u624b\u63e1\u7ef3\u7d22\uff0c\u53cc\u8db3\u6709\u5982\u873b\u8712\u70b9\u6c34\u822c\u5411\u4e0b\u8dc3\u53bb\u3002 \u3000\u3000\u7531\u4e8e\u5929\u8272\u5f88\u6697\uff0c\u5ca9\u58c1\u4e0a\u7a76\u7adf\u6709\u6ca1\u6709\u4ec0\u4e48\u85cf\u8eab\u7684\u6d1e\u7a74\uff0c\u4e00\u65f6\u8fd8\u771f\u4e0d\u5bb9\u6613\u627e\u51fa\u6765\u3002 \u3000\u3000\u4e09\u6839\u7ef3\u7d22\u957f\u5ea6\uff0c\u90fd\u4e0d\u8fc7\u662f\u4e09\u5341\u6765\u4e08\uff0c\u4f46\u60ac\u5d16\u7684\u9ad8\u5ea6\u663e\u7136\u5728\u767e\u4e08\u4ee5\u4e0a\u3002 \u3000\u3000\u8981\u60f3\u964d\u5230\u6c34\u9762\uff0c\u53f6\u957f\u9752\u867d\u7136\u53e6\u5916\u5e26\u4e86\u4e00\u6839\u957f\u7ef3\uff0c\u4f3c\u4e4e\u4e5f\u529e\u4e0d\u5230\u3002 \u3000\u3000\u6b64\u523b\uff0c\u53f6\u957f\u9752\u6b63\u5728\u628a\u53e6\u4e00\u6839\u7ef3\u7d22\u7ed3\u5728\u539f\u6765\u7684\u90a3\u6839\u7ef3\u5b50\u4e0a\u3002 \u3000\u3000\u7389\u5170\u53cc\u59dd\u7684\u7ef3\u7d22\u957f\u5ea6\uff0c\u5df2\u7ecf\u5230\u4e86\u5c3d\u5934\u3002 \u3000\u3000\u4e09\u4eba\u505c\u8eab\u4e4b\u5904\uff0c\u7ea6\u5f53\u79bb\u5f00\u5cf0\u9876\u4e09\u5341\u4e08\u5de6\u53f3\u3002 \u3000\u3000\u9762\u5bf9\u60ac\u5ca9\uff0c\u8eab\u540e\u5c31\u662f\u4e07\u9877\u6cb3\u6c34\uff0c\u5982\u679c\u662f\u5927\u767d\u5929\uff0c\u5411\u4e0b\u770b\u4e00\u773c\uff0c\u51c6\u4f1a\u53eb\u4eba\u80c6\u98a4\u5fc3\u60ca\uff0c\u6df1\u6050\u5931\u624b\u8dcc\u6b7b\u3002 \u3000\u3000\u4f46\u6b64\u65f6\u5374\u662f\u591c\u665a\uff0c\u770b\u4e0d\u6e05\u695a\uff0c\u4f7f\u5f97\u7389\u5170\u53cc\u59dd\u5728\u611f\u89c9\u4e0a\u5e76\u65e0\u8eab\u4e34\u7edd\u58c1\u4e4b\u611f\u3002 \u3000\u3000\u53f6\u957f\u9752\u5df2\u7ecf\u53c8\u5411\u4e0b\u6ed1\u53bb\u3002 \u3000\u3000\u5170\u767d\u76b1\u4e86\u76b1\u7709\uff0c\u4f4e\u58f0\u5411\u8eab\u8fb9\u4e0d\u8fdc\u7684\u7389\u7fe0\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u8fd9\u77f3\u58c1\u597d\u50cf\u6709\u4e00\u9053\u6c9f\u5462\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u80fd\u4e0d\u80fd\u5bb9\u4eba\u7acb\u8db3?\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u9645\uff0c\u5979\u5df2\u7ecf\u5728\u627e\u5bfb\u53ef\u5426\u8ba9\u81ea\u5df1\u7ad9\u7a33\u7684\u7a7a\u9699\u3002 \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u597d\u50cf\u4e0d\u80fd\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u7b11\u4e86\u4e00\u7b11\uff0c\u63a5\u9053\uff1a \u201c\u6709\u4e86\uff0c\u59b9\u59b9\uff0c\u4f60\u90a3\u8fb9\u5462?\u7ad9\u5f97\u4f4f\u4e48?\u201d \u3000\u3000\u5170\u767d\u53cc\u8db3\u62b9\u6765\u62b9\u53bb\uff0c\u53c8\u817e\u51fa\u4e00\u53ea\u624b\u6478\u7d22\uff0c\u53ea\u89c9\u5f97\u89e6\u624b\u4e4b\u5904\uff0c\u5c3d\u662f\u5e73\u6574\u7684\u5de8\u77f3\u548c\u9752\u82d4\uff0c\u53ea\u597d\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4e0d\u884c\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f60\u79fb\u8fc7\u6765\uff0c\u8fd9\u5757\u5de8\u77f3\u7a81\u51fa\u7684\u5730\u65b9\u5f88\u5927\uff0c\u53ef\u4ee5\u7ad9\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5170\u767d\u9ad8\u5174\u7684\u63a5\u9053\uff1a\u201c\u597d\u3002\u201d \u3000\u3000\u5979\u53cc\u8db3\u8fde\u70b9\uff0c\u5de6\u624b\u6293\u7d27\u4e86\u7ef3\u5b50\uff0c\u7a33\u4f4f\u8eab\u5b50\u7684\u91cd\u91cf\uff0c\u53f3\u624b\u6309\u7740\u77f3\u58c1\uff0c\u7f13\u7f13\u5730\u5411\u7389\u7fe0\u8eab\u8fb9\u79fb\u53bb\u3002 \u3000\u3000\u7389\u7fe0\u8fd9\u65f6\u4e5f\u4f38\u51fa\u4e86\u53f3\u624b\uff0c\u5411\u5170\u767d\u7684\u53f3\u624b\u63a2\u7d22\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u7389\u7fe0\u6293\u7262\u4e86\u5170\u767d\uff0c\u5c06\u5979\u62c9\u5411\u51f9\u8fdb\u53bb\u4e94\u5c3a\u4e0d\u5230\u7684\u4e00\u5757\u5de8\u77f3\u4e4b\u4e0a\u3002 \u3000\u3000\u5170\u767d\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u4e0d\u9519\u554a!\u201d \u3000\u3000\u6562\u60c5\uff0c\u5979\u4eec\u505c\u8eab\u7684\u6240\u5728\uff0c\u5b9b\u4f3c\u2014\u4e2a\u5c0f\u5c0f\u7684\u5c71\u6d1e\u3002 \u3000\u3000\u4f46\u662f\uff0c\u7531\u4e8e\u51f9\u8fdb\u53bb\u7684\u6df1\u5ea6\u5f88\u6d45\uff0c\u800c\u4e0b\u9762\u7684\u90a3\u5c71\u5ca9\u53c8\u7a81\u51fa\u6765\u8f83\u591a\uff0c\u56e0\u6b64\uff0c\u4ece\u6e56\u6c34\u5e73\u9762\u4e0a\u770b\uff0c\u6839\u672c\u770b\u4e0d\u51fa\u6709\u8fd9\u4e48\u4e00\u4e2a\u53ef\u4ee5\u7acb\u8db3\u7684\u6240\u5728\u3002 \u3000\u3000\u53cd\u8fc7\u6765\u7531\u5c71\u9876\u5411\u4e0b\u770b\u6765\uff0c\u5219\u56e0\u85e4\u845b\u7684\u906e\u63a9\uff0c\u4e5f\u53d1\u73b0\u4e0d\u4e86\u3002 \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u54b1\u4eec\u5148\u5750\u4e0b\u6b47\u6b47\uff0c\u770b\u770b\u80fd\u4e0d\u80fd\u60f3\u4ec0\u4e48\u529e\u6cd5\u518d\u5411\u4e0b\u6500\u63f4\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u5750\u4e86\u4e0b\u6765\uff0c\u561f\u7740\u5c0f\u5634\u9053\uff1a \u201c\u6709\u4ec0\u4e48\u6cd5\u5b50?\u6211\u770b\uff0c\u9664\u975e\u518d\u6709\u6839\u8349\u7ef3\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u9519\u5440!\u201d \u3000\u3000\u5979\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u63a5\u9053\uff1a \u201c\u59b9\u59b9\uff0c\u4f60\u7b49\u5728\u8fd9\u513f\uff0c\u6211\u6500\u4e0a\u53bb\uff0c\u8981\u4e01\u5cf0\u518d\u627e\u4e24\u6839\u7ef3\u5b50\u6765\u3002\u201d \u5170\u767d\u5ffd\u7136\u7075\u673a\u4e00\u52a8\uff0c\u7b11\u9053\uff1a \u201c\u59d0\u59d0\uff0c\u6211\u4e0a\u53bb\uff0c\u6211\u770b\u54b1\u4eec\u8eab\u5b50\u5f88\u8f7b\uff0c\u4e24\u4e2a\u4eba\u53ef\u4ee5\u5408\u7528\u4e00\u6839\u5440\u3002\u201d \u3000\u3000\u7389\u7fe0\u5df2\u7ecf\u62c9\u7740\u7ef3\u5b50\u8981\u5411\u4e0a\u6500\u53bb\uff0c\u95fb\u8a00\u7b11\u9053\uff1a \u201c\u5bf9\uff0c\u6211\u4eec\u5148\u524d\u600e\u4e48\u6ca1\u6709\u60f3\u5230?\u201d \u3000\u3000\u8eab\u5b50\u4e00\u4f4e\uff0c\u7f13\u7f13\u7684\u5750\u4e0b\uff0c\u63a5\u9053\uff1a \u201c\u4f60\u4e0a\u53bb\uff0c\u6211\u628a\u8fd9\u513f\u89e3\u5f00\uff0c\u4f60\u4e0d\u59a8\u987a\u7740\u6211\u7528\u7684\u8fd9\u4e00\u6839\u4e0b\u6765\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4f60\u2026\u2026\u5750\u5f97\u7262\u5417?\u201d \u3000\u3000\u7389\u7fe0\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u5bbd\u7684\u5730\u65b9\uff0c\u4f60\u8fd8\u6015\u6211\u6454\u4e0b\u53bb\u5417?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5c0f\u5fc3\u4e9b\u603b\u662f\u597d\u3002\u201d \u3000\u3000\u8eab\u5b50\u4e00\u6643\uff0c\u6293\u7262\u4e86\u7ef3\u7d22\uff0c\u733f\u7334\u822c\u7684\u5411\u5c71\u9876\u6500\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u5df2\u4e0b\u964d\u5230\u4e86\u4e03\u5341\u4e08\u5de6\u53f3\uff0c\u8ddd\u79bb\u6c34\u9762\uff0c \u5df2\u4e0d\u8db3\u4e09\u5341\u4e08\u4e86\u3002 \u8fd9\u513f\u662f\u4e00\u7247\u5f88\u5149\u6574\u7684\u77f3\u5ca9\u3002 \u3000\u3000\u65e5\u5149\u6240\u53ca\uff0c\u51e0\u4e4e\u5bf8\u8349\u4e0d\u751f\u3002 \u3000\u3000\u53f6\u957f\u9752\u624b\u8db3\u5e76\u7528\uff0c\u5728\u8fd9\u7247\u5149\u6ed1\uff1b\u60ac\u5d16\u77f3\u58c1\u4e0a\uff0c\u4ed4\u7ec6\u7684\u5bdf\u770b\u7740\uff0c\u4ed6\u5f88\u5e0c\u671b\u80fd\u627e\u5230\u4e00\u4e9b\u4ec0\u4e48\u53ef\u7591\u7684\u88c2\u7f1d\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u53f6\u957f\u9752\u4f9d\u7136\u5931\u671b\u4e86\u3002 \u3000\u3000\u4eff\u4f5b\u4e0a\u9762\u90a3\u4e00\u6bb5\u77f3\u58c1\u4e00\u6837\uff0c\u4ed6\u4ecd\u7136\u6ca1\u6709\u627e\u51fa\u4efb\u4f55\u53ef\u7591\u4e4b\u5904\u3002 \u3000\u3000\u7ef3\u7d22\u7684\u957f\u5ea6\uff0c \u5df2\u7136\u653e\u5c3d\uff0c\u53f6\u957f\u9752\u5f2f\u7740\u8eab\u5b50\u770b\u770b\u4e0b\u9762\u8fd8\u6709\u4e09\u5341\u4e08\u7684\u77f3\u58c1\uff0c\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u5bfb\u601d\u7740\uff0c\u8981\u600e\u4e48\u6837\u624d\u80fd\u518d\u5411\u4e0b\u79fb\u52a8\u3002\u5982\u679c\u4e0b\u9762\u4e0d\u662f\u6709\u7740\u90a3\u5343\u9877\u6e56\u6c34\u7684\u592a\u6e56\uff0c\u51e0\u5341\u4e08\u9ad8\u7684\u60ac\u5d16\uff0c\u5e76\u96be\u4e0d\u5012\u53f6\u957f\u9752\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e0d\u8bc6\u6c34\u6027\uff0c\u56e0\u6b64\uff0c\u4e0b\u9762\u90a3\u4e00\u6c6a\u6e56\u6c34\uff0c\u53ef\u5c31\u4ee4\u4ed6\u5927\u611f\u4e3a\u96be\u3002 \u3000\u3000\u53f6\u957f\u9752\u60f3\u4e86\u4e00\u60f3\uff0c\u5c45\u7136\u4e5f\u8ba9\u4ed6\u60f3\u51fa\u4e86\u8ddf\u7389\u4e8c\u53cc\u59dd\u76f8\u540c\u7684\u60f3\u6cd5\uff0c\u53bb\u4ece\u5979\u4eec\u90a3\u513f\u89e3\u4e0b\u4e00\u6839\u7ef3\u5b50\u6765\uff0c\u7ee7\u7eed\u5f80\u4e0b\u53bb\u67e5\u770b\u3002 \u3000\u3000\u53f6\u957f\u9752\u6697\u81ea\u4e00\u7b11\uff0c\u7acb\u5373\u63f4\u7740\u7ef3\u5b50\u53c8\u6500\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u4ed6\u8eab\u5f62\u5feb\u6377\uff0c\u7728\u773c\u95f4\u5df2\u6500\u4e0a\u4e86\u5eff\u4e08\u5de6\u53f3\u3002 \u3000\u3000\u4e0a\u9762\u5341\u4e08\u5de6\u53f3\uff0c\u5c31\u662f\u7389\u7fe0\u72ec\u81ea\u76d8\u5750\u7684\u77f3\u6d1e\u3002 \u3000\u3000\u53f6\u957f\u9752\u53cc\u624b\u6293\u7740\u957f\u7ef3\uff0c\u5168\u51ed\u4e24\u8db3\u8e0f\u7740\u77f3\u58c1\u5411\u4e0a\u6500\u63f4\uff0c\u8eab\u5b50\u4e00\u5f39\u4e00\u5f39\u7684\uff0c\u9887\u4f3c\u4e00\u53ea\u767d\u5154\u5728\u817e\u8dc3\uff0c\u770b\u4e0a\u53bb\uff0c\u4f3c\u4e4e\u662f\u89c9\u4e0d\u51fa\u4ed6\u7684\u53cc\u8db3\u4f1a\u89e6\u53ca\u77f3\u58c1\u3002 \u3000\u3000\u4f46\u662f\uff0c\u5b9e\u9645\u4e0a\u662f\u6bcf\u4e00\u6b65\u90fd\u4f1a\u89e6\u53ca\u77f3\u58c1\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53f6\u957f\u9752\u5ffd\u7136\u89c9\u5f97\u5de6\u8db3\u4f38\u51fa\u53bb\u7684\u5730\u65b9\uff0c\u6ca1\u6709\u5f97\u529b\uff0c\u56e0\u6b64\uff0c\u6709\u8db3\u4e5f\u8ddf\u7740\u8e0f\u4e0d\u51fa\u53bb\uff0e\u6574\u4e2a\u7684\u8eab\u5b50\uff0c\u4e00\u4e0b\u5b50\u5411\u4e0b\u4e00\u6c89\u3001\u5411\u4e0b\u6ed1\u4e86\u4e03\u516b\u5c3a\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u4e00\u60ca\uff0c\u53cc\u624b\u4e00\u7d27\uff0c\u7d27\u7d27\u7684\u6293\u7262\u4e86\u7ef3\u7d22\uff0c\u624d\u4f7f\u5f97\u81ea\u5df1\u6ca1\u6709\u8dcc\u843d\u6570\u5341\u4e08\u4ee5\u4e0b\u7684\u592a\u6e56\u4e4b\u4e2d\u3002 \u3000\u3000\u53f6\u957f\u9752\u6697\u53eb\u4e86\u4e00\u58f0\u4fa5\u5e78\uff0c\u7a33\u4f4f\u5382\u8eab\u5b50\uff0c\u8fd9\u4e00\u56de\u53ef\u4e0d\u6562\u7c97\u5fc3\u5927\u610f\uff0c\u7f13\u6162\u7684\u4e00\u6b65\u4e00\u6b65\u7684\u5411\u4e0a\u6500\u63f4\u3002 \u3000\u3000\u5f88\u5feb\u7684\uff0c\u4ed6\u53c8\u8e0f\u5230\u4e86\u521a\u624d\u6ed1\u843d\u7684\u5730\u65b9\uff0c\u5de6\u811a\u8fd9\u56de\u5341\u5206\u5c0f\u5fc3\u7684\u5411\u5ca9\u77f3\u4e0a\u8e0f\u53bb\u3002 \u3000\u3000\u540c\u6837\u7684\uff0c\u53c8\u8e0f\u4e86\u4e2a\u7a7a\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a \u201c\u83ab\u975e\u8fd9\u513f\u662f\u4e00\u4e2a\u77f3\u6d1e?\u4e0d\u7136\u600e\u4e48\u4f1a\u2026\u2026\u201d \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u53f6\u957f\u9752\u7acb\u5373\u633d\u8d77\u90a3\u62d6\u5f97\u957f\u957f\u7684\u4e0b\u5782\u7ef3\u7d22\uff0c\u5728\u81ea\u5df1\u7684\u8170\u90e8\u7f20\u4e86\u4e00\u4e2a\u5708\uff0c\u628a\u81ea\u5df1\u7684\u8eab\u5b50\u56fa\u5b9a\u597d\uff0c\u817e\u51fa\u4e86\u53cc\u624b\uff0c\u5c0f\u5fc3\u7684\u5728\u5ca9\u4e0a\u6478\u7d22\u3002 \u3000\u3000\u6ca1\u6709\u9519\uff0c\u662f\u4e00\u4e2a\u65b9\u5706\u7ea6\u5728\u4e09\u5c3a\u89c1\u65b9\u7684\u77f3\u6d1e\u3002 \u3000\u3000\u521a\u624d\u4ed6\u7531\u4e0a\u9762\u4e0b\u6765\uff0c\u56e0\u4e3a\u4ec5\u51ed\u53cc\u624b\u6293\u7740\u7ef3\u5b50\uff0c\u4ea4\u6362\u7740\u5411\u4e0b\u964d\u843d\uff0c\u56e0\u6b64\uff0c\u5ca9\u58c1\u4e0a\u7684\u8fd9\u4e2a\u88ab\u9752\u82d4\u548c\u91ce\u8349\u906e\u63a9\u4e86\u7684\u5c0f\u6d1e\uff0c\u662f\u4e0d\u5bb9\u6613\u5bdf\u89c9\u5230\u7684\u3002 \u3000\u3000\u53f6\u957f\u9752\u53cc\u624b\u62e8\u5f00\u91ce\u8349\uff0c\u6293\u7740\u5ca9\u6d1e\u7684\u8fb9\u6cbf\uff0c\u4ed4\u7ec6\u7684\u671b\u7740\u8fd9\u4e2a\u77f3\u6d1e\uff0c\u4e0d\u7981\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u6562\u60c5\uff0c\u8fd9\u4e2a\u6d1e\u5f88\u6df1\uff0c\u6df1\u5f97\u770b\u4e0d\u5230\u5e95\u3002 \u3000\u3000\u53f6\u957f\u9752\u63a2\u5934\u5411\u5185\u5f20\u671b\uff0c\u53ea\u89c9\u5f97\u9635\u9635\u51b7\u98ce\uff0c \u7531\u6d1e\u4e2d\u5411\u5916\u900f\u51fa\u3002 \u5c71\u6d1e\u91cc\u9762\u6709\u98ce\uff0c\u8fd9\u8868\u793a\u5c71\u6d1e\u8fd8\u6709\u53e6\u4e00\u4e2a\u51fa\u53e3\uff0c\u5426\u5219\uff0c\u98ce\u529b\u600e\u4e48\u5bf9\u6d41? \u3000\u3000\u53f6\u957f\u9752\u7565\u4e00\u8fdf\u7591\uff0c\u8eac\u7740\u8eab\u5b50\u89e3\u5f00\u7cfb\u5728\u8eab\u4e0a\u7684\u7ef3\u5b50\uff0c\u8bd5\u7740\u60f3\u5411\u5185\u722c\u53bb\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u53ea\u722c\u4e86\u534a\u4e2a\u8eab\u5b50\uff0c\u5c31\u505c\u4e0a\u4e86\u3002 \u3000\u3000\u6d1e\u662f\u659c\u7684\u3002 \u3000\u3000\u5411\u4e0b\u503e\u659c\u7740\uff0c\u5982\u679c\u53f6\u957f\u9752\u518d\u5411\u6d1e\u5185\u722c\uff0c\u5fc5\u5c06\u5f04\u5f97\u53cc\u624b\u6293\u4e0d\u5230\u6d1e\u58c1\uff0c\u60ac\u7a7a\u540a\u5728\u6d1e\u5185\u4e86\u3002 \u3000\u3000\u53f6\u957f\u9752\u8003\u8651\u4e00\u4e0b\uff0c\u5c31\u7f29\u51fa\u6d1e\u5916\u3002 \u3000\u3000\u6562\u60c5\uff0c\u4ed6\u5df2\u53d1\u73b0\u4e86\u8fd9\u4e2a\u6d1e\u53e3\u867d\u7136\u5f88\u5c0f\uff0c\u4f46\u5165\u5185\u4ee5\u540e\uff0c\u5c31\u50cf\u4e00\u4e2a\u5012\u653e\u7740\u7684\u6f0f\u6597\uff0c\u91cc\u9762\u662f\u7a7a\u7684\u3002 \u3000\u3000\u5f88\u660e\u663e\uff0c\u8fd9\u5ea7\u4e3b\u5cf0\u7684\u5c71\u8179\u4e4b\u4e2d\uff0c\u65e0\u53ef\u7f6e\u7591\u7684\uff0c\u6709\u4e00\u4e2a\u5f88\u5927\u7684\u6d1e\u7a9f\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e0d\u518d\u72b9\u8c6b\uff0c\u8fc5\u6377\u7684\u63f4\u7ef3\u4e0a\u6500\u3002 \u3000\u3000\u62b5\u8fbe\u4e86\u7389\u7fe0\u505c\u8eab\u4e4b\u5904\uff0c\u53f6\u957f\u9752\u5e76\u672a\u7559\u4e0b\u6765\uff0c\u4f9d\u7136\u733f\u7334\u822c\u6500\u4e0a\u5c71\u9876\u3002 \u3000\u3000\u9a6c\u826f\u548c\u5170\u767d\u6b63\u5728\u5c71\u9876\u7b49\u5019\u7740\u3002 \u3000\u3000\u4e01\u5cf0\u5df2\u7136\u8d76\u53bb\u8349\u68da\uff0c\u5e94\u5170\u767d\u4e4b\u6c42\uff0c\u518d\u62c6\u4e0b\u4e24\u6839\u7ef3\u7d22\u6765\u5907\u7528\u3002 \u3000\u3000\u773c\u89c1\u53f6\u957f\u9752\u91cd\u56de\u5c71\u9876\uff0c\u9a6c\u826f\u6df1\u6df1\u5730\u5410\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e0a\u5f97\u5c71\u9876\uff0c \u76ee\u5149\u4e00\u8f6c\u7b11\u9053\uff1a \u201c\u5170\u59d1\u5a18\uff0c\u4f60\u600e\u4e48\u53c8\u4e0a\u6765\u4e86?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a \u201c\u7ef3\u5b50\u4e0d\u591f\u957f\u561b\uff0c\u6211\u8981\u4e01\u5cf0\u518d\u53bb\u53d6\u4e24\u6839\u7ef3\u5b50\u6765\uff0c\u53f6\u5c11\u4fa0\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e5f\u6500\u56de\u5c71\u9876\u4e86?\u4f60\u627e\u5230\u4e86\u4ec0\u4e48\u6ca1\u6709?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u627e\u5230\u4e00\u4e2a\u5c0f\u5c71\u6d1e\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5c0f\u5c71\u6d1e?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6d1e\u53e3\u5f88\u5c0f\uff0c\u4f46\u91cc\u9762\u5374\u770b\u4e0d\u51fa\u7a76\u7adf\u6709\u591a\u5927\u3002\u201d \u3000\u3000\u5170\u767d\u5927\u611f\u5174\u8da3\uff0c\u63a5\u9053\uff1a\u201c\u91cc\u9762\u5f88\u5927\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u5f88\u5927\u3002\u4e0d\u8fc7\u2026\u2026\u5929\u8272\u592a\u9ed1\uff0c\u6839\u672c\u770b\u4e0d\u51fa\u771f\u6b63\u5927\u5c0f\u2026\u2026\u201d \u9a6c\u826f\u63a5\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u5c5e\u4e0b\u53bb\u51c6\u5907\u4e00\u6839\u706b\u628a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u9053\uff1a \u201c\u8fd9\u2026\u2026\u5bf9\u65b9\u7684\u8239\u53ea\uff0c\u4f1a\u4e0d\u4f1a\u56e0\u4e3a\u71c3\u4e86\u706b\u628a\uff0c\u800c\u7a81\u7136\u5c55\u5f00\u653b\u51fb\u5462?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a \u201c\u5251\u4f7f\u5982\u679c\u5165\u6d1e\u518d\u884c\u70b9\u71c3\uff0c\u5e94\u8be5\u662f\u4e0d\u4f1a\u88ab\u53d1\u73b0\u7684\u4e86!\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u9519!\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u5c5e\u4e0b\u8fd9\u5c31\u53bb\u53d6\u706b\u628a\u2026\u2026\u201d \u3000\u3000\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u4e01\u5cf0\u5df2\u5306\u5306\u8d76\u6765\u3002 \u3000\u3000\u4ed6\u5e26\u6765\u4e86\u4e24\u6839\u7ef3\u7d22\uff0c\u5170\u767d\u7b11\u7740\u63a5\u8fc7\u3002 \u3000\u3000\u4e01\u5cf0\u89c1\u5230\u53f6\u957f\u9752\u4e5f\u5728\u5c71\u9876\uff0c\u4e0d\u7531\u7565\u611f\u610f\u5916\u3002 \u3000\u3000\u4e01\u5cf0\u56de\u5934\u4e0d\u89c1\u9a6c\u826f\uff0c\u66f4\u662f\u4e0d\u5b89\uff0c\u8131\u53e3\u9053\uff1a\u201c\u5251\u4f7f\uff0c\u9a6c\u826f\u5462?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u8981\u4ed6\u53d6\u706b\u628a\u53bb\u5382\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5251\u4f7f\u8981\u7528\u706b\u628a\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u6211\u627e\u5230\u4e86\u4e00\u4e2a\u5c71\u6d1e\uff0c\u5fc5\u987b\u8981\u6709\u706b\u628a\uff0c\u65b9\u80fd\u5165\u5185\u67e5\u770b\u3002\u201d \u9a6c\u826f\u8fd9\u65f6\u4e5f\u5306\u5306\u7684\u8d76\u6765\uff1a \u3000\u3000\u4ed6\u62ff\u4e86\u4e24\u652f\u5f88\u5927\u7684\u706b\u628a\uff0c\u7af9\u7b52\u4e4b\u4e2d\uff0c\u704c\u6ee1\u4e86\u9999\u6cb9\uff0e\u663e\u7136\u8fd9\u4e9b\u90fd\u662f\u65e9\u5c31\u51c6\u5907\u597d\uff0c\u7528\u4ee5\u5bf9\u654c\u7167\u660e\u7528\u7684\u3002 \u3000\u3000\u53f6\u957f\u9752\u7a0d\u4f5c\u68c0\u67e5\uff0c\u5e26\u4e86\u4e00\u4e2a\u706b\u79cd\uff0c \u5e76\u5c06\u5170\u767d\u6240\u8981\u7684\u4e24\u6839\u957f\u7ef3\uff0c\u4e5f\u4e00\u5e76\u53d6\u8fc7\u6765\u7f20\u5728\u8eab\u4e0a\uff0c\u7b11\u9053\uff1a \u201c\u5170\u59d1\u5a18\uff0c \u4f60\u8ddf\u7389\u7fe0\u5148\u5728\u90a3\u77f3\u5ca9\u4e0a\u7b49\u6211\u5427\uff0c\u8fd9\u4e24\u6839\u7ef3\u5b50\uff0c\u5148\u501f\u7528\u4e00\u4e0b\u5b50\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u6211\u8ddf\u4f60\u4e00\u9053\u8fdb\u6d1e\u53bb\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u2026\u2026\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5170\u59d1\u5a18\uff0c\u5251\u4f7f\u4e00\u4e2a\u4eba\u58eb\uff0c\u522b\u65e0\u987e\u5fcc\uff0c\u5982\u679c\u5170\u59d1\u5a18\u4e5f\u53bb\uff0c\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e0b\u9762\u7684\u8bdd\uff0c\u6ca1\u8bf4\u51fa\u6765\uff0c\u4f46\u4efb\u4f55\u4eba\u90fd\u660e\u767d\uff0c \u4e01\u5cf0\u7684\u7528\u5fc3\u826f\u82e6\uff0c\u5728\u5728\u90fd\u4e3a\u53f6\u957f\u9752\u7684\u5b89\u5168\u7740\u60f3\u3002 \u3000\u3000\u5170\u767d\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u4e01\u5144\uff0c \u4f60\u6015\u6211\u62d6\u7d2f\u4e86\u4ed6?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u9a6c\u826f\u63a5\u9053\uff1a\u201c\u5170\u59d1\u5a18\uff0c\u672c\u95e8\u4e94\u5927\u5251\u4f7f\uff0c\u5730\u4f4d\u5d07\u9ad8\u3001\u8d23\u4efb\u91cd\u5927\uff0c\u56e0\u6b64\uff0c\u6211\u7b49\u90fd\u5949\u4e86\u5821\u4e3b\u4e4b\u547d\uff0c\u968f\u65f6\u8981\u4fdd\u62a4\u5251\u4f7f\u5b89\u5168\uff0c \u4e01\u5144\u6b64\u8a00\uff0c\u7eb5\u6709\u5192\u72af\u3001\u4e5f\u5e0c\u671b\u59d1\u5a18\u4e0d\u8981\u653e\u5728\u5fc3\u4e0a\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u7b11\u9053\uff1a\u201c\u600e\u4e48\u4f1a?\u4e0d\u53bb\u5c31\u4e0b\u53bb\u5427!\u201d \u3000\u3000\u53f6\u957f\u9752\u5fae\u5fae\u4e00\u7b11\uff0c \u62ff\u8d77\u706b\u628a\uff0c\u8fc5\u901f\u7684\u5411\u4e0b\u6500\u63f4\u3002 \u5170\u767d\u5411\u4e01\u3001\u9a6c\u4e8c\u4eba\u9053\uff1a \u201c\u6211\u5c71\u5230\u4e0b\u9762\u53bb\uff0c\u5fc5\u8981\u65f6\u6211\u8ddf\u59d0\u59d0\u53ef\u4ee5\u63a5\u5e94\u53f6\u5c11\u4fa0\u3002\u201d \u4e01\u3001\u9a6c\u4e8c\u4eba\u4e00\u62b1\u62f3\uff0c\u540c\u65f6\u9053\uff1a\u201c\u591a\u8c22\uff01\u201d \u3000\u3000\u5170\u767d\u8fd9\u4e00\u56de\u5e76\u6ca1\u6709\u7528\u81ea\u5df1\u539f\u6765\u90a3\u6839\u7ef3\u5b50\uff0c\u5979\u628a\u8fd9\u6839\u7ef3\u5b50\u89e3\u4e0b\u6765\uff0c\u7cfb\u5728\u8170\u95f4\uff0c\u6293\u7740\u7389\u7fe0\u7528\u7684\u90a3\u4e00\u6839\u5411\u5ca9\u77f3\u4e0b\u9762\u4e0b\u964d\u3002 \u3000\u3000\u8fd9\u6837\uff0c\u5979\u4e5f\u591a\u51fa\u4e86\u4e00\u6839\u7ef3\u5b50\u4e86\u3002 \u3000\u3000\u53f6\u957f\u9752\u76f4\u7ebf\u800c\u4e0b\uff0c\u7728\u773c\u95f4\u7ecf\u8fc7\u7389\u7fe0\u8eab\u8fb9\u3002 \u3000\u3000\u7389\u7fe0\u89c1\u4ed6\u4e0b\u6765\uff0c\u7b11\u9053\uff1a\u201c\u53f6\u5144\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u505c\u4e86\u4e00\u4e0b\u9053\uff1a \u201c\u7389\u7fe0\uff0c\u6211\u627e\u5230\u4e86\u4e00\u4e2a\u77f3\u6d1e\u4e86\uff0c\u6211\u5148\u8fdb\u53bb\u770b\u770b\u3002\u201d \u7389\u7fe0\u9053\uff1a\u201c\u6211\u542c\u5230\u4f60\u4eec\u5728\u4e0a\u9762\u8bf4\u7684\u8bdd\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u7389\u7fe0\u7b11\u9053\uff1a\u201c\u591c\u9759\u66f4\u6df1\uff0c\u8bdd\u58f0\u4f20\u5f97\u5f88\u8fdc\u554a!\u201d \u3000\u3000\u53f6\u957f\u9752\u8fde\u5fd9\u538b\u4f4e\u4e86\u58f0\u97f3\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u6211\u4eec\u5ffd\u7565\u4e86\u8fd9\u4e9b\u2026\u2026\u4f46\u613f\u5bf9\u65b9\u6ca1\u6709\u542c\u5230\u3002\u201d \u7389\u7fe0\u9053\uff1a\u201c\u53f6\u5144\u653e\u5fc3\uff0c \u4f60\u4eec\u8bf4\u7684\u8bdd\uff0c\u4f20\u4e0d\u4e86\u90a3\u4e48\u8fdc\u7684!\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5170\u59d1\u5a18\u8fd8\u5728\u5c71\u9876\u4e0a\uff0c\u7389\u7fe0\uff0c \u4f60\u6700\u597d\u4e5f\u4e0a\u53bb\u5427!\u201d \u3000\u3000\u5170\u767d\u964d\u4e86\u4e0b\u6765\uff0c\u7b11\u9053\uff1a\u201c\u53f6\u5c11\u4fa0\uff0c\u6211\u4e5f\u6765\u4e86!\u201d \u3000\u3000\u53f6\u957f\u9752\u6447\u6447\u5934\u9053\uff1a \u201c\u4e24\u4f4d\u70ed\u5fc3\u53ef\u611f\u2026\u2026\u4f46\u5728\u4e0b\u5e0c\u671b\u4f60\u4eec\u4e0d\u8981\u5192\u9669\u3002\u201d \u5170\u767d\u5df2\u505c\u5728\u7389\u7fe0\u8eab\u8fb9\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u4f1a\u7684!\u201d \u3000\u3000\u53f6\u957f\u9752\u770b\u4e86\u4e8c\u5973\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u5148\u4e0b\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u53cc\u624b\u4e00\u6ed1\uff0c\u5411\u4e0b\u964d\u53bb\u3002 \u3000\u3000\u8f7b\u8f66\u719f\u8def\uff0c\u4e0d\u4e00\u77ac\u5df2\u5230\u4e86\u6d1e\u53e3\u65b9\u4f4d\u3002 \u3000\u3000\u53f6\u957f\u9752\u5148\u5c06\u81ea\u5df1\u8eab\u5b50\u5750\u5728\u6d1e\u53e3\uff0c\u7136\u540e\u89e3\u4e0b\u633d\u5728\u8eab\u4e0a\u7684\u7ef3\u5b50\u7ed3\u597d\uff0c\u62ff\u7740\u706b\u628a\uff0c\u811a\u5728\u5185\uff0c\u5934\u5728\u5916\uff0c\u76f4\u5411\u90a3\u5c0f\u5c0f\u7684\u6d1e\u53e3\u722c\u53bb\u3002 \u3000\u3000\u524d\u884c\u4e0d\u5230\u4e94\u5c3a\uff0c\u811a\u4e0b\u5c31\u6ca1\u6709\u843d\u811a\u4e4b\u5904\u4e86\u3002 \u3000\u3000\u6562\u60c5\uff0c\u8fd9\u513f\u8fd8\u771f\u662f\u4e00\u4e2a\u5927\u6f0f\u6597\u5f62\u72b6\u7684\u5c71\u6d1e\u3002 \u3000\u3000\u4e0a\u9762\u7684\u6d1e\u53e3\u3002\u51e0\u4e4e\u662f\u4e00\u4e2a\u51fa\u6c14\u901a\u98ce\u7684\u6c14\u53e3\u3002 \u3000\u3000\u53f6\u957f\u9752\u7565\u4e00\u5bfb\u601d\uff0c\u6253\u7740\u706b\u79cd\uff0c\u70b9\u8d77\u4e86\u706b\u628a\uff0c\u8499\u8499\u7684\u706b\u5149\uff0c\u987f\u65f6\u7167\u4eae\u8eab\u8fb9\u4e00\u5207\u3002 \u3000\u3000\u4ed6\u5148\u524d\u9884\u6599\u7684\u4e0d\u9519\uff0c\u679c\u7136\u4e0b\u9762\u662f\u7a7a\u7684\uff0c\u800c\u8fd9\u4e2a\u5c0f\u7684\u6d1e\u53e3\uff0c\u6b63\u662f\u901a\u98ce\u4e4b\u7528\u3002 \u3000\u3000\u53f6\u957f\u9752\u52fe\u7740\u8eab\u5b50\u5411\u4e0b\u671b\u53bb\u3002 \u3000\u3000\u4e00\u7247\u9ed1\u6697\uff0c\u6df1\u4e0d\u89c1\u5e95\u3002 \u3000\u3000\u706b\u628a\u867d\u7136\u5f88\u4eae\uff0c\u4f46\u4e5f\u7167\u4e0d\u51fa\u4e09\u4e08\u4e4b\u5916\u3002 \u3000\u3000\u4ed6\u7565\u4e00\u8fdf\u7591\uff0c\u53f3\u624b\u62ff\u7740\u706b\u628a\uff0c\u5de6\u624b\u548c\u53cc\u817f\u7ede\u7740\u7ef3\u5b50\uff0c\u51cc\u7a7a\u5411\u4e0b\u964d\u53bb\u3002 \u3000\u3000\u8eab\u4e0b\u5c71\u8179\u4e2d\u7a76\u7adf\u6709\u4e9b\u4ec0\u4e48\uff0c\u4ed6\u4e5f\u4e0d\u518d\u53bb\u601d\u7d22\uff0c \u53ea\u597d\u8d70\u4e00\u6b65\u7b97\u4e00\u6b65\u4e86\u3002 \u8fd9\u65f6\uff0c\u4ed6\u56e0\u4e3a\u53ea\u53ef\u7528\u4e00\u53ea\u624b\uff0c\u6240\u4ee5\uff0c\u964d\u843d\u7684\u901f\u5ea6\uff0c \u6bd4\u5148\u524d\u6162\u5f97\u5f88\u591a\u3002 \u76f4\u5230\u4ed6\u628a\u8fd9\u4e00\u6839\u957f\u8fbe\u4e09\u5341\u4e08\u7684\u7ef3\u5b50\u964d\u5230\u4e86\u53ea\u5269\u4e94\u4e08\u4e0d\u5230\uff0c\u811a\u4e0b\u4ecd\u7136\u7a7a\u7a7a\u8361\u8361\u3002 \u3000\u3000\u800c\u4e14\uff0c\u4e3e\u76ee\u4e0b\u671b\uff0c\u4f9d\u7136\u6ca1\u6709\u770b\u5230\u5e95\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u6697\u5403\u60ca\uff0c\u5fd6\u9053\uff1a \u201c\u96be\u9053\u2026\u2026\u8fd9\u4e2a\u5c71\u6d1e\u6df1\u8fc7\u4e86\u6e56\u9762\u5417?\u201d \u3000\u3000\u4e8b\u5230\u5982\u4eca\uff0c\u4ed6\u53ef\u4e0d\u80fd\u534a\u9014\u800c\u5e9f\uff0c\u56e0\u6b64\uff0c \u53e3\u542b\u706b\u628a\uff0c\u628a\u53e6\u4e00\u6839\u957f\u7ef3\uff0c\u91cd\u65b0\u7ed3\u4e0a\uff0c\u53c8\u7f13\u7f13\u5411\u4e0b\u964d\u53bb\u3002 \u3000\u3000\u76f4\u5230\u8fd9\u4e00\u6839\u7ef3\u7d22\u53ea\u5269\u4e0b\u5341\u4e08\u5de6\u53f3\uff0c\u4ed6\u7ec8\u4e8e\u770b\u5230\u4e86\u4e0b\u8fb9\u4e00\u5757\u5de8\u77f3\uff0c\u7531\u5de6\u8fb9\u7684\u5c71\u58c1\u4e4b\u95f4\u4f38\u5c55\u51fa\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u53cc\u8db3\u843d\u5730\uff0c\u4e0d\u7981\u5410\u51fa\u4e00\u53e3\u5927\u6c14\u3002 \u3000\u3000\u4f46\u4fef\u770b\u524d\u8eab\uff0c\u4f9d\u7136\u662f\u65e0\u5e95\u6df1\u6e0a\u3002 \u3000\u3000\u53f6\u957f\u9752\u7565\u4e00\u5598\u606f\uff0c\u5ffd\u7136\u8033\u4e2d\u542c\u5230\u4e86\u6ce2\u6d9b\u649e\u51fb\u4e4b\u58f0!\uff0c\u6562\u60c5\uff0c\u90a3\u6df1\u6e0a\u4e4b\u4e0b\uff0c\u6b63\u662f\u592a\u6e56\u7684\u6e56\u6c34\u3002 \u3000\u3000\u53f6\u957f\u9752\u62ff\u7740\u706b\u628a\uff0c\u633d\u8d77\u7ef3\u5b50\uff0c\u7531\u5de8\u77f3\u4e0a\u5411\u524d\u7f13\u7f13\u5730\u524d\u884c\u3002 \u3000\u3000\u7ea6\u83ab\u8d70\u4e86\u4e03\u6b65\u5de6\u53f3\uff0c\u7a81\u7136\u53d1\u73b0\u9762\u524d\u6709\u4e00\u5835\u5de8\u5927\u77f3\u58c1\u6321\u4f4f\u4e86\u53bb\u8def\u3002 \u3000\u3000\u53f6\u957f\u9752\u4ed4\u7ec6\u7684\u5728\u5c71\u58c1\u4e0a\u5bfb\u627e\u7740\uff0c\u56e0\u4e3a\uff0c\u4ed6\u81ea\u4ece\u8e0f\u4e0a\u8fd9\u7a81\u51fa\u7684\u5ca9\u77f3\u4e4b\u540e\uff0c\u5c31\u5df2\u53d1\u73b0\u4e01\u8fd9\u957f\u7ea6\u516b\u4e08\u7684\u5de8\u77f3\u4e4b\u4e0a\uff0c\u6ca1\u6709\u4e1d\u6beb\u9752\u82d4\u4e4b\u7c7b\u6742\u8349\u3002 \u3000\u3000\u663e\u7136\uff0c\u8fd9\u5de8\u77f3\u4e0a\u4e0d\u65f6\u6709\u4e1c\u897f\u7ecf\u8fc7\u3002\u4e5f\u8bb8\u662f\u4eba\uff0c\u4e5f\u8bb8\u662f\u4ec0\u4e48\u91ce\u517d\u4e5f\u4e0d\u4e00\u5b9a\u3002 \u3000\u3000\u6545\u800c\uff0c\u53f6\u957f\u9752\u53ef\u4ee5\u786e\u5b9a\uff0c\u5bf9\u9762\u7684\u5c71\u58c1\uff0c\u5e94\u8be5\u4e5f\u6709\u4e00\u4e2a\u53ef\u4ee5\u8fdb\u53bb\u7684\u95e8\u6237\u3002 \u3000\u3000\u4ed7\u7740\u706b\u628a\u7684\u5149\u4eae\uff0c\u53f6\u957f\u9752\u7ec8\u4e8e\u627e\u5230\u4e86\u4e00\u5904\u53ef\u7591\u7684\u5c71\u77f3\u3002 \u3000\u3000\u8fd9\u5757\u5c71\u77f3\u5927\u7ea6\u6709\u56db\u5c3a\u65b9\u5706\uff0c\u5f88\u5149\u6ed1\uff0c\u65c1\u8fb9\u867d\u7136\u53ea\u9732\u51fa\u4e86\u5f88\u7a84\u7684\u7f1d\u9699\uff0c\u4f46\u4ecd\u7136\u7792\u4e0d\u8fc7\u53f6\u957f\u9752\u7684\u89c2\u5bdf\u3002 \u3000\u3000\u53f6\u957f\u9752\u6697\u6697\u4e00\u7b11\uff0c\u5c06\u706b\u628a\u63d2\u5728\u53e6\u4e00\u8fb9\u5c71\u77f3\u7f1d\u4e2d\uff0c\u53cc\u624b\u8fd0\u529b\uff0c\u5411\u77f3\u58c1\u4e0a\u63a8\u53bb\u3002 \u3000\u3000\u4e00\u9635\u54af\u54af\u4e4b\u58f0\u5165\u8033\uff0c\u5c71\u77f3\u679c\u7136\u88ab\u4ed6\u63a8\u5f97\u6d3b\u52a8\u4e86\uff0c\u5f88\u5feb\u7684\u9732\u51fa\u4e00\u9053\u5c0f\u5c0f\u7684\u95e8\u6237\u3002 \u3000\u3000\u53f6\u957f\u9752\u62ff\u8d77\u706b\u628a\uff0c\u5c06\u8fd8\u6709\u4e08\u8bb8\u957f\u7684\u7ef3\u5b50\uff0c\u5939\u5728\u77f3\u7f1d\u4e2d\u4ee5\u4fbf\u5c11\u65f6\u9000\u56de\u4e4b\u7528\uff0c\u8fd9\u624d\u5927\u6b65\u8de8\u5165\u95e8\u5185\u3002 \u3000\u3000\u95e8\u5185\uff0c\u662f\u4e00\u95f4\u77f3\u5ba4\uff0c\u706b\u5149\u7167\u5904\uff0c\u53f6\u957f\u9752\u4e0d\u7981\u4e00\u60ca\u3002 \u3000\u3000\u4e00\u540d\u957f\u53d1\u5782\u80a9\uff0c\u8eab\u5b50\u534a\u88f8\u7684\u5c11\u5973\uff0c\u6b63\u7ad9\u5728\u77f3\u5ba4\u4e2d\u95f4\u3002 \u3000\u3000\u706b\u5149\u8f89\u7167\u4e4b\u4e0b\uff0c\u53ef\u4ee5\u6e05\u695a\u7684\u770b\u5230\u90a3\u662f\u4e00\u4f4d\u5f88\u7f8e\u7684\u59d1\u5a18\uff0c\u79c0\u4e3d\u7684\u8f6e\u5ed3\uff0c\u4e24\u70b9\u5bd2\u661f\u822c\u7684\u5927\u773c\u775b\uff0c\u957f\u53d1\u62ab\u5782\u8170\u9645\uff0c\u4e00\u53cc\u8d64\u88f8\u7684\u5929\u8db3\uff0c\u5c0f\u8179\u95f4\u56f4\u7740\u4e00\u6761\u5bbd\u4e0d\u53ca\u5c3a\uff0c\u95ea\u7740\u9cde\u5149\u7684\u76ae\u88d9\u3002 \u3000\u3000\u53ef\u60dc\u53f6\u957f\u9752\u8ba4\u4e0d\u51fa\u90a3\u662f\u4ec0\u4e48\u9cde\u76ae\u3002 \u3000\u3000\u5c11\u5973\u7684\u524d\u80f8\u4e0a\uff0c\u4e5f\u7528\u4e24\u5757\u624b\u638c\u5927\u5c0f\u7684\u9cde\u76ae\uff0c\u906e\u53bb\u4e86\u53cc\u4e73\u3002 \u3000\u3000\u8fd9\u4f4d\u5c11\u5973\u4e5f\u6709\u7f3a\u70b9\uff0c\u90a3\u5c31\u662f\u5979\u7684\u80a4\u8272\u4e0d\u591f\u83b9\u767d\uff0c\u5168\u8eab\u7684\u808c\u80a4\uff0c\u90fd\u662f\u4e00\u7247\u53e4\u94dc\u989c\u8272\u3002 \u3000\u3000\u4f46\u5979\u808c\u8089\u7ed3\u5b9e\uff0c\u7ebf\u6761\u5747\u5300\uff0c\u8eab\u8eaf\u534a\u88f8\uff0c\u770b\u4e0a\u53bb\u6709\u7740\u7279\u522b\u52a8\u4eba\u7684\u611f\u89c9\u3002 \u3000\u3000\u8fd9\u662f\u5973\u4eba\u4e2d\u53e6\u5916\u4e00\u4e2a\u5178\u578b\uff0c\u5145\u6ee1\u7740\u4e00\u79cd\u91ce\u6027\u7f8e\u3002 \u3000\u3000\u4e00\u79cd\u4f7f\u5165\u8ff7\u60d8\u7684\u5a9a\u6001\u3002 \u3000\u3000\u53f6\u957f\u9752\u5446\u5446\u7684\u671b\u7740\u90a3\u5c11\u5973\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u6d3b\u3002 \u3000\u3000\u5012\u662f\u90a3\u534a\u88f8\u5c11\u5973\u5148\u5f00\u53e3\uff0c\u9053\uff1a\u201c\u4f60\u5728\u770b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u611f\u89c9\u5931\u6001\uff0c\u6025\u9053\uff1a\u201c\u6211\uff0c\u6211\u89c9\u7740\u6709\u4e9b\u5947\u602a?\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a \u201c\u54a6!\u4f60\u8dd1\u5230\u4e86\u6211\u4f4f\u7684\u5730\u65b9\uff0c\u8fd8\u89c9\u7740\u5947\u602a\uff0c\u6709\u4ec0\u4e48\u597d\u5947\u7684?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5728\u4e0b\u4e4b\u610f\u662f\u8bf4\uff0c\u4f60\u6bcf\u5929\u5c31\u8fd9\u6837?\u201d \u3000\u3000\u534a\u88f8\u5c11\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u600e\u4e48\u6837?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u4e00\u5e74\u56db\u5b63\uff0c\u5c31\u53ea\u7a7f\u8fd9\u70b9\u8863\u670d\u5417?\u201d \u3000\u3000\u534a\u88f8\u5c11\u5973\u9053\uff1a\u201c\u4f60\u770b\u8fc7\u6c34\u4e2d\u7684\u9c7c\u5417\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f53\u7136\u89c1\u8fc7\u3002\u201d \u3000\u3000\u534a\u88f8\u5c11\u5973\u9053\uff1a \u201c\u9c7c\u5728\u6c34\u4e2d\u6e38\u8d70\uff0c\u4e0d\u7ba1\u5929\u5bd2\u5730\u51bb\uff0c\u5b83\u4eec\u5c31\u4e0d\u7a7f\u8863\u670d\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u4eba\u600e\u4e48\u80fd\u548c\u9c7c\u6bd4?\u201d \u3000\u3000\u534a\u88f8\u5c11\u5973\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u548c\u5b83\u4eec\u4e00\u6837\uff0e\u7ec8\u65e5\u5728\u6c34\u4e2d\u751f\u6d3b\uff0c\u6240\u4ee5\uff0c\u5c31\u4e0d\u7528\u7a7f\u8863\u773c\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53c8\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u7b80\u76f4\u662f\u4e00\u4ef6\u4e0d\u53ef\u601d\u8bae\u7684\u7387\u3002 \u3000\u3000\u4eba\uff0c\u600e\u80fd\u548c\u9c7c\u4e00\u6837\uff0c\u7ec8\u65e5\u5728\u6c34\u4e2d\u751f\u6d3b\u3002 \u3000\u3000\u90a3\u534a\u88f8\u5c11\u5973\u4e3e\u624b\u7406\u4e00\u4e0b\u9b13\u8fb9\u7684\u957f\u53d1\uff0c\u63a5\u9053\uff1a \u201c\u4f60\u77e5\u9053\uff0c\u6211\u4e3a\u4ec0\u4e48\u5bf9\u4f60\u5f88\u5ba2\u6c14\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u6447\u6447\u5934\u3002 \u3000\u3000\u534a\u88f8\u5c11\u5973\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u6211\u521a\u624d\u5077\u5403\u4e86\u4f60\u4eec\u7684\u4e1c\u897f\uff0c\u800c\u4e14\uff0c\u53c8\u62ff\u4e86\u5979\u4eec\u7684\u8863\u670d\uff0c\u5509!\u6709\u4e00\u6b21\uff0c\u6211\u5230\u96c6\u4e0a\u53bb\u4e70\u4e1c\u897f\uff0c\u5f88\u591a\u4eba\u770b\u5230\u6211\uff0c\u4e0d\u662f\u5413\u7684\u56de\u5934\u5c31\u8dd1\uff0c\u5c31\u662f\u50cf\u4f60\u4e00\u6837\uff0c\u77aa\u7740\u773c\u775b\u770b\u6211\uff0c\u8fd8\u6709\u4e9b\u4eba\u5927\u558a\u5927\u53eb\uff0c\u542c\u5f97\u6211\u597d\u70e6\uff0c\u597d\u70e6\uff0c\u6240\u4ee5\uff0c\u6211\u53c8\u8dd1\u4e86\u56de\u6765\uff0c\u4e00\u76f4\u4e0d\u6562\u518d\u89c1\u522b\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u4eec\u53eb\u4f60\u4ec0\u4e48?\u201d \u3000\u3000\u534a\u88f8\u5c11\u5973\u9053\uff1a\u201c\u6709\u4eba\u53eb\u6211\u5996\u602a\uff0c\u4e5f\u6709\u4eba\u53eb\u6211\u7f8e\u4eba\u9c7c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u534a\u88f8\u5c11\u5973\u9053\uff1a \u201c\u5bf9\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4e00\u76f4\u60f3\u4e86\u5f88\u4e45\uff0c\u89c9\u7740\u4ed6\u4eec\u957f\u7684\u548c\u6211\u5e76\u6ca1\u6709\u5f88\u5927\u7684\u4e0d\u540c\uff0c \u53ea\u4e0d\u8fc7\uff0c \u4ed6\u4eec\u50cf\u4f60\u4e00\u6837\uff0c\u7a7f\u7740\u8863\u670d\u7f62\u4e86\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u5c31\u5077\u4e86\u5979\u4eec\u7684\u8863\u670d?\u201d \u3000\u3000\u534a\u88f8\u5c11\u5973\u9053\uff1a\u201c\u5979\u4eec\u7684\u8863\u670d\u5f88\u597d\u770b\uff0c\u6240\u4ee5\uff0c\u6211\u5c31\u53d6\u6765\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u987a\u4fbf\u4e5f\u53d6\u8d70\u4e86\u5979\u4eec\u7684\u9497\u73af\u3002\u201d \u3000\u3000\u534a\u88f8\u5c11\u5973\u9053\uff1a \u201c\u55ef!\u8fd8\u6709\u5f88\u591a\u63d2\u5728\u5934\u4e0a\u7684\u4e1c\u897f\uff0c \u5509!\u4e0d\u8fc7\uff0c\u6211\u4e0d\u4f1a\u4f7f\u7528\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u59d1\u5a18\uff0c \u4f60\u600e\u4e48\u751f\u6d3b\u7684\uff1f\u201d \u3000\u3000\u534a\u88f8\u5c11\u5973\u9053\uff1a\u201c\u4f60\u662f\u95ee\u6211\u5403\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5bf9\uff01\u59d1\u5a18\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u5403\u4e9b\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u534a\u88f8\u5c11\u5973\u9053\uff1a \u201c\u8fd9\u6e56\u4e2d\u6709\u592a\u591a\u7684\u4e1c\u897f\u53ef\u4ee5\u5403\u3002\u201d \u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u9053\uff1a\u770b\u6837\u5b50\uff0c\u5979\u4e00\u76f4\u751f\u6d3b\u5728\u592a\u6e56\u4e4b\u4e2d\uff0c\u4f46\u5979\u53e3\u9f7f\u6e05\u6670\uff1a\u4e0d\u50cf\u4e00\u76f4\u548c\u9f9f\u867e\u4e3a\u4f0d\u7684\u6837\u5b50\u3002\u5fc5\u7136\u8fd8\u6709\u9047\u5408\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c \u53e3\u4e2d\u8bf4\u9053\uff1a \u201c\u59d1\u7405\u80fd\u591f\u5728\u753b\u822b\u4e4b\u4e2d\uff0c\u8f7b\u6613\u53d6\u8d70\u4e86\u7389\u5170\u53cc\u59dd\u7684\u8863\u670d\uff0c\u7adf\u4f7f\u5979\u4eec\u6ca1\u6709\u53d1\u89c9\uff0c\u8fd9\u4e00\u4efd\u8eab\u624b\uff0c\u5b9e\u5728\u9ad8\u660e\u3002\u201d \u3000\u3000\u534a\u88f8\u5c11\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u9ad8\u660e\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5728\u4e0b\u7684\u610f\u601d\u662f\u8bf4\uff0c\u59d1\u5a18\u7684\u4e00\u8eab\u6b66\u529f\u4e0d\u9519\u554a!\u201d\uff0c\u534a\u88f8\u5c11\u5973\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u59e5\u59e5\u4e5f\u8fd9\u4e48\u8bf4\uff0c\u6211\u7684\u6b66\u529f\u4e0d\u9519\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u59e5\u59e5\u662f\u8c01\uff1f\u201d \u3000\u3000\u534a\u88f8\u5c11\u5973\u9053\uff1a\u201c\u59e5\u59e5\u5c31\u662f\u59e5\u59e5\uff0c\u5979\u4e00\u76f4\u7167\u987e\u6211\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u8fd9\u4e00\u8eab\u6b66\u529f\uff0c\u4e5f\u662f\u59e5\u59e5\u6559\u7ed9\u4f60\u7684?\u201d \u3000\u3000\u534a\u88f8\u5c11\u5973\u70b9\u70b9\u5934\uff0c \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u59d1\u5a18\u3001\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57?\u201d \u3000\u3000\u534a\u88f8\u5c11\u5973\u6c89\u541f\u4e86\u2014\u9635\uff0c\u9053\uff1a\u201c\u59e5\u59e5\u4e00\u5411\u53eb\u6211\u5c0f\u971e\u513f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u539f\u6765\u662f\u5c0f\u971e\u59d1\u5a18\u3002\u201d \u3000\u3000\u5c0f\u971e\u513f\u9053\uff1a\u201c\u4f60\u95ee\u4e86\u6211\u534a\u5929\uff0c\u6211\u8fd8\u6ca1\u6709\u95ee\u8fc7\u4f60\u5462!\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u59d1\u5a18\u8981\u95ee\u4ec0\u4e48?\u201d \u3000\u3000\u5c0f\u971e\u513f\u9053\uff1a \u201c\u8fd9\u672c\u662f\u4e00\u5ea7\u5f88\u8352\u51c9\u7684\u5c0f\u5c9b\uff0c \u4f60\u4e3a\u4ec0\u4e48\u8981\u6765\u8fd9\u91cc?\u201d \u3000\u3000\u53f6\u957f\u9752\u6697\u9053\uff1a\u542c\u5979\u4e4b\u8a00\uff0c\u5e76\u975e\u662f\u5168\u4e0d\u61c2\u4e8b\uff0c\u800c\u4e14\uff0c\u8fd8\u6709\u4e00\u4f4d\u59e5\u59e5\uff0c\u90a3\u5b9a\u662f\u4e00\u4f4d\u6c5f\u6e56\u9ad8\u4eba\uff0c\u5bf9\u7b54\u4e4b\u95f4\u5012\u662f\u4e0d\u80fd\u592a\u8fc7\u5927\u610f\uff0e\u81f3\u5c11\u4e0d\u80fd\u8981\u5979\u4eec\u628a\u6211\u4eec\u770b\u4f5c\u654c\u4eba\u3002\u4e00\u60f3\u4e00\u60f3\uff0c\u53f9\u53e3\u6c14\uff0c\u9053\uff1a \u3000\u3000\u201c\u54b1\u4eec\u4e5f\u662f\u60c5\u975e\u5f97\u5df2\uff0c\u88ab\u4eba\u903c\u4e0a\u4e86\u8fd9\u91cc\u3002\u201d \u3000\u3000\u5c0f\u971e\u513f\u9053\uff1a\u201c\u5c9b\u5916\u9762\uff0c\u6709\u5f88\u591a\u8239\uff0c\u5c31\u662f\u903c\u4f60\u4eec\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ed6\u4eec\u4eba\u6570\u4f17\u591a\uff0c\u6b66\u529f\u4e5f\u9ad8\u5f3a\uff0c\u201d \u3000\u3000\u5c0f\u971e\u513f\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u73b0\u5728\uff0c\u591c\u8272\u6df1\u6d53\uff0c\u800c\u4e14\uff0c\u6e56\u4e0a\u6709\u96fe\uff0c\u5929\u4eae\u4e4b\u540e\uff0c\u53ea\u6015\u4ed6\u4eec\u4f1a\u5411\u8fd9\u5c9b\u4e0a\u653b\u51fb\u3002\u201d \u3000\u3000\u5c0f\u971e\u513f\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u5728\u4e0b\u7b49\u7eb5\u7136\u6218\u6b7b\u6b64\u5730\uff0c\u90a3\u662f\u548e\u7531\u81ea\u53d6\uff0c\u4f46\u5982\u56e0\u6b64\u88ab\u4ed6\u4eec\u53d1\u89c9\u4e86\u59d1\u5a18\u7684\u5b58\u8eab\u4e4b\u5904\u60ca\u6270\u5230\u6e05\u9759\u7684\u751f\u6d3b\uff0c\u5728\u4e0b\u7b49\u5c31\u751a\u611f\u4e0d\u5b89\u4e86\u3002\u201d \u3000\u3000\u5c0f\u971e\u513f\u7728\u52a8\u4e86\u4e00\u4e0b\u4eae\u5982\u5bd2\u661f\u7684\u53cc\u76ee\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u7b49\u4e00\u7b49\uff0c\u6211\u53bb\u95ee\u59e5\u59e5\u3002\u201d \u3000\u3000\u7a81\u7136\u8f6c\u8eab\u4e00\u8dc3\uff0c\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u53f6\u957f\u9752\u6697\u6697\u5403\u60ca\u9053\uff1a\u597d\u5feb\u7684\u8eab\u6cd5\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u8ffd\u8e2a\u67e5\u770b\uff0c\u53ea\u662f\u9759\u9759\u7684\u7ad9\u7740\u3002 \u3000\u3000\u5c0f\u971e\u513f\u53bb\u7684\u5feb\uff0c\u56de\u6765\u7684\u4e5f\u5f88\u5feb\uff0c \u53ea\u4e0d\u8fc7\u7247\u523b\u4eba\u5df2\u56de\u5230\u539f\u5904\u3002 \u7b11\u9053\uff1a\u201c\u59e5\u59e5\u8981\u6211\u95ee\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5728\u4e0b\u53f6\u957f\u9752\u3002\u201d \u3000\u3000\u9752\u5b57\u843d\u53e3\uff0c\u5c0f\u971e\u5df2\u8e2a\u5f71\u4e0d\u89c1\uff0c \u3000\u3000\u8fd9\u4f4d\u534a\u88f8\u7684\u59d1\u5a18\uff0c\u8eab\u6cd5\u4e4b\u5feb\uff0c\u5b9e\u5df2\u5230\u4e86\u4e00\u53bb\u5982\u98ce\u7684\u5883\u754c\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u5ff5\u5934\u8fd8\u672a\u8f6c\u5b9a\u5c0f\u971e\u53c8\u98d8\u7136\u800c\u81f3\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59e5\u59e5\u8bf4\u5979\u4e0d\u77e5\u9053\u4f60\u662f\u597d\u4eba\u8fd8\u662f\u574f\u4eba?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u751f\u5f97\u665a\uff0c\u59d1\u5a18\u59e5\u59e5\uff0c\u81ea\u7136\u4e0d\u8bc6\u3002\u201d \u3000\u3000\u5c0f\u971e\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59e5\u59e5\u4e0d\u8ba4\u8bc6\u4f60!\u4f46\u4f60\u81ea\u5df1\u603b\u8be5\u77e5\u9053\u662f\u597d\u4eba\uff0c\u8fd8\u662f\u574f\u4eba\u5427?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u8bf7\u59d1\u5a18\u4e0a\u8986\u59e5\u59e5\uff0c\u5c31\u8bf4\u5728\u4e0b\u884c\u4e0d\u62d8\u793c\uff0c\u5fc3\u5b58\u65e5\u6708\u3002\u201d \u5c0f\u971e\u9053\uff1a\u201c\u54e6!\u884c\u4e0d\u62d8\u793c\uff0c\u5fc3\u5b58\u65e5\u6708\uff0c\u7a76\u7adf\u662f\u597d\u4eba\uff0c\u8fd8\u662f\u574f\u4eba\u5462?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u60f3\uff0c\u59e5\u59e5\u81ea\u7136\u4e86\u89e3\u4e2a\u4e2d\u4e4b\u610f\u3002\u201d \u3000\u3000\u5c0f\u971e\u70b9\u70b9\u5934\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u5c0f\u971e\u53bb\u800c\u590d\u8fd4\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u59e5\u59e5\u8bf4\uff0c\u4f60\u53ef\u4ee5\u56de\u53bb\u4e86\uff0c\u522b\u5bc4\u671b\u4ec0\u4e48\uff0c\u5979\u4f1a\u91cf\u60c5\u800c\u4e3a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u544a\u8f9e\u3002\u201d \u3000\u3000\u5c0f\u971e\u9053\uff1a\u201c\u59e5\u59e5\u8fd8\u8981\u6211\u544a\u8bc9\u4f60\uff0c\u4e0d\u8981\u8bf4\u51fa\u4f60\u89c1\u5230\u6211\u4eec\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u9053\uff1a\u201c\u597d\uff0c\u5728\u4e0b\u4e0d\u63d0\u3002\u201d \u3000\u3000\u8f6c\u8eab\u884c\u53bb\uff0c\u6500\u7d22\u800c\u4e0a\u3002 \u3000\u3000\u7389\u5170\u53cc\u59dd\uff0c\u5df2\u5728\u6d1e\u53e3\u5904\u7b49\u4faf\uff0c\u53f6\u957f\u9752\u53ea\u8981\u518d\u665a\u4e0a\u4e00\u523b\uff0c\u4e24\u4eba\u4fbf\u8981\u4e0b\u53bb\u67e5\u770b\u4e86\u3002 \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u53f6\u5144\uff0c\u770b\u5230\u4e86\u4ec0\u4e48?\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d70!\u54b1\u4eec\u56de\u753b\u822b\u53bb\uff0c\u518d\u4f5c\u8ba1\u8bae\u3002\u201d \u3000\u3000\u4e09\u4eba\u6500\u4e0a\u5cf0\u9876\uff0c\u89e3\u53bb\u7d22\u7ef3\u3002 \u3000\u3000\u4e01\u5cf0\u3001\u9a6c\u826f\uff0c\u65e9\u5df2\u5728\u5cf0\u9876\u7b49\u5019\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u60c5\u52bf\u53ef\u6709\u53d8\u5316?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a \u201c\u54b1\u4eec\u5931\u6563\u7684\u4e24\u8258\u5c0f\u821f\uff0c\u5df2\u9760\u5cb8\u767b\u9646\uff0c\u56db\u4e2a\u4eba\u6e38\u6c34\u800c\u4e0a\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u597d!\u8fd8\u6709\u591a\u5c11\u5931\u8e2a\u4eba\u624b?\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a \u201c\u5931\u8e2a\u4e09\u4eba\uff0c\u4f46\u591a\u4e86\u4e00\u4e2a\u590f\u6740\uff0c\u54b1\u4eec\u7684\u5b9e\u529b\u5e76\u672a\u5f71\u54cd\u3002\u201d \u53f6\u957f\u9752\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a \u201c\u8981\u4ed6\u4eec\u597d\u597d\u4f11\u606f\u2014\u4e0b\uff0c\u5929\u4eae\u4e4b\u540e\uff0c\u7b49\u5019\u884c\u4f7f\u4ee4\u8c15\u3002\u201d \u4e01\u5cf0\u9053\uff1a\u201c\u5929\u4eae\u4e4b\u540e\uff0c\u654c\u4eba\u6765\u653b\uff0c\u8840\u6218\u96be\u514d\uff0c\u5251\u4f7f\u4e3b\u6301\u5927\u5c40\uff0c\u4e0d\u5b9c\u592a\u8fc7\u64cd\u52b3\uff0c\u8d81\u65f6\u95f4\u8fd8\u65e9\uff0c\u5251\u4f7f\u6700\u597d\u80fd\u4f11\u606f\u4e00\u4e0b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u4eec\u4e5f\u8981\u4fdd\u6301\u4f53\u80fd\uff0c\u501f\u673a\u5750\u606f\u4e00\u4e0b\u3002\u201d \u3000\u3000\u4e01\u5cf0\u7b11\u9053\uff1a \u201c\u90a3\u4e00\u7247\u6d45\u6ee9\uff0c \u51e0\u4e4e\u5c31\u53ef\u9a70\u4e0a\u5c0f\u821f\uff0c \u5c5e\u4e0b\u51c6\u5907\u5728\u6c34\u4e0a\u5e03\u7f6e\u4e00\u4e9b\u969c\u788d\u2026\u2026\u201d \u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u6709\u56f0\u96be\u5417\uff1f\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a \u201c\u7269\u5177\u4e0d\u9f50\uff0c \u53ea\u80fd\u56e0\u964b\u5c31\u7b80\uff0c\u5927\u56f0\u6ca1\u6709\uff0c\u81f3\u5c11\u53ef\u4ee5\u8fdf\u5ef6\u4e00\u4e0b\u4ed6\u4eec\u7684\u884c\u52a8\u3002\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a \u201c\u54b1\u4eec\u53c8\u56de\u6765\u4e86\u5341\u51e0\u4e2a\u4eba\uff0c\u5b9e\u529b\u5927\u589e\uff0c \u4eba\u624b\u8f83\u591a\uff0c\u53c8\u5728\u4e00\u5904\u5c0f\u89d2\u4e2d\uff0c\u53d1\u73b0\u4e86\u4e0d\u5c11\u7fe0\u7af9\u3001\u6811\u6728\uff0c\u5c5e\u4e0b\u51c6\u5907\u5236\u9020\u4e00\u4e9b\u7b80\u5355\u7684\u5f13\u7bad\u3001\u64c2\u77f3\uff0c\u5728\u5cf0\u524d\u5404\u53e3\u5904\uff0c \u5e03\u7f6e\u5382\u4e00\u9053\u9632\u5b88\u4e4b\u9635\uff0c\u5fc5\u8981\u65f6\uff0c\u9000\u5b88\u5c71\u4e0b\uff0c\u636e\u9669\u4ee5\u6297\u3002\u201d \u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u597d\uff01\u867d\u7136\u6211\u76f8\u4fe1\u5821\u4e3b\u5fc5\u6709\u5b8c\u7f8e\u7684\u63a5\u5e94\u90e8\u7f72\uff0c\u601b\u54b1\u4eec\u4e0d\u80fd\u5fc3\u5b58\u4fa5\u5e78\uff0c\u5c3d\u91cf\u5229\u7528\u54b1\u4eec\u7684\u80fd\u529b\uff0c\u51c6\u5907\u51b3\u2014\u6b7b\u6218\u3002\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u5251\u4f7f\u653e\u5fc3\uff0c\u5c5e\u4e0b\u5144\u5f1f\u4eec\u90fd\u6709\u5fc3\u6b7b\u4e4b\u5fc3\uff0c\u654c\u52bf\u867d\u5927\uff0c\u4e5f\u4e0d\u4f1a\u751f\u51fa\u754f\u60e7\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u6e56\u9762\u7684\u6d53\u96fe\uff0c\u5e2e\u4e86\u53f6\u957f\u9752\u7b49\u5f88\u5927\u7684\u5fd9\uff0c \u76f4\u5230\u65e5\u4e0a\u4e09\u7aff\uff0c\u6d53\u96fe\u624d\u9010\u6e10\u6563\u53bb\u3002 \u4ed6\u4eec\u4e00\u591c\u5fd9\u788c\uff0c\u603b\u7b97\u6709\u4e00\u4e2a\u591a\u65f6\u8fb0\u7684\u4f11\u606f\u3002 \u3000\u3000\u91d1\u8272\u7684\u9633\u5149\uff0c\u9010\u8d70\u4e86\u6d53\u96fe\uff0c\u4e5f\u7167\u4eae\u4e86\u6e56\u9762\u3002 \u3000\u3000\u53f6\u957f\u9752\u5e26\u7740\u4e01\u5cf0\u3001\u9a6c\u826f\u3001\u7389\u5170\u53cc\u59dd\u548c\u590f\u6740\uff0c\u7ad9\u5728\u6e56\u7554\uff0c\u671b\u7740\u4e24\u8258\u53cc\u6845\u5de8\u5e06\u3001\u5728\u4e24\u8258\u5927\u8239\u7684\u56db\u5468\uff0e\u5c0f\u821f\u5f80\u6765\u5c06\u8fd1\u4e24\u767e\u8258\u4e4b\u591a\u3002 \u3000\u3000\u4e24\u8258\u5927\u8239\uff0c\u8ddd\u5cb8\u7ea6\u5728\u767e\u4e08\u4e4b\u5916\uff0c\u4f46\u89c1\u72c2\u9f99\u3001\u98de\u9e70\u65d7\u5728\u6e56\u98ce\u4e2d\u98de\u821e\u3001\u62db\u5c55\u3002\u8fd1\u767e\u8258\u7a7f\u68ad\u5c0f\u821f\uff0c\u5374\u6563\u5e03\u6210\u6247\u9762\u5f62\uff0c\u5305\u56f4\u4e01\u8fd9\u4e2a\u5c0f\u5c9b\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5341\u51e0\u53ea\u5c0f\u821f\uff0c \u5df2\u5f00\u59cb\u5411\u5c9b\u4e0a\u5212\u6765\u3002 \u3000\u3000\u4e01\u5cf0\u4f4e\u58f0\u9053\uff1a \u201c\u5251\u4f7f\uff0c\u72c2\u9f99\u3001\u98de\u9e70\uff0c\u90fd\u662f\u5f88\u96be\u5bf9\u4ed8\u7684\u4eba\u7269\uff0c\u662f\u5148\u8981\u57cb\u4f0f\u7684\u7af9\u7bad\u5f13\u624b\u6321\u4ed6\u4eec\u4e00\u9635\uff0c\u6216\u662f\u7b49\u4e24\u4eba\u767b\u5cb8\u4e4b\u540e\uff0c\u96c6\u4e2d\u5bf9\u4ed8\u4ed6\u4eec\u4e24\u4e2a\u4eba?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u72c2\u9f99\u3001\u98de\u9e70\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u7af9\u7bad\u5982\u4f55\u80fd\u4f24\u5f97\u4e86\u4ed6\u4eec\u3002\u201d \u4e01\u5cf0\u9053\uff1a \u201c\u5c5e\u4e0b\u65e9\u5df2\u601d\u8651\u53ca\u6b64\uff0c\u6240\u4ee5\uff0c\u5728\u90a3\u7fa4\u5f29\u7bad\u624b\u4e2d\uff1a\u6df7\u5165\u4e86\u4e00\u4e9b\u771f\u6b63\u7684\u6697\u5668\u9ad8\u624b\uff0c\u5c04\u4eba\u5c04\u9a6c\uff0c\u64d2\u8d3c\u64d2\u738b\uff0c\u53ea\u8981\u8fd9\u4e24\u4e2a\u4eba\u53d7\u4f24\uff0c\u4f59\u5b50\u788c\u788c\uff0c\u5c31\u4e0d\u8db3\u6302\u9f7f\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u6700\u597d\uff0c\u5148\u7a33\u4f4f\u4ed6\u4eec\uff0c\u65f6\u95f4\u5bf9\u54b1\u4eec\u6709\u5229\uff0c\u5982\u82e5\u4e00\u5f00\u59cb\u5c31\u662f\u6df7\u6218\uff0c\u654c\u52bf\u5f3a\u5927\uff0c \u54b1\u4eec\u7eb5\u6709\u4e00\u4e9b\u57cb\u4f0f\uff0c\u53ea\u6015\u4e5f\u65e0\u6cd5\u5236\u654c\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5251\u4f7f\u7684\u610f\u601d\u5462?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6700\u597d\u662f\u6211\u51fa\u9762\u5411\u4ed6\u4eec\u4e24\u4eba\u4e4b\u2014\u6311\u6218\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a \u201c\u5251\u4f7f\uff0c\u72c2\u9f99\u3001\u98de\u9e70\u4e24\u4e2a\u4eba\uff0c\u90fd\u662f\u6b66\u6797\u4e2d\u51f6\u540d\u5353\u8457\u7684\u4eba\u7269\uff0c\u5404\u6709\u5f88\u7279\u6b8a\u7684\u6b66\u529f\uff0c\u8fd9\u4e00\u6218\u53ea\u6015\u662f\u51f6\u9669\u4e07\u7aef\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a \u201c\u6211\u5982\u4e0d\u80fd\u5148\u8e48\u5371\u5883\uff0c\u5982\u4f55\u80fd\u5bf9\u5f97\u8d77\u8fd9\u4e9b\u8ddf\u968f\u6211\u4eec\u7684\u5144\u5f1f\u3002\u201d \u4e01\u5cf0\u671b\u671b\u9a6c\u826f\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5341\u51e0\u8258\u5c0f\u821f\uff0c\u5df2\u7ecf\u9760\u8fd1\u4e86\u5cb8\uff0c\u6bcf\u4e00\u8258\u5c0f\u821f\u4e0a\uff0c\u90fd\u6709\u4e09\u4e2a\u4eba\uff0c \u4e00\u4e2a\u6447\u6a79\uff0c\u4e24\u4e2a\u8eab\u7740\u52ab\u88c5\uff0c\u4f69\u5e26\u7740\u5175\u5203\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u8ddd\u79bb\u5cb8\u7554\u4e94\u4e08\u5de6\u53f3\u5904\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u867d\u7136\uff0c\u5cb8\u4e0a\u7684\u57cb\u4f0f\u5341\u5206\u79d8\u5bc6\uff0c\u4f46\u5306\u5306\u90e8\u7f72\u800c\u6210\uff0c\u9690\u9690\u95f4\uff0c\u53ef\u89c1\u5f62\u8ff9\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u8fd9\u4e5f\u662f\u4e01\u5cf0\u4e00\u79cd\u7b56\u7565\u7684\u8fd0\u7528\u3002 \u3000\u3000\u4f7f\u5f97\u5bf9\u65b9\u80fd\u77a7\u5230\u57cb\u4f0f\u7684\u75d5\u8ff9\uff0c\u81f3\u5c11\uff0c\u53ef\u4ee5\u4f7f\u5f97\u654c\u6709\u4e9b\u987e\u5fcc\u3002 \u3000\u3000\u679c\u7136\uff0c\u8fd9\u4e00\u7740\uff0c\u53d1\u751f\u4e00\u4e9b\u5413\u963b\u7684\u529b\u91cf\u3002 \u3000\u3000\u81f3\u5c11\uff0c\u90a3\u4e9b\u5c0f\u8239\uff0c\u5c31\u6ca1\u6709\u5f88\u76f4\u63a5\u7684\u51b2\u4e0a\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e3e\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u52a8\uff0c\u4e01\u5cf0\u3001\u9a6c\u826f\u3001\u7389\u5170\u53cc\u59dd\u3001\u590f\u6740\uff0c\u90fd\u8ddf\u7740\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u5df2\u7ecf\u884c\u5230\u4e86\u5cb8\u8fb9\u3002 \u3000\u3000\u4e24\u8258\u9ad8\u6311\u72c2\u9f99\u3001\u98de\u9e70\u7684\u5927\u65d7\uff0c\u4e5f\u9a70\u8fd1\u6e56\u5cb8\u3002 \u3000\u3000\u5df2\u7ecf\u63a5\u8fd1\u4e86\u4e01\u5cf0\u5e03\u4e0b\u7684\u6c34\u5e95\u6697\u7901\uff0c\u5927\u8239\u7a81\u7136\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53cc\u65b9\u7684\u8ddd\u79bb\uff0c\u4e5f\u4e0d\u8fc7\u662f\u4e09\u4e08\u5de6\u53f3\u3002 \u3000\u3000\u53f6\u957f\u9752\u56de\u987e\u5feb\u6b65\u8ffd\u5230\u8eab\u4fa7\u7684\u4e01\u5cf0\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e01\u5cf0\uff0c\u53bb\uff0c\u8bf7\u72c2\u9f99\u6216\u98de\u9e70\u7b54\u8bdd\u3002\u201d \u3000\u3000\u4e01\u5cf0\u5e94\u4e86\u4e00\u58f0\uff0c\u884c\u524d\u4e24\u6b65\uff0c\u4e00\u62f1\u624b\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a \u201c\u9634\u9633\u5821\u53f6\u5251\u4f7f\uff0c\u8bf7\u8d35\u65b9\u4e3b\u9996\u4eba\u5458\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u53ea\u89c1\u4e24\u8258\u5927\u8239\u4e0a\uff0c\u5404\u81ea\u884c\u51fa\u4e00\u4e2a\u957f\u886b\u4e2d\u5e74\u4eba\u3002 \u3000\u3000\u72c2\u9f99\u65d7\u4e0b\u7684\u4e2d\u5e74\u5927\u6c49\uff0c\u5148\u5f00\u53e3\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u53f6\u5251\u4f7f?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u5e38\u603b\u7ba1\uff0c\u4f46\u4e0d\u77e5\u8d35\u5821\u5821\u4e3b\u53ef\u5728?\u201d \u3000\u3000\u5e38\u603b\u7ba1\u9053\uff1a\u201c\u5728\uff0c\u5c31\u5728\u8fd9\u5927\u8239\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u597d\uff0c\u672c\u5821\u7684\u53f6\u5251\u4f7f\u8bf7\u4ed6\u7b54\u8bdd\u3002\u201d \u3000\u3000\u5e38\u603b\u7ba1\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c0f\u5c0f\u4e00\u4e2a\u5251\u4f7f\uff0c\u6709\u4ec0\u4e48\u8eab\u4efd\u8bf7\u672c\u5821\u4e3b\u7b54\u8bdd\u3002\u201d \u3000\u3000\u4e01\u5cf0\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u72c2\u7684\u53e3\u6c14\u3002\u201d \u3000\u3000\u5e38\u603b\u7ba1\u9053\uff1a\u201c\u4f55\u6b62\u662f\u72c2\uff0c\u800c\u4e14\u662f\u8ffd\u547d\u7684\u960e\u7f57\uff0c\u4e0d\u4f1a\u8d85\u8fc7\u5348\u65f6\uff0c\u4f60\u4eec\u90fd\u5c06\u8840\u6e85\u592a\u6e56\u3002\u201d \u3000\u3000\u4e01\u5cf0\u4ef0\u5929\u5927\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u51ed\u72c2\u9f99\u5821\u90a3\u70b9\u5b9e\u529b\u5417?\u201d \u3000\u3000\u98de\u9e70\u65d7\u8239\u4e0a\u7684\u4e2d\u5e74\u4eba\u51b7\u7136\u63a5\u9053\uff1a \u201c\u9601\u4e0b\u522b\u5fd8\u4e86\uff0c\u8fd8\u6709\u98de\u9e70\u5821\u7684\u4eba\u624b\u5728\u6b64\u3002\u201d \u5e38\u603b\u7ba1\u56de\u987e\u4e86\u98de\u9e70\u8239\u4e0a\u7684\u4e2d\u5e74\u4eba\u4e00\u773c\uff0c\u63a5\u9053\uff1a \u201c\u5bf9\u4ed8\u9634\u9633\u5821\u4e2d\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5251\u4f7f\uff0c\u7528\u4e0d\u7740\u98de\u9e70\u5821\u4e2d\u4eba\u624b\u51fa\u52a8\uff0c\u5355\u51ed\u72c2\u9f99\u5821\u4e2d\u4eba\u624b\uff0c\u5df2\u7ecf\u53ef\u4ee5\u5bf9\u4ed8\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u4eba\u4e00\u76b1\u7709\u5934\uff0c\u63a5\u9053\uff1a\u201c\u5e38\u5144\u7684\u610f\u601d\u662f\uff0c\u7528\u4e0d\u7740\u54b1\u4eec\u98de\u9e70\u5821\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u5e38\u603b\u7ba1\u9053\uff1a \u201c\u655d\u5821\u4e3b\u7684\u610f\u601d\u662f\uff0c\u5982\u82e5\u72c2\u9f99\u3001\u98de\u9e70\u4e24\u5821\u8054\u5408\uff0c\u5bf9\u4ed8\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5251\u4f7f\uff0c\u4f20\u626c\u5230\u6c5f\u6e56\u4e4b\u4e0a\u53ea\u6015\u6d41\u4e3a\u7b11\u67c4\uff0c\u66f2\u603b\u7ba1\u4e0d\u8981\u8bef\u4f1a\u624d\u597d\u3002\u201d \u3000\u3000\u66f2\u603b\u7ba1\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u597d!\u54b1\u4eec\u4e0d\u51fa\u624b\uff0c\u53ea\u5e0c\u671b\u72c2\u9f99\u5821\u4e0d\u8981\u8d25\u4e0b\u9635\u6765\u3002\u201d \u3000\u3000\u5e38\u603b\u7ba1\u9053\uff1a \u201c\u8fd9\u4e00\u70b9\uff0c\u66f2\u5144\u53ef\u4ee5\u653e\u5fc3\uff0c\u72c2\u9f99\u5821\u82e5\u8fd8\u5bf9\u4ed8\u4e0d\u4e86\u9634\u9633\u4e2d\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5251\u4f7f\uff0c\u90a3\u5c82\u4e0d\u662f\u4e00\u573a\u7b11\u8bdd\u5417?\u201d \u3000\u3000\u66f2\u603b\u7ba1\u9053\uff1a\u201c\u5e0c\u671b\u83ab\u8981\u95f9\u51fa\u7b11\u8bdd\u624d\u597d\u3002\u201d \u3000\u3000\u4e0d\u8981\u7406\u5e38\u603b\u7ba1\uff0c\u8f6c\u8eab\u9000\u5165\u8231\u4e2d\u3002 \u3000\u3000\u540c\u65f6\uff0c\u98de\u9e70\u65d7\u8239\uff0c\u4e5f\u7f13\u7f13\u5411\u540e\u9000\u53bb\uff0c\u76f4\u5230\u56db\u4e94\u5341\u4e08\u5916\u624d\u505c\u4e0b\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u8c08\u8bdd\u7684\u58f0\u97f3\u5f88\u9ad8\uff0c\u53f6\u957f\u9752\u7b49\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u72c2\u9f99\u5821\u9a84\u72c2\u81ea\u5927\uff0c\u98de\u9e70\u95e8\u4e0b\u4eba\uff0c\u521a\u610e\u81ea\u7528\uff0c\u5fcd\u4e0d\u4e0b\u8fd9\u53e3\u6c14\uff0c\u5bf9\u53f6\u957f\u9752\u800c\u8a00\uff0c \u81ea\u7136\u662f\u518d\u597d\u4e0d\u8fc7\uff0c\u5bf9\u4ed8\u4e00\u4e2a\u72c2\u9f99\u5821\uff0c\u603b\u6bd4\u5bf9\u4ed8\u4e24\u6279\u4eba\u8054\u624b\u8f7b\u677e\u3002 \u3000\u3000\u5e38\u603b\u7ba1\u51b7\u7b11\u4e00\u58f0\uff0c \u76ee\u5149\u8f6c\u6ce8\u5230\u4e01\u5cf0\u7684\u8eab\u4e0a\uff0c\u9053\uff1a \u201c\u4f60\u4e0d\u59d3\u53f6\u81ea\u7136\u4e0d\u662f\u5251\u4f7f\uff0c\u53bb\u53eb\u59d3\u53f6\u7684\u6765\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u53f6\u67d0\u4eba\u5728\u6b64\u3002\u201d \u3000\u3000\u5e38\u603b\u7ba1\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u655d\u5821\u4e3b\u6709\u4e00\u53e5\u8bdd\uff0c\u8981\u6211\u8f6c\u544a\u9601\u4e0b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8bf4!\u201d \u3000\u3000\u5e38\u603b\u7ba1\u9053\uff1a\u201c\u5728\u53cc\u65b9\u8fd8\u672a\u52a8\u624b\u4e4b\u524d\uff0c\u4f60\u4eec\u8fd8\u6709\u4e00\u4e2a\u9009\u62e9\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5e38\u603b\u7ba1\u9053\uff1a\u201c\u5982\u82e5\u8bf8\u4f4d\u53ef\u4ee5\u653e\u4e0b\u5175\u5203\uff0c\u655d\u5821\u4e3b\u53ef\u4ee5\u7f51\u5f00\u4e00\u9762\uff0c\u653e\u8bf8\u4f4d\u4e00\u6761\u751f\u8def\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e38\u603b\u7ba1\u7684\u597d\u610f\uff0c\u54b1\u4eec\u5fc3\u9886\u4e86\uff0c\u9634\u9633\u5821\u53ea\u6709\u6218\u6b7b\u82f1\u96c4\uff0c\u6ca1\u6709\u60dc\u547d\u7684\u61e6\u592b\uff0c\u8bf7\u4e0a\u590d\u8d35\u5821\u4e3b\uff0c\u533a\u533a\u4e0d\u624d\uff0c\u60f3\u548c\u4ed6\u5148\u884c\u4e00\u51b3\u751f\u6b7b\u3002\u201d \u3000\u3000\u5e38\u603b\u7ba1\u9053\uff1a\u201c\u5c31\u51ed\u4f60\uff0c\u8981\u5411\u655d\u5821\u4e3b\u6311\u6218\u3002\u201d \u3000\u3000\u5170\u767d\u6012\u9053\uff1a\u201c\u4e03\u5251\u8ffd\u9b42\uff0c\u5728\u6c5f\u6e56\u7684\u540d\u6c14\uff0c\u4e0d\u8f93\u72c2\u9f99\uff0c\u4f60\u4e0d\u804b\uff0c\u4e5f\u4e0d\u778e\uff0c\u96be\u9053\u6ca1\u6709\u542c\u8fc7\u3002\u201d \u3000\u3000\u5e38\u603b\u7ba1\u9053\uff1a\u201c\u4f60\u662f\u7389\u7fe0\uff0c\u8fd8\u662f\u5170\u767d\uff1f\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u6211\u662f\u5170\u767d\uff0c\u600e\u4e48\u6837?\u201d \u3000\u3000\u5e38\u603b\u7ba1\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u59d0\u59b9\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\u8f6c\u8eab\u5411\u8231\u4e2d\u884c\u53bb\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c \u2014\u4e2a\u8eab\u7740\u9ed1\u8863\uff0c \u80f8\u7ee3\u767d\u9f99\u7684\u4e2d\u5e74\u4eba\uff0c \u5728\u5e38\u603b\u7ba1\u968f\u4f8d\u4e0b\uff0c\u884c\u4e0a\u4e86\u7532\u677f\u8239\u5934\u3002 \u53ea\u89c1\u4ed6\u7565\u4e00\u6253\u91cf\u56db\u5468\u7684\u5f62\u52bf\uff0c \u7a81\u7136\u98de\u8eab\u800c\u8d77\uff0c \u8dc3\u8d77\u4e24\u4e08\u591a\u9ad8\uff0c\u8eab\u8eaf\u659c\u843d\uff0c\u8f7b\u98d8\u98d8\u843d\u5728\u53f6\u957f\u9752\u8eab\u524d\u4e03\u516b\u5c3a\u5904\u3002 \u3000\u3000\u5e38\u603b\u7ba1\u7d27\u968f\u8eab\u540e\uff0c\u98de\u843d\u5cb8\u4e0a\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u843d\u540e\u4e86\u56db\u5c3a\u8ddd\u79bb\uff0c \u3000\u3000\u7d27\u63a5\u7740\u4e24\u4e2a\u9752\u8863\u52b2\u88c5\u7684\u5927\u6c49\uff0c \u4e5f\u8dc3\u843d\u5728\u5cb8\u4e0a\u3002 \u4e24\u4eba\u4e00\u843d\u5cb8\u4e0a\uff0c \u7acb\u523b\u5206\u522b\u7ad9\u5728\u4e86\u9ed1\u8863\u4eba\u7684\u8eab\u540e\u3002 \u9ed1\u8863\u4eba\u6253\u91cf\u4e86\u53f6\u957f\u9752\u4e24\u773c\u3001\u6de1\u7136\u8bf4\u9053\uff1a \u201c\u4f60\u5c31\u662f\u4e03\u5251\u8ffd\u9b42\u53f6\u957f\u9752\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u6b63\u662f\u533a\u533a\uff0c\u9601\u4e0b\u662f\u72c2\u9f99\u5821\u4e3b\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5bf9\uff0c\u72c2\u9f99\u7ae0\u8d85\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e45\u95fb\u5927\u540d\uff0c \u4eca\u65e5\u5e78\u4f1a\u3002\u201d \u3000\u3000\u72c2\u9f99\u7ae0\u8d85\u62c2\u9aef\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u542c\u8bf4\uff0c \u4f60\u8fd1\u5e74\u5df2\u5728\u6c5f\u6e56\u4e0a\u95ef\u51fa\u4e86\u4e00\u70b9\u540d\u6c14\uff0c\u4e3a\u4ec0\u4e48\u8981\u6295\u5165\u9634\u9633\u5821\u4e2d\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4eba\u5404\u6709\u5fd7\uff0c \u9601\u4e0b\u4e0d\u7528\u95ee\u7406\u7531\u4e86\uff0c \u4e8b\u5b9e\u4e0a\uff0c \u4f60\u72c2\u9f99\u4eca\u65e5\u548c\u53f6\u67d0\u4eba\u4e3a\u654c\uff0c\u8ffd\u8e2a\u5230\u592a\u6e56\u800c\u6765\u53ea\u6015\u4e5f\u975e\u51fa\u4e8e\u81ea\u613f\u4e86\u3002\u201d \u3000\u3000\u7ae0\u8d85\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u4e5f\u597d\uff0c\u65e2\u7136\u5f7c\u6b64\u4e3a\u654c\uff0c \u4e5f\u5c31\u4e0d\u7528\u8bf4\u4ec0\u4e48\u9053\u7406\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u72c2\u9f99\u7684\u5927\u540d\uff0c\u65e9\u6ee1\u6c5f\u6e56\uff0c \u4f46\u770b\u4e0a\u53bb\uff0c \u6bd4\u8d35\u5821\u603b\u7ba1\u8981\u548c\u6c14\u591a\u4e86\u3002\u201d \u7ae0\u8d85\u9053\uff1a\u201c\u90a3\u8981\u770b\u5bf9\u4ec0\u4e48\u4eba\u4e86\uff0c\u4f60\u53f6\u957f\u9752\u4e5f\u662f\u4e00\u4e2a\u4eba\u7269\uff0c\u5728\u4e0b\u4e0d\u5f97\u4e0d\u7a0d\u793a\u8c26\u8ba9\uff0c\u4f60\u5212\u4e0b\u9053\u6765\u5427\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54b1\u4eec\u5bf9\u51b3\u767e\u62db\uff0c\u4ee5\u5b9a\u80dc\u8d1f\uff1f\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a\u201c\u597d\uff01 \u53ef\u6709\u4ec0\u4e48\u9650\u5236\uff1f\u201d \u3000\u3000\u53f6\u5e86\u9752\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u5404\u5c3d\u6240\u957f\uff0c\u62f3\u62f3\u5175\u5203\uff0c\u968f\u5fc3\u6240\u6b32\u3002\u201d \u3000\u3000\u72c2\u9f99\u7ae0\u8d85\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6697\u5668\u5462\uff1f\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1b\u201c\u4e24\u4f4d\u90fd\u662f\u540d\u4eba\uff0c\u6700\u597d\u5404\u51ed\u6b66\u529f\uff1b\u4e00\u51b3\u751f\u6b7b\uff0c\u4e0d\u80fd\u4f7f\u7528\u6697\u5668\u3002\u201d \u3000\u3000\u539f\u6765\u3002\u72c2\u9f99\u7ae0\u8d85\u6709\u4e00\u62db\u201c\u5929\u9f99\u6296\u7532\u201d\uff0c\u662f\u5175\u5203\uff0c\u6697\u5668\u7684\u6df7\u5408\u7edd\u6280\uff0c\u636e\u8bf4\u3001\u5f88\u5c11\u6709\u4eba\u80fd\u591f\u9003\u8fc7\u8fd9\u4e00\u62db\u4e4b\u4e0b\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\uff0c \u6c5f\u6e56\u4e0a\u7684\u4eba\u77e5\u6653\u4e0d\u591a\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u89c1\u8fc7\u90a3\u4e00\u62db\u201c\u5929\u9f99\u6296\u7532\u201d\u7684\u4eba\uff0c\u5168\u90e8\u6b7b\u5728\u90a3\u4e00\u62db\u4e4b\u4e0b\u3002 \u3000\u3000\u4f46\u7389\u7fe0\u77e5\u9053\uff0c\u6240\u4ee5\uff0c\u5979\u63d0\u51fa\u4e86\u4e0d\u51c6\u65bd\u7528\u6697\u5668\u7684\u9650\u5236\u3002 \u3000\u3000\u5374\u4e0d\u6599\u53f6\u957f\u9752\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u9650\u5236\uff0c\u90a3\u5c31\u8fde\u6697\u5668\u4e5f\u4e0d\u9650\u5236\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1b\u201c\u53f6\u5144\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u7ae0\u8d85\u54c8\u54c8\u4e00\u7b11\uff0c \u63a5\u9053\uff1a\u201c\u597d\uff01\u53f6\u5144\u679c\u4e0d\u4e8f\u6c5f\u6e56\u4e0a\u540e\u8d77\u4e4b\u79c0\uff0c\u4f60\u4eae\u5251\u5427\u3002\u201d \u8fd9\u65f6\uff0c\u5e38\u603b\u7ba1\u5374\u7a81\u7136\u8e0f\u4e0a\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u5bf9\u4ed8\u4e00\u4e2a\u5e74\u8f7b\u540e\u8f88\uff0c\u4f55\u52b3\u5821\u4e3b\u4eb2\u81ea\u51fa\u624b\uff0c\u5728\u4e0b\u5c31\u53ef\u4ee5\u6599\u7406\u4e86\u3002\u201d \u3000\u3000\u7ae0\u8d85\u4ea6\u662f\u4e45\u95fb\u4e03\u5251\u8ffd\u9b42\u4e4b\u540d\u3001\u4ed6\u6210\u540d\u591a\u5e74\uff0c\u5f97\u77e5\u6c5f\u6e56\u4e0a\u540d\u65e0\u5e78\u81f3\uff0c\u80fd\u6709\u5e38\u603b\u7ba1\u5148\u884c\u51fa\u624b\uff0c\u4ee5\u6d4b\u865a\u5b9e\u81ea\u7136\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e03\u5251\u8ffd\u9b42\uff0c\u5b9e\u975e\u865a\u5f20\uff0c\u4f60\u8981\u591a\u591a\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u5e38\u603b\u7ba1\u9053\uff1a\u201c\u5c5e\u4e0b\u77e5\u9053\u3002\u201d\u7f13\u6b65\u884c\u524d\uff0c\u4eae\u51fa\u5175\u5203\u3002 \u3000\u3000\u9a6c\u826f\u4e00\u6a2a\u8eab\uff0c\u9053\uff1a\u201c\u59d3\u5e38\u7684\uff0c\u4f60\u8fd9\u4e2a\u8eab\u4efd\uff0c\u8fd8\u4e0d\u914d\u548c\u6211\u4eec\u7684\u5251\u4f7f\u52a8\u624b\uff0c\u60f3\u73a9\u5417\uff1f\u533a\u533a\u5728\u4e0b\u53ef\u4ee5\u5949\u966a\u4f60\u51e0\u62db\u3002\u201d \u590f\u6740\u5374\u5ffd\u7136\u4e00\u4fa7\u8eab\uff0c\u5feb\u6b65\u884c\u5230\u4e86\u9a6c\u826f\u7684\u8eab\u524d\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u9635\u8ba9\u7ed9\u6211\u3002\u201d \u3000\u3000\u53f3\u624b\u4e00\u677e\u8170\u95f4\u7684\u6d3b\u6263\uff0c\u521a\u7684\u4e00\u58f0\uff0c\u7f05\u94c1\u8f6f\u5200\u51fa\u9798\uff0c\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u5e38\u603b\u7ba1\uff0c\u4f60\u5148\u51fa\u624b\uff0c\u8fd8\u662f\u8981\u8ba9\u6211\u4e09\u62db\uff1f\u201d \u3000\u3000\u5e38\u603b\u7ba1\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u8ba9\u4f60\u4e09\u62db\u3002\u201d\u53f3\u624b\u4e00\u6325\uff0c\u4e00\u9053\u5bd2\u8292\uff0c\u76f4\u88ad\u590f\u6740\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\uff0c\u660e\u660e\u8d64\u624b\u7a7a\u62f3\uff0c\u4f46\u4e00\u62ac\u624b\uff0c\u5374\u591a\u51fa\u4e86\u4e00\u9053\u5bd2\u8292\u3002 \u3000\u3000\u90a3\u662f\u4e00\u628a\u5bbd\u4e0d\u8fc7\u4e8c\u6307\uff0c\u4e00\u9762\u5f00\u950b\uff0c\u5f62\u5982\u4e03\u661f\u5c16\u5b50\u7684\u5175\u5203\uff0c\u4f46\u5374\u5f88\u8584\uff0c\u5f88\u8584\u3002 \u3000\u3000\u539f\u6765\uff0c\u8fd9\u662f\u4e00\u79cd\u5f88\u7279\u6b8a\u7684\u5175\u5203\uff0c\u53eb\u505a\u8896\u4e2d\u5200\uff0c\u4e0d\u7528\u65f6\uff0c\u5c31\u85cf\u5728\u8896\u4e2d\uff0c\u7528\u65f6\uff0c\u5374\u7a81\u7136\u75be\u5c04\u800c\u51fa\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u79cd\u5175\u5203\u5f88\u96be\u65bd\u7528\uff0c\u56e0\u4e3a\u5b83\u5f88\u8584\uff0c\u5f88\u8f6f\uff0c\u975e\u6709\u76f8\u5f53\u7684\u5185\u529f\u706b\u5019\uff0c\u4e0d\u5bb9\u6613\u5f97\u5fc3\u5e94\u624b\u3002 \u3000\u3000\u4f46\u5e38\u603b\u7ba1\u5374\u80fd\u628a\u8896\u4e2d\u5200\u8fd0\u7528\u5f97\u5341\u5206\u7075\u6d3b\u3002 \u3000\u3000\u4f46\u89c1\u5bd2\u8292\u8fde\u95ea\uff0c\u5e38\u603b\u7ba1\u4e00\u7728\u773c\u95f4\uff0c\u653b\u51fa\u4e86\u4e94\u5200\u3002 \u3000\u3000\u8fd9\u4e94\u5200\u653b\u51fa\u7684\u5f62\u52bf\u4e0d\u540c\uff0c\u5ffd\u7136\u8212\u5c55\uff0c\u5ffd\u7136\u5377\u7f29\uff0c\u5ffd\u7136\u95f4\uff0c\u50cf\u5de8\u6d6a\u56de\u5377\u4e00\u822c\uff0c\u62e6\u8170\u5377\u5230\u3002 \u3000\u3000\u8fd9\u79cd\u5175\u5203\uff0c\u6c5f\u6e56\u4e0a\u4e0d\u591a\u89c1\uff0c\u5b83\u7684\u62db\u5f0f\u53d8\u5316\uff0c\u4e5f\u548c\u4e00\u822c\u5175\u5203\u5927\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u590f\u6740\u867d\u7136\u662f\u4e45\u7ecf\u5927\u654c\u7684\u8001\u624b\uff0c\u4f46\u4e00\u4e0a\u675f\uff0c\u4e5f\u88ab\u903c\u5f97\u6709\u4e9b\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u5e38\u603b\u7ba1\u653b\u51fa\u4e86\u516b\u62db\u4e4b\u540e\uff0c\u590f\u6740\u624d\u6709\u4e86\u8fd8\u624b\u7684\u673a\u4f1a\uff0c\u5ffd\u7136\u5288\u51fa\u4e00\u5200\u3002 \u3000\u3000\u53ea\u4e00\u5200\uff0c\u51cc\u5389\u7edd\u4f26\u7684\u4e00\u5200\u3002 \u3000\u3000\u9c9c\u8840\u8ff8\u6e85\uff0c\u90a3\u4e00\u5200\u659c\u7740\u7531\u5e38\u603b\u7ba1\u7684\u5de6\u80a9\u4e0a\u5212\u7834\u4e86\u5e38\u603b\u7ba1\u7684\u524d\u80f8\u53ca\u5c0f\u8179\u3002 \u3000\u3000\u590f\u6740\u7684\u8138\u4e0a\uff0c\u9732\u51fa\u4e86\u7b11\u5bb9\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u7b11\uff0c\u672a\u514d\u65e9\u4e86\u4e00\u4e9b\uff0c\u5e38\u603b\u7ba1\u7684\u8896\u91cc\u5200\uff0c\u7adf\u7136\u7531\u540e\u9762\u75be\u5c04\u8fc7\u6765\u3002 \u3000\u3000\u5c16\u5229\u7684\u5200\u950b\uff0c\u6b63\u5bf9\u7740\u590f\u6740\u7684\u80cc\u5fc3\u3002 \u3000\u3000\u53ef\u662f\uff0c\u590f\u6740\u8fd8\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u5f53\u7684\u4e00\u58f0\uff0c\u4e00\u628a\u957f\u5251\u98de\u4e86\u8fc7\u6765\uff0c\u51fb\u504f\u4e86\u90a3\u8896\u4e2d\u8f6f\u5200\u3002 \u3000\u3000\u53f6\u957f\u9752\u51fa\u4e86\u5251\uff0c\u597d\u5feb\u7684\u4e00\u5251\u3002 \u3000\u3000\u9664\u4e86\u53f6\u957f\u9752\u7684\u5feb\u5251\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u80fd\u6551\u4e86\u590f\u6740\u3002 \u3000\u3000\u590f\u6740\u7684\u7b11\u5bb9\u50f5\u4f4f\u4e86\uff0c\u56de\u987e\u4e86\u53f6\u957f\u9752\u4e00\u773c\uff0c\u8138\u4e0a\u662f\u4e00\u7247\u60ed\u6127\u795e\u8272\u3002 \u3000\u3000\u4f46\u590f\u6740\u603b\u7b97\u6361\u56de\u4e86\u4e00\u6761\u547d\u3002 \u3000\u3000\u7830\u7136\u4e00\u58f0\uff0c\u5e38\u603b\u7ba1\u7684\u5c38\u4f53\uff0c\u5012\u6454\u5728\u5730\u4e0a\u3002 \u3000\u3000\u72c2\u9f99\u7ae0\u8d85\u5bf9\u5e38\u603b\u7ba1\u7684\u6b7b\uff0c\u5e76\u6ca1\u6709\u5f88\u6fc0\u52a8\u7684\u611f\u89c9\uff0c\u53ea\u51b7\u51b7\u7684\u671b\u4e86\u5e38\u603b\u7ba1\u7684\u5c38\u4f53\u4e00\u773c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u4f60\u4e0d\u8be5\u51fa\u5251\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a\u201c\u4ed6\u4eec\u672c\u6765\u662f\u4e00\u4e2a\u540c\u5f52\u4e8e\u5c3d\u7684\u5c40\u9762\uff0c\u4f46\u4f60\u51fa\u4e86\u4e00\u5251\uff0c\u5374\u6551\u4e86\u4ed6\u7684\u547d\uff0c\u6211\u767d\u767d\u8d54\u8fdb\u53bb\u4e86\u4e00\u4e2a\u603b\u7ba1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u7684\u4eba\uff0c\u5df2\u7ed9\u4e86\u4ed6\u673a\u4f1a\uff0c\u4ed6\u8fde\u653b\u4e86\u516b\u62db\uff0c\u4ecd\u7136\u6ca1\u6709\u6740\u4e86\u590f\u6740\u3002\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u590f\u6740\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u7ae0\u8d85\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u590f\u6740\uff0c\u4f60\u8fc7\u6765!\u201d \u3000\u3000\u590f\u6740\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u7f13\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u590f\u6740\u56de\u6765\u3002\u201d \u3000\u3000\u7ae0\u8d85\u7a81\u7136\u4e00\u626c\u624b\uff0c\u9053\uff1a\u201c\u53ea\u6015\u56de\u4e0d\u53bb\u4e86\u3002\u201d \u3000\u3000\u5f53\u7684\u4e00\u58f0\uff0c\u91d1\u94c1\u4ea4\u9e23\u3002 \u3000\u3000\u8033\u9645\u95f4\uff0c\u54cd\u8d77\u4e86\u53f6\u957f\u9752\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u53ea\u6015\u672a\u89c1\u5fc5\u5f97\u3002\u201d \u3000\u3000\u72c2\u9f99\u7ae0\u8d85\u523a\u51fa\u4e86\u4e00\u5200\uff0c\u4f46\u5374\u88ab\u53f6\u957f\u9752\u4e00\u5251\u7ed9\u5c01\u4f4f\u3002 \u3000\u3000\u590f\u6740\u53c8\u6361\u56de\u4e86\u4e00\u6761\u547d\u3002 \u3000\u3000\u590f\u6740\u672c\u6765\u4e5f\u662f\u6c5f\u6e56\u4e0a\u5f88\u6709\u540d\u7684\u6740\u624b\uff0c\u4f46\u5728\u72c2\u9f99\u548c\u53f6\u957f\u9752\u7684\u5feb\u5200\u5feb\u5251\u4e4b\u4e0b\uff0c\u7adf\u7136\u8fde\u62d4\u5200\u7684\u673a\u4f1a\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u6b66\u529f\u7684\u4fee\u4e3a\uff0c\u6709\u4e0d\u53ef\u5426\u8ba4\u7684\u8ddd\u79bb\uff0c\u5728\u72c2\u9f99\u548c\u53f6\u957f\u9752\u7684\u9762\u524d\uff0c\u590f\u6740\u5b8c\u5168\u8d70\u4e86\u6837\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u5b8c\u5168\u4e0d\u4f1a\u6b66\u529f\u7684\u4eba\u4e00\u6837\u3002 \u3000\u3000\u7ae0\u8d85\u53cc\u76ee\u795e\u51dd\uff0c\u76ef\u6ce8\u7740\u53f6\u957f\u9752\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u4f60\u8fd8\u53ef\u4ee5\u548c\u6211\u4e00\u6218\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8bd5\u8bd5\u770b\u5427\uff01\u5728\u4e0b\u5f88\u613f\u610f\u594b\u529b\u4e00\u8bd5\u3002\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a\u201c\u4f60\u5f88\u8c26\u865a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u79cd\u7f8e\u5fb7\uff0c\u6240\u4ee5\uff0c\u9601\u4e0b\u6700\u597d\u4e5f\u5b66\u5b66\u3002\u201d \u3000\u3000\u7ae0\u8d85\u5ffd\u7136\u98de\u8eab\u800c\u8d77\uff0c\u6325\u5200\u5288\u6765\u3002 \u3000\u3000\u5200\u5982\u6025\u6d41\u5954\u653e\uff0c\u53ea\u770b\u4e00\u7247\u5200\u5149\uff0c\u5374\u65e0\u6cd5\u5206\u8fa8\u51fa\u591a\u5c11\u5200\u5f0f\uff0c\u8fde\u7ef5\u4e8e\u4e00\u5904\u3002 \u3000\u3000\u72c2\u9f99\u5200\u6cd5\uff0c\u679c\u7136\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u53f6\u957f\u9752\u7684\u5251\uff0c\u70b9\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u53ea\u542c\u5230\u4e00\u9635\u7ef5\u7684\u91d1\u94b1\u4ea4\u54cd\u3001\u4f20\u5165\u8033\u9645\uff0c\u65e0\u6cd5\u5206\u8fa8\u662f\u591a\u5c11\u6b21\u91d1\u94c1\u76f8\u51fb\u3002 \u3000\u3000\u5200\u5149\u6d88\u9000\uff0c\u5251\u6c14\u655b\u6536\uff0e\u4e24\u4e2a\u4eba\u53c8\u6062\u590d\u4e86\u76f8\u5bf9\u800c\u7acb\u7684\u5c40\u9762\u3002 \u3000\u3000\u76f4\u5230\u6b64\u523b\uff0c\u4e01\u5cf0\u3001\u9a6c\u826f\uff0c\u624d\u7b97\u770b\u5230\u4e86\u53f6\u957f\u9752\u7684\u5251\u6cd5\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u4e5f\u7ec8\u4e8e\u660e\u767d\u4e86\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u80fd\u5165\u9009\u4e3a\u5251\u4f7f\u7684\u8eab\u4efd\uff0c \u60f3\u4fdd\u62a4\u53f6\u957f\u9752\uff0c\u5b9e\u5728\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002 \u3000\u3000\u7ae0\u8d85\u9053\uff1a\u201c\u597d\u5251\u6cd5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f7c\u6b64\uff0c\u5f7c\u6b64\u3002\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a\u201c\u4f60\u53f7\u79f0\u4e03\u5251\u8ffd\u9b42\uff0c\u60f3\u6765\u90a3\u4e03\u62db\u5251\u6cd5\uff0c\u5b9a\u7136\u662f\u5341\u5206\u51cc\u5389\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u6740\u4e86\u4e0d\u5c11\u7684\u4eba\uff0c\u4f46\u4ece\u672a\u7528\u5b8c\u4e03\u5251\uff0c\u5927\u6982\u4e0d\u4f1a\u592a\u5dee\u5427\uff01\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u4f60\u90a3\u4e03\u5251\uff0c\u80fd\u4e0d\u5c31\u8ffd\u53bb\u6211\u72c2\u9f99\u4e4b\u9b42\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8bd5\u8bd5\u770b\u5427\uff01\u5728\u4e0b\u5c3d\u529b\u800c\u4e3a\u3002\u201d \u3000\u3000\u72c2\u9f99\u7ae0\u8d85\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u4f60\u5982\u4e03\u5251\u4e0d\u80fd\u6740\u4e86\u6211\uff0c\u53c8\u8be5\u5982\u4f55?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8981\u6253\u8d4c?\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a \u201c\u4f60\u51fa\u624b\u4e00\u5251\uff0c\u6551\u4e86\u590f\u6740\uff0c\u4f7f\u6211\u635f\u5931\u4e86\u4e00\u4e2a\u603b\u7ba1\uff0c\u5e38\u603b\u7ba1\u867d\u7136\u66ff\u6211\u5e72\u4e86\u5f88\u4e45\u7684\u603b\u7ba1\uff0c\u4f46\u4ed6\u4e0d\u77e5\u957f\u8fdb\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u8d8a\u6765\u8d8a\u5dee\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a \u201c\u548c\u9601\u4e0b\u9002\u624d\u4ea4\u624b\u4e00\u62db\uff0c\u4f7f\u6211\u611f\u5230\u72c2\u9f99\u5821\u5982\u6709\u9601\u4e0b\u8fd9\u4e48\u4e00\u4e2a\u4eba\u51fa\u4efb\u603b\u7ba1\uff0c\u90a3\u624d\u662f\u76f8\u5f97\u76ca\u5f70\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u6211\u5982\u8981\u8fdb\u72c2\u9f99\u5821\uff0c\u5e0c\u671b\u80fd\u6df7\u4e2a\u5821\u4e3b\u8eab\u4efd\uff0c\u201d \u7ae0\u8d85\u9053\uff1a \u201c\u884c! \u51fa\u5251\u5427!\u6740\u4e86\u6211\u7ae0\u8d85\uff0c\u4f60\u5c31\u53ef\u4ee5\u5f53\u72c2\u9f99\u5821\u7684\u5821\u4e3b\uff0c\u5982\u662f\u4f60\u4e0d\u5e78\u8d25\u4e8e\u6211\u7684\u5200\u4e0b\uff0c\u90a3\u5c31\u53ea\u597d\u5c48\u9a7e\u4e3a\u603b\u7ba1\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u5591\u9053\uff1a\u8fd9\u72c2\u9f99\u7ae0\u8d85\u867d\u7136\u6709\u4e9b\u72c2\uff0c\u4f46\u8fd8\u4e0d\u5931\u7537\u513f\u672c\u8272\uff0c\u5982\u80fd\u6298\u670d\u4e8e\u4ed6\uff0c\u5012\u4e0d\u5931\u4e00\u4e2a\u5e2e\u624b\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c \u53e3\u4e2d\u8bf4\u9053\uff1a \u201c\u597d!\u53f6\u67d0\u4eba\u5982\u662f\u8d25\u4e8e\u4f60\u5200\u4e0b\uff0c\u8fd8\u80fd\u7559\u5f97\u6027\u547d\uff0c\u53ef\u4ee5\u5c31\u4efb\u4f60\u72c2\u9f99\u5821\u603b\u7ba1\u4e4b\u4f4d\u4e86\uff0c\u5982\u662f\u53f6\u67d0\u4fa5\u5e78\u80dc\u5f97\u4e00\u62db\u534a\u5f0f\u5462?\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a\u201c\u4f60\u613f\u8d4c\u4e86\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u6c5f\u6e56\u751f\u6daf\uff0c\u968f\u65f6\u73a9\u547d\uff0c\u8d4c\u4e00\u8d4c\u8fd0\u6c14\uff0c \u4e5f\u65e0\u4f24\u5927\u96c5\u3002\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a\u201c\u4f60\u5982\u771f\u80fd\u80dc\u4e86\u6211\uff0c\u6211\u5c31\u628a\u72c2\u9f99\u5821\u4e3b\u8ba9\u7ed9\u4f60\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u90a3\u5012\u4e0d\u7528\uff0c\u5728\u4e0b\u5982\u82e5\u80dc\u4e86\u5821\u4e3b\uff0c \u53ea\u5e0c\u671b\u4f60\u80fd\u4e3a\u6211\u6548\u547d\u4e8c\u6b21\uff1b\u201d \u3000\u3000\u7ae0\u8d85\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u597d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5927\u4e08\u592b\u4e00\u8a00\u3002\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a\u201c\u9a77\u9a6c\u96be\u8ffd\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\uff01\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u8ffd\u9b42\u7b2c\u4e00\u5251\u6025\u653b\u51fa\u624b\u3002 \u3000\u3000\u5251\u5149\u5982\u95ea\u7535\uff0c\u5206\u88ad\u7ae0\u8d85\u4e03\u5904\u5927\u7a74\u3002 \u3000\u3000\u4e00\u5251\u4e03\u661f\u3002 \u3000\u3000\u7ae0\u8d85\u5927\u559d\u4e00\u58f0\uff0c\u5355\u5200\u4e00\u6325\uff0c\u5bd2\u8292\u5982\u5e55\uff0c\u62a4\u4f4f\u4e86\u5168\u8eab\u3002 \u3000\u3000\u91d1\u94c1\u4ea4\u9707\u58f0\u4e2d\uff0c\u72c2\u9f99\u7ae0\u8d85\u5c01\u5f00\u4e86\u7b2c\u4e00\u5251\u3002 \u3000\u3000\u53f6\u957f\u9752\u7b2c\u4e00\u5251\u88ab\u5c01\u5f00\u4e4b\u540e\uff0c\u505c\u5251\u672a\u653b\uff0c\u7b49\u5f85\u7ae0\u8d85\u7684\u53cd\u51fb\u3002 \u3000\u3000\u7ae0\u8d85\u5374\u6ca1\u6709\u53cd\u51fb\uff0c\u6a2a\u5200\u4ee5\u5f85\u3002 \u3000\u3000\u884c\u5bb6\u4e00\u51fa\u624b\uff0c\u4fbf\u77e5\u6709\u6ca1\u6709\u3002 \u3000\u3000\u53f6\u957f\u9752\u653b\u51fa\u4e86\u7b2c\u4e00\u5251\uff0c\u7ae0\u8d85\u5df2\u770b\u51fa\u4e86\u8ffd\u9b42\u4e03\u5251\u7684\u51cc\u5389\u3002 \u3000\u3000\u4ed6\u89c1\u8bc6\u8fc7\u4e86\u53f6\u957f\u9752\u7684\u5feb\u5251\uff0c\u7edd\u4e0d\u5728\u4ed6\u7ae0\u8d85\u7684\u5feb\u5200\u4e4b\u4e0b\u3002 \u3000\u3000\u4f46\u53f6\u957f\u9752\u7684\u8ffd\u9b42\u4e03\u5251\uff0c\u5374\u662f\u5251\u62db\u4e2d\u7684\u5947\u5b66\uff0c\u7ae0\u8d85\u63a5\u4e0b\u4e86\u7b2c\u4e00\u5251\uff0c\u5df2\u89c9\u5230\u6c14\u52bf\u4e0d\u51e1\u3002 \u3000\u3000\u867d\u7136\u653b\u51fb\u662f\u6700\u597d\u7684\u9632\u5fa1\uff0c\u4f46\u53f6\u957f\u9752\u7684\u8ffd\u9b42\u4e03\u5251\uff0c\u4e43\u5251\u6cd5\u4e2d\u7684\u5947\u5389\u4e4b\u62db\uff0c\u7ae0\u8d85\u81ea\u5ba1\u5c40\u52bf\uff0c\u5fc5\u987b\u5168\u529b\u63a5\u8fc7\u4ed6\u8ffd\u9b42\u4e03\u5251\uff0c\u624d\u80fd\u653e\u624b\u548c\u4ed6\u4e00\u6218\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u7ae0\u8d85\u51b3\u5b9a\u6539\u91c7\u9632\u5b88\u3002 \u3000\u3000\u51c6\u5907\u5168\u529b\u63a5\u4e0b\u53f6\u957f\u9752\u8ffd\u9b42\u4e03\u5251\u4e4b\u540e\uff0c\u518d\u4f5c\u53cd\u51fb\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e0d\u89c1\u72c2\u9f99\u53cd\u51fb\uff0c\u5df2\u77e5\u4ed6\u5fc3\u610f\uff0c\u7b2c\u4e8c\u62db\u201c\u4e00\u8f6e\u660e\u6708\u7167\u5e7d\u9b42\u201d\uff0c\u8fde\u7ef5\u51fa\u624b\u3002 \u3000\u3000\u90a3\u662f\u4e00\u4e2a\u5f88\u5927\u7684\u5149\u5708\uff0c\u75be\u5411\u72c2\u9f99\u7ae0\u8d85\u5377\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u624b\u4e2d\u4e4b\u5251\uff0c\u5df2\u5931\u5176\u5f62\u4f53\uff0c\u957f\u5251\u5316\u4f5c\u4e86\u4e00\u5708\u5149\u8292\u3002 \u3000\u3000\u7ae0\u8d85\u5168\u529b\u6325\u5200\uff0c\u4e00\u8fde\u5288\u51fa\u4e86\u5341\u516b\u5200\u3002 \u3000\u3000\u5341\u516b\u5200\u6784\u6210\u4e86\u4e00\u7247\u5e7b\u8d77\u7684\u5200\u8292\uff0c\u5c01\u4f4f\u4e86\u7f69\u843d\u7684\u5251\u52bf\u3002 \u3000\u3000\u8ffd\u9b42\u4e03\u5251\u7b2c\u4e09\u62db\uff0c\u201c\u5904\u5904\u65e0\u5bb6\u5904\u5904\u5bb6\u201d\u3002 \u3000\u3000\u6447\u98a0\u7684\u5251\u8292\uff0c\u5316\u4f5c\u70b9\u70b9\u5bd2\u661f\uff0c\u95ea\u70c1\u4e0d\u5b9a\uff0c\u5206\u7531\u56db\u9762\u516b\u65b9\u523a\u6765\u3002 \u3000\u3000\u72c2\u9f99\u7ae0\u8d85\uff0c\u534a\u751f\u6c5f\u6e56\uff0c\u8eab\u7ecf\u4f55\u6b62\u767e\u6218\uff0c\u4f46\u5374\u4ece\u672a\u89c1\u8fc7\u8fd9\u7b49\u7684\u5251\u62db\u3002 \u3000\u3000\u4ed6\u6325\u5200\u6025\u65a9\uff0c\u2014\u5239\u90a3\u95f4\uff0c\u624b\u4e2d\u4e4b\u5200\uff0c\u8fde\u53d8\u4e86\u4e8c\u5341\u516b\u4e2a\u62db\u5f0f\u3002 \u3000\u3000\u4f46\u4ecd\u7136\u6ca1\u6709\u5b8c\u5168\u5c01\u6321\u5f00\u53f6\u957f\u9752\u624b\u4e2d\u7684\u51cc\u5389\u5251\u52bf\u3002 \u3000\u3000\u4f46\u95fb\u55e4\u55e4\u4e24\u58f0\uff0c\u72c2\u9f99\u7ae0\u8d85\u7684\u5de6\u8896\u88ab\u523a\u7a7f\uff0c\u53f3\u81c2\u4e0a\uff0c\u4e5f\u88ab\u5251\u8292\u5212\u4e86\u4e00\u9053\u4f24\u53e3\u3002 \u3000\u3000\u7ae0\u8d85\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u603b\u7b97\u628a\u7b2c\u4e09\u62db\u8ffd\u9b42\u5251\u62db\u5c01\u5f00\u3002 \u3000\u3000\u53f6\u957f\u9752\u5927\u559d\u4e00\u58f0\uff0c\u7b2c\u56db\u3001\u7b2c\u4e94\u62db\uff0c\u8fde\u7ef5\u51fa\u624b\u3002 \u3000\u3000\u4f46\u89c1\u5bd2\u8292\u7535\u63a3\uff0c\u971c\u5203\u5377\u96ea\uff0c\u6709\u5982\u6392\u5c71\u5012\u6d77\u822c\uff0c\u76f4\u538b\u8fc7\u53bb\u3002 \u3000\u3000\u72c2\u9f99\u8fde\u58f0\u5927\u559d\uff0c\u5200\u52bf\u72c2\u5288\u3002 \u3000\u3000\u4ed6\u611f\u89c9\u5230\u56db\u9762\u516b\u65b9\u90fd\u662f\u5bd2\u8292\u5377\u88ad\uff0c\u53ea\u597d\u62da\u547d\u6325\u5200\u5c01\u6321\u3002 \u3000\u3000\u72c2\u9f99\u7ae0\u8d85\u7684\u5200\u6cd5\uff0c\u7a76\u7adf\u662f\u6709\u7740\u6781\u6df1\u9020\u8be3\uff0c\u4e00\u9635\u731b\u5288\u6025\u6325\uff0c\u4ecd\u7136\u88ab\u5c01\u6321\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u7b2c\u516d\u62db\uff0c\u5e0c\u671b\u9601\u4e0b\u4ecd\u7136\u80fd\u591f\u5c01\u5f00\u8fd9\u4e00\u62db\u3002\u201d \u4e00\u4fa7\u89c2\u6218\u7684\u4e01\u5cf0\u3001\u9a6c\u826f\u3001\u7389\u5170\u53cc\u59dd\uff0c\u7b97\u662f\u5927\u5f00\u4e86\u773c\u754c\u3002 \u3000\u3000\u770b\u5230\u4e86\u540d\u52a8\u6c5f\u6e56\u7684\u8ffd\u9b42\u4e03\u5251\u62db\uff0c\u7ade\u6709\u5982\u6b64\u5de8\u5927\u7684\u5a01\u529b\u3002 \u3000\u3000\u9a6c\u826f\u5401\u4e00\u53e3\u6c14\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u4e01\u5144\uff0c\u770b\u6765\uff0c\u54b1\u4eec\u662f\u767d\u767d\u4e3a\u5251\u4f7f\u62c5\u5fc3\u4e86\u3002\u201d \u4e01\u5cf0\u9053\uff1a\u201c\u4e03\u5251\u8ffd\u9b42\uff0c\u4f46\u72c2\u9f99\u5df2\u7ecf\u907f\u8fc7\u4e86\u4e94\u5251\u3002\u201d \u3000\u3000\u9a6c\u826f\u4e00\u6014\uff0c\u9053\uff1a \u201c\u4ed6\u867d\u7136\u907f\u8fc7\u4e86\u4e94\u5251\uff0c\u4f46\u7b2c\u4e00\u5251\uff0c\u90fd\u95f9\u5f97\u4ed6\u624b\u5fd9\u811a\u4e71\uff0c\u8fd9\u6700\u540e\u4e24\u62db\u5251\u6cd5\uff0c\u5fc5\u662f\u6700\u7cbe\u5389\u7684\u5251\u6cd5\uff0c\u72c2\u9f99\u53ea\u6015\u96be\u9003\u8fd9\u4e24\u5251\u4e4b\u5371\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\u3002\u201d \u3000\u3000\u590f\u6740\u7a81\u7136\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u5c31\u7b97\u5251\u4f7f\u7684\u8ffd\u9b42\u4e03\u5251\uff0c\u4e0d\u80fd\u53d6\u72c2\u9f99\u4e4b\u547d\uff0c\u4f46\u90a3\u5e76\u4e0d\u8868\u793a\u4ed6\u5df2\u65e0\u518d\u6218\u4e4b\u80fd\uff0c\u8ffd\u9b42\u4e03\u5251\u867d\u7136\u662f\u7a81\u51fa\u4e4b\u5b66\uff0c\u4f46\u5251\u4f7f\u7684\u5251\u6cd5\uff0c\u4e00\u76f4\u4fdd\u6301\u7740\u76f8\u5f53\u7684\u6c34\u51c6\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u53f6\u957f\u9752\u7684\u8ffd\u9b42\u7b2c\u516d\u5251\uff0c\u5df2\u7136\u51fa\u624b\u3002 \u3000\u3000\u201c\u70b9\u70b9\u9b3c\u706b\u7167\u4eba\u6765\u201d\u3002 \u3000\u3000\u72c2\u9f99\u7ae0\u8d85\u5728\u53f6\u957f\u9752\u6325\u52a8\u957f\u5251\u4e4b\u65f6\uff0c \u5df2\u7136\u5200\u821e\u9f99\u86c7\uff0c\u5148\u62a4\u4f4f\u8eab\u8eaf\u3002 \u8fd9\u65f6\u3002\u4e24\u4e2a\u52b2\u88c5\u5251\u58eb\uff0c\u773c\u770b\u5821\u4e3b\u6709\u4e9b\u614c\u4e71\uff0c\u7acb\u5373\u62d4\u5200\u51fb\u51fa\u3002 \u3000\u3000\u5251\u5149\u3001\u5200\u5149\uff0c\u6d51\u7136\u4e00\u7247\u4e2d\uff0c\u8ff8\u5c04\u51fa\u4e24\u80a1\u9c9c\u8840\u3002 \u3000\u3000\u4ed6\u4eec\u60f3\u5e2e\u52a9\u4e3b\u4eba\u7684\u76ee\u7684\u8fbe\u5230\u4e86\uff0c \u4f46\u5374\u4e22\u4e86\u4e24\u6761\u547d\u3002 \u53f6\u957f\u9752\u8fd9\u4e00\u62db\u201c\u70b9\u70b9\u9b3c\u706b\u7167\u4eba\u6765\u201d\u3002\u523a\u6b7b\u4e86\u4e24\u4e2a\u52b2\u88c5\u5251\u624b\u4e4b\u540e\uff0c\u4f59\u52bf\u5df2\u8870\u3002 \u3000\u3000\u72c2\u9f99\u7ae0\u8d85\u75be\u8d77\u4e00\u5200\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u9707\u5f00\u53f6\u957f\u9752\u624b\u4e2d\u957f\u5251\u3002 \u3000\u3000\u7136\u540e\uff0c\u6325\u5200\u62a2\u653b\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u4e24\u4e2a\u5251\u58eb\u51fa\u624b\uff0c\u8fd9\u4e00\u62db\u7684\u51cc\u5389\u53d8\u5316\uff0c \u5fc5\u4f7f\u72c2\u9f99\u7ae0\u8d85\u6e85\u8840\u3002 \u4e24\u4e2a\u52b2\u88c5\u5251\u58eb\u3001 \u867d\u7136\u6b7b\u5728\u4e86\u53f6\u957f\u9752\u7684\u5251\u4e0b\uff0c\u4f46\u4ed6\u4eec\u5374\u6551\u4e86\u7ae0\u8d85\u3002 \u7ae0\u8d85\u53d1\u89c9\u4e86\u8ffd\u9b42\u4e03\u5251\u7684\u5229\u5bb3\uff0c \u524d\u4e94\u5251\uff0c \u4ed6\u52c9\u5f3a\u53ef\u4ee5\u5bf9\u4ed8\uff0c\u4f46\u7b2c\u516d\u5251\u4e00\u5b9a\u53ef\u4ee5\u6740\u4e86\u4ed6\u3002 \u3000\u3000\u8ffd\u9b42\u4e03\u5251\u8d8a\u6765\u8d8a\u51f6\u5389\uff0c\u8fd9\u7b2c\u4e03\u5251\u60f3\u6765\u662f\u66f4\u5177\u5a01\u529b\u3002 \u3000\u3000\u4ed6\u540e\u6094\u81ea\u5df1\u7684\u9a84\u72c2\uff0c \u5982\u82e5\u4ed6\u4e0d\u62d2\u7edd\u98de\u9e70\u7684\u8054\u624b\u62d2\u654c\uff0c\u81ea\u7136\u4e0d\u4f1a\u6709\u8fd9\u4e2a\u5c40\u9762\u3002 \u4f46\u6094\u6068\u5df2\u8fdf\uff0c\u73b0\u5728\u552f\u4e00\u7684\u529e\u6cd5\uff0c \u53ea\u6709\u5168\u529b\u51fa\u624b\u4e0d\u8ba9\u53f6\u957f\u9752\u65bd\u5c55\u51fa\u8ffd\u9b42\u7b2c\u4e03\u5251\u62db\u3002 \u4e24\u4e2a\u5251\u571f\u4e4b\u6b7b\uff0c\u7ed9\u4e86\u4ed6\u53cd\u51fb\u7684\u673a\u53bb\u3002 \u3000\u3000\u72c2\u9f99\u624b\u4e2d\u4e4b\u5200\uff0c \u5c55\u5f00\u4e86\u75af\u72c2\u7684\u653b\u52bf\u3002 \u3000\u3000\u4e00\u5200\u8fde\u4e00\u5200\u7684\u8fde\u7ef5\u653b\u52bf\u3002 \u3000\u3000\u53f6\u957f\u9752\u6697\u6697\u2014\u7b11\uff0c \u957f\u5251\u6325\u5c55\uff0e\u6539\u91c7\u5b88\u52bf\u3002 \u3000\u3000\u72c2\u9f99\u7ae0\u8d85\u4e00\u53e3\u6c14\uff0c\u653b\u51fa\u4e86\u4e03\u5341\u516b\u5200\u3002 \u3000\u3000\u53f6\u957f\u9752\u5b8c\u5168\u63a5\u4e86\u4e0b\u6765\u3001 \u3000\u3000\u72c2\u9f99\u7ae0\u8d85\u5200\u52bf\u4e00\u987f\uff0c\u970d\u7136\u5411\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u9053\uff1a \u201c\u53f6\u957f\u9752\u3002 \u8981\u4e0d\u8981\u8bd5\u8bd5\u6211\u7684\u98de\u9f99\u5341\u4e8c\u5200\u3002\u201d \u3000\u3000\u4ed6\u4e00\u53e3\u6c14\uff0c\u653b\u51fa\u4e86\u4e03\u5341\u516b\u5200\uff0c \u5df2\u65e0\u6cd5\u518d\u8fde\u7eed\u4e0b\u53bb\u3002 \u8981\u5b9e\u65bd\u98de\u9f99\u5341\u4e8c\u5200\uff0c\u8fd9\u4e4b\u95f4\uff0c\u5fc5\u7136\u6709\u4e00\u4e2a\u505c\u987f\u7684\u8d77\u624b\u3002 \u3000\u3000\u4f46\u8fd9\u4e00\u4e2a\u505c\u987f\uff0c\u5f88\u53ef\u80fd\u4f1a\u5f15\u8d77\u53f6\u957f\u9752\u7684\u653b\u52bf\uff0c\u8ffd\u9b42\u7b2c\u4e03\u5251\u3002 \u3000\u3000\u72c2\u9f99\u867d\u72c2\uff0c\u4f46\u4ed6\u4e5f\u4e0d\u613f\u62ff\u7740\u81ea\u5df1\u7684\u6027\u547d\u5356\u72c2\uff0c\u6240\u4ee5\uff0c\u4e0d\u5f97\u4e0d\u7528\u70b9\u5fc3\u673a\uff0c\u7a33\u4f4f\u4e86\u53f6\u957f\u9752\u3002 \u3000\u3000\u53f6\u957f\u9752\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u597d!\u5728\u4e0b\u6781\u613f\u89c1\u8bc6\u4e00\u4e0b\u7ae0\u5821\u4e3b\u7684\u98de\u9f99\u5200\u6cd5\u7edd\u6280\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u7ae0\u8d85\u5df2\u7f13\u8fc7\u4e86\u4e00\u53e3\u6c14\uff0c\u4e0d\u5bb9\u53f6\u957f\u9752\u518d\u8bf4\u4e0b\u53bb\uff0c\u98de\u8dc3\u800c\u8d77\uff0c\u4e00\u5200\u5288\u51fa\u53bb\u3002 \u3000\u3000\u51f6\u5389\u7edd\u4f26\u7684\u4e00\u5200\u3002 \u3000\u3000\u53f6\u957f\u9752\u6325\u5251\u8fce\u654c\uff0c\u5200\u5251\u53c8\u4e00\u6b21\u89e6\u63a5\u3002 \u3000\u3000\u7ae0\u8d85\u5c31\u501f\u90a3\u5200\u5251\u4ea4\u63a5\u4e4b\u529b\uff0c\u60ac\u7a7a\u4e00\u4e2a\u5012\u7ffb\uff0c\u8eab\u5b50\u53c8\u5411\u4e0a\u5347\u8d77\u516b\u5c3a\uff0c\u7531\u4e0a\u5411\u4e0b\u523a\u51fa\u4e86\u7b2c\u4e8c\u5200\u3002 \u3000\u3000\u539f\u6765\u4ed6\u8fd9\u98de\u9f99\u5341\u4e8c\u5200\uff0c\u5b8c\u5168\u662f\u817e\u7a7a\u98de\u640f\u4e4b\u672f\uff0c\u8eab\u4e0d\u843d\uff0c\u501f\u7740\u5200\u5251\u89e6\u63a5\u4e4b\u529b\uff0c\u60ac\u7a7a\u7ffb\u8f6c\uff0c\u53d1\u5200\u51fb\u654c\u3002 \u3000\u3000\u4ed6\u60ac\u7a7a\u53d1\u5200\uff0c\u7b2c\u4e00\u5200\u7684\u529b\u9053\uff0c\u90fd\u5341\u5206\u5f3a\u731b\uff0c\u53f6\u957f\u9752\u63a5\u4e0b\u4e86\u4e09\u5200\u4e4b\u540e\uff0c\u611f\u89c9\u5230\u624b\u8155\u9690\u9690\u53d1\u9ebb\u3002 \u3000\u3000\u7ae0\u8d85\u53d1\u51fa\u4e86\u7b2c\u56db\u5200\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e0d\u518d\u786c\u63a5\u5bf9\u65b9\u7684\u5200\u52bf\uff0c\u5ffd\u7136\u95f4\uff0c\u5411\u4e00\u4fa7\u95ea\u907f\u516b\u5c3a\u3002 \u3000\u3000\u7ae0\u8d85\u5288\u843d\u5200\u52bf\uff0c\u65e0\u5904\u501f\u5200\uff0c\u843d\u7740\u5728\u5b9e\u5730\u4e4b\u4e0a\u3002 \u3000\u3000\u4f46\u4ed6\u843d\u5730\u4e4b\u540e\uff0c\u7acb\u523b\u6325\u5200\u6025\u653b\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e00\u7740\u5931\u63aa\uff0c\u7acb\u523b\u9677\u5165\u4e86\u5371\u673a\u4e4b\u4e2d\u3002 \u3000\u3000\u72c2\u9f99\u5200\u6cd5\u5df2\u8fde\u7ef5\u800c\u81f3\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e00\u843d\u4e0b\u98ce\uff0c\u5b8c\u5168\u6ca1\u6709\u4e86\u8fd8\u624b\u4e4b\u529b\uff0c\u5239\u90a3\u95f4\uff0c\u88ab\u4e00\u7247\u5200\u5149\u5305\u56f4\u3002 \u3000\u3000\u4e01\u5cf0\u773c\u770b\u53f6\u957f\u9752\u9677\u5165\u4e86\u5371\u5883\uff0c\u4e0d\u7531\u4e00\u76b1\u7709\u5934\u9053\uff1a \u201c\u9a6c\u5144\uff0c\u5251\u4f7f\u5df2\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c\u54b1\u4eec\u5e94\u8be5\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u597d!\u54b1\u4eec\u5206\u5de6\u53f3\u4e24\u5219\u653b\u4e0a\u3002\u201d \u3000\u3000\u590f\u6740\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u4e14\u6162\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u590f\u5144\uff0c\u4f60\u662f\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u590f\u6740\u4f4e\u58f0\u9053\uff1a\u201c\u5251\u4f7f\u6709\u8ffd\u9b42\u4e03\u5251\uff0c\u4f46\u5374\u53ea\u7528\u4e86\u516d\u62db\uff0c\u8fd8\u6709\u4e00\u62db\uff0c\u4f60\u4eec\u53ef\u77e5\u9053\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u7528?\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a \u201c\u72c2\u9f99\u5200\u6cd5\u51cc\u5389\uff0c\u7f20\u6597\u7d27\u5bc6\uff0c\u4ed6\u5df2\u7ecf\u6ca1\u6709\u65f6\u95f4\u51fa\u624b\u4e86\u3002\u201d \u590f\u6740\u6447\u6447\u5934\u9053\uff1a \u201c\u4e0d\u662f\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u613f\u610f\u6740\u6b7b\u72c2\u9f99\uff0c\u6240\u4ee5\uff0c\u4e0d\u80af\u65bd\u51fa\u8ffd\u9b42\u7b2c\u4e03\u5251\u3002\u201d \u4e01\u5cf0\u9053\uff1a\u201c\u53cc\u65b9\u76f8\u640f\uff0c\u5251\u4f7f\u4f55\u4ee5\u4e0d\u80af\u6c42\u80dc\u5462?\u201d \u3000\u3000\u590f\u6740\u9053\uff1a \u201c\u6740\u4e86\u72c2\u9f99\u7ae0\u8d85\uff0c\u8fd8\u6709\u4e00\u4e2a\u98de\u9e70\uff0c\u800c\u4e14\u72c2\u9f99\u5982\u6b7b\uff0c\u5fc5\u7136\u4f1a\u6fc0\u8d77\u72c2\u9f99\u5821\u7684\u5168\u529b\u53cd\u51fb\uff0c\u90a3\u65f6\uff0c\u54b1\u4eec\u4eba\u6570\u5c11\uff0c\u5982\u82e5\u52a8\u8d77\u624b\u6765\uff0c\u53cc\u65b9\u52bf\u5fc5\u8981\u5c55\u5f00\u7fa4\u6bb4\uff0c\u90a3\u65f6\uff0c\u54b1\u4eec\u5c31\u5403\u4e8f\u5f88\u5927\u4e86\u3002\u201d \u3000\u3000\u4e01\u5cf0\u671b\u4e86\u573a\u4e2d\u7684\u640f\u6597\u5f62\u52bf\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u770b\u6765\u5251\u4f7f\u4e0d\u6b62\u8ffd\u9b42\u4e03\u5251\uff0c\u5c31\u662f\u4e00\u822c\u5251\u62db\u4e0a\u7684\u9020\u8be3\uff0c\u4e5f\u5341\u5206\u9ad8\u660e\u554a!\u201d \u3000\u3000\u590f\u6740\u9053\uff1a \u201c\u8ffd\u9b42\u4e03\u5251\u53ea\u662f\u4ed6\u7684\u62db\u724c\u5251\u6cd5\uff0c\u5e76\u975e\u662f\u53ea\u4f1a\u8ffd\u9b42\u4e03\u5251\u514b\u654c\u3002\u201d \u539f\u6765\uff0c\u53f6\u957f\u9752\u5df2\u9010\u6e10\u9002\u5e94\u4e86\u72c2\u9f99\u7ae0\u8d85\u7684\u5200\u6cd5\uff0c\u867d\u7136\u88ab\u56f0\u5728\u5200\u5149\u4e4b\u5185\uff0c\u4f46\u4ece\u6709\u60ca\u65e0\u9669\u4e2d\uff0c\u8f6c\u53d8\u4e3a\u4ece\u5bb9\u5e94\u4ed8\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u72c2\u9f99\u7ae0\u8d85\u4e5f\u611f\u89c9\u5230\u9047\u4e0a\u4e86\u52b2\u654c\uff0c\u5bf9\u65b9\u8fd8\u6709\u4e00\u62db\u5947\u5b66\u4e0d\u7528\uff0c\u800c\u7ae0\u8d85\u672c\u4eba\u5df2\u611f\u89c9\u5230\u81ea\u5df1\u7edd\u5bf9\u65e0\u6cd5\u6740\u6b7b\u53f6\u957f\u9752\u4e86\u3002 \u3000\u3000\u72c2\u9f99\u7684\u5200\u6cd5\uff0c\u5927\u5f00\u5927\u9616\uff0c\u5145\u6ee1\u7740\u9633\u521a\u4e4b\u529b\uff0c\u51f6\u5389\u4e4b\u6781\uff0c\u4f46\u53f6\u957f\u9752\u7684\u957f\u5251\uff0c\u5374\u662f\u4ee5\u7ef5\u7ef5\u4e0d\u7edd\u7684\u9634\u67d4\u4e4b\u52b2\u89c1\u957f\u3002 \u3000\u3000\u51f6\u5389\u7684\u5200\u52bf\uff0c\u5168\u88ab\u7ef5\u67d4\u7684\u5251\u52b2\u5316\u53bb\u3002 \u3000\u3000\u80dc\u8d1f\u867d\u7136\u8fd8\u672a\u5206\uff0c\u4f46\u72c2\u9f99\u7684\u4fe1\u5fc3\u5df2\u5931\u3002 \u3000\u3000\u53f6\u957f\u9752\u867d\u7136\u4ee5\u67d4\u514b\u5219\uff0c\u9010\u6e10\u7a33\u5b9a\u4e86\u640f\u6740\u5f62\u52bf\uff0c\u4f46\u5fc3\u4e2d\u4ea6\u662f\u6697\u6697\u9707\u52a8\u3002 \u3000\u3000\u4ed6\u81ea\u51fa\u9053\u4ee5\u6765\uff0c\u9664\u4e86\u5bf9\u9ad8\u5929\u5065\u90a3\u4e00\u6218\u4e00\u8d25\u6d82\u5730\uff0c\u548c\u4f55\u5bd2\u8863\u6253\u4e86\u4e2a\u76f8\u5f62\u89c1\u62d9\u4e4b\u5916\uff0c\u8fd9\u72c2\u9f99\u7ae0\u8d85\u662f\u4ed6\u9047\u4e0a\u7684\u7b2c\u4e09\u4e2a\u5f3a\u654c\u3002 \u3000\u3000\u8ffd\u9b42\u7b2c\u516d\u5251\uff0c\u6740\u4e86\u72c2\u9f99\u4e24\u4e2a\u4ece\u5c5e\uff0c\u4e5f\u8bb8\u90a3\u5251\u53ef\u4ee5\u4f24\u4ed6\uff0c\u4e03\u5251\u53bb\u516d\uff0c\u53ea\u6709\u6700\u540e\u4e00\u5251\u4e86\u3002 \u3000\u3000\u4f46\u7ae0\u8d85\u7684\u5200\u52bf\u4e0d\u8870\uff0c\u6700\u540e\u4e00\u5251\u51fa\u624b\uff0c\u80fd\u4e0d\u80fd\u6740\u5bf9\u65b9\uff0c\u8fd8\u662f\u4e2a\u672a\u77e5\u6570\u3002 \u3000\u3000\u9664\u4e86\u8ffd\u9b42\u4e03\u5251\u4e4b\u5916\uff0c\u53f6\u957f\u9752\u5fc3\u4e2d\u660e\u767d\uff0c\u81ea\u5df1\u7684\u5251\u6cd5\uff0c\u53ea\u80fd\u81ea\u4fdd\u3002 \u3000\u3000\u4f46\u72c2\u9f99\u7ae0\u8d85\u7684\u5200\u6cd5\u662f\u5426\u8fd8\u6709\u5947\u53d8\uff0c\u4ecd\u662f\u672a\u53ef\u9884\u6599\u3002 \u3000\u3000\u7ae0\u8d85\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u72c2\u653b\u4e09\u5200\uff0c\u75be\u9000\u516b\u5c3a\uff0c\u6a2a\u5200\u800c\u7acb\u3002 \u3000\u3000\u53f6\u957f\u9752\u5401\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u7ae0\u5144\uff0c\u597d\u5200\u6cd5\uff0c\u4e3a\u4f55\u4e0d\u6218\u4e86?\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a\u201c\u54b1\u4eec\u518d\u6253\u4e0b\u53bb\uff0c\u4e5f\u662f\u4e2a\u80dc\u8d25\u96be\u5206\u4e4b\u5c40\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u4e45\u6218\u529b\u635f\uff0c\u6253\u4e0b\u53bb\uff0c\u54b1\u4eec\u603b\u4f1a\u5206\u51fa\u80dc\u8d1f\u7684!\u201d \u3000\u3000\u7ae0\u8d85\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u9886\u6559\u4e86\u9601\u4e0b\u5251\u6cd5\uff0c\u603b\u7b97\u8ba9\u6211\u89c1\u8bc6\u4e03\u5251\u8ffd\u9b42\u4e4b\u540d\uff0c\u679c\u4e0d\u865a\u4f20\uff0c\u6211\u62e5\u6709\u7edd\u5927\u4f18\u52bf\u4f3c\u4e4e\u662f\u7528\u4e0d\u7740\u548c\u4f60\u82e6\u6218\u4e0b\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7ae0\u5144\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a\u201c\u72c2\u9f99\u3001\u98de\u9e70\u8054\u8882\u800c\u6765\uff0c\u6740\u4f60\u4e0d\u662f\u6211\u4e00\u4eba\u4e4b\u8d23\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u7ae0\u5144\u8c6a\u6c14\u5e72\u4e91\uff0c\u5982\u82e5\u548c\u98de\u9e70\u8054\u624b\u800c\u6218\uff0c\u4e0d\u6015\u8d3b\u7b11\u6b66\u6797\u5417?\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a \u201c\u6211\u867d\u672a\u8d25\uff0c\u4f46\u5df2\u77e5\u6740\u4f60\u4e4b\u96be\uff0c\u518d\u8bf4\uff0c\u4e45\u6218\u62d6\u5ef6\uff0c\u5bf9\u6211\u4e0d\u5229\uff0c\u6211\u7eb5\u6709\u820d\u547d\u82e6\u62fc\u4e4b\u5fc3\uff0c\u4f46\u98de\u9e70\u5374\u672a\u5fc5\u6709\u6b64\u8010\u6027\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u7ae0\u5144\u5b81\u8ba9\u82f1\u540d\u53d7\u635f\uff0c\u4ea6\u4e0d\u613f\u5c65\u7ea6\u51b3\u6218\u4e86\u3002\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a \u201c\u6c5f\u5c71\u4ee3\u6709\u624d\u4eba\u51fa\uff0c\u4f30\u4e0d\u5230\u4f60\u5c0f\u5c0f\u5e74\u9f84\uff0c\u7adf\u6709\u5982\u6b64\u6210\u5c31\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u5fc3\u4e2d\u5df2\u65e0\u80dc\u4f60\u7684\u628a\u63e1\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u5c0f\u5f1f\u4e1a\u5df2\u5168\u529b\u65bd\u4e3a\u4e86\u3002\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a \u201c\u4e2a\u4eba\u610f\u6c14\u4e4b\u4e89\uff0c\u5982\u82e5\u5f71\u54cd\u4e86\u5927\u5c40\uff0c\u5728\u4e0b\u5c31\u7b97\u80dc\u5f97\u9601\u4e0b\uff0c\u4ea6\u662f\u5f97\u4e0d\u507f\u5931\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u7ae0\u5144\uff0c\u600e\u77e5\u5728\u4e0b\u6ca1\u6709\u4f0f\u5175?\u201d \u3000\u3000\u7ae0\u8d85\u51b7\u7b11\u9053\uff1a \u201c\u72c2\u9f99\u3001\u98de\u9e70\u4e24\u7cbe\u9510\uff0c\u5c3d\u96c6\u4e8e\u6b64\uff0c\u5c31\u7b97\u4f60\u6709\u51e0\u4e2a\u4ece\u4eba\uff0c\u51b3\u96be\u6297\u62d2\uff0c \u53ea\u8981\u6211\u548c\u98de\u9e70\u56f0\u4f4f\u4f60\u53f6\u957f\u9752\uff0c\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u53ef\u4f7f\u4f60\u5168\u519b\u8986\u6ca1\u3002\u201d \u3000\u3000\u8fd9\u6b63\u662f\u53f6\u957f\u9752\u62c5\u5fc3\u7684\u4e8b\u3002 \u3000\u3000\u72c2\u9f99\u7ae0\u8d85\u867d\u7136\u6709\u4e9b\u72c2\uff0c\u5374\u4e5f\u77e5\u89c1\u673a\u884c\u4e8b\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u7126\u6025\uff0c\u6740\u673a\u987f\u8d77\uff0c\u957f\u5251\u5e73\u4e3e\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u8ffd\u9b42\u6709\u4e03\u5251\uff0c\u5728\u4e0b\u53ea\u7528\u51fa\u516d\u62db\uff0c\u7ae0\u5144\u65e2\u7136\u51c6\u5907\u53ec\u98de\u9e70\u76f8\u52a9\uff0c\u90a3\u5c31\u5f97\u63a5\u4e0b\u6700\u540e\u4e00\u62db\uff0c\u624d\u6709\u673a\u4f1a\u3002\u201d \u3000\u3000\u72c2\u9f99\u7ae0\u8d85\u5355\u5200\u6a2a\u5728\u9f3b\u6881\u4e4b\u524d\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u4f60\u5982\u786e\u5b9a\u8ffd\u9b42\u7b2c\u4e03\u5251\u786e\u7136\u80fd\u591f\u6740\u6211\uff0c\u90a3\u5c31\u4e0d\u59a8\u51fa\u624b\u4e00\u8bd5\u4f60\u8fd9\u6700\u540e\u4e00\u51fb\uff0c\u5982\u4e0d\u80fd\u53d6\u6211\u4e4b\u547d\uff0c\u9e7f\u6b7b\u8c01\u624b\uff0c\u90a3\u5c31\u5f88\u96be\u9884\u6599\u4e86\uff1b\u201d \u3000\u3000\u53f6\u957f\u9752\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u7ae0\u8d85\uff0c\u8ffd\u9b42\u4e03\u5251\uff0c\u53ef\u4ee5\u53cd\u590d\u51fa\u624b\uff0c\u5c31\u7b97\u4f60\u80fd\u907f\u8fc7\u7b2c\u4e03\u5251\uff0c\u4e5f\u672a\u5fc5\u5c31\u80fd\u4fdd\u4f4f\u6027\u547d\u3002\u201d \u3000\u3000\u7ae0\u8d85\u7eb5\u58f0\u800c\u7b11\uff0c\u9053\uff1a \u201c\u5927\u4e08\u592b\u6218\u6b7b\u640f\u6740\u4e4b\u4e2d\uff0c\u6b7b\u800c\u4f55\u61be\uff0c\u9601\u4e0b\u53ef\u4ee5\u51fa\u624b\u4e86\u3002\u201d \u53f6\u957f\u9752\u4e3e\u8d77\u4e86\u957f\u5251\u3002 \u3000\u3000\u7ae0\u8d85\u795e\u60c5\u4e25\u8083\uff0c\u53cc\u624b\u63e1\u5200\u3002 \u3000\u3000\u624b\u80cc\u4e0a\uff0c\u66b4\u8d77\u4e86\u4e00\u6761\u4e00\u6761\u7684\u9752\u7b4b\u3002 \u3000\u3000\u663e\u7136\uff0c\u4ed6\u5185\u5fc3\u4e4b\u4e2d\uff0c \u4e5f\u6709\u7740\u65e0\u6bd4\u7684\u7d27\u5f20\uff0e\u4e5f\u8fd0\u96c6\u4e86\u5168\u8eab\u7684\u529f\u529b\uff0c\u51c6\u5907\u4f5c\u751f\u6b7b\u7684\u4e00\u640f\u3002 \u98de\u9e70\u5de8\u5e06\uff0c\u7a81\u7136\u4ee5\u6781\u5feb\u7684\u901f\u5ea6\uff0c\u5411\u5cb8\u4e0a\u9a70\u6765\u3002 \u3000\u3000\u8ddd\u79bb\u5cb8\u7554\u8fd8\u6709\u4e09\u5341\u4f59\u4e08\u65f6\uff0c\u4e00\u6761\u4eba\u5f64\uff0c\u9661\u7136\u98de\u8d77\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u591f\u4e00\u4e0b\u5b50\u98de\u8dc3\u4e09\u5341\u4f59\u4e08\u7684\u8ddd\u79bb\uff0c \u4e0d\u8bba\u90a3\u4eba\u7684\u8f7b\u529f\u5982\u4f55\u9ad8\u660e\u3002 \u4ed6\u8eab\u4e0a\u6709\u4e24\u53ea\u7279\u6b8a\u7684\u88c5\u5907\uff0c\u5c31\u50cf\u4e00\u53ea\u5927\u9e70\u7684\u53cc\u7ffc\uff0c\u501f\u53cc\u7ffc\u7684\u6247\u52a8\u4e4b\u529b\u8f7b\u6613\u98de\u8dc3\u8fc7\u4e09\u5341\u591a\u4e08\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u90a3\u662f\u4e0d\u6298\u4e0d\u6263\uff0c\u7528\u5927\u9e70\u7fbd\u6bdb\u5236\u6210\u7684\u98de\u7ffc\uff0c\u7cbe\u5de7\u7684\u5236\u4f5c\uff0c\u914d\u5408\u7740\u7279\u5236\u7684\u8863\u670d\uff0c\u7528\u53cc\u81c2\u7684\u4f38\u5c55\u4e4b\u529b\uff0e\u64cd\u7eb5\u53cc\u7ffc\u3002 \u3000\u3000\u73b0\u5728\uff0c\u98de\u9e70\u5df2\u843d\u5728\u7ae0\u8d85\u8eab\u540e\u4e0d\u8fdc\u7684\u5730\u65b9\u3002 \u3000\u3000\u539f\u6765\uff0c\u98de\u9e70\u7684\u79f0\u53f7\uff0c\u5e76\u4e0d\u662f\u8ba9\u4eba\u53eb\u7740\u73a9\u7684\uff0c\u4ed6\u786e\u6709\u50cf\u9e70\u4e00\u6837\u98de\u7fd4\u7684\u672c\u9886\u3002 \u3000\u3000\u8fd9\u867d\u7136\u662f\u4e00\u4ef6\u4e0d\u592a\u590d\u6742\u7684\u4e8b\u60c5\uff0c\u4f46\u4e00\u4e2a\u4eba\uff0c\u80fd\u628a\u4e24\u4e2a\u9e70\u7fbd\u4f5c\u6210\u7684\u98de\u7ffc\uff0c\u64cd\u7eb5\u7684\u90a3\u4e48\u7075\u6d3b\uff0c\u4e5f\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002 \u3000\u3000\u98de\u9e70\u843d\u7740\u5b9e\u5730\u4e4b\u540e\uff0c\u5f88\u5feb\u7684\u6536\u8d77\u4e86\u53cc\u7ffc\u3002 \u3000\u3000\u4e24\u6761\u58a8\u8272\u7684\u4e1d\u5e26\uff0c\u6263\u8d77\u4e86\u53cc\u7ffc\u3002 \u3000\u3000\u6536\u655b\u7684\u53cc\u7ffc\uff0c\u53ea\u5728\u4ed6\u4e24\u808b\u95f4\u5fae\u5fae\u9f13\u8d77\u4e24\u9053\u75d5\u8ff9\uff0c\u770b\u4e0a\u53bb\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u59a8\u788d\u5230\u4ed6\u548c\u4eba\u52a8\u624b\u7684\u7075\u6d3b\u3002 \u3000\u3000\u98de\u9e70\u5f88\u5feb\u7684\u4eae\u51fa\u4e86\u5175\u5203\uff0c\u4e00\u628a\u957f\u5251\uff0c\u548c\u4e00\u628a\u5bbd\u9762\u7684\u77ed\u5200\u3002 \u3000\u3000\u5175\u5203\u51fa\u9798\u7684\u540c\u65f6\uff0c\u4eba\u5df2\u548c\u72c2\u9f99\u7ae0\u8d85\u5e76\u80a9\u800c\u7acb\u3002 \u3000\u3000\u770b\u6765\uff0c\u4ed6\u4e00\u76f4\u7559\u5fc3\u7740\u53f6\u957f\u9752\u548c\u7ae0\u8d85\u7684\u51b3\u6597\u60c5\u52bf\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u80fd\u5728\u7ae0\u8d85\u6700\u9700\u8981\u5e2e\u52a9\u7684\u65f6\u5019\uff0c\u53ca\u65f6\u800c\u6765\u3002 \u3000\u3000\u7ae0\u8d85\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5434\u5144\u6765\u4e86\u3002\u201d \u3000\u3000\u98de\u9e70\u5434\u901a\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6211\u4eec\u6709\u8fc7\u5f88\u591a\u6b21\u7684\u5408\u4f5c\uff0c\u4e00\u76f4\u5f88\u6109\u5feb\u3002\u201d \u7ae0\u8d85\u9053\uff1a \u201c\u5e38\u603b\u7ba1\u5df2\u7ecf\u6b7b\u4e86\uff0c\u672c\u6765\uff0c\u6211\u53ef\u4ee5\u6740\u4ed6\u7684\uff0c\u4f46\u6211\u6ca1\u6709\u51fa\u624b\u3002\u201d \u5434\u901a\u9053\uff1a \u201c\u5e38\u603b\u7ba1\u8fd9\u4e2a\u4eba\uff0c\u5e76\u4e0d\u662f\u592a\u574f\uff0c\u53ea\u662f\u6709\u4e9b\u592a\u7231\u9762\u5b50\u3002\u201d \u7ae0\u8d85\u9053\uff1a \u201c\u4e3b\u8981\u7684\u662f\u4ed6\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u7adf\u7136\u4f24\u5230\u4e86\u5434\u5144\uff0c\u6240\u4ee5\uff0c\u4ed6\u5e94\u8be5\u6b7b\u3002\u201d \u5434\u901a\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u54b1\u4eec\u4e00\u76f4\u662f\u5f88\u597d\u7684\u670b\u53cb\uff0c\u5144\u5f1f\u4e5f\u4e0d\u4f1a\u548c\u4ed6\u8ba1\u8f83\u3002\u201d \u3000\u3000\u7ae0\u8d85\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5f00\u7f6a\u5434\u5144\u7684\u4eba\uff0c\u6211\u7edd\u4e0d\u4f1a\u653e\u8fc7\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4eba\uff0c\u867d\u7136\u5728\u4e0d\u505c\u7684\u4ea4\u8c08\uff0c\u4f46\u56db\u9053\u76ee\u5149\uff0c\u4e00\u76f4\u76ef\u6ce8\u7740\u53f6\u957f\u9752\u3002 \u3000\u3000\u72c2\u9f99\u4e0e\u98de\u9e70\u4f3c\u4e4e\u5df2\u51c6\u5907\u8054\u624b\u63a5\u4e0b\u4e00\u5251\u3002 \u3000\u3000\u8ffd\u9b42\u4e03\u5251\u4e2d\u6700\u540e\u7684\u4e00\u62db\uff0c\u6700\u51cc\u5389\u7684\u4e00\u5251\u3002 \u3000\u3000\u98de\u9e70\u662f\u5f88\u806a\u660e\u7684\u4eba\uff0c\u4ed6\u4e0d\u6ee1\u72c2\u9f99\uff0c\u4f46\u5374\u7edd\u5bf9\u4e0d\u80fd\u8ba9\u72c2\u9f99\u6b7b\u5728\u53f6\u957f\u9752\u7684\u8ffd\u9b42\u4e03\u5251\u4e4b\u4e0b\u3002 \u3000\u3000\u8ffd\u9b42\u4e03\u5251\u5982\u82e5\u771f\u80fd\u6740\u6b7b\u7ae0\u8d85\uff0c\u4e5f\u5f88\u53ef\u80fd\u6740\u6b7b\u4ed6\u5434\u901a\u3002 \u3000\u3000\u98de\u9e70\u5434\u901a\u5f88\u660e\u767d\uff0c\u4e24\u4e2a\u4eba\u8054\u624b\u7684\u529b\u91cf\uff0c\u4f1a\u589e\u5f3a\u5f88\u591a\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u7389\u5170\u53cc\u59dd\u7a81\u7136\u8054\u8882\u800c\u51fa\u3002 \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u72c2\u9f99\u3001\u98de\u9e70\uff0c\u53ef\u662f\u6c5f\u6e56\u54cd\u53ee\u5f53\u7684\u4eba\u7269\uff0c\u4e24\u4f4d\u5982\u82e5\u8054\u624b\u5bf9\u4ed8\u4e00\u4eba\uff0c\u5c82\u4e0d\u53eb\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u4eec\u7b11\u6389\u4e86\u5927\u7259\u3002\u201d \u3000\u3000\u7ae0\u8d85\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u6b64\u4e8b\u5f88\u5c11\u6709\u673a\u4f1a\u4f20\u626c\u5230\u6c5f\u6e56\u4e0a\u53bb\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a \u201c\u53ef\u60dc\uff0c\u6211\u4eec\u4e5f\u4e0d\u6b62\u53f6\u957f\u9752\u4e00\u4e2a\u4eba\uff0c\u4e24\u4f4d\u54ea\u4e00\u4e2a\u6709\u5174\u81f4\u548c\u6211\u4eec\u4e24\u4e2a\u59d0\u59b9\u73a9\u73a9?\u201d \u3000\u3000\u5434\u901a\u9053\uff1a\u201c\u600e\u4e48?\u51c6\u5907\u7fa4\u6bb4\u4e86?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a \u201c\u4f60\u4eec\u4e24\u4e2a\u5927\u540d\u9f0e\u9f0e\u7684\u4eba\u7269\uff0c\u5408\u529b\u5bf9\u4e00\u4eba\uff0c\u4e0d\u89c9\u7740\u6709\u4e9b\u6c57\u989c\u5417?\u201d \u3000\u3000\u5434\u901a\u7eb5\u58f0\u800c\u7b11\uff0c\u9053\uff1a \u201c\u7ae0\u5144\uff0c\u65e2\u7136\u8fd9\u4e24\u4e2a\u4e2b\u5934\u8981\u51fa\u624b\uff0c\u54b1\u4eec\u4f55\u4e0d\u4e0b\u4ee4\u8981\u4ed6\u4eec\u653b\u4e0a\u6765\uff0c\u4e00\u6b21\u89e3\u51b3\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5982\u662f\u7389\u5170\u53cc\u59dd\u4e0d\u51fa\u624b\uff0c\u5c31\u4e0d\u81f4\u4e8e\u53d1\u751f\u7fa4\u6bb4\u4e86\u3002\u201d \u3000\u3000\u98de\u9e70\u5434\u901a\u9053\uff1a \u201c\u5982\u662f\u4f60\u5c0f\u5b50\u6562\u5355\u72ec\u548c\u6211\u4e00\u51b3\u751f\u6b7b\uff0c\u90a3\u5c31\u60c5\u5f62\u4e0d\u540c\u4e86\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u600e\u4e48\u4e00\u4e2a\u4e0d\u540c\u6cd5?\u201d \u3000\u3000\u5434\u901a\u9053\uff1a\u201c\u64d2\u8d3c\u64d2\u738b\uff0c\u54b1\u4eec\u5148\u751f\u64d2\u4e86\u4f60\uff0c\u7136\u540e\uff0c\u518d\u4f5c\u9053\u7406\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u597d!\u6211\u548c\u4e24\u4f4d\u653e\u624b\u4e00\u62fc\uff0c\u4e0d\u8fc7\uff0c\u4e24\u4f4d\u8bf4\u8bdd\u7b97\u8bdd?\u201d \u3000\u3000\u98de\u9e70\u5434\u901a\u9053\uff1a\u201c\u600e\u4e48\u6837?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e24\u4f4d\u662f\u51c6\u5907\u5408\u529b\u6740\u6b7b\u6211\u4e86\u3002\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u4e0d\u6b7b\uff0c \u53ea\u8981\u80af\u5c48\u5c31\u6211\u72c2\u9f99\u5821\u7684\u603b\u7ba1\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\uff0c\u6211\u8981\u6295\u5165\u72c2\u9f99\u5821\uff0c\u81f3\u5c11\u8981\u6df7\u4e2a\u5821\u4e3b\u8eab\u4efd\u3002\u201d \u3000\u3000\u5434\u901a\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u5982\u6b64\u56fa\u6267\uff0c\u90a3\u5c31\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e24\u4f4d\u5982\u6b64\u6709\u4fe1\u5fc3\u53d6\u6211\u53f6\u67d0\u4eba\u4e4b\u547d\uff0c\u90a3\u5c31\u8981\u5148\u4e0b\u4e00\u9053\u4ee4\u8c15\u4e86\u3002\u201d \u3000\u3000\u5434\u901a\u9053\uff1a\u201c\u4ec0\u4e48\u4ee4\u8c15?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u4e24\u4f4d\u6ca1\u6709\u6740\u6b7b\u6211\u53f6\u67d0\u4eba\u4e4b\u524d\uff0c\u7edd\u5bf9\u4e0d\u8bb8\u5c5e\u4e0b\u51fa\u624b\u3002\u201d \u5434\u901a\u9053\uff1a\u201c\u7ae0\u5144\uff0c\u8fd9\u5c0f\u5b50\u597d\u5927\u7684\u53e3\u6c14\u3002\u201d \u3000\u3000\u7ae0\u8d85\u9053\uff1a \u201c\u53f6\u957f\u9752\uff0c\u5c31\u8fd9\u6837\u4e00\u8a00\u4e3a\u5b9a\u4e86\uff0c\u4f60\u6ca1\u6709\u65ad\u6c14\u4e4b\u524d\uff0c\u6211\u4eec\u4e0d\u4f1a\u8ba9\u4ed6\u4eec\u51fa\u624b\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a \u201c\u5509!\u7ae0\u5144\u539f\u672c\u548c\u5144\u5f1f\u6709\u7ea6\uff0c\u4f46\u5374\u60f3\u4e0d\u5230\uff0c\u54b1\u4eec\u8fd8\u6ca1\u6709\u5206\u51fa\u80dc\u8d1f\uff0c\u7ae0\u5144\u5df2\u591a\u4e86\u4e00\u4e2a\u5e2e\u624b\uff0c\u6240\u4ee5\uff0c\u5144\u5f1f\u89c9\u5f97\uff0c\u4e24\u4f4d\u8fd8\u662f\u5148\u4f20\u4e0b\u4ee4\u8c15\uff0c\u5144\u5f1f\u624d\u80fd\u76f8\u4fe1\u3002\u201d \u3000\u3000\u5434\u901a\u9053\uff1a \u201c\u5175\u4e0d\u538c\u8bc8\uff0c\u8d8a\u8bc8\u8d8a\u597d\uff0c\u54b1\u4eec\u4e0d\u662f\u4ea4\u670b\u53cb\uff0c\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u4fe1\u4e49\u4e8c\u5b57\uff0c\u7ae0\u5144\uff0c\u4e0d\u8981\u548c\u4ed6\u591a\u8d39\u53e3\u820c\u4e86\uff0c\u54b1\u4eec\u51fa\u624b\u3002\u201d \u3000\u3000\u957f\u5251\u4e00\u6325\uff0c\u523a\u51fa\u4e86\u4e00\u5251\u3002 \u3000\u3000\u5170\u767d\u4e00\u626c\u624b\u4e2d\u9e26\u53e3\u5200\uff0c\u7a81\u7136\u51b2\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u8eab\u8eaf\u75be\u95ea\uff0c\u957f\u5251\u6a2a\u51fa\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u5251\u52bf\u5374\u4e0d\u662f\u8f6c\u5411\u5434\u901a\uff0c\u800c\u662f\u5c01\u4f4f\u4e86\u5170\u767d\u7684\u9e26\u53e3\u5200\u3002 \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5251\u4f7f\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5170\u767d\uff0c\u9000\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u72c2\u9f99\u3001\u98de\u9e70\u8054\u624b\u653b\u4f60\u4e00\u4eba\uff0c\u592a\u4e0d\u516c\u5e73\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6c5f\u6e56\u4e0a\uff0c\u5f31\u8089\u5f3a\u98df\uff0c\u54ea\u6709\u516c\u5e73\u7684\u4e8b\u60c5\u3002\u201d \u7389\u7fe0\u9053\uff1a \u201c\u53f6\u5144\uff0c\u6211\u548c\u820d\u59b9\uff0c\u90fd\u5df2\u7ecf\u662f\u4e0d\u4e45\u4e8e\u4e16\u7684\u4eba\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u5979\u5728\u6b7b\u53bb\u4e4b\u524d\uff0c\u4e3a\u53f6\u5144\u7565\u6548\u7ef5\u529b\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a \u201c\u6211\u59d0\u59d0\u8bf4\u7684\u662f\u554a!\u5982\u82e5\u6211\u4eec\u6bd2\u53d1\u800c\u6b7b\uff0c\u5012\u4e0d\u5982\u8f70\u8f70\u70c8\u70c8\u5730\u6253\u4e00\u4e2a\u8840\u6e85\u9ec4\u6c99\uff0c\u53f6\u5144\uff0c\u6700\u597d\u522b\u963b\u6b62\u6211\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u662f\u6211\u963b\u6b62\u4f60\uff0c\u800c\u662f\u6211\u4e0d\u5e0c\u671b\u5c55\u5f00\u4e00\u573a\u7fa4\u6218\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f60\u771f\u7684\u76f8\u4fe1\u4ed6\u4eec\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u76f8\u4fe1\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a \u201c\u8fd9\u5c31\u662f\u4e86\uff0c \u4f60\u65e2\u7136\u4e0d\u76f8\u4fe1\u4ed6\u4eec\uff0c \u4f60\u4e07\u4e00\u4f24\u5728\u4ed6\u4eec\u4e24\u4eba\u8054\u624b\u4e4b\u4e0b\uff0c\u4ed6\u4eec\u53ef\u4ee5\u4e0b\u4ee4\u7fa4\u653b\uff0c \u4f60\u80dc\u4e86\uff0c \u4ed6\u4eec\u4e00\u6837\u4ee4\u4eba\u4e00\u62e5\u800c\u4e0a\uff0c\u5012\u4e0d\u5982\u7531\u6211\u820d\u547d\u62b5\u4f4f\u5176\u4e2d\u4e4b\u4e00\uff0c \u4f60\u5168\u529b\u6740\u6b7b\u2014\u4eba\uff0c \u53ea\u8981\u72c2\u9f99\u3001\u98de\u9e70\u5c31\u6b7c\uff0c\u4ed6\u4eec\u4eba\u6570\u867d\u591a\uff0c\u4e5f\u5c31\u4e0d\u662f\u4e3a\u8651\u4e86\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u5170\u767d\uff0c\u4f60\u8fd9\u6837\u4e0d\u662f\u5e2e\u52a9\u6211\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u63a5\u9053\uff1a \u201c\u6211\u548c\u4eba\u62da\u547d\uff0c \u53ea\u662f\u60f3\u8bd5\u8bd5\u81ea\u5df1\u7684\u6b66\u529f\u5982\u4f55\uff0c\u4e5f\u6ca1\u6709\u6253\u7b97\u5e2e\u52a9\u4f60\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u4f60\u5982\u51fa\u624b\u7684\u540e\u679c\u5417?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u987e\u5fcc\u7684\u592a\u591a\u4e86\uff0c \u4f60\u5982\u65e9\u4e9b\u653e\u624b\u6740\u4e86\u72c2\u9f99\uff0c\u6b64\u523b\u5f62\u52bf\uff0c\u65e9\u5df2\u662f\u53e6\u4e00\u4e2a\u5c40\u9762\uff0c\u8fd9\u4f4d\u98de\u9e70\u4e5f\u672a\u5fc5\u6562\u4e8e\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u9e26\u53e3\u5200\u7a81\u7136\u4e00\u626c\uff0c\u5bd2\u8292\u4e00\u95ea\uff0c\u76f4\u53d6\u98de\u9e70\u3002 \u3000\u3000\u53f6\u957f\u9752\u773c\u770b\u65e0\u6cd5\u963b\u6b62\u5170\u767d\u51fa\u624b\uff0c \u53ea\u597d\u6325\u5251\u653b\u5411\u72c2\u9f99\u7ae0\u8d85\u3002 \u4e8b\u5b9e\u4e0a\uff0c\u5170\u767d\u8bf4\u7684\u4e5f\u4e0d\u9519\uff0c \u53ea\u8981\u80fd\u6740\u4e86\u72c2\u9f99\u3001\u98de\u9e70\uff0c\u86c7\u65e0\u5934\u4e0d\u884c\uff0c\u4ed6\u4eec\u867d\u7136\u6709\u5f88\u591a\u7684\u5c5e\u4e0b\uff0c\u4f46\u6307\u6325\u4e4f\u4eba\uff0c \u672a\u5fc5\u80fd\u53d1\u6325\u51fa\u591a\u5927\u7684\u6548\u679c\u3002 \u5434\u901a\u51b7\u7b11\u4e00\u58f0\uff0c\u53f3\u624b\u4e2d\u957f\u5251\u4e00\u7ffb\uff0c\u6a2a\u51fb\u9e26\u53e3\u5200\uff0c \u4eba\u5374\u75be\u5feb\u7684\u6b3a\u8fd1\u4e86\u5170\u767d\uff0c\u5de6\u624b\u5bbd\u9762\u5200\u75be\u65a9\u53f3\u81c2\u3002 \u3000\u3000\u957f\u5251\u5c01\u4f4f\u9e26\u53e3\u5200\uff0c\u77ed\u5200\u653b\u654c\uff0c \u5200\u5251\u4e4b\u95f4\uff0c\u914d\u53f0\u7684\u5404\u6781\u5999\u7528\u3002 \u5170\u767d\u5ffd\u7136\u4ef0\u9762\u5411\u5730\u4e0a\u5012\u5367\u4e0b\u53bb\uff0c \u907f\u5f00\u4e86\u5434\u901a\u2014\u5200\u3002 \u5434\u901a\u5fc3\u4e2d\u51b7\u7b11\uff0c\u5fd6\u9053\uff1a \u201c\u65bd\u7528\u94c1\u677f\u6865\u7684\u8eab\u6cd5\uff0c\u4e5f\u80fd\u907f\u5f00\u5434\u7237\u4e00\u5200\uff0c\u5c82\u4e0d\u662f\u592a\u8fc7\u4f4e\u4f30\u6211\u4e86\u3002\u201d \u3000\u3000\u5bbd\u9762\u5200\u7a81\u7136\u6536\u56de\uff0c\u7565\u4e00\u505c\u987f\uff0c\u6a2a\u65a9\u8fc7\u53bb\u3002 \u3000\u3000\u65f6\u95f4\u7b97\u7684\u662f\u5de7\u5230\u597d\u5904\uff0c\u5982\u82e5\u5170\u767d\u7528\u7684\u662f\u94c1\u677f\u6865\uff0c \u4e5f\u6b63\u597d\u662f\u5979\u633a\u8eab\u800c\u8d77\u7684\u65f6\u5019\uff0c\u8fd9\u4e00\u5200\u6b63\u597d\u65a9\u4e2d\u5170\u767d\u3002 \u3000\u3000\u5374\u4e0d\u6599\u5170\u767d\u8eab\u5012\u5367\u5728\u5730\u4e0a\u4e4b\u540e\uff0c\u7a81\u7136\u5411\u4e00\u4fa7\u6eda\u4e86\u8fc7\u53bb\uff0c\u9e26\u53e3\u5200\u4e5f\u968f\u7740\u5979\u6eda\u52a8\u7684\u8eab\u8eaf\uff0c\u5012\u98de\u56de\u6765\u3002 \u3000\u3000\u539f\u6765\uff0c\u90a3\u9e26\u53e3\u5200\u540e\u9762\u6709\u4e00\u9053\u5f88\u7ec6\u7684\u4e1d\u7d22\uff0c\u8fd9\u9e26\u53e3\u5200\u662f\u5170\u767d\u7684\u5175\u5203\uff0c\u4e5f\u662f\u6697\u5668\u3002 \u3000\u3000\u98de\u9e70\u5434\u901a\u4e00\u51fb\u843d\u7a7a\u3002 \u3000\u3000\u7389\u7fe0\u7a81\u7136\u6b3a\u8eab\uff0c\u5de6\u624b\u4e00\u67c4\u94f6\u5200\uff0c\u53f3\u624b\u4e00\u6839\u91d1\u68d2\u3002 \u3000\u3000\u5987\u9053\u4eba\u5bb6\uff0c\u5f88\u5c11\u65bd\u7528\u8fd9\u79cd\u5175\u5203\uff0c\u4f46\u7389\u7fe0\u65bd\u7528\u4e86\uff0c\u7528\u8fd9\u6837\u53cd\u5e38\u7684\u5175\u5203\uff0c\u5fc5\u7136\u6709\u5176\u5999\u7528\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u7389\u5170\u53cc\u59dd\u7684\u5175\u5203\uff0c\u90fd\u6709\u4e9b\u602a\uff0c\u5145\u6ee1\u7740\u90aa\u6c14\u3002 \u3000\u3000\u7389\u7fe0\u94f6\u5200\u8fde\u95ea\uff0c\u5288\u51fa\u4e09\u5200\uff0c\u7b11\u9053\uff1a \u201c\u6211\u4eec\u59d0\u59b9\uff0c\u4e24\u4e2a\u5973\u6d41\u4e4b\u8f88\uff0c\u5bf9\u4ed8\u4f60\u5927\u540d\u9f0e\u9f0e\u7684\u98de\u9e70\uff0c\u4e0d\u7b97\u662f\u4e22\u4eba\u5427\uff01\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u4e2d\u91d1\u68d2\u3001\u94f6\u5200\uff0c\u5374\u662f\u5bc6\u5982\u72c2\u96e8\u822c\u8fde\u8fde\u653b\u51fa\u3002 \u3000\u3000\u5170\u767d\u7684\u9e26\u53e3\u5200\u4e5f\u5377\u4e86\u4e0a\u6765\uff0c\u7389\u5170\u53cc\u59dd\uff0c\u5408\u529b\u7f20\u4f4f\u4e86\u98de\u9e70\u3002 \u3000\u3000\u5bf9\u4e8c\u5973\u7684\u5175\u5203\uff0c\u98de\u9e70\u6709\u7740\u5f88\u591a\u7684\u987e\u8651\uff0c\u603b\u89c9\u5f97\u90aa\u91cc\u90aa\u6c14\uff0c\u5fc5\u6709\u5947\u5999\u4e4b\u7528\u3002 \u3000\u3000\u8fd9\u5c31\u5206\u4e01\u4ed6\u4e0d\u5c11\u7684\u5fc3\u795e\uff0c\u4e0d\u80fd\u5168\u529b\u65bd\u5c55\u3002 \u3000\u3000\u8fd9\u4e5f\u4f7f\u4ed6\u63d0\u9ad8\u4e86\u8b66\u89c9\uff0c\u53e3\u4e2d\u8fde\u53d1\u51fa\u4e24\u58f0\u957f\u5578\u3002 \u3000\u3000\u8fd9\u662f\u4f20\u8fbe\u603b\u653b\u51fb\u7684\u4ee4\u8c15\u3002 \u3000\u3000\u53ea\u89c1\u6570\u5341\u8258\u5c0f\u8247\uff0c\u75be\u5feb\u7684\u5411\u524d\u51b2\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u98de\u9e70\u5c5e\u4e0b\u4e00\u52a8\uff0c\u72c2\u9f99\u5821\u7684\u4eba\uff0c\u4e5f\u5f00\u59cb\u5411\u4e0a\u51b2\u6765\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e01\u5cf0\u3001\u9a6c\u826f\u3001\u590f\u6740\uff0c\u4e5f\u6709\u4e86\u51c6\u5907\uff0c\u4e09\u4eba\u5411\u524d\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u539f\u6765\uff0c\u4e01\u5cf0\u5e03\u7f6e\u7684\u5f13\u7bad\uff0c\u5728\u6d45\u5c71\u5761\u524d\uff0c\u90a3\u662f\u51c6\u5907\u654c\u4eba\u767b\u9646\u4e4b\u540e\uff0c\u5728\u5c71\u5761\u524d\uff0c\u51b3\u4e00\u6b7b\u6218\u3002 \u3000\u3000\u56e0\u4e3a\u4e34\u65f6\u5236\u6210\u7684\u7af9\u5f13\uff0c\u5f88\u96be\u53ca\u8fdc\uff0c\u4e01\u5cf0\u51c6\u5907\u628a\u767b\u9646\u7684\u654c\u4eba\uff0c\u5f15\u5165\u7af9\u5f13\u5c04\u7a0b\u53ef\u53ca\u4e4b\u5904\u3002 \u3000\u3000\u5e03\u7f6e\u6e56\u6c34\u4e2d\u7684\u6697\u7483\uff0c\u53d1\u751f\u4e86\u963b\u654c\u7684\u529b\u91cf\uff0c\u5f53\u5148\u51e0\u53ea\u5feb\u821f\uff0c\u5148\u884c\u649e\u7834\u3002 \u3000\u3000\u5c0f\u8239\u4e0a\u7684\u4eba\uff0c\u4e0d\u662f\u5168\u90fd\u4f1a\u6c34\uff0c\u773c\u89c1\u8239\u88ab\u649e\u7834\uff0c\u7acb\u523b\u5411\u5cb8\u4e0a\u98de\u8dc3\u3002 \u3000\u3000\u590f\u6740\u98de\u6251\u800c\u4e0a\uff0c\u8f6f\u5200\u75be\u65a9\uff0c\u4e24\u4e2a\u8dc3\u767b\u5728\u5cb8\u4e0a\u7684\u4eba\uff0c\u811a\u8fd8\u672a\u7ad9\u7a33\uff0c\u5df2\u88ab\u4ed6\u65a9\u6bd9\u5200\u4e0b\u3002 \u3000\u3000\u4ed6\u5b66\u7684\u6740\u624b\u6b66\u529f\uff0c\u5bf9\u4ed8\u72c2\u9f99\u90a3\u6837\u9ad8\u624b\uff0c\u5f88\u96be\u53d1\u6325\u51fa\u5a01\u529b\uff0c\u4f46\u5bf9\u4ed8\u6b66\u529f\u4e0d\u5982\u4ed6\u7684\u4eba\uff0c\u5374\u662f\u6700\u51cc\u5389\u7684\u5200\u6cd5\u3002 \u3000\u3000\u7a81\u7136\uff0c\u6e56\u4e2d\u8d77\u4e86\u4e00\u9635\u6ce2\u6f9c\uff0c\u51e0\u8258\u5c0f\u8239\u7ffb\u6c89\u3002 \u3000\u3000\u72c2\u9f99\u3001\u98de\u9e70\u597d\u4f3c\u5403\u4e86\u4e00\u60ca\uff0c\u51e0\u58f0\u957f\u5578\uff0c\u98de\u8eab\u8dc3\u56de\u5927\u8239\uff0c\u5927\u5c0f\u8239\u53ea\u4ed3\u60f6\u6389\u5934\u800c\u53bb\u3002 \u3000 \u7b2c \u516d \u56de\u3000\u6069\u6069\u6028\u6028 \u3000 \u3000\u3000\u671b\u7740\u9000\u8d70\u7684\u654c\u4eba\uff0c\u4e01\u5cf0\u8138\u4e0a\u6cdb\u8d77\u4e86\u7b11\u610f\uff0c\u9053\uff1a\u201c\u539f\u672c\u662f\u4e00\u573a\u6b8a\u6b7b\u4e4b\u6218\uff0c\u4f46\u5374\u6ca1\u6709\u60f3\u5230\uff0c\u8fd9\u72c2\u9f99\u3001\u98de\u9e70\uff0c\u7adf\u7136\u4f1a\u95f9\u4e00\u4e2a\u864e\u5934\u86c7\u5c3e\u800c\u53bb\u3002\u201d \u3000\u3000\u9a6c\u826f\u9053\uff1a\u201c\u521a\u624d\uff0c\u90a3\u4e00\u9053\u7a81\u8d77\u7684\u6c34\u6ce2\uff0c\u4e00\u4e0b\u5b50\u5f04\u7ffb\u4e86\u51e0\u8258\u5feb\u821f\uff0c\u624d\u662f\u4ed6\u4eec\u64a4\u8d70\u7684\u771f\u6b63\u539f\u56e0\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\u5e2e\u4e86\u54b1\u4eec\u4e00\u4e2a\u5927\u5fd9\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u4e0d\u50cf\u662f\u4eba\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4e0d\u662f\u4eba\u662f\u4ec0\u4e48?\u96be\u9053\u8fd8\u6709\u4ec0\u4e48\u602a\u7269\u4e0d\u6210\u3002\u201d \u3000\u3000\u4e01\u5cf0\u9053\uff1a\u201c\u5b9e\u5728\u6709\u4e9b\u50cf\u602a\u7269\uff0c\u53ea\u662f\u4e0d\u77e5\u9053\u5b83\u662f\u4ec0\u4e48\u602a\u7269?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u8d35\u5821\u4e3b\u7b97\u65e0\u9057\u7b56\uff0c\u662f\u4e0d\u662f\u4ed6\u5e03\u4e0b\u7684\u4f0f\u5175?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u5927\u6982\u662f\u5427!\u5f3a\u654c\u5df2\u9000\uff0c\u54b1\u4eec\u8be5\u597d\u597d\u4f11\u606f\u4e00\u4e0b\uff0c\u7531\u6628\u591c\u5fd9\u5230\u73b0\u5728\uff0c\u5927\u5bb6\u90fd\u63d0\u7740\u4e00\u53e3\u6c14\uff0c\u8fd9\u53e3\u6c14\u4e0d\u80fd\u6c38\u8fdc\u63d0\u7740\uff0c\u4e01\u5cf0\uff0c\u4f20\u8c15\u4e0b\u793a\uff0c\u8981\u4ed6\u4eec\u597d\u597d\u5403\u4e00\u987f\uff0c\u7761\u4e00\u89c9\u3002\u201d \u3000\u3000\u4e01\u5cf0\u5e94\u58f0\u800c\u53bb\u3002 \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u5144\uff0c\u72c2\u9f99\u3001\u98de\u9e70\u5b9e\u529b\u672a\u635f\uff0c\u4ed6\u4eec\u4f1a\u5f88\u5feb\u518d\u6765\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4ed6\u4eec\u518d\u6765\u7684\u65f6\u5019\uff0c\u5fc5\u7136\u66f4\u96be\u5e94\u4ed8\u3002\u201d \u3000\u3000\u7389\u7fe0\u63a5\u9053\uff1a\u201c\u8fd9\u662f\u751f\u6b7b\u4e4b\u6218\uff0c\u6211\u4e0d\u6740\u654c\uff0c\u654c\u4fbf\u6740\u6211\uff0c\u5e0c\u671b\u4f60\u4ee5\u540e\u8bb0\u7740\u8fd9\u4ef6\u4e8b\uff0c\u518d\u6709\u6740\u6b7b\u5f3a\u654c\u7684\u673a\u4f1a\u65f6\uff0c\u5343\u4e07\u6148\u60b2\u4e0d\u5f97\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u82e6\u7b11\u4e00\u4e0b\uff0c\u5fd6\u9053\uff1a\u72c2\u9f99\u7ae0\u8d85\u5982\u679c\u662f\u90a3\u6837\u5bb9\u6613\u6740\u7684\u4eba\uff0c\u53c8\u600e\u4f1a\u6210\u4e00\u65b9\u9738\u4e3b\u3002 \u3000\u3000\u8fd9\u4e00\u6218\uff0c\u53ea\u6709\u4ed6\u81ea\u5df1\u5fc3\u4e2d\u660e\u767d\uff0c\u4ed6\u6740\u6b7b\u72c2\u9f99\u7ae0\u8d85\u7684\u673a\u4f1a\u5728\u8ffd\u9b42\u7b2c\u516d\u5251\uff0c\u4f46\u5374\u88ab\u7ae0\u8d85\u4e24\u4e2a\u6b66\u58eb\u4ee3\u66ff\u7ae0\u8d85\u800c\u6b7b\u3002 \u3000\u3000\u8ffd\u9b42\u4e03\u5251\u7684\u53d8\u5316\uff0c\u4e5f\u8bb8\u9ad8\u660e\u4e00\u4e9b\uff0c\u4f46\u4e45\u6218\u4e4b\u540e\uff0c \u5185\u529b\u5927\u635f\uff0c\u5df2\u65e0\u6cd5\u628a\u5251\u52bf\u901f\u5ea6\u589e\u5feb\u3002 \u590f\u6740\u9053\uff1a \u201c\u5728\u4e0b\u7684\u610f\u601d\uff0c\u54b1\u4eec\u4e5f\u8be5\u5148\u56de\u5230\u753b\u822b\u4e0a\u4f11\u606f\u4e00\u4e0b\uff0c\u81f3\u5c11\uff0c\u72c2\u9f99\u3001\u98de\u9e70\u5728\u4e00\u4e2a\u65f6\u8fb0\u5185\uff0c\u4e0d\u4f1a\u518d\u6765\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5170\u767d\u671b\u7740\u90a3\u6d69\u701a\u7684\u6e56\u6c34\uff0c\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u59ca\u59ca\uff0c\u9519\u8fc7\u4e86\u4eca\u65e5\u7684\u653e\u624b\u4e00\u6218\uff0c\u53ea\u6015\u54b1\u4eec\u8981\u6b7b\u5728\u6bd2\u836f\u4e4b\u4e0b\u4e86\u3002\u201d \u3000\u3000\u5979\u60f3\u5230\u4e86\u8fd8\u6709\u6570\u65e5\u5373\u5c06\u7ed3\u675f\u7684\u751f\u547d\uff0c\u4e0d\u7981\u9eef\u7136\u795e\u4f24\u3002 \u3000\u3000\u90a3\u662f\u4e00\u79cd\u81ea\u60dc\u81ea\u601c\u7684\u60b2\u54c0\uff0c\u50cf\u4e00\u682a\u5c06\u8981\u7efd\u653e\u7684\u82b1\u857e\u3001\u5373\u5c06\u5410\u8273\u3001\u7ade\u79c0\uff0c\u5bf9\u4e00\u4e2a\u5341\u4e03\u516b\u5c81\u7684\u5c11\u5973\u800c\u8a00\uff0c\u751f\u547d\u662f\u90a3\u4e48\u7f8e\u597d\u3002 \u3000\u3000\u53ef\u60dc\u7684\u662f\uff0c\u8fd9\u4e00\u6735\u672a\u5f00\u653e\u7684\u84d3\u857e\uff0c\u5374\u5373\u5c06\u67af\u840e\u3002 \u3000\u3000\u4e24\u884c\u6e05\u6cea\uff0c\u7f13\u7f13\u6d41\u4e86\u4e0b\u6765\uff0c\u6302\u5728\u5170\u767d\u7684\u53cc\u988a\u4e0a\u3002 \u3000\u3000\u6e56\u98ce\u98d8\u8d77\u4e86\u5979\u7684\u957f\u53d1\u3002 \u3000\u3000\u9e26\u53e3\u5200\u5df2\u8dcc\u843d\u5728\u5730\u4e0a\uff0c\u6574\u4e2a\u7684\u4eba\u4e5f\u50f5\u7acb\u5728\u90a3\u91cc\u3002 \u3000\u3000\u7389\u7fe0\u7f13\u6b65\u884c\u4e86\u8fc7\u53bb\uff0c\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48?\u201d \u3000\u3000\u5170\u767d\u4e3e\u8d77\u8863\u8896\uff0c\u62ed\u53bb\u8138\u4e0a\u7684\u6cea\u75d5\uff0c\u9053\uff1a \u201c\u6211\u5728\u770b\u90a3\u6e56\u6c34\u4e2d\u7684\u8273\u9633\uff0c\u597d\u660e\u4eae\uff0c\u597d\u8000\u76ee\uff0c\u8fc7\u53bb\uff0c\u6211\u6ca1\u6709\u611f\u89c9\u5230\u5b83\u90a3\u4e48\u53ef\u7231\u8fc7\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u90a3\u662f\u56e0\u4e3a\uff0c\u4f60\u77e5\u9053\uff0c\u770b\u5230\u8fd9\u4e9b\u7684\u673a\u4f1a\u4e0d\u591a\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u59ca\u59ca\uff0c\u6211\u8be5\u65e9\u4e9b\u51fa\u624b\u7684\uff0c\u5c31\u7b97\u98de\u9e70\u6740\u4e86\u6211\uff0c\u6211\u4e5f\u80fd\u523a\u4e2d\u4ed6\u4e00\u5200\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5df2\u7ecf\u8fc7\u53bb\u4e86\uff0c\u59b9\u59b9\uff0c\u4e5f\u8bb8\uff0c\u6211\u4eec\u8fd8\u6709\u62fc\u547d\u7684\u673a\u4f1a\uff0c\u8d70\uff01\u54b1\u4eec\u56de\u53bb\u5427?\u201d \u3000\u3000\u590f\u6740\u6084\u7136\u8d70\u4e86\u3002 \u3000\u3000\u53f6\u957f\u9752\u4ecd\u7136\u9759\u9759\u7684\u7ad9\u5728\u90a3\u91cc\u3002 \u3000\u3000\u4e00\u5411\u5bf9\u7389\u7fe0\u767e\u4f9d\u767e\u987a\u7684\u5170\u767d\uff0c\u6b64\u523b\uff0c\u5374\u7a81\u7136\u5f88\u56fa\u6267\uff0c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u56de\u54ea\u91cc\u53bb\u5462?\u54ea\u91cc\u662f\u6211\u5bb6\uff0c\u8fd9\u91cc\u548c\u753b\u822b\u4e0a\uff0c\u6709\u4ec0\u4e48\u4e0d\u540c\u5462?\u59ca\u59ca\uff0c\u8ba9\u6211\u7559\u5728\u8fd9\u91cc\u5427!\u6211\u8981\u770b\u9ec4\u660f\u65f6\u90a3\u6ee1\u5929\u665a\u971e\uff0c\u6211\u8981\u770b\u5915\u9633\u6b8b\u7167\u65f6\uff0c\u90a3\u79cd\u51c4\u5e7d\u7684\u7f8e\u4e3d\u3002\u201d \u3000\u3000\u7389\u7fe0\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u90fd\u662f\u6211\u8fd9\u505a\u59ca\u59ca\u7684\u4e0d\u597d\uff0c\u6211\u4e0d\u80fd\u4fdd\u62a4\u4f60\uff0c\u4f7f\u4f60\u514d\u4e8e\u53d7\u5230\u4f24\u5bb3\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u63a5\u9053\uff1a\u201c\u7fe0\u59ca\uff0c\u6211\u4e0d\u662f\u6015\u6b7b\uff0c\u6211\u53ea\u662f\u89c9\u7740\u8fd9\u6837\u7b49\u6b7b\u6709\u4e9b\u4e0d\u503c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u63a5\u9053\uff1a\u201c\u5e0c\u671b\u655d\u5821\u4e3b\u8fd9\u4e00\u4e24\u5929\u80fd\u5230?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4ed6\u6765\u4e01\u53c8\u80fd\u600e\u6837?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u655d\u5821\u4e3b\u535a\u5b66\u591a\u624d\uff0c\u4e5f\u8bb8\u4ed6\u80fd\u533b\u6cbb\u4f60\u4eec\u8eab\u4e2d\u4e4b\u6bd2\u3002\u201d \u5170\u767d\u51c4\u51c9\u4e00\u7b11\uff0c\u9053\uff1b\u201c\u53f6\u5927\u54e5\uff0c\u8fd9\u5e0c\u671b\u5f88\u6e3a\u832b\uff0c\u5bf9\u65b9\u7528\u7684\u662f\u72ec\u95e8\u914d\u5236\u7684\u6bd2\u836f\uff0c\u5c31\u7b97\u662f\u6c5f\u6e56\u4e0a\u7b2c\u4e00\u6d41\u7684\u89e3\u6bd2\u9ad8\u624b\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e5f\u65e0\u6cd5\u914d\u5236\u6210\u89e3\u6bd2\u836f\u7269\u51fa\u6765\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6211\u4e86\u89e3\u5170\u767d\uff0c\u5979\u8bf4\u7684\u662f\u5b9e\u8bdd\uff0c\u5979\u5e76\u4e0d\u6015\u6b7b\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u4eec\u59ca\u59b9\uff0c\u5e38\u5e38\u9762\u5bf9\u7740\u6b7b\u4ea1\uff0c\u53ea\u662f\uff0c\u5979\u4e00\u76f4\u6ca1\u6709\u597d\u597d\u7684\u6d3b\u8fc7\uff0c\u5341\u516b\u5c81\u7684\u5973\u5b69\uff0c\u8fd8\u6ca1\u6709\u5b8c\u5168\u4f53\u4f1a\u5230\u4eba\u751f\uff0c\u5374\u5c31\u8981\u6b7b\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1b\u201c\u6211\u660e\u767d\uff0c\u73b0\u5728\uff0c\u8fd8\u6709\u89e3\u836f\u7ef4\u6301\u51e0\u5929\uff0c\u6211\u4eec\u4e5f\u8bb8\u80fd\u7814\u7a76\u51fa\u4e00\u4e2a\u529e\u6cd5\u6765?\u201d \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6218\u9635\u640f\u6740\uff0c\u6177\u6168\u8d74\u6b7b\uff0c\u65ad\u9b42\u4e8e\u950b\u9550\u4e4b\u4e0b\uff0c\u51ed\u4e00\u80a1\u8c6a\u58ee\u4e4b\u6c14\uff0c\u5e76\u4e0d\u96be\uff0c\u4f46\u8981\u6bcf\u65e5\u5750\u7b49\u6b7b\u4ea1\uff0c\u8fd9\u65e5\u5b50\u5b9e\u5728\u5f88\u96be\u8fc7\uff0c\u522b\u8bf4\u5170\u767d\u4e86\uff0c\u5c31\u662f\u6211\uff0c\u4e5f\u6709\u4e9b\u627f\u53d7\u4e0d\u4f4f\u8fd9\u4e00\u80a1\u7cbe\u795e\u4e0a\u7684\u6298\u78e8\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5343\u53e4\u8270\u96be\u552f\u4e00\u6b7b\uff0c\u6211\u80fd\u4f53\u4f1a\u5f97\u8fd9\u79cd\u5fc3\u60c5\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u5144\uff0c\u5982\u679c\u6362\u4e86\u4f60\uff0c\u4f60\u5982\u4f55\u5ea6\u8fc7\u8fd9\u51e0\u5929?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u4f1a\u53bb\u559d\u9152\uff0c\u4e00\u9189\u89e3\u5e72\u6101\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1b\u201c\u53ef\u60dc\uff0c\u5170\u767d\u4e0d\u80fd\u559d\u9152\uff0c\u6700\u597d\u7684\u5973\u513f\u7ea2\uff0c\u559d\u5230\u5979\u5634\u91cc\u4e5f\u662f\u82e6\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u8fd9\u5c31\u5f88\u96be\u4e86\uff0c\u6bd2\u6027\u672a\u53d1\u4f5c\u524d\uff0c\u4eba\u662f\u90a3\u4e48\u6e05\u9192\uff0c\u9664\u4e86\u4e00\u9189\u4e4b\u5916\uff0c\u5f88\u96be\u63a7\u5236\u5979\u4e0d\u53bb\u60f3\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1b\u201c\u6570\u65e5\u65f6\u5149\uff0c\u8f6c\u773c\u5373\u8fc7\uff0c\u4f46\u5728\u4e00\u4e2a\u7b49\u5f85\u7740\u6b7b\u4ea1\u7684\u4eba\uff0c\u8fd9\u51e0\u5929\u5374\u662f\u90a3\u4e48\u60a0\u957f\uff0c\u5170\u59b9\u65e0\u6cd5\u6392\u9063\u8fd9\u4e9b\u65f6\u95f4\uff0c\u6240\u4ee5\uff0c\u5979\u5f88\u82e6\u607c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u5728\u4e0b\u4e5f\u65e0\u6cd5\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u7389\u7fe0\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4f60\u662f\u552f\u4e00\u80fd\u5e2e\u5fd9\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u63a5\u9053\uff1a \u201c\u6b22\u4e50\u6613\u8fc7\uff0c \u826f\u5bb5\u82e6\u77ed\uff0c\u96be\u9053\u4f60\u8fd8\u77a7\u4e0d\u51fa\u5170\u59b9\u7684\u5fc3\u610f\u5417?\u5c31\u662f\u8fd9\u51e0\u5929\u65f6\u5149\uff0c\u5979\u5df2\u7ecf\u6ca1\u6709\u4ee5\u540e\uff0c\u672a\u6765\uff0c\u53f6\u5144\uff0c\u4f60\u5982\u679c\u613f\u610f\uff0c\u4f60\u53ef\u4f7f\u5979\u8fd9\u51e0\u5929\u5f97\u5230\u6b22\u4e50\uff0c\u542b\u7b11\u4e5d\u6cc9\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\uff0c\u771f\u6709\u8fd9\u4e48\u5927\u7684\u529b\u91cf?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u6709\uff0c\u53f6\u5144\u4f55\u4e0d\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u5170\u59d1\u5a18\u5a07\u5982\u6625\u82b1\uff0c\u522b\u8bf4\u8981\u6211\u966a\u5979\u51e0\u5929\uff0c\u5c31\u662f\u966a\u5979\u4e09\u5e74\u4e94\u8f7d\uff0c\u6211\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u6211\u53f6\u67d0\u4eba\uff0c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u6b63\u4eba\u541b\u5b50\uff0c\u8fc7\u53bb\u53ef\u4ee5\uff0c\u53ef\u662f\u73b0\u5728\u4e0d\u884c\u3002\u201d \u3000\u3000\u7389\u7fe0\u9ed8\u7136\u3002 \u3000\u3000\u53f6\u957f\u9752\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6211\u73b0\u5728\u662f\u9634\u9633\u5821\u7684\u5251\u4f7f\uff0c\u5e26\u7740\u8fd9\u4e48\u591a\u7684\u4eba\u624b\uff0c\u4f55\u51b5\uff0c\u98de\u9e70\uff0c\u72c2\u9f99\uff0c\u968f\u65f6\u4f1a\u5377\u571f\u91cd\u6765\uff0c\u6211\u600e\u80fd\u4e0d\u987e\u5927\u5c40\uff0c\u81ea\u5f97\u5176\u4e50\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u5904\u5883\u5f88\u96be\uff0c\u4f46\u4f60\u603b\u4e0d\u80fd\u773c\u7741\u7741\u770b\u7740\u5979\u8eab\u53d7\u714e\u71ac\uff0c\u7f6e\u4e4b\u4e0d\u7406\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5170\u767d\u5df2\u76f4\u5411\u6e56\u6c34\u4e2d\u884c\u53bb\uff0c\u6c34\u5df2\u53ca\u8170\u3002 \u3000\u3000\u7389\u7fe0\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u9053\uff1a\u201c\u5feb\u53bb\uff0c\u53eb\u5979\u4e0a\u6765\u3002\u201d \u3000\u3000\u6b64\u65f6\u6b64\u60c5\uff0c\u53f6\u957f\u9752\u5df2\u65e0\u6cd5\u591a\u60f3\uff0c\u6025\u8ffd\u8fc7\u53bb\uff0c\u4e00\u9762\u53eb\u9053\uff1a\u201c\u5170\u767d\uff0c\u4f60\u8981\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u4ed6\u8eab\u6cd5\u5feb\u901f\uff0c\u8bf4\u51fa\u53e3\uff0c\u4eba\u5df2\u8ffd\u5230\u8eab\u4fa7\uff0c\u4e00\u628a\u6293\u4f4f\u4e86\u5170\u767d\u7684\u53f3\u81c2\u3002 \u3000\u3000\u5170\u767d\u56de\u7738\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u89c9\u5f97\u597d\u70ed\u597d\u70ed\uff0c\u60f3\u5728\u6c34\u91cc\u6ce1\u4e00\u6ce1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u62d6\u7740\u5979\u4e0a\u4e86\u5cb8\uff0c\u9053\uff1a\u201c\u6e56\u6c34\u72b9\u5e26\u5bd2\u610f\uff0c\u54ea\u91cc\u4f1a\u70ed\uff0c\u5feb\u56de\u53bb\u6362\u8863\u670d\u3002\u201d \u3000\u3000\u5170\u767d\u6447\u6447\u5934\u9053\uff1a \u201c\u6211\u5fc3\u70e6\uff0c\u53f6\u5927\u54e5\uff0c\u522b\u7ba1\u6211\u597d\u5417?\u8fd9\u4e9b\u5e74\u6211\u4e00\u76f4\u542c\u7fe0\u59ca\u7684\u8bdd\uff0c\u5979\u8981\u6211\u505a\u4ec0\u4e48\uff0c\u6211\u4ece\u6765\u5c31\u6ca1\u6709\u95ee\u8fc7\u4e3a\u4ec0\u4e48?\u73b0\u5728\uff0c\u6211\u5feb\u8981\u6b7b\u4e86\uff0c\u80fd\u4e0d\u80fd\u8ba9\u81ea\u505a\u4e00\u6b21\u4e3b\u5f20?\u201d \u3000\u3000\u53f6\u957f\u9752\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u5170\u767d\uff0c\u8fd9\u548c\u4f60\u59ca\u59ca\u4f55\u5173\u5462?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u662f\u7fe0\u59ca\u8981\u4f60\u62c9\u6211\u4e0a\u6765\u7684\u3002\u201d \u3000\u3000\u7389\u7fe0\u6ca1\u6709\u8ddf\u6765\uff0c\u4eba\u4e5f\u8d70\u5f97\u8e2a\u5f71\u4e0d\u89c1\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u8fd9\u4e0d\u5173\u7389\u7fe0\u7684\u4e8b\uff0c\u5170\u767d\uff0c\u4e0d\u8981\u505a\u50bb\u4e8b\uff0c\u6c5f\u6e56\u4e0a\u4e8b\uff0c\u53d8\u5316\u4e07\u5343\uff0c\u4e5f\u8bb8\u6211\u4eec\u80fd\u5728\u4f60\u6bd2\u53d1\u4e4b\u524d\uff0c\u53d6\u5230\u89e3\u836f\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53d6\u5230\u89e3\u836f\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u89e3\u4f60\u4eec\u8eab\u4e2d\u4e4b\u6bd2\u554a!\u201d \u3000\u3000\u5170\u767d\u9053\uff1a \u201c\u4e0d\u7528\u4e86\uff0c\u8fc7\u53bb\uff0c\u6211\u4e0d\u6653\u5f97\uff0c\u4e00\u5207\u90fd\u542c\u7fe0\u59ca\u7684\uff0c\u8fd9\u51e0\u5929\u6765\uff0c\u6211\u81ea\u5df1\u5f00\u59cb\u7528\u5fc3\u53bb\u60f3\uff0c\u60f3\u4e86\u5f88\u591a\u4e8b\uff0c\u60f3\u60f3\u6211\u548c\u59ca\u59ca\u8fc7\u53bb\u4e3a\u94f6\u94b1\u6740\u4eba\uff0c\u5fc3\u91cc\u597d\u96be\u8fc7\uff0c\u53f6\u5927\u54e5\uff0c\u6211\u957f\u4e86\u8fd9\u4e48\u5927\uff0c\u4ece\u6765\u6ca1\u6709\u4e00\u4ef6\u4e8b\uff0c\u7559\u7ed9\u6211\u4e00\u4e2a\u7f8e\u4e3d\u7684\u56de\u5fc6\uff0c\u597d\u4e11\u6076\u7684\u8fc7\u53bb\uff0c\u597d\u75db\u82e6\u7684\u4eba\u751f\uff0c\u6211\u4e3a\u4ec0\u4e48\u8fd8\u8981\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5979\u8bf4\u7684\u4f3c\u662f\u800c\u975e\uff0c\u5145\u6ee1\u4e86\u75db\u6094\u3002 \u3000\u3000\u60f3\u5230\u5979\u51e0\u65e5\u540e\u5373\u5c06\u6bd2\u53d1\u800c\u6b7b\uff0c\u54ea\u91cc\u8fd8\u6709\u4ee5\u540e\uff0c\u7a81\u7136\u4f4f\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u5728\u60f3\u4e00\u4e2a\u4eba\u7684\u751f\u6b7b\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u7b11\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u4eec\u4e0d\u8981\u8c08\u751f\u6b7b\u5927\u4e8b\uff0c\u6211\u662f\u771f\u7684\u5e76\u4e0d\u6015\u6b7b\uff0c\u6211\u7684\u751f\u547d\u867d\u7136\u77ed\u4fc3\u4e00\u4e9b\uff0c\u4f46\u6211\u81ea\u5df1\u4e5f\u89c9\u7740\u8be5\u6b7b\uff0c\u60f3\u60f3\u770b\uff0c\u6211\u8fd9\u70b9\u5e74\u7eaa\uff0c\u5df2\u7ecf\u8c0b\u5bb3\u4e86\u4e0d\u5c11\u7684\u4eba\uff0c\u5929\u7406\u662d\u5f70\uff0c\u4e5f\u8be5\u6709\u62a5\u5e94\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5170\u767d\uff0c\u4f60\u6709\u8fd9\u79cd\u7f6e\u751f\u6b7b\u4e8e\u5ea6\u5916\u7684\u80f8\u895f\uff0c\u5f88\u53eb\u4eba\u656c\u4f69\u3002\u201d \u3000\u3000\u5170\u767d\u7b11\u9053\uff1a \u201c\u6211\u867d\u7136\u4e0d\u6015\u6b7b\uff0c\u4f46\u6211\u5374\u4e0d\u60f3\u7b49\u5f85\u7740\u6bd2\u53d1\u65f6\u7684\u75db\u82e6\u3002\u201d \u53f6\u957f\u80cc\u5fc3\u4e2d\u6697\u9053\uff1a\u5979\u867d\u7136\u4e0d\u8c08\u751f\u6b7b\uff0c\u4f46\u6bd5\u7adf\u662f\u4eba\u751f\u5927\u4e8b\uff0c\u5982\u679c\u8bf4\u4e00\u4e2a\u4eba\u80fd\u5b8c\u5168\u770b\u5f00\u5b83\uff0c\u8c08\u4f55\u5bb9\u6613\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u91cc\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5170\u59d1\u5a18\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u9053\uff1a \u201c\u6211\u5728\u60f3\uff0c\u5982\u662f\u6211\u65e9\u6b7b\u4e00\u5929\uff0c\u4e0d\u662f\u53ef\u4ee5\u514d\u53bb\u90a3\u6bd2\u53d1\u6b7b\u4ea1\u7684\u75db\u82e6\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u65e9\u6b7b\u4e00\u5929\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u63a5\u9053\uff1a\u201c\u5bf9!\u6211\u8981\u81ea\u5df1\u9009\u62e9\u4e00\u4e2a\u6b7b\u4ea1\u7684\u65b9\u6cd5\uff0c\u6b7b\u5f97\u5e72\u5e72\u8106\u8106\uff0c\u800c\u4e14\uff0c\u6211\u4e5f\u5e0c\u671b\u80fd\u591f\u7fe0\u59ca\u59ca\u540c\u610f\u6211\u7684\u60f3\u6cd5\uff0c\u6211\u5e7c\u5931\u6019\u6043\uff0c\u751f\u5e73\u65e0\u77e5\u5df1\uff0c\u53ea\u6709\u4f9d\u9644\u7fe0\u59ca\u59ca\uff0c\u751f\u524d\u662f\u597d\u59ca\u59ca\uff0c\u6b7b\u540e\u5e76\u9aa8\u540c\u7a74\uff0c\u4e5f\u662f\u6211\u552f\u4e00\u7684\u5b89\u6170\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u81ea\u5df1\u4e0d\u80fd\u4e3a\u5979\u89e3\u6bd2\uff0c\u5b9e\u5728\u4e5f\u60f3\u4e0d\u51fa\u4ec0\u4e48\u5b89\u6170\u7684\u8bdd\uff0c\u53ea\u597d\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u8fd9\u5012\u662f\u5f88\u65b0\u7684\u60f3\u6cd5\uff0e\u5728\u4e0b\u4e5f\u89c9\u7740\u6ca1\u6709\u4ec0\u4e48\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u597d\u6781\u5566\uff0c\u53f6\u5927\u54e5\u4e5f\u540c\u610f\u6211\u7684\u60f3\u6cd5\uff0c\u6211\u5c31\u544a\u8bc9\u7fe0\u59ca\u59ca\u53bb\uff0c\u8fd9\u4e9b\u5e74\u6765\uff1b\u6211\u4e00\u5207\u90fd\u9075\u7167\u7740\u7fe0\u59ca\u59ca\u7684\u5429\u5490\u529e\u4e8b\uff0c\u60f3\u4e0d\u5230\uff0c\u6211\u5728\u4e34\u6b7b\u4e4b\u524d\uff0c\u7adf\u7136\u4f1a\u60f3\u51fa\u4e86\u4e00\u4e2a\u597d\u4e3b\u610f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5170\u59d1\u5a18\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c \u4f60\u4eec\u59ca\u59b9\u4e2d\u6bd2\u4e4b\u4e8b\uff0c\u548c\u6211\u6709\u5173\uff0c\u6211\u5fc3\u4e2d\u5341\u5206\u4e0d\u5b89\uff0c\u6211\u53c8\u65e0\u6cd5\u89e3\u53bb\u4f60\u4eec\u8eab\u4e2d\u4e4b\u6bd2\uff0c\u6211\u5982\u80fd\u7a0d\u5c3d\u7ef5\u8584\uff0c\u5e0c\u671b\u4f60\u80fd\u7ed9\u6211\u4e00\u4e2a\u673a\u4f1a\u3002\u201d \u3000\u3000\u5170\u767d\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8981\u5e2e\u6211\u5fd9?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5728\u4e0b\u5e0c\u671b\u80fd\u4e3a\u59d1\u5a18\u4e00\u5c3d\u5fc3\u529b\u3002\u201d \u3000\u3000\u5170\u767d\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u672c\u6765\uff0c\u6211\u662f\u60f3\u8bf7\u4f60\u5e2e\u70b9\u5fd9\u7684\uff0c\u4e0d\u8fc7\uff0c\u73b0\u5728\uff0c\u4f3c\u4e4e\u662f\u6709\u70b9\u4e0d\u884c\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff0c\u4f60\u8bf4\u5427!\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u6211\u5e0c\u671b\u4f60\u80fd\u966a\u966a\u6211\uff0c\u4f46\u5728\u5f3a\u654c\u5f53\u524d\uff0c\u968f\u65f6\u53ef\u80fd\u53d1\u751f\u4e8b\u60c5\uff0c\u8fd9\u4e2a\u5fc3\u613f\uff0c\u53ea\u6015\u662f\u5f88\u96be\u5f97\u507f\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u5170\u767d\uff0c\u5728\u53ef\u80fd\u7684\u60c5\u5f62\u4e0b\uff0c\u6211\u5c3d\u91cf\u62bd\u65f6\u95f4\uff0c\u73b0\u5728\u54b1\u4eec\u56de\u53bb\u5427? \u3000\u3000\u7389\u7fe0\u8fd8\u5728\u753b\u822b\u4e0a\u7b49\u4f60\u3002\u201d \u3000\u3000\u5170\u767d\u70b9\u70b9\u5934\uff0c\u8f6c\u8eab\u884c\u53bb\uff0c\u884c\u52a8\u4e4b\u95f4\uff0c\u53d8\u5f97\u5341\u5206\u67d4\u987a\u3002 \u3000\u3000\u53f6\u957f\u9752\u7d27\u968f\u8eab\u540e\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5170\u767d\uff0c\u4f60\u5fc3\u4e2d\u8fd8\u60f3\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u6211\u5728\u60f3\uff0c\u6211\u8be5\u4e0d\u8be5\u518d\u505a\u4e00\u4ef6\u4e8b?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5509\uff0c\u4f60\u60f3\u518d\u505a\u4e00\u4ef6\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u8fc7\u4e00\u591c\u6d1e\u623f\u82b1\u70db\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u542c\u5f97\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6d1e\u623f\u82b1\u70db\uff0c\u8fd9\u5730\u65b9\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u63a5\u9053\uff1a\u201c\u6211\u4e0d\u662f\u771f\u7684\u8981\u5927\u9523\u5927\u9f13\u7684\u5750\u82b1\u8f7f\uff0c\u548c\u4eba\u5bb6\u62dc\u5802\u6210\u4eb2\uff0c\u6211\u662f\u5728\u60f3\uff0c\u5728\u6211\u6b7b\u53bb\u4e4b\u524d\uff0c\u5e0c\u671b\u5fc3\u91cc\u63a5\u7eb3\u4e00\u4e2a\u7537\u4eba\u3002\u201d \u3000\u3000\u5170\u767d\u63a5\u9053\uff1a\u201c\u6211\u4e24\u624b\u8840\u8165\uff0c\u4f46\u8eab\u4f53\u4ecd\u7136\u662f\u7389\u6d01\u51b0\u6e05\uff0c\u6211\u4e0d\u77e5\uff0c\u6211\u6e05\u767d\u6765\u6e05\u767d\u53bb\uff0c\u8fd8\u662f\u5e26\u4e00\u4efd\u4eba\u95f4\u7684\u67d4\u60c5\u518d\u53bb\uff0c\u8fd9\u95ee\u9898\u597d\u607c\u4eba\uff0c\u53f6\u5927\u54e5\uff0c\u4f60\u80fd\u7ed9\u6211\u4e00\u70b9\u610f\u89c1?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u8fd9\u4ef6\u4e8b\u597d\u7b80\u5355\uff0c\u4f46\u4e5f\u5f88\u6df1\u5965\uff0c\u662f\u6b22\u4e50\u4e5f\u662f\u9057\u61be\u3002\u201d \u3000\u3000\u5170\u767d\u7f13\u7f13\u5782\u4e0b\u5934\u53bb\uff0c\u8138\u4e0a\u6cdb\u8d77\u4e00\u7247\u7f9e\u610f\uff0c\u9053\uff1b\u201c\u53f6\u5927\u54e5\uff0c\u4f60\u662f\u4e0d\u662f\u6709\u4e9b\u770b\u4e0d\u8d77\u6211?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u5bf9\u4f60\u4eec\u8fc7\u53bb\u4f5c\u4e3a\uff0c\u6211\u542c\u5230\u8fc7\u5f88\u591a\u7684\u4f20\u8a00\uff0c\u4f46\u6211\u89c1\u5230\u4f60\u4eec\u4e4b\u540e\uff0c\u53d1\u89c9\u4e86\u5e76\u4e0d\u50cf\u4f20\u8a00\u90a3\u4e48\u56de\u4e8b\uff0c\u81f3\u5c11\uff0c\u4f60\u5df2\u6709\u662f\u975e\u4e4b\u5fc3\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u505c\u4e0b\u811a\u6b65\uff0c\u8f6c\u8fc7\u4e86\u8eab\u5b50\u3002 \u3000\u3000\u5170\u767d\u4e5f\u6709\u8b66\u89c9\uff0c\u75be\u5feb\u56de\u5934\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u8258\u5c0f\u821f\uff0c\u4e58\u98ce\u7834\u6d6a\u800c\u6765\u3002 \u3000\u3000\u8239\u5934\u4e0a\u7ad9\u7740\u4e00\u4eba\uff0c\u6b63\u662f\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u53f6\u957f\u9752\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4f55\u5144\u5c0f\u5fc3\u2026\u2026\u201d \u3000\u3000\u665a\u4e86\uff0c\u4f55\u5bd2\u8863\u7684\u5feb\u821f\uff0c\u5df2\u7136\u9a70\u8fd1\u6e56\u7554\uff0c\u649e\u4e0a\u4e86\u4e01\u5cf0\u5e03\u4e0b\u7684\u6697\u7901\u3002 \u3000\u3000\u7830\u7136\u4e00\u58f0\uff0c\u5c0f\u821f\u788e\u88c2\u3002 \u3000\u3000\u8fd9\u65f6\u4f55\u5bd2\u8863\u7684\u5c0f\u821f\u8ddd\u79bb\u6e56\u5cb8\uff0c\u8fd8\u6709\u4e24\u4e08\u5de6\u53f3\uff0c\u5c31\u5728\u5c0f\u821f\u788e\u88c2\u7684\u540c\u65f6\uff0c\u4f55\u5bd2\u8863\u98de\u8eab\u800c\u8d77\u8f7b\u98d8\u7684\u843d\u4e0a\u4e86\u6e56\u5cb8\u3002 \u3000\u3000\u90a3\u64cd\u821f\u4eba\u6c34\u91cc\u529f\u592b\u4e0d\u9519\uff0c\u4ed6\u4e0d\u80fd\u50cf\u4f55\u5bd2\u8863\u98de\u8dc3\u767b\u5cb8\uff0c\u4f46\u5374\u4ece\u6c34\u4e2d\u6e38\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u8fce\u4e86\u4e0a\u6765\uff0c\u9053\uff1a\u201c\u4f55\u5144\uff0c\u53d7\u60ca\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u4eec\u5e03\u7f6e\u7684\u6697\u77a7?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e01\u5cf0\u7684\u6770\u4f5c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u5f88\u6709\u5934\u8111\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a \u201c\u5144\u5f1f\u8d74\u63f4\u6765\u8fdf\uff0c\u4f46\u53f6\u5144\u5374\u51fb\u9000\u4e86\u72c2\u9f99\uff0c\u98de\u9e70\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a \u201c \u5c0f\u5f1f\u4e0d\u6562\u5c45\u529f\uff0c \u4e00\u662f\u5144\u5f1f\u4eec\u540c\u5fc3\u5408\u529b\uff0c\u4e8c\u6765\u662f\u6697\u6709\u52a9\u529b\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6697\u6709\u52a9\u529b\uff0c\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5144\u5f1f\u4e5f\u4e0d\u592a\u6e05\u695a\u2026\u2026\u201d \u3000\u3000\u8bdd\u9898\u4e00\u8f6c\uff0c\u63a5\u9053\uff1a\u201c\u5821\u4e3b\u4f1a\u6765\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7389\u5170\u53cc\u59dd\u8eab\u4e2d\u5947\u6bd2\uff0c\u5c31\u5728\u8fd9\u51e0\u5929\u4e4b\u5185\u53d1\u4f5c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u5982\u82e5\u4e0d\u662f\u4e3a\u8fd9\u4ef6\u4e8b\u60c5\u803d\u8bef\uff0c\u53ef\u4ee5\u65e9\u5230\u534a\u4e2a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u600e\u4e48?\u4f55\u5144\u5df2\u7ecf\u6709\u4e86\u89e3\u836f\u4e4b\u6cd5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u9053\uff1a\u201c\u6cd5\u5b50\u7075\u4e0d\u7075\uff0c\u8fd8\u8981\u8bd5\u8fc7\u624d\u77e5\u3002\u201d \u3000\u3000\u56de\u987e\u5170\u767d\u4e00\u773c\uff0c\u9053\uff1a \u201c\u5170\u59d1\u5a18\uff0c\u8d35\u59ca\u59b9\u662f\u5426\u613f\u610f\u52a0\u5165\u9634\u9633\u5821\u3002\u201d \u5170\u767d\u9053\uff1a\u201c\u5982\u82e5\u6211\u4eec\u59ca\u59b9\u771f\u80fd\u7559\u5f97\u6027\u547d\uff0c \u81ea\u7136\u53ef\u4ee5\u52a0\u5165\u9634\u9633\u5821\uff0c\u5982\u662f\u8eab\u4e2d\u4e4b\u6bd2\u65e0\u6cd5\u89e3\u53bb\uff0c\u90a3\u4e5f\u4e0d\u7528\u591a\u6b64\u4e00\u4e3e\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u53f6\u957f\u9752\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a \u201c\u53f6\u5144\uff0c\u4e8b\u60c5\u53c8\u6709\u4e86\u4e00\u4e9b\u53d8\u5316\u2026\u2026\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u53d8\u5316?\u201d \u3000\u3000\u8fd9\u65f6\u5170\u767d\u8fdc\u8fdc\u907f\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5821\u4e3b\u67e5\u770b\u5723\u6c34\uff0c\u82e6\u601d\u4e00\u65e5\u591c\uff0c\u4f3c\u4e4e\u60f3\u51fa\u4e86\u4e00\u70b9\u5185\u60c5\uff0c\u4f46\u4ed6\u8fd8\u4e0d\u80fd\u5b8c\u5168\u4e86\u89e3\uff0c\u51b3\u5fc3\u53bb\u5411\u4e24\u4f4d\u524d\u8f88\u8bf7\u6559\uff0c\u6211\u770b\u4ed6\u795e\u8272\u51dd\u91cd\uff0c\u8fd9\u4ef6\u4e8b\u53ea\u6015\u5173\u7cfb\u5f88\u5927\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u4f55\u5144\uff0c\u8fd9\u5723\u6c34\u5df2\u6d41\u5165\u4e86\u6c5f\u6e56\u4e0d\u5c11\u5e74\uff0c\u96be\u9053\u5b83\u7684\u4f5c\u7528\uff0c\u8fd8\u662f\u4e00\u4e2a\u795e\u79d8\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u53ea\u8981\u67e5\u4e00\u4e0b\uff0c\u8fd9\u5723\u6c34\u7684\u53bb\u5904\uff0c\u5c31\u7b97\u4e0d\u80fd\u67e5\u51fa\u79d8\u5bc6\uff0c\u4e5f\u53ef\u4ee5\u4e86\u7136\u5341\u4e4b\u4e03\u516b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u95ee\u9898\u5c31\u5728\u771f\u6b63\u7684\u53bb\u5904\u5f88\u96be\u67e5\uff0c\u5f88\u591a\u7684\u6c5f\u6e56\u7ec4\u5408\uff0c\u53bb\u8d2d\u5f97\u5723\u6c34\uff0c\u4f46\u4ed6\u4eec\u5e76\u975e\u81ea\u7528\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5982\u82e5\u4ed6\u4eec\u4e5f\u4e0d\u77e5\u9053\u5723\u6c34\u7684\u4f5c\u7528\uff0c\u90a3\u53c8\u4f55\u82e6\u62a2\u8d2d\u5723\u6c34\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8f6c\u4e70\uff0c\u5821\u4e3b\u6000\u7591\u5723\u6c34\u7684\u53bb\u5904\uff0c\u90fd\u6d41\u5411\u4e00\u6e90\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4f55\u5144\uff0c\u54b1\u4eec\u4e3a\u5723\u6c34\u62fc\u547d\uff0c\u4f46\u5374\u8fde\u770b\u4e5f\u6ca1\u6709\u770b\u5230\u8fc7\u5723\u6c34\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u5723\u6c34\u5e76\u4e0d\u597d\u770b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f55\u5144\u770b\u5230\u4e86?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u770b\u5230\u4e86\uff0c\u90a3\u53ea\u662f\u4e00\u79cd\u6de1\u91d1\u8272\u7684\u6c34\u2026\u2026\u201d \u3000\u3000\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u63a5\u9053\uff1a\u201c\u5821\u4e3b\u5982\u82e5\u4eca\u591c\u80fd\u5230\uff0c\u4ed6\u4f1a\u4eb2\u81ea\u544a\u8bc9\u4f60\u4e8b\u60c5\u7684\u8ba1\u5212\uff0c\u8fd9\u4ef6\u4e8b\u8981\u501f\u91cd\u7389\u5170\u53cc\u59dd\u5f88\u591a\uff0c\u8fd8\u8981\u53f6\u5144\u591a\u4e0b\u70b9\u529f\u592b\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u5979\u4eec\u5df2\u7ecf\u5bf9\u9634\u9633\u5821\u6709\u4e86\u4e00\u4efd\u9ed8\u5951\u548c\u60c5\u611f\uff0c\u5982\u771f\u6709\u7528\u5979\u4eec\u7684\u5730\u65b9\uff0c\u6211\u76f8\u4fe1\uff0c\u5979\u4eec\u4f1a\u5168\u529b\u4ee5\u8d74\uff0c\u4e0d\u8fc7\uff0c\u6700\u597d\u80fd\u89e3\u53bb\u4ed6\u4eec\u7684\u6bd2\u4f24\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u7389\u5170\u53cc\u59dd\u4e2d\u6bd2\u7684\u4e8b\uff0c\u6211\u5df2\u7ecf\u544a\u8bc9\u4e86\u5821\u4e3b\uff0c\u5821\u4e3b\u7ed9\u4e86\u6211\u4e24\u7c92\u836f\u7269\uff0c\u8fd9\u4e24\u7c92\u4e39\u836f\uff0c\u542c\u8bf4\u53ef\u4ee5\u89e3\u53bb\u5f88\u591a\u79cd\u6bd2\uff0c\u4f46\u80fd\u4e0d\u80fd\u89e3\u5f97\u5979\u4eec\u59ca\u59b9\u7684\u8eab\u4e2d\u4e4b\u6bd2\uff0c\u5144\u5f1f\u4e5f\u6ca1\u6709\u628a\u63e1\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u79cd\u4e39\u836f\uff0c\u81f3\u5c11\u53ef\u4ee5\u5ef6\u8fdf\u6bd2\u6027\u53d1\u4f5c\u7684\u65f6\u95f4\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u80fd\u591f\u5ef6\u8fdf\u591a\u4e45?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e03\u81f3\u5341\u65e5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5982\u662f\u836f\u4e0d\u5bf9\u75c7\uff0c\u4e03\u5230\u5341\u65e5\u540e\uff0c\u8fd8\u4f1a\u53d1\u4f5c\u4e86\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u96be\u9053\u5821\u4e3b\u4e5f\u4e0d\u80fd\u89e3\u51b3\u5417\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u786e\u5177\u6709\u591a\u65b9\u9762\u7684\u624d\u80fd\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4e0d\u662f\u4e07\u80fd\uff0c\u771f\u6b63\u8981\u627e\u51fa\u89e3\u6bd2\u836f\u7269\uff0c\u5fc5\u987b\u8981\u7ed9\u4ed6\u65f6\u95f4\uff0c\u6211\u60f3\uff0c\u8fd9\u4ef6\u4e8b\u4e0d\u4f1a\u96be\u4f4f\u4ed6\uff0c\u53ea\u662f\uff0c\u4ed6\u73b0\u5728\u592a\u5fd9\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7f13\u7f13\u7531\u6000\u4e2d\u6562\u51fa\u4e24\u7c92\u4e39\u4e38\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u7c92\u836f\u4e38\uff0c\u4f60\u62ff\u53bb\uff0c\u4ea4\u7ed9\u5979\u4eec\uff0c\u8ba9\u5979\u4eec\u5148\u670d\u4e0b\u53bb\uff0c\u770b\u770b\u53cd\u5e94\u5982\u4f55?\u5821\u4e3b\u8bf4\uff0c\u8fd9\u79cd\u836f\u7269\uff0c\u53ef\u89e3\u6570\u5341\u79cd\u5947\u6bd2\uff0c\u4e5f\u8bb8\u7389\u5170\u53cc\u59dd\u8eab\u4e2d\u4e4b\u6bd2\uff0c\u4f1a\u88ab\u8fd9\u79cd\u836f\u7269\u89e3\u4e86\u5462?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u73b0\u5728\u4e5f\u53ea\u6709\u5148\u78b0\u78b0\u8fd0\u6c14\u4e86\u2026\u2026\u201d \u3000\u3000\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u63a5\u9053\uff1a \u201c\u4f55\u5144\uff0c\u8fd9\u5730\u65b9\u4f4f\u7684\u6709\u4eba\uff0c\u60ca\u9000\u72c2\u9f99\uff0c\u98de\u9e70\uff0c\u5979\u5e2e\u4e86\u5f88\u5927\u7684\u5fd9\u3002\u201d \u4f55\u5bd2\u8863\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u53f6\u957f\u9752\u7b80\u7565\u7684\u8bf4\u660e\u4e86\u7ecf\u8fc7\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1b \u201c\u65e2\u7136\u5979\u4eec\u4e0d\u613f\u73b0\u5728\u548c\u6211\u4eec\u76f8\u89c1\uff0c\u6700\u597d\uff0c\u6682\u65f6\u5148\u522b\u60ca\u52a8\u5979\u4eec\uff0c\u89c1\u8fc7\u5821\u4e3b\u4e4b\u540e\uff0c\u518d\u4f5c\u9053\u7406\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u4f55\u5144\uff0c\u54b1\u4eec\u9634\u9633\u5821\u7684\u5b9e\u529b\uff0c\u662f\u4e0d\u662f\u53ef\u4ee5\u548c\u9ad8\u5929\u5065\u653e\u624b\u4e00\u62fc\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d\u50cf\u4e0d\u884c\uff0c\u9ad8\u5929\u5065\u7fbd\u7ffc\u5df2\u6210\uff0c\u5821\u4e3b\u82e6\u601d\u4e86\u4e00\u65e5\u591c\uff0c\u89c9\u7740\u53ea\u6709\u4e00\u4e2a\u5bf9\u4ed8\u4ed6\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u529e\u6cd5?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5148\u524a\u53bb\u4ed6\u7684\u90e8\u5206\u5b9e\u529b\uff0c\u660e\u663e\u70b9\u8bf4\uff0c\u5c31\u662f\u4e2a\u4e2a\u51fb\u7834\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u548c\u98de\u9e70\u3001\u72c2\u9f99\u4e00\u6218\u4e4b\u540e\uff0c\u6211\u53d1\u89c9\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u53ef\u4ee5\u60f3\u6cd5\u5b50\u628a\u4ed6\u4eec\u6536\u4e3a\u6211\u7528\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u82f1\u96c4\u6240\u89c1\u7565\u540c\uff0c\u5821\u4e3b\u4e5f\u6b63\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f55\u5144\uff0c\u90a3\u63a7\u5236\u5723\u6c34\u7684\uff0c\u4e5f\u662f\u9ad8\u5929\u5065\u7684\u4eba\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1b\u201c\u8fd9\u5c31\u662f\u5821\u4e3b\u8fd1\u65e5\u8017\u53bb\u5fc3\u8840\u6700\u591a\u7684\u5730\u65b9\uff0c\u4ed6\u4eec\u662f\u53e6\u4e00\u80a1\u52bf\u529b\uff0c \u76ee\u4e0b\u6c5f\u6e56\u4e0a\u9f0e\u8db3\u800c\u7acb\u7684\uff0c\u5c31\u662f\u9ad8\u5929\u5065\uff0c\u9634\u9633\u5821\uff0c\u548c\u90a3\u80a1\u4e0d\u53ef\u6349\u6478\u7684\u52bf\u529b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u67e5\u51fa\u4ed6\u4eec\u7684\u8eab\u4efd\u7531\u6765\u5417\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u597d\u50cf\u5df2\u7ecf\u6709\u4e86\u70b9\u7709\u76ee\uff0c\u7b49\u4ed6\u89e3\u5f00\u4e86\u5723\u6c34\u4e4b\u79d8\uff0c\u5927\u6982\u5c31\u53ef\u4ee5\u80af\u5b9a\u90a3\u80a1\u795e\u79d8\u52bf\u529b\u7684\u7531\u6765\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1b\u201c\u5947\u602a\u7684\u662f\uff0c\u9ad8\u5929\u5065\u600e\u4e48\u4f1a\u5bb9\u8bb8\u8fd9\u6837\u4e00\u4e2a\u795e\u79d8\u52bf\u529b\u5b58\u5728\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u6279\u4eba\u53ea\u5728\u5236\u9020\u5723\u6c34\uff0c\u548c\u9ad8\u5929\u5065\u8fd8\u6ca1\u6709\u5f88\u76f4\u63a5\u7684\u51b2\u7a81\u3002\u201d - \u3000\u3000\u7b11\u4e00\u7b11\u63a5\u9053\uff1a\u201c\u5821\u4e3b\u67e5\u6e05\u695a\u4ed6\u8eab\u4efd\u7531\u6765\u4e4b\u540e\uff0c\u5c31\u4f1a\u60f3\u6cd5\u5f15\u8d77\u4ed6\u4eec\u4e4b\u95f4\u7684\u51b2\u7a81\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f55\u5144\uff0c\u8fd1\u5e74\u6765\uff0c\u6c5f\u6e56\u4e0a\u7684\u53d8\u5316\u5f88\u5927\uff0c\u6211\u5c31\u60f3\u4e0d\u9041\uff0c\u5c11\u6797\uff0c\u6b66\u5f53\uff0c\u8fd9\u4e9b\u6b63\u6d3e\u5927\u95e8\u6237\uff0c\u600e\u4f1a\u5750\u89c6\u4e0d\u7ba1\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5509!\u5c11\u6797\u5bfa\u4e8c\u5341\u5e74\u524d\uff0c\u53d1\u751f\u4e00\u6b21\u5185\u4e89\uff0c\u4f24\u4e86\u4e0d\u5c11\u7684\u5143\u6c14\uff0c\u8fd9\u4e9b\u5e74\uff0e\u8fd8\u5728\u6574\u987f\uff0c\u4e0d\u95ee\u6c5f\u6e56\u4e2d\u4e8b\uff0c\u81f3\u4e8e\u6b66\u5f53\uff0c\u90a3\u5c31\u5f88\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f88\u96be\u8bf4\u4e86?\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5821\u4e3b\u62c5\u5fc3\u7684\u662f\uff0c\u6b66\u5f53\u6d3e\u53ef\u80fd\u5df2\u4e3a\u9ad8\u5929\u5065\u6240\u63a7\u5236\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u5c31\u96be\u602a\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u5929\u5065\u7684\u806a\u660e\uff0c\u5c31\u5728\u4ed6\u80fd\u591f\u628a\u81ea\u5df1\u5c5e\u4e0b\uff0c\u53d8\u6210\u4e86\u5f88\u591a\u80a1\u4e0d\u540c\u7684\u7ec4\u7ec7\uff0c\u6709\u540d\u6b63\u8a00\u987a\u7684\u9556\u5c40\uff0c\u4e5f\u6709\u6f5c\u9690\u6c5f\u6e56\u7684\u9ed1\u9053\u7ec4\u7ec7\uff0c\u770b\u4e0a\u53bb\uff0c\u8fd9\u4e9b\u4eba\uff0c\u548c\u4ed6\u90fd\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u9664\u4e86\u90a3\u4e9b\u7ec4\u7ec7\u4e2d\u9996\u8111\u4eba\u7269\u4e4b\u5916\uff0c\u5927\u90fd\u4e0d\u77e5\u5185\u60c5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u795e\u60c5\u7a81\u7136\u95f4\u53d8\u5f97\u5341\u5206\u4e25\u8083\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u53f6\u5144\uff0c\u5821\u4e3b\u7684\u667a\u7565\u5982\u4f55?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5f3a\u8fc7\u5144\u5f1f\u5341\u500d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u8ddf\u4e86\u5821\u4e3b\u4e0d\u5c11\u5e74\uff0c\u4e0d\u8bba\u9047\u4e0a\u4e86\u4efb\u4f55\u7684\u56f0\u96be\u5927\u4e8b\uff0c\u4ed6\u90fd\u80fd\u4ece\u5bb9\u5e94\u4ed8\uff0c\u4e5f\u4ece\u6765\u6ca1\u6709\u770b\u5230\u8fc7\u4ed6\u6709\u8fc7\u4e3a\u96be\u7684\u795e\u8272\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a \u201c\u96be\u9053\uff0c\u5821\u4e3b\u9047\u4e0a\u4e86\u4ec0\u4e48\u4e3a\u96be\u7684\u5927\u4e8b?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u6bcf\u4e00\u6b21\uff0c\u6211\u548c\u4ed6\u89c1\u9762\u65f6\uff0c\u4ed6\u603b\u662f\u5145\u6ee1\u7740\u6b22\u6109\uff0c\u4f46\u8fd9\u4e00\u6b21\uff0c\u6211\u770b\u51fa\u4e86\u4ed6\u662f\u5f3a\u989c\u6b22\u7b11\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u5462\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u95ee\u8fc7\u4ed6\uff0c\u4f46\u4ed6\u4e0d\u80af\u8bf4\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f55\u5144\u7684\u610f\u601d\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u4e0d\u4f1a\u56e0\u56f0\u96be\u800c\u754f\u7f29\uff0c\u4e5f\u4e0d\u4f1a\u4e3a\u8270\u9669\u800c\u62c5\u5fc3\uff0c\u6240\u4ee5\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u5fc5\u7136\u548c\u4ed6\u4e2a\u4eba\u5173\u7cfb\u5f88\u5927!\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u4ed6\u9047\u4e0a\u7684\u56f0\u96be\uff0c\u662f\u4ed6\u4e2a\u4eba\u7684\u79c1\u4e8b?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4ed6\u4e0d\u4fbf\u7528\u7ec4\u7ec7\u7684\u529b\u91cf\u5e2e\u52a9\u4ed6\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54b1\u4eec\u5982\u4f55\u5e2e\u52a9\u4ed6?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53f6\u5144\uff1b\u6b64\u5730\u4e4b\u4e8b\uff0c\u56e0\u60c5\u52bf\u53d8\u5316\uff0c\u4f3c\u662f\u7528\u4e0d\u7740\u518d\u548c\u72c2\u9f99\u3001\u98de\u9e70\u4f5c\u4e00\u573a\u6fc0\u70c8\u7684\u6076\u6597\uff0c\u54b1\u4eec\u5982\u80fd\u5e2e\u4e0a\u5821\u4e3b\u7684\u5fd9\uff0c\u6700\u597d\u80fd\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u4eba\u624b\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8981\u4e01\u5cf0\u5e26\u7740\u4ed6\u4eec\u64a4\u79bb\u6b64\u5730\uff0c\u518d\u7ea6\u4e2a\u4f1a\u5408\u4e4b\u5904\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\u3002 \u3000\u3000\u56de\u5230\u4e86\u753b\u822b\u4e4b\u4e0a\uff0c\u4f55\u5bd2\u8863\u5148\u53d6\u51fa\u4e24\u7c92\u89e3\u836f\uff0c\u4ea4\u7ed9\u7389\u5170\u53cc\u59dd\uff0c\u8ba9\u5979\u5148\u884c\u670d\u4e0b\uff0c\u518d\u76d8\u5750\u8c03\u606f\u3002 \u3000\u3000\u53f6\u957f\u9752\u5374\u53ec\u6765\u4e86\u9a6c\u826f\u3001\u4e01\u5cf0\uff0c\u8981\u4ed6\u4eec\u6697\u4f5c\u51c6\u5907\uff0c\u665a\u4e0a\uff0c\u53ef\u80fd\u8981\u79bb\u5f00\u6b64\u5730\u3002 \u3000\u3000\u4ed6\u6709\u4e9b\u60f3\u5ff5\u971e\u513f\uff0c\u4f46\u4ed6\u660e\u767d\uff0c\u73b0\u5728\uff0c\u7edd\u5bf9\u4e0d\u53ef\u4ee5\u53bb\u770b\u5979\u3002 \u3000\u3000\u8fd9\u662f\u4e2a\u79d8\u5bc6\uff0c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u8fd8\u53ea\u6709\u81ea\u5df1\u4e00\u4e2a\u4eba\u77e5\u9053\u7684\u79d8\u5bc6\uff0c\u4ed6\u4e0d\u60f3\u4f7f\u8fd9\u4e2a\u79d8\u5bc6\u6cc4\u6f0f\u51fa\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u4f20\u4e0b\u4e86\u4ee4\u6e1d\u540e\uff0c\u7acb\u523b\u548c\u4f55\u5bd2\u8863\u4e00\u540c\u5230\u6c99\u6ee9\u4e4b\u4e0a\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u76d8\u819d\u800c\u5750\uff0c\u9762\u5bf9\u7740\u6d69\u701a\u7684\u6e56\u6ce2\uff0c\u4e00\u9762\u76d1\u89c6\u654c\u52bf\uff0c\u4e00\u9762\u6b23\u8d4f\u7740\u6e56\u5149\u666f\u8272\u3002 \u3000\u3000\u4f46\u6700\u91cd\u8981\u7684\u662f\uff0c\u4ed6\u4eec\u5728\u7b49\u4eba\u3002 \u3000\u3000\u53f6\u957f\u9752\u5bf9\u9634\u9633\u5821\u9ec4\u5821\u4e3b\u7684\u624d\u80fd\uff0c\u5341\u5206\u656c\u4f69\uff0c\u4f46\u4ed6\u6700\u4fe1\u670d\u7684\uff0c\u5374\u662f\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u4ed6\u613f\u4e3a\u9634\u9633\u5821\u4e2d\u5251\u4f7f\uff0c\u4e3a\u9ec4\u7075\u6548\u547d\uff0c\u5bf9\u4ed6\u5f71\u54cd\u6700\u5927\u7684\u4eba\uff0c\u4e5f\u662f\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4e24\u4eba\u76d8\u819d\u5bf9\u5750\uff0c\u4e00\u9762\u6e56\u6c34\uff0c\u4e00\u9762\u6c99\u6ee9\uff0c\u6570\u5341\u4e08\u5185\uff0c\u4efb\u4f55\u4e00\u70b9\u5fae\u5c0f\u60c5\u51b5\uff0c\u90fd\u4e0d\u53ef\u80fd\u7792\u8fc7\u4ed6\u4eec\u3002 \u3000\u3000\u53f6\u957f\u9752\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u4f55\u5144\uff0c\u6709\u51e0\u53e5\u8bdd\uff0c\u6211\u5df2\u5728\u80f8\u4e2d\u85cf\u4e86\u5f88\u4e45\uff0c\u4eca\u65e5\u60f3\u4e00\u5410\u4e3a\u5feb\u3002\u201d \u4f55\u5bd2\u8863\u7b11\u9053\uff1a\u201c\u5c3d\u7ba1\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u6211\u5fc3\u4e2d\u6709\u51e0\u4e2a\u7591\u70b9\uff0c\u5e0c\u671b\u80fd\u5f97\u6f84\u6e05\uff0c\u8fd9\u4e9b\u8bdd\uff0c\u4e0d\u80fd\u544a\u8bc9\u522b\u4eba\uff0c\u5728\u9634\u9633\u5821\u4e2d\uff0c\u4f60\u4f55\u5144\u662f\u6211\u552f\u4e00\u53ef\u4ee5\u503e\u8bc9\u7684\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u770b\u5f97\u8d77\u6211\uff0c\u5144\u5f1f\u5982\u82e5\u77e5\u9053\u7684\uff0c\u81ea\u5f53\u7aed\u8bda\u5949\u544a\uff0c\u5982\u662f\u5144\u5f1f\u4e0d\u77e5\u9053\uff0c\u90a3\u5c31\u8981\u8bf7\u53f6\u5144\u591a\u539f\u8c05\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5173\u4e8e\u54b1\u4eec\u9634\u9633\u5821\u7684\u4e8b\uff0c\u4f55\u5144\u77e5\u9053\u591a\u5c11?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u9053\uff1a \u201c\u8fd9\u65b9\u9762\u7684\u4e8b\uff0c\u6211\u77e5\u9053\u7684\u4e0d\u5c11\uff0c\u9634\u9633\u5821\u4ee5\u6536\u7f57\u9ed1\u9053\u4eba\u624b\u4e3a\u4e3b\uff0c \u81ea\u7136\uff0c\u6b63\u5927\u95e8\u6d3e\u4e2d\u4eba\uff0c\u5982\u82e5\u613f\u610f\u6295\u6548\uff0c\u4e5f\u662f\u6b22\u8fce\u5f97\u5f88\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u8fd9\u5c31\u662f\u5144\u5f1f\u60f3\u4e0d\u901a\u7684\u539f\u56e0\u4e4b\u4e00\u4e86\uff0c\u65e2\u7136\u662f\u54b1\u4eec\u4e13\u4ee5\u548c\u7eff\u6797\u9053\u4e0a\u7684\u532a\u76d7\u4e3a\u654c\uff0c\u4ed6\u4eec\u53c8\u600e\u80af\u6295\u6548\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u9053\uff1a\u201c\u9634\u9633\u5821\u6709\u4e00\u79cd\u5f88\u4e25\u5bc6\u7684\u5236\u5ea6\uff0c\u4e0d\u8bba\u4f55\u4eba\uff0c\u53ea\u8981\u6295\u6548\u5165\u9634\u9633\u5821\u4e2d\u4e4b\u540e\uff0c\u5c31\u88ab\u4e00\u79cd\u4e25\u5bc6\u7684\u63a7\u5236\u6240\u7ea6\u675f\uff0c\u4ed6\u4eec\u5c31\u8fd9\u6837\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u4e3a\u9634\u9633\u5821\u5356\u547d\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f55\u5144\uff0c\u5821\u4e3b\u4e0d\u4f1a\u522b\u6709\u7528\u5fc3\u5427?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u4f1a\uff0c\u8fd9\u4e00\u70b9\uff0c\u53f6\u5144\u53ef\u4ee5\u653e\u5fc3\uff0c\u4ed6\u8fc7\u53bb\u4f5c\u4e3a\u5982\u4f55?\u4ed6\u5df2\u81ea\u4f5c\u8bf4\u660e\uff0c\u4e5f\u8bb8\u4ed6\u8fd8\u6709\u9690\u85cf\u4e4b\u5904\uff0c\u4f46\u76ee\u4e0b\u7684\u9634\u9633\u5821\u4e3b\uff0c\u786e\u5b9e\u662f\u4e00\u4f4d\u80f8\u6000\u6b63\u4e49\uff0c\u5fc3\u5b58\u6b66\u6797\u7684\u5927\u4ec1\u5927\u52c7\u4e4b\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6709\u4f55\u5144\u8fd9\u53e5\u8bdd\uff0c\u5144\u5f1f\u5c31\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u906d\uff1a\u201c\u81ea\u53f6\u5144\u52a0\u5165\u4e86\u9634\u9633\u5821\u4e4b\u540e\uff0c\u6b63\u8d76\u4e0a\u6c5f\u6e56\u5f62\u52bf\u6709\u7740\u5f88\u5927\u7684\u53d8\u5316\uff0c\u6240\u4ee5\uff0c\u5821\u4e3b\u65e0\u6cd5\u548c\u53f6\u5144\u5e38\u5e38\u89c1\u9762\uff0c\u4e5f\u6ca1\u6709\u529e\u6cd5\u8ba9\u53f6\u5144\u5bf9\u5821\u4e2d\u7684\u4e8b\u60c5\uff0c\u591a\u4e00\u4e9b\u4e86\u89e3\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5144\u5f1f\u5f88\u76f8\u4fe1\u4f55\u5144\uff0c\u4f55\u5144\u4e00\u8a00\uff0c\u5144\u5f1f\u5fc3\u4e2d\u7684\u56f0\u60d1\uff0c\u5c31\u4e00\u626b\u800c\u7a7a\u4e86\u3002\u201d \u3000\u3000\u72c2\u9f99\u3001\u98de\u9e70\u79bb\u53bb\u4e4b\u540e\uff0c\u7adf\u7136\u672a\u518d\u6765\u653b\u3002 \u3000\u3000\u665a\u971e\u6ee1\u5929\uff0c \u65e5\u66ae\u9ec4\u660f\u4e4b\u524d\uff0c\u9634\u9633\u5821\u4e3b\u9ec4\u7075\u4e00\u53f6\u6241\u821f\uff0c\u4e00\u4ef6\u9752\u886b\uff0c\u5982\u7ea6\u8d76\u5230\u3002 \u8fd9\u4f4d\u7edf\u7387\u9634\u9633\u5821\u7684\u5e74\u8f7b\u9ad8\u4eba\uff0c\u65e2\u6ca1\u6709\u524d\u62a4\u540e\u62e5\u7684\u6392\u573a\uff0c\u751a\u81f3\u8fde\u9634\u9633\u5821\u4e2d\u7684\u4eba\uff0c\u4e5f\u5f88\u591a\u4e0d\u8ba4\u8bc6\u4ed6\u3002 \u3000\u3000\u4ed6\u5efa\u7acb\u4e00\u5957\u5236\u5ea6\u3001\u65b9\u6cd5\uff0c\u5c31\u7528\u90a3\u79cd\u65b9\u6cd5\u7edf\u7387\u7740\u9634\u9633\u5821\u3002 \u3000\u3000\u9634\u9633\u5821\u4e2d\u6570\u767e\u4f4d\u9ad8\u624b\uff0c\u771f\u6b63\u8ba4\u8bc6\u8fd9\u4f4d\u5821\u4e3b\u7684\uff0c\u4e5f\u4e0d\u8fc7\u5341\u4e2a\u516b\u4e2a\u4eba\u800c\u5df2\u3002 \u3000\u3000\u53f6\u957f\u9752\u5bf9\u9ec4\u7075\u6709\u7740\u4e00\u5206\u5c0a\u656c\uff0c\u4e5f\u6709\u4e00\u5206\u6b49\u759a\uff0c\u6025\u6025\u7ad9\u8d77\u8eab\u5b50\u9053\uff1a\u201c\u5821\u4e3b\u3002\u201d \u3000\u3000\u51c6\u5907\u5927\u793c\u62dc\u89c1\u3002 \u3000\u3000\u9ec4\u7075\u6025\u6025\u62c9\u4f4f\u4e86\u53f6\u957f\u9752\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u62d8\u4fd7\u793c\uff0c\u8fd9\u91cc\u7684\u4eba\uff0c\u9664\u4e86\u4f60\u548c\u5bd2\u8863\u53ca\u590f\u6740\u4e4b\u5916\uff0c\u90fd\u4e0d\u8ba4\u8bc6\u6211\uff0c\u4f60\u8fd9\u4e00\u62dc\uff0c\u5c82\u4e0d\u662f\u6cc4\u6f0f\u4e86\u6211\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u5e2d\u5730\u800c\u5750\u9053\uff1a \u201c\u53f6\u5144\uff0c\u90a3\u4e24\u7c92\u4e39\u836f\u7684\u6548\u7528\u5982\u4f55?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u4eec\u5728\u6b64\u606d\u5019\u5821\u4e3b\uff0c\u8fd8\u672a\u53bb\u67e5\u770b\u7389\u5170\u53cc\u59dd\u670d\u836f\u7684\u6548\u679c\u5982\u4f55\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\u9053\uff1a\u201c\u53f6\u5144\u548c\u7389\u5170\u53cc\u59dd\u76f8\u5904\u4e86\u4e00\u4e9b\u65f6\u65e5\uff0c\u4e0d\u77e5\u5730\u5979\u4eec\u7684\u770b\u6cd5\u5982\u4f55\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e8c\u5973\u7684\u672c\u8d28\uff0c\u8fd8\u7b97\u4e0d\u9519\uff0c\u53ea\u53ef\u60dc\u6ca6\u4e3a\u6740\u624b\uff0e\u5df2\u505a\u8fc7\u4e0d\u5c11\u4f24\u5929\u5bb3\u7406\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u8981\u6709\u8fc7\u80fd\u6539\uff0c\u5584\u83ab\u5927\u7109\uff0c\u53f6\u5144\u662f\u5426\u613f\u610f\u7ed9\u5979\u4eec\u4e00\u4e2a\u81ea\u65b0\u7684\u673a\u4f1a\u5462\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u8981\u5821\u4e3b\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u51f6\u9669\u6c5f\u6e56\uff0c\u4e0d\u8bba\u662f\u597d\u4eba\u3001\u574f\u4eba\uff0c\u4f46\u5728\u6c5f\u6e56\u4e0a\u6240\u65e5\u7684\u98ce\u9669\uff0c\u90fd\u662f\u4e00\u6837\uff0c\u5982\u82e5\u7389\u5170\u53cc\u59dd\u613f\u610f\u53bb\u5b8c\u6210\u4e00\u4ef6\u8270\u82e6\u4efb\u52a1\uff0c\u4e0d\u4f46\u53ef\u4ee5\u5c3d\u91ca\u524d\u5acc\uff0c\u800c\u4e14\uff0c\u5979\u4eec\u8fd8\u4f1a\u5f97\u5230\u6b66\u6797\u540c\u9053\u76f8\u5f53\u7684\u656c\u91cd\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u5462\uff1f\u201d \u3000\u3000\u9ec4\u7075\u53d6\u51fa\u4e00\u4e2a\u5bc6\u5c01\u9526\u56ca\uff0c\u9053\uff1a\u201c\u4f60\u4ed4\u7ec6\u770b\u8fc7\uff0c\u8981\u5979\u4eec\u9075\u7167\u884c\u4e8b\uff0c\u53ea\u662f\u6b64\u53bb\u51f6\u9669\u4e07\u7aef\uff0e\u4e5d\u6b7b\u4e00\u751f\uff0c\u53f6\u5144\u80fd\u5426\u8bf4\u670d\u5979\u4eec\uff0c\u8fd8\u96be\u9884\u6599\uff0e\u53ea\u4e0d\u8fc7\u6b64\u4e8b\u5173\u7cfb\u91cd\u5927\uff0e\u4e0d\u5f97\u6709\u4e00\u6b65\u9519\u5931\uff0c\u8981\u5979\u4eec\u51fa\u4e8e\u81ea\u613f\u624d\u884c\uff0c\u4e0d\u53ef\u5f3a\u8feb\u5979\u4eec\u884c\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u8fc7\u9526\u56ca\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u6b64\u5730\u6ca1\u6709\u5916\u4eba\uff0c\u6709\u4ec0\u4e48\u4e8b\uff0c\u8bb2\u5728\u5f53\u9762\u5c31\u662f\uff0c\u600e\u7684\u7adf\u4f1a\u8d50\u4e86\u6211\u8fd9\u4e00\u4e2a\u9526\u56ca\u3002 \u3000\u3000\u53ea\u542c\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u5982\u82e5\u7389\u5170\u53cc\u59dd\u4e0d\u80af\u5e94\u7ea6\uff0c\u53f6\u5144\u4e5f\u4e0d\u7528\u903c\u8feb\uff0c\u800c\u77e5\u4e8b\u5173\u91cd\u5927\uff0c\u5982\u975e\u51fa\u4e8e\u5979\u4eec\u81ea\u613f\uff0c\u5341\u6709\u4e5d\u5fc5\u8d25\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5c5e\u4e0b\u8bb0\u4e0b\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u76ee\u5149\u8f6c\u5230\u4f55\u5bd2\u8863\u7684\u8eab\u4e0a\uff0c\u7b11\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u54b1\u4eec\u76f8\u5904\u6570\u5e74\uff0c\u9634\u9633\u5821\u4e2d\u4eba\uff0c\u4f60\u5bf9\u6211\u4e86\u89e3\u7684\u6700\u6df1\uff0c\u76ee\u4e0b\u6c5f\u6e56\u60c5\u52bf\uff0c\u6b63\u5230\u4e86\u7d27\u8981\u5173\u5934\uff0c\u4f46\u6211\u5374\u4e3a\u4e00\u4ef6\u6614\u5e74\u6069\u6028\uff0c\u7275\u626f\u4e0a\u8eab\uff0c\u6211\u5982\u4e07\u4e00\u6709\u4ec0\u4e48\u4e0d\u5e78\uff0c\u8fd8\u671b\u4f55\u5144\u4e0d\u8f9e\u8270\u8f9b\uff0c\u7ee7\u7eed\u9886\u5bfc\u8fd9\u4e2a\u7ec4\u7ec7\uff0c\u53ea\u8981\u80fd\u63ed\u5f00\u8fd9\u6bb5\u79d8\u8f9b\uff0c\u6c5f\u6e56\u4e2d\u4eba\uff0c\u5fc5\u4f1a\u632f\u8882\u800c\u8d77\uff0c\u76f8\u4e3a\u652f\u63f4\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u606f\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u5821\u4e3b\u8eab\u80a9\u91cd\u4efb\uff0c\u5b9e\u975e\u5728\u4e0b\u8fd9\u70b9\u624d\u5177\u80fd\u627f\u8863\u94b5\uff0c\u8fd8\u671b\u4ee5\u5927\u4e8b\u4e3a\u91cd\uff0c\u81f3\u4e8e\u5821\u4e3b\u4e00\u4e9b\u79c1\u4eba\u6069\u6028\uff0c\u5bd2\u8863\u613f\u4ee3\u7ea6\u4e00\u884c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u548c\u4f55\u5144\u540c\u884c\uff0c\u5408\u4e24\u4eba\u4e4b\u529b\uff0c\u5c31\u7b97\u4e0d\u53ca\u5821\u4e3b\uff0c\u4e5f\u8bb8\u52c9\u53ef\u5e94\u4ed8\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u76db\u60c5\u53ef\u611f\uff0c\u4f46\u6b64\u523b\u5e76\u975e\u6bd4\u6b66\u62fc\u547d\uff0c\u4e24\u4f4d\u968f\u884c\uff0c\u4e5f\u662f\u4e8e\u4e8b\u65e0\u8865\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u65e2\u975e\u6bd4\u6b66\u62fc\u547d\uff0e\u8fd8\u6709\u4ec0\u4e48\u51f6\u9669\u53ef\u8a00\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\u3002\u4e00\u65f6\u95f4\u4e5f\u65e0\u6cd5\u89e3\u91ca\u5f97\u6e05\u695a\uff0c\u4e24\u4f4d\u4e0d\u7528\u518d\u4e3a\u6b64\u4e8b\u8d39\u5fc3\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u8fd8\u8981\u518d\u8bf4\uff0c\u5374\u4e3a\u4f55\u5bd2\u8863\u793a\u610f\u963b\u6b62\u3002 \u3000\u3000\u9ec4\u7075\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u7389\u5170\u53cc\u59dd\u7684\u4e8b\u3001\u6211\u5c31\u91cd\u6258\u53f6\u5144\uff0c\u4e94\u5927\u5251\u4f7f\uff0c\u867d\u7136\u5404\u6709\u4e00\u8eab\u5947\u6280\uff0c\u4f46\u771f\u6b63\u80fd\u591f\u9886\u8896\u7fa4\u4f26\uff0c\u53c8\u80fd\u5f97\u6c5f\u6e56\u4e2d\u6b63\u5927\u95e8\u6237\u4e2d\u4eba\u8c05\u89e3\u7684\uff0c\u53ea\u6709\u4f55\u5144\u548c\u4f60\uff0c\u8fd8\u671b\u53f6\u5144\u80fd\u591f\u5168\u5fc3\u4e00\u610f\uff0c\u8f85\u4f50\u4f55\u5144\uff0e\u5171\u4e3a\u6c5f\u6e56\u6b63\u4e49\u6548\u547d\u3002\u201d \u3000\u3000\u9ec4\u7075\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u8981\u53bb\u4e86\uff0c\u4e24\u4f4d\u4e07\u52ff\u8d1f\u6211\u6240\u6258\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fcd\u4e0b\u4f4f\u4e86\uff0c\u6025\u6025\u8bf4\u9053\uff1a\u201c\u5821\u4e3b\u7559\u6b65\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5982\u679c\u90a3\u4e24\u7c92\u836f\u7269\uff0c\u6551\u4e0d\u4e86\u7389\u5170\u53cc\u59dd\u5462\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u5728\u9526\u56ca\u4e2d\u5df2\u6709\u5b89\u6392\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u80fd\u8bf4\u670d\u7389\u5170\u53cc\u59dd\u5462\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u53ef\u7528\u5f3a\uff0c\u653e\u5979\u4eec\u59ca\u59b9\u53bb\u5427\uff01\u4f46\u7389\u5170\u53cc\u59dd\u6b20\u4f60\u4e00\u6761\u547d\uff0c\u5979\u4eec\u5982\u662f\u901a\u60c5\u8fbe\u7406\uff0c\u5927\u6982\u4f1a\u548c\u53f6\u5144\u5408\u4f5c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\u662f\u8fd9\u4e2a\u7ec4\u7ec7\u4e4b\u9996\uff0c\u4e0d\u53ef\u8f7b\u6613\u6d89\u9669\uff0c\u5c5e\u4e0b\u613f\u968f\u5821\u4e3b\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u63a5\u9053\u3002\u201c\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5821\u4e3b\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u9ec4\u7075\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5982\u662f\u53ef\u4ee5\u5e26\u53f6\u5144\u540c\u884c\uff0c\u6211\u53c8\u4e3a\u4ec0\u4e48\u62d2\u4eba\u4e8e\u5343\u91cc\u4e4b\u5916\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53f6\u5144\uff0c\u5821\u4e3b\u80f8\u7f57\u7384\u673a\uff0c\u624b\u63e1\u667a\u73e0\uff0c\u7528\u4e0d\u7740\u54b1\u4eec\u66ff\u4ed6\u8d39\u5fc3\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u5c3d\u7ba1\u653e\u5fc3\uff0c\u6211\u4f1a\u4e3a\u81ea\u5df1\u6c42\u547d\u3002\u201d \u3000\u3000\u8f6c\u8eab\u8dc3\u4e0a\u5c0f\u821f\uff0c\u8fd0\u6868\u5982\u98de\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u9ec4\u7075\u8fdc\u53bb\u80cc\u5f71\uff0c\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f55\u5144\uff0c\u5c31\u8fd9\u6837\u653e\u4ed6\u53bb\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u9053\uff1a\u201c\u81ea\u7136\u4e0d\u653e\u4ed6\u4e00\u4e2a\u4eba\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5982\u82e5\u8981\u8ffd\u4ed6\uff0c\u4e0d\u5b9c\u518d\u62d6\u5ef6\u65f6\u95f4\uff0c\u54b1\u4eec\u73b0\u5728\u8981\u52a8\u8eab\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ee5\u5821\u4e3b\u6b66\u529f\u6210\u5c31\uff0c\u54b1\u4eec\u53ea\u8981\u8fdb\u5165\u5341\u4e08\u4e4b\u5185\uff0c\u5fc5\u88ab\u53d1\u89c9\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u96be\u9053\u5c31\u7f62\u4e86\u4e0d\u6210?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53f6\u5144\u7a0d\u5b89\u52ff\u8e81\uff0c\u5144\u5f1f\u5df2\u6709\u5b89\u6392\uff0c\u73b0\u5728\uff0c\u53f6\u5144\u5148\u770b\u8fc7\u5821\u4e3b\u7559\u4e0b\u6765\u7684\u9526\u56ca\uff0c\u518d\u4f5c\u9053\u7406\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6253\u5f00\u9526\u56ca\u3002 \u3000\u3000\u90a3\u662f\u4e00\u5c01\u957f\u4fe1\uff0c\u8fd8\u6709\u4e24\u7247\u67af\u5e72\u4e86\u7684\u836f\u7247\u3002 \u3000\u3000\u4fe1\u4e0a\u6307\u70b9\u4ed6\u4eec\u8bf4\u670d\u7389\u5170\u53cc\u59dd\u7684\u65b9\u6cd5\uff0c\u4e5f\u6307\u70b9\u4e86\u7389\u5170\u53cc\u59dd\u8981\u505a\u7684\u4e8b\u60c5\uff0c\u5982\u662f\u4e39\u4e38\u65e0\u6cd5\u89e3\u53bb\u7389\u5170\u53cc\u59dd\u4e4b\u6bd2\uff0c\u5c31\u628a\u4e24\u7247\u67af\u53f6\uff0c\u5206\u522b\u8981\u7389\u5170\u53cc\u59dd\u670d\u4e0b\uff0c\u4e39\u4e38\u80fd\u591f\u89e3\u6bd2\uff0c\u73cd\u85cf\u4e24\u7247\u67af\u53f6\uff0c\u90a3\u662f\u4e24\u7247\u5f88\u5b9d\u8d35\u7684\u4e1c\u897f\uff0c\u4e07\u91d1\u96be\u6c42\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4fe1\u4e0a\u6307\u793a\uff0c\u5341\u5206\u660e\u786e\uff0c\u6211\u76f8\u4fe1\u53ef\u4ee5\u8bf4\u670d\u7389\u5170\u53cc\u59dd\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d!\u4f60\u53bb\u770b\u7389\u5170\u53cc\u59dd\uff0c\u5171\u628a\u6b64\u4e8b\u529e\u59a5\uff0c\u518d\u4e0b\u4ee4\u4e01\u5cf0\u6492\u8d70\u4eba\u624b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\u7684\u4e8b\u60c5\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u9053\uff1a\u201c\u8fd9\u51e0\u5e74\u548c\u4ed6\u76f8\u5904\uff0c\u8033\u6ee1\u76ee\u67d3\uff0c\u5b66\u4e86\u4e0d\u5c11\u7684\u4e1c\u897f\uff0c\u73b0\u5728\uff0c\u6211\u4e5f\u5b89\u6392\u4e86\u4e00\u62db\uff0c\u4e0d\u8fc7\uff0c\u5821\u4e3b\u667a\u6167\u8fc7\u4eba\uff0c\u5e0c\u671b\u4ed6\u4e0d\u4f1a\u770b\u7a7f\u624d\u597d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\u5427!\u5144\u5f1f\u8fd9\u5c31\u770b\u7389\u5170\u53cc\u59dd\uff0c\u4f55\u5144\u540c\u5f80\u4e00\u884c\u5982\u4f55?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53ea\u6015\u5144\u5f1f\u53bb\u4e86\u6709\u4e0d\u4fbf\u4e4b\u5904\uff0c\u6211\u5728\u6b64\u7b49\u5019\u56de\u97f3\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u56de\u5230\u4e86\u753b\u822b\u4e4b\u4e0a\uff0c\u5148\u8981\u4e01\u5cf0\u3001\u590f\u6740\uff0c\u51c6\u5907\u5929\u4e00\u9ed1\u7acb\u523b\u64a4\u8d70\u4eba\u624b\uff0c\u5e76\u4e14\uff0c\u7ea6\u5b9a\u4e86\u4f1a\u5408\u4e4b\u5904\uff0c\u8054\u7edc\u6697\u8bb0\u3002 \u3000\u3000\u4e24\u4eba\u53bb\u540e\uff0c\u753b\u822b\u4e0a\u53ea\u4f59\u4e0b\u7389\u5170\u53cc\u59dd\u3002 \u3000\u3000\u773c\u4e0b\u4e86\u836f\u7269\u4e4b\u540e\uff0c\u4e8c\u5973\u4e00\u76f4\u5728\u6253\u5750\u8c03\u606f\u3002 \u3000\u3000\u53f6\u957f\u9752\u884c\u5165\u5185\u8231\uff0c\u4e8c\u5973\u4e5f\u6b63\u5750\u606f\u9192\u6765\u3002 \u3000\u3000\u7389\u7fe0\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u8fd9\u836f\u7269\u597d\u50cf\u6709\u70b9\u6548\u7528\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u80fd\u597d\u50cf\uff0c\u800c\u662f\u8981\u786e\u5b9a\u4e24\u4f4d\u8eab\u4e2d\u4e4b\u6bd2\uff0c\u662f\u5426\u5df2\u89e3\uff1f\u201d \u3000\u3000\u7389\u7fe0\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u53ea\u662f\u75c7\u8c61\u51cf\u8f7b\uff0c\u4f59\u6bd2\u4ecd\u5b58\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u4f60\u4eec\u670d\u7528\u7684\u836f\u7269\uff0c\u53ef\u89e3\u6570\u5341\u79cd\u5947\u6bd2\uff0c\u4f46\u4e24\u4f4d\u4e2d\u7684\u6bd2\uff0c\u5374\u4e0d\u5728\u5176\u5185\u3002\u201d \u3000\u3000\u4f38\u624b\u7531\u6000\u4e2d\u53d6\u51fa\u4e24\u7247\u67af\u53f6\uff0c\u9053\uff1a \u201c\u7389\u7fe0\u3001\u5170\u767d\uff0c\u628a\u8fd9\u4e24\u7247\u53f6\u5b50\u5403\u4e0b\u53bb\u3002\u201d \u7389\u7fe0\u9053\uff1b\u201c\u8fd9\u662f\u4ec0\u4e48\u53f6\u5b50?\u201d \u3000\u3000\u53f6\u957f\u80b2\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u4e0d\u8fc7\uff0c\u5403\u4e0b\u53bb\u4e00\u5b9a\u6709\u7528\u5904\u3002\u201d \u3000\u3000\u5170\u767d\u5f53\u5148\u4f38\u624b\u63a5\u8fc7\uff0c\u4e00\u5f20\u53e3\u5403\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u7389\u7fe0\u6014\u4e86\u4e00\u6014\uff0c\u4e5f\u4f38\u624b\u63a5\u8fc7\uff1b\u4e00\u53e3\u541e\u4e0b\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u867d\u7136\u4e0d\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u53f6\u5b50\uff0c\u4f46\u6211\u77e5\u9053\uff0c\u8fd9\u662f\u5f88\u540d\u8d35\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u541e\u5165\u4e86\u53e3\u4e2d\u4e4b\u540e\uff0c\u4f59\u5473\u6e05\u5f7b\u80ba\u8151\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5170\u767d\uff0c\u5feb\u4e9b\u8fd0\u6c14\u8c03\u606f\u4e00\u4e0b\uff0c\u770b\u770b\u662f\u5426\u4f59\u6bd2\u5df2\u89e3\uff0c\u6211\u5728\u8231\u5916\u7b49\u4f60\u3002\u201d \u3000\u3000\u7389\u5170\u53cc\u59dd\uff0c\u7ecf\u8fc7\u4e86\u4e00\u9635\u8c03\u606f\u4e4b\u540e\uff0c\u7adf\u7136\u53d1\u89c9\u8eab\u4e0a\u7684\u5947\u6bd2\u5df3\u89e3\u3002 \u3000\u3000\u5170\u767d\u9996\u5148\u4e00\u8dc3\u800c\u8d77\uff0c\u9053\uff1a\u201c\u7fe0\u59ca\uff0c\u6211\u8eab\u4e0a\u7684\u5947\u6bd2\u89e3\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u7f13\u7f13\u7741\u5f00\u53cc\u76ee\uff0c\u9053\uff1a \u201c\u662f!\u5947\u6bd2\u89e3\u4e86\uff0c\u90a3\u4e24\u7247\u67af\u53f6\uff0c\u5b9e\u5728\u662f\u5f88\u540d\u8d35\u7684\u4e1c\u897f\uff0c\u60f3\u4e0d\u5230\u53f6\u957f\u9752\u7adf\u80af\u628a\u4ed6\u6536\u85cf\u591a\u5e74\u7684\u5b9d\u7269\uff0c\u7ed9\u54b1\u4eec\u98df\u7528\u89e3\u6bd2\u3002\u201d \u3000\u3000\u4e0d\u77e5\u4f55\u65f6\uff0c\u53f6\u957f\u9752\u5df2\u8fdb\u5165\u8231\u4e2d\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u662f\u6211\uff0c\u6211\u8fde\u90a3\u4e24\u7247\u67af\u53f6\u662f\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e0d\u662f\u4f60\uff0c\u53c8\u662f\u8c01\uff0c\u6211\u60f3\u4e0d\u51fa\uff0c\u8fd9\u4e16\u95f4\u8fd8\u6709\u4ec0\u4e48\u4eba\uff0c\u80fd\u591f\u5bf9\u6211\u4eec\u4e24\u59ca\u59b9\u8fd9\u4e48\u5173\u5fc3?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9634\u9633\u5821\u7684\u9ec4\u5821\u4e3b!\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u662f\u4ed6\uff0c\u9634\u9633\u5821\u4e2d\uff0c\u4e91\u96c6\u4e86\u5404\u8def\u4eba\u7269\uff0c\u4ed6\u5374\u662f\u76d7\u4e2d\u4e4b\u738b\uff0c\u532a\u4e2d\u4e4b\u9996\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u662f\u6c5f\u6e56\u4e0a\u5bf9\u4ed6\u7684\u8bef\u89e3\uff0c\u4ed6\u624d\u662f\u771f\u6b63\u7684\u4eba\u95f4\u4fa0\u58eb\u3002\u201c\u7389\u7fe0\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u4ed6\u80af\u628a\u6536\u85cf\u591a\u5e74\u7684\u5b9d\u7269\uff0c\u7ed9\u6211\u4eec\u89e3\u53bb\u8eab\u4e2d\u4e4b\u6bd2\uff0c\u6211\u60f3\u4ed6\u5fc5\u7136\u662f\u6709\u8981\u7528\u6211\u4eec\u7684\u5730\u65b9\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u786e\u6709\u501f\u91cd\u4e24\u4f4d\u4e4b\u5904\uff0c\u9ec4\u5821\u4e3b\u672c\u6765\u662f\u60f3\u628a\u8d60\u836f\u7684\u4ea4\u60c5\uff0c\u63a8\u5230\u4e86\u6211\u7684\u8eab\u4e0a\uff0c\u6211\u4e5f\u77e5\u9053\uff0c\u90a3\u4f1a\u4f7f\u4e8b\u60c5\u987a\u5229\u5f88\u591a\uff0c\u4f46\u6211\u53f6\u67d0\u4eba\u4e0d\u613f\u63a0\u4eba\u4e4b\u7f8e\uff0c\u6240\u4ee5\uff0c\u636e\u5b9e\u76f8\u544a\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u5f88\u5149\u660e\u78ca\u843d\uff0c\u6211\u4eec\u4e00\u76f4\u6b20\u4f60\u4e00\u5206\u60c5\uff0c\u4e0d\u7ba1\u6388\u610f\u7684\u4eba\u662f\u8c01\uff0c\u4f46\u53ea\u8981\u8bdd\u4ece\u53f6\u5144\u53e3\u4e2d\u8bf4\u51fa\u6765\uff0c\u8d74\u6c64\u8e48\u706b\uff0c\u6211\u4eec\u5728\u6240\u4e0d\u8f9e\uff0c\u8981\u6211\u4eec\u5e72\u4ec0\u4e48?\u53f6\u5144\u8bf7\u5429\u5490\u5427\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u6ca1\u6709\u631f\u6069\u6c42\u62a5\u7684\u610f\u601d\uff0c\u5728\u4e0b\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u7528\u5fc3\u3002\u201d \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u7a76\u7adf\u59bb\u6211\u4eec\u505a\u4ec0\u4e48\uff0c\u4f55\u4e0d\u5148\u884c\u8bf4\u51fa\u6765\u542c\u542c\u5462\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u5341\u5206\u51f6\u9669\uff0c\u4e0d\u8fc7\uff0c\u4e5f\u5f88\u4f1f\u5927\uff0c\u4e24\u4f4d\u662f\u5426\u613f\u610f?\u8981\u4f60\u4eec\u81ea\u5df1\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u8bf4\u5427!\u5c31\u7b97\u53bb\u6b7b\uff0c\u6211\u4eec\u4e5f\u8ba4\u4e86\u3002\u201c \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8981\u4e24\u4f4d\u6df7\u5165\u9ad8\u5929\u5065\u7684\u8eab\u4fa7\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u5403\u4e86\u4e00\u60ca\uff0c\u63a5\u9053\uff1a\u201c\u4e2d\u5dde\u7b2c\u4e00\u5200\uff0c\u9ad8\u5927\u4fa0\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c31\u662f\u4ed6\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4ed6\u8a89\u6ee1\u6c5f\u6e56\uff0c\u662f\u5f53\u4eca\u6b66\u6797\u4e2d\u516c\u8ba4\u7684\u5927\u4fa0\uff0c\u9634\u9633\u5821\u9ec4\u5821\u4e3b\u5bf9\u4ed8\u4ed6\uff0c\u662f\u975e\u5c82\u4e0d\u662f\u5df2\u7ecf\u5f88\u660e\u663e\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u90a3\u662f\u4ed6\u7684\u5916\u8c8c\uff0c\u771f\u6b63\u7684\u5185\u60c5\uff0c\u5374\u975e\u5982\u6b64\uff0c\u8fc7\u53bb\uff0c\u6211\u4e5f\u548c\u4e24\u4f4d\u59d1\u5a18\u4e00\u6837\uff0c\u5185\u5fc3\u4e2d\u5bf9\u4ed6\u656c\u6155\u4e07\u5206\uff0c\u4f46\u6211\u4eb2\u773c\u770b\u7740\u4ed6\u6740\u4e86\u75c5\u53df\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9ad8\u5929\u5065\u5728\u6211\u5fc3\u76ee\u4e2d\u6240\u53d7\u7684\u656c\u91cd\uff0c\u7edd\u5bf9\u4e0d\u5728\u522b\u4eba\u4e4b\u4e0b\uff0c\u5982\u975e\u4eb2\u773c\u89c1\u4ed6\u4f5c\u4e3a\uff0c\u4f20\u8a00\u6211\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u4ed6\u90a3\u4e48\u9ad8\u7684\u58f0\u671b\uff0c\u5982\u4f55\u4f1a\u6536\u7559\u6211\u4eec\u8fd9\u4e24\u4e2a\u6740\u624b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u6b63\u56e0\u4e3a\u4f60\u4eec\u662f\u4e24\u4e2a\u540d\u52a8\u6c5f\u6e56\u7684\u5973\u6740\u624b\uff0c\u624d\u6709\u673a\u4f1a\u4f7f\u4ed6\u771f\u6b63\u4fe1\u4efb\u2026\u2026\u201d \u5ffd\u7136\u4e00\u8f6c\u8bdd\u9898\uff0c\u9053\uff1a \u201c\u4e24\u4f4d\u53ef\u5426\u5148\u544a\u8bc9\u6211\u4f60\u4eec\u6b64\u523b\u7684\u611f\u53d7\uff0c\u6b64\u540e\u51c6\u5907\u4f55\u53bb\u4f55\u4ece\uff0c\u5982\u662f\u906d\u4e0d\u540c\uff0c\u96be\u76f8\u4e3a\u8c0b\uff0c\u6211\u4eec\u4e5f\u4e0d\u4fbf\u9ebb\u70e6\u4e24\u4f4d\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5170\u767d\uff0c\u8bf4\u8bf4\u4f60\u5fc3\u4e2d\u60f3\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8981\u771f\u8bda\u7684\u53d1\u81ea\u5185\u5fc3\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u7fe0\u59ca\uff0c\u5c0f\u59b9\u89c9\u7740\uff0c\u4e0d\u8bba\u4ec0\u4e48\u4eba\u7ed9\u6211\u4eec\u7684\u836f\u7269\uff0c\u4f46\u6211\u4eec\u53ea\u611f\u6fc0\u53f6\u5144\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5170\u767d\u9063\uff1a\u201c\u6240\u4ee5\uff0c\u5982\u662f\u53f6\u5144\u8981\u6211\u4eec\u8d74\u6c64\u8e48\u706b\uff0c\u6211\u4eec\u81ea\u7136\u662f\u5728\u6240\u4e0d\u8f9e\uff0c\u4f46\u6211\u4eec\u4e0d\u542c\u522b\u4eba\u7684\u3002\u201d \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u5417?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u6ca1\u6709\u5566\uff0c\u5c0f\u59b9\u5c31\u662f\u8fd9\u4e00\u70b9\u610f\u89c1\uff0c\u4e0d\u77e5\u59d0\u59d0\u8ba4\u4e3a\u5982\u4f55?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5f88\u5bf9!\u6211\u4e5f\u662f\u8fd9\u4e48\u60f3\u3002\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u53f6\u957f\u9752\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u53f6\u5144\u542c\u5230\u4e86\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u542c\u5230\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u6211\u89c9\u7740\u5170\u767d\u8bf4\u7684\u4e0d\u9519\uff0c\u4e5f\u8bb8\u90a3\u4e24\u7247\u73cd\u85cf\u7684\u5b9d\u53f6\u771f\u7684\u662f\u4f60\u4eec\u9ec4\u5821\u4e3b\u7ed9\u6211\u4eec\u7684\uff0c\u4f46\u4ea4\u7ed9\u6211\u4eec\u59d0\u59b9\u7684\uff0c\u662f\u4f60\u53f6\u5144\uff0c\u5982\u82e5\u6211\u4eec\u6709\u4e00\u4efd\u611f\u6fc0\uff0c\u90a3\u662f\u611f\u6fc0\u4f60\u53f6\u5144\uff0c\u4e0d\u4f1a\u611f\u6fc0\u522b\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u53f6\u5144\u6709\u4ec0\u4e48\u8981\u6211\u4eec\u59d0\u59b9\u505a\u7684\uff0c\u53ea\u7ba1\u5429\u5490\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u8f6c\u773c\u5411\u5170\u767d\u770b\u53bb\uff0c\u53ea\u89c1\u5170\u81ea\u5fae\u7b11\u5982\u82b1\uff0c\u5fc3\u4e2d\u4f3c\u662f\u5341\u5206\u5f97\u610f\uff0c\u5f53\u4e0b\u5fd6\u9053\uff1a\u4e0d\u7ba1\u8fd9\u4ef6\u4e8b\u5982\u4f55\u7684\u53bb\u529e\uff0c\u53ea\u8981\u529e\u59a5\u5c31\u597d\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u65e2\u5982\u6b64\u8bf4\uff0c\u90a3\u5c31\u7b97\u5728\u4e0b\u5411\u4e24\u4f4d\u8981\u6c42\u7684\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a \u201c\u65e2\u7136\u662f\u53f6\u5144\u8981\u6c42\uff0c\u6211\u4eec\u5168\u529b\u4ee5\u8d74\uff0c\u59d0\u59d0\u54b1\u4eec\u51e0\u65f6\u52a8\u8eab?\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5170\u767d\uff0c\u4f60\u8981\u5148\u60f3\u6e05\u695a\uff0c\u8fd9\u6869\u4e8b\uff0c\u5341\u5206\u5371\u9669\u3002\u201d \u5170\u767d\u9053\uff1a\u201c\u59d0\u59d0\u548c\u6211\uff0c\u90fd\u542c\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5170\u767d\uff0c\u6709\u6ca1\u6709\u4ec0\u4e48\u6761\u4ef6?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4f60\u6551\u4e86\u6211\u4eec\uff0c\u6211\u4eec\u6b20\u4f60\u4e00\u6761\u547d\uff0c\u5c31\u7b97\u6211\u4eec\u6b7b\u4e86\uff0c\u4e5f\u4e0d\u8fc7\u662f\u628a\u547d\u8fd8\u7ed9\u4f60\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u5170\u767d\uff0c\u4f60\u8bf4\u7684\u8bdd\u662f\u4e0d\u9519\uff0c\u4e0d\u8fc7\uff0c\u6211\u5e0c\u671b\u4f60\u628a\u60f3\u6cd5\u6269\u5927\u4e00\u4e9b\uff0c\u4f60\u4eec\u8fd9\u4e00\u6b21\u5192\u9669\u8d74\u96be\uff0c\u4e0d\u662f\u4e3a\u4e86\u6211\uff0c\u800c\u662f\u4e3a\u4e86\u6574\u4e2a\u7684\u6b66\u6797\u540c\u9053\uff0c\u4e3a\u4e86\u6c5f\u6e56\u4e0a\u7684\u9053\u4e49\uff0c\u8fd9\u6837\u60f3\uff0c\u5c31\u7b97\u662f\u6b7b\u4e5f\u503c\u5f97\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4e0d!\u90a3\u662f\u4f60\u4eec\u7537\u4eba\u7684\u60f3\u6cd5\uff0c\u597d\u7537\u513f\u5fd7\u5728\u56db\u65b9\uff0c\u4e3a\u4e07\u4e16\u7acb\u57fa\u4e1a\uff0c\u4e3a\u5343\u79cb\u7559\u7f8e\u540d\uff0c\u6211\u4eec\u5973\u4eba\u4e0d\u540c\uff0c\u6211\u4eec\u53ea\u5e0c\u671b\u627e\u4e00\u4e2a\u597d\u7537\u4eba\uff0c\u5ac1\u7ed9\u4ed6\uff0c\u751f\u513f\u3001\u80b2\u5973\uff0c\u8fc7\u4e00\u8f88\u5b50\u5b89\u4e50\u751f\u6d3b\uff0c\u5c31\u7b97\u6211\u4eec\u6ca6\u4e3a\u6740\u624b\uff0c\u6740\u4eba\u8d5a\u94b1\uff0c\u4e5f\u662f\u4e3a\u4e86\u540e\u534a\u8f88\u5b50\u7684\u5b89\u4e50\u7740\u60f3\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4ea4\u60c5\u5168\u5356\u5728\u6211\u7684\u8eab\u4e0a\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5bf9\u554a!\u6211\u4eec\u51b2\u7740\u4f60\u561b\u3002\u201d \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\u9053\uff1a \u201c\u53f6\u5144\uff0c\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u6d3b\u5df2\u7ecf\u8bf4\u7684\u5982\u6b64\u660e\u767d\u4e86\uff0c\u96be\u9053\u8fd8\u8981\u5979\u518d\u8868\u793a\u6e05\u695a\u4e00\u4e9b\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7389\u7fe0\uff0c\u6211\uff0c\u6211\u4e0d\u914d\uff0c\u5170\u767d\u90a3\u4e48\u2014\u4e2a\u597d\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u63a5\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u5982\u89c9\u5f97\u5979\u662f\u4e00\u4e2a\u597d\u59d1\u5a18\uff0c\u5c31\u522b\u8ba9\u5979\u5931\u671b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u597d!\u7389\u7fe0\uff0c\u544a\u8bc9\u6211\u5e94\u8be5\u600e\u4e48\u505a?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5a36\u5979\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u54c8\u54c8\u2014\u7b11\uff0c\u9053\uff1a\u201c\u597d!\u4e00\u53e5\u8bdd\uff0c\u5c31\u70e6\u4f60\u7389\u7fe0\u59d1\u5a18\u7ed9\u6211\u505a\u4e2a\u5a92\u3002\u201d \u3000\u3000\u7389\u7fe0\u6b22\u6109\u4e2d\u5e26\u7740\u4e00\u70b9\u60ca\u6115\uff0c\u9053\uff1a\u201c\u53f6\u5144\uff0c\u8fd9\u662f\u771f\u7684?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5343\u771f\u4e07\u786e\uff0c\u800c\u4e14\uff0c\u73b0\u5728\u5c31\u529e\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u6211\u4eec\u5148\u8ba2\u4e0b\u5a5a\u7ea6\uff0c \u7b49\u6c5f\u6e56\u4e0a\u8fd9\u6bb5\u9ed1\u6697\u6e05\u660e\u4e86\uff0c\u6211\u4eec\u518d\u884c\u7ed3\u6210\u592b\u5987\u3002\u201d \u3000\u3000\u7389\u7fe0\u56de\u987e\u4e86\u5170\u767d\u4e00\u773c\uff0c\u9053\uff1a \u201c\u59b9\u59b9\uff0c \u4f60\u90fd\u542c\u5230\u4e86\uff0c\u4e0d\u77e5\u4f60\u610f\u4e0b\u5982\u4f55?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u59ca\u59ca\uff0c\u6211\u4e0d\u60f3\u52c9\u5f3a\u4ed6\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u52c9\u5f3a\uff0c\u4e00\u70b9\u4e5f\u4e0d\u52c9\u5f3a\uff0c\u5728\u4e0b\u5b8c\u5168\u662f\u51fa\u4e8e\u4e00\u7247\u81f3\u8bda\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u59ca\u59ca\u770b\u7740\u529e\u5427!\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u597d!\u90a3\u4e48\u59ca\u59ca\u5c31\u66ff\u4f60\u505a\u4e3b\u4e86\uff0c\u54b1\u4eec\u6c5f\u6e56\u513f\u5973\uff0c\u4e0d\u62d8\u4fd7\u5957\uff0c\u7acb\u523b\u7ed9\u4f60\u4eec\u5b8c\u6210\u6587\u5b9a\u624b\u7eed\uff0c\u53ea\u53ef\u60dc\uff0c\u7537\u65b9\u8fd8\u7f3a\u4e2a\u5927\u5a92\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8981\u4ec0\u4e48\u6837\u7684\u4eba?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u81ea\u7136\u662f\u8eab\u4efd\u8d8a\u9ad8\u8d8a\u597d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u53bb\u627e\u4f55\u5bd2\u8863\uff0c\u5e0c\u671b\u4ed6\u80fd\u7b54\u5e94\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u91d1\u5251\u98de\u8f6e\u4f55\u5bd2\u8863?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u591f\u4e0d\u591f\u5206\u91cf?\u201d \u3000\u3000\u7389\u7fe0\u5fae\u5fbd\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u591f!\u592a\u591f\u4e86\uff0c\u8fd9\u8bc1\u660e\u53f6\u5144\u7684\u4e00\u7247\u8bda\u5fc3\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u7b54\u5e94\u4e86\uff0c\u5c31\u4f1a\u5f88\u8ba4\u771f\u3002\u201d \u3000\u3000\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u5170\u767d\u4f4e\u58f0\u9053\uff1b\u201c\u59ca\u59ca\uff0c\u4f60\u8fd9\u662f\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u63a5\u9053\uff1b\u201c\u600e\u4e48\uff0c\u4f60\u4e0d\u540c\u610f\u8fd9\u95e8\u4eb2\u4e8b\u5417?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u7fe0\u59ca\uff0c\u5c0f\u59b9\u4e0d\u4f1a\u4f5c\u4f2a\uff0c\u6211\u5f88\u611f\u6fc0\u4f60\uff0c\u53ef\u662f\u59ca\u59ca\u4f60\u5462?\u4f60\u628a\u6211\u5b89\u6392\u7684\u8fd9\u6837\u597d\uff0c\u4f46\u81ea\u5df1\u5374\u53c8\u4f5c\u4f55\u6253\u7b97\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u5170\u59b9\uff0c\u5982\u679c\u8bf4\u6211\u5728\u8fd9\u4e2a\u4eba\u4e16\u4e0a\uff0c\u8fd8\u6709\u4e00\u4f4d\u4eb2\u4eba\uff0c\u90a3\u4e2a\u4eba\u5c31\u662f\u4f60\uff0c\u53ea\u8981\u4f60\u5feb\u4e50\uff0c\u59ca\u59ca\u5c31\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5e26\u7740\u4f55\u5bd2\u8863\u884c\u5165\u5185\u8231\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5bf9\u5170\u767d\u4e00\u62f1\u624b\uff0c\u9053\uff1a\u201d\u606d\u559c\u4e8c\u59d1\u5a18\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u5782\u4e0b\u4e86\u5934\uff0c\u53cc\u988a\u4e0a\u6cdb\u8d77\u4e86\u4e00\u7247\u7ea2\u6655\u3002 \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f55\u7237\uff0c\u9ebb\u70e6\u4f60\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u662f\u559c\u4e8b\uff0c\u5728\u4e0b\u4e5f\u8981\u5411\u5927\u59d1\u5a18\u606d\u559c\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f55\u7237\uff0c\u6211\u8fd9\u4e2a\u505a\u5e08\u59ca\u7684\u5f88\u60ed\u6127\uff0c\u5728\u8fd9\u4e48\u4e00\u4e2a\u73af\u5883\u4e0b\uff0c\u66ff\u5e08\u59b9\u529e\u559c\u4e8b\uff0c\u51c6\u5907\u4e0d\u5468\uff0c\u4e00\u5207\u90fd\u6ca1\u6709\u5b89\u6392\u597d\uff0c\u8fde\u6c34\u9152\u4e5f\u6ca1\u6709\u66ff\u5927\u5a92\u4eba\u51c6\u5907\u4e00\u676f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u7528\uff0c\u4e0d\u7528\uff0c\u6211\u548c\u53f6\u5144\uff0c\u4e00\u89c1\u5982\u6545\uff0c\u76ee\u4e0b\u60c5\u5883\u7279\u6b8a\uff0c\u4e0d\u5fc5\u4e3a\u4fd7\u793c\u6240\u62d8\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u597d!\u4f55\u7237\uff0c\u4f60\u770b\u8981\u4e0d\u8981\u66ff\u4ed6\u4eec\u4e3e\u884c\u4e00\u4e2a\u4ec0\u4e48\u4eea\u5f0f\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u6211\u770b\u8fd9\u4e2a\u514d\u4e86\uff0c\u5f53\u7740\u4f55\u5144\uff0c\u548c\u4f60\u7389\u7fe0\u59d1\u5a18\u4e4b\u9762\uff0c\u6211\u4eec\u8bf4\u4e00\u53e5\u8bdd\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u5144\uff0c\u603b\u4e0d\u80fd\u53ea\u8bf4\u4e00\u53e5\u8bdd\u5c31\u7b97\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6b64\u523b\u5904\u5883\u7279\u6b8a\uff0c \u65e5\u540e\u53ef\u4ee5\u8865\u884c\u5927\u793c\uff0c\u597d\u597d\u7684\u70ed\u95f9\u4e00\u756a\u3002\u201d \u7389\u7fe0\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u597d\u5427!\u8fd9\u91cc\u53ea\u6709\u6211\u4eec\u56db\u4e2a\u4eba\uff0c\u5f53\u7740\u4f55\u7237\u4e4b\u51fb\uff0c\u6211\u628a\u5e08\u59b9\u7ec8\u8eab\u6258\u7ed9\u4f60\u4e86\uff0c\u5e0c\u671b\u4f60\u597d\u597d\u7684\u7167\u987e\u5979\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u5f53\u5c3d\u529b\u4e4b\u6240\u80fd\uff0c\u7167\u987e\u5170\u767d\u3002\u201d \u3000\u3000\u7389\u7fe0\u76ee\u5149\u7a81\u7136\u8f6c\u5230\u4f55\u5bd2\u8863\u7684\u8eab\u4e0a\uff0c\u9053\uff1a \u201c\u4f55\u7237\uff0c\u8fd9\u4e00\u6b21\uff0c\u6211\u4eec\u53bb\u4e3a\u9ec4\u5821\u4e3b\u6548\u547d\uff0c\u53ef\u4ee5\u8bf4\u662f\u5341\u5206\u7684\u51f6\u9669\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u662f\u7684\uff0c\u7389\u7fe0\u59d1\u5a18\uff0c\u4f60\u4eec\u5fc5\u9700\u8981\u51ed\u4ed7\u81ea\u5df1\u7684\u673a\u667a\uff0c\u4fdd\u62a4\u81ea\u5df1\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f55\u7237\uff0c\u5170\u59b9\u8fd8\u662f\u4e00\u4f4d\u597d\u59d1\u5a18\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u770b\u5f97\u51fa\u6765\uff0c\u5979\u4e0d\u9519\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5170\u59b9\u548c\u53f6\u5144\u7684\u5a5a\u4e8b\u3001\u559c\u8baf\uff0c\u6700\u597d\u4e0d\u8981\u4f20\u51fa\u53bb\uff0c\u9700\u77e5\u90a3\u662f\u4f1a\u5f71\u54cd\u5230\u4f60\u4eec\u7684\u5b89\u5168\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u4eec\u51e0\u65f6\u52a8\u8eab?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8d8a\u5feb\u8d8a\u597d\uff0c\u4e0d\u8fc7\uff0c\u4e24\u4f4d\u51c6\u5907\u5982\u4f55\u53bb\u6cd5\u5462?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u5728\u4e3a\u6b64\u4e8b\u7b79\u5212\uff0c\u4e00\u65f6\u95f4\uff0c\u8fd8\u672a\u60f3\u5230\u826f\u7b56\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8981\u4ed6\u4eec\u76f8\u4fe1\uff0c\u5fc5\u5f97\u6709\u4e9b\u8868\u73b0\u624d\u884c\u3002\u201d \u3000\u3000\u7389\u7fe0\u70b9\u70b9\u5934\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f55\u7237\uff0c\u90a3\u4f4d\u9ad8\u5929\u5065\u662f\u4e0d\u662f\u5f88\u559c\u6b22\u5973\u8272?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u636e\u6211\u4eec\u6240\u77e5\uff0c\u9ad8\u5929\u5065\u662f\u4e00\u4e2a\u5f88\u8d2a\u5e55\u5973\u8272\u7684\u4eba\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4e00\u5411\u8bb2\u6c42\u865a\u540d\uff0c\u6240\u4ee5\uff0c\u5728\u8868\u9762\u4e0a\uff0c\u4ed6\u4e00\u76f4\u8868\u73b0\u51fa\u4e00\u526f\u6b63\u4eba\u541b\u5b50\u7684\u6837\u5b50\uff01\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f55\u7237\uff0c\u9ec4\u5821\u4e3b\u9009\u4e2d\u4e86\u6211\u4eec\u59d0\u59b9\u4e24\u4eba\uff0c\u662f\u4e0d\u662f\u89c9\u7740\u6211\u4eec\u59d0\u59b9\u8fd8\u6709\u4e00\u70b9\u59ff\u8272?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u6211\u60f3\u9664\u4e86\u4e24\u4f4d\u7684\u59ff\u8272\u4e4b\u5916\uff0c\u8fd8\u6709\u4e24\u4f4d\u90fd\u662f\u5f88\u5bcc\u4e8e\u673a\u667a\u7684\u4eba\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53f6\u5144\u5df2\u7ecf\u8bf4\u670d\u4e86\u6211\u4eec\uff0c\u820d\u8eab\u5582\u864e\uff0c\u6211\u4eec\u59d0\u59b9\u662f\u5728\u6240\u4e0d\u60dc\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e2a\u4eba\u6ca1\u6709\u4efb\u4f55\u987e\u8651\uff0c \u53ea\u662f\u820d\u59b9\u5170\u767d\uff0c\u662f\u4e0d\u662f\u6709\u4e9b\u53ef\u60dc\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u63a5\u9053\uff1a\u201c\u6211\u662f\u8bf4\uff0c\u820d\u59b9\u8fd8\u662f\u4e00\u4f4d\u5f88\u597d\u7684\u59d1\u5a18\uff0c\u5982\u82e5\u5979\u88ab\u7cdf\u584c\u4e86\uff0c\u5c82\u4e0d\u662f\u592a\u4fbf\u5b9c\u9ad8\u5929\u5065\u90a3\u4e2a\u8001\u9b54\u5934\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8981\u8bf7\u6559\u4e00\u4e0b\u53f6\u5144\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7684\u795e\u60c5\uff0c\u6709\u4e09\u5206\u5c34\u5c2c\uff0c\u4e03\u5206\u4e25\u8083\uff0c\u7f13\u7f13\u8bf4\u9053\uff1b\u201c\u7389\u7fe0\uff0c\u5927\u4e49\u6240\u5728\uff0c\u6709\u4e9b\u5730\u65b9\uff0c\u5f88\u96be\u53eb\u4eba\u6ce8\u91cd\u5c0f\u8282\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201d\u53f6\u5144\u7684\u6c14\u5ea6\uff0c\u5f88\u53eb\u4eba\u611f\u6fc0\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u4f55\u5bd2\u8863\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u4f55\u7237\uff0c\u80fd\u4e0d\u80fd\u8ba9\u6211\u4eec\u59d0\u59b9\u5728\u8fd9\u91cc\u591a\u7559\u4e00\u591c?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u60f3\uff0c\u5e94\u8be5\u6ca1\u6709\u95ee\u9898\u5427\uff01\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1b \u201c\u65e2\u7136\u662f\u6ca1\u6709\u95ee\u9898\uff0c\u5c0f\u59b9\u6597\u80c6\u4f5c\u4e00\u4e2a\u51b3\u5b9a\uff0c\u4eca\u591c\uff0c\u8981\u8bf7\u4f55\u7237\u559d\u676f\u559c\u9152\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5728\u4e0b\u53ea\u6015\u662f\u6709\u4e9b\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u63a5\u9053\uff1a\u201c\u4f55\u7237\uff0c\u820d\u59b9\u65e2\u7136\u5df2\u662f\u53f6\u5144\u7684\u4eba\u4e86\uff0c\u6211\u60f3\u5c3d\u65e9\u66ff\u4ed6\u4eec\u5b8c\u6210\u6d1e\u623f\u82b1\u70db\uff0c\u53f6\u5144\u4fa0\u58eb\u80f8\u895f\uff0c\u820d\u59b9\u5374\u662f\u4e2a\u56fa\u6267\u7684\u5973\u5b69\uff0c\u8981\u5979\u4ee5\u6e05\u767d\u4e4b\u8eab\u5468\u65cb\u4e8e\u9ad8\u5929\u5065\u7684\u8eab\u4fa7\uff0c\u53ea\u6015\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6587\u5b9a\u4e4b\u793c\uff0c\u5982\u6b64\u7b80\u964b\uff0c\u5728\u4e0b\u5df2\u7ecf\u89c9\u5f97\u5bf9\u4e0d\u4f4f\u5170\u767d\u59d1\u5a18\u4e86!\u5982\u82e5\u6d1e\u623f\u82b1\u70db\uff0c\u518d\u95f9\u7684\u5982\u6b64\u7b80\u964b\uff0c\u5728\u4e0b\u7684\u5fc3\u4e2d\uff0c\u5c31\u66f4\u96be\u5b89\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u6211\u7684\u597d\u59b9\u4e08\uff0c\u65e2\u7136\u628a\u6211\u9001\u5165\u4e86\u864e\u5c71\u4e4b\u4e0a\uff0c\u90a3\u5df2\u7ecf\u6ce8\u5b9a\u4e86\u8fd9\u4ef6\u4e8b\u4e0d\u80fd\u5f20\u626c\uff0c\u6211\u95ee\u7684\u662f\u4f60\u613f\u4e0d\u613f\u610f\uff0c\u660e\u6708\u6e05\u98ce\uff0c\u6d69\u701a\u6e56\u6ce2\uff0c\u4e00\u53f6\u753b\u822b\uff0c\u884c\u9a70\u5e7d\u9759\u6e56\u6ce2\u4e4b\u4e2d\uff0c\u90a3\u4e0d\u662f\u5f88\u6709\u8bd7\u610f\u7684\u4e00\u6b21\u6d1e\u623f\u82b1\u70db\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u53ea\u8981\u5170\u767d\u59d1\u5a18\u4e0d\u89c9\u7740\u59d4\u5c48\uff0c\u5728\u4e0b\u5012\u662f\u6ca1\u6709\u4ec0\u4e48\u820c\u8bf4\uff0c\u4e0d\u53ea\u8fc7\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u4ec0\u4e48?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u6211\u65e0\u6cd5\u786e\u5b9a\u81ea\u5df1\u51e0\u65f6\u8981\u79bb\u5f00\u6b64\u5730\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e0d\u8bba\u6709\u4ec0\u4e48\u8981\u7d27\u7684\u4e8b\uff0c\u5c31\u4e0d\u80fd\u803d\u5ef6\u4e00\u5bb5\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53f6\u5144\uff0c\u6211\u770b\u4f60\u5c31\u7559\u5728\u8fd9\u91cc\u5427!\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f55\u7237\u4e5f\u8981\u7559\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\u9053\uff1a \u201c\u6211\u786e\u5b9e\u6709\u4e8b\uff0c\u4f46\u7389\u7fe0\u59d1\u5a18\u7684\u7559\u5ba2\u76db\u60c5\uff0c\u5728\u4e0b\u5341\u5206\u611f\u6fc0\uff0c\u6211\u53ea\u80fd\u7b54\u5e94\u5c3d\u53ef\u80fd\u7559\u4e0b\u6765\uff0c\u6211\u8fd9\u5c31\u53bb\u5b89\u6392\u4e00\u4e0b\uff0c\u5982\u662f\u60c5\u51b5\u4e0d\u7d27\u6025\uff0c\u8981\u4ed6\u4eec\u7b2c\u4e8c\u5929\u518d\u6765\u63a5\u6211\uff0c\u5982\u662f\u60c5\u51b5\u7d27\u6025\uff0c\u4ed6\u4eec\u4f1a\u627e\u6765\u6b64\u5730\u53eb\u6211\uff0c\u90a3\u65f6\uff0c\u5e0c\u671b\u59d1\u5a18\u5141\u8bb8\u5728\u4e0b\u79bb\u5f00?\u5982\u4f55\uff1f\u201d \u3000\u3000\u7389\u7fe0\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u8f6c\u8eab\u79bb\u5f00\u753b\u822b\u800c\u53bb\u3002 \u3000\u3000\u7389\u7fe0\u671b\u4e86\u53f6\u957f\u9752\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u8981\u548c\u4f55\u7237\u4e00\u8d77\u8d70?\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\u3002 \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u597d\u5427!\u5982\u662f\u771f\u6709\u4e86\u5f88\u91cd\u8981\u7684\u4e8b\uff0c\u4f60\u5c31\u548c\u4f55\u7237\u4e00\u8d77\u8d70\u5427\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u53ea\u597d\u70b9\u5934\u3002 \u3000\u3000\u4e01\u5cf0\u3001\u9a6c\u826f\u3001\u590f\u6740\u5e26\u7740\u9634\u9633\u5821\u7684\u4eba\u624b\u64a4\u8d70\u3002 \u3000\u3000\u7389\u7fe0\u4e5f\u628a\u753b\u822b\u9a70\u79bb\u4e86\u5cb8\u3002 \u3000\u3000\u753b\u822b\u4e0a\u53ea\u6709\u56db\u4e2a\u4eba\u3002 \u3000\u3000\u5728\u4e00\u5904\u5e7d\u9759\u7684\u6e56\u9762\u4e0a\uff0c\u7389\u7fe0\u629b\u4e0b\u951a\u3002 \u3000\u3000\u53ea\u6709\u56db\u76d8\u83dc\uff0c\u4f46\u5374\u6709\u4e00\u58f6\u597d\u8336\u3002 \u3000\u3000\u56db\u76d8\u83b1\u90fd\u662f\u9c7c\uff0c\u53ea\u662f\u505a\u6cd5\u4e0d\u540c\uff0c\u7ea2\u70e7\u3001\u6e05\u7096\u3001\u5e72\u7092\u548c\u6cb9\u70b8\u3002 \u3000\u3000\u4e00\u58f6\u597d\u8336\uff0c\u6743\u5f53\u9152\u3002 \u3000\u3000\u753b\u822b\u5c0f\u684c\u4e0a\uff0c\u56f4\u5750\u7740\u56db\u4e2a\u4eba\u3002 \u3000\u3000\u53f6\u957f\u9752\u3001\u4f55\u5bd2\u8863\u3001\u7389\u7fe0\u3001\u5170\u767d\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u53f6\u957f\u9752\u548c\u5170\u767d\u7684\u5927\u559c\u4e4b\u591c\u3002 \u3000\u3000\u4eca\u591c\u6e56\u4e0a\u65e0\u98ce\uff0c\u4f46\u84dd\u5929\u5374\u900f\u51fa\u4e86\u4e00\u8f6e\u660e\u6708\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u548c\u7389\u7fe0\u884c\u4e0a\u4e86\u7532\u677f\uff0c\u4e24\u4eba\u76d8\u819d\u800c\u5750\u3002 \u3000\u3000\u753b\u822b\u5185\u8231\uff0c\u6682\u4f5c\u4e86\u6d1e\u623f\u3002 \u3000\u3000\u8fd9\u4e00\u591c\uff0c\u8fc7\u5f97\u5f88\u9759\uff0c\u76f4\u5230\u65ed\u65e5\u521d\u5347\uff0c\u624d\u6709\u4e00\u8258\u5c0f\u821f\u9a76\u8fd1\u4e86\u753b\u822b\uff0c\u63a5\u8d70\u4e86\u4f55\u5bd2\u8863\u548c\u53f6\u957f\u9752\u3002 \u3000\u3000\u671b\u7740\u4e24\u4eba\u4e58\u5750\u7684\u5c0f\u821f\u53bb\u8fdc\uff0c\u7389\u7fe0\u624d\u50cf\u5378\u4e30\u4e86\u4e00\u526f\u91cd\u8892\uff0c\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u56de\u5230\u4e86\u8231\u4e2d\u3002 \u3000\u3000\u5170\u767d\u65e9\u5df2\u8d77\u8eab\uff0c\u800c\u4e14\u68b3\u6d17\u5df2\u6bd5\u3002 \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\uff0c\u906d\uff1a\u201c\u5170\u767d\uff0c\u6628\u591c\u8fc7\u5f97\u8fd8\u597d\u5427?\u201d \u3000\u3000\u5170\u767d\u5fae\u7b11\u4e0d\u7b54\u3002 \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u600e\u4e48?\u65b0\u5a18\u8fdb\u6d1e\u623f\uff0c\u5a92\u4eba\u4e22\u8fc7\u5899\uff0c\u8fde\u5bf9\u6211\u8fd9\u4e2a\u59ca\u59ca\uff0c\u4e5f\u8981\u4fdd\u5bc6\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59ca\u59ca\uff0c\u6211\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u5411\u4f60\u89e3\u8bf4\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5509!\u59b9\u59b9\uff0c\u4ed6\u5bf9\u4f60\u4e0d\u9519\u5427\uff01\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5bf9\u6211\u5f88\u597d\uff0c\u4e0d\u8fc7\uff0c\u59ca\u59ca\uff0c\u6211\u4ecd\u7136\u662f\u4f60\u7389\u6d01\u51b0\u6e05\u7684\u5c0f\u5e08\u59b9\u3002\u201d \u3000\u3000\u7389\u7fe0\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u53c8\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u59ca\u59ca\uff0c\u4ed6\u5f88\u4f53\u8d34\uff0c\u4e5f\u5bf9\u6211\u8bb8\u4e0b\u4e86\u5c71\u76df\u6d77\u8a93\uff0c\u6211\u76f8\u4fe1\u4ed6\u662f\u771f\u7684\uff0c\u4e0d\u8fc7\uff0c\u6211\u6ca1\u8ba9\u4ed6\u78b0\u6211\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e2b\u5934\uff0c\u4f60\u75af\u4e86\uff0c\u4f60\u671d\u4e5f\u601d\uff0c\u66ae\u4e5f\u60f3\uff0c\u6211\u8fd9\u4e2a\u505a\u59ca\u59ca\u7684\u539a\u7740\u8138\u76ae\uff0c\u7b97\u662f\u628a\u4f60\u5b89\u6392\u4e86\u4e00\u4e2a\u6d1e\u623f\u82b1\u70db\uff0c\u4f60\u4e3a\u4ec0\u4e48\u7adf\u7136\u8f7b\u8f7b\u7684\u5ea6\u8fc7\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u59ca\u59ca\uff0c\u6211\u5f88\u611f\u6fc0\u4f60\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e5f\u6709\u6211\u7684\u60f3\u6cd5\uff0c\u53ea\u8981\u4ed6\u771f\u5fc3\u5bf9\u6211\uff0c\u6211\u5c31\u5f88\u6ee1\u8db3\u4e86\u3002\u6211\u4e0d\u4f1a\u8d2a\u56fe\u90a3\u4e00\u591c\u4e4b\u6b22\uff0c\u5f04\u5f97\u65e5\u540e\u4e0d\u77e5\u8be5\u5982\u4f55\u89e3\u8bf4?\u201d \u3000\u3000\u7389\u7fe0\u6709\u4e9b\u660e\u767d\uff0c\u4f46\u8fd8\u6709\u4e00\u70b9\u4e0d\u592a\u6e05\u695a\uff0c\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u5170\u59b9\uff0c\u80fd\u4e0d\u80fd\u518d\u8bf4\u6e05\u695a\u4e00\u4e9b\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u59ca\u59ca\uff0c\u6211\u8981\u4fdd\u7559\u4e0b\u6e05\u767d\u7684\u8eab\u5b50\uff0c\u518d\u91cd\u9022\uff0c\u6211\u8981\u5411\u4ed6\u8bc1\u660e\uff0c\u6211\u8fd8\u662f\u6e05\u767d\u4e4b\u8eab\uff0c\u6240\u4ee5\uff0c\u6211\u4e0d\u60f3\u8ba9\u4ed6\u78b0\u6211\uff0c\u59ca\u59ca\uff0c\u6211\u5982\u8eab\u8eaf\u73b7\u6c61\uff0c\u65e5\u540e\uff0c\u6211\u7eb5\u80fd\u4fdd\u6301\u6e05\u767d\uff0c\u4e5f\u65e0\u6cd5\u5411\u4ed6\u8bc1\u660e\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u7389\u7fe0\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1b\u201c\u5170\u59b9\uff0c\u4f60\u600e\u4e48\u8fd9\u4e48\u7cca\u6d82\u554a?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u6211\u600e\u4e48\u7cca\u6d82\u5462?\u6211\u81ea\u5df1\u89c9\u7740\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u8fd8\u505a\u5f97\u633a\u806a\u660e\u5462!\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1b\u201c\u5509!\u4f60\u6ca1\u6709\u542c\u5230\u4f55\u5bd2\u8863\u7684\u8bdd\u5417?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4ed6\u8bf4\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4ed6\u8bf4\u9ad8\u5929\u5065\uff0c\u662f\u4e00\u4e2a\u5f88\u7231\u5973\u8272\u7684\u5165\uff0c\u7b28\u59b9\u59b9\uff0c\u9ec4\u5821\u4e3b\u4e0d\u60dc\u4ed6\u6536\u85cf\u591a\u5e74\u7684\u5b9d\u7269\uff0c\u53eb\u53f6\u957f\u9752\u6551\u6d3b\u54b1\u4eec\uff0c\u4f60\u53ef\u77e5\u9053\u4ed6\u7684\u7528\u5fc3\u5417?\u201d \u3000\u3000\u5170\u767d\u6447\u6447\u5934\u3002 \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u4ed6\u7684\u7528\u5fc3\u5f88\u660e\u663e\uff0c\u8981\u54b1\u4eec\u53bb\u63a5\u8fd1\u9ad8\u5929\u5065\uff0c\u5c31\u662f\u8981\u54b1\u4eec\u5e03\u65bd\u8272\u8eab\u3002\u201d \u5170\u767d\u7684\u8138\u8272\u5f88\u5e84\u4e25\uff0c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u59ca\u59ca\uff0c\u4ec0\u4e48\u4e8b\uff0c\u6211\u90fd\u53ef\u4ee5\u542c\u4f60\u7684\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u6211\u4e0d\u80fd\u542c\u4f60\u7684\uff0c\u6211\u5b81\u53ef\u6b7b\uff0c\u4e5f\u4e0d\u613f\u767d\u74a7\u73b7\u6c61\u3002\u201d \u3000\u3000\u7389\u7fe0\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5170\u767d\uff0c\u4f60\u8fd8\u8981\u4e0d\u8981\u53bb\u89c1\u9ad8\u5929\u5065?\u201d \u3000\u3000\u5170\u767d\u9053\uff1b\u201c\u53bb!\u4e3a\u4e86\u4ed6\uff0c\u6211\u4e0d\u6015\u4efb\u4f55\u51f6\u9669\u3002\u201d \u3000\u3000\u7389\u7fe0\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u5427!\u54b1\u4eec\u8d70\u4e00\u6b65\u7b97\u4e00\u6b65\uff0c\u59ca\u59ca\u5c3d\u529b\u7ef4\u62a4\u4f60\u5c31\u662f\uff0c\u5982\u662f\u4e07\u4e00\u7ef4\u62a4\u4e0d\u4f4f\uff0c\u90a3\u4e5f\u662f\u6ca1\u6709\u6cd5\u5b50\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a \u201c\u597d!\u5c31\u8fd9\u4e48\u4e00\u8a00\u4e3a\u5b9a\uff0c\u5230\u65f6\u95f4\uff0c\u6211\u5982\u767d\u74a7\u73b7\u6c61\uff0c\u6211\u5c31\u4f1a\u4e00\u6b7b\u4ee5\u8c22\u53f6\u90ce\u3002\u201d \u3000\u3000\u4e00\u53f6\u8f7b\u821f\uff0c\u628a\u4f55\u5bd2\u8863\u548c\u53f6\u957f\u9752\u9001\u4e0a\u4e86\u6e56\u5cb8\uff0c \u3000\u3000\u4f55\u5bd2\u8863\u4f3c\u662f\u65e9\u5df2\u77e5\u6653\u4e86\u53bb\u5904\u4f55\u5728\uff0c\u5e26\u7740\u53f6\u957f\u9752\u5230\u4e86\u4e00\u5ea7\u6e56\u7554\u519c\u820d\u4e4b\u4e2d\u3002 \u3000\u3000\u867d\u7136\u662f\u8305\u820d\u7af9\u7b52\uff0c\u4f46\u5374\u6253\u626b\u5f97\u5341\u5206\u5e72\u51c0\u3002 \u3000\u3000\u5927\u5385\u4e2d\u65e9\u5df2\u7ecf\u5750\u4e86\u4e09\u4e2a\u4eba\u3002 \u3000\u3000\u4e09\u4eba\u4e2d\u4e24\u7537\u4e00\u5973\uff0c\u53f6\u957f\u9752\u8ba4\u8bc6\u4e86\u5176\u4e2d\u4e24\u4e2a\u3002 \u3000\u3000\u90a3\u662f\u9ebb\u9762\u8840\u624b\u5c60\u65e0\u65b9\u3001\u51b7\u624b\u7f57\u5239\u534e\u5999\u771f\u3002 \u3000\u3000\u8fd8\u6709\u4e2a\u4e09\u65ec\u5de6\u53f3\u7684\u9752\u8863\u4eba\uff0c\u7ecf\u8fc7\u4e86\u4f55\u5bd2\u8863\u7684\u5f15\u89c1\uff0c\u53f6\u957f\u9752\u624d\u77e5\u9053\u90a3\u662f\u6155\u540d\u5df2\u4e45\u7684\u5929\u624b\u5200\u4e07\u80dc\u3002 \u3000\u3000\u4e07\u80dc\u548c\u4f55\u5bd2\u8863\u4e00\u6837\uff0c\u662f\u6b63\u6d3e\u95e8\u6237\u4e2d\u4eba\uff0c\u662f\u6c5f\u6e56\u4e0a\u516c\u8ba4\u7684\u5927\u4fa0\u3002 \u3000\u3000\u9634\u9633\u5821\u4e2d\u4e94\u5927\u5251\u4f7f\u805a\u9f50\u4e86\uff0c\u4f1a\u6664\u5728\u8fd9\u4e2a\u5c0f\u5c0f\u7684\u519c\u820d\u4e4b\u4e2d\u3002 \u3000\u3000\u534e\u5999\u771f\u671b\u4e86\u53f6\u957f\u9752\u4e00\u773c\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e45\u8fdd\u4e86\uff0c\u53f6\u5144\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6025\u6025\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u4e45\u8fdd\u4e45\u8fdd\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5e74\u7eaa\u548c\u53f6\u957f\u9752\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u5728\u4e94\u4eba\u4e2d\u53ef\u80fd\u6700\u5e74\u8f7b\u7684\u4e00\u4e2a\uff0c\u4f46\u4ed6\u5374\u662f\u4e94\u5927\u5251\u4f7f\u4e4b\u9996\u3002 \u3000\u3000\u5385\u4e2d\u4e00\u5f20\u516b\u4ed9\u684c\u65c1\uff0c\u65e9\u5df3\u653e\u4e86\u4e94\u5f20\u6728\u6905\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e5f\u4e0d\u8c26\u900a\uff0c\u5c31\u5728\u9996\u4f4d\u4e0a\u5750\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u4e07\u5144\uff0c\u67e5\u660e\u4e86\u5821\u4e3b\u7684\u53bb\u5904\u5417?\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u5144\u5f1f\u4e00\u76f4\u6d3e\u4eba\u76ef\u7740\u5821\u4e3b\uff0c\u800c\u4e14\u6cbf\u9014\u7559\u4e0b\u4e86\u4eba\u624b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u5f88\u673a\u667a\uff0c\u522b\u8ba9\u4ed6\u53d1\u89c9\u4e86\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a \u201c\u4e0d\u4f1a\uff0c\u4e3a\u4e86\u8ffd\u8e2a\u5821\u4e3b\u7684\u53bb\u5904\uff0c\u5144\u5f1f\u8fd9\u6b21\u5e26\u4e86\u4e09\u5341\u516d\u4e2a\u4eba\u6765\uff0c\u8fd9\u4e9b\u4eba\uff0c\u90fd\u662f\u5f88\u5c11\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\u7684\u4eba\uff0c\u4e5f\u662f\u5144\u5f1f\u4e07\u5bb6\u5be8\u7684\u7cbe\u9510\uff0c\u4ed6\u4eec\u90fd\u5f88\u8c28\u614e\u5c0f\u5fc3\uff0c\u800c\u4e14\u4e2a\u4e2a\u5e72\u7ec3\uff0c\u5982\u82e5\u51fa\u4e86\u6bdb\u75c5\uff0c\u5144\u5f1f\u613f\u8d1f\u8d77\u8d23\u4efb\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e09\u5341\u516d\u4eba\u4ea4\u66ff\u8ffd\u8e2a\uff0c\u5821\u4e3b\u7eb5\u7136\u7cbe\u660e\uff0c\u53ea\u6015\u4e5f\u5f88\u96be\u53d1\u89c9\u4e86\u3002\u201d \u3000\u3000\u4e07\u80dc\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5821\u4e3b\u667a\u6167\uff0c\u5f3a\u8fc7\u54b1\u4eec\u5341\u500d\uff0c\u6240\u4ee5\uff0c\u6211\u8981\u4ed6\u4eec\u5316\u88c5\u6210\u5404\u79cd\u4e0d\u540c\u7684\u8eab\u4efd\uff0c\u60f3\u6cd5\u5b50\u8ffd\u8e2a\u5821\u4e3b\uff0c\u800c\u4e14\uff0c\u6211\u5df2\u7ecf\u4ea4\u4ee3\u4ed6\u4eec\uff0c\u5904\u5904\u5c0f\u5fc3\uff0c\u4e0d\u8981\u66b4\u9732\u4e86\u884c\u8e2a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d!\u4e07\u5144\u7684\u90e8\u7f72\uff0c\u5341\u5206\u5b8c\u7f8e\uff0c\u4e0d\u77e5\u9053\u54b1\u4eec\u51e0\u65f6\u624d\u80fd\u591f\u5f97\u5230\u6d88\u606f\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u5929\u8272\u5165\u591c\u4e4b\u524d\uff0c\u5b9a\u4f1a\u6709\u6d88\u606f\u4f20\u6765\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u4f55\u5144\uff0c\u5e38\u548c\u5821\u4e3b\u503e\u8c08\uff0c\u53ef\u77e5\u9053\uff0c\u4ed6\u548c\u4ec0\u4e48\u4eba\u7ed3\u7684\u6069\u6028\u5417\uff1f\u201d \u4f55\u5bd2\u8863\u9053\uff1a \u201c\u8be6\u7ec6\u5185\u60c5\uff0c\u4e0d\u592a\u4e86\u89e3\uff0c\u4f46\u6211\u770b\u5f97\u51fa\u6765\uff0c\u8fd9\u5728\u4ed6\u5185\u5fc3\u4e4b\u4e2d\uff0c\u662f\u4e00\u4e2a\u5f88\u5927\u7684\u8d1f\u62c5\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4f55\u5144\uff0c\u5821\u4e3b\u548c\u4eba\u7ea6\u4f1a\u4e4b\u5904\uff0c\u4e0d\u4f1a\u79bb\u6b64\u592a\u8fdc\u5427?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e94\u8be5\u4e0d\u4f1a\u592a\u8fdc\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a \u201c\u8bf8\u4f4d\u8fdc\u9053\u8d76\u6765\uff0c\u60f3\u5fc5\u5df2\u5341\u5206\u56f0\u4e4f\uff0c\u8bf4\u4e0d\u5b9a\uff0c\u4eca\u591c\u4e4b\u4e2d\uff0c\u8fd8\u4f1a\u6709\u4e00\u573a\u8840\u6218\uff0c\u54b1\u4eec\u4f55\u4e0d\u5229\u7528\u8fd9\u6bb5\u65f6\u95f4\uff0c\u597d\u597d\u5750\u606f\u4e00\u4e0b?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e3a\u4e86\u4fdd\u5bc6\u884c\u8e2a\uff0c\u5c0f\u59b9\u4e0b\u53a8\uff0c\u66ff\u8bf8\u4f4d\u51c6\u5907\u597d\u4e86\u4e00\u4e9b\u5403\u559d\u4e4b\u7269\uff0c\u8bf8\u4f4d\u80e1\u4e71\u7528\u4e9b\uff0c\u518d\u4f11\u606f\u5982\u4f55?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1b\u201c\u534e\u59d1\u5a18\u4eb2\u624b\u505a\u7684\u4f73\u80b4\u7f8e\u5473\uff0c\u53ef\u662f\u96be\u5f97\u7684\u5f88\uff0c\u5feb\u8bf7\u7aef\u51fa\u6765\u5427\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u6367\u4e0a\u4e86\u9152\u83b1\u3002 \u3000\u3000\u8fd9\u4f4d\u53f1\u5412\u6c5f\u6e56\u7684\u5973\u7f57\u5239\uff0c\u5c45\u7136\u70e7\u5f97\u4e00\u624b\u597d\u83dc\u3002 \u3000\u3000\u5929\u624b\u5200\u4e07\u80dc\u7684\u5c5e\u4e0b\u5f88\u5b88\u65f6\uff0c\u5929\u8fd1\u9ec4\u660f\u65f6\u5206\u679c\u7136\u4f20\u6765\u4e86\u6d88\u606f\u3002 \u3000\u3000\u90a3\u662f\u4e2a\u4e00\u8eab\u571f\u5e03\u88e4\u8902\u7684\u4e2d\u5e74\u5927\u6c49\uff0e\u80a9\u4e0a\u8fd8\u625b\u7740\u4e00\u67c4\u94c1\u9504\u3002 \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u5821\u4e3b\u7684\u5927\u9a7e\uff0c\u73b0\u5728\u4f55\u5904?\u201d \u3000\u3000\u90a3\u5e03\u8863\u5927\u6c49\u5f88\u606d\u8c28\u5730\u5e94\u9053\uff1a\u201c\u5821\u4e3b\u8fdb\u4e86\u4e00\u5ea7\u8352\u51c9\u7684\u5b85\u9662\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1b\u201c\u4e00\u5ea7\u8352\u51c9\u7684\u5b85\u9662\uff0c\u5728\u54ea\u91cc?\u79bb\u6b64\u591a\u8fdc?\u201d \u3000\u3000\u5e03\u8863\u5927\u6c49\u9053\uff1a \u201c\u6211\u4eec\u5df2\u6253\u542c\u8fc7\u4e86\uff0c\u90a3\u5ea7\u8352\u51c9\u7684\u5b85\u9662\uff0c\u53eb\u4f5c\u97e9\u5bb6\u5927\u9662\uff0c\u5df2\u6709\u4e8c\u4e09\u5341\u5e74\u6ca1\u6709\u4f4f\u8fc7\u4eba\u4e86\uff0c\u5b85\u9662\u7684\u89c4\u6a21\u5f88\u5927\uff0c\u4f46\u65b9\u5706\u4e09\u56db\u91cc\u5185\uff0c\u6ca1\u6709\u4eba\u5bb6\uff0c\u542c\u8bf4\u90a3\u91cc\u4e0d\u592a\u5e73\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e0d\u592a\u5e73\uff0c\u53ef\u662f\u95f9\u9b3c\u4e86?\u201d \u3000\u3000\u5e03\u8863\u5927\u6c49\u9053\uff1a \u201c\u9644\u8fd1\u7684\u4eba\uff1b\u662f\u8fd9\u4e48\u4f20\u8bf4\uff0c\u6240\u4ee5\uff0c\u5929\u4e00\u9ed1\uff0c\u5728\u9644\u8fd1\u79cd\u7530\u7684\u4eba\uff0c\u90fd\u6025\u6025\u79bb\u53bb\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u4f60\u4eec\u5462?\u4e5f\u6015\u9b3c\u5417?\u201d \u3000\u3000\u5e03\u8863\u5927\u6c49\u9053\uff1a \u201c\u5c5e\u4e0b\u5df2\u5728\u90a3\u8352\u56ed\u56db\u5468\uff0c\u6309\u4e0b\u5341\u4e8c\u5904\u6697\u6869\uff0c\u5821\u4e3b\u53ea\u8981\u79bb\u5f00\u90a3\u91cc\uff0c\u90fd\u4f1a\u88ab\u6211\u4eec\u53d1\u73b0\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u5f88\u597d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u97e9\u5bb6\u5e9f\u56ed\uff0c\u79bb\u6b64\u591a\u8fdc?\u201d \u3000\u3000\u5e03\u8863\u5927\u6c49\u9053\uff1a\u201c\u4e0d\u8fdc\uff0c\u603b\u5728\u5341\u56db\u4e94\u91cc\u5de6\u53f3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5947\u602a\u5440!\u4ed6\u600e\u4e48\u4f1a\u5230\u90a3\u4e48\u4e2a\u5730\u65b9\u5462?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u7ea6\u4e86\u4eba\u5728\u90a3\u91cc\u4f1a\u9762?\u4ed6\u65e2\u7136\u4e0d\u60f3\u6211\u4eec\u77e5\u9053\u4ed6\u79c1\u4eba\u7684\u6069\u6028\uff0c\u81ea\u7136\u7ea6\u4f1a\u7684\u5730\u65b9\uff0c\u8d8a\u79d8\u5bc6\u8d8a\u597d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u97e9\u5bb6\u5ead\u56ed\uff0c\u65e2\u7136\u662f\u8352\u51c9\u4e86\u4e8c\u5341\u591a\u5e74\uff0c\u81ea\u662f\u4e0d\u4f1a\u6709\u4eba\u5c45\u4f4f\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1b\u201c\u90a3\u662f\u8bf4\uff0c\u4ed6\u7ea6\u4e86\u522b\u4eba\u5728\u90a3\u91cc\u4e86?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\u3002 \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u5c31\u60f3\u4e0d\u901a\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u4f1a\u6709\u4ec0\u4e48\u4eba\u80fd\u4f7f\u5821\u4e3b\u5982\u6b64\u7518\u613f\u5c31\u8303\u7684\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u60f3\uff0c\u5821\u4e3b\u4e0d\u662f\u4e3a\u4e86\u90a3\u4eba\u7684\u6b66\u529f\u5c31\u8303\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u96be\u9053\u6709\u4eba\u638c\u63e1\u4e86\u5821\u4e3b\u4ec0\u4e48\u7f3a\u61be\u4e0d\u6210\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u4e5f\u8bb8\u54b1\u4eec\u4eca\u665a\u4e0a\u5c31\u53ef\u4ee5\u63ed\u7a7f\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u4e5f\u8be5\u53bb\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5e26\u7740\u56db\u4e2a\u4eba\uff0c\u8d76\u5230\u4e86\u97e9\u5bb6\u5ead\u56ed\u3002 \u3000\u3000\u679c\u7136\uff0c\u5728\u97e9\u5bb6\u5927\u9662\u7684\u56db\u5468\uff0c\u5e03\u5b88\u4e86\u5341\u4e8c\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u5341\u4e8c\u4e2a\u4eba\u5206\u7ad9\u7684\u65b9\u4f4d\uff0c\u521a\u597d\u628a\u6574\u5ea7\u7684\u97e9\u5bb6\u5927\u9662\u6240\u6709\u7684\u9762\uff0c\u5b8c\u5168\u5728\u7684\u76d1\u89c6\u4e4b\u4e0b\uff0c\u4efb\u4f55\u4e00\u4e2a\u4eba\uff0c\u53ea\u8981\u7531\u97e9\u5bb6\u5927\u9662\u51fa\u6765\uff0c\u7edd\u5bf9\u6ca1\u6709\u529e\u6cd5\u9003\u8fc7\u4ed6\u4eec\u7684\u76d1\u89c6\u3002 \u3000\u3000\u4e07\u80dc\u5429\u5490\u4e86\u5341\u4e8c\u4e2a\u4eba\uff0c\u4ecd\u7136\u5b88\u5728\u539f\u4f4d\uff0c\u5168\u9762\u76d1\u89c6\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u3001\u53f6\u957f\u9752\u3001\u5c60\u65e0\u65b9\u3001\u534e\u5999\u771f\u3001\u4e07\u80dc\u7b49\u4e94\u4eba\u5374\u6084\u7136\u884c\u5165\u97e9\u5bb6\u5927\u9662\u3002 \u3000\u3000\u97e9\u5bb6\u5927\u9662\uff0c\u867d\u7136\u662f\u4e00\u7247\u8352\u51c9\uff0c\u4f46\u4ecd\u7136\u9690\u9690\u53ef\u89c1\u6614\u5e74\u7684\u89c4\u6a21\uff0c\u5e7f\u5927\u7684\u5ead\u9662\u4e2d\uff0c\u957f\u6ee1\u4e86\u6df1\u53ef\u53ca\u8170\u7684\u8352\u8349\u3002 \u3000\u3000\u5230\u5904\u662f\u6b8b\u57a3\u65ad\u58c1\uff0c\u89e6\u76ee\u4e00\u7247\u51c4\u51c9\u3002 \u3000\u3000\u4eca\u591c\u6709\u4e91\uff0c\u6708\u88ab\u4e91\u906e\u3002 \u3000\u3000\u6708\u5149\u900f\u8fc7\u4e91\u5c42\uff0c\u6d12\u4e0b\u6765\uff0c\u4e00\u7247\u5e7d\u9ec4\u7684\u5149\u8f89\u3002 \u3000\u3000\u4e94\u4e2a\u4eba\uff0c\u90fd\u6362\u4e0a\u4e86\u6697\u8272\u7684\u8863\u670d\uff0c\u9690\u5728\u8352\u8349\u4e2d\u3002 \u3000\u3000\u4ed6\u4eec\u65e9\u5df2\u6709\u4e86\u8ba1\u8bae\uff0c\u51b3\u5b9a\u5c3d\u91cf\u9690\u79d8\u884c\u8e2a\u3002 \u3000\u3000\u4ed6\u4eec\u4e86\u89e3\u9ec4\u7075\u4e4b\u80fd\uff0c\u5341\u4e08\u4e4b\u5185\uff0c\u53ef\u5206\u8fa8\u843d\u53f6\u7684\u58f0\u97f3\u3002 \u3000\u3000\u97e9\u5bb6\u5ead\u56ed\uff0c\u867d\u7136\u8352\u51c9\uff0c\u4f46\u5374\u5f88\u5e7f\u5927\uff0c\u4e94\u4e2a\u4eba\u5c0f\u5fc3\u7ffc\u5180\u7684\u6478\u8fdb\u4e86\u7b2c\u4e8c\u91cd\u9662\u843d\u3002 \u3000\u3000\u56db\u68f5\u9ad8\u5927\u7684\u767d\u6768\u6811\uff0c\u4e09\u68f5\u767e\u5e74\u4ee5\u4e0a\u7684\u767d\u679c\u6811\uff0c\u628a\u97e9\u5bb6\u5ead\u56ed\u7b2c\u4e8c\u8fdb\u5ead\u9662\u5185\u9020\u6210\u4e86\u4e00\u79cd\u5947\u7279\u7684\u9634\u68ee\u3002 \u3000\u3000\u8fd9\u4e03\u68f5\u5927\u6811\uff0c\u5e76\u6ca1\u6709\u56e0\u4e3a\u97e9\u5bb6\u5927\u9662\u7684\u8352\u51c9\u800c\u840e\u67af\uff0c\u4ecd\u7136\u662f\u84ec\u52c3\u7fe0\u7eff\uff0c\u679d\u53f6\u5bc6\u8302\u3002 \u3000\u3000\u4e03\u68f5\u5927\u6811\uff0c\u836b\u7f69\u4e86\u5927\u534a\u4e2a\u4e8c\u8fdb\u9662\u843d\uff0c \u3000\u3000\u8fd9\u5c31\u4f7f\u5f97\u6574\u4e2a\u9662\u843d\uff0c\u663e\u5f97\u6709\u4e9b\u9634\u68ee\uff0c\u9ec4\u660f\u7684\u5929\u5149\uff0c\u6e05\u51b7\u7684\u591c\u98ce\uff0c\u4f7f\u5f97\u6574\u4e2a\u4e8c\u8fdb\u9662\u843d\u4e2d\uff0c\u7b3c\u7f69\u5728\u2014\u79cd\u8be1\u79d8\u6c14\u6c1b\u4e2d\u3002 \u3000\u3000\u767d\u6768\u53f6\u5728\u591c\u98ce\u4e2d\uff0c\u53d1\u51fa\u4e00\u79cd\u6c99\u6c99\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u95ea\u8eb2\u5728\u4e00\u682a\u5927\u6811\u4e4b\u540e\uff0c\u8fd0\u662f\u76ee\u529b\u5411\u56db\u4e0b\u671b\u53bb\u3002 \u3000\u3000\u6574\u4e2a\u7684\u5ead\u9662\u4e2d\u90fd\u662f\u53ca\u4eba\u7684\u9752\u8349\u3002 \u3000\u3000\u98ce\u5439\u8349\u52a8\uff0c\u4f3c\u4e4e\u662f\u6709\u65e0\u6570\u7684\u4eba\u5728\u6447\u52a8\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6781\u5c3d\u76ee\u529b\uff0c\u641c\u5bfb\u5f97\u5341\u5206\u4ed4\u7ec6\u3002\u8db3\u8db3\u82b1\u53bb\u4e86\u4e00\u523b\u5de5\u592b\u4e4b\u4e45\uff0c\u4f55\u5bd2\u8863\u624d\u786e\u5b9a\u8fd9\u91cc\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u8352\u51c9\u7684\u97e9\u5bb6\u5ead\u56ed\uff0c\u623f\u820d\u867d\u7136\u5df2\u5012\u584c\uff0c\u4f46\u5385\u820d\u7684\u89c4\u6a21\u4ecd\u7136\u5f88\u5927\uff0c\u623f\u5c4b\u867d\u7136\u6b8b\u7f3a\u4e86\uff0c\u4f46\u4ecd\u7136\u6709\u623f\u5c4b\u7684\u5f62\u5f0f\u3002 \u3000\u3000\u5982\u82e5\u9ec4\u5821\u4e3b\u5728\u4e00\u5ea7\u7a7a\u623f\u4e4b\u4e2d\uff0c\u90a3\u5b9e\u5728\u5f88\u96be\u627e\u5230\u4e86\uff0c\u53f6\u957f\u9752\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f55\u5144\uff0c\u54b1\u4eec\u5e94\u8be5\u5982\u4f55?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u4f4e\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u518d\u5411\u540e\u9762\u627e\u627e\u770b\u3002\u201d \u3000\u3000\u4e94\u4e2a\u4eba\u7684\u884c\u52a8\uff0c\u66f4\u5c0f\u5fc3\u4e86\uff0c\u7f13\u6b65\u6162\u884c\uff0c\u70b9\u5c18\u4e0d\u60ca\u3002 \u3000\u3000\u7b2c\u4e09\u8fdb\u9662\u843d\u662f\u4e00\u4e2a\u82b1\u56ed\u3002 \u3000\u3000\u82b1\u56ed\u4e2d\u66f4\u8352\u51c9\u3002 \u3000\u3000\u82b1\u56ed\u4e2d\u5df2\u7ecf\u65e0\u82b1\uff0c\u4ee3\u4e4b\u7684\u662f\u2014\u2014\u56ed\u8352\u8349\u3002 \u3000\u3000\u4f46\u8352\u51c9\u7684\u82b1\u56ed\u4e2d\uff0c\u5374\u6709\u4e00\u5ea7\u51c9\u4ead\u3002 \u3000\u3000\u90a3\u51c9\u4ead\u5f88\u5b8c\u597d\uff0c\u4e00\u70b9\u4e5f\u4e0d\u6b8b\u7834\u3002 \u3000\u3000\u60f3\u6765\uff0c\u5f53\u5e74\u5efa\u7b51\u8fd9\u5ea7\u97e9\u5bb6\u5927\u9662\u65f6\uff0c\u8fd9\u5ea7\u51c9\u4ead\uff0c\u5efa\u7b51\u5f97\u7279\u522b\u575a\u56fa\u3002 \u3000\u3000\u51c9\u4ead\u4e2d\u5750\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u591c\u98ce\u6447\u52a8\u7740\u8352\u8349\uff0c\u54cd\u8d77\u4e86\u6c99\u6c99\u7684\u58f0\u97f3\u3002 \u3000\u3000\u8fd9\u5bf9\u4f55\u5bd2\u8863\u7b49\u4eba\u5e2e\u5fd9\u5f88\u5927\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7b49\u76ee\u5149\u867d\u7136\u9510\u5229\uff0c\u4f46\u4e5f\u770b\u4e0d\u51fa\u90a3\u4ead\u4e2d\u5750\u7684\u4ec0\u4e48\u4eba? \u3000\u3000\u8fd9\u6837\u8352\u51c9\u7684\u5ead\u9662\uff0c\u5e94\u8be5\u4e0d\u4f1a\u6709\u4eba\u6765\uff0c \u81ea\u7136\uff0c\u90a3\u4eba\u5341\u5206\u53ef\u80fd\u5c31\u662f\u9ec4\u7075\u3002 \u4f55\u5bd2\u8863\u7b49\u9690\u8eab\u5728\u8352\u8349\u4e4b\u4e2d\uff0c \u76ee\u5149\u5374\u6295\u6ce8\u5728\u51c9\u4ead\u4e4b\u4e0a\u3002 \u90a3\u4eba\u4f3c\u4e4e\u662f\u6709\u6240\u8b66\u89c9\uff0c\u5ffd\u7136\u7ad9\u8d77\u4e86\u8eab\u5b50\uff0c\u56db\u4e0b\u671b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b49\u542c\u51fa\u6765\u4e86\uff0c\u90a3\u6b63\u662f\u9ec4\u7075\u7684\u58f0\u97f3\u3002 \u3000\u3000\u51e0\u4eba\u65e9\u6709\u8ba1\u8bae\uff0c\u542c\u5230\u4e86\u9ec4\u7075\u7684\u58f0\u97f3\u4e4b\u540e\uff0c\u7acb\u523b\u4f4f\u4e86\u547c\u5438\u3002 \u3000\u3000\u4e0d\u4f46\u8fde\u5927\u6c14\u4e5f\u4e0d\u6562\u51fa\u4e00\u53e3\uff0c\u800c\u4e14\u628a\u5934\u4f4e\u4e00\u4f4e\uff0c\u4f0f\u5728\u4e86\u6df1\u8349\u4e1b\u4e2d\u3002 \u3000\u3000\u9ec4\u7075\u867d\u7136\u5fc3\u6709\u6240\u601d\uff0c\u4f46\u4ed6\u5bf9\u81ea\u5df1\u7684\u8033\u6735\uff0c\u6709\u7740\u5f3a\u70c8\u7684\u81ea\u4fe1\u3002\u4e0d\u95fb\u56de\u5e94\u4e4b\u58f0\uff0c\u7acb\u65f6\u53c8\u5927\u58f0\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u5a07\u5a07\u8106\u8106\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u6211!\u201d \u3000\u3000\u968f\u90a3\u98d8\u6765\u7684\u58f0\u97f3\uff0c\u75be\u5982\u6d41\u661f\u4e00\u822c\uff0c\u7531\u4e00\u91cd\u5c4b\u810a\u4e0a\u98de\u843d\u4e0b\u4e24\u6761\u4eba\u5f71\u3002 \u3000\u3000\u597d\u4f73\u5999\u7684\u8f7b\u529f\u3002 \u3000\u3000\u90a3\u5c4b\u810a\u8ddd\u51c9\u4ead\uff0c\u81f3\u5c11\u6709\u516d\u4e03\u4e08\u8fdc\uff0c\u4f46\u8fd9\u4e24\u4eba\u4e00\u4e2a\u98de\u8dc3\uff0c\u7adf\u7136\u843d\u5230\u4e86\u51c9\u4ead\u5916\u9762\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5fae\u5fae\u62ac\u5934\u770b\u53bb\uff0c\u53d1\u89c9\u662f\u4e24\u4e2a\u8001\u4eba\u3002 \u3000\u3000\u4e00\u7537\u4e00\u5973\u7684\u4e24\u4e2a\u8001\u4eba\u3002 \u3000\u3000\u7537\u7684\u767d\u9aef\u5782\u80f8\uff0c\u5927\u7ea6\u6709\u516d\u65ec\u4ee5\u4e0a\u3002 \u3000\u3000\u5973\u7684\u767d\u53d1\u7070\u888d\uff0c\u624b\u4e2d\u4e00\u6839\u9e20\u5934\u62d0\u3002 \u3000\u3000\u9ec4\u7075\u4e00\u62b1\u62f3\u9053\uff1a\u201c\u4e24\u4f4d\uff0c\u8bf7\u5165\u51c9\u4ead\u4e00\u53d9\u3002\u201d \u3000\u3000\u7070\u8863\u8001\u5987\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u548c\u4f60\u6ca1\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\uff0c\u4f60\u62ff\u547d\u6765\u5427\uff01\u201d \u3000\u3000\u9ec4\u7075\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u4eca\u591c\u5728\u4e0b\u8d74\u7ea6\u800c\u6765\uff0c\u5c31\u51c6\u5907\u63a5\u53d7\u4efb\u4f55\u60e9\u7f5a\uff0c\u4e0d\u8fc7\uff0c\u4e8b\u60c5\u603b\u9700\u8981\u8bf4\u4e2a\u660e\u767d\uff0c\u957f\u591c\u6f2b\u6f2b\uff0c\u73b0\u5728\u4e0d\u8fc7\u521d\u66f4\u65f6\u5206\uff0e\u4e24\u4f4d\u53c8\u4f55\u5fc5\u8fd9\u4f6f\u6025\u4fc3\u5462?\u201d \u3000\u3000\u7070\u8863\u8001\u5987\u9053\uff1a \u201c\u8001\u8eab\u60f3\u4e0d\u51fa\uff0e\u8fd8\u6709\u4ec0\u4e48\u8981\u8bf4\u660e\u767d\u7684\uff0c \u5c0f\u5973\u56e0\u4f60\u800c\u6b7b\uff0c\u5df2\u7ecf\u662f\u4e0d\u4e89\u4e4b\u5b9e\uff0c\u96be\u9053\u8fd8\u8981\u6211\u592b\u5987\u62ff\u51fa\u4ec0\u4e48\u8bc1\u660e\u4e0d\u6210\u3002\u201d \u3000\u3000\u9ec4\u7075\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u4ee4\u7231\u4e4b\u6b7b\uff0c\u5728\u4e0b\u81ea\u662f\u4e0d\u80fd\u63a8\u5378\u8d23\u4efb\uff0c\u81f3\u5c11\uff0c\u5728\u4e0b\u662f\u539f\u56e0\u4e4b\u4e00\uff0c\u53ea\u53ef\u60dc\uff0c\u6211\u8d76\u5230\u4e4b\u65f6\uff0c\u4e24\u4f4d\u5df2\u628a\u4ee4\u7231\u7684\u5c38\u4f53\u8fd0\u8d70\uff0c\u5728\u4e0b\u4e00\u76f4\u672a\u80fd\u770b\u5230\u4ee4\u7231\u7684\u5c38\u4f53\u2026\u2026\u201d \u3000\u3000\u7070\u8863\u8001\u5987\u6012\u9053\uff1a \u201c\u59d3\u9ec4\u7684\uff0c\u96be\u9053\u6211\u592b\u5987\u8fd8\u4f1a\u9a97\u4f60\u4e0d\u6210\uff0c\u5c31\u7b97\u6211\u4eec\u8981\u9a97\u4f60\uff0c\u4e5f\u4e0d\u4f1a\u5492\u81ea\u5df1\u7684\u5973\u513f\u53bb\u6b7b\u554a\uff01\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5c1a\u592b\u4eba\u2026\u2026\u201d \u3000\u3000\u7070\u8863\u8001\u5987\u5389\u559d\u9053\uff1a\u201c\u522b\u53eb\u6211\u592b\u4eba\uff0c\u4f60\u8fd9\u4e2a\u79bd\u66fe\u4e0d\u5982\u7684\u4e1c\u897f!\u201d \u3000\u3000\u90a3\u4e00\u76f4\u672a\u5f00\u53e3\u7684\u767d\u53d1\u8001\u4eba\uff0c \u7a81\u7136\u5f00\u4e86\u53e3\uff0c\u9053\uff1a \u201c\u8001\u4f34\uff0c\u54b1\u4eec\u65e2\u7136\u5df2\u7ecf\u5230\u8fd9\u91cc\u4e86\uff0c\u800c\u4e14\uff0c \u4ed6\u4e5f\u6765\u4e86\uff0c\u8fd9\u6b21\u6069\u6028\uff0c \u4e00\u5b9a\u4f5c\u4e2a\u4e86\u65ad\uff0c\u9ec4\u7075\u4e5f\u8bf4\u5f97\u4e0d\u9519\uff0c\u6211\u4eec\u603b\u8981\u628a\u4e8b\u60c5\u5f04\u4e2a\u660e\u767d\u624d\u597d\u3002\u201d \u3000\u3000\u7070\u8863\u8001\u5987\u5e74\u7eaa\u867d\u5927\uff0c\u4f46\u813e\u6c14\u5374\u662f\u706b\u7206\u5f97\u5f88\uff0c\u5389\u58f0\u8bf4\u9053\uff1a \u201c\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\u2026\u2026\u201d \u767d\u9aef\u8001\u4eba\u4e5f\u52a8\u4e86\u706b\uff0c\u5927\u58f0\u63a5\u9053\uff1a \u201c\u5c31\u7b97\u662f\u6cd5\u5b98\u95ee\u6848\u5427?\u4e5f\u5f97\u8ba9\u4eba\u5bb6\u6709\u4e2a\u8bf4\u8bdd\u7684\u673a\u4f1a\uff0c\u54b1\u4eec\u603b\u4e0d\u80fd\u51fa\u624b\u5c31\u6740\u4e86\u4eba\u5bb6\u5427?\u201d \u3000\u3000\u7070\u8863\u8001\u5987\u4eba\u9053\uff1a\u201c\u6211\u770b\u6740\u4e86\u518d\u95ee\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u767d\u9aef\u8001\u4eba\u9053\uff1a \u201c\u54b1\u4eec\u82e6\u82e6\u5bfb\u627e\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u624d\u628a\u9ec4\u7075\u627e\u5230\uff0c \u4e00\u65e6\u628a\u4ed6\u6740\u4e86\uff0c\u53c8\u53bb\u95ee\u8c01?\u201d \u3000\u3000\u7070\u8863\u8001\u5987\u4eba\u5446\u4e86\u2014\u5446\uff0c\u9053\uff1a \u201c\u597d\u5427!\u8981\u95ee\u4f60\u95ee\uff0c \u8001\u8eab\u61d2\u5f97\u548c\u4ed6\u591a\u8d39\u53e3\u820c\u3002\u201d \u767d\u9aef\u8001\u4eba\u51b7\u54fc\u4e00\u58f0\uff0c\u56de\u5934\u5bf9\u9ec4\u7075\u8bf4\u9053\uff1a\u201c\u5c0f\u5973\u4f3c\u662f\u81ea\u7edd\u7684?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6b7b\u4e8e\u4ec0\u4e48\u5175\u5203\u4e4b\u4e0b?\u201d \u3000\u3000\u767d\u9aef\u8001\u4eba\u9053\uff1a \u201c\u5979\u6b7b\u5728\u5929\u7edd\u6307\u4e0b\uff0c\u90a3\u662f\u8001\u592b\u7684\u72ec\u95e8\u6b66\u529f\uff0c\u9664\u4e86\u5c0f\u5973\u4e4b\u5916\uff0c\u522b\u4eba\u4e5f\u4e0d\u4f1a\u8fd9\u79cd\u6b66\u529f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ee4\u7231\u6b7b\u540e\uff0c\u53ef\u7559\u6709\u4ec0\u4e48\u6307\u51fa\u51f6\u624b\u7684\u7ebf\u7d22?\u201d \u3000\u3000\u7070\u8863\u8001\u5987\u4eba\u5df2\u618b\u4e86\u534a\u5929\uff0c\u518d\u4e5f\u618b\u4e0d\u4f4f\u4e86\uff0c\u9053\uff1a\u201c\u51f6\u624b\u5c31\u662f\u4f60\u4f60\u5bf9\u5979\u59cb\u4e71\u7ec8\u5f03\uff0c\u5979\u81ea\u89c9\u65e0\u989c\u89c1\u4eba\uff0c\u6240\u4ee5\u624d\u81ea\u7edd\u800c\u6b7b\u3002\u201d \u3000\u3000\u767d\u9aef\u8001\u4eba\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u7b54\u590d\u5c1a\u5929\u4e49\u4e00\u53e5\u8bdd\uff0c\u4f60\u6709\u6ca1\u6709\u5bf9\u5c0f\u5973\u59cb\u4e71\u7ec8\u5f03\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u6709!\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u5c1a\u592b\u4eba\u63a5\u9053\uff1a \u201c\u597d\u554a!\u8001\u5934\u5b50\uff0c\u4f60\u542c\u542c\uff0c\u4ed6\u5df2\u7ecf\u62db\u8ba4\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\uff0c\u54b1\u4eec\u4e0a\u5427! \u3000\u3000\u6211\u8981\u628a\u4ed6\u7838\u6210\u8089\u6ce5\uff0c\u4ee5\u96ea\u5fc3\u4e2d\u4e4b\u6068\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a \u201c\u8001\u4f34\u513f\uff0c\u54b1\u4eec\u81ea\u7136\u8981\u66ff\u5979\u62a5\u4ec7\uff0c\u4e0d\u8fc7\uff0c\u8001\u592b\u5e0c\u671b\u80fd\u591a\u77e5\u9053\u5979\u4e00\u4e9b\u6b7b\u56e0\u3002\u201d \u5c1a\u592b\u4eba\u9053\uff1a \u201c\u8001\u5934\u5b50\uff0c\u6211\u5df2\u7ecf\u7b49\u4e86\u516d\u5e74\uff0c\u6211\u6068\u4e0d\u5f97\u7acb\u523b\u624b\u5203\u8fd9\u5c0f\u5b50\uff0c\u54ea\u80fd\u542c\u5f97\u8fdb\u4ed6\u5de7\u53e3\u72e1\u8fa9\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5c1a\u592b\u4eba\uff0c\u6211\u53ef\u4ee5\u4e0d\u627f\u8ba4\u548c\u4ee4\u7231\u4e4b\u95f4\uff0c\u6709\u4ec0\u4e48\u60c5\u7231\u7ea0\u7eb7\u2026\u2026\u201d \u5c1a\u592b\u4eba\u6012\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4eec\u5df2\u7ecf\u67e5\u5f97\u5f88\u6e05\u695a\uff0c\u4f60\u627f\u4e0d\u627f\u8ba4\u90fd\u65e0\u5173\u7d27\u8981\u4e86\u3002\u201d \u3000\u3000\u4e00\u626c\u624b\u4e2d\u7684\u9e20\u5934\u6756\uff0c\u5288\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5c1a\u5929\u4e49\u4e00\u4f38\u624b\uff0c\u7adf\u7136\u628a\u9e20\u5934\u6756\u7ed9\u751f\u751f\u6293\u4f4f\uff0c\u9053\uff1a\u201c\u8001\u4f34\uff0c\u9634\u9633\u5821\u7684\u5b9e\u529b\uff0c\u662f\u4f55\u7b49\u5e9e\u5927\uff0c\u5982\u82e5\u9ec4\u7075\u6ca1\u6709\u6539\u8fc7\u5411\u5584\u4e4b\u5fc3\uff0c\u54b1\u4eec\u6c38\u8fdc\u89c1\u4e0d\u7740\u4ed6\uff0c\u66f4\u4e0d\u4f1a\u5355\u4eba\u5339\u9a6c\u5230\u6b64\u8d74\u7ea6\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u6012\u58f0\u559d\u9053\uff1a \u201c\u5c31\u7b97\u4ed6\u6709\u4e86\u6539\u8fc7\u5411\u5584\u4e4b\u5fc3\uff0c\u6211\u4e5f\u4e00\u6837\u8981\u62a5\u5973\u513f\u4e4b\u4ec7\u3002\u201d \u5c1a\u5929\u4e49\u9053\uff1a \u201c\u62a5\u4ec7\u53ef\u4ee5\uff0c\u4f46\u6211\u5fc3\u4e2d\u6709\u5f88\u591a\u7591\u7aa6\uff0c\u5fc5\u987b\u8981\u5f53\u4ed6\u4e4b\u9762\uff0c\u95ee\u4e2a\u6e05\u695a\uff0c\u9700\u77e5\uff0c\u9519\u8fc7\u4eca\u591c\u7684\u673a\u4f1a\uff0c\u8fd9\u4e9b\u7591\u70b9\uff0c\u53ef\u80fd\u6c38\u6210\u7591\u6848\u4e86\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u7684\u6fc0\u52a8\u60c5\u7eea\uff0c\u9010\u6e10\u5e73\u9759\u4e0b\u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u597d!\u4f60\u95ee\u5427!\u4e0d\u8fc7\uff0c\u95ee\u660e\u767d\u4e4b\u540e\uff0c\u4f60\u8981\u4e3a\u5973\u513f\u62a5\u4ec7\uff0c\u4eca\u591c\u5982\u82e5\u8d70\u4e86\u9ec4\u7075\uff0c\u6211\u548c\u4f60\u8981\u95f9\u4e00\u8f88\u5b50\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a \u201c\u5973\u513f\u4e5f\u4e0d\u662f\u4f60\u4e00\u4e2a\u4eba\u7684\uff0c\u96be\u9053\uff0c\u6211\u5fc3\u4e2d\u4e0d\u75db\u82e6\u5417?\u201d \u3000\u3000\u5c1a\u592b\u4eba\u7a81\u7136\u6536\u4e86\u62d0\u6756\uff0c\u6d41\u4e0b\u4e0b\u4e24\u884c\u8001\u6cea\uff0c \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u627f\u8ba4\u5bf9\u5c0f\u5973\u59cb\u4e71\u7ec8\u5f03\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a \u201c\u5c1a\u8001\u524d\u8f88\uff0c\u4ee4\u7231\u662f\u4e00\u4f4d\u5f88\u5f00\u6717\u7684\u4eba\uff0c\u6211\u627f\u8ba4\u59cb\u4e71\u7ec8\u5f03\u662f\u4e8b\u5b9e\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e2a\u7ed3\u679c\uff0c\u662f\u4ee4\u7231\u5148\u884c\u540c\u610f\u7684!\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48?\u5c0f\u5973\u5148\u884c\u540c\u610f\u4f60\u8fd9\u4e48\u505a?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u662f\u7684!\u8001\u524d\u8f88\uff0c\u4ee4\u7231\u660e\u6717\u806a\u6167\uff0c\u5bb9\u8272\u7edd\u4e16\uff0c\u5979\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u6df1\u8fdc\u3001\u660e\u5feb\uff0c\u4eba\u6240\u96be\u53ca\uff0c\u5979\u5145\u6ee1\u81ea\u4fe1\uff0c\u5979\u76f8\u4fe1\u5728\u4e0b\u4e00\u65e6\u60f3\u6210\u5bb6\u7acb\u4e1a\u65f6\uff0c\u5b9a\u7136\u4f1a\u518d\u53bb\u627e\u5979\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u54e6!\u4f60\u52a8\u8fc7\u518d\u53bb\u627e\u5979\u7684\u5ff5\u5934\u6ca1\u6709?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ec4\u7075\u6d41\u6d6a\u6c5f\u6e56\uff0c\u8ba4\u8bc6\u4e86\u4e0d\u5c11\u5357\u5317\u4f73\u4e3d\u2026\u2026\u201d \u3000\u3000\u5c1a\u592b\u4eba\u51b7\u54fc\u4e00\u58f0\uff0c\u63a5\u9053\uff1a \u201c\u4f60\u98ce\u6d41\u6210\u6027\uff0e\u6b66\u6797\u4e2d\u4eba\u4eba\u77e5\u6653\uff0c\u53ef\u662f\u6709\u540d\u7684\u8272\u72fc\u3002\u201d \u5c1a\u5929\u4e49\u9053\uff1a\u201c\u8001\u5a46\u5b50\uff0c\u4f60\u5148\u5c11\u8bf4\u4e24\u53e5\u4e0d\u884c\u4e48\uff0c\u7b49\u6211\u4eec\u8bf4\u5b8c\u4e86\uff0c\u4f60\u518d\u8bf4\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a \u201c\u4f60\u4eec\u8bf4\u5feb\u4e9b\uff0c\u6211\u5fc3\u4e2d\u6012\u6c14\u5982\u6f6e\uff0c \u53ef\u6ca1\u6709\u8010\u5fc3\u7b49\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f46\u80fd\u4f7f\u5728\u4e0b\u5ff5\u5ff5\u96be\u5fd8\u7684\uff0c\u552f\u4ee4\u7231\u4e00\u4eba\u800c\u5df2\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5a36\u5979?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5728\u4e0b\u548c\u4ee4\u7231\u5206\u624b\u4e0d\u8db3\u4e09\u6708\uff0c\u5c31\u4f20\u51fa\u4e86\u4ee4\u7231\u7684\u5669\u8017\uff0c\u5728\u4e0b\u5306\u5306\u8d76\u53bb\u65f6\uff0c\u4ee4\u7231\u5c38\u4f53\u5df2\u88ab\u4e24\u4f4d\u8fd0\u8d70\uff0c\u7adf\u672a\u80fd\u89c1\u5979\u6700\u540e\u4e00\u9762\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u5979\u4eba\u5df2\u6b7b\u4e86\uff0c\u5c31\u7b97\u4f60\u5df2\u7ecf\u6709\u4e86\u5a36\u5979\u4e4b\u5fc3\uff0c\u4e5f\u5df2\u662f\u56de\u5929\u4e4f\u672f\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5c1a\u8001\u524d\u8f88\u5fc3\u4e2d\u6709\u5f88\u591a\u7684\u7591\u7aa6\uff0c\u5728\u4e0b\u5fc3\u4e2d\u4e5f\u6709\u5f88\u591a\u4e0d\u89e3\u4e4b\u5904\uff0c\u53ea\u53ef\u60dc\uff0c\u4ee4\u7231\u6b7b\u4ea1\u73b0\u573a\u5df2\u906d\u7834\u574f\uff0e\u5c38\u4f53\u5c71\u88ab\u8fd0\u8d70\uff0c\u7eb5\u7136\u7559\u4e0b\u4e86\u4ec0\u4e48\u86db\u4e1d\u9a6c\u8ff9\uff0c\u53ea\u6015\u4e5f\u906d\u7834\u574f\u4e86\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u4f60\u5bf9\u5c0f\u5973\u4e4b\u6b7b\uff0c\u4f55\u5904\u5b58\u7591?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u6000\u7591\u5979\u662f\u88ab\u4eba\u6740\u6b7b\u7684\uff0c\u800c\u975e\u81ea\u7edd\u800c\u6b7b\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5c1a\u592b\u4eba\u5374\u51b7\u51b7\u63a5\u9053\uff1a \u201c\u5979\u662f\u88ab\u4eba\u6740\u6b7b\uff0c\u600e\u4f1a\u6b7b\u5728\u5929\u7edd\u6307\u4e0b\u653e\u773c\u5f53\u4eca\u4e4b\u4e16\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u4f1a\u5929\u7edd\u6307\u7684\u795e\u529f?\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a \u201c\u592b\u4eba\uff0c\u5929\u7edd\u6307\u867d\u7136\u662f\u5c1a\u5bb6\u72ec\u95e8\u7edd\u6280\uff0c\u4f46\u6050\u795e\u529f\u65e9\u5df2\u5916\u6cc4\u4e86\u3002\u201d \u5c1a\u592b\u4eba\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u9ec4\u7075\u672c\u662f\u548c\u5c1a\u5929\u4e49\u5bf9\u9762\u800c\u7acb\uff0c\u95fb\u8a00\u7a81\u7136\u8f6c\u8fc7\u8eab\u5b50\uff0c\u53f3\u624b\u98df\u4e2d\u4e8c\u6307\u4e00\u5e76\u70b9\u51fa\u3002 \u3000\u3000\u6307\u529b\u6307\u5411\u4e09\u5c3a\u5916\u4e00\u5757\u575a\u786c\u7684\u9752\u7816\u4e4b\u4e0a\u3002 \u3000\u3000\u9ec4\u7075\u4f0f\u8eab\u6361\u8d77\u5730\u4e0a\u9752\u7816\uff0c\u53cc\u624b\u6367\u4e0a\uff0c\u9053\uff1a \u201c\u5c1a\u8001\u524d\u8f88\u8bf7\u8fc7\u76ee\u3002\u201d \u5c1a\u5929\u4e49\u63a5\u8fc7\u9752\u7816\uff0c\u53ea\u89c1\u8868\u76ae\u4e0a\u5168\u65e0\u635f\u4f24\uff0c\u4f46\u9752\u7816\u5165\u624b\uff0c\u7a0d\u2014\u7528\u529b\uff0c\u7acb\u523b\u5316\u4f5c\u788e\u7c89\uff0c\u6d12\u843d\u4e00\u5730\u3002 \u3000\u3000\u8fd9\u662f\u5929\u7edd\u6307\u529b\u795e\u529f\u3002 \u3000\u3000\u5c1a\u5929\u4e49\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5929\u7edd\u6307\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u6012\u9053\uff1a \u201c\u597d!\u4f60\u8fd9\u5c0f\u5b50\uff0c\u7adf\u7136\u628a\u5c1a\u5bb6\u72ec\u95e8\u7edd\u6280\u4e5f\u9a97\u5230\u624b\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u6211\u6ca1\u6709\u9a97\u8fc7\u5979\uff0c\u6211\u4eec\u4e4b\u95f4\uff0c\u4efb\u4f55\u4e8b\uff0c\u90fd\u8bf4\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4e0d\u9519\uff0c\u5728\u4e0b\u9002\u624d\u65bd\u5c55\u7684\u6b63\u662f\u5929\u7edd\u6307\u6b66\u529f\uff0c\u8fd9\u6b66\u529f\u5e76\u975e\u7531\u4ee4\u7231\u4f20\u6388\u5728\u4e0b\uff0c\u4ee5\u5c1a\u8001\u524d\u8f88\u773c\u529b\uff0c\u4e0d\u96be\u9274\u8bc6\u51fa\u6765\uff0c\u665a\u8f88\u8fd9\u5929\u7edd\u6307\u529f\u6709\u591a\u5c11\u5e74\u7684\u706b\u5019\u4e86\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u81f3\u5c11\u6709\u5341\u5e74\u4ee5\u4e0a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u665a\u8f88\u548c\u4ee4\u7231\u76f8\u8bc6\uff0c\u8fd8\u4e0d\u8db3\u4e03\u8f7d\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a \u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4f60\u83ab\u8981\u770b\u9519\u4e86\uff0c\u5c1a\u5bb6\u7684\u72ec\u95e8\u7edd\u6280\uff0c\u600e\u4f1a\u6d41\u5165\u6c5f\u6e56\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u6c89\u541f\u4e0d\u8bed\uff0c\u4f3c\u662f\u5728\u5c3d\u529b\u56de\u5fc6\u7740\u4ec0\u4e48? \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5728\u4e0b\u80fd\u5f97\u5929\u7edd\u6307\u529b\uff0c\u8db3\u8bc1\u5929\u7edd\u6307\u529b\u7684\u5fc3\u5fc3\u6cd5\u5df2\u4f20\u5165\u4e86\u6c5f\u6e56\uff0c\u6211\u80fd\u7ec3\u6210\uff0c\u522b\u4eba\u4e5f\u80fd\u7ec3\u6210\u3002\u4e5f\u8bb8\u5df2\u6709\u5f88\u591a\u4eba\u7ec3\u6210\u8fd9\u79cd\u6b66\u529f\uff0c\u53ea\u4e0d\u8fc7\uff0c\u8fd8\u672a\u5230\u65bd\u7528\u7684\u65f6\u673a\uff0c\u6240\u4ee5\uff0c\u4e24\u4f4d\u8001\u524d\u8f88\u8fd8\u672a\u53d1\u73b0\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u4f60\u65bd\u5c55\u51fa\u6211\u4eec\u5c1a\u5bb6\u7684\u5929\u7edd\u6307\u529b\uff0c\u7528\u5fc3\u4f55\u5728?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u53ea\u662f\u60f3\u8bc1\u660e\uff0c\u4ee4\u7231\u867d\u6b7b\u4e8e\u5929\u7edd\u6307\u4e0b\uff0c\u4f46\u5374\u672a\u5fc5\u662f\u81ea\u7edd\u800c\u6b7b\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\uff0c\u522b\u4eba\u6740\u4e86\u5979?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5728\u4e0b\u6b63\u662f\u6b64\u610f\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a \u201c\u5982\u82e5\u5c0f\u5973\u662f\u88ab\u4eba\u6240\u8c0b\u6740\uff0c\u4f60\u662f\u5acc\u7591\u6700\u5927\u7684\u4e00\u4e2a\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u5728\u4e0b\u81ea\u7136\u660e\u767d\uff0c\u6211\u5982\u4e0d\u65bd\u5c55\u51fa\u5929\u7edd\u6307\u529b\uff0c\u4e24\u4f4d\u7edd\u5bf9\u60f3\u4e0d\u51fa\u6211\u4e5f\u7ec3\u6210\u8fd9\u79cd\u6b66\u529f\uff0c\u6211\u80af\u65bd\u5c55\u51fa\u6765\uff0c\u53ea\u6c42\u8bc1\u660e\u6211\u4e8e\u5fc3\u65e0\u6127\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u70b9\u70b9\u5934\uff0c \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e24\u4f4d\u5bf9\u4ee4\u7231\u7684\u6027\u683c\uff0c\u5e94\u8be5\u660e\u767d\uff0c\u5979\u662f\u4e2a\u5145\u6ee1\u667a\u6167\u7684\u5c11\u5973\uff0c\u5979\u81ea\u4fe1\u3001\u52c7\u6562\u3001\u4e0d\u754f\u8270\u82e6\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u81ea\u7edd\u4e00\u6b7b\uff0c\u5982\u82e5\u975e\u6b7b\u4e0d\u53ef\uff0c\u4ea6\u5fc5\u6709\u9057\u4e66\u7ed9\u4e24\u4f4d\uff0c\u5979\u5fc3\u4e2d\u5e94\u8be5\u660e\u767d\uff0c\u5979\u7684\u6b7b\u4ea1\uff0c\u5bf9\u4e24\u4f4d\u7684\u6253\u51fb\u6709\u591a\u5927?\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u5509!\u8bf4\u5f97\u4e5f\u662f\uff0c\u5979\u7adf\u7136\u672a\u7559\u4e0b\u7247\u7eb8\u53ea\u5b57\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u6211\u95ee\u4f60\uff0c \u4f60\u5bf9\u5c0f\u5973\u600e\u4f1a\u5982\u6b64\u5173\u5fc3?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\uff0c\u6211\u60f3\u5411\u4e24\u4f4d\u8bf4\u660e\uff0c \u81ea\u4ece\u4ee4\u7231\u6b7b\u53bb\u4e4b\u540e\uff0c\u5728\u4e0b\u4ece\u672a\u518d\u63a5\u8fd1\u8fc7\u4efb\u4f55\u5973\u4eba\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a\u201c\u8fd9\u8bdd\u5f53\u771f?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7687\u5929\u5728\u4e0a\uff0c\u9ec4\u7075\u5982\u6709\u4e0d\u5b9e\u4e4b\u8a00\uff0c\u5929\u8bdb\u5730\u706d\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c \u4f60\u8fd8\u6709\u70b9\u826f\u5fc3\u4e0b\u3002\u201d \u76f4\u5230\u6b64\u523b\uff0c\u4f55\u5bd2\u8863\u7b49\u7ec8\u4e8e\u77e5\u9053\uff0c\u9ec4\u7075\u4e0d\u63a5\u8fd1\u5973\u8272\u7684\u539f\u56e0\u4e86\u5c1a\u5929\u4e49\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u7167\u4f60\u8fd9\u4e2a\u8bf4\u6cd5\uff0c\u5c0f\u5973\u662f\u6b7b\u4e8e\u522b\u4eba\u7684\u6697\u7b97 \u4e4b\u4e0b\u4e86?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5c1a\u8001\u524d\u8f88\uff0c\u5982\u82e5\u5728\u73b0\u573a\u770b\u4e0d\u51fa\u6253\u6597\u7684\u75d5\u8ff9\uff0c\u90a3\u662f\u6b7b\u4e8e\u522b\u4eba\u7684\u6697\u7b97\u4e4b\u4e0b\u4e86\u3002\u201d \u5c1a\u592b\u4eba\u9053\uff1a\u201c\u8001\u5934\u5b50\uff0c\u73b0\u573a\u4e4b\u4e2d\uff0c\u597d\u50cf\u672a\u7559\u4e0b\u6253\u6597\u7684\u75d5\u8ff9\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u559d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u53ef\u77e5\u9053\u51f6\u624b\u662f\u8c01\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7ec3\u4f1a\u4e86\u5929\u7edd\u6307\u7684\u4eba\uff0c\u90fd\u53ef\u80fd\u662f\u51f6\u624b\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a \u201c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u6211\u8001\u5a46\u5b50\u53ea\u77e5\u9053\u4f60\u662f\u5c1a\u5bb6\u4e4b\u5916\u552f\u4e00\u7ec3\u4f1a\u4e86\u5929\u7edd\u6307\u529b\u7684\u4eba\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u4f46\u6211\u7edd\u4e0d\u662f\u51f6\u624b\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u51f6\u624b\uff0c\u8c01\u662f\u51f6\u624b?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5509!\u592b\u4eba\uff0c\u5982\u82e5\u4ee4\u7231\u662f\u88ab\u4eba\u6740\u7684\uff0c\u5f53\u4eca\u6b66\u6797\u4e4b\u4e2d\u4e5f\u4e0d\u8fc7\u53ea\u6709\u4e09\u4e94\u4e2a\u4eba\u800c\u5df2\uff0c\u8fd9\u4e2a\u4eba\uff0c\u5e94\u8be5\u662f\u4e0d\u96be\u67e5\u51fa\u6765\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u60f3\u5230\u4ec0\u4e48\u4eba\u53ef\u80fd\u662f\u51f6\u624b?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5c1a\u8001\u524d\u8f88\uff0c\u8fd9\u5148\u8981\u627e\u51fa\u6765\u4ec0\u4e48\u4eba\u53ef\u80fd\u5df2\u7ecf\u7ec3\u6210\u5929\u7edd\u6307\u529b\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8005\u592b\u5b9e\u5728\u60f3\u4e0d\u51fa\u6765\uff0c\u5982\u82e5\u4e0d\u662f\u4f60\u4eca\u5929\u8868\u6f14\u51fa\u5929\u7edd\u6307\u529b\uff0c\u6211\u6839\u672c\u60f3\u4e0d\u5230\u4f60\u4e5f\u4f1a\u8fd9\u95e8\u6b66\u529f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9aa4\u7136\u95f4\u542c\u8d77\u6765\uff0c\u8fd9\u597d\u50cf\u7f9a\u7f8a\u6302\u89d2\uff0c\u65e0\u8ff9\u53ef\u5bfb\uff0c\u4f46\u5982\u4ed4\u7ec6\u60f3\u4e00\u60f3\uff0c\u5e76\u975e\u662f\u5168\u65e0\u7ebf\u7d22\uff0c\u5929\u7edd\u6307\u786e\u662f\u4f60\u4eec\u5c1a\u5bb6\u72ec\u95e8\u6b66\u529f\uff0c\u5982\u4f55\u6cc4\u6f0f\u4e8e\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u5e94\u8be5\u6709\u70b9\u7709\u76ee\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u597d!\u4f60\u5148\u8bf4\uff0c\u4f60\u5982\u4f55\u5b66\u5230\u5929\u7edd\u6307\u529b?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e70\u7684\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a \u201c\u4e70\u7684?\u6211\u5c1a\u5bb6\u7684\u72ec\u95e8\u6b66\u529f\uff0c\u7adf\u8fd8\u4f1a\u6709\u4eba\u51fa\u5356\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5929\u7edd\u6307\u8981\u4e00\u5343\u4e24\u9ec4\u91d1\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u5446\u4f4f\u4e86\uff0c\u534a\u664c\u4e4b\u540e\uff0c\u624d\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u90a3\u51fa\u5356\u5929\u7edd\u6307\u529f\u7684\u4eba\u662f\u8c01?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u4ed6\u7684\u59d3\u540d\uff0c\u4f46\u6211\u89c1\u8fc7\u90a3\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u4ed6\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u201d\u9ec4\u7075\u9053\uff1a\u201c\u4e00\u4e2a\u6ee1\u5934\u7070\u53d1\u7684\u8001\u4eba\uff0c\u6211\u76f8\u4fe1\uff0c \u4ed6\u7ecf\u8fc7\u6613\u5bb9\uff0c\u4e0d\u8fc7\uff0c\u6709\u4e9b\u5730\u65b9\uff0c\u4e0d\u662f\u6613\u5bb9\u672f\u6240\u53ef\u4ee5\u6539\u53d8\u7684\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u4ec0\u4e48\u5730\u65b9?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u773c\u795e\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u4f60\u80fd\u5f62\u5bb9\u51fa\u6765\u4ed6\u7684\u773c\u795e\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ed6\u7684\u76ee\u5149\u51b7\u5389\uff0c\u800c\u4e14\uff0c\u5de6\u773c\u4f3c\u662f\u53d7\u5230\u8fc7\u4f24\u5bb3\uff0c\u89c6\u529b\u4e0d\u4f73\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4e00\u79cd\u72ec\u95e8\u6b66\u529f\uff0c\u552e\u4ef7\u5343\u4e24\u9ec4\u91d1\uff0c\u867d\u7136\u83b7\u76ca\u5f88\u5927\uff0c\u4f46\u5982\u4e0e\u8fd9\u5929\u7edd\u6307\u7684\u4ef7\u503c\u6bd4\u8f83\u8d77\u6765\uff1b\u5e76\u975e\u5f88\u9ad8\uff0c\u8fd9\u4e2a\u4eba\u51fa\u5356\u6b66\u529f\u7684\u7528\u5fc3\uff0c\u9664\u4e86\u83b7\u5229\u4e4b\u5916\uff0e\u8fd8\u6709\u522b\u7684\u7528\u5fc3\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u7528\u5fc3?\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a \u201c\u592b\u4eba\uff0c\u8fd9\u7528\u5fc3\u8fd8\u4e0d\u662f\u5f88\u660e\u663e\u5417?\u4ed6\u8981\u4f7f\u5f97\u5c1a\u5bb6\u7684\u72ec\u95e8\u6b66\u529f\uff0c\u5728\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u5e7f\u4e3a\u6d41\u4f20\uff0c\u4e0d\u7ba1\u5929\u7edd\u6307\u7684\u5a01\u529b\u6709\u597d\u5927\uff0c\u4f46\u5982\u662f\u4f1a\u7684\u4eba\u592a\u591a\u4e86\uff0c\u8fd9\u79cd\u6b66\u529f\uff0c\u5c31\u6ca1\u6709\u4ef7\u503c\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u8001\u524d\u8f88\uff0c \u4f60\u53ef\u4ee5\u4ed4\u7ec6\u7684\u60f3\u4e00\u60f3\uff0c \u53ea\u8981\u627e\u51fa\u90a3\u4e2a\u4eba\u662f\u4ec0\u4e48\u4eba\uff0c\u5c31\u4e0d\u96be\u67e5\u51fa\u5185\u60c5\u4e86\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a\u201c\u8001\u5934\u5b50\uff0c\u60f3\u51fa\u6765\u6ca1\u6709\uff0c\u90a3\u4e2a\u4eba\u662f\u8c01?\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a \u201c\u6211\u6b63\u5728\u60f3\uff0c \u5c1a\u5bb6\u7684\u5929\u7edd\u6307\uff0c\u65e2\u7136\u964d\u5165\u4e86\u6c5f\u6e56\uff0c\u4e3a\u4ec0\u4e48\u8fd9\u79cd\u6b66\u529f\uff0c\u8fd8\u6ca1\u6709\u5728\u6c5f\u6e56\u4e0a\u4f20\u51fa\u6765\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u5728\u4e0b\u5012\u6709\u2014\u70b9\u770b\u6cd5\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u4ec0\u4e48\u770b\u6cd5?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5929\u7edd\u6307\u529b\uff0c\u867d\u7136\u662f\u53ef\u4ee5\u4ef7\u8d2d\uff0c\u4f46\u80fd\u591f\u7ec3\u6210\u7684\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u592a\u591a\u3002\u201d \u5c1a\u5929\u4e49\u9053\uff1a \u201c\u4f60\u7684\u610f\u601d\u662f\u8bf4\uff0c\u8fd9\u4e9b\u4eba\u90fd\u8fd8\u5728\u7ec3\u4e60\u8fd9\u79cd\u6b66\u529f\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5728\u4e0b\u5341\u5e74\u524d\uff0c\u7528\u5343\u4e24\u9ec4\u91d1\uff0c\u4e70\u5230\u4e86\u5929\u7edd\u6307\u6b66\u529f\u4f46\u6211\u7b2c\u4e8c\u5e74\u518d\u53bb\u65f6\uff0c\u90a3\u91cc\u5df2\u7ecf\u4eba\u4e8b\u5168\u975e\u4e86\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u600e\u4e48\u6837\u4e86?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5c4b\u5df2\u7834\u6bc1\uff0c\u4eba\u4e5f\u4e0d\u89c1\uff0c\u5982\u662f\u5728\u4e0b\u7684\u63a8\u65ad\u4e0d\u9519\uff0c\u90a3\u51fa\u5356\u5c1a\u5bb6\u5929\u7edd\u6307\u529b\u7684\u4eba\uff0c\u5df2\u7ecf\u5c4b\u711a\u4eba\u4ea1\u4e86\uff0c\u6240\u4ee5\uff0c\u5929\u7edd\u6307\u867d\u7136\u6d41\u5165\u6c5f\u6e56\uff0c\u4f46\u5b66\u5f97\u8fd9\u79cd\u6b66\u529f\u7684\u4eba\uff0c\u4e0d\u4f1a\u592a\u591a\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a\u201c\u5728\u4f60\u4e4b\u524d\uff0c\u6709\u591a\u5c11\u5165\u5b66\u4f1a\u4e86\u8fd9\u79cd\u6b66\u529f\uff0c\u4f60\u53c8\u600e\u77e5\u6653?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5728\u4e0b\u5c31\u4e0d\u592a\u6e05\u695a\u4e86\uff0c\u8981\u5c1a\u8001\u524d\u8f88\u591a\u591a\u8d39\u4e9b\u5fc3\u601d\u4e86\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u5982\u82e5\u9ec4\u7075\u6ca1\u6709\u8bf4\u9519\uff0c\u4e00\u5b9a\u662f\u4ed6\u4e86!\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a\u201c\u8c01?\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u4ee4\u5f1f\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u5782\u9996\u4e0d\u8a00\u3002 \u3000\u3000\u663e\u7136\uff0c\u9ec4\u7075\u8bf4\u51fa\u90a3\u4eba\u7684\u7279\u5f81\u4e4b\u540e\uff0c\u4ed6\u5df2\u7ecf\u6709\u4e9b\u660e\u767d\u4e86\uff0c\u53ea\u4e0d\u8fc7\uff0c\u6ca1\u6709\u8bf4\u51fa\u662f\u8c01\u4e86\u3002 \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a \u201c\u5343\u4e24\u9ec4\u91d1\u867d\u7136\u4e0d\u5c11\uff0c\u4f46\u4f1a\u51fa\u5356\u4e00\u79cd\u72ec\u95e8\u6b66\u6797\u7edd\u6280\u7684\uff0c\u4ee4\u5f1f\u662f\u552f\u4e00\u53ef\u80fd\u7684\u4eba\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u4f4e\u58f0\u9053\uff1a \u201c\u4ed6\u6b66\u529f\u57fa\u7840\u4e0d\u56fa\uff0c\u5c31\u7b97\u5f97\u5230\u4e86\u5929\u7edd\u6307\u7684\u771f\u8bc0\uff0c\u4e5f\u65e0\u6cd5\u7ec3\u6210\u8fd9\u79cd\u7edd\u6280\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a \u201c\u8fd9\u4e5f\u662f\u4ed6\u51fa\u5356\u7684\u91cd\u8981\u539f\u56e0\u4e4b\u4e00\uff0c\u4ed6\u751f\u6027\u8d2a\u9119\uff0c\u81ea\u5df1\u65e2\u7136\u65e0\u6cd5\u7ec3\u6210\uff0c\u62ff\u6765\u6362\u6210\u91d1\u94f6\u4f7f\u7528\uff0c\u6b63\u662f\u4ed6\u7684\u4e3a\u4eba\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a\u201c\u54b1\u4eec\u90fd\u6ca1\u6709\u8bc0\u7a8d\u7ed9\u4ed6\u5b66\uff0c\u53c8\u600e\u4e48\u4f1a\u5462?\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a \u201c\u54fc!\u9ec4\u7075\u8bf4\u7684\u4e0d\u9519\uff0c\u6211\u60f3\u4f60\u5fc3\u4e2d\u4e5f\u8be5\u660e\u767d\u4e86\uff0c\u5f53\u5e74\u5c31\u56e0\u4e3a\u4ed6\u5077\u770b\u6211\u4e60\u7ec3\u5929\u7edd\u6307\u529b\uff0c\u88ab\u6211\u4f24\u4e86\u4e00\u53ea\u5de6\u773c\uff0c\u9003\u51fa\u5c1a\u5bb6\uff0c\u4e09\u5e74\u540e\uff0c\u4ed6\u53c8\u56de\u6765\uff0c\u82e6\u6c42\u4f60\u6536\u7559\u4e8e\u4ed6\uff0c\u53c8\u5728\u54b1\u4eec\u5bb6\u4e2d\u4f4f\u4e86\u4e09\u5e74\u2026\u2026\u201d \u3000\u3000\u5c1a\u592b\u4eba\u63a5\u9053\uff1a \u201c\u90a3\u4e09\u5e74\u4ed6\u8868\u73b0\u5f97\u5f88\u597d\u554a! \u4e00\u76f4\u5faa\u89c4\u8e48\u77e9\uff0c\u800c\u4e14\u5e38\u5e26\u7740\u4e91\u513f\u53bb\u73a9\u554a!\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u4ece\u90a3\u65f6\u5f00\u59cb\uff0c\u4ed6\u5df2\u7ecf\u5b58\u5fc3\u5077\u5b66\u5929\u7edd\u6307\u4e0b\uff0c\u90a3\u65f6\uff0c\u4e91\u513f\u6b63\u5728\u7ec3\u8fd9\u79cd\u6b66\u529f\uff0c\u800c\u4e14\u5979\u53ea\u6709\u516b\u5c81\uff0e\u4ed6\u5e38\u5e26\u4e91\u513f\uff0c \u81ea\u7136\u662f\u66f4\u5bb9\u6613\u4e86\u89e3\u5fc3\u8bc0\uff0c\u4ed6\u7a81\u7136\u4e0d\u8f9e\u800c\u522b\uff0c\u90a3\u53c8\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a\u201c\u6ca1\u6709\u8bc1\u636e\uff0c\u54b1\u4eec\u4e5f\u4e0d\u80fd\u8d56\u5728\u4ed6\u7684\u8eab\u4e0a\u554a\uff01\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u8bc1\u636e\uff0c\u8fd9\u8fd8\u8981\u4ec0\u4e48\u8bc1\u636e\uff0c\u5f62\u8c8c\u6837\u5b50\u50cf\u4ed6\uff0c\u800c\u4e14\uff0c\u4ed6\u4e00\u8d70\u5341\u4f59\u5e74\uff0c\u6773\u65e0\u6d88\u606f\uff0c\u4e0d\u662f\u4ed6\u3002\u53ea\u6709\u4f60\u6211\u548c\u6b7b\u53bb\u7684\u5973\u513f\u6709\u5acc\u7591\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u7531\u4e91\u513f\u7684\u5e74\u9f84\u6697\u4e2d\u63a8\u7b97\uff0c\u65f6\u95f4\u5341\u5206\u63a5\u8fd1\u3002 \u3000\u3000\u4f46\u4ed6\u6ca1\u6709\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u53ea\u542c\u5c1a\u592b\u4eba\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u8fd9\u4e9b\u5e74\u6765\uff0e\u54b1\u4eec\u5f88\u5c11\u5728\u5bb6\uff0c\u4e5f\u8bb8\u4ed6\u56de\u53bb\u8fc7\uff0c\u54b1\u4eec\u4e0d\u5728\uff0c\u4e0d\u7ba1\u5982\u6cb3\uff0c \u53ea\u8981\u4ed6\u4e0d\u6b7b\uff0c\u6211\u4e00\u5b9a\u8981\u627e\u5230\u4ed6\uff0c\u95ee\u4e2a\u6e05\u695a\u660e\u767d\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a \u201c\u6211\u770b\u4f60\u662f\u627e\u4e0d\u5230\u4ed6\u4e0b\u3002\u4ed6\u4e5f\u8bb8\u5fc3\u4e2d\u53ea\u6709\u9ec4\u91d1\u3001\u767d\u94f6\uff0c\u4f46\u522b\u7684\u4eba\u60f3\u6cd5\u4e0d\u540c\u3001\u4ed6\u51fa\u4e86\u5343\u4e24\u9ec4\u91d1\uff0c\u5b66\u5f97\u5929\u7edd\u6307\u7684\u5fc3\u8bc0\uff0c\u81ea\u7136\u4e0d\u5e0c\u671b\u5b83\u5728\u6c5f\u6e56\u5e7f\u4e3a\u6d41\u4f20\uff0c\u5339\u592b\u65e0\u7f6a\uff0c\u6000\u58c1\u5176\u7f6a\uff0c\u4eba\u5bb6\u6740\u4e86\u4ed6\uff0c\u53c8\u53d6\u56de\u5343\u4e24\u9ec4\u91d1\uff0c\u4f55\u4e50\u4e0d\u4e3a\uff0c\u5176\u5b9e\uff0c \u4e00\u4e2a\u4eba\uff0c\u6709\u5343\u4e24\u9ec4\u91d1\uff0c\u8fd9\u4e00\u751f\u5df2\u5403\u559d\u4e0d\u5c3d\u4e86\uff0c\u4ed6\u5982\u80fd\u89c1\u597d\u5c31\u6536\uff0c \u4e5f\u4e0d\u4f1a\u843d\u5f97\u88ab\u6740\u7684\u4e0b\u573a\u4e86\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u76ee\u5149\u4e00\u63a0\u9ec4\u7075\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u8001\u5934\u5b50\uff0c\u7109\u77e5\u4ed6\u4e0d\u662f\u51f6\u624b!\u59d3\u9ec4\u7684\uff0c \u4f60\u4e0d\u4f46\u6740\u4e86\u6211\u7684\u5973\u513f\uff0c\u4e5f\u6740\u4e86\u6211\u7684\u5144\u5f1f\u2026\u2026\u201d \u3000\u3000\u5c1a\u5929\u4e49\u51b7\u51b7\u63a5\u9053\uff1a \u201c\u4f60\u80af\u5b9a\uff0c\u90a3\u662f\u4f60\u5144\u5f1f\u51fa\u5356\u54b1\u4eec\u5c1a\u5bb6\u7684\u5929\u7edd\u6307?\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a\u201c\u7b97\u65f6\u95f4\uff0c\u542c\u6a21\u6837\uff0c\u4e0d\u662f\u4ed6\uff0c\u8fd8\u6709\u8c01\u5462?\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u80fd\u80af\u5b9a\u9ec4\u7075\u662f\u51f6\u624b\u4e86\uff01\u201d \u3000\u3000\u5c1a\u592b\u4eba\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u5c31\u662f\u4ed6\u4e0d\u662f\u6740\u6b7b\u5973\u513f\u7684\u51f6\u624b\uff0c\u4f46\u5973\u513f\u56e0\u4ed6\u800c\u6b7b\uff0c\u90a3\u662f\u7edd\u4e0d\u4f1a\u9519\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u4e91\u513f\u6b7b\u5728\u5929\u7edd\u6307\u4e0b\uff0c\u5979\u53ef\u80fd\u81ea\u7edd\uff0c\u4e5f\u53ef\u80fd\u6b7b\uff1b\u9ec4\u7075\u624b\u4e2d\u2026\u2026\u201d \u3000\u3000\u5c1a\u592b\u4eba\u63a5\u9053\uff1a\u201c\u5bf9\u554a!\u90a3\u54b1\u4eec\u5c31\u8be5\u66ff\u5973\u513f\u62a5\u4ec7\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u5982\u82e5\u5979\u662f\u6b7b\u5728\u522b\u4eba\u624b\u4e2d\u5462?\u5929\u7edd\u6307\u5df2\u6d41\u5165\u6c5f\u6e56\u9664\u4e86\u9ec4\u7075\u4e4b\u5916\uff0c\u81f3\u5c11\u8fd8\u6709\u4e00\u4e2a\u4eba\u5b66\u4f1a\u4e86\u8fd9\u95e8\u6b66\u529f\uff0c\u4f55\u51b5\uff0c\u4e5f\u53ef\u80fd\u6709\u4e8c\u4e2a\u3001\u4e94\u4e2a\uff0c\u4ed6\u4eec\u90fd\u6709\u53ef\u80fd\u3002\u201d \u3000\u3000\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a \u201c\u592b\u4eba\uff0c\u5bf9\u4ee4\u5f1f\uff0c\u6211\u4eec\u6709\u4e0d\u5c11\u6b21\u4e89\u6267\uff0c\u4f60\u603b\u662f\u767e\u822c\u7684\u53bb\u8892\u62a4\u4ed6\uff0c\u73b0\u5728\uff0c\u4f60\u77e5\u9053\u4e86\uff0c\u4ed6\u5077\u4e86\u5c1a\u5bb6\u5929\u7edd\u6307\u4e0d\u8bf4\uff0c\u8fd8\u5bb3\u4e86\u81ea\u5df1\u7684\u5916\u7525\u5973\uff0c\u4f60\u552f\u4e00\u7684\u5973\u513f\u2026\u2026\u201d \u3000\u3000\u5c1a\u5929\u4e49\u76ee\u5149\u8f6c\u6ce8\u5230\u9ec4\u7075\u8eab\u4e0a\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u59d1\u4e0d\u8bba\u6740\u6b7b\u5c0f\u5973\u7684\u662f\u4ec0\u4e48\u4eba\uff0c\u4f46\u4f60\u9ec4\u7075\u603b\u8981\u8d1f\u4e00\u4e9b\u8d23\u4efb\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u662f!\u5728\u4e0b\u4e00\u76f4\u6ca1\u6709\u63a8\u5378\u8d23\u4efb\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a\u201c\u90a3\u4f60\u51c6\u5907\u5982\u4f55\u5411\u6211\u4eec\u4ea4\u4ee3?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5728\u4e0b\u6ca1\u6709\u4ec0\u4e48\u51c6\u5907\uff0c\u8fd9\u8981\u770b\u4e24\u4f4d\u5982\u4f55\u5429\u5490\u5728\u4e0b\u4e86\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a \u201c\u5f88\u7b80\u5355\uff0c\u4f60\u53ea\u8981\u5e2e\u6211\u4eec\u67e5\u51fa\u6740\u5bb3\u5c0f\u5973\u7684\u51f6\u624b\u90a3\u5c31\u5f88\u81ea\u7136\u6d17\u96ea\u6389\u4f60\u7684\u5acc\u7591\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5728\u4e0b\u53ef\u4ee5\u5e2e\u4e24\u4f4d\u67e5\uff0c\u4f46\u8fd8\u8981\u4e24\u4f4d\u5408\u4f5c\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1b \u201c\u6211\u4eec\u5df2\u7ecf\u5f88\u5408\u4f5c\u4e86\uff0c\u4f46\u4e0d\u77e5\u8fd8\u8981\u6211\u4eec\u5982\u4f55\u4e00\u4e2a\u5408\u4f5c\u6cd5?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8001\u524d\u8f88\u662f\u4e0d\u662f\u5df2\u786e\u5b9a\u4e86\u90a3\u4e2a\u51fa\u5356\u706d\u7edd\u6307\u7684\u4eba?\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u786e\u5b9a\u4e86\uff0c\u5c31\u662f\u62d9\u8346\u4e4b\u5f1f\u7ae5\u5668\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8001\u524d\u8f88\u80fd\u591f\u4ed4\u7ec6\u7684\u7b97\u51fa\u4ed6\u79bb\u5f00\u5e9c\u4e0a\u7684\u65e5\u671f\u5417?\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u6211\u8bb0\u5f97\u90a3\u65f6\u5c0f\u5973\u53ea\u6709\u5341\u4e00\u5c81\uff0c\u7b97\u6765\uff0c\u5e94\u6709\u5341\u4e94\u5e74\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u6211\u5b66\u5f97\u5929\u7edd\u6307\u5df2\u6709\u5341\u4e09\u5e74\uff0c\u9664\u4e86\u4ed6\u51c6\u5907\u7684\u65f6\u95f4\uff0c\u4e2d\u95f4\u53ea\u6709\u4e00\u5e74\u76f8\u5dee\uff0c\u8fd9\u65f6\u95f4\uff0c\u4ed6\u5e94\u8be5\u4e0d\u4f1a\u4f20\u6388\u592a\u591a\u4eba\uff0c\u53ef\u80fd\u53ea\u6709\u4e24\u4e09\u4e2a\u4eba\uff0c\u56e0\u4e3a\uff0c\u4e00\u4e2a\u4eba\u5b66\u4e60\u5929\u7edd\u6307\uff0c\u6700\u5feb\u4e5f\u8981\u4e03\u4e03\u56db\u5341\u4e5d\u5929\uff0c\u624d\u80fd\u5960\u5b9a\u597d\u57fa\u7840\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u56db\u5341\u4e5d\u5929\uff0c\u90a3\u4e2a\u4eba\u4e00\u5b9a\u662f\u4e2a\u5929\u624d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u533a\u533a\u5c31\u662f\u82b1\u4e86\u56db\u5341\u4e5d\u5929\uff0c\u5b66\u4f1a\u4e86\u5929\u7edd\u6307\u7684\u4eba\uff0c\u6b66\u6797\u4e2d\uff0c\u50cf\u6211\u9ec4\u67d0\u4eba\u8fd9\u70b9\u624d\u80fd\u7684\u4eba\uff0c\u5927\u6982\u4e0d\u4f1a\u5f88\u5c11\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u8001\u592b\u89c9\u7740\uff0c\u5b66\u4f1a\u5929\u7edd\u6307\u6cd5\u5fc3\u8bc0\uff0c\u800c\u53c8\u80fd\u7ec3\u4e60\u7eaf\u719f\u5b83\u7684\u7a8d\u8981\u3001\u65b9\u6cd5\uff0c\u4e0d\u8bba\u90a3\u4e2a\u4eba\u5982\u4f55\u806a\u660e\uff0c\u81f3\u5c11\u4e5f\u8981\u767e\u65e5\u65f6\u95f4\uff0c\u624d\u80fd\u5960\u57fa\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f20\u6388\u65f6\u95f4\u6108\u957f\uff0c\u5b66\u5f97\u8fd9\u79cd\u7edd\u6280\u7684\u4eba\uff0c \u81ea\u7136\u662f\u6108\u5c11\uff0c\u8fd9\u79cd\u7edd\u4e16\u5947\u529f\uff0c \u81ea\u7136\u662f\u4e00\u6b21\u4f20\u6388\u4e00\u4eba\uff0c\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u8fd9\u4e48\u7b97\u6765\uff0c\u4ed6\u51fa\u5356\u5929\u7edd\u6307\u7684\u65f6\u95f4\uff0c \u53ea\u6709\u4e00\u5e74\u534a\uff0c\u9664\u975e\u4ed6\u540c\u65f6\u4f20\u6388\u6570\u4eba\u4e4b\u5916\uff0c\u6700\u591a\u4e5f\u53ea\u80fd\u4f20\u7ed9\u4e09\u4e2a\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5982\u82e5\u53ea\u4f20\u6388\u4e09\u4e2a\u4eba\uff0c\u90a3\u5c31\u5bb9\u6613\u627e\u51fa\u4ed6\u4eec\u7684\u8eab\u4efd\u4e86\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u5c31\u7b97\u5b66\u5f97\u6b64\u6280\u7684\u4eba\u624b\u4e0d\u591a\uff0c\u4f46\u4f20\u6388\u4e00\u76f4\u5728\u9690\u5bc6\u4e2d\u8fdb\u884c\uff0c\u9664\u975e\u4ed6\u65bd\u5c55\u51fa\u6765\uff0c\u6211\u4eec\u5f88\u96be\u53d1\u89c9\u4ec0\u4e48\u4eba\u4f1a\u6b64\u6b66\u529f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u86db\u4e1d\u9a6c\u8ff9\uff0c\u5e76\u975e\u662f\u5168\u65e0\u53ef\u5bfb\uff0c\u53ea\u8981\u7559\u5fc3\u4e00\u4e9b\uff0c\u6211\u60f3\u4e5f\u8bb8\u4f1a\u67e5\u51fa\u7709\u76ee\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u80fd\u5982\u7ea6\u524d\u6765\uff0c\u8db3\u89c1\u5fc3\u80f8\u78ca\u843d\uff0c\u51ed\u6b64\u4e00\u70b9\uff0c\u4e5f\u8bb8\u771f\u4e0d\u662f\u6740\u5bb3\u5c0f\u5973\u7684\u51f6\u624b\u3002\u201d \u3000\u3000\u9ec4\u7075\u82e6\u7b11\u4e0d\u8a00\u3002 \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u4f46\u653e\u773c\u5929\u4e0b\u6b66\u6797\uff0c\u4f60\u8fd8\u662f\u5acc\u7591\u6700\u91cd\u7684\u4e00\u4e2a\uff0c\u5982\u82e5\u627e\u4e0d\u5230\u522b\u7684\u51f6\u5acc\uff0c\u5c0f\u5973\u4e4b\u6b7b\uff0c\u4f60\u4ecd\u975e\u507f\u547d\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fc3\u4e2d\u6697\u9053\uff1a \u201c\u8fd9\u4f4d\u5c1a\u8001\u513f\uff0c\u4e5f\u662f\u4e0d\u8bb2\u7406\u5f97\u5f88\uff0c\u65e2\u7136\u77e5\u9053\u4e86\u9ec4\u5821\u4e3b\u4e0d\u662f\u51f6\u624b\uff0c\u600e\u4e48\u53ef\u4ee5\u628a\u6740\u4eba\u5927\u7f6a\u8d56\u5728\u9ec4\u5821\u4e3b\u7684\u8eab\u4e0a\u3002\u201d \u3000\u3000\u53ea\u542c\u9ec4\u7075\u8bf4\u9053\uff1a \u201c\u597d\u5427!\u5c1a\u524d\u8f88\u5982\u82e5\u4fe1\u4efb\u5728\u4e0b\uff0c\u7ed9\u6211\u4e00\u5e74\u65f6\u95f4\uff0c\u5b9a\u5f53\u67e5\u51fa\u6740\u6b7b\u4ee4\u7231\u7684\u51f6\u624b\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u5982\u82e5\u4e00\u5e74\u4e4b\u5185\uff0c\u4f60\u67e5\u4e0d\u51fa\u6740\u4eba\u7684\u51f6\u624b\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5982\u662f\u67e5\u4e0d\u51fa\u6765\uff0c\u5728\u4e0b\u613f\u4e3a\u4ee4\u7231\u507f\u547d\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u597d\uff01\u4e00\u5e74\u4e4b\u540e\uff0c\u54b1\u4eec\u5728\u54ea\u91cc\u89c1\u9762\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ecd\u5728\u6b64\u5730\u5982\u4f55\uff1f\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u597d\uff01\u660e\u5e74\u6b64\u65e5\uff0c\u8001\u592b\u5728\u6b64\u606d\u5019\uff0c\u4f60\u5982\u4e0d\u6765\uff0c\u5230\u5c31\u8bc1\u660e\u4f60\u662f\u51f6\u624b\u4e86\uff0c\u8001\u4f34\u513f\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u8f6c\u8eab\u75be\u8dc3\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u5c1a\u5929\u4e49\u592b\u5987\u7684\u80cc\u5f71\u9010\u6e10\u8fdc\u53bb\u3002\u9ec4\u7075\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u8bf7\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\u3002\u539f\u6765\uff0c\u4ed6\u65e9\u5df2\u77e5\u9053\u6211\u4eec\u6765\u4e86\u3002 \u3000\u3000\u6b63\u5f85\u7ad9\u8d77\u8eab\u5b50\u3001\u7a81\u95fb\u8106\u5982\u94f6\u94c3\uff0c\u800c\u53c8\u51b0\u51b7\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u5c1a\u5929\u4e49\u4e3a\u4ec0\u4e48\u4e0d\u6740\u4e86\u4f60\uff01\u201d \u3000\u3000\u968f\u7740\u90a3\u8bf4\u8bdd\u4e4b\u58f0\uff0c\u4e00\u95f4\u6b8b\u7834\u7684\u7a7a\u623f\u4e4b\u4e2d\uff0c\u7f13\u6b65\u884c\u51fa\u6765\u4e2a\u98ce\u59ff\u7ef0\u7ea6\u7684\u4e3d\u4eba\u3002 \u3000\u3000\u8fd9\u65f6\u4e91\u7834\u6708\u73b0\uff0c\u6e05\u8f89\u7167\u4eba\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u90a3\u4e3d\u4eba\u8eab\u7740\u7384\u88c5\uff0c\u4e24\u4e2a\u9752\u8863\u52b2\u670d\u7684\u5973\u5a62\uff0c\u7d27\u968f\u5728\u90a3\u7384\u8863\u4e3d\u4eba\u8eab\u540e\u800c\u6765\u3002 \u3000\u3000\u4e24\u4e2a\u5973\u5a62\uff0c\u4e00\u4e2a\u6267\u5251\uff0c\u4e00\u4e2a\u62ff\u7740\u91c7\u76d8\u3002 \u3000\u3000\u76d8\u4e0a\u653e\u7740\u4e00\u4e9b\u94f6\u5149\u95ea\u95ea\u4e4b\u7269\uff0c\u53ea\u53ef\u60dc\u8ddd\u79bb\u592a\u8fdc\uff0c\u77a7\u4e0d\u6e05\u695a\u662f\u4ec0\u4e48\u4e1c\u897f\u3002 \u3000\u3000\u7384\u8863\u4e3d\u4eba\u7f13\u6b65\u884c\u8fd1\u4e86\u51c9\u4ead\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\u522b\u6765\u65e0\u6059\u3002\u201d \u3000\u3000\u7384\u5a18\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u6b7b\u91cc\u9003\u751f\uff0c\u603b\u7b97\u6d3b\u5f97\u4e0b\u6765\u4e86\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1b\u201c\u5c1a\u4e91\u662f\u4e0d\u662f\u4f60\u5bb3\u7684\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4e5f\u8bb8\u6211\u53ea\u662f\u4f24\u5230\u4e86\u5979\u7684\u5fc3\u2026\u2026\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4f24\u4e86\u5979\u7684\u5fc3 \u96be\u9053\u8fd8\u4e0d\u591f\u5417\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u4e00\u76f4\u6ca1\u6709\u63a8\u5378\u8d23\u4efb\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u5509\uff01\u9ec4\u7075\uff0c\u544a\u8bc9\u6211\uff0c\u4f60\u7a76\u7adf\u4f24\u8fc7\u591a\u5c11\u4e2a\u5973\u4eba\u7684\u5fc3\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5f88\u591a\uff0c\u6240\u4ee5\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u4e00\u76f4\u8b66\u60d5\u81ea\u5df1\uff0c\u4e0d\u80fd\u518d\u6709\u903e\u8d8a\u2026\u2026\u201d \u3000\u3000\u7384\u5a18\u63a5\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u6740\u4e86\u4e00\u4e2a\u4eba\uff0c\u4e0d\u662f\u5ff5\u51e0\u53e5\u4f5b\u53f7\u5c31\u53ef\u4ee5\u8d85\u5ea6\u7684\uff0c\u522b\u7684\u6211\u4e0d\u7ba1\uff0c\u6211\u4e5f\u61d2\u5f97\u7ba1\uff0c \u4f60\u51c6\u5907\u5bf9\u6211\u600e\u4e48\u4ea4\u4ee3?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\u51c6\u5907\u8981\u5728\u4e0b\u5982\u4f55\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u6b20\u503a\u8fd8\u94b1\uff0c\u6211\u6ca1\u6709\u6b7b\uff0c\u6240\u4ee5\uff0c \u7528\u4e0d\u7740\u4f60\u507f\u547d\uff0c\u4f46\u6211\u8981\u4e00\u6761\u624b\u81c2\uff0c\u4e0d\u7b97\u592a\u8fc7\u5206\u5427\uff1f\u201d \u3000\u3000\u9ec4\u7075\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4f60\u8981\u53f3\u81c2\u3002\u8fd8\u662f\u5de6\u81c2\uff1f\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u53f3\u81c2\uff0c \u7531\u8098\u95f4\u5207\u65ad\uff0c\u6211\u5e26\u4f60\u4e00\u6761\u624b\u81c2\u56de\u53bb\uff0c\u98ce\u5e72\u4e86\uff0c\u597d\u597d\u7684\u4f9b\u8d77\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\uff0c\u6211\u53ef\u4ee5\u7b54\u5e94\u4f60\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u7384\u5a18\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7ed9\u6211\u4e00\u70b9\u65f6\u95f4\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u65f6\u95f4\uff0c\u591a\u5c11\u65f6\u95f4?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e09\u5e74\u3002\u201d \u3000\u3000\u7384\u5a18\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u592a\u4e45\u4e86\uff0c\u6211\u4e0d\u8981\u653e\u8fd9\u6837\u957f\u65f6\u95f4\u7684\u5e10\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\u7684\u610f\u601d\u5462\uff0c\u7ed9\u6211\u591a\u5c11\u65f6\u95f4\uff1f\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6700\u597d\u662f\uff0c\u6211\u73b0\u5728\u5c31\u6536\u56de\u6765\uff0c\u5982\u5ff5\u4ea4\u60c5\uff0c\u6211\u53ea\u80fd\u7ed9\u4f60\u4e09\u4e2a\u6708\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u7384\u5a18\uff0c\u4e09\u4e2a\u6708\uff0c\u662f\u4e0d\u662f\u592a\u77ed\u4e86\u4e00\u4e9b\uff0c \u4f60\u77e5\u9053\uff0c\u6c5f\u6e56\u7684\u60c5\u52bf\uff0c\u76ee\u4e0b\u5341\u5206\u7d27\u5f20\u3002\u201d \u3000\u3000\u7384\u5a18\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u4f60\u4ee5\u4e00\u8eab\u7cbe\u6e5b\u7684\u6b66\u6280\u548c\u6df1\u539a\u7684\u5185\u529f\uff0c\u6709\u4e0a\u4e24\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u4f60\u53c8\u53ef\u4ee5\u590d\u5143\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\uff0c\u8fd9\u60e9\u7f5a\uff0c\u5bf9\u6211\u5b9e\u5728\u662f\u592a\u8f7b\u4e86\uff0c\u6211\u5f88\u611f\u6fc0\u4f60\u7684\u5bbd\u5927\uff0c\u4e0d\u8fc7\uff0c\u6211\u5b9e\u5728\u6709\u592a\u591a\u7684\u91cd\u8981\u4e8b\u6ca1\u6709\u529e\u5b8c\uff0c\u6c42\u6c42\u4f60\uff0c\u7384\u5a18\uff0c\u7ed9\u6211\u4e09\u5e74\u5427!\u4e8b\u5b9e\u4e0a\uff0c\u8fd9\u4e09\u5e74\uff0c\u8fd8\u4e0d\u591f\u6211\u7528\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u7b54\u5e94\uff0c\u9ec4\u7075\uff0c\u4f60\u8fd8\u6709\u4e00\u4e2a\u529e\u6cd5\uff0c\u90a3\u5c31\u662f\u51fa\u624b\u6297\u62d2\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u6297\u62d2\u7684\uff0c\u5c31\u7b97\u4f60\u771f\u7684\u8981\u6740\u4e86\u6211\uff0c\u6211\u4e5f\u4e0d\u4f1a\u6297\u62d2\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u90a3\u5f88\u597d!\u4f60\u65e2\u7136\u51b3\u5fc3\u4e0d\u6297\u62d2\u4e86\uff0c\u90a3\u5c31\u4f38\u51fa\u4f60\u7684\u53f3\u81c2\u5427\uff01\u201d \u3000\u3000\u5979\u56de\u5934\u7531\u4e00\u4e2a\u5973\u5a62\u7684\u624b\u4e2d\uff0c\u53d6\u8fc7\u4e86\u957f\u5251\u3002 \u3000\u3000\u9ec4\u7075\u53cc\u76ee\u4e2d\u662f\u4e00\u7247\u4e5e\u6c42\u7684\u795e\u5149\uff0c\u9053\uff1a \u201c\u7384\u5a18\uff0c\u7b54\u5e94\u6211\u5427!\u4f60\u5df2\u7ecf\u7b49\u4e86\u5f88\u591a\u5e74\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u518d\u7b49\u4e09\u5e74?\u201d \u3000\u3000\u7384\u5a18\u54ac\u4e86\u54ac\u7259\uff0c\u9053\uff1a\u201c\u4e0d\u884c!\u6211\u73b0\u5728\u5c31\u8981\u65a9\u4f60\u4e00\u6761\u53f3\u81c2\uff0c\u5982\u4f60\u51fa\u624b\u53cd\u6297\uff0c\u6211\u53ef\u80fd\u4f1a\u5931\u624b\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u5979\u62bd\u51fa\u4e86\u957f\u5251\u3002 \u3000\u3000\u6697\u6de1\u7684\u6708\u4eae\uff0c\u5b9d\u5251\u95ea\u8d77\u4e86\u5bd2\u8292\u3002 \u3000\u3000\u90a3\u662f\u4e00\u628a\u5f88\u950b\u5229\u7684\u5b9d\u5251\u3002 \u3000\u3000\u7384\u5a18\u4e3e\u8d77\u4e86\u957f\u5251\u3002 \u3000\u3000\u9ec4\u7075\u95ed\u4e0a\u4e86\u53cc\u76ee\u3002 \u3000\u3000\u770b\u6837\u5b50\uff0c\u4ed6\u771f\u7684\u4e0d\u51c6\u5907\u62b5\u6297\u4e86\u3002 \u3000\u3000\u7384\u5a18\u7684\u957f\u5251\u8fd8\u6ca1\u6709\u51fa\u624b\uff0c\u8033\u9645\u95f4\u5df2\u54cd\u8d77\u4e86\u4e00\u4e2a\u51b7\u51b7\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u9ec4\u5821\u4e3b\u7b54\u5e94\u4e86\uff0c\u6211\u4eec\u4e5f\u4e0d\u7b54\u5e94\u3002\u201d \u3000\u3000\u662f\u4f55\u5bd2\u8863\u548c\u53f6\u957f\u9752\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u3001\u4e07\u80dc\u3001\u534e\u5999\u771f\u4ecd\u7136\u9690\u5728\u6697\u4e2d\uff0c\u6ca1\u6709\u51fa\u9762\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u548c\u53f6\u957f\u9752\u7684\u4e3e\u52a8\u5f88\u5feb\uff0c\u4e00\u7728\u773c\u95f4\uff0c\u5df2\u5230\u9ec4\u7075\u7684\u8eab\u524d\u3002 \u3000\u3000\u53f6\u957f\u9752\u53f3\u624b\u5df2\u7136\u63e1\u4f4f\u4e86\u5251\u67c4\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5374\u8f6c\u5411\u9ec4\u7075\uff0c\u8138\u4e0a\u662f\u4e00\u7247\u5927\u4e49\u51db\u7136\u7684\u795e\u60c5\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u4e3e\u6b62\u95f4\uff0c\u4ecd\u6709\u7740\u65e0\u6bd4\u7684\u5d07\u656c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u6267\u5927\u4e49\u4e0d\u62d8\u5c0f\u8282\uff0c\u4f60\u53ef\u80fd\u6b20\u4e86\u8fd9\u4f4d\u7384\u5a18\u59d1\u5a18\u4e00\u4e9b\u503a\uff0c\u4f46\u8fd8\u503a\u7684\u65b9\u6cd5\u5f88\u591a\uff0c\u4e0d\u4e00\u5b9a\u8981\u9001\u4e0a\u4e00\u6761\u624b\u81c2\u3002\u201d \u3000\u3000\u9ec4\u7075\u53cc\u76ee\u4e2d\u95ea\u52a8\u4e00\u62b9\u51b7\u8292\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u6765\u4e86?\u201d \u3000\u3000\u7384\u5a18\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u4e0d\u7528\u88c5\u4f5c\u4e86\uff0c\u4f60\u65e9\u5df2\u5728\u8fd9\u5730\u65b9\u8bbe\u4e0b\u4e86\u57cb\u4f0f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u8981\u8d23\u602a\u9ec4\u5821\u4e3b\uff0c\u4ed6\u786e\u7136\u4e0d\u77e5\u9053\u6211\u4eec\u6765\uff0c\u6211\u4f55\u5bd2\u8863\u53ef\u4ee5\u6307\u6708\u4e3a\u8a93\u2026\u2026\u201d \u3000\u3000\u7384\u5a18\u63a5\u9053\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f55\u5bd2\u8863\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u6211\u542c\u8fc7\u4f60\u7684\u540d\u5b57\uff0c\u4f46\u4f60\u4e3a\u4ec0\u4e48\u548c\u9ec4\u7075\u8fd9\u6837\u7684\u4eba\u5728\u4e00\u8d77?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u662f\u5728\u4e00\u8d77\uff0c\u800c\u662f\u9ec4\u7075\u5728\u9886\u5bfc\u7740\u6211\u4eec\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4f60\u59d1\u5a18\u7684\u8fd1\u5e74\u4e4b\u4e2d\uff0c\u662f\u4e0d\u662f\u5e38\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u5bf9\u6c5f\u6e56\u4e2d\u7684\u4e8b\uff0c\u4e86\u89e3\u591a\u5c11?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u6211\u4f11\u517b\u4e86\u4e94\u5e74\uff0c\u5c31\u5728\u4ed6\u4f24\u5bb3\u5c1a\u4e91\u7684\u540c\u4e00\u65f6\u95f4\uff0c\u4e5f\u4f24\u5bb3\u4e86\u6211\uff0c\u5c1a\u4e91\u7b54\u5e94\u4ed6\uff0c\u4e0d\u627e\u4ed6\u7b97\u5e10\uff0c\u4f46\u6211\u6ca1\u6709\u7b54\u5e94\u4ed6\uff0c\u6240\u4ee5\uff0c\u6211\u627e\u4e0a\u4e86\u4ed6\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u7384\u5a18\u59d1\u5a18\uff0c \u4f60\u53ea\u662f\u8981\u62a5\u4f60\u79c1\u4eba\u7684\u4ec7\u6068\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u5bf9!\u4ed6\u4f24\u5bb3\u4e86\u6211\uff0c\u6211\u627e\u4ed6\u62a5\u590d\uff0c\u6ca1\u6709\u7275\u6d89\u5230\u522b\u7684\u4eba\uff0c\u8fd9\u6ca1\u6709\u4ec0\u4e48\u9519\u5931\u5427!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u6ca1\u6709\uff0c\u7384\u5a18\u59d1\u5a18\uff0c\u62a5\u4f60\u7684\u79c1\u4eba\u4ec7\u6028\uff0c\u522b\u4eba\u4e0d\u80fd\u7f6e\u5599\uff0c\u4e0d\u8fc7\uff0c\u8fd1\u5e74\u6c5f\u6e56\u5f62\u52bf\uff0c\u90aa\u6076\u9ad8\u5f20\u7684\u76ee\u4e0b\uff0c\u80fd\u591f\u548c\u8fd9\u4e2a\u90aa\u6076\u52bf\u529b\u6297\u62d2\u7684\uff0c\u53ea\u6709\u9ec4\u5821\u4e3b\uff0c\u54b1\u4eec\u4e0d\u7ba1\u59d1\u5a18\u548c\u9ec4\u5821\u4e3b\u7684\u79c1\u4eba\u6069\u6028\uff0c\u53ea\u8bf7\u6c42\u59d1\u5a18\u7b54\u5141\u5ef6\u957f\u51e0\u5e74\u518d\u62a5\u4f60\u79c1\u4eba\u4e4b\u6068\uff0c\u4f7f\u9ec4\u5821\u4e3b\u80fd\u591f\u5927\u5c55\u957f\u624d\uff0c\u9886\u5bfc\u6211\u4eec\uff0c\u4e3a\u6c5f\u6e56\u4e00\u4f38\u6b63\u4e49\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u9ec4\u7075\u80fd\u4e3a\u6c5f\u6e56\u4f38\u5f20\u6b63\u4e49\uff0c \u4f55\u5bd2\u8863\uff0c \u4f60\u6ca1\u6709\u5f04\u9519\u5427?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u8fc7\u53bb\u6709\u591a\u5c11\u7f6a\u6076\uff0c\u5728\u4e0b\u7b49\u4e0d\u77e5\uff0c\u4e0d\u6562\u5984\u8bba\uff0c\u4e0d\u8fc7\uff0c\u81ea\u6211\u4eec\u8bc6\u5f97\u9ec4\u5821\u4e3b\u4ee5\u540e\uff0c\u4ed6\u6240\u4f5c\u6240\u4e3a\uff0c\u6ca1\u6709\u4e00\u4ef6\u4e0d\u4f7f \u3000\u3000\u4eba\u656c\u4f69\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ed6\u4e0d\u6c41\u540d\uff0c\u4e0d\u6c42\u5229\uff0c\u4f46\u6240\u4f5c\u6240\u4e3a\uff0c \u65e0\u4e00\u4e0d\u662f\u884c\u4ec1\u536b\u9053\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u5982\u82e5\u6211\u4e0d\u7b54\u5e94\u4f60\u7684\u8981\u6c42\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u4eec\u4e0d\u5e0c\u671b\u548c\u4f60\u4e3a\u654c\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u4f1a\u5168\u529b\u7ef4\u62a4\u5821\u4e3b\u7684\u5b89\u5168\uff0c\u7eb5\u7136\u62fc\u6b7b\u4e00\u6218\uff0c\u4e5f\u662f\u5728\u6240\u4e0d\u60dc\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4f60\u4eec\u5f88\u591a\u4eba\u4e86?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9634\u9633\u5821\u4e94\u5927\u5251\u4f7f\uff0c\u90fd\u5728\u6b64\u5730\uff0c\u5e0c\u671b\u59d1\u5a18\u4e09\u601d\u3002\u201d \u3000\u3000\u7384\u5a18\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u4f60\u5927\u6982\u5bf9\u6211\u77e5\u9053\u7684\u4e0d\u591a\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u4e0b\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u59d1\u5a18\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u9ec4\u7075\u7518\u5fc3\u53d7\u622e\u5251\u4e0b\uff0c\u9664\u4e86\u4ed6\u7406\u4e8f\u4e4b\u5916\uff0c\u5bf9\u6211\u4e5f\u662f\u6709\u6240\u987e\u8651\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u81ea\u7136\uff0c\u59d1\u5a18\u4e5f\u975e\u5e73\u5e38\u4eba\u7269\uff0c\u4f46\u6b64\u4e8b\u5173\u7cfb\u91cd\u5927\uff0c\u5728\u4e0b\u4e5f\u53ea\u6709\u6597\u80c6\u4e00\u8bd5\u4e86\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u597d!\u4f60\u5982\u80fd\u63a5\u5341\u62db\uff0c\u6211\u5c31\u5ef6\u957f\u62a5\u4ec7\u4e4b\u671f\uff0c\u6bcf\u591a\u4e00\u62db\uff0c\u5c31\u591a\u5ef6\u957f\u4e00\u5e74\uff0c\u4e07\u4e00\u6211\u5728\u5341\u62db\u4e4b\u5185\uff0c\u6740\u4e86\u4f60\uff0c\u4f60\u4e5f\u8981\u6b7b\u800c\u65e0\u6028?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u5347\u8d77\u4e86\u4e00\u7f15\u6012\u706b\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7384\u5a18\u59d1\u5a18\uff0c\u4f60\u597d\u5927\u7684\u53e3\u6c14\uff01\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u6211\u548c\u7384\u5a18\u7684\u4e8b\uff0c\u7eaf\u5c5e\u4e8e\u6211\u4eec\u4e4b\u95f4\u7684\u79c1\u4eba\u6069\u6028\uff0c\u548c\u9634\u9633\u5821\u65e0\u5173\uff0c\u8fd8\u662f\u7531\u6211\u81ea\u5df1\u6765\u5e94\u4ed8\u5427\uff01\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u5bf9!\u4f55\u5927\u4fa0\uff0c\u4f60\u4e0d\u662f\u53d7\u5bb3\u7684\u4eba\uff0c\u6240\u4ee5\uff0c\u4f60\u60f3\u4e0d\u5230\u6211\u5fc3\u4e2d\u6068\u6709\u591a\u6df1\u3002\u9ec4\u7075\u662f\u806a\u660e\u7edd\u4f26\u7684\uff0c\u4ed6\u4f1a\u77e5\u9053\uff0c\u5e94\u8be5\u5982\u4f55\u5904\u7406\u6211\u4eec\u4e4b\u95f4\u7684\u4e8b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u56de\u987e\u4e86\u9ec4\u7075\u4e00\u773c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u6211\u4eec\u4e0d\u662f\u4fdd\u62a4\u4f60\uff0c\u800c\u662f\u4fdd\u62a4\u4e00\u4e2a\u9886\u5bfc\u6211\u4eec\u4e3a\u6c5f\u6e56\u6b63\u4e49\u594b\u6597\u7684\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u5fc3\u5934\u4e00\u9707\uff0c\u51b7\u6c57\u6dcb\u6f13\u800c\u4e0b\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u8083\u7136\u63a5\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u6211\u4eec\u656c\u91cd\u4f60\uff0c\u662f\u656c\u91cd\u4f60\u7684\u5927\u4e49\u51db\u7136\uff0c\u4e5f\u656c\u4f69\u4f60\u7684\u624d\u667a\u6b66\u529f\uff0c\u4f60\u7ec4\u6210\u4e86\u9634\u9633\u5821\uff0c\u628a\u6211\u4eec\u805a\u96c6\u4e8e\u4e00\u5904\uff0c\u6709\u4e00\u4e2a\u76ee\u6807\uff0c\u6709\u4e00\u4e2a\u4f1f\u5927\u7684\u7528\u5fc3\uff0c\u73b0\u5728\u6c5f\u6e56\uff0c\u6b63\u5904\u5728\u98ce\u96e8\u98d8\u6447\u4e4b\u4e2d\uff0c\u5f88\u9700\u8981\u6709\u4eba\u632f\u81c2\u800c\u8d77\uff0c\u633d\u6551\u5371\u5c40\uff0c\u54b1\u4eec\u51c6\u5907\u4e86\u5f88\u591a\u5e74\uff0c\u73b0\u5728\uff0c\u6b63\u662f\u632f\u81c2\u800c\u8d77\u7684\u65f6\u5019\uff0c\u5982\u82e5\u4f60\u4e3a\u4e86\u507f\u8fd8\u6614\u5e74\u7684\u6069\u6028\uff0c\u65ad\u53bb\u4e86\u4e00\u6761\u53f3\u81c2\uff0c\u6211\u4eec\u8fd9\u4e9b\u5e74\u7684\u51c6\u5907\uff0c \u5c82\u4e0d\u662f\u767d\u767d\u5730\u6d6a\u8d39\u4e86\uff0c\u6211\u4eec\u90a3\u5b8f\u5927\u7684\u5fc3\u613f\uff0c\u5c82\u4e0d\u662f\u6c38\u8fdc\u65e0\u6cd5\u5b9e\u73b0\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u4f55\u5251\u4f7f\uff0c \u4f60\u4eec\u9000\u5f00\u4e00\u6b65\uff0c\u6211\u8981\u7ed9\u7384\u5a18\u4e00\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5411\u65c1\u8fb9\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\uff0c\u6211\u4e0d\u662f\u9a97\u4f60\u7684\uff0c\u6211\u771f\u7684\u9700\u8981\u4e00\u4e9b\u65f6\u95f4\uff0c\u7384\u5a18\uff0c\u6211\u6709\u592a\u591a\u7684\u4e8b\u6ca1\u6709\u529e\uff0c\u6211\u5e0c\u671b\u4f60\u80fd\u7ed9\u6211\u4e00\u4e2a\u673a\u4f1a\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6211\uff0c\u6211\u4e0d\u80fd\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u7384\u5a18\uff0c\u4f60\u5df2\u7ecf\u5b8c\u5168\u4e86\u89e3\u8fd9\u4e2a\u60c5\u5f62\uff0c\u6211\u60f3\uff0c\u6211\u7528\u4e0d\u7740\u518d\u7ed9\u4f60\u89e3\u91ca\u4e86\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4e0d\u7528\u89e3\u8bf4\uff0c\u6211\u5df2\u7ecf\u5f88\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u7384\u5a18\uff0c\u4f60\u5982\u662f\u4e00\u5b9a\u4e0d\u653e\u8fc7\u6211\uff0c\u90a3\u662f\u903c\u6211\u62fc\u547d\u4e86\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u6211\u5df2\u7ecf\u65e9\u77e5\u9053\u4f60\u4e0d\u4f1a\u5c31\u8303\u4e86\uff0c\u6240\u4ee5\uff0c\u6211\u4e5f\u662f\u6709\u5907\u800c\u6765\uff0c\u4f60\u53ef\u62db\u547c\u4f60\u57cb\u4f0f\u7684\u4eba\u624b\u51fa\u6765\uff0c\u54b1\u4eec\u653e\u624b\u4e00\u62fc\u5c31\u662f\u3002\u201d \u3000\u3000\u9ec4\u7075\u6447\u6447\u5934\u9053\uff1a \u201c\u7384\u5a18\uff0c\u4e0d\u7528\u4ed6\u4eec\u51fa\u624b\uff0c\u6211\u4e00\u4e2a\u4eba\u5c31\u53ef\u4ee5\u5e94\u4ed8\u4f60\u4e86\u3002\u201d \u7384\u5a18\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u6211\u4e0d\u4f1a\u548c\u4f60\u4e00\u5bf9\u4e00\u7684\u52a8\u624b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4e2a\u4eba\u4e00\u9f50\u51fa\u624b\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u76ee\u4e0b\uff0c\u6211\u4eec\u53ea\u6709\u4e09\u4e2a\u4eba\uff0c\u4f46\u5982\u62db\u547c\u4f60\u7684\u624b\u4e0b\u51fa\u624b\uff0c\u6211\u4e5f\u53ef\u4ee5\u518d\u8bf7\u5e2e\u624b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\uff0c\u4f60\u8fd8\u5e26\u522b\u7684\u4eba\u624b\u4e86\uff1f\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u4f60\u522b\u7d27\u5f20\uff0c\u4ed6\u4eec\u73b0\u5728\u8ddd\u6b64\u8fd8\u6709\u4e00\u6bb5\u5f88\u957f\u7684\u8ddd\u79bb\uff0c\u6211\u653e\u51fa\u62db\u8bf7\u4ed6\u4eec\u7684\u4fe1\u53f7\u65f6\uff0c\u4ed6\u4eec\u624d\u4f1a\u8d76\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u7528\u4efb\u4f55\u4eba\u51fa\u624b\uff0c \u53ea\u6211\u4e00\u4e2a\u4eba\uff0c\u5c31\u53ef\u4ee5\u5bf9\u4ed8\u4f60\u4eec\u4e09\u4e2a\u4e86\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u522b\u592a\u81ea\u4fe1\uff0c\u6211\u4eec\u662f\u6709\u5907\u800c\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\uff0c\u6211\u5df2\u7ecf\u7b54\u5e94\u4f60\u4efb\u4f55\u4f24\u5bb3\u6211\u7684\u65b9\u6cd5\uff0c\u6740\u3001\u5250\u6089\u51ed\u5c0a\u4fbf\uff0c\u6211\u6240\u8981\u6c42\u7684\uff0c\u53ea\u662f\u8981\u4e00\u70b9\u65f6\u95f4\uff0c\u5e0c\u671b\u4f60\u7b54\u5e94\u6211\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u4ec7\u6068\u50cf\u6bd2\u86c7\u4e00\u6837\uff0c\u5543\u566c\u7740\u6211\u7684\u5fc3\uff0c\u6211\u5df2\u7ecf\u5fcd\u4e86\u5f88\u591a\u5e74\uff0c\u4e5f\u82e6\u4e86\u5f88\u591a\u5e74\uff0c\u6211\u5b9e\u5728\u5fcd\u4e0d\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\uff0c\u6211\u5b9e\u5728\u4e0d\u613f\u548c\u4f60\u52a8\u624b\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4f60\u5df2\u7ecf\u6ca1\u6709\u529e\u6cd5\u9009\u62e9\uff0c\u4f60\u4e0d\u60f3\u6b7b\uff0c\u4f46\u6211\u5374\u975e\u8981\u6740\u6b7b\u4f60\u4e0d\u53ef\u3002\u201d \u3000\u3000\u9ec4\u7075\u82e6\u7b11\u4e00\u4e0b\u9053\uff1a\u201c\u597d\u5427!\u770b\u6765\uff0c\u6211\u4eec\u662f\u65e0\u6cd5\u518d\u8c08\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u51fa\u624b\u5427!\u201d \u3000\u3000\u9ec4\u7075\u5438\u4e00\u53e3\u6c14\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u5982\u662f\u4e00\u5b9a\u8981\u5175\u5203\u76f8\u89c1\uff0c\u59d1\u5a18\u5148\u8bf7\u51fa\u624b\u5427!\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u597d!\u4f60\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\u4e00\u6325\u53f3\u624b\uff0c\u4e00\u638c\u5288\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u9ec4\u7075\u5728\u638c\u52bf\u5c06\u8fd1\u8eab\u524d\uff0c\u7a81\u7136\u4e00\u95ea\uff0c\u907f\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u7384\u5a18\u638c\u5982\u95ea\u7535\u4e00\u8fde\u5288\u51fa\u4e86\u4e00\u5341\u4e8c\u638c\u3002 \u3000\u3000\u8fd9\u4e00\u5341\u4e8c\u638c\u7684\u5feb\u901f\uff0c\u8fde\u4f55\u5bd2\u8863\u548c\u53f6\u957f\u9752\u4e5f\u770b\u5f97\u5fc3\u4e2d\u9707\u61be\u4e0d\u5df2\u3002 \u3000\u3000\u4f46\u9ec4\u7075\u5374\u5f88\u8f7b\u5de7\u5730\u907f\u8fc7\u4e86\u4e00\u5341\u4e8c\u638c\u3002 \u3000\u3000\u7384\u5a18\u6536\u4f4f\u638c\u52bf\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u4f60\u4e5f\u6709\u5f88\u5927\u7684\u8fdb\u6b65\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u7384\u5a18\uff0c\u4f60\u6740\u4e0d\u4e86\u6211\u7684\uff0c\u7b97\u4e86\u5427!\u4f60\u5df2\u7ecf\u7b49\u4e86\u5f88\u591a\u5e74\uff0c\u518d\u591a\u7b49\u51e0\u5e74\uff0c\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u5462?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6211\u4e5f\u8bb8\u771f\u7684\u6740\u4e0d\u4e86\u4f60\uff0c\u4f46\u6211\u8981\u5c3d\u91cf\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u9ec4\u7075\u7684\u795e\u60c5\u5ffd\u7136\u53d8\u5f97\u5f88\u4e25\u8083\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7384\u5a18\uff0c\u6211\u4e0d\u80fd\u6b7b\uff0c\u5e76\u4e0d\u662f\u4e3a\u4e86\u8d2a\u56fe\u751f\u547d\uff0c\u6211\u613f\u610f\u4ee5\u6211\u81ea\u5df1\u7684\u9c9c\u8840\uff0c\u6d17\u5237\u6211\u8fc7\u53bb\u9020\u6210\u7684\u9519\u5931\uff0c\u4f46\u6211\u4e0d\u80fd\u8f9c\u8d1f\u9634\u9633\u5821\u4e2d\u5f88\u591a\u540c\u9053\u5bf9\u6211\u7684\u671f\u671b\uff0c\u7384\u5a18\uff0c\u4eca\u591c\uff0c\u6211\u4eec\u5fc5\u987b\u4f5c\u4e00\u4e2a\u4e86\u65ad\u3002\u201d \u3000\u3000\u7384\u5a18\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u51e0\u5e74\uff0c\u6211\u7ec3\u6210\u4e86\u51e0\u79cd\u6b66\u529f\uff0c\u81ea\u89c9\u5b83\u53ef\u4ee5\u5bf9\u4ed8\u4f60\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u4f60\u7ec3\u6210\u4ec0\u4e48\u6b66\u529f\uff0c\u4e0d\u59a8\u5c3d\u91cf\u65bd\u5c55\uff0c\u771f\u80fd\u6740\u4e86\u6211\uff0c\u6211\u5c31\u53ea\u597d\u8ba4\u547d\uff0c\u5982\u662f\u4f60\u6240\u7ec3\u6210\u7684\u6b66\u529f\uff0c\u6740\u4e0d\u4e86\u6211\uff0c\u5e0c\u671b\u4f60\u80fd\u6682\u65f6\u6253\u6d88\u62a5\u590d\u5ff5\u5934\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u597d!\u5982\u662f\u6211\u672c\u8eab\u7684\u6b66\u529f\uff0c\u6740\u4e0d\u4e86\u4f60\uff0c\u6211\u5c31\u518d\u7b49\u4f60\u4e09\u5e74\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u54b1\u4eec\u5370\u8bc1\u4e4b\u540e\uff0c\u4f60\u4e0d\u80fd\u6740\u6211\uff0c\u65f6\u95f4\u548c\u65e5\u671f\uff0c\u5c31\u4e0d\u80fd\u518d\u7531\u4f60\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u7384\u5a18\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5982\u6740\u4e0d\u4e86\u4f60\uff0c\u4e0d\u7b54\u5e94\u4e5f\u5f97\u7b54\u5e94\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u3001\u53f6\u957f\u9752\u76f8\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u7f13\u7f13\u5411\u540e\u9000\u4e86\u4e94\u6b65\u3002 \u3000\u3000\u7384\u5a18\u626c\u8d77\u4e86\u53f3\u624b\uff0c\u7ea4\u5de7\u7684\u7389\u6307\uff0c\u534a\u5c48\u534a\u4f38\u3002 \u3000\u3000\u9ec4\u7075\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u641c\u9b42\u624b\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u54e6!\u4f60\u8ba4\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u641c\u9b42\u624b\u6e90\u51fa\u85cf\u8fb9\uff0c\u672c\u662f\u5587\u561b\u6559\u4e2d\u7684\u7edd\u6280\u4e4b\u4e00\uff0c\u548c\u5927\u624b\u5370\u540c\u51fa\u4e00\u95e8\uff0c\u53ea\u662f\u4e00\u521a\u4e00\u67d4\uff0e\u4e2d\u539f\u6b66\u6797\u9053\u4e0a\uff0c\u7ec3\u6210\u5927\u624b\u5370\u7684\u4eba\uff0c\u5012\u5e38\u542c\u95fb\uff0c\u4f46\u7ec3\u6210\u641c\u9b42\u624b\u7684\uff0c\u5374\u662f\u7edd\u65e0\u4ed7\u6709\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u5b9e\u5728\u5f88\u6e0a\u535a\uff0c\u4f60\u65e2\u80fd\u770b\u51fa\u6211\u65bd\u5c55\u641c\u9b42\u624b\uff0c\u4f46\u4e0d\u77e5\u80fd\u4e0d\u80fd\u7834\u89e3\u5f97\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\uff0c\u4f60\u5230\u6b64\u591a\u4e45\u4e86?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4e0d\u592a\u4e45\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u521a\u624d\uff0c\u6211\u548c\u5c1a\u5929\u4e49\u7684\u4ea4\u8c08\uff0c\u4f60\u542c\u5230\u591a\u5c11\uff1f\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4e0d\u591a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5c1a\u5bb6\u7684\u5929\u7edd\u6307\uff0c\u6b63\u597d\u662f\u641c\u9b42\u624b\u7684\u514b\u661f\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u5c1a\u4e91\u628a\u4ed6\u4eec\u5c1a\u5bb6\u7684\u72ec\u95e8\u7edd\u6280\uff0c\u4e5f\u4f20\u7ed9\u4f60\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\uff0c\u6211\u4e0d\u60f3\u8c08\u8fd9\u4e9b\u9898\u5916\u4e8b\uff0c\u603b\u4e4b\uff0c\u6211\u7684\u5929\u7edd\u6307\u529b\uff0c\u5df2\u7ecf\u6709\u4e86\u76f8\u5f53\u7684\u706b\u5019\uff0c\u6211\u5982\u65bd\u5c55\u51fa\u6765\uff0c\u6211\u4eec\u4e4b\u95f4\uff0c\u5fc5\u6709\u4e00\u4eba\u53d7\u4f24\uff0c\u5929\u7edd\u6307\u65e2\u662f\u641c\u9b42\u624b\u7684\u514b\u661f\uff0c\u4f24\u7684\u4eba\u81ea\u7136\u662f\u4f60\u3002\u201d \u3000\u3000\u7384\u5a18\u53f3\u624b\u4e00\u6536\uff0c\u53cc\u624b\u4ea4\u9519\uff0c\u5de6\u811a\u8e0f\u524d\u534a\u6b65\uff0c\u4e00\u4ed8\u6b32\u4f5c\u6251\u51fb\u4e4b\u52bf\u3002\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u770b\u770b\u8fd9\u662f\u4ec0\u4e48?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e1c\u6d77\u795e\u7fc1\u7684\u4ea4\u526a\u638c\uff0c\u7384\u5a18\uff0c\u60f3\u4e0d\u5230\u77ed\u77ed\u51e0\u5e74\uff0c\u4f60\u7adf\u5b66\u4f1a\u4e86\u897f\u85cf\u548c\u4e1c\u6d77\u4e24\u5904\u795e\u79d8\u95e8\u6237\u7684\u5947\u5b66\u3002\u201d \u3000\u3000\u7384\u5a18\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u4f60\u4e5f\u8ba4\u5f97\u51fa\u6765?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4ea4\u526a\u638c\uff0c\u5982\u526a\u7ede\u8eab\uff0c\u662f\u4e00\u79cd\u5f88\u5947\u5e7b\u7684\u6b66\u529f\uff0c\u5f53\u5e74\u4e1c\u6d77\u795e\u7fc1\uff0c\u8fdb\u5165\u4e2d\u539f\uff0c\u5c31\u4ee5\u6b64\u795e\u529f\uff0c\u51fb\u4f24\u4e2d\u539f\u6b66\u6797\u9053\u4e0a\u4e8c\u5341\u516d\u4f4d\u9ad8\u624b\uff0c\u5c11\u6797\u5bfa\u65b9\u4e08\u4eb2\u8eab\u4e34\u654c\uff0c\u4ee5\u8fbe\u6469\u771f\u7ecf\u4e2d\u2018\u4e00\u67f1\u64ce\u5929\u2019\uff0c\u914d\u5408\u65e0\u76f8\u795e\u529f\uff0c\u7834\u4e86\u4e1c\u6d77\u795e\u7fc1\u7684\u4ea4\u526a\u638c\u8feb\u4ed6\u8ba2\u57ce\u4e0b\u4e4b\u76df\uff0c\u9000\u56de\u4e1c\u6d77\uff0c\u4e00\u751f\u4e0d\u518d\u8fdb\u5165\u4e2d\u539f\uff0c\u7384\u5a18\uff0c\u4f60\u662f\u600e\u4e48\u5b66\u4f1a\u8fd9\u79cd\u6b66\u529f\u7684?\u201d \u3000\u3000\u7384\u5a18\u795e\u60c5\u6fc0\u52a8\uff0c\u5168\u8eab\u5fae\u5fae\u53d1\u6296\uff0c\u9053\uff1a \u201c\u4f60\u8ba4\u5f97\u51fa\u6765\uff0c\u4f46\u4f60\u80fd\u591f\u7834\u89e3\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u80fd\uff01\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4f60\u5b66\u8fc7\u5c11\u6797\u4e00\u67f1\u64ce\u5929?\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6211\u5f88\u5fd9\uff0c\u800c\u4e14\uff0c\u5f88\u5c11\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u7528\u5fc3\u5c31\u5728\u5145\u5b9e\u81ea\u5df1\uff0c\u6211\u6f5c\u5165\u5c11\u6797\u85cf\u7ecf\u9601\uff0c\u4f4f\u4e86\u4e09\u5341\u4e8c\u5929\uff0c\u7528\u5fc3\u4e4b\u4e00\uff0c\u5c31\u5728\u5bfb\u627e\u5f53\u5e74\u767d\u4e91\u5927\u5e08\u51fb\u8d25\u4e1c\u6d77\u795e\u7fc1\u7684\u6863\u6848\uff0c\u90a3\u662f\u4e00\u4ef6\u6c5f\u6e56\u5927\u4e8b\uff0c\u4e5f\u662f\u5c11\u6797\u5bfa\u4e2d\u7684\u5149\u8363\u4e8b\u8ff9\u4e4b\u4e00\uff0c\u6211\u60f3\u4ed6\u4e00\u5b9a\u4f1a\u6709\u8bb0\u8f7d\uff0c\u5e78\u597d\uff0c\u88ab\u6211\u6599\u4e2d\u4e86\uff0c\u90a3\u4e0a\u9762\u8bb0\u8f7d\u7684\u5f88\u8be6\u5c3d\u3002\u201d\u7384\u5a18\u9053\uff1a \u201c\u90a3\u4e0d\u53ef\u80fd\uff0c\u6ca1\u6709\u4eba\u80fd\u5728\u5c11\u6797\u5bfa\u85cf\u7ecf\u9601\u4e2d\u4f4f\u4e86\u4e09\u5341\u4e8c\u5929\uff0c\u4e0d\u88ab\u5c11\u6797\u50e7\u4fa3\u53d1\u89c9\u3002\u201d \u3000\u3000\u9ec4\u7075\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u6c5f\u6e56\u4e0a\uff0c\u53ea\u4e86\u89e3\u5c11\u6797\u5bfa\u662f\u6b66\u6797\u4e2d\u7684\u6cf0\u6597\uff0c\u4e5f\u53ea\u77e5\u9053\u638c\u95e8\u65b9\u4e08\u7684\u6743\uff0c\u8fbe\u6469\u9662\u662f\u9020\u5c31\u5c11\u6797\u9ad8\u624b\u7684\u5730\u65b9\uff0c\u7f57\u6c49\u9635\u662f\u5929\u4e0b\u7b2c\u4e00\u5927\u9635\uff0c\u4e0a\u5ea7\u4e09\u50e7\uff0c\u662f\u5c11\u6797\u5bfa\u4e2d\u7684\u9ad8\u624b\uff0c\u4f46\u5374\u4e0d\u77e5\u9053\uff0c\u771f\u6b63\u7684\u6709\u9053\u884c\u7684\u9ad8\u50e7\uff0c\u5374\u9690\u5c45\u5728\u85cf\u7ecf\u9601\u4e2d\uff0c\u548c\u6212\u5f8b\u9662\u5185\uff0c\u4ed6\u4eec\u53ef\u80fd\u9ed8\u9ed8\u65e0\u95fb\uff0c\u751a\u81f3\uff0c\u8fde\u5c11\u6797\u5bfa\u4e2d\u7edd\u5927\u90e8\u5206\u7684\u50e7\u4fa3\uff0c\u90fd\u4e0d\u77e5\u9053\u4ed6\u4eec\u7684\u6210\u5c31\u2026\u2026\u201d \u3000\u3000\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u6211\u76f8\u4fe1\u4ed6\u4eec\u65e9\u53d1\u89c9\u4e86\u6211\uff0c\u800c\u4e14\uff0c\u4e5f\u53ef\u80fd\u4e00\u76f4\u5728\u76d1\u89c6\u7740\uff0c\u4f46\u4ed6\u4eec\u4f3c\u4e4e\u662f\u5bf9\u6211\u6709\u610f\u7684\u5bb9\u5fcd\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u53ef\u80fd\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\uff0c \u4ed6\u4eec\u624d\u662f\u771f\u6b63\u7684\u9ad8\u50e7\uff0c\u6d1e\u5f7b\u4e16\u60c5\uff0c\u770b\u7834\u4e86\u540d\u5229\uff0c\u6211\u542c\u8fc7\u4ed6\u4eec\u4e4b\u95f4\u7684\u4e24\u53e5\u5bf9\u8bdd\uff0c\u624d\u653e\u80c6\u4f4f\u4e0b\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e00\u6bb5\u6c5f\u6e56\u79d8\u5e78\uff0c \u4e0d\u4f46\u5f15\u8d77\u4e86\u4f55\u5bd2\u8863\u548c\u53f6\u957f\u9752\u5f3a\u70c8\u7684\u5174\u8da3\uff0c\u4e5f\u5f15\u8d77\u7384\u5a18\u7684\u597d\u5947\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\uff0c\u90fd\u4ee5\u671f\u5f85\u7684\u76ee\u5149\uff0c\u51dd\u4f4f\u7740\u9ec4\u7075\u3002 \u3000\u3000\u7384\u5a18\u8f7b\u8f7b\u53f9\u606f\uff0c\u9053\uff1a \u201c\u96be\u9053\uff0c\u90a3\u65f6\u5019\uff0c \u4f60\u5df2\u77e5\u9053\u6211\u8981\u5b66\u4e1c\u6d77\u4ea4\u526a\u638c?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4e0d\u77e5\u9053\uff0c\u6211\u4f4f\u5728\u85cf\u7ecf\u9601\u4e09\u5341\u4e8c\u5929\uff0c\u4e5f\u4e0d\u662f\u4e3a\u4e86\u4f60\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4e0d\u662f\u4e3a\u4e86\u6211\uff0c\u90a3\u662f\u4e3a\u4e86\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u7384\u5a18\uff0c\u6211\u4e0d\u77e5\u9053\u4f60\u662f\u5426\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u8fc7\uff0c\u4ee5\u4f60\u7684\u806a\u6167\uff0c\u5e94\u8be5\u53ef\u4ee5\u770b\u51fa\u6765\uff0c \u76ee\u4e0b\u6c5f\u6e56\u4e0a\u8868\u9762\u5f88\u5e73\u9759\uff0c\u4f46\u6bcf\u9694\u4e00\u6bb5\u65f6\u95f4\uff0c\u5fc5\u7136\u6709\u4e00\u6b21\u5f88\u60ca\u4eba\u7684\u5927\u6848\u5b50\u53d1\u751f\uff0c\u8ba1\u5212\u5468\u5bc6\uff0c\u672a\u7559\u4e0b\u4e00\u70b9\u7ebf\u7d22\uff0c\u4e00\u822c\u6b66\u6797\u4e2d\u4eba\uff0c\u867d\u7136\u6ca1\u6709\u77a7\u51fa\u4ec0\u4e48\uff0c\u4f46\u5c11\u6797\u5bfa\u4e2d\u7684\u9ad8\u50e7\u66fe\u6709\u8b66\u89c9\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u8db3\u4e0d\u6d89\u6c5f\u6e56\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u65e0\u6cd5\u77a7\u51fa\u4ec0\u4e48\u5f81\u8c61\uff0c\u4f46\u4ed6\u4eec\u7cbe\u6df1\u7684\u4fee\u517b\uff0c\u6709\u4e00\u79cd\u5e38\u4eba\u96be\u53ca\u7684\u7075\u89c9\uff0c\u8fd9\u79cd\u7075\u89c9\uff0c\u4f7f\u4ed6\u4eec\u611f\u89c9\u5230\u6709\u4ec0\u4e48\u4e8b\u53d1\u751f\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u6240\u4ee5\uff0c\u4ed6\u4eec\u7eb5\u5bb9\u4f60\u5728\u85cf\u7ecf\u9601\u4e2d\uff0c\u8bfb\u4e86\u4e09\u5341\u4e8c\u5929\u7684\u4e66\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5bf9\uff0c\u4ed6\u4eec\u6ca1\u6709\u544a\u8bc9\u6211\u4ec0\u4e48\uff0c\u751a\u81f3\u8fde\u9762\u4e5f\u4e0d\u548c\u6211\u89c1\u4e00\u6b21\uff0c\u90a3\u4e09\u5341\u4e8c\u5929\uff0c\u4f7f\u6211\u7684\u6536\u83b7\u5f88\u5927\uff0e\u4ed6\u4eec\u6709\u610f\u7684\u5305\u5bb9\uff0c\u4f7f\u6211\u5f88\u5b89\u5fc3\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4f60\u58f0\u540d\u72fc\u85c9\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u7eb5\u5bb9\u4f60?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\uff0c\u4f60\u53ef\u4ee5\u4e0d\u4fe1\u4efb\u6211\uff0c\u4f46\u4f60\u5e94\u8be5\u76f8\u4fe1\u5c11\u6797\u5bfa\u7684\u51e0\u4f4d\u9ad8\u50e7\uff0c\u4f60\u4e5f\u5e94\u8be5\u4fe1\u4efb\u4f55\u5bd2\u8863\u548c\u53f6\u957f\u9752\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u53f6\u957f\u9752\uff0c\u53ef\u662f\u4e03\u5251\u8ffd\u9b42\u53f6\u957f\u9752\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6b63\u662f\u533a\u533a\u5728\u4e0b\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u6211\u4eec\u5148\u6c42\u8bc1\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u5148\u8bc1\u660e\uff0c\u4f60\u771f\u80fd\u7834\u4e86\u6211\u4ea4\u526a\u638c?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d\u5427!\u7384\u5a18\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e0d\u60f3\u7528\u4ea4\u624b\u7684\u65b9\u5f0f\u6765\u6c42\u8bc1\u660e\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4ec0\u5e7a\u65b9\u5f0f\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u6211\u6446\u51fa\u2018\u4e00\u67f1\u64ce\u5929\u2019\u7684\u62db\u5f0f\uff0c\u4f60\u5e94\u8be5\u770b\u5f97\u51fa\u6765\uff0c\u5b83\u80fd\u4e0d\u80fd\u5bf9\u4ed8\u4ea4\u526a\u638c\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6211\u867d\u7136\u6ca1\u6709\u89c1\u8fc7\u2018\u4e00\u67f1\u64ce\u5929\u2019\uff0c\u4f46\u6211\u76f8\u4fe1\u80fd\u591f\u770b\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u53f3\u817f\u4e00\u6536\uff0c\u5de6\u638c\u7acb\u80f8\uff0c\u53f3\u624b\u4e94\u6307\u534a\u5c48\uff0c\u5fae\u5fae\u62ac\u8d77\u3002 \u3000\u3000\u7384\u5a18\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u770b\u6765\uff0c\u6211\u6c38\u8fdc\u65e0\u6cd5\u62a5\u4ec7\u4e86\uff0c\u8fc7\u53bb\uff0c\u6211\u6253\u4e0d\u8fc7\u4f60\uff0c\u73b0\u5728\u4e5f\u6740\u4e0d\u4e86\u4f60\uff0c\u4ee5\u540e\uff0c\u53ea\u6015\u4e5f\u6ca1\u6709\u4ec0\u4e48\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\uff0c\u4f60\u5982\u679c\u6709\u4e00\u4e2a\u6740\u6211\u7684\u5fc3\u613f\uff0c\u6211\u76f8\u4fe1\u4f60\u4f1a\u5f97\u507f\u7684\uff0c\u53ea\u4e0d\u8fc7\uff0c\u628a\u65f6\u95f4\u5ef6\u540e\u4e00\u4e9b\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u597d\u5427!\u6211\u7b49\u4f60\u4e09\u5e74\uff0c\u53ea\u6c42\u4e0a\u5929\u4fdd\u4f51\u4f60\uff0c\u8fd9\u4e09\u5e74\u4e4b\u5185\uff0c\u4e0d\u8981\u88ab\u522b\u4eba\u6740\u4e86\u3002\u201d \u3000\u3000\u56de\u987e\u4e86\u4e24\u4e2a\u5973\u5a62\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427!\u201d \u3000\u3000\u8f6c\u8eab\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u9ec4\u7075\u4f4e\u58f0\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u529d\u5979\u7559\u4e0b\u6765\uff0c\u5979\u662f\u4e2a\u5f88\u597d\u7684\u5e2e\u624b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u62ac\u5934\u671b\u671b\u5df2\u5230\u4e86\u4e24\u4e08\u5916\u7684\u7384\u5a18\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u5979\u5bf9\u4f60\u4ec7\u6068\u5f88\u6df1\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u9ec4\u7075\u53c8\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u5973\u4eba\u7684\u4ec7\u6068\u5f3a\u70c8\uff0c\u4f46\u4e0d\u4f1a\u6df1\u523b\uff0c\u5979\u4eec\u6539\u53d8\u5f97\u5f88\u5feb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u54e6\u4e86\u4e00\u58f0\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u7384\u5a18\u59d1\u5a18\uff0c\u8bf7\u7559\u6b65\u3002\u201d \u3000\u3000\u7384\u5a18\u5df2\u884c\u5230\u4e86\u4e09\u56db\u4e08\u5916\uff0c\u95fb\u8a00\u505c\u4e0b\u4e86\u811a\u6b65\uff0c\u9053\uff1a\u201c\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6717\u6717\u795e\u5dde\uff0c \u76ee\u4e0b\u5374\u7b3c\u7f69\u7740\u4e00\u7247\u6101\u4e91\u60e8\u96fe\uff0c\u4f60\u4e00\u8eab\u7edd\u6280\uff0c\u4f55\u4e0d\u7559\u4e0b\u6765\u2026\u2026\u201d \u3000\u3000\u7384\u5a18\u63a5\u9053\uff1a\u201c\u7559\u4e0b\u6765\u5e2e\u52a9\u9ec4\u7075\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u662f\u5e2e\u52a9\u9ec4\u5821\u4e3b\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4e0d\u5e2e\u4ed6\uff0c\u90a3\u662f\u5e2e\u4f60\u4e86?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u5e2e\u6211\uff0c\u800c\u662f\u4e3a\u6c5f\u6e56\u4e0a\u6b63\u4e49\u5c3d\u529b\u3002\u201d \u3000\u3000\u7384\u5a18\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\uff0c\u7559\u4e0b\u6765\uff0c\u5bf9\u4f60\u6709\u597d\u5904\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u51fa\uff0c\u5bf9\u6211\u6709\u4ec0\u4e48\u597d\u5904!\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u4f60\u53ef\u4ee5\u770b\u5230\u6211\uff0c\u662f\u5426\u88ab\u522b\u4eba\u6740\u4e86?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u5c31\u7b97\u6211\u770b\u522b\u4eba\u80fd\u6740\u4f60\u65f6\uff0c\u603b\u4e0d\u80fd\u51fa\u624b\u6551\u4f60\u5427?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u5462\u2026\u2026\u201d \u3000\u3000\u7384\u5a18\u6012\u9053\uff1a\u201c\u6211\u6068\u6b7b\u4f60\u4e86\uff0c\u8fd8\u8981\u6211\u6551\u4f60\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4f60\u5982\u771f\u60f3\u4eb2\u624b\u6740\u6b7b\u6211\uff0c\u603b\u4e0d\u80fd\u773c\u770b\u6211\u6b7b\u5728\u522b\u4eba\u624b\u4e2d\u5427?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u55ef!\u8fd9\u4e5f\u6709\u7406\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u53ef\u4ee5\u5f88\u8ba4\u771f\u7684\u8003\u8651\u4e00\u4e0b\u8981\u4e0d\u8981\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u7384\u5a18\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u7384\u5a18\u59d1\u5a18\uff0c\u8fc7\u53bb\uff0c\u9ec4\u5821\u4e3b\u4e00\u5b9a\u5f88\u5bf9\u4e0d\u8d77\u4f60\uff0c\u6211\u4eec\u4e5f\u4e0d\u6562\u4e3a\u4f60\u4eec\u6392\u96be\u89e3\u7eb7\uff0c\u6211\u4eec\u62e5\u62a4\u9ec4\u5821\u4e3b\uff0c\u662f\u62e5\u62a4\u4ed6\u7684\u624d\u6167\uff0c\u4ed6\u7684\u7edd\u4e16\u6b66\u529f\u548c\u4ed6\u7684\u6551\u4e16\u80f8\u6000\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4e0d\u7528\u518d\u89e3\u91ca\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4eec\u5f88\u5e0c\u671b\u4f60\u80fd\u7559\u4e0b\u6765\uff0c\u5171\u540c\u4e3a\u6c5f\u6e56\u9053\u4e49\u4e00\u5c3d\u5fc3\u529b\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u7ed9\u6211\u51e0\u5929\u65f6\u95f4\uff0c\u6211\u5982\u60f3\u901a\u4e0b\uff0c\u6211\u81ea\u4f1a\u53bb\u627e\u4f60\u4eec\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d!\u54b1\u4eec\u606d\u5019\u4f73\u97f3\u3002\u201d \u3000\u3000\u7384\u5a18\u70b9\u70b9\u5934\uff0c\u5e26\u7740\u4e8c\u5a62\u79bb\u53bb\u3002 \u3000\u3000\u9ec4\u7075\u56de\u987e\u4e86\u53f6\u957f\u9752\u548c\u4f55\u5bd2\u8863\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u6765\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e94\u5927\u5251\u4f7f\uff0c\u9f50\u805a\u4e8e\u65af\u3002\u201d \u3000\u3000\u9ec4\u7075\u82e6\u7b11\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u8eab\u8d1f\u65e7\u6028\u3001\u4ec7\u6068\u592a\u591a\uff0c\u8fde\u5e26\u8bf8\u4f4d\u2026\u2026\u201d \u3000\u3000\u53ea\u89c1\u4eba\u5f71\u95ea\u52a8\uff0c\u534e\u5999\u771f\u3001\u5c60\u65e0\u65b9\u3001\u4e07\u80dc\uff0c\u4e00\u9f50\u98de\u843d\u5230\u51c9\u4ead\u65c1\u5228\u3002 \u3000\u3000\u534e\u5999\u771f\u7b11\u9053\uff1a\u201c\u5c60\u9ebb\u5b50\u548c\u6211\u51b7\u624b\u7f57\u5239\uff0c\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u8a89\uff0c\u6bd4\u8d77\u9ec4\u5821\u4e3b\uff0c\u53ea\u6015\u8fdc\u8981\u574f\u4e0a\u5341\u500d\uff0c\u6c5f\u6e56\u4e0a\u6b63\u6d3e\u4eba\u58eb\uff0c\u66f4\u662f\u5bf9\u6211\u4eec\u6df1\u6076\u75db\u7edd\uff0c\u4e00\u65e6\u77e5\u9053\u4e86\u6211\u4eec\u4e5f\u548c\u8bf8\u4f4d\u76f8\u5904\u4e00\u8d77\uff0c\u5bf9\u8bf8\u4f4d\u7684\u5f71\u54cd\u66f4\u5927\u4e86\u3002 \u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u534e\u59d1\u5a18\uff0c\u5982\u82e5\u8bf4\u6614\u5e74\u884c\u4e3a\u4e4b\u574f\uff0c\u624b\u6bb5\u4e4b\u6bd2\uff0c\u5bb3\u4eba\u4e4b\u591a\uff0c\u5728\u4e0b\u5b9e\u5728\u4e0d\u5728\u59d1\u5a18\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a \u201c\u5c60\u5144\u3001\u534e\u59d1\u5a18\uff0c\u6b64\u65f6\u4f55\u65f6\uff0c\u6211\u4eec\u76f8\u5904\u6570\u5e74\uff0c\u81f3\u5c11\uff0c\u6211\u53ef\u4ee5\u4e3a\u4e24\u4f4d\u4f5c\u8bc1\uff0c\u4f60\u4eec\u8eab\u8d1f\u6076\u540d\uff0c\u4f46\u4f60\u4eec\u6240\u4f5c\u6240\u4e3a\uff0c\u6bd4\u8d77\u90a3\u4e9b\u4fa0\u540d\u6ee1\u5929\u4e0b\u7684\u4eba\uff0c\u5f3a\u8fc7\u5341\u500d\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u7b11\u9053\uff1a \u201c\u6211\u4e5f\u662f\u8fd9\u4e48\u60f3\u6cd5\uff0c\u7b11\u9a82\u7531\u4eba\uff0c\u53ea\u8981\u6211\u4eec\u4ef0\u4e0d\u6127\u5929\uff0c\u4fef\u4e0d\u4f5c\u5730\uff0c\u5fc3\u4e2d\u5b89\u7136\uff0c\u53c8\u4f55\u5fc5\u6c41\u8f83\u8eab\u5916\u4e4b\u540d\u5462\uff1f\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u5c0f\u59b9\u6297\u62d2\u6c5f\u6e56\u4e0a\u8fd9\u80a1\u90aa\u6076\u52bf\u529b\u4e4b\u5fc3\u5e76\u672a\u52a8\u6447\uff0c\u5982\u662f\u4e0d\u6015\u6211\u548c\u5c60\u9ebb\u5b50\u7684\u6076\u540d\u62d6\u7d2f\uff0c\u54b1\u4eec\u5c31\u5408\u4f5c\u4e00\u6b21\uff0c\u5982\u662f\u6709\u4e0d\u4fbf\u4e4b\u5904\uff0c\u5c0f\u59b9\u5012\u60f3\u4e86\u4e00\u4ef6\u4e24\u5168\u4e4b\u7b56\uff0c\u7531\u5c0f\u59b9\u548c\u5c60\u5144\uff0c\u5206\u51fa\u4e00\u90e8\u5206\u9634\u9633\u5821\u4e2d\u4eba\u624b\uff0c\u6211\u4eec\u60f3\u6cd5\u5b50\u72ec\u81ea\u6d3b\u52a8\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u6cd5\u5b50\u4e0d\u884c\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u54b1\u4eec\u82e6\u82e6\u6478\u7d22\uff0c\u5982\u4eca\u5df2\u7b97\u627e\u51fa\u4e86\u4e00\u70b9\u5934\u7eea\uff0c\u6c5f\u6e56\u4e0a\u7978\u60a3\u6839\u6e90\uff0c \u5df2\u7136\u6307\u5411\u4e86\u9ad8\u5929\u5065\uff0c\u73b0\u5728\uff0c\u54b1\u4eec\u7f3a\u5c11\u7684\u662f\uff0c\u8fd9\u4e2a\u63a7\u5236\u6c5f\u6e56\u7684\u9634\u8c0b\u3001\u8bc1\u636e\uff0c\u4e00\u65e6\u80fd\u628a\u8fd9\u4e9b\u4e8b\u60c5\u63ed\u53d1\u51fa\u6765\uff0e\u6211\u60f3\u6211\u4eec\u4e0d\u4f46\u5bf9\u6c5f\u6e56\u6709\u4e86\u4e00\u4e2a\u4ea4\u4ee3\uff0c\u6c5f\u6e56\u540c\u9053\uff0c\u4e5f\u4f1a\u5bf9\u6211\u4eec\u53e6\u773c\u76f8\u770b\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a \u201c\u5821\u4e3b\u4e3a\u4e86\u5e94\u4ed8\u8fd9\u573a\u6069\u6028\uff0c\u4e2d\u67a2\u5df2\u9886\u5bfc\u4e4f\u4eba\uff0c\u867d\u7136\u53f6\u5144\u3001\u4f55\u5144\uff0c\u80fd\u4ee3\u5821\u4e3b\u5206\u52b3\u4e0d\u5c11\uff0c\u4f46\u7a76\u7adf\u914d\u5408\u4e0a\uff0c\u4e0d\u80fd\u5929\u8863\u65e0\u7f1d\uff0c\u8001\u5b9e\u8bf4\uff0c\u6574\u4e2a\u9634\u9633\u5821\u7684\u884c\u52a8\uff0c\u5df2\u7ecf\u6709\u4e9b\u5fd9\u4e71\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u5bd2\u8863\u4e00\u5e2d\u8bdd\uff0c\u4f7f\u6211\u751f\u51fa\u4e86\u5f88\u5927\u7684\u8b66\u60d5\u4e4b\u5fc3\uff0c\u767e\u91cc\u884c\u7a0b\u534a\u4e5d\u5341\uff0c\u6211\u4e0d\u80fd\u4e22\u4e0b\u9634\u9633\u5821\u4e2d\u4e8b\u4e0d\u7ba1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\u632f\u4f5c\u8d77\u6765\uff0c\u6211\u4eec\u624d\u80fd\u6709\u6240\u4f5c\u4e3a\u3002\u201d \u3000\u3000\u9ec4\u7075\u7f13\u7f13\u7531\u8eab\u4e0a\u53d6\u51fa\u4e00\u9526\u56ca\uff0c \u53ea\u89c1\u4e0a\u9762\u5199\u9053\uff1a \u201c\u7384\u5a18\uff0c\u6211\u6b7b\u4e4b\u540e\uff0c\u8bf7\u628a\u6b64\u56ca\u8f6c\u4ea4\u4f55\u5bd2\u8863\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u8fd9\u662f\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u8fd9\u662f\u6211\u591a\u5e74\u7b79\u601d\u7684\u4e00\u4e2a\u8ba1\u5212\uff0c\u6211\u6ca1\u6709\u60f3\u5230\uff0c\u8fd8\u80fd\u6d3b\u5f97\u4e0b\u53bb\uff0c\u6211\u4e86\u89e3\u7384\u5a18\uff0c\u6211\u6b7b\u4e4b\u540e\uff0c\u5979\u5fc5\u7136\u4f1a\u641c\u6211\u4e4b\u8eab\uff0c\u4e5f\u4e00\u5b9a\u4f1a\u628a\u8fd9\u4e2a\u9526\u56ca\u4ea4\u5230\u4f60\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9eef\u7136\u8bf4\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u653e\u773c\u5f53\u4eca\u4e4b\u4e16\uff0c\u9664\u4f60\u4e4b\u5916\uff0c \u53ea\u6015\u518d\u96be\u627e\u51fa\u7b2c\u4e8c\u4e2a\u4eba\uff0c\u80fd\u591f\u548c\u9ad8\u5929\u5065\u5bf9\u6297\u4e86\uff0c\u60f3\u4e0d\u5230\uff0c \u4f60\u7adf\u4f1a\u52a8\u4e86\u8f7b\u751f\u4e4b\u5ff5\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5f53\u5c40\u8005\u8ff7\uff0c\u6211\u89c9\u7740\u9634\u9633\u5821\u5df2\u7ecf\u6709\u4e86\u5f88\u597d\u7684\u7ec4\u7ec7\uff0c\u6211\u662f\u751f\u662f\u6b7b\uff0c\u5df2\u4e0d\u4f1a\u5f71\u54cd\u5927\u5c40\uff0c\u9002\u624d\u542c\u4f60\u2014\u5e2d\u8bdd\uff0c\u624d\u4f7f\u6211\u5ffd\u7136\u6e05\u9192\u8fc7\u6765\u2026\u2026\u201d \u3000\u3000\u534e\u5999\u771f\u63a5\u9053\uff1a\u201c\u597d!\u6709\u5821\u4e3b\u8fd9\u4e00\u53e5\u8bdd\uff0c\u6211\u4eec\u5c31\u653e\u5fc3\u4e86\uff1a\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u4f55\u5bd2\u8863\u3001\u53f6\u957f\u9752\uff0c\u63a5\u9053\uff1a\u201c\u4e24\u4f4d\u90fd\u662f\u6b63\u5927\u95e8\u6237\u4e2d\u4eba\uff0c\u4e0d\u77e5\u9053\uff0c\u6211\u4eec\u8fd9\u4e9b\u51fa\u8eab\u9ed1\u9053\u4e0a\u4eba\u7684\u75db\u82e6\uff0c\u6211\u4eec\u505a\u597d\u4e8b\uff0c\u522b\u4eba\u4e0d\u4fe1\uff0c\u505a\u4e00\u70b9\u574f\u4e8b\uff0c\u7acb\u523b\u4f20\u904d\u5929\u4e0b\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a \u201c\u534e\u59d1\u5a18\uff0c\u4f60\u660e\u767d\u5417?\u6211\u4eec\u5f88\u656c\u91cd\u4f60\uff0c\u800c\u4e14\uff0c\u4ee5\u540e\uff0c\u6574\u4e2a\u7684\u6b66\u6797\u540c\u9053\uff0c\u90fd\u4f1a\u5bf9\u4f60\u5341\u6c41\u656c\u91cd\uff1b\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u4f55\u5c11\u4fa0\uff0c\u6211\u4eec\u51fa\u8eab\u4e0d\u540c\uff0c\u5728\u6c5f\u6e56\u4e0a\u7684\u5730\u4f4d\u4e5f\u4e0d\u540c\uff0c\u4f60\u53d7\u5c3d\u4e86\u6b66\u6797\u540c\u9053\u7684\u656c\u91cd\uff0c\u6211\u4eec\u5374\u53d7\u5c3d\u4e86\u51b7\u773c\uff0c\u7167\u8bf4\uff0c\u6211\u4eec\u5e94\u8be5\u662f\u751f\u6b7b\u4e4b\u654c\uff0c\u50cf\u4f60\u8fd9\u79cd\u8eab\u4efd\uff0c\u5982\u82e5\u80fd\u591a\u6740\u4e00\u4e2a\u6211\u4eec\u8fd9\u6837\u7684\u4eba\uff0c\u5c31\u4f1a\u591a\u589e\u52a0\u4e00\u70b9\u58f0\u8a89\uff0c\u4f46\u6211\u4eec\u5374\u4f1a\u5728\u4e00\u8d77\uff0c\u5171\u540c\u5728\u4e00\u4e2a\u7ec4\u7ec7\u4e2d\u3002\u201d \u3000\u3000\u9ec4\u7075\u4e00\u6325\u624b\uff0c\u62e6\u4f4f\u4e86\u534e\u5999\u771f\u518d\u8bf4\u4e0b\u53bb\uff0c\u63a5\u9053\uff1a \u201c\u534e\u59d1\u5a18\uff0c\u6b66\u5f53\u6d3e\u8fd8\u5728\u8ffd\u6740\u4f60\u5417?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u653e\u7740\u6c5f\u6e56\u5927\u4e8b\uff0c\u4ed6\u4eec\u4e0d\u7ba1\uff0c\u5374\u5bf9\u6211\u82e6\u82e6\u8ffd\u8e2a\u4e0d\u820d\uff0c\u534a\u6708\u4e4b\u524d\uff0c\u6211\u8fd8\u9047\u4e0a\u4ed6\u4eec\uff0c\u88ab\u4ed6\u4eec\u8ffd\u4e86\u5927\u534a\u5929\uff0c\u6211\u8dd1\u4e86\u4e94\u516d\u5341\u91cc\uff0c\u624d\u628a\u4ed6\u4eec\u6446\u8131\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u8138\u4e0a\u4e00\u70ed\uff0c\u9053\uff1a\u201c\u534e\u59d1\u5a18\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u8ffd\u4f60\u3002\u201d\u534e\u5999\u771f\u9053\uff1a \u201c\u56e0\u4e3a\uff0c\u6211\u4f24\u4e86\u4e00\u4e2a\u6b66\u5f53\u95e8\u4e0b\u7684\u5f1f\u5b50\uff0c\u542c\u8bf4\uff0c\u4ed6\u5e08\u7236\u63a5\u638c\u4e86\u95e8\u6237\uff0c\u6240\u4ee5\uff0c\u4e0b\u4ee4\u6b66\u5f53\u7684\u95e8\u4e0b\uff0c\u5bf9\u6211\u82e6\u82e6\u8feb\u6740\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u672c\u6765\uff0c\u6211\u53ef\u4ee5\u6740\u4e86\u4ed6\u7684\uff0c\u6740\u4e86\u4ed6\uff0c\u4e5f\u8bb8\u5c31\u6ca1\u6709\u4e8b\u4e86\uff0c\u4f46\u6211\u5374\u624b\u4e0b\u7559\u60c5\uff0c\u53ea\u65ad\u4e86\u4ed6\u4e00\u6761\u624b\u81c2\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\uff0c\u4e3a\u4ec0\u4e48\u4f60\u65ad\u4e86\u4ed6\u4e00\u6761\u624b\u81c2?\u201d \u3000\u3000\u534e\u5999\u771f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bf4\u8d77\u6765\uff0c\u662f\u4e00\u4ef6\u5f88\u597d\u7b11\u7684\u4e8b\uff0c\u4ed6\u7adf\u7136\u6562\u5bf9\u6211\u52a8\u624b\u52a8\u811a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4f55\u5144\u53ef\u662f\u6709\u4e9b\u4e0d\u4fe1?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u4e0d\u7792\u534e\u59d1\u5a18\u8bf4\uff0c\u5728\u4e0b\u51fa\u8eab\u6b66\u5f53\uff0c\u540c\u9053\u6709\u6b64\u7b49\u4eba\u7269\uff0c\u5728\u4e0b\u7406\u5e94\u5411\u59d1\u5a18\u81f4\u6b49\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u90a3\u4e0d\u6562\u5f53\uff0c\u6b66\u5f53\u6d3e\u6b63\u5927\u95e8\u6237\uff0c\u4e5f\u4e0d\u7b97\u4ec0\u4e48\u5927\u4e8b\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u5bf9\u5c0f\u59b9\u592a\u8fc7\u5206\u4e86\u4e00\u4e9b\uff0c\u6211\u5fcd\u65e0\u53ef\u5fcd\uff0c\u624d\u65ad\u4e86\u4ed6\u4e00\u6761\u624b\u81c2\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u65ad\u5f97\u597d\uff01\u201d \u3000\u3000\u534e\u5999\u771f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u9ed1\u9053\u4e2d\u4eba\uff0c\u90fd\u77e5\u5c0f\u59b9\u7684\u6076\u540d\uff0c\u5012\u662f\u4e0d\u6562\u5bf9\u6211\u65e0\u793c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u8fd9\u4ef6\u4e8b\uff0c\u5c0f\u5f1f\u4e00\u5b9a\u8981\u5bf9\u534e\u59d1\u5a18\u6709\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f55\u5144\uff0c\u8fc7\u53bb\u7684\u4e8b\u5c31\u7b97\u4e86\uff0c\u73b0\u5728\uff0c\u91cd\u8981\u7684\u662f\uff0c\u8981\u5bf9\u4ed8\u9ad8\u5929\u5065\uff0c\u81f3\u4e8e\u5c0f\u59b9\u548c\u6b66\u5f53\u7684\u4e8b\uff0c\u613f\u542c\u4f55\u5144\u7684\u5b89\u6392\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d!\u5c0f\u5f1f\u4e00\u5b9a\u5c3d\u529b\u800c\u4e3a\u3002\u201d \u3000\u3000\u9ec4\u7075\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u54b1\u4eec\u76f8\u5904\u8fd9\u4e9b\u5e74\uff0c\u867d\u7136\u5f88\u878d\u6d3d\uff0c\u4f46\u6709\u4e9b\u4e8b\uff0c\u5927\u5bb6\u8fd8\u662f\u6709\u610f\u907f\u5f00\uff0c\u4eca\u65e5\u80fd\u8fd9\u6837\u5766\u7136\u4ea4\u8c08\uff0c\u4ee5\u540e\uff0c\u5c31\u66f4\u597d\u76f8\u5904\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u6211\u4eec\u5bf9\u4f60\u7684\u8fc7\u53bb\uff0c \u53ea\u662f\u731c\u6d4b\uff0c\u4eca\u591c\uff0c\u7b97\u662f\u6709\u4e86\u4e00\u4e9b\u4e86\u89e3\uff0c\u4f60\u628a\u6211\u4eec\u805a\u96c6\u4e00\u5904\uff0c\u6240\u4ee5\uff0c \u4f60\u5fc5\u5148\u8981\u632f\u4f5c\u8d77\u6765\uff0c\u4e0d\u8bba\u4f60\u4e3a\u60c5\u3001\u4e3a\u4ec7\uff0c\u4f46\u4f60\u90fd\u4e0d\u80fd\u6b7b\uff0c\u5c31\u76ee\u4e0b\u7684\u60c5\u5f62\u800c\u8a00\uff0c\u6b7b\u4ea1\u662f\u4e00\u79cd\u9003\u907f\uff0c\u73b0\u5728\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8981\u4ea4\u4ee3\u7684\u5fc3\u613f\u6ca1\u6709?\u201d \u3000\u3000\u9ec4\u7075\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u8fd8\u6709\uff0c\u6211\u4e5f\u4e0d\u4f1a\u9003\u907f\u4e86\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u597d\u5566\uff0c\u5821\u4e3b\u662f\u54b1\u4eec\u7684\u5934\u5934\uff0c\u4eca\u591c\u91cc\u54b1\u4eec\u4ee5\u4e0b\u72af\u4e0a\uff0c\u5b8c\u5168\u5fd8\u4e86\u89c4\u77e9\u3002\u201d \u3000\u3000\u9ec4\u7075\u6325\u6325\u624b\uff0c\u7b11\u9053\uff1a\u201c\u5c60\u5144\uff0c\u73b0\u5728\uff0c\u54b1\u4eec\u662f\u8bba\u79c1\u4ea4\u3002\u201d \u3000\u3000\u4e00\u76f4\u672a\u5f00\u53e3\u7684\u4e07\u80dc\uff0c\u7a81\u7136\u5f00\u4e86\u53e3\uff0c\u9053\uff1a \u201c\u73b0\u5728\uff0c \u54b1\u4eec\u5e94\u8be5\u8c08\u8c08\u516c\u4e8b\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5728\u4e0b\u5148\u5411\u5821\u4e3b\u8bf7\u6559\u4e00\u4e8b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u5723\u6c34\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u6709\u4e9b\u4ec0\u4e48\u7528\u5904?\u5821\u4e3b\uff0c\u53ef\u5426\u89e3\u8bf4\u7ed9\u54b1\u4eec\u542c\u542c\u3002\u201d \u3000\u3000\u9ec4\u7075\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u636e\u6211\u6240\u77e5\uff0c\u90a3\u662f\u4e00\u79cd\u7279\u6b8a\u7684\u77ff\u6c34\uff0c\u4f5c\u4ec0\u4e48\u7528\uff0c\u6211\u8fd8\u6ca1\u6709\u67e5\u6e05\u695a\uff0c\u4e0d\u8fc7\uff0c\u6211\u5df2\u7ecf\u67e5\u51fa\u4e86\u5927\u90e8\u5206\u7684\u5723\u6c34\u53bb\u5904\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5230\u54ea\u91cc\u53bb\u4e86?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5929\u5065\u6536\u53bb\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u4ec0\u4e48\u90fd\u5e72\uff0c\u4ed6\u6536\u5723\u6c34\u4f5c\u4ec0\u4e48?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u76ee\u524d\u8fd8\u4e0d\u77e5\u9053!\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u90a3\u6301\u5723\u6c34\u7684\u4e00\u6279\u4eba\uff0c\u548c\u9ad8\u5929\u5065\u65e0\u5173\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e00\u6279\u4eba\uff0c\u65e0\u610f\u4e2d\u5e2e\u4e86\u54b1\u4eec\u5f88\u5927\u7684\u5fd9\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u8fd9\u6279\u4eba\u4e5f\u4e0d\u7b80\u5355\uff0c\u4e0d\u4f46\u4eba\u624b\u5f88\u591a\uff0c \u800c\u4e14\uff0c\u4e2a\u4e2a\u6b66\u529f\u9ad8\u5f3a\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u80fd\u4e0d\u80fd\u60f3\u6cd5\u5b50\uff0c\u5148\u6311\u62d4\u8fd9\u6279\u4eba\uff0c\u548c\u9ad8\u5929\u5065\u51b2\u7a81\u8d77\u6765?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u53ea\u6709\u4e00\u4e2a\u529e\u6cd5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7531\u5723\u6c34\u4e0a\u7740\u624b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bf9!\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u53ea\u53ef\u60dc\uff0c\u5723\u6c34\u7684\u4ea4\u6613\u65f6\u671f\u5df2\u8fc7\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u628a\u54b1\u4eec\u8d2d\u597d\u7684\u5723\u6c34\u4ea4\u7ed9\u4ed6\u4eec\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ea4\u7ed9\u8c01?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5929\u5065\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a \u201c\u672c\u6765\uff0c\u6211\u662f\u60f3\u5148\u524a\u5f31\u4ed6\u7684\u5b9e\u529b\uff0c\u7136\u540e\u518d\u5bf9\u4ed8\u4ed6\uff0c\u73b0\u5728\uff0c\u6211\u6539\u53d8\u8ba1\u5212\u4e86\uff0c\u5148\u5bf9\u4ed8\u9ad8\u5929\u5065\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5bf9!\u6253\u86c7\u6253\u5934\uff0c\u64d2\u8d3c\u64d2\u738b\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u6811\u5012\u7322\u72f2\u6563\uff0c\u9ad8\u5929\u5065\u5982\u662f\u88ab\u54b1\u4eec\u6740\u4e86\uff0c\u6211\u60f3\u4ed6\u9886\u5bfc\u7684\u7ec4\u7ec7\uff0c\u5c31\u7b97\u518d\u5e9e\u5927\u4e00\u4e9b\uff0c\u4e5f\u4f1a\u5f88\u5feb\u5730\u74e6\u89e3\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u54b1\u4eec\u9634\u9633\u5821\u7684\u5b9e\u529b\u867d\u7136\u4e0d\u5f31\uff0c\u4f46\u5982\u7531\u6211\u4eec\u72ec\u7acb\u5bf9\u4ed8\u9ad8\u5929\u5065\uff0c\u5b9e\u529b\u76f8\u5dee\u4ecd\u7136\u5341\u5206\u60ac\u6b8a\uff0c\u5728\u6211\u539f\u6765\u7684\u8ba1\u5212\u4e2d\uff0c\u4e00\u9762\u548c\u4ed6\u5468\u65cb\uff0c\u4e00\u9762\u641c\u96c6\u4ed6\u7684\u6076\u8ff9\uff0c\u516c\u8bf8\u6c5f\u6e56\uff0c\u4e5f\u5c31\u662f\u4e00\u9762\u548c\u4ed6\u52a8\u624b\uff0c\u4e00\u9762\u4f7f\u5f97\u6c5f\u6e56\u540c\u9053\u89c9\u9192\uff0c\u54b1\u4eec\u4ee5\u6697\u5bf9\u6697\uff0c\u9003\u907f\u548c\u4ed6\u4eec\u4f5c\u5b9e\u529b\u5bf9\u6297\uff0c\u5982\u82e5\u6211\u7684\u8ba1\u5212\uff0c\u6ca1\u6709\u4ec0\u4e48\u5dee\u9519\uff0c\u5468\u65cb\u4e0b\u53bb\uff0c\u6211\u4eec\u7684\u5b9e\u529b\u4f1a\u8d8a\u6765\u8d8a\u5f3a\uff0c\u9ad8\u5929\u5065\u7684\u5b9e\u529b\u4f1a\u76f8\u5bf9\u51cf\u5f31\uff0c\u4f46\u76ee\u4e0b\u60c5\u5f62\uff0c\u4f3c\u4e4e\u5df2\u6709\u4e86\u6539\u53d8\uff0c\u54b1\u4eec\u4e0d\u5f97\u4e0d\u4fee\u6b63\u4e00\u4e0b\u8ba1\u5212\u4e86\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u4f55\u5bd2\u8863\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u4f55\u5144\uff0c\u54b1\u4eec\u76f8\u5904\u6570\u5e74\uff0c\u7adf\u7136\u4e0d\u77e5\u4f60\u51fa\u8eab\u6b66\u5f53\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5c34\u5c2c\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5728\u4e0b\u5e76\u975e\u6709\u610f\u7684\u9690\u7792\u8eab\u4efd\uff0c\u56e0\u4e3a\uff0c\u4ece\u6b63\u7edf\u7684\u95e8\u6237\u89c4\u6212\u7b97\uff0c\u6211\u4e0d\u7b97\u6b66\u5f53\u6b63\u5f0f\u7684\u95e8\u4e0b\uff0c\u6240\u4ee5\uff0c\u6211\u4e5f\u4e0d\u53d7\u6b66\u5f53\u90a3\u4e9b\u6212\u89c4\u675f\u7f1a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u4e9b\u8bdd\u600e\u4e48\u8bf4?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u4f20\u6388\u6211\u5251\u672f\u7684\uff0c\u662f\u4e00\u4f4d\u6b66\u5f53\u540d\u5bbf\uff0c\u4ed6\u4e5f\u662f\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u4ed6\u548c\u6211\u5fd8\u5e74\u8bba\u4ea4\uff0c\u4ee5\u5144\u5f1f\u76f8\u79f0\uff0c\u5374\u4e00\u76f4\u4e0d\u80af\u5bb9\u8bb8\u6211\u62dc\u5728\u4ed6\u7684\u95e8\u4e0b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4f46\u4f60\u81ea\u79f0\u6b66\u5f53\u5f1f\u5b50\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a\u201c\u6211\u90a3\u4f4d\u6388\u827a\u957f\u5144\uff0c\u867d\u662f\u7688\u4f9d\u4e09\u6e05\uff0c\u4f46\u4ed6\u7684\u8f88\u4efd\u5f88\u9ad8\uff0c\u5728\u6b66\u5f53\u95e8\u4e2d\uff0c\u6781\u53d7\u656c\u91cd\uff0c\u6211\u53bb\u8fc7\u6b66\u5f53\u4e09\u5143\u89c2\uff0c\u4e5f\u89c1\u8fc7\u638c\u95e8\u4eba\uff0c\u638c\u95e8\u4eba\u4e5f\u66fe\u8981\u6211\u8ba4\u7956\u5f52\u95e8\uff0c\u4f5c\u6b66\u5f53\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u4f46\u4e00\u76f4\u88ab\u6211\u90a3\u4f4d\u6388\u827a\u957f\u5144\u6240\u963b\uff0c\u800c\u4e14\uff0c\u7acb\u523b\u5e26\u6211\u4e0b\u5c71\uff0c\u540c\u53bb\u89c1\u4ed6\u4e00\u4f4d\u597d\u53cb\uff0c\u53c8\u4f20\u4e86\u6211\u4e03\u7edd\u98de\u8f6e\u624b\u6cd5\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u795e\u8f6e\u83ab\u5f3a?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u662f!\u5821\u4e3b\u8ba4\u8bc6\u4ed6?\u201d \u3000\u3000\u9ec4\u7075\u4e0d\u7b54\uff0c\u53cd\u95ee\u9053\uff1a\u201c\u4f20\u4f60\u5251\u6cd5\u7684\u53ef\u662f\u6b66\u5f53\u540d\u5bbf\u7af9\u6756\u7fc1?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u4e5f\u8ba4\u8bc6\u4ed6?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5341\u51e0\u5e74\u524d\uff0c\u6211\u89c1\u8fc7\u4ed6\u4eec\u4e24\u4f4d\uff0c\u800c\u4e14\uff0c\u8fd8\u548c\u4ed6\u4eec\u52a8\u8fc7\u624b\uff0c\u7af9\u6756\u7fc1\u7684\u5251\u6cd5\u9020\u8be3\uff1b\u5df2\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u83ab\u5f3a\u7684\u4e03\u7edd\u98de\u8f6e\uff0c\u66f4\u662f\u7384\u5999\u83ab\u6d4b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u5fae\u5fae\u4e00\u7b11\u9053\uff1a \u201c\u5728\u90a3\u4e00\u6218\u4e2d\uff0c\u5821\u4e3b\u662f\u80dc\u4e86\uff0c\u8fd8\u662f\u8d25\u4e86?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u6ca1\u6709\u80dc\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u7684\u5f88\u542b\u7cca\uff0c\u6ca1\u6709\u80dc\uff0c \u81ea\u7136\u662f\u8d25\u4e86\uff0c\u4f46\u4ed6\u6ca1\u6709\u660e\u8bf4\u8d25\u4e86\u3002 \u3000\u3000\u5374\u8f6c\u8fc7\u8bdd\u9898\uff0c\u9053\uff1a \u201c\u9634\u9633\u5821\u4ee5\u9ed1\u9053\u7ec4\u7ec7\u51fa\u73b0\u6c5f\u6e56\uff0c\u800c\u4e14\uff0c\u884c\u52a8\u4e00\u76f4\u5341\u5206\u8c28\u614e\uff0c\u6240\u4ee5\uff0c\u9ad8\u5929\u5065\u4e5f\u6ca1\u6709\u592a\u6ce8\u610f\u6211\u4eec\uff0c\u8fd9\u5bf9\u6211\u4eec\u7684\u53d1\u5c55\uff0c\u5e2e\u52a9\u5f88\u5927\uff0c\u4f46\u8fd9\u4e00\u6b21\u5723\u6c34\u4ea4\u6613\uff0c\u65e0\u7591\u662f\u6b63\u5f0f\u63ed\u7a7f\u4e86\u6211\u4eec\u548c\u9ad8\u5929\u5065\u4e3a\u654c\u7684\u5185\u60c5\uff0c\u98de\u9e70\u548c\u72c2\u9f99\uff0c\u8fd9\u6b21\u53c8\u5728\u592a\u6e56\u632b\u8d25\uff0c\u8fd9\u5bf9\u9ad8\u5929\u5065\u662f\u4e00\u4e2a\u5f88\u5927\u7684\u523a\u6fc0\uff0c\u6211\u60f3\uff0c\u4ed6\u4f1a\u628a\u54b1\u4eec\u770b\u6210\u7b2c\u4e00\u53f7\u654c\u4eba\uff0c\u4e5f\u5fc5\u4f1a\u96c6\u805a\u4ed6\u7684\u529b\u91cf\uff0c\u5148\u6c42\u6d88\u706d\u54b1\u4eec\uff0c \u81ea\u662f\u4e0d\u6613\u518d\u7f13\u6162\u884c\u52a8\uff0c\u524a\u5f31\u4ed6\u7684\u7fbd\u7ffc\u4e86\uff0c\u53ea\u597d\u96c6\u4e2d\u5168\u529b\uff0c\u4f5c\u64d2\u738b\u2014\u51fb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\u53ef\u5df2\u6709\u4e86\u8ba1\u5212?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6709!\u4f46\u7389\u5170\u53cc\u59dd\u5bf9\u6211\u4eec\u7684\u6210\u8d25\uff0c\u6709\u7740\u5f88\u5927\u7684\u5173\u7cfb\uff0c\u4e0d\u77e5\u9053\u5979\u4eec\u4f1a\u4e0d\u4f1a\u5168\u529b\u4ee5\u8d74?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u6211\u5df2\u5c3d\u529b\u8bf4\u670d\u4e86\u5979\u4eec\uff0c\u5979\u4eec\u4e5f\u5f88\u611f\u6fc0\u5821\u4e3b\u7684\u6551\u547d\u4e4b\u6069\uff0c\u4f46\u5979\u4eec\u4f5c\u591a\u5c11\uff0c\u90a3\u5c31\u5f88\u96be\u9884\u6599\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5821\u4e3b\u662f\u4e0d\u662f\u51c6\u5907\u96c6\u4e2d\u9634\u9633\u5821\u7684\u4eba\u624b\uff0c\u4f5c\u5168\u529b\u4e00\u51fb\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u9634\u9633\u5821\u7684\u4eba\u624b\uff0c \u5df2\u5206\u6563\u4e8e\u6c5f\u6e56\u4e4b\u4e2d\uff0c\u9664\u4e86\u957f\u9752\u5e26\u8fd9\u4e00\u90e8\u5206\u4eba\u624b\uff0c\u53ef\u4ee5\u8c03\u7528\u4e4b\u5916\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u8981\u628a\u4ed6\u4eec\u96c6\u4e2d\u8d77\u6765\uff0c\u4e5f\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u60c5\uff0c\u800c\u4e14\uff0c\u4eba\u6570\u4e00\u591a\uff0c\u884c\u52a8\u4e5f\u4e0d\u65b9\u4fbf\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a \u201c\u5c31\u662f\u54b1\u4eec\u8fd9\u51e0\u4e2a\u4eba\uff0c\u627e\u4e0a\u9ad8\u5929\u5065\uff0c\u4ee5\u5821\u4e3b\u7684\u529f\u529b\uff0c\u518d\u4f50\u4ee5\u4f55\u5144\u7684\u98de\u8f6e\u3001\u53f6\u5144\u7684\u8ffd\u9b42\u4e03\u5251\uff0c\u53ef\u4ee5\u62fc\u5b83\u4e00\u9635\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u54b1\u4eec\u51e0\u4e2a\u4eba\uff0c\u53c8\u592a\u5355\u8584\u4e86\u4e00\u4e9b\uff0c\u6211\u65e9\u5df2\u5b89\u6392\u4e86\u4e00\u652f\u4f0f\u5175\uff0c\u73b0\u5728\u53ef\u4ee5\u6d3e\u4e0a\u7528\u573a\u4e86\u3002\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u4e07\u80dc\u7684\u8eab\u4e0a\uff0c\u9053\uff1a \u201c\u4e07\u5144\uff0c\u4f60\u628a\u8fd9\u51e0\u5e74\u7684\u51c6\u5907\u7ed9\u5927\u5bb6\u8bf4\u4e00\u4e0b\u3002\u201d \u3000\u3000\u5929\u624b\u5200\u4e07\u80dc\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u8fd9\u51e0\u5e74\uff0c\u5144\u5f1f\u4e00\u76f4\u5f88\u5c11\u548c\u8bf8\u4f4d\u89c1\u9762\uff0c\u5c31\u662f\u5949\u4e86\u5821\u4e3b\u4e4b\u547d\uff0c\u5728\u6697\u4e2d\u8bad\u7ec3\u4e86\u4e00\u6279\u4eba\u624b\uff0c\u56e0\u4e3a\u65f6\u95f4\u592a\u77ed\u4fc3\uff0c\u8981\u628a\u4e00\u4e2a\u65b0\u4eba\u8bad\u7ec3\u6210\u6709\u7528\u7684\u4eba\uff0c\u5b9e\u5728\u4e0d\u592a\u5bb9\u6613\uff0c\u6240\u4ee5\uff0c\u9009\u53d6\u7684\u4eba\u624b\uff0c\u5927\u90fd\u662f\u5df2\u6709\u6b66\u529f\u57fa\u7840\u7684\u4eba\uff0c \u76ee\u4e0b\u80fd\u591f\u6d3e\u4e0a\u7528\u573a\u7684\uff0c\u5927\u7ea6\u6709\u4e94\u516d\u5341\u4e2a\u4eba\uff0c\u53ef\u5f53\u5927\u7528\u7684\uff0c\u53ea\u6709\u4e8c\u5341\u56db\u4e2a\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u6279\u4eba\u73b0\u5728\u4f55\u5904?\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u628a\u4ed6\u4eec\u96c6\u4e2d\u4e8e\u4e00\u5904\u9690\u79d8\u6240\u5728\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u6709\u8fd9\u4e8c\u5341\u56db\u4e2a\u4eba\uff0c\u8db3\u53ef\u4ee5\u4f9b\u6211\u4eec\u8c03\u5ea6\u4e86\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u597d!\u5c5e\u4e0b\u7acb\u523b\u901a\u77e5\u4ed6\u4eec\u8d76\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u6447\u6447\u624b\uff0c\u9053\uff1a \u201c\u4e0d\u5fc5\u4e86\uff0c\u4f60\u5e26\u7740\u4ed6\u4eec\u8d70\uff0c\u6700\u597d\u80fd\u591f\u6613\u5bb9\u4e0a\u8def\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4e0d\u7528\u6025\u7740\u8d76\u8def\uff0c\u4ee5\u9690\u79d8\u884c\u8e2a\u4e3a\u8981\uff0c\u6211\u4eec\u5728\u4fe1\u9633\u5dde\u5929\u5916\u5ba2\u6808\u89c1\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u5c5e\u4e0b\u7406\u4f1a\u5f97\uff0c\u201d \u3000\u3000\u5bf9\u4f55\u5bd2\u8863\u7b49\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u5148\u8d70\u4e00\u6b65\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u671b\u7740\u4e07\u80dc\u7684\u80cc\u5f71\uff0c\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u5821\u4e3b\u679c\u7136\u662f\u8ba1\u5212\u5468\u5bc6\uff0c\u4eba\u6240\u96be\u53ca\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5929\u5065\u7684\u8033\u76ee\u4f17\u591a\uff0c\u54b1\u4eec\u4e5f\u8981\u5c0f\u5fc3\u4e00\u4e9b\u624d\u884c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u7684\u610f\u601d\uff0c\u54b1\u4eec\u4e5f\u8981\u6613\u5bb9\u800c\u884c\u4e86\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u8bf8\u845b\u4e00\u751f\u552f\u8c28\u614e\uff0c\u8fd9\u4e00\u6218\u592a\u91cd\u8981\uff0c\u54b1\u4eec\u5931\u8d25\u4e0d\u8d77\u3002\u201d \u3000\u3000\u4fe1\u9633\u5728\u6cb3\u5357\u5357\u90e8\uff0c\u5e02\u9762\u7e41\u8363\uff0c\u5546\u8d3e\u4e91\u96c6\uff0c \u3000\u3000\u5929\u5916\u5ba2\u6808\u662f\u4fe1\u9633\u5dde\u7b2c\u4e00\u7b49\u5927\u5ba2\u6808\uff0c\u5f88\u597d\u627e\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u548c\u534e\u5999\u771f\u8d70\u5728\u4e00\u5904\uff0c\u4e24\u4e2a\u4eba\u5feb\u4e00\u6b65\uff0c\u6700\u5148\u8d76\u5230\u3002 \u3000\u3000\u91d1\u5251\u98de\u8f6e\u7684\u540d\u6c14\u867d\u5927\uff0c\u4f46\u6c5f\u6e56\u4e0a\u89c1\u8fc7\u4ed6\u7684\u5374\u662f\u4e0d\u591a\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u7a7f\u7740\u4e00\u88ad\u5929\u84dd\u957f\u886b\uff0c\u5b8c\u5168\u662f\u4e00\u4e2a\u6e38\u5b66\u58eb\u5b50\u7684\u6253\u626e\u3002 \u3000\u3000\u534e\u5999\u771f\u5973\u626e\u7537\u88c5\uff0c\u767d\u8863\u65b9\u5dfe\uff0c\u7f8e\u5a07\u5a18\u5316\u8eab\u4fcf\u4e66\u751f\u3002 \u3000\u3000\u5929\u5916\u5ba2\u6808\u89c4\u6a21\u76f8\u5f53\u5927\uff0c\u4f46\u5ba2\u4eba\u5fc3\u5f88\u591a\uff0c\u4e03\u516b\u4e2a\u5e97\u5c0f\u4e8c\uff0c\u5fd9\u5f97\u56e2\u56e2\u8f6c\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u6b63\u662f\u65e5\u843d\u897f\u5c71\uff0c\u5ba2\u4eba\u4f4f\u5e97\u7684\u65f6\u5019\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u5728\u95e8\u53e3\u7b49\u4e86\u4e00\u4f1a\uff0c\u624d\u89c1\u4e00\u4e2a\u5e97\u5c0f\u4e8c\u8fce\u4e86\u4e0a\u6765\uff0c\u6253\u91cf\u4e86\u4e24\u4eba\u624b\u4e2d\u7275\u7684\u6bdb\u9a74\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e24\u4f4d\u628a\u5750\u9a91\u4ea4\u7ed9\u6211\u5427!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6211\u4eec\u8981\u4f4f\u5e97\uff0c\u4e00\u8def\u98ce\u5c18\uff0c\u60f3\u5728\u8fd9\u513f\u591a\u4f4f\u4e24\u5929\uff0c\u80fd\u4e0d\u80fd\u7ed9\u6211\u4eec\u4e00\u4e2a\u5e7d\u9759\u4e00\u70b9\u7684\u8de8\u9662\uff1f\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u8de8\u9662\uff0c\u6211\u5012\u53ef\u4ee5\u66ff\u4e24\u4f4d\u60f3\u4e2a\u6cd5\u5b50\uff0c \u53ea\u662f\u4ef7\u94b1\u8d35\u4e86\u4e00\u4e9b\u2026\u2026\u201d \u3000\u3000\u4e24\u4eba\u7a7f\u7684\u8863\u670d\u867d\u7136\u5f88\u5e72\u51c0\uff0c\u4f46\u7edd\u5bf9\u4e0d\u662f\u5f88\u6709\u94b1\u7684\u4eba\uff0c\u6709\u94b1\u4eba\u4e0d\u4f1a\u82b1\u51e0\u94b1\u94f6\u5b50\uff0c\u9a91\u2014\u5934\u6bdb\u9a74\u8d76\u8def\uff0c\u534e\u8863\u9a8f\u9a6c\uff0c\u6e38\u5b66\u56db\u65b9\uff0c\u90a3\u624d\u662f\u4eba\u751f\u4e50\u4e8b\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4f4e\u58f0\u9053\uff1a\u201c\u94f6\u5b50\u6211\u4eec\u8fd8\u6709\u4e00\u4e9b\uff0c\u5c0f\u4e8c\u54e5\u53ea\u7ba1\u653e\u5fc3\u3002\u201d \u3000\u3000\u5ba2\u5927\u5403\u884c\uff0c\u884c\u5927\u5403\u5ba2\uff0c\u8fd9\u5929\u5916\u5ba2\u6808\u7684\u5e97\u4f19\u8ba1\uff0c\u4e5f\u6709\u4e00\u80a1\u5927\u5ba2\u6808\u7684\u6c14\u52bf\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u4e24\u4f4d\u4eca\u5929\u65e9\u4e0a\u96c7\u7684\u6bdb\u9a74\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u8fd9\u4e00\u7ad9\u662f\u516b\u5341\u91cc\uff0c\u5305\u8349\u5305\u6599\uff0c\u4e00\u5934\u662f\u516b\u94b1\u94f6\u5b50\uff0c\u4e8c\u516b\u4e00\u4e24\u516d\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53d6\u51fa\u4e00\u7efd\u94f6\u5b50\uff0c\u7ea6\u83ab\u4e94\u4e24\uff0c\u9012\u5b50\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u8fd9\u70b9\u94f6\u5b50\uff0c\u5c0f\u4e8c\u54e5\u5148\u6536\u7740\u2026\u2026\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u63a5\u8fc7\u94f6\u5b50\uff0c\u8138\u4e0a\u7acb\u523b\u6324\u51fa\u4e86\u7b11\u5bb9\uff0c\u8170\u4e5f\u5f2f\u5566\uff0c\u4e00\u9762\u62db\u547c\u53e6\u4e00\u4e2a\u5e97\u4f19\u8ba1\u628a\u6bdb\u9a74\u9001\u4e0a\u69fd\uff0c\u4e00\u9762\u6253\u7740\u8eac\u628a\u4f55\u5bd2\u8863\u3001\u534e\u5999\u771f\uff0c\u5f15\u5165\u4e86\u4e00\u5ea7\u8de8\u9662\u4e2d\u3002 \u3000\u3000\u8fd9\u5ea7\u8de8\u9662\u76f8\u5f53\u7684\u5bbd\u5927\uff0c\u9664\u4e86\u4e00\u5ea7\u4e3b\u623f\u4e4b\u5916\uff0c\u8fd8\u6709\u4e24\u95f4\u53a2\u623f\uff0c\u4e00\u4e2a\u5c0f\u5de7\u7684\u9662\u843d\uff0c\u79cd\u690d\u4e86\u4e00\u4e9b\u82b1\u8349\u3002 \u3000\u3000\u4f46\u6700\u4ee4\u534e\u5999\u771f\u6ee1\u610f\u7684\u662f\uff0c\u6709\u4e00\u68f5\u7e41\u679d\u5bc6\u53f6\u7684\u767d\u679c\u6811\uff0c\u8038\u7acb\u5728\u5ead\u9662\u4e2d\u3002 \u3000\u3000\u5e97\u5c0f\u4e8c\u9001\u4e0a\u4e86\u8336\u6c34\uff0c\u7b11\u9053\uff1a \u201c\u4e24\u4f4d\u76f8\u516c\uff0c\u8981\u4e0d\u8981\u51c6\u5907\u70b9\u5403\u7684\u4e1c\u897f?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u597d!\u7ed9\u6211\u4eec\u5907\u51e0\u4e2a\u83dc\uff0c\u4e00\u76d8\u635e\u997c\uff0c\u4e00\u58f6\u72b6\u5143\u7ea2\u3002\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u79bb\u53bb\u4e4b\u540e\uff0c\u534e\u5999\u771f\u624d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f55\u5144\uff0c\u8fd9\u5ea7\u5929\u5916\u5ba2\u6808\uff0c\u8fd8\u7ecf\u8425\u6bdb\u9a74\u751f\u610f\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u9053\uff1a \u201c\u90a3\u5012\u4e0d\u662f\uff0c\u8c6b\u5357\u51e0\u53bf\uff0c\u90fd\u6709\u8fd9\u4e00\u79cd\u751f\u610f\uff0c\u5404\u884c\u90fd\u6709\u4e00\u5b9a\u7684\u8def\u7ebf\uff0c\u4e0d\u7528\u4eba\u8ddf\uff0c\u5230\u7ad9\u5934\u65f6\uff0c\u4ed6\u4eec\u6709\u4eba\u6536\u94b1\u3001\u63a5\u9a74\uff0c\u6709\u7684\u548c\u5927\u5ba2\u6808\u6709\u6765\u5f80\uff0c\u8fde\u8def\u90fd\u4e0d\u7528\u95ee\uff0c\u6bdb\u9a74\u5c31\u628a\u4f60\u9001\u5230\u4e86\u5ba2\u6808\u95e8\u53e3\uff0c\u4ed6\u4eec\u65e9\u5df3\u628a\u4ef7\u94b1\u7b97\u597d\uff0c\u4f5c\u4e86\u4e2a\u6697\u53f7\uff0c\u5230\u5730\u5934\u6536\u94b1\uff0c\u5206\u6587\u4e0d\u9519\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u8fd9\u5012\u662f\u65b9\u4fbf\u7684\u5f88\uff0c\u6211\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u4e86\u4e0d\u5c11\u5e74\uff0c\u3000\u3000\u4e24\u5ea6\u7ecf\u8fc7\u50cf\u5357\uff0c\u5374\u6ca1\u6709\u9a91\u8fc7\u8fd9\u79cd\u7684\u6bdb\u9a74\u5b50\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u4eba\uff0c\u81ea\u7136\u5f88\u5c11\u9a91\u8fd9\u79cd\u5c0f\u6bdb\u9a74\u3002\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u9001\u4e0a\u9152\u83dc\uff0c\u4e24\u4e2a\u4eba\u5bf9\u5750\u5c0f\u914c\u3002 \u3000\u3000\u534e\u5999\u771f\u559d\u4e86\u4e00\u676f\u9152\uff0c\u7a81\u7136\u653e\u4e0b\u9152\u676f\uff0c\u9053\uff1a\u201c\u4f55\u5144\uff0c\u60c5\u5f62\u6709\u4e9b\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4f60\u6ce8\u610f\u5230\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6ce8\u610f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u90a3\u4e2a\u5e97\u5c0f\u4e8c\uff0c\u4ed6\u5b9e\u5728\u88c5\u5f97\u5f88\u50cf\uff0c\u8fde\u6211\u4e5f\u88ab\u4ed6\u7792\u8fc7\u53bb\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u90a3\u5e97\u5c0f\u4e8c\u6709\u6bdb\u75c5\uff1f\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e0d\u662f\u6bdb\u75c5\uff0c\u662f\u95ee\u9898\uff0c\u4f60\u7559\u5fc3\u5916\u9762\uff0c\u6211\u68c0\u67e5\u4e00\u4e0b\u9152\u83dc\u3002\u201d \u3000\u3000\u5979\u53d6\u51fa\u4e00\u4e2a\u94f6\u7c2a\uff0c\u5f88\u4ed4\u7ec6\u7684\u68c0\u67e5\u4e86\u9152\u83dc\u3002 \u3000\u3000\u5e78\u597d\uff0c\u9152\u83dc\u4e4b\u4e2d\u65e0\u6bd2\u3002 \u3000\u3000\u534e\u5999\u771f\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u4e0d\u7b80\u5355\uff0c\u4e5f\u8bb8\uff0c\u4ed6\u8ba4\u4e3a\u5df2\u9a97\u4f4f\u4e86\u54b1\u4eec\uff0c\u4e5f\u8bb8\u4ed6\u8fd8\u5728\u6478\u54b1\u4eec\u7684\u5e95\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u534e\u5999\u771f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f55\u5144\uff0c\u770b\u6837\u5b50\uff0c\u4f60\u597d\u50cf\u4e0d\u592a\u76f8\u4fe1\u6211\u7684\u8bdd\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u80fd\u4e0d\u80fd\u8bf4\u5f97\u8be6\u7ec6\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u53ea\u8981\u4f60\u7559\u5fc3\u4e00\u4e9b\uff0c\u4e5f\u5e94\u8be5\u770b\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u770b\u4ed6\u6c14\u5ea6\uff1f\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5bf9\uff0c\u90a3\u53ea\u662f\u539f\u56e0\u4e4b\u4e00\uff0c\u66f4\u91cd\u8981\u7684\u662f\uff0c\u4ed6\u7684\u773c\u795e\uff0c\u4ed6\u53ef\u4ee5\u9690\u85cf\u8d77\u4ed6\u6240\u6709\u7684\u4e1c\u897f\uff0c\u4f46\u4ed6\u65e0\u6cd5\u5b8c\u5168\u9690\u53bb\u90a3\u4e00\u5bf9\u7cbe\u5149\u9690\u9690\u7684\u773c\u795e\uff0c\u4ed6\u85cf\u950b\u655b\u5203\u7684\u672c\u9886\uff0c\u8fd8\u4e0d\u5230\u5bb6\uff0c\u4e00\u9ad8\u5174\uff0c\u773c\u795e\u5c31\u9732\u4e86\u51fa\u6765\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u5fae\u5fae\u9894\u9996\uff0c\u4ed4\u7ec6\u60f3\u8d77\u6765\uff0c\u786e\u7136\u662f\u6709\u4e9b\u53ef\u7591\u3002 \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u597d\uff01\u54b1\u4eec\u8c08\u5230\u8fd9\u91cc\u4e3a\u6b62\uff0c\u4e5f\u8bb8\uff0c\u6211\u7684\u770b\u6cd5\uff0c\u6709\u70b9\u9519\u8bef\uff0c\u6211\u63d0\u9192\u4f55\u5144\u7684\u76ee\u7684\uff0c\u53ea\u662f\u8981\u4f60\u5c0f\u5fc3\u4e00\u4e9b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4e24\u4eba\u7528\u8fc7\u9152\u996d\uff0c\u5206\u522b\u5f52\u623f\u5b89\u6b47\u3002 \u3000\u3000\u8fd9\u8de8\u9662\u4e2d\uff0c\u4e00\u4e3b\u4e00\u53a2\uff0c\u4f55\u5bd2\u8863\u4f4f\u4e86\u4e0a\u623f\uff0c\u534e\u5999\u771f\u4f4f\u5728\u53a2\u623f\u3002 \u3000\u3000\u534e\u5999\u771f\u7684\u63d0\u793a\uff0c\u5728\u4f55\u5bd2\u8863\u7684\u5185\u5fc3\u4e4b\u4e2d\uff0c\u53d1\u751f\u4e86\u5f88\u5f3a\u70c8\u7684\u9707\u52a8\u3002 \u3000\u3000\u4ed6\u5f00\u59cb\u56de\u5fc6\u90a3\u5e97\u5c0f\u4e8c\u7684\u4e00\u5207\u4e3e\u6b62\uff0c\u4e00\u70b9\u70b9\u4e5f\u4e0d\u653e\u8fc7\u3002 \u3000\u3000\u4ed4\u7ec6\u7684\u63a8\u6572\u8fc7\u540e\uff0c\u679c\u7136\u611f\u5230\u90a3\u5e97\u5c0f\u4e8c\u6709\u4e9b\u53ef\u7591\u3002 \u3000\u3000\u4e8c\u66f4\u65f6\u5206\uff0c\u4f55\u5bd2\u8863\u5728\u5e8a\u5934\u5e03\u7f6e\u4e86\u4e00\u756a\u3002\u6084\u7136\u63a8\u7a97\u800c\u53bb\uff0c\u8dc3\u6500\u4e0a\u5ead\u56ed\u7684\u5927\u6811\u4e0a\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4e2a\u66f4\u6b21\uff0c\u8de8\u9662\u4e2d\u4ecd\u7136\u662f\u90a3\u4e48\u5e7d\u9759\u3002 \u3000\u3000\u4e09\u590f\u4e4b\u540e\uff0c\u6574\u4e2a\u7684\u5929\u5916\u5ba2\u6808\uff0c\u90fd\u9759\u4e86\u4e0b\u6765\uff0c\u9759\u5f97\u542c\u4e0d\u5230\u4e00\u70b9\u58f0\u606f\u3002 \u3000\u3000\u7e41\u661f\u70b9\u70b9\uff0c\u95ea\u70c1\u5728\u591c\u7a7a\u4e2d\u3002 \u3000\u3000\u9759\u591c\uff0c\u7e41\u661f\uff0c\u9760\u5728\u5927\u6811\u679d\u53f6\u5bc6\u5904\u7684\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u56db\u66f4\u65f6\u5206\u4e86\uff0c\u4f55\u5bd2\u8863\u4e0d\u7981\u54d1\u7136\u5931\u7b11\u3002 \u3000\u3000\u4e5f\u8bb8\u662f\u534e\u5999\u771f\u8fc7\u8651\u4e86\uff0c\u5bf9\u4ed6\u4e5f\u6709\u4e86\u5f71\u54cd\u3002 \u3000\u3000\u6bcf\u4e00\u4e2a\u4eba\u7684\u4e3e\u6b62\uff0c\u5982\u82e5\u8981\u4ed4\u7ec6\u7684\u63a8\u6572\u8d77\u6765\uff0c\u9e21\u86cb\u91cc\u6311\u9aa8\u5934\uff0c\u603b\u53ef\u4ee5\u627e\u51fa\u4e00\u4e9b\u6bdb\u75c5\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u51b3\u5fc3\u56de\u623f\u53bb\u4e86\uff0c\u6b63\u51c6\u5907\u8dc3\u4e0b\u6811\u53bb\uff0c\u7a81\u7136\u95f4\uff0c\u8dc3\u843d\u4e0b\u4e00\u6761\u4eba\u5f71\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u4f55\u5bd2\u8863\u6697\u53eb\u4e86\u4e24\u58f0\u60ed\u6127\u3002 \u3000\u3000\u5fcd\u8010\u7684\u529f\u592b\uff0c\u7adf\u7136\u662f\u5982\u6b64\u7684\u91cd\u8981\u3002 \u3000\u3000\u53ea\u8981\u65e9\u8d70\u7247\u523b\uff0c\u8fd9\u4e00\u591c\u7684\u529f\u592b\uff0c\u5c31\u7b97\u662f\u767d\u7b49\u4e86\u3002 \u3000\u3000\u90a3\u4eba\u7a7f\u7740\u4e00\u8eab\u7684\u9ed1\u8272\u7684\u8863\u670d\uff0c\u8dc3\u843d\u5730\u9762\uff0c\u7adf\u7136\u662f\u4e0d\u95fb\u4e00\u70b9\u58f0\u606f\u3002 \u3000\u3000\u76f8\u5f53\u9ad8\u660e\u7684\u8f7b\u529f\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6253\u8d77\u7cbe\u795e\uff0c\u6697\u6697\u63d0\u805a\u771f\u6c14\uff0c\u51c6\u5907\u5e94\u53d8\u3002 \u3000\u3000\u4ed6\u867d\u7136\u5df2\u6709\u51c6\u5907\uff0c\u4f46\u534e\u5999\u771f\u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u4f4d\u9ed1\u8863\u4eba\uff0c\u662f\u90a3\u4e48\u5145\u6ee1\u7740\u81ea\u4fe1\u7684\u4eba\uff0c\u843d\u5730\u4e4b\u540e\uff0c \u76ee\u5149\u8f6c\u52a8\uff0c\u56db\u987e\u4e00\u773c\uff0c\u4e3e\u6b65\u76f4\u5411\u534e\u5999\u771f\u4f4f\u7684\u53a2\u623f\u884c\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u884c\u5230\u7a97\u524d\uff0c\u4fa7\u8033\u542c\u4e86\u4e00\u9635\uff0e\u7a81\u7136\u4f38\u624b\u5165\u6000\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u4e00\u7d27\uff0c\u6b63\u60f3\u8dc3\u843d\u6811\u4e0b\uff0c \u8033\u9645\u95f4\u5ffd\u7136\u54cd\u8d77\u4e86\u4e00\u4e2a\u8f7b\u5fae\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4f55\u5144\uff0c\u4e0d\u8981\u52a8\u3002\u201d \u3000\u3000\u58f0\u97f3\u5165\u8033\uff0c\u4f55\u5bd2\u8863\u7acb\u523b\u542c\u51fa\u6765\u4e86\uff0c\u90a3\u6b63\u662f\u534e\u5999\u771f\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5fae\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u534e\u5999\u771f\u5c31\u7aef\u5750\u5728\u4ed6\u5934\u9876\u4ee5\u4e0a\u56db\u5c3a\u5de6\u53f3\u5904\uff0c\u4e00\u4e2a\u5c94\u679d\u4e4b\u4e0a\uff0c\u671b\u7740\u4ed6\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u5fd6\u9053\uff1a\u5982\u8bba\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u770b\u6765\uff0c\u6211\u5b9e\u5728\u4e0d\u5982\u534e\u5999\u771f\uff0c\u6211\u7adf\u7136\u4e0d\u77e5\u9053\u5979\u65e9\u5df3\u5728\u5927\u6811\u4e4b\u4e0a\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u9ed1\u8863\u4eba\u7531\u6000\u4e2d\u53d6\u51fa\u4e00\u7269\uff0c\u63d2\u5165\u7a97\u4e2d\u3002 \u3000\u3000\u53cc\u65b9\u8ddd\u79bb\u867d\u7136\u4e0d\u592a\u8fdc\uff0e\u4f46\u591c\u6697\u4e4b\u4e2d\uff0c\u4f55\u5bd2\u8863\u65e0\u6cd5\u770b\u6e05\u695a\u90a3\u662f\u4e00\u4e2a\u4ec0\u4e48\u4e1c\u897f\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u9ed1\u8863\u4eba\u6536\u8d77\u90a3\u5c0f\u5de7\u4e4b\u7269\uff0c\u8f7b\u8f7b\u63a8\u7a97\u800c\u5165\u3002 \u3000\u3000\u534e\u5999\u771f\u4f4e\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u5206\u7531\u4e24\u9762\u5835\u4f4f\u4ed6\u3002\u201d \u3000\u3000\u98d8\u8eab\u800c\u4e0b\uff0c\u843d\u7740\u5b9e\u5730\uff0c \u3000\u3000\u4f55\u5bd2\u8863\u4e5f\u8ddf\u7740\u8dc3\u843d\u5730\u9762\uff0c\u5835\u5728\u53e6\u4e00\u9762\u3002 \u3000\u3000\u4f46\u90a3\u9ed1\u8863\u4eba\u884c\u5165\u4e86\u5ba4\u4e2d\u4e4b\u540e\uff0c\u5374\u65e2\u672a\u71c3\u706f\uff0c\u4e5f\u672a\u518d\u51fa\u73b0\uff0c\u4f3c\u4e4e\u662f\uff0c\u4ed6\u8fdb\u5165\u5ba4\u4e2d\u4e4b\u540e\uff0c\u5ffd\u7136\u95f4\u6d88\u5931\u3002 \u3000\u3000\u5e78\u597d\uff0c\u4f55\u5bd2\u8863\u548c\u534e\u5999\u771f\uff0c\u90fd\u6709\u5f88\u597d\u7684\u8010\u5fc3\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u5f88\u8010\u5fc3\u7684\u7b49\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5bf9\u65b9\u5165\u5ba4\u4e4b\u540e\uff0c\u5ffd\u7136\u4e0d\u95fb\u58f0\u606f\uff0c\u884c\u52a8\u83ab\u522e\u9884\u6d4b\uff0c \u663e\u7136\u5df2\u7ecf\u53d1\u89c9\u4e2d\u4e86\u8bf1\u654c\u4e4b\u8ba1\uff0c\u534e\u5999\u771f\u548c\u4f55\u5bd2\u8863\uff0c\u4e00\u65f6\u95f4\uff0c \u4e5f\u4e0d\u6562\u8fdb\u5165\u5ba4\u4e2d\u3002 \u3000\u3000\u65f6\u5149\u5728\u6c89\u5bc2\u4e2d\u6d88\u5931\u3002 \u3000\u3000\u53cc\u65b9\u6697\u4e2d\u5bf9\u5cd9\u4e86\u662f\u662f\u6709\u4e00\u987f\u996d\u7684\u529f\u592b\u4e4b\u4e45\u3002 \u3000\u3000\u8fdb\u5165\u5ba4\u4e2d\u7684\u9ed1\u8863\u4eba\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u7a81\u7136\u98de\u8eab\u4e00\u8dc3\uff0c\u76f4\u7a9c\u51fa\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u8033\u76ee\u7075\u654f\uff0c\u5bf9\u65b9\u4e00\u6709\u4e3e\u52a8\uff0c\u4f55\u5bd2\u8863\u5df2\u7ecf\u6709\u4e86\u8b66\u89c9\uff0c\u65e9\u4f5c\u6212\u5907\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u7a7f\u7a97\u800c\u51fa\u65f6\uff0c\u4f55\u5bd2\u8863\u4e5f\u7531\u4e00\u65c1\u6a2a\u91cc\u98de\u51fa\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u60ac\u7a7a\u63a5\u4e86\u4e00\u638c\uff0c\u53cc\u53cc\u843d\u7740\u5b9e\u5730\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u548c\u9ed1\u8863\u4eba\u5bf9\u8fc7\u4e86\u4e00\u638c\u4e4b\u540e\uff0c\u53f3\u624b\u5df2\u63e1\u5728\u4e86\u5251\u628a\u4e4b\u4e0a\u3002 \u3000\u3000\u53f3\u624b\u4e00\u62ac\uff0c\u957f\u5251\u51fa\u9798\u3002 \u3000\u3000\u5b9d\u5251\u4e4e\u80f8\uff0c\u6446\u51fa\u4e86\u4e00\u526f\u51fa\u51fb\u7684\u59ff\u52bf\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u672a\u80fd\u95ef\u8fc7\uff0c\u4eba\u53cd\u800c\u51b7\u9759\u4e0b\u6765\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\uff1a\u201c\u597d\u8eab\u6cd5!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5f7c\u6b64\uff0c\u5f7c\u6b64\uff0c\u9601\u4e0b\u5924\u591c\u6765\u6b64\uff0c\u4e0d\u77e5\u7528\u5fc3\u4f55\u5728?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a \u201c\u4e24\u4f4d\u6df1\u591c\u4e4b\u4e2d\uff0c\u4e0d\u5728\u623f\u91cc\u7761\u89c9\uff0c\u5374\u9b3c\u9b3c\u795f\u795f\uff0c\u8dd1\u5230\u4e86\u5916\u9762\u505a\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u8fd9\u771f\u662f\u55a7\u5bbe\u593a\u4e3b\u5b50\uff0c\u9601\u4e0b\u600e\u4e48\u79f0\u547c?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u65e0\u540d\u5c0f\u5352\uff0c\u7528\u4e0d\u7740\u901a\u540d\u62a5\u59d3\u4e86\u3002\u201d \u3000\u3000\u4e00\u4e2a\u51b7\u51b7\u7684\u58f0\u97f3\uff0c \u7531\u8eab\u540e\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a \u201c\u9601\u4e0b\u65e2\u662f\u65e0\u540d\u5c0f\u5352\uff0c\u54b1\u4eec\u4e5f\u4e0d\u591a\u95ee\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u95f7\u54fc\u4e00\u58f0\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6ca1\u6709\u7740\u5730\uff0c \u5df2\u88ab\u534e\u5999\u771f\u4f38\u624b\u631f\u8d77\uff0c\u7b11\u9053\uff1a \u201c\u4f55\u5144\uff0c\u4ea4\u7ed9\u4f60\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\uff0c\u63a5\u8fc7\u9ed1\u8863\u4eba\u4f4e\u58f0\u9053\uff1a\u201c\u600e\u4e48\u5904\u7f6e\u4ed6?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u968f\u4fbf\u4f60\u600e\u4e48\u5904\u7f6e\u5427\uff01\u201d \u3000\u3000\u4f55\u5bd2\u8863\u884c\u5165\u623f\u4e2d\uff0c\u628a\u9ed1\u8863\u4eba\u653e\u5728\u5e8a\u4e0a\uff0c\u62cd\u6d3b\u4ed6\u7a74\u9053\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7684\u7a74\u9053\u4ecd\u7136\u88ab\u70b9\uff0c\u9664\u4e86\u53e3\u8fd8\u80fd\u8a00\u4e4b\u5916\uff0c\u4ecd\u7136\u4e0d\u80fd\u52a8\u5f39\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9601\u4e0b\u597d\u597d\u60f3\u60f3\uff0c\u613f\u4e0d\u613f\u56de\u7b54\u5728\u4e0b\u51e0\u4e2a\u95ee\u9898?\u201d \u3000\u3000\u4e00\u9762\u95ee\u8bdd\uff0c\u4e00\u9762\u9664\u53bb\u4ed6\u8138\u4e0a\u7684\u9ed1\u7eb1\uff0c \u3000\u3000\u534e\u5999\u771f\u731c\u7684\u4e0d\u9519\uff0c\u8fd9\u4eba\uff0c\u679c\u7136\u6b63\u662f\u90a3\u4e2a\u7aef\u8336\u9001\u6c34\u7684\u5e97\u5c0f\u4e8c\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u4e0d\u7528\u95ee\u6211\u4ec0\u4e48\u4e86\uff0c\u6211\u4e0d\u4f1a\u56de\u7b54\u4f60\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6c89\u541f\u5b50\u4e00\u9635\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u662f\u4e0d\u60f3\u6d3b\u4e86?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u95ed\u4e0a\u4e86\u53cc\u76ee\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d!\u5728\u4e0b\u6210\u5168\u4f60\u3002\u201d \u3000\u3000\u53f3\u624b\u98df\u4e2d\u4e8c\u6307\u4e00\u6c89\uff0c\u70b9\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u7741\u5f00\u53cc\u76ee\uff0c\u8138\u4e0a\u6cdb\u8d77\u4e86\u60ca\u6016\u4e4b\u8272\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6307\u950b\u4e00\u504f\uff0c\u6ca1\u6709\u70b9\u4e2d\u90a3\u9ed1\u8863\u4eba\u7684\u6b7b\u7a74\u3002\u5374\u70b9\u4e2d\u90a3\u9ed1\u8863\u4eba\u7684\u6655\u7a74\u3002 \u3000\u3000\u534e\u5999\u771f\u7f13\u7f13\u884c\u4e86\u8fc7\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6740\u4e86\u4ed6?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u6ca1\u6709\uff0c\u6211\u60f3\u7559\u4e0b\u4ed6\u4e00\u6761\u547d\uff0c\u603b\u6bd4\u6740\u4e86\u4ed6\u597d\u4e00\u4e9b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u7684\u6b66\u529f\u4e0d\u9519\uff0c\u5982\u82e5\u5728\u6b63\u9762\u548c\u4ed6\u52a8\u624b\uff0c \u53ea\u6015\u9700\u8981\u4e00\u756a\u82e6\u6218\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u4e0d\u662f\u5e9f\u4e86\u4ed6\u7684\u6b66\u529f\uff1f\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u5982\u82e5\u5bf9\u54b1\u4eec\u6ca1\u6709\u4ec0\u4e48\u7528\u5904\u4e86\uff0c\u90a3\u5c31\u6740\u4e86\u4ed6\u7b97\u5566!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u8fd9\u4e2a\u4eba\u4e5f\u8bb8\u5f88\u574f\uff0c\u53ef\u662f\uff0c\u6211\u4eec\u4e0d\u77e5\u9053\u4ed6\u6709\u4ec0\u4e48\u6076\u8ff9?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4f55\u5144\uff0c\u5bf9\u654c\u4eba\u4e0d\u80fd\u4ec1\u6148\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53ef\u662f\uff0c\u6211\u4eec\u8fd8\u4e0d\u77e5\u9053\u4ed6\u662f\u5426\u662f\u54b1\u4eec\u7684\u654c\u4eba?\u201d \u3000\u3000\u534e\u5999\u771f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f55\u5144\uff0c\u672c\u6765\uff0e \u6211\u4e0d\u60f3\u95ee\u4f60\u5982\u4f55\u5904\u7f6e\u8fd9\u4e2a\u4eba\uff0c\u4f46\u6211\u60f3\u4e00\u60f3\uff0c\u8fd8\u662f\u6765\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u653e\u5fc3\u6211?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u4f60\u51fa\u8eab\u6b63\u5927\u95e8\u6d3e\uff0c\u867d\u7136\u5df2\u7ecf\u5728\u9634\u9633\u5821\u5386\u7ec3\u4e86\u5f88\u4e45\uff0c\u4f46\u6211\u4ecd\u7136\u62c5\u5fc3\u4f60\u4e0b\u4e0d\u4e86\u624b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5c34\u5c2c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u5012\u662f\u88ab\u4f60\u731c\u5bf9\u4e86\uff0c\u867d\u7136\uff0c\u6211\u548c\u4ed6\u4eec\u654c\u5bf9\u76f8\u5904\uff0c\u4f46\u8981\u6211\u6740\u4e00\u4e2a\u5b8c\u5168\u6ca1\u6709\u53cd\u6297\u4e4b\u529b\u7684\u4eba\uff0c\u5728\u4e0b\u5b9e\u5728\u4e0b\u4e0d\u4e86\u624b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f46\u4ed6\u73b0\u5728\u771f\u7684\u6b7b\u4e86\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a\u201c\u4e0d\u4f1a\uff0c\u6211\u53ea\u70b9\u4e86\u4ed6\u7684\u6655\u7a74\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e5f\u8bb8\u4f60\u4e0b\u624b\u91cd\u4e86\u4e00\u4e9b\uff0c\u4e5f\u8bb8\u4ed6\u65e9\u6709\u4e86\u81ea\u7edd\u7684\u51c6\u5907\uff0c\u5982\u4f55\u5904\u7f6e\u8fd9\u4e00\u5177\u5c38\u4f53\uff0c\u90a3\u5c31\u504f\u52b3\u4f55\u5144\u4e86\uff0c\u8bf4\u4e0d\u5b9a\uff0c\u6211\u4eec\u8fd8\u7528\u5f97\u7740\u4ed6\u7684\u8eab\u4efd\uff0c\u4f55\u5144\uff0c\u60f3\u51fa\u4ec0\u4e48\u5999\u8ba1\u65f6\uff0c\u518d\u548c\u5c0f\u59b9\u5546\u91cf\u3002\u201d \u3000\u3000\u8f6c\u8eab\u884c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u63a9\u4e0a\u623f\u95e8\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u4ed6\u53d8\u7684\u66f4\u7ec6\u5fc3\u4e86\uff0c\u8fd8\u52a0\u4e0a\u4e86\u95e8\u6813\u3002 \u3000\u3000\u56de\u5230\u5e8a\u4e0a\uff0c\u5c48\u6307\u4e00\u5f39\uff0c\u89e3\u4e86\u90a3\u9ed1\u8863\u4eba\u7684\u6655\u7a74\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u5982\u82e5\u80af\u8bf4\u5b9e\u8bdd\uff0c\u6211\u53ef\u4ee5\u653e\u4f60\u4e00\u6761\u751f\u8def\u2026\u2026\u201d \u3000\u3000\u9ed1\u8863\u4eba\u95ed\u76ee\u4e0d\u8bed\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9601\u4e0b\u5982\u6b64\u5014\u5f3a\uff0c\u90a3\u5c31\u522b\u602a\u6211\u5fc3\u72e0\u624b\u8fa3\u4e86\uff0c\u6211\u8981\u4f60\u5c1d\u5c1d\u5206\u7b4b\u9519\u9aa8\u7684\u6ecb\u5473\u3002\u201d \u3000\u3000\u53f3\u624b\u4e00\u63a2\uff0c\u6293\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u624b\u6307\u4e00\u89e6\u9ed1\u8863\u4eba\uff0c\u5ffd\u7136\u89c9\u51fa\u4e0d\u5bf9\uff0c\u518d\u63a2\u9f3b\u606f\uff0c\u7adf\u5df2\u6c14\u7edd\u800c\u6b7b\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5446\u4e86\u4e00\u5446\uff0c\u770b\u4e1c\u65b9\u5df2\u900f\u51fa\u9c7c\u809a\u767d\u8272\uff0c\u53ea\u597d\u6682\u628a\u5c38\u4f53\u85cf\u5165\u5e8a\u4e0b\u3002 \u3000\u3000\u4ed6\u867d\u7136\u8eab\u8d1f\u7edd\u4e16\u6b66\u529f\uff0c\u4f46\u4e00\u60f3\u5230\u5e8a\u4e0b\u653e\u4e86\u4e00\u5177\u5c38\u4f53\uff0c\u603b\u662f\u7761\u4e0d\u5b89\u7a33\u3002 \u3000\u3000\u53ea\u597d\u548c\u8863\u8eba\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u591c\uff0c\u4ed6\u5b8c\u5168\u6ca1\u6709\u5408\u773c\u3002 \u3000\u3000\u5982\u82e5\u534e\u5999\u771f\u4e0d\u662f\u5973\u7684\uff0c\u4ed6\u5b9a\u8fc7\u623f\u548c\u5979\u5e76\u5367\u4e00\u69bb\u3002 \u3000\u3000\u5e78\u597d\uff0c\u4e0d\u592a\u4e45\uff0c\u5929\u5df2\u5927\u4eae\u3002 \u3000\u3000\u800c\u4e14\uff0c\u5e97\u5c0f\u4e8c\u6765\u7684\u5f88\u65e9\u3002 \u3000\u3000\u6ca1\u6709\u5e97\u4f19\u8ba1\u8fd9\u6837\u65e9\u627e\u4e0a\u5ba2\u4eba\u623f\u4e2d\u7684\uff0c\u5e78\u597d\u4f55\u5bd2\u8863\u4eba\u5168\u65e0\u7761\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5f00\u4e86\u623f\u95e8\uff0c\u5e97\u5c0f\u4e8c\u63d0\u7740\u4e00\u58f6\u5f00\u6c34\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4ecd\u662f\u6628\u5929\u90a3\u4e2a\u5e97\u5c0f\u4e8c\uff0c\u7a7f\u7740\u90a3\u8eab\u8863\u670d\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u96be\u9053\u4e16\u4e0a\u771f\u6709\u501f\u5c38\u8fd8\u9b42\u7684\u4e8b\uff0c\u8fd8\u662f\u6709\u5982\u6b64\u76f8\u50cf\u7684\u4eba\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4ed4\u7ec6\u770b\u8fc7\u90a3\u5e8a\u4e0b\u7684\u5c38\u4f53\uff0c\u5c31\u662f\u62db\u547c\u4ed6\u4eec\u7684\u5e97\u5c0f\u4e8c\u3002 \u3000\u3000\u5e78\u597d\u5e97\u5c0f\u4e8c\u5148\u5f00\u4e86\u53e3\uff0c\u9053\uff1a \u201c\u5c0f\u59b9\u534e\u5999\u771f\uff0c\u6211\u73b0\u5728\u662f\u5e97\u5c0f\u4e8c\u5468\u4e03\u3002\u201d \u2018\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u534e\u5999\u771f\u4e00\u9762\u51b2\u6c34\uff0c\u4e00\u9762\u8bf4\u9053\uff1a \u201c\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u627e\u770b\u8fd9\u5e97\u91cc\u8fd8\u6709\u8033\u76ee\uff0c\u4e0d\u8fc7\uff0c\u5468\u4e03\u597d\u50cf\u662f\u2014\u4e2a\u5934\u5934\uff0c\u53ef\u80fd\uff0c\u8fd8\u6709\u4eba\u4f1a\u76d1\u89c6\u54b1\u4eec\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u54e6\u4e86\u4e00\u58f0\uff0c\u8f6c\u8fc7\u8eab\u5b50\uff0c\u5750\u5728\u6728\u6905\u4e0a\u3002 \u3000\u3000\u534e\u5999\u771f\u63a5\u9053\uff1a \u201c\u5e8a\u4e0b\u653e\u4e00\u5177\u5c38\u4f53\uff0e\u60f3\u6765\uff0c \u4f60\u4e00\u5b9a\u7761\u5f97\u4e0d\u597d\uff0c\u4e0d\u59a8\u5230\u5c0f\u59b9\u623f\u4e2d\u4f11\u606f\u4e00\u4e0b\uff0c\u5927\u767d\u5929\uff0c\u5927\u6982\u4e0d\u4f1a\u51fa\u4e8b\uff0c\u5982\u82e5\u6709\u4eba\u95ee\u8d77\u6211\u5c31\u8bf4\u6211\u51fa\u53bb\u770b\u670b\u53cb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u534e\u59d1\u5a18\u600e\u4e48\u77e5\u9053\uff0c\u4ed6\u53eb\u5468\u4e03\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u6628\u591c\uff0c\u6211\u6bd4\u4f60\u65e9\u4e00\u4e2a\u66f4\u6b21\u51fa\u6765\uff0c\u6362\u4e86\u53e6\u4e00\u4e2a\u5ba2\u4eba\u7684\u8eab\u4efd\uff0c\u6478\u6e05\u4e86\u4ed6\u7684\u5e95\u5b50\uff0c\u4e5f\u67e5\u51fa\u4e86\u4ed6\u7684\u4f4f\u5740\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u9ad8\u5929\u5065\u4f3c\u4e4e\u5fc3\u6709\u4e86\u51c6\u5907\uff0c\u770b\u6765\uff0e \u4ed6\u4eec\u5e03\u4e86\u4e0d\u5c11\u6697\u6869\uff0c\u6211\u60f3\u501f\u5468\u4e03\u7684\u8eab\u4efd\u6d3b\u52a8\uff0c\u4e5f\u8bb8\u8fd8\u80fd\u53d1\u73b0\u66f4\u591a\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c \u5982\u82e5\u4ed6\u4eec\u8fd9\u91cc\u8fd8\u6709\u522b\u7684\u4eba\uff0c\u6211\u60f3\u4ed6\u4eec\u5fc5\u6709\u8054\u7edc\u6697\u8bb0\uff0c\u4f60\u8981\u5c0f\u5fc3\u4e00\u4e9b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u591a\u8c22\u4f55\u5144\u5173\u5fc3\uff0c\u6211\u4f1a\u5c0f\u5fc3\u4ece\u4e8b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e8a\u4e0b\u5c38\u4f53\u2026\u2026\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u6682\u65f6\u4e0d\u7528\u7ba1\u4ed6\uff0c\u5165\u591c\u518d\u4f5c\u6253\u7b97\uff0c\u5821\u4e3b\u4e5f\u8bb8\u4eca\u591c\u5c31\u53ef\u4ee5\u8d76\u5230\uff0c\u5230\u65f6\u95f4\uff0c\u6211\u4f1a\u901a\u77e5\u4f55\u5144\uff0c\u5c0f\u59b9\u53bb\u540e\uff0c \u8bf7\u63a9\u4e0a\u623f\u95e8\uff0c\u7531\u7a97\u4e2d\u51fa\u53bb\uff0c\u5230\u5c0f\u59b9\u623f\u91cc\u597d\u597d\u517b\u606f\u4e00\u4e0b\u7cbe\u795e\u5427!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u534e\u59d1\u5a18\uff0c\u5728\u4e0b\u597d\u751f\u60ed\u6127\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u4f60\u51fa\u8eab\u540d\u95e8\uff0c\u4e0d\u77e5\u6c5f\u6e56\u4e0a\u9b3c\u872e\u4f0e\u4fe9\uff0c\u6ca1\u6709\u4ec0\u4e48\u597d\u60ed\u6127\u7684\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d!\u5728\u4e0b\u8ffd\u968f\u9ed1\u3001\u767d\u4e24\u9053\u4e2d\u7b2c\u4e00\u806a\u660e\u4eba\u9ec4\u5821\u4e3b\u7adf\u7136\u672a\u80fd\u5b66\u5f97\u4e00\u4e9b\u81ea\u536b\u4e4b\u80fd\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f55\u5144\uff0c\u8fd9\u4e0d\u80fd\u602a\u4f60\uff0c\u4e5f\u4e0d\u8868\u793a\u4f60\u7684\u806a\u660e\u624d\u667a\u4e0d\u5982\u522b\u4eba\uff0c\u91cd\u8981\u7684\u662f\u4f60\u5bf9\u4e8b\u60c5\u7684\u770b\u6cd5\uff0c\u548c\u6211\u4e0d\u540c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\u5bf9\u6599\u654c\u3001\u65ad\u4e8b\uff0c\u4e5f\u6709\u5173\u7cfb\u5417\uff1f\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5173\u7cfb\u592a\u5927\u4e86\uff0c\u4f60\u5bf9\u4eba\u5bf9\u4e8b\u7684\u6001\u5ea6\uff0c\u662f\u5f80\u597d\u5904\u60f3\uff0c\u542c\u4ee5\uff0c\u5ffd\u7565\u4e86\u5f88\u591a\u4e0d\u5229\u6211\u4eec\u7684\u5c0f\u8282\uff0c\u6211\u5bf9\u4e8b\u60c5\u7684\u770b\u6cd5\uff0c\u662f\u5148\u4ece\u574f\u5904\u60f3\uff0c\u60f3\u60f3\u770b\uff0c\u8fd9\u4e2d\u95f4\u6709\u591a\u5927\u7684\u8ddd\u79bb\uff0c\u5982\u82e5\uff0c\u6211\u4eec\u80fd\u786e\u77e5\u8fd9\u5730\u529b\u6f5c\u4f0f\u4e00\u4e2a\u654c\u4eba\uff0c\u51ed\u85c9\u667a\u6167\uff0c\u628a\u5b83\u627e\u51fa\u6765\uff0c\u8001\u5b9e\u8bf4\uff0c\u5c0f\u59b9\u5c31\u672a\u5fc5\u5982\u4f60\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4e0d\u7528\u5b89\u6170\u6211\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u7ed9\u6211\u4e00\u4e2a\u5f88\u5927\u7684\u542f\u793a\uff0c\u6211\u4eec\u8fdb\u4e86\u4fe1\u9633\u5dde\uff0c\u5c31\u63a5\u89e6\u5230\u4e86\u654c\u4eba\uff0c\u968f\u65f6\u90fd\u53ef\u80fd\u6709\u610f\u5916\u7684\u53d8\u5316\uff0c\u5fc5\u987b\u5c0f\u5fc3\u5e94\u4ed8\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u7b11\u9053\uff1a \u201c\u597d!\u8fd9\u4e2a\u6536\u83b7\u6700\u5927\uff0c\u4f60\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u6211\u8981\u53bb\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u6211\u4f1a\u5c0f\u5fc3\uff0c\u5176\u5b9e\uff0c\u4f60\u7684\u5904\u5883\uff0c \u6bd4\u6211\u5371\u9669\u5341\u500d\uff0c\u4e07\u4e00\u4ed6\u4eec\u8981\u4e0e\u4f60\u8054\u7edc\uff0c\u5f88\u53ef\u80fd\u4f1a\u7acb\u523b\u9732\u51fa\u9a6c\u811a\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u591a\u8c22\u5173\u6ce8\uff0c\u6211\u60f3\uff0c\u5929\u9ed1\u4e4b\u524d\uff0c\u5821\u4e3b\u548c\u53f6\u5144\u3001\u5c60\u9ebb\u5b50\uff0c\u4e5f\u90fd\u8be5\u8d76\u5230\u4e86\uff0c\u5509! \u3000\u3000\u53ea\u8981\u5821\u4e3b\u8d76\u5230\uff0c\u5f88\u591a\u4e8b\uff0c\u90fd\u7528\u4e0d\u7740\u54b1\u4eec\u8d39\u5fc3\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8fc7\u8eab\u5b50\uff0c\u7f13\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u63a9\u4e0a\u623f\u95e8\uff0c\u76d8\u819d\u800c\u5750\uff0c\u8fd0\u6c14\u8c03\u606f\u8d77\u6765\u3002 \u3000\u3000\u534e\u5999\u771f\u7ed9\u4e86\u4ed6\u5f88\u5927\u7684\u8b66\u60d5\uff0c\u5df2\u4f7f\u4ed6\u611f\u89c9\u5230\uff0c\u884c\u85cf\u968f\u65f6\u53ef\u80fd\u6cc4\u9732\uff0c\u4e5f\u968f\u65f6\u53ef\u80fd\u5f15\u8d77\u51b2\u7a81\uff0c\u6709\u4e00\u756a\u6fc0\u70c8\u7684\u640f\u6740\u4e4b\u6218\u3002 \u3000\u3000\u4ed6\u5fc5\u9700\u4fdd\u6301\u7740\u4f53\u80fd\uff0c\u624d\u53ef\u4ee5\u5bf9\u4ed8\u5f3a\u654c\u3002 \u3000\u3000\u4ed6\u5185\u529f\u7cbe\u6e5b\uff0c\u6f84\u6e05\u5fc3\u4e2d\u6742\u5ff5\uff0c\u7acb\u523b\u795e\u6e38\u7269\u5916\uff0c\u8fdb\u5165\u7985\u5b9a\u4e4b\u5883\u3002 \u3000\u3000\u4e0d\u77e5\u9053\u8fc7\u53bb\u4e86\u591a\u5c11\u65f6\u95f4\uff0c\u4e00\u9635\u6572\u95e8\u4e4b\u58f0\uff0c\u60ca\u9192\u4e86\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5438\u4e00\u53e3\u6c14\uff0c\u6253\u5f00\u6728\u95e8\uff0c \u534e\u5999\u771f\u75be\u5feb\u7684\u95ea\u8eab\u800c\u5165\u3002 \u3000\u3000\u56de\u8eab\u63a9\u4e0a\u6728\u95e8\uff0c\u534e\u5999\u771f\u4f4e\u58f0\u9053\uff1a \u201c\u4f55\u5144\uff0c \u6709\u4e09\u4f4d\u5ba2\u4eba\u4f4f\u8fdb\u5929\u5916\u5ba2\u6808\uff0c\u5c0f\u59b9\u8ba4\u8bc6\u5176\u4e2d\u4e24\u4e2a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u72c2\u9f99\u3001\u98de\u9e70\uff0c \u548c\u4e00\u4f4d\u5f88\u5e74\u8f7b\u7684\u5c0f\u4f19\u5b50\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u4e00\u9707\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u96be\u9053\u9ad8\u5929\u5065\u5df2\u7ecf\u542c\u5230\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u4e5f\u5f00\u59cb\u628a\u4eba\u624b\u8c03\u96c6\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c \u5f88\u53ef\u80fd\uff0c\u9ad8\u5929\u5065\u542c\u5230\u4e86\u4ec0\u4e48\u98ce\u58f0\uff0c\u628a\u4eba\u624b\u4e5f\u8c03\u96c6\u56de\u4fe1\u9633\u5dde\u6765\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u7684\u8eab\u4efd\uff0c\u662f\u5426\u88ab\u53d1\u89c9\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u5f88\u5c0f\u5fc3\uff0c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u627e\u8fd8\u672a\u88ab\u4ed6\u4eec\u6000\u7591\u5230\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e48\u770b\u6765\uff0c\u5929\u5916\u5ba2\u6808\uff0c \u4e5f\u662f\u9ad8\u5929\u5065\u7684\u8033\u76ee\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u636e\u6211\u6697\u4e2d\u8c03\u67e5\u5929\u5916\u5ba2\u6808\u3002\u5927\u4f53\u4e0a\u8bf4\uff0c\u4ed6\u4eec\u8fd8\u662f\u6b63\u5f53\u7684\u751f\u610f\u4eba\uff0c\u9ad8\u5929\u5065\u53ea\u662f\u5728\u8fd9\u4e2d\u95f4\uff0c\u5b89\u63d2\u4e86\u4e00\u4e9b\u4eba\u624b\u3002 \u4eba\u90fd\u662f\u5e97\u5c0f\u4e8c\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e5f\u53ea\u6709\u8fd9\u4e9b\u4eba\uff0c\u624d\u80fd\u63a5\u89e6\u5230\u4f4f\u5e97\u5ba2\u4eba\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5821\u4e3b\u5462\uff1f\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u6d88\u606f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u51fa\u4e8b\uff1f\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u5821\u4e3b\u4e00\u884c\uff0c\u7eb5\u7136\u51fa\u4e8b\uff0c \u4ed6\u4eec\u4e5f\u53ef\u4ee5\u5e94\u4ed8\u3002\u5c0f\u59b9\u62c5\u5fc3\u7684\u662f\u4e07\u5144\uff0c\u5982\u82e5\u9ad8\u5929\u5065\u542c\u5217\u4e86\u4ec0\u4e48\u98ce\u58f0\uff0c\u5929\u624b\u5200\u548c\u4ed6\u5e26\u7684\u4e00 \u3000\u3000\u6279\u4eba\uff0c\u5f88\u5bb9\u6613\u88ab\u4eba\u53d1\u89c9\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u770b\u72c2\u9f99\u3001\u98de\u9e70\uff0c\u548c\u90a3\u5e74\u8f7b\u4eba\uff0c \u795e\u6001\u90fd\u5f88\u8f7b\u677e\uff0c\u4e0d\u50cf\u6709\u6025\u4e8b\u7684\u6837\u5b50?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u72c2\u9f99\u548c\u98de\u9e70\u5230\u4e86\u4fe1\u9633\u5dde\u6765\uff0c\u603b\u4e0d\u4f1a\u662f\u65e0\u7f18\u65e0\u6545\u5427?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u4ee5\u8fd9\u4e24\u4e2a\u4eba\u5728\u6c5f\u6e56\u4ea1\u7684\u6c14\u52bf\uff0c\u884c\u52a8\u4e4b\u95f4\uff0c\u5fc5\u7136\u662f\u4ec6\u4ece\u5982\u4e91\uff0c\u81f3\u5c11\uff0c\u4e5f\u4f1a\u5e26\u4e24\u4e2a\u542c\u5019\u4ed6\u4eec\u4f7f\u5524\u7684\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u73b0\u5728\uff0c \u53ea\u6709\u4ed6\u4eec\u4e24\u4e2a\u4eba\u5728\u8fd9\u91cc\uff0c\u8fd9\u8bf4\u660e\u4e86\uff0c\u4ed6\u4eec\u5230\u8fd9\u91cc\u6765\uff0c\u662f\u79d8\u5bc6\u7684\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u79d8\u5bc6\u6765\u6b64?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u4ed6\u4eec\u4e24\u4e2a\u4eba\uff0c \u53ef\u4ee5\u7528\u5f88\u591a\u795e\u79d8\u65b9\u6cd5\u7792\u8fc7\u522b\u4eba\u6df7\u8fdb\u6765\uff0c\u4f46\u4ed6\u4eec\u5982\u679c\u5e26\u4e86\u4e00\u5927\u6279\u4eba\u624b\u6765\uff0c\u90a3\u5c31\u65e0\u6cd5\u7792\u8fc7\u4eba\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u5929\u5065\u4f3c\u4e4e\u662f\u5728\u62db\u96c6\u4ed6\u5c5e\u4e0b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u53ef\u80fd\u662f\u4e00\u6b21\u91cd\u8981\u7684\u4f1a\u5546\uff0c\u4e5f\u53ef\u80fd\u662f\u51c6\u5907\u96c6\u4e2d\u5168\u529b\u7684\u4e00\u51fb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u9664\u4e86\u72c2\u9f99\u3001\u98de\u9e70\u4e4b\u5916\uff0c\u4e0d\u77e5\u9053\u8fd8\u6709\u4e9b\u4ec0\u4e48\u4eba\u5230\u6b64\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f55\u5144\uff0c\u6211\u65e0\u6cd5\u77e5\u9053\u4ed6\u4eec\u96c6\u4f1a\u7684\u539f\u56e0\uff0c\u5821\u4e3b\u53ea\u544a\u8bc9\u8fc7\u6211\u4eec\uff0c\u9ad8\u5929\u5065\u9886\u5bfc\u4e86\u4e5d\u4e2a\u9ed1\u9053\u7ec4\u7ec7\uff0c\u72c2\u9f99\u548c\u98de\u9e70\uff0c\u53ea\u662f\u4e5d\u4e2a\u9ed1\u9053\u7ec4\u7ec7\u4e2d\u7684\u4e24\u4e2a\uff0c\u8fd8\u6709\u4e03\u4e2a\u7ec4\u7ec7\uff0c\u662f\u4ec0\u4e48\u4eba\u6240\u9886\u5bfc\uff0c\u6211\u4eec\u90fd\u65e0\u6cd5\u77e5\u9053\uff0e\u5c31\u7b97\u4ed6\u4eec\u6765\u4e86\uff0c\u6211\u4eec\u4e5f\u65e0\u6cd5\u8ba4\u8bc6\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e0c\u671b\u5821\u4e3b\u65e9\u4e9b\u8d76\u5230\uff0c\u4e5f\u8bb8\uff0c\u4ed6\u80fd\u591f\u60f3\u51fa\u529e\u6cd5\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f55\u5144\uff0c\u72c2\u9f99\u3001\u98de\u9e70\uff0c\u4e3a\u4eba\u5f88\u72c2\u50b2\uff0c\u5982\u662f\u548c\u4ed6\u4eec\u8eab\u4efd\u4e0d\u7b49\u7684\u4eba\uff0c\u4ed6\u4eec\u7edd\u5bf9\u4e0d\u4f1a\u5047\u4ee5\u8f9e\u8272\u3002\u201d \u3000\u3000\u53c8\u9053\uff1a \u201c\u4f46\u662f\u90a3\u4e2a\u5e74\u8f7b\u4eba\uff0c\u5374\u548c\u4ed6\u4eec\u76f8\u5904\u5728\u4e00\u5904\uff0c\u5c31\u6211\u89c2\u5bdf\u6240\u5f97\uff0c\u72c2\u9f99\u3001\u98de\u9e70\u5bf9\u90a3\u4e2a\u5e74\u8f7b\u4eba\uff0c \u4f3c\u4e4e\u662f\u8fd8\u6709\u4e9b\u5df4\u7ed3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5bf9\uff01\u7531\u72c2\u9f99\u3001\u98de\u9e70\u8eab\u4e0a\u7740\u624b\uff0c\u867d\u4e0d\u4e2d\uff0c\u4ea6\u4e0d\u8fdc\u77e3!\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u6211\u53bb\u4e86\uff0c \u4f60\u81ea\u5df1\u5c0f\u5fc3\u2014\u4e9b\uff0c\u6211\u4e0d\u77e5\u9053\u8fd9\u4e2a\u5e97\u5c0f\u4e8c\u80fd\u626e\u5230\u4ec0\u4e48\u65f6\u5019\uff0c\u968f\u65f6\u53ef\u80fd\u4f1a\u88ab\u63ed\u7a7f\u8eab\u4efd\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a \u201c\u59d1\u5a18\u5982\u679c\u88ab\u4eba\u53d1\u89c9\u65f6\uff0c\u8bf7\u907f\u5165\u6b64\u5730\uff0c \u54b1\u4eec\u8054\u624b\u5e94\u654c\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u5230\u65f6\u5019\uff0c\u770b\u60c5\u5f62\u518d\u4f5c\u51b3\u5b9a\uff0c\u6211\u4e5f\u8bb8\u4f1a\u907f\u5f00\u53bb\uff0c\u4e5f\u53ef\u80fd\u627e\u4f60\u6c42\u63f4\uff0c\u4f46\u4f55\u5144\uff0c\u8981\u81ea\u5df1\u4f5c\u6253\u7b97\uff0c\u4e07\u4e00\u6211\u8d70\u4e86\uff0c \u4f60\u8981\u5c0f\u5fc3\u4e00\u4e9b\u3002\u201d \u3000\u3000\u5979\u53cc\u76ee\u4e2d\uff0c\u5ffd\u7136\u6cdb\u73b0\u51fa\u65e0\u9650\u5173\u5207\u4e4b\u60c5\uff0c\u75be\u5feb\u7684\u8f6c\u8eab\u800c\u53bb\u3002"}),showOnHomepage:!0},{name:"\u7559\u7ed9\u4e86\u4f55\u5bd2\u8863\u4e00\u7247\u832b\u7136\u3002",content:(0,t.jsx)(t.Fragment,{children:"\u5979\u51fa\u8eab\u9ed1\u9053\uff0c\u4f46\u5374\u6781\u5177\u624d\u6167\uff0c\u5979\u6709\u7740\u6210\u719f\u7684\u7f8e\uff0c\u548c\u51b7\u5389\u5916\u8868\uff0c\u4f55\u5bd2\u8863\u81ea\u548c\u5979\u76f8\u8bc6\u4ee5\u6765\uff0c\u4ece\u6ca1\u6709\u53d1\u89c9\u5979\u5173\u5207\u8fc7\u522b\u4eba\uff0c\u5c31\u7b97\u5979\u5e2e\u52a9\u522b\u4eba\u65f6\uff0c\u4e5f\u662f\u90a3\u80a1\u51b7\u51b7\u7684\u5473\u9053\uff0c \u5979\u597d\u8c61\u53ea\u662f\u5728\u6267\u884c\u4e00\u79cd\u5de5\u4f5c\uff0c\u7edd\u4e0d\u5e26\u79c1\u4eba\u611f\u60c5\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4f55\u5bd2\u8863\u7adf\u7136\u53d1\u89c9\u4e86\u5979\u7684\u5173\u5207\u3002 \u3000\u3000\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u4f55\u5bd2\u8863\u5e26\u4e0a\u4e86\u623f\u95e8\uff0c\u7f13\u7f13\u6b65\u5165\u5385\u4e2d\u3002 \u3000\u3000\u5929\u5916\u5ba2\u6808\uff0c\u9664\u4e86\u6709\u5ba2\u623f\u4e4b\u5916\uff0c\u8fd8\u517c\u8425\u7740\u9152\u996d\u751f\u610f\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5df2\u8fd1\u5348\u65f6\u3002 \u3000\u3000\u5929\u5916\u5ba2\u6808\u7684\u5927\u5385\u4e2d\uff0c\u5df2\u4e0a\u4e86\u4e94\u6210\u5ea7\u3002 \u3000\u3000\u4ed6\u6709\u7740\u4e00\u79cd\u5f88\u5947\u602a\u7684\u611f\u89c9\uff0c\u5e0c\u671b\u770b\u5230\u534e\u5999\u771f\u3002 \u3000\u3000\u5f88\u53ef\u60dc\uff0c\u534e\u5999\u771f\u5e76\u672a\u5728\u5927\u5385\u5341\u62db\u547c\u5ba2\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u5e97\u5c0f\u4e8c\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u5382\u56db\u6837\u83dc\uff0c \u4e00\u58f6\u9152\u3002 \u3000\u3000\u4ed6\u9009\u62e9\u7684\u4f4d\u7f6e\uff0c\u662f\u5927\u5385\u4e00\u89d2\u5904\uff0c\u2014\u5f20\u5c0f\u684c\u5b50\uff0c\u6700\u4e0d\u8d77\u773c\u7684\u5730\u65b9\u3002 \u3000\u3000\u4f46\u5750\u90a3\u91cc\uff0c\u5374\u53ef\u770b\u89c1\u5230\u5927\u5385\u4e2d\u6bcf\u4e00\u4e2a\u89d2\u843d\u3002 \u3000\u3000\u5e97\u5c0f\u4e8c\u9001\u4e0a\u9152\u83dc\uff0c\u4f55\u5bd2\u8863\u81ea\u659f\u81ea\u996e\uff0c\u4e00\u9762\u6253\u91cf\u5385\u4e2d\u7684\u4eba\u7269\uff0c\u534e\u5999\u771f\u4e00\u76f4\u672a\u51fa\u73b0\uff0c\u5385\u4e2d\u4e5f\u6ca1\u6709\u770b\u5230\u72c2\u9f99\u3001\u98de\u9e70\u548c\u90a3\u5e74\u8f7b\u4eba\u3002 \u3000\u3000\u6ca1\u6709\u53d1\u73b0\u72c2\u9f99\u3001\u98de\u9e70\uff0c\u4e0d\u8db3\u4e3a\u5947\uff0c\u8fd9\u4e9b\u4eba\uff0c\u53ef\u4ee5\u8eb2\u5728\u623f\u95f4\u4e2d\u53eb\u9152\u83b1\u98df\u7528\uff0c\u4f46\u534e\u5999\u771f\u7684\u4eba\u4e0d\u51fa\u73b0\uff0c\u4f7f\u5f97\u4f55\u5bd2\u8863\u6709\u4e9b\u62c5\u5fc3\u3002 \u3000\u3000\u65f6\u5df2\u6b63\u5348\u3002 \u3000\u3000\u5ba2\u4eba\u7a81\u7136\u589e\u591a\uff0c\u672c\u6765\u53ea\u6709\u4e94\u6210\u5ea7\u7684\u5927\u5385\u7247\u523b\u95f4\u5750\u6ee1\u4e86\u5ba2\u4eba\u3002 \u3000\u3000\u7a0d\u665a\u4e00\u70b9\u7684\u5ba2\u4eba\uff0c\u5df2\u7ecf\u6ca1\u6709\u4e86\u5ea7\u4f4d\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u7a81\u7136\u6709\u4e24\u4e2a\u5ba2\u4eba\uff0c\u76f4\u5bf9\u4f55\u5bd2\u8863\u7684\u5ea7\u4f4d\u4e0a\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u5f20\u5c0f\u684c\u5b50\uff0c\u4f55\u5bd2\u8863\u53eb\u4e86\u56db\u4e2a\u83dc\uff0c \u5df2\u7ecf\u6446\u6ee1\u4e86\u5927\u534a\u4e2a\u684c\u9762\u3002 \u3000\u3000\u4f46\u90a3\u4e24\u4e2a\u5ba2\u4eba\uff0c\u5374\u671b\u4e5f\u4e0d\u671b\u4f55\u5bd2\u8863\uff0c\u5c31\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u4e0d\u60a6\uff0c\u4f46\u5374\u5fcd\u4e0b\u4e86\u6ca1\u6709\u53d1\u4f5c\u3002 \u3000\u3000\u4ed6\u5df2\u660e\u767d\uff0c\u5fcd\u8010\uff0c\u662f\u4e00\u79cd\u5f88\u9ad8\u7684\u4fee\u517b\u3002 \u3000\u3000\u4e24\u4e2a\u5ba2\u4eba\uff0c\u90fd\u7a7f\u7740\u957f\u886b\uff0c\u5e26\u7740\u6be1\u5e3d\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4eba\u7a81\u7136\u8131\u4e0b\u6be1\u5e3d\uff0c\u653e\u5728\u684c\u5b50\u4e0a\uff0c\u501f\u6be1\u5e3d\u63a9\u906e\uff0c\u53f3\u624b\u6307\u3001\u4e2d\u65e0\u540d\u4e09\u6307\uff0c\u534a\u5c48\u534a\u4f38\uff0c\u624b\u5fc3\u5fae\u5fae\u5411\u4e0a\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u9ec4\u7075\u89c4\u5b9a\u7684\u8054\u7edc\u4fe1\u53f7\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u4f4f\u5728\u4e00\u5ea7\u8de8\u9662\u4e2d\u3002\u201d \u3000\u3000\u90a3\u6253\u51fa\u6697\u53f7\u7684\u5ba2\u4eba\u63a5\u9053\uff1a \u201c\u8fd9\u4f4d\u5144\u53f0\uff0c\u8fd9\u91cc\u751f\u610f\u592a\u597d\uff0c\u5927\u5bb6\u6324\u4e00\u6324\uff0c\u4e0e\u4eba\u65b9\u4fbf\uff0c\u54b1\u4eec\u5403\u70b9\u4e1c\u897f\u5c31\u8d70\u4e86\u3002\u201d \u3000\u3000\u58f0\u97f3\u5f88\u964c\u751f\uff0c\u4f55\u5bd2\u8863\u542c\u4e0d\u51fa\u662f\u4ec0\u4e48\u4eba\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u62db\u8fc7\u5e97\u5c0f\u4e8c\uff0c\u7ed3\u8fc7\u5e10\uff0c\u8d77\u8eab\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u73b0\u5728\uff0c\u53ea\u77e5\u9053\u6709\u9634\u9633\u5821\u7684\u4eba\uff0c\u5230\u4e86\u5929\u5916\u5ba2\u6808\uff0c\u90a3\u4eba\u4e0d\u662f\u9ec4\u7075\uff0c\u4e5f\u4e0d\u662f\u53f6\u957f\u9752\u548c\u5c60\u65e0\u65b9\uff0c\u6ca1\u6709\u4f20\u6765\u4efb\u4f55\u6d88\u606f\uff0c\u53ea\u662f\u793a\u610f\u4f55\u5bd2\u8863\u5c3d\u5feb\u79bb\u5f00\u5927\u5385\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u79bb\u5f00\u4e86\u5927\u5385\uff0c\u53ea\u6709\u4e00\u4e2a\u53bb\u5904\uff0c\u90a3\u5c31\u662f\u56de\u5230\u4ed6\u4f4f\u7684\u8de8\u9662\u623f\u91cc\u3002 \u3000\u3000\u8de8\u9662\u4e2d\uff0c\u4ecd\u7136\u4fdd\u6301\u4e86\u76f8\u5f53\u7684\u5e7d\u9759\uff0c\u4ed6\u76f8\u4fe1\uff0c\u534e\u5999\u771f\u5982\u82e5\u5728\u6b64\uff0c\u4e00\u5b9a\u4f1a\u5f88\u5bb9\u6613\u89e3\u51b3\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u81f3\u5c11\uff0c\u5979\u4f1a\u63d0\u51fa\u4e00\u4e2a\u5f88\u5177\u4f53\u7684\u7ed3\u8bba\u3002 \u3000\u3000\u5979\u662f\u4e2a\u5f88\u806a\u660e\u7684\u4eba\uff0c\u52a0\u4e0a\u4e30\u5bcc\u7684\u6c5f\u6e56\u7ecf\u9a8c\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u534e\u5999\u771f\u6ca1\u51fa\u73b0\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u53d6\u8fc7\u8336\u676f\uff0c\u5012\u4e00\u676f\u8336\uff0c\u559d\u4e0b\u53bb\u3002 \u3000\u3000\u8336\u5165\u8179\u4e2d\uff0c\u7acb\u523b\u611f\u89c9\u5230\u4e0d\u5bf9\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u592a\u665a\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u7acb\u523b\u611f\u89c9\u90a3\u662f\u4e00\u79cd\u5f88\u5f3a\u70c8\u7684\u6bd2\u836f\uff0c\u800c\u4e14\uff0c\u6548\u679c\u7acb\u7aff\u89c1\u5f71\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7acb\u523b\u611f\u89c9\u5230\uff0c\u771f\u6c14\u4e0d\u7ee7\uff0c\u597d\u50cf\u5ffd\u7136\u95f4\u5931\u53bb\u4e86\u4e8c\u5341\u5e74\u82e6\u7ec3\u7684\u6b66\u529f\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5173\u95ed\u7684\u6728\u95e8\uff0c\u7a81\u7136\u63a8\u5f00\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u4e8c\u5341\u56db\u4e94\u7684\u5e74\u8f7b\u4eba\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u90a3\u4eba\u7684\u8138\u4e0a\uff0c\u5e26\u7740\u4e00\u62b9\u6de1\u6de1\u7684\u7b11\u610f\uff0c\u90a3\u662f\u4e00\u79cd\u5f88\u8bbd\u523a\u7684\u7b11\u610f\u3002 \u3000\u3000\u5e74\u8f7b\u4eba\u7a7f\u7740\u4e00\u4ef6\u9752\u7ef8\u5b50\u957f\u886b\uff0c\u8170\u4e2d\u675f\u4e0b\u4e00\u6761\u96ea\u767d\u7684\u5e26\u5b50\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5c3d\u91cf\u4fdd\u6301\u5e73\u9759\uff0c\u5e0c\u671b\u80fd\u63a9\u53bb\u4e2d\u6bd2\u7684\u5f81\u8c61\u3002 \u3000\u3000\u9752\u8863\u5e74\u8f7b\u4eba\uff0c\u4e24\u9053\u51b7\u5389\u7684\u773c\u5149\uff0c\u6253\u91cf\u4e86\u4f55\u5bd2\u8863\u4e00\u9635\uff0c\u7a81\u7136\u54c8\u54c8\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6545\u4f5c\u9547\u9759\u5730\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48?\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f60\u53eb\u4f55\u5bd2\u8863\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u55ef!\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u91d1\u5251\u98de\u8f6e\u4f55\u5bd2\u8863\uff0c\u51fa\u8eab\u6b66\u5f53\uff0c\u4e0d\u9519\u5427?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7f13\u7f13\u7ad9\u8d77\u4e86\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u4f60\u51fa\u8eab\u6b63\u5927\u95e8\u6237\uff0c\u53ef\u77e5\u9053\u6740\u4eba\u507f\u547d\u8fd9\u53e5\u8bdd?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6\u2026\u2026\u201d \u3000\u3000\u9752\u8863\u4eba\u63a5\u9053\uff1a \u201c\u4f60\u6740\u4e86\u4e00\u4e2a\u5e97\u5c0f\u4e8c\uff0c\u5c38\u4f53\u5c31\u5728\u4f60\u7684\u5e8a\u4e0b\uff0c\u6211\u8981\u628a\u4f60\u9001\u4ea4\u5b98\u5e9c\u6cbb\u7f6a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9601\u4e0b\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba?\u53ef\u4ee5\u89c1\u544a\u5417?\u201d \u3000\u3000\u9752\u8863\u4eba\u7b11\u9053\uff1a\u201c\u6211\u662f\u8c01\uff0c\u5f88\u91cd\u8981\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u4e0b\u5e94\u8be5\u4e86\u89e3\uff0c\u4f60\u6709\u6ca1\u6709\u8d44\u683c\u8ddf\u6211\u8bf4\u8fd9\u4e9b\u8bdd?\u201d \u3000\u3000\u9752\u8863\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u73b0\u5728\uff0c\u4e0d\u8bba\u4ec0\u4e48\u4eba\u90fd\u53ef\u4ee5\u548c\u4f60\u8bf4\u8bdd\uff0c\u4e0d\u8bba\u4ec0\u4e48\u4eba\u90fd\u53ef\u4ee5\u548c\u4f60\u52a8\u624b\u4e86\uff0c\u56e0\u4e3a\uff0c\u5c31\u7b97\u662f\u4e00\u4e2a\u666e\u901a\u7684\u4eba\uff0c\u4e5f\u53ef\u4ee5\u548c\u4f60\u6253\u4e00\u67b6\uff0c\u800c\u4e14\uff0c\u4e00\u5b9a\u53ef\u4ee5\u6253\u5f97\u8fc7\u4f60\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u670d\u7684\u662f\u4ec0\u4e48\u836f\u7269?\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u8f6f\u9aa8\u6d88\u529f\u6563\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5f88\u6076\u6bd2\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a \u201c\u800c\u4e14\uff0c\u4e5f\u5f88\u5b9e\u7528\uff0c\u4e00\u4e2a\u4eba\u98df\u4e0b\u8fd9\u79cd\u836f\u7269\u4e4b\u540e\uff0c\u771f\u7684\u662f\u9aa8\u8f6f\u529f\u6d88\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5927\u4e08\u592b\u5934\u53ef\u65ad\u3001\u8840\u53ef\u6d41\uff0c\u4e0d\u5bb9\u7f9e\u8fb1\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u6717\u6717\u4e00\u7b11\u9053\uff1b\u201c\u4f55\u5bd2\u8863\uff0c\u4f60\u5f88\u6015\u7f9e\u8fb1\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6740\u5250\u4efb\u51ed\u5904\u7f6e\uff0c\u9601\u4e0b\u53ef\u4ee5\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u6697\u4e2d\u8fd0\u6c14\u76f8\u8bd5\uff0c\u5df2\u786e\u77e5\u771f\u6c14\u96be\u63d0\uff0c\u65e0\u6cd5\u548c\u4eba\u518d\u52a8\u624b\u4e86\u3002 \u3000\u3000\u9752\u8863\u4eba\u6447\u6447\u5934\uff0c\u9053\uff1a \u201c\u4f55\u5bd2\u8863\uff0c\u6211\u4e0d\u4f1a\u6740\u4f60\uff0c\u6740\u4e86\u4f60\uff0c\u5c82\u4e0d\u662f\u592a\u4fbf\u5b9c\u4e86\u4f60\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u3001\u4f60\u2026\u2026\u201d \u3000\u3000\u9752\u8863\u5e74\u8f7b\u4eba\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u4f60\u4e0d\u7528\u77aa\u773c\uff0c\u4f60\u73b0\u5728\u662f\u7827\u4e0a\u4e4b\u8089\uff0c\u6211\u9ad8\u5174\u600e\u4e48\u5bb0\u5272\uff0c\u5c31\u600e\u4e48\u5bb0\u5272\u4f60\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5782\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u5145\u6ee1\u4e86\u6fc0\u6124\u3001\u75db\u6068\uff0c\u4f46\u4ed6\u5374\u65e0\u6cd5\u53d1\u4f5c\u3002 \u3000\u3000\u4ed6\u5df2\u6697\u81ea\u8bd5\u8fc7\uff0c\u5c31\u7b97\u60f3\u81ea\u7edd\uff0c\u4e5f\u662f\u6709\u6240\u4e0d\u80fd\u4e86\u3002 \u3000\u3000\u9752\u8863\u5e74\u8f7b\u4eba\u597d\u6574\u4ee5\u6687\u5730\u5728\u4f55\u5bd2\u8863\u5bf9\u9762\u5750\u4e0b\uff0c\u9053\uff1a \u201c\u4f60\u542c\u7740\uff0c\u4f55\u5bd2\u8863\uff0c\u4f60\u53ea\u6709\u4e00\u4e2a\u529e\u6cd5\uff0c\u53ef\u4ee5\u9003\u907f\u6b7b\u4ea1\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4e0d\u6015\u6b7b\uff01\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4f46\u6211\u4f1a\u7f9e\u8fb1\u4f60\uff0c\u4f7f\u4f60\u6c42\u6b7b\u4e0d\u5f97\uff0c\u6c42\u751f\u4e0d\u80fd\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5389\u58f0\u5927\u559d\u9053\uff1a\u201c\u4e0d\u884c\u2026\u2026\u201d \u3000\u3000\u9752\u8863\u4eba\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u884c\uff0c\u7ed9\u6211\u8dea\u4e0b\u3002\u201d \u3000\u3000\u53f3\u624b\u5fae\u5fae\u4e00\u5e26\uff0c\u5de6\u624b\u6c89\u8155\u6a2a\u5207\uff0c\u4f55\u5bd2\u8863\u8eab\u4e0d\u81ea\u4e3b\u7684\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u672a\u53d7\u8fc7\u5982\u6b64\u7684\u59d4\u5c48\uff0c\u987f\u6709\u7740\u751f\u4e0d\u5982\u6b7b\u4e4b\u611f\u3002 \u3000\u3000\u54ac\u54ac\u7259\u7ad9\u4e86\u8d77\u6765\uff0c\u7528\u5c3d\u6240\u6709\u7684\u6c14\u529b\uff0c\u5411\u5899\u58c1\u4e0a\u649e\u53bb\u3002 \u3000\u3000\u53ea\u89c9\u8863\u9886\u88ab\u4eba\u6293\u4f4f\uff0c\u7528\u529b\u4e00\u5e26\uff0c\u751f\u751f\u88ab\u62c9\u4e86\u56de\u6765\u3002 \u3000\u3000\u662f\u7684\uff0c\u4ed6\u8fde\u53d6\u6b7b\u7684\u80fd\u529b\uff0c\u4e5f\u5df2\u7ecf\u6ca1\u6709\u4e86\uff0c\u4e0d\u89c9\u82f1\u96c4\u6cea\u4e0b\uff0c\u6eda\u843d\u4e24\u816e\u3002 \u3000\u3000\u8c01\u8bf4\u82f1\u96c4\u4e0d\u5f39\u6cea\uff0c\u53ea\u7f18\u672a\u5230\u4f24\u5fc3\u5904c \u3000\u3000\u9752\u8863\u4eba\u5de6\u53f3\u5f00\u5f13\uff0c\u4e52\u4e53\u4e52\u4e53\u4e24\u8bb0\u8033\u5149\uff0c\u6253\u5f97\u4f55\u5bd2\u8863\u53e3\u89d2\u6d8c\u8840\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u4f55\u5bd2\u8863\uff0c\u4f60\u542c\u7740\uff0c\u5c31\u7b97\u6211\u4e0d\u62c9\u4f60\uff0c \u4f60\u4e5f\u649e\u4e0d\u6b7b\uff0c\u4e00\u4e2a\u670d\u7528\u4e0b\u8f6f\u9aa8\u6d88\u529f\u6563\u7684\u4eba\uff0c\u5c31\u50cf\u4e00\u4e2a\u5927\u75c5\u521d\u6108\u7684\u4eba\uff0c\u4ed6\u5bf9\u75db\u82e6\u7684\u5fcd\u53d7\uff0c\u4e5f\u6709\u7740\u5f88\u5927\u7684\u6539\u53d8\uff0c \u4f60\u5df2\u7ecf\u4e0d\u662f\u4ee5\u524d\u7684\u91d1\u5251\u98de\u8f6e\uff0c\u4f60\u5bf9\u75db\u82e6\u7684\u5fcd\u53d7\u529b\uff0c \u51e0\u4e4e\u4e0d\u5982\u4e00\u4e2a\u666e\u901a\u4eba\uff0c\u4f60\u662f\u6b7b\u4e0d\u4e86\uff0c\u6211\u4eec\u4e5f\u4e0d\u8ba9\u4f60\u6b7b\uff0c\u4f60\u8981\u6d3b\u7740\u7b54\u590d\u6211\u4eec\u7684\u95ee\u8bdd\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u8c22\u8c22\u6307\u6559\u3002\u201d \u3000\u3000\u7f13\u7f13\u95ed\u4e0a\u53cc\u76ee\u3002 \u3000\u3000\u90a3\u6de1\u6de1\u7684\u7b11\u5bb9\uff0c \u542b\u6709\u7740\u5f3a\u70c8\u7684\u6f20\u89c6\u4e0e\u8f7b\u85d0\uff0c \u4e5f\u6709\u4e00\u80a1\u81ea\u6211\u8ba5\u5632\u5473\u9053\u3002 \u3000\u3000\u52d8\u7834\u751f\u6b7b\u7684\u4e00\u7b11\uff0c\u7b11\u5f97\u662f\u90a3\u4e48\u51c4\u8ff7\uff0c\u90a3\u4e48\u6006\u51c9\u548c\u65e0\u5948\u3002 \u3000\u3000\u9752\u8863\u4eba\u6709\u4e9b\u6124\u6012\u5730\u53eb\u9053\uff1a \u201c\u4f55\u5bd2\u8863\uff0c\u542c\u6e05\u695a\uff0c \u4f60\u4eec\u6709\u51e0\u4e2a\u4eba\u5230\u4e86\u8fd9\u91cc\uff0c\u4f60\u90a3\u4f4d\u5192\u5145\u6b7b\u8005\u7684\u540c\u4f34\u54ea\u91cc\u53bb\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6697\u6697\u5fd6\u9053\uff1a\u8fd8\u597d\uff0c\u534e\u5999\u771f\u5df2\u7ecf\u9003\u51fa\u4e86\u9669\u5730\uff0e\u81f3\u5c11\uff0c\u6ca1\u6709\u843d\u5728\u4ed6\u4eec\u624b\u4e2d\u3002 \u3000\u3000\u9752\u8863\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u542c\u5230\u6ca1\u6709?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7741\u5f00\u773c\u775b\uff0c\u5e73\u9759\u5730\u8bf4\u9053\uff1a \u201c\u6211\u4f53\u4f1a\u5230\u4e86\u6b7b\u4ea1\u5473\u9053\uff0c\u4f60\u6709\u4ec0\u4e48\u6076\u6bd2\u7684\u624b\u6cd5\uff0c\u5c3d\u7ba1\u65bd\u5c55\uff0c\u6211\u4e0d\u4f1a\u56de\u7b54\u4f60\u2014\u53e5\u6d3b\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u6709\u79cd\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u4f60\u4eec\u7684\u624b\u6bb5\u5f88\u9634\u9669\uff0c\u4f46\u4f60\u5374\u5f88\u80c6\u5c0f\u3002\u201d \u3000\u3000\u9752\u8863\u4eba\u4e00\u62ac\u624b\uff0c\u8f7b\u8f7b\u4e00\u638c\uff0c\u628a\u4f55\u5bd2\u8863\u6253\u5f97\u8fde\u4eba\u5e26\u6905\u5b50\uff0c\u7ffb\u5012\u5728\u5730\u4e0a\uff0c\u6012\u9053\uff1a\u201c\u4f60\u8bf4\u6e05\u695a\u4e00\u4e9b\uff0c\u6211\u54ea\u91cc\u80c6\u5c0f\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7f13\u7f13\u4ece\u5730\u4e0a\u722c\u4e86\u8d77\u6765\uff0c\u4ed6\u722c\u5f97\u5f88\u5403\u529b\uff0c\u62cd\u62cd\u8eab\u4e0a\u7684\u5c18\u571f\uff0c\u53c8\u5728\u53e6\u4e00\u5f20\u6905\u5b50\u4e0a\u5750\u4e0b\uff0c\u7b11\u9053\uff1a \u201c\u4f60\u53ef\u4ee5\u6298\u78e8\u6211\uff0c\u4f46\u4f60\u5374\u4e0d\u6562\u628a\u59d3\u540d\u544a\u8bc9\u6211?\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u597d!\u544a\u8bc9\u4f60\uff0c\u6211\u59d3\u9ad8\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a\u201c\u9ad8\u5929\u5065\u7684\u513f\u5b50?\u201d \u3000\u3000\u9752\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9ad8\u98de\u8679\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5f88\u597d\u542c\u7684\u540d\u5b57\uff0c\u4e00\u8868\u4eba\u624d\uff0c\u53ea\u53ef\u60dc\u548c\u4f60\u7239\u4e00\u6837\uff0c\u8868\u91cc\u4e0d\u4e00\uff0c\u4ee5\u5916\u8c8c\u7684\u5f69\u4e3d\uff0c\u63a9\u906e\u4e86\u5185\u5728\u4e11\u6076\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u626c\u8d77\u4e86\u53f3\u638c\uff0c\u4f46\u4ed6\u6ca1\u6709\u5288\u4e0b\u53bb\uff0c\u5374\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u662f\u4e0d\u662f\u60f3\u6fc0\u6012\u6211\uff0c\u4e00\u638c\u628a\u4eff\uff1b\u5288\u6b7b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u867d\u7136\u4e0d\u6015\u6b7b\uff0c\u4f46\u8fd8\u60f3\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u8981\u770b\u5230\u4f60\u4eec\u62a5\u5e94\u4e34\u5934\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a \u201c\u522b\u8bf4\u8fd9\u662f\u4e00\u4ef6\u4e0d\u53ef\u80fd\u7684\u4e8b\uff0c\u7eb5\u7136\u53ef\u80fd\uff0c\u53ea\u6015\u4f60\u4e5f\u770b\u4e0d\u5230\u4e86\u3002\u201d \u201c\u8c01\u8bf4\u7684!\u201d \u3000\u3000\u4e00\u4e2a\u51b7\u51b7\u7684\u58f0\u97f3\uff0c\u7a81\u7136\u7531\u9ad8\u98de\u8679\u7684\u8eab\u540e\uff0c\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u9ad8\u98de\u8679\u8f6c\u8eab\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u8eab\u7740\u9ed1\u8863\u957f\u888d\u7684\u4eba\uff0c\u5c31\u7ad9\u5728\u8eab\u540e\u4e0d\u8fdc\u5904\u3002 \u3000\u3000\u4ee5\u9ad8\u98de\u8679\u7684\u8033\u76ee\u4e4b\u7075\uff0c\u7adf\u7136\u4e0d\u77e5\u4eba\u5bb6\u4f55\u65f6\u8fdb\u5165\u4e86\u5ba4\u4e2d\u3002 \u3000\u3000\u8138\u4e0a\u95ea\u63a0\u8fc7\u4e00\u4e1d\u60ca\u8bb6\uff0c\u4f46\u7acb\u523b\u53c8\u6062\u590d\u4e86\u9547\u9759\uff0c\u9053\uff1a \u201c\u4f60\u662f\u8c01?\u201d \u3000\u3000\u4e09\u4e2a\u5b57\u7684\u58f0\u97f3\u5f88\u9ad8\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u9ad8\u516c\u5b50\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u6765\u4e86\uff0c\u4f60\u4e0d\u7528\u591a\u8d39\u5fc3\u673a\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5df2\u7ecf\u542c\u51fa\u4e86\u6765\u4eba\u7684\u58f0\u97f3\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u9ad8\u516c\u5b50\uff0c\u516d\u6708\u503a\uff0c\u8fd8\u5f97\u5feb\uff0c\u4f60\u521a\u624d\u5bf9\u6211\u4f55\u67d0\u4eba\u7684\u795e\u6c14\u3002\u54ea\u91cc\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u6012\u9053\uff1a \u201c\u4f55\u5bd2\u8863\uff0c \u4f60\u5982\u6562\u518d\u4ece\u4e2d\u63d2\u53e3\uff0c\u6211\u5c31\u4e00\u638c\u6d3b\u5288\u4e86\u4f60\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a \u201c\u9ad8\u516c\u5b50\uff0c \u4f60\u6ca1\u6709\u8fd9\u79cd\u673a\u4f1a\uff0c\u5e0c\u671b\u4f60\u522b\u592a\u51b2\u52a8\u4e86!\u201d \u3000\u3000\u9ad8\u98de\u8679\u51b7\u51b7\u63a5\u9053\uff1a \u201c\u5c31\u7b97\u4f60\u4eec\u5236\u670d\u4e86\u72c2\u9f99\u3001\u98de\u9e70\uff0c \u4e5f\u672a\u5fc5\u80fd\u5bf9\u4ed8\u672c\u516c\u5b50\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u597d!\u9ad8\u516c\u5b50\u613f\u610f\u8bd5\uff0c\u4e0d\u59a8\u51fa\u624b\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4f60\u8981\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a \u201c\u9ad8\u516c\u5b50\uff0c \u4e5f\u8bb8\u4f60\u5f88\u81ea\u8d1f\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u786e\u5b9e\u6ca1\u6709\u628a\u4f60\u770b\u5728\u773c\u91cc\uff0c\u6211\u4eec\u8981\u5bf9\u4ed8\u7684\u4eba\uff0c\u662f\u4f60\u8001\u5b50\uff0c\u4ea4\u51fa\u89e3\u836f\uff0c\u5c31\u653e\u4f60\u79bb\u5f00\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\uff0c\u6211\u4f1a\u76f8\u4fe1\u5417\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u4f60\u4e0d\u80af\u81ea\u52a8\u4ea4\u51fa\u89e3\u836f\uff0e\u6211\u4eec\u53ea\u6709\u4e0b\u624b\u62a2\u593a\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4e0b\u624b\u62a2\u593a\uff0c\u89e3\u836f\u4e00\u5b9a\u4f1a\u5728\u6211\u7684\u8eab\u8fb9\u5417?\u4f60\u6709\u628a\u63e1\u62a2\u5f97\u53bb\u5417?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u662f\u5426\u62a2\u5f97\u53bb\uff0c\u4f60\u4f55\u4e0d\u7528\u5fc3\u60f3\u4e00\u60f3\uff0c\u72c2\u9f99\u3001\u98de\u9e70\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u4f46\u6211\u4eec\u5df2\u7ecf\u5b8c\u5168\u628a\u4e24\u4eba\u5236\u670d\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u70b9\uff0c\u9ad8\u98de\u8679\u5012\u662f\u4fe1\u4e86\uff0c\u5982\u82e5\u72c2\u9f99\u3001 \u98de\u9e70\u6ca1\u6709\u53d7\u5236\uff0c\u4ed6\u4eec\u65e9\u5e94\u8be5\u8d76\u6765\u63f4\u52a9\u4e86\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u7a81\u7136\u4e00\u95ea\u8eab\u8eaf\uff0c\u4e0d\u77e5\u600e\u7684\u7adf\u7136\u6321\u5728\u4e86\u4f55\u5bd2\u8863\u7684\u8eab\u524d\u3002 \u3000\u3000\u8fd9\u56fa\u7136\u662f\u4e3a\u4e86\u4fdd\u62a4\u4f55\u5bd2\u8863\uff0c\u751f\u6050\u9ad8\u98de\u8679\u4ee5\u4f55\u5bd2\u8863\u7684\u751f\u547d\uff0c\u4f5c\u4e3a\u8981\u631f\uff0c\u4f46\u4e5f\u7b49\u4e8e\u9732\u4e86\u2014\u624b\u3002 \u3000\u3000\u9ad8\u98de\u8679\u5fc3\u4e2d\u4e5f\u6b63\u5728\u76d8\u7b97\uff0c\u5e94\u8be5\u5982\u4f55\u5bf9\u4ed8\uff0e\u773c\u770b\u7740\u9ed1\u8863\u4eba\u5ffd\u7136\u8ba9\u5f00\u4e86\u53bb\u8def\uff0c\u7acb\u65f6\u4e00\u4e2a\u7bad\u6b65\uff0c\u5411\u5916\u7a9c\u4e86\u53bb\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u58f0\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u56de\u53bb\u3002\u201d\u4e00\u80a1\u5f3a\u52b2\u65e0\u5339\u7684\u6f5c\u529b\uff0c\u8fce\u9762\u51fb\u6765\u3002 \u3000\u3000\u539f\u6765\u95e8\u5916\u4e4b\u4eba\u65e9\u5df2\u6709\u4e86\u57cb\u4f0f\u3002 \u3000\u3000\u9ad8\u98de\u8679\u53f3\u624b\u4e00\u62ac\uff0c\u63a5\u4e0b\u4e86\u4e00\u638c\u3002 \u3000\u3000\u95e8\u5916\u7684\u638c\u529b\u96c4\u6d51\uff0c\u9ad8\u98de\u8679\u7adf\u88ab\u90a3\u4e00\u638c\u7ed9\u903c\u9000\u4e86\u4e09\u6b65\uff0c\u95e8\u53e3\u5904\uff0c\u7acb\u523b\u53c8\u51fa\u73b0\u4e86\u53e6\u4e00\u4e2a\u9ed1\u8863\u4eba\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u90fd\u7a7f\u7740\u9ed1\u8272\u7684\u8863\u670d\uff0c\u989c\u8272\u4e00\u6837\uff0c\u6b3e\u5f0f\u4e5f\u4e00\u6837\uff0c\u8138\u4e0a\u4e0d\u89c1\u4e00\u70b9\u8840\u8272\u3002 \u3000\u3000\u9ad8\u98de\u8679\u7684\u6c5f\u6e56\u7ecf\u9a8c\u867d\u7136\u4e0d\u591a\uff0c\u4f46\u4e5f\u770b\u5f97\u604d\u7136\u5927\u609f\u3002 \u3000\u3000\u539f\u6765\uff0c\u8fd9\u4e24\u4e2a\u9ed1\u8863\u4eba\u90fd\u6234\u7740\u4eba\u76ae\u9762\u5177\u3002 \u3000\u3000\u9ad8\u98de\u8679\u611f\u89c9\u5230\u4e8b\u6001\u4e25\u91cd\u4e86\u3002 \u3000\u3000\u4ed6\u521a\u624d\u548c\u7b2c\u4e8c\u4e2a\u9ed1\u8863\u4eba\u5bf9\u4e86\u4e00\u638c\uff0c \u5df2\u611f\u89c9\u5230\u90a3\u9ed1\u8863\u4eba\u7684\u5185\u529b\uff0c\u7edd\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\u3002 \u3000\u3000\u4f46\u4ed6\u76f4\u89c9\u7684\u611f\u53d7\u4e2d\uff0c\u7b2c\u4e00\u4e2a\u51fa\u73b0\u7684\u9ed1\u8863\u4eba\uff0c\u6b66\u529f\u66f4\u9ad8\u660e\u4e00\u4e9b\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u9ed1\u8863\u4eba\uff0c\u7f13\u7f13\u64a9\u8d77\u4e86\u9ed1\u886b\u7684\u8863\u895f\uff0c\u624b\u4e2d\u7acb\u523b\u591a\u4e86\u4e00\u67c4\u957f\u5251\u3002 \u3000\u3000\u5bd2\u5149\u4e00\u95ea\uff0c\u957f\u5251\u6307\u5411\u4e86\u9ad8\u98de\u8679\u3002 \u3000\u3000\u9ad8\u98de\u8679\u81ea\u5bf9\u65b9\u7684\u76ee\u5149\u4e2d\u770b\u5230\u4e86\u6d53\u91cd\u7684\u6740\u673a\uff0c\u548c\u611f\u89c9\u5230\u5bf9\u65b9\u51fa\u5251\u7684\u971c\u6c14\u3002 \u3000\u3000\u4e00\u80a1\u5bd2\u610f\uff0c\u7531\u5fc3\u5e95\u76f4\u6cdb\u4e0a\u6765\u3002 \u3000\u3000\u201c\u4eae\u5175\u5203\uff0c\u5728\u4e0b\u4e0d\u613f\u6740\u4e00\u4e2a\u624b\u4e2d\u4e0d\u63e1\u5200\u5251\u7684\u4eba\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u4e2a\u9ed1\u8863\u4eba\u51b7\u5389\u7684\u58f0\u97f3\uff0c\u4f20\u5165\u8033\u9645\u3002 \u3000\u3000\u7b2c\u4e00\u4e2a\u9ed1\u8863\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u63a5\u9053\uff1a \u201c\u4f60\u552f\u4e00\u81ea\u6551\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u4ea4\u51fa\u89e3\u836f\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u6253\u91cf\u4e86\u4e00\u4e0b\u76ee\u524d\u7684\u5f62\u52bf\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u4ea4\u51fa\u89e3\u836f\u4e4b\u540e\u5462?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a \u201c\u653e\u4f60\u79bb\u53bb\uff0c\u9ad8\u5929\u5065\u8981\u4f60\u51fa\u6765\u8d70\u52a8\u4e00\u756a\uff0c\u4e0d\u8fc7\u662f\u8981\u4f60\u7a0d\u5fae\u957f\u957f\u89c1\u8bc6\uff0e\u4f46\u4f60\u5374\u53d1\u73b0\u4e86\u4e00\u4e2a\u91cd\u5927\u7684\u79d8\u5bc6\uff0c\u5355\u51ed\u8fd9\u4e00\u70b9\uff0c\u5c31\u8db3\u4ee5\u4f7f\u4f60\u626c\u7709\u5410\u6c14\u4e86\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4ec0\u4e48\u79d8\u5bc6?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6211\u4eec\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a \u201c\u4f55\u5bd2\u8863\u5e26\u4eba\u5230\u8fd9\u5929\u5916\u5ba2\u6808\u65f6\uff0c\u6211\u4eec\u5df2\u7ecf\u53d1\u89c9\u4e86\u4ed6\u7684\u8eab\u4efd!\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4e24\u4e2a\u4eba\u540c\u6765\uff0c\u4f60\u4eec\u77e5\u9053\u90a3\u4e2a\u4eba\u662f\u8c01?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u90a3\u4e2a\u662f\u8c01\uff0c\u5e76\u4e0d\u91cd\u8981\uff0c\u6211\u4eec\u65e2\u7136\u53d1\u89c9\u4e86\u4f55\u5bd2\u8863\uff0c\u5c31\u4f1a\u627e\u5230\u4ed6\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u627e\u5230\u4e86\u5417\uff1f\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u8fd9\u8fd8\u6ca1\u6709\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a \u201c\u4f60\u627e\u4e0d\u5230\u7684\uff0c\u5c31\u7b97\u4f60\u627e\u5230\u4e86\uff0c \u4f60\u4e5f\u65e0\u6cd5\u77e5\u6653\u4ed6\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4f46\u6211\u5236\u4f4f\u4e86\u4f55\u5bd2\u8863\uff0c\u90a3\u5df2\u7ecf\u591f\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u786e\u5b9e\u662f\u6211\u4eec\u7684\u2014\u4e2a\u758f\u5ffd\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u4eec\u4e5f\u60f3\u4e0d\u5230\u5802\u5802\u4e2d\u5dde\u7b2c\u2014\u5200\u7684\u5927\u516c\u5b50\uff0c\u7adf\u7136\u4f1a\u7528\u90a3\u4e48\u5351\u52a3\u7684\u624b\u6bb5\u5bb3\u4eba!\u201d \u3000\u3000\u9ad8\u98de\u8679\u8138\u535c\u4e00\u70ed\uff0c\u9053\uff1a \u201c\u5175\u4e0d\u538c\u8bc8\uff0c\u6211\u4eec\u65e2\u7136\u662f\u654c\u5bf9\u76f8\u5904\uff0c\u90a3\u81ea\u7136\u7528\u4e0d\u7740\u4ec0\u4e48\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u597d\uff0c\u9ad8\u516c\u5b50\u65e2\u5982\u6b64\uff0c\u54b1\u4eec\u53ea\u597d\u7167\u7740\u4f60\u81ea\u5df2\u7684\u610f\u601d\u529e\u4e8b\u4e86!\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a \u201c\u54b1\u4eec\u628a\u4f60\u9ad8\u516c\u5b50\u751f\u64d2\u6d3b\u6349\u4e86\uff0c\u4e0d\u6015\u9ad8\u5929\u5065\u4e0d\u4ea4\u51fa\u89e3\u836f\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1b\u6211\u5982\u4f55\u80fd\u76f8\u4fe1\u4f60\u4eec\u7684\u8bdd?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a \u201c\u9ad8\u5929\u5065\u4f20\u4e86\u4f60\u4e0d\u5c11\u7684\u6b66\u529f\uff0c\u5c31\u7b97\u4f60\u5c11\u4e0d\u66f4\u4e8b\uff0c\u4f46\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\u751f\u6b7b\u5229\u5bb3\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a \u201c\u8fd9\u4e00\u70b9\uff0c\u6211\u5f88\u6e05\u695a\uff0c\u4f46\u5982\u5728\u4e0b\u53d7\u5230\u4ec0\u4e48\u4f24\u5bb3\uff0c\u4f55\u5bd2\u8863\u4e5f\u8981\u966a\u5728\u4e0b\u6b7b\u4ea1\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u9ad8\u516c\u5b50\uff0c \u4f60\u592a\u5ae9\u4e86\uff0c\u5982\u82e5\u5728\u4e0b\u63a8\u7b97\u7684\u4e0d\u9519\uff0c\u90a3\u89e3\u836f\u5c31\u5e26\u5728\u4f60\u7684\u8eab\u4e0a\uff0c\u5c31\u7b97\u4e0d\u5728\uff0c\u4f60\u8001\u5b50\u4e5f\u4f1a\u62ff\u51fa\u89e3\u836f\uff0c\u6362\u4f60\u4e00\u6761\u6027\u547d\u3002\u201d \u3000\u3000\u6267\u5251\u9ed1\u8863\u4eba\uff0c\u53f3\u624b\u5fae\u4e00\u52a0\u529b\uff0c\u68ee\u5bd2\u7684\u5251\u5c16\uff0c\u523a\u5165\u9ad8\u98de\u8679\u7684\u54bd\u5589\uff0c\u53ea\u662f\u523a\u7834\u4e00\u70b9\u8868\u76ae\u3002 \u3000\u3000\u4f46\u90a3\u5df2\u7ecf\u4f7f\u5f97\u9ad8\u98de\u8679\u5fc3\u80c6\u7686\u88c2\u4e86\u3002 \u3000\u3000\u4ed6\u60f3\u95ea\u907f\uff0c\u4f46\u4ed6\u8eab\u540e\u8fd8\u6709\u654c\u4eba\uff0c\u5982\u679c\u4ed6\u968f\u4fbf\u51fb\u51fa\u4e00\u638c\uff0c\u5c31\u53ef\u4ee5\u53d6\u4ed6\u6027\u547d\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u4e0d\u6562\u52a8\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u4f46\u5374\u56e0\u884c\u52a8\u8868\u73b0\u51fa\u4e86\u4ed6\u7684\u754f\u60e7\u3002 \u3000\u3000\u4ed6\u53d6\u51fa\u4e86\u4e00\u4e2a\u7389\u74f6\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u74f6\u4e2d\u6709\u4e24\u7c92\u89e3\u836f\uff0c\u670d\u7528\u4e00\u7c92\uff0c\u5c31\u53ef\u4ee5\u5316\u53bb\u6bd2\u6027\uff0c\u6211\u5df2\u7ecf\u4ea4\u51fa\u89e3\u836f\uff0c\u5c31\u8981\u770b\u4f60\u4eec\u8bb2\u4e0d\u8bb2\u4fe1\u7528\u4e86\u3002\u201d \u3000\u3000\u5251\u5c16\u4ecd\u7136\u523a\u5728\u9ad8\u98de\u8679\u7684\u54bd\u5589\u4e0a\u3002 \u3000\u3000\u8eab\u540e\u9ed1\u8863\u4eba\u9053\uff1a \u201c\u9ad8\u516c\u5b50\uff0c\u6211\u8981\u5148\u8bd5\u8bd5\u8fd9\u7389\u74f6\u4e2d\u7684\u89e3\u836f\u771f\u5047\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u5bf9\u75c7\u4e4b\u836f\u7acb\u523b\u89c1\u6548\uff0c\u53ea\u5e0c\u671b\u4f60\u7684\u8bd5\u9a8c\u5feb\u4e00\u4e9b\u3002\u201d \u3000\u3000\u539f\u6765\u90a3\u6267\u5251\u9ed1\u8863\u4eba\uff0c \u5df2\u628a\u9ad8\u98de\u8679\u903c\u5230\u4e86\u4e00\u5904\u5899\u89d2\u4e4b\u4e0a\u3002 \u3000\u3000\u5251\u5c16\u523a\u5165\u54bd\u5589\u7684\u808c\u80a4\u4e4b\u4e2d\uff0c\u9ad8\u98de\u8679\u5c31\u7b97\u6709\u53cd\u6297\u7684\u80fd\u529b\uff0c\u4e5f\u4e0d\u6562\u5192\u8fd9\u4e2a\u9669\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u670d\u4e86\u89e3\u836f\uff0c\u679c\u7136\u5f88\u5feb\u7684\u6062\u590d\u4e86\u6b66\u529f\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u559d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9ad8\u516c\u5b50\u679c\u7136\u6ca1\u6709\u9a97\u6211\u4eec\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u4ea4\u51fa\u4e86\u89e3\u836f\uff0c\u5e94\u8be5\u653e\u6211\u8d70\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u9ad8\u516c\u5b50\uff0c\u6211\u4eec\u4f1a\u653e\u4f60\u8d70\u7684\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5f88\u5feb\uff0c\u5728\u4e0b\u7b54\u5e94\u7684\u4e8b\uff0c\u4e00\u5b9a\u5151\u73b0\uff0c\u4e0d\u8fc7\uff0c\u6211\u5e0c\u671b\u4f60\u9ad8\u516c\u5b50\u8fd8\u4e3a\u6211\u4eec\u505a\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7b11\u9053\uff1a\u201c\u5e26\u4e00\u70b9\u4e1c\u897f\u56de\u53bb\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u597d!\u4ec0\u4e48\u4e1c\u897f?\u62ff\u7ed9\u6211\uff01\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e00\u7c92\u836f\u4e38\uff0c\u9ad8\u516c\u5b50\u8bf7\u5f20\u5f00\u5634\u5427!\u201d \u3000\u3000\u9ad8\u98de\u8679\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u4ec0\u4e48\u836f?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6bd2\u836f!\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4f60\u4eec\u7b54\u5e94\u653e\u6211\u7684\uff0c\u600e\u80fd\u8a00\u800c\u65e0\u4fe1\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5403\u8fc7\u4e86\u8fd9\u4e00\u9897\u6bd2\u836f\uff0c\u9ad8\u516c\u5b50\u5c31\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u8ba9\u6211\u670d\u4e0b\u6bd2\u836f\uff0c\u6bd2\u53d1\u800c\u6b7b\uff0c\u548c\u6b7b\u5728\u4f60\u4eec\u7684\u5251\u4e0b\uff0c\u6709\u4ec0\u4e48\u4e0d\u540c?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u9ad8\u516c\u5b50\uff0c\u5f20\u5f00\u5634\u5427!\u53ea\u8981\u9ad8\u516c\u5b50\u80af\u548c\u6211\u4eec\u5408\u4f5c\uff0c\u5728\u4e0b\u4fdd\u8bc1\u4e0d\u4f1a\u4f24\u5bb3\u5230\u9ad8\u516c\u5b50\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u770b\u5230\u4e86\u54bd\u5589\u4e0a\u6d41\u4e0b\u6765\u7684\u9c9c\u8840\uff0c \u53ea\u597d\u5f20\u5f00\u4e86\u5634\u5df4\uff0c\u4e00\u7c92\u836f\u4e38\u98de\u4e86\u8fc7\u6765\uff0c\u6295\u5165\u4e86\u9ad8\u98de\u8679\u53e3\u4e2d\u3002 \u3000\u3000\u624b\u6cd5\u5947\u51c6\uff0c\u9ad8\u98de\u8679\u4e0d\u81ea\u4e3b\u541e\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u7b11\u9053\uff1a\u201c\u9ad8\u516c\u5b50\uff0c\u8fd9\u662f\u4e00\u7c92\u5f3a\u70c8\uff0c\u4f46\u5374\u662f\u53d1\u4f5c\u5f88\u6162\u7684\u6bd2\u836f\uff0c\u4e0d\u8fc7\uff0c\u4e00\u65e6\u53d1\u4f5c\u4e86\uff0c\u90a3\u5c31\u65e0\u6cd5\u6551\u6cbb\uff0c\u9ad8\u516c\u5b50\uff0c\u6211\u77e5\u9053\u9ad8\u5929\u5065\u624b\u4e0b\u6709\u4e0d\u5c11\u9ad8\u660e\u4eba\u7269\uff0c\u4f46\u4ed6\u4eec\u65e0\u6cd5\u89e3\u4f60\u8eab\u4e2d\u4e4b\u6bd2\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u6211\u662f\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u9ad8\u516c\u5b50\u5bb6\u4ea7\u4e07\u8d2f\uff0c\u5e74\u7eaa\u8f7b\u8f7b\uff0e\u6b7b\u4e86\uff0c\u5c82\u4e0d\u662f\u53ef\u60dc\u7684\u5f88\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u540e\u5929\u665a\u4e0a\uff0c\u4e09\u66f4\u65f6\u5206\uff0c\u8bf7\u9ad8\u516c\u5b50\u5728\u7b2c\u4e00\u5821\u5916\u4e94\u91cc\u5904\u5927\u67f3\u6811\u4e0b\u7b49\u6211\u4eec\uff0c\u54b1\u4eec\u60f3\u8fdb\u4eba\u7b2c\u4e00\u5821\u4e2d\u770b\u770b\u2026\u2026\u201d \u3000\u3000\u9ad8\u98de\u8679\u5403\u4e86\u4e00\u60ca\uff0c\u63a5\u9053\uff1a\u201c\u770b\u770b\uff0c\u770b\u4ec0\u4e48?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a \u201c\u770b\u770b\u8d35\u5821\u7684\u5f62\u52bf\uff0c\u4e0d\u8fc7\uff0c\u9ad8\u516c\u5b50\uff0c\u54b1\u4eec\u7edd\u4e0d\u4f1a\u5728\u8d35\u5821\u4e2d\u95f9\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4e0d\u5728\u5821\u4e2d\u95f9\u4e8b\uff0c \u4f60\u4eec\u53bb\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a \u201c\u9ad8\u516c\u5b50\uff0c\u4f60\u73b0\u5728\u4e0d\u662f\u95ee\u8bdd\u7684\u65f6\u5019\uff0e\u800c\u662f\u56de\u7b54\u6211\u4eec\u7684\u95ee\u8bdd\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u95ed\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a \u201c\u9ad8\u516c\u5b50\uff0c\u5e0c\u671b\u4f60\u522b\u544a\u8bc9\u4ee4\u5c0a\uff0c\u56e0\u4e3a\uff0c\u4f60\u670d\u7528\u7684\u6bd2\u836f\uff0c\u662f\u6df7\u5408\u4e4b\u6bd2\uff0c\u5c31\u7b97\u8d35\u5e9c\u4e2d\u517b\u6709\u5f88\u591a\u540d\u533b\uff0c\u4f46\u4e5f\u6ca1\u6709\u529e\u6cd5\u627e\u51fa\u662f\u4ec0\u4e48\u6bd2\u836f\u6df7\u5408\u7684\u6bd2\u6027\uff0c\u4ed6\u4eec\u603b\u4e0d\u80fd\u628a\u4f60\u7684\u809a\u5b50\u5272\u5f00\uff0c\u4e8b\u5b9e\u4e0a\u5c31\u7b97\u5272\u5f00\u4e86\uff0c\u4e5f\u65e0\u6cd5\u627e\u51fa\u6bd2\u836f\uff0c\u8bdd\u5df2\u7ecf\u8bf4\u5f97\u5f88\u6e05\u695a\u4e86\uff0c\u4fe1\u4e0d\u4fe1\uff0c\u662f\u4f60\u9ad8\u516c\u5b50\u7684\u4e8b\u4e86\uff0c\u73b0\u5728\uff0c\u4f60\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u6211\u773c\u4e0b\u7684\u8fd9\u79cd\u6bd2\u836f\uff0c\u51e0\u5929\u53ef\u4ee5\u53d1\u4f5c\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e09\u5929\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u903c\u5728\u6211\u8eab\u4e0a\u7684\u957f\u5251\uff0c\u53ef\u4ee5\u62ff\u5f00\u4e86\u5427?\u201d \u3000\u3000\u524d\u9762\u7684\u9ed1\u8863\u4eba\uff0c\u7a81\u7136\u95f4\u6536\u56de\u4e86\u957f\u5251\u3002 \u3000\u3000\u9ad8\u98de\u8679\u4e3e\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u591a\u8c22\u5821\u4e3b\u76f8\u52a9\u3002\u201d \u3000\u3000\u90a3\u5f53\u5148\u9ed1\u8863\u4eba\u4f38\u624b\u8f7b\u8f7b\u4e00\u62b9\uff0c\u53d6\u4e0b\u4eba\u76ae\u9762\u5177\u3002 \u3000\u3000\u6b63\u662f\u9634\u9633\u5821\u4e3b\u9ec4\u7075\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u8fdb\u6765\u7684\u9ed1\u8863\u4eba\uff0c\u4e5f\u53d6\u4e0b\u4e86\u4eba\u76ae\u9762\u5177\uff0c\u7adf\u662f\u53f6\u957f\u9752\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u628a\u4ed6\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u9ad8\u516c\u4e8e\uff0c\u6ca1\u6709\u4ec0\u4e48\u5927\u7528\uff0c\u552f\u4e00\u7684\u80fd\u529b\uff0c\u5c31\u662f\u4ed6\u59d3\u9ad8\u3002\u9ad8\u5929\u5065\u53ef\u4ee5\u4e0d\u5173\u5fc3\u5929\u4e0b\u6240\u6709\u7684\u4eba\uff0c\u4f46\u4ed6\u6ca1\u6709\u529e\u6cd5\u4e0d\u5173\u5fc3\u4ed6\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u5982\u82e5\u9ad8\u5929\u5065\u771f\u7684\u5f88\u559c\u6b22\u4ed6\u7684\u513f\u5b50\uff0c\u54b1\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u628a\u4ed6\u7559\u4e0b\u6765\uff0c\u4f5c\u4e3a\u4eba\u8d28\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u5c31\u8981\u4ece\u9ad8\u5929\u5065\u7684\u6027\u683c\u4e0a\u53bb\u5206\u6790\u4e86\uff0c\u8fd9\u4e2a\u4eba\u9634\u6c89\u6210\u6027\uff0c\u5982\u82e5\u4e8b\u60c5\u5b8c\u5168\u8131\u79bb\u4e86\u4ed6\u7684\u638c\u63e1\uff0c\u90a3\u5c31\u4e0d\u540c\u4e86\uff0c\u4ed6\u53ef\u80fd\u4f1a\u8fde\u4ed6\u7684\u513f\u5b50\u4e5f\u727a\u7272\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u5982\u6b64\u6076\u6bd2\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u864e\u6bd2\u4e0d\u98df\u5b50\uff0c\u4f46\u9ad8\u5929\u5065\u8fd9\u4e2a\u4eba\uff0c\u53ef\u662f\u5f88\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u4f60\u770b\u4ed6\u4f1a\u4e0d\u4f1a\u544a\u8bc9\u9ad8\u5929\u5065\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u8fd9\u4f4d\u9ad8\u516c\u5b50\u5f88\u6015\u6b7b\uff0c\u4f46\u4ed6\u4e00\u76f4\u5728\u5176\u7236\u4eb2\u7684\u853d\u836b\u4e0b\u957f\u5927\uff0c\u6240\u4ee5\uff0c\u4ed6\u5bf9\u7236\u4eb2\uff0c\u4e5f\u6709\u7740\u5f88\u5f3a\u70c8\u7684\u4fe1\u5fc3\uff0c\u4ed6\u662f\u5426\u544a\u8bc9\u9ad8\u5929\u5065\uff0c\u673a\u4f1a\u5404\u5360\u4e00\u534a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u5982\u82e5\u4ed6\u544a\u8bc9\u4e86\u9ad8\u5929\u5065\uff0c\u54b1\u4eec\u662f\u4e0d\u662f\u8fd8\u8981\u5230\u7b2c\u4e00\u5821\u53bb?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u53bb!\u5c31\u7b97\u4ed6\u544a\u8bc9\u4e86\u9ad8\u5929\u5065\uff0c\u6211\u4eec\u8fd8\u662f\u8981\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u9ad8\u5929\u5065\u77e5\u9053\u4e86\uff0c\u4e5f\u2014\u6837\u65e0\u6cd5\u6551\u4ed6\uff0c\u9ad8\u98de\u8679\u5728\u4ed6\u624b\u91cc\uff0c\u4ed6\u5982\u65e0\u6cd5\u6551\u6cbb\uff0c\u5bf9\u4ed6\u662f\u4e00\u79cd\u6311\u6218\uff0c\u4e5f\u662f\u4e00\u79cd\u4f24\u5bb3\uff0c\u6240\u4ee5\uff0c\u4ed6\u5fc5\u987b\u5c3d\u529b\uff0c\u4ed6\u6ca1\u6709\u529e\u6cd5\u4e0b\u624b\uff0c\u90a3\u5c31\u53ea\u597d\u6c42\u6211\u4eec\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u770b\uff0c\u9ad8\u5929\u5065\u4e0d\u662f\u8fd9\u6837\u7684\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5bf9\u522b\u4eba\uff0c\u9ad8\u5929\u5065\u4e0d\u4f1a\u6709\u4efb\u4f55\u987e\u8651\uff0c\u4f46\u9ad8\u98de\u8679\u7a76\u7adf\u662f\u4ed6\u7684\u513f\u5b50\uff0c\u9664\u975e\u4ed6\u81ea\u89c9\u5df2\u7ecf\u5b8c\u5168\u4e0d\u80fd\u63a7\u5236\u60c5\u52bf\uff0c\u9ad8\u5929\u5065\u4e00\u5b9a\u6709\u58ee\u58eb\u65ad\u8155\u7684\u7cbe\u795e\uff0c\u53ef\u662f\uff0c\u9ad8\u98de\u8679\u5728\u4ed6\u8eab\u8fb9\uff0c\u5728\u4ed6\u7684\u611f\u89c9\u4e2d\uff0c\u4ed6\u4e00\u76f4\u80fd\u63a7\u5236\u7740\u5c40\u52bf\uff0c\u8fd9\u5c31\u662f\u4ed6\u7684\u5f31\u70b9\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u6700\u91cd\u8981\u7684\u65f6\u523b\uff0c\u624d\u53d1\u89c9\u4e86\u5821\u4e3b\u7684\u624d\u6167\u662f\u90a3\u4e48\u6e05\u6f88\uff0c\u5821\u4e3b\u5904\u4e8b\uff0c\u662f\u90a3\u4e48\u9547\u9759\uff0c\u60f3\u6765\uff0c\u771f\u53eb\u5c5e\u4e0b\u89c9\u7740\u60ed\u6127\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4e0d\u7528\u81ea\u8d23\uff0c\u4ed6\u4eec\u51c6\u5907\u5f97\u592a\u5145\u5206\u4e86\uff0c\u522b\u8bf4\u4f60\u7684\u6c5f\u6e56\u7ecf\u9a8c\u8fd8\u4e0d\u591f\u4e30\u5bcc\uff0c\u5c31\u7b97\u662f\u6c5f\u6e56\u4e0a\u7ecf\u9a8c\u6700\u4e30\u5bcc\u7684\u4eba\uff0c\u53ea\u6015\u4e5f\u4e00\u6837\u4f1a\u7740\u4e86\u9053\u513f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u8fd9\u4e00\u6b21\uff0c\u4e8b\u60c5\u867d\u7136\u6709\u60ca\u65e0\u9669\uff0c \u4ee5\u5821\u4e3b\u7684\u624d\u6167\uff0c\u6551\u4e86\u5c5e\u4e0b\uff0c\u4e0d\u4f46\u662f\u5f97\u5230\u4e86\u4e00\u4e2a\u6559\u8bad\uff0c\u800c\u4e14\uff0c \u4e5f\u4f7f\u5f97\u5c5e\u4e0b\u8ba4\u8bc6\u4e86\u4e00\u4e2a\u4eba!\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u8ba4\u8bc6\u4e86\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u534e\u5999\u771f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u534e\u5251\u4f7f\uff0c\u5979\u600e\u4e48\u6837\u4e86?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u770b\u5230\u4e86\u534e\u5251\u4f7f\u7684\u80fd\u529b\uff0c\u771f\u4f7f\u5728\u4e0b\u89c9\u5f97\u60ed\u6127\u5f97\u5f88\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u534e\u5251\u4f7f\u600e\u4e48\u6837?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u4e0d\u662f\u534e\u5251\u4f7f\uff0c\u4e5f\u8bb8\u5728\u4e0b\u65e9\u5df2\u53d7\u5230\u4e86\u90a3\u5e97\u5c0f\u4e8c\u7684\u6697\u7b97\u4e86\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u534e\u5251\u4f7f\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5979\u5f88\u5e73\u5b89\uff0c\u4e5f\u8bb8\u5c31\u5feb\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u89c1\u8fc7\u5821\u4e3b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u7f13\u6b65\u884c\u8fdb\u4e86\u6765\uff0c\u5979\u4ecd\u7136\u662f\u7a7f\u7740\u5e97\u5c0f\u4e8c\u7684\u8863\u670d\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u534e\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u534e\u5999\u771f\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a \u201c\u6211\u89c1\u5230\u4e86\u5821\u4e3b\uff0c\u6240\u4ee5\uff0c\u5f88\u653e\u5fc3\u4f60\u7684\u5b89\u5168\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5728\u4e0b\u679c\u7136\u7740\u4e86\u9053\u513f\uff0c\u5982\u82e5\u4e0d\u662f\u5821\u4e3b\u53ca\u65f6\u8d76\u6765\uff0c\u53ea\u6015\u5728\u6b64\u523b\uff0c\u5df2\u7ecf\u6b7b\u4e8e\u4ed6\u4eec\u624b\u4e2d\u4e86\u3002\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u9ec4\u7075\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u5728\u4e0b\u8ffd\u8e2a\u90a3\u4eba\uff0c\u53d1\u89c9\u4ed6\u8fdb\u4e86\u5927\u901a\u5f53\u94fa\u3002\u201d \u3000\u3000\u9ec4\u7075\u6c89\u541f\u4e86\uff0c\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u6536\u62fe\u4e00\u4e0b\uff0c\u54b1\u4eec\u8d70!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5230\u54ea\u91cc\u53bb?\u201d \u3000\u3000\u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u8fd9\u91cc\u5df2\u7ecf\u4e0d\u80fd\u4f4f\u4e86\uff0c\u54b1\u4eec\u5230\u4e00\u5904\u5b89\u5168\u5730\u65b9\u53bb\u3002\u201d \u3000 \u7b2c \u4e03 \u56de\u3000\u6df1\u5165\u7b2c\u4e00\u5821 \u3000 \u3000\u3000\u539f\u6765\uff0c\u9ec4\u7075\u65e9\u5df2\u5728\u4fe1\u9633\u5dde\u5b89\u6392\u4e86\u4e00\u5ea7\u5206\u8235\u3002 \u3000\u3000\u90a3\u662f\u4e00\u5904\u505a\u9152\u7684\u4f5c\u574a\u3002 \u3000\u3000\u5e73\u5e38\uff0c\u8fd9\u662f\u4e00\u5904\u771f\u6b63\u7684\u5356\u9152\u5730\u65b9\uff0c\u9664\u4e86\u4e3b\u6301\u4f5c\u574a\u7684\u638c\u67dc\u548c\u5e10\u623f\u5148\u751f\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u4eba\uff0c\u4e5f\u90fd\u662f\u771f\u6b63\u7684\u505a\u9152\u5de5\u4eba\u3002 \u3000\u3000\u9ec4\u7075\u4e25\u4ee4\u4ed6\u4eec\u4e0d\u5f97\u6709\u4efb\u4f55\u6d3b\u52a8\uff0c\u7b2c\u4e00\u5821\u8033\u76ee\u904d\u5e03\u7684\u5730\u65b9\uff0c\u4ed6\u4eec\u4e00\u76f4\u5341\u5206\u8c28\u614e\u5c0f\u5fc3\u3002 \u3000\u3000\u8fd9\u662f\u9ec4\u7075\u51c6\u5907\u7684\u6700\u9690\u79d8\u5206\u8235\uff0c \u4e00\u76f4\u6ca1\u6709\u88ab\u4eba\u53d1\u89c9\u3002 \u9664\u4e86\u9ec4\u7075\u4e4b\u5916\uff0c\u9634\u9633\u5821\u4e2d\uff0c\u4e5f\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u77e5\u9053\u3002 \u3000\u3000\u4f5c\u623f\u7684\u89c4\u6a21\u5f88\u5927\uff0c\u9ec4\u7075\u7b49\u88ab\u5f15\u5165\u4e86\u5bc6\u5ba4\u3002 \u3000\u3000\u90a3\u662f\u85cf\u9152\u7684\u5730\u7a96\u3002 \u3000\u3000\u4f46\u8fd9\u5ea7\u5730\u7a96\u4e2d\uff0c\u5374\u6ca1\u6709\u85cf\u9152\uff0c\u5e03\u7f6e\u5f97\u5f88\u96c5\u81f4\u3002 \u3000\u3000\u72c2\u9f99\u3001\u98de\u9e70\uff0c\u4e5f\u5728\u8fd9\u91cc\uff0c\u770b\u5b88\u4e24\u4eba\u7684\uff0c\u662f\u8fd9\u91cc\u7684\u5e10\u623f\u5148\u751f\u548c\u5929\u624b\u5200\u4e07\u80dc\u3002 \u3000\u3000\u4e07\u80dc\u5e26\u7684\u516b\u4e2a\u4eba\uff0c\u5148\u8d76\u5230\u4e86\u4f5c\u574a\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u3001\u53f6\u957f\u9752\u90fd\u5f88\u4f69\u670d\u9ec4\u7075\u601d\u8651\u7684\u5468\u5bc6\u3002 \u3000\u3000\u72c2\u9f99\u3001 \u98de\u9e70\u88ab\u88c5\u5165\u4e86\u4e24\u4e2a\u7279\u5236\u7684\u5927\u9152\u7bd3\u4e2d\uff0c\u9001\u5165\u4e86\u4f5c\u574a\u3002 \u56e0\u4e3a\uff0c\u5929\u5916\u5ba2\u6808\u4e5f\u4e70\u8fd9\u5ea7\u4fe1\u9633\u4f5c\u574a\u7684\u9152\u3002 \u3000\u3000\u9ec4\u7075\u3001\u4f55\u5bd2\u8863\u3001\u534e\u5999\u771f\u3001\u53f6\u957f\u9752\u9c7c\u8d2f\u884c\u5165\u4e86\u9152\u7a96\u3002 \u3000\u3000\u8fdb\u5165\u4f5c\u574a\u4e4b\u540e\uff0c\u5927\u90fd\u6062\u590d\u4e86\u672c\u6765\u7684\u9762\u76ee\uff0c \u53ea\u6709\u9ec4\u7075\uff0c\u6234\u4e0a\u4e86\u4eba\u76ae\u9762\u5177\u3002 \u4ed6\u672c\u6765\uff0c\u5c31\u662f\u4e2a\u795e\u79d8\u7684\u4eba\u7269\u3002 \u3000\u3000\u72c2\u9f99\u548c\u98de\u9e70\u4f3c\u662f\u5df2\u7ecf\u88ab\u70b9\u4e86\u7a74\u9053\uff0c \u56e0\u4e3a\u4e24\u4eba\u8eab\u4e0a\u65e2\u65e0\u67b7\u9501\uff0c\u5374\u4e00\u76f4\u9759\u5750\u672a\u52a8\u3002 \u53f6\u957f\u9752\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4e24\u4f4d\uff0c\u54b1\u4eec\u53c8\u89c1\u9762\u4e86\uff0c\u771f\u662f\u4eba\u751f\u4f55\u5904\u4e0d\u76f8\u9022\u554a!\u201d \u3000\u3000\u72c2\u9f99\u4e24\u9053\u51b7\u5389\u7684\u76ee\u5149\uff0c\u51dd\u6ce8\u5728\u53f6\u957f\u9752\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u4e03\u5251\u8ffd\u9b42\u53f6\u957f\u9752?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e24\u4f4d\u8fd8\u8bb0\u5f97\u6211?\u201d \u3000\u3000\u72c2\u9f99\u76ee\u5149\u8f6c\u6ce8\u5230\u4f55\u5bd2\u8863\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u9601\u4e0b\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u4f55\u5bd2\u8863\u3002\u201d \u3000\u3000\u98de\u9e70\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u91d1\u5251\u98de\u8f6e\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6b63\u662f\u533a\u533a\u3002\u201d \u3000\u3000\u72c2\u9f99\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4e03\u5251\u8ffd\u9b42\u3001\u5929\u624b\u5200\u3001\u91d1\u5251\u98de\u8f6e\uff0c\u90fd\u662f\u6c5f\u6e56\u4e0a\u7d20\u8457\u4fa0\u540d\u7684\u4eba\uff0c\u60f3\u4e0d\u5230\uff0c\u7adf\u7136\u90fd\u662f\u9634\u9633\u5821\u4e2d\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e24\u4f4d\uff0c\u9634\u9633\u5821\u6709\u4ec0\u4e48\u4e0d\u597d?\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a \u201c\u6c5f\u6e56\u4e0a\u6709\u8c01\u4e0d\u77e5\uff0c\u9634\u9633\u5821\u662f\u4e00\u4e2a\u5f88\u51f6\u6b8b\u3001\u5351\u4e0b\u7684\u9ed1\u9053\u7ec4\u5408\u3002\u201d \u9ec4\u7075\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u9634\u9633\u5821\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u8a89\uff0c\u7684\u786e\u662f\u4e0d\u592a\u597d\u3002\u201d \u72c2\u9f99\u9053\uff1a \u201c\u4f46\u8c01\u4f1a\u60f3\u5230\uff0c\u6c5f\u6e56\u4e0a\u76db\u540d\u7d20\u8457\u7684\u4e09\u4f4d\u5927\u4fa0\uff0c\u7adf\u7136\u662f\u9634\u9633\u5821\u4e2d\u7684\u4eba\u3002\u201d \u9ec4\u7075\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9634\u9633\u5821\u7684\u540d\u8a89\u662f\u4e0d\u592a\u597d\uff0c\u4e0d\u8fc7\uff0c\u54b1\u4eec\u505a\u4e86\u4ec0\u4e48\u574f\u4e8b\uff0c\u8fd8\u671b\u8bf8\u4f4d\u6307\u51fa\u4e00\u4e8c\u3002\u201d \u3000\u3000\u72c2\u9f99\u3001\u98de\u9e70\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u60f3\u6765\u60f3\u53bb\uff0c\u5b9e\u5728\u60f3\u4e0d\u51fa\u9634\u9633\u5821\u505a\u4e86\u4ec0\u4e48\u574f\u4e8b\u3002 \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u662f\u6b66\u6797\u4e2d\u666f\u4ef0\u7684\u5730\u65b9\uff0c\u4e5f\u4ee3\u8868\u4e86\u6b63\u4e49\uff0c\u4f60\u4eec\u4e5f\u662f\u7b2c\u4e00\u5821\u4e2d\u7684\u4eba\uff0c\u4f46\u4f60\u4eec\u505a\u4e86\u4ec0\u4e48\u6709\u76ca\u4e8e\u6c5f\u6e56\u7684\u4e8b?\u201d \u3000\u3000\u72c2\u9f99\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9634\u9633\u5821\u4e3b\u3002\u201d \u3000\u3000\u72c2\u9f99\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u4f60\u662f\u9634\u9633\u5821\u4e3b?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a \u201c\u4f55\u5bd2\u8863\u3001\u53f6\u957f\u9752\u3001\u4e07\u80dc\uff0c\u90fd\u662f\u4f60\u7f57\u81f4\u8fdb\u5165\u9634\u9633\u5821\u4e2d?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5982\u662f\u4e24\u4f4d\u613f\u610f\uff0c\u9634\u9633\u5821\u540c\u6837\u6b22\u8fce\u4e24\u4f4d\u3002\u201d \u3000\u3000\u98de\u9e70\u9053\uff1a\u201c\u4f60\u4eec\u8d76\u6765\u4fe1\u9633\u5dde\uff0c\u53ef\u662f\u4e13\u4ee5\u5bf9\u4ed8\u7b2c\u4e00\u5821\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u7b2c\u4e00\u5821\u505a\u4e86\u4ec0\u4e48\u4e8b\uff0c\u4e24\u4f4d\u5e94\u8be5\u5f88\u660e\u767d\u4e86\uff0c \u4f60\u4eec\u662f\u7b2c\u4e00\u5821\u7684\u4eba\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u4eec\u505a\u4e86\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u98de\u9e70\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u4e0d\u8bba\u6211\u4eec\u505a\u4e86\u4ec0\u4e48\u4e8b\uff0c\u6c5f\u6e56\u540c\u9053\uff0c\u90fd\u53ef\u4ee5\u5bb9\u5fcd\u3002\u201d \u9ec4\u7075\u9053\uff1a \u201c\u72c2\u9f99\u3001\u98de\u9e70\uff0c\u90fd\u662f\u6c5f\u6e56\u4e2d\u4e00\u65b9\u8c6a\u96c4\uff0c\u4f46\u4e0d\u77e5\u4e3a\u4ec0\u4e48\uff0c\u7adf\u7136\u7518\u613f\u4e3a\u9ad8\u5929\u5065\u7684\u722a\u7259\uff0c\u4e5f\u8bb8\uff0c \u4f60\u4eec\u5728\u7cbe\u5bc6\u7b56\u5212\u3001\u4e92\u76f8\u63a9\u62a4\u4e4b\u4e0b\uff0c\u6240\u4f5c\u6240\u4e3a\uff0c\u5f88\u96be\u4e3a\u522b\u4eba\u53d1\u89c9\uff0c\u4f46\u5728\u4f60\u4eec\u751f\u6d3b\u5f97\u5feb\u4e50\u5417?\u201d \u3000\u3000\u72c2\u9f99\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u6de1\u6de1\u4e00\u7b11\uff0c\u63a5\u9053\uff1a \u201c\u6211\u77e5\u9053\u4f60\u65e0\u6cd5\u56de\u7b54\uff0c \u4eba\u53ef\u4ee5\u6b3a\u9a97\u5929\u4e0b\u7684\u4eba\uff0c\u4f46\u5374\u65e0\u6cd5\u6b3a\u9a97\u81ea\u5df1\u3002\u201d \u3000\u3000\u72c2\u9f99\u3001\u98de\u9e70\uff0c\u76f8\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u9ed8\u9ed8\u4e0d\u8bed\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u6211\u77e5\u9053\u4e24\u4f4d\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u8a89\u3002\u4e0d\u7b97\u597d\u4eba\uff0c\u4f46\u4e5f\u4e0d\u592a\u574f\u2026\u2026\u201d \u72c2\u9f99\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u4f60\u8bf4\u8fd9\u4e9b\uff0c\u662f\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u6ca1\u6709\u4ec0\u4e48\u610f\u601d\uff0c \u53ea\u662f\u8981\u4e24\u4f4d\u591a\u60f3\u60f3\u3002\u201d \u72c2\u9f99\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u672c\u6765\uff0c\u6211\u53ef\u4ee5\u653e\u4e86\u4e24\u4f4d\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u5bf9\u73b0\u5728\u7684\u5b89\u8eab\u6240\u5728\uff0c\u8fd8\u4e0d\u4fbf\u6cc4\u6f0f\u51fa\u53bb\uff0c\u53ea\u597d\u6682\u65f6\u59d4\u5c48\u4e24\u4f4d\u4e86\u3002\u201d \u3000\u3000\u72c2\u9f99\u82e6\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u60f3\u5949\u529d\u5821\u4e3b\u4e00\u8a00\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u5929\u4e0b\u6ca1\u6709\u4eba\u80fd\u80dc\u8fc7\u9ad8\u5929\u5065\uff0c\u6b66\u6797\u4e2d\uff0c\u4e5f\u6ca1\u6709\u4efb\u4f55\u4e00\u4e2a\u7ec4\u5408\uff0e\u53ef\u4ee5\u548c\u7b2c\u4e00\u5821\u5f3a\u5927\u7684\u5b9e\u529b\u6297\u62d2\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u4eec\u5c48\u670d\u5728\u4e86\u9ad8\u5929\u5065\u7684\u538b\u529b\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u72c2\u9f99\u9ed8\u9ed8\u4e0d\u8bed\u3002 \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e24\u4f4d\u4e5f\u662f\u9053\u4e0a\u7684\u9876\u5c16\u4eba\u7269\uff0c\u600e\u4f1a\u7518\u613f\u505a\u9ad8\u5929\u5065\u7684\u722a\u7259?\u201d \u3000\u3000\u98de\u9e70\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u653e\u773c\u5f53\u4eca\u6c5f\u6e56\uff0c\u6709\u51e0\u4e2a\u7ec4\u7ec7\uff0c\u672a\u548c\u7b2c\u4e00\u5821\u626f\u4e0a\u5173\u7cfb?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9634\u9633\u5821\u5c31\u6ca1\u6709\uff01\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u4eec\u5f88\u5feb\u5c31\u4f1a\u5012\u4e0b\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\u9053\uff1a \u201c\u4f60\u4eec\u53d7\u5230\u7684\u5f71\u54cd\u592a\u5927\uff0c\u53d7\u5230\u7684\u4f24\u5bb3\u592a\u6df1\u3002\u201d \u72c2\u9f99\u9053\uff1a \u201c\u5f71\u54cd\u592a\u5927\uff0c\u8fd8\u53ef\u89e3\u8bf4\uff0c\u4f24\u5bb3\u592a\u5927\uff0c\u5728\u4e0b\u5c31\u6709\u4e9b\u60f3\u4e0d\u901a\u4e86\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u5728\u4e0b\u76f8\u4fe1\u4e24\u4f4d\u6295\u5165\u7b2c\u4e00\u5821\uff0c\u5fc5\u7136\u6709\u4e00\u4e9b\u539f\u56e0\u3002\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u4ec0\u4e48\u539f\u56e0?\u201d \u3000\u3000\u9ec4\u7075\u4f4e\u58f0\u9053\uff1a \u201c\u4e24\u4f4d\u6709\u4ec0\u4e48\u75db\u82e6\uff0c\u88ab\u9ad8\u5929\u5065\u6240\u638c\u63e1\uff0c\u4f55\u59a8\u5766\u7136\u8bf4\u51fa\u6765\uff0c\u4e5f\u8bb8\uff0c\u6211\u4eec\u80fd\u4ee3\u4e3a\u6548\u529b\u3002\u201d \u3000\u3000\u72c2\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4eec\u90fd\u5f88\u597d\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u5c0f\u59b9\u5b9e\u5728\u60f3\u4e0d\u51fa\uff0c\u4e24\u4f4d\u7adf\u7136\u5bf9\u9ad8\u5929\u5065\uff0c\u5982\u6b64\u7684\u5fe0\u8bda\u3002\u201d \u98de\u9e70\u9053\uff1a\u201c\u4e24\u4f4d\uff0c\u4e0d\u7528\u591a\u8d39\u5507\u820c\uff0c\u54b1\u4eec\u65e2\u5df2\u88ab\u64d2\uff0c\u552f\u6b7b\u800c\u5df2\uff0c\u5982\u60f3\u4ece\u54b1\u4eec\u7684\u53e3\u4e2d\uff0c\u63a2\u542c\u5230\u4ec0\u4e48\uff0c\u90a3\u662f\u767d\u8d39\u5fc3\u673a\u4e86\u3002\u201d \u3000\u3000\u8a00\u7f62\uff0c\u95ed\u4e0a\u53cc\u76ee\u3002 \u3000\u3000\u72c2\u9f99\u4e5f\u8868\u73b0\u51fa\u4e86\u540c\u6837\u51b3\u7edd\u3002 \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u8fd9\u4e24\u4f4d\u5982\u6b64\u56fa\u6267\uff0c\u770b\u6765\uff0c\u975e\u5f97\u8d39\u4e00\u756a\u624b\u811a\u4e0d\u53ef\u4e86\u3002\u201d \u9ec4\u7075\u6447\u6447\u5934\u9053\uff1a \u201c\u4e0d\uff01\u8fd9\u4e24\u4eba\u867d\u662f\u9ed1\u9053\u4e2d\u4eba\uff0c\u4f46\u6076\u8ff9\u4e0d\u591a\uff0c\u5982\u82e5\u54b1\u4eec\u7528\u624b\u6bb5\uff0c\u8feb\u4ed6\u4eec\u5c31\u8303\uff0c\u548c\u9ad8\u5929\u5065\u53c8\u6709\u4f55\u5f02?\u201d \u3000\u3000\u534e\u5999\u771f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5821\u4e3b\u8bf4\u7684\u662f\u3002\u201d \u3000\u3000\u72c2\u9f99\u51b7\u51b7\u63a5\u9053\uff1a \u201c\u8fd9\u79cd\u5047\u4ec1\u5047\u4e49\uff0c\u6545\u793a\u5927\u65b9\u7684\u505a\u6cd5\uff0c\u5728\u4e0b\u89c1\u5f97\u591a\u4e86\uff0c\u5982\u679c\u5c31\u8fd9\u6837\uff0c\u60f3\u8981\u54b1\u4eec\u5fc3\u5b58\u611f\u6fc0\uff0c\u90a3\u771f\u662f\u5f02\u60f3\u5929\u5f00\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4f60\u4eec\u4e0d\u7528\u611f\u6fc0\u4ec0\u4e48\uff0c\u54b1\u4eec\u4e5f\u672a\u5b58\u501f\u91cd\u4e24\u4f4d\u4e4b\u610f\uff0c\u53ea\u4e0d\u8fc7\uff0c\u73b0\u5728\u6211\u4eec\u4e0d\u80fd\u91ca\u653e\u4e24\u4f4d\uff0c \u53ea\u597d\u5148\u59d4\u5c48\u4e24\u4f4d\u51e0\u5929\uff0c\u591a\u5219\u5341\u5929\uff0c\u5c11\u5219\u4e03\u65e5\uff0c\u81ea\u7136\u4f1a\u653e\u4e24\u4f4d\u79bb\u5f00\u3002\u201d \u72c2\u9f99\u3001\u98de\u9e70\u76f8\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u9ec4\u7075\u56de\u987e\u4e86\u90a3\u5e10\u623f\u5148\u751f\u4e00\u773c\uff0c\u9053\uff1a \u201c\u4e8e\u56db\uff0c\u8bb0\u7740\uff0c\u5bf9\u8fd9\u4e24\u4f4d\u8d35\u5bbe\uff0c\u4e0d\u53ef\u7a0d\u6709\u6020\u6162\uff0c\u4ee5\u540e\u4e5f\u4e0d\u8bb8\u518d\u95ee\u4ed6\u4ec0\u4e48\u3002\u201d \u3000\u3000\u4e8e\u56db\u8eac\u8eab\u5e94\u9053\uff1a\u201c\u5c5e\u4e0b\u9886\u547d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4e24\u4f4d\uff0c\u54b1\u4eec\u5f88\u53ef\u80fd\u548c\u9ad8\u5929\u5065\uff0c\u4f1a\u6709\u51b2\u7a81\uff0c \u4e5f\u8bb8\u6ca1\u6709\u673a\u4f1a\u518d\u6765\u63a2\u671b\u4e24\u4f4d\u4e86\uff0c\u54b1\u4eec\u5c31\u6b64\u522b\u8fc7\uff0c\u8be5\u91ca\u653e\u4e24\u4f4d\u7684\u65f6\u95f4\uff0c\u4e8e\u56db\u81ea\u4f1a\u89e3\u5f00\u4e24\u4f4d\u7a74\u9053\u3002\u201d \u3000\u3000\u4e5f\u4e0d\u5f85\u72c2\u9f99\u3001\u98de\u9e70\u56de\u7b54\uff0c\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002\u4f55\u5bd2\u8863\u3001\u53f6\u957f\u9752\u3001\u4e07\u80dc\uff0c\u534e\u5999\u771f\u7b49\u76f8\u7ee7\u79bb\u53bb\u3002 \u3000\u3000\u72c2\u9f99\u3001\u98de\u9e70\u5bf9\u89c6\u4e00\u773c\uff0c \u89c1\u5ba4\u4e2d\u53ea\u4f59\u4e0b\u4e8e\u56db\u4e00\u4eba\u3002 \u72c2\u9f99\u9053\uff1a \u201c\u4e8e\u8001\u5144\uff0c\u7b2c\u4e00\u5821\u4e2d\u4eba\u53ea\u6015\u5373\u5c06\u8ffd\u67e5\u5230\u6b64\uff0c\u4f55\u4e0d\u653e\u4e86\u6211\u4eec\uff0c\u4ee5\u514d\u7389\u77f3\u4ff1\u711a\u3002\u201d \u4e8e\u56db\u9053\uff1a \u201c\u7b2c\u4e00\u5821\u8ffd\u67e5\u5230\u6b64\uff0c\u5728\u4e0b\u81ea\u7136\u662f\u975e\u6b7b\u4e0d\u53ef\u3002\u53ef\u662f\uff0c\u90a3\u662f\u7b2c\u4e00\u5821\u4e2d\u4eba\u67e5\u51fa\u6765\u624d\u884c\uff0c\u5982\u679c\u7b2c\u4e00\u5821\u4e2d\u4eba\u67e5\u4e0d\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u4e2d\u5165\u8033\u76ee\u4f17\u591a\uff0c\u5c82\u6709\u67e5\u4e0d\u51fa\u7684\u9053\u7406\u3002\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u5c31\u7b97\u80fd\u67e5\u51fa\u6765\u5427?\u90a3\u4e5f\u8981\u51e0\u5929\u7684\u65f6\u95f4\uff0c\u5bf9\u5427?\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u7edd\u5bf9\u4e0d\u4f1a\u8d85\u8fc7\u4e09\u65e5\u3002\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a \u201c\u53ef\u662f\uff0c\u6211\u8981\u653e\u4e86\u4f60\u4eec\uff0c \u53ea\u6015\u638c\u67dc\u7684\u7acb\u523b\u5c31\u4f1a\u8981\u6211\u7684\u547d\u3002\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u72c2\u9f99\u5821\u8fd8\u7f3a\u5c11\u4e00\u4e2a\u603b\u7ba1\uff0c \u4f60\u8001\u5144\u5982\u80af\u5c48\u5c31\u2026\u2026\u201d \u4e8e\u56db\u63a5\u9053\uff1a\u201c\u72c2\u9f99\u5821\u7684\u5c40\u9762\uff0c\u6bd4\u8fd9\u5ea7\u4f5c\u574a\u5982\u4f55\uff1f\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u81f3\u5c11\u8981\u5927\u5341\u500d\u3002\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u8d35\u5821\u6709\u6ca1\u6709\u526f\u5821\u4e3b?\u201d \u3000\u3000\u72c2\u9f99\u8138\u8272\u4e00\u5bd2\uff0c\u4f46\u7acb\u523b\u6362\u4e0a\u7b11\u5bb9\uff0c\u9053\uff1a \u201c\u5bf9\uff0c\u5bf9\uff0c\u6211\u4eec\u72c2\u9f99\u5821\u4e2d\uff0c\u8fd8\u7f3a\u4e00\u4e2a\u526f\u5821\u4e3b\uff0c\u6211\u770b\u4f60\u4e8e\u5144\uff0c\u5c31\u662f\u5f88\u9002\u5408\u7684\u4e00\u4e2a\u4eba\u9009\u3002\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u771f\u662f\u8bf1\u60d1\u7684\u5f88\u554a\u3002\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u526f\u5821\u4e3b\u7684\u8eab\u5206\uff0c\u5728\u72c2\u9f99\u5821\u6570\u5e72\u4eba\u4e2d\uff0c\u53ea\u5728\u5821\u4e3b\u4e00\u4eba\u4e4b\u4e0b\uff0c\u4e8e\u5144\u53ef\u5426\u5c31\u4efb\u5462?\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u6211\u5fc3\u4e2d\u60f3\u5f97\u5f88\u554a!\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u54b1\u4eec\u4e00\u8a00\u4e3a\u5b9a\uff0c\u653e\u4e86\u6211\u548c\u98de\u9e70\u4e4b\u540e\uff0c\u4f60\u5c31\u662f\u72c2\u9f99\u5821\u4e2d\u7684\u526f\u5821\u4e3b\u4e86\u3002\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u6211\u62c5\u5fc3\u2026\u2026\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1b\u201c\u6211\u4e00\u5411\u8a00\u800c\u6709\u4fe1\uff0c\u4f60\u8fd8\u62c5\u7684\u4ec0\u4e48\u5fc3\u4e8b?\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u6211\u4e0d\u662f\u62c5\u5fc3\u5821\u4e3b\u9a97\u6211\uff0c\u6211\u662f\u62c5\u5fc3\u6211\u653e\u4e86\u4e24\u4f4d\u4e4b\u540e\u4ed6\u4eec\u4f1a\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u653e\u4e86\u6211\u4eec\u4e4b\u540e\uff0c\u4f60\u5c31\u548c\u6211\u4eec\u4e00\u8d77\u79bb\u5f00\u6b64\u5730\uff0c\u600e\u4e48\u627e\u5f97\u5230\u4f60?\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u8fd9\u91cc\u597d\u624b\u5f88\u591a\uff0c\u653e\u4e01\u4f60\uff0c\u6050\u6015\u6211\u4eec\u79bb\u4e0d\u5f00\u8fd9\u5ea7\u4f5c\u574a\u3002\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u80c6\u5c0f\u5f97\u5f88\u54ea?\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u6211\u5728\u60f3\u4e00\u4e2a\u4eba\u5982\u679c\u6ca1\u6709\u4e86\u547d\uff0c\u4e0d\u7ba1\u5982\u4f55\uff0c\u5c31\u7b97\u6709 \u5927\u6279\u7684\u91d1\u94f6\u8d22\u5bcc\uff0c\u53c8\u6709\u4ec0\u4e48\u7528\u5904?\u201d \u3000\u3000\u98de\u9e70\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9f99\u5144\uff0c\u4f60\u8fd8\u6ca1\u6709\u6e05\u9192\u5417?\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1b\u201c\u6211\u6e05\u9192\u5f97\u5f88\u3002\u201d \u3000\u3000\u98de\u9e70\u9053\uff1a\u201c\u90a3\u4f60\u96be\u9053\u8fd8\u6ca1\u6709\u77a7\u51fa\u6765?\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u8fd9\u59d3\u4e8e\u7684\u5c0f\u5b50\uff0c\u53ef\u80fd\u5728\u9a97\u6211\u4eec?\u201d \u3000\u3000\u98de\u9e70\u9053\uff1a\u201c\u6bd4\u9a97\u8fd8\u8981\u574f\u4e00\u4e9b\uff0c\u4ed6\u6839\u672c\u662f\u5728\u9017\u7740\u4f60\u73a9\u7684\u3002\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u4e8e\u56db\uff0c\u8fd9\u8bdd\u5f53\u771f\u5417?\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u5047\u7684\u3002\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u90a3\u5c31\u653e\u4e86\u6211\u4eec\u3002\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u5b50\uff0c\u4e00\u8fde\u5728\u72c2\u9f99\u8eab\u4e0a\u62cd\u4e86\u5341\u4f59\u638c\u3002 \u3000\u3000\u8fd9\u5341\u638c\u843d\u52bf\u5f88\u91cd\u3002 \u3000\u3000\u72c2\u9f99\u88ab\u8fd9\u5341\u4f59\u638c\uff0c\u6253\u5f97\u9aa8\u808c\u9178\u75bc\uff0c\u4f46\u4ed6\u7684\u7a74\u9053\u4ecd\u7136\u6ca1\u6709\u89e3\u5f00\u3002 \u3000\u3000\u72c2\u9f99\u4e00\u76b1\u7709\u5934\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u4f4f\u624b\u4e86\u3002\u201d \u3000\u3000\u4e8e\u56db\u505c\u4e0b\u624b\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u79cd\u5f88\u7279\u6b8a\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u5728\u4e0b\u6050\u6015\u6ca1\u6709\u529e\u6cd5\u89e3\u5f00\u4e86\u3002\u201d \u3000\u3000\u72c2\u9f99\u56de\u987e\u4e86\u98de\u9e70\u4e00\u773c\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u4ed6\u5012\u662f\u4e00\u7247\u771f\u5fc3\u3002\u201d \u3000\u3000\u98de\u9e70\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u5728\u4e0b\u591a\u8651\u4e86\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u65e0\u6cd5\u89e3\u5f00\u54b1\u4eec\u7a74\u9053\uff0c\u90a3\u662f\u6ca1\u6709\u6cd5\u5b50\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u5728\u4e0b\u5f88\u60ed\u6127\u3002\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u4e8e\u526f\u5821\u4e3b\u4f60\u60f3\u60f3\u770b\uff0c\u8fd8\u6709\u6ca1\u6709\u522b\u7684\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u4e8e\u56db\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6cd5\u5b50\u5012\u6709\u4e00\u4e2a\uff0c\u53ea\u662f\u6709\u4e9b\u5192\u9669\u3002\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u5feb\u8bf4\u51fa\u6765\u542c\u542c\u3002\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u5728\u8fd9\u4f5c\u574a\u4e4b\u4e2d\u6709\u4e00\u4f4d\u9ad8\u624b\uff0c\u548c\u5728\u4e0b\u76f8\u5904\u7684\u5f88\u878d\u6d3d\uff0c\u4ed6\u7684\u6b66\u529f\u5341\u5206\u9ad8\u5f3a\uff0c\u4e5f\u8bb8\u4ed6\u80fd\u591f\u89e3\u5f00\u4e24\u4f4d\u8eab\u4e0a\u7684\u7a74\u9053\u3002\u201d \u3000\u3000\u72c2\u9f99\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4eba\u7269\uff0c\u8d35\u5821\u4e3b\u4e3a\u4ec0\u4e48\u4e0d\u91cd\u7528\u4ed6\u5462?\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u9634\u9633\u5821\u7684\u758f\u6f0f\u4e4b\u5904\u4e86\u3002\u201d \u3000\u3000\u98de\u9e70\u9053\uff1a\u201c\u54e6!\u8fd9\u662f\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u90a3\u4eba\u6df1\u85cf\u4e0d\u9732\uff0c\u4e3a\u4e86\u9003\u907f\u51e0\u4e2a\u4ec7\u5bb6\uff0c\u6240\u4ee5\u9690\u5c45\u5728\u4f5c\u574a\u4e4b\u5185\uff0c\u4f46\u4ed6\u548c\u6211\u5374\u76f8\u5904\u5f97\u5f88\u597d\uff0c\u6240\u4ee5\uff0c\u6211\u77e5\u9053\u4ed6\u7684\u5f88\u591a\u4e8b\u3002\u201d \u3000\u3000\u72c2\u9f99\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u7ba1\u4ed6\u662f\u8c01\uff0c\u53ea\u8981\u4ed6\u80fd\u591f\u6551\u4e86\u6211\u4eec\uff0c\u6211\u4eec\u5c31\u80fd\u62c5\u8d1f\u4ed6\u7684\u5b89\u5168\u8d23\u4efb\u3002\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u597d!\u8bf8\u4f4d\u8bf7\u7a0d\u5019\u7247\u523b\uff0c\u6211\u8fd9\u5c31\u53bb\u627e\u4ed6\u56de\u6765\u3002\u201d \u3000\u3000\u72c2\u9f99\u9053\uff1a\u201c\u597d!\u4f60\u5c3d\u5feb\u56de\u6765\u3002\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u5728\u4e0b\u8fd9\u5c31\u53bb\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8eab\u5feb\u6b65\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u91cc\u81ea\u7136\u4e0d\u4f1a\u6709\u90a3\u4e48\u4e00\u4e2a\u4eba\u7269\uff0c\u4e8e\u56db\u89c1\u662f\u9634\u9633\u5821\u4e3b\u9ec4\u7075\u3002 \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u600e\u4e48\u6837?\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u4ed6\u4eec\u5f88\u6025\uff0c\u800c\u4e14\uff0c\u8981\u6211\u7acb\u523b\u627e\u90a3\u4e2a\u4eba\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d!\u4f60\u5e26\u4ed6\u53bb\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u7adf\u7136\u662f\u9ec4\u7075\u548c\u4e8e\u56db\u65e9\u5df2\u7814\u7a76\u597d\u7684\u8ba1\u5212\u3002 \u3000\u3000\u4e8e\u56db\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u4f60\u96be\u9053\u771f\u7684\u8981\u653e\u8d70\u4e24\u4e2a\u4eba\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u662f\u4eca\u5929\uff0c\u8981\u4e09\u5929\u4e4b\u540e\uff0c\u624d\u80fd\u653e\u4ed6\u4eec\u8d70\u3002\u201d \u4e8e\u56db\u9053\uff1a\u201c\u4ed6\u4eec\u8981\u7acb\u523b\u8d70\uff0c\u62d6\u5ef6\u4e09\u5929\uff0c\u53ea\u6015\u4e0d\u662f\u4e00\u4ef6\u5f88\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u8fd9\u5c31\u8981\u4f60\u4eec\u4e24\u4e2a\u4eba\u7684\u914d\u5408\u4e86\uff0c\u914d\u5408\u5f97\u5929\u8863\u65e0\u7f1d\uff0c\u624d\u80fd\u4f7f\u4eba\u76f8\u4fe1\u3002\u201d \u4e8e\u56db\u9053\uff1a\u201c\u8fd9\u8981\u5821\u4e3b\u6307\u70b9\u4e86\u3002\u201d \u3000\u3000\u56de\u987e\u4e86\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\u73b0\u5728\u4f55\u5904?\u6211\u8981\u548c\u4ed6\u5148\u5546\u91cf\u4e00\u4e0b\u624d\u597d\u3002\u201d \u3000\u3000\u9ec4\u7075\u4f38\u624b\u4e92\u51fb\u4e00\u638c\uff0c\u4e00\u4e2a\u8eab\u7740\u9ed1\u5e03\u77ed\u886b\u3001\u957f\u88e4\u7684\u5927\u6c49\u5feb\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u7a7f\u4e00\u8eab\u5de5\u4f5c\u8863\u670d\uff0c\u8eab\u4e0a\u8fd8\u6709\u7740\u6d53\u91cd\u7684\u9152\u7cbe\u6c14\uff0c\u90a3\u662f\u771f\u6b63\u7684\u5de5\u4eba\u8863\u670d\u3002 \u3000\u3000\u4e8e\u56db\u6253\u91cf\u4e86\u90a3\u5927\u6c49\u4e00\u9635\u9053\uff1a\u201c\u5144\u53f0\u662f\u2026\u2026\u201d \u3000\u3000\u9ed1\u8863\u4eba\u63a5\u9053\uff1a\u201c\u6797\u98de\u3002\u201d \u3000\u3000\u4e8e\u56db\u9053\uff1a\u201c\u539f\u6765\u662f\u6797\u5144\uff0c\u5931\u656c\u3001\u5931\u656c\u3002\u201d \u3000\u3000\u6797\u98de\u9053\uff1a\u201c\u4e0d\u7528\u5ba2\u6c14\uff0c\u4e8e\u5144\u6709\u4ec0\u4e48\u6307\u6559\uff0c\u4f46\u8bf7\u5429\u5490\u3002\u201d \u3000\u3000\u4e8e\u56db\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6307\u6559\u4e0d\u6562\u5f53\uff0c\u4e0d\u8fc7\uff0c\u54b1\u4eec\u8981\u7814\u7a76\u51fa\u4e00\u4e2a\u65b9\u6848\u6765\uff0c\u624d\u80fd\u4f7f\u8bf4\u6cd5\u4e00\u81f4\uff0c\u800c\u4f7f\u4eba\u76f8\u4fe1\u3002\u201d \u3000\u3000\u6797\u98de\u9053\uff1a\u201c\u4e8e\u5144\u53ef\u5df2\u6709\u4ec0\u4e48\u8179\u6848\u3002\u201d \u3000\u3000\u4e8e\u56db\u8bf4\u51fa\u4e86\u81ea\u5df1\u7684\u8ba1\u5212\u3002 \u3000\u3000\u6797\u98de\u4e5f\u63d0\u51fa\u4e86\u81ea\u5df1\u7684\u610f\u89c1\u3002 \u3000\u3000\u9ec4\u7075\u770b\u4ed6\u4eec\u5546\u91cf\u597d\u529e\u6cd5\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u8bb0\u7740\uff0c\u8981\u4f7f\u4ed6\u4eec\u4fe1\u4efb\u4f60\u4eec\u3002\u201d \u4e8e\u56db\u9053\uff1a\u201c\u5821\u4e3b\u653e\u5fc3\uff0c\u5728\u4e0b\u5168\u529b\u65bd\u4e3a\uff0c\u72c2\u9f99\u3001\u98de\u9e70\u8feb\u6025\u4e4b\u60c5\u5df2\u6ea2\u4e8e\u8a00\u8868\u4e4b\u95f4\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u4f60\u4eec\u53bb\u5427\u3002\u201d \u3000\u3000\u4e8e\u56db\u5e94\u4e86\u4e00\u58f0\uff0c\u5e26\u7740\u6797\u98de\u5feb\u6b65\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u4e24\u4eba\u7684\u80cc\u5f71\u6d88\u5931\uff0c\u9ec4\u7075\u624d\u53ec\u51fa\u4e94\u5927\u5251\u4f7f\uff0c\u7b11\u9053\uff1a \u201c\u4f60\u4eec\u662f\u5426\u89c9\u7740\uff0c\u6211\u8fd9\u6837\u505a\u4e8b\uff0c\u4f3c\u4e4e\u662f\u592a\u8fc7\u5206\u4e86\u4e9b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5bf9\u4ed8\u9ad8\u5929\u5065\u8fd9\u79cd\u4eba\uff0c \u81ea\u7136\u4e5f\u4e0d\u7528\u4ec0\u4e48\u541b\u5b50\u624b\u6bb5\u4e86\uff0c\u4e0d\u8fc7\uff0c\u5728\u4e0b\u60f3\u4e0d\u901a\uff0c\u653e\u4e86\u8fd9\u4e24\u4e2a\u4eba\u7684\u4f5c\u7528\u4f55\u5728?\u201d \u3000\u3000\u9ec4\u7075\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9ad8\u5929\u5065\u7684\u5b9e\u529b\u592a\u8fc7\u5f3a\u5927\u4e86\uff0c\u5982\u82e5\u4e0d\u80fd\u5f15\u8d77\u4ed6\u4eec\u81ea\u76f8\u6b8b\u6740\uff0c\u53ea\u6015\u5f88\u96be\u4f7f\u4ed6\u4eec\u74e6\u89e3\u2026\u2026\u201d \u3000\u3000\u7b11\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u6211\u5df2\u522b\u4f5c\u5b89\u6392\uff0c\u4f7f\u9ad8\u5929\u5065\u5bf9\u72c2\u9f99\u3001\u98de\u9e70\u751f\u51fa\u4e86\u6000\u7591\uff0c\u4f46\u67e5\u65e0\u5b9e\u636e\uff0c\u4e0d\u4f1a\u7acb\u523b\u5bf9\u4e24\u4eba\u4e0b\u624b\uff0c\u4f46\u4e24\u4eba\u7684\u5185\u5fc3\u4e2d\u5df2\u611f\u89c9\u5230\u5371\u9669\uff0c\u4f1a\u968f\u65f6\u53d1\u751f\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5c31\u7b97\u72c2\u9f99\u548c\u98de\u9e70\u60f3\u80cc\u53db\u9ad8\u5929\u5065\u4e86\uff0c\u4ed6\u53c8\u5982\u4f55\u548c\u6211\u4eec\u8054\u7edc\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u8981\u6d3e\u4e24\u4e2a\u4eba\u8ddf\u7740\u4ed6\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u72c2\u9f99\u3001\u98de\u9e70\u79bb\u5f00\u4e86\u6b64\u5730\u4e4b\u540e\uff0c\u5c31\u5f88\u53ef\u80fd\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u6211\u4e5f\u60f3\u5230\u4e86\uff0c\u4e8e\u56db\u548c\u6797\u98de\u90fd\u4e0d\u662f\u7b80\u5355\u7684\u4eba\uff0c\u72c2\u9f99\u548c\u98de\u9e70\u60f3\u8981\u6740\u4ed6\uff0c\u4e5f\u4e0d\u662f\u4e00\u4ef6\u5f88\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4ed6\u4eec\u5982\u662f\u6740\u4e0d\u4e86\u4e8e\u56db\u3001\u6797\u98de\uff0c\u90a3\u53ea\u6709\u5e26\u7740\u4ed6\u4eec\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u72c2\u9f99\u548c\u98de\u9e70\u6740\u4e0d\u4e86\u4e8e\u56db\u548c\u6797\u98de\uff0c \u81ea\u5df1\u4e5f\u611f\u89c9\u5230\u662f\u4e00\u4ef6\u5f88\u4e22\u4eba\u7684\u4e8b\u60c5\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u8bf4\u51fa\u53bb\uff0c \u81ea\u7136\uff0c\u4e5f\u4e0d\u4f1a\u544a\u8bc9\u9ad8\u5929\u5065\uff0c\u771f\u6b63\u7684\u60c5\u5f62\uff0c\u7ecf\u8fc7\u4e00\u6b21\u4eba\u4e3a\u63a9\u906e\u4e4b\u540e\uff0c\u5c31\u4f1a\u9690\u85cf\u53bb\u5f88\u591a\u79d8\u5bc6\uff0c\u4e0d\u7528\u4e8e\u56db\u548c\u6797\u98de\u60f3\u7406\u7531\uff0c\u4ed6\u4eec\u5c31\u4f1a\u548c\u4e8e\u56db\u7b49\u5546\u91cf\u51fa\u4e00\u4e2a\u529e\u6cd5\u51fa\u6765\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u7b11\u9053\uff1a\u201c\u5821\u4e3b\u7684\u8bbe\u60f3\uff0c\u679c\u7136\u662f\u5929\u8863\u65e0\u7f1d\uff0c\u4f46\u4e0d\u77e5\u4e8e\u56db\u548c\u6797\u98de\uff0c\u662f\u5426\u771f\u7684\u80fd\u591f\u62b5\u6297\u72c2\u9f99\u548c\u98de\u9e70\u5462\uff0c\u8fd9\u4e24\u4eba\u9ed8\u9ed8\u65e0\u540d\uff0c\u6c5f\u6e56\u4e0a\u4ece\u6765\u6ca1\u6709\u542c\u8fc7\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u8fd9\u4e00\u70b9\uff0c\u534e\u5251\u4f7f\u5012\u53ef\u4ee5\u653e\u5fc3\uff0c\u4e24\u4e2a\u4eba\u7684\u6b66\u529f\uff0c\u6211\u5341\u5206\u6e05\u695a\uff0c\u4ed6\u4eec\u6709\u5f88\u5f3a\u5927\u7684\u97e7\u529b\uff0c\u9047\u5f3a\u5219\u5f3a\uff0c\u9047\u5f31\u5219\u5f31\uff0c\u56e0\u4e3a\uff0c\u4ed6\u4eec\u7684\u6b66\u529f\uff0c\u90fd\u662f\u6211\u5728\u6697\u4e2d\u6240\u4f20\u6388\u7684\uff0c\u9664\u4e86\u4ed6\u4eec\u4e24\u4eba\u548c\u6211\u4e4b\u5916\uff0c\u4eca\u5929\u53c8\u591a\u4e86\u4f60\u4eec\u4e94\u4f4d\u77e5\u9053\u8fd9\u4e9b\u5185\u60c5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u54b1\u4eec\u73b0\u5728\u505a\u4e9b\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5927\u5bb6\u4f11\u606f\u4e24\u5929\uff0c\u540e\u5929\u65e9\u4e0a\uff0c\u54b1\u4eec\u8981\u591c\u63a2\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u4e0d\u662f\u548c\u9ad8\u98de\u8679\u6709\u7ea6\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5bf9!\u6211\u4eec\u4e24\u8def\u8fdb\u5175\uff0c\u6211\u7531\u9ad8\u98de\u8679\u5e26\u5165\u9ad8\u5e9c\uff0c\u4f60\u4eec\u7531\u53e6\u4e00\u9762\u8fdb\u5165\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5168\u90e8\u8fdb\u53bb\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d!\u5c11\u8005\u4e00\u4eba\uff0c\u591a\u5219\u4e24\u4e2a\uff0c\u4f59\u4e0b\u4e09\u4e2a\u4eba\uff0c\u5728\u5916\u63a5\u5e94\u3002\u201d \u3000\u3000\u4f3a\u5bd2\u8863\u9053\uff1a \u201c\u54ea\u4e24\u4e2a\u4eba\u8fdb\u5165\u7b2c\u4e00\u5821\uff0c\u54ea\u4e24\u4e2a\u4eba\u5728\u5916\u9762\u63a5\u5e94\uff0c\u5821\u4e3b\u6700\u597d\u4e8b\u5148\u5206\u914d\u4e00\u4e0b\uff0c\u514d\u5f97\u5230\u65f6\u95f4\uff0c\u5927\u5bb6\u4e89\u6267\u4e0d\u4e0b\u3002\u201d \u3000\u3000\u9ec4\u7075\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u7b11\u9053\uff1a \u201c\u6211\u4e5f\u65e0\u6cd5\u5206\u914d\uff0c\u5982\u679c\u4f60\u4eec\u89c9\u7740\u8fd9\u4e24\u4ef6\u4e8b\u5206\u91cf\u4e0d\u540c\uff0c\u6211\u5728\u8c03\u5ea6\u4e0a\u5c31\u6709\u4e9b\u56f0\u96be\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4e00\u76f4\u90fd\u662f\u5821\u4e3b\u5206\u6d3e\u5de5\u4f5c\uff0c\u4ece\u6765\u6ca1\u6709\u4e00\u4e2a\u4eba\u53cd\u5bf9\u8fc7\u5821\u4e3b\u7684\u5206\u6d3e\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u90a3\u4e0d\u540c\uff0c\u4f60\u4eec\u5404\u6709\u7edd\u827a\uff0c\u90fd\u662f\u72ec\u5f53\u4e00\u9762\u7684\u5927\u624d\uff0c\u5f88\u5c11\u96c6\u4e2d\u4e00\u5904\uff0c\u53bb\u505a\u4e00\u4ef6\u4e8b\u60c5\uff0c\u73b0\u5728\uff0c\u4f60\u4eec\u4e94\u4e2a\u4eba\uff0c\u540c\u65f6\u51fa\u52a8\uff0c\u90a3\u5c31\u6709\u4e9b\u4e0d\u540c\u4e86\uff0c\u800c\u4e14\uff0c\u8981\u6211\u5f53\u9762\u8c03\u5ea6\uff0c\u5c31\u7b97\u4f60\u4eec\u90fd\u540c\u610f\uff0c\u6211\u4e5f\u4e0d\u4fbf\u8bf4\u4ec0\u4e48\u3002\u4e0d\u8fc7\uff0c\u6211\u5012\u6709\u4e00\u4e2a\u5f88\u597d\u7684\u529e\u6cd5\uff0c\u5230\u65f6\uff0c\u7531\u4f55\u5251\u4f7f\u4e3b\u6301\uff0c\u4f60\u4eec\u62bd\u7b3a\u51b3\u5b9a\u5982\u4f55?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d\u529e\u6cd5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5821\u4e3b\u987e\u8651\u7684\u5468\u5230\uff0c\u5b9e\u975e\u6211\u4eec\u80fd\u53ca\u4e86\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4ef6\u4e8b\uff0c\u8bf7\u6559\u5821\u4e3b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u8bf4\u5427\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u8fdb\u5165\u7b2c\u4e00\u5821\u7684\u4eba\uff0c\u5982\u82e5\u88ab\u654c\u4eba\u53d1\u89c9\u4e86\uff0c\u5e94\u8be5\u5982\u4f55?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u975e\u8981\u51fa\u624b\uff0c\u4e0d\u59a8\u51fa\u624b\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u8981\u604b\u6218\uff0c\u4eca\u665a\u4e0a\uff0c\u6211\u8981\u5148\u53bb\u52d8\u67e5\u4e00\u4e0b\u7b2c\u4e00\u5821\u7684\u5f62\u52bf\uff0c\u7136\u540e\uff0c\u628a\u4e07\u80dc\u5e26\u6765\u7684\u4eba\u624b\uff0c\u57cb\u4f0f\u5728\u90a3\u91cc\uff0c\u4f5c\u4e3a\u6700\u540e\u7684\u63a5\u5e94\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u5982\u662f\u6211\u4eec\u80fd\u640f\u6740\u5bf9\u65b9\uff0c\u662f\u4e0d\u662f\u4e00\u5b9a\u8981\u628a\u4ed6\u4eec\u5f15\u5165\u57cb\u4f0f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u7528\u4e86\uff0c\u4f60\u4eec\u5982\u82e5\u80fd\u591f\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u6700\u597d\u751f\u64d2\uff0c\u5fc5\u8981\u65f6\uff0c\u4e88\u4ee5\u640f\u6740\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u57cb\u4f0f\u662f\u6700\u540e\u4e00\u9053\u4f0f\u5175\uff0c\u975e\u4e0d\u5f97\u5df2\u65f6\uff0c\u54b1\u4eec\u4e0d\u8981\u52a8\u7528\u3002\u201d \u4f3a\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u5c5e\u4e0b\u5fc3\u4e2d\u6709\u4e00\u4e9b\u6000\u7591\uff0c\u4e0d\u77e5\u9053\u662f\u5426\u53ef\u4ee5\u8bf4\u51fa\u6765?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u8bf4\u5427!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u62c5\u5fc3\u90a3\u4f4d\u9ad8\u516c\u5b50\uff0c\u4e0d\u4f1a\u5b88\u79d8\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u55ef?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u5821\u4e3b\u591c\u63a2\u7b2c\u4e00\u5821\uff0c\u5904\u5883\u5f88\u5371\u9669\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u5c31\u7b97\u54b1\u4eec\u660e\u591c\u8fdb\u5165\u4e86\u7b2c\u4e00\u5821\uff0c\u4e5f\u662f\u4e00\u6837\u6709\u5371\u9669\uff0c\u6211\u4eca\u591c\u53bb\u63a2\u7b2c\u4e00\u5821\u7684\u76ee\u7684\uff0c\u5c31\u662f\u8981\u67e5\u660e\u767d\uff0c\u54b1\u4eec\u6709\u591a\u5927\u7684\u5371\u9669\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u82e6\u7b11\u4e00\u4e0b\u9053\uff1a\u201c\u5821\u4e3b\u4e00\u4eba\u6d89\u9669\uff0c\u4f55\u4e0d\u5e26\u5c5e\u4e0b\u540c\u5f80?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\uff0c\u4e0d\u77e5\u4f55\u5144\u662f\u5426\u77e5\u9053?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7389\u5170\u53cc\u59dd\uff0c\u4e5f\u5728\u7b2c\u4e00\u5821\u4e2d\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u9ad8\u5929\u5065\u6709\u4e00\u4f4d\u5973\u513f\uff0c\u548c\u4e00\u4f4d\u4e49\u5973\uff0c\u6b66\u529f\u90fd\u5341\u5206\u9ad8\u660e\uff0c\u6210\u5c31\u5f3a\u8fc7\u9ad8\u516c\u5b50\u5341\u500d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u5bf9\u7b2c\u4e00\u5821\u7684\u4e8b\uff0c\u4f3c\u662f\u5341\u5206\u6e05\u695a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u82b1\u4e86\u5f88\u591a\u5e74\u7684\u5de5\u592b\uff0c\u624d\u63a2\u51fa\u4e86\u7b2c\u4e00\u5821\u4e00\u4e9b\u5185\u60c5\uff0c\u5c24\u5176\u662f\u4ed6\u90a3\u4f4d\u4e49\u5973\uff0c\u7ec3\u7684\u662f\u4e00\u79cd\u5f88\u90aa\u95e8\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u53ef\u77e5\u9053\u5979\u7684\u540d\u5b57?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u9ad8\u5e9c\u4e0a\u4e0b\uff0c\u90fd\u53eb\u5979\u5927\u5c0f\u59d0\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u7ec3\u7684\u4ec0\u4e48\u6b66\u529f\u5462?\u201d \u3000\u3000\u9ec4\u7075\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4f3c\u4e4e\u662f\u7531\u5929\u7afa\u4f20\u6765\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u542c\u95e8\u4e2d\u4e00\u4f4d\u957f\u8f88\u8bf4\u8fc7\uff0c\u767e\u5e74\u524d\uff0c\u4e00\u4f4d\u5929\u7afa\u5947\u4eba\uff0c\u5230\u8fc7\u4e2d\u539f\u4e00\u6b21\uff0c\u7528\u4e00\u79cd\u5f88\u5947\u602a\u7684\u6b66\u529f\uff0c\u5728\u4e2d\u539f\u6b66\u6797\u9053\u4e0a\uff0c\u6380\u8d77\u4e86\u4e00\u573a\u98ce\u6ce2\uff0c\u5e78\u597d\uff0c\u4ed6\u7559\u5728\u4e2d\u539f\u65f6\u95f4\u4e0d\u4e45\uff0c\u5f88\u5feb\u79bb\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6c5f\u6e56\u4e0a\u786e\u6709\u8fd9\u4e48\u4e00\u4e2a\u4f20\u8bf4\uff0c\u542c\u8bf4\uff0c\u90a3\u4eba\u5230\u4e2d\u539f\u9053\u4e0a\u6765\uff0c\u662f\u4e3a\u4e86\u6293\u4e00\u4e2a\u53db\u5f92\uff0c\u6293\u5230\u4e86\u90a3\u53db\u5f92\u5c31\u79bb\u53bb\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u542c\u8bf4\u90a3\u4eba\u5e76\u6ca1\u6709\u56de\u5230\u5929\u7afa\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bf9!\u4ed6\u6293\u5230\u53db\u5f92\uff0c\u5728\u8def\u4e0a\u9047\u5230\u4e86\u7eff\u6797\u9053\u4e0a\u7684\u9ad8\u624b\u4f0f\u51fb\uff0c\u4f46\u4ed6\u8fd8\u662f\u628a\u90a3\u4f4d\u53db\u5f92\u91cd\u6bd9\u4e86\uff0c\u5e76\u4e14\uff0c\u6740\u4e86\u4f0f\u51fb\u4ed6\u7684\u5168\u90e8\u4eba\u624b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u5f88\u771f\u5b9e\u4e86?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u8ddd\u6b64\u5df2\u6709\u516b\u4e5d\u5341\u5e74\u4e86\uff0c\u6b66\u6797\u4e2d\u4e8b\uff0c\u6ca1\u6709\u6b63\u5f0f\u7684\u8bb0\u8f7d\uff0c\u662f\u4e0d\u662f\u5f88\u771f\u5b9e\uff0c\u5c31\u5f88\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u81ea\u90a3\u6b21\u4e8b\u4ef6\u4e4b\u540e\uff0c\u4ece\u672a\u518d\u542c\u8bf4\u8fc7\u5929\u7afa\u56fd\u6709\u4eba\u5230\u8fc7\u4e2d\u539f\uff0c\u9ad8\u5929\u5065\u90a3\u4f4d\u4e49\u5973\uff0c\u600e\u5b66\u5230\u5929\u7afa\u6b66\u529f\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u8fd9\u4ef6\u4e8b\uff0c\u6c5f\u6e56\u4e0a\u5c1a\u65e0\u4f20\u95fb\uff0c\u6211\u53ea\u662f\u6253\u542c\u51fa\u6765\u8fd9\u4e48\u4e00\u70b9\u6d88\u606f\uff0c\u662f\u5426\u771f\u5b9e\uff0c\u4e5f\u65e0\u6cd5\u8bc1\u660e\u3002\u201d \u3000\u3000\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a \u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u6211\u8981\u5148\u53bb\u89c1\u8bc6\u4e00\u4e0b\u90a3\u4e24\u4f4d\u59d1\u5a18\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u5929\u5065\u90a3\u4f4d\u5973\u513f\uff0c\u53c8\u6709\u4e9b\u4ec0\u4e48\u7279\u6b8a\u7684\u5730\u65b9?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5979\u7528\u4e03\u8272\u5251\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u4e03\u8272\u5251\uff0c\u90a3\u662f\u8bf4\uff0c\u662f\u4e00\u67c4\u5251\u4e0a\uff0c\u6709\u4e03\u79cd\u989c\u8272\u4e86?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e03\u79cd\u989c\u8272\uff0c\u4e03\u628a\u5251\uff0c\u6bcf\u4e00\u67c4\u5251\u90fd\u6709\u7279\u6b8a\u7684\u4f5c\u7528\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u7528\u4e03\u67c4\u5251\u548c\u4eba\u8fc7\u62db\uff0c\u5012\u662f\u4ece\u672a\u542c\u8bf4\u8fc7\u7684\u4e8b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u6709\u89c1\u8bc6\u8fc7\uff0c\u6240\u4ee5\u51c6\u5907\u53bb\u5f00\u5f00\u773c\u754c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u9ad8\u624b\u5982\u4e91\uff0c\u9ad8\u5e9c\u4e2d\u4e24\u4f4d\u59d1\u5a18\uff0c\u5982\u82e5\u771f\u662f\u5404\u8d1f\u7edd\u6280\uff0c\u5821\u4e3b\u4e00\u4e2a\u4eba\u53bb\uff0c\u672a\u514d\u662f\u592a\u8fc7\u8349\u7387\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u53bb\u7684\u4eba\uff0c\u5bf9\u6211\u4e00\u5b9a\u8981\u6709\u5e2e\u52a9\uff0c\u5047\u82e5\u8981\u6211\u56de\u5934\u6765\u7167\u987e\u4ed6\uff0c\u90a3\u5c82\u4e0d\u662f\u8d8a\u5e2e\u8d8a\u5fd9\u4e86?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u81ea\u77e5\u6b66\u529f\u96be\u548c\u5821\u4e3b\u5339\u654c\uff0c\u4f46\u81ea\u4fe1\u8fd8\u53ef\u81ea\u4fdd\uff0c\u5c31\u7b97\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u4e5f\u4e0d\u4f1a\u574f\u4e86\u5821\u4e3b\u7684\u4e8b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f55\u5144\u7684\u6b66\u529f\uff0c\u8db3\u53ef\u4ee5\u53bb\u5f97\uff0c\u4e0d\u8fc7\uff0c\u4f60\u5982\u5fc3\u91cc\u6709\u592a\u591a\u7684\u675f\u7f1a\uff0c\u90a3\u5c31\u5f88\u96be\u65bd\u5c55\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u6240\u8bf4\u675f\u7f1a\u7684\u610f\u601d\uff0c\u5728\u4e0b\u8fd8\u4e0d\u592a\u660e\u767d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u610f\u601d\u662f\u8bf4\uff0c\u9ad8\u5929\u5065\u90a3\u4f4d\u4e49\u5973\uff0c\u6b66\u529f\u5f88\u90aa\uff0c\u505a\u4e8b\u4e5f\u4e0d\u6309\u6b63\u89c4\uff0c\u5979\u5f88\u601c\u624d\uff0c\u4e00\u5957\u91cf\u624d\u7684\u6807\u51c6\uff0c\u5c31\u5728\u4e0b\u542c\u5230\u7684\u6d88\u606f\uff0c\u4f55\u5144\u5f88\u9002\u5408\u5979\u90a3\u6807\u51c6\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u5728\u4e0b\u8ffd\u968f\u5821\u4e3b\uff0c\u5b66\u5230\u4e86\u4e0d\u5c11\u7684\u4e1c\u897f\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u7537\u5b50\u6c49\u3001\u5927\u4e08\u592b\uff0c\u62d4\u5251\u800c\u8d77\uff0c\u6218\u6b7b\u4f55\u61be\uff0c\u4f46\u975e\u8981\u7528\u4e9b\u624b\u6bb5\u4e0d\u53ef\uff0c\u4e5f\u8981\u627e\u4e00\u4e2a\u9002\u5408\u7684\u4eba\u624d\u597d\uff0c\u6211\u63a8\u8350\u53f6\u5144\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6709\u4e00\u70b9\uff0c\u6211\u5fc5\u987b\u8bf4\u660e\u7684\uff0c\u4e5f\u5c31\u662f\u91cf\u624d\u7684\u5c3a\u5ea6\uff0c\u638c\u63e1\u5728\u522b\u4eba\u624b\u4e2d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u82e5\u5e2e\u4e3b\u540c\u610f\u5728\u4e0b\u540c\u5f80\uff0c\u5728\u4e0b\u81ea\u7136\u9075\u547d\u884c\u4e8b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u4eca\u591c\u4e2d\uff0c\u54b1\u4eec\u4e24\u4e2a\u4eba\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u3001\u534e\u5999\u771f\u9f50\u58f0\u63a5\u9053\uff1a\u201c\u54b1\u4eec\u4e5f\u8ddf\u53bb\u77a7\u77a7\u5982\u4f55?\u201d \u3000\u3000\u9ec4\u7075\u6447\u6447\u5934\uff0c\u9053\uff1a \u201c\u4e0d\u884c\uff0c\u54b1\u4eec\u4e0d\u662f\u53bb\u62fc\u547d\u7684\uff0c\u53bb\u4eba\u592a\u591a\uff0c\u6709\u5bb3\u65e0\u76ca\uff0c\u8fd9\u4ef6\u4e8b\u5c31\u8fd9\u4e48\u51b3\u5b9a\u4e86\uff0c\u4f55\u5144\uff0c\u597d\u597d\u7684\u5750\u606f\u4e00\u4e0b\uff0c\u4eca\u591c\u4e8c\u66f4\u65f6\u5206\uff0c\u54b1\u4eec\u8d70\u4e00\u8d9f\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u7adf\u7136\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u5c0f\u59b9\u6709\u4e00\u5f97\u4e4b\u611a\uff0c\u5949\u4e0a\u5821\u4e3b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6700\u597d\u5821\u4e3b\u5728\u7b2c\u4e00\u5821\u5916\uff0c\u5b89\u6392\u4e0b\u63a5\u5e94\u7684\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u8fd9\u4ef6\u4e8b\u5c31\u8bf7\u534e\u59d1\u5a18\u591a\u8d39\u5fc3\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u770b\u5982\u82e5\u8981\u5b89\u6392\u4eba\u624b\u63a5\u5e94\uff0c\u90a3\u5c31\u8981\u5168\u529b\u4ee5\u8d74\uff0c\u5982\u82e5\u5821\u4e3b\u771f\u7684\u88ab\u4eba\u8feb\u4e86\u51fa\u6765\uff0c\u53ea\u6015\u4f1a\u6709\u4e00\u573a\u6fc0\u70c8\u4e4b\u6218\uff0c\u80dc\u8d1f\u4e4b\u5206\uff0c\u5bf9\u53cc\u65b9\u90fd\u6709\u5f88\u5927\u7684\u5173\u7cfb\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u534e\u5251\u4f7f\u6307\u6325\u57cb\u4f0f\uff0c\u5b89\u6392\u4eba\u7269\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4e07\u5144\uff0c\u5c60\u5144\u548c\u6211\uff0c\u5168\u6570\u51fa\u52a8\uff0c\u53e6\u5916\uff0c\u56db\u4e2a\u6697\u5668\u9ad8\u624b\u968f\u884c\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u534e\u59d1\u5a18\uff0c\u8fd9\u4e0d\u50cf\u57cb\u4f0f\uff0c\u597d\u50cf\u8981\u51b3\u6218\u4f3c\u7684\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u672c\u6765\u5c31\u662f\u4e00\u573a\u5f88\u4e25\u91cd\u7684\u640f\u6218\uff0c\u5e0c\u671b\u7528\u4e0d\u7740\u6211\u4eec\u51fa\u624b\uff0c\u5982\u662f\u5f88\u4e0d\u5e78\u7684\u51fa\u4e86\u624b\uff0c\u90a3\u5c31\u662f\u4e00\u573a\u51b3\u6218\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u534e\u5251\u4f7f\u80fd\u4e0d\u80fd\u8bf4\u6e05\u695a\u4e00\u4e9b?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u7684\u9ad8\u5929\u5065\uff0c\u5200\u6cd5\u5353\u7edd\uff0c\u4eba\u79f0\u4e2d\u5dde\u7b2c\u4e00\u5200\uff0c\u4f46\u4ed6\u5e76\u4e0d\u662f\u6700\u53ef\u6015\u7684\u4eba\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a \u201c\u96be\u9053\u7b2c\u4e00\u5821\u4e2d\uff0c\u8fd8\u6709\u6bd4\u9ad8\u5929\u5065\u66f4\u5389\u5bb3\u7684\u4eba\u7269\u4e0d\u6210?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5bf9\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5434\u53cc\u5973\uff0c\u4e5f\u5c31\u662f\u9ad8\u5929\u5065\u90a3\u4f4d\u4e49\u5973\uff0c\u7b2c\u4e00\u5821\u7684\u5927\u5c0f\u59d0\uff0c\u5979\u672c\u59d3\u5434\uff0c\u540d\u53cc\u5973\uff0c\u8ba4\u5728\u9ad8\u5929\u5065\u819d\u4e0b\u4e4b\u540e\uff0c\u6539\u540d\u53eb\u4f5c\u9ad8\u65e0\u53cc\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5434\u53cc\u5973?\u8fd9\u540d\u5b57\u4ece\u672a\u542c\u8fc7\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5979\u8fd8\u6709\u4e00\u4e2a\u5916\u53f7\uff0c\u4f60\u9ebb\u5b50\u4e00\u5b9a\u542c\u8fc7\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8bf4\u8bf4\u770b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e94\u5e74\u524d\uff0c\u50cf\u4e00\u9053\u95ea\u7535\u822c\u63a0\u8fc7\u6c5f\u6e56\u7684\u5929\u5973\uff0c\u90a3\u4f4d\u59d1\u5a18\u4f60\u8be5\u77e5\u9053\u5427?\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u4f1a\u662f\u5979?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5bf9\uff0c\u5c31\u662f\u5979\uff0c\u9ad8\u5929\u5065\u8d39\u5c3d\u4e86\u5fc3\u673a\uff0c\u624d\u628a\u5979\u6536\u5165\u7b2c\u4e00\u5821\u4e2d\u3002\u201d \u3000\u3000\u8bed\u6c14\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5821\u4e3b\u7684\u6d88\u606f\u5f88\u7075\u901a\uff0c\u4e5f\u82b1\u8d39\u4e86\u5f88\u5927\u7684\u5fc3\u8840\uff0c\u53ea\u662f\uff0c\u5f97\u5230\u7684\u6d88\u606f\uff0c\u4e0d\u591f\u5b8c\u6574\u3002\u7b2c\u4e00\u5821\u4e2d\u7b2c\u4e8c\u4e2a\u53ef\u6015\u7684\u4eba\u7269\uff0c\u662f\u9ad8\u5929\u5065\u771f\u6b63\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u770b\u8d77\u6765\uff0c\u534e\u5251\u4f7f\u77e5\u9053\u7684\u4e8b\u60c5\uff0c\u4f3c\u662f\u6bd4\u6211\u8fd8\u591a\u4e00\u4e9b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u5bf9!\u56e0\u4e3a\uff0c\u6211\u6709\u4e00\u4e2a\u4f84\u5973\u5728\u7b2c\u4e00\u5821\u4e2d\u4f5c\u4e2b\u5934\uff0c\u5c31\u662f\u90a3\u4f4d\u771f\u6b63\u9ad8\u59d1\u5a18\u7684\u8fd1\u8eab\u4e2b\u5934\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u90a3\u5c31\u65e0\u602a\u4e86\uff0c\u5173\u4e8e\u9ad8\u5929\u5065\u7684\u5973\u513f\uff0c\u53c8\u662f\u600e\u4e48\u56de\u4e8b?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u5821\u4e3b\u7684\u6d88\u606f\u4e0d\u9519\uff0c\u5979\u662f\u7528\u7684\u4e03\u8272\u5251\uff0c\u4e03\u628a\u5251\uff0c\u4e03\u79cd\u989c\u8272\uff0c\u4e03\u79cd\u4f5c\u7528\uff0c\u5979\u624d\u662f\u7b2c\u4e00\u5821\u4e2d\uff0c\u7b2c\u4e8c\u4e2a\u53ef\u6015\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5927\u540d\u9f0e\u9f0e\u7684\u9ad8\u5929\u5065\uff0c\u96be\u9053\u53ea\u662f\u7b2c\u4e00\u5821\u4e2d\u7b2c\u4e09\u53f7\u4eba\u7269?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u90a3\u5012\u4e0d\u662f\uff0c\u7b2c\u4e00\u5821\u7684\u6743\u529b\u4ecd\u63e1\u5728\u9ad8\u5929\u5065\u624b\u4e2d\uff0c\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\u90fd\u5f88\u542c\u9ad8\u5929\u5065\u7684\u8bdd\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u9ad8\u5929\u5065\u8fd9\u4e48\u4e00\u4e2a\u4f2a\u541b\u5b50\uff0c\u504f\u4f1a\u6709\u8fd9\u4e48\u4e00\u4e2a\u5973\u513f\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5982\u679c\u8ffd\u51fa\u6765\u7684\u662f\u9ad8\u5e7d\u5170\u6216\u5434\u53cc\u5973\uff0c\u90a3\u90fd\u514d\u4e0d\u4e86\u4e00\u573a\u5927\u6218\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5408\u9634\u9633\u5821\u4e94\u5927\u5251\u4f7f\u4e4b\u529b\uff0c\u6597\u6597\u5434\u53cc\u5973\uff0c\u6216\u662f\u9ad8\u5e7d\u5170\uff0c\u5927\u6982\u603b\u662f\u53ef\u4ee5\u5427?\u201d \u3000\u3000\u534e\u5999\u771f\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u8bf4\u8bdd\u5440!\u4f60\u5bf9\u5979\u4eec\u5f88\u6e05\u695a\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u591a\u8bf4\u7ed9\u6211\u4eec\u4e00\u4e9b\u8d44\u6599\u5462?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u56e0\u4e3a\uff0c\u6211\u89c9\u7740\uff0c\u54b1\u4eec\u4e94\u4e2a\u4eba\u5408\u529b\u5bf9\u5979\u4eec\uff0c\u53ea\u6015\u4e5f\u6ca1\u6709\u5fc5\u80dc\u7684\u628a\u63e1\u3002\u201d \u9ec4\u7075\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u5982\u82e5\u628a\u6211\u4e5f\u7b97\u4e0a\u53bb\u5462?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5821\u4e3b\u7684\u6b66\u529f\uff0c\u9ad8\u6df1\u96be\u6d4b\uff0c\u4e0d\u8fc7\uff0c\u6700\u591a\u4e5f\u53ea\u80fd\u5bf9\u4ed8\u5979\u4eec\u4e00\u4e2a\u3002\u201d \u3000\u3000\u9ec4\u7075\u4e00\u7b11\u9053\uff1a \u201c\u4f60\u4eec\u8fd8\u662f\u9ad8\u4f30\u4e86\u6211\uff0c\u8001\u5b9e\u8bf4\uff0c\u5bf9\u4ed8\u5434\u53cc\u5973\uff0c\u6211\u6ca1\u628a\u63e1\uff0c\u5bf9\u4ed8\u9ad8\u5e7d\u5170\u4e5f\u4e00\u6837\u6ca1\u628a\u63e1\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u8fd9\u662f\u4e0d\u662f\u592a\u8c26\u865a\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u6211\u78b0\u4e0a\u5434\u53cc\u5973\uff0c\u5341\u6709\u4e5d\u5fc5\u8d25\uff0c\u5982\u82e5\u78b0\u4e0a\u4e03\u8272\u5251\uff0c\u4e5f\u5341\u5206\u9ebb\u70e6\uff0c\u6b66\u529f\u4e00\u9053\uff0c\u867d\u7136\u5341\u5206\u535a\u6742\uff0c\u4f46\u4e03\u8272\u5251\uff0c\u4f60\u4eec\u54ea\u4e2a\u89c1\u8fc7?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u4e00\u4e2a\u4eba\u7528\u4e03\u628a\u5251\uff0c\u6709\u4e03\u79cd\u4f5c\u7528\uff0c\u53ef\u662f\u4ece\u672a\u542c\u8fc7\u7684\u4e8b\uff0c\u90a3\u662f\u4e00\u79cd\u4ec0\u4e48\u6837\u5b50\u7684\u6b66\u529f\uff0c\u6211\u8fde\u60f3\u4e5f\u60f3\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u7b11\u4e00\u7b11\u9053\uff1a \u201c\u5173\u4e8e\u4e03\u8272\u5251\u6b66\u529f\uff0c\u6211\u5012\u662f\u542c\u5230\u4e00\u4e9b\uff0c\u4e0d\u8fc7\uff0c\u662f\u4e0d\u662f\u6b63\u786e\uff0c\u6211\u5374\u4e0d\u6562\u62c5\u4fdd\uff0c\u8bf8\u4f4d\u8981\u4e0d\u8981\u542c\u4e00\u542c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d\u554a!\u7eb5\u662f\u9053\u542c\u9014\u8bf4\uff0c\u4e5f\u6bd4\u5b8c\u5168\u4e0d\u77e5\u9053\u7684\u597d\u4e00\u4e9b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u4e5f\u4e0d\u5b8c\u5168\u662f\u9053\u542c\u9014\u8bf4\uff0c\u6211\u90a3\u4f4d\u4f84\u5973\u770b\u5230\u7684\uff0c\u7ed9\u6211\u900f\u9732\u4e86\u70b9\u5185\u5bb9\uff0c\u4e0d\u8fc7\uff0c\u9ad8\u5e7d\u5170\u7ec3\u5251\u4e4b\u65f6\uff0c\u975e\u5e38\u79d8\u5bc6\uff0c\u7eb5\u7136\u662f\u8d34\u8eab\u5973\u5a62\uff0c\u4e5f\u4e0d\u80fd\u7aa5\u4f3a\uff0c\u4f46\u6211\u90a3\u4f4d\u4f84\u5973\uff0c\u8ffd\u968f\u5979\u4e94\u516d\u5e74\uff0c\u4e00\u5411\u88ab\u5979\u89c6\u4f5c\u5fc3\u8179\uff0c\u4e5f\u53ea\u89c1\u8fc7\u5979\u4e00\u6b21\u7ec3\u5251\uff0c\u542c\u8bf4\uff0c\u4e03\u8272\u5251\u53ef\u4ee5\u540c\u65f6\u5728\u5979\u7684\u8eab\u4fa7\u98de\u821e\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u600e\u4e48\u53ef\u80fd\u5462?\u4e24\u53ea\u624b\uff0c\u6700\u591a\u80fd\u7528\u56db\u652f\u5251\uff0c\u90a3\u5df2\u7ecf\u4e0d\u5f97\u4e86\u5566\uff0c\u4e03\u652f\u5251\uff0c\u53c8\u5982\u4f55\u4e00\u4e2a\u7528\u6cd5\u5462?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u8fd9\u4e00\u70b9\uff0c\u5f53\u65f6\u6211\u4e5f\u4e0d\u592a\u660e\u767d\uff0c\u4e0d\u8fc7\uff0c\u4e8b\u540e\u6211\u7ecf\u8fc7\u4e86\u4e00\u756a\u4ed4\u7ec6\u7684\u63a8\u6572\u4e4b\u540e\uff0c\u60f3\u51fa\u4e86\u4e00\u4e2a\u53ef\u80fd\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u613f\u95fb\u5176\u8be6\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u53ea\u6709\u4e24\u53ea\u624b\uff0c\u64cd\u7eb5\u4e03\u652f\u5251\u7684\u53d8\u5316\uff0c\u5b9e\u5728\u662f\u4e00\u4ef6\u4e0d\u592a\u53ef\u80fd\u7684\u4e8b\uff0c\u53ea\u6709\u4e00\u4e2a\u529e\u6cd5\uff0c\u53ef\u4ee5\u4f7f\u4e03\u652f\u5251\uff0c\u90fd\u4e0d\u81f4\u8131\u79bb\u638c\u63e1\uff0c\u90a3\u5c31\u5728\u4e03\u5251\u4e4b\u540e\uff0c\u7cfb\u4e00\u6839\u7ec6\u7d22\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u7edd\u4e0d\u53ef\u80fd\u518d\u6709\u7b2c\u4e8c\u4e2a\u529e\u6cd5\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u54b1\u4eec\u5c31\u7b97\u60f3\u51fa\u4e86\u5176\u4e2d\u539f\u56e0\uff0c\u4f46\u6211\u4e00\u76f4\u60f3\u4e0d\u51fa\u5bf9\u4ed8\u7684\u65b9\u6cd5\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u5982\u82e5\u4e16\u4e0a\u771f\u6709\u5bf9\u4ed8\u4e03\u8272\u5251\u7684\u529e\u6cd5\uff0c\u4e5f\u8981\u770b\u8fc7\u5979\u65bd\u5c55\u4e4b\u5ea6\uff0c\u624d\u80fd\u60f3\u4e2a\u529e\u6cd5\u51fa\u6765\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u534e\u59d1\u5a18\uff0c\u5bf9\u90a3\u5434\u53cc\u5973\uff0c\u4f60\u53c8\u77e5\u9053\u591a\u5c11?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u53ea\u662f\u542c\u4eba\u8bf4\u7684\uff0c\u5e0c\u671b\u8bf8\u4f4d\u542c\u4e86\uff0c\u4e0d\u8981\u5f71\u54cd\u5230\u4f60\u4eec\u7684\u5fc3\u7406\u624d\u597d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u4f60\u8bf4\u5427!\u5728\u573a\u4e4b\u4eba\uff0c\u90fd\u662f\u4e45\u5386\u6c5f\u6e56\u7684\u4eba\u7269\uff0c\u7ecf\u8fc7\u4e86\u4e0d\u5c11\u5927\u98ce\u5927\u6d6a\uff0c\u5c31\u7b97\u542c\u5230\u4e86\u4ec0\u4e48\u6050\u6016\u3001\u9707\u60ca\u7684\u4e8b\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u5413\u5f97\u5168\u65e0\u6597\u5fd7\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u81ea\u5df1\u4ea6\u4e0d\u76f8\u4fe1\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1b\u201c\u5434\u53cc\u5973\u7a76\u7adf\u6709\u4ec0\u4e48\u7279\u6b8a\u4e4b\u5904?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u542c\u8bf4\u5979\u7ec3\u6210\u4e86\u4e00\u79cd\u638c\u529b\uff0c\u90a3\u662f\u4e00\u79cd\u5f88\u5947\u602a\u7684\u638c\u529b\uff0c\u4e2d\u4eba\u4e4b\u540e\uff0c\u4e0d\u89c1\u4f24\u75d5\uff0c\u4f46\u5185\u90e8\u5374\u5f00\u59cb\u8150\u574f\u3002\u201d \u3000\u3000\u5728\u573a\u4e4b\u4eba\uff0c\u5168\u90fd\u542c\u5f97\u4e00\u6014\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u6b66\u529f\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u597d\u50cf\u53eb\u4ec0\u4e48\u6d88\u9b42\u6467\u5fc3\u638c\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u51fa\uff0c\u4e2d\u539f\u6b66\u5b66\u95e8\u6d3e\u4e2d\uff0c\u6709\u8fd9\u4e48\u4e00\u79cd\u6b66\u529f\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5434\u53cc\u5973\u7684\u6b66\u529f\uff0c\u975e\u5f97\u81ea\u4e2d\u539f\u95e8\u6d3e\u4e4b\u5185\uff0c\u8fd9\u4e00\u70b9\uff0c\u5927\u6982\u53ef\u4ee5\u80af\u5b9a\u4e86\u3002\u800c\u4e14\uff0c\u5979\u4e00\u8eab\u8be1\u5f02\u7684\u6b66\u529f\u4e2d\uff0c\u4e5f\u786e\u6709\u51e0\u79cd\u53ef\u6015\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u5979\u4f3c\u662f\u76f2\u672a\u5c3d\u610f\uff0c\u4f46\u5374\u7a81\u7136\u4f4f\u53e3\u3002 \u3000\u3000\u534e\u5999\u771f\u7a81\u7136\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u5904\u5730\u65b9\uff0c\u8fdb\u5165\u7b2c\u4e00\u5821\u7684\u4eba\uff0c\u4e0d\u53ef\u4e0d\u77e5\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u795e\u5200\u5802\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5bf9!\u795e\u5200\u5802\uff0c\u90a3\u662f\u9ad8\u5929\u5065\u4eb2\u81ea\u63a7\u5236\u7684\u5b9e\u529b\uff0c\u4f46\u4e0d\u77e5\u5821\u4e3b\u5bf9\u795e\u5200\u5802\uff0c\u53c8\u77e5\u9053\u591a\u5c11?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u6709\u8fd9\u4e48\u4e00\u4e2a\u5730\u65b9\uff0c\u5374\u4e0d\u77e5\u8be6\u60c5\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u6709\u9650\u5f97\u5f88\uff0c\u56e0\u4e3a\uff0c\u795e\u5200\u5802\u624d\u662f\u7b2c\u4e00\u5821\u4e2d\u552f\u4e00\u795e\u79d8\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u534e\u59d1\u5a18\u77e5\u6653\u591a\u5c11\uff0c\u5c31\u8bf4\u591a\u5c11\u5427\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u90a3\u662f\u7b2c\u4e00\u5821\u4e2d\u4e00\u5904\u9690\u79d8\u4e4b\u5730\uff0c\u91cc\u9762\u4f4f\u4e86\u4e00\u4e9b\u5e74\u9752\u4eba\uff0c\u8fd9\u4e9b\u4eba\uff0c\u90fd\u662f\u9ad8\u5929\u5065\u4eb2\u81ea\u8c03\u6559\u51fa\u6765\u7684\u7528\u5200\u9ad8\u624b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1b\u201c\u53ea\u6709\u8fd9\u4e9b?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u795e\u79d8\u5904\uff0c\u662f\u4ed6\u4eec\u8bad\u7ec3\u7684\u65b9\u6cd5\uff0c\u7b2c\u4e00\u5821\u7edf\u6cbb\u7684\u6c5f\u6e56\u7ec4\u5408\uff0c\u5df2\u7ecf\u5341\u5206\u5e9e\u5927\uff0c\u6240\u4ee5\uff0c\u795e\u5200\u5802\u7684\u4eba\uff0c\u4e00\u76f4\u6ca1\u6709\u5728\u6c5f\u6e56\u4e0a\u51fa\u73b0\u8fc7\uff0c\u6ca1\u6709\u4eba\u77e5\u9053\u8fd9\u4e9b\u4eba\u7a76\u7adf\u6709\u591a\u5927\u7684\u6210\u5c31\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u4e2d\u6709\u5982\u6b64\u5f3a\u5927\u7684\u5b9e\u529b\uff0c\u65e0\u602a\u6c5f\u6e56\u4e0a\uff0c\u4e00\u76f4\u6ca1\u6709\u4eba\u80fd\u8f7b\u5165\u7b2c\u4e00\u5821\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e5f\u8bb8\u6709\u4e0d\u5c11\u4eba\u53bb\u8fc7\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u5df2\u7ecf\u65e0\u6cd5\u628a\u89c1\u5230\u7684\u4e8b\u7269\uff0c\u6cc4\u9732\u4e8e\u6c5f\u6e56\u4e4b\u4e0a\u4e86\u3002\u201d \u3000\u3000\u4e00\u76f4\u5f88\u5c11\u8bf4\u8bdd\u7684\u4e07\u80dc\uff0c\u5ffd\u7136\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u9ad8\u98de\u8679\u88ab\u8feb\u670d\u6bd2\uff0c\u72c2\u9f99\u548c\u98de\u9e70\u5931\u8e2a\uff0c\u8fd9\u4e9b\u4e8b\uff0c\u7b2c\u4e00\u5821\u4e2d\u5c82\u4f1a\u4e0d\u77e5?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5c31\u7b97\u9ad8\u98de\u8679\u6015\u6bd2\u53d1\u800c\u6b7b\uff0c\u4e0d\u6562\u8bf4\u51fa\u53bb\uff0c\u9ad8\u5929\u5065\u4e5f\u5e94\u8be5\u77e5\u9053\u4e86\uff0c\u54b1\u4eec\u53ea\u6709\u4e00\u70b9\u5e78\u8fd0\u7684\u673a\u4f1a\uff0c\u90a3\u5c31\u662f\u9ad8\u5929\u5065\u4e0d\u5728\u5821\u4e2d\uff0c\u7fa4\u9f99\u65e0\u9996\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u4eec\u65e0\u6cd5\u6709\u6240\u63aa\u65bd\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6700\u597d\uff0c\u54b1\u4eec\u7948\u6c42\u9ad8\u5929\u5065\u5728\u7b2c\u4e00\u5821\u4e2d\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u534e\u59d1\u5a18\uff0c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u56e0\u4e3a\uff0c\u9ad8\u5929\u5065\u4e0d\u5728\u7b2c\u4e00\u5821\u4e2d\uff0c\u7ba1\u7406\u7b2c\u4e00\u5821\u4e2d\u7684\u4e8b\u52a1\u5927\u6743\uff0c\u5c31\u843d\u5728\u4e86\u5434\u53cc\u5973\u7684\u8eab\u4e0a\uff0c\u9ad8\u5929\u5065\u867d\u7136\u667a\u8c0b\u767e\u51fa\uff0c\u4f46\u5728\u54b1\u4eec\u7684\u5fc3\u76ee\u4e2d\uff0c\u8fd8\u6709\u8f68\u8ff9\u53ef\u5bfb\uff0c\u5434\u53cc\u5973\u90a3\u5c31\u5168\u4e0d\u662f\u90a3\u56de\u4e8b\u4e86\uff0c\u5979\u8981\u5982\u4f55\u63aa\u65bd\u4e86\uff0c\u54b1\u4eec\u5b8c\u5168\u65e0\u6cd5\u731c\u6d4b\uff0c\u800c\u4e14\uff0c\u5728\u5979\u624b\u4e2d\u51fa\u4e86\u4e8b\uff0c\u5979\u5fc5\u4f1a\u5168\u529b\u4ee5\u8d74\uff0c\u4e00\u65e6\u53d1\u73b0\u4e86\u4ec0\u4e48\uff0c\u5fc5\u7136\u82e6\u8ffd\u4e0d\u820d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5012\u4e5f\u6709\u7406\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u4e3a\u4eca\u4e4b\u8ba1\uff0c\u5148\u60f3\u6cd5\u5b50\uff0c\u628a\u5434\u53cc\u5973\u548c\u9ad8\u5e7d\u5170\u5236\u670d\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8bf4\u8bf4\u7b80\u5355\uff0c\u4f46\u8981\u5236\u670d\u8fd9\u4e24\u4e2a\u4e2b\u5934\uff0c \u53ea\u6015\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u4e86\u3002\u201d \u5200\u80dc\u9053\uff1a \u201c\u54b1\u4eec\u5c06\u8ba1\u5c31\u8ba1\uff0c\u628a\u4e24\u4e2a\u4e2b\u5934\u5f15\u51fa\u6765\uff0c\u5408\u54b1\u4eec\u51e0\u4eba\u4e4b\u529b\uff0c\u4e88\u4ee5\u640f\u6740\u3002\u201d \u9ec4\u7075\u9053\uff1a \u201c\u5434\u53cc\u5973\u4e3a\u4ec0\u4e48\u7518\u4e3a\u9ad8\u5929\u5065\u6240\u56f0\uff0c\u8fd9\u4e2d\u95f4\uff0c\u5fc5\u6709\u539f\u56e0\u3002\u201d \u534e\u5999\u771f\u9053\uff1a\u201c\u81ea\u4ece\u8e0f\u5165\u7b2c\u4e00\u5821\u540e\uff0c\u5979\u597d\u50cf\u6d88\u5931\u7684\u4e91\u70df\uff0c\u4e0d\u5728\u518d\u6c5f\u6e56\u9732\u9762\uff0c\u9ad8\u5929\u5065\u7528\u4ec0\u4e48\u65b9\u6cd5\uff0c\u7adf\u7136\u4f7f\u5979\u7518\u613f\u86f0\u4f0f\u7b2c\u4e00\u5821\u4e2d?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u53ea\u8981\u54b1\u4eec\u627e\u51fa\u8fd9\u4e2a\u539f\u56e0\uff0c\u5c31\u80fd\u4f7f\u5434\u53cc\u5973\u8131\u79bb\u7b2c\u4e00\u5821\u2026\u2026\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u54b1\u4eec\u5148\u53bb\u7b2c\u4e00\u5821\u770b\u770b\uff0c\u7167\u54b1\u4eec\u7684\u5e94\u53d8\u51c6\u5907\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d\uff01\u5c31\u8fd9\u4e48\u51b3\u5b9a\u4e86\uff0c\u6211\u548c\u4f55\u5251\u4f7f\u5982\u82e5\u5728\u4e94\u66f4\u65f6\u5206\uff0c\u8fd8\u672a\u51fa\u6765\uff0c\u4f60\u4eec\u5c31\u4e0d\u7528\u7b49\u4e86\uff0c\u9634\u9633\u5821\u4e2d\u4e8b\u52a1\uff0c\u6b64\u540e\u5c31\u5f52\u534e\u5251\u4f7f\u7387\u9886\uff0c\u5927\u5bb6\u4f11\u606f\u4e00\u4e0b\u5427\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u6211\u5f3a\u715e\u4e86\u4e5f\u662f\u4e00\u4e2a\u5973\u6d41\u4e4b\u8f88\uff0c\u6211\u770b\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u6325\u6325\u624b\uff0c\u9053\uff1a\u201c\u5f53\u4ec1\u4e0d\u8ba9\uff0c\u534e\u5251\u4f7f\u4e0d\u7528\u591a\u8bf4\u4e86\u3002\u201d \u3000\u3000\u4e8c\u66f4\u65f6\u5206\uff0c\u9ec4\u7075\u548c\u4f55\u5bd2\u8863\u6362\u4e86\u4e00\u8eab\u591c\u884c\u8863\uff0c\u5e26\u4e86\u5175\u5203\u3001\u6697\u5668\uff0c\u76f4\u6251\u7b2c\u4e00\u5821\u3002 \u3000\u3000\u5c31\u5728\u4e24\u4eba\u53bb\u540e\u4e0d\u4e45\uff0c\u53f6\u957f\u9752\u3001\u534e\u5999\u771f\u3001\u5c60\u65e0\u65b9\u3001\u4e07\u80dc\uff0c\u5e26\u4e86\u5341\u4e2a\u5251\u624b\uff0c\u4e5f\u79bb\u5f00\u4e86\u4f5c\u574a\u3002 \u3000\u3000\u4ed6\u4eec\u65e9\u5df2\u7ecf\u6709\u4e86\u5f88\u7cbe\u5bc6\u7684\u8ba1\u5212\uff0c\u6240\u4ee5\u884c\u52a8\u8d77\u6765\uff0c\u5341\u5206\u5feb\u901f\u3002 \u3000\u3000\u7b2c\u4e00\u5821\u5360\u5730\u5f88\u5927\uff0c\u6709\u4e00\u90e8\u5206\u5efa\u7b51\u5728\u5c71\u5761\u4e0a\uff0c\u5821\u524d\u677e\u7af9\u73af\u7ed5\uff0c\u4f46\u5374\u7ecf\u8fc7\u4e86\u5f88\u7cbe\u5bc6\u7684\u4fee\u6574\uff0c\u96c5\u81f4\u4e2d\uff0c\u4e0d\u59a8\u788d\u8fbd\u9614\u7684\u89c6\u7ebf\u3002 \u3000\u3000\u5bbd\u7ea6\u4e09\u4e08\u7684\u62a4\u5821\u6cb3\uff0c\u5f15\u5c71\u6cc9\u6c47\u6210\u4e86\u4e00\u80a1\u6df1\u8fc7\u4e24\u4e08\u7684\u6e05\u6d41\u3002\u4e09\u4e08\u516d\u5c3a\u9ad8\u9752\u7816\u4fee\u7b51\u7684\u5821\u5899\uff0c\u5916\u9762\u5149\u6ed1\u5f02\u5e38\u3002 \u3000\u3000\u5c31\u7b97\u7b2c\u4e00\u6d41\u7684\u8f7b\u529f\uff1b\u4e5f\u65e0\u6cd5\u5728\u4e09\u4e08\u5916\u62d4\u8eab\u800c\u8d77\uff0c\u8d8a\u8fc7\u62a4\u5821\u6cb3\uff0c\u518d\u7a9c\u5347\u4e09\u4e08\u516d\u5c3a\uff0c\u767b\u4e0a\u57ce\u5821\u3002 \u3000\u3000\u7b2c\u4e00\u5821\u7684\u6b63\u95e8\u5904\u6709\u4e00\u5ea7\u4e24\u4e08\u591a\u5bbd\u7684\u6728\u6865\uff0c\u6865\u4e0a\u9ad8\u6311\u56db\u76cf\u6c14\u6b7b\u98ce\u706f\uff0c\u65b9\u5706\u6570\u4e08\u5185\uff0c\u4eae\u5982\u767d\u663c\u3002 \u3000\u3000\u5821\u95e8\u4e24\u4fa7\uff0c\u5c31\u9752\u7816\u5821\u5899\u4e0a\uff0c\u5efa\u4e86\u4e24\u5ea7\u7a81\u8d77\u7684\u7889\u697c\uff0c\u9ad8\u8fc7\u5821\u5899\u4e24\u4e08\u3002 \u3000\u3000\u90a3\u662f\u7b2c\u4e00\u5821\u7684\u4e24\u53ea\u773c\u775b\uff0c\u5982\u5728\u767d\u5929\uff0c\u7ad9\u5728\u8fd9\u4e24\u5ea7\u7889\u697c\u4e0a\uff0c\u6781\u5c3d\u76ee\u529b\uff0c\u5927\u7ea6\u53ef\u770b\u5230\u5341\u91cc\u4e4b\u5185\u7684\u8f66\u9a6c\u884c\u4eba\u3002 \u3000\u3000\u5e78\u597d\u662f\u591c\u665a\uff0c\u4eca\u591c\u53c8\u65e0\u6708\u3002 \u3000\u3000\u9ec4\u7075\u548c\u4f55\u5bd2\u8863\u85cf\u8eab\u5728\u677e\u7af9\u6797\u5185\uff0c\u7aa5\u4f3a\u4e86\u4e0d\u77ed\u7684\u65f6\u95f4\uff0c\u4f46\u4e24\u4eba\u4e00\u76f4\u60f3\u4e0d\u51fa\u8fdb\u5165\u7b2c\u4e00\u5821\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u6211\u4eec\u8fdb\u5165\u7b2c\u4e00\u5821\u7684\u673a\u4f1a\uff0c\u5b9e\u5728\u4e0d\u5927\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u603b\u4e0d\u80fd\u5c31\u8fd9\u6837\u9000\u56de\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u5728\u60f3\u54b1\u4eec\u7531\u6865\u4e0a\u8d70\u8fc7\u53bb\uff0c\u662f\u673a\u4f1a\u4e4b\u4e00\u3002\u201d\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u90a3\u56db\u76cf\u5f88\u660e\u4eae\u7684\u706f\uff0c\u53ef\u4ee5\u7167\u6e05\u695a\u6865\u4e0a\u98de\u8fc7\u53bb\u7684\u4e00\u53ea\u5c0f\u9e1f\uff0c\u5012\u4e0d\u5982\u7ed5\u5230\u540e\u9762\u770b\u770b\uff0c\u4e5f\u8bb8\uff0c\u90a3\u4e00\u7247\u4f9d\u5c71\u5efa\u7b51\u7684\u5730\u65b9\uff0c\u4f1a\u7559\u4e0b\u673a\u4f1a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u9ad8\u5929\u5065\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u6211\u60f3\u5bb9\u6613\u6df7\u8fdb\u53bb\u7684\u5730\u65b9\uff0c\u6212\u5907\u5f97\u66f4\u4e3a\u68ee\u4e25\u3002\u201d \u4f55\u5bd2\u8863\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u54b1\u4eec\u53ea\u597d\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u4f1a\u4e0d\u4f1a\u6c34?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6e38\u8fc7\u53bb?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6e38\u8fc7\u53bb\uff0c\u7528\u58c1\u864e\u529f\uff0c\u722c\u4e0a\u4e09\u56db\u4e08\u7684\u7816\u5899\uff0c\u4e5f\u8bb8\u54b1\u4eec\u90fd\u53ef\u4ee5\u505a\u5230\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u9752\u7816\u7b51\u6210\u7684\u5899\u58c1\u5f88\u5149\u6ed1\uff0c\u4e0d\u8fc7\uff0c\u6211\u60f3\u603b\u53ef\u4ee5\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u62c5\u5fc3\u7684\u662f\uff0c\u4ee5\u9ad8\u5929\u5065\u7684\u9634\u6c89\uff0c\u8fd9\u62a4\u57ce\u6cb3\u7684\u6c34\u4e2d\uff0c\u662f\u4e0d\u662f\u4f1a\u6709\u53e4\u602a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u5929\u5065\u8001\u8c0b\u6df1\u7b97\uff0c\u8fd9\u4e00\u70b9\u5012\u662f\u4e0d\u53ef\u4e0d\u9632\u3002\u201d \u3000\u3000\u9ec4\u7075\u5ffd\u7136\u8eab\u8eaf\u6eda\u52a8\uff0c\u5c18\u571f\u672b\u626c\u7684\u4eba\u5df2\u5230\u4e86\u6cb3\u8fb9\u3002 \u3000\u3000\u53ea\u89c1\u53f3\u624b\u4e00\u626c\uff0c\u4e00\u7269\u7834\u7a7a\u800c\u51fa\u3002 \u3000\u3000\u4e00\u9053\u5bd2\u5149\uff0c\u63a0\u7740\u6c34\u9762\u5e73\u98de\uff0c\u6ce2\u7684\u4e00\u58f0\uff0c\u9489\u5165\u9752\u7816\u58c1\u4e2d\u3002 \u3000\u3000\u9752\u7816\u5f88\u575a\u7262\uff0c\u4f46\u9ec4\u7075\u6253\u7684\u5c0f\u5de7\u94f6\u68ad\u4e5f\u5f88\u9510\u5229\u3002 \u3000\u3000\u94f6\u68ad\u540e\u9762\uff0c\u5e26\u4e86\u4e00\u6761\u5f88\u7ec6\u7684\u4e1d\u7d22\u3002 \u3000\u3000\u4e1d\u7d22\u867d\u7ec6\uff0c\u5374\u5f88\u575a\u7262\u3002 \u3000\u3000\u9ec4\u7075\u62c9\u7d27\u4e1d\u7d22\uff0c\u90a3\u7d22\u7ef3\u53ea\u9ad8\u51fa\u6c34\u9762\u4e24\u4e09\u5bf8\uff0c\u4e3e\u624b\u4e00\u62db\uff0c\u4f55\u5bd2\u8863\u7d27\u968f\u7740\u7ffb\u6eda\u8fc7\u6765\uff0c\u867d\u7136\u672a\u89c1\u6212\u5907\uff0c\u4f46\u4e24\u4eba\u4ecd\u7136\u6781\u7aef\u5c0f\u5fc3\u3002 \u3000\u3000\u9ec4\u7075\u4f4e\u58f0\u9053\uff1a \u201c\u8fd9\u6761\u7ebf\u7d22\uff0c\u53ef\u4ee5\u8d1f\u91cd\u5728\u4e8c\u5341\u65a4\u5de6\u53f3\uff0c\u4f55\u5144\u5927\u6982\u53ef\u4ee5\u8fc7\u53bb\u4e86\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5927\u6982\u53ef\u4ee5\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5148\u8fc7\uff0c\u6e21\u8fc7\u62a4\u5821\u6cb3\u4e4b\u540e\uff0c\u5c31\u4ee5\u58c1\u864e\u529f\u4e0a\u5821\u5899\uff0c\u5c0f\u5fc3\u5821\u5899\u4e0a\u7684\u5de1\u5b88\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\uff0c\u633a\u8eab\u800c\u8d77\uff0c\u4e00\u63d0\u4e39\u7530\u771f\u6c14\uff0c\u8e0f\u7d22\u800c\u8fc7\u3002 \u3000\u3000\u9ec4\u7075\u968f\u540e\u800c\u81f3\u3002 \u3000\u3000\u9ec4\u7075\u5728\u7af9\u5b50\u4e0a\u6253\u7684\u662f\u6d3b\u7ed3\uff0c\u5fae\u5fae\u7528\u529b\uff0c\u626d\u5f00\u4e86\u7d22\u7ef3\u3002 \u3000\u3000\u5f53\u771f\u662f\u7f9a\u7f8a\u6302\u89d2\uff0c\u4e0d\u7559\u4e00\u70b9\u75d5\u8ff9\u3002 \u3000\u3000\u6536\u56de\u4e86\u94f6\u68ad\uff0c\u7acb\u523b\u65bd\u5c55\u58c1\u864e\u529f\uff0c\u4e0a\u4e86\u5821\u5899\u3002 \u3000\u3000\u5821\u5899\u4e0a\u9762\u76f8\u5f53\u7a7a\u9614\uff0c\u56db\u4e94\u4e2a\u4eba\u5e76\u6392\u8d70\uff0c\u4e0d\u4f1a\u6709\u6324\u7684\u611f\u89c9\u3002 \u3000\u3000\u5821\u5899\u4e0a\uff0c\u672a\u89c1\u5de1\u536b\u3002 \u3000\u3000\u4e5f\u8bb8\uff0c\u7b2c\u4e00\u5821\u5728\u6c5f\u6e56\u4e0a\u7684\u540d\u6c14\u592a\u5927\u4e86\uff0c\u5927\u5230\u6ca1\u6709\u4eba\u6562\u8f7b\u634b\u864e\u987b\uff0c\u5821\u4e2d\u7684\u9632\u536b\uff0c\u4e5f\u9010\u6e10\u677e\u61c8\u4e0b\u6765\u3002 \u3000\u3000\u4f0f\u9996\u4e0b\u671b\uff0c\u53ea\u89c1\u5821\u5185\u5927\u90e8\u5206\u5730\u65b9\uff0c\u90fd\u4e00\u7247\u9ed1\u6697\uff0c\u53ea\u6709\u4e09\u56db\u5904\u5730\u65b9\uff0c\u70b9\u6709\u706f\u5149\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u8f7b\u8f7b\u5448\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u597d\u8c61\u5168\u65e0\u9632\u536b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u624d\u4f7f\u4eba\u6709\u65e0\u6cd5\u4e0b\u624b\u7684\u611f\u89c9\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54b1\u4eec\u662f\u4e0d\u662f\u5148\u5230\u6709\u706f\u5149\u7684\u5730\u65b9\u53bb\u77a7\u77a7?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5148\u627e\u4e00\u4e2a\u4eba\u95ee\u95ee\uff0c\u4e86\u89e3\u4e00\u4e0b\u5821\u4e2d\u9632\u536b\u7684\u5f62\u52bf\uff0c\u4f60\u5728\u8fd9\u91cc\u7b49\u5019\uff0c\u6211\u4e0b\u53bb\u627e\u4e2a\u4eba\u95ee\u95ee\u3002\u201d \u3000\u3000\u9ec4\u7075\u8eab\u6cd5\u8f7b\u6377\uff0c\u591c\u8272\u4e2d\uff0c\u6709\u5982\u4e00\u5934\u5de8\u9e64\u3002 \u3000\u3000\u4e09\u4e08\u591a\u9ad8\u5821\u5899\uff0c\u843d\u5730\u4e4b\u540e\uff0c\u7adf\u7136\u4e0d\u95fb\u58f0\u606f\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u7684\u8eab\u8eaf\u95ea\u4e01\u95ea\uff0c\u5df2\u7ecf\u6ca1\u5165\u591c\u8272\u4e4b\u4e2d\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u9ec4\u7075\u51fa\u73b0\u5821\u5899\u4e4b\u4e0a\uff0c\u4e3e\u624b\u76f8\u62db\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u98de\u4e0b\u57ce\u5821\uff0c\u4f4e\u58f0\u9053\uff1b\u201c\u67e5\u51fa\u4e86\u4ec0\u4e48\u6ca1\u6709?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54b1\u4eec\u6765\u7684\u65f6\u95f4\u5f88\u597d!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u539f\u6765\uff0c\u8fd9\u7b2c\u4e00\u5821\u7684\u5de1\u5b88\uff0c\u7531\u4e09\u66f4\u65f6\u5206\u5f00\u59cb\uff0c\u4e00\u65e6\u5f00\u59cb\uff0c\u5c31\u4e25\u5bc6\u5f02\u5e38\uff0c\u73b0\u5728\u5df2\u5feb\u63a5\u8fd1\u4e09\u66f4\u4e86\uff0c\u54b1\u4eec\u53ef\u4ee5\u770b\u5230\u7b2c\u4e00\u5821\u9632\u5b88\u7684\u60c5\u5f62\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u8fd9\u5012\u662f\u6c5f\u6e56\u4e0a\u4ece\u672a\u6709\u8fc7\u7684\u4e8b\uff0c\u4e09\u66f4\u65f6\uff0c\u624d\u5f00\u59cb\u5de1\u5b88!\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1b \u201c\u4e00\u822c\u591c\u884c\u4eba\u7684\u4e60\u6027\uff0c\u5927\u90fd\u662f\u4e09\u66f4\u65f6\u5206\u624d\u51fa\u52a8\uff0c\u4e09\u66f4\u5230\u4e94\u66f4\u4e4b\u95f4\uff0c\u5927\u4f53\u4e0a\u8bf4\u6765\uff0c\u662f\u4e00\u822c\u4eba\u7761\u5f97\u6700\u719f\u7684\u65f6\u523b\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54b1\u4eec\u73b0\u5728\u5e94\u8be5\u5982\u4f55?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u6211\u5df2\u7ecf\u627e\u5230\u4e86\u4e2a\u5f88\u597d\u7684\u85cf\u8eab\u5730\u65b9\uff0c\u54b1\u4eec\u5148\u770b\u770b\u4ed6\u4eec\u9632\u536b\u4e4b\u540e\u7684\u60c5\u5f62\uff0c\u7136\u540e\uff0c\u518d\u627e\u51fa\u4e0b\u624b\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u90a3\u662f\u4e00\u5ea7\u5c0f\u5ead\u9662\uff0c\u9662\u4e2d\u6709\u4e00\u682a\u9ad8\u5927\u7684\u767d\u6768\u3002 \u3000\u3000\u9ec4\u7075\u3001\u4f55\u5bd2\u8863\u722c\u4e0a\u4e86\u9ad8\u5927\u7684\u767d\u6768\u6811\u4e0a\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u90a3\u4e2a\u6cc4\u5bc6\u7ed9\u4f60\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ed6\u7761\u5f97\u5f88\u751c\uff0c\u4e0d\u5230\u660e\u65e5\u5348\u65f6\uff0c\u5927\u6982\u4e0d\u4f1a\u9192\u8fc7\u6765\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u54b1\u4eec\u4eca\u591c\u4e2d\u88ab\u4eba\u53d1\u73b0\u4e86\u884c\u8e2a\uff0c\u90a3\u4eba\u662f\u6b7b\u662f\u6d3b\uff0c\u53cd\u800c\u4e0d\u592a\u91cd\u8981\u4e86\uff0c\u5982\u662f\u4eca\u591c\u54b1\u4eec\u907f\u8fc7\u654c\u950b\u4e86\uff0c\u90a3\u4ed6\u660e\u5348\u9192\u6765\uff0c\u5c82\u4e0d\u662f\u8981\u6cc4\u6f0f\u54b1\u4eec\u7684\u5e95\u5b50\u4e86?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u597d!\u4f55\u5144\u7684\u987e\u8651\u8d8a\u6765\u8d8a\u5468\u5230\u4e86\uff0c\u8fd9\u5c31\u662f\u6c5f\u6e56\u7ecf\u9a8c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u6211\u60f3\u5821\u4e3b\u65e9\u5df2\u60f3\u5230\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u8fd8\u672a\u6765\u5f97\u53ca\u7b54\u8bdd\uff0c\u7a81\u95fb\u4e00\u58f0\u6025\u4fc3\u7684\u9f13\u58f0\uff0c\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e0d\u77e5\u90a3\u662f\u4ec0\u4e48\u505a\u6210\u7684\u9f13\uff0c\u58f0\u97f3\u6c89\u91cd\uff0c\u54cd\u5f7b\u5168\u5821\u3002 \u3000\u3000\u4e09\u901a\u9f13\u8fc7\uff0c\u7b2c\u4e00\u5821\u4e2d\uff0c\u7a81\u7136\u540c\u65f6\u4eae\u8d77\u4e86\u4e03\u5904\u706b\u628a\u3002 \u3000\u3000\u6bcf\u4e00\u5904\u706b\u628a\uff0c\u8ba1\u5728\u5341\u652f\u5de6\u53f3\uff0c\u4e03\u5904\u706b\u628a\u7acb\u523b\u4f7f\u5f97\u7b2c\u4e00\u5821\u4e2d\uff0c\u6709\u7740\u660e\u4eae\u7684\u611f\u89c9\u3002 \u3000\u3000\u7d27\u63a5\u7740\uff0c\u57ce\u5821\u4e0a\uff0c\u5404\u5904\u8981\u9053\u8857\u53e3\uff0c\u90fd\u6709\u706b\u628a\u4eae\u8d77\u3002 \u3000\u3000\u4e0d\u8fc7\u7247\u523b\u529f\u592b\uff0c\u539f\u672c\u6c89\u5367\u5728\u591c\u8272\u4e2d\u7684\u7b2c\u4e00\u5821\uff0c\u5ffd\u7136\u95f4\u4e00\u7247\u660e\u4eae\u3002 \u3000\u3000\u706b\u628a\u5411\u5168\u5821\u8513\u5ef6\uff0c\u5230\u5904\u90fd\u662f\u63d0\u5200\u6267\u67aa\u7684\u52b2\u88c5\u4eba\u5728\u5954\u8d70\u3002 \u3000\u3000\u771f\u662f\u4e00\u77ac\u4e4b\u95f4\u5201\u6597\u68ee\u4e25\uff0c\u5982\u679c\u6ca1\u6709\u5145\u5206\u51c6\u5907\u7684\u4eba\uff0c\u8fdb\u5165\u7b2c\u4e00\u5821\u540e\uff0c\u52bf\u975e\u7acb\u523b\u88ab\u4eba\u53d1\u73b0\u4e0d\u53ef\u3002 \u3000\u3000\u9ec4\u7075\u3001\u4f55\u5bd2\u8863\uff0c\u85cf\u8eab\u7684\u767d\u6768\u6811\u4e0b\uff0c\u4e5f\u6709\u2014\u961f\u5de1\u5b88\u7684\u5821\u4e01\u7ecf\u8fc7\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u884c\u4e09\u4eba\uff0c\u4e00\u4e2a\u4eba\u624b\u6267\u706b\u628a\uff0c\u8170\u60ac\u957f\u5251\uff0c\u53e6\u5916\u4e24\u4e2a\u5de6\u53f3\u968f\u884c\uff0c\u4e00\u4eba\u63d0\u62a2\uff0c\u4e00\u4eba\u63d0\u5200\u3002 \u3000\u3000\u8fd9\u662f\u5f88\u4e25\u5bc6\u7684\u7f16\u7ec4\uff0c\u5c0f\u7ec4\u4e2d\u6bcf\u4e00\u4e2a\u4eba\uff0c\u8fd0\u7528\u7684\u5175\u5203\uff0c\u90fd\u7ecf\u8fc7\u5f88\u7ec6\u5fc3\u7684\u8c03\u914d\u3002 \u3000\u3000\u5de1\u884c\u7684\u8def\u7ebf\uff0c\u4f3c\u4e4e\u90fd\u7ecf\u8fc7\u4e86\u4e25\u5bc6\u7684\u5b89\u6392\u3002 \u3000\u3000\u4f46\u89c1\u706b\u628a\u6d41\u52a8\uff0c\u5f88\u5feb\u5730\u4f38\u5ef6\u5168\u5821\u3002 \u3000\u3000\u53ea\u8981\u6709\u4eba\u5728\u5821\u4e2d\u884c\u52a8\uff0c\u7edd\u5bf9\u65e0\u6cd5\u907f\u8fc7\u8fd9\u4e25\u5bc6\u7684\u641c\u5bfb\u3002 \u3000\u3000\u5e78\u597d\uff0c\u9ec4\u7075\u548c\u4f55\u5bd2\u8863\u662f\u5728\u767d\u6768\u6811\u4e0a\uff0c\u800c\u8fd9\u682a\u767d\u6768\u6811\uff0c\u53c8\u5728\u5ea7\u5c0f\u5ead\u9662\u4e2d\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4f4e\u58f0\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u8fd9\u4e0d\u50cf\u662f\u4e00\u822c\u7684\u5de1\u5b88\uff0c\u597d\u50cf\u4ed6\u4eec\u5728\u627e\u4ec0\u4e48\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u4e5f\u53d1\u89c9\u60c5\u52bf\u6709\u4e9b\u4e0d\u5bf9\u4e86\uff0c\u76b1\u76b1\u7709\u5934\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u597d\u50cf\u662f\u5728\u8ffd\u67e5\u4ec0\u4e48\u3002\u201d \u3000\u3000\u6ee1\u5e03\u5168\u5821\u7684\u706b\u628a\uff0c\u5927\u90e8\u5206\u9759\u6b62\u4e0b\u6765\uff0c\u53ea\u6709\u5c11\u6570\u5728\u6d3b\u52a8\u3002 \u3000\u3000\u9ec4\u7075\u3001\u4f55\u5bd2\u8863\u85cf\u8eab\u7684\u767d\u6768\u6811\u4e0b\u4e0d\u8fdc\u5904\uff0c\u5c31\u6709\u4e00\u652f\u706b\u628a\u505c\u4e0b\u4e00\u7ec4\u4eba\u5b88\u5728\u90a3\u91cc\u3002 \u3000\u3000\u8ddd\u79bb\u767d\u6768\u6811\uff0c\u4e5f\u4e0d\u8fc7\u4e09\u56db\u4e08\u8fdc\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u9053\uff1a\u201c\u597d\u4e25\u5bc6\u7684\u641c\u67e5\u3002\u201d \u3000\u3000\u4e24\u4e2a\u9752\u8863\u4f69\u5251\u7684\u5973\u5a62\uff0c\u5f15\u5bfc\u7740\u4e00\u4e2a\u8eab\u7740\u6de1\u7eff\u52b2\u88c5\u7684\u5c11\u5973\uff0c\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5b88\u5728\u5c94\u9053\u4e0a\u7684\u4eba\u7269\uff0c\u4f3c\u4e4e\u662f\u5bf9\u7eff\u8863\u5c11\u5973\u6781\u4e3a\u5d07\u656c\uff0c\u8083\u7acb\u5782\u9996\uff0c\u671b\u4e5f\u4e0d\u6562\u671b\u4e00\u773c\u3002 \u3000\u3000\u7eff\u8863\u5c11\u5973\u76ee\u5149\u4e00\u63a0\u90a3\u6267\u706b\u628a\u7684\u9ed1\u8863\u5927\u6c49\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u662f\u90a3\u2014\u5802\u7684\u624b\u4e0b?\u201d \u3000\u3000\u9ed1\u8863\u5927\u6c49\u9053\uff1a\u201c\u795e\u5200\u5802\u4e0b\uff0c\u7b2c\u56db\u5341\u4e03\u7ec4\u3002\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u4f60\u770b\u8fc7\u8fd9\u9644\u8fd1\u7684\u5f62\u52bf\u4e86?\u201d \u3000\u3000\u9ed1\u8863\u5927\u6c49\u9053\uff1a\u201c\u770b\u8fc7\u4e86\u3002\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u4f60\u89c9\u5f97\u54ea\u91cc\u6700\u53ef\u7591?\u201d \u3000\u3000\u9ed1\u8863\u5927\u6c49\u9053\uff1a\u201c\u53ef\u80fd\u9690\u85cf\u654c\u4eba\u7684\u5730\u65b9\u5c5e\u4e0b\u90fd\u4e0d\u4f1a\u653e\u8fc7\u3002\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u6211\u5982\u679c\u662f\u6f5c\u5165\u5821\u4e2d\u7684\u654c\u4eba\uff0c\u6211\u5c31\u4f1a\u9690\u85cf\u5728\u90a3\u682a\u767d\u6768\u6811\u4e0a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u5934\u4e00\u9707\u3002 \u3000\u3000\u7eff\u8863\u5c11\u5973\u7684\u76ee\u5149\uff0c\u7a81\u7136\u6295\u6ce8\u5728\u767d\u6768\u6811\u4e0a\uff0c\u9053\uff1a\u201c\u90a3\u91cc\u5c45\u9ad8\u4e34\u4e0b\uff0c\u53ef\u4ee5\u770b\u6e05\u695a\u5821\u4e2d\u666f\u7269\u7684\u4e00\u5207\u3002\u201d \u3000\u3000\u9ed1\u8863\u5927\u6c49\u9053\uff1a\u201c\u662f\uff01 \u5c5e\u4e0b\u4f1a\u4ed4\u7ec6\u7684\u641c\u67e5\u3002\u201d \u7eff\u8863\u5c11\u5973\u6253\u91cf\u4e86\u767d\u6768\u6811\u826f\u4e45\uff0c\u5e26\u7740\u5973\u5a62\u79bb\u53bb\u3002 \u3000\u3000\u5979\u5728\u505c\u7559\u65f6\uff0c\u4f55\u5bd2\u8863\u4e0d\u6562\u8bf4\u8bdd\uff0c\u76f4\u5f85\u5979\u79bb\u53bb\u4e4b\u540e\uff0c\u624d\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u5434\u53cc\u5973\uff0c\u8fd8\u662f\u9ad8\u5e7d\u5170\uff1f\u201d \u3000\u3000\u9ec4\u7075\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u5c1a\u672a\u89c1\u5979\u914d\u5e26\u5175\u5203\uff0c\u5434\u53cc\u5973\u7684\u6210\u4efd\u4f3c\u4e4e\u662f\u5927\u4e9b\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u53ea\u89c1\u5728\u9053\u65c1\u5b88\u62a4\u7684\u4e00\u7ec4\u4eba\uff0c\u4e0d\u505c\u5730\u628a\u76ee\u5149\u6295\u6ce8\u5728\u9ad8\u5927\u7684\u767d\u6768\u6811\u4e0a\u3002 \u3000\u3000\u4f3c\u4e4e\uff0c\u4ed6\u4eec\u5df2\u6000\u7591\u5230\u5927\u6811\u4e0a\u85cf\u7684\u6709\u4eba\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f3c\u4e4e\u4ed6\u4eec\u5df2\u7ecf\u53d1\u89c9\u4e86\u5927\u6811\u4e0a\u85cf\u7684\u6709\u4eba\u3002\u201d\u53c8\u63a5\u7740\u9053\uff1b\u201c\u53ea\u662f\u6000\u7591\uff0c\u8fd8\u672a\u80af\u5b9a\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u65e9\u665a\u4f1a\u641c\u67e5\u8fd9\u68f5\u5927\u6811\u7684\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8981\u4e0d\u8981\u5148\u79bb\u5f00\u8fd9\u91cc\uff0c\u6362\u4e00\u4e2a\u85cf\u8eab\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7b49\u4ed6\u4eec\u4e0d\u6ce8\u610f\u65f6\u523b\uff0c\u54b1\u4eec\u8eb2\u5230\u5c0f\u5ead\u9662\u4e2d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9996\u5148\u98de\u8dc3\u4e0b\u5927\u6811\u3002 \u3000\u3000\u9ec4\u7075\u7d27\u968f\u7740\u98de\u8eab\u800c\u4e0b\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u7684\u8f7b\u529f\uff0c\u90fd\u5df2\u5230\u4e86\u6781\u9ad8\u7684\u7adf\u754c\uff0c\u843d\u5730\u65e0\u58f0\u3002 \u3000\u3000\u90a3\u662f\u4e2a\u5c0f\u5c0f\u7684\u4e09\u5408\u5ead\u9662\uff0c\u4e00\u5ea7\u4e3b\u623f\uff0c\u4e24\u5ea7\u53a2\u623f\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54b1\u4eec\u5728\u623f\u4e2d\u8eb2\u4e00\u8eb2\u3002\u201d \u3000\u3000\u4f38\u624b\u4e00\u63a8\uff0c\u6b63\u5385\u6728\u95e8\uff0c\u7adf\u7136\u5e94\u624b\u800c\u5f00\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e3e\u6b65\u8de8\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u540c\u65f6\uff0c\u6697\u4e2d\u63d0\u805a\u771f\u6c14\uff0c\u51dd\u805a\u529f\u529b\uff0c\u53ea\u8981\u8fd9\u623f\u4e2d\u6709\u4eba\uff0c\u5373\u7528\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\uff0c\u51fa\u624b\u4e00\u51fb\u3002 \u3000\u3000\u4f46\u623f\u4e2d\u65e0\u4eba\u3002\u591c\u8272\u6b63\u6d53\uff0c\u623f\u4e2d\u66f4\u662f\u9ed1\u6697\u3002 \u3000\u3000\u9ec4\u7075\u51dd\u76ee\u542c\u4e86\u4e00\u9635\uff0c\u624d\u7f13\u6b65\u884c\u4eba\u623f\u4e2d\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u623f\u4e2d\u65e0\u4eba\uff1f\u201d \u3000\u3000\u5385\u89d2\u4e00\u5904\u6697\u5f71\u4e2d\uff0c\u7a81\u7136\u54cd\u8d77\u4e86\u4e00\u4e2a\u6e05\u51b7\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u624d\u6765\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u62ac\u624b\u4e00\u638c\uff0c\u51fb\u5411\u53d1\u58f0\u4e4b\u5904\u3002 \u3000\u3000\u4e00\u80a1\u52b2\u529b\uff0c\u50cf\u6295\u5165\u5927\u6d77\u4e2d\u7684\u6c99\u77f3\uff0c\u4e0d\u95fb\u4efb\u4f55\u53cd\u5e94\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u5934\u5927\u9707\u3002 \u3000\u3000\u9ec4\u7075\u6ca1\u6709\u51fa\u624b\uff0c\u53ea\u662f\u6697\u4e2d\u63d0\u6c14\u6212\u5907\u3002 \u3000\u3000\u4e24\u6247\u6728\u95e8\uff0c\u7a81\u7136\u5173\u4e0a\uff0c\u6697\u5ba4\u4e00\u89d2\u4e2d\uff0c\u706b\u5149\u7a81\u7136\u4e00\u95ea\uff0c\u4eae\u8d77\u4e86\u4e00\u652f\u706b\u70db\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u8eab\u7740\u7eff\u8863\u7684\u5c11\u5973\uff0c\u51b7\u51b7\u7684\u7ad9\u5728\u5927\u5385\u4e00\u89d2\u3002 \u3000\u3000\u5728\u5979\u7684\u8eab\u8fb9\uff0c\u7ad9\u7740\u4e24\u4f4d\u8eab\u7740\u9752\u8863\u7684\u5973\u5a62\u3002 \u3000\u3000\u7eff\u8863\u5c11\u5973\u6253\u91cf\u4e86\u4e24\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u662f\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u533a\u533a\u9ec4\u7075\u3002\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u662f\u9634\u9633\u5821\u7684\u5821\u4e3b?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u4eca\u5929\u5149\u4e34\u655d\u5821\uff0c\u4e0d\u77e5\u662f\u4f55\u7528\u5fc3?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u662f\u4e0d\u662f\u5434\u53cc\u5973\uff1f\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u662f\u7b2c\u4e00\u5821\u4e2d\u4eba\uff0c\u4e0d\u7ba1\u662f\u8c01\u90fd\u662f\u4e00\u6837\uff0c\u90fd\u53ef\u4ee5\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u9ec4\u7075\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u59d1\u5a18\uff01\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u542c\u8bf4\u4f60\u662f\u4e2a\u8272\u72fc\uff0c\u6709\u4e0d\u5c11\u59d1\u5a18\u6b7b\u5728\u4f60\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\u662f\u4f60\u8fd9\u79cd\u4eba\uff0c\u4e0d\u8be5\u6d3b\u5728\u4e16\u4e0a\u3002\u201d \u3000\u3000\u5979\u8c8c\u7f8e\u5982\u82b1\uff0c\u4f46\u51fa\u8a00\u9510\u5229\uff0c\u53e3\u6c14\u4e5f\u662f\u6076\u6bd2\u5f97\u5f88\u3002 \u3000\u3000\u9ec4\u7075\u5012\u662f\u8868\u73b0\u5f97\u51fa\u5947\u7684\u9759\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u771f\u51c6\u5907\u6740\u4e86\u5728\u4e0b?\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u591c\u5165\u7b2c\u4e00\u5821\uff0c\u5df2\u7ecf\u8be5\u6b7b\uff0c\u4f55\u51b5\uff0c\u4f60\u7684\u540d\u58f0\u4e5f\u574f\u5f97\u5f88\uff0c\u66f4\u662f\u6b7b\u6709\u4f59\u8f9c\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u8981\u6740\u5728\u4e0b\u4e00\u4e2a\u5462\uff0c\u8fd8\u662f\u4e24\u4e2a\u4eba\u4e00\u8d77\u6740?\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u76ee\u5149\u8f6c\u6ce8\u5230\u4f55\u5bd2\u8863\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u4e0b\u4f55\u5bd2\u8863\u3002\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u7565\u4e00\u6c89\u601d\u9053\uff1a \u201c\u4f60\u51fa\u8eab\u6b66\u5f53\u95e8\u4e0b\uff0c\u662f\u7af9\u6756\u7fc1\u7684\u5f1f\u5b50?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u59d1\u5a18\u5bf9\u5728\u4e0b\u77e5\u9053\u5f97\u8fd9\u6837\u6e05\u695a\uff1f\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u4e2d\u6709\u5929\u4e0b\u5404\u79cd\u6b66\u6797\u4eba\u7269\u7684\u8d44\u6599\uff0c\u4f60\u4f55\u5bd2\u8863\u4e5f\u7b97\u662f\u6b66\u6797\u4e2d\u7684\u540d\u4eba\uff0c\u7b2c\u4e00\u5821\u81ea\u7136\u6709\u4f60\u7684\u8d44\u6599\u5566!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5728\u4e0b\u7adf\u7136\u6709\u8fd9\u6837\u7684\u540d\u6c14\u3002\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4e2a\u5916\u53f7\uff0c\u53eb\u505a\u91d1\u5251\u98de\u8f6e\u5427?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5bf9\u3002\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a \u201c\u4f60\u51fa\u8eab\u6b63\u5927\u95e8\u6237\uff0c\u5230\u76ee\u524d\u4e3a\u6b62\u8fd8\u6ca1\u6709\u4ec0\u4e48\u6076\u8ff9\uff0c\u4f46\u4f60\u79c1\u95ef\u7b2c\u4e00\u5821\uff0c\u5374\u72af\u4e86\u6b7b\u7f6a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u662f\u8bf4\uff0c\u59d1\u5a18\u8fde\u5728\u4e0b\u4e5f\u8981\u4e00\u8d77\u6740\u4e86?\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u6740\u597d\u4eba\uff0c\u4f60\u65e2\u7136\u6ca1\u6709\u6076\u8ff9\uff0c\u53ef\u4ee5\u514d\u53bb\u6b7b\u7f6a\uff0c\u6b7b\u7f6a\u867d\u53ef\u514d\uff0c\u4f46\u6d3b\u7f6a\u5374\u4e0d\u80fd\u6055\uff0c\u6240\u4ee5\uff0c\u6211\u8981\u628a\u4f60\u4e0b\u5165\u6c34\u7262\u4e4b\u4e2d\uff0c\u5173\u4f60\u534a\u5e74\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5173\u6211\u534a\u5e74?\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a \u201c\u5bf9!\u8fd9\u662f\u6211\u4eec\u7b2c\u4e00\u5821\u60e9\u6cbb\u5916\u4eba\u6700\u4f4e\u7684\u5211\u6cd5\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u51c6\u5907\u600e\u4e48\u5173\u6211\u5462?\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u81ea\u7136\u662f\u628a\u4f60\u6d3b\u6349\u4e4b\u540e\u5173\u8fdb\u53bb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u662f\u60f3\u8981\u6211\u4eec\u675f\u624b\u5c31\u7f1a\u5417?\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u6211\u4ece\u6765\u6ca1\u6709\u60f3\u8fc7\u8fd9\u4e48\u597d\u7684\u4e8b\uff0c\u4e0d\u8fc7\uff0c\u4f60\u4eec\u52a8\u4e0d\u52a8\u624b\u90fd\u4e00\u6837\u3002\u201d \u3000\u3000\u9ec4\u7075\u4f3c\u662f\u6709\u610f\u7684\u8981\u4f55\u5bd2\u8863\u5904\u7406\u8fd9\u4ef6\u4e8b\uff0c\u6240\u4ee5\uff0c\u95ed\u53e3\u4e0d\u591a\u8a00\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u671b\u4e86\u9ec4\u7075\u4e00\u773c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u59d1\u5a18\u7684\u610f\u601d\u662f\uff0c\u4f60\u6709\u628a\u63e1\u751f\u64d2\u533a\u533a\u4e86?\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u4e3e\u624b\u4e4b\u52b3\u800c\u5df2\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u4f60\u53ef\u662f\u4e0d\u76f8\u4fe1?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u80fd\u6709\u8fd9\u4e48\u5927\u7684\u53e3\u6c14\uff0c\u5b9a\u7136\u662f\u6709\u4e9b\u672c\u9886\u4e86?\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u4f60\u662f\u4e2a\u5f88\u806a\u660e\u7684\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5b9e\u5728\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u867d\u7136\u5728\u4e0b\u5f88\u76f8\u4fe1\u59d1\u5a18\u7684\u8bdd\uff0c\u4e0d\u8fc7\uff0c\u5982\u82e5\u5728\u4e0b\u4e0d\u8bd5\uff0c\u4e5f\u5b9e\u5728\u653e\u5fc3\u4e0d\u4e0b\u3002\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u4eba\uff0c\u90fd\u6709\u8fd9\u4e2a\u4e60\u6027\uff0c\u4e0d\u89c1\u68fa\u6750\u4e0d\u6389\u6cea\uff0c\u4e0d\u5230\u9ec4\u6cb3\u5fc3\u4e0d\u6b7b\uff0c\u4f60\u51fa\u5251\u5427\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u8f7b\u8f7b\u7684\u62bd\u51fa\u4e86\u957f\u5251\uff0c\u4e3e\u5728\u80f8\u524d\uff0c\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u5728\u4e0b\u8fd8\u60f3\u8bf7\u6559\u4e00\u4ef6\u4e8b\u3002\u201d \u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u7684\u59d3\u540d\u3002\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u8fd9\u4e2a\u5173\u7cfb\u5f88\u5927\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u9053\uff1a \u201c\u5982\u82e5\u5728\u4e0b\u5931\u624b\u88ab\u64d2\uff0c\u90a3\u65f6\u518d\u95ee\u59d1\u5a18\u7684\u59d3\u540d\uff0c\u5c82\u4e0d\u662f\u592a\u665a\u4e86\u5417?\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u55ef!\u4f55\u4e5f\u6709\u7406\uff0c\u6211\u662f\u9ad8\u65e0\u53cc\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ad8\u65e0\u53cc\uff0c\u9ad8\u5821\u4e3b\u7684\u5343\u91d1\u3002\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a\u201c\u4e49\u5973\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u7684\u672c\u59d3\u662f\u5434\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u7eff\u8863\u5c11\u5973\u9053\uff1a \u201c\u4f60\u4e00\u5b9a\u8981\u6211\u4eb2\u53e3\u544a\u8bc9\u4f60\uff0c\u6211\u5c31\u662f\u5434\u53cc\u5973\u624d\u884c?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u5728\u4e0b\u542c\u4eba\u8bf4\u8fc7\uff0c\u7b2c\u4e00\u5821\u4e2d\u6700\u53ef\u6015\u7684\u4eba\u7269\uff0c\u5e76\u4e0d\u662f\u9ad8\u5929\u5065\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54e6!\u90a3\u662f\u8c01?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5434\u53cc\u5973\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u6de1\u6de1\u4e00\u7b11\u9053\uff1a \u201c\u5f88\u4e0d\u5e78\uff0c\u4f60\u8fdb\u5165\u7b2c\u4e00\u5821\uff0c\u5c31\u78b0\u5230\u4e86\u5979\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4eba\u80cc\u65f6\uff0c\u4ec0\u4e48\u4e8b\u90fd\u9047\u5f97\u4e0a\uff0c\u5728\u4e0b\u4e0d\u60f3\u7684\u5c31\u662f\u9047\u4e0a\u59d1\u5a18\uff0c\u4f46\u504f\u504f\u9047\u5230\u4e86\uff0c\u7b2c\u4e00\u5821\u4e2d\u8fd9\u4e48\u591a\u9ad8\u624b\uff0c\u7adf\u7136\u9047\u4e0a\u4e86\u4f60\uff0c\u5728\u4e0b\u771f\u7684\u662f\u4f55\u5176\u4e0d\u5e78\u4e5f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u522b\u592a\u7070\u5fc3\uff0c\u4e5f\u8bb8\uff0c\u6211\u4e0d\u662f\u4f20\u8bf4\u4e2d\u90a3\u4e48\u5389\u5bb3\uff0c\u8bf4\u4e0d\u5b9a\uff0c\u6211\u662f\u4e00\u4e2a\u5192\u5145\u7684\u4eba\u5462?\u51fa\u5251\u5427!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d\u5427!\u5728\u4e0b\u732e\u4e11\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\u4e00\u5251\u523a\u4e86\u8fc7\u53bb\uff0c \u3000\u3000\u5251\u81f3\u4e2d\u9014\uff0c\u53f3\u624b\u4e00\u9707\uff0c\u95ea\u8d77\u4e94\u6735\u5251\u82b1\uff0c\u5206\u53d6\u5434\u53cc\u5973\u7684\u4e94\u5927\u7a74\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d\u5251\u3002\u201d\u53f3\u624b\u4e00\u62ac\uff0c\u786c\u5411\u5251\u8fce\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5ffd\u7136\u6536\u4e86\u957f\u5251\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u5c31\u5728\u6536\u5251\u7684\u540c\u65f6\uff0c\u611f\u89c9\u5230\u4e00\u80a1\u6f5c\u529b\u903c\u4e86\u8fc7\u6765\uff0c\u4e0d\u7981\u5fc3\u4e2d\u4e00\u60ca\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6536\u4f4f\u5251\u52bf\u4e86?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u59d1\u5a18\u7528\u624b\u63a5\u5728\u4e0b\u7684\u5251\u52bf\uff0c\u96be\u9053\u4e0d\u6015\u4f24\u4e86\u7389\u624b\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u662f\u6211\u7684\u4e8b\uff0c\u5982\u82e5\u4f24\u4e86\u6211\u7684\u624b\uff0c\u4f60\u5c31\u8d62\u4e86\u8fd9\u4e00\u573a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u5728\u6697\u81ea\u76d8\u7b97\uff0c\u81ea\u5df1\u82e5\u7559\u5728\u8fd9\u91cc\uff0c\u81f3\u5c11\u8981\u60f3\u4e2a\u529e\u6cd5\u4f7f\u9ec4\u7075\u79bb\u5f00\u6b64\u5730\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u4e0b\u4e86\u51b3\u5fc3\uff0c\u4e0d\u60dc\u7559\u5728\u7b2c\u4e00\u5821\uff0c\u4ee5\u4fbf\u9ec4\u7075\u8131\u8eab\u3002 \u3000\u3000\u56f0\u96be\u5728\uff0c\u5982\u4f55\u624d\u80fd\u4f7f\u9ec4\u7075\u9886\u53d7\u81ea\u5df1\u7684\u5fc3\u610f\u3002 \u3000\u3000\u4ed6\u4ee5\u76ee\u5149\u793a\u610f\u9ec4\u7075\u3002 \u3000\u3000\u9ec4\u7075\u4e5f\u4ee5\u76ee\u5149\u56de\u7b54\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u4e0b\u82e5\u80dc\u4e86\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u82e5\u80dc\u4e86\u6211\uff0c\u5c31\u653e\u4f60\u4eec\u79bb\u5f00\u6b64\u5730\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\u662f\u9664\u4e86\u5728\u4e0b\u4ee5\u5916\uff0c\u8fd8\u6709\u9ec4\u5821\u4e3b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9!\u4f60\u4eec\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u542c\u8bf4\u7b2c\u4e00\u5821\u95e8\u89c4\u68ee\u4e25\uff0c\u59d1\u5a18\u653e\u8d70\u4e86\u6211\u4eec\uff0c\u5c31\u4e0d\u6015\u9ad8\u5821\u4e3b\u602a\u7f6a\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u662f\u6211\u7684\u4e8b\uff0c\u7528\u4e0d\u7740\u4f60\u6765\u8d39\u5fc3\u3002\u201d \u3000\u3000\u5979\u8138\u4e0a\u6cdb\u8d77\u5fae\u5fae\u7684\u7b11\u610f\uff0c\u4f3c\u4e4e\u5bf9\u4f55\u5bd2\u8863\u5982\u6b64\u5730\u5173\u5fc3\u4e00\u4e2a\u654c\u4eba\uff0c\u611f\u89c9\u5230\u6709\u4e9b\u597d\u7b11\u3002 \u3000\u3000\u9ec4\u7075\u653e\u5fc3\u4e86\uff0c\u4ed6\u5df2\u770b\u51fa\u6765\uff0c\u4f55\u5bd2\u8863\u5bf9\u5434\u53cc\u5973\u7684\u5370\u8c61\u5f88\u597d\uff0c\u8868\u73b0\u51fa\u6765\u7684\u5173\u5fc3\uff0c\u4e00\u534a\u662f\u624b\u6bb5\uff0c\u4e00\u534a\u4e5f\u662f\u53d1\u81ea\u5185\u5fc3\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u65e9\u5df3\u5728\u6697\u4e2d\u6212\u5907\uff0c\u5168\u529b\u653b\u51fa\u4e86\u4e00\u5251\u3002 \u3000\u3000\u4e00\u9053\u95ea\u8d77\u7684\u5bd2\u5149\uff0c\u95ea\u7535\u822c\u98de\u5c04\u8fc7\u53bb\u3002 \u3000\u3000\u5251\u4e0a\uff0c\u95ea\u8d77\u4e86\u4e00\u7247\u5251\u82b1\u3002 \u3000\u3000\u5206\u523a\u4e0a\u5434\u53cc\u5973\u7684\u6570\u5904\u5927\u7a74\u3002 \u3000\u3000\u5434\u53cc\u5973\u53cc\u624b\u9f50\u626c\uff0c\u5341\u6307\u4e2d\u900f\u51fa\u4e86\u5f3a\u70c8\u7684\u6f5c\u529b\uff0c\u903c\u4f4f\u4e86\u5251\u52bf\u3002 \u3000\u3000\u9ec4\u7075\u501f\u673a\u53d1\u52a8\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u5ffd\u7136\u98de\u8dc3\u800c\u8d77\uff0c\u76f4\u51b2\u5c4b\u9876\u3002 \u3000\u3000\u4f46\u95fb\u7830\u7136\u4e00\u58f0\uff0c\u7816\u74e6\u6a2a\u98de\uff0c\u9ec4\u7075\u7adf\u4ece\u5c4b\u9876\u76f4\u51b2\u800c\u51fa\u3002 \u3000\u3000\u4e24\u4e2a\u5a62\u5973\u5e76\u8d77\u76f4\u8ffd\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5168\u529b\u8fd0\u5251\uff0c\u5251\u5982\u6253\u95ea\uff0c\u7f20\u4f4f\u4e86\u5434\u53cc\u5973\u3002 \u3000\u3000\u5434\u53cc\u5973\u82b1\u4e86\u4e94\u62db\u7684\u65f6\u95f4\uff0c\u624d\u628a\u4f55\u5bd2\u8863\u7684\u5251\u52bf\u7ed9\u903c\u5f00\u3002 \u3000\u3000\u4f46\u4e24\u4e2a\u5a62\u5973\uff0c\u5df2\u5148\u540e\u9000\u4e86\u56de\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e5f\u6536\u4f4f\u4e86\u5251\u52bf\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u5f88\u5403\u60ca\uff0c\u5434\u53cc\u5973\u8d64\u624b\u7a7a\u62f3\uff0c\u7adf\u7136\u5728\u4e94\u516d\u62db\u5185\u628a\u4ed6\u903c\u5f97\u5251\u52bf\u6536\u7f29\uff0c\u6709\u4e9b\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u5434\u53cc\u5973\u5706\u5706\u7684\u5927\u773c\u775b\u4e2d\uff0c\u653e\u5c04\u51fa\u51cc\u5389\u7684\u51b7\u8292\uff0c\u626b\u63a0\u4e86\u4f55\u5bd2\u8863\u4e00\u773c\uff0c\u5374\u5bf9\u4e8c\u5a62\u8bf4\u9053\uff1b\u201c\u8ffd\u4e0a\u4e86\u6ca1?\u201d \u3000\u3000\u4e8c\u5a62\u9f50\u58f0\u5e94\u9053\uff1a\u201c\u4ed6\u8f7b\u529f\u5353\u7edd\uff0c\u5a62\u5b50\u4eec\u65e0\u6cd5\u53ca\u65f6\u8d76\u4e0a\uff0c\u4e09\u9053\u963b\u622a\u7684\u57cb\u4f0f\uff0c\u90fd\u672a\u53ca\u53d1\u52a8\uff0c\u4ed6\u5df2\u51b2\u51fa\u4e86\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d\u53ef\u6076\uff0c\u4f55\u5bd2\u8863\uff0c\u4f60\u4eec\u662f\u4e0d\u662f\u5df2\u7ecf\u5546\u91cf\u597d\u4e86?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u7684\u770b\u6cd5\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9ec4\u7075\u7684\u6b66\u529f\uff0c\u5e94\u8be5\u6bd4\u4f60\u9ad8\u660e\u4e9b\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4ed6\u624d\u5f53\u5821\u4e3b\uff0c\u6211\u5f53\u4ed6\u7684\u5c5e\u4e0b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u653e\u624b\u548c\u6211\u4e00\u6218\uff0c\u5374\u628a\u4f60\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u6211\u4e0d\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u770b\u6765\uff0c\u4ed6\u662f\u4e0d\u987e\u4f60\u7684\u751f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\u201c\u8fd9\u4ef6\u4e8b\u4e5f\u51fa\u4e4e\u5728\u4e0b\u7684\u610f\u6599\u4e4b\u5916\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u4eec\u4e0d\u662f\u4e8b\u5148\u5546\u91cf\u597d\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u4f60\u73b0\u5728\uff0c\u51c6\u5907\u4f5c\u4f55\u6253\u7b97?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u73b0\u5728\uff0c\u5728\u4e0b\u8d70\u7684\u673a\u4f1a\u597d\u50cf\u662f\u4e0d\u5927\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5b8c\u5168\u6ca1\u6709\uff0c\u4f60\u6700\u597d\u522b\u4f5c\u8fd9\u6837\u7684\u6253\u7b97\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u53ea\u6709\u653e\u624b\u4e00\u6218\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6700\u597d\u662f\u653e\u4e0b\u5251\uff0c\u675f\u624b\u5c31\u7f1a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u7537\u5b50\u6c49\uff0c\u5927\u4e08\u592b\uff0c\u5c31\u7b97\u660e\u77e5\u9053\u8981\u6218\u6b7b\uff0c\u4e5f\u4e0d\u5e94\u8be5\u754f\u7f29\u4e0d\u524d\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u7537\u5b50\u6c49\uff0c\u5927\u4e08\u592b\uff0c\u8981\u8d62\u5f97\u6f02\u4eae\uff0c\u8f93\u5f97\u5e72\u8106\uff0c\u8f93\u4e86\u8981\u8ba4\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d!\u73b0\u5728\uff0c\u6211\u5c31\u7ed9\u4f60\u4e00\u4e2a\u8ba4\u8f93\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6ee1\u6000\u72c2\u50b2\u800c\u6765\uff0c\u4f46\u548c\u5434\u53cc\u5973\u52a8\u624b\u4e00\u6218\u4e4b\u540e\uff0c\u8fd9\u80a1\u72c2\u50b2\u4e4b\u6c14\uff0c\u7acb\u65f6\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u5434\u53cc\u5973\u7684\u6b66\u529f\u9020\u8be3\u4e0a\uff0c\u4e5f\u8bb8\u672a\u5fc5\u6709\u6781\u9ad8\u7684\u6210\u5c31\uff0c\u4f46\u5979\u602a\u5f02\u7684\u6b66\u529f\u8def\u6570\uff0c\u7b80\u76f4\u4f7f\u4eba\u65e0\u6cd5\u62db\u67b6\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u4e00\u4e2a\u4eba\u7684\u751f\u6b7b\u8363\u8fb1\uff0c\u800c\u662f\u5173\u7cfb\u6574\u4e2a\u6c5f\u6e56\u7684\u5927\u5c40\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4f55\u5bd2\u8863\u5df2\u51b3\u5fc3\u6682\u65f6\u8ba4\u547d\u3002 \u3000\u3000\u8f7b\u8f7b\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u4f55\u5bd2\u8863\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u5b50\u7684\u673a\u4f1a\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u53f7\u79f0\u91d1\u5251\u98de\u8f6e\uff0c\u5728\u8fd9\u4e00\u65b9\u9762\u4e00\u5b9a\u6709\u7740\u5f88\u6df1\u7684\u9020\u8be3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f3c\u4e4e\u5728\u59d1\u5a18\u4e4b\u524d\uff0c\u5728\u4e0b\u4e0d\u4fbf\u6709\u6240\u5938\u53e3\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u80fd\u627f\u8ba4\u81ea\u5df1\u5931\u8d25\uff0c\u90a3\u4e2a\u4eba\u4e00\u5b9a\u6709\u7740\u5f88\u5927\u7684\u6210\u529f\u7684\u673a\u4f1a\uff0c\u770b\u6765\uff0c\u4f55\u516c\u5b50\u5c31\u662f\u90a3\u6837\u7684\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u591a\u8c22\u5938\u5956\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4e0d\u8fc7\uff0c\u6211\u4e5f\u660e\u767d\uff0c\u4f60\u4eec\u8fd9\u79cd\u4eba\u5fc3\u4e2d\u7684\u611f\u89c9\uff0c\u6709\u70b9\u4e0d\u89c1\u4e8b\u5b9e\uff0c\u4e0d\u8ba4\u8f93\u7684\u611f\u89c9\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4eae\u51fa\u4f60\u7684\u91d1\u5251\u3001\u98de\u8f6e\uff0c\u5c55\u51fa\u4f60\u7684\u770b\u5bb6\u672c\u9886\uff0c\u5982\u679c\u4f60\u80fd\u591f\u80dc\u4e86\u6211\uff0c\u6211\u5c31\u606d\u9001\u4f60\u79bb\u5f00\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u4f3a\u5bd2\u8863\u9053\uff1a\u201c\u771f\u7684?\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u81ea\u7136\u662f\u771f\u7684\uff0c\u4e0d\u8fc7\uff0c\u8981\u6709\u4e2a\u62db\u6570\u7684\u9650\u5236\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u662f\u81ea\u7136\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5341\u62db\u591f\u4e0d\u591f?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u591f\u4e01!\u591f\u4e86!\u201d - \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u597d!\u90a3\u5c31\u51fa\u624b\u5341\u62db\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\u65bd\u5c55\u6740\u624b\uff0c\u5728\u8fd9\u5341\u62db\u4e4b\u5185\uff0c\u6211\u4e0d\u4f1a\u53cd\u51fb\uff0c\u53ea\u6709\u62db\u67b6\uff0c\u6240\u4ee5\uff0c\u4e0d\u4f1a\u59a8\u788d\u4f60\u5168\u529b\u7684\u65bd\u5c55\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u4e2a\uff0c\u53ea\u6015\u4e0d\u592a\u597d\u5427?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u597d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u4e0b\u7684\u610f\u601d\u662f\uff0c\u4e07\u4e00\u4e0d\u5e78\u4f24\u5bb3\u5230\u4e86\u59d1\u5a18\uff0c\u90a3\u5c82\u4e0d\u662f\u4e00\u4ef6\u5f88\u5927\u7684\u61be\u4e8b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5982\u662f\u4f60\u771f\u7684\u628a\u6211\u6740\u4e86\uff0c\u5728\u4f60\u4eec\u9634\u9633\u5821\u4e2d\uff0c\u53ef\u7b97\u5f97\u4e0a\u662f\u5947\u529f\u4e00\u4ef6\uff0c\u9ec4\u7075\u5e94\u8be5\u628a\u90a3\u4e2a\u5821\u4e3b\u8ba9\u7ed9\u4f60\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\uff0c\u6211\u6562\u548c\u4f60\u8fd9\u6837\u8d4c\uff0c\u603b\u5f52\u662f\u6709\u516b\u6210\u7684\u628a\u63e1\u3002\u201d \u3000\u3000\u8bdd\u8bf4\u5f97\u5f88\u5ba2\u6c14\uff0c\u53ea\u8bf4\u6709\u516b\u6210\u7684\u80dc\u7b97\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5f03\u53bb\u4e86\u624b\u4e2d\u7684\u957f\u5251\u53f3\u624b\u4e00\u63a2\u8170\uff0c\u91d1\u5251\u5df2\u5165\u638c\u63e1\u3002 \u3000\u3000\u53ea\u662f\u4e00\u5c3a\u591a\u957f\u7684\u5251\u52bf\u3002 \u3000\u3000\u4f46\u5434\u53cc\u5973\u5e76\u6ca1\u6709\u5c0f\u770b\u90a3\u67c4\u77ed\u5251\uff0c\u4e00\u5bf8\u77ed\uff0c\u4e00\u5bf8\u9669\uff0c\u77ed\u5c0f\u7684\u5175\u5203\uff0c\u5fc5\u7136\u6709\u7279\u6b8a\u7684\u53d8\u5316\u62db\u6570\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u7528\u591a\u8651\uff0c\u4f60\u51fa\u624b\u5427!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u7684\u98de\u8f6e\uff0c\u5939\u5728\u91d1\u5251\u4e4b\u4e2d\uff0c\u968f\u65f6\u4f1a\u51fa\u624b\u4f24\u4eba\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d!\u4e0d\u8fc7\uff0c\u4e0d\u80fd\u8d85\u8fc7\u5341\u62db\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u59d1\u5a18\u8bf7\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u53f3\u624b\u4e00\u626c\uff0c\u91d1\u5251\u75be\u5982\u6d41\u661f\u800c\u51fa\u3002 \u3000\u3000\u8fd9\u628a\u91d1\u5251\uff0c\u867d\u7136\u6bd4\u666e\u901a\u7684\u5251\u77ed\u4e86\u5f88\u591a\uff0c\u4f46\u5728\u4f55\u5bd2\u8863\u7684\u624b\u4e2d\uff0c\u5a01\u529b\u5374\u6bd4\u4e00\u628a\u957f\u5251\u5f3a\u5927\u5f88\u591a\u3002 \u3000\u3000\u91d1\u8292\u7535\u5c04\u95f4\uff0c\u95ea\u8d77\u4e86\u4e00\u7247\u91d1\u82b1\u3002 \u3000\u3000\u5434\u53cc\u5973\u8eab\u8eaf\u4e00\u8f6c\uff0c\u5ffd\u7136\u95f4\u95ea\u5230\u4e86\u4f55\u5bd2\u8863\u7684\u65c1\u4fa7\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u5251\u5f88\u5feb\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53f3\u624b\u4e00\u4f38\uff0c\u7ea4\u7ea4\u7389\u624b\uff0c \u5df2\u7ecf\u642d\u4e0a\u4e86\u4f55\u5bd2\u8863\u7684\u5de6\u80a9\u4e4b\u4e0a\u3002 \u4f55\u5bd2\u8863\u4e22\u5f03\u624b\u4e2d\u7684\u91d1\u5251\u3002 \u3000\u3000\u4ed6\u56fa\u7136\u6ca1\u6709\u5168\u529b\u95ea\u907f\uff0c\u4f46\u5434\u53cc\u5973\u638c\u52bf\u88ad\u6765\u7684\u5feb\u901f\uff0c\u786e\u4f7f\u4ed6\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u5434\u53cc\u5973\u6de1\u6de1\u4e00\u7b11\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8bc6\u65f6\u52a1\u8005\u4e3a\u4fca\u6770\uff0c\u4f60\u662f\u4e00\u4e2a\u5f88\u8bc6\u65f6\u52a1\u7684\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53cc\u65b9\u6709\u7740\u5f88\u5927\u7684\u8ddd\u79bb\uff0c\u5728\u4e0b\u5c31\u7b97\u820d\u547d\u4e00\u62fc\uff0c\u4e5f\u662f\u5168\u65e0\u673a\u4f1a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u62db\u62db\u624b\uff0c\u4e00\u4e2a\u5973\u5a62\u4fef\u8eab\u62fe\u8d77\u5730\u4e0a\u7684\u91d1\u5251\uff0c\u4ea4\u7ed9\u4e86\u5434\u53cc\u5973\u3002 \u3000\u3000\u5434\u53cc\u5973\u7f13\u7f13\u7684\u628a\u91d1\u5251\u8fd8\u5165\u4e86\u4f55\u5bd2\u8863\u8eab\u4e0a\u7684\u5251\u9798\u4e2d\uff0c\u9053\uff1a \u201c\u628a\u98de\u8f6e\u4e5f\u6536\u8d77\u6765\u5427\u3002\u201d \u4f55\u5bd2\u8863\u6536\u8d77\u5b50\u98de\u8f6e\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u675f\u624b\u5c31\u7f1a\u4e86\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u4f1a\u4eab\u53d7\u5f88\u5927\u7684\u4f18\u5f85\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u4f55\u7684\u4e00\u4e2a\u4f18\u5f85\u6cd5?\u201d \u3000\u3000\u5434\u53cc\u5973\u671b\u7740\u4e00\u4e2a\u5973\u5a62\uff0c\u9053\uff1a\u201c\u628a\u4ed6\u62bc\u56de\u5f69\u8679\u697c?\u201d \u3000\u3000\u5979\u8bf4\u5f97\u5f88\u6e05\u695a\uff0c\u4f46\u90a3\u4e2a\u9752\u8863\u5973\u5a62\u5374\u53c8\u95ee\u4e86\u4e00\u53e5\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u662f\u8bf4\u5f69\u8679\u697c\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u4e0d\u662f\u6211\u8bf4\u8bdd\u4e0d\u592a\u6e05\u695a\u3002\u201d \u3000\u3000\u9752\u8863\u5973\u5a62\u6025\u6025\u7684\u8dea\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u4e0d!\u662f\u5a62\u5b50\u8be5\u6b7b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8d77\u6765\u5427!\u5e7d\u5170\u8bf4\uff0c\u4f60\u662f\u5979\u6700\u806a\u660e\u7684\u4e00\u4e2a\u4e2b\u73af\u3002\u201d \u3000\u3000\u9752\u8863\u5973\u5a62\u9053\uff1a\u201c\u5176\u5b9e\u5a62\u5b50\u5f88\u7b28\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u7b49\u4e00\u4f1a\u513f\u53bb\u544a\u8bc9\u4f60\u4eec\u5c0f\u59d0\u3002\u201d \u3000\u3000\u9752\u8863\u5973\u5a62\u9053\uff1a\u201c\u5a62\u5b50\u53ea\u662f\u542c\u547d\u884c\u4e8b\uff0c\u5176\u4ed6\u7684\uff0c\u4ec0\u4e48\u90fd\u4e0d\u6653\u5f97\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d!\u4f60\u53bb\u5427!\u201d \u3000\u3000\u7a81\u7136\u4e00\u6307\uff0c\u70b9\u5728\u4e86\u4f55\u5bd2\u8863\u7684\u80cc\u4e0a\uff0c\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u5bf9\u4e0d\u4f4f\u5566\uff0c\u4f60\u5f88\u9ad8\u660e\uff0c\u6211\u4e0d\u5f97\u4e0d\u70b9\u4f60\u7a74\u9053\uff0c\u9664\u4e86\u4e0d\u80fd\u5984\u52a8\u771f\u529b\uff0c\u4e0d\u4f1a\u6709\u522b\u7684\u59a8\u5bb3\uff0c\u4f60\u4eec\u53bb\u5427!\u201d \u3000\u3000\u9752\u8863\u5973\u5a62\u5e26\u7740\u4f55\u5bd2\u8863\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5f88\u8c28\u614e\uff0c\u4e00\u76f4\u672a\u5f00\u53e3\u8bf4\u8bdd\u3002 \u3000\u3000\u9752\u8863\u5973\u5a62\u5728\u7b2c\u4e00\u5821\u4e2d\uff0c\u4f3c\u662f\u4eba\u5934\u5f88\u719f\uff0c\u884c\u8fc7\u51e0\u9053\u660e\u5361\uff0c\u90fd\u65e0\u4eba\u62e6\u95ee\u3002 \u3000\u3000\u5f69\u8679\u697c\uff0c\u82b1\u6811\u73af\u7ed5\u3002 \u3000\u3000\u5728\u5e7f\u5927\u7684\u7b2c\u4e00\u5821\u4e2d\uff0c\u5f69\u8679\u697c\u72ec\u6210\u4e00\u5904\u683c\u5c40\u3002 \u3000\u3000\u7a7f\u8fc7\u767d\u77f3\u94fa\u6210\u7684\u5c0f\u5f84\uff0c\u8fdb\u5165\u655e\u5385\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e09\u7f04\u5176\u53e3\uff0c\u9752\u8863\u5973\u5a62\u4e00\u8def\u4e0a\u4e5f\u6ca1\u8bf4\u8bdd\u3002 \u3000\u3000\u76f4\u5230\u8fdb\u5165\u4e86\u655e\u5385\uff0c\u9752\u8863\u5973\u5a62\u71c3\u8d77\u4e86\u76cf\u5bab\u706f\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5f69\u8679\u697c\u3002\u201d \u3000\u3000\u9752\u8863\u5973\u5a62\u9053\uff1a\u201c\u5f69\u8679\u697c\u662f\u4f5c\u4ec0\u4e48\u7684?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u9752\u8863\u5973\u5a62\u9053\uff1a\u201c\u5f69\u8679\u697c\u662f\u5927\u5c0f\u59d0\u4f4f\u7684\u5730\u65b9\uff0c\u95f2\u6742\u4eba\u7b49\u8bef\u5165\u8005\uff0c\u7acb\u523b\u683c\u6740\uff0c\u770b\u6765\uff0c\u5979\u5bf9\u4f60\u4e0d\u9519\uff0c\u81f3\u5c11\uff0c\u7b2c\u4e00\u5370\u8c61\u5f88\u597d\uff0c\u4e0d\u8fc7\uff0c\u5979\u5f88\u96be\u6349\u6478\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u7a81\u7136\u6740\u4f60\uff0c\u4e0d\u60f3\u6b7b\u5f97\u8f7b\u5982\u9e3f\u6bdb\uff0c\u4f60\u5c31\u5f97\u597d\u597d\u7684\u7528\u70b9\u5fc3\u8ba1\u3002\u201d \u3000\u3000\u9752\u8863\u5973\u5a62\u53c8\u9053\uff1a\u201c\u4f60\u8ba4\u8bc6\u534e\u5999\u771f\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4eec\u540c\u5728\u9634\u9633\u5821\u4e2d\u62c5\u4efb\u5251\u4f7f\u3002\u201d \u3000\u3000\u9752\u8863\u5973\u5a62\u9053\uff1a\u201c\u6211\u53eb\u96ea\u6885\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u96ea\u6885\u50b2\u971c\uff0c\u540d\u5b57\u4e0d\u9519\u3002\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u4e2b\u5934\u561b\uff0c\u4e0d\u662f\u6885\uff0c\u5c31\u662f\u96ea\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u96ea\u6885\u59d1\u5a18\uff0c\u6211\u60f3\u8bf7\u6559\u59d1\u5a18\u51e0\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u4f55\u5e94\u4ed8\u8fd9\u5434\u53cc\u5973\uff0c\u8fd8\u671b\u59d1\u5a18\u6307\u70b9\u4e00\u4e8c\u3002\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u5434\u53cc\u5973\u7684\u8fc7\u53bb\uff0c\u6211\u4e0d\u6e05\u695a\uff0c\u4f46\u5979\u81ea\u8fdb\u5165\u7b2c\u4e00\u5821\u540e\uff0c\u751f\u6d3b\u5012\u5f88\u4e25\u8c28\uff0c\u5f88\u591a\u4eba\u90fd\u6015\u5979\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90fd\u6015\u5979?\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u56e0\u4e3a\u5979\u7684\u813e\u6c14\u4e0d\u597d\uff0c\u60f9\u706b\u4e86\u5979\uff0c\u5c31\u8981\u52a8\u624b\u6740\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\u3002\u201d \u3000\u3000\u96ea\u6885\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6015\u4e86\uff0c\u662f\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u4e0b\u5df2\u7ecf\u9677\u5165\u4e86\u8fd9\u4e2a\u6f29\u6da1\u4e4b\u4e2d\uff0c\u5c31\u7b97\u6709\u4e9b\u5bb3\u6015\uff0c\u4e5f\u65e0\u6cd5\u9003\u907f\u4e86\u3002\u201d \u3000\u3000\u96ea\u6885\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u600e\u4e48\u6709\u4e9b\u60f3\u4e0d\u5f00\u554a!\u5979\u628a\u4f60\u5e26\u5165\u5f69\u8679\u697c\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u6740\u4f60\uff0c\u5979\u662f\u5973\u4eba\uff0c\u4e00\u4e2a\u5f88\u7f8e\u7684\u5973\u4eba\uff0c\u4f60\u662f\u7537\u4eba\uff0c\u5979\u628a\u4f60\u5e26\u4e86\u8fdb\u6765\uff0c\u90a3\u5c31\u8868\u793a\uff0c\u5979\u6709\u70b9\u559c\u6b22\u4f60\uff0c\u61c2\u4e0d\u61c2?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5ce8!\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u636e\u6211\u6240\u77e5\uff0c\u5979\u662f\u4e2a\u5f88\u7cbe\u660e\u7684\u4eba\uff0c\u4f60\u5982\u5e94\u4ed8\u4e0d\u597d\uff0c\u53ef\u80fd\u4f1a\u7acb\u523b\u7ffb\u8138\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u7acb\u523b\u7ffb\u8138?\u201d \u3000\u3000\u96ea\u6885\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u6211\u60f3\uff0c\u5979\u4f1a\u95ee\u4f60\u5f88\u591a\u4e8b\uff0c\u5e94\u5bf9\u4e4b\u95f4\uff0c\u5982\u4f55\u628a\u63e1\u5f97\u6070\u5230\u597d\u5904\uff0c\u5341\u5206\u91cd\u8981\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5979\u5927\u6982\u5feb\u56de\u6765\u4e86\uff0c\u6211\u5230\u95e8\u53e3\u5b88\u7740\uff0c\u4f60\u5728\u5385\u91cc\u5750\u5427\uff01\u201d \u3000\u3000\u4e0d\u5f85\u4f55\u5bd2\u8863\u63a5\u53e3\uff0c\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6ca1\u6709\u963b\u6b62\uff0c\u5374\u5728\u5385\u4e2d\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u95f4\u60f3\u660e\u767d\u4e86\uff0c\u96ea\u6885\u5b9e\u5728\u65e0\u6cd5\u7ed9\u4ed6\u592a\u591a\u7684\u5e2e\u52a9\uff0c\u8fd9\u662f\u4ed6\u548c\u5434\u53cc\u5973\u7684\u4e8b\uff0c\u7537\u4eba\u548c\u5973\u4eba\u95f4\u7684\u4e8b\uff0c\u95ee\u53e6\u5916\u4e00\u4f4d\u59d1\u5a18\uff0c\u5c82\u4e0d\u662f\u95ee\u9053\u4e8e\u76f2\u3002 \u3000\u3000\u4f46\u96ea\u6885\u544a\u8bc9\u4ed6\u7684\u8bdd\uff0c\u4ecd\u7136\u5f88\u6709\u4ef7\u503c\u3002 \u3000\u3000\u90a3\u5c31\u662f\u5434\u53cc\u5973\u7684\u6027\u683c\u3002 \u3000\u3000\u96ea\u6885\u7684\u4f30\u7b97\u5f88\u51c6\uff0c\u4e0d\u8fc7\u4e00\u76cf\u70ed\u8336\u7684\u529f\u592b\uff0c\u5434\u53cc\u5973\u5c31\u56de\u5230\u4e86\u5f69\u8679\u697c\u3002 \u3000\u3000\u5434\u53cc\u5973\u72ec\u81ea\u884c\u5165\u5927\u5385\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e24\u4e2a\u4e2b\u5934\uff0c\u90fd\u53bb\u4f11\u606f\u4e86\uff0c\u6298\u817e\u4e86\u5927\u534a\u591c\uff0c\u5979\u4eec\u4e5f\u6709\u4e00\u70b9\u7d2f\u4e86?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5927\u5c0f\u59d0\u53ef\u662f\u4e5f\u6709\u4e00\u4e9b\u7d2f\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u6211\u8fd8\u652f\u6491\u5f97\u4f4f\uff0c\u6211\u7ec3\u7684\u6b66\u529f\uff0c\u548c\u4e00\u822c\u4eba\u4e0d\u540c\uff0c\u4e0d\u592a\u5bb9\u6613\u75b2\u52b3\u3002\u201d \u4f55\u5bd2\u8863\u6697\u4e2d\u7422\u78e8\u4e86\u4e00\u9635\uff0c\u624d\u60f3\u5230\u5434\u53cc\u5973\u7684\u610f\u601d\uff0c\u90a3\u65e0\u7591\u662f\u8bf4\uff0c\u76ee\u4e0b\u8fd9\u5f69\u8679\u697c\uff0c\u53ea\u6709\u54b1\u4eec\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u679c\u7136\uff0c\u5434\u53cc\u5973\u53c8\u4f5c\u4e86\u4e00\u70b9\u8bf4\u660e\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4f4f\u7684\u5730\u65b9\uff0c\u867d\u7136\u770b\u4e0d\u5230\u4ec0\u4e48\u9632\u5b88\uff0c\u5374\u4e0d\u592a\u6709\u4eba\u6562\u6765\u6b64\u5730\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5b9e\u5728\u60f3\u4e0d\u51fa\u7528\u4ec0\u4e48\u63aa\u8bcd\u56de\u7b54\u5434\u53cc\u5973\u7684\u8bdd\u3002 \u3000\u3000\u5434\u53cc\u5973\u7b11\u9053\uff1a\u201c\u4f55\u5144\uff0c\u4f60\u5728\u6c5f\u6e56\u4e0a\u58f0\u8a89\u5f88\u597d\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u627e\u5230\u4e86\u8bdd\uff0c\u6025\u6025\u63a5\u9053\uff1b\u201c\u59d1\u5a18\u5938\u5956\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1b\u201c\u6ca1\u6709\u542c\u8bf4\u4f60\u505a\u8fc7\u4ec0\u4e48\u574f\u4e8b\uff0c\u4f46\u6211\u60f3\u4e0d\u901a\u7684\u662f\uff0c\u4f60\u4e3a\u4ec0\u4e48\u52a0\u5165\u4e86\u9634\u9633\u5821?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u662f\u4e0d\u662f\u88ab\u9ec4\u7075\u7528\u4ec0\u4e48\u624b\u6bb5\u7ed9\u62c9\u8fc7\u53bb\u7684?\u201d \u3000 \u7b2c \u516b \u56de\u3000\u5f69\u8679\u697c \u3000 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u5f88\u559c\u6b22\u670b\u53cb\uff0c\u5728\u4e0b\u548c\u4ed6\u4ea4\u5f80\u8fc7\u4e00\u6bb5\u65f6\u95f4\uff0c\u5f7c\u6b64\u76f8\u5904\u5f97\u4e0d\u9519\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u53ea\u662f\u8fd9\u6837\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u522b\u7684\u6ca1\u6709\u4ec0\u4e48\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u548c\u9ec4\u7075\u7684\u6e0a\u6e90\uff0c\u5e76\u975e\u5f88\u6df1?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4eec\u4ea4\u5f80\u5f97\u5f88\u6295\u673a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u542c\u8bf4\u9ec4\u7075\u5f88\u6709\u624b\u6bb5\uff0c\u7f57\u81f4\u4e86\u4e0d\u5c11\u9ad8\u4eba\uff0c\u597d\u50cf\uff0c\u4ed6\u6709\u4e00\u4f4d\u5f88\u7f8e\u4e3d\uff0c\u53c8\u5584\u89e3\u4eba\u610f\u7684\u4e2b\u5934\uff0c\u4e0d\u77e5\u9053\u4f60\u89c1\u8fc7\u6ca1\u6709?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u8bf4\u7684\u53ef\u662f\u6021\u7ea2?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5979\u53eb\u6021\u7ea2\uff0c\u5f88\u7f8e\u7684\u540d\u5b57\u554a!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u5c31\u5728\u4e0b\u6240\u77e5\uff0c\u9ec4\u5821\u4e3b\u7684\u8eab\u4fa7\uff0c\u53ea\u6709\u8fd9\u4e48\u4e00\u4e2a\u4e2b\u5934\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5979\u5f88\u7f8e?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u55ef!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u542c\u8bf4\uff0c\u9ec4\u7075\u5e38\u5e38\u7528\u5979\u6765\u62c9\u62e2\u4eba\uff0c\u4ee5\u4f7f\u90a3\u4e9b\u4eba\u5bf9\u4ed6\u5fe0\u5b9e\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u9634\u9633\u5821\u4e2d\u4e94\u5927\u5251\u4f7f\uff0c\u4e5f\u6709\u5973\u4eba\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u6021\u7ea2\u53ea\u662f\u9ec4\u7075\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5c31\u5728\u4e0b\u6240\u77e5\uff0c\u9ec4\u7075\u548c\u6021\u7ea2\u4e4b\u95f4\uff0c\u4e00\u76f4\u5341\u5206\u6e05\u767d\uff0c\u6021\u7ea2\u5f88\u656c\u91cd\u5979\u7684\u4e3b\u4eba\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u600e\u4e48\u6837?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u548c\u6021\u7ea2\u4e4b\u95f4\uff0c\u662f\u4e0d\u662f\u4e5f\u5f88\u6e05\u767d\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6e05\u767d\uff0c\u7edd\u5bf9\u6e05\u767d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u96be\u9053\u6ca1\u6709\u4eba\u548c\u6021\u7ea2\u5f88\u4eb2\u8fd1\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6709\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u662f\u522b\u4eba\u7684\u4e8b\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u8bba\u957f\u8bf4\u77ed\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\uff1a\u201c\u6211\u7b54\u5e94\u4f60\u8fd9\u4ef6\u4e8b\uff0c\u7edd\u4e0d\u8bf4\u51fa\u53bb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6021\u7ea2\u51fa\u8eab\u5993\u9662\uff0c\u4f46\u5979\u81ea\u8ffd\u968f\u9ec4\u5821\u4e3b\u4e4b\u540e\uff0c\u4e00\u76f4\u8fd8\u80fd\u6d01\u8eab\u81ea\u7231\uff0c\u5979\u548c\u53f6\u5251\u4f7f\u6709\u8fc7\u4e00\u6bb5\u60c5\u8c0a\u7684\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u4e03\u5251\u8ffd\u9b42\u53f6\u957f\u9752?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u662f\u53f6\u957f\u9752\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u4e4b\u95f4\u65e0\u4eba\u64ae\u5408\uff0c\u662f\u51fa\u81ea\u4e24\u4eba\u7684\u5185\u5fc3\uff0c\u53d1\u4e4e\u4e8e\u60c5\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9ec4\u7075\u77e5\u9053\u8fd9\u4ef6\u4e8b\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u77e5\u9053\uff0c\u4f46\u4ed6\u6ca1\u6709\u963b\u6b62\uff0c\u4ed6\u5c0a\u91cd\u522b\u4eba\u7684\u611f\u60c5\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9ec4\u7075\u7684\u540d\u58f0\u5f88\u574f\uff0c\u8fc7\u53bb\uff0c\u4ed6\u5077\u4e86\u4e0d\u5c11\u5973\u4eba\u5fc3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u7684\u8fc7\u53bb\uff0c\u5145\u6ee1\u7740\u7f6a\u6076\uff0c\u4f46\u4ed6\u6539\u8fc7\u4e4b\u540e\uff0c\u5374\u662f\u6bd4\u6211\u89c1\u8fc7\u7684\u597d\u4eba\u66f4\u597d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u770b\u6765\uff0c\u9ec4\u7075\u5f85\u4f60\u4eec\u7684\u65b9\u6cd5\u5f88\u6210\u529f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ed6\u4ee5\u8eab\u4f5c\u5219\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6240\u4ee5\uff0c\u624d\u4e0d\u60dc\u8eab\u9677\u864e\u7a74\uff0c\u8fd8\u60f3\u5c3d\u529e\u6cd5\u52a9\u4ed6\u9003\u8d70\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u53c8\u4e3a\u4f55\u4e3a\u9ad8\u5929\u5065\u62bd\u7528\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u4e0d\u540c\uff0c\u4ed6\u662f\u6211\u7684\u4e49\u7236\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e49\u7236\u3001\u4e49\u5973\uff0c\u548c\u670b\u53cb\u6709\u4ec0\u4e48\u4e0d\u540c?\u8fd8\u4e0d\u662f\u4eba\u4e3a\u7684\u56e0\u7d20\u5728\u5185\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u4f60\u662f\u4e0d\u662f\u60f3\u8bf4\u670d\u6211?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u80fd\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e78\u597d\uff0c\u5728\u4e0b\u8fd8\u6ca1\u6709\u8fd9\u4e2a\u6253\u7b97\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6709\u4e9b\u4ec0\u4e48\u6253\u7b97\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6700\u597d\u7684\u6253\u7b97\uff0c\u662f\u4f60\u80fd\u653e\u4e86\u6211\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\uff0c\u6211\u653e\u4e86\u4f60\u4e4b\u540e\uff0c\u4f60\u8fd8\u80fd\u751f\u79bb\u6b64\u5730\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u6211\u7684\u6b66\u529f\uff0c\u8fd8\u4e0d\u7b97\u592a\u574f\u5427?\u7b2c\u4e00\u5821\u4e2d\u9664\u4e86\u4f60\u59d1\u5a18\u4e4b\u5916\uff0c\u62e6\u4f4f\u6211\u7684\u4eba\uff0c\u4e0d\u4f1a\u592a\u591a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u81f3\u5c11\u6709\u56db\u4e94\u4e2a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u5929\u5065\u4e4b\u5916\uff0c\u5728\u4e0b\u8fd8\u60f3\u4e0d\u51fa\u6709\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4f60\u592a\u4f4e\u4f30\u7b2c\u4e00\u5821\u4e86\uff0c\u4f4e\u4f30\u654c\u4eba\uff0c\u5f80\u5f80\u662f\u6700\u5927\u7684\u9519\u8bef\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u8fd8\u6709\u8c01?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\uff0c\u4e5f\u8be5\u77e5\u9053\u9ad8\u5e7d\u5170\uff0c\u795e\u5200\u5802\u4e3b\uff0c\u548c\u4e09\u4f4d\u526f\u5821\u4e3b\uff0c\u4f60\u7b97\u7b97\u770b\u6709\u51e0\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4e09\u4f4d\u526f\u5821\u4e3b\uff0c\u786e\u5b9e\u662f\u4f55\u5bd2\u8863\u6240\u4e0d\u77e5\u9053\u7684\uff0c\u5446\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4e09\u4f4d\u526f\u5821\u4e3b\uff0c\u4e5f\u80fd\u62e6\u5f97\u4f4f\u6211?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u4e09\u4e2a\u526f\u5821\u4e3b\u90fd\u4e0d\u662f\u7b80\u5355\u4eba\u7269\uff0c\u4e0d\u662f\u6211\u4f4e\u4f30\u4f60\u4f55\u5927\u4fa0\uff0c\u7b2c\u4e00\u5821\u4e09\u4f4d\u526f\u5821\u4e3b\uff0c\u54ea\u4e00\u4e2a\u90fd\u53ef\u4ee5\u80dc\u4f60\u4e00\u7b79\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5728\u4e0b\u771f\u662f\u4e0d\u6210\u6750\u5f97\u5f88\u554a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\uff1a\u201c\u90a3\u4e5f\u4e0d\u7528\u5984\u81ea\u83f2\u8584\uff0c\u7b2c\u4e00\u5821\u6709\u5343\u4f59\u4eba\uff0c\u534a\u6570\u90fd\u7b97\u5f97\u4e0a\u662f\u9ad8\u624b\uff0c\u80dc\u4f60\u7684\u4eba\uff0c\u4e5f\u4e0d\u8fc7\u5341\u4f59\u4e2a\u4eba\u7f62\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u8bdd\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\u201c\u59d1\u5a18\u8981\u5728\u4e0b\u8bf4\u4ec0\u4e48\u5462?\u8d25\u519b\u4e4b\u5c06\uff0c\u4f55\u8db3\u8a00\u52c7\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u8d70\u6c5f\u6e56\uff0c\u96be\u514d\u4f1a\u9047\u4e0a\u4e00\u4e9b\u632b\u6298\uff0c\u518d\u8bf4\uff0c\u4f60\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u5982\u80fd\u523b\u610f\u5411\u6b66\u529f\u4e0a\u6c42\u8fdb\uff0c\u6211\u76f8\u4fe1\uff0c\u4f60\u4f1a\u66f4\u4e0a\u4e00\u5c42\u697c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8c08\u8fc7\u4e86\u5f88\u591a\u95f2\u8bdd\uff0c\u6211\u60f3\u5e94\u8be5\u8c08\u8c08\u6b63\u7ecf\u4e8b?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u51c6\u5907\u4f5c\u4f55\u6253\u7b97?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u4e0b\u8fd8\u6709\u4ec0\u4e48\u81ea\u7531\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u795e\u60c5\u4e00\u53d8\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u6709\u6b7b\uff0c\u9664\u975e\uff0c\u4f60\u6839\u672c\u4e0d\u5728\u4e4e\u81ea\u5df1\u7684\u751f\u6b7b\uff0c\u5c31\u8be5\u60f3\u529e\u6cd5\u6c42\u751f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u60f3\u529e\u6cd5\u6c42\u751f?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5bf9!\u4e00\u4e2a\u4eba\uff0c \u81ea\u5df1\u5982\u6709\u5f3a\u70c8\u7684\u6c42\u751f\u4e4b\u5fc3\uff0c \u81ea\u4f1a\u60f3\u51fa\u6c42\u751f\u7684\u529e\u6cd5\uff0c\u5047\u82e5\u4e00\u4e2a\u4eba\uff0c\u8fde\u81ea\u5df1\u90fd\u4e0d\u60f3\u6d3b\u4e86\uff0c\u5c31\u7b97\u522b\u4eba\u60f3\u5e2e\u52a9\u4ed6\uff0c\u4e5f\u662f\u65e0\u4ece\u7740\u624b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u53c8\u80fd\u9009\u62e9\u4e9b\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u751f\u4e0e\u6b7b\uff0c\u8363\u4e0e\u8fb1\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u751f\u4e0e\u6b7b\uff0c\u8363\u4e0e\u8fb1\uff0c\u4efb\u4f55\u4eba\u5c31\u5b57\u9762\u4e0a\u60f3\uff0c \u81ea\u7136\u53ef\u4ee5\u60f3\u51fa\u4e00\u4e2a\u5185\u60c5\u6765\uff0c\u4e0d\u8fc7\uff0c\u540c\u4e00\u4e2a\u5b57\u7684\u89e3\u91ca\uff0c\u6709\u5f88\u591a\u7684\u4e0d\u540c\uff0c\u6211\u60f3\uff0c\u6700\u597d\u59d1\u5a18\u89e3\u8bf4\u4e00\u4e0b\uff0c\u6c5f\u6e56\u4eba\uff0c\u751f\u6b7b\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u4e8b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u662f\u4e0d\u5728\u4e4e\u751f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u679c\u5728\u4e0b\u4e00\u5b9a\u8981\u6b7b\uff0c\u90a3\u4e5f\u662f\u65e0\u53ef\u5948\u4f55\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u5047\u5982\u975e\u6b7b\u4e0d\u53ef\uff0c\u90a3\u5c31\u4e0d\u4f1a\u7ed9\u4f60\u9009\u62e9\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u82e5\u6211\u9009\u62e9\u4e0d\u6b7b\uff0c\u4e00\u5b9a\u6709\u4e9b\u6761\u4ef6\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9009\u62e9\u561b\uff0c\u81ea\u7136\u6709\u6b63\u6709\u53cd\uff0c\u5bf9\u4eba\u4e00\u9762\u6709\u5229\uff0c\u53e6\u4e00\u9762\uff0c\u81ea\u7136\u4f1a\u5403\u4e8f\u4e00\u4e9b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u80fd\u4e0d\u80fd\u5148\u542c\u542c\u4e0d\u6b7b\u7684\u6761\u4ef6?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u6761\u4ef6\u5f88\u7b80\u5355\uff0c \u53ea\u8981\u4f60\u7559\u5728\u7b2c\u4e00\u5821\u4e2d\u5c31\u884c\u4e86\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u80cc\u53db\u9634\u9633\u5821?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5bf9!\u9634\u9633\u5821\u65e2\u7136\u4e0d\u80fd\u4fdd\u62a4\u4f60\uff0c\u79bb\u5f00\u90a3\u4e2a\u7ec4\u7ec7\u6709\u4f55\u4e0d\u53ef\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u5728\u90a3\u4e2a\u7ec4\u7ec7\u4e2d\u662f\u5251\u4f7f\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u4eec\u6709\u5f88\u591a\u5251\u4f7f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e94\u4e2a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u8fc7\u662f\u4e94\u4e2a\u4eba\u4e2d\u7684\u4e00\u4e2a\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u867d\u7136\u6709\u4e94\u4e2a\u5251\u4f7f\uff0c\u4f46\u6211\u4eec\u7684\u8eab\u4efd\uff0c\u53ea\u5728\u5821\u4e3b\u4e00\u4eba\u4e4b\u4e0b\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a \u201c\u4f60\u7684\u610f\u601d\u662f\uff0c\u5982\u8981\u6295\u5165\u7b2c\u4e00\u5821\uff0c\u5fc5\u987b\u4e5f\u5f97\u722c\u4e0a\u4e00\u4e2a\u526f\u5821\u4e3b\u7684\u4f4d\u7f6e\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u4e0b\u603b\u4e0d\u80fd\u592a\u59d4\u5c48\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u4eba\u6015\u51fa\u540d\uff0c\u5c31\u7b97\u4f60\u5e72\u4e0a\u4e86\u526f\u5821\u4e3b\uff0c\u4f46\u4f60\u884c\u4e8b\u65e0\u6743\uff0c\u5f92\u62e5\u865a\u540d\uff0c\u800c\u4e14\u4e5f\u5f88\u53ef\u80fd\u4f1a\u56e0\u4e3a\u8fd9\u4e2a\u865a\u540d\uff0c\u8eab\u6d89\u5371\u9669\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u7684\u610f\u601d\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u52a1\u5b9e\u4e00\u4e9b\uff0c\u4e0d\u53d7\u865a\u540d\u56f0\u6270\uff0c\u8865\u4e00\u4e2a\u4e00\u7b49\u6b66\u58eb\u8eab\u4efd\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u662f\u4e00\u7b49\u6b66\u58eb?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u6b66\u58eb\uff0c\u5206\u4e86\u5f88\u591a\u7b49\u7ea7\uff0c\u4e00\u7b49\u6b66\u58eb\uff0c\u662f\u8fd9\u91cc\u7b49\u7ea7\u6700\u597d\u7684\u4e00\u79cd\u6b66\u58eb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e00\u5171\u51e0\u7b49\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u56db\u7b49\uff0c\u4e00\u7b49\u6b66\u58eb\u7684\u8eab\u4efd\uff0c\u6781\u53d7\u91cd\u89c6\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ee5\u540e\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u8bf4\u201c\u4f60\u662f\u8bf4\uff0c\u5f53\u4e86\u4e00\u7b49\u6b66\u58eb\u4e4b\u540e?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u662f\u554a\uff01\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4e00\u7b49\u6b66\u58eb\u7684\u5de5\u4f5c\uff0c\u53ef\u4ee5\u56e0\u4eba\u6d3e\u804c\uff0c\u4f60\u60f3\u5e72\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u60f3\uff0c\u6211\u60f3\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u8bf4\u554a!\u7528\u4e0d\u7740\u541e\u541e\u5410\u5410\u7684\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8fc7\u662f\u5821\u4e3b\u7684\u4e49\u5973\uff0c\u5c31\u7b97\u59d1\u5a18\u613f\u610f\u5e2e\u5fd9\uff0c\u53ea\u6015\u4e5f\u6709\u788d\u96be\u4e4b\u5904\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54e6!\u4f60\u4e0d\u4f1a\u662f\u60f3\u5f53\u7b2c\u4e00\u5821\u7684\u5821\u4e3b\u5427?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e00\u7b49\u6b66\u58eb\u7684\u8eab\u4efd\uff0c\u8ddd\u79bb\u5821\u4e3b\u8fd8\u76f8\u5f53\u9065\u8fdc\u5427?\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f60\u53ea\u8981\u4e0d\u662f\u60f3\u5f53\u5821\u4e3b\u4e4b\u4f4d\uff0c\u8bf4\u8bf4\u770b\uff0c\u6211\u4e5f\u8bb8\u80fd\u5e2e\u52a9\u4f60\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u60f3\u5f53\u4ec0\u4e48\u4e00\u7b49\u6b66\u58eb\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u4f60\u8981\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u73af\u987e\u4e86\u56db\u5468\u4e00\u773c\uff0c\u9053\uff1a \u201c\u8fd9\u5f69\u8679\u697c\u4e2d\u4e0d\u77e5\u9053\u662f\u4e0d\u662f\u6709\u7537\u4eba\u7684\u804c\u4f3a?\u201d \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u7559\u5728\u5f69\u8679\u697c?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u80fd\u4e0d\u80fd\u6709\u8fd9\u4e2a\u673a\u4f1a?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5f69\u8679\u697c\u7528\u7537\u4eba\u7684\u804c\u4f4d\uff0c\u5012\u6709\u4e00\u4e2a\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\uff0c\u4ed6\u662f\u56db\u7b49\u6b66\u58eb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u9053\uff1a \u201c\u56db\u7b49\u6b66\u58eb\u4e5f\u597d\uff0c\u516b\u7b49\u6b66\u4e0a\uff0c \u53ea\u8981\u80fd\u7559\u5728\u5f69\u8679\u697c\u5c31\u884c\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u770b\u6765\uff0c\u4f60\u5bf9\u6211\u7684\u4e86\u89e3\u8fd8\u662f\u4e0d\u591f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u672c\u6765\u5c31\u4e0d\u591a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u65e2\u7136\u4e86\u89e3\u4e0d\u591a\uff0c\u600e\u80fd\u5192\u5192\u5931\u5931\u7684\u7559\u5728\u5f69\u8679\u697c?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u5426\u6307\u793a\u4e00\u4e8c\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1b\u201c\u6211\u7684\u813e\u6c14\u4e0d\u597d\uff0c\u7b2c\u4e00\u5821\u4e2d\u4eba\uff0c\u6700\u6015\u7684\u4e0d\u662f\u5821\u4e3b\uff0c\u800c\u662f\u6211\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5728\u4e0b\u7684\u611f\u89c9\u4e2d\uff0c \u4f60\u4e00\u76f4\u5f88\u548c\u6c14\uff0c \u600e\u4e48\u4f1a\u5f88\u5389\u5bb3?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6216\u8bb8\u6211\u5bf9\u4f60\u7684\u813e\u6c14\u7279\u522b\u597d\u4e00\u4e9b\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u4eec\u90fd\u5f88\u6015\u6211\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u4e0b\u600e\u7684\u4e00\u76f4\u90fd\u6ca1\u6709\u8fd9\u6837\u7684\u611f\u89c9?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u662f\u56e0\u4e3a\u4f60\u4e00\u76f4\u6ca1\u6709\u770b\u8fc7\u6211\u53d1\u813e\u6c14\uff0c\u4f60\u5982\u5f88\u4e0d\u5e78\u89c1\u4e86\u4e00\u6b21\uff0c\u90a3\u5c31\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a\u201c\u628a\u6211\u6740\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u4f1a\uff0c\u53ea\u6015\u4f1a\u4f7f\u4f60\u7ec8\u8eab\u96be\u5fd8\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53ea\u8981\u4e0d\u6740\u4eba\uff0c\u5c31\u662f\u518d\u5389\u5bb3\u7684\u813e\u6c14\uff0c\u6211\u4e5f\u4e0d\u6015\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u771f\u7684\u4e0d\u6015\u6211?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9664\u975e\uff0c\u4f60\u6545\u610f\u8981\u4f7f\u6211\u5bb3\u6015\uff0c\u5426\u5219\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u6015\u4f60?\u201d \u3000\u3000\u5434\u53cc\u5973\u7a81\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0b\u53bb\u4f11\u606f\uff0c\u54b1\u4eec\u660e\u5929\u518d\u8c08\u3002\u201d \u3000\u3000\u5979\u6ca1\u6709\u53d1\u813e\u6c14\uff0c\u4f46\u795e\u8272\u7a81\u7136\u95f4\u53d8\u5f97\u5341\u5206\u4e25\u8083\u8d77\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u6697\u9053\uff0c\u8fd9\u4e2a\u5973\u4eba\uff0c\u5f53\u771f\u662f\u5389\u5bb3\u5f97\u5f88\uff0c\u8fde\u559c\u6012\u54c0\u4e50\uff0c\u795e\u60c5\u53d8\u5316\uff0c\u4f3c\u4e4e\u662f\u90fd\u80fd\u63a7\u5236\u81ea\u5982\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5434\u53cc\u5973\u5927\u58f0\u53eb\u9053\uff1a\u201c\u96ea\u6885\uff01\u201d \u3000\u3000\u96ea\u6885\u6025\u6b65\u5954\u5165\uff0c\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e26\u4ed6\u5230\u5ba2\u623f\u4e2d\u4f11\u606f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u4e0d\u5f85\u5bf9\u65b9\u591a\u95ee\uff0c\u7acb\u523b\u7ad9\u8d77\u8eab\u5b50\uff0c\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u96ea\u6885\u62a2\u5148\u4e00\u6b65\uff0c\u5e26\u8def\u800c\u884c\u3002 \u3000\u3000\u5434\u53cc\u5973\u51b7\u6ce0\u8bf4\u9053\uff1a\u201c\u4f55\u5bd2\u8863\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u56de\u8fc7\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4ec0\u4e48\u5429\u5490?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d\u597d\u4f11\u606f\uff0c\u522b\u5984\u52a8\u9003\u8d70\u7684\u5ff5\u5934\uff0c\u5426\u5219\u4e22\u4e86\u6027\u547d\uff0c\u4e0d\u8981\u602a\u6211\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u4e0b\u8bb0\u4e0b\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u53bb\u5427!\u201d \u3000\u3000\u96ea\u6885\u5e76\u6ca1\u6709\u628a\u4f55\u5bd2\u8863\u5e26\u51fa\u5f69\u8679\u697c\u7684\u56f4\u5899\uff0c\u51fa\u4e86\u5927\u5385\uff0c\u53f3\u8f6c\u5f2f\uff0c\u8fdb\u5165\u4e86\u4e00\u5ea7\u82b1\u6811\u73af\u7ed5\u7684\u96c5\u5ba4\u4e4b\u4e2d\u3002 \u3000\u3000\u5f69\u8679\u697c\u5f88\u9ad8\u5927\uff0c\u6240\u4ee5\uff0c\u8fd9\u4e00\u95f4\u96c5\u5ba4\u770b\u4e0a\u53bb\u672a\u514d\u76f8\u5f62\u89c1\u7ecc\u3002 \u3000\u3000\u5c0f\u5c0f\u4e24\u95f4\u74e6\u820d\uff0c\u5b9e\u5728\u5e03\u7f6e\u5f97\u5f88\u7cbe\u5de7\uff0c\u7eff\u7eeb\u5e54\u58c1\uff0c\u4e00\u623f\u4e00\u5385\uff0c\u5385\u4e2d\u6709\u4e66\u53a8\uff0c\u6709\u58c1\u7089\u3002 \u3000\u3000\u58c1\u7089\u4e2d\u65e9\u5df2\u70b9\u8d77\u4e86\u85aa\u706b\uff0c\u623f\u4e2d\u6d0b\u6ea2\u7740\u4e00\u80a1\u6696\u610f\u3002 \u3000\u3000\u96ea\u6885\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u4e0d\u9519\u5427?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u9519\uff0c\u800c\u662f\u5f88\u597d\u3002\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u4f60\u5c31\u4f4f\u5728\u8fd9\u91cc\uff0c\u8fd9\u662f\u5927\u5c0f\u59d0\u79c1\u4eba\u7684\u5ba2\u623f\uff0c\u636e\u6211\u6240\u77e5\uff0c\u8fd8\u6ca1\u6709\u4f4f\u8fc7\u7537\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u96ea\u6885\u59d1\u5a18\uff0c\u6211\u60f3\u8bf7\u6559\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u96ea\u6885\u5ffd\u7136\u95f4\u6709\u4e86\u751c\u751c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u8ba9\u6211\u4f4f\u5728\u8fd9\u91cc\uff0c\u662f\u4f55\u5c45\u5fc3?\u201d \u3000\u3000\u96ea\u6885\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5146\u5934\u4e0d\u9519\uff0c\u6211\u60f3\uff0c\u4f60\u53ef\u80fd\u5df2\u88ab\u2026\u2026\u201d \u3000\u3000\u53c8\u7b11\u7b11\uff0c\u9053\uff1a\u201c\u6211\u867d\u7136\u662f\u5973\u4eba\uff0c\u4f46\u6211\u5bf9\u5973\u4eba\uff0c\u4e5f\u4e0d\u80fd\u7b97\u4e86\u89e3\uff0c\u4e5f\u8bb8\u4f60\u8fd8\u6bd4\u6211\u4e86\u89e3\u5f97\u591a\u4e00\u4e9b\uff0c\u6211\u53ea\u80fd\u544a\u8bc9\u4f60\uff0c\u8fd9\u5730\u65b9\uff0c\u4f60\u597d\u50cf\u662f\u7b2c\u4e00\u4e2a\u88ab\u7559\u5c45\u4f4f\u7684\u7537\u4eba\uff0c\u6d17\u4e2a\u6fa1\uff0c\u4f11\u606f\u5427! \u4f60\u4e0d\u80fd\u521b\u9020\uff0c\u53ea\u6709\u7b49\u5f85\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8eab\u884c\u53bb\uff0c\u5e26\u4e0a\u6728\u95e8\u3002 \u3000\u3000\u8fd9\u5ea7\u5c0f\u5de7\u7684\u5ba2\u623f\uff0c\u4e0d\u4f46\u5e03\u7f6e\u5f97\u96c5\u81f4\u800c\u4e14\u5341\u5206\u8212\u9002\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u5f97\uff0c\u4f55\u5bd2\u8863\u7adf\u7136\u7761\u719f\u4e86\u8fc7\u53bb\uff0c \u3000\u3000\u4e00\u9635\u6025\u4fc3\u7684\u6572\u95e8\u58f0\uff0c\u4f20\u5165\u4e86\u8033\u9645\uff0c\u60ca\u9192\u4e86\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u770b\u5e8a\u524d\u6728\u6848\u7ea2\u70db\u5df2\u6b8b\uff0c\u8fd9\u4e00\u89c9\u7761\u4e86\u4e0d\u5c11\u65f6\u95f4\u3002 \u3000\u3000\u62c9\u5f00\u7a97\u5e18\uff0c\u65e5\u5149\u8000\u76ee\u3002 \u3000\u3000\u539f\u6765\uff0c\u8fd9\u7a97\u4e0a\u7684\u5782\u5e18\u5f88\u539a\uff0c\u906e\u4f4f\u4e86\u9633\u5149\u900f\u5165\u3002 \u3000\u3000\u95e8\u5916\uff0c\u53c8\u54cd\u8d77\u4e86\u535c\u535c\u53e9\u95e8\u4e4b\u58f0\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5401\u4e00\u53e3\u6c14\u3002\u9053\uff1a\u201c\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u201c\u6211\u662f\u96ea\u6885\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8bf7\u7a0d\u5019\u7247\u523b\u3002\u201d \u3000\u3000\u6025\u6025\u6d74\u6d17\uff0c\u7a7f\u597d\u8863\u670d\uff0c\u624d\u6253\u5f00\u6728\u95e8\u3002 \u3000\u3000\u95e8\u53e3\u5904\uff0c\u7ad9\u7740\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u9664\u4e86\u96ea\u6885\u5916\uff0c\u8fd8\u6709\u5434\u53cc\u5973\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u59d1\u5a18\u4e5f\u6765\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u6628\u591c\u7761\u5f97\u5f88\u751c\u5427?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u7761\u5f97\u5f88\u719f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u56de\u5934\u5bf9\u96ea\u6885\u8bf4\u9053\uff1a \u201c\u53bb\u544a\u8bc9\u4e8c\u5c0f\u59d0\uff0c\u5c31\u8bf4\uff0c\u4f55\u5148\u751f\u5f88\u5feb\u5c31\u5230\u4e86\u3002\u201d \u96ea\u6885\u9053\uff1a\u201c\u5a62\u5b50\u9075\u547d\u3002\u201d\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u5434\u53cc\u5973\u5374\u6b65\u5165\u96c5\u5ba4\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4e8c\u5c0f\u59d0\u662f\u8c01\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u7684\u59b9\u59b9\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1b\u201c\u4e49\u59b9\uff0c\u9ad8\u5821\u4e3b\u7684\u4eb2\u751f\u5973\u513f\u3002\u201d \u3000\u3000\u5979\u4eca\u5929\u7a7f\u4e01\u4e00\u4ef6\u9e45\u9ec4\u8272\u7684\u886b\u88d9\uff0c\u9e45\u9ec4\u7f0e\u9762\u7ee3\u82b1\u978b\uff0c\u957f\u53d1\u4e5f\u7528\u4e00\u6839\u9e45\u9ec4\u8272\u5e26\u675f\u8d77\uff0c\u4e00\u5927\u534a\u6563\u5782\u540e\u80a9\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u627e\u6211\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5979\u662f\u7b2c\u4e00\u5821\u4e2d\u7684\u5343\u91d1\u5c0f\u59d0\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u627e\u4f60?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u53ef\u80fd\u4f1a\u95ee\u4f60\u5f88\u591a\u4e8b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u80fd\u8bf4\u7684\uff0c\u6211\u4f1a\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd8\u6709\u4e0d\u80fd\u8bf4\u7684\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u4e0b\u51b3\u4e0d\u4f1a\u8bf4\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u4e49\u59b9\u662f\u4e00\u4e2a\u5f88\u51b7\u5389\u7684\u4eba\uff0c\u60f9\u4e86\u5979\uff0c\u6709\u5f97\u4f60\u82e6\u5934\u597d\u5403\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u6709\u6ca1\u6709\u60f3\u5230\uff0c\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u5982\u82e5\u53d7\u5230\u4e86\u7f9e\u8fb1\uff0c\u90a3\u5c82\u4e0d\u662f\u6bd4\u6b7b\u8fd8\u96be\u8fc7\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u751f\u547d\u5f88\u53ef\u8d35\uff0c\u5c82\u53ef\u8f7b\u6613\u629b\uff0c\u4f60\u53bb\u89c1\u5979\u5427!\u5982\u82e5\u6709\u4ec0\u4e48\u4e8b\u65e0\u6cd5\u56de\u7b54\u65f6\uff0c\u4e0d\u59a8\u63a8\u5230\u6211\u7684\u8eab\u4e0a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u4e0d\u592a\u597d\u5427!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u603b\u6bd4\u4f60\u6b7b\u4e86\u5f3a\u4e00\u4e9b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u88ab\u4eba\u7f9e\u8fb1\u4e0e\u6b7b\u4ea1\u4e4b\u95f4\uff0c\u5982\u82e5\u8981\u5728\u4e0b\u6709\u4e2a\u9009\u62e9\u65f6\uff0c\u6211\u5b81\u53ef\u9009\u62e9\u6b7b\u4ea1\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5fcd\u8010\uff1b\u4e0d\u4f46\u662f\u4e00\u79cd\u6db5\u517b\uff0c\u4e5f\u662f\u4e00\u79cd\u7f8e\u5fb7\uff0c\u9002\u5ea6\u7684\u5fcd\u8010\uff0c\u5bf9\u4e00\u4e2a\u4eba\u6765\u8bf4\uff0c\u5e94\u8be5\u662f\u4e00\u4ef6\u5f88\u91cd\u8981\u7684\u4e8b\u2026\u2026\u201d \u3000\u3000\u4e3e\u624b\u7406\u4e00\u7406\u9b13\u8fb9\u7684\u6563\u53d1\uff0c\u63a5\u9053\uff1a\u201c\u5e0c\u671b\u4f60\u80fd\u5fcd\u8010\u4e00\u4e9b\uff0c\u8d70\u5427!\u53bb\u5927\u5385\u4e2d\u89c1\u89c1\u5979\uff0c\u5979\u867d\u7136\u884c\u4e8b\u7684\u624b\u6bb5\u51b7\u9177\u4e00\u4e9b\uff0c\u4f46\u5979\u662f\u4e00\u4e2a\u5f88\u7f8e\u7684\u5973\u5b69\u3002\u201d \u3000\u3000\u5e7f\u655e\u7684\u5927\u5385\u4e4b\u4e2d\uff0c\u5f88\u6c14\u6d3e\uff0c\u4e5f\u5f88\u96c5\u81f4\u3002 \u3000\u3000\u5385\u4e2d\u6446\u4e86\u5341\u51e0\u76c6\u76db\u653e\u7684\u6d77\u68e0\uff0c\u4f7f\u5f97\u6574\u5ea7\u5927\u5385\u4e2d\uff0c\u90fd\u6709\u79cd\u6de1\u6de1\u7684\u82b1\u9999\u6c14\u5473\u3002 \u3000\u3000\u4e00\u4e2a\u8eab\u7740\u6d45\u84dd\u52b2\u88c5\u7684\u5c11\u5973\uff0c\u7aef\u5750\u5728\u5927\u5385\u4e2d\u4e00\u5f20\u592a\u5e08\u6905\u4e0a\u3002 \u3000\u3000\u526a\u88c1\u5408\u8eab\u7684\u52b2\u670d\uff0c\u886c\u6258\u51fa\u5979\u73b2\u73d1\u7684\u66f2\u7ebf\u3002 \u3000\u3000\u5434\u53cc\u5973\u8bf4\u7684\u4e0d\u9519\uff0c\u5979\u662f\u4e00\u4e2a\u5f88\u7f8e\u7684\u5973\u5b69\u5b50\uff0c\u79c0\u4e3d\u7684\u8f6e\u5ed3\uff0c\u767d\u91cc\u900f\u7ea2\u7684\u5ae9\u8138\uff0c\u53ea\u662f\u8138\u4e0a\u6709\u4e00\u80a1\u51b7\u51b7\u7684\u5473\u9053\uff0c\u662f\u4e00\u4e2a\u8273\u82e5\u6843\u674e\uff0c\u51b7\u82e5\u51b0\u971c\u7684\u4eba\u3002 \u3000\u3000\u96ea\u6885\u5c31\u7ad9\u5728\u5979\u7684\u8eab\u4fa7\u3002 \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u5c31\u662f\u4ed6\u4e86\u3002\u201d \u3000\u3000\u84dd\u8863\u5c11\u5973\u6253\u91cf\u4e86\u4f55\u5bd2\u8863\u4e00\u9635\uff0c\u9053\uff1b\u201c\u4f60\u53eb\u4f55\u5bd2\u8863?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u6211\u6765\u81ea\u9634\u9633\u5821\uff0c\u9634\u9633\u5821\u4e2d\u6709\u4e94\u5927\u5251\u4f7f\uff0c\u6211\u5c31\u662f\u4e94\u5927\u5251\u4f7f\u4e4b\u4e00\u3002\u201d \u84dd\u8863\u5c11\u5973\u70b9\u70b9\u5934\uff0c\u9053\uff1b\u201c\u4f60\u591c\u5165\u7b2c\u4e00\u5821\uff0c\u7528\u5fc3\u4f55\u5728?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u63a2\u542c\u865a\u5b9e\u3002\u201d \u3000\u3000\u84dd\u8863\u5c11\u5973\u9053\uff1a\u201c\u63a2\u542c\u5230\u4ec0\u4e48\u6ca1\u6709?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5c31\u7b97\u662f\u63a2\u542c\u5230\u4ec0\u4e48\uff0c\u4f3c\u4e4e\u4e5f\u662f\u65e0\u6cd5\u4f20\u9012\u51fa\u53bb\u4e86\u3002\u201d \u84dd\u8863\u5c11\u5973\u9053\uff1a\u201c\u4f60\u5f88\u5766\u767d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u5df2\u7ecf\u6709\u4e86\u5e95\u5b50\uff0c\u80c6\u5b50\u58ee\u4e86\u4e0d\u5c11\uff0c\u7b11\u4e00\u7b11\u9053\uff1a \u201c\u5728\u4e0b\u77e5\u9053\u7684\uff0c\u90fd\u5df2\u7ecf\u544a\u8bc9\u4e86\u5434\u59d1\u5a18\u4e86\u3002\u201d \u3000\u3000\u84dd\u8863\u5c11\u5973\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u56de\u987e\u4e86\u5434\u53cc\u5973\u4e00\u773c\uff0c\u9053\uff1a \u201c\u5927\u59ca\uff0c\u8fd9\u662f\u4f60\u4f4f\u7684\u5730\u65b9\uff0c\u6211\u770b\uff0c \u8fd8\u662f\u628a\u4ed6\u5e26\u5230\u5c0f\u59b9\u90a3\u91cc\u53bb\uff0c\u95ee\u4e2a\u6e05\u695a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e0d\u6e05\u695a\u7684\u5730\u65b9\uff0c\u5c3d\u7ba1\u8bf7\u95ee\u3002\u201d \u3000\u3000\u84dd\u8863\u5c11\u5973\u5fae\u5fae\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5927\u59ca\uff0c\u4f60\u51c6\u5907\u600e\u6837\u5904\u7f6e\u4ed6?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u8fd8\u6ca1\u6709\u51b3\u5b9a\u3002\u201d \u3000\u3000\u84dd\u8863\u5c11\u5973\u9053\uff1a\u201c\u4f55\u4e0d\u4ea4\u7ed9\u5c0f\u59b9\u5462?\u59ca\u59ca\u4e5f\u597d\u5c11\u4e86\u4e00\u4e9b\u9ebb\u70e6\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u7b49\u6211\u51b3\u5b9a\u4e86\uff0c\u518d\u901a\u77e5\u4f60\uff0c\u5982\u4f55?\u201d \u3000\u3000\u84dd\u8863\u5c11\u5973\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u5927\u59ca\uff0c\u4ed6\u662f\u72af\u4eba\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u84dd\u8863\u5c11\u5973\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u5927\u59ca\u4e3a\u4ec0\u4e48\u4e0d\u628a\u4ed6\u4ea4\u7ed9\u5c0f\u59b9\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u6211\u5df2\u7ecf\u8bf4\u5f97\u5f88\u6e05\u695a\u4e86\uff0c\u7b49\u6211\u51b3\u5b9a\u4e86\uff0c\u6211\u5c31\u4f1a\u901a\u77e5\u4f60\u3002\u201d \u3000\u3000\u84dd\u8863\u5c11\u5973\u9053\uff1a\u201c\u5927\u59ca\u65e2\u7136\u4e0d\u613f\u5c0f\u59b9\u628a\u4ed6\u5e26\u8d70\uff0c\u53ef\u5426\u628a\u4ed6\u4ea4\u5165\u5211\u5802\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u96be\u9053\u5bf9\u5927\u59ca\u4e0d\u592a\u653e\u5fc3\u5417?\u201d \u3000\u3000\u5979\u5c31\u662f\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6253\u91cf\u4e86\u9ad8\u5e7d\u5170\u4e00\u773c\uff0c\u53d1\u89c9\u5979\u8d64\u624b\u7a7a\u62f3\uff0c\u5e76\u672a\u4f69\u5e26\u4e03\u8272\u5251\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7ad9\u8d77\u8eab\u5b50\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5c0f\u59b9\u600e\u4e48\u6562\u5462?\u6211\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e8c\u59b9\u597d\u8d70\uff0c\u6055\u6211\u4e0d\u8fed\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u5927\u59ca\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u96ea\u6885\uff0c\u9001\u9001\u4f60\u4eec\u5c0f\u59d0\u3002\u201d \u3000\u3000\u96ea\u6885\u8fd8\u611f\u5c34\u5c2c\uff0c\u4e0d\u77e5\u9053\u5982\u4f55\u5e94\u4ed8\u8fd9\u4e2a\u573a\u9762\uff0c\u7ad9\u5728\u90a3\u91cc\u53d1\u6123\u3002 \u3000\u3000\u5434\u53cc\u5973\u8fd9\u53e5\u8bdd\uff0c\u7ed9\u5979\u89e3\u4e86\u56f4\uff0c\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u5a62\u5b50\u9075\u547d\u3002\u201d \u3000\u3000\u5feb\u6b65\u884c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u671b\u7740\u4e24\u4eba\u7684\u80cc\u5f71\uff0c\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u597d\u50cf\u6211\u88ab\u4f60\u62d6\u4e0b\u6c34\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\u662f\uff0c\u9ad8\u5e7d\u5170\u597d\u50cf\u5bf9\u6211\u751f\u51fa\u4e86\u8bef\u4f1a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u51c6\u5907\u5e26\u6211\u8fdb\u5211\u5802\uff0c\u7136\u540e\uff0c\u60f3\u6cd5\u5b50\u903c\u4f9b?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u7684\u5475\u62a4\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7a81\u7136\u957f\u957f\u7684\u53f9\u606f\u4e00\u58f0\uff0c\u5750\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u5750\u5427\uff01\u201d \u3000\u3000\u4f55\u5bd2\u8863\u4f9d\u8a00\u5750\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u5434\u53cc\u5973\u5fae\u5fae\u76b1\u8d77\u7709\u5934\uff0c\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e5f\u53ea\u597d\u9759\u9759\u7684\u5750\u7740\u3002 \u3000\u3000\u4ed6\u60f3\u8bf4\u51e0\u53e5\u8bdd\uff0c\u5374\u53c8\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\u3002 \u3000\u3000\u5434\u53cc\u5973\u6c89\u601d\u4e86\u826f\u4e45\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u8fd9\u4ef6\u4e8b\u53ef\u5927\u53ef\u5c0f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u4f55\u4e00\u4e2a\u5927\u5c0f\u6cd5\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u9ad8\u5e7d\u5170\u5982\u679c\u628a\u8fd9\u4ef6\u4e8b\uff0c\u544a\u8bc9\u5821\u4e3b\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u90a3\u4f1a\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u5c40\u9762\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u7684\u611f\u89c9\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u4e49\u7236\u4e00\u5b9a\u4f1a\u903c\u7740\u6211\u628a\u4f60\u4ea4\u51fa\u53bb\uff0c\u4ea4\u7ed9\u4e86\u5211\u5802\u4e4b\u540e\uff0c\u4f1a\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u5c40\u9762\uff0c\u90a3\u5c31\u5f88\u96be\u8bf4\u4e0b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6700\u574f\u4f1a\u662f\u4e2a\u4ec0\u4e48\u5c40\u9762\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6298\u78e8\u4f60\uff0c\u6253\u5f97\u4f60\u904d\u4f53\u9cde\u4f24\uff0e\u903c\u51fa\u4f60\u80f8\u4e2d\u7684\u6bcf\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u4e48\u4e25\u91cd\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5211\u7f5a\u5982\u7089\uff0c\u4efb\u4f60\u662f\u94a2\u94c1\u6c49\u5b50\uff0c\u4e5f\u4e00\u6837\u65e0\u6cd5\u627f\u53d7\uff0c\u4f55\u51b5\uff0c\u4ed6\u4eec\u4f1a\u5148\u5e9f\u4e86\u4f60\u7684\u6b66\u529f\uff0c\u4f7f\u4f60\u8fde\u6297\u62d2\u7684\u673a\u4f1a\uff0c\u4e5f\u5b8c\u5168\u6d88\u5931\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u60f3\u50cf\uff0c\u5931\u53bb\u6b66\u529f\u4e4b\u540e\uff0c\u5982\u4f55\u6297\u62d2\u90a3\u52a0\u8bf8\u5728\u8eab\u4e0a\u7684\u6b8b\u9177\u5211\u7f5a\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u6211\u5982\u575a\u6301\u4e0d\u628a\u4f60\u4ea4\u51fa\u53bb\uff0c\u8fde\u6211\u4e5f\u4f1a\u88ab\u62d6\u8fdb\u6f29\u6da1\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ed6\u4eec\u96be\u9053\u4f1a\u5bf9\u59d1\u5a18\u7528\u6b66?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f1a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u59d1\u5a18\u628a\u5728\u4e0b\u4ea4\u7ed9\u4ed6\u4eec\u5427!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u81ea\u4fe1\u80fd\u6491\u9177\u5211\u6298\u78e8?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u5728\u4e0b\u4e0d\u6562\u6709\u592a\u5f3a\u7684\u81ea\u4fe1\uff0c\u53ea\u6c42\u59d1\u5a18\u80fd\u89e3\u9664\u5728\u4e0b\u8eab\u4e0a\u7684\u7981\u5236\uff0c\u4f7f\u6211\u6709\u4e00\u4e2a\u53cd\u6297\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u4e24\u9053\u51b7\u5389\u7684\u76ee\u5149\uff0c\u7f13\u7f13\u7684\u8f6c\u6ce8\u5230\u4f55\u5bd2\u8863\u7684\u8138\u4e0a\uff0c\u51dd\u6ce8\u4e86\u826f\u4e45\uff0c\u53f9\u9053\uff1a\u201c\u6211\u4e0d\u5e0c\u671b\u4f60\u53d7\u5230\u4f24\u5bb3\u4e86\uff0c\u4f46\u53c8\u5f88\u96be\u627e\u5230\u4fdd\u62a4\u4f60\u7684\u85c9\u53e3\u2026\u2026\u201d \u3000\u3000\u96ea\u6885\u7f13\u7f13\u884c\u4e86\u8fdb\u6765\uff0c\u884c\u8fd1\u5434\u53cc\u5973\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u5c0f\u59d0\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u6162\u6162\u5730\u8f6c\u8fc7\u5934\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a \u201c\u4e8c\u5c0f\u59d0\u8bf4\uff0c\u5979\u62db\u60f9\u4e86\u5927\u5c0f\u59d0\u751f\u6c14\uff0c\u5fc3\u4e2d\u5341\u5206\u4e0d\u5b89\u3002\u201d \u5434\u53cc\u5973\u51b7\u54fc\u4e00\u58f0\u3002 \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\u8bf4\uff0c\u4eca\u5929\u665a\u4e0a\uff0c\u5979\u51c6\u5907\u4e00\u684c\u9152\u83dc\uff0c\u8bf7\u5927\u5c0f\u59d0\u559d\u4e00\u676f\uff0c\u6d88\u6d88\u6c14\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u6562\u3002\u201d \u3000\u3000\u96ea\u6885\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e3b\u5ba2\u9664\u4e86\u5927\u5c0f\u59d0\u5916\uff0c\u8fd8\u6709\u4f55\u516c\u5b50\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u516c\u5b50?\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u662f\uff0c\u554a\uff01\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\u6709\u6ca1\u6709\u5f04\u9519\uff0c\u4f55\u5bd2\u8863\u53ea\u662f\u4e00\u4e2a\u72af\u4eba\u554a\uff01\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a \u201c\u4e8c\u5c0f\u59d0\u8bf4\uff0c\u4ed6\u7559\u5728\u5927\u5c0f\u59d0\u7684\u5f69\u8679\u697c\uff0c\u5c31\u662f\u5927\u5c0f\u59d0\u7684\u8d35\u5bbe\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u54fc!\u5979\u53ef\u662f\u5728\u8ba5\u7b11\u6211?\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\u8bf4\uff0c\u5927\u5c0f\u59d0\u4e00\u5411\u516c\u6b63\uff0c\u4e3b\u6301\u5821\u4e2d\u5927\u4e8b\uff0c\u4eba\u4eba\u656c\u754f\uff0c\u65e2\u7136\u4e0d\u80af\u628a\u4f55\u516c\u5b50\u79fb\u4ea4\u5211\u5802\uff0c \u81ea\u7136\u662f\u975e\u4eb2\u5373\u6545\uff0c\u6240\u4ee5\uff0c\u4e8c\u5c0f\u59d0\u8bf4\uff0c\u5979\u81ea\u89c9\u592a\u9c81\u83bd\u4e86\u4e00\u4e9b\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u96ea\u6885\u628a\u76ee\u5149\u8f6c\u6ce8\u5230\u4f55\u5bd2\u8863\u8eab\u4e0a\uff0c\u9053\uff1a \u201c\u4f55\u516c\u5b50\uff0c\u4f60\u610f\u4e0b\u5982\u4f55?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u60f3\uff0c\u8fd8\u662f\u7531\u5927\u5c0f\u59d0\u51b3\u5b9a\u5427\uff01\u201d \u3000\u3000\u5434\u53cc\u5973\u53c8\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u597d!\u53bb\u544a\u8bc9\u4e8c\u5c0f\u59d0\uff0c\u6211\u4eec\u662f\u597d\u59ca\u59b9\uff0c\u5979\u8fd9\u756a\u597d\u610f\uff0c\u6211\u5982\u4e0d\u9886\u53d7\u4e0b\u6765\uff0c\u53ea\u6015\u5979\u5fc3\u91cc\u96be\u53d7\uff0c\u4eca\u5929\u665a\u4e0a\uff0c\u6211\u4eec\u4e00\u5b9a\u53bb\u7684\u3002\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u5a62\u5b50\u8fd9\u5c31\u53bb\u544a\u8bc9\u4e8c\u5c0f\u59d0\uff0c\u597d\u8ba9\u5979\u5fc3\u4e2d\u5feb\u4e50\u4e00\u4e9b\u3002\u201d \u3000\u3000\u8f6c\u8eab\u6025\u6b65\u800c\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u54b1\u4eec\u4eca\u591c\u771f\u53bb\u8d74\u7ea6?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u6697\u9053\uff1a\u8fd9\u4e24\u4eba\u662f\u7b2c\u4e00\u5821\u4e2d\u4e3b\u529b\uff0c\u5047\u82e5\u80fd\u591f\u4f7f\u5f97\u5979\u4eec\u7ffb\u8138\uff0c\u5bf9\u6211\u4eec\u5c31\u5927\u6709\u5e2e\u52a9\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u8bf4\u5e7d\u5170\u59d1\u5a18\u4f1a\u4e0d\u4f1a\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u8138\u4e0a\u73b0\u51fa\u72b9\u8c6b\u4e4b\u8272\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u5979\u4f1a\u4e0d\u4f1a\u5728\u9152\u83b1\u4e4b\u4e2d\u4e0b\u6bd2?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u4e0b\u6b63\u662f\u6b64\u610f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u6211\u65e0\u6cd5\u80af\u5b9a\uff0c\u4e0d\u8fc7\uff0c\u6211\u60f3\u5979\u4e0d\u6562\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5979\u5fc3\u4e2d\u5e94\u8be5\u660e\u767d\uff0c\u6211\u4eec\u5fc3\u4e2d\u65e9\u5df2\u6709\u4e86\u51c6\u5907\uff0c\u4e00\u65e6\u88ab\u6211\u4eec\u53d1\u89c9\uff0c\u5979\u5982\u4f55\u4e0b\u53f0\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u53ef\u4ee5\u8bf4\u51fa\u4e00\u767e\u79cd\u7406\u7531\u6765\u89e3\u91ca\u8fd9\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e00\u4ef6\u4e8b\u5b9e\uff0c\u80dc\u8fc7\u767e\u53e5\u96c4\u8fa9\uff0c\u90a3\u65f6\uff0c\u4e0d\u8bba\u5979\u8bf4\u4ec0\u4e48\u6211\u4eec\u90fd\u4e0d\u4f1a\u542c\u4e86\u3002\u201d \u3000\u3000\u5979\u4e00\u76f4\u4f7f\u7528\u6211\u4eec\uff0c\u4e0d\u7528\u6211\u5b57\uff0c\u4f3c\u662f\u628a\u4f55\u5bd2\u8863\u4e5f\u5e26\u8fdb\u53bb\u4e86\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u82e5\u8bc1\u5b9e\u6211\u4eec\u5df2\u7ecf\u4e2d\u4e86\u6bd2\uff0c\u4f3c\u662f\uff0c\u5979\u4e5f\u7528\u4e0d\u7740\u518d\u89e3\u91ca\u4ec0\u4e48\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u5144\uff0c\u8bd5\u6bd2\u7684\u65b9\u6cd5\u5f88\u591a\uff0c\u54b1\u4eec\u4f3c\u4e4e\u662f\u7528\u4e0d\u7740\u4ee5\u8eab\u76f8\u8bd5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u5434\u53cc\u5973\u662f\u4e2a\u5f88\u806a\u660e\u7684\u4eba\uff0c\u4f55\u5bd2\u8863\u5982\u82e5\u518d\u8bf4\u5f97\u9732\u9aa8\u4e00\u4e9b\uff0c\u6050\u6015\u4f1a\u5f15\u8d77\u5434\u53cc\u5973\u7684\u6000\u7591\u3002 \u3000\u3000\u671b\u671b\u4f55\u5bd2\u8863\u7684\u795e\u60c5\uff0c\u5434\u53cc\u5973\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u5728\u60f3\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b49\u7684\u5c31\u662f\u8fd9\u53e5\u8bdd\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5728\u4e0b\u4e0d\u4fbf\u8bf4\u4e86\uff0c\u4e07\u4e00\u518d\u8bf4\u4e0b\u53bb\uff0c\u4f3c\u4e4e\u662f\u8981\u5f15\u8d77\u8bef\u4f1a\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u8bf4\u5427!\u4e0d\u7528\u62c5\u5fc3\uff0c\u8bf4\u9519\u4e86\u4e5f\u4e0d\u8981\u7d27\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5c31\u5728\u4e0b\u6240\u77e5\uff0c\u6c5f\u6e56\u4e0a\uff0c\u6709\u5f88\u591a\u914d\u5236\u9ad8\u660e\u7684\u6bd2\u836f\uff0c\u5c31\u7b97\u94f6\u7c2a\u8c61\u7259\uff0c\u4e5f\u8bd5\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u7b49\u4e8b\uff0c\u5728\u6c5f\u6e56\u4e0a\uff0c\u4ea6\u662f\u6781\u4e3a\u666e\u901a\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u4e00\u7b11\u9053\uff1a \u201c\u4f55\u5bd2\u8863\uff0c\u4f60\u662f\u5173\u5fc3\u6211\uff0c\u8fd8\u662f\u5173\u5fc3\u81ea\u5df1\uff1f \u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u5173\u5fc3\u59d1\u5a18\uff0c\u4e5f\u5173\u5fc3\u81ea\u5df1\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u6211\u5728\u60f3\uff0c\u6211\u4eec\u5e94\u8be5\u548c\u4ed6\u4eec\u8c08\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u660e\u767d\u4e86\uff0c\u4f46\u5374\u6545\u4f5c\u4e0d\u660e\u767d\uff0c\u9053\uff1a\u201c\u8c08\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u4eec\u4e4b\u95f4\uff0c\u5e94\u8be5\u6709\u4e00\u70b9\u4ec0\u4e48\u5173\u7cfb\uff0c\u624d\u80fd\u548c\u4ed6\u4eec\u8c08\u8c08\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u770b\u54b1\u4eec\u62c9\u4e00\u70b9\u4ec0\u4e48\u5173\u7cfb\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u60f3\u60f3\u770b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e00\u8868\u4e09\u5343\u91cc\uff0c\u5c31\u8bf4\u6211\u662f\u4f60\u8868\u54e5\u5982\u4f55?\u201d \u3000\u3000\u5434\u53cc\u5973\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u597d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4eec\u662f\u540c\u95e8\u5e08\u5144\u59b9\u5982\u4f55?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5929\u4e0b\u6b66\u6797\u540c\u9053\uff0c\u6709\u8c01\u4e0d\u77e5\u4f60\u662f\u51fa\u8eab\u6b66\u5f53\u95e8\u4e0b\u7684\u5f1f\u5b50\uff0c\u6211\u548c\u6b66\u5f53\u95e8\u4e0b\u4e0d\u4f46\u6ca1\u6709\u6e0a\u6e90\uff0c\u800c\u4e14\u4e48\uff0c\u8fd8\u6709\u4e00\u70b9\u7ea0\u7eb7\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u5012\u4e5f\u662f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u518d\u60f3\u60f3\u770b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211!\u6211\u60f3\u4e0d\u51fa\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u7537\u5973\u4e4b\u95f4\uff0c\u9664\u4e86\u4eb2\u621a\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u5173\u7cfb\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u670b\u53cb\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u591f\u4eb2\u5bc6\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u4e0d\u80fd\u518d\u88c5\u7cca\u6d82\u4e86\uff0c\u9053\uff1a \u201c\u9664\u4e86\u4eb2\u621a\u3001\u5e08\u5144\u59b9\u3001\u670b\u53cb\u4e4b\u5916\uff0c\u53ea\u6709\u592b\u59bb\u4e00\u4f26\u4e86\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u592b\u59bb\uff0c\u90a3\u600e\u4e48\u884c\u5462\uff01\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u82e5\u592b\u59bb\u4e5f\u4e0d\u884c\uff0c\u5728\u4e0b\u5c31\u60f3\u4e0d\u51fa\u522b\u7684\u529e\u6cd5\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5982\u82e5\u8981\u7528\u592b\u59bb\u540d\u4efd\uff0c\u90a3\u53ea\u80fd\u672a\u5a5a\u592b\u59bb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u592a\u59d4\u5c48\u59d1\u5a18\u4e86\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5509!\u6709\u4ec0\u4e48\u529e\u6cd5\u5462?\u82e5\u662f\u6211\u4eec\u60f3\u5e73\u5e73\u5b89\u5b89\u7684\u5e94\u5fd6\u8fc7\u53bb\u8fd9\u4ef6\u4e8b\uff0c\u5fc5\u9700\u8981\u627e\u4e00\u4e2a\u53ef\u4ee5\u5e94\u4ed8\u7684\u7406\u7531\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u5c82\u4e0d\u4f1a\u4f7f\u59d1\u5a18\u7684\u6e05\u540d\u73b7\u6c61\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u6ca1\u6709\u6cd5\u5b50\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u4e00\u63d0\uff0c\u6211\u60f3\u8d77\u4e00\u4ef6\u5f88\u91cd\u8981\u7684\u4e8b\u4e86\uff0c\u6211\u60f3\u95ee\u95ee\u4f60\uff0c\u4f60\u51c6\u5907\u600e\u4e48\u5e94\u4ed8\u8fd9\u4ef6\u4e8b\u60c5?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e94\u4ed8\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u8c0e\u8a00\u8bf4\u8fc7\u4ee5\u540e\uff0c\u5e73\u5e73\u5b89\u5b89\u7684\u5ea6\u8fc7\u4e86\u8fd9\u6b21\u98ce\u6ce2\uff0c\u4f60\u51c6\u5907\u600e\u4e48\u529e?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u5728\u4e0b\u4e5f\u6b63\u4e3a\u6b64\u4e8b\u53d1\u6101\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u53ea\u662f\u53d1\u6101\u4e5f\u4e0d\u80fd\u89e3\u51b3\u95ee\u9898\uff0c \u4f60\u8981\u60f3\u4e2a\u4e3b\u610f\u624d\u884c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u59d1\u5a18\u624d\u6167\uff0c\u80dc\u6211\u5341\u500d\uff0c\u5982\u82e5\u59d1\u5a18\u60f3\u4e0d\u51fa\u529e\u6cd5\uff0c\u5728\u4e0b\u53c8\u5982\u4f55\u80fd\u60f3\u51fa\u529e\u6cd5\u5462?\u201d \u3000\u3000\u5176\u5b9e\uff0c\u8fd9\u4ef6\u4e8b\u5f88\u597d\u89e3\u51b3\uff0c\u53ea\u8981\u4f55\u5bd2\u8863\u5927\u80c6\u8bf4\u51fa\u6765\u5c31\u884c\u4e86\uff0c\u4f46\u4f55\u5bd2\u8863\u504f\u504f\u4e0d\u8bf4\u3002 \u3000\u3000\u5434\u53cc\u5973\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a \u201c\u4ee5\u4f60\u4e4b\u80fd\uff0c\u4e5f\u8bb8\u65e9\u5df2\u60f3\u51fa\u529e\u6cd5\u4e86\uff0c\u6709\u4e9b\u4e8b\uff0c\u7eb5\u7136\u6211\u80fd\u60f3\u5230\uff0c\u4f46\u4e5f\u4e0d\u4fbf\u8bf4\u51fa\u53e3\u6765\u3002\u201d \u3000\u3000\u8fd9\u5df2\u7ecf\u8bf4\u5f97\u65e0\u6cd5\u518d\u660e\u663e\u4e86\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e5f\u8b66\u89c9\u5f97\u5230\uff0c\u518d\u8981\u88c5\u7cca\u6d82\u4e0b\u53bb\uff0c\u5f88\u53ef\u80fd\u4f1a\u4f7f\u4e8b\u60c5\u7838\u4e86\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u82e5\u59d1\u5a18\u8981\u4fdd\u6301\u6e05\u540d\uff0c\u53ea\u6709\u4e00\u4e2a\u529e\u6cd5\uff0c\u90a3\u5c31\u662f\u2026\u2026\u5c31\u662f\u5ac1\u7ed9\u6211\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u4ecd\u7136\u88c5\u51fa\u4e00\u526f\u72b9\u62b1\u7435\u7436\u534a\u906e\u9762\u7684\u59ff\u6001\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4e0d\u662f\u53eb\u903c\u4e0a\u6881\u5c71\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u597d\u50cf\u6ca1\u6709\u522b\u7684\u529e\u6cd5\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u6211\u5012\u8981\u95ee\u95ee\u4f60\u4e86\uff0c\u4f60\u771f\u7684\u6562\u5a36\u6211?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u81ea\u7136\u662f\u65e9\u5df2\u60f3\u8fc7\u4e86\u8fd9\u4e2a\u95ee\u9898\uff0c\u5434\u53cc\u5973\u957f\u5f97\u5f88\u7f8e\uff0c\u800c\u4e14\uff0c\u4e5f\u5e76\u975e\u4f20\u8bf4\u90a3\u6837\u574f\uff0c\u81f3\u5c11\uff0c\u5728\u4f55\u5bd2\u8863\u8fd9\u4e9b\u65f6\u95f4\u7684\u89c2\u5bdf\u4e2d\uff0c\u5979\u8fd8\u662f\u4e2a\u5f88\u77e5\u5206\u5bf8\u7684\u4eba\u3002 \u3000\u3000\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u53ea\u8981\u59d1\u5a18\u4e0d\u5f03\uff0c\u5728\u4e0b\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u522b\u7b54\u5e94\u5f97\u592a\u5feb\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u7684\u8fc7\u53bb?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1b\u201c\u600e\u4e48?\u4f60\u7684\u8fc7\u53bb\u4e0d\u592a\u597d?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u6b62\u662f\u4e0d\u592a\u597d\uff0c\u800c\u4e14\u662f\u5f88\u574f\uff0c\u5f88\u574f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6!\u574f\u5230\u4ec0\u4e48\u7a0b\u5ea6\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u574f\u5230\u6c5f\u6e56\u4e0a\u4eba\u95fb\u540d\u4e27\u80c6\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u662f\u5f88\u6709\u540d\u6c14\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9!\u6211\u8fc7\u53bb\u6709\u4e2a\u5916\u53f7\uff0c\u53eb\u505a\u5929\u9b54\u5973\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6545\u4f5c\u5403\u60ca\uff0c\u9053\uff1a\u201c\u5929\u9b54\u5973?\u201d\u3002 \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u7b11\u5f97\u5341\u5206\u5a07\u5a9a\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7b2c\u4e00\u6b21\u770b\u5230\u5434\u53cc\u5973\u5a07\u5a9a\u7684\u7b11\u5bb9\u4e86\u3002 \u3000\u3000\u5979\u672c\u6765\u5f88\u7f8e\uff0c\u4f46\u5374\u592a\u51b7\uff0c\u8fd9\u4e00\u6b21\uff0c\u5316\u53bb\u4e86\u5916\u5c42\u7684\u51b7\u5389\uff0c\u4e5f\u66f4\u663e\u51fa\u5979\u7684\u5973\u4eba\u5473\u3002 \u3000\u3000\u53ea\u89c1\u5979\u4e3e\u624b\u7406\u4e00\u7406\u9b13\u8fb9\u7684\u6563\u53d1\uff0c\u9053\uff1a\u201c\u600e\u4e48\u6837?\u4f60\u5bb3\u6015\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4f46\u6211\u542c\u8fc7\u5929\u9b54\u5973\u7684\u4e8b\uff0c\u50cf\u4e00\u9053\u5f69\u8679\uff0c\u4f46\u5374\u5f88\u5feb\u6d88\u5931\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u542c\u5230\u7684\u53ea\u662f\u8fd9\u4e9b\u4e86?\u201d \u3000\u3000\u53f9\u53e3\u6c14\u63a5\u9053\uff1a\u201c\u592a\u7f8e\u4e86\uff0c\u4f46\u5929\u9b54\u5973\u4e0d\u662f\u90a3\u4e48\u6837\u7684\u4f20\u8bf4\uff0c\u5979\u50cf\u4e00\u9635\u96f7\u96e8\uff0c\u4e00\u9635\u5f3a\u70c8\u7684\u66b4\u98ce\u96e8\uff0c\u867d\u5f88\u77ed\u6682\uff0c\u4f46\u5374\u7ed9\u4e86\u6c5f\u6e56\u4e0a\u5f88\u6df1 \u3000\u3000\u7684\u8bb0\u5fc6\uff0c\u5f88\u574f\u7684\u540d\u58f0\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e5f\u7ed9\u4eba\u5f88\u6df1\u7684\u6000\u5ff5\uff0c\u5f88\u6df1\u7684\u5411\u5f80\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7a81\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u4f60\u597d\u50cf\u662f\u6709\u4e3a\u800c\u6765\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u8b66\u89c9\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u59d1\u5a18\u53ef\u662f\u5bf9\u5728\u4e0b\u6709\u4e86\u6000\u7591\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u4e00\u5f00\u59cb\u5c31\u6000\u7591\uff0c\u4e0d\u8fc7\uff0c\u73b0\u5728\uff0c\u6211\u66f4\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u5934\u4e00\u9707\uff0c\u9053\uff1a\u201c\u4f60\u660e\u767d\u4e86\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u4eec\u7684\u7528\u5fc3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u7528\u5fc3?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5468\u90ce\u5999\u8ba1\u5b89\u5929\u4e0b\uff0c\u966a\u4e86\u592b\u4eba\u53c8\u6298\u5175\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e16\u4e0a\uff0c\u6709\u7f8e\u4eba\u8ba1\uff0c\u4f60\u4eec\u662f\u4e0d\u662f\u5728\u800d\u7f8e\u7537\u8ba1\u554a\uff01\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u4e0b\u53ea\u6015\u62c5\u53d7\u4e0d\u8d77\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u662f\u66f4\u597d\uff0c\u771f\u8981\u662f\u7f8e\u7537\u8ba1\uff0c\u6211\u4e5f\u5f88\u4f69\u670d\u4f60\u4eec\u9ec4\u5821\u4e3b\u7684\u667a\u6167\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u53c8\u600e\u4e48\u89e3\u91ca?\u201d \u3000\u3000\u5434\u53cc\u5973\u9063\uff1a \u201c\u4ed6\u4e3a\u4ec0\u4e48\u9009\u4e2d\u4f60\uff0c\u800c\u4e14\uff0c \u4f60\u53c8\u80fd\u4f7f\u6211\u6026\u7136\u52a8\u5fc3?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u597d\u50cf\uff0c\u6211\u53d8\u6210\u4e86\u73a9\u5177\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u5984\u81ea\u83f2\u8584\uff0c\u544a\u8bc9\u6211\u771f\u8bdd\uff0c\u662f\u4e0d\u662f\u771f\u7684\u4e0d\u6015\u6211?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e5f\u4e0d\u8ba1\u8f83\u6211\u7684\u8fc7\u53bb?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5f80\u4e8b\u5982\u4e91\u70df\uff0c\u7f25\u7f08\u4e8e\u865a\u65e0\u4e4b\u95f4\uff0c\u6211\u53c8\u5982\u4f55\u8ba1\u8f83\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4f55\u5bd2\u8863\uff0c\u6211\u4e0d\u613f\u88ab\u4eba\u6b3a\u9a97\uff0c\u6240\u4ee5\uff0c\u4f60\u6709\u4ec0\u4e48? \u3000\u3000\u6211\u4eec\u5c31\u8bf4\u6e05\u695a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d!\u59d1\u5a18\u8bf7\u8bf4\uff0c\u5728\u4e0b\u4e00\u5b9a\u636e\u5b9e\u56de\u7b54\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u771f\u7684\u4f1a\u5a36\u6211\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5343\u771f\u4e07\u786e\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5982\u82e5\u4f60\u5df2\u53d1\u89c9\u2026\u2026\u6211\u5df2\u7ecf\u4e0d\u662f\u4e00\u4e2a\u597d\u5973\u5b69\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5f80\u4e8b\u4e0d\u8ba1\u8f83\uff0c\u6211\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u662f\u7531\u8877\u4e4b\u8a00?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u6211\u62a5\u7b54\u4f60\u7684\u662f\u4ee5\u540e\uff0c\u4ee5\u540e\uff0c\u6211\u4f1a\u6d17\u624b\u505a\u7fb9\u6c64\uff0c\u5c3d\u91cf\u4f5c\u4e00\u4e2a\u597d\u59bb\u5b50\u3002\u201d \u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u5e0c\u671b\u4f60\u7531\u4eca\u65e5\u8d77\uff0c\u7acb\u5730\u6210\u4f5b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u660e\u767d\uff0c\u6211\u4f1a\u5c3d\u529b\u5e2e\u52a9\u81ea\u5df1\u7684\u4e08\u592b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7a81\u7136\u4f38\u51fa\u624b\uff0c\u62e6\u4f4f\u4e86\u5434\u53cc\u5973\u7684\u67f3\u8170\u3002 \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u7684\u4e00\u6014\uff0c\u7f13\u7f13\u504e\u5165\u4e86\u4f55\u5bd2\u8863\u7684\u6000\u4e2d\u3002 \u3000\u3000\u539f\u672c\u8fd9\u53ea\u662f\u4e00\u79cd\u8ba1\u8c0b\u3002 \u3000\u3000\u4f46\u4f55\u5bd2\u8863\u5374\u771f\u7684\u6709\u70b9\u52a8\u4e86\u60c5\u3002 \u3000\u3000\u4e00\u5219\u662f\uff0c\u5434\u53cc\u5973\u957f\u5f97\u5f88\u7f8e\uff0c\u51b7\u8273\u4e2d\u6709\u4e00\u79cd\u5a07\u5a9a\uff0c\u518d\u8005\uff0c\u5979\u4e0d\u50cf\u4f20\u8bf4\u4e2d\u90a3\u4e48\u574f\u3002 \u3000\u3000\u6700\u91cd\u8981\u7684\u4e00\u70b9\u662f\uff0c\u96ea\u6885\u5728\u6697\u4e2d\u5e2e\u4e86\u5fd9\uff0c\u544a\u8bc9\u4e86\u5434\u53cc\u5973\u5728\u7b2c\u4e00\u5821\u4e2d\u7684\u4e25\u8c28\u751f\u6d3b\u3002 \u3000\u3000\u8f6f\u7389\u6e29\u5f97\u62b1\u6ee1\u6000\uff0c\u4f55\u5bd2\u8863\u6e10\u6e10\u7684\u6709\u70b9\u628a\u6301\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5f00\u59cb\u4e86\u4e0d\u89c4\u77e9\u7684\u6d3b\u52a8\u4e86\u3002 \u3000\u3000\u5434\u53cc\u5973\u626d\u52a8\u4e00\u4e0b\u504e\u5165\u5728\u4f55\u5bd2\u8863\u6000\u4e2d\u7684\u5a07\u8eaf\u3002 \u3000\u3000\u4f46\u5979\u5e76\u6ca1\u6709\u62d2\u7edd\u3002 \u3000\u3000\u4e0d\u77e5\u4f55\u65f6\uff0c\u56db\u74e3\u5634\u5507\uff0c\u4ea4\u5408\u5728\u4e00\u8d77\u3002 \u3000\u3000\u65e0\u6cd5\u5206\u8fa8\u51fa\uff0c\u662f\u4f55\u5bd2\u8863\u4f4e\u9996\u8f7b\u543b\uff0c\u8fd8\u662f\u5434\u53cc\u5973\u8fce\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5434\u53cc\u5973\u7684\u53cc\u988a\uff0c\u6cdb\u8d77\u4e86\u6d53\u91cd\u7684\u8273\u7ea2\u3002 \u3000\u3000\u4e0d\u662f\u7f9e\u7ea2\uff0c\u800c\u662f\u4f55\u5bd2\u8863\u7684\u80c6\u5927\u5e26\u52a8\uff0c\u6311\u8d77\u4e86\u5979\u7684\u6b32\u7130\u3002 \u3000\u3000\u50cf\u4e00\u6761\u86c7\u6837\uff0c\u5434\u53cc\u5973\u7d27\u7d27\u7684\u7f20\u4f4f\u4e86\u4f55\u5bd2\u8863\u7684\u8eab\u5b50\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e5f\u6709\u4e9b\u628a\u6301\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u62b1\u8d77\u4e86\u5434\u53cc\u5973\u7684\u8eab\u5b50\uff0c\u76f4\u5411\u697c\u4e0a\u884c\u53bb\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u4e0a\u8fc7\u5f69\u8679\u697c\uff0c\u4f46\u4ed6\u5374\u5f88\u8f7b\u6613\u7684\u627e\u5230\u4e86\u5434\u53cc\u5973\u7684\u95fa\u623f\u3002 \u3000\u3000\u5374\u662f\u4e00\u95f4\u5e03\u7f6e\u5f97\u5f88\u7cbe\u81f4\u7684\u5367\u5ba4\u3002 \u3000\u3000\u4e0d\u77e5\u662f\u5434\u53cc\u5973\u65e9\u6709\u51c6\u5907\uff0c\u6216\u662f\u5979\u7279\u522b\u559c\u7231\u5e7d\u9759\uff0c\u5f69\u8679\u697c\u4e0a\uff0c\u7adf\u7136\u4e0d\u89c1\u4e00\u4e2a\u4e2b\u5934\u3002 \u3000\u3000\u5e26\u7740\u4e00\u8eab\u706b\uff0c\u4f55\u5bd2\u8863\u6ca1\u6709\u4ed4\u7ec6\u7684\u6b23\u8d4f\u95fa\u623f\u7684\u5e03\u7f6e\uff0c\u76f4\u5954\u90a3\u9526\u5e10\u4f4e\u5782\u5904\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u80c6\u5927\u7684\u6495\u4e0b\u4e86\u5434\u53cc\u5973\u8eab\u4e0a\u7684\u8863\u88d9\u3002 \u3000\u3000\u5434\u53cc\u5973\u4f3c\u4e4e\u662f\u7a81\u7136\u9192\u4e86\u8fc7\u6765\uff0c\u5ffd\u7136\u95f4\u4e00\u6eda\u8eab\uff0c\u5230\u5927\u5e8a\u7684\u4e00\u89d2\u3002 \u3000\u3000\u7528\u624b\u62c9\u8d77\u4e86\u88ab\u6495\u88c2\u7684\u8863\u670d\uff0c\u63a9\u906e\u96ea\u767d\u7684\u524d\u80f8\uff0c\u53cc\u76ee\u4e2d\u65e0\u9650\u7684\u60c5\u610f\uff0c\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u4e24\u884c\u60c5\u6cea\uff0c\u987a\u7740\u773c\u89d2\u6d41\u4e0b\u3002 \u3000\u3000\u6cea\u6c34\u6d47\u706d\u4e86\u4f55\u5bd2\u8863\u71c3\u8d77\u7684\u6b32\u706b\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u7f13\u7f13\u5728\u5e8a\u8fb9\u5750\u4e0b\uff0c\u9053\uff1a\u201c\u4f60\u54ed\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7f13\u7f13\u7741\u5f00\u773c\u775b\uff0c\u4e24\u9897\u83b9\u6676\u7684\u6cea\u6c34\uff0c\u593a\u7736\u800c\u51fa\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u6211\u4e3a\u4ec0\u4e48\u88ab\u79f0\u4f5c\u5929\u9b54\u5973\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u6211\u6740\u4e86\u5f88\u591a\u4eba\uff0c\u800c\u4e14\uff0c\u5927\u90fd\u662f\u7537\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u6068\u7537\u4eba\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5f53\u65f6\uff0c\u6211\u5b9e\u5728\u6068\u4ed6\u4eec\uff0c\u4f46\u6211\u6740\u8fc7\u4e86\u5f88\u591a\u4eba\u4e4b\u540e\uff0c\u6068\u610f\u6e10\u6e10\u7684\u6d88\u9000\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u516b\u5e74\u524d\uff0c\u6211\u8fd8\u662f\u4e00\u4e2a\u5341\u516d\u5c81\u7684\u5c0f\u5973\u5b69\uff0c\u4f46\u6211\u5df2\u7ec3\u6210\u4e00\u8eab\u5f88\u90aa\u5f02\u7684\u6b66\u529f\uff0c\u4e00\u4e2a\u4eba\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\uff0c\u5c31\u7b97\u6709\u4e00\u8eab\u7684\u6b66\u529f\uff0c\u4f46\u4e5f\u672a\u5fc5\u80fd\u4fdd\u62a4\u81ea\u5df1\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u6211\u9047\u4e0a\u4e86\u4e00\u4e2a\u7537\u4eba\uff0c\u4e00\u4e2a\u8868\u9762\u5f88\u548c\u5584\u7684\u7537\u4eba\uff0c \u6211\u5c11\u4e0d\u66f4\u4e8b\uff0c\u867d\u7136\u5f88\u987d\u76ae\uff0c\u4f46\u4e00\u70b9\u4e5f\u4e0d\u6210\u719f\uff0c\u4ed7\u7740\u4e00\u8eab\u7684\u6b66\u529f\uff0c\u5168\u65e0\u9632\u8303\u4e4b\u5fc3\uff0c\u90a3\u4e2a\u4eba\u548c\u6211\u540c\u884c\u4e86\u5341\u4f59\u65e5\uff0c\u4e00\u76f4\u4fdd\u6301\u7740\u541b\u5b50\u98ce\u5ea6\uff0c\u6211\u5bf9\u4ed6\u5168\u65e0\u9632\u8303\u4e86\uff0c\u6211\u4eec\u5e74\u9f84\u76f8\u5dee\u5f97\u5f88\u60ac\u6b8a\uff0c\u6211\u628a\u4ed6\u5f53\u4f5c\u957f\u8f88\u770b\u5f85\uff0c\u4f46\u4ed6\u7528\u9152\u704c\u9189\u4e86\u6211\uff0c\u90a3\u4e00\u591c\uff0c\u4ed6\u5360\u6709\u4e86\u6211\uff0c\u593a\u53bb\u4e86\u6211\u7684\u7ae5\u8d1e\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6697\u53eb\u4e86\u4e24\u58f0\u60ed\u6127\uff0c\u9053\uff1b\u201c\u4ee5\u540e\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4ee5\u540e\uff0c\u6211\u6e05\u9192\u4e86\uff0c\u51fa\u624b\u6740\u4e86\u4ed6\uff0c\u7531\u90a3\u65f6\u5f00\u59cb\uff0c \u6211\u4e5f\u7531\u4e00\u4f4d\u7eaf\u6d01\u7684\u5c11\u5973\uff0c\u53d8\u6210\u4e86\u5c0f\u5987\u4eba\u4e86\uff0c\u6211\u597d\u50cf\u5ffd\u7136\u95f4\u8001\u4e86\u5341\uff0c\u4e5f\u6210\u719f\u4e86\u5f88\u591a\uff0c\u6211\u5f00\u59cb\u653e\u6d6a\u5f62\u9ab8\uff0c\u4e5f\u5f00\u59cb\u968f\u4fbf\u6740\u4eba\uff0c\u5c31\u8fd9\u6837\uff0c\u5f97\u5230\u5929\u9b54\u5973\u8fd9\u4e2a\u5916\u53f7\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u600e\u4f1a\u548c\u9ad8\u5929\u5065\u6500\u4e0a\u4e86\u5173\u7cfb\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u663e\u7136\u662f\u5728\u7b79\u601d\u63aa\u8bcd\uff0c\u826f\u4e45\u4e4b\u540e\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u6211\u6740\u4e86\u4e0d\u5c11\u4eba\u4e4b\u540e\uff0c\u5185\u5fc3\u4e4b\u4e2d\u7684\u4ec7\u6068\uff0c\u4e5f\u6d88\u9000\u4e86\u5f88\u591a\uff0c\u518d\u52a0\u4e0a\u4e49\u7236\u7684\u58f0\u671b\uff0c\u4e00\u756a\u82e6\u53e3\u529d\u8bf4\uff0c\u7ec8\u4e8e\u4f7f\u6211\u6709\u4e86\u5f88\u5927\u7684\u6539\u53d8\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5929\u9b54\u5973\u7a81\u7136\u5728\u6c5f\u6e56\u4e0a\u9500\u58f0\u533f\u8ff9\u4e86\uff0c\u5c31\u662f\u56e0\u4e3a\u4f60\u8fdb\u5165\u4e86\u7b2c\u4e00\u5821?\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f!\u6211\u8fdb\u5165\u4e86\u7b2c\u4e00\u5821\uff0c\u8fd9\u65f6\u95f4\uff0c\u6211\u5df2\u7ecf\u6709\u4e86\u5f88\u5927\u7684\u6539\u53d8\uff0c\u6211\u5c3d\u91cf\u7684\u60f3\u6062\u590d\u6211\u4e2a\u4eba\u7684\u5c0a\u4e25\uff0c\u4f46\u5929\u9b54\u5973\u7559\u4e0b\u7684\u5370\u8c61\u592a\u6df1\u523b\u4e86\uff0c\u592a\u8f7b\u4f7b\uff0c\u6240\u4ee5\uff0c\u6211\u53ea\u597d\u53d8\u5f97\u51b7\u82e5\u51b0\u971c\uff0c\u4ee5\u4e25\u9177\u4ee3\u66ff\u4e86\u8f7b\u4f7b\uff0c\u5e0c\u671b\u628a\u7559\u5728\u4eba\u5fc3\u4e2d\u7684\u5370\u8c61\u626d\u8f6c\u8fc7\u6765\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u7b80\u5355\u7684\u8fc7\u53bb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u6068\u5f97\u592a\u6df1\uff0c\u4e5f\u96be\u602a\u4f60\u53d8\u5f97\u51b7\u5389\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u4f55\u90ce\uff0c\u6211\u7684\u6068\u610f\u6d88\u9000\u4e86\uff0c\u60f3\u4e0d\u5230\uff0c\u4f1a\u9047\u4e0a\u4f60\u3002\u201d \u4f55\u5bd2\u8863\u671b\u671b\u90a3\u6a2a\u69bb\u4e0a\u7684\u7389\u4f53\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u53c8\u60f9\u8d77\u4f60\u6f5c\u85cf\u5728\u5fc3\u4e2d\u7684\u6068\u610f?\u201d \u3000\u3000\u5434\u53cc\u5973\u6447\u6447\u5934\uff0c\u9053\uff1b\u201c\u4e0d\u77e5\u9053\uff0c\u4f60\u7adf\u4f7f\u6211\u681d\u4e95\u822c\u7684\u5fc3\u60c5\uff0c\u91cd\u8d77\u4e86\u6ce2\u6f9c\uff0c\u7184\u53bb\u7684\u706b\u7130\u91cd\u71c3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u5f88\u60ed\u6127\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u7528\u60ed\u6127\uff0c\u6211\u8bf4\u8fd9\u4e9b\u8bdd\u7684\u610f\u601d\uff0c\u53ea\u662f\u8981\u4f60\u4e86\u89e3\uff0c\u6211\u5df2\u975e\u5b8c\u74a7\uff0c\u6211\u8bf4\u7684\u4e5f\u90fd\u662f\u5f88\u771f\u5b9e\u7684\u7ecf\u8fc7\u60c5\u5f62\uff0c\u4f60\u73b0\u5728\u5982\u60f3\u6bc1\u53bb\u8fd9\u4e2a\u5a5a\u7ea6\uff0c\u8fd8\u6765\u5f97\u53ca\uff0c\u4e0d\u8fc7\uff0c\u4f55\u90ce\uff0c\u5c31\u7b97\u4f60\u6bc1\u4e86\u5a5a\u7ea6\uff0c\u6211\u8fd8\u5e2e\u52a9\u4f60\uff0c\u81f3\u5c11\u4e48\uff0c\u6211\u53ef\u4ee5\u4f7f\u4f60\u5b89\u5168\u7684\u79bb\u5f00\u6b64\u5730\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u662f\u6211\u4e0d\u613f\u610f\u6bc1\u53bb\u8fd9\u95e8\u5a5a\u7ea6\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u96be\u9053\uff0c\u4f60\u613f\u610f\u5a36\u4e00\u4e2a\u6b8b\u82b1\u8d25\u67f3\u7684\u5973\u4eba\uff0c\u4f5c\u4e3a\u59bb\u5b50?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u672c\u6765\uff0c\u6211\u5df2\u7ecf\u51b3\u5b9a\u4e86\uff0c\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u60f3\u53d8\u5366\u4e86\uff0c\u662f\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u73b0\u5728\uff0c\u66f4\u575a\u5b9a\u4e86\uff0c\u6211\u5e0c\u671b\uff0c \u4f60\u4e5f\u80fd\u628a\u8fc7\u53bb\u6446\u5f00\uff0c\u4e0d\u8981\u4f7f\u5b83\u6210\u4e3a\u4e00\u79cd\u538b\u529b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7a81\u7136\u7b11\u4e86\uff0c\u7b11\u5f97\u5f88\u5a07\u5a9a\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u90a3\u8981\u770b\u4f60\u4e86\uff0c\u80fd\u4e0d\u80fd\u89e3\u5f00\u6211\u5fc3\u4e2d\u8fd9\u4e2a\u7ed3?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u884c\uff0c\u73b0\u5728\uff0c\u6211\u8981\u8bc1\u660e\u6211\u7684\u8bda\u610f\u3002\u201d \u3000\u3000\u4f38\u624b\u62b1\u8fc7\u6765\u5434\u53cc\u5973\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u5434\u53cc\u5973\u672a\u518d\u53cd\u6297\uff0c\u800c\u4e14\uff0c\u5341\u5206\u987a\u4ece\u3002 \u3000\u3000\u5929\u5730\u4ea4\u6cf0\uff0c\u9634\u9633\u5bf9\u6d41\u3002 \u3000\u3000\u5434\u53cc\u5973\u5b8c\u5168\u5931\u53bb\u4e86\u82f1\u98ce\u3001\u8c6a\u6c14\u3001\u5a49\u8f6c\u547b\u541f\uff0c\u662f\u75db\u82e6\u3001\u662f\u6b22\u60a6\uff0c\u5f88\u96be\u5206\u8fa8\u3002 \u3000\u3000\u826f\u4e45\u4e4b\u540e\uff0c\u624d\u7a81\u7136\u9759\u6b62\u4e0b\u6765\u3002 \u3000\u3000\u5434\u53cc\u5973\u867d\u975e\u521d\u8bd5\u4e91\u60c5\uff0c\u4f46\u5979\u5fc3\u7406\u4e0a\uff0c\u5374\u662f\u7b2c\u4e00\u6b21\u63a5\u7eb3\u4e00\u4e2a\u7537\u4eba\u3002 \u3000\u3000\u9664\u4e86\u90a3\u4e00\u6b21\u9152\u540e\u88ab\u5f3a\u66b4\u4e4b\u5916\uff0c\u4ee5\u540e\uff0c\u653e\u8361\u7684\u5929\u9b54\u5973\u4e5f\u53ea\u662f\u4e00\u4e2a\u5916\u5f62\u3002 \u3000\u3000\u6240\u8c13\u63a5\u89e6\u8fc7\u5979\u7684\u7537\u4eba\uff0c\u4e5f\u53ea\u662f\u78b0\u78b0\u5979\u7684\u624b\u3001\u5979\u7684\u8eab\u8eaf\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u5bf9\u7537\u4eba\u5145\u6ee1\u7740\u75db\u6068\u3001\u538c\u6076\uff0c\u4e0d\u80af\u8ba9\u4efb\u4f55\u7537\u4eba\u771f\u78b0\u5230\u5979\u3002 \u3000\u3000\u4f46\u4f55\u5bd2\u8863\u5374\u8f7b\u6613\u7684\u53e9\u5f00\u4e86\u5979\u7684\u5fc3\u95e8\uff0c\u5fc3\u91cc\u63a5\u7eb3\u8fd9\u4e2a\u7537\u4eba\uff0c\u610f\u5916\u7684\u662f\uff0c\u51fa\u8eab\u6b63\u5927\u95e8\u6237\u7684\u4f55\u5bd2\u8863\uff0c\u7adf\u7136\u4e5f\u6562\u8131\u4e86\u5979\u7684\u8863\u670d\u3002 \u3000\u3000\u6625\u6f6e\u521d\u9000\uff0c\u7f9e\u610f\u72b9\u5b58\u3002 \u3000\u3000\u4e24\u4e2a\u6c5f\u6e56\u4e0a\u4e00\u6d41\u7684\u9ad8\u624b\uff0c\u4f46\u5374\u4e0d\u77e5\u9053\u5982\u4f55\u5584\u540e\u3002 \u3000\u3000\u60f3\u4e86\u4e00\u9635\uff0c\u5434\u53cc\u5973\u5ffd\u7136\u633a\u8eab\u800c\u8d77\uff0c\u7ad9\u8d77\u6765\uff0c\u624d\u53d1\u89c9\u81ea\u5df1\u8eab\u65e0\u5bf8\u7f15\uff0c\u6025\u6025\u7684\u6293\u8fc7\u4e86\u4e00\u4ef6\u8863\u670d\u62ab\u4e0a\uff0c\u7406\u4e00\u7406\u84ec\u4e71\u7684\u79c0\u53d1\u2026\u2026 \u3000\u3000\u5434\u53cc\u5973\u5ffd\u7136\u95f4\u53d8\u5f97\u65e0\u9650\u7684\u6e29\u67d4\uff0c\u6e29\u67d4\u5982\u6625\u6c34\u3002 \u3000\u3000\u5979\u62ff\u7740\u8c61\u7259\u68b3\u5b50\uff0c\u66ff\u4f55\u5bd2\u8863\u7406\u597d\u4e86\u4e71\u53d1\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u542c\u7740\uff0c\u4ece\u73b0\u5728\u5f00\u59cb\uff0c\u6211\u751f\u662f\u4f55\u5bb6\u4eba\uff0c\u6b7b\u662f\u4f55\u5bb6\u9b3c\uff0c\u4f60\u8981\u662f\u4e22\u4e86\u6211\uff0c\u6211\u53ea\u6709\u4e00\u6761\u8def\u53ef\u8d70\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u6211\u867d\u662f\u9b54\u5973\uff0c\u4f46\u4e5f\u4e0d\u5fcd\u5fc3\u6740\u5bb3\u81ea\u5df1\u7684\u4e08\u592b\uff0c\u4f46\u6211\u4f1a\u81ea\u7edd\u3002\u201d \u4f55\u5bd2\u8863\u7a81\u7136\u628a\u5434\u53cc\u5973\u63fd\u5165\u6000\u4e2d\uff0c\u9053\uff1a\u201c\u8c01\u8bf4\u4f60\u662f\u9b54\u5973\uff0c\u6211\u770b\u4f60\u662f\u4f4d\u4ed9\u5973\uff0c\u4ed9\u5973\u601d\u51e1\uff0c\u5c0f\u8c2a\u4eba\u95f4\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u628a\u8138\u504e\u5165\u4e86\u4f55\u5bd2\u8863\u7684\u80f8\u4e2d\uff0c\u9053\uff1a\u201c\u6211\u4f1a\u5c3d\u91cf\u505a\u4e2a\u597d\u59bb\u5b50\uff0c\u6211\u4e0d\u4f1a\u518d\u72af\u4e00\u70b9\u9519\u8bef\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u8fc7\u53bb\u7684\u522b\u518d\u60f3\u5b83\u4e86\uff0c\u4e5f\u522b\u518d\u63d0\u5b83\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8c22\u8c22\u4f60\uff0c\u4f55\u90ce\uff0c\u6211\u597d\u5feb\u4e50\uff0c\u597d\u5e78\u8fd0\uff0c\u4f60\u4f7f\u6211\u6062\u590d\u4e86\u81ea\u6211\uff0c\u4e5f\u4f7f\u6211\u62fe\u56de\u4e86\u6b22\u7b11\uff0c\u90a3\u4e00\u70b9\u9057\u61be\uff0c\u6211\u4f1a\u8865\u507f\u4f60\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8865\u507f\u6211\uff0c\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u544a\u8bc9\u4f60\uff0c\u5230\u65f6\u5019\uff0c\u6211\u81ea\u4f1a\u8bf4\u4e2a\u660e\u767d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53cc\u5973\uff0c\u522b\u60f3\u5f97\u592a\u591a\uff0c\u6211\u4f55\u67d0\u4eba\u8bf4\u51fa\u7684\u8bdd\uff0c\u53e5\u53e5\u51fa\u81ea\u80ba\u8151\uff0c\u6211\u4eec\u867d\u7136\u662f\u4e00\u89c1\u949f\u60c5\uff0c\u4f46\u8fd9\u60c5\u611f\u5f88\u771f\u5b9e\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u522b\u8bef\u4f1a\uff0c\u4f55\u90ce\uff0c\u6211\u77e5\u9053\u505a\u4e00\u4e2a\u597d\u59bb\u5b50\uff0c\u7b2c\u4e00\u4ef6\u91cd\u8981\u7684\u4e8b\uff0c\u5c31\u662f\u4e0d\u60f9\u4e08\u592b\u751f\u6c14\uff0c\u6709\u4e9b\u4e8b\uff0c\u65e0\u6cd5\u5148\u8bf4\u660e\u767d\uff0c\u8bf4\u660e\u767d\uff0c\u5c31\u4f1a\u51cf\u4f4e\u4e86\u5b83\u7684\u4ef7\u503c\uff0c\u800c\u4e14\uff0c\u65f6\u673a\u4e0d\u81f3\uff0c\u8bf4\u4e86\u4e5f\u662f\u767d\u8bf4\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u4e49\u7236\u662f\u4e2a\u5f88\u6df1\u6c89\u7684\u4eba\uff0c \u4f60\u5728\u5821\u4e2d\uff0c\u4ed6\u65e9\u77e5\u9053\u4e86\uff0c\u6211\u628a\u4f60\u5e26\u4e0a\u4e86\u5f69\u8679\u697c\uff0c\u4f46\u4ed6\u4e3a\u4f55\u6ca1\u6709\u53cd\u5e94?\u795e\u5200\u5802\u4e2d\uff0c\u5f88\u591a\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\uff0c\u54b1\u4eec\u4e0d\u80fd\u4e0d\u5c0f\u5fc3\u70b9\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u4e3a\u4e86\u6211\uff0c \u4f60\u4e0d\u60dc\u4e0e\u9ad8\u5929\u5065\u7ffb\u8138\u3002\u201d\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u5f53\u7136\u8981\u4fdd\u62a4\u81ea\u5df1\u7684\u4e08\u592b\uff0c \u53ea\u8981\u6211\u6709\u4e00\u53e3\u6c14\u5728\uff0c\u5c31\u4e0d\u5141\u8bb8\u4ed6\u4eec\u4f24\u5bb3\u5230\u4f60\u3002\u201d \u4f55\u5bd2\u8863\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u4ed8\u51fa\u7684\u4ee3\u4ef7\u4e0d\u662f\u592a\u5927\u4e86\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u592b\u59bb\u60c5\uff0c\u6d77\u6837\u6df1\uff0c\u751f\u540c\u7f57\u5e0f\uff0c\u6b7b\u540c\u7a74\uff0c\u8fd8\u8c08\u4ec0\u4e48\u4ee3\u4ef7\u5462\uff1f\u201d \u4f55\u5bd2\u8863\u9053\uff1a \u201c\u7537\u5b50\u6c49\uff0c\u5927\u4e08\u592b\uff0c\u6211\u4e0d\u80fd\u4fdd\u62a4\u4f60\uff0e\u8981\u4f60\u4fdd\u62a4\u6211\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u522b\u5fd8\u4e86\u6211\u662f\u4f60\u7684\u59bb\u5b50\uff0c\u59bb\u5b50\u548c\u4e08\u592b\uff0c\u8fd8\u6709\u4ec0\u4e48\u4f60\u6211\u4e4b\u5206\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d\u5427!\u4e00\u5207\u4e8b\uff0c\u90fd\u7531\u4f60\u6765\u51b3\u5b9a\uff0c\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d!\u6211\u4eec\u5546\u91cf\u7740\u529e\uff0c\u6211\u53ef\u4ee5\u51fa\u4e3b\u610f\uff0c\u4f46\u8981\u4f60\u6765\u51b3\u5b9a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u6211\u4eec\u5546\u91cf\u7740\u529e\u5427!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d!\u522b\u5fd8\u4e86\uff0c\u6211\u4f1a\u6c38\u8fdc\u542c\u4f60\u7684\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u60f3\u4e0d\u5230\u554a!\u505a\u4e86\u4e08\u592b\u4f1a\u8fd9\u4e48\u6709\u6743\u5a01\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u997f\u4e0d\u997f\uff0c\u6211\u53bb\u66ff\u4f60\u716e\u7897\u9762\u5403\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u53bb\u716e?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9!\u7b2c\u4e00\uff0c\u6211\u716e\u7684\u9762\u5f88\u597d\u5403\uff0c\u7b2c\u4e8c\uff0c\u6211\u4e5f\u4e0d\u60f3\u8981\u522b\u4eba\u53bb\u716e\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u522b\u5fd8\u4e86\uff0c\u54b1\u4eec\u8fd8\u8981\u53bb\u8d74\u7ea6\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\u7684\u9152\u91cf\u5f88\u597d\uff0c\u6240\u4ee5\uff0c\u54b1\u4eec\u8981\u5148\u5403\u70b9\u4e1c\u897f\uff0c\u7a7a\u8179\u559d\u9152\uff0c\u6700\u6613\u9189\u4eba\u3002\u201d \u3000\u3000\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u8bb0\u7740\uff0c\u9ad8\u5e7d\u5170\u656c\u4f60\u559d\u9152\u65f6\u8981\u770b\u6211\u7684\u773c\u8272\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u600e\u4e48?\u5979\u4f1a\u5728\u9152\u4e2d\u4e0b\u6bd2\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5f88\u96be\u8bf4\uff0c\u9ad8\u5e7d\u5170\u8fd9\u4e2a\u4eba\uff0c\u5f88\u6df1\u6c89\uff0c\u4e5f\u5f88\u96be\u6d4b\uff0c\u6211\u4eec\u4e0d\u80fd\u4e0d\u9632\u5907\u4e00\u4e0b\u3002\u201d \u90a3\u7897\u9762\u5b9e\u5728\u716e\u5f97\u4e0d\u9519\u3002 \u3000\u3000\u5434\u53cc\u5973\u9759\u9759\u5750\u5728\u4e00\u4fa7\uff0c\u8138\u4e0a\u6d0b\u6ea2\u7740\u4e00\u80a1\u5e78\u798f\u7684\u559c\u60a6\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5403\u5b8c\u4e86\u4ee5\u540e\uff0c\u653e\u4e0b\u7897\u7b77\u9053\uff1a \u201c\u60f3\u4e0d\u5230\uff0c\u4f60\u8fd8\u4f1a\u70f9\u996a\u4e4b\u672f\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u70e7\u5f97\u4e00\u624b\u597d\u83dc\uff0c\u4ee5\u540e\uff0c\u6211\u4f1a\u4e00\u6837\u4e00\u6837\u7684\u505a\u7ed9\u4f60\uff0c\u8d70\uff0c\u4e0a\u697c\u53bb\uff0c\u4f11\u606f\u4e00\u4f1a\uff0c\u6211\u8fd8\u8981\u548c\u4f60\u7814\u7a76\u4e24\u62db\u6b66\u529f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u6b66\u529f?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e24\u62db\u5251\u6cd5\uff0c\u867d\u4e0d\u5f88\u6b63\u5b97\uff0c\u4f46\u5374\u5f88\u5b9e\u7528\u3002\u201d \u3000\u3000\u5979\u8868\u73b0\u6df1\u6df1\u7684\u60c5\u610f\uff0c\u8c26\u865a\u7684\u7f8e\u5fb7\uff0c\u867d\u7136\u662f\u660e\u660e\u8981\u4f20\u4f55\u5bd2\u8863\u4e24\u62db\u5251\u6cd5\uff0c\u4f46\u5979\u5374\u8bf4\u7684\u90a3\u4e48\u59d4\u5a49\u3002 \u3000\u3000\u82b1\u53bb\u4e86\u8fd1\u4e00\u4e2a\u65f6\u8fb0\u7684\u5de5\u592b\uff0c\u5434\u53cc\u5973\u624d\u548c\u4f55\u5bd2\u8863\u7814\u7a76\u597d\u4e86\u4e24\u62db\u5251\u6cd5\u3002 \u3000\u3000\u5929\u8272\u5df2\u5feb\u5230\u4e86\u638c\u706f\u7684\u65f6\u8fb0\u3002 \u3000\u3000\u5434\u53cc\u5973\u5148\u66ff\u4f55\u5bd2\u8863\u6574\u7406\u597d\u4e86\u8863\u670d\uff0c\u624d\u6574\u7406\u4e00\u4e0b\u4e71\u53d1\u6a2a\u9497\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8be5\u8d70\u4e86\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u4e0b\u4e86\u5f69\u8679\u697c\uff0c\u96ea\u6885\u65e9\u5df2\u5728\u95e8\u53e3\u5904\u7b49\u3002 \u3000\u3000\u8bc6\u76f8\u7684\u4e2b\u5934\uff0c\u5b88\u5728\u95e8\u53e3\u5904\uff0c\u4e00\u76f4\u6ca1\u6709\u767b\u4e0a\u5f69\u8679\u697c\u3002 \u3000\u3000\u96ea\u6885\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u89c1\u8fc7\u5927\u5c0f\u59d0\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u96ea\u6885\uff0c\u4f60\u5b9e\u5728\u5f88\u806a\u660e\uff0c\u52ff\u602a\u5e7d\u5170\u90a3\u4e48\u559c\u6b22\u4f60\u3002\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u5a62\u5b50\u53ea\u662f\u5728\u5c3d\u804c\u800c\u5df2\uff0c\u600e\u6562\u5f53\u5927\u5c0f\u59d0\u7684\u5938\u5956\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u96ea\u6885\uff0c\u6211\u60f3\u5411\u5e7d\u5170\u628a\u4f60\u8981\u8fc7\u6765\uff0c\u4f60\u540c\u4e0d\u540c\u610f?\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u5a62\u5b50\u8ddf\u5927\u5c0f\u59d0\u548c\u4e8c\u5c0f\u59d0\uff0c\u90fd\u662f\u4e00\u6837\uff0c\u5927\u5c0f\u59d0\u5982\u80af\u63d0\u643a\uff0c\u5a62\u4e8e\u662f\u611f\u6fc0\u4e0d\u5c3d\u7684\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u72e1\u733e\u7684\u5c0f\u4e2b\u5934\uff0c\u7ed9\u6211\u5e26\u8def\u5427!\u201d \u3000\u3000\u96ea\u6885\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\u8bf4\uff0c\u60f3\u6539\u53d8\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u600e\u4e48?\u4e0d\u60f3\u8bf7\u4e86?\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\u8bf4\uff0c\u8fd9\u51e0\u5929\u5821\u4e2d\u4e8b\u52a1\u591a\uff0c\u6050\u6015\u6709\u4ec0\u4e48\u7a81\u53d1\u4e8b\u4ef6\uff0c\u6270\u4e86\u4f60\u5927\u5c0f\u59d0\u7684\u96c5\u5174\uff0c\u6240\u4ee5\uff0c\u60f3\u6362\u4e2a\u5730\u65b9\uff0c\u4e0d\u77e5\u5927\u5c0f\u59d0\u662f\u5426\u540c\u610f?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6362\u5728\u4ec0\u4e48\u5730\u65b9?\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u54b1\u4eec\u7b2c\u4e00\u5821\u4e2d\u6700\u5e7d\u9759\u7684\u5730\u65b9\uff0c\u5c31\u662f\u8377\u82b1\u6c60\uff0c\u6240\u4ee5\uff0c\u4e8c\u5c0f\u59d0\u60f3\u8bf7\u5927\u5c0f\u59d0\u548c\u4f55\u516c\u5b50\uff0c\u5230\u8377\u82b1\u6c60\u671b\u6708\u4ead\u4e2d\u4e00\u53d9\uff0c\u4e0d\u77e5\u5927\u5c0f\u59d0\u610f\u4e0b\u5982\u4f55?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u597d\u5427!\u4f60\u5148\u56de\u53bb\u7980\u62a5\u4e8c\u5c0f\u59d0\uff0c\u6211\u4eec\u76f4\u63a5\u5230\u671b\u6708\u4ead\u53bb\u3002\u201d \u3000\u3000\u96ea\u6885\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u5a62\u5b50\u9075\u547d\u3002\u201d \u8f6c\u8eab\u6025\u5954\u800c\u53bb\u3002 \u5434\u53cc\u5973\u56de\u987e\u4e86\u4f55\u5bd2\u8863\u4e00\u773c\uff0c\u9053\uff1a\u201c\u90fd\u542c\u5230\u4e0b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5fc5\u5230\u671b\u6708\u4ead\u53bb\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u91cc\u5f88\u5e7d\u9759\uff0c\u56db\u9762\u8377\u82b1\uff0c\u671b\u6708\u4ead\u5c31\u5728\u8377\u82b1\u6c60\u4e2d\u95f4\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u662f\u4e0d\u662f\u9ad8\u5e7d\u5170\u522b\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d!\u5979\u662f\u5728\u8868\u767d\u81ea\u5df1\uff0c\u8bc1\u660e\u5979\u6ca1\u6709\u522b\u7684\u6253\u7b97\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6!\u201d\u5434\u53cc\u5973\u9053\uff1a\u201c\u5979\u4f4f\u7684\u5730\u65b9\u5f88\u5927\uff0c\u5230\u5904\u90fd\u53ef\u4ee5\u8bbe\u4e0b\u57cb\u4f0f\uff0c\u4f46\u671b\u6708\u4ead\u4e0d\u884c\uff0c\u56e0\u4e3a\u671b\u6708\u4ead\u56db\u9762\u7a7a\u65f7\uff0c\u4e0d\u8bba\u4ec0\u4e48\u57cb\u4f0f\uff0c \u4e00\u773c\u5c31\u53ef\u4ee5\u770b\u7a7f\u4e86\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u8377\u82b1\u6c60\u76f8\u5f53\u5927\uff0c\u4f55\u5bd2\u8863\u7ea6\u7565\u4f30\u8ba1\u4e00\u4e0b\uff0c\u5360\u5730\u81f3\u5c11\u5728\u767e\u4ea9\u4ee5\u4e0a\uff0c\u800c\u4e14\uff0c\u76f8\u5f53\u6df1\u3002 \u3000\u3000\u6c34\u6ce2\u8361\u6f3e\uff0c\u770b\u4e0a\u53bb\uff0c\u5c31\u50cf\u4e00\u5ea7\u5929\u7136\u7684\u5927\u6e56\u3002 \u3000\u3000\u8377\u6c60\u56db\u9762\uff0c\u82b1\u7566\u6210\u884c\uff0c\u5206\u690d\u4e86\u5341\u51e0\u79cd\u4e0d\u540c\u7684\u82b1\u6811\u3002 \u3000\u3000\u4e00\u5ea7\u4e5d\u66f2\u6731\u6865\uff0c\u7531\u5047\u5c71\u4ead\u76f8\u901a\u8377\u82b1\u6c60\u4e2d\u7684\u671b\u6708\u4ead\u4e0a\u3002 \u3000\u3000\u671b\u6708\u4ead\uff0c\u73b0\u5728\uff0c\u70b9\u71c3\u4e86\u4e00\u7247\u706f\u706b\u3002 \u3000\u3000\u7167\u4eae\u4e86\u671b\u6708\u4ead\uff0c\u4e5f\u7167\u4eae\u4e86\u56db\u9762\u8377\u6c60\u3002 \u3000\u3000\u4e5d\u66f2\u6731\u6865\u4e0a\uff0c\u7ad9\u7740\u4e2a\u767d\u8863\u4e3d\u4eba\uff0c\u957f\u53d1\u4efb\u665a\u98ce\u98d8\u52a8\uff0c\u83b2\u6b65\u8f7b\u76c8\u7684\u8fce\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u662f\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u5979\u4f3c\u662f\u7ecf\u8fc7\u4e86\u4e00\u756a\u523b\u610f\u7684\u4fee\u9970\uff0c\u767d\u8863\u5982\u96ea\uff0c\u7c89\u988a\u6cdb\u7ea2\uff0c\u6e05\u9038\u4e2d\u5e26\u7740\u4e00\u80a1\u98d8\u9038\u7684\u6c14\u8d28\u3002 \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u597d\u6f02\u4eae\uff0c\u5973\u4e3a\u60a6\u5df1\u8005\u5bb9\uff0c\u4eca\u665a\u4e0a\u6253\u626e\u5f97\u8fd9\u4e48\u597d\u770b\uff0c\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e3a\u4e86\u5927\u59ca\u554a\uff01\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u59d0\u59d0\u4e0d\u4fe1?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54b1\u4eec\u76f8\u8bc6\u6709\u5e74\u4e86\uff0c\u4f60\u51e0\u65f6\u4e3a\u6211\u5982\u6b64\u4fee\u9970\u8fc7?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u6709\u4e86\u4e00\u4e2a\u5a07\u8273\u5982\u82b1\u7684\u59d0\u59d0\uff0c\u6211\u8fd9\u505a\u59b9\u59b9\u7684\uff0c\u603b\u4e0d\u80fd\u592a\u96be\u770b\uff0c\u867d\u7136\u4e3d\u8d28\u8981\u5929\u751f\uff0c\u4f46\u6253\u626e\u4e5f\u6709\u4e09\u5206\u7528\uff0c\u5728\u59d0\u59d0\u7684\u8d35\u5bbe\u9762\u524d\uff0c\u5c0f\u59b9\u603b\u4e0d\u80fd\u4e71\u53d1\u6a2a\u9497\uff0c\u5931\u4e86\u59d0\u59d0\u7684\u9762\u5b50\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u597d\u4e00\u4e2a\u4e3a\u6211\u800c\u5bb9\uff0c\u77a7\u77a7\u4f60\u8fd9\u5f20\u5634\u91cc\u6709\u591a\u5c11\u871c\u7cd6\u3002\u201d \u9ad8\u5e7d\u5170\u7b11\u9053\uff1a \u201c\u5927\u59d0\uff0c\u4ead\u4e2d\u5750\u5427!\u5c0f\u59b9\u5df2\u5907\u597d\u4e86\u51e0\u6837\u5c0f\u83dc\uff0c\u4e00\u58f6\u7f8e\u9152\uff0c\u54b1\u4eec\u4e00\u9762\u559d\u9152\u3001\u4e00\u9762\u8c08\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\uff0c\u4e3e\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f55\u516c\u5b50\uff0c\u6211\u548c\u5927\u59d0\uff0c\u867d\u975e\u4e00\u6bcd\u540c\u80de\uff0c\u4f46\u5374\u60c5\u540c\u9aa8\u8089\uff0c\u6211\u5e94\u8be5\u53eb\u4f60\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u968f\u4fbf\u53eb\u4ec0\u4e48\u90fd\u53ef\u4ee5\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u6ca1\u89c4\u77e9\u4e0d\u6210\u65b9\u5706\uff0c\u5e94\u8be5\u53eb\u4ec0\u4e48?\u603b\u5f97\u53eb\u554a!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u7a81\u7136\u56de\u5934\uff0c\u9053\uff1a\u201c\u53eb\u59d0\u592b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u554a!\u8fd8\u662f\u59d0\u59d0\u5927\u65b9\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5170\u59b9\uff0c\u4eca\u591c\u4e4b\u4f1a\uff0c \u4f60\u4e00\u76f4\u4e0d\u505c\u7ed9\u59d0\u59d0\u6211\u6234\u9ad8\u5e3d\u5b50\uff0c\u60f3\u6765\uff0c\u5b9a\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u6211\u5e2e\u5fd9\u4e86?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\u8bf4\u7684\u54ea\u91cc\u7684\u8bdd\uff0c\u5c0f\u59b9\u4e00\u5411\u5bf9\u5927\u59ca\u4e0d\u9519\u554a!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u5012\u4e5f\u662f\uff0c\u54b1\u4eec\u59ca\u59b9\u4e24\u4e2a\uff0c\u4e00\u5411\u662f\u60c5\u6295\u610f\u5408\u3002\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u5df2\u8fdb\u5165\u4e86\u671b\u6708\u4ead\u3002 \u3000\u3000\u4ead\u4e2d\u7684\u4e00\u5f20\u767d\u7389\u4f5c\u6210\u7684\u684c\u5b50\u4e0a\uff0c\u65e9\u5df2\u6392\u597d\u4e86\u516d\u76d8\u83dc\u3001\u4e00\u58f6\u9152\uff0c\u96ea\u6885\u4f38\u51fa\u7ea4\u7ea4\u7684\u7389\u624b\uff0c\u633d\u8d77\u4e86\u9152\u58f6\uff0c\u659f\u6ee1\u4e86\u9152\u676f\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5c0f\u59b9\u5148\u5411\u5927\u59ca\u606d\u559c\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5170\u59b9\uff0c\u6211\u60f3\u77e5\u9053\u5b9e\u5728\u7684\u60c5\u5f62\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5c0f\u59b9\u672c\u8eab\uff0c\u786e\u7136\u5982\u6b64\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u8fd8\u6709\u522b\u4eba\u7684\u610f\u601d?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u5821\u4e3b?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e49\u7236\u7684\u770b\u6cd5\uff0c\u6700\u4e3a\u91cd\u8981\uff0c\u4f46\u9664\u4e86\u4e49\u7236\u4e4b\u5916\uff0c\u8fd8\u6709\u51e0\u4f4d\u5802\u4e3b\u7684\u610f\u89c1\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u51e0\u4f4d\u5802\u4e3b\u7684\u610f\u4e0b\u5982\u4f55\uff0c\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u7239\u548c\u6211\u89c1\u8fc7\u9762\u4e86\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u624d\u662f\u6b63\u9898\uff0c\u4e8c\u59b9\uff0c\u8bf4\u5427!\u7239\u6709\u4ec0\u4e48\u770b\u6cd5?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\u7684\u610f\u601d\uff0c\u662f\u60f3\u77e5\u9053\u4f60\u7684\u76ee\u7684\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54e6!\u90a3\u662f\u8bf4\uff0c\u4e49\u7236\u4ed6\u8001\u4eba\u5bb6\uff0c\u4e0d\u592a\u8d5e\u6210\u8fd9\u4ef6\u4e8b\u4e86?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u60f3\uff0c\u7239\u4e0d\u662f\u4e0d\u592a\u8d5e\u6210\u8fd9\u4ef6\u4e8b\uff0c\u800c\u662f\u5e0c\u671b\u4e86\u89e3\u59ca\u59ca\u7684\u5fc3\u613f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e00\u5b9a\u8981\u6211\u8bf4\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\uff0c\u5e73\u5fc3\u800c\u8bba\uff0c\u7239\u5bf9\u4f60\u771f\u7684\u4e0d\u9519\uff0c\u6bcf\u4e00\u4ef6\u4e8b\u5927\u90fd\u4f1a\u5f88\u4f9d\u4ece\u4f60\uff0c\u96be\u9053\uff0c\u4f60\u5fcd\u5fc3\u4f24\u5bb3\u4ed6\u5417?\u201d \u3000\u3000\u5979\u8bed\u610f\u5a49\u8f6c\u5145\u6ee1\u7740\u611f\u60c5\uff0c\u5434\u53cc\u5973\u6123\u4f4f\u4e86\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e49\u7236\u6ca1\u6709\u544a\u8bc9\u4f60\uff0c\u4ed6\u7684\u770b\u6cd5\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\u5bf9\u8fd9\u4ef6\u4e8b\uff0c\u6ca1\u6709\u8bf4\u51fa\u4ed6\u7684\u770b\u6cd5\uff0c\u4e0d\u8fc7\uff0c\u5374\u8bf4\u51fa\u4e86\u4ed6\u7684\u5fc3\u613f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u5fc3\u613f?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\u4f1a\u5c3d\u91cf\u9075\u7167\u4f60\u7684\u610f\u89c1\uff0c\u5982\u82e5\u59ca\u59ca\u613f\u610f\u548c\u59ca\u592b\u8fd1\u65e5\u4e2d\u4e3e\u884c\u5927\u793c\uff0c\u7239\u4e5f\u613f\u610f\u51fa\u9762\u4e3a\u4f60\u4e3b\u6301\u5a5a\u793c\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u56de\u987e\u4e86\u96ea\u6885\u4e00\u773c\uff0c\u9053\uff1a\u201c\u53bb\u5b88\u5728\u4ead\u5b50\u5916\u9762\uff0c\u4efb\u4f55\u4eba\u90fd\u4e0d\u8bb8\u8fdb\u5165\u671b\u6708\u4ead\u4e2d\u3002\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u5982\u82e5\u6765\u4eba\u662f\u5821\u4e3b\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u90a3\u5c31\u60f3\u6cd5\u5b50\u901a\u77e5\u6211\u4eec\u4e00\u58f0\u3002\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u5a62\u5b50\u9075\u547d\u3002\u201d \u3000\u3000\u8f6c\u8eab\u6025\u6b65\u800c\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4e3e\u8d77\u9152\u676f\uff0c\u9053\uff1a\u201c\u4f55\u5144\uff0c\u6211\u73b0\u5728\u8be5\u53eb\u4f60\u4e00\u58f0\u59d0\u592b\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53cc\u988a\u500f\u5730\u80c0\u7ea2\uff0c\u4e3e\u676f\u8bf4\u9053\uff1a\u201c\u5170\u59d1\u5a18\u6709\u4ec0\u4e48\u5429\u5490?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u59d0\u592b\uff0c\u5c0f\u59b9\u656c\u4f60\u4e00\u676f\u9152\u3002\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u6897\u4f4f\u4e86\uff0c\u4f55\u5bd2\u8863\u672c\u6765\u8bf4\u597d\u7684\uff0c\u8981\u770b\u5434\u53cc\u5973\u8138\u8272\u884c\u4e8b\uff0c\u4f46\u73b0\u5728\uff0e\u4ed6\u4f3c\u4e4e\u662f\u6ca1\u6709\u673a\u4f1a\u4e86\uff0c\u53ea\u597d\u7aef\u8d77\u9152\u676f\uff0c\u4e00\u996e\u800c\u5c3d\uff0c \u3000\u3000\u9ad8\u5e7d\u5170\u4e5f\u5e72\u4e86\u676f\u3002 \u3000\u3000\u5982\u82e5\u8fd9\u9152\u4e2d\u6709\u6bd2\uff0c\u90a3\u4e00\u58f0\u59ca\u592b\uff0c\u5c31\u662f\u4ed6\u50ac\u547d\u7684\u65e0\u5e38\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u54b1\u4eec\u59ca\u59b9\u8fd9\u4e9b\u5e74\u6765\uff0c\u5b9e\u5728\u76f8\u5904\u5f97\u5f88\u597d\uff0c\u4f60\u5982\u6709\u4ec0\u4e48\u4e8b\uff0c\u8bf7\u53ea\u7ba1\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u8b6c\u5982\u8bf4\uff0c\u4f60\u5bf9\u59ca\u59ca\u548c\u4f55\u5bd2\u8863\u8fd9\u4ef6\u4e8b\uff0c\u6709\u4ec0\u4e48\u4e0d\u6ee1\u610f\uff0c\u4e0d\u59a8\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u59ca\u59ca\uff0c\u4f60\u548c\u4f55\u5144\u60f3\u6765\u662f\u65e9\u5c31\u8ba4\u8bc6\u7684\uff0c\u4ee5\u59ca\u59ca\u7684\u660e\u667a\uff0c\u6211\u76f8\u4fe1\u4e0d\u4f1a\u5728\u77ed\u77ed\u7684\u4e00\u5929\u5185\uff0c\u51b3\u5b9a\u4e86\u81ea\u5df1\u7684\u7ec8\u8eab\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5170\u59b9\uff0c\u8fd9\u4ef6\u4e8b\u7684\u524d\u56e0\u540e\u679c\uff0c\u4f60\u5c31\u4e0d\u521a\u591a\u95ee\u4e86\uff0c\u6211\u60f3\u77e5\u9053\u7684\u662f\uff0c\u5170\u59b9\u5bf9\u8fd9\u4ef6\u4e8b\u7684\u770b\u6cd5\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\u8bf4\uff0c\u4f60\u5982\u4e3e\u884c\u5a5a\u793c\uff0c\u4ed6\u8981\u5927\u5f20\u65d7\u9f13\uff0c\u597d\u597d\u7684\u70ed\u95f9\u4e00\u4e0b\uff0c\u5e7f\u9080\u5929\u4e0b\u82f1\u96c4\uff0c\u9020\u6210\u4e00\u573a\u76db\u4f1a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e49\u7236\u8fd9\u6837\u5bf9\u6211\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u8bf4\u5b9e\u5728\u7684\uff0c\u5927\u59ca\uff0c\u7239\u5bf9\u4f60\uff0c\u6bd4\u5bf9\u6211\u66f4\u597d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7684\u795e\u60c5\u6709\u4e9b\u6728\u7136\uff0c\u9ad8\u5929\u5065\u548c\u9ad8\u5e7d\u5170\u8fd9\u7b49\u52a8\u4e4b\u4ee5\u60c5\u7684\u4f5c\u6cd5\uff0c\u4f7f\u5979\u5fc3\u4e2d\u6709\u4e9b\u96be\u8fc7\uff0c\u4e5f\u6709\u4e9b\u4e0d\u5b89\u3002 \u3000\u3000\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c \u4f60\u59ca\u592b\u662f\u9634\u9633\u5821\u7684\u4eba?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u77e5\u9053\uff0c\u7239\u4e5f\u77e5\u9053\uff0c\u4e0d\u8fc7\uff0c \u7239\u8bf4\uff0c\u4eba\u975e\u5723\u8d24\uff0c\u5b70\u80fd\u65e0\u8fc7\uff0c\u53ea\u8981\u59ca\u592b\u80af\u8131\u79bb\u9634\u9633\u5821\uff0c\u7b2c\u4e00\u5821\u7ed9\u4ed6\u7684\u804c\u4f4d\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u4f4e\u8fc7\u9634\u9633\u5821\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u76ee\u6ce8\u4f55\u5bd2\u8863\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5bd2\u8863\uff0c \u4f60\u90fd\u542c\u5230\u4e86?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\u9053\uff1a\u201c\u90fd\u542c\u5230\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u6709\u4ec0\u4e48\u770b\u6cd5?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u59d0\u592b\uff0c\u5927\u80c6\u7684\u8bf4\uff0c\u6211\u548c\u5927\u59ca\uff0c\u65e0\u8bdd\u4e0d\u8c08\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u59d1\u5a18\u4e00\u5b9a\u8981\u6211\u8bf4\uff0c\u5728\u4e0b\u5c31\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5c0f\u59b9\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u53ef\u80fd\u8131\u79bb\u9634\u9633\u5821\u3002\u201d \u3000\u3000\u50cf\u5de8\u77f3\u6295\u5165\u6c34\u6f6d\u4e00\u822c\uff0c\u4f7f\u5f97\u539f\u672c\u5f88\u548c\u6c14\u7684\u573a\u9762\uff0c\u4e00\u4e0b\u5b50\u50f5\u4f4f\u4e86\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u8138\u4e0a\u7684\u7b11\u5bb9\uff0c\u5ffd\u7136\u95f4\u6d88\u5931\u4e86\u3002 \u3000\u3000\u5434\u53cc\u5973\u7684\u8138\u8272\uff0c\u4e5f\u4e25\u8083\u8d77\u6765\u3002 \u3000\u3000\u5e26\u7740\u4e09\u5206\u7f9e\u602f\u7684\u4f55\u5bd2\u8863\uff0c\u5374\u6253\u5f00\u8bdd\u5323\u5b50\uff0c\u4f83\u4f83\u800c\u8c08\u3002\u9053\uff1a\u201c\u6211\u4e0d\u613f\u6b3a\u9a97\u9ad8\u59d1\u5a18\uff0c\u6211\u8bf4\u7684\u8bdd\u90fd\u662f\u5fc3\u4e2d\u7684\u8bdd\uff0c\u6211\u65e0\u6cd5\u6446\u8131\u9634\u9633\u5821\uff0c\u5e76\u4e0d\u662f\u56e0\u4e3a\u9634\u9633\u5821\u6709\u4ec0\u4e48\u529b\u91cf\u7ea6\u675f\u6211\uff0c\u800c\u662f\uff0c\u6211\u5bf9\u662f\u975e\u6807\u51c6\u770b\u6cd5\uff0c\u6709\u81ea\u5df1\u7684\u610f\u89c1\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u59ca\u592b\u7684\u610f\u601d\uff0c\u662f\u8bf4\u9634\u9633\u5821\u662f\u4e00\u4e2a\u597d\u7684\u7ec4\u7ec7?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\u7684\u770b\u6cd5\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5b83\u5f88\u4e0d\u597d\uff0c\u5728\u6c5f\u6e56\u4e0a\uff0c\u662f\u4e00\u4e2a\u5f88\u574f\u7684\u7ec4\u7ec7\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u662f\u59d1\u5a18\u7684\u770b\u6cd5\uff0c\u5728\u4e0b\u7684\u770b\u6cd5\u5c31\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u59d0\u592b\u7684\u770b\u6cd5\u662f\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u9634\u9633\u5821\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u8a89\uff0c\u867d\u7136\u4e0d\u592a\u597d\uff0c\u4f46\u6240\u4f5c\u6240\u4e3a\uff0c\u5374\u90fd\u662f\u4ef0\u4e0d\u6127\u5929\u3001\u4fef\u4e0d\u600d\u5730\u7684\u4e8b\uff0c\u6240\u4ee5\uff0c\u6211\u5fc3\u4e2d\u5f88\u5e73\u5b89\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u59ca\u592b\uff0c\u81f3\u5c11\uff0c\u6211\u4eec\u7b2c\u4e00\u5821\u7684\u58f0\u8a89\uff0c\u6bd4\u9634\u9633\u5821\u597d\u4e00\u70b9\u5427?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u8a89\uff0c\u7b2c\u4e00\u5821\u786e\u7136\u9ad8\u8fc7\u9634\u9633\u5821\u5f88\u591a\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5b9e\u9645\u60c5\u5f62\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5b9e\u9645\u60c5\u5f62\uff0c\u5728\u4e0b\u4e0d\u4fbf\u8bf4\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u5c3d\u7ba1\u76f4\u8bf4\u5427!\u4e0d\u8981\u7d27\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u59d1\u5a18\u5bf9\u8d35\u5821\u4e2d\u4e4b\u4e8b\uff0c\u96be\u9053\u4e00\u70b9\u90fd\u4e0d\u77e5\u9053\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u81ea\u7136\u77e5\u9053\uff0c\u4e0d\u8fc7\uff0c\u6211\u77e5\u9053\u7684\u662f\u5185\u4e2d\u60c5\u5f62\uff0c\u5bf9\u6c5f\u6e56\u4e0a\u7684\u4f20\u8bf4\uff0c\u6211\u5374\u662f\u4e0d\u592a\u4e86\u89e3\uff0c\u9664\u4e86\u6211\uff0c\u8fd8\u6709\u5927\u59ca\uff0c\u6ca1\u6709\u5916\u4eba\uff0c\u5c31\u7b97\u4f60\u542c\u5230\u4e86\u5bf9\u7b2c\u4e00\u5821\uff0c\u6709\u4ec0\u4e48\u4e0d\u597d\u7684\u98ce\u8bc4\uff0c\u4e5f\u4e0d\u59a8\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u56de\u987e\u4e86\u5434\u53cc\u5973\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u5e94\u4e0d\u5e94\u8be5\u636e\u5b9e\u800c\u8a00\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u59ca\u59ca\uff0c\u8ba9\u4ed6\u8bf4\u5427!\u8bf4\u51fa\u4ed6\u5fc3\u4e2d\u6240\u6709\u7684\u8bdd\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4ed6\u5982\u679c\u8bf4\u9519\u4e86\uff0c\u4f60\u771f\u4e0d\u602a\u4ed6\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u600e\u6562\u5462?\u4ed6\u662f\u59ca\u592b\u554a\uff01\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u597d!\u5bd2\u8863\uff0c\u4f60\u8bf4\u5427\uff0c\u6211\u77e5\u9053\u5170\u59b9\u7684\u6027\u683c\uff0c\u4e00\u8bfa\u5343\u91d1\uff0c\u5c31\u7b97\u4f60\u8bf4\u9519\u4e86\uff0c\u4e5f\u4e0d\u4f1a\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u5c3d\u7ba1\u5434\u53cc\u5973\u4e5f\u5728\u50ac\u4fc3\uff0c\u4f46\u6709\u5173\u7b2c\u4e00\u5821\u4e2d\u7684\u4e8b\uff0c\u603b\u662f\u6709\u4e9b\u788d\u96be\u8bf4\u660e\u4e4b\u5904\uff0c\u88ab\u5f62\u52bf\u4e00\u903c\uff0c\u60f3\u8d77\u4e86\u5723\u6c34\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a \u201c\u4e24\u4f4d\u53ef\u542c\u8bf4\u8fc7\u5723\u6c34\u4e4b\u4e8b?\u201d \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u9894\u9996\uff0c\u9ad8\u5e7d\u5170\u5374\u63a5\u9053\uff1a \u201c\u77e5\u9053\uff0c\u4e3a\u8fd9\u4ef6\u4e8b\uff0c\u6c5f\u6e56\u4e0a\u6709\u51e0\u4e2a\u7ec4\u7ec7\uff0c\u5bf9\u5bb6\u7236\u90fd\u5f88\u4e0d\u6ee1\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6697\u9053\uff1a \u201c\u597d\u5389\u5bb3\u7684\u4e2b\u5934\uff0c\u679c\u7136\u662f\u6076\u4eba\u5148\u544a\u72b6\uff0c\u5012\u6253\u4e86\u4e00\u9489\u8019\u3002\u201d \u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a \u201c\u4ee4\u5c0a\u6536\u96c6\u4e86\u5f88\u591a\u5723\u6c34\uff0c\u4e0d\u77e5\u59d1\u5a18\u662f\u5426\u77e5\u6653?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6536\u96c6\u4e86\u5f88\u591a\u5723\u6c34?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5bf9!\u53ef\u4ee5\u8bf4\uff0c\u6574\u4e2a\u5723\u6c34\u7684\u4ea4\u6613\uff0c\u5927\u90e8\u63a7\u5236\u5728\u7b2c\u4e00\u5821\u4e2d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u4e0d\u8fc7\uff0c\u5728\u4e0b\u81ea\u77e5\u5bf9\u5723\u6c34\u7684\u8ba4\u8bc6\u4e0d\u591f\uff0c\u6240\u4ee5\uff0c\u8fd8\u65e0\u6cd5\u4e86\u89e3\u5723\u6c34\u7684\u7528\u9014\u4e3a\u4f55?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u59d0\u592b\uff0c\u6211\u60f3\u8bf4\u660e\u4e00\u4ef6\u4e8b\uff0c\u90a3\u5c31\u662f\u7b2c\u4e00\u5821\uff0c\u7edd\u5bf9\u6ca1\u6709\u50a8\u85cf\u5723\u6c34\u7684\u5730\u65b9\uff0c\u8001\u5b9e\u8bf4\uff0c\u5bf9\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4e5f\u4e86\u89e3\u7684\u4e0d\u6df1\uff0c\u59d0\u592b\u8bf4\uff0c\u7b2c\u4e00\u5821\u76f4\u63a5\u5e72\u6d89\u5723\u6c34\u7684\u4ea4\u6613\uff0c\u4e0d\u77e5\u7528\u7684\u662f\u4ec0\u4e48\u65b9\u6cd5?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u7b2c\u4e00\u5821\u7684\u52bf\u529b\uff0c \u5df2\u7ecf\u6df1\u5165\u6c5f\u6e56\u5404\u5904\uff0c\u9664\u4e86\u56db\u5927\u9556\u5c40\u4e4b\u5916\uff0c\u8fd8\u6709\u51e0\u4e2a\u5b9e\u529b\u5f88\u5927\u7684\u7ec4\u7ec7\uff0c\u4e5f\u5728\u7b2c\u4e00\u5821\u7684\u63a7\u5236\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u7239\u548c\u5927\u59ca\u53ca\u6211\uff0c \u4ee5\u8c08\u5230\u8fc7\u6c5f\u6e56\u4e0a\u7684\u4e8b\u60c5\uff0c\u4ed6\u66fe\u7ecf\u8bb2\u8fc7\u5982\u771f\u60f3\u4f7f\u6c5f\u6e56\u4e0a\u5e73\u5b9a\u4e0b\u6765\uff0c\u5355\u7eaf\u7684\u53ea\u6740\u51e0\u4e2a\u6076\u4eba\uff0c\u90a3\u6536\u6548\u5e76\u4e0d\u592a\u5927\uff0c\u5fc5\u987b\u628a\u4ed6\u4eec\u5b8c\u5168\u7eb3\u5165\u4e00\u4e2a\u7ec4\u7ec7\u4e4b\u4e0b\uff0c\u7edf\u4e00\u7ba1\u7406\uff0c\u624d\u4f1a\u4f7f\u6574\u4e2a\u5c40\u9762\u6539\u89c2\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e49\u7236\u7684\u613f\u671b\u5b9e\u73b0\u4e86\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u8fd9\u6ca1\u6709\u542c\u7239\u8bf4\u8fc7\uff0c\u4f46\u4ed6\u4e00\u751f\u505a\u4e8b\uff0c\u7d20\u6240\u575a\u6bc5\uff0c\u53ea\u8981\u4ed6\u60f3\u5230\u7684\u4e8b\u60c5\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u5168\u529b\u4fc3\u5176\u5b9e\u73b0\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5979\u5b9e\u5728\u4e0d\u4fbf\u8bf4\u4ec0\u4e48\uff0c\u53ea\u597d\u70b9\u5934\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a \u201c\u4e0d\u8fc7\uff0c\u6211\u5bf9\u5728\u6c5f\u6e56\u7684\u4e0a\u4e8b\u60c5\uff0c\u4e5f\u77e5\u6653\u4e0d\u591a\u9664\u4e86\u4ed6\u544a\u8bc9\u6211\u4eec\u4e00\u4e9b\u4e4b\u5916\uff0c\u6211\u5e76\u6ca1\u6709\u95ee\u8fc7\uff0c\u59d0\u592b\uff0c\u5982\u542c\u5230\u4e86\u7b2c\u2014\u5821\u6709\u4ec0\u4e48\u9519\u5931\u4e4b\u5904\uff0c\u5c3d\u7ba1\u8bf7\u8bb2\uff0c\u6211\u4f1a\u53bb\u95ee\u660e\u767d\u8fd9\u4ef6\u4e8b\uff0c\u4f1a\u7ed9\u4f60\u2014\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5728\u4e0b\u5bf9\u4ee4\u5c0a\u4e86\u89e3\u7684\u4e0d\u591a\uff0c\u4e0d\u8fc7\uff0c\u6211\u60f3\u4ed6\u65e2\u7136\u4e0d\u80af\u544a\u8bc9\u4f60\uff0c\u53ea\u6015\uff0c\u4f60\u5f88\u96be\u95ee\u660e\u771f\u8c61\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4eec\u7236\u5973\u65e0\u8bdd\u4e0d\u8c08\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u4ee4\u5c0a\u53ef\u4ee5\u544a\u8bc9\u4f60\u7684\u8bdd\uff0c\u6211\u76f8\u4fe1\u4ed6\u4e00\u5b9a\u4f1a\u544a\u8bc9\u4f60\u3002\u201d \u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e5f\u6709\u7406\uff0c\u6211\u7239\u82e5\u505a\u4e86\u89c1\u4e0d\u5f97\u4eba\u7684\u4e8b\uff0c\u4ed6\u4e5f\u771f\u7684\u4e0d\u4f1a\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u5434\u53cc\u5973\uff0c\u63a5\u9053\uff1a\u201c\u59ca\u59ca\u6709\u4ec0\u4e48\u9ad8\u89c1?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u6ca1\u4ec0\u4e48\u9ad8\u89c1\uff0c\u6211\u53ea\u662f\u89c9\u5f97\uff0c\u8fd9\u4ef6\u4e8b\u5e94\u8be5\u67e5\u660e\u771f\u8c61\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5927\u59ca\uff0c\u5982\u82e5\u7239\u548c\u59d0\u592b\u4e4b\u95f4\uff0c\u8981\u4f5c\u4e00\u4e2a\u9009\u62e9\uff0c\u59ca\u59ca\u51c6\u5907\u9009\u62e9\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a\u201c\u9ad8\u59d1\u5a18\uff0c\u4e0d\u8981\u9009\u62e9\u4ec0\u4e48\u4eba\uff0c\u53ea\u8981\u9009\u62e9\u771f\u7406\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u59d0\u592b\uff0c\u5982\u82e5\u6211\u7239\u5c31\u662f\u4e0d\u80af\u8bf4\u5b9e\u8bdd\uff0c\u4f60\u51ed\u4ec0\u4e48\u80fd\u8ba9\u6211\u76f8\u4fe1\u4f60\u7684\u8bdd\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u4e0d\u662f\u76f8\u4fe1\u4e0d\u76f8\u4fe1\u7684\u4e8b\uff0c\u800c\u8981\u627e\u51fa\u771f\u51ed\u5b9e\u636e\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u8bf4\u5427!\u4f60\u7a76\u7adf\u7528\u5fc3\u4f55\u5728?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6211\u4e0d\u613f\u5927\u59d0\u548c\u7239\u5931\u548c\uff0c\u4e5f\u4e0d\u613f\u4f60\u79bb\u5f00\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u5e74\u6765\uff0c\u5434\u53cc\u5973\u548c\u9ad8\u5e7d\u5170\u76f8\u5904\u5f97\u786e\u7136\u6709\u6df1\u539a\u7684\u611f\u60c5\uff0c\u51e0\u53e5\u8bdd\uff0c\u987f\u7136\u4f7f\u5f97\u5434\u53cc\u5973\u6709\u7740\u9eef\u7136\u795e\u4f24\u7684\u611f\u53d7\uff0c\u5e7d\u5e7d\u8bf4\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u6211\u5728\u7b2c\u4e00\u5821\uff0c\u8fd9\u4e9b\u5e74\uff0c\u4f60\u6211\u76f8\u5904\u5f97\u5341\u5206\u878d\u6d3d\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u4e5f\u820d\u4e0d\u5f97\u79bb\u5f00\u7b2c\u4e00\u5821\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59d0\uff0c\u529d\u59d0\u592b\u7559\u4e0b\u6765\uff0c\u6211\u77e5\u9053\u4f60\u4eec\u662f\u4e00\u89c1\u949f\u60c5\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u7239\u548c\u6211\u90fd\u4f1a\u5bb9\u5fcd\uff0c\u6211\u4eec\u4e0d\u8981\u59d0\u592b\u548c\u4ed6\u7684\u670b\u53cb\u4e3a\u654c\uff0c\u53ea\u8981\u4ed6\u7b54\u5e94\u7559\u5728\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u671b\u4e86\u4f55\u5bd2\u8863\u4e00\u773c\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9ad8\u59d1\u5a18\uff0c\u6211\u4e0d\u80fd\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u59d0\u592b\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u4f60\u5982\u575a\u6301\u79bb\u5f00\uff0c\u90a3\u5c31\u7b49\u4e8e\u6d3b\u751f\u751f\u7684\u62c6\u6563\u6211\u4eec\u59d0\u59b9\uff0c\u56e0\u4e3a\uff0c\u4f60\u5982\u575a\u6301\u8981\u8d70\uff0c\u59d0\u59d0\u7edd\u5bf9\u4e0d\u4f1a\u4e00\u4e2a\u4eba\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u6211\u60f3\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u8ba9\u6211\u6162\u6162\u5730\u548c\u4ed6\u8c08\uff0c\u6211\u73b0\u5728\u60f3\u77e5\u9053\uff0c\u7239\u5bf9\u8fd9\u4ef6\u4e8b\u7684\u6001\u5ea6?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5df2\u7ecf\u628a\u8bdd\u8bf4\u5f97\u5f88\u660e\u767d\u4e86\uff0c\u5434\u53cc\u5973\u4e5f\u5c31\u4e0d\u518d\u63a9\u906e\u3002 \u3000\u3000\u8f7b\u8f7b\u4e00\u98a6\u67f3\u7709\u513f\uff0c\u9ad8\u5e7d\u5170\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u7239\u8868\u793a\uff0c\u4f60\u559c\u6b22\u4ec0\u4e48\u4eba\uff0c\u4ed6\u4e0d\u5e72\u6d89\uff0c\u4f46\u5e0c\u671b\u59d0\u59d0\u80fd\u987e\u5ff5\u7236\u5973\u60c5\u610f\uff0c\u4e0d\u8981\u575a\u6301\u79bb\u53bb\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u59d0\u59d0\u5f88\u4e3a\u96be\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u96ea\u6885\u7684\u58f0\u97f3\u4f20\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u5821\u4e3b\u8bf7\uff01\u201d \u3000\u3000\u5434\u53cc\u5973\u970d\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u4e49\u7236\u6765\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u8138\u4e0a\u63a0\u8fc7\u4e00\u62b9\u8bb6\u5f02\u795e\u8272\uff0c\u4e5f\u8ddf\u7740\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u53ea\u6709\u4f55\u5bd2\u8863\u8fd8\u5750\u7740\u672a\u52a8\uff0c \u3000\u3000\u9ad8\u5929\u5065\u7f13\u6b65\u884c\u5165\u4e86\u671b\u6708\u4ead\u3002 \u3000\u3000\u4ed6\u7a7f\u7740\u6708\u767d\u8272\u7684\u957f\u886b\uff0c\u5e26\u7740\u4e00\u8138\u6148\u7231\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u8eac\u8eab\u4e00\u793c\uff0c\u9053\uff1a\u201c\u89c1\u8fc7\u4e49\u7236\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u6325\u6325\u624b\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u591a\u793c\u4e86\uff0c\u4f60\u4eec\u5750\u4e0b\u5427!\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u662f\u4f55\u5c11\u4fa0\u5427!\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u4ed6\u53eb\u4f55\u5bd2\u8863\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u4eec\u89c1\u8fc7\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u662f\u2026\u2026\u5728\u4e0b\u89c1\u8fc7\u9ad8\u5821\u4e3b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8001\u592b\u5012\u60f3\u4e0d\u8d77\u6765\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5728\u4e0b\u8fd8\u8bb0\u5f97\uff0c\u90a3\u662f\u4eca\u5e74\u5723\u6c34\u4ea4\u6613\u540e\u4e24\u65e5\uff0c\u5728\u4e00\u5ea7\u5927\u5b85\u9662\uff0c\u5821\u4e3b\u4ee5\u5200\u6cd5\u5353\u7edd\uff0c\u4e00\u5200\u5288\u6b7b\u4e86\u75c5\u53df\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u5475\u5475\u5927\u7b11\uff0c\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u4f60\u8fd9\u4e00\u63d0\uff0c\u8001\u592b\u4e5f\u60f3\u8d77\u6765\u4e86\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u77e5\u8001\u524d\u8f88\u662f\u5426\u5df2\u7ecf\u53d6\u5f97\u4e86\u5723\u6c34?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u6ca1\u6709\uff0c\u5723\u6c34\u4e0b\u843d\u4e0d\u660e\uff0c\u53ea\u6015\u4f1a\u4e3a\u6c5f\u6e56\u6784\u6210\u4e00\u5371\u5bb3\u4e86\u3002\u201d \u4f55\u5bd2\u8863\u5bf9\u5723\u6c34\u7684\u7528\u5904\uff0c\u4e00\u76f4\u65e0\u6cd5\u4e86\u89e3\uff0c\u542c\u90a3\u9ad8\u5929\u5065\u7684\u53e3\u6c14\u4f3c\u4e4e\u662f\u4ed6\u5f88\u6e05\u695a\uff0c\u5fcd\u4e0d\u4f4f\u8bf4\u9053\uff1a \u3000\u3000\u201c\u9ad8\u5821\u4e3b\u5982\u6b64\u8bf4\uff0c\u90a3\u5723\u6c34\u662f\u4e00\u79cd\u5f88\u6076\u6bd2\u4e4b\u7269\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6c34\u53ef\u8f7d\u821f\uff0c\u4ea6\u53ef\u8986\u821f\uff0c\u5723\u6c34\u7684\u672c\u8eab\uff0c\u5e76\u65e0\u7f6a\u6076\uff0c\u8981\u770b\u65bd\u7528\u4eba\u5bf9\u5b83\u7684\u7528\u6cd5\uff0c\u7528\u5728\u597d\u5904\uff0c\u5bf9\u4eba\u6709\u76ca\uff0c\u5982\u662f\u7528\u5728\u574f\u5904\uff0c\u90a3\u5c31\u5bf9\u4eba\u635f\u5bb3\u5f88\u5927\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u867d\u7136\u660e\u77e5\u9053\u4e0d\u8be5\u95ee\uff0c\u4f46\u4ecd\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a \u201c\u4ec0\u4e48\u662f\u7528\u5728\u574f\u5904?\u4ec0\u4e48\u662f\u7528\u5728\u597d\u5904\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f55\u5c11\u4fa0\u5bf9\u5723\u6c34\uff0c\u4e86\u89e3\u4e0d\u591a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u662f\u7684\uff0c\u5728\u4e0b\u5bf9\u5723\u6c34\u5b9e\u5728\u4e86\u89e3\u7684\u4e0d\u591a\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f55\u5c11\u4fa0\uff0c\u662f\u5426\u60f3\u5bf9\u5723\u6c34\u6709\u4e00\u4e9b\u4e86\u89e3\u5462\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u4e0b\u786e\u6709\u6b64\u5fc3\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u597d!\u4f55\u5c11\u4fa0\u6709\u6b64\u7528\u5fc3\uff0c\u90a3\u5c31\u8bf7\u7559\u5728\u7b2c\u4e00\u5821\u4e2d\uff0c\u6211\u60f3\uff0c\u6709\u4e2a\u4e09\u4e94\u5929\uff0c\u4f60\u5c31\u53ef\u4ee5\u5b8c\u5168\u4e86\u89e3\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8981\u8fd9\u4e48\u957f\u7684\u65f6\u95f4\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u82b1\u4e09\u4e94\u5929\u7684\u65f6\u95f4\uff0c\u80fd\u4e86\u89e3\u5230\u8fd9\u4e9b\u4e8b\uff0c\u5df2\u7ecf\u662f\u6781\u6709\u5929\u624d\u4e86\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u5c31\u5f88\u4e3a\u96be\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f55\u5c11\u4fa0\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a\u201c\u5728\u4e0b\u53ea\u6015\u6ca1\u6709\u4e09\u4e94\u5929\u7684\u65f6\u95f4\uff0c\u7559\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u9053\uff1a\u201c\u4f55\u5c11\u4fa0\u51c6\u5907\u51e0\u65f6\u79bb\u5f00\u7b2c\u4e00\u5821\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u7239\uff0c\u4f55\u5144\u5df2\u7ecf\u548c\u5927\u59ca\u8ba2\u4e86\u5a5a\u7ea6\uff0c\u4f60\u4e0d\u7528\u548c\u4ed6\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u54e6!\u53cc\u5973\uff0c\u6709\u8fd9\u6837\u7684\u4e8b\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8bf7\u4e49\u7236\u539f\u8c05\uff0c\u53cc\u5973\u6ca1\u6709\u5148\u884c\u7980\u660e\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u5bf9\u8fd9\u4f4d\u4e49\u5973\u5a7f\u521d\u5ea6\u7684\u5370\u8c61\uff0c\u6211\u8fd9\u4e2a\u505a\u4e49\u7236\u7684\u4e5f\u5341\u5206\u6ee1\u610f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5973\u513f\u7684\u773c\u5149\u4e0d\u9519\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u9519\uff0c\u800c\u662f\u5f88\u597d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e49\u7236\u4e0d\u53cd\u5bf9\uff0c\u5973\u513f\u5f88\u611f\u6fc0\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4ece\u6b64\u4ee5\u540e\uff0c\u54b1\u4eec\u5c31\u662f\u4e00\u5bb6\u4eba\u4e86\uff0c\u50cf\u5bd2\u8863\u8fd9\u7b49\u4eba\u624d\uff0c\u5982\u82e5\u4e0d\u4e8e\u91cd\u7528\uff0e\u672a\u514d\u662f\u6709\u4e9b\u57cb\u6ca1\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e49\u7236\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u8fd9\u8981\u9760\u4f60\u6765\u5e2e\u5fd9\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e49\u7236\u5429\u5490?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u7b2c\u4e00\u5821\uff0c\u7f3a\u5c11\u4e00\u4f4d\u603b\u62a4\u9662\uff0c\u4e0d\u77e5\u5bd2\u8863\u80fd\u5426\u5c48\u5c31\uff1f\u201d \u4f55\u5bd2\u8863\u9053\uff1a \u201c\u8fd9\u4e2a\u4e0d\u884c\uff0c\u5728\u4e0b\u5df2\u7ecf\u5148\u5411\u5821\u4e3b\u7980\u660e\u4e86\uff0c \u53ea\u6015\u65e0\u6cd5\u7559\u5728\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e49\u7236\uff0c\u770b\u6765\uff0c\u53ea\u6015\u5973\u513f\u65e0\u6cd5\u5e2e\u5fd9\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u771f\u662f\u4e00\u4ef6\u61be\u4e8b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5ac1\u9e21\u968f\u9e21\uff0c\u5ac1\u72d7\u968f\u72d7\uff0c\u5bd2\u8863\u5982\u4e0d\u80fd\u7559\u4e0b\u6765\uff0c\u5973\u513f\u53ea\u6015\u4e5f\u65e0\u6cd5\u5728\u6b64\u5730\u957f\u4f4f\u4e0b\u6765\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u6709\u4e9b\u9eef\u7136\u5730\u70b9\u4e86\u5934\uff0c\u9053\uff1a\u201c\u53cc\u5973\uff0c\u4f60\u8bf4\uff0c\u7528\u4ec0\u4e48\u65b9\u6cd5\uff0c\u624d\u80fd\u628a\u4f60\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5973\u513f\u60f3\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\uff0c\u8fd9\u4e0d\u662f\u5f88\u660e\u663e\u5417?\u53ea\u8981\u4f60\u8001\u4eba\u5bb6\u80fd\u628a\u59d0\u592b\u7559\u4f4f\uff0c\u5927\u59ca\u5c31\u4e0d\u4f1a\u8d70\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5bf9!\u5bf9!\u5bf9!\u4f60\u770b\uff0c\u6211\u5df2\u7ecf\u6709\u4e9b\u6025\u7cca\u6d82\u4e86\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u4f55\u5bd2\u8863\uff0c\u63a5\u9053\uff1a \u201c\u5bd2\u8863\uff0c\u4f60\u8bf4\uff0c\u600e\u4e48\u6837\uff0c\u624d\u80fd\u628a\u4f60\u7559\u5728\u7b2c\u4e00\u5821\u4e2d\uff0c\u53ea\u8981\u4f60\u80fd\u5f00\u51fa\u6761\u4ef6\uff0c\u4e49\u7236\u4e00\u5b9a\u529e\u5230\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u6697\u9053\uff1a \u201c\u4ed6\u8fd9\u822c\u70ed\u60c5\u3001\u6148\u548c\uff0c\u5b9e\u5728\u53eb\u4eba\u6709\u4e9b\u4e3a\u96be\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u59d0\u592b\uff0c\u7239\u662f\u4e00\u7247\u8bda\u610f\uff0c\u4f60\u7559\u4e0b\u6765\uff0c\u4e5f\u53ef\u4ee5\u5bf9\u7b2c\u4e00\u5821\u591a\u4e00\u4e9b\u4e86\u89e3\uff0c\u548c\u4f60\u5728\u6c5f\u6e56\u542c\u5230\u7684\u4f20\u8bf4\uff0c\u662f\u4e0d\u662f\u4e00\u6837\u7684\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a \u201c\u8fd9\u6837\u5427!\u4f60\u7559\u4e0b\u6765\uff0c\u4e0d\u7528\u62c5\u4efb\u4efb\u4f55\u804c\u53f8\uff0c\u5148\u4f4f\u5341\u5929\uff0c\u5047\u5982\u4e0d\u4e60\u60ef\uff0c\u518d\u8d70\u5982\u4f55?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u53cc\u5973\u867d\u7136\uff1a\u4e0d\u662f\u6211\u4eb2\u751f\u5973\u513f\uff0c\u4f46\u6211\u5f85\u5979\u548c\u5e7d\u5170\uff0c\u5b8c\u5168\u4e00\u6837\uff0c\u4f60\u5fcd\u5fc3\u62c6\u6563\u6211\u4eec\u7236\u5973\u5417?\u201d \u3000\u3000\u53e5\u53e5\u4eb2\u60c5\uff0c\u6d41\u73b0\u51fa\u65e0\u6bd4\u7684\u6148\u7231\u4e4b\u5fc3\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u832b\u7136\u4e86\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u4f4d\u6148\u548c\u7684\u957f\u8005\uff0c\u600e\u4f1a\u662f\u9634\u9669\u7684\u574f\u4eba\u5462? \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u5e7d\u5170\u8bf4\u7684\u4e5f\u5bf9\uff0c\u54b1\u4eec\u5c31\u8fd9\u6837\u7684\u51b3\u5b9a\uff0c\u4ee5\u5341\u65e5\u4e3a\u671f\uff0c\u4f60\u5982\u65e0\u6cd5\u4e60\u60ef\u4e8e\u8fd9\u79cd\u751f\u6d3b\uff0c\u90a3\u65f6\uff0c\u518d\u8d70\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u65e0\u6cd5\u63a8\u8f9e\uff0c\u53ea\u597d\u7b54\u5141\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u7684\u9ad8\u5929\u5065\uff0c\u672c\u662f\u5341\u8db3\u7684\u4f2a\u541b\u5b50\uff0c\u4f46\u5728\u8fd9\u4e0d\u592a\u957f\u7684\u65f6\u5149\u4e2d\uff0c\u4f55\u5bd2\u8863\u4f3c\u662f\u5df2\u7ecf\u6709\u4e86\u5f88\u5927\u7684\u6539\u53d8\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7684\u76ee\u5149\u8f6c\u79fb\u5230\u5434\u53cc\u5973\u7684\u8eab\u4e0a\uff0c\u9053\uff1a \u201c\u53cc\u5973\u4f60\u89c9\u7740\u5982\u4f55?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5973\u513f\u6ca1\u6709\u610f\u89c1\uff0c\u53ea\u8981\u4ed6\u80af\u7559\u4e0b\uff0c\u5973\u513f\u4e5f\u81ea\u7136\u7559\u4e0b\u4e86\u3002\u201d \u9ad8\u5929\u5065\u9eef\u7136\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u771f\u662f\u5973\u5927\u4e0d\u4e2d\u7559\u554a!\u5e7d\u5170\uff0c\u6211\u770b\uff0c\u4f60\u4e5f\u8981\u627e\u4e2a\u5a46\u5bb6\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u4e5f\u8be5\u5148\u8ba9\u5927\u59d0\u5ac1\u4e86\uff0c\u624d\u8be5\u5230\u6211\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5bf9!\u53cc\u5973\uff0c\u6211\u770b\u4f55\u516c\u5b50\u5df2\u7ecf\u7b54\u5141\u7559\u5728\u8fd9\u91cc\u5341\u5929\u4e86\uff0c\u4f55\u4e0d\u501f\u8fd9\u5341\u5929\u4e4b\u5185\uff0c\u529e\u4e86\u559c\u4e8b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4e0d\u8bba\u4f60\u5c06\u6765\u80fd\u4e0d\u80fd\u7559\u5728\u7b2c\u4e00\u5821\uff0c\u4f46\u54b1\u4eec\u7ed3\u4e86\u8fd9\u573a\u7236\u5973\u7f18\uff0c\u6211\u603b\u8981\u5c3d\u5fc3\uff0c\u4e0d\u662f\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u5f88\u611f\u52a8\uff0c\u773c\u775b\u4e5f\u6e7f\u6da6\u4e86\uff0c\u9053\uff1a\u201c\u5973\u513f\u597d\u96be\u8fc7\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u603b\u8981\u5ac1\u4eba\u7684\uff0c\u65e9\u665a\u90fd\u8981\u79bb\u5f00\u8fd9\u91cc\u3002\u5b69\u5b50\uff0c\u4e0d\u8c08\u8fd9\u4ef6\u4e8b\u4e86\uff0c\u6709\u4e00\u70b9\uff0c\u4e5f\u4f7f\u4e49\u7236\u89c9\u7740\u62b1\u6b49\uff0c\u90a3\u5c31\u662f\u5982\u6b64\u4ed3\u4fc3\uff0c\u53ea\u6015\u65e0\u6cd5\u7ed9\u4f60\u529e\u5f97\u5f88\u6ee1\u610f\u2026\u2026\u201d \u3000\u3000\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u6211\u7684\u539f\u610f\u662f\uff0c\u5e0c\u671b\u7ed9\u4f60\u529e\u5f97\u5149\u5f69\u4e00\u4e9b\uff0c\u904d\u9080\u4e5d\u5927\u95e8\u6d3e\u4e2d\u4eba\uff0c\u548c\u5929\u4e0b\u82f1\u96c4\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u8981\u529e\u5f97\u5929\u4e0b\u7686\u77e5\uff0c\u4f46\u8fd9\u82e5\u6ca1\u6709\u534a\u5e74\u7684\u65f6\u95f4\uff0c\u53ea\u6015\u662f\u5f88\u96be\u529e\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e49\u7236\uff0c\u5973\u513f\u89c9\u5f97\u5c31\u7b97\u662f\u4e3e\u884c\u5a5a\u793c\uff0c\u4e5f\u662f\u81ea\u5df1\u4eba\u7684\u4e8b\u60c5\uff0c\u4f3c\u4e4e\u662f\u7528\u4e0d\u7740\u9ebb\u70e6\u522b\u7684\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u90a3\u4e0d\u662f\u592a\u59d4\u5c48\u4e86\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e49\u7236\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4eec\u81ea\u5df1\u529e\u4e86\u7b97\u5566!\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u597d\u5427!\u4f60\u65e2\u7136\u4e0d\u6015\u59d4\u5c48\uff0c\u6211\u8fd9\u4e2a\u505a\u4e49\u7236\u7684\uff0c\u4e5f\u53ea\u597d\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\u7b54\u5e94\u4e86\uff0c\u59d0\u592b\u4e5f\u4e0d\u53cd\u5bf9\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4eec\u5c31\u5feb\u70b9\u7740\u624b\u529e\u5427!\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u597d!\u5e7d\u5170\uff0c\u8fd9\u4ef6\u4e8b\uff0c \u4f60\u6765\u5b89\u6392\u4f60\u5927\u59d0\u548c\u59d0\u592b\u7684\u65f6\u95f4\uff0c\u6211\u4e5f\u53bb\u51c6\u5907\u4e00\u4e0b\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u59d0\uff0c\u4f60\u8fd8\u6709\u6ca1\u6709\u4ec0\u4e48\u610f\u89c1?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u770b\u7531\u4e49\u7236\u51b3\u5b9a\u5427\uff01\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u660e\u5929\u5c31\u52a8\u624b\u7b79\u5907\uff0c\u65e2\u7136\u4e0d\u94fa\u5f20\uff0c\u90a3\u5c31\u8d8a\u5feb\u8d8a\u597d\u3002\u201d \u7ad9\u8d77\u8eab\u5b50\uff0c\u8f6c\u5934\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u9ad8\u5929\u5065\u7684\u80cc\u5f71\uff0c\u5434\u53cc\u5973\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u73b0\u5728\uff0c\u54b1\u4eec\u8fd8\u6709\u4ec0\u4e48\u597d\u8c08\u7684\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\u8fd8\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5982\u82e5\u6ca1\u6709\u522b\u7684\u4e8b\uff0c\u6211\u60f3\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\uff0c\u9152\u8fd8\u672a\u559d\uff0c\u5c31\u8981\u8d70\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u559d\u4e0d\u4e0b\u5566!\u8001\u5b9e\u8bf4\uff0c\u59ca\u59ca\u7684\u5185\u5fc3\u4e2d\uff0c\u4e5f\u6709\u4e00\u70b9\u7d27\u5f20\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4e5f\u4e0d\u7559\u5927\u59ca\u4e86\uff0c\u5c0f\u59b9\u4eca\u665a\u4e0a\u7b56\u5212\u4e00\u4e0b\uff0c\u660e\u5929\uff0c\u6211\u53bb\u548c\u59ca\u59ca\u8c08\u8c08\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u90a3\u597d!\u90a3\u4e48\u59ca\u59ca\u5148\u544a\u8f9e\u4e86\u3002\u201d\u7ad9\u8d77\u8eab\u5b50\u4e3e\u6b65\u800c\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7d27\u8ddf\u7740\u5979\u7ad9\u4e86\u8d77\u6765\uff0c\u4e3e\u6b65\u800c\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a \u201c\u5927\u59d0\u592b\uff0c\u65b0\u5a5a\u4e4b\u5bb4\u4e0a\uff0c\u522b\u5fd8\u4e86\u6211\u8fd9\u4e2a\u5c0f\u59e8\u5b50\uff0c\u8981\u591a\u656c\u6211\u4e24\u676f\u9152\u3002\u201d \u5979\u7b11\u5bb9\u5982\u82b1\uff0c\u4e00\u5f20\u5ae9\u7ea2\u7684\u8138\uff0c\u51e0\u4e4e\u649e\u5230\u4e86\u4f55\u5bd2\u8863\u7684\u9f3b\u5b50\u3002 \u3000\u3000\u8bf4\u5b8c\u4e86\u9002\uff0c\u53c8\u8f7b\u8f7b\u5439\u51fa\u4e00\u53e3\u6c14\uff0c\u5982\u5170\u5982\u6842\uff0c\u76f4\u5439\u5165\u4e86\u4f55\u5bd2\u8863\u7684\u9f3b\u5b54\u4e4b\u4e2d\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6025\u6025\u8bf4\u9053\uff1a \u201c\u5fd8\u4e0d\u4e86\uff0c\u5fd8\u4e0d\u4e86\uff0c\u4e00\u5b9a\u8981\u597d\u597d\u656c\u4f60\u51e0\u676f\u3002\u201d \u52a0\u5feb\u811a\u6b65\uff0c\u8ffd\u4e0a\u4e86\u5434\u53cc\u5973\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u9ad8\u59d1\u5a18\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\u5bf9\u4f60\u7684\u5370\u8c61\u4e0d\u9519\u5427\uff01\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u597d\u50cf\u6709\u70b9\u8f7b\u6d6e\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6211\u544a\u8bc9\u4f60\uff0c\u5e7d\u5170\u4e00\u70b9\u4e5f\u4e0d\u8f7b\u6d6e\uff0c\u8fd9\u4e9b\u5e74\uff0c\u6211\u6ca1\u6709\u89c1\u8fc7\u5979\u5bf9\u4efb\u4f55\u4e00\u4e2a\u7537\u4eba\uff0c\u5047\u4ee5\u8f9e\u8272\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u5f88\u6ee1\u610f\u81ea\u5df1\u7684\u773c\u5149\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53cc\u5973\uff0c\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1b\u201c\u6211\u9009\u62e9\u7684\u4e08\u592b\uff0c\u9ad8\u5e7d\u5170\u7adf\u7136\u4e5f\u770b\u4e0a\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u82e6\u7b11\u4e00\u4e0b\u9053\uff1a\u201c\u53cc\u5973\uff0c\u4f60\u5728\u5f00\u4ec0\u4e48\u73a9\u7b11\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u662f\u5f00\u73a9\u7b11\uff0c\u5979\u771f\u7684\u770b\u4e0a\u4e86\u4f60\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u771f\u7684\uff0c\u4e0d\u8fc7\uff0c\u5728\u4e0b\u4e4b\u5fc3\uff0c\u552f\u5929\u53ef\u8868\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u522b\u8bef\u4f1a\uff0c\u6211\u8bf4\u8fd9\u8bdd\u7684\u610f\u601d\uff0c\u662f\u544a\u8bc9\u4f60\uff0c\u5979\u4e0d\u662f\u4e2a\u8f7b\u6d6e\u7684\u5973\u5b69\u5b50\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4f55\u90ce\uff0c\u8bf4\u4e86\u4f60\u4e0d\u8981\u751f\u6c14\uff0c\u4e5f\u4e0d\u8981\u8bef\u4f1a\uff0c\u6211\u786e\u5b9e\u6709\u8fd9\u4e2a\u610f\u601d\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u66ff\u4f60\u505a\u5a92\uff0c\u5e7d\u5170\u5bf9\u4f60\u7684\u5370\u8c61\u4e0d\u9519\uff0c\u800c\u4e14\u6211\u76f8\u4fe1\u5979\u4f1a\u7b54\u5e94\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u884c\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5947\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u5979\u662f\u9ad8\u5929\u5065\u7684\u5973\u513f\uff0c\u6211\u4eec\u8fd9\u4e2a\u7ec4\u7ec7\uff0c\u548c\u9ad8\u5929\u5065\u662f\u52bf\u96be\u4e24\u7acb\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4f60\u8bf4\uff0c\u9ad8\u5929\u5065\u6076\u8ff9\u751a\u591a\uff0c\u6697\u4e2d\u7edf\u7387\u9ed1\u9053\u7ec4\u7ec7\uff0c\u8fd9\u4ef6\u4e8b\u53ef\u662f\u771f\u7684?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u53ef\u6709\u4ec0\u4e48\u51ed\u8bc1?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u77e5\u9053\u7684\u4eba\u4e0d\u5c11\uff0c\u4f46\u8981\u62ff\u51fa\u51ed\u8bc1\uff0c\u90a3\u53ef\u662f\u6709\u4e9b\u56f0\u96be\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4f60\u770b\u5462?\u4ed6\u4eec\u600e\u6837\u624d\u4f1a\u8bf4\u51fa\u6765?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u5929\u5065\u65e0\u6cd5\u6740\u4ed6\u4eec\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u624d\u4f1a\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9634\u9633\u5821\u6765\u4e86\u591a\u5c11\u4eba?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u548c\u4e94\u5927\u5251\u4f7f\uff0c\u5c3d\u90fd\u96c6\u4e2d\u4e8e\u6b64\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u662f\u9634\u9633\u5821\u4e2d\u7684\u7cbe\u9510\uff0c\u4f46\u4eba\u6570\u592a\u5c11\u4e86\u4e00\u4e9b\uff0c\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u5929\u5065\u7684\u8033\u76ee\u4f17\u591a\uff0c\u800c\u4e14\u5bf9\u6211\u4eec\u5c55\u5f00\u4e86\u5168\u9762\u7684\u6253\u51fb\uff0c\u9634\u9633\u5821\u7684\u4eba\uff0c \u5df2\u65e0\u6cd5\u96c6\u4e2d\u529b\u91cf\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u6211\u4e5f\u6709\u8033\u95fb\u9634\u9633\u5821\u7684\u5927\u540d\uff0c\u4e49\u7236\u5728\u6709\u610f\u65e0\u610f\u4e4b\u95f4\uff0c\u5e38\u5e38\u63d0\u5230\u9634\u9633\u5821\u8fd9\u4e2a\u7ec4\u7ec7\uff0c\u4ee5\u6211\u6027\u683c\uff0c\u65e9\u5c31\u8bf7\u5e26\u4e00\u90e8\u4eba\u624b\uff0c\u8d76\u5f80\u9634\u9633\u5821\u53bb\uff0c\u4f1a\u4f1a\u4f60\u4eec\u90a3\u4f4d\u9ec4\u5821\u4e3b\u5b50\uff0c\u60f3\u4e0d\u5230\uff0c\u8fd9\u4e00\u6b21\uff0c\u6211\u7adf\u672a\u591a\u95ee\u6b64\u4e8b\uff0c\u4e5f\u5e78\u597d\u6ca1\u6709\u591a\u95ee\u2026\u2026\u201d \u3000\u3000\u5a07\u67d4\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4e0d\u662f\u65e9\u6709\u7075\u7280\u4e00\u70b9\u901a?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\uff0c\u5927\u80cc\u5e38\u60c5\uff0c\u90a3\u53ea\u6709\u5f52\u8bf8\u547d\u8fd0\u4e4b\u8bf4\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u7684\u547d\u8fd0\uff0c\u8fd8\u4e0d\u592a\u574f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5374\u4e5f\u672a\u5fc5\u5f88\u597d\uff0c\u56e0\u4e3a\uff0c\u4f60\u8ba4\u8bc6\u4e86\u6211\uff0c\u9634\u9633\u5821\u548c\u7b2c\u4e00\u5821\uff0c\u8fdf\u65e9\u603b\u6709\u4e00\u573a\u8840\u6218\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u5bf9\u8fd9\u4e00\u4ed7\u7684\u770b\u6cd5\u5982\u4f55?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u5bf9\u7b2c\u4e00\u5821\u8fd8\u4e0d\u592a\u4e86\u89e3\u5176\u5b9e\u529b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u5f88\u8be6\u5c3d\u7684\u544a\u8bc9\u4f60\uff0c\u4e0d\u8fc7\uff0c\u90a3\u5e76\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u600e\u4e48\u8bf4?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5c31\u7b97\u4f60\u4eec\u4e86\u89e3\u7b2c\u4e00\u5821\u7684\u5b9e\u529b\uff0c\u4e5f\u6ca1\u6709\u53d6\u80dc\u7684\u673a\u4f1a\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u4e0b\u6709\u56db\u5927\u9556\u5c40\u548c\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a \u201c\u4e0d\u7b97\u4ed6\u4eec\uff0c\u6211\u8bf4\u7684\u53ea\u662f\u7b2c\u4e00\u5821\u4e2d\u73b0\u6210\u7684\u529b\u91cf\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u6b64\u5f3a\u5927\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5bf9!\u5355\u662f\u4e00\u4e2a\u795e\u5200\u5802\uff0c\u5c31\u8db3\u6297\u62d2\u6c5f\u6e56\u4efb\u4f55\u4e00\u4e2a\u95e8\u6d3e\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9664\u4e86\u795e\u5200\u5802\u5916\uff0c\u5173\u952e\u5728\u9ad8\u5e7d\u5170\u8eab\u4e0a\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u96be\u9053\u4f1a\u5f3a\u8fc7\u9ad8\u5929\u5065\u4e0d\u6210\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u522b\u5c0f\u770b\u5979\uff0c\u52a8\u4e0a\u624b\uff0c\u5979\u6bd4\u9ad8\u5929\u5065\u53ea\u5f3a\u4e0d\u5f31\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u6837\u5389\u5bb3?\u201d \u3000\u3000\u8138\u4e0a\uff0c\u662f\u4e00\u7247\u4e0d\u5927\u76f8\u4fe1\u7684\u795e\u60c5\u3002 \u3000\u3000\u5434\u53cc\u5973\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u6211\u8bf4\u7684\u5f88\u771f\u5b9e\uff0c\u4e5f\u8bb8\u9ad8\u5e7d\u5170\u5728\u5185\u529b\u4fee\u4e3a\u4e0a\uff0c\u4e0d\u5982\u7236\u4eb2\uff0c\u4f46\u5979\u6740\u4eba\u7684\u624b\u6cd5\uff0c\u5374\u662f\u5f3a\u8fc7\u7236\u4eb2\u5341\u500d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u771f\u7684\u90a3\u4e48\u5389\u5bb3\uff1f\u201d\u5434\u53cc\u5973\u9053\uff1a \u201c\u6211\u548c\u5979\u4e00\u8d77\u52a8\u8fc7\u624b\uff0c\u770b\u5230\u5979\u51fa\u624b\u7684\u51cc\u5389\u901f\u5ea6\uff0c\u5730\u51fa\u624b\u4e03\u5251\u6740\u4e86\u4e03\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e00\u5251\u2014\u4e2a\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u4e9b\u88ab\u6740\u7684\u4eba\uff0c\u90fd\u4e0d\u662f\u4e00\u822c\u7684\u6c5f\u6e56\u6b66\u5e08\uff0c\u6709\u51e0\u4e2a\u8fd8\u662f\u4e2a\u5f88\u6709\u540d\u671b\u7684\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\u201c\u4ed6\u4eec\u662f\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u53ea\u8bb0\u5f97\u5176\u4e2d\u4e00\u4e2a\u4eba\u7684\u5916\u53f7\uff0c\u53eb\u94c1\u526a\u5200\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48?\u94c1\u526a\u5200\u6797\u4e39\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9!\u597d\u50cf\u662f\u53eb\u6797\u4e39\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ed6\u5c01\u4e0d\u4f4f\u9ad8\u5e7d\u5170\u4e00\u5251?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9!\u9ad8\u5e7d\u5170\u4e00\u5251\u5c31\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53cc\u5973\uff0c\u771f\u7684\u53ea\u6709\u4e00\u5251\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eb2\u773c\u770b\u5230\u4e86\u5979\u51fa\u624b\uff0c \u53ea\u4e00\u5251\uff0c\u5c31\u6740\u6b7b\u4e86\u94c1\u526a\u5200\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5982\u82e5\u9ad8\u5e7d\u5170\u548c\u4f60\u52a8\u8d77\u624b\u6765\uff0c \u4f60\u4eec\u4e24\u4e2a\u8c01\u5360\u4f18\u52bf?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u771f\u8981\u52a8\u4e0a\u4e86\u624b\uff0c\u81ea\u7136\u662f\u6211\u4f1a\u80dc\u5979\u4e00\u7b79\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5bf9\u9ad8\u5e7d\u5170\u7684\u5feb\u5251\uff0c \u4f60\u6709\u5bf9\u4ed8\u4e4b\u6cd5\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6ca1\u6709\u5f88\u597d\u7684\u529e\u6cd5\uff0c\u6211\u80dc\u5979\uff0c\u56e0\u4e3a\u6211\u7684\u6b66\u529f\u6bd4\u5979\u9ad8\u660e\u4e00\u4e9b\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a \u201c\u6211\u6ca1\u6709\u4e00\u79cd\u76f4\u63a5\u7684\u6b66\u529f\u5bf9\u4ed8\u9ad8\u5e7d\u5170\u7684\u5251\u6cd5\uff0c\u6211\u4f20\u4f60\u4e24\u62db\u5251\u6cd5\uff0c\u5927\u6982\u53ef\u4ee5\u62b5\u6297\u4e00\u4e0b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u80fd\u7834\u89e3\u5979\u7684\u5251\u6cd5?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u80fd\u3002\u53ea\u80fd\u5c01\u6321\u4f4f\u5979\u7684\u653b\u52bf\uff0c\u4f7f\u5979\u4f24\u4e0d\u4e86\u4f60\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5728\u4e0b\u5012\u53ef\u4ee5\u548c\u9ad8\u5e7d\u5170\u653e\u624b\u4e00\u6218\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u90a3\u592a\u5192\u9669\u4e86\uff0c\u90a3\u4e24\u62db\u5251\u6cd5\uff0c\u5bf9\u4ed8\u9ad8\u5e7d\u5170\uff0c\u53ea\u80fd\u7528\u505a\u4fdd\u547d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u542c\u8bf4\u5979\u7528\u7684\u662f\u4e03\u8272\u5251\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e03\u79cd\u989c\u8272\uff0c\u4e03\u79cd\u5251\u6cd5\u3001\u4e03\u79cd\u81f4\u547d\u7684\u5947\u62db\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u4ece\u6765\u6ca1\u542c\u5230\u8fc7\u4e03\u8272\u5251\u7684\u4f20\u8bf4\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u5c31\u50cf\u6211\u7684\u6b66\u529f\u4e00\u6837\uff0c\u5e08\u627f\u7684\u95e8\u6237\uff0c\u4e0d\u7b97\u6b63\u5b97\uff0c\u6240\u4ee5\uff0c\u5f88\u96be\u627e\u51fa\u4e00\u79cd\u8109\u7edc\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u53ea\u662f\u6b66\u529f\u8be1\u5f02\uff0c\u4f46\u4e03\u8272\u5251\u5374\u662f\u4e00\u79cd\u5175\u5203\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\u867d\u7136\u5c3d\u91cf\u7684\u907f\u5f00\u6211\uff0c\u4e0d\u8ba9\u6211\u770b\u51fa\u5979\u7684\u5251\u8def\uff0c\u4f46\u6211\u4e3e\u4e00\u53cd\u4e09\u3002\u60f3\u51fa\u5979\u5230\u6cd5\u4e2d\u7684\u5251\u8def\uff0c\u4f46\u4e0d\u80fd\u80af\u5b9a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u8bf4\u8fd9\u4ef6\u4e8b\u4e86\u3001\u6211\u4eec\u8c08\u8c08\u73b0\u5728\uff0c\u73b0\u5728\uff0c\u6211\u4eec\u8be5\u5982\u4f55\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u4f60\u53ef\u4e0d\u53ef\u80fd\u7559\u4e0b\u6765\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u7559\u5728\u7b2c\u4e00\u5821\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9\uff01\u7559\u5728\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u671b\u7740\u4f55\u5bd2\u8863\uff0c\u53c8\u95ee\u9053\uff1b\u201c\u9ad8\u5821\u4e3b\u5728\u6c5f\u6e56\u4e0a\u771f\u7684\u662f\u6076\u8ff9\u662d\u5f70\u5417\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53ea\u4ece\u5916\u9762\u53bb\u542c\uff0c\u4ed6\u4e0d\u4f46\u6ca1\u6709\u6076\u8ff9\uff0c\u800c\u4e14\uff0c\u540d\u58f0\u5f88\u597d\uff0c\u5b9e\u9645\u4e0a\uff0c\u4ed6\u786e\u662f\u4e00\u4e2a\u65e0\u6076\u4e0d\u4f5c\u7684\u4eba\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u4f60\u65e2\u7136\u51b3\u5b9a\u8981\u8d70\u3002\u662f\u5426\u8fd8\u8981\u4ed6\u4eec\u66ff\u6211\u4eec\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u9053\uff1a\u201c\u5b8c\u6210\u5a5a\u4e8b\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53cc\u5973\uff0c\u4f60\u770b\u5462\uff1f\u4f60\u5982\u60f3\u5b8c\u6210\u4e86\u5a5a\u4e8b\uff0c\u6211\u4eec\u5c31\u7559\u4e0b\u6765\u51e0\u5929\uff01\u201d \u5434\u53cc\u5973\u9053\uff1a \u3000\u3000\u201c\u82e5\u4f60\u4e0d\u53cd\u5bf9\uff0c\u6211\u662f\u5e0c\u671b\u4f60\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d!\u5c31\u7167\u4f60\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8981\u4e0d\u8981\u901a\u77e5\u4ed6\u4eec\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u901a\u77e5\u8c01?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u554a!\u4f60\u4e0d\u662f\u8bf4\u9634\u9633\u5821\u4e2d\u7684\u7cbe\u9510\uff0c\u4ed6\u4eec\u5168\u90e8\u90fd\u5728\u8fd9\u91cc\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u662f\u5e94\u8be5\u901a\u77e5\u4ed6\u4eec\u4e00\u58f0\uff0c\u514d\u5f97\u4ed6\u4eec\u60ac\u5ff5\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u4f60\u60f3\u5230\u4e86\u6ca1\u6709\uff0c\u8fd9\u6d88\u606f\u5982\u8ba9\u9ad8\u5821\u4e3b\u77e5\u9053\uff0c\u4f1a\u6709\u4e9b\u4ec0\u4e48\u5371\u9669?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4eec\u9690\u79d8\u4e00\u4e9b\uff0c\u4ed6\u4eec\u53c8\u600e\u4f1a\u77e5\u9053\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u8981\u4f4e\u4f30\u4e86\u9ad8\u5821\u4e3b\uff0c\u7b2c\u4e00\u5821\u4e2d\u7684\u9632\u5907\uff0c\u4e25\u5bc6\u5f97\u5f88\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u679c\u662f\u7531\u59d1\u5a18\u4ee3\u5728\u4e0b\u53d1\u51fa\u8baf\u606f\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u770b\u6765\uff0c\u4f60\u8fd8\u662f\u4f4e\u4f30\u4e86\u7b2c\u4e00\u5821\u4e2d\u4eba\u7684\u80fd\u529b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u600e\u4e48\u8bf4?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u867d\u7136\u628a\u6211\u5f53\u4f5c\u4eb2\u751f\u5973\u513f\u770b\u5f85\uff0c\u4f46\u4ed6\u5bf9\u6211\u4ecd\u7136\u4e0d\u653e\u5fc3\u5f97\u5f88\uff0c\u5c24\u5176\u662f\u73b0\u5728\uff0c\u5bf9\u6211\u76d1\u89c6\u7684\u4e25\u5bc6\uff0c\u7edd\u4e0d\u4f1a\u5728\u4f60\u4e4b\u4e0b\u2026\u2026\u201d \u3000\u3000\u4e3e\u624b\u7406\u4e00\u7406\u9b13\u8fb9\u7684\u6563\u53d1\uff0c\u63a5\u9053\uff1a\u201c\u82e5\u4ed6\u5fc3\u4e2d\u6709\u6068\uff0c\u6068\u6211\u4e4b\u5fc3\uff0c\u53ea\u6015\u6bd4\u4f60\u8fd8\u6df1\u4e9b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u9664\u975e\u4f60\u7b54\u5e94\u5728\u7b2c\u4e00\u5821\uff0c\u4ed6\u5df2\u7ecf\u628a\u54b1\u4eec\u5f53\u4f5c\u4e86\u654c\u4eba\u770b\u5f85\uff0c\u6240\u4ee5\uff0c \u7531\u73b0\u5728\u5f00\u59cb\uff0c\u54b1\u4eec\u8981\u968f\u65f6\u9632\u5907\u6697\u7b97\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fde\u4f60\u4e5f\u4f1a\u7b97\u8ba1?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5982\u6211\u731c\u7684\u4e0d\u9519\u7684\u8bdd\uff0c\u6211\u624d\u662f\u4ed6\u7684\u7b2c\u4e00\u76ee\u6807\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5ffd\u7136\u7ad9\u8d77\u8eab\u5b50\u6765\uff0c\u9053\uff1a \u201c\u8fd9\u4e48\u8bf4\u6765\uff0c \u4ed6\u4eec\u4e5f\u4f1a\u6478\u4e0a\u5f69\u8679\u697c\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u8fd9\u70b9\u8bf7\u4f60\u653e\u5fc3\uff0c\u4ed6\u4eec\u8fd8\u6ca1\u6709\u8fd9\u4e48\u5927\u7684\u80c6\u5b50\uff0c\u4e5f\u4e0d\u4f1a\u4f5c\u5f97\u8fd9\u4e48\u9732\u9aa8\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ed6\u4eec\u5bf9\u4f60\uff0c\u662f\u4e0d\u662f\u6709\u7740\u5f88\u5927\u7684\u987e\u5fcc?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9!\u6709\u7740\u5f88\u5927\u7684\u987e\u5fcc\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u53cc\u5973\uff0c\u5509!\u771f\u662f\u62b1\u6b49\uff0c\u6211\u60f3\u4e0d\u5230\uff0c\u7adf\u628a\u4f60\u4e5f\u62d6\u5165\u6f29\u6da1\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u662f\u6211\u81ea\u613f\u7684\uff0c\u592b\u59bb\u4e4b\u60c5\uff0c\u751f\u540c\u7f57\u5e0f\uff0c\u6b7b\u540c\u7a74\uff0c\u8fd8\u6709\u4ec0\u4e48\u4f60\u6211\u4e4b\u5206\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u6211\u60f3\u8ddf\u4f60\u8bf4\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u652f\u6301\u7b2c\u4e00\u5821\u5c79\u7acb\u6c5f\u6e56\u7684\u6709\u4e24\u5927\u652f\u67f1\uff0c\u4f60\u53ea\u8981\u628a\u8fd9\u4e24\u652f\u5927\u67f1\u62c6\u4e86\uff0c\u7b2c\u4e00\u5821\u5c31\u7acb\u523b\u4f1a\u57ae\u53f0\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u662f\u5176\u4e2d\u4e4b\u4e00?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u548c\u9ad8\u5e7d\u5170\u7b97\u662f\u4e00\u4e2a\uff0c\u53e6\u4e00\u4e2a\uff0c\u5c31\u662f\u795e\u5200\u5802\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u795e\u5200\u5802\u6709\u90a3\u4e48\u5927\u7684\u5b9e\u529b?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u795e\u5200\u5802\u53ea\u662f\u4e2a\u603b\u540d\u79f0\uff0c\u4e0b\u9762\u9ad8\u624b\u6781\u591a\uff0c\u6211\u53ea\u77e5\u9053\u5341\u5206\u5e9e\u5927\uff0c\u8be6\u7ec6\u60c5\u5f62\u8fde\u6211\u4e5f\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8bf4\u8bf4\u770b\u5982\u4f55\u5bf9\u4ed8\u795e\u5200\u5802\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u529e\u6cd5\uff0c\u795e\u5200\u5802\u575a\u56fa\u5f97\u5f88\uff0c\u6839\u672c\u65e0\u6cd5\u63d2\u624b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u8981\u5bf9\u4ed8\u4ec0\u4e48\u4eba\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u7b2c\u4e00\u5821\u6709\u4e24\u5927\u652f\u67f1\uff0c\u4f60\u53ea\u62c6\u4e86\u4e00\u4e2a\u652f\u67f1\u7684\u4e00\u534a\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\u662f\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u628a\u9ad8\u5e7d\u5170\u4e5f\u62c9\u8fc7\u6765\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u62c9\u9ad8\u5e7d\u5170\u8fc7\u6765?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u770b\u5979\u5bf9\u4f60\u4e0d\u9519\uff0c\u4f55\u59a8\u4e00\u8bd5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u600e\u4e48\u8bd5?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u529e\u6cd5\u603b\u662f\u4eba\u60f3\u51fa\u6765\u7684\uff0c\u53ea\u8981\u7b54\u5e94\u4e86\uff0c\u6211\u4eec\u518d\u6765\u60f3\u529e\u6cd5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u7528\u60f3\u529e\u6cd5\u4e86\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u770b\u4e0d\u884c\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u9ad8\u5e7d\u5170\u662f\u4e2a\u597d\u5973\u5b69\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u8981\u6211\u600e\u6837\u62c9\u62e2\u5979?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u53ea\u8981\u4f60\u7b54\u5e94\u5a36\u5979\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a\u201c\u4ec0\u4e48!\u6211\u4eec\u8fd8\u672a\u5b8c\u6210\u5a5a\u793c\uff0c\u6211\u53c8\u8981\u5a36\u5c0f\u8001\u5a46\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u6709\u4e9b\u4e8b\uff0c\u54b1\u4eec\u4e0d\u80fd\u4e0d\u5148\u5b89\u6392\u4e00\u4e0b\uff0c\u9ad8\u5e7d\u5170\u8fd9\u4e2a\u4eba\uff0c\u6211\u5f88\u4e86\u89e3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u600e\u4e48\u6837?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5979\u548c\u6211\u7684\u6027\u683c\u5dee\u4e0d\u591a\uff0c\u90fd\u662f\u6709\u70b9\u81ea\u8d1f\u7684\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u4eca\u5929\u770b\u5230\u5979\u7684\u968f\u548c\uff0c\u4e0d\u505c\u7684\u5a07\u558a\u5a07\u53eb\uff0c\u5b9e\u5728\u662f\u5f88\u5c11\u6709\u7684\u4e8b\uff0c\u5e73\u5e38\u65e5\u5b50\u91cc\uff0c\u53ea\u8981\u6709\u7537\u4eba\u5728\u5ea7\uff0c\u96be\u5f97\u770b\u5230\u5979\u6709\u4e00\u6b21\u7b11\u5bb9\uff0c\u51b7\u82e5\u51b0\u971c\u8fd9\u53e5\u8bdd\uff0c\u5bf9\u5979\u5b9e\u5728\u662f\u5f88\u6070\u5f53\u7684\u5f62\u5bb9\u3002\u8a00\u4e3a\u5fc3\u58f0\uff0c\u4eca\u5929\u5979\u8bb2\u4e86\u90a3\u4e48\u591a\u8bdd\uff0c\u4e00\u53e3\u4e00\u4e2a\u59d0\u592b\uff0c\u53eb\u5f97\u9707\u5929\u822c\u54cd\uff0c\u90a3\u5e76\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u96be\u9053\u5979\u8fd8\u6709\u4ec0\u4e48\u7528\u5fc3\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u81ea\u7136\u662f\u522b\u6709\u7528\u5fc3\uff0c\u56e0\u4e3a\u5979\u5fc3\u4e2d\u559c\u6b22\u4f60\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u6211\u8bf4\u7684\u662f\u771f\u8bdd\uff0c\u5979\u5bf9\u4f60\u7684\u4eb2\u5207\u60c5\u5f62\uff0c\u662f\u6211\u7b2c\u4e00\u6b21\u89c1\u5230\u7684\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u4f60\u7684\u9762\u5b50\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5982\u662f\u770b\u5728\u6211\u7684\u9762\u5b50\uff0c\u4e0d\u4f1a\u5f88\u4eb2\u5207\uff0c\u800c\u662f\u5bf9\u4f60\u5c0a\u656c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5509!\u8fd9\u771f\u662f\u53eb\u6211\u6709\u4e9b\u7cca\u6d82\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u6211\u89c9\u5f97\u5e7d\u5170\u5f88\u597d\uff0c\u4f46\u662f\u6211\u7edd\u5bf9\u6ca1\u6709\u903c\u4f60\u7684\u610f\u601d\uff0c\u4e0d\u8fc7\u5e7d\u5170\u7684\u4e8b\uff0c\u4f60\u53ef\u4ee5\u597d\u597d\u7684\u60f3\u60f3\uff0c\u5bd2\u8863\uff0c\u6ca1\u6709\u4e00\u4e2a\u59bb\u5b50\u4e0d\u7231\u81ea\u5df1\u7684\u4e08\u592b\uff0c\u4f60\u4e00\u5b9a\u5f88\u5947\u602a\uff0c\u6211\u4e3a\u4ec0\u4e48\u628a\u5fc3\u7231\u7684\u4e08\u592b\u5411\u5916\u63a8\uff0c\u63a8\u5411\u522b\u4eba\u7684\u6000\u62b1\uff0c\u8001\u5b9e\u8bf4\uff0c\u96be\u9053\u6211\u5fc3\u91cc\u4e0d\u75bc\u5417?\u6211\u662f\u66ff\u4f60\u60f3\u2026\u2026\u201d \u3000\u3000\u4e3e\u624b\u7406\u4e00\u7406\u9b13\u8fb9\u7684\u6563\u53d1\uff0c\u7f13\u7f13\u63a5\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u521a\u624d\u6211\u60f3\u4e86\u5f88\u591a\u4e8b\uff0c\u6211\u89c9\u7740\uff0c\u5e7d\u5170\u5bf9\u6211\u4eec\u5f88\u91cd\u8981\uff0c\u4e0d\u4f46\u662f\u4e3a\u4e86\u4f60\uff0c\u4e5f\u662f\u4e3a\u4e86\u4f60\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5446\u4e00\u4e86\u5446\uff0c\u9053\uff1a\u201c\u4e3a\u4e86\u6211\u7684\u670b\u53cb?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5bf9!\u73b0\u5728\uff0c\u4f60\u4e0d\u7528\u7b54\u590d\uff0c\u4f60\u665a\u4e0a\u597d\u597d\u7684\u60f3\u60f3\uff0c\u660e\u5929\u518d\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u597d!\u8ba9\u6211\u597d\u597d\u60f3\u60f3\uff0c\u7136\u540e\uff0c\u6211\u4f1a\u7ed9\u4f60\u4e00\u4e2a\u7b54\u590d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6162\u6162\u7684\u60f3\u5427\uff0c\u4e00\u5b9a\u8981\u60f3\u4ed4\u7ec6\u4e00\u4e9b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1b\u201c\u6211\u4f1a\u597d\u597d\u7684\u60f3\u4e00\u60f3\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u73b0\u5728\uff0c\u54b1\u4eec\u8c08\u8c08\u4f60\u5982\u4f55\u4f20\u51fa\u6d88\u606f\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4eec\u6709\u4e00\u79cd\u8054\u7edc\u7684\u65b9\u6cd5\uff0c\u53ea\u8981\u628a\u8fd9\u79cd\u65b9\u6cd5\u4f20\u51fa\u53bb\uff0c\u4ed6\u4eec\u5c31\u77e5\u9053\u600e\u4e48\u56de\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u628a\u65b9\u6cd5\u8bf4\u51fa\u6765\uff0c\u770b\u770b\u6211\u6709\u6ca1\u6709\u673a\u4f1a\u4f20\u51fa\u53bb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u8bf4\u660e\u4e86\u8054\u7edc\u7684\u4fe1\u53f7\u3002 \u3000\u3000\u5434\u53cc\u5973\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u65b9\u6cd5\u4e0d\u9519\uff0c\u6211\u8bd5\u8bd5\u770b\u5427\uff01\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u5b50\uff0c\u63a5\u9053\uff1a\u201c\u8d70\uff0c\u4f11\u606f\u53bb\u5427\uff0c\u4f60\u7761\u5728\u6211\u7684\u623f\u4e2d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u966a\u966a\u4f60\uff0c\u4e0d\u8fc7\uff0c\u54b1\u4eec\u8fd8\u672a\u6b63\u5f0f\u6210\u4eb2\uff0c\u603b\u4e0d\u597d\u610f\u601d\u660e\u76ee\u5f20\u80c6\u7684\u4f4f\u5728\u4e00\u8d77\u5427\uff01\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7a81\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u4e00\u4f38\u624b\uff0c\u628a\u5434\u53cc\u5973\u63fd\u5165\u4e86\u6000\u4e2d\u3002 \u3000\u3000\u7b2c\u4e00\u5821\u4e2d\u5f00\u59cb\u7b79\u5907\u4f55\u5bd2\u8863\u4e0e\u5434\u53cc\u5973\u7684\u5a5a\u4e8b\u4e86\uff0c\u5230\u5904\u5f20\u706f\u7ed3\u5f69\u3002 \u3000\u3000\u7b2c\u4e00\u5821\u4e2d\uff0c\u5230\u5904\u6d0b\u6ea2\u7740\u4e00\u7247\u559c\u6c14\u3002 \u3000\u3000\u4f46\u5434\u53cc\u5973\u548c\u4f55\u5bd2\u8863\u53cd\u800c\u53d7\u5230\u4e86\u5f88\u5927\u62d8\u675f\u3002 \u3000\u3000\u7b2c\u4e00\u5821\u4e2d\u7684\u4eba\uff0c\u53ea\u8981\u4e00\u770b\u5230\u5434\u5927\u5c0f\u59d0\uff0c\u90fd\u4f1a\u606d\u606d\u656c\u656c\u7684\u8bf4\u4e00\u53e5\u606d\u559c\u5927\u5c0f\u59d0\u3002 \u3000\u3000\u8fd9\u4f7f\u5434\u53cc\u5973\u4e0d\u597d\u610f\u601d\u5728\u7b2c\u4e00\u5821\u4e2d\u5230\u5904\u8d70\u52a8\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u66f4\u60e8\uff0c\u6839\u672c\u6ca1\u529e\u6cd5\u79bb\u5f00\u5f69\u8679\u697c\u3002 \u3000\u3000\u8db3\u8db3\u6709\u4e94\u5929\uff0c\u4f55\u5bd2\u8863\u8db3\u4e0d\u51fa\u6237\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u80fd\u591f\u770b\u5230\u7684\u4eba\uff0c\u53ea\u6709\u4e24\u4e2a\uff0c\u4e00\u4e2a\u662f\u5434\u53cc\u5973\uff0c\u4e00\u4e2a\u662f\u96ea\u6885\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5076\u5c14\u4e5f\u4f1a\u6765\u4e00\u6b21\uff0c\u6bcf\u4e00\u6b21\u6765\u65f6\uff0c\u7a7f\u7684\u670d\u9970\u90fd\u4e0d\u4e00\u6837\uff0c\u6709\u6d53\u5986\u3001\u6709\u6de1\u62b9\u3002 \u3000\u3000\u5982\u628a\u897f\u6e56\u6bd4\u897f\u5b50\uff0c\u6d53\u5986\u6de1\u62b9\u603b\u76f8\u5b9c\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5c31\u662f\u8fd9\u6837\u4e00\u4e2a\u4eba\uff0c\u6d53\u5986\u6709\u6d53\u5986\u7684\u5a07\u4e3d\uff0c\u6de1\u62b9\u6709\u6de1\u62b9\u7684\u6e05\u79c0\u3002 \u3000\u3000\u8fd9\u65e5\u9ec4\u660f\u65f6\u5206\uff0c\u9ad8\u5e7d\u5170\u4e00\u8eab\u767d\u8863\u88d9\uff0c\u9762\u5e26\u5fae\u7b11\uff0c\u884c\u5165\u4e86\u5f69\u8679\u697c\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e0d\u5728\uff0c\u96ea\u6885\u4e5f\u4e0d\u5728\u3002 \u3000\u3000\u655e\u5927\u7684\u5f69\u8679\u697c\uff0c\u53ea\u6709\u4f55\u5bd2\u8863\u4e00\u4e2a\u4eba\u5728\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u8138\u4e0a\u5e26\u7740\u5fae\u7b11\uff0c\u7b11\u5f97\u5982\u82b1\u76db\u653e\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5c31\u5750\u5728\u5927\u5385\u4e2d\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d0\u592b\uff0c\u5927\u59d0\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u521a\u521a\u51fa\u53bb\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u540e\u5929\u5c31\u662f\u4f60\u548c\u5927\u59d0\u7684\u5927\u559c\u65e5\u5b50\uff0c\u7239\u8d39\u4e86\u4e0d\u5c11\u7684\u5fc3\u8840\uff0c\u6574\u4e2a\u7684\u7b2c\u4e00\u5821\uff0c\u5230\u5904\u6d0b\u6ea2\u7740\u4e00\u7247\u559c\u6c14\uff0c\u59d0\u592b\u600e\u4e48\u4e0d\u51fa\u53bb\u8d70\u8d70\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5509!\u7b2c\u4e00\u5821\u7684\u4eba\uff0c\u90fd\u5bf9\u6211\u592a\u5ba2\u6c14\u4e86\uff0c\u5ba2\u6c14\u5f97\u4f7f\u6211\u6709\u4e9b\u4e0d\u597d\u610f\u601d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4f60\u662f\u65b0\u4eba\u561b!\u4ee5\u540e\uff0c\u5927\u5bb6\u719f\u6089\u4e86\uff0c\u5c31\u4e0d\u4f1a\u8fd9\u6837\u591a\u793c\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u5fd6\u9053\uff1a\u4ee5\u540e\uff0c\u54ea\u91cc\u8fd8\u6709\u4ee5\u540e\uff0c\u5a5a\u793c\u5b8c\u6210\uff0c\u6211\u5c31\u8981\u79bb\u5f00\u4e86\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u59d0\u592b\uff0c\u80fd\u4e0d\u80fd\u7559\u5728\u7b2c\u4e00\u5821?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5f88\u4e3a\u96be\uff0c\u53ea\u8981\u59d0\u592b\u80af\u7559\u4e0b\u6765\uff0c\u6211\u60f3\uff0c\u6240\u6709\u7684\u96be\u5904\uff0c\u90fd\u53ef\u4ee5\u514b\u670d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\u4e48\u2026\u2026\u5e7d\u5170\uff0c\u6211\u5f88\u96be\u51b3\u5b9a\uff0c\u8ba9\u6211\u6162\u6162\u7684\u60f3\u4e00\u60f3\u597d\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7741\u5927\u773c\u775b\uff0c\u9053\uff1a\u201c\u4f60\u53eb\u6211\u4ec0\u4e48?\u662f\u4e0d\u662f\u5e7d\u5170?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u662f\u4e0d\u662f\u592a\u5931\u793c\u4e86?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6025\u6025\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d!\u4f60\u53eb\u6211\u5e7d\u5170\uff0c\u6211\u5fc3\u4e2d\u597d\u5feb\u4e50\uff0c\u8fd9\u6837\uff0c\u4e0d\u662f\u5f88\u4eb2\u5207\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u4e0d\u89c1\u602a\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u59d0\u592b\uff0c\u522b\u8fd9\u6837\u8bf4\uff0c\u6211\u559c\u6b22\u5927\u59d0\uff0c\u4e5f\u559c\u6b22\u4f60\uff0c\u4f46\u4f60\u5374\u5f88\u5feb\u7684\u8981\u79bb\u5f00\u8fd9\u91cc\uff0c\u59d0\u592b\uff0c\u53ea\u8981\u4f60\u80af\u7559\u4e0b\u6765\uff0c\u65e0\u8bba\u4ec0\u4e48\u4e8b\uff0c\u6211\u4eec\u90fd\u4f1a\u7b54\u5e94\u4f60\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u80fd\u591f\u505a\u5f97\u4e86\u4e3b\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u80fd!\u7239\u5f88\u5c0a\u91cd\u6211\u7684\u610f\u89c1\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5509!\u5e7d\u5170\uff0c\u6709\u4e9b\u8bdd\uff0c\u662f\u5f88\u96be\u542f\u9f7f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u8fd9\u91cc\u6ca1\u6709\u522b\u4eba\uff0c\u53ea\u6709\u4f60\u6211\uff0c\u65e0\u8bba\u4ec0\u4e48\u8bdd\uff0c\u4f60\u5c3d\u7ba1\u8bf7\u8bf4\uff0c\u8bf4\u9519\u4e86\u4e5f\u4e0d\u8981\u7d27\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ee5\u6211\u4eec\u5f7c\u6b64\u7684\u7acb\u573a\u800c\u8a00\uff0c\u6211\u4eec\u662f\u5bf9\u7acb\u7684\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u4e3a\u4e86\u4f60\uff0c\u4e5f\u4e3a\u4e86\u5927\u59d0\uff0c\u6211\u4eec\u53ef\u4ee5\u5316\u654c\u4e3a\u53cb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u6837\u7b80\u5355\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u59d0\u592b\uff0c\u6211\u4e0d\u4fe1\u5929\u4e0b\u6709\u505a\u4e0d\u5230\u7684\u4e8b\u60c5\uff0c\u53ea\u8981\u5168\u529b\u4ee5\u8d74\u3002\u201d \u53e3\u6c14\u4e4b\u95f4\uff0c\u9690\u9690\u663e\u51fa\u5979\u7684\u5014\u5f3a\u3001\u4efb\u6027\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u5e7d\u5170\u4f60\u80fd\u6539\u53d8\u81ea\u5df1\u7684\u7236\u4eb2\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u7239\u5f88\u7231\u60dc\u6211\uff0c\u6211\u81ea\u5df1\u89c9\u5f97\u4e5f\u7b97\u5f97\u4e0a\u662f\u4e00\u4f4d\u5b5d\u987a\u7684\u5973\u513f\uff0c\u5728\u5bb6\u5ead\uff0c\u7239\u662f\u5f88\u6148\u7231\u7684\u7236\u4eb2\uff0c\u5927\u59d0\u7684\u5014\u5f3a\u3001\u4efb\u6027\uff0c\u8d85\u8fc7\u6211\u5341\u500d\uff0c\u4f46\u5979\u5728\u7b2c\u4e00\u5821\u4e2d\u5c45\u7136\u4f4f\u4e86\u4e0b\u6765\uff0c\u4e00\u4f4f\u6570\u5e74\u4e4b\u4e45\uff0c\u5c31\u51ed\u8fd9\u4e00\u70b9\uff0c\u5c31\u8bc1\u660e\u7239\u662f\u4e2a\u4ec1\u6148\u3001\u8bb2\u7406\u7684\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u662f\u4e2a\u597d\u7236\u4eb2\uff0c\u4e5f\u662f\u4e00\u4e2a\u548c\u6c14\u7684\u957f\u8005\uff0c\u53ef\u60dc\u7684\u662f\uff0c\u4ed6\u5728\u6c5f\u6e56\u4e0a\u7684\u4f5c\u4e3a\uff0c\u5374\u4e0d\u662f\u4e2a\u5f88\u4ec1\u6148\u7684\u4eba\u3002\u201d \u3000\u3000\u6697\u4e2d\u5374\u4e00\u76f4\u7559\u5fc3\u7740\u9ad8\u5e7d\u5170\u7684\u795e\u60c5\u53d8\u5316\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u6ca1\u6709\u751f\u6c14\u7684\u6837\u5b50\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u54e5\u54e5\u4e0d\u4e89\u6c14\uff0c\u6709\u4e9b\u80e1\u4f5c\u975e\u4e3a\uff0c\u4f46\u7239\u5bf9\u4ed6\u7684\u7ba1\u675f\u5f88\u4e25\u683c\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a \u201c\u7239\u592a\u7231\u670b\u53cb\uff0c\u4e5f\u8bb8\u4ea4\u7684\u4eba\u4e2d\uff0c\u6709\u4e9b\u826f\u83a0\u4e0d\u9f50\uff0c\u53ea\u8981\u4f60\u80af\u8bf4\u51fa\u6765\uff0c\u6211\u4fdd\u8bc1\u8ba9\u7239\u6539\u6b63\u8fc7\u6765\u3002\u201d \u3000\u3000\u5979\u80fd\u4f7f\u81ea\u5df1\u7684\u7236\u4eb2\uff0c\u5929\u4e0b\u7b2c\u4e00\u5821\u7684\u5821\u4e3b\u6539\u53d8\uff0c\u8fd9\u662f\u591a\u4e48\u5927\u7684\u53e3\u6c14\u3002 \u3000\u3000\u4f46\u9ad8\u5e7d\u5170\u5374\u8f7b\u8f7b\u677e\u677e\u7684\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u662f\u4e2a\u5145\u6ee1\u81ea\u4fe1\u7684\u5973\u5b69\u5b50\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u611f\u89c9\u5f97\u5230\uff0c\u5979\u5728\u6781\u529b\u5730\u6291\u5236\u81ea\u5df1\u7684\u6027\u60c5\uff0c\u4f7f\u521a\u70c8\u3001\u53d8\u6210\u6e29\u67d4\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u8f7b\u8f7b\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\uff0c\u5728\u4e0b\u4e0d\u5e94\u8be5\u8bf4\u51fa\u6765\uff0c\u4f46\u4e8b\u5b9e\u4e0a\uff0c\u5374\u53c8\u65e0\u6cd5\u4e0d\u8bf4\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u8bf7\u8bf4\uff0c\u6211\u4f1a\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u4ee4\u5c0a\u9664\u4e86\u7b2c\u4e00\u5821\u5e9e\u5927\u7684\u5b9e\u529b\u5916\uff0c\u8fd8\u9886\u5bfc\u4e86\u56db\u5927\u9556\u5c40\uff0c\u548c\u5f88\u591a\u4e2a\u9ed1\u9053\u7684\u7ec4\u7ec7\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u662f\u771f\u7684?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u771f\u7684\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u6211\u4e0d\u6e05\u695a\uff0c\u4f46\u6211\u76f8\u4fe1\u59d0\u592b\u7684\u8bdd\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5462?\u59d1\u5a18\u4e0d\u6c42\u8bc1\u4e00\u4e0b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u4f60\u6240\u8bf4\u90fd\u662f\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u8fd8\u77e5\u9053\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6700\u91cd\u8981\u7684\u662f\uff0c\u6211\u60f3\u77e5\u9053\uff0c\u7239\u548c\u59d0\u592b\u4e4b\u95f4\u7684\u4ec7\u6068\uff0c\u80fd\u4e0d\u80fd\u5316\u89e3?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u548c\u9ad8\u5821\u4e3b\u4e4b\u95f4\uff0c\u6ca1\u6709\u4e2a\u4eba\u7684\u4ec7\u6068\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u96be\u9053\u5728\u6c5f\u6e56\u4e0a\uff0c\u4f60\u4eec\u975e\u8981\u5bf9\u7acb\u4e0d\u53ef?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5ffd\u7136\u8b66\u89c9\uff0c\u6697\u5fd6\u9053\uff1a\u5982\u82e5\u8fd9\u6837\u4ea4\u8c08\u4e0b\u53bb\uff0c\u6211\u4f1a\u4e0d\u77e5\u4e0d\u89c9\u7684\u628a\u5fc3\u4e8b\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0e\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u6709\u4e9b\u4e8b\uff0c\u5728\u4e0b\u53ea\u662f\u542c\u5230\u4e00\u4e9b\u98ce\u58f0\uff0c\u4f46\u5374\u4e0d\u80fd\u8bf4\u51fa\u5168\u90e8\u7684\u5185\u60c5\uff0c\u4e5f\u65e0\u6cd5\u4e3e\u51fa\u8bc1\u660e\uff0c\u59d1\u5a18\u662f\u806a\u660e\u4eba\uff0c\u4f55\u4e0d\u81ea\u5df1\u53bb\u6c42\u8bc1\u4e00\u4e0b\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u7239\u5e38\u5e74\u5728\u5916\uff0c\u7a76\u7adf\u505a\u4e86\u4ec0\u4e48\u4e8b\uff0c\u6211\u4e5f\u4e0d\u660e\u767d\uff0c\u6211\u5e94\u8be5\u53bb\u4e86\u89e3\u4e00\u4e0b\u4e86\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u9053\uff1a\u201c\u5927\u59d0\u5462?\u662f\u4e0d\u662f\u77e5\u9053\u4e00\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u548c\u5979\u4ece\u6765\u6ca1\u6709\u8c08\u8fc7\u8fd9\u4e9b\u4e8b\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u5434\u53cc\u5973\u7f13\u6b65\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a \u201c\u5927\u59d0\uff0c\u5c0f\u59b9\u6709\u70b9\u4e8b\u7279\u6765\u5411\u59d0\u592b\u8bf7\u6559\u3002\u201d \u5434\u53cc\u5973\u7b11\u9053\uff1a\u201c\u597d!\u4f60\u4eec\u5e94\u8be5\u597d\u597d\u7684\u804a\u804a\u624d\u662f\uff0c\u5bd2\u8863\u4e00\u4e2a\u4eba\uff0c\u5728\u8fd9\u91cc\u4e5f\u662f\u5bc2\u5bde\u5f97\u5f88\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5411\u59d0\u592b\u8bf7\u6559\u6765\u7684\uff0c\u5c0f\u59b9\u5f88\u5c11\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u6709\u7740\u5750\u4e95\u89c2\u5929\u7684\u611f\u89c9\uff0c\u7279\u5730\u5411\u59d0\u592b\u8bf7\u6559\u4e00\u4e9b\u6c5f\u6e56\u4e0a\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u53ef\u60dc\uff0c\u5728\u4e0b\u7684\u9605\u5386\u4e5f\u4e0d\u4e30\u5bcc\u3002\u80fd\u591f\u544a\u8bc9\u4f60\u7684\uff0c\u5b9e\u5728\u592a\u5c11\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u59d0\u592b\u5ba2\u6c14\uff0c\u5c0f\u59b9\u5df2\u7ecf\u83b7\u76ca\u4e0d\u6d45\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u518d\u966a\u966a\u4f60\u59d0\u592b\u804a\u804a\uff0c\u6211\u53bb\u529e\u70b9\u4e8b\u60c5\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d!\u6211\u8fd8\u5f97\u53bb\u66ff\u4f60\u4eec\u5b89\u6392\u4e00\u70b9\u4e8b\u60c5\u5462\u3002\u201d \u3000\u3000\u4e3e\u6b65\u5411\u5916\u8d70\u53bb\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e5f\u6ca1\u6709\u575a\u6301\u7559\u5979\u3002 \u3000\u3000\u76ee\u7779\u9ad8\u5e7d\u5170\u79bb\u53bb\u4e4b\u540e\uff0c\u5434\u53cc\u5973\u624d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5bd2\u8863\uff0c\u5979\u548c\u4f60\u8bf4\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u95ee\u8fc7\u9ad8\u5821\u4e3b\u7684\u4e8b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u63d0\u5230\u522b\u7684\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6709\uff0c\u5979\u529d\u6211\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u7b54\u5e94\u4e86\u6ca1\u6709?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u4f55\u90ce\uff0c\u8fd9\u4e24\u5929\u597d\u597d\u4f11\u606f\uff0c\u5a5a\u793c\u53ea\u4e0d\u8fc7\u662f\u4e00\u4e2a\u5f62\u5f0f\uff0c\u6211\u4eec\u5df2\u6709\u4e86\u540d\u4efd\u4e5f\u6709\u4e86\u592b\u59bb\u4e4b\u5b9e\uff0c \u4f60\u65e2\u7136\u575a\u6301\u8981\u8d70\uff0c\u6211\u4e5f\u4e0d\u60f3\u518d\u529d\u4f60\u7559\u4e0b\u4e86\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a \u201c\u9ad8\u5821\u4e3b\u4e0d\u662f\u5df2\u7ecf\u7b54\u5e94\u54b1\u4eec\uff0c\u884c\u8fc7\u5a5a\u793c\u4e4b\u540e\uff0c\u54b1\u4eec\u5c31\u53ef\u4ee5\u79bb\u5f00\u4e86\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u5509!\u4f55\u90ce\uff0c\u54b1\u4eec\u4eca\u665a\u4e0a\u8d70\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4eca\u665a\u4e0a\u8d70\uff0c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u9ad8\u5821\u4e3b\u771f\u7684\u4f1a\u653e\u54b1\u4eec\u8d70\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u600e\u4e48?\u96be\u9053\u4ed6\u8bf4\u4e86\u4e0d\u7b97?\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4e0d\u80fd\u602a\u4ed6\uff0c\u522b\u5fd8\u4e86\uff0c \u4f60\u8fdb\u4e86\u7b2c\u4e00\u5821\uff0c\u5e26\u4e86\u4ed6\u4e00\u4e2a\u5973\u513f\u8dd1\uff0c\u800c\u4e14\u8fd9\u5973\u513f\u53c8\u662f\u4ed6\u7684\u5f97\u529b\u52a9\u624b\u4e4b\u4e00\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u4f60\u662f\u8bf4\uff0c\u9ad8\u5821\u4e3b\u4f1a\u5bf9\u6211\u4eec\u6709\u4ec0\u4e48\u4e0d\u5229\u7684\u4e3e\u52a8\u5417\uff1f\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u80fd\u4e0d\u9632\uff0c\u6211\u77e5\u9053\u4e49\u7236\u7684\u4e3a\u4eba\uff0c\u4e5f\u77e5\u9053\u6211\u5728\u4ed6\u5fc3\u76ee\u4e2d\u7684\u4efd\u91cf\uff0c\u6211\u7559\u5728\u7b2c\u4e00\u5821\u4ed6\u4f1a\u771f\u7684\u5f88\u5ba0\u6211\uff0c\u4f46\u6211\u79bb\u5f00\u4e86\u7b2c\u4e00\u5821\uff0c\u4ed6\u4f1a\u9664\u4e86\u6211\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u96be\u9053\uff0c\u9ad8\u5929\u5065\u8fd9\u4e9b\u4e60\u6027\uff0c\u9ad8\u5e7d\u5170\u771f\u7684\u4e0d\u77e5\u9053\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5979\u53ef\u80fd\u771f\u7684\u4e0d\u77e5\u9053\uff0c\u56e0\u4e3a\uff0c\u9ad8\u5929\u5065\u9690\u85cf\u5f97\u5f88\u597d\uff0c\u9ad8\u5e7d\u5170\u827a\u6210\u4e4b\u540e\uff0c\u5c31\u5e2e\u5979\u7236\u4eb2\u5904\u7406\u7b2c\u4e00\u5821\u7684\u4e8b\u52a1\uff0c\u5979\u7edd\u9876\u806a\u660e\uff0c\u53c8\u591a\u667a\u7565\uff0c\u552f\u4e00\u7f3a\u4e4f\u7684\u662f\u6c5f\u6e56\u7ecf\u9a8c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u9ad8\u5e7d\u5170\u5982\u679c\u77e5\u9053\u5979\u7236\u4eb2\u7684\u4e3a\u4eba\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u662f\u4e2a\u597d\u7236\u4eb2\uff0c\u9ad8\u5e7d\u5170\u4e5f\u662f\u4e00\u4e2a\u5f88\u5b5d\u987a\u7684\u5973\u513f\uff0c\u771f\u8981\u4e34\u5230\u5175\u5203\u76f8\u89c1\uff0c\u53ea\u6015\u5979\u4eec\u7236\u5973\uff0c\u8fd8\u662f\u4f1a\u5408\u624b\u4e00\u5904\u5462\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u9ad8\u5e7d\u5170\u4f1a\u5168\u529b\u52a9\u7236\u4e3a\u6076\u4e86\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9\u4ed8\u9ad8\u5e7d\u5170\uff0c\u53ea\u6709\u4e00\u4e2a\u529e\u6cd5\uff0c\u5f88\u53ef\u60dc\uff0c\u4f60\u5374\u4e0d\u542c\u6211\u7684\u8bdd\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u529e\u6cd5?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5a36\u4e86\u5979\uff0c\u5979\u5c31\u4f1a\u50cf\u6211\u4e00\u6837\uff0c\u670d\u670d\u8d34\u8d34\u7684\u8ddf\u7740\u4f60\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u6211\u5df2\u7ecf\u662f\u4e2a\u4f7f\u541b\u6709\u5987\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ef\u662f\uff0c\u6211\u540c\u610f\u4f60\u518d\u5a36\u4e2a\u8001\u5a46\u5440!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53ea\u6015\u6211\u65e0\u798f\u6d88\u53d7\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u6211\u662f\u8bda\u5fc3\u8bda\u610f\u7684\uff0c\u4e00\u65b9\u9762\uff0c\u7b97\u6211\u5bf9\u4f60\u7684\u8865\u507f\uff0c\u7b2c\u4e8c\uff0c\u9ad8\u5e7d\u5170\u4f1a\u5bf9\u4f60\u6709\u5f88\u5927\u7684\u5e2e\u52a9\uff0c\u4f46\u6211\u4e5f\u4e0d\u613f\u4e3a\u6b64\u4e8b\uff0c\u4f7f\u6211\u4eec\u4e4b\u95f4\u6709\u592a\u5f3a\u70c8\u7684\u4e89\u6267\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53cc\u5973\uff0c\u9ad8\u5e7d\u5170\u771f\u7684\u5f88\u91cd\u8981\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ee5\u540e\uff0c\u4f1a\u6709\u8bc1\u660e?\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f11\u606f\u4e00\u4e0b\u5427!\u4e8c\u66f4\u65f6\uff0c\u6211\u4eec\u52a8\u8eab\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90fd\u51c6\u5907\u597d\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u4e0d\u8981\u60f3\u5f97\u592a\u5982\u610f\uff0c\u6211\u4eec\u79bb\u5f00\uff0c\u4e0d\u4f1a\u5f88\u987a\u5229\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u9ad8\u5929\u5065\u4f1a\u622a\u6740\u6211\u4eec?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f46\u613f\u6211\u7684\u63a8\u60f3\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u82e5\u6211\u7b54\u5e94\u4ed6\u4eec\u7559\u5728\u7b2c\u4e00\u5821\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ed6\u4eec\u4f1a\u628a\u54b1\u4eec\u5f85\u5982\u4e0a\u5bbe\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u73b0\u5728\uff0c\u5982\u7528\u70b9\u6743\u672f\uff0c\u6539\u53d8\u4e3b\u610f\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6765\u4e0d\u53ca\uff0c\u90a3\u4e0d\u4f46\u4f1a\u88ab\u4eba\u62c6\u7a7f\uff0c\u800c\u4e14\uff0c\u8fd8\u6709\u635f\u4f60\u7684\u82f1\u540d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8bba\u6c5f\u6e56\u4e0a\u7684\u9605\u5386\u3001\u7ecf\u9a8c\uff0c\u5728\u4e0b\u5b9e\u5728\u662f\u592a\u5dee\u4e86\u4e00\u4e9b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u4e0d\u8981\u81ea\u8d23\uff0c\u9ad8\u5929\u5065\u8fd9\u4e48\u505a\uff0c\u8fde\u6211\u4e5f\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u521a\u5f00\u59cb\uff0c\u6211\u4e5f\u662f\u8ba4\u4e3a\u4ed6\u662f\u771f\u7684\u8981\u66ff\u6211\u4eec\u529e\u559c\u4e8b\uff0c\u6240\u4ee5\uff0c\u6211\u4e5f\u6ca1\u6709\u6212\u5907\uff0c\u521a\u624d\uff0c\u624d\u53d1\u89c9\u60c5\u52bf\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u53d1\u89c9\u4e86\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u795e\u5200\u5802\u4e2d\u4eba\uff0c\u51fa\u52a8\u4e86\u5f88\u591a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ed6\u4eec\u8981\u600e\u6837?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u795e\u5200\u5802\u4e2d\u4eba\u51fa\u52a8\uff0c\u9664\u4e86\u5bf9\u4ed8\u6211\u4eec\u4e4b\u5916\uff0c\u5b9e\u5728\u60f3\u4e0d\u5230\u5176\u4ed6\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u2014\u987f\u53c8\u9053\uff1a\u201c\u4ed6\u4eec\u5728\u9690\u79d8\u4e2d\u884c\u4e8b\uff0c\u6211\u672c\u6765\u4e5f\u672a\u6ce8\u610f\uff0c\u662f\u96ea\u6885\u90a3\u4e2b\u5934\u70b9\u4e86\u6211\u2014\u4e0b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u5934\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u96ea\u6885\u7684\u8eab\u4efd\uff0c\u4e07\u4e07\u4e0d\u80fd\u66b4\u9732\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u96ea\u6885\u4f1a\u5e2e\u6211\u4eec?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e5f\u8bb8\u5979\u662f\u65e0\u610f\uff0c\u4f46\u5979\u786e\u5b9e\u5e2e\u4e86\u6211\u4eec\u5f88\u5927\u7684\u5fd9\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u662f\u6709\u610f\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u592a\u53ef\u80fd\uff0c\u5979\u662f\u9ad8\u5e7d\u5170\u7684\u5fc3\u8179\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u54b1\u4eec\u5012\u662f\u4e0d\u80fd\u5bb3\u5979\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u81ea\u7136\u4e0d\u80fd\u5bb3\u5979\uff0c\u5979\u5e2e\u4e86\u54b1\u4eec\u7684\u5fd9\uff0c\u54b1\u4eec\u5e94\u8be5\u611f\u6fc0\u5979\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6700\u597d\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u4e0d\u628a\u5979\u7275\u8fdb\u53bb\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u53bb\u5750\u606f\u4e00\u9635\uff0c\u4f60\u4e5f\u8be5\u4f11\u606f\u4e00\u4e0b\uff0c\u5982\u662f\u4f60\u63a8\u65ad\u7684\u4e0d\u9519\uff0c\u53ea\u6015\u665a\u4e0a\u8fd8\u8981\u6709\u4e00\u573a\u8840\u6218\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6700\u597d\u518d\u590d\u4e60\u4e00\u4e0b\u6211\u4f20\u4f60\u90a3\u4e24\u62db\u5251\u6cd5\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u9047\u4e0a\u9ad8\u5e7d\u5170\u3002\u201d \u3000 \u7b2c \u4e5d \u56de\u3000\u795e\u5200\u5802 \u3000 \u3000\u3000\u5929\u5230\u521d\u66f4\u3002 \u3000\u3000\u4eca\u591c\u65e0\u6708\uff0c\u4f46\u4ea6\u65e0\u4e91\uff0c\u6ee1\u5929\u7e41\u661f\u95ea\u70c1\u3002 \u3000\u3000\u5434\u53cc\u5973\u6362\u4e86\u4e00\u8eab\u6de1\u84dd\u8272\u7684\u52b2\u88c5\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e5f\u7a7f\u7740\u75be\u670d\uff0c\u5e26\u4e0a\u4e86\u91d1\u5251\uff0c\u98de\u8f6e\u3002 \u3000\u3000\u5434\u53cc\u5973\u8d70\u5728\u524d\u9762\uff0c\u4f55\u5bd2\u8863\u76f8\u968f\u8eab\u540e\uff0c\u4e24\u4e2a\u4eba\u4fdd\u6301\u4e86\u4e94\u5c3a\u4ee5\u4e0a\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u5979\u5728\u5821\u4e2d\u4f4f\u4e86\u6570\u5e74\uff0c\u5bf9\u5821\u4e2d\u5730\u5f62\u548c\u9632\u536b\u60c5\u4e8b\uff0c\u5341\u5206\u4e86\u89e3\uff0c\u907f\u5f00\u4e86\u5c97\u54e8\u800c\u884c\u3002 \u3000\u3000\u4e00\u53e3\u6c14\u884c\u5230\u57ce\u5821\u524d\u9762\uff0c\u90fd\u672a\u9047\u4e0a\u963b\u88ad\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4f4e\u58f0\u9053\uff1a\u201c\u770b\u6765\uff0c\u4ed6\u4eec\u6ca1\u6709\u4ec0\u4e48\u9632\u5907\u3002\u201d \u3000\u3000\u8bed\u58f0\u521a\u843d\uff0c\u8033\u9645\u95f4\u5df2\u54cd\u8d77\u4e86\u4e00\u4e2a\u6e05\u51b7\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u5927\u5c0f\u59d0\uff0c\u51fa\u5821\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u8eab\u7740\u6df1\u84dd\u957f\u888d\u7684\u4eba\uff0c\u5e26\u7740\u56db\u4e2a\u9ed1\u8863\u5927\u6c49\uff0c\u2014\u6392\u6a2a\u7acb\u3002 \u3000\u3000\u5434\u53cc\u5973\u5f88\u6c89\u7740\uff0c\u62db\u62db\u624b\uff0c\u8981\u4f55\u5bd2\u8863\u884c\u5230\u5979\u7684\u8eab\u4fa7\uff0c\u7136\u540e\uff0c\u624d\u6253\u91cf\u53d1\u8bdd\u4eba\u4e00\u9635\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u90d1\u603b\u7ba1\u3002\u201d \u3000\u3000\u84dd\u888d\u4eba\u9053\uff1a\u201c\u90d1\u98de\u89c1\u8fc7\u5927\u5c0f\u59d0\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5927\u603b\u7ba1\u5e26\u5934\u62e6\u6b64\uff0c\u662f\u4f55\u7528\u5fc3\u3002\u201d \u3000\u3000\u90d1\u603b\u7ba1\u9053\uff1a\u201c\u5728\u4e0b\u5949\u5821\u4e3b\u4e4b\u547d\uff0c\u4e25\u5b88\u95e8\u6237\uff0c\u4e0d\u51c6\u5978\u7ec6\u6df7\u5165\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5978\u7ec6\u5427?\u201d \u3000\u3000\u90d1\u98de\u9053\uff1a\u201c\u5927\u5c0f\u59d0\u8bf4\u7b11\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u95ea\u5f00\u4e9b\uff0c\u6211\u8981\u51fa\u5821\u3002\u201d \u3000\u3000\u90d1\u98de\u9053\uff1a\u201c\u5927\u5c0f\u59d0\uff0c\u53ea\u6015\u5f88\u56f0\u96be\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u8138\u4e0a\u95ea\u63a0\u4e00\u62b9\u6740\u673a\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48?\u201d \u3000\u3000\u90d1\u98de\u9053\uff1a\u201c\u6211\u8bf4\uff0c\u53ea\u6015\u5f88\u56f0\u96be?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90d1\u98de\u4f60\u662f\u4e0d\u60f3\u6d3b\u4e86\u3002\u201d \u3000\u3000\u90d1\u98de\u9053\uff1a\u201c\u5728\u4e0b\u5982\u82e5\u4e0d\u60f3\u6d3b\u4e86\uff0c\u4e5f\u4e0d\u4f1a\u62e6\u963b\u5927\u5c0f\u59d0\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48?\u201d \u3000\u3000\u90d1\u98de\u9053\uff1a \u201c\u4e00\u53e5\u8bdd\u53ef\u4ee5\u89e3\u8bf4\u6e05\u695a\uff0c\u6211\u662f\u5949\u547d\u800c\u6765\uff0c\u5927\u5c0f\u59d0\u82e5\u8981\u6740\u6211\uff0c\u5728\u4e0b\u4e5f\u53ea\u6709\u8ba4\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5c31\u51ed\u4f60\u90d1\u603b\u7ba1\u90a3\u70b9\u6210\u5c31\uff0c\u5c31\u80fd\u62e6\u5f97\u4f4f\u6211?\u201d \u3000\u3000\u90d1\u98de\u9053\uff1a\u201c\u5728\u4e0b\u81ea\u77e5\u975e\u654c\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4f60\u5982\u4e0d\u80af\u8ba9\u8def\uff0c\u9664\u4e86\u6740\u4f60\u4e4b\u5916\uff0c\u6211\u5c31\u60f3\u4e0d\u51fa\u522b\u7684\u529e\u6cd5\u4e86\u3002\u201d \u90d1\u98de\u9053\uff1a\u201c\u59d1\u5a18\u5e94\u8be5\u770b\u5230\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u770b\u5230\u4e86\uff0c\u5c31\u662f\u90a3\u4e9b\u7a7f\u9ed1\u8863\u7684\u4eba\u3002\u201d \u3000\u3000\u90d1\u98de\u9053\uff1a\u201c\u4ed6\u4eec\u6765\u81ea\u795e\u5200\u5802\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d!\u4f60\u8ba9\u5f00\u3002\u201d \u3000\u3000\u90d1\u98de\u8fd9\u4e00\u6b21\uff0c\u5012\u662f\u542c\u8bdd\u7684\u5f88\uff0c\u4f9d\u8a00\u95ea\u5230\u65c1\u4fa7\u3002 \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u4eba\uff0c\u5ffd\u7136\u4e00\u62ac\u53f3\u624b\uff0c\u4eae\u51fa\u56db\u628a\u4e0d\u540c\u7684\u5200\u3002 \u3000\u3000\u4e00\u652f\u7ec6\u957f\uff0c\u5f62\u5982\u67f3\u6708\u7684\u67f3\u53f6\u5200\uff0c\u4e00\u628a\u957f\u4e0d\u8fc7\u4e94\u5c3a\u7684\u5bbd\u9762\u5200\u3002 \u3000\u3000\u4e00\u628a\u602a\u5f62\u602a\u72b6\u7684\u53cc\u5203\u5200\u3002\u4e00\u628a\u5f62\u5982\u79cb\u6708\uff0c\u56db\u9762\u90fd\u662f\u950b\u5203\u7684\u5706\u9762\u5200\u3002 \u3000\u3000\u56db\u628a\u602a\u5200\uff0c\u56db\u4e2a\u9ed1\u8863\u4eba\u5206\u7ad9\u6210\u56db\u4e2a\u4e0d\u540c\u7684\u65b9\u4f4d\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6697\u4f5c\u6212\u5907\uff0c\u53f3\u624b\u5df2\u63e1\u4f4f\u4e86\u91d1\u5251\u7684\u5251\u67c4\u3002 \u3000\u3000\u4f46\u5434\u53cc\u5973\u5374\u6ca1\u6709\u8981\u4ed6\u51fa\u624b\u7684\u610f\u601d\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u53ea\u597d\u9759\u9759\u7684\u7ad9\u7740\u3002 \u3000\u3000\u671b\u4e86\u56db\u4e2a\u9ed1\u8863\u4eba\u4e00\u773c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u542c\u8bf4\u8fc7\u795e\u5200\u5802\u521b\u51fa\u4e86\u767e\u5200\u5947\u62db\uff0c\u73b0\u5728\uff0c\u6211\u624d\u89c1\u5230\u56db\u79cd\u5200\u3002\u201d \u3000\u3000\u5979\u660e\u663e\u7684\u662f\u5e0c\u671b\u7528\u8bdd\u6765\u5f15\u8bf1\u56db\u4e2a\u4eba\u4e2d\u4efb\u4f55\u4e00\u4e2a\u5f00\u53e3\u3002 \u3000\u3000\u4f46\u4ed6\u5f88\u5931\u671b\uff0c\u56db\u4e2a\u4eba\u53ea\u662f\u9759\u9759\u7684\u7ad9\u7740\u3002 \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u4eba\uff0c\u5927\u90e8\u5206\u7684\u76ee\u5149\uff0c\u90fd\u6295\u6ce8\u5728\u56db\u4e2a\u4eba\u624b\u4e2d\u7684\u5200\u4e0a\uff0c\u53ea\u6709\u4e00\u5c0f\u90e8\u5206\u7684\u6ce8\u610f\u529b\uff0c\u5206\u6ce8\u5434\u53cc\u5973\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u4eba\u7684\u8138\u4e0a\uff0c\u6cdb\u73b0\u51fa\u65e0\u6bd4\u7684\u8654\u8bda\u3002 \u3000\u3000\u597d\u8c61\u624b\u4e2d\u4e4b\u5200\uff0c\u662f\u4ed6\u4eec\u7684\u59bb\u513f\uff0c\u662f\u4ed6\u4eec\u7684\u751f\u547d\u3002 \u3000\u3000\u90a3\u662f\u5b66\u5200\u4eba\u6781\u9ad8\u7684\u6210\u5c31\u3002\u628a\u81ea\u5df1\u7684\u7cbe\u529b\u548c\u624b\u4e2d\u7684\u5200\uff0c\u53ef\u6eb6\u4e8e\u4e00\u5904\u3002 \u3000\u3000\u5434\u53cc\u5973\u5ffd\u7136\u6709\u6240\u8b66\u89c9\u3002\u53eb\u9053\uff1a\u201c\u5411\u540e\u9000\u3002\u201d \u3000\u3000\u559d\u58f0\u4e2d\uff0c\u4eba\u5df2\u5411\u540e\u98de\u8dc3\u800c\u9000\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e5f\u4e00\u76f4\u4fdd\u6301\u7740\u9ad8\u5ea6\u7684\u8b66\u89c9\u3002\u95fb\u58f0\u5411\u540e\u66b4\u9000\u4e00\u4e08\u3002 \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u4eba\u4ecd\u7136\u6ce8\u89c6\u7740\u5200\uff0c \u3000\u3000\u4f55\u5bd2\u8863\u4f4e\u58f0\u9053\uff1a\u201c\u53cc\u5973\uff0c\u600e\u4e48\u56de\u4e8b?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u795e\u5200\u5802\u4e2d\u7684\u6b7b\u58eb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u600e\u4e48\u89e3\u91ca\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u795e\u5200\u5802\u5982\u4f55\u8bad\u7ec3\u51fa\u8fd9\u6837\u7684\u4eba\uff0c\u6211\u4e0d\u6e05\u695a\uff0c\u4f46\u6211\u611f\u89c9\u5230\u4ed6\u4eec\u8eab\u4e0a\u6563\u5e03\u51fa\u6d53\u91cd\u7684\u6740\u6c14\uff0c\u53ea\u8981\u4ed6\u4eec\u64cd\u5200\u4e00\u51fb\uff0c\u5c31\u4f1a\u6709\u77f3\u7834\u5929\u60ca\u7684\u529b\u91cf\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u4e5f\u65e0\u6cd5\u7834\u89e3?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4ed6\u4eec\u5f88\u53ef\u80fd\u5168\u8eab\u7684\u529f\u529b\uff0c\u90fd\u51dd\u805a\u5728\u90a3\u4e00\u5200\u4e0a\uff0c\u5982\u82e5\u8981\u786c\u63a5\u4ed6\u4eec\u7684\u5200\u52bf\uff0c\u53ea\u6015\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u5e94\u8be5\u5982\u4f55? \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6700\u597d\u4e0d\u548c\u4ed6\u4eec\u8fd1\u8eab\u76f8\u640f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ed6\u4eec\u6765\u4e86\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u56db\u4e2a\u9ed1\u8863\u4eba\uff0c\u90fd\u626c\u8d77\u4e86\u624b\u4e2d\u4e4b\u5200\uff0c\u7f13\u7f13\u5411\u4e24\u4eba\u903c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5feb\u5411\u540e\u9000\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u4f9d\u8a00\u5411\u540e\u9000\u53bb\u3002 \u3000\u3000\u4f46\u5434\u53cc\u5973\u5374\u672a\u540e\u9000\uff0c\u53cd\u5411\u524d\u9762\u8fce\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u4eba\uff0c\u7a81\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u516b\u9053\u76ee\u5149\uff0c\u51dd\u6ce8\u5728\u5434\u53cc\u5973\u8eab\u4e0a\u3002 \u3000\u3000\u5434\u53cc\u5973\u795e\u60c5\u51b7\u8083\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8ba4\u8bc6\u6211\u5417?\u201d \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u4eba\u6447\u6447\u5934\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90d1\u603b\u7ba1\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b?\u201d \u3000\u3000\u90d1\u98de\u9053\uff1a\u201c\u5927\u5c0f\u59d0\uff0c\u6211\u771f\u7684\u4e0d\u6e05\u695a\uff0c\u7b2c\u4e00\u5821\u4e2d\u7684\u4eba\uff0c\u90fd\u8ba4\u8bc6\u5927\u5c0f\u59d0\uff0c\u4e5f\u90fd\u5bf9\u5927\u5c0f\u59d0\u5341\u5206\u656c\u754f\uff0c\u5728\u4e0b\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u51b7\u51b7\u63a5\u9053\uff1a \u201c\u5e9f\u8bdd\u592a\u591a\u4e86\uff0c\u6211\u95ee\u4f60\u7684\u662f\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u8ba4\u8bc6\u6211\u3002\u201d \u90d1\u98de\u9053\uff1a\u201c\u5927\u5c0f\u59d0\uff0c\u7b2c\u4e00\u5821\u4e2d\u7684\u4eba\uff0c\u6211\u4e0d\u8ba4\u8bc6\u7684\u5f88\u5c11\uff0c\u4f46\u8fd9\u56db\u4e2a\u4eba\uff0c\u6211\u5374\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f46\u4f60\u5374\u5e26\u7740\u4ed6\u4eec\u6765\u6740\u6211\u3002\u201d \u3000\u3000\u90d1\u98de\u5bf9\u5434\u53cc\u5973\uff0c\u4f3c\u4e4e\u6709\u7740\u6781\u7aef\u7684\u754f\u60e7\uff0c\u867d\u7136\u662f\u654c\u5bf9\u76f8\u5904\uff0c\u4f46\u4ecd\u7136\u5bf9\u5979\u4fdd\u6301\u5341\u5206\u7684\u5c0a\u656c\u3002 \u3000\u3000\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u5949\u547d\u884c\u4e8b\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a \u201c\u91cf\u4f60\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u80c6\u5b50\uff0c\u662f\u4ec0\u4e48\u4eba\u4e0b\u4ee4\u7ed9\u4f60\uff1f\u662f\u4e0d\u662f\u5821\u4e3b?\u201d \u3000\u3000\u90d1\u98de\u6447\u6447\u5934\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u662f\u9ad8\u5e7d\u5170\u4e86?\u201d \u3000\u3000\u90d1\u98de\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u4e8c\u5c0f\u59d0\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u4eec\u4e24\u4e2a\u4eba\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u90d1\u98de\u9053\uff1a\u201c\u795e\u5200\u5802\u4e3b\u3002\u201d \u3000\u3000\u5979\u4e00\u9762\u548c\u90d1\u98de\u4ea4\u8c08\uff0c\u4e00\u9762\u7559\u5fc3\u90a3\u56db\u4e2a\u9ed1\u8863\u4eba\u7684\u4e3e\u52a8\u3002 \u3000\u3000\u53d1\u89c9\u4e86\u56db\u4e2a\u6267\u5200\u9ed1\u8863\u4eba\uff0c\u5e76\u6ca1\u6709\u51fa\u624b\u5411\u5979\u653b\u51fb\uff0c\u4f3c\u4e4e\u5728\u7b49\u5f85\u7740\u4ec0\u4e48? \u3000\u3000\u5434\u53cc\u5973\u5f88\u5feb\u7684\u60f3\u901a\u4e86\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u9ed1\u8863\u5200\u624b\u7684\u76ee\u7684\uff0c\u53ea\u662f\u963b\u62e6\u5979\u79bb\u5f00\u7b2c\u4e00\u5821\u3002 \u3000\u3000\u5982\u82e5\u5979\u73b0\u5728\u8fd4\u56de\u5f69\u8679\u697c\uff0c\u9000\u5230\u4e86\u4e00\u5b9a\u7684\u9650\u8ddd\u4e4b\u5185\uff0c\u8fd9\u56db\u4e2a\u4eba\u5c31\u4e0d\u4f1a\u518d\u76ef\u6ce8\u5979\u3002\u5979\u662f\u5927\u884c\u5bb6\uff0c\u4e00\u773c\u4e4b\u95f4\uff0c\u5c31\u77a7\u51fa\u6765\uff0c\u8fd9\u56db\u4e2a\u9ed1\u8863\u6c49\uff0c\u4e0d\u4f46\u5200\u6cd5\u5947\u5e7b\uff0c\u7ec3\u6b66\u6709\u6210\uff0c\u4e5f\u5b8c\u5168\u8d70\u7684\u662f\u8be1\u5f02\u8def\u5b50\u3002 \u3000\u3000\u5979\u4e5f\u770b\u51fa\u6765\u4e86\uff0c\u8fd9\u56db\u4e2a\u4eba\u53d7\u8fc7\u7279\u6b8a\u7684\u7981\u5236\uff0c\u90a3\u4f1a\u4f7f\u4ed6\u4eec\u5728\u6b66\u529f\u4e0a\uff0c\u6709\u7740\u6781\u9ad8\u7684\u6210\u5c31\uff0c\u4f46\u5728\u667a\u6167\u4e0a\uff0c\u5374\u6709\u4e86\u5f88\u5927\u7684\u635f\u4f24\u3002 \u3000\u3000\u5434\u53cc\u5973\u5bf9\u8fd9\u79cd\u6b66\u529f\uff0c\u6709\u7740\u5f88\u591a\u7684\u4e86\u89e3\uff0c\u5979\u660e\u767d\u8fd9\u56db\u4e2a\u9ed1\u8863\u4eba\uff0c\u51fa\u5200\u4e00\u51fb\uff0c\u5f88\u53ef\u80fd\u77f3\u7834\u5929\u60ca\u3002 \u3000\u3000\u5979\u62c5\u5fc3\u81ea\u5df1\u63a5\u4e0d\u4e0b\u8fd9\u4e00\u5200\uff0c\u66f4\u5206\u5fc3\u4e8e\u4f55\u5bd2\u8863\u65e0\u80fd\u627f\u53d7\u8fd9\u4e00\u51fb\u3002 \u3000\u3000\u5434\u53cc\u5973\u671b\u671b\u90d1\u98de\uff0c\u9053\uff1b\u201c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u51fa\u624b?\u201d \u3000\u3000\u90d1\u98de\u9053\uff1b\u201c\u5927\u5c0f\u59d0\u8ba4\u4e3a\u6211\u80fd\u6307\u6325\u4ed6\u4eec\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u7a81\u7136\u4e00\u626c\u53f3\u624b\uff0c\u4e00\u7f15\u6307\u98ce\uff0c\u75be\u5feb\u7684\u5c04\u51fa\u53bb\u3002 \u3000\u3000\u4e00\u4e2a\u9ed1\u8863\u4eba\u5e94\u58f0\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u53e6\u5916\u4e09\u4e2a\u9ed1\u8863\u4eba\u6014\u4e86\u4e00\u6014\uff0c \u76ee\u5149\u8f6c\u6ce8\u5230\u5434\u53cc\u5973\u7684\u8eab\u4e0a\u3002 \u5434\u53cc\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e09\u4f4d\u53ef\u4ee5\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u5ffd\u7136\u4e4b\u95f4\uff0c\u8eac\u8eab\u5411\u524d\u51b2\u53bb\u3002 \u3000\u3000\u4e09\u4e2a\u9ed1\u8863\u4eba\u7684\u53cd\u5e94\uff0c\u4f3c\u4e4e\u662f\u5341\u5206\u8fdf\u987f\uff0c\u5434\u53cc\u5973\u51b2\u4e86\u8fc7\u53bb\uff0c\u4e09\u4e2a\u4eba\u7adf\u6ca1\u6709\u4e3e\u52a8\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e00\u63a0\u800c\u8fc7\uff0c\u53c8\u4e00\u4e2a\u9ed1\u8863\u4eba\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4f59\u4e0b\u4e24\u4e2a\u9ed1\u8863\u4eba\uff0c\u4ecd\u7136\u6709\u4e9b\u72b9\u8c6b\uff0c\u4f46\u5434\u53cc\u5973\u5374\u4e0b\u624b\u65e0\u60c5\uff0c\u53ea\u89c1\u5979\u53cc\u624b\u4e00\u6325\uff0c\u4e24\u4e2a\u9ed1\u8863\u4eba\u53c8\u5012\u5b50\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u4e48\u6837\u7b80\u7b80\u5355\u5355\u7684\u89e3\u51b3\u4e86\u56db\u4e2a\u9ed1\u8863\u4eba\uff0c\u8fde\u5434\u53cc\u5973\u4e5f\u6709\u7740\u610f\u5916\u7684\u611f\u89c9\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a \u3000\u3000\u201c\u90d1\u98de\uff0c\u4ed6\u4eec\u662f\u600e\u4e48\u56de\u4e8b?\u201d \u3000\u3000\u90d1\u98de\u6447\u6447\u5934\u3002 \u3000\u3000\u5434\u53cc\u5973\u671b\u671b\u56db\u4e2a\u8eba\u5728\u5730\u4e0a\u7684\u9ed1\u8863\u4eba\uff0c\u9053\uff1a \u201c\u6211\u53ea\u70b9\u4e86\u4ed6\u4eec\u7684\u7a74\u9053\uff0c\u56db\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u4ed6\u4eec\u5e94\u8be5\u6e05\u9192\u8fc7\u6765\u3002\u201d \u3000\u3000\u90d1\u98de\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90d1\u98de\uff0c\u4f60\u73b0\u5728\u8fd8\u6709\u4ec0\u4e48\u80fd\u529b\u62e6\u963b\u6211\u4eec?\u201d \u3000\u3000\u90d1\u98de\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u73b0\u5728\u53ef\u4ee5\u8d70\u4e86\u5427?\u201d \u3000\u3000\u90d1\u98de\u9053\uff1a\u201c\u5927\u5c0f\u59d0\u8bf7\u5427\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u4e3e\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u52a0\u5feb\u4e86\u811a\u6b65\uff0c\u6025\u6025\u7684\u8ffd\u5728\u5434\u53cc\u5973\u7684\u8eab\u540e\u3002 \u3000\u3000\u90d1\u98de\u679c\u7136\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u5434\u53cc\u5973\u884c\u4e0d\u8fc7\u4e24\u4e08\uff0c\u8033\u9645\u95f4\uff0c \u5df2\u54cd\u8d77\u4e86\u4e00\u4e2a\u51b7\u51b7\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u7ad9\u4f4f\u3002\u201d \u58f0\u97f3\u6765\u81ea\u8eab\u540e\uff0c\u4f55\u5bd2\u8863\u7b2c\u4e00\u4e2a\u8f6c\u8fc7\u8eab\u5b50\u3002 \u3000\u3000\u5434\u53cc\u5973\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u201c\u795e\u5200\u5802\u4e3b\u3002\u201d \u3000\u3000\u4e00\u4e2a\u8eab\u7740\u9ed1\u8272\u4e1d\u888d\u7684\u4e2d\u5e74\u4eba\uff0c\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u8863\u7740\u5341\u5206\u8bb2\u7a76\uff0c\u4e00\u4ef6\u9ed1\u8272\u4e1d\u888d\uff0c\u95ea\u95ea\u751f\u5149\uff0c\u5934\u4e0a\u4e5f\u6234\u7740\u4e00\u9876\u9ed1\u8272\u9e26\u96c0\u5dfe\uff0c\u7559\u7740\u4e94\u7efa\u957f\u53d1\uff0c\u6d53\u7709\u5165\u9b13\uff0c \u76ee\u5982\u94dc\u94c3\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\uff0c\u770b\u8d77\u6765\u5f88\u5a01\u6b66\uff0c\u53ea\u662f\u4e00\u4e2a\u9f3b\u5b50\u751f\u7684\u592a\u574f\u3002 \u3000\u3000\u9e70\u94a9\u9f3b\u5b50\uff0c\u770b\u8d77\u6765\uff0c\u6709\u4e00\u80a1\u7279\u522b\u9634\u6c89\u7684\u5473\u9053\u3002 \u3000\u3000\u5434\u53cc\u5973\u671b\u671b\u5730\u4e0a\u8eba\u7740\u7684\u56db\u4e2a\u9ed1\u8863\u4eba\uff0c\u9053\uff1a \u201c\u8fd9\u4e9b\u4eba\uff0c\u662f\u4f60\u7684?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u662f\uff0c\u4ed6\u4eec\u53ef\u662f\u4f24\u5728\u4f60\u7684\u624b\u4e0b?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u70b9\u4e86\u4ed6\u4eec\u7684\u7a74\u9053\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4ed6\u4eec\u4f3c\u4e4e\u662f\u5e76\u6ca1\u6709\u4f24\u5bb3\u4f60\u7684\u7528\u5fc3\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u4ed6\u4eec\u62e6\u7740\u6211\u7684\u53bb\u8def\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a \u201c\u4ed6\u4eec\u6709\u5f88\u5f3a\u7684\u6218\u6597\u80fd\u529b\uff0c\u5982\u679c\u4ed6\u4eec\u653e\u624b\u4e00\u6218\uff0c\u5927\u5c0f\u59d0\u60f3\u4f24\u4ed6\u4eec\uff0c\u6015\u4e0d\u662f\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u770b\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u5927\u5c0f\u59d0\uff0c\u4f24\u4e86\u4eba\u5c31\u51c6\u5907\u8fd9\u6837\u8d70\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e86\uff0c\u7b11\u5f97\u5a07\u5a07\u751c\u751c\u7684\uff0c\u4f46\u53e3\u6c14\u5374\u662f\u5f02\u5e38\u51b7\u6f20\u5730\u8bf4\u9053\uff1a\u201c\u5982\u679c\u6211\u6ca1\u8bb0\u9519\uff0c\u4f60\u597d\u8c61\u59d3\u96f7\u2026\u2026\u201d \u3000\u3000\u9ed1\u888d\u4eba\u63a5\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u5927\u5c0f\u59d0\u7684\u8bb0\u5fc6\u5b9e\u5728\u4e0d\u9519\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u96f7\u5802\u4e3b\u53ef\u662f\u51c6\u5907\u8981\u62e6\u963b\u6211?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u5927\u5c0f\u59d0\u8a00\u91cd\u4e86\uff0c\u533a\u533a\u4e0d\u8fc7\u662f\u4e00\u4e2a\u5802\u4e3b\u7684\u8eab\u4efd\uff0c\u600e\u4e48\u6562\u5bf9\u5927\u5c0f\u59d0\u65e0\u793c\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u5802\u4e3b\u65e2\u7136\u6ca1\u6709\u62e6\u963b\u6211\u7684\u610f\u601d\uff0c\u90a3\u6211\u4eec\u5c31\u8d70\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5927\u5c0f\u59d0\u8981\u8d70\u4e5f\u53ef\u4ee5\uff0c\u4e0d\u8fc7\uff0c\u5e0c\u671b\u4f60\u7559\u4e0b\u53e5\u8bdd\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d!\u7559\u4e0b\u4e00\u53e5\u4ec0\u4e48\u8bdd?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4f60\u5927\u5c0f\u59d0\uff0c\u8fd8\u662f\u4e0d\u662f\u7b2c\u4e00\u5821\u4e2d\u7684\u4eba\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u53e5\u8bdd\u6709\u8bed\u75c5\uff0c\u8981\u6211\u600e\u4e48\u6837\u56de\u7b54\u4f60?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u636e\u5b9e\u800c\u8a00\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u73b0\u5728\u8fd8\u662f\u7b2c\u4e00\u5821\u4e2d\u4eba\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ee5\u540e\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ee5\u540e\u54b1\u4eec\u518d\u89c1\u9762\uff0c\u4e5f\u8bb8\u4f1a\u654c\u5bf9\u76f8\u5904?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u96f7\u67d0\u656c\u91cd\u4f60\u7684\u539f\u56e0\uff0c\u662f\u56e0\u4e3a\u4f60\u662f\u7b2c\u4e00\u5821\u7684\u5927\u5c0f\u59d0\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u6487\u5f00\u5927\u5c0f\u59d0\u7684\u8eab\u4efd\u4e0d\u8c08\uff0c\u4f60\u51c6\u5907\u5bf9\u4ed8\u6211?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u679c\u6487\u5f00\u4f60\u5927\u5c0f\u59d0\u7684\u8eab\u4efd\uff0c \u4f60\u81f3\u5c11\u8981\u5bf9\u6211\u6709\u4e2a\u4ea4\u4ee3\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u4ea4\u4ee3\u4ec0\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u795e\u5200\u5802\u56db\u4e2a\u4eba\u88ab\u4f60\u4f24\u4e86\uff0c\u4f60\u603b\u8be5\u6709\u4e2a\u4ea4\u4ee3\u5427?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u4f60\u8fd9\u662f\u6545\u610f\u627e\u9ebb\u70e6\u4e86?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u4f60\u5982\u679c\u8981\u8131\u79bb\u7b2c\u4e00\u5821\uff0c\u90a3\u5c31\u5931\u53bb\u4e86\u5927\u5c0f\u59d0\u7684\u8eab\u4efd\uff0c\u5728\u4e0b\u5c31\u6ca1\u6709\u4ec0\u4e48\u987e\u8651\u7684\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5982\u82e5\u4f60\u8fd8\u662f\u7b2c\u4e00\u5821\u7684\u5927\u5c0f\u59d0\uff0c\u522b\u8bf4\u4f60\u4f24\u4e86\u56db\u4e2a\u795e\u5200\u5802\u592b\uff0c\u5c31\u662f\u6740\u4e86\u4ed6\u4eec\uff0c\u96f7\u67d0\u4eba\u4e5f\u53ea\u597d\u8ba4\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u5927\u5c0f\u59d0\u7684\u8eab\u4efd\uff0c\u7adf\u7136\u662f\u5982\u6b64\u91cd\u8981\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u786e\u7136\u5982\u6b64\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u6697\u4e2d\u4e00\u63d0\u6c14\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u73b0\u5728\uff0c\u54b1\u4eec\u4e0d\u7528 \u8c08\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u59d1\u5a18\u5429\u5490\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u662f\u5434\u53cc\u5973\uff0c\u548c\u7b2c\u4e00\u5821\u5168\u65e0\u5173\u7cfb\u7684\u5434\u53cc\u5973\uff0c\u4f60\u96f7\u5802\u4e3b\u51c6\u5907\u5982\u4f55\u5bf9\u4ed8\u6211?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u7eb5\u58f0\u5927\u7b11\uff0c\u9053\uff1a \u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5927\u5c0f\u59d0\u5df2\u7ecf\u6b63\u5f0f\u8bf4\u660e\uff0c\u8131\u79bb\u7b2c\u4e00\u5821\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u5434\u53cc\u5973\uff0c\u4f60\u4f24\u4e86\u672c\u5821\u56db\u4e2a\u4eba\uff0c\u5fc5\u987b\u968f\u672c\u5ea7\u5230\u795e\u5200\u5802\u53bb\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53ef\u4ee5\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4f60\u96f7\u5802\u4e3b\u8981\u60f3\u6cd5\u5b50\u5e26\u6211\u53bb\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u6700\u597d\u662f\u5408\u4f5c\uff0c\u4f46\u5982\u59d1\u5a18\u4e00\u5b9a\u4e0d\u5408\u4f5c\uff0c\u90a3\u4e5f\u662f\u6ca1\u6709\u529e\u6cd5\u7684\u4e8b\u4e86\u3002\u201d \u5434\u53cc\u5973\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u54b1\u4eec\u8d70!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5e94\u58f0\u884c\u4e86\u8fc7\u53bb\uff0c\u8d8a\u8fc7\u4e86\u5434\u53cc\u5973\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u4f60\u73b0\u5728\u53ef\u4ee5\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u51b7\u51b7\u4e00\u7b11\uff0c\u4e3e\u624b\u4e92\u51fb\u4e24\u638c\u3002 \u3000\u3000\u5434\u53cc\u5973\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u96f7\u5802\u4e3b\u5e26\u4e86\u4e0d\u5c11\u7684\u5e2e\u624b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u59d1\u5a18\u6b66\u529f\u9ad8\u5f3a\uff0c\u5927\u6982\u4e0d\u4f1a\u628a\u795e\u5200\u5802\u4e2d\u4eba\u653e\u5728\u773c\u4e2d\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u795e\u5200\u5802\u662f\u7b2c\u4e00\u5821\u7684\u4e3b\u529b\uff0c\u4e5f\u662f\u6700\u795e\u79d8\u7684\u5730\u65b9\uff0c \u80fd\u6709\u673a\u4f1a\u89c1\u8bc6\u4e00\u4e0b\uff0c\u4e5f\u597d\u5f00\u5f00\u773c\u754c\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u59d1\u5a18\u7acb\u523b\u5c31\u89c1\u5230\u4e86\u3002\u201d \u3000\u3000\u56db\u4e2a\u52b2\u88c5\u9ed1\u8863\u4eba\uff0c\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u795e\u5200\u5802\u4e2d\u7684\u4eba\uff0c\u4f3c\u4e4e\u662f\u5bf9\u9ed1\u8272\u6709\u4e00\u79cd\u504f\u7231\uff0c\u4e5f\u8bb8\u4ed6\u4eec\u7528\u9ed1\u8272\u4f5c\u4e3a\u4e00\u79cd\u6807\u5fd7\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u90fd\u7a7f\u7740\u9ed1\u8272\u7684\u8863\u670d\uff0c\u4f46\u5728\u8bbe\u8ba1\u4e0a\uff0c\u5374\u82b1\u4e86\u4e0d\u5c11\u7684\u5fc3\u8840\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u670d\u88c5\u7684\u989c\u8272\u867d\u7136\u4e00\u6837\uff0c\u4f46\u683c\u5f0f\u7684\u8bbe\u8ba1\uff0c\u5374\u662f\u5b8c\u5168\u4e0d\u540c\uff0c\u4e00\u773c\u4e4b\u95f4\uff0c\u5c31\u80fd\u7ed9\u4eba\u4e00\u5904\u660e\u767d\u7684\u5206\u8fa8\u3002 \u3000\u3000\u9664\u4e86\u4ed6\u4eec\u90fd\u7a7f\u9ed1\u8272\u7684\u8863\u670d\u4e4b\u5916\uff0c\u5434\u53cc\u5973\u8fd8\u53d1\u89c9\u4e86\u795e\u5200\u5802\u7684\u4eba\uff0c\u6bcf\u4e00\u6b21\uff0c\u90fd\u662f\u56db\u4e2a\u4eba\u4e00\u7ec4\u3002 \u3000\u3000\u56db\u4e2a\u4eba\uff0c\u56db\u79cd\u5b8c\u5168\u4e0d\u540c\u7684\u5200\uff0c\u60f3\u6765\uff0c\u6bcf\u4e2a\u4eba\u7684\u5200\u6cd5\uff0c\u90fd\u8981\u548c\u4ed6\u624b\u4e2d\u7684\u5947\u5f62\u602a\u5200\u914d\u5408\u3002 \u3000\u3000\u5434\u53cc\u5973\u56de\u987e\u4e86\u4f55\u5bd2\u8863\u4e00\u773c\u9053\uff1a \u201c\u5bd2\u8863\uff0c\u4e0d\u8981\u5e2e\u5fd9\uff0c\u4e0d\u8bba\u6211\u9047\u4e0a\u4ec0\u4e48\u51f6\u9669\uff0c\u751a\u81f3\u53d7\u4e86\u4f24\uff0c\u90fd\u4e0d\u8bb8\u51fa\u624b\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u54b1\u4eec\u662f\u592b\u5987\u554a!\u592b\u59bb\u4e4b\u95f4\uff0c\u96be\u9053\u80fd\u89c1\u6b7b\u4e0d\u6551\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u9053\uff1a \u201c\u4f60\u653e\u5fc3\uff0c\u4ed6\u4eec\u6740\u4e0d\u4e86\u6211\uff0c\u6211\u62c5\u5fc3\u7684\u662f\uff0c\u4ed6\u4eec\u8fd9\u4e9b\u4eba\u7684\u624b\u6cd5\u4e00\u5b9a\u5f88\u5947\u602a\uff0c\u4e07\u4e00\uff0c\u6211\u88ab\u95f9\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u4f60\u7a81\u7136\u51fa\u624b\uff0c\u90a3\u4e0d\u4f46\u5bf9\u6211\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u800c\u4e14\uff0c\u8fd8\u4f1a\u6270\u4e71\u4e86\u6211\u7684\u5fc3\u795e\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53cc\u5973\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u8ba9\u6211\u653e\u5fc3\uff0c\u6211\u624d\u80fd\u4e13\u5fc3\u4e00\u610f\u7684\u5bf9\u654c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\u5427!\u6211\u4eec\u7b49\u4e00\u4e2a\u7ed3\u679c\u51fa\u6765\uff0c\u4f60\u5e94\u8be5\u660e\u767d\uff0c\u4f60\u5982\u679c\u6709\u4e86\u4ec0\u4e48\u4e09\u957f\u4e24\u77ed\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u4f1a\u653e\u8fc7\u6211\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u6211\u4eec\u4f1a\u5b89\u5168\u51fa\u4e86\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u7684\u751f\u6b7b\u8bc0\u522b\uff0c\u5b8c\u4e86\u6ca1\u6709?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5b8c\u4e86\uff0c\u4f60\u8981\u4ed6\u4eec\u51fa\u624b\u5427\uff01\u201d \u3000\u3000\u5ffd\u7136\u4e00\u4fa7\u5a07\u8eaf\uff0c\u62cd\u51fa\u4e00\u638c\u3002 \u3000\u3000\u90a3\u4e00\u638c\u5feb\u5982\u95ea\u7535\uff0c\u9ed1\u8863\u4eba\u8fd8\u672a\u4e3e\u8d77\u624b\u4e2d\u4e4b\u5200\uff0c\u5434\u53cc\u5973\u7684\u4e00\u638c\uff0c\u5df2\u7136\u62cd\u5728\u4e86\u4e00\u4e2a\u6267\u5200\u9ed1\u8863\u4eba\u7684\u53f3\u81c2\u4e4b\u4e0a\u3002 \u3000\u3000\u542c\u4e0d\u5230\u7830\u7136\u5927\u9707\u7684\u638c\u58f0\u3002 \u3000\u3000\u4f46\u90a3\u4e2d\u638c\u7684\u9ed1\u8863\u4eba\uff0c\u5374\u50cf\u4e00\u5806\u70c2\u6ce5\u4e00\u822c\uff0c\u5ffd\u7136\u95f4\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u7acb\u523b\u95f4\uff0c\u5200\u5149\u95ea\u8f6c\uff0c\u53e6\u5916\u4e09\u4e2a\u9ed1\u8863\u4eba\uff0c\u4e09\u628a\u602a\u5200\uff0c\u540c\u65f6\u51fa\u624b\u3002 \u3000\u3000\u5200\u5200\u5feb\u5982\u95ea\u7535\u3002 \u3000\u3000\u4e00\u4fa7\u89c2\u6218\u7684\u4f55\u5bd2\u8863\uff0c\u53ea\u770b\u5f97\u5fc3\u5934\u5927\u9707\uff0c\u56e0\u4e3a\u8fd9\u4e09\u5200\uff0c\u4e0d\u4f46\u8fc5\u5feb\u65e0\u6bd4\uff0c\u800c\u4e14\uff0c\u8fd8\u81ea\u7136\u6784\u6210\u4e86\u4e00\u7247\u7ef5\u5bc6\u7684\u5200\u7f51\u3002 \u3000\u3000\u5c01\u4f4f\u4e86\u53ef\u4ee5\u95ea\u907f\u7684\u65b9\u4f4d\u3002 \u3000\u3000\u5434\u53cc\u5973\u5728\u5200\u5149\u4e2d\u5ffd\u7136\u5411\u4e0b\u5012\u53bb\uff0c\u50cf\u4e00\u652f\u5f29\u7bad\u822c\u5c04\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5200\u5149\u8fc7\u53bb\uff0c\u65a9\u4e0b\u4e86\u5434\u53cc\u5973\u8eab\u4e0a\u6570\u5904\u8863\u670d\u3002 \u3000\u3000\u4e5f\u5212\u7834\u4e86\u5434\u53cc\u5973\u7684\u5de6\u817f\u3002 \u3000\u3000\u7ec6\u5ae9\u7684\u5927\u817f\u4e0a\uff0c\u5f00\u4e86\u4e00\u9053\u8840\u53e3\u3002 \u3000\u3000\u9c9c\u8840\u98de\u6e85\u800c\u51fa\u3002 \u3000\u3000\u4f46\u4ecd\u7136\u8131\u79bb\u4e86\u4e09\u5200\u5168\u51fb\u7684\u5200\u7f51\u3002 \u3000\u3000\u5c31\u5728\u5979\u8eab\u8eaf\u75be\u5c04\u800c\u51fa\u7684\u540c\u65f6\uff0c\u53cc\u817f\u5ffd\u7136\u98de\u8d77\u3002 \u3000\u3000\u98de\u5feb\u7684\u4e00\u811a\uff0c\u70b9\u5728\u4e86\u4e24\u4e2a\u9ed1\u8863\u4eba\u7684\u80cc\u5fc3\u4e4b\u4e0a\u3002 \u3000\u3000\u4e24\u4e2a\u9ed1\u8863\u4eba\u76f4\u633a\u633a\u7684\u5012\u4e86\u4e0b\u53bb\uff0c\u624b\u4e2d\u4ecd\u7136\u7d27\u63e1\u7740\u5200\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u51b7\u51b7\u8d5e\u9053\uff1a\u201c\u597d\u817f\u6cd5\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9ca4\u9c7c\u6253\u633a\uff0c\u4eba\u5df2\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6025\u6025\u95ee\u9053\uff1a\u201c\u817f\u4f24\u5982\u4f55?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6ca1\u6709\u4f24\u5230\u7b4b\u9aa8\uff0c\u795e\u5200\u5802\u4e2d\u7684\u5200\u624b\uff0c\u5200\u6cd5\u5b9e\u5728\u591f\u5feb\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u59d1\u5a18\u8fd9\u4e00\u62db\u5728\u907f\u654c\u653b\u88ad\u4e2d\uff0c\u4ecd\u7136\u4e0d\u5fd8\u8bb0\u4f24\u654c\uff0c\u4e0d\u662f\u4e2d\u539f\u817f\u6cd5\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u7684\u6b66\u529f\u5f88\u7279\u522b\uff0c\u6108\u662f\u5728\u9669\u6076\u4e4b\u4e2d\uff0c\u6108\u80fd\u53d1\u6325\u51fa\u5a01\u529b\uff0c\u800c\u4e14\uff0c\u6211\u7684\u624b\u6cd5\u4e5f\u5f88\u91cd\uff0c\u4ed6\u4eec\u4e09\u4e2a\u4eba\uff0c\u90fd\u5df2\u7ecf\u65ad\u4e86\u751f\u673a\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e09\u62db\u4ea4\u63a5\uff0c\u4f60\u5df2\u8fde\u4f24\u6211\u795e\u5200\u5802\u4e09\u540d\u5200\u624b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u6240\u4ee5\uff0c\u4f60\u6700\u597d\u522b\u8ba9\u4ed6\u4eec\u51fa\u624b\u4e86\uff0c\u9601\u4e0b\u81ea\u5df1\u548c\u6211\u653e\u624b\u4e00\u6218\u3002\u201d \u96f7\u65e0\u5f71\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u8fd8\u4e0d\u5230\u6211\u51fa\u624b\u7684\u65f6\u95f4\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4eba\u624b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u53d1\u51fa\u4e86\u4e00\u58f0\u4f4e\u5578\u3002 \u3000\u3000\u6697\u5f71\u4e2d\uff0c\u7acb\u523b\u53c8\u884c\u51fa\u4e86\u56db\u4e2a\u9ed1\u8863\u4eba\u3002 \u3000\u3000\u56db\u4e2a\u88c5\u675f\u4e00\u6837\u7684\u9ed1\u8863\u4eba\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8fd9\u4e00\u6b21\uff0c\u56db\u4e2a\u4eba\u624b\u4e2d\u7684\u5175\u5203\uff0c\u662f\u56db\u628a\u957f\u8fc7\u4e09\u5c3a\u516b\u7684\u67f3\u53f6\u5f62\u957f\u5200\u3002 \u3000\u3000\u4e25\u683c\u7684\u8bf4\uff0c\u8fd9\u56db\u628a\u5200\uff0c\u5df2\u5b8c\u5168\u8131\u79bb\u4e86\u5200\u7684\u5f62\u6001\uff0c\u8bf4\u5b83\u662f\u5251\uff0c\u8fd8\u63a5\u8fd1\u4e00\u4e9b\u3002 \u3000\u3000\u4f46\u5b83\u786e\u5b9e\u662f\u5200\uff0c\u4e00\u9762\u5200\u80cc\uff0c\u4e00\u9762\u5200\u5203\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u4ecd\u7136\u662f\u5206\u7ad9\u4e86\u56db\u4e2a\u65b9\u4f4d\uff0c\u53c8\u628a\u5434\u53cc\u5973\u56f4\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5feb\u6b65\u884c\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5434\u53cc\u5973\u6447\u6447\u5934\uff0c\u9053\uff1a \u201c\u5bd2\u8863\uff0c\u9000\u56de\u53bb\uff0c\u6211\u4e0d\u8bb8\u4f60\u5e2e\u5fd9\uff0c\u4f60\u5df2\u7ecf\u7b54\u5e94\u4e86\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u4eae\u5175\u5203\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u6709\u4f60\u8fd9\u51e0\u53e5\u8bdd\u5c31\u884c\u4e86\uff0c\u5c31\u50cf\u662f\u7075\u4e39\u5999\u836f\u4e00\u6837\uff0c\u6211\u7684\u4f24\uff0c\u5df2\u7ecf\u4e0d\u75bc\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u606f\u4e00\u58f0\uff0c\u53ea\u597d\u5411\u540e\u9000\u4e86\u56de\u53bb\u3002 \u3000\u3000\u5434\u53cc\u5973\u5438\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u8fd8\u5e26\u4e86\u591a\u5c11\u4eba?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4f60\u5148\u5bf9\u4ed8\u4e86\u4ed6\u4eec\u56db\u4e2a\u4eba\u4e4b\u540e\uff0c\u6211\u4f1a\u518d\u8ba9\u4f60\u5f00\u4e00\u6b21\u773c\u754c\u3002\u201d \u3000\u3000\u4e0d\u77e5\u9053\u96f7\u65e0\u5f71\u53d1\u4e86\u4e00\u53e5\u4ec0\u4e48\u6837\u5b50\u7684\u6697\u53f7\uff0e\u56db\u628a\u67f3\u53f6\u957f\u5200\uff0c\u4e00\u9f50\u51fa\u624b\uff0c\u540c\u65f6\u6a2a\u91cc\u524a\u51fa\u3002 \u3000\u3000\u56db\u628a\u5200\u65b9\u4f4d\u4e0d\u540c\uff0c\u524a\u51fa\u7684\u5200\u52bf\uff0c\u53c8\u6784\u6210\u4e86\u4e00\u7247\u4ea4\u53c9\u7684\u5200\u7f51\u3002 \u3000\u3000\u56db\u4eba\u5408\u51fb\u7684\u5200\u6cd5\uff0c\u5982\u6b64\u51f6\u9669\u7684\u62db\u6570\uff0c\u5b9e\u5728\u662f\u4e00\u4ef6\u5f88\u5371\u9669\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u624b\u4e2d\u7684\u5200\u90fd\u5f88\u957f\uff0c\u4ea4\u53c9\u6a2a\u5288\uff0c\u56fa\u7136\u53ef\u4ee5\u7ec4\u6210\u4e00\u7ec4\u7ef5\u5bc6\u7684\u5200\u7f51\uff0c\u4f46\u5982\u90a3\u4eba\u4e00\u95ea\u907f\u5f00\u53bb\uff0c\u5f88\u53ef\u80fd\u4f24\u5230\u81ea\u5df1\u4eba\u3002 \u3000\u3000\u8fd9\u662f\u5145\u6ee1\u7740\u51f6\u9669\u7684\u4e00\u62db\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u53ea\u770b\u7684\u5927\u4e3a\u9707\u52a8\u3002 \u3000\u3000\u8fd9\u4e00\u51fb\u7684\u51f6\u5389\uff0c\u5b9e\u5728\u4e0d\u6613\u514b\u670d\u3002 \u3000\u3000\u9664\u4e86\u4ee5\u76f4\u63a5\u4e0a\u817e\u7684\u8eab\u6cd5\uff0c\u5411\u4e0a\u98de\u8dc3\u4e4b\u5916\uff0c\u51e0\u4e4e\u6ca1\u6709\u529e\u6cd5\u907f\u8fc7\u90a3\u4e00\u5200\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u770b\u4e86\u5b9e\u5728\u62c5\u5fc3\uff0c\u5c31\u4ed6\u81ea\u5df1\u7684\u6b66\u529f\u9020\u8be3\u800c\u8a00\uff0c\u7edd\u5bf9\u65e0\u6cd5\u9003\u8fc7\u8fd9\u4e00\u5200\u3002 \u3000\u3000\u4f46\u5434\u53cc\u5973\u7adf\u7136\u9003\u8fc7\u4e86\u3002 \u3000\u3000\u5979\u6ca1\u6709\u65bd\u7528\u5411\u4e0a\u98de\u817e\u7684\u8eab\u6cd5\uff0c\u800c\u662f\u76f4\u6b3a\u5165\u4e86\u4e00\u4e2a\u7528\u5200\u8005\u7684\u6000\u4e2d\u3002 \u3000\u3000\u90a3\u662f\u4e00\u79cd\u5feb\u901f\uff0c\u8be1\u5947\u7684\u8eab\u6cd5\uff0c\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e2d\uff0c\u6b3a\u5165\u4e86\u90a3\u4eba\u6000\u4e2d\uff0c\u540c\u65f6\u6447\u52a8\u4ed6\u624b\u4e2d\u7684\u957f\u5200\u3002 \u3000\u3000\u8fd9\u56db\u67c4\u957f\u5200\u7684\u5408\u51fb\u4e4b\u52bf\uff0c\u672c\u6765\u662f\u4e00\u79cd\u5f88\u7cbe\u5bc6\u7684\u914d\u5408\uff0c\u5200\u4e0e\u5200\u4e4b\u95f4\u7684\u8ddd\u79bb\uff0c\u4e5f\u53ea\u4e0d\u8fc7\u662f\u6beb\u5398\u4e4b\u5dee\u3002 \u3000\u3000\u4efb\u4f55\u8f7b\u5fae\u79fb\u52a8\uff0c\u90fd\u4f1a\u9020\u6210\u6df7\u4e71\uff0c\u4f55\u51b5\uff0c\u5434\u53cc\u5973\u6447\u52a8\u7684\u5e45\u5ea6\u53c8\u76f8\u5f53\u7684\u5927\u3002 \u3000\u3000\u53ea\u542c\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u56db\u67c4\u957f\u5200\u56e0\u4e00\u5200\u9519\u4e71\uff0c\u4ea4\u89e6\u5728\u4e00\u5904\u3002 \u3000\u3000\u5434\u53cc\u5973\u7684\u6b66\u529f\uff0c\u662f\u4e00\u79cd\u5177\u6709\u7075\u654f\u6548\u80fd\u7684\u6b66\u529f\uff0c\u4eba\u6b3a\u5165\u5bf9\u65b9\u6000\u4e2d\u4e4b\u540e\uff0c\u5de6\u624b\u5df2\u5148\u884c\u70b9\u51fa\u4e86\u4e00\u6307\uff0c\u4e00\u80a1\u5f3a\u5927\u7684\u6697\u52b2\uff0c\u5e94\u624b\u98de\u51fa\uff0c\u70b9\u4e2d\u4e86\u90a3\u5200\u624b\u7684\u7a74\u9053\u3002\u56db\u5200\u649e\u51fb\u4e4b\u540e\uff0c\u7acb\u523b\u63a5\u4f4f\u4e86\u90a3\u5200\u624b\u88ab\u9707\u8131\u624b\u7684\u957f\u5200\u3002 \u3000\u3000\u5979\u4ecd\u7136\u6ca1\u4eae\u5175\u5203\uff0c\u4f46\u5374\u5728\u7b2c\u4e8c\u56de\u5408\u4e00\u62db\u4e4b\u4e0b\uff0c\u62a2\u5230\u4e86\u4e00\u628a\u67f3\u53f6\u957f\u5200\uff0c\u90a3\u5931\u5200\u5927\u6c49\u5374\u88ab\u5434\u53cc\u5973\u501f\u52bf\u4e00\u5e26\uff0c\u76f4\u5411\u53e6\u5916\u4e09\u4eba\u7684\u5200\u53e3\u649e\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u63a5\u5200\uff0c\u62a2\u5200\uff0c\u4ee5\u4eba\u649e\u5200\uff0c\u4e00\u6c14\u5475\u6210\uff0c\u5176\u95f4\uff0c\u53ea\u6709\u5239\u90a3\u4e4b\u611f\u3002 \u3000\u3000\u5434\u53cc\u5973\u8868\u73b0\u51fa\u4e86\u6b66\u529f\u4e0a\u6781\u7aef\u7684\u6210\u5c31\uff0c\u4e00\u79cd\u60ca\u4eba\u7684\u6210\u5c31\u3002 \u3000\u3000\u4f46\u795e\u5200\u5802\u7684\u5200\u624b\uff0c\u5374\u4e5f\u662f\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\uff0c\u5728\u6781\u7aef\u5feb\u901f\u53d8\u5316\u4e2d\uff0c\u90a3\u4e09\u4e2a\u4eba\u4ecd\u7136\u53ca\u65f6\u6536\u56de\u4e86\u624b\u4e2d\u957f\u5200\uff0c\u95ea\u907f\u5f00\u53bb\u3002 \u3000\u3000\u5931\u5200\u5927\u6c49\uff0c\u5374\u7830\u7136\u4e00\u58f0\u5012\u6454\u5728\u5730\u4e0a\u3002 \u3000\u3000\u7b2c\u4e00\u6d41\u7684\u5200\u624b\uff0c\u4e5f\u662f\u6b8b\u9177\u7684\u6740\u624b\uff0c\u4e09\u4e2a\u4eba\u4e2d\u6ca1\u6709\u4e00\u4e2a\u53bb\u7167\u987e\u540c\u4f34\uff0c\u4e09\u628a\u957f\u5200\uff0c\u4e89\u53d6\u7b2c\u4e8c\u6b21\u653b\u52bf\u3002 \u3000\u3000\u4f46\u5728\u4e09\u5200\u653b\u4e0a\u7684\u77ac\u95f4\uff0c\u5374\u53c8\u7a81\u7136\u6536\u5200\u800c\u9000\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u662f\u81ea\u613f\u9000\u4e0b\uff0c\u795e\u5200\u5802\u7684\u5200\u624b\uff0c\u90fd\u662f\u4e0d\u8981\u547d\u7684\u5200\u624b\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u9000\u5f00\u7684\u539f\u56e0\uff0c\u5374\u662f\u5949\u5230\u4e86\u9000\u5f00\u7684\u4ee4\u8c15\u3002 \u3000\u3000\u4ee4\u8c15\u51fa\u81ea\u795e\u5200\u5802\u4e3b\u96f7\u65e0\u5f71\u7684\u79d8\u5bc6\u6307\u4ee4\uff0c\u4e09\u4e2a\u4eba\u540c\u65f6\u6536\u5200\uff0c\u9000\u5f00\u4e86\u516b\u5c3a\u3002 \u3000\u3000\u5979\u770b\u5230\u4e86\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5c31\u7ad9\u5728\u96f7\u65e0\u5f71\u7684\u8eab\u8fb9\uff0c \u81ea\u7136\uff0c\u96f7\u65e0\u5f71\u4e0b\u4ee4\u505c\u624b\uff0c\u660e\u663e\u7684\u662f\u53d7\u4e86\u9ad8\u5e7d\u5170\u7684\u5f71\u54cd\u3002 \u201c\u5927\u59ca!\u201d\u9ad8\u5e7d\u5170\u7f13\u7f13\u884c\u4e86\u8fc7\u6765\uff0c\u795e\u6001\u6709\u4e9b\u9eef\u7136\u5730\u63a5\u9053\uff1a\u201c\u4f60\u53d7\u4e86\u4f24?\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u7b11\u5f97\u6709\u70b9\u5c34\u5c2c\uff0c\u5f03\u4e86\u624b\u4e2d\u7684\u67f3\u53f6\u5200\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5170\u59b9\uff0c\u591a\u8c22\u4f60\u7684\u5173\u5fc3\uff0c\u8fd9\u53ea\u662f\u4e00\u4e9b\u76ae\u8089\u4e4b\u4f24\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\u4e3a\u4ec0\u4e48\u4e0e\u4ed6\u4eec\u52a8\u4e0a\u4e86\u624b?\u201d \u3000\u3000\u663e\u7136\u662f\u660e\u77e5\u6545\u95ee\u3002 \u3000\u3000\u4f46\u5434\u53cc\u5973\u53c8\u4e0d\u80fd\u4e0d\u4f5c\u7b54\u590d\uff0c\u70b9\u70b9\u5934\uff0c\u9053\u201c\u4e0d\u80fd\u602a\u4ed6\u4eec\uff0c\u4ed6\u4eec\u53ea\u662f\u62e6\u963b\u6211\uff0c\u4e0d\u51c6\u6211\u79bb\u5f00\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\u8981\u8d70\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u76ee\u5149\u6295\u6ce8\u5728\u4f55\u5bd2\u8863\u7684\u8eab\u4e0a\uff0c\u9053\uff1b \u201c\u4f55\u516c\u5b50\uff0c \u4f60\u4e5f\u8981\u8d70?\u201d \u3000\u3000\u672c\u662f\u751c\u751c\u7684\u59d0\u592b\uff0c\u7a81\u7136\u95f4\uff0c\u4f1a\u53d8\u6210\u4e86\u4f55\u516c\u5b50\uff0c\u542c\u8d77\u6765\uff0c\u5ffd\u7136\u95f4\uff0c\u62c9\u8fdc\u4e86\u4e24\u4e2a\u4eba\u4e4b\u95f4\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e7d\u5170\u59d1\u5a18\uff0c\u62b1\u6b49\u5f97\u5f88\uff0c \u4f60\u5927\u59ca\u4e5f\u662f\u53d7\u4e86\u6211\u7684\u5f71\u54cd\uff0c\u662f\u6211\u8981\u8d70\uff0c\u5e7d\u5170\uff0c\u4f60\u5982\u82e5\u8981\u6068\u4e00\u4e2a\u4eba\uff0c\u90a3\u5c31\u5e94\u8be5\u662f\u6211\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4e0d\u6068\u4f60\u4eec\uff0c\u53ea\u662f\u6709\u4e9b\u60b2\u4f24\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u4eec\u8981\u79bb\u5f00\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u544a\u8bc9\u6211\u4e00\u58f0\u5462\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u82e6\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u5170\u59b9\uff0c\u4e0d\u8981\u8ffd\u95ee\u4ec0\u4e48\u4e86\uff0c\u5982\u82e5\u8bf4\u5927\u59ca\u6709\u70b9\u9057\u61be\uff0c\u90a3\u5c31\u7b97\u6211\u4eec\u5bf9\u4e0d\u8d77\u4f60\u5427\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u59ca\uff0c\u8981\u4e0d\u8981\u89c1\u7239?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u770b\u4e0d\u4e86\uff0c\u89c1\u4e86\u4e49\u7236\uff0c\u5c82\u4e0d\u4f24\u611f\u60c5\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u662f\u4e0d\u613f\u89c1\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u89c1\u9762\u5f92\u4f5c\u4f24\u60b2\uff0c\u5bf9\u5f7c\u6b64\u90fd\u65e0\u597d\u5904\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u5927\u59ca\uff0c\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u7236\u5973\uff0c\u59ca\u59b9\uff0c\u5927\u5bb6\u76f8\u5904\u7684\u6570\u5e74\u60c5\u610f\uff0c\u4f60\u4e00\u70b9\u4e5f\u4e0d\u7559\u604b\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4e0d\u8bba\u4f60\u5fc3\u76ee\u4e2d\u5982\u4f55\u6068\u6211\uff0c\u4f46\u6211\u53ea\u80fd\u8bf4\u4e00\u53e5\u62b1\u6b49\u4e86\uff0c\u6211\u4eec\u51b3\u5fc3\u8981\u8d70\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u597d\u5427!\u4e24\u4f4d\u4e00\u5b9a\u8981\u8d70\uff0c\u90a3\u5c31\u8bf7\u4fbf\u5427!\u201d \u3000\u3000\u5434\u53cc\u5973\u6014\u4e86\u4e00\u6014\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5374\u5feb\u6b65\u884c\u4e86\u8fc7\u53bb\uff0c\u4e00\u62b1\u62f3\uff0c\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u6211\u4eec\u5f88\u60ed\u6127\u3002\u201d \u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u5feb\u8d70\u5427!\u522b\u8ba9\u6211\u6539\u53d8\u4e86\u5fc3\u610f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7a81\u7136\u4e00\u95ed\u53cc\u76ee\uff0c\u4e24\u884c\u6cea\u593a\u7736\u800c\u51fa\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u5feb\u4e9b\u8d70\uff0c\u5feb\u4e9b\u8d70!\u201d \u3000\u3000\u5979\u60c5\u7eea\u6fc0\u52a8\uff0c\u58f0\u97f3\u4e5f\u5f88\u9ad8\uff0c\u4f3c\u4e4e\u6574\u4e2a\u4eba\u5df2\u5931\u53bb\u4e86\u63a7\u5236\u3002 \u3000\u3000\u5434\u53cc\u5973\u4f4e\u58f0\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u54b1\u4eec\u8d70\u5427\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u5148\u8d70\uff0c\u6211\u65ad\u540e\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7a81\u7136\u63d0\u9ad8\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u653e\u4ed6\u4eec\u79bb\u5f00\uff0c\u4efb\u4f55\u4eba\u4e0d\u5f97\u62e6\u963b\uff0c\u8fdd\u4ee4\u8005\u6740\uff01\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\uff0c\u6e05\u8106\u9ad8\u6602\uff0c\u9ed1\u591c\u4e2d\u4f20\u51fa\u4e86\u5f88\u8fdc\u3002 \u3000\u3000\u5979\u7684\u8bdd\uff0c\u6709\u7740\u5f88\u5927\u7684\u6743\u5a01\uff0c\u5434\u53cc\u5973\u3001\u4f55\u5bd2\u8863\u884c\u8fd1\u5821\u95e8\u65f6\uff0c\u5821\u95e8\u7acb\u65f6\u5927\u5f00\uff0c\u800c\u4e14\u653e\u4e0b\u4e86\u540a\u6865\u3002 \u3000\u3000\u4e24\u4eba\u5f88\u987a\u5229\u7684\u79bb\u5f00\u4e86\u7b2c\u4e00\u5821\u3002 \u3000\u3000\u539f\u6765\u706f\u706b\u901a\u660e\u7684\u7b2c\u4e00\u5821\uff0c\u5ffd\u7136\u95f4\u7184\u4e86\u706f\u706b\u3002 \u3000\u3000\u7b2c\u4e00\u5821\u7684\u91cd\u91cd\u9601\u697c\uff0c\u5df2\u7ecf\u9690\u5931\u5728\u591c\u8272\u4e2d\u3002 \u3000\u3000\u5434\u53cc\u5973\u56de\u987e\u4e86\u5c45\u7559\u6570\u5e74\u7684\u65e7\u5730\u4e00\u773c\uff0c\u6709\u4e9b\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u4f60\u53d1\u89c9\u4e86\u6ca1\u6709?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53d1\u89c9\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u771f\u6b63\u8981\u62e6\u52ab\u6211\u4eec\u7684\u4eba\uff0c\u57cb\u4f0f\u5728\u57ce\u5821\u4e24\u4fa7\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e5f\u662f\u795e\u5200\u5802\u4e2d\u7684\u4eba?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5927\u6982\u662f\u5427!\u4e0d\u80fd\u80af\u5b9a\u4ed6\u4eec\u662f\u4e0d\u662f\u795e\u5200\u5802\u7684\u4eba\uff0c\u4f46\u6211\u77e5\u9053\uff0c\u54b1\u4eec\u9003\u8fc7\u4e86\u4e00\u6b21\u6b7b\u4ea1\u7684\u52ab\u96be\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u4eb2\u773c\u770b\u5230\u4e86\u5434\u53cc\u5973\u7684\u8eab\u624b\uff0c\u90a3\u662f\u5c5e\u4e8e\u7089\u706b\u7eaf\u9752\u7684\u6210\u5c31\uff0c\u5c24\u5176\u662f\uff0c\u90a3\u53d8\u5316\u7384\u5947\u7684\u62db\u5f0f\uff0c\u7ed9\u4eba\u4e00\u79cd\u76ee\u4e0d\u6687\u63a5\u7684\u611f\u89c9\uff0c\u4e0d\u7981\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u90a3\u662f\u4e00\u79cd\u4ec0\u4e48\u57cb\u4f0f?\u201d \u3000\u3000\u5434\u53cc\u5973\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6c34\u706b\u65e0\u60c5!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6c34\u706b\u65e0\u60c5\uff0c\u662f\u5175\u5203\uff0c\u8fd8\u662f\u4eba?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6c34\u706b\u65e0\u60c5\u662f\u7ecf\u8fc7\u4e00\u79cd\u7279\u6b8a\u8bbe\u8ba1\u7684\u5175\u5203\uff0c\u4e5f\u662f\u6697\u5668\uff0c\u4e00\u79cd\u6740\u4eba\u7684\u5229\u5668\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u90a3\u79cd\u5175\u5203?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u662f\u7b2c\u4e00\u5821\u6781\u7aef\u673a\u5bc6\u7684\u6b66\u5668\uff0c\u8fd8\u672a\u5728\u6c5f\u6e56\u4e0a\u8bd5\u7528\u8fc7\uff0c\u6211\u53ea\u662f\u542c\u8fc7\uff0c\u5175\u5203\u4e2d\u80fd\u591f\u55b7\u51fa\u6c34\u3001\u706b\uff0c\u6c34\u662f\u6bd2\u6c34\uff0c\u706b\u662f\u6bd2\u706b\uff0c\u4efb\u4f55\u4eba\u6cbe\u4e0a\u4e00\u70b9\uff0c\u5c31\u5f88\u96be\u6709\u6551\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4e16\u4e0a\u771f\u6709\u8fd9\u6837\u7684\u5175\u5203?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u662f\u7684!\u4e24\u5e74\u524d\uff0c \u5df2\u7ecf\u6253\u9020\u4e86\u51fa\u6765\uff0c\u800c\u4e14\uff0c\u9ad8\u5821\u4e3b\u7279\u5730\u8bad\u7ec3\u4e86\u4e00\u6279\u4eba\u624b\uff0c\u4ed6\u4eec\u7a7f\u7740\u4e00\u79cd\u7279\u5236\u7684\u8863\u670d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u770b\u5230\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u770b\u5230\u4e86\uff0c\u4ed6\u4eec\u5c31\u9760\u5728\u57ce\u5821\u7684\u5899\u58c1\u4e0a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u82e5\u4ed6\u4eec\u51fa\u624b\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54b1\u4eec\u751f\u5b58\u7684\u673a\u4f1a\uff0c\u5c31\u4e0d\u4f1a\u592a\u5927\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ed6\u4eec\u57cb\u4f0f\u5728\u90a3\u91cc\uff0c\u672c\u6765\u662f\u51c6\u5907\u51fa\u624b\u7684\uff0c\u4e3a\u4ec0\u4e48\u53c8\u5ffd\u7136\u6539\u53d8\u4e86\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u56e0\u4e3a\u9ad8\u5e7d\u5170\u3002\u9ad8\u5e7d\u5170\u4e0b\u4e86\u4e00\u9053\u5f88\u4e25\u5389\u7684\u4ee4\u8c15\uff0c\u4efb\u4f55\u4eba\uff0c\u53ea\u8981\u51fa\u624b\uff0c\u5c31\u53ef\u80fd\u4f1a\u906d\u5904\u6b7b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u82e5\u6ca1\u6709\uff0c\u9ad8\u5e7d\u5170\u8fd9\u4e00\u9053\u4ee4\u6e1d\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5341\u4e4b\u516b\u4e5d\uff0c\u6211\u4eec\u5df2\u7ecf\u4f0f\u5c38\u5728\u7b2c\u4e00\u5821\u5916\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u5979\u662f\u771f\u7684\u6551\u4e86\u6211\u4eec\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5982\u82e5\u622a\u6740\u54b1\u4eec\uff0c\u662f\u9ad8\u5821\u4e3b\u7684\u5b89\u6392\uff0c\u9ad8\u5e7d\u5170\u8fd9\u4f5c\u6cd5\uff0c\u53ea\u6015\u8fd8\u8981\u53d7\u5230\u9ad8\u5821\u4e3b\u7684\u8d23\u5907\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\u4e3a\u4e86\u54b1\u4eec\uff0c\u53d7\u4e86\u59d4\u5c48\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4ee5\u540e\uff0c\u4f60\u8981\u60f3\u6cd5\u5b50\u8865\u507f\u5979\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u65e5\u540e\uff0c\u54b1\u4eec\u5982\u82e5\u771f\u80fd\u5e2e\u4e0a\u5979\u7684\u5fd9\uff0c\u81ea\u7136\u4f1a\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u8bb0\u5728\u5fc3\u4e2d\u5c31\u597d\uff0c\u4e0d\u8981\u8f9c\u8d1f\u4e86\u5979\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e24\u4eba\u5df2\u79bb\u5f00\u7b2c\u4e00\u5821\u5341\u91cc\u4e4b\u5916\u3002 \u3000\u3000\u591c\u8272\u4e2d\uff0c\u53ea\u89c1\u4e24\u4e2a\u4eba\u7ad9\u5728\u8def\u4e2d\u3002 \u3000\u3000\u5434\u53cc\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48?\u201d \u3000\u3000\u53f3\u624b\u4e00\u62ac\uff0c\u4e00\u80a1\u6697\u52b2\u5df2\u903c\u4e01\u8fc7\u53bb\u3002 \u3000\u3000\u5de6\u8fb9\u4e00\u4e2a\u8eab\u7740\u84dd\u886b\u4eba\uff0c\u7a81\u7136\u4e00\u4e0a\u6b65\uff0c\u53f3\u624b\u4e00\u62ac\uff0c\u63a5\u4e0b\u90a3\u80a1\u6697\u52b2\u3002 \u3000\u3000\u4f46\u4f55\u5bd2\u8863\u5df2\u7136\u770b\u6e05\u695a\u4e86\u6765\u4eba\u662f\u8c01\uff0c\u6025\u6025\u53eb\u9053\uff1a \u201c\u5feb\u4e9b\u4f4f\u624b\uff0c\u81ea\u5df1\u4eba\u3002\u201d \u4ed6\u7684\u8bdd\uff0c\u4ecd\u7136\u665a\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u84dd\u886b\u4eba\u5df2\u63a5\u4e0b\u5434\u53cc\u5973\u9065\u9065\u51fb\u51fa\u7684\u4e00\u80a1\u638c\u529b\uff0c\u8eab\u4e0d\u7531\u5df1\u7684\u5411\u540e\u9000\u4e00\u6b65\u3002 \u3000\u3000\u5434\u53cc\u5973\u51fb\u51fa\u4e00\u638c\u4e4b\u540e\uff0c\u4eba\u5df2\u6b3a\u8eab\u800c\u4e0a\uff0c\u542c\u5f97\u4f55\u5bd2\u8863\u559d\u53eb\u4e00\u58f0\uff0c\u7acb\u523b\u4f4f\u624b\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5feb\u6b65\u5954\u4e86\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u53f6\u5144\u3001\u4e07\u5144\u3002\u201d \u3000\u3000\u6765\u4eba\u7adf\u662f\u53f6\u957f\u9752\u548c\u4e07\u80dc\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54b1\u4eec\u5949\u4e86\u5821\u4e3b\u4e4b\u547d\uff0c\u7279\u6765\u63a5\u8fce\u4f55\u5144\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u4eec\u6536\u5230\u6211\u4f20\u51fa\u7684\u6d88\u606f\u4e86?\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c \u76ee\u5149\u4e00\u63a0\u5434\u53cc\u5973\uff0c\u63a5\u9053\uff1a \u201c\u8fd9\u4f4d\u59d1\u5a18\u597d\u6df1\u539a\u7684\u529f\u529b\uff0c\u4f55\u5144\u600e\u4e0d\u66ff\u6211\u4eec\u5f15\u89c1\u4e00\u4e0b?\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5434\u53cc\u5973\uff0c\u4f60\u662f\u5927\u540d\u9f0e\u9f0e\u7684\u4e03\u5251\u8ffd\u9b42\u4e86?\u201d \u3000\u3000\u521a\u624d\uff0c\u4e24\u4eba\u5bf9\u4e86\u4e00\u638c\uff0c\u53f6\u957f\u9752\u5df2\u5c1d\u8bd5\u5230\u4e86\u5434\u53cc\u5973\u7684\u5389\u5bb3\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff0c\u4f55\u5144\u591a\u627f\u5434\u59d1\u5a18\u7167\u987e\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u4e24\u4f4d\u5f88\u8f9b\u82e6\u5427!\u5821\u4e3b\u6b63\u5728\u606d\u5019\u5927\u9a7e\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8bf8\u4f4d\u600e\u77e5\u6211\u4eec\u4eca\u591c\u79bb\u5f00\u7b2c\u4e00\u5821?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u672c\u6765\u662f\u4e0d\u77e5\u9053\uff0c\u4f46\u534a\u4e2a\u65f6\u8fb0\u4e4b\u524d\uff0c\u6211\u4eec\u63a5\u5230\u4e86\u5821\u4e3b\u7684\u4ee4\u8c15\uff0c\u6765\u6b64\u63a5\u5e94\u4e24\u4f4d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u5df2\u7ecf\u77e5\u9053\u6211\u4eec\u79bb\u5f00\u7b2c\u4e00\u5821?\u201d \u3000\u3000\u4e07\u80dc\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8d35\u5821\u4e3b\u73b0\u5728\u4f55\u5904?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5728\u6b64\u4e0d\u8fdc\uff0c\u5728\u4e0b\u66ff\u4e24\u4f4d\u5e26\u8def\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u56de\u987e\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u53ef\u80fd\u6709\u4eba\u76ef\u4e0b\u6765\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u59d1\u5a18\u653e\u5fc3\uff0c\u54b1\u4eec\u5728\u8def\u4e0a\uff0c\u4e5f\u8bbe\u6709\u57cb\u4f0f\uff0c\u81f3\u5c11\uff0c\u4f1a\u963b\u6b62\u4ed6\u4eec\u8ffd\u8e2a\u7684\u4eba\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e00\u7247\u7d27\u4f9d\u5c71\u5761\u7684\u6742\u6797\uff0c\u6797\u4e2d\u6709\u8305\u820d\u6570\u6979\u3002 \u3000\u3000\u5c45\u4e2d\u4e00\u95f4\uff0c\u706f\u5149\u9690\u9690\uff0c\u800c\u4e14\uff0c\u623f\u95e8\u65e9\u5df2\u5927\u5f00\u3002 \u3000\u3000\u4e00\u8eab\u7384\u8863\u7684\u9ec4\u7075\uff0c\u65e9\u5df2\u5728\u95e8\u53e3\u7b49\u5019\u3002 \u3000\u3000\u201c\u5bd2\u8863\uff0c\u8f9b\u82e6\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u5feb\u6b65\u8fce\u4e86\u4e0a\u6765\uff0c\u7d27\u63e1\u7740\u4f55\u5bd2\u8863\u7684\u624b\u3002\u9053\uff1a \u201c\u6211\u4eec\u90fd\u4e00\u76f4\u4e3a\u4f60\u62c5\u5fc3\u3002\u201d \u201c\u8c22\u8c22\u5821\u4e3b\u3002\u201d\u4f55\u5bd2\u8863\u56de\u987e\u4e86\u5434\u53cc\u5973\u4e00\u773c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fc7\u6765\uff0c\u89c1\u8fc7\u9ec4\u5821\u4e3b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u524d\u884c\u51e0\u6b65\uff0c\u8eac\u8eab\u4f5c\u793c\uff0c\u9053\uff1a \u201c\u4f55\u95e8\u5434\u53cc\u5973\uff0c\u89c1\u8fc7\u5821\u4e3b\u3002\u201d \u4e00\u53e5\u8bdd\uff0c\u660e\u663e\u5730\u8bf4\u51fa\u4e86\u81ea\u5df1\u7684\u8eab\u4efd\u3002 \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5bd2\u8863\u5f97\u4f60\u7167\u987e\uff0c\u6211\u4eec\u90fd\u5f88\u611f\u8c22\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5821\u4e3b\u8fc7\u5956\uff0c\u8d31\u59be\u7406\u5f53\u4e3a\u592b\u6548\u547d\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u3001\u534e\u5999\u771f\uff0c\u884c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7acb\u523b\u66ff\u5434\u53cc\u5973\u5f15\u89c1\u3002 \u3000\u3000\u534e\u5999\u771f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd8\u8bb0\u5f97\u8d31\u59be\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u51b7\u624b\u7f57\u5239?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6b63\u662f\u8d31\u59be\u7684\u532a\u53f7\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u73b0\u5728\u662f\u9634\u9633\u5821\u4e2d\u7684\u4ec0\u4e48\u8eab\u4efd?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5251\u4f7f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e94\u5927\u5251\u4f7f\u4e4b\u4e00?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5012\u53eb\u59d1\u5a18\u89c1\u7b11\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5434\u59d1\u5a18\uff0c\u54b1\u4eec\u4eca\u540e\u8be5\u5982\u4f55\u79f0\u547c\u59d1\u5a18?\u201d \u3000\u3000\u5434\u53cc\u5973\u8138\u4e0a\u5ffd\u7136\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u968f\u8bf8\u4f4d\u4e4b\u610f\u597d\u4e86\uff0c\u53eb\u6211\u5434\u53cc\u5973\u4e5f\u597d\uff0c\u4f55\u5434\u6c0f\u4e5f\u597d\u3002\u201d \u3000\u3000\u9ec4\u7075\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1b\u201c\u54b1\u4eec\u8fd8\u672a\u5403\u8fc7\u59d1\u5a18\u548c\u5bd2\u8863\u7684\u559c\u9152\uff0c\u4ee5\u540e\uff0c\u54b1\u4eec\u8fd8\u662f\u79f0\u547c\u4f60\u4e00\u58f0\u5434\u59d1\u5a18\u5427\uff0c\u6b64\u95f4\u4e8b\u4e86\u9634\u9633\u5821\u5c06\u5927\u529e\u4e00\u573a\u559c\u4e8b\uff0c\u4f7f\u6709\u60c5\u4eba\u7ec8\u6210\u7737\u5c5e\uff0c\u5144\u5f1f\u4e5f\u597d\u501f\u6b64\u673a\u4f1a\u6d17\u624b\uff0c\u6c5f\u6e56\u5c31\u4e0d\u518d\u6709\u9634\u9633\u5821\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u9ec4\u5821\u4e3b\u6210\u7acb\u9634\u9633\u5821\uff0c\u662f\u4e13\u95e8\u4e3a\u4e86\u5bf9\u4ed8\u7b2c\u4e00\u5821\u4e86?\u201d \u3000\u3000\u9ec4\u7075\u4e00\u62b1\u62f3\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u8bf7\u8fdb\u5165\u8305\u820d\u4e2d\u5750\u5427\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u88ab\u8ba9\u5165\u5ba4\u4e2d\u3002 \u3000\u3000\u4e24\u652f\u706b\u70db\uff0c\u7167\u5f97\u8305\u820d\u4e2d\u4e00\u7247\u901a\u660e\u3002 \u3000\u3000\u9ec4\u7075\u8ba9\u5434\u53cc\u5973\u5c31\u4e0a\u5e2d\uff0c\u5434\u53cc\u5973\u5374\u575a\u6301\u4e0d\u80af\uff0c\u8ba9\u4e86\u4e00\u756a\uff0c\u5434\u53cc\u5973\u624d\u5750\u4e0a\u4e86\u5ba2\u4f4d\u3002 \u3000\u3000\u684c\u5b50\u4e0a\uff0c\u65e9\u5df2\u6446\u597d\u4e86\u9152\u83dc\u3002 \u3000\u3000\u9ec4\u7075\u795e\u60c5\u8083\u7136\u5730\u8bf4\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u6240\u4ee5\u4f1a\u751f\u51fa\u4e86\u9738\u7edf\u6c5f\u6e56\u7684\u91ce\u5fc3\uff0c\u662f\u56e0\u4e3a\u5b83\u592a\u5f3a\u5927\u4e86\uff0c\u5f3a\u5927\u5230\u653e\u773c\u6c5f\u6e56\uff0c\u5df2\u7ecf\u5168\u65e0\u654c\u624b\uff0c\u6240\u4ee5\u624d\u751f\u51fa\u4e86\u552f\u543e\u72ec\u5c0a\u4e4b\u5fc3\uff0c\u5343\u767e\u5e74\u6765\uff0c\u65e0\u6570\u7684\u524d\u9274\u3001\u6559\u8bad\uff0c\u4f46\u5374\u4e00\u76f4\u65e0\u6cd5\u5e73\u606f\u4ee3\u6709\u67ad\u96c4\u7684\u91ce\u5fc3\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u662f\u771f\u7684\u5f3a\u5927\uff0c\u5b9e\u5728\u4e5f\u65e0\u6cd5\u8d23\u602a\u9ad8\u5929\u5065\u6709\u6b64\u91ce\u5fc3\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5434\u59d1\u5a18\u5728\u7b2c\u4e00\u5821\u4e2d\u4f4f\u4e86\u5f88\u4e45\uff0c\u5bf9\u7b2c\u4e00\u5821\u4e2d\u4e8b\uff0c\u60f3\u5fc5\u5df2\u77e5\u6653\u5f88\u591a\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u662f\u77e5\u9053\u5f88\u591a\uff0c\u4e0d\u8fc7\uff0c\u5e76\u975e\u662f\u5b8c\u5168\u77e5\u9053\uff0c\u56e0\u4e3a\uff0c\u7b2c\u4e00\u5821\u4e2d\u6709\u4e00\u4e2a\u795e\u5200\u5802\uff0c\u90a3\u624d\u662f\u7b2c\u4e00\u5821\u4e2d\u771f\u6b63\u5b9e\u529b\u6240\u5728\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u77e5\u9053\u795e\u5200\u5802\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u795e\u5200\u5802\u5f88\u795e\u79d8\uff0c\u6211\u77e5\u9053\u7684\u5b9e\u5728\u5f88\u5c11\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4eca\u591c\uff0c\u6211\u4eec\u79bb\u5f00\u7b2c\u4e00\u5821\u65f6\uff0c\u5c31\u9047\u5230\u4e86\u795e\u5200\u5802\u4e2d\u7684\u5200\u624b\u62e6\u622a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\uff0c\u9664\u4e86\u795e\u5200\u5802\u4e4b\u5916\uff0c\u7b2c\u4e00\u5821\u4e2d\u8fd8\u6709\u4e24\u4e2a\u6700\u53ef\u6015\u7684\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u80fd\u591f\u89c1\u8a00\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5982\u679c\u6211\u4e0d\u80af\u8bf4\uff0c\u5c31\u4e0d\u4f1a\u63d0\u51fa\u6765\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5c31\u662f\u9ad8\u5929\u5065\u548c\u9ad8\u5e7d\u5170\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5929\u5065\u88ab\u79f0\u4f5c\u4e2d\u5dde\u7b2c\u4e00\u5200\uff0c\u90a3\u786e\u5b9e\u662f\u5f88\u96be\u5bf9\u4ed8\u7684\u4e00\u4e2a\u654c\u624b\uff0c\u9634\u9633\u5821\u4e2d\uff0c\u8fd8\u662f\u60f3\u4e0d\u51fa\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u80fd\u548c\u4ed6\u5bf9\u624b\u4e00\u6218\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9664\u4e86\u9ad8\u5929\u5065\u5916\uff0c\u8fd8\u6709\u9ad8\u5e7d\u5170\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bf9\u9ad8\u5e7d\u5170\u6211\u4eec\u5b8c\u5168\u4e0d\u4e86\u89e3\uff0c\u8fd9\u65b9\u9762\uff0c\u8fd8\u671b\u59d1\u5a18\u591a\u591a\u63d0\u4f9b\u4e00\u4e9b\u8d44\u6599\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5982\u82e5\u4f60\u4eec\u77e5\u9053\u9ad8\u5929\u5065\u7684\u6b66\u529f\u8fc7\u4eba\uff0c\u662f\u4e00\u4e2a\u5f88\u53ef\u6015\u7684\u52b2\u654c\uff0c\u90a3\u9ad8\u5e7d\u5170\u7684\u6740\u4eba\u624b\u6cd5\uff0c\u66f4\u662f\u5f3a\u8fc7\u5341\u500d\u3002\u201d \u3000\u3000\u9ec4\u7075\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u4f60\u662f\u8bf4\uff0c\u9ad8\u5e7d\u5170\u7684\u6b66\u529f\uff0c\u6bd4\u9ad8\u5929\u5065\u8fd8\u8981\u9ad8\u660e\u5341\u500d?\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u9053\uff1a\u201c\u8bdd\u4e0d\u662f\u8fd9\u4e48\u8bf4\uff0c\u6740\u4eba\u624b\u6cd5\u9ad8\u660e\u5e76\u4e0d\u4ee3\u8868\u6b66\u529f\u9ad8\u5f3a\uff0c\u95ee\u9898\u662f\uff0c\u4f60\u4eec\u5fc5\u987b\u5f3a\u80dc\u8fc7\u5979\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u4e9b\u8bdd\uff0c\u8001\u592b\u8fd8\u662f\u6709\u4e9b\u542c\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5434\u59d1\u5a18\u7684\u610f\u601d\u5341\u5206\u660e\u663e\uff0c\u90a3\u662f\u8bf4\uff0c\u9664\u975e\u4f60\u6b66\u529f\u5f3a\u8fc7\u9ad8\u5e7d\u5170\u5f88\u591a\u3002\u5426\u5219\uff0c\u65e0\u6cd5\u963b\u6b62\u5979\u5feb\u901f\u7684\u4e03\u8272\u5251\u6cd5\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u7684\u89e3\u8bf4\uff0c\u4e5f\u53ea\u7b97\u5bf9\u4e00\u534a\uff0c\u8d31\u59be\u4e0d\u5f97\u4e0d\u8bf4\u5f97\u6e05\u695a\u4e00\u4e9b\u4e86\uff0c\u9ad8\u5e7d\u5170\u7684\u4e03\u8272\u5251\uff0c\u8bf4\u5b83\u662f\u4e00\u79cd\u5251\u672f\uff0c\u4e0d\u80fd\u7b97\u9519\uff0c\u4f46\u5982\u8bf4\u5b83\u662f\u4e00\u79cd\u4e13\u95e8\u6740\u4eba\u7684\u62db\u672f\uff0c\u5e94\u8be5\u66f4\u6070\u5f53\u4e9b\uff0c\u5c31\u7b97\u6b66\u529f\u548c\u5979\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u9664\u975e\u4f60\u80fd\u4e86\u89e3\u5979\u7684\u5251\u8def\uff0c\u5426\u5219\uff0c\u4e5f\u65e0\u6cd5\u9003\u8fc7\u5979\u51cc\u5389\u7684\u62db\u6570\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5982\u6b64\u53ef\u6015\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5f88\u771f\u5b9e\u7684\u8f6c\u8ff0\uff0c\u9664\u975e\u5979\u624b\u4e0b\u6709\u610f\u7559\u60c5\u2026\u2026\u201d \u3000\u3000\u56de\u987e\u4e86\u4e00\u773c\uff0c\u7a81\u7136\u4f4f\u53e3\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u8bf7\u8bf4\uff0c\u54b1\u4eec\u662f\u767e\u65e0\u7981\u5fcc\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u662f\u6211\u5371\u8a00\u8038\u542c\uff0c\u5821\u4e3b\u624b\u4e0b\u5305\u62ec\u5bd2\u8863\u5728\u5185\u7684\u4e94\u5927\u5251\u4f7f\uff0c\u4e00\u65e5\u9047\u4e0a\u9ad8\u5e7d\u5170\uff0c\u90fd\u5f88\u96be\u662f\u4e09\u62db\u4e4b\u654c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u4e48\u5389\u5bb3?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u5982\u6709\u5931\u8a00\u4e4b\u5904\uff0c\u8fd8\u671b\u8bf8\u4f4d\u591a\u591a\u7684\u62c5\u5f85\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5982\u82e5\u59d1\u5a18\u548c\u9ad8\u5e7d\u5170\u52a8\u624b\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u82e5\u80fd\u63a5\u4e0b\u5979\u4e94\u62db\uff0c\u5c31\u6709\u80dc\u5979\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1b\u201c\u4e94\u62db\u4e4b\u524d\uff0c\u4f60\u7684\u80dc\u9762\u5f88\u5c11?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5b8c\u5168\u6ca1\u6709\uff0c\u9ad8\u5e7d\u5170\u4e00\u65e6\u51fa\u624b\uff0c\u4e94\u62db\u4e4b\u5185\uff0c\u6211\u4e0d\u4f1a\u6709\u8fd8\u624b\u7684\u673a\u4f1a\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u7684\u786e\u662f\u4e00\u4e2a\u52b2\u654c\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9\u4e8e\u795e\u5200\u5802\uff0c\u6211\u53ea\u80fd\u63d0\u4f9b\u4e00\u4e9b\u542c\u5230\u7684\u4f20\u8bf4\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5f25\u8db3\u73cd\u8d35\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u795e\u5200\u5802\u662f\u7b2c\u4e00\u5821\u771f\u6b63\u5b9e\u529b\u6240\u5728\uff0c\u5802\u4e3b\u96f7\u65e0\u5f71\u4e5f\u662f\u4e2a\u5f88\u795e\u79d8\u7684\u4eba\u7269\uff0c\u4ed6\u4e0d\u4f46\u6b66\u529f\u7edd\u9ad8\uff0c\u800c\u4e14\u4e5f\u6709\u7740\u90aa\u5f02\u7684\u6697\u5668\uff0c\u53ea\u662f\u60f3\u4e0d\u51fa\u4e00\u4e2a\u9002\u5f53\u7684\u5f62\u5bb9\u8bcd\uff0c\u8bf4\u660e\u90a3\u662f\u4ec0\u4e48\u4e1c\u897f\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u59d1\u5a18\u80fd\u4e0d\u80fd\u8bf4\u660e\u767d\u4e00\u4e9b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u80fd\uff0c\u56e0\u4e3a\uff0c\u6211\u5b9e\u5728\u4e5f\u4e0d\u4e86\u89e3\u771f\u5b9e\u60c5\u5f62\uff0c\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u66fe\u770b\u5230\u8fc7\u4ed6\u53d1\u5c04\u51fa\u53bb\u7684\u6697\u5668?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u6ca1\u6709\u89c1\u8fc7\uff0c\u4e0d\u8fc7\uff0c\u6211\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u80fd\u4e0d\u80fd\u8f6c\u8ff0\u4e00\u4e0b?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u77e5\u662f\u4ec0\u4e48\u6697\u5668\uff0c\u4e00\u65e6\u51fa\u624b\uff0c\u4fbf\u53d1\u51fa\u4e0d\u505c\u7684\u545c\u53eb\u4e4b\u58f0\uff0c\u800c\u4e14\uff0c\u80fd\u81ea\u8fd4\u8fd8\u5165\u624b\uff0c\u518d\u6b21\u53d1\u51fa\u3002\u201d \u3000\u3000\u9ec4\u7075\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u6697\u5668?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b64\u4e8b\u5343\u771f\u4e07\u786e\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u591a\u627f\u59d1\u5a18\u89c1\u544a\uff0c\u5728\u4e0b\u7b49\u83b7\u76ca\u975e\u6d45\uff0c\u4e24\u4f4d\u51b2\u51fa\u7b2c\u4e00\u5821\u91cd\u91cd\u62e6\u622a\uff0c\u60f3\u5fc5\u5df2\u6709\u4e9b\u7d2f\u4e86\uff0c\u8bf7\u7528\u70b9\u9152\u996d\uff0c\u65e9\u70b9\u4f11\u606f\u5427\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u8fd9\u51e0\u65e5\u4e2d\uff0c\u8bf8\u4f4d\u5c31\u4e00\u76f4\u4f4f\u5728\u8fd9\u6797\u4e2d\u8305\u820d\u4e4b\u5185\u5417?\u201d \u3000\u3000\u9ec4\u7075\u7b11\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u4e2d\u7684\u4eba\uff0c \u5df2\u7ecf\u548c\u6211\u4eec\u63a5\u89e6\u8fc7\u4e24\u6b21\uff0c\u5947\u602a\u7684\u662f\uff0c\u4ed6\u4eec\u6bcf\u4e00\u6b21\u548c\u6211\u63a5\u89e6\u4e4b\u540e\uff0c\u5c31\u4e0d\u6218\u800c\u9000\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u52a8\u8fc7\u624b?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u4e0d\u662f\u6709\u8010\u5fc3\u7684\u4eba\uff0c\u9ad8\u5e7d\u5170\u4e0d\u662f\u8fd9\u6837\u7684\u6027\u683c\uff0c\u4ed6\u4eec\u8fd9\u6837\u505a\uff0c\u53ef\u80fd\u662f\u522b\u6709\u539f\u56e0\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ec0\u4e48\u539f\u56e0\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u56de\u987e\u4f55\u5bd2\u8863\u4e00\u773c\uff0c\u9053\uff1a \u201c\u662f\u4e0d\u662f\u9ad8\u5e7d\u5170\u963b\u6b62\u4e86\u8fd9\u573a\u51b2\u7a81\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u53c8\u4e3a\u4ec0\u4e48\u62e6\u963b\u8fd9\u4e00\u573a\u51b2\u7a81\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5979\u4e0d\u613f\u5a5a\u793c\u53d7\u5230\u4ec0\u4e48\u60ca\u6270\uff0c\u6240\u4ee5\uff0c\u963b\u62e6\u4f4f\u8fd9\u4e00\u573a\u51b2\u7a81\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u82e5\u771f\u7684\u662f\u5979\uff0c\u8fd9\u4e2a\u4eba\u5b9e\u5728\u592a\u53ef\u6015\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54e6!\u54ea\u91cc\u53ef\u6015?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4eca\u5929\u4e0b\u5348\uff0c\u4ed6\u8fd8\u548c\u54b1\u4eec\u89c1\u9762\uff0c\u7adf\u7136\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\u5fc3\u4e2d\u80fd\u85cf\u5e72\u519b\u4e07\u9a6c\uff0c\u4f46\u5979\u4ecd\u7136\u80fd\u4fdd\u6301\u7edd\u5bf9\u7684\u9547\u9759\uff0c\u8fd9\u5c31\u662f\u9ad8\u5e7d\u5170\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u662f\u4e2a\u57ce\u5e9c\u5f88\u6df1\u7684\u4eba\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u548c\u5979\u7684\u6b66\u529f\u4e00\u6837\uff0c\u4f7f\u4eba\u83ab\u53ef\u9884\u6d4b\u3002\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u9ec4\u7075\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\uff0c\u6211\u6709\u51e0\u53e5\u8bdd\uff0c\u8bf4\u51fa\u6765\uff0c\u4e5f\u8bb8\u6709\u4e9b\u8d8a\u6743\uff0c\u4e0d\u8fc7\uff0c\u4e8b\u5173\u91cd\u5927\uff0c\u6211\u5982\u4e0d\u8bf4\u51fa\u6765\uff0c\u5fc3\u4e2d\u4e0d\u5b89\u5f97\u5f88\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4ec0\u4e48\u8bdd\uff0c\u8bf7\u5f53\u9762\u8bb2\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5982\u82e5\u4f60\u4eec\u771f\u8981\u74e6\u89e3\u7b2c\u4e00\u5821\uff0c\u53ea\u6709\u4e00\u4e2a\u673a\u4f1a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ec0\u4e48\u673a\u4f1a?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u60f3\u6cd5\u5b50\u628a\u9ad8\u5e7d\u5170\u63a5\u8fc7\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u591a\u8c22\u6307\u6559\uff0c\u59d1\u5a18\u8bf7\u5165\u5185\u5ba4\u4f11\u606f\u5427\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u66ff\u59d1\u5a18\u5e26\u8def\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6ca1\u6709\u79bb\u5f00\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u9ec4\u7075\u7559\u4e0b\u4e86\u4ed6\u3002 \u3000\u3000\u5728\u5434\u53cc\u5973\u9762\u524d\uff0c\u9ec4\u7075\u6709\u5f88\u591a\u8bdd\u4e0d\u4fbf\u51fa\u53e3\uff0c\u73b0\u5728\uff0c\u5219\u6ca1\u6709\u5fcc\u60ee\u4e86\u3002 \u3000\u3000\u559d\u5e72\u4e86\u9762\u524d\u4e00\u676f\u9152\uff0c\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5bd2\u8863\uff0c\u6709\u4e9b\u95ee\u9898\uff0c\u54b1\u4eec\u53ea\u6709\u5411\u4f60\u6c42\u8bc1\u4e00\u4e0b\u3002\u201d \u4f55\u5bd2\u8863\u6709\u4e9b\u5934\u75db\uff0c\u4f46\u53c8\u4e0d\u80fd\u4e0d\u56de\u7b54\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u4f60\u5429\u5490\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u9ad8\u5e7d\u5170?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\u3002\u9ec4\u7075\u9053\uff1a\u201c\u5434\u59d1\u5a18\u53ea\u544a\u8bc9\u4e86\u54b1\u4eec\u539f\u5219\uff0c\u6211\u4e0d\u660e\u767d\uff0c\u54b1\u4eec\u5982\u4f55\u628a\u5979\u62c9\u8fc7\u6765\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\u4e48\u2026\u2026\u8fd9\u4e2a\uff0c\u9ad8\u5e7d\u5170\u662f\u4e00\u4f4d\u59d1\u5a18\uff0c\u53ea\u8981\u5979\u5fc3\u6709\u6240\u5c5e\uff0c\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u529e\u6cd5\u539f\u6765\u662f\u5982\u6b64\u7b80\u5355\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5bf9!\u529e\u6cd5\u5f88\u7b80\u5355\uff0c\u53ea\u8981\u627e\u4e2a\u4eba\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8d8a\u7b80\u5355\u7684\u529e\u6cd5\uff0c\u884c\u8d77\u6765\u4e5f\u8d8a\u662f\u56f0\u96be\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5bf9!\u5bf9!\u53f6\u5144\u6700\u9002\u5408\u8fd9\u4e2a\u804c\u4f4d\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53f6\u5144\u5df2\u6709\u4e86\u7389\u5170\u53cc\u59dd\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u5821\u4e3b\u7684\u610f\u601d\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5434\u59d1\u5a18\u6ca1\u6709\u660e\u7740\u8bf4\uff0c\u4f46\u5979\u7684\u610f\u601d\uff0c\u5374\u662f\u9690\u6709\u6240\u6307\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6!\u90a3\u4eba\u662f\u8c01\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u81ea\u7136\u662f\u6307\u4f60\u4f55\u5144\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fbd\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u53ea\u6015\u6211\u4e0d\u592a\u9002\u5408\u5427?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d!\u5434\u59d1\u5a18\u5df2\u540c\u610f\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u522b\u7684\u8fd8\u4f1a\u6709\u4ec0\u4e48\u4eba\u53cd\u5bf9\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\uff0c\u5821\u4e3b\uff0c\u6211\u548c\u5434\u53cc\u5973\u8fd8\u672a\u6b63\u5f0f\u7ed3\u5a5a\uff0c\u5df2\u7ecf\u8981\u79fb\u60c5\u522b\u604b\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u662f\u5434\u59d1\u5a18\u4e0e\u4f17\u4e0d\u540c\u4e4b\u5904\uff0c\u5979\u4e0d\u662f\u666e\u901a\u5973\u5b50\uff0c\u6240\u4ee5\uff0c\u4e0d\u80fd\u4ee5\u4e16\u4fd7\u7684\u773c\u5149\u53bb\u770b\u5979\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u89c1\u8fc7\u9ad8\u5e7d\u5170\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u6211\u4e0d\u7528\u53bb\u89c1\u5979\uff0c\u5434\u59d1\u5a18\u5df2\u7ecf\u63cf\u8ff0\u7684\u5f88\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u9ec4\u7075\u4f4e\u58f0\u9053\uff1a\u201c\u4f55\u5144\uff0c\u4f60\u5fc5\u987b\u540c\u610f\u4e86\uff0c\u6211\u4eec\u624d\u80fd\u7740\u624b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u7740\u624b?\u7740\u4ec0\u4e48\u624b?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u867d\u4e0d\u660e\u767d\u8be6\u7ec6\u7684\u7ecf\u8fc7\uff0c\u4f46\u5df2\u9690\u9690\u77e5\u9053\u4e86\uff0c\u4f60\u5bf9\u5979\u4f24\u5bb3\u5f88\u6df1\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u7ba1\u6211\u4eec\u8ba1\u5212\u7684\u5982\u4f55\u5468\u5bc6\uff0c\u4f46\u8fd9\u4e0d\u662f\u6211\u4eec\u4e00\u9762\u7684\u4e8b\uff0c\u5fc5\u987b\u8fd8\u8981\u8bd5\u63a2\u4e00\u4e0b\u9ad8\u59d1\u5a18\u7684\u53cd\u5e94\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5821\u4e3b\u60f3\u8fc7\u6ca1\u6709\uff0c\u9ad8\u5929\u5065\u79ef\u6076\u5982\u5c71\uff0c\u9ad8\u5e7d\u5170\u80fd\u4f1a\u4e0d\u7ba1\u81ea\u5df1\u7684\u7236\u4eb2\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u662f\u53e6\u5916\u4e00\u4ef6\u4e8b\u4e86\uff0c\u6211\u4eec\u4e0d\u5962\u671b\u9ad8\u5e7d\u5170\u5e2e\u52a9\u6211\u4eec\u5bf9\u4ed8\u5979\u7684\u7236\u4eb2\uff0c\u4f46\u6211\u4eec\u5374\u5e0c\u671b\u5979\u4e0d\u4f1a\u76f4\u63a5\u548c\u6211\u4eec\u4e3a\u654c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4eb2\u60c5\u91cd\u5982\u5c71\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a \u201c\u6211\u77e5\u9053\uff0c\u4f46\u4e3b\u8981\u7684\u539f\u56e0\uff0c\u8fd8\u662f\u9ad8\u5929\u5065\u7684\u672c\u8eab\uff0c\u9ad8\u5e7d\u5170\u5982\u662f\u660e\u767d\u662f\u975e\u7684\u4eba\uff0c\u5979\u4e0d\u4f1a\u8d5e\u6210\u7236\u4eb2\u4e3a\u975e\u4f5c\u6b79\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e8b\u60c5\u5c31\u8fd9\u6837\u51b3\u5b9a\u4e86\uff0c\u4f60\u4e5f\u56de\u53bb\u4f11\u606f\u4e00\u4e0b\uff0c\u6211\u8fd9\u5c31\u5b89\u6392\u53eb\u4ed6\u4eec\u4e0b\u624b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u5821\u4e3b\u8fd9\u662f\u4e0b\u4ee4\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5982\u82e5\u975e\u6211\u4e0b\u4ee4\u4e0d\u53ef\uff0c\u5c31\u7b97\u6211\u8fd9\u662f\u4e0b\u4ee4\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u672a\u518d\u53d1\u8a00\uff0c\u8d77\u8eab\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u4f55\u5bd2\u8863\u6d88\u5931\u7684\u80cc\u5f71\uff0c\u53f6\u957f\u9752\u4f4e\u58f0\u7b11\u9053\uff1a \u201c\u4f55\u5144\u5f88\u56fa\u6267\uff0c\u5bf9\u8fd9\u4ef6\u4e8b\uff0c\u4ed6\u597d\u50cf\u8fd8\u6709\u4e9b\u4e0d\u592a\u613f\u610f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u62e9\u5584\u56fa\u6267\uff0c\u8fd9\u4e5f\u662f\u5434\u53cc\u5973\u80fd\u4e00\u4e0b\u7231\u4ed6\u7684\u539f\u56e0\uff0c\u4ed6\u82f1\u4fca\u3001\u6f47\u6d12\uff0c\u4f46\u5374\u4e0d\u98ce\u6d41\uff0c\u80fd\u591f\u4e13\u60c5\uff0c\u90a3\u5c31\u6709\u4e86\u4e00\u79cd\u5f88\u7279\u6b8a\u7684\u6c14\u8d28\u3002\u201d \u3000\u3000\u9ec4\u7075\u5373\u5e2d\u63d0\u51fa\u4e86\u81ea\u5df1\u7684\u8ba1\u5212\u3002 \u3000\u3000\u53f6\u957f\u9752\u3001\u4e07\u80dc\u3001\u5c60\u65e0\u65b9\uff0c\u90fd\u5206\u914d\u4e86\u5de5\u4f5c\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u5ffd\u7136\u4e0b\u8d77\u4e86\u96e8\uff0c\u96e8\u4e0d\u5927\uff0c\u4f46\u96fe\u6c14\u5f88\u6d53\uff0c\u9ec4\u7075\u7b49\u505c\u8eab\u6797\u4e2d\uff0c\u90fd\u88ab\u4e00\u91cd\u96fe\u6c14\u7b3c\u7f69\u3002 \u3000\u3000\u5434\u53cc\u5973\u88ab\u62db\u5f85\u5728\u7b2c\u4e8c\u5e62\u8305\u820d\u4e2d\uff0c\u8ddd\u79bb\u9ec4\u7075\u7b49\u4f4f\u7684\u7b2c\u4e00\u5e62\u8305\u820d\uff0c\u6709\u5341\u4e08\u5de6\u53f3\u3002 \u3000\u3000\u534e\u5999\u771f\u4e00\u76f4\u5728\u966a\u7740\u5979\u3002 \u3000\u3000\u8fd9\u7247\u6742\u6797\uff0c\u80cc\u4f9d\u9ad8\u5cf0\uff0c\u5e7f\u88a4\u5341\u91cc\u3002 \u3000\u3000\u6797\u4e2d\u672c\u65e0\u623f\u820d\uff0c\u8fd9\u4e8c\u5e62\u8305\u820d\uff0c\u8fd8\u662f\u9ec4\u7075\u7b49\u4e34\u65f6\u642d\u5efa\u800c\u6210\u3002 \u3000\u3000\u9634\u9633\u5821\u7684\u4eba\u624b\uff0c\u5df2\u9646\u7eed\u5230\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u4f46\u90fd\u88ab\u9ec4\u7075\u9063\u5e03\u5728\u56db\u5468\uff0c\u5316\u6574\u4e3a\u96f6\uff0c\u800c\u4e14\uff0c\u4ed6\u4eec\u90fd\u7ecf\u8fc7\u4e86\u5f88\u4ed4\u7ec6\u7684\u6539\u88c5\uff0c\u626e\u6210\u4e86\u519c\u4eba\uff0c\u6a35\u592b\u7b49\u5c71\u5c45\u6751\u6c11\u3002 \u3000\u3000\u53ea\u6709\u4e07\u80dc\u5e26\u6765\u7684\u4e8c\u5341\u540d\u5200\u624b\uff0c\u5e03\u5b88\u5728\u6797\u4e2d\uff0c \u3000\u3000\u5df2\u662f\u8fd1\u5348\u65f6\u95f4\uff0c\u4f46\u6d53\u91cd\u7684\u96fe\u6c14\uff0c\u4f7f\u5f97\u6797\u5185\u7684\u666f\u7269\u89c6\u754c\u4e0d\u660e\uff0c\u4e8c\u4e08\u4ee5\u5916\u7684\u666f\u7269\uff0c\u90fd\u6709\u4e9b\u770b\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u9ec4\u7075\u3001\u4f55\u5bd2\u8863\u3001\u53f6\u957f\u9752\u3001\u5c60\u65e0\u65b9\u3001\u4e07\u80dc\uff0c\u805a\u5c45\u5728\u7b2c\u4e00\u5e62\u8305\u820d\u4e2d\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4e94\u4eba\u56f4\u5750\u5728\u4e00\u5f20\u6728\u684c\u4e0a\uff0c\u6b63\u5546\u91cf\u5982\u4f55\u5bf9\u4ed8\u7b2c\u4e00\u5821\u7684\u529e\u6cd5\u3002 \u3000\u3000\u7ecf\u8fc7\u4e86\u957f\u65f6\u95f4\u7684\u8bf4\u670d\uff0c\u4f55\u5bd2\u8863\u5df2\u63a5\u53d7\u4e86\u9ec4\u7075\u7684\u4ee4\u8c15\u3002 \u3000\u3000\u7b54\u5141\u4e86\u9ad8\u5e7d\u5170\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u96e8\u52bf\u6e10\u5927\uff0c\u96fe\u6c14\u66f4\u91cd\uff0c\u5929\u5730\u95f4\u4e00\u7247\u660f\u6697\u3002 \u3000\u3000\u96e8\u6253\u6811\u53f6\uff0c\u53d1\u51fa\u4e00\u79cd\u6025\u9aa4\u7684\u6c99\u6c99\u4e4b\u58f0\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4e00\u58f0\u5f62\u4f3c\u9e1f\u9e23\u7684\u602a\u53eb\uff0c\u5939\u5728\u96e8\u4e2d\u4f20\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u9ec4\u7075\u970d\u7136\u7ad9\u8d77\u4e86\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u6709\u4eba\u6765\u4e86\u3002\u201d \u3000\u3000\u5176\u5b9e\uff0c\u4e07\u80dc\u3001\u5c60\u65e0\u65b9\u3001\u53f6\u957f\u9752\uff0c\u542c\u5230\u4e86\u90a3\u4e00\u58f0\u9e1f\u53eb\uff0c\u90fd\u4e0d\u7981\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u53ea\u6709\u4f55\u5bd2\u8863\u4e00\u7247\u832b\u7136\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u4eec\u89c4\u5b9a\u7684\u4f20\u8b66\u8baf\u53f7\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8981\u4e0d\u8981\u8fce\u4e0a\u53bb\u3002\u201d\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u4ed6\u4eec\u65e2\u7136\u5df2\u7ecf\u627e\u4e0a\u4e86\u8fd9\u4e2a\u5730\u65b9\uff0c\u8fd9\u5730\u65b9\u5df2\u4e0d\u7b97\u9690\u79d8\uff0c\u63a5\u5f85\u8fc7\u8fd9\u6b21\u8bbf\u5ba2\uff0c\u54b1\u4eec\u4e5f\u8be5\u4e54\u8fc1\u4e86\u3002\u201d \u3000\u3000\u4e00\u6761\u4eba\u5f71\uff0c\u5728\u5927\u96e8\u4e2d\u51fa\u73b0\u3002 \u3000\u3000\u4ed6\u6b65\u5c65\u5f88\u6f47\u6d12\uff0c\u800c\u4e14\uff0c\u624b\u4e2d\u8fd8\u6491\u7740\u4e00\u628a\u4f1e\u3002 \u3000\u3000\u51e0\u4e4e\u662f\u76f4\u5bf9\u7740\u8305\u820d\u884c\u8fc7\u6765\u3002 \u3000\u3000\u9ec4\u7075\u7b49\u4e5f\u5f88\u9547\u9759\uff0c\u9759\u9759\u7684\u5750\u5728\u5ba4\u4e2d\uff0c\u671b\u7740\u6765\u4eba\u3002 \u3000\u3000\u6765\u4eba\uff0c\u76f4\u884c\u5230\u8305\u820d\u524d\u9762\uff0c\u624d\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4e00\u6643\u6cb9\u5e03\u96e8\u4f1e\uff0c\u624d\u77a7\u51fa\u6765\u662f\u4e00\u4e09\u5341\u5de6\u53f3\u7684\u4e2d\u5e74\u4eba\u3002 \u3000\u3000\u84dd\u886b\u4eba\u63a2\u9996\u5411\u8305\u820d\u4e2d\u671b\u4e86\u4e00\u9635\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u96e8\u52bf\u5f88\u5927\uff0c\u5728\u4e0b\u53ef\u5426\u5165\u5ba4\u4e00\u907f\u96e8\u52bf\u3002\u201d \u9ec4\u7075\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u65e2\u7136\u662f\u6709\u5907\u800c\u6765\uff0c\u53c8\u4f55\u5fc5\u60fa\u60fa\u4f5c\u6001\u5462?\u201d \u3000\u3000\u84dd\u886b\u4eba\u653e\u4e0b\u4e86\u624b\u4e2d\u7684\u96e8\u4f1e\uff0c\u5ffd\u7136\u95f4\u4e00\u8de8\u6b65\uff0c\u884c\u5165\u4e86\u5ba4\u4e2d\u3002 \u3000\u3000\u53f6\u957f\u9752\u8eab\u8eaf\u5fae\u79fb\uff0c\u6b63\u6321\u5728\u84dd\u886b\u4eba\u9762\u524d\u3002 \u3000\u3000\u53f3\u624b\u5df3\u63e1\u5728\u4e86\u5251\u67c4\u4e4b\u524d\uff0c\u84dd\u886b\u4eba\u7a0d\u6709\u4e3e\u52a8\uff0c\u53f6\u957f\u9752\u5373\u5c06\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u901f\u5ea6\uff0c\u523a\u51fa\u4e00\u5251\u3002 \u3000\u3000\u4f46\u84dd\u886b\u4eba\u5374\u4e00\u76f4\u89c4\u89c4\u77e9\u77e9\u7ad9\u7740\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u670b\u53cb\u8d35\u59d3?\u201d \u3000\u3000\u84dd\u886b\u4eba\u9053\uff1a\u201c\u5b8b\u901a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5b8b\u5144\u662f\u6765\u81ea\u7b2c\u4e00\u5821\u4e86?\u201d \u3000\u3000\u5b8b\u901a\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1b\u201c\u4f60\u662f\u4e0d\u662f\u795e\u5200\u5802\u4e2d\u7684\u4eba?\u201d \u3000\u3000\u5b8b\u901a\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u90a3\u5c31\u8bf4\u8bf4\u5b8b\u5144\u6765\u6b64\u7684\u7528\u5fc3?\u201d \u3000\u3000\u5b8b\u901a\u9053\uff1a \u201c\u8fd9\u5730\u65b9\u4e00\u70b9\u4e5f\u4e0d\u9690\u79d8\uff0c\u4e03\u5929\u524d\uff0c\u7b2c\u4e00\u5821\u7684\u4eba\u5df2\u7ecf\u53d1\u89c9\u4e86\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u55ef!\u201d \u3000\u3000\u5b8b\u901a\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u4f60\u4eec\u7684\u4e3e\u52a8\uff0c\u4e5f\u4e00\u76f4\u5728\u7b2c\u4e00\u5821\u76d1\u89c6\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u7b2c\u4e00\u5821\u5bf9\u6211\u4eec\u5f88\u5ba2\u6c14\u4e86\uff0c\u53d1\u89c9\u4e86\u6211\u4eec\u5f88\u4e45\uff0c\u4e00\u76f4\u6ca1\u6d3e\u4eba\u6765\u5bf9\u4ed8\u6211\u4eec\u3002\u201d \u5b8b\u901a\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u5728\u4e0b\u5f88\u8f7b\u6613\u7684\u627e\u5230\u8fd9\u4e2a\u5730\u65b9\uff0c\u8bf8\u4f4d\u4e5f\u4e0d\u7528\u89c9\u7740\u60ca\u5947\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u4e00\u70b9\u4e5f\u4e0d\u60ca\u5947\uff0c\u53ea\u662f\u89c9\u7740\u6709\u4e9b\u4e0d\u89e3\u3002\u201d \u3000\u3000\u5b8b\u901a\u9053\uff1a\u201c\u4e5f\u8bb8\u5728\u4e0b\u53ef\u4ee5\u4ee3\u4f5c\u89e3\u8bf4\u3002\u201d\u9ec4\u7075\u9053\uff1a\u201c\u8d35\u5821\u4e2d\u4eba\uff0c\u65e2\u7136\u65e9\u5df2\u77e5\u4e86\u54b1\u4eec\u5728\u6b64\uff0c\u7adf\u7136\u4e0d\u5bf9\u54b1\u4eec\u653b\u88ad\uff0c\u60f3\u6765\uff0c\u8fd9\u4e2d\u95f4\u5fc5\u6709\u7f18\u6545\u4e86\u3002\u201d \u3000\u3000\u5b8b\u901a\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u8fd9\u4ef6\u4e8b\u4e00\u70b9\u4e5f\u4e0d\u590d\u6742\uff0c\u56e0\u4e3a\uff0c\u6709\u4e00\u4e2a\u4eba\uff0c\u963b\u6b62\u4e86\u7b2c\u4e00\u5821\u5bf9\u8bf8\u4f4d\u53d1\u52a8\u5165\u88ad\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\u662f\u4e0d\u662f\u9ad8\u59d1\u5a18?\u201d \u3000\u3000\u5b8b\u901a\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u4e00\u4ef6\u79d8\u5bc6\uff0c\u56e0\u4e3a\uff0c\u9ad8\u59d1\u5a18\u4e0b\u4ee4\u963b\u6b62\u5bf9\u8bf8\u4f4d\u653b\u88ad\u7684\u4e8b\uff0c\u7b2c\u4e00\u5821\u4e2d\u4eba\u65e0\u4e0d\u77e5\u6653\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u73b0\u5728\uff0c\u9601\u4e0b\u4f60\u53ef\u4ee5\u8c08\u8c08\u6765\u6b64\u7684\u7528\u5fc3\u4e86\u3002\u201d \u3000\u3000\u5b8b\u901a\u9053\uff1a\u201c\u5728\u4e0b\u5e26\u4e86\u4e00\u5c01\u4fe1\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u7684\u4fe1?\u201d \u3000\u3000\u5b8b\u901a\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\uff0c\u5e7d\u5170\u59d1\u5a18\u7684\u4fe1\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4fe1\u5728\u4f55\u5904?\u201d \u3000\u3000\u5b8b\u901a\u9053\uff1a\u201c\u5728\u4e0b\u7684\u8eab\u4e0a\u3002\u201d \u3000\u3000\u4e00\u9762\u53d6\u51fa\u4e00\u5c01\u4fe1\uff0c\u9012\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9ec4\u7075\u63a5\u8fc7\u4fe1\u5c01\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7740\u201c\u4e66\u5448\u9ec4\u5821\u4e3b\u4eb2\u62c6\u201d\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u59d1\u5a18\u5199\u7684\u597d\u4e00\u624b\u633a\u79c0\u7684\u5b57\u3002\u201d \u3000\u3000\u5b8b\u901a\u9053\uff1a\u201c\u80fd\u8005\u65e0\u6240\u4e0d\u80fd\uff0c\u4e8c\u5c0f\u59d0\u672c\u6765\u662f\u4e00\u4f4d\u6587\u6b66\u5168\u624d\u3002\u201d \u3000\u3000\u9ec4\u7075\u62c6\u5f00\u770b\u53bb\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u9053\uff1a \u201c\u4e66\u4e0a\u9ec4\u5821\u4e3b\uff0c\u8d31\u59be\u4e0d\u613f\u548c\u8d35\u5821\u6b63\u5f0f\u51b2\u7a81\uff0c\u4f46\u7b2c\u4e00\u5821\u7684\u9644\u8fd1\uff0c\u4ea6\u4e0d\u5bb9\u8d35\u5821\u4e2d\u4eba\u5e38\u5c45\uff0c\u5e0c\u671b\u8d35\u5821\u4e2d\u4eba\uff0c\u80fd\u5728\u4e09\u65e5\u5185\uff0c\u64a4\u79bb\u6b64\u5730\uff0c\u5426\u5219\u5f15\u8d77\u53cc\u65b9\u51b2\u7a81\uff0c\u522b\u602a\u8d31\u59be\u672a\u5148\u884c\u5949\u544a\u4e5f\u3002\u201d \u3000\u3000\u9ec4\u7075\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4e8c\u59d1\u5a18\u7684\u4fe1\uff0c\u5728\u4e0b\u770b\u5230\u4e86\uff0c\u8bf7\u8f6c\u544a\u9ad8\u59d1\u5a18\uff0c\u5c31\u8bf4\u5728\u4e0b\u4e0d\u53ca\u4fee\u4e66\u56de\u590d\u3002\u201d \u3000\u3000\u5b8b\u901a\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u5728\u4e0b\u544a\u8f9e\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u5b9e\u5728\u76f8\u5f53\u806a\u660e\uff0c\u9887\u6709\u4e3e\u4e00\u53cd\u4e09\u4e4b\u80fd\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9601\u4e0b\u660e\u767d\u4ec0\u4e48?\u201d \u3000\u3000\u5b8b\u901a\u9053\uff1a\u201c\u4e09\u65e5\u4e4b\u5185\uff0c\u8bf8\u4f4d\u4e5f\u8bb8\u4f1a\u64a4\u8d70\uff0c\u4e5f\u8bb8\u4e0d\u4f1a\u64a4\u8d70?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u55ef?\u201d \u3000\u3000\u5b8b\u901a\u9053\uff1a\u201c\u9601\u4e0b\u64a4\u79bb\u4e86\u6b64\u5730\u4e4b\u540e\uff0c\u81ea\u7136\u53cc\u65b9\u5e73\u5b89\u65e0\u4e8b\uff0c\u5982\u662f\u9601\u4e0b\u4e0d\u80af\u64a4\u8d70\uff0c\u90a3\u5c31\u53ea\u6709\u654c\u5bf9\u76f8\u5904\uff0c\u51c6\u5907\u548c\u655d\u5821\u4e00\u51b3\u80dc\u8d1f\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u52bf\u529b\u5e9e\u5927\uff0c\u655d\u5821\u81ea\u77e5\u96be\u4ee5\u5339\u654c\uff0c\u4e0d\u8fc7\uff0c\u54b1\u4eec\u51e0\u4e2a\u4eba\uff0c\u90fd\u8fd8\u6709\u4e00\u628a\u786c\u9aa8\u5934\uff0c\u771f\u8981\u548c\u7b2c\u4e00\u5821\u5bf9\u4e0a\u4e86\u54b1\u4eec\u4e5f\u4e0d\u4f1a\u9003\u907f\u3002\u201d \u3000\u3000\u5b8b\u901a\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u597d!\u9ec4\u5821\u4e3b\uff0c\u4e8c\u5c0f\u59d0\u7684\u4fe1\u6211\u5df2\u7ecf\u5e26\u5230\uff0c\u8d70\u4e0d\u8d70\uff0c\u662f\u4f60\u4eec\u7684\u4e8b\uff0c\u5728\u4e0b\u5374\u8be5\u8d70\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8d70\u5c31\u8d70\uff0c\u8f6c\u8eab\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u5b8b\u901a\u6d88\u5931\u5728\u96e8\u4e2d\u7684\u80cc\u5f71\uff0c\u9ec4\u7075\u5634\u89d2\u95f4\u6cdb\u8d77\u4e86\u4e00\u79cd\u51b7\u51b7\u7684\u7b11\u610f\uff0c\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\u679c\u7136\u4e0d\u51e1\uff0c\u5148\u793c\u540e\u5175\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u5c01\u4fe1\uff0c\u5f88\u50cf\u4e00\u5c01\u6311\u6218\u4e66\u3002\u201d \u3000\u3000\u5c60\u65e0\u65b9\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u4f5c\u4f55\u6253\u7b97\u5462?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u54b1\u4eec\u4e0d\u80fd\u9003\u907f\uff0c\u4e03\u8272\u5251\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e03\u79cd\u989c\u8272\u800c\u5df2\uff0c\u6211\u4e0d\u4fe1\u5b83\u771f\u4f1a\u6709\u4ec0\u4e48\u5947\u5e7b\u4e4b\u5904\u3002\u201d \u3000\u3000\u9ec4\u7075\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u4e2d\uff0c\u90fd\u628a\u9ad8\u5e7d\u5170\u5f62\u5bb9\u5f97\u5982\u5929\u795e\u4e0b\u964d\uff0c\u54b1\u4eec\u8be5\u6218\u8be5\u8d70\uff0c\u4e5f\u8be5\u65e9\u6709\u6253\u7b97\u554a\uff01\u201d \u3000\u3000\u9ec4\u7075\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6211\u8981\u4f1a\u4f1a\u5979\uff0c\u6536\u670d\u5979\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b49\u6ca1\u6709\u79bb\u5f00\uff0c\u7b2c\u56db\u5929\uff0c\u5929\u4e00\u4eae\uff0c\u9ec4\u7075\u5c31\u5e26\u7740\u56db\u5927\u5251\u4f7f\uff0c\u5750\u5728\u8305\u820d\u5916\u9762\u7b49\u5f85\u3002 \u3000\u3000\u56db\u5927\u5251\u4f7f\u662f\uff0c\u5c60\u65e0\u65b9\u3001\u4f55\u5bd2\u8863\u3001\u53f6\u957f\u9752\u3001\u4e07\u80dc\uff0c\u534e\u5999\u771f\u4ecd\u7136\u505c\u5728\u7b2c\u4e00\u8fdb\u8305\u820d\uff0c\u966a\u4f34\u7740\u5434\u53cc\u5973\u3002 \u3000\u3000\u53cc\u65b9\u9762\u65e2\u7136\u90fd\u5df2\u7ecf\u53eb\u660e\u4e86\uff0c\u4f3c\u4e4e\u662f\u8c01\u4e5f\u7528\u4e0d\u7740\u8eb2\u907f\u4ec0\u4e48? \u3000\u3000\u9ec4\u7075\u5e26\u7740\u4eba\u5927\u5927\u65b9\u65b9\u7684\u7b49\u3002 \u3000\u3000\u5bf9\u65b9\u4e5f\u662f\u660e\u76ee\u5f20\u80c6\u7684\u6765\u3002 \u3000\u3000\u6765\u7684\u662f\u56db\u4e2a\u5e74\u9752\u4eba\uff0c\u767d\u8863\u4f69\u5251\u3002 \u3000\u3000\u56db\u4e2a\u767d\u8863\u4eba\u540e\u9762\uff0c\u662f\u4e24\u4e2a\u9752\u8863\u5973\u5a62\uff0c\u4e24\u4e2a\u5973\u5a62\u5404\u6367\u7740\u4e00\u4e2a\u5251\u67b6\uff0c\u4e0a\u9762\u653e\u7740\u4e03\u652f\u5b9d\u5251\uff0c\u53ea\u4ece\u90a3\u98d8\u52a8\u7684\u5251\u7a57\u4e0a\u770b\uff0c \u5df2\u53ef\u770b\u51fa\u6765\uff0c\u90a3\u662f\u4e03\u79cd\u989c\u8272\uff0c\u7ea2\u3001\u9ec4\u3001\u84dd\u3001\u767d\u3001\u9ed1\u3001\u7eff\u3001\u7d2b\u3002\u4e8c\u4e2a\u5973\u5a62\uff0c\u5171\u5e26\u4e86\u4e00\u5341\u56db\u652f\u5251\uff0c\u6bcf\u4e00\u4e2a\u4eba\u5e26\u4e03\u652f\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4e5f\u7a7f\u7740\u4e00\u8eab\u767d\u8863\uff0c\u957f\u53d1\u9ad8\u9ad8\u633d\u8d77\u4e00\u4e2a\u5bab\u9afb\u3002 \u3000\u3000\u8eab\u540e\u9762\u7d27\u8ddf\u7740\u84dd\u8272\u52b2\u88c5\u7684\u96ea\u6885\u3002 \u3000\u3000\u96ea\u6885\u4e5f\u4f69\u7740\u4e00\u652f\u957f\u5251\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u8f7b\u8f7b\u4e00\u6325\u624b\uff0c\u56db\u4e2a\u767d\u8863\u4eba\u8ba9\u5230\u4e24\u4fa7\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4f4e\u58f0\u9053\uff1a\u201c\u5979\u5c31\u662f\u9ad8\u5e7d\u5170\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u795e\u60c5\u5f88\u51b7\u6de1\uff0c\u76ee\u5149\u6295\u6ce8\u5728\u4f55\u5bd2\u8863\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u59d0\u592b\uff0c\u5927\u59d0\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1b\u201c\u5c0f\u59b9\u5df2\u5c3d\u5230\u4e86\u5fc3\u529b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u6211\u4eec\u5fc3\u4e2d\u90fd\u5f88\u611f\u6fc0\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u76ee\u5149\u8f6c\u6ce8\u5230\u9ec4\u7075\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u662f\u9ec4\u7075?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u63a5\u5230\u6211\u7684\u4fe1\u4e86?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u63a5\u5230\u4e86\u59d1\u5a18\u7684\u5927\u51fd\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e09\u5929\u7684\u65f6\u95f4\u5f88\u5145\u5206\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u8d70\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u3002\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u4f55\u5bd2\u8863\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4ed6\u4e0d\u8981\u8d70\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u662f\u554a!\u4ed6\u8981\u7559\u4e0b\u6765\uff0c\u6211\u4eec\u53ea\u597d\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u662f\u4e00\u5821\u4e4b\u4e3b\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u81ea\u5df1\u505a\u4e3b?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u867d\u7136\u662f\u4e00\u5821\u4e4b\u4e3b\uff0c\u4f46\u5bf9\u624b\u4e0b\u7684\u4e94\u5927\u5251\u4f7f\uff0c\u4e00\u5411\u662f\u5341\u5206\u656c\u91cd\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u662f\u4e0d\u662f\u4f60\u575a\u6301\u8981\u7559\u4e0b\u6765?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u662f\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u56e0\u4e3a\uff0c\u6211\u2026\u2026\u6211\u60f3\u2026\u2026\u201d \u3000\u3000\u60f3\u4e86\u534a\u5929\uff0c\u8138\u4e5f\u7ea2\u4e86\uff0c\u5374\u4ecd\u7136\u662f\u8bf4\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff0c\u600e\u4e48\u4e0d\u8bf4\u4e86?\u201d \u3000\u3000\u53f6\u957f\u9752\u7a81\u7136\u63a5\u53e3\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u4ed6\u60f3\u518d\u89c1\u9ad8\u59d1\u5a18\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u53f6\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1b\u201c\u4e03\u5251\u8ffd\u9b42\u53f6\u957f\u9752?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6b63\u662f\u53f6\u67d0\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u8138\u4e0a\u6cdb\u73b0\u51fa\u4e00\u80a1\u7f9e\u7ea2\uff0c\u4f46\u5979\u5374\u7528\u51b7\u5389\u7684\u795e\u8272\u63a9\u9970\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u59d0\u592b\uff0c\u4ed6\u8bf4\u7684\u53ef\u662f\u771f\u8bdd?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u662f\u771f\u7684\u3002\u201c \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u597d!\u4f60\u8981\u89c1\u6211\u6709\u4ec0\u4e48\u4e8b\uff0c\u73b0\u5728\u53ef\u4ee5\u8bf4\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\u201c\u5e7d\u5170\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u4e0d\u8981\u6025\uff0c\u6162\u6162\u7684\u8bf4\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7a81\u7136\u60f3\u8d77\u4e00\u756a\u63aa\u8bcd\uff0c\u9053\uff1a \u201c\u6211\u60f3\u548c\u4f60\u8c08\u8c08\u7b2c\u4e00\u5821\u7684\u4e8b\u3002\u201d \u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d!\u6211\u5148\u548c\u9ec4\u5821\u4e3b\u8c08\u597d\u4e4b\u540e\uff0c\u54b1\u4eec\u518d\u8c08\u5427\uff01\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4ec0\u4e48\u4e8b\uff0c\u53ea\u7ba1\u5429\u5490\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u60f3\u77e5\u9053\u9ec4\u5821\u4e3b\u7684\u51b3\u5b9a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u6211\u548c\u59d0\u592b\u8bf4\u4ec0\u4e48\uff0c\u662f\u79c1\u60c5\uff0c\u7b2c\u4e00\u5821\u548c\u9634\u9633\u5821\u7684\u4e8b\uff0c\u662f\u516c\u4e8b\uff0c\u6211\u8981\u9ec4\u5821\u4e3b\u7b54\u590d\u6211\u4e00\u53e5\u8bdd\uff0c\u8981\u4e0d\u8981\u79bb\u5f00\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u9ec4\u7075\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u4e2b\u5934\uff0c\u5148\u628a\u95ee\u9898\u63d0\u51fa\u6765\uff0c\u518d\u6162\u6162\u53bb\u89e3\u51b3\uff0c\u81ea\u5df1\u5148\u7ad9\u5f97\u7a33\u7a33\u7684\uff0c\u8fdb\u5219\u53ef\u4ee5\u653b\uff0c\u9000\u5219\u53ef\u4ee5\u5b88\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u8f6c\u5ff5\uff0c\u53e3\u4e2d\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u5179\u4e8b\u4f53\u5927\uff0c\u5728\u4e0b\u8fd8\u8981\u518d\u8003\u8651\u4e00\u6bb5\u65f6\u95f4\u518d\u8bf4\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u5df2\u7ecf\u7ed9\u4e86\u4f60\u5f88\u591a\u7684\u65f6\u95f4\uff0c\u4f60\u5e94\u8be5\u4f5c\u4e2a\u51b3\u5b9a\u4e86\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u4eec\u5148\u79c1\u540e\u516c\u5427\uff0c\u4f60\u5148\u548c\u4f55\u5251\u4f7f\u8c08\u8c08\uff0c\u4e5f\u8bb8\uff0c\u4f60\u4f1a\u6539\u53d8\u81ea\u5df1\u7684\u60f3\u6cd5\uff0c\u4e0d\u8981\u7acb\u523b\u64b5\u6211\u4eec\u8d70\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8d70\u4e0d\u8d70\u662f\u4f60\u4eec\u51b3\u5b9a\u7684\u4e8b\u60c5\uff0c\u4f46\u6211\u5e0c\u671b\u4f60\u4eec\u80fd\u591f\u8d70\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u79c1\u8c0a\u4e0a\u8bf4\uff0c\u4f60\u4eec\u90fd\u662f\u59d0\u592b\u7684\u670b\u53cb\uff0c\u6211\u4e0d\u5e0c\u671b\u4f60\u4eec\u53d7\u5230\u4f24\u5bb3\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u7684\u610f\u601d\u662f\uff0c\u6211\u4eec\u5982\u82e5\u7559\u4e0b\u6765\uff0c\u5c31\u4f1a\u53d7\u5230\u4f24\u5bb3\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bf9!\u90fd\u4f1a\u53d7\u5230\u72d9\u6740\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u4eec\u81f3\u5c11\u53ef\u4ee5\u4fdd\u6301\u7740\u53cd\u51fb\u7684\u529b\u91cf\u5427\uff1f\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bf9!\u4f60\u4eec\u53ef\u4ee5\u4fdd\u6301\u53cd\u51fb\uff0c\u4e0d\u8fc7\uff0c\u4f60\u4eec\u53cd\u51fb\u7684\u6210\u6548\u4e0d\u4f1a\u592a\u5927\u3002\u201d\u9ec4\u7075\u9053\uff1a \u3000\u3000\u201c\u9634\u9633\u5821\u548c\u7b2c\u4e00\u5821\u5df2\u7ecf\u6709\u8fc7\u4e8e\u5f88\u591a\u6b21\u7684\u51b2\u7a81\uff0c\u4f46\u6211\u4eec\u90fd\u8fd8\u597d\u597d\u7684\u6d3b\u7740\u3002\u201d\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u8fd9\u4e00\u6b21\u4e0d\u540c\uff0c\u4f60\u4eec\u4e5f\u8bb8\u4f1a\u9047\u4e0a\u5f88\u6fc0\u70c8\u7684\u9635\u4ed7\uff0c\u53ef\u80fd\u4f1a\u9047\u4e0a\u6697\u6740\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6697\u6740?\u7b2c\u4e00\u5821\u4e2d\u4eba\uff0c\u4f1a\u505a\u51fa\u8fd9\u6837\u7684\u4e8b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f1a!\u4ed6\u4eec\u662f\u4e13\u95e8\u6697\u6740\u7684\u4eba\uff0c\u4ed6\u4eec\u5728\u6c5f\u6e56\u4e0a\u85c9\u85c9\u65e0\u540d\uff0c\u4e5f\u4e0d\u5728\u4e4e\u4eba\u5bb6\u600e\u4e48\u6279\u8bc4\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ed6\u4eec\u4e00\u5b9a\u6709\u4e00\u79cd\u7279\u522b\u7684\u6740\u4eba\u6280\u672f\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bf9!\u6709\u4e00\u79cd\u7279\u522b\u7684\u6740\u4eba\u6280\u672f\uff0c\u800c\u4e14\uff0c\u767e\u53d1\u767e\u4e2d\uff0c\u5f88\u5c11\u6709\u4eba\u80fd\u591f\u907f\u8fc7\u4ed6\u4eec\u7684\u72d9\u6740\u3002\u201d \u3000\u3000\u56db\u987e\u4e86\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u4eec\u5728\u8fd9\u56db\u5468\u5e03\u7f6e\u4e86\u4e0d\u5c11\u4eba\u624b\uff0c\u4ed6\u4eec\u7684\u547d\u8fd0\uff0c\u90fd\u4f1a\u5f88\u60b2\u60e8\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u600e\u6837\u4e00\u4e2a\u60b2\u60e8\u6cd5\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u4eec\u4f1a\u8fde\u5bf9\u624b\u7684\u9762\u4e5f\u6ca1\u6709\u89c1\u5230\uff0c\u4eba\u5c31\u4f1a\u5012\u4e86\u4e0b\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u59d1\u5a18\u7684\u8bdd\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u4f60\u6253\u7b97\u600e\u4e48\u529e?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u4eec\u4f1a\u5f88\u8ba4\u771f\u8ba8\u8bba\u4e00\u4e0b\u9ad8\u59d1\u5a18\u7ed9\u6211\u4eec\u7684\u8b66\u544a\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u7684\u8bdd\uff0c\u5df2\u7ecf\u4f20\u5230\u4e86\uff0c\u600e\u4e48\u4e00\u4e2a\u4f5c\u6cd5\uff0c\u90a3\u662f\u4f60\u4eec\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u8fd8\u6709\u4ec0\u4e48\u516c\u4e8b\u8981\u8c08\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e00\u4ef6\u4e8b\uff0c\u542c\u4e0d\u542c\u5728\u4f60\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u516c\u4e8b\u65e2\u5df2\u8c08\u5b8c\uff0c\u73b0\u5728\uff0c\u4f60\u53ef\u4ee5\u8c08\u8c08\u79c1\u60c5\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8eab\u884c\u5165\u8305\u820d\u3002 \u3000\u3000\u5c60\u65e0\u65b9\u3001\u53f6\u957f\u9752\u7b49\uff0c\u90fd\u968f\u7740\u8f6c\u4e86\u56de\u53bb\u3002 \u3000\u3000\u573a\u4e2d\u53ea\u4f59\u4e0b\u4e86\u4f55\u5bd2\u8863\u4e00\u4eba\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8bf4\u5427!\u59d0\u592b\uff0c\u4f60\u8981\u89c1\u6211\u6709\u4ec0\u4e48\u4e8b\uff0c\u53ef\u4ee5\u8bf4\u4e86\u5427?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u786e\u662f\u60f3\u89c1\u89c1\u59d1\u5a18\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u73b0\u5728\u89c1\u5230\u4e86\uff0c\u53ef\u4ee5\u8bf4\u4e2a\u660e\u767d\uff0c\u89c1\u6211\u6709\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u60f3\u8bf7\u6559\u59d1\u5a18\u4e00\u4ef6\u4e8b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u59d0\u592b\uff0c\u4e0d\u8981\u541e\u541e\u5410\u5410\uff0c\u6709\u4ec0\u4e48\u4e8b\uff0c\u5c31\u75db\u75db\u5feb\u5feb\u7684\u8bf4\u51fa\u6765\u5427\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u60f3\u548c\u4f60\u8c08\u8c08\u9ad8\u5821\u4e3b\u7684\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u8981\u8bf4\u6211\u7239\u4ec0\u4e48\u4e8b\uff0c\u53ea\u7ba1\u8bf4\u5427!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u56db\u987e\u4e86\u4e00\u773c\uff0c\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u6709\u4e9b\u8bdd\uff0c\u4e0d\u5b9c\u592a\u591a\u7684\u4eba\u542c\u5230\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u8981\u548c\u6211\u5355\u72ec\u8c08\u8c08\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6325\u6325\u624b\uff0c\u5bf9\u968f\u6765\u7684\u968f\u4ece\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u9000\u5f00\u53bb\u3002\u201d \u3000\u3000\u5305\u62ec\u96ea\u6885\u5728\u5185\u7684\u4ece\u4eba\uff0c\u7acb\u523b\u5411\u540e\u9000\u53bb\u3002 \u3000\u3000\u7247\u523b\u5de5\u592b\uff0c\u90a3\u4e9b\u4eba\uff0c\u90fd\u9000\u5165\u4e86\u6811\u6797\u4e4b\u4e2d\u4e0d\u89c1\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u73b0\u5728\uff0c\u53ef\u4ee5\u8bf4\u4e0b\u5427\uff01\u201d \u3000\u3000\u5979\u827a\u9ad8\u80c6\u5927\uff0c\u4e00\u4eba\u8d64\u624b\u7a7a\u62f3\u7684\u7559\u4e0b\u6765\uff0c\u8fde\u66ff\u5979\u6367\u5251\u7684\u4e2b\u5934\uff0c\u4e5f\u9000\u5165\u4e86\u6797\u4e2d\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u5982\u82e5\u6211\u4eec\u80fd\u8bc1\u660e\uff0c\u4ee4\u5c0a\u786e\u7136\u4f5c\u4e86\u5f88\u591a\u6709\u6096\u516c\u4e49\u3001\u89c1\u4e0d\u5f97\u4eba\u7684\u4e8b\uff0c\u4f60\u662f\u5426\u8fd8\u8981\u7ef4\u62a4\u4ed6?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u8bf4\uff1a\u201c\u4f60\u6709\u8bc1\u636e?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u6709\u5f88\u591a\u7684\u8bc1\u4eba\u53ef\u4ee5\u8bc1\u660e\uff0c\u6700\u91cd\u8981\u7684\u662f\uff0c\u4f60\u53ef\u4ee5\u67e5\uff0c\u53ef\u4ee5\u6c42\u8bc1\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u59d0\u592b\uff0c\u516c\u4e49\u7684\u5c3a\u5ea6\uff0c\u662f\u5426\u6709\u4e00\u5b9a\u7684\u6807\u51c6\uff0c\u662f\u4e00\u4e2a\u95ee\u9898\uff0c\u4f46\u5728\u6c5f\u6e56\u4e0a\u7239\u7684\u540d\u8a89\u5f88\u597d\uff0c\u96be\u9053\u522b\u4eba\u90fd\u662f\u778e\u5b50\uff0c\u90fd\u4e0d\u4f1a\u5206\u8fa8\u51fa\u9ed1\u767d\uff0c\u597d\u574f?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u6211\u8bf4\u7684\u90fd\u662f\u771f\u8bdd\uff0c\u9ad8\u5821\u4e3b\u7684\u58f0\u8a89\uff0c\u548c\u4ed6\u7684\u884c\u4e8b\u65b9\u6cd5\uff0c\u5b8c\u5168\u662f\u4e24\u9762\u4eba!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u53ea\u6709\u8fd9\u4e9b\u4e8b\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\uff0c\u8fd8\u6709\u4e00\u4ef6\u4e8b\uff0c\u8981\u548c\u4f60\u8c08\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u8138\u4e0a\u95ea\u63a0\u4e00\u62b9\u60ca\u559c\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u597d!\u5c0f\u59b9\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53cc\u5973\u5bf9\u4f60\u5f88\u63a8\u5d07\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59d0\u90fd\u8bf4\u6211\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u8bf4\u4f60\u5982\u80af\u5e2e\u52a9\u6211\u4eec\uff0c\u4e8b\u60c5\u5c31\u597d\u89e3\u51b3\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8981\u6211\u5e2e\u52a9\u4f60\u4eec?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5bf9!\u542c\u8bf4\uff0c\u9ad8\u5821\u4e3b\u5bf9\u4f60\u5341\u5206\u770b\u91cd\uff0c \u4f60\u5982\u529d\u529d\u4ed6\uff0c\u5f88\u53ef\u80fd\u4f1a\u4f7f\u4ed6\u7184\u53bb\u4e89\u9738\u6c5f\u6e56\u7684\u96c4\u5fc3\u3001\u4f01\u56fe\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59d0\u53ea\u8bf4\u4e86\u8fd9\u4e9b\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd8\u8bf4\uff0c\u8fd8\u8bf4\u4e86\u4e9b\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d0\u592b\uff0c\u6211\u4eec\u4e4b\u95f4\u6ca1\u6709\u4ec0\u4e48\u4e0d\u597d\u8bf4\u7684\uff0c\u4f60\u8fd8\u987e\u8651\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u8bf4\uff0c\u5979\u8bf4\u8981\u6211\u2026\u2026\u5509!\u5b8c\u5168\u662f\u80e1\u8bf4\u516b\u9053\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u59d0\u592b\uff0c\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4e8b\u554a!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u6211\u4eec\u90fd\u5e0c\u671b\u8bf7\u4f60\u8fc7\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fc7\u5230\u54ea\u91cc?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fc7\u5230\u6211\u4eec\u8fd9\u8fb9\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7239\uff0c\u4f60\u4eec\u662f\u6211\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5f88\u4ed4\u7ec6\u7aef\u8be6\u4e86\u9ad8\u5e7d\u5170\uff0c\u53d1\u89c9\u5979\u5b9e\u5728\u5f88\u7f8e\uff0c\u7f8e\u5f97\u548c\u5434\u53cc\u5973\u6709\u4e9b\u4e0d\u540c\uff0c\u5434\u53cc\u5973\u5982\u82e5\u50cf\u4e00\u682a\u5a07\u8273\u6b32\u6ef4\u7684\u7261\u4e39\uff0c\u9ad8\u5e7d\u5170\u5c31\u50cf\u4e00\u682a\u76db\u653e\u7684\u6625\u5170\u3002 \u3000\u3000\u5170\u82b1\u591a\u5a07\u5ae9\uff0c\u9ad8\u5e7d\u5170\u6bd4\u8d77\u5434\u53cc\u5973\u591a\u4e86\u4e00\u80a1\u5a07\u5ae9\u7684\u5473\u9053\u3002 \u3000\u3000\u6c89\u601d\u4e86\u597d\u4e00\u9635\uff0c\u4f55\u5bd2\u8863\u7ec8\u4e8e\u60f3\u51fa\u4e86\u4e00\u53e5\u8bdd\uff0c\u9053\uff1a\u201c\u7236\u5973\u60c5\u610f\uff0c\u4e0e\u751f\u4ff1\u6765\uff0c\u6211\u4eec\u4e4b\u95f4\u7684\u611f\u60c5\uff0c\u4f3c\u4e4e\u662f\u9700\u8981\u4e00\u6bb5\u65f6\u95f4\uff0c\u624d\u80fd\u5efa\u7acb\u8d77\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u8bf4\u5462!\u4f60\u51c6\u5907\u600e\u6837\u548c\u6211\u5efa\u7acb\u5173\u7cfb?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u4e00\u5b9a\u8981\u6211\u5f53\u9762\u8bf4\u6e05\u695a\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u60f3\u4e0d\u51fa\uff0c\u4e16\u754c\u4e0a\u6709\u4e00\u79cd\u4ec0\u4e48\u611f\u60c5\uff0c\u53ef\u4ee5\u4f7f\u7236\u5973\u7684\u611f\u60c5\u7834\u88c2\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u592b\u5987\u7684\u60c5\u610f\uff0c\u662f\u4e0d\u662f\u5f88\u91cd?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5f88\u91cd\uff0c\u4f46\u8fd8\u8981\u52a0\u4e0a\u4e00\u79cd\u4e1c\u897f\uff0c\u624d\u66f4\u591f\u4efd\u91cf\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u52a0\u4e0a\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u4e49\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u59d0\u592b\uff0c\u73b0\u5728\uff0c\u4f60\u8981\u6211\u5ac1\u7ed9\u54ea\u4e00\u4e2a?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e09\u5927\u5251\u4f7f\u4e2d\uff0c\u6709\u4e24\u4e2a\u90fd\u4e0d\u9519\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54ea\u4e24\u4f4d?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53f6\u957f\u9752\u548c\u4e07\u80dc\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u8138\u8272\u4e00\u53d8\uff0c\u4f46\u7acb\u523b\u53c8\u6062\u590d\u4e86\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u8981\u4e0d\u8981\u6211\u6279\u8bc4\u4ed6\u4eec\u4e24\u4f4d\u51e0\u53e5!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d!\u5728\u4e0b\u5012\u8fd8\u672a\u77a7\u51fa\u4ed6\u4eec\u6709\u4ec0\u4e48\u7f3a\u70b9\uff1f\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1b\u201c\u53f6\u957f\u9752\u5f88\u6e05\u79c0\uff0c\u79c0\u800c\u4fca\uff0c\u4f46\u5374\u7f3a\u5c11\u90a3\u4e00\u80a1\u6c89\u7a33\u4e4b\u6c14\uff0c\u6709\u4e9b\u98d8\u6d6e\uff0c\u5c31\u50cf\u4ed6\u7684\u8ffd\u9b42\u4e03\u5251\u4e00\u6837\uff0c\u867d\u7136\u591f\u51f6\u72e0\uff0c\u4f46\u5374\u4e0d\u662f\u5251\u9053\u6b63\u5b97\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u518d\u8bf4\u4e07\u80dc\u5427\uff0c\u4ed6\u786e\u6709\u4e00\u80a1\u5f88\u6c89\u7a33\u7684\u6c14\u52bf\uff0c\u4f46\u5374\u5c11\u4e86\u90a3\u4e00\u79cd\u79c0\u4fca\u4e4b\u6c14\uff0c\u4e00\u822c\u6765\u8bf4\uff0c\u4e24\u4e2a\u4eba\u90fd\u7b97\u4e0d\u4e0a\u4ec0\u4e48\u51fa\u7c7b\u62d4\u8403\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u4e00\u4e2a\u4eba\uff0c\u5982\u8981\u5341\u5206\u5b8c\u7f8e\uff0c\u53ea\u6015\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u60f3\u77e5\u9053\u4e48?\u6211\u4eec\u7b2c\u4e00\u5821\u4e2d\uff0c\u6709\u5f88\u591a\u7684\u4eba\uff0c\u6bd4\u4ed6\u4eec\u4e24\u4e2a\u8fd8\u597d\u4e00\u4e9b\uff0c\u7528\u4e0d\u7740\u4f60\u6765\u66ff\u6211\u505a\u5a92\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u4eec\u9634\u9633\u5821\u4e2d\uff0c\u6ca1\u6709\u4eba\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u53ea\u6709\u4e00\u4e2a\uff0c\u6211\u6709\u4e9b\u559c\u6b22\uff0c\u53ea\u53ef\u60dc\uff0c\u90a3\u4e2a\u4eba\u5df2\u7ecf\u662f\u4f7f\u541b\u6709\u5987\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u4eba\u662f\u8c01?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u82e5\u88c5\u7cca\u6d82\uff0c\u6211\u5c31\u53ea\u6709\u8bf4\u660e\u767d\u4e86\uff0c\u90a3\u4e2a\u4eba\u5c31\u662f\u4f60\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u8981\u8c08\u7684\u4e8b\uff0c\u662f\u4e0d\u662f\u5df2\u7ecf\u8c08\u5b8c\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u8981\u6211\u8bf4\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ec0\u4e48\u4e5f\u4e0d\u7528\u8bf4\u4e86\uff0c\u4f60\u5df2\u7ecf\u8bf4\u4e86\u5f88\u591a\u7684\u8bdd\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5434\u53cc\u5973\u5462\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u89c1\u5979\u9732\u9762?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u662f\u4e0d\u662f\u8981\u89c1\u89c1\u5979?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u6765\u662f\u5ba2\u4eba\uff0c\u5979\u65e2\u7136\u4e0d\u80af\u89c1\u6211\uff0c\u90a3\u5c31\u4e0d\u7528\u89c1\u4e86\u3002\u201d \u53ea\u542c\u4e00\u9635\u54af\u54af\u7684\u7b11\u58f0\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a \u201c\u4e8c\u59b9\uff0c\u53ef\u662f\u602a\u59ca\u59ca\u6ca1\u6709\u63a5\u4f60?\u201d \u3000\u3000\u968f\u7740\u7b11\u58f0\uff0c\u5434\u53cc\u5973\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\uff0c\u59b9\u59b9\u6765\u6b64\u7684\u7528\u5fc3\uff0c\u4f60\u662f\u5426\u660e\u767d\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u660e\u767d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\u591a\u539f\u8c05\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u76ee\u524d\uff0c\u4f60\u8fd8\u662f\u7b2c\u4e00\u5821\u4e2d\u4eba\uff0c\u9634\u9633\u5821\u5374\u662f\u7b2c\u4e00\u5821\u7684\u654c\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\uff0c\u4ed6\u4eec\u662f\u6697\u88ad\uff0c\u6740\u4e0d\u5b8c\u9634\u9633\u5821\u4eba\uff0c\u4e0d\u4f1a\u505c\u624b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u795e\u5200\u5802\u7684\u6740\u624b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d!\u662f\u6c34\u706b\u65e0\u60c5\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u771f\u7684\u51c6\u5907\u8981\u5927\u5f00\u6740\u6212\u4e86?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u662f\u6211\uff0c\u662f\u7239\u51b3\u5b9a\u7684\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u4f60\u6ca1\u6709\u529d\u8fc7\u7239\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5927\u59ca\uff0c\u6211\u5df2\u7ecf\u5c3d\u4e86\u5fc3\u529b\uff0c\u65e9\u5df2\u7ecf\u901a\u77e5\u4e86\u4ed6\u4eec\uff0c\u6709\u8db3\u591f\u7684\u65f6\u95f4\u8ba9\u4ed6\u4eec\u64a4\u8d70\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u73b0\u5728\u7239\u5728\u4f55\u5904?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\u6ca1\u6709\u6765\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u5c31\u7b97\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u54b1\u4eec\u59ca\u59b9\u5e94\u8be5\u597d\u597d\u7684\u8c08\u8c08\uff0c\u662f\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8c08\u4ec0\u4e48\u5462? \u3000\u3000\u5434\u53cc\u5973\u9053\u201c\u8c08\u4f60\u3001\u6211\u3001\u4ed6\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u53c8\u662f\u8c01?\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u662f\u4f55\u5bd2\u8863\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u7684\u59d0\u592b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u59d0\u592b\u548c\u5c0f\u59e8\u4e4b\u95f4\uff0c\u603b\u8be5\u6709\u4e00\u70b9\u60c5\u610f\u5427\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5c0f\u59b9\u5982\u662f\u5168\u65e0\u60c5\u610f\u4e4b\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u901a\u77e5\u4f60\u4eec\uff0c\u8981\u4f60\u4eec\u65e9\u4e9b\u79bb\u5f00\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u54b1\u4eec\u4e0d\u8c08\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u8c08\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54b1\u4eec\u59d0\u59b9\u76f8\u5904\u5f97\u4e0d\u9519\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u672c\u6765\u662f\u60c5\u540c\u624b\u8db3\uff0c\u4f46\u5927\u59ca\u5374\u79bb\u6211\u4eec\u800c\u53bb\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4e8c\u59b9\uff0c\u5728\u5bb6\u4ece\u7236\u6bcd\uff0c\u51fa\u5ac1\u4ece\u4e08\u592b\uff0c\u54b1\u4eec\u5973\u4eba\uff0c\u5c31\u662f\u8fd9\u70b9\u5403\u4e8f\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\uff0c\u4f60\u4ece\u592b\u5374\u53cd\u4e86\u7239\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u6211\u597d\u60f3\u4f60\u8fc7\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8981\u6211\u5230\u54ea\u91cc\u53bb?\u201d \u3000\u3000\u5434\u53cc\u5973\u4e3e\u6b65\u5411\u9ad8\u5e7d\u5170\u884c\u4e86\u8fc7\u53bb\uff0c\u4e00\u9762\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u4e8c\u59b9\uff0c\u5a25\u7687\u3001\u5973\u82f1\u7684\u4e8b\uff0c\u4f60\u89c9\u5f97\u5982\u4f55?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\uff0c\u4f60\u660e\u8bf4\u5427\uff0c\u4e0d\u7528\u7ed5\u5708\u5b50\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d!\u54b1\u4eec\u5171\u4e8b\u4e00\u592b\uff0c\u4f60\u613f\u4e0d\u613f\u610f?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8981\u6211\u4f5c\u4f55\u5bd2\u8863\u7684\u59be?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u6211\u59d0\u59b9\uff0c\u8fd8\u5206\u4ec0\u4e48\u59bb\u59be\u3001\u5927\u5c0f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59d0\uff0c\u4f55\u5bd2\u8863\u662f\u4e0d\u9519\uff0c\u8001\u5b9e\u8bf4\uff0c\u4ed6\u5728\u6211\u5fc3\u76ee\u4e2d\uff0c\u4e5f\u6709\u5f88\u91cd\u7684\u4efd\u91cf\uff0c\u81ea\u6211\u61c2\u4e8b\u4ee5\u6765\uff0c\u6211\u5fc3\u4e2d\u7b2c\u4e00\u6b21\u95ef\u8fdb\u6765\u7684\u7537\u4eba\uff0c\u5c31\u662f\u4ed6\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u8fb9\u8bf4\u8fb9\u8d70\uff0c\u79bb\u8305\u820d\u4e5f\u8d8a\u6765\u8d8a\u8fdc\uff0c\u8c08\u8bdd\u7684\u58f0\u97f3\uff0c\u4e5f\u8d8a\u6765\u8d8a\u5c0f\uff0c\u522b\u4eba\u5df2\u7ecf\u65e0\u6cd5\u542c\u5230\u4e86\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54b1\u4eec\u59d0\u59b9\u662f\u5fc3\u6709\u7075\u7280\uff0c\u6211\u4e5f\u65e9\u611f\u89c9\u5230\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4ed6\u5df2\u4e3a\u4f60\u6240\u6709\uff0c\u5c0f\u59b9\u4e5f\u4e0d\u613f\u518d\u63d2\u4e00\u811a\u4e86\uff0c\u8fd9\u51e0\u5929\uff0c\u6211\u60f3\u4e86\u5f88\u591a\uff0c\u54b1\u4eec\u662f\u597d\u59d0\u59b9\u53c8\u4f55\u82e6\u4e3a\u4e00\u4e2a\u7537\u4eba\u53bb\u4e89\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u662f\u4e0d\u662f\u4f60\u89c9\u5f97\u592a\u59d4\u5c48\u4e86?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f55\u5bd2\u8863\u662f\u4e0d\u9519\uff0c\u4f46\u5927\u59d0\u662f\u5929\u4eba\uff0c \u4f60\u914d\u4ed6\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\uff0c\u81f3\u4e8e\u5c0f\u59b9\u4e48\uff0c\u4e5f\u4e0d\u6562\u5984\u81ea\u83f2\u8584\uff0c\u6211\u4e5f\u914d\u5f97\u4e0a\u4ed6\uff0c\u4f46\u5982\u54b1\u4eec\u59d0\u59b9\u914d\u4ed6\u4e00\u4e2a\uff0c\u90a3\u5c31\u672a\u514d\u6709\u4e9b\u592a\u8fc7\u5206\u4e86\uff0c\u59d0\u59d0\u5403\u4e8f\uff0c\u6211\u4e5f\u59d4\u5c48\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f46\u5374\u6709\u4e00\u4e2a\u4eba\uff0c\u5360\u4e86\u5f88\u5927\u7684\u4fbf\u5b9c!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8c01?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u5bd2\u8863\u5440!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u4ed6\u5360\u4fbf\u5b9c?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u4ed6\u95ef\u5165\u4e86\u6211\u4eec\u7684\u5fc3\u7075\u3002\u201d \u3000\u3000\u2014\u987f\uff0c\u53c8\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u89c1\u8fc7\u5f88\u591a\u7684\u7537\u4eba\uff0c\u4f46\u4f60\u770b\u4e0a\u51e0\u4e2a?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59d0\uff0c\u5b9e\u5728\u8bf4\uff0c\u6211\u6ca1\u6709\u5f88\u7528\u5fc3\u53bb\u770b\u8fc7\u4e00\u4e2a\u7537\u4eba\uff0c\u60f3\u4e0d\u5230\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u4f60\u5374\u770b\u4e0a\u4e86\u5bd2\u8863\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59d0\uff0c\u4f60\u8bf4\uff0c\u8fd9\u662f\u5b7d\u503a\u5462?\u8fd8\u662f\u7f18\u4efd?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u7684\u770b\u6cd5\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u5c31\u662f\u4e0d\u77e5\u9053\uff0c\u624d\u95ee\u5927\u59d0\u554a!\u201d \u3000\u3000\u5434\u53cc\u5973\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8981\u6211\u8bf4\u4e48\uff0c\u54b1\u4eec\u90fd\u662f\u4e0a\u8f88\u5b50\u6b20\u4e86\u4ed6\u7684!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u8fd8\u6ca1\u6709\u6b20\u4ed6\uff0c\u6b20\u4ed6\u7684\u662f\u4f60!\u201d \u3000\u3000\u5434\u53cc\u5973\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u54b1\u4eec\u59d0\u59b9\u76f8\u5904\uff0c\u8868\u9762\u4e0a\uff0c\u6211\u5f88\u521a\u5f3a\uff0c\u4f46\u9aa8\u5b50\u91cc\uff0c\u4f60\u5374\u662f\u6bd4\u6211\u8fd8\u8981\u521a\u5f3a\u4e00\u4e9b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59d0\uff0c\u5728\u7b2c\u4e00\u5821\u4e2d\u65f6\uff0c\u6211\u54ea\u91cc\u4e0d\u8ba9\u7740\u4f60\uff0c\u4f46\u73b0\u5728\uff0c\u6211\u597d\u50cf\u4e0d\u80fd\u518d\u8ba9\u4f60\u4e86\uff0c\u662f\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u8fc7\u53bb\uff0c\u4f60\u662f\u5927\u59d0\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u5c31\u4e0d\u662f\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u73b0\u5728\u5e94\u8be5\u4e0d\u662f\u4e86\uff0c\u4f60\u5df2\u7ecf\u79bb\u5f00\u4e86\u7b2c\u4e00\u5821\uff0c\u4e5f\u8bb8\uff0c\u6709\u4e00\u5929\uff0c\u6211\u4eec\u4f1a\u5175\u620e\u76f8\u89c1\uff0c\u90a3\u65f6\uff0c\u8fd8\u6709\u4ec0\u4e48\u59d0\u59b9\u7684\u60c5\u5206\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u4e5f\u5728\u60f3\u8fd9\u4e2a\u95ee\u9898\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ec0\u4e48\u95ee\u9898?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5982\u82e5\u6211\u4eec\u771f\u5f62\u6210\u4e86\u654c\u5bf9\u4e4b\u52bf\uff0c\u53ea\u6015\u6211\u4eec\u78b0\u5934\u7684\u673a\u4f1a\u5f88\u5927\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u5982\u65e0\u6cd5\u5584\u7ec8\uff0c\u53ea\u6015\u8fd9\u573a\u706b\u5e76\uff0c\u65e0\u6cd5\u9003\u907f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5014\u5f3a\u7684\u59b9\u59b9\uff0c\u4f60\u5fc3\u4e2d\u65e9\u6709\u8fd9\u79cd\u6253\u7b97\u4e86\uff0c\u662f\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5927\u59d0\uff0c\u653e\u773c\u5f53\u4eca\u6c5f\u6e56\uff0c\u80fd\u4f7f\u5c0f\u59b9\u89c6\u4f5c\u52b2\u654c\u7684\uff0c\u4e5f\u4e0d\u8fc7\u4e09\u4e94\u4e2a\u4eba\u800c\u5df2\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u662f\u5176\u4e2d\u4e4b\u4e00?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bf9!\u5927\u59d0\u662f\u6211\u5fc3\u76ee\u4e2d\u7684\u7b2c\u4e00\u52b2\u654c\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60\u8fd9\u4e48\u63a8\u5d07\u6211\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59d0\uff0c\u5c0f\u59b9\u662f\u4e0d\u662f\u4f60\u5fc3\u76ee\u4e2d\u7b2c\u4e00\u53f7\u654c\u4eba?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u6211\u6ca1\u6709\u628a\u4f60\u5f53\u654c\u4eba\u770b\uff0c\u4e0d\u8bba\u5728\u4eba\u524d\u4eba\u540e\uff0c\u90fd\u5f88\u63a8\u5d07\u4f60\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5c0f\u59b9\u662f\u592a\u5c0f\u6c14\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4e0d\u8bba\u4f60\u5fc3\u4e2d\u600e\u4e48\u60f3\uff0c\u6211\u90fd\u4e0d\u5e0c\u671b\u54b1\u4eec\u6709\u706b\u5e76\u7684\u4e00\u5929\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u56e0\u4e3a\uff0c\u54b1\u4eec\u4e00\u65e6\u52a8\u4e0a\u624b\uff0c \u53ea\u6015\u4f1a\u5f62\u6210\u4e0d\u6b7b\u4e0d\u505c\u7684\u5c40\u9762\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7a81\u7136\u505c\u4f4f\u4e86\u811a\u6b65\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5927\u59d0\u53ef\u662f\u6015\u4f24\u4e86\u5c0f\u59b9?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u4e5f\u53ef\u80fd\u4f24\u4e86\u5927\u59d0\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u4e24\u4e2a\u672a\u518d\u591a\u8a00\uff0c\u5c31\u8fd9\u6837\u5e76\u80a9\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u76f4\u5165\u6742\u6797\u6df1\u5904\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u53c8\u4e0b\u8d77\u4e86\u96e8\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\uff0c\u8eb2\u5728\u4e00\u68f5\u5927\u6811\u4e4b\u4e0b\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7d27\u7d27\u95ed\u7740\u5634\u5df4\u3002 \u3000\u3000\u5434\u53cc\u5973\u5fcd\u8010\u4e86\u8bb8\u4e45\uff0c\u624d\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u4e8c\u59b9\u51b3\u5b9a\u4e86\u6ca1\u6709?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u4e00\u6014\uff0c\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u544a\u8bc9\u5927\u59d0\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5c31\u7b97\u5927\u59d0\u4e0d\u95ee\uff0c\u6211\u4e5f\u4f1a\u544a\u8bc9\u4f60\u6211\u7684\u7684\u51b3\u5b9a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5927\u59d0\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u7acb\u523b\u8d70\uff0c\u6700\u597d\uff0c\u5927\u59d0\u548c\u59d0\u592b\u79bb\u5f00\u9634\u9633\u5821\uff0c\u5766\u767d\u8bf4\uff0c\u6211\u6709\u4e9b\u559c\u6b22\u59d0\u592b\uff0c\u4f46\u6211\u4e0d\u80fd\u4f24\u5bb3\u5927\u59d0\uff0c\u5939\u5728\u4f60\u4eec\u4e4b\u95f4\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u6709\u4e9b\u610f\u5916\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u8fd9\u6050\u6015\u4e0d\u592a\u597d\u5427\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6211\u60f3\u8fc7\u4e86\uff0c\u544a\u8bc9\u59d0\u592b\uff0c\u6211\u5f88\u62b1\u6b49\u3002\u201d \u3000\u3000\u7b11\u4e00\u7b11\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u5434\u53cc\u5973\u671b\u7740\u9ad8\u5e7d\u5170\u7684\u80cc\u5f71\uff0c\u8138\u4e0a\u662f\u4e00\u7247\u51dd\u91cd\u4e4b\u8272\u3002 \u3000\u3000\u9ec4\u7075\u7684\u795e\u60c5\u4e5f\u5f88\u51dd\u91cd\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5434\u59d1\u5a18\uff0c\u6709\u6ca1\u6709\u529e\u6cd5\u633d\u56de?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5f88\u96be\uff0c\u5f88\u96be\uff0c\u4e5f\u8bb8\u8fd8\u6709\u4e00\u4e2a\u673a\u4f1a?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ec0\u4e48\u673a\u4f1a?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8981\u4f55\u5251\u4f7f\u5e2e\u5fd9\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u53ea\u6709\u4f60\u53bb\u8bd5\u8bd5\u770b\uff0c\u80fd\u4e0d\u80fd\u633d\u56de\u5979\u7684\u82b3\u5fc3\u201d\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u53cc\u5973\uff0c\u4f60\u77e5\u9053\u6211\u4e0d\u80fd\uff0c\u4f60\u4eec\u60c5\u540c\u9aa8\u8089\uff0c\u90fd\u4e0d\u80fd\u8bf4\u670d\u5979\uff0c\u6211\u6709\u4ec0\u4e48\u529e\u6cd5\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u73b0\u5728\uff0c\u60c5\u51b5\u4e0d\u540c\u4e86\uff0c\u9664\u4f60\u4e4b\u5916\uff0c\u522b\u4eba\u5efa\u8bae\u7684\u673a\u4f1a\u4e5f\u6ca1\u6709\u4e86\u3002\u201d \u9ec4\u7075\u9053\uff1a \u201c\u4f55\u5144\uff0c\u4e0d\u8981\u4f60\u53bb\u8bf4\u670d\uff0c\u6211\u8fd9\u91cc\u6709\u4e00\u5c01\u4fe1\uff0c\u4f60\u53ef\u4ee5\u9001\u7ed9\u5979\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53ef\u4ee5\u3002\u201d \u3000\u3000\u9ec4\u7075\u53d6\u51fa\u4e00\u4e2a\u5c01\u7b80\uff0c\u9053\uff1a\u201c\u62ff\u53bb\u7ed9\u5979\u5427!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u8fc7\u4e66\u4fe1\uff0c\u7acb\u523b\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u4f55\u5bd2\u8863\u6d88\u5931\u7684\u80cc\u5f71\uff0c\u9ec4\u7075\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u5434\u59d1\u5a18\uff0c\u9760\u5f97\u4f4f\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u7167\u6211\u7684\u770b\u6cd5\u3001\u5224\u65ad\uff0c\u5979\u5e94\u8be5\u4e0d\u4f1a\u4f24\u5bb3\u5bd2\u8863\uff0c\u4f46\u6211\u7684\u5224\u65ad\uff0c\u5df2\u7ecf\u53d1\u751f\u4e86\u4e00\u6b21\u9519\u8bef\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ec0\u4e48\u9519\u8bef?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5979\u5fc3\u4e2d\u5f88\u559c\u6b22\u5bd2\u8863\uff0c\u4f46\u5979\u7adf\u7136\u80fd\u5fcd\u53d7\u7740\uff0c\u4e0d\u8868\u8fbe\u51fa\u6765\uff0c\u800c\u4e14\uff0c\u5f88\u51b3\u7edd\u7684\u63a8\u62d2\u6211\u7684\u8bf7\u6c42\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u5979\u65e2\u7136\u975e\u8981\u5bf9\u54b1\u4eec\u4e0b\u624b\u4e0d\u53ef\uff0c\u6211\u770b\u90a3\u53ea\u6709\u5175\u6765\u5c06\u6321\uff0c\u548c\u5979\u5468\u65cb\u4e00\u756a\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u795e\u60c5\u8083\u7136\u5730\u8bf4\u9053\uff1a \u201c\u6709\u4e00\u4ef6\u4e8b\uff0c\u8bf8\u4f4d\u5fc5\u987b\u8bb0\u7740\uff0c\u9ad8\u5e7d\u5170\u7684\u4e03\u8272\u5251\uff0c\u786e\u662f\u51f6\u6076\u9738\u9053\uff0c\u4e00\u822c\u7684\u6b66\u529f\uff0c\u5f88\u96be\u9003\u5f97\u8fc7\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u9664\u4e86\u9ad8\u5e7d\u5170\u7684\u4e03\u8272\u5251\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u6015\u7684\u4eba\u7269?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u795e\u5200\u5802\u4e2d\u7684\u6740\u624b\uff0c\u4e5f\u5f88\u96be\u5bf9\u4ed8\uff0c\u4f46\u6700\u53ef\u6015\u7684\uff0c\u8fd8\u662f\u2018\u6c34\u706b\u65e0\u60c5\u2019\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u662f\u4e0d\u662f\u4e00\u79cd\u6697\u5668\uff1f\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u987e\u540d\u601d\u8bae\uff0c\u90a3\u662f\u4e00\u79cd\u548c\u6c34\u706b\u6709\u5173\u7684\u6697\u5668\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u597d\u50cf\u5982\u6b64\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u4e5f\u6ca1\u6709\u89c1\u8fc7\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4e0d\u8fc7\uff0c\u6211\u542c\u8bf4\u8fc7\u8fd9\u4e9b\u6697\u5668\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u5b50?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u542c\u8bf4\uff0c\u90a3\u53ea\u662f\u4e00\u4e2a\u957f\u5f62\u7684\u91d1\u7b52\uff0c\u91cc\u9762\u80fd\u591f\u5c04\u51fa\u6bd2\u6c34\u6bd2\u706b\uff0c\u4e0d\u8bba\u6c34\u706b\uff0c\u53ea\u8981\u89e6\u53ca\u5230\u4eba\u8eab\u4e00\u70b9\u7acb\u523b\u5c31\u4f1a\u6e83\u70c2\u800c\u6b7b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u90a3\u662f\u4e00\u79cd\u706b\u7130\u3001\u6bd2\u6c34\uff0c\u800c\u4e14\uff0c\u55b7\u5c04\u7684\u529b\u9053\u5f88\u5f3a\uff0c\u9664\u975e\u80fd\u591f\u4e8b\u5148\u5236\u6b62\u5b83\uff0c\u5426\u5219\uff0c\u5c31\u5f88\u96be\u8eb2\u5f97\u5f00\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5982\u82e5\u4ed6\u4eec\u771f\u6709\u8fd9\u6837\u7684\u6697\u5668\uff0c\u90a3\u662f\u4e00\u4e2a\u5f88\u5927\u7684\u9ebb\u70e6\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u60f3\u5230\u4e00\u4e2a\u514b\u5236\u4e4b\u6cd5\uff0c\u4f46\u4e0d\u77e5\u73b0\u5728\u662f\u5426\u8fd8\u6765\u5f97\u53ca\u51c6\u5907?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u5feb\u8bf4\u51fa\u6765\uff0c\u6211\u60f3\uff0c\u4e5f\u8bb8\u80fd\u8d76\u5236\u5f97\u53ca\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u753b\u4e00\u4e2a\u56fe\u6848\uff0c\u4f60\u53bb\u7167\u56fe\u5236\u9020\u3002\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5434\u53cc\u5973\u65e9\u5df3\u753b\u597d\u4e86\u90a3\u526f\u56fe\u6848\u3002 \u3000\u3000\u800c\u4e14\uff0c\u56fe\u6848\u4e0a\u4e5f\u65e9\u5df2\u7ecf\u8bf4\u660e\u4e86\u5236\u9020\u7684\u6750\u6599\u3002 \u3000\u3000\u9ec4\u7075\u770b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e0a\u9762\u5df2\u89e3\u8bf4\u7684\u5f88\u6e05\u695a\uff0c\u4e0d\u8fc7\uff0c\u6750\u6599\u65b9\u9762\u6211\u662f\u5426\u53ef\u6539\u53d8\u4e00\u4e9b?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u53ef\u4ee5\u3002\u201d \u3000\u3000\u9ec4\u7075\u53d6\u7b14\u5728\u4e0a\u9762\u5199\u4e86\u51e0\u4e2a\u5b57\uff0c\u9053\uff1a\u201c\u65e0\u65b9\uff0c\u8fd9\u4e2a\uff0c\u4ea4\u7ed9\u4f60\u529e\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u5148\u8d76\u5236\u4e03\u516b\u5957\u6765\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c \u65e5\u591c\u8d76\u5de5\uff0c\u5927\u6982\u4e24\u5929\u5185\u53ef\u4ee5\u8d76\u5236\u5341\u5957\uff0c\u4e0d\u8fc7\uff0c\u96be\u5904\u5728\u6d88\u606f\u8981\u7edd\u5bf9\u79d8\u5bc6\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u80fd\u6cc4\u9732\u51fa\u53bb\uff0c\u9ad8\u5e7d\u5170\u5f88\u806a\u660e\uff0c\u53ea\u8981\u5979\u542c\u5230\u6d88\u606f\uff0c\u5c31\u4f1a\u60f3\u51fa\u6539\u9020\u6c34\u706b\u65e0\u60c5\u7684\u65b9\u6cd5\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5982\u82e5\u6709\u4eba\u60f3\u6cd5\u5b50\uff0c\u628a\u2018\u6c34\u706b\u65e0\u60c5\u2019\u5077\u4e00\u652f\u51fa\u6765\u77a7\u77a7\uff0c\u4e5f\u8bb8\u80fd\u627e\u51fa\u4e00\u4e2a\u5f88\u597d\u7684\u5e94\u4ed8\u529e\u6cd5\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u6c34\u706b\u65e0\u60c5\uff0c\u662f\u7b2c\u4e00\u5821\u4e2d\u9ad8\u5ea6\u7684\u673a\u5bc6\u4e4b\u4e00\uff0c\u6211\u867d\u7136\u77e5\u9053\u6709\u8fd9\u79cd\u6697\u5668\uff0c\u4f46\u5374\u6ca1\u89c1\u8fc7\uff0c\u6211\u770b\u771f\u6b63\u77e5\u9053\u5185\u60c5\u7684\uff0c\u6050\u6015\u53ea\u6709\u9ad8\u5e7d\u5170\u548c\u9ad8\u5821\u4e3b\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u542c\u8bf4\uff0c\u5434\u59d1\u5a18\u5728\u7b2c\u4e00\u5821\u4e2d\uff0c\u5f88\u53d7\u5c0a\u91cd\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u5f88\u53d7\u5c0a\u91cd\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u65e2\u662f\u5f88\u53d7\u5c0a\u91cd\u7684\u4eba\uff0c\u96be\u9053\u5c31\u6ca1\u6709\u53c2\u4e0e\u673a\u5bc6\u7684\u673a\u4f1a\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u6211\u53c2\u4e0e\u5f88\u591a\u7684\u673a\u5bc6\uff0c\u4e0d\u8fc7\uff0c\u90a3\u4e0d\u662f\u592a\u91cd\u8981\u7684\uff0c\u771f\u6b63\u91cd\u8981\u7684\u4e8b\u60c5\uff0c\u5374\u6ca1\u6709\u8ba9\u6211\u53c2\u4e0e\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u90a3\u662f\u8bf4\uff0c\u4ed6\u4eec\u4e00\u5f00\u59cb\uff0c\u5c31\u5bf9\u59d1\u5a18\u5b58\u6709\u4e0d\u4fe1\u4efb\u7684\u5fc3\u7406\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u5185\u5fc3\u4e2d\u5bf9\u7b2c\u4e00\u5821\u7684\u60ed\u759a\uff0c\u76f8\u5bf9\u7684\u51cf\u4f4e\u4e86\u4e0d\u5c11\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d!\u54b1\u4eec\u73b0\u5728\uff0c\u8be5\u5546\u91cf\u4e00\u4e0b\uff0c\u5982\u4f55\u63a5\u5e94\u4f55\u5251\u4f7f\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u7528\u53bb\u63a5\u5e94\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u53bb\u63a5\u5e94?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9!\u9ad8\u5e7d\u5170\u662f\u4e2a\u5f88\u5c0f\u5fc3\u7684\u4eba\uff0c\u6211\u60f3\u5979\u4e00\u5b9a\u5728\u8def\u4e0a\u8bbe\u6709\u57cb\u4f0f\uff0c\u54b1\u4eec\u5982\u82e5\u63a5\u5e94\u4f55\u5bd2\u8863\uff0c\u5fc5\u5b9a\u4f1a\u5f15\u8d77\u51b2\u7a81\uff0c\u90a3\u4e0d\u4f46\u81f4\u4f55\u5bd2\u8863\u4e8e\u9669\u5730\uff0c\u800c\u4e14\uff0c\u8fd8\u4f1a\u9020\u6210\u9ad8\u5e7d\u5170\u7684\u4ec7\u89c6\u3002\u201d \u3000\u3000\u9ec4\u7075\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u96be\u9053\u54b1\u4eec\u5c31\u4e0d\u7ba1\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u6700\u597d\u4e0d\u7ba1\uff0c\u6700\u591a\u9ad8\u5e7d\u5170\u4e00\u4e2a\u9489\u5b50\u628a\u4ed6\u78b0\u56de\u6765\uff0c \u7edd\u4e0d\u4f1a\u6740\u4e86\u4ed6\uff0c\u5982\u82e5\u54b1\u4eec\u63a5\u8fd1\u4e0b\u53bb\uff0c\u540e\u679c\u5c31\u5f88\u96be\u9884\u6599\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u672a\u518d\u575a\u6301\u3002 \u3000\u3000\u4ed6\u660e\u767d\uff0c\u5434\u53cc\u5973\u6bd4\u4ed6\u66f4\u4e86\u89e3\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u4e00\u4e2a\u65f6\u8fb0\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u5434\u53cc\u5973\u7709\u5b87\u95f4\u7684\u5fe7\u6101\uff0c\u5df2\u7136\u660e\u663e\u7684\u9732\u4e86\u51fa\u6765\uff0c\u518d\u65e0\u6cd5\u5b89\u5750\u5f97\u4f4f\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u5411\u5916\u9762\u884c\u53bb\u3002 \u3000\u3000\u9ec4\u7075\u5ffd\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u4f46\u613f\u662f\u5bd2\u8863\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u679c\u7136\u662f\u4f55\u5bd2\u8863\uff0c\u6025\u6025\u5954\u5165\u8305\u820d\u3002 \u3000\u3000\u5434\u53cc\u5973\u6697\u6697\u5401\u4e00\u53e3\u6c14\uff0c\u8138\u4e0a\u4e5f\u6cdb\u8d77\u4e86\u7b11\u5bb9\uff0c\u9053\uff1a \u201c\u89c1\u8fc7\u9ad8\u5e7d\u5170\u6ca1\u6709?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u89c1\u662f\u89c1\u8fc7\u4e86\uff0c \u53ea\u53ef\u60dc\uff0c\u5bf9\u54b1\u4eec\u6ca1\u6709\u4e00\u70b9\u5e2e\u52a9\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5979\u90fd\u8bf4\u4e9b\u4ec0\u4e48\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8bf4\u5f97\u5f88\u96be\u542c\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u6709\u4ec0\u4e48\uff0c\u4f60\u5c31\u8bf4\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u8bf4\uff0c\u8981\u6211\u8f6c\u544a\u4f60\u548c\u5821\u4e3b\uff0c\u8fc7\u4e86\u5348\u65f6\uff0c\u5979\u5c31\u53ef\u80fd\u4e0b\u4ee4\u52a8\u624b\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u52a8\u624b\u7684\u610f\u601d\uff0c\u5c31\u662f\u8981\u7528\u2018\u6c34\u706b\u65e0\u60c5\u2019\u5bf9\u4ed8\u6211\u4eec\u4e86\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d\u50cf\u5982\u6b64\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u5bf9\u6b64\u4e8b\u770b\u6cd5\u5982\u4f55?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u542c\u53e3\u6c14\uff0c\u5979\u4f3c\u662f\u5df2\u7ecf\u94c1\u77f3\u5fc3\u80a0\uff0c\u4e0d\u4f1a\u518d\u548c\u6211\u4eec\u5fc3\u5b58\u548c\u610f\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u5979\u662f\u5426\u8bf4\u8fc7\uff0c\u4f60\u4e5f\u8981\u9000\u51fa\u53bb?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d\u50cf\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u770b\u6765\u8fd9\u4e00\u573a\u640f\u6740\uff0c\u4f3c\u4e4e\u662f\u5df2\u7ecf\u65e0\u6cd5\u907f\u514d\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u80fd\u591f\u8bf4\u670d\u9ad8\u59d1\u5a18\u7684\uff0c\u8fd8\u6709\u4e9b\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\uff0c\u4e0d\u8fc7\uff0c\u673a\u4f1a\u4e0d\u5927\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u53ea\u8981\u6709\u673a\u4f1a\uff0c\u5c31\u5e94\u8be5\u8bd5\u8bd5\uff0c\u975e\u5230\u7edd\u671b\u4e0d\u5f97\u5df2\uff0c\u6211\u4e0d\u613f\u548c\u5979\u4e3a\u654c\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5821\u4e3b\u4e14\u6162\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u5434\u53cc\u5973\uff0c\u63a5\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8bf7\u770b\u8d31\u59be\u5982\u4f55?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8bf4\u670d\u80fd\u529b\uff0c\u6211\u89c9\u5f97\u5f3a\u8fc7\u5821\u4e3b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u9053\uff1a\u201c\u5192\u9669\u592a\u5927\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u600e\u4e48\u8bf4?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5979\u5fc3\u4e2d\u53ef\u80fd\u6709\u5f88\u591a\u7684\u6028\u6068\uff0c\u4e5f\u53ef\u80fd\u4f1a\u53d1\u5230\u4e86\u4f60\u8eab\u4e0a\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5927\u6709\u53ef\u80fd\uff0c\u4e0d\u8fc7\uff0c\u6211\u81ea\u4fe1\u80fd\u4f7f\u4ed6\u5251\u4e0b\u7559\u4eba\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\u81ea\u4fe1\uff0c\u90a3\u5c31\u53bb\u5427!\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201d\u6709\u52b3\u4f55\u5144\u9001\u6211\u4e00\u7a0b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u534e\u5251\u4f7f\uff0c\u6211\u770b\u5979\u4e0d\u4f1a\u7b54\u5141\u4ec0\u4e48\uff0c\u4e0d\u7528\u53bb\u6b64\u4e00\u8d9f\u3002\u201d \u534e\u5999\u771f\u9053\uff1a\u201c\u591a\u62d6\u5ef6\u4e0a\u4e00\u5929\uff0c\u5bf9\u6211\u4eec\u5c31\u6709\u5f88\u5927\u7528\u5904\uff0c\u4f55\u5251\u4f7f\uff0c\u8bf7\u5e26\u8def\u5427\u3002\u201d \u3000\u3000\u4e24\u4eba\u79bb\u5f00\u4e86\u8305\u820d\u3002 \u3000\u3000\u884c\u51fa\u4e86\u6811\u6797\u4e4b\u5916\uff0c\u534e\u5999\u771f\u7a81\u7136\u505c\u4e0b\u811a\u6b65\uff0c\u9053\uff1a \u201c\u4f55\u5144\uff0c\u8bf7\u56de\u7b54\u6211\u4e24\u53e5\u8bdd\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u534e\u5251\u4f7f\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u559c\u6b22\u9ad8\u5e7d\u5170?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u5df2\u6709\u4e86\u59bb\u5ba4\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u5434\u59d1\u5a18\u4f3c\u4e4e\u5341\u5206\u8bda\u610f\uff0c\u4e3a\u4e86\u4f60\uff0c\u4e5f\u4e3a\u4e86\u6574\u4e2a\u6c5f\u6e56\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u4f60\u8981\u6211\u600e\u4e48\u8bf4\u5462?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8bf4\u51fa\u4f60\u5fc3\u4e2d\u60f3\u7684\u5c31\u884c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u82e5\u8bf4\u6211\u4e0d\u559c\u6b22\u5e7d\u5170\uff0c\u90a3\u4e0d\u662f\u5fc3\u4e2d\u4e4b\u8bdd\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8fd9\u5c31\u591f\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\uff0c \u4f60\u8bb0\u7740\uff0c\u6211\u6709\u6743\u4ee3\u8868\u4f60\u7b54\u5e94\u4ec0\u4e48\uff0c\u9664\u975e\uff0c\u4f60\u4e0d\u7ba1\u6211\u7684\u6b7b\u6d3b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d\u5427\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u6211\u7b54\u5e94\u4e86\uff0c\u4f60\u8981\u5c65\u884c\uff0e\u4e0d\u80fd\u9677\u5e72\u6211\u4e0d\u4fe1\u4e0d\u4e49\u4e4b\u5883\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u8981\u4ee3\u8868\u6211\u7b54\u5e94\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5f88\u591a\uff0c\u5f88\u591a\uff0c\u5305\u62ec\u9ad8\u5e7d\u5170\u7684\u5a5a\u7ea6\u5728\u5185\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e00\u4e2a\uff0c\u5979\u600e\u4e48\u4f1a\u542c\u4f60\u7684\u5462?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u90a3\u662f\u6211\u7684\u4e8b\uff0c\u6211\u73b0\u5728\u9700\u8981\u7684\u662f\u4f60\u7684\u7b54\u5141\u3002\u201d \u3000\u3000\u4fdd\u5bd2\u8863\u9053\uff1a\u201c\u597d\u5427!\u5982\u82e5\u4f60\u80fd\u8bf4\u670d\u5979\uff0c\u6211\u5c31\u7b54\u5141\u4f60\u7684\u8981\u6c42\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u597d!\u4f55\u5251\u4f7f\uff0c\u4f60\u8981\u53d8\uff0c\u4e5f\u53d8\u4e0d\u8fc7\u6765\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8eab\u4e00\u8dc3\uff0c\u4eba\u8e2a\u987f\u6773\u3002 \u3000\u3000\u671b\u7740\u534e\u5999\u771f\u7684\u80cc\u5f71\uff0c\u9ec4\u7075\u4f4e\u58f0\u8bed\u9053\uff1a \u201c\u5434\u59d1\u5a18\uff0c\u534e\u5251\u4f7f\u53bb\uff0c\u5408\u9002\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u53ef\u4ee5\uff0c\u6211\u5ffd\u7136\u89c9\u5f97\uff0c\u65e9\u8be5\u6d3e\u5979\u53bb\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u8be5\u518d\u6d3e\u4eba\u53bb\u7684\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u9ad8\u5e7d\u5170\u5df2\u7ecf\u5b8c\u5168\u62d2\u7edd\u4e86\u548c\u89e3\u7684\u53ef\u80fd\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u4eec\u6839\u672c\u4e0d\u662f\u548c\u89e3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u4eec\u628a\u5979\u7531\u7b2c\u4e00\u5821\u4e2d\u62c9\u8fc7\u6765\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u82e6\u7b11\u4e00\u4e0b\u9053\uff1a\u201c\u8fd9\u673a\u4f1a\u4e0d\u5927\u3002\u201d \u3000\u3000\u9ec4\u7075\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a \u201c\u8bf8\u4f4d\uff0c\u4e0d\u8bba\u534e\u5251\u4f7f\u6b64\u884c\u7684\u7ed3\u679c\u5982\u4f55?\u6211\u4eec\u8fd8\u8981\u6709\u5e94\u53d8\u7684\u51c6\u5907\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u51c6\u5907\u4ec0\u4e48?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bf9\u4ed8\u9ad8\u5e7d\u5170\u7684\u7a81\u88ad\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u4f60\u51c6\u5907\u548c\u4ed6\u4eec\u5728\u6b64\u4e00\u51b3\u6b7b\u6218\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4e0d\u7ba1\u5982\u4f55\uff0c\u54b1\u4eec\u603b\u4e0d\u80fd\u88ab\u4ed6\u4eec\u5413\u8dd1\u554a\uff01\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8d31\u59be\u7684\u770b\u6cd5\u4e0d\u7136\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4f55\u9ad8\u89c1\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u662f\u6697\u5668\u4e2d\u7684\u5947\u6280\uff0c\u8d31\u59be\u4e4b\u610f\uff0c\u4e0d\u5b9c\u5728\u8fd9\u91cc\u548c\u4ed6\u4eec\u52a8\u624b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4ec0\u4e48\u6253\u7b97?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u6211\u4eec\u5e94\u8be5\u9009\u62e9\u4e00\u4e2a\u5bf9\u6211\u4eec\u6709\u5229\u7684\u5730\u5f62\u3002\u201d \u3000\u3000\u9ec4\u7075\u4e48\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5bf9!\u6211\u8fd9\u5c31\u53bb\u5b89\u6392\u4e00\u4e0b\uff0c\u8bf8\u4f4d\u4e5f\u8be5\u53bb\u4f11\u606f\u4e00\u4e0b\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u5f88\u987a\u5229\u7684\u89c1\u5230\u4e86\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u90a3\u4e5f\u662f\u4e00\u5ea7\u8305\u820d\uff0c\u4e00\u5ea7\u6253\u626b\u5f97\u5f88\u5e72\u51c0\u7684\u8305\u820d\u3002 \u3000\u3000\u534e\u5999\u771f\u88ab\u5e26\u5165\u8305\u820d\u5927\u5385\u4e4b\u4e2d\u3002 \u3000\u3000\u5b88\u536b\u7684\u5341\u5206\u68ee\u4e25\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7684\u795e\u60c5\uff0c\u5341\u5206\u51b7\u6f20\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u53eb\u534e\u5999\u771f\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u9634\u9633\u5821\u4e2d\u4e94\u5927\u5251\u4f7f\u4e4b\u4e00\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u89c1\u8fc7\u4e86\u4f60\u4eec\u5821\u4e3b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u6765\u89c1\u6211\uff0c\u6709\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u534e\u5999\u771f\u56db\u987e\u4e86\u4e00\u773c\u9053\uff1a\u201c\u8fd9\u91cc\u8033\u76ee\u592a\u591a\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e8b\u65e0\u4e0d\u53ef\u5bf9\u4eba\u8a00\uff0c\u4ed6\u4eec\u542c\u5230\u4e86\u4e5f\u4e0d\u8981\u7d27\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u548c\u59d1\u5a18\u8bf4\u7684\u4e0d\u662f\u516c\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8bf4\u79c1\u4e8b?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5bf9!\u662f\u79c1\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u548c\u4f60\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4ece\u65e0\u6765\u5f80\uff0c\u6709\u4ec0\u4e48\u79c1\u4e8b\u597d\u8bf4\u3002\u201d \u534e\u5999\u771f\u9053\uff1a\u201c\u59d1\u5a18\u8ba4\u8bc6\u5434\u53cc\u5973\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5434\u53cc\u5973\u53c8\u600e\u4e48\u6837?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u59d1\u5a18\u5c4f\u9000\u5de6\u53f3\u4e4b\u540e\uff0c\u54b1\u4eec\u518d\u8bf4\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6325\u6325\u624b\uff0c\u5de6\u53f3\u9000\u4e0b\u3002 \u3000\u3000\u4f46\u4e03\u8272\u5251\u5374\u6446\u5728\u4e86\u5979\u9762\u524d\u6728\u684c\u4e0a\u3002 \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4f55\u5bd2\u8863\u5f88\u75db\u82e6\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u662f\u4f60\u4eec\u9634\u9633\u5821\u7684\u5251\u4f7f\uff0c\u5173\u6211\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4f46\u4ed6\u7684\u75db\u82e6\uff0c\u5374\u548c\u59d1\u5a18\u6709\u5173\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u8bb8\u8bf4\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6211\u4e0d\u613f\u610f\u542c\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u4ee5\u4e0d\u542c\uff0c\u4f46\u8d31\u59be\u4e00\u5b9a\u8981\u8bf4\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54fc!\u4f60\u600e\u4e48\u5982\u6b64\u5f3a\u4eba\u6240\u96be\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u4f5b\u8bf4\u6cd5\uff0c\u5730\u6d8c\u83b2\u82b1\uff0c\u542c\u8005\u5f97\u9053\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u4e0d\u542c\u7684\u4eba\uff0c\u90a3\u5c31\u4e0d\u4f1a\u5f97\u9053\u4e86?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u4f60\u53ef\u4ee5\u4e0d\u542c\uff0c\u8d31\u59be\u8bf4\u5b8c\u4e86\uff0c\u56de\u5934\u5c31\u8d70\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u597d\u5427!\u4f60\u8bf4\uff0c\u6211\u5012\u8981\u542c\u542c\uff0c\u4f60\u80fd\u8bf4\u4ec0\u4e48\u540d\u5802\u51fa\u6765\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u4eba\u751f\u673a\u4f1a\u4e0d\u5728\uff0c\u653e\u8fc7\u4e86\u773c\u524d\u7684\u673a\u9047\uff0c\u518d\u56de\u9996\uff0c\u5df2\u662f\u767e\u5e74\u8eab\uff0c\u50cf\u6211\u534e\u5999\u771f\uff0c\u5c31\u662f\u4e00\u4e2a\u5f88\u597d\u7684\u4f8b\u5b50\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u53ea\u662f\u60f3\u8bf4\u660e\u4e00\u4e0b\uff0c\u6211\u534e\u5999\u771f\u7684\u5f80\u4e8b\u7f62\u4e86\uff0c\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u75f4\u5fc3\u5973\u5b50\u8d1f\u5fc3\u6c49\uff0c\u8c01\u8981\u4f60\u90a3\u4e48\u591a\u60c5\u5462?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u5982\u591a\u60c5\uff0c\u54ea\u4f1a\u6709\u4eca\u5929\u51c4\u51c9\u7684\u4e0b\u573a\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u5f88\u51c4\u51c9\u5417?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u96be\u9053\u4f60\u89c9\u5f97\uff0c\u6211\u5f88\u5feb\u4e50?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u6211\u770b\u4e0d\u51fa\uff0c\u4f60\u6709\u4ec0\u4e48\u51c4\u51c9\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u6211\u4eca\u5e74\u4e09\u5341\u4e03\u5c81\uff0c\u5c0f\u59d1\u5c45\u5904\u72b9\u65e0\u90ce\uff0c\u4f60\u8bf4\u6211\u662f\u4e0d\u662f\u5f88\u51c4\u51c9\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4f60\u867d\u540d\u58f0\u4e0d\u592a\u597d\uff0c\u4f46\u4f60\u957f\u5f97\u4e5f\u5f53\u5f97\u7f8e\u4eba\u4e4b\u79f0\uff0c\u5b9a\u7136\u662f\u773c\u5149\u8fc7\u9ad8\uff0c\u624d\u4e0d\u80af\u8f7b\u6613\u9002\u4eba\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8d31\u59be\u4e0d\u6562\u548c\u4f60\u53ca\u5434\u5927\u5c0f\u59d0\u76f8\u6bd4\uff0c\u4f46\u6211\u4e5f\u662f\u4e2a\u9887\u4e3a\u81ea\u8d1f\u7684\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u8f7b\u5401\u4e00\u53e3\u6c14\u9053\uff1a \u201c\u8fd9\u4ef6\u4e8b\uff0c\u548c\u6211\u4eec\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u544a\u8bc9\u6211\u5462?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5c06\u4eba\u6bd4\u5df1\uff0c\u53ef\u4ee5\u53d1\u6398\u51fa\u5f88\u591a\u7684\u771f\u7406\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u54e6!\u8bf4\u4e0b\u53bb\u5427\uff01\u201d \u3000\u3000\u663e\u7136\uff0c\u534e\u5999\u771f\u7684\u8bf4\u670d\u4e4b\u529b\uff0c\u5df2\u7ecf\u53d1\u6325\u4e86\u5f88\u5927\u7684\u4f5c\u7528\u3002 \u3000\u3000\u534e\u5999\u771f\u6de1\u6de1\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a \u201c\u9ad8\u59d1\u5a18\uff0c\u8d31\u59be\u4f55\u5e78\uff0c\u51fa\u9053\u4e0d\u4e45\uff0c\u5c31\u9047\u4e0a\u90a3\u6837\u4e00\u4e2a\u7537\u4eba\uff0c\u53c8\u4f55\u4e0d\u5e78\uff0c\u4f1a\u9047\u4e0a\u4ed6\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u6837\u4e86?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4ed6\u4e0d\u7b97\u5f88\u82f1\u4fca\uff0c\u4f46\u5374\u5f88\u6709\u6027\u683c\uff0c\u7adf\u88ab\u4ed6\u95ef\u5165\u6211\u7684\u5fc3\u4e2d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u65e2\u7136\u559c\u6b22\u4ed6\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5ac1\u7ed9\u4ed6?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u4ed6\u5df2\u7ecf\u6709\u4e86\u4e00\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u5e94\u8be5\u6740\u4e86\u4ed6\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\uff0c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u6ca1\u6709\u6740\u4ed6\uff0c\u4f46\u5374\u6740\u4e86\u90a3\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u4e5f\u6ca1\u6709\u9519\u554a!\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u90a3\u4e2a\u5973\u4eba\uff0c\u662f\u4ed6\u7684\u5e08\u59b9\uff0c\u4ed6\u5e08\u7236\u4e34\u6b7b\u4e4b\u524d\uff0c\u628a\u5979\u6258\u4ed8\u7ed9\u5e08\u5144\u7167\u987e\uff0c\u4ed6\u5e76\u4e0d\u7231\u5979\uff0c\u4f46\u6211\u6740\u4e86\u90a3\u5973\u4eba\u4e4b\u540e\uff0c\u4ed6\u5374\u611f\u89c9\u5230\u6127\u5bf9\u5e08\u7236\uff0c\u6240\u4ee5\uff0c\u5c31\u62d4\u5200\u81ea\u520e\u6b7b\u4e86\u3002\u201d \u3000\u3000\u53f9\u53e3\u6c14\u63a5\u9053\uff1a \u201c\u6211\u597d\u540e\u6094\uff0c\u597d\u60b2\u75db\uff0c\u5bf9\u81ea\u5df1\u7684\u9c81\u83bd\uff0c\u611f\u5230\u5341\u5206\u7684\u4e0d\u5b89\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u771f\u7684\u662f\u592a\u9c81\u83bd\uff0c\u600e\u4e48\u4e8b\u5148\u4e0d\u95ee\u6e05\u695a\u5462?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5992\u706b\u4e2d\u70e7\uff0c\u54ea\u91cc\u8fd8\u4f1a\u95ee\u5f97\u8bb8\u591a\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ee5\u540e\u5462?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u4ed6\u6b7b\u4e86\uff0c\u6211\u7684\u5fc3\u4e5f\u6b7b\u4e86\uff0c \u624d\u843d\u4e86\u4e2a\u7f57\u5239\u7684\u532a\u53f7\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54e6!\u4ee5\u540e\uff0c \u4f60\u662f\u5426\u8fd8\u559c\u6b22\u8fc7\u522b\u7684\u7537\u4eba\uff1f\u201d \u534e\u5999\u771f\u9053\uff1a\u201c\u597d\u50cf\u6709\u8fc7\uff0c\u4f46\u6211\u5374\u4e00\u76f4\u62b9\u4e0d\u6389\u5fc3\u4e0a\u90a3\u7247\u9634\u5f71\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5509!\u4f60\u65e2\u5bf9\u4ed6\u559c\u6b22\u5f97\u90a3\u4e48\u6df1\u523b\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u4f5c\u601d\u91cf?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u53ef\u60b2\u7684\u662f\uff0c\u6211\u4e8b\u5148\u5e76\u4e0d\u77e5\u9053\uff0c\u6211\u5bf9\u4ed6\u559c\u7231\u7684\u7adf\u662f\u5982\u6b64\u4e4b\u6df1\u3002\u201d \u9ad8\u5e7d\u5170\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u6c89\u541f\u4e01\u826f\u4e45\u4e4b\u540e\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u65f6\u5019\u77e5\u9053\u7684?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4ed6\u6b7b\u4e86\u4e4b\u540e\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u672a\u6b7b\u4e4b\u524d\uff0c \u4f60\u4e3a\u4ec0\u4e48\u4e0d\u77e5\u9053\uff1f\u201d \u534e\u5999\u771f\u9053\uff1a\u201c\u80fd\u591f\u5e38\u89c1\u5230\u7684\u4e1c\u897f\uff0c\u60f3\u4e0d\u5230\u4ed6\u7684\u53ef\u8d35\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e16\u4e0a\u6709\u5982\u6b64\u4f17\u591a\u7684\u7537\u4eba\uff0c\u96be\u9053\u5c31\u6ca1\u6709\u4e00\u4e2a\u4eba\uff0c\u80fd\u53d6\u4ee3\u4ed6\u7559\u5728\u4f60\u5fc3\u4e2d\u7684\u5730\u4f4d\uff1f\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u66fe\u7ecf\u6ca7\u6d77\u96be\u4e3a\u6c34\uff0c\u9664\u5374\u5deb\u5c71\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e0d\u662f\u4e91\uff0c\u5bf9\u5417?\u54fc!\u5b8c\u5168\u80e1\u8bf4\u516b\u9053\uff0c\u5deb\u5c71\u4e91\u548c\u534e\u5c71\u7684\u4e91\uff0c\u6709\u4ec0\u4e48\u4e0d\u540c?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5f88\u591a\u7684\u5730\u65b9\u4e0d\u540c\uff0e\u59d1\u5a18\u5230\u8fc7\u5deb\u5c71\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6447\u6447\u5934\u3002 \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u59d1\u5a18\u5230\u8fc7\u534e\u5c71\u4e86?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u53c8\u6447\u6447\u5934\u3002 \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u5230\u8fc7\u534e\u5c71\uff0c\u4e5f\u5230\u8fc7\u5deb\u5c71\u5341\u4e8c\u5cf0\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u534e\u5c71\u7684\u4e91\uff0c\u96c4\u6d51\u3001\u5947\u5e7b\uff0c\u770b\u4e0a\u53bb\u6df1\u8fdc\u3001\u5b8f\u4f1f\uff0c\u5341\u5206\u58ee\u89c2\uff0e\u5deb\u5c71\u7684\u4e91\uff0c\u7ea4\u5de7\u3001\u7470\u4e3d\uff0c\u770b\u53bb\uff0c\u5145\u6ee1\u8bd7\u60c5\u3001\u753b\u610f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u8bfb\u4e07\u5377\u4e66\uff0c\u4e0d\u5982\u8d70\u4e07\u91cc\u8def\uff0c\u7ecf\u9a8c\u662f\u5f88\u53ef\u8d35\u7684\u3002\u201d \u534e\u5999\u771f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u7684\u6545\u4e8b\u8bf4\u5b8c\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4e5f\u542c\u5b8c\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u51c6\u5907\u600e\u4e48\u529e?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8981\u4e0d\u8981\u542c\u542c\u6211\u7684\u610f\u89c1\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u770b\u4e0d\u7528\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6709\u4e9b\u4e8b\uff0c\u6211\u8fd8\u6ca1\u6709\u60f3\u660e\u767d\uff0c\u60f3\u660e\u767d\u4e86\u4e4b\u540e\uff0c\u6211\u5e0c\u671b\u518d\u89c1\u89c1\u4f60\u3002\u201d \u534e\u5999\u771f\u9053\uff1a\u201c\u4ee5\u59d1\u5a18\u7684\u624d\u6167\uff0c\u5927\u6982\uff0c\u5f88\u5feb\u5c31\u53ef\u4ee5\u60f3\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e5f\u8bb8\u4e00\u591c\uff0c\u4e5f\u8bb8\u4e09\u4e94\u5929\uff0c\u624d\u80fd\u60f3\u6e05\u695a\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u597d!\u65e2\u7136\u5982\u6b64\uff0c\u6211\u5c31\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d!\u5e0c\u671b\u4f60\u80fd\u591a\u7559\u4e00\u4f1a\u513f\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u89c9\u7740\uff0c\u4f60\u6709\u4e9b\u4e8b\uff0c\u53ef\u4ee5\u660e\u767d\u7684\u548c\u6211\u5546\u91cf\u4e00\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u662f\u5546\u91cf\uff0c\u6211\u662f\u60f3\u95ee\u4f60\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u597d\u5427!\u9ad8\u59d1\u5a18\u8981\u95ee\u4e9b\u4ec0\u4e48?\u73b0\u5728\u53ef\u4ee5\u95ee\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u8981\u8bf4\u7684\u4e8b\u60c5\uff0c\u6211\u5df2\u7ecf\u4e86\u89e3\u4e86\uff0c\u4f46\u6211\u8fd8\u662f\u4e0d\u592a\u660e\u767d\u4f60\u7684\u6765\u610f\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u9ad8\u59d1\u5a18\u79c0\u4e3d\u82e5\u82b1\uff0c\u5728\u4e0b\u4e0d\u5e0c\u671b\u4f60\u7ea2\u989c\u8584\u547d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u8d8a\u6765\u8d8a\u80c6\u5927\u4e86\uff0c\u53e3\u65e0\u906e\u62e6\u3002\u201d \u3000\u3000\u8bcd\u950b\u867d\u7136\u9510\u5229\uff0c\u4f46\u53e3\u6c14\u5374\u5341\u5206\u7f13\u548c\u3002 \u3000\u3000\u534e\u5999\u771f\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6211\u8bf4\u7684\u662f\u7531\u8877\u4e4b\u8a00\uff0c\u59d1\u5a18\u5982\u82e5\u4e0d\u4fe1\uff0c\u8d31\u59be\u53ea\u597d\u544a\u8f9e\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u5728\u62ff\u4e54\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8d31\u59be\u4e0d\u6562\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u5c31\u56de\u7b54\u6211\u7684\u95ee\u8bdd\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u59d1\u5a18\u8bf7\u8bf4?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5148\u544a\u8bc9\u6211\uff0c\u4f60\u5230\u8fd9\u91cc\u6765\uff0c\u662f\u53d7\u4eba\u4e4b\u6258\u800c\u6765\uff0c\u8fd8\u662f\u5949\u547d\u800c\u6765\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u4e2a\u5f88\u91cd\u8981\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5f88\u91cd\u8981\uff0c\u6240\u4ee5\u4f60\u8981\u636e\u5b9e\u56de\u7b54\u6211\u7684\u8bdd\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u534a\u662f\u53d7\u4eba\u4e4b\u6258\u3001\u534a\u662f\u81ea\u613f\u800c\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u8bdd\u8bf4\u5f97\u597d\u6ed1\u5934?\u8bf4\u662f\u53d7\u4eba\u4e4b\u6258\uff0c\u600e\u4f1a\u53c8\u662f\u81ea\u613f\u800c\u6765\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u90a3\u4eba\u6ca1\u6709\u53eb\u6211\u6765\uff0c\u4f46\u4ed6\u5374\u8bf4\u660e\u4e86\u8fd9\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u542c\u5230\u4e86\uff0c \u81ea\u5df1\u6765\u89c1\u6211\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u662f!\u4ed6\u6ca1\u6709\u8981\u6c42\u6211\u4ec0\u4e48\uff0c\u8d31\u59be\u81ea\u5df1\u627e\u4e86\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\u662f\u8c01?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4ee5\u59d1\u5a18\u7684\u624d\u6167\uff0c\u53ea\u8981\u731c\u4e00\u731c\uff0c\u6211\u60f3\u4e00\u5b9a\u4f1a\u731c\u5230\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4e0d\u8981\u731c\uff0c\u6211\u8981\u4f60\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u597d!\u6211\u544a\u8bc9\u4f60\uff0c\u4ed6\u5c31\u662f\u4f55\u5bd2\u8863\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54fc!\u4f55\u5bd2\u8863\u79bb\u5f00\u8fd9\u91cc\u4e0d\u4e45\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4ed6\u7ed9\u6211\u8bf4\u8fd9\u6837\u4e00\u4ef6\u4e8b\uff0c\u4e5f\u7528\u4e0d\u4e86\u591a\u5c11\u65f6\u95f4\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u544a\u8bc9\u4f60\u8fd9\u4ef6\u4e8b\u65f6\uff0c\u8fd8\u6709\u4e9b\u4ec0\u4e48\u4eba\u5728\u573a?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u518d\u65e0\u522b\u4eba\u3002\u201d \u3000\u3000\u662f\u8c0e\u8a00\u3002 \u3000\u3000\u4f46\u534e\u5999\u771f\u611f\u89c9\u5230\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u5fc5\u987b\u8981\u8bf4\u4e9b\u8c0e\u8a00\u4e0d\u53ef\u4e86\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u4e0b\u5934\u53bb\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u544a\u8bc9\u4ed6\uff0c\u4eca\u665a\u4e0a\u6211\u53bb\u770b\u4ed6\uff0c\u8981\u4ed6\u5728\u6797\u53e3\u7b49\u6211\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8981\u4ed6\u4e00\u4e2a\u4eba\u6765\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u53ef\u4ee5\u544a\u8bc9\u5434\u53cc\u5973\uff0c\u4e0d\u8fc7\uff0c\u8981\u4ed6\u4e00\u4e2a\u4eba\u8d74\u7ea6\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u597d!\u8fd9\u4e2a\u4fe1\uff0c\u6211\u4e00\u5b9a\u5e26\u5230\uff0c\u800c\u4e14\u6211\u76f8\u4fe1\u4ed6\u5b9a\u4f1a\u51c6\u65f6\u8d74\u7ea6\uff0c\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u597d\u5fc3\u6025\uff0c\u6211\u8fd8\u6ca1\u6709\u544a\u8bc9\u4f60\u4ec0\u4e48\u65f6\u523b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u60f3\u5929\u4e00\u9ed1\uff0c\u4ed6\u5c31\u4f1a\u5728\u90a3\u91cc\u606d\u5019\u82b3\u9a7e\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u7528\u4e86\uff0c\u544a\u8bc9\u4ed6\u4e8c\u66f4\u65f6\u5206\uff0c\u6211\u4f1a\u8d76\u5230\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u8d77\u8eab\u800c\u53bb\u3002 \u3000\u3000\u56de\u5230\u8305\u820d\u65f6\uff0c\u9ec4\u7075\u8fd8\u5728\u7b49\u5019\u3002 \u3000\u3000\u534e\u5999\u771f\u8bf4\u660e\u4e86\u4f1a\u6664\u9ad8\u5e7d\u5170\u7684\u7ecf\u8fc7\u60c5\u5f62\u3002 \u3000\u3000\u9ec4\u7075\u6c89\u601d\u4e0d\u8bed\u3002 \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5821\u4e3b\u5728\u60f3\u4ec0\u4e48?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u534e\u5251\u4f7f\uff0c\u4f60\u770b\uff0c\u8fd9\u4f1a\u4e0d\u4f1a\u662f\u4e00\u4e2a\u9677\u9631?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5821\u4e3b\u7684\u610f\u601d\u662f\uff0c\u6211\u4eec\u5e03\u7f6e\u4e00\u4e2a\u9677\u9631\uff0c\u8fd8\u662f\u5bf9\u65b9\u8bbe\u8ba1\u7684\u9677\u9631\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u90fd\u6709\u53ef\u80fd\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\u5f88\u7cbe\u660e\uff0c\u6211\u76f8\u4fe1\uff0c\u5979\u4f1a\u6709\u6240\u51c6\u5907\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u62c5\u5fc3\u7684\u4e5f\u5c31\u662f\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4f60\u5df2\u7ecf\u6fc0\u8d77\u5979\u7684\u60c5\u6ce2\u7231\u6d6a\uff0c\u6211\u6015\u5979\u4e0d\u62e9\u624b\u6bb5\u65bd\u4e3a\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4f60\u8bf4\u5979\u4f1a\u63b3\u8d70\u4e86\u4f55\u5bd2\u8863?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5927\u6709\u53ef\u80fd\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u8fd9\u4e2a\u9669\u503c\u5f97\u5192\uff0c\u5979\u4e86\u89e3\u5434\u53cc\u5973\uff0c\u5927\u6982\u8fd8\u4e0d\u4f1a\u6fc0\u8d77\u5434\u53cc\u5973\u62fc\u547d\u4e4b\u5fc3\u3002\u201d \u9ec4\u7075\u4f4e\u58f0\u9053\uff1a\u201c\u5982\u82e5\u6211\u4eec\u5b89\u6392\u4e00\u4e0b\u3002\u628a\u9ad8\u5e7d\u5170\u63b3\u8fc7\u6765\u5462?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u636e\u6211\u89c2\u5bdf\u6240\u5f97\uff0c\u9ad8\u5e7d\u5170\u5bf9\u4f55\u5bd2\u8863\uff0c\u4f3c\u4e4e\u662f\u4e00\u7247\u771f\u60c5\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u534e\u5251\u4f7f\uff0c\u5175\u4e0d\u538c\u8bc8\uff0c\u81f3\u5c11\uff0c\u54b1\u4eec\u4e5f\u5e94\u8be5\u6709\u4e9b\u51c6\u5907\u3002\u201d \u534e\u5999\u771f\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u6211\u60f3\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6700\u597d\u522b\u7528\u8bc8\u672f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u54b1\u4eec\u548c\u7b2c\u4e00\u5821\u4e4b\u95f4\uff0c\u4f3c\u662f\u5df2\u7ecf\u5230\u4e86\u6c34\u706b\u96be\u5bb9\uff0c\u975e\u6709\u4e00\u573a\u62fc\u6740\u4e0d\u53ef\uff0c\u4e0d\u8bba\u9e7f\u6b7b\u8c01\u624b\uff0c \u9634\u9633\u5821\u4e2d\u4eba\uff0c\u90fd\u4f1a\u5728\u5821\u4e3b\u9886\u5bfc\u4e4b\u4e0b\uff0c\u5168\u529b\u4ee5\u8d74\uff0c\u6b7b\u800c\u65e0\u6028\uff0c\u4f46\u5bf9\u9ad8\u5e7d\u5170\u8fd9\u4ef6\u4e8b\uff0c\u5c5e\u4e0b\u5374\u6709\u4e0d\u540c\u7684\u770b\u6cd5\u3002\u201d \u3000\u3000\u9ec4\u7075\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f60\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e5f\u8bb8\u6211\u7684\u770b\u6cd5\u4e0d\u5bf9\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u5bf9\u7684!\u53bb\u544a\u8bc9\u4f55\u5251\u4f7f\u5427!\u544a\u8bc9\u4ed6\uff0c\u8fd9\u4ef6\u4e8b\u5bf9\u6574\u4e2a\u7684\u6c5f\u6e56\u5927\u5c40\u90fd\u5f88\u91cd\u8981\uff0c\u5e0c\u671b\u4ed6\u80fd\u4e3a\u6b66\u6797\u540c\u9053\u2014\u5c3d\u5fc3\u529b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u660e\u767d\u3002\u201d \u3000\u3000\u4e8c\u66f4\u65f6\u5206\uff0c\u4f55\u5bd2\u8863\u5982\u7ea6\u8d76\u5230\u3002 \u3000\u3000\u610f\u5916\u7684\u662f\u5374\u6709\u4eba\u65e9\u5728\u7b49\u4faf\u3002 \u3000\u3000\u90a3\u4eba\u4e0d\u662f\u9ad8\u5e7d\u5170\uff0c\u7adf\u662f\u4e2b\u5934\u96ea\u6885\uff0c \u3000\u3000\u6d6e\u4e91\u63a9\u6708\uff0c\u661f\u5149\u9eef\u6de1\uff0c\u4f46\u4f55\u5bd2\u8863\u4ecd\u7136\u770b\u6e05\u695a\u4e86\u96ea\u6094\u3002 \u3000\u3000\u96ea\u6885\u6709\u610f\u4fa7\u8eab\u800c\u7acb\uff0c\u4e0d\u8ba9\u4f55\u5bd2\u8863\u770b\u6e05\u5979\u7684\u8eab\u4efd\uff0c\u4f46\u4f55\u5bd2\u8863\u5374\u770b\u5f97\u5341\u5206\u6e05\u695a\uff0e\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u96ea\u6885\uff0c\u4e8c\u5c0f\u59d0\u6295\u6709\u6765\u5417?\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u4f55\u7237\u597d\u9510\u5229\u7684\u76ee\u5149\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u4e0d\u662f\u6211\u6765\u665a\u4e86\u3002\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u4e0d!\u5929\u8272\u521a\u5230\u4e8c\u66f4\uff0c\u59d1\u5a18\u6b63\u5728\u5019\u9a7e\uff0c\u5c0f\u5a62\u7ed9\u4f55\u7237\u5e26\u8def\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u679c\u7136\u4e0d\u662f\u7b80\u5355\u4eba\u7269\uff0c\u6bcf\u4ef6\u4e8b\u90fd\u7ecf\u8fc7\u4ed4\u7ec6\u7684\u601d\u91cf\uff0c\u7cbe\u5bc6\u7684\u5e03\u7f6e\u3002 \u3000\u3000\u8ddd\u79bb\u76f8\u5f53\u8fdc\uff0c\u4f55\u5bd2\u8863\u5feb\u901f\u75be\u884c\uff0c\u4ecd\u8d70\u4e86\u987f\u996d\u5de5\u592b\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6ca1\u6709\u770b\u5230\u5e03\u5b88\u5728\u8def\u4e0a\u7684\u57cb\u4f0f\uff0c\u4f46\u4ed6\u5374\u611f\u89c9\u5230\uff0c\u6cbf\u9014\u90fd\u6709\u57cb\u4f0f\u4eba\u624b\u3002 \u3000\u3000\u6709\u5ea7\u5c0f\u4ead\uff0c\u4ead\u4e2d\u77f3\u6848\u4e0a\u94fa\u7740\u96ea\u767d\u7684\u5e03\u5355\uff0c\u4e0a\u9762\u65e9\u5df2\u6446\u597d\u4e86\u9999\u8317\u7ec6\u70b9\u3002 \u3000\u3000\u77f3\u58a9\u5df2\u88ab\u79fb\u5f00\uff0c\u6362\u4e0a\u7684\u662f\u4e24\u4e2a\u9526\u58a9\u3002 \u3000\u3000\u4e00\u8eab\u7384\u8272\u52b2\u88c5\u7684\u9ad8\u5e7d\u5170\uff0c\u7a81\u7136\u884c\u4e86\u8fdb\u6765\uff0c\u5728\u4e00\u5f20\u9526\u58a9\u4e0a\u5750\u4e0b\u3002 \u3000\u3000\u4ead\u4e2d\u53ea\u6709\u4e24\u4e2a\u9526\u58a9\uff0c\u96ea\u6885\u53ea\u597d\u6084\u7136\u79bb\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u8bf7\u5750\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5728\u5bf9\u9762\u5750\u4e0b\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u8336\u662f\u6700\u597d\u7684\u96c0\u820c\uff0c\u70b9\u5fc3\u4e5f\u662f\u6211\u5429\u5490\u4ed6\u4eec\u505a\u7684\uff0c\u76f8\u5f53\u7cbe\u81f4\uff0c\u968f\u4fbf\u5403\u4e00\u70b9\u5427\uff01\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5148\u559d\u4e86\u4e00\u53e3\u8336\uff0c\u5c1d\u4e86\u51e0\u6837\u70b9\u5fc3\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u660e\u767d\uff0c\u90a3\u662f\u8868\u793a\u51fa\u8336\u548c\u70b9\u5fc3\uff0c\u90fd\u6ca1\u6709\u4e0b\u6bd2\u3002 \u3000\u3000\u4e5f\u53ea\u597d\u8ddf\u7740\u98df\u7528\u3002 \u3000\u3000\u4e24\u4eba\u5c31\u8fd9\u6837\u5bf9\u5750\u7740\uff0c\u559d\u8336\u5403\u70b9\u5fc3\uff0c\u76f8\u5bf9\u9ed8\u7136\u3002 \u3000\u3000\u65f6\u95f4\uff0c\u8fc7\u53bb\u4e86\u8db3\u8db3\u6709\u4e00\u987f\u996d\u5de5\u592b\u4e4b\u4e45\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u5f88\u6c89\u7740\uff0c\u6837\u6837\u90fd\u8981\u5360\u5148\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d!\u5728\u4e0b\u5f88\u60f3\u5f00\u53e3\uff0c\u4f46\u5374\u4e0d\u77e5\u8be5\u8bf4\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u534e\u5999\u771f\u89c1\u8fc7\u6211\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u53ef\u662f\u4f60\u8981\u5979\u6765\u7684?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u662f\u5979\u81ea\u613f\u6765\u7684\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u4f60\u770b\u770b\uff0c\u7b2c\u4e00\u5821\u5982\u548c\u9634\u9633\u5821\u52a8\u4e0a\u624b\u4e86\uff0c\u90a3\u4e00\u9762\u7684\u80dc\u7b97\u5927\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u6211\u4e0d\u60f3\u4f5c\u8bc4\u65ad\uff0c\u6211\u662f\u9634\u9633\u5821\u4e2d\u4e00\u4e2a\u5251\u4f7f\uff0c\u53ea\u6015\u5f88\u96be\u5bf9\u8fd9\u4ef6\u4e8b\uff0c\u4f5c\u4e2a\u8bc4\u8bba\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u770b\u6765\uff0c\u6211\u5f88\u96be\u628a\u4f60\u62c9\u5165\u7b2c\u4e00\u5821\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u5982\u60f3\u529d\u59d1\u5a18\u8131\u79bb\u7b2c\u4e00\u5821\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\uff0c\u51ed\u4ec0\u4e48\u529d\u6211\u79bb\u5f00\u7b2c\u4e00\u5821?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u529d\u529d\u4f60\uff0c\u603b\u662f\u4e0d\u9519\u5427?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\uff0c\u4f60\u5e94\u8be5\u660e\u767d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u53ea\u8981\u6211\u4e00\u58f0\u4ee4\u4e0b\uff0c\u9634\u9633\u5821\u4e2d\u7684\u7cbe\u9510\uff0c\u90fd\u4f1a\u5728\u4e09\u4e94\u5929\u5185\uff0c\u57cb\u9aa8\u6b64\u5730\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4e5f\u8bb8\u9634\u9633\u5821\uff0c\u771f\u7684\u4e0d\u5982\u7b2c\u4e00\u5821\u5f3a\u5927\uff0c\u4f46\u5982\u8bf4\u4f60\u4e00\u9053\u4ee4\u8c15\uff0c\u5c31\u80fd\u628a\u9634\u9633\u5821\u4e2d\u5c3d\u6b7c\u4e8e\u6b64\uff0c\u8fd9\u4e00\u70b9\uff0c\u5728\u4e0b\u5f88\u96be\u76f8\u4fe1\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u76f8\u4fe1\uff0c\u90a3\u5c31\u4e0d\u59a8\u8bd5\u8bd5\u770b?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u4eca\u5929\uff0c\u6211\u5148\u4e0b\u4ee4\u6740\u4e86\u4f60\u4eec\u5e03\u5b88\u5728\u56db\u5468\u9632\u5b88\u4e4b\u4eba\uff0c\u4f60\u53ef\u4ee5\u8ddf\u6211\u53bb\u5f00\u5f00\u773c\u754c\u3002\u201d \u3000\u3000\u5979\u53d8\u4e86\uff0c\u53d8\u5f97\u6c14\u52bf\u9738\u51cc\uff0c\u5484\u5484\u903c\u4eba\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5f88\u60f3\u8d77\u8eab\u79bb\u53bb\uff0c\u4f46\u4ed6\u8fd8\u662f\u7ec8\u4e8e\u5fcd\u4e0b\u4e86\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5982\u662f\u8d77\u8eab\u4e00\u8d70\uff0c\u90a3\u5c31\u6c38\u65e0\u548c\u597d\u7684\u673a\u4f1a\u4e86\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u60f3\u4e86\u5f88\u4e45\uff0c\u5f88\u4e45\uff0c\u7ec8\u4e8e\u60f3\u8d77\u4e86\u4e00\u53e5\u8bdd\uff0c\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u4f60\u53d8\u4e86\uff0c\u53d8\u5f97\u597d\u51f6\uff0c\u597d\u51f6\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u5f88\u51f6?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5bf9!\u548c\u8fc7\u53bb\u7684\u6e29\u67d4\uff0c\u5b8c\u5168\u7684\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4e00\u76f4\u4e0d\u662f\u4e00\u4e2a\u5f88\u6e29\u67d4\u7684\u5973\u5b69\u5b50\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d!\u4f60\u8fc7\u53bb\u5f88\u6e29\u67d4\uff0c\u53ef\u662f\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u6e29\u67d4\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53e3\u6c14\u5484\u5484\u903c\u4eba\uff0c\u6c14\u52bf\u4e5f\u5f88\u51f6\u9738\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u5bb3\u6015\u4e86?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5509!\u5e7d\u5170\uff0c\u4f60\u8fd9\u6837\u7684\u903c\u6211\uff0c\u4f60\u8bf4\uff0c\u6211\u5e94\u8be5\u600e\u4e48\u56de\u7b54\u4f60\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6c89\u541f\u4e01\u4e00\u9635\uff0c\u9053\uff1a \u201c\u4f60\u8981\u6211\u600e\u4e48\u8bf4?\u53c8\u8981\u6211\u8bf4\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5b9e\u5728\u7684\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u8be5\u8bf4\u4e9b\u4ec0\u4e48?\u6709\u4e9b\u8bdd\uff0c\u4e0d\u80fd\u8bf4\uff0c\u6709\u4e9b\u4e8b\u4e0d\u6562\u8bf4\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u7528\u5bb3\u6015\uff0c\u4e0d\u6562\u8bf4\u7684\u8bdd\uff0c\u4e5f\u5c3d\u7ba1\u8bf7\u8bf4\u5427\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u6211\u8bf4\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u55ef!\u8bf4\uff0c\u5c31\u7b97\u4f60\u9a82\u6211\u4e00\u987f\uff0c\u6211\u4e5f\u4e0d\u4f1a\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u79bb\u5f00\u7b2c\u4e00\u5821\uff0c\u522b\u518d\u7ba1\u7b2c\u4e00\u5821\u7684\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5c31\u8fd9\u51e0\u53e5\u8bdd?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u662f\u554a!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u7684\u5821\u4e3b\uff0c\u662f\u6211\u7239\uff0c\u4f60\u77e5\u9053\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4e0d\u5e2e\u7239\u7684\u5fd9\uff0c\u53bb\u5e2e\u4ed6\u7684\u4ec7\u4eba?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u662f\u5e2e\u4ec7\u4eba\uff0c\u662f\u5e2e\u4f60\u7684\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u77aa\u7740\u4e24\u53ea\u773c\u775b\uff0c\u671b\u7740\u4f55\u5bd2\u8863\uff0c\u8138\u4e0a\u662f\u4e00\u7247\u5f88\u5947\u602a\u7684\u8868\u60c5\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9f13\u8db3\u4e86\u6240\u6709\u7684\u52c7\u6c14\uff0c\u9053\uff1a\u201c\u5e2e\u4f60\u4e08\u592b\u7684\u5fd9\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u8fd8\u5f85\u5b57\u95fa\u4e2d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u6211\u5df2\u7ecf\u8bf4\u5f97\u5f88\u660e\u767d\u4e86\uff0c\u96be\u9053\u8fd8\u8981\u6211\u5982\u4f55\u8868\u793a?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u4f60\u8981\u5a36\u6211?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u662f!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f55\u5bd2\u8863\uff0c \u4f60\u662f\u771f\u7684\u559c\u6b22\u6211\uff0c\u8fd8\u662f\u4e3a\u4e86\u6551\u4f60\u90a3\u4e9b\u540c\u4f34\u7684\u6027\u547d\u5462?\u201d \u3000\u3000\u8fd9\u6837\u5355\u5200\u76f4\u5165\u7684\u95ee\u6cd5\uff0c\u987f\u7136\u4f7f\u5f97\u4f55\u5bd2\u8863\u6709\u7740\u65e0\u6cd5\u56de\u7b54\u7684\u611f\u89c9\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5401\u4e00\u53e3\u6c14\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u4f55\u5bd2\u8863\uff0c \u4f60\u4e0d\u89c9\u7740\u7528\u8fd9\u6837\u7684\u65b9\u6cd5\uff0c\u5bf9\u4ed8\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u592a\u8fc7\u5351\u4e0b\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6025\u9053\uff1a \u201c\u5e7d\u5170\uff0c \u4f60\u600e\u4e48\u53ef\u4ee5\u5982\u6b64\u8bf4\u5462?\u6211\u662f\u4e00\u7247\u8bda\u610f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u8bda\u610f?\u521a\u521a\u5a36\u4e86\u4e00\u4e2a\u5434\u53cc\u5973\uff0c\u7acb\u523b\u53c8\u6709\u80c3\u53e3\uff0c\u53c8\u8981\u5a36\u6211\u9ad8\u5e7d\u5170\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5509!\u4f60\u8fd9\u8bf4\u6cd5\uff0c\u4e0d\u6015\u592a\u8fc7\u4f24\u4eba\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f46\u5374\u5f88\u771f\u5b9e\uff0c\u4f60\u4e5f\u771f\u6709\u8fd9\u6837\u7684\u6253\u7b97?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u6253\u7b97?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u4eec\u7528\u4f60\u4f5c\u9975\uff0c\u60f3\u628a\u6211\u7684\u8fc7\u53bb\uff0c\u4f46\u4f60\u4e5f\u60c5\u7518\u6548\u547d\uff0c\u88ab\u4ed6\u4eec\u5229\u7528\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u548c\u5979\u6709\u7740\u5f88\u5927\u7684\u4e0d\u540c\uff1b\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5979\u662f\u8c01?\u201d \u3000\u3000\u201c\u5434\u53cc\u5973\u3002\u201d\u4f55\u5bd2\u8863\u5fae\u7b11\u7740\u8bf4\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5979\u5f88\u7f8e\uff0c\u4e5f\u5f88\u8d24\u6167\uff0c\u4e0d\u50cf\u6211\u8fd9\u6837\u6cfc\u8fa3\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u5bf9!\u5979\u5e38\u5e38\u5728\u6211\u9762\u524d\u5938\u5956\u4f60\uff0c\u8bf4\u4f60\u7f8e\u4e3d\u3001\u8d24\u6167\uff0c\u8981\u6211\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u8981\u4f60\u5a36\u5c0f\u8001\u5a46\uff0c\u8fd9\u5c0f\u8001\u5a46\u4e0d\u4f46\u957f\u5f97\u8fd8\u8fc7\u5f97\u53bb\uff0c\u800c\u4e14\uff0c\u4e00\u8eab\u6b66\u529f\u4e5f\u53ef\u4ee5\u5e2e\u52a9\u4e08\u592b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6012\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\uff0c\u4f60\u8bf4\u8bdd\u597d\u523b\u8584\u3002\u201d \u3000\u3000\u970d\u7136\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u4f55\u5bd2\u8863\u53d1\u813e\u6c14\uff0c\u770b\u4ed6\u8138\u4e5f\u7ea2\u4e86\uff0c\u773c\u775b\u4e5f\u5706\u4e86\uff0c\u7709\u5b87\u95f4\u4e00\u7247\u6012\u8272\uff0c\u4e0d\u7981\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u771f\u7684\u751f\u6c14\u4e86\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53eb\u6211\u5982\u4f55\u80fd\u4e0d\u751f\u6c14\uff0c\u5979\u5728\u80cc\u540e\uff0c\u5bf9\u4f60\u63a8\u5d07\u5907\u81f3\uff0c\u8bb2\u4f60\u806a\u6167\uff0c\u8bb2\u4f60\u6b66\u529f\u9ad8\u5f3a\uff0c\u4f46\u4f60\u5374\u5904\u5904\u8bec\u8511\u5979\uff0c\u4f60\u4eec\u540c\u6837\u7684\u7f8e\u8c8c\u5982\u82b1\uff0c\u4f46\u4e3a\u4ec0\u4e48\u4f60\u4eec\u7684\u5fc3\u5730\uff0c\u7adf\u7136\u662f\u5982\u6b64\u7684\u4e0d\u540c\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u626c\u8d77\u4e86\u67f3\u7709\u513f\uff0c\u4f3c\u662f\u60f3\u8981\u53d1\u4f5c\uff0c\u4f46\u5979\u5374\u5f3a\u5fcd\u4e86\u4e0b\u53bb\uff0c\u9eef\u7136\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u4e0d\u8981\u751f\u6c14\u561b\uff0c\u5c31\u7b97\u6211\u8bf4\u9519\u4e86\u3002\u201d \u3000\u3000\u5979\u5fae\u5fae\u95ed\u7740\u661f\u76ee\uff0c\u88f9\u8eab\u52b2\u88c5\uff0c\u663e\u9732\u51fa\u73b2\u73d1\u7684\u66f2\u7ebf\u3002 \u3000\u3000\u5979\u663e\u7136\u6781\u529b\u5728\u5fcd\u7740\u5185\u5fc3\u7684\u59d4\u5c48\uff0c\u9ad8\u8038\u7684\u524d\u80f8\u53cc\u5cf0\uff0c\u5fae\u5fae\u7684\u98a4\u52a8\u7740\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6697\u6697\u7684\u5fd6\u9053\uff1a\u5f13\u4e0d\u80fd\u62c9\u5f97\u592a\u7d27\uff0c\u8fd9\u65f6\u95f4\uff0c\u5343\u4e07\u4e0d\u80fd\u800d\u6027\u683c\uff0c\u4e07\u4e00\u628a\u4e8b\u60c5\u5f04\u7838\u4e86\uff0c\u4e0d\u662f\u6211\u4e2a\u4eba\u7684\u751f\u6b7b\u4e4b\u4e8b\uff0c\u5173\u7cfb\u7740\u6574\u4e2a\u6c5f\u6e56\u5927\u5c40\uff0c\u6211\u8981\u5fcd\u8010\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u4eba\u53c8\u5750\u4e86\u4e0b\u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u6211\u4e0d\u6562\u8981\u6c42\u4f60\u4eec\u6548\u5a25\u7687\u5973\u82f1\u5148\u8d24\u5f80\u4e8b\uff0c\u4f46\u6211\u53ea\u6c42\u4e86\u89e3\u4e00\u4e0b\u4f60\u7684\u5fc3\u610f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u8981\u4e86\u89e3\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u60f3\u77e5\u9053\uff0c\u4f60\u5fc3\u4e2d\u60f3\u7684\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u5fc3\u4e2d\u60f3\u7684\u548c\u6211\u505a\u7684\uff0c\u5b8c\u5168\u4e0d\u4e00\u6837\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u6487\u5f00\u6211\u7684\u79c1\u60c5\u4e0d\u8c08\uff0c\u4f60\u5bf9\u4ee4\u5c0a\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u53ef\u6709\u4ec0\u4e48\u610f\u89c1\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e0d\u7ba1\u6211\u7239\u8981\u505a\u4ec0\u4e48\uff0c\u4f60\u8bf4\uff0c\u6211\u8fd9\u4e2a\u505a\u5973\u513f\u7684\uff0c\u5e94\u4e0d\u5e94\u8be5\u5e2e\u52a9\u4ed6\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4eb2\u60c5\u56fa\u7136\u662f\u4e94\u4f26\u4e4b\u4e00\uff0c\u4f46\u5927\u4e49\u53ef\u4ee5\u706d\u4eb2\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u59d0\u592b\uff0c\u4f60\u53ef\u66fe\u653e\u773c\u770b\u8fc7\u6c5f\u6e56\u7684\u60c5\u5f62?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u77e5\u9053\u7b2c\u4e00\u5821\u5f88\u5f3a\u5927\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1b\u201c\u9634\u9633\u5821\u4e4b\u5916\uff0c \u76ee\u4e0b\u6c5f\u6e56\u4e0a\uff0c\u5b8c\u5168\u6ca1\u6709\u4e00\u4e2a\u7ec4\u5408\uff0c\u6562\u548c\u7b2c\u4e00\u5821\u4e3a\u654c\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a \u201c\u6211\u4e0d\u61c2\uff0c\u4ee4\u5c0a\u5728\u6c5f\u6e56\u4e0a\u5df2\u7ecf\u53d7\u5c3d\u4e86\u6b66\u6797\u540c\u9053\u7684\u5c0a\u656c\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u9634\u8c0b\u5f81\u670d\u6c5f\u6e56?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u6211\u53ef\u4ee5\u60f3\u6cd5\u5b50\u529d\u529d\u6211\u7239\u597d\u5417?\u201d\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ed6\u4f1a\u542c\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u6211\u613f\u610f\u53bb\u8bd5\u8bd5\u770b\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u8981\u66ff\u6211\u60f3\u4e2a\u8bf4\u8bcd\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u6211\u548c\u4f60\u540c\u56de\u7b2c\u4e00\u5821\u53bb\uff0c\u89c1\u4f60\u7239\uff0c\u770b\u770b\u80fd\u5426\u8bf4\u670d\u4ed6\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4f60\u8ddf\u6211\u540c\u56de\u7b2c\u4e00\u5821\uff0c\u6211\u6015\u592a\u5371\u9669\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5c31\u7b97\u5f88\u5371\u9669\u5427\uff0c\u4e5f\u662f\u503c\u5f97\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7728\u52a8\u4e86\u4e00\u4e0b\u5927\u5927\u7684\u773c\u775b\u9053\uff1a\u201c\u503c\u5f97?\u4ec0\u4e48\u503c\u5f97?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u8bf4\u7684\u610f\u601d\u662f\uff0c\u5c31\u7b97\u662f\u6211\u4e3a\u4f60\u9001\u4e86\u6027\u547d\uff0c\u4e5f\u662f\u503c\u5f97\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u771f\u7684\u8fd9\u6837\u60f3?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u7531\u8877\u4e4b\u8a00\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u4f60\u5bf9\u6211\u771f\u7684\u8fd9\u4e48\u597d?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u597d\u50cf\u4e00\u76f4\u5728\u6000\u7591\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u55ef!\u6211\u662f\u6709\u4e00\u4e9b\u6000\u7591\uff0c\u6000\u7591\u4ed6\u4eec\u5229\u7528\u4f60\u6765\u9a97\u6211\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u5934\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u8fd9\u4e2b\u5934\u679c\u7136\u7cbe\u660e\u5f97\u5389\u5bb3\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u5929\u4e0b\u4e8b\uff0c\u90fd\u53ef\u4ee5\u9a97\uff0c\u53ea\u6709\u4e00\u4e2a\u60c5\u5b57\uff0c\u4e0d\u80fd\u9a97\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u75f4\u60c5\u5973\u5b50\u8d1f\u5fc3\u6c49\uff0c\u8fd9\u4eba\u4e16\u4e4b\u95f4\uff0c\u4e0d\u77e5\u9053\u6709\u591a\u5c11\u5973\u5b50\u53d7\u5230\u7537\u4eba\u7684\u9a97\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u6211\u6ca1\u6709\u5b58\u5fc3\u9a97\u4f60\u7684\uff0c\u6211\u4e5f\u4e0d\u4f1a\u9a97\u4f60\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fc7\u53bb\uff0c\u6211\u4e00\u76f4\u8fd9\u6837\u60f3\uff0c\u4f46\u73b0\u5728\uff0c\u6211\u76f8\u4fe1\u4f60\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u6211\u5e0c\u671b\uff0c\u6211\u4eec\u80fd\u8bf4\u670d\u4ee4\u5c0a\uff0c\u7b2c\u4e00\u5821\u867d\u7136\u5f88\u5f3a\u5927\uff0c\u4f46\u4e5f\u5f88\u96be\u5b8c\u6210\u7edf\u6cbb\u6c5f\u6e56\u7684\u5fc3\u613f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u53e4\u5f80\u4eca\u6765\uff0c\u5386\u4ee3\u6709\u67ad\u96c4\uff0c\u6bcf\u4e2a\u4eba\uff0c\u90fd\u4f1a\u89c9\u7740\u6709\u79cd\u5145\u5206\u7684\u673a\u4f1a\uff0c\u5b8c\u6210\u6c5f\u6e56\u9738\u4e1a\uff0c\u4f46\u53c8\u6709\u51e0\u4e2a\u4eba\u6210\u529f\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u73b0\u5728\u65f6\u72b9\u672a\u665a\uff0c\u5e0c\u671b\u4f60\u5bf9\u4ed6\u6709\u5f88\u5927\u7684\u5f71\u54cd\u529b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u4f60\u8ba4\u4e3a\u6211\u7239\u662f\u4e00\u4e2a\u5f88\u5bb9\u6613\u8bf4\u670d\u7684\u4eba\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u60f3\uff0c\u8fd9\u662f\u4e00\u4ef6\u5f88\u96be\u7684\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bf9\uff0c\u7239\u5bf9\u6211\u5f88\u6148\u548c\uff0c\u800c\u4e14\uff0c\u6211\u8981\u505a\u7684\u4e8b\uff0c\u4ed6\u90fd\u4f1a\u4f9d\u6211\uff0c\u4f46\u90a3\u4e9b\u4e8b\uff0c\u90fd\u662f\u4e9b\u5c0f\u4e8b\uff0c\u6211\u77e5\u9053\uff0c\u4ed6\u5bf9\u539f\u5219\u5f88\u575a\u6301\uff0c\u6211\u65e0\u6cd5\u4e86\u89e3\uff0c \u81ea\u5df1\u80fd\u5bf9\u4ed6\u5f71\u54cd\u591a\u5c11\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a \u201c\u4e0d\u7ba1\u5bf9\u4ed6\u7684\u5f71\u54cd\u6709\u591a\u5c11\uff0c\u6211\u4eec\u90fd\u5e94\u5f53\u5c3d\u529b\u800c\u4e3a\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u4f60\u8981\u60f3\u4e00\u4e2a\u7406\u7531\u6765\u3002\u6211\u4eec\u5982\u4f55\u624d\u53ef\u4ee5\u5f00\u53e3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e0d\u56f0\u96be\uff0c\u6211\u77e5\u9053\u5f88\u591a\u7684\u4e8b\u5b9e\uff0c\u5fc5\u8981\u65f6\uff0c\u6211\u53ef\u4ee5\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\u662f\uff0c\u7b2c\u4e00\u53e5\uff0c\u6211\u5e94\u8be5\u8bf4\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u6211\u60f3\u5e94\u8be5\u76f4\u63a5\u4e86\u5f53\u7684\u8bf4\u5427!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\u8981\u95ee\u6211\u4e00\u53e5\u8bdd\uff0c\u6211\u5c31\u6ca1\u6709\u529e\u6cd5\u7b54\u590d\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u95ee\u6211\uff0c\u600e\u4f1a\u7a81\u7136\u95f4\u60f3\u5230\u4e86\u8fd9\u4e9b\u4e8b\u6765\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e8b\u7b80\u5355\uff0c\u6211\u5c31\u5728\u4f60\u7684\u65c1\u8fb9\uff0c\u4f60\u53ef\u4ee5\u8bf4\uff0c\u542c\u6211\u8bf4\u7684\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5509!\u4e3a\u4ec0\u4e48\u6211\u8981\u76f8\u4fe1\u4f60\u7684\u8bdd?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u8fd9\u662f\u4e8b\u5b9e\uff0c\u7b2c\u4e8c\uff0c\u6211\u662f\u4f60\u7684\u4e08\u592b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u771f\u7684\u8981\u8fd9\u4e48\u8bf4?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u8fd9\u4e48\u8bf4\uff0c\u96be\u9053\u4f60\u4e0d\u6562\u627f\u8ba4?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\u77e5\u9053\uff0c\u4f60\u5df2\u662f\u6211\u7684\u59d0\u592b\u3002\u201d\u4f55\u5bd2\u8863\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u8fd9\u5df2\u7ecf\u662f\u4e00\u4ef6\u4e8b\u5b9e\uff0c\u65e0\u6cd5\u6539\u53d8\u4e86\uff0c\u4f60\u4f55\u4e0d\u5766\u7136\u544a\u8bc9\u4f60\u7239\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8bf4\u6211\u597d\u559c\u6b22\u4f60\uff0c\u79bb\u5f00\u4e86\uff0c\u6211\u5c31\u4e0d\u80fd\u6d3b\u5566\uff0c\u6240\u4ee5\uff0c\u8584\u547d\u601c\u537f\u7518\u4f5c\u59be\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8bf4\uff0c\u6211\u559c\u6b22\u4f60\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\u5982\u95ee\u4f60\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5148\u5411\u6211\u6c42\u4eb2\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u82e6\u7b11\u4e00\u4e0b\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u8fd9\u6837\u8bf4\u6cd5\uff0c\u54b1\u4eec\u518d\u8c08\u4e00\u767e\u5e74\uff0c\u4e5f\u8c08\u4e0d\u51fa\u4e00\u4e2a\u7ed3\u679c\u6765\u3002\u201d \u53ea\u89c1\u9ad8\u5e7d\u5170\u54ac\u54ac\u7259\uff0c\u9053\uff1a\u201c\u6211\u770b\uff0c\u54b1\u4eec\u4e0d\u7528\u5546\u91cf\u4e86\uff0c\u5230\u65f6\u5019\uff0c\u6211\u8bf4\u4ec0\u4e48\uff0c\u4f60\u627f\u8ba4\u5c31\u884c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53ea\u597d\u70b9\u5934\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u8fd8\u6709\u4e00\u4ef6\u4e8b\uff0c\u5982\u662f\u6211\u7239\u7ffb\u4e86\u8138\uff0c\u54b1\u4eec\u5e94\u8be5\u5982\u4f55\uff1f\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u8bf4\u5427\uff0c\u54b1\u4eec\u662f\u53cd\u6297\uff0c\u8fd8\u662f\u675f\u624b\u5c31\u622e?\u201d \u9ad8\u5e7d\u5170\u6c89\u541f\u4e86\u826f\u4e45\uff0c\u6447\u6447\u5934\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u8f66\u5230\u5c71\u524d\u81ea\u6709\u8def\uff0c\u65e2\u7136\u60f3\u4e0d\u51fa\u529e\u6cd5\uff0c \u54b1\u4eec\u5c31\u4e0d\u7528\u60f3\u4e86\uff0c\u5230\u65f6\u5019\u518d\u4f5c\u6253\u7b97\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u6765\u8fd9\u91cc\uff0c\u5927\u59d0\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u6ca1\u6709\u5bf9\u5979\u8bf4\uff0c\u4e0d\u8fc7\uff0c\u6211\u76f8\u4fe1\u5979\u77e5\u9053\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u53bb\u544a\u8bc9\u5979\u6211\u4eec\u7684\u51b3\u5b9a\uff0c\u770b\u770b\u5979\u600e\u4e48\u8bf4?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u544a\u8bc9\u5979?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5bf9!\u5927\u59d0\u5bf9\u7239\u7684\u5f71\u54cd\u529b\uff0c \u4e0d\u4f1a\u6bd4\u6211\u5c0f\uff0c\u6240\u4ee5\uff0c\u6700\u597d\u8981\u5979\u4e5f\u53bb\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4eec\u4e09\u4e2a\u4eba\u4e00\u540c\u53bb\uff1f\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bf9!\u544a\u8bc9\u4f60\u7684\u670b\u53cb\uff0e\u6211\u4eec\u5982\u679c\u5728\u4e00\u65e5\u4e4b\u5185\uff0c\u4e0d\u89c1\u56de\u97f3\uff0c\u8981\u4ed6\u4eec\u7acb\u523b\u64a4\u8d70\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u5f88\u6709\u4e49\u6c14\u7684\u4eba\uff0c \u53ea\u6015\u4e0d\u4f1a\u79bb\u5f00\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5fc5\u987b\u79bb\u5f00\uff0c\u5bd2\u8863\uff0c\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\uff0c\u6ca1\u6709\u4eba\u80fd\u9003\u8fc7\u2018\u6c34\u706b\u65e0\u60c5\u2019\u7684\u6697\u88ad\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u4e0d\u80fd\uff0c\u5927\u59d0\u4e5f\u4e0d\u80fd\uff0c\u6211\u60f3\u4f60\u4eec\u7684\u5821\u4e3b\u4e5f\u4e0d\u884c\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u8981\u53cc\u5973\u540c\u53bb\uff0c\u4f1a\u4e0d\u4f1a\u95f9\u6210\u7acb\u523b\u95f4\u62d4\u5200\u76f8\u5411?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4e0d\u8fc7\uff0c\u5927\u59d0\u53bb\u4e86\uff0c\u81f3\u5c11\u4f1a\u5bf9\u4f60\u6709\u4e9b\u5e2e\u52a9\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5bf9\u6211\u6709\u5e2e\u52a9?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bf9!\u6709\u5927\u59d0\uff0c\u4e5f\u8bb8\u4f1a\u4f7f\u7239\u6709\u4e00\u70b9\u987e\u5fcc\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6709\u70b9\u660e\u767d\u4e86\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u522b\u62c5\u5fc3\u6211\u7684\u5b89\u5371\uff0c\u4e8b\u60c5\u5e94\u8be5\u5982\u4f55\u529e\uff0c\u6700\u4e3a\u91cd\u8981\uff0c\u4f60\u89c9\u7740\u53cc\u5973\u53bb\u5408\u9002\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u592b\u59bb\u540c\u547d\uff0c\u5979\u662f\u4f60\u7684\u59bb\u5b50\uff0c\u5982\u82e5\u662f\u6709\u4ec0\u4e48\u5371\u9669\uff0c\u5979\u5e94\u8be5\u548c\u4f60\u5b88\u5728\u4e00\u8d77\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u82e5\u4ee4\u5c0a\u8981\u6740\u6211\uff0c\u53cc\u5973\u540c\u53bb\u4e86\uff0c\u4e5f\u672a\u5fc5\u80fd\u591f\u633d\u6551\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u542c\u6211\u7684\u8bdd\uff0c\u6211\u4eec\u4e00\u540c\u53bb\u89c1\u5927\u59d0\uff0c\u95ee\u4e86\u5979\u7684\u610f\u89c1\u5982\u4f55?\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8bf4\u8fd9\u4e9b\u4e86\uff0c\u660e\u5929\uff0c\u54b1\u4eec\u89c1\u7740\u5927\u59d0\u65f6\u518d\u8c08\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5728\u4e0b\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u4f60\u6765\u6b64\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8bf4\u670d\u4f60\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u53ef\u662f\u5df2\u7ecf\u8bf4\u670d\u6211\u4e86?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u6211\u4eec\u4e0d\u662f\u5df2\u7ecf\u7ea6\u597d\u4e86\u5417?\u660e\u5929\uff0c\u6211\u4eec\u89c1\u9762\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8bf4\u597d\u4e86\u4e5f\u4f1a\u53d8\uff0c\u7537\u5b50\u6c49\u5927\u4e08\u592b\u4e00\u8a00\u51fa\u53e3\uff0c\u9a77\u9a6c\u96be\u8ffd\uff0c\u4f46\u5973\u5b69\u5b50\u627f\u8bfa\u7684\u4e8b\uff0c\u5c31\u4e0d\u4e00\u5b9a\u4f1a\u5151\u73b0\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u2026\u2026\u600e\u53ef\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a \u201c\u4f60\u8981\u6211\u5411\u7239\u8bc1\u660e\u4ec0\u4e48?\u5e94\u8be5\u8ba9\u4ed6\u76f8\u4fe1\uff0c \u7239\u662f\u5f88\u7cbe\u660e\u7684\u4eba\uff0c\u6211\u4eec\u9a97\u4e0d\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6709\u70b9\u660e\u767d\u4e86\uff0c\u7f13\u7f13\u5411\u9ad8\u5e7d\u5170\u884c\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u7684\u624b\u6709\u70b9\u6296\uff0c\u4f46\u8fd8\u662f\u4f38\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u7684\u62e5\u62b1\u4f4f\u9ad8\u5e7d\u5170\u65f6\uff0c\u9ad8\u5e7d\u5170\u5374\u6709\u4e86\u5f88\u5927\u80c6\u548c\u5f3a\u70c8\u7684\u53cd\u5e94\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u53ea\u662f\u8f7b\u8f7b\u63fd\u4f4f\u4e86\u5979\u7684\u67f3\u8170\uff0c\u4f46\u9ad8\u5e7d\u5170\u5374\u50cf\u86c7\u4e00\u6837\u7f20\u5728\u4f55\u5bd2\u8863\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u4e8b\u60c5\u6709\u5f00\u59cb\uff0c\u5c31\u6709\u7ed3\u675f\u7684\u65f6\u5019\u3002 \u3000\u3000\u5f00\u59cb\u5728\u90a3\u9edd\u6697\u7684\u51c9\u4ead\u4e2d\uff0c\u4f46\u7ed3\u679c\u5374\u5728\u4e00\u95f4\u5145\u6ee1\u7740\u6e29\u99a8\u4f3c\u7684\u5c0f\u8305\u820d\u4e2d\u3002 \u3000\u3000\u7af9\u58c1\u3001\u8305\u820d\uff0c\u4f46\u5374\u662f\u9526\u88ab\u7f57\u5e0f\u3002 \u3000\u3000\u9ad8\u59d1\u5a18\u662f\u4e00\u4f4d\u5f88\u4f1a\u4eab\u53d7\u7684\u4eba\u3002 \u3000\u3000\u5929\u5df2\u9ece\u660e\uff0c\u9ad8\u5e7d\u5170\u6e29\u67d4\u5730\u66ff\u4f55\u5bd2\u8863\u7a7f\u597d\u8863\u670d\u3002 \u3000\u3000\u6bcf\u4e00\u4e2a\u6263\u5b50\uff0c\u90fd\u4e0d\u8bb8\u4f55\u5bd2\u8863\u81ea\u5df1\u52a8\u3002 \u3000\u3000\u5979\u8868\u73b0\u8fc7\u51b7\u5389\uff0c\u4e5f\u8868\u73b0\u51fa\u4e86\u6625\u6c34\u4e00\u6837\u7684\u6e29\u67d4\u3002 \u3000\u3000\u7a7f\u597d\u4e86\u4f55\u5bd2\u8863\u7684\u8863\u88e4\uff0c\u53c8\u52a8\u624b\u66ff\u4ed6\u7a7f\u597d\u978b\u889c\u3002 \u3000\u3000\u65e0\u9650\u6e29\u67d4\u3001\u65e0\u9650\u6101\uff0c\u58f0\u97f3\u5e26\u7740\u9eef\u7136\uff0c\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u544a\u8bc9\u5927\u59d0\uff0c\u4eca\u591c\u4e2d\u53d1\u751f\u7684\u4e8b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u4e00\u76f4\u5e0c\u671b\u80fd\u548c\u4f60\u76f8\u5904\u5f97\u5f88\u597d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u541b\u5b50\u4e0d\u593a\u4eba\u4e4b\u7231\uff0c\u6211\u5374\u5077\u4e86\u5979\u5fc3\u7231\u7684\u4e08\u592b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e0d\u4f1a\u7684\uff0c\u5e7d\u5170\uff0c\u5979\u4e00\u76f4\u5728\u60f3\uff0c\u60f3\u548c\u4f60\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u53f9\u53e3\u6c14\uff0c\u63a5\u9053\uff1a \u201c\u5a25\u7687\u5973\u82f1\u7684\u4e8b\u80fd\u591f\u6d41\u4f20\u8fd9\u4e9b\u5e74\uff0c\u4e3a\u4eba\u79f0\u7fa1\uff0c\u60f3\u6765\uff0c\u90a3\u4e00\u5b9a\u662f\u4e00\u4ef6\u5f88\u96be\u7684\u4e8b\uff0c\u5927\u59d0\u4e5f\u8bb8\u771f\u662f\u4e00\u4e2a\u5f88\u6709\u6c14\u5ea6\u7684\u4eba\uff0c\u4f46\u6211\u62c5\u5fc3\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u62c5\u5fc3\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u62c5\u5fc3\u6211\u80fd\u4e0d\u80fd\u5bb9\u5f97\u4e86\u5979\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1b\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u653e\u5fc3\u5427!\u5927\u59d0\u4e0d\u662f\u7701\u6cb9\u706f\uff0c\u770b\u4f60\u6025\u6210\u90a3\u6837\u5b50\uff0c\u4e00\u5b9a\u5f88\u504f\u5fc3\uff0c\u5feb\u56de\u53bb\u5427!\u7559\u8fd9\u91cc\u5927\u534a\u591c\uff0c\u53ea\u6015\u5927\u59d0\u4e00\u591c\u6765\u773c\u775b\u4e5f\u672a\u5408\u8fc7\uff0c\u522b\u8ba9\u5979\u592a\u6302\u5ff5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u76ee\u6ce8\u4f55\u5bd2\u8863\u7684\u80cc\u5f71\u8fdc\u53bb\uff0c\u624d\u7f13\u7f13\u56de\u5230\u4e86\u8305\u820d\u3002 \u3000\u3000\u534a\u5bb5\u7f20\u7ef5\uff0c\u7559\u7ed9\u5979\u65e0\u9650\u7684\u751c\u871c\uff0c\u4e5f\u6539\u53d8\u4e86\u5979\u6574\u4e2a\u7684\u4eba\u751f\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u64e6\u4e00\u64e6\u653e\u5728\u6728\u684c\u4e0a\u7684\u94dc\u955c\u3002 \u3000\u3000\u987e\u5f71\u81ea\u601c\uff0c\u4f9d\u7136\u662f\u7389\u5bb9\u5982\u82b1\u3002 \u3000\u3000\u60f3\u5230\u6628\u5bb5\u7684\u80c6\u5927\u548c\u75af\u72c2\uff0c\u8fd8\u4e0d\u89c9\u8138\u4e0a\u6cdb\u8d77\u4e86\u4e00\u7247\u7ea2\u6655\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6765\u5f97\u5f88\u5feb\u3002 \u3000\u3000\u539f\u56e0\u662f\uff0c\u5434\u53cc\u5973\u4e0d\u4f46\u4e00\u591c\u672a\u5408\u8fc7\u773c\u775b\uff0c\u800c\u4e14\u4e00\u76f4\u5728\u5c71\u6797\u5916\u9762\u7b49\u7740\u3002 \u3000\u3000\u770b\u5230\u4f55\u5bd2\u8863\u5306\u5306\u5f52\u6765\uff0c\u5434\u53cc\u5973\u6709\u4e9b\u83ab\u540d\u5fc3\u9178\u3002 \u3000\u3000\u591c\u672a\u5408\u8fc7\u7684\u773c\u775b\uff0c\u4ecd\u7136\u662f\u90a3\u4e48\u660e\u4eae\uff0c\u76ef\u6ce8\u5728\u4f55\u5bd2\u8863\u7684\u8138\u4e0a\uff0c\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6628\u591c\u8fd8\u597d\u5427!\u201d \u3000\u3000\u6709\u4e09\u5206\u60ed\u6127\uff0c\u4e03\u5206\u4e0d\u5b89\uff0c\u4f55\u5bd2\u8863\u58f0\u97f3\uff0c\u4e5f\u53d8\u5f97\u5f88\u4f4e\u6c89\u4e86\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u53cc\u5973\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a \u201c\u522b\u544a\u8bc9\u6211\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u6211\u53ea\u60f3\u77e5\u9053\uff0c\u9ad8\u5e7d\u5170\u662f\u4e0d\u662f\u6709\u4e86\u6539\u53d8\u3002\u201d \u4f55\u5bd2\u8863\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5979\u8981\u4f60\u548c\u6211\uff0c\u4e00\u8d77\u53bb\u89c1\u9ad8\u5821\u4e3b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\u5f88\u6df1\u6c89\uff0c\u4f60\u76f8\u4fe1\u5979\u7684\u8bdd\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u76f8\u4fe1\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d\u5427!\u6211\u4eec\u53bb\u89c1\u89c1\u5979\u3002\u201d \u3000\u3000\u4e24\u4eba\u89c1\u5230\u9ad8\u5e7d\u5170\u65f6\uff0c\u9ad8\u5e7d\u5170\u4ecd\u5728\u671b\u7740\u955c\u5b50\u53d1\u5446\u3002 \u3000\u3000\u770b\u5230\u4e86\u5434\u53cc\u5973\uff0c\u9ad8\u5e7d\u5170\u6709\u4e9b\u7f9e\u60ed\uff0c\u4f4e\u5782\u8793\u9996\uff0c\u9053\uff1a \u201c\u5927\u59d0\uff0c\u5bd2\u8863\u544a\u8bc9\u4f60\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ed6\u53ea\u544a\u8bc9\u6211\uff0c \u4f60\u51b3\u5b9a\u4e86\u5e2e\u52a9\u4ed6\uff0c\u800c\u4e14\uff0c\u8981\u5e26\u4ed6\u53bb\u89c1\u5821\u4e3b\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59d0\u7684\u770b\u6cd5\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u89c9\u5f97\u8be5\u4e0d\u8be5\u53bb\u89c1\u5821\u4e3b\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5927\u59d0\uff0c\u5c31\u7b97\u6211\u4eec\u7236\u5973\u76f8\u5411\uff0c\u6211\u4eec\u4e5f\u65e0\u6cd5\u6297\u62d2\u7b2c\u4e00\u5821\uff0c\u5982\u82e5\u80fd\u591f\u8bf4\u670d\u7239\uff0c\u90a3\u4e0d\u662f\u4e24\u5168\u5176\u7f8e\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u53ea\u6015\u4e0d\u592a\u5bb9\u6613\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u4e5f\u8981\u6709\u5e94\u53d8\u7684\u51c6\u5907\uff0c\u201d \u3000\u3000\u8bed\u58f0\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u9053\uff1a \u201c\u5927\u59d0\uff0c\u7239\u5bf9\u4f60\u6709\u4e09\u5206\u987e\u8651\uff0c\u6240\u4ee5\uff0c\u6211\u8981\u4f60\u540c\u53bb\uff0c\u4f60\u6211\u90fd\u5728\u573a\uff0c\u6211\u60f3\u7239\u53ef\u80fd\u88ab\u6211\u4eec\u8bf4\u52a8\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d!\u8fd9\u4ef6\u4e8b\uff0c\u8981\u4e0d\u8981\u548c\u9ec4\u5821\u4e3b\u5546\u91cf\u4e00\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4ed6\u662f\u9996\u8111\u4eba\u7269\uff0c \u81ea\u7136\u8981\u4ed6\u540c\u610f\uff0c\u5927\u59d0\uff0c \u4f60\u4eec\u5b89\u6392\u5e94\u53d8\u7684\u51c6\u5907\uff0c\u4e0d\u7528\u8ddf\u6211\u5546\u91cf\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5509!\u5170\u59b9\uff0c\u6211\u4eec\u662f\u5168\u5fc3\u5168\u610f\u7684\u4fe1\u4efb\u7740\u4f60\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u81f3\u5c11\u6211\u4e0d\u80fd\u8981\u6c42\u4eba\u5bb6\u9ec4\u5821\u4e3b\u8fd9\u4e48\u505a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e7d\u5170\u5982\u6b64\u8bf4\uff0c\u4e5f\u6709\u9053\u7406\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u54b1\u4eec\u542c\u542c\u9ec4\u5821\u4e3b\u7684\u610f\u89c1\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u4e9b\u57cb\u4f0f\u7684\u6740\u624b\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u8981\u4ed6\u4eec\u64a4\u8d70\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54b1\u4eec\u51e0\u65f6\u56de\u5230\u7b2c\u4e00\u5821\u53bb?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6211\u5728\u60f3\u8fd9\u4ef6\u4e8b\uff0c\u6700\u597d\u522b\u56de\u7b2c\u4e00\u5821\u53bb\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u628a\u4e49\u7236\u7ea6\u51fa\u6765?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6700\u597d\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u53ea\u6015\uff0c\u7239\u4e0d\u4f1a\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u5f88\u597d\u7684\u4eba\u8d28\u5728\u8fd9\u91cc\uff0c\u8fd8\u6015\u7239\u4e0d\u5c31\u8303\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u5bf9!\u662f\u6211\uff0c\u544a\u8bc9\u7239\uff0c\u8981\u4ed6\u5230\u6307\u5b9a\u7684\u5730\u65b9\u89c1\u9762\uff0c\u903e\u65f6\u4e0d\u53bb\uff0c\u5c31\u6740\u4e86\u6211\uff0c\u63aa\u8bcd\u4e0d\u59a8\u4e25\u5389\u4e00\u4e9b\uff0c\u7239\u4e3a\u4e86\u8981\u6551\u4ed6\u7684\u5973\u513f\uff0c\u53ea\u6709\u5c48\u9a7e\u4e00\u884c\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u529e\u6cd5\u662f\u597d\uff0c\u53ea\u662f\u592a\u59d4\u5c48\u4f60\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59d0\uff0c\u8fd8\u8bf4\u4ec0\u4e48\u59d4\u5c48\u2026\u2026\u201d \u3000\u3000\u671b\u4e86\u4f55\u5bd2\u8863\u4e00\u773c\uff0c\u6709\u4e9b\u7f9e\u602f\u5730\u63a5\u9053\uff1a \u201c\u5927\u59d0\uff0c\u4ed6\u4e0d\u544a\u8bc9\u4f60\uff0c\u6211\u4e5f\u8981\u8bf4\u660e\u767d\uff0c\u6211\u4eec\u60c5\u540c\u9aa8\u8089\uff0c\u5c0f\u59b9\u59b9\u5982\u662f\u505a\u9519\u4e86\u4ec0\u4e48\uff0c\u5927\u59d0\u4e5f\u4f1a\u539f\u8c05\u6211\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u56de\u987e\u4e86\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff0c\u4f60\u8bf4\u5427\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ee5\u5927\u59d0\u7684\u806a\u660e\uff0c\u4f60\u662f\u65e9\u77e5\u9053\u4e86\uff0c\u65e2\u7136\u8981\u6211\u660e\u8bf4\u51fa\u6765\uff0c\u5c0f\u59b9\u4e5f\u53ea\u597d\u9075\u547d\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u867d\u7136\u5927\u65b9\uff0c\u4f46\u4e00\u4e2a\u5927\u59d1\u5a18\u5bb6\uff0c\u8981\u5766\u5766\u767d\u767d\u8bf4\u51fa\u53e3\uff0c\u5b9e\u611f\u65e0\u6cd5\u542f\u9f7f\uff0c\u6240\u4ee5\uff0c\u6211\u4e86\u534a\u5929\uff0c\u6211\u4e0d\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\u3002 \u3000\u3000\u5434\u53cc\u5973\u7b11\u9053\uff1a\u201c\u4f60\u51b3\u5fc3\u5ac1\u7ed9\u4ed6\u4e86?\u201d \u3000\u3000\u8fd9\u5012\u7ed9\u4e86\u9ad8\u5e7d\u5170\u4e00\u4e2a\u63a5\u53e3\u7684\u673a\u4f1a\uff0c\u9053\uff1a\u201c\u662f\uff0c\u73b0\u5728\uff0c\u4e0d\u5ac1\u4ed6\u4e0d\u884c\u4e86\uff0c\u6211\u5fc3\u4e2d\u597d\u62b1\u548e\uff0c\u7adf\u7136\u53bb\u62a2\u4e86\u81ea\u5df1\u7684\u59d0\u592b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8bdd\u8bf4\u660e\u767d\u4e86\u6700\u597d\uff0c\u53ea\u4e0d\u8fc7\u662f\u6211\u4eec\u90fd\u592a\u5403\u4e8f\uff0c\u4fbf\u5b9c\u8ba9\u4ed6\u4e00\u4e2a\u4eba\u5360\u5b8c\u4e86\u3002\u201d \u3000\u3000\u671b\u7740\u4e24\u4e2a\u5982\u82b1\u4f3c\u7389\u7684\u7f8e\u4eba\uff0c\u7adf\u7136\u662f\u5982\u6b64\u7684\u548c\u7766\u76f8\u5904\uff0c\u4f55\u5bd2\u8863\u4e0d\u81ea\u89c9\u6709\u4e9b\u98d8\u98d8\u7136\u7684\u611f\u89c9\u3002 \u3000\u3000\u5434\u53cc\u5973\u548c\u9ad8\u5e7d\u5170\uff0c\u5f97\u4e00\u4eba\uff0c\u5c31\u53ef\u4ee5\u7ec8\u8eab\u65e0\u61be\uff0c\u4ed6\u4f55\u5bd2\u8863\u7adf\u7136\u4e8c\u5973\u5e76\u4f8d\uff0c\u8fd9\u662f\u4f55\u7b49\u7684\u8273\u798f\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u76ee\u5149\u4e00\u63a0\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5927\u59d0\uff0c\u4f60\u77a7\u5230\u6ca1\u6709?\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u770b\u4ed6\u90a3\u79cd\u7709\u98de\u8272\u821e\u7684\u6837\u5b50\uff0c\u548c\u90a3\u4e00\u5bf9\u6c34\u6c6a\u6c6a\u7684\u6843\u82b1\u773c\uff0c\u4ee5\u540e\uff0c\u5927\u59d0\u8981\u7ba1\u675f\u4ed6\u4e00\u4e9b\uff0c\u4ed6\u8981\u91ce\u60ef\u4e86\uff0c\u518d\u642d\u4e0a\u4e00\u4e2a\u6765\uff0c\u90a3\u53ef\u662f\u6405\u4e86\u5c40\u5566\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u4f1a\u5462\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u4f1a\uff0c\u6211\u770b\u90a3\u4e2a\u534e\u5999\u771f\u5c31\u5bf9\u4f60\u4e0d\u9519\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6025\u9053\uff1a\u201c\u6ca1\u6709\u7684\u4e8b\uff0c\u8fd9\u600e\u4e48\u53ef\u80fd\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u9053\uff1a\u201c\u5170\u59b9\u662f\u9017\u7740\u4f60\u73a9\u7684\uff0c\u770b\u4f60\u6025\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u5012\u771f\u6709\u4e9b\u50cf\u505a\u8d3c\u5fc3\u865a\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u66f4\u6025\uff0c\u8138\u7ea2\u8033\u8d64\u5730\u53eb\u9053\uff1a\u201c\u53cc\u5973\uff0c\u4f60\u4e5f\u8fd9\u6837\u6000\u7591\uff0c\u96be\u9053\u4f60\u4eec\u8981\u903c\u6211\u8d77\u8a93\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u770b\u4f60\u6025\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u6211\u4eec\u662f\u9017\u7740\u4f60\u73a9\u7684\u554a\uff01\u201d \u3000\u3000\u5434\u53cc\u5973\u8138\u4e0a\u7684\u7b11\u5bb9\u5ffd\u7136\u6d88\u5931\u4e86\uff0c\u4ee3\u4e4b\u800c\u8d77\u7684\u662f\u4e00\u8138\u51b7\u8083\u4e4b\u8272\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u8bf4\uff0c\u7239\u4f1a\u4e0d\u4f1a\u8ddf\u54b1\u4eec\u7ffb\u8138\u3002\u201d \u3000\u3000\u5979\u8868\u73b0\u51fa\u5929\u9b54\u5973\u7684\u6027\u683c\uff0c\u521a\u624d\u8fd8\u7b11\u5bb9\u6ee1\u8138\uff0c\u7acb\u523b\u95f4\uff0c\u51b0\u51b7\u7684\u8138\u4e0a\u53ef\u4ee5\u522e\u4e0b\u4e00\u5c42\u971c\u6765\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u76b1\u4e00\u4e0b\u7709\u5934\uff0c\u9053\uff1a \u201c\u5927\u59ca\uff0c\u6211\u89c9\u7740\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6709\u5f88\u591a\u7684\u56f0\u96be\uff0c\u5e0c\u671b\u5408\u4f60\u6211\u7684\u529b\u91cf\uff0c\u80fd\u591f\u8bf4\u670d\u7239\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u51c6\u5907\u5728\u54ea\u91cc\u548c\u4e49\u7236\u89c1\u9762\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e66\u753b\u697c\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d\u5730\u65b9\uff0c\u4e49\u7236\u4f1a\u4e0d\u4f1a\u6765\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8bd5\u8bd5\u770b\uff0c\u660e\u5929\u65e5\u843d\u65f6\u5206\uff0c\u6211\u4eec\u5728\u4e66\u753b\u697c\u89c1\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d!\u6211\u548c\u5bd2\u8863\u5148\u884c\u544a\u8f9e\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5c0f\u59b9\u4e0d\u9001\uff0c\u4e0d\u8fc7\uff0c\u6211\u5e0c\u671b\u5927\u59ca\u4e5f\u8981\u7ed9\u6211\u4e00\u4e2a\u4fdd\u8bc1\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u8bf4\u5427\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e8b\u60c5\u6ca1\u6709\u7ed3\u679c\u4e4b\u524d\uff0c\u53cc\u65b9\u6682\u505c\u654c\u5bf9\uff0c\u4e0d\u8bb8\u9634\u9633\u5821\u4e2d\u4eba\u6697\u91cc\u52a0\u5bb3\uff0c\u65bd\u88ad\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u8fd9\u4e00\u4e2a\uff0c\u6211\u4fdd\u8bc1\uff0c\u5982\u82e5\u9634\u9633\u5821\u4e2d\uff0c\u6709\u4eba\u6697\u7b97\uff0c\u65bd\u88ad\uff0c\u5728\u4e0b\u7b2c\u4e00\u4e2a\u5c31\u5148\u548c\u4ed6\u4eec\u62fc\u547d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u597d\uff0c\u6709\u4f60\u8fd9\u4e00\u53e5\u8bdd\uff0c\u6211\u5c31\u653e\u5fc3\u4e86\u3002\u5c0f\u59b9\u5148\u884c\u544a\u8f9e\u3002\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u5b50\uff0c\u4e3e\u6b65\u800c\u53bb\u3002 \u3000\u3000\u53c8\u662f\u843d\u65e5\u5411\u9ec4\u660f\u3002 \u3000\u3000\u4e66\u753b\u697c\u91cc\u5f97\u6708\u5385\uff0c\u5df2\u7ecf\u71c3\u8d77\u4e86\u56db\u652f\u513f\u81c2\u7c97\u7ec6\u7684\u5de8\u70db\u3002 \u3000\u3000\u718a\u718a\u706b\u5149\uff0c\u7167\u5f97\u6ee1\u5ba4\u901a\u660e\u3002 \u3000\u3000\u5bbd\u655e\u7684\u5927\u5385\u4e2d\uff0c\u53ea\u6446\u4e86\u4e00\u5f20\u684c\u5b50\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u3001\u5434\u53cc\u5973\u5df2\u7136\u5750\u5728\u5385\u4e2d\u3002 \u3000\u3000\u4e24\u4e2a\u7a7f\u7740\u957f\u886b\u7684\u4f53\u9762\u4f19\u8ba1\uff0c\u5782\u624b\u7ad9\u5728\u95e8\u4fa7\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5e26\u7740\u4e00\u8138\u7b11\u5bb9\uff0c\u884c\u5165\u4e86\u5927\u5385\u4e2d\u3002 \u3000\u3000\u4ed6\u8eab\u540e\u7d27\u968f\u4e00\u8eab\u7c89\u7ea2\u8272\u52b2\u88c5\u7684\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u5434\u53cc\u5973\u3001\u4f55\u5bd2\u8863\u53cc\u53cc\u7ad9\u8d77\u4e86\u8eab\u5b50\uff0c\u5feb\u6b65\u884c\u4e86\u4e0a\u53bb\uff0c\u5434\u53cc\u5973\u76c8\u76c8\u8dea\u5012\uff0c\u9053\uff1a\u201c\u89c1\u8fc7\u4e49\u7236\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u53cc\u5973\uff0c\u5feb\u4e9b\u8d77\u6765\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5973\u513f\u4e0d\u5b5d\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u8fc7\u53bb\u7684\uff0c\u4e0d\u8981\u540e\u6094\uff0c\u6765\u65e5\u65b9\u957f\uff0c\u8d77\u6765\u597d\u8c08\u8bdd\uff0c\u5e7d\u5170\uff0c\u5feb\u6276\u8d77\u4f60\u5927\u59ca\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6276\u8d77\u4e86\u5434\u53cc\u5973\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e00\u4e2a\u62b1\u62f3\u957f\u63d6\uff0c\u9053\uff1b\u201c\u7ed9\u8001\u524d\u8f88\u89c1\u793c\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u6211\u5e94\u8be5\u600e\u6837\u79f0\u547c\u4f60?\u8d24\u4f84\u3001\u5973\u5a7f\uff0c\u8fd8\u662f\u4f55\u8001\u5f1f?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u665a\u8f88\u2026\u2026\u665a\u8f88\u2026\u2026\u201d \u3000\u3000\u201c\u5750\u4e0b\u518d\u8c08\u3002\u201d\u9ad8\u5929\u5065\u628a\u4e24\u4eba\u8ba9\u5165\u5e2d\u4f4d\u3002 \u3000\u3000\u4e00\u5f20\u5f88\u5927\u7684\u684c\u5b50\uff0c\u5206\u5750\u7740\u56db\u4e2a\u4eba\u3002 \u3000\u3000\u4e24\u4e2a\u4f19\u8ba1\uff0c\u66ff\u56db\u4eba\u659f\u6ee1\u4e86\u9152\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u7aef\u8d77\u9152\u676f\uff0c\u9053\uff1a\u201c\u6211\u5148\u656c\u5821\u4e3b\u4e00\u676f\uff0c\u201d \u3000\u3000\u9ad8\u5929\u5065\u4e00\u996e\u800c\u5c3d\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4f60\u5e26\u8dd1\u6211\u82b1\u6735\u4e00\u822c\u7684\u5927\u5973\u513f\uff0c\u600e\u80fd\u53ea\u656c\u4e00\u676f?\u6765\uff0c\u54b1\u4eec\u5148\u559d\u4e09\u676f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53ea\u597d\u559d\u4e09\u676f\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u6325\u6325\u624b\uff0c\u793a\u610f\u4e24\u4e2a\u5e97\u4f19\u8ba1\u9000\u51fa\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u522b\u559d\u591a\u4e86\uff0c\u6211\u4eec\u8fd8\u6709\u5927\u4e8b\u5546\u8c08\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5170\u513f\uff0c\u4f60\u8981\u6211\u6765\u8fd9\u91cc\uff0c\u6211\u5df2\u7ecf\u77e5\u9053\u6709\u5927\u4e8b\u5546\u8c08\u4e86\uff0c\u8bf4\u5427!\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\uff0c\u4f60\u662f\u4e0d\u662f\u540c\u610f\u5927\u59d0\u5ac1\u7ed9\u4f55\u5bd2\u8863\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u50bb\u4e2b\u5934\uff0c\u6211\u4e0d\u540c\u610f\u884c\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\uff0c \u4f60\u600e\u4e48\u6837\u4e86?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u5ac1\u7ed9\u4f55\u5bd2\u8863\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u542c\u5f97\u5f88\u6e05\u695a\uff0c\u4f46\u4ed6\u4ecd\u7136\u5fcd\u4e0d\u4f4f\u95ee\u4e86\u4e00\u53e5\u8bdd\uff0e\u9053\uff1a \u201c\u5170\u513f\uff0c\u4f60\u8bf4\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u8bf4\uff0c\u6211\u4e5f\u60f3\u5ac1\u7ed9\u4f55\u5bd2\u8863\uff0c\u7239\u4e0d\u53cd\u5bf9\u5927\u59d0\uff0c\u60f3\u6765\uff0c\u4e5f\u4e0d\u4f1a\u53cd\u5bf9\u6211\u4e86\uff0c\u201d \u201c\u8352\u5510\uff0c\u8352\u5510\uff0c\u8fd9\u73a9\u7b11\u80fd\u548c\u8001\u5b50\u5f00\u7684\u3002\u201d\u9ad8\u5929\u5065\u5fae\u7b11\u7740\u8bf4\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\uff0c\u6211\u8bf4\u7684\u662f\u771f\u8bdd\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u5170\u513f\uff0c \u4f60\u5927\u59d0\u3001\u59d0\u592b\uff0c\u90fd\u5728\u6b64\u5730\uff0c\u8fd9\u73a9\u7b11\uff0c\u53ef\u4e0d\u80fd\u5f00\u5f97\u8fc7\u5206\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5973\u513f\u8bf4\u7684\u662f\u771f\u8bdd\uff0c\u800c\u4e14\u2026\u2026\u800c\u4e14\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u8138\u8272\u4e00\u53d8\uff0c\u63a5\u9053\uff1a\u201c\u800c\u4e14\u2026\u2026\u4ec0\u4e48?\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u663e\u7136\u6709\u6781\u5ea6\u7684\u9707\u6012\uff0c\u867d\u7136\u6781\u529b\u60f3\u4fdd\u6301\u9547\u7741\uff0c\u4f46\u5df2\u65e0\u6cd5\u63a7\u5236\u90a3\u98a4\u6296\u7684\u58f0\u97f3\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\uff0c\u4f60\u4e0d\u8d5e\u6210\uff0e\u662f\u5417\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u5170\u513f\uff0c\u6b66\u6797\u4e2d\u513f\u5973\uff0c\u867d\u4e0d\u50cf\u666e\u901a\u5973\u5b50\uff1b\u5927\u95e8\u4e0d\u51fa\uff0c\u4e8c\u95e8\u4e0d\u8fc8\uff0c\u4f46\u4e5f\u4e0d\u80fd\u8fc7\u5206\u653e\u8361\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u5bf9\u7740\u9ad8\u5929\u5065\u8dea\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a \u201c\u7239\u7239\u539f\u8c05\uff0c\u5973\u513f\u6709\u975e\u4f55\u5bd2\u8863\u4e0d\u5ac1\u7684\u82e6\u8877\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u970d\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u5170\u513f\uff0c\u54b1\u4eec\u8d70\u5566\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u4ecd\u7136\u8dea\u5728\u5730\u4e0a\u672a\u52a8\uff0c\u63a5\u9053\uff1a\u201c\u7239\uff0e\u539f\u8c05\u5973\u513f\u3002\u201d \u3000\u3000\u6ee1\u8138\u6012\u5bb9\u7684\u9ad8\u5929\u5065\uff0c\u7a81\u7136\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u597d!\u5170\u513f\uff0c\u4e0d\u8bba\u4f60\u72af\u4e86\u4ec0\u4e48\u5927\u9519\uff0c\u7239\u90fd\u4e0d\u4f1a\u602a\u4f60\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\uff0c\u5973\u513f\u8981\u60f3\u628a\u4e8b\u60c5\u8bf4\u6e05\u695a\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u5fae\u5fae\u4e00\u6014\u540e\uff0c\u7b11\u9053\uff1a\u201c\u597d\u5427!\u4f60\u4e00\u5b9a\u8981\u8bf4\uff0c\u4e3a\u7236\u7684\u4e5f\u53ea\u597d\u542c\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4e4b\u95f4\uff0c\u6709\u4e86\u6781\u5927\u7684\u6539\u53d8\uff0c\u7531\u4e00\u8138\u6012\u8272\uff0c\u6539\u53d8\u6210\u4e86\u4e00\u8138\u7b11\u5bb9\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5973\u513f\uff0c\u5973\u513f\u5df2\u7ecf\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u56de\u987e\u4e86\u4f55\u5bd2\u8863\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u5170\u513f\uff0c\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5973\u513f\u5df2\u5931\u8eab\u4f55\u5bd2\u8863\uff0c\u4eca\u751f\u4e00\u4e16\uff0c\u975e\u4f55\u90ce\u4e0d\u5ac1\u4e86\u3002\u201d \u9ad8\u5929\u5065\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u4f60\u597d\u5927\u7684\u80c6\u5b50\uff0c\u9a97\u8d70\u6211\u7684\u4e49\u5973\uff0c\u53c8\u6b3a\u6094\u4e86\u5e7d\u5170\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6251\u8eab\u8dea\u5012\uff0c\u9053\uff1a\u201c\u665a\u8f88\u77e5\u9519\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u80fd\u602a\u4ed6\uff0c\u662f\u5973\u513f\u540c\u610f\u7684\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5170\u513f\uff0c\u4f60\u600e\u4e48\u5bf9\u5927\u59d0\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5973\u513f\uff0c\u5973\u513f\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u4e49\u7236\uff0c\u6211\u548c\u5e7d\u5170\u60c5\u540c\u9aa8\u8089\uff0c\u597d\u5e0c\u671b\u5979\u80fd\u591f\u548c\u6211\u5e38\u805a\u4e00\u5904\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u6325\u6325\u624b\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u5148\u8d77\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7ad9\u8d77\u4e86\u8eab\u5b50\u3002 \u3000\u3000\u671b\u671b\u5434\u53cc\u5973\uff0c\u53c8\u671b\u671b\u9ad8\u5e7d\u5170\u548c\u4f55\u5bd2\u8863\uff0c\u9ad8\u5929\u5065\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u597d\u554a!\u4f60\u4eec\u662f\u6446\u597d\u4e86\u5708\u5957\uff0c\u8ba9\u6211\u5f80\u91cc\u9762\u8df3\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u5973\u513f\u4e0d\u6562\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u5750\u4e0b\u5427!\u201d \u3000\u3000\u539f\u6765\uff0c\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\u867d\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u4f46\u5374\u5782\u624b\u800c\u7acb\u3002 \u3000\u3000\u770b\u4e09\u4eba\u5165\u5750\u5e2d\u4f4d\uff0c\u9ad8\u5929\u5065\u76ee\u5149\u8f6c\u6ce8\u5230\u4f55\u5bd2\u8863\u7684\u8eab\u4e0a\uff0c\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u6211\u4e24\u6735\u82b1\u4f3c\u7684\u5973\u513f\uff0c\u90fd\u88ab\u4f60\u9a97\u5230\u4e86\u624b\u4e2d\uff0c\u4f60\u51c6\u5907\u4f5c\u4f55\u6253\u7b97?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8001\u524d\u8f88\u6307\u6559\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\u201c\u597d\u5427!\u6211\u5148\u8ba9\u4f60\u505a\u7b2c\u4e00\u5821\u7684\u603b\u5de1\u67e5\uff0c \u65e5\u540e\uff0c\u6211\u9000\u4f11\u4e86\uff0c\u4f60\u5c31\u63a5\u6211\u5821\u4e3b\u4e4b\u4f4d\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1b\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u6055\u96be\u4ece\u547d\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u96be\u9053\uff0c\u73b0\u5728\u4f60\u5c31\u60f3\u4efb\u5821\u4e3b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u81ea\u5df1\u8bf4\u5427?\u4f60\u60f3\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8bf7\u6c42\u8001\u524d\u8f88\u6253\u6d88\u4e86\u9738\u7edf\u6c5f\u6e56\u7684\u5ff5\u5934\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u7a81\u7136\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5446\u5446\u7684\u671b\u7740\u9ad8\u5929\u5065\u3002 \u3000\u3000\u505c\u4f4f\u4e86\u5927\u7b11\u4e4b\u58f0\uff0c\u9ad8\u5929\u5065\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4ece\u4f55\u8bf4\u8d77\u554a!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u8001\u524d\u8f88\u7edf\u7387\u4e86\u4e5d\u4e2a\u9ed1\u9053\u7ec4\u5408\uff0c\u56db\u5927\u9556\u5c40\uff0c\u8fd9\u662f\u5343\u771f\u4e07\u786e\u7684\u4e86\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a \u201c\u56db\u5927\u9556\u5c40\u7684\u603b\u9556\u5934\uff0c\u6765\u7b2c\u4e00\u5821\u63a2\u671b\u8fc7\u6211\uff0c\u5012\u662f\u4e0d\u9519\uff0c\u4f46\u5982\u8bf4\u8001\u592b\u7edf\u7387\u4e86\u4e5d\u4e2a\u9ed1\u9053\u7ec4\u5408\u4e0d\u77e5\u662f\u4ece\u4f55\u8bf4\u8d77\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u72c2\u9f99\u3001\u98de\u9e70\u662f\u5426\u548c\u7b2c\u4e00\u5821\u6709\u5173\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6709!\u4ed6\u4eec\u90fd\u662f\u8001\u592b\u7684\u8d35\u5bbe\uff0c\u5230\u4fe1\u9633\u7b2c\u4e00\u5821\u63a2\u671b\u6211\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u82e6\u7b11\u4e00\u4e0b\u9053\uff1a\u201c\u8001\u524d\u8f88\u7684\u58f0\u8a89\uff0c \u5df2\u8fbe\u6781\u9650\uff0c\u6c5f\u6e56\u4e0a\u4eba\uff0c\u63d0\u5230\u4f60\u8001\u4eba\u5bb6\uff0c\u65e0\u4e0d\u5341\u5206\u7684\u656c\u91cd\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d!\u4e5f\u6709\u4e0d\u656c\u91cd\u8001\u592b\u7684\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60!\u9634\u9633\u5821\u4e2d\u4eba\uff0c\u90fd\u5bf9\u8001\u592b\u4e0d\u591f\u656c\u91cd\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8001\u524d\u8f88\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u963b\u6b62\u4e86\u4f55\u5bd2\u8863\u8bf4\u4e0b\u53bb\uff0c\u63a5\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u5bf9\u4f60\u7684\u4eba\u54c1\uff0c\u8001\u592b\u5185\u5fc3\u5df2\u7136\u5341\u5206\u75db\u6068\u4e86\uff0c\u60f3\u4e0d\u5230\uff0c\u4f60\u7adf\u8fd8\u522b\u6709\u7528\u5fc3\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a\u201c\u665a\u8f88\u7528\u5fc3\uff0c\u5929\u65e5\u53ef\u9274\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\uff0c\u62d0\u53bb\u6211\u4e24\u4e2a\u5973\u513f\uff0c\u8fd8\u80fd\u7b97\u4e2a\u597d\u4eba\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u53e3\u6c14\uff0c\u5782\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u8fd9\u5b9e\u5728\u662f\u4e00\u4ef6\u5f88\u5c34\u5c2c\u7684\u4e8b\u3002 \u3000\u3000\u9ad8\u5929\u5065\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f55\u5bd2\u8863\uff0c\u522b\u4eba\u8bf4\u8001\u592b\u4e9b\u4ec0\u4e48\uff0c\u90fd\u8fd8\u6ca1\u6709\u8bc1\u660e\uff0c\u4f46\u4f60\u62d0\u4e86\u8001\u592b\u4e24\u4e2a\u5973\u513f\uff0c\u5374\u662f\u5343\u771f\u4e07\u786e\u7684\u4e8b\u4e86\uff0c\u53ea\u6b64\u4e00\u6869\uff0c\u8001\u592b\u5c31\u53ef\u4ee5\u6bd9\u4e86\u4f60\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8bba\u665a\u8f88\u4e4b\u7f6a\uff0c\u786e\u5b9e\u5982\u6b64\uff0c\u4e0d\u8fc7\uff0c\u8001\u524d\u8f88\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u6012\u9053\uff1a\u201c\u4e0d\u8981\u53eb\u6211\u8001\u524d\u8f88\uff0c\u6211\u4e5f\u6ca1\u6709\u4f60\u8fd9\u79cd\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a \u201c\u4e49\u7236\uff0c\u8981\u8d23\u5907\uff0c\u53ea\u7ba1\u9a82\u6211\u548c\u5e7d\u5170\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u4e0d\u80fd\u53ea\u602a\u5bd2\u8863\u4e00\u4e2a\u4eba\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u4e5f\u771f\u662f\u4e22\u5c3d\u4e86\u6211\u7684\u8138\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\uff0c\u4f60\u9a82\u5427!\u6211\u548c\u5927\u59d0\uff0c\u90fd\u4f1a\u53d7\u6559\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u53d7\u6559\u6709\u4ec0\u4e48\u7528?\u4e8b\u5b9e\u5df2\u6210\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u7239\u65e2\u77e5\u4e8b\u5b9e\u5df2\u6210\uff0c\u90a3\u5c31\u539f\u8c05\u5973\u513f\u4eec\u5427\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u603b\u4e0d\u80fd\u628a\u81ea\u5df1\u7684\u5973\u513f\uff0c\u5ac1\u7ed9\u4e00\u4e2a\u5bf9\u6211\u4e0d\u5fe0\u4e0d\u656c\u7684\u4eba\u5427!\u201d \u3000\u3000\u9ad8\u5929\u5065\u970d\u7136\u7ad9\u8d77\u8eab\u4e86\u9053\uff1a\u201c\u5170\u513f\uff0c\u54b1\u4eec\u8d70\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\uff0c\u8fd9\u662f\u4f60\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5170\u513f\uff0c\u4f60\u628a\u7239\u8bf4\u7cca\u6d82\u4e86\uff0c\u4ec0\u4e48\u673a\u4f1a?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u7239!\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u96be\u9053\u8ba4\u4e3a\uff0c\u6211\u4eec\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48?\u8bf4\u51fa\u6765!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u73b0\u5728\uff0c\u5927\u9519\u8fd8\u672a\u94f8\u6210\uff0c\u56de\u5934\u8fd8\u6765\u5f97\u53ca\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u771f\u60f3\u4e0d\u5230\uff0c\u6211\u81ea\u5df1\u7684\u5973\u513f\uff0c\u7adf\u4f1a\u6765\u9677\u5bb3\u6211\u3002\u201d \u3000\u3000\u51b7\u54fc\u4e00\u58f0\uff0c\u4e3e\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8981\u4e09\u601d\u554a!\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8981\u6211\u4e09\u601d\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\u5982\u6267\u8ff7\u4e0d\u609f\uff0c\u5c31\u4f1a\u5931\u53bb\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5931\u53bb\u6027\u547d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d!\u5931\u53bb\u4e24\u4e2a\u5973\u513f\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u6837\u4e0d\u5b5d\u7684\u5973\u513f\uff0c\u6211\u5b81\u53ef\u5931\u53bb\u7b97\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7a81\u7136\u98de\u8eab\u4e00\u8dc3\uff0c\u62e6\u53bb\u4e86\u9ad8\u5929\u5065\uff0c\u9053\uff1a\u201c\u7239!\u542c\u6211\u8bf4\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u5fae\u5fae\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u597d\u5427!\u4f60\u8981\u7239\u600e\u4e48\u505a\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6bc1\u4e86\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u9063\u6563\u795e\u5200\u5802\u7684\u6740\u624b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u795e\u5200\u5802\u6ca1\u6709\u6740\u624b\uff0c\u53ea\u6709\u6b66\u58eb\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u5c31\u9063\u6563\u6b66\u58eb\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u6211\u4e00\u751f\u8fc7\u95ee\u6c5f\u6e56\u4e2d\u95f2\u4e8b\u592a\u591a\uff0c\u9063\u6563\u4e86\u795e\u5200\u5802\u4e2d\u4eba\uff0c\u8c01\u6765\u4fdd\u62a4\u7239\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd8\u6709\u6211\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5982\u82e5\u8001\u524d\u8f88\u613f\u610f\u665a\u8f88\u7559\u5728\u8eab\u8fb9\uff0c\u665a\u8f88\u4e5f\u7b97\u4e00\u4e2a\u3002\u201d \u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u8bdd\u5f53\u771f\u5417\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u3001\u5434\u53cc\u5973\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u5343\u771f\u4e07\u786e\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4eec\u627f\u6b22\u819d\u4e0b\uff0c\u5b5d\u656c\u60a8\u4e00\u751f\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u4f60\u4eec\u771f\u80fd\u5982\u6b64\uff0c\u6211\u5012\u53ef\u4ee5\u8003\u8651\u4e00\u4e0b\u4e86\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u665a\u8f88\u4e00\u7247\u81f3\u8bda\uff0c\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u80fd\u79bb\u5f00\u9634\u9633\u5821?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u5982\u82e5\u80fd\u6d88\u53bb\u4e86\u4e89\u9738\u6c5f\u6e56\u7684\u5ff5\u5934\uff0c\u6c5f\u6e56\u4e0a\u6062\u590d\u4e86\u5e73\u9759\uff0c\u9634\u9633\u5821\u4e5f\u8be5\u89e3\u6563\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u597d!\u9634\u9633\u5821\u5982\u80fd\u89e3\u6563\uff0c\u8001\u592b\u4e5f\u9063\u6563\u795e\u5200\u5802\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4e49\u7236\uff0c\u8fd9\u6279\u4eba\uff0c\u5982\u662f\u5b89\u7f6e\u4e0d\u597d\uff0c\u88ab\u4ed6\u4eec\u6d41\u5165\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u5fc5\u6210\u6d41\u6bd2\u3002\u201d \u9ad8\u5929\u5065\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u4e00\u65f6\u95f4\uff0c\u60f3\u4e0d\u51fa\u9002\u5f53\u7684\u5904\u7f6e\u826f\u7b56\uff0c\u7adf\u7136\u7b54\u4e0d\u4e0a\u8bdd\u3002 \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u662f\u4e0d\u662f\u8981\u6211\u6740\u4e86\u4ed6\u4eec?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u662f\u5c31\u597d\uff0c\u4ed6\u4eec\u8ffd\u968f\u6211\u5f88\u591a\u5e74\uff0c\u6211\u4e0d\u80fd\u8c0b\u5bb3\u4ed6\u4eec\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u76ee\u6ce8\u4f55\u5bd2\u8863\uff0c\u9053\uff1a \u201c\u4f55\u516c\u5b50\uff0c\u6211\u5012\u6709\u4e00\u4e2a\u95ee\u9898\u8bf7\u6559\u4f60\u4e86?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8bf7\u95ee\u5427!\u665a\u8f88\u662f\u77e5\u65e0\u4e0d\u8a00\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u51c6\u5907\u5982\u4f55\u5b89\u6392\u6211\u8fd9\u4e24\u4e2a\u5973\u513f\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8001\u524d\u8f88\u6709\u4f55\u89c1\u6559?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8001\u592b\u7eb5\u6709\u610f\u89c1\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u542c\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u665a\u8f88\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u53cc\u5973\u5df2\u7ecf\u53d7\u5c3d\u4e86\u59d4\u5c48\uff0c\u4f60\u8fd8\u8981\u59d4\u5c48\u5e7d\u5170\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7239\uff0c\u5973\u513f\u81ea\u613f\u7684\uff0c\u4e0d\u80fd\u602a\u5728\u5bd2\u8863\u7684\u5934\u4e0a\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4e8b\u5df2\u81f3\u6b64\uff0c\u6211\u5982\u4e00\u529b\u53cd\u5bf9\uff0c\u4e5f\u4e0d\u8fc7\u5f92\u589e\u7eb7\u6270\u7f62\u4e86\uff0c\u81f3\u5c11\uff0c\u5728\u9762\u4e0a\uff0c\u8981\u8fc7\u5f97\u53bb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8001\u524d\u8f88\u5429\u5490\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e09\u65e5\u4e4b\u5185\uff0c\u4f60\u5e26\u7740\u8058\u793c\uff0c\u5230\u7b2c\u4e00\u5821\u4e2d\u63d0\u4eb2\uff0c\u8001\u592b\u7acb\u523b\u7ed9\u4f60\u4eec\u5b8c\u6210\u6d1e\u623f\u82b1\u70db\uff0c\u7136\u540e\uff0c\u8001\u592b\u4f9d\u7ea6\u9063\u6563\u795e\u5200\u5802\u4e2d\u4eba\u624b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u665a\u8f88\u9075\u547d\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u54b1\u4eec\u8d70!\u201d \u3000\u3000\u7a81\u7136\u4f38\u624b\u6293\u4f4f\u4e86\u9ad8\u5e7d\u5170\uff0c\u8d77\u8eab\u9762\u53bb\u3002 \u3000\u3000\u671b\u7740\u4e24\u4eba\u80cc\u5f71\uff0c\u4f55\u5bd2\u8863\u5446\u5446\u51fa\u795e\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u4e2a\u7ed3\u679c\uff0c\u5927\u51fa\u4ed6\u610f\u6599\u4e4b\u5916\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u4e0d\u77e5\u5982\u4f55\u63aa\u65bd\u3002 \u3000\u3000\u5434\u53cc\u5973\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u56de\u53bb\u5427!\u6b64\u4e8b\u5173\u7cfb\u91cd\u5927\uff0c\u54b1\u4eec\u5f97\u548c\u9ec4\u5821\u4e3b\u5546\u91cf\u4e00\u4e0b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u5929\u5065\u771f\u7684\u4f1a\u9063\u6563\u795e\u5200\u5802\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9ad8\u5929\u5065\u5bf9\u5e7d\u5170\u5341\u5206\u5ba0\u7231\uff0c\u81f3\u4e8e\u9ad8\u5e7d\u5170\u4f1a\u4e0d\u4f1a\u548c\u7236\u4eb2\u8054\u624b\u5408\u4f5c\uff0c\u5bf9\u4ed8\u6211\u4eec\uff0c\u90a3\u5c31\u8981\u770b\u4f60\u7684\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u770b\u6211\u7684?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5bf9!\u5982\u82e5\u9ad8\u5e7d\u5170\u5bf9\u4f60\u662f\u4e00\u7247\u771f\u60c5\uff0c\u90a3\u5c31\u7edd\u5bf9\u4e0d\u52a0\u5bb3\u6211\u4eec\uff0c\u800c\u4e14\uff0c\u5979\u8fd8\u4f1a\u60f3\u6cd5\u5b50\u901a\u77e5\u6211\u4eec\u4e00\u58f0\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5509!\u8fd9\u4e2a\uff0c\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5947\u602a\u9053\uff1a\u201c\u5979\u5bf9\u4f60\u662f\u771f\u60c5\uff0c\u662f\u5047\u610f\uff0c\u4f60\u90fd\u4e0d\u77e5\u9053\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u7275\u626f\u7236\u5973\u4e4b\u60c5\uff0c\u592b\u5987\u4e4b\u7231\uff0c\u5728\u4e0b\u4e00\u65f6\u4e4b\u95f4\uff0c\u5b9e\u5728\u4e5f\u65e0\u6cd5\u5206\u8fa8\u51fa\uff0c\u5979\u662f\u771f\u662f\u5047\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u521a\u624d\u770b\u5230\u4e86\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u5929\u5065\u62c9\u4f4f\u5e7d\u5170\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5982\u82e5\u6211\u6ca1\u6709\u770b\u9519\uff0c\u9ad8\u5929\u5065\u4e00\u628a\u6263\u4f4f\u4e86\u9ad8\u5e7d\u5170\u7684\u8109\u7a74\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u771f\u7684?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u65e0\u6cd5\u8bc1\u5b9e\u8fd9\u4ef6\u4e8b\uff0c \u53ea\u6709\u9ad8\u5929\u5065\u548c\u9ad8\u5e7d\u5170\u624d\u662f\u771f\u6b63\u77e5\u9053\u5185\u60c5\u7684\u4eba\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u6211\u5012\u6ca1\u6709\u6ce8\u610f\u3002\u201d \u3000\u3000\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u63a5\u9053\uff1a\u201c\u6211\u60f3\u5e7d\u5170\u4e0d\u81f3\u4e8e\u51fa\u5356\u6211\u4eec\u3002\u201d \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u6e05\u4eae\u7684\u58f0\u97f3\uff0c\u63a5\u9053\uff1a \u201c\u5c31\u7b97\u9ad8\u59d1\u5a18\u5168\u5fc3\u5168\u610f\u5730\u504f\u91cd\u6211\u4eec\uff0c\u4f46\u54b1\u4eec\u4e5f\u4e0d\u80fd\u4e0d\u4f5c\u51c6\u5907\u3002\u201d \u3000\u3000\u53e3\u97f3\u5f88\u719f\uff0c\u4f46\u8eab\u4e0a\u5374\u7a7f\u7740\u4e00\u5957\u5e97\u4f19\u8ba1\u7684\u8863\u670d\u3002 \u3000\u3000\u662f\u9ec4\u7075\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5821\u4e3b\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ad8\u5929\u5065\u5e76\u975e\u662f\u5168\u7136\u65e0\u5907\u800c\u6765\uff0c\u968f\u884c\u4e00\u5341\u4eba\u4f4d\u9ad8\u624b\uff0c\u5206\u5e03\u5728\u8fd9\u4e66\u753b\u697c\u524d\u540e\u5de6\u53f3\uff0c\u5f85\u6708\u697c\u7684\u56db\u5468\uff0c\u4e5f\u5e03\u6709\u4eba\u624b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e9b\u60c5\u5f62\u9ad8\u5e7d\u5170\u4e5f\u77e5\u9053\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u8fd9\u5c31\u4e0d\u592a\u6e05\u695a\u4e86\uff0c\u4e0d\u8fc7\uff0c\u636e\u5728\u4e0b\u7684\u770b\u6cd5\uff0c\u9ad8\u5e7d\u5170\u4f3c\u4e4e\u662f\u4e0d\u77e5\u9053\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u9ad8\u5929\u5065\u662f\u4e00\u4e2a\u5f88\u5c0f\u5fc3\u7684\u4eba\uff0c\u4ed6\u6709\u4e00\u79cd\u81ea\u52a8\u4f38\u7f29\u7684\u4fdd\u536b\u7ec4\u7ec7\uff0c\u5c31\u6211\u6240\u89c1\uff0c\u9ad8\u5e7d\u5170\u4e8b\u5148\u4e0d\u4f1a\u77e5\u9053\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u770b\u5230\u4ed6\u4eec\u7236\u5973\u4e86?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u770b\u662f\u770b\u5230\u4e86\uff0c\u4e0d\u8fc7\uff0c\u8ddd\u79bb\u76f8\u5f53\u8fdc\uff0c\u6211\u65e0\u6cd5\u770b\u5f97\u5f88\u6e05\u695a\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5821\u4e3b\u4e00\u76f4\u6ca1\u6709\u63a5\u8fd1\u4e66\u753b\u697c\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u662f\u554a\uff0c\u9664\u4e86\u534e\u5999\u771f\u6df7\u8fdb\u6765\u4e4b\u5916\uff0c\u8fde\u6211\u4eec\u4e5f\u88ab\u9694\u5728\u4e66\u753b\u697c\u5916\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u662f\u8bf4\uff0c\u4ed6\u4eec\u5e03\u5b88\u5f97\u5f88\u4e25\u5bc6\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bf9!\u8fde\u4e66\u753b\u697c\u7684\u8336\u623f\uff0c\u4e5f\u4e0d\u80fd\u63a5\u8fd1\u5f85\u6708\u697c\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u73b0\u5728\uff0c\u54b1\u4eec\u5e94\u8be5\u5982\u4f55?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u8981\u5821\u4e3b\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u548c\u9ad8\u5929\u5065\u8c08\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u4ed4\u7ec6\u5730\u8bf4\u660e\u4e86\u7ecf\u8fc7\u3002 \u3000\u3000\u9ec4\u7075\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u4e0d\u8bba\u4e8b\u60c5\u5982\u4f55\uff0c\u6211\u4eec\u90fd\u8981\u5c3d\u529b\u800c\u4e3a\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u660e\u5929\uff0c\u6211\u4eec\u5c31\u51c6\u5907\u4e00\u4efd\u4f18\u539a\u7684\u8058\u793c\uff0c\u5230\u7b2c\u4e00\u5821\u53bb\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u4e5f\u8981\u53bb\u4e48?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u53bb!\u4e0d\u8fc7\uff0c\u53bb\u7684\u4eba\u4e0d\u662f\u9634\u9633\u5821\u4e3b\u9ec4\u7075\uff0c\u800c\u662f\u4e00\u4e2a\u966a\u9001\u8058\u793c\u7684\u5de5\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u600e\u884c\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4e3a\u4ec0\u4e48\u4e0d\u884c\uff0c \u4e0d\u4f46\u6211\u8981\u53bb\uff0c\u800c\u4e14\u4e94\u5927\u5251\u4f7f\u90fd\u8981\u53bb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u4e07\u4e00\u88ab\u9ad8\u5929\u5065\u67e5\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u6211\u76f8\u4fe1\uff0c\u6211\u7684\u5316\u5986\u672f\u8db3\u53ef\u4ee5\u7792\u8fc7\u4ed6\u4eec\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5821\u4e3b\u53ef\u6709\u8ba1\u5212?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6709!\u6211\u8981\u628a\u672c\u5821\u4e2d\u6240\u6709\u7684\u4eba\u624b\uff0c\u4e00\u9f50\u96c6\u4e2d\u5230\u7b2c\u4e00\u5821\u4e2d\uff0c\u4e00\u65e6\u6709\u53d8\uff0c\u53ea\u597d\u653e\u624b\u4e00\u6218\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u4f55\u624d\u80fd\u628a\u54b1\u4eec\u7684\u4eba\u624b\uff0c\u8fd0\u5165\u7b2c\u4e00\u5821\u4e2d\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u8fd9\u4e2a\u529e\u6cd5\u5f88\u591a\uff0c\u6211\u4eec\u53ef\u4ee5\u5047\u626e\u4ece\u4eba\uff0c\u8ddf\u968f\u8bf8\u4f4d\u8fdb\u5165\u5821\u4e2d\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u4e2a\u529e\u6cd5\u4e0d\u597d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ee5\u5728\u4e0b\u7684\u6613\u5bb9\u624b\u6cd5\uff0c\u5c31\u7b97\u9ad8\u5821\u4e3b\u795e\u76ee\u5982\u7535\uff0c\u4e5f\u672a\u5fc5\u80fd\u77a7\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4ee5\u9ad8\u5929\u5065\u7684\u667a\u6167\uff0c\u7528\u4e0d\u7740\u770b\u51fa\u4ec0\u4e48\u6bdb\u75c5\uff0c \u53ea\u8981\u4ed6\u611f\u89c9\u5230\u53ef\u7591\uff0c\u4ed6\u5c31\u4f1a\u51fa\u624b\u5bf9\u4ed8\u4f60\u4eec\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u90a3\u662f\u8bf4\u8fd9\u4e2a\u529e\u6cd5\u884c\u4e0d\u901a\u4e86\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9!\u884c\u4e0d\u901a\u3002\u201d \u3000\u3000\u9ec4\u7075\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4ec0\u4e48\u529e\u6cd5\u5462\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u4f60\u4eec\u80fd\u591f\u5316\u88c5\u6210\u7b2c\u4e00\u5821\u4e2d\u4eba\uff0c\u800c\u53c8\u4f7f\u4ed6\u4eec\u4e0d\u53d1\u89c9\u3002\u201d \u3000\u3000\u9ec4\u7075\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u597d!\u8fd9\u4e00\u70b9\uff0c\u6211\u76f8\u4fe1\u4e0d\u4f1a\u592a\u96be\uff0c\u4e8c\u4f4d\u53ea\u7ba1\u653e\u5fc3\u5165\u5821\uff0c\u6211\u4eec\u4f1a\u60f3\u529e\u6cd5\uff0c\u8fdb\u5165\u7b2c\u4e00\u5821\u53bb\uff1a\u201d \u3000\u3000\u5434\u53cc\u5973\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u9ec4\u5821\u4e3b\uff0c \u4f60\u53ef\u66fe\u60f3\u5230\u8fdb\u5165\u4e86\u7b2c\u4e00\u5821\u7684\u540e\u679c\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u96be\u9053\u4f60\u8ba4\u4e3a\u9ad8\u5929\u5065\u771f\u7684\u4f1a\u7ed9\u4f60\u4eec\u4e09\u4e2a\u4eba\uff0c\u5b8c\u6210\u6d1e\u623f\u82b1\u70db\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9\u6211\uff0c\u4ed6\u53ef\u80fd\u4e0d\u4f1a\uff0c\u4f46\u6211\u4e0d\u76f8\u4fe1\uff0c\u4ed6\u8fd8\u4f1a\u52a0\u5bb3\u5e7d\u5170\uff0c\u5e7d\u5170\u4e00\u76f4\u662f\u4ed6\u6700\u5ba0\u7231\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4eb2\u60c5\u5982\u80fd\u6253\u52a8\u4e86\u9ad8\u5929\u5065\u7684\u94c1\u77f3\u5fc3\u80a0\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u9677\u6eba\u5982\u6b64\u4e4b\u6df1\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u9632\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u65e0\uff0c\u4f60\u4eec\u9762\u5bf9\u7684\u662f\u5929\u4e0b\u7b2c\u4e00\u4f2a\u541b\u5b50\uff0c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u6c5f\u6e56\u4e0a\u7edd\u5927\u90e8\u4efd\u7684\u4eba\u8fd8\u8ba4\u4e3a\u4ed6\u662f\u4e00\u4e2a\u5f88\u597d\u7684\u4eba\uff0c\u5c31\u7b97\u53d7\u8fc7\u4ed6\u7b97\u8ba1\u7684\u4eba\uff0c\u4e5f\u672a\u5fc5\u77e5\u9053\u662f\u4ed6\u5728\u5e55\u540e\u64cd\u7eb5\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u9053\u7b2c\u4e00\u5821\u7684\u5b9e\u529b\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u77e5\u9053\u5f88\u5f3a\u5927\uff0c\u4e0d\u8fc7\uff0c\u73b0\u5728\u9664\u4e86\u72c2\u9f99\u3001\u98de\u9e70\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u4eba\uff0c\u90fd\u8fd8\u672a\u8c03\u5165\u5821\u4e2d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u771f\u6b63\u7684\u5f3a\u5927\u5b9e\u529b\uff0c\u4e0d\u5728\u5821\u5916\uff0c\u800c\u5728\u7b2c\u4e00\u5821\u4e2d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u795e\u5200\u5802\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u795e\u5200\u5802\u975e\u5e38\u5e7f\u5927\uff0c\u5bb9\u7eb3\u4e86\u4e0a\u767e\u4f4d\u7684\u9ad8\u624b\u3002\u201d \u9ec4\u7075\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9664\u4e86\u795e\u5200\u5802\u4e4b\u5916\uff0c\u8fd8\u6709\u4e9b\u4ec0\u4e48\u4eba\u7269\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5927\u603b\u7ba1\u3001\u4e8c\u603b\u7ba1\uff0c\u90fd\u662f\u4e0d\u597d\u60f9\u7684\u4eba\uff0c\u8fd8\u6709\u9ad8\u5e7d\u5170\u7387\u9886\u7684\u5341\u4e8c\u98de\u71d5\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u5341\u4e8c\u98de\u71d5\uff0c\u662f\u600e\u4e48\u6837\u7684\u4eba\u7269?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5341\u4e8c\u4f4d\u5c11\u5973\uff0c\u4e00\u822c\u5927\u5c0f\u7684\u5e74\u9f84\uff0c\u662f\u9ad8\u5e7d\u5170\u4eb2\u81ea\u8c03\u6559\u51fa\u7684\u5973\u9ad8\u624b\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\u5e74\u7eaa\u4e0d\u5927\uff0c\u8c03\u6559\u8fd9\u4e9b\u4eba\u4e0d\u4f1a\u592a\u4e45\u5427\uff01\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u8fd9\u4e9b\u5973\u5b69\u5b50\uff0c\u672c\u662f\u795e\u5200\u5802\u4e2d\u7684\u5973\u6740\u624b\uff0c\u88ab\u9ad8\u5e7d\u5170\u9009\u62e9\u4e86\u4e00\u90e8\u5206\uff0c\u518d\u52a0\u8bad\u7ec3\uff0c\u5979\u4eec\u5df2\u7ecf\u6709\u7740\u5f88\u597d\u7684\u6b66\u529f\u57fa\u7840\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u5979\u4eec\u4e3a\u4ec0\u4e48\u79f0\u5341\u4e8c\u98de\u71d5\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u56e0\u4e3a\uff0c\u5979\u4eec\u5251\u6cd5\u5feb\u5982\u95ea\u7535\uff0c\u98de\u8dc3\u5982\u71d5\uff0c\u6240\u4ee5\u79f0\u4f5c\u5341\u4e8c\u98de\u71d5\u3002\u53c8\u79f0\u5341\u4e8c\u5973\u5c06\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u5341\u4e8c\u98de\u71d5\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5341\u4e8c\u98de\u71d5\u7684\u5b9e\u529b\u5982\u4f55\uff0c\u4e0d\u4f46\u6211\u4e0d\u5341\u5206\u4e86\u89e3\uff0c\u53ea\u6015\u9ad8\u5929\u5065\u4e5f\u4e0d\u592a\u4e86\u89e3\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\u662f\u4f55\u7528\u5fc3\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4e0d\u77e5\u9053\uff0c\u5979\u662f\u4e2a\u5f88\u6709\u5fc3\u8ba1\u7684\u59d1\u5a18\uff0c\u6211\u51b7\u773c\u65c1\u89c2\u5f88\u591a\u5e74\uff0c\u4e00\u76f4\u5230\u73b0\u5728\u8fd8\u4e0d\u592a\u4e86\u89e3\u5979\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u90a3\u662f\u8bf4\uff0c\u5341\u4e8c\u98de\u71d5\u4e00\u76f4\u5728\u9ad8\u5e7d\u5170\u7684\u638c\u63e1\u4e4b\u4e2d\u4e86\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9!\u5341\u98de\u71d5\u73b0\u5728\u88ab\u9ad8\u5e7d\u5170\u85cf\u4e86\u8d77\u6765\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u85cf\u8d77\u6765?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u85cf\u7684\u610f\u601d\uff0c\u5c31\u662f\u628a\u5341\u4e8c\u98de\u71d5\u5b89\u6392\u5728\u4e00\u4e2a\u5f88\u9690\u79d8\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u7b2c\u4e00\u5821\u867d\u7136\u5f88\u5927\uff0c\u4f46\u5341\u4e8c\u98de\u71d5\u662f\u5341\u4e8c\u4e2a\u4eba\uff0c\u9ad8\u5e7d\u5170\u5982\u4f55\u80fd\u591f\u628a\u5979\u4eec\u85cf\u8d77\u6765\uff0c\u800c\u4e0d\u8ba9\u4eba\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u81ea\u7136\u662f\u6709\u4eba\u77e5\u9053\uff0c\u4e0d\u8fc7\uff0c\u90a3\u5730\u65b9\u88ab\u9ad8\u5c0f\u59d0\u5212\u4e3a\u7981\u5730\uff0c\u4efb\u4f55\u4eba\u4e0d\u5f97\u8f7b\u6613\u8fdb\u5165\uff0c\u90a3\u91cc\u5c31\u6210\u4e86\u2014\u4e2a\u9690\u79d8\u4e4b\u5730\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u9ad8\u59d1\u5a18\u7a76\u7adf\u60f3\u4e9b\u4ec0\u4e48\u4e1c\u897f\uff0c \u6574\u4e2a\u7b2c\u4e00\u5821\u4e2d\u4eba\uff0c\u90fd\u4e0d\u592a\u4e86\u89e3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u6211\u8fd8\u662f\u6709\u4e9b\u4e0d\u660e\u767d\uff0c\u5979\u8fd9\u505a\u6cd5\uff0c\u9ad8\u5929\u5065\u4f1a\u7b54\u5e94\u5979\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u9ad8\u5821\u4e3b\u6709\u610f\u6002\u607f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u9ad8\u5929\u5065\u662f\u5141\u8bb8\u7b2c\u4e00\u5821\u4e2d\uff0c\u5404\u522b\u5efa\u7acb\u52bf\u529b\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u7236\u5973\u4eb2\u60c5\uff0c\u9ad8\u5929\u5065\u5bf9\u5979\uff0c\u603b\u662f\u96be\u514d\u6709\u4e9b\u7eb5\u5bb9\u3002\u201d \u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6211\u770b\uff0c\u8fd9\u662f\u9ad8\u5929\u5065\u522b\u6709\u7528\u5fc3\uff0c \u7236\u5973\u4e24\u4e2a\u4eba\uff0c\u65e9\u5c31\u5546\u91cf\u597d\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5f88\u6709\u53ef\u80fd\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u9ad8\u5e7d\u5170\u4e5f\u5b8c\u5168\u9760\u4e0d\u4f4f\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u8fc7\u53bb\uff0c\u6211\u662f\u8fd9\u4e48\u60f3\uff0c\u4f46\u73b0\u5728\uff0c\u6211\u5bf9\u5979\u7684\u770b\u6cd5\uff0c\u7a0d\u6709\u53d8\u5316\u4e86\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u600e\u4e48\u8bf4\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5982\u82e5\u5979\u5bf9\u4f60\u5168\u65e0\u60c5\u610f\uff0c\u5979\u4e0d\u4f1a\u628a\u6e05\u767d\u5973\u513f\u4e4b\u8eab\u4ea4\u7ed9\u4f60\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u771f\u7684\u5417?\u5bd2\u8863\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u9ec4\u5821\u4e3b\u4e0d\u8981\u89c1\u7b11\uff0c\u6211\u4eec\u73b0\u5728\u8ba8\u8bba\u7684\u6c5f\u6e56\u5927\u8ba1\uff0c\u513f\u5973\u79c1\u60c5\u7684\u4e8b\uff0c\u8bf7\u6055\u6211\u4e0d\u987e\u7f9e\u803b\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6c5f\u6e56\u513f\u5973\uff0c\u4e0d\u62d8\u5c0f\u8282\uff0c\u53ea\u8981\u9898\u76ee\u5802\u5802\u6b63\u6b63\uff0c\u4e0d\u7528\u987e\u5fcc\u592a\u591a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u9ad8\u5e7d\u5170\u6709\u6d01\u50fb\uff0c\u522b\u4eba\u7528\u8fc7\u7684\u4e1c\u897f\uff0c\u5979\u5c31\u4e0d\u80af\u518d\u7528\uff0c\u4f55\u51b5\u80af\u8ba9\u4f60\u63a5\u8fd1\u5979\u7684\u8eab\u5b50\uff0c\u9664\u975e\u5bf9\u4f60\u60c5\u7231\u592a\u6df1\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5434\u59d1\u5a18\uff0c\u4e8b\u5df2\u5982\u6b64\uff0c\u54b1\u4eec\u4f3c\u4e4e\u662f\u6709\u8fdb\u65e0\u9000\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4e0d\u9519\u554a!\u4e0d\u8bba\u9ad8\u5929\u5065\u5b89\u6392\u4e86\u4ec0\u4e48\u5929\u7f57\u5730\u7f51\uff0c\u54b1\u4eec\u4e5f\u5f97\u95ef\u4e00\u95ef\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u6c89\u601d\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u4f9d\u7ea6\u884c\u4e8b\uff0c\u6211\u4eec\u4f1a\u6df7\u5165\u7b2c\u4e00\u5821\u4e2d\u63a5\u5e94\u3002\u201d\u8f6c\u8eab\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u5434\u53cc\u5973\u56de\u987e\u4e86\u4f55\u5bd2\u8863\u4e00\u773c\uff0c\u9053\uff1a \u201c\u4e0d\u5165\u864e\u7a74\uff0c\u7109\u5f97\u864e\u5b50\uff0c\u4ee5\u7b2c\u4e00\u5821\u76ee\u4e0b\u7684\u5b9e\u529b\u4e4b\u5f3a\uff0c\u9664\u4e86\u7a9d\u91cc\u53cd\u4e4b\u5916\uff0c\u4e5f\u6ca1\u6709\u53ef\u884c\u4e4b\u8def\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u60c5\u52bf\u5982\u6b64\uff0c\u4e5f\u53ea\u6709\u5b64\u6ce8\u4e00\u63b7\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u518d\u5230\u7b2c\u4e00\u5821\u65f6\uff0c\u53d7\u5230\u4e86\u5f88\u9686\u91cd\u7684\u6b22\u8fce\u3002 \u3000\u3000\u9ad8\u5929\u5065\u4eb2\u81ea\u8fce\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5c31\u50cf\u8fce\u63a5\u65b0\u8d35\u4e00\u6837\uff0c\u4e00\u6761\u5f88\u957f\u7684\u7ea2\u6bef\uff0c\u7531\u7b2c\u4e00\u5821\u5927\u95e8\u53e3\u5904\uff0c\u76f4\u94fa\u5230\u5927\u5385\u524d\u9762\u3002 \u3000\u3000\u5927\u5385\u4e2d\u6446\u597d\u4e86\u4e00\u684c\u9152\u5e2d\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e5f\u6765\u4e86\uff0c\u5c31\u968f\u5728\u4f55\u5bd2\u8863\u7684\u8eab\u540e\u3002 \u3000\u3000\u6709\u4e00\u4ef6\u4e8b\u60c5\u5f88\u5947\u602a\uff0c\u90a3\u5c31\u662f\uff0c\u9664\u4e86\u9ad8\u5929\u5065\u4e4b\u5916\uff0c\u5434\u53cc\u5973\u6ca1\u6709\u518d\u770b\u5230\u4e00\u4e2a\u81ea\u5df1\u8ba4\u8bc6\u7684\u4eba\u3002 \u3000\u3000\u4f3c\u4e4e\u6574\u4e2a\u7b2c\u4e00\u5821\u4e2d\u4eba\uff0c\u5b8c\u5168\u90fd\u6362\u8fc7\u4e00\u6837\u3002 \u3000\u3000\u9ad8\u5929\u5065\u628a\u4e24\u4eba\u8ba9\u5165\u4e86\u5ba2\u4f4d\uff0c \u81ea\u5df1\u5728\u4e3b\u4f4d\u4e0a\u76f8\u966a\u3002 \u8c6a\u534e\u3001\u5e7f\u5927\u7684\u5ba2\u5385\uff0c\u9664\u4e86\u4f3a\u5019\u9152\u83dc\u7684\u56db\u4e2a\u5973\u5a62\u3001\u56db\u4e2a\u7ae5\u5b50\u4e4b\u5916\uff0c\u53ea\u6709\u4ed6\u4eec\u4e09\u4e2a\u4eba\u3002 \u3000\u3000\u4e24\u4e2a\u5ba2\u4eba\uff0c\u4e00\u4e2a\u4e3b\u4eba\u3002 \u3000\u3000\u5434\u53cc\u5973\u56de\u987e\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e49\u7236\uff0c \u53ea\u6709\u6211\u4eec\u4e24\u4e2a\u4eba\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u52a0\u4e0a\u4e3a\u7236\u7684\uff0c\u54b1\u4eec\u4e00\u5171\u6709\u4e09\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u73b0\u5728\uff0c\u5979\u4e0d\u597d\u610f\u601d\u73b0\u8eab\uff0c\u4e0d\u8fc7\uff0c\u6211\u60f3\u7b49\u4e00\u4f1a\uff0c\u5979\u4f1a\u51fa\u6765\u7684\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u5821\u4e2d\u7684\u51e0\u4f4d\u603b\u7ba1\u5462\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u53cc\u5973\uff0c\u4f60\u60f3\u60f3\u770b\uff0c\u6211\u8fd8\u8981\u8c01\u53c2\u52a0\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u81f3\u5c11\uff0e\u4e24\u4f4d\u5802\u4e3b\u5e94\u8be5\u6765\u5440\uff01\u201d \u3000\u3000\u9ad8\u5929\u5065\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u8981\u70ed\u95f9\uff0c\u54b1\u4eec\u5c31\u60ca\u52a8\u5929\u4e0b\uff0c\u529e\u4e00\u573a\u6b66\u6797\u4e2d\u4ece\u672a\u6709\u7684\u5927\u559c\u5bb4\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e49\u7236\uff0c\u4e3a\u4ec0\u4e48\u53c8\u6539\u53d8\u4e01\u4e3b\u610f\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a \u201c\u5e7d\u5170\u7684\u610f\u601d\uff0c\u5979\u8bf4\u5a5a\u59fb\u662f\u4e24\u4e2a\u4eba\u7684\u4e8b\uff0c\u4e0d\u8981\u60ca\u52a8\u522b\u4eba\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u4e49\u7236\u540c\u610f\u4e86\u5979\u7684\u8bf4\u6cd5\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u4e0d\u540c\u610f\uff0c\u4e5f\u4e0d\u884c\u554a\uff01\u201d \u3000\u3000\u5434\u53cc\u5973\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e49\u7236\uff0c \u5973\u513f\u5c71\u51b3\u5b9a\u4e86\u4e00\u4ef6\u5927\u4e8b\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u54e6!\u4ec0\u4e48\u5927\u4e8b?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u60f3\uff0c\u8ba9\u5e7d\u5170\u548c\u5bd2\u8863\u5171\u540c\u6210\u4eb2\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u884c\uff0c \u4f60\u662f\u5927\u59d0\uff0c \u800c\u4e14\u4f60\u548c\u4f55\u5bd2\u8863\u5148\u6709\u4e86\u5a5a\u7ea6\uff0c\u600e\u4e48\u80fd\u55a7\u5bbe\u593a\u4e3b?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4e49\u7236\uff0c\u5e7d\u5170\u53eb\u4e86\u6211\u5f88\u591a\u5e74\u7684\u5927\u59ca\uff0c\u6211\u8fd9\u4e2a\u505a\u59ca\u59ca\u7684\uff0c\u603b\u5e94\u8be5\u8ba9\u5979\u4e00\u4e8c\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a \u201c\u53cc\u5973\uff0c \u4f60\u4e5f\u7b97\u6211\u7684\u5973\u513f\uff0c\u6211\u8fd9\u4e2a\u505a\u4e49\u7236\u7684\uff0c\u4e5f\u5e94\u8be5\u516c\u5e73\u4e00\u4e9b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e49\u7236\uff0c\u5c31\u7b97\u6211\u5e74\u7eaa\u5927\u4e00\u4e9b\u4e48\uff0c\u6211\u4e5f\u5e94\u8ba9\u8ba9\u5979\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u4eec\u59ca\u59b9\u95f4\u7684\u611f\u60c5\u5982\u6b64\u4e4b\u597d\uff0c\u53eb\u6211\u8fd9\u505a\u4e49\u7236\u7684\uff0c\u4e5f\u5f88\u60ed\u6127\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4e0d\uff0c\u4e49\u7236\uff0c\u4f60\u5bf9\u6211\u4eec\uff0c\u5e73\u5e38\u5df2\u7ecf\u5341\u5206\u7231\u62a4\u4e86\uff0c\u5982\u4eca\u9047\u4e0a\u4e86\u8fd9\u4e48\u5927\u7684\u53d8\u6545\uff0c\u6211\u4eec\u505a\u5973\u513f\u7684\uff0c\u4e5f\u5e94\u8be5\u4e3a\u4f60\u5206\u5206\u5fe7\uff0c\u5c3d\u70b9\u5b5d\u5fc3\u554a\uff01\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5509!\u4f60\u4eec\u8fd9\u4e48\u8bf4\uff0c\u6211\u8fd9\u505a\u4e49\u7236\u7684\uff0c\u4e5f\u4e0d\u4fbf\u8bf4\u4ec0\u4e48\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e49\u7236\uff0c\u8fd9\u4ef6\u4e8b\u4f60\u540c\u610f\u4e86\uff0c\u4e0d\u77e5\u7b2c\u4e00\u5821\u4e2d\u7684\u4eba\u770b\u6cd5\u5982\u4f55?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u6ca1\u6709\u548c\u4ed6\u4eec\u8c08\u8fc7\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e49\u7236\u4e3a\u4ec0\u4e48\u4e0d\u80af\u544a\u8bc9\u4ed6\u4eec?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u672c\u6765\uff0c\u6211\u60f3\u627e\u4ed6\u4eec\u5546\u91cf\u4e00\u4e0b\uff0c\u4e0d\u627e\u4ed6\u4eec\u7684\u539f\u56e0\uff0c\u4e5f\u662f\u5e7d\u5170\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\u600e\u4e48\u8bf4?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5979\u8bf4\uff0c\u8fd9\u662f\u6211\u4eec\u7236\u5973\u7684\u4e8b\uff0c\u548c\u522b\u4eba\u6ca1\u6709\u592a\u5927\u7684\u5173\u7cfb\uff0c\u6211\u540c\u610f\u4e86\u7239\u4e5f\u540c\u610f\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u548c\u522b\u7684\u4eba\u5546\u91cf\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\u7684\u8bdd\uff0c\u5012\u4e5f\u6709\u7406\u3002\u201d \u3000\u3000\u826f\u4e45\u4e4b\u540e\uff0c\u9ad8\u5929\u5065\u671b\u7740\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u4f60\u6709\u4ec0\u4e48\u610f\u89c1?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\uff0c\u6ca1\u6709\u4ec0\u4e48\u610f\u89c1\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u53cc\u5973\u521a\u624d\u8bf4\u7684\u8bdd\uff0c\u4f60\u4e5f\u540c\u610f\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u4eec\u4e24\u4f4d\u540c\u610f\u7684\u4e8b\uff0c\u5728\u4e0b\u4e5f\u53ea\u6709\u540c\u610f\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u597d!\u597d!\u5bd2\u8863\uff0c\u54b1\u4eec\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u4e0d\u80fd\u548c\u5973\u4eba\u4e00\u822c\u89c1\u8bc6\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u53cc\u65b9\u53c8\u6c89\u9ed8\u4e0b\u6765\u3002 \u3000\u3000\u9ad8\u5929\u5065\u4e5f\u60f3\u4e0d\u51fa\u4ec0\u4e48\u8bdd\u8bf4\u4e86\uff0c\u7d22\u6027\u95ed\u4e0a\u4e86\u773c\u775b\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u56de\u987e\u5434\u53cc\u5973\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u7761\u7740\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e49\u7236\uff0c\u54b1\u4eec\u8fd8\u8981\u7b49\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5929\u5065\u7741\u5f00\u773c\u775b\uff0c\u671b\u4e86\u5434\u53cc\u5973\uff0c\u9053\uff1a\u201c\u7b49\u5e7d\u5170\u554a!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5979\u51e0\u65f6\u6765?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5feb\u4e86\uff0c\u5feb\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u53c8\u95ed\u4e0a\u4e86\u773c\u775b\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e00\u626c\u7709\u67f3\u513f\uff0c\u9053\uff1a\u201c\u4e49\u7236\uff0c \u4f60\u5f88\u75b2\u7d2f\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5bf9!\u8fd9\u4e00\u5929\u4e00\u591c\uff0c\u6211\u90fd\u6ca1\u6709\u7761\u8fc7\uff0e\u6211\u5728\u60f3\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4e49\u7236\uff0c\u6211\u4eec\u5df2\u7ecf\u8fdb\u4e86\u7b2c\u4e00\u5821\uff0c \u4f60\u51c6\u5907\u600e\u4e48\u529e\uff0c\u4e5f\u8be5\u4f5c\u4e2a\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53cc\u5973\uff0c \u4f60\u53ef\u662f\u5728\u903c\u6211?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4e49\u7236\uff0c\u73b0\u5728\uff0c\u597d\u50cf\u5df2\u7ecf\u5230\u4e86\u6c34\u843d\u77f3\u51fa\u7684\u65f6\u5019\u4e86\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u597d!\u4f60\u4eec\u5148\u89c1\u89c1\u5e7d\u5170\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u51fa\u73b0\u4e86\uff0c\u7a7f\u7740\u4e00\u4ef6\u7c89\u7ea2\u8272\u7684\u8863\u773c\uff0c\u7c89\u7ea2\u7f57\u88d9\u7c89\u7ea2\u886b\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u53d8\u4e86\uff0c\u800c\u4e14\uff0c\u53d8\u5f97\u5f88\u591a\uff0c \u76ee\u5149\u75f4\u5446\uff0c\u8138\u8272\u82cd\u767d\uff0c\u7f13\u7f13\u884c\u4e86\u8fc7\u6765\u3002 \u5434\u53cc\u5973\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u5e7d\u5170!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u4f3c\u662f\u4ecd\u7136\u6709\u7740\u76f8\u5f53\u7684\u89c6\u542c\u80fd\u529b\uff0c\u505c\u4e0b\u811a\u6b65\uff0c\u75f4\u5446\u7684\u76ee\u5149\uff0c\u7f13\u7f13\u8f6c\u5230\u4e86\u5434\u53cc\u5973\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7a81\u7136\u95f4\u6709\u7740\u5fc3\u75bc\u7684\u611f\u89c9\uff0c\u970d\u7136\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a \u201c\u9ad8\u59d1\u5a18\u3002\u201d\u4e3e\u6b65\u51b2\u4e86\u8fc7\u53bb\u3002 \u5434\u53cc\u5973\u4e00\u4f38\u624b\uff0c\u6293\u4f4f\u4e86\u4f55\u5bd2\u8863\uff0c\u9053\uff1a \u201c\u9547\u9759\u4e9b\uff0c\u5979\u672a\u5fc5\u662f\u771f\u7684\u9ad8\u5e7d\u5170\u3002\u201d \u4f55\u5bd2\u8863\u5fc3\u4e2d\u4e00\u5bbd\uff0c\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u9ad8\u5929\u5065\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5982\u5047\u5305\u6362\u7684\u9ad8\u5e7d\u5170\uff0c\u6211\u7684\u4eb2\u751f\u5973\u513f\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a \u201c\u864e\u6bd2\u4e0d\u98df\u5b50\uff0c\u4e49\u7236\u8fd9\u6837\u5bf9\u4ed8\u5e7d\u5170\uff0c\u4e0d\u89c9\u7740\u624b\u6bb5\u592a\u8fc7\u6076\u6bd2\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u4f60\u53ef\u4ee5\u80cc\u53db\u6211\uff0c\u4f60\u7a76\u7adf\u4e0d\u662f\u6211\u7684\u5973\u513f\uff0c\u4f60\u8eab\u4e0a\u6ca1\u6709\u9ad8\u5bb6\u7684\u8840\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a \u201c\u5f88\u597d\uff0c\u4e49\u7236\u8fd9\u51e0\u53e5\u8bdd\uff0c\u65e0\u7591\u662f\u65ad\u4e86\u54b1\u4eec\u4e00\u573a\u7236\u5973\u4e4b\u60c5\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u672c\u6765\u662f\u4e2a\u9b54\u5973\uff0c\u6c38\u8fdc\u4e0d\u767b\u5927\u96c5\u4e4b\u5802\u7684\u9b54\u5973\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u4f60\u65e2\u65e0\u6cd5\u8d70\u4e0a\u6b63\u9053\uff0c\u53ea\u6709\u518d\u6ca6\u5165\u9b54\u9053\uff0c\u548c\u9634\u9633\u5821\u90a3\u6279\u4eba\u6df7\u5728\u4e00\u8d77\u4e86\u3002\u201d \u5434\u53cc\u5973\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e49\u7236\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u4e0d\u8bb8\u53eb\u6211\u4e49\u7236\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d!\u9ad8\u5821\u4e3b\uff0c\u4f60\u8fd9\u6837\u4e00\u610f\u5b64\u884c\uff0c\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u63a5\u9053\uff1a \u201c\u90a3\u53ea\u602a\u4f60\u4eec\u5750\u4e95\u89c2\u5929\uff0c\u5bf9\u6c5f\u6e56\u4e0a\u7684\u4e8b\uff0c\u77e5\u9053\u5f97\u592a\u5c11\u4e86\u3002\u201d \u5434\u53cc\u5973\u6309\u8010\u4e0b\u5fc3\u4e2d\u6cdb\u8d77\u7684\u6012\u706b\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u53ef\u5426\u8ba9\u6211\u548c\u5e7d\u5170\u8bf4\u51e0\u53e5\u8bdd?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f60\u53ea\u662f\u60f3\u8bc1\u660e\u4e00\u4e0b\u5979\u662f\u4e0d\u662f\u771f\u7684\u9ad8\u5e7d\u5170?\u201d \u3000\u3000\u5434\u53cc\u5973\uff1a\u201c\u4e0d\u9519\uff0c\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u6211\u8fd8\u4e0d\u592a\u76f8\u4fe1\uff0c\u5979\u662f\u771f\u7684\u9ad8\u5e7d\u5170\uff0c\u5509!\u4f60\u4e00\u76f4\u5bf9\u5979\u5f88\u5ba0\u7231\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u6211\u5ba0\u7231\u5979\uff0c\u4f46\u5374\u7edd\u5bf9\u4e0d\u5141\u8bb8\u5979\u80cc\u53db\u6211\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u672a\u518d\u7406\u4f1a\u9ad8\u5929\u5065\uff0c\u6697\u4e2d\u7d27\u63d0\u771f\u6c14\uff0c\u884c\u8fd1\u4e86\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u5979\u7f13\u7f13\u4f38\u51fa\u624b\u53bb\uff0c\u6293\u4f4f\u4e86\u9ad8\u5e7d\u5170\u7684\u53f3\u8155\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u6ca1\u6709\u56de\u5e94\uff0c\u53ea\u7528\u75f4\u5446\u7684\u76ee\u5149\u770b\u7740\u5979\u3002 \u3000\u3000\u5434\u53cc\u5973\u5f88\u4ed4\u7ec6\u7684\u770b\u4e86\u4e00\u9635\uff0c\u90a3\u662f\u771f\u771f\u6b63\u6b63\u7684\u9ad8\u5e7d\u5170\uff0c\u4e0d\u7981\u4e00\u5446\u3002 \u3000\u3000\u9ad8\u5929\u5065\u6de1\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u76f8\u4fe1\u4e86\u5427?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u786e\u7136\u662f\u9ad8\u5e7d\u5170\uff0c\u4f60\u597d\u6bd2\u8fa3\u7684\u624b\u6bb5\u554a\uff01\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u987a\u6211\u8005\u751f\uff0c\u9006\u6211\u8005\u6b7b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5305\u62ec\u4f60\u6700\u7231\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u9519!\u201d \u3000\u3000\u7a81\u7136\u63d0\u9ad8\u4e86\u58f0\u97f3\u9053\uff1a\u201c\u6765\u4eba\u554a!\u201d \u3000\u3000\u56db\u4e2a\u4eba\uff0c\u9c7c\u8d2f\u884c\u5165\u4e86\u5927\u5385\u3002 \u3000\u3000\u5f53\u5148\u4e00\u4eba\uff0c\u6b63\u662f\u795e\u5200\u5802\u4e3b\u96f7\u65e0\u5f71\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u662f\u5927\u603b\u7ba1\u90d1\u98de\u3002 \u3000\u3000\u7d27\u63a5\u7740\u662f\u72c2\u9f99\u3001\u98de\u9e70\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\uff0c\u8fd9\u5c31\u662f\u4f60\u7684\u5f85\u5ba2\u4e4b\u9053\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u662f\u5ba2\u4eba\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u7980\u544a\u5821\u4e3b\uff0c\u4e8c\u5341\u56db\u540d\u795e\u5200\u624b\uff0c \u5df2\u5728\u5927\u5385\u5916\u9762\u6212\u5907\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u90d1\u98de\u9053\uff1a\u201c\u7980\u544a\u5821\u4e3b\uff0c\u5168\u5821\u90fd\u5df2\u8fdb\u5165\u4e86\u6212\u5907\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u597d!\u732e\u4e0a\u4e38\u836f\u3002\u201d \u3000\u3000\u90d1\u98de\u9053\uff1a\u201c\u662f\uff01\u201d\u7531\u8896\u5185\u53d6\u51fa\u4e00\u4e2a\u7389\u74f6\uff0e\u653e\u5728\u684c\u5b50\u4e0a\u3002 \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u7389\u74f6\u662f\u4e24\u7c92\u5931\u5fc3\u4e5d\uff0c\u670d\u4e0b\u4e4b\u540e\uff0c\u5c31\u548c\u73b0\u5728\u7684\u5e7d\u5170\u4e00\u6837\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u4eec\u4f1a\u670d\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u670d\u4e0b\u5931\u5fc3\u4e39\uff0c \u53ea\u6709\u4e00\u6761\u8def\u8d70\uff0c\u90a3\u5c31\u662f\u2018\u6b7b\u2019!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5927\u4e08\u592b\u53ef\u6740\u4e0d\u53ef\u8fb1\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f55\u5bd2\u8863\uff0c\u522b\u592a\u62ac\u9ad8\u81ea\u5df1\uff0c\u6740\u4f60\u53ea\u4e0d\u8fc7\u662f\u4e3e\u624b\u4e4b\u52b3\uff0c\u8001\u592b\u5982\u6b64\u5b89\u6392\uff0c\u662f\u4e3a\u4e86\u5bf9\u4ed8\u5434\u53cc\u5973\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9\u4ed8\u6211?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5bf9!\u6211\u4e00\u76f4\u6ca1\u6709\u8f7b\u89c6\u8fc7\u4f60\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u5728\u4f60\u9ad8\u5821\u4e3b\u7684\u5fc3\u76ee\u4e2d\u6709\u8fd9\u6837\u91cd\u7684\u4efd\u91cf\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u5c31\u7b97\u9634\u9633\u5821\u4e2d\u7684\u4eba\uff0c\u5168\u90e8\u52a0\u4e0a\uff0c\u4e5f\u4e0d\u5982\u4f60\u5434\u53cc\u5973\u5728\u6211\u5fc3\u76ee\u4e2d\u7684\u4efd\u91cf\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u8fd9\u4e48\u91cd\u89c6\u6211\uff0c\u90a3\u662f\u975e\u8981\u6740\u6211\u4e0d\u53ef\u4e86?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u9664\u4e86\u6b7b\u4ea1\u4e4b\u5916\uff0c\u597d\u50cf\uff0c\u8fd8\u6709\u4e24\u4e2a\u53ef\u884c\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8bf7\u6559\u9ad8\u5821\u4e3b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u662f\u670d\u4e0b\u5931\u5fc3\u4e39\uff0c\u7b2c\u4e8c\u4e2a\u662f\u8bda\u5fc3\u8bda\u610f\u7684\u518d\u6295\u5165\u7b2c\u4e00\u5821\u6765\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u600e\u4e48\u6837\u624d\u7b97\u8bda\u5fc3\u8bda\u610f?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u76ee\u4e0b\u6709\u4e00\u4e2a\u6700\u597d\u7684\u8bc1\u660e\uff0c\u90a3\u5c31\u662f\u6740\u4e86\u4f55\u5bd2\u8863\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u8981\u6211\u6740\u81ea\u5df1\u7684\u4e08\u592b?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u4f60\u53f7\u79f0\u5929\u9b54\u5973\uff0c\u4ec0\u4e48\u4e8b\u505a\u4e0d\u51fa\u6765\uff0c\u4f60\u7b2c\u4e00\u4e2a\u7537\u4eba\uff0c\u4e0d\u4e5f\u662f\u88ab\u4f60\u6740\u4e86\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u7684\u8138\u8272\u53d8\u4e86\uff0c\u5a07\u8273\u7684\u53cc\u988a\u4e0a\uff0c\u6cdb\u8d77\u5b50\u4e00\u7247\u94c1\u9752\u4e4b\u8272\u3002 \u3000\u3000\u5979\u7684\u8eab\u8eaf\uff0c\u4e5f\u6709\u4e9b\u5fae\u5fae\u7684\u98a4\u6296\u3002 \u3000\u3000\u56de\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u4f55\u5bd2\u8863\u7684\u8138\u4e0a\uff0c\u4e00\u7247\u5e73\u9759\uff0c\u4e0d\u7981\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5bd2\u8863!\u4f60\u90fd\u542c\u5230\u4e86?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u6211\u542c\u5230\u4e86\u4ed6\u5728\u653e\u5c41!\u4ed6\u80fd\u5bf9\u81ea\u5df1\u4eb2\u751f\u5973\u513f\u4e0b\u6bd2\u624b\uff0c\u8fd9\u4e2a\u4eba\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e8b\u4e0d\u80fd\u505a\uff0c\u4ec0\u4e48\u8bdd\u8bf4\u4e0d\u51fa\u53e3\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9\u8fd9\u6837\u7684\u4eba\uff0c\u54b1\u4eec\u5c31\u4e0d\u5e94\u8be5\u751f\u6c14\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ed6\u7684\u7528\u5fc3\u5c31\u5728\u6fc0\u6012\u54b1\u4eec\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u4e00\u70b9\u4e5f\u4e0d\u751f\u6c14\u4e86\u3002\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u9ad8\u5929\u5065\u7684\u8eab\u4e0a\uff0c\u9053\uff1a \u201c\u9ad8\u5821\u4e3b\uff0c\u4f60\u81ea\u8be9\u4e3a\u4e2d\u5dde\u7b2c\u4e00\u5200\uff0c\u53ef\u60dc\uff0c\u6211\u4e0d\u592a\u76f8\u4fe1\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5434\u53cc\u5973\uff0c\u4f60\u60f3\u8bf4\u670d\u6211\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u548c\u6211\u4e00\u51b3\u6b7b\u6218\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u4f60\u662f\u767d\u8d39\u5fc3\u673a\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u4e0d\u6562?\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u6562\uff0c\u800c\u662f\u7528\u4e0d\u7740\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u672c\u6765\u662f\u80cc\u5bf9\u9ad8\u5929\u5065\u800c\u5750\uff0c\u6b64\u523b\uff0c\u7adf\u7136\u5fae\u5fae\u52a8\u4e00\u4e0b\uff0c\u4f7f\u5979\u7684\u5750\u52bf\u66f4\u4e3a\u6b63\u786e\u3002 \u3000\u3000\u5434\u53cc\u5973\u770b\u5230\u4e86\u5979\u53e3\u9f7f\u5fae\u5fae\u7684\u542f\u52a8\uff0c\u4f46\u5374\u53d1\u4e0d\u51fa\u4efb\u4f55\u58f0\u97f3\u3002 \u3000\u3000\u5e78\u597d\uff0c\u5979\u8bf4\u7684\u5f88\u7b80\u5355\u3002 \u3000\u3000\u53ea\u7528\u53e3\u52bf\u8fde\u7eed\u7684\u8868\u8fbe\uff0c\u5434\u53cc\u5973\u7ec8\u4e8e\u660e\u767d\u4e86\u5185\u60c5\u3002 \u3000\u3000\u90a3\u53e5\u8bdd\u662f\u8bf4\u201c\u62d6\u5ef6\u65f6\u95f4\u201d\u3002 \u3000\u3000\u5434\u53cc\u5973\u5f88\u8c28\u614e\uff0c\u5979\u660e\u767d\u4e86\uff0c\u4f46\u672a\u544a\u8bc9\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u6293\u4f4f\u4e86\u9ad8\u5e7d\u5170\u7684\u53cc\u624b\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\uff0c\u773c\u4e0b\u4e86\u5931\u5fc3\u4e39\uff0c\u662f\u4e0d\u662f\u5c31\u548c\u5e7d\u5170\u4e00\u6837\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u5979\u6b63\u5728\u836f\u529b\u53d1\u4f5c\u65f6\u523b\uff0c\u7b49\u836f\u529b\u884c\u904d\u4e86\u5168\u8eab\uff0c\u5b8c\u5168\u6539\u53d8\u4e86\u4e00\u4e2a\u4eba\u4ee5\u540e\uff0c\u5979\u5c31\u4f1a\u6062\u590d\u6b63\u5e38\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5509!\u6211\u770b\u5e7d\u5170\u5f88\u75db\u82e6\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u7684\u601d\u60f3\uff0c\u667a\u6167\uff0c\u9762\u4e34\u7740\u4e00\u79cd\u6539\u53d8\u65f6\uff0c\u603b\u96be\u514d\u4f1a\u5f88\u75db\u82e6\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u79cd\u75db\u82e6\uff0c\u4e0d\u8fc7\u662f\u4e24\u4e09\u5929\u7f62\u4e86\uff0c\u4e24\u4e09\u5929\u8fc7\u53bb\u4e4b\u540e\uff0c\u4e00\u5207\u90fd\u4f1a\u590d\u5143\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\u670d\u7528\u4e8e\u591a\u4e45?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4eca\u5929\u662f\u7b2c\u4e8c\u5929\uff0c\u8fc7\u4e86\u4eca\u591c\u5b50\u65f6\uff0c\u5979\u5c31\u5b8c\u5168\u590d\u5143\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u4fe1\uff0c\u4f60\u53ef\u4ee5\u770b\u7740\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5c31\u662f\u8981\u4ed6\u8bb2\u8fd9\u4e00\u53e5\u8bdd\uff0c\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u8fc7\u4e86\u5b50\u65f6\u4e4b\u540e\uff0c\u5979\u7684\u6b66\u529f\uff0c\u4e5f\u540c\u6837\u5931\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u4e0d\u4f1a\uff0c\u5979\u7684\u6b66\u529f\uff0c\u53ef\u80fd\u4f1a\u6bd4\u8fc7\u53bb\u66f4\u9ad8\u5f3a\u4e86\u4e00\u4e9b\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u53ea\u6709\u4eb2\u773c\u770b\u5230\u4e86\u624d\u4f1a\u76f8\u4fe1\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4e2b\u5934\uff0c\u4f60\u95ee\u4e86\u8fd9\u4e48\u591a\u7684\u8bdd\uff0c\u7528\u5fc3\u4f55\u5728?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u662f\u5728\u60f3\uff0c\u662f\u4e0d\u662f\u5e94\u8be5\u6709\u6240\u9009\u62e9?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5bf9!\u8bc6\u65f6\u52a1\u8005\u4e3a\u4fca\u6770\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f46\u6211\u4e5f\u6709\u6761\u4ef6\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u6211\u8fd8\u8981\u4f4f\u5728\u5f69\u8679\u697c\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u90a3\u672c\u6765\u5c31\u662f\u4f60\u7684\u4f4f\u5904\uff0c \u81ea\u7136\u5e94\u8be5\u4f60\u4f4f\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u7b2c\u4e8c\u4ef6\u4e8b\uff0c\u6211\u4eec\u4e09\u4e2a\u4eba\u8981\u4f4f\u5728\u4e00\u5904\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u53cc\u76ee\u76ef\u6ce8\u5728\u5434\u53cc\u5973\u7684\u8138\u4e0a\u77a7\u4e86\u4e00\u9635\uff0c\u9053\uff0c\u201c\u597d!\u8fd9\u4e2a\u8001\u592b\u4e5f\u7b54\u5e94\u4f60\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd8\u6709\u7b2c\u4e09\u4e2a\u6761\u4ef6\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u597d!\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u670d\u4e0b\u8fd9\u5931\u5fc3\u4e39\u4e4b\u540e\uff0c\u53ea\u5bf9\u4f60\u4e00\u4eba\u5fe0\u5b9e\uff0c\u4f46\u6211\u4eec\u7684\u667a\u6167\u5374\u906d\u53d7\u4e86\u8499\u853d\uff0c\u6211\u4eec\u8fd8\u8ba4\u4e0d\u8ba4\u8bc6\u81ea\u5df1\u7684\u4eb2\u4eba?\u201d \u3000\u3000\u9ad8\u5929\u5065\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5927\u6982\u8fd8\u8ba4\u8bc6\u5427\uff0c\u5c31\u50cf\u5e7d\u5170\u8fd8\u8ba4\u8bc6\u6211\uff0c\u4e5f\u8ba4\u8bc6\u4f60\u4e00\u6837\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u54ea\u4e00\u4e2a\u5730\u65b9\uff0c\u4f60\u89c9\u7740\u5b89\u5168\uff0c\u73b0\u5728\uff0c\u5c31\u628a\u6211\u4eec\u9001\u53bb\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u7528\u4e86\uff0c\u4f60\u548c\u5bd2\u8863\u670d\u4e0b\u5931\u5fc3\u4e39\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u8981\u770b\u770b\u5e7d\u5170\u836f\u529b\u6d88\u5931\u540e\u7684\u60c5\u5f62\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fc7\u4e86\u4eca\u591c\u5b50\u65f6\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6240\u4ee5\uff0e\u6211\u8bf7\u4f60\u9009\u4e00\u4e2a\u5b89\u5168\u7684\u5730\u65b9\uff0c\u6211\u4eec\u9003\u4e0d\u8d70\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u53ea\u6709\u795e\u5200\u5802\u7684\u4e2d\u95f4\u77f3\u7262\uff0c\u6700\u4e3a\u5b89\u5168\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u5c31\u628a\u6211\u4eec\u4e09\u4e2a\u4eba\u9001\u5165\u795e\u5200\u5802\u7684\u77f3\u7262\u53bb\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u6709\u4e9b\u610f\u5916\uff0c\u671b\u4e86\u96f7\u65e0\u5f71\u4e00\u773c\uff0c\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u77f3\u7262\u6709\u7a7a\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6709!\u4e09\u3001\u4e94\u53f7\u77f3\u7262\uff0c\u90fd\u5728\u7a7a\u7740\uff0c\u53ea\u662f\uff0c\u628a\u4ed6\u4eec\u4e09\u4e2a\u4eba\u5173\u5728\u4e00\u8d77\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u6015\u4ed6\u4eec\u7834\u7262\u800c\u51fa\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u90a3\u5012\u4e0d\u4f1a\uff0c\u77f3\u7262\u575a\u56fa\uff0c\u9632\u5b88\u68ee\u4e25\uff0e\u6b66\u529f\u518d\u9ad8\u7684\u4eba\uff0c\u4e5f\u4e0d\u53ef\u80fd\u4f1a\u8d8a\u7262\u800c\u51fa\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u90a3\u4f60\u62c5\u5fc3\u4ec0\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u628a\u8fd9\u4e09\u4e2a\u4eba\u5173\u5728\u4e00\u5904\uff0c\u6015\u4ed6\u4eec\u4f1a\u95f9\u51fa\u4ec0\u4e48\u82b1\u6837\u6765\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u82b1\u6837\u53ef\u95f9\u5462?\u4ed6\u4eec\u65e0\u6cd5\u89e3\u53bb\u5931\u5fc3\u4e39\u7684\u6bd2\u6027\uff0c\u4e5f\u65e0\u6cd5\u7834\u7262\u800c\u51fa\u2026\u2026\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5982\u82e5\u4ed6\u4eec\u6709\u4ec0\u4e48\u4e3e\u52a8\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u683c\u6740\u52ff\u8bba\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6709\u5821\u4e3b\u8fd9\u4e48\u4e00\u53e5\u8bdd\uff0c\u5c31\u591f\u4e86\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u5434\u53cc\u5973\uff0c\u63a5\u9053\uff1a\u201c\u5927\u5c0f\u59d0\uff0c\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u51b7\u7b11\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a \u201c\u4e0d\u7528\u5047\u60fa\u60fa\u4e86\uff0c\u6211\u73b0\u5728\u4e0d\u662f\u7b2c\u4e00\u5821\u7684\u5927\u5c0f\u59d0\uff0c\u800c\u662f\u7b2c\u4e00\u5821\u4e2d\u9636\u4e0b\u56da\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4f60\u65e2\u7136\u8fd9\u4e48\u8bf4\uff0c\u5728\u4e0b\u4e5f\u4e0d\u5ba2\u6c14\u4e86\uff0c\u5434\u59d1\u5a18\u80fd\u60f3\u51fa\u4e00\u4e2a\u4ec0\u4e48\u6837\u5b50\u7684\u529e\u6cd5\u80fd\u591f\u8ba9\u5728\u4e0b\u76f8\u4fe1\uff0c \u4f60\u80fd\u5f88\u5b89\u9759\u7684\u5230\u795e\u5200\u5802\u7684\u77f3\u7262\u4e2d\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5c31\u7b97\u6211\u80fd\u60f3\u51fa\u6765\uff0c \u53ea\u6015\u4f60\u96f7\u5802\u4e3b\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u4f55\u4e0d\u7531\u4f60\u8bf4\u4e2a\u529e\u6cd5?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u63a5\u9053\uff1a\u201c\u597d!\u90a3\u6211\u5c31\u4e0d\u5ba2\u6c14\u4e86\uff0c \u4f60\u53ea\u4e0d\u8fc7\u662f\u60f3\u8bc1\u660e\u4e00\u4e0b\uff0c\u4e8c\u5c0f\u59d0\u836f\u6027\u8fc7\u53bb\u4e4b\u540e\u7684\u60c5\u5f62\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u90a3\u548c\u6b66\u529f\u65e0\u5173\uff0c\u6240\u4ee5\uff0c\u4f60\u53ef\u5e9f\u53bb\u6b66\u529f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u8eab\u5982\u7535\u51fb\uff0c\u51e0\u4e4e\u8981\u66b4\u8df3\u800c\u8d77\u3002 \u3000\u3000\u671b\u671b\u8eab\u4fa7\u7684\u9ad8\u5e7d\u5170\u7adf\u53c8\u5fcd\u4e86\u4e0b\u53bb\uff0c\u6de1\u6de1\u4e00\u7b11\uff0e\u9053\uff1a \u201c\u8fd9\u4e2a\u529e\u6cd5\u81ea\u7136\u5f88\u597d\uff0c\u53ea\u662f\u96f7\u5802\u4e3b\u5fd8\u53bb\u4e86\u4e00\u4ef6\u5f88\u91cd\u8981\u7684\u4e8b\u2026\u2026\u201d \u3000\u3000\u96f7\u65e0\u5f71\u54e6\u4e86\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4ec0\u4e48\u91cd\u8981\u7684\u4e8b\uff0c\u5728\u4e0b\u5012\u662f\u60f3\u4e0d\u8d77\u6765\u4e86\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u6015\u6b7b\uff0c\u4f46\u66f4\u6015\u75db\u82e6\uff0c\u6240\u4ee5\uff0c\u6211\u8981\u8bc1\u660e\u670d\u4e0b\u5931\u5fc3\u4e39\u7684\u75db\u82e6\u6709\u591a\u4e45\uff0c\u5982\u82e5\u957f\u671f\u7684\u75db\u82e6\u548c\u6b7b\u4ea1\uff0c\u8981\u6211\u6765\u4f5c\u4e00\u4e2a\u9009\u62e9\uff0c\u6211\u5b81\u53ef\u9009\u62e9\u6b7b\u4ea1\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u59d1\u5a18\u7684\u610f\u601d\uff0c\u662f\u4e0d\u8bb8\u5e9f\u53bb\u6b66\u529f\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5982\u8981\u5e9f\u53bb\u6211\u7684\u6b66\u529f\uff0c\u5012\u4e0d\u5982\u62fc\u547d\u4e00\u6218\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u5c31\u7b97\u653e\u624b\u4e00\u6218\uff0c\u4e5f\u662f\u6ca1\u6709\u4ec0\u4e48\u673a\u4f1a\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1b\u201c\u6211\u7559\u4e0b\u6b66\u529f\u8fd8\u6709\u4e00\u4e2a\u4f5c\u7528\uff0c\u90a3\u5c31\u662f\u5728\u670d\u4e0b\u4e86\u5931\u5fc3\u4e39\u4e4b\u540e\uff0c\u6211\u8fd8\u8981\u4e3a\u7b2c\u4e00\u5821\u6548\u529b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6709\u9053\u7406\uff0c\u4e0d\u8fc7\uff0c\u59d1\u5a18\u603b\u4e0d\u80fd\u5c31\u8fd9\u6837\u8ddf\u5230\u77f3\u7262\u4e2d\u53bb\u5427!\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u7ed9\u5979\u52a0\u4e0a\u767e\u65a4\u91cd\u67b7\uff0c\u8fdb\u5165\u77f3\u7262\u4e4b\u540e\u518d\u628a\u94c1\u67b7\u53d6\u4e0b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5434\u59d1\u5a18\uff0c\u5821\u4e3b\u7684\u4ee4\u8c15\u59d1\u5a18\u542c\u5230\u4e86\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u542c\u5230\u4e86\uff0c\u4f60\u4eec\u53d6\u67b7\u6765\u7f62\u3002\u201d \u3000\u3000\u4e24\u53ea\u767e\u65a4\u94c1\u67b7\uff0c\u548c\u516b\u540d\u5200\u624b\uff0c\u5728\u96f7\u65e0\u5f71\u4eb2\u81ea\u76d1\u7763\u4e4b\u4e0b\uff0c\u4f55\u5bd2\u8863\u3001\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\u88ab\u9001\u5165\u795e\u5200\u5802\u4e94\u53f7\u77f3\u7262\u4e4b\u4e2d\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u4e5f\u5f88\u5b88\u4fe1\u8bfa\uff0c\u5728\u4ed6\u4eec\u8fdb\u5165\u77f3\u7262\u4e4b\u65f6\uff0c\u53d6\u4e0b\u4e86\u4f55\u5bd2\u8863\u548c\u5434\u53cc\u5973\u8eab\u4e0a\u7684\u767e\u65a4\u94c1\u67b7\uff0c\u5374\u7559\u4e0b\u4e24\u7c92\u5931\u5fc3\u4e39\u3002 \u3000\u3000\u77f3\u7262\u6df1\u5904\u5730\u4e0b\uff0c\u56db\u5468\u90fd\u662f\u575a\u786c\u7684\u9752\u77f3\uff0c\u800c\u4e14\uff0c\u8fd8\u5341\u5206\u7684\u5149\u6ed1\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5173\u4e0a\u4e86\u7262\u95e8\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u5434\u59d1\u5a18\uff0c\u4f60\u597d\u597d\u7684\u60f3\u60f3\uff0c\u96f7\u67d0\u5bf9\u4f60\u6709\u4e00\u4e2a\u5f88\u7f8e\u597d\u7684\u56de\u5fc6\uff0c\u8fd9\u5730\u65b9\u575a\u7262\u65e0\u6bd4\uff0c\u5c31\u7b97\u59d1\u5a18\u4f1a\u4e03\u5341\u4e8c\u53d8\uff0c\u4e5f\u65e0\u6cd5\u9003\u51fa\u3002\u201d \u3000\u3000\u7b11\u58f0\u9010\u6e10\u8fdc\u53bb\uff0c\u7ec8\u4e8e\u6d88\u5931\u4e0d\u95fb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u56de\u987e\u4e86\u4e00\u773c\uff0c\u624d\u53d1\u89c9\u8fd9\u4e2a\u5730\u65b9\u53ea\u4e0d\u8fc7\u4e00\u4e08\u65b9\u5706\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e00\u76b1\u7709\u5934\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u4f60\u53eb\u6211\u4eec\u5fcd\uff0c\u6211\u4eec\u5fcd\u4e0b\u6765\u4e86\uff0c\u73b0\u5728\u600e\u4e48\u529e\u3002\u201d \u9ad8\u5e7d\u5170\u7a81\u7136\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u59ca\uff0c\u7a0d\u5fae\u5fcd\u8010\u4e00\u4e9b\uff0c\u6211\u5168\u90e8\u7684\u529f\u529b\uff0c\u90fd\u7528\u6765\u548c\u6bd2\u6027\u6297\u62d2\uff0c\u597d\u597d\u4f11\u606f\uff0c\u8fc7\u4e86\u5b50\u591c\u518d\u8bf4\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u7a76\u7adf\u6709\u6ca1\u6709\u670d\u4e0b\u5931\u5fc3\u4e39?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u670d\u4e0b\u4e86\uff0c\u5982\u82e5\u6ca1\u6709\u670d\u4e0b\u5931\u5fc3\u4e39\uff0c\u600e\u4e48\u6210\u8fd9\u4e2a\u5c40\u9762?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u542c\u8bf4\u90a3\u5931\u5fc3\u4e39\u670d\u4e0b\u4e4b\u540e\uff0c\u4e00\u4e2a\u4eba\uff0c\u4f1a\u53d8\u6210\u75f4\u75f4\u5446\u5446\uff0c\u800c\u4e14\uff0c\u5168\u65e0\u89e3\u836f\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a \u201c\u73b0\u5728\u8ddd\u79bb\u5b50\u591c\uff0c\u4f3c\u662f\u5df2\u7ecf\u4e0d\u592a\u8fdc\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u591a\u5fcd\u8010\u4e00\u523b\u3002\u201d \u5434\u53cc\u5973\u53ea\u597d\u5fcd\u8010\u4e0b\u53bb\u3002 \u3000\u3000\u5b50\u591c\u4e34\u964d\u7684\u65f6\u5019\uff0c\u77f3\u5ba4\u4e2d\u4ecd\u7136\u662f\u90a3\u4e48\u9759\u6084\u6084\u7684\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6253\u91cf\u4e86\u77f3\u5ba4\u4e00\u773c\uff0c\u53d1\u89c9\u8fd9\u662f\u4e00\u4e2a\u5f88\u6076\u52a3\u7684\u5730\u65b9\u3002 \u3000\u3000\u5403\u3001\u7761\uff0c\u90fd\u5728\u8fd9\u5ea7\u5c0f\u77f3\u5ba4\u4e0d\u8bf4\uff0c\u800c\u4e14\uff0c\u6709\u4e00\u79cd\u5f88\u96be\u95fb\u7684\u6c14\u5473\u3002 \u3000\u3000\u90a3\u662f\u4e24\u4e2a\u5c0f\u5c0f\u7684\u6728\u6876\uff0c\u5f88\u96be\u95fb\u7684\u6c14\u5473\uff0c\u5c31\u7531\u90a3\u6728\u6876\u6563\u53d1\u51fa\u6765\u3002 \u3000\u3000\u90a3\u662f\u4fbf\u6eba\u7528\u7684\u6728\u6876\u3002 \u3000\u3000\u5434\u53cc\u5973\u7d20\u6709\u6d01\u7656\uff0c\u59d4\u5c48\u5728\u8fd9\u6837\u4e00\u95f4\u5c0f\u5ba4\u4e2d\uff0c\u771f\u6709\u7740\u5bf8\u9634\u5982\u5e74\u7684\u611f\u89c9\u3002 \u3000\u3000\u4e0d\u7ba1\u5149\u9634\u591a\u96be\u8fc7\uff0c\u4f46\u4e5f\u5fc5\u987b\u8981\u8fc7\u4e0b\u53bb\u3002 \u3000\u3000\u5b50\u65f6\u8fc7\u540e\uff0c\u9ad8\u5e7d\u5170\u7f13\u7f13\u7741\u5f00\u4e86\u53cc\u76ee\u3002 \u3000\u3000\u8fd9\u77f3\u7262\u6784\u9020\u5f97\u4e0d\u4f46\u5f88\u575a\u56fa\uff0c\u800c\u4e14\uff0c\u4e24\u4e2a\u58c1\u89d2\u5904\uff0c\u7559\u7740\u4e24\u4e2a\u5f88\u5c0f\u7684\u5b54\u6d1e\uff0c\u5b54\u6d1e\u5904\u6709\u706f\u5149\u900f\u5165\u8fdb\u6765\u3002 \u3000\u3000\u4e0d\u8fc7\u706f\u5149\u5f88\u5fae\u5f31\u3002 \u3000\u3000\u4f46\u4ee5\u4e09\u4eba\u7684\u529f\u529b\uff0c\u6709\u8fd9\u70b9\u5fae\u5f31\u7684\u706f\u5149\uff0c\u5c31\u53ef\u4ee5\u770b\u5f97\u5f88\u6e05\u695a\u4e86\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u56db\u987e\u4e00\u773c\uff0c\u9053\uff1a \u201c\u4f60\u4eec\u542c\u7740\uff0c\u6211\u670d\u98df\u7684\u5931\u5fc3\u4e39\u836f\u529b\u5f88\u5f3a\uff0c\u800c\u4e14\uff0c\u5931\u5fc3\u4e39\u4e5f\u786e\u6709\u8ff7\u5931\u5fc3\u5fd7\u7684\u529b\u91cf\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u7684\u5fc3\u5fd7\u662f\u5426\u5df2\u7ecf\u8ff7\u5931\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u4e00\u76f4\u5728\u548c\u836f\u529b\u640f\u6597\uff0c\u73b0\u5728\uff0c\u5371\u9669\u548c\u75db\u82e6\u5df2\u7ecf\u8fc7\u53bb\u4e86\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u96be\u9053\uff0c\u5931\u5fc3\u4e39\u7684\u836f\u529b\u4e5f\u53ef\u4ee5\u89e3\u53bb\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u8c08\u8bdd\u7684\u58f0\u97f3\u5c3d\u91cf\u7684\u5c0f\uff0c\u6211\u76f8\u4fe1\u4f1a\u4e00\u76f4\u6709\u4eba\u5728\u6697\u4e2d\u76d1\u89c6\u6211\u4eec\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u5982\u4f55\u89e3\u53bb\u4e86\u5931\u5fc3\u4e39\u7684\u836f\u6027\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u6211\u65e9\u5df2\u7ecf\u6709\u4e86\u51c6\u5907\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54e6!\u96be\u9053\u4f60\u5df2\u7ecf\u670d\u4e0b\u4e86\u89e3\u836f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u6211\u65e9\u670d\u4e0b\u89e3\u6bd2\u836f\u7269\uff0c\u867d\u7136\u836f\u4e0d\u5bf9\u8bc1\uff0c\u4f46\u90a3\u662f\u53ef\u89e3\u767e\u6bd2\u4e4b\u836f\uff0c\u963b\u6b62\u4e86\u5931\u5fc3\u4e39\u7684\u6bd2\u6027\uff0c\u6240\u4ee5\uff0c\u6211\u624d\u80fd\u8270\u82e6\u7684\u903c\u51fa\u5947\u6bd2\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u8eab\u4e2d\u4e4b\u6bd2\uff0c\u662f\u5426\u5df2\u7ecf\u5b8c\u5168\u7684\u89e3\u4e86?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5b8c\u5168\u590d\u5143\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u5e94\u8be5\u5982\u4f55\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u80fd\u9003\u51fa\u8fd9\u4e2a\u77f3\u7262\uff0c\u53ea\u6709\u4e00\u4e2a\u529e\u6cd5\uff0c\u5e0c\u671b\u4ed6\u4eec\u628a\u6211\u4eec\u5e26\u51fa\u53bb\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d!\u5e26\u51fa\u53bb\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bf9!\u4f60\u4eec\u670d\u4e0b\u4e86\u5931\u5fc3\u4e39\uff0c\u6211\u4eec\u5c31\u53ef\u4ee5\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e5f\u9884\u5148\u670d\u4e0b\u89e3\u836f\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d!\u90a3\u592a\u5192\u9669\uff0c\u4f60\u4eec\u5047\u88c5\u670d\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u96f7\u65e0\u5f71\u5f88\u7cbe\u660e\uff0c\u80fd\u591f\u9a97\u5f97\u8fc7\u4ed6\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u670d\u4e0b\u5931\u5fc3\u4e39\u7684\u4eba\uff0c\u6709\u4e00\u79cd\u5f88\u7279\u522b\u7684\u60c5\u51b5\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u7279\u522b\u60c5\u51b5?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u4e2d\u6bd2\u4e0d\u6df1\uff0c\u4e8b\u524d\u6709\u8ba1\u5212\u3001\u9884\u9632\uff0c\u6240\u4ee5\uff0c\u5bf9\u8fd9\u4e2a\u8fc7\u7a0b\uff0c\u6211\u6709\u8bb0\u5fc6\uff0c\u90a3\u662f\u5148\u54ed\u540e\u7b11\uff0c\u7136\u540e\uff0c\u518d\u8fdb\u5165\u75db\u82e6\u7684\u8fc7\u7a0b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5148\u54ed\u540e\u7b11\uff0c\u90a3\u4e0d\u662f\u75af\u5b50\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u662f\u75af\u5b50\uff0c\u4e00\u4e2a\u6b63\u5e38\u7684\u4eba\uff0c\u53d8\u5f97\u4e0d\u6b63\u5e38\uff0c\u5fc5\u987b\u5148\u8981\u7ecf\u8fc7\u4e00\u4e2a\u75af\u7684\u8fc7\u7a0b\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u4e9b\u75db\u82e6\uff0c\u53c8\u662f\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6ca1\u6709\u529e\u6cd5\u5f88\u5177\u4f53\u7684\u628a\u5b83\u8bf4\u51fa\u6765\uff0c\u5927\u4f53\u800c\u8a00\uff0c\u5934\u5f00\u59cb\u75bc\uff0c\u8bb0\u5fc6\u529b\u9010\u6e10\u7684\u6d88\u9000\uff0c\u5bf9\u8fc7\u53bb\u7559\u5728\u8111\u9645\u7684\u5370\u8c61\uff0c\u4e5f\u5f00\u59cb\u6a21\u7cca\uff0c\u4f46\u8fd8\u672a\u4f7f\u6211\u610f\u8bc6\u5168\u65e0\u3002\u201d \u3000\u3000\u53c8\u9053\uff1a\u201c\u6b64\u65f6\uff0c\u8336\u996d\u4e0d\u601d\uff0c\u4eba\u6e10\u9ebb\u6728\uff0c\u6211\u867d\u65e9\u6709\u51c6\u5907\uff0c\u4ea6\u51e0\u4e4e\u88ab\u836f\u529b\u6240\u4fb5\uff0c\u89c1\u5230\u5927\u59ca\u65f6\uff0c\u6211\u5df2\u7a0d\u611f\u5b89\u5b9a\u4e86\uff0c\u624d\u963b\u6b62\u4f60\u548c\u5821\u4e3b\u51b2\u7a81\u3002\u201d \u3000\u3000\u5979\u4e0d\u79f0\u7236\u4eb2\uff0c\u6539\u79f0\u5821\u4e3b\uff0c\u5df2\u53ef\u89c1\u5185\u5fc3\u7684\u8f6c\u53d8\u4e86\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1b \u201c\u5e7d\u5170\uff0c\u773c\u8fc7\u5931\u5fc3\u4e39\uff0c\u7ecf\u8fc7\u4e00\u6bb5\u75db\u82e6\u8fc7\u7a0b\u4e4b\u540e\uff0c\u4f1a\u662f\u4ec0\u4e48\u6837\u5b50?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u542c\u8bf4\u4e8b\u8fc7\u4e4b\u540e\uff0c\u4e00\u4e2a\u4eba\u8868\u9762\u4e0a\u53c8\u5b8c\u5168\u6062\u590d\u4e86\u6b63\u5e38\uff0c\u4e0d\u8fc7\uff0c\u90a3\u5df2\u662f\u4e00\u5177\u884c\u5c38\u8d70\u8089\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u600e\u4e48?\u8fde\u610f\u8bc6\u3001\u601d\u60f3\u4e5f\u6ca1\u6709\u4e86?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff0c\u201c\u5b8c\u5168\u6ca1\u6709\u4e86\uff0c \u5df2\u7ecf\u53d8\u6210\u4e86\u4e00\u4e2a\u5b8c\u5168\u6ca1\u6709\u611f\u89c9\u7684\u4eba\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u65f6\uff0c\u4ed6\u4f1a\u5bf9\u67d0\u4e00\u4e2a\u4eba\u7edd\u5bf9\u5fe0\u8bda\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\uff0c\u5c31\u662f\u9ad8\u5929\u5065\u9ad8\u5821\u4e3b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u6982\u662f\u4ed6\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e24\u4f4d\uff0c\u73b0\u5728\uff0c\u54b1\u4eec\u5e94\u8be5\u5982\u4f55?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6bc1\u53bb\u5931\u5fc3\u4e39\uff0c\u5047\u88c5\u4e2d\u6bd2\uff0c\u4ed6\u4eec\u81ea\u4f1a\u8fdb\u6765\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u627f\u53d7\u4e86\u836f\u529b\u53d1\u4f5c\u8fc7\u7a0b\u7684\u75db\u82e6\uff0c\u4f46\u836f\u6027\u8fc7\u540e\uff0c\u662f\u4ec0\u4e48\u6837\u5b50\uff0c\u6211\u4e5f\u4e0d\u6e05\u695a\u4e86\uff0c\u6211\u53ea\u80fd\u53bb\u731c\u60f3\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d!\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4eec\u53ea\u6709\u8bd5\u4e00\u8bd5\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e00\u5f00\u59cb\u4e4b\u540e\uff0c\u5c31\u8981\u9760\u4f60\u4eec\u81ea\u5df1\u5e94\u53d8\u4e86\uff0c\u8bb0\u7740\uff0c\u5bf9\u4f60\u6240\u4f5c\u6240\u4e3a\uff0c\u5168\u88ab\u836f\u529b\u63a7\u5236\uff0c\u54ed\u8981\u53d1\u81ea\u5185\u5fc3\uff0c\u54ed\u7684\u5f88\u60b2\u4f24\uff0c\u7b11\u4e5f\u53d1\u81ea\u5185\u5fc3\uff0c\u7b11\u7684\u5f88\u7545\u5feb\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u51e0\u65f6\u670d\u4e0b\u8fd9\u5931\u5fc3\u4e39\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u73b0\u5728\uff0c\u8981\u88c5\u51fa\u670d\u7528\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d!\u6211\u770b\u5e94\u8be5\u5148\u544a\u8bc9\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6253\u5f00\u74f6\u76d6\uff0c\u5012\u51fa\u4e86\u4e00\u7c92\u5931\u5fc3\u4e39\uff0c\u770b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u53cc\u5973\uff0c\u54b1\u4eec\u8981\u5f53\u7740\u4ed6\u4eec\u7684\u9762\uff0c\u628a\u5931\u5fc3\u4e39\u670d\u4e0b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u55ef!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u6211\u8eab\u4e0a\u6709\u4e00\u7c92\u4e39\u836f\uff0c\u5f62\u6001\u9887\u4f3c\u5931\u5fc3\u4e39\uff0c\u6709\u6b62\u75bc\u7597\u4f24\u4e4b\u6548\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5feb\u4e9b\u6362\u8fc7\uff0c\u88c5\u5165\u74f6\u4e2d\uff0c\u5f53\u4ed6\u4e4b\u9762\u628a\u74f6\u76d6\u6253\u5f00\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u4e00\u76f4\u4f4e\u5934\u800c\u5750\uff0c\u58f0\u97f3\u5f88\u4f4e\uff0c\u4e5f\u53ea\u4f7f\u4e24\u4e2a\u4eba\u521a\u521a\u542c\u5230\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u7684\u4e00\u5207\u4e3e\u52a8\u90fd\u5f88\u5c0f\u5fc3\u3002 \u3000\u3000\u4e00\u5207\u51c6\u5907\u59a5\u5f53\u4e86\uff0c\u5434\u53cc\u5973\u5ffd\u7136\u504e\u5165\u4e86\u4f55\u5bd2\u8863\u7684\u6000\u4e2d\uff0c\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u4f60\u51b3\u5b9a\u4e86?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e24\u53e5\u8bdd\uff0c\u867d\u7136\u4e5f\u8bf4\u7684\u58f0\u97f3\u5f88\u4f4e\uff0c\u4f46\u662f\u77f3\u5ba4\u56de\u97f3\uff0c\u8db3\u53ef\u628a\u58f0\u97f3\u4f20\u9001\u51fa\u53bb\u3002 \u3000\u3000\u5982\u82e5\u6697\u4e2d\u6709\u7aa5\u4f3a\u4e4b\u4eba\uff0c\u4ed6\u4eec\u4f1a\u542c\u5f97\u5341\u5206\u6e05\u695a\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u5982\u82e5\u670d\u4e0b\u4e86\u8fd9\u79cd\u836f\u7269\uff0c\u54b1\u4eec\u90fd\u53d8\u6210\u50bb\u74dc\uff0c\u4f60\u662f\u4e0d\u662f\u8fd8\u8ba4\u8bc6\u6211\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8981\u662f\u90a3\u6837\uff0c\u6211\u4eec\u5c82\u4e0d\u662f\u53d8\u5f97\u66f4\u75db\u82e6\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u96f7\u65e0\u5f71\u7684\u58f0\u97f3\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a \u201c\u4e0d\u4f1a\u7684\uff0c\u90a3\u65f6\uff0c\u4f60\u4eec\u5df2\u7ecf\u4e0d\u77e5\u9053\u4ec0\u4e48\u662f\u75db\u82e6\u4e86\u3002 \u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u662f\u96f7\u65e0\u5f71\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5927\u5c0f\u59d0\u6709\u4ec0\u4e48\u5429\u5490\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u8981\u548c\u4e49\u7236\u8c08\u8c08\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u548c\u6211\u8c08\u4e5f\u662f\u4e00\u6837\uff0c\u5821\u4e3b\u5df2\u7ecf\u4ea4\u4ee3\u4e86\u5728\u4e0b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5821\u4e3b\u6388\u7ed9\u591a\u5927\u7684\u6743\u529b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u53ef\u4ee5\u63a7\u5236\u4f60\u4eec\u7684\u751f\u6b7b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8fd9\u5ea7\u77f3\u7262\uff0c\u5c31\u662f\u6211\u4eec\u57cb\u9aa8\u4e4b\u5730\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u90a3\u8981\u770b\u4f60\u59d1\u5a18\u600e\u4e48\u5904\u7406\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u8bf4\u6e05\u695a\u2014\u4e9b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u8981\u4eb2\u81ea\u770b\u7740\u4f60\u4eec\u670d\u4e0b\u5931\u5fc3\u4e39\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u6211\u8981\u7b97\u7b97\u770b\uff0c\u6211\u4eec\u56f0\u6b7b\u6b64\u5730\uff0c\u548c\u670d\u4e0b\u5931\u5fc3\u4e39\u90a3\u4e00\u79cd\u5212\u7b97\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u81ea\u7136\u662f\u773c\u4e0b\u5931\u5fc3\u4e39\u5212\u7b97\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u6211\u4e0d\u6015\u6b7b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u597d\u6b7b\u4e0d\u5982\u8d56\u6d3b\u7740\uff0c\u4f55\u51b5\uff0c\u50cf\u4f60\u8fd9\u7b49\u5929\u59ff\u56fd\u8272\u7684\u4eba\uff0c\u6b7b\u4e86\u5b9e\u5728\u53ef\u60dc\u7684\u5f88\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a \u201c\u6211\u4eec\u770b\u5230\u4e86\u7ed3\u679c\uff0c\u9ad8\u5e7d\u5170\uff0c\u73b0\u5728\u5df2\u6ca1\u6709\u4ec0\u4e48\u75db\u82e6\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u5979\u5728\u5b89\u5b89\u9759\u9759\u7684\u5750\u7740\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u539f\u6765\uff0c\u4f60\u4e5f\u770b\u5f97\u5230\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5979\u5df2\u7ecf\u7ed9\u4e86\u4f60\u4eec\u5f88\u597d\u8bc1\u660e\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4f46\u5979\u597d\u50cf\u5df2\u7ecf\u53d8\u6210\u4e86\u54d1\u5df4!\u4e00\u53e5\u8bdd\u4e5f\u4e0d\u4f1a\u8bf4\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5979\u670d\u7528\u7684\u836f\u529b\uff0c \u5df2\u7ecf\u8fc7\u53bb\uff0c\u5f88\u5feb\u5c31\u53ef\u4ee5\u6062\u590d\u4e86\uff0c\u4f60\u4f1a\u770b\u5230\u5979\u6709\u8bf4\u6709\u7b11\u7684\u60c5\u5f62\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u771f\u7684\u8981\u903c\u6211\u670d\u4e0b\u5931\u5fc3\u4e39\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5343\u771f\u4e07\u786e\uff0c\u9664\u4e86\u4f60\u670d\u4e0b\u5931\u5fc3\u4e39\u4e4b\u5916\uff0c \u5df2\u7ecf\u522b\u65e0\u4ed6\u9014\u53ef\u5faa\u4e86\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u5509!\u60f3\u4e0d\u5230\u6211\u5434\u53cc\u5973\u7adf\u7136\u683d\u5728\u4f60\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u5c0f\u59d0\uff0c\u8fd9\u4e0d\u8fc7\u662f\u8fdf\u65e9\u7684\u4e8b\uff0c\u5c31\u7b97\u4e0d\u53d1\u751f\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u4e5f\u662f\u4f1a\u683d\u5012\u5728\u6211\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u73b0\u5728\uff0c\u544a\u8bc9\u4f60\u4e5f\u4e0d\u6015\u4f60\u5927\u5c0f\u59d0\u751f\u6c14\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u5427?\u73b0\u5728\uff0c\u6211\u60f3\u6ca1\u6709\u4fdd\u5bc6\u7684\u5fc5\u8981\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\uff0c\u53ea\u6015\u4f60\u5927\u5c0f\u59d0\u8fd8\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5c0f\u59b9\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e00\u5e74\u524d\uff0c\u5821\u4e3b\u5df2\u7ecf\u7b54\u5e94\u4e86\u6211\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u628a\u4f60\u5ac1\u7ed9\u6211\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u53ea\u89c9\u5fc3\u4e2d\u4e00\u80a1\u6012\u706b\uff0c\u5192\u4e86\u8d77\u6765\uff0c\u4f46\u5374\u5fcd\u4e0b\u6ca1\u6709\u53d1\u4f5c\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u600e\u4e48\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u5821\u4e3b\u7b54\u5e94\u4e86\uff0c\u4f46\u5374\u4e0d\u8bb8\u6211\u8bf4\u51fa\u53bb\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u6015\u6211\u4e0d\u540c\u610f?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5bf9!\u6015\u4f60\u4e0d\u540c\u610f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u5374\u903c\u6211\u670d\u4e0b\u5931\u5fc3\u4e39\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u62b1\u6b49\u4e07\u5206\uff0c\u4f60\u5fc5\u9700\u591a\u591a\u539f\u8c05\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u6211\u670d\u4e0b\u4e86\u5931\u5fc3\u4e39\u4e4b\u540e\uff0c\u4f1a\u53d8\u6210\u4e00\u4e2a\u5446\u5b50\uff0c\u611f\u60c5\u9ebb\u6728\uff0c\u4e0d\u89e3\u98ce\u60c5\uff0c\u4f60\u5e0c\u671b\u8981\u90a3\u4e48\u4e00\u4e2a\u4eba\uff0c\u662f\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u4e86\uff0c\u4e0d\u4f1a\u518d\u8bf4\u4e00\u904d\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u5176\u5b9e\uff0c\u6211\u5df2\u7ecf\u542c\u5f97\u5f88\u6e05\u695a\u4e86\uff0c\u4e0d\u8fc7\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\uff0c\u6211\u4f1a\u5728\u9002\u5f53\u7684\u65f6\u673a\u4e2d\uff0c\u8ba9\u4f60\u6062\u590d\u795e\u667a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u53ef\u80fd\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u6709\u89e3\u836f\uff0c\u968f\u65f6\u53ef\u4ee5\u4f7f\u4f60\u795e\u5fd7\u6e05\u660e\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u6743\u529b\u64cd\u4e4b\u5728\u6211\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u662f\u8bf4\uff0c\u4e0d\u7ba1\u5982\u4f55\uff0c\u6211\u8981\u670d\u4e0b\u5931\u5fc3\u4e39\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5bf9!\u4ee5\u540e\uff0c\u8981\u770b\u4f60\u7684\u8868\u73b0\uff0c\u80fd\u4e0d\u80fd\u6062\u590d\u795e\u5fd7\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u6211\u670d\u4e0b\u5931\u5fc3\u4e39\u540e\uff0c\u5df2\u7ecf\u5b8c\u5168\u5931\u53bb\u4e86\u81ea\u6211\uff0c\u6211\u4f5c\u4e9b\u4ec0\u4e48\u4e8b\uff0c\u600e\u4f1a\u77e5\u9053?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1b\u201c\u8fd9\u4e0d\u7528\u4f60\u62c5\u5fc3\uff0c\u5feb\u4e9b\u670d\u4e0b\u5931\u5fc3\u4e39\u5427\uff01\u201d \u3000\u3000\u5434\u53cc\u5973\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u5427!\u4f55\u90ce\uff0c\u6211\u5bf9\u4e0d\u4f4f\u4f60\u4e86\u3002\u201d \u3000\u3000\u6253\u5f00\u74f6\u76d6\uff0c\u5012\u51fa\u4e00\u7c92\u4e39\u4e38\uff0c\u7528\u7ea4\u5de7\u7684\u98df\u4e2d\u4e8c\u6307\uff0c\u8f7b\u8f7b\u634f\u4e86\u8d77\u6765\uff0c\u653e\u5165\u53e3\u4e2d\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8981\u6b7b\uff0c\u5927\u5bb6\u6b7b\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u4e5f\u628a\u5931\u5fc3\u4e39\u541e\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u7b49\u4e00\u4f1a\u513f\uff0c\u6211\u4f1a\u63a5\u4f60\u4eec\u51fa\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1b \u201c\u4e0d\u8981\u51fa\u58f0\uff0c\u6211\u8981\u4f60\u4eec\u54ed\u7684\u65f6\u5019\uff0c\u5c31\u5f00\u59cb\u54ed\u3002\u201d \u5927\u7ea6\u8fc7\u4e00\u987f\u996d\u5de5\u592b\u4e4b\u540e\uff0c\u9ad8\u5e7d\u5170\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5f00\u59cb\u54ed\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u3001\u5434\u53cc\u5973\u653e\u58f0\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\uff0c\u54ed\u5f97\u5f88\u5389\u5bb3\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\uff0c\u4e00\u4e2a\u795e\u5fd7\u6e05\u660e\u7684\u4eba\uff0c\u8981\u653e\u58f0\u5927\u54ed\uff0c\u5b9e\u5728\u4e5f\u4e0d\u662f\u4e00\u4ef6\u5f88\u5bb9\u6613\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u4e24\u4e2a\u5374\u7528\u51fa\u5168\u529b\u54ed\uff0c\u7a7a\u5ba4\u56de\u97f3\uff0c\u54ed\u5f97\u54c0\u54c0\u6b32\u7edd\u3002 \u3000\u3000\u4e0d\u77e5\u9053\u54ed\u4e86\u591a\u4e45\uff0c\u4e24\u4e2a\u4eba\u7684\u55d3\u5b50\uff0c\u4e5f\u54ed\u54d1\u4e86\uff0c\u800c\u4e14\uff0c\u4e5f\u54ed\u51fa\u4e86\u6ee1\u8eab\u5927\u6c57\u3002 \u3000\u3000\u6700\u96be\u5fcd\u53d7\u7684\u662f\u90a3\u7a7a\u5ba4\u56de\u97f3\uff0c\u9707\u5f97\u8033\u6735\u55e1\u55e1\u4f5c\u54cd\u3002 \u3000\u3000\u5e78\u597d\uff0c\u9ad8\u5e7d\u5170\u8bf4\u8bdd\u4e86\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u505c\u6b62\u5427\uff01\u201d \u3000\u3000\u4e24\u4e2a\u4eba\uff0c\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4e00\u9635\u6c89\u5bc2\u8fc7\u540e\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5f00\u59cb\u7b11\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u53c8\u5f00\u59cb\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u7b11\u5f97\u58f0\u9707\u77f3\u5ba4\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e00\u987f\u996d\u5de5\u5730\u4e4b\u540e\uff0c\u4e24\u4e2a\u4eba\u5df2\u5feb\u65e0\u6cd5\u518d\u7b11\u4e0b\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u53c8\u4f4e\u58f0\u9053\uff1a\u201c\u505c\u4e0b\u6765\u3002\u201d \u3000\u3000\u4e24\u4e2a\u4eba\u505c\u4f4f\u7b11\u58f0\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u7531\u73b0\u5728\u5f00\u59cb\uff0c\u4e24\u4f4d\u8981\u53d8\u5f97\u75f4\u75f4\u5446\u5446\uff0c\u4e0d\u8981\u8bf4\u8bdd\u3002\u201d \u4e8b\u5b9e\u4e0a\uff0c\u4e24\u4e2a\u4e5f\u5b9e\u5728\u6ca1\u6709\u529b\u6c14\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u8981\u7b49\u4e0b\u53bb\uff0c\u7b49\u5019\u53d8\u5316\uff01\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54b1\u4eec\u7b49\u4e0b\u53bb\uff0c\u8981\u4e0d\u8981\u5403\u4e1c\u897f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u5f88\u997f\uff0c\u4f46\u5fc5\u987b\u5fcd\u4e0b\u53bb\uff0c\u73b0\u5728\uff0c\u8bf8\u4f4d\u8981\u8fd0\u6c14\u8c03\u606f\uff0c\u4f7f\u529f\u529b\u590d\u5143\uff0c\u4e5f\u8bb8\uff0c\u5728\u54b1\u4eec\u79bb\u5f00\u4e4b\u540e\uff0c\u5c31\u4f1a\u6709\u4e00\u573a\u6076\u6218\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u4f4e\u58f0\u9053\uff1a\u201c\u51fa\u95e8\u5c31\u6253\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5982\u82e5\u88ab\u4ed6\u4eec\u77a7\u4e86\u51fa\u6765\uff0c\u90a3\u53ea\u6709\u7acb\u523b\u52a8\u624b\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ed6\u4eec\u5982\u662f\u77a7\u4e0d\u51fa\u6765\uff0c\u6211\u4eec\u4f1a\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u5b50\u7684\u7ed3\u679c?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4eec\u4f1a\u88ab\u9001\u5165\u4e00\u4e2a\u5730\u65b9\uff0c\u90a3\u4e2a\u5730\u65b9\uff0c\u5f88\u53ef\u80fd\u90fd\u662f\u548c\u6211\u4eec\u4e00\u6837\u7684\u4eba!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90fd\u662f\u670d\u7528\u8fc7\u5931\u5fc3\u4e39\u7684\u4eba?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u6982\u662f\u5427!\u4e0d\u8fc7\uff0c\u8fd9\u4e2d\u95f4\uff0c\u6709\u4e00\u4e2a\u5f88\u91cd\u8981\u7684\u95ee\u9898\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u95ee\u9898?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\uff0c\u90fd\u662f\u7b2c\u4e00\u5821\u7684\u5fe0\u5fc3\u6740\u624b\uff0c\u7edd\u4e0d\u4f1a\u751f\u51fa\u53db\u79bb\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u7a77\u82e6\u95f4\uff0c\u4e00\u8fde\u4e32\u811a\u6b65\u58f0\uff0c\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u6709\u4eba\u6765\u4e86\uff0c\u4e24\u4f4d\u522b\u5fd8\u4e86\uff0c\u4f60\u4eec\u662f\u670d\u7528\u4e86\u5931\u5fc3\u4e39\u7684\u4eba\uff0c\u73b0\u5728\uff0c\u6b63\u5728\u4e00\u79cd\u6fc0\u70c8\u3001\u832b\u7136\u7684\u75db\u82e6\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u7d27\u63a5\u7740\uff0c\u4e00\u9635\u8f67\u8f67\u4e4b\u58f0\uff0c\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u77f3\u5ba4\u7684\u95e8\u6237\uff0c\u7a81\u7136\u5927\u5f00\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5f53\u5148\u51fa\u73b0\u3002 \u3000\u3000\u4ed6\u8eab\u540e\u7d27\u968f\u7740\u4e00\u4e2a\u9ed1\u8863\u4eba\uff0c\u624b\u4e2d\u6267\u7740\u4e00\u652f\u706b\u628a\u3002 \u3000\u3000\u706b\u628a\u9ad8\u4e3e\uff0c\u7167\u5f97\u4e00\u5ba4\u901a\u660e\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\u3002\u201d \u3000\u3000\u58f0\u97f3\u5f88\u5927\uff0c\u6ee1\u5ba4\u56de\u97f3\uff0c\u7ed5\u5f97\u8033\u9645\u4e0d\u7edd\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9759\u9759\u7684\u5750\u7740\uff0c\u6ca1\u6709\u62ac\u5934\u3002 \u3000\u3000\u5434\u53cc\u5973\u3001\u4f55\u5bd2\u8863\uff0c\u4e5f\u5446\u5446\u7684\u5750\u7740\uff0c\u5bf9\u90a3\u547c\u559d\u4e4b\u58f0\uff0c\u5145\u8033\u4e0d\u95fb\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u8fd9\u5931\u5fc3\u4e39\u5f53\u771f\u5c31\u5982\u6b64\u7684\u5389\u5bb3\u5417?\u201d \u3000\u3000\u5ffd\u7136\u62cd\u624b\u4e00\u638c\uff0c\u62ec\u5411\u5434\u53cc\u5973\u7684\u8138\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u6253\u5f97\u5f88\u91cd\uff0c\u5434\u53cc\u5973\u5750\u7740\u672a\u52a8\uff0c\u88ab\u4e00\u638c\u6253\u5f97\u7ffb\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u7c89\u988a\u4e0a\uff0c\u6cdb\u73b0\u51fa\u4e94\u4e2a\u6e05\u6670\u7684\u6307\u75d5\u3002 \u3000\u3000\u5434\u53cc\u5973\u6614\u5e74\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u53f7\u79f0\u5929\u9b54\u5973\uff0c\u813e\u6c14\u66b4\u8e81\uff0c\u51fa\u624b\u5c31\u8981\u6740\u4eba\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u5979\u7adf\u7136\u5fcd\u4e86\u4e0b\u53bb\uff0c \u3000\u3000\u96f7\u65e0\u5f71\u5168\u795e\u6212\u5907\u7740\uff0c\u51b7\u51b7\u5730\u770b\u7740\u5434\u53cc\u5973\u3002 \u3000\u3000\u4ed6\u7edd\u5bf9\u4e0d\u76f8\u4fe1\uff0c\u5434\u53cc\u5973\u80fd\u5fcd\u53d7\u8fd9\u4e00\u8bb0\u8033\u5149\uff0c\u9664\u975e\u5730\u670d\u7528\u4e86\u5931\u5fc3\u4e39\u3002 \u3000\u3000\u5434\u53cc\u5973\u7f13\u7f13\u7684\u5750\u4e86\u8d77\u6765\uff0c\u671b\u4e86\u96f7\u65e0\u5f71\u4e00\u773c\u3002 \u3000\u3000\u5979\u7684\u76ee\u5149\u6ca1\u6709\u4ec7\u6068\uff0c\u4e5f\u6ca1\u6709\u53d7\u618e\uff0c\u8138\u4e0a\u7684\u795e\u60c5\uff0c\u4e5f\u53ea\u662f\u4e00\u7247\u75f4\u5446\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u53cc\u5973\uff0c\u6211\u4e00\u5b9a\u8981\u8bc1\u660e\u4f60\u662f\u4e0d\u662f\u771f\u7684\u670d\u4e0b\u4e86\u5931\u5fc3\u4e39\u3002\u201d \u3000\u3000\u56de\u987e\u95e8\u53e3\uff0c\u63a5\u9053\uff1a\u201c\u6765\u4eba\u554a!\u628a\u4ed6\u4eec\u5e26\u51fa\u53bb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u3001\u9ad8\u5e7d\u5170\uff0c\u88ab\u5e26\u5165\u4e86\u53e6\u4e00\u4e2a\u5730\u4e0b\u5927\u5385\u3002 \u3000\u3000\u90a3\u91cc\u5206\u6210\u4e86\u5f88\u591a\u4e2a\u5c0f\u623f\u95f4\uff0c\u4f55\u5bd2\u8863\u3001\u9ad8\u5e7d\u5170\u88ab\u9001\u5165\u4e86\u4e24\u4e2a\u4e0d\u540c\u7684\u623f\u4e2d\u3002 \u3000\u3000\u91cc\u9762\u5e03\u7f6e\u5f88\u7b80\u5355\uff0c\u9664\u4e86\u4e00\u69bb\u3001\u4e00\u684c\u3001\u4e00\u5f20\u6728\u6905\u4e4b\u5916\uff0c\u518d\u65e0\u522b\u7684\u4e1c\u897f\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u8d70\u7684\u6162\u4e86\u4e00\u4e9b\uff0c\u770b\u5230\u4e86\u9ad8\u5e7d\u5170\u88ab\u9001\u5230\u4e86\u9694\u58c1\u4e00\u95f4\u5c0f\u623f\u5b50\u4e2d\u3002 \u3000\u3000\u8fd9\u5927\u5385\u4e2d\u7684\u5c0f\u623f\u95f4\uff0c\u6709\u5982\u8702\u5de2\u4e00\u822c\uff0c\u4e00\u773c\u95f4\uff0c\u4e5f\u770b\u4e0d\u6e05\u695a\u6709\u591a\u5c11\u95f4\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4f55\u5bd2\u8863\u62c5\u5fc3\u7684\u4e00\u4ef6\u4e8b\uff0c\u662f\u5434\u53cc\u5973\u7684\u4e0b\u843d\uff0c\u4ed6\u4eec\u88ab\u5206\u5f00\u4e86\uff0c\u5434\u53cc\u5973\u4e0d\u77e5\u9053\u88ab\u9001\u5230\u4f55\u5904? \u3000\u3000\u5434\u53cc\u5973\u88ab\u9001\u5230\u4e86\u4e00\u95f4\u7f8e\u4e3d\u7684\u623f\u95f4\u4e2d\u3002 \u3000\u3000\u90a3\u91cc\u9526\u5e10\u7ee3\u88ab\u3001\u5986\u53f0\u3001\u94dc\u955c\uff0c\u8bbe\u5907\u7684\u8c6a\u534e\uff0c\u4e0d\u8f93\u5f69\u8679\u697c\u3002 \u3000\u3000\u5434\u53cc\u5973\u88ab\u4e00\u4e2a\u9ed1\u8863\u4eba\u9001\u4e86\u8fdb\u53bb\uff0c\u90a3\u4eba\u8f7b\u8f7b\u5e26\u4e0a\u4e86\u623f\u95e8\u800c\u53bb\u3002 \u3000\u3000\u5434\u53cc\u5973\u611f\u89c9\u5230\u81ea\u5df1\u5904\u5883\u7684\u5371\u9669\u3002 \u3000\u3000\u5979\u5df2\u660e\u767d\u4e86\u96f7\u65e0\u5f71\u7684\u7528\u5fc3\uff0c\u4f46\u5374\u60f3\u4e0d\u5230\uff0c\u4e8b\u60c5\u4f1a\u5982\u6b64\u4e4b\u5feb\u53d1\u751f\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u53ef\u80fd\u4f1a\u968f\u65f6\u51b2\u5165\u623f\u95f4\uff0c\u6240\u4ee5\uff0c\u5979\u5fc5\u987b\u6709\u6240\u51b3\u5b9a\uff0c\u5982\u4f55\u6765\u5e94\u4ed8\u8fd9\u4e00\u573a\u6b8b\u9177\u7684\u4e8b\u5b9e\u3002 \u3000\u3000\u5979\u5ff5\u5934\u8fd8\u672a\u8f6c\uff0c\u96f7\u65e0\u5f71\u5df3\u5e26\u7740\u6ee1\u8138\u7b11\u5bb9\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5434\u53cc\u5973\u6ca1\u6709\u5fd8\u8bb0\u81ea\u5df1\u662f\u670d\u8fc7\u5931\u5fc3\u4e39\u7684\u4eba\uff0c\u7acb\u523b\u4e00\u5438\u6c14\uff0c\u53d8\u5f97\u5341\u5206\u75f4\u5446\u3002 \u3000\u3000\u4ee5\u96f7\u65e0\u5f71\u9605\u5386\u7684\u4e30\u5bcc\uff0c\u8fd9\u4e3e\u6b62\uff0c\u5e94\u8be5\u53ef\u4ee5\u770b\u51fa\u6765\u3002 \u3000\u3000\u4f46\u96f7\u65e0\u5f71\u5374\u6ca1\u6709\u53d1\u89c9\uff0c\u4e5f\u8bb8\u662f\u5174\u594b\uff0c\u4f7f\u4ed6\u6709\u4e9b\u5ffd\u7565\u3002 \u3000\u3000\u76f4\u5230\u96f7\u65e0\u5f71\u8d70\u5230\u4e86\u5434\u53cc\u5973\u5750\u7684\u6728\u69bb\u524d\u9762\uff0c\u5434\u53cc\u5973\u624d\u611f\u89c9\u7740\u4e00\u80a1\u5f3a\u70c8\u7684\u6251\u9f3b\u9152\u6c14\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\uff0c\u8eab\u4e0a\u53d1\u51fa\u90a3\u6837\u5f3a\u70c8\u7684\u9152\u6c14\uff0c\u81f3\u5c11\u5e94\u8be5\u6709\u4e03\u5206\u9152\u610f\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u501f\u9152\u58ee\u80c6\uff0c\u6709\u4e9b\u4eba\u501f\u9152\u88c5\u75af\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u7a81\u7136\u4f38\u51fa\u4e24\u53ea\u624b\uff0c\u6367\u8d77\u4e86\u5434\u53cc\u5973\u7684\u8138\u513f\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u6211\u8fd9\u4e48\u5feb\u5c31\u80fd\u591f\u5f97\u507f\u4e86\u5fc3\u613f\u3002\u201d \u3000\u3000\u6765\u5f97\u592a\u5feb\u4e86\uff0c\u5434\u53cc\u5973\u8fd8\u6ca1\u6709\u51b3\u5b9a\u8be5\u600e\u4e48\u505a\uff0c\u4e5f\u6ca1\u6709\u5fd8\u8bb0\u81ea\u5df1\u662f\u670d\u4e0b\u6bd2\u836f\u7684\u4eba\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u7684\u52a8\u4f5c\uff0c\u5c31\u50cf\u95ea\u7535\u4e00\u822c\uff0c\u4e00\u5f20\u5927\u5634\uff0c\u4eb2\u4e0a\u5434\u53cc\u5973\u7684\u6a31\u5507\u3002 \u3000\u3000\u5434\u53cc\u5973\u5fc3\u4e2d\u597d\u706b\uff0c\u4e5f\u6709\u4e9b\u513f\u7f9e\u6025\uff0c\u4f46\u5979\u5fcd\u4e0b\u4e86\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u573a\u5192\u9669\uff0c\u4e0d\u4f46\u5173\u7cfb\u7740\u4e2a\u4eba\u7684\u751f\u6b7b\uff0c\u4e5f\u5173\u7cfb\u7740\u9ad8\u5e7d\u5170\u548c\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u5e78\u597d\uff0c\u5979\u662f\u4e2a\u5446\u5b50\uff0c\u5bf9\u90a3\u706b\u822c\u7684\u70ed\u60c5\uff0c\u53ef\u4ee5\u4e0d\u4f5c\u4efb\u4f55\u53cd\u5e94\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u773c\u524d\u662f\u6d3b\u751f\u751f\u7684\u5434\u53cc\u5973\uff0c\u662f\u96f7\u65e0\u5f71\u68a6\u5bd0\u4ee5\u6c42\u7684\u4eba\uff0c\u4f46\u53ef\u60dc\uff0c\u5979\u7adf\u4f3c\u6728\u96d5\u77f3\u523b\u4e00\u6837\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u653e\u5f00\u4e86\u5434\u53cc\u5973\uff0c\u77aa\u7740\u4e00\u53cc\u6ee1\u662f\u8840\u4e1d\u7684\u773c\u775b\uff0c\u5446\u5446\u7684\u770b\u7740\u5434\u53cc\u5973\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u53ef\u77e5\u9053\uff0c \u4f60\u5c31\u53d8\u6210\u6211\u96f7\u65e0\u5f71\u7684\u4eba\u4e86\uff0c\u4f60\u53ef\u77e5\u9053\uff0c\u6211\u5bf9\u4f60\u7684\u601d\u5ff5\uff0c\u662f\u591a\u4e48\u7684\u6df1\u523b\uff0c \u4f60\u53ef\u77e5\u9053\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u662f\u600e\u4e48\u8fc7\u7684\uff0c\u6211\u7528\u4e86\u591a\u5927\u7684\u514b\u5236\u529b\uff0c\u538b\u5236\u4e0b\u53bb\u4e86\u6cdb\u6ee5\u7684\u60c5\u6b32\u2026\u2026\u201d \u3000\u3000\u4ed6\u81ea\u8bf4\u81ea\u8bdd\uff0c\u9762\u5bf9\u7740\u5434\u53cc\u5973\u8868\u767d\u51fa\u5185\u5fc3\u7684\u60c5\u610f\uff0c\u8574\u85cf\u7684\u75db\u82e6\u3002 \u3000\u3000\u5434\u53cc\u5973\u5fc3\u4e2d\u597d\u82e6\uff0c\u5979\u542c\u5f97\u5f88\u6e05\u695a\uff0c\u4f46\u5374\u4e0d\u80fd\u56de\u7b54\uff0c\u751a\u81f3\u4e5f\u4e0d\u80fd\u8868\u8fbe\u4e00\u70b9\u5185\u5fc3\u4e2d\u7684\u53cd\u5e94\u3002 \u3000\u3000\u5979\u597d\u62c5\u5fc3\uff0c\u62c5\u5fc3\u96f7\u65e0\u5f71\u518d\u6709\u4ec0\u4e48\u66b4\u70c8\u7684\u4e3e\u52a8\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u5979\u8fd8\u6ca1\u6709\u51b3\u5b9a\u5982\u4f55\u5e94\u4ed8\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u5e78\u597d\uff0c\u96f7\u65e0\u5f71\u6ca1\u6709\u518d\u8fdb\u4e00\u6b65\u7684\u4e3e\u52a8\u3002 \u3000\u3000\u4ed6\u6709\u7740\u5f88\u6d53\u70c8\u7684\u9152\u610f\uff0c\u4f46\u8fd8\u6ca1\u6709\u9189\u5230\u7406\u667a\u5168\u5931\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u884c\u5230\u4e86\u4e00\u5f20\u6728\u6905\u4e0a\u5750\u4e86\u4e0b\u6765\uff0c\u95ed\u4e0a\u53cc\u76ee\u3002 \u3000\u3000\u5434\u53cc\u5973\u65e0\u6cd5\u80af\u5b9a\u4ed6\u662f\u95ed\u76ee\u517b\u606f\uff0c\u8fd8\u662f\u5df2\u6c89\u6c89\u7684\u7761\u53bb\u3002 \u3000\u3000\u5982\u82e5\u4ed6\u662f\u7761\u7740\u4e86\uff0c\u8fd9\u5e94\u8be5\u662f\u4e00\u4e2a\u5f88\u597d\u7684\u673a\u4f1a\u3002 \u3000\u3000\u6b64\u523b\u5982\u7a81\u7136\u51fa\u624b\uff0c\u5236\u4f4f\u96f7\u65e0\u5f71\u5e76\u975e\u96be\u4e8b\u3002 \u3000\u3000\u5434\u53cc\u5973\u6709\u7740\u8839\u8822\u6b32\u52a8\u7684\u60f3\u6cd5\u3002 \u3000\u3000\u4f46\u8fd9\u65f6\u523b\uff0c\u7d27\u95ed\u7740\u7684\u6728\u95e8\uff0c\u5374\u54cd\u8d77\u4e86\u2014\u9635\u5265\u5544\u4e4b\u58f0\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5ffd\u7136\u5750\u76f4\u4e86\u8eab\u5b50\uff0c\u7741\u5f00\u773c\u775b\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u201c\u662f\u6211\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u8fdb\u6765\u3002\u201d \u3000\u3000\u4e00\u4e2a\u5168\u8eab\u52b2\u88c5\uff0c\u9e70\u9f3b\u3001\u9e5e\u773c\uff0c\u4e09\u5341\u56db\u4e94\u7684\u9ed1\u8863\u52b2\u88c5\u5927\u6c49\uff0c\u63a8\u95e8\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u6562\u60c5\uff0c\u4e24\u6247\u95e8\u5e76\u672a\u52a0\u6813\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u6709\u4e9b\u6124\u6012\uff0c\u4e24\u9053\u76ee\u5149\u4e2d\uff0c\u53ef\u4ee5\u770b\u5f97\u51fa\u6765\uff0c\u51b7\u5389\u7684\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u5c11\u5821\u4e3b\u6765\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u7684\u9152\u610f\u4f3c\u662f\u5ffd\u7136\u95f4\uff0c\u6d88\u9000\u4e86\u4e00\u534a\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u5c11\u5821\u4e3b\u6765\u4e86?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u662f!\u800c\u4e14\uff0c\u4eba\u5df2\u7ecf\u8fdb\u4e86\u795e\u5200\u5802\u3002\u201d \u3000\u3000\u6709\u4e9b\u4e0d\u6ee1\u4e5f\u6709\u4e9b\u60ca\u5947\uff0c\u96f7\u65e0\u5f71\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4ed6\u6765\u4f5c\u4ec0\u4e48?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4ed6\u8bf4\uff0c\u6709\u8981\u4e8b\uff0c\u8bf7\u89c1\u5802\u4e3b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u597d!\u6211\u5728\u5927\u5385\u4e2d\u89c1\u4ed6\u3002\u201d \u3000\u3000\u53ea\u542c\u6717\u6717\u7b11\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u6211\u5df2\u5230\u96c5\u5ba4\u95e8\u5916\u3002\u201d \u3000\u3000\u5440\u7136\u4e00\u58f0\uff0c\u6728\u95e8\u88ab\u63a8\u5f00\uff0c\u9ad8\u98de\u8679\u8eab\u7740\u9752\u886b\uff0c\u7f13\u6b65\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4ed6\u66fe\u88ab\u9ec4\u7075\u903c\u7740\u541e\u670d\u6df7\u5408\u4e4b\u6bd2\uff0c\u4f46\u9ad8\u5929\u5065\u4ecd\u7136\u80fd\u628a\u4ed6\u7684\u6bd2\u6027\u89e3\u53bb\u3002 \u3000\u3000\u7b2c\u4e00\u5821\u4e2d\u85cf\u9f99\u5367\u864e\uff0c\u5b9e\u5728\u6709\u4e0d\u5c11\u9ad8\u660e\u4eba\u7269\u3002 \u3000\u3000\u8fd9\u4f4d\u9ad8\u5927\u5c11\u7237\uff0c\u5728\u7b2c\u4e00\u5821\u4e2d\uff0c\u8fde\u7236\u4eb2\u4e5f\u4e0d\u592a\u6015\uff0c\u6700\u6015\u7684\u4e24\u4e2a\u4eba\uff0c\u90fd\u662f\u4ed6\u7684\u59b9\u59b9\uff0c\u4e00\u4e2a\u662f\u4e49\u59b9\u5434\u53cc\u5973\uff0c\u4e00\u4e2a\u662f\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5fc3\u4e2d\u60f3\u5455\uff0c\u5455\u5f97\u9152\u610f\u9192\u4e86\u4e0d\u5c11\uff0c\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u9ad8\u516c\u5b50\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u90a3\u6728\u69bb\u4e0a\u5750\u7684\u53ef\u662f\u5434\u53cc\u5973?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u9519\u5440!\u6b63\u662f\u4ee4\u59b9\u2026\u2026\u201d \u3000\u3000\u9ad8\u98de\u8679\u63a5\u9053\uff1a \u201c\u73b0\u5728\uff0c\u5979\u5df2\u7ecf\u4e0d\u662f\u6211\u7684\u59b9\u59b9\u4e86\u3002\u5bb6\u7236\u5df2\u548c\u5979\u5207\u65ad\u4e86\u4e49\u7236\u5973\u7684\u60c5\u610f\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u5f53\u5e74\u5bb6\u7236\u628a\u5979\u5f15\u5165\u54b1\u4eec\u7b2c\u4e00\u5821\u4e2d\u7684\u7528\u5fc3\uff0c\u96f7\u5802\u4e3b\u53ef\u66fe\u77e5\u6653?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u5979\u672c\u662f\u5bb6\u7236\u4e3a\u6211\u9009\u7684\u5ab3\u5987\u2026\u2026\u201d \u3000\u3000\u96f7\u65e0\u5f71\u63a5\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u53d8\u6210\u4e86\u5927\u5c0f\u59d0\uff0c\u6210\u4e86\u4f60\u7684\u59b9\u59b9?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a \u201c\u90fd\u602a\u5e7d\u5170\uff0c\u8bf4\u6211\u6709\u5f88\u591a\u7684\u7f3a\u70b9\uff0c\u6240\u4ee5\uff0c\u5979\u5c31\u51b3\u5fc3\u4e0d\u5ac1\u7ed9\u6211\u4e86\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u88ab\u9ad8\u5821\u4e3b\u6536\u4f5c\u4e49\u5973\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u5927\u6982\u8fd9\u4e48\u56de\u4e8b\u5427!\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c11\u5821\u4e3b\uff0c\u5c31\u53ea\u6709\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u6211\u5417?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u8fd9\u662f\u524d\u56e0\uff0c\u8fd8\u6709\u540e\u679c\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5728\u4e0b\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u4e00\u6307\u5434\u53cc\u5973\uff0c\u9053\uff1a\u201c\u6211\u8981\u628a\u5979\u5e26\u8d70\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u628a\u5979\u5e26\u8d70?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u5bf9!\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c11\u5821\u4e3b\u53ef\u77e5\u9053\uff0c\u5979\u5df2\u7ecf\u670d\u7528\u8fc7\u5931\u5fc3\u4e39\u4e86\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f46\u5931\u5fc3\u4e39\u6709\u836f\u53ef\u89e3\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u4ee4\u5c0a\u7684\u4ee4\u8c15\uff1f\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u5c31\u7b97\u6211\u7239\u77e5\u9053\u4e86\uff0c \u4ed6\u4e5f\u4e0d\u4f1a\u53cd\u5bf9\uff0c\u56e0\u4e3a\uff0c\u4ed6\u4e00\u76f4\u60f3\u8981\u8ba8\u5979\u4f5c\u4e3a\u513f\u5ab3\u5987\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c11\u5821\u4e3b\u53ef\u77e5\u9053\uff0c\u4ee4\u5c0a\u7b54\u5e94\u8fc7\u6211\u4e00\u4ef6\u4e8b\uff01\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u628a\u9ad8\u5927\u5c0f\u59d0\u5434\u53cc\u5973\uff0c\u9001\u7ed9\u5728\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u7b11\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4e0d\u77e5\u9053\uff0c\u4e0d\u8fc7\u4f60\u96f7\u5802\u4e3b\u4e00\u5927\u628a\u5e74\u7eaa\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8981\u5434\u53cc\u5973\u8fd9\u6837\u5e74\u8f7b\u7684\u59d1\u5a18\u5462?\u518d\u8bf4\uff0c\u7b2c\u4e00\u5821\u4e2d\u7f8e\u5973\u5f88\u591a\uff0c\u4f60\u53c8\u4f55\u5fc5\u4e00\u5b9a\u8981\u5979\u5462?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u4e2d\u7684\u7f8e\u5973\u867d\u7136\u4e0d\u5c11\uff0c\u4f46\u88ab\u6211\u96f7\u67d0\u4eba\u77a7\u4e0a\u7684\u53ea\u6709\u5979\u4e00\u4e2a\u4e86\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u7b11\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u8fd8\u6709\u4e00\u4e2a\u4eba\u7684\u7f8e\u8c8c\uff0c\u4e0d\u5728\u5979\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u5413\u4e86\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u5c11\u5821\u4e3b\uff0c\u4f60\u8bf4\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u6211\u8bf4\u9ad8\u5e7d\u5170\u554a!\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\uff0c\u4f60\u7684\u4eb2\u59b9\u59b9\uff0c\u9ad8\u5821\u4e3b\u7684\u7231\u5973\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u7b11\u9053\uff1a\u201c\u6211\u7239\u5982\u82e5\u8fd8\u5f88\u7231\u5979\uff0c\u600e\u4f1a\u7ed9\u5979\u670d\u4e0b\u5931\u5fc3\u4e39\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u5c11\u5821\u4e3b\uff0c\u6b63\u5982\u4f60\u6240\u8bf4\uff0c\u5931\u5fc3\u4e39\u6709\u836f\u53ef\u89e3\uff0c\u5821\u4e3b\u7ed9\u4e8c\u5c0f\u59d0\u670d\u4e0b\u5931\u5fc3\u4e39\uff0c\u53ea\u4e0d\u8fc7\u60f3\u4f7f\u5979\u53d7\u70b9\u75db\u82e6\uff0c\u8fc7\u51e0\u5929\uff0c\u8fd8\u4e0d\u662f\u9700\u8981\u628a\u5979\u6551\u51fa\u6765\uff0c\u5c82\u4f1a\u771f\u8981\u5979\uff0c\u6ca6\u4f5c\u65e0\u77e5\u6740\u624b\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\u7684\u4e8b\uff0c\u6211\u7239\u4e0d\u4f1a\u518d\u6551\u5979\u4e86\uff0c\u9664\u975e\u80fd\u4f7f\u5979\u5fd8\u4e86\u8fc7\u53bb\u2026\u2026\u201d \u3000\u3000\u6307\u6307\u5434\u53cc\u5973\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\uff0c\u6211\u8981\u5e26\u8d70\uff0c\u4f60\u6562\u4e0d\u6562\u8981\u9ad8\u5e7d\u5170\uff0c\u90a3\u662f\u4f60\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u56de\u987e\u4e86\u5434\u53cc\u5973\u4e00\u773c\uff0c\u9053\uff1a \u201c\u5c11\u5821\u4e3b\u4e00\u5b9a\u60f3\u8981\u5434\u53cc\u5973\uff0c\u53ea\u6709\u4e00\u4e2a\u529e\u6cd5\u3002\u201d \u9ad8\u98de\u8679\u9053\uff1a \u201c\u6211\u5df2\u7ecf\u4e3a\u5979\u68a6\u9b42\u8426\u7ed5\u591a\u5e74\uff0c\u4e00\u5b9a\u8981\u5f97\u5230\u624b\uff0c\u4f60\u8bf4\u4ec0\u4e48\u529e\u6cd5?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u53ea\u8981\u4ee4\u5c0a\u4e0b\u4e00\u9053\u4ee4\u8c15\uff0c\u5c11\u5821\u4e3b\u5c31\u53ef\u4ee5\u5e26\u8d70\u5434\u53cc\u5973\u4e86\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u96f7\u53d4\u53d4\uff0c\u600e\u4e48\u548c\u6211\u5982\u6b64\u8ba4\u771f\u5462?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u8ba4\u771f\uff0c\u800c\u662f\u975e\u5982\u6b64\u4e0d\u53ef\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u7b11\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u6211\u76f8\u4fe1\u7239\u7edd\u4e0d\u4f1a\u53cd\u5bf9\uff0c\u53ea\u8981\u4f60\u628a\u5434\u53cc\u5973\u4ea4\u7ed9\u6211\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u884c\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u6211\u5df2\u7ecf\u5f88\u7ed9\u4f60\u9762\u5b50\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c11\u5821\u4e3b\uff0c\u4ee4\u5c0a\u5df2\u628a\u5434\u53cc\u5973\u8d50\u7ed9\u6211\u4e86\uff0c\u6211\u4e0d\u4fe1\u4ed6\u4f1a\u51fa\u5c14\u53cd\u5c14\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u6211\u59b9\u5e7d\u5170\uff0c\u5bb9\u8272\u7edd\u4e0d\u5728\u5434\u53cc\u5973\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u90a3\u662f\u4f60\u7684\u4eb2\u59b9\u59b9\uff0c\u5821\u4e3b\u7684\u771f\u6b63\u5973\u513f\uff0c\u6211\u96f7\u65e0\u5f71\uff0c\u8fd8\u6ca1\u6709\u8fd9\u4e2a\u80c6\u5b50\u5f00\u53e3\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u6211\u7239\u80fd\u8ba9\u5e7d\u5170\u670d\u4e0b\u5931\u5fc3\u4e39\uff0c\u90a3\u8bf4\u660e\uff0c\u65e9\u5df2\u5b8c\u5168\u548c\u5979\u65ad\u4e86\u60c5\u610f\uff0c\u53ea\u8981\u4f60\u80af\u8ba9\u51fa\u5434\u53cc\u5973\uff0c\u96f7\u5802\u4e3b\u7684\u4e8b\uff0c\u5305\u5728\u6211\u8eab\u4e0a\uff0c\u6211\u53bb\u5bf9\u7239\u8bf4\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u5728\u4e0b\u5f88\u77e5\u8db3\uff0c\u4e0d\u6562\u5b58\u975e\u5206\u4e4b\u60f3\uff0c\u5c11\u5821\u4e3b\u8bf7\u56de\u5427\u3002\u201d \u9ad8\u98de\u8679\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u4e0d\u77e5\u4f60\u60f3\u5230\u4e86\u6ca1\u6709\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u9601\u4e0b\u8bf7\u8bf4?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u4f7f\u5979\u6c38\u8fdc\u4e0d\u6e05\u9192\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u55ef!\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4e00\u65e6\u5979\u795e\u5fd7\u6e05\u660e\u8fc7\u6765\uff0c\u4f60\u5982\u6b64\u5bf9\u5979\uff0c\u4e0d\u6015\u5979\u62a5\u590d\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c11\u5821\u4e3b\u96be\u9053\u4e0d\u6015\u5417?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u5728\u4e0b\u81ea\u6709\u5999\u8ba1\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u533a\u533a\u4e5f\u6709\u826f\u7b56\uff0c\u4e0d\u52b3\u5c11\u5821\u4e3b\u8d39\u5fc3\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7684\u795e\u5fd7\u5f88\u6e05\u9192\uff0c\u628a\u4e24\u4eba\u7684\u4ea4\u8c08\uff0c\u5b57\u5b57\u542c\u5165\u8033\u4e2d\u3002 \u3000\u3000\u8868\u9762\u4e0a\u5979\u5782\u9996\u800c\u5750\uff0c\u795e\u60c5\u6728\u7136\uff0c\u5185\u5fc3\u4e2d\u5374\u662f\u5343\u56de\u767e\u8f6c\uff0c\u4e00\u76f4\u5728\u60f3\u529e\u6cd5\uff0c\u5982\u4f55\u5e94\u4ed8\u8fd9\u4e2a\u5c40\u9762\u3002 \u3000\u3000\u5979\u8fd8\u672a\u60f3\u597d\u529e\u6cd5\uff0c\u6240\u4ee5\uff0c\u5f88\u5e0c\u671b\u4ed6\u4eec\u518d\u7ea0\u7f20\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u96f7\u65e0\u5f71\u5341\u5206\u51b3\u7edd\uff0c\u5927\u6709\u4e0d\u60dc\u7ffb\u8138\u4e4b\u52bf\u3002 \u3000\u3000\u9ad8\u98de\u8679\u867d\u7136\u662f\u5c11\u5821\u4e3b\u7684\u8eab\u4efd\uff0c\u4f46\u4ed6\u6b66\u529f\u4e0a\u7684\u6210\u5c31\uff0c\u5374\u662f\u4e0d\u582a\u96f7\u65e0\u5f71\u51fa\u624b\u4e00\u51fb\uff0c\u7edd\u4e0d\u81f4\u548c\u96f7\u65e0\u5f71\u5f53\u9762\u7ffb\u8138\u3002 \u3000\u3000\u5434\u53cc\u5973\u6697\u5fd6\u76ee\u524d\u7684\u5c40\u52bf\uff0c\u9ad8\u98de\u8679\u53ea\u8981\u4e00\u9000\u8d70\uff0c\u96f7\u65e0\u5f71\u5c31\u53ef\u80fd\u6709\u975e\u5206\u7684\u4e3e\u52a8\u3002 \u3000\u3000\u9664\u975e\u7518\u5fc3\u5fcd\u53d7\u96f7\u65e0\u5f71\u52a0\u8bf8\u7684\u8e42\u8e8f\u4e4b\u5916\uff0c\u90a3\u53ea\u6709\u51fa\u624b\u4e00\u6218\u3002 \u3000\u3000\u76ee\u524d\uff0c\u867d\u5df2\u8eab\u5728\u77f3\u7262\u4e4b\u5916\uff0c\u4f46\u4f55\u5bd2\u8863\u3001\u9ad8\u5e7d\u5170\u7684\u4e0b\u843d\u4e0d\u660e\uff0c\u4e00\u51fa\u624b\uff0c\u5fc5\u7136\u4f1a\u628a\u4e24\u4eba\u4e5f\u62d6\u5165\u4e86\u6f29\u6da1\u4e4b\u4e2d\uff0c\u4e24\u4eba\u4e0d\u77e5\u79d8\u5bc6\u5df2\u6cc4\uff0c\u53ef\u80fd\u4f1a\u906d\u6697\u7b97\u3002 \u3000\u3000\u524d\u601d\u540e\u60f3\uff0c\u4e00\u76f4\u60f3\u4e0d\u51fa\u4e00\u4e2a\u5b8c\u7f8e\u4e4b\u7b56\u3002 \u3000\u3000\u53ea\u542c\u9ad8\u98de\u8679\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u4f55\u4e0d\u518d\u4ed4\u7ec6\u60f3\u60f3\uff0c\u5728\u4e0b\u4e4b\u610f\uff0c\u5b9e\u662f\u4e24\u5168\u5176\u7f8e\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u7528\u60f3\u4e86\uff0c\u5c11\u5821\u4e3b\u8bf7\u5427\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u51b7\u51b7\u7684\u77aa\u4e86\u96f7\u65e0\u5f71\u4e00\u773c\uff0c\u9053\uff1a \u201c\u4e00\u5b9a\u8981\u6211\u7239\u4f20\u4e0b\u4ee4\u8c15\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u9519!\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u597d!\u4f60\u7b49\u7740\uff0c\u6211\u53bb\u544a\u8bc9\u6211\u7236\u4eb2\uff0c\u4e0d\u8fc7\uff0c\u5728\u4e0b\u8981\u548c\u96f7\u5802\u4e3b\u5148\u6765\u4e2a\u541b\u5b50\u534f\u5b9a\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ec0\u4e48\u534f\u5b9a?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u6211\u7239\u672a\u4e0b\u4ee4\u8c15\u4e4b\u524d\uff0c\u96f7\u5e38\u4e3b\u4e0d\u8bb8\u5bf9\u5434\u53cc\u5973\u6709\u4ec0\u4e48\u65e0\u793c\u4e3e\u52a8\u3002\u201d \u3000\u3000\u8fd9\u6b63\u597d\u63ed\u7a7f\u4e86\u96f7\u65e0\u5f71\u7684\u5fc3\u610f\uff0c\u4e0d\u7981\u8138\u8272\u5927\u53d8\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c11\u5821\u4e3b\uff0c\u53bb\u627e\u4ee4\u5c0a\u5427! \u3000\u3000\u5821\u4e3b\u4ee4\u8c15\u6765\u5230\u4e4b\u524d\uff0c\u6211\u4f1a\u505a\u4e9b\u4ec0\u4e48\u4e8b\uff0c\u90a3\u662f\u6211\u7684\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u5fc3\u4e2d\u660e\u767d\uff0c\u518d\u8c08\u4e0b\u53bb\uff0c\u53ef\u80fd\u81ea\u8ba8\u6ca1\u8da3\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u6211\u7acb\u523b\u53bb\u627e\u6211\u7239\u3002\u201d \u3000\u3000\u8f6c\u8fc7\u8eab\u5b50\uff0c\u75be\u5954\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u9ad8\u98de\u8679\u7684\u80cc\u5f71\uff0c\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u7a81\u7136\u8f6c\u8eab\u63a9\u4e0a\u4e86\u623f\u95e8\u3002 \u3000\u3000\u5434\u53cc\u5973\u6697\u6697\u5438\u4e00\u53e3\u6c14\uff0c\u7eb3\u5165\u4e39\u7530\uff0c\u5fd6\u9053\uff1a\u770b\u6765\uff0c\u53ea\u6709\u4e0d\u60dc\u6311\u660e\uff0c\u52a8\u624b\u4e00\u6218\u4e86\u3002 \u3000\u3000\u5979\u628a\u5934\u5782\u5f97\u66f4\u4f4e\u4e00\u4e9b\uff0c\u5934\u4e0a\u7684\u957f\u53d1\u6563\u62ab\u4e86\u4e0b\u6765\uff0c\u63a9\u906e\u53bb\u4e86\u8138\u4e0a\u7684\u795e\u8272\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4e0d\u8bba\u662f\u96f7\u65e0\u5f71\uff0c\u6216\u662f\u9ad8\u98de\u8679\uff0c\u53ea\u8981\u4ed6\u4eec\u7559\u5fc3\u4e00\u4e9b\uff0c\u90fd\u4f1a\u770b\u51fa\u4e00\u4e9b\u53ef\u7591\u4e4b\u5904\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u90fd\u6ca1\u6709\u7559\u5fc3\u3002 \u3000\u3000\u4ed6\u4eec\u5bf9\u5931\u5fc3\u4e39\u7684\u6bd2\u6027\uff0c\u6709\u7740\u5f3a\u70c8\u7684\u4fe1\u5fc3\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u4f38\u624b\u6293\u4f4f\u4e86\u5434\u53cc\u5973\u3002 \u3000\u3000\u5434\u53cc\u5973\u53ea\u62ac\u5934\u671b\u4e86\u96f7\u65e0\u5f71\u4e00\u773c\uff0c\u5e76\u65e0\u6323\u52a8\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u660e\u767d\uff0c\u5f88\u5feb\u7684\uff0c\u96f7\u65e0\u5f71\u4f1a\u6709\u8fdb\u4e00\u6b65\u7684\u4e3e\u52a8\uff0c\u5979\u8981\u9009\u62e9\u6700\u6709\u5229\u7684\u65f6\u673a\uff0c\u4f5c\u81f4\u547d\u7684\u4e00\u51fb\u3002 \u3000\u3000\u5728\u6781\u7aef\u4e3a\u96be\u7684\u60c5\u5f62\u4e0b\uff0c\u5434\u53cc\u5973\u7ec8\u4e8e\u6697\u81ea\u4f5c\u4e86\u4e00\u4e2a\u51b3\u5b9a\u3002 \u3000\u3000\u6740\u6b7b\u96f7\u65e0\u5f71\uff0c\u4e00\u638c\u6bd9\u654c\uff0c\u7136\u540e\uff0c\u6362\u4e0a\u4ed6\u7684\u8863\u670d\uff0c\u518d\u53bb\u5bfb\u627e\u4f55\u5bd2\u8863\u548c\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u7a81\u7136\u4f4e\u4e0b\u5934\u53bb\uff0c\u4e00\u5f20\u5927\u5634\uff0c\u543b\u4e0a\u4e86\u5434\u53cc\u5973\u7684\u6a31\u5507\u3002 \u3000\u3000\u5434\u53cc\u5973\u6ca1\u6709\u8eb2\u907f\uff0c\u4e5f\u65e0\u6cd5\u8eb2\u907f\u3002 \u3000\u3000\u5979\u4e00\u76f4\u8bb0\u7740\u9ad8\u5e7d\u5170\u7684\u8bdd\uff0c\u670d\u8fc7\u5931\u5fc3\u4e39\u7684\u4eba\uff0c\u662f\u4e00\u4e2a\u767d\u75f4\u3002 \u3000\u3000\u767d\u75f4\u4e0d\u4f1a\u61c2\u4eb2\u543b\uff0c\u4e5f\u4e0d\u4f1a\u9003\u907f\u522b\u4eba\u7684\u4eb2\u543b\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u5434\u53cc\u5973\u6ca1\u6709\u9003\u907f\uff0c\u53ea\u6709\u7d27\u7d27\u7684\u95ed\u7740\u6a31\u5507\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5fc3\u6155\u5434\u53cc\u5973\u7684\u7f8e\u4e3d\u65e9\u5df2\u68a6\u9b42\u8426\u7ed5\uff0c\u6b64\u523b\u80fd\u5f97\u4e00\u543b\uff0c\u81ea\u662f\u5927\u5927\u7684\u4e00\u6170\u76f8\u601d\u4e4b\u82e6\u3002 \u3000\u3000\u53ea\u53ef\u60dc\uff0c\u4f73\u4eba\u670d\u836f\uff0c\u6709\u5982\u6728\u96d5\u77f3\u523b\u4e00\u822c\uff0c\u6ca1\u6709\u4e00\u70b9\u53cd\u5e94\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u81ea\u5df1\u8fc7\u4e86\u4e00\u9635\u5e72\u763e\u4e4b\u540e\uff0c\u624d\u7f13\u7f13\u7ad9\u6b63\u4e86\u8eab\u5b50\u3002 \u3000\u3000\u5979\u867d\u7136\u5f62\u6001\u75f4\u5446\uff0c\u4f46\u8eab\u4e0a\u5e7d\u9999\u4ecd\u5b58\uff0c\u808c\u80a4\u5149\u6ed1\uff0c\u6709\u5982\u7f8a\u8102\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u8d2a\u5a6a\u7684\u53c8\u5728\u5434\u53cc\u5973\u7684\u988a\u4e0a\u4eb2\u4e86\u4e00\u4e0b\uff0c\u6293\u8d77\u4e86\u5434\u53cc\u5973\u7684\u53f3\u624b\u3002 \u3000\u3000\u53ea\u89c1\u4e94\u6307\u7ea4\u957f\uff0c\u67d4\u82e5\u65e0\u9aa8\uff0c\u987f\u751f\u7231\u4e0d\u5fcd\u91ca\u4e4b\u611f\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u5982\u6b64\u4e4b\u7f8e\u5973\uff0c\u5982\u82e5\u771f\u88ab\u90a3\u9ad8\u98de\u8679\u62a2\u4e86\u8fc7\u53bb\uff0c\u5b9e\u5728\u662f\u7ec8\u8eab\u5927\u61be\u3001\u5927\u6068\u7684\u4e8b\u60c5\uff0c \u3000\u3000\u4f46\u5982\u9ad8\u5929\u5065\u542c\u4fe1\u4e86\u9ad8\u98de\u8679\u7684\u6c42\u544a\u4e4b\u8a00\uff0c\u4f20\u4e0b\u4e86\u4ee4\u6e1d\uff0c\u8981\u6211\u4ea4\u51fa\u5434\u53cc\u5973\uff0c\u90a3\u65f6\uff0c\u5c31\u6709\u4e9b\u4e3a\u96be\u4e86\u3002 \u3000\u3000\u5982\u82e5\u4e0d\u4ea4\uff0c\u8fdd\u6297\u4e86\u4ee4\u8c15\uff0c\u4ee5\u5821\u4e3b\u7684\u9634\u6c89\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u653e\u8fc7\u4e00\u4e2a\u8fdd\u6297\u4ed6\u4ee4\u8c15\u7684\u4eba\u3002 \u3000\u3000\u60f3\u5230\u4e3a\u96be\u4e4b\u5904\uff0c\u672c\u662f\u4e00\u8138\u6d0b\u6d0b\u559c\u6c14\uff0c\u7acb\u523b\u95f4\uff0c\u53d8\u5f97\u65e0\u9650\u5fe7\u82e6\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u6b66\u529f\u5353\u7edd\uff0c\u9886\u8896\u7b2c\u4e00\u5821\u795e\u5200\u5802\uff0c\u8bad\u7ec3\u4e86\u5f88\u591a\u6b66\u529f\u5353\u7edd\u7684\u6740\u624b\uff0c\u7b2c\u4e00\u5821\u80fd\u6709\u4eca\u5929\uff0c\u96f7\u65e0\u5f71\u7acb\u529f\u6700\u5927\u3002 \u3000\u3000\u4f46\u96f7\u65e0\u5f71\u4e5f\u660e\u767d\u9ad8\u5929\u5065\u7684\u4e3a\u4eba\uff0c\u4ed6\u53ef\u4ee5\u5141\u8bb8\u4f60\u6709\u4e00\u767e\u4ef6\u529f\u52b3\uff0c\u4f46\u5374\u4e0d\u5141\u8bb8\u4f60\u6709\u4e00\u4ef6\u9519\u8bef\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5bf9\u9ad8\u5929\u5065\u5341\u5206\u5fe0\u5fc3\uff0c\u4f46\u73b0\u5728\uff0c\u4ed6\u5ffd\u7136\u5f00\u59cb\u6000\u7591\u8d77\u6765\u3002 \u3000\u3000\u201c\u96be\u9053\u771f\u7684\u4e3a\u4e00\u4e2a\u5973\u4eba\uff0c\u80cc\u53db\u4e86\u7b2c\u4e00\u5821\u4e0d\u6210?\u201d \u3000\u3000\u8fd9\u672c\u662f\u4ed6\u5fc3\u4e2d\u7684\u8bdd\uff0c\u4f46\u4e00\u6025\u4e4b\u4e0b\uff0c\u7adf\u7136\u8bf4\u51fa\u4e86\u53e3\u3002 \u3000\u3000\u5434\u53cc\u5973\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u5973\u4eba\u7684\u7f8e\u4e3d\uff0c\u7adf\u7136\u6709\u5982\u6b64\u7684\u91cd\u8981\u3002 \u3000\u3000\u4e00\u4e2a\u5973\u4eba\uff0c\u9664\u4e86\u6b66\u529f\u4e4b\u5916\uff0c\u8fd8\u6709\u522b\u7684\u5a01\u529b\uff0c\u5f88\u5927\u7684\u5a01\u529b\u3002 \u3000\u3000\u602a\u4e0d\u5f97\uff0c\u8fc7\u53bb\u7684\u4e66\u4e0a\uff0c\u6709\u8fc7\u7f8e\u4eba\u8ba1\u7684\u8ff0\u8bf4\u3002 \u3000\u3000\u5c3d\u7ba1\u8fc7\u53bb\u7684\u5386\u53f2\u4e0a\uff0c\u6709\u5f88\u591a\u7684\u7f8e\u4eba\u503e\u56fd\u7684\u6545\u4e8b\uff0c\u6709\u5f88\u591a\u82f1\u96c4\u6c14\u77ed\uff0c\u513f\u5973\u60c5\u957f\u7684\u4f20\u8bf4\uff0c\u4f46\u7537\u4eba\uff0c\u5c24\u5176\u662f\u6709\u70b9\u81ea\u8d1f\u7684\u7537\u4eba\uff0c\u9047\u4e0a\u81ea\u5df1\u559c\u7231\u7684\u7f8e\u5973\u65f6\uff0c\u4ecd\u662f\u60c5\u96be\u81ea\u7981\u3002 \u3000\u3000\u82f1\u96c4\u96be\u8fc7\u7f8e\u4eba\u5173\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u6765\u56de\u5728\u5ba4\u4e2d\u8d70\u52a8\u3002 \u3000\u3000\u4ed6\u5f00\u59cb\u5728\u60f3\uff0c\u601d\u7d22\u5982\u4f55\u5e94\u4ed8\u8fd9\u4ef6\u4e8b\u60c5\u3002 \u3000\u3000\u9ad8\u5929\u5065\u53ea\u6709\u90a3\u4e00\u4e2a\u72ec\u5b50\uff0c\u5e73\u5e38\u65f6\u65e5\uff0c\u5bf9\u4ed6\u5341\u5206\u9a84\u7eb5\u3002 \u3000\u3000\u4e00\u65e6\u4ed6\u82e6\u82e6\u54c0\u6c42\uff0c\u9ad8\u5929\u5065\u5f88\u53ef\u80fd\u4f1a\u7b54\u5e94\u4ed6\u3002 \u3000\u3000\u9ad8\u98de\u8679\u6025\u5f97\u50cf\u70ed\u9505\u4e0a\u7684\u8682\u8681\u4e00\u6837\uff0c\u5230\u5904\u5728\u627e\u9ad8\u5929\u5065\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e5f\u4e00\u76f4\u5728\u6697\u6697\u62c5\u5fc3\uff0c\u5979\u62c5\u5fc3\u96f7\u65e0\u5f71\u4f1a\u4f5c\u66f4\u8fc7\u5206\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u4f46\u5979\u5df2\u7ecf\u6697\u4e2d\u4f5c\u4e86\u4e00\u4e2a\u51b3\u5b9a\uff0c\u5982\u82e5\u96f7\u65e0\u5f71\u518d\u6709\u8d85\u8d8a\u4e00\u6b65\u8fdb\u72af\uff0c\u5979\u5df2\u51b3\u5fc3\u6297\u62d2\uff0c\u4e0d\u60dc\u66b4\u9732\u4f2a\u88c5\u3002 \u3000 \u7b2c \u5341 \u56de\u3000\u53cd\u76ee\u6210\u4ec7 \u3000 \u3000\u3000\u9ad8\u5929\u5065\u6b63\u5728\u4e00\u5ea7\u9ad8\u697c\u4e0a\u9762\u559d\u9152\u3002 \u3000\u3000\u5728\u4ed6\u7684\u65c1\u8fb9\uff0c\u5750\u4e86\u4e24\u4e2a\u5f88\u7f8e\u7684\u5973\u4eba\uff0c\u98ce\u60c5\u4e07\u79cd\u7684\u7f8e\u5973\u3002 \u3000\u3000\u662f\u7389\u7fe0\u3001\u5170\u767d\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u6295\u5165\u4e86\u7b2c\u4e00\u5821\u4e2d\u3002 \u3000\u3000\u5f88\u7cbe\u5bc6\u7684\u8bbe\u8ba1\uff0c\u800c\u4e14\uff0c\u5170\u767d\u4e5f\u4e3a\u6b64\u53d7\u4e86\u70b9\u4f24\u3002\u9ad8\u5929\u5065\u867d\u7136\u591a\u7591\uff0c\u4f46\u4ed6\u5bf9\u4e24\u4eba\u5374\u4e00\u70b9\u4e5f\u4e0d\u6000\u7591\u3002 \u3000\u3000\u81ea\u7136\uff0c\u7389\u7fe0\u3001\u5170\u767d\u7684\u8868\u6f14\u903c\u771f\u4f7f\u4ed6\u76f8\u4fe1\uff0c\u6700\u91cd\u8981\u7684\u8fd8\u662f\u4e24\u4eba\u5f88\u7f8e\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u5ea7\u5f88\u9ad8\u7684\u697c\uff0c\u4e00\u5171\u6709\u4e03\u5c42\uff0c\u5728\u90a3\u4e2a\u65f6\u4ee3\uff0c\u5efa\u7b51\u4e03\u5c42\u697c\uff0c\u5b9e\u5728\u662f\u4e00\u4e2a\u5f88\u4e0d\u5bb9\u6613\u7684\u6280\u672f\u3002 \u3000\u3000\u8fd9\u5ea7\u9ad8\u697c\u5efa\u7b51\u5f97\u5f88\u575a\u7262\uff0c\u90fd\u662f\u7528\u9752\u77f3\u780c\u6210\u7684\u3002 \u3000\u3000\u9ad8\u5929\u5065\u559d\u9152\u7684\u5730\u65b9\uff0c\u5728\u7b2c\u4e03\u5c42\u697c\u4e0a\u3002 \u3000\u3000\u5e03\u7f6e\u7684\u8c6a\u534e\uff0c\u56db\u9762\u7684\u7a97\u5b50\u4e0a\uff0c\u90fd\u6709\u7740\u5f88\u539a\u7684\u5e37\u5e55\uff0c\u906e\u4f4f\u697c\u4e0a\u7684\u706f\u706b\u3001\u6625\u8272\u3002 \u3000\u3000\u8fd9\u662f\u9ad8\u5929\u5065\u884c\u4e50\u7684\u5730\u65b9\uff0c\u9632\u5907\u5f97\u5f88\u68ee\u4e25\uff0c\u4e5f\u5f88\u673a\u5bc6\uff0c\u673a\u5bc6\u5230\u8fde\u4ed6\u7684\u513f\u5b50\u9ad8\u98de\u8679\u4e5f\u4e0d\u77e5\u9053\u8fd9\u662f\u996e\u9152\u53d6\u4e50\u4e4b\u5904\u3002 \u3000\u3000\u8fc7\u53bb\u7684\u9ad8\u5e7d\u5170\u548c\u5434\u53cc\u5973\uff0c\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u4f46\u7b2c\u4e00\u5821\u4e2d\uff0c\u90fd\u77e5\u9053\u8fd9\u5ea7\u9ad8\u697c\uff0c\u4f46\u4ed6\u4eec\u53ea\u77e5\u9053\u8fd9\u662f\u7981\u533a\uff0c\u662f\u7b2c\u4e00\u5821\u4e2d\u6700\u795e\u79d8\u7684\u5730\u65b9\u4e4b\u4e00\u3002 \u3000\u3000\u9664\u4e86\u9ad8\u5929\u5065\u4e4b\u5916\uff0c\u4efb\u4f55\u4eba\u672a\u5f97\u5141\u51c6\u4e4b\u524d\uff0c\u90fd\u4e0d\u8bb8\u8fdb\u5165\u7981\u533a\u4e00\u6b65\u3002 \u3000\u3000\u8fd9\u5730\u65b9\uff0c\u4e5f\u5b9e\u5728\u4e0d\u80fd\u6765\uff0c\u56e0\u4e3a\u56db\u3001\u4e94\u3001\u516d\u697c\uff0c\u4f4f\u7684\u90fd\u662f\u7f8e\u5973\uff0c\u5357\u5317\u4f73\u4e3d\uff0c\u71d5\u7626\u73af\u80a5\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u7687\u5bab\u5185\u4e09\u5bab\u516d\u9662\uff0c\u548c\u4e03\u5341\u4e8c\u5983\u90a3\u4e48\u591a\u4eba\uff0c\u4f46\u8fd9\u4e09\u5c42\u697c\u5185\uff0c\u4e5f\u5206\u4f4f\u4e86\u5341\u51e0\u4e2a\u7f8e\u5973\u3002 \u3000\u3000\u5979\u4eec\u6709\u7740\u5f88\u597d\u7684\u4eab\u53d7\uff0c\u5403\u7684\u662f\u5c71\u73cd\u6d77\u5473\uff0c\u7a7f\u7684\u662f\u7eeb\u7f57\u7ef8\u7f0e\u3002 \u3000\u3000\u552f\u4e00\u7684\u7981\u5fcc\u662f\uff0c\u5979\u4eec\u4e0d\u80fd\u79bb\u5f00\u4f4f\u7684\u5730\u65b9\u3002 \u3000\u3000\u4f46\u9ad8\u5929\u5065\u4eab\u4e50\u7684\u5730\u65b9\uff0c\u5374\u662f\u5728\u7b2c\u4e03\u5c42\u697c\u4e0a\u3002 \u3000\u3000\u4ed6\u60f3\u8981\u54ea\u4e00\u4e2a\u7f8e\u5973\u65f6\uff0c\u5c31\u628a\u5979\u53ec\u4e0a\u7b2c\u4e03\u5c42\u697c\u3002 \u3000\u3000\u56db\u3001\u4e94\u3001\u516d\u3001\u4e03\u5c42\uff0c\u5145\u6ee1\u7740\u6625\u8272\u3001\u67d4\u60c5\uff0c\u4f46\u4e00\u3001\u4e8c\u3001\u4e09\u697c\uff0c\u786e\u662f\u6b65\u6b65\u6740\u673a\u3002 \u3000\u3000\u8fd9\u91cc\u4e00\u5171\u4f4f\u4e86\u5341\u516b\u4e2a\u9ad8\u5929\u5065\u7684\u5fc3\u8179\u3001\u6b7b\u58eb\uff0c \u65e5\u591c\u4e0d\u65ad\u5728\u8fd9\u9ad8\u697c\u7981\u533a\u4e4b\u5185\u5de1\u89c6\u3002 \u9664\u4e86\u5341\u516b\u4e2a\u5fc3\u8179\u6b7b\u58eb\u4e4b\u5916\uff0c\u8fd8\u6709\u5341\u4e8c\u53ea\u5f02\u79cd\u864e\u7352\uff0c\u8fd9\u4e9b\u7352\u72ac\u8033\u76ee\u7075\u654f\uff0c\u7981\u533a\u5185\u4efb\u4f55\u58f0\u606f\u90fd\u7792\u4e0d\u4f4f\u5b83\u4eec\u3002 \u3000\u3000\u4f46\u6700\u5389\u5bb3\u7684\uff0c\u662f\u56db\u4e2a\u706b\u5668\u9ad8\u624b\uff0c\u4ed6\u4eec\u5c45\u5e84\u5728\u7b2c\u4e09\u5c42\u4e0a\uff0c\u9664\u4e86\u4e00\u8eab\u4e0d\u9519\u7684\u6b66\u529f\u4e4b\u5916\uff0c\u90fd\u6709\u4e00\u8eab\u706b\u5668\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\uff0c\u7ec4\u6210\u4e86\u4e00\u9053\u575a\u5f3a\u65e0\u6bd4\u7684\u9632\u56f4\uff0c\u4f7f\u4eba\u96be\u8d8a\u96f7\u6c60\u4e00\u6b65\u3002 \u3000\u3000\u51e1\u662f\u9ad8\u5929\u5065\u4e0d\u592a\u559c\u6b22\u7684\u5973\u4eba\uff0c\u5c31\u8d4f\u7ed9\u4e86\u8fd9\u4e9b\u5c5e\u4e0b\u3002 \u3000\u3000\u8fd9\u91cc\u5df2\u65e0\u5ec9\u803b\u53ef\u8a00\uff0c\u4f46\u4ed6\u4eec\u7684\u751f\u6d3b\u5374\u5f88\u60a0\u54c9\u3002 \u3000\u3000\u9ad8\u98de\u8679\u627e\u904d\u4e86\u7b2c\u4e00\u5821\u4e2d\u5927\u90e8\u5206\u5730\u65b9\uff0c\u7ec8\u4e8e\u60f3\u8d77\u4e86\u8fd9\u5ea7\u9ad8\u697c\u3002 \u3000\u3000\u8fd9\u5ea7\u9ad8\u697c\uff0c\u6709\u4e00\u4e2a\u5f88\u62bd\u8c61\uff0c\u4f46\u5374\u5f88\u96c5\u81f4\u7684\u540d\u5b57\uff0c\u53eb\u4f5c\u201c\u70df\u9501\u697c\u201d\u3002 \u3000\u3000\u70df\u9501\u7684\u610f\u601d\uff0c\u5c31\u662f\u770b\u4e0d\u6e05\u695a\uff0c \u4f60\u53ef\u4ee5\u53bb\u731c\uff0c \u4f60\u600e\u4e48\u53bb\u731c\u90fd\u884c\u3002 \u3000\u3000\u9ad8\u98de\u8679\u60f3\u5230\u4e86\u201c\u70df\u9501\u697c\u201d\u3002 \u3000\u3000\u81ea\u7136\uff0c\u7b2c\u4e00\u5821\u7684\u673a\u5bc6\u5730\u65b9\u5f88\u591a\uff0c\u4f46\u5927\u90fd\u5728\u795e\u5200\u5802\u5185\uff0c\u795e\u5200\u5802\u4e4b\u5916\uff0c\u53ea\u6709\u70df\u9501\u697c\uff0c\u6700\u4e3a\u673a\u5bc6\u3002 \u3000\u3000\u4f46\u9ad8\u98de\u8679\u6599\u5b9a\u4e86\u7236\u4eb2\u4e0d\u5728\u795e\u5200\u5802\u5185\uff0c\u56e0\u4e3a\uff0c\u4ed6\u5982\u5728\u795e\u5200\u5802\u4e2d\uff0c\u96f7\u65e0\u5f71\u4e00\u5b9a\u8981\u966a\u4f8d\u8eab\u8fb9\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\uff0c\u6ca1\u6709\u5927\u624d\u5927\u80fd\uff0c\u4f46\u5c0f\u806a\u660e\uff0c\u8fd8\u6709\u4e00\u4e9b\u3002 \u3000\u3000\u9ad8\u98de\u8679\u7ec8\u4e8e\u627e\u4e0a\u201c\u70df\u9501\u697c\u201d\u3002\u9ed1\u591c\u4e2d\uff0c\u9ad8\u697c\u8038\u7acb\uff0c\u770b\u4e0d\u5230\u4efb\u4f55\u706f\u706b\u3002 \u3000\u3000\u9ad8\u98de\u8679\u884c\u5165\u4e86\u7981\u533a\uff0c\u5148\u542c\u4e00\u58f0\u72ac\u5420\uff0c\u4e00\u53ea\u9ad8\u5927\u7684\u864e\u7352\uff0c\u7a81\u7136\u51fa\u73b0\u3002 \u3000\u3000\u8fd9\u53ea\u662f\u7981\u533a\u7684\u56f4\u5899\u95e8\u53e3\u3002 \u3000\u3000\u9694\u7740\u4e00\u9053\u94c1\u6805\uff0c\u864e\u7352\u7684\u53cc\u76ee\u4e2d\uff0c\u95ea\u52a8\u6de1\u7ea2\u7684\u5149\u8292\u3002 \u3000\u3000\u4e00\u4e2a\u9ed1\u8863\u52b2\u88c5\uff0c\u8eab\u4f69\u957f\u5251\u7684\u6b66\u58eb\uff0c\u7a81\u7136\u51fa\u73b0\u5728\u94c1\u6805\u95e8\u5185\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u51b7\u5730\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4f60\u8ba4\u4e0d\u8ba4\u8bc6\u6211?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u6447\u6447\u5934\u3002 \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u6211\u53eb\u9ad8\u98de\u8679\uff0c\u8fd9\u91cc\u5c11\u5821\u4e3b\uff0c\u4f60\u603b\u77e5\u9053\u5427?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u53c8\u6447\u6447\u5934\u3002 \u3000\u3000\u9ad8\u98de\u8679\u706b\u4e86\uff0c\u6012\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e0d\u662f\u7b2c\u4e00\u5821\u7684\u4eba?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7b11\u9053\uff1a\u201c\u9601\u4e0b\uff0c\u8fd9\u91cc\u662f\uff0c\u2018\u70df\u9501\u697c\u2019\uff0c\u70df\u9501\u4e91\u5c01\uff0c\u6211\u4eec\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\uff0c\u6240\u4ee5\uff0c\u9601\u4e0b\u4e5f\u7528\u4e0d\u7740\u751f\u6c14\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4e0d\u662f\u4ec0\u4e48\u697c\uff0c\u4e0d\u7ba1\u70df\u548c\u4e91\uff0c\u4f60\u4eec\u603b\u77e5\u9053\u9ad8\u5821\u4e3b\u5427?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u55ef!\u77e5\u9053\u53c8\u600e\u4e48\u6837?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u77e5\u9053\u5c31\u597d\uff0c\u6211\u662f\u4ed6\u7684\u513f\u5b50\uff0c\u552f\u4e00\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u8bba\u662f\u8c01\uff0c\u4e5f\u65e0\u6cd5\u8fdb\u5165\u8fd9\u70df\u9501\u697c\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u54fc!\u4f60\u8fd9\u4e2a\u4eba\u5f00\u4e0d\u5f00\u7a8d\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u5949\u547d\u5b88\u6b64\uff0c\u4ec0\u4e48\u4eba\u64c5\u5165\u4e00\u6b65\uff0c\u6211\u4eec\u5c31\u4f1a\u53d6\u4ed6\u6027\u547d\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4f60\u4eec\u5949\u8c01\u4e4b\u547d?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u662f\u6211\u7239\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u597d\uff0c\u73b0\u5728\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u603b\u8be5\u77e5\u9053\u4e86\u5427?\u95ea\u5f00\u8def\uff0c\u8ba9\u6211\u8fdb\u53bb\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u8fd9\u91cc\u4efb\u4f55\u4eba\u90fd\u4e0d\u80fd\u968f\u4fbf\u51fa\u5165\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4efb\u4f55\u4eba\uff0c\u6211\u662f\u5c11\u5821\u4e3b\uff0c\u5821\u4e3b\u80fd\u5230\u7684\u5730\u65b9\uff0c\u6211\u90fd\u80fd\u5230\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u5ffd\u7136\u5411\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u4e0d\u7ba1\u4f60\u662f\u8c01\uff0c\u9664\u975e\u4f60\u6709\u8170\u724c\uff0c\u6216\u662f\u6211\u4eec\u5949\u5230\u4e86\u4ee4\u8c15\uff0c\u4e0d\u7136\u7684\u8bdd\uff0c\u5929\u738b\u8001\u5b50\u5230\u8fd9\u91cc\uff0c\u6211\u4eec\u4e00\u6837\u4e5f\u8981\u6740\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u96be\u9053\u8fd9\u70df\u9501\u697c\u4e2d\uff0c\u771f\u6709\u4ec0\u4e48\u79d8\u5bc6\uff0c\u9664\u4e86\u6211\u7239\uff0c\u522b\u4eba\u4e0d\u80fd\u89c1\u5230\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u8f6c\u5ff5\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a \u201c\u597d\u5427!\u65e2\u662f\u6211\u4e0d\u80fd\u8fdb\u53bb\uff0c\u90a3\u5c31\u6709\u52b3\u4f60\u8001\u5144\u5e2e\u4e2a\u5fd9\u4e86\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u54c8\u54c8\u4e00\u7b11\u9053\uff1a \u201c\u9601\u4e0b\u60f3\u5f97\u592a\u5929\u771f\u4e86\uff0c\u54b1\u4eec\u4e0d\u4f1a\u66ff\u4f60\u901a\u62a5\u3002\u201d \u9ad8\u98de\u8679\u5fcd\u4e86\u53c8\u5fcd\uff0c\u9053\uff1a \u201c\u4f60\u5c0f\u5b50\u8bb0\u7740\uff0c \u4f60\u8fd9\u6837\u7559\u96be\u6211\uff0c\u54b1\u4eec\u65e5\u540e\u518d\u7b97\u8d26\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u6b63\u8981\u53d1\u4f5c\uff0c\u5374\u88ab\u53e6\u4e00\u4e2a\u9ed1\u8863\u4eba\u63a5\u9053\uff1a\u201c\u65e2\u7136\u4ed6\u81ea\u79f0\u662f\uff0c\u5821\u4e3b\u7684\u513f\u5b50\uff0c\u90a3\u5c31\u66ff\u4ed6\u901a\u62a5\u4e00\u58f0\u3002\u201d \u3000\u3000\u6321\u5728\u95e8\u53e3\u7684\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u597d!\u4f60\u53bb\u901a\u62a5\u5427\uff0c\u6211\u5728\u8fd9\u91cc\u5b88\u62a4\u3002\u201d \u3000\u3000\u4e00\u4e2a\u9ed1\u8863\u4eba\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u6211\u6709\u91cd\u8981\u5927\u4e8b\u7980\u62a5\u3002\u201d \u3000\u3000\u6321\u5728\u95e8\u53e3\u7684\u9ed1\u8863\u4eba\u9053\uff1a \u201c\u6211\u4eec\u53ea\u7ba1\u901a\u62a5\uff0c\u80fd\u4e0d\u80fd\u89c1\u4f60\uff0c\u8fd8\u662f\u672a\u77e5\u6570\u3002\u201d \u9ad8\u98de\u8679\u56de\u987e\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u53ef\u4ee5\u8fdb\u53bb\u5750\u5750\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u672a\u518d\u7406\u4f1a\u4ed6\uff0c\u53ea\u662f\u51b7\u51b7\u7684\u7ad9\u7740\u3002 \u3000\u3000\u9ad8\u98de\u8679\u53e3\u4e2d\u5c3d\u7ba1\u72e0\uff0c\u4f46\u5374\u4e0d\u6562\u518d\u5411\u524d\u903c\u8fdb\u4e00\u6b65\u3002 \u3000\u3000\u7b49\u4e86\u8db3\u8db3\u6709\u4e00\u987f\u996d\u5de5\u592b\u4e4b\u4e45\u3002 \u3000\u3000\u9ad8\u98de\u8679\u4e0d\u505c\u5728\u95e8\u53e3\u5904\u8d70\u52a8\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7a81\u7136\u51fa\u73b0\uff0c \u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u53e3\u6c14\u51b7\u8083\uff0c\u8138\u4e0a\u4e5f\u662f\u4e00\u7247\u4e0d\u60a6\u4e4b\u8272\u3002 \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u7239\uff0c\u6709\u4e00\u4ef6\u91cd\u5927\u7684\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5148\u56de\u53bb\u5427\uff0c\u4e0d\u8bba\u4ec0\u4e48\u4e8b\uff0c\u7b49\u660e\u5929\u518d\u8bf4\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u80fd\u7b49\u5230\u660e\u5929\uff0c\u6211\u4e5f\u4e0d\u7528\u627e\u7239\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u54e6!\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u5434\u53cc\u5973\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u679c\u7136\u5fc3\u4e2d\u4e00\u60ca\uff0c\u6025\u6025\u63a5\u9053\uff1a\u201c\u5434\u53cc\u5973\u600e\u4e48\u6837\u4e86?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u88ab\u96f7\u65e0\u5f71\u5e26\u51fa\u4e86\u5730\u4e0b\u77f3\u7262\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u677e\u4e01\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u8fd8\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u8fd9\u4e5f\u503c\u5f97\u5927\u60ca\u5c0f\u602a\u5417?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a \u201c\u773c\u770b\u4ed6\u5c31\u8981\u5f97\u624b\u4e86\uff0c\u5b69\u513f\u53bb\u89c1\u4ed6\uff0c\u6c42\u4ed6\u653e\u4eba\uff0c\u4ed6\u4e0d\u4f46\u4e0d\u80af\uff0c\u53cd\u800c\u628a\u5b69\u513f\u7ed9\u9a82\u4e86\u56de\u6765\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u54e6!\u6211\u77e5\u9053\u4e86\uff0c\u4f60\u5148\u56de\u53bb\u5427\uff0c\u6709\u4ec0\u4e48\u8bdd\uff0c\u54b1\u4eec\u660e\u5929\u518d\u8c08\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u7239\uff0c\u4e0d\u884c\u554a\uff0c\u660e\u5929\u5c31\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u9ad8\u5929\u5065\u5fc3\u4e2d\u5df2\u660e\u767d\u4e86\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4f46\u4ed6\u5374\u88c5\u4f5c\u4e0d\u660e\u767d\uff0c\u9053\uff1a\u201c\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u7239\u4e0d\u662f\u5e0c\u671b\u5b69\u513f\u80fd\u5a36\u4e2a\u597d\u5ab3\u5987\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u662f\u554a!\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u6700\u597d\u662f\u80fd\u628a\u5434\u53cc\u5973\u7ed9\u5a36\u5230\u624b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u55ef!\u864e\u7236\u72ac\u5b50\uff0c\u4f60\u5a36\u4e0d\u5230\u624b\uff0c\u6211\u6709\u4ec0\u4e48\u529e\u6cd5?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u73b0\u5728\uff0c\u8981\u7239\u5e2e\u4e00\u4e2a\u5fd9\uff0c\u6211\u4e00\u5b9a\u80fd\u5a36\u5230\u5434\u53cc\u5973\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8bf4\u5427!\u8981\u6211\u600e\u6837\u5e2e\u4f60\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u53ea\u8981\u80fd\u4e0b\u4e00\u9053\u4ee4\u8c15\uff0c\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u867d\u7136\u611f\u89c9\u6b64\u5b50\uff0c\u6ca1\u6709\u4ec0\u4e48\u51fa\u606f\uff0c\u4f46\u513f\u5b50\u7a76\u7adf\u662f\u81ea\u5df2\u7684\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u98de\u8679\uff0c\u4e0d\u8981\u518d\u60f3\u5434\u53cc\u5973\u7684\u4e8b\u4e86\uff0c\u5929\u4e0b\u4f73\u4e3d\u65e0\u6570\uff0c\u5341\u6b65\u4e2d\u5fc5\u6709\u82b3\u8349\uff0c\u4f60\u4f55\u5fc5\u4e00\u5b9a\u8981\u60f3\u5979\u5462?\u201d \u3000\u3000\u9ad8\u98de\u8679\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u7239\uff0c\u4f60\u53ef\u77e5\u9053\u96f7\u65e0\u5f71\u628a\u5434\u53cc\u5973\u5f04\u51fa\u77f3\u7262\u7684\u7528\u5fc3\u4f55\u5728\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u603b\u4e0d\u4f1a\u653e\u4e86\u5979\u5427?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4ed6\u8981\u5360\u6709\u5434\u53cc\u5973\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u7531\u4ed6\u53bb\u7f62\uff0c\u4ed6\u4e3a\u7b2c\u4e00\u5821\u51fa\u529b\u5f88\u5927\uff0c\u5434\u53cc\u5973\u80cc\u53db\u4e86\u54b1\u4eec\uff0c\u4ed6\u8981\u600e\u4e48\u5904\u7f6e\u5434\u53cc\u5973\uff0c\u4e5f\u5c31\u4e0d\u91cd\u8981\u4e86\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u53f9\u606f\u4e00\u58f0\u9053\uff1a \u201c\u7239\uff0c\u5b69\u513f\uff0c\u5b69\u513f\uff0c\u5bf9\u5434\u53cc\u5973\u8fd8\u662f\u5ff5\u5ff5\u96be\u5fd8\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u5c31\u7b97\u5434\u53cc\u5973\u5ac1\u7ed9\u4f60\uff0c\u4f60\u4e5f\u65e0\u6cd5\u9a7e\u9a6d\uff0c\u4e0d\u7528\u518d\u60f3\u8fd9\u4ef6\u4e8b\u4e86\uff0c\u7239\u7b54\u5e94\u4f60\uff0c\u66ff\u4f60\u9009\u4e00\u4e2a\u5bb9\u8272\u7edd\u4e16\u7684\u5ab3\u5987\u5c31\u662f\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4e0d!\u6211\u4e00\u5b9a\u8981\u5434\u53cc\u5973\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u7531\u4e0d\u5f97\u4f60\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u7239\uff0c\u4f60\u53ea\u6709\u6211\u4e00\u4e2a\u513f\u5b50\u554a\uff01\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u628a\u4f60\u7ed9\u5ba0\u574f\u4e86\uff0c\u9f50\u5927\u975e\u5076\uff0c\u4e0d\u8981\u75f4\u5fc3\u5984\u60f3\u4e86\uff0c\u53bb\u4f11\u606f\u5427\uff01\u201d \u3000\u3000\u9ad8\u98de\u8679\u8fd8\u60f3\u518d\u8bf4\u4ec0\u4e48\uff0c\u4f46\u770b\u5230\u4e86\u9ad8\u5929\u5065\u8138\u4e0a\u7684\u6012\u8272\uff0c\u53ea\u597d\u54ac\u54ac\u7259\u5fcd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u7f13\u7f13\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u660e\u767d\uff0c\u8fd9\u4ef6\u4e8b\u5f88\u56f0\u96be\uff0c\u4f46\u5374\u672a\u60f3\u5230\u9ad8\u5929\u5065\u4f1a\u5b8c\u5168\u7684\u62d2\u7edd\uff0c\u8fd9\u4f7f\u9ad8\u98de\u8679\u5f88\u4f24\u5fc3\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5374\u91cd\u56de\u5230\u70df\u9501\u697c\u4e0a\u3002 \u3000\u3000\u5170\u767d\u4f3c\u662f\u5df2\u529b\u4e0d\u80dc\u9152\uff0c\u5750\u5728\u7ea2\u6f06\u5927\u6728\u6905\u5b50\u4e0a\uff0c\u4eba\u5df2\u7761\u7740\u3002 \u3000\u3000\u7389\u7fe0\u5374\u662f\u7cbe\u795e\u9971\u6ee1\uff0c\u5a07\u6001\u6a2a\u751f\uff0c\u800c\u4e14\uff0c\u80c6\u5927\u7684\u8131\u4e86\u4e0a\u8863\uff0c\u53ea\u7a7f\u7740\u7d27\u8eab\u77ed\u8896\u7684\u767d\u7eeb\u886b\uff0c\u9690\u9690\u53ef\u89c1\u91cc\u9762\u7684\u7ea2\u809a\u513f\u3002 \u3000\u3000\u671b\u7740\u7389\u7fe0\u90a3\u80a1\u5a07\u60c5\u5a9a\u6001\uff0c\u9ad8\u5929\u5065\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5170\u767d\u559d\u9189\u4e86\u3002\u201d \u3000\u3000\u4ed6\u662f\u8001\u5403\u8001\u505a\u7684\u5927\u5978\u5de8\u9b54\uff0c\u5bf9\u5df2\u7ecf\u5230\u53e3\u7684\u8089\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u72fc\u541e\u864e\u54bd\u7684\u5403\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u8981\u7ec6\u56bc\u6162\u54bd\uff0c\u54c1\u51fa\u90a3\u80a1\u6ecb\u5473\u6765\u3002 \u3000\u3000\u7389\u7fe0\u4e5f\u662f\u9605\u4eba\u591a\u77e3!\u5e74\u7eaa\u867d\u7136\u4e0d\u592a\uff0c\u4f46\u5374\u662f\u89c1\u591a\u8bc6\u5e7f\u3002 \u3000\u3000\u5979\u662f\u6c5f\u6e56\u4e2d\u7b2c\u4e00\u6d41\u7684\u5973\u6740\u624b\uff0c\u7f8e\u8272\u8bf1\u6740\uff0c\u662f\u5979\u7684\u7edd\u62db\u4e4b\u4e00\u3002 \u3000\u3000\u4f46\u5979\u660e\u767d\u9ad8\u5929\u5065\uff0c\u90a3\u662f\u6210\u4e86\u7cbe\u7684\u8001\u72d0\u72f8\uff0c\u8fc7\u5206\u70ed\u60c5\uff0c\u51b7\u82e5\u51b0\u971c\uff0c\u90fd\u4f1a\u4f7f\u4ed6\u52a8\u7591\uff0c\u8981\u6536\u655b\u5f97\u6070\u5230\u597d\u5904\uff0c\u81ea\u81ea\u7136\u7136\uff0c\u624d\u80fd\u4f7f\u4ed6\u6d88\u9664\u987e\u8651\u4e4b\u5fc3\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\uff0c\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u4f46\u7389\u7fe0\u5374\u505a\u5f97\u5f88\u597d\u3002 \u3000\u3000\u56de\u987e\u4e86\u5170\u767d\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5979\u4e00\u76f4\u7ec3\u4e0d\u51fa\u9152\u91cf\u6765\uff0c\u5e73\u5e38\u4e5f\u4e0d\u591a\u559d\uff0c\u88ab\u4f60\u704c\u4e86\u51e0\u676f\uff0c\u5c31\u539f\u5f62\u6bd5\u9732\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u9053\uff1a\u201c\u5c0f\u59ae\u5b50\u5982\u6b64\u7684\u65e0\u91cf\u5417?\u201d \u3000\u3000\u7389\u7fe0\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u5979\u672c\u4e0d\u5584\u9152\uff0c\u4f46\u521d\u6b21\u548c\u5821\u4e3b\u5171\u996e\uff0c\u53c8\u4e0d\u4fbf\u63a8\u62d2\uff0c\u6240\u4ee5\uff0c\u5c31\u559d\u9189\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u53cc\u76ee\u76ef\u6ce8\u5728\u5170\u767d\u7684\u8138\u4e0a\uff0c\u53cc\u76ee\u4e2d\u6cdb\u8d77\u4e86\u60c5\u6001\u7684\u706b\u7130\u3002 \u3000\u3000\u7389\u7fe0\u5fc3\u5934\u4e00\u9707\uff0c\u6697\u9053\uff1a\u7cdf\u4e86\uff0c\u83ab\u8981\u88ab\u4ed6\u77a7\u51fa\u4e86\u4ec0\u4e48\u7834\u7efd\uff0c\u88c5\u9189\u53cd\u800c\u88c5\u51fa\u6bdb\u75c5\u6765\u4e86\uff0c\u5979\u8a93\u6b7b\u8981\u4fdd\u6301\u6e05\u767d\u4e4b\u8eab\uff0c\u4e07\u4e00\u88ab\u9ad8\u5929\u5065\u5bf9 \u3000\u3000\u5979\u52a8\u624b\uff0c\u5979\u7edd\u5bf9\u4e0d\u4f1a\u4f9d\u4ece\uff0c\u90a3\u5c82\u4e0d\u662f\u7acb\u523b\u8981\u9732\u51fa\u9a6c\u811a\u4e86\u5417?\u201d \u3000\u3000\u53ea\u542c\u9ad8\u5929\u5065\u8bf4\u9053\uff1a\u201c\u7389\u7fe0\uff0c\u770b\u4e0a\u53bb\uff0c\u4ee4\u59b9\u8fd8\u4f3c\u662f\u4e00\u4e2a\u5904\u5b50\u4e4b\u8eab?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\uff0c\u4f60\u5f00\u73a9\u7b11\u4e86\uff0c\u6211\u4eec\u6d41\u843d\u6c5f\u6e56\uff0c\u8eab\u4e3a\u6740\u624b\u624b\uff0c\u5c31\u7b97\u6211\u4eec\u60f3\u6d01\u8eab\u81ea\u7231\uff0c\u53ea\u6050\u4e5f\u6709\u6240\u4e0d\u80fd\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u662f\u5728\u4e0b\u770b\u8d70\u773c\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u662f\u56e0\u4e3a\u4e2b\u5934\u9189\u4e86\u5462?\u8fd8\u662f\u9ad8\u7237\u559d\u9152\u591a\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u9053\uff1a\u201c\u8001\u592b\u7684\u9152\u91cf\uff0c \u81ea\u4fe1\u518d\u559d\u4e2a\u4e09\u4e94\u65a4\u4e0b\u53bb\uff0c\u4e5f\u9189\u4e0d\u4e86\u3002\u201d \u7389\u7fe0\u5a07\u5a9a\u4e00\u7b11\u9053\uff1a\u201c\u5821\u4e3b\u4f60\u53ef\u77e5\u9053\uff0c\u7389\u5170\u53cc\u59dd\uff0c\u4e00\u5411\u662f\u5927\u5c0f\u6709\u5e8f\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u6014\u4e86\u4e00\u6014\uff0c\u7a81\u7136\u653e\u58f0\u5927\u7b11\u9053\uff1a\u201c\u600e\u4e48?\u4f60\u8fd8\u5403\u59b9\u59b9\u7684\u918b?\u201d\u7389\u7fe0\u9053\uff1a \u201c\u4e0d\u662f\u8d31\u59be\u5403\u918b\uff0c\u800c\u662f\u5979\u4e00\u5411\u5f88\u5c0a\u91cd\u6211\uff0c\u4e0d\u80af\u5360\u5148\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a \u201c\u54e6!\u4ee4\u59b9\u8fd8\u6709\u8fd9\u4e2a\u7f8e\u5fb7\uff0c\u90a3\u771f\u662f\u66f4\u53ef\u7230\u4e86\uff0c\u7389\u7fe0\uff0c\u4eca\u665a\u5c31\u7531\u4f60\u8fd9\u4e2a\u505a\u59d0\u59d0\u7684\u5148\u966a\u966a\u6211\u5427?\u201d \u3000\u3000\u9ad8\u5929\u5065\u62e5\u4f4f\u4e86\u7389\u7fe0\uff0c\u7b11\u9053\uff1a\u201c\u8fd8\u8981\u518d\u559d\u4e0b\u53bb\u5417?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u8d31\u59be\u7684\u9152\u91cf\uff0c\u5df2\u7ecf\u5c3d\u91cf\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u660e\u5929\uff0c\u54b1\u4eec\u518d\u559d\u5427!\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6089\u51ed\u5821\u4e3b\u5429\u5490\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u7389\u7fe0\uff0c\u4eca\u591c\uff0c\u4f60\u7559\u5728\u8fd9\u91cc\u966a\u6211\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5821\u4e3b\u5982\u4e0d\u5acc\u5f03\u8d31\u82b1\u8d25\u67f3\uff0c\u8d31\u59be\u81ea\u5f53\u4ece\u547d\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff0c\u4f60\u5f88\u5584\u89e3\u4eba\u610f\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u820d\u59b9\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u70df\u9501\u697c\u4e0a\uff0c\u53ef\u4ee5\u518d\u591a\u4f4f\u51e0\u4f4d\u4f73\u4e3d\u3002\u201d \u3000\u3000\u54c8\u54c8\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u6765\u4eba\u554a!\u201d \u3000\u3000\u4e24\u4e2a\u5973\u5a62\uff0c\u5e94\u58f0\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u9ad8\u5929\u5065\u671b\u4e86\u671b\u5012\u5728\u5750\u6905\u4e0a\u7684\u5170\u767d\uff0c\u9053\uff1a\u201c\u628a\u4e8c\u5c0f\u59d0\u6276\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4e24\u4e2a\u5973\u5a62\u4f38\u624b\u6276\u4f4f\u4e86\u5170\u767d\uff0c\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u8981\u5979\u4f4f\u5728\u7261\u4e39\u9601\u4e2d\u3002\u201d \u3000\u3000\u5de6\u9996\u4e00\u4e2a\u5973\u5a62\u5e94\u9053\uff1a\u201c\u7261\u4e39\u9601\u4e0d\u662f\u4f4f\u6709\u4e03\u592b\u4eba\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8981\u5979\u642c\u4e00\u642c\u3002\u201d \u3000\u3000\u5973\u5a62\u5e94\u4e86\u4e00\u58f0\uff0c\u6276\u7740\u5170\u767d\u4e0b\u697c\u800c\u53bb\u3002 \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e03\u592b\u4eba\u662f\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e00\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u8d31\u59be\u60f3\u95ee\u7684\u662f\u5979\u7684\u51fa\u8eab?\u201d \u3000\u3000\u9ad8\u5929\u5065\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u8fdb\u5165\u8fd9\u70df\u9501\u697c\u4e2d\u7684\u4eba\uff0c\u4e0d\u7ba1\u5979\u7684\u51fa\u8eab\u5982\u4f55?\u53d7\u5ba0\u7231\u7684\u7a0b\u5ea6\uff0c\u8981\u770b\u5979\u7684\u7f8e\u8c8c\u6807\u51c6\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u7261\u4e39\u9601\u662f\u6700\u597d\u7684\u5730\u65b9\uff0c\u4e5f\u662f\u4f60\u6700\u5ba0\u7231\u7684\u4eba\uff0c\u4f4f\u7684\u5730\u65b9?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u70df\u9501\u697c\u4e0a\u7684\u7261\u4e39\u9601\uff0c\u5e76\u4e0d\u662f\u6700\u597d\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u4f4f\u7684\u5730\u65b9\u6700\u597d?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u54e6!\u8fd9\u7b2c\u4e03\u5c42\u697c\u4e0a\uff0c\u6709\u4e00\u95f4\u91d1\u78a7\u8f89\u714c\u7684\u5367\u5ba4\u90a3\u624d\u662f\u70df\u9501\u697c\u4e0a\u6700\u597d\u7684\u5730\u65b9\uff0c\u4eca\u665a\u4e0a\uff0c\u4f60\u5c31\u4f4f\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u7389\u7fe0\u5ffd\u7136\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u4e3a\u4e86\u820d\u59b9\uff0c\u4f60\u9010\u8d70\u4e86\u4e03\u592b\u4eba\u4eca\u591c\u6211\u7559\u5728\u6b64\u5730\uff0c\u660e\u591c\uff0c\u4e0d\u77e5\u6b64\u5ba4\u53c8\u6362\u4e86\u4ec0\u4e48\u6837\u7684\u5973\u4eba?\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u7389\u7fe0\uff0c\u8ba7\u6e56\u4e0a\u8bf4\u4f60\u6740\u4eba\u65e0\u6570\uff0c\u662f\u5f53\u4eca\u6c5f\u6e56\u4e0a\u6700\u6770\u51fa\u7684\u5973\u6740\u624b\uff0c\u60f3\u4e0d\u5230\uff0c\u4f60\u7adf\u4e5f\u662f\u513f\u5973\u60c5\u957f\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u522b\u5fd8\u4e86\uff0c\u6211\u4e5f\u662f\u5973\u4eba\u554a!\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u597d!\u597d!\u6c5f\u6e56\u4e0a\u7b2c\u4e00\u6d41\u7684\u5973\u6740\u624b\uff0c\u5bf9\u6211\u9ad8\u67d0\u4eba\u52a8\u4e86\u771f\u60c5\uff0c\u53ef\u771f\u53eb\u6211\u6709\u4e9b\u53d7\u5ba0\u82e5\u60ca\u554a!\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u5728\u4f60\u9ad8\u5821\u4e3b\u7684\u5fc3\u4e2d\uff0c\u8fd8\u6709\u4e00\u70b9\u4efd\u91cf\u5566\uff01\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u54e6!\u91cd\u8981\u7684\u5f88\u554a\uff01\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u4f60\u53ef\u77e5\u9053\uff0c\u8981\u4e00\u4e2a\u5973\u4eba\u4ed8\u51fa\u771f\u60c5\uff0c\u7537\u4eba\u5e94\u8be5\u5982\u4f55?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u4e2a\u4e48?\u4f60\u8fd8\u771f\u628a\u6211\u95ee\u4f4f\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5509!\u4eba\u5fc3\u6362\u4eba\u5fc3\uff0c\u4e5f\u8bb8\uff0c\u5973\u4eba\u771f\u7684\u4e0d\u592a\u9002\u5408\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u4e0d\u8fc7\u4e09\u56db\u5e74\uff0c\u6211\u5df2\u7ecf\u6709\u4e9b\u538c\u5026\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\uff0c\u4f60\u4eec\u4e24\u59ca\u59b9\u8d5a\u4e86\u4e0d\u5c11\u7684\u94b1\u554a!\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u662f\u5b58\u4e86\u4e00\u4e9b\u94f6\u5b50\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u591f\u591a\uff0c\u6bd4\u8d77\u4f60\u9ad8\u5821\u4e3b\uff0c\u90a3\u5b9e\u662f\u4e5d\u725b\u4e00\u6bdb\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u5e72\u4e86\u4e00\u676f\u9152\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5fc3\u5012\u4e0d\u5c0f\uff0c\u60f3\u53d1\u6211\u8fd9\u6837\u7684\u5927\u8d22\uff0c\u53ef\u77e5\u9053\u662f\u4e00\u4ef6\u591a\u4e48\u56f0\u96be\u7684\u4e8b?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u77e5\u9053\u90a3\u5f88\u56f0\u96be\uff0c\u6240\u4ee5\uff0c\u6211\u8981\u6d17\u624b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u5f88\u806a\u660e\uff0c\u6025\u6d41\u52c7\u9000\uff0c\u4fdd\u7559\u4e86\u4f60\u7684\u9752\u6625\uff0c\u5176\u5b9e\uff0c\u4e00\u4e2a\u5973\u4eba\u7684\u7f8e\u4e3d\uff0c\u624d\u662f\u6700\u5927\u7684\u672c\u94b1\uff0c\u53c8\u4f55\u82e6\u8981\u53bb\u62da\u547d\u5462?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u624d\u6295\u5165\u5929\u4e0b\u6700\u5b89\u5168\uff0c\u6700\u6709\u5a01\u52bf\u7684\u7b2c\u4e00\u5821\u6765\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u6709\u4e9b\u4e0d\u660e\u767d\uff0c\u5c31\u4e3a\u4e86\u8981\u8d2a\u6211\u7b2c\u4e00\u5821\u9526\u8863\u7389\u98df\uff0c\u8d35\u59ca\u59b9\u624d\u6295\u5165\u7b2c\u4e00\u5821\u6765?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u90a3\u53ea\u662f\u8868\u9762\u4e0a\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u5185\u5728\u7684\u539f\u56e0\u5462?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u4eec\u62db\u60f9\u4e86\u4e00\u4e2a\u4e0d\u8be5\u62db\u60f9\u7684\u4eba!\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e03\u5251\u8ffd\u9b42\u53f6\u957f\u9752\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u5e78\u597d\u4f60\u8bf4\u4e86\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u5982\u53ea\u60f3\u82b1\u8a00\u5de7\u8bed\uff0c\u60f3\u9a97\u8fc7\u6211\uff0c\u6211\u4f1a\u5bf9\u4f60\u4eec\u59ca\u59b9\u751f\u51fa\u6000\u7591\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u751f\u51fa\u6000\u7591\u4e4b\u540e\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u4eec\u6c38\u8fdc\u4e0d\u80fd\u79bb\u5f00\u8fd9\u70df\u9501\u697c\u4e0b\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6740\u4e86\u6211\u4eec?\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u7231\u62a4\u5bf9\u6211\u5fe0\u5fc3\u4e0d\u4e8c\u7684\u4eba\uff0c\u4f46\u4e5f\u4f1a\u5bf9\u4ed8\u6b3a\u9a97\u6211\u7684\u4eba\u3002\u201d \u3000\u3000\u7389\u7fe0\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u770b\u6765\uff0c\u5728\u4f60\u7684\u9762\u524d\uff0c\u771f\u8981\u8001\u5b9e\u4e00\u4e9b\uff0c\u4e00\u4e24\u53e5\u73a9\u7b11\u4e4b\u8a00\uff0c\u4e5f\u53ef\u80fd\u62db\u6765\u4e86\u6740\u8eab\u4e4b\u7978\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7389\u7fe0\uff0c\u65f6\u5019\u4e0d\u65e9\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u4eec\u662f\u4e0d\u662f\u5e94\u8be5\u4f11\u606f\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5e0c\u671b\u4e00\u591c\u7684\u7f20\u7ef5\u4e4b\u540e\uff0c\u4f60\u4f1a\u559c\u6b22\u8fd9\u91cc\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u597d\u50cf\uff0c\u6211\u4eec\u4e5f\u522b\u65e0\u6cd5\u53bb\u5904\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5982\u82e5\u4f60\u4eec\u4e24\u59ca\u59b9\u53ea\u60f3\u627e\u4e00\u4e2a\u5b89\u4e50\u7a9d\uff0c\u4f3c\u4e4e\u662f\u4f60\u4eec\u627e\u5bf9\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\uff0c\u6211\u4eec\u5728\u88ab\u8feb\u9003\u4ea1\u7684\u65f6\u5019\uff0c\u786e\u7136\u53ea\u60f3\u627e\u4e00\u4e2a\u53ef\u4ee5\u5b89\u8eab\u7acb\u547d\u7684\u5b89\u4e50\u7a9d\uff0c\u4f46\u6211\u73b0\u5728\uff0c\u53c8\u60f3\u5230\u4e86\u4e00\u4ef6\u4e8b\uff0c\u4e00\u65e6\u8fc7\u4e86\u5e73\u9759\u7684\u65e5\u5b50\u4e4b\u540e\uff0c\u4f1a\u4e0d\u4f1a\u6545\u6001\u590d\u840c\uff0c\u53c8\u60f3\u5728\u6c5f\u6e56\u4e0a\u95ef\u8361\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u5973\u4eba\u672c\u6765\u662f\u5173\u5728\u7b3c\u4e2d\u7684\u9e1f\u513f\uff0c\u4f46\u6709\u4e9b\u9e1f\u513f\u5374\u662f\u4e0d\u9002\u5408\u88ab\u5173\u5728\u7b3c\u4e2d\uff0c\u6709\u4e9b\u4eba\u4e5f\u4e0d\u9002\u5408\u6c38\u8fdc\u4f4f\u5728\u5bb6\u4e2d\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u4eec\u4e24\u59ca\u59b9\uff0c\u5c31\u662f\u8fd9\u6837\u7684\u4eba\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u9519\u554a!\u5728\u5973\u4eba\u4e2d\uff0c\u4f60\u4eec\u5e94\u8be5\u662f\u5f88\u51fa\u8272\u7684\u4eba\u7269\uff0c\u7f8e\u4e3d\u3001\u806a\u660e\uff0c\u518d\u52a0\u4e0a\u4e00\u8eab\u6b66\u529f\uff0c\u4f46\u4f60\u4eec\u9009\u9519\u4e86\u884c\u4e1a\uff0c\u7adf\u7136\u505a\u4e86\u6740\u624b\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\uff0c\u89c9\u7740\u6211\u4eec\u5e94\u8be5\u5e72\u54ea\u4e00\u884c\u624d\u5bf9\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u4e2a\u4e48?\u4f60\u4eec\u53ef\u4ee5\u9690\u4e8e\u5e02\u4e95\uff0c\u5ac1\u4e00\u4e2a\u5bcc\u8d35\u4eba\u5bb6\uff0c\u6df1\u85cf\u4e86\u81ea\u5df1\u4e00\u8eab\u6b66\u529f\uff0c\u975e\u4e0d\u5f97\u5df2\u65f6\uff0c\u4e0d\u8f7b\u6613\u65bd\u7528\u51fa\u6765\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u53ef\u60dc\u7684\u662f\uff0c\u8fd1\u6731\u8005\u8d64\uff0c\u8fd1\u58a8\u8005\u9ed1\uff0c\u6211\u4eec\u4e24\u59ca\u59b9\u5df2\u7ecf\u662f\u4e00\u4e2a\u6740\u624b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8fd8\u4e0d\u9519\uff0c\u7537\u6015\u5165\u9519\u884c\uff0c\u5973\u6015\u5ac1\u9519\u90ce\uff0c\u4f60\u4eec\u73b0\u5728\u8fd8\u6ca1\u6709\u5ac1\u4eba\u3002\u201d \u3000\u3000\u7389\u7fe0\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5ac1\u4eba\uff0c\u662f\u4e00\u4ef6\u5f88\u56f0\u96be\u7684\u4e8b\uff0c\u8981\u6211\u4eec\u7684\uff0c\u6211\u4eec\u672a\u5fc5\u4f1a\u5ac1\uff0c\u6211\u4eec\u60f3\u5ac1\u7684\u4eba\uff0c\u4eba\u5bb6\u672a\u5fc5\u4f1a\u8981\uff0c\u8fd9\u5b9e\u5728\u662f\u4e00\u4ef6\u6839\u56f0\u96be\u7684\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u9053\uff1a\u201c\u95ee\u9898\u662f\u4f60\u5df2\u5e72\u8fc7\u6740\u624b\uff0c\u4eca\u540e\uff0c\u5df2\u7ecf\u65e0\u6cd5\u518d\u5b89\u4e8e\u5ba4\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u8bf4\u7684\u662f\uff0c\u6b63\u5982\u4f60\u5821\u4e3b\u6240\u8bf4\uff0c\u6211\u4eec\u662f\u806a\u660e\u4eba\uff0c\u6211\u4eec\u5df2\u7ecf\u65e0\u6cd5\u518d\u5584\u5c3d\u4e00\u4e2a\u5973\u4eba\u7684\u8d23\u4efb\u4e86\u3002\u6240\u4ee5\uff0c\u6211\u4eec\u624d\u6765\u6295\u5954\u9ad8\u5821\u4e3b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6ca1\u6709\u9519\u554a!\u8fd9\u662f\u4e00\u79cd\u5f88\u660e\u667a\u7684\u9009\u62e9\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6211\u5f88\u62c5\u5fc3\uff0c\u6211\u4eec\u4e00\u65e6\u518d\u6709\u91cd\u51fa\u6c5f\u6e56\u4e4b\u60f3\uff0c\u4f1a\u4e0d\u4f1a\u5f15\u8d77\u4f60\u9ad8\u5821\u4e3b\u7684\u6012\u706b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u4f1a\uff0c\u6211\u662f\u5f88\u660e\u9053\u7406\u7684\u7537\u4eba\u3002\u201d \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u6211\u770b\uff0c\u4f60\u8fd9\u70df\u9501\u697c\u4e0a\u7684\u91cd\u91cd\u5e03\u7f6e\uff0c\u53ea\u6015\u5f88\u96be\u5141\u8bb8\u4eba\u91cd\u51fa\u6c5f\u6e56\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u53ea\u662f\u5bf9\u4ed8\u4e00\u822c\u5973\u4eba\uff0c\u50cf\u4f60\u4eec\u59ca\u59b9\uff0c\u5e94\u8be5\u4e0d\u53d7\u8fd9\u79cd\u7ea6\u675f\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u54e6!\u6211\u4eec\u5f88\u7279\u6b8a?\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u4eec\u662f\u7f8e\u5973\uff0c\u9700\u8981\u95fa\u623f\u4e2d\u7684\u67d4\u60c5\u871c\u610f\uff0c\u4f46\u4f60\u4eec\u66f4\u9700\u8981\u6c5f\u6e56\u4e0a\u7eb5\u6a2a\u6765\u53bb\uff0c\u8fd9\u5c31\u4e0d\u662f\u4efb\u4f55\u7537\u4eba\u53ef\u4ee5\u529e\u5230\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4f60\u80fd\u529e\u5230?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u80fd\uff0c\u4f60\u4eec\u662f\u6211\u7684\u7ea2\u7c89\u871c\u53cb\uff0c\u4f46\u4e5f\u662f\u6211\u7b2c\u4e00\u5821\u4e2d\u7684\u6740\u624b\uff0c\u7b2c\u4e00\u5821\u5c06\u4f1a\u652f\u6301\u4f60\u4eec\u91cd\u51fa\u6c5f\u6e56\u7684\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u522b\u4eba\uff0c\u6211\u4eec\u59ca\u59b9\u90fd\u4e0d\u4f1a\u653e\u5728\u5fc3\u4e0a\uff0c\u53ea\u6015\u4e03\u5251\u8ffd\u9b42\u53f6\u957f\u9752\u4e0d\u4f1a\u653e\u8fc7\u6211\u4eec\u3002\u201d \u9ad8\u5929\u5065\u7b11\u9053\uff1a\u201c\u53f6\u957f\u9752\u662f\u5f88\u96be\u5bf9\u4ed8\u7684\u4eba\uff0c\u4e5f\u7684\u786e\u53ef\u6015\uff0c\u542c\u8bf4\u4ed6\u5df2\u5251\u4e0d\u7559\u547d\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e9b\u90fd\u5df2\u7ecf\u8fc7\u53bb\u4e86\uff0c\u4eca\u540e\u4e0d\u7528\u518d\u6015\u4ed6\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u8fc7\u53bb\uff0c\u53ea\u662f\u4f60\u4eec\u59ca\u59b9\u4e24\u4e2a\uff0c \u81ea\u7136\u662f\u65e0\u6cd5\u5bf9\u4ed8\u4ed6\u4e86\uff0c\u4f46\u73b0\u5728\u4e0d\u540c\u4e86\uff0c\u73b0\u5728\uff0c\u5728\u4f60\u4eec\u7684\u80cc\u540e\uff0c\u6709\u4e00\u4e2a\u5f3a\u5927\u7684\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u7389\u7fe0\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u6c5f\u6e56\u4e0a\u5f3a\u7684\u7ec4\u7ec7\u5f88\u591a\uff0c\u7b2c\u4e00\u5821\u53c8\u80fd\u5f3a\u5927\u5230\u4ec0\u4e48\u7a0b\u5ea6\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u9053\uff1a\u201c\u653e\u773c\u5f53\u4eca\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u80fd\u591f\u548c\u7b2c\u4e00\u5821\u6297\u62d2\u7684\u9ad8\u624b\uff0c\u53ea\u6015\u5f88\u96be\u627e\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u8db3\u4e3a\u654c\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u542c\u8bf4\u6709\u4e00\u4e2a\u9634\u9633\u5821\uff0c\u5b9e\u529b\u975e\u5e38\u5f3a\u5927\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u4f60\u7684\u6d88\u606f\u5f88\u7075\u901a\uff0c\u653e\u773c\u6c5f\u6e56\uff0c\u90a3\u9634\u9633\u5821\u662f\u552f\u4e00\u53ef\u548c\u6211\u9889\u9883\u4e00\u4e0b\u7684\u7ec4\u7ec7\uff0c\u4f46\u4e5f\u5c06\u6210\u8fc7\u53bb\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u8fd9\u8bdd\u600e\u4e48\u8bf4?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u73b0\u5728\uff0c\u8001\u592b\u8fd8\u5728\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u5feb\u5219\u4e09\u4e94\u65e5\uff0c\u8fdf\u5219\u534a\u6708\uff0c\u6211\u5c31\u4f1a\u628a\u8fd9\u4e2a\u7ec4\u7ec7\u5b8c\u5168\u51fb\u6e83\uff0c\u5e76\u4e14\uff0c\u6740\u4e86\u4ed6\u4eec\u7684\u4e3b\u8111\u4eba\u7269\u3002\u201d \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u771f\u7684\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8001\u592b\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u600e\u80fd\u4fe1\u53e3\u5f00\u6cb3\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u542c\u8bf4\uff0c\u4e03\u5251\u8ffd\u9b42\u4e5f\u5728\u9634\u9633\u5821\u4e2d?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5bf9\uff0c\u4ed6\u662f\u9634\u9633\u5821\u4e2d\u4e94\u5927\u5251\u4f7f\u4e4b\u4e00\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u6740\u4e86\u53f6\u957f\u9752\uff0c\u6211\u4eec\u5c31\u6ca1\u6709\u540e\u987e\u4e4b\u5fe7\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u8fd9\u4e0d\u8fc7\u662f\u4e09\u4e94\u5929\u4e2d\u7684\u4e8b\uff0c\u4f60\u53ef\u4ee5\u7b49\u4faf\u597d\u6d88\u606f\u4e86\u3002\u201d \u7389\u7fe0\u5fc3\u4e2d\u6697\u9053\uff1a \u201c\u5e78\u597d\u53ea\u6211\u4e00\u4eba\u5728\u573a\uff0c\u5982\u662f\u5170\u767d\u4e5f\u5728\u6b64\u5730\uff0c\u5fc5\u5c06\u8138\u8272\u5927\u53d8\u3002\u201d \u53ea\u542c\u9ad8\u5929\u5065\u63a5\u9053\uff1a \u201c\u8fd9\u4ef6\u4e8b\uff0c\u8bf4\u5230\u6b64\u5904\uff0c\u6211\u5012\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff0c\u660e\u5929\uff0c\u6211\u5c06\u5f00\u59cb\u8981\u5fd9\u788c\u51e0\u5929\uff0c\u53ea\u6015\u4e0d\u80fd\u770b\u4f60\u4eec\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u5fc3\u4e2d\u5fd6\u9053\uff1b\u770b\u8d77\u6765\uff0c\u5170\u767d\u7684\u8fd0\u6c14\uff0c\u8fd8\u4e0d\u7b97\u592a\u574f\u3002 \u3000\u3000\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u5728\u5916\u7559\u505c\u591a\u4e45?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u4f1a\u592a\u4e45\uff0c\u4e09\u4e24\u5929\u603b\u662f\u8981\u7684\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u5f88\u91cd\u5927\u7684\u4e8b?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5bf9\uff0c\u91cd\u5927\u5230\u5173\u7cfb\u5230\u4f60\u7684\u751f\u6b7b\uff0c\u8001\u592b\u7684\u6210\u8d25\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\uff0c\u5982\u82e5\u7b2c\u4e00\u5821\u5b9e\u529b\u548c\u9634\u9633\u5821\u96be\u5206\u8f7b\u91cd\u5462?\u201d \u3000\u3000\u9ad8\u5065\u9053\uff1a\u201c\u6211\u544a\u8bc9\u4f60\uff0c\u8fd9\u662f\u7edd\u4e0d\u53ef\u80fd\u7684\u4e8b\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u6625\u5bb5\u4e00\u523b\u503c\u5e72\u91d1\uff0c\u54b1\u4eec\u8be5\u7761\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u65e9\u5df2\u7ecf\u51c6\u5907\u727a\u7272\uff0c\u6240\u4ee5\uff0c\u95fb\u8a00\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u597d\u5427!\u5e0c\u671b\u9ad8\u5821\u4e3b\u4e0d\u8981\u5acc\u5f03\u6211\u6b8b\u82b1\u8d25\u67f3\u3002\u201d \u3000\u3000\u7389\u7fe0\u65bd\u51fa\u4e86\u6d51\u8eab\u89e3\u6570\u3002 \u3000\u3000\u9ad8\u5929\u5065\u867d\u7136\u662f\u82b1\u4e1a\u8001\u624b\uff0c\u4e5f\u88ab\u7389\u7fe0\u6446\u5e03\u7684\u670d\u670d\u8d34\u8d34\u3002 \u3000\u3000\u76f4\u5230\u65e5\u5347\u4e09\u7aff\uff0c\u9ad8\u98de\u8679\u8c61\u7af9\u7aff\u4f3c\u7684\uff0c\u76f4\u76f4\u6811\u7acb\u5728\u94c1\u6805\u95e8\u5916\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5446\u5b50\u4e00\u5446\uff0c\u9053\uff1a\u201c\u98de\u8679\uff0c\u4f60\u4e00\u591c\u6ca1\u6709\u7761\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u6211\u7761\u4e0d\u7740\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\uff0c\u5012\u662f\u6267\u7740\u5f97\u5f88\u3002 \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u98de\u8679\uff0c\u7239\u7ed9\u4f60\u9009\u4e00\u4e2a\u5ab3\u5987\uff0c\u4fdd\u8bc1\u662f\u59ff\u5bb9\u7edd\u4e16\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u63a5\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u53ea\u8981\u5434\u53cc\u5973\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u4f60\u5148\u56de\u53bb\u6b47\u7740\uff0c\u6211\u53bb\u770b\u770b\u5979\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u96f7\u65e0\u5f71\u4e0d\u4f1a\u653e\u8fc7\u5230\u53e3\u4e2d\u7684\u4e00\u5757\u80a5\u8089\uff0c\u7239\u5982\u6628\u591c\u80af\u53bb\uff0c\u5c31\u4e0d\u4f1a\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u795e\u8272\u51b7\u5cfb\uff0c\u63a5\u9053\uff1a\u201c\u98de\u8679\uff0c\u4f60\u4e00\u5b9a\u8981\u5434\u53cc\u5973\uff0c\u7239\u53ef\u4ee5\u53bb\u548c\u96f7\u5802\u4e3b\u5546\u91cf\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u9ad8\u98de\u8679\u63a5\u9053\uff1a \u201c\u548c\u4ed6\u5546\u91cf?\u7239\u53ea\u8981\u4e0b\u4e00\u9053\u4ee4\u8c15\uff0c\u4ed6\u8fd8\u6562\u4e0d\u6545\u4eba\u5417?\u201d \u3000\u3000\u78b0\u4e0a\u8fd9\u6837\u5b9d\u8d1d\u7684\u513f\u5b50\uff0c\u9ad8\u5929\u5065\u4e5f\u5b9e\u5728\u6ca1\u6709\u6cd5\u5b50\uff0c\u53ea\u597d\u8010\u7740\u6027\u5b50\uff0c\u53f9\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u6211\u662f\u6709\u8fd9\u4e2a\u6743\u5a01\uff0c\u53ea\u8981\u6211\u4e0b\u4e00\u9053\u4ee4\u8c15\uff0c\u6211\u76f8\u4fe1\u4ed6\u4f1a\u653e\u4eba\uff0c\u4e0d\u8fc7\uff0c\u5b69\u5b50\uff0c\u7239\u5df2\u7ecf\u7b54\u5e94\u8fc7\u96f7\u65e0\u5f71\uff0c\u628a\u5434\u53cc\u5973\u8d4f\u8d50\u7ed9\u4ed6\uff0c\u96be\u9053\u4f60\u8981\u7239\u51fa\u5c14\u53cd\u5c14\uff0c\u8bf4\u4e86\u4e0d\u7b97\u5417?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u7239\uff0c\u4f60\u5bf9\u96f7\u65e0\u5f71\u5982\u6b64\u7684\u4f18\u6e25\u3001\u5ba0\u4fe1\uff0c\u96be\u9053\u5c31\u4e0d\u6015\u4ed6\u52bf\u529b\u80c0\u5927\u4e86\u4e4b\u540e\uff0c\u4f1a\u80cc\u53db\u4f60\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u542c\u5f97\u4e00\u5446\u3002 \u3000\u3000\u9ad8\u98de\u8679\u8fd9\u5c0f\u5b50\u6311\u62e8\u7684\u624b\u6bb5\uff0c\u5012\u662f\u5341\u5206\u5230\u5bb6\u3002 \u3000\u3000\u6709\u4e9b\u4eba\uff0c\u6ca1\u6709\u6210\u4e8b\u4e4b\u80fd\uff0c\u5374\u6709\u7740\u5f88\u5927\u7684\u8d25\u4e8b\u4e4b\u80fd\u3002 \u3000\u3000\u9ad8\u98de\u8679\u5c31\u662f\u8fd9\u6837\u7684\u4eba\u3002 \u3000\u3000\u9ad8\u5929\u5065\u51fa\u4e86\u4e00\u9635\u5b50\u7684\u795e\uff0c\u9053\uff1a \u201c\u5b69\u5b50\uff0c\u4f60\u7684\u8bdd\u4e5f\u6709\u9053\u7406\uff0c\u4e0d\u8fc7\uff0c\u76ee\u4e0b\u54b1\u4eec\u7b2c\u4e00\u5821\u6b63\u5728\u7528\u4eba\u4e4b\u9645\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4e0d\u80fd\u6a2a\u751f\u679d\u8282\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8fc7\u53bb\u5bf9\u5434\u53cc\u5973\uff0c\u65bd\u6069\u751a\u6df1\uff0c\u5982\u82e5\u7239\u80af\u653e\u7eb5\uff0c\u5979\u65e9\u5c31\u53d8\u6210\u4e86\u4f60\u7684\u5ab3\u5987\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u4f60\u8ba4\u4e3a\u4f60\u80fd\u5f97\u5230\u5434\u53cc\u5973\uff0c\u54fc!\u4e0d\u662f\u8001\u7239\u8fd9\u4e2a\u9762\u5b50\u6297\u4f4f\uff0c\u53ea\u6015\u4f60\u65e9\u5df2\u6b7b\u5728\u5979\u624b\u4e2d\u4e86\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u6211\u4e0d\u662f\u5979\u7684\u654c\u624b\uff0c\u4e0d\u8fc7\uff0c\u6211\u53ef\u4ee5\u7528\u624b\u6bb5\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u624b\u6bb5\uff0c\u4ec0\u4e48\u624b\u6bb5?\u201d\u9ad8\u98de\u8679\u9053\uff1a\u201c\u8ff7\u836f\uff0c\u5434\u53cc\u5973\u60f3\u4e0d\u5230\u6211\u4f1a\u7528\u8ff7\u836f\u8ff7\u5979\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u54fc!\u5979\u9192\u6765\u4e4b\u540e\uff0c\u8fd8\u4e0d\u662f\u53ef\u4ee5\u6740\u4f60\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a \u201c\u7239\uff0c\u770b\u6765\u4f60\u8fd8\u662f\u4e0d\u592a\u4e86\u89e3\u5973\u4eba\uff0c\u5979\u9192\u6765\u4e4b\u540e\uff0c\u5df2\u7ecf\u662f\u751f\u7c73\u716e\u6210\u4e86\u719f\u996d\uff0c\u5c31\u7b97\u5979\u5fc3\u4e2d\u6068\u6211\uff0c\u4e5f\u4e0d\u4f1a\u6740\u4e86\u6211\u3002\u65e5\u540e\uff0c\u6211\u518d\u7528\u4e00\u70b9\u6c34\u78e8\u7684\u529f\u592b\uff0c\u8fd8\u6015\u5979\u4e0d\u4e56\u4e56\u505a\u4f60\u7684\u513f\u5ab3\u5987\u4e86\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u9ad8\u98de\u8679\u63a5\u9053\uff1a\u201c\u53ea\u53ef\u60dc\uff0c\u73b0\u5728\u8bf4\u8fd9\u4e9b\u5df2\u7ecf\u665a\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u4e3a\u7236\u4e5f\u5728\u62c5\u5fc3\uff0c\u96f7\u65e0\u5f71\u4e0d\u80af\u7b54\u5e94\uff0c\u90a3\u53c8\u8be5\u5982\u4f55?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u529e\u6cd5\uff0c\u5012\u662f\u6709\u4e00\u4e2a\uff0c\u53ea\u662f\u6211\u4e0d\u6562\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd8\u6709\u4f60\u4e0d\u6562\u8bf4\u7684\u4e8b\u5417?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u7239\u4e00\u5b9a\u8981\u6211\u8bf4\uff0c\u8bf4\u51fa\u6765\uff0c\u5e0c\u671b\u4f60\u4e0d\u8981\u751f\u6c14\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u54e6!\u4e0d\u8981\u7d27\uff0c\u4f60\u53ea\u7ba1\u8bf4\u5427!\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u96f7\u65e0\u5f71\u9664\u4e86\u559c\u6b22\u5434\u53cc\u5973\u4e4b\u5916\uff0c\u8fd8\u559c\u6b22\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8c01?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u662f\u5e7d\u5170\u59b9\u59b9\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u6211\u8bf4\u5e7d\u5170\u59b9\u59b9\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u755c\u751f\uff0c\u8fd9\u8bdd\u4f60\u4e5f\u80fd\u8bf4\u5f97\u51fa\u53e3?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u7239\uff0c\u4f60\u80fd\u7ed9\u5979\u5403\u4e0b\u5931\u5fc3\u4e39\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u628a\u5979\u5ac1\u7ed9\u96f7\u65e0\u5f71\uff0c\u8fd9\u53ef\u4ee5\u4f7f\u5f97\u96f7\u65e0\u5f71\u5bf9\u4f60\u66f4\u5fe0\u5fc3\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u539f\u672c\u6709\u4e9b\u6fc0\u52a8\uff0c\u4f46\u5f88\u5feb\u7684\uff0c\u4ed6\u7684\u60c5\u7eea\u5c31\u5e73\u9759\u4e86\u4e0b\u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u60f3\u7684\u5f88\u5982\u610f\uff0c\u53ef\u662f\u8fd9\u4e2d\u95f4\uff0c\u6709\u4e00\u4e2a\u6700\u5927\u95ee\u9898\uff0c\u4f60\u60f3\u901a\u4e86\u6ca1\u6709?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u4ec0\u4e48\u95ee\u9898?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u613f\u610f\u8981\u4e00\u4e2a\u5931\u53bb\u5fc3\u5fd7\u7684\u5973\u4eba\u5417?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u96f7\u65e0\u5f71\u4e5f\u4e0d\u613f\u610f\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u5979\u4eec\u4e24\u4e2a\u4eba\uff0c \u53ea\u8981\u6e05\u9192\u8fc7\u6765\uff0c\u5fc5\u7136\u4f1a\u627e\u4f60\u4eec\u62da\u547d\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u7239\u65e0\u6240\u4e0d\u80fd\uff0c\u53ef\u4ee5\u6682\u65f6\u5e9f\u4e86\u5979\u4eec\u6b66\u529f\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u98de\u8679\uff0c\u5012\u4e8f\u4f60\u60f3\u51fa\u8fd9\u6837\u7684\u529e\u6cd5\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4ef6\u4e8b\u884c\u4e0d\u901a\u3002\u201d \u9ad8\u98de\u8679\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u4ed6\u4eec\u4e24\u4e2a\u4eba\u7684\u6b66\u529f\uff0c\u5341\u5206\u9ad8\u5f3a\uff0c\u662f\u4e24\u4e2a\u6700\u597d\u7684\u6740\u624b\uff0c\u5e9f\u4e86\u5979\u4eec\u7684\u6b66\u529f\uff0c \u5c82\u4e0d\u662f\u53ef\u60dc\u5f97\u5f88!\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a \u201c\u4f60\u5728\u795e\u5200\u5802\uff0c\u5b89\u6392\u4e86\u4e0d\u5c11\u7684\u6740\u624b\uff0c\u96be\u9053\u8fd8\u4f1a\u628a\u4e24\u4e2a\u4eba\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u8ba9\u6211\u6162\u6162\u7684\u60f3\u60f3\u518d\u8bf4\uff1b\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a \u201c\u4e0d\u80fd\u6162\u6162\u60f3\u4e86\uff0c\u5434\u53cc\u5973\u5df2\u7ecf\u843d\u5230\u4e86\u96f7\u65e0\u5f71\u7684\u624b\u4e2d\uff0c\u7239\uff0c\u518d\u4e0d\u60f3\u529e\u6cd5\uff0c\u5979\u53ef\u80fd\u5c31\u53d8\u6210\u4e86\u96f7\u592b\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u5b69\u5b50\uff0c\u5982\u82e5\u5979\u8981\u53d8\u6210\u96f7\u592b\u4eba\uff0c \u53ea\u6015\u65e9\u5df2\u7ecf\u53d8\u6210\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u53e5\u8bdd\uff0c\u5bf9\u9ad8\u98de\u8679\u6709\u7740\u5f88\u5927\u7684\u523a\u6fc0\uff0c\u5fcd\u4e0d\u4f4f\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u5feb\u53bb\u628a\u5434\u53cc\u5973\u5e26\u51fa\u6765\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u8138\u8272\u4e00\u53d8\uff0c\u4f3c\u60f3\u53d1\u4f5c\uff0c\u4f46\u8fd9\u6bd5\u7adf\u662f\u4ed6\u4eb2\u751f\u7684\u9aa8\u8089\uff0c\u552f\u4e00\u7684\u513f\u5b50\uff0c\u9ad8\u5929\u5065\u867d\u7136\u6b8b\u66b4\u3001\u9634\u9669\uff0c\u4f46\u4e5f\u4e0d\u5fcd\u5bf9\u9ad8\u98de\u8679\u592a\u8fc7\u51b7\u5389\uff0c\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\u5427!\u4f60\u5148\u56de\u53bb\uff0c\u6211\u53bb\u795e\u5200\u5802\u770b\u770b\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u795e\u60c5\u8083\u7136\u5730\u8bf4\u9053\uff1a \u201c\u7239\uff0c\u6211\u7b49\u4f60\uff0c\u5f97\u4e0d\u5230\u5434\u53cc\u5973\uff0c\u6211\u4f1a\u6d3b\u4e0d\u4e0b\u53bb\u3002\u201d \u8f6c\u8eab\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u9ad8\u5929\u5065\u671b\u7740\u9ad8\u98de\u8679\u7684\u80cc\u5f71\uff0c \u51fa\u4e86\u4e00\u9635\u5b50\u795e\uff0c \u53c8\u8f7b\u8f7b\u53f9\u4e00\u53e3\u6c14\uff0c\u4e3e\u6b65\u884c\u5411\u795e\u5200\u5802\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u6b63\u5750\u5728\u795e\u5200\u5802\u5927\u5385\u4e2d\u53d1\u5446\u3002 \u3000\u3000\u5230\u53e3\u7684\u80a5\u8089\u4f1a\u8dd1\uff0c\u716e\u719f\u7684\u9e2d\u5b50\u4f1a\u98de\u3002 \u3000\u3000\u4ed6\u60f3\u5360\u6709\u5434\u53cc\u5973\uff0c\u60f3\u4e0d\u5230\u7adf\u4f1a\u5728\u6700\u91cd\u8981\u7684\u65f6\u95f4\uff0c\u51fa\u4e86\u5c94\u5b50\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4e8b\u60c5\u5341\u5206\u7b80\u5355\uff0c\u5434\u53cc\u5973\u6839\u672c\u795e\u5fd7\u6e05\u9192\uff0c\u81ea\u7136\u4e0d\u4f1a\u8ba9\u4ed6\u5f97\u624b\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5343\u7b97\u4e07\u7b97\uff0c\u5c31\u662f\u6ca1\u6709\u7b97\u5230\uff0c\u5434\u53cc\u5973\u672a\u66fe\u670d\u4e0b\u5931\u5fc3\u4e39\u3002 \u3000\u3000\u4ed6\u4e0b\u4ee4\u5c01\u9501\u4e86\u795e\u5200\u5802\uff0c\u6d3e\u51fa\u4e86\u56db\u4e2a\u4eb2\u4fe1\uff0c\u5b88\u5728\u795e\u5200\u5802\u7684\u5927\u95e8\u53e3\u5904\uff0c\u4e0d\u8bba\u4ec0\u4e48\u4eba\uff0c\u90fd\u4e0d\u8bb8\u8fdb\u6765\u3002 \u3000\u3000\u9ad8\u98de\u8679\u60ca\u6270\u4e86\u4ed6\u7684\u597d\u4e8b\uff0c\u4ed6\u4e0d\u5e0c\u671b\u518d\u6765\u4e00\u6b21\u6405\u5c40\u3002 \u3000\u3000\u8fd9\u4e5f\u7ed9\u4e86\u5434\u53cc\u5973\u5f88\u597d\u7684\u673a\u4f1a\uff0c\u5f53\u4ed6\u8272\u6001\u85b0\u5fc3\uff0c\u8131\u4e0b\u4e86\u81ea\u5df1\u7684\u8863\u670d\u65f6\uff0c\u5434\u53cc\u5973\u5374\u7a81\u7136\u51fa\u624b\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u6454\u5012\u5728\u5730\u4e0a\u7684\u58f0\u97f3\u5f88\u54cd\uff0c\u5982\u82e5\u5728\u5e73\u65f6\uff0c\u4e00\u5b9a\u4f1a\u6709\u4eba\u542c\u5230\uff0c\u4f46\u73b0\u5728\uff0c\u96f7\u65e0\u5f71\u5374\u9063\u8d70\u4e86\u5b88\u5728\u5ba4\u5916\u7684\u4eba\u3002 \u3000\u3000\u5434\u53cc\u5973\u8fa3\u624b\u903c\u4f9b\uff0c\u8feb\u4f7f\u96f7\u65e0\u5f71\u8bf4\u51fa\u4e86\u4f55\u5bd2\u8863\u548c\u9ad8\u5e7d\u5170\u7684\u4f4f\u5904\uff0c\u7136\u540e\uff0c\u7528\u96f7\u65e0\u5f71\u7684\u968f\u8eab\u91d1\u724c\uff0c\u6551\u51fa\u4e86\u4f55\u5bd2\u8863\u548c\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u5f88\u5947\u602a\u7684\u4e8b\u60c5\uff0c\u5f80\u5f80\u5728\u8fc7\u7a0b\u4e2d\u5341\u5206\u7b80\u5355\uff0c\u4f55\u5bd2\u8863\u548c\u9ad8\u5e7d\u5170\uff0c\u90fd\u4e5f\u795e\u5fd7\u6e05\u9192\uff0c\u5c31\u7b97\u6709\u4e9b\u5b88\u536b\uff0c\u4e5f\u6321\u4ed6\u4eec\u4e0d\u4f4f\uff0c\u4e3a\u4e86\u51c6\u5907\u4ed6\u4eec\u505a\u6740\u624b\uff0c\u6240\u4ee5\uff0c\u4e09\u4e2a\u4eba\u7684\u5175\u5203\u5c31\u653e\u5927\u5385\u4e2d\u7684\u5175\u5668\u67b6\u4e0a\u3002 \u3000\u3000\u79bb\u5f00\u795e\u5200\u5802\u4e4b\u524d\uff0c\u5434\u53cc\u5973\u5f3a\u8feb\u96f7\u65e0\u5f71\u670d\u4e0b\u4e00\u7c92\u836f\uff0c\u544a\u8bc9\u4ed6\uff0c\u90a3\u662f\u4e03\u65e5\u65ad\u9b42\u6563\uff0c\u6bd2\u6027\u4e0d\u53d1\u65f6\uff0c\u548c\u5e38\u4eba\u65e0\u5f02\uff0c\u4e00\u65e6\u53d1\u4f5c\uff0c\u7acb\u523b\u65ad\u9b42\uff0c\u7ea6\u597d\u4e86\u7b2c\u516d\u5929\u591c\u665a\u5b50\u65f6\uff0c\u5728\u7b2c\u4e00\u5821\u5916\uff0c\u4e00\u5904\u53cc\u67cf\u6811\u4e0b\u89c1\u9762\uff0c\u5434\u53cc\u5973\u7ed9\u4ed6\u89e3\u6bd2\u836f\uff0c\u4f46\u96f7\u65e0\u5f71\u81ea\u5df1\u7528\u4e00\u4e9b\u6761\u4ef6\u4ea4\u6362\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\uff0c\u53c8\u5229\u7528\u4ee4\u724c\uff0c\u9ece\u660e\u65f6\u5206\u79bb\u5f00\u4e86\u795e\u5200\u5802\u3002 \u3000\u3000\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\u90fd\u662f\u5728\u7b2c\u4e00\u5821\u4e2d\u957f\u5927\u7684\u4eba\uff0c\u9664\u4e86\u795e\u5200\u5802\u5185\u5979\u4eec\u5f88\u5c11\u6765\u4e4b\u5916\uff0c\u7b2c\u4e00\u5821\u5185\uff0c\u5979\u4eec\u6bd4\u96f7\u65e0\u5f71\u8fd8\u719f\u3002 \u3000\u3000\u7b2c\u4e00\u5821\u592a\u5927\u4e86\uff0c\u60f3\u8981\u627e\u4e09\u4e2a\u4eba\uff0c\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002 \u3000\u3000\u81ea\u7136\uff0c\u4e09\u4e2a\u4eba\u4e5f\u53ef\u80fd\u8fde\u591c\u79bb\u5f00\u4e86\u7b2c\u4e00\u5821\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5728\u4e09\u4eba\u79bb\u53bb\u4e4b\u540e\uff0c\u81ea\u884c\u8fd0\u6c14\u51b2\u7a74\uff0c\u82b1\u4e86\u8db3\u8db3\u6709\u4e00\u987f\u996d\u5de5\u592b\u4e4b\u4e45\uff0c\u624d\u7b97\u628a\u7a74\u9053\u51b2\u5f00\u3002 \u3000\u3000\u795e\u667a\u51b7\u9759\u4e0b\u6765\uff0c\u5f00\u59cb\u601d\u7d22\u8fd9\u4ef6\u4e8b\u60c5\u7684\u4e25\u91cd\u6027\uff0c\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\u3001\u4f55\u5bd2\u8863\uff0c\u9003\u79bb\u4e86\u795e\u5200\u5802\uff0c\u8fd9\u5b9e\u5728\u662f\u4e00\u4ef6\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u4e8b\u60c5\uff0c\u7adf\u7136\u662f\u53d1\u751f\u4e86\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u6709\u4e9b\u5947\u602a\uff0c\u5b9e\u5728\u60f3\u4e0d\u901a\uff0c\u4e3a\u4ec0\u4e48\u5979\u4f1a\u89e3\u53bb\u4e86\u8eab\u4e0a\u7684\u5931\u5fc3\u4e39\u6bd2\u6027\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u64a4\u56de\u4e86\u5b88\u536b\u5728\u5927\u95e8\u53e3\u7684\u4eb2\u4fe1\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5750\u5728\u5385\u4e2d\u51fa\u795e\u3002 \u3000\u3000\u4ed6\u62c5\u5fc3\u7684\u4e8b\u60c5\u7ec8\u4e8e\u6765\u4e34\u4e86\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7f13\u6b65\u884c\u4eba\u4e86\u5927\u5385\u4e2d\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5012\u8fd8\u6c89\u5f97\u4f4f\u6c14\uff0c\u9664\u4e86\u628a\u5b88\u536b\u90a3\u95f4\u5730\u4e0b\u5ba4\u7684\u4eba\uff0c\u6536\u62bc\u8d77\u6765\u4e4b\u5916\uff0c\u8fd8\u6ca1\u6709\u628a\u8fd9\u4ef6\u4e8b\uff0c\u5ba3\u5e03\u51fa\u53bb\u3002 \u3000\u3000\u9ad8\u5929\u5065\u4e00\u811a\u8e0f\u5165\u4e86\u5385\u95e8\uff0c\u96f7\u65e0\u5f71\u7acb\u523b\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u9ad8\u5929\u5065\u6253\u91cf\u4e86\u96f7\u65e0\u5f71\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6837\u4e86?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c5e\u4e0b\u60f6\u6050\u4e0d\u5b89\u7684\u5f88\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u4e0d\u8981\u548c\u98de\u8679\u4e00\u822c\u89c1\u8bc6\uff0c\u4ed6\u8fd8\u662f\u4e2a\u5b69\u5b50\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u5176\u5b9e\uff0c\u5c5e\u4e0b\u5982\u82e5\u542c\u4ece\u4e86\u5c11\u5821\u4e3b\u7684\u8bdd\uff0c\u4e5f\u5c31\u597d\u4e86\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u8981\u653e\u5728\u5fc3\u4e0a\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u8bdd\u6709\u8bed\u75c5\u3002\u201d \u3000\u3000\u4ed6\u6709\u6781\u9ad8\u7684\u8b66\u60d5\u4e4b\u5fc3\uff0c\u7acb\u65f6\u53e3\u6c14\u4e00\u53d8\uff0c\u9053\uff1a \u201c\u600e\u4e48?\u51fa\u4e86\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ed6\u4eec\u9003\u8d70\u4e86\u3002\u201d \u3000\u3000\u6709\u5982\u7a81\u7136\u88ab\u4eba\u5728\u524d\u80f8\u4e0a\uff0c\u91cd\u91cd\u7684\u6253\u4e86\u51e0\u62f3\u822c\uff0c\u9ad8\u5929\u5065\u5438\u4e00\u53e3\u6c14\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u9ad8\u5e7d\u5170\u548c\u5434\u53cc\u5973\u9003\u8d70\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u8fd8\u6709\u4f55\u5bd2\u8863\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u53ef\u80fd?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u662f\u7684\uff0c\u5c5e\u4e0b\u4e5f\u662f\u767e\u601d\u83ab\u89e3\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u53cc\u76ee\u4e2d\u795e\u5149\u66b4\u5c04\uff0c\u76ef\u6ce8\u5728\u96f7\u65e0\u5f71\u7684\u8138\u4e0a\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u670d\u8fc7\u5931\u5fc3\u4e39\u554a?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u5c5e\u4e0b\u7684\u6212\u5907\u624d\u4e0d\u591f\u68ee\u4e25\uff0c\u4f7f\u4ed6\u4eec\u501f\u673a\u800c\u9041\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5931\u5fc3\u4e39\u7684\u795e\u6548\uff0c\u4e0d\u670d\u89e3\u836f\uff0c\u7edd\u5bf9\u4e0d\u53ef\u80fd\u6062\u590d\u4f53\u80fd\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c5e\u4e0b\u4e5f\u8fd9\u4e48\u60f3\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5434\u53cc\u5973\u548c\u4f55\u5bd2\u8863\uff0c\u662f\u5426\u771f\u7684\u670d\u4e0b\u4e86\u5931\u5fc3\u4e39?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c5e\u4e0b\u770b\u5230\u4ed6\u4eec\u670d\u4e0b\uff0c\u4e5f\u770b\u5230\u836f\u6027\u53d1\u4f5c\uff0c\u624d\u628a\u4ed6\u4eec\u79fb\u79bb\u77f3\u7262\uff0c\u9001\u5165\u5bc6\u5ba4\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8c01\u7ed9\u4e86\u4ed6\u4eec\u7684\u89e3\u836f\uff1f\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5931\u5fc3\u4e39\u7684\u89e3\u836f\uff0c\u4e00\u5411\u7531\u5821\u4e3b\u4eb2\u81ea\u63a7\u5236\uff0c\u975e\u5f97\u5821\u4e3b\u4ee4\u8c15\uff0c\u8c01\u4e5f\u65e0\u6cd5\u53d6\u5230\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u6211\u5b9e\u5728\u60f3\u4e0d\u901a\uff0c\u8fd9\u89e3\u836f\u600e\u4f1a\u6d41\u843d\u51fa\u6765\uff0c\u96be\u9053\u5e7d\u5170\u90a3\u4e2b\u5934\uff0c\u65e9\u6709\u4e86\u5b89\u6392?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ed6\u4eec\u670d\u4e0b\u8fc7\u5931\u5fc3\u4e39\uff0c\u81f3\u5c11\uff0c\u9ad8\u5e7d\u5170\u662f\u5821\u4e3b\u770b\u8fc7\u5979\u670d\u7528\u4e0b\u53bb\u7684\uff0c\u5821\u4e3b\u4f55\u4e0d\u67e5\u67e5\u770b?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u975e\u8981\u67e5\u4e2a\u660e\u767d\u4e0d\u53ef\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ed6\u4eec\u5236\u670d\u4e86\u6211\uff0c\u53d6\u8d70\u4e86\u6211\u8eab\u4e0a\u4ee4\u724c\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u4e0d\u5982\u6b64\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u4f1a\u9003\u51fa\u795e\u5200\u5802\u4e86\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c5e\u4e0b\u65e0\u80fd\uff0c\u5c5e\u4e0b\u60ed\u6127\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u600e\u4e48\u9003\u7684?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\u548c\u4f55\u5bd2\u8863\u5148\u9003\u51fa\u6765\uff0c\u63a5\u5e94\u5434\u53cc\u5973\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u90a3\u65f6\uff0c\u5434\u53cc\u5973\u5728\u4ec0\u4e48\u5730\u65b9?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5728\u5c5e\u4e0b\u7684\u5367\u5ba4\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u8fd9\u4e00\u70b9\u5012\u662f\u636e\u5b9e\u800c\u8a00\u3002 \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u5f97\u5230\u4e86\u5979\u7684\u8eab\u4f53?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u8fd8\u6ca1\u6709\uff0c\u4e0d\u8fc7\u5c5e\u4e0b\u786e\u6709\u6b64\u610f\uff0c\u53ea\u662f\u88ab\u5c11\u5821\u4e3b\u6405\u4e86\u5c40\uff0c\u5c5e\u4e0b\u5fc3\u613f\u672a\u8fbe\uff0c\u5434\u53cc\u5973\u4e5f\u597d\u50cf\u6ca1\u6709\u4e2d\u6bd2\uff0c\u9ad8\u5e7d\u5170\u7a81\u7136\u51fa\u73b0\uff0c\u5c5e\u4e0b\u6b63\u8981\u52a8\u624b\u64d2\u5979\u65f6\uff0c\u5434\u53cc\u5973\u5374\u7a81\u7136\u51fa\u624b\u70b9\u4e86\u6211\u7684\u7a74\u9053\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u548c\u5979\u5355\u72ec\u76f8\u5904\u5f88\u4e45\uff0c\u7adf\u7136\u4e0d\u77e5\u5979\u795e\u5fd7\u6e05\u9192?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u4ed6\u4eec\u88c5\u5f97\u5f88\u597d\uff0c\u5982\u82e5\u6211\u77e5\u9053\uff0c\u65e9\u5c31\u628a\u5979\u5236\u670d\u4e86\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u4eec\u6839\u672c\u5c31\u6ca1\u6709\u670d\u4e0b\u5931\u5fc3\u4e39\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u9ad8\u5e7d\u5170\u4e0d\u662f\u88c5\u7684\u5427!\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5979\u4e0d\u662f\uff0c\u6211\u4eb2\u773c\u770b\u5230\u5979\u670d\u4e0b\u53bb\u7684\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u5947\u602a\u7684\u4e5f\u5c31\u5728\u6b64\u4e86\uff0c\u5c5e\u4e0b\u4e5f\u662f\u770b\u4ed6\u4eec\u670d\u4e0b\u53bb\u7684\u3002\u201d \u9ad8\u5929\u5065\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u6211\u4e0d\u660e\u767d\uff0c\u773c\u4e0b\u4e86\u5931\u5fc3\u4e39\u7684\u4eba\uff0c\u5c31\u7b97\u624b\u4e2d\u6709\u89e3\u836f\uff0c\u4e5f\u4e0d\u4f1a\u5403\u4e0b\u53bb\u554a\uff01\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u5c5e\u4e0b\u65e0\u80fd\uff0c\u613f\u9886\u8d23\u7f5a\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u65e0\u5f71\uff0c \u4f60\u8bf4\u7684\u4ec0\u4e48\u8bdd\uff0c\u8fd9\u4e5f\u8bb8\u4e0d\u662f\u4f60\u7684\u9519\uff0c\u9886\u7684\u4ec0\u4e48\u8d23\u7f5a\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c5e\u4e0b\u6ca1\u6709\u5c3d\u5230\u804c\u8d23\uff0c\u800c\u4e14\uff0c\u72af\u4e86\u8272\u6212\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u8272\u5982\u6709\u6212\uff0c\u5929\u4e0b\u7f8e\u5973\uff0c \u5c82\u4e0d\u90fd\u4f1a\u6709\u7ea2\u989c\u8584\u547d\u7684\u611f\u89c9\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a \u201c\u96f7\u5802\u4e3b\uff0c\u4f60\u542c\u7740\uff0c\u6211\u6765\u8fd9\u91cc\uff0c\u4e3b\u8981\u662f\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c5e\u4e0b\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u6211\u7b54\u5e94\u628a\u5434\u53cc\u5973\u8d4f\u7ed9\u4f60\uff0c\u8bdd\u662f\u6211\u8bf4\u7684\uff0c\u7edd\u4e0d\u66f4\u6539\uff0c\u4e0d\u8bba\u662f\u4ec0\u4e48\u4eba\uff0c\u90fd\u4e0d\u80fd\u548c\u4f60\u4e89\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u591a\u8c22\u5821\u4e3b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u8bb8\u8bfa\u4e5f\u6c38\u8fdc\u6709\u6548\uff0c\u73b0\u5728\uff0c\u4f60\u60f3\u529e\u6cd5\uff0c\u627e\u5979\u4eec\u56de\u6765\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u518d\u6293\u5230\u5979\u4eec\u65f6\uff0c\u90a3\u5c31\u60f3\u6cd5\u5b50\u628a\u5979\u4eec\u7684\u6b66\u529f\u5e9f\u53bb\uff0c\u8981\u5979\u4eec\u89c4\u89c4\u77e9\u77e9\uff0c\u505a\u4e00\u4e2a\u4f9d\u9644\u7537\u4eba\u7684\u5c0f\u5987\u4eba\u3002\u201d \u3000\u3000\u4ed6\u53e3\u53e3\u58f0\u58f0\uff0c\u8bf4\u5979\u4eec\u4e8c\u5b57\uff0c\u663e\u7136\u662f\uff0c\u4e5f\u5305\u62ec\u4e86\u9ad8\u5e7d\u5170\uff0c\u4f46\u5374\u4e0d\u77e5\u9053\uff0c\u9ad8\u5929\u5065\u8981\u628a\u9ad8\u5e7d\u5170\u8d50\u7ed9\u4f55\u4eba? \u3000\u3000\u96f7\u65e0\u5f71\u6ca1\u6709\u95ee\uff0c\u9ad8\u5929\u5065\u4e5f\u6ca1\u6709\u518d\u89e3\u91ca\u3002\u53ea\u4e00\u8eac\u8eab\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u7acb\u523b\u4e0b\u4ee4\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u597d!\u5168\u529b\u5bfb\u627e\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u90fd\u5f97\u628a\u5979\u4eec\u627e\u5230\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c5e\u4e0b\u5168\u529b\u4ee5\u8d74\u2026\u2026\u201d\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u5982\u82e5\u5979\u4eec\u53cd\u6297\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u65e0\u5f71\uff0c\u90a3\u5c31\u683c\u6740\u65e0\u8bba\uff0c\u751f\u64d2\u56fa\u7136\u662f\u597d\uff0c\u4f46\u5982\u4e0d\u80fd\u751f\u64d2\u65f6\uff0c\u90a3\u5c31\u6740\u4e86\u5979\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c5e\u4e0b\u9075\u547d\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u53bb\u5b89\u6392\u4e00\u4e0b\u5427!\u628a\u8bad\u7ec3\u5df2\u6210\u7684\u5341\u5934\u82b1\u8c79\uff0c\u4e5f\u5e26\u51fa\u6765\uff0c\u987a\u4fbf\u901a\u77e5\u72c2\u9f99\u3001\u98de\u9e70\u4e00\u58f0\uff0c\u8981\u4ed6\u4eec\u4e5f\u53c2\u4e0e\u641c\u5bfb\uff0c\u5e76\u4e14\u628a\u4ed6\u4eec\u7684\u6700\u5927\u7684\u80fd\u529b\uff0c\u4e5f\u7528\u51fa\u6765\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u4e00\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u7acb\u523b\u901a\u77e5\u4ed6\u4eec\u3002\u201d \u3000\u3000\u5927\u8d24\u5927\u6076\uff0c\u90fd\u6709\u5f02\u4e8e\u5e38\u4eba\u4e4b\u5904\uff0c\u4ed6\u4eec\u5fcd\u4eba\u6240\u4e0d\u80fd\u5fcd\uff0c\u4e5f\u80fd\u4e3a\u4eba\u6240\u4e0d\u80fd\u4e3a\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5c31\u662f\u8fd9\u6837\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6709\u7740\u6fc0\u6124\u4e4b\u706b\uff0c\u4f46\u4ed6\u5374\u54ac\u7259\u5fcd\u4e0b\u4e86\u3002\u4ed6\u660e\u767d\u76ee\u4e0b\u7684\u5904\u5883\u3002\u96f7\u65e0\u5f71\u8fd9\u4e2a\u4eba\uff0c\u7edd\u5bf9\u4e0d\u80fd\u5f00\u7f6a\u3002 \u3000\u3000\u671b\u7740\u96f7\u65e0\u5f71\u8fdc\u53bb\u7684\u80cc\u5f71\uff0c\u9ad8\u5929\u5065\u8138\u4e0a\u95ea\u63a0\u8fc7\u4e00\u62b9\u72de\u7b11\u3002 \u3000\u3000\u4ed6\u5fc5\u987b\u5fcd\u8010\uff0c\u628a\u6124\u6012\u6df1\u57cb\u5fc3\u5e95\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u4e2a\u7ed3\u679c\uff0c\u5927\u51fa\u4e86\u96f7\u65e0\u5f71\u7684\u610f\u6599\u4e4b\u5916\uff0c\u5179\u4e8b\u4f53\u5927\uff0c\u539f\u60f3\u4f1a\u53d7\u4e00\u756a\u8d23\u5907\uff0c\u4f46\u5374\u672a\u6599\u5230\uff0c\u9ad8\u5929\u5065\u7adf\u7136\u6ca1\u6709\u53d1\u4f5c\u3002 \u3000\u3000\u81ea\u7136\uff0c\u96f7\u65e0\u5f71\u4e5f\u662f\u4e45\u8d70\u6c5f\u6e56\u7684\u4eba\u4e86\uff0c\u5bf9\u9ad8\u5929\u5065\u7684\u624b\u6bb5\uff0c\u4e5f\u77e5\u4e4b\u751a\u6df1\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u65e2\u6709\u7740\u77e5\u9047\u7684\u611f\u6069\u4e4b\u60f3\uff0c\u4e5f\u6709\u4e9b\u6000\u7591\u7684\u4e0d\u5b89\u3002 \u3000\u3000\u4f46\u76ee\u4e0b\uff0c\u603b\u7b97\u662f\u6ca1\u6709\u4e8b\u4e86\u3002 \u3000\u3000\u5728\u4e00\u4f4d\u9a6f\u517d\u5f02\u4eba\u8bad\u7ec3\u4e0b\u7684\u5341\u5934\u82b1\u8c79\uff0c\u662f\u9ad8\u5929\u5065\u51c6\u5907\u9010\u9e7f\u6c5f\u6e56\u65f6\uff0c\u7528\u4ee5\u8ffd\u6740\u5f3a\u654c\u4e4b\u65f6\u7528\u7684\uff0c\u6b64\u523b\uff0c\u7adf\u7136\u4e0d\u60dc\u7528\u6765\u5bf9\u4ed8\u9ad8\u5e7d\u5170\u548c\u5434\u53cc\u5973\u3002 \u3000\u3000\u8fd9\u4e5f\u8bf4\u660e\u4e86\u4e00\u4ef6\u4e8b\uff0c\u9ad8\u5929\u5065\u5df2\u7ecf\u4e0b\u4e86\u51b3\u5fc3\uff0c\u975e\u8981\u628a\u4e24\u4e2a\u4eba\u627e\u51fa\u6765\u4e0d\u53ef\u3002 \u3000\u3000\u4f3c\u4e4e\u662f\uff0c\u9ad8\u5929\u5065\u5bf9\u5434\u53cc\u5973\u548c\u9ad8\u5e7d\u5170\uff0c\u5df2\u7ecf\u6068\u5230\u4e86\u6781\u70b9\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7f13\u6b65\u884c\u51fa\u4e86\u795e\u5200\u5802\u3002 \u3000\u3000\u9ad8\u98de\u8679\u5c31\u7ad9\u5728\u795e\u5200\u5802\u7684\u95e8\u53e3\u3002 \u3000\u3000\u770b\u5230\u4e86\u8fd9\u4e2a\u5b9d\u8d1d\u513f\u5b50\uff0c\u9ad8\u5929\u5065\u4e5f\u4e0d\u7981\u6709\u4e9b\u5934\u75bc\u3002\u4e00\u76b1\u7709\u5934\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u7b49\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u7b49\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u7b49\u7239\u548c\u5434\u59d1\u5a18\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u5728\u505a\u767d\u65e5\u68a6\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u7239\uff0c\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ad8\u5929\u5065\u5fcd\u4e0b\u5fc3\u5934\u4e00\u80a1\u6012\u706b\u9053\uff1a\u201c\u5434\u53cc\u5973\u8dd1\u4e86\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u4e0d\u53ef\u80fd\u554a!\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u54e6!\u4f60\u5012\u8bf4\u8bf4\u770b\uff0c\u600e\u4e48\u4e0d\u53ef\u80fd?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u795e\u5200\u5802\u9632\u5b88\u5f97\u8fd9\u4e48\u68ee\u4e25\uff0c\u5979\u600e\u4e48\u80fd\u8dd1\u5f97\u4e86?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4f60\u7684\u770b\u6cd5?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u8fd9\u662f\u4e8b\u5b9e\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u795e\u5200\u5802\u7684\u9632\u5b88\uff0c\u662f\u5341\u5206\u68ee\u4e25\uff0c\u4e0d\u8fc7\uff0c\u5979\u70b9\u4e86\u96f7\u65e0\u5f71\u7684\u7a74\u9053\uff0c\u53d6\u4e86\u4ed6\u7684\u8170\u724c\uff0c\u795e\u5200\u5802\uff0c\u5c31\u53ef\u80fd\u901a\u884c\u65e0\u963b\uff0c\u4e86\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u5979\u4eec\u4e0d\u662f\u5df2\u7ecf\u670d\u8fc7\u4e86\u5931\u5fc3\u4e39\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u8fd9\u5927\u6982\u662f\u4e00\u4e2a\u5343\u53e4\u7591\u6848\u4e86\uff0c\u6211\u5c31\u60f3\u4e0d\u660e\u767d\uff0c\u670d\u8fc7\u4e86\u5931\u5fc3\u4e39\u7684\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u80fd\u51fa\u624b\u70b9\u4eba\u7a74\u9053?\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u7239\u662f\u5426\u770b\u5230\u5979\u4eec\u670d\u4e0b\u4e86\u5931\u5fc3\u4e39\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u5012\u6ca1\u6709\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u90a3\u53ea\u6709\u4e24\u4e2a\u7b54\u6848\u4e86\uff0c\u4e00\u4e2a\u662f\uff0c\u5979\u4eec\u6839\u672c\u6ca1\u6709\u670d\u4e0b\u5931\u5fc3\u4e39\uff0c\u53e6\u4e00\u4e2a\u662f\uff0c\u96f7\u65e0\u5f71\u7ed9\u4e86\u5979\u4eec\u89e3\u836f\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\u5077\u5230\u5931\u5fc3\u4e39\u7684\u89e3\u836f\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u90a3\u5c31\u662f\u5979\u4eec\u6839\u672c\u6ca1\u6709\u670d\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f46\uff0c\u9ad8\u5e7d\u5170\u670d\u4e0b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\u4e5f\u9003\u8d70\u4e86?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6240\u4ee5\uff0c\u6211\u4e0d\u80fd\u6000\u7591\u96f7\u65e0\u5f71\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u7239\uff0c\u73b0\u5728\uff0c\u4f60\u8981\u600e\u4e48\u529e\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5168\u529b\u641c\u5bfb\u5979\u4eec\uff0c\u627e\u5230\u4e3a\u6b62\uff0c\u683c\u6740\u52ff\u8bba\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u59b9\u59b9\u548c\u5434\u53cc\u5973\uff0c\u90fd\u6709\u5f88\u9ad8\u660e\u7684\u6b66\u529f\uff0c\u627e\u5230\u4e86\u5979\u4eec\uff0c\u53ea\u6015\u4e5f\u96be\u514d\u4f1a\u6709\u4e00\u9635\u706b\u5e76\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5979\u4eec\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u6211\u81ea\u4fe1\u80fd\u640f\u6740\u5979\u4eec\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u7239\uff0c\u4f60\u5728\u5bf9\u4ed8\u5434\u53cc\u5973\u65f6\uff0c\u624b\u4e0b\u7559\u60c5\uff0c\u53cd\u6b63\uff0c\u5979\u5df2\u7ecf\u65e0\u6cd5\u518d\u4e3a\u6211\u4eec\u6240\u7528\uff0c\u5c31\u5e9f\u4e86\u5979\u7684\u6b66\u529f\uff0c\u5ac1\u7ed9\u6211\u6709\u4f55\u4e0d\u53ef?\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u522b\u518d\u6253\u5434\u53cc\u5973\u7684\u4e3b\u610f\u4e86\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u7239\uff0c\u4f60\u53d8\u5366\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u542c\u7740\uff0c\u5434\u53cc\u5973\u5982\u82e5\u88ab\u6211\u4eec\u53d1\u73b0\u4e86\uff0c\u6211\u60f3\u5979\u4e0d\u4f1a\u7559\u4e0b\u6027\u547d\u7b49\u6211\u4eec\u7f9e\u8fb1\u3002\u201d \u3000\u3000\u9ad8\u98de\u8679\u9053\uff1a\u201c\u8fd9\u8981\u770b\u7239\u7684\u505a\u6cd5\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u6325\u6325\u624b\uff0c\u9053\uff1a\u201c\u4f60\u53bb\u5427!\u201d \u3000\u3000\u9ad8\u98de\u8679\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u5341\u5934\u82b1\u8c79\uff0c\u548c\u96f7\u65e0\u5f71\u7b49\uff0c\u5c55\u5f00\u4e86\u641c\u7d22\u3002 \u3000\u3000\u5434\u53cc\u5973\u7b49\u4e5f\u771f\u7684\u672a\u79bb\u5f00\u7b2c\u4e00\u5821\u3002 \u3000\u3000\u800c\u4e14\uff0c\u4ecd\u4f4f\u5728\u5f69\u8679\u697c\u3002 \u3000\u3000\u81ea\u7136\u5434\u53cc\u5973\u79bb\u53bb\u4e4b\u540e\uff0c\u5f69\u8679\u697c\u5c31\u9501\u4e86\u8d77\u6765\u3002 \u3000\u3000\u9ad8\u5929\u5065\u6709\u4e00\u4e2a\u5f88\u5982\u610f\u7684\u7b97\u76d8\uff0c\u90a3\u5c31\u662f\u5e0c\u671b\u6709\u4e00\u5929\uff0c\u5434\u53cc\u5973\u8fd8\u80fd\u56de\u6765\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u5f69\u8679\u697c\u7684\u4e00\u5207\uff0c\u90fd\u8fd8\u4fdd\u6301\u7740\u539f\u6765\u7684\u6a21\u6837\u3002 \u3000\u3000\u552f\u4e00\u4e0d\u540c\u7684\u662f\uff0c\u5f69\u8679\u697c\u4e0a\uff0c\u52a0\u4e86\u4e00\u628a\u94c1\u9501\uff1b \u3000\u3000\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\uff0c\u5e76\u6ca1\u6709\u6253\u5f00\u94c1\u9501\uff0c\u4ed6\u4eec\u7531\u4e00\u6247\u7a97\u5b50\u884c\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u96c6\u4e2d\u5728\u5434\u53cc\u5973\u7684\u5367\u5ba4\u4e2d\u3002 \u3000\u3000\u5434\u53cc\u5973\u671b\u671b\u5fae\u660e\u7684\u5929\u8272\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f55\u90ce\uff0c\u4ed6\u4eec\u5f88\u5feb\u5c31\u4f1a\u627e\u5230\u6211\u4eec\uff0c\u6211\u60f3\u90a3\u662f\u4e00\u573a\u8840\u6218\uff0c\u4e0d\u77e5\u4f60\u4f5c\u4f55\u6253\u7b97?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d\u50cf\u662f\uff0c\u6211\u4eec\u5df2\u7ecf\u6ca1\u6709\u522b\u7684\u529e\u6cd5\u9009\u62e9\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u4f60\u8be5\u4f5c\u4e00\u4e2a\u660e\u6717\u7684\u51b3\u5b9a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u51b3\u4e00\u6b7b\u6218\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e09\u4eba\u540c\u547d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u751f\u540c\u7f57\u5e0f\uff0c\u6b7b\u540c\u7a74\uff0c\u59ca\u59ca\uff0c \u4f60\u8fd8\u8ba4\u4e0d\u8ba4\u6211\u8fd9\u4e2a\u59b9\u59b9?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8ba4\uff0c\u4ece\u6b64\u4e4b\u540e\uff0c\u54b1\u4eec\u4e09\u4eba\u4e00\u4f53\uff0c\u59b9\u59b9\u3001\u6211\u548c\u4f55\u90ce\uff0c\u5df2\u7ecf\u6709\u4e86\u592b\u5987\u4e4b\u5b9e\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5c0f\u59b9\u60ed\u6127\uff0c\u4e5f\u732e\u8eab\u4f55\u90ce\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u4e00\u9053\uff0c\u7b11\uff1a \u201c\u54b1\u4eec\u548c\u4f55\u90ce\u4e4b\u95f4\uff0c\u8fd8\u7f3a\u5c11\u4e00\u70b9\u4ec0\u4e48\uff1f\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u592b\u5987\u4e4b\u540d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54b1\u4eec\u73b0\u5728\uff0c\u4f55\u4e0d\u62dc\u4e2a\u5929\u5730\uff0c\u4ee5\u6b63\u540d\u4efd\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u597d\u554a!\u4f60\u4eec\u4e24\u4f4d\u884c\u793c\u5427\uff01\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u522b\u5fd8\u4e86\u54b1\u4eec\u662f\u4e09\u4f4d\u4e00\u4f53\u554a\uff01\u201d \u3000\u3000\u5f69\u8679\u697c\u5916\uff0c\u98ce\u4e91\u7d27\u6025\uff0c\u5341\u4e8c\u5934\u82b1\u8c79\uff0c\u548c\u65e0\u6570\u9ad8\u624b\uff0c\u5728\u641c\u5bfb\u4e09\u4eba\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u4e09\u4e2a\u5374\u5e03\u7f6e\u4e8e\u4e00\u4e2a\u7b80\u5355\u7684\u793c\u5802\uff0c\u7adf\u7136\u62dc\u8d77\u5929\u5730\u6765\u3002 \u3000\u3000\u4e00\u7537\u4e8c\u5973\u540c\u5802\u62dc\u5802\uff0c\u9664\u4e86\u4e09\u4e2a\u5f53\u4e8b\u4eba\u4e4b\u5916\uff0c\u6ca1\u6709\u4e00\u4e2a\u8d3a\u5ba2\uff0c\u6ca1\u6709\u4e3b\u5a5a\u5927\u5a92\uff0c\u4e5f\u7b97\u4e00\u4ef6\u4eba\u95f4\u5947\u4e8b\u4e86\u3002 \u3000\u3000\u884c\u8fc7\u5927\u793c\u4e4b\u540e\uff0c\u9ad8\u5e7d\u5170\u7684\u5fc3\u4e2d\u4e5f\u843d\u5b9e\uff0c\u611f\u89c9\u4e2d\uff0c\u5979\u5df2\u771f\u771f\u6b63\u6b63\u662f\u4f55\u5bd2\u8863\u7684\u59bb\u5b50\u4e86\u3002\u4eba\u4e5f\u5f00\u6717\u4e86\u8d77\u6765\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5927\u59ca\uff0c\u4f60\u5f53\u5e74\u7684\u6b66\u529f\u662f\u5426\u90fd\u6401\u4e0b\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6ca1\u6709\u554a\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u7792\u59ca\u59ca\u8bf4\uff0c\u5c0f\u59b9\u8fd9\u8fd1\u5e74\u6765\uff0c\u81ea\u89c9\u7740\u7cbe\u8fdb\u4e0d\u5c11\uff0c\u7b2c\u4e00\u5821\u4e2d\uff0c\u80fd\u548c\u54b1\u4eec\u653e\u624b\u4e00\u62da\u7684\uff0c\u4e5f\u6ca1\u6709\u51e0\u4e2a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u53ea\u6015\u4ed6\u4eec\u4e0d\u4f1a\u771f\u5200\u771f\u67aa\u7684\u548c\u6211\u4eec\u62da\u4e2a\u751f\u6b7b\u51fa\u6765\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u77e5\u9053\uff0c\u4ed6\u4eec\u4f1a\u65bd\u7528\u6697\u7b97\uff0c\u4f46\u54b1\u4eec\u53ef\u4ee5\u5229\u7528\u5f69\u8679\u697c\u7684\u5f62\u52bf\uff0c\u6297\u62d2\u4ed6\u4eec\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u662f\u5821\u4e3b\u7684\u4eb2\u751f\u5973\u513f\uff0c\u4e00\u65e6\u5230\u4e86\u9762\u5bf9\u9762\uff0c\u771f\u67aa\u771f\u5200\u62fc\u547d\u7684\u65f6\u5019\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u6709\u4ec0\u4e48\u6539\u53d8?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u4f60\u662f\u4e0d\u662f\u89c9\u7740\u5f88\u5947\u602a?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u5bf9\u9ad8\u98de\u8679\u548c\u5bf9\u6211\u6709\u4e9b\u4ec0\u4e48\u4e0d\u540c?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9\u9ad8\u98de\u8679\u8868\u9762\u4e25\u5389\uff0c\u5b9e\u5219\u7eb5\u5bb9\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u53f9\u4e00\u53e3\u6c14\uff0c\u63a5\u9053\uff1a\u201c\u4e00\u4e2a\u751f\u8eab\u4e4b\u7236\uff0c\u4f1a\u628a\u5931\u5fc3\u4e39\uff0c\u8feb\u4ed6\u7684\u5973\u513f\u670d\u4e0b\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5bf9!\u8fd9\u4e2d\u95f4\uff0c\u786e\u4e5f\u6709\u5f88\u591a\u53ef\u4ee5\u5546\u69b7\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u548c\u9ad8\u98de\u8679\u7684\u6027\u683c\uff0c\u662f\u90a3\u4e48\u4e0d\u540c\uff0c\u6839\u672c\u65e0\u6cd5\u76f8\u4fe1\u662f\u4e00\u6bcd\u540c\u80de\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u4e00\u8bf4\uff0c\u6211\u5012\u60f3\u8d77\u4e00\u4ef6\u4e8b\u4e86\uff0c\u4ee4\u5802\u5462?\u6211\u8fdb\u5165\u7b2c\u4e00\u5821\u4e4b\u540e\uff0c\u5c31\u6ca1\u6709\u89c1\u5230\u8fc7\u9ad8\u592b\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u9ad8\u5929\u5065\u7684\u8bf4\u6cd5\u662f\uff0c\u751f\u4e0b\u6211\u4e4b\u540e\uff0c\u6bcd\u4eb2\u5c31\u6b7b\u4e86\uff0c\u6709\u4e00\u6b21\uff0c\u6211\u8981\u5230\u6bcd\u4eb2\u575f\u4e0a\u62dc\u796d\uff0c\u9ad8\u5929\u5065\u7adf\u53d1\u4e86\u4e00\u987f\u813e\u6c14\uff0c\u8fd8\u6253\u4e86\u6211\u4e00\u987f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ee5\u540e\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u513f\u65f6\u60c5\u666f\u8bb0\u5fc6\u4f9d\u7a00\uff0c\u90a3\u65f6\uff0c\u6211\u5927\u6982\u53ea\u6709\u56db\u5c81\u5427!\u4ee5\u540e\uff0c\u6211\u5c31\u4ece\u5e08\u5b66\u827a\u53bb\u4e86\uff0c\u4e00\u53bb\u5c31\u5341\u4e8c\u5e74\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u56de\u5230\u7b2c\u4e00\u5821\u4e09\u5e74\u591a\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u7ec3\u6210\u7edd\u827a\uff0c\u9ad8\u5821\u4e3b\u5bf9\u6211\u4e5f\u5ba2\u6c14\u591a\u4e86\uff0c\u6e10\u6e10\u7684\uff0c\u8fde\u9ad8\u98de\u8679\u4e5f\u6709\u4e9b\u6015\u6211\u4e86\uff0c\u8fd9\u4f60\u90fd\u77e5\u9053\u4e86\uff0c\u81ea\u7136\u4e0d\u7528\u518d\u8bf4\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u542c\u4f60\u8bf4\u8fd9\u4e9b\u5f80\u4e8b\uff0c\u786e\u5b9e\u6709\u5f88\u591a\u7684\u53ef\u7591\u4e4b\u5904\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u8fd9\u4e9b\u4e8b\uff0c \u5df2\u5728\u5fc3\u4e2d\u6253\u4e86\u7ed3\uff0c\u4f46\u4ed6\u7ed9\u6211\u773c\u4e0b\u5931\u5fc3\u4e39\uff0c\u5374\u4f7f\u6211\u6000\u7591\u5f97\u66f4\u6df1\uff0c\u6211\u4e00\u76f4\u5728\u60f3\uff0c\u4e3a\u4eba\u7236\u8005\uff0c\u4f1a\u4e0d\u4f1a\u5bf9\u5973\u513f\u5982\u6b64\u7684\u6bd2\u8fa3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e94\u8be5\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u5446\u53cc\u5973\u9053\uff1a\u201c\u9664\u975e\uff0c\u90a3\u4e0d\u662f\u4ed6\u4eb2\u751f\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e5f\u8bb8\u5929\u4e0b\u771f\u6709\u8fd9\u6837\u7684\u7236\u4eb2\uff0c\u4e0d\u8fc7\uff0c\u90a3\u4e2a\u4eba\uff0c\u4e0d\u662f\u9ad8\u5929\u5065\uff0c\u4ed6\u5bf9\u9ad8\u98de\u8679\u7684\u6eba\u7231\uff0c\u53ef\u4ee5\u5f97\u5230\u8bc1\u660e\uff0c\u624b\u5fc3\u624b\u80cc\u90fd\u662f\u8089\uff0c\u6211\u5982\u662f\u4ed6\u7684\u4eb2\u751f\u5973\u513f\uff0c\u53c8\u600e\u4f1a\u5982\u6b64\u5bf9\u6211\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u542c\u8bf4\uff0c\u4f60\u8bad\u7ec3\u4e86\u4e00\u6279\u4eba\u624b\uff0c\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a \u201c\u73b0\u5728\uff0c\u6211\u4eec\u662f\u751f\u6b7b\u540c\u547d\uff0c \u81ea\u7136\u4e5f\u7528\u4e0d\u7740\u5bf9\u4f60\u4fdd\u6709\u4ec0\u4e48\u79d8\u5bc6\u4e86\uff0c\u6211\u8bad\u7ec3\u4e86\u5341\u4e8c\u4e2a\u5973\u5b50\uff0c\u79f0\u4e3a\u5341\u4e8c\u98de\u71d5\uff0c\u8fd9\u4e5f\u662f\u9ad8\u5821\u4e3b\u7684\u610f\u601d\uff0c\u7528\u4ee5\u7275\u5236\u795e\u5200\u5802\u7684\u96f7\u5802\u4e3b\uff0c\u6211\u60f3\u9ad8\u5821\u4e3b\u8feb\u6211\u670d\u4e0b\u5931\u5fc3\u4e39\uff0c\u8fd9\u4e5f\u662f\u539f\u56e0\u4e4b\u4e00\u3002\u201d \u3000\u3000\u5979\u662f\u4e2a\u5014\u5f3a\u7684\u4eba\uff0c\u5fc3\u4e2d\u7684\u7591\u95ee\u6ca1\u6709\u6f84\u6e05\u4e4b\u524d\uff0c\u5df2\u4e0d\u518d\u627f\u8ba4\u9ad8\u5929\u5065\u662f\u5979\u7236\u4eb2\u3002 \u3000\u3000\u4f46\u4ecd\u7136\u4fdd\u6301\u7740\u76f8\u5f53\u7684\u5c0a\u91cd\u3002 \u3000\u3000\u5982\u4ee5\u51b7\u9759\u548c\u5904\u4e8b\u800c\u8a00\uff0c\u9ad8\u5e7d\u5170\u6bd4\u4f55\u5bd2\u8863\u548c\u5434\u53cc\u5973\u90fd\u9ad8\u4e86\u4e00\u7b79\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u8c08\u8c08\u773c\u524d\u7684\u4e8b\uff0c \u4f60\u5bf9\u5341\u4e8c\u98de\u71d5\u6709\u591a\u5927\u7684\u5f71\u54cd\u529b\u91cf\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u6211\u5f88\u4e25\u5389\uff0c\u5979\u4eec\u90fd\u5f88\u6015\u6211\uff0c\u6ca1\u6709\u5b58\u5728\u4efb\u4f55\u79c1\u5fc3\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u4e4b\u95f4\u4e5f\u6ca1\u6709\u4ec0\u4e48\u79c1\u4eba\u7684\u611f\u60c5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u4eec\u7684\u795e\u667a\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5b8c\u5168\u6b63\u5e38\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u5f88\u597d\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u5f88\u597d?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u53bb\u548c\u5979\u4eec\u8c08\u8c08\u770b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4f1a\u53bb\u8bd5\u8bd5\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u662f\u73b0\u5728\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u8981\u4ec0\u4e48\u65f6\u5019?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5e94\u4ed8\u7b2c\u4e00\u6ce2\u7684\u653b\u52bf\u4e4b\u540e\uff0c\u6211\u4eec\u518d\u60f3\u529e\u6cd5\u53bb\u89c1\u5979\u4eec\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d\u5427!\u6211\u770b\u8fd9\u4ef6\u4e8b\uff0c\u7531\u4f60\u51b3\u5b9a\u5427\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8d31\u59be\u9075\u547d\u3002\u201d \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4f20\u6765\u4e86\u4e00\u9635\u8f7b\u5fae\u7684\u91d1\u94c1\u76f8\u89e6\u4e4b\u58f0\uff0c\u8fd9\u58f0\u97f3\u5341\u5206\u8f7b\u5fae\uff0c\u5982\u975e\u8033\u76ee\u7279\u522b\u7075\u654f\u7684\u4eba\uff0c\u6839\u672c\u5c31\u542c\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54fc!\u4ed6\u4eec\u6765\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\uff0c\u4e0d\u8981\u51b2\u52a8\uff0c\u6211\u4eec\u8fce\u4e0a\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u59b9\u59b9\uff0c\u54b1\u4eec\u76f8\u5904\u4e86\u5f88\u591a\u5e74\uff0c\u4f46\u5374\u5f7c\u6b64\u90fd\u672a\u89c1\u8fc7\u5bf9\u65b9\u51fa\u624b\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u542c\u8bf4\uff0c\u5927\u59ca\u7684\u624b\u6cd5\u5f88\u9ad8\u660e\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u7684\u6740\u4eba\u624b\u6cd5\uff0c\u4e5f\u5f88\u9ad8\u660e\u554a!\u201d \u3000\u3000\u4e09\u4e2a\u4eba\uff0c\u884c\u51fa\u4e86\u5367\u5ba4\u3002 \u3000\u3000\u6765\u4eba\u5df2\u767b\u4e0a\u4e86\u4e8c\u697c\u3002 \u3000\u3000\u5434\u53cc\u5973\u7684\u5367\u5ba4\uff0c\u5c31\u5728\u4e8c\u697c\u4e0a\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u6765\u4e86?\u201d \u3000\u3000\u6765\u7684\u7adf\u662f\u96f7\u65e0\u5f71\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u7b11\u4e00\u58f0\uff0c\u63a5\u9053\uff1a \u201c\u6211\u4e0d\u662f\u4e8c\u5c0f\u59d0\uff0c\u6211\u5df2\u7ecf\u4e0d\u662f\u7b2c\u4e00\u5821\u7684\u4eba\u4e86\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4f46\u4f60\u8fd8\u662f\u7b2c\u4e00\u5821\u4e2d\u7684\u4e8c\u5c0f\u59d0\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u771f\u7684\u8fd8\u628a\u6211\u5f53\u4e8c\u5c0f\u59d0\u770b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u6211\u6253\u4f60\uff0c \u4f60\u4e5f\u4e0d\u4f1a\u8fd8\u624b\u4e86\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u2014\u4e2a\u51b7\u51b7\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a \u201c\u73b0\u5728\u4e0d\u4f1a\uff0c\u56e0\u4e3a\uff0c\u6211\u4e0d\u4f1a\u7eb5\u5bb9\u81ea\u5df1\u7684\u513f\u5973\u53bb\u4f24\u5bb3\u6211\u7684\u5c5e\u4e0b\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u7684\u7adf\u7136\u662f\u9ad8\u5929\u5065\u3002 \u3000\u3000\u4ed6\u4e5f\u627e\u4e0a\u4e86\u5f69\u8679\u697c\uff0c \u3000\u3000\u5434\u53cc\u5973\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u4e5f\u6765\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5434\u53cc\u5973\uff0c \u4f60\u53eb\u6211\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\uff0c\u9ad8\u5929\u5065\uff0c\u90a3\u91cc\u4e0d\u5bf9\u4e86\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u600e\u4e48?\u9ad8\u5929\u5065\u4e5f\u662f\u4f60\u53eb\u7684\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u5c71\uff0c\u6211\u5e94\u8be5\u53eb\u4f60\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u8fc7\u53bb\u53eb\u6211\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5982\u82e5\u54b1\u4eec\u8fd8\u6709\u2014\u70b9\u7236\u5973\u4e4b\u60c5\uff0c\u4e5f\u88ab\u4f60\u90a3\u4e00\u9897\u5931\u5fc3\u4e39\uff0c\u628a\u5b83\u6bd2\u5149\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u63a5\u9053\uff1a \u201c\u8fd9\u4e5f\u662f\u5728\u4e0b\u4e0d\u89e3\u4e4b\u5904\uff0c \u4e3a\u4ec0\u4e48\u4f60\u5927\u5c0f\u59d0\u670d\u4e0b\u4e86\u5931\u5fc3\u4e39\u540e\uff0c\u7adf\u7136\u80fd\u591f\u5f88\u5feb\u5730\u590d\u5143\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u5f88\u60f3\u77e5\u9053\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5728\u4e0b\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u96f7\u5802\u4e3b\u8001\u8c0b\u6df1\u7b97\uff0c\u662f\u4e0d\u662f\u62c5\u5fc3\uff0c\u6709\u4e00\u5929\uff0c\u9ad8\u5821\u4e3b\uff0c\u4e5f\u4f1a\u7ed9\u4f60\u4e0b\u4e86\u6bd2\uff0c\u4f7f\u4f60\u4e5f\u670d\u4e0b\u5931\u5fc3\u4e39\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5728\u4e0b\u5bf9\u5821\u4e3b\u5fe0\u5fc3\u4e0d\u4e8c\uff0c\u751f\u6b7b\u4e0d\u6e1d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u662f\u6761\u72d7\u2026\u2026\u201d \u3000\u3000\u96f7\u65e0\u5f71\u8138\u9091\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a \u201c\u662f\u4e0d\u662f\u611f\u89c9\u5230\uff0c\u6211\u8e22\u5728\u4f60\u7684\u75db\u5904\u4e86\uff0e\u9a82\u5728\u4f60\u7684\u4f24\u5fc3\u5904\u4e86\u3002\u201d \u9ad8\u5929\u5065\u6012\u58f0\u559d\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u4f60\u5982\u6b64\u653e\u8086\uff0c\u773c\u775b\u4e2d\uff0c\u8fd8\u6709\u6211\u8fd9\u4e2a\u8001\u5b50\u5417?\u201d \u3000\u3000\u4e8c\u5973\u53e3\u9f7f\u5c16\u5229\uff0c\u4f55\u5bd2\u8863\u5012\u53cd\u843d\u5f97\u8f7b\u677e\u4e86\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u662f\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u6211\u662f\u4f60\u7239\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a \u201c\u53e4\u5f80\u4eca\u6765\uff0c\u6211\u8fd8\u6ca1\u6709\u542c\u8fc7\u7236\u4eb2\u5bf9\u5973\u513f\u4e0b\u6bd2\u7684?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u90a3\u5bf9\u4f60\u662f\u4e00\u79cd\u60e9\u7f5a\uff0c\u60e9\u7f5a\u4f60\u4ee5\u540e\u4e0d\u8981\u80e1\u4f5c\u975e\u4e3a\u3002\u201d \u9ad8\u5e7d\u5170\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5728\u6ca1\u6709\u627e\u51fa\u8bc1\u636e\u4e4b\u524d\uff0c\u6211\u5e0c\u671b\uff0c\u4f60\u6211\u4e4b\u95f4\uff0c\u6682\u65f6\u4e0d\u8981\u51b2\u7a81\uff0c\u4f60\u5df2\u6bd2\u5bb3\u8fc7\u6211\u4e00\u6b21\uff0c\u90a3\u662f\u6bd4\u6b7b\u8fd8\u8981\u91cd\u7684 \u3000\u3000\u60e9\u7f5a\uff0c\u5982\u662f\u6211\u4eec\u4e4b\u95f4\uff0c\u6709\u4e00\u70b9\u517b\u80b2\u6069\u60c5\uff0c\u4e5f\u88ab\u4f60\u8fd9\u4e00\u7c92\u4e39\u836f\uff0c\u6bd2\u5f97\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u4e0d\u4f1a\u5411\u4f60\u4fb5\u72af\uff0c\u4f46\u4e5f\u4e0d\u5141\u8bb8\u4f60\u518d\u5bf9\u6211\u65e0\u793c\uff0c\u4efb\u4f55\u5bf9\u6211\u7684\u653b\u52bf\uff0c\u6211\u90fd\u4f1a\u5c55\u5f00\u65e0\u60c5\u7684\u53cd\u51fb\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8bfb\u5723\u8d24\u4e66\uff0c\u53ef\u77e5\u9053\u7236\u8981\u5b50\u6b7b\uff0c\u5b50\u4e0d\u6b7b\u5373\u8c13\u4e0d\u5b5d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u53ef\u61c2\u53e4\u5723\u5148\u8d24\u8bf4\u8fc7\u7684\u4e00\u53e5\u8bdd\uff0c\u7236\u6148\u5b50\u5b5d\uff0c\u6211\u5bf9\u7b2c\u4e00\u5821\u6709\u4e0d\u5c11\u7684\u8d21\u732e\uff0c\u6211\u4e0d\u559c\u6b22\u4f60\u5904\u4e8b\u65b9\u6cd5\uff0c\u4f46\u6211\u8fd8\u662f\u5c3d\u91cf\u5c48 \u3000\u3000\u4ece\u4f60\uff0c\u4f46\u4f60\u7adf\u5bb3\u6211\u670d\u4e0b\u5931\u5fc3\u4e39\uff0c\u8981\u4e0d\u662f\u6211\u88ab\u4eba\u89e3\u53bb\u6bd2\u6027\u2026\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u89e3\u4e86\u4f60\u7684\u6bd2\u6027?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u671b\u4e86\u96f7\u65e0\u5f71\u4e00\u773c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u90a3\u4e00\u773c\u50cf\u4e00\u628a\u5229\u5251\u4e00\u6837\uff0c\u523a\u4f24\u4e86\u96f7\u65e0\u5f71\uff0c\u4e5f\u523a\u4f24\u4e86\u9ad8\u5929\u5065\u3002 \u3000\u3000\u597d\u5389\u5bb3\u7684\u4e00\u773c\u3002 \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u54b1\u4eec\u4e4b\u95f4\u5168\u65e0\u7236\u5973\u4e4b\u60c5\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u7684\u7236\u4eb2\uff0c\u4e0d\u4f1a\u6bd2\u5bb3\u4ed6\u4eb2\u751f\u5973\u513f\uff0c\u6240\u4ee5\uff0c\u4f60\u4e0d\u662f\u6211\u7684\u7236\u4eb2\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e2b\u5934\uff0c\u8001\u7236\u5728\u6b64\uff0c\u4f60\u7adf\u6562\u76ee\u65e0\u5c0a\u957f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5982\u82e5\u6211\u771f\u662f\u4f60\u7684\u5973\u513f\uff0c\u90a3\u4e5f\u88ab\u4f60\u6bd2\u6b7b\u4e86\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a \u201c\u90a3\u662f\u4e00\u4e2a\u4e0d\u80af\u542c\u8bdd\u7684\u5973\u513f\uff0e\u5979\u6d3b\u7740\uff0c\u4e5f\u4e0d\u8fc7\u662f\u4e00\u4e2a\u4e0d\u5b5d\u7684\u5973\u513f\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u5df2\u7ecf\u8bf4\u5f97\u5f88\u6e05\u695a\uff0c\u4e0d\u7ba1\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u548c\u6211\u4e4b\u95f4\uff0c\u6709\u4e9b\u4ec0\u4e48\u4ec7\u6068\uff0c\u4f46\u5728\u6211\u8fd8\u6ca1\u6709\u627e\u51fa\u8bc1\u660e\u4e4b\u524d\uff0c\u6211\u4e0d\u4f1a\u5411\u4f60\u95ee\u7f6a\uff0c\u4f60\u6bd2\u4e86\u6211\uff0c\u4e5f\u6bd2\u53bb\u7559\u5728\u6211\u5fc3\u4e2d\u7684\u4e00\u70b9\u60c5\u610f\uff0c\u6240\u4ee5\uff0c\u6211\u4e5f\u4e0d\u4f1a\u5bf9\u4f60\u6709\u4ec0\u4e48\u7684\u5bb9\u5fcd\uff0c\u4f60\u5982\u5bf9\u6211\u653b\u51fb\uff0c\u6211\u4f1a\u5168\u529b\u53cd\u51fb\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u4f60\u8ba4\u4e3a\uff0c \u4f60\u90a3\u4e03\u8272\u5251\uff0c\u771f\u662f\u4e16\u95f4\u7b2c\u4e00\u6740\u4eba\u5229\u5668\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5e0c\u671b\u4f60\u4e0d\u8981\u8bd5\uff0c\u7b2c\u4e00\u5821\u4e2d\u6709\u5f88\u591a\u7684\u6740\u624b\uff0c\u627e\u4e00\u4e2a\u6b66\u529f\u9ad8\u5f3a\u7684\u4eba\u8bd5\u8bd5\uff0c\u6211\u4e0d\u5e0c\u671b\u6211\u7b2c\u4e00\u6b21\u4e03\u5251\u5168\u51fa\u65f6\uff0c\u662f\u5bf9\u4ed8\u4f60\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u9664\u975e\u4f60\u80fd\u6539\u53d8\u5fc3\u610f\uff0c\u56de\u5230\u7b2c\u4e00\u5821\u6765\uff0c\u54b1\u4eec\u7236\u5973\u4e4b\u95f4\uff0c\u603b\u4f1a\u6709\u4e00\u6b21\u640f\u6740\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u96f7\u65e0\u5f71\u63a5\u9053\uff1a\u201c\u591a\u6d3e\u9ad8\u624b\uff0c\u4e0d\u8bba\u5979\u662f\u4ec0\u4e48\u8eab\u4efd\uff0c\u5982\u4e0d\u5c31\u8303\uff0c\u5c31\u683c\u6740\u52ff\u8bba\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c5e\u4e0b\u9075\u547d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u54b1\u4eec\u5c31\u5728\u8fd9\u5f69\u8679\u697c\u4e0a\u5206\u4e2a\u751f\u6b7b\u51fa\u6765!\u201d \u3000\u3000\u96f7\u65e0\u5f71\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u5927\u5c0f\u59d0\uff0c \u53ef\u89c9\u7740\u5403\u5b9a\u6211\u4e86\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u7ed9\u4e86\u6211\u5f88\u591a\u7684\u7f9e\u8fb1\uff0c\u54b1\u4eec\u4e4b\u95f4\u603b\u8981\u6709\u4e2a\u4e86\u65ad\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u8fd9\u5730\u65b9\u592a\u5c0f\u4e86\u4e00\u70b9\uff0c\u5f69\u8679\u697c\u4e0b\uff0c\u662f\u4e00\u7247\u5e7f\u5927\u7684\u8349\u5730\uff0c\u90a3\u5730\u65b9\uff0c\u53ef\u4ee5\u653e\u624b\u4e00\u6218\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u542c\u8bf4\u9ad8\u5821\u4e3b\u7684\u5200\u6cd5\uff0c\u4e00\u76f4\u9886\u8896\u6b66\u6797\uff0c\u4eca\u5929\uff0c\u6211\u4e5f\u8bb8\u80fd\u6709\u673a\u4f1a\uff0c\u9886\u6559\u4e00\u4e0b\u9ad8\u5821\u4e3b\u7684\u5200\u6cd5\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u542c\u8bf4\uff0c\u96f7\u5802\u4e3b\u4e00\u8eab\u6a2a\u7ec3\u5de5\u592b\u5df2\u7ecf\u5230\u4e86\u5200\u67aa\u4e0d\u5165\u4e4b\u5883\uff0c\u5c0f\u59b9\u8fd9\u4e03\u8272\u5251\uff0c\u4e0d\u77e5\u9053\u80fd\u4e0d\u80fd\u5e94\u654c\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u3001\u96f7\u65e0\u5f71\u8fc5\u901f\u5730\u884c\u51fa\u4e86\u5f69\u8679\u697c\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u3001\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\u4e5f\u8ddf\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5929\u5df2\u5927\u4eae\uff0c\u4e1c\u65b9\u5929\u9645\uff0c\u6cdb\u8d77\u4e86\u4e07\u9053\u971e\u5149\u3002 \u3000\u3000\u5f69\u8679\u697c\u5916\u7684\u8349\u576a\u4e0a\uff0c\u6a2a\u7acb\u7740\u4e00\u6392\u4eba\u3002 \u3000\u3000\u5341\u4e8c\u4e2a\u4eba\uff0c\u90fd\u7a7f\u7740\u4e00\u8eab\u9ed1\u8272\u7684\u8863\u670d\uff0c\u90fd\u4f69\u7740\u957f\u5251\u3002 \u3000\u3000\u5434\u53cc\u5973\u76ee\u5149\u8f6c\u52a8\uff0c\u6253\u91cf\u4e86\u5341\u4e8c\u4e2a\u9ed1\u8863\u4eba\u4e00\u773c\uff0c\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u4ed6\u4eec\u90fd\u662f\u4f60\u795e\u5200\u5802\u7684\u6740\u624b?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5341\u4e8c\u5251\u58eb\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u662f\u4e0d\u662f\u7b2c\u4e00\u6d41\u7684\u6740\u624b?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4ed6\u4eec\u4e0d\u662f\u654c\u624b\uff0c\u53bb\u53ec\u96c6\u4f60\u624b\u4e0b\u7b2c\u4e00\u6d41\u6740\u624b\u6765\u5427!\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u4ed6\u4eec\u53ef\u4ee5\u5e94\u4ed8\u4e00\u9635\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6c5f\u6e56\u4eba\u6709\u4e00\u4e2a\u901a\u75c5\uff0c\u4e0d\u5230\u9ec4\u6cb3\u4e0d\u6b7b\u5fc3\uff0c\u96f7\u5802\u4e3b\u5927\u6982\u662f\u89c9\u7740\u6211\u6740\u4e0d\u4e86\u4ed6\u4eec\uff0c\u662f\u5427?\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u7ad9\u7740\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u4ed6\u867d\u7136\u662f\u548c\u9ad8\u5e7d\u5170\u76f8\u5904\u5f88\u4e45\uff0c\u800c\u4e14\uff0c\u82b1\u5c3d\u4e86\u5fc3\u8840\uff0c\u628a\u9ad8\u5e7d\u5170\u9001\u5165\u4e86\u9b54\u59d1\u95e8\u4e0b\uff0c\u7ec3\u6210\u4e86\u4e03\u8272\u5251\u7edd\u6280\uff0c\u4f46\u4ed6\u672c\u4eba\uff0c\u4e5f\u672a\u770b\u8fc7\u4e03\u8272\u5251\u7684\u5a01\u529b\u3002 \u3000\u3000\u6c5f\u6e56\u4e0a\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u770b\u8fc7\u4e03\u8272\u5251\uff0c\u56e0\u4e3a\uff0c\u770b\u8fc7\u4e03\u8272\u5251\u7684\u4eba\u90fd\u5df2\u7ecf\u6b7b\u4e86\u3002 \u3000\u3000\u4e03\u8272\u5251\u4e00\u5411\u5355\u4f20\uff0c\u800c\u4e14\u9650\u5b9a\u5973\u6027\uff0c\u8fd9\u4e9b\u4eba\uff0c\u5927\u90fd\u5f88\u5b64\u50fb\uff0c\u79bb\u7fa4\u72ec\u5c45\uff0c\u6240\u4ee5\uff0c\u4e5f\u6ca1\u6709\u770b\u5230\u5979\u4eec\u7ec3\u5251\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u662f\u552f\u4e00\u8eab\u6000\u4e03\u8272\u5251\u7edd\u6280\u800c\u672a\u79bb\u7fa4\u72ec\u5c45\u7684\u4eba\u3002 \u3000\u3000\u4f46\u522b\u4eba\u53ea\u770b\u5230\u4e86\u5979\u4f69\u5e26\u7684\u4e03\u8272\u5251\uff0c\u5374\u6ca1\u6709\u4eba\u770b\u5230\u5979\u7ec3\u3002 \u3000\u3000\u9ad8\u5929\u5065\u66fe\u7ecf\u8d39\u5c3d\u5fc3\u673a\u60f3\u770b\u5230\u5979\u7ec3\u5251\u7684\u60c5\u5f62\u3002 \u3000\u3000\u4ed6\u81ea\u89c9\u770b\u5230\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u4ed4\u7ec6\u60f3\u4e00\u60f3\u65f6\uff0c\u53c8\u89c9\u7740\u4ec0\u4e48\u90fd\u6ca1\u6709\u770b\u5230\u3002 \u3000\u3000\u539f\u6765\uff0c\u4ed6\u770b\u5230\u7684\uff0c\u53ea\u662f\u9ad8\u5e7d\u5170\u5728\u7ec3\u4e60\u4e00\u652f\u5251\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5077\u770b\u4e86\u4e09\u6b21\uff0c\u4e09\u6b21\uff0c\u90fd\u662f\u540c\u4e00\u989c\u8272\u7684\u5251\u3002 \u3000\u3000\u90a3\u662f\u4e00\u652f\u767d\u8272\u7684\u5251\u3002 \u3000\u3000\u5251\u6cd5\u5f88\u51cc\u5389\uff0c\u4f46\u53ea\u6709\u4e00\u652f\u5251\uff0c \u3000\u3000\u5982\u82e5\u4e00\u4e2a\u4eba\uff0c\u7528\u4e24\u652f\u5251\u65f6\uff0c\u4ed6\u7684\u5251\u6cd5\u3001\u53d8\u5316\uff0c\u4e00\u5b9a\u548c\u4e00\u652f\u4e0d\u540c\u3002 \u3000\u3000\u4f55\u51b5\uff0e\u9ad8\u5e7d\u5170\u7528\u7684\u662f\u4e03\u652f\u5251\uff0c\u4e03\u652f\u989c\u8272\u4e0d\u540c\u7684\u5251\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e5f\u6ca1\u6709\u770b\u8fc7\u3002\u5979\u770b\u7684\uff0c\u4e5f\u662f\u4e00\u652f\u5251\u3002\u4e03\u8272\u5251\uff0c\u5c31\u662f\u8fd9\u4e48\u4e00\u4e2a\u795e\u79d8\u7684\u95e8\u6237\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u76ee\u5149\u4e00\u63a0\u9ad8\u5929\u5065\u548c\u96f7\u65e0\u5f71\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4e24\u4f4d\u7ad9\u7740\u4e0d\u52a8\uff0c\u60f3\u6765\uff0c\u662f\u51c6\u5907\u8981\u727a\u7272\u8fd9\u5341\u4e8c\u4e2a\u4eba\uff0c\u770b\u770b\u6211\u7684\u5251\u6cd5\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u5927\u80c6\u51fa\u624b\uff0c\u6211\u4f1a\u53eb\u4ed6\u770b\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54e6!\u4f60\u4f1a\u969c\u773c\u6cd5?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u90a3\u5012\u6ca1\u6709\uff0c\u4e0d\u8fc7\uff0c\u6211\u4f1a\u8ba9\u4ed6\u4eec\u770b\u4e0d\u5230\uff0c\u8bf8\u4f4d\u4e0d\u4fe1\uff0c\u7acb\u523b\u53ef\u89c1\u5206\u6653\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5927\u59ca\uff0c\u6211\u77e5\u9053\u4f60\u6709\u529e\u6cd5\uff0c\u4e0d\u7528\u8d39\u4e8b\uff0c\u4e03\u8272\u5251\u5982\u662f\u5f88\u5bb9\u6613\u53eb\u4eba\u770b\u6e05\u695a\uff0c \u65e9\u5c31\u5728\u6c5f\u6e56\u4e0a\u4f20\u5f00\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d!\u6211\u76f8\u4fe1\u4f60\u7684\u8bdd\u3002\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4e0d\u4f46\u662f\u9ad8\u5929\u5065\u548c\u96f7\u65e0\u5f71\uff0c\u5c31\u662f\u5434\u53cc\u5973\u548c\u4f55\u5bd2\u8863\uff0c\u4e5f\u5b89\u8010\u4e0d\u4e0b\u5fc3\u4e2d\u7684\u597d\u5947\uff0c\u5e0c\u671b\u89c1\u8bc6\u4e00\u4e0b\u4e03\u8272\u5251\u6cd5\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7f13\u7f13\u884c\u4e86\u4e24\u6b65\uff0c\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u8981\u4ed6\u4eec\u51fa\u624b\u5427\uff01\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u4ed6\u4eec\u4e0d\u4f1a\u5148\u51fa\u624b\uff0c \u56e0\u4e3a\uff0c\u6211\u4e0d\u76f8\u4fe1\uff0c\u6709\u4eba\u80fd\u4e00\u4e3e\u624b\u95f4\uff0c\u6740\u6b7b\u4e00\u5341\u4e8c\u4e2a\u5251\u58eb\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a \u201c\u6211\u77e5\u9053\uff0c \u4f60\u4e0d\u8fc7\u662f\u60f3\u7528\u5341\u4e8c\u4e2a\u5251\u58eb\u7684\u6027\u547d\uff0c\u5f15\u8bf1\u6211\u7528\u4e03\u8272\u5251\u6cd5\uff0c\u96f7\u65e0\u5f71\uff0c\u6211\u4f1a\u8ba9\u4f60\u770b\u89c1\u7684\uff0e\u4e0d\u8fc7\uff0c\u4f60\u8fd8\u662f\u65e0\u6cd5\u4e86\u89e3\u3002\u201d \u3000\u3000\u53e3\u4e2d\u867d\u5bf9\u96f7\u65e0\u5f71\u8bf4\u8bdd\uff0c\u76ee\u5149\u5374\u4e00\u63a0\u7ad9\u5728\u4e00\u4e08\u5f00\u5916\u7684\u9ad8\u5929\u5065\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u9ad8\u5929\u5065\u9009\u62e9\u7684\u624d\u662f\u6700\u597d\u7684\u5730\u65b9\uff0c\u90a3\u4f4d\u7f6e\u3001\u90a3\u8ddd\u79bb\uff0c\u6b63\u662f\u770b\u6e05\u695a\u5168\u573a\u6700\u4f73\u7684\u4f4d\u7f6e\u3002 \u3000\u3000\u5341\u4e8c\u4e2a\u9ed1\u8863\u5251\u58eb\uff0c\u7f13\u7f13\u6563\u5f00\uff0c\u4ed6\u4eec\u5df2\u7ecf\u5e03\u4e86\u5408\u51fb\u4e4b\u52bf\uff0c\u4f46\u5374\u4e00\u76f4\u6ca1\u6709\u51fa\u624b\u3002 \u3000\u3000\u663e\u7136\uff0c\u4ed6\u4eec\u5728\u7b49\u5f85\uff0c\u7b49\u5f85\u7740\u9ad8\u5e7d\u5170\u51fa\u624b\u3002 \u3000\u3000\u5148\u884c\u51fa\u624b\u7684\u4eba\uff0c\u5251\u52bf\u4e0a\u7684\u53d8\u5316\uff0c \u81ea\u7136\u8ba9\u4eba\u5f88\u5bb9\u6613\u770b\u5f97\u6e05\u695a\u3002 \u53cc\u65b9\u76f8\u6301\u4e86\u826f\u4e45\uff0c\u96f7\u65e0\u5f71\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u9053\uff1a \u201c\u4e8c\u5c0f\u59d0\uff0c\u600e\u7684\u8fd8\u4e0d\u51fa\u624b\uff0c\u54b1\u4eec\u8fd8\u5728\u7b49\u5f85\u4e00\u5f00\u773c\u754c\u5462\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u8010\u5fc3\u4e00\u4e9b\uff0c\u50cf\u4e03\u8272\u5251\u8fd9\u6837\u7684\u5251\u6cd5\uff0c\u5c82\u662f\u80fd\u591f\u8f7b\u6613\u770b\u5230\u7684\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u51fa\uff0c\u50cf\u8fd9\u6837\u7b49\u5f85\u4e0b\u53bb\uff0c\u5bf9\u4f60\u59d1\u5a18\uff0c\u4f1a\u6709\u4ec0\u4e48\u597d\u5904?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u4e0d\u4f1a\u6709\u4ec0\u4e48\u574f\u5904\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\uff0c\u53ef\u662f\u60f3\u7b49\u5f85\u9634\u9633\u5821\u7684\u63f4\u624b\u8d76\u6765\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u94dc\u5899\u4e00\u6837\uff0c\u9634\u9633\u5821\u4e2d\u4eba\uff0c\u53ea\u6015\u662f\u5f88\u96be\u8fdb\u5f97\u6765\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u90a3\u4e48\u59d1\u5a18\u5728\u7b49\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u7528\u4e03\u8272\u5251\u7684\u4eba\uff0c\u8fd8\u6709\u4e00\u5fc5\u8981\u7684\u6761\u4ef6\uff0c\u90a3\u5c31\u662f\u5fcd\u8010\uff0c\u654c\u4e0d\u52a8\uff0c\u6211\u4e0d\u52a8\uff0c\u654c\u4e00\u52a8\uff0c\u6211\u5148\u52a8\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d!\u4e09\u3001\u56db\u53f7\u5251\u58eb\u51fa\u624b\u3002\u201d \u3000\u3000\u4f46\u89c1\u4e24\u4e2a\u9ed1\u8863\u5251\u58eb\uff0c\u5ffd\u7136\u95f4\u5411\u524d\u51b2\u6765\uff0c\u624b\u4e2d\u7684\u957f\u5251\uff0c\u968f\u7740\u5411\u524d\u5954\u52a8\u7684\u8eab\u8eaf\uff0c\u6709\u5982\u4e24\u9053\u7535\u5149\u4e00\u822c\uff0c\u75be\u5c04\u800c\u81f3\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5ffd\u7136\u4e00\u8f6c\u5a07\u8eaf\uff0c\u4e00\u7ea2\u4e00\u767d\u4e24\u9053\u5251\u5149\u95ea\u8d77\u3002 \u3000\u3000\u6ca1\u6709\u542c\u5230\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u53ea\u89c1\u5230\u5251\u8292\u4ea4\u9519\uff0c\u4e24\u4e2a\u5411\u524d\u5954\u51b2\u7684\u9ed1\u8863\u5251\u58eb\uff0c\u4eba\u5df2\u7ecf\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e0d\u77e5\u4f55\u65f6\uff0c\u9ad8\u5e7d\u5170\u5df2\u7ecf\u8fd8\u5251\u5165\u9798\uff0c\u9759\u9759\u7684\u7ad9\u5728\u539f\u5730\u3002 \u3000\u3000\u9c9c\u8840\uff0c\u75be\u5c04\u800c\u51fa\u3002 \u3000\u3000\u4e24\u4e2a\u9ed1\u8863\u4eba\uff0c\u90fd\u88ab\u4e00\u5251\u6d1e\u900f\u524d\u80f8\u5fc3\u810f\u8981\u5bb3\u800c\u6b7b\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u770b\u6e05\u695a\u4e86\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u5f88\u9ad8\u660e\u7684\u5251\u6cd5\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u662f\u5426\u613f\u610f\u81ea\u5df1\u51fa\u624b\u4e00\u8bd5\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u56de\u987e\u4e86\u9ad8\u5929\u5065\u4e00\u773c\u3002 \u3000\u3000\u4ed6\u5df2\u88ab\u9ad8\u5e7d\u5170\u6446\u5e03\u5f97\u6709\u4e9b\u5fc3\u4e2d\u4e0d\u5b89\u3002 \u3000\u3000\u9ad8\u5929\u5065\u662f\u4e2a\u5f88\u591a\u7591\u7684\u4eba\uff0c\u96f7\u65e0\u5f71\u5f88\u660e\u767d\u81ea\u5df1\u5904\u5883\u7684\u5c34\u5c2c\u3002 \u3000\u3000\u4e03\u8272\u5251\u6cd5\u5947\u5e7b\u3001\u6df1\u5965\uff0c\u83ab\u53ef\u9884\u6d4b\uff0c\u4f46\u4e5f\u4e0d\u81f3\u4e8e\u4f7f\u96f7\u65e0\u5f71\u5fc3\u4e2d\u5bb3\u6015\u3002 \u3000\u3000\u4ed6\u62c5\u5fc3\u7684\u662f\u9ad8\u5929\u5065\u5bf9\u81ea\u5df1\u7684\u6000\u7591\u3002 \u3000\u3000\u9ad8\u5929\u5065\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u96f7\u5802\u4e3b\uff0c\u4e0d\u8981\u6fc0\u52a8\uff0c\u4e0d\u7528\u51fa\u624b\uff0c\u4f60\u662f\u7b2c\u4e00\u5821\u7684\u67f1\u77f3\uff0c\u51e0\u53e5\u6311\u62e8\u4e4b\u8a00\uff0c\u8fd8\u65e0\u6cd5\u4f7f\u6211\u4e2d\u8ba1\uff0c\u4e0b\u4ee4\u53ec\u6765\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u4e09\u4eba\u4e00\u4f53\u683c\u6740\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u8138\u4e0a\u6cdb\u8d77\u4e86\u7b11\u5bb9\uff0c\u9053\uff1a \u201c\u5821\u4e3b\u660e\u5bdf\uff0c \u4e8c\u5c0f\u59d0\uff0c \u4f60\u8fd8\u6709\u4ec0\u4e48\u82b1\u62db\u8981\u800d?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u96f7\u5802\u4e3b\uff0c \u53ea\u8981\u4f60\u7ed9\u6211\u4e00\u70b9\u673a\u4f1a\uff0c\u6211\u5c31\u4f1a\u53d6\u4f60\u6027\u547d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7a81\u7136\u63a5\u9053\uff1a \u201c\u9ad8\u5821\u4e3b\uff0c \u4f60\u7684\u5200\u6cd5\u5929\u4e0b\u65e0\u53cc\uff0c\u6211\u60f3\u9886\u6559\u51e0\u62db\uff0c\u4e0d\u77e5\u53ef\u5426\u8d50\u6559?\u201d \u3000\u3000\u201c\u6c34\u706b\u65e0\u60c5\u201d\u592a\u53ef\u6015\u4e86\uff0c\u9ad8\u5e7d\u5170\u3001\u5434\u53cc\u5973\uff0c\u90fd\u6ca1\u6709\u4fe1\u5fc3\u5bf9\u4ed8\u8fd9\u6837\u7684\u6697\u5668\uff0c\u5434\u53cc\u5973\u5f88\u5feb\u7684\u4f5c\u4e86\u51b3\u5b9a\uff0c\u76f4\u63a5\u5411\u9ad8\u5929\u5065\u6311\u6218\u3002 \u3000\u3000\u4ee5\u4ed6\u7b2c\u4e00\u5821\u4e3b\u7684\u8eab\u4efd\uff0c\u5e94\u8be5\u4e0d\u4f1a\u9000\u7f29\u3002 \u3000\u3000\u5374\u4e0d\u6599\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8981\u5411\u8001\u592b\u6311\u6218?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u6562\u4e0d\u6562\u5e94\u6218\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u662f\u6562\u4e0d\u6562\uff0c\u800c\u662f\u8001\u592b\u4e0d\u7528\u51fa\u624b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u5bb3\u6015\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u653e\u773c\u5f53\u4eca\u6c5f\u6e56\uff0c\u8001\u592b\u6015\u8fc7\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u4e0d\u6015\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u548c\u6211\u653e\u624b\u4e00\u6218\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u4f60\u4eec\u5728\u7b2c\u4e00\u5821\u4e2d\uff0c\u7528\u4e0d\u7740\u8001\u592b\u51fa\u624b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u603b\u6709\u4e00\u5929\uff0c\u6211\u4f1a\u548c\u4f60\u4e00\u51b3\u751f\u6b7b\u7684\u2026\u2026\u201d \u3000\u3000\u56de\u987e\u9ad8\u5e7d\u5170\u548c\u4f55\u5bd2\u8863\u4e00\u773c\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u4e0e\u5176\u7b49\u5230\u2018\u6c34\u706b\u65e0\u60c5\u2019\u6697\u5668\u88ad\u6765\uff0c\u4f55\u4e0d\u73b0\u5728\u51b2\u51fa\u53bb\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\uff0c\u542c\u8bf4\u4f60\u884c\u8d70\u6c5f\u6e56\u4e4b\u65f6\uff0c\u6709\u5f88\u591a\u53d6\u4eba\u6027\u547d\u7684\u6697\u5668\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8fdb\u5165\u7b2c\u4e00\u5821\u4e4b\u540e\uff0c\u5c31\u89c9\u7740\u8fd9\u4e9b\u4e1c\u897f\u592a\u8fc7\u6b79\u6bd2\uff0c\u6240\u4ee5\uff0c\u6211\u5c31\u6ca1\u6709\u518d\u7528\u8fc7\uff0c\u8fd9\u4e00\u6b21\uff0c\u6211\u5e26\u4f60\u4eec\u4e24\u4e2a\u4eba\uff0c\u91cd\u56de\u5f69\u8679\u697c\u7684\u7528\u5fc3\uff0c\u5c31\u5728\u91cd\u6574\u8fd9\u4e9b\u4e1c\u897f\uff0c\u6211\u5df2\u7ecf\u51c6\u5907\u597d\u4e86\uff0c\u7b49\u2014\u4f1a\uff0c\u6211\u4f1a\u8ba9\u4ed6\u4eec\u89c1\u8bc6\u4e00\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u597d!\u5c0f\u59b9\u5f00\u9053\uff0c\u4f60\u4eec\u8ddf\u7740\u6211\u95ef\u3002\u201d \u3000\u3000\u8bdd\u843d\u53e3\uff0c\u5251\u5df2\u51fa\u9798\uff0c\u76f4\u5411\u6b63\u5317\u65b9\u9762\u51b2\u51fa\u3002 \u3000\u3000\u5434\u53cc\u5973\u7d27\u968f\u800c\u8d77\uff0c\u4f55\u5bd2\u8863\u8ffd\u5728\u5434\u53cc\u5973\u7684\u8eab\u540e\u3002 \u3000\u3000\u5b88\u5728\u6b63\u5317\u65b9\u7684\uff0c\u6709\u56db\u4e2a\u9ed1\u8863\u5251\u58eb\u3002 \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u4eba\uff0c\u56db\u628a\u957f\u5251\uff0c\u5728\u9ad8\u5e7d\u5170\u4e00\u52a8\u7684\u65f6\u95f4\uff0c \u5df2\u7136\u4e3e\u4e86\u8d77\u6765\u3002 \u56db\u628a\u5251\uff0c\u5e03\u6210\u4e86\u4e00\u7247\u5251\u5e55\u3002 \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u4eba\uff0c\u4e00\u5f00\u59cb\uff0c\u5c31\u7528\u7684\u5b88\u52bf\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u6325\u5251\u98de\u51fb\uff0c \u53ea\u89c1\u4e00\u9053\u767d\u8292\uff0c\u4f46\u8fd1\u4e86\u56db\u4e2a\u9ed1\u8863\u5251\u58eb\u4e4b\u540e\uff0c\u5ffd\u7136\u95f4\uff0c\u5e7b\u5316\u51fa\u9ed1\u7ea2\u767d\u4e09\u9053\u5947\u5149\u3002 \u3000\u3000\u4e09\u8272\u5149\u8292\uff0c\u8000\u773c\u751f\u82b1\u3002 \u3000\u3000\u4f46\u771f\u6b63\u53d6\u547d\uff0c\u5374\u662f\u53e6\u4e00\u9053\u7d2b\u8272\u7684\u5251\u5149\u3002 \u3000\u3000\u7d2b\u8272\u7684\u5251\u5149\uff0c\u7531\u4e0b\u76d8\u626b\u8fc7\uff0c\u5207\u5f00\u4e86\u56db\u4e2a\u6b66\u58eb\u7684\u5c0f\u8179\u3002 \u3000\u3000\u80a0\u809a\u5916\u6d41\uff0c\u9c9c\u8840\u55b7\u6d8c\u3002 \u3000\u3000\u597d\u5389\u5bb3\u7684\u4e03\u8272\u5251\uff0c\u597d\u6b8b\u9177\u7684\u6740\u4eba\u624b\u6cd5\u3002 \u3000\u3000\u7ad9\u5728\u4e00\u4fa7\u89c2\u6218\u7684\u9ad8\u5929\u5065\uff0c\u4e5f\u770b\u5f97\u4e3a\u4e4b\u4e00\u5446\u3002 \u3000\u3000\u4e03\u8272\u5251\uff0c\u4e00\u62db\u6740\u4eba\uff0c\u679c\u7136\u4e0d\u5047\uff0c \u3000\u3000\u8fd9\u662f\u4e00\u5957\u5947\u5e7b\u7684\u5251\u6cd5\uff0c\u4e0d\u8bb2\u7a76\u5251\u52bf\u53d8\u5316\uff0c\u8bb2\u7a76\u7684\u662f\u51fa\u5251\u7684\u624b\u6cd5\u3002 \u3000\u3000\u771f\u6b63\u7684\u5251\u6cd5\u3001\u53d8\u5316\uff0c\u53ea\u5728\u4e00\u67c4\u5251\u4e0a\uff0c\u4f59\u4e0b\u7684\u516d\u8272\u5251\uff0c\u90fd\u662f\u6740\u62db\u3002 \u3000\u3000\u5434\u53cc\u5973\u5e76\u672a\u5e2e\u52a9\u9ad8\u5e7d\u5170\uff0c\u4f46\u5979\u5374\u963b\u6321\u4f4f\u4e86\u4e1c\u3001\u897f\u4e24\u9762\u7684\u5408\u51fb\u6b66\u58eb\u3002 \u3000\u3000\u4e00\u652f\u5251\uff0c\u5c01\u6321\u4f4f\u4e1c\u65b9\u653b\u6765\u7684\u4e24\u4e2a\u9ed1\u8863\u5251\u58eb\uff0c\u4e24\u9053\u9752\u8292\uff0c\u5c04\u5012\u4e86\u897f\u9762\u65b9\u4f4d\u4e0a\u51b2\u6765\u7684\u4eba\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u5979\u5c04\u51fa\u4ec0\u4e48\u6697\u5668\uff0c\u4e24\u4e2a\u6b66\u58eb\uff0c\u7acb\u523b\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u522b\u4eba\u4e0d\u8ba4\u8bc6\uff0c\u4f46\u9ad8\u5929\u5065\u8ba4\u8bc6\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u5929\u9b54\u9488\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5bf9\u4ed8\u4f60\u9ad8\u5821\u4e3b\u8fd9\u79cd\u4eba\uff0c\u4e0d\u62e9\u624b\u6bb5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u4e5f\u540c\u65f6\u6321\u4f4f\u4e86\u5357\u65b9\u653b\u6765\u7684\u4e24\u4e2a\u6b66\u58eb\uff0c\u91d1\u5251\u75be\u51fa\uff0c\u5c01\u5f00\u4e86\u4e24\u4e2a\u9ed1\u8863\u4eba\u7684\u5251\u52bf\uff0c\u4e24\u4e2a\u98de\u8f6e\uff0c\u968f\u7740\u91d1\u5251\u98de\u51fa\u3002 \u3000\u3000\u5251\u8f6e\u914d\u5408\u4f73\u5999\uff0c\u593a\u9b42\u53d6\u547d\uff0c\u4e24\u4e2a\u9ed1\u8863\u6b66\u58eb\u88ab\u98de\u8f6e\u5212\u7834\u4e86\u54bd\u5589\u800c\u6b7b\u3002 \u3000\u3000\u6210\u540d\u6b66\u6797\u7684\u9ad8\u624b\uff0c\u679c\u975e\u51e1\u54cd\u3002 \u3000\u3000\u5341\u4e8c\u4e2a\u9ed1\u8863\u5251\u58eb\uff0c\u7247\u523b\u95f4\uff0c\u53ea\u4f59\u4e0b\u4e24\u4e2a\u6d3b\u7684\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\uff0c\u5b9e\u5728\u5df2\u6ca1\u6709\u80fd\u529b\u963b\u6b62\u8fd9\u4e09\u4e2a\u9ad8\u624b\u3002 \u3000\u3000\u9664\u975e\u9ad8\u5929\u5065\u548c\u96f7\u65e0\u5f71\u4e5f\u51fa\u624b\u3002 \u3000\u3000\u4f46\u4e24\u4e2a\u4eba\u4e00\u76f4\u7ad9\u7740\u672a\u52a8\u3002 \u3000\u3000\u4e03\u8272\u5251\u5177\u6709\u5f3a\u5927\u7684\u5a01\u529b\uff0c\u90a3\u51e0\u62db\u5df2\u7ecf\u4e0d\u662f\u5251\u6cd5\uff0c\u800c\u662f\u4e00\u79cd\u51b7\u5389\u7684\u6740\u4eba\u624b\u6cd5\u3002 \u3000\u3000\u8fde\u9ad8\u5929\u5065\u548c\u96f7\u65e0\u5f71\u8fd9\u6837\u7684\u9ad8\u624b\uff0c\u90fd\u4e3a\u4e03\u8272\u5251\u6240\u9707\u52a8\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u4ecd\u7136\u672a\u77a7\u51fa\u6765\u4e00\u70b9\u95e8\u9053\u3002 \u3000\u3000\u9ad8\u5929\u5065\u53f9\u53e3\u6c14\uff0e\u9053\uff1a\u201c\u65e0\u5f71\uff0c\u4e03\u8272\u5251\u7684\u5a01\u529b\u5982\u4f55?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u51fa\u4eba\u610f\u6599\u7684\u5f3a\u5927\uff0c\u51e0\u4e4e\u4e0d\u662f\u4e00\u79cd\u5251\u6cd5\uff0c\u51fa\u5fc5\u4f24\u4eba\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u4e00\u76f4\u6ca1\u6709\u770b\u6e05\u695a\uff0c\u5979\u662f\u5982\u4f55\u51fa\u5251\u7684\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e03\u79cd\u8000\u76ee\u7684\u5f69\u8272\uff0c\u4f7f\u4eba\u76ee\u8ff7\u795e\u7729\uff0c\u5979\u5982\u4f55\u51fa\u5251\uff0c\u522b\u4eba\u5c31\u5f88\u96be\u770b\u5f97\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u5979\u6709\u4e03\u652f\u5251\uff0c\u53ea\u6709\u4e24\u53ea\u624b\uff0c\u5982\u4f55\u51fa\u5251\u80fd\u4f7f\u4e03\u628a\u5251\uff0c\u5728\u4e0d\u540c\u7684\u89d2\u5ea6\u4e4b\u5185\uff0c\u53ef\u4ee5\u4e00\u9f50\u653b\u51fb?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e03\u8272\u5251\u6700\u9ad8\u660e\u4e4b\u5904\uff0c\u6211\u521a\u624d\u770b\u4ed4\u7ec6\uff0c\u4f46\u4e5f\u6ca1\u6709\u770b\u5f97\u5f88\u6e05\u695a\u5979\u51fa\u5251\u7684\u624b\u6cd5\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u53ea\u662f\u4e00\u79cd\u6280\u5de7\u624b\u6cd5\uff0c\u6211\u76f8\u4fe1\u53ea\u8981\u6211\u4eec\u518d\u770b\u4e24\u6b21\uff0c\u5c31\u53ef\u4ee5\u770b\u51fa\u5979\u7684\u51fa\u5251\u624b\u6cd5\u4e86\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u770b\u6765\uff0c\u81f3\u5c11\u8fd8\u8981\u727a\u7272\u4e24\u6279\u4eba\u624b\uff0c\u624d\u53ef\u4ee5\u77a7\u51fa\u5979\u51fa\u5251\u7684\u624b\u6cd5\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u80fd\u627e\u51fa\u7834\u89e3\u4e03\u8272\u5251\u7684\u529e\u6cd5\uff0c\u5c31\u7b97\u591a\u727a\u7272\u4e00\u6279\u5200\u624b\uff0c\u4e5f\u5f88\u503c\u5f97\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u7528\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u6740\u6b7b\u4e86\u4ed6\u4eec\uff0c\u4e03\u8272\u5251\u5c82\u4e0d\u662f\u5c31\u6b64\u6d88\u5931\u4e86\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u65e0\u5f71\uff0c\u6211\u89c9\u7740\uff0c\u5434\u53cc\u5973\u548c\u5e7d\u5170\uff0c\u662f\u4e24\u4e2a\u5f88\u597d\u7684\u6740\u624b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u5c5e\u4e0b\u6709\u4e00\u4e9b\u987e\u8651\uff0c\u4e0d\u53d6\u5979\u4eec\u4e4b\u547d\uff0c\u53ea\u6015\u4f1a\u7ed9\u4ed6\u4eec\u9003\u8d70\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u7ed9\u5979\u4eec\u8fd9\u4e2a\u673a\u4f1a\uff0c\u5c31\u6740\u4e86\u5979\u4eec\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\u51b3\u5b9a\u4e86\uff0c\u5c5e\u4e0b\u53ea\u597d\u9075\u547d\u884c\u4e8b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u597d!\u4f60\u53bb\u51c6\u5907\u4e00\u4e0b\uff0c\u591a\u8c03\u96c6\u4e00\u4e9b\u9ad8\u624b\u6765\uff0c\u6700\u597d\u6349\u6d3b\u7684\uff0c\u4e07\u4e00\u6709\u95ee\u9898\uff0c\u54b1\u4eec\u5c31\u53ea\u597d\u628a\u5979\u4fe9\u6740\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u5979\u4eec\u73b0\u5728\u5230\u54ea\u91cc\u53bb?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5927\u6982\u53bb\u4e86\u94c1\u82b1\u9662\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u94c1\u82b1\u9662?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5bf9!\u90a3\u91cc\u4f4f\u4e86\u5341\u4e8c\u4e2a\u5973\u5b50\uff0c\u90fd\u662f\u5979\u8bad\u7ec3\u7684\u4eba\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\u5bf9\u5979\u4eec\u7684\u5f71\u54cd\u529b\u5982\u4f55?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u4f1a\u592a\u5927\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\u6709\u628a\u63e1\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6709! \u5341\u4e8c\u98de\u71d5\uff0c\u90fd\u5728\u4e24\u4e2a\u6b63\u526f\u9886\u961f\u7684\u63a7\u5236\u4e4b\u4e0b\uff0c\u5979\u4eec\u542c\u547d\u4e8e\u6211\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\u77e5\u9053\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u5979\u5f88\u806a\u660e\uff0c\u5927\u6982\u6709\u70b9\u660e\u767d\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u4f1a\u592a\u6e05\u695a\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u5821\u4e3b\u6545\u610f\u4e0d\u7406\u4f1a\u5979\u4eec\u4e86?\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u9053\uff1a \u201c\u6211\u4e86\u89e3\u5979\u4eec\u7684\u53bb\u5904\uff0c\u6240\u4ee5\uff0c\u8ba9\u5979\u4eec\u6295\u5165\u94c1\u82b1\u9662\u53bb\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u81ea\u6295\u7f57\u7f51?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5bf9!\u53bb\u51c6\u5907\u5427!\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c5e\u4e0b\u8fd9\u5c31\u53bb\u8c03\u96c6\u4eba\u624b\u3002\u201d \u3000\u3000\u8f6c\u8eab\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u96f7\u5802\u4e3b\uff0c\u8bb0\u7740\u6211\u8bf4\u8fc7\u7684\u8bdd\uff0e\u751f\u64d2\u4e86\u5434\u53cc\u5973\uff0c\u5979\u4ecd\u7136\u662f\u4f60\u7684\u59bb\u5b50\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u591a\u8c22\u5821\u4e3b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5e26\u7740\u4f55\u5bd2\u8863\u548c\u5434\u53cc\u5973\u884c\u5165\u4e86\u94c1\u82b1\u9662\u4e2d\u3002 \u3000\u3000\u94c1\u82b1\u9662\u5c31\u5728\u7b2c\u4e00\u5821\u82b1\u56ed\u897f\u5317\u65b9\u4f4d\u4e0a\u3002 \u3000\u3000\u90a3\u662f\u4e00\u5ea7\u5b81\u9759\u7684\u9662\u843d\uff0c\u5f88\u5bbd\u655e\u7684\u5ead\u9662\uff0c\u5f88\u9ad8\u7684\u56f4\u5899\u3002 \u3000\u3000\u7b2c\u4e00\u5821\u4f9d\u5c71\u800c\u7b51\uff0c\u5360\u5730\u5e7f\u5927\uff0c\u4f46\u5374\u6709\u5f88\u591a\u7684\u56f4\u5899\uff0c\u628a\u5b83\u5272\u6210\u4e86\u4e00\u4e2a\u4e00\u4e2a\u5355\u5143\uff0c\u6bcf\u4e00\u4e2a\u5355\u5143\u4e2d\uff0c\u90fd\u5145\u6ee1\u7740\u795e\u79d8\u3002 \u3000\u3000\u94c1\u82b1\u9662\u5185\u7684\u5efa\u7b51\uff0c\u4e8b\u5b9e\u4e0a\uff0c \u5341\u5206\u7b80\u5355\uff0c\u9664\u4e86\u9662\u5b50\u5e7f\u5927\u4e4b\u5916\uff0c\u53ea\u6709\u4e00\u5ea7\u5c0f\u5385\uff0c\u4e24\u4fa7\u51e0\u95f4\u53a2\u623f\uff0c\u5c31\u662f\u5341\u4e8c\u98de\u71d5\u7684\u5bbf\u4f4f\u4e4b\u5904\u3002 \u3000\u3000\u73b0\u5728\uff0c\u9ad8\u5e7d\u5170\u5c31\u5750\u5728\u5385\u4e2d\u7684\u4e00\u5f20\u592a\u5e08\u6905\u4e0a\u3002 \u3000\u3000\u90a3\u4e5f\u662f\u5979\u5e38\u5750\u7684\u5730\u65b9\uff0c\u5341\u4e8c\u98de\u71d5\uff0c\u672c\u662f\u5730\u4e00\u624b\u8bad\u7ec3\u7684\u5c5e\u4e0b\u3002 \u3000\u3000\u5341\u4e8c\u4e2a\u5973\u5c06\uff0c\u5206\u6210\u4e24\u961f\uff0c\u6bcf\u4e00\u961f\u516d\u4e2a\u4eba\uff0c\u5206\u7a7f\u4e24\u79cd\u7edd\u5bf9\u4e0d\u540c\u7684\u8863\u670d\uff0c\u4e00\u961f\u7ea2\u88f3\uff0c\u4e00\u961f\u7eff\u886b\u3002 \u3000\u3000\u5434\u53cc\u5973\u4ed4\u7ec6\u6253\u91cf\u4e86\u5341\u4e8c\u4e2a\u5973\u5b50\u4e00\u773c\uff0c\u53d1\u89c9\u5979\u4eec\u7684\u5e74\u9f84\uff0c\u5b8c\u5168\u4e0d\u540c\uff0c\u6700\u5927\u7684\u4f3c\u4e4e\u6709\u4e09\u5341\u591a\u5c81\uff0c\u5e74\u8f7b\u7684\u4e5f\u6709\u4e8c\u5341\u4e09\u56db\u5c81\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5f00\u59cb\u8bad\u7ec3\u5979\u4eec\uff0c\u53ea\u6709\u4e09\u5e74\u65f6\u95f4\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u4e9b\u4eba\uff0c\u5728\u672a\u88ab\u9ad8\u5e7d\u5170\u8bad\u7ec3\u4e4b\u524d\uff0c\u5df2\u6709\u4e86\u6b66\u529f\u57fa\u7840\u3002 \u3000\u3000\u7ea2\u8863\u9886\u961f\u7684\u53eb\u7ea2\u82b1\uff0c\u7eff\u8863\u9886\u961f\u7684\u53eb\u7eff\u53f6\u3002 \u3000\u3000\u81ea\u7136\uff0c\u8fd9\u4e0d\u662f\u5979\u4eec\u7684\u771f\u5b9e\u540d\u5b57\uff0c\u800c\u662f\u9ad8\u5e7d\u5170\u7ed9\u5979\u4eec\u53d6\u7684\u3002 \u3000\u3000\u6bcf\uff0c\u4e00\u4e2a\u5973\u5c06\u7684\u8eab\u4e0a\uff0c\u90fd\u4f69\u7740\u4e09\u652f\u5251\u3002 \u3000\u3000\u4e03\u8272\u5251\uff0c\u5979\u4eec\u5df2\u7ecf\u7ec3\u6210\u4e86\u4e09\u79cd\u3002 \u3000\u3000\u5385\u4e2d\u7684\u6c14\u6c1b\u5f88\u4e25\u8083\uff0c\u9ad8\u5e7d\u5170\u795e\u60c5\u51b7\u5cfb\u5730\u8bf4\u9053\uff1a \u201c\u4f60\u4eec\u542c\u7740\uff0c\u7b2c\u4e00\u5821\u795e\u5200\u5802\u4e2d\u7684\u4eba\u8981\u6740\u6211\uff0c\u4f60\u4eec\u662f\u5426\u613f\u4e3a\u6211\u6548\u547d\uff0c\u613f\u610f\u7684\u8bdd\uff0c\u6211\u5f88\u611f\u6fc0\uff0c\u4e0d\u613f\u610f\uff0c\u6211\u4e5f\u4e0d\u4f1a\u8d23\u602a\uff0c\u4f60\u4eec\u60f3\u4e00\u4e0b\u518d\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u5341\u4e2a\u5973\u5c06\u7684\u76ee\u5149\uff0c\u5206\u522b\u6295\u6ce8\u5728\u7ea2\u82b1\u3001\u7eff\u53f6\u7684\u8eab\u4e0a\uff0c\u4f3c\u4e4e\u662f\u7b49\u5f85\u8fd9\u4e24\u4eba\u7684\u51b3\u5b9a\u3002 \u3000\u3000\u663e\u7136\uff0c\u8fd9\u4e24\u4e2a\u4eba\u5728\u4f17\u5973\u5c06\u7684\u5fc3\u76ee\u4e2d\uff0c\u5f88\u6709\u6743\u5a01\u3002 \u3000\u3000\u6c89\u9ed8\u8db3\u8db3\u6709\u4e00\u523b\u5de5\u592b\u4e4b\u4e45\uff0c\u7ea2\u82b1\u624d\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u4e0e\u795e\u5200\u5802\u51b2\u7a81\u8d77\u6765\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u5821\u4e3b\u77e5\u9053\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u662f\u4f55\u7b49\u806a\u660e\u7684\u4eba\uff0c\u4e00\u542c\u7ea2\u82b1\u53e3\u6c14\uff0c\u7acb\u523b\u80f8\u4e2d\u4e86\u7136\u3002 \u3000\u3000\u70b9\u70b9\u5934\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a \u201c\u6211\u4e5f\u65e0\u610f\u8981\u62d6\u4f60\u4eec\u5377\u5165\u4e86\u8fd9\u573a\u7ea0\u7eb7\u4e4b\u4e2d\u3002\u201d \u7ea2\u82b1\u9053\uff1b\u201c\u59d1\u5a18\u5982\u662f\u548c\u795e\u5200\u5802\u51b2\u7a81\uff0c\u54b1\u4eec\u4e5f\u8bb8\u53ef\u4ee5\u5e2e\u5fd9\uff0c\u5982\u82e5\u662f\u8fdd\u6297\u4e86\u5821\u4e3b\u4e4b\u547d\uff0c\u6211\u4eec\u5c31\u65e0\u6cd5\u63d2\u624b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u5f97\u4e5f\u662f\uff0c\u4f60\u4eec\u7a76\u7adf\u8fd8\u662f\u7b2c\u4e00\u5821\u7684\u4eba\u554a!\u201d \u3000\u3000\u7ea2\u82b1\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u59d1\u5a18\u5982\u6b64\u660e\u767d\u4e8b\u7406\uff0c\u5c5e\u4e0b\u7b49\u597d\u751f\u611f\u6fc0\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u7528\u4e86\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u7eff\u53f6\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a \u201c\u7eff\u53f6\uff0c \u4f60\u5bf9\u8fd9\u4ef6\u4e8b\u7684\u770b\u6cd5\u5982\u4f55?\u201d \u3000\u3000\u7eff\u53f6\u9053\uff1a\u201c\u8fd9\u4e2a\u5c0f\u5a62\u5012\u6ca1\u6709\u610f\u89c1\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7eff\u53f6\uff0c\u6ca1\u6709\u610f\u89c1\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u7eff\u53f6\u63a5\u9053\uff1a\u201c\u59d1\u5a18\u8981\u6211\u4eec\u505a\u4ec0\u4e48\uff0c\u8bf7\u5429\u5490\u4e00\u58f0\u5c31\u662f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u597d!\u6211\u60f3\u8bf7\u5927\u5bb6\u516c\u51b3\u4e00\u4e0b\u3002\u201d \u3000\u3000\u5341\u4e8c\u4e2a\u5973\u5c06\u5728\u5979\u5e38\u5e74\u7684\u79ef\u5a01\u4e4b\u4e0b\uff0c \u5df2\u517b\u6210\u4e86\u4e00\u79cd\u754f\u60e7\u5fc3\u7406\uff0c\u5c31\u662f\u7ea2\u82b1\u3001\u7eff\u53f6\uff0c\u65e9\u5df2\u88ab\u9ad8\u5929\u5065\u6697\u4e2d\u63a7\u5236\uff0c \u4e5f\u4e0d\u6562\u660e\u76ee\u5f20\u80c6\u7684\u53cd\u5bf9\u5979\u3002 \u5341\u4e8c\u4e2a\u4eba\u4e92\u76f8\u671b\u4e86\u4e00\u773c\uff0c\u4e00\u65f6\u95f4\uff0c\u9762\u9762\u76f8\u89d1\uff0c\u8c01\u4e5f\u8bf4\u4e0d\u51fa\u4e00\u53e5\u8bdd\u6765\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u8138\u4e0a\u5e26\u7740\u7b11\u5bb9\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u4f60\u4eec\u542c\u7740\uff0c\u6211\u5df2\u88ab\u5821\u4e3b\u89e3\u53bb\u4e86\u7ba1\u7406\u4f60\u4eec\u7684\u804c\u52a1\uff0c\u73b0\u5728\uff0c\u7b2c\u4e00\u5821\u4e2d\u7684\u4eba\uff0c\u8981\u8ffd\u6740\u6211\uff0c \u4f60\u4eec\u613f\u4e0d\u613f\u5e2e\u6211\u4e00\u4e2a\u5fd9?\u201d \u3000\u3000\u7eff\u53f6\u671b\u4e86\u7ea2\u82b1\u4e00\u773c\uff0e\u9053\uff1a\u201c\u59ca\u59ca\uff0c \u4f60\u770b\uff0c\u54b1\u4eec\u5e94\u8be5\u5982\u4f55?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7eff\u53f6\uff0c\u4e0d\u8981\u5546\u91cf\uff0c \u4f60\u4eec\u8981\u81ea\u5df1\u4f5c\u4e2a\u51b3\u5b9a\u3002\u201d \u7ea2\u82b1\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u6211\u4ecd\u662f\u7b2c\u4e00\u5821\u7684\u4eba\uff0c \u81f3\u5c11\uff0c\u6211\u4eec\u4e0d\u80fd\u591f\u8fdd\u6297\u9ad8\u5821\u4e3b\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u8981\u542c\u5821\u571f\u5429\u5490\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u5927\u6982\u4f60\u8fd8\u6ca1\u6709\u542c\u6e05\u695a\u6211\u8bf4\u7684\u8bdd\uff0c\u73b0\u5728\uff0c\u8981\u8ffd\u6740\u6211\u7684\u4eba\uff0c\u5c31\u662f\u9ad8\u5821\u4e3b\u3002\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\uff0c\u5c31\u53eb\u5c5e\u4e0b\u4e3a\u96be\u4e86\uff0c\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6ca1\u6709\u4ec0\u4e48\u597d\u4e3a\u96be\u7684\uff0c \u4f60\u65e0\u6cd5\u5e2e\u6211\u7684\u5fd9\uff0c\u90a3\u5c31\u8bf7\u4fbf\u5427\u3002\u201d \u3000\u3000\u7ea2\u82b1\u4e00\u6014\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u662f\u4e00\u7ec4\u4e4b\u957f\uff0e\u8981\u6211\u5230\u54ea\u91cc\u53bb\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u79bb\u5f00\u94c1\u82b1\u9662\uff0c\u8fd9\u91cc\u5f88\u4e0d\u9002\u5408\u4f60\u3002\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u597d!\u5c5e\u4e0b\u9075\u547d\uff0c\u4e0d\u8fc7\uff0c\u6211\u8981\u5e26\u5979\u4eec\u4e00\u8d77\u8d70\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u7ea2\u82b1\uff0c\u4f60\u9519\u4e86\uff0c\u7b2c\u4e00\u5821\u7684\u9ad8\u5821\u4e3b\uff0c\u53ef\u4ee5\u628a\u6211\u8d76\u51fa\u7b2c\u4e00\u5821\uff0c\u4f46\u6211\u53ef\u4ee5\u628a\u4f60\u8d76\u51fa\u94c1\u82b1\u9662\uff0c\u96be\u9053\u4f60\u4e0d\u89c9\u7740\uff0c\u6211\u5bf9\u4f60\u5df2\u7ecf\u5f88\u5bbd\u5927?\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u6211\u4e0d\u628a\u5979\u4eec\u5e26\u8d70\uff0c\u5979\u4eec\u7559\u5728\u8fd9\u91cc\u8981\u8c01\u7167\u9890?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211!\u6216\u662f\u7eff\u53f6\u2026\u2026\u201d \u3000\u3000\u7ea2\u82b1\u63a5\u9053\uff1a\u201c\u7eff\u53f6\uff0c\u5979\u4e00\u6837\u4e0d\u53ef\u4ee5\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7ea2\u82b1\uff0c\u7eff\u53f6\u7684\u4e8b\uff0c\u4f60\u80fd\u591f\u505a\u4e3b\u5417?\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u6211\uff0c\u6211\uff0c\u6211\u60f3\u5979\u4f1a\u548c\u6211\u4e00\u6837\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7ea2\u82b1\uff0c\u6211\u5728\u4f20\u6388\u4f60\u4eec\u5251\u6cd5\u65f6\uff0c\u7b49\u4e8e\u662f\u4f60\u4eec\u7684\u5e08\u7236\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u5e08\u4f26\u5927\u9053\u8fd9\u4ef6\u4e8b\u60c5?\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u77e5\u9053\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u5bf9\u4f60\u4eec\u5341\u4e8c\u4e2a\u4eba\uff0c\u662f\u4e00\u6837\u7684\u4f20\u6388\u529e\u6cd5\uff0c\u4f60\u867d\u7136\u662f\u4e00\u7ec4\u4e4b\u957f\uff0c\u4f46\u4f60\u5e76\u4e0d\u662f\u4e00\u4e2a\u5f88\u51fa\u8272\u7684\u5251\u624b\uff0c\u5728\u4f60\u7684\u4e00\u7ec4\u4e2d\uff0c\u4f60\u4e5f\u5e76\u975e\u5f88\u6770\u51fa\u3002\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u4f46\u5979\u4eec\u4f1a\u542c\u6211\u7684\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u5c31\u7ed9\u4f60\u4e00\u4e2a\u673a\u4f1a\u8bd5\u8bd5\uff0c\u4f60\u770b\u770b\u80fd\u4e0d\u80fd\u60f3\u529e\u6cd5\u628a\u5979\u4eec\u5e26\u8d70\u3002\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u59d1\u5a18\u53ea\u8981\u4e0d\u963b\u6b62\uff0c\u6211\u76f8\u4fe1\u5979\u4eec\u90fd\u4f1a\u542c\u6211\u4e4b\u547d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u5f53\u7136\u8981\u963b\u6b62\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u662f\u5f3a\u884c\u62e6\u963b\uff0c\u800c\u4e14\u662f\u4e00\u79cd\u529d\u8bf4\uff0c\u5979\u4eec\u613f\u53bb\u613f\u7559\uff0c\u7531\u5979\u4eec\u81ea\u5df1\u51b3\u5b9a\u3002\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u53ea\u6015\u4f60\u4f1a\u5f88\u5931\u671b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u5462?\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u59d1\u5a18\u867d\u7136\u4f20\u6388\u5979\u4eec\u6b66\u529f\uff0c\u4f46\u5979\u4eec\u7a76\u7adf\u8fd8\u662f\u8ddf\u6211\u63a5\u8fc7\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u662f\u65e9\u6709\u8ba1\u7b97\u4e86?\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u8981\u8bef\u4f1a\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u8bc1\u660e\u4e00\u4e0b\uff0c\u6211\u82b1\u4e86\u51e0\u5e74\u5de5\u592b\u8bad\u7ec3\u51fa\u6765\u7684\u5f1f\u5b50\uff0c\u5bf9\u4e8e\u6211\u8fd9\u4e2a\u505a\u8001\u5e08\u7684\u6709\u591a\u5c11\u7684\u73cd\u91cd\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u613f\u610f\u8ddf\u7ea2\u82b1\u8d70\u7684\uff0c\u8bf7\u4f60\u4eec\u9000\u540e\u4e09\u6b65\u3002\u201d \u3000\u3000\u4e94\u4e2a\u7ea2\u8863\u5973\u5251\u624b\uff0c\u6709\u4e24\u4e2a\u5411\u540e\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u52a0\u4e0a\u7ea2\u82b1\uff0c\u5171\u6709\u4e09\u4eba\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u534a\u4e4b\u6570\u3002 \u3000\u3000\u7eff\u53f6\u672c\u6765\u5df2\u62ac\u8d77\u5b50\u811a\uff0c\u4f46\u89c1\u7ea2\u82b1\u53ea\u80fd\u5f71\u54cd\u4e24\u4e2a\u4eba\uff0c\u5ffd\u7136\u6539\u53d8\u4e86\u5fc3\u610f\uff0c\u4e5f\u7ad9\u7740\u672a\u52a8\u3002 \u3000\u3000\u5341\u4e8c\u4e2a\u5973\u5c06\u4e2d\uff0c\u516d\u4e2a\u7eff\u8863\u5251\u624b\uff0c\u4e00\u76f4\u7ad9\u7740\u672a\u52a8\u3002 \u3000\u3000\u7ea2\u8863\u5251\u624b\uff0c\u4e00\u534a\u4e00\u534a\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5bf9\u81ea\u5df1\u6709\u7740\u5982\u6b64\u91cd\u5927\u7684\u5f71\u54cd\u529b\u91cf\uff0c\u6709\u4e9b\u610f\u5916\uff0c\u5fc3\u4e2d\u5341\u5206\u6fc0\u52a8\uff0c\u7b11\uff0c\u7b11\uff0c\u9053\uff1a\u201c\u8c22\u8c22\u8bf8\u4f4d\u597d\u59ca\u59b9\u3002\u201d \u3000\u3000\u7ea2\u82b1\u5374\u6709\u4e9b\u4e0b\u4e0d\u4e86\u53f0\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a \u201c\u7eff\u53f6\uff0c\u4f60\u7559\u4e0b\u6765\u627e\u6b7b\u4e48\uff1f\u201d \u7eff\u53f6\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5c3d\u91cf\u4f7f\u81ea\u5df1\u7684\u5fc3\u60c5\uff0c\u4fdd\u6301\u5e73\u548c\uff0c\u9053\uff1a \u201c\u7ea2\u82b1\uff0c\u4f60\u8fd9\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u53ec\u89c1\u8fc7\u6211\u4eec\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54e6!\u4ed6\u8bf4\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a \u201c\u4ed6\u8bf4\uff0c\u4f60\u53ea\u8d1f\u8d23\u4f20\u6388\u6211\u4eec\u7684\u6b66\u529f\uff0c\u5176\u4ed6\u7684\u4e8b\uff0c\u6211\u4eec\u76f4\u63a5\u542c\u547d\u4e8e\u4ed6\u3002\u201d \u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fc7\u53bb\uff0c\u4ed6\u8bf4\u7684\u6ca1\u6709\u9519\uff0c\u4f46\u73b0\u5728\uff0c\u60c5\u5f62\u6709\u4e9b\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u540c?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u73b0\u5728\uff0c\u662f\u548c\u4ed6\u654c\u5bf9\u76f8\u5904\uff0c\u4ed6\u8981\u6740\u6211\uff0c\u6211\u4e0d\u613f\u610f\u6b7b\uff0c\u53ea\u597d\u548c\u4ed6\u5bf9\u6297\u4e86\u3002\u201d \u7ea2\u82b1\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u660e\u767d\u4e86?\u201d \u3000\u3000\u7ea2\u82b1\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u7ea2\u82b1\uff0c \u76ee\u4e0b\u7684\u60c5\u52bf\uff0c \u5df2\u7ecf\u5f88\u660e\u663e\uff0c \u4f60\u5fc5\u987b\u5728\u6211\u548c\u9ad8\u5821\u4e3b\u4e4b\u95f4\uff0c\u9009\u62e9\u4e00\u4e2a\u6548\u5fe0\u7684\u4eba\u3002\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a \u201c\u5982\u82e5\u6211\u4eec\u9009\u62e9\u4e86\u9ad8\u5821\u4e3b\uff0c\u59d1\u5a18\u662f\u4e0d\u662f\u73b0\u5728\u5c31\u6740\u6211\u4eec?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e0d\u4f1a\uff0c\u6211\u4f1a\u653e\u4f60\u4eec\u8d70! \u4f60\u53ef\u4ee5\u5e26\u7740\u5bf9\u4f60\u5fe0\u5b9e\u7684\u5c5e\u4e0b\u79bb\u5f00\u8fd9\u91cc\u3002\u201d \u7ea2\u82b1\u9053\uff1a\u201c\u662f\u771f\u7684?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u7edd\u5bf9\u771f\u5b9e\uff0c\u6211\u5982\u82e5\u6b3a\u9a97\u4e86\u4f60\uff0c\u7559\u5728\u8fd9\u91cc\u7684\u4eba\u4e5f\u4e0d\u4f1a\u518d\u4fe1\u4efb\u6211\uff0c\u5212\u660e\u654c\u53cb\uff0c\u6211\u8981\u4ee5\u6211\u8fd9\u4e9b\u4f20\u6388\u4f60\u7684\u6b66\u529f\uff0c\u4f7f\u4f60\u4eec\u5185\u5fc3\u4e4b\u4e2d\uff0c\u4ea7\u751f\u51fa\u5bf9\u6211\u7684\u5c0a\u656c\u548c\u60c5\u611f\uff0c\u7531\u4f60\u4eec\u81ea\u7531\u6289\u62e9\u3002\u201d \u3000\u3000\u7eff\u53f6\u9053\uff1a\u201c\u7ea2\u82b1\uff0c\u6211\u89c9\u7740\u9ad8\u59d1\u5a18\u8bf4\u5f97\u5bf9\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u5979\u4f20\u6388\u6211\u4eec\u6b66\u529f\uff0c\u867d\u7136\u8981\u6c42\u5f97\u5f88\u4e25\u683c\uff0c\u4f46\u90a3\u662f\u5e0c\u671b\u6211\u4eec\u628a\u6b66\u529f\u7ec3\u597d\uff0c\u8fd9\u4e9b\u5e74\u7684\u76f8\u5904\uff0c\u5927\u5bb6\u5185\u5fc3\u4e4b\u4e2d\u90fd\u5df2\u65e9\u6709\u60c5\u611f\uff0c\u53ea\u4e0d\u8fc7\u9ad8\u59d1\u5a18\u6ca1\u6709\u5411\u6211\u4eec\u8868\u8fbe\u8fc7\uff0c\u6211\u4eec\u5bf9\u5979\u592a\u656c\u754f\uff0c\u4e5f\u4e0d\u6562\u5411\u5979\u8868\u8fbe\u51fa\u6765\uff0c\u4f46\u90a3\u5e76\u4e0d\u662f\u8bf4\uff0c\u8fd9\u51e0\u5e74\u7684\u76f8\u5904\u5168\u65e0\u60c5\u611f\u3002\u201d \u3000\u3000\u7ea2\u82b1\u70b9\u70b9\u5934\u3002 \u3000\u3000\u7eff\u53f6\u9053\uff1a\u201c\u6211\u4e0d\u559c\u6b22\u9ad8\u5821\u4e3b\u7684\u4f5c\u6cd5\uff0c\u4ed6\u8981\u4e8c\u5c0f\u59d0\u4f20\u6211\u4eec\u6b66\u529f\uff0c\u5374\u53c8\u8ba9\u6211\u4eec\u76f4\u63a5\u542c\u547d\u4e8e\u4ed6\u3002\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u8bb2\u60c5\u3001\u8bba\u7406\uff0c\u54b1\u4eec\u90fd\u5e94\u8ffd\u968f\u9ad8\u5c0f\u59d0\u3002\u201d \u3000\u3000\u7eff\u53f6\u9053\uff1a\u201c\u5bf9!\u5979\u662f\u6211\u4eec\u7684\u6388\u827a\u6069\u5e08\uff0c\u4e5f\u7b97\u662f\u6211\u4eec\u7684\u4e3b\u4eba\u3002\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u7eff\u53f6\uff0c\u4f60\u53ef\u77e5\u9053\uff1b\u5982\u82e5\u6211\u4eec\u4e0d\u542c\u9ad8\u5821\u4e3b\u7684\u8bdd\uff0c\u4ed6\u4f1a\u6740\u4e86\u6211\u4eec\u3002\u201d \u3000\u3000\u7eff\u53f6\u9053\uff1a \u201c\u6211\u77e5\u9053\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u542c\u4e86\u4ed6\u7684\u8bdd\uff0c\u4ed6\u4e5f\u4f1a\u8981\u6211\u4eec\u53bb\u6740\u4eba\uff0c\u6211\u4eec\u6740\u4e0d\u4e86\u4eba\u7684\u540e\u679c\u662f\u4ec0\u4e48?\u4f60\u53ef\u660e\u767d?\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u4ec0\u4e48\u540e\u679c?\u201d \u3000\u3000\u7eff\u53f6\u9053\uff1a\u201c\u88ab\u6740\u3002\u201d \u3000\u3000\u7ea2\u82b1\u82e6\u7b11\u4e00\u4e0b\u9053\uff1a\u201c\u6211\u597d\u5f77\u5fa8\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u6289\u62e9\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7ea2\u82b1\uff0c\u6211\u8bf4\u8fc7\u4e0d\u52c9\u5f3a\u4f60\uff0c\u4f60\u81ea\u5df1\u51b3\u5b9a\u5427\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u94c1\u82b1\u9662\u5df2\u88ab\u56f4\u4e86\u8d77\u6765\u3002 \u3000\u3000\u9ad8\u5929\u5065\u3001\u96f7\u65e0\u5f71\uff0c\u5e26\u7740\u4e8c\u5341\u56db\u4e2a\u4eba\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u90a3\u4e8c\u5341\u56db\u4e2a\u4eba\uff0c\u5341\u4e8c\u4e2a\u7a7f\u7740\u767d\u8863\uff0c\u8170\u4e2d\u4f69\u5200\u3002 \u3000\u3000\u795e\u5200\u5802\u4e2d\u6700\u7cbe\u9510\u7684\u6740\u624b\uff0c\u767d\u8863\u5200\u5ba2\u3002 \u3000\u3000\u5341\u4e8c\u4e2a\u9ed1\u8863\u4eba\uff0c\u624b\u4e2d\u6267\u5251\uff0c\u4f46\u8eab\u4e0a\uff0c\u5374\u80cc\u4e86\u4e00\u4e2a\u9ed1\u8272\u7684\u94c1\u7b52\u3002 \u3000\u3000\u201c\u6c34\u706b\u65e0\u60c5\u201d\u6267\u884c\u624b\uff0c\u662f\u7b2c\u4e00\u5821\u4ed7\u51ed\u4e89\u9738\u6b66\u6797\u7684\u7cbe\u9510\u3002 \u3000\u3000\u6700\u5389\u5bb3\u7684\u6b66\u58eb\u3002 \u3000\u3000\u4e8c\u5341\u56db\u4e2a\u4eba\uff0c\u9762\u8272\u90fd\u5f88\u8083\u7136\uff0e\u51b7\u51b7\u7684\u9762\u5b54\uff0c\u770b\u4e0d\u51fa\u4e00\u70b9\u8868\u60c5\u3002 \u3000\u3000\u4efb\u4f55\u4eba\u53ea\u8981\u770b\u4ed6\u4eec\u4e00\u773c\uff0c\u90fd\u4f1a\u770b\u51fa\u6765\uff0c\u4ed6\u4eec\u662f\u65e0\u60c5\u7684\u6740\u624b\u3002 \u3000\u3000\u662f\u90a3\u79cd\u53ea\u77e5\u9053\u6267\u884c\u4ee4\u8c15\uff0c\u4e0d\u95ee\u662f\u975e\u7684\u4eba\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\uff0c\u4f60\u4e0d\u5e94\u8be5\u5230\u8fd9\u91cc\u6765\u7684\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5341\u4e8c\u98de\u71d5\u4e0d\u4f1a\u5e2e\u52a9\u4f60\u7684\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\uff0c\u6211\u4eec\u53ea\u6709\u6218\u6b7b\u4e00\u9014\u4e86?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u6761\u53ef\u884c\u4e4b\u8def\uff0c\u90a3\u5c31\u662f\u5f03\u5251\u6295\u964d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5728\u6211\u6b7b\u53bb\u4e4b\u524d\uff0c\u6709\u4e00\u4e8b\uff0c\u5411\u4f60\u8bf7\u6559\uff0c\u4e0d\u77e5\u9053\u4f60\u6562\u4e0d\u6562\u636e\u5b9e\u56de\u7b54\uff1f\u201d \u9ad8\u5929\u5065\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u5427\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u7684\u4eb2\u751f\u7236\u6bcd\u662f\u8c01?\u4ed6\u4eec\u73b0\u5728\u4f55\u5904?\u201d \u3000\u3000\u9ad8\u5929\u5065\u5446\u4e86\u4e00\u5446\uff0c\u7a81\u7136\u653e\u58f0\u5927\u7b11\uff0c\u9053\uff1a \u201c\u4f60\u627e\u5230\u4e86\u4ec0\u4e48\u8bc1\u636e?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u627f\u8ba4\u4e86?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u6211\u627f\u8ba4\u4e0d\u627f\u8ba4\uff0c\u4f3c\u4e4e\u662f\u4e0d\u592a\u91cd\u8981\uff0c\u95ee\u9898\u5728\u4f60\uff0c\u4f60\u627e\u5230\u4e86\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u73b0\u5728\u6211\u5df2\u7ecf\u4ec0\u4e48\u4e5f\u4e0d\u7528\u627e\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5bf9!\u8fd9\u95ee\u9898\uff0c\u4f60\u5df2\u65e0\u6cd5\u518d\u5f04\u5f97\u5f88\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u5bf9\u81ea\u5df1\u832b\u7136\u4e86\u5341\u51e0\u5e74\uff0c\u73b0\u5728\u603b\u7b97\u6709\u5e95\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5c31\u7b97\u4f60\u660e\u767d\u4e86\u4e00\u4e9b\u4ec0\u4e48\uff0c\u6211\u60f3\uff0c\u5bf9\u4f60\u4e5f\u65e0\u6240\u5e2e\u52a9\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\u9053\uff1a \u201c\u4f60\u8c03\u52a8\u4e86\u5341\u4e8c\u652f\uff0c\u2018\u6c34\u706b\u65e0\u60c5\u2019\u5bf9\u4ed8\u6211\u4eec\uff0c\u770b\u6765\uff0c\u5bf9\u6211\u4eec\u5f88\u662f\u770b\u91cd\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u6ca1\u6709\u6cd5\u5b50\u7684\u4e8b\uff0c\u6211\u4e0d\u80fd\u518d\u6709\u758f\u5ffd\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u56e0\u4e3a\u758f\u5ffd\u548c\u4ec1\u6148\uff0c\u5df2\u7ecf\u9020\u6210\u4e86\u5f88\u5927\u7684\u9519\u8bef\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5341\u4e8c\u53ea\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u5c31\u4e00\u5b9a\u80fd\u6740\u4e86\u6211\u4eec\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u80fd\u9003\u8fc7\u8fd9\u79cd\u6697\u5668\uff0c\u4f60\u4eec\u4e5f\u4e0d\u80fd\u3002\u201d \u3000\u3000\u54c8\u54c8\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4f46\u6211\u8fd8\u662f\u5e0c\u671b\u4f60\u4eec\u80fd\u591f\u53ca\u65f6\u56de\u5934\uff0c\u6211\u60f3\uff0c\u4e09\u4f4d\u5bf9\u7b2c\u4e00\u5821\u8fd8\u6709\u4ef7\u503c\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e0c\u671b\u6211\u4eec\u53d8\u6210\u4e00\u4e2a\u767d\u75f4\u6740\u624b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u4f60\u4eec\u5df2\u7ecf\u65e0\u5904\u53ef\u53bb\uff0c\u552f\u4e00\u4e0d\u6b7b\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u4f5c\u7b2c\u4e00\u5821\u7684\u6740\u624b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u7a81\u7136\u63a5\u9053\uff1a\u201c\u4e24\u4f4d\u5c0f\u59d0\u5bb9\u8272\u7edd\u4e16\uff0c\u5982\u975e\u5fc5\u8981\uff0c\u54b1\u4eec\u4e5f\u4e0d\u5e0c\u671b\u65bd\u4e0b\u6bd2\u624b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u4eec\u65e0\u5904\u53ef\u53bb\uff0c\u4e5f\u8bb8\u4f60\u4eec\u8fd8\u4e0d\u592a\u76f8\u4fe1\uff0c\u73b0\u5728\uff0c\u6211\u60f3\u7ed9\u4f60\u4eec\u4e00\u4e2a\u8bc1\u660e\uff0c\u5e7d\u5170\uff0c\u8fd9\u5341\u4e8c\u5973\u5c06\uff0c\u90fd\u662f\u4f60\u4eb2\u624b\u8bad\u7ec3\u7684\u4eba\uff0c\u4f60\u5bf9\u5979\u4eec\u6709\u591a\u5c11\u7684\u4fe1\u5fc3?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7f13\u6b65\u5411\u5385\u4e2d\u884c\u53bb\uff0c\u4e00\u9762\u8bf4\uff1a \u201c\u96be\u9053\u4f60\u65e9\u5df2\u6536\u5356\u4e86\u5979\u4eec?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u4f1a\u7ed9\u4f60\u4e00\u4e2a\u8bc1\u660e\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\u9053\uff1a\u201c\u7ea2\u82b1\u3001\u7eff\u53f6\uff0c\u5e26\u7740\u4f60\u4eec\u7684\u624b\u4e0b\u8fc7\u6765\u3002\u201d \u3000\u3000\u7ea2\u82b1\u56de\u987e\u4e86\u7eff\u53f6\u4e00\u773c\uff0c\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u53ea\u6015\u4f60\u5931\u671b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48?\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u5979\u4eec\u4e0d\u4f1a\u542c\u6211\u4eec\u7684\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\u3002\u201d \u3000\u3000\u7eff\u53f6\u9053\uff1a\u201c\u662f\u771f\u7684\uff0c\u5979\u4eec\u8fd9\u4e9b\u5e74\u6765\uff0c\u8ddf\u7740\u4e8c\u5c0f\u59d0\u5b66\u5251\uff0c\u5185\u5fc3\u4e4b\u4e2d\uff0c\u65e9\u5df2\u628a\u4e8c\u5c0f\u59d0\u89c6\u4f5c\u5e08\u5c0a\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u54e6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u5462?\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u6211\u4eec\u5206\u6210\u4e24\u7ec4\uff0c\u6bcf\u4e00\u7ec4\u4eba\u624b\uff0c\u90fd\u5177\u6709\u76f8\u5f53\u5f3a\u5927\u7684\u5a01\u529b\uff0c\u5982\u82e5\u5206\u6563\u4e86\uff0c\u90a3\u5c31\u4f1a\u5f62\u6210\u4e86\u5355\u6253\u72ec\u6597\u7684\u5c40\u9762\uff0c\u6211\u4eec\u4e2a\u4eba\u7684\u6210\u5c31\uff0c\u5c31\u6709\u9650\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u90a3\u5979\u4eec\u53ea\u6709\u6b7b\u4ea1\u4e00\u9014\uff0c \u4f60\u4eec\u4e24\u4e2a\u8fc7\u6765\u5427!\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u9ad8\u5e7d\u5170\u5bf9\u7ea2\u82b1\u3001\u7eff\u53f6\u7684\u5fc3\u610f\u5982\u4f55\uff0c\u4ea6\u662f\u5168\u65e0\u628a\u63e1\uff0c\u6240\u4ee5\uff0c\u4e00\u76f4\u6ca1\u6709\u51fa\u8a00\u963b\u6b62\u3002 \u3000\u3000\u4f46\u5434\u53cc\u5973\u5374\u6709\u4e86\u51c6\u5907\uff0c\u6697\u4e2d\u6263\u4e86\u4e00\u628a\u5929\u9b54\u9488\uff0c\u4e8c\u5973\u53ea\u8981\u6709\u80cc\u79bb\u7684\u4e3e\u52a8\uff0c\u7acb\u65f6\u5c06\u8fc5\u901f\u51fa\u624b\uff0c\u53d6\u4e24\u4eba\u4e4b\u547d\u3002 \u3000\u3000\u5e78\u597d\uff0c\u7ea2\u82b1\u3001\u7eff\u53f6\uff0c\u5e76\u6ca1\u6709\u80cc\u79bb\u7684\u4e3e\u52a8\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u540c\u65f6\u6447\u6447\u5934\uff0c\u7ea2\u82b1\u5f00\u53e3\u8bf4\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u6211\u4eec\u7684\u4ef7\u503c\u662f\u5e26\u7740\u5979\u4eec\u8fc7\u53bb\uff0c\u5982\u82e5\u65e0\u6cd5\u8bf4\u52a8\u8fd9\u4e9b\u4eba\uff0c\u6211\u4eec\u4f1a\u4e86\u89e3\u81ea\u5df1\uff0c\u6211\u4eec\u5c31\u7b97\u8fc7\u53bb\u4e86\uff0c\u4e5f\u4e0d\u4f1a\u53d7\u5230\u91cd\u89c6\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u4e0d\u80fd\u602a\u4f60\u4eec\uff0c\u4f60\u4eec\u8bf7\u8fc7\u6765\u5427\uff01\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u4e0d\u884c\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5168\u90e8\u9000\u5165\u5927\u5385\uff01\u201d \u3000\u3000\u5341\u4e8c\u5973\u5c06\u7684\u52a8\u4f5c\u5f88\u5feb\uff0c\u95fb\u58f0\u5012\u8dc3\u5165\u5385\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e09\u4eba\u4e5f\u8dc3\u5165\u5385\u4e2d\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u4eba\u90fd\u4f1a\u5206\u8fa8\u662f\u975e\uff0c\u5979\u4eec\u53ef\u4ee5\u88ab\u4f60\u6b3a\u9a97\u4e00\u65f6\uff0c\u4e0d\u4f1a\u6c38\u8fdc\u88ab\u4f60\u6b3a\u9a97\uff0c\u5979\u4eec\u73b0\u5728\u662f\u4f60\u7684\u5c5e\u4e0b\uff0c\u7b49\u5230\u4e86\u89e3\u4f60\u7684\u9634\u9669\u3001\u6b8b\u5fcd\u65f6\uff0c\u5979\u4eec\u5c31\u4f1a\u79bb\u4f60\u800c\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d!\u90a3\u5c31\u8fde\u5979\u4eec\u4e00\u9f50\u683c\u6740!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u2018\u6c34\u706b\u65e0\u60c5\u2019\u867d\u7136\u5389\u5bb3\uff0c\u4f46\u5b83\u53ea\u662f\u4e00\u79cd\u6697\u5668\uff0c\u867d\u7136\u6709\u5f3a\u5927\u7684\u6740\u4f24\u529b\uff0c\u8fd8\u8981\u9760\u4eba\u8fd0\u7528\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u5341\u4e8c\u7b52\u6c34\u706b\u65e0\u60c5\uff0c\u6211\u4e0d\u4fe1\u6740\u53d6\u4e0d\u4e86\u4f60\u4eec\u7684\u6027\u547d\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u2018\u6c34\u706b\u65e0\u60c5\u2019\u53ef\u4ee5\u6740\u4eba\uff0c\u4f46\u8fd9\u5730\u65b9\uff0c\u5e76\u4e0d\u592a\u9002\u5408\u4ed6\u4eec\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a \u201c\u6211\u4eec\u501f\u8fd9\u4e9b\u5385\u623f\u63a9\u853d\uff0c\u2018\u6c34\u706b\u65e0\u60c5\u2019\u7684\u5a01\u529b\u5c06\u5f88\u96be\u53d1\u6325\u2026\u2026\u201d \u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u63a5\u9053\uff1a \u201c\u5f53\u4f60\u4eec\u770b\u5230\u6211\u65f6\uff0c\u8981\u5c0f\u5fc3\u6211\u7684\u5929\u9b54\u9488\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u6211\u7684\u98de\u73af\u5c11\u4e86\u4e00\u4e9b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\uff0c\u4f60\u5982\u4e00\u89c1\u9762\u5c31\u7528\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u6211\u4eec\u5c31\u5f88\u96be\u9003\u8fc7\u4eca\u65e5\u4e4b\u52ab\uff0c\u53ef\u60dc\uff0c\u4f60\u6ca1\u6709\u4e0b\u4ee4\u65bd\u7528\uff0c\u73b0\u5728\uff0c\u673a\u4f1a\u4f3c\u662f\u5df2\u7ecf\u8fc7\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e94\u5341\u6b65\u4e0e\u767e\u6b65\u800c\u5df2\uff0c\u5c31\u7b97\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u4e00\u65f6\u95f4\u65e0\u6cd5\u4f24\u5230\u4f60\u4eec\uff0c\u4f46\u4f60\u4eec\u8981\u5982\u4f55\u751f\u79bb\u6b64\u5730?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u7528\u5821\u4e3b\u8d39\u5fc3\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a \u201c\u7ea2\u82b1\u3001\u7eff\u53f6\uff0c\u548c\u8bf8\u4f4d\u59ca\u59b9\uff0c\u4f60\u4eec\u542c\u7740\uff0c\u6240\u8c13\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u662f\u4e00\u79cd\u6eb6\u7740\u706b\u7130\u3001\u6bd2\u9488\u7684\u6697\u5668\u3002\u542b\u6709\u5f3a\u70c8\u7684\u6bd2\uff0c\u6027\uff0c\u4e0d\u80fd\u7528\u5175\u5203\u963b\u6321\uff0c\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u95ea\u907f\uff0c\u4f46\u5b83\u55b7\u6d12\u51fa\u7684\u8303\u56f4\u5f88\u5927\uff0c\u8fdc\u53ca\u4e24\u4e09\u4e08\u7b3c\u7f69\u6709\u4e94\u5c3a\u65b9\u5706\uff0c\u6700\u597d\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u501f\u7269\u4f53\u963b\u62e6\uff0c\u5982\u662f\u4e00\u65e6\u4e0d\u5e78\uff0c\u88ab\u6bd2\u6c34\u6cbe\u4e0a\u8eab\u4f53\uff0c\u8981\u6709\u58ee\u58eb\u65ad\u8155\u7684\u7cbe\u795e\uff0c\u7528\u624b\u4e2d\u5175\u5203\u524a\u53bb\u90a3\u4e00\u7247\u808c\u80a4\uff0c\u5982\u662f\u88ab\u6bd2\u706b\u6cbe\u71c3\uff0c\u8fc5\u901f\u65a9\u53bb\u8863\u670d\uff0c\u548c\u524a\u53bb\u706b\u6bd2\u70e7\u4f24\u4e4b\u5904\uff0c\u5b83\u7684\u6bd2\u6027\u6563\u5e03\u5f88\u5feb\uff0c\u4e0d\u53ef\u7a0d\u6709\u72b9\u8c6b\u3002\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u6307\u70b9\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u4f4e\u58f0\u9053\uff1a \u201c\u4f60\u4eec\u53bb\u53d6\u4e00\u4e9b\u68c9\u88ab\u6765\uff0c\u7528\u6c34\u6d78\u6e7f\uff0c\u6c34\u706b\u65e0\u60c5\u867d\u7136\u5389\u5bb3\uff0c\u4f46\u5b83\u5374\u6ca1\u6709\u7a7f\u900f\u4e4b\u529b\uff0c\u53ea\u8981\u662f\u4e00\u4ef6\u7269\u54c1\uff0c\u90fd\u53ef\u4ee5\u6297\u62d2\u5f97\u4f4f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5c4b\u4e2d\u5668\u5177\uff0c\u90fd\u53ef\u5e94\u7528\uff0c\u80fd\u4f5c\u6210\u76fe\u724c\u7684\uff0c\u4e0d\u59a8\u53d6\u6765\u5e94\u7528\u3002\u201d \u5341\u4e8c\u5973\u5c06\uff0c\u5728\u9ad8\u5e7d\u5170\u8bad\u7ec3\u4e86\u6570\u5e74\u4e4b\u4e0b\uff0c\u7b2c\u4e00\u6b21\u5bf9\u654c\uff0c\u5c31\u9047\u4e0a\u4e86\u5929\u4e0b\u6700\u6076\u6bd2\u7684\u6697\u5668\uff0c\u5fc3\u60c5\u65e2\u662f\u7d27\u5f20\uff0c\u53c8\u662f\u5174\u594b\u3002 \u3000\u3000\u5728\u7ea2\u82b1\u3001\u7eff\u53f6\u7387\u9886\u4e4b\u4e0b\uff0c\u7acb\u65f6\u5c55\u5f00\u4e86\u90e8\u7f72\u3002 \u3000\u3000\u5341\u4f59\u6761\u68c9\u88ab\uff0c\u88ab\u6c34\u6d78\u6e7f\uff0c\u653e\u5728\u6728\u6848\u4e0a\uff0c\u51c6\u5907\u62d2\u654c\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u56de\u987e\u4f55\u5bd2\u8863\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u542c\u7740\uff0c\u8fd9\u4e0d\u662f\u6b66\u6797\u4e2d\u6307\u540d\u8f83\u6280\uff0c\u800c\u662f\u4e00\u79cd\u6b79\u6bd2\u7684\u62fc\u6740\uff0c\u4e0d\u8981\u624b\u4e0b\u7559\u60c5\uff0c\u5c24\u5176\u5bf9\u65bd\u7528\u2018\u6c34\u706b\u65e0\u60c5\u2019\u7684\u4eba\uff0c\u66f4\u662f\u4e0d\u8981\u7559\u60c5\uff0c\u80fd\u591f\u7528\u5591\u5668\uff0c\u5c31\u7528\u6697\u5668\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6697\u5668\u7684\u610f\u601d\uff0c\u5c31\u662f\u4e0d\u7528\u51fa\u8a00\u7684\u62db\u547c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u53d6\u8fc7\u51e0\u4e2a\u5927\u74f7\u7897\u6765\uff0c\u5341\u5206\u5c0f\u5fc3\u5730\u628a\u5b83\u4eec\u6572\u788e\uff0c\u7136\u540e\uff0c\u6536\u5165\u888b\u4e2d\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u7ea2\u82b1\u3001\u7eff\u53f6\uff0c \u4f60\u4eec\u8fd8\u8bb0\u7740\u6211\u4f20\u8fc7\u4f60\u4eec\u7684\u4e00\u62db\u5251\u6cd5\u5417?\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u4ec0\u4e48\u5251\u6cd5?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u7529\u624b\u5251\uff0c\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u77e5\u9053\uff0c\u8fd9\u4e00\u62db\u5f88\u5947\u602a\u3002 \u5927\u5bb6\u90fd\u7528\u5fc3\u7ec3\u8fc7\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u90a3\u5f88\u597d\uff0c\u6361\u4e00\u4e9b\u91cd\u91cf\u9002\u4e2d\u4e4b\u7269\uff0c \u62ff\u5728\u624b\u4e0a\uff0c\u770b\u5230\u65bd\u7528\u2018\u6c34\u706b\u65e0\u60c5\u2019\u7684\u4eba\uff0c\u5c31\u7528\u7529\u624b\u5251\u7684\u65b9\u6cd5\uff0c\u628a\u5b83\u6253\u51fa\u53bb\u3002\u201d \u3000\u3000\u7ea2\u82b1\uff0c\u7eff\u53f6\uff0c\u9f50\u9f50\u5e94\u4e86\u4e00\u58f0\uff0c \u4e0b\u4ee4\u624b\u4e0b\u5341\u5973\uff0c\u5404\u81ea\u9009\u53d6\u9002\u7528\u4e4b\u7269\u3002 \u5927\u5385\u867d\u7136\u5fd9\u788c\uff0c\u4f46\u9ad8\u5e7d\u5170\u548c\u5434\u53cc\u5973\uff0c \u4e00\u76f4\u5f88\u7528\u5fc3\u7684\u76d1\u89c6\u7740\u5385\u5916\u9762\u7684\u4e3e\u52a8\u3002 \u9ad8\u5929\u5efa\u4e5f\u4f20\u4e0b\u4e86\u4ee4\u8c15\uff0c\u5341\u4e8c\u4e2a\u767d\u8863\u5200\u624b\uff0c\u914d\u5408\u7740\u5341\u4e8c\u4e2a\u9ed1\u8863\u2018\u6c34\u706b\u65e0\u60c5\u2019\u7684\u6740\u624b\uff0c\u4e5f\u5f00\u59cb\u4e86\u884c\u52a8\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5df2\u53d6\u4e0b\u4e86\u80cc\u4e0a\u7684\u94c1\u7b52\uff0c\u63e1\u5728\u624b\u4e2d\u3002 \u3000\u3000\u5341\u4e8c\u4e2a\u767d\u8863\u5200\u624b\uff0c\u5206\u6210\u4e86\u56db\u7ec4\uff0c\u6bcf\u7ed9\u4e09\u4e2a\u4eba\uff0c\u914d\u4e00\u4e2a\u8eab\u7740\u9ed1\u8863\u7684\u2018\u6c34\u706b\u65e0\u60c5\u2019\u6740\u624b\u3002 \u3000\u3000\u56db\u7ec4\u4eba\uff0c\u7f13\u7f13\u5411\u5927\u5385\u56f4\u4e0a\u6765\u3002 \u3000\u3000\u5434\u53cc\u5973\u56de\u987e\u4e86\u9ad8\u5e7d\u5170\u4e00\u773c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u5e7d\u5170\u3002\u8fd9\u4e9b\u5200\u624b\u548c\u9ed1\u8863\u6740\u624b\uff0c\u795e\u5fd7\u90fd\u8fd8\u6e05\u9192\u5427?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u795e\u5200\u5802\u4e2d\u7684\u5200\u624b\uff0c\u5206\u4e3a\u516d\u7b49\uff0c\u8fd9\u4e9b\u4eba\uff0c\u662f\u51e0\u7b49\u5200\u624b\uff0c\u6211\u5c31\u4e0d\u6e05\u695a\u4e86\uff0c\u4e0d\u8fc7\uff0c\u5c31\u6211\u6240\u77e5\u7684\uff0c\u90a3\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u662f\u4e00\u79cd\u5f88\u7cbe\u4e13\u7684\u6697\u5668\uff0c\u65bd\u7528\u8fd9\u79cd\u6697\u5668\uff0c\u4e5f\u4e0d\u662f\u4e00\u4ef6\u7b80\u5355\u7684\u4e8b\u60c5\uff0c\u6240\u4ee5\uff0c\u90a3\u4e9b\u4eba\uff0c\u90fd\u662f\u5f88\u7cbe\u660e\u7684\u4eba\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u6211\u8981\u653e\u8086\u65bd\u4e3a\u4e86\uff0c\u4e0d\u77e5\u4f60\u610f\u4e0b\u5982\u4f55?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\uff0c\u4e0d\u8bba\u4f60\u600e\u4e48\u505a\uff0c\u5c0f\u59b9\u90fd\u540c\u610f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u628a\u5979\u4eec\u7684\u4f4d\u7f6e\u5206\u4e00\u4e0b\uff0c\u8981\u5979\u4eec\u5206\u522b\u5b88\u5728\u95e8\u53e3\u3001\u7a97\u5185\uff0c\u5404\u6709\u804c\u53f8\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5e94\u4e86\u4e00\u58f0\uff0c\u53ec\u8fc7\u5341\u4e8c\u5973\u5c06\uff0c\u4f4e\u58f0\u5429\u5490\u5979\u4eec\u7684\u9632\u5b88\u4e4b\u4f4d\u3002 \u3000\u3000\u5434\u53cc\u5973\u63a2\u624b\u7531\u6000\u4e2d\u6478\u51fa\u4e86\u4e00\u628a\u5929\u9b54\u9488\uff0c\u7f13\u6b65\u884c\u5230\u4e86\u5927\u95e8\u53e3\u5904\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u6709\u6ca1\u6709\u8d70\u4e86\uff1f\u201d \u3000\u3000\u96f7\u65e0\u5f71\u7684\u58f0\u97f3\u4f20\u4e86\u8fdb\u6765\uff0c\u9053\uff1a \u201c\u533a\u533a\u5728\u6b64\uff0c\u59d1\u5a18\u6709\u4ec0\u4e48\u5429\u5490?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4f60\u8fd9\u5341\u4e8c\u4e2a\u767d\u8863\u5200\u624b\uff0c\u662f\u795e\u5200\u5802\u4e2d\u7684\u7b2c\u51e0\u7b49\u5200\u624b?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u7ba1\u51e0\u7b49\u5200\u624b\uff0c\u4ed6\u4eec\u90fd\u4f1a\u7ed9\u4f60\u4e00\u70b9\u5a01\u80c1\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u7684\u5a01\u80c1?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ed6\u4eec\u5df2\u7ecf\u5f00\u59cb\u5411\u4f60\u4eec\u653b\u88ad\u4e86\uff0c\u59d1\u5a18\u5f88\u5feb\u5c31\u53ef\u4ee5\u77e5\u9053\u4ed6\u4eec\u7684\u865a\u5b9e\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u89c9\u7740\u4f60\u8fd9\u4e2a\u795e\u5200\u5802\u7684\u4f4d\u7f6e\u5f88\u7a33\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u59d1\u5a18\u51c6\u5907\u6311\u62e8\u79bb\u95f4?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u6211\u5e0c\u671b\u4f60\u5e94\u8be5\u660e\u767d\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u55ef!\u59d1\u5a18\u8bf7\u8bf4?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5507\u4ea1\u9f7f\u5bd2\uff0c\u9ad8\u5929\u5065\u53ef\u4ee5\u5bf9\u4ed8\u6211\u548c\u9ad8\u5e7d\u5170\uff0c\u96be\u9053\u4ed6\u4eec\u4e0d\u4f1a\u5bf9\u4ed8\u4f60\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u5f88\u52a8\u542c\u7684\u6311\u62e8\uff0c\u4e0d\u8fc7\uff0c\u5728\u4e0b\u5f88\u76f8\u4fe1\u9ad8\u5821\u4e3b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u771f\u7684\u76f8\u4fe1\u9ad8\u5821\u4e3b\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u81ea\u7136\u662f\u5343\u771f\u4e07\u786e\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u8bf4\u7684\u8bdd\uff0c\u90fd\u662f\u5047\u7684\u4e86?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u552f\u5973\u5b50\u4e0e\u5c0f\u4eba\u4e3a\u96be\u517b\u4e5f\uff0c\u53e4\u4eba\u8bda\u4e0d\u6b3a\u6211\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u54b1\u4eec\u8c08\u597d\u7684\u4e8b\u60c5\uff0c\u4f60\u4e0d\u51c6\u5907\u5c65\u884c\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5434\u59d1\u5a18\uff0c\u54b1\u4eec\u4e4b\u95f4\uff0c\u6709\u4e9b\u4ec0\u4e48\u534f\u5b9a\uff0c\u6211\u5012\u5e0c\u671b\u4f60\u80fd\u8001\u8001\u5b9e\u5b9e\u5730\u8bf4\u51fa\u6765?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u73b0\u5728\u6b64\u5730\uff0c\u5f53\u9762\u8bf4\u51fa\u6765\uff0c\u65b9\u4fbf\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\u4e0d\u65b9\u4fbf\u7684\uff0c\u59d1\u5a18\u53ea\u7ba1\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u5982\u771f\u7684\u8fd9\u6837\u65e0\u60c5\uff0c\u90a3\u5c31\u522b\u602a\u6211\u65e0\u4e49\u4e86?\u201d \u3000\u3000\u9ad8\u5929\u5065\u7a81\u7136\u63a5\u4e86\u53e3\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5434\u53cc\u5973\uff0c\u6ca1\u6709\u7528\u7684\uff0c\u6211\u5f88\u4fe1\u4efb\u96f7\u65e0\u5f71\uff0c\u7eb5\u7136\u4ed6\u771f\u7684\u5bf9\u4f60\u8868\u793a\u4e86\u4ec0\u4e48\uff0c\u6211\u76f8\u4fe1\uff0c\u90a3\u4e5f\u662f\u4e3a\u4e86\u5bf9\u6211\u6548\u5fe0\uff0c\u4f55\u51b5\uff0c\u4f60\u8fd9\u79cd\u6311\u62e8\u7684\u624b\u6bb5\uff0c\u5f88\u4f4e\u4fd7\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u9ad8\u5929\u5065\uff0c\u7b2c\u4e00\u5821\u4f1a\u6709\u5f88\u591a\u4eba\u8981\u80cc\u53db\u4e8e\u4f60\uff0c\u6211\u3001\u9ad8\u5e7d\u5170\u3001\u96f7\u65e0\u5f71\u4e4b\u5916\uff0c\u8fd8\u6709\u4ed6\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u56de\u987e\u4e86\u96f7\u65e0\u5f71\u4e00\u773c\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f60\u4e0d\u7528\u653e\u5728\u5fc3\u4e0a\uff0c\u6211\u5f88\u660e\u767d\uff0c\u8fd8\u662f\u5f7b\u5934\u5f7b\u5c3e\u7684\u6311\u62e8\u79bb\u95f4\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u9ad8\u5929\u5065\uff0c\u4f60\u53e3\u4e2d\u867d\u7136\u8bf4\u7684\u5927\u65b9\uff0c\u4f46\u4f60\u7684\u5fc3\u4e2d\u60f3\u7684\uff0c\u8c01\u77e5\u9053\u5462?\u4ee5\u4f60\u7684\u6027\u683c\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u653e\u8fc7\u4e00\u4e2a\u654c\u4eba\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u7b2c\u4e00\u7ec4\u7684\u4eba\u624b\uff0c\u5df2\u7ecf\u5230\u4e86\u5927\u5385\u95e8\u524d\u3002 \u3000\u3000\u4e00\u4e2a\u767d\u8863\u5200\u624b\uff0c\u7a81\u7136\u626c\u8d77\u4e00\u811a\u8e22\u5728\u4e86\u5385\u95e8\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u811a\u7684\u529b\u91cf\u2014\u5341\u5206\u5f3a\u5927\uff0c\u7830\u7136\u5927\u9707\u58f0\u4e2d\uff0c\u6728\u95e8\u88c2\u5f00\u4e86\u4e24\u9053\u957f\u75d5\u3002 \u3000\u3000\u770b\u6837\u5b50\uff0c\u53ea\u8981\u518d\u8e22\u4e00\u811a\uff0c\u6728\u95e8\u5c31\u8981\u88c2\u5f00\u3002 \u3000\u3000\u5434\u53cc\u5973\u8d1f\u8d23\u5927\u95e8\u7684\u5b88\u536b\uff0c\u4e0d\u7981\u8138\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u597d\u72c2\u3002\u201d \u3000\u3000\u4f38\u624b\u62c9\u5f00\u4e86\u6728\u6813\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u59ca\uff0c\u4ed6\u4eec\u662f\u6740\u624b\uff0c\u4e0d\u80fd\u548c\u4ed6\u4eec\u6597\u6c14\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u767d\u8863\u5200\u624b\u7b2c\u4e8c\u811a\u53c8\u8e22\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5927\u95e8\u7a81\u7136\u5927\u5f00\u3002 \u3000\u3000\u6599\u4e0d\u5230\u4f1a\u6709\u8fd9\u6837\u7684\u53d8\u5316\uff0c\u90a3\u767d\u8863\u5200\u624b\u8eab\u4e0d\u7531\u5df1\u7684\u649e\u5165\u95e8\u5185\u3002 \u3000\u3000\u4f46\u89c1\u767d\u8292\u4e00\u95ea\uff0c\u5434\u53cc\u5973\u65a9\u51fa\u4e86\u4e00\u5251\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u5feb\u5982\u7535\u5149\u77f3\u706b\uff0c\u90a3\u767d\u8863\u5200\u624b\u4e00\u9897\u8111\u888b\uff0c\u7a81\u7136\u6eda\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5bf9\u65b9\u727a\u7272\u4e86\u4e00\u4e2a\u4eba\uff0c\u4f46\u5385\u95e8\u88ab\u6253\u5f00\u4e86\u3002 \u3000\u3000\u5728\u5434\u53cc\u5973\u793a\u610f\u4e4b\u4e0b\uff0c\u7ea2\u82b1\u548c\u5979\u7387\u9886\u7684\u4e8c\u4e2a\u5973\u5c06\uff0c\u4e00\u76f4\u9690\u8eab\u5728\u6728\u95e8\u540e\u9762\uff0c\u6ca1\u6709\u9732\u51fa\u5934\u6765\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u867d\u7136\u95ee\u5fc3\u65e0\u6127\uff0c\u4f46\u4ed6\u5bf9\u9ad8\u5929\u5065\u8fd9\u4e2a\u4eba\uff0c\u5374\u662f\u6df1\u6000\u6212\u5fc3\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u53ea\u6709\u7528\u82f1\u52c7\u7684\u8868\u73b0\uff0c\u6765\u8868\u8fbe\u4ed6\u7684\u5fe0\u8bda\u3002 \u3000\u3000\u5385\u95e8\u5df2\u5f00\uff0c\u7acb\u65f6\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u51b2\u8fdb\u53bb\u3002\u201d \u3000\u3000\u4e24\u4e2a\u767d\u8863\u5200\u624b\uff0c\u7a81\u7136\u5411\u524d\u51b2\u8fdb\u6765\u3002 \u3000\u3000\u4e09\u4e2a\u9ed1\u8863\u4eba\u4e5f\u7d27\u968f\u800c\u5165\u3002 \u3000\u3000\u7ea2\u82b1\u98de\u8dc3\u800c\u51fa\uff0c\u4e24\u67c4\u5251\u8fc5\u5982\u7535\u5149\u4e00\u822c\uff0c\u5206\u5411\u4e24\u4e2a\u767d\u8863\u5200\u624b\u523a\u53bb\u3002 \u3000\u3000\u4f46\u89c1\u4e24\u4e2a\u767d\u8863\u5200\u624b\u53f3\u624b\u4e00\u626c\uff0c\u4e24\u67c4\u5200\u8fc5\u5feb\u51fa\u624b\u3002 \u3000\u3000\u4f46\u95fb\u5f53\u5f53\u4e24\u58f0\uff0c\u7ea2\u82b1\u653b\u51fa\u7684\u4e24\u5251\uff0c\u88ab\u5c01\u6321\u5f00\u53bb\u3002 \u3000\u3000\u8fd9\u4e9b\u767d\u8863\u5200\u624b\uff0c\u548c\u5f69\u8679\u697c\u524d\u7684\u9ed1\u8863\u5251\u624b\uff0c\u6b66\u529f\u6709\u7740\u5f88\u5927\u7684\u4e0d\u540c\u3002 \u3000\u3000\u81f3\u5c11\uff0c\u8981\u6bd4\u4ed6\u4eec\u9ad8\u51fa\u4e24\u7b49\u3002 \u3000\u3000\u4e09\u4e2a\u9ed1\u8863\u4eba\uff0c\u88ab\u4e24\u4e2a\u767d\u8863\u5200\u624b\u548c\u7ea2\u82b1\u7684\u6076\u6597\u7ed9\u6321\u5728\u4e86\u95e8\u53e3\u3002 \u3000\u3000\u4ed6\u4eec\u624b\u4e2d\u5df2\u4e3e\u8d77\u4e86\u201c\u6c34\u706b\u65e0\u60c5\u201d\uff0c\u5374\u4e0d\u4fbf\u65bd\u653e\u51fa\u6765\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e00\u76f4\u5168\u795e\u8d2f\u6ce8\u7740\u53cc\u65b9\u640f\u6740\u5f62\u52bf\uff0c\u7a81\u7136\u4e00\u62ac\u624b\uff0c\u516d\u679a\u5929\u9b54\u9488\u7535\u5c04\u800c\u51fa\u3002 \u3000\u3000\u516d\u679a\u5929\u9b54\u9488\uff0c\u5206\u5411\u4e09\u4e2a\u9ed1\u8863\u4eba\u5c04\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5929\u9b54\u9488\u662f\u4e00\u79cd\u5f88\u6b79\u6bd2\u7684\u6697\u5668\uff0c\u53d1\u51fa\u65e0\u58f0\u65e0\u606f\uff0c\u4e09\u4e2a\u9ed1\u8863\u4eba\u53c8\u4e00\u76f4\u628a\u7cbe\u795e\u8d2f\u6ce8\u6253\u6597\u7684\u573a\u4e2d\u3002 \u3000\u3000\u4ed6\u4eec\u6025\u4e8e\u8868\u73b0\u51fa\u201c\u6c34\u706b\u65e0\u60c5\u201d\u7684\u5a01\u529b\uff0c\u5e0c\u671b\u80fd\u5c3d\u5feb\u53d1\u5c04\u51fa\u53bb\u3002 \u3000\u3000\u4f46\u7ea2\u82b1\u7684\u5251\u62db\u5f88\u8be1\u5f02\u51cc\u5389\uff0c\u5979\u8eab\u4e0a\u6709\u4e09\u652f\u5251\uff0c\u5de6\u53f3\u53cc\u624b\uff0c\u5404\u6267\u4e00\u5251\uff0c\u4e0d\u505c\u7684\u653b\u5411\u4e24\u4eba\uff0c\u4e24\u4e2a\u767d\u8863\u5200\u624b\uff0c\u867d\u7136\u5200\u6c89\u52bf\u731b\uff0c\u4f46\u4e00\u65f6\u95f4\uff0c\u7adf\u7136\u4e5f\u65e0\u6cd5\u5236\u773c\u5f3a\u654c\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u7ea2\u82b1\u4e5f\u4e86\u89e3\u81ea\u5df1\u5904\u5883\u7684\u9669\u6076\uff0c\u5979\u770b\u5230\u4e86\u4e09\u5177\u201c\u6c34\u706b\u65e0\u60c5\u201d\uff0c\u6b63\u5bf9\u7740\u5979\uff0c\u9664\u4e86\u5229\u7528\u4e24\u4e2a\u767d\u8863\u5200\u624b\u963b\u6321\u654c\u4eba\u7684\u653b\u52bf\u4e4b\u5916\uff0c\u522b\u65e0\u53ef\u884c\u4e4b\u7b56\uff0c\u6240\u4ee5\uff0c\u5979\u51fa\u624b\u7684\u5251\u62db\uff0c \u53ea\u6c42\u5c01\u6321\uff0c\u5e76\u65e0\u6740\u4eba\u4e4b\u610f\u3002 \u3000\u3000\u4e24\u4e2a\u767d\u8863\u5200\u624b\uff0c\u4e00\u76f4\u88ab\u903c\u5f97\u6321\u5728\u5979\u7684\u8eab\u524d\uff0c\u6321\u4f4f\u4e86\u4e09\u4e2a\u201c\u6c34\u706b\u65e0\u60c5\u201d\u3002 \u3000\u3000\u5434\u53cc\u5973\u7684\u5929\u9b54\u9488\uff0c\u50cf\u4e00\u697c\u8f7b\u70df\u4e00\u822c\uff0c\u75be\u5c04\u800c\u81f3\u3002 \u3000\u3000\u4e09\u4e2a\u9ed1\u8863\u4eba\u5168\u4e3a\u5c04\u4e2d\u3002 \u3000\u3000\u4e24\u4e2a\u8eab\u4e2d\u8981\u5bb3\uff0c\u4e00\u58f0\u4e0d\u54cd\u7684\u5012\u4e86\u4e0b\u53bb\u3001 \u3000\u3000\u5de6\u9996\u4e00\u4eba\uff0c\u5fc3\u751f\u8b66\u89c9\uff0c\u6025\u5207\u79fb\u52a8\u4e0b\u4e00\u4e0b\u8eab\u4f53\u3002 \u3000\u3000\u4e24\u679a\u5929\u9b54\u9488\uff0c\u4e00\u4e2d\u53f3\u80a9\uff0c\u4e00\u679a\u5c04\u4e2d\u4e86\u524d\u80f8\u3002 \u3000\u3000\u867d\u7136\u4e0d\u662f\u8981\u5bb3\uff0c\u4f46\u5929\u9b54\u9488\u4e0a\u5947\u6bd2\uff0c\u7acb\u523b\u4f7f\u4ed6\u7684\u5168\u8eab\u53d1\u9ebb\u3002 \u3000\u3000\u6025\u5207\u95f4\uff0c\u6309\u52a8\u673a\u94ae\uff0c\u7830\u7136\u4e00\u58f0\uff0c\u53d1\u51fa\u4e86\u201c\u6c34\u706b\u65e0\u60c5\u201d\u3002 \u3000\u3000\u4e00\u9053\u5f3a\u70c8\u7684\u706b\u7130\u75be\u5c04\u800c\u51fa\u3002 \u3000\u3000\u5939\u6742\u7740\u4e00\u9635\u9635\u7684\u9ed1\u8272\u6bd2\u6c41\u3002 \u3000\u3000\u50cf\u96e8\u6ef4\u4e00\u822c\u7684\u6bd2\u6c41\u3002 \u3000\u3000\u6309\u52a8\u4e86\u673a\u94ae\u4e4b\u540e\uff0c\u4eba\u4e5f\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u65e0\u6cd5\u7784\u51c6\uff0c\u5927\u90e8\u5206\u7684\u706b\u7130\u6bd2\u6c34\uff0c\u5c04\u5411\u4e86\u5c4b\u9876\u3002 \u3000\u3000\u4e00\u90e8\u5206\u5374\u51fb\u4e2d\u4e86\u4e24\u4e2a\u767d\u8863\u4eba\u3002 \u3000\u3000\u4e24\u4e2a\u6325\u5200\u731b\u653b\u7ea2\u82b1\u7684\u767d\u8863\u5200\u624b\uff0c\u7a81\u7136\u53d1\u51fa\u4e86\u60e8\u53eb\u4e4b\u58f0\u3002 \u3000\u3000\u987e\u4e0d\u5f97\u518d\u6325\u5200\u653b\u5411\u5f3a\u654c\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5a07\u58f0\u559d\u9053\uff1a\u201c\u7ea2\u82b1\uff0c\u5feb\u8fc7\u6765\u3002\u201d \u3000\u3000\u7ea2\u82b1\u5e94\u58f0\u98de\u843d\u5230\u9ad8\u5e7d\u5170\u7684\u8eab\u4fa7\u3002 \u3000\u3000\u4e24\u4e2a\u767d\u8863\u5200\u624b\u8eab\u4e0a\u7684\u8863\u670d\uff0c \u5df2\u7ecf\u71c3\u70e7\u8d77\u6765\uff0c \u4e00\u4e2a\u767d\u8863\u5200\u624b\uff0c\u5de6\u988a\u4e0a\u88ab\u6bd2\u6c34\u51fb\u4e2d\uff0c\u7acb\u523b\u5f00\u59cb\u6e83\u70c2\u3002 \u3000\u3000\u4e00\u9635\u50cf\u53d7\u4f24\u6012\u517d\u822c\u7684\u602a\u543c\u4e4b\u58f0\uff0c\u4e0d\u505c\u5730\u4f20\u6765 \u3000\u3000\u4e24\u4e2a\u767d\u8863\u5200\u624b\uff0c\u7a81\u7136\u8f6c\u8eab\u5954\u51fa\u5ba4\u5916\uff0c\u5411\u5730\u4e0a\u6eda\u53bb\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u51b7\u54fc\u4e00\u58f0\uff0c\u53f3\u624b\u4e00\u6325\uff0c\u4e24\u9053\u5bd2\u8292\uff0c\u75be\u5c04\u800c\u51fa\uff0c\u51fb\u6bd9\u4e86\u4e24\u4e2a\u767d\u8863\u5200\u624b\u3002 \u3000\u3000\u4eba\u867d\u7136\u6b7b\u4e86\uff0c\u4f46\u4ed6\u4eec\u8eab\u4e0a\u6cbe\u67d3\u7684\u6bd2\u706b\uff0c\u4ecd\u7136\u4e0d\u505c\u7684\u71c3\u70e7\u3002 \u3000\u3000\u8fd9\u5f53\u513f\uff0c\u4e00\u6761\u4eba\u5f71\uff1b\u5feb\u5982\u98d8\u98ce\u822c\uff0c\u843d\u5230\u4e86\u5385\u95e8\u53e3\u5904\uff0c\u4fef\u8eab\u6361\u8d77\u4e86\u4e24\u7b52\u6c34\u706b\u65e0\u60c5\u3002 \u3000\u3000\u662f\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u4ed6\u76ee\u7779\u4e86\u201c\u6c34\u706b\u65e0\u60c5\u201d\u7684\u5a01\u529b\uff0c\u5ffd\u53d1\u5947\u60f3\uff0c\u628a\u8dcc\u843d\u5385\u95e8\u53e3\u5904\u7684\u4e24\u7b52\u201c\u6c34\u706b\u65e0\u60c5\u201d\u7ed9\u6361\u56de\u6765\u3002 \u3000\u3000\u4e00\u8bed\u4e0d\u53d1\u7684\u7a81\u7136\u884c\u52a8\u3002 \u3000\u3000\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\u90fd\u770b\u5230\u4e86\u4e2a\u90ce\u4ee5\u8eab\u6d89\u9669\u3002 \u3000\u3000\u963b\u6b62\u5df2\u6765\u4e0d\u53ca\uff0c\u53c8\u4e0d\u6562\u51fa\u58f0\u559d\u53eb\uff0c\u90a3\u65e0\u7591\u662f\u544a\u8bc9\u654c\u4eba\u3002 \u3000\u3000\u53ef\u60dc\u7684\u662f\u96f7\u65e0\u5f71\u4e5f\u770b\u5230\u4e86\u3002 \u3000\u3000\u6765\u4e0d\u53ca\u62db\u547c\u5c5e\u4e0b\u7528\u201c\u6c34\u706b\u65e0\u60c5\u201d\u5bf9\u4ed8\uff0c\u4e00\u626c\u624b\uff0c\u53d1\u51fa\u4e86\u4e24\u679a\u98de\u5200\u3002\u8fd9\u4e24\u679a\u98de\u5200\uff0c\u4e5f\u8bb8\u4f24\u4e0d\u4e86\u4f55\u5bd2\u8863\uff0c\u4f46\u96f7\u65e0\u5f71\u7684\u7528\u5fc3\uff0c\u53ea\u5728\u8fdf\u7f13\u4e00\u4e0b\u4f55\u5bd2\u8863\u7684\u4e3e\u52a8\u3002 \u3000\u3000\u53ea\u8981\u4f55\u5bd2\u8863\u5206\u5fc3\u5bf9\u4ed8\u98de\u5200\uff0c\u53e6\u4e00\u7b52\u6c34\u706b\u65e0\u60c5\uff0c\u5c31\u6709\u5bf9\u4ed6\u53d1\u5c04\u7684\u673a\u4f1a\u3002 \u3000\u3000\u5374\u4e0d\u6599\u5434\u53cc\u5973\u4e5f\u6253\u51fa\u4e86\u4e24\u9053\u5bd2\u8292\uff0c\u8fce\u5411\u96f7\u65e0\u5f71\u7684\u98de\u5200\u51fb\u53bb\u3002 \u3000\u3000\u7b2c\u4e00\u6d41\u7684\u624b\u6cd5\uff0c\u679c\u975e\u51e1\u54cd\uff0c\u5728\u4f55\u5bd2\u8863\u8eab\u524d\u4e03\u516b\u5c3a\u5904\uff0c\u649e\u5728\u4e86\u4e00\u5904\uff0c\u4f55\u5bd2\u8863\u5df2\u6361\u8d77\u4e86\u4e24\u7b52\u201c\u6c34\u706b\u65e0\u60c5\u201d\u8dc3\u843d\u5230\u9ad8\u5e7d\u5170\u7684\u8eab\u4fa7\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5401\u4e00\u53e3\u6c14\uff0c\u767d\u4e86\u4f55\u5bd2\u8863\u4e00\u773c\uff0c\u5374\u8f6c\u5411\u7ea2\u82b1\u95ee\u9053\uff1a \u201c\u7ea2\u82b1\uff0c\u53d7\u4f24\u4e86\u6ca1\u6709?\u201d \u3000\u3000\u7ea2\u82b1\u6447\u6447\u5934\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u5982\u975e\u90a3\u4e24\u4e2a\u767d\u8863\u5200\u624b\uff0c\u53ea\u6015\u5c0f\u5a62\u8981\u6b7b\u5728\u90a3\u4e00\u7b52\u2018\u6c34\u706b\u65e0\u60c5\u2019\u4e4b\u4e0b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u4e5f\u662f\u7b2c\u4e00\u6b21\u770b\u5230\u4e86\u8fd9\u79cd\u6697\u5668\u53d1\u5c04\uff0c\u5fc3\u4e2d\u6697\u6697\u9707\u60ca\u3002 \u3000\u3000\u4f30\u7b97\u90a3\u6c34\u706b\u65e0\u60c5\u7b3c\u7f69\u7684\u5730\u65b9\uff0c\u5c31\u7b97\u662f\u7b2c\u4e00\u6d41\u8f7b\u529f\uff0c\u4e5f\u4e0d\u6613\u95ea\u907f\u3002 \u3000\u3000\u9664\u975e\u6709\u4e00\u4e2a\u53ef\u4ee5\u906e\u853d\u8eab\u8eaf\u7684\u5730\u65b9\uff0c\u5728\u4e09\u4e08\u4e4b\u5185\u7684\u4eba\uff0c\u975e\u6b7b\u5728\u5b83\u7684\u5a01\u529b\u4e4b\u4e0b\u4e0d\u53ef\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a \u201c\u5bf9\u6297\u2018\u6c34\u706b\u65e0\u60c5\u2019\u65f6\uff0c\u9700\u8981\u6709\u7740\u9690\u853d\u8eab\u5b50\u7684\u5730\u65b9\u3002\u201d \u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u5c4b\u9876\u6cbe\u67d3\u7684\u6bd2\u706b\uff0c\u4ecd\u7136\u53d1\u7740\u84dd\u8272\u7684\u706b\u7130\uff0c\u4e0d\u505c\u7684\u71c3\u70e7\u3002 \u3000\u3000\u90a3\u6bd2\u706b\u7684\u53ef\u6015\uff0c\u5c31\u5728\u5b83\u71c3\u70e7\u7684\u80fd\u91cf\u5f88\u5f3a\uff0c\u800c\u4e14\u5f88\u96be\u5c06\u5b83\u7184\u706d\u3002 \u3000\u3000\u5e78\u597d\uff0c\u8fd9\u5ea7\u5927\u5385\uff0c\u90fd\u662f\u7816\u77f3\u5efa\u9020\uff0c\u6728\u6599\u4e0d\u591a\uff0c\u90a3\u6bd2\u706b\u6cbe\u67d3\u7684\u5730\u65b9\uff0c\u4e5f\u662f\u90a3\u51e0\u7247\u7816\u77f3\u4e4b\u4e2d\uff0c\u706b\u7130\u867d\u7136\u4e0d\u7184\uff0c \u4e00\u65f6\u95f4\uff0c\u8fd8\u4e0d\u4f1a\u70e7\u4e86\u623f\u5b50\u3002 \u4f46\u90a3\u4e24\u5177\u767d\u8863\u5200\u624b\u7684\u5c38\u4f53\uff0c\u5c31\u4e0d\u540c\u4e0b\uff0c\u718a\u718a\u706b\u7130\uff0c\u5728\u5c38\u4f53\u4e0a\u71c3\u70e7\u8d77\u6765\u3002 \u3000\u3000\u53ea\u542c\u9ad8\u5929\u5065\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a \u201c\u4f60\u4eec\u542c\u7740\uff0c\u6211\u7ed9\u4f60\u4eec\u4e00\u987f\u996d\u7684\u65f6\u95f4\u60f3\u60f3\uff0c\u5982\u82e5\u8fd8\u4e0d\u80af\u5f03\u5251\u5c31\u7f1a\uff0c\u6211\u8981\u4e0b\u4ee4\u70e7\u4e86\u94c1\u82b1\u9662\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u56de\u987e\u4e00\u773c\uff0c\u9053\uff1a \u201c\u4ed6\u4eec\u771f\u8981\u70e7\u4e86\u8fd9\u5ea7\u94c1\u82b1\u9662\uff0c\u90a3\u5012\u662f\u4e00\u4ef6\u5f88\u9ebb\u70e6\u7684\u4e8b\u60c5\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u4e2a\u80fd\u505a\u5f97\u5230\uff0c\u4e0d\u5bb9\u6000\u7591\uff0c\u4e0d\u8fc7\uff0e\u8fd9\u662f\u4ed6\u6700\u540e\u624b\u6bb5\uff0c\u73b0\u5728\uff0c\u8fd8\u4e0d\u4f1a\u65bd\u7528\u51fa\u6765\u3002\u201d \u3000\u3000\u518d\u770b\u7ea2\u82b1\u548c\u7eff\u53f6\uff0c\u7387\u9886\u7684\u5341\u4e8c\u5973\u5c06\uff0c\u4e00\u4e2a\u4e2a\u795e\u60c5\u9547\u9759\uff0c\u5bf9\u4e8e\u9ad8\u5929\u5065\u8981\u70e7\u6bc1\u94c1\u82b1\u9662\u7684\u4e8b\uff0c\u6beb\u65e0\u60ca\u614c\u7684\u611f\u89c9\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u54cd\u8d77\u4e86\u4e00\u58f0\u5a07\u53f1\uff0c\u4e00\u4e2a\u7ea2\u8863\u5c11\u5973\uff0c\u8fde\u4eba\u5e26\u5251\u98de\u8dc3\u800c\u8d77\uff0c\u649e\u5411\u4e86\u4e00\u6247\u7a97\u5b50\u4e4b\u4e0a\u3002 \u3000\u3000\u7d27\u63a5\u7740\u4e00\u58f0\u60e8\u53eb\uff0c\u548c\u51e0\u58f0\u91d1\u94c1\u4e4b\u9e23\u3002 \u3000\u3000\u90a3\u98de\u8dc3\u800c\u8d77\u7684\u7ea2\u8863\u5973\u5b50\uff0c\u7a81\u7136\u6454\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u54cd\u8d77\u7684\u58f0\u97f3\u5f88\u6742\uff0c\u53ea\u4e0d\u8fc7\u4e8b\u60c5\u53d1\u751f\u5f97\u592a\u8fc7\u5feb\u901f\uff0c\u4e00\u65f6\u95f4\uff0c\u4f7f\u4eba\u6709\u7740\u76ee\u4e0d\u6687\u63a5\u7684\u611f\u53d7\u3002 \u3000\u3000\u5728\u573a\u4e4b\u4eba\u7684\u76ee\u529b\uff0c\u90fd\u8d85\u5f02\u5e38\u4eba\u5f88\u591a\uff0c\u8bf4\u4ed6\u4eec\u5b8c\u5168\u672a\u770b\u6e05\u695a\uff0c\u81ea\u7136\u4e0d\u662f\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4e8b\u60c5\u53d8\u5316\u7684\u8d85\u8fc7\u4e86\u4ed6\u4eec\u76ee\u529b\u6240\u89c1\u4e4b\u540e\uff0c\u4e00\u65f6\u8fd8\u65e0\u6cd5\u7406\u51fa\u4e00\u4e2a\u5934\u7eea\u6765\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u4eec\u770b\u6e05\u695a\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u90a3\u7ea2\u8863\u5973\u5251\u624b\uff0c\u5df2\u7ecf\u71c3\u70e7\u8d77\u6765\u4e86\u3002 \u3000\u3000\u6562\u60c5\u5979\u4e2d\u4e86\u4e00\u7b52\u201c\u6c34\u706b\u65e0\u60c5\u201d\u3002\u4f46\u7ea2\u8863\u5973\u5b50\u8eab\u4e0a\u4f69\u7740\u7684\u4e09\u652f\u5251\uff0c\u5c11\u4e86\u4e24\u652f\u3002 \u3000\u3000\u4e00\u5e55\u60e8\u5267\u3002 \u3000\u3000\u539f\u6765\uff0c\u7ea2\u8863\u5973\u5251\u624b\uff0c\u4e24\u652f\u5251\u5206\u522b\u63d2\u5165\u4e00\u4e2a\u767d\u8863\u5200\u624b\uff0c\u548c\u4e00\u4e2a\u624b\u6267\u201c\u6c34\u706b\u65e0\u60c5\u201d\u7684\u6740\u624b\u8eab\u4e0a\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u672c\u60f3\u51fb\u7834\u6728\u7a97\uff0c\u8dc3\u5165\u5ba4\u4e2d\uff0c\u4f46\u5374\u88ab\u5b88\u5728\u7a97\u5185\u7684\u7ea2\u8863\u5973\u5c06\u53d1\u89c9\uff0c\u8dc3\u8d77\u51fa\u5251\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\uff0c\u4e5f\u521a\u597d\u51c6\u5907\u7834\u7a97\u800c\u5165\u3002 \u3000\u3000\u4e24\u67c4\u5251\uff0c\u5206\u522b\u63d2\u5165\u4e86\u4e24\u4e2a\u6740\u624b\u7684\u8981\u5bb3\uff0c\u4e24\u4e2a\u4eba\u6454\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u90a3\u7ea2\u8863\u5973\u5c06\uff0c\u4e5f\u88ab\u4e00\u7b52\u201c\u6c34\u706b\u65e0\u60c5\u201d\u5b8c\u5168\u5c04\u4e2d\u3002 \u3000\u3000\u53ea\u662f\u8ddd\u79bb\u592a\u8fd1\u4e86\uff0c\u201c\u6c34\u706b\u65e0\u60c5\u201d\u7684\u5a01\u529b\uff0c\u53cd\u800c\u4e0d\u80fd\u53d1\u6325\u3002 \u3000\u3000\u6bd2\u706b\u3001\u6bd2\u6c34\uff0c\u5b8c\u5168\u5c04\u5728\u4e86\u90a3\u7ea2\u8863\u5973\u5c06\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u5979\u4eba\u7acb\u523b\u6b7b\u4ea1\uff0c\u4f46\u5374\u6551\u4e86\u5f88\u591a\u7684\u540c\u4f34\u3001\u53cb\u4eba\u3002 \u3000\u3000\u7ea2\u82b1\u773c\u770b\u81ea\u5df1\u8f96\u4e0b\u7684\u4e00\u4e2a\u59ca\u59b9\u60e8\u6b7b\uff0c\u5185\u5fc3\u4e4b\u4e2d\uff0c\u60b2\u75db\u83ab\u540d\u3002 \u3000\u3000\u8fd9\u4e9b\u5e74\u6765\uff0c\u957f\u4e45\u76f8\u5904\uff0c\u5341\u4e8c\u4eba\u90fd\u5df2\u6709\u4e86\u76f8\u5f53\u6df1\u539a\u7684\u60c5\u610f\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4f20\u7ed9\u5979\u4eec\u5251\u6cd5\uff0c\u867d\u7136\u90aa\u95e8\u5f97\u5f88\uff0c\u4f46\u5bf9\u5979\u4eec\u7684\u6001\u5ea6\uff0c\u5374\u662f\u5f88\u6b63\u5e38\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u8fd9\u4e9b\u4eba\u7684\u611f\u60c5\u3001\u6027\u683c\uff0c\u90fd\u548c\u5e38\u4eba\u65e0\u5f02\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u5434\u53cc\u5973\u4e00\u626c\u624b\uff0c\u54cd\u8d77\u4e86\u4e00\u58f0\u543c\u53eb\uff0c\u7d27\u63a5\u6251\u901a\u4e00\u58f0\uff0c\u6709\u4eba\u6454\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u539f\u6765\uff0c\u53e6\u4e00\u4e2a\u767d\u8863\u5251\u624b\uff0c\u51c6\u5907\u98de\u8dc3\u800c\u5165\uff0c\u5374\u88ab\u5434\u53cc\u5973\u4e00\u628a\u5929\u9b54\u9488\uff0c\u7ed9\u6253\u4e2d\u8981\u5bb3\u800c\u6b7b\u3002 \u3000\u3000\u5929\u9b54\u9488\uff0c\u672c\u5c31\u662f\u6c5f\u6e56\u4e0a\u51fa\u4e86\u540d\u7684\u6b79\u6bd2\u6697\u5668\uff0c\u5434\u53cc\u5973\u81ea\u6295\u5165\u7b2c\u4e00\u5821\u540e\uff0c\u5c31\u672a\u518d\u7528\u8fc7\u3002 \u3000\u3000\u4f46\u5979\u5e76\u6ca1\u653e\u4e0b\u8fd9\u95e8\u5de5\u592b\uff0c\u53cd\u800c\u5e38\u5728\u5f69\u8679\u697c\u4e2d\uff0c\u6697\u4e2d\u7ec3\u4e60\u3002 \u3000\u3000\u8fd9\u5c31\u4f7f\u5979\u7684\u5929\u9b54\u9488\uff0c\u66f4\u4e3a\u51fa\u795e\u5165\u5316\uff0c\u5feb\u5982\u95ea\u7535\u3002 \u3000\u3000\u6b64\u60c5\u6b64\u666f\u4e4b\u4e0b\uff0c\u5929\u9b54\u9488\u5374\u662f\u53d1\u6325\u4e86\u6781\u5927\u7684\u5a01\u529b\u3002 \u3000\u3000\u7830\u7136\u4e00\u58f0\u5927\u9707\uff0c\u4e00\u5757\u5927\u77f3\u98de\u4e86\u8fdb\u6765\uff0c\u4e1c\u9762\u7684\u4e00\u6247\u6728\u7a97\uff0c\u53c8\u88ab\u7838\u7834\u3002 \u3000\u3000\u90a3\u5757\u77f3\u5934\u76f8\u5f53\u7684\u5927\uff0c\u7838\u7834\u7684\u5730\u65b9\uff0c\u8db3\u4ee5\u98de\u5165\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u9632\u5b88\u90a3\u9762\u7a97\u5b50\u7684\u662f\u7eff\u53f6\u7684\u5c5e\u4e0b\u3002 \u3000\u3000\u7eff\u53f6\u6b63\u8981\u98de\u8eab\u67e5\u770b\u3002 \u3000\u3000\u4f46\u5374\u88ab\u9ad8\u5e7d\u5170\u62e6\u963b\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8981\u9632\u5907\u2018\u6c34\u706b\u65e0\u60c5\u2019\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u2018\u6c34\u706b\u65e0\u60c5\u2019\u5f88\u51f6\u6b8b\uff0c\u8bf8\u4f4d\u59ca\u59b9\u591a\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a \u201c\u5927\u5bb6\u53ef\u4ee5\u9690\u5165\u5f2f\u89d2\u5904\uff0c\u7528\u6697\u5668\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u9664\u4e86\u4ed6\u4eec\u5df2\u51b2\u5165\u5927\u5385\u4e4b\u5916\uff0c\u4e0d\u7528\u62fc\u547d\uff0c \u54b1\u4eec\u4eba\u6570\u5c11\uff0c\u7ecf\u4e0d\u8d77\u4f24\u4ea1\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u7ea2\u82b1\u5df2\u62ff\u6765\u4e00\u6761\u6e7f\u6c34\u7684\u68c9\u88ab\uff0c\u76d6\u5728\u6b7b\u4e86\u5c5e\u4e0b\u5c38\u4f53\u4e0a\u3002 \u3000\u3000\u56e0\u4e3a\u90a3\u7ea2\u8863\u5973\u5c06\u7684\u5c38\u4f53\uff0c\u4ecd\u7136\u5728\u718a\u718a\u5730\u71c3\u70e7\u7740\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u51b7\u9759\u5730\u89c2\u5bdf\u7740\u5927\u5385\u4e2d\u7684\u5f62\u52bf\uff0c\u654c\u4f17\u6211\u5be1\uff0c\u800c\u4e14\u90a3\u7ea2\u8863\u5973\u5c06\u7684\u6b7b\u72b6\u5947\u60e8\uff0c\u4f55\u5bd2\u8863\u5f88\u62c5\u5fc3\uff0c\u90a3\u4f1a\u5f71\u54cd\u5230\u5176\u4f59\u5973\u5c06\u7684\u6597\u5fd7\u3002 \u3000\u3000\u51fa\u4eba\u610f\u5916\u7684\u662f\uff0c\u4f59\u4e0b\u7684\u4eba\uff0c\u795e\u60c5\u90fd\u5f88\u6b63\u5e38\uff0c\u6ca1\u6709\u4efb\u4f55\u6fc0\u52a8\uff0c\u4e5f\u6ca1\u6709\u6d41\u73b0\u51fa\u754f\u60e7\u3002 \u3000\u3000\u51e0\u5e74\u7684\u82e6\u5b66\uff0c\u521d\u5ea6\u4e34\u654c\u7684\u5174\u594b\uff0c \u4f3c\u4e4e\u662f\u63a9\u906e\u4e86\u6050\u60e7\u3002 \u5341\u4e8c\u5973\u5c06\uff0c\u662f\u4e00\u652f\u8bad\u7ec3\u6709\u7d20\u7684\u9ad8\u624b\u961f\u4f0d\uff0c\u867d\u5904\u9006\u5883\uff0c\u4ecd\u80fd\u4fdd\u6301\u9547\u9759\u3002 \u3000\u3000\u4f46\u8fd9\u5206\u6fc0\u6602\u7684\u6218\u5fd7\uff0c\u80fd\u591f\u4fdd\u6301\u591a\u4e45\uff0c\u4f55\u5bd2\u8863\u7684\u5185\u5fc3\u4e2d\uff0c\u5b9e\u5728\u6709\u4e9b\u6000\u7591\u3002 \u3000\u3000\u5728\u201c\u6c34\u706b\u65e0\u60c5\u201d\u7684\u63a7\u5236\u4e4b\u4e0b\uff0c\u4efb\u4f55\u7a81\u56f4\u800c\u51fa\u7684\u8ba1\u5212\uff0c\u90fd\u6ca1\u6709\u673a\u4f1a\uff0c\u4f46\u8fd9\u5730\u65b9\uff0c\u4e5f\u4e0d\u80fd\u957f\u671f\u6b7b\u5b88\u3002 \u3000\u3000\u5982\u82e5\u9ad8\u5929\u5065\u771f\u7684\u4e0b\u4ee4\u653e\u706b\uff0c\u8fd9\u5730\u65b9\u81ea\u7136\u65e0\u6cd5\u5b88\u5f97\u4f4f\u3002 \u3000\u3000\u81ea\u4ece\u9ad8\u5e7d\u5170\u5c55\u793a\u4e03\u8272\u5251\u7684\u5a01\u529b\u4e4b\u540e\uff0c\u4f55\u5bd2\u8863\u76f8\u4fe1\uff0c\u8fd9\u4e24\u4e2a\u5973\u4eba\uff0c\u786e\u7136\u662f\u6b66\u6797\u4e2d\u7b2c\u4e00\u6d41\u9876\u5c16\u9ad8\u624b\uff0c\u5979\u4eec\u7684\u6b66\u529f\u3001\u5251\u62db\uff0c\u53c8\u8be1\u5f02\u7edd\u4f26\uff0c\u4f46\u51ed\u5b9e\u529b\uff0c\u95ef\u51fa\u7b2c\u4e00\u5821\u4f3c\u975e\u96be\u4e8b\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u7684\u662f\u4e16\u95f4\u7adf\u6709\u5982\u6b64\u51f6\u6076\u7684\u6697\u5668\uff0c\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\uff0c\u7adf\u7136\u88ab\u56f0\u5728\u6697\u5668\u4e4b\u4e0b\u3002 \u3000\u3000\u5728\u672a\u9047\u5230\u201c\u6c34\u706b\u65e0\u60c5\u201d\u8fd9\u6837\u7684\u6697\u5668\u4e4b\u524d\uff0c\u6c5f\u6e56\u867d\u7136\u4e5f\u6709\u5f88\u591a\u6b79\u6bd2\u7684\u6697\u5668\uff0c\u50cf\u6dec\u6bd2\u6885\u82b1\u9488\u3001\u5b50\u6bcd\u5f39\u7b49\uff0c\u4f46\u6697\u5668\u5728\u6c5f\u6e56\u4e0a\uff0c\u4e00\u76f4\u5f62\u4e0d\u6210\u4e00\u80a1\u4e3b\u6d41\uff0c\u53ea\u662f\u6b66\u529f\u4e0a\u7684\u4e00\u79cd\u8865\u52a9\u3002 \u3000\u3000\u5f88\u591a\u4eba\uff0c\u5bf9\u6697\u5668\u4e0a\u7684\u65bd\u7528\u6cd5\uff0c\u82b1\u53bb\u4e86\u4e0d\u5c11\u7684\u5de5\u592b\uff0c\u6d88\u8017\u4e86\u4e0d\u5c11\u7684\u7cbe\u529b\uff0c\u4f46\u5bf9\u4e00\u4e2a\u771f\u6b63\u7684\u6b66\u529f\u9ad8\u624b\uff0c\u5b83\u5374\u4e00\u76f4\u65e0\u6cd5\u6784\u6210\u7edd\u5bf9\u7684\u5a01\u80c1\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u5b66\u6b66\u7684\u4eba\uff0c\u4e5f\u628a\u5bf9\u4ed8\u6697\u5668\uff0c\u7b97\u6210\u5b50\u4e00\u95e8\u5fc5\u4fee\u4e4b\u8bfe\u3002 \u3000\u3000\u4f46\u8fd9\u201c\u6c34\u706b\u65e0\u60c5\u201d\uff0c\u5374\u4e3a\u6697\u5668\u5f00\u8f9f\u6210\u4e86\u4e00\u80a1\u4e3b\u6d41\u3002 \u3000\u3000\u5b83\u6210\u4e86\u4e00\u79cd\u65e0\u53ef\u6297\u62d2\u7684\u529b\u91cf\u3002 \u3000\u3000\u51f6\u6b8b\u3001\u9738\u9053\uff0c\u517c\u800c\u6709\u4e4b\u3002 \u3000\u3000\u6210\u4e86\u4e00\u79cd\u7edd\u5bf9\u7684\u6740\u4eba\u5229\u5668\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u60f3\u8d77\u4e86\u624b\u4e2d\u7684\u98de\u8f6e\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u4e00\u79cd\u7279\u6b8a\u7684\u6697\u5668\uff0c\u7528\u7279\u6b8a\u7684\u624b\u6cd5\u6253\u51fa\u7684\u6697\u5668\u3002 \u3000\u3000\u4ed6\u53f7\u79f0\u91d1\u521a\u98de\u8f6e\uff0c\u98de\u8f6e\u4e5f\u662f\u4ed6\u6210\u540d\u6c5f\u6e56\u7684\u7edd\u6280\u4e4b\u4e00\u3002 \u3000\u3000\u548c\u6c34\u706b\u65e0\u60c5\u4e0d\u540c\u7684\u662f\uff0c\u98de\u8f6e\u672c\u8eab\u5e76\u975e\u51f6\u6b8b\u9738\u9053\uff0c\u53d8\u5316\u662f\u5728\u5b83\u8fd0\u7528\u7684\u624b\u6cd5\u4e4b\u4e0a\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7f13\u7f13\u892a\u4e0b\u4e86\u8155\u4e0a\u4e24\u53ea\u91d1\u73af\u98de\u8f6e\uff0c\u63e1\u5728\u624b\u4e2d\uff0c\u6084\u7136\u79fb\u52a8\u811a\u6b65\uff0c\u627e\u5230\u4e86\u4e00\u5904\u89c6\u754c\u826f\u597d\u7684\u4f4d\u7f6e\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u6084\u7136\u884c\u4e86\u8fc7\u6765\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u4f55\u90ce\uff0c\u4e0d\u8981\u5192\u9669\uff0c\u6211\u4eec\u90fd\u4e0d\u6015\u6b7b\uff0c\u4f46\u6211\u4e0d\u613f\u4f60\u5148\u6b7b\uff0c\u8981\u6b7b\u65f6\uff0c\u5927\u5bb6\u6b7b\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u5bf9!\u751f\u540c\u7f57\u5e0f\u6b7b\u540c\u7a74\uff0c\u54b1\u4eec\u4e09\u4e2a\u662f\u4e00\u4f53\u554a!\u201d \u3000\u3000\u4e0d\u77e5\u4f55\u65f6\uff0c\u5979\u4e5f\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u663e\u7136\u4e24\u4e2a\u4eba\u5bf9\u8fd9\u4f4d\u5fc3\u4e0a\u60c5\u90ce\uff0c\u6709\u7740\u6781\u5ea6\u7684\u5173\u5fc3\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u653e\u5fc3\uff0c\u4e24\u4f4d\u4e0d\u540c\u610f\uff0c\u6211\u8fd8\u820d\u4e0d\u5f97\u6b7b\u5462\u3002\u90a3\u4e9b\u767d\u8863\u5200\u624b\u7684\u6b66\u529f\u867d\u7136\u4e0d\u9519\uff0c\u4f46\u6700\u53ef\u6015\u7684\u662f\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u7b97\u7b97\u4ed6\u4eec\u6765\u4e86\u5341\u4e8c\u4e2a\uff0c\u597d\u50cf\u5df2\u7ecf\u6b7b\u53bb\u4e86\u56db\u4e2a\uff0c\u6211\u5982\u80fd\u518d\u4f24\u4ed6\u4eec\u4e24\u4e2a\uff0c\u53ef\u4ee5\u53bb\u54b1\u4eec\u4e0d\u5c11\u7684\u538b\u529b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c \u54b1\u4eec\u4e0d\u80fd\u6b7b\u5b88\u6b64\u5730\uff0c\u7b49\u5929\u8272\u5165\u591c\uff0c\u6211\u4eec\u4f1a\u7a81\u56f4\u800c\u51fa\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u8981\u5982\u4f55\u4f24\u4ed6\u4eec\u4e24\u4e2a\u2018\u6c34\u706b\u65e0\u60c5\u2019\u7684\u6740\u624b?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u7528\u98de\u8f6e\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7528\u98de\u8f6e\u53ef\u4ee5\uff0c\u4f46\u5374\u4e0d\u80fd\u4ee5\u8eab\u6d89\u9669\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u542c\u5230\u6ca1\u6709\uff0c\u5170\u59b9\u7684\u519b\u4ee4\u4e00\u5411\u68ee\u4e25\uff0c \u4f60\u8981\u5c0f\u5fc3\u4e00\u4e9b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u4ed6\u8981\u771f\u53d7\u4e86\u4f24\uff0c \u53ea\u6015\u5927\u59ca\u6bd4\u6211\u8fd8\u8981\u5fc3\u75bc\u3002\u201d \u6b64\u65f6\u6b64\u60c5\uff0c\u4e09\u4eba\u4f4e\u58f0\u9017\u7b11\uff0c\u6d0b\u6ea2\u95fa\u623f\u60c5\u8da3\uff0c\u51b2\u6de1\u4e86\u4e0d\u5c11\u7d27\u5f20\u5fc3\u60c5\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u522b\u8981\u4ed6\u4eec\u51b2\u4e0b\u8fdb\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4f60\u8981\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u5f53\u77e5\u4f60\u5982\u770b\u5230\u654c\u4eba\u65f6\uff0c\u654c\u4eba\u5c31\u53ef\u80fd\u4f1a\u770b\u5230\u4f60\uff0c\u4f60\u5982\u80fd\u5c04\u4e2d\u654c\u4eba\u65f6\uff0c\u654c\u4eba\u4e5f\u53ef\u4ee5\u5c04\u4e2d\u4f60\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4f1a\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u8981\u5192\u9669\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4f1a\u73cd\u60dc\u81ea\u5df1\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\u6084\u7136\u5206\u5411\u4e24\u4fa7\u884c\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7f13\u7f13\u79fb\u52a8\u7740\u8eab\u8eaf\uff0c \u76ee\u5149\u51dd\u6ce8\u5728\u5927\u5385\u5916\u9762\uff1b \u5ffd\u7136\u53cc\u624b\u4e00\u626c\uff0c\u4e24\u679a\u91d1\u73af\u98de\u8f6e\u8131\u624b\u800c\u51fa\u3002 \u3000\u3000\u4e24\u679a\u91d1\u73af\u98de\u8f6e\u662f\u76f4\u63a5\u7684\u98de\u4e86\u51fa\u53bb\uff0c\u4f46\u51fa\u4e86\u5927\u5385\u7684\u95e8\u6237\u4e4b\u5916\uff0c\u7a81\u7136\u5411\u5de6\u53f3\u5206\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u7d27\u63a5\u7740\u4e24\u58f0\u60e8\u53eb\uff0c\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e24\u679a\u91d1\u73af\u98de\u8f6e\u7a81\u7136\u6253\u4e86\u4e24\u4e2a\u8f6c\uff0c\u7adf\u7136\u53c8\u98de\u4e86\u56de\u6765\u3002 \u3000\u3000\u6562\u60c5\uff0c\u8fd9\u98de\u8f6e\u4f1a\u8f6c\u5f2f\u3002 \u3000\u3000\u4e24\u4e2a\u98de\u8f6e\u53bb\u800c\u590d\u8fd4\uff0c\u53c8\u56de\u5230\u4e86\u4f55\u5bd2\u8863\u7684\u624b\u4e2d\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u770b\u770b\u624b\u4e2d\u98de\u8f6e\uff0c\u5e26\u6709\u8840\u8ff9\uff0c\u5fc3\u77e5\u5df2\u7ecf\u4f24\u4e86\u4eba\u3002 \u3000\u3000\u4f46\u4f24\u4e86\u4ec0\u4e48\u4eba\uff0c\u4f55\u5bd2\u8863\u5374\u65e0\u6cd5\u4e86\u89e3\u3002 \u3000\u3000\u7eff\u53f6\u5c31\u7ad9\u5728\u4f55\u5bd2\u8863\u7684\u8eab\u4fa7\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f55\u5927\u4fa0\uff0c\u4f60\u8fd9\u6697\u5668\uff0c\u8fd8\u4f1a\u8f6c\u5f2f\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u6ca1\u6709\u4ec0\u4e48\u7a00\u5947\uff0c\u53ea\u662f\u4e00\u79cd\u56de\u65cb\u4e4b\u529b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u90a3\u4e5f\u4e0d\u5bb9\u6613\uff0c\u9700\u8981\u5f88\u9002\u5f53\u7684\u529b\u9053\uff0c\u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u624d\u80fd\u6536\u53d1\u968f\u5fc3\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4f24\u4e86\u4e24\u4e2a\u4eba\uff0c\u53ea\u53ef\u60dc\u4e0d\u77e5\u9053\u662f\u4f24\u4e86\u4e24\u4e2a\u4ec0\u4e48\u6837\u5b50\u7684\u4eba\uff0c\u5509!\u5e0c\u671b\u662f\u4f24\u4e86\u4e24\u4e2a\u65bd\u7528\u2018\u6c34\u706b\u65e0\u60c5\u2019\u7684\u4eba\u3002\u201d \u3000\u3000\u7a81\u542c\u7ea2\u82b1\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u5c0f\u5fc3!\u201d \u3000\u3000\u98de\u8eab\u4e00\u8dc3\uff0c\u76f4\u51b2\u8fc7\u53bb\u3002 \u3000\u3000\u4eba\u4e00\u52a8\uff0c\u624b\u4e2d\u95ea\u8d77\u4e86\u4e24\u9053\u7ea2\u3001\u767d\u5251\u5149\uff0c\u76f4\u5377\u8fc7\u53bb\u3002 \u3000\u3000\u539f\u6765\uff0c\u8fd9\u65f6\u6b63\u6709\u4e00\u4e2a\u767d\u8863\u5200\u624b\uff0c \u5411\u4e00\u4e2a\u7ea2\u8863\u5973\u5251\u624b\u8eab\u540e\u653b\u53bb\u3002 \u6c5f\u6e56\u4e0a\u751f\u6b7b\u76f8\u640f\uff0c\u6b66\u529f\u7b2c\u4e00\uff0c\u4f46\u4e8b\u5b9e\u4e0a\uff0c\u4e5f\u9700\u8981\u4e00\u70b9\u8fd0\u6c14\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u3001\u9ad8\u5e7d\u5170\u7684\u8fd0\u6c14\u8fd8\u7b97\u4e0d\u9519\u3002 \u3000\u3000\u5982\u82e5\u6084\u7136\u8fdb\u6765\u7684\uff0c\u662f\u4e00\u4e2a\u624b\u6267\u201c\u6c34\u706b\u65e0\u60c5\u201d\u7684\u6740\u624b\uff0c\u5c4b\u5185\u4e4b\u4eba\uff0c\u90fd\u4f1a\u6b7b\u4f24\u5f97\u5f88\u60e8\u3002 \u3000\u3000\u504f\u5de7\u7684\u662f\uff0c\u8fdb\u6765\u7684\u7adf\u662f\u4e00\u4e2a\u767d\u8863\u5200\u624b\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u548c\u7ea2\u82b1\u6253\u5728\u4e86\u4e00\u8d77\u3002 \u3000\u3000\u5728\u5341\u4e8c\u5973\u5c06\u4e2d\uff0c\u7ea2\u82b1\u662f\u6700\u6770\u51fa\u7684\u4e00\u4e2a\uff0c \u540c\u6837\u7684\u5b66\u4e60\u4e00\u79cd\u5251\u6cd5\uff0c\u4f46\u5979\u7684\u6210\u5c31\uff0c\u5374\u662f\u8d85\u8d8a\u4e86\u540c\u4f34\u3002 \u3000\u3000\u4e0d\u4f46\u5728\u5251\u672f\u4e0a\uff0c\u5979\u6709\u4e86\u5f88\u7279\u522b\u7684\u6210\u5c31\uff0c\u5c31\u662f\u5728\u8033\u76ee\u884c\u52a8\u4e0a\uff0c\u5979\u4e5f\u6709\u7740\u8d85\u8fc7\u522b\u4eba\u7684\u53cd\u5e94\u3002 \u3000\u3000\u8fd9\u767d\u8863\u5200\u624b\uff0c\u4e0d\u662f\u5728\u9ad8\u5e7d\u5170\u76d1\u89c6\u7684\u95e8\u7a97\u4e4b\u4e2d\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u4f7f\u5f97\u9ad8\u5e7d\u5170\u3001\u5434\u53cc\u5973\u8bb6\u7136\u4e0d\u5df2\u3002 \u3000\u3000\u5434\u53cc\u5973\u56db\u4e0b\u6253\u91cf\u5f88\u4e45\uff0c\u624d\u53d1\u89c9\u4e86\u4e00\u4e2a\u79d8\u5bc6\uff1a \u3000\u3000\u539f\u6765\uff0c\u8fd9\u767d\u8863\u5200\u5ba2\u662f\u5728\u5c4b\u9876\u548c\u5899\u58c1\u7ed3\u5408\u4e4b\u5904\uff0c\u6253\u7834\u4e86\u4e00\u4e2a\u6d1e\u6f5c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5434\u53cc\u5973\u53d1\u89c9\u7684\u539f\u56e0\uff0c\u5c31\u662f\u90a3\u5899\u58c1\u4e0a\uff0c\u6709\u4e00\u7247\u7070\u571f\uff0c\u6b63\u81ea\u4e0a\u9762\u7f13\u7f13\u5411\u4e0b\u843d\u4e0b\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\uff0c\u6b63\u7531\u90a3\u91cc\u5411\u91cc\u9762\u94bb\u8fdb\u6765\u3002 \u3000\u3000\u53ef\u4ee5\u770b\u5230\u9ed1\u8272\u7684\u8863\u670d\u3002 \u3000\u3000\u7a7f\u9ed1\u8863\u7684\u4eba\uff0c\u5c31\u662f\u201c\u6c34\u706b\u65e0\u60c5\u201d\u6740\u624b\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e00\u62ac\u624b\uff0c\u51e0\u679a\u5929\u9b54\u9488\uff0c\u75be\u5c04\u800c\u51fa\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u5b8c\u5168\u65e0\u6cd5\u95ea\u907f\uff0c\u8fde\u770b\u4e5f\u6ca1\u6709\u770b\u5230\u3002 \u3000\u3000\u5c31\u90a3\u6837\uff0c\u6b7b\u5728\u4e86\u90a3\u58c1\u95f4\u7a7a\u9699\u4e4b\u4e2d\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u6837\uff0c\u54b1\u4eec\u8fde\u90a3\u58c1\u6d1e\u4e5f\u4e0d\u7528\u5c01\u95ed\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u53ef\u4ee5\u5728\u4e00\u5904\u5730\u65b9\u5f00\u6d1e\u800c\u5165\uff0c\u5c31\u53ef\u4ee5\u5728\u5341\u5904\u5730\u65b9\u5f00\u6d1e\u800c\u5165\u3002\u201d \u3000\u3000\u7eff\u53f6\u9053\uff1a \u201c\u4e0d\u4f1a\u7684\uff0c\u53ea\u6709\u90a3\u4e00\u5904\u5730\u65b9\u6700\u8106\u5f31\uff0c\u8fd9\u5ea7\u5927\u5385\uff0c\u4fee\u7b51\u5f97\u5f88\u575a\u56fa\uff0c\u4e0d\u662f\u4efb\u4f55\u5730\u65b9\u90fd\u53ef\u4ee5\u7834\u5f00\u7684\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u7eff\u53f6\uff0c \u5982\u82e5\u4ed6\u4eec\u653e\u706b\u70e7\u8fd9\u5ea7\u5927\u5385\uff0c\u53ea\u6015\u4e5f\u4e0d\u592a\u5bb9\u6613\u5427?\u201d \u3000\u3000\u7eff\u53f6\u9053\uff1a \u201c\u5f88\u96be\u70e7\u5f97\u8d77\u6765\uff0c\u4f46\u5e76\u975e\u4e0d\u80fd\u70e7\uff0c\u6700\u53ef\u6015\u7684\u5c31\u662f\u6d53\u70df\u85b0\u4eba\uff0c \u4eba\u5c31\u5f88\u96be\u6297\u4e86\u3002\u201d \u3000\u3000\u7a81\u542c\u4e00\u9635\u60e8\u53eb\uff0c\u90a3\u767d\u8863\u5200\u5b50\u5012\u4e0b\u53bb\u3002 \u3000\u3000\u7ea2\u82b1\u6740\u4e86\u90a3\u767d\u8863\u5200\u624b\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4e0d\u662f\u7ea2\u82b1\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u7ea2\u82b1\u5728\u548c\u4ed6\u52a8\u624b\u7684\u65f6\u5019\uff0c\u4e24\u4e2a\u7ea2\u8863\u5973\u5c06\uff0c\u6084\u7136\u800c\u4e0a\uff0c\u4e09\u4eba\u5408\u51fb\uff0c\u5341\u5408\u4e4b\u5185\uff0c\u6740\u6b7b\u4e86\u767d\u8863\u5200\u624b\u3002 \u3000\u3000\u795e\u5200\u5802\u7684\u4eba\u624b\uff0c\u6298\u635f\u5f88\u91cd\u3002 \u3000\u3000\u53cc\u65b9\u7684\u6697\u5668\u90fd\u5f88\u5389\u5bb3\uff0c\u4e00\u65f6\u95f4\uff0c\u5f7c\u6b64\u90e8\u63a9\u853d\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5434\u53cc\u5973\u51e0\u6b21\u60f3\u51fa\u53bb\u77a7\u77a7\uff0c\u4f46\u5374\u88ab\u9ad8\u5e7d\u5170\u529d\u963b\u3002 \u3000\u3000\u4e00\u573a\u6697\u88ad\u640f\u6740\u4e4b\u540e\uff0c\u7a81\u7136\u95f4\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u9759\u5f97\u6709\u7740\u6b7b\u5bc2\u7684\u611f\u89c9\u3002 \u3000\u3000\u5434\u53cc\u5973\u7b2c\u4e00\u4e2a\u53d7\u4e0d\u4e86\u8fd9\u4e2a\u5e7d\u5bc2\uff0c\u4f46\u5e78\u597d\u9ad8\u5e7d\u5170\u529d\u963b\u4e86\u5979\u3002 \u3000\u3000\u5341\u4e8c\u4e2a\u6c34\u706b\u65e0\u60c5\u6740\u624b\uff0c\u5df2\u7ecf\u6b7b\u4e86\u516d\u4e2a\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5f88\u5fc3\u75bc\uff0c\u628a\u4f59\u4e0b\u7684\u516d\u4e2a\uff0c\u8c03\u4e86\u56de\u6765\uff0c\u85cf\u8eab\u5728\u82b1\u6811\u4e4b\u540e\uff0c\u76d1\u89c6\u7740\u94c1\u82b1\u9662\uff0c\u53ea\u8981\u6709\u4eba\u51fa\u5934\uff0c\u518d\u7528\u6697\u5668\u963b\u6321\u3002 \u3000\u3000\u795e\u5200\u5802\u7684\u767d\u8863\u5200\u624b\uff0c\u4e94\u6b7b\u4e00\u4f24\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u4e5f\u5f88\u5fc3\u75bc\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u770b\u4e0d\u5230\u5385\u4e2d\u5f62\u52bf\uff0c\u60f3\u50cf\u4e2d\u5341\u4e8c\u5973\u5c06\u4e5f\u6709\u4e86\u5f88\u5927\u7684\u4f24\u4ea1\u3002 \u3000\u3000\u5982\u82e5\u4ed6\u77e5\u9053\u5341\u4e8c\u5973\u5c06\uff0c\u53ea\u6298\u635f\u4e00\u4e2a\u4eba\u65f6\uff0c\u4ed6\u4f1a\u66f4\u75db\u82e6\u3002 \u3000\u3000\u53cc\u65b9\u6210\u4e86\u4e00\u79cd\u50f5\u6301\u4e4b\u5c40\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u4f4e\u58f0\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u54b1\u4eec\u73b0\u5728\u5e94\u8be5\u5982\u4f55\u5bf9\u4ed8\u8fd9\u4ef6\u4e8b?\u201d \u3000\u3000\u9ad8\u5929\u5065\u7684\u5185\u5fc3\u4e4b\u4e2d\uff0c\u4e5f\u6709\u4e9b\u832b\u7136\u7684\u611f\u89c9\uff0c\u60f3\u4e0d\u5230\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\uff0c\u7adf\u6709\u5982\u6b64\u9ad8\u660e\u7684\u6b66\u529f\u3002 \u3000\u3000\u5b89\u6392\u597d\u597d\u7684\u5341\u4e8c\u5973\u5c06\uff0c\u7adf\u53c8\u4f1a\u4e34\u9635\u5012\u6208\uff0c\u91cd\u56de\u5230\u9ad8\u5e7d\u5170\u7684\u638c\u63e1\u4e4b\u4e2d\u3002 \u3000\u3000\u5728\u6b66\u529f\u7684\u6210\u5c31\u4e0a\uff0c\u9ad8\u5929\u5065\u81ea\u8d1f\u8d85\u4eba\uff0c\u4f46\u6b64\u523b\uff0c\u4ed6\u5df2\u65e0\u4fe1\u5fc3\uff0c\u4e00\u5bf9\u4e00\u7684\u548c\u5434\u53cc\u5973\u5c55\u5f00\u4e00\u573a\u640f\u6740\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5728\u9ed8\u9ed8\u4e2d\u57f9\u517b\u51fa\u7684\u611f\u671b\uff0c\u4e5f\u51fa\u4e86\u9ad8\u5929\u5065\u7684\u610f\u6599\u3002 \u3000\u3000\u4f46\u6700\u53ef\u6015\u7684\uff0c\u8fd8\u662f\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\u6311\u660e\u4e86\u5f88\u591a\u7684\u5185\u60c5\u3002 \u3000\u3000\u4efb\u4f55\u5728\u573a\u7684\u4eba\uff0c\u90fd\u5df2\u611f\u89c9\u5230\uff0c\u9ad8\u5e7d\u5170\u4e0d\u662f\u9ad8\u5929\u5065\u7684\u5973\u513f\u3002 \u3000\u3000\u800c\u8fd9\u4ef6\u4e8b\uff0c\u5728\u7b2c\u4e00\u5821\u4e2d\u662f\u6700\u5927\u7684\u673a\u5bc6\uff0c\u9664\u4e86\u9ad8\u5929\u5065\u4e4b\u5916\uff0c\u518d\u65e0\u522b\u4eba\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u7eb5\u6a2a\u9ed1\u767d\u4e24\u9053\uff0c\u672c\u662f\u4e2a\u6781\u4f1a\u88c5\u4f5c\u7684\u4eba\uff0c\u4f46\u8fd9\u4e00\u6b21\uff0c\u4ed6\u7adf\u7136\u9690\u9690\u7684\u627f\u8ba4\u4e86\u9ad8\u5e7d\u5170\u7684\u6307\u8d23\u3002 \u3000\u3000\u4ed6\u8fd9\u5927\u534a\u751f\u4e2d\uff0c\u65e0\u5f80\u4e0d\u5229\uff0c\u540d\u58f0\u52a8\u6c5f\u6e56\uff0c\u4f46\u5374\u53c8\u4e00\u624b\u906e\u4f4f\u4e86\u5929\u4e0b\u4eba\u7684\u8033\u76ee\u3002 \u3000\u3000\u4ed6\u4ece\u6ca1\u6709\u50cf\u8fd9\u4e48\u61ca\u4e27\u8fc7\u3002 \u3000\u3000\u4e00\u4ee3\u67ad\u96c4\u7684\u4fe1\u5fc3\uff0c\u5f00\u59cb\u52a8\u6447\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u7a76\u7adf\u662f\u7ecf\u8fc7\u5927\u98ce\u5927\u6d6a\u7684\u4eba\uff0c\u5b9a\u4e00\u4e0b\u795e\uff0c \u56de\u5934\u5bf9\u96f7\u65e0\u5f71\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u4e0d\u7528\u518d\u786c\u62fc\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u4f4e\u58f0\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u53ef\u662f\u771f\u51c6\u5907\u653e\u706b\u70e7\u4e86\u8fd9\u94c1\u82b1\u9662\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u56de\u987e\u4e86\u96f7\u65e0\u5f71\u4e00\u773c\uff0c\u5ffd\u7136\u89c9\u7740\u8fd9\u4e2a\u4eba\uff0c\u4e0d\u53ef\u5341\u5206\u4fe1\u4efb\u3002 \u3000\u3000\u4f46\u4ed6\u795e\u6001\u4e4b\u4e0a\uff0c\u5374\u8868\u73b0\u5f97\u66f4\u4e3a\u67d4\u548c\uff0c\u9053\uff1a \u201c\u96f7\u5802\u4e3b\u7684\u610f\u601d\u5462?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c5e\u4e0b\u4e4b\u610f\uff0c\u5982\u975e\u5fc5\u8981\uff0c\u4e0d\u7528\u653e\u706b!\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u54e6!\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u9634\u9633\u5821\u4e2d\u7684\u4eba\uff0c\u8fd8\u5728\u7b2c\u4e00\u5821\u5916\uff0c\u5982\u82e5\u653e\u706b\u4e00\u70e7\uff0c\u5c82\u4e0d\u662f\u544a\u8bc9\u4ed6\u4eec\uff0c\u7b2c\u4e00\u5821\u5185\u53d1\u751f\u4ec0\u4e48\u91cd\u5927\u7684\u53d8\u5316\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u5fae\u5fae\u4e00\u7b11\u9053\uff1a \u201c\u5bf9!\u5bf9!\u4e5f\u8bb8\uff0c\u4ed6\u4eec\u5df2\u7ecf\u6df7\u5165\u4e86\u7b2c\u4e00\u5821\u4e2d\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\u5427!\u7b2c\u4e00\u5821\u4e2d\u5404\u5904\u9632\u536b\uff0c\u90fd\u672a\u4f20\u8b66\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u7b2c\u4e00\u5821\u592a\u8001\u8fc8\u4e86\uff0c\u8fd9\u4e9b\u5e74\u517b\u5c0a\u5904\u4f18\uff0c\u4ed6\u4eec\u53ef\u80fd\u90fd\u5df2\u6709\u4e86\u677e\u61c8\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6b64\u4e8b\u8fc7\u53bb\u4ee5\u540e\uff0c\u54b1\u4eec\u5e94\u8be5\u597d\u597d\u6574\u7406\u4e00\u4e0b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5e0c\u671b\uff0c\u6211\u4eec\u80fd\u591f\u8fc7\u4e86\u8fd9\u4e00\u5173\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u8fd9\u4e00\u5173\u5f88\u96be\u8fc7\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u5fae\u5fae\u4e00\u6014\uff0c\u6697\u751f\u8b66\u60d5\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4ee5\u54b1\u4eec\u7b2c\u4e00\u5821\u7684\u5b9e\u529b\uff0c\u6c5f\u6e56\u4e0a\u4efb\u4f55\u4e00\u4e2a\u95e8\u6d3e\uff0c\u4e5f\u96be\u4e0e\u54b1\u4eec\u6297\u62d2\uff0c \u53ea\u4e0d\u8fc7\uff0c\u8fd9\u4e48\u591a\u4e8b\u6765\uff0c\u4ed6\u4eec\u751f\u6d3b\u5f97\u592a\u5b89\u9038\u4e86\uff0c\u53ef\u80fd\u758f\u5ffd\u4e86\u5f88\u591a\u4e8b\u60c5\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\u8bf4\u5f97\u662f\uff0c\u5c5e\u4e0b\u8981\u4ed6\u4eec\u51c6\u5907\u6850\u6cb9\u3001\u6728\u67f4\u53bb\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u5c5e\u4e0b\u60f3\u4e86\u4e00\u4e0b\uff0c\u5821\u4e3b\u8bf4\u5f97\u5bf9\uff0c\u5fa1\u5916\u5fc5\u5148\u6518\u5185\uff0c\u5148\u628a\u5434\u53cc\u5973\u548c\u9ad8\u5e7d\u5170\u7ed9\u6d88\u706d\u4e86\u518d\u8bf4\u3002\u201d \u3000\u3000\u4e00\u573a\u6697\u88ad\u640f\u6740\u4e4b\u540e\uff0c\u7a81\u7136\u95f4\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u9759\u5f97\u6709\u7740\u6b7b\u5bc2\u7684\u611f\u89c9\u3002 \u3000\u3000\u5434\u53cc\u5973\u7b2c\u4e00\u4e2a\u53d7\u4e0d\u4e86\u8fd9\u4e2a\u5e7d\u5bc2\uff0c\u4f46\u5e78\u597d\u9ad8\u5e7d\u5170\u529d\u963b\u4e86\u5979\u3002 \u3000\u3000\u5341\u4e8c\u4e2a\u6c34\u706b\u65e0\u60c5\u6740\u624b\uff0c\u5df2\u7ecf\u6b7b\u4e86\u516d\u4e2a\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5f88\u5fc3\u75bc\uff0c\u628a\u4f59\u4e0b\u7684\u516d\u4e2a\uff0c\u8c03\u4e86\u56de\u6765\uff0c\u85cf\u8eab\u5728\u82b1\u6811\u4e4b\u540e\uff0c\u76d1\u89c6\u7740\u94c1\u82b1\u9662\uff0c\u53ea\u8981\u6709\u4eba\u51fa\u5934\uff0c\u518d\u7528\u6697\u5668\u963b\u6321\u3002 \u3000\u3000\u795e\u5200\u5802\u7684\u767d\u8863\u5200\u624b\uff0c\u4e94\u6b7b\u4e00\u4f24\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u4e5f\u5f88\u5fc3\u75bc\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u770b\u4e0d\u5230\u5385\u4e2d\u5f62\u52bf\uff0c\u60f3\u50cf\u4e2d\u5341\u4e8c\u5973\u5c06\u4e5f\u6709\u4e86\u5f88\u5927\u7684\u4f24\u4ea1\u3002 \u3000\u3000\u5982\u82e5\u4ed6\u77e5\u9053\u5341\u4e8c\u5973\u5c06\uff0c\u53ea\u6298\u635f\u4e00\u4e2a\u4eba\u65f6\uff0c\u4ed6\u4f1a\u66f4\u75db\u82e6\u3002 \u3000\u3000\u53cc\u65b9\u6210\u4e86\u4e00\u79cd\u50f5\u6301\u4e4b\u5c40\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u4f4e\u58f0\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u54b1\u4eec\u73b0\u5728\u5e94\u8be5\u5982\u4f55\u5bf9\u4ed8\u8fd9\u4ef6\u4e8b?\u201d \u3000\u3000\u9ad8\u5929\u5065\u7684\u5185\u5fc3\u4e4b\u4e2d\uff0c\u4e5f\u6709\u4e9b\u832b\u7136\u7684\u611f\u89c9\uff0c\u60f3\u4e0d\u5230\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\uff0c\u7adf\u6709\u5982\u6b64\u9ad8\u660e\u7684\u6b66\u529f\u3002 \u3000\u3000\u5b89\u6392\u597d\u597d\u7684\u5341\u4e8c\u5973\u5c06\uff0c\u7adf\u53c8\u4f1a\u4e34\u9635\u5012\u6208\uff0c\u91cd\u56de\u5230\u9ad8\u5e7d\u5170\u7684\u638c\u63e1\u4e4b\u4e2d\u3002 \u3000\u3000\u5728\u6b66\u529f\u7684\u6210\u5c31\u4e0a\uff0c\u9ad8\u5929\u5065\u81ea\u8d1f\u8d85\u4eba\uff0c\u4f46\u6b64\u523b\uff0c\u4ed6\u5df2\u65e0\u4fe1\u5fc3\uff0c\u4e00\u5bf9\u4e00\u7684\u548c\u5434\u53cc\u5973\u5c55\u5f00\u4e00\u573a\u640f\u6740\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5728\u9ed8\u9ed8\u4e2d\u57f9\u517b\u51fa\u7684\u611f\u671b\uff0c\u4e5f\u51fa\u4e86\u9ad8\u5929\u5065\u7684\u610f\u6599\u3002 \u3000\u3000\u4f46\u6700\u53ef\u6015\u7684\uff0c\u8fd8\u662f\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\u6311\u660e\u4e86\u5f88\u591a\u7684\u5185\u60c5\u3002 \u3000\u3000\u4efb\u4f55\u5728\u573a\u7684\u4eba\uff0c\u90fd\u5df2\u611f\u89c9\u5230\uff0c\u9ad8\u5e7d\u5170\u4e0d\u662f\u9ad8\u5929\u5065\u7684\u5973\u513f\u3002 \u3000\u3000\u800c\u8fd9\u4ef6\u4e8b\uff0c\u5728\u7b2c\u4e00\u5821\u4e2d\u662f\u6700\u5927\u7684\u673a\u5bc6\uff0c\u9664\u4e86\u9ad8\u5929\u5065\u4e4b\u5916\uff0c\u518d\u65e0\u522b\u4eba\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u7eb5\u6a2a\u9ed1\u767d\u4e24\u9053\uff0c\u672c\u662f\u4e2a\u6781\u4f1a\u88c5\u4f5c\u7684\u4eba\uff0c\u4f46\u8fd9\u4e00\u6b21\uff0c\u4ed6\u7adf\u7136\u9690\u9690\u7684\u627f\u8ba4\u4e86\u9ad8\u5e7d\u5170\u7684\u6307\u8d23\u3002 \u3000\u3000\u4ed6\u8fd9\u5927\u534a\u751f\u4e2d\uff0c\u65e0\u5f80\u4e0d\u5229\uff0c\u540d\u58f0\u52a8\u6c5f\u6e56\uff0c\u4f46\u5374\u53c8\u4e00\u624b\u906e\u4f4f\u4e86\u5929\u4e0b\u4eba\u7684\u8033\u76ee\u3002 \u3000\u3000\u4ed6\u4ece\u6ca1\u6709\u50cf\u8fd9\u4e48\u61ca\u4e27\u8fc7\u3002 \u3000\u3000\u4e00\u4ee3\u67ad\u96c4\u7684\u4fe1\u5fc3\uff0c\u5f00\u59cb\u52a8\u6447\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u7a76\u7adf\u662f\u7ecf\u8fc7\u5927\u98ce\u5927\u6d6a\u7684\u4eba\uff0c\u5b9a\u4e00\u4e0b\u795e\uff0c \u56de\u5934\u5bf9\u96f7\u65e0\u5f71\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u4e0d\u7528\u518d\u786c\u62fc\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u4f4e\u58f0\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u53ef\u662f\u771f\u51c6\u5907\u653e\u706b\u70e7\u4e86\u8fd9\u94c1\u82b1\u9662\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u56de\u987e\u4e86\u96f7\u65e0\u5f71\u4e00\u773c\uff0c\u5ffd\u7136\u89c9\u7740\u8fd9\u4e2a\u4eba\uff0c\u4e0d\u53ef\u5341\u5206\u4fe1\u4efb\u3002 \u3000\u3000\u4f46\u4ed6\u795e\u6001\u4e4b\u4e0a\uff0c\u5374\u8868\u73b0\u5f97\u66f4\u4e3a\u67d4\u548c\uff0c\u9053\uff1a \u201c\u96f7\u5802\u4e3b\u7684\u610f\u601d\u5462?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c5e\u4e0b\u4e4b\u610f\uff0c\u5982\u975e\u5fc5\u8981\uff0c\u4e0d\u7528\u653e\u706b!\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u54e6!\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u9634\u9633\u5821\u4e2d\u7684\u4eba\uff0c\u8fd8\u5728\u7b2c\u4e00\u5821\u5916\uff0c\u5982\u82e5\u653e\u706b\u4e00\u70e7\uff0c\u5c82\u4e0d\u662f\u544a\u8bc9\u4ed6\u4eec\uff0c\u7b2c\u4e00\u5821\u5185\u53d1\u751f\u4ec0\u4e48\u91cd\u5927\u7684\u53d8\u5316\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u5fae\u5fae\u4e00\u7b11\u9053\uff1a \u201c\u5bf9!\u5bf9!\u4e5f\u8bb8\uff0c\u4ed6\u4eec\u5df2\u7ecf\u6df7\u5165\u4e86\u7b2c\u4e00\u5821\u4e2d\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\u5427!\u7b2c\u4e00\u5821\u4e2d\u5404\u5904\u9632\u536b\uff0c\u90fd\u672a\u4f20\u8b66\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u7b2c\u4e00\u5821\u592a\u8001\u8fc8\u4e86\uff0c\u8fd9\u4e9b\u5e74\u517b\u5c0a\u5904\u4f18\uff0c\u4ed6\u4eec\u53ef\u80fd\u90fd\u5df2\u6709\u4e86\u677e\u61c8\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6b64\u4e8b\u8fc7\u53bb\u4ee5\u540e\uff0c\u54b1\u4eec\u5e94\u8be5\u597d\u597d\u6574\u7406\u4e00\u4e0b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5e0c\u671b\uff0c\u6211\u4eec\u80fd\u591f\u8fc7\u4e86\u8fd9\u4e00\u5173\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u8fd9\u4e00\u5173\u5f88\u96be\u8fc7\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u5fae\u5fae\u4e00\u6014\uff0c\u6697\u751f\u8b66\u60d5\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4ee5\u54b1\u4eec\u7b2c\u4e00\u5821\u7684\u5b9e\u529b\uff0c\u6c5f\u6e56\u4e0a\u4efb\u4f55\u4e00\u4e2a\u95e8\u6d3e\uff0c\u4e5f\u96be\u4e0e\u54b1\u4eec\u6297\u62d2\uff0c \u53ea\u4e0d\u8fc7\uff0c\u8fd9\u4e48\u591a\u4e8b\u6765\uff0c\u4ed6\u4eec\u751f\u6d3b\u5f97\u592a\u5b89\u9038\u4e86\uff0c\u53ef\u80fd\u758f\u5ffd\u4e86\u5f88\u591a\u4e8b\u60c5\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\u8bf4\u5f97\u662f\uff0c\u5c5e\u4e0b\u8981\u4ed6\u4eec\u51c6\u5907\u6850\u6cb9\u3001\u6728\u67f4\u53bb\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u5c5e\u4e0b\u60f3\u4e86\u4e00\u4e0b\uff0c\u5821\u4e3b\u8bf4\u5f97\u5bf9\uff0c\u5fa1\u5916\u5fc5\u5148\u6518\u5185\uff0c\u5148\u628a\u5434\u53cc\u5973\u548c\u9ad8\u5e7d\u5170\u7ed9\u6d88\u706d\u4e86\u518d\u8bf4\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u6447\u6447\u5934\uff0c\u9053\uff1a \u201c\u6682\u65f6\u4e0d\u7528\u4e86\uff0c\u5929\u8272\u5df2\u665a\uff0c\u4e00\u628a\u706b\u53ef\u80fd\u903c\u5f97\u5979\u4eec\u5168\u529b\u7a81\u56f4\u800c\u9003\uff0c\u9ad8\u5e7d\u5170\u3001\u5434\u53cc\u5973\u5bf9\u8fd9\u91cc\u5341\u5206\u719f\u6089\uff0c\u4e00\u65e6\u88ab\u5979\u4eec\u5154\u8131\u800c\u8d70\uff0c\u90a3\u5c31\u5f88\u96be\u518d\u628a\u5979\u4eec\u6293\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u518d\u53bb\u8c03\u96c6\u4e00\u6279\u4eba\u624b\u6765\uff0c\u7d27\u5bc6\u56f4\u56f0\uff0c\u7b49\u5929\u4eae\u4e4b\u540e\uff0c\u518d\u7528\u706b\u653b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\u53bb\u4f11\u606f\u4e00\u4e0b\u5427!\u6b64\u5730\u7531\u5c5e\u4e0b\u76d1\u7763\uff0c\u60f3\u6765\u4e0d\u4f1a\u51fa\u4ec0\u4e48\u4e8b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u6628\u591c\u548c\u7389\u7fe0\u7f20\u7ef5\u901a\u5bb5\uff0c\u5b9e\u5728\u4e5f\u9700\u8981\u4f11\u606f\u4e00\u4e0b\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u5427!\u591a\u52a0\u4eba\u624b\uff0c\u4e0d\u8bb8\u9003\u8d70\u4e00\u4eba\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\u653e\u5fc3\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5fc3\u4e2d\u660e\u767d\uff0c\u6b64\u523b\u6709\u4e0d\u5f97\u4efb\u4f55\u4e00\u70b9\u5927\u610f\uff0c\u90a3\u4f1a\u4f7f\u9ad8\u5929\u5065\u5fc3\u4e2d\u751f\u7591\u3002\u6240\u4ee5\uff0c\u53c8\u4ece\u795e\u5200\u5802\u8c03\u51fa\u4e86\u4e00\u6279\u7cbe\u9510\u7684\u5200\u624b\u3002 \u3000\u3000\u9ad8\u5929\u5065\u4e5f\u679c\u7136\u4f20\u4e0b\u4e86\u4ee4\u8c15\uff0c\u516b\u4e2a\u8fd1\u536b\u9ad8\u624b\uff0c\u5e26\u4e86\u56db\u53ea\u82b1\u8c79\u8d76\u6765\u52a9\u6218\u3002 \u3000\u3000\u82b1\u8c79\u7075\u654f\u8fc7\u5165\u7684\u55c5\u89c9\uff0c\u5bf9\u9ad8\u5e7d\u5170\u7b49\u7a81\u56f4\u7684\u6253\u7b97\uff0c\u5b9e\u5728\u662f\u4e00\u4e2a\u5f88\u5927\u7684\u5a01\u80c1\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u96f7\u65e0\u5f71\u4e5f\u806a\u660e\u4e86\uff0c\u4e0d\u6c42\u6709\u529f\uff0c\u5148\u6c42\u65e0\u8fc7\uff0c\u4eba\u624b\u90fd\u5b89\u6392\u5728\u8ddd\u79bb\u94c1\u82b1\u9662\u76f8\u5f53\u7684\u8ddd\u79bb\uff0c\u4e0d\u518d\u5411\u5385\u4e2d\u653b\u88ad\u3002 \u3000\u3000\u5982\u4f55\u5bf9\u4ed8\u94c1\u82b1\u9662\uff0c\u7559\u5f85\u660e\u5929\u9ad8\u5929\u5065\u6765\u51b3\u5b9a\u3002 \u3000\u3000\u56e0\u591c\u4ea4\u4e8c\u66f4\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u3001\u5434\u53cc\u5973\u3001\u4f55\u5bd2\u8863\uff0c\u5df2\u51b3\u5b9a\u4e86\u51b2\u7834\u5305\u56f4\u800c\u51fa\u3002 \u3000\u3000\u56e0\u4e0d\u89c1\u4efb\u4f55\u7684\u653b\u88ad\u52a8\u9759\uff0c\u53cd\u800c\u4f7f\u4e09\u4eba\u72b9\u8c6b\u8d77\u6765\u3002 \u3000\u3000\u5434\u53cc\u5973\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u6211\u4eec\u8be5\u5982\u4f55\u884c\u52a8\uff0c\u4f60\u8be5\u6709\u4e2a\u6253\u7b97\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u672c\u6709\u7a81\u56f4\u7684\u6253\u7b97\uff0c\u4f46\u73b0\u5728\uff0c\u6211\u60f3\u6539\u53d8\u4e3b\u610f\u4e86\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u592a\u9759\u4e86\uff0c\u9759\u5f97\u53ef\u6015\uff0c\u6211\u60f3\uff0c\u4ed6\u4eec\u5b9a\u6709\u4ec0\u4e48\u5b89\u6392\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u5148\u51fa\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e0d\u884c\uff0c\u4f60\u51fa\u53bb\uff0c\u4e0d\u5982\u6211\u51fa\u53bb\uff0c\u8fd9\u91cc\u4f60\u4e00\u70b9\u4e5f\u4e0d\u719f\u3002\u201d \u7ea2\u82b1\u4e00\u62c9\u7eff\u53f6\uff0c\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u522b\u8fd9\u6837\u53eb\u6211\uff0c\u6211\u5df2\u7ecf\u4e0d\u662f\u4e8c\u5c0f\u59d0\u3002\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u662f\uff0c\u53eb\u60ef\u4e86\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5f88\u96be\u6539\u53e3\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a \u201c\u6211\u548c\u7eff\u53f6\uff0c\u5bf9\u6b64\u5730\u6700\u4e3a\u719f\u6089\uff0c\u7531\u6211\u4eec\u4e24\u4e2a\u4eba\uff0c\u51fa\u53bb\u77a7\u77a7\u3002\u201d \u9ad8\u5e7d\u5170\u6c89\u541f\u4e86\u2014\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u8fd8\u662f\u7531\u6211\u51fa\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5e7d\u5170\uff0c \u76ee\u4e0b\u60c5\u5f62\uff0c \u4f60\u662f\u4e3b\u6301\u5168\u5c40\u7684\u4eba\u7269\uff0c\u600e\u53ef\u6d89\u9669\uff0c\u8fd8\u662f\u7531\u6211\u53bb\u770b\u770b\u5427\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5c0f\u59b9\u5728\u8fd9\u91cc\uff0c\u4f20\u827a\u6570\u5e74\u4e4b\u4e45\uff0c\u8fd9\u9662\u4e2d\u7684\u4e00\u8349\u4e00\u672c\uff0c\u6211\u90fd\u5341\u5206\u719f\u6089\uff0c\u4ed6\u4eec\u7eb5\u6709\u57cb\u4f0f\uff0c \u4e5f\u65e0\u6cd5\u9a97\u5f97\u8fc7\u6211\uff0c\u59ca\u59ca\u5c31\u6d89\u9669\u5f88\u5927\u4e86\u3002\u201d \u53ea\u542c\u4e00\u4e2a\u4f4e\u6c89\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u8c01\u90fd\u4e0d\u80fd\u53bb\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u3002\u201d \u3000\u3000\u201c\u6211!\u8bf8\u4f4d\u522b\u7528\u6697\u9752\u5b50\u62db\u547c\u6211\uff0c\u5728\u4e0b\u8fdb\u6765\u4e86\u3002\u201d \u3000\u3000\u4eba\u5f71\u4e00\u95ea\uff0c\u8fdb\u6765\u4e86\u4e00\u4eba\u3002 \u3000\u3000\u90a3\u4eba\u7a7f\u7740\u767d\u8863\uff0c\u624b\u6267\u957f\u5200\uff0c\u5206\u660e\u662f\u795e\u5200\u5802\u4e2d\u7684\u5200\u624b\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e00\u626c\u624b\uff0c\u6b63\u5f85\u53d1\u51fa\u5929\u9b54\u9488\uff0c \u4f55\u5bd2\u8863\u5df2\u542c\u51fa\u4e86\u6765\u4eba\u662f\u8c01\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u662f\u5821\u4e3b\u3002\u201d \u201c\u533a\u533a\u662f\u9ec4\u7075\u3002\u201d \u3000\u3000\u201c\u54e6!\u4f60\u4eec\u662f\u600e\u4e48\u8fdb\u6765\u7684!\u201d\u5434\u53cc\u5973\u5bf9\u51e0\u4e4e\u53d1\u51fa\u5929\u9b54\u9488\u4e00\u4e8b\uff0c\u5341\u5206\u62b1\u6b49\u3002 \u3000\u3000\u9ec4\u7075\u7b11\u9053\uff1a\u201c\u4e94\u5927\u5251\u4f7f\uff0c\u7559\u4e00\u4e2a\u5728\u7b2c\u4e00\u5821\uff0c\u53e6\u5916\u56db\u4f4d\uff0c\u5982\u4f55\u653e\u5f97\u4e0b\u5fc3\uff0c\u6240\u4ee5\u5168\u90fd\u6765\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u600e\u80fd\u51b2\u8fc7\u2018\u6c34\u706b\u65e0\u60c5\u2019\u7684\u9632\u5b88\u5462?\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e03\u7f6e\u8fd9\u91cc\u7684\u4eba\uff0c\u90fd\u5df2\u88ab\u5236\u4f4f\uff0c\u516d\u7b52\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u4e5f\u843d\u5728\u4e86\u6211\u4eec\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u96f7\u65e0\u5f71\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u96f7\u65e0\u5f71\u7ed9\u6211\u4eec\u7559\u4e0b\u4e86\u4e00\u4e2a\u5f88\u597d\u7684\u673a\u4f1a\uff0c\u4ed6\u56de\u795e\u5200\u5802\u8c03\u96c6\u4eba\u624b\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u76ee\u4e0b\u60c5\u52bf\uff0c\u65e0\u6cd5\u591a\u8c08\uff0c\u54b1\u4eec\u5148\u79bb\u5f00\u6b64\u5730\u5982\u4f55\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u5728\u4e0b\u5df2\u7ecf\u60f3\u8fc7\u4e86\uff0c\u5728\u8fd9\u91cc\u548c\u7b2c\u4e00\u5821\u51b3\u6218\uff0c\u6bd4\u5176\u4ed6\u7684\u5730\u65b9\u66f4\u597d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u53ea\u6709\u4f60\u9634\u9633\u5821\u4e2d\u7684\u4e94\u5927\u5251\u4f7f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd8\u6709\u51e0\u4f4d\u6b66\u6797\u524d\u8f88\uff0c\u4e5f\u90fd\u8d76\u6765\u4e86\u6b64\u5730\uff0c\u6574\u4e2a\u7684\u6c5f\u6e56\uff0c\u90fd\u5df2\u7ecf\u89c9\u9192\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u795e\u60c5\u8083\u7136\u5730\u8bf4\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\uff0c\u73b0\u5728\uff0c\u5e03\u5b88\u5728\u94c1\u82b1\u9662\u7684\u4eba\u624b\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u5df2\u7ecf\u5b8c\u5168\u6362\u6210\u4e86\u6211\u4eec\u7684\u4eba\uff0c\u800c\u4e14\uff0c\u90fd\u7a7f\u7740\u795e\u5200\u5802\u767d\u8863\u5200\u624b\u7684\u8863\u670d\uff0c\u51e0\u7b52\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u4e5f\u843d\u5728\u4e86\u6211\u4eec\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u4eec\u8fdb\u6765\u4e86\u591a\u5c11\u4eba?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u6211\u548c\u56db\u5927\u5251\u4f7f\uff0c\u52a0\u4e0a\u4e86\u516d\u4e2a\u5200\u624b\uff0c\u4e00\u5171\u5341\u4e00\u4e2a\u4eba\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u662f\u8bf4\uff0c\u73b0\u5728\u5e03\u5b88\u5728\u94c1\u82b1\u9662\u56db\u5468\u7684\u4eba\uff0c\u90fd\u662f\u4f60\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5509!\u59d1\u5a18\u5bf9\u4ee4\u5c0a\uff0c\u6709\u4ec0\u4e48\u770b\u6cd5\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u9ec4\u5821\u4e3b\u53ef\u4ee5\u653e\u5fc3\uff0c\u9ad8\u5929\u5065\u5df2\u7ecf\u4e0d\u662f\u6211\u7684\u7236\u4eb2\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u6709\u8fd9\u7b49\u4e8b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u7528\u987e\u8651\u6211\u4ec0\u4e48\uff0c\u6211\u548c\u9ad8\u5821\u4e3b\u4e4b\u95f4\u7684\u4e00\u70b9\u60c5\u610f\u4e5f\u65e9\u5df2\u7ecf\u6069\u5c3d\u4e49\u7edd\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u5c31\u6709\u70b9\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8d31\u59be\u8fd8\u62c5\u5fc3\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u62c5\u5fc3\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u62c5\u5fc3\uff0c\u4f60\u4eec\u5168\u90fd\u8fdb\u5165\u4e86\u7b2c\u4e00\u5821\u6765\uff0c\u6709\u6ca1\u6709\u628a\u63e1\u518d\u79bb\u5f00\u6b64\u5730\u3002\u201d \u9ec4\u7075\u9053\uff1a \u201c\u8fd9\u4e00\u70b9\uff0c\u59d1\u5a18\u53ef\u4ee5\u653e\u5fc3\u4e86\uff0c\u6211\u4eec\u8fdb\u6765\u4e4b\u540e\uff0c\u5c31\u4e0d\u4f1a\u518d\u62c5\u5fc3\u751f\u6b7b\u7684\u4e8b\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u751f\u6b7b\u4e4b\u4e8b\uff0c\u56fa\u7136\u53ef\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u4e0d\u8fc7\uff0c\u6210\u8d25\uff0c\u5374\u80fd\u4e0d\u7ba1\u3002\u201d \u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5728\u4e0b\u6700\u5927\u7684\u5fc3\u613f\uff0c\u5c31\u662f\u627e\u51fa\u771f\u8c61\uff0c\u5e0c\u671b\u80fd\u627e\u51fa\u9ad8\u5929\u5065\u5728\u6c5f\u6e56\u4e0a\u6240\u505a\u7684\u6076\u8ff9\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u627e\u5230\u4e86\u6ca1\u6709?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u627e\u5230\u4e86\uff0c\u800c\u4e14\uff0c\u6c5f\u6e56\u4e0a\u5f88\u591a\u7684\u95e8\u6d3e\uff0c \u5df2\u7ecf\u627f\u8ba4\u4e86\u8fd9\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5c31\u7b97\u627f\u8ba4\u4e86\uff0c\u4f46\u963b\u529b\u8fd8\u662f\u5f88\u5927\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u6211\u77e5\u9053\uff0c\u7b2c\u4e00\u5821\u7684\u5bf9\u8c61\u662f\u6574\u4e2a\u7684\u6c5f\u6e56\u540c\u9053\uff0c\u6574\u4e2a\u6c5f\u6e56\u5b9e\u529b\uff0c\u4ed6\u4eec\u90fd\u5df2\u7ecf\u7b97\u8ba1\u4e0a\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4f60\u4eec\u65e2\u6709\u4e86\u51c6\u5907\uff0c\u6211\u4e5f\u4e0d\u591a\u8bf4\u4e86\uff0c\u73b0\u5728\uff0c\u5821\u4e3b\u51c6\u5907\u4f5c\u5982\u4f55\u6253\u7b97?\u201d \u3000\u3000\u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u6211\u4eec\u6df7\u5165\u7b2c\u4e00\u5821\u6765\uff0c\u5df2\u7ecf\u548c\u4ed6\u4eec\u4ea4\u4e86\u4e24\u6b21\u624b\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u55ef!\u53d1\u89c9\u4ed6\u4eec\u5982\u4f55?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5f88\u987d\u5f3a\uff0c\u4e0d\u8fc7\uff0c\u4e24\u6b21\uff0c\u6211\u4eec\u90fd\u7edd\u5bf9\u80dc\u5229\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7edd\u5bf9\u80dc\u5229\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u7edd\u5bf9\u80dc\u5229\u7684\u610f\u601d\u5c31\u662f\uff0c\u4ed6\u4eec\u5168\u90fd\u4f24\u5728\u4e86\u6211\u4eec\u7684\u624b\u4e2d\uff0c\u624d\u80fd\u4fdd\u6301\u4e86\u6d88\u606f\u6ca1\u6709\u5916\u6cc4\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u6709\u4f24\u4ea1\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6709!\u6211\u4eec\u968f\u6765\u7684\u6b66\u58eb\uff0c\u6b7b\u4e86\u4e09\u4e2a\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\u7684\u88ad\u51fb\u5f88\u6210\u529f\uff0c\u6211\u4eec\u6ca1\u6709\u542c\u5230\u4e00\u70b9\u58f0\u606f\uff0c\u4f60\u4eec\u5c31\u89e3\u51b3\u4e86\u90a3\u4e9b\u5200\u624b\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u8fd9\u4e00\u6b21\uff0c\u5168\u662f\u6697\u88ad\uff0c\u4e00\u4e3e\u6210\u529f\uff0c\u6211\u4eec\u7a7f\u4e86\u4ed6\u4eec\u540c\u6837\u7684\u8863\u670d\uff0c\u540c\u65f6\u627e\u4e0a\u4e86\u4ed6\u4eec\uff0c\u4ed6\u4eec\u5b8c\u5168\u6ca1\u6709\u9632\u5907\u3002\u8bf4\u8d77\u6765\uff0c\u8fd9\u624b\u6bb5\u4e5f\u6709\u4e9b\u4e0d\u592a\u5149\u660e\uff0c\u4e0d\u8fc7\uff0c\u4e8b\u60c5\u7d27\u6025\uff0c\u4e5f\u53ea\u597d\u4ece\u6743\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5175\u4e0d\u538c\u8bc8\uff0c\u8d8a\u8bc8\u8d8a\u597d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u5728\u7b2c\u4e00\u5821\u5c45\u4f4f\u6709\u5e74\uff0c\u5bf9\u6b64\u5730\u7684\u4eba\u4eba\u4e8b\u4e8b\uff0c\u90fd\u5f88\u4e86\u89e3\uff0c\u4e0d\u77e5\u4f55\u4ee5\u6559\u6211?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e0d\u6562\u5f53\uff0c\u8d31\u59be\u89c9\u7740\uff0c\u5c31\u54b1\u4eec\u8fd9\u4e9b\u4eba\u800c\u8a00\uff0c\u548c\u7b2c\u4e00\u5821\u6b63\u9762\u51b2\u7a81\uff0c\u5b9e\u529b\u672a\u514d\u8fc7\u5f31\u4e00\u4e9b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u4e5f\u53ea\u597d\u5c3d\u529b\u800c\u4e3a\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u9664\u4e86\u6211\u4eec\u7684\u4eba\u624b\u4e4b\u5916\uff0c\u8fd8\u6709\u4e9b\u4ec0\u4e48\u4eba\u8d76\u6765\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u6211\u60f3\uff0c\u4f1a\u6709\u5f88\u591a\u4eba\u6765\uff0c\u4f46\u5374\u672a\u5fc5\u8d76\u5f97\u4e0a\u6211\u4eec\u548c\u7b2c\u4e00\u5821\u8fd9\u573a\u51b3\u6218\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u4eec\u7684\u5b9e\u529b\uff0c\u662f\u5355\u8584\u4e86\u4e00\u4e9b\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u4e5f\u8bb8\u6211\u4eec\u7684\u4eba\u624b\uff0c\u548c\u7b2c\u4e00\u5821\u76f8\u5dee\u7684\u5f88\u591a\uff0c\u4e0d\u8fc7\uff0c\u6211\u76f8\u4fe1\uff0c\u6211\u4eec\u7684\u6218\u6597\u610f\u5fd7\uff0c\u5b9a\u7136\u5341\u5206\u575a\u5f3a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bf9!\u53ea\u8981\u6211\u4eec\u6709\u4fe1\u5fc3\uff0c\u8fd9\u4e00\u6218\uff0c\u5c31\u53ef\u4ee5\u653e\u624b\u4e00\u62fc\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u91cd\u8981\u7684\u662f\u80dc\u8d1f\u7684\u673a\u4f1a?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4f55\u9ad8\u89c1\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u80fd\u4f4e\u4f30\u7b2c\u4e00\u5821\u7684\u529b\u91cf\uff0c\u6211\u4eec\u62fc\u547d\u4e00\u6218\uff0c\u4f46\u5374\u672a\u5fc5\u80fd\u8d62\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u6211\u4e0d\u60f3\u628a\u53ef\u80fd\u7684\u529b\u91cf\u90fd\u7b97\u4e0a\uff0c\u6211\u4eec\u80fd\u638c\u63e1\u7684\uff0c\u624d\u662f\u771f\u6b63\u7684\u529b\u91cf\uff0c\u4ed6\u4eec\u8d76\u6765\u4e86\uff0c\u6211\u4eec\u4f1a\u591a\u4e00\u4efd\u80dc\u5229\u7684\u673a\u4f1a\uff0c\u5982\u662f\u4ed6\u4eec\u4e0d\u80af\u8d76\u6765\uff0c\u6211\u4eec\u4e5f\u4e0d\u4f1a\u5931\u671b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u9ec4\u5821\u4e3b\u8bf4\u7684\u5bf9\uff0c\u5927\u7ea6\u5821\u4e3b\u5fc3\u4e2d\uff0c \u5df2\u7ecf\u6709\u4e86\u5bf9\u6297\u7b2c\u4e00\u5821\u7684\u6253\u7b97\uff0c\u8981\u6211\u4eec\u505a\u4e9b\u4ec0\u4e48?\u53ea\u7ba1\u5429\u5490\u5c31\u662f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5728\u4e0b\u7684\u6784\u60f3\u4e4b\u4e2d\uff0c\u786e\u6709\u4e00\u4e2a\u548c\u7b2c\u4e00\u5821\u51b3\u6218\u7684\u6253\u7b97\uff0c\u4e0d\u8fc7\uff0c\u8fd8\u5f97\u5411\u4e24\u4f4d\u8bf7\u6559\u4e00\u4e9b\u4e8b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5821\u4e3b\u5429\u5490\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u9ad8\u5929\u5065\u7684\u5200\u6cd5\uff0c \u5df2\u5230\u4e86\u7089\u706b\u7eaf\u9752\u7684\u5883\u754c\uff0c \u81ea\u662f\u7b2c\u4e00\u53f7\u5f3a\u654c\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u4e0d\u77e5\u9053\u8fd8\u6709\u4ec0\u4e48\u4eba\u8f83\u4e3a\u53ef\u6015\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u8fd9\u4e2a\u4eba\u7ec3\u7684\u662f\u5916\u95e8\u529f\u529b\uff0c\u4f46\u4ed6\u7684\u6210\u5c31\u4e4b\u9ad8\uff0c\u653e\u773c\u5f53\u4eca\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u4e0d\u4f5c\u7b2c\u4e8c\u4eba\u60f3\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u795e\u5200\u5802\u7684\u5200\u624b\uff0c\u4e5f\u5341\u5206\u53ef\u6015\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6709\u6ca1\u6709\u7279\u522b\u6770\u51fa\uff0c\u9700\u8981\u6211\u4eec\u5168\u795e\u62db\u547c\u7684\u4eba?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u795e\u5200\u5802\u4e2d\uff0c\u9664\u96f7\u65e0\u5f71\u4e4b\u5916\uff0c\u8fd8\u6709\u5f88\u591a\u4eba\uff0c\u89c4\u6a21\u76f8\u5f53\u7684\u5e9e\u5927\uff0c\u6211\u60f3\u4f1a\u6709\u4e0d\u5c11\u9ad8\u624b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u9664\u4e86\u2018\u6c34\u706b\u65e0\u60c5\u2019\u4e4b\u5916\uff0c\u7b2c\u4e00\u5821\u8fd8\u6709\u4ec0\u4e48\u53ef\u6015\u7684\u6697\u5668\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6ca1\u6709\u4e86\uff0c\u8d31\u59be\u62c5\u5fc3\u7684\uff0c\u4e5f\u5c31\u662f\u2018\u6c34\u706b\u65e0\u60c5\u2019\u8fd9\u4e00\u70b9\u5b50\uff0c\u542c\u8bf4\uff0c\u7b2c\u4e00\u5821\u8bad\u7ec3\u4e86\u4e0d\u5c11\u8fd9\u6837\u7684\u4eba\u624b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u6709\u4e00\u4e2a\u8be6\u7ec6\u7684\u6570\u5b57?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u542c\u8bf4\u6709\u516b\u5341\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u516b\u5341\u4e00\u4e2a\u4eba\uff0c\u521a\u624d\uff0c\u4ed6\u4eec\u6298\u635f\u4e86\u5341\u4e8c\u4e2a\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a \u201c\u8fd8\u6709\u4e94\u5341\u4e5d\u4e2a\uff0c\u4e0d\u8fc7\uff0c\u6309\u6211\u6240\u77e5\uff0c\u9ad8\u5929\u5065\u5904\u51b3\u4e86\u4e09\u4e2a\uff0c\u8fd8\u6709\u4e94\u5341\u516d\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\uff0c\u53ef\u77e5\u9053\u4ed6\u4eec\u7684\u4f4f\u5904?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u73b0\u5728\u80fd\u4e0d\u80fd\u5e26\u6211\u4eec\u53bb?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5077\u88ad\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5982\u4f55\u5077\u88ad\u6cd5?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6c34\u706b\u65e0\u60c5\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5f88\u597d\u7684\u4e3b\u610f\uff0c\u4ec0\u4e48\u4eba\u8ddf\u6211\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211!\u516d\u4e2a\u6562\u6b7b\u7684\u5251\u624b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1b\u201c\u4e0d!\u4f60\u662f\u4e00\u519b\u4e3b\u5e05\uff0c\u600e\u53ef\u8f7b\u6613\u6d89\u9669\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u9053\uff1b\u201c\u6211\u5df2\u80f8\u6709\u6210\u7af9\uff0c\u4f60\u4e0d\u7528\u548c\u6211\u4e89\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u7528\u4e0d\u7740\u4e24\u4f4d\u53bb\uff0c\u4ea4\u7ed9\u6211\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u4ee5\u53bb\uff0c\u4e0d\u8fc7\uff0c\u4e00\u5b9a\u8981\u5728\u4e0b\u9886\u961f\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u9ad8\u5e7d\u5170\uff0c\u63a5\u9053\uff1a\u201c\u6b64\u5730\u7684\u4e8b\uff0c\u5c31\u4ea4\u7ed9\u59d1\u5a18\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u4e00\u4e2a\u4e0d\u60c5\u4e4b\u6c42\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8bf4\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff0c\u201c\u6211\u548c\u5434\u53cc\u5973\u6389\u8fc7\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1b\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u59d0\u59d0\u7684\u6b66\u529f\u6bd4\u6211\u9ad8\uff0c\u4f46\u6211\u4e03\u8272\u5251\u7684\u6740\u4eba\u624b\u6cd5\u5374\u6bd4\u5979\u66f4\u4e3a\u6709\u6548\uff0c\u88ad\u51fb\u2018\u6c34\u706b\u65e0\u60c5\u2019\u6740\u624b\uff0c\u5c31\u8981\u4e00\u4e3e\u640f\u6740\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d!\u6211\u6709\u5929\u9b54\u9488\u4e3a\u52a9\uff0c\u6740\u4eba\u901f\u5ea6\uff0c\u7edd\u4e0d\u5728\u4f60\u4e4b\u4e0b\uff0c\u4f55\u51b5\uff0c\u5821\u4e3b\u79bb\u53bb\uff0c\u6b64\u5730\u9760\u4f60\u6491\u6301\u5927\u5c40\u3002\u201d \u3000\u3000\u9ec4\u7075\u56de\u987e\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5341\u4e8c\u5973\u5c06\u51fa\u81ea\u4f60\u4e00\u624b\u8bad\u7ec3\uff0c\u4f60\u7559\u6b64\uff0c\u6307\u6325\u8fd0\u7528\u4e0a\uff0c\u4e5f\u65b9\u4fbf\u4e00\u4e9b\u2026\u2026\u201d \u3000\u3000\u4ef0\u9996\u53f9\u606f\u4e00\u58f0\uff0c\u63a5\u9053\uff1a \u201c\u8bf8\u4f4d\uff0c\u5728\u7b2c\u4e00\u5821\u4e2d\u4efb\u4f55\u4e00\u5904\u5730\u65b9\uff0c\u90fd\u5145\u6ee1\u7740\u51f6\u9669\uff0c\u9ad8\u59d1\u5a18\u4e0d\u7528\u4e89\u4e86\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u4f55\u5bd2\u8863\uff0c\u63a5\u9053\uff1a\u201c\u8fd9\u5730\u65b9\uff0c\u5c31\u4ea4\u7ed9\u4f60\u4eec\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u56db\u4f4d\u5251\u4f7f\uff0c\u4f1a\u4e0d\u4f1a\u7559\u5728\u8fd9\u91cc?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e07\u80dc\uff0c\u534e\u5999\u771f\uff0c\u7559\u4e0b\u6765\uff0c\u4ed6\u4eec\u7edf\u7740\u516d\u4e2a\u5200\u624b\uff0c\u7559\u5728\u8fd9\u91cc\u5e2e\u52a9\u4f60\u4eec\u3002\u201d \u3000\u3000\u56de\u987e\u4e86\u5434\u53cc\u5973\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6709\u52b3\u59d1\u5a18\u5e26\u8def\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u56de\u7738\u4e00\u7b11\uff0c\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u5434\u53cc\u5973\u8fdc\u53bb\u7684\u80cc\u5f71\uff0c\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a \u201c\u5bd2\u8863\uff0c\u54b1\u4eec\u4e5f\u53bb\u5b89\u6392\u4e00\u4e0b\u5427\uff1f\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u66ff\u4f60\u5f15\u89c1\u4e00\u4e0b\u4e07\u80dc\u3001\u534e\u5999\u771f\u4e24\u4f4d\u5251\u4f7f\u3002\u201d \u3000\u3000\u5927\u5385\u5916\u4f20\u8fdb\u6765\u534e\u5999\u771f\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u96f7\u65e0\u5f71\u7684\u4eba\u624b\uff0c\u53ef\u80fd\u5c31\u8981\u5230\u4e86\uff0c\u80fd\u6d3b\u8fc7\u8fd9\u573a\u6d69\u52ab\uff0c\u518d\u5411\u4e24\u4f4d\u9053\u8d3a\uff0c\u5403\u4f60\u4e00\u676f\u6c34\u9152\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u534e\u5927\u59d0\uff0c\u6211\u4eec\u975e\u5f97\u89c1\u9762\u4e0d\u53ef\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5341\u4e8c\u5973\u5c06\uff0c\u8981\u548c\u4f60\u4eec\u7684\u4eba\u624b\u914d\u5408\uff0c\u603b\u8981\u6709\u4e00\u4e2a\u534f\u5546\u624d\u884c\u3002\u201d \u534e\u5999\u771f\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0e\u5c0f\u59b9\u6765\u4e86\u3002\u201d \u3000\u3000\u4eba\u5f71\u4e00\u95ea\u3002 \u3000\u3000\u4e00\u4e2a\u767d\u8863\u5200\u624b\uff0c\u98de\u5165\u5385\u4e2d\u3002 \u3000\u3000\u5979\u5168\u8eab\u90fd\u63a9\u5728\u4e00\u4ef6\u767d\u8272\u7684\u957f\u888d\u4e4b\u4e2d\uff0c\u5982\u975e\u65e9\u5df3\u77e5\u9053\uff0c\u7edd\u96be\u5206\u8fa8\u51fa\uff0c\u5979\u662f\u534e\u5999\u771f\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4ecd\u8fd8\u6709\u4e9b\u4e0d\u653e\u5fc3\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u5c31\u662f\u534e\u5927\u59d0\u3002\u201d \u3000\u3000\u201c\u662f\u6211\u3002\u201d\u6bd5\u5999\u771f\u63ed\u5f00\u4e86\u63a9\u906e\u4f4f\u8138\u513f\u7684\u767d\u7eb1\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u534e\u5927\u59d0\uff0c\u51c6\u5907\u5982\u4f55\u8fce\u51fb\u654c\u4eba\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4ee5\u640f\u6740\u4e3a\u4e3b\uff0c\u6211\u4eec\u9690\u5728\u6697\u5904\uff0c\u5411\u654c\u65bd\u88ad\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6b64\u7b56\u501f\u591c\u8272\u63a9\u62a4\u53ef\u884c\uff0c\u5982\u82e5\u5728\u5927\u767d\u5929\uff0e\u53ea\u6015\u5f88\u96be\u5982\u613f\u3002\u201d \u534e\u5999\u771f\u9053\uff1a\u201c\u96f7\u65e0\u5f71\u4f1a\u4e0d\u4f1a\u4eca\u665a\u6765\u88ad\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6765\u7684\u673a\u4f1a\u4e0d\u5927\uff0c\u4ed6\u53d7\u4e86\u5f88\u5927\u7684\u632b\u8d25\uff0c\u4e0d\u4f1a\u518d\u5192\u9669\u4e86\u3002\u201d \u534e\u5999\u771f\u9053\uff1a\u201c\u59d1\u5a18\u7684\u610f\u601d\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5c0f\u59b9\u51c6\u5907\u4ee5\u5341\u2014\u4f4d\u5973\u5251\u624b\uff0c\u914d\u5408\u5927\u59d0\u6240\u6709\u7684\u5c5e\u4e0b\uff0c\u53cc\u65b9\u81ea\u6210\u5355\u5143\uff0c\u76f8\u4e92\u4e3a\u7528\uff0c\u5f7c\u6b64\u5207\u5b9e\u652f\u63f4\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4f46\u613f\u5821\u4e3b\u7684\u6697\u88ad\u6210\u529f\uff0c\u6211\u4eec\u5df2\u6709\u4e86\u516b\u7b52\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u4e13\u4ee5\u7528\u4f5c\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u53ea\u8981\u4e0d\u7ed9\u4ed6\u4eec\u5728\u9002\u5f53\u8ddd\u79bb\u5185\u65bd\u653e\u7684\u673a\u4f1a\uff0c\u53ef\u51cf\u5c11\u5f88\u5927\u7684\u5a01\u529b\u3002\u201d \u3000\u3000\u4e24\u4eba\u4f4e\u58f0\u8ba1\u8bae\u5b50\u5bf9\u654c\u4e4b\u7b56\u3002 \u3000\u3000\u8fd9\u4e00\u8c08\uff0c\u534e\u5999\u771f\u4e0d\u80fd\u4e0d\u5bf9\u9ad8\u5e7d\u5170\u4f69\u670d\u4e86\u3002 \u3000\u3000\u8fd9\u4f4d\u59d1\u5a18\u7684\u624d\u6167\u4e4b\u9ad8\uff0c\u7edd\u4e0d\u5728\u9ec4\u7075\u4e4b\u4e0b\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4e00\u7c92\u7802\u77f3\uff0c\u98de\u5165\u5927\u5385\u3002 \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u654c\u4eba\u6765\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6025\u6025\u8bf4\u9053\uff1a \u201c\u7ea2\u82b1\u3001\u7eff\u53f6\uff0c\u8ddf\u6211\u51fa\u53bb\uff0c\u5bd2\u8863\uff0c\u7559\u51e0\u4e2a\u5973\u5c06\u7ed9\u4f60\uff0c\u8981\u597d\u597d\u7684\u6307\u5bfc\u5979\u4eec\u3002\u201d \u3000\u3000\u8bdd\u8bf4\u5b8c\uff0c\u4eba\u5df2\u51fa\u4e86\u5927\u5385\u3002 \u3000\u3000\u7ea2\u82b1\u3001\u7eff\u53f6\uff0c\u5982\u5f71\u968f\u5f62\u822c\uff0c\u8ffd\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u60f3\u8bf4\u8bdd\uff0c\u4f46\u9ad8\u5e7d\u5170\u5374\u6ca1\u6709\u7ed9\u4ed6\u8bf4\u8bdd\u7684\u673a\u4f1a\u3002 \u3000\u3000\u7559\u7ed9\u4e86\u4ed6\u4e00\u4e2a\u96be\u9898\u3002 \u3000\u3000\u5e78\u597d\uff0c\u7ea2\u3001\u7eff\u4e24\u7ec4\u7684\u5973\u5c06\u4eec\uff0c\u5bf9\u4ed6\u7684\u5370\u8c61\u8fd8\u7b97\u4e0d\u9519\u3002\u6bcf\u4e2a\u4eba\u90fd\u671b\u7740\u4ed6\u5fae\u7b11\u3002 \u3000\u3000\u90a3\u662f\u4e00\u79cd\u53cb\u5584\u7684\u8868\u793a\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8bf8\u4f4d\u59d1\u5a18\uff0c\u8fd9\u5927\u5385\u56db\u9762\u7684\u5200\u624b\uff0c\u867d\u90fd\u5df2\u88ab\u9634\u9633\u5821\u9ec4\u5821\u4e3b\u5e26\u4eba\u7ed9\u8083\u6e05\u4e86\uff0c\u4f46\u54b1\u4eec\u4e0d\u80fd\u5927\u610f\uff0c\u7ea2\u82b1\u7ec4\u9632\u5b88\u5927\u5385\uff0c\u7eff\u53f6\u7ec4\u8ddf\u6211\u51c6\u5907\u63a5\u5e94\u5185\u5916\u3002\u201d \u3000\u3000\u4e94\u4e2a\u7eff\u8863\u5973\u5c06\uff0c\u90fd\u884c\u5230\u4e86\u4f55\u5bd2\u8863\u7684\u8eab\u4fa7\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u56de\u987e\u4e86\u7eff\u8863\u4e94\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f60\u4eec\u7559\u8fd9\u91cc\uff0c\u6211\u51fa\u53bb\u77a7\u77a7\u3002\u201d \u5e74\u7eaa\u6700\u5927\u7684\u4e00\u4e2a\u7eff\u8863\u5973\u5b50\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a \u201c\u4f55\u516c\u5b50\uff0c\u4e0d\u7528\u53bb\u3002\u201d \u4f55\u5bd2\u8863\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u7eff\u8863\u5973\u7b11\u9053\uff1a \u201c\u9700\u8981\u6211\u4eec\u51fa\u53bb\u65f6\uff0c\u7eff\u53f6\u59d1\u5a18\u81ea\u7136\u4f1a\u4e0b\u4ee4\u6211\u4eec\u51fa\u624b\u3002\u201d \u4f55\u5bd2\u8863\u56de\u987e\u4e86\u7eff\u8863\u5973\u5b50\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8bf7\u95ee\u59d1\u5a18\u82b3\u540d?\u201d \u3000\u3000\u7eff\u8863\u5973\u5b50\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u53eb\u7eff\u840d\uff0c\u4f55\u7237\u6709\u4ec0\u4e48\u5429\u5490?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u4e8b\u5b9e\u4e0a\uff0c\u6ca1\u6709\u4ec0\u4e48\u8bdd\u597d\u8bf4\uff0c\u4f46\u7eff\u840d\u65e2\u7136\u95ee\u4e86\uff0c\u53ea\u597d\u6ca1\u4e8b\u627e\u4e8b\uff0c\u65e0\u8bdd\u627e\u8bdd\u5730\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4f60\u53eb\u7eff\u840d\u5417?\u201d \u3000\u3000\u7eff\u840d\u9053\uff1a\u201c\u5bf9!\u7eff\u53f6\u7ec4\u7684\u4eba\uff0c\u90fd\u7528\u7eff\u5b57\u6392\u540d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u7ea2\u82b1\u7ec4\u7684\u4eba\uff0c\u90fd\u662f\u7528\u7ea2\u5b57\u6392\u540d\u4e86?\u201d \u3000\u3000\u7eff\u840d\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u554a!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u4eec\u539f\u6765\u6ca1\u6709\u59d3\u540d\u5417?\u201d \u3000\u3000\u7eff\u840d\u9053\uff1a \u201c\u81ea\u7136\u662f\u6709\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u8fdb\u5165\u94c1\u82b1\u9662\u5b66\u5251\u65f6\uff0c\u90fd\u7acb\u4e0b\u4e86\u91cd\u8a93\uff0e\u8fde\u540d\u5b57\u4e5f\u6539\u4e86\uff0c\u4ee5\u793a\u6c38\u8fdc\u6548\u5fe0\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u73b0\u5728\u5462?\u201d \u3000\u3000\u7eff\u840d\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u4eec\u8fd8\u662f\u6548\u5fe0\u9ad8\u59d1\u5a18\u554a\uff01\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f60\u4eec\u770b\u5230\u4e86\u540c\u4f34\u60e8\u6b7b\uff0c\u5fc3\u4e2d\u662f\u4e0d\u662f\u5f88\u96be\u8fc7\uff1f\u201d \u7eff\u840d\u9053\uff1a\u201c\u4e0d\u96be\u8fc7!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e0d\u96be\u8fc7?\u201d \u3000\u3000\u7eff\u840d\u9053\uff1a \u201c\u662f\u554a!\u6211\u4eec\u5b66\u7684\u6740\u4eba\u5251\u6cd5\uff0c\u6211\u4eec\u8981\u6740\u4eba\uff0c\u522b\u4eba\u4e5f\u8981\u6740\u6211\u4eec\uff0c\u6b7b\u4e86\u4e5f\u662f\u6ca1\u6709\u6cd5\u5b50\u7684\u4e8b\u60c5\u7684\u5566\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6697\u5fd6\u9053\uff1a\u201c\u9ad8\u5e7d\u5170\u8fd9\u4e2a\u4eba\uff0c\u5b9e\u5728\u4e0d\u7b80\u5355\uff0c\u4ed6\u8bad\u7ec3\u7684\u4eba\uff0c\u4e0d\u4f46\u628a\u5979\u4eec\u8bad\u7ec3\u6210\u4e86\u7b2c\u4e00\u6d41\u7684\u5251\u624b\uff0c \u4ee5\u628a\u4ed6\u4eec\u7cbe\u795e\u8bad\u7ec3\u6210\u4e86\u7b2c\u4e00\u6d41\u6218\u5fd7\uff0c\u6f20\u89c6\u751f\u6b7b\uff0c\u52c7\u608d\u7edd\u4f26\u3002 \u53ea\u542c\u7eff\u53f6\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u51fa\u6765\u3002\u201d \u3000\u3000\u7eff\u840d\u5e94\u4e86\u4e00\u58f0\uff0c\u98de\u8eab\u800c\u51fa\u3002 \u3000\u3000\u7eff\u840d\u4e00\u52a8\uff0c\u6240\u6709\u7684\u7eff\u8863\u5973\u5b50\uff0c\u8fde\u7ef5\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e5f\u7f13\u6b65\u884c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u62ac\u5934\u770b\uff0c\u53ea\u89c1\u7ea2\u82b1\u548c\u9ad8\u5e7d\u5170\u9690\u8eab\u5728\u4e00\u682a\u82b1\u6811\u4e4b\u540e\u3002 \u3000\u3000\u7eff\u53f6\u5e26\u7740\u4ed6\u7684\u4e94\u4e2a\u5c5e\u4e0b\uff0c\u85cf\u5728\u4e00\u7247\u82b1\u4e1b\u4e4b\u540e\uff0c\u9664\u4e86\u53ef\u89c1\u9ad8\u5e7d\u5170\u5e26\u6765\u7684\u4eba\u624b\u4e4b\u5916\uff0c\u9634\u9633\u5821\u7684\u4eba\uff0c\u90fd\u9690\u85cf\u7684\u5341\u5206\u79d8\u5bc6\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7a81\u7136\u56de\u8fc7\u4e86\u5934\uff0c\u671b\u7740\u4f55\u5bd2\u8863\uff0c\u4e0d\u505c\u6447\u6325\u7740\u53f3\u624b\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u660e\u767d\u90a3\u662f\u8ba9\u4ed6\u9000\u56de\u5927\u5385\u7684\u610f\u601d\uff0c\u4f46\u4ed6\u5374\u88c5\u4f5c\u4e0d\u660e\u767d\uff0c\u53cd\u800c\u8eac\u8eab\u51b2\u5230\u4e86\u9ad8\u5e7d\u5170\u8eab\u4fa7\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u8981\u4f60\u56de\u53bb\uff0c\u4f60\u6765\u8fd9\u91cc\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6!\u8981\u6211\u56de\u53bb\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u91cc\u8fd8\u6709\u56db\u4e2a\u5973\u5b69\u5b50\uff0c\u8981\u4f60\u7167\u987e\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u4f46\u628a\u4ed6\u4eec\u8bad\u7ec3\u6210\u4e86\u7b2c\u4e00\u6d41\u7684\u5251\u624b\uff0c\u800c\u4e14\uff0c\u4e5f\u8bad\u7ec3\u4e86\u5979\u4eec\u6709\u7740\u94c1\u4e00\u822c\u7684\u610f\u5fd7\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f46\u5979\u4eec\u8fd8\u662f\u8981\u4eba\u7167\u987e\uff0c\u4f60\u5728\u90a3\u91cc\uff0c\u4f1a\u5bf9\u5979\u4eec\u6709\u7740\u5f88\u5927\u7684\u9f13\u821e\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u4eec\u548c\u6211\u76f8\u5904\u4e0d\u4e45\uff0c\u5bf9\u6211\u5168\u65e0\u4fe1\u5fc3\uff0c\u6211\u600e\u4f1a\u5bf9\u4ed6\u4eec\u6709\u90a3\u4e48\u5927\u7684\u5f71\u54cd\u529b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u8bf4\u6709\u5c31\u662f\u6709\uff0c\u5feb\u4e9b\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9eef\u7136\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u6211\u660e\u767d\u4f60\u7684\u5fc3\u610f\uff0c\u4f60\u5e0c\u671b\u6211\u8eb2\u5728\u5927\u5385\u91cc\uff0c\u56e0\u4e3a\u90a3\u91cc\u5f88\u5b89\u5168\uff0c\u662f\u4e0d\u662f?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4f60\u80e1\u601d\u4e71\u60f3\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u6211\u8bf4\u7684\u662f\u771f\u8bdd\uff0c\u4f60\u5fc3\u4e2d\u4e5f\u660e\u767d\uff0c\u5e7d\u5170\uff0c\u5176\u5b9e\u4f60\u60f3\u9519\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54ea\u91cc\u9519\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u548c\u53cc\u5973\uff0c\u90fd\u5728\u5192\u9669\uff0c\u6211\u5374\u8eb2\u5728\u5927\u5385\uff0c\u8981\u6211\u7684\u5fc3\u795e\uff0c\u5982\u4f55\u80fd\u5b89\uff0c\u518d\u8bf4\uff0c\u4f60\u4eec\u4e24\u4e2a\u4eba\uff0c\u5982\u82e5\u6709\u4ec0\u4e48\u9519\u5931\uff0c\u6211\u53c8\u80fd\u5982\u4f55\u6d3b\u5f97\u4e0b\u53bb\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6709\u4e9b\u611f\u52a8\u7684\u9053\uff1a\u201c\u5509!\u6211\u4eec\u4e09\u4e2a\u4eba\uff0c\u5206\u4e5f\u5e94\u8be5\u5206\u4e00\u4e2a\u5728\u540e\u9762\uff0c\u5bf9\u5427?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5c31\u7b97\u5e94\u8be5\u5206\u4e00\u4e2a\uff0c\u90a3\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u8be5\u662f\u6211\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u4f60\u8bf4\uff0c\u5e94\u8be5\u662f\u8c01\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60!\u4f60\u662f\u88ab\u6211\u4eec\u786c\u62d6\u4e0b\u6c34\u7684\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7684\u773c\u775b\u6709\u4e9b\u6da6\u6e7f\uff0c\u7528\u6781\u4f4e\u5fae\u7684\u58f0\u97f3\uff0c\u8bf4\u9053\uff1a \u201c\u4f55\u90ce\uff0c\u4f60\u8981\u591a\u4fdd\u91cd\uff0c\u9700\u77e5\uff0c\u4f60\u5982\u6709\u4ec0\u4e48\u4e0d\u6d4b\uff0c\u6211\u548c\u5927\u59d0\uff0e\u90fd\u6d3b\u4e0d\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u6211\u4eec\u4e09\u4f4d\u4e00\u4f53\uff0c\u8c01\u90fd\u4e0d\u80fd\u6b7b\uff0c\u8c01\u90fd\u4e0d\u80fd\u53d7\u4f24\u5bb3\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u7ec3\u7684\u5251\u6cd5\uff0c\u662f\u4e13\u4ee5\u6740\u4eba\u7528\u7684\uff0c\u6211\u76f8\u4fe1\u6709\u5f88\u5f3a\u70c8\u7684\u5236\u654c\u529b\u91cf\uff0c\u4e0d\u8fc7\uff0c\u6211\u5e0c\u671b\u80fd\u4e13\u5fc3\u5bf9\u654c\uff0c\u624d\u6709\u5f88\u597d\u7684\u6548\u679c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u548c\u6211\u6709\u4f55\u5173\u7cfb\uff0c\u96be\u9053\u6211\u4f1a\u6405\u4e71\u4f60\u7684\u5fc3\u795e\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5bf9\uff0c\u770b\u5230\u4e86\u4f60\uff0c\u6211\u7684\u5fc3\u4f1a\u4e71\uff0c\u800c\u4e14\uff0c\u90a3\u4f1a\u5f71\u54cd\u6211\u51fa\u624b\u7684\u901f\u5ea6\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u6709\u8fd9\u4e48\u574f?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u662f\u574f\uff0c\u662f\u5173\u5fc3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d\u5566\uff0c\u4e0d\u7528\u8bf4\u4e86\uff0c\u6211\u4e0d\u6015\u5371\u9669\u7684\u5730\u65b9\uff0c\u4e5f\u4e0d\u80fd\u8eb2\u5728\u6700\u5b89\u5168\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u7a81\u7136\uff0c\u4f20\u8fc7\u6765\u4e00\u58f0\u60e8\u53eb\u3002 \u3000\u3000\u90a3\u662f\u4e00\u4e2a\u4eba\uff0c\u5728\u9047\u4e0a\u4e86\u6700\u5927\u7684\u75db\u82e6\u65f6\uff0c\u54cd\u8d77\u7684\u60e8\u53eb\u4e4b\u58f0\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u8f7b\u8f7b\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u6563\u5f00\uff0c\u542c\u4e0d\u5230\u6211\u7684\u4ee4\u8c15\uff0c\u4e0d\u8bb8\u51fa\u624b\u3002\u201d \u3000\u3000\u7eff\u53f6\u5e26\u7740\u4e94\u4e2a\u5973\u5c06\u5ffd\u7136\u6563\u5e03\u82b1\u8349\u4e1b\u4e2d\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u524d\u9762\u770b\u770b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u60f3\u963b\u6b62\uff0c\u4f46\u5374\u53c8\u5fcd\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u501f\u82b1\u4e1b\u63a9\u62a4\uff0c\u51b2\u5230\u4e86\u94c1\u82b1\u9662\u6700\u524d\u7684\u56f4\u5899\u8fb9\u7f18\u3002 \u3000\u3000\u53ea\u89c1\u56db\u4e2a\u767d\u8863\u5200\u624b\uff0c\u5206\u5e03\u4e00\u4e08\u5de6\u53f3\u7684\u8ddd\u79bb\u4e4b\u5185\uff0c\u4e00\u4e2a\u767d\u8863\u5200\u624b\uff0c\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u90a3\u767d\u8863\u5200\u624b\u4e00\u9762\u8d70\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u4f55\u5144\uff0c\u5c0f\u5fc3\uff0c\u4e00\u90e8\u5206\u654c\u4eba\u5df2\u5230\u4e86\u56f4\u5899\u5916\u9762\u3002\u201d \u3000\u3000\u4ed6\u5f00\u4e86\u53e3\uff0c\u4f55\u5bd2\u8863\u624d\u542c\u51fa\u662f\u4e07\u80dc\u7684\u58f0\u97f3\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5929\u5df2\u5feb\u4eae\uff0c\u4f46\u9ece\u660e\u524d\u7684\u4e00\u6bb5\u9ed1\u6697\uff0c\u4f7f\u654c\u6211\u53cc\u65b9\uff0c\u90fd\u65e0\u6cd5\u770b\u5230\u4e00\u4e08\u5916\u7684\u666f\u7269\uff0c\u4e5f\u90fd\u4e0d\u6562\u8d38\u7136\u51fa\u624b\u5411\u524d\u653b\u8fdb\u3002 \u3000\u3000\u53cc\u65b9\u5f62\u6210\u4e86\u4e00\u79cd\u5bf9\u5cd9\uff0c\u4e00\u79cd\u51f6\u9669\u7684\u5bf9\u5cd9\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8981\u4e0d\u8981\u8fc7\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u4e07\u80dc\u6447\u6447\u5934\uff0c\u9053\uff1b \u201c\u4e0d\u884c\uff0c\u6211\u60f3\u4ed6\u548c\u6211\u4eec\u4e00\u6837\uff0c\u5df2\u7ecf\u6709\u4e86\u5f88\u5b8c\u6574\u7684\u90e8\u7f72\uff0c\u53ea\u8981\u9732\u51fa\u4e00\u4e2a\u8111\u888b\uff0c\u90fd\u53ef\u80fd\u4f1a\u906d\u5230\u5bf9\u65b9\u7684\u653b\u88ad\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u8fb9\u5462?\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u4e5f\u5f88\u4e25\u5bc6\uff0c\u53ea\u8981\u5bf9\u65b9\u7a0d\u6709\u52a8\u9759\uff0c\u4e5f\u4f1a\u906d\u5230\u731b\u70c8\u7684\u653b\u88ad\u3002\u201d \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u8fde\u7ef5\u4e0d\u7edd\u7684\u91d1\u94c1\u76f8\u51fb\u4e4b\u58f0\u4f20\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4f4e\u58f0\u9053\uff1a \u201c\u4e07\u5144\uff0c\u5821\u4e3b\u5df2\u548c\u4ed6\u4eec\u52a8\u4e0a\u4e86\u624b\uff0c\u54b1\u4eec\u4e5f\u8be5\u51fa\u624b\u4e86\u3002\u201d \u4e5f\u4e0d\u5f85\u4e07\u80dc\u56de\u7b54\uff0c\u8eab\u5b50\u4e00\u8d77\uff0c\u8d34\u7740\u5899\u7ffb\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e1c\u65b9\u5df2\u6cdb\u8d77\u4e86\u9c7c\u809a\u767d\u8272\uff0c\u666f\u7269\u6e05\u6670\u53ef\u89c1\u3002 \u3000\u3000\u4e07\u80dc\u5ffd\u7136\u95f4\u98de\u8dc3\u800c\u8d77\uff0c\u8d8a\u8fc7\u77ed\u5899\u3002 \u3000\u3000\u7d27\u63a5\u7740\u662f\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u5979\u4e00\u76f4\u5728\u6ce8\u610f\u4f55\u5bd2\u8863\u7684\u4e3e\u6b62\uff0c\u770b\u5230\u4ed6\u98de\u8d8a\u77ed\u5899\u800c\u8fc7\uff0c\u7acb\u523b\u7d27\u968f\u7740\u8dc3\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5e26\u6765\u4e86\u795e\u5200\u5802\u4e2d\u6700\u7cbe\u9510\u7684\u6740\u624b\uff0c\u4e8c\u5341\u56db\u4e2a\u94c1\u7532\u6b66\u58eb\u3002 \u3000\u3000\u53ef\u60dc\u7684\u662f\uff0c\u201c\u6c34\u706b\u65e0\u60c5\u201d\u6740\u624b\uff0c\u7531\u9ad8\u5929\u5065\u76f4\u63a5\u63a7\u5236\uff0c\u96f7\u65e0\u5f71\u65e0\u6cd5\u8c03\u52a8\uff0c\u8fd9\u65f6\uff0c\u4ed6\u5982\u6709\u201c\u6c34\u706b\u65e0\u60c5\u201d\u6740\u624b\uff0c \u53ea\u6015\u4f55\u5bd2\u8863\u3001\u9ad8\u5e7d\u5170\u3001\u4e07\u80dc\uff0c\u90fd\u8981\u4f24\u5728\u201c\u6c34\u706b\u65e0\u60c5\u201d\u4e4b\u4e0b\u3002 \u4f46\u4e8c\u5341\u56db\u540d\u94c1\u7532\u6b66\u58eb\uff0c\u4e5f\u4e0d\u662f\u597d\u5bf9\u4ed8\u7684\u654c\u4eba\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u5374\u88ab\u90a3\u9065\u9065\u4f20\u6765\u7684\u6253\u6597\u4e4b\u58f0\uff0c\u5206\u53bb\u4e86\u5f88\u591a\u7684\u7cbe\u795e\u3002 \u3000\u3000\u76f4\u5230\u9ad8\u5e7d\u5170\u4e5f\u8dc3\u8fc7\u4e01\u56f4\u5899\uff0c\u96f7\u65e0\u5f71\u624d\u970d\u7136\u8b66\u89c9\u3002 \u3000\u3000\u4e8c\u5341\u56db\u540d\u94c1\u7532\u6b66\u58eb\uff0c\u4e5f\u5c55\u5f00\u4e86\u5bf9\u4e09\u4eba\u7684\u5305\u56f4\u3002 \u3000\u3000\u770b\u5230\u4e86\u4e09\u9762\u5408\u56f4\u4e4b\u52bf\uff0c\u9ad8\u5e7d\u5170\u53cd\u800c\u5fc3\u4e2d\u4e00\u5bbd\uff0c\u9053\uff1a \u201c\u8bf8\u4f4d\u5c0f\u5fc3\uff0c\u8fd9\u662f\u795e\u5200\u5802\u4e2d\u6700\u7cbe\u9510\u7684\u6740\u624b\uff0c\u4e0d\u53ef\u529b\u654c\uff0c\u628a\u529b\u91cf\u96c6\u4e2d\u8d77\u6765\u3002\u201d \u3000\u3000\u5979\u5bbd\u5fc3\u7684\u662f\uff0c\u96f7\u65e0\u5f71\u672a\u5e26\u201c\u6c34\u706b\u65e0\u60c5\u201d\u6740\u624b\u3002 \u3000\u3000\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u5411\u4f55\u5bd2\u8863\u9760\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e07\u80dc\u5fc3\u4e2d\u6709\u70b9\u4e0d\u670d\uff0c\u6697\u9053\uff1a\u6211\u5200\u6c89\u52bf\u731b\uff0c\u6211\u4e0d\u4fe1\u4ed6\u4eec\u4e0d\u6015\u3002 \u3000\u3000\u4ed6\u7ad9\u7740\u672a\u52a8\u3002 \u3000\u3000\u4e00\u4e2a\u94c1\u7532\u6b66\u58eb\uff0c\u51b2\u5230\u4e86\u4e07\u80dc\u7684\u9762\u524d\u3002 \u3000\u3000\u4e07\u80dc\u4e00\u6325\u624b\u4e2d\u7684\u5927\u5200\uff0c\u76f4\u5288\u8fc7\u53bb\u3002 \u3000\u3000\u5200\u52bf\u5982\u98ce\uff0c\u8fce\u5934\u65a9\u4e0b\u3002 \u3000\u3000\u90a3\u94c1\u7532\u6b66\u58eb\uff0c\u7adf\u7136\u4e0d\u987e\u90a3\u4e00\u5200\u7684\u51f6\u5389\uff0c\u7a81\u7136\u4e00\u626c\u624b\u4e2d\u4e09\u68f1\u5200\uff0c\u523a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4f46\u95fb\u7830\u7136\u4e00\u58f0\uff0c\u780d\u5728\u4e86\u94c1\u7532\u6b66\u58eb\u7684\u5934\u4e0a\u3002 \u3000\u3000\u706b\u661f\u8ff8\u5192\u3002 \u3000\u3000\u8fd9\u4e00\u5200\uff0c\u7adf\u7136\u6ca1\u6709\u4f24\u5f97\u90a3\u94c1\u7532\u6b66\u58eb\u3002 \u3000\u3000\u539f\u6765\uff0c\u90a3\u94c1\u7532\u6b66\u58eb\uff0c\u5934\u4e0a\u6234\u4e86\u4e00\u9876\u5e3d\u5b50\uff0c\u7adf\u7136\u662f\u7cbe\u94c1\u94f8\u6210\u3002 \u3000\u3000\u8fd9\u5b8c\u5168\u51fa\u4e86\u4e07\u80dc\u7684\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u5bf9\u90a3\u523a\u6765\u7684\u4e00\u5200\uff0c\u5b9e\u5728\u6709\u4e9b\u63aa\u624b\u4e0d\u53ca\u3002 \u3000\u3000\u8eab\u8eaf\u8fde\u8fde\u540e\u9000\uff0c\u4f46\u4ecd\u7136\u88ab\u4e09\u68f1\u5200\u950b\u6240\u4f24\u3002 \u3000\u3000\u5200\u5212\u5728\u5de6\u81c2\u4e0a\uff0c\u88c2\u5f00\u4e86\u4e00\u9053\u4e09\u5bf8\u957f\u7684\u8840\u53e3\u3002 \u3000\u3000\u9c9c\u8840\u7531\u81c2\u4e0a\u5192\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u75be\u8dc3\u800c\u81f3\u5c01\u5f00\u4e86\u53e6\u4e00\u628a\u523a\u6765\u7684\u4e09\u68f1\u5200\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u968f\u540e\u8d76\u4e0a\uff0c\u4e09\u5251\u9f50\u51fa\uff0c\u5272\u4e86\u4e00\u4e2a\u94c1\u7532\u6b66\u58eb\u7684\u54bd\u5589\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6293\u4f4f\u4e86\u4e07\u80dc\u9000\u5230\u5899\u8fb9\u3002 \u3000\u3000\u4e07\u80dc\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u94c1\u7532\u6b66\u58eb\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u8fd9\u4e9b\u94c1\u7532\u6b66\u58eb\uff0c\u8eab\u4e0a\u62ab\u6709\u7532\u80c4\uff0c\u4e00\u822c\u7684\u65b9\u6cd5\uff0c\u5f88\u96be\u4f24\u5230\u4ed6\u4eec\u3002\u201d \u773c\u770b\u4e00\u4e2a\u94c1\u7532\u6b66\u58eb\u5012\u4e86\u4e0b\u53bb\uff0c\u53e6\u5916\u7684\u4eba\uff0c\u4ecd\u7136\u56f4\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6025\u9053\uff1a\u201c\u5feb\u8bf4\uff0c\u5e7d\u5170\uff0c\u4ed6\u4eec\u7684\u81f4\u547d\u5904\u5728\u4ec0\u4e48\u5730\u65b9\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8116\u5b50\uff0c\u6ca1\u6709\u62a4\u5b88\u7684\u5730\u65b9\uff0c\u53cc\u819d\u5173\u8282\u5904\uff0c\u548c\u4e24\u6761\u624b\u81c2\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53f3\u624b\u4e00\u6307\uff0c\u9053\uff1a\u201c\u4e07\u5144\u8fc7\u53bb\uff0c\u8f6c\u544a\u54b1\u4eec\u7684\u4eba\u3002\u201d \u3000\u3000\u6697\u8fd0\u5185\u529b\uff0c\u5411\u4e0a\u4e00\u9001\uff0c\u628a\u4e07\u80dc\u9001\u8fc7\u4e86\u56f4\u5899\u3002 \u3000\u3000\u5c31\u8fd9\u4e00\u77ac\u5de5\u592b\uff0c\u9ad8\u5e7d\u5170\u3001\u4f55\u5bd2\u8863\uff0c\u5df2\u7ecf\u88ab\u56f4\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u53cc\u624b\u4e00\u4f38\uff0c\u4e24\u67c4\u91d1\u5251\uff0c\u5df2\u63e1\u5230\u4e86\u624b\u4e2d\u3002 \u3000\u3000\u91d1\u5251\u5230\u624b\uff0c\u4e24\u67c4\u4e09\u68f1\u5200\uff0c\u5df2\u75be\u653b\u800c\u81f3\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4e00\u5251\u98de\u51fa\uff0c\u6321\u5f00\u4e00\u5200\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u53cc\u5251\u4ea4\u9519\uff0c\u5c01\u5f00\u4e86\u53e6\u4e00\u628a\u5200\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u5bd2\u8863\u56de\u53bb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u4e5f\u611f\u89c9\u5230\u8fd9\u4e9b\u94c1\u7532\u6b66\u58eb\uff0c\u5200\u52bf\u6c89\u731b\uff0c\u4e0d\u53ef\u529b\u654c\u3002 \u3000\u3000\u542c\u5230\u9ad8\u5e7d\u5170\u7684\u62db\u547c\uff0c\u4f55\u5bd2\u8863\u7acb\u523b\u98de\u8eab\u800c\u8d77\uff0c\u8dc3\u4e0a\u56f4\u5899\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4e5f\u98de\u8eab\u800c\u4e0a\u3002 \u3000\u3000\u4f46\u89c1\u5149\u8292\u4ea4\u9519\uff0c\u95ea\u4e86\u51e0\u95ea\uff0c\u54cd\u8d77\u4e86\u4e00\u58f0\u60e8\u53eb\uff0c\u548c\u7830\u7136\u4e00\u9707\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u51fa\u4e86\u56db\u628a\u5251\uff0c\u56db\u79cd\u4e0d\u540c\u7684\u989c\u8272\u3002 \u3000\u3000\u6bcf\u591a\u4e00\u628a\u5251\uff0c\u5c31\u591a\u4e86\u4e00\u4efd\u5a01\u529b\u3002 \u3000\u3000\u4e00\u4e2a\u94c1\u7532\u6b66\u58eb\uff0c\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u54bd\u5589\u4e2d\u5192\u51fa\u4e86\u9c9c\u8840\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u65ad\u4e86\u4e00\u6761\u624b\u81c2\uff0c\u75db\u5f97\u4ed6\u62b1\u7740\u4f24\u5904\u8e72\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7ad9\u5728\u56f4\u5899\u4e0a\uff0c\u770b\u5f97\u5341\u5206\u6e05\u695a\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u770b\u4e0d\u51fa\u5251\u52bf\u53d8\u5316\uff0c\u4e03\u8272\u5251\uff0c\u4f3c\u4e4e\u662f\u4e13\u95e8\u4e3a\u4e86\u6740\u4eba\u521b\u51fa\u6765\u7684\u5251\u62db\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4f7f\u5bf9\u65b9\u4e00\u6b7b\u4e00\u4f24\u4e4b\u540e\uff0c\u53cc\u8db3\u672a\u843d\u5b9e\u5730\uff0c\u534a\u7a7a\u4e2d\u633a\u8eab\u4e00\u8dc3\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u4eba\u5230\u4e86\u56f4\u5899\u4e4b\u4e0a\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u4e0b\u53bb\uff0c\u4ed6\u4eec\u8eab\u4e0a\u7684\u94c1\u7532\u7b28\u91cd\uff0c\u4e0d\u80fd\u98de\u8dc3\u3002\u201d \u3000\u3000\u4e00\u62c9\u4f55\u5bd2\u8863\uff0c\u8dc3\u4e0b\u56f4\u5899\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4f4e\u58f0\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u6700\u91cd\u8981\u7684\u662f\uff0c\u54b1\u4eec\u8981\u60f3\u6cd5\u5b50\u53bb\u63a5\u5e94\u5821\u4e3b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u662f\u8bf4\uff0c\u54b1\u4eec\u5fc5\u987b\u8981\u60f3\u529e\u6cd5\u51b2\u8fc7\u8fd9\u4e9b\u94c1\u7532\u6b66\u58eb\u7684\u62e6\u5835\u624d\u884c\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u4e0d\u592a\u5bb9\u6613\uff0c\u4f60\u5e94\u8be5\u611f\u89c9\u5230\uff0c\u4ed6\u4eec\u4e00\u5f00\u59cb\uff0c\u5c31\u662f\u7fa4\u6bb4\uff0c\u4efb\u4f55\u4eba\uff0c\u53ea\u8981\u9677\u5165\u4e86\u94c1\u7532\u6b66\u58eb\u7684\u5305\u56f4\u4e4b\u4e2d\uff0c\u4fbf\u5f88\u96be\u8131\u56f4\u800c\u51fa\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54b1\u4eec\u603b\u4e0d\u80fd\u4e0d\u60f3\u529e\u6cd5\u51fa\u53bb\u554a?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u73b0\u5728\u6b63\u5728\u627e\u4ed6\u4eec\u7684\u7f3a\u70b9\uff0c\u800c\u4e14\uff0c\u5df2\u7ecf\u627e\u51fa\u4e86\u7f3a\u70b9\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u7f3a\u70b9?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4ed6\u4eec\u8eab\u7a7f\u94c1\u7532\uff0c\u65e0\u6cd5\u98de\u8dc3\uff0c\u8fd9\u662f\u4ed6\u4eec\u7684\u81f4\u547d\u7f3a\u70b9\u4e4b\u4e00\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u8981\u5982\u4f55\u5bf9\u4ed8\u8fd9\u4e9b\u4eba?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7ed5\u8fc7\u53bb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5bf9\uff0c\u7ed5\u8fc7\u53bb\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a \u201c\u5bd2\u8863\uff0c\u5728\u7ed5\u8fc7\u53bb\u4e4b\u524d\uff0c\u54b1\u4eec\u8981\u60f3\u4e2a\u529e\u6cd5\uff0c\u5148\u544a\u8bc9\u4ed6\u4eec\uff0c\u5e94\u5982\u4f55\u5bf9\u4ed8\u94c1\u7532\u6b66\u58eb\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u544a\u8bc9\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4f60\u548c\u4e07\u5251\u4f7f\u8c08\u8c08\uff0c\u6211\u4e5f\u548c\u7ea2\u82b1\u3001\u7eff\u53f6\u4ea4\u5f85\u51e0\u53e5\u8bdd\u3002\u201d \u8fd9\u65f6\uff0c\u4e07\u80dc\u7684\u4f24\u53e3\u5df2\u7ecf\u5305\u624e\u5b8c\u597d\uff0c\u5feb\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4f4e\u58f0\u9053\uff1a\u201c\u4e07\u5144\uff0c\u4f24\u52bf\u7684\u60c5\u5f62\u5982\u4f55?\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u53ea\u662f\u4e00\u70b9\u808c\u8089\u4e4b\u4f24\uff0c\u8fd8\u6ca1\u6709\u4f24\u5230\u7b4b\u9aa8\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd8\u53ef\u4ee5\u52a8\u624b\u5417?\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u53ef\u4ee5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5bf9\u4ed8\u94c1\u7532\u6b66\u58eb\u7684\u529e\u6cd5\uff0c\u4e07\u5144\u53ef\u80fd\u6210\u7b97?\u201d \u3000\u3000\u5200\u80dc\u9053\uff1a \u201c\u6211\u521a\u624d\u4e5f\u60f3\u4e86\u4e00\u4e0b\uff0e\u603b\u89c9\u7740\u529e\u6cd5\u8fd8\u4e0d\u7406\u60f3\uff0c\u4e0d\u77e5\u4f55\u5144\uff0c\u6709\u4ec0\u4e48\u9ad8\u660e\u7684\u529e\u6cd5?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ed6\u4eec\u552f\u4e00\u7684\u7f3a\u70b9\uff0c\u5c31\u662f\u8fd0\u7528\u4e0d\u5f88\u7075\u6d3b\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u6709\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u51c6\u5907\u5bf9\u4ed8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7565\u4e00\u6c89\u541f\u9053\uff1a \u201c\u597d!\u4ed6\u4eec\u5584\u4e8e\u96c6\u4e2d\u56f4\u51fb\uff0c\u7528\u2018\u6c34\u706b\u65e0\u60c5\u2019\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u5e94\u8be5\u662f\u5f88\u6709\u6548\u7528\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u8fd9\u51e0\u7b52\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u5bf9\u6211\u4eec\u5f88\u91cd\u8981\uff0c\u6240\u4ee5\uff0c\u6211\u4e0d\u4f1a\u8f7b\u6613\u65bd\u7528\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5bf9!\u8fd9\u51e0\u7b52\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u4e5f\u662f\u54b1\u4eec\u5bf9\u4ed8\u7b2c\u4e00\u5821\u7684\u4e00\u80a1\u529b\u91cf\uff0c\u94c1\u7532\u6b66\u58eb\u8fd0\u8f6c\u4e0d\u7075\uff0c\u53ea\u8981\u4e0d\u8ba9\u4ed6\u4eec\u56f4\u4f4f\uff0c\u5c31\u53ef\u4ee5\u548c\u4ed6\u4eec\u6e38\u6597\u4e86\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u6211\u4f1a\u4ea4\u4ee3\u4ed6\u4eec\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u9ad8\u5e7d\u5170\u4e5f\u884c\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u4e07\u5144\uff0c\u4e5d\u4e2a\u5973\u5c06\u4e5f\u4ea4\u7ed9\u4f60\u4e86\uff0c\u6211\u4e5f\u544a\u8bc9\u4e86\u5979\u4eec\u5bf9\u4ed8\u94c1\u7532\u6b66\u58eb\u7684\u529e\u6cd5\uff0c\u5982\u82e5\u5979\u4eec\u914d\u5408\u5f97\u5f88\u597d\uff0c\u6211\u60f3\u53ef\u4ee5\u5bf9\u4ed8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u6211\u5e26\u7ea2\u82b1\u3001\u7eff\u53f6\uff0c\u51c6\u5907\u53bb\u63a5\u5e94\u9ec4\u5821\u4e3b\uff0c\u9632\u5b88\u94c1\u82b1\u9662\u7684\u8d23\u4efb\uff0c\u5c31\u7531\u4e07\u5144\u627f\u62c5\u4e86\u3002\u201d \u3000\u3000\u4e07\u80dc\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5e26\u6765\u8fd9\u4e9b\u5c5e\u4e0b\uff0c\u6b66\u529f\u4e0a\uff0c\u6211\u4e0d\u6562\u4fdd\u8bc1\uff0c\u4f46\u4ed6\u4eec\u53ef\u4ee5\u4e3a\u6211\u6548\u6b7b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u8fd9\u4e9b\u5973\u5c06\uff0c\u4e5f\u90fd\u662f\u6b7b\u58eb\uff0c\u4e0d\u7ba1\u5982\u4f55\u51f6\u9669\uff0c\u5979\u4eec\u90fd\u4e0d\u4f1a\u9000\u7f29\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u4e24\u4f4d\u653e\u5fc3\u5427!\u6211\u4f1a\u5168\u529b\u4ee5\u8d74\u3002\u201d \u3000\u3000\u4f46\u95fb\u7830\u7830\u5927\u9707\uff0c\u56f4\u5899\u88ab\u649e\u4e86\u4e00\u4e2a\u7f3a\u53e3\u3002 \u3000\u3000\u4e07\u80dc\u5df2\u5403\u8fc7\u4e86\u8fd9\u4e9b\u94c1\u7532\u6b66\u58eb\u7684\u82e6\u5934\uff0c\u5fc3\u77e5\u4e0d\u53ef\u529b\u654c\uff0c\u7acb\u523b\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u53ef\u529b\u62fc\u3002\u201d \u3000\u3000\u95ea\u8eab\u5230\u4e00\u5904\u5927\u6811\u540e\u9762\uff0c\u85cf\u8d77\u4e86\u8eab\u5b50\u3002 \u3000\u3000\u4e8c\u5341\u4e2a\u94c1\u7532\u6b66\u58eb\uff0c\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u597d\u5728\u8fd9\u94c1\u82b1\u9662\u4e2d\uff0c\u6709\u5f88\u591a\u7684\u82b1\u6811\uff0c\u4e07\u80dc\u548c\u4ed6\u7684\u5200\u624b\uff0c\u53ef\u4ee5\u501f\u4ee5\u85cf\u8eab\u3002 \u3000\u3000\u8fd9\u4e9b\u94c1\u7532\u6b66\u58eb\uff0c\u867d\u7136\u8eab\u62ab\u91cd\u7532\uff0c\u884c\u52a8\u4e0d\u4fbf\uff0c\u4fbf\u4ed6\u4eec\u5e76\u975e\u50bb\u5b50\u3002 \u3000\u3000\u5927\u7ea6\u96f7\u65e0\u5f71\u4e5f\u544a\u8beb\u4e86\u4ed6\u4eec\uff0c\u6240\u4ee5\uff0c\u8fd9\u4e9b\u94c1\u7532\u6b66\u58eb\uff0c\u4e00\u8fdb\u5165\u4e86\u94c1\u82b1\u9662\u4e4b\u540e\uff0c\u7acb\u523b\u6563\u5e03\u5f00\u53bb\u3002 \u3000\u3000\u4e07\u80dc\u593a\u5f97\u4e86\u516d\u7b52\u6c34\u706b\u65e0\u60c5\uff0c\u4e5f\u5df2\u57cb\u4f0f\u4e86\u8d77\u6765\uff0c\u7b49\u5f85\u6700\u9002\u5f53\u7684\u65f6\u673a\uff0c\u53d1\u5c04\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u9ec4\u7075\u5e26\u6765\u7684\u4eba\u624b\uff0c\u90fd\u662f\u4e07\u80dc\u8bad\u7ec3\u51fa\u6765\u7684\u5251\u624b\uff0c\u9009\u62d4\u51fa\u6765\u7684\u7cbe\u9510\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\uff0c\u548c\u4e07\u80dc\u957f\u5e74\u76f8\u5904\uff0c\u7531\u4e07\u80dc\u5b8b\u6307\u6325\uff0c\u66f4\u662f\u5f97\u5fc3\u5e94\u624b\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u4e00\u76f4\u6ca1\u6709\u51fa\u73b0\uff0c\u4f46\u8fdb\u5165\u94c1\u82b1\u9662\u7684\u94c1\u7532\u6b66\u58eb\uff0c\u5374\u5df2\u5206\u5e03\u6210\u4e00\u4e2a\u5f27\u5f62\u7684\u5927\u5708\uff0c\u5411\u524d\u56f4\u653b\u8fc7\u53bb\u3002 \u3000\u3000\u5f88\u663e\u7136\u7684\uff0c\u4ed6\u4eec\u4ee5\u94c1\u82b1\u5927\u5385\u4e3a\u76ee\u6807\uff0c\u7f13\u7f13\u5411\u524d\u8feb\u8fdb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e00\u4e2a\u94c1\u7532\u6b66\u58eb\uff0c\u6025\u6b65\u633a\u8fdb\uff0c\u76f4\u5411\u5927\u5385\u51b2\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u4f3c\u5df2\u6709\u9632\u5907\uff0c\u4eba\u4e0e\u4eba\u4e4b\u95f4\u7684\u8ddd\u79bb\uff0c\u4fdd\u6301\u4e86\u4e03\u516b\u5c3a\u8fdc\u3002 \u3000\u3000\u90a3\u5df2\u8d85\u8fc7\u4e86\u201c\u6c34\u706b\u65e0\u60c5\u201d\u5a01\u529b\u7b3c\u7f69\u7684\u534a\u5f84\u3002 \u3000\u3000\u4e00\u7b52\u201c\u6c34\u706b\u65e0\u60c5\u201d\uff0c\u53ea\u80fd\u4f24\u5230\u4ed6\u4eec\u4e00\u4e2a\u3002 \u3000\u3000\u4e07\u80dc\u770b\u90a3\u6025\u884c\u51b2\u5411\u5927\u5385\u7684\u94c1\u7532\u6b66\u58eb\uff0c\u9664\u4e86\u53f3\u624b\u7684\u4e09\u68f1\u5200\u4e4b\u5916\uff0c\u8eab\u8fb9\u8fd8\u5e26\u4e00\u4e2a\u6728\u76d2\u3002 \u3000\u3000\u867d\u7136\u4e07\u80dc\u65e0\u6cd5\u6599\u77e5\u90a3\u6728\u76d2\u4e2d\u85cf\u7684\u4ec0\u4e48\uff0c\u4f46\u60f3\u5230\u5fc5\u6709\u4f5c\u7528\uff0c\u4e0d\u7981\u4e00\u76b1\u7709\u5934\uff0c\u6b63\u60f3\u73b0\u8eab\u62e6\u963b\u4ed6\uff0c\u4e00\u4e2a\u5200\u5b50\u5374\u5df2\u62a2\u5148\u800c\u51fa\u3002 \u3000\u3000\u4ed6\u8eab\u7740\u767d\u8863\uff0c\u624b\u4e2d\u5bbd\u9762\u5200\uff0c\u4e00\u8bed\u4e0d\u53d1\uff0c\u4e00\u5200\u524a\uff0c\u94c1\u7532\u6b66\u58eb\u624b\u4e2d\u7684\u4e09\u68f1\u5200\uff0c\u5ffd\u7136\u4e00\u6325\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u5c01\u5f00\u4e86\u5bbd\u9762\u5200\uff0c\u5de6\u80a9\u4e00\u6c89\uff0c\u76f4\u649e\u8fc7\u6765\u3002 \u3000\u3000\u767d\u8863\u4eba\u672a\u6599\u5230\uff0c\u4ed6\u4f1a\u4ee5\u80a9\u76f8\u649e\uff0c\u95ea\u907f\u5df2\u6765\u4e0d\u53ca\uff0c\u8eab\u5b50\u4e00\u4fa7\uff0c\u907f\u5f00\u8981\u7a74\uff0c\u8eab\u5b50\u4e0d\u9000\u53cd\u8fdb\u3002 \u3000\u3000\u94c1\u7532\u6b66\u58eb\u4e09\u68f1\u5200\u4e00\u6536\u523a\u51fa\uff0c\u7a7f\u5411\u524d\u80f8\u3002 \u3000\u3000\u4e09\u68f1\u5200\u50cf\u4e00\u652f\u5229\u7bad\uff0c\u7a7f\u80f8\u800c\u8fc7\u3002 \u3000\u3000\u767d\u8863\u4eba\u624b\u4e2d\u7684\u5bbd\u9762\u5200\uff0c \u4e5f\u540c\u65f6\u5212\u5411\u4e86\u5bf9\u65b9\u7684\u54bd\u5589\u3002 \u4e24\u4e2a\u4eba\u540c\u65f6\u5012\u4e0b\u53bb\u3002 \u3000\u3000\u90fd\u4f24\u5728\u8981\u5bb3\u5730\u65b9\uff0c\u8c01\u4e5f\u6ca1\u6709\u53d1\u51fa\u4efb\u4f55\u4e00\u70b9\u58f0\u606f\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u4e07\u5251\u4f7f\uff0c\u4ed6\u4eec\u5f88\u52c7\u6562\uff0c\u90a3\u4e00\u5200\u4e5f\u5f88\u5947\u5e7b\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u2018\u7389\u77f3\u4ff1\u711a\u2019\u5148\u6709\u5fc5\u6b7b\u4e4b\u5fc3\uff0c \u624d\u80fd\u65bd\u5c55\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e07\u5251\u4f7f\u504f\u52b3\uff0c\u6211\u4eec\u8d70\u4e86\u3002\u201d \u3000\u3000\u5e26\u7740\u7ea2\u82b1\u3001\u7eff\u53f6\u3001\u4f55\u5bd2\u8863\uff0c\u7531\u5385\u540e\u800c\u53bb\u3002 \u3000\u3000\u7eff\u840d\u53d7\u547d\uff0c\u9886\u5bfc\u4e86\u4e5d\u5458\u5973\u5c06\u3002 \u3000\u3000\u5f53\u5e74\u9ad8\u5929\u5065\u4e0b\u4ee4\u8981\u9ad8\u5e7d\u5170\u7ec4\u8bad\u5341\u4e8c\u5973\u5c06\uff0c\u7528\u5fc3\u5c31\u5728\u7275\u5236\u795e\u5200\u5802\u592a\u8fc7\u81a8\u80c0\u7684\u52bf\u529b\uff0c\u8fd9\u4e00\u6279\u4eba\u624b\u9009\u62e9\u91cc\uff0c\u82b1\u4e86\u4e0d\u5c11\u7684\u5fc3\u8840\uff0c\u4e0d\u4f46\u8d44\u8d28\u597d\uff0c\u800c\u4e14\uff0c\u90fd\u8fd8\u8981\u6709\u76f8\u5f53\u7684\u6b66\u529f\u57fa\u7840\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u628a\u5979\u4eec\u8bad\u7ec3\u6210\u7b2c\u4e00\u6d41\u7684\u5973\u5251\u624b\u3002 \u3000\u3000\u5979\u4eec\u5b66\u4f1a\u4e86\u5929\u4e0b\u6700\u6076\u6bd2\u7684\u5251\u6cd5\u3002 \u3000\u3000\u7eff\u840d\u884c\u4e86\u8fc7\u6765\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e07\u5251\u4f7f\uff0c\u6211\u53eb\u7eff\u840d\uff0c\u5949\u9ad8\u59d1\u5a18\u4e4b\u547d\uff0c\u542c\u5019\u4f60\u7684\u4ee4\u8c15\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u7528\u5ba2\u6c14\uff0c\u54b1\u4eec\u8981\u5168\u529b\u4fdd\u4f4f\u94c1\u82b1\u9662\uff0c\u7275\u5236\u96f7\u65e0\u5f71\u3002\u201d \u3000\u3000\u7eff\u840d\u9053\uff1a\u201c\u52a0\u4e0a\u6211\uff0c\u6211\u4eec\u8fd8\u6709\u4e5d\u4e2a\u4eba\uff0c\u4e00\u4e2a\u62fc\u4e00\u4e2a\uff0c\u4e5f\u65e0\u6cd5\u628a\u4ed6\u4eec\u5168\u6570\u62fc\u5b8c\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u795e\u5200\u5802\u4e0d\u53ea\u8fd9\u4e8c\u5341\u51e0\u4e2a\u94c1\u7532\u6b66\u58eb\uff0c\u4e0d\u80fd\u548c\u4ed6\u4eec\u62fc\u547d\uff0c\u975e\u5fc5\u8981\uff0c\u6700\u597d\u80fd\u4fdd\u5168\u5b9e\u529b\u3002\u201d \u3000\u3000\u7eff\u840d\u9053\uff1a\u201c\u597d!\u6211\u548c\u4e5d\u4f4d\u59ca\u59b9\uff0c\u5bf9\u4ed8\u4e5d\u4e2a\u94c1\u7532\u6b66\u58eb\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u597d!\u4f59\u4e0b\u7684\u6211\u6765\u5bf9\u4ed8\u3002\u201d \u3000\u3000\u7eff\u840d\u98de\u8eab\u4e00\u8dc3\uff0c\u5411\u4e00\u4e2a\u94c1\u7532\u6b66\u58eb\u6251\u53bb\u3002 \u3000\u3000\u4eba\u8fd8\u672a\u5230\uff0c\u534a\u7a7a\u4e2d\u5251\u5149\u4e71\u95ea\u3002 \u3000\u3000\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u8fc7\u540e\uff0c\u91cd\u53c8\u6062\u590d\u4e86\u5e73\u9759\u3002 \u3000\u3000\u4e5d\u5973\u5c06\u653b\u51fa\u7684\u5251\u52bf\uff0c\u6ca1\u6709\u4f24\u5230\u4e00\u4e2a\u94c1\u7532\u6b66\u58eb\u3002 \u3000\u3000\u4e5d\u5973\u4e00\u51fb\u672a\u4e2d\uff0c\u53c8\u7acb\u523b\u98de\u8dc3\u56de\u6765\u3002 \u3000\u3000\u53cc\u65b9\u90fd\u65e0\u635f\u5931\u3002 \u3000\u3000\u4f46\u4e07\u80dc\u5374\u770b\u5f97\u5fc3\u5934\u4e00\u52a8\uff0c\u6697\u9053\uff1a \u201c\u6211\u82e6\u5fc3\u8bad\u7ec3\u6570\u5e74\u7684\u5200\u624b\uff0c\u6bd4\u8d77\u7b2c\u4e00\u5821\u4e2d\u7684\u5341\u4e8c\u5973\u5c06\uff0c\u4f3c\u4e4e\u662f\u8fd8\u7a0d\u6709\u900a\u8272\u4e86\u3002\u201d \u3000\u3000\u7eff\u840d\u7684\u52a8\u4f5c\u6700\u5feb\uff0c\u843d\u56de\u539f\u4f4d\u4e4b\u540e\uff0c\u7acb\u523b\u53c8\u98de\u8dc3\u800c\u8d77\uff0c\u6251\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u53cc\u624b\u5404\u6267\u4e00\u5251\uff0c\u5206\u523a\u4e00\u4e2a\u94c1\u7532\u6b66\u58eb\u7684\u53cc\u76ee\u3002 \u3000\u3000\u90a3\u94c1\u7532\u6b66\u58eb\uff0c\u4e09\u68f1\u5200\u5ffd\u7136\u4e00\u6325\uff0c\u7eff\u840d\u624b\u4e2d\u4e24\u652f\u5251\uff0c\u4e00\u9f50\u88ab\u6321\u9707\u5f00\u53bb\u3002 \u3000\u3000\u4f46\u53e6\u4e00\u9053\u5251\u5149\uff0c\u7a81\u7136\u98de\u51fa\u3002 \u3000\u3000\u7eff\u840d\u7528\u4e86\u7b2c\u4e09\u628a\u5251\u3002 \u3000\u3000\u5251\u5c16\u4e00\u95ea\uff0c\u5212\u8fc7\u4e86\u94c1\u7532\u6b66\u58eb\u7684\u54bd\u5589\u3002 \u3000\u3000\u7ea2\u5149\u8fdb\u5c04\uff0c\u94c1\u6b66\u58eb\u53c8\u5012\u4e0b\u53bb\u4e00\u4e2a\u3002 \u3000\u3000\u4f59\u4e0b\u7684\u94c1\u7532\u6b66\u58eb\uff0c\u4ecd\u7136\u4e0d\u505c\u7684\u5411\u524d\u884c\u53bb\uff0c \u3000\u3000\u9664\u975e\u628a\u4ed6\u4eec\u5168\u6570\u6740\u5149\uff0c\u4f3c\u4e4e\u662f\u6ca1\u6709\u529e\u6cd5\u80fd\u963b\u6b62\u4ed6\u4eec\u524d\u8fdb\u3002 \u3000\u3000\u94c1\u7532\u6b66\u58eb\u8fd8\u6709\u5341\u4e5d\u4e2a\u4eba\u3002 \u3000\u3000\u4e07\u80dc\u5df3\u660e\u767d\u4e86\uff0c\u8fd9\u94c1\u7532\u6b66\u58eb\uff0c\u7edd\u5bf9\u65e0\u6cd5\u529b\u654c\uff0c\u53ea\u6709\u667a\u53d6\u4e00\u9014\u3002 \u3000\u3000\u6c89\u601d\u4e4b\u95f4\uff0c\u7a81\u95fb\u4e00\u58f0\u60e8\u53eb\u4f20\u6765\uff0c\u4e00\u4e2a\u94c1\u7532\u6b66\u58eb\uff0c\u548c\u4e00\u4e2a\u7ea2\u8863\u5973\u5c06\uff0c\u4e00\u9f50\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u53c8\u662f\u4e00\u5bf9\u540c\u5f52\u4e8e\u5c3d\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e24\u4e2a\u7eff\u8863\u5973\u5c06\uff0c\u7ad9\u8d77\u8eab\u5b50\u6b65\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u4e07\u80dc\u5fc3\u4e2d\u660e\u767d\uff0c\u8fd9\u53c8\u662f\u4e00\u5e55\u540c\u5f52\u4e8e\u5c3d\u7684\u60e8\u5267\u3002 \u3000\u3000\u5979\u4eec\u627e\u51fa\u4e86\u94c1\u7532\u6b66\u58eb\u7684\u5f31\u70b9\uff0c\u4f46\u5374\u65e0\u6cd5\u51ed\u6b66\u529f\u6740\u4e86\u4ed6\u4eec\uff0c\u7389\u77f3\u4ff1\u711a\u7684\u6253\u6cd5\uff0c\u5374\u53ef\u4f7f\u53cc\u65b9\u5055\u4ea1\u3002 \u3000\u3000\u52c7\u6562\u7684\u5973\u4eba\uff0c\u4e07\u80dc\u4e0d\u5f97\u4e0d\u5728\u5fc3\u4e2d\u6697\u6697\u7684\u8d5e\u8d4f\u3002 \u3000\u3000\u7eff\u840d\u7a81\u7136\u5927\u58f0\u559d\u9053\uff1a\u201c\u56de\u6765\u3002\u201d \u3000\u3000\u4e24\u4e2a\u5973\u5b50\u4e00\u6014\uff0c\u4e24\u4e2a\u94c1\u7532\u6b66\u58eb\u7684\u4e09\u68f1\u5200\u5df2\u75be\u523a\u4e86\u8fc7\u6765\uff0c\u7531\u524d\u80f8\u76f4\u900f\u540e\u80cc\u3002 \u3000\u3000\u7eff\u840d\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u4e07\u80dc\u4f4e\u58f0\u9053\uff1a\u201c\u7eff\u840d\u59d1\u5a18\uff0c\u53cc\u65b9\u90fd\u662f\u9ad8\u624b\uff0c\u5728\u52a8\u624b\u65f6\uff0c\u4e0d\u80fd\u8f7b\u6613\u559d\u53eb\u5979\u4eec\uff0c\u90a3\u4f1a\u4f7f\u5979\u5206\u795e\u3002\u201d \u3000\u3000\u4eba\u5f71\u98de\u8d77\uff0c\u4e24\u4e2a\u7ea2\u8863\u5973\u5b50\uff0c\u7a81\u7136\u8fce\u5411\u4e24\u4e2a\u94c1\u7532\u6b66\u58eb\u3002 \u3000\u3000\u7830\u7136\u4e00\u58f0\uff0c\u706b\u7130\u98de\u5c04\uff0c\u6bd2\u6c41\u56db\u6e85\uff0c \u3000\u3000\u4e0d\u77e5\u4f55\u4eba\u53d1\u5c04\u4e86\u4e00\u7b52\u201c\u6c34\u706b\u65e0\u60c5\u201d\u3002 \u3000\u3000\u8fd9\u4e9b\u94c1\u7532\u6b66\u58eb\u867d\u7136\u4e0d\u754f\u5200\u5251\uff0c\u4f46\u5728\u6bd2\u706b\u3001\u6bd2\u6c41\u4e4b\u4e0b\uff0c\u5374\u5bf9\u4ed6\u4eec\u53d1\u751f\u6781\u5927\u7684\u5a01\u80c1\u3002 \u3000\u3000\u4f46\u95fb\u4e24\u58f0\u60e8\u53eb\uff0c\u4e24\u4e2a\u94c1\u7532\u6b66\u58eb\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e07\u80dc\u7684\u5c5e\u4e0b\uff0c\u548c\u7eff\u8863\u7684\u5973\u5c06\u4eec\uff0c\u4e00\u9f50\u51b2\u4e86\u51fa\u6765\u3002 \u3000\u3000\u53cc\u65b9\u5c55\u5f00\u4e86\u4e00\u573a\u6fc0\u70c8\u7684\u6df7\u6218\u3002 \u3000\u3000\u8fd9\u5730\u65b9\u5f88\u4e0d\u9002\u5408\u94c1\u7532\u6b66\u58eb\uff0c\u56e0\u4e3a\u5ead\u9662\u4e2d\u957f\u6ee1\u4e86\u82b1\u6811\u3002 \u3000\u3000\u4e07\u80dc\u5e26\u6765\u7684\u5200\u624b\u5f88\u795e\u52c7\uff0c\u4e5f\u8bb8\u8fd9\u4e9b\u5973\u5b69\u7684\u52c7\u6562\uff0c\u6fc0\u8d77\u4e86\u4ed6\u4eec\u7684\u6597\u5fd7\u3002 \u3000\u3000\u8fd9\u662f\u60b2\u70c8\u7684\u4e00\u6218\u3002 \u3000\u3000\u6240\u6709\u94c1\u7532\u6b66\u58eb\uff0c\u90fd\u88ab\u6482\u5012\u4e86\uff0c\u4f46\u7eff\u840d\u7387\u9886\u7684\u7ea2\u3001\u7eff\u5973\u5c06\uff0c\u4e5f\u53ea\u4f59\u4e0b\u4e24\u4e2a\uff0c\u52a0\u4e0a\u5979\uff0c\u4e09\u4e2a\u4eba\uff0c\u4e07\u80dc\u7684\u767d\u8863\u5200\u624b\uff0c\u4e5f\u53ea\u4f59\u4e0b\u4e86\u56db\u4e2a\u3002 \u3000\u3000\u7b97\u8d77\u6765\uff0c\u62fc\u5012\u4e09\u4e2a\u94c1\u7532\u6b66\u58eb\uff0c\u4ed6\u4eec\u4e5f\u6709\u4e24\u4e2a\u4eba\u5012\u4e86\u4e0b\u3002 \u3000\u3000\u50cf\u4e07\u80dc\u8fd9\u6837\u7684\u5200\u624b\uff0c\u4e5f\u770b\u5f97\u4e3a\u4e4b\u5fc3\u9178\u3002\u4e0d\u81ea\u7981\u6ef4\u4e0b\u4e24\u884c\u6cea\u6c34\u3002 \u3000\u3000\u7eff\u840d\u6ca1\u6709\u6d41\u6cea\uff0c\u53ea\u662f\u9759\u9759\u7684\u7ad9\u7740\u3002 \u3000\u3000\u5979\u4eec\u672c\u5c31\u662f\u9ad8\u5929\u5065\u51c6\u5907\u7275\u5236\u795e\u5200\u5802\u7684\u4e00\u652f\u4f0f\u5175\uff0c\u4ecd\u7136\u662f\u7528\u6765\u5bf9\u4ed8\u4e86\u795e\u5200\u5802\u7684\u94c1\u7532\u6b66\u58eb\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u4e00\u76f4\u6ca1\u6709\u51fa\u73b0\u3002 \u3000\u3000\u4e5f\u8bb8\uff0c\u4ed6\u4e0b\u4ee4\u94c1\u7532\u6b66\u58eb\u5c55\u5f00\u653b\u52bf\u4e4b\u540e\uff0c\u4eba\u5c31\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u7ecf\u8fc7\u4e00\u79cd\u4e25\u683c\u7684\u8bad\u7ec3\u4e4b\u540e\uff0c\u5973\u4eba\uff0c\u4f3c\u662f\u5e76\u4e0d\u6bd4\u7537\u4eba\u602f\u5f31\u3002 \u3000\u3000\u795e\u5200\u5802\u5e76\u6ca1\u6709\u7b2c\u4e8c\u6279\u5200\u624b\u8d76\u6765\uff0c\u5200\u5149\u5251\u5f71\u3001\u8840\u8089\u6e85\u98de\u7684\u5927\u6218\u4e4b\u540e\uff0c\u6062\u590d\u4e86\u4e00\u6bb5\u6682\u65f6\u7684\u5e73\u9759\u3002 \u3000\u3000\u7eff\u840d\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e07\u5251\u4f7f\uff0c\u6211\u4eec\u73b0\u5728\u5e94\u8be5\u5982\u4f55?\u201d \u3000\u3000\u5979\u4eec\u63a5\u53d7\u5947\u5e7b\u5251\u8bad\u7ec3\uff0c\u51b7\u9177\u7684\u6740\u4eba\u624b\u6cd5\uff0c\u4f46\u5979\u4eec\u5374\u6ca1\u6709\u5b66\u8fc7\u5982\u4f55\u5904\u7406\u4e8b\u60c5\u3002 \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u5e26\u5979\u4eec\u597d\u597d\u5750\u606f\u4e00\u4e0b\uff0c\u4e5f\u8bb8\uff0c\u6211\u4eec\u5f88\u5feb\u53c8\u8981\u6709\u4e00\u573a\u640f\u6740\u3002\u201d \u3000\u3000\u6b8b\u5c06\u3001\u6b8b\u5175\uff0c\u4e00\u65f6\u95f4\uff0c\u4e07\u80dc\u4e5f\u65e0\u6cd5\u51b3\u5b9a\u8be5\u5982\u4f55\u884c\u52a8\u3002 \u3000\u3000\u6700\u597d\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u518d\u7b49\u4e0b\u53bb\u3002 \u3000\u3000\u666f\u7269\u51c4\u51c9\uff0c\u5c38\u4f53\u6a2a\u9648\uff0c\u6fc0\u70c8\u7684\u640f\u6740\u751f\u6b7b\u77ac\u606f\uff0c\u4f7f\u4eba\u611f\u89c9\u5230\u65f6\u95f4\u5f88\u957f\uff0c\u5176\u5b9e\uff0c\u8fd8\u4e0d\u8fc7\u8fb0\u65f6\u5149\u666f\u3002 \u3000\u3000\u5e78\u597d\uff0c\u9ec4\u7075\u51fa\u73b0\u4e86\uff0c \u7d27\u63a5\u7740\uff0c \u51e0\u4eba\u90fd\u53d7\u4e86\u4f24\uff0c\u867d\u7136\u5df2\u7ecf\u5305\u624e\u4f46\u4ecd\u7136\u88ab\u9c9c\u8840\u67d3\u900f\u3002 \u3000\u3000\u4e07\u80dc\u8fce\u4e86\u4e0a\u53bb\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u5c60\u5251\u4f7f\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u6b7b\u4e86\uff0c\u6b7b\u5f97\u5f88\u58ee\u70c8\uff0c\u4e00\u4e2a\u4eba\u51b2\u4e0a\u53bb\uff0c\u5835\u4f4f\u4e86\u4e09\u7b52\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u8981\u4e0d\u7136\uff0c\u6211\u4eec\u90fd\u4f1a\u6b7b\u5728\u90a3\u4e09\u7b52\u2018\u6c34\u706b\u65e0\u60c5\u2019\u7684\u6697\u88ad\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u82e6\u7b11\u4e00\u4e0b\uff0c\u63a5\u9053\uff1a\u201c\u4e07\u5144\uff0c\u4f60\u8bad\u7ec3\u7684\u5200\u624b\uff0c\u5168\u6b7b\u4e86?\u201d \u3000\u3000\u9ad8\u4e07\u80dc\u9053\uff1a\u201c\u4ed6\u4eec\u5f88\u52c7\u6562\uff0c\u50cf\u5e7d\u5170\u59d1\u5a18\u7684\u5341\u4e8c\u5973\u5c06\u4e00\u6837\uff0c\u706b\u62fc\u4e86\u94c1\u7532\u6b66\u58eb\uff0c\u6211\u8fd9\u91cc\u8fd8\u4f59\u4e0b\u56db\u4e2a\u4eba\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5979\u4eec\u5168\u6570\u6218\u6b7b\u4e86?\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a\u201c\u8fd8\u6709\u4e09\u4e2a\uff0c\u6211\u8981\u5979\u4eec\u5728\u8c03\u606f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u7ea2\u82b1\u3001\u7eff\u53f6\uff0c\u4e24\u7ec4\u5251\u624b\u8fd8\u6ca1\u6709\u53d1\u6325\u771f\u6b63\u7684\u5a01\u529b\u51fa\u6765\uff0c\u5341\u4e8c\u5973\u5c06\uff0c\u5df2\u7ecf\u6b7b\u53bb\u4e86\u4e03\u4e2a\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u6211\u8eab\u7ecf\u767e\u6218\uff0c\u4ece\u6ca1\u7ecf\u5386\u5982\u6b64\u523a\u6fc0\u7684\u640f\u6740\uff0c\u4e5f\u6ca1\u6709\u89c1\u8fc7\u50cf\u5e7d\u5170\u59d1\u5a18\u90a3\u6837\u51cc\u5389\u7684\u6740\u4eba\u4e4b\u5251\u6cd5\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\u7684\u5feb\u5251\u3001\u6697\u5668\uff0c\u6bd4\u6211\u8fd8\u8981\u9ad8\u660e\u4e00\u4e9b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u8fd9\u624d\u662f\u5f00\u59cb\uff0c\u4e0d\u662f\u7ed3\u675f\uff0c\u795e\u5200\u5802\u4e2d\uff0c\u8fd8\u4e0d\u77e5\u9053\u6709\u591a\u5c11\u6740\u624b\uff0c\u5e78\u597d\uff0c\u54b1\u4eec\u6bc1\u4e86\u2018\u6c34\u706b\u65e0\u60c5\u2019\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u4e00\u6218\uff0c\u4e24\u4f4d\u51fa\u529b\u6700\u5927\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e8b\u5b9e\u4e0a\uff0c\u5821\u4e3b\u8eab\u624b\u6700\u77eb\u5065\uff0c\u6bcf\u4e00\u6b21\u90fd\u5f53\u5148\u5192\u9669\u3002\u201d \u534e\u5999\u771f\u9053\uff1a \u201c\u8fd9\u4e00\u6218\uff0c\u54b1\u4eec\u4f24\u4ea1\u867d\u5927\uff0c\u4f46\u603b\u7b97\u60e8\u80dc\u4e86\uff0c\u4f46\u4e0d\u77e5\u795e\u5200\u5802\u4e2d\u8fd8\u6709\u591a\u5c11\u53ef\u6015\u7684\u6740\u624b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5947\u602a\u7684\u662f\uff0c\u9ad8\u5929\u5065\u7adf\u672a\u51fa\u73b0\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u4ed6\u8fd8\u76f4\u63a5\u63a7\u5236\u7740\u4e00\u6279\u9ad8\u624b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54b1\u4eec\u9700\u8981\u4f11\u606f\u4e00\u4e0b\u2026\u2026\u201d \u3000\u3000\u53ea\u89c1\u4eba\u5f71\u95ea\u52a8\uff0c\u7ea2\u82b1\u98de\u5954\u800c\u5165\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6709\u4eba\u6765\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u5b50?\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u4e00\u4e2a\u8001\u5934\u5b50\uff0c\u4e00\u4e2a\u8001\u592b\u4eba\uff0c\u8fd8\u6709\u4e00\u4e2a\u7384\u8863\u4e3d\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u8863\u4e3d\u4eba\uff0c\u53ef\u6709\u5973\u5a62\u76f8\u968f?\u201d \u3000\u3000\u7ea2\u82b1\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u770b\u5230\uff0c\u7eff\u53f6\u5b88\u5728\u90a3\u91cc\uff0c\u6211\u6765\u4f20\u8baf\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u544a\u8bc9\u7eff\u53f6\uff0c\u4e0d\u53ef\u8f7b\u6613\u51fa\u624b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u8ba4\u8bc6\u5979\u4eec?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u60f3\u53ef\u80fd\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u53bb\u544a\u8bc9\u7eff\u53f6\uff0c\u4e0d\u53ef\u968f\u4fbf\u51fa\u624b\uff0c\u8ba9\u4ed6\u4eec\u8fdb\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1b\u201c\u8bf8\u4f4d\u5148\u5404\u627e\u4e00\u5904\u5730\u65b9\uff0c\u9690\u8d77\u6212\u5907\uff0c\u4e5f\u8bb8\u5728\u4e0b\u5224\u65ad\u6709\u8bef\u3002\u201d \u3000\u3000\u7ea2\u82b1\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u7f13\u6b65\u884c\u5165\u4e86\u94c1\u82b1\u9662\u3002 \u3000\u3000\u9ec4\u7075\u679c\u7136\u662f\u8ba4\u8bc6\u3002 \u3000\u3000\u6d3b\u7740\u7684\u56db\u5927\u5251\u4f7f\uff0c\u4e5f\u90fd\u8ba4\u8bc6\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u5c1a\u5929\u4e49\u592b\u5987\u548c\u7384\u5a18\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u4eba\uff0c\u90fd\u662f\u9ec4\u7075\u7684\u751f\u6b7b\u5bf9\u5934\uff0c\u4e14\u90fd\u5b58\u6709\u4e86\u6740\u6b7b\u9ec4\u7075\u4e4b\u5fc3\u3002 \u3000\u3000\u9ec4\u7075\u671b\u4e86\u4e09\u4eba\u4e00\u773c\uff0c\u53ea\u597d\u82e5\u7b11\u3002 \u3000\u3000\u5c1a\u5929\u4e49\u52a0\u811a\u6b65\uff0c\u884c\u8fd1\u9ec4\u7075\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u611f\u89c9\u5230\u5f88\u610f\u5916\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u4eec\u53d7\u7b2c\u4e00\u5821\u4e4b\u9080\uff0c\u8d76\u6765\u6b64\u5730\u52a9\u62f3\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u5bf9!\u540c\u65f6\uff0c\u6211\u4eec\u4e5f\u6536\u5230\u4e86\u5c11\u6797\u638c\u95e8\u4eba\u7684\u9080\u8bf7\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u63a5\u9053\uff1a\u201c\u4e24\u5f20\u8bf7\u8d34\uff0c\u4e00\u4e2a\u4f1a\u9762\u7684\u5730\u70b9\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u4e24\u4f4d\u662f\u5426\u5df2\u6709\u6289\u62e9\u4e86\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a\u201c\u5bf9!\u6211\u4eec\u5df2\u7ecf\u6709\u6240\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u76ee\u5149\u8f6c\u5230\u7384\u5a18\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u5462?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6211\u53ea\u63a5\u5230\u4e86\u4e00\u4e2a\u9080\u7ea6\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u7684?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u5821\u4e3b\u9ad8\u5929\u5065\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u5c31\u5f88\u660e\u663e\u4e86\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u4f60\u7684\u9ebb\u70e6\u592a\u591a\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c \u4f60\u6069\u6028\u7ea0\u7f20\uff0c\u60c5\u5b7d\u7ed5\u8eab\uff0c\u5148\u51b3\u5b9a\u4e86\u4f60\u548c\u5c1a\u8001\u524d\u8f88\u7684\u4e8b\uff0c\u6211\u4eec\u518d\u4f5c\u51b3\u5b9a\u5982\u4f55?\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u5c1a\u524d\u8f88\uff0c\u6709\u4ec0\u4e48\u6253\u7b97\uff0c\u53ef\u4ee5\u5429\u5490\u4e0b\u6765\u4e86\u3002\u201d \u5c1a\u5929\u4e49\u56db\u987e\u4e86\u4e00\u773c\u9053\uff1a\u201c\u4f60\u7684\u4eba\u624b\u5462?\u201d \u3000\u3000\u539f\u6765\uff0c\u6240\u6709\u7684\u4eba\uff0c\u90fd\u85cf\u4e86\u8d77\u6765\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u4eec\u7684\u4f24\u4ea1\u5f88\u60e8\u91cd\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u671b\u671b\u56db\u4e0b\u6a2a\u9648\u7684\u5c38\u4f53\uff0c\u9053\uff1a \u201c\u6211\u770b\u5f97\u51fa\u6765\uff0c\u8fd9\u640f\u6740\u5f88\u60e8\u70c8\u3002\u201d \u5c1a\u592b\u4eba\u9053\uff1a\u201c\u4f60\u597d\u50cf\u5f88\u5b64\u5355\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5929\u5065\u7b2c\u4e00\u5821\u4e2d\u7684\u5b9e\u529b\u592a\u8fc7\u5f3a\u5927\uff0c\u5f3a\u5927\u5230\u51fa\u4eba\u610f\u6599\u4e4b\u5916\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u5e78\u597d\uff0c\u6211\u4eec\u592b\u5987\u8d76\u6765\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u8001\u524d\u8f88\u7684\u76db\u60c5\uff0c\u9ec4\u7075\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u597d!\u8c08\u5b8c\u4e86\u4f60\u7684\u7b2c\u4e00\u4ef6\u9ebb\u70e6\u4e8b\uff0c\u73b0\u5728\uff0c\u54b1\u4eec\u53ef\u4ee5\u8c08\u8c08\u6211\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u6211\u53d1\u89c9\u4e86\u4e00\u4ef6\u4e8b\uff0c \u4f60\u4f3c\u4e4e\u662f\u5f88\u8ba4\u771f\u5728\u4e3a\u6c5f\u6e56\u540c\u9053\u5c3d\u529b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\u7684\u610f\u601d\u662f\u2026\u2026?\u201d \u3000\u3000\u7384\u5a18\u63a5\u9053\uff1a\u201c\u4f60\u8fdb\u5165\u4e86\u7b2c\u4e00\u5821\u4e2d\uff0c\u800c\u4e14\uff0c\u4e5f\u548c\u4ed6\u4eec\u6b63\u5f0f\u51b2\u7a81\uff0c\u8fd9\u4e2a\u8c6a\u58ee\u4e4b\u6c14\uff0c\u4f7f\u6211\u5fc3\u6298\uff0c\u9ec4\u7075\u8fd8\u662f\u548c\u8fc7\u53bb\u4e00\u6837\uff0c\u505a\u4e00\u4e9b\u60ca\u5929\u52a8\u5730\u7684\u5927\u4e8b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8c22\u8c22\u4f60\uff0c\u7384\u5a18\uff0c\u6709\u4f60\u8fd9\u51e0\u53e5\u8bdd\uff0c\u7ed9\u4e86\u6211\u5f88\u5927\u7684\u9f13\u52b1\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6211\u60f3\u4e86\u5f88\u4e45\uff0c\u624d\u4e0b\u4e86\u51b3\u5fc3\u6765\uff0c\u52a9\u4f60\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u771f\u7684\u5f88\u611f\u6fc0\uff0c\u4e0d\u8fc7\uff0c\u7384\u5a18\uff0c\u8fd9\u5b9e\u5728\u592a\u5371\u9669\uff0c\u6211\u5b8c\u5168\u6ca1\u6709\u628a\u63e1\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u6d88\u606f\u544a\u8bc9\u4f60\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u4e5f\u4e0d\u6562\u8bf4\u662f\u597d\u662f\u574f?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ec0\u4e48\u6d88\u606f?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u522b\u5bc4\u671b\u4f60\u7684\u63f4\u624b\u4f1a\u5230\uff0c\u4f46\u4ed6\u4eec\u4e5f\u66ff\u4f60\u963b\u6321\u4e86\u63f4\u52a9\u7b2c\u4e00\u5821\u7684\u5f3a\u654c\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u7b2c\u4e00\u5821\u5df2\u53ec\u96c6\u4e86\u5c5e\u4e0b\u56de\u805a\u4e8e\u6b64\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u56db\u5927\u9556\u5c40\u5916\uff0c\u8fd8\u6709\u591a\u6570\u7684\u6c5f\u6e56\u4eba\u7269\uff0c\u90fd\u5df2\u5f00\u59cb\u5411\u6b64\u5730\u96c6\u4e2d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u522b\u5e0c\u671b\u4f1a\u6709\u5f88\u591a\u4eba\u6765\u52a9\u4f60\uff0c\u4e5f\u8bb8\uff0c\u53ea\u6709\u6211\u4eec\u56db\u4e2a\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u54c8\u54c8\u4e00\u7b11\u9053\uff1a \u201c\u597d\uff0c\u521d\u5ea6\u63a5\u89e6\uff0c\u6211\u4eec\u5df2\u6d88\u706d\u4e86\u6700\u53ef\u6015\u7684\u2018\u6c34\u706b\u65e0\u60c5\u2019\u3002\u201d \u671b\u671b\u7384\u5a18\u8eab\u8fb9\u7684\u4e2b\u5934\uff0c\u63a5\u9053\uff1a\u201c\u7384\u5a18\uff0c\u8fd9\u4f4d\u5c0f\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u7384\u5a18\u63a5\u9053\uff1a\u201c\u5979\u8ddf\u7740\u6211\uff0c\u4e5f\u662f\u6211\u7684\u597d\u5e2e\u624b\uff0c\u6211\u4eec\u4e24\u4e2a\u7b97\u662f\u4e00\u7ec4\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e24\u4f4d\u8001\u524d\u8f88\uff0c\u6709\u4ec0\u4e48\u5429\u5490\uff0c\u53ea\u7ba1\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u5165\u5883\u968f\u4fd7\uff0c\u4f60\u662f\u8fd9\u7fa4\u4eba\u4e2d\u7684\u4e3b\u5e05\uff0c\u54b1\u4eec\u4e00\u5207\u542c\u5019\u5429\u5490\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u76ee\u4e0b\u7b2c\u4e00\u5821\u4e2d\u7684\u60c5\u51b5\uff0c\u6211\u4eec\u8fd8\u4e0d\u592a\u4e86\u89e3\uff0c\u4e5f\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u4ed6\u4eec\u4f1a\u5f00\u59cb\u653b\u88ad\uff0c\u4e24\u4f4d\u8001\u524d\u8f88\u5c31\u7b97\u662f\u603b\u63a5\u5e94\u5427\uff0c\u90a3\u91cc\u9700\u8981\u63f4\u624b\u65f6\uff0c\u4e24\u4f4d\u5c31\u5230\u54ea\u91cc\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u554a!\u4f60\u8fd9\u4e48\u770b\u5f97\u8d77\u6211\u4eec\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5c1a\u8001\u592b\u4eba\u8a00\u91cd\u4e86\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6211\u5462?\u4f60\u4e5f\u6d3e\u4e2a\u804c\u53f8\u5427\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u8fd9\u91cc\u6ca1\u6709\u7279\u5b9a\u7684\u804c\u53f8\uff0c\u654c\u4eba\u5982\u4f55\u6765\uff0c\u6211\u4eec\u5982\u4f55\u6321\u3002\u201d\u7384\u5a18\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u90a3\u4e5f\u597d\uff0c\u6211\u4eec\u81ea\u5df1\u51b3\u5b9a\u884c\u52a8\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6b64\u95f4\u4e8b\u4e86\u4e4b\u540e\uff0c\u6211\u4f1a\u7ed9\u4f60\u4e00\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4f60\u6709\u628a\u63e1\u6d3b\u4e0b\u53bb?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u7384\u5a18\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5728\u4e0b\u53ea\u8981\u6709\u4e00\u53e3\u6c14\u5728\uff0c\u6211\u90fd\u4f1a\u5c65\u884c\u8bfa\u8a00\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u5982\u662f\u6211\u6b7b\u4e86\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u5728\u7b2c\u4e00\u5821\u4e2d\u8fd9\u4e2a\u5730\u65b9\uff0c\u9ec4\u7075\u5b9e\u4e0d\u6562\u8bf4\uff0c\u5927\u5bb6\u6709\u51e0\u5206\u6d3b\u547d\u7684\u5e0c\u671b\u3002 \u3000\u3000\u54ac\u54ac\u7259\uff0c\u9ec4\u7075\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e0d\u5e78\u4f60\u6b7b\u4e86\uff0c\u5982\u662f\u6211\u8fd8\u6d3b\u7740\uff0c\u6211\u4e5f\u4f1a\u7ed9\u4f60\u4e2a\u4ea4\u4ee3\uff0c\u8ba9\u4f60\u6b7b\u5f97\u7791\u76ee\u4e5d\u6cc9\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u7384\u5a18\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ef\u8981\u542c\u6211\u7684\u6761\u4ef6?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u5148\u627f\u8bfa\uff0c\u6211\u8bf4\u4e86\uff0c\u5c31\u4e0d\u4f1a\u6539\u53d8\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u53ea\u6709\u4e00\u4ef6\u4e8b\u4e0d\u80fd\u7b54\u5e94\uff0c\u73b0\u5728\uff0c\u6211\u4e0d\u80fd\u6b7b\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u53ef\u4ee5\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u5728\u4e0b\u65e0\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u4f60\u542c\u7740\uff0c\u7b2c\u4e00\u3001\u5982\u662f\u8fd9\u4e00\u6218\u4e2d\uff0c\u4f60\u6ca1\u88ab\u4eba\u6740\u6b7b\uff0c\u90a3\u5c31\u4e0d\u8bb8\u6b7b\uff0c\u6c5f\u6e56\u4e0a\uff0c\u8fd8\u6709\u5f88\u591a\u4e8b\u7b49\u4f60\u53bb\u505a\uff0c\u7b2c\u4e8c\u3001\u6211\u5982\u4e5f\u6ca1\u6709\u6b7b\uff0c\u4f60\u8981\u7528\u82b1\u8f7f\u628a\u6211\u5a36\u8fc7\u53bb\uff0c\u800c\u4e14\uff0c\u6211\u73b0\u5728\u5c31\u8981\u4e24\u4e2a\u5927\u5a92\u4eba\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u8001\u592b\u5982\u4f55?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u591a\u8c22\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a\u201c\u8001\u5934\u5b50\uff0c\u6211\u770b\uff0c\u54b1\u4eec\u4e0d\u80fd\u505a\u5a92\u4eba\uff1f\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a\u201c\u54b1\u4eec\u7684\u5e74\u9f84\uff0c\u5e94\u8be5\u4f5c\u6cf0\u5c71\uff0c\u6cf0\u6c34\u4e86\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u597d\u554a!\u7384\u5a18\uff0c\u4f60\u53ef\u613f\u8ba4\u5728\u8001\u592b\u7684\u819d\u4e0b\u5f80\u540e\uff0c\u5982\u662f\u9ec4\u7075\u518d\u559c\u65b0\u538c\u65e7\uff0c\u4f60\u4e5f\u591a\u4e00\u4e2a\u5174\u5e08\u95ee\u7f6a\u7684\u4eba\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1b\u201c\u4e49\u7236\u6bcd\u5728\u4e0a\uff0c\u53d7\u5973\u513f\u4e00\u62dc\u3002\u201d \u3000\u3000\u76c8\u76c8\u62dc\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u56db\u5927\u5251\u4f7f\uff0c\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\uff0c\u4e00\u9f50\u51fa\u73b0\uff0c\u62b1\u62f3\u4f5c\u793c\u9053\uff1a\u201c\u606d\u559c\u4e09\u4f4d\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u7384\u5a18\uff0c\u6211\u7684\u58f0\u8a89\u5728\u6c5f\u6e56\u4e0a\u4e0d\u592a\u597d\uff0c\u4e0d\u77e5\u9053\u6211\u53ef\u4e0d\u53ef\u4ee5\u505a\u5a92\u4eba?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4f60\u8981\u8ddf\u6211\u4e00\u9f50\u5ac1\u8fc7\u53bb\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u8ddf\u6211\u4e00\u8d77\uff0c\u5ac1\u7ed9\u9ec4\u7075\uff0c\u6211\u4e49\u7236\u6bcd\uff0c\u4e5f\u591a\u4e86\u4e00\u4e2a\u5e72\u5973\u513f\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u7384\u5a18\uff0c\u4f60\u5728\u8bf4\u4ec0\u4e48\u4e8b?\u4ed6\u662f\u5821\u4e3b\uff0c\u4e5f\u662f\u6211\u7684\u4e0a\u53f8\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f46\u4ed6\u662f\u7537\u4eba\uff0c\u4f60\u662f\u5973\u4eba\uff0c\u7537\u5927\u5f53\u5a5a\uff0c\u5973\u5927\u5f53\u5ac1\uff0c\u8fd9\u6709\u4ec0\u4e48\u4e0d\u5bf9?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u5bf9\u4e0d\u5bf9\u7684\u4e8b\uff0c\u800c\u662f\u5b8c\u5168\u4e0d\u53ef\u80fd\u7684\u4e8b\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u534e\u5927\u59ca\uff0c\u6ca1\u6709\u4ec0\u4e48\u4e0d\u53ef\u80fd\u7684\u4e8b\uff0c\u6211\u539f\u6765\u5f88\u6068\u4ed6\uff0c\u6211\u82e6\u7ec3\u5404\u79cd\u6b66\u529f\uff0c\u53ea\u6709\u4e00\u4e2a\u76ee\u7684\uff0c\u90a3\u5c31\u662f\u6740\u4e86\u4ed6\uff0c\u73b0\u5728\uff0c\u4e0d\u4f46\u4e0d\u6740\u4ed6\u4e86\uff0c\u800c\u4e14\uff0c\u8fd8\u51c6\u5907\u5ac1\u7ed9\u4ed6\uff0c\u4f60\u8bf4\u8fd9\u6709\u4ec0\u4e48\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\u4e0d\u5bf9?\u201c \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u597d!\u4e16\u4e8b\u53d8\u5e7b\uff0c\u83ab\u53ef\u9884\u6d4b\uff0c\u6628\u662f\u4eca\u975e\uff0c\u5168\u5728\u5fc3\u5ff5\u4e4b\u95f4\uff0c\u6240\u4ee5\uff0c\u6211\u5e0c\u671b\u534e\u5927\u59ca\uff0c\u4e0d\u7528\u575a\u6301\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u7384\u5a18\uff0c\u8fd9\u5b8c\u5168\u662f\u5728\u80e1\u95f9\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4e0d!\u4e00\u70b9\u4e5f\u4e0d\u80e1\u95f9\uff0c\u6211\u53ea\u662f\u5728\u60f3\u6e05\u4f60\u5e2e\u52a9\u6211\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u6709\u4ec0\u4e48\u9700\u8981\u5c0f\u59b9\u5e2e\u5fd9\u4e4b\u5904\uff0c\u4f60\u53ea\u8981\u5429\u5490\u4e00\u58f0\uff0c\u5c0f\u59b9\u65e0\u4e0d\u5168\u529b\u4ee5\u8d74\uff0c\u50cf\u8fd9\u79cd\u73a9\u7b11\uff0c\u5f00\u7684\u672a\u514d\u4e5f\u6709\u4e9b\u8fc7\u706b\u4e86\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4e0d\u662f\u73a9\u7b11\uff0c\u800c\u662f\u8bf4\u5f97\u5f88\u8ba4\u771f\uff0c\u6211\u8981\u4f60\u5e2e\u5fd9\u7684\uff0c\u4e5f\u5c31\u662f\u8fd9\u4e9b\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4f60\u5012\u8bf4\u8bf4\u770b\uff0c\u8fd9\u7b97\u4ec0\u4e48\u5e2e\u5fd9\u5462?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u9ec4\u7075\u98ce\u6d41\u6210\u6027\uff0c\u867d\u8bf4\u4ed6\u73b0\u5728\uff0c\u5df2\u7ecf\u6539\u8fc7\u81ea\u65b0\u4e86\uff0c\u4f46\u8c01\u6562\u4fdd\u8bc1\u4ed6\u4e0d\u4f1a\u6545\u6001\u590d\u840c\u800c\u53c8\u8272\u8d77\u6765\u4e86\u5462?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8fd9\u548c\u6211\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u6211\u53c8\u80fd\u5e2e\u4e9b\u4ec0\u4e48\u5fd9?\u201c\u7384\u5a18\u9053\uff1a \u201c\u5982\u534e\u5927\u59ca\u548c\u6211\u5408\u529b\u540c\u5fc3\u7ba1\u5236\u4ed6\uff0c\u4eca\u540e\uff0c\u4ed6\u5982\u6562\u518d\u80e1\u95f9\uff0c\u54b1\u4eec\u4e24\u4e2a\u5c31\u5408\u529b\u628a\u4ed6\u6740\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4f60\u8d8a\u8bf4\u8d8a\u4e0d\u50cf\u8bdd\u4e86\uff0c\u7384\u5a18\u3002\u201d \u3000\u3000\u9ec4\u7075\u76b1\u7740\u7709\u5934\u8bf4\u9053\uff1a \u201c\u534e\u59d1\u5a18\uff0c\u4f60\u5c31\u522b\u518d\u4e89\u6267\u4e86\uff0c\u8fd9\u65f6\u523b\uff0c\u54b1\u4eec\u968f\u65f6\u53ef\u4ee5\u62da\u547d\uff0c\u8001\u5b9e\u8bf4\uff0c\u5377\u5165\u4e86\u8fd9\u4e00\u573a\u7ea0\u7eb7\u7684\u4eba\uff0c\u53ea\u6015\u5f88\u5c11\u6709\u4eba\u80fd\u591f\u6d3b\u7740\u79bb\u5f00\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u53f9\u53e3\u6c14\uff0c\u4e0d\u518d\u591a\u8a00\u3002 \u3000\u3000\u7384\u5a18\u5374\u903c\u6ce8\u7740\uff0c\u8bf4\u9053\uff1a \u201c\u534e\u5927\u59ca\uff0c\u4f60\u4e0d\u4e89\u6267\uff0c\u90a3\u5c31\u8868\u793a\uff0c\u4f60\u5df2\u7ecf\u7b54\u5e94\u4e86\u3002\u201d \u534e\u5999\u771f\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u968f\u4f60\u7684\u4fbf\u5427!\u4f60\u6446\u5e03\u6211\uff0c\u73b0\u5728\uff0c\u53ea\u597d\u8ba4\u4e86\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u90a3\u662f\u8bf4\u4f60\u5fc3\u6709\u4e0d\u7518\uff0c\u8fc7\u4e86\u4eca\u5929\u4e4b\u540e\uff0c\u51c6\u5907\u548c\u5c0f\u59b9\u7b97\u8d26\u4e86\u3002\u201d \u534e\u5999\u771f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7384\u5a18\uff0c\u591f\u4e86\uff0c\u522b\u518d\u903c\u6211\u5566\u3002\u201d \u3000\u3000\u7384\u5a18\u7b11\u4e00\u7b11\uff0c\u5374\u8f6c\u671b\u7740\u9ec4\u7075\u8bf4\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u600e\u4e48\u8bf4?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u542c\u4f60\u5429\u5490\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u90a3\u4f60\u662f\u7b54\u5e94\u4e86\uff0c\u4e0d\u8bba\u4f60\u60f3\u4ec0\u4e48\u529e\u6cd5\uff0c\u6b64\u95f4\u4e8b\u4e86\uff0c\u5982\u82e5\u6211\u4eec\u90fd\u8fd8\u6d3b\u7740\uff0c\u90a3\u4f60\u5c31\u5fc5\u987b\u5a36\u5230\u534e\u5927\u59ca\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d\u5427!\u201d \u3000\u3000\u7384\u5a18\u82e6\u82e6\u76f8\u903c\uff0c\u8981\u9ec4\u7075\u5a36\u5230\u82b1\u5999\u771f\uff0c\u8fde\u9ec4\u7075\u4e5f\u5f04\u4e0d\u592a\u6e05\u695a\u5979\u4f55\u4ee5\u5982\u6b64\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e5f\u6709\u7740\u83ab\u540d\u5176\u5999\u4e4b\u611f\u3002 \u3000\u3000\u53ea\u6709\u9ad8\u5e7d\u5170\u5374\u4f3c\u5fc3\u795e\u9886\u4f1a\uff0c\u8138\u4e0a\u6cdb\u73b0\u51fa\u5fae\u5fae\u7b11\u610f\u3002 \u3000\u3000\u4e89\u6267\u603b\u7b97\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u7ea2\u82b1\u3001\u7eff\u53f6\uff0c\u53cc\u53cc\u9000\u5165\u4e86\u94c1\u82b1\u9662\u3002 \u3000\u3000\u7d27\u968f\u5728\u7ea2\u82b1\u4e4b\u540e\uff0c\u662f\u96f7\u65e0\u5f71\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u4e0d\u662f\u4e00\u4e2a\u4eba\u6765\uff0c\u4ed6\u8eab\u540e\u8ddf\u7740\u56db\u4e94\u5341\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\uff0c\u867d\u7136\u5206\u7a7f\u7740\u4e0d\u540c\u989c\u8272\u7684\u8863\u670d\uff0c\u4f46\u4ed6\u4eec\u9762\u8272\u8083\u51b7\uff0c\u5e26\u7740\u6d53\u91cd\u7684\u6740\u6c14\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u795e\u5200\u5802\u7684\u7cbe\u9510\uff0c\u5168\u90e8\u51fa\u52a8\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u4f60\u4eec\u5982\u82e5\u518d\u80fd\u80dc\u4e86\u8fd9\u4e00\u6279\u4eba\uff0c\u795e\u5200\u5802\u7684\u7cbe\u9510\uff0c\u771f\u6709\u5341\u4e4b\u4e03\u516b\uff0c\u6b7b\u5728\u4f60\u4eec\u624b\u4e2d\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd8\u6709\u5341\u4e4b\u4e00\u4e8c\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5e26\u6765?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u90a3\u662f\u6211\u4eec\u7684\u4e8b\u4e86\uff0c\u9601\u4e0b\u4e0d\u89c9\u7740\u7ba1\u7684\u592a\u591a\u4e86\u5417?\u201d \u3000\u3000\u9ec4\u7075\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u6709\u51e0\u4ef6\u4e8b\uff0c\u53ea\u6015\u4f60\u8fd8\u4e0d\u77e5\u9053?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5728\u4e0b\u8fd9\u4e9b\u5e74\u6765\uff0c\u4e00\u76f4\u5728\u8bbe\u6cd5\u63ed\u53d1\u7b2c\u4e00\u5821\u7684\u9634\u8c0b\uff0c\u8fc7\u7a0b\u5f88\u8270\u82e6\uff0c\u4f46\u5de5\u592b\u672a\u767d\u8d39\uff0c \u76ee\u4e0b\u5df2\u7ecf\u4f7f\u5f97\u6c5f\u6e56\u4e0a\u5404\u5927\u95e8\u6d3e\uff0c\u77e5\u9053\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d \u96f7\u65e0\u5f71\u8868\u9762\u4e0a\u5bf9\u8fd9\u4e9b\u8bdd\u5341\u5206\u51b7\u6f20\uff0c\u4f46\u9aa8\u5b50\u91cc\u5374\u662f\u5173\u5fc3\u5f97\u5f88\u3002 \u3000\u3000\u9ec4\u7075\u6253\u91cf\u4e86\u96f7\u65e0\u5f71\u4e00\u773c\uff0c\u63a5\u9053\uff1a\u201c\u76ee\u4e0b\u6c5f\u6e56\u4e0a\uff0c\u5df2\u7ecf\u89c9\u9192\u4e86\uff0c\u800c\u4e14\uff0c\u9ad8\u5929\u5065\u4e5f\u653e\u51fa\u4e86\u4f20\u4e66\u98de\u9e3d\uff0c\u901a\u77e5\u4ed6\u9886\u5bfc\u7684\u56db\u5927\u9556\u5c40\uff0c\u4e5d\u4e2a\u9ed1\u9053\u7ec4\u5408\uff0c\u56de\u6551\u7b2c\u4e00\u5821\uff0c\u4f46\u5df2\u88ab\u6211\u4eec\u622a\u4e0b\uff0c\u800c\u4e14\uff0c\u4ed6\u4eec\u90fd\u4f1a\u88ab\u622a\u62e6\u4e8e\u9002\u5f53\u7684\u5730\u65b9\uff0c\u5f88\u96be\u5230\u7b2c\u4e00\u5821\u9a70\u63f4\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c31\u7b97\u6ca1\u6709\u5916\u63f4\uff0c\u51ed\u501f\u7b2c\u4e00\u5821\u5f3a\u5927\u7684\u5b9e\u529b\uff0c\u4e5f\u8db3\u4ee5\u548c\u8bf8\u4f4d\u5468\u65cb\u6297\u62d2\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u4f55\u4e0d\u62ac\u5934\u770b\u770b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u770b\u4ec0\u4e48?\u201d \u3000\u3000\u5176\u5b9e\uff0c\u96f7\u65e0\u5f71\u65e9\u5c31\u770b\u5230\u4e86\u3002 \u3000\u3000\u770b\u5230\u4e86\u5c1a\u5929\u4e49\u592b\u5987\u548c\u7384\u5a18\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1b\u201c\u54b1\u4eec\u7684\u63f4\u624b\uff0c\u5374\u65e9\u5df2\u7ecf\u6e90\u6e90\u7684\u8d76\u5230\u4e86\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u96be\u9053\u4f60\u4e0d\u8ba4\u8bc6\u8001\u592b\u5417\uff1f\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c1a\u8001\u7237\u5b50\u8d24\u592b\u5987\uff0c\u5728\u4e0b\u600e\u4f1a\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u65e2\u7136\u8ba4\u5f97\u8001\u592b\uff0c\u600e\u7684\u5982\u6b64\u65e0\u793c\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u51b7\u7136\u4e00\u7b11\uff0c\u9053\uff1b \u201c\u5c31\u51ed\u4f60\u4eec\u4e00\u5bf9\u8fc7\u65f6\u7684\u8001\u4eba\uff0c\u5c31\u662f\u9634\u9633\u5821\u4e3b\u7684\u63f4\u624b\u5417?\u201d \u3000\u3000\u5c1a\u5929\u4e49\u3001\u5c1a\u592b\u4eba\uff0c\u540c\u65f6\u6012\u559d\u4e00\u58f0\uff0c\u5411\u524d\u51b2\u53bb \u3000\u3000\u4f46\u5374\u88ab\u9ec4\u7075\u6240\u62e6\u4f4f\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u4e24\u4f4d\u8001\u524d\u8f88\uff0c\u6682\u8bf7\u606f\u6012\uff0c\u5927\u654c\u5f53\u524d\uff0c\u54b1\u4eec\u4e0d\u80fd\u4e71\u4e86\u9635\u811a\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u592b\u5987\u4eba\u867d\u9000\u4e86\u4e0b\u6765\uff0c\u4f46\u4e24\u9053\u6012\u76ee\uff0c\u5374\u671b\u7740\u96f7\u65e0\u5f71\uff0c\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u8001\u592b\u8bb0\u4e0b\u8fd9\u7b14\u8d26\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u5374\u4e00\u6325\u624b\uff0c\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u770b\u6765\u54b1\u4eec\u662f\u5f88\u96be\u8c08\u51fa\u4e2a\u7ed3\u679c\u4e86\u3002\u201d\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u4f60\u4eec\u592a\u4f4e\u4f30\u7b2c\u4e00\u5821\u4e86\uff0c\u5c31\u51ed\u4f60\u4eec\u8fd9\u51e0\u4e2a\u4eba\uff0c\u7edd\u65e0\u6cd5\u751f\u79bb\u6b64\u5730\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u53e3\u8bf4\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u5c31\u6211\u4eec\u8fd9\u51e0\u4e2a\uff0c\u6d88\u706d\u4e86\u6700\u53ef\u6015\u7684\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u800c\u4e14\u4e5f\u6d88\u706d\u4e86\u6700\u7cbe\u9510\u7684\u94c1\u7532\u6b66\u58eb\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u54c8\u54c8\u4e00\u7b11\u9053\uff1a \u201c\u4e8c\u5c0f\u59d0\uff0c \u4f60\u7684\u5341\u4e8c\u5973\u5c06\u5462?\u4e5f\u6b7b\u5f97\u5dee\u4e0d\u591a\u4e86\u5427!\u795e\u5200\u5802\u867d\u7136\u4e0d\u5c0f\uff0c\u4f46\u4ed6\u4eec\u5728\u6574\u4e2a\u7b2c\u4e00\u5821\u4e2d\u7684\u5206\u91cf\uff0c\u5e76\u4e0d\u7b97\u5f88\u91cd\uff0c\u53ef\u662f\u4f60\u7684\u5341\u4e8c\u5973\u5c06\uff0c\u5374\u4f1a\u662f\u4f60\u7684\u5168\u90e8\u5b9e\u529b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u4eec\u4e5f\u6709\u4f24\u4ea1\uff0c\u4f46\u4f24\u4ea1\u6bd4\u8d77\u6765\uff0c\u6211\u4eec\u5f88\u5c0f\uff0c\u5341\u4e8c\u5973\u5c06\u6709\u635f\u5931\uff0c\u4f46\u8fd8\u4f59\u4e0b\u4e00\u534a\u2026\u2026\u201d \u3000\u3000\u96f7\u65e0\u5f71\u63a5\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e0d\u76f8\u4fe1\uff0c\u4e5f\u5f97\u76f8\u4fe1\uff0c\u5979\u4eec\u90fd\u5728\u90a3\u91cc\u4f11\u606f\uff0c\u6211\u7acb\u523b\u53ef\u4ee5\u628a\u5979\u4eec\u53ec\u51fa\u6765\uff0c\u8ba9\u4f60\u770b\u770b\u3002\u201d \u3000\u3000\u770b\u5230\u4e86\u7eff\u53f6\u3001\u7ea2\u82b1\uff0c\u5c31\u7ad9\u5728\u9ad8\u5e7d\u5170\u7684\u8eab\u540e\uff0c\u96f7\u65e0\u5f71\u7684\u4fe1\u5fc3\u52a8\u6447\u4e86\u3002 \u3000\u3000\u81f3\u5c11\uff0c\u5341\u4e8c\u5973\u5c06\u4e2d\uff0c\u8fd8\u4f59\u4e0b\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u8bad\u7ec3\u5341\u4e8c\u5973\u5c06\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u95ed\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u95ed\u53e3\u4e0d\u8a00\uff0c\u90a3\u5c31\u662f\u8bf4\uff0c\u4ed6\u4e5f\u77e5\u9053\u4e86\u4e00\u4e9b\u5185\u60c5\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ad8\u5929\u5065\u8981\u6211\u9632\u5907\u4f60\uff0c\u9632\u5907\u4f60\u795e\u5200\u5802\u7684\u5b9e\u529b\u592a\u5927\uff0c\u6210\u4e86\u5c3e\u5927\u4e0d\u6389\u4e4b\u52bf\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5728\u4e0b\u4e00\u76f4\u5bf9\u5821\u4e3b\u5fe0\u8bda\u4e0d\u4e8c\uff0c\u771f\u91d1\u4e0d\u6015\u706b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u7684\u5fe0\u8bda\uff0c\u5374\u672a\u5fc5\u6362\u5f97\u5230\u4ed6\u7684\u4fe1\u4efb\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\uff0c\u4e0d\u7ba1\u60c5\u52bf\u5982\u4f55\uff0c\u6211\u4eec\u4e4b\u95f4\u603b\u662f\u4e00\u4e2a\u52bf\u96be\u4e24\u7acb\u7684\u5c40\u9762\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u96f7\u65e0\u5f71\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c31\u76ee\u4e0b\u5b9e\u529b\u800c\u8a00\uff0c\u4f60\u4eec\u8fd8\u5904\u5728\u52a3\u52bf\u4e4b\u4e0b\uff0c\u9ad8\u5821\u4e3b\u81ea\u5df1\u8fd8\u4fdd\u6709\u7740\u4e00\u6279\u5f3a\u5927\u7684\u5b9e\u529b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u4eec\u4e00\u76f4\u5904\u5728\u52a3\u52bf\u4e4b\u4e0b\uff0c\u4f46\u6211\u4eec\u6d88\u706d\u4e86\u2018\u6c34\u706b\u65e0\u60c5\u2019\u8fd9\u4e2a\u7ec4\u5408\uff0c\u73b0\u5728\uff0c\u62e5\u6709\u8fd9\u4e9b\u7edd\u6bd2\u6697\u5668\u7684\uff0c\u4e0d\u662f\u4f60\u4eec\uff0c\u800c\u662f\u5728\u6211\u4eec\u624b\u4e2d\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u8138\u8272\u5927\u53d8\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u4f60\u7387\u9886\u8fd9\u4e9b\u5c5e\u4e0b\uff0c\u90fd\u53ef\u80fd\u4f1a\u6b7b\u4f24\u5728\u90a3\u7edd\u6bd2\u6697\u5668\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u522b\u4f4e\u4f30\u5821\u4e3b\uff0c\u4e5f\u522b\u770b\u4f4e\u795e\u5200\u5802\uff0c\u5728\u4e0b\u6765\u6b64\u4e4b\u524d\uff0c\u4e5f\u60f3\u5230\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u4eec\u53ef\u80fd\u5df2\u62e5\u6709\u4e00\u4e9b\u2018\u6c34\u706b\u65e0\u60c5\u2019\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u4f60\u771f\u7684\u5982\u6b64\u56fa\u6267\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u4e0d\u8981\u518d\u52a9\u7ea3\u4e3a\u8650\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u4f5c\u4e9b\u4ec0\u4e48\u4e8b\u96be\u9053\u4f60\u81ea\u5df1\u4e0d\u660e\u767d\u5417?\u201d\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u6211\u660e\u767d\uff0c\u6240\u4ee5\uff0c\u6211\u624d\u4e86\u89e3\u65e0\u6cd5\u8131\u79bb\u7b2c\u4e00\u5821\uff0c\u8fd9\u91cc\u624d\u662f\u53ef\u4ee5\u7acb\u8db3\u7684\u5730\u65b9\u3002\u201d \u4ed6\u7684\u795e\u60c5\uff0c\u7a81\u7136\u95f4\u8f6c\u53d8\u7684\u5f88\u51b7\u8083\uff0c\u76ef\u6ce8\u5728\u5434\u53cc\u5973\u7684\u8eab\u4e0a\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\uff0c\u53ef\u662f\u8981\u5728\u4e0b\u5012\u53cd\u7b2c\u4e00\u5821\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a \u201c\u90a3\u662f\u4e3a\u4f60\u597d! \u4f60\u96be\u9053\u8fd8\u77a7\u4e0d\u51fa\u6765\uff0c\u7b2c\u4e00\u5821\u5df2\u5c06\u8986\u4ea1\u3002\u201d \u96f7\u65e0\u5f71\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4e8c\u5c0f\u59d0\u662f\u54b1\u4eec\u7b2c\u4e00\u5821\u5f88\u6709\u540d\u7684\u806a\u660e\u4eba\uff0c\u96be\u9053\uff0c\u53ea\u77a7\u51fa\u4e86\u8fd9\u4e9b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u626c\u626c\u67f3\u7709\u513f\uff0c\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u522b\u548c\u4ed6\u626f\u4e86\uff0c\u5927\u4e0d\u4e86\u653e\u624b\u4e00\u6218\u3002\u201d \u96f7\u65e0\u5f71\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u53ef\u66fe\u8ba1\u7b97\u8fc7\uff0c\u8fd9\u4e00\u6218\uff0c\u4f60\u4eec\u6709\u591a\u5c11\u53d6\u80dc\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u4ed6\u7684\u76ee\u5149\u5df2\u8bf4\u660e\u4e86\u4ed6\u7684\u6761\u4ef6\uff0c\u90a3\u662f\u8bf4\uff0c\u4ed6\u53ef\u4ee5\u5012\u6208\u76f8\u5411\uff0c\u4f46\u5374\u6709\u4e00\u4e2a\u91cd\u8981\u7684\u6761\u4ef6\uff0c\u90a3\u6761\u4ef6\u548c\u5434\u53cc\u5973\u6709\u5173\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5fc3\u4e2d\u65e9\u660e\u767d\u4e86\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e5f\u7ecf\u611f\u89c9\u5230\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4f55\u5bd2\u8863\u3001\u9ec4\u7075\uff0c\u90fd\u4e5f\u77a7\u4e86\u51fa\u6765\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\uff0c\u5927\u5bb6\u90fd\u6ca1\u6709\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u9ec4\u7075\u7a81\u7136\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u8ba4\u8bc6\u533a\u533a\u5728\u4e0b\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u4ed4\u7ec6\u7684\u6253\u91cf\u4e86\u9ec4\u7075\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4f60\u662f\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u9ec4\u7075\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4f60\u5c31\u662f\u9634\u9633\u5821\u4e3b?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5bf9!\u6211\u5c31\u662f\u9634\u9633\u5821\u4e3b\uff0c\u8bb0\u4e0d\u8bb0\u5f97\uff0c\u5341\u5e74\u524d\u4f60\u548c\u6211\u4e4b\u95f4\u7684\u4e00\u4e2a\u7ea6\u5b9a?\u201d \u3000\u3000\u6562\u60c5\uff0c\u8fd9\u4e24\u4e2a\u4eba\uff0c\u7adf\u7136\u65e9\u5df2\u76f8\u8bc6\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4f60\u4e0d\u592a\u50cf\u4ed6\u3002\u201d \u3000\u3000\u9ec4\u7075\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u4f60\u5fc3\u4e2d\u65e9\u5df2\u8ba4\u5b9a\u662f\u6211\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u73b0\u5728\u662f\u4e00\u5821\u4e4b\u4e3b\uff0c\u8fd9\u4e9b\u4eba\uff0c\u90fd\u662f\u4f60\u7684\u5c5e\u4e0b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4ed6\u4eec\u90fd\u662f\u6211\u7684\u670b\u53cb\uff0c\u5fd7\u540c\u9053\u5408\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6211\u6240\u8ba4\u8bc6\u7684\u9ec4\u7075\uff0c\u5e76\u4e0d\u662f\u5584\u7537\u3001\u4fe1\u5973\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u6211\u4e0d\u60f3\u548c\u4f60\u4e89\u8fa9\uff0c\u6211\u53ea\u8981\u95ee\u4f60\u4e24\u4ef6\u4e8b\uff1f\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5341\u5e74\u524d\uff0c\u4f60\u7b54\u5e94\u8fc7\u6211\uff0c\u53ea\u8981\u4f60\u6ca1\u6709\u6b7b\uff0c\u8fd9\u4e00\u751f\u4e2d\uff0c\u4f60\u4f1a\u4e3a\u6211\u6548\u529b\u4e00\u6b21\uff0c\u53ef\u8fd8\u8bb0\u5f97\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4ed6\u60f3\u8d56\uff0c\u53ef\u662f\u8bf4\u4e0d\u51fa\u53e3\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u7a76\u7adf\u662f\u4e00\u4e2a\u6c5f\u6e56\u4eba\uff0c\u800c\u4e14\uff0c\u5f88\u6709\u540d\u6c14\u7684\u4eba\uff0c\u771f\u771f\u5b9e\u5b9e\u7684\u4e8b\uff0c\u8981\u4ed6\u786c\u4e0d\u627f\u8ba4\uff0c\u8fd8\u662f\u505a\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u5e94\u8be5\u5151\u73b0\u8fd9\u53e5\u8bdd\u4e86\uff0c\u4e3a\u6211\u6548\u529b\u4e00\u6b21\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u8fd9\u65f6\u523b\uff0c\u53ea\u6015\u5728\u4e0b\u5f88\u96be\u6709\u5c65\u884c\u627f\u8bfa\u7684\u80fd\u529b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u53ef\u662f\u60f3\u8981\u8d56\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u96be\u9053\u8fd8\u60f3\u4e0d\u901a\uff0c\u54b1\u4eec\u8fd9\u4e00\u6218\uff0c\u662f\u751f\u6b7b\u4e4b\u640f\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u77e5\u9053\u5c31\u597d\u4e86\uff0c\u54b1\u4eec\u5728\u8fd9\u4e00\u6218\u4e4b\u4e2d\uff0c\u5982\u82e5\u9601\u4e0b\u88ab\u6740\u4e86\uff0c\u4f3c\u4e4e\u5728\u4e0b\u4e5f\u7528\u4e0d\u7740\u5bf9\u9601\u4e0b\u5c65\u884c\u627f\u8bfa\u4e86\uff0c\u5982\u82e5\u9601\u4e0b\u8fd8\u6d3b\u7740\uff0c \u3000\u3000\u90a3\u5728\u4e0b\u5fc5\u6b7b\u65e0\u7591\u4e86\uff0c\u4e5f\u7528\u4e0d\u7740\u518d\u5c65\u884c\u54b1\u4eec\u7684\u7ea6\u5b9a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u9519\u4e86!\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u9519\u4e86?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9!\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u6218\u6b7b\u4e86\uff0c\u90a3\u662f\u5e94\u8be5\uff0c\u5c31\u7b97\u4f60\u80dc\u4e86\uff0c\u4f60\u8fd8\u662f\u6d3b\u4e0d\u4e0b\u53bb\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u610f\u601d\u5f88\u660e\u767d\uff0c\u9ad8\u5929\u5065\u5df2\u7ecf\u5bf9\u4f60\u6709\u4e86\u6000\u7591\uff0c\u5e7d\u5170\u548c\u6211\u7684\u5f03\u6697\u6295\u660e\uff0c\u7ed9\u4e86\u4ed6\u66f4\u591a\u7684\u8b66\u60d5\uff0e \u4f60\u60f3\u60f3\uff0c \u4ed6\u8fd8\u4f1a\u7559\u4e0b\u4f60\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u9e1f\u5c3d\u5f13\u85cf\uff0c\u5154\u6b7b\u62d8\u70f9\uff0c \u4f60\u4e0d\u8fc7\u662f\u4e00\u4e2a\u5de5\u5177\uff0c\u4e00\u65e6\u6211\u4eec\u90fd\u6bc1\u706d\u4e86\uff0c \u7559\u4e0b\u4f60\u6709\u4ec0\u4e48\u7528?\u53ea\u4e0d\u8fc7\u662f\u4ed6\u4e00\u4e2a\u5fc3\u8179\u5927\u60a3\u7f62 \u3000\u3000\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u662f\u4e0d\u662f\u76f8\u4fe1\u3002\u201d \u3000\u3000\u5bf9\u5434\u53cc\u5973\u8fd9\u4e2a\u4eba\uff0c\u96f7\u65e0\u5f71\u5185\u5fc3\u4e2d\uff0c\u6709\u4e00\u80a1\u70bd\u70c8\u7684\u7231\u610f\uff0c\u4f46\u4e5f\u4e00\u80a1\u5931\u53bb\u7684\u5992\u6068\uff0c\u8fd8\u6709\u7740\u4e00\u79cd\u83ab\u540d\u7684\u754f\u60e7\uff1a\u5979\u8fd9\u4e48\u4e00\u8d28\u95ee\uff0c\u987f\u7136\u4f7f\u5f97\u96f7\u65e0\u5f71\u6709\u7740\u54d1\u53e3\u65e0\u8a00\u4e4b\u611f\u3002 \u3000\u3000\u5446\u4e86\u4e00\u5446\uff0c\u96f7\u65e0\u5f71\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a \u201c\u826f\u79bd\u62e9\u6728\u800c\u6816\uff0c\u73b0\u5728\uff0c \u4f60\u8fd8\u6765\u5f97\u53ca\u9009\u62e9\u81ea\u5df1\u8be5\u8d70\u7684\u8def\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u5fd6\u601d\u4e4b\u95f4\uff0c\u5ffd\u89c1\u96f7\u65e0\u5f71\u53f3\u624b\u4e00\u6325\u9053\uff1a\u201c\u4e0a\u3002\u201d \u3000\u3000\u7acb\u65f6\u6709\u516b\u4e2a\u624b\u6267\u9752\u94a2\u8f6e\u7684\u9ed1\u8863\u5927\u6c49\uff0c\u5feb\u6b65\u7684\u51b2\u6765\u3002 \u3000\u3000\u9752\u94a2\u8f6e\uff0c\u5c5e\u4e8e\u5916\u95e8\u5175\u5203\uff0c\u65bd\u7528\u7684\u4eba\u4e0d\u592a\u591a\u3002 \u3000\u3000\u9ec4\u7075\u4e00\u76b1\u7709\u5934\uff0c\u8fd8\u672a\u51b3\u5b9a\u4f55\u4eba\u51fa\u624b\uff0c\u9ad8\u5e7d\u5170\u5df2\u5e26\u7740\u7ea2\u82b1\u3001\u7eff\u53f6\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u91d1\u5251\u4e00\u6446\uff0c\u51b2\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5fc3\u4e2d\u6709\u7740\u5f88\u5927\u7684\u6012\u706b\uff0c\u4e09\u79cd\u989c\u8272\u4e0d\u76f8\u540c\u7684\u5251\uff0c\u4e00\u9f50\u51fa\u9798\u3002 \u3000\u3000\u7ea2\u82b1\u3001\u7eff\u53f6\uff0c\u4e5f\u662f\u4e09\u5251\u540c\u51fa\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\uff0c\u53ea\u6709\u4e24\u53ea\u624b\uff0c\u4e0d\u77e5\u5982\u4f55\u80fd\u591f\u540c\u65f6\u7528\u4e09\u79cd\u5251\uff0c\u548c\u4eba\u5bf9\u654c\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u573a\u4e2d\u4e4b\u4eba\uff0c\u90fd\u770b\u5f97\u5f88\u4ed4\u7ec6\u3002 \u3000\u3000\u53ea\u89c1\u5251\u5149\u98de\u95ea\uff0c\u8f6e\u5f71\u7eb5\u6a2a\uff0c\u53cc\u65b9\u9762\u6253\u7684\u5341\u5206\u6fc0\u70c8\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u624b\u4e2d\u63e1\u7740\u4e0d\u540c\u989c\u8272\u7684\u5251\uff0c\u7eb5\u7a7f\u8f6e\u5f71\u4e4b\u4e2d\u3002 \u3000\u3000\u9ec4\u7075\u5168\u795e\u8d2f\u6ce8\uff0c\u7ec8\u4e8e\u770b\u7684\u5341\u5206\u6e05\u695a\u4e86\u3002 \u3000\u3000\u7b2c\u4e09\u652f\u5251\uff0c\u5e76\u4e0d\u5728\u5979\u4eec\u624b\u4e2d\u63e1\u7740\u800c\u662f\u5ffd\u73b0\u5ffd\u9690\uff0c\u597d\u50cf\u662f\u7528\u4e00\u79cd\u7ec6\u7d22\u8fde\u7ed3\u5728\u624b\u8155\u4e4b\u4e0a\u3002 \u3000\u3000\u7528\u8155\u529b\uff0c\u64cd\u7eb5\u7740\u53e6\u4e00\u652f\u5251\u3002 \u3000\u3000\u516b\u4e2a\u65bd\u7528\u9752\u94a2\u8f6e\u7684\u9ed1\u8863\u4eba\uff0c\u8f6e\u6cd5\u975e\u5e38\u7cbe\u5947\uff0c\u867d\u7136\u662f\u4e24\u4eba\u5408\u6218\u4e00\u4e2a\uff0c\u4f46\u8fdb\u9000\u4e4b\u95f4\uff0c\u9690\u9690\u6709\u516b\u4eba\u914d\u5408\u4e4b\u52bf\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e5f\u4ee5\u5168\u529b\u62a2\u653b\uff0c\u91d1\u5251\u5947\u62db\u8fde\u51fa\u3002 \u3000\u3000\u4f46\u516b\u4e2a\u65bd\u8f6e\u7684\u9ed1\u8863\u5927\u6c49\uff0c\u6b66\u529f\u5341\u5206\u9ad8\u5f3a\u3001\u4f55\u5bd2\u8863\u72ec\u6218\u4e24\u4eba\uff0c\u867d\u7136\u5168\u529b\u65bd\u4e3a\uff0c\u4e5f\u53ea\u80fd\u4fdd\u6301\u4e00\u4e2a\u4e0d\u80dc\u4e0d\u8d25\u4e4b\u5c40\u3002 \u3000\u3000\u9ec4\u7075\u4e00\u76f4\u5f88\u51b7\u9759\u5730\u770b\u7740\u640f\u6740\u7684\u60c5\u5f62\u3002 \u3000\u3000\u4ed6\u53d1\u89c9\u9ed1\u8863\u4eba\u7684\u6b66\u529f\uff0c\u786e\u7136\u5341\u5206\u9ad8\u660e\u3002\u548c\u9634\u9633\u5821\u4e0b\u4e94\u5927\u5251\u4f7f\u76f8\u6bd4\uff0c\u6b66\u529f\u867d\u7136\u7a0d\u900a\u4e00\u7b79\uff0c\u4f46\u4e5f\u76f8\u5dee\u6709\u9650\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u4eec\u4e24\u4e2a\u4eba\u5408\u6253\u4e00\u4e2a\uff0c\u90a3\u5c31\u60c5\u52bf\u6709\u4e9b\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u7ea2\u82b1\u3001\u7eff\u53f6\u9996\u5148\u6709\u4e9b\u62b5\u6321\u4e0d\u4f4f\uff0c\u9732\u51fa\u4e86\u8d25\u8c61\u3002 \u3000\u3000\u4f46\u9ad8\u5e7d\u5170\u5374\u7a81\u7136\u5927\u53d1\u795e\u5a01\uff0c\u53ea\u542c\u5979\u5a07\u53f1\u4e00\u58f0\uff0c\u201c\u6740\u3002\u201d \u3000\u3000\u5239\u90a3\u95f4\uff0c\u6f2b\u5929\u5251\u5f71\u6d41\u52a8\u3002 \u3000\u3000\u597d\u5389\u5bb3\u7684\u5251\u6cd5\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u770b\u6e05\u695a\uff0c\u4ed6\u53d1\u51fa\u4e86\u51e0\u5251\uff0c\u4f46\u5374\u54cd\u8d77\u4e86\u4e00\u9635\u8fde\u7ef5\u4e0d\u7edd\u7684\u60e8\u53eb\u4e4b\u58f0\u3002 \u3000\u3000\u5251\u5149\u655b\u6536\u540e\uff0c\u5730\u4e0a\u8eba\u4e0b\u4e86\u516d\u5177\u5c38\u4f53\u3002 \u3000\u3000\u516d\u4e2a\u65bd\u7528\u9752\u94a2\u8f6e\u7684\u9ed1\u8863\u5927\u6c49\u3002 \u3000\u3000\u73b0\u573a\u4e2d\uff0c\u8fd8\u4e2a\u4e09\u4eba\u5728\u640f\u6740\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u548c\u4e24\u4e2a\u9ed1\u8863\u5927\u6c49\uff0c\u4e8c\u652f\u91d1\u5251\u82e6\u6597\u4e24\u53ea\u9752\u94a2\u8f6e\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4e00\u4e3e\u95f4\uff0c\u6740\u4e86\u516d\u4e2a\u4eba\uff0c\u4f46\u4e0d\u77e5\u4f55\u4ee5\u6ca1\u6709\u6740\u5230\u4e24\u4e2a\u548c\u4f55\u5bd2\u8863\u640f\u6597\u7684\u4eba\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u91d1\u5251\u867d\u7136\u53d8\u5316\u5947\u5999\uff0c\u4e5f\u4e0d\u8fc7\u662f\u4e2a\u582a\u582a\u81ea\u4fdd\u4e4b\u5c40\u3002 \u3000\u3000\u9ec4\u7075\u4e00\u76b1\u7709\u5934\uff0c\u6b63\u5f85\u4e0b\u4ee4\u53f6\u957f\u9752\u51fa\u624b\uff0c\u5ffd\u542c\u4f55\u5bd2\u8863\u5927\u559d\u2014\u58f0\uff0c\u53f3\u624b\u91d1\u5251\u4e00\u9707\uff0c\u7535\u5c04\u800c\u51fa\u3002 \u3000\u3000\u91d1\u5251\u8131\u624b\uff0c\u53cc\u8155\u4e0a\u7684\u98de\u8f6e\u4e5f\u968f\u7740\u75be\u5c04\u800c\u51fa\u3002 \u3000\u3000\u91d1\u5149\u968f\u7740\u5251\u6c14\u75be\u5c04\uff0c\u53d1\u51fa\u4e86\u4e24\u58f0\u60e8\u53eb\uff0c\u4e24\u4e2a\u9ed1\u8863\u4eba\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6bcf\u4eba\u7684\u524d\u80f8\u4e4b\u4e0a\uff0c\u4e2d\u4e86\u4e00\u679a\u98de\u8f6e\u3002 \u3000\u3000\u9ec4\u7075\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0e\u9053\uff1a\u201c\u4f55\u5144\uff0c\u597d\u7cbe\u5947\u7684\u98de\u8f6e\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4fa5\u5e78\u5f97\u5f88\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5c31\u5728\u4e0b\u8bb0\u5fc6\u4e4b\u4e2d\uff0c\u4ece\u672a\u770b\u5230\u4f55\u5144\u6253\u51fa\u8fc7\u98de\u8f6e\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u79cd\u624b\u6cd5\u592a\u6b79\u6bd2\uff0c\u975e\u81f3\u91cd\u8981\u5173\u5934\uff0c\u5728\u4e0b\u4e0d\u613f\u65bd\u7528\u3002\u201d \u3000\u3000\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u7531\u4e24\u4e2a\u9ed1\u8863\u5927\u6c49\u8eab\u4e0a\u53d6\u4e0b\u4e24\u679a\u98de\u8f6e\uff0c\u62ed\u53bb\u4e86\u8840\u8ff9\uff0c\u53c8\u6234\u5728\u624b\u8155\u4e0a\u3002 \u3000\u3000\u9ec4\u7075\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u8fd9\u516b\u4e2a\u4eba\uff0c\u5728\u4f60\u7684\u795e\u5200\u5802\u4e0b\uff0c\u662f\u4e2a\u4ec0\u4e48\u6837\u5b50\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ed6\u4eec\u5f88\u597d\uff0c\u4e0d\u8fc7\uff0c\u5e76\u4e0d\u662f\u592a\u7cbe\u9510\u7684\u4eba\u624b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u662f\u6709\u610f\u727a\u7272\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u56e0\u4e3a\u4ed6\u4eec\u5e76\u4e0d\u91cd\u8981\uff0c\u7531\u4e8e\u4ed6\u4eec\u4e4b\u6b7b\uff0c\u4f7f\u6211\u770b\u51fa\u4e86\u4e03\u8272\u5251\u6cd5\u7684\u5965\u5999\uff0c\u90a3\u4e5f\u662f\u4e00\u4ef6\u5f88\u503c\u5f97\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u516b\u6761\u4eba\u547d\uff0c\u53ea\u662f\u7ed9\u4f60\u4f5c\u4e00\u4e2a\u6d4b\u9a8c\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u53c8\u770b\u51fa\u4e86\u4ec0\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e03\u8272\u5251\u6cd5\u4e0d\u662f\u4e00\u79cd\u5251\u6cd5\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u662f\u4ec0\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e00\u79cd\u7eaf\u6740\u4eba\u7684\u6280\u672f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5f88\u597d\uff0c\u96f7\u5802\u4e3b\u53ef\u662f\u770b\u51fa\u4e86\u4ec0\u4e48\u8bc0\u7a8d?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5feb\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5feb\u4e86\u7684\u610f\u601d\uff0c\u5c31\u662f\u96f7\u5802\u4e3b\u8fd8\u6ca1\u6709\u770b\u7684\u5f88\u6e05\u695a\uff0c\u51c6\u5907\u518d\u51fa\u624b\u4e00\u8bd5\u4e86\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5bf9!\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u65b9\u9762\uff0c\u53ea\u6015\u8981\u4f60\u96f7\u5802\u4e3b\u5f88\u5931\u671b\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ec0\u4e48\u610f\u601d\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u56e0\u4e3a\u4e0b\u4e00\u6b21\uff0c\u9ad8\u59d1\u5a18\u4e0d\u4e00\u5b9a\u51fa\u624b\u4e86\uff0c\u6211\u4eec\u8fd8\u6709\u5f88\u591a\u4eba\uff0c\u4e5f\u6709\u5f88\u591a\u7684\u5947\u6280\uff0c\u51c6\u5907\u4e00\u8bd5\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u7b11\u9053\uff1a \u201c\u597d\u5728\uff0c\u533a\u533a\u7684\u4eba\u624b\u5f88\u591a\uff0c\u5c31\u7b97\u4f60\u4eec\u6bcf\u4e00\u4e2a\u90fd\u6740\u4e86\u4ed6\u4eec\u4e24\u4e2a\uff0c\u6211\u60f3\uff0c\u5728\u4e0b\u8fd8\u6709\u518d\u6218\u4e4b\u529b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u4e0d\u4f46\u662f\u4e00\u4e2a\u5be1\u4fe1\u5c0f\u4eba\uff0c\u800c\u4e14\uff0c\u4e5f\u662f\u51b7\u8840\u5c60\u592b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u8981\u6740\u4ed6\u4eec\u7684\u4e0d\u662f\u6211\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f46\u5374\u662f\u4f60\u8ba9\u4ed6\u4eec\u9001\u6b7b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u5982\u679c\u6562\u81ea\u5df1\u51fa\u624b\uff0c\u90a3\u6211\u5c31\u8ba9\u4f60\u518d\u89c1\u8bc6\u4e00\u4e0b\u4e03\u8272\u5251\u6cd5\uff0c\u54b1\u4eec\u4e00\u5bf9\u4e00\uff0c\u4e0d\u6b7b\u4e0d\u4f11\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\u5411\u6211\u6311\u6218\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u77e5\u9053\u4f60\u4e0d\u6562\u7b54\u5e94\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u6562\u4e0d\u6562\u7b54\u5e94\uff0c\u800c\u662f\u8981\u4e0d\u8981\u7b54\u5e94?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u96f7\u5802\u4e3b\u8981\u4e0d\u8981\u5462?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u8981\u3002\u201d \u3000\u3000\u9ec4\u7075\u6697\u4e2d\u6570\u8ba1\uff0c\u53d1\u89c9\u96f7\u65e0\u5f71\u8eab\u540e\u8fd8\u6709\u4e09\u5341\u4e5d\u4e2a\u4eba\uff0c\u5f53\u4e0b\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u96f7\u5821\u4e3b\u8fd9\u4e9b\u5c5e\u4e0b\uff0c\u5982\u82e5\u662f\u5168\u90fd\u6218\u6b7b\u4e86\uff0c\u5927\u6982\u5c31\u975e\u8981\u81ea\u5df1\u51fa\u624b\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5982\u82e5\u4f60\u4eec\u771f\u80fd\u628a\u795e\u5200\u5802\u8fd9\u4e9b\u7cbe\u9510\u4eba\u624b\uff0c\u5168\u6570\u6740\u6b7b\uff0c\u4e5f\u4e0d\u8fc7\u53ea\u6d88\u706d\u4e86\u7b2c\u4e00\u5821\u4e2d\u4e00\u90e8\u5206\u529b\u91cf\u800c\u5df2\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u522b\u5fd8\u4e86\u4f60\u5728\u8fd9\u4e00\u7fa4\u4eba\u4e2d\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u53e3\u820c\u4e4b\u4e89\uff0c\u4f3c\u4e4e\u662f\u5df2\u7ecf\u5f88\u96be\u4f7f\u987d\u77f3\u70b9\u5934\u4e86\uff0c\u54b1\u4eec\u52a8\u624b\u5427!\u201d \u3000\u3000\u4ed6\u5e74\u7eaa\u867d\u7136\u6700\u5927\uff0c\u4f46\u706b\u6c14\u4e5f\u5927\u7684\u5f88\uff0c\u8bf4\u6253\u5c31\u6253\uff0c\u8eab\u5b50\u4e00\u4fa7\uff0c\u5411\u524d\u51b2\u53bb\u3002 \u3000\u3000\u7384\u5a18\u4e00\u4f38\u624b\uff0c\u62e6\u4f4f\u4e86\u5c1a\u5929\u4e49\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u4e49\u7236\uff0c\u8bf7\u5fcd\u8010\u4e00\u4e0b\u3002\u201d \u5c1a\u5929\u4e49\u9053\uff1a \u201c\u600e\u4e48?\u773c\u4e0b\u60c5\u5f62\uff0c\u9664\u4e86\u52a8\u624b\u4e00\u6218\u4e4b\u5916\uff0c\u8fd8\u6709\u522b\u7684\u529e\u6cd5\u5417?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u4e49\u7236\uff0c\u9ec4\u7075\u640f\u6740\u7684\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u4ed6\u53bb\u9009\u62e9\u4e00\u4e2a\u5f88\u9002\u5408\u7684\u65f6\u95f4\u51fa\u624b\uff0c\u4e49\u7236\u4e0d\u7528\u62c5\u5fc3\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u4f4e\u58f0\u9053\uff1a \u201c\u5b69\u5b50\uff0c\u6211\u5c31\u662f\u60f3\u4e0d\u660e\u767d\uff0c\u73b0\u5728\u8fd8\u4e0d\u51fa\u624b\uff0e\u54b1\u4eec\u8981\u7b49\u5f85\u4ec0\u4e48?\u201d \u3000\u3000\u7384\u5a18\u7b11\u4e00\u7b11\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u4e49\u7236\uff0c \u4f60\u867d\u7136\u5fb7\u9ad8\u671b\u91cd\uff0c\u4f46\u4ed6\u662f\u4e00\u65b9\u7684\u4e3b\u5e05\uff0c\u5c31\u8ba9\u4ed6\u6765\u51b3\u5b9a\u5427!\u201d \u3000\u3000\u5c1a\u5929\u4e49\u5446\u4e86\u4e00\u5446\uff0c\u7b11\u9053\uff1a\u201c\u5973\u5fc3\u5916\u5411\uff0c\u770b\u6765\u662f\u5343\u53e4\u7686\u7136\u4e86\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4e49\u7236\uff0c\u4eba\u5bb6\u662f\u8bf4\u7684\u771f\u8bdd\u561b\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u672c\u6765\u4e00\u809a\u5b50\u6012\u706b\uff0c\u88ab\u8fd9\u4e2a\u4e49\u5973\u4e00\u6492\u5a07\uff0c \u2014\u809a\u5b50\u6c14\uff0c\u987f\u7136\u6d88\u9000\u4e86\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u8001\u7236\u6211\u4e5f\u6ca1\u6709\u8bf4\u5047\u8bdd\u5440\uff01\u201d \u3000\u3000\u53ea\u542c\u9ec4\u7075\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u73b0\u5728\u4f55\u4e0d\u518d\u7b97\u4e00\u7b97?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u7b97\u4ec0\u4e48?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7b97\u7b97\u770b\uff0c\u4f60\u73b0\u5728\u62bd\u817f\uff0c\u8fd8\u6765\u5f97\u53ca\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u62bd\u817f\u7684\u610f\u601d\uff0c\u5c31\u662f\u8981\u5728\u4e0b\u8dd1\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u610f\u601d\u662f\uff0c\u4f60\u73b0\u5728\u6295\u6548\u5230\u6211\u4eec\u8fd9\u4e00\u8fb9\u6765\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u7b2c\u4e00\u5821\u5df2\u7ecf\u662f\u4e00\u5ea7\u5c06\u503e\u7684\u5927\u53a6\u4e86\uff0c \u4f60\u5c31\u7b97\u5fe0\u5fc3\u803f\u803f\uff0c\u4e5f\u65e0\u6cd5\u652f\u6491\u5f97\u4f4f\uff0c\u4f55\u51b5\uff0c\u9ad8\u5929\u5065\u65e9\u5df2\u5bf9\u4f60\u4e0d\u4fe1\u4efb\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u8fd9\u4e2a\u4e48?\u5728\u4e0b\uff0c\u5728\u4e0b\u2026\u2026\u201d \u3000\u3000\u663e\u7136\uff0c\u4ed6\u7684\u5185\u5fc3\u4e4b\u4e2d\uff0c\u5fc3\u7ecf\u6709\u4e86\u5f88\u5927\u7684\u52a8\u6447\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u96f7\u5802\u4e3b\uff0c\u5f53\u7740\u8fd9\u591a\u4eba\u7684\u9762\u524d\uff0c\u5728\u4e0b\u5411\u4f60\u4fdd\u8bc1\u4e00\u53e5\u8bdd\uff0c\u5982\u82e5\u4f60\u771f\u80fd\u5f03\u6697\u6295\u660e\uff0c\u5728\u4e0b\u613f\u610f\u8d1f\u8d23\u4f60\u7684\u5b89\u5168\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u90a3\u5012\u4e0d\u7528\uff0c\u6211\u4e0d\u662f\u5c71\u7a77\u6c34\u5c3d\u5df2\u65e0\u8def\u7684\u5904\u5883\uff0c\u6211\u8fd8\u7edf\u9a6d\u7740\u5f88\u591a\u7684\u9ad8\u624b\uff0c\u4efb\u4f55\u4eba\uff0c\u5982\u662f\u60f3\u5bf9\u4ed8\u6211\uff0c\u6211\u5c31\u4f1a\u4e0d\u60dc\u548c\u4ed6\u653e\u624b\u4e00\u6218\u3002\u201d \u3000\u3000\u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u96f7\u5144\uff0c\u4e5f\u8bb8\u662f\u5144\u5f1f\u8bf4\u8bdd\u4e0d\u5f53\uff0c\u4e0d\u8fc7\uff0c\u5728\u4e0b\u662f\u4e00\u7247\u8bda\u610f\u3002\u201d \u96f7\u65e0\u5f71\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u6837\u5427!\u6211\u53ef\u4ee5\u7387\u9886\u4eba\u624b\uff0c\u4e24\u9762\u4e0d\u5e2e\uff0c\u4f60\u4eec\u548c\u9ad8\u5929\u5065\u52a8\u624b\u5206\u4e2a\u80dc\u8d1f\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u4f60\u5728\u65c1\u8fb9\u770b\uff0c\u4e5f\u597d\u5750\u6536\u6e14\u5229\u5bf9\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u9ad8\u5929\u5065\u8fd8\u6709\u4e00\u80a1\u529b\u91cf\uff0c\u73b0\u5728\u6211\u4eec\u53ef\u4ee5\u5408\u800c\u4e3a\u4e00\uff0c\u5982\u82e5\u6211\u8896\u624b\u65c1\u89c2\uff0c\u770b\u4f60\u4eec\u628a\u4ed6\u51fb\u8d25\u4e86\uff0c\u6211\u518d\u51fa\u624b\uff0c\u90a3\u5c82\u4e0d\u662f\u592a\u611a\u4e86\u4e00\u4e9b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u5c31\u8fd9\u4e48\u4e00\u8a00\u4e3a\u5b9a\uff0c\u6211\u4eec\u53bb\u627e\u9ad8\u5929\u5065\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ed6\u5728\u70df\u9501\u697c\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u6d3e\u4eba\u5e26\u6211\u4eec\u53bb\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\uff0c\u90fd\u77e5\u9053\u70df\u9501\u697c\u5728\u54ea\u91cc\u3002\u201d \u3000\u3000\u4e00\u62b1\u62f3\uff0c\u63a5\u9053\uff1a\u201c\u5728\u4e0b\u544a\u9000\u4e86\u3002\u201d\u8f6c\u8eab\u5927\u6b65\u800c\u53bb\u3002\u4ed6\u8eab\u540e\u6570\u5341\u4eba\uff0c\u679c\u7136\u90fd\u8ddf\u5728\u4ed6\u8eab\u540e\u8d70\u4e86\u3002 \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\uff0c\u672a\u5b89\u597d\u5fc3\uff0c\u4ed6\u60f3\u8d81\u54b1\u4eec\u548c\u9ad8\u5929\u5065\u62fc\u5230\u7d27\u8981\u5173\u5934\u65f6\uff0c\u4ed6\u518d\u51fa\u624b\uff0c\u4e00\u4e3e\u95f4\uff0c\u628a\u54b1\u4eec\u5168\u6570\u6b7c\u706d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4e0d\u8981\u96be\u8fc7\uff0c\u4ed6\u592a\u4f4e\u4f30\u9ad8\u5929\u5065\u4e86\uff0c\u6709\u5f97\u4ed6\u7684\u82e6\u5934\u597d\u5403\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5e7d\u5170\u59d1\u5a18\uff0c\u9ad8\u5929\u5065\u771f\u7684\u65e9\u5df2\u5b89\u6392\u4e86\u5bf9\u4ed8\u4ed6\u7684\u4eba\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u76ee\u5149\u8f6c\u6ce8\u5230\u5c1a\u5929\u4e49\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u96f7\u65e0\u5f71\u4e5f\u8bb8\u8fd8\u4e0d\u592a\u660e\u767d\uff0c\u9ad8\u5929\u5065\u65e9\u5c31\u5bf9\u4ed6\u6709\u9632\u5907\u4e86\uff0c\u4ed6\u6697\u4e2d\u8425\u79c1\u7ed3\u515a\uff0c\u6811\u690d\u81ea\u5df1\u7684\u52bf\u529b\uff0c\u4ed6\u8fd8\u8ba4\u4e3a\u9ad8\u5929\u5065\u4e0d\u77e5\u9053\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u9ad8\u5929\u5065\u65e9\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5e7d\u5170\u59d1\u5a18\uff0c\u4f60\u770b\u8fd9\u4f1a\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u5b50\u7684\u7ed3\u679c\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u770b\uff0c\u7ed3\u679c\u6700\u60e8\u7684\u662f\u96f7\u65e0\u5f71\uff0c\u4e0d\u8fc7\uff0c\u54b1\u4eec\u8981\u7ed9\u9ad8\u5929\u5065\u4e00\u4e2a\u673a\u4f1a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ec0\u4e48\u673a\u4f1a?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5982\u6b64\uff0c\u5982\u6b64\u3002\u201d \u3000\u3000\u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u59d1\u5a18\u7684\u89c1\u89e3\u3001\u5b89\u6392\uff0c\u53eb\u5728\u4e0b\u597d\u751f\u4f69\u670d\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5821\u4e3b\u5938\u5956\uff0c\u53ea\u4e0d\u8fc7\uff0c\u56e0\u4e3a\u6211\u5e38\u5e74\u4f4f\u5728\u7b2c\u4e00\u5821\u4e2d\uff0c\u5bf9\u4ed6\u4eec\u5f88\u4e86\u89e3\uff0c\u6240\u4ee5\uff0c\u80fd\u63d0\u51fa\u4e00\u5f97\u4e4b\u611a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u4eec\u7acb\u523b\u5f00\u59cb\u51c6\u5907\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5e26\u4e0a\u90a3\u51e0\u7b52\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u8fd9\u79cd\u6697\u5668\uff0c\u5728\u6c5f\u6e56\u4e0a\u867d\u7136\u85c9\u85c9\u65e0\u540d\uff0c\u4f46\u5728\u7b2c\u4e00\u5821\uff0c\u5374\u662f\u4eba\u4eba\u5bb3\u6015\uff0c\u96f7\u65e0\u5f71\u8fd9\u4e2a\u4eba\u5f88\u4e0d\u53ef\u9760\uff0c\u4e5f\u8bb8\uff0c\u4ed6\u4f1a\u6539\u53d8\u5fc3\u610f\uff0c\u5728\u9014\u4e2d\u6697\u7b97\u54b1\u4eec\uff0c\u5e26\u51e0\u4e2a\u2018\u6c34\u706b\u65e0\u60c5\u2019\uff0c\u5bf9\u4ed6\u6709\u5f88\u5927\u7684\u9707\u9a87\u4f5c\u7528\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u7acb\u523b\u5206\u914d\u4e86\u804c\u53f8\uff0c\u5f00\u59cb\u884c\u52a8\u3002 \u3000\u3000\u53d7\u4f24\u7684\u4eba\uff0c\u7ecf\u8fc7\u4e86\u5305\u624e\u4e4b\u540e\uff0c\u4e00\u8d77\u884c\u52a8\uff0c\u5b8c\u5168\u653e\u5f03\u4e86\u94c1\u82b1\u9662\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u719f\u6089\u5730\u5f62\uff0c\u5f53\u5148\u5e26\u8def\uff0c\u8ffd\u5954\u70df\u9501\u697c\u3002 \u3000\u3000\u70df\u9501\u697c\u6805\u95e8\u7d27\u95ed\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5e26\u4ed6\u4eec\u4e00\u76f4\u884c\u5230\u4e86\u94c1\u6805\u95e8\u5916\u3002 \u3000\u3000\u4e24\u58f0\u72ac\u5420\uff0c\u4e00\u53ea\u864e\u7352\uff0c\u51fa\u73b0\u5728\u6805\u95e8\u5185\u3002 \u3000\u3000\u56db\u4e2a\u4f69\u5200\u6b66\u58eb\uff0c\u4e5f\u4e00\u6392\u6a2a\u5217\u5728\u6805\u95e8\u4e4b\u5185\u3002 \u3000\u3000\u9ec4\u7075\u6325\u6325\u624b\uff0c\u9053\uff1a\u201c\u53bb\u901a\u62a5\u9ad8\u5821\u4e3b\uff0c\u5c31\u8bf4\u533a\u533a\u627e\u4e0a\u95e8\u6765\u3002\u201d \u3000\u3000\u5de6\u9996\u4f69\u5200\u6b66\u58eb\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5728\u4e0b\u9ec4\u7075\u3002\u201d \u3000\u3000\u5de6\u9996\u4f69\u5200\u4eba\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u542c\u4eba\u8bf4\u8fc7\u7684\u65e0\u540d\u5c0f\u5352\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5c31\u7b97\u6211\u662f\u4e2a\u65e0\u540d\u5c0f\u5352\uff0c\u9601\u4e0b\u8fd8\u662f\u5e94\u8be5\u901a\u62a5\u4e00\u58f0\u624d\u5bf9\u3002\u201d \u3000\u3000\u5de6\u9996\u5200\u624b\u9053\uff1a\u201c\u8fd8\u4e0d\u884c\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u53c8\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u5200\u5ba2\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u79cd\u9ed8\u9ed8\u65e0\u540d\u7684\u4eba\uff0c\u5982\u4f55\u80fd\u89c1\u5821\u4e3b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6012\u9053\uff1a\u201c\u4e0d\u8981\u548c\u4ed6\u7f57\u55e6\u4e86\uff0c\u54b1\u4eec\u51b2\u8fdb\u5c31\u662f\u3002\u201d \u3000\u3000\u56db\u4e2a\u4f69\u5200\u4eba\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u4f55\u4e0d\u8fdb\u6765\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4e00\u63d0\u6c14\uff0c\u6b63\u60f3\u8dc3\u8fc7\u56f4\u5899\uff0c\u5374\u88ab\u9ec4\u7075\u62e6\u4f4f\uff0c\u9053\uff1a \u201c\u4e0d\u53ef\u8e81\u8fdb\u3002\u201d \u76ee\u5149\u8f6c\u8fc7\u94c1\u6805\u4e4b\u5185\uff0c\u9053\uff1a\u201c\u9ad8\u5929\u5065\u5728\u8fd9\u91cc\u5417?\u201d \u3000\u3000\u5de6\u9996\uff0c\u4f69\u5200\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u597d!\u9601\u4e0b\u4e0d\u8bf4\uff0c\u6211\u7acb\u523b\u4e0b\u4ee4\uff0c\u8fd0\u6728\u67f4\u6765\u70e7\u4e86\u8fd9\u91cc\u3002\u201d \u3000\u3000\u4f69\u5200\u4eba\u9053\uff1a\u201c\u8fd9\u91cc\u9762\u5f88\u8fbd\u9614\u3001\u5e7f\u5927\u3001\u7a7a\u65f7\uff0c\u7528\u706b\u70e7\uff0c\u4e5f\u4e0d\u5bb9\u6613!\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u5bb9\u6613\uff0c\u5e76\u975e\u662f\u4e0d\u80fd\u554a!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7a81\u7136\u5411\u524d\u4e00\u6b65\uff0c\u91d1\u5251\u51fa\u624b\uff0c\u5288\u5411\u6805\u95e8\u5185\u6813\u3002 \u3000\u3000\u4ed6\u7684\u91d1\u5251\uff0c\u5e76\u975e\u5b9d\u5203\uff0c\u4fbf\u5374\u5f88\u575a\u5229\u3002 \u3000\u3000\u4ed6\u60f3\u6253\u5f00\u94c1\u6813\uff0c\u81ea\u7136\u5c31\u53ef\u4ee5\u884c\u5165\u70df\u9501\u697c\u4e2d\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u7a81\u7136\u5411\u524d\u4e00\u8dc3\uff0c\u56db\u628a\u5200\u540c\u65f6\u51fa\u9798\u3002 \u3000\u3000\u653b\u5411\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u5200\u957f\u5251\u77ed\uff0c\u4e2d\u95f4\u53c8\u9694\u7740\u4e00\u9053\u94c1\u6805\u3002\u4f55\u5bd2\u8863\u975e\u5411\u540e\u9000\u4e0d\u53ef\u3002 \u3000\u3000\u4f46\u9ec4\u7075\u5374\u7a81\u7136\u51fa\u624b\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u53cc\u624b\u5404\u6293\u4f4f\u4e86\u4e00\u628a\u5200\u3002 \u3000\u3000\u53f6\u957f\u9752\u53f3\u624b\u6325\u52a8\uff0c\u9694\u7740\u94c1\u6805\u523a\u51fa\u4e86\u4e09\u5251\uff0c\u903c\u9000\u4e86\u4e24\u4e2a\u5200\u624b\u3002 \u3000\u3000\u9ec4\u7075\u6697\u8fd0\u529f\u529b\uff0c\u7d27\u63e1\u4e24\u5200\uff0c\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u5982\u82e5\u53ea\u662f\u8fd9\u70b9\u6210\u5c31\uff0c\u6050\u6015\u4e0d\u662f\u4ed6\u4eec\u624b\u4e0b\u4e09\u5408\u4e4b\u654c\u3002\u201d\u7a81\u7136\u653e\u624b\uff0c\u518d\u52a0\u5185\u529b\u4e00\u9001\uff0c\u4e24\u4e2a\u5200\u624b\u76f4\u5411\u540e\u9762\u8dcc\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u4e5f\u770b\u51fa\u4e86\u9ec4\u7075\u771f\u6b63\u7684\u529f\u529b\u3002 \u3000\u3000\u4e24\u4e2a\u5200\u624b\uff0c\u540e\u9000\u4e86\u4e03\u516b\u5c3a\u540e\uff0c\u4ecd\u7136\u4e00\u5c41\u80a1\u5750\u5728\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u5434\u53cc\u5973\u548c\u9ad8\u5e7d\u5170\u7ad9\u5728\u4e00\u8d77\uff0c\u4e24\u4eba\u8fd8\u4e0d\u6253\u7b97\u7acb\u523b\u9732\u9762\uff0c\u5e26\u7740\u7ea2\u82b1\u3001\u7eff\u53f6\u4e0e\u56db\u4e2a\u5341\u4e8c\u5973\u5c06\u4e2d\u4eba\uff0c\u9690\u5728\u4e00\u5904\u8f6c\u89d2\u540e\u9762\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u5b88\u62a4\u5728\u70df\u9501\u697c\u7684\u4eba\uff0c\u4e0d\u662f\u5eb8\u624b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9ec4\u7075\u7684\u529f\u529b\u5f88\u60ca\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e5f\u8bb8\u6bd4\u6211\u4eec\u66f4\u80dc\u4e00\u7b79\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ec4\u7075\u80fd\u7edf\u6cbb\u9634\u9633\u5821\uff0c\u786e\u5b9e\u6bd4\u51e0\u4e2a\u5251\u4f7f\u9ad8\u660e\u4e00\u4e9b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\uff0c\u4e0d\u53ea\u9ad8\u660e\u4e00\u4e9b\uff0e\u6211\u770b\u4ed6\u662f\u9ad8\u660e\u5f88\u591a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u77e5\u4ed6\u548c\u9ad8\u5821\u4e3b\u6bd4\u8d77\u6765\uff0c\u54ea\u4e00\u4e2a\u9ad8\u660e?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u770b\u4e24\u4e2a\u4eba\u6253\u8d77\u6765\uff0c\u9ec4\u7075\u4e0d\u4f1a\u662f\u9ad8\u5821\u4e3b\u7684\u654c\u624b\uff1f\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u770b\u4e0b\u53bb\u5427!\u5fc5\u8981\u65f6\uff0c\u54b1\u4eec\u4e5f\u53ea\u597d\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5df2\u7ecf\u65e0\u6cd5\u9003\u907f\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u70df\u9501\u697c\u5916\uff0c\u53c8\u6709\u4e86\u5f88\u5927\u7684\u53d8\u5316\u3002 \u3000\u3000\u9ec4\u7075\u9732\u4e86\u4e00\u624b\u7cbe\u6e5b\u7684\u5185\u529f\u3001\u624b\u6cd5\uff0c\u53d1\u751f\u4e86\u5f88\u5927\u7684\u9707\u9a87\u4f5c\u7528\u3002 \u3000\u3000\u90a3\u4e9b\u5200\u624b\uff0c\u7adf\u7136\u4e0d\u6562\u518d\u5bf9\u9ec4\u7075\u65e0\u793c\u3002 \u3000\u3000\u4f46\u6d88\u606f\u7acb\u523b\u4f20\u5165\u4e86\u70df\u9501\u697c\u3002 \u3000\u3000\u9ad8\u5929\u5065\u51fa\u73b0\u4e86\u3002 \u3000\u3000\u4ed6\u8eab\u540e\u8ddf\u7740\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\uff0c\u5e73\u65f6\u5f88\u5c11\u51fa\u73b0\uff0c\u5434\u53cc\u5973\u548c\u9ad8\u5e7d\u5170\u90fd\u4e0d\u8ba4\u8bc6\u3002 \u3000\u3000\u4e00\u4e2a\u662f\u767d\u53d1\u8427\u8427\uff0c\u8138\u4e0a\u76b1\u7eb9\u5806\u7d2f\u7684\u8001\u592a\u592a\uff0c\u4e00\u4e2a\u662f\u7fbd\u6247\u7eb6\u5dfe\uff0c\u8eab\u7740\u516b\u5366\u888d\u7684\u4e2d\u5e74\u6587\u58eb\u3002 \u3000\u3000\u8fd9\u4f4d\u7a7f\u5f97\u50cf\u4e2a\u9053\u58eb\uff0c\u7eb6\u5dfe\u4e0a\u7ee3\u4e86\u5f88\u591a\u82b1\uff0c\u770b\u4e0a\u53bb\uff0c\u5c31\u4e0d\u4f26\u4e0d\u7c7b\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u4e3e\u6b62\uff0c\u884c\u52a8\uff0c\u4e5f\u6ca1\u6709\u4e00\u70b9\u9053\u58eb\u7684\u6837\u5b50\uff0c\u7559\u7740\u957f\u9aef\uff0c\u7fbd\u6247\u8f7b\u6447\u800c\u6765\u3002 \u3000\u3000\u9ad8\u5929\u5065\u51b7\u7136\u4e00\u7b11\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\uff0c\u8fd9\u4e48\u5feb\u53c8\u89c1\u9762\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4e0d\u540c\u7684\u662f\uff0c\u4e0a\u4e00\u6b21\uff0c \u4f60\u5728\u533a\u533a\u7684\u5c0f\u5e84\u9662\u5185\uff0c\u8fd9\u4e00\u6b21\uff0c\u54b1\u4eec\u662f\u5728\u7b2c\u4e00\u5821\u4e2d\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u89c9\u7740\uff0c\u8fd9\u662f\u4e00\u4ef6\u5f88\u5149\u8363\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u5f88\u96be\u5f97\u5427?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u5012\u4e5f\u4e0d\u9519\uff0c\u7b2c\u4e00\u5821\u6210\u7acb\u8fc4\u4eca\uff0c\u6765\u7684\u90fd\u662f\u670b\u53cb\uff0c\u4ece\u6765\u6ca1\u6709\u4e00\u4e2a\u654c\u4eba\u627e\u4e0a\u95e8\u8fc7\u3002\u201d \u3000\u3000\u9ec4\u7075\u76ee\u5149\u5f88\u5feb\u626b\u63a0\u8fc7\u90a3\u8001\u5987\u4eba\u548c\u9053\u88c5\u4e66\u751f\u7684\u8138\u4e0a\uff0c\u4ed6\u60f3\u770b\u51fa\u4e24\u4eba\u7a76\u7adf\u662f\u4f55\u8bb8\u4eba\u7269\u3002 \u3000\u3000\u4ee5\u4ed6\u89c1\u8bc6\u7684\u5e7f\u535a\uff0c\u7adf\u7136\u662f\u8ba4\u4e0d\u51fa\u6765\u8fd9\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u53ea\u542c\u9ad8\u5929\u5065\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u66f4\u96be\u5f97\u7684\u662f\uff0c\u4f60\u4eec\u8fd8\u6d3b\u7740\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\uff0c\u662f\u4e0d\u662f\u89c9\u7740\u5f88\u610f\u5916\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u7684\u786e\u662f\u6709\u70b9\u610f\u5916\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u795e\u5200\u5802\u6ca1\u6709\u7559\u4f4f\u4f60\u4eec\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u4eec\u5f88\u5e73\u5b89\u7684\u5230\u4e86\u70df\u9501\u507b\uff0c\u4ed6\u81ea\u7136\u662f\u6ca1\u6709\u6321\u4f4f\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u96f7\u65e0\u5f71\u5462?\u201d \u3000\u3000\u9ec4\u5821\u4e3b\u9053\uff1a\u201c\u5e0c\u671b\u6211\u544a\u8bc9\u4f60\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u4e0d\u8bb2\uff0c\u6211\u5f88\u5feb\u4e5f\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u9ad8\u5821\u4e3b\u7b49\u7740\u6162\u6162\u53bb\u77e5\u9053\u5427\uff0c\u73b0\u5728\uff0c\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u662f\u4f60\u628a\u94c1\u6805\u6253\u5f00\u8ba9\u6211\u4eec\u8fdb\u53bb\u5462?\u8fd8\u662f\u4f60\u9ad8\u5821\u4e3b\u81ea\u5df1 \u3000\u3000\u51fa\u6765\uff0c\u4f60\u8be5\u660e\u767d\uff0c\u8fd9\u4e00\u9053\u94c1\u6805\u548c\u8fd9\u4e9b\u56f4\u5899\uff0c\u62e6\u4e0d\u4f4f\u6211\u4eec\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u7a81\u7136\u53d1\u51fa\u4e00\u58f0\u957f\u5578\u3002 \u3000\u3000\u5578\u58f0\u56de\u8361\uff0c\u54cd\u5f7b\u4e86\u6574\u4e2a\u7684\u5e84\u9662\u3002 \u3000\u3000\u8fd9\u5578\u58f0\uff0c\u662f\u4e00\u79cd\u6697\u53f7\u3002 \u3000\u3000\u4e5f\u8868\u73b0\u4e86\u4ed6\u6df1\u539a\u7684\u529f\u529b\u3002 \u3000\u3000\u9ec4\u7075\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u8fd9\u4e2a\u4eba\u4e0d\u4f46\u5200\u6cd5\u7edd\u4f26\uff0c\u5185\u529f\u7adf\u7136\u4e5f\u5982\u6b64\u7684\u6df1\u539a\u3002 \u3000\u3000\u9ad8\u5929\u5065\u957f\u5578\u4e4b\u540e\uff0c\u7ad9\u7740\u6ca1\u6709\u52a8\uff0c\u9ec4\u7075\u4e5f\u7ad9\u7740\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u53cc\u65b9\u9762\uff0c\u5c31\u8fd9\u6837\u50f5\u6301\u7740\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u523b\u5de5\u592b\u4e4b\u4e45\uff0c\u4ecd\u7136\u6ca1\u6709\u52a8\u9759\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7684\u8138\u8272\u53d8\u4e86\uff0c\u4ed6\u7edd\u5bf9\u4e0d\u76f8\u4fe1\uff0c\u795e\u5200\u5802\u7684\u5b9e\u529b\u5df2\u7ecf\u5b8c\u5168\u906d\u53d7\u4e86\u6d88\u706d\u3002 \u3000\u3000\u9ec4\u7075\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\uff0c\u53ef\u662f\u51c6\u5907\u53ec\u8bf7\u96f7\u65e0\u5f71\u6765\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff0c\u4f60\u4eec\u80fd\u51fb\u6e83\u795e\u5200\u5802\u7684\u4e3b\u529b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u4e0d\u4fe1\uff0c\u6240\u4ee5\uff0c\u5c3d\u91cf\u60f3\u6cd5\u5b50\u53ec\u8bf7\u4ed6\u6765\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u80cc\u53db\u4e86\u6211\uff0c\u5bf9\u5417?\u201d\u9ec4\u7075\u9053\uff1a \u201c\u6211\u4e0d\u4f1a\u544a\u8bc9\u4f60\u4ec0\u4e48?\u4f60\u9ad8\u5821\u4e3b\u662f\u806a\u660e\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u4f1a\u53bb\u60f3?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u60f3\u8fc7\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u60f3\u901a\u4e86\uff0c\u4e5f\u5e94\u8be5\u6709\u4e2a\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u51b3\u5b9a\u4ec0\u4e48?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4f60\u51fa\u6765\uff0c\u8fd8\u662f\u6211\u4eec\u8fdb\u53bb\uff0c \u597d\u50cf\u6211\u4eec\u4e4b\u95f4\u53ea\u6709\u653e\u624b\u4e00\u6218\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53ea\u6709\u4f60\u4eec\u8fd9\u51e0\u4e2a\u4eba?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u6162\u6162\u7684\u53d1\u89c9\uff0c\u6211\u4eec\u7684\u4eba\u4f1a\u8d8a\u6253\u8d8a\u591a\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u544a\u8bc9\u6211\uff0c\u96f7\u65e0\u5f71\u662f\u4e0d\u662f\u5df2\u7ecf\u80cc\u53db\u4e86\u6211\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e8b\u5df2\u5982\u6b64\uff0c\u73b0\u5728\uff0c \u4f60\u8fd8\u6c42\u8bc1\u8fd9\u4e9b\u4e8b\u60c5\u5e72\u4ec0\u4e48\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u8981\u4e86\u89e3\u771f\u8c61\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u73b0\u5728\uff0c\u662f\u4e0d\u662f\u5df2\u7ecf\u4e86\u89e3\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5c31\u7b97\u6211\u5df2\u7ecf\u4e86\u89e3\u4e86\uff0c\u6211\u4e5f\u8981\u4f60\u4eb2\u53e3\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\uff0c\u53ea\u6015\uff0c\u4f60\u4f1a\u5f88\u5931\u671b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u80af\u8bf4\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bf9!\u6211\u4eec\u4e0d\u4f1a\u544a\u8bc9\u4f60\u4ec0\u4e48?\u201d\u9ad8\u5929\u5065\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u8001\u592b\u8ddf\u4f60\u5546\u91cf\u4e00\u4ef6\u4e8b\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u5bf9\u4f60\u4eec\u5927\u5927\u6709\u5229\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8bf4\u5427\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u8ba9\u6211\u5148\u53bb\u627e\u96f7\u65e0\u5f71\uff0c\u628a\u6211\u4eec\u7684\u4e8b\u60c5\u89e3\u51b3\u4e86\uff0c\u7136\u540e\uff0c\u54b1\u4eec\u518d\u89e3\u51b3\u4f60\u6211\u7684\u4e8b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a \u201c\u96f7\u65e0\u5f71\u4e0d\u662f\u597d\u76f8\u4e0e\u7684\u4eba\uff0c\u6211\u4e0d\u76f8\u4fe1\u4f60\u4eec\u80fd\u591f\u5b8c\u5168\u6d88\u706d\u795e\u5200\u5802\u7684\u5b9e\u529b\uff0c\u6240\u4ee5\uff0c \u4ed6\u4e00\u5b9a\u548c\u4f60\u4eec\u8c08\u597d\u4e86\u6761\u4ef6?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4ed6\u8ba9\u6211\u4eec\u706b\u5e76\u4e4b\u540e\uff0c\u4ed6\u597d\u5750\u6536\u6e14\u5229\u3002\u201d \u3000\u3000\u9ec4\u7075\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u53ea\u6015\u6211\u4eec\u5f88\u96be\u7b54\u5e94\u4f60\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u4eec\u5df2\u7ecf\u7b54\u5e94\u4e86\u96f7\u65e0\u5f71\uff0c\u662f\u5417?\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8001\u592b\u5982\u82e5\u5728\u6c5f\u6e56\u4e0a\u662f\u4e00\u4e2a\u53ef\u6015\u7684\u4eba\u7269\uff0c\u4f46\u96f7\u65e0\u5f71\u662f\u4e00\u4e2a\u66f4\u4e3a\u53ef\u6015\u7684\u4eba\u7269\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u7684\u610f\u601d\u662f\u2014\u2014\u201d \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u8ba9\u6211\u5148\u627e\u96f7\u65e0\u5f71\uff0c\u6211\u4eec\u8fd9\u4e00\u573a\u62da\u4e0b\u6765\uff0c\u4e0d\u6ca6\u8c01\u80dc\u90fd\u5bf9\u4f60\u4eec\u6709\u767e\u5229\u800c\u65e0\u4e00\u5bb3\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u4f60\u53c8\u5982\u4f55\u80fd\u627e\u5230\u96f7\u65e0\u5f71\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u53ea\u8981\u4f60\u540c\u610f\uff0c\u8001\u592b\u8fd9\u5c31\u6d3e\u4eba\u53bb\u627e\u4ed6\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u53ea\u8981\u4f60\u9ad8\u5929\u5065\u4e0d\u79bb\u5f00\uff0c\u4f60\u53ef\u4ee5\u6d3e\u4eba\u53bb\u627e\u4ed6\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9ad8\u58f0\u9053\uff1a\u201c\u4e54\u5b9d\u4f55\u5728?\u201d \u3000\u3000\u4e00\u4e2a\u52b2\u88c5\u5927\u6c49\uff0c\u5e94\u58f0\u800c\u51fa\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u5728\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u4f4e\u8a00\u6570\u8bed\uff0c\u7a81\u7136\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u53bb\u53eb\u96f7\u65e0\u5f71\u6765\u3002\u201d \u3000\u3000\u4e54\u5b9d\u5e94\u4e86\u4e00\u58f0\uff0c\u5927\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\uff0c\u6211\u6d3e\u4eba\u53bb\u627e\u96f7\u65e0\u5f71\u6765\uff0c\u5e0c\u671b\u4f60\u4eec\u4f1a\u653e\u4ed6\u8fc7\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u4e00\u65f6\u4e4b\u95f4\uff0c\u5fc3\u4e2d\u5012\u4e5f\u662f\u62ff\u4e0d\u5b9a\u4e3b\u610f\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u5982\u82e5\u4ed6\u771f\u7684\u628a\u96f7\u65e0\u5f71\u627e\u4e86\u56de\u6765\uff0c\u8fd9\u96f7\u65e0\u5f71\u4f1a\u6709\u4ec0\u4e48\u53d8\u5316\uff0c\u90a3\u662f\u5f88\u96be\u8bf4\u7684\u4e00\u4ef6\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5fd6\u601d\u4e4b\u95f4\uff0c\u4e54\u5b9d\u5df2\u4ee5\u98de\u5feb\u7684\u901f\u5ea6\u75be\u9a70\u800c\u8fc7\u3002 \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u770b\u6765\uff0c\u9601\u4e0b\u4f3c\u662f\u5f88\u6c89\u5f97\u4f4f\u6c14\uff0c\u60f3\u5fc5\u65e9\u548c\u90a3\u96f7\u65e0\u5f71\u6709\u4ec0\u4e48\u7ea6\u5b9a\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5821\u4e3b\u65e2\u7136\u8981\u89c1\u96f7\u65e0\u5f71\uff0c\u5728\u4e0b\u5982\u662f\u6a2a\u52a0\u963b\u6270\uff0c\u90a3\u5c82\u4e0d\u662f\u5f92\u589e\u5821\u4e3b\u70e6\u607c\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u54fc\uff0c\u6211\u5012\u8981\u77a7\u77a7\u4ed6\u96f7\u65e0\u5f71\u6709\u591a\u5927\u80c6\u5b50\uff0c\u7adf\u6562\u80cc\u53db\u4e86\u6211\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u672a\u518d\u591a\u8a00\u3002 \u3000\u3000\u53cc\u65b9\u9759\u9759\u7684\u5bf9\u5cd9\u4e0b\u53bb\u3002 \u3000\u3000\u4e54\u5b9d\u5e26\u6765\u4e86\u96f7\u65e0\u5f71\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5e26\u7740\u516b\u4e2a\u4eba\uff0c\u56db\u4e2a\u7a7f\u7740\u767d\u8863\uff0c\u8eab\u4f69\u957f\u5251\u7684\u4eba\u3002 \u3000\u3000\u8863\u767d\u4eba\u4e5f\u767d\uff0c\u8138\u4e0a\u767d\u5f97\u4e0d\u89c1\u4e00\u70b9\u8840\u8272\uff0c\u50cf\u4e00\u5757\u900f\u660e\u7684\u7389\u3002 \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u4eba\u4f69\u5200\u3002 \u3000\u3000\u8863\u670d\u9ed1\uff0c\u4f46\u8138\u4e0a\u5374\u662f\u7ea2\u7684\uff0c\u7ea2\u5f97\u50cf\u4e00\u56e2\u706b\u3002 \u3000\u3000\u9ec4\u7075\u4ed4\u7ec6\u7684\u89c2\u5bdf\u4e86\u516b\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u8138\u8272\u767d\u548c\u7ea2\uff0c\u90fd\u90a3\u4e48\u4e0d\u81ea\u7136\u3002 \u3000\u3000\u663e\u7136\uff0c\u90a3\u662f\u7ec3\u529f\u7ec3\u51fa\u6765\u7684\u989c\u8272\u3002 \u3000\u3000\u9ad8\u5929\u5065\u8138\u8272\u94c1\u9752\uff0c\u51b7\u51b7\u5730\u671b\u4e86\u96f7\u65e0\u5f71\u4e00\u773c\uff0c\u9053\uff1a\u201c\u96f7\u5802\u4e3b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201d\u5821\u4e3b\u627e\u6211?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u795e\u5200\u5802\u8fd8\u6709\u6ca1\u6709\u53ef\u6218\u4e4b\u4eba?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\u7684\u770b\u6cd5\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u770b\uff0c\u4f60\u662f\u6709\u610f\u7684\u4fdd\u5b58\u5b9e\u529b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u4e0d\u597d\uff0c\u6211\u4fdd\u5b58\u5b9e\u529b\uff0c\u5821\u4e3b\u624d\u6709\u4e1c\u5c71\u518d\u8d77\u7684\u672c\u94b1\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u7b2c\u4e00\u5821\u8fd8\u662f\u597d\u597d\u7684\uff0c\u65e2\u7136\u57ae\u4e0d\u4e86\uff0c\u4e0d\u7740\u4e1c\u5c71\u518d\u8d77\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u73b0\u5728\uff0c\u597d\u50cf\u8fd8\u6765\u5f97\u53ca\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u6765\u5f97\u53ca\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8c03\u51fa\u6765\u4f60\u795e\u5200\u5802\u4e0b\u7684\u4eba\u624b\uff0c\u5bf9\u4ed8\u9ec4\u7075\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u56de\u987e\u4e86\u9ec4\u7075\u4e00\u773c\uff0c\u9053\uff1a \u201c\u6211\u4eec\u4f24\u4ea1\u5f88\u91cd\uff0c\u94c1\u7532\u6b66\u58eb\u548c\u516b\u82f1\u4e5d\u4fdd\uff0c\u90fd\u5df2\u7ecf\u5b8c\u5168\u4f24\u4ea1\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd8\u4e0d\u5230\u4f60\u795e\u5200\u5802\u4e00\u534a\u4eba\u624b?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\u7684\u610f\u601d\u662f\u8bf4\uff0c\u8981\u795e\u5200\u5802\u6218\u5230\u4e86\u4e00\u5175\u4e00\u5352\u65f6\u8fd8\u8981\u652f\u6491\u4e0b\u53bb\u3002 \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u7684\u5b9e\u529b\u8fd8\u662f\u5f3a\u5927\u5f97\u5f88\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u5821\u4e3b\u9a6d\u4e0b\u592a\u4e25\u4e86\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u611f\u89c9\u4e0d\u5230\u6709\u4ec0\u4e48\u6069\u4e49\u53ef\u8a00\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u8fd9\u8bdd\u7528\u5fc3\u4f55\u5728?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\u662f\u4f55\u7b49\u806a\u660e\u7684\u4eba\u7269\uff0c\u96be\u9053\u8fd8\u542c\u4e0d\u61c2\u5728\u4e0b\u8bf4\u7684\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5929\u5065\u6012\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u597d\u5927\u7684\u80c6\u5b50?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5728\u4e0b\u80c6\u5b50\u518d\u5c0f\u4e00\u4e9b\uff0c\u53ea\u6015\u4e5f\u65e0\u6cd5\u4f7f\u5f97\u5821\u4e3b\u5bf9\u5728\u4e0b\u7684\u4fe1\u4efb\uff0c\u65e2\u7136\u59d4\u66f2\u65e0\u6cd5\u6c42\u5168\uff0c\u5728\u4e0b\u4e5f\u4e0d\u7528\u592a\u59d4\u5c48\u81ea\u5df1\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u597d\u50cf\u5728\u4e0b\u8fc7\u53bb\u5bf9\u4f60\u592a\u4e25\u5389\u4e86\u4e00\u4e9b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u96be\u9053\u5821\u4e3b\u4e0d\u89c9\u7740\u5982\u6b64\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5728\u4e0b\u4e00\u76f4\u89c9\u7740\uff0c\u6211\u5bf9\u4f60\u4e0d\u7b97\u592a\u574f\u5427!\u996e\u6c34\u601d\u6e90\uff0c\u4f60\u4eca\u5929\u80fd\u7edf\u7387\u5982\u6b64\u4f17\u591a\u9ad8\u624b\uff0c\u662f\u4ec0\u4e48\u4eba\u57f9\u517b\u4f60\u7684\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u539f\u60f3\u6fc0\u4ed6\u53d1\u6012\uff0c\u53cc\u65b9\u501f\u673a\u95f9\u7ffb\uff0c\u60f3\u4e0d\u5230\u9ad8\u5929\u5065\u7adf\u662f\u4e00\u76f4\u6ca1\u6709\u53d1\u4f5c\u3002 \u3000\u3000\u4f46\u96f7\u65e0\u5f71\u5fc3\u4e2d\u660e\u767d\uff0c\u7ecf\u6b64\u4e00\u756a\u4e89\u8bba\u4e4b\u540e\uff0c\u6bcf\u4e00\u65f6\u523b\uff0c\u9ad8\u5929\u5065\u90fd\u53ef\u80fd\u6740\u4e86\u4ed6\u3002 \u3000\u3000\u4e8b\u5b9e\u5df2\u7ecf\u65e0\u793a\u518d\u633d\u56de\u4e86\uff0c\u90a3\u5c31\u53ea\u6709\u95f9\u4e00\u4e2a\u660e\u767d\u7684\u7ed3\u679c\u51fa\u6765\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5821\u4e3b\u5bf9\u5728\u4e0b\u4e0d\u4fe1\u4efb\uff0c\u5728\u4e0b\u4e3a\u4e86\u81ea\u4fdd\uff0c\u4e5f\u53ea\u6709\u4e00\u9014\u53ef\u5faa\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u597d!\u4f60\u8bf4\u5427?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u5728\u4e0b\u5df2\u65e0\u6cd5\u518d\u548c\u5821\u4e3b\u5904\u4e0b\u53bb\uff1b\u7b2c\u4e8c\uff0c\u4f60\u548c\u9634\u9633\u5821\u7684\u640f\u6740\uff0c\u5728\u4e0b\u4e0d\u518d\u63d2\u624b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd8\u6709\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u8bb0\u5f97\u4f60\u662f\u7b2c\u4e00\u5821\u4e2d\u4eba\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u8bb0\u5f97\uff0c\u9ad8\u5821\u4e3b\uff0c\u4f60\u7684\u4e3a\u4eba\u5f88\u5931\u8d25\uff0c\u4f60\u7684\u4e24\u4e2a\u5973\u513f\uff0c\u90fd\u79bb\u4f60\u800c\u53bb\uff0c\u4f55\u51b5\u522b\u4eba\u2026\u2026\u201d \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u5979\u4eec\u90fd\u4e0d\u662f\u6211\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u90a3\u66f4\u574f\uff0c\u5979\u4eec\u53ef\u80fd\u5c31\u5728\u9644\u8fd1\uff0c\u4f60\u8fd9\u53e5\u8bdd\u88ab\u5979\u4eec\u542c\u5230\u4e86\uff0c\u8001\u5b9e\u8bf4\uff0c\u5979\u4eec\u66f4\u53ef\u4ee5\u653e\u624b\u5bf9\u4ed8\u4f60\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u53ef\u77e5\u9053\u8001\u592b\u4ecd\u6709\u6740\u4f60\u7684\u80fd\u529b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u770b\u5230\u6211\u5e26\u7684\u8fd9\u4e9b\u4eba\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u770b\u5230\u4e86\uff0c\u53c8\u600e\u4e48\u6837\uff0c\u4ed6\u4eec\u96be\u9053\u8fd8\u6562\u5bf9\u6211\u51fa\u624b?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u6562!\u4ed6\u4eec\u6548\u5fe0\u7684\u53ea\u662f\u5bf9\u6211\u4e00\u4e2a\u4eba\u800c\u5df2\uff0c\u4ed6\u4eec\u53ea\u6709\u6211\u8fd9\u4e2a\u795e\u5200\u5802\u4e3b\uff1b\u5374\u4e0d\u4f1a\u627f\u8ba4\u4f60\u8fd9\u4e2a\u5821\u4e3b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u4e48\u770b\u6765\uff0c\u4f60\u662f\u65e9\u6709\u4e86\u8c0b\u53cd\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u597d\u5427!\u5c31\u7b97\u6211\u5df2\u6709\u6b64\u5fc3\uff0c\u5821\u4e3b\u53c8\u80fd\u5982\u4f55?\u201d \u3000\u3000\u6709\u6b64\u4e00\u8a00\uff0c\u53cc\u65b9\u65e0\u7591\u662f\u6b63\u5f0f\u7684\u95f9\u7ffb\u4e86\u3002 \u3000\u3000\u9ad8\u5929\u5065\u53cc\u76ee\u4e2d\u66b4\u5c04\u51fa\u51b7\u5389\u7684\u6012\u706b\uff0c\u671b\u7740\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u8bb0\u4f4f\uff0c\u6211\u8981\u5148\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5821\u4e3b\u522b\u5fd8\u4e86\u9ec4\u7075\u4e5f\u5728\u6b64\u5730\u3002\u201d \u3000\u3000\u5728\u9ad8\u5929\u5065\u8fc7\u53bb\u7684\u5e38\u5e74\u79ef\u5a01\u4e4b\u4e0b\uff0c\u96f7\u65e0\u5f71\u770b\u4ed6\u771f\u7684\u706b\u4e86\uff0c\u5fc3\u4e2d\u8fd8\u662f\u6709\u4e00\u70b9\u754f\u60e7\u7684\u611f\u89c9\u3002 \u3000\u3000\u9ec4\u7075\u7a81\u7136\u63a5\u53e3\uff0c\u9053\uff1a \u201c\u5bf9!\u9ad8\u5821\u4e3b\uff0c \u5df2\u7ecf\u6c42\u5f97\u6240\u77e5\uff0c\u4e5f\u8be5\u4f5c\u4e2a\u51b3\u5b9a\u4e86\u3002\u201d \u8fd9\u51e0\u53e5\u8bdd\u5f88\u542b\u7cca\uff0c\u82e5\u6709\u6240\u6307\u3002 \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u7ed9\u6211\u4e00\u4e2a\u65f6\u8fb0\u7684\u65f6\u95f4\uff0c\u6211\u8981\u5148\u628a\u8fd9\u4e2a\u53db\u5f92\u6740\u4e86\uff0c\u54b1\u4eec\u518d\u4f5c\u4e86\u65ad\u3002\u201d \u3000\u3000\u9ec4\u7075\u56de\u987e\u4e86\u96f7\u65e0\u5f71\u4e00\u773c\uff0c\u9053\uff1a \u201c\u8fd9\u4ef6\u4e8b\uff0c\u5728\u4e0b\u53ea\u6015\u65e0\u6cd5\u51b3\u5b9a?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u51b3\u5b9a\uff0c\u90a3\u8981\u4ec0\u4e48\u4eba\u624d\u80fd\u51b3\u5b9a\u5462?\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u53ea\u6015\u8981\u95ee\u96f7\u65e0\u5f71\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u95ee\u96f7\u65e0\u5f71?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u5fc3\u4e2d\u6697\u6697\u9a82\u9053\uff1a \u201c\u597d\u554a!\u6211\u5df2\u7ecf\u8868\u73b0\u5f97\u5f88\u5408\u4f5c\u4e86\uff0c\u60f3\u4e0d\u5230\uff0c\u4f60\u7adf\u7136\u8fd8\u8981\u5f80\u6211\u7684\u5934\u4e0a\u5957\u4e2a\u6b7b\u7ed3\u3002\u201d \u3000\u3000\u4f46\u4ed6\u76ee\u4e0b\u5904\u5883\uff0c\u6709\u5982\u8239\u884c\u6c5f\u5fc3\uff0c\u5df2\u7ecf\u65e0\u6cd5\u518d\u56de\u5934\u4e86\uff0c\u53ea\u597d\u786c\u7740\u5934\u76ae\u8bf4\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u662f\u4e0d\u662f\u89c9\u7740\u5f88\u60ca\u5947?\u201d \u3000\u3000\u4e8b\u60c5\u5df2\u7ecf\u5b8c\u5168\u5730\u660e\u6717\u4e86\uff0c\u9ad8\u5929\u5065\u53cd\u800c\u51b7\u9759\u4e86\u4e0b\u6765\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u5462?\u4f60\u600e\u4f1a\u6709\u8fd9\u6837\u5927\u7684\u80c6\u5b50\uff0c\u539f\u6765\u5df2\u7ecf\u6295\u964d\u4e86\u9ec4\u7075\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u4e0d\u8981\u8bef\u4f1a\uff0c\u96f7\u5802\u4e3b\u548c\u54b1\u4eec\u53ea\u6709\u662f\u5408\u4f5c\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u65e9\u8be5\u6740\u4e86\u4ed6\u7684!\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u4f60\u65e9\u8be5\u6740\u7684\u5f88\u591a\uff0c\u50cf\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\uff0c\u90fd\u8be5\u6740\u7684\uff0c\u4f46\u4f60\u5374\u6ca1\u6709\u6740\u5979\u4eec\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u6211\u60f3\u5148\u89e3\u51b3\u6211\u4eec\u5185\u90e8\u7684\u7eb7\u4e89\uff0c\u4e0d\u77e5\u9053\u4f60\u7684\u610f\u89c1\u5982\u4f55?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u4eec\u5bf9\u5408\u4f5c\u4eba\u4e00\u5411\u5c0a\u91cd\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u8fd8\u8981\u96f7\u5802\u4e3b\u81ea\u5df2\u51b3\u5b9a\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u6709\u4ec0\u4e48\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u4f4e\u58f0\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u9ad8\u5929\u5065\u5411\u96f7\u67d0\u6311\u6218\uff0c\u9601\u4e0b\u4f5c\u4f55\u6253\u7b97?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5982\u82e5\u96f7\u5802\u4e3b\u4e0d\u654c\u65f6\uff0c\u54b1\u4eec\u51b3\u4e0d\u5750\u89c6\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u5f88\u6b7b\uff0c\u5982\u82e5\u9ec4\u7075\u8981\u662f\u8bf4\uff0c\u7531\u4ed6\u51b3\u5b9a\u65f6\uff0c\u96f7\u65e0\u5f71\u5c31\u5927\u6709\u6587\u7ae0\u597d\u4f5c\u4e86\uff0c\u4f46\u9ec4\u7075\u5374\u53ea\u7b54\u5e94\u4ed6\u5982\u4e0d\u654c\uff0c\u613f\u4f5c\u652f\u63f4\u3002 \u3000\u3000\u4e09\u65b9\u5bf9\u9762\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u4e0d\u80fd\u9762\u4f5c\u5546\u91cf\uff0c\u96f7\u65e0\u5f71\u65e2\u7136\u65e0\u6cd5\u5411\u540e\u9000\u4e86\uff0c\u53ea\u597d\u5f3a\u81ea\u632f\u4f5c\uff0c\u9053\uff1a \u3000\u3000\u201c\u9ad8\u5929\u5065\uff0c\u96f7\u65e0\u5f71\u5982\u4e0d\u548c\u522b\u4eba\u5408\u4f5c\uff0c\u9ad8\u5144\u4e5f\u65e9\u6709\u6740\u6211\u4e4b\u5fc3\u4e86\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4f5c\u8d3c\u5fc3\u865a\uff0c\u6211\u5f85\u4f60\u4e0d\u8584\uff0c\u4f60\u7adf\u80cc\u53db\u4e86\u6211\uff0c\u96f7\u65e0\u5f71\uff0c\u4f60\u624b\u67d3\u7684\u8840\u8165\u3001\u7f6a\u6076\uff0c\u4e0d\u6bd4\u6211\u5c11\u2026\u2026\u201d \u3000\u3000\u96f7\u65e0\u5f71\u63a5\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u9ad8\u5821\u4e3b\u5bf9\u5728\u4e0b\u8bb0\u6068\u751a\u6df1\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u8981\u5148\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u80fd\u591f\u6740\u5f97\u4e86\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u4f60\u8bd5\u8bd5\u770b\uff0c \u4f60\u5982\u80fd\u63a5\u4e0b\u6211\u4e8c\u5341\u62db\uff0c\u6211\u5c31\u4e0d\u518d\u52a8\u4f60\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u9ad8\u5821\u4e3b\uff0c\u795e\u5200\u5802\u6709\u591a\u5c11\u5b9e\u529b\uff0c \u4f60\u5fc3\u4e2d\u660e\u767d\uff0c\u8fd9\u4e9b\u4eba\uff0c\u90fd\u5728\u6211\u7684\u4e25\u5bc6\u63a7\u5236\u4e4b\u4e0b\uff0c\u4ed6\u4eec\u4f1a\u4e3a\u6211\u6548\u6b7b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5982\u82e5\u4f60\u6ca1\u6709\u8fd9\u4e00\u6279\u5b9e\u529b\uff0c\u9ec4\u7075\u4e5f\u4e0d\u4f1a\u548c\u4f60\u5408\u4f5c\uff0c\u4f60\u4e5f\u4e0d\u81f4\u80cc\u53db\u6211\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u8fd8\u6ca1\u6709\u592a\u660e\u767d\u6211\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u548c\u4f60\u52a8\u624b\uff0c\u9664\u975e\uff0c\u6211\u5df2\u65e0\u53ef\u7528\u4e4b\u4eba\u3002\u201d \u3000\u3000\u7a81\u7136\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u56db\u5251\u5408\u58c1\u3002\u201d \u3000\u3000\u56db\u4e2a\u767d\u8863\u5251\u624b\uff0c\u7a81\u7136\u5411\u524d\u4e0a\u4e86\u51e0\u6b65\uff0c\u62e6\u5728\u4e86\u96f7\u65e0\u5f71\u7684\u8eab\u524d\uff0c\u5e03\u6210\u4e86\u4e00\u4e2a\u659c\u5f62\u9635\u52bf\u3002 \u3000\u3000\u9ad8\u5929\u5065\u53cc\u76ee\u4e2d\u795e\u8292\u95ea\u52a8\uff0c\u6253\u91cf\u4e86\u56db\u4e2a\u767d\u8863\u5251\u624b\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8ba4\u8bc6\u6211\u5417?\u201d \u3000\u3000\u56db\u4e2a\u767d\u8863\u5251\u624b\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u597d!\u6211\u662f\u7b2c\u4e00\u5821\u7684\u5821\u4e3b\uff0c\u96f7\u65e0\u5f71\u80cc\u53db\u4e86\u7b2c\u4e00\u5821\uff0c\u4f60\u4eec\u628a\u4ed6\u6740\u6b7b\uff0c\u8c01\u4e0b\u7684\u624b\uff0c\u51c6\u5c31\u53ef\u4ee5\u4efb\u795e\u5200\u5802\u4e3b\u3002\u201d \u3000\u3000\u56db\u4e2a\u4eba\u9762\u9762\u76f8\u89d1\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7684\u6761\u4ef6\uff0c\u4f7f\u4ed6\u4eec\u52a8\u5fc3\uff0c\u4f46\u4ed6\u4eec\u5374\u53c8\u4e0d\u6562\u5bf9\u96f7\u65e0\u5f71\u51fa\u624b\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u770b\u5230\u56db\u4e2a\u767d\u8863\u5251\u624b\u4eec\u7684\u795e\u60c5\uff0c\u4e0d\u7981\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u770b\u6765\u9ad8\u5929\u5065\u5bf9\u8fd9\u4e9b\u4eba\uff0c\u8fd8\u6709\u7740\u5f88\u5927\u7684\u5f71\u54cd\u529b\u3002\u5982\u82e5\u4ed6\u518d\u591a\u8bf4\u51e0\u53e5\uff0c\u8fd9\u4e9b\u4eba\u53ef\u80fd\u4f1a\u88ab\u4ed6\u8bf4\u52a8\u3002 \u3000\u3000\u4ed6\u672c\u60f3\u91c7\u53d6\u5b88\u52bf\uff0c\u8ba9\u9ad8\u5929\u5065\u5148\u548c\u9ec4\u7075\u7b49\u51b2\u7a81\uff0c\u4f46\u770b\u60c5\u5f62\uff0c\u5374\u5df2\u65e0\u6cd5\u5b88\u5f97\u4e0b\u53bb\u4e86\uff0c\u53ea\u597d\u4e00\u54ac\u7259\uff0c\u53d8\u4e86\u4e3b\u610f\u3002 \u3000\u3000\u9ad8\u5929\u5065\u773c\u770b\u56db\u4e2a\u767d\u8863\u5251\u624b\uff0c\u4e3a\u4e4b\u52a8\u5bb9\uff0c\u5fc3\u4e2d\u6697\u559c\uff0c\u5fd6\u9053\uff1a\u201c\u80fd\u8bf4\u52a8\u8fd9\u6279\u5251\u624b\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u8bf4\u52a8\u5176\u4ed6\u7684\u4eba\u4e86\u3002\u201d\u5f53\u4e0b\u51b7\u7b11\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u522b\u5fd8\u4e86\uff0c\u4ed6\u4eec\u90fd\u662f\u7b2c\u4e00\u5821\u4e2d\u7684\u4eba\uff0c\u6211\u662f\u7b2c\u4e00\u5821\u4e2d\u7684\u5821\u4e3b\uff0c\u4ed6\u4eec\u5fc3\u76ee\u4e2d\uff0c\u65e9\u5df2\u6709\u4e86\u8f7b\u91cd\u662f\u975e\u4e4b\u5ff5\uff0c\u5982\u82e5\u9601\u4e0b\u60f3\u4ed7\u4ed6\u4eec\u6765\u5bf9\u4ed8\u6211\uff0c\u90a3\u5c31\u9519\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u7a81\u7136\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u6740!\u201d \u3000\u3000\u56db\u4e2a\u767d\u8863\u5251\u624b\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u4ecd\u7136\u7ad9\u7740\u672a\u52a8\u3002 \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u5f53\u771f\u662f\u81f3\u6b7b\u4e0d\u609f\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u7684\u8138\u8272\u53d8\u4e86\uff0c\u5982\u82e5\u4ed6\u65e0\u6cd5\u6307\u6325\u8fd9\u4e9b\u5251\u624b\uff0c\u90a3\u5c31\u5b8c\u5168\u4e27\u5931\u4e86\u672c\u94b1\u3002 \u3000\u3000\u4f46\u90a3\u56db\u4e2a\u767d\u8863\u5251\u624b\uff0c\u6014\u4e86\u4e00\u6014\u4e4b\u95f4\uff0c\u5374\u81ea\u884c\u6392\u51fa\u4e86\u4e00\u4e2a\u65b9\u5f62\u9635\u52bf\uff0c\u4e24\u524d\u4e24\u540e\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u9ad8\u5929\u5065\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5374\u957f\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5821\u4e3b\u5bf9\u6211\u96f7\u67d0\u4eba\u7684\u63a7\u5236\u80fd\u529b\u4f4e\u4f30\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u4f38\u624b\u6478\u4f4f\u4e86\u5200\u67c4\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u7684\u5fff\u6124\uff0c\u60ca\u6115\uff0c\u4ea4\u7ec7\u6210\u4e86\u4e00\u7247\u6012\u706b\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u767d\u53d1\u8427\u8427\u7684\u8001\u5a18\uff0c\u7a81\u7136\u4e00\u6643\u8eab\u8eaf\uff0c\u8dc3\u843d\u5728\u9ad8\u5929\u5065\u7684\u8eab\u524d\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u8fd9\u4e9b\u4eba\u662f\u4e0d\u662f\u90fd\u8be5\u6b7b?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8be5\u6b7b\u3002\u201d \u3000\u3000\u767d\u53d1\u8005\u59aa\u9053\uff1a \u201c\u597d!\u65e2\u7136\u8be5\u6b7b\uff0c\u8001\u8eab\u5c31\u66ff\u5821\u4e3b\uff0c\u5904\u7f6e\u4e86\u4ed6\u4eec\u3002\u201d \u3000\u3000\u53f3\u624b\u4e00\u63a2\uff0c\u5411\u53f3\u8fb9\u4e00\u4e2a\u767d\u8863\u5251\u624b\u6293\u53bb\u3002 \u3000\u3000\u5979\u7684\u8eab\u8eaf\u53c8\u9ed1\u53c8\u7626\uff0c\u5374\u7559\u7740\u4e00\u5bf8\u957f\u77ed\u7684\u6307\u7532\u3002 \u3000\u3000\u8fd9\u6837\u7684\u624b\uff0c\u51e0\u4e4e\u5df2\u7ecf\u4e0d\u50cf\u4e00\u53ea\u624b\uff0c\u50cf\u4e00\u53ea\u9e1f\u722a\u3002 \u3000\u3000\u5979\u7684\u4eba\uff0c\u4e5f\u7075\u5de7\u5f97\u50cf\u4e00\u53ea\u9e1f\u3002 \u3000\u3000\u6574\u4e2a\u7684\u8eab\u8eaf\uff0c\u968f\u7740\u90a3\u4f38\u51fa\u7684\u53f3\u624b\uff0c\u5ffd\u7136\u95f4\u5411\u524d\u51b2\u53bb\u3002 \u3000\u3000\u5bd2\u5149\u95ea\u52a8\uff0c\u767d\u8863\u4eba\u624b\u4e2d\u7684\u957f\u5251\uff0c\u75be\u5feb\u7684\u65a9\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e00\u5251\u53d1\u52a8\uff0c\u4e09\u5251\u968f\u8fdb\uff0c\u539f\u672c\u7f13\u7f13\u800c\u884c\u7684\u56db\u4e2a\u767d\u8863\u4eba\uff0c\u5ffd\u7136\u95f4\uff0c\u628a\u90a3\u767d\u53d1\u8001\u59aa\u56f4\u4e86\u8d77\u6765\u3002 \u3000\u3000\u53cc\u65b9\u7684\u52a8\u4f5c\uff0c\u90fd\u6781\u4e3a\u5feb\u901f\uff0c\u5feb\u5f97\u4eba\u76ee\u4e0d\u6687\u63a5\u3002 \u3000\u3000\u5251\u5149\u95ea\u52a8\uff0c\u9c9c\u8840\u8fdb\u5c04\u3002 \u3000\u3000\u4e00\u4e2a\u767d\u8863\u5251\u624b\uff0c\u5012\u4e86\u4e0b\u53bb\u3002\u4ed6\u6b7b\u5728\u81ea\u5df1\u4eba\u7684\u5251\u4e0b\u3002 \u3000\u3000\u539f\u6765\uff0c\u90a3\u767d\u53d1\u8001\u59aa\u4e00\u51fa\u624b\uff0c\u5c31\u6293\u4f4f\u4e86\u4e00\u4e2a\u767d\u8863\u4eba\uff0c\u5411\u5251\u4e0a\u8fce\u53bb\u3002 \u3000\u3000\u53ea\u95fb\u5979\u4e00\u9635\u558b\u558b\u602a\u7b11\uff0c\u53cc\u624b\u9f50\u51fa\uff0c\u7adf\u7136\u6293\u4f4f\u4e0b\u4e24\u67c4\u957f\u5251\u3002 \u3000\u3000\u950b\u5229\u7684\u5251\u5203\uff0c\u7adf\u7136\u65e0\u6cd5\u4f24\u5230\u5979\u67af\u7626\u7684\u624b\u6307\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u767d\u8863\u5251\u58eb\uff0c\u672c\u6765\u662f\u6709\u7740\u5f88\u597d\u7684\u9ed8\u5951\uff0c\u4f46\u5374\u5728\u4e00\u7167\u9762\u95f4\uff0c\u5c31\u6b7b\u4e86\u4e00\u4e2a\uff0c\u800c\u4e14\u662f\u6b7b\u5728\u81ea\u5df1\u540c\u4f34\u7684\u5251\u4e0b\u3002 \u3000\u3000\u8fd9\u5c31\u4f7f\u4e09\u4eba\u4e00\u5446\u3002 \u3000\u3000\u767d\u53d1\u8001\u59aa\u5c31\u5728\u4e09\u4eba\u4e00\u5446\u95f4\uff0c\u51fa\u624b\u6293\u4f4f\u4e86\u5de6\u53f3\u4e24\u67c4\u957f\u5251\u3002 \u3000\u3000\u8fd8\u6709\u4e00\u652f\u5251\uff0c\u5c31\u662f\u6740\u6b7b\u81ea\u5df1\u540c\u4f34\u7684\u90a3\u652f\u5251\uff0c\u5e26\u7740\u8840\u5149\uff0c\u75be\u5c04\u8fc7\u6765\u3002 \u3000\u3000\u767d\u53d1\u8001\u59aa\u7a81\u7136\u4e00\u5f20\u53e3\uff0c\u5c04\u51fa\u4e86\u4e00\u7f15\u5bd2\u8292\u3002 \u3000\u3000\u6b63\u4e2d\u7684\u767d\u8863\u4eba\uff0c\u7acb\u523b\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e00\u5f20\u82cd\u767d\u7684\u8138\u4e0a\uff0c\u5ffd\u7136\u6cdb\u8d77\u4e86\u4e00\u5c42\u9ed1\u6c14\uff0c \u3000\u3000\u4e00\u679a\u84dd\u6c6a\u6c6a\u7684\u94a2\u9488\uff0c\u6b63\u9489\u5728\u4ed6\u9876\u95e8\u4e0a\uff0c\u767d\u8863\u4eba\u7684\u5251\u52bf\u8fd8\u672a\u5230\u767d\u53d1\u8001\u59aa\u7684\u524d\u80f8\uff0c\u597d\u6bd2\u7684\u4e00\u9488\u3002 \u3000\u3000\u8fd9\u4e0d\u8fc7\u662f\u4e00\u5239\u90a3\u7684\u5de5\u592b\uff0c\u5de6\u53f3\u4e24\u4e2a\u767d\u8863\u4eba\uff0c\u6b63\u5728\u8f6c\u624b\u4e2d\u957f\u5251\uff0c\u5c06\u8155\u5411\u540e\u6536\u53bb\u3002 \u3000\u3000\u5374\u4e0d\u6599\u767d\u53d1\u8005\u59aa\u7a81\u7136\u4e00\u653e\u53cc\u624b\uff0c\u53cc\u8896\u4e2d\u5bd2\u8292\u95ea\u52a8\uff0c \u53c8\u5c04\u51fa\u4e24\u7f15\u94f6\u8292\uff0c\u4e24\u4e2a\u767d\u8863\u4eba\u53c8\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u53c8\u662f\u6bd2\u9488\uff0c\u8fd9\u767d\u53d1\u8001\u59aa\u7684\u8eab\u4e0a\uff0c\u4f3c\u662f\u5230\u5904\u90fd\u6709\u6bd2\u9488\uff0c\u5b9e\u53eb\u4eba\u9632\u4e0d\u80dc\u9632\u3002 \u3000\u3000\u56db\u4e2a\u7b2c\u4e00\u6d41\u7684\u5251\u624b\uff0c\u5c31\u5728\u8fd9\u4e24\u4e09\u4e2a\u56de\u5408\u4e2d\uff0c\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u89c1\u591a\u8bc6\u5e7f\u7684\u9ec4\u7075\uff0c\u7a81\u7136\u60f3\u8d77\u4e86\u4e00\u4e2a\u4eba\uff0c\u4e0d\u7981\u5931\u58f0\u800c\u53eb\uff0c\u9053\uff1a\u201c\u5343\u9488\u4e07\u8292\uff0c\u5deb\u5a46\u5a46\u3002\u201d \u3000\u3000\u767d\u53d1\u8001\u59aa\u7a81\u7136\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a \u201c\u60f3\u4e0d\u5230\u554a!\u6c5f\u6e56\u4e0a\u8fd8\u6709\u77e5\u6653\u8001\u8eab\u7684\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u60f3\u4e0d\u5230\uff0c\u4f60\u606f\u9690\u4e86\u56db\u5341\u5e74\u540e\uff0c\u7adf\u7136\u53c8\u4f1a\u5728\u6c5f\u6e56\u4e0a\u51fa\u73b0\u3002\u201d \u5deb\u5a46\u5a46\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4f60\u4eec\u90fd\u89c9\u7740\u6211\u8001\u5a46\u5b50\u6b7b\u4e86?\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u4f60\u65e9\u8be5\u6b7b\u4e86\uff0c\u53ef\u662f\u4f60\u7adf\u7136\u8fd8\u6d3b\u7740\u3002\u201d \u3000\u3000\u5deb\u5a46\u5a46\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u4f60\u662f\u4ec0\u4e48\u4eba?\u6562\u5bf9\u8001\u5a46\u5b50\u5982\u6b64\u65e0\u793c\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u8001\u592b\u5c1a\u5929\u4e49\u3002\u201d \u3000\u3000\u5deb\u5a46\u5a46\u9053\uff1a\u201c\u9ec4\u5c71\u5c1a\u5929\u4e49\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u6b63\u662f\u8001\u592b\u3002\u201d \u3000\u3000\u5deb\u5a46\u5a46\u9053\uff1a\u201c\u597d!\u4f60\u51fa\u6765\uff0c\u522b\u4eba\u6015\u4f60\u4eec\u9ec4\u5c71\u5929\u7edd\u6307\uff0c\u8001\u5a46\u5b50\u4e0d\u6015\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u54fc\u54fc\uff0c\u5343\u9488\u4e07\u8292\uff0c\u4e5f\u672a\u5fc5\u80fd\u4f24\u5f97\u4e86\u8001\u592b\u3002\u201d \u3000\u3000\u7f13\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7a81\u7136\u5f00\u4e86\u53e3\uff0c\u9053\uff1a\u201c\u6162\u7740\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u505c\u4e0b\u811a\u6b65\uff0c\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u4f60\u8981\u51fa\u624b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u548c\u9ec4\u7075\u6709\u7ea6\uff0c\u7b49\u6211\u4eec\u529e\u5b8c\u4e86\u6e05\u7406\u672c\u5821\u7684\u5185\u5978\u5927\u4e8b\u4e4b\u540e\uff0c\u518d\u8c08\u6211\u4eec\u7684\u4e8b\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u96f7\u65e0\u5f71\uff0c\u6307\u9053\uff1a\u201c\u53db\u5f92\uff0c\u4e71\u81e3\uff0c\u4f60\u51fa\u6765\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u4e00\u6325\u624b\uff0c\u9053\uff1a\u201c\u8be5\u6211\u51fa\u624b\u7684\u65f6\u5019\uff0c\u6211\u81ea\u4f1a\u51fa\u624b\u3002\u201d \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u5200\u624b\uff0c\u4e00\u9f50\u62d4\u5200\u5411\u524d\u3002 \u3000\u3000\u5deb\u5a46\u5a46\u9053\uff1a\u201c\u597d!\u770b\u4f60\u4eec\u6709\u591a\u5c11\u4e0d\u6015\u6b7b\u7684\u4eba\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u90a3\u8eab\u7740\u516b\u5366\u9053\u888d\u7684\u4e2d\u5e74\u6587\u58eb\uff0c\u7f13\u6b65\u884c\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u5343\u9488\u4e07\u8292\u5df2\u89c1\u9ad8\u660e\uff0c\u8fd9\u7b2c\u4e8c\u9635\uff0c\u8ba9\u7ed9\u6211\u5427!\u201d \u3000\u3000\u5927\u6b65\u884c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5deb\u5a46\u5a46\u9053\uff1a\u201c\u5047\u9053\u58eb\uff0c\u4f60\u8981\u62a2\u751f\u610f?\u201d \u3000\u3000\u9053\u888d\u6587\u58eb\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u603b\u4e0d\u80fd\u8ba9\u4f60\u5a46\u5a46\u4e13\u7f8e\u4e8e\u524d\u3002\u201d \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u5200\u624b\uff0c\u5df2\u628a\u5deb\u5a46\u5a46\u56f4\u4f4f\u3002 \u3000\u3000\u4f46\u90a3\u9053\u888d\u6587\u58eb\uff0c\u8eab\u5b50\u95ea\u4e86\u4e00\u95ea\uff0c\u4eba\u5df2\u7ecf\u5230\u4e86\u5deb\u5a46\u5a46\u7684\u8eab\u524d\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u56db\u4f4d\u8ba4\u5f97\u6211\u5417?\u201d \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u5200\u624b\u6447\u6447\u5934\u3002 \u3000\u3000\u9053\u888d\u6587\u58eb\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u6211\u53eb\u8d3e\u4f3c\u9053\u3002\u201d \u3000\u3000\u6562\u60c5\uff0c\u8fd9\u5047\u9053\u58eb\u771f\u7684\u59d3\u8d3e\uff0c\u800c\u540d\u53eb\u4f3c\u9053\uff0c\u8fd9\u59d3\u540d\uff0c\u5f88\u5408\u4ed6\u7684\u8eab\u4efd\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u672c\u6765\u4e0d\u50cf\u9053\u58eb\uff0c\u5374\u504f\u53c8\u7a7f\u4e86\u4e00\u4ef6\u516b\u5366\u9053\u888d\u3002 \u3000\u3000\u8d3e\u4f3c\u9053\u9762\u5bf9\u7740\u56db\u4e2a\u9ed1\u8863\u5200\u624b\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u73b0\u5728\uff0c\u4f60\u4eec\u53ef\u4ee5\u51fa\u624b\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c0f\u5fc3\u4e2d\u6bd2\u3002\u201d \u3000\u3000\u56db\u4e2a\u9ed1\u8863\u4eba\u76f8\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u7a81\u7136\u5411\u524d\u51b2\u53bb\u3002 \u3000\u3000\u56db\u67c4\u5200\u95ea\u51fa\u56db\u9053\u5bd2\u8292\uff0c\u5206\u5411\u8d3e\u4f3c\u9053\u5288\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u597d\u8fc5\u5feb\u7684\u4e00\u5200\u3002 \u3000\u3000\u8d3e\u4f3c\u9053\u5ffd\u7136\u4e00\u626c\u624b\u4e2d\u7684\u6298\u6247\uff0c\u6247\u67c4\u5f20\u5f00\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u5288\u5288\u556a\u556a\u4e4b\u58f0\uff0c\u8fde\u7ef5\u800c\u6765\u3002 \u3000\u3000\u56db\u67c4\u957f\u5200\uff0c\u5c3d\u4e3a\u9707\u5f00\u3002 \u3000\u3000\u63a5\u4e0b\u4e86\u4e00\u62db\u4e4b\u540e\uff0c\u8d3e\u4f3c\u9053\u672a\u884c\u65bd\u88ad\uff0c\u4f46\u56db\u9ed1\u8863\u5200\u624b\uff0c\u5374\u7a81\u7136\u5012\u4e0b\u4e86\u3002 \u3000\u3000\u9ec4\u7075\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u5982\u4f55\u4f24\u4e86\u4ed6\u4eec?\u201d \u3000\u3000\u8d3e\u4f3c\u9053\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4f24\u4e86\u4ed6\u4eec\uff0c\u800c\u662f\u6740\u4e86\u4ed6\u4eec\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u6740\u4eba\u507f\u547d\u3002\u201d \u3000\u3000\u8d3e\u4f3c\u9053\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u6562\u66ff\u4ed6\u4eec\u51fa\u5934\u5462?\u201d \u3000\u3000\u9ec4\u7075\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\uff01\u201d \u3000\u3000\u96f7\u65e0\u5f71\u795e\u60c5\u5f88\u7d27\u5f20\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\uff0c\u4ed6\u4eec\u600e\u4e48\u6b7b\u7684?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6b7b\u5728\u6298\u6247\u4e2d\u7684\u6bd2\u7c89\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\uff0c\u6211\u5728\u6c5f\u6e56\u4e0a\u65f6\u65e5\u4e0d\u77ed\uff0c\u4ece\u672a\u89c1\u8fc7\u8fd9\u4e2a\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u6de1\u6de1\u7684\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6211\u4e5f\u6ca1\u6709\u542c\u8bf4\u8fc7\u8fd9\u4e2a\u4eba\uff0c\u6240\u4ee5\uff0c\u6211\u6000\u7591\u4ed6\u7528\u7684\u4e0d\u662f\u771f\u6b63\u59d3\u540d\u3002\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u4eba\u5374\u5927\u6b65\u884c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u6e05\u8106\u751c\u7f8e\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u7559\u6b65\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u548c\u9ad8\u5e7d\u5170\uff0c\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u559d\u53eb\u7684\u4eba\uff0c\u6b63\u662f\u5434\u53cc\u5973\u3002 \u3000\u3000\u5bf9\u9ad8\u5e7d\u5170\u7684\u667a\u6167\uff0c\u9ec4\u7075\u65e9\u5df2\u6697\u4e2d\u5fc3\u6298\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u4e24\u4f4d\uff0c\u6709\u4ec0\u4e48\u5429\u5490?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u4e2a\u5047\u8001\u9053\u7684\u6298\u6247\u4f20\u6bd2\u624b\u6cd5\uff0c\u6211\u7565\u77e5\u4e00\u4e8c\uff0c\u6240\u4ee5\uff0c\u8bf7\u5821\u4e3b\u628a\u8fd9\u4e00\u9635\u8ba9\u7ed9\u6211\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u4e2a\u5047\u8001\u9053\u4f20\u6bd2\u624b\u6cd5\u9ad8\u660e\uff0c\u4e0d\u77e5\u9053\u5bb3\u4e86\u591a\u5c11\u4eba\uff0c\u6240\u4ee5\uff0c\u6211\u8981\u5e2e\u5927\u59ca\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u4f60\u4eec\u8981\u4ee5\u4e8c\u5bf9\u4e00\uff0c\u4e0d\u6015\u574f\u4e86\u6c5f\u6e56\u89c4\u77e9\uff0c\u574f\u4e86\u81ea\u5df1\u7684\u540d\u58f0\u3002\u201d \u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u8fd9\u4e24\u4f4d\u59d1\u5a18\u8054\u624b\u51fa\u6218\uff0c\u5929\u4e0b\u53ea\u6015\u5f88\u5c11\u4eba\u80fd\u63a5\u5f97\u4e0b\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u884c\u4e8b\u4ece\u6765\u4e0d\u6309\u89c4\u77e9\uff0c\u6240\u4ee5\uff0c\u54b1\u4eec\u7528\u4e0d\u7740\u5b88\u89c4\u77e9\uff0c\u6211\u4eec\u672c\u6765\u662f\u65e0\u540d\u5c0f\u5352\uff0c\u4e5f\u4e0d\u7528\u7740\u6015\u574f\u4e86\u540d\u58f0\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u795e\u60c5\u8083\u7136\uff0c\u5728\u4e00\u4e08\u5916\u5c31\u505c\u4e86\u4e0b\u6765\uff0c\u4e00\u6307\u8d3e\u4f3c\u9053\uff0c\u9053\uff1a\u201c\u8ba4\u8bc6\u6211\u5417?\u201d \u3000\u3000\u8d3e\u4f3c\u9053\u6447\u6447\u5934\u3002 \u3000\u3000\u5434\u53cc\u5973\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u4f46\u6211\u5374\u8ba4\u8bc6\u4f60\uff0c\u8131\u4e0b\u90a3\u5c42\u5047\u9762\u5177\u5427\u3002\u201d \u5979\u53e3\u4e2d\u624d\u8bf4\u8bdd\uff0c\u4e24\u9053\u773c\u795e\u5374\u76ef\u5728\u5bf9\u65b9\u7684\u6298\u6247\u4e4b\u4e0a\uff0c\u53cc\u624b\u84c4\u52bf\u5f85\u53d1\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7ad9\u5728\u5434\u53cc\u5973\u8eab\u540e\u53f3\u4fa7\u516b\u5c3a\u5904\uff0c\u53cc\u624b\u90fd\u63e1\u4f4f\u4e86\u5251\u67c4\u3002 \u3000\u3000\u5979\u4eec\u72c2\u50b2\uff0c\u81ea\u8d1f\uff0c\u4f46\u7edd\u4e0d\u5927\u610f\u3002 \u3000\u3000\u8d3e\u4f3c\u9053\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u7d20\u6627\u5e73\u751f\uff0c\u59d1\u5a18\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u6bd2\u624b\u516c\u5b50\uff0c\u4f60\u5316\u6210\u4e86\u7070\uff0c\u6211\u4e5f\u80fd\u628a\u4f60\u8ba4\u51fa\u6765\u3002\u201d \u3000\u3000\u53ea\u542c\u8d3e\u4f3c\u9053\u54c8\u54c8\u4e00\u7b11\u9053\uff1a \u201c\u5929\u9b54\u5973\uff0c\u60f3\u4e0d\u5230\u4f60\u7adf\u5bf9\u6211\u6709\u5982\u6b64\u6df1\u523b\u7684\u5370\u8c61\u3002\u201d \u53e3\u4e2d\u8bf4\u8bdd\uff0c\u4eba\u5374\u540e\u9000\u4e86\u4e00\u4e08\u591a\u8fdc\uff0c\u8131\u4e0b\u9053\u888d\uff0e\u53d6\u4e0b\u4e0b\u4eba\u76ae\u9762\u5177\uff0c\u9732\u51fa\u4e86\u9e70\u9f3b\u7684\u672c\u6765\u7684\u9762\u76ee\uff0c\u679c\u7136\u662f\u5927\u540d\u9f0e\u9f0e\u7684\u6bd2\u624b\u516c\u5b50\u3002 \u3000\u3000\u8d3e\u4f3c\u9053\u6ca1\u6709\u4eba\u77e5\u9053\uff0c\u4f46\u6bd2\u624b\u516c\u5b50\uff0c\u5374\u662f\u65e0\u4eba\u4e0d\u77e5\uff0c\u65e0\u4eba\u4e0d\u6653\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u7384\u5a18\uff0c\u5c1a\u5929\u4e49\u592b\u5987\uff0c\u7ea2\u82b1\u3001\u7eff\u53f6\u3001\u534e\u5999\u771f\u7b49\uff0c\u90fd\u5df2\u7ecf\u73b0\u8eab\u51fa\u6765\uff0c\u903c\u5411\u4e86\u573a\u4e2d\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u4e00\u73b0\u8eab\uff0c\u987f\u4f7f\u5f97\u9ec4\u7075\u4e00\u65b9\u58f0\u52bf\u5927\u589e\u3002 \u3000\u3000\u9ad8\u5929\u5065\u539f\u60f3\u5728\u96f7\u65e0\u5f71\u56f4\u653b\u94c1\u82b1\u9662\u90a3\u4e00\u573a\u640f\u6740\u4e4b\u4e2d\uff0c\u7b2c\u4e00\u5821\u867d\u7136\u4f24\u4ea1\u5f88\u5927\uff0c\u9634\u9633\u5821\u4ea6\u5fc5\u662f\u7cbe\u9510\u5c3d\u5931\u3002 \u3000\u3000\u4f46\u51fa\u4eba\u610f\u5916\u7684\u662f\uff0c\u9634\u9633\u5821\u4e2d\uff0c\u5927\u90e8\u5206\u7684\u4e3b\u8111\uff0c\u90fd\u672a\u635f\u4f24\u3002 \u3000\u3000\u51b7\u624b\u7f57\u5239\u534e\u5999\u771f\u4f3c\u662f\u65e9\u548c\u7384\u5a18\u5546\u91cf\u597d\u4e86\uff0c\u4e24\u4e2a\u4eba\u5e76\u80a9\u800c\u884c\uff0c\u5835\u4f4f\u4e86\u5343\u9488\u4e07\u8292\u5deb\u5a46\u5a46\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6bd2\u624b\u516c\u5b50\uff0c\u4f60\u8fc7\u6765\u5427!\u201d \u3000\u3000\u539f\u6765\uff0c\u6bd2\u624b\u516c\u5b50\u6062\u590d\u4e86\u672c\u6765\u7684\u9762\u76ee\u4e4b\u540e\uff0c\u5c31\u4e00\u76f4\u505c\u5728\u539f\u5730\uff0c\u672a\u518d\u5411\u524d\u884c\u8fdb\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5ffd\u7136\u95f4\u6709\u4e00\u79cd\u82f1\u96c4\u672b\u8def\u7684\u611f\u89c9\u3002 \u3000\u3000\u4ed6\u53f1\u54a4\u98ce\u4e91\uff0c\u5a01\u9707\u6c5f\u6e56\uff0c\u60f3\u4e0d\u5230\uff0c\u5c31\u5728\u81ea\u5df1\u4eb2\u624b\u5efa\u9020\u7684\u7b2c\u4e00\u5821\u4e2d\uff0c\u6570\u65e5\u4e4b\u95f4\uff0c\u7adf\u95f9\u5f97\u4f17\u53db\u4eb2\u79bb\u3002 \u3000\u3000\u5343\u9488\u4e07\u8292\uff0c\u6bd2\u624b\u516c\u5b50\uff0c\u4e24\u4e2a\u4eba\u7684\u6c14\u52bf\uff0c\u4f3c\u662f\u90fd\u5df2\u88ab\u5bf9\u65b9\u538b\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5fc3\u4e2d\u660e\u767d\uff0c\u6b64\u523b\u52c9\u5f3a\u51fa\u624b\uff0c \u5df2\u5728\u5fc3\u7406\u4e0a\u5148\u8f93\u4e86\u4e00\u7b79\u3002 \u5f53\u673a\u7acb\u65ad\uff0c\u7acb\u523b\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9000\u56de\u70df\u9501\u697c\u4e2d\u3002\u201d \u3000\u3000\u4f46\u89c1\u4eba\u5f71\u95ea\u52a8\uff0c\u7247\u523b\u95f4\uff0c\u8d70\u7684\u4e00\u4e2a\u4e0d\u5269\u3002 \u3000\u3000\u9ec4\u7075\u56de\u987e\u4e86\u96f7\u65e0\u5f71\u4e00\u773c\uff0c\u9053\uff1a \u201c\u96f7\u5144\uff0c\u8fd9\u70df\u9501\u697c\u5185\uff0c\u662f\u4e0d\u662f\u6709\u5f88\u591a\u7684\u57cb\u4f0f?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u8fd9\u4e2a\u5730\u65b9\uff0c\u4e00\u76f4\u662f\u5821\u4e3b\u7684\u79c1\u4eba\u5730\u65b9\uff0c\u5728\u4e0b\u6ca1\u6709\u8fdb\u53bb\u8fc7\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5b8c\u5168\u4e0d\u77e5\u9053\uff0c\u6211\u53ea\u77e5\u9053\u8fd9\u4e2a\u5730\u65b9\uff0c\u9632\u8303\u5f88\u68ee\u4e25\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u4eec\u5e94\u8be5\u5982\u4f55?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fdb\uff0c\u70df\u9501\u697c\u4e2d\u4e5f\u8bb8\u6709\u57cb\u4f0f\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u8eb2\u5230\u91cc\u9762\uff0c\u90a3\u8bf4\u660e\u4e86\uff0c\u4ed6\u5df2\u7ecf\u5c71\u7a77\u6c34\u5c3d\uff0c\u522b\u65e0\u53bb\u5904\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5343\u9488\u4e07\u8292\u5982\u679c\u8eb2\u5728\u623f\u4e2d\u6253\u51fa\u7684\u6697\u5668\uff0c\u66f4\u4e3a\u51cc\u5389\u4e00\u4e9b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u4e5f\u8bb8\u8fd8\u6709\u4e9b\u522b\u7684\u57cb\u4f0f\uff0c\u4e0d\u8fc7\uff0c\u6211\u60f3\u4e0d\u901a\u7684\u662f\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8eb2\u8fdb\u53bb\uff0c\u96be\u9053\uff0c\u4ed6\u771f\u7684\u51c6\u5907\u8981\u4ee5\u6b7b\u5b88\u70df\u9501\u697c\uff0c\u548c\u6211\u4eec\u6297\u62d2\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u8fd9\u4e00\u70b9\uff0c\u5f88\u53ef\u7591\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u89c9\u5f97\u4ed6\u4e0d\u662f\u8fd9\u6837\u7684\u4eba\uff0c\u56e0\u4e3a\u4ed6\u8fd8\u6709\u5f88\u591a\u7684\u673a\u4f1a\u53ef\u4ee5\u9003\u8d70\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5bf9!\u4e3a\u4ec0\u4e48\u4e0d\u8d70\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e24\u4e2a\u539f\u56e0\uff0c\u4e00\u4e2a\u662f\u70df\u9501\u697c\u4e2d\u6709\u4ed6\u65e0\u6cd5\u4e22\u5f03\u7684\u4e1c\u897f\uff0c\u4e00\u4e2a\u662f\u4ed6\u6709\u4fe1\u5fc3\uff0c\u5229\u7528\u70df\u9501\u697c\u4e2d\u7684\u57cb\u4f0f\uff0c\u53ef\u4ee5\u548c\u6211\u4eec\u4e00\u51b3\u751f\u6b7b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u4f60\u51c6\u5907\u5982\u4f55\u6253\u7b97?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u9601\u4e0b\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\u5f88\u660e\u663e\uff0c\u96f7\u5802\u4e3b\u51c6\u5907\u5982\u4f55\u5bf9\u4ed8\u6b64\u4e8b?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u7684\u610f\u601d\uff0c\u53ef\u662f\u8981\u5144\u5f1f\u56de\u53bb\uff0c\u518d\u627e\u4e00\u90e8\u5206\u4eba\u6765\uff0c\u4ee3\u4f60\u6253\u5934\u9635\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5144\u5f1f\u5012\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u8fd9\u4e00\u9635\u7531\u5728\u4e0b\u5e26\u8def\uff0c\u5f7c\u6b64\u4e4b\u95f4\u65e2\u7136\u5408\u4f5c\u4e86\uff0c\u6211\u603b\u5e94\u8be5\u77e5\u9053\u96f7\u5802\u4e3b\u7684\u52a8\u5411\u5982\u4f55?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u5982\u662f\u8981\u533a\u533a\u7559\u4e0b\uff0c\u6211\u5c31\u7559\u5728\u6b64\u5730\uff0c\u5982\u679c\u8981\u5144\u5f1f\u8d70\uff0c\u6211\u5c31\u7acb\u523b\u544a\u8f9e\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5f53\u7136\u662f\u9601\u4e0b\u7559\u5728\u8fd9\u91cc\u6700\u597d\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5144\u5f1f\u4ece\u547d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8bf8\u4f4d\uff0c\u9ad8\u5e7d\u5170\u59d1\u5a18\u8bf4\u7684\u4e0d\u9519\uff0c\u9ad8\u5929\u5065\u8eb2\u5165\u8fd9\u4e2a\u70df\u9501\u697c\u4e2d\uff0c\u5fc5\u7136\u6709\u6240\u4ed7\u6043\uff0c\u8fd9\u91cc\u9762\u53ef\u80fd\u662f\u6b65\u6b65\u51f6\u9669\uff0c\u6240\u4ee5\uff0c\u4e0d\u80fd\u4e89\u5148\u6050\u540e\uff0c\u54b1\u4eec\u8fdb\u53bb\u65f6\uff0c\u7531\u6211\u5e26\u8def\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u4e0d\u53ef\u4ee5\uff0c\u4f60\u662f\u4e00\u5821\u4e4b\u4e3b\uff0e\u4e5f\u662f\u9996\u8111\u4eba\u7269\uff0c\u600e\u53ef\u8f7b\u8eab\u6d89\u9669\u3002\u201d \u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u73b0\u5728\u4e8b\u60c5\u5df2\u7ecf\u5b8c\u5168\u660e\u6717\u4e86\uff0c\u4ece\u6b64\u4e4b\u540e\uff0c\u6c5f\u6e56\u4e0a\uff0c\u518d\u4e5f\u6ca1\u6709\u9634\u9633\u5821\u4e86\uff0c\u54ea\u91cc\u8fd8\u5821\u4e3b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5c31\u7b97\u5982\u6b64\u5427!\u5c0f\u5f1f\u5e74\u8f7b\u51e0\u5c81\uff0c\u4e5f\u8be5\u8d70\u5728\u524d\u9762\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u4e0d\u8981\u4e89\u4e86\uff0c \u5144\u5f1f\u5f00\u9053\u3002\u201d \u98de\u8eab\u4e00\u8dc3\uff0c\u4eba\u5df2\u7ffb\u5165\u4e86\u94c1\u6805\u4e4b\u5185\u3002 \u3000 \u7b2c\u5341\u4e00\u56de\u3000\u5251\u4e0d\u7559\u75d5\u4eba\u6709\u60c5 \u3000 \u3000\u3000\u6805\u95e8\u672c\u5df2\u5927\u5f00\uff0c\u56f4\u5899\u5185\uff0c\u662f\u4e00\u7247\u5e7f\u5927\u7684\u7a7a\u5730\uff0c\u8db3\u8db3\u6709\u5341\u4ea9\u5927\u5c0f\u3002 \u3000\u3000\u4f46\u70df\u9501\u697c\u5360\u5730\uff0c\u4e5f\u4e0d\u8fc7\u4ea9\u8bb8\u5de6\u53f3\u3002 \u3000\u3000\u6d41\u76ee\u56db\u987e\uff0c\u770b\u4e0d\u5230\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4f3c\u4e4e\u6240\u6709\u7684\u4eba\uff0c\u90fd\u8eb2\u5165\u4e86\u697c\u4e2d\u3002 \u3000\u3000\u4e3b\u697c\u662f\u7528\u9752\u7816\u548c\u5ca9\u77f3\u780c\u6210\uff0c\u7ed9\u4eba\u4e00\u79cd\u575a\u56fa\u65e0\u6bd4\u7684\u611f\u89c9\u3002 \u3000\u3000\u9ec4\u7075\u9ad8\u58f0\u9053\uff1a\u201c\u957f\u9752\u5feb\u9000\u4e0b\u6765\uff0c\u8ba9\u6211\u5f00\u9053\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u5821\u4e3b\u8bf7\u968f\u6211\u8eab\u540e\u5427!\u4f60\u8981\u548c\u6211\u4e89\uff0c\u90a3\u6211\u5c31\u53ea\u597d\u4e0d\u8ba1\u4e00\u5207\u51b2\u5411\u70df\u9501\u697c\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u957f\u9752\uff0c\u54b1\u4eec\u4e0d\u6015\u6b7b\uff0c\u4e0d\u8fc7\uff0c\u4e5f\u4e0d\u80fd\u8f7b\u8a00\u6b7b\uff0c\u6b7b\u6709\u8f7b\u4e8e\u9e3f\u6bdb\uff0c\u91cd\u5982\u6cf0\u5c71\uff0c\u4f60\u5982\u679c\u5192\u5192\u5931\u5931\u5730\u4e22\u4e86\u751f\u547d\uff0c\u90a3\u5c31\u662f\u8f7b\u5982\u9e3f\u6bdb\u7684\u6b7b\u6cd5\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u77e5\u9053\u5c31\u597d\uff0c\u54b1\u4eec\u4e00\u8d77\u8fc7\u53bb\u5427?\u201d \u3000\u3000\u53f6\u957f\u9752\u7b11\u4e00\u7b11\u9053\uff1a \u201c\u5982\u679c\u524d\u9762\u6ca1\u6709\u4ec0\u4e48\u5371\u9669\uff0c\u6211\u8d70\u5728\u524d\u9762\uff0c\u4e5f\u4e0d\u4f1a\u9047\u4e0a\u4ec0\u4e48\u5371\u9669\uff0c\u4e0d\u5e78\u9047\u4e86\u4ec0\u4e48\u5371\u9669\uff0c\u6b7b\u4e86\u6211\u53f6\u957f\u9752\uff0c\u4fdd\u5b58\u4e0b\u5821\u4e3b\uff0c\u4e5f\u662f\u4e00\u4ef6\u529f\u5fb7\uff0c\u5728\u4e0b\u6b7b\u800c\u4f55\u61be\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u4f60\u5c0f\u5fc3\u4e00\u4e9b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u9ec4\u7075\u8ffd\u968f\u5728\u53f6\u957f\u9752\u7684\u8eab\u540e\uff0c\u5927\u7ea6\u6709\u4e00\u4e08\u591a\u8fdc\u3002 \u3000\u3000\u8fd9\u662f\u53f6\u957f\u9752\u7684\u8981\u6c42\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u4e00\u4ef6\u5f88\u660e\u663e\u7684\u7528\u5fc3\uff0c\u53f6\u957f\u9752\u4e00\u65e6\u9047\u4e0a\u4e86\u4ec0\u4e48\u5371\u9669\uff0c\u9ec4\u7075\u4e5f\u597d\u6709\u5145\u5206\u7684\u5e94\u53d8\u51c6\u5907\u3002 \u3000\u3000\u53f6\u957f\u9752\u63a5\u8fd1\u4e86\u70df\u9501\u697c\u3002 \u3000\u3000\u8def\u4e0a\u5f88\u5e73\u9759\uff0c\u70df\u9501\u697c\u4e0a\uff0c\u4e5f\u662f\u4e00\u7247\u5e7d\u5bc2\u3002 \u3000\u3000\u795e\u79d8\u7684\u7889\u697c\u4e0a\uff0c\u7a97\u95e8\u7d27\u95ed\u3002 \u3000\u3000\u53f6\u957f\u9752\u884c\u8fd1\u697c\u95e8\uff0c\u7528\u957f\u5251\u8f7b\u8f7b\u4e00\u51fb\uff0c\u53d1\u89c9\u7adf\u7136\u662f\u4e00\u5ea7\u94c1\u95e8\u3002 \u3000\u3000\u5404\u5904\u7684\u7a97\u95e8\u4e0a\uff0c\u90fd\u62c9\u4e0a\u4e86\u539a\u539a\u7684\u7a97\u5e55\uff0c\u800c\u4e14\uff0c\u4e5f\u88c5\u7740\u5f88\u7c97\u7684\u94c1\u6805\u3002 \u3000\u3000\u53f6\u957f\u9752\u56de\u987e\u4e86\u9ec4\u7075\u4e00\u773c\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u6240\u6709\u7684\u57cb\u4f0f\uff0c\u90fd\u5728\u697c\u5185\uff0c\u76ee\u524d\u54b1\u4eec\u5982\u4f55\u653b\u5165\u697c\u4e2d?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u90a3\u7a97\u5e55\u4e4b\u540e\uff0c\u53ef\u80fd\u6709\u5f88\u6076\u6bd2\u7684\u8be1\u8ba1\u3002\u201d \u3000\u3000\u9ec4\u7075\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7528\u6697\u5668\u5077\u88ad\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u6536\u96c6\u4e86\u5f88\u591a\u7684\u5723\u6c34\uff0c\u4f46\u5374\u90fd\u4e0d\u77e5\u9053\u5b83\u5728\u4f55\u5904\uff0c\u73b0\u5728\u60f3\u6765\u5b83\u53ef\u80fd\u5b58\u5728\u70df\u9501\u697c\u4e0b\u3002\u201d \u3000\u3000\u9ec4\u7075\u5fc3\u5934\u4e00\u9707\uff0c\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u90a3\u5723\u6c34\u6709\u4f55\u4f5c\u7528\uff0c\u59d1\u5a18\u660e\u767d\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u5f88\u60ed\u6127\uff0c\u6211\u8fde\u5723\u6c34\u4f55\u79cd\u989c\u8272\uff0c\u90fd\u672a\u89c1\u8fc7\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5947\u602a\u5440!\u4ed6\u4e3a\u4ec0\u4e48\u8981\u628a\u5723\u6c34\uff0c\u5b58\u5728\u8fd9\u91cc?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e5f\u8bb8\uff0c\u90a3\u5723\u6c34\u53ef\u4ee5\u6740\u4eba\u3002\u201d \u3000\u3000\u201c\u6740\u4eba!\u4e0d\u4f1a\u5427\uff01\u201d\u4f55\u5bd2\u8863\u8868\u73b0\u5176\u6000\u7591\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u89c1\u8fc7\u5723\u6c34\uff0c\u5723\u6c34\u672c\u8eab\u7edd\u4e0d\u4f1a\u6740\u4eba\uff0c\u4f46\u5982\u7ecf\u8fc7\u4e86\u4e00\u4e9b\u52a0\u5de5\u4e4b\u540e\uff0c\u90a3\u5c31\u5f88\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5355\u5c31\u90a3\u5723\u6c34\u800c\u8a00\uff0c\u5b83\u7684\u4f5c\u7528\u5982\u4f55?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5723\u6c34\u6709\u4e9b\u4ec0\u4e48\u7279\u6b8a\u4f5c\u7528\uff0c\u5728\u4e0b\u6ca1\u6709\u8bd5\u8fc7\uff0c\u5c31\u5728\u4e0b\u4e2a\u4eba\u7684\u770b\u6cd5\u4e2d\uff0c\u5b83\u597d\u50cf\u5f88\u5bb9\u6613\u71c3\u70e7\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bb9\u6613\u71c3\u70e7?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bf9!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u671b\u7740\u70df\u9501\u697c\u8bf4\u9053\uff1a \u201c\u70df\u9501\u697c\uff0c\u70df\u9501\u7684\u610f\u601d\uff0c\u5c31\u662f\u4e0d\u8ba9 \u522b\u4eba\u770b\u7684\u5f88\u6e05\u695a\uff0c\u56e0\u4e3a\uff0c\u5145\u6ee1\u7740\u795e\u79d8\uff0c\u5145\u6ee1\u7740\u5371\u673a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u5145\u6ee1\u7740\u5371\u673a\u7684\u610f\u601d\uff0c\u662f\u8bf4\u8fd9\u5ea7\u697c\u4e2d\u7684\u57cb\u4f0f\uff0c\u4f7f\u4eba\u9632\u4e0d\u80dc\u9632\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u5ffd\u7136\u60f3\u5230\u9ad8\u5929\u5065\u8bf4\u8fc7\u7684\u4e00\u53e5\u8bdd\uff0c\u90a3\u5c31\u662f\u4ed6\u662f\u4e00\u4ee3\u82f1\u96c4\uff0c\u4ed6\u4e00\u751f\u4e2d\u7f9e\u8fb1\u8fc7\u5f88\u591a\u4eba\uff0c\u4f46\u4ed6\u4e0d\u8ba9\u4eba\u7f9e\u8fb1\u4ed6\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54e6!\u90a3\u662f\u8bf4\uff0c\u4e0d\u4f1a\u8ba9\u4eba\u751f\u64d2\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ed6\u9020\u7684\u5b7d\u592a\u591a\u4e86\uff0c\u4ed6\u77e5\u9053\u4e00\u65e6\u906d\u5230\u62a5\u590d\u65f6\uff0c\u90a3\u4f1a\u5341\u5206\u60b2\u60e8\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u55ef!\u6240\u4ee5\uff0c\u6211\u60f3\u5230\u4e86\u8fd9\u5ea7\u697c\uff0c\u53ef\u80fd\u4f1a\u5728\u4e00\u79cd\u5b89\u6392\u4e0b\uff0c\u6574\u5ea7\u7206\u6bc1\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5446\u4e00\u5446\uff0c\u9053\uff1a\u201c\u56e0\u6b64\uff0c\u4ed6\u60f3\u628a\u6211\u4eec\u5168\u90e8\u8bf1\u5165\u697c\u4e2d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7136\u540e\uff0c\u7206\u4e86\u5b83\uff0c\u5316\u4f5c\u98de\u7070\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5927\u6709\u53ef\u80fd\uff0c\u8fd9\u5ea7\u697c\u867d\u7136\u5efa\u5f97\u5f88\u7262\u56fa\u4f46\u5e76\u975e\u9669\u8981\u53ef\u5b88\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u6709\u4ec0\u4e48\u597d\u529e\u6cd5\uff0c\u653b\u4e0b\u8fd9\u5ea7\u70df\u9501\u697c\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u91cc\u9762\uff0c\u4e5f\u8bb8\u6709\u7740\u53ef\u6016\u7684\u57cb\u4f0f\uff0c\u4e0d\u8fc7\uff0c\u54b1\u4eec\u5b81\u53ef\u548c\u4ed6\u540c\u5f52\u4e8e\u5c3d\uff0c\u4e5f\u4e0d\u80fd\u4e34\u5371\u9000\u7f29\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u56de\u987e\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8d31\u59be\u6709\u4e00\u5f97\u4e4b\u611a\uff0c\u4e0d\u77e5\u5821\u4e3b\u662f\u5426\u540c\u610f?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u54b1\u4eec\u5148\u9009\u51e0\u4e2a\u4eba\uff0c\u5f3a\u884c\u653b\u51fb\u4e00\u4e0b\u8bd5\u8bd5\u770b\uff0c\u5176\u4f59\u7684\u4eba\uff0c\u9000\u8fdc\u4e00\u70b9\uff0c\u514d\u5f97\u53d7\u5230\u6ce2\u53ca\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d\u4e3b\u610f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8d31\u59be\u7b97\u4e00\u4e2a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u3001\u5434\u53cc\u5973\u3001\u53f6\u957f\u9752\u3001\u534e\u5999\u771f\u3001\u4e07\u80dc\u3001\u7384\u5a18\uff0c\u4e2a\u4e2a\u8981\u4e89\u53d6\u7b2c\u4e00\u6b21\u653b\u88ad\u7684\u4efb\u52a1\u3002 \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u6837\u5427\uff0c\u7b2c\u4e00\u6b21\u8bd5\u653b\uff0c\u6709\u56db\u4e2a\u4eba\u4e5f\u5c31\u591f\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5728\u4e0b\u4e00\u5b9a\u8981\u7b97\u4e00\u4e2a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8bf8\u4f4d\u4e2a\u4e2a\u4e89\u5148\u6050\u540e\uff0c\u53ea\u6015\u5f88\u96be\u7406\u51fa\u4e2a\u5934\u7eea\uff0c\u6240\u4ee5\uff0c\u8fd9\u56db\u4e2a\u4eba\uff0c\u7531\u6211\u6765\u9009\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u9ad8\u5e7d\u5170\uff0c\u63a5\u9053\uff1a \u201d\u9ad8\u5e7d\u5170\u4e00\u4e2a\uff0c\u5728\u4e0b\u4e00\u4e2a\uff0c\u53f6\u5144\u4e00\u4e2a\uff0c\u7b2c\u56db\u4e2a\u4eba\uff0c\u5728\u4e0b\u5e0c\u671b\u96f7\u5144\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6211!\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5bf9!\u9ad8\u59d1\u5a18\u548c\u4f60\u90fd\u662f\u7b2c\u4e00\u5821\u4e2d\u4eba\uff0c\u6211\u548c\u53f6\u957f\u9752\uff0c\u6765\u81ea\u5821\u5916\uff0c\u8fd9\u6837\u7684\u5206\u914d\uff0c\u662f\u4e0d\u662f\u5f88\u516c\u5e73\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u89c9\u5f97\u5f88\u516c\u5e73\uff0c\u96f7\u5802\u4e3b\u6709\u4f55\u9ad8\u89c1!\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5c31\u7b97\u6709\u9ad8\u89c1\uff0c\u597d\u50cf\u4e5f\u4e0d\u884c\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u96f7\u5802\u4e3b\u662f\u540c\u610f\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u770b\u6765\uff0c\u6211\u4e0d\u540c\u610f\u4e5f\u4e0d\u884c\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u597d!\u96f7\u5802\u4e3b\uff0c\u54b1\u4eec\u662f\u7b2c\u4e00\u5821\u7684\u4eba\uff0c\u5e94\u8be5\u5148\u6253\u524d\u950b\uff0c\u8d70!\u201d \u3000\u3000\u5f53\u5148\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u4e8b\u5df2\u81f3\u6b64\uff0c\u96f7\u65e0\u5f71\u5b9e\u5df2\u65e0\u6cd5\u518d\u63a8\u8f9e\u4e86\uff0c \u53ea\u597d\u5411\u524d\u884c\u53bb\u3002 \u9ec4\u7075\u9053\uff1a\u201c\u957f\u9752\uff0c\u54b1\u4eec\u4e5f\u4e0a\u53bb\u5427!\u5176\u4f59\u7684\u4eba\u3002\u8bf7\u518d\u9000\u4e09\u4e08\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u3001\u5434\u53cc\u5973\u7b49\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u5411\u540e\u9000\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u9ad8\u5e7d\u5170\u4e00\u9a6c\u5f53\u5148\uff0c \u5df2\u7ecf\u5230\u4e86\u697c\u95e8\u524d\u9762\u3002 \u96f7\u65e0\u5f71\u867d\u7136\u8d70\u5f97\u5f88\u6162\uff0c\u4f46\u7ec8\u4e8e\u8fd8\u662f\u8d70\u4e86\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u54b1\u4eec\u5e94\u8be5\u5982\u4f55\u4e0b\u624b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u4f38\u624b\u4e00\u62cd\u697c\u95e8\uff0c\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u662f\u94c1\u95e8\uff0c\u60f3\u649e\u7834\u94c1\u95e8\uff0c\u53ea\u6015\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4f38\u624b\u5728\u94c1\u95e8\u4e0a\u51fb\u4e86\u4e24\u4e0b\u9053\uff1a \u201c\u8fd9\u94c1\u95e8\u575a\u7262\u7684\u5f88\uff0c\u653b\u7834\u4e0d\u6613\uff0c\u6211\u770b\uff0c\u5012\u4e0d\u5982\u7531\u7a97\u5b50\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u7a81\u7136\u98de\u8eab\u800c\u8d77\uff0c\u53cc\u81c2\u6296\u52a8\uff0c \u4eba\u5df2\u5411\u4e0a\u5347\u8d77\u4e86\u4e00\u4e08\u4e03\u516b\u5c3a\u9ad8\uff0c\u5de6\u624b\u4e00\u63a2\uff0c\u6293\u4f4f\u4e86\u7a97\u4e0a\u7684\u94c1\u6805\u3002 \u3000\u3000\u53f3\u624b\u957f\u5251\u5df2\u7136\u51fa\u9798\uff0c\u62a4\u4f4f\u4e86\u9762\u95e8\u3002 \u3000\u3000\u4f46\u90a3\u539a\u539a\u7684\u5e37\u5b50\u4e2d\uff0c\u7adf\u7136\u6ca1\u6709\u53cd\u5e94\uff0c\u6ca1\u6709\u6697\u5668\uff0c\u4e5f\u6ca1\u6709\u5175\u5203\u3002 \u3000\u3000\u53f6\u957f\u9752\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u957f\u5251\u63a2\u5165\u94c1\u6805\u4e4b\u5185\uff0c\u7528\u529b\u4e00\u6405\uff0c\u7acb\u523b\u628a\u7a97\u5185\u7684\u7a97\u5e03\u6405\u788e\u3002 \u3000\u3000\u8fd9\u662f\u70df\u9501\u697c\u7684\u7b2c\u4e8c\u5c42\u697c\u3002 \u3000\u3000\u697c\u5185\u5e03\u7f6e\u5f97\u5f88\u534e\u4e3d\uff0c\u4f46\u5374\u88ab\u5206\u9694\u6210\u5f88\u591a\u623f\u95f4\u3002 \u3000\u3000\u53f6\u957f\u9752\u80fd\u591f\u770b\u5230\u7684\uff0c\u53ea\u662f\u4e00\u6bb5\u5c0f\u5c0f\u7684\u5385\u623f\u3002 \u3000\u3000\u90a3\u4e00\u7247\u7a7a\u65f7\u7684\u5730\u65b9\uff0c\u5927\u7ea6\u6709\u4e00\u4e08\u65b9\u5706\u3002 \u3000\u3000\u4f46\u5374\u9759\u9759\u7684\u4e0d\u89c1\u4eba\u8ff9\u3002 \u3000\u3000\u9ec4\u7075\u5f88\u62c5\u5fc3\u53f6\u957f\u9752\u7684\u5b89\u5371\uff0c\u4f46\u4ed6\u4e0d\u6562\u51fa\u58f0\u547c\u53eb\uff0c\u751f\u6050\u5206\u4e86\u4ed6\u7684\u5fc3\u795e\uff0c\u53d7\u654c\u6697\u7b97\u3002 \u3000\u3000\u53f6\u957f\u9752\u7528\u529b\u62c9\u4e86\u4e00\u4e0b\u94c1\u6805\uff0c\u53d1\u89c9\u94c1\u6805\u575a\u786c\u5f02\u5e38\u3002 \u3000\u3000\u8fd9\u4e5f\u8bb8\u5c31\u662f\u4ed6\u4eec\u653e\u5fc3\u7684\u5730\u65b9\u3002 \u3000\u3000\u9ec4\u7075\u7a81\u7136\u7eb5\u8eab\u4e00\u8dc3\uff0c\u6293\u4f4f\u94c1\u6805\uff0c\u5411\u7a97\u5185\u77a7\u4e86\u4e00\u773c\uff0c\u7acb\u751f\u8b66\u89c9\uff0c\u9053\uff1a\u201c\u957f\u9752\uff0c\u5feb\u4e9b\u677e\u624b\u3002\u201d \u3000\u3000\u53ea\u542c\u4e00\u9635\u9634\u68ee\u7684\u51b7\u7b11\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a \u201c\u53ea\u6015\u6765\u4e0d\u53ca\u4e86\uff0c\u8fd9\u94c1\u6805\u4e0a\uff0c\u6d82\u6709\u5947\u6bd2\uff0c\u53ea\u8981\u89e6\u53ca\u94c1\u6805\uff0c\u6bd2\u6027\u4fbf\u4fb5\u4e86\u808c\u80a4\uff0c\u867d\u7136\u6bd2\u6027\u53d1\u4f5c\u7684\u4e0d\u592a\u5feb\uff0c\u4e0d\u8fc7\u5374\u5341\u5206\u987d\u5f3a\uff0c\u5f88\u96be\u6297\u62d2\uff0c\u9664\u4e86\u6211\u7684\u72ec\u95e8\u89e3\u836f\uff0c\u518d\u65e0\u53ef\u4ee5\u89e3\u6551\u7684\u836f\u7269\u3002\u201d \u3000\u3000\u9ec4\u7075\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u5012\u672a\u5fc5\u3002\u201d \u3000\u3000\u4e00\u6247\u623f\u95e8\u7a81\u7136\u5927\u5f00\uff0c\u9ad8\u5929\u5065\u7f13\u6b65\u800c\u51fa\uff0c\u63a5\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u6211\u6ca1\u6709\u60f3\u5230\u9493\u5230\u4f60\u8fd9\u6761\u9c7c\uff0c\u6bd2\u6027\u5728\u534a\u4e2a\u65f6\u8fb0\u540e\uff0c\u5f00\u59cb\u53d1\u4f5c\uff0c\u5148\u662f\u624b\u81c2 \u3000\u3000\u9ebb\u6728\uff0c\u5f88\u5feb\u6269\u53ca\u5168\u8eab\uff0c\u5b50\u4e0d\u8fc7\u5348\uff0c\u5348\u4e0d\u89c1\u5b50\uff0c\u5341\u4e8c\u4e2a\u65f6\u8fb0\u5185\uff0c\u6bd2\u53d1\u800c\u6b7b\uff0c\u516d\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u6bd2\u6027\u8fd8\u53ef\u4ee5\u89e3\u53bb\uff0c\u4f60\u81ea\u5df1\u597d\u597d\u7684\u60f3\u60f3\u5427!\u201d \u3000\u3000\u9ec4\u7075\u3001\u53f6\u957f\u9752\u98d8\u843d\u5b9e\u5730\u3002 \u3000\u3000\u7ec6\u770b\u5de6\u624b\uff0c\u53ea\u89c9\u89e6\u53ca\u94c1\u6805\u4e4b\u5904\uff0c\u4e00\u7247\u9752\u7d2b\u3002 \u3000\u3000\u53f6\u957f\u9752\u7a81\u7136\u4e00\u6325\u957f\u5251\uff0c\u65a9\u5411\u5de6\u81c2\u3002 \u3000\u3000\u4f46\u5374\u88ab\u9ec4\u7075\u51fa\u624b\u62e6\u4f4f\uff0c\u9053\uff1a\u201c\u957f\u9752\uff0c\u5982\u82e5\u9ad8\u5929\u5065\u8bf4\u7684\u662f\u5b9e\u8bdd\uff0c\u65a9\u4e0b\u8fd9\u4e00\u6761\u5de6\u81c2\uff0c\u4e5f\u65e0\u6cd5\u963b\u6b62\u4fb5\u5165\u4f53\u5185\u7684\u6bd2\u6027\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u96be\u9053\u54b1\u4eec\u5c31\u8fd9\u6837\u8ba4\u547d\u4e86\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u6709\u65f6\u95f4\uff0c\u8bd5\u8bd5\u770b\u6709\u6ca1\u6709\u89e3\u6bd2\u4e4b\u6cd5\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u65e9\u8be5\u60f3\u5230\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u534e\u5999\u771f\u3001\u7384\u5a18\u5df2\u5feb\u6b65\u884c\u4e86\u8fc7\u6765\uff1a \u3000\u3000\u671b\u671b\u9ec4\u7075\u7684\u5de6\u624b\uff0c\u7384\u5a18\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e2d\u4e86\u6bd2?\u201d \u3000\u3000\u9ec4\u7075\u82e6\u7b11\u4e00\u4e0b\uff0c\u70b9\u70b9\u5934\u3002 \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u770b\u770b\uff0c\u80fd\u4e0d\u80fd\u89e3\u6389\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54b1\u4eec\u5148\u9000\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u4eec\u505c\u8eab\u4e4b\u5730\uff0c\u5c31\u5728\u70df\u9501\u697c\u4e0b\uff0c\u5982\u82e5\u5bf9\u65b9\u5c04\u51fa\u4e86\u6697\u5668\uff0c\u62b5\u6321\u5341\u5206\u4e0d\u6613\u3002 \u3000\u3000\u534e\u5999\u771f\u4ed4\u7ec6\u67e5\u770b\u8fc7\u4e24\u4eba\u4f24\u52bf\u540e\uff0c \u53f9\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u77a7\u4e0d\u51fa\u662f\u4ec0\u4e48\u6bd2\uff0c\u6211\u8eab\u4e0a\u6709\u4e00\u79cd\u89e3\u6bd2\u836f\u7269\uff0c\u8981\u4e0d\u8981\u670d\u7528\u4e00\u4e0b\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6700\u597d\u4e0d\u8981\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4ed6\u4eec\u4e0d\u80af\u501f\u673a\u6740\u4eba\uff0c\u5bf9\u4ed8\u53f6\u5144\uff0c\u6211\u60f3\uff0c\u5fc5\u6709\u7528\u5fc3\u3002\u201d \u534e\u5999\u771f\u9053\uff1a\u201c\u55ef!\u53ea\u662f\u60f3\u4e0d\u51fa\u4ed6\u4eec\u7684\u7528\u5fc3\u4f55\u5728\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u5728\u4e0b\u4e5f\u8bb8\u53ef\u4ee5\u731c\u5230\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u96f7\u5144\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u9ad8\u5929\u5065\u4e00\u5411\u4e0d\u559c\u81ea\u8d70\u7edd\u8def\uff0c\u5fc5\u7136\u4f1a\u548c\u9ec4\u5821\u4e3b\u8c08\u8c08\u6761\u4ef6\u3002\u201d \u534e\u5999\u771f\u9053\uff1a\u201c\u5bf9!\u4ed6\u73b0\u5728\u4e5f\u53ea\u4f59\u4e0b\u70df\u9501\u697c\u8fd9\u4e00\u6ce8\u672c\u94b1\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0e\u90a3\u7d27\u95ed\u7684\u94c1\u95e8\uff0c\u7a81\u7136\u4e00\u542f\uff0c\u4e00\u4e2a\u7f8e\u4e3d\u7684\u5c11\u5973\uff0c\u7f13\u7f13\u884c\u4e86\u51fa\u6765\uff0c\u662f\u5170\u767d\u3002 \u3000\u3000\u5170\u767d\u56db\u987e\u4e86\u4e00\u773c\uff0c\u7f13\u6b65\u5411\u524d\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e00\u4e0b\u5b50\u8df3\u4e86\u8d77\u6765\uff0c\u51c6\u5907\u8fce\u4e0a\u524d\u53bb\uff0c\u4f46\u5374\u88ab\u9ec4\u7075\u62e6\u4f4f\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5821\u4e3b\u8ba4\u8bc6\u5979?\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5170\u767d\u884c\u5230\u4e86\u51e0\u4eba\u505c\u8eab\u4e4b\u5904\uff0c\u9053\uff1a \u201c\u697c\u4e0a\u6709\u4eba\u76d1\u89c6\u6211\uff0c\u4ed6\u542c\u4e0d \u5230\u6211\u7684\u8bf4\u8bdd\uff0c\u4f46\u80fd\u770b\u89c1\u6211\u7684\u4e3e\u6b62\u3002\u201d \u3000\u3000\u9ec4\u7075\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8981\u4e0d\u8981\u907f\u5f00\u4ed6\u4eec?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a \u201c\u4e0d\u7528\uff0c\u5728\u4ed6\u4eec\u76d1\u89c6\u4e4b\u4e0b\uff0c\u53cd\u800c\u4f1a\u51fa\u4ed6\u4eec\u610f\u6599\u4e4b\u5916\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u5949\u547d\u6765\u6b64\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u662f\uff01\u4f60\u4eec\u54ea\u4f4d\u4e2d\u4e86\u6bd2?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211!\u800c\u4e14\uff0c\u62d6\u7d2f\u4e86\u9ec4\u5821\u4e3b\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5f88\u91cd\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8f7b\u91cd\u90fd\u662f\u4e00\u6837\uff0c\u8fc7\u4e0d\u4e86\u5341\u4e8c\u65f6\u8fb0\u3002\u201d \u3000\u3000\u5170\u767d\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e2d\u6bd2\u7684\u7adf\u7136\u4f1a\u662f\u4f60\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u65e0\u8bba\u8c01\u4e2d\u6bd2\uff0c\u90fd\u662f\u4e00\u6837\uff0c\u9ad8\u5929\u5065\u6d3e\u4f60\u6765\u505a\u4ec0\u4e48?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u8981\u6211\u5e26\u5821\u4e3b\u8fdb\u5165\u70df\u9501\u697c\u4e2d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u54b1\u4eec\u8d70\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u4e5f\u53bb\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4ed6\u53ea\u8ba9\u6211\u5e26\u9ec4\u5821\u4e3b\u4e00\u4e2a\u4eba\u8fdb\u53bb\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u957f\u9752\uff0c\u8fd9\u4e0d\u662f\u4e89\u6267\u7684\u65f6\u5019\uff0c\u5979\u505a\u4e0d\u4e86\u4e3b\u3002\u201d \u53f6\u957f\u9752\u6025\u9053\uff1a \u201c\u5170\u767d\uff0c\u53bb\u544a\u8bc9\u9ad8\u5929\u5065\uff0c\u6211\u4eec\u4e24\u4e2a\u4eba\u4e2d\u4e86\u6bd2\u4e3a\u4ec0\u4e48\u53ea\u51c6\u4e00\u4e2a\u4eba\u53bb?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4ed6\u8fd9\u4e48\u5429\u5490\u7684\uff0c\u4f60\u5982\u4e00\u5b9a\u8981\u53bb\uff0c\u5fc5\u8981\u4ed6\u540c\u610f\uff0c\u4e8c\u697c\u7a97\u53e3\u5904\uff0c\u57cb\u4f0f\u4e86\u5f88\u591a\u7684\u6697\u5668\uff0c\u4ed6\u5982\u4e0d\u540c\u610f\uff0c\u4f60\u4eec\u6839\u672c\u8fdb\u4e0d\u4e86\u70df \u3000\u3000\u9501\u697c\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u544a\u8bc9\u9ad8\u5929\u5065\uff0c\u6211\u4eec\u6709\u529e\u6cd5\uff0c\u8fdb\u5165\u70df\u9501\u697c\u3002\u201d \u5170\u767d\u9053\uff1a\u201c\u54e6!\u4ec0\u4e48\u529e\u6cd5?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u653e\u706b\u70e7\uff0c\u4ed6\u53ef\u4ee5\u4e0d\u4e0b\u70df\u9501\u697c\uff0c\u6211\u4eec\u4e5f\u4e0d\u4f1a\u8fdb\u53bb\uff0c\u5927\u5bb6\u8017\u5728\u8fd9\u91cc\uff0c\u70df\u9501\u697c\u4e0a\uff0c\u5b58\u4e86\u591a\u5c11\u7cae\u98df\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u6211\u76f8\u4fe1\uff0c\u603b\u6709\u5403\u5b8c\u7684\u4e00\u5929\u3002\u201d \u3000\u3000\u5170\u767d\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u4f1a\u544a\u8bc9\u4ed6\u7684\u3002\u201d \u3000\u3000\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a \u201c\u4f60\u4eec\u4e24\u4f4d\u8ddf\u6211\u6765\u5427!\u4e0d\u8fc7\uff0c\u4e00\u4e08\u5f00\u5916\uff0c\u7b49\u6211\u8bf4\u660e\u4e86\u4e4b\u540e\uff0c\u770b\u4ed6\u4f1a\u4e0d\u4f1a\u540c\u610f\u3002\u201d \u3000\u3000\u9ec4\u7075\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427!\u201d \u3000\u3000\u5170\u767d\u5f53\u5148\u800c\u884c\uff0c\u9ec4\u7075\u548c\u53f6\u957f\u9752\uff0c\u8fdc\u8fdc\u7684\u8ddf\u5728\u8eab\u540e\uff0c\u4e24\u4eba\u5728\u8ddd\u79bb\u697c\u524d\u4e00\u4e08\u5de6\u53f3\u5904\uff0c\u5c31\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u5170\u767d\u91cd\u53c8\u884c\u4e86\u51fa\u6765\uff0c\u9053\uff1a \u201c\u4e24\u4f4d\u53ef\u4ee5\u8fdb\u53bb\u4e86\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u80fd\u5e26\u5175\u5203\u3002\u201d \u53f6\u957f\u9752\u56de\u987e\u4e86\u9ec4\u7075\u4e00\u773c\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u653e\u4e0b\u957f\u5251\u5427!\u201d \u3000\u3000\u53f6\u957f\u9752\u53ea\u597d\u653e\u4e0b\u4e86\u957f\u5251\u3002 \u3000\u3000\u5728\u5170\u767d\u7684\u5e26\u8def\u4e4b\u4e0b\uff0c\u9ec4\u7075\u548c\u53f6\u957f\u9752\u884c\u8fdb\u4e86\u94c1\u95e8\u4e4b\u5185\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5750\u5728\u4e00\u5f20\u592a\u5e08\u6905\u4e0a\u3002 \u3000\u3000\u53e6\u5916\u4e24\u5f20\u5927\u6728\u6905\u6446\u5728\u9ad8\u5929\u5065\u7684\u5bf9\u9762\uff0c\u6728\u6848\u4e0a\uff0c\u65e9\u5df2\u6446\u597d\u4e86\u9999\u8317\u3002 \u3000\u3000\u9ad8\u5929\u5065\u8083\u5ba2\u5165\u5ea7\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4e24\u4f4d\u4e00\u671d\u88ab\u86c7\u54ac\uff0c\u5341\u5e74\u6015\u4e95\u7ef3\uff0c\u5927\u6982\u4e0d\u6562\u559d\u8fd9\u676f\u8336\u4e86\u3002\u201d \u3000\u3000\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u81ea\u884c\u53d6\u8fc7\u8336\u676f\uff0c\u559d\u4e86\u4e00\u53e3\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u8bf7\u54b1\u4eec\u6765\uff0c\u53ea\u4e3a\u4e86\u559d\u4e00\u676f\u8336\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b\uff0c\u8fd8\u662f\u5f88\u5014\u5f3a\u554a!\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4e2d\u6bd2\u7684\u53ea\u662f\u9ec4\u67d0\u548c\u53f6\u957f\u9752\u4e24\u4e2a\u4eba\uff0c\u6211\u60f3\uff0c\u4e0d\u81f4\u4f7f\u54b1\u4eec\u5411\u4f60\u9ad8\u5821\u4e3b\u6c42\u9976\u5427?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u4e0d\u7528\uff0c\u4f46\u4e24\u4f4d\u60f3\u6cbb\u7597\u597d\u8eab\u4e2d\u4e4b\u6bd2\uff0c\u90a3\u5c31\u5fc5\u987b\u548c\u9ad8\u67d0\u5408\u4f5c\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5408\u4f5c\u7684\u529e\u6cd5\uff0c\u81ea\u7136\u662f\u53cc\u65b9\u4e92\u60e0\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u613f\u95fb\u9ad8\u89c1\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u5949\u8d60\u5404\u4f4d\u4e00\u534a\u89e3\u836f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u53ea\u5949\u9001\u4e00\u534a?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u53e6\u4e00\u534a\uff0c\u6211\u6d3e\u4eba\u9001\u5230\u7b2c\u4e00\u5821\u5916\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u90a3\u662f\u8bf4\uff0c\u8981\u6211\u4eec\u9000\u51fa\u7b2c\u4e00\u5821\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u6761\u4ef6\u4e0d\u7b97\u592a\u82db\u5427\uff01\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5821\u4e3b\u53ef\u66fe\u60f3\u5230\uff0c\u54b1\u4eec\u9000\u51fa\u7b2c\u4e00\u5821\u4e4b\u540e\u8fd8\u53ef\u4ee5\u518d\u8fdb\u6765\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u90a3\u5f53\u7136\uff0c\u4e24\u4f4d\u9000\u51fa\u4e4b\u540e\uff0c\u53ea\u8981\u5728\u5821\u5916\u505c\u7559\u4e00\u5929\uff0c\u7136\u540e\uff0c\u53ef\u4ee5\u518d\u5165\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u4f1a\u4e0d\u4f1a\u7acb\u523b\u4ea4\u51fa\u89e3\u836f\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u62c5\u5fc3\u5728\u64a4\u8d70\u4eba\u624b\u4ee5\u540e\uff0c\u5728\u4e0b\u4e0d\u80af\u4ea4\u51fa\u89e3\u836f\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u662f\u5426\u4e5f\u62c5\u5fc3\uff0c\u4ea4\u51fa\u4e86\u89e3\u836f\u4e4b\u540e\uff0c\u9ec4\u7075\u4e0d\u80af\u64a4\u51fa\u70df\u9501\u697c\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u54b1\u4eec\u6700\u597d\u80fd\u7814\u5546\u4e00\u4e2a\u4e24\u5168\u5176\u7f8e\u4e4b\u7b56\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u7a81\u7136\u5f00\u53e3\uff0c\u9053\uff1a\u201c\u5728\u4e0b\uff0c\u5012\u6709\u4e00\u4e2a\u529e\u6cd5\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8bf7\u6559?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u4ea4\u51fa\u4e00\u4efd\u89e3\u836f\uff0c\u6211\u53f6\u67d0\u4eba\u7559\u8fd9\u91cc\u4f5c\u4e3a\u4eba\u8d28\uff0c\u5982\u82e5\u9ec4\u5821\u4e3b\u4e0d\u5b88\u4fe1\u7684\uff0c\u4f60\u53ef\u4ee5\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u8a89\uff0c\u867d\u5f88\u54cd\u4eae\uff0c\u4f46\u8fd8\u4e0d\u591f\u4f5c\u4e3a\u4eba\u8d28\u7684\u4efd\u91cf\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1b\u201c\u6362\u4e00\u4e2a\u4eba\u8d28\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8c01?\u201d \u3000\u3000\u201c\u6211\uff01\u201d\u9ec4\u7075\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u7ed9\u53f6\u957f\u9752\u4e24\u4efd\u89e3\u836f\uff0c\u4e00\u4efd\u7531\u53f6\u5144\u5f53\u573a\u8bd5\u9a8c\uff0c\u786e\u662f\u89e3\u53bb\u4ed6\u8eab\u4e2d\u4e4b\u6bd2\uff0c\u53e6\u4e00\u4efd\uff0c\u7531\u4ed6\u5e26\u8d70\uff0c\u6211\u4eec\u7684\u4eba\u624b\uff0c\u64a4\u51fa\u4e86\u7b2c\u4e00\u5821\u4e4b\u540e\uff0c\u9ad8\u5821\u4e3b\u518d\u653e\u533a\u533a\u79bb\u5f00\u5982\u4f55?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u597d!\u5c31\u8fd9\u4e48\u529e\uff01\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e00\u8a00\u4e3a\u5b9a\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u8d77\u8eab\u79bb\u53bb\u3002 \u3000\u3000\u9ec4\u7075\u660e\u77e5\u89e3\u6bd2\u836f\u7269\u53ef\u80fd\u5c31\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u4f46\u4e5f\u4e0d\u70b9\u7834\u3002 \u3000\u3000\u8db3\u8db3\u7b49\u4faf\u4e86\u4e00\u523b\u5de5\u592b\u4e4b\u540e\uff0c\u9ad8\u5929\u5065\u624d\u7f13\u6b65\u800c\u5165\uff0e\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u8fd8\u6709\u52a8\u624b\u7684\u80fd\u529b\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5728\u4e0b\u662f\u5426\u8fd8\u6709\u52a8\u624b\u4e4b\u80fd\u529b\uff0c\u9ad8\u5821\u4e3b\u5e94\u8be5\u5f88\u6e05\u695a\u4e86\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u8981\u70b9\u4e86\u4f60\u51e0\u5904\u7a74\u9053\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f46\u8bf7\u51fa\u624b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51fa\u624b\u5982\u98ce\uff0c\u70b9\u4e86\u9ec4\u7075\u56db\u5904\u7a74\u9053\uff0c\u624d\u53d6\u51fa\u4e00\u4e2a\u7389\u74f6\uff0c\u5012\u51fa\u4e86\u4e24\u7c92\u4e39\u4e38\uff0c\u4ea4\u7ed9\u4e86\u53f6\u957f\u9752\uff0c\u9053\uff1a\u201c\u670d\u4e0b\u4e00\u7c92\uff0e\u7acb\u523b\u8fd0\u6c14\u8c03\u606f\uff0c\u7136\u540e\uff0c\u544a\u8bc9\u9ec4\u5821\u4e3b\u3002\u201d \u3000\u3000\u4e8b\u5df2\u81f3\u6b64\uff0c\u53f6\u957f\u9752\u4e5f\u4e0d\u518d\u591a\u8a00\uff0c\u63a5\u8fc7\u836f\u4e38\uff0c\u7acb\u523b\u773c\u4e0b\u4e00\u7c92\uff0c\u7136\u540e\uff0c\u76d8\u819d\u5750\u4e0b\uff0c\u8fd0\u6c14\u8c03\u606f\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5750\u5728\u592a\u5e08\u6905\u4e0a\uff0c\u671b\u7740\u53f6\u957f\u9752\u3002 \u3000\u3000\u9ec4\u7075\u7531\u4e8e\u56db\u5904\u7a74\u9053\u53d7\u5236\uff0c\u5168\u8eab\u90fd\u5df2\u65e0\u6cd5\u884c\u52a8\uff0c\u4f46\u4ed6\u7684\u795e\u667a\u5f88\u6e05\u695a\uff0c\u76ee\u529b\u4e5f\u672a\u53d7\u5f71\u54cd\u3002 \u3000\u3000\u53f6\u957f\u9752\u82b1\u53bb\u4e86\u534a\u4e2a\u65f6\u8fb0\u4e4b\u4e45\uff0c\u624d\u7f13\u7f13\u7741\u5f00\u53cc\u76ee\uff0c\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u771f\u7684\u89e3\u836f\uff0e\u5c5e\u4e0b\u8eab\u4e0a\u7684\u5947\u6bd2\u5df2\u89e3\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u4f60\u5e26\u7740\u89e3\u836f\u56de\u53bb\u5427!\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8f6c\u8fbe\u5821\u4e3b\u4e4b\u547d\uff0c\u5168\u6570\u64a4\u51fa\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9ad8\u5929\u5065\u4e00\u76f4\u76ef\u7740\u4e24\u4eba\uff0c\u4efb\u4f55\u4e00\u4e2a\u4eba\u7ec6\u5c0f\u7684\u52a8\u4f5c\uff0c\u90fd\u6ce8\u610f\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u53f6\u957f\u9752\u6536\u597d\u4e39\u4e38\uff0c\u9053\uff1a\u201c\u5821\u4e3b\u4fdd\u91cd\uff0c\u957f\u9752\u544a\u9000\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u4e0d\u544a\u8bc9\u9ec4\u5821\u4e3b\uff0c\u5728\u90a3\u91cc\u4f1a\u5408\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u54b1\u4eec\u4f1a\u5728\u7b2c\u4e00\u5821\u7684\u5821\u95e8\u5916\uff0c\u7b49\u5019\u5821\u4e3b\u3002\u201d \u3000\u3000\u4e00\u5207\u90fd\u6309\u7167\u53cc\u65b9\u8c08\u597d\u7684\u6761\u4ef6\u8fdb\u884c\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5728\u70df\u9501\u697c\u4e0a\uff0c \u76ee\u7779\u7740\u4f55\u5bd2\u8863\u3001\u5434\u53cc\u5973\u7b49\u79bb\u5f00\u4e86\u70df\u9501\u697c\u3002 \u65e5\u5c06\u843d\u5c71\u7684\u65f6\u5206\uff0c\u9ad8\u5929\u5065\u4e5f\u9001\u51fa\u4e86\u9ec4\u7075\u3002 \u3000\u3000\u51fa\u4eba\u610f\u6599\u7684\u662f\uff0c\u4ed6\u4eb2\u81ea\u9001\u4eba\u800c\u6765\u3002 \u3000\u3000\u800c\u4e14\uff0c\u53ea\u5e26\u4e86\u4e00\u4e2a\u603b\u7ba1\u90d1\u98de\u3002 \u3000\u3000\u5c1a\u5929\u4e49\u3001\u5c1a\u592b\u4eba\u3001\u7384\u5a18\u3001\u534e\u5999\u771f\u56db\u4e2a\u4eba\uff0c\u4e00\u6392\u6a2a\u7acb\uff0c\u63a5\u4e0b\u9ec4\u7075\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u3001\u4f55\u5bd2\u8863\u3001\u5434\u53cc\u5973\u7b49\uff0c\u90fd\u8fdc\u8fdc\u7684\u7ad9\u5728\u4e94\u4e08\u4e4b\u5916\u3002 \u3000\u3000\u7384\u5a18\u63a5\u8fc7\uff0c\u9ec4\u7075\u89e3\u5f00\u4e86\u4ed6\u8eab\u4e0a\u7a74\u9053\u3002 \u3000\u3000\u9ec4\u7075\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u4f60\u5192\u4e86\u5f88\u5927\u7684\u9669\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u592b\u8fd9\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u672a\u5982\u6b64\u5730\u4fe1\u4efb\u8fc7\u4e00\u4e2a\u4eba\uff0c\u5e0c\u671b\u8fd9\u4e00\u6b21\uff0c\u4f60\u4eec\u4e0d\u4f1a\u4f7f\u6211\u5931\u671b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4f60\u653e\u5fc3\u53bb\uff0c\u660e\u65e5\u5348\u65f6\u4e4b\u540e\uff0c\u6211\u4eec\u8fd8\u4f1a\u8d76\u5230\u70df\u9501\u697c\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e00\u591c\u5de5\u592b\uff0c\u8001\u592b\u4f1a\u91cd\u6574\u597d\u7b2c\u4e00\u5821\uff0c\u518d\u8fdb\u5165\u7b2c\u4e00\u5821\u65f6\uff0c\u4f60\u4f1a\u89c1\u8bc6\u7b2c\u4e00\u5821\u771f\u6b63\u7684\u5f3a\u5927\u5b9e\u529b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5e0c\u671b\u660e\u5929\u54b1\u4eec\u80fd\u653e\u624b\u4e00\u6218\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u53ea\u8981\u4f60\u518d\u627e\u4e0a\u70df\u9501\u697c\uff0c\u8001\u592b\u4f1a\u820d\u547d\u5949\u966a\uff0c\u544a\u9000\u4e86\u3002\u201d \u3000\u3000\u5e26\u7740\u90d1\u98de\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u671b\u7740\u9ad8\u5929\u5065\u7684\u80cc\u5f71\uff0c\u9ec4\u7075\u795e\u60c5\u51b7\u8083\u5730\u8bf4\u9053\uff1a \u201c\u8c01\u4f5c\u4e86\u64a4\u51fa\u7b2c\u4e00\u5821\u7684\u51b3\u5b9a\u3002\u201d \u7384\u5a18\u9053\uff1a\u201c\u6211\uff01\u201d \u3000\u3000\u9ec4\u7075\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4f55\u82e6\u5462?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6211\u4eec\u5546\u91cf\u8fc7\u4e86\uff0c\u4e0d\u60f3\u4f60\u6b7b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5165\u4e86\u7f51\u7684\u9c7c\u513f\uff0c\u4f60\u4eec\u5374\u7f51\u5f00\u4e00\u9762\uff0c\u8fd9\u662f\u7eb5\u864e\u5f52\u5c71\u3002\u201d \u8fd9\u65f6\uff0c\u9ad8\u5e7d\u5170\u7b49\u4e00\u884c\u4eba\uff0c\u4e5f\u884c\u4e86\u8fc7\u6765\uff1a \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u4f60\u662f\u9886\u519b\u7684\u4e3b\u5e05\uff0c\u6211\u4eec\u4e0d\u5e0c\u671b\u4f60\u8eab\u906d\u4e0d\u6d4b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u66f4\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u662f\u90a3\u91cc\u4e0d\u9002\u5408\u4f5c\u820d\u547d\u4e00\u6218\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u4ed6\u5df2\u65e0\u8def\u53ef\u8d70\uff0c\u6211\u4eec\u53ef\u4ee5\u903c\u4ed6\u51fa\u624b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6574\u5ea7\u7684\u70df\u9501\u697c\uff0c\u4f1a\u7206\u70b8\u6210\u4e00\u7247\u98de\u7070\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u5446\u4e86\u4e00\u5446\uff0c\u63a5\u9053\uff1a\u201c\u662f\u771f\u7684\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u771f\u7684\uff0c\u96f7\u65e0\u5f71\u548c\u6211\u5546\u91cf\u4e86\u5f88\u4e45\uff0c\u624d\u627e\u51fa\u4e00\u4e2a\u7834\u7efd\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u96f7\u65e0\u5f71\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\u4ed6\u53d8\u5f97\u5f88\u52c7\u6562\u4e86\uff0c\u7adf\u7136\u7559\u5728\u795e\u5200\u5802\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5982\u679c\u6211\u4eec\u4e0d\u5b88\u4fe1\u7528\uff0c\u73b0\u5728\u5c31\u8fdb\u5165\u7b2c\u4e00\u5821\u53bb\uff0c\u8fd8\u53ef\u4ee5\u66ff\u96f7\u65e0\u5f71\u6253\u4e2a\u63a5\u5e94\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u8fd9\u4e00\u70b9\u4fe1\u7528\uff0c\u54b1\u4eec\u4e00\u5b9a\u8981\u5b88\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u7537\u4eba\u561b\uff0c\u521b\u4e1a\u6c5f\u6e56\uff0c\u5e94\u8be5\u662f\u6709\u4fe1\u7528\uff0c\u4e0d\u8fc7\uff0c\u9ad8\u5929\u5065\u4e0a\u4ea4\u51fa\u89e3\u836f\u7684\u539f\u56e0\uff0c\u5c31\u5e0c\u671b\u6709\u662f\u591f\u7684\u65f6\u95f4\uff0c\u5bf9\u4ed8\u96f7\u65e0\u5f71\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u96f7\u65e0\u5f71\u8bf4\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u96f7\u65e0\u5f71\u5145\u6ee1\u7740\u81ea\u4fe1\uff0c \u4ed6\u544a\u8bc9\u6211\uff0c \u53ea\u8981\u6211\u4eec\u80fd\u5728\u660e\u5929\u4e2d\u5348\u4e4b\u524d\u8d76\u5230\u90a3\u91cc\uff0c\u4ed6\u81ea\u4fe1\u8fd8\u53ef\u6491\u5f97\u4e0b\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u96f7\u65e0\u5f71\u4e5f\u4e0d\u4f1a\u4f5c\u6ca1\u6709\u628a\u63e1\u7684\u4e8b\uff0c\u770b\u6765\uff0c\u5c31\u7b97\u54b1\u4eec\u4e0d\u80c1\u8feb\u4ed6\uff0c\u4ed6\u4e5f\u51b3\u5b9a\u53cd\u53db\u9ad8\u5929\u5065\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u795e\u5200\u5802\u7684\u5b9e\u529b\u592a\u5927\uff0c\u9ad8\u5929\u5065\u65e9\u5c31\u6709\u4e86\u9632\u8303\u4e4b\u5fc3\uff0c\u8981\u6211\u8bad\u7ec3\u5341\u4e8c\u5973\u5c06\uff0c\u5c31\u662f\u51c6\u5907\u5bf9\u4ed8\u96f7\u65e0\u5f71\u7684\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u96f7\u65e0\u5f71\u5bf9\u795e\u5200\u5802\u7684\u63a7\u5236\u529b\uff0c\u8981\u8d85\u8fc7\u9ad8\u5929\u5065\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u597d\u50cf\u662f\u96f7\u65e0\u5f71\u65e9\u5df2\u6709\u4e86\u5b89\u6392\u3002\u201d \u3000\u3000\u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ad8\u59d1\u5a18\uff0c\u6211\u4eec\u76ee\u524d\u5e94\u8be5\u5982\u4f55?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e8b\u60c5\u5df2\u7ecf\u53d1\u5c55\u7684\u5728\u6211\u4eec\u7406\u60f3\u4e4b\u4e2d\uff0c\u9ad8\u5929\u5065\u6709\u4fe1\u5fc3\u5bf9\u4ed8\u96f7\u65e0\u5f71\uff0c\u96f7\u65e0\u5f71\u4e5f\u4f3c\u662f\u6709\u4fe1\u5fc3\u5bf9\u4ed8\u9ad8\u5929\u5065\uff0c\u53cc\u65b9\u9762\uff0c\u5fc5\u5c06\u6709\u4e00\u573a\u706b\u5e76\uff0c\u8fd9\u5c31\u662f\u6211\u4eec\u7684\u76ee\u7684\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u4e00\u591c\uff0c\u7b2c\u4e00\u5821\u4e2d\uff0c\u5c06\u6709\u4e00\u573a\u60ca\u5929\u52a8\u5730\u7684\u81ea\u76f8\u6b8b\u6740\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5e94\u8be5\u662f\u8fd9\u4e2a\u6837\u5b50\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d\u5427!\u4e8b\u5df2\u5982\u6b64\uff0c\u54b1\u4eec\u53ea\u6709\u5750\u4ee5\u5f85\u53d8\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u597d!\u54b1\u4eec\u597d\u597d\u5229\u7528\u8fd9\u4e2a\u673a\u4f1a\uff0c\u5750\u606f\u4e00\u4e0b\uff0c\u660e\u5929\u4e2d\u5348\u518d\u8fdb\u7b2c\u4e00\u5821\u4e2d\u53bb\u77a7\u77a7\u5427\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u5929\u4e2d\u5348\u8fc7\u540e\uff0c\u9ec4\u7075\u7b49\u53c8\u8fdb\u5165\u4e86\u7b2c\u4e00\u5821\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u9ec4\u7075\u5f53\u5148\u800c\u884c\uff0c\u53f6\u957f\u9752\u3001\u4f55\u5bd2\u8863\uff0c\u7d27\u968f\u5de6\u53f3\u3002\u7b2c\u4e00\u5821\u4e2d\uff0c\u7adf\u7136\u4e0d\u89c1\u4e00\u4e2a\u8fce\u51fb\u4e4b\u4eba\u3002 \u3000\u3000\u5e7f\u5927\u7684\u7b2c\u4e00\u5821\u4e2d\uff0c\u7adf\u7136\u662f\u4e00\u7247\u5bc2\u9759\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u600e\u4e48\u56de\u4e8b?\u201d \u3000\u3000\u9ec4\u7075\u5185\u5fc3\u4e5f\u6709\u7740\u91cd\u91cd\u7684\u7591\u95ee\u3002 \u3000\u3000\u6628\u591c\u4ed6\u542c\u5f97\u5f88\u7528\u5fc3\uff0c\u4f46\u5374\u4e00\u76f4\u672a\u542c\u51fa\u7b2c\u4e00\u5821\u5185\u6709\u5175\u5203\u76f8\u51fb\u4e4b\u58f0\u3002 \u3000\u3000\u56de\u987e\u4e86\u9ad8\u5e7d\u5170\u4e00\u773c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u9ad8\u59d1\u5a18\uff0c\u60c5\u52bf\u662f\u4e0d\u662f\u6709\u4e9b\u5f88\u5947\u602a?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u55ef\uff0c\u6628\u591c\uff0c\u597d\u50cf\u5f88\u5e73\u9759?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4e0d\u662f\u8c08\u548c\u4e86?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u5f88\u53ef\u80fd\uff0c\u9ad8\u5929\u5065\uff0c\u96f7\u65e0\u5f71\uff0c\u90fd\u4e0d\u662f\u4ec0\u4e48\u597d\u4eba\uff0c \u8fd9\u4e24\u4e2a\u4eba\uff0c\u5229\u5bb3\u76f8\u5173\u65f6\uff0c\u5c31\u4f1a\u4fc3\u5728\u4e00\u5757\u513f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5fae\u5fae\u4e00\u7b11\uff1a\u201c\u4ed6\u4eec\u5f88\u96be\u5408\u4f5c\uff0c\u56e0\u4e3a\u4ed6\u4eec\u4e24\u4e2a\u4eba\uff0c\u90fd\u6709\u91ce\u5fc3\uff0c\u76ee\u4e0b\u96f7\u65e0\u5f71\u5360\u4e86\u6781\u4e3a\u4f18\u8d8a\u7684\u6761\u4ef6\uff0c\u9ad8\u5929\u5065\u4e5f\u51b3\u4e0d\u4f1a\u5411\u4ed6\u4f4e\u5934\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5982\u82e5\u4ed6\u4eec\u6709\u4e00\u573a\u640f\u6740\uff0c\u90a3\u640f\u6740\u5e94\u8be5\u5341\u5206\u6fc0\u70c8\uff0c\u4f46\u54b1\u4eec\u7adf\u7136\u6ca1\u6709\u542c\u5230\u4e00\u70b9\u58f0\u97f3\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u53ea\u6709\u5230\u795e\u5200\u5802\u77a7\u77a7\u4e86\u3002\u201d \u3000\u3000\u795e\u5200\u5802\u95e8\u7981\u4e00\u5411\u68ee\u4e25\uff0c\u4f46\u73b0\u5728\uff0c\u7adf\u7136\u662f\u53cc\u95e8\u5927\u5f00\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7a81\u7136\u52a0\u5feb\u4e86\u811a\u6b65\uff0c\u62a2\u5728\u524d\u9762\u3002 \u3000\u3000\u5979\u4e00\u52a8\uff0c\u7ea2\u82b1\u3001\u7eff\u53f6\u4e5f\u75be\u5feb\u5730\u8ddf\u4e86\u4e0a\u53bb\uff0c \u3000\u3000\u9ec4\u7075\u5927\u884c\u4e24\u6b65\uff0c\u548c\u9ad8\u5e7d\u5170\u5e76\u80a9\u800c\u7acb\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u4e0d\u50cf\u6709\u8fc7\u6fc0\u70c8\u640f\u6740\u7684\u6837\u5b50\u3002\u201d \u539f\u6765\uff0c\u5927\u95e8\u5185\u5ead\u9662\u4e4b\u4e2d\uff0c\u7adf\u7136\u4e0d\u89c1\u4e00\u70b9\u640f\u6740\u75d5\u8ff9\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u9ec4\u5821\u4e3b\uff0c\u8bf7\u7559\u4e0b\u4e00\u6b65\uff0c\u8d31\u59be\u8fdb\u5165\u5385\u4e2d\u77a7\u77a7\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u4e0d!\u8fd8\u662f\u6211\u8fdb\u53bb\u5427!\u59d1\u5a18\u7559\u5728\u5927\u5385\u5916\u9762\uff1b\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u4e0d\u7528\u4e89\u5566\uff0c\u5bf9\u4ed8\u7a81\u51fb\uff0c\u4e03\u8272\u5251\u7edd\u4e0d\u5728\u5821\u4e3b\u4e4b\u4e0b\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u59d1\u5a18\u5c0f\u5fc3\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\uff0c\u793a\u610f\u7ea2\u82b1\u3001\u7eff\u53f6\u9000\u4e0b\uff0c\u4e3e\u6b65\u884c\u5411\u5927\u5385\u3002 \u3000\u3000\u8fd9\u5ea7\u5927\u5385\u5f88\u5e7f\u5927\uff0c\u6b64\u523b\uff0c\u5374\u9759\u5f97\u542c\u4e0d\u5230\u4e00\u70b9\u58f0\u606f\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u53cc\u624b\u90fd\u63e1\u5728\u5251\u67c4\u4e4b\u4e0a\uff0c\u7f13\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u76f4\u884c\u5728\u5927\u5385\u4e2d\u95f4\uff0c\u624d\u505c\u4e86\u4e0b\u6765\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u96f7\u5802\u4e3b\u2026\u2026\u201d \u8fde\u547c\u6570\u58f0\uff0c\u5374\u4e00\u76f4\u672a\u95fb\u6709\u4eba\u76f8\u5e94\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u4e0d\u7ba1\u662f\u654c\u662f\u53cb\uff0c\u4f60\u90fd\u8be5\u6709\u4e00\u4e2a\u4ea4\u4ee3\u554a!\u7537\u5b50\u6c49\u3001\u5927\u4e08\u592b\uff0c\u7adf\u7136\u5982\u6b64\u7684\u6ca1\u6709\u6c14\u6168\u3002\u201d \u3000\u3000\u58f0\u97f3\u521a\u843d\uff0c\u7a81\u95fb\u4e00\u9635\u54c8\u54c8\u5927\u7b11\u4e4b\u58f0\uff0c\u4f20\u4e86\u51fa\u6765\uff0c\u96f7\u65e0\u5f71\u7f13\u6b65\u7531\u5385\u4e2d\u884c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u7adf\u7136\u8fd8\u6d3b\u7740?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a \u201c\u9ad8\u59d1\u5a18\uff0c\u4e0d\u8981\u8bcd\u950b\u5982\u5200\uff0c\u522b\u5fd8\u4e86\uff0c\u54b1\u4eec\u662f\u5408\u4f19\u4eba\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4f60\u8fd8\u8bb0\u5f97\u54b1\u4eec\u662f\u5408\u4f19\u4eba\uff0c\u90a3\u8fd8\u4e3a\u4ec0\u4e48\u5982\u6b64\u7684\u9b3c\u9b3c\u795f\u795f\u3002\u201d \u96f7\u65e0\u5f71\u795e\u60c5\u4e00\u53d8\uff0c\u4f3c\u8981\u53d1\u4f5c\uff0c\u4f46\u5374\u53c8\u7a81\u7136\u5fcd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u5462?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ecd\u5728\u70df\u9501\u697c\u4e2d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u4eec\u53c8\u8c08\u548c\u4e86\uff1f\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u90a3\u5012\u6ca1\u6709\uff0c\u9ad8\u5929\u5065\u6068\u4e0d\u5f97\u98df\u6211\u4e4b\u8089\uff0c\u559d\u6211\u4e4b\u8840\uff0c\u600e\u4e48\u548c\u6211\u8c08\u548c\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u9ad8\u5929\u5065\u628a\u6211\u4eec\u903c\u51fa\u7b2c\u4e00\u5821\u7684\u7528\u5fc3\uff0c\u4f60\u53ef\u660e\u767d?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u59d1\u5a18\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u8981\u4e89\u53d6\u4e00\u591c\u65f6\u95f4\uff0c\u5bf9\u4ed8\u4f60\u96f7\u65e0\u5f71\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u7136\u77e5\u9053\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u9000\u51fa\u53bb\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e8b\u5173\u9ec4\u5821\u4e3b\u7684\u751f\u6b7b\uff0c\u4f60\u53c8\u4e0d\u662f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u6240\u4ee5\uff0c\u6211\u548c\u9ad8\u5929\u5065\u4e4b\u95f4\uff0c\u6628\u591c\u624d\u672a\u4f5c\u62fc\u6b7b\u4e00\u640f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u8bdd\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u610f\u601d\u5f88\u660e\u663e\uff0c\u9ad8\u5929\u5065\u611f\u89c9\u7740\u6211\u4e0d\u662f\u4ed6\u7684\u654c\u624b\uff0c\u6240\u4ee5\uff0c\u4ed6\u628a\u5b9e\u529b\u7559\u4e0b\u6765\uff0c\u51c6\u5907\u5bf9\u4ed8\u4f60\u4eec\u7684\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54e6!\u4f60\u4eec\u59a5\u534f\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u5509!\u9ad8\u5929\u5065\u4e0d\u653b\u795e\u5200\u5802\uff0c\u6211\u603b\u4e0d\u80fd\u52c9\u5f3a\u51fa\u624b\u5427?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u60f3\u5750\u6536\u6e14\u5229?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u59d1\u5a18\u4e00\u5b9a\u5982\u6b64\u8bf4\uff0c\u5728\u4e0b\u4e5f\u65e0\u6cd5\u89e3\u91ca\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u7528\u89e3\u91ca\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u603b\u4e0d\u80fd\u4f60\u4eec\u5148\u653b\u6253\u6211\u8fd9\u795e\u5200\u5802\u5427?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5982\u679c\u4f60\u96f7\u5802\u4e3b\u53ef\u4ee5\u6539\u53d8\u4e3b\u610f\uff0c\u6211\u4eec\u96be\u9053\u4e0d\u53ef\u4ee5\u6539\u53d8\u4e3b\u610f\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u81ea\u7136\u53ef\u4ee5\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e0d\u4f1a\u548c\u4f60\u4eec\u76f4\u63a5\u640f\u6740\uff0c\u6211\u4f1a\u5229\u7528\u8fd9\u91cc\u7684\u4f18\u52bf\u5730\u5f62\uff0c\u548c\u4f60\u4eec\u4f5c\u6d88\u8017\u6027\u7684\u5bf9\u6297\u3002\u201d \u3000\u3000\u9ec4\u7075\u884c\u4e86\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u597d\u50cf\u5f88\u6709\u628a\u63e1\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u9ad8\u5929\u5065\u662f\u7b2c\u4e00\u5821\u7684\u521b\u59cb\u4eba\uff0c\u4ed6\u7b97\u8fc7\u4e86\u8fd9\u7b14\u8d26\uff0c\u4ed6\u5982\u5168\u529b\u653b\u5165\u8fd9\u5ea7\u795e\u5200\u5802\uff0c\u81f3\u5c11\u8981\u82b1\u8d39\u4e09\u5929\u4ee5\u4e0a\u7684\u65f6\u95f4\uff0c\u800c\u4e14\uff0c\u4ed6\u4e5f\u65e0\u6cd5\u786e\u5b9a\u5728\u8fd9\u573a\u5bf9\u6297\u4e2d\uff0c \u4e00\u5b9a\u80fd\u591f\u80dc\u8fc7\u6211\u3002\u201d \u3000\u3000\u9ec4\u7075\u65e2\u7136\u63a5\u4e86\u53e3\uff0c\u9ad8\u5e7d\u5170\u53cd\u800c\u4e0d\u4fbf\u518d\u8bf4\u4ec0\u4e48\u4e86\uff0c\u7f13\u7f13\u9000\u5230\u4e86\u9ec4\u7075\u7684\u8eab\u540e\u3002 \u3000\u3000\u5979\u662f\u4e2a\u5f88\u6709\u5206\u5bf8\u7684\u5973\u4eba\u3002 \u3000\u3000\u9ec4\u7075\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u96f7\u5144\u8fd8\u6ca1\u6709\u5f88\u786e\u5207\u7684\u8bf4\u51fa\u4f60\u7684\u5fc3\u613f\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u6211\u5b88\u4f4f\u795e\u5200\u5802\uff0c \u4f60\u4eec\u653b\u6253\u70df\u9501\u697c\uff0c\u6211\u5df2\u7ecf\u7b54\u5e94\u8fc7\u9ad8\u5929\u5065\uff0c\u4e24\u8fb9\u6211\u90fd\u4e0d\u63d2\u624b\uff0c\u5e0c\u671b\u4f60\u9ec4\u5821\u4e3b\uff0c \u4e5f\u80fd\u8c05\u89e3\u5728\u4e0b\u8fd9\u70b9\u82e6\u8877\uff0c\u4e0d\u7ba1\u5982\u4f55\uff0c\u6211\u548c\u9ad8\u5821\u4e3b\u76f8\u5904\u4e86\u4e8c\u5341\u51e0\u5e74\uff0c\u4ed6\u8fd9\u4e00\u70b9\u8bf7\u6c42\uff0c\u5e76\u4e0d\u8fc7\u5206\u3002\u201d \u3000\u3000\u9ec4\u7075\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u96f7\u5144\u53ef\u66fe\u60f3\u5230\uff0c\u6211\u4eec\u548c\u9ad8\u5929\u5065\u8fd9\u4e00\u573a\u640f\u6740\u4e4b\u4e2d\uff0c\u8c01\u80dc\u8c01\u8d1f\uff0c\u53ef\u66fe\u60f3\u5230\uff0c\u6211\u4eec\u5982\u82e5\u4e0d\u5e78\u7684\u5931\u8d25\u4e86\uff0c\u90a3\u4f1a\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u5b50\u7684\u5c40\u9762\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u6211\u7b97\u8fc7\u4e86\u4f60\u4eec\u53cc\u65b9\u7684\u5b9e\u529b\uff0c\u5982\u82e5\u4f60\u9ec4\u5821\u4e3b\u80fd\u591f\u6321\u4f4f\u9ad8\u5929\u5065\uff0c\u4f60\u4eec\u7684\u80dc\u7b97\uff0c\u5c31\u5360\u4e86\u5927\u90e8\u5206\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5c31\u7b97\u6211\u4eec\u80dc\u4e86\uff0c\u4f60\u51c6\u5907\u548c\u6211\u4eec\u4e4b\u95f4\uff0e\u5982\u4f55\u76f8\u5904\u4e0b\u53bb\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5728\u4e0b\u6ca1\u6709\u9ad8\u5929\u5065\u7684\u91ce\u5fc3\uff0c\u65e0\u610f\u4e89\u96c4\u6c5f\u6e56\uff0c\u6240\u4ee5\uff0c\u6211\u76f8\u4fe1\u53ef\u4ee5\u548c\u8bf8\u4f4d\u597d\u597d\u5730\u76f8\u5904\u4e0b\u53bb\u7684\uff1a\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5982\u679c\u662f\u9ad8\u5929\u5065\u80dc\u4e86\uff0c\u662f\u4e0d\u662f\u4e5f\u4f1a\u653e\u8fc7\u4f60?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u4e0d\u4f1a\uff0c\u9ad8\u5929\u5065\u91ce\u5fc3\u52c3\u52c3\uff0c\u5fd7\u5728\u6c5f\u6e56\uff0c\u4ed6\u5982\u679c\u80dc\u4e86\uff0c\u7edd\u5bf9\u4e0d\u653e\u8fc7\u6211\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u4f60\u603b\u7b97\u8fd8\u5f88\u6e05\u9192\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6211\u4e00\u76f4\u4e0d\u592a\u7cca\u6d82\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f46\u4f60\u5374\u7b97\u76d8\u6253\u7684\u592a\u5982\u610f\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u73b0\u5728\uff0c\u6211\u5360\u4e86\u5929\u65f6\u5730\u5229\uff0c \u4f60\u51c6\u5907\u5982\u4f55\u5bf9\u4ed8\u6211\uff0c\u4f60\u4eec\u603b\u4e0d\u80fd\u4e0d\u987e\u9ad8\u5929\u5065\uff0c\u5bf9\u4ed8\u6211\u96f7\u65e0\u5f71\u5427?\u201d \u3000\u3000\u9ec4\u7075\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u96be\u9053\u6211\u4eec\u4e0d\u4f1a\u548c\u9ad8\u5929\u5065\u8054\u5408\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u548c\u9ad8\u5929\u5065\u8054\u5408\uff0c\u8fd9\u4e2a\uff0c\u4f60\u4eec\u4e0d\u89c9\u7740\u5192\u9669\u592a\u5927\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5982\u82e5\u4f60\u505a\u7684\u592a\u8fc7\u5206\uff0c\u90a3\u5c31\u662f\u903c\u6211\u4eec\u5148\u884c\u5bf9\u4ed8\u4f60\u4e86\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u9ec4\u67d0\u4eba\u8bf4\u8bdd\uff0c\u5728\u9ad8\u5929\u5065\u7684\u9762\u524d\uff0c\u8fd8\u6709\u4e00\u70b9\u4fe1\u7528\uff0c\u4f46\u4f60\u96f7\u5144\uff0c\u5374\u662f\u5168\u65e0\u4fe1\u7528\u53ef\u8a00\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5821\u4e3b!\u6700\u597d\u522b\u8d70\u6781\u7aef\uff0c\u90a3\u662f\u628a\u6211\u903c\u8fc7\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u672c\u6765\u662f\u5bf9\u4f60\u5f88\u6709\u5229\u7684\u4e8b\uff0c\u4f46\u53ea\u6015\u4f1a\u88ab\u4f60\u5f04\u7cdf\u4e86\u3002\u201d \u96f7\u65e0\u5f71\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u70df\u9501\u697c\u4e2d\uff0c\u6b63\u5728\u589e\u52a0\u4e86\u5f88\u591a\u7684\u5e03\u7f6e\uff0c\u9ad8\u5929\u5065\u628a\u4f60\u4eec\u652f\u8d70\uff0c\u5e76\u975e\u662f\u4e3a\u4e86\u6211\u7684\u5173\u7cfb\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u5e03\u7f6e\u4ec0\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u5728\u90a3\u91cc\u9762\u5b89\u6392\u4e86\u4ec0\u4e48\u57cb\u4f0f\uff0c\u4f46\u6211\u770b\u4ed6\u5168\u65e0\u5fe7\u60e7\u4e4b\u8272\uff0c\u4f3c\u662f\u5145\u6ee1\u7740\u4fe1\u5fc3\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u5bb3\u6015\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ed6\u662f\u4e2a\u5f88\u8c28\u614e\u7684\u4eba\uff0c\u5982\u82e5\u6ca1\u6709\u4ec0\u4e48\u628a\u63e1\uff0c\u4ed6\u4e0d\u4f1a\u90a3\u4e48\u6c89\u7740\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u591a\u8c22\u6307\u6559\uff0c\u5821\u4e3b\uff0c\u54b1\u4eec\u8d70\u5427\u3002\u201d \u3000\u3000\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u6025\u9053\uff1a\u201c\u9ad8\u59d1\u5a18\uff0c\u9ec4\u5821\u4e3b\uff0c\u8bf7\u7559\u6b65\u7247\u523b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6b65\u5c65\u8fc5\u5feb\uff0c\u4eba\u5df2\u51fa\u4e86\u5927\u5385\uff0c\u9ec4\u7075\u5230\u4e86\u5385\u95e8\u53e3\u5904\uff0c\u505c\u6b65\u56de\u8eab\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u96f7\u5144\uff0c\u8fd8\u6709\u4ec0\u4e48\u89c1\u6559?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5982\u82e5\u9ec4\u5821\u4e3b\u80fd\u7ed9\u5728\u4e0b\u4e00\u4e2a\u4fdd\u8bc1\uff0c\u96f7\u67d0\u8fd8\u4f1a\u548c\u9ec4\u5144\u5408\u4f5c\uff01\u201d \u3000\u3000\u9ec4\u7075\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u96f7\u5144\u8981\u4ec0\u4e48\u6837\u5b50\u7684\u4fdd\u8bc1\u5462?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u8bf4\u8d77\u6765\uff0c\u4e5f\u4e0d\u7b97\u4ec0\u4e48\uff0c\u4e00\u65e6\u6d88\u706d\u4e86\u9ad8\u5929\u5065\u4ee5\u540e\uff0c\u5728\u4e0b\u5e0c\u671b\u80fd\u591f\u63a5\u6536\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5728\u4e0b\u6ca1\u6709\u9010\u9e7f\u6b66\u6797\u7684\u5ff5\u5934\uff0c\u53ea\u8981\u5b89\u5b89\u7a33\u7a33\u7684\u7ecf\u8425\u7b2c\u4e00\u5821\uff0c\u9ec4\u5144\u4fdd\u8bc1\u7b2c\u4e00\u5821\u4e0d\u88ab\u6b66\u6797\u540c\u9053\u7684\u4fb5\u72af\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u96f7\u5144\u7684\u60f3\u6cd5\uff0c\u5b9e\u5728\u4e0d\u7b97\u8fc7\u5206\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u5728\u4e0b\u6bd4\u4e0d\u4e0a\u9ad8\u5929\u5065\u7684\u96c4\u624d\u5927\u7565\uff0c\u4f46\u5374\u5341\u5206\u5b88\u5206\u3002\u201d \u9ec4\u7075\u9053\uff1a \u201c\u597d!\u6211\u4f1a\u5f88\u4ed4\u7ec6\u7684\u60f3\u60f3\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u7136\u540e\uff0c\u518d\u56de\u590d\u96f7\u5144\u5982\u4f55?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u80fd\u7acb\u523b\u51b3\u5b9a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u533a\u533a\u5728\u4e0b\uff0c\u5728\u6c5f\u6e56\u4e0a\u8fd9\u70b9\u58f0\u8a89\uff0c \u53ea\u6015\u4e5f\u65e0\u6cd5\u4f5c\u5f97\u4e86\u4e3b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u9ec4\u5144\uff0c\u6709\u4e00\u70b9\uff0c\u5728\u4e0b\u5fc5\u987b\u63d0\u9192\u9ec4\u5144\u7684\uff0c\u90a3\u5c31\u662f\u9ad8\u5929\u5065\u4e0d\u53ea\u7b2c\u4e00\u5821\u8fd9\u70b9\u5b9e\u529b\uff0c\u6628\u591c\uff0c\u4ed6\u5df2\u4f20\u51fa\u4e86\u4ee4\u8c15\uff0c\u53ec\u96c6\u5404\u8def\u7684\u4eba\u624b\uff0c\u56de\u6551\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u6211\u77e5\u9053\uff0c\u7b2c\u4e00\u5821\u4e4b\u4e0b\u6709\u56db\u5927\u9556\u5c40\uff0c\u548c\u51e0\u4e2a\u9ed1\u9053\u7ec4\u5408\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e9b\u5df2\u7ecf\u4e0d\u662f\u79d8\u5bc6\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u9ec4\u5821\u4e3b\u6d88\u606f\u7075\u901a\uff0c\u8fd9\u4e00\u70b9\u5728\u4e0b\u5012\u662f\u758f\u5ffd\u4e86\uff0c\u4e0d\u8fc7\u56db\u5927\u9556\u5c40\uff0c\u548c\u51e0\u4e2a\u7ec4\u5408\u7684\u5b9e\u529b\uff0c\u9ec4\u5821\u4e3b\u77e5\u9053\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7565\u77e5\u4e00\u4e8c!\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u90a3\u51e0\u5bb6\u9556\u5c40\uff0c\u548c\u9ed1\u9053\u7ec4\u5408\uff0c\u4efb\u4f55\u4e00\u5904\u7684\u5b9e\u529b\uff0c\u90fd\u53ef\u7b97\u4e0a\u534a\u4e2a\u7b2c\u4e00\u5821\uff0c\u5982\u82e5\u4ed6\u4eec\u8054\u624b\u800c\u6765\uff0c\u9ec4\u5144\u8fd9\u4e9b\u4eba\u624b\uff0c\u53ea\u6015\u662f\u5f88\u96be\u5e94\u4ed8\u5f97\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\uff0c\u53ea\u6015\u96f7\u5144\u8fd8\u4e0d\u77e5\u9053?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9634\u9633\u5821\u8fd9\u70b9\u5c0f\u5c0f\u7684\u5b9e\u529b\uff0c\u5982\u4f55\u80fd\u591f\u5bf9\u4ed8\u7b2c\u4e00\u5821\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u9ec4\u5821\u4e3b\u8fd8\u6709\u540e\u63f4\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5c11\u6797\u3001\u6b66\u5f53\uff0c\u548c\u5f88\u591a\u6c5f\u6e56\u2014\u4e0a\u7684\u95e8\u6237\uff0c\u90fd\u5df2\u7ecf\u6d3e\u4eba\u5230\u6b64\uff0c\u5927\u6982\u53ef\u4ee5\u5bf9\u4ed8\u5f97\u4e86\u56db\u5927\u9556\u5c40\u548c\u51e0\u4e2a\u7ec4\u5408\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a \u201c\u539f\u6765\u5982\u6b64\u3002\u4e0d\u8fc7\uff0c\u7b2c\u4e00\u5821\u4e5f\u6709\u4e86\u4e3b\u529b\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u662f\u4e0d\u662f\u72c2\u9f99\u548c\u98de\u9e70?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5bf9!\u4ed6\u4eec\u65e9\u5230\u4e86\u7b2c\u4e00\u5821\u4e2d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u662f\u554a!\u8fd9\u4e24\u4e2a\u4eba\uff0c\u4e5f\u65e9\u8be5\u51fa\u73b0\u4e86\uff0c\u4e3a\u4f55\u8fdf\u8fdf\u4e0d\u89c1\u8e2a\u5f71\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ed6\u4eec\u5e26\u9886\u4e86\u4e00\u6279\u4eba\u624b\uff0c\u9690\u4f0f\u5728\u7b2c\u4e00\u5821\u4e2d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u70df\u9501\u697c\u4e2d?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7a81\u7136\u9053\uff1a \u201c\u9664\u4e86\u4f60\u795e\u5200\u5802\u8fd9\u80a1\u529b\u91cf\u5916\uff0c\u7b2c\u4e00\u5821\u8fd8\u6709\u4ec0\u4e48\u5b9e\u529b\u5462?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6709! \u53ea\u4e0d\u8fc7\uff0c\u90a3\u80a1\u529b\u91cf\uff0c\u59d1\u5a18\u4e0d\u77e5\u9053\u7f62\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u5462?\u77e5\u9053\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u81ea\u7136\u662f\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8bf7\u6559\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u59d1\u5a18\u8be5\u77e5\u9053\u53bb\u5e74\u9ad8\u5821\u4e3b\u7684\u5927\u5bff\u5bb4\u4f1a\u4e0a\uff0c\u6709\u4e24\u4e2a\u9762\u76ee\u51b7\u5cfb\u7684\u9ed1\u8863\u4eba\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bb4\u4f1a\u4e0a\u7684\u4eba\u5f88\u591a\uff0c\u7a7f\u9ed1\u8863\u7684\u4eba\u4e5f\u4e0d\u5c11\uff0c\u6211\u5012\u8bb0\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u4eba\u5f88\u7279\u6b8a\uff0c\u4e00\u76f4\u6ca1\u6709\u8bf4\u8fc7\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6211\u60f3\u8d77\u6765\u4e86\uff0c\u786e\u6709\u8fd9\u4e48\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u662f\u90a3\u4e24\u4e2a\u4eba\uff0c\u9886\u5bfc\u6709\u4e00\u80a1\u9887\u5927\u7684\u5b9e\u529b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fdc\u6c34\u96be\u6551\u8fd1\u706b\uff0c\u8fd9\u4e9b\u4eba\uff0c\u53ea\u8981\u4e0d\u5728\u7b2c\u4e00\u5821\uff0c\u5c31\u5f88\u96be\u53d1\u6325\u51fa\u4f5c\u7528\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4f46\u4ed6\u4eec\u5c31\u5728\u7b2c\u4e00\u5821\u4e2d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u9664\u4e86\u70df\u9501\u697c\u4e4b\u5916\uff0c\u5c31\u662f\u4f60\u8fd9\u795e\u5200\u5802\u4e86\uff0c\u9664\u4e86\u8fd9\u4e24\u5904\u5730\u65b9\uff0c\u7b2c\u4e00\u5821\u6ca1\u6709\u6211\u4e0d\u719f\u6089\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u8001\u5b9e\u8bf4\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u8fd9\u4e00\u80a1\u529b\u91cf\u85cf\u5728\u4f55\u5904\uff0c\u4f46\u4ed6\u4eec\u786e\u5728\u7b2c\u4e00\u5821\u4e2d\u3002\u201d \u5434\u53cc\u5973\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u6628\u591c\u9ad8\u5929\u5065\u662f\u8bf4\u670d\u4e86\u4f60\uff0c\u8fd8\u662f\u4e70\u901a\u4e86\u4f60?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u90fd\u4e0d\u662f\uff0c\u9ad8\u5929\u5065\u53ea\u6cc4\u6f0f\u4e86\u8fd9\u4e00\u70b9\u673a\u5bc6\u7ed9\u6211\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff0c\u201c\u4f60\u5c31\u771f\u7684\u76f8\u4fe1\u4e86?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5bf9!\u6211\u76f8\u4fe1\u4e86\uff01\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54fc!\u6211\u770b\uff0c\u4f60\u662f\u88ab\u4ed6\u552c\u4f4f\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u5927\u5c0f\u59d0\uff0c\u4f60\u770b\u6211\u96f7\u65e0\u5f71\u662f\u597d\u552c\u7684\u4eba\u5417?\u9ad8\u5929\u5065\u603b\u8981\u544a\u8bc9\u4e00\u4e9b\u4f7f\u6211\u76f8\u4fe1\u7684\u4e8b\u5b9e\u624d\u884c\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u4e2d\u7684\u673a\u8981\uff0c\u6211\u81ea\u4fe1\u77e5\u9053\u7684\u6bd4\u4f60\u8981\u591a\u4e00\u4e9b\uff0c\u4f46\u6211\u60f3\u4e0d\u51fa\u7b2c\u4e00\u5821\u4e2d\uff0c\u4f1a\u771f\u6709\u8fd9\u80a1\u529b\u91cf\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u534a\u5e74\u524d\uff0c\u7b2c\u4e00\u5821\u4e2d\uff0c\u95f9\u8fc7\u4e00\u6b21\u6349\u8d3c\u7684\u4e8b\uff0c\u59d1\u5a18\u8fd8\u8bb0\u5f97\u5427?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u56de\u4e8b\uff0c\u6211\u8bb0\u5f97\u4f60\u96f7\u5802\u4e3b\uff0c\u8fd8\u5e26\u4e86\u4eba\u624b\uff0c\u641c\u67e5\u4e86\u5927\u534a\u591c\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ee5\u540e\uff0c\u5821\u4e3b\u4e0b\u4ee4\uff0c\u4e0d\u8ba9\u518d\u641c\u67e5\u4e86\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u59d1\u5a18\u662f\u5426\u8fd8\u8bb0\u5f97\uff0c\u90a3\u4e00\u6b21\u95f9\u8d3c\u7684\u7ed3\u679c\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8bb0\u5f97\uff0c\u7b2c\u4e00\u5821\u6b7b\u4e86\u4e09\u4e2a\u6b66\u58eb\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u90a3\u4e09\u4e2a\u4eba\uff0c\u90fd\u662f\u6211\u795e\u5200\u5802\u7684\u4eba\uff0c\u4ed6\u4eec\u662f\u7b2c\u4e00\u6d41\u7684\u5200\u624b\uff0c\u53ea\u53ef\u60dc\u5440\uff0c\u53ef\u60dc\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u53ef\u60dc\u4ec0\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u53ef\u60dc\u59d1\u5a18\u6ca1\u6709\u770b\u8fc7\u90a3\u4e09\u4e2a\u4eba\u7684\u6b7b\u4ea1\u60c5\u5f62\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u5f88\u91cd\u8981\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5f88\u91cd\u8981\uff0c\u4e00\u5251\u7a7f\u5fc3\uff0c\u4e00\u51fb\u800c\u6b7b\uff0c\u90a3\u4e9b\u4eba\u5c31\u7b97\u9047\u4e0a\u4f60\u9ad8\u59d1\u5a18\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u4e5f\u672a\u5fc5\u80fd\u4e00\u5251\u6740\u6b7b\u4ed6\u4eec\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u96f7\u5144\uff0c\u6709\u4ec0\u4e48\u770b\u6cd5?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u90a3\u624d\u662f\u771f\u6b63\u7684\u6740\u624b\uff0c\u9ad8\u5929\u5065\u4eb2\u81ea\u7387\u9886\u7684\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5982\u679c\u7b2c\u4e00\u5821\u771f\u6709\u8fd9\u6837\u7684\u4eba\uff0c\u90a3\u9ad8\u5929\u5065\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u6d3e\u4ed6\u4eec\u51fa\u624b\uff0c\u73b0\u5728\uff0c\u4ed6\u5df2\u7ecf\u662f\u7a77\u9014\u672b\u8def\u4e86\uff0c\u5982\u8fd8\u6709\u4e00\u6279\u8d4c\u6ce8\uff0c\u4ed6\u5e94\u8be5\u7528\u51fa\u6765\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u4f1a\u7684\uff0c\u8fd9\u4e00\u6279\u4eba\uff0c\u5f88\u5feb\u5c31\u4f1a\u51fa\u73b0\uff0c\u548c\u4f60\u6211\u5bf9\u6297\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u548c\u6211\u4eec\u5bf9\u6297\uff0c\u4e0d\u662f\u548c\u4f60\u5bf9\u6297\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u8bba\u548c\u8c01\u5bf9\u6297\uff0c\u8fd9\u4e9b\u4eba\uff0c\u90fd\u5f88\u53ef\u6015\uff0c\u6211\u60f3\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u6b63\u9762\u51fa\u624b\u548c\u4f60\u4eec\u5bf9\u6297\uff0c\u800c\u662f\u5728\u6697\u4e2d\u5411\u4f60\u4eec\u65bd\u88ad\uff0c\u4e5f\u8bb8\u4ed6\u4eec\u9690\u85cf\u4e00\u9762\u5899\u58c1\u7684\u8f6c\u89d2\u5904\u3001\u6697\u5f71\u4e2d\uff0c\u9009\u62e9\u4e86\u6700\u6709\u5229\u7684\u65f6\u673a\uff0c\u4f1a\u7a81\u7136\u523a\u51fa\u4e00\u5251\uff0e\u90a3\u662f\u81f4\u547d\u7684\u4e00\u5251\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u4e9b\u4eba\uff0c\u53ef\u80fd\u9690\u85cf\u5728\u4ec0\u4e48\u5730\u65b9\u5462?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u4eec\u85cf\u5728\u4f55\u5904\uff0c\u4f46\u7edd\u5bf9\u4e0d\u5728\u70df\u9501\u697c\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u53ef\u6709\u6240\u672c?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u70df\u9501\u697c\u53ea\u662f\u4e00\u7247\u7981\u5730\uff0c\u53ef\u80fd\u9690\u85cf\u6709\u5f88\u795e\u79d8\u7684\u4e1c\u897f\uff0c\u4f46\u5b83\u5e76\u4e0d\u7b97\u673a\u5bc6\uff0c\u6211\u4eec\u90fd\u77e5\u9053\uff0c\u90a3\u662f\u9ad8\u5929\u5065\u996e\u9152\u53d6\u4e50\u7684\u5730\u65b9\uff0c\u6709\u4e00\u4e9b\u4fdd\u62a4\u7684\u4eba\uff0c\u4f46\u90a3\u4e9b\u4eba\u662f\u4ed6\u7684\u5fc3\u8179\u6b66\u58eb\uff0c\u4e0d\u662f\u6740\u624b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u601d\u7d22\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u4f1a\u85cf\u5728\u90a3\u91cc?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u5730\u65b9?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u60f3\u6ca1\u6709\u7559\u5fc3\u7684\u5730\u65b9\uff0c\u5c31\u662f\u517b\u6b8b\u9662\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u517b\u6b8b\u9662\uff0c\u8fd9\u662f\u4e2a\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u4e2d\u6709\u4e00\u4e2a\u5f88\u597d\u7684\u5b89\u6392\uff0c\u90a3\u5c31\u662f\u7b2c\u4e00\u5821\u7684\u6b66\u58eb\uff0c\u56e0\u640f\u6740\u800c\u4f24\u6b8b\u65f6\uff0c\u5c31\u53ef\u4ee5\u9001\u5230\u517b\u6b8b\u9662\u4e2d\uff0c\u90a3\u91cc\u6709\u5f88\u597d\u7684\u7167\u987e\uff0c\u76f4\u5230\u6b7b\u53bb\u4e3a\u6b62\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5982\u82e5\u4ed6\u4eec\u8981\u79bb\u5f00\u65f6\uff0c\u662f\u5426\u53ef\u4ee5\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u53ef\u4ee5\uff0c\u517b\u6b8b\u9662\u4e2d\u6709\u4e00\u4e2a\u89c4\u5b9a\uff0c\u517b\u597d\u4e86\u4f24\u52bf\u7684\u4eba\uff0c\u613f\u610f\u79bb\u5f00\u65f6\uff0c\u53ef\u4ee5\u968f\u4fbf\u79bb\u5f00\uff0c\u800c\u4e14\uff0c\u6709\u4e00\u7b14\u5f88\u4e30\u539a\u7684\u517b\u8001\u91d1\u53ef\u62ff\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e0d\u8fc7\uff0c\u5728\u6211\u7684\u8bb0\u5fc6\u4e4b\u4e2d\uff0c\u597d\u50cf\u6ca1\u6709\u4eba\u79bb\u5f00\u8fc7\u517b\u6b8b\u9662\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5462?\u4ed6\u4eec\u90fd\u5f88\u613f\u610f\u7559\u5728\u90a3\u91cc\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u7834\u7efd\uff0c\u4e0d\u8fc7\uff0c\u5f53\u65f6\u6ca1\u6709\u6ce8\u610f\u7f62\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u517b\u6b8b\u9662\u4e2d\u7684\u4eba\uff0c\u53ef\u90fd\u662f\u6b8b\u5e9f?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90fd\u662f\u6b8b\u5e9f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u89c9\u7740\u53ef\u7591\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u7b2c\u4e00\uff0c\u7b2c\u4e00\u5821\u6211\u5f88\u719f\uff0c\u9664\u4e86\u795e\u5200\u5802\u548c\u70df\u9501\u697c\u4e4b\u5916\uff0c\u6240\u6709\u5730\u65b9\uff0c\u6211\u90fd\u77e5\u6089\u6e05\u695a\uff0c\u4f46\u517b\u6b8b\u9662\uff0c\u6211\u5374\u5ffd\u7565\u4e86\uff1b\u7b2c\u4e8c\uff0c\u5982\u82e5\u6709\u4e00\u6279\u4eba\uff0c\u8981\u85cf\u5728\u4e00\u4e2a\u4e0d\u4e3a\u4eba\u77e5\u7684\u5730\u65b9\uff0c\u90a3\u91cc\u662f\u4e2a\u5f88\u597d\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u54b1\u4eec\u5148\u53bb\u517b\u6b8b\u9662\u4e2d\u77a7\u77a7\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u597d!\u54b1\u4eec\u5148\u53bb\u77a7\u77a7\u5427\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u51b7\u7b11\u4e00\u58f0\uff0c\u8f6c\u8eab\u884c\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5982\u82e5\u8bf8\u4f4d\u5728\u517b\u6b8b\u9662\u4e2d\u518d\u4f5c\u4e00\u573a\u640f\u6597\uff0c\u53ea\u6015\u96be\u514d\u6709\u4e9b\u4f24\u4ea1\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u5982\u662f\u4f24\u4ea1\u5f88\u60e8\u91cd\uff0c\u4f60\u4eec\u518d\u56de\u5934\u5bf9\u9ad8\u5929\u5065\u65f6\uff0c\u53ea\u65e7\u5b9e\u529b\u4e0a\uff0c\u4f1a\u6709\u5f88\u5927\u7684\u6298\u635f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u96f7\u5802\u4e3b\u544a\u8bc9\u6211\u4eec\u8fd9\u4e9b\u4e8b\uff0e\u7528\u5fc3\u4f55\u5728\u5462?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5728\u4e0d\u53ea\u662f\u63d0\u9192\u8bf8\u4f4d\u4e00\u58f0\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u5df2\u7ecf\u63d0\u9192\u6211\u4eec\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u5728\u4e0b\u548c\u8bf8\u4f4d\u5408\u4f5c\u4e01\u4e00\u573a\uff0c\u5949\u544a\u8bf8\u4f4d\u51e0\u53e5\uff0c\u7b97\u662f\u7a0d\u5c3d\u4e86\u4e00\u4e9b\u5fc3\u610f\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u591a\u8c22\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u7528\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u96f7\u5802\u4e3b\u7684\u610f\u601d\uff0c\u73b0\u5728\u548c\u6211\u4eec\u5b8c\u5168\u65e0\u5173\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u80fd\u628a\u5728\u4e0b\u8bf4\u5f97\u5982\u6b64\u65e0\u60c5\u3001\u5be1\u4e49\u3002\u201d \u3000\u3000\u7a81\u7136\u8f6c\u8eab\uff0c\u5927\u6b65\u800c\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u624b\u63e1\u5251\u67c4\uff0c\u51e0\u6b21\u60f3\u62d4\u5251\u6295\u51fa\uff0c\u4f46\u5374\u5fcd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9ec4\u7075\u7b49\u9000\u51fa\u795e\u5200\u5802\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u9ec4\u5821\u4e3b\uff0c\u5982\u82e5\u96f7\u65e0\u5f71\u6ca1\u6709\u9a97\u54b1\u4eec\uff0c\u53ea\u6015\u8fd9\u4e00\u8def\u5230\u70df\u9501\u697c\uff0c\u90fd\u4f1a\u6709\u4eba\u4f0f\u6740\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u7684\u610f\u601d\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5148\u5230\u70df\u9501\u697c\u53bb\uff0c\u5821\u4e3b\uff0c\u96f7\u65e0\u5f71\u65e2\u7136\u6709\u4e86\u53d8\u5316\uff0c\u54b1\u4eec\u4e5f\u4e0d\u80fd\u4e00\u6210\u4e0d\u53d8\uff0c \u76ee\u4e0b\uff0c\u8fd9\u7b2c\u4e00\u5821\u4e2d\u6709\u4e09\u4e2a\u529b\u91cf\u3002\u9ad8\u5929\u5065\u3001\u96f7\u65e0\u5f71\uff0c\u548c\u6211\u4eec\uff0c\u8c01\u8981\u80fd\u5148\u6311\u8d77\u53e6\u5916\u4e24\u65b9\u9762\u52a8\u624b\uff0c\u90a3\u4eba\u5c31\u64cd\u4e86\u5fc5\u7136\u80dc\u7b97\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\u59d1\u5a18\u7684\u610f\u601d\uff0c\u4e0d\u8fc7\uff0c\u73b0\u5728\u518d\u8054\u5408\u96f7\u65e0\u5f71\uff0c\u5df2\u7ecf\u6709\u4e9b\u4e0d\u53ef\u80fd\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bf9!\u6240\u4ee5\uff0c\u8bd5\u8bd5\u9ad8\u5929\u5065\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u4e0e\u864e\u8c0b\u76ae\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u8fd9\u91cc\u6709\u4e24\u53ea\u864e\uff0c\u54b1\u4eec\u65e2\u4e0d\u80fd\u540c\u65f6\u6253\u4e24\u53ea\u8001\u864e\uff0c\u90a3\u53ea\u6709\u60f3\u529e\u6cd5\uff0c\u5148\u8ba9\u4e8c\u864e\u76f8\u6597\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8bf4\u6765\u5bb9\u6613\uff0c\u4f46\u529e\u8d77\u6765\uff0c\u53ea\u6015\u662f\u56f0\u96be\u5f97\u5f88\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\uff0c\u6211\u77e5\u9053\u5f88\u56f0\u96be\uff0c\u4f46\u603b\u8981\u8bd5\u8bd5\u624d\u597d\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u7ea2\u82b1\u3001\u7eff\u53f6\uff0c\u8ddf\u6211\u5f00\u9053\u3002\u201d \u3000\u3000\u7ea2\u82b1\uff0c\u7eff\u53f6\u5e94\u4e86\u4e00\u58f0\uff0c\u5927\u6b65\u5411\u524d\u884c\u53bb\uff0c\u4e24\u4eba\u4e00\u5de6\u4e00\u53f3\uff0c\u968f\u5728\u9ad8\u5e7d\u5170\u7684\u8eab\u540e\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u8c28\u9632\u7a81\u88ad\u3002\u201d \u3000\u3000\u7ea2\u82b1\u3001\u7eff\u53f6\u70b9\u70b9\u5934\uff0c\u624b\u63e1\u5251\u67c4\uff0c\u76ee\u6ce8\u4e24\u4fa7\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7684\u51b3\u65ad\u3001\u679c\u6562\uff0c\u65e0\u5f62\u4e2d\u5df2\u6210\u4e3a\u9ec4\u7075\u4e4b\u5916\u7684\u53e6\u4e00\u4e2a\u9886\u8896\u4eba\u7269\u3002 \u3000\u3000\u671b\u7740\u9ad8\u5e7d\u5170\u7684\u80cc\u5f71\uff0c\u9ec4\u7075\u5fc3\u4e2d\u6697\u6697\u53f9\u9053\uff1a\u5e78\u597d\u5979\u662f\u4e00\u4e2a\u5973\u4eba\uff0c\u4e5f\u5e78\u597d\u5979\u7231\u4e0a\u4e86\u4f55\u5bd2\u8863\uff0c\u4e5f\u5e78\u597d\u4f55\u5bd2\u8863\u662f\u4e00\u4e2a\u5f88\u6b63\u6d3e\u7684\u4eba\uff0e\u662f\u6211\u7684\u670b\u53cb\uff0c\u4e5f\u5e78\u597d\uff0c\u9ad8\u5929\u5065\u6bd2\u4e86\u5979\u4e00\u4e0b\uff0c \u8fd9\u4f4d\u59d1\u5a18\u7684\u6b66\u529f\u5982\u4f55\uff0c\u4e0d\u53bb\u7ba1\u5b83\uff0c\u5c31\u662f\u8fd9\u4e00\u4efd\u679c\u6562\u7684\u884c\u52a8\uff0c\u987b\u7709\u96be\u53ca\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u95ee\u9ad8\u5e7d\u5170\u76ee\u7684\u4f55\u5904\uff0c\u5927\u5bb6\u53ea\u662f\u8ddf\u5728\u5979\u7684\u8eab\u540e\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u8d70\u5411\u4e86\u70df\u9501\u697c\u3002 \u3000\u3000\u70df\u9501\u697c\u6805\u95e8\u7d27\u95ed\uff0c\u5374\u4e0d\u89c1\u9632\u5b88\u4e4b\u4eba\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u6ca1\u6709\u51b2\u8fdb\u53bb\uff0c\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u6709\u6ca1\u6709\u5f53\u503c\u7684\u4eba?\u201d \u3000\u3000\u201c\u6709\uff0c\u201d\u6805\u95e8\u540e\uff0c\u5c0f\u5c4b\u4e2d\uff0c\u884c\u51fa\u4e86\u4e00\u4e2a\u7a7f\u7740\u52b2\u88c5\u7684\u5251\u58eb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u4e00\u4e2a\u4eba\u5728\u9632\u5b88\u3002\u201d \u3000\u3000\u52b2\u88c5\u5927\u6c49\u9053\uff1a\u201c\u4e0d\u662f\u9632\u5b88\uff0c\u662f\u5728\u5b88\u95e8\u3002\u201d \u3000\u3000\u4e00\u9762\u6253\u5f00\u4e86\u6805\u95e8\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u770b\u4ed6\u867d\u7740\u52b2\u88c5\uff0c\u4f46\u5374\u672a\u5e26\u5175\u5200\uff0c\u5f53\u4e0b\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u52b2\u88c5\u5927\u6c49\u9053\uff1a\u201c\u5728\u4e0b\u5949\u4e86\u5821\u4e3b\u4e4b\u547d\uff0c\u5728\u6b64\u7b49\u4faf\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7b49\u5019\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u52b2\u88c5\u5927\u6c49\u9053\uff1a\u201c\u7b49\u4faf\u8bf8\u4f4d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u53c8\u5b89\u6392\u4e86\u4ec0\u4e48\u8be1\u8ba1?\u201d \u3000\u3000\u52b2\u88c5\u5927\u6c49\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u5728\u4e0b\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u60f3\uff0c\u8fd9\u5ea7\u6805\u95e8\u4e4b\u540e\uff0c\u65e9\u5df2\u5e03\u6ee1\u4e86\u6bd2\u836f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4f55\u9ad8\u89c1\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u70df\u9501\u697c\u8fd9\u6837\u5927\u4e00\u5ea7\u7a7a\u65f7\u7684\u9662\u5b50\uff0c\u5c31\u7b97\u54b1\u4eec\u653e\u706b\u70e7\uff0c\u53ea\u6015\u4e5f\u65e0\u6cd5\u70e7\u5230\u90a3\u5ea7\u70df\u9501\u697c\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u90a3\u662f\u65e0\u53ef\u5948\u4f55\u4e86?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u5821\u4e3b\u548c\u4ed6\u7b54\u8bdd\u5427\uff0c\u8981\u4ed6\u627e\u9ad8\u5929\u5065\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u4e3e\u6b65\u76f4\u5165\u6805\u95e8\u3002 \u3000\u3000\u7384\u5a18\u9ad8\u58f0\u53eb\u9053\uff1a \u201c\u5c0f\u5fc3\uff0c\u522b\u518d\u4e2d\u4e86\u6bd2\uff0c\u5bb3\u6211\u53c8\u8981\u9000\u51fa\u7b2c\u4e00\u5821\u3002\u201d \u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4f1a\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u53e3\u8bf4\u5c0f\u5fc3\uff0c\u4eba\u5374\u76f4\u5411\u6805\u95e8\u884c\u53bb\u3002 \u3000\u3000\u4ed4\u7ec6\u6253\u91cf\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u53bb\u627e\u9ad8\u5929\u5065\u6765\u3002\u201d \u3000\u3000\u52b2\u88c5\u5927\u6c49\u9053\uff1a \u201c\u5821\u4e3b\u5429\u5490\u8fc7\u4e86\uff0c\u4f60\u8981\u89c1\u9ad8\u5821\u4e3b\uff0c\u53ef\u4ee5\u4e0d\u7528\u901a\u62a5\uff0c\u4ed6\u5728\u70df\u9501\u697c\u4e2d\u606d\u5019\u5927\u9a7e\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u53bb\u544a\u8bc9\u4ed6\uff0c\u6211\u4eec\u4e0d\u8fdb\u53bb\u5566\uff0c\u8981\u4ed6\u51fa\u6765\u89c1\u9762\u3002\u201d \u3000\u3000\u5b88\u95e8\u4eba\u884c\u7ea6\u56db\u4e08\uff0c\u7a81\u7136\u56de\u8fc7\u8eab\u5b50\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a \u201c\u5821\u4e3b\u6599\u5230\u4e86\u8bf8\u4f4d\u4e0d\u4f1a\u8fdb\u6765\uff0c\u4f60\u4eec\u5982\u662f\u65e0\u80c6\u8fdb\u5165\u70df\u9501\u697c\uff0c\u53ea\u6015\u5f88\u96be\u89c1\u5230\u4ed6\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u672a\u7406\u4f1a\u90a3\u4eba\uff0c\u5374\u56de\u5934\u5bf9\u9ad8\u5e7d\u5170\u7b49\u8bf4\u9053\uff1a\u201c\u6211\u770b\uff0c\u4ed6\u603b\u4e0d\u4f1a\u5728\u6574\u5ea7\u7684\u5ead\u9662\u4e2d\u5e03\u6bd2\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4e0d\u8981\u5192\u9669\uff0c\u9ad8\u5929\u5065\u7f29\u5934\u4e0d\u51fa\uff0c\u663e\u7136\u662f\u5230\u4e86\u7a77\u9014\u672b\u8def\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\uff0c\u54b1\u4eec\u5982\u4e0d\u80fd\u4e00\u9f13\u4f5c\u6c14\uff0c\u628a\u4ed6\u6b7c\u706d\uff0c\u53ea\u6015\u4f1a\u591c\u957f\u68a6\u591a\u3002\u201d \u3000\u3000\u7384\u5a18\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u5165\u70df\u9501\u697c\uff0c\u96be\u9053\u5c31\u65e0\u6cd5\u5bf9\u4ed8\u4ed6\u5417?\u201d \u3000\u3000\u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7384\u5a18\u6709\u4f55\u9ad8\u89c1?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u8fd9\u91cc\u4e0d\u9002\u5408\u7528\u706b\uff0c\u4e3a\u4f55\u4e0d\u6539\u7528\u6c34?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7528\u6c34\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u5bf9!\u7528\u6c34\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6c34\u5728\u54ea\u91cc?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u8377\u6c60\u4e2d\u6709\u6c34\uff0c\u5f15\u81ea\u5c71\u4e0a\uff0c\u5982\u82e5\u60f3\u6cd5\u5b50\u628a\u6c34\u5f15\u5165\u6b64\u5730\uff0c\u6df9\u4e86\u70df\u9501\u697c\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u628a\u6c34\u5f15\u5165\u6b64\u5730\uff0c\u6015\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7384\u5a18\u8bf4\u7684\u4e0d\u9519\uff0c\u8fd8\u4ef6\u4e8b\u4e0d\u4f1a\u592a\u96be\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u4e0d\u4f1a\u592a\u96be\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bf9\u5f15\u5165\u8377\u6c60\u7684\u6c34\uff0c\u662f\u5c71\u4e0a\u5f15\u4e0b\u6765\u7684\uff0c\u53ea\u8981\u5f00\u51fa\u4e00\u4e2a\u6c34\u53e3\uff0c\u628a\u6c34\u5f15\u5165\u8fd9\u4e2a\u65b9\u5411\uff0c\u5c31\u53ef\u4ee5\u6c34\u56f0\u70df\u9501\u697c\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u5ea7\u697c\u76f8\u5f53\u7684\u9ad8\uff0c\u6df9\u4e86\u5b83\uff0c\u53ea\u6015\u5f88\u96be\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u542c\u61c2\u9ad8\u59d1\u5a18\u7684\u8bdd\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u59d1\u5a18\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u7384\u5a18\u63a5\u9053\uff1a\u201c\u662f\u6c34\u56f0\u70df\u9501\u697c\uff0c\u4e0d\u662f\u6c34\u6df9\u70df\u9501\u697c\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u770b\u4f60\u548c\u53f6\u5144\u4e2d\u6bd2\u7684\u60c5\u5f62\uff0c\u4e3e\u4e00\u53cd\u4e09\uff0c\u60f3\u6765\uff0c\u9ad8\u5929\u5065\u5f88\u53ef\u80fd\u4e5f\u662f\u4e2a\u7528\u6bd2\u7684\u9ad8\u624b\uff0c\u81f3\u5c11\u5728\u70df\u9501\u697c\u4e2d\uff0c\u6709\u4e00\u4e2a\u5f88\u5584\u4e8e\u7528\u6bd2\u7684\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\u3002 \u3000\u3000\u7384\u5a18\u63a5\u9053\uff1a \u201c\u70df\u9501\u697c\u6805\u95e8\u5927\u5f00\uff0c\u54b1\u4eec\u5374\u4e0d\u6562\u8fdb\u53bb\uff0c\u54b1\u4eec\u6015\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6015\u4ed6\u5e03\u6bd2\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u6bd2\u6015\u6c34\uff0c\u5927\u6c34\u4e00\u51b2\uff0c\u7eb5\u6709\u4ec0\u4e48\u5947\u6bd2\uff0c\u4e5f\u4f1a\u88ab\u5927\u6c34\u51b2\u8d70\u3002\u201d \u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u5012\u4e5f\u662f\uff0c\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5f15\u51fa\u90a3\u91cc\u7684\u6c34\u6e90\uff0c\u5e76\u975e\u662f\u4e00\u4ef6\u96be\u4e8b\uff0c\u6211\u7acb\u523b\u53bb\u529e\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u6162\u4e00\u70b9\uff0c\u9ad8\u59d1\u5a18\u4e0d\u80fd\u4e00\u4e2a\u4eba\u53bb\uff0c\u96f7\u65e0\u5f71\u7684\u8bdd\uff0c\u6211\u4eec\u4e0d\u80fd\u5168\u4fe1\uff0c\u4f46\u4e5f\u4e0d\u80fd\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5821\u4e3b\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u5e26\u4e24\u4e2a\u4eba\u540c\u53bb\uff0c\u4e00\u65e6\u9047\u53d8\uff0c\u4e5f\u597d\u6709\u4e2a\u63a5\u5e94\uff0c\u53cd\u6b63\u9ad8\u5929\u5065\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e0d\u6562\u51fa\u6765\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4e8b\u60c5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5e26\u7740\u7ea2\u82b1\u3001\u7eff\u53f6\u4e00\u8d77\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u671b\u4e86\u5434\u53cc\u5973\u4e00\u773c\uff0e\u9053\uff1a\u201c\u5927\u59ca\uff0c\u8981\u4e0d\u8981\u2014\u8d77\u53bb\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u4f60\u4eec\u5c0f\u5fc3\u4e00\u4e9b\u5427\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u4e3e\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u7ea2\u82b1\u3001\u7eff\u53f6\u7d27\u968f\u5728\u9ad8\u5e7d\u5170\u4e24\u4fa7\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7ee7\u540e\u800c\u884c\u3002 \u3000\u3000\u7384\u5a18\u4f4e\u58f0\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u54b1\u4eec\u8981\u60f3\u6cd5\u5b50\uff0c\u5835\u4e0a\u8fd9\u4e2a\u6805\u95e8\uff0c\u7528\u8fd9\u7247\u56f4\u5899\u84c4\u6c34\u3002\u201d \u9ec4\u7075\u9053\uff1a \u201c\u54b1\u4eec\u8fd9\u5c31\u52a8\u624b\uff0c\u70df\u9501\u697c\u4e0a\uff0c\u53ef\u4ee5\u770b\u5230\u54b1\u4eec\u884c\u52a8\uff0c\u4ee5\u9ad8\u5929\u5065\u7684\u806a\u660e\uff0c\u5fc5\u7136\u4f1a\u7406\u89e3\u6211\u4eec\u7684\u7528\u5fc3\u4f55\u5728?\u90a3\u65f6\uff0c\u4ed6\u4e5f\u8bb8\u4f1a\u6709\u6240\u53cd\u5e94\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u8fd8\u8981\u7559\u5fc3\u4f55\u5144\u548c\u9ad8\u59d1\u5a18\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u51e0\u4eba\u4e00\u52a8\u624b\uff0c\u70df\u9501\u697c\u4e0a\uff0c\u7acb\u523b\u6709\u4e86\u53cd\u5e94\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7684\u58f0\u97f3\uff0c\u8fdc\u8fdc\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\uff0c\u4f60\u5728\u6253\u4ec0\u4e48\u4e3b\u610f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\uff0c\u80fd\u591f\u770b\u5230\u533a\u533a\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u770b\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u6211\u4eec\u51c6\u5907\u5835\u4e0a\u8fd9\u5ea7\u6805\u95e8\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u4eec\u60f3\u5f15\u6c34\u56f0\u4f4f\u70df\u9501\u697c\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5728\u4e0b\u6b63\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u5c31\u7b97\u5f15\u6765\u6c34\u6e90\uff0c\u5835\u4e0a\u6805\u95e8\uff0c\u4e5f\u4e00\u6837\u6df9\u4e0d\u4e86\u6211\u3002\u201d\u9ec4\u7075\u9053\uff1a \u3000\u3000\u201c\u8fd9\u6837\u8c08\u8bdd\u592a\u8d39\u529b\uff0c\u9ad8\u5821\u4e3b\uff0c\u4f55\u4e0d\u51fa\u6765\u4e00\u8c08\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u5728\u70df\u9501\u697c\u5019\u9a7e\uff0c\u9ec4\u5144\u8bf7\u8fdb\u6765\u5427\u3002\u201d \u3000\u3000\u9ec4\u7075\u4e0d\u518d\u7406\u4f1a\u9ad8\u5929\u5065\uff0c\u5374\u52a8\u624b\u5c01\u95ed\u6805\u95e8\u3002 \u3000\u3000\u7b2c\u4e00\u5821\u5230\u5904\u90fd\u662f\u53ef\u7528\u7684\u6750\u6599\u3002 \u3000\u3000\u5f88\u5feb\uff0c\u628a\u6805\u95e8\u5835\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e0d\u8fdc\u5904\u7684\u5c71\u5761\u4e0a\uff0c\u5df2\u53ef\u770b\u5230\u4e00\u80a1\u7ffb\u6eda\u7684\u6d41\u7011\uff0c\u5377\u6cc4\u800c\u81f3\u3002 \u3000\u3000\u6c34\u6d41\u6781\u5feb\uff0c\u7247\u523b\u95f4\uff0c \u5df2\u7ecf\u51b2\u8fd1\u4e86\u56f4\u5899\u3002 \u3000\u3000\u8fd9\u56f4\u5899\u9ad8\u8fc7\u4e00\u4e08\uff0c\u800c\u4e14\u5f88\u575a\u5b9e\uff0c\u4f46\u5374\u88ab\u4f55\u5bd2\u8863\u5728\u5899\u4e0a\u649e\u5f00\u4e00\u4e2a\u5927\u6d1e\uff0c\u6d2a\u6c34\u5c31\u4ece\u90a3\u6d1e\u4e2d\u6d8c\u5165\u3002 \u3000\u3000\u6c34\u52bf\u6108\u6765\u6108\u5927\uff0c\u4e00\u4f1a\u513f\u5de5\u592b\uff0c\u6574\u5ea7\u70df\u9501\u697c\u7684\u5ead\u9662\u4e2d\uff0c\u90fd\u770b\u5230\u4e86\u79ef\u6c34\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u3001\u9ad8\u5e7d\u5170\u3001\u7ea2\u82b1\u3001\u7eff\u53f6\uff0c\u90fd\u98de\u8eab\u4e0a\u4e86\u56f4\u5899\uff0c\u8f6c\u5230\u6805\u95e8\u524d\u9762\u3002 \u3000\u3000\u9ec4\u7075\u671b\u671b\u6025\u6d8c\u800c\u6765\u7684\u6c34\u52bf\uff0c\u7b11\u9053\uff1a \u201c\u8fd9\u5ea7\u5ead\u9662\u867d\u5927\uff0c\u8981\u6c34\u6ee1\u56f4\u5899\uff0c\u53ea\u9700\u8981\u534a\u65e5\u529f\u592b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u770b\u6765\uff0c\u9ad8\u5929\u5065\u5f88\u6c89\u5f97\u4f4f\u6c14\uff0c\u4f3c\u4e4e\u51c6\u5907\u6b7b\u5728\u70df\u9501\u6402\u4e86\u3002\u201d \u7384\u5a18\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u8fd9\u4e00\u80a1\u5927\u6c34\uff0c\u53ef\u4ee5\u7834\u574f\u9ad8\u5929\u5065\u5f88\u591a\u7684\u8ba1\u5212\uff0c\u4f7f\u4ed6\u7684\u5e03\u7f6e\u88ab\u51b2\uff0c\u4f7f\u4ed6\u7684\u63f4\u624b\u53d7\u963b\uff0c\u54b1\u4eec\u53ef\u4ee5\u5168\u5fc3\u5168\u610f\u5bf9\u4ed8\u4ed6\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4ee5\u76ee\u4e0b\u54b1\u4eec\u8fd9\u4e9b\u4eba\u624b\uff0c\u5bf9\u4ed8\u9ad8\u5929\u5065\uff0c\u662f\u4e00\u4e2a\u80dc\u8d1f\u96be\u6599\u7684\u5c40\u9762\uff0c\u6015\u7684\u662f\u96f7\u65e0\u5f71\u51fa\u624b\u76f8\u52a9\uff0c\u54b1\u4eec\u5c31\u80dc\u7b97\u4e0d\u5927\u4e86\u3002\u201d \u3000\u3000\u7384\u5a18\u51b7\u8083\u5730\u8bf4\u9053\uff1a\u201c\u9ad8\u5929\u5065\u8be1\u8ba1\u591a\u7aef\uff0c\u8fd9\u4e2a\u4eba\u7edd\u5bf9\u4e0d\u80fd\u7559\u4e0b\uff0c\u8d31\u59be\u7684\u770b\u6cd5\u662f\u5148\u6740\u4e86\u9ad8\u5929\u5065\uff0c\u518d\u56de\u5934\u5bf9\u4ed8\u96f7\u65e0\u5f71\uff0c\u4f1a\u6536\u529f\u500d\u4e8b\u534a\u4e4b\u6548\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u7ec6\u7b97\uff1a\u70df\u9501\u697c\u4e2d\u4eba\u7269\uff0c\u5c31\u54b1\u4eec\u5df2\u77e5\u9053\u7684\u4eba\u624b\u4e2d\uff0c\u9664\u4e86\u9ad8\u5929\u5065\u4e4b\u5916\uff0c\u4e24\u4e2a\u6700\u624e\u624b\u7684\u4eba\u7269\uff0c\u5c31\u662f\u5343\u9488\u4e07\u8292\u5deb\u5a46\u5a46\uff0c\u548c\u6bd2\u624b\u516c\u5b50\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u5e2e\u4f60\uff0c\u4e8c\u5341\u62db\u5185\u53ef\u4ee5\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\uff0c\u7edd\u4e0d\u80fd\u7559\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u6211\u4eec\u8001\u592b\u59bb\uff0c\u5bf9\u4ed8\u5343\u9488\u4e07\u8292\u5deb\u5a46\u5a46\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u6211\u5bf9\u4ed8\u9ad8\u5929\u5065\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u548c\u53f6\u5144\u3001\u4f55\u5144\u3001\u4e07\u5144\u5bf9\u4ed8\u5176\u4ed6\u7684\u4eba\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u6c34\u52bf\u5df2\u6da8\u5230\u4e09\u5c3a\u591a\u6df1\u3002 \u3000\u3000\u4f46\u70df\u9501\u697c\u4e0a\uff0c\u4ecd\u65e0\u52a8\u9759\u3002 \u3000\u3000\u7384\u5a18\u56de\u987e\u4e86\u4e00\u773c\uff0c\u9053\uff1a \u201c\u7b49\u6c34\u52bf\u518d\u6da8\u4e24\u5c3a\uff0c\u54b1\u4eec\u5c31\u53ef\u4ee5\u6252\u53bb\u8fd9\u6805\u95e8\u4e0a\u7684\u963b\u6321\u4e4b\u7269\u4e86\uff0c\u653e\u8d70\u8fd9\u80a1\u6d2a\u6c34\uff0c\u7136\u540e\u627e\u4e0a\u70df\u9501\u697c\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6d2a\u6c34\u51b2\u53bb\u4e86\u5947\u6bd2\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u60c5\u5f62\u6709\u4e9b\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u54ea\u91cc\u4e0d\u5bf9?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5929\u5065\u4f3c\u662f\u6709\u6043\u65e0\u6050\uff0c\u5b8c\u5168\u4e0d\u5728\u4e4e\u8fd9\u80a1\u6d2a\u6d41\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u653e\u6c34\u7684\u7528\u5fc3\uff0c\u5728\u51b2\u53bb\u5ead\u9662\u5e03\u4e0b\u7684\u5947\u6bd2\uff0c\u771f\u6b63\u8981\u56f0\u4ed6\u4eec\uff0c\u7edd\u975e\u6613\u4e8b\u3002\u201d \u3000\u3000\u6c34\u52bf\u6f2b\u8fc7\u4e86\u4e00\u697c\uff0c\u6574\u5ea7\u7684\u5927\u5ead\u9662\u4e2d\uff0c\u4e5f\u53d8\u6210\u4e86\u4e00\u7247\u6c6a\u6d0b\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u7a81\u7136\u6709\u51e0\u53ea\u4fe1\u9e3d\uff0c \u7531\u70df\u9501\u697c\u4e0a\uff0c\u98de\u4e86\u8d77\u6765\uff0c\u7834\u7a7a\u800c\u53bb\u3002 \u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u539f\u6765\u4ed6\u548c\u5404\u79cd\u4eba\u624b\u8054\u7edc\u7684\u5730\u65b9\uff0c\u4e5f\u5728\u70df\u9501\u697c\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u4ed6\u4e3a\u4ec0\u4e48\u65bd\u653e\u4fe1\u9e3d\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u53ec\u5524\u4ed6\u9644\u8fd1\u7684\u4eba\u624b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u54b1\u4eec\u4e00\u8def\u4e0a\uff0c\u4e00\u76f4\u672a\u906d\u5230\u4efb\u4f55\u88ad\u51fb\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u96f7\u65e0\u5f71\u7684\u5a01\u80c1\uff0c\u4e0d\u662f\u7a7a\u7a74\u6765\u98ce\uff0c\u9ad8\u5929\u5065\u6700\u6068\u80cc\u53db\u4ed6\u7684\u4eba\uff0c\u7adf\u7136\u80fd\u591f\u5fcd\u4e0b\u4e86\u5fc3\u4e2d\u7684\u6012\u706b\u653e\u5f03\u6740\u6b7b\u96f7\u65e0\u5f71\u8ba1\u5212\uff0c\u8fd9\u79cd\u75db\u82e6\uff0c\u5728\u9ad8\u5929\u5065\u800c\u8a00\u662f\u4e00\u79cd\u6781\u7aef\u7684\u75db\u82e6\uff0c\u4ed6\u7adf\u7136\u541e\u4e86\u4e0b\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u96f7\u65e0\u5f71\u638c\u63e1\u4e86\u76f8\u5f53\u591a\u7684\u5b9e\u529b\uff0c\u662f\u987e\u8651\u4e4b\u4e00\uff0c\u4f46\u4ed6\u6700\u5927\u7684\u987e\u8651\uff0c\u662f\u4f60\u548c\u96f7\u65e0\u5f71\u4e4b\u95f4\u7684\u9009\u62e9\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u96f7\u65e0\u5f71\u867d\u7136\u638c\u63e1\u4e86\u5b9e\u529b\uff0c\u4f46\u9ad8\u5929\u5065\u60f3\u4e86\u4e00\u60f3\uff0c\u89c9\u5f97\u5bf9\u4ed8\u4f60\uff0c\u8fd8\u662f\u6bd4\u5bf9\u4ed8\u96f7\u65e0\u5f71\u91cd\u8981\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u7684\u610f\u601d\uff0c\u662f\u89c9\u5f97\u5bf9\u4ed8\u6211\uff0c\u6bd4\u5bf9\u4ed8\u96f7\u65e0\u5f71\u8fd8\u8981\u91cd\u8981\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u6709\u8fd9\u4e00\u4e2a\u7406\u7531\uff0c\u624d\u4f7f\u5f97\u96f7\u65e0\u5f71\u53d8\u4e86\u4e3b\u610f\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u73b0\u5728\uff0c\u54b1\u4eec\u5168\u529b\u5bf9\u4ed8\u9ad8\u5929\u5065\uff0c\u4f1a\u4e0d\u4f1a\u4f7f\u96f7\u65e0\u5f71\u5750\u6536\u6e14\u5229\u3002\u201d \u9ad8\u5e7d\u5170\u56de\u987e\u5434\u53cc\u5973\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5927\u59ca\uff0c\u4f60\u5bf9\u96f7\u65e0\u5f71\u8fd9\u4e2a\u4eba\u7684\u770b\u6cd5\u5982\u4f55?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u96f7\u65e0\u5f71\u5e76\u4e0d\u50bb\uff0c\u4ed6\u5bf9\u8fd9\u4ef6\u4e8b\uff0c\u4e5f\u660e\u767d\u5f97\u5f88\uff0c\u6211\u60f3\uff0c\u4ed6\u660e\u767d\u9ad8\u5929\u5065\u5982\u82e5\u5728\u8fd9\u4e00\u573a\u640f\u6740\u4e4b\u4e2d\u83b7\u80dc\u7684\u540e\u679c\uff0c\u6bd4\u6211\u4eec\u83b7\u80dc\u4e86\u8fd8\u8981\u4e25\u91cd\u5341\u500d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4e0d\u4f1a\uff0c\u4f46\u8fd9\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u80fd\u76f8\u4fe1\uff0e\u5982\u82e5\u4ed6\u89c9\u7740\u5728\u54b1\u4eec\u6d88\u706d\u4e86\u9ad8\u5929\u5065\u8fd9\u4e00\u80a1\u529b\u91cf\u4e4b\u540e\uff0c\u6709\u628a\u63e1\u80fd\u628a\u54b1\u4eec\u4e00\u4e3e\u6b7c\u706d\uff0c\u4ed6\u5c31\u4f1a\u7a81\u7136\u51fa\u624b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u7b49\u54b1\u4eec\u6d88\u706d\u4e86\u9ad8\u5929\u5065\u4e4b\u540e\uff0c\u4ed6\u518d\u51fa\u624b\uff0c\u90a3\u65f6\u95f4\uff0c\u54b1\u4eec\u518d\u56de\u5934\u5bf9\u4ed8\u4ed6\uff0c\u867d\u7136\u5f88\u5403\u529b\uff0c\u4f46\u6211\u76f8\u4fe1\u53ef\u4ee5\u5e94\u4ed8\uff0c\u6015\u7684\u662f\uff0c\u54b1\u4eec\u6b63\u5728\u5bf9\u4ed8\u9ad8\u5929\u5065\u65f6\uff0c\u4ed6\u6697\u653e\u51b7\u7bad\uff0c\u7a81\u7136\u51fa\u624b\uff0c\u90a3\u5c31\u6709\u4e9b\u9ebb\u70e6\u4e86\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u6211\u60f3\u5e94\u8be5\u6709\u4e2a\u529e\u6cd5\u5bf9\u4ed8\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u529e\u6cd5?\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u7ecf\u8fc7\u8fd9\u4e00\u573a\u5927\u6c34\uff0c\u8fd9\u91cc\u7eb5\u7136\u5e03\u6709\u5947\u6bd2\uff0c\u4e5f\u88ab\u5927\u6c34\u51b2\u53bb\u4e86\uff0c\u5c31\u7b97\u8fd9\u91cc\u57cb\u4f0f\u6709\u5730\u96f7\u706b\u70ae\uff0c\u4e5f\u4f1a\u5931\u53bb\u6548\u7528\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u53ef\u4ee5\u653e\u5fc3\u7684\u628a\u51b3\u6218\u5b89\u6392\u5728\u70df\u9501\u697c\u4e0a\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u597d\u529e\u6cd5\uff0c\u53ea\u8981\u96f7\u65e0\u5f71\u770b\u4e0d\u5230\u660e\u663e\u7684\u7ed3\u679c\uff0c\u5c31\u4e0d\u4f1a\u51fa\u624b\u3002\u201d \u8fd9\u65f6\uff0c\u79ef\u6c34\u5df2\u8d8a\u4e86\u56f4\u5899\u3002 \u3000\u3000\u6c34\u8d8a\u8fc7\u5899\uff0c\u6d41\u4e86\u51fa\u6765\u3002 \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u73b0\u5728\uff0c\u53ef\u4ee5\u6253\u5f00\u5835\u585e\u6805\u95e8\u7684\u4e1c\u897f\u4e86\u3002\u201d \u3000\u3000\u90a3\u6d41\u5165\u5ead\u9662\u4e2d\u7684\u6c34\u6d1e\uff0c\u53ea\u4e0d\u8fc7\u4e09\u5c3a\u5de6\u53f3\u3002 \u3000\u3000\u6805\u95e8\u4e0a\u7684\u5835\u585e\u4e4b\u7269\u4e00\u5f00\uff0c\u4e0d\u8fc7\u987f\u996d\u529f\u592b\uff0c\u79ef\u6c34\u5df2\u5b8c\u5168\u6cc4\u53bb\u4e86\uff0c\u9ec4\u7075\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u54b1\u4eec\u73b0\u5728\u5c31\u53bb\u3002\u201d \u3000\u3000\u7384\u5a18\u5f53\u5148\u51b2\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u7384\u5a18\u5954\u884c\u7684\u901f\u5ea6\u5947\u5feb\uff0c\u9ec4\u7075\u8ffd\u4e0a\u5979\u65f6\uff0c\u5df2\u5230\u4e86\u70df\u9501\u697c\u5916\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7387\u9886\u7fa4\u8c6a\u4e5f\u5df2\u7ecf\u5230\u4e86\u95e8\u53e3\u3002 \u3000\u3000\u9ec4\u7075\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u9ad8\u5929\u5065\uff0c\u6211\u4e5f\u6709\u76f8\u540c\u7684\u5fc3\u613f\uff0c\u54b1\u4eec\u4f55\u4e0d\u653e\u624b\u4e00\u6218\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u8981\u548c\u6211\u5355\u6253\u72ec\u6597?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54b1\u4eec\u653e\u624b\u4e00\u640f\uff0c\u4e0d\u6b7b\u4e0d\u4f11\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u9664\u975e\u54b1\u4eec\u8fd9\u4e00\u6218\u7684\u751f\u6b7b\uff0c\u4ee3\u8868\u53cc\u65b9\u7684\u8d1f\u80dc\u4e4b\u5206\uff0c\u6211\u4e0d\u4f1a\u548c\u4f60\u6253\u4e00\u573a\u72ec\u6597\u4e4b\u7ea6\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u8fd9\u70df\u9501\u697c\u4e0a\uff0c\u6709\u7740\u91cd\u91cd\u7684\u673a\u5173\uff0c\u8bf8\u4f4d\u80fd\u4e0a\u6765\uff0c\u81f3\u5c11\u4f1a\u6709\u76f8\u5f53\u7684\u4f24\u4ea1\uff0c\u90a3\u65f6\u6211\u518d\u5bf9\u4ed8\u4f60\u4eec\u5c82\u4e0d\u4e8b\u534a\u529f\u500d\u3002\u201d \u3000\u3000\u7384\u5a18\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u54e6!\u65e2\u7136\u5982\u6b64\uff0c\u4f55\u4e0d\u6253\u5f00\u697c\u95e8\uff0c\u8ba9\u6211\u4eec\u4e0a\u53bb\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u697c\u95e8\u7d27\u95ed\uff0c\u4e5f\u662f\u673a\u5173\u4e4b\u4e00\uff0c\u8bf8\u4f4d\u6709\u80fd\u529b\uff0c\u81ea\u5df1\u4e0a\u6765\u5427!\u201d \u3000\u3000\u7384\u5a18\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u603b\u4f1a\u4e0a\u53bb\u7684\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u4e07\u80dc\u7a81\u7136\u98de\u8eab\u800c\u8d77\uff0c\u624b\u4e2d\u5927\u780d\u5200\u62a1\u52a8\uff0c\u4f46\u95fb\u9535\u9535\u4e24\u58f0\u91d1\u94c1\u4ea4\u9e23\uff0c\u7a97\u4e0a\u7684\u94c1\u6805\uff0c\u7adf\u88ab\u4e07\u80dc\u5927\u5200\u780d\u65ad\u3002 \u3000\u3000\u4ed6\u7684\u5200\uff0c\u5e76\u4e0d\u662f\u524a\u94c1\u5982\u6ce5\u7684\u5b9d\u5200\uff0c\u4f46\u5200\u91cd\u529b\u731b\uff0c\u7a97\u4e0a\u94c1\u6805\uff0c\u88ab\u8fd9\u5f3a\u731b\u7684\u4e00\u5200\uff0c\u65a9\u65ad\u4e86\u6570\u6761\u3002 \u3000\u3000\u4e07\u80dc\u4e00\u8fde\u4e09\u4e2a\u98de\u8dc3\uff0c\u91d1\u80cc\u5927\u780d\u5200\uff0c\u8fde\u5288\u4e09\u6b21\uff0c\u5df2\u88ab\u4ed6\u65a9\u51fa\u4e86\u4e00\u4e2a\u53ef\u4ee5\u5bb9\u4eba\u51fa\u5165\u7684\u5927\u7f3a\u53e3\u6765\u3002 \u3000\u3000\u4e07\u80dc\u8dc3\u8d77\u65a9\u65ad\u7a97\u4e0a\u94c1\u6805\u7684\u65f6\u5019\uff0c\u5434\u53cc\u5973\u548c\u9ad8\u5e7d\u5170\uff0c\u90fd\u5728\u5168\u795e\u6212\u5907\uff0c\u62c5\u5fc3\u90a3\u94c1\u7a97\u5185\u98de\u51fa\u7684\u7a81\u88ad\u3002 \u3000\u3000\u4f46\u5bf9\u65b9\u7adf\u65e0\u53cd\u5e94\u3002 \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u94c1\u7a97\u5df2\u7834\uff0c\u4f60\u4eec\u53ef\u4ee5\u8fdb\u6765\u4e86\u3002\u201d \u4e0d\u518d\u7406\u4f1a\u9ec4\u7075\u7b49\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54b1\u4eec\u653b\u8fdb\u53bb\u5427\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5728\u4e0b\u5148\u4e0a\u3002\u201d \u3000\u3000\u4e00\u5f2f\u8170\uff0c\u51c6\u5907\u8dc3\u8eab\u800c\u8d77\u3002 \u3000\u3000\u4f46\u5374\u88ab\u9ec4\u7075\u4e00\u4f38\u624b\uff0c\u6309\u4f4f\u4e86\u80a9\u5934\uff0c\u9053\uff1a\u201c\u6162\u7740\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5821\u4e3b\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u8083\u7136\u8bf4\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4e0d\u662f\u4e89\u5148\u6050\u540e\u7684\u65f6\u5019\uff0c\u9ad8\u5929\u5065\u6709\u6043\u65e0\u6050\uff0c\u60f3\u6765\u8fd9\u70df\u9501\u697c\u4e0a\uff0c\u5b9a\u7136\u6709\u7740\u5f88\u5389\u5bb3\u7684\u57cb\u4f0f\uff0c\u54b1\u4eec\u5fc5\u9700\u8981\u6709\u5f88\u4e25\u8083\u7684\u7eaa\u5f8b\uff0c\u8fd9\u4e00\u70b9\uff0c\u5e0c\u671b\u8bf8\u4f4d\u8981\u542c\u547d\u884c\u4e8b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c5e\u4e0b\u4ece\u547d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\uff0c\u54b1\u4eec\u5148\u4e0a\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u8d31\u59be\u9075\u547d\uff0c\u6211\u6253\u5934\u9635\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u522b\u614c\uff0c\u9ad8\u59d1\u5a18\u548c\u5434\u59d1\u5a18\u968f\u540e\uff0c\u4f59\u4e0b\u7684\u4eba\uff0c\u542c\u6211\u4eec\u62db\u547c\u518d\u51b2\u4e0a\u53bb\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u5c5e\u4e0b\u89c9\u5f97\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a \u201c\u4e0d\u7528\u518d\u8bf4\u4e0b\u53bb\u4e86\uff0c\u5c31\u8fd9\u6837\u51b3\u5b9a\u5566!\u7384\u5a18\uff0c\u8ddf\u6211\u8eab\u540e\u3002\u201d \u3000\u3000\u98de\u8eab\u800c\u8d77\uff0c\u7a7f\u7a97\u800c\u5165\u3002 \u3000\u3000\u521a\u5230\u7a97\u53e3\uff0c\u8fce\u5934\u4e00\u7247\u5bd2\u8292\uff0c\u7f69\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u9ec4\u7075\u65e9\u5df2\u6709\u5907\uff0c\u53cc\u624b\u4e00\u5c42\uff0c\u4e24\u628a\u65ad\u5251\u98de\u51fa\uff0c\u62a4\u4f4f\u4e86\u8eab\u5b50\u3002 \u3000\u3000\u539f\u6765\uff0c\u9ec4\u7075\u4e0d\u7528\u5175\u5203\uff0c\u4f46\u4ed6\u5374\u80fd\u7528\u5404\u79cd\u5175\u5203\uff0c\u6280\u827a\u4e0d\u4f46\u535a\u6742\uff0c\u800c\u4e14\u90fd\u5f88\u7cbe\u6e5b\uff0c\u4ed6\u7ec3\u8fc7\u94c1\u526a\u624b\uff0c\u6307\u529b\u7279\u522b\u7684\u5f3a\u52b2\u3002 \u3000\u3000\u8fd9\u4e24\u622a\u65ad\u5251\uff0c\u662f\u4ed6\u5728\u548c\u4eba\u52a8\u624b\u7684\u65f6\u5019\uff0c\u501f\u673a\u8bd5\u9a8c\u94c1\u526a\u624b\uff0c\u626d\u65ad\u4e86\u4e24\u67c4\u65ad\u5251\uff0c\u85cf\u5165\u6000\u4e2d\u3002 \u3000\u3000\u73b0\u5728\uff0c\u9ec4\u7075\u5c31\u7528\u8fd9\u4e24\u67c4\u65ad\u5251\uff0c\u6321\u4f4f\u4e86\u4e24\u67c4\u957f\u5251\u7684\u653b\u51fb\u3002 \u3000\u3000\u9ec4\u7075\u62e8\u5f00\u4e86\u4e24\u652f\u957f\u5251\u4e4b\u540e\uff0c\u53cc\u8155\u4e00\u9707\uff0c\u4e24\u622a\u65ad\u5251\u8131\u624b\u800c\u51fa\u3002 \u3000\u3000\u65ad\u5251\u5982\u6d41\u661f\uff0c\u5f53\u4f5c\u6697\u5668\u4e00\u822c\uff0c\u5c04\u5411\u4e86\u4e24\u4e2a\u6267\u5251\u7684\u9ed1\u8863\u4eba\u3002 \u3000\u3000\u4e24\u4e2a\u9ed1\u8863\u4eba\u6325\u5251\u63a5\u6321\uff0c\u9707\u5f00\u4e86\u9ec4\u7075\u7684\u65ad\u5251\u3002 \u3000\u3000\u4f46\u9ec4\u7075\u5df2\u7ecf\u843d\u4e0b\u4e86\u8eab\u5b50\u3002 \u3000\u3000\u7384\u5a18\u50cf\u98de\u9e1f\u4e00\u6837\uff0c\u7a7f\u7a97\u800c\u5165\u3002 \u3000\u3000\u4eba\u8fd8\u672a\u843d\u5b9e\uff0c\u53cc\u624b\u5df2\u53d1\u51fa\u4e86\u51e0\u7f15\u5bd2\u5149\u3002 \u3000\u3000\u4e24\u4e2a\u9ed1\u8863\u4eba\u4e3e\u5251\u5c01\u6321\uff0c\u4f46\u957f\u5251\u4e3e\u8d77\u65f6\uff0c\u5374\u88ab\u9ec4\u7075\u53cc\u624b\u6293\u4f4f\u3002 \u3000\u3000\u94c1\u526a\u624b\uff0c\u624b\u6307\u6263\u5728\u5251\u4e0a\uff0c\u6709\u5982\u94c1\u94b3\u94b3\u4f4f\u4e00\u822c\u7684\u575a\u7262\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u5446\u4e86\u4e00\u5446\uff0c\u7384\u5a18\u5bd2\u8292\u5df2\u7ecf\u7a7f\u5165\u4e86\u4e24\u4eba\u524d\u80f8\u3002 \u3000\u3000\u662f\u4e24\u679a\u5c0f\u5de7\u7684\u94f6\u68ad\u3002 \u3000\u3000\u94f6\u68ad\u7684\u540e\u9762\uff0c\u5e26\u6709\u4e1d\u7ef3\uff0c\u7cfb\u5728\u7384\u5a18\u7684\u624b\u8155\u4e0a\uff0c\u4e00\u632b\u8155\uff0c\u7adf\u628a\u4e24\u679a\u94f6\u68ad\u6536\u4e86\u56de\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u5c42\u697c\u4e0a\uff0c\u6709\u4e00\u534a\u8f9f\u4f5c\u4e00\u95f4\u5927\u5385\uff0c\u5bf9\u9762\u662f\u4e00\u6392\u9694\u95f4\u7684\u5c0f\u623f\u3002 \u3000\u3000\u655e\u5385\u7684\u4e24\u4fa7\uff0c\u662f\u4e00\u6392\u623f\u95f4\uff0c\u5927\u5385\u4e24\u4fa7\uff0c\u6709\u4e24\u5ea7\u653e\u5175\u5203\u7684\u67b6\u5b50\u3002 \u3000\u3000\u770b\u6765\uff0c\u8fd9\u5ea7\u5927\u5385\u4e0a\uff0c\u662f\u5e73\u5e38\u7ec3\u6b66\u7684\u5730\u65b9\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u548c\u5434\u53cc\u5973\uff0c\u4e5f\u7a7f\u7a97\u800c\u5165\u3002 \u3000\u3000\u9ec4\u7075\u671b\u671b\u5bf9\u9762\u7684\u4e00\u6392\u9694\u7acb\u5c0f\u623f\u95f4\uff0c\u548c\u7d27\u95ed\u7684\u6728\u95e8\uff0c\u8f7b\u8f7b\u53f9\u606f\u2014\u58f0\uff0c\u9053\uff1a\u201c\u9ad8\u5929\u5065\u5230\u5e95\u95f9\u7684\u4ec0\u4e48\u628a\u620f\uff0c\u4ed6\u5df2\u77e5\u9053\u4e86\u54b1\u4eec\u4f1a\u7834\u7a97\u800c\u5165\uff0c\u4e3a\u4ec0\u4e48\u53ea\u6d3e\u4e86\u4e24\u4e2a\u4eba\u5b88\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u95ee\u9898\u5728\u5bf9\u9762\u7684\u623f\u95f4\u4e2d\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u672a\u505c\uff0c\u4e00\u6392\u623f\u95e8\u7a81\u7136\u5927\u5f00\u3002 \u3000\u3000\u516d\u95f4\u623f\u95e8\u53e3\u5904\uff0c\u51fa\u73b0\u4e86\u516d\u4e2a\u4eba\u3002 \u3000\u3000\u4e24\u53ea\u8fde\u73e0\u5323\u5f29\uff0c\u4e24\u7b52\u6885\u82b1\u9488\uff0c\u548c\u4e24\u4e2a\u5706\u5f62\u94c1\u7b52\u3002 \u3000\u3000\u5323\u5f29\u53d1\u52a8\uff0c\u8fde\u73e0\u5f29\u7bad\u75be\u5c04\u800c\u51fa\u3002 \u3000\u3000\u4e24\u7b52\u6885\u82b1\u9488\uff0c\u5c04\u51fa\u4e86\u6570\u5341\u679a\u94f6\u9488\u3002 \u3000\u3000\u4e24\u4e2a\u5706\u7b52\u4e2d\uff0c\u4e00\u7b52\u55b7\u51fa\u4e86\u6bd2\u70df\uff0c\u4e00\u7b52\u55b7\u51fa\u6bd2\u6c34\u3002 \u3000\u3000\u5f29\u7bad\u3001\u6885\u82b1\u9488\u3001\u6bd2\u70df\u3001\u6bd2\u6c34\uff0c\u7b3c\u7f69\u4e86\u6574\u4e2a\u655e\u5385\u7684\u7a7a\u95f4\uff0c\u53ef\u6015\u7684\u6697\u5668\uff0c\u4f73\u5999\u7684\u8c03\u914d\uff0c\u5e03\u6210\u4e86\u4e00\u7247\u6b7b\u4ea1\u9677\u9631\u3002 \u3000\u3000\u9ec4\u7075\u7b49\u4e5f\u60f3\u5230\u4e01\u8fd9\u91cc\u5f88\u51f6\u9669\uff0c\u4f46\u5374\u672a\u60f3\u5230\uff0c\u6784\u6210\u51f6\u9669\u7684\u5185\u5bb9\u662f\u4ec0\u4e48? \u3000\u3000\u5434\u53cc\u5973\u5e94\u53d8\u7684\u624b\u6cd5\u6700\u5feb\uff0c\u5728\u516d\u6247\u623f\u95e8\u6253\u5f00\u65f6\uff0c\u5979\u5df2\u7ecf\u89e3\u5f00\u4e86\u8170\u95f4\u7684\u6c57\u5dfe\uff0c\u540c\u65f6\uff0c\u8131\u4e0b\u4e86\u8eab\u4e0a\u5916\u8863\u3002 \u3000\u3000\u5c31\u5728\u53d1\u51fa\u5f29\u7bad\u3001\u6bd2\u70df\u3001\u6bd2\u6c34\u3001\u6885\u82b1\u9488\u65f6\uff0c\u5434\u53cc\u5973\u5df2\u98de\u63a0\u5230\u9ec4\u7075\u8eab\u524d\uff0c\u8863\u886b\u6325\u52a8\uff0c\u821e\u8d77\u4e86\u4e00\u7247\u8863\u5e55\uff0c\u4e00\u9762\u53eb\u9053\uff1a\u201c\u8bf8\u4f4d\uff0c\u5feb\u8bf7\u9000\u4e0b\uff0c\u8fd9\u4e0d\u662f\u4eba\u529b\u53ef\u4ee5\u6297\u62d2\u7684\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8d70!\u201d \u3000\u3000\u5f53\u5148\u7a7f\u7a97\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u660e\u767d\uff0c\u4ed6\u5982\u4e0d\u8d70\uff0c\u522b\u7684\u4eba\u672a\u5fc5\u80af\u8d70\uff0c \u3000\u3000\u9ad8\u5e7d\u5170\u4e5f\u672a\u901e\u5f3a\uff0c\u4e00\u62c9\u7384\u5a18\uff0c\u7d27\u968f\u7740\u7a7f\u7a97\u800c\u51fa\u3002 \u3000\u3000\u53ea\u7559\u4e0b\u5434\u53cc\u5973\u72ec\u6491\u5371\u5c40\u3002 \u3000\u3000\u5979\u5168\u529b\u65bd\u4e3a\uff0c\u4e00\u4ef6\u4e0a\u8863\uff0c\u5316\u6210\u4e00\u7247\u5e03\u5e55\uff0c\u5f3a\u5927\u7684\u5185\u529b\uff0c\u5e26\u8d77\u4e86\u75be\u52b2\u7684\u6697\u52b2\u3002 \u3000\u3000\u6321\u4f4f\u4e86\u5f29\u7bad\uff0c\u6bd2\u6c34\u3001\u6885\u82b1\u9488\uff0c\u8fde\u55b7\u51fa\u7684\u6bd2\u70df\uff0c\u4e5f\u88ab\u6682\u65f6\u6321\u4f4f\u3002 \u3000\u3000\u9ec4\u7075\u7b49\u843d\u7740\u5b9e\u5730\uff0c\u4f55\u5bd2\u8863\u6025\u6025\u8fce\u4e86\u4e0a\u6765\uff0c\u9053\uff1a\u201c\u5e7d\u5170\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8131\u4e0b\u4f60\u8eab\u4e0a\u8863\u670d\uff0c\u6211\u53bb\u5e2e\u5927\u59ca\u3002\u201d \u3000\u3000\u9ec4\u7075\u4e5f\u5728\u8131\u4e0b\u8863\u886b\uff0c\u51c6\u5907\u53bb\u52a9\u5434\u53cc\u5973\u4e00\u81c2\u4e4b\u529b\u3002 \u3000\u3000\u5e78\u597d\uff0c\u5434\u53cc\u5973\u5f88\u5feb\u7684\u4e5f\u98de\u8dc3\u800c\u4e0b\uff0c\u843d\u5728\u4e86\u5730\u4e0a\u3002 \u3000\u3000\u5979\u5916\u886b\u8131\u53bb\uff0c\u53ea\u7559\u4e00\u4ef6\u8d34\u8eab\u7684\u767d\u7ef8\u77ed\u886b\uff0c\u9690\u9690\u95f4\uff0c\u53ef\u89c1\u8863\u670d\u7684\u7ea2\u8272\u809a\u513f\u3001\u808c\u80a4\u3002 \u3000\u3000\u843d\u7740\u5b9e\u5730\uff0c\u7acb\u65f6\u6296\u5f00\u8863\u670d\uff0c\u51c6\u5907\u7a7f\u4e0a\uff0e\u4f46\u5374\u88ab\u9ad8\u5e7d\u5170\u4e00\u628a\u62a2\u8fc7\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8863\u670d\u4e0a\u5df2\u6cbe\u6bd2\u6c34\uff0c\u4e0d\u80fd\u7a7f\u4e86\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u5df2\u5148\u884c\u9001\u8fc7\u6765\u4e00\u4ef6\u957f\u5916\u886b\u3002 \u3000\u3000\u8fd9\u4f4d\u8001\u6c5f\u6e56\u4e00\u5207\u90fd\u51c6\u5907\u4e86\uff0c\u7adf\u7136\u7a7f\u4e86\u4e24\u4ef6\u957f\u886b\u3002 \u3000\u3000\u7a7f\u4e0a\u8863\u670d\uff0c\u5434\u53cc\u5973\u624d\u6447\u4e86\u4e24\u6447\uff0c\u5411\u5730\u4e0a\u5012\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e00\u628a\u62b1\u4f4f\u4e86\u5979\uff0c\u6025\u6025\u53eb\u9053\uff1a\u201c\u53cc\u5973\uff0c\u600e\u4e48\u5566!\u201d \u3000\u3000\u5434\u53cc\u5973\u53d1\u4e71\u9497\u6a2a\uff0c\u6447\u6447\u5934\uff0c\u82e6\u7b11\u9053\uff1a \u201c\u6211\u5438\u5165\u4e86\u4e00\u70b9\u6bd2\u70df\uff0c\u4e0d\u8981\u7d27\uff0c\u4e00\u4f1a\u5c31\u597d\u4e86\u3002\u201d \u5c1a\u592b\u4eba\u5927\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u9053\uff1a \u201c\u5434\u59d1\u5a18\u5403\u4e0b\u53bb\u9ec4\u5c71\u7684\u5730\u9ec4\u89e3\u6bd2\u4e38\uff0c\u9664\u4e86\u7279\u6b8a\u8c03\u914d\u7684\u6bd2\u836f\u4e4b\u5916\uff0c\u5bf9\u89e3\u6bd2\uff0c\u8fd8\u6709\u4e00\u70b9\u529f\u6548\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u8fc7\u836f\u4e38\uff0c\u541e\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a \u201c\u591a\u8c22\u592b\u4eba\uff0c\u5bd2\u8863\uff0c\u6276\u6211\u5230\u4e00\u5904\u53ef\u907f\u5f29\u7bad\u7684\u5730\u65b9\u53bb\uff0c\u8ba9\u6211\u5750\u606f\u4e00\u4e0b\u3002\u201d \u3000\u3000\u4e94\u697c\u7a97\u53e3\u5927\u5f00\uff0c\u53c8\u4f38\u51fa\u4e86\u9ad8\u5929\u5065\u7684\u8111\u888b\uff0c\u540c\u65f6\uff0c\u4e5f\u54cd\u8d77\u4e86\u9ad8\u5929\u5065\u7684\u5927\u7b11\u4e4b\u58f0\uff0c\u9053\uff1a \u3000\u3000\u201c\u9ec4\u7075\uff0c\u70df\u9501\u697c\u5e03\u7f6e\u4e00\u756a\uff0c\u6211\u76f8\u4fe1\uff0c\u8bf4\u5b83\u662f\u94dc\u5899\u94c1\u58c1\u4e5f\u4e0d\u4e3a\u8fc7\u3002\u201d \u3000\u3000\u9ec4\u7075\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u672c\u6765\u6211\u4eec\u8fd8\u4e0d\u60f3\u65bd\u4e0b\u6bd2\u624b\uff0c\u73b0\u5728\u53ea\u6709\u653e\u624b\u65bd\u7528\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u5f15\u6c34\u51b2\u6df9\uff0c\u624b\u6bb5\u5df2\u65e0\u6240\u4e0d\u7528\u5176\u6781\uff0c\u8fd8\u6709\u4ec0\u4e48\u624b\u6bb5\u53ef\u7528\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u706b\u70e7\uff0c\u9ad8\u5929\u5065\uff0c\u6211\u51c6\u5907\u548c\u4f60\u5c31\u8fd9\u6837\u76f8\u6301\u4e0b\u53bb\uff0c\u96f7\u65e0\u5f71\u4e0d\u4f1a\u5e2e\u6211\uff0c\u4f46\u4e5f\u4e0d\u4f1a\u5e2e\u4f60\uff0c\u6211\u4f1a\u7528\u706b\u653b\uff0c\u6162\u6162\u7684\u70e7\u4e86\u4f60\u8fd9\u5ea7\u70df\u9501\u697c\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u9752\u7816\u5ca9\u77f3\uff0c\u5c31\u7b97\u4f60\u7528\u706b\u653b\uff0c\u4e5f\u672a\u5fc5\u5c31\u80fd\u70e7\u5f97\u4e86\u8fd9\u5ea7\u697c\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6ca1\u6709\u4efb\u4f55\u6b66\u529f\uff0c\u80fd\u591f\u957f\u671f\u6297\u62d2\u706b\u70e7\uff0c\u6211\u4f1a\u5728\u4f60\u8fd9\u697c\u4e0b\u56db\u5e03\u6ee1\u6728\u67f4\uff0c\u70e7\u4f60\u4e2a\u4e09\u5929\u4e09\u591c\uff0c\u522b\u8bf4\u8fd9\u70df\u9501\u697c\u53ea\u662f\u7816\u77f3\u5efa\u6210\uff0c\u5c31\u7b97\u662f\u94c1\u6253\u94dc\u6d47\uff0c\u6211\u4e5f\u8981\u628a\u5b83\u70e7\u6210\u6eb6\u6c34\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4eca\u5929\u5165\u591c\u4e4b\u540e\uff0c\u4f60\u5c31\u4f1a\u611f\u89c9\u5230\u4f60\u81ea\u5df1\u5904\u5883\u662f\u591a\u4e48\u7684\u5371\u9669\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u8fd9\u662f\u5a01\u5413\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u9ad8\u5929\u5065\u5982\u53ea\u662f\u7b2c\u4e00\u5821\u8fd9\u70b9\u5b9e\u529b\uff0c\u4e5f\u4e0d\u4f1a\u541b\u4e34\u6c5f\u6e56\u4e09\u5341\u5e74\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bf9\u4ed8\u4f60\u7684\uff0c\u4e5f\u4e0d\u53ea\u662f\u6211\u9ec4\u7075\u8fd9\u4e00\u6279\u4eba\u624b\uff0c\u60f3\u7b49\u63e1\u624b\u8d76\u6765\uff0c\u53ea\u6015\u4f60\u4f1a\u5b8c\u5168\u5931\u671b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u4e2a\uff0c\u8001\u592b\u65e9\u5df2\u77e5\u9053\u4e86\uff0c\u4e0d\u8fc7\uff0c\u8001\u592b\u6709\u8001\u592b\u7684\u6253\u7b97\uff0c\u8001\u592b\u6709\u8001\u592b\u7684\u8ba1\u5212\uff0c\u9ec4\u7075\uff0c\u81f3\u5c11\u76ee\u524d\u54b1\u4eec\u8fd8\u662f\u4e2a\u80dc\u8d1f\u672a\u5206\u7684\u5c40\u9762\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u9ad8\u5821\u4e3b\u65e9\u5df2\u7ecf\u80f8\u6709\u6210\u7af9\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u9ad8\u67d0\u4eba\u7eb5\u6a2a\u6c5f\u6e56\u4e09\u5341\u5e74\uff0c\u535a\u5f97\u5929\u4e0b\u82f1\u96c4\u4e00\u540c\u94a6\u656c\uff0c\u5c82\u662f\u90a3\u4e48\u5bb9\u6613\u5bf9\u4ed8\u7684\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5509!\u9ad8\u5929\u5065\uff0c \u4f60\u662f\u7a77\u9014\u672b\u8def\uff0c\u6211\u60f3\u4e0d\u51fa\u4f60\u8fd8\u6709\u4ec0\u4e48\u4eba\u53ef\u4ee5\u6551\u4f60\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u7b49\u7740\u770b\u5427\u3002\u201d \u3000\u3000\u9ec4\u7075\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u9ad8\u5929\u5065\uff0c\u4e0d\u8bba\u4f60\u662f\u5426\u771f\u6709\u63f4\u624b\uff0c\u4f46\u4f60\u5df2\u8eab\u9677\u5371\u5883\uff0c\u7eb5\u6709\u63f4\u624b\uff0c\u4e5f\u96be\u633d\u6551\u4f60\u7684\u5782\u5371\u547d\u8fd0\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u5f88\u5feb\u54b1\u4eec\u5c31\u4f1a\u8bc1\u660e\u4e86\u3002\u201d \u3000\u3000\u7830\u7136\u4e00\u58f0\uff0c\u5173\u4e0a\u7a97\u95e8\u3002 \u3000\u3000\u9ec4\u7075\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\uff0c\u770b\u6765\u662f\u5f88\u96be\u9192\u609f\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4ed6\u4e0d\u662f\u5a01\u80c1\uff0c\u4e5f\u4e0d\u662f\u4fe1\u53e3\u5f00\u6cb3\uff0c \u4ed6\u8bf4\u7684\u5f88\u771f\u5b9e\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u8fd9\u8bf4\u6765\uff0c\u9ad8\u5929\u5065\u771f\u8fd8\u6709\u4e00\u6279\u4eba\u624b\uff0c\u57cb\u4f0f\u5728\u8fd9\u4ed9\u5973\u95e8\u4e2d\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u6211\u4e86\u89e3\u9ad8\u5929\u5065\u8fd9\u4e2a\u4eba\uff0e\u5fc3\u4e2d\u5982\u82e5\u4e0d\u5f88\u8e0f\u5b9e\uff0c\u4ed6\u4f1a\u65e9\u548c\u4f60\u8c08\u548c\u89e3\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u60f3\uff0c\u6211\u4eec\u4e5f\u5e94\u8be5\u884c\u52a8\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5982\u4f55\u4e00\u4e2a\u884c\u52a8?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5806\u79ef\u5e72\u67f4\uff0c\u81f3\u5c11\u6446\u51fa\u4e00\u526f\u653e\u706b\u70e7\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u7528\u6446\u6837\u5b50\uff0c\u54b1\u4eec\u5c31\u771f\u771f\u6b63\u6b63\u7684\u51c6\u5907\u70e7\u5427!\u201d \u3000\u3000\u5341\u4e8c\u5973\u5c06\u4e2d\uff0c\u9664\u4e86\u7ea2\u82b1\u3001\u7eff\u53f6\u4e4b\u5916\uff0c\u8fd8\u6709\u56db\u4e2a\u53d7\u4f24\u7684\uff0c\u9664\u4e00\u4e2a\u4f24\u52bf\u5f88\u91cd\u4e4b\u5916\uff0c\u4e09\u4e2a\u4eba\uff0c\u5df2\u7ecf\u5927\u81f4\u590d\u5143\uff0c\u4e07\u80dc\u5e26\u6765\u7684\u6b66\u58eb\u3002\u8fdb\u5165\u7b2c\u4e00\u5821\u4e2d\u7684\u4eba\uff0c \u5df2\u6b7b\u4ea1\u6b86\u5c3d\uff0c\u9664\u6b64\u4e4b\u5916\uff0c \u4e07\u80dc\u5374\u5e03\u4e0b\u4e86\u2014\u652f\u4f0f\u5175\uff0c\u5341\u4e94\u4e2a\u5200\u624b\uff0c\u9690\u85cf\u5728\u7b2c\u4e00\u5821\u5185\u3002 \u3000\u3000\u8fd9\u4e00\u6279\u4eba\u624b\uff0c\u672c\u662f\u4e07\u80dc\u51c6\u5907\u4e0d\u5e78\u843d\u8d25\u65f6\uff0c\u4f5c\u4e3a\u9000\u51fa\u7b2c\u2014\u5821\u7684\u6551\u5e94\u4e4b\u7528\uff0c\u73b0\u5728\uff0c\u4e5f\u628a\u5b83\u8c03\u51fa\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6279\u65b0\u9510\u51fa\u73b0\uff0c\u4e0d\u4f46\u589e\u52a0\u4e86\u5de5\u4f5c\u4e0a\u7684\u6548\u7387\uff0c \u800c\u4e14\uff0c\u5bf9\u9ad8\u5929\u5065\u5fc3\u7406\u4e0a\u7684\u5a01\u80c1\u5f88\u5927\u3002 \u53f6\u957f\u9752\u3001\u4f55\u5bd2\u8863\uff0c\u4eb2\u81ea\u5e26\u7740\u5341\u4e94\u4e2a\u6b66\u58eb\uff0c\u52a8\u624b\u5806\u79ef\u6728\u85aa\u5728\u70df\u9501\u697c\u5916\u3002 \u3000\u3000\u9ec4\u7075\u3001\u9ad8\u5e7d\u5170\uff0c\u5374\u501f\u673a\u5728\u697c\u5916\u8c03\u606f\uff0c\u4e00\u9762\u9632\u6b62\u9ad8\u5929\u5065\u5b9e\u65bd\u88ad\u51fb\u3002 \u3000\u3000\u795e\u5200\u5802\u7684\u96f7\u65e0\u5f71\uff0c\u4e00\u76f4\u6ca1\u6709\u52a8\u9759\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5fc3\u4e2d\u60f3\u7684\u4ec0\u4e48\u4e1c\u897f\u3002 \u3000\u3000\u5929\u8272\u5165\u591c\u65f6\u5149\uff0c\u5df2\u7ecf\u5728\u70df\u9501\u697c\u5916\uff0c\u5806\u79ef\u767e\u62c5\u6728\u67f4\u3002 \u3000\u3000\u9ad8\u5929\u5065\u6ca1\u6709\u518d\u51fa\u73b0\u8fc7\u70df\u9501\u697c\u4e0a\u3002 \u3000\u3000\u795e\u5200\u5802\u7684\u96f7\u65e0\u5f71\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u53cd\u5e94\u3002 \u3000\u3000\u4ed6\u597d\u8c61\u771f\u7684\u5df2\u7f6e\u8eab\u4e8b\u5916\uff0c\u628a\u6240\u6709\u7684\u4eba\u624b\u963b\u6b62\u4e8e\u795e\u5200\u5802\u5185\uff0c\u4e0d\u8ba9\u4ed6\u4eec\u51fa\u73b0\u5728\u795e\u5200\u5802\u5916\u3002 \u3000\u3000\u591c\u5e55\u4f4e\u5782\u65f6\u5206\uff0c\u9ec4\u7075\u7acb\u523b\u4e0b\u4ee4\uff0c\u53ec\u56de\u5341\u4e94\u4e2a\u5200\u624b\u548c\u7ea2\u82b1\u3001\u7eff\u53f6\uff0c\u8981\u4ed6\u4eec\u5e03\u5b88\u5728\u70df\u9501\u697c\u5916\uff0c\u4e00\u9762\u8fd0\u6c14\u8c03\u606f\uff0c\u4e00\u9762\u6446\u6210\u4e00\u4e2a\u62d2\u654c\u65b9\u9635\uff0c\u56db\u9762\u8b66\u6212\u3002 \u3000\u3000\u9ad8\u5929\u5065\u62db\u8bf7\u7684\u4eba\u624b\uff0c\u8fd8\u672a\u73b0\u8eab\uff0c\u4e5f\u8bb8\u6839\u672c\u6ca1\u6709\u8fd9\u4e00\u6279\u4eba\u624b\u3002 \u3000\u3000\u4f46\u9ec4\u7075\u4e0d\u80fd\u4e0d\u9632\u3002 \u3000\u3000\u8fd9\u4e00\u6279\u4eba\u624b\uff0c\u5982\u82e5\u771f\u7684\u7a81\u7136\u51fa\u73b0\uff0c\u501f\u591c\u8272\u63a9\u62a4\uff0c\u6697\u4e2d\u65bd\u88ad\uff0c\u5f88\u53ef\u80fd\u4f1a\u9020\u6210\u9ec4\u7075\u7b49\u5f88\u5927\u7684\u4f24\u4ea1\u3002 \u3000\u3000\u5c3d\u7ba1\u9ec4\u7075\u5fc3\u4e2d\u7126\u6025\uff0c\u4f46\u4ed6\u8868\u9762\u4e0a\uff0c\u4e0d\u5f97\u4e0d\u88c5\u51fa\u9547\u9759\u3002 \u3000\u3000\u53cc\u65b9\u5f62\u6210\u4e86\u4e00\u4e2a\u5e7d\u9759\u7684\u5bf9\u5cd9\u5c40\u9762\u3002\u5929\u5230\u521d\u66f4\u65f6\u5206\u3002 \u3000\u3000\u65b0\u6708\u5982\u94a9\uff0c\u5347\u4e0a\u67f3\u68a2\u5934\u3002 \u3000\u3000\u7b2c\u4e00\u5821\u5df2\u4e0d\u590d\u5f53\u5e74\u6c14\u52bf\uff0c\u5904\u5904\u706f\u706b\uff0c\u5de1\u66f4\u4e0d\u7edd\u3002 \u3000\u3000\u73b0\u5728\u4e00\u7247\u6c89\u5bc2\u3002 \u3000\u3000\u6574\u5ea7\u7b2c\u4e00\u5821\u4e0d\u89c1\u706f\u706b\u3002 \u3000\u3000\u70df\u9501\u697c\u4e0a\uff0c\u4e5f\u4e0d\u89c1\u706f\u706b\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7ec8\u4e8e\u6c89\u4e0d\u4f4f\u6c14\u4e86\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u4f60\u51c6\u5907\u548c\u6211\u5bf9\u5cd9\u4e0b\u53bb\u4e86\u3002\u201d \u9ec4\u7075\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u662f\u554a!\u53cd\u6b63\uff0c\u6211\u73b0\u5728\u4e5f\u4e0d\u592a\u6025\uff0c\u6162\u6162\u7684\u71ac\u4e0b\u53bb\u5427!\u6211\u4eec\u5df2\u6709\u65b0\u7684\u63f4\u624b\u8d76\u5230\uff0c\u5927\u5bb6\u6491\u4e0b\u53bb\u5427\uff0c\u770b\u770b\u7ed3\u679c\u5982\u4f55?\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u90a3\u4e9b\u4eba\uff0c\u6211\u90fd\u770b\u5230\u4e86\uff0c\u4ed6\u4eec\u53ea\u662f\u9634\u9633\u5821\u4e2d\u7684\u6b66\u58eb\uff0c\u7edd\u5bf9\u4e0d\u662f\u5404\u95e8\u6d3e\u4e2d\u7684\u9ad8\u624b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4e0d\u8bba\u4ed6\u4eec\u662f\u4ec0\u4e48\u4eba\uff0c\u4f46\u4ed6\u4eec\u662f\u6211\u4eec\u7684\u63f4\u624b\u4e0d\u9519\uff0c\u4f60\u9ad8\u5821\u4e3b\u7684\u63f4\u624b\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u4ed6\u4eec\u4f1a\u5230\u7684\uff0c\u4e0d\u4f1a\u8d85\u8fc7\u4e09\u66f4\u65f6\u5206\uff0c\u7b49\u4ed6\u4eec\u5230\u4e86\u4e4b\u540e\uff0c\u4f60\u4eec\u4f1a\u53d7\u5230\u4e24\u9762\u5939\u653b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5e0c\u671b\u4ed6\u4eec\u4f1a\u65e9\u4e9b\u51fa\u73b0\uff0c\u5728\u4e0b\u5df2\u7ecf\u5b89\u6392\u597d\u4e86\u6297\u62d2\u4ed6\u4eec\u7684\u51c6\u5907\uff0c\u4e5f\u60f3\u5230\u4e86\u70df\u9501\u697c\u4e2d\u4f1a\u63a5\u5e94\u4ed6\u4eec\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u5ffd\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u4e0d\u8fc7\u6c5f\u6e56\u4e0a\u4e00\u4e2a\u6d6a\u5b50\uff0c\u4e0d\u8bba\u4f60\u7684\u58f0\u8a89\u3001\u5730\u4f4d\uff0c\u90fd\u4e0d\u5f88\u597d\uff0c\u4e5f\u4e0d\u88ab\u6c5f\u6e56\u4e0a\u5c0a\u4ef0\uff0c\u4f60\u4e3a\u4ec0\u4e48\u5374\u5bf9\u6211\u82e6\u82e6\u76f8\u903c\uff0c\u4f60\u7a76\u7adf\u5728\u8ffd\u6c42\u4ec0\u4e48?\u53c8\u60f3\u5f97\u5230\u4ec0\u4e48?\u201d \u3000\u3000\u53cc\u65b9\u9762\u4e92\u4e0d\u76f8\u89c1\uff0c\u70df\u9501\u697c\u7684\u7a97\u95e8\uff0c\u4e5f\u6ca1\u6709\u6253\u5f00\u3002 \u3000\u3000\u4f46\u53cc\u65b9\u9762\u7684\u5bf9\u7b54\u4e4b\u8a00\uff0c\u5f7c\u6b64\u95f4\u90e8\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u9ec4\u7075\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u6211\u53ea\u662f\u4e00\u4e2a\u6d6a\u5b50\uff0c \u4f46\u6d6a\u5b50\u56de\u5934\u91d1\u4e0d\u6362\uff0c\u6211\u4e0d\u8d2a\u56fe\u4ec0\u4e48\u597d\u5904\uff0c\u4e5f\u4e0d\u8981\u6c42\u522b\u4eba\u5c0a\u656c\uff0c\u6211\u53ea\u662f\u505a\u6211\u89c9\u7740\u5e94\u8be5\u505a\u7684\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u6211\u4e0d\u660e\u767d\uff0c\u9ec4\u7075\uff0c \u4e00\u4e2a\u4eba\u505a\u4e8b\uff0c\u90fd\u5e94\u8be5\u6709\u4ed6\u7684\u76ee\u7684\uff0c\u4f60\u7684\u76ee\u7684\u53c8\u662f\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u6ca1\u6709\u76ee\u7684\uff0c\u6211\u53ea\u662f\u60f3\u4e3a\u6c5f\u6e56\u505a\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u7b80\u76f4\u662f\u6709\u4e9b\u51a5\u987d\u4e0d\u5316\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\u8a00\u91cd\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u6211\u6709\u65e0\u6570\u7684\u9ec4\u91d1\uff0c\u6211\u638c\u63e1\u4e86\u5929\u4e0b\u5f88\u591a\u7684\u67a2\u7ebd\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u548c\u6211\u5408\u4f5c\uff0c\u5171\u56fe\u5929\u4e0b\u9738\u6743\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u4eec\u4e4b\u95f4\u7684\u8ddd\u79bb\u5c31\u5728\u6b64\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u73b0\u5728\uff0c\u4f60\u8fd8\u6709\u673a\u4f1a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ec0\u4e48\u673a\u4f1a?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u8001\u592b\u613f\u548c\u4f60\u5e73\u5206\u5929\u4e0b\uff0c\u5e73\u5206\u8d22\u5bcc\uff0c\u5e73\u5206\u8001\u592b\u62e5\u6709\u7684\u4e00\u5207\u2026\u2026\u201d \u9ec4\u7075\u5927\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u600e\u7684\u4f1a\u5982\u6b64\u5927\u65b9\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u8001\u592b\u53d1\u89c9\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u54b1\u4eec\u4e24\u4e2a\u4eba\u82e5\u80fd\u5408\u4f5c\uff0c\u6211\u60f3\uff0c\u653e\u773c\u5929\u4e0b\uff0c\u5c31\u518d\u65e0\u6211\u4eec\u7684\u654c\u624b\u4e86\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u4e8b\u5230\u5982\u4eca\uff0c\u4f60\u7adf\u8fd8\u6267\u8ff7\u4e0d\u609f\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u54cd\u8d77\u4e86\u5434\u53cc\u5973\u7684\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u9ad8\u5929\u5065\u63a5\u9053\uff1a \u201c\u4ed6\u4eec\u51fa\u73b0\u4e86\uff0c\u73b0\u5728\uff0c\u4f60\u5982\u6539\u53d8\u4e3b\u610f\uff0c\u8fd8\u6765\u5f97\u53ca\u3002\u201d \u867d\u7136\u6708\u8272\u660e\u6717\uff0c\u4f46\u603b\u4e0d\u5982\u767d\u5929\u770b\u5f97\u6e05\u695a\u3002 \u3000\u3000\u9ec4\u7075\u4e0d\u613f\u5c5e\u4e0b\u53d7\u5230\u635f\u5931\uff0c\u6240\u4ee5\uff0c\u4ed6\u628a\u4eba\u624b\uff0c\u96c6\u4e2d\u5728\u4e00\u5904\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u4e00\u7fa4\u9ed1\u5f71\uff0c\u7f13\u7f13\u5411\u70df\u9501\u697c\u524d\u96c6\u4e2d\u8fc7\u6765\u3002 \u3000\u3000\u6765\u4eba\u5206\u4f5c\u4e24\u8def\uff0c\u770b\u4e0a\u53bb\uff0c\u6bcf\u4e00\u8def\uff0c\u5927\u7ea6\u6709\u4e03\u516b\u4e2a\u4eba\u3002 \u3000\u3000\u4e24\u8def\u52a0\u8d77\u6765\uff0c\u7adf\u6709\u5341\u4e94\u516d\u4eba\u4e4b\u591a\u3002 \u3000\u3000\u5982\u6b64\u5e9e\u5927\u7684\u4e00\u6279\u4eba\u624b\uff0c\u4f7f\u5f97\u9ec4\u7075\u4e5f\u6709\u4e9b\u5fc3\u5934\u9707\u52a8\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u5feb\u4e9b\u4e0b\u4ee4\uff0c\u628a\u4eba\u624b\u96c6\u4e2d\u8d77\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u64a4\u56de\u5404\u5904\u4eba\u624b\uff0c\u54b1\u4eec\u5c31\u65e0\u6cd5\u770b\u5230\u70df\u9501\u697c\u4e0a\u7684\u666f\u7269\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u9ad8\u5929\u5065\u8981\u5b88\u5728\u70df\u9501\u697c\u4e0a\uff0c\u6bd4\u4ed6\u79bb\u5f00\u70df\u9501\u697c\u8fd8\u8981\u6709\u5229\u4e00\u4e9b\u3002\u201d \u3000\u3000\u9ec4\u7075\u5bf9\u9ad8\u5e7d\u5170\u7684\u610f\u89c1\uff0c\u5341\u5206\u91cd\u89c6\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u7acb\u523b\u53ec\u96c6\u4ed6\u4eec\u8fc7\u6765\u3002\u201d \u3000\u3000\u5341\u4e94\u4e2a\u7cbe\u9510\u7684\u5200\u624b\uff0c\u52a0\u4e0a\u7ea2\u82b1\u3001\u7eff\u53f6\u548c\u4e09\u4e2a\u5973\u5c06\uff0c\u4f7f\u5f97\u4e00\u5ea7\u4e94\u65b9\u9635\uff0c\u66f4\u663e\u5f97\u4e25\u5bc6\u3002 \u3000\u3000\u539f\u6765\u5341\u4e94\u4e2a\u5200\u624b\u7684\u4e03\u4e2a\uff0c\u88ab\u6d3e\u5230\u70df\u9501\u697c\u4e09\u4e2a\u4e0d\u540c\u7684\u65b9\u5411\uff0c\u76d1\u89c6\u7740\u70df\u9501\u697c\u3002 \u3000\u3000\u73b0\u5728\uff0c\u9ec4\u7075\u628a\u4ed6\u4eec\u53ec\u96c6\u4e8e\u4e00\u5904\u3002 \u3000\u3000\u84dd\u5929\u5982\u6d17\uff0c\u6708\u5149\u660e\u5a9a\u3002 \u3000\u3000\u4e24\u9762\u884c\u6765\u7684\u4eba\uff0c\u7ec8\u4e8e\u63a5\u8fd1\u4e86\u65b9\u9635\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u4f4f\u5728\u517b\u6b8b\u9662\u4e2d\u7684\u4eba\u3002\u201d \u3000\u3000\u4e24\u961f\u7684\u4eba\uff0c\u90fd\u7a7f\u7740\u7070\u8272\u7684\u8863\u670d\uff0c \u624b\u4e2d\u7684\u957f\u5251\uff0c\u5728\u524d\u6bb5\u7684\u5251\u8eab\u4e0a\uff0c\u6709\u4e86\u4e00\u4e2a\u5f88\u5927\u7684\u7f3a\u53e3\u3002 \u9aa4\u770b\u4e0a\u53bb\uff0c\u90a3\u662f\u4e00\u628a\u6b8b\u7f3a\u7684\u5251\u3002 \u3000\u3000\u4f46\u6bcf\u4e00\u4e2a\u4eba\u7684\u5251\u4e0a\uff0c\u90fd\u6709\u7740\u6b8b\u7f3a\u7684\u88c2\u53e3\uff0c\u8fd9\u5c31\u8bc1\u660e\u4e86\uff0c\u6b8b\u7f3a\u5e76\u975e\u662f\u56e0\u4e3a\u78b0\u649e\u3002 \u3000\u3000\u8fd9\u662f\u4eba\u4e3a\u7684\u6b8b\u7f3a\uff0c\u4e14\u94f8\u9020\u8fd9\u628a\u5251\u65f6\uff0c\u5c31\u7559\u4e0b\u4e86\u8fd9\u4e48\u4e00\u4e2a\u7f3a\u53e3\u3002 \u3000\u3000\u4e0d\u77e5\u9053\u8fd9\u7f3a\u53e3\u7684\u7528\u610f\u4f55\u5728\uff0c\u4f46\u5b83\u5fc5\u6709\u4f5c\u7528\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u8fd9\u628a\u5251\uff0c\u6545\u610f\u7559\u4e0b\u4e86\u8fd9\u4e48\u4e00\u4e2a\u7f3a\u53e3\uff0c\u5fc5\u6709\u5b83\u7684\u4f5c\u7528\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u6447\u6447\u5934\u3002 \u3000\u3000\u5979\u5f88\u806a\u660e\uff0c\u4e0d\u660e\u767d\u7684\u4e8b\uff0c\u7edd\u5bf9\u4e0d\u80e1\u4e71\u53d1\u8a00\uff1a \u3000\u3000\u5c1a\u5929\u4e49\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u542c\u8bf4\uff0c\u8ddd\u4eca\u767e\u5e74\u4e4b\u524d\uff0c\u6c5f\u6e56\u4e0a\u6709\u4e00\u4e2a\u95e8\u6d3e\uff0c\u7528\u7684\u6b8b\u7f3a\u5251\u6cd5\uff0c\u80fd\u5728\u640f\u6740\u4e2d\uff0c\u5e26\u8d77\u5c16\u5389\u54e8\u58f0\uff0c\u4f7f\u4eba\u5fc3\u795e\u53d7\u5236\uff0c\u4f46\u8fd9\u4e2a\u5947\u602a\u7684\u5251\u6d3e\uff0c\u6709\u5982\u6619\u82b1\u4e00\u73b0\u822c\uff0c\u5f88\u5feb\u53c8\u5728\u6c5f\u6e56\u4e0a\u6d88\u5931\u4e86\uff0c\u8001\u592b\u8fd9\u628a\u5e74\u7eaa\uff0c\u4e5f\u6ca1\u6709\u89c1\u8fd9\u4e2a\u5251\u6d3e\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u8001\u524d\u8f88\uff0c\u8fd9\u79cd\u5251\u6cd5\uff0c\u5982\u65e9\u5df2\u5728\u6c5f\u6e56\u4e2d\u6d88\u5931\u4e86\uff0c\u4eca\u591c\uff0c\u53c8\u600e\u4f1a\u5728\u7b2c\u4e00\u5821\u4e2d\u51fa\u73b0\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u8001\u592b\u4e0d\u89e3\u7684\u5730\u65b9\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u597d!\u4e07\u80dc\u5144\uff0c\u4f20\u8bdd\u4e0b\u53bb\uff0c\u5bf9\u65b9\u7684\u5251\u4e0a\u7f3a\u53e3\uff0c\u53ef\u80fd\u4f1a\u5e26\u8d77\u4e86\u5578\u98ce\u4e4b\u98ce\uff0c\u4e0d\u8981\u4e3a\u5bf9\u65b9\u6240\u60d1\uff0c\u5f71\u54cd\u4e86\u6218\u5fd7\uff0c\u4e71\u4e86\u5fc3\u795e\u3002\u201d \u3000\u3000\u4e07\u80dc\u9053\uff1a \u201c\u5c1a\u8001\u524d\u8f88\uff0c\u6b8b\u7f3a\u5251\u6cd5\uff0c\u9664\u4e86\u4e71\u4eba\u5fc3\u795e\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u6015\u4e4b\u5904\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u6211\u4e0d\u6e05\u695a\u8fd9\u5957\u5251\u6cd5\uff0c\u4f46\u5b83\u5374\u5f88\u6709\u5a01\u529b\u3002\u201d \u3000\u3000\u4e07\u80dc\u70b9\u70b9\u5934\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a \u201c\u4f60\u4eec\u5982\u82e5\u81ea\u77e5\u65e0\u6cd5\u6297\u62d2\u5bf9\u65b9\u7684\u5251\u6cd5\u65f6\uff0c\u90a3\u5c31\u8981\u7279\u522b\u5c0f\u5fc3\u4e86\uff0c\u4e0d\u6c42\u4f24\u654c\uff0c\u5148\u4ee5\u81ea\u4fdd\u4e3a\u4e3b\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u5200\u624b\uff0c\u90fd\u662f\u4ed6\u8f9b\u82e6\u8bad\u7ec3\u51fa\u6765\u7684\u4eba\u624b\uff0c \u5df2\u7ecf\u6709\u4e86\u5927\u534a\u7684\u4f24\u4ea1\u3002 \u8fd9\u5341\u4e94\u4e2a\u4eba\uff0c\u662f\u4ed6\u8bad\u7ec3\u7684\u4eba\u624b\u4e2d\uff0c\u6700\u540e\u7684\u4e00\u6279\u4e86\u3002 \u3000\u3000\u4e07\u80dc\u5bf9\u4ed6\u4eec\u7684\u727a\u7272\uff0c\u5b9e\u5728\u6709\u4e9b\u5fc3\u75bc\u3002 \u3000\u3000\u8a00\u8bed\u95f4\uff0c\u6d41\u9732\u51fa\u65e0\u6bd4\u7684\u5173\u5fc3\u3002 \u3000\u3000\u5c1a\u5929\u4e49\u7a81\u7136\u8bf4\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u65e2\u662f\u53cc\u65b9\u975e\u8981\u6709\u4e00\u573a\u640f\u6740\u4e0d\u53ef\uff0c\u4e3a\u4ec0\u4e48\u5b9a\u8981\u4ed6\u4eec\u5148\u51fa\u624b\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d\uff0c\u90a3\u8fd9\u4e00\u9635\u5c31\u7531\u4f55\u5144\u548c\u957f\u9752\u51fa\u9a6c\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u6211\u4eec\u5148\u5bf9\u4ed8\u897f\u65b9\u4eba\u624b\uff0c\u8bf8\u4f4d\u7559\u5fc3\u4e1c\u9762\u7684\u653b\u8fc7\u6765\u3002\u201d \u4e3e\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7d27\u968f\u5728\u53f6\u957f\u9752\u7684\u8eab\u540e\u3002 \u3000\u3000\u7531\u897f\u65b9\u884c\u6765\u7684\u7070\u8863\u4eba\uff0c\u5171\u6709\u516b\u4e2a\uff0c\u516b\u4e2a\u4eba\uff0c\u5206\u6210\u4e86\u4e24\u6392\uff0c\u524d\u4e09\u540e\u4e94\u3002 \u3000\u3000\u53f6\u957f\u9752\u6253\u91cf\u8fc7\u4ed6\u4eec\u624b\u4e2d\u7684\u5251\uff0c\u6709\u56db\u5c3a\u4e8c\u5bf8\u4ee5\u4e0a\uff0c\u6bd4\u8d77\u4e00\u822c\u7684\u5b9d\u5251\uff0c\u8981\u957f\u51fa\u516d\u4e03\u5bf8\u3002 \u3000\u3000\u90a3\u7f3a\u53e3\uff0c\u5c31\u5728\u5251\u5c16\u5904\u4e24\u5bf8\u5de6\u53f3\u3002 \u3000\u3000\u53f6\u957f\u9752\u884c\u8fd1\u4e86\u654c\u4eba\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u5148\u51fa\u624b\uff0c\u4f60\u5728\u540e\u9762\u63a5\u5e94\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u770b\u6837\u5b50\uff0c\u4ed6\u4eec\u5e03\u7f6e\u5f97\u5f88\u50cf\u4e00\u5305\u56f4\u7684\u961f\u5f62\uff0c\u5e0c\u671b\u4f60\u8981\u5c0f\u5fc3\u4e00\u4e9b\u522b\u8ba9\u4ed6\u4eec\u56f4\u8fc7\u6765\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u7a81\u7136\u4e00\u5251\u523a\u5411\u524d\u6392\u5c45\u4e2d\u4e4b\u4eba\u7684\u524d\u80f8\u3002 \u3000\u3000\u90a3\u4eba\u957f\u5251\u4e00\u626c\uff0c\u7adf\u7136\u662f\u786c\u6253\u786c\u63a5\u7684\u62db\u672f\uff0c\u786c\u5c01\u53f6\u957f\u9752\u7684\u5251\u52bf\u3002 \u3000\u3000\u540c\u65f6\uff0c\u5de6\u53f3\u4e24\u4fa7\u7684\u5251\u624b\uff0c\u6709\u5982\u5e38\u5c71\u4e4b\u86c7\uff0c\u5ffd\u7136\u95f4\u5377\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e24\u652f\u6b8b\u7f3a\u957f\u5251\uff0c\u76f4\u523a\u8fc7\u6765\u3002 \u3000\u3000\u5251\u6cd5\u4e0d\u89c1\u7cbe\u5999\uff0c\u4f46\u4ed6\u4eec\u52a8\u4f5c\u914d\u5408\u7684\u5feb\u901f\uff0c\u5374\u5f62\u6210\u4e86\u4e00\u79cd\u5947\u5927\u7684\u5a01\u529b\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u53ca\u65f6\u51fa\u624b\uff0c\u91d1\u5251\u75be\u51fa\uff0c\u5c01\u4f4f\u4e86\u5de6\u9762\u4e00\u5251\u3002 \u3000\u3000\u8fd9\u5c31\u4f7f\u4e09\u4eba\u5408\u56f4\u4e4b\u52bf\uff0c\u7559\u4e0b\u4e86\u4e00\u4e2a\u5f88\u5927\u7684\u7f3a\u53e3\u3002 \u3000\u3000\u53f6\u957f\u9752\u7a81\u7136\u95f4\u4f7f\u51fa\u4e86\u8ffd\u9b42\u4e03\u5251\uff0c\u5feb\u5982\u661f\u706b\u7684\u653b\u51fa\u4e86\u4e24\u62db\u3002 \u3000\u3000\u8fd9\u7b49\u8ffd\u9b42\u593a\u547d\u7684\u5251\u5f0f\uff0c\u679c\u7136\u975e\u51e1\uff0c\u903c\u5f97\u90a3\u5c45\u4e2d\u7684\u4eba\u8fde\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u4f46\u5feb\u5251\u75be\u653b\uff0c\u4e5f\u903c\u51fa\u4e86\u5bf9\u65b9\u7684\u5feb\u901f\u8fd8\u51fb\uff0c\u4f46\u89c1\u6b8b\u7f3a\u957f\u5251\u6d41\u52a8\uff0c\u54cd\u8d77\u4e86\u4e00\u7247\u524c\u8033\u7684\u5578\u58f0\u3002 \u3000\u3000\u679c\u7136\u662f\u4e00\u79cd\u5947\u5f02\u7684\u5251\u6cd5\u3002 \u3000\u3000\u5c1a\u5929\u4e49\u867d\u7136\u5148\u6709\u4e86\u8bf4\u660e\uff0c\u4f46\u53f6\u957f\u9752\uff0c\u4ecd\u7136\u6709\u7740\u5fc3\u795e\u4e0d\u5b89\u7684\u611f\u89c9\u3002 \u3000\u3000\u90a3\u4e0d\u662f\u4e00\u822c\u5175\u5203\u7684\u91d1\u98ce\u7834\u7a7a\u4e4b\u58f0\uff0c\u800c\u662f\u4e00\u79cd\u5947\u5389\u7684\u5578\u58f0\u3002 \u3000\u3000\u90a3\u67c4\u957f\u5251\u7684\u7f3a\u53e3\uff0c\u60f3\u6765\u4e5f\u5e76\u975e\u53ea\u662f\u4e00\u79cd\u7f3a\u53e3\uff0c\u4e2a\u4e2d\u8fd8\u6709\u5de7\u5999\u7684\u6784\u9020\u3002 \u3000\u3000\u53f6\u957f\u9752\u5fc3\u795e\u4e00\u9707\uff0c\u8ffd\u9b42\u4e03\u5251\u7684\u5a01\u529b\u5c31\u5927\u5927\u7684\u6253\u4e86\u6298\u6263\u3002 \u3000\u3000\u5e78\u597d\u4f55\u5bd2\u8863\u53cc\u5251\u9f50\u51fa\uff0c\u5c01\u4f4f\u4e86\u5de6\u53f3\u4e24\u7ffc\u7684\u5408\u540c\u4e4b\u52bf\u3002 \u3000\u3000\u53f6\u957f\u9752\u6536\u5251\u75be\u9000\u3002 \u3000\u3000\u516b\u4e2a\u7070\u8863\u4eba\uff0c \u53c8\u5e03\u6210\u4e86\u524d\u4e09\u540e\u4e94\u7684\u9635\u52bf\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u9000\u56de\u6765\u5427\uff01\u201d \u3000\u3000\u4e24\u4f4d\u5e94\u58f0\u800c\u9000\u3002 \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u679c\u7136\u662f\u6b8b\u7f3a\u95e8\u6d3e\uff0c\u8fd9\u2014\u6d3e\u5251\u6cd5\uff0c\u7b80\u79f0\u6b8b\u5251\u95e8\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u63a5\u9053\uff1a \u201c\u8001\u524d\u8f88\uff0c\u90a3\u54cd\u58f0\u5f88\u523a\u8033\uff0c\u4e3a\u4ec0\u4e48\u7528\u5251\u4e4b\u4eba\uff0c\u4e0d\u4f1a\u4e3a\u5c16\u5389\u5578\u58f0\u6240\u52a8\u5462?\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u8001\u592b\u4e5f\u60f3\u4e0d\u901a\uff1a\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u6b8b\u5251\u95e8\u7684\u4eba\uff0c\u5206\u5e03\u4e8e\u4e1c\u897f\u4e24\u9762\uff0c\u5404\u81ea\u5e03\u6210\u4e86\u524d\u4e09\u540e\u4e94\u7684\u9635\u52bf\u3002 \u3000\u3000\u5357\u9762\u662f\u70df\u9501\u697c\uff0c\u53ea\u6709\u5317\u9762\u4e00\u4e2a\u7a7a\u9699\u3002 \u3000\u3000\u6708\u5149\u4e0b\u770b\u5f97\u5341\u5206\u6e05\u695a\uff0c \u53ea\u89c1\u6bcf\u4e00\u4e2a\u6b8b\u5251\u95e8\u7684\u4eba\uff0c\u795e\u8272\u51dd\u91cd\uff0c\u76ee\u5149\u5982\u70ac\uff0c\u53ea\u662f\u795e\u60c5\u4e0a\uff0c\u6709\u4e9b\u5446\u75f4\u3002 \u3000\u3000\u9ec4\u7075\u628a\u624b\u4e0b\u7684\u5973\u5c06\uff0c\u5e03\u6210\u4e00\u4e2a\u65b9\u9635\uff0c\u53ef\u4ee5\u540c\u65f6\u6297\u62d2\u56db\u65b9\u9762\u7684\u653b\u52bf\u3002 \u3000\u3000\u4f46\u9ec4\u7075\u4e5f\u7ea6\u675f\u4e0b\u5c5e\u4e0b\u7684\u6b66\u58eb\u4eec\u51fa\u624b\u3002 \u3000\u3000\u6b8b\u5251\u95e8\u7684\u4eba\uff0c\u4e5f\u672a\u51fa\u624b\uff0c\u53cc\u65b9\u9762\uff0c\u4fdd\u6301\u4e86\u4e00\u4e2a\u5bf9\u5cd9\u7684\u5c40\u52bf\u3002 \u3000\u3000\u70df\u9501\u697c\u4e0a\uff0c\u4ecd\u7136\u4e00\u7247\u9759\uff0c\u4e0d\u89c1\u884c\u52a8\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4ed4\u7ec6\u7684\u6253\u91cf\u4e86\u6b8b\u5251\u95e8\u4e2d\u4eba\uff0c\u7a81\u7136\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u54e6!\u6211\u660e\u767d\u4e86\uff0c\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u660e\u767d\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6b8b\u5251\u95e8\u7684\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u53d7\u5251\u4e0a\u7684\u5947\u5578\u5f71\u54cd\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u539f\u56e0\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4eec\u90fd\u662f\u804b\u5b50\uff0c\u6839\u672c\u542c\u4e0d\u5230\u5578\u58f0\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5929\u751f\u6b8b\u7f3a\u7684\u4eba\uff0c\u4e00\u5411\u5c31\u6bd4\u8f83\u8fdf\u5446\uff0c\u80fd\u5b66\u4f1a\u8fd9\u7b49\u6076\u6bd2\u7684\u5251\u6cd5\uff0c\u5b9e\u5728\u6709\u4e9b\u51fa\u4eba\u610f\u5916\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u9ad8\u5929\u5065\u65e0\u6cd5\u6539\u53d8\u4e00\u4e2a\u4eba\u7684\u8d44\u8d28\uff0c\u4f46\u4ed6\u5374\u53ef\u4ee5\u628a\u4e00\u4e2a\u597d\u4eba\u5f04\u6210\u804b\u5b50\u3002\u201d \u9ec4\u7075\u9053\uff1a \u201c\u5bf9!\u9ad8\u5929\u5065\u8fd9\u79cd\u4eba\uff0c\u4ec0\u4e48\u4e8b\u505a\u4e0d\u51fa\u6765\uff0c\u4ed6\u4f1a\u9009\u62e9\u597d\u8d44\u8d28\uff0c\u628a\u4ed6\u4eec\u5f04\u804b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6b8b\u5251\u95e8\u7684\u4eba\uff0c\u5251\u6b8b\u4eba\u4e5f\u6b8b\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a \u201c\u4e3a\u4e86\u5b66\u6210\u4e00\u79cd\u7edd\u6280\uff0c \u81ea\u6b8b\u8eaf\u4f53\uff0c\u8fd9\u79cd\u4eba\uff0c\u5012\u4e5f\u662f\u591f\u72e0\u5fc3\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u662f\u81ea\u6b8b\u8eaf\u4f53\uff0c\u800c\u662f\u88ab\u6b8b\u8eaf\u4f53\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u9ad8\u5929\u5065\u8fd9\u4e2a\u4eba\uff0c\u53ef\u7b85\u5f97\u662f\u5fc3\u72e0\u624b\u8fa3\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u6211\u60f3\u8d77\u6765\u4e86\uff0c\u517b\u6b8b\u9662\u4e2d\u4f4f\u4e00\u4e9b\u53d7\u4f24\u7684\u6b66\u58eb\uff0c\u53ea\u4e0d\u8fc7\u662f\u63a9\u4eba\u8033\u76ee\uff0c\u771f\u6b63\u7684\u4f5c\u7528\uff0c\u662f\u5728\u90a3\u91cc\u8bad\u7ec3\u6b8b\u5251\u6740\u624b\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\uff0c\u548c\u6b8b\u5251\u95e8\u4e2d\u4eba\u52a8\u624b\u76f8\u640f\uff0c\u7edd\u5bf9\u4e0d\u80fd\u6709\u4e1d\u6beb\u4ec1\u6148\u4e4b\u5fc3\uff0c\u6b8b\u5251\u7684\u51f6\u72e0\uff0c\u521a\u624d\u4f60\u4eec\u5df2\u7ecf\u89c1\u8bc6\u8fc7\u4e86\uff0c\u4ed6\u4eec\u6740\u4eba\u7684\u624b\u6cd5\uff0c\u4e5f\u5f88\u6076\u6bd2\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6700\u91cd\u8981\u7684\u662f\uff0c\u4ed6\u4eec\u8033\u6735\u542c\u4e0d\u5217\uff0c\u4f60\u7eb5\u7136\u6709\u5f88\u591a\u7684\u9053\u7406\uff0c\u4e5f\u65e0\u6cd5\u4f7f\u4ed6\u4eec\u660e\u767d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8fd9\u6279\u4eba\u662f\u975e\u8981\u6740\u6389\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a \u201c\u5c31\u7b97\u4e0d\u53d6\u4ed6\u4eec\u7684\u6027\u547d\uff0c\u4e5f\u8981\u628a\u4ed6\u4eec\u5236\u670d\uff0c\u4f7f\u4ed6\u4eec\u6ca1\u6709\u53cd\u6297\u4e4b\u529b\u3002\u201d \u9ec4\u7075\u70b9\u70b9\u5934\u9053\uff1a\u201c\u56f0\u96be\u5728\u5bf9\u4ed8\u8fd9\u6279\u6b8b\u5251\u624b\uff0c\u4e0d\u5927\u5bb9\u6613\uff0c\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u73b0\u5728\uff0e \u4ed6\u4eec\u6709\u5341\u516d\u4eba\uff0c\u6211\u60f3\u7387\u9886\u7ea2\u82b1\u3001\u7eff\u53f6\u5148\u53bb\u5bf9\u4ed8\u4e1c\u9762\u7684\u654c\u4eba\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u4eec\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\u80af\u5e2e\u5fd9\uff0c \u5c0f\u59b9\u5c31\u66f4\u591a\u513f\u5206\u628a\u63e1\u4e86\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u6211\u53bb\u5bf9\u4ed8\u897f\u65b9\u654c\u4eba\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6211\u4e5f\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54b1\u4eec\u4e24\u4e2a\u4eba\uff0c \u53ea\u6015\u5e94\u4ed8\u4e0d\u6765\uff0c\u5bd2\u8863\u3001\u957f\u9752\u3001\u4e07\u80dc\u3001\u534e\u59d1\u5a18\uff0c \u518d\u52a0\u4e0a\u4f60\uff0e\u53ef\u4ee5\u653e\u624b\u4e00\u6218\u4e86\uff0c \u65b9\u9635\u4e4b\u4e2d\uff0c\u5c31\u9ebb\u70e6\u5c1a\u524d\u8f88\u548c\u592b\u4eba\u4e3b\u6301\u4e86\u3002\u201d \u5434\u53cc\u5973\u548c\u9ad8\u5e7d\u5170\u5148\u884c\u884c\u52a8\uff0c\u5e26\u7740\u7ea2\u82b1\u3001\u7eff\u53f6\uff0c\u5411\u4e1c\u9762\u65b9\u4f4d\u4e0a\u7684\u6b8b\u5251\u6740\u624b\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u5434\u53cc\u5973\u7b49\u4e0e\u5e03\u5b88\u4e1c\u65b9\u7684\u6b8b\u5251\u624b\uff0c \u5df2\u5f62\u6210\u4e86\u5bf9\u5cd9\u7684\u5c40\u9762\u3002 \u7384\u5a18\u5f53\u5148\u884c\u51fa\u4e86\u65b9\u9635\uff0c\u9053\uff1a\u201c\u6211\u5148\u4e0a\u3002\u201d \u3000\u3000\u5c1a\u592b\u4eba\u9053\uff1a \u201c\u7384\u5a18\uff0c\u8bb0\u7740\uff0c\u4e00\u51fa\u624b\uff0c\u5c31\u522b\u7559\u60c5\uff0c \u4ed6\u4eec\u7684\u5251\u6cd5\u6076\u6bd2\uff0c\u6211\u4eec\u5df2\u7ecf\u5931\u53bb\u4e86\u552f\u4e00\u7684\u5973\u513f\uff0c\u4e0d\u613f\u518d\u5931\u53bb\u4e00\u4e2a\u4e49\u5973\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u5a18\uff0c\u6211\u4f1a\u7231\u60dc\u81ea\u5df1\u3002\u201d \u3000\u3000\u70df\u9501\u697c\u4e0a\uff0c\u4f20\u51fa\u6765\u9ad8\u5929\u5065\u7684\u5927\u7b11\u4e4b\u58f0\uff0c\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u4f60\u770b\u5230\u4e86\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6b8b\u5251\u6740\u624b?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u5bf9!\u4ed6\u4eec\u662f\u6211\u6240\u6709\u4eba\u624b\u7684\u4e00\u90e8\u4efd\uff0e \u53ea\u8981\u6211\u4e00\u58f0\u4ee4\u4e0b\uff0c\u7acb\u523b\u53ef\u4ee5\u8c03\u96c6\u5230\u66f4\u591a\u7684\u4eba\u624b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u9ad8\u5821\u4e3b\u9690\u85cf\u7684\u4eba\u624b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8981\u4f60\u660e\u767d\u4e00\u4ef6\u4e8b\uff0c\u7b2c\u4e00\u5821\u4e0d\u662f\u4e00\u4e2a\u53ef\u4ee5\u8f7b\u4fae\u7684\u5730\u65b9\uff0c\u60ac\u5d16\u52d2\u9a6c\uff0c\u6c5f\u5cb8\u56de\u5934\uff0c\u73b0\u5728\uff0c\u8fd9\u4e00\u5207\uff0c\u90fd\u8fd8\u6765\u5f97\u53ca\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u72e1\u5154\u4e09\u7a9f\uff0c\u4f60\u6bd4\u72e1\u5154\u8fd8\u72e1\u733e\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u542c\u770b\uff0c\u96f7\u65e0\u5f71\u4e0d\u53ef\u9760\uff0c\u4ed6\u5728\u7b49\uff0c\u7b49\u4e00\u4e2a\u673a\u4f1a\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4ed6\u60f3\u5750\u6536\u9c7c\u5229?\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u53ea\u731c\u5bf9\u4e86\u4e00\u534a\uff0c\u9664\u4e86\u7b49\u8fd9\u4e2a\u673a\u4f1a\u4e4b\u5916\uff0c\u4ed6\u8fd8\u5728\u89c2\u671b\u4e00\u4ef6\u4e8b\uff0c\u4e00\u65e6\u4ed6\u53d1\u89c9\u4e86\u6211\u8fd8\u6709\u5f88\u5f3a\u7684\u5b9e\u529b\uff0c\u4ed6\u4f1a\u7a81\u7136\u95f4\u7387\u4eba\u624b\u51b2\u8fc7\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u9ad8\u5821\u4e3b\u7684\u610f\u601d\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\u662f\u65e7\u4e8b\u91cd\u63d0\uff0c\u4f60\u6211\u53ef\u4ee5\u5408\u4f5c\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5408\u4f5c\u4e3a\u6076\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u90a3\u662f\u770b\u6cd5\u4e0a\u7684\u8ddd\u79bb\u3002\u9ec4\u5821\u4e3b\u4e3a\u4ec0\u4e48\u4e0d\u60f3\u60f3\uff0c\u4f60\u6211\u5408\u4f5c\uff0c\u53ef\u4ee5\u51cf\u5c11\u5f88\u591a\u7684\u51b2\u7a81\u3002\u201d \u3000\u3000\u9ec4\u7075\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u4f60\u53ef\u66fe\u60f3\u8fc7\uff0c\u53e4\u5f80\u4eca\u6765\uff0c\u6709\u591a\u5c11\u82f1\u96c4\u4eba\u7269\uff0c\u5374\u65e0\u6cd5\u63a9\u53bb\u4ed6\u672c\u8d28\u4e0a\u7684\u5149\u91c7\uff0c\u4f60\u5728\u6c5f\u6e56\u4e0a\u7684\u5730\u4f4d\uff0c\u65e9\u5df2\u7ecf\u53d7\u5c3d\u4e86\u522b\u4eba\u7684\u656c\u91cd\uff0c\u6211\u5b9e\u5728\u60f3\u4e0d\u51fa\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4f1a\u51fa\u751f\u79f0\u9738\u6c5f\u6e56\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u672c\u6765\uff0c\u6211\u5df2\u7ecf\u5927\u529f\u5c06\u6210\uff0c\u5374\u672a\u6599\u5230\uff0c\u7adf\u7136\u574f\u5728\u4f60\u7684\u624b\u91cc\uff0c\u800c\u4f60\u662f\u6c5f\u6e56\u4e0a\u540d\u58f0\u6700\u574f\u7684\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4e0d\u8bba\u4e16\u4eba\u5bf9\u6211\u6709\u4e9b\u4ec0\u4e48\u6279\u8bc4\uff0c\u4f46\u6211\u9ec4\u7075\u53ea\u6c42\u5fc3\u5b89\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u4e0d\u660e\u767d\uff0c\u4f60\u9ec4\u7075\u8fd9\u4e2a\u4eba\uff0c\u7a76\u7adf\u662f\u7528\u5fc3\u4f55\u5728?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u540c\u7684\u662f\u5c31\u662f\u6211\u6ca1\u6709\u7528\u5fc3\uff0c\u6211\u4e0d\u4f1a\u559c\u6b22\u4f60\u6240\u4f5c\u6240\u4e3a\uff0c\u624d\u53cd\u5bf9\u4f60\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u5c31\u7b97\u4f60\u7684\u53cd\u5bf9\u6210\u529f\u4e86\uff0c\u5bf9\u4f60\u6709\u4e9b\u4ec0\u4e48\u597d\u5904\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bf9\u6211\u662f\u6ca1\u6709\u597d\u5904\uff0c\u800c\u4e14\uff0c\u6211\u76f8\u4fe1\uff0c\u4f60\u9ad8\u5929\u5065\u5012\u4e0b\u53bb\u4e4b\u540e\uff0c\u8fd8\u4f1a\u6709\u522b\u4eba\u8d77\u6765\uff0c\u518d\u4e00\u6b21\u640f\u6218\u8fc7\u540e\uff0c\u6c5f\u6e56\u4e0a\u53c8\u4f1a\u6709\u4e00\u6bb5\u65f6\u95f4\u7684\u5e73\u9759\uff0c\u6c5f\u6e56\u4e0a\uff0c\u5c31\u662f\u8fd9\u4e48\u4e00\u4e2a\u5faa\u73af\u4e0d\u7edd\u7684\u73af\u5883\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u9ec4\u7075\uff0c \u4f60\u5c31\u662f\u90a3\u4e00\u6279\u81ea\u8c13\u7684\u6b63\u4e49\u529b\u91cf?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u6bcf\u4e00\u4ee3\uff0c\u603b\u8981\u6709\u4e00\u4e9b\u4eba\u727a\u7272\uff0c\u6211\u4eec\u5c31\u662f\u8fd9\u4e9b\u727a\u7272\u7684\u4eba\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a \u201c\u6211\u4e0d\u660e\u767d\uff0c\u727a\u7272\u6709\u4e00\u4e2a\u76ee\u7684\uff0c \u4f60\u8fd9\u4e9b\u727a\u7272\uff0c\u8fd8\u6709\u4e9b\u4ec0\u4e48\u610f\u601d\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u9ad8\u5929\u5065\uff0c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4e0d\u4f1a\u6709\u4ec0\u4e48\u7ed3\u8bba\u7684\uff0c\u56e0\u4e3a\uff0c\u8fd9\u4e9b\u4e8b\uff0c \u5df2\u7ecf\u5386\u4e86\u51e0\u5343\u5e74\uff0c\u4e00\u76f4\u627e\u4e0d\u51fa\u4e00\u4e2a\u7ed3\u679c\u6765\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u73b0\u5728\uff0c\u54b1\u4eec\u627e\u51fa\u4e00\u4e2a\u7ed3\u679c\u6765\u5982\u4f55?\u201d \u3000\u3000\u9ec4\u7075\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u4e0d\u53ef\u80fd\uff0c\u552f\u4e00\u7684\u7ed3\u679c\uff0c\u5c31\u662f\u4f60\u653e\u5f03\u4e86\u8fd9\u4e2a\u60f3\u6cd5\uff0c\u73b0\u5728\u65f6\u72b9\u672a\u665a\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u6240\u8c13\u65f6\u72b9\u672a\u665a\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u653e\u5f03\u4f60\u7684\u8363\u8000\uff0c\u653e\u5f03\u4f60\u7684\u5730\u4f4d\uff0c\u5e26\u7740\u4f60\u4e24\u4e2a\u4eb2\u4fe1\uff0c\u559c\u7231\u7684\u59bb\u59be\uff0c\u79bb\u5f00\u8fd9\u91cc\u5427!\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8fd9\u662f\u8001\u592b\u5efa\u7acb\u7684\u57fa\u4e1a\uff0c\u4f60\u8981\u6211\u5230\u54ea\u91cc\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4e0d\u7ba1\u5230\u54ea\u91cc\u53bb\uff0c\u4ece\u6b64\u522b\u518d\u5728\u6c5f\u6e56\u4e0a\u51fa\u73b0\uff0c\u6211\u662f\u56de\u5934\u7684\u6d6a\u5b50\uff0c\u6211\u660e\u767d\u4e00\u4e2a\u6539\u8fc7\u5411\u5584\u7684\u4eba\uff0c \u60f3\u627e\u4e00\u6761\u5411\u5584\u4e4b\u8def\u7684\u56f0\u96be\uff0c\u6240\u4ee5\uff0c\u6211\u613f\u610f\u5c3d\u6211\u4e4b\u529b\uff0c\u4fdd\u62a4\u4f60\u7684\u5b89\u5168\u3002\u201d \u9ad8\u5929\u5065\u7eb5\u58f0\u5927\u7b11\u4e09\u58f0\uff0c\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u6211\u770b\u54b1\u4eec\u8c08\u4e0d\u4e0b\u53bb\u4e86\uff0c\u542c\u5b8c\u4f60\u8fd9\u4e9b\u8bdd\uff0c\u6211\u624d\u53d1\u89c9\u4e86\u54b1\u4eec\u662f\u4e24\u4e2a\u8a93\u4e0d\u4e24\u7acb\u7684\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f60\u592a\u987a\u5229\u4e86\uff0c \u4e00\u51fa\u9053\uff0c\u5c31\u662f\u6c5f\u6e56\u4e0a\u975e\u5e38\u53d7\u656c\u91cd\u7684\u4eba\uff0c\u4f60\u8fd9\u4e00\u751f\u4e2d\u51e0\u4e4e\u662f\u65e0\u5f80\u4e0d\u5229\uff0c\u4e00\u624b\u906e\u5c3d\u5929\u4e0b\u4eba\u7684\u8033\u76ee\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8001\u592b\u80fd\u6709\u4eca\u5929\u8fd9\u70b9\u513f\u58f0\u8a89\uff0c \u4e5f\u4e0d\u662f\u51ed\u7a7a\u6361\u6765\u7684\uff0c\u82b1\u8d39\u4e86\u4e0d\u5c11\u7684\u5fc3\u8840\uff0c\u624d\u6253\u51fa\u4eca\u65e5\u7684\u5c40\u9762\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u8fd9\u4e00\u70b9\uff0c\u5012\u662f\u4e0d\u80fd\u4e0d\u53eb\u4eba\u4f69\u670d\u9601\u4e0b\u7684\u806a\u660e\uff0c\u6709\u4e9b\u4eba\uff0c\u505a\u4e86\u5f88\u591a\u7684\u597d\u4e8b\uff0c\u4f46\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u7adf\u7136\u662f\u6ca1\u6709\u4e00\u4e2a\u4eba\u77e5\u9053\uff0c\u6709\u4e9b\u4eba\u505a\u4e86\u6570\u5341\u767e\u4ef6\u7684\u574f\u4e8b\uff0c\u6b66\u6797\u4e2d\u7adf\u7136\u4e5f\u65e0\u4eba\u77e5\u6653\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u76ee\u5149\u4e00\u63a0\u56db\u5468\u7684\u5f62\u52bf\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u56db\u9762\u695a\u6b4c\uff0c\u8fd9\u53e5\u8bdd\u7684\u610f\u601d\uff0c\u4f60\u61c2\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u6709\u4e00\u79cd\u4eba\uff0c\u65e0\u5f80\u4e0d\u5229\uff0c\u6570\u5341\u5e74\u4e2d\uff0c\u90fd\u5728\u6210\u529f\uff0c\u4f46\u4ed6\u5374\u7981\u4e0d\u8d77\u4e00\u6b21\u5931\u8d25\uff0c\u56e0\u4e3a\uff0c\u4ed6\u4e00\u65e6\u5931\u8d25\u4e86\uff0e\u5c31\u6210\u4e86\u4f17\u77e2\u4e4b\u7684\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u8001\u592b\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u8bf4\u4e86\u534a\u5929\uff0c\u5c31\u662f\u5728\u8bf4\u9601\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u9664\u4e86\u4f60\u9ec4\u7075\u4e4b\u5916\uff0c\u8001\u592b\u8fd8\u672a\u53d1\u89c9\u6709\u522b\u7684\u4eba\uff0c\u8ddf\u8001\u592b\u4f5c\u5bf9\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u81ea\u5df1\u89c9\u7740\uff0c\u4f60\u4ee3\u8868\u4e86\u6b63\u4e49\uff0c\u66ff\u6b66\u6797\u9664\u5bb3\uff0c\u4f46\u4f60\u53ef\u66fe\u60f3\u5230\uff0c\u8001\u592b\u5982\u82e5\u4e00\u65e6\u79bb\u5f00\u4e86\u6b64\u5730\uff0c\u968f\u4fbf\u8bf4\u51e0\u53e5\u8bdd\uff0c\u5c31\u53ef\u4ee5\u4f7f\u4f60\u8fd9\u4e00\u756a\u5fc3\u8840\u3001\u5fc3\u529b\uff0c\u767d\u8d39\u4e86\uff0c\u800c\u4e14\u4f60\u5f88\u5feb\u4f1a\u53d8\u6210\u4f17\u77e2\u7684\u4e4b\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6709\u8001\u592b\u5728\u6b64\uff0c\u5c82\u5bb9\u4f60\u4fe1\u53e3\u96cc\u9ec4\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\uff0c\u4f60\u662f\u4ec0\u4e48\u4eba?\u4f60\u4e0d\u8fc7\u662f\u6c5f\u6e56\u4e0a\u7684\u8001\u673d\u4eba\u7269\u7f62\u4e86\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u6012\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u8001\u592b\u8981\u6d3b\u6d3b\u7684\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u4efd\u80fd\u529b\u5417?\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u603b\u6709\u4e00\u5929\uff0c\u6211\u4f1a\u6d3b\u6d3b\u7684\u5288\u4e86\u4f60\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5c1a\u5929\u4e49\uff0c\u8fd9\u4e00\u751f\u4e2d\uff0c\u4f60\u6ca1\u6709\u8fd9\u4e48\u4e00\u4e2a\u673a\u4f1a\uff0c\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u80fd\u529b\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u6c14\u5f97\u5168\u8eab\u53d1\u6296\uff0c\u9053\uff1a\u201c\u4f60\u7b49\u7740\uff0c\u9ad8\u5929\u5065\uff0c\u8fd9\u4e00\u5929\u4e0d\u4f1a\u592a\u8fdc\u3002\u201d \u3000\u3000\u7384\u5a18\u4f4e\u58f0\u9053\uff1a\u201c\u4e49\u7236\uff0c\u4ed6\u5df2\u5982\u56f0\u517d\uff0c\u4f55\u5fc5\u548c\u4ed6\u4e00\u822c\u89c1\u8bc6\u5462?\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u7a81\u7136\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u73b0\u5728\uff0c \u4f60\u662f\u5426\u51b3\u5b9a\u4e86\u548c\u6211\u51fa\u624b\u4e00\u6218?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u4f1a\u7684\uff0c\u6211\u4eec\u65e9\u665a\u90fd\u514d\u4e0d\u4e86\u4e00\u573a\u62fc\u6218\uff0c\u4e0d\u8fc7\u4e0d\u662f\u73b0\u5728\uff0c\u6211\u5e94\u8be5\u51fa\u6218\u7684\u65f6\u5019\uff0c\u6211\u4f1a\u9009\u62e9\u65f6\u95f4\u3002\u201d \u3000\u3000\u9ec4\u7075\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u8fd9\u662f\u5a01\u80c1\u5417?\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4e0d\u7528\u548c\u4ed6\u8c08\u4e86\uff0c\u70df\u9501\u697c\u4e2d\u4e8b\uff0c\u4ea4\u7ed9\u6211\u4e86\uff0c\u4f60\u4eec\u5148\u6d88\u706d\u4e86\u6b8b\u5251\u95e8\u4e2d\u7684\u5251\u624b\u518d\u8bf4\u3002\u201d \u3000\u3000\u6574\u4e2a\u65b9\u9635\uff0c\u7f13\u7f13\u5411\u524d\u6740\u53bb\uff0c\u5835\u4f4f\u4e86\u70df\u9501\u697c\u7684\u95e8\u53e3\u3002 \u3000\u3000\u9ec4\u7075\u8f6c\u5411\u4e86\u897f\u9762\u7684\u6b8b\u5251\u6740\u624b\u3002 \u3000\u3000\u9ad8\u5929\u5065\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u4e5f\u8bb8\u6709\u8fd9\u4efd\u80fd\u529b\uff0c\u4e0d\u8fc7\uff0c\u4f60\u4eec\u5c06\u4f1a\u906d\u53d7\u5230\u5f88\u5927\u7684\u4f24\u4ea1\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u7528\u4f60\u62c5\u5fc3\u4e86\uff0c\u53cd\u6b63\u4ed6\u4eec\u5df2\u7ecf\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6211\u4f1a\u968f\u65f6\u5e26\u9886\u4eba\u624b\u51fa\u53bb\uff0c\u63a5\u5e94\u4ed6\u4eec\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u4eec\u4f1a\u606d\u5019\u90a3\u4e00\u65f6\u523b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u548c\u4e1c\u9762\u7684\u6b8b\u5251\u95e8\u4e2d\u5251\u624b\uff0c\u4ecd\u7136\u662f\u4fdd\u6301\u4e86\u4e00\u4e2a\u5bf9\u5cd9\u4e4b\u52bf\u3002 \u3000\u3000\u53cc\u65b9\u90fd\u51fa\u5947\u7684\u51b7\u9759\uff0c\u76f8\u5bf9\u800c\u7acb\uff0c\u76ee\u5149\u4ea4\u6295\u3002 \u3000\u3000\u4f46\u5374\u65e0\u4eba\u51fa\u624b\u3002 \u3000\u3000\u9ec4\u7075\u884c\u8fd1\u4e86\u897f\u9762\u7684\u5251\u624b\uff0c\u9ec4\u7075\u8bf4\u9053\uff1a \u201c\u8bf8\u4f4d\uff0c\u80fd\u591f\u542c\u5230\u6211\u8bf4\u7684\u8bdd\u5417?\u201d \u3000\u3000\u7ad9\u5728\u524d\u6392\u5c45\u4e2d\u800c\u7acb\u7684\u5251\u624b\uff0c\u7adf\u7136\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u90a3\u5f88\u597d\uff0c\u5728\u54b1\u4eec\u6ca1\u6709\u52a8\u624b\u4e4b\u524d\uff0c\u6211\u60f3\u8bf7\u6559\u4e00\u4ef6\u4e8b\u3002\u201d \u8fd9\u4e9b\u4eba\u662f\u771f\u7684\u804b\u5b50\uff0c\u9ec4\u7075\u7684\u58f0\u97f3\uff0c\u7a0d\u5fae\u5c0f\u4e86\u4e00\u4e9b\uff0c\u4ed6\u4eec\u4f3c\u4e4e\u662f\u90fd\u542c\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u9ec4\u7075\u628a\u58f0\u97f3\uff0c\u6eb6\u5165\u5185\u529b\uff0c\u63a5\u9053\uff1a \u201c\u4f60\u4eec\u4e0d\u662f\u5929\u751f\u7684\u804b\u5b50\uff0c\u800c\u662f\u9ad8\u5929\u5065\u4e3a\u4e86\u8981\u4f60\u4eec\u5b66\u4e60\u8fd9\u79cd\u6b8b\u5251\u95e8\u7684\u5251\u6cd5\uff0c\u6240\u4ee5\uff0c\u624d\u628a\u4ed6\u4eec\u53d8 \u3000\u3000\u6210\u4e86\u804b\u5b50\u3002\u201d \u3000\u3000\u4ed6\u76f8\u4fe1\u5bf9\u9762\u7684\u654c\u4eba\uff0c\u4e00\u5b9a\u542c\u5230\u4e86\u4ed6\u7684\u8c08\u8bdd\uff0c\u53ea\u4e0d\u8fc7\u5bf9\u65b9\u5374\u6ca1\u6709\u4ec0\u4e48\u8868\u793a\u3002 \u3000\u3000\u9ec4\u7075\u5f88\u7528\u5fc3\u7684\u89c2\u5bdf\u4e86\u4ed6\u4eec\uff0c\u4f46\u5374\u770b\u4e0d\u51fa\u4ed6\u4eec\u6709\u4efb\u4f55\u53cd\u5e94\u3002 \u3000\u3000\u7384\u5a18\u4f4e\u58f0\u53f9\u9053\uff1a \u201c\u6211\u770b\u4f60\u662f\u767d\u8d39\u5fc3\u673a\u4e86\uff0c\u5982\u82e5\u9ad8\u5929\u5065\u6ca1\u6709\u628a \u63e1\u63a7\u5236\u4ed6\u4eec\uff0c\u4e5f\u4e0d\u4f1a\u7528\u4ed6\u4eec\u51fa\u9762\u62d2\u654c\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u60f3\u8bf4\u670d\u4ed6\u4eec\uff0c\u53ea\u6015\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u5230\u9ad8\u5929\u5065\u7684\u58f0\u97f3\uff0c\u7531\u70df\u9501\u697c\u4e0a\u4f20\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\uff0c\u4ed6\u662f\u6211\u4eb2\u81ea\u63a7\u5236\u7684\u4eba\u624b\uff0c\u5982\u82e5\u4f60\u60f3\u8bf4\u670d\u4ed6\u4eec\uff0c\u53ea\u6015\u4f1a\u5f88\u5931\u671b\u3002\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u4e0a\u5929\u6709\u597d\u751f\u4e4b\u5fb7\uff0c\u5982\u82e5\u4ed6\u4eec\u65e0\u6cd5\u4f53\u4f1a\u5230\u9ec4\u5821\u4e3b\u7684\u5fc3\uff0c\u90a3\u5c31\u53ea\u597d\u6740\u4e86\u4ed6\u4eec\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u672a\u518d\u7406\u4f1a\u5c1a\u5929\u4e49\uff0c\u5168\u573a\u4e2d\u7a81\u7136\u95f4\u9759\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u79cd\u6c89\u9ed8\u5145\u6ee1\u7740\u7d27\u5f20\u3002 \u3000\u3000\u660e\u6708\u5728\u5929\uff0c\u5149\u534e\u94fa\u5730\uff0c\u8fd9\u662f\u4e2a\u5f88\u7f8e\u5f88\u7f8e\u7684\u6708\u591c\u3002 \u3000\u3000\u4f46\u8fd9\u7f8e\u4e3d\u7684\u6708\u591c\uff0c\u5374\u88ab\u4e00\u79cd\u6d53\u70c8\u7684\u6740\u6c14\u63a9\u906e\u3002 \u3000\u3000\u53cc\u65b9\u9762\u6210\u4e86\u5251\u62d4\u5f29\u5f20\u4e4b\u5c40\u3002 \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u6211\u5148\u51fa\u624b\u3002\u201d \u3000\u3000\u5ffd\u7136\u95f4\u4e00\u632b\u67f3\u8170\uff0c\u76f4\u51b2\u8fc7\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4eae\u51fa\u4e86\u91d1\u5251\u3002 \u3000\u3000\u534e\u5999\u771f\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u6162\u7740\u3002\u201d \u3000\u3000\u7a81\u7136\u628a\u4e00\u5757\u7ee2\u5e15\u626f\u5f00\uff0c\u63a5\u9053\uff1a \u201c\u628a\u8033\u6735\u585e\u8d77\u6765\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u51cf\u5c11\u4e00\u4e9b\u6b8b\u5251\u5389\u5578\u7684\u5a01\u80c1\u3002\u201d \u8fd9\u4ef6\u4e8b\u60c5\u5f88\u7b80\u5355\uff0c\u4f46\u5374\u7adf\u7136\u662f\u6ca1\u6709\u4eba\u60f3\u5f97\u8d77\u6765\u3002 \u3000\u3000\u597d\u597d\u7684\u4e00\u4e2a\u4eba\uff0c\u9aa4\u7136\u95f4\u628a\u8033\u6735\u5835\u8d77\u6765\uff0c\u7acb\u523b\u5c31\u6709\u7740\u4e0d\u592a\u4e60\u60ef\u7684\u611f\u89c9\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4e0a!\u5200\u957f\u5251\u4e00\u63a2\uff0c\u523a\u5411\u4e86\u53f3\u9762\u4e00\u4eba\u3002 \u3000\u3000\u4e07\u80dc\u548c\u4f55\u5bd2\u8863\u540c\u886c\u53d1\u52a8\uff0c\u653b\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u7528\u7ee2\u5e15\u628a\u8033\u6735\u585e\u4e86\u8d77\u6765\uff0c\u679c\u7136\u51cf\u5c11\u4e86\u654c\u4eba\u5251\u5578\u4e0a\u5f88\u5927\u7684\u5a01\u80c1\u3002 \u3000\u3000\u6b8b\u5251\u95e8\u4e2d\u7684\u5251\u6cd5\uff0c\u5c31\u7b97\u6ca1\u6709\u90a3\u523a\u8033\u7684\u5251\u5578\u58f0\u7684\u5a01\u80c1\uff0c\u4f46\u5b83\u7684\u53d8\u5316\uff0c\u4e5f\u5341\u5206\u51cc\u5389\u3002 \u3000\u3000\u4f46\u51cf\u5c11\u4e86\u90a3\u79cd\u51c4\u5389\u58f0\u7684\u5a01\u80c1\uff0c\u5bf9\u4f55\u5bd2\u8863\u7b49\uff0c\u4e5f\u662f\u4e00\u79cd\u5f88\u5927\u7684\u5e2e\u52a9\u3002 \u3000\u3000\u516d\u4eba\u7684\u5267\u6597\uff0c\u5341\u5206\u6fc0\u70c8\u3002 \u3000\u3000\u5341\u4e8c\u4e2a\u7167\u9762\u8fc7\u540e\uff0c\u53cc\u65b9\u4ecd\u7136\u662f\u4e00\u4e2a\u4e0d\u80dc\u4e0d\u8d25\u7684\u5c40\u9762\u3002 \u3000\u3000\u4e8b\u60c5\u7684\u53d1\u5c55\uff0c\u679c\u5728\u4f55\u5bd2\u8863\u7684\u9884\u6599\u4e4b\u4e2d\uff0c\u53cc\u65b9\u52a8\u624b\u5230\u4e94\u5341\u5408\u4e4b\u540e\uff0c\u4e94\u4e2a\u7ad9\u5728\u540e\u9762\u7684\u4eba\uff0c\u5f00\u59cb\u7531\u4e24\u9762\u5305\u56f4\u8fc7\u6765\u3002 \u3000\u3000\u9ec4\u7075\u4e00\u76f4\u5728\u89c2\u5bdf\u7740\u53cc\u65b9\u52a8\u624b\u7684\u60c5\u5f62\u3002 \u3000\u3000\u4ed6\u957f\u5e74\u5728\u6c5f\u6e56\u4e0a\u8d70\uff0c\u4e00\u5ea6\u66fe\u662f\u6d6a\u5b50\uff0c\u4f46\u4e5f\u662f\u4e2a\u5929\u624d\uff0c\u4ed6\u80f8\u7f57\u535a\u6742\uff0c\u8eab\u8d1f\u5341\u4f59\u79cd\u7edd\u6280\u3002 \u3000\u3000\u9ed8\u5bdf\u5bf9\u65b9\u5251\u52bf\u7684\u53d8\u5316\uff0c\u5e0c\u671b\u80fd\u627e\u51fa\u4e00\u4e2a\u529e\u6cd5\u6765\uff0c\u4f46\u4ed6\u5f88\u5931\u671b\uff0c\u53ea\u89c9\u5bf9\u65b9\u7684\u5251\u6cd5\uff0c\u53d8\u5316\u8be1\u5f02\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u7136\u60f3\u4e0d\u51fa\u7834\u89e3\u4e4b\u6cd5\u3002 \u3000\u3000\u773c\u770b\u90a3\u8eab\u540e\u7684\u4e94\u4e2a\u4eba\u7531\u4e24\u7ffc\u5305\u6284\u8fc7\u6765\uff0c\u7acb\u65f6\u53f3\u624b\u4e00\u6325\uff0c\u9053\uff1a\u201c\u4e0a\u5427!\u201d \u3000\u3000\u5251\u5578\u58f0\u5212\u7a7a\u800c\u8d77\uff0c\u7acb\u523b\u95f4\uff0c\u4e00\u7247\u68ee\u5bd2\u7684\u5251\u5149\uff0c\u6321\u4f4f\u4e86\u7384\u5a18\u7684\u653b\u52bf\u3002 \u3000\u3000\u90a3\u4e0d\u662f\u4e00\u67c4\u5251\u6240\u821e\u51fa\u7684\u5251\u8292\uff0c\u800c\u662f\u4e09\u67c4\u5251\uff0c\u5408\u4f5c\u800c\u6210\u7684\u5251\u5e55\u3002 \u3000\u3000\u5c16\u5389\u7684\u5578\u58f0\uff0c\u5212\u7834\u4e86\u9759\u591c\uff0c\u4f20\u51fa\u5f88\u8fdc\u3002 \u3000\u3000\u7384\u5a18\u8fde\u65bd\u5c55\u4e09\u79cd\u7edd\u6280\uff0c\u4ecd\u7136\u51b2\u4e0d\u7834\u90a3\u4e09\u652f\u6b8b\u5251\u7ec4\u6210\u7684\u7ef5\u5bc6\u5251\u5e55\uff0c\u65e0\u529f\u800c\u9000\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u5979\u8138\u8272\u5fae\u5fae\u53d1\u767d\uff0c\u800c\u4e14\uff0c\u8fd8\u6709\u4e9b\u8f7b\u5fae\u7684\u5598\u606f\u3002 \u3000\u3000\u9ec4\u7075\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u7384\u5a18\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u4eae\u5175\u5203\uff0c\u5bf9\u4ed8\u8fd9\u4e9b\u4eba\uff0c\u7528\u4e0d\u7740\u5ba2\u6c14\u3002\u201d \u7384\u5a18\u9053\uff1a\u201c\u6211\u6709\u2018\u5f39\u6307\u795e\u901a\u2019\uff0c\u6df7\u5408\u7740\u94c1\u526a\u624b\uff0c\u5bf9\u4ed8\u8fc7\u4e0d\u5c11\u4e00\u6d41\u5251\u624b\u3001\u5200\u5ba2\uff0c\u4f46\u4ed6\u4eec\u4e09\u5251\u914d\u5408\u7684\u592a\u4e25\u5bc6\uff0c\u6211\u6ca1\u6709\u529e\u6cd5\u51b2\u8fc7\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u6700\u8ba8\u538c\u7684\u662f\u90a3\u5251\u4e0a\u53d1\u51fa\u7684\u5578\u58f0\uff0c\u5c16\u5389\u523a\u8033\uff0c\u5bf9\u4e00\u4e2a\u5251\u624b\u6765\u8bf4\uff0c\u5f71\u54cd\u5fc3\u795e\u5f88\u5927\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u53f6\u5144\uff0c\u4e07\u5144\uff0c\u54b1\u4eec\u4e09\u4e2a\u4eba\u4e00\u9f50\u51fa\u624b\u8bd5\u8bd5\uff0c\u6bcf\u4e00\u4e2a\u4eba\u5bf9\u4ed8\u4e00\u4e2a\uff0c\u4f7f\u4ed6\u5f88\u96be\u76f8\u987e\uff0c\u65e0\u6cd5\u914d\u5408\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d!\u54b1\u4eec\u8bd5\u8bd5\u770b\uff0c\u4e07\u5144\u653b\u5de6\u7ffc\uff0c\u6211\u653b\u53f3\u7ffc\uff0c\u4f55\u5144\u5c45\u4e2d\uff0c\u201d \u3000\u3000\u660e\u660e\u77e5\u9053\uff0c\u4ed6\u4eec\u804b\u7684\u5f88\u5f7b\u5e95\uff0c\u53f6\u957f\u9752\u548c\u4f55\u5bd2\u8863\u7684\u4ea4\u8c08\uff0c\u4e5f\u672a\u907f\u8bb3\u5bf9\u65b9\u3002 \u3000\u3000\u4e07\u80dc\u5df2\u7ecf\u4eae\u51fa\u4e86\u5200\uff0c\u6a2a\u5411\u5de6\u7ffc\u3002 \u3000\u3000\u53f6\u6c0f\u9752\u9053\uff1a\u201c\u4f55\u5144\u4f60\u4e0b\u4ee4\u51fa\u624b\uff0c\u54b1\u4eec\u52a1\u6c42\u4e00\u51fb\u6210\u529f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5148\u6c42\u81ea\u4fdd\uff0c\u4ed6\u4eec\u540e\u9762\u8fd8\u6709\u4e94\u4e2a\u4eba\uff0c\u4ed6\u4eec\u5e03\u7684\u8fd9\u4e2a\u9635\u52bf\uff0c\u5f88\u666e\u901a\uff0c\u4f46\u5374\u5f88\u5947\u602a\uff0c\u4f46\u4e07\u53d8\u4e0d\u79bb\u5176\u5b97\uff0c\u6211\u60f3\u4ed6\u4eec\u4e00\u5b9a\u7531\u4e24\u7ffc\u5305\u6284\uff0c\u5728\u672a\u4e86\u89e3\u6b8b\u5251\u4e0a\u771f\u6b63\u7684\u5a01\u529b\u4e4b\u524d\uff0c\u6211\u60f3\uff0c\u54b1\u4eec\u8fd8\u662f\u5c0f\u5fc3\u4e00\u4e9b\u7684\u597d\uff0c\u4e0d\u53ef\u9690\u5165\u4e86\u5bf9\u65b9\u7684\u5305\u56f4\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u548c\u7384\u5a18\u3001\u5999\u771f\u4f1a\u63a5\u5e94\u4f60\u4eec\uff0c\u653e\u5fc3\u51fa\u624b\u5427!\u201d \u3000\u3000\u534e\u5999\u771f\u3001\u7384\u5a18\uff0c\u7acb\u523b\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u5835\u622a\u7684\u5305\u6284\uff0c\u6bcf\u4e00\u4e2a\u4eba\uff0c\u62b5\u6321\u4e24\u652f\u6b8b\u5251\u3002 \u3000\u3000\u4e24\u652f\u6b8b\u5251\u7684\u538b\u529b\u5f3a\u5927\uff0c\u7384\u5a18\u3001\u534e\u5999\u771f\u867d\u7136\u662f\u6b66\u6797\u4e2d\u4e00\u6d41\u9ad8\u624b\uff0c\u4f46\u4e24\u652f\u6b8b\u5251\u7684\u5a01\u529b\uff0c\u5341\u5206\u5f3a\u5927\u7684\u7384\u5a18\u548c\u534e\u5999\u771f\u7684\u53cc\u5251\u5939\u51fb\u4e4b\u4e0b\uff0c\u62db\u67b6\u5341\u5206\u56f0\u96be\uff0c\u4f46\u4e24\u4eba\u5374\u54ac\u7259\u82e6\u6491\uff0c\u65bd\u5c55\u51fa\u4e86\u5168\u8eab\u7684\u89e3\u6570\u3002 \u3000\u3000\u9ec4\u7075\u4e00\u76b1\u7709\u5934\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u7384\u5a18\uff0c\u5c3d\u4f60\u5168\u529b\u62d2\u654c\uff0c\u6211\u5148\u52a9\u5999\u771f\u62d2\u654c\u3002\u201d \u3000\u3000\u4ed6\u51b7\u773c\u65c1\u89c2\uff0c\u53d1\u89c9\u4e86\u7384\u5a18\u548c\u534e\u5999\u771f\u867d\u7136\u5728\u62d2\u654c\u4e4b\u65f6\uff0c\u90fd\u5341\u5206\u5403\u529b\uff0c\u4f46\u7384\u5a18\u8eab\u8d1f\u591a\u79cd\u5947\u6280\uff0c\u6bcf\u9047\u9669\u62db\u4e4b\u65f6\uff0c\u90fd\u7528\u4e00\u62db\u5947\u5b66\uff0c\u7834\u89e3\u53bb\u5371\u96be\u3002 \u3000\u3000\u534e\u5999\u771f\u5374\u51ed\u501f\u672c\u8eab\u7684\u529f\u529b\uff0c\u786c\u884c\u6297\u62d2\u3002 \u3000\u3000\u5979\u672c\u6765\u6709\u4e00\u8eab\u6697\u5668\u529f\u592b\uff0c\u4f46\u5374\u4e00\u76f4\u65e0\u6cd5\u65bd\u5c55\u3002 \u3000\u3000\u5bf9\u65b9\u7684\u538b\u529b\u5f3a\u5927\uff0c\u534e\u5999\u771f\u5df2\u65e0\u6cd5\u7a7a\u51fa\u624b\u6765\uff0c\u65bd\u5c55\u6697\u5668\u3002 \u3000\u3000\u4e24\u4e8b\u6743\u8861\uff0c\u9ec4\u7075\u89c9\u7740\u5e94\u8be5\u5148\u52a9\u534e\u5999\u771f\u4e00\u81c2\u4e4b\u529b\u3002 \u3000\u3000\u4ed6\u4e00\u8fde\u653b\u51fa\u4e86\u5341\u56db\u4e94\u62db\uff0c\u4f46\u7adf\u7136\u672a\u80fd\u4f24\u5f97\u4e86\u5bf9\u65b9\u3002 \u3000\u3000\u8fd9\u4f7f\u5f97\u9ec4\u7075\u5fc3\u4e2d\u5927\u4e3a\u9707\u60ca\u3002 \u3000\u3000\u8fd9\u4e9b\u6b8b\u5251\u6740\u624b\uff0c\u7adf\u7136\u662f\u5982\u6b64\u7684\u96be\u5bf9\u4ed8\u3002 \u3000\u3000\u4f46\u9ad8\u5e7d\u5170\u548c\u5434\u53cc\u5973\u90a3\u4e00\u9762\uff0c\u5374\u5df2\u5f97\u5230\u4e86\u6781\u5927\u7684\u4f18\u52bf\u3002 \u3000\u3000\u4e03\u8272\u521b\uff0c\u662f\u5929\u4e0b\u6700\u5947\u5e7b\u7684\u5251\u6cd5\uff0c\u5b83\u4e0d\u7b97\u6700\u597d\u7684\u6b66\u529f\uff0c\u4f46\u5374\u662f\u6700\u597d\u7684\u6740\u4eba\u5251\u6cd5\u3002 \u3000\u3000\u5728\u7ea2\u82b1\u3001\u7eff\u53f6\u7684\u914d\u5408\u4e4b\u4e0b\uff0c\u4e03\u8272\u5251\u53d1\u6325\u51fa\u4e86\u6781\u81f4\u7684\u5a01\u529b\u3002 \u3000\u3000\u5434\u53cc\u5973\u7b49\u7684\u7cbe\u5bc6\u5408\u4f5c\uff0c\u4e0d\u5230\u5341\u62db\uff0c \u5df2\u6740\u4e86\u56db\u4e2a\u6b8b\u5251\u624b\u3002 \u867d\u7136\u53ea\u5269\u4f59\u4e0b\u4e86\u56db\u4e2a\u4eba\uff0c\u4f46\u56db\u4e2a\u4eba\u7684\u6297\u62d2\u4e4b\u529b\u4ecd\u7136\u5341\u5206\u5f3a\u5927\u3002 \u3000\u3000\u4ed6\u4eec\u5f88\u987d\u5f3a\uff0c\u867d\u7136\u5df2\u7ecf\u6b7b\u4e86\u56db\u4e2a\u4eba\uff0c\u4f46\u4f59\u4e0b\u7684\u56db\u4e2a\u4eba\uff0c\u597d\u50cf\u5b8c\u5168\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\uff0c\u4ecd\u7136\u662f\u6325\u5251\u82e6\u6218\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u51b7\u559d\u9053\uff1a \u201c\u4f60\u4eec\u5982\u6b64\u987d\u51a5\u4e0d\u5316\uff0c\u522b\u602a\u6211\u65bd\u4e0b\u6bd2\u624b\u4e86\u3002\u201d \u559d\u58f0\u4e2d\u4e03\u5251\u5e76\u51fa\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u4e03\u8272\u5251\uff0c\u95ea\u8d77\u5b50\u4e03\u9053\u5149\u534e\u3002 \u3000\u3000\u4f46\u95fb\u51e0\u58f0\u60e8\u53eb\uff0c\u8fde\u7ef5\u800c\u8d77\u3002 \u3000\u3000\u56db\u4e2a\u4eba\uff0c\u53c8\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7b2c\u4e00\u6b21\u7528\u51fa\u4e86\u4e03\u8272\u5251\u3002 \u3000\u3000\u4e03\u5251\u9f50\u51fa\uff0c\u679c\u7136\u662f\u5a01\u529b\u975e\u51e1\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u6740\u4e86\u4e00\u6279\u6b8b\u5251\u95e8\u4e2d\u4eba\uff0c \u56de\u5934\u53d1\u89c9\u4e86\u9ec4\u7075\u7b49\u4eba\uff0c\u4ecd\u7136\u9677\u5165\u82e6\u6218\u3002 \u8fd9\u65f6\uff0c\u9ec4\u7075\u4e1a\u5df2\u6539\u53d8\u4e86\u6218\u6cd5\uff0c\u4f55\u5bd2\u8863\u3001\u7384\u5a18\u3001\u534e\u5999\u771f\u3001\u53f6\u957f\u9752\u3001\u4e07\u80dc\uff0c\u96c6\u4e2d\u4e00\u5904\u3002 \u3000\u3000\u6b8b\u5251\u95e8\u7684\u516b\u4e2a\u5251\u624b\uff0c\u628a\u51e0\u4eba\u56e2\u56e2\u7684\u56f4\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u6837\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u53cc\u65b9\u9762\uff0c\u53cd\u800c\u53d8\u6210\u4e86\u534a\u65a4\u516b\u4e24\u4e4b\u5c40\u3002 \u3000\u3000\u516b\u4e2a\u6b8b\u5251\u624b\uff0c\u5251\u5149\u4ea4\u7ec7\uff0c\u5df2\u6210\u4e86\u4e00\u7247\u5251\u5e55\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u56de\u987e\u4e86\u9ec4\u7075\u7b49\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u59ca\uff0c\u54b1\u4eec\u53bb\u52a9\u9ec4\u5821\u4e3b\u4e00\u81c2\uff0c\u5148\u628a\u8fd9\u4e9b\u6b8b\u5251\u624b\u7ed9\u6d88\u706d\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u597d!\u6740\u5149\u8fd9\u4e00\u6279\u4eba\uff0c\u770b\u770b\u9ad8\u5929\u5065\u8fd8\u6709\u4ec0\u4e48\u5b9e\u529b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u8f6c\u5411\u897f\u65b9\u7684\u6b8b\u5251\u624b\u65f6\uff0c\u70df\u9501\u697c\u4e0a\u4e5f\u6709\u4e86\u53cd\u5e94\u3002 \u3000\u3000\u94c1\u95e8\u7a81\u7136\u5927\u5f00\uff0c\u9ad8\u5929\u5065\u5e26\u7740\u4e00\u6279\u4eba\u624b\uff0c\u51b2\u4e86\u51fa\u6765\u3002\u5c1a\u5929\u4e49\u3001\u5c1a\u592b\u4eba\uff0c\u5e26\u7740\u4e00\u6279\u5200\u624b\uff0c\u7acb\u523b\u4e00\u62e5\u800c\u4e0a\uff0c\u5835\u5728\u4e86\u5927\u95e8\u5904\uff0c\u5c1a\u592b\u4eba\u7684\u62d0\u6756\uff0c\u914d\u5408\u7740\u5341\u51e0\u628a\u4ea4\u66ff\u653b\u51fa\u7684\u5feb\u5200\uff0c\u7d27\u7d27\u5730\u628a\u95e8\u53e3\u5c01\u4f4f\u3002\u5c1a\u5929\u4e49\u8fde\u7eed\u53d1\u51fa\u4e09\u62db\u5929\u7edd\u6307\uff0c\u903c\u5f97\u4ed6\u4eec\u51b2\u51fa\u6765\u7684\u4eba\uff0c\u53c8\u9000\u4e86\u56de\u53bb\u3002 \u3000\u3000\u5c1a\u592b\u4eba\u7684\u6253\u6cd5\u662f\u5148\u6c42\u65e0\u8fc7\uff0c\u6240\u4ee5\uff0c\u76f4\u903c\u5230\u697c\u95e8\u53e3\u5904\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u9ad8\u5929\u5065\u7387\u9886\u7684\u4eba\u624b\uff0c\u5168\u88ab\u5835\u5728\u4e86\u697c\u95e8\u53e3\u5904\u3002 \u3000\u3000\u5bf9\u65b9\u7684\u4eba\u624b\uff0c\u867d\u6709\u4e0d\u5c11\uff0c\u4f46\u4ed6\u5374\u65e0\u6cd5\u51b2\u51fa\u6765\u3002 \u3000\u3000\u6574\u4e2a\u95e8\u53e3\uff0c\u88ab\u5c1a\u592b\u4eba\u7684\u62d0\u6756\u548c\u5200\u5149\u5835\u6b7b\uff0c\u518d\u914d\u4e0a\u4e86\u5c1a\u5929\u4e49\u7684\u5929\u7edd\u6307\uff0c\u4e00\u4e2a\u4eba\u60f3\u51b2\u51fa\u6765\uff0c\u5b9e\u975e\u6613\u4e8b\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u5c1a\u5929\u4e49\uff0c\u4f60\u4eec\u592b\u5987\u4e00\u5b9a\u8981\u548c\u8001\u592b\u4f5c\u5bf9\u5417?\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u54b1\u4eec\u5df2\u7ecf\u548c\u4f60\u4f5c\u5bf9\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u6012\u9053\uff1a\u201c\u597d!\u90a3\u8001\u592b\u5c31\u5148\u6740\u4e86\u4f60\u4eec\u3002\u201d \u3000\u3000\u53f3\u624b\u7a81\u7136\u4e00\u6325\uff0c\u5200\u5149\u4e00\u95ea\uff0c\u76f4\u5288\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5c1a\u592b\u4eba\u6325\u62d0\u4e00\u67b6\uff0c\u4f46\u95fb\u5f53\u7684\u4e00\u58f0\uff0c\u7adf\u88ab\u5f39\u9707\u5f00\u53bb\u3002 \u3000\u3000\u5c1a\u5929\u4e49\u4e00\u626c\u624b\uff0c\u4e00\u7f15\u6307\u98ce\u88ad\u4e86\u8fc7\u53bb\uff0c\u56db\u628a\u957f\u5200\uff0c\u4e5f\u540c\u65f6\u5377\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e07\u80dc\u82b1\u4e86\u5f88\u5927\u529f\u592b\uff0c\u8bad\u7ec3\u51fa\u6765\u7684\u5200\u6cd5\uff0c\u679c\u7136\u975e\u540c\u51e1\u54cd\u3002 \u3000\u3000\u4ed6\u4eec\u90a3\u9010\u5c42\u5206\u6ce2\u7684\u653b\u52bf\uff0c\u8fde\u7ef5\u6210\u4e00\u7247\u5200\u7f51\uff0c\u628a\u70df\u9501\u697c\u7684\u95e8\u53e3\u5b8c\u5168\u5c01\u4f4f\u3002 \u3000\u3000\u4f7f\u5f97\u697c\u4e2d\u4eba\uff0c\u65e0\u6cd5\u51b2\u5230\u95e8\u5916\uff0c\u9694\u7edd\u4e86\u697c\u5185\u697c\u5916\u7684\u5173\u8fde\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u4ed6\u4eec\u62e6\u4e0d\u4f4f\u9ad8\u5929\u5065\u3002 \u3000\u3000\u4e2d\u5dde\u7b2c\u4e00\u5200\uff0c\u4e0d\u662f\u6d6a\u5f97\u865a\u540d\uff0c\u53ea\u89c1\u4ed6\u5200\u5149\u7eb5\u6a2a\uff0c\u6709\u5982\u5de8\u6d6a\u6392\u7a7a\u4e00\u822c\uff0c\u7247\u523b\u5de5\u592b\uff0c\u5f3a\u5389\u7684\u5200\u52bf\uff0c\u7adf\u628a\u56f4\u5835\u5728\u697c\u95e8\u53e3\u7684\u5200\u624b\uff0c\u8feb\u5411\u4e24\u4fa7\u3002 \u3000\u3000\u5c1a\u5929\u4e49\u8fde\u53d1\u4e24\u8bb0\u5929\u6307\u7edd\uff0c\u5c1a\u592b\u4eba\u6025\u653b\u4e94\u638c\uff0c\u4f46\u5374\u65e0\u6cd5\u963b\u62e6\u4f4f\u9ad8\u5929\u5065\u7684\u653b\u52bf\u3002 \u3000\u3000\u9ad8\u5929\u5065\u51b2\u51fa\u4e86\u70df\u9501\u697c\u3002 \u3000\u3000\u56db\u4e2a\u5200\u624b\uff0c\u4f24\u5728\u4e86\u9ad8\u5929\u5065\u7684\u5200\u4e0b\u3002 \u3000\u3000\u5c1a\u592b\u4eba\u4e5f\u88ab\u524a\u53bb\u4e86\u5934\u4e0a\u4e00\u7efa\u767d\u53d1\u3002 \u3000\u3000\u9ad8\u5929\u5065\u51b2\u51fa\u4e86\u70df\u9501\u697c\uff0c\u7528\u5fc3\u5728\u63a5\u5e94\u6b8b\u5251\u624b\u3002 \u3000\u3000\u4f46\u4ed6\u665a\u4e86\u4e00\u6b65\uff0c\u5341\u516d\u540d\u6b8b\u5251\u624b\uff0c \u5df2\u5168\u6570\u6388\u9996\u3002 \u4e00\u5927\u534a\u6b7b\u5728\u9ad8\u5e7d\u5170\u7684\u4e03\u8272\u5251\u4e0b\u3002 \u3000\u3000\u4e5f\u8bb8\u4e03\u8272\u5251\u5e76\u4e0d\u662f\u5929\u4e0b\u6700\u7cbe\u5947\u7684\u5251\u6cd5\uff0c\u4e5f\u4e0d\u662f\u6700\u6df1\u5965\u7684\u5251\u6cd5\u3002 \u3000\u3000\u4f46\u5374\u662f\u6700\u51cc\u5389\u3001\u6700\u6b8b\u9177\u3001\u6700\u5947\u5e7b\u7684\u5251\u6cd5\u3002 \u3000\u3000\u4e03\u5251\u5e76\u98de\uff0c\u8000\u773c\u751f\u82b1\uff0c\u8fd8\u6ca1\u6709\u770b\u6e05\u695a\u662f\u600e\u4e48\u56de\u4e8b\uff0c \u4eba\u5df2\u6b7b\u5728\u5251\u4e0b\u3002 \u5c31\u8fde\u8eab\u8d1f\u5341\u4f59\u79cd\u7edd\u6280\u7684\u9ec4\u7075\uff0c\u4e5f\u770b\u5f97\u76ee\u5446\u795e\u75f4\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u60f3\u51fa\uff0c\u4e03\u5251\u4e34\u5934\u65f6\uff0c\u5982\u4f55\u4e00\u4e2a\u6297\u62d2\u4e4b\u6cd5\u3002 \u3000\u3000\u7384\u5a18\u57cb\u9996\u5341\u5e74\uff0c\u82e6\u7814\u6b66\u529f\uff0c\u4e0d\u60dc\u82b1\u8d39\u4e86\u751f\u5e73\u7684\u79ef\u84c4\uff0c\u81ea\u4fe1\u5df2\u5f88\u6709\u6210\u5c31\uff0c\u5979\u539f\u8981\u51ed\u4ed7\u5341\u5e74\u82e6\u5b66\u6765\u6740\u6b7b\u9ec4\u7075\uff0c\u4f46\u4e86\u89e3\u4e86\u9ec4\u7075\u4e4b\u540e\uff0c\u53cd\u800c\u65e7\u60c5\u590d\u71c3\uff0c\u6ee1\u8154\u6028\u6068\uff0c\u53c8\u5316\u6210\u4e00\u7f15\u67d4\u60c5\u3002 \u3000\u3000\u5728\u5bf9\u6b8b\u5251\u6740\u624b\u8fc7\u7a0b\u4e2d\uff0c\u7384\u5a18\u53d1\u89c9\u4e86\u4e00\u4ef6\u4e8b\uff0c\u90a3\u5c31\u662f\u9ec4\u7075\u7684\u6b66\u529f\uff0c\u6bd4\u5979\u8fd8\u9ad8\u660e\u5f88\u591a\u3002 \u3000\u3000\u518d\u7ec3\u5341\u5e74\uff0c\u4e5f\u672a\u5fc5\u662f\u9ec4\u7075\u7684\u654c\u624b\u3002 \u3000\u3000\u5bf9\u4e03\u8272\u5251\u7684\u5a01\u529b\uff0c\u7384\u5a18\u66f4\u662f\u521d\u6b21\u89c1\u5230\uff0c \u3000\u3000\u53ea\u89c9\u90a3\u8000\u773c\u751f\u82b1\u7684\u5251\u5149\uff0c\u6839\u672c\u4f7f\u4eba\u6709\u7740\u770b\u4e0d\u6e05\u695a\u7684\u611f\u89c9\u3002 \u3000\u3000\u7384\u5a18\u4f4e\u58f0\u53f9\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u9ec4\u7075\uff0c\u6211\u5b66\u4e86\u5927\u534a\u751f\u7684\u6b66\u529f\uff0c\u770b\u6765\uff0c\u4e5f\u63a5\u4e0d\u4e0b\u9ad8\u59d1\u5a18\u4e00\u5251\u3002\u201d \u3000\u3000\u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4e00\u62db\u4e03\u5251\u5e76\u98de\uff0c\u53ea\u6015\u4e5f\u82b1\u53bb\u4e86\u9ad8\u59d1\u5a18\u4e0d\u5c11\u7684\u65f6\u95f4\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7f13\u6b65\u884c\u8fc7\u6765\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u771f\u529f\u592b\uff0c\u78b0\u4e0a\u4e86\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\u4e4b\u540e\uff0c\u7acb\u523b\u5c31\u5931\u53bb\u4e86\u4f5c\u7528\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u59d1\u5a18\u5ba2\u6c14\uff0c\u8fd9\u662f\u6211\u770b\u5230\u5251\u6cd5\u4e2d\uff0c\u6700\u51cc\u5389\u7684\u5251\u6cd5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54b1\u4eec\u4f55\u4e0d\u63a5\u7740\u6740\u4e0a\u70df\u9501\u697c\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b2\u7834\u4e86\u5bf9\u5c01\u70df\u9501\u697c\u7684\u5200\u9635\uff0c\u4f46\u5374\u81ea\u884c\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4ed6\u5df2\u7ecf\u53d1\u89c9\u4e86\u6b8b\u5251\u624b\u5df2\u5168\u90e8\u88ab\u6740\u3002 \u3000\u3000\u4ed6\u51b2\u51fa\u6765\u7684\u76ee\u7684\uff0c\u5c31\u662f\u8981\u63a5\u5e94\u8fd9\u6279\u5251\u624b\uff0c\u73b0\u5728\uff0c\u4ed6\u53ea\u597d\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5446\u5446\u7684\u7ad9\u5728\u70df\u9501\u697c\u5916\uff0c\u671b\u7740\u90a3\u4e9b\u5012\u4e0b\u53bb\u7684\u5c38\u4f53\u51fa\u795e\u3002 \u3000\u3000\u5c1a\u5929\u4e49\u3001\u5c1a\u592b\u4eba\uff0c\u672a\u518d\u51fa\u624b\u62a2\u653b\u3002 \u3000\u3000\u4ed6\u4eec\u5df2\u7ecf\u9886\u6559\u4e86\u7b2c\u4e00\u5200\u7684\u5389\u5bb3\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\uff0c\u5200\u6cd5\u4e0a\u9020\u8be3\u4e4b\u9ad8\uff0c\u653e\u773c\u5929\u4e0b\uff0c\u5b9e\u4e0d\u4f5c\u7b2c\u4e8c\u4eba\u60f3\u3002 \u3000\u3000\u9ec4\u7075\u559d\u9000\u4e86\u5200\u624b\uff0c\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u9053\uff1b \u201c\u9ad8\u5929\u5065\uff0c\u73b0\u5728\uff0c\u54b1\u4eec\u8be5\u4e00\u5206\u80dc\u8d1f\u4e86\u3002\u201d \u9ad8\u5929\u5065\u795e\u60c5\u6728\u7136\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u628a\u4ed6\u4eec\u5168\u90e8\u6740\u6b7b\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4eec\u5927\u90e8\u5206\u6b7b\u4e8e\u9ad8\u59d1\u5a18\u7684\u4e03\u8272\u5251\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5509!\u6211\u65e9\u8be5\u6740\u4e86\u5979\u7684\u3002\u201d \u3000\u3000\u9ec4\u7075\u51b7\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u73b0\u5728\uff0c\u9601\u4e0b\u540e\u6094\u4e86?\u201d \u3000\u3000\u9ad8\u5929\u5065\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5982\u4eca\u6094\u6068\u5df2\u8fdf\u3002\u201d \u3000\u3000\u201c\u4e0d\u8fdf\u3002\u201d\u9ad8\u5e7d\u5170\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\u3002\u201c\u6211\u8fd8\u6d3b\u7740\uff0c\u4f60\u53ef\u4ee5\u66ff\u4ed6\u4eec\u62a5\u4ec7\uff0c\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u53ef\u662f\u89c9\u7740\uff0c\u4e03\u8272\u5251\uff0c\u771f\u7684\u5929\u4e0b\u65e0\u654c\u4e86\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u5b83\u6740\u4eba\u7684\u529b\u91cf\u5341\u5206\u5f3a\u5927\uff0c\u6b8b\u5251\u624b\uff0c\u6709\u4e0d\u5c11\u6b7b\u5728\u4e86\u6211\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\uff0c\u4e5f\u80fd\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e0d\u7ba1\u80fd\u4e0d\u80fd\u6740\u4e86\u4f60\uff0c\u6211\u4eec\u603b\u8981\u6709\u4e00\u573a\u6fc0\u70c8\u7684\u640f\u6740\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u597d! \u4f60\u8bd5\u8bd5\u770b\uff0c \u4e03\u8272\u5251\u80fd\u4e0d\u80fd\u6740\u4e86\u8001\u592b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4f1a\u8bd5\u9a8c\uff0c\u4e0d\u8fc7\uff0c\u5728\u6211\u4eec\u6ca1\u6709\u52a8\u624b\u4e4b\u95f4\uff0c\u6211\u6709\u51e0\u4ef6\u4e0d\u89e3\u4e4b\u5904\uff0c\u8bf7\u6559\u9601\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u6839\u672c\u5c31\u4e0d\u59d3\u9ad8\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u65e9\u77e5\u9053\u4e86?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u55ef!\u6211\u59d3\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8001\u592b\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4f60\u6740\u4e86\u4ed6\u4eec\uff0c\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e8b\u5df2\u5982\u6b64\uff0c\u96be\u9053\u4f60\u8fd8\u4e0d\u6562\u627f\u8ba4\u5417\uff1f\u201d \u3000\u3000\u9ad8\u5929\u5065\u8bf4\uff1a \u201c\u6211\u9ad8\u5929\u5065\u6562\u505a\u6562\u5f53\uff0c\u6211\u5982\u6740\u4e86\u4f60\u7684\u7236\u6bcd\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6562\u627f\u8ba4\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u628a\u6211\u6536\u5165\u7b2c\u4e00\u5821\u4e2d\u7684\u7ecf\u8fc7\u5462?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u4f60\u53ea\u662f\u5b64\u513f\uff0c\u8001\u592b\u628a\u4f60\u5e26\u5165\u7b2c\u4e00\u5821\u65f6\uff0c\u4f60\u8fd8\u4e0d\u662f\u4e24\u5c81\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u628a\u6211\u517b\u5927\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f46\u6211\u5341\u51e0\u5e74\u6765\uff0c\u5374\u81ea\u5df1\u517b\u4e86\u4e00\u4e2a\u654c\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u53ea\u53ef\u60dc\uff0c\u7ecf\u4e0d\u8d77\u4e00\u70b9\u8003\u9a8c\uff0c\u4f60\u5c31\u5fcd\u5fc3\u5bf9\u6211\u4e0b\u6bd2\uff0c\u51e0\u4e4e\u5bb3\u5f97\u6211\u4e07\u52ab\u4e0d\u590d\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u4f60\u52fe\u7ed3\u4f55\u5bd2\u8863\uff0c\u6211\u4e0d\u6740\u4f60\uff0c\u5c31\u662f\u5bf9\u4f60\u624b\u4e0b\u7559\u60c5\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e8b\u60c5\u672c\u4e0d\u4f1a\u5982\u4f60\u60f3\u7684\u90a3\u4e48\u6076\u5316\uff0c\u4f46\u4f60\u5374\u628a\u5b83\u641e\u574f\u4e86\u3002\u201d \u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u6211\u4e0d\u4fe1\uff0c\u6211\u4e0d\u5bf9\u4f60\u4e0b\u6bd2\uff0c\u4f60\u5c31\u4e0d\u4f1a\u80cc\u53db\u6211\u3002\u201d \u9ad8\u5e7d\u5170\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8bd5\u8bd5\uff0c\u4f60\u6ca1\u6709\u52c7\u6c14\u5c1d\u8bd5\uff0c\u4f60\u4e0d\u6562\u5192\u4e00\u70b9\u9669\uff0c\u4f60\u8981\u628a\u522b\u4eba\u7f6e\u4e8e\u6b7b\u5730\uff0c\u638c\u63e1\u522b\u4eba\u4e00\u5207\u5951\u673a\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u592b\u786e\u7136\u5982\u6b64\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7f13\u7f13\u63e1\u4f4f\u4e86\u5251\u67c4\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u53ef\u4ee5\u4e00\u51b3\u751f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u597d!\u4f60\u5148\u51fa\u5251\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u53cc\u624b\u63e1\u4f4f\u4e86\u56db\u628a\u5251\u3002 \u3000\u3000\u5979\u7684\u624b\u4e0d\u505c\u7684\u53d1\u6296\uff0c\u4f3c\u4e4e\u662f\u5ffd\u7136\u95f4\uff0c\u63e1\u4e0d\u7a33\u4e86\u5b9d\u5251\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u653e\u5f00\u4e86\u5251\u67c4\uff0c\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u7b54\u590d\u6211\u4e00\u53e5\u771f\u8bdd\uff0c\u4f60\u6709\u6ca1\u6709\u6740\u4e86\u6211\u7684\u7236\u6bcd?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u662f\u771f\u7684?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8001\u592b\u6740\u4e86\u51e0\u767e\u4eba\u4e4b\u591a\uff0c\u8fd8\u5728\u4e4e\u591a\u627f\u8ba4\u4e00\u4e2a\u7f6a\u540d\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7a81\u7136\u8f6c\u8fc7\u8eab\u5b50\uff0c\u7f13\u6b65\u800c\u53bb\u3002 \u3000\u3000\u60f3\u5230\u4e86\u5341\u51e0\u5e74\u7684\u517b\u80b2\u4e4b\u6069\uff0c\u5979\u5b9e\u5728\u65e0\u529b\u51fa\u624b\u3002 \u3000\u3000\u4f46\u9ad8\u5929\u5065\u5374\u770b\u5230\u4e86\u6700\u597d\u7684\u673a\u4f1a\u3002 \u3000\u3000\u5ffd\u7136\u4e00\u626c\u624b\uff0c\u4e00\u9053\u5bd2\u8292\uff0c\u75be\u98de\u800c\u53bb\u3002 \u3000\u3000\u4e00\u628a\u98de\u5200\u3002 \u3000\u3000\u4e0d\u77e5\u9053\u9ad8\u5929\u5065\u7528\u7684\u4ec0\u4e48\u624b\u6cd5\uff0c\u8fd9\u4e00\u5200\u7adf\u7136\u662f\u65e0\u58f0\u65e0\u606f\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u65e0\u5168\u6240\u89c9\u3002 \u3000\u3000\u4f46\u9ec4\u7075\u5374\u7a81\u7136\u62d4\u5251\u626b\u51fa\uff0c\u5f53\u7684\u4e00\u58f0\u51fb\u843d\u98de\u5200\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u970d\u7136\u56de\u8eab\uff0c\u51b7\u51b7\u5730\u671b\u4e86\u9ad8\u5929\u5065\u4e00\u773c\uff0c\u9053\uff1a \u201c\u8fd9\u4e00\u5200\uff0c\u65ad\u53bb\u4e86\u4f60\u5bf9\u6211\u5341\u51e0\u5e74\u517b\u80b2\u4e4b\u60c5\uff0c\u6211\u5df2\u4e0d\u518d\u6b20\u4f60\u4ec0\u4e48\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u597d!\u59d1\u5a18\u65e2\u7136\u5df2\u7ecf\u4e0d\u6b20\u4ed6\u4ec0\u4e48\uff0c\u53ef\u4ee5\u5fc3\u5e73\u6c14\u548c\u4e86\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u7b2c\u4e00\u9635\uff0c\u8fd8\u662f\u8ba9\u7ed9\u6211\u9ec4\u67d0\u5427?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\uff0c\u591a\u591a\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u3001\u53f6\u957f\u9752\u3001\u4e07\u80dc\u3001\u534e\u5999\u771f\uff0c\u5168\u90fd\u56f4\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5df2\u7ecf\u5931\u53bb\u4e86\u9547\u9759\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u4f60\u4eec\u51c6\u5907\u4e00\u9f50\u4e0a\u5462?\u8fd8\u662f\u8f66\u8f6e\u5927\u6218\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8f66\u8f6e\u5927\u6218\u4e5f\u597d\uff0c\u4e00\u9f50\u56f4\u653b\u4e5f\u597d\uff0c\u53cd\u6b63\uff0c\u5bf9\u4f60\u8fd9\u79cd\u4eba\uff0c\u7528\u4e0d\u7740\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u9053\u4e49\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d!\u4f60\u4eec\u54ea\u4e00\u4e2a\u5148\u4e0a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\uff01\u201d \u3000\u3000\u91d1\u5251\u4e00\u632f\uff0c\u5411\u4e0a\u6251\u53bb\u3002 \u3000\u3000\u4f46\u9ec4\u7075\u6bd4\u4ed6\u66f4\u5feb\uff0c\u53f3\u624b\u4e00\u62ac\uff0c\u4e00\u9053\u5bd2\u5149\uff0c\u7531\u8896\u4e2d\u5c04\u51fa\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u4f55\u5144\uff0c\u5148\u8bf7\u9000\u4e0b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6ca1\u6709\u9000\u4e0b\u3002 \u3000\u3000\u4f46\u5374\u88ab\u9ad8\u5929\u5065\u4e00\u5200\u7ed9\u5c01\u6321\u5f00\u53bb\u3002 \u3000\u3000\u9ad8\u5929\u5065\u4e00\u5200\u5c01\u6321\u5f00\u4e24\u4e2a\u4eba\u7684\u653b\u52bf\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7684\u4e00\u5bf9\u91d1\u5251\uff0c\u9ec4\u7075\u4e00\u628a\u8f6f\u5251\u3002 \u3000\u3000\u501f\u52bf\u8fd8\u51fb\uff0c\u5200\u5149\u4e00\u95ea\uff0c\u653b\u5411\u4e86\u9ec4\u7075\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u75be\u9000\u4e03\u5c3a\uff0c\u6b63\u51c6\u5907\u518d\u653b\u4e0a\uff0c\u5374\u88ab\u53f6\u957f\u9752\u62e6\u4f4f\u3002\u4f4e\u58f0\u9053\uff1a\u201c\u4f55\u5144\uff0c\u8ba9\u5821\u4e3b\u548c\u4ed6\u5355\u6253\u72ec\u6597\u4e00\u9635\u518d\u8bf4\u3002\u201d \u3000\u3000\u4e24\u4eba\u8fd9\u4e00\u9635\u8bf4\u8bdd\u7684\u5de5\u592b\uff0c\u9ad8\u5929\u5065\u548c\u9ec4\u7075\u5df2\u7ecf\u4ea4\u624b\u5341\u4f59\u62db\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u6c5f\u6e56\u4e0a\u7f55\u95fb\u7f55\u89c1\u7684\u6253\u6597\uff0c\u53cc\u65b9\u90fd\u662f\u4ee5\u5feb\u6253\u5feb\u3002 \u3000\u3000\u4f46\u89c1\u5200\u5251\u95ea\u5149\uff0c\u53cc\u65b9\u7684\u53d8\u5316\uff0c\u5df2\u4e0d\u662f\u76ee\u5149\u6240\u80fd\u770b\u5f97\u6e05\u695a\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u573a\u62a2\u5236\u5148\u673a\u7684\u5feb\u653b\u3002 \u3000\u3000\u7384\u5a18\u5706\u7741\u661f\u76ee\uff0c\u5168\u795e\u8d2f\u6ce8\u5728\u53cc\u65b9\u7684\u51b3\u6597\u4e4b\u4e0a\uff0c\u4e00\u9762\u84c4\u52bf\u6212\u5907\u3002 \u3000\u3000\u4f46\u5979\u4ecd\u7136\u65e0\u6cd5\u770b\u5f97\u6e05\u695a\u53cc\u65b9\u640f\u6597\u7684\u60c5\u5f62\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u4e24\u4eba\u7684\u5200\u5251\u53d8\u5316\u592a\u5feb\u4e86\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u5200\u5149\u3001\u5251\u5f71\uff0c\u5374\u4e0d\u89c1\u4eba\u8e2a\u3002 \u3000\u3000\u5076\u5c14\u54cd\u8d77\u4e86\u4e00\u58f0\u5200\u5251\u4ea4\u89e6\u7684\u58f0\u97f3\uff0c\u5212\u7834\u4e86\u6708\u591c\u9759\u5bc2\u3002 \u3000\u3000\u53cc\u65b9\u90fd\u5c4f\u606f\u51dd\u795e\u89c2\u6218\u3002 \u3000\u3000\u4e00\u987f\u996d\u65f6\u95f4\u8fc7\u53bb\u4e86\uff0c\u53cc\u65b9\u4ecd\u7136\u662f\u4e00\u4e2a\u7f20\u6597\u96be\u5206\u4e4b\u5c40\u3002 \u3000\u3000\u53f6\u957f\u9752\u5401\u4e00\u53e3\u6c14\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u4f55\u5144\uff0c\u770b\u6765\u9ec4\u5821\u4e3b\u5b9e\u5728\u6bd4\u6211\u4eec\u9ad8\u660e\u5f88\u591a\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u73b0\u5728\uff0c\u54b1\u4eec\u7ec8\u4e8e\u89c1\u5230\u4ed6\u771f\u6b63\u7684\u6b66\u529f\u4e86\u3002\u201d \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u54cd\u8d77\u4e24\u58f0\u5389\u559d\u3001\u6012\u53f1\uff0c\u7f20\u6597\u5728\u4e00\u5904\u7684\u9ad8\u5929\u5065\u548c\u9ec4\u7075\uff0c\u970d\u7136\u5206\u5f00\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u9ec4\u7075\u957f\u53d1\u6563\u62ab\uff0c\u524d\u80f8\u8863\u7269\u7834\u88c2\uff0c\u9c9c\u8840\u6d41\u51fa\u3002 \u3000\u3000\u4ed6\u7684\u675f\u6b66\u58eb\u5dfe\uff0c\u5df2\u88ab\u9ad8\u5929\u5065\u524a\u53bb\u3002 \u3000\u3000\u524d\u80f8\u5904\u53d7\u4e86\u4f24\uff0c\u800c\u4e14\uff0c\u6b63\u6d41\u7740\u9c9c\u8840\u3002 \u3000\u3000\u7384\u5a18\u75be\u5954\u8fc7\u53bb\uff0c\u6025\u9053\uff1a\u201c\u4f24\u7684\u5f88\u91cd\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd8\u597d\u3002\u201d \u3000\u3000\u7384\u5a18\u62c9\u5f00\u4e86\u9ec4\u7075\u7684\u8863\u670d\uff0c\u4f46\u89c1\u4ed6\u524d\u80f8\u5904\u4e00\u9053\u8840\u53e3\uff0c\u8db3\u8db3\u6709\u4e94\u5bf8\u591a\u957f\u3002 \u3000\u3000\u770b\u4e0d\u51fa\u6709\u591a\u6df1\uff0c\u4f46\u89c1\u9c9c\u8840\u6d8c\u6d41\u4e4b\u52bf\uff0c\u4f24\u7684\u4e0d\u4f1a\u592a\u6df1\u3002 \u3000\u3000\u5c31\u5728\u7384\u5a18\u5954\u5411\u9ec4\u7075\u7684\u540c\u65f6\uff0c\u4f55\u5bd2\u8863\u548c\u53f6\u957f\u9752\u5df2\u62e6\u5728\u4e86\u9ec4\u7075\u7684\u524d\u8eab\u3002 \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u8fd8\u6562\u548c\u8001\u592b\u518d\u6218\u4e09\u5408\u5417?\u201d \u3000\u3000\u9ec4\u7075\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a \u201c\u4e2d\u5dde\u7b2c\u4e00\u5200\uff0c\u679c\u7136\u975e\u51e1\uff0c\u4f46\u4f60\u6ca1\u6709\u6740\u4e86\u6211\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u5982\u6562\u548c\u8001\u592b\u518d\u52a8\u624b\uff0c\u6211\u4fdd\u8bc1\u5728\u767e\u62db\u4e4b\u5185\u53d6\u4f60\u4e4b\u547d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u4f60\u6c38\u4e0d\u4f1a\u6709\u8fd9\u4e2a\u673a\u4f1a\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u3001\u7384\u5a18\uff0c \u5df2\u628a\u9ec4\u7075\u6276\u4e86\u4e0b\u53bb\uff0c\u66ff\u4ed6\u6577\u836f\uff0c\u548c\u5305\u624e\u4f24\u52bf\u3002 \u7384\u5a18\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u9000\u4e0b\u6765\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ad8\u5929\u5065\u7684\u5200\u6cd5\u5982\u86db\u4e1d\u4e00\u822c\uff0c\u88ab\u4ed6\u7f20\u4e0a\u4e86\u5f88\u96be\u8131\u5f00\uff0c\u8981\u4ed6\u611f\u89c9\u5230\u4e0d\u652f\u65f6\uff0c\u7acb\u523b\u62db\u547c\u522b\u4eba\u5e2e\u5fd9\uff0c\u4e0d\u53ef\u901e\u5f3a\u3002\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u8bf4\u7684\u58f0\u97f3\u5df2\u7ecf\u5f88\u5927\uff0c\u7528\u4e0d\u7740\u7384\u5a18\u518d\u8f6c\u8ff0\u4e86\u3002 \u3000\u3000\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u592b\u59bb\u540c\u547d\uff0c\u5bf9\u4f55\u5bd2\u8863\uff0c\u5979\u4eec\u6709\u7740\u4e00\u79cd\u65e0\u6cd5\u8a00\u55bb\u7684\u5173\u5fc3\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5df2\u548c\u4f55\u5bd2\u8863\u3001\u53f6\u6c0f\u9752\u52a8\u4e86\u4e0a\u624b\u3002 \u3000\u3000\u4ed6\u65e0\u58f0\u65e0\u606f\u7684\u653b\u51fa\u4e00\u5200\uff0c\u5200\u52bf\u5982\u7f51\uff0c\u4e00\u4e0b\u5b50\u628a\u4f55\u5bd2\u8863\u548c\u53f6\u957f\u9752\uff0c\u5168\u90e8\u5708\u5165\u4e86\u4e00\u7247\u5200\u8292\u4e4b\u4e2d\u3002 \u3000\u3000\u5434\u53cc\u5973\u539f\u60f3\u66ff\u4e0b\u4f55\u5bd2\u8863\uff0c\u4f46\u5374\u665a\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e00\u53cc\u91d1\u5251\uff0c\u5168\u529b\u65bd\u5c55\u51fa\u6b66\u5f53\u6b63\u5b97\u5251\u6cd5\u3002 \u3000\u3000\u53f6\u957f\u9752\u5374\u5c3d\u91cf\u914d\u5408\u4f55\u5bd2\u8863\u7684\u5251\u6cd5\uff0c\u5c01\u7a7a\u5835\u9699\uff0c\u4f7f\u4f55\u5bd2\u8863\u7684\u5251\u6cd5\uff0c\u53d8\u7684\u66f4\u4e3a\u4e25\u5bc6\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u65bd\u5c55\u8ffd\u9b42\u4e03\u5251\uff0c\u90a3\u662f\u7edd\u5bf9\u653b\u52bf\u7684\u5251\u6cd5\uff0c\u73b0\u5728\uff0c\u4ed6\u4eec\u5b8c\u5168\u662f\u5b88\u52bf\u3002 \u3000\u3000\u9ad8\u5929\u5065\u4e00\u8bed\u4e0d\u53d1\uff0c\u5168\u529b\u8fd0\u7528\uff0c\u4e24\u4eba\u7684\u5251\u5c16\uff0c\u5b8c\u5168\u88ab\u5305\u56f4\u5728\u4e00\u7247\u5200\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u4e07\u80dc\u5401\u4e00\u53e3\u6c14\uff0c\u7f13\u7f13\u4e3e\u8d77\u4e86\u6570\u5341\u65a4\u91cd\u7684\u5927\u5200\u3002 \u3000\u3000\u4ed6\u5df2\u51dd\u805a\u4e86\u5168\u795e\uff0c\u529b\u8d2f\u5200\u8eab\uff0c\u7b49\u7740\u4e00\u4e2a\u6700\u6709\u5229\u7684\u65f6\u673a\uff0c\u5c31\u5168\u529b\u653b\u51fa\u4e00\u5200\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7684\u5200\u7f51\u592a\u5bc6\u4e86\uff0c\u5bc6\u7684\u4e07\u80dc\u5bfb\u4e0d\u51fa\u7a7a\u9699\uff0c\u65e0\u6cd5\u4e0b\u624b\u3002 \u3000\u3000\u4e07\u80dc\u8db3\u8db3\u7b49\u5019\u4e86\u4e00\u523b\u5de5\u592b\u4e4b\u4e45\uff0c\u4ecd\u7136\u662f\u627e\u4e0d\u5230\u51fa\u624b\u7684\u673a\u4f1a\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u5927\u59d0\uff0c\u60c5\u5f62\u6709\u4e9b\u4e0d\u5bf9?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u5bf9?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u770b\uff0c\u9ad8\u5929\u5065\u5df2\u52a8\u4e86\u6740\u673a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4ed6\u5168\u529b\u8fd0\u5200\uff0c\u4e00\u8bed\u4e0d\u53d1\uff0c\u5206\u660e\u662f\u60f3\u65bd\u4e0b\u6bd2\u624b\uff0c\u6740\u4e86\u4f55\u5bd2\u8863\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u8fd9\u7fa4\u4eba\u4e2d\uff0c \u4ed6\u5fc3\u4e2d\u6700\u6068\u7684\uff0c\u6050\u6015\u5c31\u662f\u4f55\u5bd2\u8863\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u80fd\u8ba9\u4ed6\u5f97\u624b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4ed6\u5200\u6cd5\u7ef5\u5bc6\uff0c \u5200\u5251\u5df2\u5377\u5165\u5728\u4e00\u5904\uff0c\u54b1\u4eec\u5c31\u7b97\u60f3\u63d2\u624b\uff0c\u4e5f\u662f\u6709\u6240\u4e0d\u4fbf\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u53eb\u4ed6\uff0c\u6211\u51c6\u5907\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u5ffd\u6709\u7f9e\u610f\uff0c\u9053\uff1a\u201c\u53eb\u4ed6\u7684\u540d\u5b57\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u968f\u4fbf\u4f60\u53eb\u4ec0\u4e48\uff0c\u8981\u4ed6\u542c\u5230\u5c31\u662f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8bf4\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8981\u4ed6\u5168\u529b\u53cd\u51fb\u4e00\u62db\uff0c\u7ed9\u6211\u4e00\u4e2a\u51fa\u624b\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u56de\u987e\u4e86\u534e\u5999\u771f\u4e00\u773c\u3002 \u3000\u3000\u534e\u5999\u771f\u542c\u5230\u4e86\u4e24\u4e2a\u4eba\u7684\u8c08\u8bdd\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u7a81\u7136\u63d0\u9ad8\u4e86\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4f55\u5251\u4f7f\uff0c\u5bf9\u4ed8\u9ad8\u5929\u5065\u8fd9\u79cd\u4eba\uff0c\u4e5f\u4e0d\u7528\u548c\u4ed6\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u89c4\u77e9\u4e86\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u5fae\u5fae\u4e00\u987f\u63a5\u9053\uff1a \u201c\u4f55\u5144\uff0c\u53cd\u51fb\u4ed6\u4e24\u5251\uff0c\u54b1\u4eec\u52a0\u5165\u8fdb\u53bb\uff0c\u52a9\u4f60\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u4f55\u5bd2\u8863\u679c\u7136\u5927\u559d\u4e00\u58f0\uff0c\u91d1\u5251\u75be\u5feb\u7684\u53cd\u51fb\u4e86\u4e09\u62db\u3002 \u3000\u3000\u8fd9\u4e09\u5251\u5a01\u529b\u5341\u5206\u731b\u70c8\uff0c\u4e09\u5251\u8fde\u7ef5\u4e8e\u4e00\u5904\uff0c\u679c\u7136\u628a\u9ad8\u5929\u5065\u7684\u5200\u52bf\u5c01\u5f00\u3002 \u3000\u3000\u90a3\u53ea\u662f\u4e00\u70b9\u70b9\u7684\u7a7a\u9699\uff0c\u4f46\u5df2\u7ecf\u591f\u4e86\u3002 \u3000\u3000\u4e00\u652f\u5251\uff0c\u5feb\u5982\u95ea\u7535\u4e00\u822c\u4f38\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u90a3\u662f\u5f88\u5de7\u5999\u7684\u4e00\u5251\uff0c\u6b63\u597d\u5c01\u4f4f\u4e86\u9ad8\u5929\u5065\u4e0b\u4e00\u5200\u7684\u53d8\u5316\u3002 \u3000\u3000\u9ad8\u5929\u5065\u867d\u7136\u5200\u901f\u731b\u5feb\uff0c\u529b\u91cf\u5f3a\u5927\uff0c\u4f46\u4ed6\u8fd9\u4e00\u5200\u8ddd\u79bb\u5251\u901f\uff0c\u529b\u9053\u53cd\u800c\u6709\u4e9b\u4e0d\u80fd\u53d1\u6325\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u53cd\u800c\u4f7f\u4ed6\u7684\u5200\u6cd5\u4e00\u7a7a\u3002 \u3000\u3000\u5c31\u8fd9\u4e00\u77ac\u5de5\u592b\uff0c\u53f6\u957f\u9752\u5df2\u5c55\u5f00\u4e86\u653b\u52bf\uff0c\u8ffd\u9b42\u4e03\u5251\uff0c\u7ef5\u7ef5\u800c\u51fa\u3002 \u3000\u3000\u8fd9\u4e03\u5f0f\u540d\u9707\u6c5f\u6e56\u7684\u5251\u52bf\uff0c\u679c\u7136\u662f\u51f6\u9738\u7edd\u4f26\uff0c\u4ee5\u9ad8\u5929\u5065\u8fd9\u7b49\u7684\u6b66\u529f\uff0c\u4e5f\u88ab\u8feb\u5f97\u8fde\u8fde\u5411\u540e\u9000\u53bb\u3002 \u3000\u3000\u8fd9\u4f7f\u5f97\u4f55\u5bd2\u8863\u4e5f\u6709\u6687\u5c55\u5f00\u4e86\u53cd\u653b\u4e4b\u52bf\u3002 \u3000\u3000\u53e6\u5916\u63d2\u8fdb\u6765\u7684\u4e00\u652f\u5251\uff0c\u4e0d\u662f\u51c6\u5907\u51fa\u624b\u7684\u9ad8\u5e7d\u5170\uff0c\u800c\u662f\u5434\u53cc\u5973\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e5f\u7528\u5251\uff0c\u4e00\u628a\u8f6f\u5251\uff0c\u4e09\u5c3a\u516b\u5bf8\u957f\u7684\u8f6f\u5251\u3002 \u3000\u3000\u5f88\u5c11\u89c1\u5434\u53cc\u5973\u4eae\u8fc7\u5979\u8fd9\u628a\u8f6f\u5251\uff0c\u5979\u5728\u7b2c\u2014\u5821\u4e2d\uff0c\u4f4f\u4e86\u8fd9\u4e48\u4e45\uff0c\u5c31\u4e00\u76f4\u6ca1\u6709\u7528\u8fc7\u5175\u5203\u3002 \u3000\u3000\u5728\u51cc\u5389\u7684\u8ffd\u9b42\u4e03\u5251\u4e4b\u4e0b\uff0c\u518d\u52a0\u4e0a\u4f55\u5bd2\u8863\u548c\u5434\u53cc\u5973\uff0c\u4e24\u4e2a\u4e25\u5b9e\u7684\u914d\u5408\uff0c\u9ad8\u5929\u5065\u88ab\u8feb\u5f97\u5411\u540e\u9000\u4e86\u4e24\u4e09\u6b65\u8fdc\u3002 \u3000\u3000\u4f46\u9ad8\u5929\u5065\u786e\u975e\u51e1\u54cd\uff0c\u4ecd\u7136\u5e94\u4ed8\u4e0b\u4e86\u4e09\u4e2a\u4eba\u5408\u624b\u7684\u731b\u653b\u3002 \u3000\u3000\u53f6\u957f\u9752\u4e03\u62db\u5251\u5f0f\u7528\u5b8c\uff0c\u7adf\u7136\u6ca1\u6709\u4f24\u5230\u5bf9\u65b9\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5374\u5c55\u5f00\u4e86\u53cd\u51fb\uff0c\u5200\u5149\u5982\u96ea\uff0c\u653b\u4e0b\u4e0a\u6765\u3002 \u3000\u3000\u5434\u53cc\u5973\u7684\u4e00\u628a\u8f6f\u5251\u5f88\u5947\u602a\uff0c\u5728\u53f6\u957f\u9752\u7684\u653b\u51fb\u4e2d\uff0c\u5979\u6ca1\u6709\u53d1\u6325\u51fa\u4ec0\u4e48\u5a01\u529b\uff0c\u4f46\u53f6\u957f\u9752\u653b\u52bf\u5b8c\u6bd5\u4e4b\u540e\uff0c\u9ad8\u5929\u5065\u5c55\u5f00\u4e86\u731b\u70c8\u7684\u53cd\u51fb\u4e4b\u540e\uff0c\u5434\u53cc\u5973\u8fd9\u67c4\u8f6f\u5251\uff0c\u5374\u53cd\u800c\u53d1\u6325\u4e86\u6781\u5927\u7684\u5a01\u529b\u3002 \u3000\u3000\u5979\u7684\u62db\u6570\u5947\u5f02\uff0c\u5e38\u5e38\u7531\u4e0d\u592a\u53ef\u80fd\u7684\u89d2\u5ea6\u4e2d\uff0c\u653b\u51fa\u4e00\u62db\u3002 \u3000\u3000\u90a3\u4e00\u5251\u5e38\u5e38\u628a\u9ad8\u5929\u5065\u7684\u51cc\u5389\u653b\u52bf\uff0c\u5316\u89e3\u4e8e\u65e0\u5f62\u4e4b\u4e2d\u3002 \u3000\u3000\u6700\u5999\u7684\u662f\uff0c\u5434\u53cc\u5973\u90a3\u628a\u5251\u67d4\u67d4\u8f6f\u8f6f\u7684\uff0c \u4f3c\u4e4e\u662f\u5df2\u7ecf\u65e0\u529b\u9a7e\u9a6d\uff0c\u5b8c\u5168\u4e0d\u662f\u6b63\u89c4\u7684\u5251\u8def\u53d8\u5316\uff0c\u8fd9\u53cd\u800c\u4f7f\u5f97\u9ad8\u5927\u5065\u5728\u5e94\u4ed8\u4e0a\uff0c\u6709\u4e9b\u624b\u5fd9\u811a\u4e71\u7684\u611f\u89c9\u3002 \u3000\u3000\u4f46\u4e09\u4eba\u8054\u624b\u7684\u5a01\u529b\uff0c\u4ecd\u7136\u65e0\u6cd5\u51fb\u8d25\u9ad8\u5929\u5065\uff0c \u53ea\u4e0d\u8fc7\u662f\u963b\u6b62\u4e86\u4ed6\u51cc\u5389\u7684\u653b\u52bf\u800c\u5df2\u3002 \u534e\u5999\u771f\u56de\u987e\u4e86\u9ec4\u7075\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u4f24\u52bf\u5982\u4f55?\u201d \u3000\u3000\u539f\u6765\uff0c\u9ec4\u7075\u5df2\u5305\u624e\u597d\u4e86\u4f24\u52bf\u4e4b\u540e\uff0c\u4ecd\u7136\u6ca1\u6709\u79bb\u5f00\uff0c\u4ecd\u5728\u573a\u4e2d\u89c2\u6218\u3002 \u3000\u3000\u9ad8\u7075\u9053\uff1a \u201c\u5434\u59d1\u5a18\u5251\u8def\u5947\u5e7b\uff0c\u800c\u4e14\uff0c\u5251\u4e0a\u5168\u662f\u9634\u67d4\u4e4b\u529b\uff0c\u67d4\u80fd\u514b\u521a\uff0c\u4f3c\u4e4e\u5df2\u963b\u4e0a\u4e86\u9ad8\u5929\u5065\u7684\u653b\u52bf\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\uff0c\u54b1\u4eec\u5e94\u8be5\u6709\u4ec0\u4e48\u884c\u52a8?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u518d\u52a0\u4e0a\u51e0\u4e2a\u4eba\u624b\uff0c\u5408\u653b\u9ad8\u5929\u5065?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u4ed6\u80cc\u5bf9\u70df\u9501\u697c\uff0c \u54b1\u4eec\u53ea\u8981\u65e0\u6cd5\u7ed5\u8fc7\u53bb\uff0c \u5df2\u65e0\u6cd5\u518d\u591a\u52a0\u4eba\u624b\u653b\u52bf\uff0c\u6211\u662f\u8bf4\uff0c\u54b1\u4eec\u662f\u4e0d\u662f\u5e94\u8be5\u501f\u673a\u4f1a\u653b\u5165\u70df\u9501\u697c\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c\u4e0d\u8fc7\uff0c\u9ad8\u5929\u5065\u624d\u662f\u5173\u952e\u4eba\u7269\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u501f\u673a\u4f1a\u51fb\u8d25\u4ed6\uff0c\u6216\u4f24\u4e86\u4ed6\uff0c\u70df\u9501\u697c\u4e2d\u7eb5\u7136\u8fd8\u6709\u5f88\u591a\u9ad8\u624b\uff0c\u4f46\u4ed6\u4eec\u5df2\u7ecf\u6ca1\u6709\u518d\u6218\u4e4b\u5fd7\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u9ad8\u5e7d\u5170\u7a81\u7136\u5a07\u53f1\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u4f60\u4eec\u9000\u5f00\uff0c\u8ba9\u6211\u8bd5\u8bd5\u770b\u3002\u201d \u5434\u53cc\u5973\u53f3\u624b\u75be\u6325\uff0c\u8f6f\u5251\u5feb\u5982\u6d41\u661f\u4e00\u822c\uff0c\u75be\u653b\u4e86\u4e09\u62db\u3002 \u3000\u3000\u8fd9\u4e09\u5251\uff0c\u53d8\u5316\u8be1\u5f02\uff0c\u8feb\u5f97\u9ad8\u5929\u5065\u8fde\u653b\u4e86\u4e94\u5200\uff0c\u624d\u7b97\u628a\u4e09\u5251\u5c01\u6321\u5f00\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u548c\u53f6\u957f\u9752\u5374\u501f\u673a\u9000\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u53cc\u5251\u9f50\u4e0b\uff0c\u653b\u51fa\u4e86\u4e00\u62db\u3002 \u3000\u3000\u9ad8\u5929\u5065\u75be\u5feb\u7684\u5411\u540e\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e5f\u501f\u673a\u4f1a\u5411\u540e\u9000\u5f00\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u6211\u4eec\u5e94\u8be5\u5206\u4e2a\u80dc\u8d1f\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e03\u8272\u5251\u867d\u7136\u662f\u5251\u672f\u4e2d\u7684\u5947\u6280\uff0c\u4f46\u5374\u672a\u5fc5\u80fd\u591f\u4f24\u5f97\u4e86\u6211\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8bd5\u8bd5\u770b\u5427!\u201d \u3000\u3000\u4e24\u4eba\u8c08\u8bdd\u4e4b\u95f4\uff0c\u9ad8\u5e7d\u5170\u7684\u624b\u4e2d\uff0c\u53c8\u591a\u4e86\u4e24\u67c4\u5251\u3002 \u3000\u3000\u7ea2\u3001\u9ec4\u3001\u84dd\u3001\u767d\u56db\u652f\u5251\u3002 \u3000\u3000\u9ad8\u5929\u5065\u7a81\u7136\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u4f60\u662f\u4e0d\u662f\u771f\u60f3\u77e5\u9053\u4f60\u7684\u51fa\u8eab\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u6211\u662f\u4e00\u4e2a\u5b64\u513f\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u8ba4\u5b9a\uff0c\u4f60\u4e00\u5b9a\u662f\u6740\u5bb3\u6211\u7236\u6bcd\u7684\u51f6\u624b\uff0c\u6240\u4ee5\uff0c\u6211\u4e0d\u60f3\u518d\u95ee\u8be6\u7ec6\u7684\u60c5\u5f62\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u77e5\u9053\u8be6\u7ec6\u7684\u60c5\u5f62\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u5982\u613f\u8bf4\uff0c\u6211\u4e0d\u8fc7\u662f\u59d1\u5984\u542c\u4e4b\uff0c\u4f60\u5982\u662f\u4e0d\u613f\u8bf4\uff0c\u6211\u4e5f\u4e0d\u4f1a\u52c9\u5f3a\u4f60\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u4e16\u754c\u4e0a\uff0c\u4e0d\u4f1a\u6709\u90a3\u4e48\u591a\u7684\u5b64\u513f\uff0c\u4f60\u7684\u7236\u6bcd\uff0c\u786e\u662f\u88ab\u4eba\u6740\u5bb3\u7684\u3002\u201d \u9ad8\u5e7d\u5170\u5fcd\u8010\u7740\u80f8\u4e2d\u7684\u6fc0\u52a8\u4e4b\u60c5\uff0c\u9053\uff1a\u201c\u4f60\u662f\u51f6\u624b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u662f\u6536\u517b\u4f60\u7684\u4eba\uff0c\u4e0d\u662f\u51f6\u624b\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f60\u53ef\u60f3\u77e5\u9053\u51f6\u624b\u662f\u8c01?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u968f\u4fbf\u8bf4\u4e00\u4e2a\u51f6\u624b\u51fa\u6765\uff0c\u662f\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8001\u592b\u81ea\u7136\u63e1\u6709\u8bc1\u636e\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ec0\u4e48\u8bc1\u636e\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u6bcd\u4eb2\u7684\u8840\u4e66\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u6211\u6bcd\u4eb2\u7684\u8840\u4e66\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u5bf9!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4e0d\u660e\u767d\uff0c\u5148\u6bcd\u7684\u8840\u4e66\uff0c\u600e\u4f1a\u843d\u5728\u4f60\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u4ee4\u5802\u4ea4\u7ed9\u6211\u7684\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5c3d\u91cf\u6309\u8010\u4e0b\u5fc3\u4e2d\u7684\u6fc0\u52a8\u4e4b\u60c5\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u8840\u4e66\u73b0\u5728\u4f55\u5904?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u73b0\u5728\u8001\u592b\u8eab\u4e0a\uff0c\u53ea\u8981\u4f60\u80fd\u4e3a\u6211\u505a\u4e00\u4ef6\u4e8b\uff0c\u7acb\u523b\u53ef\u4ee5\u4ea4\u7ed9\u4f60\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ad8\u5929\u5065\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6740\u4e86\u5434\u53cc\u5973\uff0c\u8001\u592b\u7acb\u523b\u53ef\u4ee5\u4ea4\u51fa\u8840\u4e66\u3002\u201d \u9ad8\u5e7d\u5170\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u8fd9\u7b49\u4f4e\u52a3\u7684\u6311\u62e8\u624b\u6bb5\uff0c\u4f60\u4e5f\u80fd\u505a\u5f97\u51fa\u6765\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u5982\u82e5\u8001\u592b\u628a\u4ee4\u5802\u7559\u4e0b\u7684\u8840\u4e66\u6bc1\u53bb\uff0c\u4f60\u8fd9\u4e00\u751f\u4e4b\u4e2d\uff0c\u4e5f\u65e0\u6cd5\u4e86\u89e3\u4f60\u7684\u8eab\u4e16\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u9ad8\u5929\u5065\uff0c\u4f60\u53ef\u66fe\u60f3\u5230\uff0c\u6211\u8fd8\u6709\u4e00\u4e2a\u53d6\u5f97\u8840\u4e66\u7684\u529e\u6cd5\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u55ef!\u8fd9\u4e5f\u662f\u529e\u6cd5\u4e4b\u4e00\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u4f4e\u58f0\u9053\uff1a \u201c\u59b9\u59b9\uff0c\u8fd9\u4e2a\u4eba\u6545\u610f\u62d6\u5ef6\u65f6\u95f4\uff0c\u4e0d\u77e5\u9053\u5728\u65bd\u5c55\u4ec0\u4e48\u8be1\u8ba1\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4ed6\u5df2\u9ed4\u9a74\u6280\u7a77\uff0c\u91cf\u4ed6\u4e5f\u800d\u4e0d\u51fa\u4ec0\u4e48\u82b1\u6837\u4e86\uff0c\u6211\u4eec\u5c31\u770b\u4e2a\u660e\u767d\u5427\u3002\u201d \u4e8b\u5b9e\u4e0a\uff0c\u6b8b\u5251\u624b\u88ab\u6d88\u706d\u4e4b\u540e\uff0c\u6574\u5ea7\u70df\u9501\u697c\u7684\u5ead\u9662\uff0c\u5df2\u7ecf\u5b8c\u5168\u843d\u5165\u4e86\u9ec4\u7075\u63a7\u5236\u4e4b\u4e0b\u3002 \u3000\u3000\u4f46\u9ad8\u5929\u5065\u4e00\u592b\u5f53\u5173\uff0c\u70df\u9501\u697c\u7684\u5927\u95e8\u867d\u7136\u5f00\u4e86\uff0c\u4f46\u9ec4\u7075\u7b49\u4ecd\u7136\u65e0\u6cd5\u51b2\u8fdb\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5b9e\u5728\u662f\u4e00\u4e2a\u5f88\u5177\u6709\u97ec\u7565\u7684\u5973\u5b69\u5b50\uff0c\u5f53\u5979\u633a\u8eab\u800c\u51fa\uff0c\u548c\u9ad8\u5929\u5065\u52a8\u624b\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u4f5c\u4e86\u5b89\u6392\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u3001\u53f6\u957f\u9752\u3001\u4e07\u80dc\u3001\u534e\u5999\u771f\uff0c\u90fd\u5df2\u6709\u5907\u3002\u9ad8\u5e7d\u5170\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u9ad8\u5821\u4e3b\uff0c\u5ff5\u5728\u5341\u51e0\u5e74\u76f8\u5904\u7684\u60c5\u610f\u4e4b\u4e0a\uff0c\u8ba9\u4f60\u5148\u673a\uff0c\u4f60\u53ef\u4ee5\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u770b\u770b\u4f60\u6bcd\u4eb2\u7559\u4e0b\u7684\u8840\u4e66\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u8840\u4e66\u867d\u5728\u4f60\u7684\u8eab\u4e0a\uff0c\u4f46\u8fd9\u4e0d\u4f1a\u5bf9\u6211\u6784\u6210\u4efb\u4f55\u7684\u8bf1\u60d1\u529b\u91cf\uff0c\u4e5f\u65e0\u6cd5\u4f7f\u6211\u5bf9\u4f60\u51cf\u4f4e\u4efb\u4f55\u7684\u6212\u5907\u548c\u654c\u610f\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u770b\u6765\uff0c\u4f60\u5bf9\u4f60\u7684\u51fa\u8eab\u5bb6\u4e8b\uff0c\u5e76\u975e\u5341\u5206\u5173\u5fc3?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u8bf4\u9053\uff1a \u201c\u8840\u4e66\u5982\u82e5\u5728\u4f60\u8eab\u4e0a\uff0c\u6740\u4e86\u4f60\uff0c\u6211\u81ea\u7136\u53ef\u4ee5\u53d6\u5f97\u5230\u2026\u2026\u201d \u9ad8\u5929\u5065\u63a5\u9053\uff1a\u201c\u5982\u662f\u8001\u592b\u6740\u4e86\u4f60\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u5982\u82e5\u6211\u4e0d\u5e78\u88ab\u6740\u4e86\uff0c\u5c31\u7b97\u4f60\u7559\u4e0b\u8840\u4e66\uff0c\u5bf9\u6211\u4e5f\u6ca1\u6709\u7528\u4e86\u3002\u201d \u9ad8\u5929\u5065\u7f13\u7f13\u4e3e\u8d77\u4e86\u624b\u4e2d\u4e03\u661f\u5b9d\u5200\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a \u201c\u4f60\u4e00\u5b9a\u8981\u903c\u8001\u592b\u4e00\u62fc\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u5c40\uff0c\u9664\u4e86\u653e\u624b\u4e00\u6218\u4e4b\u5916\uff0c\u6211\u60f3\u4e0d\u51fa\uff0c\u8fd8\u6709\u4ec0\u4e48\u522b\u7684\u529e\u6cd5\u4e86?\u201d \u3000\u3000\u9ad8\u5929\u5065\u5de6\u624b\u63a2\u5165\u6000\u4e2d\uff0c\u53d6\u51fa\u4e00\u4e2a\u9ec4\u7eeb\u5c01\u5957\uff0c\u9053\uff1a \u201c\u62ff\u53bb\uff0c\u770b\u770b\u5427!\u201d \u3000\u3000\u5de6\u624b\u4e00\u626c\uff0c\u76f4\u5411\u9ad8\u5e7d\u5170\u629b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5e76\u672a\u4f38\u624b\u53bb\u63a5\uff0c\u5de6\u624b\u53cc\u5251\u4e00\u6325\uff0c\u5251\u950b\u7a7f\u8fc7\u9ec4\u888b\u3002 \u3000\u3000\u9ad8\u5929\u5065\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u5c0f\u4e2b\u5934\uff0c \u591a\u7591\u5f97\u5f88\uff0c\u5bf9\u4f60\u5a18\u7559\u4e0b\u7684\u8840\u4e66\uff0c\u7adf\u7136\u662f\u59d0\u6b64\u7684\u4e0d\u656c\u3002\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u8bdd\uff0c \u4eba\u5df2\u8fd8\u5200\u5165\u9798\uff0c \u5411\u540e\u9000\u5382\u4e09\u6b65\uff0c\u9ad8\u5e7d\u5170\u51b7\u51b7\u671b\u4e86\u9ad8\u5929\u5065\u4e00\u773c\uff0c\u53f3\u624b\u53cc\u5251\u4e00\u6325\uff0c\u53d6\u8fc7\u9ec4\u8272\u7684\u888b\u5b50\u3002 \u3000\u3000\u9ec4\u888b\u4e4b\u5185\uff0c\u679c\u7136\u6709\u4e00\u5f20\u767d\u7eeb\u5199\u7684\u8840\u4e66\uff0c\u4e0a\u9762\u7684\u8840\u8ff9\u5df2\u6210\u5e72\u75d5\uff0c\u663e\u7136\uff0c\u5df2\u6536\u85cf\u4e86\u5f88\u4e45\u7684\u65f6\u95f4\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u6253\u5f00\u4e86\u8840\u4e66\uff0c \u53ea\u89c1\u4e0a\u9762\u5199\u9053\uff1a\u201c\u5c3a\u7ee2\u7559\u75d5\uff0c \u8840\u4e66\u6d17\u51a4\uff0c\u795e\u9f99\u53d9\u60c5\uff0c\u9752\u77f3\u8865\u5929\u3002\u201d \u3000\u3000\u56db\u53e5\u4f3c\u8bd7\u975e\u8bd7\u7684\u5048\u8bed\uff1a \u3000\u3000\u9ad8\u5e7d\u5170\u867d\u7136\u662f\u5145\u6ee1\u7740\u667a\u6167\u7684\u4eba\uff0c \u4e5f\u770b\u7684\u83ab\u540d\u5176\u5999\uff0c\u4e0d\u7981\u4e00\u5446\u3002 \u9ad8\u5929\u5065\u671b\u671b\u5446\u5446\u51fa\u795e\u7684\u9ad8\u5e7d\u5170\uff0c\u51b7\u7b11\u4e00\u58f0\u3002\u9053\uff1a \u201c\u770b\u5b8c\u4e86\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u770b\u4e0d\u61c2\u8fd9\u4e0a\u9762\u8bf4\u7684\u4ec0\u4e48?\u201d \u3000\u3000\u9ad8\u5929\u5065\u7a81\u7136\u7eb5\u58f0\u5927\u7b11\uff0c\u9053\uff1a \u201c\u4f60\u5f88\u7cbe\u660e\uff0c\u4f46\u4ecd\u7136\u4e3a\u8001\u592b\u7b97\u8ba1\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u6025\u6025\u629b\u53bb\u8840\u4e66\u3002 \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u665a\u4e86\uff0c\u90a3\u767d\u7ee2\u4e0a\u7684\u5947\u6bd2\uff0c\u65e9\u5df2\u6cbe\u4f60\u7684\u624b\u4e0a\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u597d\u5351\u9119\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53ea\u6709\u4e00\u4e2a\u529e\u6cd5\u53ef\u4ee5\u89e3\u53bb\u4f60\u8eab\u4e0a\u4e4b\u6bd2\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7b54\u975e\u6240\u95ee\u5730\u9053\uff1a \u201c\u53ea\u8981\u80fd\u538b\u5236\u534a\u4e2a\u65f6\u8fb0\uff0c\u6211\u5c31\u53ef\u4ee5\u548c\u4f60\u62fc\u51fa\u4e2a\u751f\u6b7b\u51fa\u6765\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u4f60\u53ea\u8981\u4e00\u63d0\u771f\u6c14\uff0c\u7acb\u523b\u5c31\u5f15\u53d1\u6bd2\u6027\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u9053\uff1a \u201c\u4ec0\u4e48\u529e\u6cd5\uff0c\u53ef\u4ee5\u89e3\u53bb\u5979\u8eab\u4e2d\u4e4b\u6bd2\u3002\u201d \u9ad8\u5929\u5065\u9053\uff1a\u201c\u4e03\u7c92\u89e3\u6bd2\u91d1\u4e39\uff0c\u6bcf\u65e5\u670d\u7528\u4e00\u7c92\uff0c\u4e03\u65e5\u5185\uff0c\u5947\u6bd2\u53ef\u89e3\uff0c\u8001\u592b\u51c6\u5907\u8fd9\u4e2a\u9526\u56ca\uff0c\u51c6\u5907\u5bf9\u4ed8\u80fd\u548c\u8001\u592b\u9889\u9883\u7684\u9ad8\u624b\uff0c\u4f46\u5374\u60f3\u4e0d\u5230\uff0c\u7adf\u7136\u7528\u5728\u4e86\u8fd9\u4e2a\u5c0f\u4e2b\u5934\u8eab\u4e0a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u591a\u884c\u4e0d\u4e49\u5fc5\u81ea\u6bd9\uff0c\u4f60\u638c\u7406\u7b2c\u4e00\u5821\u95e8\u6237\uff0c\u672c\u6765\u6709\u8db3\u591f\u5b9e\u529b\uff0c\u53ef\u4ee5\u5b8c\u6210\u4f60\u7684\u57fa\u4e1a\uff0c\u4f46\u4f60\u592a\u51b7\u9177\uff0c\u592a\u6b8b\u5fcd\u4e86\uff0c\u624d\u5f04\u5f97\u4f17\u53db\u4eb2\u79bb\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u8001\u592b\u53ea\u627f\u8ba4\u72af\u4e86\u4e24\u4e2a\u9519\u8bef\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u662f\uff0c\u8001\u592b\u8fc7\u4e8e\u4f4e\u4f30\u4e86\u4f60\u4eec\u4e24\u4e2a\u4e2b\u5934\uff0c\u6240\u4ee5\uff0c\u6ca1\u6709\u65e9\u5728\u4f60\u4eec\u8eab\u4e0a\uff0c\u6697\u4e0b\u7981\u5236\uff0c\u53ea\u60f3\u7528\u6069\u60c5\u7b3c\u7edc\u4f60\u4eec\uff1b\u7b2c\u4e8c\u662f\uff0c\u8001\u592b\u4e0d\u80fd\u9632\u60a3\u672a\u7136\uff0c\u65e9\u4e00\u70b9\u6740\u4e86\u4f55\u5bd2\u8863\uff0c\u81f4\u517b\u864e\u6210\u60a3\uff0c\u4e0d\u8fc7\uff0c\u8001\u592b\u8fd8\u662f\u80dc\u4e86\u8fd9\u6700\u91cd\u8981\u7684\u4e00\u5173\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5c31\u7b97\u9ad8\u5e7d\u5170\u6bd2\u53d1\u4e0d\u80fd\u518d\u6218\uff0c\u522b\u5fd8\u4e86\u8fd8\u6709\u6211\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u9ad8\u5e7d\u5170\u4e2d\u6bd2\uff0c\u9ec4\u7075\u53d7\u4f24\uff0c\u5c31\u7b97\u4f60\u80fd\u548c\u6211\u4e00\u6218\uff0c\u4f60\u4eec\u4e5f\u5168\u65e0\u80dc\u7b97\u4e86\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u5012\u672a\u5fc5\uff0c\u9634\u9633\u5821\u8fd8\u6709\u56db\u5927\u5251\u4f7f\uff0c\u90fd\u4e0d\u662f\u597d\u5bf9\u4ed8\u7684\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u7eb5\u58f0\u5927\u7b11\uff0c\u9053\uff1a \u201c\u5434\u53cc\u5973\uff0c\u4f60\u660e\u767d\u4e48\uff0c\u9ad8\u5e7d\u5170\u7684\u4e03\u8272\u5251\uff0c\u5927\u7ea6\u662f\u5929\u4e0b\u6700\u5947\u602a\u7684\u5251\u6cd5\u4e86\uff0c\u4efb\u4f55\u4eba\uff0c\u6297\u62d2\u8fd9\u79cd\u5251\u6cd5\uff0c\u90fd\u4f1a\u6709\u7740\u5947\u7a81\u4e4b\u611f\uff0c\u6240\u4ee5\uff0c\u8001\u592b\u6ca1\u6709\u628a\u63e1\u80dc\u5979\u3002\u9ec4\u7075\u7684\u6b66\u529f\uff0c\u786e\u5b9e\u4e0d\u9519\uff0c\u4f46\u4ed6\u5df2\u4f24\u3002\u81f3\u4e8e\u4f60\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u548c\u6211\u52a8\u624b\u4e00\u640f\uff0c\u81f3\u4e8e\u56db\u5927\u5251\u4f7f\uff0c\u6211\u76f8\u4fe1\u5deb\u5a46\u5a46\u548c\u8d3e\u4f3c\u9053\u53ef\u4ee5\u5e94\u4ed8\u5f97\u4e86\uff0c\u4f55\u51b5\uff0c\u6211\u8fd9\u70df\u9501\u697c\u4e0a\uff0c\u8fd8\u6709\u6570\u5341\u4f4d\u6b7b\u58eb\uff0c\u4ed6\u4eec\u90fd\u5fe0\u5fc3\u803f\u803f\uff0c\u4e0d\u53ef\u80fd\u80cc\u53db\u4e8e\u6211\uff0c\u8fd9\u4e00\u6218\u80dc\u8d1f\u4e4b\u5206\uff0c\u4f60\u5fc3\u4e2d\u8be5\u6709\u6570\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u9ad8\u5929\u5065\uff0c\u81f3\u5c11\uff0c\u54b1\u4eec\u4e4b\u95f4\u8fd9\u4e00\u6218\uff0c\u8fd8\u5f88\u96be\u6599\u5b9a\u80dc\u8d1f?\u201d \u3000\u3000\u9ad8\u5929\u5065\u9053\uff1a \u201c\u542c\u8bf4\u4f60\u4e60\u6709\u9b54\u529f\uff0c\u4f46\u4f60\u5728\u7b2c\u4e00\u5821\u4e2d\uff0c\u4ece\u672a\u7528\u8fc7\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9!\u6ca1\u6709\u7528\u8fc7\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u51c6\u5907\u5bf9\u6211\u65bd\u7528\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u5f88\u591a\u5e74\uff0c\u6ca1\u6709\u65bd\u5c55\u9b54\u529f\uff0c\u4f46\u90a3\u5e76\u975e\u662f\u8bf4\uff0c\u6211\u5df2\u7ecf\u4e0d\u4f1a\uff0c\u76f8\u53cd\u7684\uff0c\u6211\u7cbe\u5fc3\u82e6\u7ec3\uff0c\u8fd9\u4e9b\u6211\u4eec\u53ef\u4ee5\u5404\u5c3d\u6240\u80fd\u7684\u653e\u624b\u4e00\u640f\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u5446\u4e86\u4e00\u5446\uff0c\u8272\u5389\u5185\u834f\u5730\u9053\uff1a \u201c\u8fd9\u4e9b\u5e74\uff0c\u4f60\u8fd8\u6ca1\u6709\u628a\u9b54\u529f\u653e\u4e0b\u3002\u201d \u5434\u53cc\u5973\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\u2026\u2026\u201d \u3000\u3000\u76ee\u5149\u8f6c\u6ce8\u5230\u4f55\u5bd2\u8863\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u4f60\u8981\u542c\u6e05\u695a\uff0c\u6211\u5f88\u613f\u610f\u53d8\u4e00\u4e2a\u5a34\u9759\u7684\u5973\u5b69\u5b50\uff0c\u8fd9\u4e9b\u5e74\u6211\u4e5f\u6ca1\u6709\u65bd\u5c55\u8fc7\u9b54\u529f\uff0c\u4f46\u76ee\u524d\u60c5\u52bf\u4e0d\u540c\uff0c\u6211\u5982\u4e0d\u5168\u529b\u65bd\u5c55\uff0c\u5b8c\u5168\u6ca1\u6709\u80dc\u7b97\uff0c\u4e0d\u8fc7\uff0c\u5929\u9b54\u95e8\u6b66\u529f\uff0c\u4e00\u65e6\u65bd\u5c55\u51fa\u6765\uff0c\u6709\u4e9b\u4e0d\u582a\u5165\u76ee\uff0c\u5e0c\u671b\u4f60\u4e0d\u8981\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u4e0d\u8bba\u4ec0\u4e48\u6b66\u529f\uff0c \u53ea\u8981\u662f\u9664\u66b4\u5b89\u826f\uff0c\u90fd\u662f\u597d\u6b66\u529f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u5982\u6b64\u5927\u91cf\uff0c\u6211\u5c31\u653e\u5fc3\u65bd\u4e3a\u4e86\uff1a\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5ffd\u7136\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u59ca\uff0c\u4f60\u89c9\u7740\uff0c\u4e00\u5b9a\u8981\u65bd\u5c55\u9b54\u529f\uff0c\u624d\u80fd\u4e00\u640f\u5417\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u662f\u7684\uff0c\u5e7d\u5170\uff0c\u4ed6\u6b66\u529f\u7684\u9ad8\u5f3a\uff0c\u5200\u6cd5\u7684\u7cbe\u5389\uff0c\u5b9e\u5df2\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u4f60\u7684\u4e03\u8272\u5251\uff0c\u662f\u5251\u6cd5\u4e2d\u7684\u5947\u7edd\u4e4b\u6280\uff0c\u4e0d\u80fd\u4ee5\u5e38\u60c5\u6d4b\u5ea6\uff0c\u4e5f\u4e0d\u80fd\u7528\u4ee5\u548c\u522b\u4eba\u7684\u6b66\u529f\u76f8\u6bd4\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\uff0c\u5c0f\u59b9\u7684\u610f\u601d\uff0c\u60f3\u8bf7\u5927\u59ca\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u53ea\u8981\u6211\u80fd\u529b\u6240\u53ca\u7684\uff0c \u81ea\u7136\u4f1a\u7b54\u5e94\u4f60\uff0c\u4f60\u8bf4\u5427\uff0c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u542c\u8bf4\uff0c\u4f60\u4eec\u5929\u9b54\u95e8\u4e2d\uff0c\u6709\u4e00\u79cd\u624b\u6cd5\uff0c\u53ef\u4ee5\u6fc0\u8d77\u4e00\u4e2a\u4eba\u751f\u547d\u4e2d\u7684\u6f5c\u529b?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u5f88\u597d\uff0c\u59ca\u59ca\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\uff0c\u6fc0\u53d1\u6211\u751f\u547d\u4e2d\u7684\u6f5c\u529b\uff0c\u5bf9\u6297\u6bd2\u6027\uff0c\u7531\u5c0f\u59b9\u4eb2\u624b\u62a5\u4ec7\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u4e5f\u8bb8\uff0c\u6211\u4eec\u8fd9\u624b\u6bb5\uff0c\u786e\u53ef\u4ee5\u6297\u62d2\u6bd2\u6027\u7684\u53d1\u4f5c\uff0c\u5e7d\u5170\uff0c\u4f60\u53ef\u77e5\u9053\u540e\u679c\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u53cc\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u9053?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5bf9!\u662f\u4e0d\u662f\u5728\u6fc0\u53d1\u51fa\u751f\u547d\u7684\u6f5c\u529b\u4e4b\u540e\uff0c\u90a3\u4ee4\u4eba\u4f1a\u53d7\u5230\u5f88\u5927\u7684\u4f24\u5bb3?\u201d \u3000\u3000\u5434\u53cc\u5973\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5f88\u5927\u7684\u4f24\u5bb3\uff0c\u90a3\u662f\u4e00\u822c\u7684\u60c5\u5f62\u4e0b\u800c\u8a00\uff0c\u4ee5\u4f60\u76ee\u4e0b\u7684\u5904\u5883\uff0c\u4e0d\u4ec5\u662f\u53d7\u5230\u4e86\u4f24\u5bb3\uff0c\u800c\u4e14\u662f\u4e22\u4e86\u6027\u547d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u6697\u4e2d\u8fd0\u6c14\u8bd5\u8fc7\u4e86\uff0c\u9ad8\u5929\u5065\u6ca1\u6709\u8bf4\u8c0e\uff0c\u6211\u786e\u4e2d\u4e86\u6bd2\uff0c\u8fd9\u662f\u4ed6\u7b2c\u4e8c\u6b21\u5bf9\u6211\u4e0b\u6bd2\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u6211\u53ea\u8981\u80dc\u4e86\u4ed6\uff0c\u4e00\u5b9a\u4f1a\u903c\u4ed6\u4ea4\u51fa\u89e3\u836f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u8bba\u4f60\u662f\u5426\u80dc\u4e86\u4ed6\uff0c\u4f46\u6211\u7684\u65e5\u5b50\u90fd\u4e0d\u597d\u8fc7\uff0c\u5982\u82e5\u6211\u7684\u63a8\u60f3\u4e0d\u9519\uff0c\u6211\u7684\u751f\u8eab\u7236\u6bcd\uff0c\u53ef\u80fd\u662f\u4ed6\u6740\u7684\uff0c\u5982\u4e0d\u662f\u6211\u90a3\u4f4d\u56fa\u50fb\u7684\u5e08\u7236\uff0c\u770b\u4e0a\u4e86\u6211\uff0c\u975e\u8981\u6211\u627f\u7ee7\u8d77\u4e03\u8272\u5251\u7684\u8863\u94b5\u4e0d\u53ef\uff0c\u6211\u662f\u4ec0\u4e48\u547d\u8fd0\uff0c\u90a3\u5c31\u5f88\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5bf9!\u4e0d\u662f\u90a3\u8001\u9053\u59d1\u770b\u4e0a\u4f60\uff0c\u4f60\u65e9\u5df2\u7ecf\u2026\u2026\u201d \u3000\u3000\u65e9\u5df2\u5982\u4f55?\u4ed6\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u65e9\u5df2\u6ca6\u4e3a\u4f60\u7684\u59ec\u59be\uff0c\u70df\u9501\u697c\u4e0a\u7684\u73a9\u7269\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u6ca1\u6709\u5426\u8ba4\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\uff0c\u6211\u5fc3\u4e2d\u5145\u6ee1\u7740\u4ec7\u6068\u4e4b\u706b\uff0c\u6211\u8981\u4e3a\u7236\u62a5\u4ec7\uff0c\u6211\u8981\u4eb2\u624b\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\u62b1\u6b49\uff0c\u6211\u4e0d\u80fd\u7b54\u5e94\u4f60\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4e0d\u5fcd\u5fc3\u505a\uff0c\u4e5f\u505a\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u76ee\u5149\u8f6c\u6ce8\u5230\u4f55\u5bd2\u8863\u7684\u8eab\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u5e2e\u6211\u6c42\u6c42\u5927\u59ca\uff0c\u8981\u5979\u5e2e\u6211\u8fd9\u4e2a\u5fd9\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7f13\u6b65\u884c\u4e86\u8fc7\u53bb\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u73b0\u5728\uff0c\u662f\u4e0d\u662f\u5f88\u96be\u8fc7?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6bd2\u6027\u8fd8\u672a\u53d1\u4f5c\uff0c\u4f46\u6211\u5df2\u4e0d\u80fd\u63d0\u6c14\u7528\u5251\u4e86\uff0c\u6709\u7740\u751f\u4e0d\u5982\u6b7b\u7684\u611f\u89c9\uff0c\u4f55\u90ce\uff0c\u5982\u82e5\u6211\u7684\u751f\u547d\u4e2d\uff0e\u80fd\u7ed9\u4f60\u4eec\u7559\u4e0b\u4e00\u4e9b\u7eaa\u5ff5\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u6211\u7559\u4e0b\u6765\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u597d\u5427!\u6211\u53bb\u8ddf\u5979\u5546\u91cf\u4e00\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u76ee\u5149\u70af\u70af\uff0c\u73af\u89c6\u5168\u573a\uff0c\u4f3c\u662f\u6b63\u5728\u7b49\u5f85\u7740\u4e00\u4e2a\u51fa\u624b\u4e00\u51fb\u7684\u673a\u4f1a\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7a81\u7136\u51fa\u624b\u4e00\u6307\uff0c\u70b9\u4e86\u9ad8\u5e7d\u5170\u7684\u7a74\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u5bf9\u4e0d\u8d77\uff0c\u6211\u4e0d\u5e0c\u671b\u4f60\u6b7b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5706\u7741\u79c0\u76ee\uff0c\u671b\u7740\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u5434\u53cc\u5973\u8131\u4e0b\u5916\u8863\uff0c\u9732\u51fa\u4e00\u4ef6\u7d27\u88f9\u8eab\u8eaf\u7684\u7ea2\u8272\u5185\u8863\u3002 \u3000\u3000\u8fd9\u5185\u8863\u6ca1\u6709\u8896\u5b50\uff0c\u9732\u51fa\u4e86\u4e24\u4e2a\u7c89\u5ae9\u6d51\u5706\u7684\u624b\u81c2\u3002 \u3000\u3000\u6bcf\u4e00\u6761\u624b\u81c2\u4e0a\u5957\u7740\u4e24\u679a\u91d1\u73af\uff0c \u3000\u3000\u5434\u53cc\u5973\u672c\u662f\u957f\u7684\u5f88\u7f8e\uff1b\u8fd9\u65f6\u523b\u770b\u4e0a\u53bb\uff0c\u66f4\u662f\u5145\u6ee1\u7740\u8bf1\u60d1\u3002 \u3000\u3000\u9ad8\u5929\u5065\u53cc\u76ee\u6012\u7741\uff0c\u77aa\u7740\u5434\u53cc\u5973\uff0c\u9053\uff1a \u201c\u8001\u592b\u517b\u4e86\u4f60\u5f88\u591a\u5e74\uff0c\u96be\u9053\u5c31\u5168\u65e0\u4e00\u70b9\u60c5\u610f\u5417\uff1f\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u6ca1\u6709\u60c5\u610f\u7684\u662f\u4f60\uff0c\u4f60\u4e24\u5ea6\u5bf9\u5e7d\u5170\u4e0b\u6bd2\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u7a81\u62ac\u53f3\u624b\uff0c\u9053\uff1a \u201c\u5c0f\u5fc3\u4e86\uff0c\u6211\u4e0d\u4f1a\u548c\u4f60\u4f5c\u53e3\u820c\u4e0a\u7684\u4e89\u8bba\u3002\u201d \u8bf4\u8bdd\u58f0\u4e2d\uff0c\u53f3\u624b\u7a81\u7136\u4e00\u6c89\uff0c\u6307\u5411\u4e86\u9ad8\u5929\u5065\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5374\u7a81\u7136\u611f\u89c9\u5230\u4e86\u4e00\u80a1\u5f88\u5927\u7684\u538b\u529b\uff0c\u76f4\u901a\u4e0a\u6765\uff0c\u6025\u6025\u6325\u5200\u4e00\u65a9\u3002 \u3000\u3000\u4eba\u4e5f\u540c\u65f6\u5411\u65c1\u4fa7\uff0c\u95ea\u907f\u800c\u53bb\u3002 \u3000\u3000\u7384\u5a18\u4e00\u76f4\u5b88\u5728\u9ec4\u7075\u7684\u8eab\u4fa7\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u4f60\u8fd9\u6837\u5168\u795e\u8d2f\u6ce8\uff0c\u4e0d\u6015\u7d2f\u7740\u5417?\u201d \u9ec4\u7075\u9053\uff1b\u201c\u4e0d\u4f1a\u7684\uff0e\u6211\u7684\u4f24\u52bf\u5e76\u4e0d\u592a\u91cd\uff0c \u8001\u5b9e\u8bf4\uff0c\u6211\u8fd8\u6709\u518d\u6218\u4e4b\u80fd\u3002\u201d \u7384\u5a18\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u4f24\u7684\u5f88\u91cd\uff0c\u4e0d\u80fd\u968f\u4fbf\u51fa\u624b!\u201d\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7384\u5a18\u522b\u63d0\u9192\u6211\uff0c\u8fd9\u4e00\u6b21\uff0c\u6211\u4eec\u5982\u4e0d\u80fd\u6d88 \u3000\u3000\u706d\u4e86\u9ad8\u5929\u5065\uff0c\u53ea\u6015\u4ee5\u540e\uff0c\u518d\u4e5f\u6ca1\u6709\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\uff0c\u5982\u82e5\u5434\u53cc\u5973\u6218\u4e0d\u4e0b\uff0c\u5173\u952e\u65f6\uff0c\u6211\u8981\u51fa\u624b\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u6211\u53bb\u52a9\u5979\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\uff0c\u534e\u5999\u771f\uff0c\u6211\uff0c\u4e00\u8d77\u51fa\u624b\uff0c\u4e0d\u8bba\u5982\u4f55\uff0c\u4e0d\u80fd\u8ba9\u9ad8\u5929\u5065\u518d\u9003\u5165\u70df\u9501\u697c\u4e2d\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a \u201c\u6211\u548c\u534e\u5999\u771f\uff0c\u53ef\u4ee5\u51fa\u624b\u4e00\u6218\uff0c\u4f46\u4f60\u7684\u4f24\u52bf\u4e25\u91cd\uff0c\u6700\u597d\u4e0d\u8981\u5984\u52a8\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u7384\u5a18\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u867d\u7136\u5b66\u4f1a\u4e86\u4e0d\u5c11\u7684\u6b66\u529f\uff0c\u4f46\u4f60\u548c\u5999\u771f\u7684\u8054\u624b\uff0c\u4e5f\u4e0d\u662f\u4ed6\u7684\u654c\u624b\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u5982\u82e5\u52a0\u4e0a\u4f60\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u52a0\u4e0a\u6211\uff0c\u81f3\u5c11\u53ef\u4ee5\u548c\u4ed6\u4e00\u62fc\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u80dc\u8d1f\u4e4b\u5206\uff0c\u4f60\u4e5f\u5168\u65e0\u628a\u63e1\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6ca1\u6709\u628a\u63e1\uff0c\u8fd9\u4e2a\u4eba\uff0c\u5200\u6cd5\u9020\u8be3\u4e4b\u6df1\uff0c\u5df2\u7ecf\u5230\u4e86\u795e\u4e0e\u5200\u4f1a\u7684\u5883\u754c\uff0c\u5e7d\u5170\u59d1\u5a18\u7684\u4e03\u8272\u5251\u6cd5\uff0c\u662f\u5251\u4e2d\u4ece\u672a\u6709\u8fc7\u7684\u5947\u6280\uff0c\u5bf9\u9ad8\u5929\u5065\uff0c\u786e\u7136\u6784\u6210\u4e00\u79cd\u5a01\u80c1\uff0c\u6240\u4ee5\uff0c\u4ed6\u6709\u4e9b\u6015\uff0c\u628a\u5e7d\u5170\u59d1\u5a18\u770b\u6210\u4e86\u7b2c\u4e00\u52b2\u654c\u3002\u201d \u3000\u3000\u7384\u5a18\u9053\uff1a\u201c\u5434\u53cc\u5973\u5462?\u201d \u3000\u3000\u989f\u9053\uff1a \u201c\u5434\u53cc\u5973\u4ee5\u5929\u9b54\u529f\u548c\u4ed6\u52a8\u624b\u76f8\u640f\uff0c\u80dc\u8d1f\u5f88\u96be\u9884\u6599\uff0c \u54b1\u4eec\u51c6\u5907\u4f5c\u63a5\u8fce\uff0c\u6211\u5df2\u7ecf\u60f3\u51fa\u4e86\u4e00\u4e2a\u5bf9\u4ed8\u7684\u529e\u6cd5\uff0c\u53bb\u53eb\u5999\u771f\u8fc7\u6765\uff0c\u6211\u4eec\u5546\u91cf\u4e00\u4e0b\u3002\u201d \u3000\u3000\u7384\u5a18\u62db\u547c\u8fc7\u534e\u5999\u771f\uff0c\u4e09\u4e2a\u4eba\u4f4e\u58f0\u8ba1\u8bae\u3002 \u3000\u3000\u5434\u53cc\u5973\u548c\u9ad8\u5929\u5065\u5df2\u5c55\u5f00\u6fc0\u70c8\u7684\u640f\u6740\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5200\u5149\u7eb5\u6a2a\uff0c\u653b\u52bf\u731b\u70c8\u5f02\u5e38\u3002 \u3000\u3000\u5434\u53cc\u5973\u672a\u7528\u5175\u5200\u7a7a\u624b\u8fce\u654c\uff0c\u4f46\u5979\u7f8e\u5992\u7684\u59ff\u6001\uff0c\u7fe9\u7fe9\u8eab\u6cd5\uff0c\u6709\u5982\u821e\u6b65\u4e00\u822c\uff0c\u5728\u7eb5\u6a2a\u5200\u5149\u4e2d\u7a7f\u68ad\u884c\u8d70\u3002 \u3000\u3000\u9ad8\u5929\u5065\u5200\u6cd5\u5f88\u6025\uff0c\u4f46\u5374\u4e00\u65f6\u65e0\u6cd5\u5288\u4e2d\u5434\u53cc\u5973\u3002 \u3000\u3000\u770b\u4e0a\u53bb\uff0c\u8fd9\u4e00\u573a\u640f\u6597\uff0c\u5434\u53cc\u5973\u5b8c\u5168\u9677\u5165\u5728\u5371\u6025\u4e4b\u4e2d\uff0c\u9ad8\u5929\u5065\u968f\u65f6\u4e00\u5200\uff0c\u5c31\u53ef\u4ee5\u53d6\u5f97\u5434\u53cc\u5973\u7684\u6027\u547d\u3002 \u3000\u3000\u4f46\u6570\u5341\u62db\u8fc7\u53bb\u4e86\uff0c\u5434\u53cc\u5973\u4ecd\u7136\u5728\u5200\u5149\u4e2d\u7a7f\u68ad\u884c\u8d70\uff0c\u597d\u597d\u7684\u6d3b\u7740\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u54cd\u8d77\u4e86\u4e00\u9635\u91d1\u94c1\u4e4b\u58f0\u3002 \u3000\u3000\u5979\u5de6\u81c2\u4e0a\u5e26\u4e86\u4e24\u4e2a\u91d1\u73af\uff0c\u5df2\u7ecf\u88ab\u65a9\u65ad\u4e86\u4e00\u4e2a\u3002 \u3000\u3000\u91d1\u73af\u788e\u6210\u4e86\u6570\u5757\uff0c\u4e24\u5757\u9489\u5728\u4e86\u9ad8\u5929\u5065\u7684\u8138\u4e0a\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u867d\u7136\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u4f46\u5979\u5374\u4ee5\u76ee\u793a\u610f\u3002 \u3000\u3000\u7ea2\u82b1\u3001\u7eff\u53f6\u4e00\u58f0\u4e0d\u54cd\uff0c\u516d\u652f\u5251\u7a81\u7136\u98de\u51fa\u3002 \u3000\u3000\u516d\u652f\u5251\uff0c\u4e09\u79cd\u4e0d\u540c\u7684\u5f69\u5149\uff0c\u5377\u5411\u4e86\u9ad8\u5929\u5065\u3002 \u3000\u3000\u9ad8\u5929\u5065\u75be\u5feb\u7684\u6325\u624b\uff0c\u4e03\u661f\u5b9d\u5200\u5ffd\u7136\u95f4\uff0c\u95ea\u8d77\u5b50\u4e00\u7247\u5200\u5149\u3002 \u3000\u3000\u91d1\u94c1\u4ea4\u9e23\uff0c\u5251\u6c14\u548c\u5200\u5149\u9f50\u655b\u3002 \u3000\u3000\u653b\u5411\u9ad8\u5929\u5065\u7684\u7ea2\u82b1\u3001\u7eff\u53f6\uff0c\u624b\u4e2d\u7684\u516d\u652f\u5f69\u5251\uff0c\u90fd\u5df2\u53d8\u4e86\u534a\u622a\uff0c\u4e8c\u5973\u7f13\u7f13\u5012\u4e0b\u53bb\u3002 \u3000\u3000\u5c38\u4f53\u5012\u4e0b\u53bb\uff0c\u624d\u89c1\u4e8c\u5973\u9876\u95e8\u4e0a\u6d41\u51fa\u4e86\u9c9c\u8840\u3002 \u3000\u3000\u597d\u5feb\u7684\u5200\u6cd5\u3002 \u3000\u3000\u4f46\u516d\u622a\u65ad\u5251\uff0c\u5374\u4e5f\u6709\u4e09\u622a\uff0c\u63d2\u5728\u4e86\u9ad8\u5929\u5065\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u4e24\u622a\u63d2\u5728\u53cc\u80a9\u4e0a\uff0c\u4e00\u622a\u63d2\u5728\u4e86\u524d\u8138\u4e4b\u4e0a\u3002 \u3000\u3000\u9ad8\u5929\u5065\u8138\u8272\u5f88\u51b7\u8083\uff0c\u6a2a\u5200\u800c\u7acb\uff0c\u4ecd\u7136\u662f\u795e\u5a01\u51db\u51db\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\uff0c\u8fde\u53d7\u4e86\u4e94\u5904\u4f24\uff0c\u4e09\u652f\u65ad\u5251\uff0c\u4e24\u7247\u788e\u4e86\u7684\u91d1\u73af\u3002 \u3000\u3000\u4f46\u5374\u4ecd\u7136\u4e0d\u89c1\u4e00\u70b9\u9c9c\u8840\u3002 \u3000\u3000\u4f46\u89c1\u4eba\u5f71\u95ea\u52a8\uff0c\u534e\u5999\u771f\u548c\u7384\u5a18\uff0c\u5306\u5306\u56f4\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u9ec4\u7075\u5927\u6b65\u800c\u884c\uff0c\u76f4\u9047\u5230\u9ad8\u5929\u5065\u7684\u8eab\u524d\uff0c\u9053\uff1a \u201c\u9ad8\u5821\u4e3b\uff0c\u8fd8\u6709\u518d\u6218\u4e4b\u80fd\u5417?\u201d \u3000\u3000\u9ad8\u5929\u5065\u53cc\u76ee\u653e\u5149\uff0c\u4e0d\u53d1\u4e00\u8bed\u3002 \u3000\u3000\u9ec4\u7075\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u662f\u4e0d\u662f\u4f24\u52bf\u592a\u91cd\u4e86\uff0c\u91cd\u5f97\u4e0d\u80fd\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u9ad8\u5929\u5065\u8f7b\u8f7b\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u5ffd\u7136\u4e3e\u624b\u4e00\u5200\u5288\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u5200\uff0c\u5200\u52bf\u5947\u5e7b\u53d8\u5316\uff0c\u9ec4\u7075\u5438\u5165\u4e00\u53e3\u6c14\uff0c\u5ffd\u7136\u95f4\u5411\u540e\u9000\u5f00\u4e94\u6b65\u3002 \u3000\u3000\u4f46\u534e\u5999\u771f\u548c\u7384\u5a18\uff0c\u5374\u7531\u4e24\u4fa7\u75be\u653b\u800c\u4e0a\u3002 \u3000\u3000\u4e24\u652f\u5251\u5206\u5de6\u53f3\u653b\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u534e\u5999\u771f\u9664\u4e86\u957f\u5251\u4e4b\u5916\uff0c\u8fd8\u5e26\u4e86\u4e24\u679a\u5b50\u5348\u8ffd\u9b42\u7684\u6697\u5668? \u3000\u3000\u9ad8\u5929\u5065\u5200\u5149\u5982\u8f6e\uff0c\u5ffd\u7136\u95f4\uff0c\u5c55\u5e03\u6210\u4e00\u7247\u5200\u5e55\u3002 \u3000\u3000\u4f46\u95fb\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u4f20\u5165\u8033\u9645\uff0c\u5175\u5203\u548c\u9c9c\u8840\u9f50\u98de\u3002 \u3000\u3000\u4e00\u5207\u90fd\u6062\u590d\u5e73\u9759\u3002 \u3000\u3000\u51dd\u76ee\u671b\u53bb\uff0c\u53ea\u89c1\u7384\u5a18\u4e00\u6761\u5de6\u81c2\uff0c\u9f50\u8098\u800c\u65ad\uff0c\u9c9c\u8840\u72c2\u55b7\u800c\u51fa\u3002 \u3000\u3000\u534e\u5999\u771f\u6ca1\u6709\u65ad\u81c2\uff0c\u4f46\u5de6\u80a9\u4e00\u5200\uff0c\u76f4\u5212\u5230\u524d\u80f8\uff0c\u8863\u886b\u5206\u88c2\uff0c\u8840\u6d41\u534a\u8eab\u3002 \u3000\u3000\u770b\u4e0a\u53bb\uff0c\u5979\u7684\u4f24\u4e0d\u4f1a\u6bd4\u7384\u5a18\u8f7b\u3002 \u3000\u3000\u9ad8\u5929\u5065\u53c8\u591a\u4e86\u4e24\u5904\u4f24\u3002 \u3000\u3000\u7384\u5a18\u7684\u65ad\u81c2\uff0c\u9489\u5728\u9ad8\u5929\u5065\u5c0f\u817f\u4e4b\u4e0a\u3002 \u3000\u3000\u539f\u6765\uff0c\u7384\u5a18\u65bd\u5c55\u5929\u7edd\u6307\uff0c\u4e58\u865a\u800c\u5165\uff0c\u8fd9\u4e00\u6307\uff0c\u672c\u6765\u662f\u8981\u70b9\u4e2d\u5411\u9ad8\u5929\u5065\u7684\u592a\u9633\u7a74\u4e4b\u4e0a\uff0c\u4f46\u5374\u88ab\u9ad8\u5929\u5065\u4e00\u5200\u9707\u504f\u4e86\u65b9\u4f4d\u3002 \u3000\u3000\u5929\u7edd\u6307\u529b\u5df2\u53d1\uff0c\u7a7f\u77f3\u900f\u6728\u3002 \u3000\u3000\u9ad8\u5929\u5065\u4e00\u5200\u65a9\u4e86\u5de6\u81c2\uff0c\u4f46\u5374\u65a9\u4e0d\u65ad\u5929\u7edd\u6307\u529b\u3002 \u3000\u3000\u6307\u9510\u5982\u7bad\uff0c\u523a\u5165\u4e86\u9ad8\u5929\u5065\u7684\u5c0f\u8179\u3002 \u3000\u3000\u534e\u5999\u771f\u7684\u4e00\u5200\uff0c\u4e5f\u6ca1\u6709\u81ea\u62d4\uff0c\u5979\u7684\u4e24\u679a\u5b50\u5348\u900f\u9aa8\u9488\uff0c\u4e5f\u9489\u5728\u4e86\u9ad8\u5929\u5065\u524d\u80f8\u4e4b\u4e0a\u3002 \u3000\u3000\u5c1a\u5929\u4e49\u3001\u5c1a\u592b\u4eba\u75be\u5954\u800c\u6765\uff0c\u6276\u4e0b\u4e86\u7384\u5a18\u548c\u534e\u5999\u771f\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u70df\u9501\u697c\u5185\u75be\u51b2\u51fa\u5341\u4e2a\u6b66\u58eb\u3002 \u3000\u3000\u4f46\u5374\u88ab\u4e07\u80dc\u548c\u5341\u4e8c\u5200\u624b\uff0c\u62da\u547d\u6321\u4f4f\uff0c\u53cc\u65b9\u6fc0\u6218\u51f6\u5389\uff0c\u4f24\u4ea1\u5f88\u5927\uff0c\u4f46\u4e07\u80dc\u82e6\u6218\u4e0d\u9000\uff0c\u5bf9\u65b9\u4e5f\u65e0\u6cd5\u51b2\u51fa\u6765\u3002 \u3000\u3000\u9ec4\u7075\u7a81\u7136\u7531\u9ad8\u5929\u5065\u7684\u5200\u6cd5\u9886\u609f\u51fa\u4e86\u9ad8\u5929\u5065\u7684\u5904\u5883\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u8eab\u53d7\u91cd\u4f24\uff0c\u8eab\u8eaf\u7684\u79fb\u52a8\uff0c\u5df2\u7136\u4e0d\u7075\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u51f6\u5389\u7684\u5200\u6cd5\u4ecd\u5728\u3002 \u3000\u3000\u5f53\u4e0b\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ad8\u5929\u5065\uff0c\u4f60\u5df2\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u6491\u4e0d\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4f38\u624b\u642c\u8d77\u8eab\u4fa7\u4e00\u5757\u5de8\u77f3\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u5168\u529b\u7838\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9ad8\u5929\u5065\u679c\u7136\u65e0\u6cd5\u95ea\u907f\u3002\u4f46\u4ed6\u8fd8\u80fd\u8fd0\u5200\u3002 \u3000\u3000\u4e03\u661f\u5b9d\u5200\u4e00\u95ea\uff0c\u8fce\u5411\u5de8\u77f3\u65a9\u53bb\u3002 \u3000\u3000\u5200\u77f3\u76f8\u51fb\uff0c\u7830\u7136\u5927\u9707\u58f0\u4e2d\uff0c\u5de8\u77f3\u88ab\u5b9d\u5200\u5288\u6210\u4e86\u4e24\u534a\uff0c\u9ec4\u7075\u5374\u501f\u52bf\u6251\u4e86\u4e0a\u53bb\uff0c\u98de\u8d77\u4e00\u811a\uff0c\u8e22\u4e2d\u4e86\u9ad8\u5929\u5065\u7684\u5c0f\u8179\u3002 \u3000\u3000\u9ad8\u5929\u5065\u8eab\u5b50\u98de\u4e86\u8d77\u6765\uff0c\u91cd\u91cd\u5730\u6454\u5728\u5730\u4e0a\u3002 \u597d\u4e00\u4e2a\u9ad8\u5929\u5065\uff0c\u8fde\u53d7\u5982\u6b64\u91cd\u4f24\uff1b\u8eab\u5b50\u843d\u5730\u4e4b\u540e\uff0c\u7adf\u7136\u53c8\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5c1a\u592b\u4eba\u5927\u559d\u4e00\u58f0\uff0c\u94c1\u62d0\u8fce\u5934\u5288\u4e0b\u3002 \u3000\u3000\u9ad8\u5929\u5065\u8fd8\u80fd\u8fd0\u5200\uff0c\u5b9d\u5200\u4e00\u4e3e\u5f53\u7684\u4e00\u58f0\uff0c\u628a\u94c1\u62d0\u67b6\u5f00\u3002 \u3000\u3000\u5434\u53cc\u5973\u54ac\u7259\u51fa\u624b\uff0c\u53f3\u624b\u626c\u6325\uff0c\u624b\u81c2\u4e0a\u4e24\u679a\u91d1\u73af\uff0c\u8131\u98de\u800c\u51fa\u3002 \u3000\u3000\u91d1\u73af\u5212\u8d77\u4e01\u4e24\u9053\u51b7\u8292\uff0c\u75be\u5feb\u5982\u7535\u3002 \u3000\u3000\u9ad8\u5929\u5065\u60f3\u4e3e\u5200\u5c01\u67b6\uff0c\u4f46\u4ed6\u6765\u4e0d\u53ca\u4e86\uff0c\u91d1\u73af\u5206\u4e2d\u4e1c\u897f\u4e24\u9762\u592a\u9633\u7a74\u4e0a\uff0c\u6df1\u5165\u4e00\u5bf8\u591a\u6df1\u3002 \u3000\u3000\u7ef5\u8fde\u7684\u91cd\u4f24\u5df2\u4f7f\u4ed6\u65e0\u6cd5\u518d\u652f\u6491\u4e0b\u53bb\uff0c\u5f20\u5634\u55b7\u51fa\u4e86\u4e24\u53e3\u9c9c\u8840\uff0c\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u5f20\u5927\u5634\u5df4\uff0c\u4f3c\u4e4e\u662f\u6709\u5f88\u591a\u8bdd\u8981\u8bf4\uff0e\u4f46\u4ed6\u4f24\u52bf\u592a\u91cd\u4e86\uff0c\u91cd\u7684\u8bf4\u4e0d\u51fa\u4e00\u53e5\u8bdd\u6765\u3002\u4e00\u4ee3\u82f1\u96c4\uff0c\u5200\u738b\uff0c\u5c31\u662f\u8fd9\u6837\u6b7b\u53bb\u4e86\uff0c\u8fde\u2014\u53e5\u9057\u8a00\uff0c\u4e5f\u6ca1\u6709\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u5168\u8eab\u90fd\u5d4c\u6ee1\u4e86\u5175\u5203\uff0c\u6697\u5668\u3002\u4f46\u9634\u9633\u5821\u8fd9\u9762\u7684\u4f24\u4ea1\uff0c\u4e5f\u5f88\u91cd\u5927\uff0c\u7ea2\u82b1\u3001\u7eff\u53f6\u6218\u6b7b\uff0c\u7384\u5a18\u65ad\u81c2\uff0c\u9ec4\u7075\u3001\u534e\u5999\u771f\u3001\u5434\u53cc\u5973\uff0c\u90fd\u53d7\u4e86\u4f24\u800c\u4e14\u90fd\u4f24\u7684\u4e0d\u8f7b\u3002 \u3000\u3000\u6700\u53eb\u4eba\u70e6\u607c\u7684\u662f\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u8fd9\u4e00\u4e2a\u5929\u4e0b\u7b2c\u4e00\u5947\u5251\u9ad8\u624b\uff0c\u8eab\u4e2d\u5947\u6bd2\uff0c\u9ad8\u5929\u5065\u8fd9\u4e00\u6b7b\uff0c\u771f\u4e0d\u77e5\u5982\u4f55\u53bb\u89e3\u5979\u8eab\u4e2d\u4e4b\u6bd2\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u70df\u9501\u697c\u5916\u7684\u6218\u6597\uff0c\u5e76\u672a\u7ec8\u6b62\uff0c\u53ea\u4e0d\u8fc7\uff0c\u9ad8\u5929\u5065\u6b7b\u4e86\uff0c \u3000\u3000\u4e07\u80dc\u7387\u9886\u7684\u5200\u624b\uff0c\u4ecd\u7136\u5835\u4f4f\u4e86\u70df\u9501\u697c\u95e8\uff0c\u53cc\u65b9\u6fc0\u6218\u640f\u6740\uff0c\u4e5f\u662f\u51f6\u5389\u7edd\u4f26\uff0c\u4e07\u80dc\u6700\u7cbe\u9510\u7684\u5200\u624b\uff0c\u4e5f\u53ea\u4f59\u4e0b\u4e86\u4e5d\u4e2a\u4eba\uff0c\u5176\u4ed6\u7684\uff0c\u5927\u90fd\u6218\u6b7b\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u8fd9\u4e9b\u5200\u624b\uff0c\u5341\u5206\u52c7\u6562\uff0c\u53d7\u4f24\u4ea6\u4e0d\u80af\u540e\u9000\uff0c\u552f\u6c42\u6218\u6b7b\u800c\u5df2\u3002 \u3000\u3000\u51e0\u540d\u9a81\u52c7\u5584\u6218\u7684\u5973\u5c06\uff0c\u4e5f\u5728\u4e00\u573a\u6fc0\u70c8\u7684\u640f\u6740\u4e2d\uff0c\u6b7b\u4ea1\u6b86\u5c3d\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u6210\u529f\u7684\u5835\u4f4f\u4e86\u70df\u9501\u697c\u4e2d\u4eba\uff0c\u4ed6\u4eec\u65e0\u6cd5\u5411\u5916\u9a70\u63f4\uff0c\u4f46\u4e5f\u65e0\u6cd5\u5173\u95ed\u4e0a\u697c\u95e8\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u573a\u6fc0\u70c8\u7684\u6218\u6597\u3002 \u3000\u3000\u968f\u9ad8\u5929\u5065\u4e4b\u540e\uff0c\u9ec4\u7075\u65b9\u9762\uff0c\u4e5f\u6709\u5927\u90e8\u5206\u7cbe\u9510\uff0c\u4e27\u5931\u4e86\u6218\u6597\u529b\u3002 \u3000\u3000\u76ee\u4e0b\u80fd\u591f\u6218\u6597\u7684\uff0c\u9634\u9633\u5821\u65b9\u9762\uff0c\u53ea\u6709\u53f6\u957f\u9752\u3001\u4f55\u5bd2\u8863\u3001\u5c1a\u5929\u4e49\u592b\u5987\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\uff0c\u90fd\u6709\u5173\u5fc3\u7684\u4eba\u53d7\u4e86\u91cd\u4f24\u3002 \u3000\u3000\u5148\u66ff\u4f24\u8005\u5305\u624e\u597d\u4e4b\u540e\uff0c\u7531\u5c1a\u592b\u4eba\u7167\u987e\u4ed6\u4eec\u4f11\u606f\uff0c\u4f55\u5bd2\u8863\u3001\u53f6\u957f\u9752\u3001\u5c1a\u5929\u4e49\uff0c\u53c8\u6295\u5165\u4e86\u6218\u6597\u3002 \u3000\u3000\u9ec4\u7075\u5728\u91cd\u4f24\u4e4b\u540e\uff0c\u53c8\u5168\u529b\u51fa\u624b\uff0c\u4f7f\u5f97\u4f24\u53e3\u8fdb\u88c2\uff0c\u9c9c\u8840\u67d3\u6e7f\u4e86\u534a\u4e2a\u8eab\u5b50\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u5f3a\u5fcd\u4f24\u75bc\uff0c\u79fb\u5230\u4e86\u9ad8\u5e7d\u5170\u7684\u8eab\u4fa7\uff0c\u89e3\u5f00\u4e86\u9ad8\u5e7d\u5170\u8eab\u4e0a\u88ab\u70b9\u7684\u51e0\u5904\u7a74\u9053\u3002\u4f4e\u58f0\u9053\uff1a \u3000\u3000\u201c\u59d1\u5a18\uff0c\u4f60\u770b\u51fa\u54b1\u4eec\u7684\u5371\u673a\u6ca1\u6709?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u62c5\u5fc3\u96f7\u65e0\u5f71?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bf9!\u5982\u82e5\u96f7\u65e0\u5f71\u7387\u9886\u4e86\u4eba\u624b\uff0c\u5411\u54b1\u4eec\u653b\u51fb\uff0c\u54b1\u4eec\u52bf\u5fc5\u5168\u519b\u8986\u6ca1\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u4f60\u80fd\u89e3\u53bb\u8eab\u4e2d\u4e4b\u6bd2\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u8981\u591a\u5c11\u65f6\u95f4\uff0c\u80fd\u518d\u548c\u4eba\u52a8\u624b?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u6211\uff0c\u7384\u5a18\uff0c\u534e\u5999\u771f\uff0c\u771f\u8981\u62da\u547d\uff0c\u4e5f\u6ca1\u6709\u529e\u6cd5\u548c\u4eba\u7f20\u6218\uff0c\u4e00\u4e24\u62db\uff0c\u6c42\u4e2a\u540c\u5f52\u4e8e\u5c3d\uff0c\u771f\u8981\u6062\u590d\u6218\u6597\u80fd\u529b\uff0c\u81f3\u5c11\u8981\u4e09\u4e24\u5929\u4ee5\u4e0a\u7684\u517b\u606f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5434\u59d1\u5a18\u4e5f\u4e0d\u4f1a\u8f7b\uff0c\u6211\u770b\uff0c\u548c\u6211\u4eec\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5728\u95ed\u76ee\u517b\u606f\uff0c\u95fb\u58f0\u5ffd\u7136\u7741\u5f00\u773c\u775b\uff0c\u9053\uff1a\u201c\u6211\u53ea\u8981\u516d\u4e2a\u65f6\u8fb0\uff0c\u5c31\u53ef\u4ee5\u6062\u590d\u5927\u90e8\u5206\u6218\u6597\u80fd\u529b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u96f7\u65e0\u5f71\u5982\u8981\u51fa\u624b\uff0c \u4ed6\u4e0d\u4f1a\u7ed9\u6211\u4eec\u90a3\u4e48\u591a\u65f6\u95f4\uff0c\u5927\u59ca\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u4f60\u5fc5\u987b\u7b54\u5e94\uff0c\u96f7\u65e0\u5f71\u4e0d\u5e78\u6765\u4e34\uff0c\u4f60\u5c31\u7528\u4f60\u7684\u9b54\u624b\uff0c\u6fc0\u53d1\u6211\u751f\u547d\u4e2d\u7684\u6f5c\u529b\uff0c\u8fd9\u662f\u552f\u4e00\u7684\u62d2\u654c\u529e\u6cd5\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u6211\u4eec\u90fd\u4e0d\u6015\u6b7b\uff0c\u5982\u82e5\u60c5\u52bf\u5230\u4e86\u975e\u6b7b\u4e0d\u8db3\u4ee5\u89e3\u51b3\u95ee\u9898\u7684\u65f6\u95f4\uff0c\u6211\u4eec\u90fd\u4f1a\u633a\u8eab\u800c\u51fa\uff0c\u6211\u4f1a\u914c\u91cf\u60c5\u5f62\uff0c\u6ca1\u6709\u522b\u7684\u9009\u62e9\u65f6\uff0c\u6211\u4f1a\u7528\u9b54\u624b\u52a9\u4f60\uff0c\u4f7f\u4f60\u4e3a\u6c5f\u6e56\u6b63\u4e49\uff0c\u5c3d\u4e00\u5206\u5fc3\u529b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a \u201c\u5982\u82e5\u6211\u8eab\u4e2d\u4e4b\u6bd2\uff0c\u771f\u6709\u89e3\u836f\uff0c\u4e5f\u4f1a\u88ab\u9ad8\u5929\u5065\u85cf\u7684\u5f88\u9690\u79d8\uff0c\u5927\u59ca\uff0c\u5982\u82e5\u8981\u6211\u6b7b\u4e8e\u836f\u7269\u6bd2\u6027\u7684\u6467\u6b8b\u4e4b\u4e0b\uff0c\u90a3\u8fd8\u4e0d\u5982\u8981\u6211\u6b7b\u4e8e\u8f70\u8f70\u70c8\u70c8\u7684\u640f\u6218\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u5170\u59b9\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u8fd9\u53ea\u9b54\u624b\uff0c\u4e00\u5b9a\u4f1a\u5e2e\u52a9\u4f60\u5f97\u507f\u5fc3\u613f\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u662f\u73b0\u5728\uff0c\u800c\u662f\u5230\u4e86\u7edd\u65e0\u529e\u6cd5\u53ef\u60f3\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u603b\u5e0c\u671b\u4e0d\u53ef\u592a\u8fdf\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u9ec4\u7075\u7a81\u7136\u6323\u624e\u800c\u8d77\u3002 \u3000\u3000\u534e\u5999\u771f\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u8981\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u6211\u652f\u6491\u5f97\u4f4f\uff0c\u6211\u8981\u53bb\u8d4c\u8d4c\u8fd0\u6c14\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u9ec4\u7075\u884c\u8fd1\u4e86\u9ad8\u5929\u5065\u7684\u5c38\u4f53\u3002 \u3000\u3000\u5434\u53cc\u5973\u4f4e\u58f0\u9053\uff1a \u201c\u5170\u59b9\uff0c\u6211\u4e0d\u60f3\u4f60\u6b7b\uff0c\u4f55\u90ce\u66f4\u4e0d\u60f3\u4f60\u6b7b\uff0c\u6240\u4ee5\uff0c\u4f60\u8981\u4e89\u53d6\u6bcf\u4e00\u5206\u6d3b\u4e0b\u53bb\u7684\u5e0c\u671b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u81ea\u5df1\u6709\u4e00\u79cd\u5f88\u60ed\u6127\u7684\u611f\u89c9\uff0c\u5927\u59ca\uff0c\u6211\u4e0d\u8be5\u593a\u4f60\u6240\u7231\uff0c\u73b0\u5728\uff0c\u6211\u5df2\u7ecf\u65e0\u6cd5\u56de\u5934\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u5c31\u662f\u56e0\u4e3a\u8fd9\u4ef6\u4e8b\uff0c\u624d\u8981\u6b7b\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u5343\u4e07\u4e0d\u80fd\u6709\u8fd9\u79cd\u60f3\u6cd5\uff0c\u6211\u4eec\u73b0\u5728\u662f\u4e09\u4f4d\u4e00\u4f53\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u95ed\u4e0a\u773c\u775b\uff0c\u8033\u6735\u5374\u7ad6\u8d77\u6765\uff0c\u542c\u5f97\u5374\u4ed4\u7ec6\uff0c\u5fc3\u4e2d\u4e5f\u5f88\u591a\u611f\u6168\u3002 \u3000\u3000\u5979\u4e45\u5e74\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u9605\u5386\u7684\u4e8b\u7269\u3001\u4eba\u60c5\uff0c\u8d85\u8fc7\u5e38\u4eba\u5f88\u591a\uff0c\u5979\u5bf9\u60c5\u5b57\u4e00\u5173\uff0c\u4ecd\u7136\u662f\u52d8\u5b83\u4e0d\u7834\u3002 \u3000\u3000\u5979\u4ece\u6765\u6ca1\u6709\u5bf9\u9ec4\u7075\u6709\u8fc7\u4ec0\u4e48\u60f3\u6cd5\uff0c\u4f46\u7384\u5a18\u8fd9\u4e2a\u4eba\uff0c\u5ffd\u7136\u95f4\uff0c\u628a\u5979\u62d6\u5165\u4e86\u9ec4\u7075\u8fd9\u4e2a\u6f29\u6da1\u4e4b\u4e2d\uff0c\u5728\u5979\u7684\u5fc3\u7075\u4e4b\u4e2d\uff0c\u6295\u4e0b\u4e86\u4e00\u5757\u5de8\u5927\u7684\u77f3\u5757\u3002 \u3000\u3000\u8fd9\u4f7f\u5979\u4e09\u5341\u5e74\u591a\u5e73\u9759\u7684\u5fc3\u60c5\uff0c\u5ffd\u7136\u95f4\uff0c\u8d77\u4e86\u4e00\u79cd\u6d9f\u6f2a\u3002 \u3000\u3000\u4e0d\u81ea\u89c9\uff0c\u5979\u5f00\u59cb\u5bf9\u9ec4\u7075\u4ea7\u751f\u4e86\u4e00\u79cd\u79c1\u4eba\u7684\u60c5\u610f\uff0c\u5173\u5207\u3002 \u3000\u3000\u534e\u5999\u771f\u6709\u4e00-\u4ef6\u4e00\u76f4\u60f3\u4e0d\u901a\u7684\u4e8b\uff0c\u90a3\u5c31\u662f\u7384\u5a18\u4e3a\u4ec0\u4e48\u8981\u62d6\u4f4f\u5979\u3002 \u3000\u3000\u7167\u7406\u8bf4\uff0c\u7537\u5973\u4e4b\u95f4\u90fd\u4e0d\u5e0c\u671b\u4f1a\u6709\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u4ecb\u5165\u8fdb\u6765\u3002 \u3000\u3000\u7384\u5a18\uff0c\u4e3a\u4ec0\u4e48\u8981\u6545\u610f\u62c9\u4e00\u4e2a\u4eba\u8fdb\u6765\u3002 \u3000\u3000\u534e\u5999\u771f\u60f3\u4e0d\u901a\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u4f46\u5979\u542c\u5230\u4e86\u5434\u53cc\u5973\u548c\u9ad8\u5e7d\u5170\u7684\u4ea4\u8c08\u4e4b\u540e\uff0c\u5fc3\u4e2d\u7acb\u523b\u5f15\u8d77\u4e86\u4e00\u79cd\u5fae\u5999\u7684\u5173\u6000\u3002 \u3000\u3000\u8868\u9762\u4e0a\uff0c\u5979\u95ed\u7740\u773c\u775b\uff0c\u5176\u5b9e\uff0c\u5168\u795e\u8d2f\u6ce8\u542c\u4e24\u4eba\u7684\u8c08\u8bdd\u3002 \u3000\u3000\u5979\u4eec\u4f24\u52bf\u4e0d\u8f7b\u3002\u4f46\u5979\u4eec\u90fd\u6709\u7740\u5f88\u597d\u7684\u5185\u529f\u57fa\u7840\u3002 \u3000\u3000\u5979\u4eec\u627f\u53d7\u75db\u82e6\u7684\u97e7\u6027\u6bd4\u4e00\u822c\u4eba\u5f3a\u5927\uff0c\u514b\u5236\u75db\u82e6\u7684\u80fd\u529b\uff0c\u4e5f\u5f02\u4e8e\u5e38\u4eba\u3002 \u3000\u3000\u9ec4\u7075\u884c\u5230\u4e86\u9ad8\u5929\u5065\u524d\u9762\uff0c\u8e72\u4e0b\u8eab\u5b50\uff0c\u5728\u5c38\u4f53\u4e0a\u641c\u67e5\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u627e\u5230\u4e86\u4e0d\u5c11\u7684\u4e1c\u897f\uff0c\u6709\u5f88\u591a\u7389\u74f6\u76db\u88c5\u7684\u836f\u7269\uff0c\u9ec4\u7075\u5e26\u7740\u90a3\u4e9b\u836f\u7269\uff0c\u7f13\u6b65\u884c\u4e86\u56de\u6765\u3002 \u3000\u3000\u884c\u5230\u4e86\u9ad8\u5e7d\u5170\u7684\u8eab\u524d\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u91cc\u6709\u4e94\u74f6\u836f\u7269\uff0c\u4f60\u770b\u770b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u8fd9\u4e9b\u836f\u7269\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a \u201c\u9ad8\u5929\u5065\u8fd9\u4e2a\u4eba\u5f88\u96be\u6d4b\uff0c\u4ed6\u771f\u6b63\u76f8\u4fe1\u7684\u4eba\uff0c\u6211\u770b\u5c31\u662f\u4ed6\u81ea\u5df1\uff0c\u6240\u4ee5\uff0c\u6700\u91cd\u8981\u7684\u4e1c\u897f\uff0c\u5c31\u5fc5\u5b9a\u653e\u5728\u4ed6\u7684\u8eab\u4e0a\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5821\u4e3b\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4e5f\u8bb8\u8fd9\u51e0\u74f6\u836f\u7269\u4e4b\u4e2d\uff0c\u5c31\u6709\u4e00\u74f6\u662f\u89e3\u4f60\u8eab\u4e0a\u4e4b\u6bd2\u7684\u836f\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5c31\u7b97\u662f\u6709\uff0c\u54b1\u4eec\u4e5f\u6ca1\u6709\u529e\u6cd5\u9009\u51fa\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6709\u529e\u6cd5\uff0c\u6211\u4e5f\u5e26\u6765\u4e86\u90a3\u4efd\u8840\u4e66\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8840\u4e66\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a \u201c\u5bf9\uff01\u8840\u4e66\uff0c \u6709\u8840\u4e66\uff0c\u54b1\u4eec\u5c31\u53ef\u4ee5\u8bd5\u51fa\u8fd9\u4e9b\u836f\u7269\u4e2d\uff0c\u6709\u6ca1\u6709\u89e3\u836f\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8981\u8c01\u8bd5\u9a8c\u5462?\u201d \u3000\u3000\u6708\u5149\u4e0b\uff0c\u4e00\u89d2\u6697\u5f71\u4e2d\uff0c\u7f13\u6b65\u884c\u51fa\u4e86\u4e00\u4e2a\u5c11\u5973\uff0c\u9053\uff1a\u201c\u6211\u6765\u3002\u201d \u3000\u3000\u662f\u96ea\u6885\u3002 \u3000\u3000\u5434\u53cc\u5973\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6765\u4e86?\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a \u201c\u5c0f\u5a62\u6765\u4e86\u4e00\u9635\u5b50\uff0c \u89c1\u8bf8\u4f4d\u640f\u6740\u6fc0\u70c8\uff0c\u5c0f\u5a62\u81ea\u77e5\u65e0\u6cd5\u6297\u62d2\uff0c\u6240\u4ee5\uff0c\u6ca1\u6709\u73b0\u8eab\uff0c\u73b0\u5728\uff0c\u8fd9\u8bd5\u6bd2\u7684\u4e8b\uff0c\u5c0f\u5a62\u6b63\u597d\u6548\u529b\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a \u201c\u8fd9\u8981\u4f1a\u6b66\u529f\u7684\u4eba\uff0c\u624d\u80fd\u6b63\u786e\u8bd5\u9a8c\uff0c \u4f60\u4e0d\u4f1a\u6b66\u529f\u3002\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a \u201c\u5c0f\u5a62\u8ddf\u7740\u5e7d\u5170\u59d1\u5a18\uff0c\u4e5f\u5b66\u5f97\u4e86\u4e00\u4e9b\u8c03\u606f\u4e4b\u672f\uff0c\u81ea\u4fe1\u80fd\u5f53\u6b64\u4efb\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u597d!\u8fd9\u4e5f\u662f\u4f60\u4e00\u7247\u6551\u4e3b\u5fe0\u5fc3\uff0c\u4f60\u8fc7\u6765\u5427!\u201d \u3000\u3000\u96ea\u6885\u7f13\u6b65\u884c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9ec4\u7075\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u80fd\u591f\u8fd0\u6c14\u5417?\u201d \u3000\u3000\u96ea\u6885\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u4f38\u51fa\u5de6\u624b\uff0c\u634f\u634f\u8fd9\u5f20\u8840\u4e66\u3002\u201d \u3000\u3000\u96ea\u6885\u4f9d\u8a00\u65bd\u4e3a\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u9ec4\u7075\u53c8\u9053\uff1a\u201c\u653e\u5f00\u624b\uff0c\u8fd0\u6c14\u8bd5\u8bd5?\u201d \u3000\u3000\u96ea\u6885\u4f9d\u8a00\u65bd\u4e3a\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8bf4\u51fa\u6765\u4f60\u7684\u611f\u89c9\u3002\u201d \u3000\u3000\u96ea\u6885\u9053\uff1a\u201c\u597d\u50cf\u4e00\u79cd\u529b\u91cf\uff0c\u63a7\u5236\u7740\u56db\u80a2\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5e7d\u5170\u59d1\u5a18\uff0c\u662f\u4e0d\u662f\u8fd9\u79cd\u611f\u89c9\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u8d3e\u4f3c\u9053\u51fa\u73b0\u5728\u697c\u95e8\u53e3\u5904\uff0c\u62d2\u6321\u4f4f\u4e86\u4f55\u5bd2\u8863\u548c\u53f6\u957f\u9752\u7684\u731b\u653b\u3002 \u3000\u3000\u9ec4\u7075\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u770b\u60c5\u5f62\uff0c\u5982\u82e5\u65e0\u9ad8\u624b\u52a0\u5165\u6218\u6597\uff0c\u653b\u4eba\u70df\u9501\u697c\uff0c\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u96ea\u6885\uff0c\u4f60\u9762\u524d\u6709\u4e94\u79cd\u836f\u7269\uff0c\u4e00\u79cd\u4e00\u79cd\u7684\u8bd5\u9a8c\u5427\uff0c\u5403\u4e00\u79cd\u836f\u7269\uff0c\u5c31\u8fd0\u6c14\u3002\u201d \u3000\u3000\u96ea\u6885\u5f88\u542c\u8bdd\uff0c\u4e00\u79cd\u79cd\u7684\u8bd5\u9a8c\uff0c\u5230\u4e86\u7b2c\u4e09\u79cd\u836f\u7269\u65f6\uff0c\u96ea\u6885\u7a81\u7136\u8138\u8272\u5927\u53d8\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u7b2c\u4e09\u79cd\u836f\u7269\u4e0d\u80fd\u5403\uff0c\u8fd9\u662f\u6bd2\u7269\u3002\u201d \u3000\u3000\u96ea\u6885\u5403\u4e0b\u7b2c\u56db\u79cd\u836f\u7269\uff0c\u6bd2\u6027\u4ecd\u7136\u53d1\u4f5c\u3002 \u3000\u3000\u4f46\u5979\u5374\u541e\u4e0b\u4e86\u7b2c\u4e94\u79cd\u836f\u7269\u3002 \u3000\u3000\u5947\u8ff9\u51fa\u73b0\u4e86\u3002 \u3000\u3000\u96ea\u6885\u8eab\u4e0a\u7684\u6bd2\u6027\uff0c\u7adf\u7136\u89e3\u53bb\u4e86\u3002 \u3000\u3000\u9ec4\u7075\u5ffd\u7136\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u662f\u4e86\uff0c\u662f\u4e00\u79cd\u6df7\u6bd2\u89e3\u836f\uff0c\u4ee5\u6bd2\u653b\u6bd2\u3002\u201d \u96ea\u6885\u56e0\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u524d\u9762\u4e24\u79cd\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u836f\u7269\uff0c\u5403\u8d77\u6765\uff0c\u6ca1\u6709\u4ec0\u4e48\u4f5c\u7528?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u96ea\u6885\uff0c\u670d\u4f8d\u5e7d\u5170\u59d1\u5a18\u5403\u4e0b\u540e\u9762\u4e09\u79cd\u836f\u7269\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5403\u4e0b\u4e86\u4e09\u79cd\u836f\u7269\u3002 \u3000\u3000\u8fd0\u6c14\u4e00\u8bd5\uff0c\u8eab\u4e2d\u4e4b\u6bd2\u7adf\u771f\u89e3\u53bb\u3002 \u3000\u3000\u9ec4\u7075\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e7d\u5170\u59d1\u5a18\uff0c\u7531\u73b0\u5728\u5f00\u59cb\uff0c\u6240\u6709\u7684\u4e8b\u60c5\uff0c\u90fd\u7531\u4f60\u4e3b\u6301\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u7a81\u7136\uff0c\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5e78\u5999\u771f\u6025\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u4ed6\u4e3a\u4e86\u6551\u6211\uff0c\u8017\u5fc3\u52b3\u529b\uff0c\u5f3a\u81ea\u652f\u6301\uff0c\u73b0\u4e0b\u4e8b\u60c5\u6709\u4e86\u7ed3\u679c\uff0c\u5fc3\u795e\u4e00\u61c8\uff0c\u6655\u4e86\u8fc7\u53bb\uff0c\u6211\u4f1a\u5e2e\u4ed6\u9192\u8fc7\u6765\u3002\u201d \u3000\u3000\u6276\u8d77\u9ec4\u7075\uff0c\u52a9\u4ee5\u5185\u529b\uff0c\u5f85\u9ec4\u7075\u9192\u4e86\u4e4b\u540e\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u597d\u597d\u8fd0\u6c14\u8c03\u606f\uff0c\u522b\u518d\u60f3\u4efb\u4f55\u7684\u4e8b\u4e86\uff0c\u8fd9\u91cc\u7684\u4e8b\u60c5\uff0c\u4ea4\u7ed9\u6211\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u95ed\u76ee\u8c03\u606f\u3002 \u3000\u3000\u671b\u671b\u5929\u8272\uff0c\u5df2\u5230\u4e86\u7389\u5154\u897f\u6c89\uff0c\u5929\u8272\u5c06\u660e\u7684\u65f6\u523b\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5e76\u672a\u7acb\u523b\u884c\u52a8\uff0c\u8fd0\u6c14\u8c03\u606f\u4e00\u9635\uff0e\u4fdf\u529f\u529b\u5b8c\u5168\u6062\u590d\uff0c\u624d\u7ad9\u8d77\u8eab\u5b50\uff0c\u4f38\u5c55\u4e00\u4e0b\u53cc\u81c2\uff0c\u6574\u6574\u4e03\u8272\u5b9d\u5251\uff0c\u4f4e\u58f0\u5bf9\u96ea\u6885\u8bf4\u9053\uff1a\u201c\u7167\u987e\u4f24\u8005\uff0c\u6ce8\u89c6\u6765\u8def\uff0c\u5982\u6709\u4eba\u6765\u8fd9\u91cc\uff0c\u7acb\u523b\u53eb\u6211\u3002\u201d \u3000\u3000\u4e3e\u6b65\u884c\u5411\u70df\u9501\u697c\u3002 \u3000\u3000\u70df\u9501\u697c\u95e8\u5916\u7684\u6218\u4e8b\uff0c\u4ecd\u7136\u5341\u5206\u6fc0\u70c8\uff0c\u4f55\u5bd2\u8863\u3001\u53f6\u957f\u9752\uff0c\u5df2\u66ff\u4ee3\u4e86\u5927\u6279\u5200\u624b\uff0c\u4f7f\u4ed6\u4eec\u6709\u4e00\u4e2a\u4f11\u606f\u7684\u673a\u4f1a\u3002 \u3000\u3000\u53f6\u957f\u9752\u7684\u8ffd\u9b42\u4e03\u5251\uff0c\u548c\u4f55\u5bd2\u8863\u7684\u4e00\u652f\u91d1\u5251\uff0c\u5177\u6709\u5f88\u5f3a\u5927\u7684\u5a01\u529b\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\uff0c\u51cc\u5389\u7684\u5251\u6cd5\uff0c\u914d\u5408\u65bd\u5c55\uff0c\u5c01\u6b7b\u4e86\u70df\u9501\u697c\u7684\u5927\u95e8\u3002 \u3000\u3000\u5343\u9488\u4e07\u8292\u548c\u8d3e\u4f3c\u9053\uff0c\u90fd\u5df2\u73b0\u8eab\uff0c\u4f46\u4e24\u4e2a\u4eba\u7684\u6597\u5fd7\uff0c\u5374\u56e0\u9ad8\u5929\u5065\u4e4b\u6b7b\uff0c\u53d7\u4e86\u5f88\u5927\u7684\u5f71\u54cd\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u4e00\u76f4\u6ca1\u6709\u65bd\u5c55\u98de\u8f6e\u7edd\u6280\uff0c\u56e0\u4e3a\uff0c\u4ed6\u4e00\u76f4\u8fd8\u6ca1\u6709\u9047\u4e0a\u4ec0\u4e48\u5371\u6025\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u884c\u5230\u4e86\u697c\u95e8\u53e3\u5904\uff0c\u5a07\u58f0\u8bf4\uff1a \u201c\u53f6\u5144\u3001\u4f55\u90ce\uff0c\u4e24\u4f4d\u8bf7\u9000\u4e0b\u6765\u5427!\u201d \u3000\u3000\u4e24\u4eba\u8054\u624b\u6025\u653b\u4e86\u4e09\u5251\uff0c\u5411\u540e\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u70df\u9501\u697c\u4e2d\u7684\u6307\u6325\u5927\u6743\uff0c\u4f3c\u662f\u5df2\u843d\u5728\u4e86\u8d3e\u4f3c\u9053\u7684\u624b\u4e2d\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u56de\u987e\u9ad8\u5e7d\u5170\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u597d\u4e86?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u662f\uff0c\u9ec4\u5821\u4e3b\u4ee5\u65e0\u4e0a\u667a\u6167\uff0c\u5e2e\u6211\u89e3\u53bb\u4e86\u8eab\u4e2d\u4e4b\u6bd2\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5b8c\u5168\u590d\u539f\u4e86\u3002\u201d \u3000\u3000\u53e3\u6c14\u4e2d\uff0c\u6d41\u9732\u51fa\u65e0\u9650\u7684\u5173\u6000\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u73b0\u5728\u4e0d\u4f46\u529f\u529b\u5c3d\u590d\uff0c\u800c\u4e14\uff0c\u8fd8\u7cbe\u795e\u5f88\u597d\uff0c\u4e0d\u7528\u6302\u5fc3\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u8981\u51fa\u624b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u4eec\u5f88\u7d2f\u4e86\uff0c\u8be5\u4f11\u606f\u4e00\u4f1a\u513f\uff0c\u8ba9\u6211\u6765\u8bd5\u8bd5\u5427!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u8981\u591a\u591a\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d\u7f13\u6b65\u5411\u524d\u884c\u53bb\u3002 \u3000\u3000\u76ee\u5149\u4e00\u63a0\u8d3e\u4f3c\u9053\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8d3e\u4f3c\u9053\uff0c\u6211\u60f3\u4f60\u5df2\u7ecf\u5f88\u65e9\u6f5c\u4f0f\u5728\u7b2c\u4e00\u5821\u4e86?\u201d \u3000\u3000\u8d3e\u4f3c\u9053\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u5728\u7b2c\u4e00\u5821\u4f4f\u4e9b\u65f6\u95f4\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4f60\u5e94\u8be5\u5fc3\u4e2d\u660e\u767d\uff0c\u73b0\u5728\uff0c\u4f60\u5e94\u8be5\u6ca1\u6709\u4ec0\u4e48\u5e0c\u671b\u4e86\u3002\u201d \u8d3e\u4f3c\u9053\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u6295\u964d\u4e86\u3002\u201d \u3000\u3000\u8d3e\u4f3c\u9053\uff1a\u201c\u6295\u964d\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u653e\u4e0b\u5175\u5203\uff0c\u542c\u9ec4\u5821\u4e3b\u7684\u5904\u7f6e\u3002\u201d \u3000\u3000\u8d3e\u4f3c\u9053\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1b\u201c\u5982\u82e5\u662f\u9ec4\u5821\u4e3b\u8981\u6740\u4e86\u6211?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4fdd\u8bc1\u4e0d\u4f1a\u6740\u4f60\uff0c\u4f46\u6211\u4e0d\u80fd\u4fdd\u8bc1\u4f60\u4e0d\u53d7\u4e00\u70b9\u60e9\u7f5a\u3002\u201d \u3000\u3000\u8d3e\u4f3c\u9053\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4eec\u867d\u7136\u6740\u4e86\u9ad8\u5929\u5065\uff0c\u4f60\u4eec\u4e5f\u6709\u4e86\u5f88\u5927\u7684\u4f24\u4ea1\uff0c\u5728\u4e0b\u51b7\u773c\u65c1\u89c2\uff0c\u8fd8\u80fd\u518d\u6218\u7684\u4eba\uff0c\u4e5f\u6ca1\u6709\u51e0\u4e2a\u4e86?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u540c\u4e86!\u201d \u3000\u3000\u8d3e\u4f3c\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u540c?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u56e0\u4e3a\uff0c\u6211\u8fd8\u597d\u597d\u7684\uff0c\u9ad8\u5929\u5065\u5728\u6211\u8eab\u4e0a\u4e0b\u4e86\u6bd2\uff0c\u4f46\u4ed6\u6ca1\u6709\u628a\u6211\u6bd2\u5f97\u5012\u4e0b\u53bb\u3002\u201d \u3000\u3000\u8d3e\u4f3c\u9053\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u8fd9\u4e5f\u662f\u6211\u4eec\u80af\u548c\u4f60\u8c08\u5224\u7684\u539f\u56e0\uff0c\u56e0\u4e3a\uff0c\u4f60\u59d1\u5a18\u8fd8\u597d\u597d\u7684\u6d3b\u7740\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8d3e\u4f3c\u9053\uff0c\u4f60\u5982\u4e0d\u613f\u675f\u624b\u5c31\u7f1a\uff0c\u90a3\u5c31\u653e\u624b\u4e00\u6218\u5427\u3002\u201d \u3000\u3000\u8d3e\u4f3c\u9053\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u6211\u4eec\u5df2\u89c1\u8bc6\u8fc7\u4e03\u8272\u5251\u7684\u5389\u5bb3\uff0c\u6211\u4eec\u4e0d\u4f1a\u548c\u4f60\u52a8\u624b\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8d3e\u4f3c\u9053\uff0c\u8fd9\u53ea\u6015\u7531\u4e0d\u5f97\u4f60\u4e86\u3002\u201d \u3000\u3000\u8d3e\u4f3c\u9053\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u6211\u4eec\u53ef\u4ee5\u9009\u62e9\u4e00\u4e2a\u51b3\u6218\u7684\u5730\u70b9\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4f60\u4eec\u5df2\u7ecf\u65e0\u6cd5\u79bb\u5f00\u70df\u9501\u697c\u4e86\uff0c\u96be\u9053\u8fd8\u80fd\u98de\u4e0a\u5929\u53bb\u4e0d\u6210\u3002\u201d \u8d3e\u4f3c\u9053\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6211\u4eec\u81f3\u5c11\u53ef\u4ee5\u9009\u62e9\u5728\u8fd9\u70df\u9501\u697c\u5185\u548c\u4f60\u52a8\u624b\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u597d\u5427\uff0c \u4f60\u65e2\u7136\u4e0d\u89c1\u68fa\u6750\u4e0d\u6389\u6cea\uff0c\u672c\u59d1\u5a18\u4e5f\u6ca1\u6709\u529e\u6cd5\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\u5411\u524d\u884c\u4e86\u4e24\u6b65\uff0c\u4e00\u5251\u523a\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u90a3\u662f\u5341\u5206\u51cc\u5389\u7684\u4e00\u5251\u3002 \u3000\u3000\u8d3e\u4f3c\u9053\u8eab\u7ecf\u767e\u6218\uff0c\u4ece\u6765\u6ca1\u6709\u9047\u4e0a\u8fc7\u8fd9\u6837\u7684\u5251\u6cd5\uff0c\u4e0d\u7981\u4e00\u5446\u3002 \u3000\u3000\u4ed6\u7684\u5251\u5df2\u5c01\u4e86\u51fa\u53bb\uff0c\u52bf\u5df2\u65e0\u6cd5\u6536\u56de\uff0c \u53ea\u597d\u4e00\u5438\u6c14\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u5411\u540e\u9000\u4e86\u4e94\u6b65\uff0c\u907f\u5f00\u4e00\u5251\u3002 \u3000\u3000\u4f46\u662f\u9ad8\u5e7d\u5170\u4e00\u4e0a\u6b65\uff0c\u5ffd\u53c8\u523a\u51fa\u4e00\u5251\u3002 \u3000\u3000\u7b2c\u4e00\u5251\u662f\u767d\u8272\u7684\uff1b\u7b2c\u4e8c\u5251\u5374\u662f\u4e00\u652f\u9ed1\u8272\u7684\u957f\u5251\u3002 \u3000\u3000\u7b2c\u4e09\u5251\uff0c\u662f\u4e00\u9053\u7eff\u8272\u7684\u5251\u5149\u3002 \u3000\u3000\u4e0d\u540c\u989c\u8272\u7684\u957f\u5251\uff0c\u4e0d\u540c\u7684\u523a\u51fa\u624b\u6cd5\u3002 \u3000\u3000\u4f7f\u5f97\u4eba\u773c\u82b1\u7f2d\u4e71\u3002 \u3000\u3000\u8d3e\u4f3c\u9053\u88ab\u8feb\u5f97\u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u4f46\u9ad8\u5e7d\u5170\u624b\u4e2d\u4e4b\u5251\uff0c\u5374\u5982\u5f71\u968f\u5f62\u4e00\u822c\uff0c\u5ffd\u7136\u95f4\uff0c\u53c8\u591a\u51fa\u4e86\u4e00\u9053\u7ea2\u8272\u7684\u5251\u5149\u3002 \u3000\u3000\u8d3e\u4f3c\u9053\u987f\u6709\u7740\u65e0\u6cd5\u95ea\u907f\u4e4b\u611f\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u53ea\u6709\u4e24\u53ea\u624b\uff0c\u4f46\u5374\u80fd\u540c\u65f6\u7528\u4e86\u56db\u67c4\u957f\u5251\u3002 \u3000\u3000\u4f46\u89c1\u5979\u7389\u624b\u626c\u6325\uff0c\u5251\u5149\u4ea4\u70bd\uff0c\u5f69\u8272\u7f24\u7eb7\uff0c\u5b9e\u5728\u53eb\u4eba\u6709\u7740\u76ee\u8ff7\u4e94\u8272\uff0c\u5e94\u63a5\u4e0d\u6687\u4e4b\u611f\u3002 \u3000\u3000\u8d3e\u4f3c\u9053\u5728\u611f\u89c9\u4e4b\u4e2d\uff0c\u4f3c\u662f\u5df2\u7ecf\u5b8c\u5168\u6ca1\u6709\u4e86\u5e94\u4ed8\u7684\u529e\u6cd5\u3002 \u3000\u3000\u5343\u9488\u4e07\u8292\u5deb\u5a46\u5a46\uff0c\u53ca\u65f6\u51fa\u624b\uff0c\u5341\u51e0\u7f15\u94f6\u7ebf\u822c\u7684\u6bd2\u9488\uff0c\u76f4\u5c04\u8fc7\u6765\u3002 \u3000\u3000\u5deb\u5a46\u5a46\u7684\u6697\u5668\uff0c\u4e0d\u5177\u5148\u58f0\u593a\u4eba\u7684\u5a01\u52bf\uff0c\u4f46\u5374\u662f\u9634\u6bd2\u7684\u5f88\u3002 \u3000\u3000\u8fd9\u4e00\u628a\u94f6\u9488\uff0c\u903c\u5f97\u9ad8\u5e7d\u5170\u4e0d\u5f97\u4e0d\u5168\u795e\u5e94\u4ed8\u3002 \u3000\u3000\u4e5f\u5e2e\u4e86\u8d3e\u4f3c\u9053\u4e00\u4e2a\u5927\u5fd9\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5251\u4e0a\u7684\u5f69\u5149\u8ff8\u5c04\uff0c\u51fb\u843d\u4e86\u94f6\u9488\uff0c\u4f46\u8d3e\u4f3c\u9053\u4e5f\u8131\u79bb\u4e86\u88ab\u56f0\u4e4b\u9669\u3002 \u3000\u3000\u75be\u5feb\u7684\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u9ad8\u5e7d\u5170\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5deb\u5a46\u5a46\uff0c\u4f60\u4e5f\u4e00\u8d77\u4e0a\u5427!\u201d \u3000\u3000\u5deb\u5a46\u5a46\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u6211\u4eec\u514d\u4e0d\u4e86\u653e\u624b\u4e00\u640f\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u53f6\u957f\u9752\u3001\u4f55\u5bd2\u8863\u3001\u4e07\u80dc\u5e26\u7740\u4ec5\u6709\u7684\u516d\u4e2a\u5200\u624b\uff0c\u4e5f\u8ddf\u7740\u884c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u8d3e\u4f3c\u9053\u7b49\uff0c\u4e5f\u597d\u50cf\u51c6\u5907\u8981\u653e\u5f03\u8fd9\u4e2a\u5730\u65b9\uff0c\u5927\u90e8\u5206\u7684\u4eba\u624b\uff0c\u90fd\u5df2\u7ecf\u64a4\u9000\u4e0a\u4e8c\u697c\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5deb\u5a46\u5a46\uff0c\u8fd9\u662f\u56f0\u517d\u4e4b\u6597\u3002\u201d \u3000\u3000\u5deb\u5a46\u5a46\u9053\uff1a\u201c\u90a3\u5012\u672a\u5fc5\uff0c\u9ad8\u5929\u5065\u5728\u8fd9\u70df\u9501\u697c\u4e0a\uff0c\u57cb\u4f0f\u4e86\u5f88\u591a\u7684\u673a\u5173\uff0c\u4e5f\u5e03\u6210\u5f88\u591a\u7684\u6b7b\u58eb\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u4f60\u4eec\u522b\u5fd8\u4e86\uff0c\u4ed6\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u8d3e\u4f3c\u9053\u9053\uff1a\u201c\u4f46\u4ed6\u5e03\u7f6e\u4e0b\u7684\u57cb\u4f0f\uff0c\u4ecd\u7136\u5b58\u5728\u3002\u201d \u3000\u3000\u5deb\u5a46\u5a46\u9053\uff1a\u201c\u9ad8\u5929\u5065\u8fd8\u544a\u8bc9\u6211\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6559\u4f60\u4eec\u5982\u4f55\u8fd0\u7528\u697c\u4e0a\u7684\u57cb\u4f0f\u3002\u201d \u3000\u3000\u5deb\u5a46\u5a46\u9053\uff1a\u201c\u4ed6\u8bf4\uff0c\u5982\u82e5\u4ed6\u4e0d\u5e78\u6218\u6b7b\u4e86\uff0c\u8981\u6211\u4eec\u628a\u6b7b\u4ea1\u6d88\u606f\uff0c\u4f20\u5165\u795e\u5200\u5802\u53bb\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u54fc!\u544a\u8bc9\u96f7\u65e0\u5f71\u6709\u4ec0\u4e48\u7528\uff0c\u4ed6\u5df2\u7ecf\u80cc\u53db\u4e86\u9ad8\u5929\u5065\u3002\u201d \u3000\u3000\u5deb\u5a46\u5a46\u9053\uff1a \u201c\u6709\u4e9b\u4ec0\u4e48\u4f5c\u7528\uff0c\u8001\u8eab\u4e0d\u77e5\u9053\uff0c\u4e8c\u59d1\u5a18\u5e38\u4f4f\u7b2c\u2014\u5821\uff0c \u81ea\u7136\u6bd4\u6211\u4eec\u8981\u6e05\u695a\u591a\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u4eec\u628a\u6d88\u606f\u4f20\u51fa\u53bb\u4e86\u6ca1\u6709?\u201d \u3000\u3000\u5deb\u5a46\u5a46\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u9075\u7167\u9ad8\u5821\u4e3b\u7684\u9057\u547d\u529e\u4e8b?\u201d \u3000\u3000\u5deb\u5a46\u5a46\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u6ca1\u6709\u5b8c\u5168\u786e\u5b9a\u5b83\u5229\u5f0a\u5f97\u5931\u4e4b\u524d\uff0c\u6211\u4eec\u4e0d\u60f3\u4f20\u51fa\u8fd9\u4e2a\u6d88\u606f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5ffd\u7136\u56de\u987e\u4e86\u4f55\u5bd2\u8863\u3001\u53f6\u957f\u9752\u4e00\u773c\uff0c\u9053\uff1a \u201c\u54b1\u4eec\u9000\u51fa\u70df\u9501\u697c\u53bb\u3002\u201d \u4f55\u5bd2\u8863\u4f4e\u58f0\u9053\uff1a\u201c\u5929\u5df2\u5927\u4eae\uff0c\u800c\u4e14\uff0c\u54b1\u4eec\u7684\u4eba\u624b\u4e5f\u51b2\u4e86\u8fdb\u6765\u3002\u4f55\u4e0d\u4e00\u9f13\u4f5c\u6c14\uff0c\u626b\u5e73\u70df\u9501\u697c\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\uff0c\u54b1\u4eec\u9000\u51fa\u53bb\u3002\u201d \u3000\u3000\u5979\u8bf4\u8bdd\u867d\u7136\u5341\u5206\u6e29\u67d4\uff0c\u4f46\u6001\u5ea6\u5374\u5341\u5206\u575a\u5b9a\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u6ca1\u6709\u4f5c\u4e89\u8fa9\uff0c\u9000\u51fa\u4e86\u70df\u9501\u697c\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u76ee\u6ce8\u8d3e\u4f3c\u9053\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8d3e\u4f3c\u9053\uff0c\u4f60\u8ba4\u771f\u7684\u60f3\u4e00\u60f3\uff0c\u5348\u65f6\u4e4b\u524d\uff0c\u5e0c\u671b\u4f60\u6709\u4e2a\u51b3\u5b9a\u3002\u201d \u3000\u3000\u8d3e\u4f3c\u9053\u671b\u671b\u5929\u8272\uff0c\u9053\uff1a \u201c\u597d\u5427!\u5348\u65f6\u4e4b\u524d\uff0c\u5728\u4e0b\u4f1a\u7ed9\u59d1\u5a18\u4e00\u4e2a\u7b54\u590d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5728\u5348\u65f6\u4e4b\u524d\uff0c\u6211\u4eec\u4e0d\u4f1a\u5bf9\u70df\u9501\u697c\u518d\u6709\u4efb\u4f55\u653b\u51fb\u3002\u201d \u8d3e\u4f3c\u9053\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\u5427!\u5728\u5348\u65f6\u4e4b\u524d\uff0c\u6211\u4eec\u4e00\u5b9a\u7ed9\u59d1\u5a18\u4e00\u4e2a\u7b54\u590d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9000\u51fa\u4e4b\u540e\uff0c\u76f4\u884c\u5230\u4f55\u5bd2\u8863\u7684\u8eab\u4fa7\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u4f55\u90ce\uff0c\u662f\u4e0d\u662f\u5f88\u4e0d\u6ee1\u610f\u6211\u7684\u5f3a\u6a2a?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u4e0d\u662f\uff0c\u6211\u662f\u89c9\u7740\uff0c\u54b1\u4eec\u9000\u51fa\u70df\u9501\u697c\uff0c\u662f\u4e00\u4ef6\u5f88\u4e0d\u660e\u667a\u7684\u4e8b\u60c5\u3002\u201d \u9ad8\u5e7d\u5170\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u6837\u7684\u60f3\u6cd5\uff1f\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u624d\u53cd\u5bf9\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f55\u90ce\u5e94\u8be5\u5148\u548c\u4f60\u5546\u91cf\u4e00\u4e0b\u7684\uff0c\u6211\u5e0c\u671b\u4f60\u4e0d\u8981\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u6211\u5411\u4f60\u63d0\u51fa\u4e86\u610f\u89c1\u4e4b\u540e\uff0c\uff0c\u6211\u5fc3\u4e2d\u4e5f\u6709\u70b9\u540e\u6094\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u522b\u8981\u8fd9\u6837\u60f3\uff0c\u6211\u662f\u4f60\u59bb\u5b50\uff0c\u6709\u4e9b\u4e8b\uff0c\u672c\u6765\u5c31\u5e94\u8be5\u7531\u4f60\u505a\u4e3b\u7684\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u6709\u4ec0\u4e48\u4e0d\u660e\u767d\u7684\u5730\u65b9\uff0c\u8bf7\u8bf4\u51fa\u6765\uff0c\u4e5f\u8bb8\uff0c\u6211\u7684\u60f3\u6cd5\u9519\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u54b1\u4eec\u5df2\u7ecf\u653b\u5165\u70df\u9501\u697c\uff0c\u800c\u4e14\uff0c\u65f6\u95f4\uff0c\u5bf9\u54b1\u4eec\u4e5f\u662f\u6709\u5bb3\u65e0\u5229\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u4e00\u53e3\u6c14\u4f5c\u6210\uff0c\u628a\u4ed6\u4eec\u89e3\u51b3\u4e86\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u770b\u8fc7\u4e86\u70df\u9501\u697c\u4e2d\u7684\u5f62\u52bf\uff0c\u9002\u5b9c\u4e8e\u8fd1\u8eab\u76f8\u640f\uff0c\u800c\u6211\uff0c\u4e03\u8272\u5251\u6700\u51cc\u5389\u7684\u6740\u624b\uff0c\u5374\u9700\u8981\u8f83\u5927\u7684\u7a7a\u95f4\uff0c\u624d\u80fd\u591f\u65bd\u5c55\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5728\u70df\u9501\u697c\u4e2d\uff0c\u548c\u654c\u4eba\u52a8\u624b\uff0c\u90a3\u662f\u4ee5\u5df2\u4e4b\u77ed\uff0c\u5bf9\u4eba\u4e4b\u957f\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u53ef\u80fd\uff0c\u90a3\u5c31\u662f\u4f1a\u903c\u4ed6\u4eec\u6cc4\u9732\u51fa\u9ad8\u5929\u5065\u6b7b\u8baf\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u5f88\u91cd\u8981\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5f88\u91cd\u8981\uff0c\u5982\u82e5\u96f7\u65e0\u5f71\u77e5\u9053\u9ad8\u5929\u5065\u5df2\u7ecf\u6b7b\u4e86\uff0c\u5fc5\u7136\u4f1a\u5168\u529b\u5bf9\u4ed8\u54b1\u4eec\uff0c\u73b0\u5728\uff0c\u6211\u4eec\u7684\u4ea4\u6218\u80fd\u529b\uff0c\u5df2\u53d7\u4e86\u5f88\u5927\u7684\u635f\u4f24\uff0c\u5f88\u96be\u518d\u548c\u96f7\u65e0\u5f71\u6297\u62d2\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u4ed6\u4eec\u7684\u60f3\u6cd5\u5982\u4f55?\u4f46\u975e\u4e0d\u5f97\u5df2\uff0c\u4e0d\u4f1a\u4f20\u51fa\u9ad8\u5929\u5065\u7684\u6b7b\u8baf\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1b\u201c\u8fd9\u53c8\u4e3a\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u7684\u60f3\u6cd5\u662f\u96f7\u65e0\u5f71\u6765\u4e86\uff0c\u5bf9\u6211\u4eec\u662f\u7edd\u5bf9\u7684\u4e0d\u5229\u4f46\u5bf9\u4ed6\u4eec\uff0c\u4e5f\u672a\u5fc5\u6709\u76ca\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u662f\u4e00\u8d77\u7684\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1b\u201c\u662f\u7684\uff0c\u4f46\u5229\u5bb3\u4e0d\u540c\uff0c\u4ed6\u4eec\u662f\u9ad8\u5929\u5065\u7684\u771f\u6b63\u5fc3\u8179\u4f46\u5374\u672a\u5fc5\u4f1a\u5bf9\u96f7\u65e0\u5f71\u5fe0\u5fc3\uff0c\u96f7\u65e0\u5f71\u5bf9\u4ed6\u4eec\u4e5f\u4e0d\u4f1a\u5f88\u4fe1\u4efb\uff0c\u96f7\u65e0\u5f71\u82e5\u662f\u80dc\u4e86\uff0c\u4ed6\u4e0d\u4f1a\u7559\u4e0b\u8fd9\u4e9b\u4eba\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5bf9!\u59d1\u5a18\u8fd9\u4e48\u4e00\u8bf4\uff0c\u60f3\u60f3\u5374\u4e5f\u5982\u6b64\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4f46\u6211\u4eec\u5982\u82e5\u903c\u7684\u592a\u7d27\u4e86\uff0c\u5c31\u53ef\u80fd\u8feb\u4ed6\u4eec\u4e0d\u987e\u540e\u679c\u7684\u4f20\u51fa\u6b7b\u8baf\u3002\u201d \u4f55\u5bd2\u8863\u70b9\u70b9\u5934\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u4f60\u770b\uff0c\u5348\u65f6\u4e4b\u540e\uff0c\u4f1a\u662f\uff0c\u4e2a\u4ec0\u4e48\u7ed3\u679c\u5462?\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a \u201c\u4e0d\u8fc7\uff0c\u4ed6\u73b0\u5728\u521a\u521a\u7761\u8fc7\u53bb\uff0c\u8ba9\u4ed6\u9192\u8fc7\u6765\u518d\u8bf4\u5427\uff01\u201d \u4f55\u5bd2\u8863\u7a81\u7136\u4e00\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u7cdf\u4e86\uff0c\u7cdf\u4e86!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u96f7\u65e0\u5f71\u4f1a\u4e0d\u4f1a\u6d3e\u4eba\u5728\u6697\u4e2d\u76d1\u89c6\u54b1\u4eec\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u8fd9\u4e2a\u4eba\u4e0d\u53ef\u4fe1\u4efb\uff0c\u6211\u60f3\u4ed6\u4e00\u5b9a\u4f1a\u6d3e\u4eba\u5728\u6697\u4e2d\u76d1\u89c6\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u4e48\u9ad8\u5929\u5065\u7684\u6b7b\u8baf\uff0c\u4ed6\u53ef\u80fd\u65e9\u5df2\u7ecf\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u60f3\u4ed6\u73b0\u5728\u8fd8\u4e0d\u77e5\u9053\uff0c\u96f7\u65e0\u5f71\u662f\u4e00\u4e2a\u5f88\u5c0f\u5fc3\u7684\u4eba\uff0c\u867d\u7136\u6d3e\u4eba\u76d1\u89c6\uff0c\u4e5f\u5fc5\u7136\u4f1a\u76f8\u8ddd\u5f88\u8fdc\uff0c \u6708\u5149\u4e4b\u4e0b\uff0c\u89c6\u754c\u4e0d\u660e\uff0c\u9ad8\u5929\u5065\u867d\u7136\u6b7b\u4e86\uff0c\u4f46\u4ed6\u4eec\u5374\u672a\u5fc5\u5c31\u770b\u5f97\u5230\uff0c\u54b1\u4eec\u4e0d\u613f\u4e00\u9f13\u4f5c\u6c14\u653b\u4e0b\u4e86\u70df\u9501\u697c\uff0c\u4e5f\u6709\u8fd9\u4e00\u4e2a\u4f5c\u7528\uff0c\u53cc\u65b9\u6682\u65f6\u4fdd\u6301\u67d0\u4e00\u79cd\u76f8\u5cd9\uff0e\u81f3\u5c11\uff0c\u4e0d\u4f1a\u5f15\u8d77\u96f7\u65e0\u5f71\u7684\u51fa\u52a8\uff0c\u5982\u82e5\u4ed6\u786e\u5982\u6211\u4eec\u5df2\u7ecf\u5b8c\u5168\u5f97\u80dc\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u903c\u4ed6\u51fa\u624b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\uff0c\u597d\u9634\u9669\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1b \u201c\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u9664\u4e86\u59bb\u5b50\u540c\u547d\uff0c\u5144\u5f1f\u8fde\u5fc3\uff0c\u7236\u5b50\u60c5\u6df1\u4e4b\u5916\uff0c\u53ea\u6709\u4e00\u79cd\u60c5\u51b5\u4e4b\u4e0b\uff0c\u624d\u80fd\u771f\u6b63\u7684\u7ed3\u5408\u4e00\u5904\uff0c\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u5bf9\u795e\u5200\u5802\u7a76\u7adf\u4e86\u89e3\u591a\u5c11?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4e86\u89e3\u7684\u4e0d\u591a\uff0c\u4e0d\u8fc7\uff0c\u6211\u77e5\u9053\uff0c\u4ee5\u6211\u4eec\u76ee\u4e0b\u6b8b\u7f3a\u7684\u9635\u5bb9\uff0c\u7edd\u5bf9\u65e0\u6cd5\u5bf9\u4ed8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u4eec\u5c82\u4e0d\u662f\u9677\u5165\u4e86\u8fdb\u9000\u7ef4\u8c37\u4e4b\u5883\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6700\u597d\u7684\u529e\u6cd5\uff0c\u662f\u73b0\u5728\u6211\u4eec\u9000\u51fa\u53bb\uff0c\u7b49\u9ec4\u5821\u4e3b\u7b49\u4eba\uff0c\u4f24\u52bf\u597d\u4e86\u4e4b\u540e\uff0c\u518d\u4f5c\u9053\u7406\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4eec\u9000\u5f97\u4e86\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u4e00\u4e2a\u5f88\u5927\u7684\u5b66\u95ee\u4e86\uff0c\u5982\u82e5\u6211\u4eec\u4e0d\u80fd\u9000\u8d70\uff0c\u90a3\u5c31\u753b\u864e\u4e0d\u6210\u53cd\u7c7b\u72ac\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u76ee\u4e0b\uff0c\u6709\u653e\u624b\u4e00\u6218\u4e4b\u80fd\uff0c\u8fd8\u6709\u53f6\u957f\u9752\u3001\u4e07\u80dc\u3001\u6211\u3001\u4f60\uff0c\u5c1a\u8001\u524d\u8f88\u592b\u5987\uff0c\u5176\u4ed6\u7684\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u5176\u4ed6\u7684\u4eba\uff0c\u8fd8\u9700\u8981\u6211\u4eec\u4fdd\u62a4\uff0c\u6211\u770b\u8fc7\u4e86\uff0c\u771f\u6b63\u80fd\u6d3e\u4e0a\u7528\u573a\u7684\u8fd8\u6709\u56db\u4f4d\u5200\u624b\uff0c\u8bba\u5b9e\u529b\u4e5f\u4e0d\u7b97\u5c11\uff0c\u4f46\u75db\u82e6\u5904\uff0c\u6211\u4eec\u5fc5\u9700\u8981\u4fdd\u62a4\u8fd9\u4e9b\u4f24\u8005\uff0c\u8fd9\u8981\u5206\u53bb\u6211\u4eec\u5927\u90e8\u5206\u7684\u7cbe\u795e\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u65e2\u4e0d\u80fd\u6218\uff0c\u53c8\u4e0d\u80fd\u9000\uff0c\u90a3\u8be5\u5982\u4f55\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5927\u59ca\u597d\u50cf\u6b63\u5728\u5165\u5b9a\uff0c\u7b49\u5979\u9192\u6765\u4e4b\u540e\uff0c\u548c\u5979\u5546\u91cf\u4e00\u4e0b\uff0c\u6211\u5fc3\u4e2d\u5012\u4e5f\u6709\u4e00\u4e2a\u8ba1\u5212\uff0c\u53ea\u662f\u4e0d\u77e5\u9053\u662f\u5426\u80fd\u884c\u5f97\u901a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53cc\u5973\u7684\u4f24\u52bf\u4e0d\u662f\u4e5f\u5f88\u91cd\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5979\u7684\u4f24\u52bf\u4e0d\u7b97\u8f7b\uff0c\u4f46\u6bd4\u8d77\u5176\u4ed6\u7684\u4eba\uff0c\u5979\u5c31\u7b97\u662f\u6700\u8f7b\u7684\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u770b\u8fc7\u5979\u7684\u4f24\u52bf\uff0c\u81f3\u5c11\uff0c\u4e0d\u80fd\u548c\u4eba\u52a8\u624b\uff0c\u90a3\u4f1a\u4f7f\u4f24\u53e3\u8fdb\u88c2\uff0c\u518d\u591a\u5931\u8840\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u522b\u62c5\u5fc3\u5927\u59ca\uff0c\u5979\u7ec3\u7684\u6b66\u529f\uff0c\u548c\u522b\u4eba\u4e0d\u540c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u9b54\u529f?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bf9!\u5927\u59ca\u7684\u6b66\u529f\u5f88\u7279\u6b8a\uff0c\u5979\u6709\u4e00\u79cd\u6297\u62d2\u75db\u82e6\u7684\u80fd\u529b\uff0c\u6211\u770b\u5230\uff0c\u5979\u7528\u4e00\u67c4\u91d1\u5200\uff0c\u63d2\u5165\u4e86\u5de6\u81c2\u4e4b\u4e0a\uff0c\u6d1e\u7a7f\u800c\u8fc7\uff0c\u7adf\u7136\u4e0d\u89c1\u6d41\u8840\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u542c\u5f97\u5927\u611f\u5174\u8da3\uff0c\u9053\uff1a\u201c\u4ee5\u540e\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ee5\u540e\uff0c\u5979\u62d4\u4e0b\u4e86\u91d1\u5200\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a \u201c\u90a3\u5e94\u8be5\u7559\u4e0b\u4e00\u4e2a\u4f24\u53e3\u554a!\u6211\u600e\u4e48\u6ca1\u6709\u77a7\u5230\u8fc7\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u4e0d\u5bb3\u81ca\uff0c\u4f60\u90a3\u4e48\u7559\u5fc3\u5927\u59ca\u7684\u7389\u4f53\u554a!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u8138\u4e00\u70ed\u9053\uff1a\u201c\u6211\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u7b2c\u4e8c\u5929\u4e2d\u5348\u65f6\u5206\uff0c\u6211\u53bb\u770b\u5979\u65f6\uff0c\u5c31\u4e0d\u89c1\u4e86\u90a3\u4e2a\u4f24\u53e3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6709\uff0c\u8fd9\u7b49\u4e8b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8bf4\u8d77\u6765\u6709\u4e9b\u7384\uff0c\u4f46\u8fd9\u5374\u662f\u5f88\u771f\u7684\u4e8b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u771f\u662f\u4e0d\u53ef\u601d\u8bae\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5927\u59ca\u6709\u4e94\u53e3\u4e5d\u5bf8\u957f\u7684\u91d1\u5200\uff0c\u4e00\u76f4\u5e26\u5728\u8eab\u8fb9\uff0c\u5374\u4e00\u76f4\u6ca1\u6709\u89c1\u5979\u7528\u8fc7\uff0c\u6240\u4ee5\uff0c\u6211\u60f3\u90a3\u53ef\u80fd\u662f\u4e00\u79cd\u5947\u7edd\u4e4b\u6280\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u4e94\u53e3\u91d1\u5200\u73b0\u5728\u4f55\u5904?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u8981\u95ee\u95ee\u5927\u59ca\u4e86\uff0c\u6211\u4e5f\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\uff1b\u7761\u7684\u5f88\u597d\uff0c\u8ba9\u5979\u597d\u597d\u7761\u4e00\u4e0b\u5427!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u522b\u8bf4\u53d7\u4f24\u7684\u4eba\u4e86\uff0c\u5c31\u7b97\u662f\u4e0d\u53d7\u4f24\u7684\uff0c\u4e5f\u662f\u75b2\u7d2f\u5f97\u5f88\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u4e5f\u5f88\u9700\u8981\u4f11\u606f\u3002\u201d \u3000\u3000\u5ffd\u7136\u4e00\u4e2a\u8f7b\u8f7b\u7684\u58f0\u97f3\uff0c\u5728\u4f55\u5bd2\u8863\u7684\u8eab\u8fb9\u54cd\u8d77\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8c08\u6211\u4ec0\u4e48?\u201d \u3000\u3000\u662f\u5434\u53cc\u5973\u3002 \u3000\u3000\u4e0d\u77e5\u5979\u4f55\u65f6\u9192\u4e86\u8fc7\u6765\uff0c\u800c\u4e14\uff0c\u5df2\u7ecf\u5230\u4e86\u4f55\u5bd2\u8863\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u56de\u987e\u4e86\u9ad8\u5e7d\u5170\u4e00\u773c\uff0c\u9ad8\u5e7d\u5170\u5fae\u7b11\u4e0d\u8bed\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53cc\u5973\uff0c\u542c\u8bf4\uff0c\u4f60\u6709\u4e94\u628a\u91d1\u5200\uff0c\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5bf9!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u4e94\u628a\u91d1\u5200\uff0c\u73b0\u5728\u4f55\u5904?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5728\u6211\u7684\u8eab\u4e0a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u662f\u5175\u5203\uff0c\u8fd8\u662f\u6697\u5668?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5f88\u96be\u8bf4\uff0c\u7b97\u5175\u5203\uff0c\u4e5f\u7b97\u6697\u5668\uff0c\u4e0d\u8fc7\uff0c \u76ee\u4e0b\u7684\u60c5\u666f\uff0c\u90fd\u662f\u4e0d\u9002\u7528\uff0c\u6211\u60f3\uff0c\u6c38\u8fdc\u4e0d\u518d\u7528\u5b83\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u662f\u600e\u4e48\u56de\u4e8b\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u662f\u9b54\u529f\uff0c\u5929\u9b54\u95e8\u4e2d\u7684\u6b66\u529f\uff0c\u5b83\u5f88\u4e0d\u9002\u5b9c\u7528\u6765\u5bf9\u654c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u77e5\u9053\uff0c\u53ea\u597d\u8bb2\u7ed9\u4f60\u542c\u4e86\uff0c\u90a3\u662f\u4e00\u79cd\u7eaf\u90aa\u7684\u9b54\u529f\uff0c\u6211\u867d\u7136\u7ec3\u8fc7\u8fd9\u79cd\u6b66\u529f \u4f46\u6211\u4e00\u751f\u4e2d\uff0c\u53ea\u7528\u8fc7\u4e00\u6b21\uff0c\u81ea\u4ece\u8ba4\u8bc6\u4e86\u5e7d\u5170\u4e4b\u540e\uff0c\u90a3\u5c31\u66f4\u4e0d\u80fd\u7528\u4e86\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u76b1\u76b1\u7709\u5934\u9053\uff1a\u201c\u90a3\u8981\u8d64\u8eab\u88f8\u4f53\uff0c\u624d\u80fd\u53d1\u6325\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u901a\u554a!\u60f3\u4e0d\u901a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54ea\u91cc\u60f3\u4e0d\u901a?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u8d64\u8eab\u88f8\u4f53\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u5b83\u79f0\u4e4b\u8c13\u9b54\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u7537\u4eba\u80fd\u4e0d\u80fd\u7528?\u201d \u3000\u3000\u5434\u53cc\u5973\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u4f60\u8981\u5b66?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u5e38\u542c\u8bf4\uff0c\u6c5f\u6e56\u4e0a\u6709\u4e00\u79cd\u5929\u9b54\u529f\uff0c \u81ea\u5df1\u5374\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a \u201c\u5509!\u90a3\u4e0d\u662f\u6b63\u5f53\u4eba\u5b66\u7684\u6b66\u529f\uff0c\u4f55\u90ce\uff0c\u6211\u5df2\u7ecf\u51c6\u5907\u5fd8\u4e86\u5b83\uff0c\u6c38\u8fdc\u7684\u5fd8\u4e86\u5b83\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\uff0c\u4f60\u7684\u4f24\u52bf\u5982\u4f55?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u7ec3\u7684\u6b66\u529f\u5f88\u7279\u6b8a\uff0c\u4f24\u52bf\u7279\u522b\u5bb9\u6613\u597d\u8f6c\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u73b0\u5728\uff0c\u80fd\u4e0d\u80fd\u548c\u4eba\u52a8\u624b?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e94\u8be5\u53ef\u4ee5\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u90a3\u4e94\u53e3\u91d1\u5200\uff0c\u73b0\u5728\u6295\u6709\u4e22\u6389\u5427?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\uff0c\u6b64\u95f4\u4e8b\u4e86\uff0c\u6211\u5c31\u51c6\u5907\u4e22\u4e86\u5b83\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u2014\u76f4\u63d0\u8fd9\u4ef6\u4e8b\uff0c\u662f\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5927\u59ca\uff0c\u4f60\u653e\u773c\u770b\u770b\u76ee\u4e0b\u7684\u60c5\u5f62\uff0e\u80fd\u591f\u548c\u4eba\u52a8\u624b\u7684\uff0c\u53ea\u6709\u6211\u4eec\u51e0\u4e2a\u4e86\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a \u201c\u5c1a\u8001\u524d\u8f88\u592b\u5987\u3001\u4f60\u3001\u4f55\u90ce\u548c\u53f6\u957f\u9752\u5144\u3001\u4e07\u80dc\u516d\u4eba\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u8fd8\u6709\u51e0\u4e2a\u5200\u624b\uff0c\u4f46\u90fd\u5df2\u662f\u75b2\u7d2f\u4e4b\u8eab\uff0c\u5fc5\u987b\u8981\u4e00\u6bb5\u4f11\u606f\u65f6\u95f4\u3002\u201d \u5434\u53cc\u5973\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4f46\u6211\u4eec\u7684\u654c\u4eba\u5462\uff1f\u9664\u4e86\u70df\u9501\u697c\u4e2d\u7684\u5343\u9488\u4e07\u8292\u4e4b\u5916\uff0c\u8fd8\u6709\u96f7\u65e0\u5f71\uff0c\u5927\u59ca\uff0c\u6211\u77e5\u9053\u4f60\u4e0d\u613f\u7528\u5929\u9b54\u529f\u5bf9\u654c\uff0c\u751a\u81f3\u5bf9\u9ad8\u5929\u5065\uff0c\u4f60\u4e5f\u6ca1\u6709\u771f\u6b63\u65bd\u5c55\uff0c\u4f46\u5982\u6211\u4eec\u5230\u4e86\u5371\u4ea1\u5173\u5934\u65f6\uff0c\u603b\u5e94\u8be5\u7529\u6765\u6551\u547d\u5427?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u53cc\u5973\uff0c\u522b\u987e\u5fcc\u4ec0\u4e48\uff0c\u4f60\u662f\u6211\u7684\u59bb\u5b50\uff0c\u8fd9\u4e00\u751f\u90fd\u4e0d\u4f1a\u518d\u6709\u53d8\u5316\uff0c\u6211\u77e5\u9053\u4f60\u51fa\u8eab\u5929\u9b54\u95e8\uff0c\u4f46\u6211\u4ece\u6ca1\u6709\u8fc7\u4efb\u4f55\u611f\u89c9\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u81ea\u5df1\u5bf9\u5929\u9b54\u95e8\u7684\u6b66\u529f\uff0c\u8fd8\u6709\u7740\u4e00\u79cd\u5f3a\u70c8\u7684\u597d\u5947\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a \u201c\u4e3a\u4ec0\u4e48\u4e0d\u4fdd\u4f4f\u90a3\u4e2a\u5f3a\u70c8\u7684\u597d\u5947\u5462?\u4e3a\u4ec0\u4e48\u4e0d\u4fdd\u7559\u4e00\u4efd\uff0c\u4f60\u90a3\u79cd\u597d\u5947\u7684\u5e7b\u60f3\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u53cc\u5973\uff0c\u5c31\u7b97\u4eca\u5929\uff0c \u4f60\u4e0d\u8868\u73b0\u51fa\u5929\u9b54\u95e8\u7684\u6b66\u529f\uff0c\u6709\u4e00\u5929\uff0c\u6211\u4e5f\u4f1a\u6c42\u4f60\u8868\u6f14\u7684\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5462?\u96be\u9053\u4f60\u4e00\u5b9a\u8981\u8feb\u6211\u51fa\u4e11?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53cc\u5973\uff0c\u522b\u8bef\u4f1a\uff0c\u6211\u7684\u610f\u601d\u662f\uff0c\u4e16\u4e0a\u65e2\u7136\u6709\u8fd9\u4e48\u4e00\u95e8\u6b66\u529f\uff0c\u5e94\u8be5\u5f00\u5f00\u773c\u754c\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5728\u6211\u7ec3\u4e60\u8fd9\u95e8\u6b66\u529f\u4e4b\u65f6\uff0c\u5012\u4e5f\u4e0d\u89c9\u5f97\u4ec0\u4e48?\u81ea\u4ece\uff0c \u81ea\u4ece\u2026\u2026\u201d \u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u81ea\u4ece\u89c1\u5230\u4e86\u4f55\u90ce\u4e4b\u540e\uff0c \u4f60\u5c31\u4e0d\u613f\u518d\u7528\u5929\u9b54\u95e8\u7684\u6b66\u529f\u5bf9\u654c\u4e86\uff0c\u662f\u4e48\uff1f\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u8fdb\u5165\u4e86\u7b2c\u4e00\u5821\u4e4b\u540e\uff0c\u5c31\u6ca1\u6709\u518d\u7528\u8fc7\u5929\u9b54\u95e8\u7684\u6b66\u529f\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u76ee\u4e0b\u60c5\u52bf\u8feb\u4eba\uff0c\u4e5f\u7528\u4e0d\u7740\u8ba1\u8f83\u5c0f\u8282\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u771f\u5230\u4e86\u975e\u8981\u62fc\u547d\u4e0d\u53ef\u7684\u65f6\u5019\uff0c\u6211\u4e5f\u53ea\u6709\u51fa\u4e11\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u7a81\u7136\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u9053\uff1a \u201c\u5927\u59ca\uff0c\u4f60\u662f\u4e0d\u662f\u6709\u4e00\u4e9b\u7597\u4f24\u7684\u836f\u7269\uff0c\u5177\u6709\u5947\u6548?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u770b\u6765\uff0c\u4f60\u5bf9\u6211\u7684\u5e95\u7ec6\uff0c\u4e86\u89e3\u7684\u5341\u5206\u6e05\u695a\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u53ea\u662f\u5c0f\u59b9\u89c9\u7740\u5927\u59ca\u6709\u5f88\u591a\u7279\u5f02\u7684\u5730\u65b9\uff0c\u90a3\u662f\u4eba\u6240\u96be\u53ca\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u6211\u8eab\u4e0a\u662f\u6709\u4e00\u4e9b\u836f\u7269\uff0c\u4e0d\u8fc7\uff0c\u90a3\u90fd\u662f\u5929\u9b54\u95e8\u4e2d\u7684\u4e1c\u897f\uff0c\u5bf9\u7597\u4f24\u65b9\u9762\uff0c\u5374\u6709\u5f88\u795e\u5947\u3001\u8fc5\u901f\u7684\u6548\u7528\uff0c\u4e0d\u8fc7\uff0c\u6211\u62c5\u5fc3\u7684\u662f\uff0c\u8fd9\u4e9b\u836f\u7269\u53ef\u80fd\u4f1a\u6709\u4e0d\u826f\u7684\u540e\u679c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u600e\u4e48\u4f1a\u5462?\u4e00\u79cd\u6cbb\u7597\u7684\u836f\u7269\uff0c\u4f1a\u6709\u4ec0\u4e48\u6837\u5b50\u4e0d\u826f\u7684\u540e\u679c\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5929\u9b54\u95e8\u662f\u4e00\u4e2a\u5f88\u8be1\u5f02\u7684\u95e8\u6237\uff0c\u6b66\u529f\u8be1\u5f02\uff0c\u624b\u6cd5\u8be1\u5f02\uff0c\u7528\u7684\u836f\u7269\u4e5f\u548c\u522b\u7684\u4eba\u4e0d\u540c\uff0c\u4ed6\u4eec\u6ca1\u6709\u7ec3\u4e60\u8fc7\u5929\u9b54\u95e8\u7684\u6b66\u529f\uff0c\u8fd9\u79cd\u836f\u7269\uff0c\u662f\u5426\u80fd\u591f\u9002\u7528\uff0c\u6211\u6ca1\u6709\u628a\u63e1\uff0c\u6240\u4ee5\uff0c\u6211\u4e00\u76f4\u4e0d\u592a\u6562\u63d0\u8d77\u8fd9\u4ef6\u4e8b\u60c5\u7684\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u7528\u8fc7\u4e86\uff0c\u5982\u82e5\u6709\u4ec0\u4e48\u4e0d\u826f\u7684\u540e\u679c\uff0c\u4f60\u5e94\u8be5\u5f88\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u81ea\u5df1\u5012\u6ca1\u6709\u5f88\u7279\u6b8a\u7684\u611f\u89c9\uff0c\u6211\u53ea\u662f\u89c9\u7740\uff0c\u8fd9\u79cd\u836f\u7269\u6577\u5728\u4f24\u53e3\u4e0a\uff0c\u4f1a\u5ffd\u7136\u6709\u7740\u53e3\u6e34\u3001\u5934\u6655\u7684\u611f\u89c9\uff0c\u5168\u8eab\u6709\u4e9b\u98d8\u98d8\u8361\u853c\u7684\u611f\u53d7\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u79cd\u611f\u53d7\u7684\u65f6\u95f4\uff0c\u6709\u591a\u4e45?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u592a\u4e45\uff0c\u5927\u7ea6\u53ea\u6709\u534a\u4e2a\u65f6\u8fb0\u5de6\u53f3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ee5\u540e\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u7528\u5929\u9b54\u95e8\u7684\u4e00\u79cd\u7279\u6b8a\u5750\u606f\u4e4b\u6cd5\uff0c\u914d\u5408\u8d77\u6765\uff0c\u4f24\u52bf\u4f1a\u5f88\u5feb\u7684\u597d\u8f6c\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u4f60\u7684\u56f0\u96be\u662f\uff0c\u4e0d\u80fd\u628a\u5929\u9b54\u95e8\u5750\u606f\u4e4b\u6cd5\uff0c\u4f20\u6388\u7ed9\u4ed6\u4eec\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u5168\u662f\u5982\u6b64\uff0c\u6211\u62c5\u5fc3\u8fd9\u79cd\u836f\u7269\uff0c\u4f1a\u4f7f\u4e00\u4e2a\u4eba\u7684\u6027\u683c\u8f6c\u53d8\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff0c\u5012\u662f\u95fb\u6240\u672a\u95fb\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u8fd9\u4e9b\u4eba\uff0c\u90fd\u662f\u4f60\u7684\u597d\u53cb\uff0c\u4e00\u65e6\u7528\u8fc7\u8fd9\u4e9b\u836f\u7269\u4e4b\u540e\uff0c\u53d1\u751f\u4e86\u4e0d\u826f\u7684\u540e\u679c\uff0c\u4f60\u8981\u5982\u4f55\u5bf9\u4ed6\u4eec\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\uff0c\u6211\u60f3\u4e0d\u51fa\u4f1a\u6709\u4e9b\u4ec0\u4e48\u540e\u679c\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59ca\uff0c\u4f60\u662f\u5426\u77e5\u9053\u8fd9\u4e9b\u836f\u7269\u662f\u4ec0\u4e48\u5236\u9020\u7684\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u6211\u53ea\u662f\u6536\u5b58\u4e86\u4e00\u4e9b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u5fc5\u8981\u65f6\u53ea\u597d\u7528\u8fd9\u4e9b\u836f\u7269\uff0c\u5148\u4f7f\u4ed6\u4eec\u7684\u4f24\u52bf\u6b62\u75db\uff0c\u597d\u8f6c\uff0c\u7136\u540e\u5982\u82e5\u771f\u6709\u4ec0\u4e48\u540e\u9057\u75c7\uff0c\u518d\u60f3\u529e\u6cd5\u89e3\u51b3\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u5148\u628a\u8bdd\u8bf4\u6e05\u695a\uff0c\u5982\u82e5\u4f60\u4eec\u90fd\u89c9\u7740\u5e94\u8be5\u7528\uff0c\u90a3\u5c31\u65bd\u7528\u5427\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6709\u4e9b\u4e8b\uff0c\u4e5f\u65e0\u987b\u987e\u8651\u7684\u592a\u591a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u662f\u4e0d\u662f\u73b0\u5728\u5c31\u7528?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6211\u603b\u89c9\u7740\uff0c\u96f7\u65e0\u5f71\u4e00\u76f4\u5728\u6697\u4e2d\u76d1\u89c6\u7740\u6211\u4eec\uff0c\u968f\u65f6\u4f1a\u5e26\u9886\u4eba\u624b\u51fa\u73b0\uff0c\u6211\u4eec\u8fd8\u6709\u70df\u9501\u697c\u4e00\u573a\u640f\u6740\uff0c\u5343\u9488\u4e07\u8292\u5deb\u5a46\u5a46\u548c\u8d3e\u4f3c\u9053\u7ed3\u5408\u8d77\u70df\u9501\u697c\u4e2d\u4e00\u6279\u6b66\u58eb\uff0c\u4e5f\u662f\u4e00\u80a1\u5f3a\u5927\u7684\u529b\u91cf\uff0c\u6211\u4eec\u8981\u653b\u4e0b\u70df\u9501\u697c\uff0c\u6050\u6015\u4e5f\u8981\u4ed8\u51fa\u5f88\u5927\u4ee3\u4ef7\uff0c\u5982\u82e5\u5929\u8272\u5927\u4eae\uff0c\u96f7\u65e0\u5f71\u5728\u6697\u4e2d\u76d1\u89c6\u54b1\u4eec\u7684\u4eba\uff0c\u5927\u6982\u53ef\u4ee5\u770b\u5f97\u5f88\u6e05\u695a\u4e86\uff0c\u6240\u4ee5\uff0c\u54b1\u4eec\u5fc5\u987b\u65e9\u4f5c\u6253\u7b97\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u95ed\u76ee\u517b\u606f\u7684\u9ec4\u7075\uff0c\u7a81\u7136\u7741\u5f00\u4e86\u773c\u775b\uff0c\u9053\uff1a \u201c\u5e7d\u5170\u59d1\u5a18\u8bf4\u7684\u4e0d\u9519\uff0c\u96f7\u65e0\u5f71\u864e\u89c6\u7708\u7708\uff0c\u968f\u65f6\u53ef\u80fd\u4f1a\u51fa\u73b0\uff0c\u4e0d\u8bba\u5434\u59d1\u5a18\u7684\u836f\u7269\uff0c\u670d\u7528\u4e4b\u540e\uff0c\u6709\u4ec0\u4e48\u6837\u5b50\u7684\u540e\u679c\uff0c\u4f46\u76ee\u524d\u6700\u91cd\u8981\u7684\u662f\uff0c\u54b1\u4eec\u8981\u8fc5\u901f\u6062\u590d\u6211\u4eec\u7684\u6218\u529b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u59d0\uff0c\u73b0\u5728\uff0c\u5148\u6551\u773c\u524d\uff0c\u4ee5\u540e\u7684\u4e8b\uff0c\u4ee5\u540e\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5434\u59d1\u5a18\uff0c\u522b\u987e\u8651\u4efb\u4f55\u540e\u679c\uff0c\u6211\u4eec\u8fdb\u5165\u7b2c\u4e00\u5821\u65f6\uff0c\u90fd\u62b1\u4e86\u5fc5\u6b7b\u4e4b\u5fc3\uff0c\u6240\u4ee5\uff0c\u751f\u6b7b\u4e4b\u4e8b\uff0c\u6211\u4eec\u5df2\u7ecf\u4e0d\u653e\u5728\u5fc3\u4e0a\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d\u5427!\u4f60\u4eec\u65e2\u7136\u90fd\u4e0d\u5bb3\u6015\uff0c\u8d31\u59be\u53ea\u597d\u7528\u836f\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5434\u59d1\u5a18\uff0c\u8fd8\u8981\u4f60\u4f20\u51fa\u5929\u9b54\u95e8\u7684\u5fc3\u6cd5\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u5bf9\u4f60\u800c\u8a00\uff0c\u5b9e\u5728\u662f\u4e00\u4ef6\u5f88\u96be\u529e\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u8fd9\u5012\u4e0d\u592a\u91cd\u8981\uff0c\u5bb6\u5e08\u4f20\u4e86\u6211\u5929\u9b54\u95e8\u6b66\u529f\u4e4b\u540e\uff0c\u66fe\u544a\u8bc9\u6211\u4e00\u4ef6\u4e8b\uff0c\u5979\u51b3\u5fc3\u79bb\u5f00\u4e2d\u539f\uff0c\u897f\u884c\u6606\u4ed1\uff0c\u5929\u9b54\u95e8\u662f\u5426\u8fd8\u5728\u4e2d\u539f\u53d1\u5c55\uff0c\u8981\u6211\u81ea\u5df1\u51b3\u5b9a\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u662f\u5929\u9b54\u95e8\u7684\u6b66\u529f\uff0c\u7531\u5927\u59ca\u72ec\u8eab\u627f\u62c5\uff0c\u4eca\u540e\u5982\u4f55\u7edd\u7eed\u5b58\u4ea1\uff0c\u4e5f\u7531\u5927\u59d0\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u662f\u8fd9\u4e48\u4e00\u4e2a\u610f\u601d\uff0c\u4f46\u6211\u5df2\u4f5c\u4e86\u51b3\u5b9a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u5982\u4f55\u51b3\u5b9a\u7684?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5929\u9b54\u95e8\u5728\u4e2d\u539f\uff0c\u81f3\u6211\u800c\u7edd\uff0c\u6211\u4e0d\u4f1a\u518d\u628a\u8fd9\u79cd\u6b66\u529f\u4f20\u51fa\u53bb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u51b3\u5b9a\uff0c\u5c82\u4e0d\u4f7f\u5929\u9b54\u95e8\uff0c\u6c38\u8fdc\u5728\u6c5f\u6e56\u4e0a\u7edd\u8ff9\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9664\u975e\u5bb6\u5e08\u91cd\u53c8\u6536\u4e86\u5f1f\u5b50\uff0c\u6211\u7edd\u5bf9\u4e0d\u4f1a\u518d\u628a\u5929\u9b54\u95e8\u7684\u6b66\u529f\u4f20\u51fa\u53bb\uff0c\u6211\u6b7b\u4e86\u4e4b\u540e\uff0c\u6c5f\u6e56\u4e0a\u5c31\u6c38\u8fdc\u4e0d\u4f1a\u518d\u6709\u5929\u9b54\u95e8\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53cc\u5973\uff0c\u8fd9\u4e48\u8bf4\uff0c\u8981\u89c1\u8bc6\u4e00\u4e0b\u5929\u9b54\u95e8\u7684\u6b66\u529f\uff0c\u975e\u4f60\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5bd2\u8863\uff0c\u4f60\u771f\u7684\u5e0c\u671b\u4f60\u81ea\u5df1\u7684\u59bb\u5b50\uff0c\u8131\u4e86\u8863\u670d\uff0c\u8d64\u8eab\u88f8\u4f53\u7684\u7ed9\u4eba\u770b\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6211\u4f5c\u8bc1\uff0c\u662f\u4ed6\u903c\u7740\u4f60\u8868\u6f14\u7684\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u4e0d\u8981\u63a8\u6ce2\u52a9\u6f9c\uff0c\u6211\u4e0d\u662f\u8bf4\u5f97\u5f88\u6e05\u695a\u4e48\uff0c\u60c5\u52bf\u5fc5\u8981\u65f6\uff0c\u6211\u4f1a\u5168\u529b\u65bd\u5c55\u3002\u201d \u3000\u3000\u5979\u5f00\u59cb\u53d6\u836f\u7269\uff0c\u5206\u6577\u5728\u5404\u4eba\u7684\u4f24\u5904\u3002 \u3000\u3000\u7136\u540e\uff0c\u5f00\u59cb\u4f20\u6388\u4ed6\u4eec\u5404\u79cd\u7684\u5185\u529f\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u79cd\u5f88\u5947\u602a\u7684\u836f\u7269\uff0c\u6577\u4e0a\u4e86\u4e4b\u540e\uff0c\u7acb\u523b\u6709\u4e00\u79cd\u5982\u996e\u9187\u9152\u7684\u611f\u89c9\uff0c\u8fde\u5934\u8111\uff0c\u4e5f\u6709\u4e9b\u660f\u660f\u6c89\u6c89\u7684\u611f\u53d7\u3002 \u3000\u3000\u6577\u836f\u4eba\uff0c\u4e5f\u7acb\u523b\u9075\u7167\u7740\u5434\u53cc\u5973\u4f20\u6388\u7684\u5185\u529f\u65bd\u4e3a\u3002 \u3000\u3000\u7384\u5a18\u6ca1\u6709\u6577\u7528\u8fd9\u4e9b\u836f\u7269\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u5979\u65ad\u53bb\u4e86\u4e00\u652f\u5de6\u81c2\uff0c\u6ca1\u6709\u4e00\u6bb5\u8f83\u957f\u65f6\u95f4\u7684\u9002\u5e94\u4e4b\u540e\uff0c\u8fd0\u7528\u8d77\u6765\uff0c\u5341\u5206\u4e0d\u4fbf\u3002 \u3000\u3000\u836f\u7269\u7684\u53cd\u5e94\uff0c\u679c\u5982\u5434\u53cc\u5973\u6240\u8bf4\uff0c\u4f7f\u4eba\u6709\u7740\u4e00\u79cd\u660f\u660f\u6b32\u7761\u7684\u611f\u89c9\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5f88\u62c5\u5fc3\u3002 \u3000\u3000\u4f46\u62c5\u5fc3\u7684\u4e8b\u60c5\uff0c\u504f\u504f\u5c31\u4f1a\u53d1\u751f\u4e86\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u771f\u7684\u6765\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u96f7\u65e0\u5f71\u5e26\u6765\u7684\u4eba\u4e0d\u591a\uff0c\u8fde\u4ed6\uff0c\u4e5f\u4e0d\u8fc7\u4e09\u4e2a\u4eba\u3002 \u3000\u3000\u4e24\u4e2a\u4ece\u4eba\uff0c\u7a7f\u7740\u94f6\u7070\u7684\u52b2\u88c5\uff0c\u8eab\u4f69\u957f\u5251\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u59d0\uff0c\u836f\u6027\u8fd8\u8981\u591a\u5c11\u65f6\u95f4\uff0c\u624d\u80fd\u8fc7\u53bb\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5feb\u4e86\uff0c\u60f3\u6cd5\u5b50\uff0c\u62d6\u5b83\u4e00\u62d6\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u3001\u53f6\u957f\u9752\u3001\u4e07\u80dc\u90fd\u5df2\u6697\u4f5c\u6212\u5907\u3002 \u3000\u3000\u5c1a\u5929\u4e49\u592b\u5987\uff0c\u96ea\u6885\uff0c\u548c\u56db\u4e2a\u6ca1\u6709\u53d7\u4f24\u7684\u5200\u624b\uff0c\u5949\u547d\u4ee5\u4fdd\u62a4\u4f24\u8005\u4e3a\u4e3b\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7f13\u7f13\u7ad9\u8d77\u4e86\u8eab\u5b50\uff0c\u8fce\u5411\u96f7\u65e0\u5f71\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u88ab\u963b\u6b62\u5728\u4e24\u4e08\u5f00\u5916\u3002 \u3000\u3000\u4ed6\u5f88\u8bc6\u76f8\uff0c\u4e5f\u4e86\u89e3\u9ad8\u5e7d\u5170\u7684\u7528\u5fc3\uff0c \u81ea\u52a8\u505c\u4e0b\u4e0b\u6765\u3002 \u9ad8\u5e7d\u5170\u6de1\u6de1\u4e00\u7b11\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u96f7\u5821\u4e3b\u60f3\u6765\u5e2e\u5fd9\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u770b\u8bf8\u4f4d\u7684\u4f24\u4ea1\u5f88\u91cd\uff0c\u4e0d\u77e5\u9053\u6210\u5c31\u5982\u4f55\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6210\u5c31\u4e0d\u592a\u597d\uff0c\u9ad8\u5929\u5065\u987d\u5f3a\u4e0d\u5c48\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u56de\u987e\u4e86\u70df\u9501\u697c\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e8c\u5c0f\u59d0\uff0c\u9ad8\u5929\u5065\u8fd8\u5728\u4e48?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5728\u70df\u9501\u697c\u4e0a\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ed6\u662f\u4e0d\u662f\u53d7\u4e86\u4f24?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u597d\u50cf\u662f\u53d7\u4e86\u4f24\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4f24\u5f97\u5f88\u91cd\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u8be6\u7ec6\u7684\u4f24\u52bf\uff0c\u6211\u6ca1\u6709\u770b\u5f97\u5f88\u6e05\u695a\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5509!\u59d1\u5a18\uff0c\u4f60\u548c\u9ad8\u5929\u5065\u52a8\u8fc7\u624b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u6740\u4e00\u573a\uff01\uff01\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u59d1\u5a18\u548c\u9ad8\u5821\u4e3b\uff0c\u5df2\u7ecf\u5b8c\u5168\u6ca1\u6709\u4e86\u4e00\u70b9\u60c5\u610f\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4ed6\u6740\u5bb3\u4e86\u6211\u7684\u7236\u6bcd\uff0c\u4e24\u6b21\u5bf9\u6211\u7528\u6bd2\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eb2\u60c5\u53ef\u8a00\u3002\u201d \u96f7\u65e0\u5f71\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5728\u4e0b\u60f3\u77e5\u9053\uff0c\u4f60\u4eec\u51e0\u65f6\u53ef\u4ee5\u653b\u4e0b\u70df\u9501\u697c\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5f88\u96be\u8bf4\uff0c \u76ee\u4e0b\u9ec4\u5821\u4e3b\u548c\u4e24\u4f4d\u5251\u4f7f\uff0c\u90fd\u5df2\u53d7\u4f24\uff0c\u6b63\u5728\u517b\u606f\uff0c\u6211\u60f3\uff0c\u4ed6\u4eec\u4f24\u5f97\u4e0d\u592a\u91cd\uff0c\u4f11\u606f\u4e0a\u4e24\u4e09\u4e2a\u65f6\u8fb0\uff0c\u5c31\u53ef\u4ee5\u6062\u590d\u6218\u529b\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u59d1\u5a18\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a \u201c\u6211\u60f3\uff0c\u4e0d\u4f1a\u8d85\u8fc7\u660e\u5929\u4e2d\u5348\uff0c\u4e00\u5b9a\u53ef\u4ee5\u653b\u4e0b\u70df\u9501\u697c\u3002\u201d \u96f7\u65e0\u5f71\u795e\u60c5\u51b7\u8083\u7684\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u8981\u4e0d\u8981\u5728\u4e0b\u5e2e\u5fd9\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u770b\u4e0d\u7528\u4e86\uff0c\u96f7\u5821\u4e3b\u73b0\u5728\u5e94\u8be5\u4fdd\u6709\u5b9e\u529b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u8fd9\u8bdd\uff0c\u662f\u4ec0\u4e48\u610f\u601d\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u610f\u601d\u5f88\u7b80\u5355\uff0c\u6211\u4eec\u6253\u4e0b\u70df\u9501\u697c\uff0c\u6740\u4e86\u9ad8\u5929\u5065\u4e4b\u540e\uff0c\u96f7\u5802\u4e3b\u53ef\u4ee5\u518d\u4f5c\u51b3\u5b9a\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u8981\u6211\u51b3\u5b9a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u51b3\u5b9a\u662f\u5426\u548c\u6211\u4eec\u5408\u4f5c\uff0c\u90a3\u65f6\uff0c\u96f7\u5802\u4e3b\u7684\u5b9e\u529b\u8d8a\u5f3a\uff0c\u8c08\u5224\u7684\u8eab\u4efd\u5c31\u8d8a\u9ad8\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e8b\u5b9e\u4e0a\uff0c\u96f7\u5802\u4e3b\u4e00\u76f4\u5728\u4f5c\u8fd9\u79cd\u6253\u7b97\uff0c\u8ba4\u4e3a\u6211\u4eec\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u96f7\u65e0\u5f71\u6ca1\u6709\u518d\u8bf4\u8bdd\uff0c\u76ee\u5149\u4e00\u76f4\u5728\u90a3\u4e9b\u53d7\u4f24\u4eba\u7684\u8eab\u4e0a\u6253\u91cf\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u96f7\u5802\u4e3b\uff0c\u4f60\u60f3\u770b\u4ec0\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u4f24\u52bf\uff0c\u597d\u50cf\u6709\u4eba\u65ad\u53bb\u4e86\u4e00\u6761\u624b\u81c2?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u90a3\u662f\u7384\u5a18\uff0c\u5979\u867d\u7136\u65ad\u4e86\u4e00\u81c2\uff0c\u4f46\u53ef\u80fd\u62fc\u4e86\u9ad8\u5929\u5065\u4e00\u53ea\u773c\u775b\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u9ad8\u5929\u5065\u778e\u4e86\u4e00\u53ea\u773c?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u53ea\u662f\u53ef\u80fd\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u59d1\u5a18\u548c\u9ad8\u5929\u5065\u52a8\u624b\u6253\u4e86\u51e0\u4e2a\u5408\uff1f\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e09\u5341\u4e2a\u56de\u5408\uff0c\u603b\u5e94\u8be5\u6709\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u53d7\u4f24?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5e73\u5206\u79cb\u8272\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5f88\u60ed\u6127\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u9519\u4e86\uff0c\u653e\u773c\u5929\u4e0b\u80fd\u591f\u548c\u9ad8\u5929\u5065\u52a8\u624b\uff0c\u6253\u4e0a\u4e09\u5341\u4e2a\u56de\u5408\u7684\u4eba\uff0c\u5c48\u6307\u53ef\u6570\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u9ec4\u7075\u53ef\u4ee5\uff0c\u6211\u60f3\u96f7\u5802\u4e3b\u5927\u6982\u4e5f\u53ef\u4ee5\u5427?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u90a3\u8981\u770b\u4ec0\u4e48\u60c5\u51b5\uff0c\u5982\u82e5\u9ad8\u5929\u5065\u4f24\u7684\u5f88\u91cd\uff0c\u6211\u60f3\uff0c\u5728\u4e0b\u4e5f\u8bb8\u53ef\u4ee5\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u96f7\u5802\u4e3b\u5f88\u8c26\u865a\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4eba\u8d35\u81ea\u77e5\uff0c\u5728\u4e0b\u4e00\u5411\u4e0d\u662f\u901e\u5f3a\u597d\u80dc\u7684\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u54e6!\u96f7\u5802\u4e3b\u5fc3\u4e2d\u7684\u7591\u95ee\uff0c\u5927\u6982\u5df2\u7ecf\u95ee\u7684\u5f88\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90fd\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u662f\u4e0d\u662f\u51c6\u5907\u5e2e\u6211\u4eec\u4e00\u70b9\u5fd9?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5982\u82e5\u5e7d\u5170\u59d1\u5a18\u662f\u5411\u96f7\u67d0\u4eba\u6c42\u52a9\uff0c\u795e\u5200\u5802\u5012\u662f\u53ef\u4ee5\u6d3e\u51e0\u4e2a\u4eba\u6765\u6548\u529b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u5411\u4f60\u6c42\u52a9\uff0c\u4f60\u5982\u89c9\u7740\u5e94\u8be5\u5bf9\u4ed8\u70df\u9501\u697c\u90a3\u4f60\u5c31\u6d3e\u4eba\u6765\u5427\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u7531\u5728\u4e0b\u4eb2\u81ea\u7387\u9886\u4eba\u624b\u52a8\u624b\u5462?\u8fd8\u662f\u5728\u4e0b\u6d3e\u51e0\u4e2a\u4eba\u6765\uff0c\u7531\u4f60\u59d1\u5a18\u52a8\u624b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u6211\u770b\u8981\u7531\u4f60\u6765\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u5f88\u5927\u65b9\u554a!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u7a76\u7adf\u662f\u4f55\u7528\u5fc3\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u51fa\u6765\u5462?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u7a81\u7136\u53d8\u4e86\u8138\u8272\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u5e0c\u671b\u80fd\u591f\u4e86\u89e3\u4e00\u4e0b\u4f60\u4eec\u53cc\u65b9\u53ae\u6740\u7684\u60c5\u5f62\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u5df2\u7ecf\u4e86\u89e3\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5bf9!\u6211\u5df2\u7ecf\u4e86\u89e3\u4e86\uff0c\u4e0d\u8fc7\uff0c\u8fd8\u4e0d\u7b97\u5f88\u660e\u767d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u6765\u770b\u770b\uff0c\u662f\u4e0d\u662f\u80fd\u5750\u6536\u6e14\u5229?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5e7d\u5170\u59d1\u5a18\uff0c\u76ee\u524d\uff0c\u6700\u597d\u522b\u6811\u5728\u4e0b\u8fd9\u4e2a\u654c\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u76ee\u524d\uff0c\u4f60\u4eec\u5e94\u8be5\u662f\u6700\u9700\u8981\u670b\u53cb\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u63a5\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u6765\u770b\u770b\u7684\u76ee\u7684\uff0c\u662f\u60f3\u62a2\u4e2a\u4fbf\u5b9c\u4e86\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5927\u5c0f\u59d0\u7684\u4f24\u52bf\u5982\u4f55?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u592a\u8f7b\uff0c\u4e0d\u8fc7\uff0c\u5bf9\u4ed8\u4f60\u96f7\u65e0\u5f71\uff0c\u6211\u76f8\u4fe1\u8fd8\u53ef\u4ee5\u5e94\u4ed8\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u54e6!\u5927\u5c0f\u59d0\u7684\u53e3\u6c14\uff0c\u8fd8\u662f\u5f88\u5927\u554a!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54fc!\u96f7\u65e0\u5f71\uff0c\u4e0d\u8bba\u4f60\u800d\u51fa\u4ec0\u4e48\u82b1\u62db\uff0c\u90fd\u522b\u60f3\u7792\u5f97\u8fc7\u6211\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u5927\u5c0f\u59d0\uff0c\u4f60\u4e0d\u80fd\u8bf4\u5f97\u90a3\u4e48\u96be\u542c\uff0c\u5982\u82e5\u5728\u4e0b\u7684\u770b\u6cd5\u4e0d\u9519\uff0c\u4f60\u4eec\u73b0\u5728\u6700\u9700\u8981\u5e2e\u52a9\uff0c\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f4f\u53e3\uff0c\u6211\u5b81\u53ef\u6218\u6b7b\u70df\u9501\u697c\uff0c\u4e5f\u7528\u4e0d\u7740\u4f60\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5434\u53cc\u5973\uff0c\u522b\u592a\u51b2\u52a8\uff0c\u4e0d\u7ba1\u4f60\u7684\u60f3\u6cd5\u5982\u4f55\uff0c\u6211\u7684\u8bdd\u603b\u8981\u8bf4\u5b8c\uff0c\uff1a\u6211\u53ef\u4ee5\u5e2e\u52a9\u4f60\uff0c\u4e0d\u8bba\u9ad8\u5929\u5065\u7684\u4f24\u52bf\u5982\u4f55\uff0c\u4ee5\u6211\u795e\u5200\u5802\u7684\u5b9e\u529b\uff0c\u90fd\u53ef\u4ee5\u5403\u5b9a\u4e86\u70df\u9501\u697c\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u4ec0\u4e48\u6761\u4ef6?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5728\u4e0b\u5bf9\u5927\u5c0f\u59d0\u7684\u604b\u5e55\u4e4b\u60c5\uff0c\u786e\u5b9e\u51fa\u4e8e\u4e00\u7247\u8bda\u5fc3\uff0c\u53ea\u8981\u5979\u80af\u7b54\u5e94\u6211\u3002\u4e0d\u7528\u8bf8\u4f4d\u51fa\u624b\uff0e\u5728\u4e0b\u5305\u6253\u70df\u9501\u697c\uff0c\u800c\u4e14\uff0c\u65e5\u843d\u4e4b\u524d\u3002\u4e00\u5b9a\u628a\u70df\u9501\u697c\u6253\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54e6!\u4f60\u8981\u5979\u7b54\u5e94\u4ec0\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5a5a\u59fb\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u5979\u5df2\u7ecf\u662f\u522b\u4eba\u7684\u59bb\u5b50\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\uff0c\u5728\u4e0b\u4e5f\u4e0d\u8ba1\u8f83\uff0c\u53ea\u8981\u5927\u5c0f\u59d0\u80af\u7b54\u5e94\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u597d\u5927\u7684\u80c6\u5b50!\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u8272\u80c6\u5305\u5929\uff0c\u5728\u4e0b\u8fd9\u505a\u6cd5\uff0c\u5e94\u8be5\u662f\u6700\u6e29\u548c\u7684\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u6b63\u60f3\u53d1\u4f5c\uff0c\u5374\u88ab\u9ad8\u5e7d\u5170\u6321\u4e86\u4e0b\u6765\uff0c\u9053\uff1a \u201c\u96f7\u5802\u4e3b\uff0c\u8fd9\u5c31\u662f\u4f60\u6765\u7684\u76ee\u7684\u4e86\u3002\u201d \u8bed\u58f0\u4e00\u987f\uff0c\u9053\uff1a\u201c\u597d\u5427!\u8fd9\u4ef6\u4e8b\uff0c\u8ba9\u6211\u4eec\u5546\u91cf\u4e00\u4e0b\uff0c\u96f7\u5802\u4e3b\uff0c\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u540e\u518d\u6765\u5427\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u7b49\u4e24\u4e2a\u65f6\u8fb0\u3002\u8fd9\u4ef6\u4e8b\uff0c\u5e94\u8be5\u5f88\u5feb\u5c31\u53ef\u4ee5\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e8b\u60c5\u6ca1\u6709\u90a3\u4e48\u7b80\u5355\uff0c\u81f3\u5c11\u5e94\u8be5\u548c\u4ed6\u4eec\u5546\u91cf\u4e00\u4e0b\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5546\u91cf\uff0c\u548c\u4ec0\u4e48\u4eba\u5546\u91cf?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u9664\u4e86\u8bf4\u670d\u5927\u59d0\u4e4b\u5916\uff0c\u8fd8\u8981\u548c\u4f55\u516c\u5b50\u5546\u91cf\u4e00\u4e0b\uff0c\u5bf9\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u54e6!\u597d\u5427!\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u6211\u4f1a\u6765\u542c\u6d88\u606f\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u597d\u5427!\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u54b1\u4eec\u518d\u89c1\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u53c8\u671b\u4e86\u73b0\u573a\u4e00\u773c\uff0c\u624d\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u76ee\u7779\u96f7\u65e0\u5f71\u80cc\u5f71\u6d88\u5931\u4e4b\u540e\uff0c\u5434\u53cc\u5973\u795e\u60c5\u51b7\u8083\u5730\u8bf4\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u4f60\u662f\u4e0d\u662f\u5e0c\u671b\u770b\u770b\u5929\u9b54\u95e8\u7684\u6b66\u529f\u5417?\u7b49\u4e00\u4f1a\uff0c\u4f60\u4eec\u53ef\u4ee5\u89c1\u5230\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f60\u8981\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6740\u96f7\u65e0\u5f71\uff0c\u6211\u8981\u7528\u5929\u9b54\u95e8\u7684\u6b66\u529f\uff0c\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u59ca\uff0c\u4f60\u8981\u6740\u4e86\u4ed6\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u8be5\u6b7b\uff0c\u6211\u8981\u4e0d\u62e9\u624b\u6bb5\u7684\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u7167\u4ed6\u7684\u884c\u4e3a\uff0c\u4e0d\u7528\u5927\u59ca\u5429\u5490\uff0c\u6211\u4e5f\u8981\u6740\u4e86\u4ed6\uff0c\u4e0d\u8fc7\uff0c\u8861\u91cf\u76ee\u524d\u7684\u60c5\u5f62\uff0c\u5229\u7528\u4ed6\u4e00\u4e0b\uff0c\u6bd4\u6740\u4e86\u4ed6\uff0c\u66f4\u4e3a\u6709\u5229\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5229\u7528\u4ed6?\u8fd9\u79cd\u4eba\uff0c\u5982\u4f55\u4e00\u4e2a\u5229\u7528\u6cd5?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u6211\u770b\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u8981\u4f60\u548c\u5927\u59ca\u8bf4\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ed6\u8bf4\u4ec0\u4e48\u5462?\u5e7d\u5170\uff0c\u4f60\u4e0d\u8981\u6349\u5f04\u6211\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5c0f\u59b9\u4e0d\u6562\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u5c31\u7b97\u5e7d\u5170\u4e0d\u8981\u6211\u8bf4\uff0c\u6211\u4e5f\u60f3\u8bf4\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5927\u59d0\uff0c\u542c\u542c\u5bd2\u8863\u7684\u610f\u89c1\u561b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d!\u8ba9\u4ed6\u8bf4\u5427\uff01\u201d \u3000\u3000\u4f55\u5bd2\u8863\u8214\u8214\u5634\u5507\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u53cc\u5973\uff0c\u6211\u662f\u5927\u5904\u7740\u773c\uff0c\u5982\u82e5\u6709\u4ec0\u4e48\u8bf4\u9519\u7684\u5730\u65b9\uff0c\u5e0c\u671b\u4f60\u539f\u8c05\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u59d0\uff0c\u770b\u770b\u4ed6\u591a\u5ba2\u6c14\u554a!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u4e0d\u4f46\u5f88\u4f1a\u8ba1\u7b97\uff0c\u800c\u4e14\uff0c\u5634\u5df4\u4e5f\u5f88\u751c\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a\u201c\u6211\u60f3\uff0c \u76ee\u4e0b\u54b1\u4eec\u5f88\u96be\u518d\u53d7\u5f97\u8d77\u4e00\u6b21\u632b\u8d25\uff0c\u5982\u82e5\uff0c\u96f7\u65e0\u5f71\u771f\u7684\u7cbe\u9510\u5c3d\u51fa\uff0c\u548c\u6211\u4eec\u62fc\u4e86\uff0c\u90a3\u5c06\u662f\u4e00\u4e2a\u5f88\u60b2\u60e8\u7684\u5c40\u9762\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u6709\u628a\u63e1\u6740\u6b7b\u96f7\u65e0\u5f71\uff0c\u5927\u7ea6\u4e0d\u4f1a\u8d85\u8fc7\u4e09\u5341\u62db\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u96f7\u65e0\u5f71\u5982\u82e5\u8981\u6765\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u4e00\u4e2a\u4eba\u6765\uff0c\u4f60\u6740\u4e86\u4ed6\uff0c\u4e5f\u65e0\u6cd5\u89e3\u53bb\u5168\u4f53\u4e4b\u5371\uff0c\u518d\u8bf4\uff0c\u4ed6\u4e5f\u672a\u5fc5\u548c\u4f60\u52a8\u624b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u4e3a\u96be\u5730\u8bf4\u9053\uff1a\u201c\u7528\u70b9\u5fc3\u673a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u60f3\uff0c\u54b1\u4eec\u5e94\u8be5\uff0c\u5e94\u8be5\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u5e94\u8be5\u600e\u4e48\u6837?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u60f3\uff0c\u5bf9\u96f7\u65e0\u5f71\u5e94\u8be5\u865a\u4e0e\u59d4\u86c7\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f60\u8981\u81ea\u5df1\u7684\u59bb\u5b50\uff0c\u53bb\u548c\u53e6\u5916\u7684\u7537\u4eba\uff0c\u865a\u4e0e\u59d4\u86c7?\u4f60\u4e00\u70b9\u4e5f\u4e0d\u4f1a\u5403\u918b\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u6211\u53ef\u4ee5\u633a\u8eab\u800c\u51fa\u3002\u8840\u6d41\u4e94\u6b65\uff0c\u4fdd\u62a4\u4f60\uff0c\u4f46\u73b0\u5728\u60c5\u5f62\u4e0d\u540c\uff0c\u56e0\u4e3a\uff0c\u8fd9\u4e0d\u662f\u4e3a\u4e86\u6211\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e3a\u522b\u4eba?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u73b0\u5728\u9ec4\u5821\u4e3b\uff0c\u548c\u8fd9\u4e9b\u670b\u53cb\uff0c\u4e5f\u4e3a\u4e86\u6c5f\u6e56\u5927\u5c40\u548c\u6b63\u4e49\u3002\u53cc\u5973\uff0c\u4e00\u4e2a\u4eba\uff0c \u4e3a\u81ea\u5df1\uff0c\u53ef\u4ee5\u5802\u5802\u6b63\u6b63\uff0c\u6709\u65f6\u5019\u4e3a\u4e86\u522b\u4eba\uff0c\u5374\u5e94\u8be5\u4ed8\u51fa\u70b9\u727a\u7272\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5bf9\u4f60\u6765\u8bf4\uff0c\u8fd9\u662f\u4e00\u4ef6\u5f88\u4e3a\u96be\u7684\u4e8b\uff0c\u6211\u4e5f\u660e\u767d\uff0c\u4f60\u5185\u5fc3\u4e4b\u4e2d\uff0c\u4e00\u4e07\u4e2a\u4e0d\u613f\u610f\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u65e2\u7136\u77e5\u9053\u6211\u4e0d\u613f\u610f\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u52c9\u5f3a\u6211\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u624d\u6c42\u4f60\u2026\u2026\u201d \u3000\u3000\u8bed\u97f3\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u6211\u770b\u8fc7\u4ed6\u4eec\u7684\u4f24\u52bf\uff0c\u6bcf\u4e2a\u4eba\uff0c\u90fd\u76f8\u5f53\u7684\u91cd\uff0c\u5c31\u7b97\u4f60\u7684\u836f\u7269\u5f88\u6709\u6548\uff0c\u4f46\u5982\u8fd9\u4e00\u573a\u640f\u6740\u4e0b\u6765\uff0c\u4ed6\u4eec\u7684\u4f24\u91cd\u4e4b\u8eaf\uff0c\u53c8\u80fd\u652f\u6491\u591a\u4e45\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u6c89\u9ed8\u4e0d\u8bed\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u53cc\u5973\uff0c\u4e00\u4e2a\u4eba\u6d3b\u5728\u4e16\u4e0a\uff0c\u4e0d\u4e00\u5b9a\u5b8c\u5168\u4e3a\u4e86\u81ea\u5df1\u3002\u201d \u5434\u53cc\u5973\u957f\u957f\u547c\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u6211\u660e\u767d\u4e86\uff0c \u4f60\u8bf4\u5427!\u8981\u6211\u5982\u4f55\u505a\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u53cc\u5973\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5177\u4f53\u7684\u529e\u6cd5\uff0c\u6211\u53ea\u662f\u89c9\u7740\uff0c\u5e94\u8be5\u9009\u62e9\u5bf9\u54b1\u4eec\u6700\u6709\u5229\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1b\u201c\u4ec0\u4e48\u624d\u662f\u6700\u6709\u5229\u7684\u529e\u6cd5\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u53ea\u662f\u6c42\u4f60\u540c\u610f\uff0c\u4ec0\u4e48\u529e\u6cd5\uff0c\u800d\u54b1\u4eec\u4ed4\u7ec6\u7684\u5546\u91cf\u4e86\u3002\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u76ee\u5149\u5374\u8f6c\u671b\u7740\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u59ca\uff0c\u6709\u4e24\u4e2a\u529e\u6cd5\uff0c\u4f60\u770b\u54ea\u4e00\u4e2a\u597d?\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u96f7\u65e0\u5f71\u518d\u6765\u65f6\uff0c\u53e3\u5934\u4e0a\u5e94\u4ed8\uff0c\u60f3\u6cd5\u5b50\u63a5\u8fd1\u4ed6\uff0c\u51fa\u5176\u4e0d\u610f\u7684\u4e00\u5200\u6740\u4e86\u4ed6\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u90a3\u4f1a\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u5b50\u7684\u540e\u679c\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u540e\u679c\u4e0d\u4f1a\u5f88\u4e25\u91cd\uff0c\u4f46\u4e00\u9635\u6fc0\u70c8\uff0c\u77ed\u6682\u7684\u640f\u6740\uff0c\u52bf\u6240\u96be\u514d\uff0c\u95ee\u9898\u662f\u5728\u8981\u4e00\u51fb\u5fc5\u4e2d\uff0c\u4e0d\u80fd\u6709\u4e00\u70b9\u9519\u5931\uff0c\u5982\u82e5\u4e00\u51fb\u6740\u4e0d\u4e86\u4ed6\uff0c\u90a3\u5c31\u4f1a\u9020\u6210\u4e00\u4e2a\u5f88\u5927\u7684\u9ebb\u70e6\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u7ed9\u6211\u4e09\u5341\u56de\u5408\u65f6\u95f4\uff0c\u6211\u76f8\u4fe1\uff0c\u6211\u6740\u5f97\u4e86\u4ed6\uff0c\u4f46\u7a81\u7136\u51fa\u624b\u4e00\u62db\uff0c\u80fd\u4e0d\u80fd\u6740\u4e86\u4ed6\uff0c\u6211\u81ea\u5df2\u662f\u6beb\u65e0\u628a\u63e1\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6700\u597d\u6709\u628a\u63e1\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u4ed6\u5982\u679c\u5168\u65e0\u6212\u5907\uff0c\u6210\u7b97\u5f88\u5927\uff0c\u5982\u662f\u4ed6\u6709\u4e86\u6212\u5907\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u4ed6\u4e00\u5b9a\u4f1a\u6709\u6212\u5907\uff0c\u53ea\u662f\u4ed6\u8b66\u60d5\u5fc3\u7684\u9ad8\u4f4e\u7f62\u4e86\uff0c\u5c06\u4eba\u6bd4\u5df1\uff0c\u5982\u662f\u96f7\u65e0\u5f71\u7a81\u7136\u63a5\u8fd1\u54b1\u4eec\u65f6\uff0c\u4f60\u662f\u4e0d\u662f\u4e5f\u8981\u6709\u6240\u6212\u5907\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u81ea\u7136\u4f1a\u6709\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1b\u201c\u90a3\u5c31\u662f\u4e86\uff0c\u96f7\u65e0\u5f71\u90a3\u79cd\u4eba\u5c82\u662f\u7b80\u5355\u4eba\u7269\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u7b2c\u4e00\u4e2a\u529e\u6cd5\u884c\u4e0d\u901a\uff0c\u7b2c\u4e8c\u4e2a\u529e\u6cd5\u53c8\u662f\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7b2c\u4e8c\u4e2a\u529e\u6cd5\u8981\u9760\u5927\u59ca\u7684\u8868\u6f14\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211?\u8868\u6f14\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u76ee\u7684\u4f7f\u4ed6\u4e3a\u6211\u4eec\u6240\u7528\uff0c\u81f3\u5c11\uff0c\u53ef\u4ee5\u62d6\u5ef6\u4e00\u4e0b\u4ed6\u5bf9\u4ed8\u6211\u4eec\u7684\u65f6\u95f4\uff0c\u6211\u77e5\u9053\u5927\u59ca\u957f\u4e8e\u9002\u5e94\uff0c\u771f\u8981\u62c9\u4e0b\u8138\u6765\uff0c\u6211\u60f3\u4f60\u4f1a\u505a\u7684\u5f88\u597d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u597d!\u6211\u5c3d\u529b\u800c\u4e3a\uff0c\u4e0d\u8fc7\uff0c\u6211\u8981\u8bf4\u660e\u7684\u662f\uff0c\u6211\u4e0d\u80fd\u63a5\u53d7\u4ed6\u4efb\u4f55\u4e00\u70b9\u8f7b\u8584\u4e3e\u6b62\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4ed6\u4e0d\u6562\uff0c\u5927\u59ca\u3002\u4ed6\u89c6\u4f60\u5982\u5929\u4eba\uff0c \u4f60\u53ea\u8981\u53e3\u89d2\u6625\u98ce\uff0c\u7ed9\u4ed6\u4e00\u70b9\u751c\u7f8e\u4e4b\u611f\uff0c\u4ed6\u5c31\u4f1a\u5982\u996e\u9187\u9152\uff0c\u9676\u7136\u6b32\u9189\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\uff0c\u5426\u5219\uff0c\u6211\u53ea\u6709\u51fa\u624b\u4e00\u62fc\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6700\u597d\u4e0d\u8981\u9020\u6210\u5982\u6b64\u5c34\u5c2c\u7684\u5c40\u9762\uff0c\u5982\u5b9e\u5728\u5fc5\u8981\uff0c\u4f60\u8981\u60f3\u6cd5\u5b50\u5148\u901a\u77e5\u6211\u4e00\u58f0\uff0c\u7136\u540e\uff0c\u54b1\u4eec\u4e00\u8d77\u51fa\u624b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9ec4\u7075\u542c\u5230\u4e86\u4e09\u4eba\u7684\u8c08\u8bdd\uff0c\u4f46\u4ed6\u4e00\u76f4\u6ca1\u6709\u5f00\u53e3\u3002 \u3000\u3000\u4ed6\u660e\u767d\uff0c\u8fd9\u65f6\u523b\u63d2\u5634\uff0c\u5de6\u53f3\u4e3a\u96be\uff0c \u4e5f\u4e0d\u77e5\u9053\u5e94\u8be5\u8bf4\u4ec0\u4e48\u624d\u597d\u3002 \u8981\u4e00\u4e2a\u5973\u4eba\uff0c\u800c\u4e14\u662f\u6709\u592b\u4e4b\u5987\u7684\u5973\u4eba\uff0c\u7528\u5979\u7684\u7f8e\u8272\uff0c\u6765\u62ef\u6551\u5f88\u591a\u4eba\u7684\u6027\u547d\uff0c\u5305\u62ec\u4ed6\u9ec4\u7075\u81ea\u5df1\u5728\u5185\uff0c\u5b9e\u5728\u662f\u4e00\u4ef6\u5f88\u96be\u5f00\u53e3\u7684\u4e8b\u3002 \u3000\u3000\u9ec4\u7075\u4e00\u751f\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u8c08\u7b11\u4e8e\u751f\u6b7b\u4e4b\u4e2d\uff0c\u4e00\u76f4\u6ca1\u6709\u4f7f\u670b\u53cb\u5982\u6b64\u7684\u4e3a\u96be\u8fc7\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u4ed6\u6ca1\u6709\u529e\u6cd5\u963b\u6b62\uff0c\u56e0\u4e3a\uff0c\u53d7\u4f24\u7684\u6709\u5f88\u591a\u4eba\uff0c\u7384\u5a18\u3001\u534e\u5999\u771f\uff0c\u90fd\u5728\u5185\u3002 \u3000\u3000\u4ed6\u53ef\u4ee5\u8f7b\u6de1\u81ea\u5df1\u7684\u751f\u6b7b\uff0c\u4f46\u4e0d\u80fd\u4e0d\u7ba1\u522b\u4eba\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u534e\u5999\u771f\u548c\u7384\u5a18\uff0c\u4e5f\u542c\u5230\u4e86\u4e09\u4eba\u7684\u8c08\u8bdd\uff0c\u5434\u53cc\u5973\u53ea\u77e5\u9053\u81ea\u5df1\u8eab\u4e0a\u643a\u5e26\u7684\u836f\u7269\uff0c\u4e0d\u662f\u4e00\u822c\u4eba\u53ef\u4ee5\u7528\u7684\uff0c\u56e0\u4e3a\u8fd9\u836f\u7269\u53ef\u80fd\u6709\u5f88\u5f3a\u7684\u540e\u9057\u75c7\uff0c\u800c\u4e14\u5fc5\u987b\u4ee5\u5929\u9b54\u95e8\u7684\u6b66\u529f\uff0c\u624d\u80fd\u5f15\u53d1\u8fd9\u4e9b\u836f\u7269\u7684\u4f5c\u7528\uff0c\u5929\u9b54\u95e8\u7684\u6b66\u529f\uff0c\u6839\u672c\u7684\u7ec3\u6cd5\uff0e\u548c\u4e00\u822c\u7684\u6b66\u529f\u4e0d\u540c\uff0c\u5434\u53cc\u5973\u4e0d\u613f\u628a\u5929\u9b54\u95e8\u7684\u6b66\u529f\uff0c\u518d\u4f20\u5165\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u6240\u4ee5\u4e0d\u613f\u628a\u836f\u7269\u8f7b\u6613\u7ed9\u4eba\u3002 \u3000\u3000\u4e0d\u7ba1\u8fd9\u4e9b\u836f\u7269\u7528\u540e\u6709\u4ec0\u4e48\u540e\u9057\u75c7\uff0c\u4f46\u5929\u9b54\u5185\u529f\u5f15\u53d1\u4e86\u836f\u529b\u4e4b\u540e\uff0c\u53d7\u4f24\u7684\u4eba\u7acb\u523b\u611f\u89c9\u5230\u4f24\u75bc\u5927\u51cf\u3002 \u3000\u3000\u5e7f\u573a\u4e2d\u6062\u590d\u4e86\u4e00\u7247\u9759\u5bc2\uff0c\u9759\u5f97\u542c\u4efb\u4f55\u4e00\u70b9\u58f0\u606f\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u518d\u8bf4\u8bdd\uff0c\u4f46\u6bcf\u4e2a\u4eba\u7684\u5fc3\u5934\uff0c\u5374\u5f88\u6c89\u91cd\u3002 \u3000\u3000\u4e24\u4e2a\u65f6\u8fb0\uff0c\u5bf9\u4e0d\u540c\u7684\u4eba\uff0c\u6709\u4e0d\u540c\u7684\u611f\u89c9\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u89c9\u7740\u5f88\u957f\uff0c\u6709\u4e9b\u4eba\u611f\u89c9\u7740\u5f39\u6307\u5373\u8fc7\u3002 \u3000\u3000\u4f46\u4e0d\u7ba1\u522b\u4eba\u7684\u611f\u89c9\u5982\u4f55\uff0c\u4e24\u4e2a\u65f6\u8fb0\uff0c\u8fd8\u662f\u5f88\u5feb\u7684\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5f88\u51c6\u65f6\uff0c\u521a\u521a\u5230\u4e24\u4e2a\u65f6\u8fb0\uff0c\u96f7\u65e0\u5f71\u5c31\u51fa\u73b0\u5728\u9ad8\u5e7d\u5170\u7684\u8eab\u524d\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u6765\u5f97\u5f88\u51c6\u65f6\u554a!\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5728\u4e0b\u8a00\u800c\u6709\u4fe1\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u597d!\u8bf4\u8bf4\u4f60\u5982\u6b64\u5b88\u4fe1\u7684\u7528\u5fc3\u5427?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u4e00\u5b9a\u8981\u8bf4\u5f97\u660e\u660e\u767d\u767d\u5417?\u5728\u4e0b\u5c31\u76f4\u8bf4\u4e86\uff0c\u5728\u4e0b\u7b49\u5019\u5434\u59d1\u5a18\u7684\u56de\u97f3\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5ffd\u7136\u7ad9\u4e86\u8fd1\u6765\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u7adf\u7136\u88ab\u5413\u5f97\u5411\u540e\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u7b49\u6211\u7b54\u590d\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5bf9!\u7b49\u4f60\u7684\u7b54\u590d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u5f88\u538c\u6076\u4f60\uff0c\u4e0d\u8fc7\u6211\u53ef\u4ee5\u4e3a\u4e86\u522b\u7684\u4eba\uff0c\u727a\u7272\u81ea\u5df1\uff0c\u6240\u4ee5\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4eec\u53ef\u4ee5\u8c08\u8c08\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4e9b\u4ec0\u4e48\u5429\u5490?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u60f3\u8868\u73b0\u4e00\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u8868\u73b0?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5bf9!\u5148\u4fee\u6b63\u4e00\u4e0b\u4f60\u7559\u7ed9\u6211\u7684\u5370\u8c61\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6709\u6761\u4ef6\uff0c\u60f3\u5f53\u7136\u77e3\uff0c\u59d1\u5a18\u8bf7\u5429\u5490\u4e0b\u6765\u5427!\u53ea\u8981\u5728\u4e0b\u80fd\u591f\u505a\u5230\uff0c\u7edd\u4e0d\u63a8\u8f9e\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u66ff\u6211\u653b\u4e0b\u70df\u9501\u697c\u6765\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u53ef\u4ee5\uff0c\u5728\u4e0b\u8d1f\u8d23\u653b\u4e0b\u5b83\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u662f\u73b0\u5728\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u59d1\u5a18\u80fd\u591f\u4f7f\u5728\u4e0b\u5fc3\u4e2d\u611f\u89c9\u5230\u4ec0\u4e48?\u624b\u4e2d\u6293\u5230\u4e00\u4e9b\u4ec0\u4e48\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u8981\u4ec0\u4e48?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u59d1\u5a18\u80fd\u7ed9\u6211\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u90a3\u662f\u8bf4\uff0c\u4f60\u4eec\u4e4b\u95f4\uff0c\u8fd8\u6ca1\u6709\u4ec0\u4e48\u7ed3\u8bba\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u662f\uff0c\u6211\u4eec\u5df2\u7ecf\u2026\u2026\u201d \u3000\u3000\u5df2\u7ecf\u600e\u4e48\u6837?\u5979\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u901a\uff0c\u8fd9\u7a76\u7adf\u662f\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u60f3\uff0c\u4f60\u5148\u628a\u70df\u9501\u697c\u6253\u5f00\u518d\u8bf4\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u6761\u4ef6\u592a\u82db\u523b\u4e00\u4e9b\u5427!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4e2a\u6837\u5b50\uff0c \u4f60\u613f\u610f\u7b54\u5e94\u5c31\u8c08\uff0c\u4e0d\u7b54\u5e94\uff0c\u54b1\u4eec\u5c31\u4e0d\u7528\u518d\u8c08\u4e0b\u53bb\u4e86\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u4e0d\u7528\u518d\u8c08\u4e86?\u59d1\u5a18\uff0c\u96be\u9053\u4e0d\u66ff\u5728\u4e0b\u60f3\u4e00\u60f3\u5417?\u201d \u3000\u3000\u5434\u53cc\u5973\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1b\u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u542c\u7740\uff0c\u6211\u5bf9\u4f60\u7684\u5370\u8c61\u5f88\u574f\uff0c\u4e5f\u5f88\u8ba8\u538c\u4f60\uff0c\u6240\u4ee5\uff0c\u6211\u80af\u7b54\u5e94\u4f60\uff0c\u53ea\u4e3a\u4e86\u4e00\u4e2a\u539f\u56e0\uff0c\u6551\u6211\u8fd9\u4e9b\u670b\u53cb\u2026\u2026\u201d \u3000\u3000\u96f7\u65e0\u5f71\u63a5\u9053\uff1a\u201c\u600e\u4e48?\u4ed6\u4eec\u8fde\u81ea\u4fdd\u7684\u80fd\u529b\u4e5f\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u51b7\u51b7\u63a5\u9053\uff1a \u201c\u4ed6\u4eec\u90fd\u8fd8\u6709\u5f88\u5f3a\u70c8\u7684\u6218\u6597\u529b\uff0c\u53ea\u4e0d\u8fc7\uff0c\u6211\u4e0d\u613f\u4f7f\u4ed6\u4eec\u518d\u53d7\u5230\u4f24\u5bb3\u3002 \u3000\u3000\u4e8b\uff0c\u544a\u8bc9\u4f60\u4f60\u4e5f\u4e0d\u61c2\uff0c\u4e00\u4e2a\u4eba\u53bb\u4e3a\u522b\u4eba\u727a\u7272\uff0c\u50cf\u4f60\u8fd9\u4e2a\u4eba\uff0c\u54ea\u91cc\u4f1a\u61c2\uff0c\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u6211\u4f1a\u61c2\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u5bf9\u5904\u7406\u4e8b\u4ef6\u7684\u65b9\u6cd5\uff0c\u53ef\u80fd\u4e0d\u540c\u3002\u201d \u9ad8\u5e7d\u5170\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u96f7\u5802\u4e3b\u6709\u4ec0\u4e48\u9ad8\u660e\u7684\u529e\u6cd5?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a \u201c\u4e8b\u5b9e\u5f88\u660e\u767d\uff0c\u8fd9\u91cc\uff0c\u9664\u4e86\u4f60\u4eec\u548c\u9ad8\u5929\u5065\u4e24\u80a1\u529b\u91cf\u4e4b\u5916\uff0c\u8fd8\u6709\u80a1\u5f88\u5f3a\u5927\u7684\u529b\u91cf\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u80a1\u529b\u91cf\u5c31\u662f\u4f60\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6211\u80fd\u8fd0\u7528\u548c\u6307\u6325\u90a3\u4e00\u80a1\u529b\u91cf\uff0c\u800c\u4e14\uff0c\u8fd9\u4e00\u80a1\u529b\u91cf\uff0c\u662f\u76ee\u4e0b\u552f\u4e00\u7684\u5f3a\u5927\u529b\u91cf\uff0c\u5982\u82e5\u6211\u5e2e\u52a9\u9ad8\u5929\u5065\uff0c\u6211\u60f3\uff0c\u8bf8\u4f4d\u5f88\u96be\u9003\u8fc7\u8fd9\u4e00\u573a\u751f\u6b7b\u4e4b\u52ab\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5a01\u80c1\u3002\u201d \u3000\u3000\u9ad8\u65e0\u5f71\u9053\uff1a\u201c\u628a\u8bdd\u8bf4\u660e\u4e86\uff0c\u6211\u4eec\u76f8\u5904\u7684\u5f62\u52bf\uff0c\u6211\u5360\u7edd\u5bf9\u7684\u4f18\u52bf\uff0c\u6240\u4ee5\uff0c\u8bf8\u4f4d\u5982\u82e5\u60f3\u548c\u5728\u4e0b\u8bf4\u4ec0\u4e48\u6761\u4ef6\uff0c\u5e94\u8be5\u5148\u4e86\u89e3\u81ea\u5df1\u7684\u5904\u5883\u624d\u597d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c\u4f60\u6562\u5f97\u5bf8\u8fdb\u5c3a\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5f62\u52bf\u6bd4\u4eba\u5f3a\uff0c\u59d1\u5a18\u5bf9\u8fd9\u4e00\u70b9\uff0c\u4e0d\u4f1a\u53cd\u5bf9\u5427?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u73b0\u5728\u53d1\u89c9\u4f60\u8fd9\u4e2a\u4eba\uff0c\u66f4\u8ba8\u538c\uff0c\u66f4\u53ef\u6076\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5927\u5c0f\u59d0\uff0c\u4f60\u5df2\u7ecf\u8bf4\u5f97\u5f88\u660e\u767d\uff0c\u4f60\u4e0d\u4f1a\u5bf9\u6211\u751f\u51fa\u4efb\u4f55\u4e00\u70b9\u611f\u60c5\uff0c\u5728\u4e0b\u4e5f\u660e\u767d\uff0c\u4e0d\u8bba\u6211\u96f7\u67d0\u4eba\u5949\u732e\u51fa\u4efb\u4f55\u5fe0\u8bda\uff0c\u4e5f\u4e0d\u4f1a\u4f7f\u59d1\u5a18\u52a8\u5fc3\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u54fc!\u4f60\u5012\u6709\u81ea\u77e5\u4e4b\u660e\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u5728\u4e0b\u53ea\u597d\u7528\u65b9\u6cd5\u5bf9\u4ed8\u9601\u4e0b\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u597d\u5427!\u54b1\u4eec\u5c31\u8c08\u8c08\u6761\u4ef6\u5427\uff01\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u597d!\u59d1\u5a18\u8bf4\u5427\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\uff0c\u5148\u653b\u4e0b\u70df\u9501\u697c\u2026\u2026\u201d \u3000\u3000\u96f7\u65e0\u5f71\u63a5\u9053\uff1a\u201c\u4ee5\u540e\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u8d70\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5927\u5c0f\u59d0\uff0c\u8fd9\u662f\u640f\u547d\u7684\u4e8b\uff0c\u6211\u5e0c\u671b\u660e\u786e\u7684\u7b54\u590d\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5df2\u7ecf\u5f88\u660e\u786e\u4e86\uff0c\u6211\u8ddf\u4f60\u8d70\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u4e0d\u591f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5df2\u7ecf\u88ab\u903c\u5f97\u65e0\u6cd5\u5fcd\u8010\u4e0b\u53bb\uff0c\u65e9\u5df2\u8981\u53d1\u4f5c\u51fa\u6765\uff0c\u4f46\u5979\u56de\u5934\u671b\u4e86\u9ad8\u5e7d\u5170\u4e00\u773c\uff0c\u53c8\u5f3a\u81ea\u5fcd\u4e0b\u53bb\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u4fdd\u7559\u4e0b\u5b9e\u529b\uff0c\u4e0d\u63d2\u624b\u8fd9\u573a\u7eb7\u4e89\uff0c\u4f60\u4eec\u53cc\u65b9\u7684\u4f24\u4ea1\u8d8a\u591a\uff0c\u5bf9\u6211\u8d8a\u662f\u6709\u5229\uff0c\u5728\u4e0b\u6240\u4ee5\u8d76\u6765\u63d2\u624b\u6b64\u4e8b\uff0c\u5b8c\u5168\u662f\u4e3a\u4e86\u59d1\u5a18\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u95ed\u76ee\u800c\u5750\u7684\u9ec4\u7075\uff0c\u7a81\u7136\u7ad9\u8d77\u5b50\u8eab\u5b50\uff0c\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u4e0d\u7528\u518d\u8feb\u5bb3\u5434\u59d1\u5a18\u4e86\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9ec4\u7075\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u4f60\u89c9\u7740\uff0c\u6211\u4eec\u5df2\u7ecf\u5b8c\u5168\u6ca1\u6709\u53cd\u6297\u7684\u80fd\u529b\u4e86\u3002\u201d \u96f7\u65e0\u5f71\u9053\uff1a\u201c\u9ec4\u7075\uff0c\u4f60\u72af\u4e86\u7b2c\u4e00\u4e2a\u9519\u8bef\uff0c\u662f\u4f4e\u4f30\u4e86\u6211\u96f7\u65e0\u5f71\uff0c\u7b2c\u4e8c\u4e2a\u9519\u8bef\u662f\uff0c\u5b8c\u5168\u4e0d\u4e86\u89e3\u795e\u5200\u5802\u7684\u5b9e\u529b\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c \u4f60\u8fd9\u4e2a\u4e58\u4eba\u4e4b\u5371\u7684\u4e0b\u6d41\u4eba\u7269\uff0c\u73b0\u5728\uff0c\u6211\u4eec\u4f1a\u7ed9\u4f60\u4e00\u4e2a\u673a\u4f1a\uff0c\u8ba9\u4f60\u8bc1\u5b9e\u4e00\u4e0b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u8bc1\u5b9e\u4ec0\u4e48?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8bc1\u5b9e\u4e00\u4e0b\u6211\u7684\u5b9e\u529b\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u671b\u671b\u70df\u9501\u697c\uff0c\u9053\uff1a \u201c\u5c0f\u4e0d\u5fcd\u5219\u4e71\u5927\u8c0b\uff0c \u4f60\u53ef\u77e5\u9053\uff0c\u4e00\u52a8\u4e0a\u624b\uff0c\u9ec4\u5821\u4e3b\u518d\u540e\u6094\u5c31\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u96f7\u65e0\u5f71\uff0c \u4f60\u662f\u5f88\u5351\u9119\u7684\u4eba\uff0c\u4f60\u8fd9\u79cd\u4eba\uff0c\u4e0d\u53ef\u4fe1\u4efb\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u9053\u4e49\u53ef\u8a00\uff0c\u5c31\u7b97\u5434\u59d1\u5a18\u613f\u610f\u5ac1\u7ed9\u4f60\uff0c\u4f60\u4e00\u9635\u4e0d\u4f1a\u653e\u8fc7\u6211\u4eec\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u54b1\u4eec\u975e\u6709\u4e00\u6218\u4e0d\u53ef\u4e86\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u5f88\u53ef\u60b2\u7684\u662f\uff0c \u4f60\u5df2\u7ecf\u9519\u8fc7\u4e86\u6740\u6b7b\u6211\u4eec\u7684\u673a\u4f1a\uff0c\u6211\u5df2\u7ecf\u6062\u590d\u4e86\u5927\u90e8\u5206\u7684\u4f53\u80fd\uff0c\u522b\u4eba\u4e5f\u662f\u4e00\u6837\uff0c\u6211\u4eec\u5e0c\u671b\u4f60\u770b\u5230\u9ad8\u5929\u5065\u7684\u4e0b\u573a\uff0c\u80fd\u6709\u6240\u8b66\u60d5\u6539\u8fc7\u81ea\u65b0\u3002\u60f3\u4e0d\u5230\u4f60\u6019\u6076\u4e0d\u609b\uff0c\u4f01\u56fe\u4e58\u4eba\u4e4b\u5371\uff0c\u50cf\u4f60\u8fd9\u79cd\u4eba\uff0c\u5b9e\u5728\u7559\u4f60\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a \u201c\u9ad8\u5929\u5065\u7684\u4e0b\u573a\uff0c\u8fd9\u8bdd\u4ec0\u4e48\u610f\u601d\uff0c\u5c31\u7b97\u9ad8\u5929\u5065\u53d7\u4e86\u4f24\uff0c\u4f46\u4ee5\u4ed6\u6536\u85cf\u7684\u7075\u4e39\u5999\u836f\u4e4b\u591a\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u5728\u4f60\u4eec\u4e4b\u4e0b\uff0c\u4f60\u4eec\u7684\u4f24\u52bf\u80fd\u597d\uff0c\u4ed6\u4e5f\u4f1a\u5f88\u5feb\u7684\u75ca\u6108\u3002\u201d \u3000\u3000\u9ec4\u7075\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u9ad8\u5929\u5065\u771f\u7684\u8fd8\u6d3b\u7740\u5417?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u600e\u4e48?\u96be\u9053\u4ed6\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u4ed6\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u6b7b\u4e86?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u96f7\u65e0\u5f71\uff0c\u4f60\u81ea\u5df1\u60f3\u4e00\u60f3\uff0c\u4f60\u80fd\u6bd4\u8fc7\u9ad8\u5929\u5065\u4e48?\u8fde\u9ad8\u5929\u5065\u90fd\u5931\u8d25\u4e86\uff0c\u4f55\u51b5\u662f\u4f60\u96f7\u65e0\u5f71\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u795e\u60c5\u8083\u7136\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5982\u82e5\u9ad8\u5929\u5065\u662f\u771f\u7684\u6b7b\u4e86\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u5c31\u66f4\u4e3a\u8feb\u6025\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5728\u4e0b\u548c\u8bf8\u4f4d\uff0c\u5fc5\u9700\u8981\u65e9\u4f5c\u4e00\u4e2a\u4e86\u65ad\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54e6!\u56e0\u4e3a\uff0c\u9ad8\u5929\u5065\u6b7b\u4e86\u3002\u201d \u3000\u3000\u96f7\u65e0\u5f71\u9053\uff1a\u201c\u5bf9!\u201d \u3000\u3000\u7a81\u7136\u56de\u8eab\u5411\u5916\u5954\u53bb\u3002 \u3000\u3000\u4eba\u5f71\u95ea\u52a8\uff0c\u4e24\u4e2a\u4eba\u5e76\u80a9\u800c\u7acb\uff0c\u62e6\u4f4f\u4e86\u4ed6\u7684\u53bb\u8def\u3002 \u3000\u3000\u662f\u4f55\u5bd2\u8863\u548c\u534e\u5999\u771f\u3002 \u3000\u3000\u534e\u5999\u771f\u4e00\u76f4\u5728\u95ed\u76ee\u517b\u4f24\uff0c\u4f46\u5979\u5229\u7528\u53cc\u65b9\u4ea4\u8c08\u7684\u65f6\u95f4\uff0c\u6084\u6084\u79fb\u52a8\u8eab\u5b50\uff0c\u7ed5\u5230\u4e86\u96f7\u65e0\u5f71\u7684\u8eab\u540e\u3002 \u3000\u3000\u540c\u65f6\uff0c\u8fd8\u6697\u4e2d\u62db\u547c\u4e86\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5df2\u7ecf\u4eae\u51fa\u4e86\u4e24\u67c4\u91d1\u5251\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u4f3c\u662f\u65e0\u5fc3\u604b\u6218\uff0c\u4e00\u8bed\u4e0d\u53d1\uff0c\u5bf9\u7740\u534e\u5999\u771f\u5288\u51fa\u4e00\u62f3\u3002 \u3000\u3000\u4ed6\u770b\u5230\u4e86\u534e\u5999\u771f\u7684\u4f24\u52bf\u5f88\u91cd\uff0c\u5e0c\u671b\u80fd\u4e00\u4e3e\u903c\u9000\u4e86\u534e\u5999\u771f\uff0c\u51b2\u5f00\u4e00\u6761\u53bb\u8def\u3002 \u3000\u3000\u4f46\u534e\u5999\u771f\u5f88\u52c7\u6562\uff0c\u4e0d\u4f46\u672a\u95ea\u907f\u96f7\u65e0\u5f71\u7684\u653b\u52bf\uff0c\u53cd\u800c\u4e00\u633a\uff0c\u786c\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u53f3\u624b\u4e2d\u5bd2\u5149\u95ea\u70c1\uff0c\u6267\u7740\u4e00\u67c4\u4e09\u68f1\u77ed\u5251\u3002 \u3000\u3000\u8fd9\u771f\u662f\u6c5f\u6e56\u4e0a\u5f88\u5c11\u89c1\u7684\u5175\u5203\u3002 \u3000\u3000\u4f3c\u5175\u5203\uff0c\u4e5f\u50cf\u6697\u5668\u3002 \u3000\u3000\u534e\u5999\u771f\u662f\u6c5f\u6e56\u4e0a\u5f88\u540d\u7684\u6697\u5668\u80fd\u624b\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u51fb\u51fa\u5feb\u901f\u7684\u4e00\u638c\uff0c\u6697\u85cf\u7740\u4e24\u4e2a\u53d8\u5316\uff0c\u534e\u5999\u771f\u5982\u82e5\u4e00\u95ea\u907f\uff0c\u96f7\u65e0\u5f71\u7acb\u523b\u53ef\u53d8\u638c\u52bf\u6a2a\u5207\uff0c\u5316\u4f5c\u6025\u62f3\u8ffd\u88ad\u3002 \u3000\u3000\u4f46\u534e\u5999\u771f\u65e2\u672a\u8ba9\u907f\uff0c\u4e5f\u672a\u9000\u5f00\uff0c\u5374\u662f\u4e00\u5251\u8fce\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u8fd9\u4e00\u638c\uff0c\u53ef\u4ee5\u51fb\u4e2d\u534e\u5999\u771f\uff0c\u4f46\u5374\u65e0\u6cd5\u907f\u5f00\u534e\u5999\u771f\u7684\u53cd\u51fb\u4e00\u5251\u3002 \u3000\u3000\u8fd9\u662f\u4e0d\u8981\u547d\u7684\u6253\u6cd5\uff0c\u540c\u5f52\u4e8e\u5c3d\u7684\u786c\u62fc\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5f88\u4e0d\u613f\u610f\u53d7\u5230\u4f24\u5bb3\uff0c\u6240\u4ee5\uff0c\u4e00\u5438\u6c14\uff0c\u9000\u540e\u4e94\u6b65\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7684\u53cc\u5251\uff0c\u5374\u5df2\u501f\u52bf\u653b\u4e0a\uff0c\u6025\u5982\u661f\u706b\u3002 \u3000\u3000\u53f6\u957f\u9752\u3001\u9ad8\u5e7d\u5170\u7f20\u4f4f\u4e86\u96f7\u65e0\u5f71\u7684\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5251\u52bf\u51cc\u5389\uff0c\u4e0d\u8db3\u5341\u62db\u5df2\u7ecf\u6740\u6b7b\u4e01\u5bf9\u624b\u3002 \u3000\u3000\u53f6\u957f\u9752\u8fde\u51fa\u8ffd\u9b42\u4e03\u5251\uff0c\u628a\u654c\u624b\u523a\u6b7b\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5df2\u88ab\u4f55\u5bd2\u8863\u3001\u534e\u5999\u771f\u7f20\u4f4f\u82e6\u6597\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u91d1\u5251\u65bd\u4e3a\uff0c\u63a5\u4e0b\u4e86\u96f7\u65e0\u5f71\u5927\u90e8\u5206\u53cd\u51fb\u7684\u62f3\u638c\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5f88\u6025\u4e8e\u8131\u8eab\uff0c\u4f46\u4f55\u5bd2\u8863\u7f20\u5f97\u592a\u7d27\uff0c\u7adf\u7136\u65e0\u6cd5\u6446\u8131\u3002 \u3000\u3000\u4e8c\u5341\u56de\u5408\u540e\uff0c\u9ad8\u5e7d\u5170\u3001\u5434\u53cc\u5973\u3001\u9ec4\u7075\uff0c\u90fd\u56f4\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5fc3\u4e2d\u5927\u6025\uff0c\u53e3\u4e2d\u8fde\u53d1\u957f\u5578\u3002 \u3000\u3000\u8fd9\u957f\u5578\u4e4b\u58f0\uff0c\u6709\u4e24\u4e2a\u4f5c\u7528\uff0c\u4e00\u4e2a\u662f\u6fc0\u8d77\u81ea\u5df1\u7684\u8c6a\u6c14\uff0c\u4e00\u4e2a\u62db\u8bf7\u63f4\u624b\u3002 \u3000\u3000\u9ec4\u7075\u54c8\u54c8\u4e00\u7b11\uff0c\u52a0\u5165\u6218\u5708\uff0c\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u5e0c\u671b\u5728\u4f60\u62db\u547c\u7684\u63f4\u624b\u672a\u5230\u4e4b\u524d\u5148\u6740\u4e86\u4f60\u3002\u201d \u624b\u4e2d\u957f\u5251\u6325\u52a8\uff0c\u653b\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u4ed6\u51fa\u5251\u7684\u795e\u60c5\u5341\u5206\u6f47\u6d12\uff0c\u6709\u7740\u6f2b\u4e0d\u7ecf\u610f\u7684\u611f\u89c9\u3002 \u3000\u3000\u4f46\u96f7\u65e0\u5f71\u5374\u611f\u53d7\u5230\u4e86\u5f3a\u5927\u7684\u538b\u529b\u3002 \u3000\u3000\u53ea\u89c9\u9ec4\u7075\u653b\u6765\u7684\u5251\u52bf\uff0c\u65e0\u4e00\u4e0d\u662f\u5fc5\u6551\u7684\u5730\u65b9\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u7684\u53cc\u5251\uff0c\u4e5f\u653b\u7684\u662f\u81f4\u547d\u7684\u6240\u5728\u3002 \u3000\u3000\u8fd9\u5c31\u95f9\u5f97\u96f7\u65e0\u5f71\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u5e78\u597d\uff0c\u534e\u5999\u771f\u9000\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u6b66\u529f\u867d\u7136\u5f88\u9ad8\u5f3a\uff0c\u4f46\u4ed6\u6bd4\u8d77\u9ad8\u5929\u5065\u8fd8\u5dee\u4e86\u5f88\u8fdc\u3002 \u3000\u3000\u5c31\u7b97\u9ec4\u7075\uff0c\u4e5f\u6bd4\u4ed6\u9ad8\u51fa\u4e00\u7b79\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u672a\u5fc5\u80fd\u80dc\u4ed6\uff0c\u4e5f\u548c\u4ed6\u76f8\u5dee\u4e0d\u591a\uff0c \u3000\u3000\u96f7\u65e0\u5f71\u53c8\u53d1\u51fa\u4e00\u58f0\u957f\u5578\u3002 \u3000\u3000\u4f46\u5374\u88ab\u9ec4\u7075\u4e00\u8fde\u4e09\u5251\uff0c\u8feb\u5f97\u4ed6\u8fde\u8fde\u9000\u540e\u3002 \u3000\u3000\u8fd9\u4e09\u5251\u653b\u7684\u5730\u65b9\u5f88\u5999\uff0c\u4e00\u76f4\u662f\u4ed6\u7684\u9f3b\u5c16\u548c\u5634\u5df4\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5df2\u7ecf\u65e0\u6687\u518d\u53d1\u51fa\u5578\u58f0\uff0c\u5168\u529b\u8fd0\u5251\uff0c\u5c01\u6321\u9ec4\u7075\u7684\u5251\u52bf\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u3001\u5434\u53cc\u5973\u3001\u5c1a\u5929\u4e49\u3001\u5c1a\u592b\u4eba\uff0c\u5df2\u4e0d\u5728\u573a\u4e2d\u3002 \u3000\u3000\u4e94\u4e08\u5916\uff0c\u4f20\u6765\u4e86\u5175\u5203\u76f8\u51fb\u7684\u58f0\u97f3\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u62db\u8bf7\u7684\u63f4\u624b\u8d76\u4e86\u6765\uff0c\u4f46\u5374\u88ab\u9ad8\u5e7d\u5170\u3001\u5434\u53cc\u5973\u6321\u4e8e\u4e94\u4e08\u4e4b\u5916\u3002 \u3000\u3000\u9ec4\u7075\u957f\u5251\u4e00\u7d27\uff0c\u8fde\u73af\u523a\u51fa\u4e09\u5251\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u5c01\u4f4f\u9ec4\u7075\u7684\u957f\u5251\uff0c\u4f46\u5f88\u4e0d\u5e78\uff0c\u4f55\u5bd2\u8863\u7684\u91d1\u5251\uff0c\u5374\u7f20\u4f4f\u4e86\u4ed6\u7684\u53cc\u8155\u3002 \u3000\u3000\u4ed6\u773c\u770b\u5230\u4e00\u5251\u523a\u5411\u54bd\u5589\uff0c\u4f46\u5374\u65e0\u6cd5\u95ea\u907f\u3002 \u3000\u3000\u8fd9\u4e00\u5251\uff0c\u4e0d\u7b97\u5f88\u5feb\uff0c\u4f46\u5374\u5f88\u8be1\u5f02\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u53eb\u51fa\u4e86\u4e00\u4e2a\u5b57\uff0c\u5251\u5c16\u5df2\u523a\u5165\u4e86\u54bd\u5589\u3002 \u3000\u3000\u4e5f\u8bb8\uff0c\u4ed6\u8fd8\u60f3\u8bf4\u4ec0\u4e48\uff0c\u53ef\u60dc\uff0c\u5374\u5df2\u65e0\u6cd5\u5f00\u53e3\u3002 \u3000\u3000\u5706\u7741\u53cc\u76ee\uff0c\u671b\u7740\u9ec4\u7075\u3002 \u3000\u3000\u9ec4\u7075\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u96f7\u65e0\u5f71\uff0c\u6211\u4eec\u7ed9\u4e86\u4f60\u591a\u5c11\u7684\u673a\u4f1a\uff0c\u4f46\u5374\u65e0\u6cd5\u4f7f\u4f60\u6539\u53d8\uff0c\u6240\u4ee5\uff0c\u4e0d\u5f97\u4e0d\u6740\u4f60\u3002\u201d \u3000\u3000\u7f13\u7f13\u62d4\u51fa\u4e86\u957f\u5251\u3002 \u3000\u3000\u4e00\u80a1\u8840\u7bad\uff0c\u55b7\u51fa\u4e86\u4e94\u516d\u5c3a\u8fdc\u3002 \u3000\u3000\u201c\u9501\u4e0a\u751f\u6b7b\u2026\u2026\u95e8\u2026\u2026\u201d \u3000\u3000\u96f7\u65e0\u5f71\u7528\u4e86\u6700\u540e\u4e00\u53e3\u6c14\uff0c\u8bf4\u51fa\u4e86\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u4ed6\u54bd\u5589\u4e2d\u5251\uff0c\u8bed\u6c14\u5df2\u542b\u7cca\u4e0d\u6e05\uff0c\u4f46\u4ed6\u8fd8\u662f\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u800c\u4e14\uff0c\u9ec4\u7075\u7b49\uff0c\u4e5f\u542c\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u3001\u5434\u53cc\u5973\uff0c\u65bd\u5c55\u51fa\u6740\u624b\u3002 \u3000\u3000\u4e03\u8272\u5251\u548c\u5929\u9b54\u6307\uff0c\u662f\u72ec\u6b65\u6c5f\u6e56\u7684\u7edd\u6280\u3002 \u3000\u3000\u4e5f\u662f\u51cc\u5389\u7684\u6740\u4eba\u624b\u6cd5\u3002 \u3000\u3000\u96f7\u65e0\u5f71\u62db\u6765\u4e86\u516b\u4e2a\u63f4\u624b\uff0c\u5982\u4e0b\u4e86\u56db\u5bf9\u3002 \u3000\u3000\u534e\u5999\u771f\u4e00\u76f4\u5728\u60f3\u96f7\u65e0\u5f71\u4e34\u6b7b\u524d\u7684\u4e00\u53e5\u8bdd\uff0c\u8fd9\u53e5\u8bdd\uff0c\u4e00\u5b9a\u6709\u5f88\u5927\u7684\u4f7f\u7528\uff0c\u4f46\u5979\u4e00\u76f4\u6ca1\u6709\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u9ec4\u7075\u3001\u4f55\u5bd2\u8863\uff0c\u90fd\u5728\u60f3\uff0c\u8fd9\u53e5\u8bdd\uff0c\u7a76\u6709\u4ec0\u5e7a\u610f\u601d\u3002 \u3000\u3000\u534e\u5999\u771f\u662f\u7ecf\u9a8c\u5f88\u5949\u5bcc\u7684\u4eba\uff0c\u5979\u660e\u767d\uff0c\u552f\u4e00\u53ef\u80fd\u77e5\u9053\u8fd9\u53e5\u8bdd\u5185\u60c5\u7684\u662f\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u9ad8\u5e7d\u5170\u56de\u6765\u7684\u65f6\u5019\uff0c\u534e\u5999\u771f\u7acb\u523b\u95ee\u9053\uff1a \u201c\u5e7d\u5170\u59d1\u5a18\uff0c\u7b2c\u4e00\u5821\uff0c\u662f\u4e0d\u662f\u6709\u4e00\u5ea7\u751f\u6b7b\u95e8\uff1f\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u751f\u6b7b\u95e8\uff0c\u662f\u4e0d\u662f\u5728\u795e\u5200\u5802\u4e2d\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\u3002 \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u96f7\u65e0\u5f71\u6b7b\u524d\u8bf4\u4e86\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u8bf4\u4ec0\u4e48?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u9501\u4e0a\u751f\u6b7b\u95e8\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u9501\u4e0a\u751f\u6b7b\u95e8\uff0c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u54b1\u4eec\u8981\u8bf7\u6559\u59d1\u5a18\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u5434\u53cc\u5973\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a \u201c\u5e7d\u5170\uff0c\u6211\u597d\u50cf\u5c71\u542c\u8bf4\u8fc7\u751f\u6b7b\u95e8\uff0c\u4f46\u662f\u542c\u8fc7\u5c31\u7b97\u4e86\uff0c\u5374\u4e0d\u77e5\u9053\u5b83\u8be6\u7ec6\u7684\u5185\u60c5?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5927\u59ca\uff0c\u6211\u6bd4\u4f60\u77e5\u9053\u7684\u591a\u4e00\u4e9b\uff0c\u6211\u77e5\u9053\u751f\u6b7b\u95e8\uff0c\u662f\u795e\u5200\u5802\u4e2d\u4e00\u4e2a\u5730\u4e0b\u5bc6\u5ba4\u7684\u95e8\u6237\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u53bb\u8fc7\u751f\u6b7b\u95e8\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5e7d\u5170\u59d1\u5a18\uff0c\u4e0d\u7ba1\u96f7\u65e0\u5f71\u8fd9\u53e5\u8bdd\uff0c\u662f\u4e0d\u662f\u9634\u8c0b\u8be1\u8ba1\uff0c\u4f46\u6211\u89c9\u7740\uff0c\u6211\u4eec\u90fd\u4e0d\u5e94\u8be5\u5ffd\u89c6\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5821\u4e3b\u7684\u610f\u601d\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u70df\u9501\u697c\u7684\u4e8b\uff0c \u5df2\u8fd1\u5c3e\u58f0\uff0c\u5c31\u7b97\u9ad8\u5929\u5065\u8fd8\u6709\u6700\u540e\u4e00\u8ba1\uff0c\u4e5f\u662f\u5728\u70df\u9501\u697c\u4e0a\u8bbe\u4e0b\u57cb\u4f0f\uff0c\u54b1\u4eec\u4e0d\u653b\u5165\u70df\u9501\u697c\uff0c\u90a3\u57cb\u4f0f\u5c31\u6ca1\u6709\u7528\uff0c\u6240\u4ee5\uff0c\u6211\u60f3\uff0c \u53ea\u8981\u7559\u4e0b\u51e0\u4e2a\u80fd\u591f\u5bf9\u4ed8\u5343\u9488\u4e07\u8292\u548c\u8d3e\u4f3c\u9053\u7684\u4eba\uff0c\u5e94\u4ed8\u4ed6\u4eec\u7684\u7a81\u88ad\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5821\u4e3b\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u8fdb\u5165\u795e\u5200\u5802\u53bb\u77a7\u77a7\uff0c\u770b\u770b\u91cc\u9762\u6709\u6ca1\u6709\u4e00\u5ea7\u751f\u6b7b\u95e8\uff0c\u771f\u6709\u90a3\u5ea7\u95e8\uff0c\u5c31\u628a\u5b83\u9501\u8d77\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u82e5\u6709\u6240\u6307\uff0c\u77a7\u77a7\u4e5f\u597d\uff0c\u4f46\u4ec0\u4e48\u4eba\u53bb\uff0c\u4ec0\u4e48\u4eba\u7559\u4e0b\u6765\uff0c\u8981\u5821\u4e3b\u5206\u914d\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5e7d\u5170\u59d1\u5a18\u548c\u5434\u59d1\u5a18\uff0c\u662f\u4e00\u5b9a\u8981\u53bb\u7684\u4eba\uff0c\u56e0\u4e3a\uff0c\u795e\u5200\u5802\u7684\u5371\u9669\uff0c\u8981\u8d85\u8fc7\u6b64\u5730\u5341\u500d\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4e5f\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4ed6\u660e\u767d\u4f55\u5bd2\u8863\u7684\u610f\u601d\uff0c\u5982\u82e5\u5434\u53cc\u5973\u548c\u9ad8\u5e7d\u5170\u90fd\u53d1\u751f\u5371\u9669\uff0c\u4f55\u5bd2\u8863\u5df2\u8868\u8fbe\u51fa\u4e86\u4e0d\u613f\u72ec\u751f\u7684\u60c5\u610f\u3002 \u3000\u3000\u4e8c\u5973\u76f8\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u90fd\u89c9\u7740\u5fc3\u5934\u6709\u4e9b\u751c\u751c\u7684\u611f\u89c9\u3002 \u3000\u3000\u9ec4\u7075\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u6211\u2026\u2026\u201d \u3000\u3000\u7384\u5a18\u63a5\u9053\uff1a\u201c\u6211\u4e5f\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u65ad\u81c2\u4e4b\u4f24\uff0c\u867d\u53ef\u5fcd\u53d7\uff0c\u4f46\u4e00\u65e6\u52a8\u624b\u65f6\uff0c\u5374\u6709\u7740\u4e0d\u4fbf\u4e4b\u611f\uff0c\u4f60\u8fd8\u662f\u7559\u5728\u8fd9\u91cc\u5427\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u6211\u770b\u7531\u5728\u4e0b\u966a\u4f55\u5144\u53bb\u5427!\u6b64\u5730\u8fd8\u8981\u5821\u4e3b\u4e3b\u6301\u5927\u5c40\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u6211\u4e00\u5b9a\u5f97\u53bb\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u548c\u5821\u4e3b\u540c\u53bb\uff0c\u53f6\u5144\u7559\u4e0b\u6765\uff0c\u5bf9\u4ed8\u8d3e\u4f3c\u9053\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u4f24\u52bf\u5f88\u91cd\uff0c\u795e\u5200\u5802\u5185\uff0c\u53ef\u80fd\u4f1a\u6709\u4e00\u573a\u2026\u2026\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u53ef\u80fd\u6709\u4e00\u573a\u751f\u6b7b\u4e4b\u6218\uff0c\u4f46\u8fd9\u91cc\u4e5f\u4e00\u6837\u5371\u9669\uff0c\u5343\u9488\u4e07\u8292\u3001\u8d3e\u4f3c\u9053\uff0c\u90fd\u662f\u4e0d\u597d\u5bf9\u4ed8\u7684\u4eba\uff0c\u5434\u59d1\u5a18\u3001\u5e7d\u5170\u59d1\u5a18\u90fd\u53bb\u4e86\uff0c\u8fd9\u91cc\u624d\u9700\u8981\u51e0\u4e2a\u52c7\u731b\u5584\u6218\u7684\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u53f6\u5251\u4f7f\u5c31\u7559\u4e0b\u6765\u5427\uff01\u201d \u3000\u3000\u53f6\u957f\u9752\u4e00\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u9075\u547d\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u534e\u59d1\u5a18\uff0c\u4f24\u52bf\u5982\u4f55?\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u5434\u59d1\u5a18\u7684\u4f24\u836f\u5f88\u597d\uff0c\u6211\u5df2\u7ecf\u597d\u591a\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u54b1\u4eec\u8fd9\u5c31\u53bb\u5427\uff01\u201d \u3000\u3000\u56de\u987e\u53f6\u957f\u9752\u548c\u4e07\u80dc\u4e00\u773c\uff0c\u63a5\u9053\uff1a \u201c\u6211\u4eec\u4f1a\u5c3d\u5feb\u56de\u6765\uff0c\u5728\u6211\u4eec\u6ca1\u6709\u56de\u6765\u4ee5\u524d\uff0c\u4e0d\u8981\u8fdb\u5165\u70df\u9501\u697c\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u4eec\u9075\u547d\u884c\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5f53\u5148\u5e26\u8def\uff0c\u8fdb\u5165\u4e86\u795e\u5200\u5802\u3002 \u3000\u3000\u795e\u5200\u5802\u5360\u5730\u5f88\u5e7f\u5927\u3002 \u3000\u3000\u9664\u4e86\u5730\u9762\u4e0a\uff0c\u76d6\u5f97\u623f\u5b50\u4e4b\u5916\uff0c\u8fd9\u91cc\u6709\u5f88\u591a\u5904\u5730\u4e0b\u5bc6\u5ba4\u3002 \u3000\u3000\u751f\u6b7b\u95e8\uff0c\u795e\u5200\u5802\u6700\u540e\u4e00\u5ea7\u9662\u843d\u91cc\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u9ad8\u5e7d\u5170\u8fd9\u91cc\u5f88\u719f\u6089\uff0c\u6240\u4ee5\uff0c\u4ed6\u4eec\u5f88\u5feb\u7684\u627e\u5230\u4e86\u90a3\u91cc\u3002 \u3000\u3000\u4e09\u4e2a\u767d\u8272\u7684\u5b57\uff0c\u5199\u7740\u751f\u6b7b\u95e8\u3002 \u3000\u3000\u9ed1\u8272\u7684\u5899\u58c1\uff0c\u9ed1\u8272\u7684\u95e8\uff0c\u4e24\u6247\u9ed1\u8272\u7684\u5927\u95e8\u5f00\u7740\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u5ea7\u5730\u4e0b\u5bc6\u5ba4\u3002 \u3000\u3000\u5916\u9762\u9ed1\uff0c\u91cc\u9762\u66f4\u9ed1\u3002 \u3000\u3000\u4f3c\u4e4e\u662f\u6574\u5ea7\u7684\u5730\u4e0b\u5ba4\uff0c\u90fd\u7528\u9ed1\u8272\u7684\u77f3\u677f\u505a\u6210\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u8fd9\u4e9b\u9ed1\uff0c\u9ed1\u7684\u6709\u4e9b\u6050\u6016\uff0c\u6709\u4e9b\u53ef\u60b2\uff0c\u8fde\u9ec4\u7075\u3001\u4f55\u5bd2\u8863\u533a\u7b49\u9ad8\u624b\uff0c\u90fd\u6709\u7740\u4e0d\u6562\u76f4\u884c\u800c\u5165\u7684\u611f\u89c9\uff0c\u4f46\u4ed6\u4eec\u8fd8\u662f\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u548c\u9ec4\u7075\uff0c\u5e26\u4e0a\u4e86\u4e24\u6247\u94c1\u95e8\u3002 \u3000\u3000\u9501\u95e8\u7684\u5927\u94c1\u9501\uff0c\u5c31\u6302\u5728\u95e8\u4e0a\u3002 \u3000\u3000\u9501\u4e0a\u4e86\u751f\u6b7b\u95e8\uff0c\u6bcf\u4e2a\u4eba\u90fd\u653e\u4e0b\u4e86\u5fc3\u4e2d\u4e00\u5757\u77f3\u5934\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5ffd\u7136\u4f38\u624b\u62b9\u53bb\u4e86\u5934\u4e0a\u4e00\u62b9\u51b7\u6c57\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u60f3\u4e00\u60f3\uff0c\u8fd9\u5730\u65b9\uff0c\u5e76\u6ca1\u6709\u4ec0\u4e48\u53ef\u6015\u7684\uff0c\u4f46\u4e3a\u4ec0\u4e48\u5374\u7ed9\u4eba\u4e00\u79cd\u83ab\u540d\u7684\u7d27\u5f20\u611f\u89c9\u3002\u201d \u3000\u3000\u9ec4\u7075\u8083\u7136\u8bf4\u9053\uff1a \u201c\u8fd9\u6247\u94c1\u95e8\uff0c\u5728\u6ca1\u6709\u5173\u8d77\u4e4b\u524d\uff0c\u91cc\u9762\u900f\u51fa\u4e86\u4e00\u80a1\u6d53\u91cd\u7684\u6740\u6c14\uff0c\u90a3\u624d\u662f\u6211\u4eec\u754f\u60e7\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u54e6!\u8fd9\u5c31\u5bf9\u4e86\uff0c\u5728\u4e0b\u4e5f\u4e00\u76f4\u611f\u89c9\u6709\u4e00\u80a1\u5947\u602a\u7684\u538b\u529b\uff0c\u8fd8\u662f\u5821\u4e3b\u4e00\u8bed\u9053\u7834\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u90a3\u91cc\u9762\u7a76\u7adf\u662f\u4ec0\u4e48?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6740\u624b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6740\u624b\u4e5f\u662f\u4eba\u4e86?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u50cf\u90a3\u7b49\u9ed1\u6697\u7684\u5730\u65b9\uff0c\u4f4f\u5728\u90a3\u91cc\u7684\u4eba\uff0c \u81ea\u7136\u4e0d\u4f1a\u53ea\u662f\u4e00\u822c\u7684\u4eba\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u89c1\u591a\u8bc6\u5e7f\uff0c\u96be\u9053\u8fd9\u4e16\u4e0a\u771f\u6709\u548c\u6211\u4eec\u4e0d\u540c\u7684\u4eba\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5e7d\u5170\u59d1\u5a18\uff0c\u5c31\u7b97\u4ed6\u4eec\u662f\u548c\u6211\u4eec\u4e00\u6837\u7684\u4eba\uff0c\u4f46\u4f4f\u5728\u8fd9\u5730\u65b9\u592a\u4e45\u4e86\uff0c\u4e5f\u4f1a\u6709\u4e9b\u6539\u53d8\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6539\u53d8\uff0c\u90a3\u4e5f\u53ea\u662f\u4e00\u79cd\u6b66\u529f\u7684\u6210\u5c31\uff0c\u96be\u9053\uff0c\u4eba\u8fd8\u4f1a\u53d8\u4e0d\u6210\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4eba\u4e5f\u4f1a\u53d8\uff0c\u4f4f\u5728\u90a3\u6837\u7684\u9ed1\u6697\u4e2d\uff0c\u8981\u5982\u4f55\u53bb\u9002\u5e94\uff0c \u80fd\u9002\u5e94\uff0c\u624d\u80fd\u6709\u6210\u5c31\uff0c\u5c31\u521a\u624d\u6211\u4eec\u611f\u53d7\u5230\u6740\u6c14\u7684\u51c4\u70c8\uff0c\u90a3\u91cc\u4f4f\u7684\u4eba\uff0c\u90fd\u4e00\u5b9a\u4f1a\u6709\u6210\u5c31\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\uff0c\u54b1\u4eec\u5e94\u4e0d\u5e94\u8be5\u4e0b\u53bb\u770b\u770b\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5e94\u8be5\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u662f\u73b0\u5728!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u626b\u5e73\u4e86\u70df\u9501\u697c\uff0c\u54b1\u4eec\u517b\u597d\u4e86\u4f24\u540e\uff0c\u518d\u8fdb\u751f\u6b7b\u95e8\uff0c\u63ed\u5f00\u8fd9\u4e2a\u79d8\u5bc6\u3002\u201d \u534e\u5999\u771f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\uff0c\u8fd9\u7b2c\u4e00\u5821\u4e2d\uff0c\u7adf\u6709\u7740\u5982\u6b64\u591a\u7684\u53e4\u602a\uff0c\u804b\u5b50\u5251\u624b\uff0c\u795e\u5200\u5802\uff0c\u7adf\u7136\u8fd8\u6709\u8fd9\u4e48\u4e00\u4e2a\u751f\u6b7b\u95e8\uff0c\u5b9e\u5728\u6709\u4e9b\u53eb\u4eba\u60f3\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u9ec4\u7075\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u4e2d\uff0c\u5982\u82e5\u6ca1\u6709\u8fd9\u4e48\u591a\u82b1\u5de7\uff0c\u4e5f\u8bb8\u9ad8\u5929\u5065\u8fd8\u4e0d\u4f1a\u5984\u52a8\u9738\u4e3b\u6c5f\u6e56\u4e4b\u5ff5\u3002\u201d \u3000\u3000\u76ee\u5149\u4e00\u63a0\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u91cc\u8fd8\u6709\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u795e\u5200\u5802\u6709\u4eba?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5947\u602a\u7684\u662f\uff0c\u4ed6\u4eec\u4e3a\u4f55\u4e0d\u5411\u6211\u4eec\u653b\u88ad?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u795e\u5200\u5802\u672c\u6765\u5c31\u662f\u4e2a\u5145\u6ee1\u8be1\u5f02\u3001\u795e\u79d8\u7684\u5730\u65b9\uff0c\u5bf9\u8fd9\u4e2a\u5730\u65b9\uff0c\u6211\u77e5\u9053\u7684\u4e0d\u591a\u3002\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u50cf\u662f\u653e\u7f6e\u5b9d\u5200\u3001\u5b9d\u5251\u7684\u5b9d\u5e93\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u534e\u5999\u771f\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\u950b\u5229\u7684\u5b9d\u5200\uff0c\u5b83\u4e5f\u4e0d\u4f1a\u81ea\u5df1\u53bb\u6740\u4eba\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u82e5\u6709\u6240\u609f\u5730\u9053\uff1a\u201c\u9ad8\u5929\u5065\u4e0d\u4f1a\u771f\u7684\u53bb\u76f8\u4fe1\u4efb\u4f55\u4e00\u4e2a\u4eba\uff0c\u5c31\u8fde\u96f7\u65e0\u5f71\uff0c\u4e5f\u662f\u4e0d\u80af\u76f8\u4fe1\uff0c\u96f7\u65e0\u5f71\u867d\u7136\u662f\u795e\u5200\u5802\u4e3b\uff0c\u4f46\u4ed6\u5bf9\u6b64\u5730\u7684\u5185\u60c5\uff0c\u5e76\u4e0d\u80fd\u5b8c\u5168\u638c\u63e1\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6240\u4ee5\uff0c\u96f7\u65e0\u5f71\u867d\u7136\u65e9\u6709\u80cc\u53db\u4e4b\u5fc3\uff0c\u4f46\u5374\u4e00\u76f4\u4e0d\u6562\u4ed8\u8bf8\u884c\u52a8\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a \u201c\u96f7\u65e0\u5f71\u771f\u6b63\u80fd\u591f\u638c\u63e1\u7684\uff0c \u53ea\u662f\u8fd9\u91cc\u90a3\u4e9b\u5200\u624b\uff0c\u4f46\u795e\u5200\u5802\u771f\u6b63\u7684\u4e3b\u529b\uff0c\u5374\u4e0d\u662f\u4ed6\u4eec\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u771f\u6b63\u7684\u4e3b\u529b\uff0c\u85cf\u5728\u751f\u6b7b\u95e8\u5185\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u4e3a\u4ec0\u4e48\u8fd9\u91cc\u7684\u4eba\u4e0d\u5411\u6211\u4eec\u653b\u88ad?\u6211\u4eec\u4e0d\u662f\u9ad8\u5929\u5065\uff0c\u4e5f\u4e0d\u662f\u96f7\u65e0\u5f71\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u56e0\u4e3a\u9ad8\u5929\u5065\u6b7b\u4e86\uff0c \u5df2\u7ecf\u6ca1\u6709\u4eba\uff0e\u518d\u5411\u4ed6\u4eec\u4e0b\u8fbe\u653b\u88ad\u7684\u4ee4\u8c15\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6700\u91cd\u8981\u7684\u662f\uff0c\u4ed6\u4eec\u8fd8\u8ba4\u8bc6\u4f60\u662f\u4e8c\u5c0f\u59d0\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u4eec\u8bb0\u5fc6\u5f88\u7b80\u5355\uff0c\u8bb0\u7684\u4e8b\u60c5\uff0c\u4e0d\u7b85\u592a\u591a\uff0c\u6240\u4ee5\uff0c\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u9ec4\u7075\u8f7b\u5401\u4e00\u53e3\u6c14\u9053\uff1a \u201c\u795e\u5200\u5802\uff0c\u8fd8\u6709\u5f88\u591a\u7684\u4eba\uff0c\u4f46\u5374\u8eb2\u4e86\u8d77\u6765\u3002\u201d \u534e\u5999\u771f\u9053\uff1a \u201c\u7b2c\u4e00\u5821\u5982\u6b64\u4e4b\u5927\uff0c\u4ed6\u4eec\u771f\u8981\u8eb2\u4e86\u8d77\u6765\uff0c\u8fd8\u771f\u7684\u4e0d\u592a\u5bb9\u6613\u627e\u5230\u4ed6\u4eec\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4eec\u8d70\u5427!\u9ad8\u5929\u5065\u6b7b\u4e86\uff0c\u96f7\u65e0\u5f71\u6b7b\u4e86\uff0c\u8fd9\u4e9b\u4eba\uff0c\u5df2\u7ecf\u4e0d\u8db3\u4e3a\u60a3\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bf9!\u6ca1\u6709\u7528\u7684\u5b9d\u5200\uff0c\u6c38\u8fdc\u4e0d\u4f1a\u4f24\u4eba\u3002\u201d \u3000\u3000\u51e0\u4eba\u5306\u5306\u79bb\u53bb\u3002\u4f55\u5bd2\u8863\u6697\u4e2d\u7559\u5fc3\uff0c\u786e\u7136\u53d1\u73b0\u4e00\u4e9b\u4eba\uff0c\u8fd9\u4e9b\u4eba\u4e0d\u662f\u5446\u5446\u7684\u5750\u7740\uff0c\u5c31\u662f\u9759\u9759\u7684\u7ad9\u7740\uff0c\u5bf9\u4e8e\u9ec4\u7075\u7b49\u4e00\u884c\uff0c\u4f3c\u4e4e\u662f\u6ca1\u6709\u770b\u5230\u4e00\u6837\uff0c\u6ca1\u6709\u4eba\u95ee\uff0c\u4e5f\u6ca1\u6709\u4eba\u963b\u62e6\uff1a\u4f46\u4ed6\u4eec\u7684\u76ee\u5149\uff0c\u5374\u90fd\u6295\u6ce8\u5728\u9ad8\u5e7d\u5170\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u5434\u53cc\u5973\u867d\u7136\u4e5f\u5728\u7b2c\u4e00\u5821\u4f4f\u4e86\u5f88\u4e45\uff0c\u4f46\u5979\u5374\u5f88\u5c11\u7ba1\u5821\u5185\u7684\u4e8b\u7269\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5c31\u4e0d\u540c\u4e86\uff0c\u5979\u662f\u9ad8\u5929\u5065\u8ba4\u4e3a\u7684\u771f\u6b63\u5fc3\u8179\uff0c\u4e5f\u4e00\u76f4\u4f2a\u88c5\u51fa\u4e00\u4e2a\u6148\u7231\u7684\u7236\u4eb2\uff0c\u4ed6\u5e38\u5e38\u5916\u51fa\uff0c\u79bb\u5f00\u7b2c\u4e00\u5821\uff0c\u9ad8\u5e7d\u5170\u5c31\u4ee3\u4ed6\u638c\u7406\u4e86\u5821\u4e2d\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u5979\u5e38\u6765\u795e\u5200\u5802\uff0c\u6240\u4ee5\uff0c\u5821\u4e2d\u7684\u4eba\uff0c\u5927\u90fd\u8ba4\u8bc6\u5979\u3002 \u3000\u3000\u4f46\u9ad8\u5e7d\u5170\u4e5f\u5e76\u672a\u5b8c\u5168\u4e86\u89e3\u7b2c\u4e00\u5821\u4e2d\u6240\u6709\u7684\u4e00\u5207\u3002 \u3000\u3000\u5979\u662f\u4e2a\u806a\u660e\u7684\u5973\u5b69\u5b50\uff0c\u5979\u5f00\u59cb\u601d\u7d22\u8fd9\u7b2c\u4e00\u5821\u4e2d\u6240\u6709\u7684\u4e8b\u52a1\uff0c\u9690\u79d8\u3002 \u3000\u3000\u8fd9\u4e9b\u6f5c\u85cf\u7684\u8be1\u5947\u9690\u79d8\uff0c\u5fc5\u987b\u8981\u5f7b\u5e95\u627e\u51fa\u6765\u2026\u2026 \u3000\u3000\u4e00\u884c\u4eba\uff0c\u56de\u5230\u70df\u9501\u697c\u5916\u3002 \u3000\u3000\u53f6\u957f\u9752\u3001\u5c1a\u5929\u4e49\u592b\u59bb\uff0c\u4e07\u80dc\u7b49\uff0c\u90fd\u4e00\u76f4\u4fdd\u6301\u9ad8\u5ea6\u7684\u8b66\u89c9\uff0c\u5b88\u5728\u70df\u9501\u697c\u5916\u3002 \u3000\u3000\u76ee\u7779\u9ec4\u7075\u7b49\u4e00\u884c\u65e0\u6059\uff0c\u624d\u6697\u6697\u7684\u677e\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u9ec4\u7075\u4f4e\u58f0\u9053\uff1a\u201c\u53ef\u6709\u4ec0\u4e48\u884c\u52a8?\u201d \u3000\u3000\u53f6\u957f\u9752\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u592a\u9759\u4e86\uff0c\u9759\u7684\u6709\u4e9b\u53eb\u4eba\u4e0d\u5b89\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u5c1a\u5929\u4e49\u9053\uff1a\u201c\u8001\u592b\u4e5f\u89c9\u7740\u6709\u4e9b\u5947\u602a\uff1b\u5927\u534a\u4e2a\u65f6\u8fb0\u4e86\uff0c\u6ca1\u6709\u4eba\u4f38\u5934\u5411\u5916\u9762\u77a7\u4e00\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff0c\u8bf8\u4f4d\u6ca1\u6709\u8fdb\u53bb\u77a7\u77a7\u5417?\u201d \u3000\u3000\u4e07\u80dc\u6447\u6447\u5934\uff0c\u9053\uff1a \u201c\u6ca1\u6709\uff0c\u4e0d\u8fc7\uff0c\u53f6\u5144\u65e9\u5c31\u60f3\u8fdb\u53bb\u770b\u770b\uff0c\u4f46\u5c1a\u8001\u524d\u8f88\u529b\u4e3b\u614e\u91cd\uff0c\u7b49\u5821\u4e3b\u56de\u6765\u518d\u4f5c\u51b3\u5b9a\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u597d!\u54b1\u4eec\u53ef\u4ee5\u8fdb\u53bb\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8fdb\u5c31\u8fdb\uff0c\u4e3e\u6b65\u5411\u524d\u884c\u8d70\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6162\u4e00\u70b9\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u505c\u4e86\u4e0b\u6765\uff0c\u9053\uff1a \u201c\u4ed6\u4eec\u4e0d\u80af\u51fa\u6765\uff0c\u54b1\u4eec\u53ea\u6709\u8fdb\u53bb\u77a7\u77a7\u4e86\u3002\u201d \u5979\u6545\u610f\u628a\u58f0\u97f3\u63d0\u5f97\u5f88\u9ad8\u3002 \u3000\u3000\u4f46\u70df\u9501\u697c\u4e0a\uff0c\u4ecd\u4e0d\u89c1\u52a8\u9759\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u91cc\u9762\u662f\u4e0d\u662f\u53d1\u751f\u4e86\u4ec0\u4e48\u53d8\u6545\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u5927\u6c34\u6df9\u8fc7\uff0c\u5c31\u7b97\u9ad8\u5929\u5065\u5728\u8fd9\u91cc\u7b51\u6709\u5730\u9053\u53ea\u6015\u4e5f\u4e0d\u80fd\u7acb\u523b\u4f7f\u7528\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u5728\u4e0b\u8fdb\u53bb\u77a7\u77a7\u5427!\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u6302\u5ff5\u7740\u7389\u7fe0\u3001\u5170\u767d\u7684\u5b89\u5371\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u5e7d\u5170\u59d1\u5a18\u548c\u53f6\u5144\u5f00\u9053\u3002\u201d \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\uff0c\u4f20\u51fa\u6765\u4e86\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u8fdb\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u968f\u7740\u90a3\u8bf4\u8bdd\u4e4b\u58f0\uff0c\u7f13\u6b65\u884c\u51fa\u4e86\u4e00\u4e2a\u5168\u8eab\u7eff\u8863\u7684\u5c11\u5973\u3002 \u3000\u3000\u662f\u5170\u767d\u3002 \u3000\u3000\u53f6\u957f\u9752\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u5170\u767d\u3002\u201d \u3000\u3000\u5170\u767d\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u59ca\u59ca\u5728\u9152\u996d\u4e2d\u4e0b\u4e86\u6bd2\uff0c\u4f46\u5deb\u5a46\u5a46\u4e2d\u6bd2\u540e\u56de\u8eab\u53cd\u51fb\uff0c\u91cd\u4f24\u59ca\u59ca\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7389\u7fe0\u5979\u4f24\u52bf\u5982\u4f55?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5f88\u91cd\uff0c\u53ea\u4f59\u4e0b\u4e00\u53e3\u6c14\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u4e3e\u6b65\u5411\u524d\u51b2\u53bb\u3002\u5170\u767d\u4e00\u4f38\u624b\uff0c\u62e6\u4f4f\u4e86\u53f6\u957f\u9752\uff0c\u9053\uff1a\u201c\u6211\u7ed9\u5979\u670d\u4e0b\u4e86\u4e00\u4e9b\u8ff7\u795e\u836f\u7269\uff0c\u73b0\u5728\u8fd8\u672a\u6e05\u9192\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6025\u9053\uff1a \u201c\u4e22\u4e0b\u5979\u4e00\u4e2a\u4eba\u5728\u70df\u9501\u697c\u4e0a\uff0c \u5c82\u4e0d\u5341\u5206\u5371\u9669\u4e3a\u4ec0\u4e48\u4e0d\u62b1\u5979\u4e0b\u6765?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a \u201c\u9664\u4e86\u6211\u4e4b\u5916\uff0c\u70df\u9501\u697c\u4e0a\uff0c \u5df2\u7ecf\u6ca1\u6709\u53ef\u4ee5\u884c\u52a8\u7684\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u628a\u4ed6\u4eec\u5168\u6bd2\u6b7b\u4e86?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u6211\u7528\u7684\u662f\u8ff7\u9b42\u836f\u7269\uff0c\u4e0d\u8fc7\uff0c\u836f\u6027\u5f88\u5f3a\uff0c\u4e24\u4e2a\u65f6\u4e4b\u5185\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u9192\u8fc7\u6765\u2026\u2026\u201d \u3000\u3000\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u697c\u4e0a\u7684\u673a\u5173\u5e03\u7f6e\uff0c\u5f88\u5389\u5bb3\uff0c\u4f46\u6700\u53ef\u7684\u662f\uff0c\u8fd9\u5ea7\u697c\u4f1a\u7206\u6210\u98de\u7070\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u57cb\u4e86\u70b8\u836f?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u8be6\u60c5\uff0c\u59ca\u59ca\u624d\u660e\u767d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u53f6\u5144\u5f1f\uff0c\u5feb\u4e0a\u53bb\uff0c\u628a\u7389\u7fe0\u59d1\u5a18\u62b1\u4e0b\u6765\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u653e\u817f\u5411\u4e0a\u5954\u53bb\u3002 \u3000\u3000\u4f46\u53c8\u88ab\u5170\u767d\u62e6\u963b\uff0c\u9053\uff1a \u201c\u6211\u770b\u7740\u59ca\u59ca\u53ea\u4f59\u4e0b\u4e00\u53e3\u6c14\u4e86\uff0c\u5982\u82e5\u662f\u80fd\u628a\u5979\u62b1\u4e0b\u6765\uff0c\u8fd9\u70df\u9501\u697c\u4e0a\uff0c \u5df2\u7ecf\u6ca1\u6709\u80fd\u62e6\u4f4f\u6211\u7684\u4eba\u4e86\uff0c\u6211\u65e9\u628a\u5979\u62b1\u4e0b\u6765\u4e86\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u7389\u7fe0\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u5979\u670d\u4e0b\u836f\u7269\u4e4b\u524d\uff0c\u544a\u8bc9\u6211\u51e0\u53e5\u8bdd\uff0c\u4ed6\u8bf4\u4eba\u6b7b\u4e4b\u524d\uff0c\u56de\u5149\u8fd4\u7167\uff0c\u5b9a\u4f1a\u6709\u4e00\u6bb5\u65f6\u95f4\u7684\u6e05\u9192\uff0c\u4f46\u5979\u4e0d\u77e5\u9053\u8fd9\u79cd\u6e05\u9192\u80fd\u7ef4\u6301\u591a\u4e45\u65f6\u95f4\uff0c\u5979\u6709\u5f88\u591a\u8bdd\u8981\u544a\u8bc9\u4f60\uff0c\u5e0c\u671b\u80fd\u5c3d\u5410\u80f8\u4e2d\u4e4b\u79d8\uff0c\u6b7b\u800c\u65e0\u61be\uff0c\u8bf7\u6c42\u4e0a\u697c\u7b49\u5979\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5e94\u8be5\u7684\u3002\u201d \u3000\u3000\u5170\u767d\u9eef\u7136\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u53f6\u5927\u54e5\uff0c\u4e0d\u77e5\u80fd\u4e0d\u80fd\u8bf7\u4f60\u548c\u9ec4\u5821\u4e3b\u4e00\u8d77\u4e0a\u53bb\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u597d!\u5728\u4e0b\u4e5f\u6b63\u60f3\u770b\u770b\u7389\u7fe0\u59d1\u5a18\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u90a3\u5c31\u597d\u4e86\uff0c\u59ca\u59ca\u8fd9\u6700\u540e\u7684\u5fc3\u613f\uff0c\u6211\u603b\u7b97\u66ff\u5979\u5b8c\u6210\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5170\u767d\u59d1\u5a18\uff0c\u4ee4\u59ca\uff0c\u8fd8\u6709\u4ec0\u4e48\u6307\u6559\u5417?\u201d \u3000\u3000\u5170\u767d\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u4e86\uff0c\u70df\u9501\u697c\u4e0a\u7684\u4eba\uff0c\u90fd\u5df2\u7ecf\u6ca1\u6709\u53cd\u6297\u4e4b\u529b\uff0c\u53ef\u4ee5\u5148\u628a\u4ed6\u4eec\u64d2\u4f4f\u518d\u8bf4\u3002\u201d \u3000\u3000\u5170\u767d\u6ca1\u6709\u8bf4\u9519\uff0c\u70df\u9501\u697c\u4e0a\uff0c\u8fd8\u6709\u4e0d\u5c11\u7684\u4eba\uff0c\u800c\u4e14\uff0c\u5f88\u5f3a\u5927\u7684\u5b9e\u529b\uff0c\u548c\u91cd\u91cd\u673a\u5173\uff0c\u771f\u8981\u653b\u4e0a\u70df\u9501\u697c\uff0c\u53ea\u6015\u8fd8\u8981\u4ed8\u51fa\u5f88\u91cd\u5927\u7684\u727a\u7272\u3002 \u3000\u3000\u7389\u7fe0\u5374\u8f7b\u6613\u7684\u89e3\u51b3\u4e86\u8fd9\u4e9b\u95ee\u9898\u3002 \u3000\u3000\u4f46\u5979\u4e5f\u4ed8\u51fa\u4e86\u5f88\u5927\u7684\u4ee3\u4ef7\u3002 \u3000\u3000\u5979\u627f\u53d7\u4e86\u5deb\u5a46\u5a46\u3001\u8d3e\u4f3c\u9053\u836f\u6027\u53d1\u4f5c\u65f6\u7684\u53cd\u51fb\uff0c\u8eab\u4e2d\u5341\u679a\u6bd2\u9488\uff0c\u548c\u8d3e\u4f3c\u9053\u81f4\u547d\u7684\u4e00\u5251\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5979\u9759\u9759\u7684\u8eba\u5728\u4e00\u5f20\u6a80\u6728\u96d5\u82b1\u7684\u5927\u6728\u5e8a\u4e0a\uff0c\u76d6\u7740\u7c89\u7ea2\u8272\u7684\u68c9\u88ab\uff0c\u5934\u6795\u767d\u7f0e\u7ee3\u82b1\u7684\u9e33\u9e2f\u6795\u4e0a\u3002 \u3000\u3000\u6b64\u523b\uff0c\u6709\u4e09\u4e2a\u4eba\u9759\u9759\u5730\u7ad9\u5728\u6728\u69bb\u524d\u9762\u3002 \u3000\u3000\u662f\u9ec4\u7075\u3001\u5170\u767d\u3001\u53f6\u957f\u9752\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u795e\u60c5\u4e25\u8083\uff0c\u516d\u9053\u76ee\u5149\uff0c\u9759\u9759\u5730\u6295\u6ce8\u5728\u7389\u7fe0\u7684\u8138\u4e0a\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u90fd\u5750\u5728\u5385\u4e2d\uff0c\u672a\u5165\u5367\u5ba4\u3002 \u3000\u3000\u5434\u53cc\u5973\u8fdb\u6765\u8fc7\uff0c\u4e5f\u67e5\u770b\u8fc7\u7389\u7fe0\u7684\u4f24\u52bf\u3002 \u3000\u3000\u5979\u53d1\u89c9\u4e86\uff0c\u7eb5\u7136\u4e0d\u60dc\u4ee5\u5929\u9b54\u95e8\u4e0b\u7684\u836f\u7269\uff0c\u4e3a\u7389\u7fe0\u7597\u4f24\uff0c\u4f46\u6ca1\u6cd5\u633d\u56de\u7389\u7fe0\u7684\u6027\u547d\uff0c\u5979\u5df2\u6cb9\u5c3d\u706f\u67af\u65e0\u6cd5\u4e60\u7ec3\u5929\u9b54\u95e8\u7684\u5185\u529f\u3002 \u3000\u3000\u836f\u533b\u4e0d\u6b7b\u75c5\uff0c\u518d\u597d\u7684\u7075\u4e39\u3001\u5999\u836f\uff0c\u53ea\u80fd\u7597\u4f24\u3001\u4fdd\u547d\uff0c\u4f46\u5374\u6cd5\u4e3a\u4eba\u7eed\u547d\u3002 \u3000\u3000\u4f46\u5434\u53cc\u5973\u4ecd\u7136\u7559\u7ed9\u4e86\u5170\u767d\u4e00\u7c92\u4e39\u4e38\uff0c\u90a3\u662f\u63d0\u795e\u7684\u836f\u7269\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5979\u544a\u8bc9\u4e86\u5170\u767d\uff0c\u8fd9\u7c92\u4e39\u4e38\uff0c\u670d\u4e0b\u4e4b\u540e\uff0c\u53ef\u4ee5\u63d0\u795e\uff0c\u5fc5\u9700\u5728\u5979\u6e05\u9192\u4e4b\u540e\uff0c\u624d\u80fd\u670d\u4e0b\uff0c\u672a\u9192\u4e4b\u524d\uff0c\u773c\u4e0b\u8fd9\u7c92\u836f\u7269\u5c06\u6709\u65e0\u76ca\u3002 \u3000\u3000\u5170\u767d\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u53f6\u5927\u54e5\uff0c\u8981\u4e0d\u8981\u5979\u65e9\u4e9b\u6e05\u8fc7\u6765?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u600e\u4e48\u6837\u5979\u624d\u80fd\u6e05\u9192\u8fc7\u6765\u5462?\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5170\u767d\uff0c\u4e0d\u7ba1\u7389\u7fe0\u7684\u4f24\u52bf\u5982\u4f55\uff0c\u4f46\u6211\u4eec\u90fd\u8981\u5c3d\u6700\u5927\u7684\u529b\u91cf\u53bb\u6551\u5979\u3002\u201d \u3000\u3000\u9ec4\u7075\u4f4e\u58f0\u9053\uff1a\u201c\u53f6\u5144\u5f1f\uff0c\u6211\u5f88\u8d5e\u6210\u4f60\u7684\u7528\u5fc3\uff0c\u4e0d\u8fc7\uff0c\u4e8b\u5b9e\uff0c\u6551\u6d3b\u5979\u7684\u673a\u4f1a\u4e0d\u5927\uff0c\u65f6\u95f4\u62d6\u4e0b\u53bb\uff0c\u5bf9\u5979\u672a\u5fc5\u6709\u5229\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9eef\u7136\u4e00\u53f9\uff0c\u9053\uff1a\u201c\u5821\u4e3b\u7684\u610f\u601d\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u6211\u7684\u610f\u601d\u662f\uff0c\u6700\u597d\u5148\u8ba9\u5979\u628a\u5fc3\u4e2d\u7684\u4e8b\u60c5\u8bf4\u51fa\u6765\uff0c \u6211\u4eec\u4ed4\u7ec6\u5730\u770b\u8fc7\u5979\u7684\u4f24\u52bf\uff0c\u8001\u5b9e\u8bf4\uff0c\u6551\u6d3b\u5979\u7684\u5e0c\u671b\u4e0d\u5927\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u53f6\u5927\u54e5\uff0c\u59ca\u59ca\u4e5f\u8bf4\u8fc7\uff0c\u4e0d\u8981\u52c9\u5f3a\u6551\u5979\uff0c\u90a3\u53cd\u800c\u5bb3\u4e86\u5979\uff0c\u4e5f\u5bb3\u4e86\u6211\u4eec\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u5170\u767d\u9053\uff1a\u201c\u53f6\u5927\u54e5\uff0c\u59ca\u59ca\u6700\u5927\u7684\u5fc3\u613f\uff0c\u662f\u628a\u5979\u5fc3\u4e2d\u7684\u79d8\u5bc6\u8bf4\u51fa\u6765\uff0c\u5148\u8ba9\u5979\u5b8c\u6210\u4e86\u7b2c\u4e00\u4e2a\u5fc3\u613f\uff0c\u7136\u540e\u54b1\u4eec\u518d\u60f3\u529e\u6cd5\u6551\u5979\uff0c\u6211\u4e0d\u77e5\u9053\u4ec0\u4e48\u4e8b\uff0c\u80fd\u4e0d\u80fd\u8ba9\u5979\u9192\u8fc7\u6765?\u201d \u3000\u3000\u5170\u767d\u7528\u4e00\u7897\u6e05\u6c34\uff0c\u55b7\u5728\u7389\u7fe0\u7684\u8138\u4e0a\u3002 \u3000\u3000\u5979\u8138\u8272\u82cd\u767d\uff0c\u76ee\u5149\u4e2d\u4e0d\u89c1\u4e00\u70b9\u795e\u91c7\u3002 \u3000\u3000\u5979\u60f3\u5f00\u53e3\uff0c\u4f46\u5374\u6709\u4e00\u79cd\u8f6f\u5f31\u65e0\u529b\u7684\u6837\u5b50\uff0c\u597d\u50cf\u5df2\u7ecf\u65e0\u529b\u5f20\u5f00\u5634\u5df4! \u3000\u3000\u5170\u767d\u6025\u6025\u628a\u836f\u7269\u9001\u5165\u4e86\u59ca\u59ca\u7684\u53e3\u4e2d\uff0c\u9053\uff1a \u201c\u59ca\u59ca\uff0c\u628a\u8fd9\u7c92\u4e38\u836f\u541e\u4e0b\u53bb\u3002\u201d \u7389\u7fe0\u541e\u4e0b\u4e86\u836f\u7269\u3002 \u3000\u3000\u5929\u9b54\u95e8\u7684\u836f\u7269\uff0c\u679c\u7136\u6709\u7740\u9b54\u6cd5\u4e00\u6837\u7684\u6548\u529b\uff0c\u7389\u7fe0\u7acb\u523b\u632f\u594b\u8d77\u6765\u3002 \u3000\u3000\u5979\u7684\u8138\u8272\u4e5f\u6cdb\u8d77\u4e86\u4e00\u62b9\u8f7b\u7ea2\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5144\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a2\u51fa\u8eab\u5b50\uff0c\u4f4e\u58f0\uff0c\u9053\uff1a\u201c\u6211\u5728\u6b64\u5730\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u63a5\u9053\uff1a\u201c\u6211\u5df2\u5982\u98ce\u524d\u70db\u706b\uff0c\u968f\u65f6\u4f1a\u7184\u53bb\uff0c\u7559\u5fc3\u542c\u6211\u8bf4\u7684\u8bdd\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5728\u4e0b\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u5f88\u591a\u4e8b\uff0c\u6211\u6ca1\u6709\u544a\u8bc9\u5170\u767d\uff0c\u6211\u6015\u5979\u6c89\u4e0d\u4f4f\u6c14\uff0c\u9732\u51fa\u7834\u7efd\u3002\u201d \u3000\u3000\u5170\u81ea\u6d41\u4e0b\u6cea\u6765\uff0c\u9053\uff1a\u201c\u59ca\u59ca\uff0c\u6211\u77e5\u9053\uff0c\u4f60\u5f88\u7231\u62a4\u6211\u3002\u201d \u3000\u3000\u7389\u7fe0\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\uff0c\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\uff0c\u7167\u987e\u5170\u767d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u7b54\u5e94\uff0c\u6211\u4f1a\u628a\u5979\u5f53\u81ea\u5df1\u7684\u59b9\u59b9\u4e00\u6837\u770b\u5f85\u3002\u201d \u3000\u3000\u6cdb\u73b0\u51fa\u4e00\u62b9\u60b2\u6006\u53c8\u5e26\u5b89\u6170\u7684\u7b11\u610f\uff0c\u7389\u7fe0\u6cdb\u6cdb\u8bf4\u9053\uff1a \u201c\u53f6\u5144\uff0c\u5170\u767d\u4e3a\u4f60\u4fdd\u7559\u4e0b\u4e86\u6e05\u767d\u7684\u8eab\u5b50\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u51c4\u6ce3\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u59ca\u59ca\u7684\u8bbe\u8ba1\u6551\u4e86\u6211\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4f60\u4e3a\u5979\u5403\u4e86\u5f88\u591a\u7684\u82e6\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u7b54\u5e94\u6211\u5a36\u5979\u4e3a\u59bb\uff0c\u4e00\u751f\u5584\u5f85\u5979\uff0c\u5979\u867d\u7136\u662f\u4e2a\u6740\u624b\uff0c\u4f46\u5979\u5fc3\u5730\u5f88\u7eaf\u826f\uff0c\u6211\u4eec\u8fc7\u53bb\u7684\u4e00\u5207\u7f6a\u6076\uff0c\u90fd\u662f\u6211\u4e00\u624b\u5b89\u6392\u7684\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u597d!\u6211\u7b54\u5e94\u4f60\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u6211\u4f1a\u8bf7\u4e24\u4e2a\u6700\u9002\u5408\u7684\u5a92\u4eba\uff0c\u5e76\u4ee5\u4e49\u5144\u7684\u8eab\u5206\u4e3b\u6301\u4ed6\u4eec\u7684\u5a5a\u793c\u3002\u201d \u7389\u7fe0\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u2026\u2026\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u7b2c\u4e00\u5821\u4e2d\u6709\u4e24\u4e2a\u5927\u79d8\u5bc6\uff0c\u4e0d\u77e5\u9053\u4f60\u662f\u5426\u77e5\u9053\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u7b2c\u4e00\u5821\u5f88\u591a\u795e\u79d8\uff0c\u9664\u4e86\u9ad8\u5929\u5065\u4e4b\u5916\uff0c\u53ea\u6015\u5f88\u5c11\u4eba\u80fd\u5168\u76d8\u4e86\u89e3\u3002\u201d \u7389\u7fe0\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u4e00\u9053\u751f\u6b7b\u95e8\uff0c\u90a3\u4e2a\u95e8\u7edd\u4e0d\u80fd\u5f00\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u95e8\u5f00\u8fc7\uff0c\u4f46\u53c8\u5173\u95ed\u4e86\uff0c\u90a3\u91cc\u9762\u662f\u4ec0\u4e48?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u75af\u5b50\uff0c\u5e26\u7740\u602a\u75c5\u7684\u75af\u5b50\uff0c\u4ed6\u4eec\u867d\u75af\uff0c\u53c8\u6709\u7740\u602a\u75c5\u4f46\u6b66\u529f\u5374\u9ad8\u5f3a\u5f97\u5f88\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u4ed6\u4eec\u2026\u2026\u201d \u3000\u3000\u7389\u7fe0\u63a5\u9053\uff1a \u201c\u542c\u6211\u8bf4\uff0c\u6211\u6ca1\u6709\u89c1\u8fc7\u4ed6\u4eec\uff0c\u4e5f\u672a\u8fdb\u8fc7\u751f\u6b7b\u95e8\u4f46\u662f\u8fd9\u4e9b\u6d88\u606f\uff0c\u5374\u662f\u9ad8\u5929\u5065\u4eb2\u53e3\u544a\u8bc9\u8fc7\u7684\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u7389\u7fe0\u59d1\u5a18\uff0c\u8fd9\u786e\u6709\u4e00\u4e2a\u751f\u6b7b\u95e8\uff0c\u90a3\u6247\u95e8\u4e00\u5ea6\u6253\u5f00\u4e86\uff0c\u4f46\u5374\u53c8\u88ab\u5173\u4e0a\u3002\u201d \u7389\u7fe0\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4ed6\u6ca1\u6709\u9a97\u6211\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7389\u7fe0\uff0c\u4e24\u4e2a\u5927\u79d8\u5bc6\uff0c\u8fd8\u6709\u4e00\u4e2a\u662f\u4ec0\u4e48?\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1b\u201c\u8fd9\u5ea7\u70df\u9501\u697c\u4e0b\uff0c\u57cb\u6709\u706b\u836f\uff0c\u53ea\u8981\u70b9\u71c3\u836f\u4fe1\uff0c\u7acb\u523b\u4f1a\u7206\u6210\u98de\u7070\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u63a5\u9053\uff1a\u201c\u90a3\u4e00\u573a\u5927\u6c34\uff0c\u4e5f\u8bb8\u65e9\u5df3\u628a\u706b\u836f\u6e7f\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a\u201c\u4e0d\u4f1a\u6709\u592a\u5927\u7684\u5f71\u54cd\uff0c\u4ed6\u65e9\u5df2\u6709\u4e86\u9632\u5907\uff0c\u8fd9\u573a\u5927\u6c34\u6e7f\u4e0d\u4e86\u706b\u836f\u5f15\u4fe1\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u7389\u7fe0\uff0c\u4f60\u5df2\u7ecf\u8bf4\u660e\u4e86\u8981\u8bf4\u7684\u8bdd\u73b0\u5728\uff0c\u5e94\u8be5\u7597\u6cbb\u4f60\u7684\u4f24\u52bf\u4e86\u3002\u201d \u3000\u3000\u7389\u7fe0\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u662f\u771f\u7684\uff0c\u6211\u73b0\u5728\u597d\u8f7b\u677e\uff0c\u8fd9\u4e9b\u5e74\u6765\u6211\u90fd\u6ca1\u6709\u8fd9\u4e48\u5feb\u4e50\u8fc7\uff0c\u5170\u767d\u6709\u4e86\u5f52\u5bbf\uff0c\u6211\u5fc3\u4e2d\u4e5f\u6ca1\u6709\u6302\u8651\uff0c\u53f6\u90ce\uff0c\u62b1\u62b1\u6211\u5427!\u6211\u597d\u60f3\u5ac1\u7ed9\u4f60\uff0c\u4f46\u6211\u77e5\u9053\u81ea\u5df1\u4e0d\u914d\u2026\u2026\u201d \u3000\u3000\u53f6\u957f\u9752\u6fc0\u52a8\u5730\u8bf4\u9053\uff1a\u201c\u7389\u7fe0\uff0c\u6491\u4e0b\u53bb\uff0c\u6211\u4eec\u4f1a\u5c3d\u6700\u5927\u7684\u5fc3\u529b\uff0c\u60f3\u529e\u6cd5\u533b\u597d\u4f60\u3002\u201d \u3000\u3000\u7389\u7fe0\u9053\uff1a \u201c\u4e0d\u8981\u60f3\u6551\u6d3b\u6211\uff0c\u6211\u5df2\u7ecf\u5230\u4e86\u6cb9\u5c3d\u706f\u67af\u7684\u5883\u754c\uff0c\u4e0d\u8981\u60f3\u6551\u6211\u4e86\u3002\u201d \u53f6\u957f\u9752\u9053\uff1a\u201c\u7389\u7fe0\uff0c\u632f\u4f5c\u8d77\u6765\uff0e \u6211\u4eec\u4f1a\u5c3d\u529b\u60f3\u529e\u6cd5\uff0c\u4f46\u4f60\u5fc5\u987b\u8981\u6709\u6d3b\u4e0b\u53bb\u7684\u52c7\u6c14\u3002\u201d \u7389\u7fe0\u9053\uff1a\u201c\u5982\u82e5\u6211\u6ca1\u6709\u6d3b\u4e0b\u53bb\u7684\u52c7\u6c14\uff0c\u73b0\u5728\u65e9\u5df2\u7ecf\u6c14\u7edd\u591a\u65f6\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u7389\u7fe0\uff0c\u53ea\u8981\u4f60\u80fd\u575a\u6301\u4e0b\u53bb\uff0c\u6211\u76f8\u4fe1\uff0c\u6211\u4eec\u6709\u6551\u6d3b\u4f60\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u7389\u7fe0\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u53f6\u5144\uff0c\u542c\u6211\u8bf4\uff0c\u6211\u8981\u5bb3\u4e2d\u5251\uff0c\u7a74\u8109\u4e2d\u9488\uff0c\u6700\u8981\u547d\u7684\u662f\uff0c\u8d3e\u4f3c\u9053\u90a3\u72e0\u547d\u7684\u4e00\u638c\uff0c\u90a3\u4e00\u638c\uff0c\u9707\u788e\u4e86\u6211\u7684\u5185\u8151\uff0c\u7075\u836f\u53ef\u4ee5\u7597\u75c5\uff0c\u53ef\u4ee5\u6551\u4eba\uff0c\u4f46\u4e0d\u80fd\u7eed\u547d\uff0c\u6211\u73b0\u5728\uff0c\u7cbe\u795e\u80fd\u591f\u8fd9\u4e48\u597d\uff0c\u8fde\u6211\u81ea\u5df1\u90fd\u89c9\u7740\u5947\u602a\u3002\u201d \u3000\u3000\u5170\u767d\u9053\uff1a \u201c\u5434\u59d1\u5a18\u7ed9\u6211\u4e00\u7c92\u836f\uff0c\u90a3\u7c92\u836f\u7269\uff0c\u4f7f\u4f60\u7684\u7cbe\u795e\u632f\u594b\u3002\u201d \u7389\u7fe0\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u8bed\u58f0\u4e00\u987f\uff0c\u63a5\u9053\uff1a\u201c\u53f6\u5144\uff0c\u628a\u7231\u6211\u90a3\u4e00\u4efd\u60c5\u610f\uff0c\u8f6c\u8fc7\u5728\u5170\u767d\u8eab\u4e0a\uff0c\u597d\u597d\u7684\u5475\u62a4\u5979\uff0c\u6211\u8981\u53bb\u4e86\u3002\u201d \u3000\u3000\u7f13\u7f13\u95ed\u4e0a\u4e86\u53cc\u76ee\u3002 \u3000\u3000\u5170\u767d\u6025\u9053\uff1a\u201c\u59ca\u59ca\uff0c\u59ca\u59ca\uff0c\u4f60\u4e0d\u80fd\u8d70!\u201d \u3000\u3000\u7389\u7fe0\u4f3c\u662f\u542c\u5230\u4e86\uff0c\u5634\u89d2\u6cdb\u8d77\u4e86\u4e00\u4e1d\u7b11\u610f\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u770b\u770b\u5979\u7684\u4f24\u2026\u2026\u201d \u3000\u3000\u5ffd\u7136\u6380\u5f00\u4e86\u7eeb\u88ab\u3002 \u3000\u3000\u53ea\u89c1\u6ee1\u5e8a\u90fd\u662f\u9c9c\u8840\uff0c\u80f8\u53e3\u5904\uff0c\u4e00\u5904\u5251\u4f24\uff0c\u76f4\u5165\u5fc3\u810f\u3002 \u3000\u3000\u9ec4\u7075\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u591a\u8d39\u5fc3\u4e86\uff0c\u5979\u5df2\u7ecf\u65e0\u836f\u53ef\u6551\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u4f38\u624b\u4e00\u6478\uff0c\u7389\u7fe0\u7adf\u5df2\u6c14\u7edd\u3002 \u3000\u3000\u53f6\u957f\u9752\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u7389\u7fe0\uff0c\u653e\u5fc3\u53bb\u5427!\u6211\u5168\u5fc3\u5168\u610f\u5730\u7167\u987e\u5170\u767d\u3002\u201d \u3000\u3000\u5170\u767d\u518d\u4e5f\u65e0\u6cd5\u514b\u5236\u5185\u5fc3\u7684\u6fc0\u52a8\uff0c\u8f6c\u8eab\u4f0f\u5728\u53f6\u957f\u9752\u80a9\u4e0a\uff0c\u653e\u58f0\u5927\u54ed\u8d77\u6765\u3002 \u3000\u3000\u53f6\u957f\u9752\u8ba9\u5979\u54ed\u4e86\u4e00\u9635\uff0c\u624d\u62cd\u62cd\u5170\u767d\uff0c\u9053\uff1a \u201c\u522b\u54ed\u4e86\uff0c\u628a\u7389\u7fe0\u5c38\u4f53\u5e26\u8d70\u5427!\u8fd9\u5730\u65b9\u4e0d\u5b9c\u4e45\u7559\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bf9!\u5979\u4eec\u90fd\u5df2\u7ecf\u8d70\u4e86\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u56de\u5934\u671b\u4e00\u671b\uff0c\u53ea\u89c1\u697c\u4e0a\u53ea\u4f59\u4e0b\u9ec4\u7075\u4e00\u4eba\u3002 \u3000\u3000\u5170\u767d\u5ffd\u7136\u8b66\u89c9\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u4f1a\u7206\u6210\u98de\u7070\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u4e0d\u4e00\u5b9a\u4f1a\u7206\u70b8\uff0c\u4f46\u54b1\u4eec\u4e0d\u80fd\u4e0d\u51c6\u5907\u3002\u201d \u3000\u3000\u5170\u767d\u4e0d\u518d\u591a\u8a00\uff0c\u62b1\u8d77\u7389\u7fe0\u7684\u5c38\u4f53\uff0c\u5411\u5916\u5954\u53bb\u3002 \u3000\u3000\u53f6\u957f\u9752\u5954\u4e0b\u4e86\u4e8c\u5c42\uff0c\u53d1\u89c9\u9ec4\u7075\u5e76\u672a\u968f\u6765\uff0e\u5fc3\u4e2d\u5927\u5947\uff0c\u53ea\u597d\uff0c\u8eab\u53c8\u884c\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u9ec4\u7075\u7aef\u5750\u5728\u4e00\u5f20\u6728\u6905\u4e0a\uff0c\u95ed\u76ee\u6c89\u601d\u3002 \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5821\u4e3b\u600e\u4e0d\u8d70?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u5728\u60f3\uff0c\u8fd9\u70b9\u71c3\u706b\u836f\u7684\u5f15\u4fe1\uff0c\u5fc5\u7136\u5728\u8fd9\u5c42\u697c\u4e0a\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u5821\u4e3b\u8981\u5f15\u7206\u8fd9\u5ea7\u70df\u9501\u697c?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u662f\u554a!\u5982\u82e5\u8fd9\u5ea7\u70df\u9501\u697c\u4e0b\uff0c\u771f\u7684\u57cb\u6709\u5f88\u591a\u7684\u706b\u836f\u7559\u4e0b\u5b83\uff0c\u5b9e\u5728\u662f\u4e00\u5927\u5bb3\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u54b1\u4eec\u7684\u4f24\u4ea1\u5f88\u91cd\uff0c\u52bf\u5fc5\u8fd8\u8981\u7559\u5728\u8fd9\u91cc\u4f11\u606f\u4e00\u9635\uff0c\u627e\u5bfb\u90a3\u836f\u5f15\u7684\u4e8b\uff0c\u5012\u4e5f\u4e0d\u7528\u6025\u5728\u4e00\u65f6\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u957f\u9752\uff0c\u9ad8\u5929\u5065\u53ef\u4ee5\u628a\u8fd9\u4e2a\u6d88\u606f\u544a\u8bc9\u7389\u7fe0\uff0c\u4e5f\u53ef\u544a\u8bc9\u522b\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9664\u4e86\u751f\u6b7b\u95e8\u4e2d\u7684\u79d8\u5bc6\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u6869\u5f88\u5927\u7684\u9690\u79d8\u4e8b\uff0c\u672a\u542c\u5979\u8c08\u8d77\u8fc7\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5723\u6c34\uff0c\u9ad8\u5929\u5065\u5f04\u5230\u4e86\u5f88\u591a\u7684\u5723\u6c34\uff0c\u5c31\u6211\u6240\u77e5\uff0c\u8fd9\u4e9b\u5723\u6c34\uff0c\u90fd\u5b58\u5728\u7b2c\u4e00\u5821\u4e2d\uff0c\u5b58\u5728\u4f55\u5904?\u7528\u5904\u4e3a\u4f55?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u8fd9\u548c\u5f15\u7206\u70df\u9501\u697c\uff0c\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u5462?\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u9ad8\u5929\u5065\u79c1\u4eba\u7684\u85cf\u5a07\u4e4b\u6240\uff0c\u4e5f\u662f\u9ad8\u5929\u5065\u6536\u85cf\u7edd\u5bf9\u9690\u79d8\u7684\u5730\u65b9\uff0c\u5728\u8fd9\u91cc\u5f88\u53ef\u80fd\u627e\u51fa\u5723\u6c34\uff0c\u4e5f\u53ef\u80fd\u67e5\u51fa\u5b83\u7684\u4f5c\u7528\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u627e\u51fa\u5723\u6c34\uff0c\u4e5f\u8bb8\u6709\u53ef\u80fd\uff0c\u4f46\u5982\u60f3\u627e\u51fa\u5b83\u7684\u4f5c\u7528\uff0c\u53ea\u6015\u4e0d\u662f\u4e00\u4ef6\u5f88\u5bb9\u6613\u7684\u4e8b\u554a\uff01\u201d \u3000\u3000\u989f\u9053\uff1a\u201c\u5c31\u7b97\u9ad8\u5929\u5065\u77e5\u6653\u6b64\u79d8\uff0c\u4f46\u4ed6\u4e8b\u60c5\u592a\u5fd9\uff0c\u672a\u5fc5\u80fd\u5904\u7406\u5723\u6c34\u7684\u4e8b\u60c5\uff0c\u56e0\u6b64\uff0c\u5fc5\u6709\u4e00\u4e2a\u4eba\uff0c\u4ee3\u4ed6\u6267\u884c\u3002\u201d \u3000\u3000\u53ea\u89c9\u4ed6\u7684\u5206\u6790\uff0c\u5165\u60c5\u5408\u7406\uff0c\u4e0d\u5f97\u4e0d\u8ba9\u4eba\u5fc3\u751f\u656c\u670d\u3002 \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5821\u4e3b\u8bf4\u7684\u6709\u7406\uff0c\u4e0d\u8fc7\uff0c\u6b64\u523b\uff0c\u5821\u4e3b\u7684\u4f24\u52bf\u672a\u6108\uff0c\u4e0d\u5b9c\u518d\u52b3\u7d2f\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u53f6\u5144\u4e0d\u7528\u4e3a\u6211\u62c5\u5fc3\uff0c\u966a\u5170\u767d\u53bb\u5427!\u6211\u8981\u5728\u8fd9\u91cc\u4ed4\u7ec6\u7684\u770b\u4e00\u770b\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5821\u4e3b\u5982\u6b64\u5429\u5490\uff0c\u5c5e\u4e0b\u5c31\u4e0d\u6253\u6270\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8eab\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u9ec4\u7075\u7a81\u7136\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u957f\u9752\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u56de\u8fc7\u8eab\u5b50\uff0c\u7f13\u6b65\u884c\u5230\u4e86\u9ec4\u7075\u7684\u8eab\u4fa7\uff0c\u9053\uff1b\u201c\u5821\u4e3b\u8fd8\u6709\u4ec0\u4e48\u5429\u5490?\u201d \u3000\u3000\u9ec4\u7075\u4f4e\u58f0\u9053\uff1a\u201c\u901a\u77e5\u5434\u59d1\u5a18\u548c\u5e7d\u5170\u59d1\u5a18\uff0c\u8981\u4ed6\u4eec\u4e24\u4e2a\u6697\u4e2d\u7559\u5fc3\u9003\u51fa\u70df\u9501\u697c\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u8fd8\u6709\u4eba?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u53ea\u662f\u8fd9\u6837\u690e\u60f3\uff0c\u5fc3\u4e2d\u6ca1\u6709\u628a\u63e1\uff0c\u6240\u4ee5\u8981\u4f60\u4eec\u6697\u4e2d\u901a\u77e5\u5979\u4eec\uff0c\u8fd9\u4ef6\u4e8b\u4e0d\u5b9c\u5f20\u626c\u51fa\u53bb\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5c5e\u4e0b\u660e\u767d\uff0c\u5821\u4e3b\u5c0f\u5fc3\u2026\u2026\u201d \u3000\u3000\u8bf4\u5b8c\u4e86\u8bdd\uff0c\u4ecd\u7136\u7ad9\u7740\u672a\u52a8\u3002 \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u957f\u9752\uff0c\u8fd8\u6709\u4e8b\u5417?\u201d \u3000\u3000\u53f6\u957f\u9752\u9053\uff1a\u201c\u5c5e\u4e0b\u51e0\u65f6\u6765\u63a5\u5e94\u5821\u4e3b\uff0c\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u542c\u6211\u7684\u62db\u547c\u5427!\u6211\u5982\u5e94\u4ed8\u4e0d\u4e86\uff0c\u6216\u9047\u4e0a\u4e86\u4ec0\u4e48\u51f6\u9669\uff0c\u4f1a\u5578\u58f0\u62db\u547c\u4f60\u4eec\u3002\u201d \u3000\u3000\u53f6\u957f\u9752\u8f6c\u8eab\u800c\u53bb\uff0c \u3000\u3000\u76f4\u7b49\u53f6\u957f\u9752\u79bb\u53bb\u826f\u4e45\uff0c\u9ec4\u7075\u624d\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u9601\u4e0b\u53ef\u4ee5\u51fa\u6765\u4e86\u3002\u201d \u697c\u4e2d\u4e00\u7247\u6c89\u9759\uff0c\u9759\u5f97\u542c\u4e0d\u5230\u4e00\u70b9\u58f0\u606f\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u53d1\u89c9\u4e86\u4f60\u3002\u201d \u3000\u3000\u4ecd\u7136\u65e0\u56de\u58f0\u4e4b\u5e94\u3002 \u3000\u3000\u9ec4\u7075\u7f13\u7f13\u7ad9\u8d77\u8eab\u5b50\uff0c\u9053\uff1a \u201c\u9601\u4e0b\u5982\u662f\u771f\u7684\u4e0d\u80af\u51fa\u6765\uff0c\u5728\u4e0b\u53ea\u6709\u51fa\u624b\u62c9\u4f60\u51fa\u6765\u4e86\u3002\u201d \u53ea\u542c\u4e00\u58f0\u8f7b\u8f7b\u53f9\u606f\uff0c\u9053\uff1a\u201c\u4f60\u771f\u7684\u53d1\u89c9\u4e86\u6211\u3002\u201d \u3000\u3000\u58f0\u97f3\u6765\u81ea\u7a97\u5916\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u5ffd\u7565\u4e86\u4e00\u4ef6\u4e8b\uff0c\u5012\u5f71\u4f1a\u6620\u5728\u7a97\u4e0a\uff0c\u90a3\u58f0\u97f3\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u770b\u6765\uff0c\u4f60\u4e0d\u662f\u865a\u5f20\u58f0\u52bf\u4e86\uff0c\u5509!\u4f60\u5b9e\u5728\u662f\u4e2a\u5f88\u806a\u660e\u7684\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5938\u5956\uff0c\u5938\u5956\u3002\u201d \u3000\u3000\u90a3\u58f0\u97f3\u53c8\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4e00\u5b9a\u8981\u89c1\u89c1\u6211\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u7559\u5728\u8fd9\u91cc\u4e0d\u8d70\uff0c\u5c31\u662f\u5e0c\u671b\u89c1\u9601\u4e0b\u4e86\u3002\u201d \u3000\u3000\u90a3\u58f0\u97f3\u53c8\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u89c1\u4e86\u6211\uff0c \u53ea\u6015\u4f60\u4f1a\u5f88\u5931\u671b\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u5728\u4e0b\u4e0d\u4f1a\u5931\u671b\u3002\u201d \u3000\u3000\u90a3\u58f0\u97f3\u9053\uff1a\u201c\u6211\u597d\u50cf\u542c\u5230\u4f60\u6709\u4e00\u4e2a\u9634\u8c0b\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u8c08\u4e0d\u4e0a\u9634\u8c0b\uff0c\u5728\u4e0b\u53ea\u662f\u6709\u4e00\u4e2a\u51c6\u5907\u7f62\u4e86\u3002\u201d \u3000\u3000\u90a3\u58f0\u97f3\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u7684\u51c6\u5907\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u4eec\u5728\u70df\u9501\u697c\u4e0b\uff0c\u6709\u4e00\u70b9\u5c0f\u5c0f\u7684\u5e03\u7f6e\u3002\u201d \u3000\u3000\u90a3\u58f0\u97f3\u9053\uff1a\u201c\u76d1\u89c6\u6211\uff0c\u4e0d\u8ba9\u6211\u79bb\u5f00\u6b64\u5730\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u90a3\u58f0\u97f3\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u9ad8\u5929\u5065\u6b7b\u4e86\u4e4b\u540e\uff0c\u6211\u662f\u552f\u4e00\u77e5\u9053\u90a3\u706b\u836f\u5f15\u4fe1\u5b89\u6392\u5728\u4ec0\u4e48\u5730\u65b9\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u90a3\u58f0\u97f3\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u5fc5\u987b\u4e86\u89e3\u8fd9\u4e00\u70b9\uff0c\u54b1\u4eec\u5c31\u597d\u8c08\u4e86\u3002\u201d \u3000\u3000\u9ec4\u5458\u9053\uff1a\u201c\u6211\u5f88\u4e86\u89e3\u4e86\uff0c\u9601\u4e0b\u53ef\u4ee5\u73b0\u5728\u51fa\u6765\uff0c\u54b1\u4eec\u9762\u5bf9\u9762\u7684\u8c08\u8c08\u5427?\u201d \u3000\u3000\u90a3\u58f0\u97f3\u53c8\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u597d!\u5728\u4e0b\u6765\u4e86\u3002\u201d \u3000\u3000\u7d27\u95ed\u7684\u7a97\u95e8\uff0c\u7a81\u7136\u5927\u5f00\u3002 \u3000\u3000\u4e00\u4e2a\u7ec6\u5c0f\u7684\u4eba\u5f71\uff0c\u7531\u7a97\u53e3\u4e2d\u7ffb\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u9ec4\u7075\u770b\u6e05\u695a\u4e86\u90a3\u4e2a\u4eba\u4e4b\u540e\uff0c\u4e0d\u7981\u4e3a\u4e4b\u4e00\u5446\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4eba\u7a7f\u7740\u4e00\u8eab\u58a8\u8272\u7684\u8863\u670d\uff0c\u8eab\u4e0d\u8fc7\u4e94\u5c3a\uff0c\u8eab\u8eaf\u5a07\u5c0f\u3001\u73b2\u73d1\uff0c\u8138\u8272\u5fae\u73b0\u82cd\u767d\uff0c\u4e00\u65b9\u9ed1\u5dfe\uff0c\u5305\u4f4f\u4e86\u4e00\u5934\u957f\u53d1\u3002 \u3000\u3000\u957f\u53d1\u9732\u51fa\u4e86\u9ed1\u5dfe\u4e00\u534a\u3002 \u3000\u3000\u4ee5\u9ec4\u7075\u89c1\u8bc6\u4e4b\u5e7f\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u7136\u65e0\u6cd5\u770b\u6e05\u695a\u4ed6\u5230\u5e95\u662f\u7537\u662f\u5973\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u76ee\u7779\u9ec4\u7075\u7684\u53d1\u5446\u795e\u60c5\uff0c\u4e0d\u7981\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u6709\u4e9b\u5947\u602a\u7684\u611f\u89c9?\u201d \u3000\u3000\u4e00\u7b11\u4e4b\u4e0b\uff0c\u9732\u51fa\u4e86\u4e00\u53e3\u7ec6\u5c0f\u7684\u767d\u7259\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bf9!\u5728\u4e0b\u89c9\u7740\u5f88\u5947\u602a\uff0c\u7b2c\u4e00\u4e2a\u5947\u602a\u662f\uff0c\u6211\u4e0d\u77e5\u9053\u4f60\u662f\u7537\u3001\u662f\u5973?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6211\u662f\u7537\u4eba\uff0c\u4e00\u4e2a\u77ee\u5c0f\u7684\u7537\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9601\u4e0b\u8d35\u59d3?\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u9ece\u8679\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u9053\uff1a\u201c\u539f\u6765\u662f\u9ece\u5144\u3002\u201d \u3000\u3000\u9ece\u8679\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5fc3\u4e2d\u4e00\u5b9a\u6709\u5f88\u591a\u7684\u7591\u95ee\uff0c\u4e0d\u59a8\u4e8b\uff0c\u5c3d\u7ba1\u95ee\u5427\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u9ece\u5144\u8fc7\u53bb\uff0c\u662f\u5426\u5e38\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u5982\u5e38\u5e38\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u4ee5\u6211\u8fd9\u526f\u5f62\u8c8c\uff0c\u548c\u6211\u8fd9\u50cf\u5973\u4eba\u7684\u58f0\u97f3\uff0c\u5b9a\u7136\u662f\u65e9\u5df2\u7ecf\u540d\u6ee1\u6c5f\u6e56\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u9ece\u5144\u662f\u5f88\u5c11\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u4e86\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u4e5f\u4e0d\u5c3d\u7136\uff0c\u6211\u5e38\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u4e0d\u8fc7\uff0c\u6211\u592a\u5e73\u51e1\u4e86\uff0c\u5982\u82e5\u6211\u518d\u6539\u88c5\u4e00\u4e0b\uff0c\u5c31\u4e0d\u4f1a\u6709\u4eba\u77e5\u9053\u6211\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u5728\u4e0b\u5bf9\u9ece\u5144\u4e00\u70b9\u4e5f\u4e0d\u4e86\u89e3\uff0c\u4e0d\u8fc7\uff0c\u80fd\u5f97\u9ad8\u5929\u5065\u8d4f\u8bc6\u7684\u4eba\uff0c\u7edd\u5bf9\u4e0d\u662f\u5e73\u5e38\u7684\u4eba\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a \u201c\u90a3\u662f\u56e0\u4e3a\u9ad8\u5929\u5065\u6d3b\u7740\u7684\u65f6\u5019\uff0c\u6211\u662f\u4e2a\u4e0d\u80fd\u72ec\u7acb\u7684\u4eba\uff0c\u6211\u662f\u4ed6\u7684\u5f71\u5b50\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u5f88\u5947\u602a\u7684\u8bf4\u6cd5\uff0c\u5728\u4e0b\u7b2c\u4e00\u6b21\u542c\u5230\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a \u201c\u6211\u5c31\u50cf\u5f71\u5b50\u4e00\u6837\uff0c\u9644\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u6211\u6ca1\u6709\u81ea\u6211\uff0c\u4e5f\u51e0\u4e4e\u6ca1\u6709\u5b9e\u4f53\u5b58\u5728\uff0c\u6240\u4ee5\uff0c\u4ed6\u6d3b\u7740\u6211\u5c31\u7b49\u4e8e\u4e00\u4e2a\u6ca1\u6709\u751f\u547d\u7684\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8fd9\u662f\u8868\u793a\u51fa\u4e86\uff0c\u4f60\u5bf9\u4ed6\u7684\u5fe0\u5b9e\u4e86\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u8fd9\u4e9b\u60c5\u5f62\u5f88\u590d\u6742\uff0c\u8bf4\u6211\u5bf9\u4ed6\u7684\u5fe0\u5b9e\uff0c\u6ca1\u6709\u9519\uff0c\u56e0\u4e3a\u6211\u4e3a\u4ed6\u6d3b\u7740\uff0c\u8fd9\u4e9b\u5e74\u6211\u4e00\u76f4\u542c\u4ed6\u7684\u5429\u5490\uff0c\u5fe0\u5b9e\u5730\u6267\u884c\u4ed6\u7684\u547d\u4ee4\uff0c\u53ea\u6709\u4e00\u6b21\u6211\u6ca1\u6709\u7167\u4ed6\u7684\u5429\u5490\u53bb\u505a\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54ea\u4e00\u6b21?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\uff0c\u6240\u4ee5\uff0c\u4f60\u4eec\u6740\u6b7b\u4e86\u4ed6\u3002\u201d \u3000\u3000\u9ec4\u7075\u4e0d\u89e3\u3002 \u3000\u3000\u4f38\u624b\u6478\u6478\u4e0b\u989a\uff0c\u8f7b\u53f9\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u9ece\u5144\uff0c\u8fd9\u4e00\u6b21\u4f60\u6ca1\u6709\u7528\u4ed6\u7684\u5429\u5490\u53bb\u505a\uff0c\u6211\u624d\u6740\u4e86\u9ad8\u5929\u5065?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u662f\u554a!\u4f60\u53ef\u662f\u4e0d\u76f8\u4fe1\u6211\u7684\u8bdd?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u76f8\u4fe1\uff0c\u800c\u662f\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\uff0c\u5f88\u5bb9\u6613\u660e\u793a\uff0c\u6211\u662f\u4ed6\u7684\u5f71\u5b50\uff0c\u4ed6\u6d3b\u7740\u65f6\uff0c\u6211\u770b\u4f3c\u4e0d\u5b58\u5728\uff0c\u5f53\u4ed6\u5371\u6025\u7684\u65f6\u95f4\uff0c\u6211\u4f1a\u53ca\u65f6\u7684\u51fa\u73b0\u6551\u4ed6\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u6551\u4ed6\uff0c\u51ed\u4f60\u7684\u6b66\u529f\uff0c\u8fd8\u662f\u6709\u4ec0\u4e48\u5947\u672f?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u81ea\u7136\u662f\u51ed\u6211\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u8fd9\u4e48\u8bf4\uff0c \u4f60\u7684\u6b66\u529f\uff0c \u6bd4\u8d77\u9ad8\u5929\u5065\u8fd8\u8981\u9ad8\u660e\u4e86\u3002\u201d \u9ece\u8679\u9053\uff1a \u201c\u90a3\u5012\u4e0d\u662f\uff0c\u4f46\u6211\u7684\u51fa\u73b0\uff0c\u4f1a\u5728\u4ed6\u6700\u9700\u8981\u7684\u65f6\u523b\uff0c\u6700\u9700\u8981\u7684\u5730\u65b9\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u542c\u8d77\u6765\uff0c\u8fd1\u4e4e\u7384\u865a\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u9ad8\u5929\u5065\uff0c\u81ea\u4e0d\u77e5\u4e2a\u4e2d\u7684\u7384\u5965\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u8bc1\u660e\u4e00\u4e0b\uff0c\u8ba9\u6211\u5f00\u5f00\u773c\u754c\u5462?\u201d \u3000\u3000\u9ece\u8679\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u53ef\u4ee5\uff0c\u4e0d\u662f\u73b0\u5728\uff0c\u90a3\u5fc5\u987b\u8981\u6709\u4e00\u4e2a\u640f\u547d\u7684\u573a\u9762\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d\u5427!\u8fd9\u4e00\u70b9\uff0c\u54b1\u4eec\u4ee5\u540e\u518d\u8bf4\uff0c\u5728\u4e0b\u8bf7\u6559\uff0c\u4f60\u600e\u4e48\u4f1a\u7a81\u7136\u95f4\uff0c\u4e0d\u80af\u5e2e\u52a9\u4ed6\u4e86\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u90a3\u662f\u56e0\u4e3a\u6211\u5ffd\u7136\u95f4\u53d1\u73b0\u4e86\u81ea\u6211\u7684\u5b58\u5728\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u6b7b\u4e86\u4e4b\u540e\uff0c\u6211\u53c8\u540e\u6094\u3002\u201d \u3000\u3000\u9ec4\u7075\u5ffd\u7136\u95f4\u53d1\u89c9\uff0c\u4ed6\u7684\u6bcf\u53e5\u8bdd\u4e2d\uff0c\u90fd\u542b\u6709\u7740\u4e00\u79cd\u7384\u5965\u9053\u7406\u3002 \u3000\u3000\u9ece\u8679\u6709\u4e9b\u4f24\u795e\u7684\u671b\u7740\u7a97\u5916\uff0c\u9eef\u7136\u5730\u8bf4\u9053\uff1a\u201c\u4ed6\u6b7b\u4e86\uff0c\u6211\u5c31\u597d\u50cf\u5931\u53bb\u4e86\u4e00\u4e2a\u4f9d\u6258\uff0c\u4e00\u4e2a\u53ef\u652f\u6301\u7684\u4f9d\u6258\uff0c\u6211\u89c9\u7740\u597d\u7a7a\u865a\uff0c\u5728\u7a7a\u832b\u4e2d\u98d8\u6d6e\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u9b3c\u9b42\uff0c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4f60\u548c\u9ad8\u5929\u5065\uff0c\u603b\u662f\u4e24\u4e2a\u5b9e\u5728\u7684\u4eba\uff0c\u600e\u4f1a\u6709\u8fd9\u79cd\u611f\u89c9\u5462?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1b\u201c\u6211\u597d\u50cf\u4e00\u4e2a\u5bc4\u751f\u4eba\uff0c\u5bc4\u5728\u9ad8\u5929\u5065\u7684\u8eab\u4e0a\uff0c\u672c\u4f53\u5931\u53bb\u4e86\uff0c\u6211\u5c31\u6709\u7740\u832b\u7136\u65e0\u4f9d\u7684\u98d8\u6d6e\uff0c\u8fd9\u5c31\u50cf\u4e00\u4e2a\u505a\u4e86\u6570\u5341\u5e74\u5974\u624d\u7684\u4eba\uff0c\u5ffd\u7136\u95f4\u8981\u6211\u53bb\u505a\u4e3b\u4e00\u4ef6\u4ec0\u4e48\u4e8b\uff0c\u5f88\u96be\u62ff\u5b9a\u4e3b\u610f\u3002\u201d \u3000\u3000\u9ec4\u7075\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u9053\u7406\u5f88\u6df1\u5965\uff0c\u4f46\u4e0d\u96be\u7406\u89e3\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u5509!\u4f60\u80fd\u660e\u767d\u5c31\u597d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u73b0\u5728\uff0c\u8c08\u8c08\u6211\u4eec\u4e4b\u95f4\u7684\u4e8b\u5427\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u55ef!\u6211\u4eec\u4e4b\u95f4\uff0c\u6709\u4e9b\u4ec0\u4e48\u4e8b\u597d\u8c08\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5f88\u591a\uff0c\u5f88\u591a\uff0c\u8b6c\u5982\u8bf4\uff0c\u4f60\u73b0\u5728\u4f5c\u4f55\u6253\u7b97?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a \u201c\u6ca1\u6709\uff0c\u6211\u73b0\u5728\uff0c\u751a\u81f3\u60f3\u4e0d\u51fa\uff0c \u81ea\u5df1\u5e94\u8be5\u5230\u54ea\u91cc\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u8ddf\u6211\u8d70\u2026\u2026\u201d \u3000\u3000\u9ece\u8679\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u8ddf\u4f60\u8d70\uff0c\u5230\u54ea\u91cc\u53bb\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5929\u5730\u5982\u6b64\u4e4b\u5927\uff0c\u4f55\u5904\u4e0d\u53ef\u5b89\u8eab\u7acb\u547d\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u6709\u4e00\u6761\u547d\uff0c\u4f46\u6211\u60f3\u4e0d\u51fa\u5982\u4f55\u5b89\u8eab\u3002\u201d \u3000\u3000\u9ec4\u7075\u5ffd\u7136\u95f4\u53d1\u89c9\u4e86\u4e00\u4ef6\u4e8b\uff0c\u8fd9\u4e2a\u4eba\uff0c\u5e76\u975e\u50bb\u5b50\uff0c\u4f46\u4ed6\u5374\u771f\u662f\u90a3\u79cd\u65e0\u6cd5\u5b89\u6392\u81ea\u5df1\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u8981\u4f9d\u9644\u3002 \u3000\u3000\u591a\u5c11\u5e74\u6765\uff0c\u4ed6\u90fd\u6ca1\u6709\u81ea\u7acb\u8fc7\uff0c\u4e00\u65e6\u8981\u4ed6\u81ea\u7acb\u4e86\uff0c\u4ed6\u6709\u7740\u4e0d\u77e5\u5982\u4f55\u6d3b\u4e0b\u53bb\u7684\u611f\u89c9\u3002 \u3000\u3000\u9ec4\u7075\u7a81\u7136\u4f38\u51fa\u624b\u53bb\uff0c\u6293\u5411\u9ece\u8679\u3002 \u3000\u3000\u9ece\u8679\u4e00\u95ea\u8eab\uff0c\u50cf\u4e00\u9635\u98ce\u4f3c\u7684\uff0c\u98d8\u4e86\u51fa\u53bb\uff0c\u98d8\u51fa\u4e86\u7a97\u5916\u3002 \u3000\u3000\u9ec4\u7075\u770b\u5f97\u4e00\u5446\u3002 \u3000\u3000\u8fd9\u7b49\u5feb\u901f\u3001\u8f7b\u7075\u7684\u8eab\u6cd5\uff0c\u4e0d\u4f46\u9ec4\u7075\u81ea\u53f9\u5f17\u5982\uff0e\u800c\u4e14\uff0c\u9ec4\u7075\u611f\u89c9\u4e2d\uff0c\u81ea\u5df1\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u8fd9\u79cd\u98d8\u5ffd\u7684\u8eab\u6cd5\u3002 \u3000\u3000\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9ec4\u7075\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u9ece\u8679\uff0c\u4f60\u8fdb\u6765\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u6211\u4e0d\u60f3\u548c\u4eba\u6253\u67b6\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u4e0d\u548c\u4f60\u6253\u67b6\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u54e6!\u4f60\u4e0d\u662f\u521a\u521a\u51fa\u624b\u6253\u6211\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u6211\u53ea\u662f\u60f3\u8bd5\u8bd5\u4f60\u7684\u8eab\u6cd5\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a \u201c\u6211\u4e0d\u77e5\u9053\u81ea\u5df1\u7684\u672c\u9886\u6709\u591a\u5927\uff0c\u6211\u4ece\u6765\u6ca1\u6709\u5355\u72ec\u7684\u548c\u4eba\u52a8\u8fc7\u624b\u3002\u201d \u9ec4\u7075\u9053\uff1a \u201c\u4f60\u7684\u6b66\u529f\u5f88\u9ad8\uff0c\u5355\u4ee5\u4f60\u7684\u624b\u6cd5\uff0c\u8eab\u6cd5\u800c\u8bba\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u5728\u9ad8\u5929\u5065\u4e4b\u4e0b\u3002\u201c \u9ece\u8679\u9053\uff1a\u201c\u4e0d!\u6211\u6ca1\u6709\u90a3\u4e48\u9ad8\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ece\u8679\uff0c\u6211\u4eec\u597d\u597d\u8c08\u8c08\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u53ef\u4ee5\uff0c\u4f46\u4f60\u4e0d\u80fd\u5bf9\u6211\u51fa\u624b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u6211\u7b54\u5e94\u4f60\uff0c\u4e0d\u5bf9\u4f60\u51fa\u624b\u3002\u201d \u3000\u3000\u4f46\u89c1\u4eba\u5f71\u4e00\u95ea\uff0c\u9ece\u8679\u91cd\u53c8\u56de\u5230\u5ba4\u4e2d\u3002 \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9ece\u8679\uff0c\u80fd\u4e0d\u80fd\u544a\u8bc9\u6211\uff0c\u4f60\u662f\u5982\u4f55\u5e2e\u52a9\u9ad8\u5929\u5065\u548c\u4eba\u52a8\u624b\u7684?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u53ef\u4ee5\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u5728\u4e0b\u6d17\u8033\u606d\u542c\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201d\u4ed6\u548c\u4eba\u52a8\u624b\u65f6\uff0c\u6211\u4e00\u76f4\u5728\u4ed6\u7684\u5de6\u8fd1\uff0c\u5f53\u4ed6\u9700\u8981\u6211\u65f6\uff0c\u6211\u5c31\u51fa\u624b\u4e86\uff0c\u4e0d\u8fc7\uff0c\u6211\u4ece\u6765\u6ca1\u6709\u6740\u8fc7\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u6740\u8fc7\u4eba?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u5bf9!\u6211\u6ca1\u6709\u6740\u8fc7\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u90a3\u4f60\u53c8\u5982\u4f55\u5e2e\u52a9\u9ad8\u5929\u5065\u5462?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u53ea\u662f\u70b9\u4e86\u90a3\u4e2a\u4eba\u7684\u7a74\u9053\uff0c\u6216\u662f\u963b\u788d\u4e00\u4e0b\u4ed6\u7684\u653b\u52bf\uff0c\u7136\u540e\uff0c\u9ad8\u5929\u5065\u5c31\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u55ef!\u4ee5\u9ad8\u5929\u5065\u7684\u6b66\u529f\uff0c\u5982\u82e5\u6709\u4e00\u4e1d\u53ef\u4e58\u4e4b\u673a\uff0c\u4ed6\u5c31\u53ef\u53d6\u4eba\u6027\u547d\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u4f5c\u4f55\u6253\u7b97?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u6ca1\u6709\u6253\u7b97\uff0c\u6211\u53ea\u662f\u5728\u60f3\uff0c\u4eca\u540e\u8981\u5230\u54ea\u91cc\u53bb?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ece\u8679\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u8fd9\u70df\u9501\u697c\u4e2d\u4eba\u90fd\u6b7b\u4e86\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u53ea\u6709\u4e24\u4e2a\u4eba\u6d3b\u7740\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u8fd9\u91cc\u4f60\u4e0d\u80fd\u518d\u4f4f\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u5f88\u70e6\u607c\uff0c\u6211\u4e0d\u77e5\u4eca\u540e\u5982\u4f55\u751f\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u4f60\u6709\u7b2c\u4e00\u6d41\u7684\u6b66\u529f\uff0c\u5929\u4e0b\u4efb\u4f55\u5730\u65b9\uff0c\u4f60\u90fd\u53ef\u4ee5\u53bb\u5f97\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u53ea\u662f\u4e00\u4e2a\u5f71\u5b50\uff0c\u5931\u53bb\u4e86\u672c\u4f53\u7684\u5f71\u5b50\uff0c\u5982\u4f55\u72ec\u81ea\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u5fc3\u5934\u9707\u52a8\u3002 \u3000\u3000\u9ece\u8679\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u770b\u6765\uff0c\u6211\u53ea\u6709\u518d\u627e\u4e00\u4e2a\u5730\u65b9\u4e86?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u627e\u4e00\u4e2a\u4ec0\u4e48\u5730\u65b9?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u627e\u4e00\u4e2a\u53ef\u4ee5\u8ba9\u6211\u4f9d\u9644\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u627e\u4e00\u4e2a\u53ef\u4ee5\u4f7f\u4f60\u4f9d\u9644\u7684\u672c\u4f53\u3002\u201d \u3000\u3000\u9ece\u8679\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u597d\u50cf\u662f\u5427!\u6211\u603b\u4e0d\u80fd\u72ec\u81ea\u7684\u751f\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u9ec4\u7075\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u8a00\u8c08\u6b63\u5e38\uff0c\u4e0d\u50bb\u4e0d\u5446\uff0c\u4f46\u4ed6\u5374\u770b\u4e0a\u53bb\uff0c\u662f\u90a3\u4e48\u602a\u602a\u7684\uff0c\u8be5\u4e0d\u8be5\u8ba9\u4ed6\u6d3b\u4e0b\u53bb?\u201d \u3000\u3000\u9ec4\u7075\u7684\u7ecf\u9a8c\u5e7f\u535a\uff0c\u89c1\u8fc7\u4e86\u5404\u8272\u5404\u6837\u7684\u6c5f\u6e56\u602a\u4eba\uff0c\u4f46\u5374\u4ece\u672a\u60f3\u5230\u8fc7\uff0c\u4e16\u4e0a\u4f1a\u6709\u8fd9\u6837\u7684\u4eba\u3002 \u3000\u3000\u53ea\u542c\u5230\u9ece\u8679\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u8981\u8d70\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6162\u7740\uff0c\u4f60\u8981\u5230\u54ea\u91cc\u53bb?\u201d \u3000\u3000\u4ee5\u4ed6\u7684\u8eab\u624b\uff0c\u8981\u6740\u4ed6\u53ea\u6015\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002\u9ec4\u7075\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u9ece\u8679\uff0c\u4f60\u662f\u4e00\u4e2a\u4eba\uff0c\u6d3b\u751f\u751f\u7684\u4eba\uff0c\u4f60\u6709\u6b66\u529f\uff0c\u548c\u522b\u4eba\u6ca1\u6709\u4ec0\u4e48\u4e0d\u540c\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u81ea\u7acb\u8d77\u6765\u5462?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u81ea\u7acb?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bf9\u554a!\u548c\u522b\u4eba\u4e00\u6837\uff0c\u8fc7\u6b63\u5e38\u7684\u751f\u6d3b\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u6211\u60f3\u4e0d\u51fa\u8981\u5982\u4f55\u624d\u80fd\u81ea\u7acb?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u9ece\u8679\uff0c\u9ad8\u5929\u5065\u6b7b\u4e86\uff0c\u4f7f\u4f60\u89c9\u7740\u65e0\u5904\u53ef\u4ee5\u4f9d\u9644\uff0c\u4f46\u4e5f\u7ed9\u4e86\u4f60\u81ea\u7531\uff0c\u4f60\u53ef\u4ee5\u53d1\u73b0\u81ea\u6211\uff0c\u53ef\u4ee5\u60f3\u6cd5\u5b50\u8fc7\u72ec\u7acb\u7684\u751f\u6d3b\u3002\u201d \u3000\u3000\u9ece\u8679\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a \u201c\u6211\u6b63\u5728\u5c3d\u529b\u5c1d\u8bd5\uff0e\u5e0c\u671b\u6211\u80fd\u8fc7\u81ea\u5df1\u7684\u751f\u6d3b\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u9ece\u8679\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u6211\u60f3\u95ee\u95ee\u4f60?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff01\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u60f3\u77e5\u9053\uff0c\u4f60\u4e3a\u4ec0\u4e48\u5ffd\u7136\u4e0d\u80af\u5e2e\u52a9\u9ad8\u5929\u5065\u4e86\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u63a5\u9053\uff1a \u201c\u4f60\u662f\u4e0d\u662f\u53d1\u89c9\u4e86\u9ad8\u5929\u5065\u505a\u7684\u4e8b\u60c5\u4e0d\u5bf9\u3002\u201d \u9ece\u8679\u6447\u6447\u5934\u9053\uff1a \u201c\u6211\u4e0d\u77e5\u9053\uff0c\u6211\u53ea\u662f\u4e0d\u60f3\u5e2e\u52a9\u4ed6\u6740\u5bb3\u90a3\u4e24\u4f4d\u59d1\u5a18\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u5f53\u4ed6\u53ec\u6211\u51fa\u624b\u65f6\uff0c\u6211\u6ca1\u6709\u51fa\u624b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u5982\u51fa\u624b\u4e86\uff0c\u662f\u4e0d\u662f\u80fd\u591f\u6740\u4e86\u5979\u4eec\u4e24\u4f4d\u59d1\u5a18\u5462?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u5982\u51fa\u624b\uff0c\u81f3\u5c11\uff0c\u9ad8\u5929\u5065\u53ef\u4ee5\u4e0d\u6b7b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ece\u8679\uff0c\u4f60\u8fd9\u6837\u505a\uff0c\u53ef\u6709\u4ec0\u4e48\u539f\u56e0\u5417?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u6211\u662f\u89c9\u7740\u90a3\u4e24\u4e2a\u59d1\u5a18\u5f88\u597d\u770b\uff0c\u5982\u82e5\u5979\u4eec\u6b7b\u4e86\uff0c\u5c31\u4e0d\u90a3\u4e48\u597d\u770b\u4e86\u3002\u201d \u3000\u3000\u8fd9\u662f\u5927\u51fa\u610f\u6599\u4e4b\u5916\u7684\u4e00\u4e2a\u7406\u7531\uff0c\u542c\u5f97\u9ec4\u7075\u5446\u4e86\u4e00\u5446\u3002 \u3000\u3000\u8fd9\u4e2a\u5f71\u5b50\u4eba\u9ece\u8679\uff0c\u7adf\u7136\u80fd\u5206\u8fa8\u51fa\u5973\u4eba\u7684\u7f8e\u4e11\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u660e\u663e\u7684\u662f\u975e\u89c2\u5ff5\uff0c\u4f46\u5374\u6709\u5206\u8fa8\u7f8e\u3001\u4e11\u7684\u80fd\u529b\u3002 \u3000\u3000\u9ec4\u7075\u5fc3\u4e2d\u6697\u6697\u5206\u6790\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u7ecf\u8fc7\u4e86\u4e00\u756a\u4ea4\u8c08\u4e4b\u540e\uff0c\u4ed6\u53d1\u89c9\u4e86\u9ece\u8679\u8fd9\u4e2a\u4eba\uff0c\u4e0d\u662f\u4e2a\u5f88\u611a\u8822\u7684\u4eba\u3002 \u3000\u3000\u4f7f\u9ec4\u7075\u60f3\u4e0d\u901a\u7684\u662f\uff0c\u8fd9\u4e2a\u4eba\u600e\u4e48\u4f1a\u6ca1\u6709\u72ec\u7acb\u7684\u60f3\u6cd5\u3002 \u3000\u3000\u96be\u9053\u4e16\u4e0a\u771f\u6709\u8fd9\u79cd\u4eba\uff0c\u5fc5\u987b\u8981\u4f9d\u9644\u5728\u53e6\u5916\u4e00\u4e2a\u4eba\u7684\u8eab\u4e0a\uff0c\u624d\u80fd\u591f\u751f\u5b58\u5417? \u3000\u3000\u9ec4\u7075\u6709\u5f88\u591a\u7684\u4e8b\u60c5\uff0c\u60f3\u4e0d\u660e\u767d\u3002 \u3000\u3000\u4f46\u76ee\u4e0b\u7684\u60c5\u5f62\uff0c\u5374\u4e0d\u5141\u8bb8\u4ed6\u6709\u592a\u591a\u7684\u65f6\u95f4\u53bb\u60f3\u3002 \u3000\u3000\u6446\u5728\u76ee\u524d\u7684\u95ee\u9898\uff0c\u5fc5\u987b\u8981\u89e3\u51b3\u9ece\u8679\u3002 \u3000\u3000\u4ee5\u9ece\u8679\u8868\u73b0\u51fa\u7684\u8f7b\u529f\u8eab\u6cd5\uff0c\u5982\u82e5\u548c\u4ed6\u7684\u6b66\u529f\u914d\u5408\uff0c\u5c31\u7b97\u9ec4\u7075\u65bd\u5c55\u51fa\u5168\u529b\u4e00\u640f\uff0c\u4e5f\u672a\u5fc5\u80fd\u591f\u80dc\u5f97\u8fc7\u9ece\u8679\u3002 \u3000\u3000\u8fd9\u662f\u5b8c\u5168\u4f7f\u4eba\u65e0\u6cd5\u4e86\u89e3\u7684\u4eba\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u81ea\u5df1\u53ea\u627f\u8ba4\u662f\u4e00\u4e2a\u5f71\u5b50\uff0c\u4e00\u4e2a\u4f9d\u9644\u5728\u522b\u4eba\u8eab\u4e0a\u751f\u5b58\u7684\u5f71\u5b50\u3002 \u3000\u3000\u4f46\u4ed6\u7684\u8c08\u8bdd\u548c\u4e3e\u6b62\uff0c\u5374\u53c8\u662f\u4e00\u4e2a\u6d3b\u751f\u751f\u7684\u4eba\u3002 \u3000\u3000\u9ec4\u7075\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u9ece\u8679\uff0c\u4f60\uff0c\u613f\u4e0d\u613f\u548c\u6211\u4eec\u5408\u4f5c?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u5408\u4f5c\uff0c\u6211\u4e5f\u80fd\u548c\u4eba\u5408\u4f5c\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u4eec\u600e\u4e48\u4e00\u4e2a\u5408\u4f5c\u6cd5\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ece\u8679\uff0c\u5e0c\u671b\u4f60\u80fd\u76f8\u4fe1\u6211\uff0c\u8ddf\u6211\u4eec\u4e00\u8d77\u79bb\u5f00\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u79bb\u5f00\uff0c\u5230\u54ea\u91cc?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4f60\u5e38\u548c\u9ad8\u5929\u5065\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u60f3\u6765\u5bf9\u6c5f\u6e56\u4e0a\u5341\u5206\u719f\u6089\u4e86\u3002\u201d \u9ece\u8679\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u5f88\u5c11\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u65e2\u7136\u662f\u9ad8\u5929\u5065\u7684\u5f71\u5b50\uff0c\u96be\u9053\u5c31\u6ca1\u6709\u8ddf\u7740\u4ed6\u4eec?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a \u201c\u6211\u5f88\u5c11\u8ddf\u7740\u4ed6\u8d70\uff0c\u7eb5\u7136\u6211\u8ddf\u7740\u4ed6\u65f6\uff0c\u4e5f\u53ea\u662f\u5750\u5728\u4e00\u8f86\u7bf7\u8f66\u4e4b\u4e0a\u3002\u201d \u9ec4\u7075\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u8fd9\u8bc1\u660e\u4e86\u4f60\u4eec\u8fd8\u662f\u4e24\u4e2a\u4eba\uff0c\u4e24\u4e2a\u4e0d\u540c\u7684\u4e2a\u4f53\u3002\u201d \u9ece\u8679\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ece\u8679\uff0c\u8d70\u5427!\u6211\u5e26\u4f60\u53bb\u89c1\u89c1\u4ed6\u4eec\u3002\u201d \u3000\u3000\u9ece\u8679\u5947\u9053\uff1a\u201c\u89c1\u4ec0\u4e48?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5f88\u591a\u4eba\uff0c\u5305\u62ec\u4f60\u770b\u5230\u7684\u90a3\u4e24\u4e2a\u5973\u7684\u3002\u201d \u3000\u3000\u9ece\u8679\u4f3c\u6709\u52a8\u5fc3\u7684\u611f\u89c9\uff0c\u4f46\u6c89\u541f\u4e86\u4e00\u9635\u4e4b\u540e\uff0c \u53c8\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u53bb\uff0c\u6211\u8981\u8d70\u4e86\u3002\u201d \u8eab\u5f71\u4e00\u95ea\uff0c\u4eba\u53c8\u7a7f\u7a97\u800c\u53bb\u3002 \u3000\u3000\u9ec4\u7075\u671b\u7740\u7a97\u5916\uff0c\u51fa\u4e86\u4e00\u9635\u5b50\u795e\uff0c\u7f13\u6b65\u884c\u4e0b\u4e86\u70df\u9501\u697c\u3002 \u3000\u3000\u70df\u9501\u697c\u4e0b\uff0c\u65e9\u5df2\u6709\u4e86\u4e25\u5bc6\u7684\u5e03\u7f6e\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u3001\u9ad8\u5e7d\u5170\u7b49\u5206\u522b\u5e03\u5b88\u5728\u70df\u9501\u697c\u7684\u56db\u5468\u3002 \u3000\u3000\u4efb\u4f55\u4e00\u4e2a\u65b9\u4f4d\u3001\u89d2\u5ea6\uff0c \u53ea\u8981\u6709\u4eba\u4e0b\u6765\uff0c\u90fd\u53ef\u4ee5\u770b\u5f97\u5341\u5206\u6e05\u695a\u3002 \u4f55\u5bd2\u8863\u8fce\u4e86\u4e0a\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u771f\u7684\u8fd8\u6709\u4eba\u5417?\u201d \u3000\u3000\u5170\u767d\u5feb\u6b65\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u70df\u9501\u697c\u4e0a\u662f\u5426\u6709\u4eba\uff0c\u5979\u662f\u6700\u5173\u5fc3\u4e86\u3002 \u3000\u3000\u5979\u6697\u4e2d\u6570\u8fc7\u70df\u9501\u697c\u4e0a\u7684\u4eba\uff0c\u4e8b\u540e\uff0c\u4e5f\u6e05\u70b9\u8fc7\u6b7b\u4ea1\u7684\u4eba\u6570\uff0c\u4e00\u5171\u4e8c\u5341\u4e5d\u4e2a\u4eba\uff0c\u4e00\u4e2a\u4e0d\u5c11\u3002 \u3000\u3000\u5bf9\u7389\u7fe0\u7684\u6b7b\u4ea1\uff0c\u5170\u767d\u5341\u5206\u4f24\u5fc3\uff0c\u73b0\u5728\uff0c\u5979\u4e00\u76f4\u5728\u6d41\u7740\u6cea\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5979\u4ecd\u7136\u5e26\u7740\u6ee1\u8138\u7684\u6cea\u75d5\u3002 \u3000\u3000\u5979\u574e\u5777\u7684\u8eab\u4e16\uff0c\u51c4\u51c9\u7684\u5f80\u4e8b\uff0c\u4f7f\u5979\u6709\u4e00\u79cd\u8d85\u8d8a\u5979\u5e74\u9f84\u7684\u575a\u5f3a\u3002 \u3000\u3000\u867d\u7136\u7389\u7fe0\u4e0d\u662f\u4eb2\u59ca\u59b9\uff0c\u4f46\u7389\u7fe0\u7ed9\u4e88\u5979\u7684\u7231\u62a4\uff0c\u8d85\u8fc7\u4e86\u4eb2\u59ca\u59ca\u7684\u60c5\u611f\u3002 \u3000\u3000\u5c24\u5176\u662f\u5728\u70df\u9501\u697c\u4e0a\u8fd9\u4e00\u6bb5\u65e5\u5b50\uff0c\u7389\u7fe0\u60f3\u5c3d\u4e86\u529e\u6cd5\uff0c\u7528\u51fa\u4e86\u6df7\u8eab\u89e3\u6570\uff0c\u4fdd\u5168\u4e86\u5979\u7684\u6e05\u767d\u3002 \u3000\u3000\u9ad8\u5929\u5065\u50cf\u4e00\u53ea\u72fc\uff0c\u7389\u7fe0\u3001\u5170\u767d\u5c31\u50cf\u4e24\u5757\u6446\u5728\u72fc\u53e3\u524d\u7684\u8089\u3002 \u3000\u3000\u9ad8\u5929\u5065\u968f\u65f6\u53ef\u4ee5\u628a\u5979\u541e\u4e0b\u53bb\u3002 \u3000\u3000\u800c\u5170\u767d\u4e5f\u65e9\u5df2\u6697\u4e0b\u4e86\u51b3\u5fc3\uff0c\u4e00\u65e6\u5230\u4e86\u65e0\u6cd5\u9003\u907f\u7684\u65f6\u5019\uff0c\u5c31\u4ee5\u8eab\u76f8\u6b89\u3002 \u3000\u3000\u5979\u4e3a\u6b64\u4e0d\u6d17\u6fa1\uff0c\u4efb\u5b83\u84ec\u9996\u57a2\u9762\uff0c\u800c\u4e14\uff0c\u4e00\u76f4\u6697\u4e2d\u85cf\u7740\u4e00\u628a\u5315\u9996\uff0c\u4f46\u7389\u7fe0\u603b\u5728\u4e8b\u60c5\u5c06\u8981\u53d1\u751f\u65f6\uff0c\u89e3\u6551\u4e86\u5979\u7684\u5371\u673a\u3002 \u3000\u3000\u8fd9\u4efd\u6df1\u631a\u7684\u5fd9\u610f\uff0c\u50cf\u70d9\u94c1\u4e00\u6837\uff0c\u70d9\u5728\u5170\u767d\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u5979\u5feb\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u7ad9\u5728\u53f6\u957f\u9752\u7684\u8eab\u4fa7\uff0c\u5f88\u7528\u5fc3\u7684\u542c\u7740\uff0c\u4e0d\u65ad\u7684\u6cea\u73e0\u513f\uff0c\u987a\u7740\u4e24\u816e\u6eda\u4e0b\u6765\u3002 \u3000\u3000\u9ec4\u7075\u7684\u795e\u60c5\u5f88\u4e25\u8083\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u4eba\u2026\u2026\u201d \u3000\u3000\u5170\u767d\u63a5\u9053\uff1a\u201c\u771f\u7684\u6709\u4eba?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u6211\u65e0\u6cd5\u786e\u5b9a\uff0c\u4ed6\u7b97\u4e0d\u7b97\u4e00\u4e2a\u4eba\uff0c\u4f46\u4ed6\u786e\u662f\u4e00\u4e2a\u6d3b\u751f\u751f\u7684\u4eba\u3002\u201d \u5434\u53cc\u5973\u4e00\u626c\u67f3\u7709\u513f\uff0c\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4ed6\u81ea\u79f0\u662f\u4e00\u4e2a\u5f71\u5b50\uff0c\u4e00\u4e2a\u5bc4\u6258\u5728\u9ad8\u5929\u5065\u8eab\u4e0a\u7684\u5f71\u5b50\uff0c\u9ad8\u5929\u5065\u6b7b\u53bb\u4e86\u4e4b\u540e\uff0c\u4ed6\u5c31\u5931\u53bb\u4f9d\u9644\u6e38\u8361\u5728\u70df\u9501\u697c\u4e0a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u5929\u9b54\u95e8\u4e2d\uff0c\u6709\u4e00\u79cd\u8840\u5f71\u5206\u8eab\u5947\u672f\uff0c\u4f46\u90a3\u662f\u4e24\u4e2a\u4e0d\u540c\u7684\u4eba\uff0c\u7ecf\u8fc7\u4e00\u79cd\u5947\u672f\u7684\u63a7\u5236\uff0c\u53ef\u4ee5\u628a\u5b83\u53d8\u6210\u53e6\u4e00\u4e2a\u4eba\u7684\u5f62\u8c61\uff0c\u5c31\u50cf\u4e00\u4e2a\u5f71\u5b50\u4e00\u6837\uff0c\u5931\u53bb\u4e86\u4ed6\u81ea\u5df1\u7684\u610f\u5fd7\u3002\u96be\u9053\u9ad8\u5929\u5065\u4e5f\u4f1a\u6b64\u672f\u3002\u201d \u3000\u3000\u9ec4\u7075\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6709\u4e00\u70b9\uff0c\u4f46\u53c8\u4e0d\u5b89\u5168\u76f8\u540c\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8bf4\u8bf4\u770b\uff0c\u4ed6\u662f\u600e\u4e48\u6837\u4e00\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ed6\u6709\u6781\u9ad8\u660e\u7684\u8f7b\u529f\u8eab\u6cd5\uff0c\u884c\u52a8\u5982\u98ce\uff0c\u4fdd\u6301\u4e86\u76f8\u5f53\u7684\u6e05\u9192\uff0c\u8bf4\u8bdd\u4ea6\u6709\u6761\u7406\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff0c\u6211\u600e\u4e48\u4e00\u70b9\u90fd\u4e0d\u77e5\u9053\uff1a\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u8fd9\u4e5f\u8bb8\u662f\u9ad8\u5929\u5065\u4e00\u4e2a\u7edd\u5927\u7684\u673a\u5bc6\uff0c\u77e5\u9053\u7684\u4eba\uff0c\u4e0d\u4f1a\u592a\u591a\uff0c\u4ed6\u4e00\u5411\u6218\u65e0\u4e0d\u80dc\uff0c\u5f97\u8fd9\u4e2a\u5f71\u5b50\u7684\u52a9\u529b\u5f88\u5927\uff0c\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u70df\u9501\u697c\u4e0a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u9519!\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54b1\u4eec\u53bb\u627e\u4ed6\u51fa\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u548c\u4ed6\u8c08\u4e86\u5f88\u4e45\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ed6\u4e0d\u613f\u4e0b\u6765\uff0c\u4e5f\u4e0d\u613f\u89c1\u6211\u4eec\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u6709\u4e00\u4ef6\u4e8b\uff0c\u6211\u6ca1\u6709\u544a\u8bc9\u4f60\u4eec\uff0c \u4ed6\u8bf4\uff0c \u4ed6\u6ca1\u6709\u5e2e\u52a9\u9ad8\u5929\u5065\u6740\u4f60\u4eec\u4e24\u4e2a\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u54e6!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u80cc\u53db\u4e86\u9ad8\u5929\u5065?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5821\u4e3b\u53ef\u66fe\u95ee\u8fc7\u4ed6\uff0c\u4e3a\u4ec0\u4e48?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5509!\u8bf4\u6765\u5f88\u7b80\u5355\uff0c \u53ea\u4e3a\u4e86\u4f60\u4eec\u5f88\u597d\u770b\u3002\u201d \u9ad8\u5e7d\u5170\u7728\u52a8\u4e86\u4e00\u4e0b\u773c\u775b\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\uff0c\u597d\u5947\u602a\uff0c\u201d \u3000\u3000\u8f7b\u53f9\u4e86\u4e00\u58f0\uff0c\u9ec4\u7075\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u4ed6\u672c\u6765\u662f\u4e00\u4e2a\u5f88\u5b8c\u597d\u7684\u4eba\uff0c\u4e00\u4e2a\u6b63\u5e38\u7684\u4eba\uff0c\u800c\u4e14\uff0c\u4e5f\u6709\u4e03\u60c5\u516d\u6b32\uff0c\u5206\u8fa8\u7f8e\u4e11\u7684\u80fd\u529b\uff0c\u62d2\u7edd\u9ad8\u5929\u5065\u7684\u62db\u547c\uff0c\u90a3\u8bf4\u660e\u4e86\uff0c \u4ed6\u4e5f\u6709\u5145\u5206\u7684\u81ea\u4e3b\u80fd\u529b\uff0c\u6211\u60f3\u4e0d\u901a\u7684\u662f\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u7adf\u7136\u89c9\u7740\u81ea\u5df1\u662f\u4e00\u4e2a\u5f71\u5b50\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u8fd9\u548c\u6211\u4eec\u5929\u9b54\u95e8\u8840\u5f71\u5206\u8eab\u5947\u672f\u4e0d\u540c\uff0c\u4ed6\u5177\u6709\u4e00\u4e2a\u5b8c\u7f8e\u7684\u6761\u4ef6\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u5bf9\u4e8e\u8fd9\u6837\u4e00\u4e2a\u4eba\uff0c \u54b1\u4eec\u5e94\u8be5\u5982\u4f55\u7684\u5904\u7f6e\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u4e00\u76f4\u65e0\u6cd5\u786e\u5b9a\u8be5\u600e\u4e48\u529e\uff0c\u6240\u4ee5\u624d\u548c\u8bf8\u4f4d\u5546\u91cf\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u56de\u987e\u4e86\u4f55\u5bd2\u8863\u4e00\u773c\uff0c\u5e0c\u671b\u4ed6\u8bf4\u8bdd\u3002 \u3000\u3000\u5979\u5ffd\u7136\u53d1\u89c9\u81ea\u5df2\u8bf4\u8bdd\u592a\u591a\u4e86\uff0c\u5e94\u8be5\u591a\u5c0a\u91cd\u4e08\u592b\u4e00\u4e9b\uff0c\u542c\u542c\u4ed6\u7684\u610f\u89c1\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u8fd9\u4e2a\u4eba\u5f88\u5947\u602a\uff0c\u6700\u597d\u80fd\u628a\u4ed6\u751f\u64d2\u4f4f\uff0c\u95ee\u4e2a\u660e\u767d\u3002\u201d \u9ad8\u5e7d\u5170\u7b11\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5bf9!\u8fd9\u79cd\u60f3\u6cd5\u4e0d\u9519\uff0c\u4e0d\u8981\u4f24\u5bb3\u4ed6\uff0c\u53ea\u628a\u4ed6\u751f\u64d2\u4e86\u5c31\u7b97\u5566\u3002\u201d \u3000\u3000\u9ec4\u7075\u6447\u6447\u5934\uff0c\u9053\uff1a \u201c\u5355\u4ee5\u4ed6\u7684\u8f7b\u8eab\u529f\u592b\u800c\u8a00\uff0c\u53ea\u6015\u4e0d\u5728\u4f60\u6211\u4e4b\u4e0b\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u7684\u6b66\u529f\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u548c\u6211\u52a8\u8fc7\u624b\uff0c\u4f46\u6211\u7a81\u7136\u4f38\u624b\u4e00\u6293\uff0c\u6ca1\u6709\u6293\u5f97\u4f4f\u4ed6\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u60f3\u6293\u4ed6\uff0c\u8fd8\u8981\u5927\u8d39\u4e00\u756a\u624b\u811a\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u9664\u975e\u54b1\u4eec\u5168\u529b\u65bd\u4e3a\uff0c\u6293\u4ed6\u4e0d\u592a\u5bb9\u6613\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e2a\u4eba\u672c\u8eab\u867d\u7136\u65e0\u5bb3\uff0c\u4f46\u4ed6\u5982\u4f9d\u6258\u975e\u4eba\uff0c\u5c31\u53d8\u6210\u4e86\u4e00\u4e2a\u53ef\u6015\u7684\u6740\u624b\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u65e0\u5fc3\u4e3a\u6076\uff0c\u5374\u8840\u8165\u4e24\u624b\uff0c\u8fd9\u4e2a\u4eba\uff0c\u6ca1\u6709\u76ee\u6807\uff0c\u6ca1\u6709\u601d\u60f3\uff0c\u5fc5\u987b\u8981\u60f3\u6cd5\u5b50\u6536\u670d\u4e86\u4ed6\u3002\u201d \u3000\u3000\u9ec4\u7075\u6c89\u541f\u4e86\u4e00\u9635\uff0c\u9053\uff1a \u201c\u54b1\u4eec\u5206\u6210\u4e24\u6279\uff0c\u4e00\u6279\u4eba\uff0c\u4e0a\u697c\u53bb\uff0c\u4e00\u6279\u4eba\u624b\u5728\u697c\u4e0b\uff0c\u5e0c\u671b\u80fd\u4e00\u4e3e\u6210\u529f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u8981\u8bf7\u5821\u4e3b\u5206\u914d\u4e00\u4e0b\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u4f60\uff0c\u9ad8\u59d1\u5a18\uff0c\u6211\uff0c\u54b1\u4eec\u4e09\u4e2a\u4e0a\u53bb\uff0c\u5434\u59d1\u5a18\u548c\u53f6\u5144\u5f1f\u3001\u5c1a\u8001\u524d\u8f88\u7559\u5728\u697c\u4e0b\uff0c\u5168\u529b\u62e6\u622a\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u80fd\u4e0d\u80fd\u4f24\u4ed6?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6700\u597d\u662f\u751f\u64d2\u4ed6\uff0c\u6bd4\u4ed6\u8dd1\u4e86\u597d\u4e00\u4e9b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9ec4\u7075\u5f53\u5148\u800c\u884c\uff0c\u91cd\u767b\u4e0a\u70df\u9501\u697c\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u3001\u4f55\u5bd2\u8863\uff0c\u90fd\u5df2\u6697\u4f5c\u6212\u5907\u3002 \u3000\u3000\u56de\u987e\u4e86\u7389\u7fe0\u7684\u623f\u4e2d\uff0c\u9ec4\u7075\u9ad8\u58f0\u8bf4\u9053\uff1a \u201c\u9ece\u8679\uff0c\u6211\u4eec\u53c8\u6765\u770b\u4f60\u4e86\u3002\u201d \u6ca1\u6709\u4eba\u56de\u7b54\u3002 \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a \u201c\u9ece\u8679\uff0c\u6211\u548c\u4f60\u4ecd\u5728\u8fd9\u91cc\u8c08\uff0c\u9ad8\u59d1\u5a18\u6765\u770b\u4f60\uff0c\u53ea\u6709\u4e00\u4e2a\u7528\u5fc3\uff0c\u5e0c\u671b\u4f60\u548c\u6211\u4eec\u8c08\u8c08\u3002\u201d \u3000\u3000\u51fa\u4e86\u9ec4\u7075\u7684\u610f\u6599\u4e4b\u5916\uff0c\u9ece\u8679\u5ffd\u7136\u73b0\u51fa\u8eab\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u3001\u9ad8\u5e7d\u5170\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u2014\u4e2a\u7626\u7626\u7684\u3001\u77ee\u77ee\u7684\u4eba\uff0c\u7a81\u7136\u51fa\u73b0\u5728\u4e09\u4eba\u9762\u524d\u3002 \u3000\u3000\u4ed6\u4e24\u9053\u76ee\u5149\uff0c\u4e00\u76f4\u76ef\u6ce8\u5728\u9ad8\u5e7d\u5170\u8eab\u4e0a\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u53eb\u9ece\u8679?\u201d \u3000\u3000\u9ece\u8679\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u628a\u4f60\u7684\u4e8b\uff0c\u5168\u90e8\u544a\u8bc9\u4e86\u6211\u4eec\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u5509!\u6211\u77e5\u9053\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u8bf4\u7ed9\u4f60\u542c\u7684\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u9ece\u8679\uff0c\u6211\u4eec\u5e0c\u671b\u4f60\u771f\u6b63\u6210\u4e86\u4e00\u4e2a\u4eba\uff0c\u4e00\u4e2a\u5b8c\u5168\u72ec\u7acb\u7684\u4eba\u3002\u201d \u9ece\u8679\u9053\uff1a \u201c\u6211\u4e0d\u77e5\u9053\uff0c\u6211\u4f1a\u600e\u4e48\u6837\uff0c\u6211\u4e5f\u4e0d\u613f\u4f60\u4eec\u592a\u5173\u5fc3\u6211\u3002\u201d \u9ad8\u5e7d\u5170\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u9ece\u8679\uff0c \u4f60\u80cc\u53db\u4e86\u9ad8\u5929\u5065\uff0c\u90a3\u5df2\u8bf4\u660e\u4f60\u5df2\u81ea\u5df1\u53ef\u4ee5\u505a\u4e3b\u4e86\uff0c\u4f60\u5df2\u7ecf\u6740\u8fc7\u4e86\u4e0d\u5c11\u7684\u4eba\uff0c\u624b\u6cbe\u8840\u8165\uff0c\u8bba\u4f60\u7684\u6076\u884c\uff0c\u5c31\u8be5\u6740\u4e86\u4f60\uff0c\u4f46\u5ff5\u4f60\u662f\u65e0\u5fc3\u4e3a\u6076\uff0c\u53ef\u4ee5\u9976\u4f60\u4e0d\u6b7b\uff0c\u4f46\u5e0c\u671b\u4f60\u80fd\u4ece\u6b64\u4e0d\u518d\u6740\u4eba\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u6ca1\u6709\u6740\u8fc7\u4eba\uff0c\u6211\u5df2\u7ecf\u544a\u8bc9\u8fc7\u4f60\u4eec\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u9ece\u8679\uff0c\u8fc7\u53bb\u7684\uff0c \u5df2\u7ecf\u8fc7\u53bb\u4e86\uff0c\u6b64\u540e\uff0c\u6211\u4eec\u4e0d\u613f\u4f60\u518d\u505a\u5f71\u5b50\u3002\u201d \u3000\u3000\u9ece\u8679\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u505a\u5f71\u5b50\u8981\u5230\u54ea\u91cc\u53bb\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8ddf\u6211\u8d70\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u4e0d\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5ffd\u7136\u4e00\u4e2a\u8f6c\u8eab\uff0c\u75be\u5982\u98d8\u98ce\u822c\uff0c\u62e6\u5728\u4e86\u7a97\u524d\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9ece\u8679\uff0c\u53ea\u6015\u7531\u4e0d\u5f97\u4f60\u4e86\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u4f60\u4eec\u8981\u6740\u6211?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u8981\u53cd\u6297\uff0c\u6211\u4eec\u4e0d\u4f1a\u52a0\u5bb3\u4f60\uff0c\u53ea\u5e0c\u671b\u6539\u53d8\u4f60\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u5982\u82e5\u4e00\u5b9a\u8981\u4f9d\u9644\u5728\u4e00\u4e2a\u4eba\u7684\u8eab\u4e0a\uff0c\u90a3\u5c31\u9009\u62e9\u9ec4\u5821\u4e3b\u3002\u201d \u3000\u3000\u9ece\u8679\u6447\u6447\u5934\uff0c\u7a81\u7136\u98de\u8eab\u800c\u53bb\uff0c\u50cf\u4e00\u53ea\u7bad\u822c\uff0c\u5c04\u5411\u4e86\u9ec4\u7075\u3002 \u3000\u3000\u9ec4\u7075\u53f3\u624b\u4e00\u6325\uff0c\u62cd\u51fa\u4e00\u638c\u3002 \u3000\u3000\u9ece\u8679\u5411\u524d\u5954\u51b2\u7684\u8eab\u5b50\uff0c\u5ffd\u7136\u95f4\u5411\u4e00\u4fa7\u8f6c\u53bb\uff0c\u51b2\u5411\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u53cc\u624b\u9f50\u63a2\uff0c\u6293\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u65e8\u5728\u751f\u64d2\u5bf9\u65b9\uff0c\u6240\u4ee5\uff0c\u5e0c\u671b\u80fd\u6263\u4f4f\u9ece\u8679\u81c2\u3001\u8155\u5173\u8282\u3002 \u3000\u3000\u4f46\u9ece\u8679\u6ed1\u7684\u50cf\u4e00\u53ea\u6ce5\u9cc5\u4f3c\u7684\uff0c\u8f7b\u8f7b\u4e00\u95ea\uff0c\u7adf\u7136\u95ea\u907f\u5f00\u53bb\u3002 \u3000\u3000\u70df\u9501\u697c\u5171\u6709\u4e03\u5c42\uff0c\u73b0\u5728\uff0c\u4ed6\u4eec\u5728\u7b2c\u4e03\u5c42\u4e0a\u3002 \u3000\u3000\u7b2c\u4e03\u5c42\uff0c\u76f8\u5f53\u7684\u9ad8\u3002 \u3000\u3000\u5c31\u7b97\u6709\u5f88\u9ad8\u660e\u7684\u8f7b\u529f\uff0c\u4f46\u5728\u5982\u6b64\u9ad8\u7684\u697c\u4e0a\uff0c\u4e5f\u5f88\u96be\u4fdd\u6301\u529f\u529b\u7684\u5e73\u8861\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u75be\u5feb\u7684\u51b2\u5230\u7a97\u5b50\u524d\u9762\u3002 \u3000\u3000\u5411\u4e0b\u770b\u53bb\uff0c\u65e9\u5df2\u4e0d\u89c1\u4e86\u9ece\u8679\u7684\u8e2a\u5f71\u3002 \u3000\u3000\u770b\u4e0d\u5230\u9ece\u8679\uff0c\u4f46\u6709\u4e00\u70b9\u9ad8\u5e7d\u5170\u53ef\u4ee5\u80af\u5b9a\u7684\u662f\uff0c\u9ece\u8679\u6ca1\u6709\u6454\u5230\u697c\u4e0b\u3002 \u3000\u3000\u9ec4\u7075\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u4e0d\u89c1\u4e86\u4ed6\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u7684\u8f7b\u529f\uff0c\u5b9e\u5df2\u5230\u4e86\u51fa\u795e\u5165\u5316\u4e4b\u5883\uff0c\u751f\u64d2\u4ed6\u7684\u673a\u4f1a\uff0c\u53ea\u6015\u4e0d\u5927\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6740\u4ed6\uff0c\u4e5f\u4e0d\u592a\u5bb9\u6613\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u4e00\u8eb2\uff0c \u53ea\u6015\u4ed6\u4e0d\u4f1a\u518d\u548c\u54b1\u4eec\u89c1\u9762\u4e86\u3002\u201d \u4f55\u5bd2\u8863\u76ee\u671b\u697c\u9876\uff1b \u3000\u3000\u53ef\u80fd\u7684\u662f\uff0c\u9ece\u8679\u51b2\u51fa\u7a97\u5b50\u4e4b\u540e\uff0c\u7ffb\u8eab\u767b\u4e0a\u697c\u9876\u4e0a\u9762\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4e00\u58f0\u4e0d\u54cd\uff0c\u5ffd\u7136\u8dc3\u51fa\u7a97\u5916\uff0c\u4e00\u4e2a\u5012\u7ffb\uff0c\u4eba\u5df2\u8dc3\u4e0a\u5c4b\u9762\u3002 \u3000\u3000\u51dd\u76ee\u671b\u53bb\uff0c\u54ea\u91cc\u8fd8\u6709\u9ece\u8679\u7684\u5f71\u5b50\u3002 \u3000\u3000\u56de\u987e\u4e86\u4e00\u9635\uff0c\u53c8\u7ffb\u4e0b\u5c4b\u4e2d\u3002 \u3000\u3000\u6447\u6447\u5934\uff0c\u9ad8\u5e7d\u5170\u7f13\u7f13\u8131\u9053\uff1a\u201c\u6ca1\u6709\u770b\u5230\u4ed6\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u8fd9\u91cc\u6709\u4e03\u5c42\u697c\uff0c\u4ed6\u53ef\u4ee5\u968f\u4fbf\u8eb2\u5165\u54ea\u91cc\uff0c\u4ee5\u4ed6\u8eab\u8eaf\u4e4b\u77ee\u5c0f\uff0c\u8f7b\u529f\u4e4b\u5965\u5999\uff0c\u53ea\u6015\u662f\u5f88\u96be\u627e\u5230\u4ed6\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u8fd9\u4eba\u6ca1\u6709\u6076\u5ff5\uff0c \u4f46\u4ed6\u5374\u80fd\u505a\u51fa\u6700\u53ef\u6015\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u773c\u4e0b\u6700\u53ef\u6015\u7684\u4e8b\u60c5\uff0c\u5c31\u662f\u4ed6\u77e5\u9053\u8fd9\u70df\u9501\u697c\u4e0a\u7684\u79d8\u5bc6\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u4e9b\u4ec0\u4e48\u79d8\u5bc6\u5462\uff1b\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d\u50cf\u4ed6\u77e5\u9053\u9ad8\u5929\u5065\u7559\u4e0b\u7684\u836f\u5f15\u6240\u5b58\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5982\u662f\u4ed6\u71c3\u8d77\u836f\u5f15\uff0c\u8fd9\u5ea7\u697c\u5c31\u7206\u6210\u4e86\u98de\u7070\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u603b\u4e0d\u80fd\u54b1\u4eec\u4e3a\u4e86\u9ece\u8679\uff0c\u5927\u5bb6\u90fd\u628a\u6027\u547d\u8d4c\u5728\u8fd9\u91cc\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u81ea\u7136\u4e0d\u5b9c\u5982\u6b64\u3002\u201d \u3000\u3000\u7a81\u7136\u653e\u4f4e\u4e86\u58f0\u97f3\uff0c\u4f4e\u8a00\u6570\u8bed\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\uff0c\u4e0b\u4e86\u70df\u9501\u697c\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u867d\u7136\u5e38\u4f4f\u7b2c\u4e00\u5821\uff0c\u4f46\u6211\u5bf9\u5f88\u591a\u5730\u65b9\uff0c\u662f\u5426\u6709\u673a\u5173\u57cb\u4f0f\uff0c\u4e5f\u4e0d\u592a\u6e05\u695a\uff0c\u4f46\u6709\u4e00\u4e2a\u5730\u65b9\u6211\u77e5\u9053\uff0c\u90a3\u91cc\u7edd\u65e0\u57cb\u4f0f\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u5730\u65b9?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5170\u82b1\u5c45\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4f60\u4f4f\u7684\u5730\u65b9?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5bf9!\u9ad8\u5929\u5065\u751f\u524d\u66fe\u7ecf\u6d3e\u4eba\u5728\u6211\u90a3\u91cc\u57cb\u8bbe\u673a\u5173\uff0c\u4f46\u5374\u88ab\u6211\u6052\u7edd\u4e86\uff0c \u4ee5\u540e\uff0c \u53c8\u88ab\u6211\u53d1\u73b0\u4e00\u6b21\uff0c\u6211\u5c31\u6740\u4e86\u4e24\u4e2a\u4eba\uff0c\u624d\u771f\u6b63\u5b89\u9759\u4e0b\u6765\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u4ed6\u8981\u57cb\u4e9b\u4ec0\u4e48\u673a\u5173\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u7965\u7ec6\u60c5\u5f62\uff0c\u6211\u4e0d\u77e5\u9053\uff0c\u4e0d\u8fc7\uff0c\u6211\u77e5\u9053\uff0c\u4ed6\u751f\u524d\u80fd\u6697\u4e2d\u63a7\u5236\u7b2c\u4e00\u5821\u6bcf\u4e00\u4e2a\u5730\u65b9\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\uff0c\u679c\u7136\u5389\u5bb3\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u751f\u524d\uff0c\u5b9e\u5728\u5f88\u4fe1\u4efb\u6211\uff0c\u6709\u5f88\u591a\u79d8\u5bc6\uff0c\u6211\u90fd\u77e5\u9053\uff0c\u4ed6\u5e76\u975e\u6709\u610f\u544a\u8bc9\u6211\uff0c\u53ea\u662f\u6709\u4e9b\u4e8b\uff0c\u6ca1\u6709\u7792\u6211\u800c\u5df2\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7a81\u7136\u9053\uff1a\u201c\u9ad8\u98de\u8679\u5462\uff0c\u8fd9\u4e2a\u4eba\uff0c\u600e\u4e48\u5ffd\u7136\u6d88\u5931\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\uff0c\u8fd9\u4e2a\u4eba\u5f88\u574f\uff0c\u5f88\u53ef\u6076\uff0c\u4f46\u5374\u4e0d\u8db3\u4e3a\u654c\uff0c\u6ca1\u6709\u4ec0\u4e48\u7528\u5904\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u7559\u4e0b\u4ed6?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e0d\u80fd\u7559\u4e0b\u4ed6\uff0c\u4ed6\u79ef\u6076\u5f88\u6df1\uff0c\u7559\u4e0b\u4ed6\uff0c\u4e0d\u77e5\u8981\u6709\u591a\u5c11\u5973\u5b69\u5b50\u906d\u6b83\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u7167\u4f60\u8fd9\u4e48\u8bf4\u6765\uff0c\u8fd9\u7b2c\u4e00\u5821\u90fd\u5728\u63a7\u5236\u4e4b\u4e0b\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u6bc1\u706d\u4efb\u4f55\u4e00\u4e2a\u533a\u57df\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u662f!\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6574\u4e2a\u7b2c\u4e00\u5821\u5145\u6ee1\u7740\u5371\u673a\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u7b2c\u4e00\u5821\u4e0d\u80fd\u4e45\u7559\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u4f4f\u7684\u5730\u65b9\uff0c\u4e00\u5b9a\u5b89\u5168\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5f88\u5b89\u5168\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\u3002\u201d \u3000\u3000\u4e00\u884c\u4eba\u5230\u4e86\u5170\u82b1\u5c45\u3002 \u3000\u3000\u8fd9\u65f6\u523b\uff0c\u5927\u5bb6\u624d\u771f\u6b63\u7684\u5b89\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u5728\u5170\u82b1\u5c45\u4e2d\uff0c\u9ad8\u5e7d\u5170\u8fd8\u6709\u516b\u4e2a\u8d34\u8eab\u7684\u5973\u5a62\uff0c\u8fd9\u4e9b\u4eba\uff0c\u90fd\u5b66\u5f97\u4e00\u8eab\u6b66\u529f\u3002 \u3000\u3000\u4f46\u7b2c\u4e00\u5821\u4e2d\u7684\u4eba\uff0c\u90fd\u4e0d\u592a\u77e5\u9053\uff0c\u9ad8\u5e7d\u5170\u662f\u4e2a\u6781\u5bcc\u5fc3\u8ba1\u7684\u4eba\u3002 \u3000\u3000\u516b\u4e2a\u5973\u5a62\uff0c\u662f\u5979\u4e00\u652f\u4f0f\u5175\u3002 \u3000\u3000\u516b\u4e2a\u5973\u5a62\uff0c\u5206\u5b88\u5728\u5404\u5904\u8981\u9053\uff0c\u4fdd\u969c\u4e86\u5170\u82b1\u5c45\u7684\u5b89\u5168\u3002 \u3000\u3000\u53d7\u4f24\u7684\u4eba\uff0c\u5f00\u59cb\u4e86\u771f\u6b63\u7684\u7597\u4f24\u3001\u4f11\u606f\u3002 \u3000\u3000\u521d\u66f4\u8fc7\u540e\uff0c\u5170\u82b1\u5c45\u6084\u7136\u8d70\u51fa\u53bb\u4e86\u56db\u4e2a\u4eba\u3002 \u3000\u3000\u9ec4\u7075\u3001\u4f55\u5bd2\u8863\u3001\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u4eba\uff0c\u6709\u4e00\u9879\u79d8\u5bc6\u7684\u5546\u8c08\uff0c\u89c9\u7740\u5f71\u5b50\u9ece\u8679\uff0c\u4e0d\u662f\u53ef\u4ee5\u7559\u4e0b\u6765\u7684\u4eba\uff0c\u5982\u662f\u4e0d\u80fd\u6536\u670d\uff0c\u5fc5\u4e88\u94f2\u9664\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u5fc3\u673a\uff0c\u6ca1\u6709\u6076\u5ff5\uff0c\u4f46\u5374\u662f\u4e00\u4e2a\u6709\u5f3a\u70c8\u80fd\u529b\u52a9\u7ea3\u4e3a\u8650\u7684\u4eba\u3002 \u3000\u3000\u56db\u4e2a\u4eba\uff0c\u501f\u591c\u8272\u63a9\u62a4\uff0c\u53c8\u6084\u7136\u5230\u4e86\u70df\u9501\u697c\u4e0b\u3002 \u3000\u3000\u9ec4\u7075\u548c\u5434\u53cc\u5973\uff0c\u767b\u4e0a\u4e86\u70df\u9501\u697c\uff0c\u4f55\u5bd2\u8863\u548c\u9ad8\u5e7d\u5170\uff0c\u7559\u5728\u4e86\u70df\u9501\u697c\u4e0b\u3002 \u3000\u3000\u5929\u8272\u5f88\u6697\uff0c\u70df\u9501\u697c\u4e2d\u65e0\u706f\u706b\uff0c\u9ed1\u5f97\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u3002 \u3000\u3000\u4ee5\u9ec4\u7075\u548c\u5434\u53cc\u5973\u7cbe\u7eaf\u5185\u529f\uff0c\u4e5f\u5728\u767b\u697c\u4e4b\u540e\uff0c\u95ed\u4e0a\u53cc\u76ee\u4f11\u606f\u4e86\u4e00\u9635\uff0c\u624d\u80fd\u770b\u6e05\u695a\u697c\u4e0a\u7684\u666f\u7269\u3002 \u3000\u3000\u4e24\u4eba\u7684\u8f7b\u529f\uff0c\u90fd\u5df2\u5230\u4e86\u8e0f\u96ea\u65e0\u75d5\u7684\u5883\u754c\uff0c \u4e00\u53e3\u6c14\u884c\u4e0a\u4e86\u4e03\u697c\uff0c\u5f53\u771f\u662f\u4e0d\u95fb\u4e00\u70b9\u58f0\u606f\u3002 \u4e24\u4eba\u7684\u884c\u52a8\uff0c\u867d\u7136\u5df2\u6781\u5c3d\u5c0f\u5fc3\u8c28\u614e\uff0c\u4f46\u4ecd\u7136\u65e0\u6cd5\u7792\u8fc7\u9ece\u8679\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4e24\u4eba\u767b\u4e0a\u4e03\u697c\u65f6\uff0c\u9ece\u8679\u5df2\u751f\u51fa\u4e86\u8b66\u89c9\uff0c\u9053\uff1a \u201c\u4ec0\u4e48\u4eba?\u201d \u3000\u3000\u9ec4\u7075\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u6211!\u9ec4\u7075\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a \u201c\u6df1\u66f4\u534a\u591c\uff0c\u4f60\u6765\u6b64\u4f5c\u751a\uff0c\u4f55\u51b5\uff0c\u6211\u4eec\u767d\u5929\u8fd8\u89c1\u8fc7\u4e86\u9762\u3002\u201d \u9ec4\u7075\u9053\uff1a \u201c\u767d\u5929\uff0c\u54b1\u4eec\u6ca1\u6709\u8bf4\u51fa\u4e00\u4e2a\u7ed3\u679c\uff0c\u6240\u4ee5\uff0c\u5728\u4e0b\u6df1\u591c\u6765\u8bbf\u3002\u201d \u9ece\u8679\u9053\uff1a\u201c\u9664\u4e86\u4f60\uff0c\u8fd8\u6709\u522b\u7684\u4eba\u5417?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9664\u4e86\u6211\uff0c\u8fd8\u6709\u522b\u7684\u4eba\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u8fd9\u4e48\u770b\u6765\uff0c\u9ad8\u5929\u5065\u544a\u8bc9\u6211\u7684\u8bdd\u662f\u4e0d\u9519\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u54e6!\u9ad8\u5929\u5065\u544a\u8bc9\u4f60\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a \u201c\u4ed6\u544a\u8bc9\u6211\u8bf4\uff0c\u5982\u82e5\u4ed6\u6b7b\u4e4b\u540e\uff0c\u4e00\u5b9a\u4f1a\u6709\u4eba\u6740\u5bb3\u6211\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u4f60\u76f8\u4fe1\u4ed6\u4e86\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u672c\u6765\u4e0d\u592a\u76f8\u4fe1\u7684\uff0c\u73b0\u5728\uff0c\u6211\u53ea\u597d\u76f8\u4fe1\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ece\u8679\uff0c\u4f60\u65e0\u5fc3\u4e3a\u6076\uff0c\u4f46\u4f60\u5374\u6740\u5bb3\u4e86\u4e0d\u5c11\u7684\u4eba\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u6ca1\u6709\u6740\u8fc7\u4eba\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u5e2e\u9ad8\u5929\u5065\u6740\u8fc7\u4eba\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u53ea\u662f\u5e2e\u4ed6\u4e00\u70b9\u5fd9\uff0c\u6740\u4eba\u90fd\u662f\u4ed6\u6740\u7684\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u5bf9!\u8868\u9762\u4e0a\u662f\u5982\u6b64\uff0c\u5982\u82e5\u4f60\u4e0d\u5e2e\u4ed6\uff0c\u4ed6\u662f\u5426\u80fd\u591f\u6740\u5f97\u4e86\u90a3\u4e9b\u4eba\u5462?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u9ece\u8679\uff0c\u9ad8\u5929\u5065\u6b7b\u4e86\uff0c\u6709\u4e00\u5929\uff0c\u4f60\u53ef\u80fd\u4f1a\u53d8\u6210\u4e86\u53e6\u4e00\u4e2a\u4eba\u7684\u5f71\u5b50\u3002\u201d \u9ece\u8679\u9053\uff1a\u201c\u662f\u7684\uff0c\u6211\u60f3\u4e0d\u51fa\u6709\u66f4\u597d\u7684\u751f\u5b58\u529e\u6cd5\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5982\u82e5\u4f60\u5c06\u6765\u4f9d\u9644\u7684\u4eba\uff0c\u548c\u9ad8\u5929\u5065\u4e00\u6837\u7684\u559c\u6b22\u6740\u4eba\uff0c\u90a3\u5c82\u4e0d\u662f\u53c8\u6210\u4e86\u4e00\u4e2a\u5e2e\u51f6\u3002\u201d \u3000\u3000\u9ece\u8679\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ece\u8679\uff0c\u6211\u4eec\u4e00\u5b9a\u8981\u5f7b\u5e95\u7684\u8c08\u8c08\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u597d\u5427!\u6211\u80fd\u548c\u4f60\u8c08\u4e9b\u4ec0\u4e48\u5462?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u8c08\u8c08\u4f60\u4ee5\u540e\u7684\u4e8b\uff0c\u6211\u4eec\u4e0d\u5e0c\u671b\u4f60\u6210\u4e3a\u4e00\u4e2a\u7b2c\u4e00\u53f7\u5e2e\u51f6\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u4e0d\u8981\u89c1\u4f60\u4eec\uff0c\u5e0c\u671b\u4f60\u4eec\u80fd\u8ba9\u6211\u9759\u4e00\u9759\uff0c\u597d\u597d\u7684\u60f3\u4e00\u60f3\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u9ece\u8679\uff0c\u70df\u9501\u697c\u4e2d\uff0c\u9664\u4f60\u4e4b\u5916\uff0c\u6240\u6709\u7684\u4eba\uff0c\u90fd\u5df2\u7ecf\u6b7b\u5149\u4e86\uff0c\u4f60\u4e0d\u80fd\u957f\u65f6\u95f4\u7559\u8fd9\u91cc\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u6211\u73b0\u5728\u5c31\u89c9\u5f97\u5f88\u997f\uff0c\u6211\u8981\u5403\u4e1c\u897f\u3002\u201d \u3000\u3000\u9ec4\u7075\u3001\u5434\u53cc\u5973\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\uff0c\u6709\u4e9b\u4e8b\uff0c\u867d\u7136\u8bf4\u7684\u5f88\u6709\u6761\u7406\uff0c\u4f46\u6709\u4e9b\u4e8b\uff0c\u4ed6\u5374\u8868\u73b0\u7684\u5341\u5206\u5e7c\u7a1a\u3002 \u3000\u3000\u9ec4\u7075\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u9ece\u8679\uff0c\u73b0\u5728\uff0c\u6211\u5e26\u4f60\u53bb\u5403\u4e9b\u4e1c\u897f\uff0c\u7136\u540e\uff0c\u54b1\u4eec\u518d\u4ed4\u7ec6\u7684\u8c08\u8c08\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u597d\u5427! \u4f60\u4eec\u8fdb\u6765\u5427!\u4e0d\u8fc7\uff0c\u4f60\u4eec\u4e0d\u8bb8\u4f24\u5bb3\u6211\uff0c\u4f60\u4eec\u8981\u6740\u6211\u65f6\uff0c\u6211\u4f1a\u53cd\u6297\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u9ece\u8679\uff0c\u4f60\u8981\u660e\u767d\uff0c\u6211\u4eec\u662f\u6765\u5e2e\u52a9\u4f60\u7684\u3002\u201d \u3000\u3000\u4e3e\u6b65\u5411\u91cc\u884c\u53bb\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4e00\u7f15\u6307\u98ce\uff0c\u75be\u88ad\u800c\u6765\u3002 \u3000\u3000\u9ec4\u7075\u5403\u4e86\u4e00\u60ca\uff0c\u75be\u5411\u65c1\u4fa7\u95ea\u53bb\u3002 \u3000\u3000\u4f46\u7b2c\u4e8c\u9053\u6307\u98ce\uff0c\u5374\u5feb\u903e\u95ea\u7535\u800c\u81f3\uff1a \u3000\u3000\u9ec4\u7075\u907f\u5f00\u7b2c\u4e00\u6307\uff0c\u7adf\u7136\u65e0\u6cd5\u907f\u5f00\u7b2c\u4e8c\u6307\uff0c\u88ab\u70b9\u4e2d\u4e86\u7a74\u9053\u3002 \u3000\u3000\u540c\u65f6\uff0c \u4e00\u628a\u950b\u5229\u7684\u5c16\u5200\uff0c \u5df2\u7136\u6307\u5728\u4e86\u9ec4\u7075\u7684\u54bd\u5589\u4e4b\u4e0a\u3002 \u3000\u3000\u5434\u53cc\u5973\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u9ece\u8679\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u9ece\u8679\u63a5\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u4e71\u52a8\uff0c \u4f60\u4e00\u52a8\uff0c\u6211\u5c31\u4f1a\u6740\u4e86\u4ed6\u3002\u201d \u9ec4\u7075\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u662f\u6076\u6027\u4e0d\u6539\u5417?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u6211\u4e0d\u5e0c\u671b\u4f60\u4eec\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u6211\u4eec\u6ca1\u6709\u6740\u4f60\u7684\u7528\u5fc3\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u4f46\u6211\u4e0d\u80fd\u4e0d\u9632\u5907\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u51c6\u5907\u600e\u4e48\u529e?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u8981\u4fdd\u62a4\u81ea\u5df1\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u73b0\u5728\uff0c\u4f60\u5df2\u7ecf\u4fdd\u62a4\u4f4f\u4e0b\u81ea\u5df1\uff0c\u6709\u4ec0\u4e48\u4e8b\uff0c\u53ef\u4ee5\u8bf4\u51fa\u6765\u4e86\u3002\u201d \u9ece\u8679\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u5434\u53cc\u5973\u5fc3\u4e2d\u7126\u6025\uff0c\u4f46\u53c8\u4e0d\u611f\u8868\u73b0\u7684\u592a\u6025\u8e81\uff0c\u5fc3\u4e2d\u5927\u611f\u4e3a\u96be\u3002 \u3000\u3000\u5012\u662f\u9ec4\u7075\u5374\u8fd8\u6c89\u5f97\u4f4f\u6c14\uff0c\u6697\u4e2d\u4f30\u7b97\u4e86\u5f62\u52bf\u3001\u5904\u5883\uff0c\u8fd9\u5f71\u5b50\u9ece\u8679\u5982\u82e5\u771f\u8981\u6740\u4ed6\uff0c\u53ea\u662f\u4e3e\u624b\u4e4b\u52b3\u3002 \u3000\u3000\u9ec4\u7075\u8eab\u7ecf\u767e\u6218\uff0c\u4e00\u751f\u4e2d\u7ecf\u5386\u4e86\u4e0d\u5c11\u6b21\u7684\u51f6\u9669\uff0c\u4f46\u771f\u6b63\u9677\u5165\u4e86\u5168\u65e0\u53cd\u51fb\u80fd\u529b\u7684\u673a\u4f1a\uff0c\u5e76\u4e0d\u592a\u591a\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u4ed6\u786e\u9677\u5165\u4e86\u771f\u6b63\u7684\u5371\u673a\u4e2d\u3002 \u3000\u3000\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u9ec4\u7075\u7f13\u7f13\u8bf4\u9053\uff1a \u201c\u9ece\u8679\uff0c\u6740\u4e86\u6211\uff0c\u5e76\u4e0d\u80fd\u6539\u53d8\u4ec0\u4e48\u3002\u201d \u9ece\u8679\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u6740\u4f60\uff0c\u4f46\u5982\u4f60\u4eec\u903c\u7684\u592a\u7d27\uff0c\u90a3\u5c31\u5f88\u96be\u8bf4\u4e86\uff0c\u6211\u8981\u968f\u65f6\u4fdd\u6301\u7740\u6740\u4f60\u7684\u80fd\u529b\u3002\u201c \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u7684\u60f3\u6cd5\uff0c\u5df2\u7ecf\u5b9e\u73b0\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u6e29\u67d4\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u6253\u7b97\uff0c\u73b0\u5728\u53ef\u4ee5\u8bf4\u4e86\u3002\u201d \u3000\u3000\u9ece\u8679\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u56f0\u96be\u7684\u662f\uff0c\u6211\u4e0d\u77e5\u9053\uff0c\u548c\u4f60\u4eec\u8c08\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u4f60\u5fc3\u91cc\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5c31\u8bf4\u4ec0\u4e48\u5427\uff01\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u597d\u5427!\u6211\u73b0\u5728\u809a\u5b50\u5f88\u997f\uff0c\u5f88\u60f3\u5403\u4e1c\u897f\u3002\u201d \u3000\u3000\u5b9e\u5728\u53eb\u4eba\u60f3\u4e0d\u5230\uff0c\u5728\u8fd9\u7b49\u7d27\u8981\u7684\u5173\u53e3\u4e4b\u4e2d\uff0c\u4ed6\u7adf\u4f1a\u63d0\u51fa\u4e86\u8fd9\u4e48\u4e00\u4e2a\u95ee\u9898\u3002 \u3000\u3000\u5434\u53cc\u5973\u6697\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d!\u6211\u53bb\u66ff\u4f60\u53d6\u4e9b\u4e1c\u897f\u6765\u3002\u201d \u3000\u3000\u7f13\u6b65\u5411\u5916\u884c\u53bb\u3002 \u3000\u3000\u5979\u7684\u4e3e\u6b62\u5c3d\u91cf\u4fdd\u6301\u4e86\u5e73\u548c\u3001\u6e29\u67d4\uff0c\u4e0d\u5e26\u4e00\u70b9\u706b\u6c14\u3002 \u3000\u3000\u5ba4\u4e2d\uff0c\u53ea\u4f59\u4e0b\u4e86\u9ec4\u7075\u548c\u9ece\u8679\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u9ec4\u7075\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u9ece\u8679\uff0c\u73b0\u5728\uff0c\u53ea\u6709\u6211\u4eec\u4e24\u4e2a\u4e86\uff0c\u4f60\u6709\u4ec0\u4e48\u8bdd\uff0c\u53ef\u4ee5\u653e\u5fc3\u8bf4\u4e86\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u771f\u7684\uff0c\u6211\u4e0d\u77e5\u9053\uff0c\u8be5\u548c\u4f60\u8c08\u4e9b\u4ec0\u4e48?\u201d \u3000\u3000\u9ec4\u7075\u82e6\u7b11\u4e00\u4e0b\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u4ed6\u7684\u60f3\u6cd5\u5f88\u5355\u7eaf\uff0c\u5355\u7eaf\u5230\u6ca1\u6709\u529e\u6cd5\u5bf9\u4ed6\u7528\u4e00\u70b9\u5fc3\u673a\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u6709\u5f88\u591a\u7b80\u5355\u7684\u95ee\u9898\uff0c\u53cd\u800c\u5f88\u96be\u5904\u7f6e\uff0c\u5b83\u7684\u7b54\u6848\u7b80\u5355\uff0c\u5b8c\u5168\u5931\u53bb\u4e86\u56de\u65cb\u7684\u4f59\u5730\u3002 \u3000\u3000\u9ec4\u7075\u7684\u5904\u5883\uff0c\u5c31\u662f\u5982\u6b64\u3002 \u3000\u3000\u9ece\u8679\u592a\u5355\u7eaf\u4e86\uff0c\u5355\u7eaf\u5230\u6c5f\u6e56\u4e0a\u6ca1\u6709\u4e00\u4ef6\u53ef\u4ee5\u4f7f\u4ed6\u52a8\u5fc3\u7684\u4e8b\u3002 \u3000\u3000\u4ee5\u9ec4\u7075\u7684\u673a\u667a\uff0c\u4e5f\u611f\u5230\u8ba1\u7a77\u8bcd\u7aed\u4e86\uff0c\u60f3\u4e0d\u51fa\u8be5\u548c\u4ed6\u8bf4\u4e9b\u4ec0\u4e48? \u3000\u3000\u9ece\u8679\u8fd9\u4e2a\u4eba\u53ea\u662f\u5355\u7eaf\uff0c\u4f46\u5374\u4e0d\u50bb\uff0c\u4ed6\u5728\u6b66\u529f\u4e0a\u7684\u6df1\u539a\u9020\u8be3\uff0c\u66f4\u662f\u83ab\u6d4b\u9ad8\u6df1\u3002 \u3000\u3000\u9ec4\u7075\u5fc3\u4e2d\u6570\u4f5c\u76d8\u7b97\uff0c\u552f\u4e00\u7684\u8131\u8eab\u673a\u4f1a\uff0c\u53ea\u6709\u5434\u53cc\u5973\u9001\u6765\u98df\u7269\u7684\u65f6\u5019\u3002 \u3000\u3000\u5ba4\u4e2d\u9759\u4e86\u4e0b\u6765\uff0c\u9759\u7684\u542c\u4e0d\u5230\u4e00\u70b9\u58f0\u606f\u3002 \u3000\u3000\u9ec4\u7075\u5fae\u95ed\u53cc\u76ee\uff0c\u6697\u4e2d\u89c2\u5bdf\u9ece\u8679\u3002 \u3000\u3000\u7acb\u523b\u53d1\u89c9\uff0c\u9ece\u8679\u4e24\u9053\u70af\u70af\u7684\u76ee\u5149\uff0c\u4e5f\u76ef\u6ce8\u5728\u81ea\u5df1\u7684\u8138\u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u76ee\u5149\u6e05\u6f88\u660e\u4eae\uff0c\u773c\u795e\u5145\u6c9b\uff0c\u4f3c\u4e4e\u662f\u6709\u7740\u65e0\u7a77\u7684\u7cbe\u529b\u3002 \u3000\u3000\u4ed6\u5f88\u997f\uff0c\u997f\u7684\u8981\u8ba8\u4e1c\u897f\u5403\uff0c\u4f46\u5374\u6ca1\u6709\u4e00\u70b9\u9965\u997f\u7684\u75b2\u4e4f\u3002 \u3000\u3000\u4e00\u9635\u6b65\u5c65\u4e4b\u58f0\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u5212\u7834\u4e86\u6c89\u5bc2\u3002 \u3000\u3000\u5434\u53cc\u5973\u624b\u6367\u7740\u4e00\u4e2a\u6728\u76d8\uff0c\u884c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u662f\u7389\u7fe0\u7684\u5367\u623f\uff0c\u5f88\u5bbd\u5927\uff0c\u4f46\u5374\u6ca1\u6709\u684c\u6905\u3002 \u3000\u3000\u5434\u53cc\u5973\u653e\u4e0b\u98df\u7269\u4e4b\u540e\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6df1\u591c\u4e4b\u4e2d\uff0c\u53ea\u6709\u4e00\u4e9b\u4f59\u4e0b\u7684\u996d\u83dc\uff0c\u4f60\u5c31\u5c06\u5c31\u4e00\u4e9b\u5427\u3002\u201d \u3000\u3000\u9ece\u8679\u53f8\u5149\u51dd\u6ce8\u5728\u5434\u53cc\u5973\u7684\u8138\u4e0a\uff0c\u77a7\u4e86\u826f\u4e45\uff0c\u9053\uff1a\u201c\u6211\u80fd\u4e0d\u80fd\u505a\u4f60\u7684\u5f71\u5b50?\u201d \u3000\u3000\u5434\u53cc\u5973\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a \u201c\u6211!\u7537\u5973\u6709\u522b\uff0c \u53ea\u6015\u662f\u4e0d\u592a\u65b9\u4fbf\u5427?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u4f60\u51fa\u53bb\u5427!\u79bb\u5f00\u70df\u9501\u697c\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u5462?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u4ed6\u7559\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u653e\u4e86\u4ed6?\u201d \u3000\u3000\u9ece\u8679\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u8d70\u5f00\uff0c\u4e0d\u8bb8\u5728\u697c\u4e0a\u505c\u7559\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u53ea\u597d\u79bb\u53bb\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u3001\u4f55\u5bd2\u8863\uff0c\u5c31\u7b49\u5728\u70df\u9501\u697c\u4e0b\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u5306\u5306\u8fce\u4e86\u4e0a\u53bb\uff0c\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\u5462?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ecd\u5728\u4ed6\u63a7\u5236\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u4e0a\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u4e3e\u6b65\u884c\u53bb\u3002 \u3000\u3000\u5434\u53cc\u5973\u4e00\u4f38\u624b\u6293\u4f4f\u4e86\u4f55\u5bd2\u8863\uff0c\u4f4e\u58f0\u9053\uff1a \u201c\u4e0d\u53ef\u9020\u6b21\uff0c\u9ece\u8679\u4e0d\u5bb9\u6613\u5bf9\u4ed8\uff0c\u4ed6\u8868\u73b0\u7684\u6b66\u529f\uff0c\u7edd\u4e0d\u5728\u4f60\u6211\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u4ed6\u4e00\u51fa\u624b\uff0c\u5c31\u628a\u9ec4\u5821\u4e3b\u5236\u4f4f\u4e86\uff0c\u8fd9\u4eba\u7684\u6b66\u529f\uff0c\u5b9e\u5728\u662f\u4e0d\u53ef\u8f7b\u89c6\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u4ed6\u7684\u601d\u60f3\u5f88\u5355\u7eaf\uff0c\u6211\u4eec\u5b8c\u5168\u65e0\u6cd5\u9884\u6d4b\u4ed6\u4f1a\u4f5c\u51fa\u4ec0\u4e48\u6837\u7684\u4e8b\u60c5\uff0c\u4ed6\u4e0d\u80fd\u52a8\u4ee5\u5229\u5bb3\uff0c\u4e5f\u65e0\u6cd5\u52a8\u4e4b\u4ee5\u60c5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u96be\u9053\u54b1\u4eec\u5c31\u4e0d\u6551\u9ec4\u5821\u4e3b\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8981\u6551\uff0c\u4f46\u8981\u60f3\u4e00\u4e2a\u5f88\u597d\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u53cc\u5973\uff0c\u4e00\u5b9a\u8981\u6551\u4ed6\u51fa\u6765\uff0c\u4e0d\u60dc\u4efb\u4f55\u727a\u7272\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u522b\u7126\u6025\uff0c\u6211\u77e5\u9053\u8fd9\u4ef6\u4e8b\u5f88\u4e25\u91cd\uff0c\u4e0d\u8bba\u54b1\u4eec\u4ed8\u51fa\u591a\u5927\u7684\u727a\u7272\uff0c\u90fd\u8981\u6551\u4ed6\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u56f0\u96be\u5904\uff0c\u5728\u54b1\u4eec\u6ca1\u6709\u4e00\u4e2a\u4e0b\u624b\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u9ece\u8679\u662f\u4e00\u4e2a\u5b8c\u5168\u6ca1\u6709\u65b9\u6cd5\u53ef\u4ee5\u5f15\u8bf1\u7684\u4eba\uff0c\u54b1\u4eec\u4e0d\u80fd\u4e86\u89e3\u2026\u2026\u201d \u3000\u3000\u7a81\u7136\u4f4f\u53e3\u4e0d\u8a00\uff0c\u51dd\u76ee\u6c89\u601d\u8d77\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u60f3\u8ffd\u95ee\uff0c\u4f46\u5374\u88ab\u9ad8\u5e7d\u5170\u6321\u4f4f\uff0c\u4e0d\u8ba9\u4ed6\u6253\u65ad\u4e86\u5434\u53cc\u5973\u7684\u601d\u8def\u3002 \u3000\u3000\u5434\u53cc\u5973\u7f13\u7f13\u62ac\u8d77\u5934\u6765\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4ed6\u597d\u50cf\u5bf9\u6211\u4eec\u4e24\u4e2a\u8fd8\u4e0d\u9519\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5bf9\u6211\u4eec\u4e24\u4e2a\u4e0d\u9519\uff0c\u8fd9\u8bdd\u4ec0\u4e48\u610f\u601d?\u201d \u3000\u3000\u5434\u53cc\u5973\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u9ad8\u5929\u5065\u62db\u547c\u4ed6\u51fa\u624b\u65f6\uff0c\u4ed6\u4e0d\u80af\u51fa\u624b\uff0c\u5c31\u662f\u4e0d\u613f\u610f\u4f24\u5bb3\u6211\u4eec\u4e24\u4e2a\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\uff0c\u96be\u9053\u8fd8\u6709\u4ec0\u4e48\u975e\u5206\u4e4b\u60f3?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u7537\u5973\u76f8\u60a6\u5929\u7ecf\u5730\u4e49\uff0c\u90a3\u4e5f\u4e0d\u80fd\u8bf4\u4ed6\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff0c\u8fd9\u4e5f\u662f\u54b1\u4eec\u80fd\u53d1\u73b0\u4ed6\u7684\u552f\u4e00\u7f3a\u70b9\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u5927\u59ca\uff0c\u4ed6\u662f\u4e0d\u662f\u65e9\u77e5\u9053\u54b1\u4eec\u90fd\u5df2\u7ecf\u662f\u4f55\u95e8\u7684\u4eba\u4e86\u3002\u201d \u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\uff0c\u6211\u60f3\u4ed6\u8fd8\u662f\u4e0d\u592a\u6e05\u695a\uff0c\u7eb5\u7136\u662f\u77e5\u9053\u4e00\u70b9\u98ce\u58f0\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u60f3\u7684\u90a3\u4e48\u6df1\u8fdc\uff0c\u6211\u5728\u6c5f\u6e56\u4e0a\uff0c\u884c\u8d70\u4e86\u4e00\u4e9b\u65f6\u65e5\uff0c\u89c1\u8fc7\u4e86\u5404\u8272\u5404\u6837\u7684\u4eba\uff0c\u4f46\u5374\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u50cf\u4ed6\u90a3\u6837\u7684\u4eba\uff0c\u4ed6\u8ddf\u968f\u9ad8\u5929\u5065\u90a3\u6837\u4e45\u7684\u65f6\u95f4\uff0c\u603b\u8be5\u6709\u81ea\u5df1\u7684\u751f\u6d3b\u4e60\u60ef\u624d\u5bf9\u3002\u201d \u3000\u3000\u5979\u8bf4\u7684\u5f88\u542b\u84c4\uff0c\u4f46\u9ad8\u5e7d\u5170\u5374\u542c\u5f97\u660e\u767d\uff0c\u7acb\u523b\u63a5\u9053\uff1a \u201c\u5927\u59ca\uff0c\u4e0d\u7ba1\u5982\u4f55\uff0c\u54b1\u4eec\u8981\u6551\u9ec4\u5821\u4e3b\uff0c\u9ece\u8679\u65e2\u7136\u5bf9\u54b1\u4eec\u7684\u5370\u8c61\u4e0d\u9519\uff0c\u54b1\u4eec\u81f3\u5c11\u8981\u60f3\u529e\u6cd5\u63a5\u8fd1\u4ed6\u8bd5\u8bd5?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u521a\u624d\uff0c\u4ed6\u628a\u6211\u64b5\u8d70\u4e0b\u6765\uff0c\u8fd9\u8bf4\u660e\u4e86\uff0c\u4ed6\u5bf9\u4fdd\u62a4\u81ea\u5df1\u5b89\u5168\u7684\u4e8b\uff0c\u4e5f\u5f88\u91cd\u89c6\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u54b1\u4eec\u675f\u624b\u65e0\u7b56\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5c3d\u529b\u800c\u4e3a?\uff0c\u6211\u4e0a\u53bb\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7f13\u6b65\u5411\u697c\u4e0a\u884c\u53bb\u3002 \u3000\u3000\u5434\u53cc\u5973\u8f7b\u8f7b\u5401\u4e01\u4e00\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u5bd2\u8863\uff0c\u4e0d\u7528\u62c5\u5fc3\uff0c\u5982\u82e5\u5e7d\u5170\u5931\u8d25\u4e86\uff0c\u6211\u4f1a\u60f3\u6cd5\u5b50\u5bf9\u4ed8\u4ed6\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u529e\u6cd5?\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u5929\u9b54\u821e\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u54e6\u4e86\u4e00\u58f0\uff0c\u672a\u518d\u591a\u95ee\uff1a \u3000\u3000\u9ad8\u5e7d\u5170\u767b\u4e0a\u4e86\u4e03\u697c\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1b \u201c\u9ece\u8679\uff0c\u5403\u5b8c\u4e86\u6ca1\u6709\uff0c\u6211\u6765\u6536\u56de\u7897\u7b77\u3002\u201d \u9ece\u8679\u9053\uff1a\u201c\u4f60\u8fdb\u6765\u5427\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7f13\u6b65\u5165\u95e8\u3002 \u3000\u3000\u9ece\u8679\u7684\u77ed\u5200\uff0c\u5df2\u7ecf\u4e0d\u67b6\u5728\u9ec4\u7075\u7684\u54bd\u95f4\uff0c\u800c\u662f\u63e1\u5728\u624b\u4e2d\u3002 \u3000\u3000\u4f46\u9ec4\u7075\u5374\u4ef0\u5367\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5fc3\u5934\u9707\u52a8\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u9ece\u8679\uff0c\u4f60\u6740\u4e86\u4ed6?\u201d \u3000\u3000\u9ece\u8679\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4f46\u6211\u70b9\u4e86\u4ed6\u8eab\u4e0a\u4e00\u5341\u516b\u5904\u7a74\u9053\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4ed6\u8fd8\u80fd\u4e0d\u80fd\u8bf4\u8bdd?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u4e0d\u80fd\uff0c\u5341\u516b\u5904\u7a74\u9053\u4e2d\uff0c\u5305\u62ec\u5343\u54d1\u7a74\u5728\u5185\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5f88\u806a\u660e\u554a\uff1b\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u54b3!\u6211\u6ca1\u6709\u5730\u65b9\u53bb\uff0c \u53ea\u597d\u7559\u4f4f\u8fd9\u7532\u4e86\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6c38\u8fdc\u7559\u5728\u8fd9\u91cc?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a \u201c\u5bf9\u554a!\u4f60\u4eec\u6bcf\u5929\u90e8\u7ed9\u6211\u9001\u996d\u6765\uff0c\u4e0d\u7136\u6211\u5c31\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5fc3\u5934\u4e00\u9707\uff0c\u6697\u9053\uff1a\u4ed6\u8981\u628a\u9ec4\u5821\u4e3b\u5e38\u7559\u6b64\u5730\uff0c\u4f5c\u4e3a\u4eba\u8d28\u3002 \u3000\u3000\u4f46\u5fc3\u4e2d\u4e5f\u7a0d\u6709\u5bbd\u6170\uff0c\u770b\u6837\u5b50\u9ece\u8679\u6682\u65f6\u5e76\u6ca1\u6709\u6740\u5bb3\u9ec4\u5821\u4e3b\u7684\u7528\u5fc3\u3002 \u3000\u3000\u5fc3\u4e2d\u5ff5\u8f6c\uff0c \u53e3\u4e2d\u8bf4\u9053\uff1a \u201c\u9ece\u8679\uff0c\u73b0\u5728\uff0c\u4f60\u5df2\u7ecf\u5360\u5c3d\u4e86\u4e0a\u98ce\uff0c\u4f60\u60f3\u505a\u4ec0\u4e48?\u53ef\u4ee5\u8bf4\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u8bf4\u4e86\u4f60\u4e5f\u4e0d\u4f1a\u7b54\u5e94?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u4e00\u9635\u70ed\u5fc3\u8df3\uff0c\u4f46\u4ecd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u8bf4\u770b\u554a?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u662f\u5f88\u60f3\u6551\u4ed6\u5417?\u4f60\u5982\u80fd\u7559\u4e0b\u6765\uff0c\u6211\u5c31\u653e\u8d70\u4ed6\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5403\u4e86\u4e00\u60ca\uff1b\u9053\uff1a\u201c\u6211\u7559\u4e0b\u6765\uff0c\u5e72\u4ec0\u4e48?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a \u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u4f60\u7559\u4e0b\u6765\uff0c\u53ef\u4ee5\u628a\u8fd9\u4f4d\u9ec4\u5821\u4e3b\u6362\u53bb\u3002\u201d \u4ed6\u7684\u76ee\u5149\u4e2d\u7a81\u7136\uff0c\u5145\u6ee1\u2014\u79cd\u90aa\u5ff5\uff0c\u4e00\u79cd\u9965\u6e34\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u8f7b\u8f7b\u5401\u4e00\u53e3\u6c14\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u597d\u5427!\u6211\u7559\u4e0b\u6765\uff0c\u4f60\u653e\u4e86\u9ec4\u5821\u4e3b\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u8981\u70b9\u4e86\u4f60\u7684\u7a74\u9053\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\uff0c\u5fae\u5fae\u95ed\u4e0a\u53cc\u76ee\uff0c\u5fc3\u4e2d\u5374\u6697\u81ea\u5fd6\u9053\uff1a\u53ea\u8981\u4f60\u79bb\u5f00\u9ec4\u7075\uff0c\u6211\u4eec\u786e\u77e5\u80fd\u4fdd\u62a4\u4ed6\u7684\u5b89\u5168\u65f6\uff0c\u5c31\u7acb\u523b\u52a8\u624b\u3002 \u3000\u3000\u81ea\u4ece\u5979\u53d1\u73b0\u4e86\u9ece\u8679\u76ee\u5149\u4e2d\u7684\u90aa\u5ff5\u4e4b\u540e\uff0c\u9ad8\u5e7d\u5170\u5df2\u786e\u77e5\u8fd9\u4e2a\u4eba\u4e0d\u80fd\u518d\u7559\u4e0b\u6765\u3002 \u3000\u3000\u957f\u5e74\u5730\u8ffd\u968f\u9ad8\u5929\u5065\uff0c\u4ed6\u5df2\u7ecf\u6cbe\u67d3\u4e86\u9ad8\u5929\u5065\u5f88\u591a\u7684\u90aa\u6076\u610f\u5ff5\u3002 \u3000\u3000\u8fd9\u79cd\u90aa\u6076\uff0c\u6f5c\u4f0f\u5728\u4ed6\u7684\u610f\u8bc6\u4e2d\uff0c\u4e00\u65e6\u9047\u4e0a\u4e86\u4ed6\u6709\u5229\u7684\u673a\u4f1a\uff0c\u90a3\u80a1\u6f5c\u610f\u8bc6\u91cc\u7684\u90aa\u6076\uff0c\u7acb\u523b\u5c31\u53d1\u4f5c\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u65e0\u5fc3\u4e3a\u6076\uff0c\u4f46\u4ed6\u7684\u884c\u4e3a\uff0c\u5374\u6bd4\u6076\u4eba\u66f4\u53ef\u6015\u3002 \u3000\u3000\u53ef\u60dc\u7684\u662f\uff0c\u9ad8\u5e7d\u5170\u65e0\u6cd5\u628a\u5fc3\u4e2d\u60f3\u7684\u4e8b\uff0c\u544a\u8bc9\u4f55\u5bd2\u8863\u3002 \u3000\u3000\u9ece\u8679\u7f13\u6b65\u884c\u4e86\u8fc7\u6765\uff0c\u53f3\u624b\u7684\u77ed\u5200\uff0c\u4ecd\u7136\u6307\u5728\u9ec4\u7075\u7684\u54bd\u5589\u4e4b\u4e0a\u3002 \u3000\u3000\u5de6\u624b\u5374\u8f7b\u8f7b\u7684\u62db\u52a8\uff0c\u9053\uff1a\u201c\u8fc7\u6765\uff0c\u4f60\u8fc7\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7f13\u7f13\u7741\u5f00\u4e86\u773c\u775b\uff0c\u9053\uff1a\u201c\u662f\u53eb\u6211\u5417?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u662f!\u4f60\u8fc7\u6765\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u7f13\u6b65\u884c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u8981\u70b9\u4e86\u4f60\u51e0\u5904\u7a74\u9053\u3002 \u3000\u3000\u5de6\u624b\u6325\u52a8\uff0c\u70b9\u4e86\u9ad8\u5e7d\u5170\u4e09\u5904\u7a74\u9053\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u6ca1\u6709\u52a8\uff0c\u5979\u770b\u5f97\u51fa\uff0c\u4efb\u4f55\u884c\u52a8\uff0c\u90fd\u53ef\u80fd\u4f7f\u9ec4\u7075\u4e27\u547d\u5200\u4e0b\u3002 \u3000\u3000\u4ed6\u53ea\u4e0d\u8fc7\u662f\u4e00\u4e2a\u5f71\u5b50\uff0c\u4f46\u4ed6\u5374\u76f8\u5f53\u8001\u7ec3\u3002 \u3000\u3000\u4e0d\u8bba\u591a\u9ad8\u660e\u7684\u4eba\uff0c\u4f46\u5982\u88ab\u4ed6\u70b9\u4e86\u51e0\u5904\u7a74\u9053\uff0c\u5c31\u4e0d\u80fd\u518d\u4fdd\u6709\u4ed6\u7075\u6d3b\u7684\u8eab\u624b\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4e5f\u4e0d\u80fd\u3002 \u3000\u3000\u9ece\u8679\u8f7b\u8f7b\u4e00\u62c9\uff0c\u9ad8\u5e7d\u5170\u5012\u5728\u4e86\u6728\u69bb\u4e4b\u4e0a\u3002 \u3000\u3000\u5979\u5df2\u7ecf\u5b8c\u5168\u5931\u53bb\u4e86\u53cd\u6297\u7684\u80fd\u529b\u3002 \u3000\u3000\u9ece\u8679\u53f3\u624b\u4e2d\u7684\u77ed\u5200\uff0c\u79fb\u5230\u4e86\u9ad8\u5e7d\u5170\u7684\u54bd\u5589\u4e4b\u4e0a\u65f6\uff0c\u624d\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u8fc7\u6765\uff0c\u628a\u59d3\u9ec4\u7684\u5e26\u8d70\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7684\u8840\u8109\u507e\u5f20\uff0c\u80f8\u4e2d\u4e00\u80a1\u6028\u6c14\uff0c\u51e0\u4e4e\u8981\u7206\u70b8\u5f00\u6765\u3002 \u3000\u3000\u4f46\u4ed6\u54ac\u7259\u5f3a\u884c\u5fcd\u4f4f\u4e86\u6ca1\u6709\u53d1\u4f5c\u3002 \u3000\u3000\u5e26\u7740\u9ec4\u7075\u884c\u51fa\u4e86\u623f\u5916\uff0c\u4f55\u5bd2\u8863\u62cd\u6d3b\u4e86\u9ec4\u7075\u7a74\u9053\u3002 \u3000\u3000\u9ec4\u7075\u4e0d\u80fd\u52a8\uff0c\u4e0d\u80fd\u8bf4\u8bdd\uff0c\u4f46\u4ed6\u5374\u770b\u7684\u5f88\u6e05\u695a\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u89e3\u5f00\u4ed6\u88ab\u70b9\u7a74\u9053\u65f6\uff0c\u7acb\u523b\u8f6c\u56de\u8eab\u5411\u4e0a\u5954\u53bb\u3002 \u3000\u3000\u4ed6\u4e0d\u80fd\u8ba9\u9ad8\u5e7d\u5170\u4e3a\u4ed6\u6d89\u9669\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u98de\u8eab\u4e00\u8dc3\uff0c\u6293\u4f4f\u4e86\u9ec4\u7075\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5821\u4e3b\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u597d\u7cca\u6d82\uff0c\u600e\u4e48\u628a\u5e7d\u5170\u59d1\u5a18\u7559\u4e0b\u6765\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u9ece\u8679\u662f\u4e00\u4e2a\u5f88\u53ef\u6015\u7684\u4eba\uff0c\u4ed6\u5df2\u6cbe\u67d3\u4e86\u9ad8\u5929\u5065\u7684\u90aa\u6076\uff0c\u5374\u6ca1\u6709\u9ad8\u5929\u5065\u90a3\u79cd\u601d\u60f3\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u5bd2\u8863\uff0c\u4e0d\u80fd\u8ba9\u5e7d\u5170\u59d1\u5a18\u53d7\u5230\u4f24\u5bb3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u4ed6\u7684\u8033\u6735\u5f88\u7075\u654f\uff0c\u6211\u8ddf\u5e7d\u5170\u4e0a\u53bb\u65f6\uff1b\u5df2\u7136\u6781\u5c3d\u5c0f\u5fc3\uff0c\u4f46\u597d\u50cf\u4ecd\u7136\u88ab\u4ed6\u53d1\u89c9\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\uff0c\u4e0d\u6551\u5e7d\u5170\u59d1\u5a18\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u81ea\u7136\u8981\u6551\uff0c\u6240\u4ee5\uff0c\u54b1\u4eec\u8981\u51b7\u9759\uff0c\u53bb\u548c\u5434\u53cc\u5973\u5546\u91cf\u4e00\u4e0b\u3002\u201d \u3000\u3000\u9ec4\u7075\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u5979\u6709\u4ec0\u4e48\u529e\u6cd5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5979\u544a\u8bc9\u6211\uff0c\u53ef\u4ee5\u7528\u5929\u9b54\u821e\u5bf9\u4ed8\u4ed6\u3002\u201d \u3000\u3000\u9ec4\u7075\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u597d\u5427! \u4f60\u53bb\u627e\u5979\uff0c\u6211\u4e0a\u53bb\u77a7\u77a7\uff0c\u4e0d\u80fd\u8ba9\u5e7d\u5170\u59d1\u5a18\u53d7\u5230\u4efb\u4f55\u4e00\u70b9\u59d4\u5c48\u3001\u4f24\u5bb3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u4f4e\u58f0\u9053\uff1a \u201c\u5821\u4e3b\uff0c\u9ece\u8679\u4e5f\u8bb8\u771f\u7684\u5fc3\u65e0\u6076\u5ff5\uff0c\u4f46\u4ed6\u5374\u6709\u6076\u884c\uff0c\u800c\u4e14\u5176\u884c\u5fc5\u679c\uff0c\u56e0\u4e3a\u4ed6\u6ca1\u6709\u77bb\u524d\u987e\u540e\u7684\u601d\u60f3\uff0c\u6240\u4ee5\uff0c\u5fc3\u65e0\u987e\u5fcc\uff0c\u51fa\u624b\u5982\u7535\uff0c\u5c0f\u5fc3\u4ed6\u771f\u7684\u6740\u4e86\u5e7d\u5170\u3002\u201d \u3000\u3000\u9ec4\u7075\u5fc3\u5934\u52a8\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u6211\u4f1a\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u75be\u5feb\u7684\u5954\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9ec4\u7075\u63d0\u4e00\u53e3\u6c14\uff0c\u7f13\u6b65\u5411\u524d\u5954\u53bb\u3002 \u3000\u3000\u5bf9\u9ece\u8679\u7684\u6b66\u529f\uff0c\u9ec4\u7075\u4e5f\u6709\u7740\u83ab\u6d4b\u9ad8\u6df1\u7684\u611f\u89c9\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u9ec4\u7075\u6781\u5c3d\u5c0f\u5fc3\uff0c\u4e00\u76f4\u884c\u5230\u4e86\u9ece\u8679\u505c\u8eab\u7684\u95e8\u53e3\uff0c\u4ecd\u7136\u6ca1\u6709\u88ab\u4eba\u53d1\u89c9\u3002 \u3000\u3000\u9ece\u8679\u8ddf\u7740\u9ad8\u5929\u5065\u5b66\u5230\u4e86\u5f88\u591a\u6c5f\u6e56\u4e0a\u7684\u7ecf\u9a8c\u3001\u8be1\u8ba1\uff0c\u4f46\u5374\u4ed6\u5ffd\u7565\u4e86\u4e00\u4e9b\u5c0f\u8282\u3002 \u3000\u3000\u90a3\u662f\u4ed6\u6ca1\u6709\u5173\u4e0a\u5927\u95e8\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u9ec4\u7075\u767b\u4e0a\u4e03\u697c\u5df2\u7ecf\u53ef\u4ee5\u770b\u5230\u4e86\u5ba4\u4e2d\u7684\u60c5\u666f\u3002 \u3000\u3000\u53ea\u89c1\u9ece\u8679\u5446\u5446\u7684\u5750\u5728\u5e8a\u6cbf\u4e0a\uff0c\u671b\u7740\u8eba\u5728\u5e8a\u4e0a\u7684\u9ad8\u5e7d\u5170\u51fa\u795e\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u795e\u4e13\u6ce8\uff0c\u8fde\u9ec4\u7075\u884c\u8fd1\u4e86\u95e8\u53e3\uff0c\u4ecd\u65e0\u6240\u89c9\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4e5f\u77aa\u5927\u7740\u773c\u775b\uff0c\u56db\u76ee\u4ea4\u6295\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7684\u8138\u4e0a\uff0c\u6ee1\u662f\u6050\u60e7\uff0c\u9ece\u8679\u7684\u773c\u4e2d\uff0c\u5374\u662f\u4e00\u7247\u90aa\u6076\u3002 \u3000\u3000\u9ec4\u7075\u60f3\u51b2\u8fdb\u53bb\uff0c\u4f46\u4ed6\u5374\u53d1\u89c9\u4e86\u9ece\u8679\u7684\u624b\u4e2d\uff0c\u4ecd\u63e1\u7740\u90a3\u628a\u77ed\u5200\u3002 \u3000\u3000\u9ec4\u7075\u4f30\u7b97\u8ddd\u79bb\uff0c\u5fc3\u4e2d\u5b9e\u5728\u6ca1\u6709\u628a\u63e1\uff0c\u80fd\u5426\u89e3\u5f97\u4e86\u9ad8\u5e7d\u5170\u4e4b\u5371\u3002 \u3000\u3000\u65e0\u6cd5\u5728\u4ed6\u4e00\u5200\u523a\u51fa\u4e4b\u524d\uff0c\u6551\u4e0b\u9ad8\u5e7d\u5170\uff0c \u3000\u3000\u4ed6\u5fc5\u987b\u7b49\u5f85\uff0c\u7b49\u5f85\u4e00\u4e2a\u66f4\u6709\u5229\u7684\u65f6\u673a\u3002 \u3000\u3000\u9ece\u8679\u653e\u4e0b\u4e86\u77ed\u5200\uff0c\u4f38\u51fa\u53cc\u624b\uff0c\u4e0d\u505c\u5730\u5728\u9ad8\u5e7d\u5170\u8eab\u4e0a\u629a\u6478\u3002 \u3000\u3000\u4e00\u79cd\u5168\u65e0\u60c5\u8c03\u7684\u6311\u9017\u3002 \u3000\u3000\u4f46\u9ece\u8679\u5374\u662f\u5341\u5206\u9676\u9189\uff0c\u5fae\u95ed\u53cc\u76ee\uff0c\u629a\u6478\u7684\u53cc\u624b\u52a8\u4f5c\uff0c\u4e5f\u8d8a\u6765\u8d8a\u5feb\u3002 \u3000\u3000\u90a3\u662f\u53ef\u4ee5\u78b0\u6b6a\u4eba\u9f3b\u5b50\u7684\u52a8\u4f5c\u3002 \u3000\u3000\u8fd9\u5c0f\u5b50\uff0c\u5b9e\u5728\u4e0d\u89e3\u6e29\u67d4\u3002 \u3000\u3000\u4ed6\u5df2\u4e86\u89e3\u9ece\u8679\u7684\u6b66\u529f\uff0c\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\uff0c\u5fc5\u8981\u6709\u628a\u63e1\u4e00\u51fb\u6210\u529f\u65f6\uff0c\u624d\u80fd\u51fa\u624b\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u9ece\u8679\u505c\u4e0b\u4e86\u53cc\u624b\uff0c\u7f13\u7f13\u63a2\u4e0b\u5934\u53bb\uff0c\u80c6\u5927\u5730\u4eb2\u5728\u9ad8\u5e7d\u5170\u7684\u6a31\u5507\u4e4b\u4e0a\u3002 \u3000\u3000\u9ec4\u7075\u5df2\u63d0\u805a\u4e86\u771f\u529b\uff0c\u8fd0\u6c14\u5982\u73e0\uff0c\u51dd\u52b2\u82e5\u94a2\uff0c\u4f46\u5374\u6ca1\u6709\u4e00\u4e0b\u5b50\u76f4\u6251\u8fc7\u53bb\uff0c\u53cd\u800c\u7f13\u7f13\u5411\u524d\u53bb\u3002 \u3000\u3000\u4ed6\u843d\u8db3\u5c0f\u5fc3\uff0c\u5f53\u771f\u662f\u4e0d\u95fb\u4e00\u70b9\u58f0\u606f\u3002 \u3000\u3000\u9ece\u8679\u8033\u76ee\u7684\u7075\u654f\uff0c\u5e38\u4eba\u5b9e\u5728\u96be\u53ca\uff0c\u5c31\u5728\u9ec4\u7075\u5c06\u8fd1\u6728\u69bb\u65f6\uff0c\u7adf\u7136\u88ab\u53d1\u89c9\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u52a8\u4f5c\u5f88\u5feb\uff0c\u5de6\u624b\u7a81\u7136\u62cd\u51fa\u4e00\u638c\uff0c\u62cd\u5411\u9ec4\u7075\uff0c\u53f3\u624b\u5df2\u6293\u8d77\u77ed\u5200\uff0c\u6321\u5f00\u9ad8\u5e7d\u5170\u7684\u54bd\u5589\u3002 \u3000\u3000\u9ece\u8679\u7684\u76ee\u5149\uff0c\u671b\u7740\u9ec4\u7075\uff0c\u4f46\u4ed6\u53f3\u624b\u523a\u51fa\u7684\u4e00\u5200\uff0c\u786e\u662f\u6781\u4e3a\u51c6\u786e\uff0c\u76f4\u523a\u5411\u9ad8\u5e7d\u5170\u7684\u54bd\u5589\u3002 \u3000\u3000\u9ec4\u7075\u5fc3\u4e2d\u5927\u6025\uff0c\u53ea\u89c9\u65e0\u8bba\u5982\u4f55\uff0c\u90fd\u65e0\u6cd5\u523a\u5411\u8fd9\u4e00\u5200\u3002 \u3000\u3000\u5982\u82e5\u9ece\u8679\u6ca1\u6709\u62cd\u51fa\u8fd9\u4e00\u638c\uff0c\u4ed6\u76f8\u4fe1\u4ee5\u81ea\u5df1\u7684\u901f\u5ea6\uff0c\u4e5f\u8bb8\u80fd\u6551\u4e0b\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u5374\u611f\u89c9\u5230\u81ea\u5df1\u65e0\u80fd\u4e3a\u529b\uff1a \u3000\u3000\u5343\u94a7\u4e00\u53d1\u7684\u65f6\u523b\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u518d\u80fd\u963b\u6b62\u9ece\u8679\u523a\u51fa\u8fd9\u4e00\u5200\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u591f\u6551\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u9ec4\u7075\u54ac\u7259\u51fa\u638c\uff0c\u62cd\u5411\u9ece\u8679\u3002 \u3000\u3000\u7830\u7136\u4e00\u58f0\uff0c\u53cc\u638c\u63a5\u5b9e\u3002 \u3000\u3000\u9ece\u8679\u7684\u5200\u5df2\u5230\u4e86\u9ad8\u5e7d\u5170\u7684\u54bd\u5589\u4e4b\u4e0a\uff0c\u4f46\u5374\u88ab\u8fd9\u75be\u5982\u8fc5\u7535\u7684\u4e00\u638c\uff0c\u9707\u5f97\u8eab\u8eaf\u5fae\u5fae\u4e00\u9707\uff0c\u5411\u4e00\u4fa7\u504f\u53bb\u3002 \u3000\u3000\u523a\u5411\u54bd\u5589\u7684\u4e00\u5200\uff0c\u4e5f\u56e0\u4e3a\u8fd9\u4e00\u504f\u9519\u5f00\u4e86\u4e00\u5bf8\u3002 \u3000\u3000\u5200\u5203\u5212\u8fc7\u4e86\u9ad8\u5e7d\u5170\u7684\u9888\u5b50\uff0c\u9c9c\u8840\u6dcb\u6f13\u3002 \u3000\u3000\u9ec4\u7075\u811a\u843d\u5b9e\u5730\uff0c\u7acb\u523b\u7684\u98de\u8dc3\u800c\u8d77\uff0c\u53c8\u4e00\u811a\u8e22\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9ece\u8679\u7684\u8eab\u5b50\u5ffd\u7136\u98d8\u8d77\uff0c\u5411\u540e\u9000\u5f00\u3002 \u3000\u3000\u9ec4\u7075\u4e00\u811a\u6765\u4e2d\uff0c\u4eba\u5df2\u8de8\u8fc7\u4e86\u6728\u69bb\uff0c\u62e6\u5728\u4e86\u9ad8\u5e7d\u5170\u7684\u8eab\u524d\u3002 \u3000\u3000\u5306\u5fd9\u95f4\uff0c\u76ee\u5149\u4e00\u63a0\uff0c\u53d1\u89c9\u4e86\u9ad8\u5e7d\u5170\u9888\u95f4\u6ee1\u662f\u9c9c\u8840\u3002 \u3000\u3000\u4f46\u5df2\u65e0\u6687\u67e5\u770b\uff0c\u9ad8\u5e7d\u5170\u662f\u5426\u5df2\u6b7b\u3002 \u3000\u3000\u8fd9\u4e00\u751f\u4e2d\uff0c\u9ec4\u7075\u9047\u4e0a\u8fc7\u591a\u6b21\u7684\u5371\u9669\uff0c\u4f46\u5374\u4ece\u6765\u6ca1\u6709\u8fd9\u4e00\u6b21\u60b2\u545b\u3001\u607c\u6012\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9ece\u8679\uff0c\u4f60\u8be5\u6b7b\u4e86\u3002\u201d \u3000\u3000\u9ece\u8679\u7728\u52a8\u4e86\u4e00\u4e0b\u773c\u775b\uff0c\u9053\uff1a\u201c\u4ed6\u8bf4\u7684\u4e0d\u9519\uff0c\u4e00\u65e6\u4ed6\u6b7b\u4e86\uff0c\u6c5f\u6e56\u4e2d\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u653e\u8fc7\u6211\u3002\u201d \u3000\u3000\u9ec4\u7075\u6709\u7740\u557c\u7b11\u7686\u975e\u7684\u611f\u89c9\u3002 \u3000\u3000\u9ec4\u7075\u5f3a\u5fcd\u5fc3\u4e2d\u4e00\u53e3\u6012\u6c14\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8c01\u8bf4\u7684?\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u9ad8\u5929\u5065\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4f60\u65e0\u5fc3\u4e3a\u6076\uff0c\u4f46\u4f60\u505a\u51fa\u6765\u7684\u4e8b\u60c5\uff0c\u786e\u662f\u53ef\u6015\u7684\u5f88\u3002\u201d \u9ece\u8679\u9053\uff1a\u201c\u6211!\u505a\u4e86\u4ec0\u4e48\u4e8b?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u79bd\u517d\u4e0d\u5982\uff0c\u8be5\u6740\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u4f60\u4e0d\u8be5\u5230\u8fd9\u5730\u65b9\u6765\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6068\u6211\u4e0d\u8be5\u6765\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u9ece\u8679\u9053\uff1a\u201c\u6211\u2026\u2026\u4e5f\u8981\u6740\u4f60\u3002\u201d \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u633a\u5200\u523a\u6765\u3002 \u3000\u3000\u9ec4\u7075\u8eab\u7ecf\u767e\u6218\uff0c\u89c1\u8bc6\u8fc7\u65e0\u6570\u7684\u9ad8\u624b\uff0c\u4f46\u5374\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u50cf\u8fd9\u6837\u7684\u5200\u6cd5\u3002 \u3000\u3000\u4e00\u65f6\u95f4\uff0c\u7adf\u7136\u88ab\u95f9\u5f97\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u9ece\u8679\u7684\u5200\u6cd5\uff0c\u5374\u8d8a\u6765\u8d8a\u662f\u51cc\u5389\uff0c\u914d\u5408\u4ed6\u4f73\u5999\u7684\u8eab\u6cd5\uff0c\u5f53\u771f\u662f\u6709\u7740\u795e\u9b3c\u96be\u6d4b\u4e4b\u673a\u3002 \u3000\u3000\u9ec4\u7075\u867d\u7136\u6709\u4e00\u8eab\u5353\u7edd\u7684\u6b66\u529f\uff0c\u4f46\u5374\u88ab\u903c\u5f97\u6709\u4e9b\u65bd\u5c55\u4e0d\u5f00\uff0c\u5b8c\u5168\u9677\u5165\u4e86\u88ab\u52a8\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u4eba\u6b66\u529f\u4e4b\u9ad8\uff0c\u7edd\u5bf9\u4e0d\u5728\u9ad8\u5929\u5065\u4e4b\u4e0b\u3002 \u3000\u3000\u5e78\u597d\uff0c\u5434\u53cc\u5973\u3001\u4f55\u5bd2\u8863\u53ca\u65f6\u800c\u81f3\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u91d1\u5251\u4e00\u632f\uff0c\u75be\u653b\u800c\u4e0a\uff0c\u66ff\u4e0b\u4e86\u9ec4\u7075\u3002 \u3000\u3000\u9ece\u8679\u7684\u77ed\u5200\u5ffd\u7136\u95f4\u4e00\u53d8\uff0c\u75be\u653b\u4e86\u4e09\u5200\uff0c\u8feb\u5f97\u4f55\u5bd2\u8863\u5411\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u98de\u8eab\u800c\u8d77\uff0c\u76f4\u5411\u7a97\u5916\u98de\u53bb\u3002 \u3000\u3000\u51e0\u4eba\u505c\u8eab\u5728\u4e03\u697c\u4e4b\u4e0a\uff0c\u8ddd\u5730\u6709\u516b\u4e5d\u4e08\u9ad8\uff0c\u518d\u597d\u7684\u8f7b\u529f\uff0c\u6454\u4e0b\u53bb\uff0c\u4e0d\u6b7b\u4e5f\u8981\u91cd\u4f24\u3002 \u3000\u3000\u4f46\u9ece\u8679\u5374\u76f4\u7a9c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5954\u5230\u7a97\u53e3\uff0c\u5411\u5916\u671b\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u9ece\u8679\u7684\u8eab\u5b50\uff0c\u843d\u5728\u4e86\u7b2c\u4e94\u5c42\u697c\u65f6\uff0c\u4e0d\u77e5\u7528\u7684\u4ec0\u4e48\u529b\u91cf\uff0c\u50cf\u6253\u79cb\u5343\u4e00\u822c\uff0c\u843d\u56de\u5230\u4e94\u697c\u7a97\u524d\uff0c\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u3001\u5434\u53cc\u5973\u65e0\u6687\u8ffd\u654c\uff0c\u4f38\u624b\u6276\u8d77\u4e86\u9ad8\u5e7d\u5170\u3002 \u3000\u3000\u9ece\u8679\u867d\u7136\u70b9\u4e86\u5979\u4e09\u5904\u7a74\u9053\uff0c\u4f46\u4ed6\u70b9\u7a74\u7684\u624b\u6cd5\u5e76\u4e0d\u7279\u6b8a\u3002 \u3000\u3000\u5434\u53cc\u5973\u89e3\u5f00\u4e86\u9ad8\u5e7d\u5170\u7684\u7a74\u9053\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u53d6\u51fa\u91d1\u75ae\u836f\uff0c\u66ff\u9ad8\u5e7d\u5170\u6577\u4e0a\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u7741\u5f00\u4e86\u53cc\u76ee\uff0c\u770b\u4f55\u5bd2\u8863\u7684\u7d27\u5f20\u795e\u60c5\uff0c\u5fcd\u4e0d\u4f4f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u4f60\u4e0d\u7528\u6025\uff0c\u8fd9\u4e00\u5200\uff0c\u770b\u4e0a\u53bb\u5f88\u5371\u9669\uff0c\u4f46\u5374\u504f\u4e86\u4e00\u4e9b\uff0c\u6211\u4f24\u5f97\u4e0d\u91cd\u3002\u201d \u3000\u3000\u542c\u5979\u8bf4\u8bdd\uff0c\u5341\u5206\u6b63\u5e38\uff0c\u4f55\u5bd2\u8863\u3001\uff1a\u5434\u53cc\u5973\u624d\u653e\u4e0b\u4e86\u5fc3\u3002\u5434\u53cc\u5973\u4f4e\u58f0\u9053\uff1a\u201c\u5170\u59b9\uff0c\u5e78\u597d\u4f60\u4f24\u5f97\u4e0d\u91cd\uff0c\u4f60\u4e0d\u77e5\u9053\u4ed6\u521a\u624d\u90a3\u526f\u7126\u6025\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u770b\u5230\u4e86\uff0c\u4ed6\u597d\u50cf\u5f88\u9547\u9759\u561b?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u53e3\u6c14\uff0c\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u600e\u4e48?\u6211\u8bf4\u9519\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u4f60\u7684\u4f24\u5e76\u4e0d\u8f7b\uff0c\u6d41\u4e86\u5f88\u591a\u7684\u8840\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5ffd\u7136\u633a\u8eab\u5750\u4e86\u8d77\u6765\u3002\u4f55\u5bd2\u8863\u5413\u4e86\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u5e72\u4ec0\u4e48?\u8fd8\u4e0d\u8eba\u7740\u4f11\u606f\uff0c\u5f53\u5fc3\u9707\u52a8\u4e86\u4f24\u53e3\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u4f38\u624b\u53d6\u51fa\u4e86\u4e00\u65b9\u767d\u8272\u7684\u7ee2\u5e15\uff0c \u81ea\u5df1\u628a\u4f24\u53e3\u5305\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u8981\u7d27\u4e86\uff0c\u8fd9\u4e2a\u4eba\u5fc5\u9700\u8981\u65e9\u4e9b\u94f2\u9664\uff0c\u7559\u4e0b\u4ed6\uff0c\u53ea\u6015\u662f\u4e00\u5927\u7978\u60a3\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u9ec4\u7075\u5df2\u7ecf\u6084\u7136\u79bb\u53bb\u3002 \u3000\u3000\u5ba4\u4e2d\u53ea\u4f59\u4e0b\u4e86\u592b\u59bb\u4e09\u4eba\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u9ec4\u5821\u4e3b\u5df2\u7ecf\u8ffd\u4e0b\u53bb\u4e86\uff0c\u5170\u59b9\uff0c\u4f60\u5982\u80fd\u591f\u884c\u52a8\uff0c\u54b1\u4eec\u4e5f\u8981\u79bb\u5f00\u4e86\uff0c\u542c\u8bf4\u8fd9\u91cc\u9762\u57cb\u6709\u706b\u836f\u968f\u65f6\u53ef\u4ee5\u7206\u70b8\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u8dc3\u4e0b\u6728\u69bb\uff0c\u9053\uff1a \u201c\u8d70!\u8fd9\u70b9\u4f24\u4e0d\u4f1a\u59a8\u788d\u5230\u6211\u7684\u884c\u52a8\uff0c\u53ea\u4e0d\u8fc7\u8981\u7559\u4e0b\u4e00\u4e2a\u4f24\u75a4\uff0c\u5927\u59ca\uff0c\u6211\u62c5\u5fc3\u4f1a\u56e0\u6b64\u5931\u53bb\u4f55\u90ce\u7684\u6b22\u5fc3\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u8fd9\u8bdd\u4e0d\u9519\uff0c\u7537\u4eba\u5bb6\u89c1\u5f02\u601d\u8fc1\u81ea\u7136\u662f\u9760\u4e0d\u4f4f\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5fc3\u4e2d\u5f88\u6025\uff0c\u4f46\u8d8a\u6025\u8d8a\u60f3\u4e0d\u51fa\u8be5\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u53ea\u542c\u9ec4\u7075\u7684\u58f0\u97f3\uff0c\u4f20\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u9ece\u8679\uff0c\u4f60\u5982\u6b64\u6267\u8ff7\u4e0d\u609f\uff0c\u90a3\u662f\u81ea\u5bfb\u6b7b\u8def\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u5954\u8fd1\u7a97\u53e3\uff0c\u5411\u4e0b\u671b\u4e0b\u53bb\uff0c\u591c\u8272\u4e2d\uff0c\u53ea\u89c1\u4e24\u4e2a\u4eba\u76f8\u5bf9\u800c\u82b7\u3002 \u3000\u3000\u4e0d\u77e5\u9ec4\u7075\u7528\u7684\u4ec0\u4e48\u529e\u6cd5\uff0c\u5df2\u628a\u9ece\u8679\u903c\u4e0b\u4e86\u70df\u9501\u697c\u3002 \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u54b1\u4eec\u4e0b\u53bb\u5427!\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5206\u6210\u4e09\u9762\uff0c\u6084\u7136\u63a9\u8fc7\u53bb\uff0c\u628a\u4ed6\u5835\u4f4f\uff0c\u4e00\u65e6\u52a8\u4e0a\u4e86\u624b\uff0c\u90fd\u4e0d\u8981\u624b\u4e0b\u7559\u60c5\u3002\u201d \u3000\u3000\u4e09\u4eba\u7acb\u523b\u884c\u52a8\uff0c\u8fc5\u901f\u7684\u5954\u4e0b\u697c\u53bb\u3002 \u3000\u3000\u9ec4\u7075\u53d1\u89c9\u4e86\u5f71\u5b50\u9ece\u8679\u7684\u6b66\u529f\u592a\u9ad8\uff0c\u5fc3\u77e5\u4e00\u4e2a\u4eba\u60f3\u5236\u670d\u4ed6\uff0c\u5e76\u975e\u6613\u4e8b\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u51fa\u58f0\u62db\u547c\uff0c\u7528\u5fc3\u4e5f\u5c31\u662f\u60f3\u77e5\u4f1a\u5434\u53cc\u5973\u548c\u9ad8\u5e7d\u5170\u7b49\u5408\u624b\u9ece\u8679\u624b\u4e2d\u4ecd\u63e1\u7740\u90a3\u67c4\u77ed\u5200\uff0c\u51b7\u51b7\u5730\u671b\u7740\u9ec4\u7075\uff0c\u4ed6\u7684\u795e\u60c5\u4e0d\u6012\uff0c\u4e0d\u706b\uff0c\u4f46\u76ee\u5149\u4e2d\uff0c\u5374\u900f\u7740\u4e00\u80a1\u9634\u68ee\u7684\u5473\u9053\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u3001\u5434\u53cc\u5973\u3001\u4f55\u5bd2\u8863\uff0c\u540c\u65f6\u7531\u4e8c\u4e2a\u65b9\u4f4d\u51fa\u73b0\u3002 \u3000\u3000\u56db\u4e2a\u4eba\uff0c\u5df2\u7ec4\u6210\u4e86\u5408\u56f4\u4e4b\u52bf\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u9ece\u8679\uff0c \u4f60\u5f88\u574f\uff0c\u4e5f\u5f88\u6bd2\uff0c \u5df2\u4e0d\u80fd\u7559\u4f60\u3002\u201d \u3000\u3000\u9ece\u8679\u7684\u76ee\u5149\u8f6c\u52a8\uff0c\u73af\u89c6\u4e86\u56db\u4eba\u4e00\u773c\uff0c\u9053\uff1a \u201c\u81f3\u5c11\uff0c\u6b7b\u4e86\u53ef\u4ee5\u4e0d\u5403\u4e1c\u897f\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u4f7f\u6211\u5f88\u70e6\u607c\u3002\u201d \u3000\u3000\u4e00\u822c\u4eba\u89c9\u7740\u6700\u7b80\u5355\u7684\u4e8b\uff0c\u5403\u996d\uff0c\u4f46\u5bf9\u9ece\u8679\u800c\u8a00\uff0c\u5374\u662f\u9971\u53d7\u8fd9\u4ef6\u4e8b\u7684\u5a01\u80c1\u3002 \u3000\u3000\u8fd9\u662f\u6700\u76f4\u63a5\u7684\u5a01\u80c1\uff0c\u4e5f\u662f\u4ed6\u6700\u5f3a\u70c8\u771f\u5b9e\u7684\u611f\u53d7\u3002 \u3000\u3000\u4ed6\u5df2\u53d7\u8fc7\u4e86\u9965\u997f\u7684\u75db\u82e6\uff0e\u4f46\u6ca1\u6709\u5c1d\u8bd5\u8fc7\u6b7b\u4ea1\u3002 \u3000\u3000\u6240\u4ee5\uff0c\u4ed6\u4e0d\u6015\u6b7b\u4ea1\u3002 \u3000\u3000\u5982\u82e5\u4eba\u6709\u5206\u7b49\uff0c\u8fd9\u9ece\u8679\u5e94\u8be5\u662f\u4e0b\u611a\u4e2d\u4eba\uff0c \u3000\u3000\u4f46\u4ed6\u7684\u6b66\u529f\u5374\u662f\u7b2c\u4e00\u6d41\u7684\u8eab\u624b\uff1a \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a \u201c\u9ece\u8679\uff0c\u653e\u4e0b\u4f60\u624b\u4e2d\u4e52\u5203\uff0c\u6211\u4eec\u4e0d\u6740\u4f60\uff0c\u800c\u4e14\uff0c\u6bcf\u65e5\u4f1a\u8ba9\u4f60\u6709\u5f88\u597d\u7684\u4e1c\u897f\u5403\u3002\u201d \u3000\u3000\u9ece\u8679\u7728\u52a8\u4e86\u4e00\u4e0b\u773c\u775b\uff0c\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u5434\u53cc\u5973\u9053\uff1a\u201c\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u9ece\u8679\u671b\u671b\u9ec4\u7075\u3001\u4f55\u5bd2\u8863\u3001\u9ad8\u5e7d\u5170\uff0c\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u5728\u60f3\u4e9b\u4ec0\u4e48? \u3000\u3000\u4ed6\u662f\u90a3\u79cd\u83ab\u53ef\u9884\u6d4b\u7684\u4eba\uff0c\u4e00\u822c\u4eba\u7684\u667a\u6167\uff0c\u5b8c\u5168\u65e0\u6cd5\u60f3\u5230\u4ed6\u4f1a\u505a\u4e9b\u4ec0\u4e48\uff1f \u3000\u3000\u552f\u4e00\u7684\u529e\u6cd5\uff0c \u53ea\u6709\u6ce8\u610f\u4ed6\u7684\u76ee\u5149\uff0c\u795e\u60c5\u53cd\u5e94\uff0c\u4ee5\u4f5c\u9884\u9632\u3002 \u9ad8\u5e7d\u5170\u4e00\u76f4\u6781\u529b\u7684\u5fcd\u8010\u7740\uff0c\u4f46\u8fd8\u662f\u5fcd\u8010\u4e0d\u4f4f\uff0e\u53f9\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5927\u59ca\uff0c\u8fd9\u4e2a\u4eba\u7f6a\u6076\u6df1\u91cd\uff0c\u66f4\u53ef\u6015\u7684\u662f\u4ed6\u4e3a\u6076\u4e0d\u77e5\uff0c \u7559\u4e0b\u4ed6\uff0c\u53ea\u6015\u4f1a\u8d3b\u5bb3\u65e0\u7a77\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6740\u4e86\u4ed6\u3002\u201d \u9ece\u8679\u7684\u53cd\u5e94\u662f\uff0c\u53cc\u65e5\u4e2d\u795e\u5149\u70af\u70af\uff0c\u6295\u6ce8\u5728\u9ad8\u5e7d\u5170\u7684\u8138\u4e0a\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u53cc\u624b\u6267\u5251\uff0c\u8fd0\u6c14\u6212\u5907\uff0c\u5df2\u53ef\u968f\u65f6\u51fa\u624b\u3002 \u3000\u3000\u5434\u53cc\u5973\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5170\u59b9\uff0c\u4ee5\u4ed6\u7684\u4e3a\u6076\uff0c\u867d\u7136\u6b7b\u6709\u4f59\u8f9c\uff0c\u4f46\u4ed6\u5728\u6700\u91cd\u8981\u7684\u65f6\u523b\uff0c\u672a\u52a9\u9ad8\u5929\u5065\uff0c\u5e2e\u4e86\u54b1\u4eec\u4e00\u6b21\u5927\u5fd9\uff0c\u4ed6\u5982\u771f\u80af\u5f03\u5200\u53d7\u964d\uff0c\u4f55\u4e0d\u653e\u4ed6\u4e00\u6761\u751f\u8def\u3002\u201d \u3000\u3000\u9ece\u8679\u7a81\u7136\u95f4\u6709\u4e86\u4e00\u4e2a\u53e4\u602a\u7684\u53cd\u5e94\uff0c\u51b2\u7740\u5434\u53cc\u5973\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u6709\u4e24\u9053\u90aa\u6076\u7684\u76ee\u5149\uff0c\u76ef\u6ce8\u5728\u5434\u53cc\u5973\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u4e0d\u77e5\u4ed6\u5fc3\u4e2d\u5728\u60f3\u4e9b\u4ec0\u4e48?\u4f46\u4ed6\u53cc\u988a\u4e0a\uff0c\u5374\u6cdb\u8d77\u4e86\u4e00\u9635\u9635\u7ea2\u6655\u3002 \u3000\u3000\u5434\u53cc\u5973\u5fc3\u795e\u4e00\u9707\u3002 \u3000\u3000\u5979\u611f\u53d7\u5230\u9ece\u8679\u5fc3\u4e2d\u7684\u6b32\u5ff5\u548c\u90a3\u76ee\u5149\u7684\u90aa\u6c14\u3002 \u3000\u3000\u201c\u8fd9\u4e2a\u4eba\uff0c\u4e0d\u53ef\u6551\u836f\u4e86\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u4e5f\u6cdb\u8d77\u4e86\u6740\u673a\u3002 \u3000\u3000\u4f46\u9ece\u8679\u5374\u62a2\u5148\u53d1\u52a8\uff0c\u5200\u8292\u4e00\u95ea\uff0c\u5feb\u5982\u95ea\u7535\u4e00\u822c\uff0c\u523a\u5411\u4e86\u5434\u53cc\u5973\uff0c\u5200\u5149\u6307\u88ad\u4e4b\u5904\uff0c\u7adf\u662f\u5979\u80f8\u8179\u4e4b\u4e0b\u3002 \u3000\u3000\u8c01\u4f1a\u6599\u5230\u5462\uff0c\u9ece\u8679\u7adf\u7136\u4f1a\u5148\u653b\u5411\u4e3a\u4ed6\u6551\u547d\u7684\u4eba\u3002 \u3000\u3000\u5434\u53cc\u5973\u7f9e\u6124\u4ea4\u96c6\uff0c\u4e00\u5438\u6c14\uff0c\u5411\u540e\u9000\u5f00\u4e86\u516b\u5c3a\u3002 \u3000\u3000\u4f46\u9ece\u8679\u7adf\u7136\u5982\u5f71\u968f\u5f62\u4e00\u822c\uff0c\u9644\u8eab\u800c\u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u901f\u5ea6\u5feb\uff0c\u5feb\u5f97\u8fde\u5434\u53cc\u5973\u65e0\u6cd5\u4eae\u51fa\u5175\u5203\u3002 \u3000\u3000\u5728\u56f4\u4f4f\u9ece\u8679\u7684\u56db\u4e2a\u4eba\u4e2d\uff0c\u5434\u53cc\u5973\u662f\u6700\u65e0\u6212\u5907\u7684\u4e00\u4e2a\u4eba\uff0c\u8138\u4e0a\u7684\u795e\u60c5\u4e5f\u6700\u4e3a\u6e29\u548c\uff0c\u5e26\u7740\u5fae\u5fae\u7684\u7b11\u610f\u3002 \u3000\u3000\u9ece\u8679\u5374\u4e3a\u5979\u7684\u7b11\u5bb9\u6e29\u548c\u800c\u51fa\u5200\u3002 \u3000\u3000\u5e78\u597d\u9ec4\u7075\u53ca\u65f6\u653b\u51fa\u4e86\u4e00\u5251\uff0c\u5251\u6c14\u5982\u8679\uff0c\u963b\u62e6\u4f4f\u4e86\u9ece\u8679\u7684\u653b\u52bf\u3002 \u3000\u3000\u5434\u53cc\u5973\u7f13\u5f00\u624b\u811a\uff0c\u4eae\u51fa\u4e86\u5175\u5203\uff0c\u75be\u6251\u5411\u9ece\u8679\uff0c \u53e3\u4e2d\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5170\u59b9\uff0c\u4ed6\u4e0d\u662f\u4eba\uff0c\u4ed6\u8be5\u6b7b\uff0c\u4e0d\u80fd\u7559\u4e0b\u4ed6\u3002\u201d \u3000\u3000\u5c31\u5728\u8bf4\u8bdd\u7684\u5de5\u592b\uff0c\u5df2\u653b\u51fa\u4e86\u4e8c\u5341\u4e00\u5251\u3002 \u3000\u3000\u5251\u5251\u51cc\u5389\uff0c\u53d6\u9b42\u593a\u9b44\u3002 \u3000\u3000\u5434\u53cc\u5973\u786e\u5df2\u52a8\u4e86\u6012\u706b\u3002 \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4ed6\u672c\u6765\u4e0d\u662f\u4eba\uff0e\u53ea\u662f\u4e00\u4e2a\u5f71\u5b50\uff0c\u4ed6\u65e0\u662f\u975e\u4e4b\u5fc3\uff0c\u597d\u6076\u4e4b\u5ff5\uff0c\u9ad8\u5929\u5065\u7559\u7ed9\u4ed6\u592a\u591a\u7684\u90aa\u6076\u610f\u8bc6\uff0c\u8fd9\u4e9b\uff0c\u5df2\u5728\u4ed6\u5fc3\u4e2d\u751f\u6839\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u5df2\u4eae\u51fa\u4e86\u4e94\u628a\u5251\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u5df2\u5145\u6ee1\u6068\u610f\uff0c\u51c6\u5907\u4e00\u4e3e\u6251\u6740\u9ece\u8679\u3002 \u3000\u3000\u573a\u4e2d\u4e4b\u4eba\uff0c\u90fd\u5df2\u52a8\u4e86\u9664\u53bb\u9ece\u8679\u7684\u7528\u5fc3\u3002 \u3000\u3000\u4f46\u9ec4\u7075\u3001\u4f55\u5bd2\u8863\u3001\u9ad8\u5e7d\u5170\uff0c\u90fd\u5728\u66f2\u624b\u65c1\u89c2\u3002 \u3000\u3000\u5434\u53cc\u5973\u7684\u5251\u6cd5\u592a\u51cc\u5389\uff0c\u5468\u56f4\u4e00\u4e08\u5185\uff0c\u90fd\u5145\u6ee1\u7740\u51cc\u5389\u7684\u5251\u98ce\u3002 \u3000\u3000\u4e09\u4eba\u7eb5\u7136\u60f3\u52a9\u62f3\uff0c\u5374\u6709\u7740\u65e0\u6cd5\u4e0b\u624b\u4e4b\u611f\u3002 \u3000\u3000\u53ef\u60dc\u90a3\u6837\u51cc\u5389\u5251\u6cd5\uff0c\u7adf\u7136\u65e0\u6cd5\u4f24\u5230\u9ece\u8679\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u8f7b\u529f\u9020\u8be3\uff0c \u5df2\u5230\u4e86\u865a\u65e0\u98d8\u6e3a\u4e4b\u5883\uff0c\u50cf\u4e00\u56e2\u67d4\u8f7b\u7684\u98d8\u7d6e\uff0c\u8fdb\u9000\u81ea\u5982\uff0c\u968f\u7740\u5251\u52bf\u79fb\u52a8\u3002 \u3000\u3000\u4ed6\u624b\u4e2d\u53ea\u6709\u4e00\u628a\u77ed\u5200\uff0c\u4f46\u5728\u5434\u53cc\u5973\u7eb5\u6a2a\u7684\u5251\u52bf\u4e2d\uff0c\u4ecd\u7136\u6709\u5b88\u6709\u653b\u3002 \u3000\u3000\u9ece\u8679\u5200\u52bf\u7684\u51f6\u5389\uff0c\u4e5f\u8bb8\u4e0d\u5982\u9ad8\u5929\u5065\uff0c\u4f46\u8be1\u53d8\u5947\u5e7b\uff0c\u7edd\u4e0d\u5728\u9ad8\u5929\u5065\u4e4b\u4e0b\uff0c\u5982\u8bba\u8f7b\u529f\u9020\u8be3\uff0c\u95ea\u907f\u4e4b\u5999\uff0c\u90a3\u662f\u9ad8\u5929\u5065\u4ea6\u96be\u53ca\u5f97\u4e86\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4f24\u5728\u9888\u95f4\uff0c\u6bcf\u6b21\u8bf4\u8bdd\uff0c\u90fd\u6709\u4e9b\u9690\u9690\u4f5c\u75db\u3002 \u3000\u3000\u4f46\u5374\u5fcd\u4e0d\u4f4f\u53eb\u9053\uff1a \u201c\u5927\u59ca\uff0c\u903c\u4ed6\u51fa\u4f60\u7684\u5251\u52bf\u4e4b\u5916\uff0c\u4ed6\u6b66\u529f\u7279\u6b8a\uff0c\u7f20\u6597\u4e0b\u53bb\uff0c\u5f92\u7136\u6d6a\u8d39\u6c14\u529b\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u7a81\u7136\u7f29\u5c0f\u4e86\u5251\u5708\uff0c\u4ee5\u5b88\u4ee3\u653b\u3002 \u3000\u3000\u679c\u7136\uff0c\u9ece\u8679\u88ab\u903c\u51fa\u4e86\u5251\u52bf\u4e4b\u5916\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u5237\u5237\u4e24\u5251\uff0c\u903c\u5f97\u9ece\u8679\u8eab\u8eaf\u5411\u65c1\u4fa7\u504f\u53bb\u3002 \u3000\u3000\u9ec4\u7075\u8fce\u5934\u75db\u51fb\uff0c\u4e00\u5251\u201c\u6cf0\u5c71\u538b\u9876\u201d\u5288\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5251\u52bf\u4e0d\u5feb\uff0c\u62db\u6570\u666e\u901a\uff0c\u4f46\u5251\u4e0a\u5374\u542b\u8574\u4e86\u9ec4\u7075\u4e5d\u6210\u529f\u529b\u3002 \u3000\u3000\u5bf9\u4ed8\u6b66\u529f\u5201\u94bb\u7684\u9ece\u8679\uff0c\u9ec4\u7075\u4e0d\u5f97\u4e0d\u7528\u70b9\u5fc3\u673a\u4e86\u3002 \u3000\u3000\u9ece\u8679\u679c\u7136\u4e0d\u660e\u5185\u60c5\uff0c\u77ed\u5200\u4e00\u64a9\uff0c\u5411\u4e0a\u683c\u53bb\u3002 \u3000\u3000\u4ed6\u51fa\u5200\u5982\u7535\uff0c\u9ec4\u7075\u867d\u5148\u51fa\u5251\uff0c\u4f46\u9ece\u8679\u7684\u5200\u5149\u540e\u53d1\u5148\u81f3\uff0c\u5728\u9876\u5934\u4e00\u5c3a\u5904\uff0c\u67b6\u4f4f\u4e86\u957f\u5251\uff0c\u8eab\u5b50\u4e5f\u540c\u65f6\u5411\u524d\u6b3a\u53bb\uff0c\u5de6\u624b\u626c\u8d77\uff0c\u51fb\u5411\u9ec4\u7075\u524d\u80f8\u3002 \u3000\u3000\u5200\u5251\u76f8\u89e6\uff0c\u5251\u4e0a\u8574\u85cf\u7684\u5185\u52b2\uff0c\u7a81\u7136\u8fdb\u53d1\uff0c\u529b\u903e\u5343\u65a4\uff0c\u76f4\u538b\u4e0b\u6765\u3002 \u3000\u3000\u9ece\u8679\u518d\u60f3\u62bd\u5200\u95ea\u907f\uff0c\u5df2\u6765\u4e0d\u53ca\u3002 \u3000\u3000\u4ed6\u7684\u7279\u957f\u662f\u8f7b\u529f\u5353\u7edd\uff0c\u6765\u53bb\u5982\u98ce\uff0c\u51fa\u624b\u8be1\u5f02\u8fc5\u75be\uff0c\u8fd9\u4e00\u6b21\uff0c\u88ab\u9ec4\u7075\u5251\u52bf\u903c\u4f4f\uff0c\u4e0d\u5f97\u4e0d\u8fd0\u6c14\u6297\u62d2\u3002 \u3000\u3000\u5434\u53cc\u5973\u51b7\u7b11\u4e00\u58f0\uff0c\u6b3a\u8eab\u800c\u4e0a\uff0c\u4e00\u5251\u523a\u53bb\u3002 \u3000\u3000\u9ece\u8679\u5ffd\u7136\u5f03\u5200\u5012\u5730\u3002 \u3000\u3000\u9ec4\u7075\u5251\u52bf\u75be\u6c89\uff0c\u5288\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9ece\u8679\u7ffb\u6eda\u5982\u98ce\uff0c\u582a\u582a\u907f\u8fc7\u4e86\u5411\u4e0b\u51fb\u843d\u7684\u4e00\u5251\u3002 \u3000\u3000\u91d1\u8292\u4e00\u95ea\uff0c\u4f55\u5bd2\u8863\u98de\u5954\u800c\u81f3\u3002 \u3000\u3000\u8fd9\u4e9b\u9ad8\u624b\uff0c\u4ea4\u4e92\u653b\u51fb\uff0c\u770b\u6765\u7f13\u6162\uff0c\u5b9e\u5219\u5feb\u5982\u661f\u706b\u3002 \u3000\u3000\u9ece\u8679\u5728\u56db\u5927\u9ad8\u624b\u5408\u51fb\u4e4b\u4e0b\uff0c\u5ffd\u7136\u4e00\u6296\u4e24\u81c2\uff0c\u51b2\u5929\u800c\u8d77\u3002 \u3000\u3000\u8fd9\u4e00\u5f0f\u201c\u6f5c\u9f99\u5347\u5929\u201d\uff0c\u76f4\u51b2\u8d77\u4e86\u56db\u4e08\u591a\u9ad8\u3002 \u3000\u3000\u8fde\u9ec4\u7075\u4e00\u8eab\u6df1\u539a\u9020\u8be3\u7684\u4eba\uff0c\u4e5f\u770b\u5f97\u4e3a\u4e4b\u4e00\u5446\uff0c \u81ea\u5fd6\u96be\u53ca\u3002 \u516d\u9053\u5251\u5149\u51b2\u5929\u800c\u8d77\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u4e03\u8272\u5251\uff0c\u98de\u51fa\u4e86\u516d\u652f\u3002 \u3000\u3000\u516d\u8272\u5251\uff0c\u6f2b\u7a7a\u5c55\u5e03\u5e7b\u5316\u51fa\u4e00\u7247\u7eee\u4e3d\u7684\u5251\u5e55\u3002 \u3000\u3000\u8fd8\u672a\u5230\u4e03\u5251\u51fa\u624b\uff0c\u4f46\u516d\u5251\u5df2\u7ecf\u591f\u60ca\u4eba\u4e86\u3002 \u3000\u3000\u5f71\u5b50\u9ece\u8679\uff0c\u88ab\u4ea4\u9519\u7684\u5251\u5149\u4e00\u7ede\uff0c\u5316\u4f5c\u4e86\u4e00\u84ec\u8840\u96e8\uff0c\u7eb7\u7eb7\u843d\u4e0b\u3002 \u3000\u3000\u9ec4\u7075\u7b97\u662f\u771f\u6b63\u7684\u770b\u5230\u4e86\u4e03\u8272\u5251\u7684\u5389\u5bb3\uff0c\u4e0d\u7981\u4e00\u5446\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u6536\u4e86\u4e03\u8272\u5251\uff0c\u7b11\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u9ec4\u5821\u4e3b\uff0c\u662f\u4e0d\u662f\u89c9\u5f97\u5b83\u592a\u6076\u6bd2\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u4f46\u4e5f\u5f88\u96be\u7ec3\u6210\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u662f\uff0c\u7ec3\u5230\u4e03\u5251\u5e76\u51fa\u7684\u5883\u754c\uff0c\u4e0d\u4f46\u8981\u4e0b\u4e00\u756a\u529f\u592b\uff0c\u548c\u826f\u5e08\u6307\u70b9\uff0c\u8fd8\u8981\u6709\u6781\u4f73\u7684\u7980\u8d4b\u624d\u884c\uff0c\u6709\u4e9b\u4eba\uff0c\u7a77\u5176\u4e00\u751f\u7cbe\u529b\uff0c\u4e5f\u53ea\u80fd\u7ec3\u5230\u4e94\u5251\u9f50\u98de\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u59d1\u5a18\u7ec3\u5230\u4e86\u4f55\u7b49\u5883\u754c?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u52c9\u5f3a\u53ef\u4ee5\u98de\u51fa\u4e03\u5251\uff0c\u4f46\u5374\u4e0d\u5982\u516d\u5251\u8fd0\u7528\u7eaf\u719f\uff0c\u5f97\u5fc3\u5e94\u624b\u4e86\u3002\u201d \u9ec4\u7075\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u53cc\u5251\u62d2\u654c\uff0c\u4e09\u5251\u514b\u654c\uff0c\u7528\u56db\u5251\uff0c\u8db3\u53ef\u4ee5\u5728\u6c5f\u6e56\u95ef\u8361\uff0c\u7528\u4e94\u5251\uff0c\u53ef\u5217\u5165\u6b66\u6797\u9ad8\u624b\uff0c \u7528\u516d\u5251\u767b\u5802\u5165\u5ba4\uff0c\u53ef\u80fd\u7528\u4e03\u5251\uff0c\u8c13\u767b\u5cf0\u9020\u6781\uff0c\u79f0\u7edd\u5929\u4e0b\uff0c\u4e94\u5251\u4e4b\u540e\uff0c\u6bcf\u591a\u4e00\u5251\uff0e\u5a01\u529b\u589e\u5f3a\u4f55\u6b62\u5341\u500d\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u4e03\u8272\u5251\uff0c\u6c5f\u6e56\u4e0a\u4e00\u5411\u4e0d\u591a\u89c1\uff0c\u4eca\u5929\u5728\u4e0b\u771f\u7b97\u5f00\u4e86\u773c\u754c\u3002\u201d \u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e03\u8272\u5251\u4e00\u5411\u5355\u4f20\uff0c\u6211\u4e0d\u4f1a\u8ba9\u5b83\u6d41\u6bd2\u6c5f\u6e56\uff1a\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a \u201c\u5929\u9b54\u95e8\uff0c\u4e03\u8272\u5251\uff0c\u6c5f\u6e56\u4e0a\u4e24\u5927\u5947\u6280\uff0c\u5168\u5165\u4e86\u4f55\u5144\u5f1f\u4f60\u7684\u638c\u63e1\uff0c\u4eca\u540e\uff0c\u4f55\u5144\uff0c\u4f60\u662f\u4efb\u91cd\u9053\u8fdc\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u6025\u5f97\u8138\u8272\u901a\u7ea2\uff0c\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u5728\u4e0b\u3001\u5728\u4e0b\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4e5f\u4e0d\u4f1a\u518d\u6709\u4e03\u8272\u5251\u3002\u201d \u3000\u3000\u9ec4\u7075\u82e6\u7b11\u4e00\u4e0b\uff0c\u9053\uff1a \u201c\u6c5f\u6e56\u4e0a\u7684\u7edd\u6280\uff0c\u4e0d\u5bb9\u5931\u4f20\uff0c\u5c31\u7b97\u4f60\u6709\u6b64\u5fc3\uff0c\u4f46\u5982\u5230\u751f\u6b7b\u4e4b\u9645\uff0c\u4ea6\u5e38\u4f1a\u6539\u53d8\u4e3b\u610f\uff0c\u5f55\u6210\u7b14\u8bb0\uff0c\u7559\u7ed9\u6709\u7f18\uff0c\u8fd9\u529e\u6cd5\u5f88\u5192\u9669\uff0c\u4e07\u4e00\uff0c\u9047\u5408\u975e\u4eba\uff0c\u5c31\u4f1a\u9020\u6210\u5927\u5bb3\uff0c\u6240\u4ee5\uff0c\u5728\u4e0b\u7684\u770b\u6cd5\uff0c\u4e8b\u5148\u7684\u4e25\u683c\u6311\u9009\uff0c\u5012\u4e5f\u6709\u628a\u63e1\u4e00\u4e9b\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u70b9\u70b9\u5934\u9053\uff1a \u201c\u5821\u4e3b\u8bf4\u7684\u6709\u7406\uff0c\u6211\u4eec\u4f1a\u597d\u597d\u7684\u60f3\u60f3\u8fd9\u4ef6\u4e8b\u60c5\u2026\u2026\u201d \u8bed\u58f0\u4e00\u987f\uff0c \u76ee\u5149\u8f6c\u6ce8\u5230\u4f55\u5bd2\u8863\u7684\u8eab\u4e0a\uff0c\u9053\uff1a \u201c\u4f55\u90ce\uff0c\u76ee\u4e0b\u5927\u5c40\u5df2\u5b9a\uff0c\u6211\u548c\u5927\u59ca\uff0c\u5df2\u7ecf\u6697\u91cc\u5546\u91cf\u4e86\u4e24\u6b21\u5566\uff0c\u73b0\u5728\uff0c\u8981\u4f60\u505a\u4e2a\u51b3\u5b9a\u4e86\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e8b\u60c5\u597d\u50cf\u662f\u5f88\u4e25\u91cd\u5566\uff1f\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u8fd9\u662f\u770b\u6cd5\u4e0a\u7684\u4e0d\u540c\u4e86\uff0c\u6211\u548c\u5927\u59ca\uff0c\u90fd\u89c9\u5f97\u5b83\u5f88\u4e25\u91cd\uff0c\u4f46\u4e0d\u77e5\u4f60\u7684\u770b\u6cd5\u5982\u4f55?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1b\u201c\u8bf4\u8bf4\u770b\u5427?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u548c\u5927\u59ca\uff0c\u90fd\u662f\u7b2c\u4e00\u5821\u7684\u4eba\uff0c\u4e00\u65e6\u548c\u6b63\u5927\u660e\u6237\u4e2d\u76f8\u89c1\uff0c\u5fc5\u4f1a\u6210\u4f17\u77e2\u4e4b\u7684\uff0c\u88ab\u4ed6\u4eec\u903c\u95ee\u5f88\u591a\u4e8b\u60c5\uff0c\u6709\u4e9b\u4e8b\uff0c\u6211\u5f88\u96be\u542f\u9f7f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u7528\u548c\u4ed6\u4eec\u89c1\u9762\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u8fd9\u81ea\u7136\u662f\u5f88\u5bb9\u6613\uff0c\u6211\u4eec\u53ef\u4ee5\u4e00\u8d70\u4e86\u4e4b\uff0c\u4f46\u4f60\u80fd\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u6709\u56f0\u96be\uff0c\u4f60\u662f\u6b66\u5f53\u95e8\u4e0b\u7684\u5f1f\u5b50\uff0c\u603b\u8981\u664b\u89c1\u5e08\u957f\uff0c\u8bf4\u660e\u7ecf\u8fc7\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u4e0d\u4fbf\u52c9\u5f3a\u4f60\u3002\u201d \u3000\u3000\u9ec4\u7075\u8ba4\u4e3a\u4ed6\u4eec\u592b\u59bb\uff0c\u6709\u4e8b\u79d8\u5546\uff0c\u672c\u60f3\u501f\u6545\u79bb\u5f00\uff0c\u4f46\u8d8a\u542c\u8d8a\u89c9\u4e0d\u5bf9\uff0c\u53ea\u597d\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4f55\u5bd2\u8863\u8d8a\u542c\u8d8a\u89c9\u5f97\u60c5\u5f62\u4e0d\u5bf9\uff0c\u4e0d\u7981\u5fc3\u4e2d\u5927\u6025\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u5434\u53cc\u5973\u63a5\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u522b\u5413\u4ed6\u5566\uff0c\u4f60\u770b\u4ed6\u5df2\u7ecf\u53d1\u6025\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u5927\u59ca\uff0c\u4e0d\u662f\u5413\u4ed6\uff0c\u6211\u8bf4\u7684\u5f88\u771f\u5b9e\uff0c\u6211\u4eec\u5fc5\u987b\u6709\u6240\u6289\u62e9\uff0c\u54b1\u4eec\u662f\u7b2c\u4e00\u5821\u7684\u4f59\u5b7d\uff0c\u4e00\u65e6\u548c\u4ed6\u4eec\u89c1\u4e86\u9762\uff0c\u5f88\u53ef\u80fd\u4f1a\u62db\u60f9\u4e0a\u65e0\u6bd4\u7684\u9ebb\u70e6\uff0c\u8bf4\u4e0d\u5b9a\uff0c\u8fd8\u4f1a\u5f15\u8d77\u4e00\u573a\u8840\u8165\u98ce\u6ce2\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u8fd9\u6837\u4e25\u91cd\u5417?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u4f60\u53ef\u66fe\u60f3\u5230\uff0c\u5929\u4e0b\u82f1\u96c4\uff0c\u5404\u6d3e\u638c\u95e8\uff0c\u9f50\u96c6\u4e8e\u7b2c\u4e00\u5821\u540e\uff0c\u5018\u82e5\u5ffd\u7136\u6709\u4e00\u4e2a\u4eba\uff0c\u63d0\u51fa\u4e86\u65a9\u8349\u9700\u9664\u6839\u7684\u610f\u89c1\uff0c\u800c\u53c8\u5f97\u5230\u5927\u591a\u6570\u7684\u9644\u548c\uff0c\u4f60\u8981\u5982\u4f55\u5e94\u4ed8?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u600e\u4f1a\u5982\u6b64\u6df7\u6dc6\u4e0d\u6e05\uff0c\u5982\u6b64\u7684\u4e0d\u8bb2\u7406\uff0c\u8fd9\u4e00\u6b21\uff0c\u5bf9\u4ed8\u7b2c\u4e00\u5821\uff0c\u4f60\u4eec\u51fa\u529b\u6700\u5927\u2026\u2026\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u63a5\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u80fd\u591f\u89e3\u8bf4\u5f97\u6e05\u695a\u5417?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\uff0c\u8fd9\u662f\u4e8b\u5b9e\uff0c\u53c8\u4e0d\u662f\u8c0e\u8a00\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u5982\u82e5\u4ed6\u4eec\u4e0d\u542c\uff0c\u4f60\u80fd\u591f\u548c\u4ed6\u4eec\u7ffb\u8138\u5417?\u522b\u5fd8\u4e86\uff0c\u90a3\u4e9b\u4eba\u4e2d\uff0c\u6709\u4f60\u7684\u540c\u95e8\u5e08\u5144\u5f1f\uff0c\u4e5f\u6709\u4f60\u7684\u5e08\u957f\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u81f3\u5c11\uff0c\u9ec4\u5821\u4e3b\u53ef\u4ee5\u4f5c\u8bc1\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u53ef\u4ee5\u4f5c\u8bc1\uff0c\u4f46\u6211\u7684\u8bc1\u8a00\uff0c\u6709\u591a\u5927\u7684\u529b\u91cf\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u81ea\u5df1\u4e5f\u5f88\u6000\u7591\uff0c\u4f60\u662f\u5949\u547d\u5230\u9634\u9633\u5821\u4e2d\u6f5c\u4f0f\uff0c\u6211\u4eec\u7ecf\u8fc7\u4e86\u5f88\u957f\u4e45\u7684\u4e00\u6bb5\u65f6\u95f4\u76f8\u5904\uff0c\u4f60\u624d\u5bf9\u6211\u4ea7\u751f\u771f\u6b63\u7684\u4e86\u89e3\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u53f9\u606f\u9053\uff1b\u201c\u53ef\u6015\u5440!\u8bef\u4f1a\u3001\u8bef\u4f1a\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a \u201c\u6211\u5df2\u88ab\u8bef\u89e3\u5bb3\u4e86\u5f88\u591a\u5e74\uff0c\u89e3\u91ca\u660e\u767d\u4e00\u4ef6\u5f88\u6df1\u7684\u8bef\u4f1a\uff0c\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u8981\u5982\u4f55\u5462?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u4f55\u90ce\uff0c\u4e0d\u7528\u4e3a\u96be\uff0c\u6211\u548c\u5927\u59ca\uff0c \u5df2\u7ecf\u5546\u91cf\u597d\u4e00\u4e2a\u529e\u6cd5\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u529e\u6cd5?\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a \u201c\u6211\u548c\u5927\u59ca\u5148\u79bb\u5f00\u3002\u4e00\u4e2a\u6708\u540e\uff0c\u6211\u4eec\u5728\u9ec4\u9e64\u697c\u4e0b\u7b49\u4f60\u4e09\u5929\uff0c\u4f60\u53ef\u4ee5\u5728\u8fd9\u6bb5\u65f6\u95f4\u4e2d\uff0c\u597d\u597d\u7684\u60f3\u4e00\u60f3\uff0c\u4f5c\u4e2a\u6289\u62e9\uff0c\u4e09\u5929\u540e\uff0c\u4f60\u5982\u4e0d\u53bb\uff0c\u6211\u4eec\u79bb\u5f00\u4e2d\u539f\u897f\u884c\u3001\u5357\u5f80\uff0c\u8fd8\u672a\u4f5c\u6700\u540e\u51b3\u5b9a\uff0c\u4f46\u6211\u4eec\u51b3\u4e0d\u5728\u4e2d\u539f\u505c\u7559\u4e86\uff0c\u4e0d\u662f\u897f\u9690\u5927\u6f20\uff0c\u5c31\u662f\u8fdc\u8d70\u5357\u8352\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u63a5\u9053\uff1a\u201c\u5e7d\u5170\uff0c\u6211\u8981\u53bb\u7684\uff0c\u54b1\u4eec\u592b\u59bb\u60c5\u6df1\uff0c\u4e09\u4eba\u540c\u547d\uff0c\u6211\u4e0d\u4f1a\u79bb\u5f00\u4f60\u4eec\u3002\u201d \u3000\u3000\u5434\u53cc\u5973\u3001\u9ad8\u5e7d\u5170\uff0c\u90fd\u5f88\u611f\u52a8\uff0c\u6cea\u6c34\u76c8\u776b\uff0c\u4f46\u9ad8\u5e7d\u5170\u4ecd\u7136\u8bf4\u9053\uff1a\u201c\u4f55\u90ce\uff0c\u4e0d\u7ba1\u4f5c\u4ec0\u4e48\u9009\u62e9\uff0c\u6211\u4eec\u90fd\u4e0d\u4f1a\u602a\u4f60\uff0c\u4f60\u613f\u610f\u548c\u6211\u4eec\u4f5c\u4e00\u4e2a\u5e73\u51e1\u592b\u59bb\uff0c\u6211\u548c\u5927\u59ca\uff0c\u90fd\u4f1a\u5584\u5c3d\u59bb\u9053\uff0c\u4f1a\u4f7f\u4f60\u751f\u6d3b\u5c3d\u91cf\u5feb\u4e50\uff0c\u4f60\u4ee3\u8868\u4e86\u6c5f\u6e56\u4e0a\u4fa0\u4e49\u4e00\u9762\uff0c\u8fd9\u6b21\u527f\u4e86\u7b2c\u4e00\u5821\uff0c\u4f1a\u4f7f\u4f60\u7acb\u523b\u6210\u540d\uff0c\u8001\u5b9e\u8bf4\uff0c\u4f60\u7684\u6210\u5c31\u5149\u8f89\uff0c\u4f1a\u8d85\u8fc7\u9ec4\u5821\u4e3b\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u600e\u4e48\u884c\uff0c\u6211\u4f1a\u8bf4\u660e\u5b9e\u60c5\u3002\u201d \u3000\u3000\u9ec4\u7075\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5bd2\u8863\uff0c\u4e00\u5207\u8363\u8a89\uff0c\u90fd\u4f1a\u4e3a\u4f60\u6240\u6709\uff0c\u6211 \u5df2\u548c\u7384\u5a18\u3001\u5999\u771f\u6709\u4e86\u7ea6\u5b9a\uff0c\u6b64\u95f4\u4e8b\u4e00\u4e86\u7acb\u523b\u9690\u53bb\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u90a3\u600e\u4e48\u884c\u2026\u2026\u201d \u3000\u3000\u9ec4\u7075\u63a5\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u6069\u6028\u7ea0\u7f20\uff0c \u4e00\u76f4\u5f88\u96be\u5206\u8fa8\u6e05\u695a\uff0c\u6211\u89c9\u7740\u4e0a\u5929\u5df2\u5bf9\u6211\u591f\u539a\u7231\u7684\u4e86\uff0c\u7384\u5a18\u3001\u534e\u5999\u771f\u90fd\u662f\u4eba\u95f4\u5947\u5973\u5b50\uff0c\u5979\u4eec\u80af\u4e0d\u8ba1\u65e7\u6076\uff0c\u8ddf\u6211\u5578\u50b2\u5c71\u6797\uff0c\u505a\u4e00\u5bf9\u795e\u4ed9\u7737\u5c5e\uff0c\u6211\u8fd8\u4f1a\u6709\u4ec0\u4e48\u4e0d\u6ee1\u8db3\u5462?\u201d \u3000\u3000\u4f55\u5bd2\u8863\u7a81\u7136\u54ac\u7259\uff0c\u9053\uff1a\u201c\u5821\u4e3b\uff0c\u6211\u8981\u5148\u8d70\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u4f55\u5bd2\u8863\u62e5\u7740\u9ad8\u5e7d\u5170\uff0c\u884c\u8fd1\u5434\u53cc\u5973\uff0c\u7275\u8d77\u5434\u53cc\u5973\u7684\u53f3\u624b\uff0c\u9053\uff1a \u3000\u3000\u201c\u6211\u6709\u4e86\u4e24\u4e2a\u5982\u82b1\u5a07\u59bb\uff0c\u4e0a\u5929\u5f85\u6211\uff0c \u5c82\u4e0d\u4f18\u8fc7\u5821\u4e3b\uff0c\u4ec0\u4e48\u6c5f\u6e56\u540d\u671b\uff0c\u4ec0\u4e48\u95e8\u6d3e\u5b97\u4e3b\uff0c\u90fd\u662f\u8fc7\u773c\u4e91\u70df\uff0c\u9ad8\u5929\u5065\u4e00\u4ee3\u4e4b\u96c4\u4e5f\uff0c\u800c\u4eca\u5b89\u5728?\u6211\u8981\u966a\u5979\u4eec\u904d\u6e38\u540d\u5c71\u3001\u80dc\u6c34\uff0c\u8fc7\u4e00\u8fc7\u795e\u4ed9\u751f\u6d3b\u3002\u9ec4\u5821\u4e3b\uff0c\u8fd9\u91cc\u7684\u5584\u540e\u4e8b\u52a1\uff0c\u90fd\u504f\u52b3\u4f60\u4e86\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u4e49\u4e0d\u5bb9\u8f9e\uff0c\u4e0d\u8fc7\uff0c\u6211\u6709\u4e2a\u6761\u4ef6\u3002\u201d \u3000\u3000\u4f55\u5bd2\u8863\u9053\uff1a\u201c\u5821\u4e3b\u5429\u5429?\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6b64\u540e\uff0c\u6bcf\u9694\u4e09\u5e74\uff0c\u54b1\u4eec\u8981\u805a\u4f1a\u4e00\u6b21\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u6211\u4eec\u4f1a\u627e\u5230\u4f60\u7684\u3002\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u597d!\u4e09\u4f4d\u8bf7\u5427!\u6055\u6211\u4e0d\u9001\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u9ec4\u5821\u4e3b\uff0c\u522b\u5fd8\u4e86\u751f\u6b7b\u95e8\uff0c\u4f1a\u9f50\u4e86\u5929\u4e0b\u82f1\u96c4\u4e4b\u540e\uff0c\u5c31\u628a\u95e8\u5c01\u5835\u8d77\u6765\uff0c\u522b\u901e\u80fd\u6253\u5f00\u3002\u201d \u3000\u3000\u9ec4\u7075\u70b9\u70b9\u5934\u3002 \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u9ad8\u98de\u8679\u85cf\u5728\u7b2c\u4e8c\u8fdb\u9662\u843d\u4e2d\u4e00\u4e2a\u5730\u4e0b\u5bc6\u5ba4\u5185\uff0c\u5982\u4f55\u5904\u7f6e\u8bf7\u9ec4\u5821\u4e3b\u51b3\u5b9a\u5427!\u201d \u3000\u3000\u9ec4\u7075\u9053\uff1a\u201c\u6211\u5c3d\u91cf\u4ec1\u6148\uff0c\u7ed9\u4ed6\u4e00\u6761\u751f\u8def\u3002\u201d \u3000\u3000\u9ad8\u5e7d\u5170\u9053\uff1a\u201c\u518d\u544a\u8bc9\u4f60\u4e00\u4e2a\u79d8\u5bc6\uff0c\u9ad8\u5929\u5065\u5367\u5ba4\u4e00\u4e2a\u5939\u58c1\u85cf\u4e86\u4e00\u672c\u5e10\u518c\uff0c\u8bb0\u4e0b\u4e86\u4ed6\u4e00\u751f\u7684\u6069\u6028\uff0c\u548c\u6709\u5173\u5723\u6c34\u673a\u5bc6\u7684\u8d44\u6599\uff0c\u5723\u6c34\u5c31\u5b58\u5728\u70df\u9501\u697c\uff0c\u4f46\u7ecf\u6c34\u4e00\u6df9\uff0c\u4e0d\u77e5\u662f\u5426\u8fd8\u6709\u7528\uff0c\u6211\u4eec\u62dc\u522b\u4e86\u3002\u201d \u3000\u3000\u591c\u8272\u4e2d\uff0c\u4e8c\u5973\u4e00\u7537\uff0c\u8054\u8882\u800c\u53bb\u3002 \u3000\u3000\u9ec4\u7075\u671b\u7740\u4e09\u4eba\u7684\u80cc\u5f71\uff0c\u8f7b\u8f7b\u53f9\u606f\u4e00\u58f0\uff0c \u81ea\u8a00\u81ea\u8bed\u5730\u8bf4\u9053\uff1a \u201c\u5251\u8fc7\u65e0\u75d5\uff0c\u4eba\u4e0d\u7559\u540d\uff0c\u4f60\u4eec\u9009\u62e9\u4e86\u6c5f\u6e56\u4e0a\u4e00\u6761\u6700\u597d\u7684\u8def\u3002\u201d \uff08\u5168\u4e66\u5b8c\xb7\u79cb\u8349\u7f16\u8f91\uff09"}),showOnHomepage:!0}]}}]);