"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[77272],{43151:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u6c5f\u6e56\u4e09\u5973\u4fa0",content:(0,t.jsx)(t.Fragment,{children:"\u7b2c\u4e00\u56de \u8d60\u5b9d\u6536\u5f92\u3000\u5b6a\u751f\u601c\u7389\u5973 \u98de\u5934\u6ef4\u8840\u3000\u4e00\u5251\u63a2\u77e5\u4ea4 \u3000\u3000\u3000\u3000\u5251\u80c6\u7434\u5fc3\u8c01\u53ef\u8bed\uff0c\u6c5f\u6e56\u98d8\u6cca\u601c\u4e09\u5973\u3002 \u3000\u3000\u3000\u3000\u5f39\u6307\u6570\u534e\u5e74\uff0c\u534e\u5e74\u68a6\u4f3c\u70df\u3002 \u3000\u3000\u3000\u3000\u9065\u5929\u5bd2\u65e5\u66ae\uff0c\u5bc2\u5bde\u7a7a\u5c71\u8def\u3002 \u3000\u3000\u3000\u3000\u8e0f\u904d\u53bb\u6765\u679d\uff0c\u5b64\u9e3f\u72ec\u81ea\u98de\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u2014\u2014\u81ea\u9898\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\uff0c\u8c03\u5bc4\u83e9\u8428\u86ee \u3000\u3000\u3000\u3000\u5bc2\u5bde\u5c71\u6751\uff0c\u9ec4\u83ca\u8def\u65c1\u8fce\u5ba2\u81f3\uff1b \u3000\u3000\u3000\u3000\u4e2d\u79cb\u5c06\u8fd1\uff0c\u5df2\u51c9\u5929\u6c14\u672a\u5bd2\u65f6\u3002 \u3000\u3000\u5728\u76d8\u66f2\u7684\u5c71\u8def\u4e0a\uff0c\u4e00\u4e2a\u5e74\u7ea6\u4e94\u65ec\u7684\u6c49\u5b50\uff0c\u624b\u4e2d\u62ff\u7740\u4e00\u6839\u957f\u957f\u7684\u70df\u6746\uff0c\u6b63\u5728\u6021\u7136\u81ea\u5f97\u7684\u5438 \u7740\u65f1\u70df\u3002 \u3000\u3000\u5c71\u8def\u4e24\u65c1\uff0c\u6742\u82b1\u751f\u6811\uff0c\u90a3\u4e9b\u91ce\u751f\u7684\u5c0f\u9ec4\u83ca\u5c24\u5176\u53ef\u7231\u3002\u5c71\u98ce\u5439\u8fc7\uff0c\u6e05\u9999\u6251\u9f3b\u3002 \u3000\u3000\u4f46\u8fd9\u4e2a\u5c71\u8def\u4e0a\u7684\u884c\u4eba\uff0c\u5374\u4e0d\u662f\u4ec0\u4e48\u6587\u4eba\u96c5\u58eb\uff0c\u4ed6\u662f\u6cb3\u5357\u6c5d\u5dde\u7684\u540d\u6b66\u5e08\u909d\u740f\u3002 \u3000\u3000\u4ed6\u4e5f\u4e0d\u662f\u4e3a\u4e86\u6e38\u5c71\u800c\u6765\uff0c\u524d\u9762\u7684\u6751\u5e84\u6709\u4ed6\u7684\u513f\u5973\u4eb2\u5bb6\u3002\u4ed6\u7684\u4eb2\u5bb6\u59d3\u51af\u540d\u5e7f\u6f6e\uff0c\u4e5f\u662f\u4e00\u4f4d \u6b66\u5e08\uff0c\u51af\u5e7f\u6f6e\u7684\u513f\u5b50\u51af\u82f1\u5947\u5a36\u4e86\u4ed6\u7684\u5973\u513f\u909d\u7ec3\u971e\uff0c\u53bb\u5e74\u751f\u4e86\u4e00\u5bf9\u5b6a\u751f\u5973\u513f\uff0c\u4eca\u5929\u6b63\u662f\u4ed6\u8fd9\u4e00 \u5bf9\u5916\u5b59\u5973\u513f\u7684\u5468\u5c81\u4e4b\u559c\uff0c\u4ed6\u662f\u53bb\u559d\u201c\u6293\u5468\u201d\u9152\u7684\u3002\u201c\u6293\u5468\u201d\u662f\u4ed6\u4eec\u5bb6\u4e61\u7684\u98ce\u4fd7\uff0c\u7236\u6bcd\u5728\u5b69\u5b50 \u5468\u5c81\u4e4b\u65f6\uff0c\u628a\u4eb2\u53cb\u6240\u9001\u7684\u793c\u7269\u5806\u5728\u5b69\u5b50\u7684\u9762\u524d\uff0c\u8ba9\u4ed6\u81ea\u5df1\u53bb\u201c\u6293\u201d\uff0c\u4ece\u5b69\u5b50\u6240\u6293\u7684\u7269\u4e8b\uff0c\u53ef \u4ee5\u89c2\u5bdf\u4ed6\u7684\u559c\u7231\uff0c\u63a8\u65ad\u4ed6\u7684\u672a\u6765\u3002 \u3000\u3000\u201c\u4eba\u5bb6\u90fd\u8bf4\u6211\u8fd9\u4e24\u4e2a\u5916\u5b59\u5973\u513f\u662f\u7389\u5973\u4e0b\u51e1\uff0c\u963f\u971e\u8fd9\u4e2b\u5934\u7684\u798f\u6c14\u53ef\u771f\u4e0d\u5c0f\uff0c\u738b\u6bcd\u5a18\u5a18\u7684\u8eab \u8fb9\u4e5f\u53ea\u6709\u4e00\u4e2a\u7389\u5973\u5462\u3002\u55ef\uff0c\u4eca\u5929\u6211\u53ef\u5f97\u4ed4\u7ec6\u770b\u6e05\u695a\u5979\u4eec\u7684\u9152\u6da1\uff0c\u522b\u53eb\u5973\u513f\u7b11\u8bdd\u3002\u201d\u539f\u6765\u4ed6\u8fd9 \u5bf9\u5916\u5b59\u5973\u513f\uff0c\u4e0d\u4f46\u6709\u5982\u7c89\u96d5\u7389\u7422\uff0c\u9017\u4eba\u559c\u7231\u3002\u800c\u4e14\u751f\u5f97\u4e00\u6a21\u4e00\u6837\uff0c\u8138\u4e0a\u4e5f\u90fd\u6709\u4e00\u4e2a\u5c0f\u9152\u6da1\u3002 \u552f\u4e00\u7684\u5206\u522b\u662f\u59d0\u59d0\u7684\u9152\u6da1\u751f\u5728\u5de6\u8fb9\uff0c\u59b9\u59b9\u7684\u9152\u6da1\u751f\u5728\u53f3\u8fb9\u3002 \u3000\u3000\u4ed6\u6b63\u5728\u6ee1\u6000\u559c\u60a6\u7684\u60f3\u7740\u4ed6\u8fd9\u5bf9\u53ef\u7231\u7684\u5916\u5b59\u5973\u513f\uff0c\u5c71\u98ce\u5439\u6765\uff0c\u5ffd\u5730\u4f20\u6765\u4e86\u597d\u50cf\u662f\u6709\u4eba\u8bf4\u8bdd \u7684\u58f0\u97f3\u3002 \u3000\u3000\u201c\u4e0d\u4f1a\u5f04\u9519\u5427\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u3002\u90a3\u5b69\u5b50\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u597d\u50cf\u662f\u4e24\u4e2a\u4eba\u5bf9\u8bdd\uff0c\u65ad\u65ad\u7eed\u7eed\uff0c\u542c\u4e0d\u6e05\u695a\u3002\u909d\u740f\u51dd\u795e\u7ec6\u542c\uff0c\u53c8\u542c\u5230\u4e00\u53e5\u6bd4\u8f83\u5b8c\u6574\u7684\u8bf4 \u8bdd\uff1a\u201c\u4ed6\u4eec\u7684\u4ea4\u60c5\u975e\u6bd4\u5bfb\u5e38\u201d\uff0c\u4f46\u4e0b\u9762\u7684\u8bdd\u8bed\u53c8\u6a21\u7cca\u4e0d\u6e05\u4e86\uff1a\u201c\u65e2\u7136\u6709\u2026\u2026\u90a3\u4eba\u4e00\u8d70\u2026\u2026\u201d \u58f0\u97f3\u8d8a\u6765\u8d8a\u5c0f\uff0c\u7ec8\u4e8e\u542c\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u5df2\u7ecf\u8d70\u51fa\u6751\u5b50\uff0c\u4f46\u909d\u740f\u5c45\u9ad8\u4e34\u4e0b\uff0c\u867d\u7136\u770b\u4e0d\u6e05\u695a\u4ed6\u4eec\u7684\u9762\u5bb9\uff0c\u4e5f\u8fd8\u770b\u5f97\u89c1\u4ed6\u4eec \u7684\u80cc\u5f71\uff0c\u6751\u5b50\u91cc\u7684\u4eba\uff0c\u909d\u740f\u5168\u90fd\u719f\u8bc6\uff0c\u8fd9\u4e24\u4e2a\u4eba\u663e\u7136\u662f\u5916\u6765\u7684\u964c\u751f\u4eba\u3002 \u3000\u3000\u909d\u740f\u7591\u5fc3\u5927\u8d77\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u542c\u4ed6\u4eec\u7684\u53e3\u6c14\uff0c\u597d\u50cf\u662f\u6765\u8fd9\u91cc\u6253\u542c\u4ec0\u4e48\u4e8b\u60c5\u4f3c\u7684\uff0c\u53ea\u4e0d\u77e5 \u662f\u9ed1\u9053\u7684\u4eba\u7269\u8fd8\u662f\u767d\u9053\u7684\u516c\u5dee\uff1f\u201d \u3000\u3000\u4f4f\u5728\u8fd9\u4e2a\u5c71\u6751\u7684\u90fd\u662f\u666e\u901a\u767e\u59d3\uff0c\u552f\u4e00\u6709\u70b9\u201c\u7279\u6b8a\u201d\u7684\u5c31\u53ea\u662f\u4ed6\u7684\u4eb2\u5bb6\u51af\u5e7f\u6f6e\u4e86\u3002\u51af\u5e7f\u6f6e \u5c11\u5e74\u65f6\u5019\u4e5f\u66fe\u884c\u8d70\u6c5f\u6e56\uff0c\u4f46\u5728\u4e09\u5341\u4e8c\u5c81\u90a3\u5e74\uff0c\u5c31\u5f52\u9690\u6545\u91cc\uff0c\u95ed\u95e8\u8c22\u5ba2\uff0c\u8bfe\u5b50\u6388\u5f92\u3002\u4ed6\u9690\u5c45\u6545 \u91cc\u3001\u4e0d\u77e5\u4e0d\u89c9\u4ea6\u5df1\u8fc7\u4e86\u5341\u5e74\u4e86\u3002\u6b66\u6797\u670b\u53cb\u95ee\u4ed6\u4e3a\u4ec0\u4e48\u65b9\u5f53\u58ee\u76db\u4e4b\u5e74\uff0c\u4fbf\u4f5c\u5c71\u6751\u9690\u58eb\uff0c\u4ed6\u5f80\u5f80 \u987e\u5de6\u53f3\u800c\u8a00\u4ed6\uff0c\u751a\u6216\u53ea\u662f\u7b11\u800c\u4e0d\u7b54\u3002 \u3000\u3000\u909d\u740f\u4e5f\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\u4ed6\u8fd9\u6837\u65e9\u5c31\u201c\u606f\u5f71\u201d\u7530\u56ed\uff0c\u4f46\u4ed6\u77e5\u9053\u5728\u8fd9\u5341\u5e74\u5f53\u4e2d\uff0c\u51af\u5e7f\u6f6e\u786e\u5b9e\u7edd \u8ff9\u6c5f\u6e56\uff0c\u7518\u4e8e\u9690\u9038\u3002\u4ed6\u4eca\u5e74\u867d\u7136\u624d\u4e0d\u8fc7\u56db\u5341\u4e8c\u5c81\uff0c\u6bd4\u909d\u740f\u7684\u5e74\u7eaa\u8fd8\u5c0f\u516d\u5c81\uff0c\u4f46\u5df2\u50cf\u662f\u4e2a\u5fc3\u5982 \u6b62\u6c34\u7684\u8001\u4eba\u4e86\uff0c\u53bb\u5e74\u4ed6\u505a\u4e86\u7956\u7236\u4e4b\u540e\uff0c\u66f4\u52a0\u4ee5\u542b\u9974\u5f04\u5b59\u4e3a\u4e50\uff0c\u4e0d\u95ee\u5916\u95f4\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u8fd8\u77e5\u9053\u51af\u5e7f\u6f6e\u4ece\u6ca1\u53c2\u52a0\u4efb\u4f55\u53cd\u6e05\u7684\u5e2e\u4f1a\uff0c\u867d\u7136\u4ed6\u4eec\u5bf9\u6ee1\u6d32\u7684\u5165\u4e3b\u4e2d\u534e\uff0c\u538b\u8feb\u6c49\u4eba\uff0c\u90fd \u662f\u5fc3\u4e2d\u4e0d\u6ee1\u3002\u4f46\u201c\u5927\u6e05\u201d\u671d\u5ef7\u7684\u6839\u57fa\u65e9\u5df2\u7a33\u56fa\uff0c\uff08\u4eca\u5e74\u662f\u5eb7\u7199\u56db\u5341\u4e94\u5e74\uff0c\u8ddd\u79bb\u6ee1\u6e05\u5165\u5173\u5df2\u7ecf \u516d\u5341\u4e09\u5e74\u4e86\u3002\uff09\u4e0d\u6ee1\u53c8\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u591a\u5c11\u4e49\u58eb\u9057\u6c11\u4e5f\u53ea\u80fd\u541e\u58f0\u5fcd\u6cea\uff01\u4f0f\u8eab\u8349\u83bd\uff0c\u5f85\u9699\u4f3a\u673a\uff0c \u4f55\u51b5\u4ed6\u4eec\u53ea\u662f\u5bfb\u5e38\u767e\u59d3\u3002 \u3000\u3000\u6b64\u65f6\u90a3\u4e24\u4e2a\u4eba\u5df2\u7ecf\u662f\u8d70\u5f97\u8fde\u5f71\u5b50\u90fd\u4e0d\u89c1\u4e86\uff0c\u909d\u740f\u53c8\u518d\u5480\u56bc\u4ed6\u4eec\u90a3\u4e9b\u96f6\u788e\u7684\u8bdd\u8bed\uff0c\u4e0d\u505c\u7684 \u60f3\uff1a\u201c\u4ed6\u4eec\u8bf4\u7684\u90a3\u4e2a\u5b69\u5b50\u662f\u8c01\uff1f\u542c\u90a3\u4eba\u53e3\u6c14\uff0c\u4f3c\u4e4e\u4e0e\u90a3\u5b69\u5b50\u76f8\u8bc6\uff0c\u5f53\u7136\u4e0d\u4f1a\u662f\u6307\u6211\u90a3\u4e24\u4e2a\u521a \u6ee1\u5468\u5c81\u7684\u5916\u5b59\u5973\u513f\u5427\uff1f\u548c\u4ed6\u4eec\u540e\u6765\u8bf4\u7684\u90a3\u4e2a\u4eba\u53c8\u6709\u6ca1\u6709\u5173\u7cfb\u5462\uff1f\u5e7f\u6f6e\u7684\u670b\u53cb\u6211\u90fd\u77e5\u9053\uff0c\u79f0\u5f97 \u4e0a\u548c\u4ed6\u6709\u7279\u522b\u4ea4\u60c5\u7684\u6050\u6015\u53ea\u6709\u6211\u4e86\u3002\u4ed6\u7684\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u65e9\u5df2\u65ad\u7edd\u5f80\u6765\uff0c\u90a3\u8fd8\u6709\u8c01\uff1f\u4f46\u2018\u90a3 \u4eba\u2019\u603b\u4e0d\u81f3\u4e8e\u662f\u6307\u6211\u5427\uff1f\u201d \u3000\u3000\u4ed6\u60f3\u6765\u60f3\u53bb\uff0c\u4ecd\u662f\u83ab\u540d\u5176\u5999\uff0c\u6700\u540e\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\u8c08\u8bba\u7684\u4e8b\u60c5\u8bf4\u4e0d\u5b9a\u548c\u6211\u90a3\u4eb2\u5bb6\u6839\u672c \u5168\u65e0\u5173\u7cfb\uff1b\u4e5f\u8bf4\u4e0d\u5b9a\u4ed6\u4eec\u6839\u672c\u5c31\u4e0d\u662f\u6765\u67e5\u4ec0\u4e48\u6848\u7684\uff0c\u90fd\u662f\u81ea\u4f5c\u806a\u660e\u7684\u63e3\u6d4b\uff01\u201d\u201c\u522b\u7ba1\u4ed6\u4eec \u4e86\uff0c\u8fd8\u662f\u5feb\u70b9\u53bb\u770b\u6211\u90a3\u4e24\u4e2a\u53ef\u7231\u7684\u5916\u5b59\u5973\u513f\u5427\u3002\u89c1\u4e86\u5e7f\u6f6e\u518d\u8bf4\u3002\u201d\u4ed6\u62bd\u4e86\u4e00\u888b\u65f1\u70df\uff0c\u4e0d\u77e5\u4e0d \u89c9\uff0c\u5df2\u662f\u8d70\u5230\u6751\u524d\u4e86\u3002 \u3000\u3000\u51af\u5bb6\u7684\u628a\u5f0f\u573a\u5c31\u5728\u6751\u8fb9\uff0c\u909d\u740f\u8fdc\u8fdc\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u5251\u7709\u6717\u76ee\u8702\u8170\u733f\u81c2\u7684\u5c11\u5e74\uff0c\u5728\u7a7a\u573a\u4e2d \u5fc3\uff0c\u6ef4\u6e9c\u6e9c\u7684\u75be\u8f6c\uff0c\u5ffd\u800c\u8d34\u5730\u7ffb\u8170\uff0c\u72b6\u4f3c\u7280\u725b\u671b\u6708\uff0c\u5ffd\u800c\u8038\u8eab\u5f20\u81c2\u4fe8\u5982\u5251\u8fa8\u6469\u7a7a\u3002\u909d\u740f\u6697 \u9053\uff1a\u201c\u4eb2\u5bb6\u5e38\u5e38\u5938\u5956\u4ed6\u65b0\u6536\u7684\u5f92\u5f1f\u8d28\u7f8e\u597d\u5b66\uff0c\u770b\u6765\u679c\u4f3c\u4e0d\u9519\uff0c\u53ea\u662f\u8fd9\u662f\u90a3\u95e8\u5b50\u7684\u529f\u592b\u5440\uff1f\u201d \u3000\u3000\u51af\u5e7f\u6f6e\u6709\u4e24\u4e2a\u5f92\u5f1f\uff0c\u5927\u5f92\u5f1f\u738b\u9675\uff0c\u4e09\u5e74\u524d\u5b66\u6ee1\u51fa\u5e08\uff1b\u5728\u4eac\u4e2d\u5e72\u9556\u884c\u751f\u610f\u3002\u5728\u628a\u5f0f\u573a\u4e2d\u7ec3 \u6b66\u7684\u5c11\u5e74\uff0c\u540d\u53eb\u5510\u6653\u6f9c\uff0c\u4e43\u662f\u4ed6\u7684\u4e8c\u5f92\u5f1f\u3002\u8fd9\u5510\u6653\u6f9c\u6765\u5389\u751a\u5947\uff0c\u8fde\u909d\u740f\u4e5f\u4e0d\u77e5\u4ed6\u662f\u4f55\u65b9\u4eba \u6c0f\u3002\u6709\u4e00\u5929\u51af\u5e7f\u6f6e\u7a81\u5e26\u4e00\u4e2a\u5341\u4e03\u516b\u5c81\u7684\u5c11\u5e74\u6765\u62dc\u89c1\u4ed6\uff0c\u8bf4\u662f\u65b0\u6536\u7684\u5f92\u5f1f\uff0c\u8bf4\u8bdd\u5e26\u5173\u5916\u53e3\u97f3\uff0c \u4f46\u7709\u6e05\u76ee\u79c0\uff0c\u5374\u53c8\u6042\u5982\u5904\u5b50\u3002\u51af\u5e7f\u6f6e\u4ece\u672a\u5230\u8fc7\u5173\u5916\uff0c\u5374\u5982\u4f55\u4f1a\u6709\u4e2a\u5e26\u5173\u5916\u53e3\u97f3\u7684\u5f92\u5f1f\uff0c\u909d\u740f \u767e\u601d\u4e0d\u89e3\uff0c\u6697\u4e2d\u4e5f\u6709\u95ee\u8fc7\u4eb2\u5bb6\uff0c\u51af\u5e7f\u6f6e\u603b\u4e0d\u80af\u660e\u8bf4\uff0c\u800c\u4e14\u8a00\u8bcd\u4e4b\u95f4\u4f3c\u6709\u9690\u51b5\u3002\u6b66\u6797\u4e2d\u867d\u5c5e\u81f3 \u4eb2\uff0c\u4e5f\u4e0d\u4fbf\u63a2\u4eba\u9690\u79d8\uff0c\u909d\u740f\u4e5f\u5c31\u7f62\u4e86\u3002\u4eca\u65e5\u51d1\u5de7\uff0c\u78b0\u7740\u5510\u6653\u6f9c\u7ec3\u6b66\uff0c\u909d\u740f\u7ec6\u5fc3\u89c2\u770b\uff0c\u770b\u4e86\u4e00 \u9635\uff0c\u4e0d\u7981\u5927\u60ca\u5931\u8272\uff01 \u3000\u3000\u628a\u5f0f\u573a\u4e2d\u5510\u6653\u6f9c\u8eab\u6cd5\u5c55\u5f00\u8d8a\u8f6c\u8d8a\u6025\uff0c\u573a\u8fb9\u7684\u69d0\u6811\u7c41\u7c41\u4f5c\u54cd\uff0c\u4e00\u7247\u7247\u7684\u6811\u53f6\u98d8\u843d\u4e0b\u6765\uff0c\u909d \u740f\u7ec6\u671b\u5374\u4e0d\u89c1\u4ec0\u4e48\u6697\u5668\uff0c\u770b\u4ed6\u8eab\u6cd5\u624b\u6cd5\uff0c\u53c8\u4e0d\u662f\u5288\u7a7a\u638c\u4e4b\u7c7b\u7684\u529f\u592b\uff0c\u800c\u4e14\u82e5\u662f\u638c\u98ce\u6240\u9707\uff0c\u5fc5 \u7136\u4e00\u843d\u5c31\u662f\u4e00\u5806\u4fef\u53f6\uff0c\u73b0\u5728\u5374\u662f\u4e00\u7247\u8ddf\u7740\u4e00\u7247\uff0c\u8f7b\u8f7b\u98d8\u4e0b\uff0c\u5c31\u597d\u50cf\u662f\u88ab\u4f36\u4fd0\u7684\u59d1\u5a18\u5de7\u624b\uff0c\u6458 \u4e0b\u679d\u5934\uff0c\u909d\u740f\u662f\u6b66\u6797\u4e2d\u7684\u884c\u5bb6\uff0c\u770b\u51fa\u4e43\u662f\u6885\u82b1\u9488\u4e4b\u7c7b\u6781\u7ec6\u5c0f\u7684\u6697\u5668\u523a\u65ad\u53f6\u6897\uff0c\u98d8\u4e0b\u6765\u7684\u3002\u8fd9 \u4e00\u4efd\u5403\u60ca\uff0c\u7aef\u7684\u975e\u540c\u5c0f\u53ef\u3002\u6885\u82b1\u9488\u4e4b\u7c7b\u7684\u6697\u5668\uff0c\u4efd\u91cf\u6781\u8f7b\uff0c\u53d6\u51c6\u6781\u96be\u3002\u800c\u4eca\u5510\u6653\u6f9c\u80fd\u5728\u4e09\u4e08 \u4ee5\u5916\uff0c\u6253\u843d\u6811\u53f6\u3002\u8155\u529b\u4e4b\u5f3a\uff0c\u76ee\u5149\u4e4b\u51c6\uff0c\u5728\u6210\u540d\u6b66\u5e08\u4e2d\u4e5f\u4e0d\u591a\u89c1\uff0c\u4ed6\u62dc\u5e08\u4e0d\u8fc7\u4e00\u5e74\u591a\u70b9\uff0c\u4e00 \u5e74\u4e4b\u95f4\u4ed6\u5982\u4f55\u80fd\u7ec3\u6210\u5982\u6b64\u529f\u592b\uff1f\u800c\u4e14\u909d\u740f\u4e5f\u4ece\u672a\u542c\u8fc7\u51af\u5e7f\u6f6e\u4f1a\u6885\u82b1\u9488\u3002 \u3000\u3000\u909d\u740f\u53c8\u518d\u5fc3\u60f3\uff1a\u201c\u83ab\u975e\u4ed6\u662f\u5e26\u827a\u6295\u5e08\uff0c\u7136\u5219\u4ed6\u4ee5\u524d\u7684\u5e08\u5085\u53c8\u662f\u8c01\u4eba\uff0c\u4ed6\u65e2\u6709\u8fd9\u5206\u529f\u592b\uff0c \u53c8\u4f55\u5fc5\u8fdc\u6765\u8352\u6751\uff0c\u7ec3\u51af\u5bb6\u7684\u628a\u5f0f\u3002\u5e7f\u6f6e\u6b66\u529f\u867d\u7136\u6bd4\u6211\u9ad8\u660e\uff0c\u5728\u6c5f\u6e56\u4e0a\u4ed6\u8fd8\u4e0d\u80fd\u7b97\u662f\u4e00\u6d41\u597d \u624b\uff0c\u8fd9\u5c11\u5e74\u4ee5\u524d\u7684\u5e08\u5085\uff0c\u5fc5\u7136\u6bd4\u5e7f\u6f6e\u9ad8\u660e\u5f97\u591a\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u7ec3\u4e86\u4e00\u9635\uff0c\u500f\u7136\u6b62\u6b65\uff0c\u62d4\u51fa\u4e00\u67c4\u4e09\u5c3a\u591a\u957f\u7684\u5229\u5251\uff0c\u626c\u7a7a\u4e00\u95ea\uff0c\u7eb5\u6a2a\u6325\u970d\uff0c\u5de6\u53f3\u5288 \u523a\uff0c\u6377\u5982\u733f\u7334\uff0c\u6ed1\u4f3c\u72f8\u732b\uff0c\u5251\u82b1\u9519\u843d\uff0c\u5728\u671d\u9633\u4e0b\u6cdb\u51fa\u95ea\u7535\u4f3c\u7684\u5149\u8292\uff0c\u8000\u773c\u751f\u8f89\u3002\u909d\u740f\u66f4\u662f\u60ca \u5947\uff0c\u5fc3\u60f3\u51af\u5e7f\u6f6e\u4ee5\u516d\u5408\u5927\u67aa\u95fb\u540d\uff0c\u5982\u4f55\u5374\u6559\u5f92\u5f1f\u4f7f\u5251\uff1f\u800c\u4e14\u5510\u6653\u6f9c\u7684\u5251\u6cd5\uff0c\u8fc5\u6377\u800c\u4f26\uff0c\u7adf\u662f \u81ea\u5df1\u751f\u5e73\u4ec5\u89c1\uff0c\u80fd\u591f\u6559\u4ed6\u8fd9\u8def\u5251\u6cd5\u7684\u4eba\u3001\u4e0d\u662f\u4e00\u6d3e\u5b97\u5e08\uff0c\u4e5f\u5b9a\u662f\u6210\u540d\u5251\u5ba2\u3002 \u3000\u3000\u909d\u740f\u8d8a\u770b\u8d8a\u5947\uff0c\u6b63\u81ea\u51fa\u795e\uff0c\u5ffd\u89c1\u5510\u6653\u6f9c\u628a\u5251\u821e\u4e86\u4e2a\u5706\u5708\uff0c\u6a2a\u5728\u80f8\u524d\uff0c\u53f3\u624b\u642d\u7740\u5251\u8eab\uff0c\u8eac \u8170\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u521d\u7ec3\u5251\uff0c\u4e0d\u6210\u6c14\u5019\uff0c\u8d3b\u7b11\u65b9\u5bb6\uff0c\u524d\u8f88\u53ef\u662f\u6765\u627e\u5bb6\u5e08\u7684\u5417\uff1f\u201d\u909d\u740f\u5fc3\u4e2d\u6709\u6c14\uff1a \u201c\u4ec0\u4e48\u524d\u8f88\u4e0d\u524d\u8f88\uff0c\u96be\u9053\u4f60\u8fd9\u5c0f\u5b50\u8fde\u6211\u4e5f\u4e0d\u8ba4\u5f97\uff1f\u201d\u6b63\u60f3\u9a82\u4ed6\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u573a\u4e2d\u73b0\u51fa \u4e00\u4eba\uff0c\u4e09\u7efa\u957f\u987b\uff0c\u7eb6\u4e2d\u7fbd\u6247\uff0c\u98d8\u98d8\u82e5\u4ed9\uff0c\u770b\u6765\u662f\u4e2a\u56db\u5341\u6709\u4f59\u4e94\u5341\u672a\u5230\u7684\u61e6\u751f\u3002\u8eab\u6cd5\u4e4b\u5feb\uff0c\u7b80 \u76f4\u96be\u4ee5\u5f62\u5bb9\uff0c\u909d\u740f\u7adf\u4e0d\u77e5\u4ed6\u662f\u4f55\u65f6\u6765\u5230\uff0c\u53c8\u662f\u600e\u6837\u8dc3\u8fdb\u573a\u5fc3\uff0c\u5c31\u50cf\u4ece\u5929\u800c\u964d\uff0c\u5e73\u5730\u94bb\u51fa\u4f3c \u7684\u3002\u6765\u5ba2\u8f7b\u6447\u7fbd\u6247\uff0c\u7b11\u54aa\u54aa\u7684\u8bf4\u9053\uff1a\u201c\u8fd9\u8def\u5251\u6cd5\uff0c\u6211\u5df2\u4e45\u77e3\u4e4e\u672a\u89c1\u6709\u4eba\u4f7f\u8fc7\u4e86\uff0c\u4f60\u5df2\u6709\u4e09\u6210 \u706b\u5019\uff0c\u4e0d\u5fc5\u8c26\u865a\uff0c\u51ed\u4f60\u73b0\u5728\u7684\u5251\u6cd5\uff0c\u5df2\u8db3\u4ee5\u5728\u6c5f\u6e56\u4e0a\u626c\u540d\u7acb\u4e07\u4e86\uff01\u6765\uff0c\u6765\uff0c\u6211\u7ed9\u4f60\u5582\u5582 \u62db\uff01\u201d\u7fbd\u6247\u4e00\u6536\uff0c\u5411\u5510\u6653\u6f9c\u62db\u624b\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u7528\u5175\u5668\u548c\u4f60\u8fc7\u62db\uff0c\u4f60\u6765\u5427\uff0c\u770b\u770b\u4f60\u7684\u8ffd\u98ce\u5251 \u6cd5\uff0c\u80fd\u4e0d\u80fd\u6cbe\u7740\u6211\u7684\u8863\u88f3\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u4e00\u9635\u8fdf\u7591\uff0c\u602a\u5ba2\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u4ee4\u5e08\u7edd\u4e0d\u4f1a\u8d23\u602a\u4f60\uff0c\u5341\u5e74\u524d\u4ed6\u521d\u4f1a\u8fd9\u8def\u5251 \u9053\uff0c\u5c31\u66fe\u548c\u6211\u62c6\u62db\u7ec3\u5251\uff0c\u54b1\u4eec\u805a\u4e86\u5341\u5929\u624d\u6563\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u500f\u7136\u53d8\u8272\uff0c\u626c\u58f0\u8bf4\u9053\uff1a\u201c\u909d\u8001\u4f2f\u8bf7\u4ee3\u7980\u62a5\u5bb6\u5e08\uff0c\u6211\u5728\u8fd9\u91cc\u63a5\u8fd9\u4f4d\u8001\u524d\u8f88\u51e0\u62db\u3002\u201d \u9752\u94a2\u5251\u4e00\u7ffb\u9634\u628a\uff0c\u201c\u54e7\u201d\u7684\u4e00\u58f0\uff0c\u53cd\u624b\u523a\u51fa\uff0c\u602a\u5ba2\u8eab\u5f62\u5fae\u6643\uff0c\u5510\u6653\u6f9c\u4e00\u5251\u523a\u7a7a\uff0c\u5237\u5730\u4e00\u4e2a \u201c\u602a\u87d2\u7ffb\u8eab\u201d\uff0c\u8eab\u968f\u52bf\u8f6c\uff0c\u5de6\u624b\u5251\u8bc0\u659c\u5f80\u4e0a\u6307\uff0c\u53f3\u624b\u5251\u950b\u731b\u7136\u4e00\u64a9\uff0c\u5237\u5730\u53c8\u662f\u4e00\u5251\u622a\u65a9\u602a\u5ba2 \u8109\u95e8\uff0c\u602a\u5ba2\u53cc\u81c2\u4e00\u6296\uff0c\u5927\u58f0\u7b11\u9053\uff1a\u201c\u5feb\u5219\u5feb\u77e3\uff0c\u51c6\u5934\u5c1a\u5dee\uff01\u201d\u8eab\u5b50\u60ac\u7a7a\uff0c\u731b\u7136\u5f80\u4e0b\u4e00\u8e6c\uff0c\u5510 \u6653\u6f9c\u7f29\u8eab\u4e00\u95ea\uff0c\u5251\u5f80\u4e0a\u64a9\uff0c\u5ffd\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u602a\u5ba2\u8db3\u5c16\u8f7b\u70b9\u4ed6\u7684\u80a9\u5934\uff0c\u7adf\u7136\u7ffb\u5230\u4ed6\u7684\u80cc\u540e\u53bb \u4e86\u3002\u602a\u5ba2\u8fd9\u4e00\u811a\u82e5\u8e0f\u5b9e\uff0c\u5510\u6653\u6f9c\u975e\u9aa8\u788e\u808b\u6298\u4e0d\u53f8\uff01\u5510\u6653\u6f9c\u5403\u60ca\u4e0d\u5c0f\uff0c\u8fd9\u602a\u5ba2\u975e\u4f46\u8eab\u6cd5\u5947\u5feb\uff0c \u800c\u4e14\u80fd\u53d1\u80fd\u6536\u3002\u800c\u53c8\u4e0d\u4f24\u5bf9\u65b9\uff0c\u8fd9\u4efd\u529f\u592b\u5df2\u662f\u80dc\u8fc7\u4ed6\u7684\u5e08\u5085\u4e0d\u77e5\u591a\u5c11\u3002 \u3000\u3000\u4e0d\u8bf4\u5510\u6653\u6f9c\u5fc3\u91cc\u5600\u5495\uff0c\u65c1\u8fb9\u7684\u909d\u740f\u66f4\u662f\u60ca\u7591\u4e0d\u5df1\uff01\u4ed6\u672c\u6765\u662f\u8981\u53bb\u901a\u62a5\u51af\u5e7f\u6f6e\u7684\u3002\u4e3a\u4e86\u597d \u5947\uff0c\u591a\u770b\u4e00\u9635\uff0c\u90a3\u6599\u5c31\u5728\u8fd9\u7247\u523b\u4e4b\u95f4\uff0c\u53cc\u65b9\u5df2\u4ea4\u6362\u4e86\u597d\u51e0\u4e2a\u9669\u62db\uff0c\u90a3\u91cc\u8fd8\u6562\u8fdf\u7591\uff0c\u6025\u6025\u5f80\u51af \u5bb6\u8dd1\u53bb\uff0c\u80cc\u540e\u53ea\u542c\u5f97\u90a3\u602a\u5ba2\u53c8\u5728\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u5514\uff0c\u8fd9\u51e0\u624b\u8fd8\u4e0d\u9519\uff0c\u6bd4\u521a\u624d\u9547\u5b9a\u5f97\u591a\u4e86\uff01\u201d\u909d \u740f\u4e0d\u6687\u56de\u987e\uff0c\u4e00\u53e3\u6c14\u8dd1\u8fdb\u51af\u5bb6\u5927\u95e8\uff01 \u3000\u3000\u51af\u5e7f\u6f6e\u6b63\u5728\u5ead\u9662\u91cc\u95f2\u5750\u5438\u70df\uff0c\u89c1\u909d\u740f\u6c14\u6025\u8d25\u574f\u8dd1\u6765\uff0c\u4e0d\u7981\u7b11\u9053\uff1a\u201c\u4eb2\u5bb6\u7fc1\u770b\u4f60\u7684\u5916\u5b59\u5973 \u6765\u4e86\uff0c\u4e5f\u4e0d\u7528\u8dd1\u8fd9\u6837\u6025\u5440\uff01\u201d\u909d\u740f\u628a\u793c\u7269\u4e00\u6254\uff0c\u62c9\u7740\u51af\u5e7f\u6f6e\u4fbf\u8dd1\uff0c\u8bf4\u9053\uff1a\u201c\u4eb2\u5bb6\uff0c\u4f60\u7684\u5f92\u5f1f \u5728\u5916\u9762\u548c\u4eba\u8fc7\u62db\uff0c\u4f60\u8fd8\u4e0d\u5feb\u53bb\u770b\u770b\uff01\u201d\u909d\u740f\u62c5\u5fc3\u602a\u5ba2\u4e43\u662f\u51af\u5e7f\u6f6e\u7684\u654c\u4eba\u3002\u5b58\u5fc3\u524d\u6765\u62c6\u53f0\uff0c\u6240 \u4ee5\u5148\u6253\u5f92\u5f1f\uff0c\u7136\u540e\u5f15\u51fa\u5e08\u5085\u3002 \u3000\u3000\u51af\u5e7f\u6f6e\u4e00\u542c\uff0c\u811a\u6b65\u52a0\u5feb\uff0c\u4f46\u4ecd\u662f\u6c14\u8d70\u795e\u95f2\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u5440\uff1f\u6653\u6f9c\u8fd9\u5b69\u5b50\u4e09\u62db\u4e24 \u5f0f\uff0c\u8c05\u8fd8\u53ef\u4ee5\u62b5\u6321\u5f97\u4f4f\u3002\u201d \u3000\u3000\u628a\u5f0f\u573a\u5c31\u5728\u95e8\u524d\u767e\u6b65\u4e4b\u5730\uff0c\u4e24\u4eb2\u5bb6\u8fd9\u4e48\u4e00\u8dd1\uff0c\u7247\u523b\u5c31\u5230\u3002\u573a\u4e2d\u4e24\u4eba\u6597\u5f97\u6b63\u70c8\uff0c\u5ffd\u542c\u5f97\u55e4 \u7684\u4e00\u58f0\uff0c\u602a\u5ba2\u53cd\u8eab\u8dc3\u51fa\u573a\u5fc3\uff0c\u624b\u4e0a\u62ff\u7740\u5510\u6653\u6f9c\u90a3\u67c4\u957f\u5251\u3002\u5510\u6653\u6f9c\u53cc\u811a\u671d\u5929\uff0c\u8dcc\u5728\u5730\u4e0a\u3002\u909d\u740f \u53cc\u811a\u70b9\u5730\uff0c\u6b63\u60f3\u8fdb\u53bb\u6551\u4eba\uff0c\u51af\u5e7f\u6f6e\u5ffd\u7136\u4e00\u642d\u4ed6\u7684\u624b\u81c2\uff0c\u786c\u751f\u751f\u5c06\u4ed6\u62c9\u4e86\u56de\u6765\uff0c\u5bf9\u7740\u90a3\u4eba\u7b11 \u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u662f\u4f60\uff01\u6211\u8fd9\u5f92\u5f1f\u600e\u6837\uff0c\u4f60\u8dcc\u4ed6\u4e00\u8de4\u5c31\u7b97\u7ed9\u4e86\u89c1\u9762\u793c\u4e86\u5417\uff1f\u54c8\uff1f\u54c8\uff01\u201d \u5f92\u5f1f\u7ed9\u4eba\u6253\u5012\uff0c\u4ed6\u7adf\u4e00\u70b9\u4e5f\u4e0d\u52a8\u6012\u3002 \u3000\u3000\u602a\u5ba2\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u5341\u5e74\u4e0d\u89c1\uff0c\u4f60\u6559\u7684\u5f92\u5f1f\u4e5f\u8fd9\u6837\u9ad8\u660e\u4e86\uff01\u201d\u628a\u957f\u886b\u4e00\u64a9\uff0c\u53ea\u89c1\u886b\u5c3e\u5df2\u88ab \u5251\u950b\u524a\u53bb\u4e00\u5e45\u3002\u539f\u6765\u4ed6\u89c1\u51af\u5e7f\u6f6e\u6765\u5230\uff0c\u7a0d\u4e00\u5206\u5fc3\uff0c\u5510\u6653\u6f9c\u5251\u4f3c\u8ffd\u98ce\uff0c\u4e00\u4e0b\u5b50\u4fbf\u523a\u5230\u4e0b\u76d8\uff0c\u4ed6 \u903c\u5f97\u56de\u8098\u4e00\u649e\uff0c\u5c06\u5510\u6653\u6f9c\u649e\u8dcc\uff0c\u4f46\u957f\u886b\u4ea6\u5df2\u7ed9\u524a\u6389\u4e00\u5c0f\u7247\u4e86\u3002 \u3000\u3000\u51af\u5e7f\u6f6e\u7b11\u9053\uff1a\u201c\u8c01\u53eb\u4f60\u4e3a\u8001\u4e0d\u5c0a\uff0c\u6b3a\u8d1f\u5c0f\u8f88\u6765\u4e86\uff01\u201d \u3000\u3000\u602a\u5ba2\u7fbd\u6247\u8f7b\u6447\uff0c\u7b11\u7740\u9a82\u9053\uff1a\u201c\u4e8f\u4f60\u7ec3\u4e86\u51e0\u5341\u5e74\u628a\u5f0f\uff0c\u6211\u9001\u4f60\u5f92\u5f1f\u8fd9\u4efd\u5927\u793c\uff0c\u4f60\u505a\u5e08\u5085\u7684 \u8fd8\u4e0d\u591a\u8c22\uff0c\u7adf\u98a0\u5012\u8bf4\u6211\u6b3a\u8d1f\u4ed6\uff0c\u53eb\u8fd9\u4f4d\u884c\u5bb6\u542c\u4e86\uff0c\u5c82\u4e0d\u7b11\u6389\u7259\u9f7f\uff01\u201d \u3000\u3000\u6b64\u65f6\u5510\u6653\u6f9c\u5df2\u4ece\u5730\u4e0a\u722c\u8d77\uff0c\u5ffd\u5730\u8dd1\u5230\u602a\u5ba2\u9762\u524d\uff0c\u535c\u901a\u8dea\u4e0b\uff0c\u884c\u8d77\u5927\u793c\u6765\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a \u201c\u591a\u8c22\u8001\u524d\u8f88\u6307\u70b9\uff01\u201d\u602a\u5ba2\u5c06\u4ed6\u4f4d\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5251\u6cd5\u6bd4\u6211\u9884\u6599\u7684\u8981\u9ad8\u660e\u5f97\u591a\uff0c\u6211\u672c\u6765\u4ee5 \u4e3a\u4f60\u4e0d\u80fd\u6cbe\u7740\u6211\u7684\u8863\u88f3\uff0c\u6599\u4e0d\u5230\u4f60\u5c45\u7136\u80fd\u591f\u628a\u6211\u65b0\u505a\u7684\u8863\u886b\u90fd\u5f04\u7834\u3002\u201d \u3000\u3000\u51af\u5e7f\u6f6e\u8dc3\u8fdb\u573a\u5fc3\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u96be\u9053\u6211\u8fd8\u4e0d\u6653\u5f97\u4f60\u501f\u5582\u62db\u6765\u6307\u70b9\u5c0f\u5f92\uff0c\u4f60\u653e\u5fc3\uff0c\u4f60\u8001\u5f1f \u5bb6\u5883\u867d\u8d2b\uff0c\u4e00\u4ef6\u957f\u886b\u8fd8\u8d54\u5f97\u8d77\u3002\u6765\uff0c\u6765\uff0c\u4f60\u5148\u89c1\u8fc7\u6211\u7684\u4eb2\u5bb6\uff0c\u5c0f\u513f\u524d\u5e74\u6210\u5a5a\u4e86\u3002\u54b3\uff0c\u65e5\u5b50\u8fc7 \u5f97\u771f\u5feb\u554a\uff01\u201d\u4e00\u62db\u624b\uff0c\u909d\u740f\u8ddf\u7740\u8fdb\u6765\uff0c\u53c8\u662f\u60ca\u5947\uff0c\u53c8\u662f\u60ed\u6127\uff0c\u60ca\u5947\u7684\u662f\uff1a\u4ece\u672a\u542c\u4eb2\u5bb6\u8bf4\u8fc7\u6709 \u8fd9\u6837\u4e00\u4f4d\u6b66\u827a\u9ad8\u660e\u7684\u670b\u53cb\uff0c\u60ed\u6127\u7684\u662f\uff1a\u81ea\u5df1\u7adf\u7136\u770b\u4e0d\u51fa\u4ed6\u662f\u85c9\u7740\u201c\u5582\u62db\u201d\u53bb\u6307\u70b9\u6653\u6f9c\u3002 \u3000\u3000\u5510\u6653\u6f9c\u82e6\u7ec3\u8ffd\u98ce\u5251\u6cd5\uff0c\u4e0d\u8fc7\u4e00\u5e74\uff0c\u4ece\u672a\u8bd5\u8fc7\u7528\u4ee5\u5e94\u654c\uff0c\u521a\u624d\u5b9e\u5730\u62c6\u62db\uff0c\u602a\u5ba2\u4e00\u9762\u52a8\u624b\u4e00 \u9762\u6307\u51fa\u4ed6\u7684\u4f18\u52a3\u6240\u5728\uff0c\u5f53\u771f\u4ee4\u4ed6\u5f97\u76ca\u4e0d\u5c11\u3002\u4ed6\u5fc3\u60a6\u8bda\u670d\uff0c\u7ad9\u5728\u5e08\u5085\u65c1\u8fb9\uff0c\u9759\u542c\u5e08\u5085\u7684\u8bf4\u8bdd\u3002 \u3000\u3000\u51af\u5e7f\u6f6e\u62c8\u987b\u7b11\u9053\uff1a\u201c\u5f92\u513f\uff0c\u4f60\u5e08\u4f2f\u7ed9\u4f60\u7684\u89c1\u9762\u793c\u53ef\u4e0d\u8f7b\u5440\uff0c\u8dcc\u8fd9\u4e00\u8de4\u4e5f\u8fd8\u503c\u5f97\u3002\u4eb2\u5bb6\uff0c \u8fd9\u4f4d\u5ba2\u4eba\u7684\u5927\u540d\u4f60\u4e00\u5b9a\u542c\u8fc7\uff0c\u4ed6\u5c31\u662f\u65e0\u6781\u5251\u7684\u540d\u5bbf\u949f\u4e07\u5802\u5440\uff01\u201d\u909d\u740f\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a \u201c\u539f\u6765\u662f\u949f\u8001\u5e08\uff0c\u602a\u4e0d\u5f97\u8fd9\u6837\u5389\u5bb3\uff01\u201d \u3000\u3000\u949f\u4e07\u5802\u7684\u5e08\u7956\u662f\u660e\u672b\u6e05\u521d\u7684\u795e\u533b\u5085\u9752\u4e3b\uff0c\u6240\u4ee5\u4ed6\u4e5f\u9887\u901a\u533b\u672f\u3002\u5728\u6c5f\u6e56\u4e0a\u836f\u56ca\u5b9d\u5251\u968f\u8eab\uff0c \u4e5f\u505a\u8fc7\u4e0d\u5c11\u4fa0\u4e49\u4e4b\u4e8b\uff0c\u53ea\u662f\u8fd1\u5341\u5e74\u6765\uff0c\u4e5f\u50cf\u51af\u5e7f\u6f6e\u4e00\u6837\uff0c\u7a81\u7136\u9500\u58f0\u533f\u8ff9\u3002\u909d\u740f\u7edd\u672a\u60f3\u5230\u8fd9\u4f4d \u540d\u970d\u6c5f\u6e56\u7684\u5251\u5ba2\uff0c\u4f1a\u7a81\u7136\u6765\u5230\u8352\u6751\uff0c\u800c\u4e14\u8fd8\u662f\u4eb2\u5bb6\u7684\u597d\u53cb\u3002 \u3000\u3000\u51af\u5e7f\u6f6e\u4e00\u9762\u8d70\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4f1a\u6765\uff0c\u53ef\u60f3\u4e0d\u5230\u4f60\u4f1a\u6765\u5f97\u8fd9\u6837\u65e9\uff01\u201d\u949f\u4e07\u5802\u9053\uff1a \u201c\u662f\u5440\uff0c\u65e9\u4e86\u4e09\u5929\uff0c\u5341\u5e74\u524d\u4e4b\u7ea6\uff0c\u4f60\u8fd8\u8bb0\u5f97\u6e05\u695a\uff01\u201d\u51af\u5e7f\u6f6e\u9053\uff1a\u201c\u518d\u8fc7\u4e09\u65e5\u4fbf\u662f\u4e2d\u79cb\uff0c\u8fd9\u8fd8 \u4e0d\u5bb9\u6613\u8bb0\uff1f\u5582\uff0c\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u6211\u53d1\u8fd8\u672a\u767d\uff0c\u53ef\u505a\u4e86\u7956\u7236\u4e86\uff01\u4eca\u65e5\u662f\u6211\u4e24\u4e2a\u5b59\u5973\u513f\u7684\u5468\u5c81\uff0c\u4f60 \u4e5f\u6765\u770b\u770b\u5979\u4eec\u2018\u6293\u5468\u2019\u5427\uff01\u201d\u949f\u4e07\u5802\u9053\uff1a\u201c\u4f60\u7684\u513f\u5b50\u6211\u90fd\u672a\u89c1\u8fc7\uff0c\u73b0\u5728\u4f60\u8fde\u5b59\u5973\u4e5f\u6709\u4e86\u3002\u51af \u8001\u5f1f\uff0c\u4f60\u7684\u798f\u6c14\u5012\u771f\u4e0d\u9519\u5440\uff01\u6bd4\u6211\u8fd9\u8001\u5934\u597d\u591a\u4e86\uff01\u201d\u51af\u5e7f\u6f6e\u7b11\u9053\uff1a\u201c\u6211\u505a\u4e86\u7956\u7236\u90fd\u672a\u8ba4\u8001\uff0c \u4f60\u6562\u8ba4\u8001\u3002\u201d\u4e24\u8001\u53cb\u8bf4\u8bf4\u7b11\u7b11\uff0c\u8d70\u56de\u51af\u5bb6\u3002 \u3000\u3000\u51af\u5e7f\u6f6e\u7684\u513f\u5b50\u51af\u82f1\u5947\u884c\u8fc7\u62dc\u89c1\u524d\u8f88\u7684\u5927\u793c\u4e4b\u540e\uff0c\u5ab3\u5987\u968f\u540e\u4e5f\u62b1\u7740\u4e24\u4e2a\u5b59\u5973\u51fa\u6765\uff0c\u949f\u4e07\u5802 \u53ea\u89c9\u773c\u775b\u4e00\u4eae\uff01 \u3000\u3000\u8fd9\u4e24\u4e2a\u5973\u5b69\u7c89\u96d5\u7389\u7422\uff0c\u4e24\u5bf9\u5927\u773c\u775b\u56db\u5904\u6ef4\u6e9c\u6e9c\u7684\u8f6c\uff0c\u5728\u6bcd\u4eb2\u6000\u91cc\u7259\u7259\u5b66\u8bed\uff0c\u795e\u6c14\u975e\u5e38\u3002 \u800c\u4e14\u76f8\u8c8c\u5b8c\u5168\u4e00\u6837\uff0c\u7b11\u65f6\u540c\u7b11\uff0c\u54ed\u65f6\u540c\u54ed\uff0c\u7adf\u50cf\u8fde\u5fc3\u601d\u4e5f\u662f\u4e00\u6837\u7684\uff01\u949f\u4e07\u5802\u770b\u5f97\u51fa\u795e\uff0c\u8d5e \u9053\uff1a\u201c\u8001\u5f1f\u5440\uff0c\u738b\u6bcd\u5a18\u5a18\u3001\u89c2\u97f3\u83e9\u8428\u90fd\u628a\u5979\u4eec\u5ea7\u4e0b\u7684\u7389\u5973\u9001\u7ed9\u4f60\u5566\uff0c\u8fd8\u4e0d\u628a\u4f60\u4e50\u6b7b\u4e86\uff01\u77a7\u4f60 \u7b11\u5f97\u8fd9\u4e2a\u6a21\u6837\uff01\u201d\u51af\u5e7f\u6f6e\u6b62\u4e86\u7b11\u9053\uff1a\u201c\u6211\u662f\u7b11\u4f60\u4e3a\u8001\u4e0d\u5c0a\uff0c\u563b\u76ae\u7b11\u8138\uff0c\u50cf\u6211\u5b59\u5973\u4e00\u6837\u3002\u201d\u505c \u4e86\u4e00\u505c\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5973\u5a74\u597d\u662f\u597d\u6781\u4e86\uff0c\u5c31\u662f\u6709\u4e00\u6837\u4e0d\u597d\uff01\u201d\u909d\u7ec3\u971e\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u516c \u516c\uff0c\u662f\u54ea\u4e00\u6837\u4e0d\u597d\uff1f\u201d\u51af\u5e7f\u6f6e\u62c8\u987b\u7b11\u9053\uff1a\u201c\u5979\u4eec\u51fa\u751f\u4e00\u5e74\u4e86\uff0c\u6211\u8fd8\u5206\u8fa8\u4e0d\u51fa\u90a3\u4e2a\u662f\u59d0\u59d0\uff0c\u90a3 \u4e2a\u662f\u59b9\u59b9\u3002\u5582\uff0c\u4f60\u8ddf\u6211\u8bf4\u8bf4\u770b\uff0c\u90a3\u4e2a\u662f\u745b\u513f\uff0c\u90a3\u4e2a\u662f\u7433\u513f\u3002\u201d\u8fd9\u5bf9\u5b6a\u751f\u5973\u513f\uff0c\u5927\u7684\u53d6\u540d\u51af \u745b\uff0c\u5c0f\u7684\u53d6\u540d\u51af\u7433\u3002\u53ef\u662f\u505a\u7956\u7236\u7684\u5206\u8fa8\u4e0d\u51fa\uff0c\u5e73\u65e5\u53ea\u662f\u201c\u5582\uff01\u5582\uff01\u201d\u7684\u4e71\u53eb\u3002 \u3000\u3000\u909d\u7ec3\u971e\u7b11\u9053\uff1b\u201c\u6211\u5e73\u5e38\u4e5f\u5206\u8fa8\u4e0d\u51fa\u6765\u5462\uff01\u9664\u975e\u9017\u5979\u4eec\u7b11\u4e86\uff0c\u624d\u5206\u5f97\u51fa\u90a3\u4e2a\u662f\u59d0\u59d0\uff0c\u90a3\u4e2a \u662f\u59b9\u59b9\u3002\u201d\u51af\u5e7f\u6f6e\u5947\u9053\uff1a\u201c\u55ef\uff0c\u6709\u8fd9\u4e48\u4e2a\u8bb2\u7a76\uff1f\u5979\u4eec\u7684\u7b11\u53c8\u6709\u4ec0\u4e48\u7279\u522b\u4e4b\u5904\u5462\u201d\uff0c\u909d\u7ec3\u971e\u4e00 \u624b\u62b1\u7740\u4e00\u4e2a\u5973\u513f\uff0c\u505a\u4e86\u4e00\u4e2a\u9b3c\u8138\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u4e56\u4e56\uff0c\u7b11\u7ed9\u516c\u516c\u770b\uff01\u201d\u9017\u4e86\u4e00\u9635\uff0c\u4e24\u4e2a\u5a03\u679c \u7136\u54a7\u5634\u4e00\u7b11\uff0c\u7b11\u8138\u4e0a\u90fd\u73b0\u51fa\u4e00\u4e2a\u9152\u6da1\uff0c\u909d\u7ec3\u971e\u9053\uff1a\u201c\u516c\u516c\uff0c\u4f60\u770b\u51fa\u6765\u4e86\u6ca1\u6709\uff1f\u4e00\u4e2a\u9152\u6da1\u5728 \u5de6\uff0c\u4e00\u4e2a\u9152\u6da1\u5728\u53f3\u3002\u201d\u4e24\u4e2a\u5c0f\u5b69\u5b50\u53c8\u7b11\u4e00\u7b11\uff0c\u51af\u5e7f\u6f6e\u7ec6\u770b\uff0c\u679c\u7136\u5982\u6b64\uff0c\u4e50\u5f97\u54c8\u54c8\u5927\u7b11\u3002\u909d\u7ec3 \u971e\u9053\uff1a\u201c\u9152\u6da1\u5728\u5de6\u9762\u7684\u662f\u59d0\u59d0\uff0c\u9152\u6da1\u5728\u53f3\u9762\u7684\u662f\u59b9\u59b9\uff0c\u516c\u516c\u4f60\u53ef\u8981\u8bb0\u4f4f\u4e86\uff01\u201d \u3000\u3000\u65e7\u53cb\u91cd\u9022\uff0c\u5b59\u5973\u5468\u5c81\uff0c\u51af\u5e7f\u6f6e\u9ad8\u5174\u975e\u5e38\uff0c\u8bf4\u8bf4\u7b11\u7b11\uff0c\u5230\u4e86\u5348\u65f6\uff0c\u909d\u7ec3\u971e\u51c6\u5907\u505c\u5f53\uff0c\u5bf9\u516c \u516c\u8bf4\uff1a\u201c\u770b\u745b\u513f\u548c\u7433\u513f\u2018\u6293\u5468\u2019\u53bb\uff01\u201d\u51af\u5bb6\u6ca1\u8bf7\u522b\u7684\u4eb2\u53cb\uff0c\u4f46\u653e\u5728\u7ea2\u5e03\u94fa\u7740\u7684\u5706\u684c\u4e0a\u7684\u4e1c\u897f \u53ef\u8fd8\u4e0d\u5c11\uff0c\u6709\u73a9\u5177\u3001\u7cd6\u679c\uff0c\u6709\u80ed\u8102\u3001\u955c\u5b50\uff0c\u4e5f\u6709\u91d1\u952d\u94f6\u5143\u3002 \u3000\u3000\u949f\u4e07\u5802\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e5f\u653e\u4e24\u6837\u4e1c\u897f\u4e0b\u53bb\u3002\u5b69\u5b50\u8981\u662f\u6293\u7740\uff0c\u5c31\u9001\u7ed9\u5979\u4eec\u4f5c\u89c1\u9762\u793c\u3002\u201d\u63a2\u624b \u6000\u4e2d\u53d6\u51fa\u4e00\u4ef6\u91d1\u4e1d\u8f6f\u7532\uff0c\u8fd9\u4ef6\u8f6f\u7532\u539f\u662f\u65e0\u6781\u5251\u5f53\u5e74\u7684\u5927\u5b97\u5e08\u5085\u9752\u4e3b\uff0c\u4ece\u897f\u85cf\u559c\u9a6c\u62c9\u96c5\u5c71\uff0c\u730e \u5f97\u4e00\u5934\u540d\u53eb\u91d1\u6bdb\u543c\u7684\u602a\u517d\uff0c\u53eb\u5de7\u5320\u5c06\u5b83\u7684\u6bdb\u6742\u4ee5\u91d1\u4e1d\u7f16\u7ec7\u6210\u7684\uff0c\u4f20\u4e86\u4e24\u4ee3\uff0c\u4f20\u5230\u949f\u4e07\u5802\u624b \u4e0a\u3002\u56e2\u8d77\u6765\u5927\u4ec5\u76c8\u63e1\uff0c\u7a7f\u5728\u8eab\u4e0a\uff0c\u4f5c\u4e3a\u8f6f\u7532\uff0c\u53ef\u4ee5\u62b5\u5fa1\u5200\u5251\uff0c\u5f53\u771f\u540d\u8d35\u975e\u5e38\uff01\u51af\u5e7f\u6f6e\u89c1\u4ed6\u53d6 \u51fa\u8fd9\u4ef6\u5b9d\u7269\uff0c\u5403\u4e00\u60ca\u9053\uff1a\u201c\u8001\u54e5\uff0c\u8fd9\u5982\u4f55\u4f7f\u5f97\uff1f\u8fd9\u662f\u4f60\u4eec\u8d35\u6d3e\u7684\u5b9d\u7269\u5440\uff01\u201d\u949f\u4e07\u5802\u9053\uff1a\u201c\u4f60 \u4e5f\u592a\u5c0f\u89d1\u6211\u4eec\u65e0\u6781\u6d3e\u4e86\u3002\u6211\u4eec\u8fd9\u6d3e\u7684\u4f20\u5bb6\u5b9d\u662f\u533b\u836f\u548c\u5251\u672f\uff0c\u53ef\u5e76\u4e0d\u662f\u8fd9\u4ef6\u8f6f\u7532\u3002\u8fd9\u53ea\u662f\u5085\u5e08 \u7956\u5f53\u5e74\u6e38\u620f\u4eba\u95f4\uff0c\u5076\u7136\u5f97\u5230\u800c\u5df2\u3002\u201d \u3000\u3000\u51af\u5e7f\u6f6e\u7ec8\u89c9\u4e0d\u59a5\uff0c\u5c1a\u5f85\u63a8\u8f9e\uff0c\u949f\u4e07\u5802\u7b2c\u4e8c\u4ef6\u793c\u7269\u53c8\u62ff\u51fa\u6765\u4e86\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4ef6\u793c\u7269\u53ef\u6ca1\u91d1 \u4e1d\u8f6f\u7532\u90a3\u6837\u540d\u8d35\uff0c\u4f46\u4e5f\u662f\u6211\u5e73\u751f\u5f97\u610f\u7684\u73a9\u827a\u3002\u201d\u8fd9\u4ef6\u793c\u7269\u662f\u4e00\u67c4\u4e94\u5bf8\u957f\u7684\u5c0f\u5315\u9996\uff0c\u5947\u5f02\u7684 \u662f\uff1a\u901a\u4f53\u9ed1\u6cb9\u6cb9\u7684\u3002\u8fde\u950b\u5203\u4e5f\u653e\u7740\u9ed1\u5149\u3002\u539f\u6765\u8fd9\u662f\u949f\u4e07\u5802\u7684\u6210\u540d\u6697\u5668\uff0c\u201c\u593a\u547d\u795e\u5200\u3002\u201d\u65e0\u6781 \u6d3e\u524d\u8f88\u5973\u4fa0\u3001\u5929\u5c71\u4e03\u5251\u4e4b\u4e00\u7684\u5192\u6d63\u83b2\uff0c\u5f53\u5e74\u968f\u5085\u9752\u4e3b\u5b66\u6280\u4e4b\u65f6\uff0c\u6240\u4f7f\u7684\u6697\u5668\u540d\u201c\u593a\u547d\u795e \u7802\u201d\u3002\u6709\u6bd2\u7684\u4e00\u79cd\uff0c\u4f24\u4eba\u4e4b\u540e\uff0c\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u82e5\u65e0\u89e3\u836f\uff0c\u4fbf\u6bd2\u53d1\u8eab\u4ea1\uff0c\u8fd9\u95e8\u6697\u5668\u4f20\u5230\u4e86 \u949f\u4e07\u5802\u65f6\uff0c\u89c9\u5f97\u593a\u547d\u795e\u7802\u6709\u4f18\u70b9\u4e5f\u6709\u7f3a\u70b9\uff0c\u4f18\u70b9\u662f\u4e00\u6492\u5c31\u662f\u4e00\u628a\uff0c\u5b9c\u4e8e\u4ee5\u5be1\u654c\u4f17\uff0c\u7f3a\u70b9\u662f\u4e0d \u80fd\u53ca\u8fdc\uff0c\u654c\u4eba\u5728\u4e09\u4e08\u4e4b\u5916\uff0c\u4fbf\u96be\u6253\u4e2d\u3002\u949f\u4e07\u5802\u559c\u6b22\u5f3a\u653b\u786c\u6253\uff0c\u4fbf\u5c06\u5236\u7ec3\u795e\u7802\u7684\u6bd2\u836f\uff0c\u62ff\u6765\u6d78 \u70bc\u98de\u5200\uff0c\u8fd9\u79cd\u98de\u5200\uff0c\u950b\u5229\u4e4b\u6781\uff0c\u4e00\u7ecf\u6dec\u6bd2\uff0c\u89c1\u8840\u5c01\u5589\uff0c\u7aef\u7684\u5341\u5206\u5389\u5bb3\u3002\u51af\u5e7f\u6f6e\u89c1\u4ed6\u53d6\u51fa\u6b64 \u7269\uff0c\u9ed8\u7136\u4e0d\u8bed\uff0c\u89c9\u5f97\u8fd9\u79cd\u6697\u5668\uff0c\u592a\u8fc7\u72e0\u6bd2\uff0c\u4e0d\u9002\u4e8e\u7ed9\u5973\u5b69\u513f\u5bb6\u73a9\u5f04\u3002\u4f46\u89c1\u949f\u4e07\u5802\u4e00\u65f6\u9ad8\u5174\uff0c \u4e5f\u5c31\u7f62\u4e86\u3002\u949f\u4e07\u5802\u5c06\u98de\u5200\u5957\u5165\u4e00\u4e2a\u76ae\u5957\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u82e5\u662f\u8c01\u6293\u5230\u4e86\uff0c\u6211\u5c31\u6559\u5979\u8fd9\u79cd\u6697\u5668\u3002\u201d \u3000\u3000\u5404\u79cd\u7269\u4ef6\u90fd\u6446\u597d\u4e4b\u540e\uff0c\u90ca\u7ec3\u971e\u62b1\u7740\u4e24\u4e2a\u5973\u513f\uff0c\u5f00\u59cb\u201c\u6293\u5468\u201d\u3002\u8bf4\u4e5f\u5947\u602a\uff0c\u4e24\u4e2a\u5b69\u5b50\u7b2c\u4e00 \u6b21\u6293\u7684\u90fd\u662f\u4e00\u628a\u6728\u5251\uff0c\u949f\u4e07\u5802\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u5979\u4eec\u90fd\u60f3\u4f5c\u5973\u5251\u5ba2\uff0c\u4f60\u8eab\u4e0a\u7684\u90a3\u70b9\u73a9\u827a\uff0c\u6050\u6015 \u8981\u5168\u4f20\u7ed9\u5979\u4eec\u3002\u201d\u8fd9\u65f6\u5b69\u5b50\u5c1a\u7a7a\u7740\u4e00\u624b\uff0c\u909d\u7ec3\u971e\u53c8\u7ed5\u684c\u8d70\u4e00\u5468\uff0c\u51af\u745b\u4f38\u51fa\u80a5\u5ae9\u7684\u5c0f\u624b\uff0c\u4e00\u6293 \u5c31\u6293\u8d77\u90a3\u4ef6\u91d1\u4e1d\u8f6f\u7532\u3002\u51af\u5e7f\u6f6e\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u771f\u8bc6\u8d27\uff01\u628a\u4eba\u5bb6\u7684\u5b9d\u8d1d\u4e5f\u6293\u53bb\u5566\uff01\u201d\u51af\u7433\u5374\u7741 \u7740\u4e24\u53ea\u53c8\u5706\u53c8\u4eae\u7684\u5927\u773c\u775b\uff0c\u9ed1\u6c34\u94f6\u4f3c\u7684\u773c\u73e0\u6ef4\u6e9c\u6e9c\u7684\u8f6c\uff0c\u51af\u5e7f\u6f6e\u89c9\u5f97\u5947\u5f02\uff0c\u53ea\u89c1\u5979\u968f\u6bcd\u4eb2\u5728 \u684c\u8fb9\u53c8\u7ed5\u4e86\u4e00\u5468\uff0c\u7a81\u7136\u5440\u5440\u7684\u53eb\u4e86\u8d77\u6765\uff0c\u909d\u7ec3\u971e\u6b62\u6b65\u51dd\u8eab\uff0c\u6ce8\u89c6\u5979\u7684\u52a8\u4f5c\uff0c\u53ea\u89c1\u5979\u7684\u5c0f\u624b\u7f13 \u7f13\u7684\u4f38\u4e86\u4e0b\u53bb\uff0c\u4e00\u5230\u684c\u4e0a\uff0c\u628a\u684c\u4e0a\u7684\u7269\u4ef6\u4e24\u8fb9\u4e71\u626b\uff0c\u909d\u7ec3\u971e\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5bb6\u4f19\u53d1\u4ec0\u4e48\u813e\u6c14 \u5440\uff01\u201d\u51af\u7433\u5440\u5440\u7684\u53eb\u4e86\u4e00\u9635\uff0c\u7a81\u7136\u5f2f\u8170\u4f38\u624b\uff0c\u5728\u5706\u684c\u4e2d\u592e\u628a\u90a3\u67c4\u6709\u6bd2\u98de\u5200\u6293\u4e86\u8d77\u6765\uff01\u51af\u5e7f\u6f6e \u76b1\u7709\u5934\u9ed8\u4e0d\u4f5c\u58f0\u3002\u949f\u4e07\u5802\u5374\u62cd\u624b\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u5979\u5012\u770b\u4e0a\u6211\u7684\u7edd\u62db\u4e86\u3002\u8001\u51af\uff0c\u5979\u5927\u4e2a\u4e86\uff0c\u4f60 \u5c31\u9001\u7ed9\u6211\u6559\u5979\u5427\uff0c\u6211\u6536\u5979\u505a\u5973\u5f92\u5f1f\u3002\u201d\u51af\u5e7f\u6f6e\u5f3a\u7b11\u9053\uff1a\u201c\u90a3\u6562\u60c5\u597d\uff0c\u53ea\u662f\u6211\u6015\u5979\u5927\u4e86\u662f\u4e2a\u5201 \u86ee\u516c\u4e3b\uff01\u201d \u3000\u3000\u201c\u6293\u5468\u201d\u5b8c\u540e\uff0c\u4e24\u4e2a\u8001\u670b\u53cb\u53c8\u6d77\u9614\u5929\u7a7a\uff0c\u8bf4\u4e86\u4e00\u9635\uff0c\u909d\u740f\u60f3\u542c\u4ed6\u4eec\u662f\u600e\u6837\u7ed3\u8bc6\u7684\uff0c\u53ef\u662f \u5374\u603b\u4e0d\u89c1\u4ed6\u4eec\u8c08\u8d77\u3002\u53ea\u542c\u5f97\u949f\u4e07\u5802\u9053\uff1a\u201c\u524d\u8f88\u5251\u4fa0\u51cc\u672a\u98ce\u901d\u4e16\u4e4b\u540e\uff0c\u542c\u8bf4\u6b66\u5f53\u5317\u652f\u7684\u8001\u638c\u95e8 \u6842\u4ef2\u660e\u524d\u5e74\u4e5f\u53bb\u4e16\u4e86\u3002\u800c\u4eca\u4e2d\u539f\u7684\u5251\u5ba2\uff0c\u8fdc\u4e0d\u53ca\u8001\u4e00\u8f88\u7684\u9020\u8be3\u4e86\uff01\u201d\u4e24\u4eba\u4e00\u9635\u6168\u53f9\uff0c\u51af\u5e7f\u6f6e \u66f4\u662f\u795e\u4f24\u3002\u9ec4\u660f\u65f6\u5206\uff0c\u5c4b\u5916\u72ac\u58f0\u6c6a\u6c6a\uff0c\u7ee7\u800c\u72c2\u964d\u4e71\u53eb\uff0c\u4f3c\u4e4e\u662f\u7ed9\u4ec0\u4e48\u602a\u5f02\u5413\u7834\u4e86\u80c6\uff0c\u909d\u740f \u9053\uff1a\u201c\u4eb2\u5bb6\uff0c\u6211\u51fa\u53bb\u7ed9\u4f60\u770b\u770b\u662f\u8c01\u6765\u4e86\u3002\u201d\u8d70\u51fa\u5927\u95e8\uff0c\u53ea\u89c9\u4e00\u80a1\u8840\u8165\u5473\u6251\u9f3b\u800c\u6765\uff0c\u66ae\u853c\u82cd\u832b \u4e2d\uff0c\u6709\u4e00\u4e2a\u7626\u957f\u6c49\u5b50\uff0c\u77ed\u987b\u5982\u621f\uff0c\u624b\u63d0\u4e00\u4e2a\u8349\u56ca\uff0c\u6b63\u5728\u5927\u8e0f\u6b65\u8d70\u6765\uff01 \u3000\u3000\u909d\u740f\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u4e0a\u524d\u62e6\u963b\uff0c\u95ee\u9053\uff1a\u201c\u5e72\u5417\uff1f\u627e\u8c01\u6765\u7684\uff1f\u201d\u90a3\u6c49\u5b50\u7406\u4e5f\u4e0d\u7406\uff0c\u53cc\u81c2\u4e00 \u9707\uff0c\u909d\u740f\u53ea\u89c9\u4e00\u80a1\u5927\u529b\u649e\u6765\uff0c\u8eab\u4e0d\u7531\u5df2\u7684\u76f4\u50cf\u817e\u4e91\u9a7e\u96fe\u822c\u7684\u7ed9\u629b\u56de\u5c4b\u5185\uff0c\u722c\u8d77\u6765\u65f6\uff0c\u90a3\u4eba\u5df2 \u8e0f\u6b65\u7684\u8d70\u5165\u5385\u5802\uff0c\u51af\u5e7f\u6f6e\u548c\u949f\u4e07\u5802\u60ca\u53eb\u8d77\u6765\uff0c\u521a\u8bf4\u5f97\u4e00\u58f0\uff1a\u201c\u5468\u8001\u5e08\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u201d\u90a3\u4eba\u5495 \u549a\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\uff0c\u5636\u58f0\u53eb\u9053\uff1a\u201c\u62ff\u91d1\u521b\u836f\u548c\u89e3\u6bd2\u6563\u6765\uff01\u201d\u4e00\u9635\u7ffb\u817e\uff0c\u6655\u4e86\u8fc7\u53bb\uff0c\u909d\u740f\u60ca\u5f97 \u5446\u5728\u90a3\u513f\uff0c\u505a\u58f0\u4e0d\u5f97\u3002\u51af\u5e7f\u6f6e\u53eb\u9053\uff1a\u201c\u4eb2\u5bb6\uff0c\u5feb\uff0c\u5feb\uff0c\u5feb\u5173\u4e0a\u5927\u95e8\uff01\u201d\u909d\u94fe\u77e5\u9053\u4e8b\u6001\u4e25\u91cd\u3001 \u6025\u5fd9\u628a\u5927\u95e8\u5173\u4e0a\uff0c\u53ea\u89c1\u949f\u4e07\u5802\u5df2\u628a\u90a3\u4eba\u6276\u5728\u7095\u4e0a\uff0c\u89e3\u5f00\u8863\u670d\uff0c\u66ff\u4ed6\u68c0\u67e5\u4f24\u5904\u3002\u909d\u740f\u8fd9\u624d\u6ce8\u610f \u5230\uff0c\u90a3\u6c49\u5b50\u9762\u8272\u7126\u9ec4\uff0c\u7ea6\u83ab\u6709\u4e94\u5341\u5c81\u5e74\u7eaa\uff0c\u4e0a\u8eab\u77ed\u9760\u7d27\u8863\u67d3\u6ee1\u6de4\u8840\uff0c\u8840\u5473\u8165\u81ed\uff0c\u60f3\u662f\u53d7\u4e86\u4ec0 \u4e48\u5267\u6bd2\u7684\u6697\u5668\uff0c\u8feb\u4e0d\u53ca\u5f85\u7684\u8d76\u6765\u6c42\u533b\u3001\u56e0\u6b64\u65e0\u6687\u548c\u81ea\u5df1\u6253\u8bdd\uff0c\u5c31\u901e\u884c\u51b2\u8fdb\u6765\u3002 \u3000\u3000\u949f\u4e07\u5802\u89e3\u5f00\u4e86\u90a3\u6c49\u5b50\u7684\u7d27\u8863\uff0c\u9762\u8272\u82cd\u767d\u3002\u51af\u5e7f\u6f6e\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u6697\u5668\uff1f\u201d\u909d\u740f\u51d1 \u4e0a\u6765\u770b\uff0c\u53ea\u89c1\u90a3\u4eba\u7684\u80f8\u819b\u597d\u50cf\u662f\u7ed9\u5229\u722a\u6293\u4f24\uff0c\u53c8\u597d\u50cf\u662f\u7ed9\u5315\u9996\u5212\u4f24\u4e00\u6837\uff0c\u6bcf\u9053\u4f24\u75d5\u4e4b\u95f4\uff0c\u8ddd \u79bb\u90fd\u5dee\u4e0d\u591a\uff0c\u6574\u6574\u9f50\u9f50\uff0c\u6392\u6210\u4e24\u4e2a\u534a\u7403\u5f62\uff0c\u5c31\u50cf\u4e00\u53cc\u5de8\u5927\u7684\u9b54\u624b\u4e0a\u4e0b\u5408\u7f69\uff0c\u7f69\u5728\u4ed6\u7684\u80f8\u77a0 \u4e0a\uff0c\u4f46\u7ec6\u6570\u4f24\u75d5\uff0c\u5374\u6709\u5341\u4f59\u6761\u4e4b\u591a\uff0c\u663e\u89c1\u4e0d\u662f\u6307\u6293\u4f24\uff0c\u800c\u4e14\u4eba\u7684\u6307\u529b\uff0c\u4e5f\u7edd\u4e0d\u53ef\u80fd\u6709\u8fd9\u4e48\u5389 \u5bb3\uff0c\u6b63\u5728\u6b64\u9645\uff0c\u5ffd\u53c8\u542c\u5230\u51af\u745b\u5947\u60ca\u53eb\u9053\uff1a\u201c\u7238\u7238\uff0c\u4eba\u5934\uff01\u201d\u51af\u745b\u5947\u5c11\u4e0d\u66f4\u4e8b\uff0c\u4e00\u65f6\u597d\u5947\uff0c\u6253 \u5f00\u4e86\u602a\u5ba2\u7684\u8349\u56ca\uff0c\u4e24\u9897\u8840\u8089\u6a21\u7cca\u7684\u4eba\u5934\u76ae\u7403\u822c\u7684\u6eda\u4e86\u51fa\u6765\uff0c\u8840\u8165\u6c14\u5473\uff0c\u4e2d\u4eba\u6b32\u5455\u3002\u51af\u5e7f\u6f6e\u9a82 \u9053\uff1a\u201c\u4f60\u597d\u4e0d\u61c2\u4e8b\uff0c\u600e\u4e48\u597d\u80e1\u4e71\u6253\u5f00\u522b\u4eba\u7684\u4e1c\u897f\uff01\u4f60\u77e5\u9053\u4ed6\u662f\u8c01\uff01\u201d\u5fd9\u628a\u4eba\u5934\u653e\u56de\u8349\u56ca\u3002\u949f \u4e07\u5802\u4ecd\u5728\u51dd\u795e\u66ff\u90a3\u602a\u5ba2\u6577\u836f\uff0c\u51af\u5e7f\u6f6e\u9053\uff1a\u201c\u6709\u5f97\u6551\u4e48\uff1f\u201d\u949f\u4e07\u5802\u9053\uff1a\u201c\u5404\u5bb6\u5404\u6d3e\u7684\u6697\u5668\uff0c\u6211 \u6ca1\u6709\u89c1\u8fc7\u4e5f\u542c\u8bf4\u8fc7\uff0c\u53ea\u6709\u8fd9\u79cd\u6697\u5668\uff0c\u4e0d\u4f46\u89c1\u6240\u672a\u89c1\uff0c\u800c\u4e14\u95fb\u6240\u672a\u95fb\u3002\u6dec\u7ec3\u6697\u5668\u6bd2\u836f\uff0c\u4e0d\u662f\u5b54 \u96c0\u80c6\u5c31\u662f\u9e64\u9876\u7ea2\uff0c\u6050\u6015\u5f88\u96be\u6551\u6cbb\u3002\u6211\u53ea\u6709\u7528\u593a\u547d\u795e\u5200\u7684\u89e3\u836f\u4e00\u8bd5\uff0c\u4ed7\u7740\u5468\u5927\u4fa0\u6df1\u6e5b\u5185\u529f\uff0c\u6216 \u8bb8\u8fd8\u6709\u4e00\u7ebf\u751f\u673a\u3002\u201d \u3000\u3000\u602a\u5ba2\u7ed9\u6577\u4e0a\u836f\u540e\uff0c\u9f3b\u7aef\u6c14\u606f\u6e10\u7c97\uff0c\u53ea\u662f\u4eba\u8fd8\u672a\u9192\u3002\u51af\u5e7f\u6f6e\u5c48\u7740\u4e00\u819d\uff0c\u606d\u606d\u656c\u656c\u7684\u66ff\u4ed6\u6362 \u4e86\u80f8\u8863\uff0c\u63e9\u5e72\u8840\u8ff9\uff0c\u8fd9\u624d\u5401\u4e86\u53e3\u6c14\uff0c\u5bf9\u51af\u82f1\u5947\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u77e5\u9053\u4ed6\u662f\u8c01\u5417\uff1f\u4ed6\u5c31\u662f\u4f60\u7684\u5e08 \u7956\uff01\u201d\u51af\u745b\u5947\u9053\uff1a\u201c\u5e7f\u6f6e\uff0c\u4f60\u7684\u5e08\u5085\u516d\u5408\u67aa\u4f59\u5927\u6869\u4e0d\u662f\u65e9\u5c31\u53bb\u4e16\u4e86\u5417\uff1f\u600e\u4e48\u53c8\u6709\u4e00\u4e2a\u5e08 \u5085\uff1f\u201d\u51af\u5e7f\u6f6e\u82e6\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u6211\u79f0\u4ed6\u505a\u5e08\u5085\u6709\u70b9\u50ed\u8d8a\uff0c\u6211\u53ea\u662f\u4ed6\u7684\u8bb0\u540d\u5f92\u5f1f\uff0c\u82f1\u513f\uff0c\u4f60\u5148\u8dea \u4e0b\u6765\u78d5\u4e09\u4e2a\u5934\uff0c\u5e08\u7956\u867d\u7136\u660f\u8ff7\uff0c\u793c\u4eea\u5374\u4e0d\u53ef\u5e9f\uff01\u201d\u51af\u82f1\u5947\u5982\u8a00\u78d5\u5934\uff0c\u5510\u6653\u6f9c\u4e5f\u8dea\u5728\u4e00\u8fb9\u4f4e\u58f0 \u7f00\u6ce3\uff0c\u51af\u5e7f\u6f6e\u6276\u4ed6\u7684\u5934\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u4e0d\u5728\u5468\u4f2f\u4f2f\u75bc\u4f60\uff0c\u4f60\u5012\u771f\u662f\u6027\u60c5\u4e2d\u4eba\u3002\u201d\u909d\u740f\u542c\u4e86\uff0c \u66f4\u52a0\u5947\u5f02\uff0c\u8fd9\u4e2a\u602a\u5ba2\uff0c\u88ab\u949f\u4e07\u5802\u79f0\u4e3a\u201c\u5927\u4fa0\u201d\uff0c\u5374\u662f\u5510\u6653\u6f9c\u7684\u201c\u4f2f\u4f2f\u201d\u3002\u800c\u4e14\u8fd9\u4e2a\u602a\u5ba2\u770b\u6765 \u4e0d\u8fc7\u4e94\u5341\u5de6\u53f3\uff0c\u6bd4\u51af\u5e7f\u6f6e\u4e5f\u5927\u4e0d\u4e86\u591a\u5c11\uff0c\u5374\u53c8\u662f\u4ed6\u7684\u201c\u5e08\u5085\u201d\u3002 \u3000\u3000\u51af\u82f1\u5947\u78d5\u5b8c\u4e09\u4e2a\u54cd\u5934\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u51af\u5e7f\u6f6e\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5e08\u7956\u540d\u53eb\u5468\u9752\uff0c\u662f\u5929\u5c71\u5251\u5ba2 \u51cc\u672a\u98ce\u7684\u8bb0\u540d\u5f1f\u5b50\uff01\u201d\u909d\u740f\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\uff0c\u602a\u4e0d\u5f97\u5982\u6b64\u5389\u5bb3\uff0c\u91cd\u4f24\u4e4b\u540e\uff0c\u968f\u624b\u4e00\u9707\uff0c\u8fd8\u80fd \u628a\u6211\u649e\u5f97\u53d1\u660f\uff01 \u3000\u3000\u51af\u5e7f\u6f6e\u53c8\u9053\uff1a\u201c\u5eb7\u7199\u521d\u5e74\uff0c\u51cc\u672a\u98ce\u88ab\u540c\u95e8\u5e08\u5144\u695a\u662d\u5357\u7387\u4f17\u56f4\u6355\uff0c\u5173\u5728\u897f\u85cf\u62c9\u8428\u7684\u5e03\u8fbe\u62c9 \u5bab\uff0c\u540e\u6765\u5f97\u4e00\u4e2a\u6e05\u5ef7\u6b66\u58eb\u4e4b\u52a9\uff0c\u9003\u51fa\u751f\u5929\u3002\u51cc\u672a\u98ce\u4e3a\u4e86\u62a5\u7b54\u4ed6\uff0c\u5c31\u6559\u7ed9\u4ed6\u4e00\u8def\u8ffd\u98ce\u5251\u6cd5\uff0c\u8ba4 \u4ed6\u4e3a\u8bb0\u540d\u5f1f\u5b50\uff08\u4e0d\u662f\u6b63\u5f0f\u6536\u5f92\uff09\u3002\u8fd9\u4e2a\u6b66\u58eb\u5c31\u662f\u4f60\u7684\u5468\u5e08\u7956\u4e86\uff01\u201d\u8fd9\u6bb5\u638c\u6545\uff0c\u6b66\u6797\u4e2d\u7684\u524d\u8f88 \u5927\u591a\u77e5\u9053\uff0c\uff08\u6309\uff1a\u8be6\u89c1\u62d9\u8457\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\uff09\u51af\u82f1\u5947\u5374\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\uff0c\u5f20\u5927\u5634\u5df4\uff0c\u8bf4\u4e0d\u51fa \u8bdd\uff0c\u60f3\u4e0d\u5230\u81ea\u5df1\u7236\u4eb2\uff0c\u7adf\u662f\u5929\u4e0b\u95fb\u540d\u7684\u5929\u5c71\u6d3e\u524d\u8f88\u5251\u4fa0\u51cc\u672a\u98ce\u7684\u65c1\u652f\u3002 \u3000\u3000\u51af\u5e7f\u6f6e\u5477\u4e86\u53e3\u8336\uff0c\u53c8\u5bf9\u909d\u740f\u8bf4\u9053\uff1a\u201c\u4eb2\u5bb6\uff0c\u4e0d\u662f\u6211\u591a\u5e74\u6765\u4e00\u76f4\u7792\u4f60\uff0c\u53ea\u56e0\u4f60\u662f\u4e2a\u8001\u5b9e \u4eba\uff0c\u77e5\u9053\u4e86\u53cd\u800c\u62c5\u60ca\u53d7\u6015\u3002\u51cc\u672a\u98ce\u9690\u5c45\u5929\u5c71\uff0c\u6e05\u5ef7\u5948\u4f55\u4ed6\u4e0d\u5f97\u3002\u5468\u9752\u53ef\u662f\u6e05\u5bab\u4e09\u5341\u5e74\u6765\u6240\u8981 \u8ffd\u6355\u7684\u94a6\u72af\uff01\u201d\u949f\u4e07\u5802\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5468\u5927\u4fa0\u6b64\u8a00\u5dee\u77e3\uff0c\u6211\u907f\u4ec7\u5bb6\uff0c\u8f7b\u6613\u90fd\u4e0d\u6562\u5728\u6c5f\u6e56 \u9732\u9762\uff0c\u8fd9\u5341\u591a\u5e74\u6765\u6211\u4e5f\u51e0\u4e4e\u95f7\u6b7b\u5566\uff01\u201d\u51af\u5e7f\u6f6e\u987f\u4e86\u4e00\u987f\uff0c\u7eed\u9053\uff1a\u201c\u4eb2\u5bb6\uff0c\u4eca\u591c\u4f60\u90fd\u77a7\u89c1\u4e86\uff0c \u6211\u4e5f\u4e0d\u5fc5\u7792\u4f60\uff0c\u5c31\u90fd\u544a\u8bc9\u4f60\u5427\u3002\u770b\u6765\u5468\u8001\u5e08\u4e00\u5b9a\u662f\u7ed9\u5f3a\u654c\u6240\u4f24\uff0c\u8ffd\u9a91\u65e9\u665a\u4f1a\u5230\uff0c\u6211\u628a\u4f60\u7684\u5916 \u5b59\u5973\u91cd\u6258\u4f60\u4e86\uff0c\u4f60\u5e26\u5979\u4eec\u51fa\u8d70\uff01\u4f60\u662f\u4e2a\u5b89\u4efd\u5b88\u5df1\u7684\u6b66\u5e08\uff0c\u6c5f\u6e56\u4e0a\u77e5\u9053\u4f60\u7684\u4e5f\u4e0d\u591a\uff0c\u6e05\u5ef7\u4e5f\u4e0d \u4f1a\u6ce8\u610f\u4f60\uff01\u201d\u909d\u740f\u8fd9\u624d\u604d\u7136\u5927\u609f\uff1a\u201c\u539f\u6765\u90a3\u4e24\u4e2a\u5bb6\u4f19\u8bf4\u7684\u90a3\u4e2a\u4eba\u5c31\u662f\u5468\u9752\u3002\u201d\u5f53\u4e0b\u6168\u7136\u8bf4 \u9053\uff1a\u201c\u4eb2\u5bb6\uff0c\u8fd9\u662f\u4ec0\u4e48\u8bdd\u6765\uff1f\u6211\u867d\u606f\u6b66\u52a1\u519c\uff0c\u4e5f\u8fd8\u662f\u6761\u70ed\u8840\u6c49\u5b50\uff0c\u54b1\u4eec\u6709\u96be\u540c\u5f53\uff0c\u8ffd\u9a91\u82e5 \u6765\uff0c\u54b1\u4eec\u5408\u529b\u95ef\u51fa\u53bb\uff01\u201d\u51af\u5e7f\u6f6e\u5fae\u7b11\u9053\uff1a\u201c\u4f46\u613f\u80fd\u95ef\u51fa\u53bb\uff0c\u53ea\u662f\u4e0d\u6015\u4eb2\u5bb6\u751f\u6c14\uff0c\u51ed\u7740\u6211\u4eec\u8fd9 \u51e0\u624b\u4e09\u811a\u732b\u7684\u529f\u592b\uff0c\u53ea\u6015\u96be\u4ee5\u62b5\u5fa1\u5f3a\u654c\u3002\u201d\u909d\u740f\u89c1\u5468\u9752\u5c1a\u4e14\u5982\u6b64\uff0c\u60c5\u77e5\u6240\u8bf4\u4e0d\u865a\uff0c\u53f9\u53e3\u6c14 \u9053\uff1a\u201c\u90a3\u4e48\u5929\u4e00\u4eae\u6211\u5c31\u5e26\u73bb\u513f\u7433\u513f\u5230\u6ee6\u5ddd\u53bb\u627e\u6211\u7684\u5e08\u54e5\u3002\u201d \u3000\u3000\u51af\u5e7f\u6f6e\u629a\u4e86\u4e00\u4e0b\u5468\u9752\u989d\u5934\uff0c\u89c1\u4ed6\u672a\u9192\uff0c\u9053\uff1a\u201c\u4eb2\u5bb6\uff0c\u5341\u5e74\u524d\u6211\u5f52\u9690\u6545\u56ed\uff0c\u6c5f\u6e56\u4e0a\u670b\u53cb\u90fd \u5f88\u5947\u602a\uff0c\u4f60\u4e5f\u95ee\u8fc7\u6211\uff0c\u90a3\u65f6\u6211\u4e0d\u6562\u8bf4\uff0c\u73b0\u5728\u53ef\u4ee5\u544a\u8bc9\u4f60\u4e86\uff0c\u90a3\u65f6\u6211\u521a\u521a\u8ddf\u5468\u8001\u5e08\u5b66\u4f1a\u4e86\u8ffd\u98ce \u5251\u6cd5\uff0c\u662f\u5468\u8001\u5e08\u53eb\u6211\u5f52\u9690\u7684\uff01\u201d\u51af\u82f1\u5947\u7741\u5927\u773c\u8bf4\u9053\uff1a\u201c\u7238\u7238\uff0c\u4e3a\u4ec0\u4e48\u4f60\u5b66\u4f1a\u8ffd\u98ce\u5251\u6cd5\uff0c\u5374\u4e0d \u6559\u6211\uff0c\u53ea\u6559\u6211\u516d\u5408\u5927\u67aa\u3002\u5510\u5e08\u5f1f\u7ec3\u7684\u662f\u4e0d\u662f\u8ffd\u98ce\u5251\u6cd5\uff1f\u201d\u51af\u5e7f\u6f6e\u70b9\u4e86\u70b9\u5934\u3002\u51af\u82f1\u5947\u9762\u8272\u4e0d \u60a6\uff0c\u5947\u602a\u7236\u4eb2\u4f55\u4ee5\u5982\u6b64\u504f\u5fc3\uff0c\u8ffd\u98ce\u5251\u6cd5\u4f20\u4e0e\u5916\u4eba\u5374\u4e0d\u4f20\u7ed9\u513f\u5b50\uff1f\u51af\u5e7f\u6f6e\u4f3c\u4e4e\u77e5\u9053\u4ed6\u7684\u5fc3\u601d\uff0c \u5ffd\u9053\uff1a\u201c\u4f60\u61c2\u5f97\u4ec0\u4e48\uff1f\u6211\u4e0d\u60f3\u8fde\u7d2f\u4f60\uff01\u201d\u7ad9\u5728\u4e00\u8fb9\u7684\u5510\u6653\u6f9c\u53cc\u773c\u4e00\u7ea2\uff0c\u6ceb\u7136\u6b32\u6ce3\u3002 \u3000\u3000\u51af\u5e7f\u6f6e\u62c8\u987b\u53f9\u606f\uff0c\u5fc3\u60f3\uff1a\u4e0d\u5982\u8bf4\u4e86\u51fa\u6765\uff0c\u514d\u5f97\u4ed6\u4eec\u5b58\u6709\u82a5\u8482\u3002\u62c9\u7740\u513f\u5b50\u7684\u624b\uff0c\u7f13\u7f13\u8bf4 \u9053\uff1a\u201c\u4f60\u7239\u7239\u5f97\u7956\u5e08\u4f20\u6388\u8ffd\u98ce\u5251\u6cd5\uff0c\u5c31\u662f\u4e3a\u4e86\u4f60\u7684\u5510\u5e08\u5f1f\u800c\u8d77\u7684\uff0c\u6211\u8bf4\u7ed9\u4f60\u542c\uff0c\u4f60\u5c31\u77e5\u9053\u4e3a \u4ec0\u4e48\u6211\u4e0d\u80af\u6559\u4f60\u5251\u672f\u4e86\u3002\u201d \u3000\u3000\u201c\u5341\u5e74\u4e4b\u524d\uff0c\u6211\u5728\u585e\u5916\u6f2b\u6e38\uff0c\u4e00\u65e5\u4ece\u767e\u7075\u5e99\u7ecf\u8fc7\uff0c\u62df\u4eba\u56de\u7586\uff0c\u5929\u9634\u65e5\u66ae\uff0c\u5ffd\u542c\u5f97\u53f1\u54a4\u53ae \u6740\u58f0\uff0c\u89c1\u5341\u4f59\u540d\u5f3a\u5f92\u56f4\u7740\u4e00\u4e2a\u5c11\u5987\uff0c\u6253\u5f97\u5341\u5206\u70bd\u70c8\uff01\u90a3\u5c11\u5987\u7684\u5251\u6cd5\u4fca\u6781\u5566\uff0c\u5f3a\u5f92\u4e2d\u5df2\u6709\u6570\u4eba \u53d7\u4f24\uff0c\u53ef\u8fd8\u4e0d\u80af\u653e\u677e\u56f4\u653b\u3002\u5c11\u5987\u53f3\u624b\u4ed7\u5251\uff0c\u5de6\u624b\u6280\u7740\u4e00\u4e2a\u4e03\u516b\u5c81\u5927\u7684\u5b69\u5b50\uff0c\u53ea\u80fd\u9632\u5fa1\uff0c\u65e0\u6cd5 \u8fdb\u653b\u3002\u6fc0\u6218\u4e2d\u90a3\u5c11\u5987\u4e3a\u4e86\u4fdd\u536b\u5b69\u5b50\uff0c\u9669\u8c61\u73af\u751f\u3002\u6211\u98de\u9a70\u5230\u65f6\uff0c\u6070\u542c\u5f97\u90a3\u5c11\u5f52\u5927\u58f0\u53eb\u503c\uff1a\u201c\u4f60 \u4eec\u8981\u6211\u7684\u6027\u547d\u4e5f\u8fd8\u7f62\u4e86\uff0c\u5982\u4f55\u8fd8\u8981\u4f24\u5bb3\u6211\u7684\u513f\u5b50\uff1f\u201d\u5979\u4e0d\u53eb\u8fd8\u597d\uff0c\u4e00\u53eb\u51fa\u6765\uff0c\u90a3\u73ed\u5f3a\u5f92\u7684\u5200 \u67aa\u5251\u621f\u7adf\u4e00\u9f50\u5411\u90a3\u5b69\u5b50\u6233\u53bb\uff0c\u5c11\u5987\u4e00\u53e3\u5251\u524d\u906e\u540e\u6321\uff0c\u4fe8\u5982\u4e00\u5708\u94f6\u8679\uff0c\u906e\u5f97\u98ce\u96e8\u4e0d\u900f\u3002\u53ef\u662f\u5979 \u62a4\u7740\u5b69\u5b50\uff0c\u5374\u62a4\u4e0d\u4e86\u81ea\u5df1\uff0c\u53ea\u542c\u5f97\u5979\u60e8\u53eb\u6570\u58f0\uff0c\u663e\u7136\u662f\u53d7\u4e86\u91cd\u4f24\u3002\u6211\u518d\u4e5f\u6309\u637a\u4e0d\u4f4f\uff0c\u4e5f\u4e0d\u987e \u81ea\u5df1\u6b66\u827a\u4f4e\u5fae\uff0c\u4e00\u63d0\u9a6c\u7f30\uff0c\u5c31\u4ece\u540e\u5761\u4e0a\u76f4\u51b2\u4e0b\u53bb\u3002\u51fa\u5176\u4e0d\u610f\uff0c\u523a\u5012\u4e24\u540d\u5f3a\u5f92\uff0c\u51b2\u5165\u6838\u5fc3\uff0c\u90a3 \u5c11\u5987\u89c1\u6211\u51b2\u6765\uff0c\u628a\u5b69\u5b50\u5f80\u6211\u9a6c\u80cc\u4e0a\u4e00\u629b\uff0c\u53eb\u9053\uff1a\u2018\u4e49\u58eb\uff0c\u5b69\u5b50\u6258\u7ed9\u4f60\u4e86\uff0c\u4f60\u95ef\u51fa\u53bb\uff01\u2019\u5979\u5251 \u4f3c\u8ffd\u98ce\uff0c\u5f53\u8005\u62ab\u9761\u3002\u6211\u62b1\u7740\u5b69\u5b50\uff0c\u594b\u529b\u51b2\u6740\uff0c\u4ed7\u7740\u90a3\u5c11\u5987\u63a9\u62a4\uff0c\u5c45\u7136\u7ed9\u6211\u51b2\u51fa\u4e00\u6761\u8840\u8def\uff0c\u53ef \u662f\u521a\u51b2\u51fa\u91cd\u56f4\uff0c\u4fbf\u542c\u5f97\u80cc\u540e\u4e00\u58f0\u60e8\u53eb\uff0c\u90a3\u5c11\u5987\u5df2\u906d\u4e86\u6bd2\u624b\uff01\u6211\u56de\u5934\u4e00\u770b\uff0c\u51b7\u4e0d\u9632\u4e00\u652f\u51b7\u7bad\uff0c \u5288\u9762\u5c04\u6765\uff0c\u6211\u80f8\u53e3\u4e00\u9635\u5267\u75db\uff0c\u5012\u7ffb\u4e0b\u9a6c\uff0c\u5b69\u5b50\u4e5f\u7ed9\u6454\u5728\u5730\u4e0a\uff0c\u7ee7\u800c\u5927\u54ed\u3002\u5f3a\u5f92\u6076\u53eb\u903c\u6765\uff0c\u660f \u8ff7\u4e2d\u5ffd\u542c\u5f97\u4e00\u58f0\u5927\u53eb\uff1a\u201c\u9f20\u5b50\u6562\u5c14\uff01\u201d\u5c71\u5761\u4e0a\u98de\u4e0b\u4e00\u6761\u4eba\u5f71\uff0c\u6211\u4f0f\u5728\u5730\u4e0a\u53ea\u542c\u5f97\u9635\u9635\u91d1\u94c1\u4ea4 \u9e23\u4e4b\u58f0\uff0c\u53c8\u542c\u5f97\u957f\u7b11\u547c\u53f7\u4e4b\u58f0\u6742\u4f5c\uff0c\u6211\u5f3a\u7741\u53cc\u773c\uff0c\u4ee5\u8098\u652f\u5730\uff0c\u7591\u795e\u671b\u53bb\uff0c\u53ea\u89c1\u9762\u524d\u65e0\u6570\u9ed1 \u5f71\uff0c\u4e00\u7247\u94f6\u5149\uff0c\u7eb5\u8dc3\u98de\u821e\uff0c\u4e71\u505a\u4e00\u56e2\uff0c\u5176\u4e2d\u6709\u4e00\u9053\u5339\u7ec3\u4f3c\u7684\u767d\u5149\uff0c\u95ea\u7535\u4f3c\u7684\u5728\u65e0\u6570\u9ed1\u5f71\u4e2d\u7a7f \u6765\u63d2\u53bb\uff0c\u767d\u5149\u6240\u5230\u9ed1\u5f71\u5982\u6ce2\u5206\u6d6a\u88c2\uff0c\u56db\u5904\u4e71\u7a9c\uff0c\u90a3\u9053\u767d\u5149\u6fc0\u7bad\u822c\u8ffd\u9010\uff0c\u970e\u5ffd\u5411\u4e1c\u970e\u5ffd\u5411\u897f\uff0c \u7247\u523b\u95f4\u9ed1\u5f71\u7ed9\u626b\u8361\u5f97\u4e00\u4e2a\u4e0d\u7559\uff0c\u767d\u5149\u4e00\u6536\uff0c\u8352\u91ce\u95f4\u5269\u4e0b\u4e00\u4e2a\u957f\u8eab\u6c49\u5b50\uff0c\u8d70\u8fc7\u6765\u5c06\u6211\u6276\u8d77\uff0c\u8bf4 \u58f0\uff1a\u201c\u4e49\u58eb\uff0c\u4f60\u53d7\u60ca\u4e86\u3002\u201d\u6211\u672c\u6765\u75db\u6781\u6b32\u6655\uff0c\u89c1\u4e86\u8fd9\u573a\u6fc0\u6597\uff0c\u5413\u5f97\u5f20\u53e3\u7ed3\u820c\uff0c\u53cd\u800c\u4e0d\u89c9\u5f97\u75bc \u75db\u4e86\uff0c\u6211\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u5251\u4ed9\uff1f\u201d\u90a3\u4eba\u7b11\u4e86\u4e00\u7b11\uff0c\u5c06\u91d1\u521b\u836f\u7ed9\u6211\u6577\u4e0a\uff0c\u8bf4\u9053\uff1a\u2018\u50cf\u6211\u8fd9\u6837\u7684 \u529f\u592b\uff0c\u5929\u4e0b\u591a\u7684\u662f\uff01\u2019\u8fd9\u65f6\u90a3\u5b69\u5b50\u5df2\u722c\u4e86\u8d77\u6765\uff0c\u62b1\u7740\u90a3\u4eba\u7684\u817f\uff0c\u54ed\u53eb\uff1a\u2018\u5468\u4f2f\u4f2f\uff0c\u5468\u4f2f\u4f2f\uff0c \u6211\u7684\u5988\u5988\u5462\uff1f\u2019\u201d\u8bf4\u5230\u6b64\u5904\uff0c\u65c1\u8fb9\u7684\u5510\u6653\u6f9c\uff0c\u773c\u4e2d\u5df2\u6cdb\u7740\u6cea\u5149\uff01 \u3000\u3000\u909d\u740f\u5fc3\u9053\uff1a\u201c\u6562\u60c5\u90a3\u4e24\u4e2a\u5bb6\u4f19\u8bf4\u7684\u90a3\u4e2a\u5b69\u5b50\u5c31\u662f\u5510\u6653\u6f9c\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u679c\u7136\u4fbf\u542c\u5f97\u51af\u5e7f \u6f6e\u6307\u7740\u5510\u6653\u6f9c\u9053\uff1a\u201c\u90a3\u5b69\u5b50\u5c31\u662f\u4ed6\uff01\u201d\u987f\u4e86\u4e00\u987f\uff0c\u5477\u4e86\u53e3\u8336\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u90a3\u957f\u8eab\u6c49\u5b50\u5c31\u662f \u6211\u540e\u6765\u7684\u5e08\u5085\u5468\u9752\u3002\u4ed6\u542c\u4e86\u5510\u6653\u6f9c\u7684\u8bdd\uff0c\u60e8\u7b11\u9053\uff1a\u2018\u5b69\u5b50\uff0c\u96be\u4e3a\u4f60\u8fd8\u8bb0\u5f97\u6211\uff0c\u6211\u6765\u8fdf\u4e86\uff01\u2019 \u643a\u7740\u5b69\u5b50\u7684\u624b\uff0c\u5728\u4e71\u5c38\u5806\u4e2d\u68c0\u51fa\u5c11\u5987\u7684\u5c38\u9ab8\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5988\u5988\u4e3a\u4e86\u4fdd\u62a4\u4f60\uff0c\u5df2\u7ed9\u8d3c\u4eba \u5bb3\u4e86\uff0c\u53ef\u662f\u90a3\u4e9b\u8d3c\u4eba\u4e5f\u7ed9\u4f2f\u6015\u6740\u6389\u4e86\u3002\u4f60\u8981\u505a\u4e2a\u597d\u5b69\u5b50\uff0c\u5c06\u6765\u518d\u7ed9\u7238\u7238\u62a5\u4ec7\u3002\u2019\u6653\u6f9c\u4f36\u4fd0\u5f97 \u5f88\uff0c\u54ed\u4e86\u4e00\u9635\uff0c\u62b1\u7740\u5468\u5927\u4fa0\u9053\uff1a\u2018\u4f2f\u4f2f\uff0c\u4f60\u6559\u6211\u672c\u4e8b\u3002\u2019\u5468\u5927\u4fa0\u9053\uff1a\u2018\u53ea\u8981\u4f60\u505a\u4e2a\u597d\u5b69 \u5b50\u2026\u2026\u2019\u54fd\u54bd\u7740\u8bf4\u4e0d\u4e0b\u4e86\u3002\u4ed6\u5728\u5730\u4e0a\u7528\u5251\u6316\u4e86\u4e00\u4e2a\u5751\uff0c\u628a\u6653\u6f9c\u7684\u6bcd\u4eb2\u57cb\u4e86\uff0c\u5bf9\u6211\u8bf4\u9053\uff1a\u201c\u5979 \u4eec\u592b\u5987\u662f\u6211\u6700\u597d\u7684\u670b\u53cb\uff0c\u6211\u65e9\u6599\u5230\u6709\u4eca\u65e5\u4e4b\u4e8b\uff0c\u53ef\u662f\u8fd8\u662f\u6765\u8fdf\u4e00\u6b65\u3002\u201d \u3000\u3000\u201c\u90a3\u65f6\u6211\u7684\u4f24\u53e3\u6577\u836f\u4e4b\u540e\uff0c\u867d\u7136\u6b62\u75db\uff0c\u4ecd\u662f\u4e0d\u80fd\u52a8\u5f39\uff0c\u5468\u8001\u5e08\u5c06\u6211\u629b\u4e0a\u9a6c\u80cc\uff0c\u62b1\u4e86\u5b69 \u5b50\uff0c\u7b56\u9a6c\u75be\u9a70\u3002\u7b2c\u4e8c\u65e5\u9ece\u660e\uff0c\u5230\u4e86\u4e00\u95f4\u53e4\u5e99\uff0c\u636e\u5468\u8001\u5e08\u8bf4\uff0c\u5176\u5730\u5df2\u662f\u63a5\u8fd1\u56de\u7586\u8fc7\u5883\u7684\u201c\u56fe\u53e4 \u91cc\u514b\u201d\u4e86\u3002\u5e99\u4e2d\u548c\u5c1a\u662f\u4ed6\u7684\u670b\u53cb\u3002\u6211\u5728\u5e99\u91cc\u9759\u517b\u4e86\u51e0\u5929\uff0c\u4f24\u52bf\u6e10\u6e10\u75ca\u6108\u3002\u6211\u6073\u6c42\u4ed6\u6536\u6211\u505a\u5f92 \u5f1f\uff0c\u4ed6\u60f3\u4e86\u4e00\u665a\u5bf9\u6211\u8bf4\u9053\uff1a\u2018\u77a7\u4f60\u7684\u884c\u4e8b\uff0c\u542c\u4f60\u7684\u62b1\u8d1f\uff0c\u90fd\u662f\u6211\u8f88\u4e2d\u4eba\u3002\u53ea\u662f\u4e00\u6765\u4f60\u6211\u5e74\u7eaa \u76f8\u5dee\u4e0d\u8fdc\uff0c\u4e8c\u6765\u6211\u957f\u5e74\u6d41\u6d6a\uff0c\u53c8\u662f\u671d\u5ef7\u7684\u94a6\u72af\uff0c\u65e0\u6687\u6559\u4f60\u3002\u8fd9\u6837\u5427\uff0c\u6211\u628a\u4e00\u8def\u5251\u6cd5\u548c\u4e00\u79cd\u6697 \u5668\u6559\u4f60\uff0c\u4f60\u6211\u4ecd\u4ee5\u670b\u53cb\u76f8\u79f0\uff0c\u4e0d\u6302\u5e08\u5f92\u540d\u4e49\u3002\u2019\u6211\u575a\u51b3\u4e0d\u80af\uff0c\u6700\u540e\u4e24\u4e0b\u6298\u8877\uff0c\u7b97\u662f\u4ed6\u7684\u8bb0\u540d \u5f1f\u5b50\u3002\u5468\u8001\u5e08\u7528\u4e03\u5929\u5de5\u592b\uff0c\u628a\u8ffd\u98ce\u5251\u6cd5\u548c\u98de\u8292\u6697\u5668\u4f20\u6388\u7ed9\u6211\u3002\u8bf4\u9053\uff1a\u2018\u4f60\u522b\u5c0f\u89d1\u8fd9\u4e24\u95e8\u529f \u592b\uff0c\u8fd9\u662f\u5927\u5c71\u5251\u5ba2\u51cc\u672a\u98ce\u4f20\u4e0b\u6765\u7684\uff01\u8ffd\u98ce\u5251\u6cd5\u8fc5\u6377\u65e0\u8bba\uff0c\u662f\u5929\u5c71\u5251\u6cd5\u4e2d\u653b\u52bf\u6700\u52b2\u7684\u62db\u6570\uff0c\u98de \u8292\u6697\u5668\u662f\u4ece\u51cc\u5927\u4fa0\u6210\u540d\u6697\u5668\u5929\u5c71\u795e\u8292\u4e2d\u53d8\u5316\u6765\u7684\uff0c\u4f46\u98de\u8292\u6bd4\u795e\u8292\u7ec6\u5c0f\u5f97\u591a\uff0c\u5b83\u662f\u7528\u4e94\u91d1\u4e4b\u7cbe \u6240\u70bc\uff0c\u5f62\u5982\u6885\u82b1\u9488\uff0c\u4e13\u4f24\u654c\u4eba\u7a74\u9053\u3001\u8033\u76ee\u3002\u7ec3\u6210\u4e4b\u540e\uff0c\u6c5f\u6e56\u4e0a\u5df2\u7f55\u9047\u5bf9\u624b\uff01\u53ea\u662f\u6211\u5fc5\u987b\u4e25\u8beb \u4f60\u4e0d\u8bb8\u70ab\u9732\uff0c\u4e0d\u7136\u5fc5\u62db\u6740\u8eab\u4e4b\u7978\uff01\u4e0d\u5f97\u6211\u7684\u5141\u8bb8\uff0c\u4e5f\u4e0d\u51c6\u4f20\u7ed9\u4ed6\u4eba\uff0c\u867d\u81f3\u4eb2\u7684\u59bb\u5b50\u513f\u5973\uff0c\u4e5f \u4e0d\u51c6\u4f20\u6388\uff0c\u4f60\u4f9d\u5f97\u4e48\uff1f\u2019\u6211\u5fd9\u8bf4\u4f9d\u5f97\u3002\u5468\u8001\u5e08\u53c8\u9053\uff1a\u2018\u4e0d\u662f\u6211\u631f\u6280\u81ea\u73cd\uff0c\u5176\u4e2d\u53e6\u6709\u9053\u7406\u3002\u4f60 \u77e5\u9053\u6211\u662f\u8c01\uff1f\u6211\u5c31\u662f\u51cc\u672a\u98ce\u7684\u8bb0\u540d\u5f1f\u5b50\u5468\u9752\uff0c\u5982\u4eca\u671d\u5ef7\u7684\u94a6\u72af\uff0c\u4e8c\u5341\u5e74\u524d\u6e05\u5bab\u5927\u5185\u7684\u536b\u58eb\u3002 \u51cc\u672a\u98ce\u7684\u8ffd\u98ce\u5251\u6cd5\uff0c\u4e2d\u539f\u5251\u5ba2\u4f1a\u7684\u53ea\u6211\u4e00\u4eba\uff0c\u4f60\u82e5\u5728\u6c5f\u6e56\u4e0a\u6296\u9732\u51fa\u6765\uff0c\u7ed9\u671d\u5ef7\u9e70\u72ac\u770b\u7834\uff0c\u7acb \u6709\u706d\u95e8\u4e4b\u7978\u3002\u4f60\u6653\u5f97\u4e48\uff1f\u2019\u4e03\u5929\u4e4b\u540e\uff0c\u5251\u5f0f\u6211\u5df2\u5b66\u4f1a\uff0c\u5468\u5927\u4fa0\u53c8\u5bf9\u6211\u8bf4\uff1a\u2018\u4f60\u4eec\u6cb3\u5357\u5730\u65b9\uff0c \u6709\u4e00\u4f4d\u5f53\u4e16\u5947\u4eba\uff0c\u6b66\u529f\u7edd\u4e0d\u5728\u6211\u4e4b\u4e0b\uff0c\u4ed6\u662f\u65e0\u6781\u5251\u7684\u4f20\u4eba\uff0c\u5916\u53f7\u2018\u98ce\u5c18\u533b\u9690\u2019\u7684\u949f\u4e07\u5802\u3002\u4ed6 \u867d\u4e0d\u61c2\u8ffd\u98ce\u5251\u6cd5\uff0c\u4f46\u4ed6\u7684\u65e0\u6781\u5251\u5584\u4e8e\u4ee5\u67d4\u514b\u521a\uff0c\u548c\u8ffd\u98ce\u5251\u76f8\u53cd\u76f8\u6210\u3002\u4f60\u73b0\u5728\u5df2\u7c97\u4f1a\u5251\u5f0f\uff0c\u6211 \u65e0\u6687\u6559\u4f60\uff0c\u4f60\u53ef\u62ff\u6211\u8fd9\u7269\uff0c\u5230\u4f0f\u725b\u5c71\u53bb\u627e\u4ed6\uff0c\u8bf7\u4ed6\u548c\u4f60\u62c6\u62db\u7ec3\u5251\uff0c\u5f7c\u6b64\u90fd\u6709\u76ca\u5904\uff01\u2019\u201d\u8bf4\u81f3 \u6b64\u5904\uff0c\u8eba\u5728\u7095\u4e0a\u7684\u5468\u9752\uff0c\u8eab\u5b50\u5ffd\u52a8\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u949f\u4e07\u5802\u6025\u5fd9\u66ff\u4ed6\u628a\u8109\uff0c\u8bf4\u9053\uff1a\u201c\u5468\u5927\u4fa0\u5185\u529f\u771f\u9ad8\uff0c\u770b\u6765\u4e0d\u4e45\u4fbf\u53ef\u82cf\u9192\u3002\u53ea\u662f\u53d7\u6bd2\u592a\u6df1\uff0c \u89e3\u836f\u529b\u5f31\uff0c\u9192\u4e86\u4e4b\u540e\uff0c\u8fd8\u8981\u7528\u6c14\u529f\u7597\u6cd5\uff0c\u6cbb\u7597\u4e09\u5929\u3002\u201d \u3000\u3000\u51af\u5e7f\u6f6e\u5401\u4e86\u53e3\u6c14\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4e34\u522b\u65f6\uff0c\u5468\u5927\u4fa0\u53c8\u5bf9\u6211\u8bf4\uff1a\u2018\u6211\u548c\u5317\u4e94\u7701\u8c6a\u6770\uff0c\u4e94\u5e74\u4e00 \u4f1a\uff0c\u5341\u5e74\u540e\u4e2d\u79cb\u4e4b\u65e5\uff0c\u662f\u7b2c\u4e8c\u6b21\u4f1a\u671f\uff0c\u5730\u70b9\u5c06\u5728\u4f60\u4eec\u6cb3\u5357\u7701\u7684\u592a\u884c\u5c71\u4e0a\u3002\u949f\u4e07\u5802\u56e0\u907f\u5f3a\u4ec7\uff0c \u6c5f\u6e56\u76db\u4f1a\uff0c\u4f8b\u4e0d\u53c2\u52a0\u3002\u4f60\u53ef\u53eb\u4ed6\u5728\u5341\u5e74\u540e\u7684\u4e2d\u79cb\uff0c\u6709\u5230\u4f60\u5bb6\u6765\uff0c\u4e5f\u8bb8\u5230\u65f6\u6211\u4f1a\u987a\u9053\u6765\u63a2\u671b \u4f60\uff0c\u90a3\u65f6\u54b1\u4eec\u518d\u53d9\u5951\u9614\uff0c\u60f3\u4e0d\u5230\u73b0\u5728\u65e5\u671f\u672b\u5230\uff0c\u4e24\u4eba\u90fd\u5df2\u6765\u4e86\uff01\u201d \u3000\u3000\u949f\u4e07\u5802\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6700\u521d\u9690\u85cf\u5728\u4f0f\u725b\u5c71\uff0c\u4e24\u5e74\u524d\uff0c\u8e2a\u8ff9\u88ab\u5bf9\u5934\u53d1\u73b0\uff0c\u6211\u53ea\u597d\u518d \u627e\u5730\u65b9\u8eb2\u85cf\u3002\u4e0d\u6599\u524d\u51e0\u5929\u542c\u5230\u98ce\u58f0\uff0c\u8bf4\u6211\u90a3\u4e24\u4e2a\u5bf9\u5934\uff0c\u4e5f\u8981\u5230\u90a3\u4e2a\u5730\u65b9\uff0c\u6240\u4f3c\u6211\u8d76\u7740\u5411\u4e1c\u5bb6 \u8bf7\u5047\uff0c\u5047\u8bf4\u8981\u56de\u4e61\u63a2\u4eb2\uff0c\u5176\u5b9e\u662f\u6765\u770b\u4f60\u3002\u201d\u51af\u5e7f\u6f6e\u5fc3\u5ff5\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u4f60\u6709\u8d77\u4e1c\u5bb6\u6765 \u4e86\uff1f\u201d\u949f\u4e07\u5802\u9053\uff1a\u201c\u8fd9\u4e24\u5e74\u6765\u6211\u66ff\u4eba\u6559\u4e66\u3002\u201d\u51af\u5e7f\u6f6e\u9887\u611f\u8be7\u5f02\uff0c\u95ee\u9053\uff1a\u201c\u662f\u6c5f\u6e56\u4e0a\u90a3\u4f4d\u6709\u9762 \u5b50\u7684\u670b\u53cb\uff0c\u5c45\u7136\u8bf7\u5f97\u52a8\u4f60\u8fd9\u4f4d\u98ce\u5c18\u533b\u9690\uff1f\u201d\u949f\u4e07\u5802\u53c8\u7b11\u9053\uff1a\u201c\u6211\u6559\u7684\u662f\u4e00\u4e2a\u5929\u4e0b\u6700\u987d\u52a3\u7684\u5c0f \u5b69\uff0c\u4ed6\u7684\u7236\u4eb2\u548c\u6b66\u6797\u670b\u53cb\u65e0\u534a\u70b9\u6e0a\u6e90\uff0c\u5012\u662f\u548c\u6cb3\u5357\u5b98\u5e9c\u5927\u6709\u5173\u7cfb\uff01\u201d\u51af\u5e7f\u6f6e\u66f4\u662f\u5947\u5f02\uff0c\u6b63\u60f3 \u518d\u95ee\uff0c\u949f\u4e07\u5802\u5df2\u622a\u7740\u53cd\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u6653\u6f9c\u8fd9\u5b69\u5b50\u662f\u5468\u5927\u4fa0\u53eb\u4f60\u6559\u7684\uff1f\u201d \u3000\u3000\u51af\u5e7f\u6f6e\u9053\uff1a\u201c\u6b63\u662f\u3002\u53bb\u5e74\u7aef\u5348\uff0c\u8fd9\u5b69\u5b50\u62ff\u4e86\u5468\u8001\u5e08\u7684\u4fe1\u6765\u3002\u4fe1\u4e0a\u8bf4\u5b69\u5b50\u5df2\u5927\uff0c\u4ed6\u4e0d\u80fd\u5e26 \u4ed6\u5728\u6c5f\u6e56\u6d41\u6d6a\uff0c\u53c8\u4e0d\u60f3\u803d\u6401\u4ed6\u7684\u529f\u592b\uff0c\u6240\u4ee5\u53eb\u4ed6\u6765\u8ddf\u6211\u5b66\u8ffd\u98ce\u5251\u6cd5\u548c\u98de\u8292\u6697\u5668\u3002\u201d \u3000\u3000\u8bf4\u5230\u6b64\u5904\uff0c\u5510\u6653\u6f9c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u5468\u4f2f\u4f2f\u9192\u6765\u4e86\uff01\u201d\u51af\u5e7f\u6f6e\u6025\u5fd9\u51dd\u89c6\uff0c\u53ea\u89c1\u5468\u9752\u8f6c\u4e86 \u4e2a\u8eab\u773c\u76ae\u5fae\u5fae\u5f00\u542f\uff0c\u500f\u5730\u53cc\u77b3\u5c04\u51fa\u51db\u70c8\u5149\u8292\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u51af\u8001\u5f1f\uff0c\u8d39\u4e86\u4f60\u7684\u5fc3\u4e86\uff01\u201d\u51af\u5e7f \u6f6e\u6025\u9053\uff1a\u201c\u5468\u8001\u5e08\uff0c\u4f60\u89c9\u5f97\u600e\u6837\uff1f\u201d\u5468\u9752\u9053\uff1a\u201c\u628a\u6211\u7684\u8349\u56ca\u62ff\u6765\uff01\u201d\u5510\u6653\u6f9c\u5728\u65c1\u9012\u4e0a\u3002\u5468\u9752 \u6253\u5f00\u8349\u56ca\uff0c\u500f\u5730\u5750\u8d77\uff0c\u4f38\u624b\u5411\u6000\u4e2d\u4e00\u63a2\uff0c\u805a\u62e2\u4e09\u6307\uff0c\u5411\u56ca\u4e2d\u4e00\u5f39\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u56ca\u4e2d\u4e24\u4e2a\u8840\u8089 \u6a21\u7cca\u7684\u4eba\u5934\uff0c\u90fd\u5316\u6210\u4e86\u8840\u6c34\uff01\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u591f\u672c\u6709\u8d5a\uff0c\u6211\u6b7b\u4e5f\u503c\u5f97\u4e86\uff01\u201d\u949f\u4e07\u5802\u9053\uff1a\u201c\u4ee5\u4f60 \u7684\u529f\u529b\uff0c\u9759\u5750\u4e09\u5929\uff0c\u8fd8\u53ef\u6cbb\u7597\uff01\u201d\u5468\u9752\u7b11\u9053\uff1a\u201c\u8c01\u8fd8\u8010\u70e6\u9759\u5750\u4e09\u5929\uff0c\u5f85\u6211\u7a0d\u5750\u7247\u523b\uff0c\u4f53\u529b\u60ed \u590d\u5c31\u51fa\u53bb\u3002\u518d\u8fdf\u5c31\u8981\u8fde\u7d2f\u4f60\u4eec\u4e86\uff01\u201d\u51af\u5e7f\u6f6e\u9053\uff1a\u201c\u5e08\u5085\u6709\u96be\uff0c\u5f1f\u5b50\u4e07\u6b7b\u4e0d\u8f9e\u3002\u201d\u5468\u9752\u9053\uff1a \u201c\u6211\u90fd\u4e0d\u662f\u4ed6\u4eec\u5bf9\u624b\uff0c\u4f55\u51b5\u4e8e\u4f60\uff01\u201d\u949f\u4e07\u5802\u9053\uff1a\u201c\u4ec0\u4e48\u654c\u4eba\uff1f\u8fd9\u6837\u5389\u5bb3\uff1f\u201d\u949f\u4e07\u5802\u672c\u4e8b\u548c\u5468 \u9752\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u5fc3\u60f3\uff1a\u5468\u9752\u65e2\u7136\u80fd\u5728\u91cd\u4f24\u4e4b\u540e\uff0c\u9003\u5230\u6b64\u5730\uff0c\u90a3\u4e48\u6211\u6700\u5c11\u4e5f\u53ef\u4ee5\u628a\u4ed6\u4eec\u6321\u4e00\u9635 \u5427\u3002\u5468\u9752\u4e00\u58f0\u4e0d\u54cd\uff0c\u6307\u7740\u80f8\u819b\u7684\u4f24\u75d5\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u89c1\u8fd9\u4e2a\uff1f\u201d\u949f\u4e07\u5802\u6b63\u60f3\u95ee\u8fd9\u662f\u4ec0\u4e48\u6697\u5668\u6240 \u4f24\uff0c\u5468\u9752\u5df2\u4ece\u80cc\u56ca\u91cc\u6478\u51fa\u4e00\u4ef6\u5706\u5ffd\u5ffd\u7684\u4e1c\u897f\u6765\uff01 \u3000\u3000\u949f\u4e07\u5802\u770b\u65f6\uff0c\u53ea\u89c1\u662f\u4e00\u4e2a\u7cbe\u94c1\u6253\u6210\u7684\u5706\u7403\uff0c\u5916\u8868\u4e5f\u6ca1\u4ec0\u4e48\u5947\u5f02\u3002\u5468\u9752\u7528\u529b\u4e00\u65cb\uff0c\u90a3\u5706\u7403 \u500f\u7684\u5f20\u5f00\uff0c\u91cc\u9762\u85cf\u7740\u5341\u51e0\u67c4\u5229\u5200\uff0c\u6bcf\u67c4\u4e0d\u5230\u4e94\u5bf8\uff0c\u6676\u83b9\u900f\u660e\uff0c\u5176\u8584\u5982\u53f6\uff0c\u6881\u7559\u9f50\u9f50\uff0c\u6392\u5217\u5728 \u4e24\u534a\u7403\u5f62\u5185\uff0c\u72b9\u5982\u98de\u9e1f\u7684\u7fc5\u8180\u3002\u5468\u9752\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u5728\u4eac\u4e2d\u4e00\u76f4\u88ab\u8ffd\u81f3\u6b64\uff0c\u5403\u7684\u5c31\u662f\u8fd9\u4e2a\u6697\u5668 \u7684\u4e8f\uff01\u6211\u6740\u4e86\u4e24\u4eba\uff0c\u593a\u5f97\u4e00\u4e2a\uff0c\u4ed6\u4eec\u624d\u4e0d\u6562\u6025\u8ffd\uff01\u201d\u949f\u4e07\u5802\u7ec6\u770b\u6697\u5668\uff0c\u5341\u5206\u7eb3\u7f55\u3002\u5468\u9752\u9053\uff1a \u201c\u8fd9\u4e2a\u6697\u5668\u540d\u53eb\u8840\u6ef4\u5b50\uff01\u6211\u4e5f\u662f\u7b2c\u4e00\u6b21\u9047\u5230\uff0c\u673a\u62ec\u4e00\u5f00\uff0c\u91cc\u9762\u5feb\u5200\u4fbf\u5982\u8f6e\u5b50\u822c\u98de\u8f6c\uff0c\u4e00\u5f20\u5f00 \u6765\uff0c\u628a\u4eba\u5934\u7f69\u5728\u91cc\u9762\uff0c\u5706\u7403\u4fbf\u81ea\u884c\u5408\u62e2\uff0c\u4eba\u5934\u4e5f\u4e0d\u89c1\u4e86\uff01\u91cc\u9762\u7684\u5229\u5200\u90fd\u7528\u6bd2\u836f\u7ec3\u8fc7\uff0c\u5c31\u7b97\u907f \u5f97\u98de\u5934\u6ef4\u8840\u4e4b\u707e\uff0c\u53ea\u8981\u7ed9\u5b83\u4f24\u7740\uff0c\u4e5f\u662f\u6027\u547d\u4e0d\u4fdd\u3002\u8fd9\u6b21\u6211\u88ab\u5341\u51e0\u4e2a\u8840\u6ef4\u5b50\u56f4\u653b\uff0c\u4e00\u65f6\u8eb2\u907f\u4e0d \u53ca\uff0c\u4fbf\u7740\u4e86\u9053\u513f\uff01\u4f60\u4eec\u82e5\u548c\u8840\u6ef4\u5b50\u5355\u72ec\u6597\uff0c\u7528\u6697\u5668\u628a\u5b83\u6253\u843d\uff0c\u6216\u7528\u8f7b\u529f\u907f\u5f00\uff0c\u8c05\u8fd8\u53ef\u4ee5\u3002\u82e5 \u9047\u7740\u8840\u6ef4\u5b50\u56f4\u653b\uff0c\u90a3\u53ef\u662f\u5371\u9669\u4e07\u5206\uff01\u201d \u3000\u3000\u949f\u4e07\u5802\u4e00\u8dc3\u800c\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u80fd\u529b\u654c\uff0c\u90a3\u4e48\u54b1\u4eec\u8d70\uff01\u6211\u548c\u4f60\u5230\u592a\u884c\u5c71\u53bb\uff0c\u6cbf\u9014\u7528\u836f \u4fdd\u4f4f\u4f60\u7684\u4e39\u7530\u4e4b\u6c14\uff0c\u63a5\u8fd1\u592a\u884c\u5c71\u5c31\u4e0d\u6015\u4e86\u3002\u5317\u4e94\u7701\u8c6a\u6770\u8fd9\u51e0\u5929\u6b63\u9646\u7eed\u800c\u6765\uff0c\u5341\u51e0\u4e2a\u8840\u6ef4\u5b50\u54b1 \u4eec\u8fd8\u4e0d\u4f2f\u4ed6\uff01\u201d\u5468\u9752\u7741\u773c\u9053\uff1a\u201c\u4f60\u5c31\u4e0d\u6015\u4f60\u7684\u4ec7\u5bb6\u4e86\uff1f\u201d\u949f\u4e07\u5802\u9053\uff1a\u201c\u8fd9\u65f6\u8fd8\u6015\u8fd9\u4e2a\uff1f\u5e73\u65f6 \u8eb2\u907f\u4ed6\u4eec\uff0c\u662f\u72af\u4e0d\u7740\u548c\u4ed6\u4eec\u62fc\uff0c\u73b0\u5728\u662f\u9003\u547d\u8981\u7d27\uff01\u201d\u5468\u9752\u6447\u4e86\u6447\u5934\uff0c\u949f\u4e07\u5802\u6025\u9053\uff1a\u201c\u4f60\u518d\u4e0d \u8d70\uff0c\u6211\u5c31\u8981\u628a\u4f60\u80cc\u51fa\u53bb\u4e86\uff01\u201d\u5468\u9752\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u6eda\u4e0b\u7095\uff0c\u4f0f\u5730\u4e00\u542c\uff0c\u8bf4\u9053\uff1a\u201c\u8fdc\u5904\u6709\u9a6c\u5636 \u4e4b\u58f0\uff0c\u73b0\u5728\u51fa\u53bb\uff0c\u5fc5\u7136\u649e\u4e0a\uff01\u201d\u949f\u4e07\u5802\u4e00\u53e3\u6c14\u628a\u623f\u4e2d\u706f\u706b\u5439\u7184\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u522b\u52a8\u58f0\u606f\uff0c\u5018 \u82e5\u4ed6\u4eec\u771f\u4e2a\u627e\u5230\u4e0a\u95e8\uff0c\u90a3\u65f6\u624d\u548c\u4ed6\u4eec\u53ae\u6740\uff01\u201d \u3000\u3000\u9ed1\u6697\u4e2d\u5468\u9752\u62bd\u51fa\u4e00\u628a\u5b9d\u5251\uff0c\u987f\u65f6\u5bd2\u5149\u95ea\u95ea\uff0c\u7167\u89c1\u9762\u5bb9\u3002\u949f\u4e07\u5802\u4f4e\u58f0\u9053\uff1a\u201c\u628a\u5b83\u6536\u8d77\u6765\uff01 \u7b49\u8d3c\u4eba\u4e0a\u5230\u95e8\u65f6\uff0c\u518d\u62bd\u5251\u672a\u8fdf\uff01\u201d\u5468\u9752\u63d2\u5251\u5f52\u9798\uff0c\u628a\u5510\u6653\u6f9c\u62c9\u5230\u8eab\u8fb9\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u628a\u5251 \u7ed9\u4f60\uff0c\u8fd9\u662f\u4f60\u7684\u7956\u5e08\u7237\u51cc\u672a\u98ce\u4f20\u4e0b\u6765\u7684\uff0c\u540d\u53eb\u6e38\u9f99\u5251\uff01\u201d\u949f\u4e07\u5802\u609a\u7136\u4e00\u60ca\uff0c\u6e38\u9f99\u5251\u662f\u5929\u5c71\u6d3e \u4e24\u628a\u9547\u5c71\u5b9d\u5251\u4e4b\u4e00\uff0c\u51e0\u5341\u5e74\u524d\uff0c\u6666\u660e\u7985\u5e08\u7684\u53db\u5f92\u695a\u662d\u5357\u66fe\u4ed7\u6b64\u5251\u538b\u670d\u6c5f\u6e56\u3002\u60f3\u4e0d\u5230\u51cc\u672a\u98ce\u7adf \u4f1a\u9001\u7ed9\u5468\u9752\uff0c\u4eca\u53c8\u4f20\u5230\u8fd9\u4e2a\u5b69\u5b50\u624b\u4e0a\u3002\u4e0d\u7981\u66ff\u5510\u6653\u6f9c\u62c5\u5fc3\u3002\u201d\u4ed6\u6b66\u529f\u5fb7\u671b\u4e0d\u7b26\uff0c\u8eab\u85cf\u5b9d\u5251\uff0c \u53cd\u4f1a\u60f9\u7978\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u5468\u9752\u53c8\u62c9\u7740\u949f\u4e07\u5802\u7684\u624b\uff0c\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u54b1\u4eec\u4f1a\u5c11\u79bb\u591a\uff0c\u4eca\u65e5\u4e00\u4f1a\uff0c\u6b64 \u540e\u53ea\u6050\u66f4\u662f\u5e7d\u51a5\u8def\u9694\u3002\u4f60\u7684\u5f3a\u4ec7\u5df2\u4ece\u5173\u5916\u5357\u4e0b\uff0c\u4f60\u73b0\u8eb2\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u4e24\u4eba\u53cb\u8c0a\uff0c\u575a\u5982\u91d1 \u77f3\uff0c\u949f\u4e07\u5802\u773c\u775b\u6f6e\u6e7f\uff0c\u4e5f\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5173\u6ce8\u3002\u6211\u5728\u9648\u7559\u53bf\u4e61\u4e0b\u6559\u4e66\u3002\u201d\u5468\u9752\u5ffd\u9053\uff1a\u201c\u662f \u4e0d\u662f\u59d3\u5e74\u7684\u90a3\u5bb6\uff1f\u201d\u949f\u4e07\u5802\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d\u5468\u9752\u5ffd\u5730\u53eb\u8d77\u6765\u9053\uff1a\u201c\u4f60\u6559\u7684\u597d\u5f92\u5f1f\uff01\u201d\u8fd9\u53e5\u8bdd \u672c\u6765\u5e94\u8be5\u8fd8\u6709\u4e0b\u6587\u7684\uff0c\u4f46\u5c31\u5728\u6b64\u9645\uff0c\u4ed6\u5df2\u6709\u5bdf\u89c9\uff0c\u8fde\u5fd9\u5618\u58f0\u9053\uff1a\u201c\u6765\u4e86\uff01\u6765\u4e86\uff01\u5664\u58f0\uff01\u5664 \u58f0\uff01\u201d\u949f\u4e07\u5802\u83ab\u540d\u5176\u5999\uff0c\u4e0d\u4fbf\u518d\u95ee\uff0c\u53ea\u597d\u548c\u4f17\u4eba\u4f0f\u5728\u5730\u4e0a\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u679c\u7136\u542c\u5f97\u8e44\u58f0\u5f97\u5f97\uff0c \u5df2\u8fd1\u95e8\u524d\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u3000\u3000\u5348\u591c\u4fa6\u9a91\u51fa\uff0c\u8352\u6751\u641c\u5367\u9f99\u3002 \u3000\u3000\u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u56de\u3000\u8840\u6e85\u8352\u6751\u3000\u5341\u5e74\u5b8c\u4f2f\u7ea6\u3000\u6848\u7275\u5927\u5185\u3000\u5348\u591c\u51fa\u5f81\u9a91 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e8c\u56de \u8840\u6e85\u8352\u6751\u3000\u5341\u5e74\u5b8c\u4f2f\u7ea6 \u6848\u7275\u5927\u5185\u3000\u5348\u591c\u51fa\u5f81\u9a91 \u3000\u3000\u9ed1\u6697\u4e2d\u5404\u4eba\u6309\u7740\u5175\u5203\uff0c\u5c4f\u6c14\u51dd\u795e\uff0c\u8e44\u58f0\u5230\u4e86\u95e8\u524d\uff0c\u84e6\u7136\u800c\u6b62\uff0c\u949f\u4e07\u5802\u5fc3\u91cc\u5947\u602a\uff1a\u201c\u5982\u4f55 \u53ea\u662f\u4e00\u4eba\u4e00\u9a91\uff1f\u201d\u5468\u9752\u4e5f\u751a\u8be7\u5f02\uff0c\u6b63\u5f85\u8d77\u8eab\uff0c\u53ea\u542c\u5f97\u5916\u9762\u90a3\u4e2a\u62cd\u95e8\u53eb\u9053\uff1a\u201c\u5e08\u5085\uff01\u5e08\u5085\uff01\u201d \u51af\u5e7f\u6f6e\u5401\u4e86\u53e3\u6c14\uff0c\u6b22\u7136\u8bf4\u9053\uff1a\u201c\u662f\u738b\u9675\u3002\u6653\u6f9c\u4f60\u53bb\u5f00\u95e8\uff0c\u63a5\u4f60\u7684\u5927\u5e08\u54e5\u56de\u6765\uff01\u201d\u5468\u9752\u5ffd\u7136\u5c06 \u51af\u5e7f\u6f6e\u62c9\u4f4f\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u4f60\u90a3\u5728\u4eac\u4e2d\u5e72\u9556\u884c\u751f\u610f\u7684\u5f92\u5f1f\uff1f\u201d\u51af\u5e7f\u6f6e\u5e94\u58f0\u9053\u662f\u3002\u5468\u9752\u9053\uff1a \u201c\u4e0d\u8981\u8bf4\u51fa\u4f60\u66fe\u62dc\u6211\u4e3a\u5e08\uff01\u201d\u51af\u5e7f\u6f6e\u51db\u7136\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u53ef\u7591\u5417\uff1f\u201d\u5468\u9752\u9053\uff1a\u201c\u5c0f\u5fc3 \u4e3a\u4e0a\u3002\u201d \u3000\u3000\u5927\u95e8\u6253\u5f00\uff0c\u706f\u706b\u91cd\u660e\uff0c\u4e00\u4e2a\u4e09\u5341\u5de6\u53f3\u7684\u7cbe\u58ee\u6c49\u5b50\uff0c\u7f13\u7f13\u8d70\u8fdb\uff0c\u4e00\u89c1\u5c4b\u91cc\u8fd9\u4e48\u591a\u4eba\uff0c\u8eac\u8170 \u95ee\u9053\uff1a\u201c\u5e08\u5085\uff0c\u4eca\u5929\u662f\u4ec0\u4e48\u559c\u5e86\u65e5\u5b50\uff1f\u201d\u51af\u5e7f\u6f6e\u9053\uff1a\u201c\u4f60\u6dfb\u4e86\u4e24\u4e2a\u4f84\u5973\uff0c\u4eca\u5929\u662f\u5979\u4eec\u7684\u5468 \u5c81\u3002\u201d\u738b\u9675\u5fd9\u5411\u51af\u82f1\u5947\u9053\u559c\uff0c\u95ee\u9053\uff1a\u201c\u5ac2\u5b50\u548c\u4f84\u5973\u5462\uff1f\u7761\u7740\u4e86\u4e48\uff1f\u201d\u51af\u82f1\u5947\u9053\uff1a\u201c\u5728\u91cc\u9762\uff0c \u7b49\u4f1a\u53eb\u5979\u4eec\u6765\u89c1\u5e08\u54e5\u3002\u201d\u51af\u5e7f\u6f6e\u5f15\u4ed6\u62dc\u89c1\u5ba2\u4eba\uff0c\u4ed6\u542c\u5f97\u98ce\u5c18\u533b\u9690\u949f\u4e07\u5802\u7684\u540d\u5b57\uff0c\u5df2\u5403\u4e86\u4e00 \u60ca\uff0c\u518d\u542c\u5f97\u5468\u9752\u7684\u540d\u5b57\uff0c\u6025\u5fd9\u62dc\u4e86\u4e0b\u53bb\u3002\u5468\u9752\u53cc\u7738\u70af\u70af\uff0c\u9510\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u6cbf\u8def\u53ef\u78b0\u5230\u4ec0\u4e48\u7279 \u522b\u4e4b\u4eba\uff1f\u201d\u738b\u9675\u9053\uff1a\u201c\u5728\u859b\u5e97\u9644\u8fd1\uff0c\u66fe\u89c1\u5341\u4f59\u540d\u6b66\u58eb\uff0c\u8fde\u9a91\u897f\u53bb\uff01\u201d\u859b\u5e97\u79bb\u6c5d\u5dde\u4e0d\u8fc7\u767e\u91cc\uff0c \u90a3\u4e9b\u6b66\u58eb\u82e5\u662f\u4eac\u4e2d\u8ffd\u6765\u7684\u8840\u6ef4\u5b50\uff0c\u8be5\u5728\u738b\u9675\u4e4b\u524d\u6765\u5230\u6c5d\u5dde\u3002\u51af\u5e7f\u6f6e\u5fc3\u4e2d\u4e00\u5bbd\uff0c\u6697\u9053\uff1a\u4ed6\u4eec\u60f3 \u5fc5\u4e0d\u77e5\u9053\u5468\u8001\u5e08\u5728\u6b64\uff0c\u6b64\u9645\u5df2\u7ed5\u8fc7\u6c5d\u5dde\u897f\u53bb\u4e86\u3002\u5468\u9752\u9762\u8272\u7a0d\u8f6c\u7f13\u548c\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u6ca1\u6709\u95ee \u4f60\u4ec0\u4e48\u5417\uff1f\u201d\u738b\u9675\u6447\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d\u5468\u9752\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u909d\u7ec3\u971e\u542c\u5f97\u5e08\u54e5\u58f0\u97f3\uff0c\u62b1\u4e86\u51af\u745b\u51af\u7433\uff0c\u4ece\u5185\u5ba4\u8d70\u51fa\u6765\u3002\u738b\u9675\u4eb2\u4e86\u4e24\u4e2a\u5973\u5a03\uff0c\u6b22\u7136\u8bf4\u9053\uff1a \u201c\u5f1f\u59b9\uff0c\u5927\u559c\u554a\uff01\u4f60\u7684\u559c\u9152\u6211\u8fd8\u6ca1\u559d\uff0c\u73b0\u5728\u5148\u559d\u4f60\u7684\u59dc\u9152\u4e86\uff01\u201d\u909d\u7ec3\u971e\u7b11\u4e86\u4e00\u8299\uff0c\u6ca1\u8bf4\u8bdd\u3002 \u51af\u5e7f\u6f6e\u9053\uff1a\u201c\u4f60\u5728\u4eac\u4e2d\u9556\u884c\u5e72\u5f97\u597d\u597d\u7684\uff0c\u600e\u4e48\u6709\u7a7a\u56de\u6765\u3002\u201d\u7389\u9675\u9053\uff1a\u201c\u9556\u884c\u6d3e\u6211\u5230\u6dee\u9633\u63a5 \u9556\uff0c\u987a\u9053\u56de\u6765\u7ed9\u5e08\u5085\u8bf7\u5b89\u3002\u201d\u909d\u7ec3\u971e\u7b11\u9053\uff1a\u201c\u516c\u516c\uff0c\u5e08\u54e5\u8fdc\u9053\u5f52\u6765\uff0c\u8ba9\u4ed6\u8fdb\u53bb\u6d17\u4e00\u6d17\u811a\uff0c\u5378 \u4e0b\u884c\u56ca\uff0c\u518d\u51fa\u6765\u966a\u4f60\u8bf4\u8bdd\u5427\u3002\u201d\u51af\u5e7f\u6f6e\u4e5f\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u5e74\u7eaa\u6216\u8bb8\u4e0d\u7b97\u5f88\u5927\uff0c\u4eba\u5374\u771f\u662f\u6709 \u70b9\u8001\u7cca\u6d82\u4e86\u3002\u4f60\u966a\u5e08\u54e5\u8fdb\u53bb\uff0c\u745b\u513f\u7433\u513f\u7559\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u5468\u9752\u672c\u5728\u6c89\u601d\uff0c\u89c1\u7740\u4e24\u4e2a\u7c89\u96d5\u7389\u7422\u7684\u5973\u5a03\uff0c\u773c\u775b\u4e00\u4eae\uff0c\u8fc7\u53bb\u4ed4\u7ec6\u7aef\u8be6\uff0c\u6478\u4e86\u6478\u4e24\u4e2a\u5973\u5a03 \u7684\u9aa8\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5973\u5a03\u5b50\u6bd4\u4f60\u884c\uff0c\u662f\u5929\u751f\u4e60\u6b66\u80da\u5b50\uff01\u201d\u949f\u4e07\u5802\u7b11\u9053\uff1a\u201c\u8001\u5468\uff0c\u60f3\u4e0d\u5230 \u4f60\u8fd8\u4f1a\u770b\u76f8\u3002\u201d\u5468\u9752\u7aef\u4e86\u9762\u5bb9\u8bf4\u9053\uff1a\u201c\u661f\u76f8\u4e4b\u5b66\u672c\u5c5e\u65e0\u7a3d\uff0c\u4f46\u9aa8\u683c\u6027\u60c5\uff0c\u5c0f\u65f6\u5df2\u9732\u3002\u6211\u4e45\u5386 \u6c5f\u6e56\uff0c\u53ea\u89c1\u8fc7\u4e09\u4e2a\u9aa8\u683c\u5947\u7279\u7684\u5b69\u5b50\uff0c\u8fd9\u4e24\u4e2a\u5973\u5a03\u5b50\u6027\u60c5\u6211\u5c1a\u672a\u77e5\uff0c\u53e6\u5916\u4e00\u4e2a\uff0c\u5341\u591a\u5e74\u540e\uff0c\u4e0d \u662f\u82f1\u96c4\uff0c\u4fbf\u662f\u6851\u96c4\uff0c\u8001\u949f\u4f60\u53ef\u5f97\u5c0f\u5fc3\u4e86\uff01\u201d\u949f\u4e07\u5802\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u6211\u7684\u5f92\u5f1f\uff1f\u201d \u3000\u3000\u5468\u9752\u9053\uff1a\u201c\u6b63\u662f\u3002\u90a3\u5b69\u5b50\u6211\u89c1\u8fc7\u3002\u53ea\u56e0\u6211\u6709\u4e8b\u5728\u8eab\uff0c\u4e0d\u7136\u6211\u65e9\u628a\u4ed6\u5e26\u8d70\uff01\u201d\u949f\u4e07\u5802\u5947 \u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u4ed6\uff0c\u600e\u4e48\u6211\u4e0d\u77e5\u9053\uff1f\u201d\u5468\u9752\u9053\uff1a\u201c\u4f60\u7684\u5f92\u5f1f\u662f\u4e0d\u662f\u5e74\u9050\u9f84\u7684\u513f\u5b50\uff0c\u540d\u5b57\u53eb\u505a\u7fb9 \u5c27\uff1f\u201d\u949f\u4e07\u5802\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u662f\u6709\u70b9\u602a\uff01\u201d\u51af\u5e7f\u6f6e\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u5e74\u9050\u9f84 \u662f\u6cb3\u5357\u9996\u5bcc\uff0c\u600e\u4e48\u949f\u4e07\u5802\u7518\u5fc3\u4f5c\u4ed6\u897f\u5e2d\u3002\u7ee7\u800c\u4e00\u60f3\uff1a\u82e5\u4e3a\u4e86\u907f\u4ec7\uff0c\u8eb2\u8fdb\u5e74\u5bb6\uff0c\u7b97\u5f97\u662f\u4e2a\u6781\u597d \u7684\u7acb\u8db3\u4e4b\u5730\u3002\u53ea\u662f\u949f\u4e07\u5802\u6b66\u529f\u5982\u6b64\u6df1\u6e5b\uff0c\u5374\u8981\u4e1c\u8eb2\u897f\u8eb2\uff0c\u90a3\u4e48\u4ed6\u7684\u4ec7\u5bb6\uff0c\u53ea\u6015\u662f\u6bd4\u8840\u6ef4\u5b50\u8fd8 \u8981\u5389\u5bb3\u4e86\uff01\u201d \u3000\u3000\u5468\u9752\u9053\uff1a\u201c\u6211\u4e45\u5df2\u95fb\u5f97\u5e74\u7fb9\u5c27\u8fd9\u5b69\u5b50\u7684\u4e00\u4e9b\u602a\u5f02\u884c\u4e3a\uff0c\u6709\u4eba\u8bf4\u4ed6\u662f\u795e\u7ae5\uff0c\u6709\u4eba\u8bf4\u4ed6\u662f\u5929 \u4e0b\u7b2c\u4e00\u987d\u7ae5\u3002\u90a3\u5e74\u6211\u7ecf\u8fc7\u9648\u7559\uff0c\u5c31\u7279\u5730\u5077\u8fdb\u5e74\u5e9c\u53bb\u770b\uff0c\u89c1\u4e00\u4e2a\u4e09\u5bb6\u6751\u5b66\u7a76\uff0c\u6b63\u5728\u9a82\u4ed6\u4e0d\u80af\u8bfb \u4e66\uff0c\u4ed6\u95ea\u7740\u773c\u775b\u53eb\u9053\uff1a\u2018\u5148\u751f\uff0c\u4f60\u518d\u8bfb\u4e00\u904d\u7ed9\u6211\u542c\u3002\u2019\u90a3\u4e2a\u8001\u5b66\u7a76\u9053\uff1a\u2018\u597d\uff0c\u6211\u5c31\u518d\u6559\u4f60\u4e00 \u904d\uff0c\u4eca\u665a\u4f60\u4e0d\u628a\u4e66\u5ff5\u7ff1\uff0c\u5c31\u4e0d\u51c6\u4f60\u7761\u89c9\uff0c\u90a3\u8001\u5e08\u6447\u5934\u6446\u8111\u8bfb\u4e86\u4e00\u904d\uff0c\u5e74\u7fb9\u5c27\u54c8\u54c8\u7b11\u9053\uff1a\u2018\u4f60 \u542c\u6211\u7684\uff01\u2019\u53cc\u624b\u53c9\u8170\uff0c\u5927\u58f0\u628a\u90a3\u6bb5\u7ecf\u4e66\u80cc\u4e86\u51fa\u6765\uff0c\u7adf\u662f\u4e00\u5b57\u4e0d\u5dee\u3002\u90a3\u4e09\u5bb6\u6751\u5b66\u7a76\u5413\u5f97\u5446\u4e86\uff0c \u5e74\u7fb9\u5c27\u5ffd\u7136\u9a82\u9053\uff1a\u2018\u8bfb\u4e66\u6709\u4ec0\u4e48\u96be\uff0c\u5c0f\u7237\u504f\u4e0d\u7231\u8bfb\u4f60\u7684\u4e66\uff0c\u4f60\u6562\u7ba1\u6211\uff01\u2019\u8df3\u5c06\u8d77\u6765\uff0c\u4f38\u51fa\u4e24 \u4e2a\u5c0f\u62f3\u5934\u5c31\u6253\uff0c\u4ed6\u53ea\u662f\u4e2a\u516d\u4e03\u5c81\u7684\u5b69\u5b50\uff0c\u4e24\u818a\u5374\u4f3c\u6709\u767e\u65a4\u6c14\u529b\uff0c\u53ef\u601c\u90a3\u8001\u5b66\u7a76\u7ed9\u4ed6\u4e00\u8fde\u6454\u4e86 \u51e0\u8de4\uff0c\u4e00\u6e9c\u70df\u7684\u8dd1\u51fa\u4e86\u4e66\u623f\uff0c\u6211\u770b\u4ed6\u662f\u518d\u4e5f\u4e0d\u6562\u56de\u6765\u4e86\u3002\u8001\u949f\uff0c\u4f60\u88ab\u4ed6\u6253\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u949f\u4e07\u5802\u9053\uff1a\u201c\u90a3\u5b69\u5b50\u5bf9\u6211\u5012\u662f\u975e\u5e38\u656c\u91cd\uff0c\u53ea\u662f\u6211\u4e5f\u6574\u6574\u78e8\u4e86\u4e00\u5e74\u5de5\u592b\uff0c\u624d\u628a\u8fd9\u4e2a\u9b54\u661f\u6536 \u670d\u4e86\u3002\u201d\u6b63\u60f3\u518d\u8bf4\uff0c\u5ffd\u89c1\u5468\u9752\u9762\u8272\u6709\u5f02\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5468\u9752\u4f0f\u5730\u542c\u58f0\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u8d77\u8eab \u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4f30\u8ba1\u9519\u4e86\uff0c\u90a3\u6279\u8840\u6ef4\u5b50\u5f79\u6709\u7ed5\u8fc7\u6c5d\u5dde\uff0c\u8fd9\u56de\u662f\u771f\u7684\u6765\u4e86\uff01\u201d\u949f\u4e07\u5802\u9053\uff1a\u201c\u90a3\u4e48 \u5feb\u628a\u706f\u706b\u7184\u706d\uff0c\u51c6\u5907\u6697\u5668\u3002\u201d\u5468\u9752\u773c\u73e0\u4e00\u8f6c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u5446\u5728\u8fd9\u5c4b\u5b50\u91cc\u4e86\uff0c\u654c\u9a91\u4ece\u5357\u9762 \u6765\uff0c\u54b1\u4eec\u4ece\u5317\u9762\u95ef\u51fa\u53bb\uff01\u201d\u949f\u4e07\u5802\u6447\u5934\u9053\uff1a\u201c\u592a\u8fc7\u5192\u9669\uff0c\u4f60\u7684\u6bd2\u4f24\u867d\u7136\u6682\u89e3\uff0c\u8eab\u4f53\u5c1a\u672a\u590d \u5143\uff01\u201d\u5468\u9752\u5ffd\u9053\uff1a\u201c\u5728\u5c4b\u5b50\u91cc\u6050\u6015\u6709\u5371\u9669\uff01\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u95ef\u51fa\u5927\u95e8\uff0c\u949f\u4e07\u5802\u51af\u5e7f\u6f6e\u5168\u90fd\u6115 \u7136\uff0c\u731c\u4e0d\u900f\u4ed6\u4e3a\u4ec0\u4e48\u521a\u624d\u80af\u7559\u5728\u5c4b\u91cc\uff0c\u73b0\u5728\u5374\u53c8\u6025\u7740\u5916\u95ef\uff01 \u3000\u3000\u5c06\u8fd1\u4e2d\u79cb\uff0c\u6708\u5149\u5982\u7ec3\uff0c\u949f\u4e07\u5802\u98de\u8eab\u9053\u51fa\uff0c\u731b\u89c1\u5927\u95e8\u524d\u7684\u628a\u5f0f\u573a\u4e0a\uff0c\u4e00\u6392\u7ec3\u6b66\u7528\u7684\u77f3\u58a9\u540e \u9762\uff0c\u84e6\u7136\u73b0\u51fa\u4e00\u4eba\uff0c\u9e70\u9f3b\u72ee\u53e3\uff0c\u76f8\u8c8c\u72f0\u72de\uff0c\u602a\u5578\u4e00\u58f0\uff0c\u60ca\u5fc3\u52a8\u9b44\u3002\u5468\u9752\u53cc\u638c\u4e00\u9519\uff0c\u559d\u9053\uff1a \u201c\u706b\u4e91\u5cd2\u4e3b\uff0c\u4f60\u7adf\u4e5f\u7518\u5fc3\u505a\u80e1\u864f\u5974\u624d\uff0c\u53ef\u601c\u6d77\u4e91\u957f\u8001\u4e00\u4e16\u82f1\u540d\uff0c\u88ab\u4f60\u8fd9\u53db\u5f92\u8fb1\u5c3d\uff01\u201d\u706b\u4e91\u5cd2 \u4e3b\u539f\u662f\u6d77\u5357\u5c9b\u4e94\u6307\u5c71\u4e00\u4e2a\u9ece\u65cf\u914b\u957f\uff0c\u4e43\u5e08\u6d77\u4e91\u548c\u5c1a\u662f\u5a01\u9707\u5357\u7586\u7684\u5251\u5e08\uff0c\u706b\u4e91\u5cd2\u4e3b\u9f99\u6728\u516c\u5c3d\u699c \u6240\u4f20\uff0c\u53ea\u662f\u4e8c\u5341\u5e74\u6765\u5b64\u60ac\u6d77\u5916\uff0c\u672a\u5c65\u4e2d\u571f\uff0c\u6240\u4ee5\u4e2d\u539f\u5251\u5ba2\u77e5\u8005\u751a\u5c11\u3002\u5176\u5b9e\u4ed6\u4eec\u5e08\u5f92\u6240\u7ec3\u7684\u6b66 \u529f\uff0c\u7edd\u4e0d\u5728\u4e2d\u539f\u5251\u5ba2\u4e4b\u4e0b\u3002\u5468\u9752\u5341\u4f59\u5e74\u524d\uff0c\u6e21\u6d77\u6df1\u5165\u63a0\u5d16\uff0c\u66fe\u4e0a\u4e94\u6307\u5c71\u89c1\u8fc7\u9f99\u6728\u516c\u4e00\u9762\uff0c\u60f3 \u4e0d\u5230\u4ed6\u7adf\u88ab\u6e05\u5ef7\u7f51\u7f57\u4e86\u53bb\uff0c\u91cd\u9022\u5df2\u662f\u654c\u4eba\u3002 \u3000\u3000\u706b\u4e91\u5cd2\u4e3b\u9f99\u6728\u516c\u78d4\u78d4\u602a\u7b11\uff0c\u5468\u9752\u8eab\u5f62\u4e00\u95ea\uff0c\u4e00\u70b9\u5bd2\u661f\u8fce\u9762\u88ad\u6765\uff0c\u949f\u4e07\u5802\u62a2\u524d\u4e00\u6b65\uff0c\u6325\u5251 \u906e\u62e6\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u4e00\u652f\u94a2\u9556\u6389\u843d\u5730\u4e0a\uff0c\u573a\u8fb9\u7684\u4e3b\u69d0\u6811\u4e0a\uff0c\u5ffd\u53c8\u7ff1\u5982\u98de\u9e1f\u7684\u843d\u4e0b\u4e00\u4eba\uff0c\u5927\u58f0\u53eb \u9053\uff1a\u201c\u5468\u9752\uff0c\u4f60\u4e5f\u53d7\u56fd\u6069\uff0c\u968f\u6211\u56de\u53bb\u5427\uff01\u201d\u8fd9\u4eba\u53d1\u7ea2\u5982\u706b\uff0c\u5468\u9752\u4e00\u89c1\uff0c\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a \u201c\u4ed7\u6b79\u6bd2\u6697\u5668\uff0c\u6697\u7b97\u4e8e\u4eba\uff0c\u7b97\u90a3\u95e8\u6c49\u5b50\uff0c\u597d\uff0c\u8fd8\u4f60\u6697\u5668\uff01\u201d\u53cc\u638c\u4e00\u65cb\u4e00\u626c\uff0c\u4e00\u4e2a\u94c1\u7403\u547c\u547c\u98de \u53bb\uff01 \u3000\u3000\u8fd9\u4eba\u540d\u53eb\u96f7\u6d77\u97f3\uff0c\u4e43\u662f\u56db\u7687\u5b50\u5141\u796f\uff08\u6309\u5373\u540e\u6765\u7684\u96cd\u6b63\u7687\u5e1d\uff09\u95e8\u4e0b\u7684\u5f02\u58eb\u3002\u5eb7\u7199\u5b50\u5973\u751a \u591a\uff0c\u6709\u5341\u516d\u4e2a\u7687\u5b50\u4e03\u4e2a\u516c\u4e3b\uff0c\u6700\u5f97\u4ed6\u5ba0\u7231\u7684\u662f\u5341\u56db\u7687\u5b50\u3002\u56db\u7687\u5b50\u4eba\u6700\u7cbe\u660e\uff0c\u5374\u6700\u4e0d\u5f97\u7236\u7687\u6b22 \u5fc3\u3002\u539f\u6765\u5eb7\u7199\u6709\u4e00\u65e5\u5c06\u4e24\u7b3c\u897f\u85cf\u767d\u9f20\uff0c\u5206\u8d50\u56db\u7687\u5b50\u548c\u5341\u56db\u7687\u5b50\uff0c\u8fc7\u4e86\u5341\u5929\uff0c\u67e5\u95ee\u8d77\u6765\uff0c\u5341\u56db \u7687\u5b50\u9053\uff1a\u201c\u90a3\u4e9b\u767d\u9f20\u5173\u5728\u7b3c\u4e2d\uff0c\u602a\u53ef\u601c\u7684\uff0c\u81e3\u513f\u5192\u6627\uff0c\u628a\u5b83\u4eec\u653e\u4e86\uff0c\u671b\u7236\u7687\u6055\u7f6a\uff01\u201d\u56db\u7687\u5b50 \u5374\u5c06\u767d\u9f20\u5206\u6210\u4e24\u961f\uff0c\u8bad\u7ec3\u5b83\u4eec\u53ae\u6740\uff0c\u5341\u5929\u672a\u5230\uff0c\u5df2\u662f\u4f24\u4ea1\u6b86\u5c3d\u3002\u89c1\u7236\u7687\u95ee\u8d77\uff0c\u5f97\u610f\u6d0b\u6d0b\u7684\u8bf4 \u4e86\u3002\u5eb7\u7199\u4e00\u751f\u620e\u9a6c\uff0c\u6b66\u529f\u6781\u76db\uff0c\u5230\u4e86\u665a\u5e74\uff0c\u9887\u601d\u6cbd\u540d\u9493\u8a89\uff0c\u5830\u6b66\u4fee\u6587\uff0c\u4f8b\u5982\u8457\u540d\u7684\u201c\u5eb7\u7199\u5b57 \u5178\u201d\uff0c\u5c31\u662f\u90a3\u65f6\u4ed6\u53eb\u81e3\u4e0b\u7f16\u7684\u3002\u542c\u4e86\u56db\u7687\u5b50\u7684\u8bdd\uff0c\u5fc3\u60f3\uff1a\u201c\u6b64\u513f\u82e5\u7ee7\u6211\u4f4d\uff0c\u5fc5\u662f\u66b4\u541b\u3002\u201d\u81ea \u6b64\u5c31\u4e0d\u559c\u6b22\u4ed6\u4e86\u3002\u6e05\u5ba4\u7687\u4f4d\u7ee7\u627f\uff0c\u4e0d\u4f9d\u957f\u5e7c\u6b21\u5e8f\uff0c\u7531\u7687\u5e1d\u7559\u4e0b\u9057\u8bcf\uff0c\u6307\u5b9a\u4e00\u4e2a\uff0c\u653e\u5728\u6b63\u5927\u5149 \u660e\u6bbf\u7684\u6b63\u7cb1\uff0c\u6b7b\u540e\u624d\u7531\u987e\u800c\u5927\u81e3\u4e0e\u540c\u7687\u5ba4\u5f00\u62c6\uff0c\u662f\u4ee5\u7687\u5b50\u4e4b\u95f4\uff0c\u4e89\u593a\u7ee7\u627f\u751a\u70c8\uff0c\u56db\u7687\u5b50\u77e5\u9053 \u7236\u7687\u4e0d\u559c\u6b22\u81ea\u5df1\uff0c\u9634\u8c0b\u593a\u4f4d\uff0c\u66f4\u662f\u52a0\u7d27\uff0c\u4e00\u9762\u52fe\u7ed3\u56fd\u8205\u79d1\u9686\u591a\uff0c\u4e00\u9762\u517b\u80b2\u6b7b\u58eb\u3002\u8840\u6ef4\u5b50\u662f\u897f \u85cf\u4e00\u4e2a\u7ea2\u6559\u5587\u561b\u6240\u521b\uff0c\u8fd9\u5587\u561b\u4e3a\u56db\u7687\u5b50\u6240\u7528\uff0c\u8840\u6ef4\u5b50\u4e5f\u4fbf\u4f20\u7ed9\u4e86\u56db\u7687\u5b50\u624b\u4e0b\u7684\u6b66\u58eb\u3002\u96f7\u6d77\u97f3 \u4e43\u5141\u796f\u624b\u4e0b\u201c\u56db\u9738\u201d\u4e4b\u4e00\uff0c\u9f99\u6728\u516c\u5374\u662f\u6700\u8fd1\u624d\u793c\u8058\u6765\u7684\u3002\u5468\u9752\u8fd9\u6b21\u6240\u4e2d\u7684\u8840\u6ef4\u5b50\uff0c\u6b63\u662f\u96f7\u6d77 \u97f3\u6240\u653e\u3002 \u3000\u3000\u6b63\u6240\u8c13\u4ec7\u4eba\u89c1\u9762\uff0c\u5206\u5916\u773c\u7ea2\uff0c\u5468\u9752\u4e00\u89c1\u96f7\u6d77\u97f3\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u706b\u8d77\uff0c\u5c06\u593a\u81ea\u4ed6\u624b\u4e2d\u7684\u8840\u6ef4 \u5b50\uff0c\u7acb\u5373\u53cd\u5c04\u56de\u53bb\u3002 \u3000\u3000\u96f7\u6d77\u97f3\u4e00\u542c\u5578\u58f0\uff0c\u77e5\u9053\u52b2\u529b\u5947\u5927\uff0c\u4e0d\u6562\u63a5\u56de\u3002\u9f99\u6728\u516c\u98de\u8eab\u8dc3\u8d77\uff0c\u9f99\u5934\u62d0\u6756\u8fce\u7740\u5706\u7403\u4e00 \u70b9\uff0c\u534a\u7a7a\u4e2d\u5f53\u7684\u4e00\u58f0\uff0c\u8840\u6ef4\u5b50\u659c\u98de\u51fa\u53bb\u3002\u96f7\u6d77\u97f3\u9634\u607b\u607b\u7684\u7b11\u9053\uff1a\u201c\u5468\u9752\uff0c\u4f60\u4e5f\u662f\u4e2a\u6c5f\u6e56\u4e0a\u7684 \u5927\u884c\u5bb6\u4e86\uff0c\u4f60\u53d7\u4e86\u8840\u6ef4\u5b50\u4e4b\u4f24\uff0c\u7eb5\u8bb8\u6682\u65f6\u4fdd\u4f4f\u771f\u6c14\uff0c\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u4e5f\u5fc5\u6bd2\u53d1\u8eab\u4ea1\uff0c\u4f60\u548c \u6211\u786c\u62fc\u505a\u5565\uff1f\u4e0d\u5982\u968f\u6211\u56de\u4eac\uff0c\u6211\u53ef\u4ee5\u7ed9\u4f60\u89e3\u836f\uff01\u201d\u5468\u9752\u65a5\u9053\uff1a\u201c\u6211\u82e5\u8981\u91cd\u8fd4\u5bab\u4e2d\uff0c\u4e09\u5341\u5e74 \u524d\uff0c\u4e5f\u4e0d\u53cd\u51fa\u6765\u4e86\u3002\u4f60\u4ee5\u4e3a\u7ed9\u7687\u5e1d\u5356\u547d\uff0c\u4fbf\u53ef\u53d6\u5f97\u8363\u534e\u5bcc\u8d35\u4e48\uff1f\u6211\u662f\u8fc7\u6765\u4eba\uff0c\u6bd4\u4f60\u6e05\u695a\u5f97 \u591a\uff0c\u6211\u529d\u4f60\u65e9\u653e\u5c60\u5200\uff0c\u4e3a\u5b50\u5b59\u7559\u70b9\u540e\u798f\u3002\u201d\u4ed6\u4ee5\u4e3a\u96f7\u6d77\u97f3\u4e43\u662f\u5927\u5185\u536b\u58eb\uff0c\u6240\u4ee5\u62ff\u201c\u8fc7\u6765\u4eba\u201d \u8eab\u4efd\u529d\u4ed6\uff0c\u5374\u4e0d\u77e5\u96f7\u6d77\u97f3\u4e00\u5fc3\u60f3\u4fdd\u56db\u7687\u5b50\u767b\u57fa\uff0c\u8fd9\u756a\u8bdd\u5982\u4f55\u542c\u5f97\u8fdb\u53bb\uff1f\u4e0d\u5f85\u5468\u9752\u8bf4\u5b8c\uff0c\u4ed6\u5df2 \u4e00\u4e2a\u7bad\u6b65\uff0c\u7a9c\u5230\u9762\u524d\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u5fc5\u5e9f\u8bdd\uff0c\u4f60\u65e2\u4e0d\u80af\u56de\u4eac\uff0c\u8d81\u65e9\u9886\u6b7b\uff01\u201d\u4e00\u7eb5\u8eab\uff0c\u4e00\u62ac\u81c2\uff0c \u624b\u4e2d\u7684\u9b3c\u5934\u5200\u6402\u5934\u4fbf\u65ab\u3002 \u3000\u3000\u5468\u9752\u4e00\u632b\u8eab\uff0c\u95ea\u5f00\u5200\u52bf\uff0c\u9f99\u6728\u516c\u7684\u94c1\u62d0\uff0c\u547c\u7684\u6253\u5230\uff01\u5468\u9752\u5927\u559d\u4e00\u58f0\uff0c\u53f3\u8db3\u4e00\u626b\uff0c\u8d81\u7740\u524d \u503e\u4e4b\u52bf\uff0c\u907f\u6756\u8fdb\u62db\uff0c\u5de6\u638c\u4e00\u62db\u201c\u529b\u5288\u534e\u5c71\u201d\uff0c\u8fce\u9762\u5288\u53bb\uff0c\u5468\u9752\u4e09\u5341\u5e74\u5185\u5bb6\u529f\u529b\uff0c\u975e\u6bd4\u5bfb\u5e38\uff0c \u8fd9\u4e00\u638c\u82e5\u7ed9\u5288\u5b9e\uff0c\u9f99\u6728\u516c\u7684\u80f3\u818a\u975e\u6298\u65ad\u4e0d\u53ef\uff01\u4f46\u9f99\u6728\u516c\u62db\u6570\u4e5f\u7740\u5b9e\u7cbe\u5947\uff0c\u8eab\u5f62\u9aa4\u8f6c\u4e2d\uff0c\u632f\u81c2 \u659c\u80a9\uff0c\u94c1\u62d0\u75be\u70b9\u5468\u9752\u7684\u201c\u5929\u6c60\u7a74\u201d\u3002\u8fd9\u4e00\u62db\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u5468\u9752\u89c1\u4ed6\u679c\u662f\u9ad8\u624b\uff0c\u6697\u9053\u53ef \u60dc\uff0c\u4e0a\u8eab\u9661\u7f29\u534a\u5c3a\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u628a\u540e\u4fa7\u653b\u6765\u7684\u96f7\u6d77\u97f3\u624b\u8155\u62ff\u7740\uff0c\u559d\u58f0\u201c\u53bb\u4f60\u7684\uff01\u201d\u7528\u529b\u4e00 \u9001\uff0c\u96f7\u6d77\u97f3\u98de\u8dcc\u51fa\u53bb\uff0c\u5c31\u5728\u8fd9\u970e\u90a3\u95f4\uff0c\u9f99\u6728\u516c\u7684\u94c1\u62d0\u5288\u98ce\u4e4b\u58f0\u53c8\u5230\uff0c\u5468\u9752\u8d76\u5fd9\u659c\u8eab\uff0c\u90a3\u62d0\u6756 \u70b9\u5230\u80f8\u524d\uff0c\u5ffd\u7136\u5411\u5916\u4e00\u6b6a\uff0c\u7d27\u63a5\u7740\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u84ec\u98de\uff0c\u539f\u6765\u662f\u949f\u4e07\u5802\u7684\u65e0\u6781\u5251\u5df2\u628a\u94c1 \u62d0\u6321\u4f4f\uff01 \u3000\u3000\u5468\u9752\u8d81\u52bf\u8df3\u51fa\uff0c\u96f7\u6d77\u97f3\u4e5f\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u9b3c\u5934\u5200\u6a2a\u80f8\u5f85\u654c\uff0c\u5374\u81ea\u4e0d\u6562\u8fdb\u62db\uff0c\u5468\u9752\u4e2d\u4e86\u8840\u6ef4 \u5b50\u5185\u85cf\u7684\u6bd2\u5200\uff0c\u96f7\u6d77\u97f3\u6599\u4ed6\u4e0d\u6b7b\u4ea6\u4f24\uff0c\u89c1\u4ed6\u4ecd\u662f\u5982\u6b64\u5a01\u731b\uff0c\u5413\u5f97\u5446\u4e86\u3002\u5468\u9752\u6b63\u5f85\u8d76\u524d\u8fdb\u62db\uff0c \u56db\u5904\u9a6c\u5636\u4e4b\u58f0\u8d8a\u6765\u8d8a\u8fd1\uff0c\u51af\u5bb6\u7684\u4eba\uff0c\u4e5f\u5df2\u8ffd\u4e86\u51fa\u6765\uff0c\u5468\u9752\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6697\u53eb\u201c\u4e0d\u597d\u3002\u201d\u8bf4\u65f6 \u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u8840\u6ef4\u5b50\u56db\u5904\u6d8c\u73b0\uff0c\u628a\u51af\u5bb6\u7684\u4eba\u56f0\u5728\u6838\u5fc3\u3002\u51af\u82f1\u5947\u62b1\u7740\u51af\u7433\uff0c\u6b63\u5f85\u968f\u7740\u7236\u4eb2 \u5916\u95ef\uff0c\u5934\u9876\u7a81\u7136\u602a\u58f0\u5927\u4f5c\uff0c\u51e0\u4ef6\u9ed1\u5ffd\u5ffd\u7684\u4e1c\u897f\u5f53\u5934\u7f69\u4e0b\uff0c\u4ed6\u6025\u5fd9\u628a\u51af\u7433\u631f\u7d27\uff0c\u7f29\u8eab\u95ea\u8eb2\uff0c\u8033 \u9645\u542c\u5f97\u7236\u4eb2\u5927\u53eb\u4e00\u58f0\uff0c\u9888\u9879\u4e00\u51c9\uff0c\u4e00\u4e2a\u8840\u6ef4\u5b50\u5df2\u5408\u7f69\u4e86\u3002\u949f\u4e07\u5802\u865a\u6643\u4e00\u5251\uff0c\u6487\u5f00\u4e86\u9f99\u6728\u516c\uff0c \u4e00\u63a0\u6570\u4e08\uff0c\u4e00\u67c4\u98de\u5200\uff0c\u628a\u6697\u88ad\u909d\u6d9f\u7684\u8840\u6ef4\u5b50\u649e\u843d\uff0c\u6bd4\u51af\u5e7f\u6f6e\u8d76\u5148\u534a\u6b65\uff0c\u62a2\u7740\u5c06\u51af\u7433\u63a5\u5230\u624b \u4e2d\uff0c\u4f46\u53ef\u601c\u51af\u82f1\u5947\u5df2\u662f\u8eab\u9996\u5f02\u5904\u3002 \u3000\u3000\u9f99\u6728\u516c\u548c\u96f7\u6d77\u97f3\u8fd9\u65f6\u5374\u7f20\u4e0a\u4e86\u5468\u9752\uff0c\u6ee1\u7a7a\u602a\u5578\u4e4b\u58f0\uff0c\u545c\u545c\u4e71\u54cd\uff0c\u5468\u9752\u5927\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4e0d \u5fc5\u987e\u6211\uff0c\u8d76\u5feb\u9003\u751f\uff01\u201d\u949f\u4e07\u5802\u5de6\u624b\u631f\u7740\u51af\u7433\uff0c\u53f3\u624b\u4ed7\u5251\u5f00\u8def\uff0c\u5429\u5490\u909d\u740f\u9053\uff1a\u201c\u7d27\u968f\u7740\u6211\uff0c\u4e0d \u8981\u4e71\u8dd1\uff01\u201d\u909d\u740f\u6027\u60c5\u6734\u539a\uff0c\u949f\u4e07\u5802\u4e0e\u4ed6\u5341\u5206\u6295\u7f18\uff0c\u77e5\u4ed6\u6b66\u529f\u7a0d\u5dee\uff0c\u6240\u4ee5\u4e00\u529b\u4fdd\u4ed6\u3002\u909d\u7ec3\u971e\u62b1 \u7740\u51af\u745b\uff0c\u89c1\u4e08\u592b\u88ab\u6740\uff0c\u5fc3\u6467\u809d\u88c2\uff0c\u54ed\u4e0d\u51fa\u58f0\u3002\u738b\u9675\u4e0e\u5510\u6653\u6f9c\uff0c\u4e00\u4e2a\u4f7f\u516d\u5408\u5927\u67aa\uff0c\u4e00\u4e2a\u4ed7\u6e38\u9f99 \u5b9d\u5251\uff0c\u4e24\u65c1\u4fdd\u62a4\u7740\u5979\u4eec\u6bcd\u5973\u3002\u4e00\u4e2a\u8840\u6ef4\u5b50\u8fce\u9762\u98de\u6765\uff0c\u5510\u6653\u6f9c\u8dc3\u8d77\u4e00\u5288\uff0c\u4e00\u5251\u5c06\u8840\u6ef4\u5b50\u5288\u4e3a\u4e24 \u534a\u3002\u8981\u77e5\u6e38\u9f99\u5251\u950b\u5229\u5f02\u5e38\uff0c\u90a3\u65e5\u5468\u9752\u88ab\u5341\u51e0\u4e2a\u8840\u6ef4\u5b50\u811a\u5c3e\u7a77\u8ffd\uff0c\u6570\u5ea6\u56f4\u653b\uff0c\u5c31\u662f\u9760\u7740\u8fd9\u628a\u5b9d \u5251\u9003\u751f\u3002\u800c\u4eca\u51af\u5bb6\u4eba\u591a\uff0c\u8840\u6ef4\u5b50\u4e0d\u80fd\u4e13\u88ad\u4e00\u4eba\uff0c\u662f\u4ee5\u5510\u6653\u6f9c\u6b66\u529f\u867d\u8fdc\u8f83\u5468\u9752\u4e3a\u4f4e\uff0c\u5374\u4e5f\u80fd\u591f \u4fdd\u62a4\u909d\u7ec3\u971e\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u51e0\u4e2a\u5931\u4e86\u8840\u6ef4\u5b50\u7684\u6b66\u58eb\uff0c\u4e00\u89c1\u5510\u6653\u6f9c\u4eae\u51fa\u6e38\u9f99\u5b9d\u5251\uff0c\u7eb7\u7eb7\u547c\u559d\uff0c\u62a2\u6765\u62e6\u622a\u3002\u5510\u6653\u6f9c\u5251\u8bc0 \u4e00\u9886\uff0c\u5251\u5149\u95ea\u52a8\uff0c\u628a\u4e00\u540d\u6b66\u58eb\u523a\u4e86\u4e2a\u900f\u660e\u7a9f\u968d\uff0c\u8033\u8fb9\u542c\u5f97\u738b\u9675\u8be7\u5f02\u53eb\u58f0\u3002\u4ed6\u4ea6\u5df2\u65e0\u6687\u56de\u987e\uff0c \u6e38\u9f99\u5251\u8fce\u73af\u4f5c\u52bf\uff0c\u5f80\u524d\u9012\u62db\u3002\u90a3\u6599\u540e\u6765\u7684\u4e24\u4eba\u7adf\u662f\u9ad8\u624b\uff0c\u4e00\u4e2a\u624b\u4f7f\u4e03\u8282\u97ad\uff0c\u5bf9\u6e38\u9f99\u5b9d\u5251\uff0c\u7adf \u7136\u4e0d\u60e7\uff0c\u4e03\u8282\u97ad\u54d7\u5577\u5577\u6492\u5f00\uff0c\u76d8\u65cb\u7f20\u6253\uff0c\u5168\u662f\u8fdb\u624b\u62db\u6570\u3002\u53e6\u4e00\u4e2a\u4f7f\u6df7\u5143\u724c\uff0c\u5288\u5d29\u7838\u538b\uff0c\u4e5f\u662f \u52bf\u96c4\u62db\u6377\uff0c\u864e\u864e\u98ce\u751f\uff01\u5510\u6653\u6f9c\u521d\u6b21\u51fa\u9053\uff0c\u4fbf\u9047\u5f3a\u654c\uff0c\u624b\u5fd9\u811a\u4e71\uff01 \u3000\u3000\u738b\u9675\u62d6\u7740\u909d\u7ec3\u971e\uff0c\u81ea\u987e\u4e0d\u6687\u3002\u51af\u5e7f\u6f6e\u5927\u559d\u4e00\u58f0\uff0c\u8ffd\u98ce\u5251\u6cd5\u970d\u970d\u5c55\u5f00\uff0c\u628a\u9762\u524d\u4e24\u540d\u654c\u4eba\u523a \u4f24\uff0c\u6740\u51fa\u8840\u8def\uff0c\u6b63\u60f3\u6551\u5ab3\u5987\u3001\u7231\u5f92\uff0c\u731b\u89c1\u4e24\u6761\u4eba\u5f71\uff0c\u4f3c\u65ad\u7ebf\u98ce\u7b5d\u822c\u4e00\u4e2a\u968f\u7740\u4e00\u4e2a\uff0c\u51cc\u7a7a\u98de \u5760\u3002\u51af\u5e7f\u6f6e\u628a\u5934\u4e00\u4f4e\uff0c\u5468\u9752\u4ece\u4ed6\u5934\u9876\u98de\u8fc7\uff0c\u4ed6\u521a\u4e00\u8f6c\u8eab\uff0c\u540e\u5934\u90a3\u4e2a\u5df2\u4e00\u6756\u5f53\u7a7a\u6233\u4e0b\uff0c\u4ed6\u957f\u5251 \u6a2a\u6321\uff0c\u7adf\u7ed9\u9707\u9000\u51e0\u6b65\u3002\u8fd9\u4eba\u6b63\u662f\u706b\u4e91\u5cd2\u4e3b\u9f99\u6728\u516c\uff01 \u3000\u3000\u5468\u9752\u8fd9\u4e00\u8d76\u5230\u6070\u662f\u65f6\u5019\uff0c\u4f7f\u4e03\u8282\u97ad\u7684\u6b63\u5728\u4e00\u97ad\u5411\u5510\u6653\u6f9c\u53f3\u8170\u731b\u626b\uff0c\u5510\u6653\u6f9c\u7684\u5251\u88ab\u94c1\u724c\u538b \u4f4f\uff0c\u62bd\u4e0d\u51fa\u6765\uff0c\u4e07\u96be\u9003\u907f\uff0c\u4f7f\u4e03\u8282\u97ad\u7684\u6b63\u81ea\u5f97\u610f\uff0c\u4e0d\u6599\u5468\u9752\u7a81\u5982\u98de\u5c06\u519b\u4ece\u5929\u800c\u964d\uff0c\u53f3\u638c\u538b \u97ad\uff0c\u500f\u4e00\u8f6c\u8eab\uff0c\u4fbf\u8fbe\u4e2d\u5bab\uff0c\u6b3a\u8eab\u76f4\u8fdb\u3002\u5468\u9752\u8eab\u6cd5\u5947\u5feb\uff0c\u5bf9\u624b\u62bd\u97ad\u62db\u67b6\uff0c\u52bf\u5df2\u4e0d\u53ca\uff0c\u5468\u9752\u4e94\u6307 \u5982\u94a9\uff0c\u4e00\u6293\u6293\u7740\u4ed6\u7684\u80a9\u5934\uff0c\u5f80\u5916\u4e00\u7529\uff0c\u90a3\u4eba\u60e8\u53eb\u4e00\u58f0\uff0c\u7435\u7436\u9aa8\u5168\u90fd\u788e\u4e86\uff0c\u4f7f\u6df7\u5143\u724c\u7684\u7a81\u7136\u4e00 \u9707\uff0c\u624b\u52b2\u4e00\u677e\uff0c\u5510\u6653\u6f9c\u7684\u6e38\u9f99\u5b9d\u5251\u62bd\u4e86\u51fa\u6765\uff0c\u9752\u950b\u4e00\u8f6c\uff0c\u201c\u76d8\u8098\u5241\u624e\u201d\uff0c\u5411\u654c\u4eba\u80f8\u524d\u6025\u70b9\uff0c \u90a3\u4e2a\u4f7f\u94c1\u724c\u7684\u6b66\u58eb\u4e00\u62db\u201c\u6a2a\u67b6\u91d1\u6881\u201d\uff0c\u6025\u5f80\u4e0a\u5d29\u3002\u90a3\u6599\u5510\u6653\u6f9c\u8eab\u5f62\u4e00\u5c55\uff0c\u6e38\u9f99\u5251\u5df2\u662f\u7a81\u7136\u6539 \u4e86\u65b9\u5411\uff0c\u524a\u4ed6\u4e0b\u76d8\u3002\u4f7f\u94c1\u724c\u7684\u6b66\u58eb\u6551\u62db\u4e0d\u53ca\uff0c\u53cc\u8db3\u81ea\u819d\u76d6\u4ee5\u4e0b\uff0c\u5168\u7ed9\u65a9\u65ad\uff01\u8fd9\u65f6\u738b\u9675\u548c\u909d\u7ec3 \u971e\u8fd8\u5728\u5341\u6570\u4e08\u5f00\u5916\uff0c\u548c\u4e24\u540d\u6b66\u58eb\u62fc\u6597\u3002\u5510\u6653\u6f9c\u6b63\u5f85\u4e0a\u524d\u6551\u63f4\uff0c\u5ffd\u88ab\u5468\u9752\u4e00\u628a\u62c9\u4f4f\uff01 \u3000\u3000\u5510\u6653\u6f9c\u6b63\u81ea\u4e00\u6014\uff0c\u5468\u9752\u5df2\u5728\u4ed6\u8033\u8fb9\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u4f60\u5bf9\u738b\u9675\u53ef\u5f97\u5c0f\u5fc3\u5728\u610f\uff01\u201d\u201c\u653e\u624b\u201d\uff0c \u731b\u7136\u4e00\u58f0\u5927\u559d\uff0c\u5f80\u540e\u5012\u7eb5\u3002\u5510\u6653\u6f9c\u6115\u7136\u4e0d\u89e3\uff0c\u51dd\u7738\u89c2\u770b\uff0c\u53ea\u89c1\u51af\u5e7f\u6f6e\u6b65\u6cd5\u9519\u4e71\uff0c\u6447\u6447\u6b32\u5760\uff1b \u5468\u9752\u8d76\u56de\u53bb\u539f\u6765\u662f\u4e3a\u4e86\u6551\u4ed6\u5e08\u5085\u3002\u5510\u6653\u6f9c\u6025\u75db\u653b\u5fc3\uff0c\u5728\u8fd9\u7d27\u6025\u5173\u5934\uff0c\u81ea\u5df1\u7adf\u4e0d\u80fd\u62bd\u8eab\u53bb\u5e2e\u5e08 \u5085\u3002\u56e0\u4e3a\u5e08\u5ac2\u5e08\u5144\u6b66\u529f\u66f4\u5f31\uff0c\u5f62\u52bf\u66f4\u6025\uff0c\u53ea\u597d\u633a\u5251\u98de\u8eab\uff0c\u5148\u53bb\u63f4\u6551\u4ed6\u4eec\uff0c\u5468\u9752\u548c\u4ed6\u8bf4\u7684\u90a3\u53e5 \u8bdd\uff0c\u4ed6\u4ea6\u5df2\u65e0\u6687\u53bb\u601d\u7d22\u4e86\u3002 \u3000\u3000\u4f60\u9053\u5468\u9752\u4f55\u4ee5\u4f1a\u5bf9\u738b\u9675\u8d77\u7591\uff0c\u539f\u6765\u4ed6\u4e45\u5386\u6c5f\u6e56\uff0c\u4f0f\u5730\u542c\u58f0\u7684\u672c\u9886\uff0c\u66f4\u662f\u767e\u4e0d\u5931\u4e00\uff0c\u4ed6\u521a \u624d\u5728\u51af\u5bb6\u7b2c\u4e00\u6b21\u4f0f\u5730\u542c\u58f0\u4e4b\u65f6\uff0c\u660e\u660e\u542c\u51fa\u4e0d\u662f\u4e00\u4eba\uff0c\u4f46\u540e\u6765\u5230\u4e86\u95e8\u524d\uff0c\u5374\u53c8\u4ec5\u662f\u738b\u9675\u4e00\u4eba\u4e00 \u9a91\uff0c\u5df2\u81ea\u7591\u60d1\uff0c\u56e0\u6b64\u4ed6\u624d\u4e0d\u6562\u7559\u5728\u51af\u5bb6\u3002\u540e\u6765\u5f00\u95e8\u7d22\u654c\uff0c\u5e7f\u573a\u9047\u4f0f\uff0c\u9f99\u6728\u516c\u4e0e\u96f7\u6d77\u97f3\u53cc\u53cc\u73b0 \u8eab\uff0c\u66f4\u662f\u4ee4\u4ed6\u7591\u5fc3\u5927\u8d77\u3002\u4ed6\u60f3\u8fd9\u4e24\u4eba\u90fd\u6709\u6781\u597d\u7684\u8f7b\u8eab\u529f\u592b\uff0c\u83ab\u4e0d\u662f\u4e0e\u738b\u9675\u4e00\u540c\u6765\u7684\u3002\u53ea\u662f\u867d \u7136\u6000\u7591\uff0c\u5374\u8fd8\u4e0d\u6562\u65ad\u5b9a\uff0c\u6050\u9632\u51a4\u6789\u597d\u4eba\uff0c\u8981\u4e0d\u7136\u4ed6\u65e9\u628a\u738b\u9675\u5e9f\u4e86\uff01 \u3000\u3000\u518d\u8bf4\u51af\u5e7f\u6f6e\u9aa4\u9047\u5f3a\u654c\uff0c\u628a\u82e6\u7ec3\u5341\u5e74\u7684\u8ffd\u98ce\u5251\u6cd5\uff0c\u65bd\u5c55\u51fa\u6765\uff0c\u7ed3\u679c\u4e86\u4e24\u540d\u8840\u6ef4\u5b50\uff0c\u6b63\u5f85\u5916 \u95ef\uff0c\u90a3\u6599\u78b0\u7740\u4e86\u706b\u4e91\u5cd2\u4e3b\u9f99\u6728\u516c\uff0c\u521a\u4e00\u63a5\u62db\uff0c\u4fbf\u7ed9\u9707\u9000\u3002\u9f99\u6728\u516c\u94c1\u62d0\u62a1\u5706\uff0c\u65cb\u98ce\u6025\u626b\uff0c\u5ffd\u7136 \u542c\u5f97\u4e00\u7247\u53ee\u53ee\u4e4b\u58f0\uff0c\u9f99\u6728\u516c\u7a81\u89c9\u80a9\u5934\u5fae\u9ebb\uff0c\u6709\u5982\u7ed9\u5927\u8682\u8681\u53ee\u4e86\u51e0\u53e3\u4f3c\u7684\u3002\u5fc3\u4e2d\u4e00\u9707\uff0c\u6599\u662f\u4e2d \u4e86\u6885\u82b1\u9488\u4e4b\u7c7b\u6781\u5fae\u7ec6\u7684\u6697\u5668\uff0c\u4ed7\u7740\u5185\u529f\u6df1\u6e5b\uff0c\u8fd0\u6c14\u62a4\u4e86\u8981\u5bb3\uff0c\u9f99\u5934\u62d0\u6756\u5237\u5730\u4e00\u4e2a\u201c\u602a\u87d2\u7ffb \u8eab\u201d\uff0c\u6253\u5f97\u98de\u6c99\u8d70\u77f3\uff0c\u51f6\u731b\u5f02\u5e38\u3002\u51af\u5e7f\u6f6e\u5de6\u624b\u53d1\u4e86\u4e00\u628a\u98de\u8292\uff0c\u5251\u8bc0\u4e00\u9886\uff0c\u654c\u4eba\u94c1\u62d0\u5df2\u5230\u9762 \u524d\u3002\u51af\u5e7f\u6f6e\u77e5\u9053\u4e0d\u80fd\u786c\u78b0\uff0c\u53f3\u817f\u4e00\u63d0\uff0c\u4e0b\u62a4\u5176\u88c6\uff0c\u8eab\u8eaf\u534a\u8f6c\uff0c\u4fa7\u76ee\u56de\u7768\uff0c\u4e09\u5c3a\u9752\u950b\uff0c\u8fc5\u5982\u7535 \u63a3\uff0c\u4e0d\u67b6\u654c\u62db\uff0c\u53cd\u622a\u654c\u8155\uff0c\u5251\u5c16\u4e0b\u5212\uff0c\u500f\u7684\u5212\u5230\u654c\u624b\u8109\u95e8\uff01 \u3000\u3000\u8fd9\u4e00\u62db\u662f\u8ffd\u98ce\u5251\u7684\u6551\u6025\u7edd\u62db\uff0c\u6b63\u6240\u8c13\u5584\u6218\u8005\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u987f\u65f6\u628a\u654c\u62db\u7834\u5f00\u3002\u4f46\u9f99\u6728\u516c \u4e5f\u597d\u751f\u5389\u5bb3\uff0c\u559d\u58f0\uff1a\u201c\u8ffd\u98ce\u5251\u6cd5\u679c\u7136\u4e0d\u51e1\uff01\u201d\u907f\u62db\u8fdb\u62db\uff0c\u7528\u201c\u8155\u5e95\u7ffb\u4e91\u201d\u6a2a\u622a\u51af\u5e7f\u6f6e\u5251\u8eab\uff0c \u51af\u5e7f\u6f6e\u63a5\u62db\u8fd8\u62db\uff0c\u5f80\u4e0b\u4e00\u584c\u8170\uff0c\u5251\u8d70\u8f7b\u7075\uff0c\u5708\u56de\u6765\uff0c\u53d1\u51fa\u53bb\uff0c\u4e00\u62db\u201c\u6625\u4e91\u4ed1\u5c55\u201d\uff0c\u76f4\u5954\u654c\u4eba \u53f3\u808b\u3002\u9f99\u6728\u516c\u5ffd\u7136\u5411\u540e\u4e00\u5012\uff0c\u94c1\u62d0\u8131\u624b\u98de\u51fa\uff0c\u62d0\u5251\u76f8\u649e\uff0c\u5251\u8f7b\u62d0\u91cd\uff0c\u51af\u5e7f\u6f6e\u7684\u5251\u7ed9\u9707\u4e0a\u534a \u7a7a\uff0c\u864e\u53e3\u6d41\u8840\u3002\u9f99\u6728\u516c\u4e00\u8dc3\u800c\u8d77\uff0c\u4f38\u5f00\u84b2\u6247\u822c\u7684\u5927\u624b\u76f4\u6293\u4e0b\u6765\uff0c\u6708\u5149\u4e0b\u53ea\u89c1\u4ed6\u638c\u5fc3\u7ea2\u5982\u6731 \u7802\uff0c\u51af\u5e7f\u6f6e\u5927\u9a87\u6b32\u9003\uff0c\u80a9\u5934\u5df2\u4f3c\u7ed9\u5343\u65a4\u91cd\u7269\u786c\u538b\u4e0b\u6765\uff0c\u6025\u5fd9\u6c89\u80a9\u7f29\u8098\uff0c\u5f80\u540e\u4e00\u6323\uff0c\u5947\u75db\u5f7b \u9aa8\uff0c\u80a9\u5934\u5df2\u662f\u8840\u6dcb\u6dcb\u7684\uff0c\u7ed9\u9f99\u6728\u516c\u8fde\u76ae\u5e26\u8089\u6495\u53bb\u4e86\u4e00\u5927\u5757\uff01 \u3000\u3000\u907f\u5251\u3001\u63b7\u62d0\u3001\u53d1\u638c\u3001\u6293\u6495\uff0c\u56db\u4e2a\u52a8\u4f5c\u4e00\u6c14\u5475\u6210\uff0c\u5feb\u5982\u95ea\u7535\uff01\u6b63\u662f\u9f99\u6728\u516c\u8d25\u4e2d\u6c42\u80dc\u7684\u6740\u624b \u7edd\u62db\uff01\u5468\u9752\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u5012\u7eb5\u56de\u6765\uff0c\u51af\u5e7f\u6f6e\u5df2\u662f\u5012\u5730\u4e0d\u8d77\uff0c\u909d\u740f\u8fd9\u65f6\u6b63\u968f\u7740\u949f\u4e07\u5802\u594b\u529b\u51b2 \u6740\uff0c\u542c\u5f97\u558a\u58f0\uff0c\u56de\u8eab\u5f85\u6551\u4eb2\u5bb6\uff0c\u96f7\u6d77\u97f3\u7684\u9b3c\u5934\u5200\u9996\u5148\u65ab\u5230\uff0c\u201c\u6cf0\u5c71\u538b\u9876\u201d\uff0c\u8fde\u4eba\u5e26\u5200\uff0c\u786c\u5f80 \u4e0b\u843d\uff0c\u76f4\u65ab\u909d\u740f\u9879\u6881\u3002\u909d\u740f\u4e00\u95ea\uff0c\u594b\u529b\u62db\u67b6\uff0c\u4f46\u4ecd\u662f\u62db\u67b6\u4e0d\u4f4f\u3002\u96f7\u6d77\u97f3\u98de\u8d77\u4e00\u811a\uff0c\u8e22\u7740\u4ed6\u7684 \u80da\u9aa8\uff0c\u909d\u740f\u811a\u6b65\u8e09\u8dc4\uff0c\u649e\u5728\u4e00\u540d\u8840\u6ef4\u5b50\u8eab\u4e0a\u3002\u949f\u4e07\u5802\u6025\u5fd9\u659c\u91cc\u63a0\u51fa\uff0c\u98de\u811a\u5c06\u90a3\u540d\u8840\u6ef4\u5b50\u8e22 \u7ffb\uff0c\u5de6\u8098\u4e00\u5e26\u90ae\u73bb\uff0c\u4e00\u4e2a\u201c\u5012\u8e29\u4e03\u661f\u6b65\u201d\uff0c\u5f80\u540e\u6025\u9000\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u51af\u5e7f\u6f6e\u5636\u58f0\u5389\u547c\uff1a \u201c\u4f60\u4eec\u5feb\u9003\uff0c\u9003\u5f97\u4e00\u4e2a\u662f\u4e00\u4e2a\uff01\u201d\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u4ed6\u8e09\u8e09\u8dc4\u8def\u8d70\u4e86\u51e0\u6b65\uff0c\u4e24\u4e2a\u8840\u6ef4\u5b50\u4ea4\u53c9\u98de \u6765\uff0c\u602a\u5578\u58f0\u4e2d\uff0c\u51af\u5e7f\u6f6e\u4e00\u58f0\u60e8\u53eb\uff0c\u5934\u9885\u7adf\u7ed9\u8840\u6ef4\u5b50\u786c\u751f\u751f\u526a\u53bb\uff01\u949f\u909d\u4e8c\u4eba\u53c8\u60ca\u53c8\u6012\uff0c\u949f\u4e07\u5802 \u5de6\u624b\u62b1\u7740\u7684\u51af\u7433\uff0c\u5ffd\u7136\u201c\u4e4c\u54c7\u201d\u201c\u4e4c\u54c7\u201d\u7684\u60ca\u54ed\u8d77\u6765\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u51af\u7433\u82f9\u679c\u80a1\u7684\u5c0f\u8138\uff0c\u66f4\u663e\u5f97\u5206\u5916\u53ef\u7231\u3002\u949f\u4e07\u5802\u53f9\u4e86\u53e3\u6c14\uff0c\u4e00\u54ac\u7259\u6839\uff0c\u6bc5\u7136\u8bf4 \u9053\uff1a\u201c\u5148\u6551\u5b69\u5b50\u3002\u201d\u628a\u51af\u7433\u4ea4\u7ed9\u909d\u740f\uff0c\u5de6\u624b\u6263\u4e86\u51e0\u67c4\u593a\u547d\u795e\u5200\uff0c\u559d\u9053\uff1a\u201c\u968f\u6211\u6765\uff0c\u95ef\u51fa \u53bb\uff01\u201d\u96f7\u6d77\u97f3\u57ab\u6b65\u8d76\u622a\uff0c\u4e00\u5200\u5288\u53bb\uff0c\u949f\u4e07\u5802\u9661\u7136\u4e00\u4f0f\u8170\uff0c\u4f3c\u6b32\u8ba9\u62db\uff0c\u53c8\u4e00\u65cb\u8eab\uff0c\u4f3c\u6b32\u51fa\u5251\uff0c \u96f7\u6d77\u97f3\u4e5f\u662f\u8001\u624b\uff0c\u89c1\u4ed6\u865a\u5b9e\u83ab\u6d4b\uff0c\u4e0d\u6562\u8e81\u8fdb\uff0c\u4ed6\u65c1\u8fb9\u4e24\u540d\u6b66\u58eb\uff0c\u5374\u5df2\u5e76\u80a9\u62a2\u4e0a\u3002\u949f\u4e07\u5802\u9752\u94a2 \u5251\u5bd2\u5149\u95ea\u95ea\uff0c\u5bb9\u5230\u654c\u4eba\u62a2\u8fd1\uff0c\u5ffd\u7136\u65cb\u98ce\u6025\u626b\uff0c\u4e0b\u51fb\u654c\u4eba\u8170\u80ef\uff0c\u4e00\u540d\u6b66\u58eb\u60ca\u53eb\u4e00\u58f0\uff0c\u77ed\u886b\u8d34\u8089 \u4e4b\u5904\uff0c\u88ab\u5251\u5c16\u7a7f\u4e86\u4e00\u6d1e\uff0c\u5e78\u4ed6\u5c1a\u7b97\u673a\u7075\uff0c\u4f0f\u5730\u6025\u6eda\uff0c\u4f7f\u51fa\u201c\u71d5\u9752\u5341\u516b\u7ffb\u201d\u7684\u6eda\u5730\u5802\u529f\u592b\uff0c\u6eda \u51fa\u6570\u4e08\u5f00\u5916\uff0c\u90a3\u4e00\u540d\u6b66\u571f\u5403\u4e86\u4e00\u60ca\uff0c\u9000\u540e\u4e24\u6b65\uff0c\u5c1a\u5f85\u6536\u97ad\u6321\u5251\uff0c\u949f\u4e07\u5802\u5251\u968f\u8eab\u8f6c\uff0c\u593a\u547d\u795e\u5200 \u5728\u811a\u5e95\u53d1\u51fa\uff0c\u820c\u7efd\u6625\u96f7\uff0c\u559d\u9053\uff1a\u201c\u5012\uff01\u201d\u90a3\u540d\u6b66\u58eb\u679c\u7136\u5e94\u58f0\u5012\u5730\uff0c\u53eb\u9053\uff1a\u201c\u6697\u9752\u5b50\u6709\u6bd2\uff0c\u6709 \u6bd2\uff01\u201d\u949f\u4e07\u5802\u628a\u624b\u8fde\u626c\uff0c\u4e09\u67c4\u98de\u5200\uff0c\u8fde\u73af\u5c04\u51fa\u3002\u96f7\u6d77\u97f3\u6a2a\u5200\u4e00\u78d5\uff0c\u5c06\u4e00\u67c4\u98de\u5200\u78d5\u843d\u5c18\u57c3\uff0c\u65c1 \u8fb9\u4e24\u540d\u6b66\u58eb\uff0c\u53c8\u662f\u54ce\u54df\u8fde\u58f0\uff0c\u53cc\u53cc\u5012\u4e86\u4e0b\u6765\u3002\u96f7\u6d77\u97f3\u89c1\u5982\u6b64\u58f0\u52bf\uff0c\u90a3\u8fd8\u6562\u8ffd\uff1f\u4ed6\u81ea\u5df1\u7684\u8840\u6ef4 \u5b50\u5df2\u5931\uff0c\u53ea\u597d\u53eb\u4f19\u4f34\uff1a\u201c\u653e\u8840\u6ef4\u5b50\u53d6\u4ed6\uff01\u5feb\uff01\u5feb\uff01\u201d \u3000\u3000\u8840\u6ef4\u5b50\u653b\u8fdc\u4e0d\u653b\u8fd1\uff0c\u6df7\u6218\u7f20\u6597\u4e2d\uff0c\u4e0d\u597d\u65bd\u653e\u3002\u949f\u4e07\u5802\u4e00\u9003\uff0c\u8840\u6ef4\u5b50\u53ef\u5c31\u6765\u4e86\u3002\u4ed6\u542c\u5f97\u5934 \u9876\u4e0a\u7a7a\u602a\u58f0\u5927\u4f5c\uff0c\u4e00\u770b\u7adf\u662f\u4e94\u516d\u4e2a\u8840\u6ef4\u5b50\u547c\u5578\u800c\u6765\uff0c\u949f\u4e07\u5802\u63d2\u5251\u5f52\u9798\uff0c\u4e24\u624b\u6293\u8d77\u516d\u67c4\u98de\u5200\uff0c \u5927\u559d\uff1a\u201c\u8840\u6ef4\u5b50\u80fd\u5948\u6211\u4f55\uff01\u201d\u516d\u67c4\u98de\u5200\u7535\u5c04\u800c\u51fa\uff0c\u534a\u7a7a\u4e2d\u94ff\u94ff\u8fde\u58f0\uff0c\u8840\u6ef4\u5b50\u7ed9\u98de\u5200\u649e\u5f00\uff0c\u5706 \u7403\u5185\u7684\u5341\u4e8c\u628a\u5c0f\u5315\u9996\uff0c\u94f6\u5149\u8000\u76ee\uff0c\u5b9b\u5982\u6d12\u4e0b\u6ee1\u5929\u5200\u96e8\uff01\u5176\u4e2d\u6709\u4e00\u4e2a\u8840\u6ef4\u5b50\u60f3\u662f\u9ad8\u624b\u6240\u53d1\uff0c\u529b \u5ea6\u8f83\u5f3a\uff0c\u88ab\u98de\u5200\u78b0\u649e\uff0c\u8fd8\u662f\u901e\u76f4\u98de\u6765\u3002\u949f\u4e07\u5802\u6025\u5fd9\u8fce\u4e0a\u53bb\uff0c\u6325\u5251\u5c06\u8fd9\u679a\u8840\u6ef4\u5b50\u6311\u843d\u8fdc\u5904\uff0c\u8fd9 \u624d\u7bf7\u7684\u4e00\u58f0\u70b8\u5f00\u3002\u949f\u4e07\u5802\u4e5f\u81ea\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u9053\uff1a\u82e5\u662f\u5341\u51e0\u679a\u8840\u6ef4\u5b50\u56f4\u653b\uff0c\u90a3\u771f\u662f\u4e07\u96be\u62b5\u6321\uff0c \u602a\u4e0d\u5f97\u5468\u5927\u4fa0\u7740\u4e86\u9053\u513f\uff01 \u3000\u3000\u8fd9\u65f6\u4ed6\u5df2\u633d\u7740\u909d\u740f\u9003\u51fa\u8840\u6ef4\u5b50\u6240\u80fd\u53ca\u7684\u8303\u56f4\u4e4b\u5916\uff0c\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u5468\u9752\u7626\u957f\u7684\u8eab\u5f71\u5728\u6708 \u5149\u4e0b\u9f99\u817e\u864e\u8dc3\uff0c\u8fc5\u731b\u5f02\u5e38\u3002\u949f\u4e07\u5802\u5fc3\u91cc\u4e00\u5bbd\uff0c\u60f3\u9053\uff1a\u5468\u9752\u7684\u529f\u592b\u53ea\u6709\u5728\u6211\u4e4b\u4e0a\uff0c\u867d\u8bf4\u4ed6\u53d7\u4e86 \u4f24\uff0c\u4f46\u8840\u6ef4\u5b50\u4ea6\u5df2\u4f24\u4ea1\u8fc7\u534a\u3002\u4ed6\u7684\u98de\u8292\u6697\u5668\u4e5f\u51b3\u4e0d\u5728\u6211\u7684\u98de\u5200\u4e4b\u4e0b\uff0c\u6599\u60f3\u53ef\u80fd\u8131\u9669\u3002 \u3000\u3000\u949f\u4e07\u5802\u6b63\u81ea\u6c89\u541f\u4e0d\u5b9a\uff0c\u51af\u7433\u54ed\u4e86\u4e00\u9635\uff0c\u60f3\u662f\u5341\u5206\u75b2\u5026\uff0c\u7adf\u7136\u4f0f\u5728\u909d\u740f\u6000\u4e2d\u719f\u7761\u8d77\u6765\u3002\u949f \u4e07\u5802\u8138\u542b\u7b11\u610f\uff0c\u4eb2\u4e86\u5979\u4e00\u4e0b\u3002\u8fdc\u5904\u5468\u9752\u626c\u58f0\u53eb\u9053\uff1a\u201c\u949f\u5927\u54e5\uff0c\u5feb\u548c\u5b69\u5b50\u9003\u8dd1\uff01\u4f60\u6536\u7684\u90a3\u4e2a\u59d3 \u5e74\u5f92\u5f1f\uff0c\u82e5\u53d1\u89c9\u4ed6\u5fc3\u672f\u4e0d\u6b63\uff0c\u4f60\u5c31\u8be5\u5e9f\u4ed6\u7684\u6b66\u529f\uff0c\u5207\u52ff\u59d1\u606f\uff0c\u6211\u8131\u9669\u540e\uff0c\u81ea\u4f1a\u5230\u9648\u7559\u627e\u4f60\uff0c \u5feb\u9003\uff0c\u5feb\u9003\uff01\u201d \u3000\u3000\u949f\u4e07\u5802\u5fc3\u5934\u4e00\u9707\uff1a\u5468\u9752\u5728\u8fd9\u6837\u7d27\u6025\u7684\u5173\u5934\uff0c\u8fd8\u6bb7\u6bb7\u4ee5\u6b64\u76f8\u8beb\uff0c\u96be\u9053\u5e74\u7fb9\u5c27\u8fd9\u5b69\u5b50\u5c06\u6765\u771f \u4f1a\u6210\u4e3a\u4e00\u4ee3\u8885\u96c4\uff1f\u4f46\u8fd9\u65f6\u4ed6\u5df2\u65e0\u6687\u591a\u60f3\uff0c\u9065\u5e94\u4e00\u58f0\uff1a\u201c\u5468\u5144\u4e07\u5b89\uff0c\u9648\u7559\u518d\u89c1\uff01\u201d\u62b1\u7740\u51af\u7433\uff0c \u909d\u740f\u8fc8\u5f00\u5927\u6b65\uff0c\u5982\u98de\u9003\u8dd1\uff01 \u3000\u3000\u5468\u9752\u89c1\u949f\u4e07\u5802\u5df2\u7ecf\u8131\u9669\uff0c\u5401\u4e86\u53e3\u6c14\uff0c\u518d\u770b\u5510\u6653\u6f9c\u65f6\uff0c\u53ea\u89c1\u4ed6\u548c\u738b\u9675\u909d\u7ec3\u971e\u4e09\u4eba\uff0c\u6b63\u4e0e\u654c \u4eba\u6253\u5f97\u5341\u5206\u6fc0\u70c8\uff0c\u5510\u6653\u6f9c\u7684\u6e38\u9f99\u5251\u95ea\u95ea\u53d1\u5149\uff0c\u4e13\u524a\u654c\u4eba\u5175\u5203\uff0c\u738b\u9675\u7684\u516d\u5408\u5927\u67aa\u4e0a\u5d29\u4e0b\u7838\uff0c\u91cc \u64a9\u5916\u6ed1\uff0c\u4e5f\u9887\u89c1\u529f\u592b\u3002\u5bf9\u9762\u90a3\u4e09\u540d\u6b66\u58eb\u867d\u975e\u5eb8\u624b\uff0c\u4f46\u4e0e\u9f99\u6728\u516c\u96f7\u6d77\u97f3\u76f8\u6bd4\uff0c\u5374\u662f\u5dee\u5f97\u751a\u8fdc\uff0c \u5510\u6653\u6f9c\u7b49\u4e09\u4eba\u5c3d\u81ea\u62b5\u6321\u5f97\u4f4f\u3002 \u3000\u3000\u5468\u9752\u677e\u4e86\u53e3\u6c14\uff0c\u53cc\u638c\u4e00\u7d27\uff0c\u5de6\u638c\u4e0a\u6258\uff0c\u53f3\u624b\u4e00\u62c9\uff0c\u548b\u56b7\u4e00\u58f0\uff0c\u628a\u4e00\u540d\u654c\u624b\u7684\u53f3\u81c2\u786c\u751f\u751f \u6298\u65ad\u3002\u9f99\u6728\u516c\u52c3\u7136\u5927\u6012\uff0c\u94c1\u62d0\u5f80\u4e0a\u4e00\u62bd\uff0c\u987a\u52bf\u53cd\u5c55\uff0c\u75be\u5982\u9a87\u7535\uff0c\u7167\u5468\u9752\u9762\u95e8\u5288\u6765\uff0c\u8fd9\u4e00\u62db\u7528 \u5f97\u5f02\u5e38\u8fc5\u75be\u9669\u72e0\uff0c\u597d\u4e2a\u5468\u9752\uff0c\u907f\u62db\u4e0d\u53ca\uff0c\u8fd0\u8db3\u5185\u529b\uff0c\u53cd\u81c2\u4e00\u632f\uff0c\u7adf\u786c\u63a5\u4e86\u9f99\u6728\u516c\u4e00\u62d0\uff0c\u8eab\u8eaf \u4e5f\u8d81\u8fd9\u4e00\u9707\u4e4b\u529b\uff0c\u5012\u7ffb\u51fa\u4e09\u4e08\u5f00\u5916\uff01 \u3000\u3000\u9f99\u6728\u516c\u8fd9\u4e00\u62d0\u5982\u51fb\u94c1\u77f3\uff0c\u4e5f\u662f\u5012\u9000\u6570\u6b65\uff0c\u864e\u53e3\u53d1\u75db\uff0c\u4e0d\u89c9\u80c6\u5bd2\uff0c\u4ed6\u4e0d\u77e5\u5468\u9752\u5374\u4f24\u5f97\u66f4 \u91cd\uff01\u5468\u9752\u5185\u529f\u867d\u9ad8\uff0c\u4f46\u5728\u53d7\u5267\u6bd2\u6697\u5668\u6240\u4f24\u4e4b\u540e\uff0c\u4ee5\u8840\u8089\u4e4b\u8eaf\uff0c\u63a5\u4e86\u8fd9\u62d0\uff0c\u4e94\u810f\u516d\u8151\uff0c\u5747\u53d7\u9707 \u8361\uff0c\u773c\u775b\u53d1\u9ed1\uff0c\u5947\u75db\u5f7b\u5fc3\uff0c\u81ea\u77e5\u6027\u547d\u96be\u4fdd\u3002\u5510\u6653\u6f9c\u8bb7\u8bb7\u9053\uff1a\u201c\u5468\u4f2f\u4f2f\uff0c\u5feb\u6765\u5440\uff01\u54b1\u4eec\u5e76\u80a9\u5b50 \u95ef\u51fa\u53bb\uff01\u201d \u3000\u3000\u5468\u9752\u4e00\u626c\u624b\uff0c\u6253\u51fa\u4e03\u679a\u98de\u8292\u6697\u5668\uff0c\u628a\u56f4\u653b\u5510\u6653\u6f9c\u3001\u909d\u7ec3\u971e\u7684\u51e0\u540d\u6b66\u58eb\u6253\u4f24\uff0c\u53eb\u9053\uff0c\u201c\u4f60 \u4eec\u5feb\u8dd1\uff01\u4e0d\u5fc5\u7b49\u6211\uff01\u201d\u5510\u6653\u6f9c\u4e00\u9635\u8fdf\u7591\uff0c\u5468\u9752\u559d\u9053\uff1a\u201c\u4f60\u4e0d\u542c\u6211\u7684\u8bdd\u4e48\uff1f\u201d\u547c\u547c\u602a\u5578\uff0c\u4e00\u4e2a \u94c1\u7403\u53c8\u5df1\u98de\u5230\u5934\u9876\uff0c\u5510\u6653\u6f9c\u5b9d\u5251\u5f80\u4e0a\u4e00\u6311\uff0c\u628a\u6765\u88ad\u7684\u8840\u6ef4\u5b50\u6311\u5f00\uff0c\u80cc\u540e\u53c8\u542c\u5f97\u5468\u9752\u53eb\u9053\uff1a \u201c\u5feb\u8dd1\u5feb\u8dd1\uff0c\u7528\u98de\u8292\u6253\u4ed6\u4eec\u3002\u201d\u5510\u6653\u6f9c\u548c\u738b\u9675\u508d\u7740\u909d\u7ec3\u971e\uff0c\u51b2\u6740\u51fa\u53bb\uff0c\u80cc\u540e\u53ea\u6709\u51e0\u540d\u6b66\u58eb\u8ffd \u6765\u4e86\u3002 \u3000\u3000\u5468\u9752\u89c1\u5510\u6653\u6f9c\u7b49\u4e09\u4eba\u90fd\u5df2\u8131\u9669\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u4ed6\u81ea\u77e5\u6027\u547d\u96be\u4fdd\uff0c\u8981\u4ed7\u7740\u4e00\u53e3\u6c14\u5728\uff0c\u66ff\u4ed6\u4eec \u65ad\u8def\uff0c\u96f7\u6d77\u97f3\u8d76\u4e0a\u6765\uff0c\u5468\u9752\u53cc\u76ee\u5706\u7741\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u8fc5\u5982\u5954\u96f7\uff0c\u96f7\u6d77\u97f3\u5413\u5f97\u8d76\u5fd9\u5012 \u9000\uff0c\u5df2\u6765\u4e0d\u53ca\uff0c\u8155\u9aa8\u788e\u88c2\uff0c\u9b3c\u5934\u5200\u8131\u624b\u98de\u53bb\uff0c\u6655\u5012\u5730\u4e0a\u3002\u5468\u9752\u51f6\u795e\u6076\u715e\u822c\u7684\u62e6\u5728\u5927\u8def\u4e0a\uff0c\u4e00 \u4e2a\u8840\u6ef4\u5b50\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\uff0c\u4e0d\u8981\u60f9\u4ed6\u3002\u201d\u8fd9\u4e00\u6218\uff0c\u867d\u7136\u6bd9\u4e86\u51af\u5e7f\u6f6e\u7236\u5b50\uff0c\u4f46\u8840\u6ef4\u5b50\u4e5f\u5df2\u4f24\u4ea1 \u8fc7\u534a\uff0c\u96f7\u6d77\u97f3\u5e76\u4e14\u53d7\u4e86\u91cd\u4f24\uff0c\u9664\u4e86\u6709\u4e09\u56db\u4eba\u53bb\u8ffd\u5510\u6653\u6f9c\u4e4b\u5916\uff0c\u5269\u4e0b\u6765\u7684\u9f99\u6728\u516c\u5728\u5185\uff0c\u4e0d\u8fc7\u4e94 \u4eba\u3002\u9f99\u6728\u516c\u672c\u5df2\u8131\u9669\uff0c\u4f46\u4e00\u770b\u4e4b\u4e0b\uff0c\u5ffd\u5730\u53c8\u6012\u9a82\u9053\uff1a\u201c\u8113\u5305\uff01\u8ddf\u6211\u6765\uff0c\u4ed6\u9003\u4e0d\u4e86\uff01\u201d\u9f99\u5934\u62d0 \u6756\u4e00\u5c55\uff0c\u5411\u524d\u51b2\u53bb\u3002\u539f\u6765\u4ed6\u89c1\u5468\u9752\u8eb2\u907f\u8840\u6ef4\u5b50\u65f6\uff0c\u867d\u7136\u654f\u6377\uff0c\u4f46\u8eab\u6cd5\u663e\u5df2\u4e0d\u53ca\u4ece\u524d\u7075\u6d3b\uff0c\u8d77 \u6b65\u843d\u6b65\u4e4b\u9645\uff0c\u5fae\u89c1\u6447\u6643\u3002\u4f4e\u624b\u770b\u4e0d\u51fa\u6765\uff0c\u9f99\u6728\u516c\u53ef\u662f\u4e2a\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u89c1\u5fae\u77e5\u8457\uff0c\u6599\u5f97\u5468 \u9752\u5df1\u662f\u5f3a\u5f29\u4e4b\u672b\u4e86\u3002 \u3000\u3000\u8fd9\u51e0\u540d\u8840\u6ef4\u5b50\u90fd\u662f\u56db\u7687\u5b50\u7684\u6b7b\u58eb\uff0c\u7ed9\u9f99\u6728\u516c\u8fde\u9a82\u4e24\u6b21\u201c\u8113\u5305\u201d\u5341\u5206\u4e0d\u5fff\uff0c\u811a\u6b65\u6545\u610f\u8fdf \u7f13\uff0c\u8ba9\u4ed6\u72ec\u81ea\u5411\u524d\u3002\u5468\u9752\u53cc\u624b\u8fde\u626c\uff0c\u4e00\u628a\u98de\u8292\u8fce\u7a7a\u6d12\u51fa\uff0c\u9f99\u6728\u516c\u8eab\u5f62\u4e0a\u62d4\uff0c\u94c1\u62d0\u62a1\u98ce\uff0c\u4f46\u4ecd \u662f\u7ed9\u4e00\u6839\u98de\u8292\u523a\u7740\u5de6\u773c\uff0c\u767b\u65f6\u778e\u4e86\u3002\u4ed6\u5e73\u751f\u4ece\u6765\u672a\u53d7\u8fc7\u5982\u6b64\u632b\u8d25\uff0c\u51f6\u6027\u53d1\u4f5c\uff0c\u5728\u534a\u7a7a\u4e2d\u7ffb\u4e2a \u7b4b\u6597\uff0c\u8fde\u4eba\u5e26\u62d0\u6756\uff0c\u4e25\u5982\u4e00\u6761\u5f20\u7259\u821e\u722a\u7684\u9ed1\u9f99\uff0c\u534a\u7a7a\u6233\u4e0b\uff0c\u5468\u9752\u594b\u8d77\u795e\u5a01\uff0c\u53cc\u624b\u4e00\u626f\u6756\u5934\uff0c \u5927\u559d\u4e00\u4e3e\uff0c\u4e24\u4eba\u4e00\u9f50\u7528\u529b\uff0c\u7cbe\u94a2\u6253\u6210\u7684\u62d0\u6756\uff0c\u201c\u5693\u201d\u7684\u4e00\u58f0\uff0c\u5f53\u4e2d\u65ad\u4e3a\u4e24\u6bb5\u3002\u9f99\u6728\u516c\u5c06\u534a\u622a \u62d0\u6756\uff0c\u62fc\u547d\u63b7\u53bb\uff0c\u5468\u9752\u817e\u8d77\u4e00\u817f\uff0c\u5c06\u9f99\u6728\u516c\u8e22\u98de\u4e09\u4e08\u5f00\u5916\uff0c\u4f46\u80f8\u77a0\u4e5f\u7ed9\u9f99\u6728\u516c\u7684\u534a\u622a\u62d0\u6756\uff0c \u6233\u4e2a\u6b63\u7740\uff0c\u4f24\u53e3\u7834\u88c2\uff0c\u771f\u6c14\u6d88\u6563\uff0c\u8fd9\u4f4d\u51cc\u672a\u98ce\u7684\u8bb0\u540d\u5f1f\u5b50\uff0c\u4e2d\u539f\u552f\u4e00\u7cbe\u901a\u8ffd\u98ce\u5251\u6cd5\u7684\u4fa0\u58eb\uff0c \u7adf\u7136\u6b7b\u5728\u8352\u6751\u3002 \u3000\u3000\u9f99\u6728\u516c\u80f8\u53e3\u5267\u75db\uff0c\u4e00\u5927\u53e3\u4e00\u5927\u53e3\u7684\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u6025\u5fd9\u63d0\u6c14\u62a4\u4f24\uff0c\u5ffd\u542c\u5f97\u65c1\u8fb9\u7684\u8840\u6ef4\u5b50 \u8bf4\u9053\uff1a\u201c\u5927\u559c\uff0c\u5927\u559c\uff0c\u94a6\u72af\u7ed9\u4f60\u8001\u6253\u6b7b\u4e86\uff01\u201d\u9f99\u6728\u516c\u6012\u9053\uff1a\u201c\u54fc\uff01\u4f60\u4eec\u8fd9\u4e9b\u8113\u5305\uff0c\u654c\u4eba\u6b7b \u4e86\uff0c\u624d\u6562\u4e0a\u6765\u3002\u201d\u8840\u6ef4\u5b50\u4eec\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u6709\u4e2a\u8840\u6ef4\u5b50\u5ffd\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u662f\u6211\u4eec\u8113\u5305\uff01 \u6211\u4eec\u4e5f\u4e0d\u60f3\u9080\u529f\uff0c\u5c31\u8ba9\u4f60\u5272\u5468\u9752\u7684\u5934\u56de\u53bb\u7980\u62a5\u8d1d\u52d2\u5427\uff01\u201d\u9f99\u6728\u516c\u53d7\u4e86\u91cd\u4f24\uff0c\u82e5\u7136\u65e0\u4eba\u6551\u62a4\uff0c \u52bf\u5fc5\u4e5f\u966a\u846c\u8352\u6751\uff0c\u542c\u8fd9\u540d\u8840\u6ef4\u5b50\u53e3\u6c14\uff0c\u7adf\u4f3c\u60f3\u8981\u4e0d\u7406\u81ea\u5df1\uff0c\u4e0d\u7531\u5927\u6025\uff0c\u966a\u7b11\u8bf4\u9053\uff1a\u201c\u751f\u6bd9\u5de8 \u8d3c\uff0c\u5927\u5bb6\u90fd\u6709\u529f\u52b3\uff0c\u54b1\u4eec\u5144\u5f1f\u4f55\u5fc5\u4e89\u6c14\uff01\u201d\u90a3\u540d\u8840\u6ef4\u5b50\u54fc\u4e86\u4e00\u58f0\uff0c\u5c06\u9f99\u6728\u516c\u4e0e\u96f7\u6d77\u97f3\u6276\u8d77\u3002 \u81ea\u6b64\u9f99\u6728\u516c\u4e0e\u8840\u6ef4\u5b50\u4e4b\u95f4\uff0c\u6709\u4e86\u5fc3\u75c5\uff0c\u8fd9\u662f\u540e\u8bdd\u3002 \u3000\u3000\u96f7\u6d77\u97f3\u60a0\u60a0\u9192\u8f6c\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u90a3\u4e2a\u4f7f\u5251\u7684\u5c11\u5e74\u5462\uff1f\u201d\u65c1\u8fb9\u7684\u6b66\u58eb\u7b54\u9053\uff1a\u201c\u6211\u4eec\u6709\u4e09\u56db \u4e2a\u4eba\u5df2\u53bb\u8ffd\u4ed6\uff0c\u6599\u4ed6\u9003\u8dd1\u4e0d\u4e86\uff01\u201d\u96f7\u6d77\u97f3\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u672a\u5fc5\u8ffd\u5f97\u5230\u4eba\u5bb6\uff01\u201d\u4e00\u540d\u6b66\u58eb \u8bf4\u9053\uff1a\u201c\u4ed6\u662f\u548c\u738b\u9675\u4e00\u8d77\u9003\u7684\u3002\u201d\u96f7\u6d77\u97f3\u8fd9\u624d\u9762\u8272\u7a0d\u8f6c\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5514\uff0c\u90a3\u4e48\u8fd8\u6709\u5e0c\u671b\u3002 \u4f60\u4eec\u5206\u51fa\u4e24\u4eba\uff0c\u901a\u77e5\u540e\u5230\u7684\u8840\u6ef4\u5b50\uff0c\u5206\u8def\u56f4\u622a\uff01\u201d \u3000\u3000\u56db\u7687\u5b50\u5141\u796f\u8fd9\u6b21\u6697\u4e2d\u6d3e\u4eba\u8ffd\u6355\u5468\u9752\uff0c\u6709\u4e24\u4e2a\u76ee\u7684\u3002\u539f\u6765\u5eb7\u7199\u6068\u81ea\u5df1\u7684\u4eba\u80cc\u53db\uff0c\u6df1\u6015\u6b64\u98ce \u4e00\u5f00\uff0c\u8fde\u62a4\u536b\u81ea\u5df1\u7684\u6b66\u58eb\u4e5f\u9760\u4e0d\u4f4f\uff0c\u90a3\u8fd8\u5982\u4f55\u5f97\u4e86\uff0c\u5468\u9752\u662f\u5927\u5185\u536b\u58eb\u4e2d\u552f\u4e00\u5c1a\u5728\u751f\u7684\u53db\u5f92\uff0c \u5eb7\u7199\u6781\u6b32\u5f97\u800c\u7518\u5fc3\uff0c\u8981\u5c06\u4ed6\u6d3b\u6349\u56de\u6765\uff0c\u788e\u5c38\u4e07\u6bb5\uff0c\u4ee5\u505a\u6548\u5c24\u3002\u56db\u7687\u5b50\u6df1\u77e5\u7236\u7687\u5fc3\u610f\uff0c\u56e0\u6b64\u4ee4 \u95e8\u4e0b\u6b66\u58eb\uff0c\u5927\u4e3e\u8ffd\u6355\uff0c\u60f3\u5728\u7236\u7687\u9762\u524d\u9732\u8fd9\u4e00\u624b\uff0c\u538b\u5012\u5176\u4ed6\u7687\u5b50\uff0c\u53eb\u5eb7\u7199\u77e5\u9053\u4ed6\u7684\u80fd\u5e72\u3002\u53e6\u4e00 \u4e2a\u76ee\u7684\u5219\u662f\u60f3\u593a\u53d6\u5468\u9752\u7684\u6e38\u9f99\u5b9d\u5251\u548c\u8ffd\u98ce\u5251\u8bc0\u3002\u8f99\u7ffb\u5fc3\u6781\u5927\uff0c\u4e3a\u4e86\u4e89\u4f4d\uff0c\u4e0d\u60dc\u5168\u529b\u4ee5\u8d74\u3002\u4e00 \u9762\u52fe\u7ed3\u6743\u81e3\uff0c\u4e00\u9762\u5411\u7236\u7687\u9080\u5ba0\uff0c\u4e00\u9762\u8fd8\u4e0d\u60dc\u5230\u6700\u540e\u5173\u5934\uff0c\u7528\u6b66\u529b\u593a\u53d6\u7687\u4f4d\uff0c\u8776\u8840\u5bab\u5ef7\u3002\u6240\u4ee5 \u4ed6\u517b\u7684\u6b66\u58eb\u6700\u591a\uff0c\u800c\u4ed6\u81ea\u5df1\u4e5f\u6df1\u901a\u6b66\u827a\u3002\u53ea\u662f\u8fd8\u7f3a\u5c11\u4e00\u53e3\u5b9d\u5251\u3002\u695a\u662d\u5357\u7684\u6e38\u9f99\u5251\uff0c\u8001\u4e00\u8f88\u7684 \u5bab\u5ef7\u6b66\u58eb\u548c\u7981\u536b\u519b\u6559\u5934\u90fd\u8d5e\u4e0d\u7edd\u53e3\uff0c\u4ed6\u8033\u719f\u80fd\u8be6\uff0c\u6240\u4ee5\u60f3\u628a\u8fd9\u53e3\u5251\u652b\u4e3a\u5df1\u6709\u3002 \u3000\u3000\u96f7\u6d77\u97f3\u4e43\u662f\u7687\u5e9c\u201c\u56db\u9738\u201d\u4e4b\u4e00\uff0c\u751a\u5f97\u5141\u796f\u5ba0\u4fe1\uff0c\u6df1\u77e5\u7687\u5b50\u7528\u5fc3\u3002\u800c\u4eca\u77e5\u9053\u5468\u9752\u5df2\u6bd9\uff0c\u867d \u7136\u53ef\u7528\u836f\u9152\u70bc\u5468\u9752\u7684\u5934\u9885\uff0c\u4fdd\u4f4f\u4ed6\u672c\u6765\u9762\u76ee\uff0c\u8ba9\u56db\u7687\u5b50\u53ef\u4ee5\u62ff\u7740\u4eba\u5934\u53bb\u7980\u544a\u5eb7\u7199\uff0c\u53ef\u662f\u5230\u5e95 \u4e0d\u5982\u751f\u64d2\u732e\u4e0a\uff0c\u8ba9\u5eb7\u7199\u6cc4\u5fff\u7684\u597d\u3002\u56e0\u6b64\u56db\u7687\u5b50\u7684\u7b2c\u4e00\u4e2a\u76ee\u7684\uff0c\u53ea\u53ef\u8bf4\u8fbe\u5230\u4e00\u534a\u3002\u53e6\u4e00\u4e2a\u76ee \u7684\uff0c\u5374\u8fd8\u672a\u6709\u5b8c\u6210\u5e0c\u671b\u3002\u662f\u4ee5\u4ed6\u7684\u795e\u60c5\u751a\u4e3a\u4e0d\u60a6\u3002\u81f3\u4e8e\u9f99\u6728\u516c\u5219\u66f4\u52a0\u662f\u5fc3\u91cc\u4e0d\u8212\u670d\u4e86\uff0c\u4ed6\u778e \u4e86\u4e00\u773c\uff0c\u8eab\u53d7\u91cd\u4f24\uff0c\u6740\u4e86\u5468\u9752\uff0c\u81ea\u4ee5\u4e3a\u7acb\u4e86\u5929\u5927\u529f\u52b3\uff0c\u90a3\u77e5\u4ecd\u7136\u7ed9\u540c\u50da\u595a\u843d\u3002 \u3000\u3000\u5510\u6653\u6f9c\u628a\u98de\u8292\u6263\u5728\u638c\u5fc3\uff0c\u4e09\u56db\u4e2a\u5931\u4e86\u8840\u6ef4\u5b50\u6697\u5668\u7684\u6b66\u58eb\uff0c\u4e0d\u77e5\u5389\u5bb3\uff0c\u7ee7\u7eed\u8ffd\u6765\u3002\u5510\u6653\u6f9c \u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u62a4\u7740\u5ac2\u5ac2\u3002\u5148\u8d70\u4e00\u6b65\u3002\u5f85\u6211\u6253\u53d1\u8fd9\u51e0\u4e2a\u4e0d\u77e5\u6b7b\u6d3b\u7684\u4e1c\u897f\uff01\u201d\u738b\u9675\u5927\u559c\uff0c\u5bf9\u909d \u7ec3\u971e\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u5148\u8d70\uff01\u201d\u909d\u7ec3\u971e\u5374\u51dd\u6b65\u4e0d\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u96be\u540c\u5f53\uff0c\u5927\u5e08\u5144\uff0c\u4f60\u7ed9\u6211\u62b1\u62b1 \u745b\u513f\uff01\u201d\u6a2a\u5200\u4e00\u7acb\uff0c\u8981\u5e2e\u5510\u6653\u6f9c\u53ae\u6740\u3002\u738b\u9675\u5927\u4e3a\u5c34\u5c2c\uff0c\u63a5\u53c8\u4e0d\u662f\uff0c\u4e0d\u63a5\u53c8\u4e0d\u662f\u3002\u6b63\u5728\u6b64\u9645\uff0c \u5ffd\u542c\u5f97\u5510\u6653\u6f9c\u5927\u53eb\u9053\uff1a\u201c\u5012\uff01\u5012\uff01\u201d\u53cc\u624b\u98de\u626c\uff0c\u56db\u540d\u654c\u4eba\u5012\u4e86\u4e00\u53cc\uff0c\u8fd8\u6709\u4e24\u540d\u4e5f\u4f3c\u53d7\u4e86\u98de\u8292 \u4e4b\u4f24\uff0c\u8eab\u5f62\u8fdf\u6ede\uff0c\u5510\u6653\u6f9c\u4e00\u5251\u98de\u524d\uff0c\u6e38\u9f99\u5b9d\u5251\u75be\u53d1\u5982\u98ce\uff0c\u5237\uff01\u5237\uff01\u5237\uff01\u4e00\u8fde\u51e0\u5251\uff0c\u6740\u5f97\u90a3\u4e24 \u540d\u6b66\u571f\u624b\u5fd9\u811a\u4e71\u3002\u738b\u9675\u6025\u5fd9\u62a2\u4e0a\u524d\u53bb\uff0c\u516d\u5408\u5927\u67aa\u4e00\u6446\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u6211\u6765\u5e2e\u4f60\uff01\u201d\u4f46\u4ed6\u8fd8 \u672a\u62a2\u5230\u524d\u9762\uff0c\u5510\u6653\u6f9c\u7684\u5251\u5de6\u6487\u53f3\u626b\uff0c\u53c8\u628a\u4e24\u540d\u6b66\u58eb\u5168\u90fd\u7ed3\u679c\u4e86\uff01\u738b\u9675\u8d5e\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff01\u201d\u773c \u73e0\u4e00\u8f6c\uff0c\u82e5\u6709\u6240\u601d\u3002\u5510\u6653\u6f9c\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u909d\u7ec3\u971e\u6b63\u5728\u4f4e\u4f4e\u7f00\u6ce3\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u4f24\u5fc3\u5bb6\u6563\u4eba\u4ea1\u540e\uff0c\u6b64\u53bb\u6c5f\u6e56\u9669\u6076\u591a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u56de\u3000\u8be1\u8ba1\u591a\u7aef\u3000\u5978\u5f92\u6b3a\u5be1\u5987\u3000\u5947\u80fd\u5404\u663e\u3000\u56db\u4fa0\u6597\u53cc\u9b54 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e09\u56de \u8be1\u8ba1\u591a\u7aef\u3000\u5978\u5f92\u6b3a\u5be1\u5987 \u5947\u80fd\u5404\u663e\u3000\u56db\u4fa0\u6597\u53cc\u9b54 \u3000\u3000\u51af\u745b\u5728\u6bcd\u4eb2\u6000\u62b1\u4e2d\u7761\u5f97\u6b63\u9999\uff0c\u909d\u7ec3\u971e\u4f4e\u4f4e\u7f00\u6ce3\uff0c\u738b\u9675\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4e8b\u5df2\u81f3\u6b64\uff0c\u8fd8\u662f\u8282\u54c0 \u987a\u53d8\uff0c\u8d76\u5feb\u9003\u547d\u4e3a\u5b9c\u3002\u201d\u909d\u7ec3\u971e\u8f7b\u629a\u51af\u745b\u82f9\u679c\u8272\u7684\u8138\u5e9e\uff0c\u6d41\u6cea\u8bf4\u9053\uff1a\u201c\u53ef\u601c\u8fd9\u5bf9\u59d0\u59b9\uff0c\u521a\u521a \u5468\u5c81\uff0c\u5c31\u5bb6\u7834\u4eba\u4ea1\uff0c\u9aa8\u8089\u5206\u79bb\u3002\u5979\u548c\u59b9\u59b9\u4e0d\u77e5\u4f55\u65f6\u624d\u80fd\u89c1\u9762\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u949f\u5927\u4fa0\u6b66\u827a\u9ad8 \u5f3a\uff0c\u4ed6\u53c8\u7b54\u5e94\u6536\u5c0f\u4f84\u5973\u4e3a\u5f92\uff0c\u60f3\u5fc5\u65e0\u788d\uff0c\u5e08\u5ac2\u4e0d\u5fc5\u60b2\u4f24\u3002\u201d \u3000\u3000\u909d\u7ec3\u971e\u867d\u662f\u5973\u4e2d\u8c6a\u6770\uff0c\u9aa4\u9022\u5927\u53d8\uff0c\u65b9\u5bf8\u4ea6\u5df2\u4e71\u4e86\u3002\u51c4\u7136\u95ee\u9053\uff1a\u201c\u54b1\u4eec\u6709\u54ea\u91cc\u597d\u53bb\uff1f\u201d\u738b \u9675\u9053\uff1a\u201c\u541b\u5b50\u62a5\u4ec7\uff0c\u5341\u5e74\u672a\u665a\uff0c\u4f60\u548c\u6211\u5230\u4eac\u4e2d\u53bb\u5427\u3002\u6211\u5728\u4eac\u5e08\u9556\u884c\uff0c\u719f\u4eba\u5f88\u591a\uff0c\u4eac\u4e2d\u7684\u8840\u6ef4 \u5b50\u53c8\u4e0d\u8ba4\u5f97\u6211\u4eec\uff0c\u4f60\u548c\u4f84\u5973\u5c31\u968f\u6211\u4f4f\u5728\u9556\u884c\uff0c\u6211\u904d\u8bf7\u4eac\u4e2d\u597d\u624b\u6559\u5979\u6b66\u827a\uff0c\u8fd9\u662f\u4e07\u5168\u4e4b\u7b56\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u6c89\u601d\u534a\u664c\uff0c\u6602\u5934\u8bf4\u9053\uff1a\u201c\u5e08\u5ac2\uff0c\u6211\u4eec\u8fd8\u662f\u5230\u592a\u884c\u5c71\u53bb\u7684\u597d\uff01\u201d\u909d\u7ec3\u971e\u9053\uff1a\u201c\u4ec0 \u4e48\uff01\u53bb\u592a\u884c\u5c71\uff01\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5468\u5927\u4fa0\u521a\u624d\u4e0d\u662f\u8bf4\u8fc7\uff0c\u5317\u4e94\u7701\u8c6a\u6770\uff0c\u6bcf\u5341\u5e74\u805a\u4f1a\u4e00\u6b21\uff0c\u4eca\u5e74 \u4e2d\u79cb\u6070\u662f\u805a\u4f1a\u4e4b\u671f\uff0c\u5730\u70b9\u5c31\u5728\u592a\u884c\u5c71\u4e0a\u3002\u201d\u738b\u9675\u9053\uff1a\u201c\u4ed6\u4eec\u5317\u4e94\u7701\u8c6a\u6770\u805a\u4f1a\u4e0e\u6211\u4eec\u4f55\u5173\uff1f\u201d \u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u5e74\u8f7b\u8bc6\u6d45\uff0c\u4e0d\u8fc7\u770b\u8fd9\u6b21\u8840\u6ef4\u5b50\u5927\u4e3e\u51fa\u52a8\uff0c\u5fc5\u7136\u6709\u4f59\u515a\uff0c\u6b64\u53bb\u4eac\u5e08\uff0c\u8def\u9014\u9065 \u8fdc\uff0c\u51f6\u9669\u5b9a\u591a\u3002\u53bb\u592a\u884c\u5c71\u5374\u53ea\u662f\u51e0\u65e5\u8def\u7a0b\uff0c\u4e94\u7701\u8c6a\u6770\uff0c\u8fd9\u51e0\u5929\u7eb7\u7eb7\u8d76\u6765\uff0c\u8840\u6ef4\u5b50\u5c31\u7b97\u8981\u4e0e\u6211 \u4eec\u4e3a\u96be\uff0c\u4e5f\u6709\u987e\u5fcc\u3002\u201d\u738b\u9675\u55e4\u9053\uff1a\u201c\u4f60\u597d\u50cf\u548c\u8bb8\u591a\u8c6a\u6770\u8ba4\u8bc6\u4f3c\u7684\uff01\u51ed\u4ec0\u4e48\u9762\u5b50\u53eb\u4ed6\u4eec\u66ff\u6211\u4eec \u4fdd\u9556\uff1f\u201d\u5510\u6653\u6f9c\u4e0d\u7406\u738b\u9675\u51fa\u8bed\u8ba5\u8bee\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u867d\u8bc6\u4eba\u4e0d\u591a\uff0c\u4f46\u8c6a\u4fa0\u4e4b\u58eb\uff0c\u5728\u6240\u591a\u6709\uff0c \u7eb5\u975e\u4eb2\u53cb\uff0c\u8def\u89c1\u4e0d\u5e73\uff0c\u4e5f\u4f1a\u62d4\u5200\u76f8\u52a9\u7684\uff01\u201d \u3000\u3000\u4e24\u4eba\u4e89\u6301\u4e0d\u4e0b\uff0c\u909d\u7ec3\u971e\u4f4e\u5934\u9ed8\u60f3\uff0c\u4ea6\u662f\u96be\u4e8e\u51b3\u65ad\uff0c\u738b\u9675\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u7684\u5251\u6cd5\u662f \u8c01\u6559\u7684\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u81ea\u7136\u662f\u5e08\u5085\u6559\u7684\u3002\u201d\u8bf4\u4e86\u4e4b\u540e\uff0c\u53d1\u89c9\u4e0d\u59a5\uff0c\u738b\u9675\u4fbf\u9053\uff1a\u201c\u8bf7\u501f\u4f60\u7684 \u5b9d\u5251\u4e00\u89c2\uff01\u201d\u5510\u6653\u6f9c\u60f3\u8d77\u5468\u9752\u7684\u5631\u5490\uff0c\u7591\u60e7\u987f\u751f\uff0c\u966a\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u5468\u5927\u4fa0\u9001\u7684\uff0c\u4ed6\u53eb\u6211\u5251 \u4e0d\u79bb\u8eab\uff0c\u867d\u7136\u5e08\u5144\u6709\u547d\uff0c\u6211\u8fd8\u662f\u4e0d\u6562\u8fdd\u80cc\u5468\u5927\u4fa0\u7684\u5631\u5490\u3002\u201d\u738b\u9675\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5ffd\u7136\u95ee\u9053\uff1a \u201c\u4f60\u662f\u54ea\u91cc\u4eba\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u5e7c\u906d\u5b64\u9732\uff0c\u6d41\u843d\u6c5f\u6e56\uff0c\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u662f\u54ea\u91cc\u7684\u4eba\u3002\u201d\u738b\u9675 \u9053\uff1a\u201c\u4f60\u5728\u5173\u5916\u4f4f\u8fc7\u8bb8\u4e45\u5427\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff0c\u201c\u662f\u3002\u5e08\u5144\u76d8\u95ee\u8fd9\u4e9b\u5e72\u5417\uff1f\u201d\u738b\u9675\u8f6c\u8fc7\u9762\u6765\u5bf9\u909d \u7ec3\u971e\u9053\uff1a\u201c\u6211\u5728\u5e08\u95e8\u8fd9\u8bb8\u591a\u5e74\u4e5f\u4e0d\u77e5\u5e08\u5085\u4f1a\u4f7f\u5251\uff0c\u5510\u5e08\u5f1f\u624d\u6765\u4e86\u4e00\u5e74\uff0c\u5e08\u5085\u5c31\u6559\u4ed6\u4e0a\u4e58\u5251 \u6cd5\uff0c\u771f\u662f\u5404\u6709\u673a\u7f18\u3002\u5e08\u59b9\uff0c\u54b1\u4eec\u540c\u4e00\u6751\u5b50\u957f\u5927\uff0c\u5f7c\u6b64\u6765\u5386\u90fd\u5f88\u6e05\u695a\u3002\u8fd9\u4f4d\u5e08\u5f1f\uff0c\u7a81\u7136\u4ece\u5173\u5916 \u8fdc\u6765\u6295\u5e08\uff0c\u53c8\u5f97\u5e08\u5085\u8fd9\u6837\u5ba0\u7231\uff0c\u4e00\u5b9a\u662f\u5927\u6709\u6765\u5934\u7684\u4e86\uff01\u53ef\u60dc\u4ed6\u521a\u5165\u5e08\u95e8\u4e00\u5e74\uff0c\u8840\u6ef4\u5b50\u5c31\u767b\u95e8 \u62dc\u8bbf\uff0c\u4e0d\u662f\u6211\u8bf4\u8ff7\u4fe1\u7684\u8bdd\uff0c\u6050\u6015\u4ed6\u7684\u547d\u662f\u514b\u5e08\u4e4b\u547d\u3002\u201d \u3000\u3000\u738b\u9675\u7684\u8bdd\uff0c\u663e\u7136\u662f\u6307\u5510\u6653\u6f9c\u6765\u5386\u4e0d\u660e\uff0c\u5e76\u4e14\u6697\u793a\u8840\u6ef4\u5b50\u5c31\u662f\u5510\u6653\u6f9c\u5f15\u6765\uff0c\u8c0b\u5bb3\u5e08\u5085\u4e00\u5bb6 \u7684\u3002\u5510\u6653\u6f9c\u542c\u5f97\u5fc3\u5934\u706b\u8d77\uff0c\u51e0\u4e4e\u5c31\u8981\u53d1\u4f5c\u3002\u4f46\u8f6c\u60f3\u5728\u8fd9\u65f6\u5019\u4e0d\u5b9c\u540c\u5ba4\u64cd\u6208\uff0c\u800c\u81ea\u5df1\u6765\u5386\u786e\u6709 \u96be\u8a00\u4e4b\u9690\uff0c\u8fd9\u4f4d\u5e08\u5144\u4e0d\u77e5\uff0c\u81ea\u4e5f\u96be\u602a\u4ed6\u6709\u6240\u6000\u7591\u3002\u5982\u6b64\u4e00\u60f3\uff0c\u6012\u706b\u7a0d\u5e73\uff0c\u53ea\u628a\u773c\u775b\u89d1\u7740\u909d\u7ec3 \u971e\u3002\u5fc3\u4e2d\u76d8\u7b97\uff0c\u82e5\u5e08\u5ac2\u4e5f\u5982\u6b64\u89c1\u7591\uff0c\u90a3\u53ea\u6709\u98d8\u7136\u81ea\u53bb\u7684\u4e86\u3002 \u3000\u3000\u51af\u5e7f\u6f6e\u5728\u5c4b\u5b50\u91cc\u548c\u949f\u4e07\u5802\u8c08\u53ca\u5510\u6653\u6f9c\u6295\u5e08\u7ecf\u8fc7\u65f6\uff0c\u738b\u9675\u5c1a\u672a\u6765\u5230\uff0c\u909d\u7ec3\u971e\u5374\u5728\u65c1\u8fb9\uff0c\u77e5 \u9053\u8fd9\u4f4d\u5e08\u5f1f\u6765\u5386\u867d\u7136\u4e0d\u660e\uff0c\u5374\u662f\u5468\u5927\u4fa0\u4eb2\u81ea\u5631\u6258\u81ea\u5df1\u516c\u516c\u6559\u7684\u3002\u5bf9\u738b\u9675\u6311\u62e8\u4e4b\u8a00\uff0c\u5341\u5206\u4e0d \u5feb\uff0c\u672c\u6765\u4ed6\u5c1a\u672a\u51b3\u65ad\uff0c\u8fd9\u65f6\u5ffd\u7136\u6602\u9996\u8bf4\u9053\uff1a\u201c\u5510\u5e08\u5f1f\u4e4b\u8a00\u6709\u7406\uff0c\u54b1\u4eec\u5148\u4e0a\u592a\u884c\u5c71\u53bb\uff01\u201d\u738b\u9675 \u4e0d\u89c9\u6115\u7136\u3002\u909d\u7ec3\u971e\u6307\u7740\u51af\u745b\u8bf4\u9053\uff1a\u201c\u6211\u4e24\u6bcd\u5973\u5168\u4ed7\u4e24\u4f4d\u5e08\u5144\u5f1f\u6551\u52a9\uff0c\u54b1\u4eec\u4e09\u4eba\u53ef\u5f97\u4e00\u5fc3\u4e00\u610f \u5bf9\u4ed8\u654c\u4eba\uff0c\u6211\u6765\u751f\u7ed3\u8349\u8854\u73af\uff0c\u4e5f\u8981\u62a5\u4e24\u4f4d\u5927\u5fb7\u3002\u201d\u738b\u9675\u4e00\u542c\u6b64\u8a00\u3002\u77e5\u9053\u909d\u7ec3\u971e\u5df2\u7591\u81ea\u5df1\u6311 \u62e8\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u6211\u662f\u8ba4\u4e3a\u53bb\u4eac\u5e08\u66f4\u4e3a\u5b89\u5168\uff0c\u4f46\u5e08\u59b9\u548c\u5510\u5e08\u5f1f\u65e2\u7136\u51b3\u610f\u5148\u53bb\u592a\u884c\uff0c\u90a3\u505a\u5e08\u5144 \u7684\u5c31\u662f\u820d\u4e86\u6027\u547d\u4e5f\u8981\u966a\u5e08\u59b9\u524d\u5f80\u3002\u201d \u3000\u3000\u592a\u884c\u5c71\u5728\u6cb3\u5357\u897f\u5317\uff0c\u79bb\u6c5d\u5dde\u5927\u7ea6\u662f\u4e94\u65e5\u7684\u8def\u9014\uff0c\u909d\u7ec3\u971e\u4e00\u7b97\uff0c\u79bb\u4e2d\u79cb\u5c1a\u6709\u4e09\u65e5\uff0c\u5230\u592a\u884c \u5c71\u7684\u65f6\u5019\uff0c\u7fa4\u96c4\u805a\u4f1a\u4e0d\u8fc7\u4e24\u5929\uff0c\u60f3\u8fd8\u672a\u6563\u3002\u6cbf\u8def\u4e0a\u738b\u9675\u795e\u8272\u9887\u4e3a\u4e0d\u5b89\uff0c\u909d\u7ec3\u971e\u53ea\u9053\u4ed6\u662f\u56e0\u548c \u5510\u6653\u6f9c\u4e89\u6267\u4e4b\u6545\uff0c\u5e76\u672a\u5728\u610f\u3002 \u3000\u3000\u8d70\u4e86\u4e24\u5929\uff0c\u5230\u4e86\u6d1b\u9633\uff0c\u738b\u9675\u6c5f\u6e56\u9605\u5386\u751a\u4e30\uff0c\u7ec6\u4e00\u7559\u795e\uff0c\u679c\u7136\u89c1\u6709\u8ff9\u4f3c\u7eff\u6797\u7684\u4eba\u7269\uff0c\u5728\u57ce \u4e2d\u6765\u5f80\uff0c\u6697\u4e2d\u6212\u5907\u3002\u5510\u6653\u6f9c\u4e5f\u662f\u5904\u5904\u63d0\u9632\u3002\u6295\u5bbf\u4e4b\u65f6\uff0c\u5ffd\u89c1\u738b\u9675\u4e0e\u4e00\u7fa4\u6c49\u5b50\u70b9\u5934\u62db\u547c\uff0c\u95ee\u8d77 \u6765\u65f6\uff0c\u738b\u9675\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u6591\u9556\u884c\u670b\u53cb\uff0c\u6ca1\u6709\u4ec0\u4e48\u6df1\u4ea4\uff0c\u6240\u4ee5\u6253\u4e2a\u62db\u547c\u4fbf\u7b97\u3002\u201d\u5510\u6653\u6f9c\u5f53\u665a\u4e0d \u6562\u5165\u7761\uff0c\u5b9d\u5251\u60ac\u8170\uff0c\u98de\u8292\u5728\u624b\uff0c\u8b66\u5907\u4e00\u665a\uff0c\u5374\u662f\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u7b2c\u4e09\u5929\u5230\u4e86\u5b5f\u6d25\uff0c\u662f\u6cb3\u5357\u6c34\u9646\u4ea4\u901a\u7684\u8981\u5730\uff0c\u5c06\u5165\u57ce\u65f6\uff0c\u5ffd\u89c1\u4e00\u7fa4\u5c71\u4e1c\u5927\u6c49\uff0c\u5206\u4e58\u51e0\u8f86\u5927 \u8f66\uff0c\u75be\u9a70\u51fa\u57ce\u3002\u9886\u7740\u8f66\u961f\u7684\u662f\u4e00\u9a91\u9ec4\u8198\u9a6c\uff0c\u9a6c\u4e0a\u4e00\u4e2a\u7d2b\u819b\u8138\u8272\u7684\u5927\u6c49\uff0c\u770b\u89c1\u5510\u6653\u6f9c\u4e09\u4eba\uff0c\u4f3c \u4e4e\u9887\u4e3a\u60ca\u6115\uff0c\u64e6\u8eab\u8fc7\u65f6\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u4e0a\u54ea\u513f\u53bb\uff1f\u201d\u738b\u9675\u9053\uff1a\u201c\u5b5f\u6d25\u63a2\u4eb2\u6765\u7684\uff01\u201d\u90a3\u5927 \u6c49\u5b50\u53c8\u9489\u7740\u95ee\u9053\uff1a\u201c\u4e0d\u662f\u4e0a\u592a\u884c\u5c71\u5417\uff1f\u201d\u738b\u9675\u6025\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4e0d\u662f\uff01\u201d\u90a3\u5927\u6c49\u5c1a\u5f85\u518d\u95ee\uff0c\u738b \u9675\u6025\u6025\u8fdb\u57ce\u3002\u8f66\u961f\u7684\u4eba\u50ac\u9053\uff1a\u201c\u5927\u54e5\uff01\u5feb\u8d70\u554a\uff01\u201d\u90a3\u5927\u6c49\u53cc\u8db3\u4e00\u5939\uff0c\u7b56\u9a6c\u524d\u5954\uff0c\u4f46\u8fd8\u662f\u56de\u5934 \u770b\u4e86\u738b\u9675\u51e0\u773c\u3002 \u3000\u3000\u5165\u5f97\u57ce\u6765\uff0c\u5510\u6653\u6f9c\u95ee\u9053\uff1a\u201c\u5e08\u5144\uff0c\u90a3\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u738b\u51cc\u9053\uff1a\u201c\u9c81\u897f\u5927\u8c6a\u5b5f\u5efa\u96c4\u3002\u201d\u5510\u6653 \u6f9c\u66fe\u542c\u5468\u9752\u8c08\u8fc7\u5929\u4e0b\u8c6a\u6770\uff0c\u77e5\u9053\u5b5f\u5efa\u96c4\u4e5f\u662f\u4e2a\u54cd\u5f53\u5f53\u7684\u811a\u8272\uff0c\u5584\u6253\u98de\u706b\u5f39\uff0c\u662f\u9c81\u897f\u7684\u6b66\u6797\u9886 \u8896\uff0c\u5fc3\u4e2d\u5947\u9053\uff1a\u201c\u4eca\u665a\u5317\u4e94\u7701\u8c6a\u6770\u5728\u592a\u884c\u5c71\u96c6\u4f1a\uff0c\u5b5f\u5efa\u96c4\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u53c2\u52a0\uff0c\u53cd\u800c\u4ece\u592a\u884c\u5c71\u90a3 \u9762\u8d70\u56de\u6765\uff0c\u800c\u4e14\u8fd9\u6837\u884c\u8272\u5306\u5306\uff01\u201d\u4e8e\u662f\u518d\u95ee\uff1a\u201c\u5b5f\u5efa\u96c4\u7b97\u5f97\u662f\u4e2a\u6b66\u6797\u4eba\u7269\uff0c\u5e08\u5144\u4e3a\u4ec0\u4e48\u4e0d\u5bf9 \u4ed6\u8bf4\u5b9e\u8bdd\uff1f\u201d\u738b\u9675\u9762\u8272\u7a81\u53d8\uff0c\u55e4\u7b11\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4e0d\u662f\u6211\u8bf4\u4f60\uff0c\u4f60\u6709\u591a\u5c11\u6c5f\u6e56\u9605\u5386\uff0c\u4fd7\u8bed\u8bf4\u5f97 \u597d\uff0c\u77e5\u4eba\u77e5\u9762\u4e0d\u77e5\u5fc3\uff0c\u4f55\u51b5\u54b1\u4eec\u4e0e\u5b5f\u5efa\u96c4\u53c8\u6ca1\u6709\u4ec0\u4e48\u4ea4\u60c5\uff0c\u600e\u597d\u968f\u4fbf\u5bf9\u4eba\u8bf4\u51fa\u771f\u8bdd\uff1f\u201d\u8bf4\u7f62 \u77a7\u4e86\u909d\u7ec3\u971e\u4e00\u773c\u3002 \u3000\u3000\u909d\u7ec3\u971e\u9053\uff1a\u201c\u5927\u5e08\u5144\u8bf4\u5f97\u662f\uff0c\u8c28\u614e\u4e00\u4e9b\uff0c\u6709\u5229\u65e0\u5bb3\u3002\u201d\u5510\u6653\u6f9c\u66f4\u662f\u751f\u7591\uff0c\u5728\u5b5f\u6d25\u8fd9\u665a\uff0c \u4ecd\u7136\u4e0d\u6562\u719f\u7761\uff0c\u5230\u5f97\u5929\u4eae\uff0c\u5e78\u559c\u65e0\u4e8b\u3002 \u3000\u3000\u7b2c\u56db\u65e5\u4ed6\u4eec\u5230\u4e86\u4fee\u6b66\uff0c\u8fd9\u662f\u4e00\u4e2a\u5c0f\u53bf\u53bf\u57ce\uff0c\u672c\u6765\u8fc7\u4e86\u5b5f\u6d25\uff0c\u5df2\u662f\u6e10\u5165\u5c71\u533a\uff0c\u4f46\u4e00\u8def\u4e0a\u4eba \u6765\u4eba\u5f80\uff0c\u751a\u4e3a\u70ed\u95f9\u3002\u738b\u9675\u4ed4\u7ec6\u7559\u795e\uff0c\u65f6\u4e0d\u65f6\u89c1\u6709\u6c5f\u6e56\u4eba\u7269\uff0c\u4e09\u4e09\u4e94\u4e94\uff0c\u8fce\u9762\u8d70\u8fc7\u3002\u738b\u9675\u6697\u81ea \u5fc3\u559c\uff0c\u5510\u6653\u6f9c\u5374\u77a7\u51fa\u60c5\u5f62\u6709\u70b9\u4e0d\u5bf9\u3002\u53ea\u662f\u909d\u7ec3\u971e\u4e00\u5411\u5c11\u51fa\u5bb6\u95e8\uff0c\u5374\u8fd8\u61f5\u7136\u4e0d\u77e5\u3002 \u3000\u3000\u8fd9\u665a\uff0c\u4ed6\u4eec\u5728\u4fee\u6b66\u4e00\u5bb6\u5ba2\u5e97\u6295\u5bbf\uff0c\u884c\u88c5\u752b\u5378\uff0c\u5ffd\u95fb\u5f97\u9694\u5ba4\u6709\u547b\u541f\u4e4b\u58f0\u3002\u5510\u6653\u6f9c\u5077\u5077\u5f20 \u671b\uff0c\u53ea\u89c1\u90bb\u623f\u7095\u4e0a\u8eba\u7740\u4e00\u4e2a\u75c5\u4eba\uff0c\u623f\u4e2d\u5750\u7740\u4e24\u4e2a\u6c49\u5b50\uff0c\u4e00\u4e2a\u5c11\u5973\u3002\u90a3\u5c11\u5973\u7709\u76ee\u5982\u753b\uff0c\u7a1a\u6c14\u672a \u6d88\uff0c\u6700\u591a\u53ea\u6709\u5341\u4e94\u516d\u5c81\u5e74\u7eaa\uff0c\u89c1\u5510\u6653\u6f9c\u63a2\u5934\u5f20\u671b\uff0c\u72e0\u72e0\u76ef\u4e86\u4e00\u773c\uff0c\u5544\u7740\u5c0f\u5634\u513f\u9053\uff1a\u201c\u5582\uff0c\u6709 \u4ec0\u4e48\u597d\u770b\u5440\uff01\u201d\u90a3\u4e24\u4e2a\u6c49\u5b50\u95fb\u58f0\u7ad9\u8d77\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u8bf7\u6765\u5750\u5750\u3002\u201d\u738b\u9675\u4f38\u624b\u4e00\u62c9\uff0c \u6ca1\u6709\u62c9\u7740\uff0c\u5510\u6653\u6f9c\u8fdb\u90bb\u623f\u53bb\u4e86\u3002 \u3000\u3000\u7095\u4e0a\u8eba\u7684\u679c\u7136\u662f\u4e2a\u75c5\u4eba\uff0c\u88ab\u8925\u4e0a\u8fd8\u9690\u9690\u6cbe\u6709\u8840\u8ff9\u3002\u5510\u6653\u6f9c\u8d70\u8fdb\uff0c\u7095\u4e0a\u4eba\u5ffd\u7136\u5750\u8d77\uff0c\u7adf\u662f \u4e2a\u9762\u8272\u7126\u9ec4\u7684\u5e72\u7626\u8001\u5934\uff0c\u53ef\u662f\u53cc\u76ee\u4e00\u5f20\uff0c\u70af\u70af\u6709\u795e\uff0c\u4ee4\u4eba\u751f\u754f\uff0c\u8fd9\u8001\u5934\u77a7\u4e86\u5510\u6653\u6f9c\u4e00\u9635\uff0c\u6447 \u4e86\u6447\u5934\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u771f\u4eba\u9762\u524d\u4e0d\u8bf4\u5047\u8bdd\uff0c\u4f60\u662f\u4e0a\u592a\u884c\u5c71\u53bb\u7684\u5417\uff1f\u201d\u5510\u6653\u6f9c\u4e0d\u77e5\u6b64\u4eba\u5e95\u7ec6\uff0c \u4e0d\u6562\u76f4\u8bf4\uff0c\u53cd\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u524d\u8f88\u662f\u4ece\u592a\u884c\u5c71\u6765\u7684\u5417\uff1f\u201d\u90a3\u67af\u7626\u8001\u5934\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u7a81\u7136\u4ece\u88ab \u7a9d\u91cc\u4f38\u51fa\u624b\u6765\uff0c\u5f80\u5510\u6653\u6f9c\u81c2\u4e0a\u4e00\u642d\uff0c\u5510\u6653\u6f9c\u4e0d\u89c9\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u8eab\u5b50\u77ee\u4e86\u534a\u622a\uff0c\u77aa\u773c\u95ee\u9053\uff1a \u201c\u8001\u524d\u8f88\uff0c\u8fd9\u662f\u5e72\u5417\uff1f\u201d\u90a3\u8001\u5934\u513f\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\uff0c\u653e\u4e86\u624b\u9053\uff1a\u201c\u8001\u592b\u60f3\u8d77\u5e8a\u8d70\u52a8\u8d70\u52a8\uff0c\u60f3\u53eb \u4f60\u6276\u6211\u4e00\u628a\uff0c\u90a3\u6599\u4f60\u5982\u6b64\u5f31\u4e0d\u7981\u98ce\uff01\u201d\u90a3\u5c11\u5973\u6025\u5fd9\u8fc7\u6765\u5c06\u8001\u4eba\u6276\u8d77\uff0c\u4f7f\u4e86\u4e2a\u773c\u8272\uff0c\u8001\u4eba\u53c8\u6447 \u6447\u5934\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u4e0d\u662f\u6c5f\u6e56\u4eba\u7269\u3002\u201d\u5510\u6653\u6f9c\u5fc3\u5934\u6709\u6c14\uff0c\u62f1\u624b\u544a\u8f9e\uff0c\u8001\u5934\u513f\u5728\u80cc\u540e\u8f7b\u58f0\u8bf4\u9053\uff1a \u201c\u6211\u4f46\u671b\u4ed6\u4e0d\u662f\u4e0a\u592a\u884c\u5c71\uff01\u51ed\u4ed6\u8fd9\u6837\u7684\u6b66\u529f\uff0c\u82e5\u4e0a\u5c71\u53bb\uff0c\u4e00\u767e\u6761\u5c0f\u547d\u4e5f\u4fdd\u4e0d\u4f4f\uff01\u201d\u8bed\u58f0\u6781 \u4f4e\uff0c\u5374\u5b57\u5b57\u6e05\u695a\uff0c\u597d\u50cf\u662f\u4e13\u8bf4\u7ed9\u5510\u6653\u6f9c\u542c\u4f3c\u7684\uff01 \u3000\u3000\u5510\u6653\u6f9c\u56de\u5230\u623f\u4e2d\uff0c\u53c8\u6c14\u53c8\u7591\uff0c\u4e0d\u77e5\u90a3\u8001\u5934\u513f\u662f\u53cb\u662f\u654c\u3002\u738b\u9675\u8dd1\u8fc7\u6765\u95ee\uff0c\u5510\u6653\u6f9c\u6015\u4ed6\u5632 \u7b11\uff0c\u4e0d\u6562\u628a\u81ea\u5df1\u5403\u4e8f\u4e4b\u4e8b\u8bf4\u51fa\uff0c\u53ea\u8bf4\u770b\u6765\u4f3c\u662f\u666e\u901a\u884c\u65c5\uff0c\u5ba2\u4e2d\u5c91\u5bc2\uff0c\u53eb\u81ea\u5df1\u8fc7\u53bb\u804a\u5929\u7684\u3002\u738b \u9675\u5634\u89d2\u5659\u7740\u51b7\u7b11\uff0c\u6ca1\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u8fd9\u665a\u5510\u6653\u6f9c\u53c8\u4e0d\u6562\u597d\u7761\uff0c\u5230\u4e86\u534a\u591c\uff0c\u5ffd\u7136\u542c\u5f97\u5916\u9762\u8f70\u7136\u4e00\u58f0\uff0c\u65c5\u5e97\u5927\u95e8\u7ed9\u4eba\u7528\u5de8\u7269\u649e \u7834\uff0c\u706b\u628a\u901a\u660e\uff0c\u5510\u6653\u6f9c\u8df3\u8d77\u6765\uff0c\u89c1\u5916\u9762\u51b2\u8fdb\u6765\u5341\u51e0\u540d\u5927\u6c49\uff0c\u4e24\u8fb9\u8e22\u95e8\u641c\u7d22\uff0c\u90bb\u623f\u4e0d\u5f85\u4eba\u6765\uff0c \u5148\u81ea\u5f00\u4e86\u3002\u90a3\u67af\u7626\u8001\u5934\u513f\u7531\u5c11\u5973\u6276\u7740\uff0c\u501a\u95e8\u53eb\u9053\uff1a\u201c\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\u5728\u6b64\uff0c\u522b\u6270\u522b\u4eba\u6e05 \u68a6\uff01\u201d\u90a3\u4f19\u6c49\u5b50\u53d1\u4e00\u58f0\u558a\uff0c\u7a81\u7136\u602a\u58f0\u547c\u5578\uff0c\u4e09\u4e2a\u94c1\u7403\uff0c\u95ea\u7535\u822c\u7684\u5411\u8001\u4eba\u98de\u53bb\uff0c\u5510\u6653\u6f9c\u9a87\u9053\uff1a \u201c\u8840\u6ef4\u5b50\uff01\u201d\u5ffd\u7136\u7709\u5934\u4e00\u7d27\uff0c\u738b\u9675\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u4e5f\u8d77\u4e86\u8eab\uff0c\u5c06\u5510\u6653\u6f9c\u80a9\u5934\u6309\u4f4f\u3002\u5510\u6653\u6f9c\u4f4e\u559d \u9053\uff1a\u201c\u5e72\u5417\uff1f\u201d\u738b\u9675\u5618\u58f0\u9053\uff1a\u201c\u4e0d\u8bb8\u4e71\u52a8\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u6c89\u80a9\u7f29\u8098\uff0c\u628a\u738b\u9675\u8fd9\u4e00\u64d2\u62ff\u624b\u89e3\u4e86\u3002\u738b\u9675\u9aa4\u5931\u91cd\u5fc3\uff0c\u51e0\u4e4e\u6454\u8de4\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u5510 \u5e08\u5f1f\uff0c\u4ed6\u4eec\u4eba\u591a\uff0c\u54b1\u4eec\u5f62\u8ff9\u672a\u9732\uff0c\u4e0d\u8981\u5f3a\u81ea\u51fa\u5934\uff0c\u5377\u5165\u6f29\u6da1\uff01\u201d\u5510\u6653\u6f9c\u201c\u5514\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3 \u60f3\uff1a\u539f\u6765\u5e08\u5144\u8fd8\u662f\u597d\u610f\uff0c\u4e14\u770b\u4e0b\u518d\u8bf4\u3002\u8fd9\u65f6\uff0c\u90a3\u4e09\u4e2a\u8840\u6ef4\u5b50\u5df2\u98de\u5230\u8001\u4eba\u5934\u4e0a\uff0c\u521a\u521a\u7f69\u4e0b\uff0c\u4e0d \u77e5\u600e\u7684\uff0c\u5ffd\u53c8\u5347\u7a7a\uff0c\u5012\u98de\u56de\u53bb\uff01\u53ea\u89c1\u90a3\u8001\u5934\u53cc\u638c\u4e00\u6536\uff0c\u81ea\u5c11\u5973\u624b\u4e2d\u63a5\u8fc7\u5f39\u5f13\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u5f13 \u5982\u6ee1\u6708\uff0c\u5f39\u4f3c\u6d41\u661f\uff0c\u628a\u90a3\u5e2e\u5bb6\u4f19\u6253\u5f97\u4e0d\u4ea6\u4e50\u4e4e\uff01\u90a3\u4f19\u4eba\u4e2d\uff0c\u7a81\u7136\u8df3\u51fa\u4e00\u4eba\uff0c\u5982\u733f\u7334\u7eb5\u8dc3\uff0c\u6234 \u9e7f\u76ae\u624b\u5957\uff0c\u7a9c\u9ad8\u7eb5\u4f4e\uff0c\u628a\u98de\u6765\u7684\u5f39\u5b50\u968f\u63a5\u968f\u63b7\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u795e\u5f39\u5df2\u7ecf\u89c1\u8fc7\uff0c\u518d\u9886\u6559\u4f60\u7684\u94c1 \u638c\uff01\u201d\u4e00\u7eb5\u8eab\uff0c\u5230\u4e86\u8001\u4eba\u8ddf\u524d\uff0c\u53cc\u81c2\u4e00\u9707\uff0c\u8001\u5934\u8eab\u65c1\u90a3\u4e24\u540d\u6c49\u5b50\u8e09\u8e09\u8dc4\u8dc4\u9000\u4e86\u51e0\u6b65\uff0c\u8001\u4eba\u53cd \u624b\u4e00\u638c\uff0c\u5176\u75be\u5982\u7535\uff0c\u6c49\u5b50\u53cc\u62f3\u9f50\u51fa\uff0c\u521a\u521a\u62b5\u4f4f\uff0c\u8001\u4eba\u5de6\u638c\u7a81\u7136\u7a7f\u51fa\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u51fb\u4e2d\u4ed6\u7684\u5de6 \u80ef\uff0c\u559d\u9053\uff1a\u201c\u5012\u4e0b\u201d\uff0c\u90a3\u6c49\u5b50\u6447\u6447\u6446\u6446\uff0c\u9000\u51fa\u51e0\u6b65\uff0c\u56de\u5934\u53eb\u9053\uff1a\u201c\u94c1\u638c\u4e5f\u9886\u6559\u8fc7\u4e86\uff0c\u504f\u4e0d\u5982 \u4f60\u6240\u613f\uff0c\u5e76\u80a9\u5b50\u4e0a\u554a\uff0c\u6d3b\u6349\u8fd9\u4e2a\u8001\u8d3c\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u770b\u5f97\u76ee\u7729\u5fc3\u60ca\uff0c\u4ed6\u542c\u5468\u9752\u8bf4\u8fc7\uff0c\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\u662f\u5317\u4e94\u7701\u7b2c\u4e00\u540d\u8c6a\u6770\uff0c\u6b66\u529f\u6280\u827a \u5728\u4ed6\u4e4b\u4e0a\u3002\u4e0d\u77e5\u600e\u7684\u4e5f\u53d7\u4e86\u4f24\uff1f\u800c\u4e14\u4eca\u65e5\u6b63\u5f53\u592a\u884c\u5c71\u4e94\u7701\u8c6a\u6770\u4e4b\u4f1a\uff0c\u4ed6\u4e3a\u4f55\u5374\u5728\u6b64\u5730\uff1f\u800c\u53d7 \u4ed6\u8fd9\u638c\u7684\u6c49\u5b50\uff0c\u5e76\u672a\u8dcc\u5012\uff0c\u6b66\u529f\u663e\u7136\u4e5f\u6781\u6df1\u6e5b\uff0c\u8fd9\u65f6\u4e24\u8fb9\u5df2\u6210\u7fa4\u6bb4\uff0c\u90a3\u540d\u4e0e\u6768\u4ef2\u82f1\u5bf9\u654c\u7684\u6c49 \u5b50\uff0c\u8eab\u6cd5\u6b65\u6cd5\uff0c\u65e0\u4e00\u4e0d\u602a\uff0c\u7075\u6377\u5f02\u5e38\u3002\u6768\u4ef2\u82f1\u5374\u4f3c\u53d7\u4e86\u91cd\u4f24\uff0c\u8f6c\u52a8\u4e0d\u4fbf\uff0c\u53cc\u8db3\u9489\u5728\u5730\u4e0a\uff0c\u5982 \u6cf0\u5c71\u5140\u7acb\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u638c\u98ce\u547c\u547c\uff0c\u5468\u56f4\u516b\u5c3a\u4e4b\u5185\uff0c\u654c\u4eba\u4e0d\u6562\u8fd1\u8eab\uff01\u90a3\u5c11\u5973\u4e00\u53e3\u67f3\u53f6\u5200\uff0c\u4e0d\u79bb \u8001\u4eba\u5de6\u53f3\uff0c\u5200\u5149\u95ea\u70c1\uff0c\u8f7b\u7075\u7fd4\u52a8\uff0c\u4f7f\u51fa\u6765\u7684\uff0c\u7adf\u662f\u6781\u4e0a\u4e58\u7684\u5200\u6cd5\u3002\u6768\u4ef2\u82f1\u5e73\u751f\u4e0e\u4eba\u5bf9\u654c\uff0c\u65e0 \u4eba\u80fd\u4ee5\u8840\u8089\u4e4b\u8eaf\u53d7\u4ed6\u4e00\u638c\uff0c\u800c\u4eca\u638c\u51fb\u654c\u4eba\u4e0d\u5012\uff0c\u4e5f\u81ea\u5fc3\u60ca\uff0c\u6218\u4e86\u7247\u523b\uff0c\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u6765\u4eba\u53ef \u662f\u516b\u81c2\u795e\u9b54\u95e8\u4e0b\uff1f\u201d\u90a3\u6c49\u5b50\u602a\u7b11\u5e94\u9053\uff1a\u201c\u4f60\u5728\u6211\u5e08\u5085\u638c\u4e0b\u9003\u751f\uff0c\u504f\u504f\u53c8\u649e\u5728\u6211\u7684\u624b\u4e0a\uff0c\u8fd8\u6709 \u4f55\u8bdd\u53ef\u8bf4\uff1f\u94c1\u638c\u795e\u5f39\uff0c\u4eca\u65e5\u662f\u4f60\u7684\u6b7b\u671f\u5230\u4e86\uff01\u201d\u731b\u7136\u6251\u4e0a\u75be\u653b\u3002\u6768\u4ef2\u82f1\u547c\u3001\u547c\u3001\u547c\u8fde\u73af\u53d1 \u638c\uff0c\u90a3\u6c49\u5b50\u8eab\u5f62\u5feb\u6781\uff0c\u4e00\u95ea\u53c8\u4e0a\uff0c\u4e0d\u6559\u638c\u950b\u78b0\u7740\uff0c\u770b\u60c5\u5f62\u662f\u60f3\u7d2f\u6b7b\u4ed6\u3002\u6768\u4ef2\u82f1\u5403\u4e8f\u5728\u4e0d\u80fd\u8f6c \u52a8\uff0c\u529f\u529b\u867d\u7136\u5728\u90a3\u6c49\u5b50\u4e4b\u4e0a\uff0c\u5374\u662f\u65e0\u6cd5\u8ffd\u51fb\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u5de6\u638c\u865a\u626c\uff0c\u90a3\u6c49\u5b50\u5f80\u65c1\u4e00\u95ea\uff0c\u6768\u4ef2 \u82f1\u7a81\u8de8\u524d\u4e00\u6b65\uff0c\u53f3\u638c\u547c\u7684\u6253\u51fa\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u628a\u56f4\u653b\u5c11\u5973\u7684\u4e00\u4e2a\u654c\u4eba\uff0c\u6253\u98de\u51fa\u4e09\u4e08\u5f00\u5916\uff01\u56f4\u653b \u7684\u4eba\u4e00\u9635\u5927\u4e71\uff0c\u90a3\u6c49\u5b50\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u653b\u90a3\u5973\u5a03\u5b50\u4e0b\u76d8\uff01\u201d\u51cc\u7a7a\u4e0b\u8dc3\uff0c\u4e00\u6293\u5411\u8001\u5934\u6293\u4e0b\uff0c\u6768\u4ef2 \u82f1\u51b2\u5929\u4e00\u62f3\uff0c\u90a3\u6c49\u5b50\u8f7b\u98d8\u98d8\u7684\u843d\u5728\u5de6\u4fa7\uff0c\u6a2a\u811a\u4e00\u626b\uff0c\u5c11\u5973\u4e0b\u76d8\u4e0d\u7a33\uff0c\u7ed9\u903c\u9000\u51e0\u6b65\uff0c\u654c\u4eba\u767b\u65f6 \u8702\u6d8c\u800c\u4e0a\uff0c\u628a\u5c11\u5973\u548c\u8001\u4eba\u5206\u9694\u5f00\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5973\u548c\u5979\u7684\u4e24\u4e2a\u5e08\u5144\u4e00\u8d77\uff0c\u5e94\u4ed8\u5f3a\u654c\uff0c\u9669\u8c61\u73af\u751f\u3002\u6768\u4ef2\u82f1\u53c8\u88ab\u90a3\u4e2a\u4ec0\u4e48\u516b\u81c2\u795e\u9b54\u7684\u95e8 \u4eba\u7eca\u4f4f\uff0c\u79fb\u52a8\u4e0d\u5f97\uff0c\u6fc0\u6218\u4e2d\u6768\u4ef2\u82f1\u53d7\u4e86\u654c\u4eba\u4e00\u6293\uff0c\u53f3\u80a9\u9c9c\u8840\u6dcb\u6f13\uff0c\u7adf\u88ab\u6495\u53bb\u4e00\u5757\u76ae\u8089\u3002\u5e78\u5f97 \u90a3\u540d\u6c49\u5b50\u521a\u624d\u53d7\u4ed6\u4e00\u638c\uff0c\u529f\u529b\u4ea6\u5df2\u5927\u51cf\uff0c\u8981\u4e0d\u7136\u8fd9\u4e00\u6293\u4fbf\u662f\u5f00\u819b\u7834\u8179\u4e4b\u707e\u3002\u90a3\u5c11\u5973\u60ca\u53eb\u4e00 \u58f0\uff0c\u51e0\u4e4e\u4e2d\u4e86\u654c\u4eba\u4e00\u5200\u3002\u6768\u4ef2\u82f1\u53eb\u9053\uff1a\u201c\u9752\u513f\uff0c\u7528\u65cb\u98ce\u626b\u53f6\u4e94\u864e\u65ad\u95e8\u5200\uff01\u201d\u5c11\u5973\u58f0\u4eba\u5fc3\u901a\uff0c \u62db\u6570\u970d\u53d8\uff0c\u5200\u5149\u95ea\u95ea\uff0c\u81ea\u4e0b\u6311\u4e0a\uff0c\u62a4\u7740\u4e0b\u76d8\u3002\u90a3\u51e0\u540d\u654c\u4eba\u672c\u6765\u662f\u6b3a\u5979\u4e0b\u76d8\u4e0d\u7a33\u7684\uff0c\u7ed9\u5979\u8fd9\u8def \u5200\u6cd5\u4f7f\u5f00\uff0c\u7adf\u7136\u4e0d\u80fd\u5f97\u624b\u3002\u53c8\u6218\u4e86\u4e00\u9635\uff0c\u6768\u4ef2\u82f1\u5927\u53eb\u4e00\u58f0\uff0c\u5de6\u80a9\u53c8\u7ed9\u6293\u88c2\uff0c\u989d\u4e0a\u6c57\u73e0\uff0c\u7adf\u4f3c \u9ec4\u8c46\u822c\u5927\u5c0f\uff0c\u76f4\u6dcc\u51fa\u6765\u3002 \u3000\u3000\u5510\u6653\u6f9c\u672c\u6765\u5bf9\u90a3\u8001\u5934\u751a\u4e3a\u4e0d\u6ee1\uff0c\u4f46\u77e5\u9053\u4ed6\u5c31\u662f\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\uff0c\u89c2\u611f\u987f\u6539\uff0c\u656c\u610f\u6cb9\u7136\u800c \u751f\u3002\u4e00\u5927\u628a\u98de\u8292\u6263\u5728\u638c\u5fc3\uff0c\u6697\u8fd0\u5185\u529b\uff0c\u634f\u788e\u7a97\u6838\uff0c\u53cc\u638c\u8fde\u626c\uff0c\u53f3\u624b\u98de\u8292\uff0c\u6253\u90a3\u6c49\u5b50\uff0c\u5de6\u624b\u98de \u8292\uff0c\u6253\u56f4\u653b\u5c11\u5973\u7684\u654c\u4eba\uff0c\u98de\u8292\u4efd\u91cf\u6781\u8f7b\uff0c\u4ed6\u5728\u53cc\u65b9\u6fc0\u6218\u4e2d\u9aa4\u7136\u53d1\u51fa\uff0c\u53ea\u542c\u5f97\u54d7\u7136\u547c\u53eb\uff0c\u6709\u4e24 \u4eba\u4f3c\u7ed9\u98de\u8292\u6253\u4e2d\uff0c\u5728\u5730\u4e0a\u7ffb\u7ffb\u6eda\u6eda\uff0c\u5176\u4f59\u7684\u7eb7\u7eb7\u6563\u5f00\uff0c\u5927\u58f0\u559d\u9a82\uff1a\u201c\u4f55\u65b9\u5c0f\u5b50\uff0c\u80c6\u6562\u5077\u653e\u6697 \u5668\uff1f\u201d\u90a3\u6c49\u5b50\u6b66\u529f\u6df1\u6e5b\uff0c\u867d\u5728\u5267\u6218\u4e4b\u4e2d\uff0c\u4ecd\u662f\u773c\u89c2\u516d\u8def\uff0c\u8033\u542c\u516b\u65b9\u3002\u98de\u8292\u7834\u7a7a\u4e4b\u58f0\uff0c\u867d\u6781\u5fae \u5c0f\uff0c\u4ed6\u5df2\u51db\u7136\u8b66\u89c9\uff0c\u5728\u4f17\u4eba\u7eb7\u53eb\u4e2d\uff0c\u7a81\u7136\u5012\u7eb5\u51fa\u6765\uff0c\u5411\u5510\u6653\u6f9c\u85cf\u8eab\u4e4b\u5904\u6251\u53bb\uff01 \u3000\u3000\u738b\u9675\u4e0d\u77e5\u662f\u5510\u6653\u6f9c\u5077\u653e\u6697\u5668\uff0c\u89c1\u72b6\u5927\u60ca\uff0c\u6025\u5fd9\u5411\u5899\u89d2\u4e00\u7f29\uff0c\u5510\u6653\u6f9c\u626c\u624b\u53c8\u662f\u4e00\u628a\u98de\u8292\uff0c \u90a3\u540d\u6c49\u5b50\u78d4\u78d4\u602a\u7b11\uff0c\u888d\u8896\u4e00\u62e9\uff0c\u98de\u8292\u7a81\u7136\u53cd\u5c04\u8fc7\u6765\u3002\u5510\u6653\u6f9c\u5fd9\u5373\u4f0f\u8eab\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u4e4b\u58f0\uff0c\u54cd \u4e2a\u672a\u505c\uff01\u98de\u8292\u7adf\u7136\u90fd\u4ece\u7a97\u683c\u6253\u5165\uff0c\u649e\u5728\u5bf9\u9762\u5899\u58c1\u4e4b\u4e0a\u3002\u8bf4\u65f6\u8fdf\u90a3\u65f6\u5feb\uff0c\u7b11\u58f0\u672a\u505c\uff0c\u548b\u54b3\u4e00 \u58f0\uff0c\u7a97\u6846\u5df2\u65ad\uff0c\u4e00\u53ea\u84b2\u6247\u822c\u7684\u5927\u624b\u4f38\u4e86\u8fdb\u6765\uff0c\u638c\u98ce\u98d2\u7136\uff0c\u5510\u6653\u6f9c\u5934\u76ae\u53c8\u51b7\u53c8\u9ebb\uff0c\u770b\u770b\u5c31\u8981\u6293 \u5230\u5934\u4e0a\u3002\u5510\u6653\u6f9c\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u6eda\u5f00\u4e08\u4f59\u3002\u5c31\u5728\u6b64\u9645\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u6709\u4eba\u9a82\u9053\uff1a\u201c\u4e0d\u8981 \u8138\uff0c\u6b3a\u8d1f\u5c0f\u8f88\uff01\u201d\u63a5\u7740\u84ec\u84ec\u4e24\u58f0\uff0c\u5982\u5de8\u6728\u76f8\u649e\uff0c\u602a\u624b\u4e0d\u89c1\uff0c\u559d\u58f0\u6e10\u8fdc\u3002\u5510\u6653\u6f9c\u5927\u7740\u80c6\uff0c\u7ad9\u4e86 \u8d77\u6765\uff0c\u5f20\u76ee\u5077\u7aa5\uff0c\u53ea\u89c1\u5916\u9762\u5ffd\u7136\u6dfb\u4e86\u4e24\u4e2a\u602a\u5ba2\uff0c\u4e00\u4eba\u53c8\u7626\u53c8\u9ad8\uff0c\u773c\u73e0\u767d\u6e17\u6e17\u7684\uff1b\u4e00\u4eba\u53c8\u80a5\u53c8 \u77ee\u53ea\u6709\u4e09\u5c3a\u6765\u9ad8\uff0c\u4e24\u4eba\u53cc\u624b\u7a7a\u7a7a\uff0c\u76ef\u7740\u90a3\u540d\u6c49\u5b50\uff0c\u5730\u4e0a\u6a2a\u4e03\u7ad6\u516b\u7684\u5012\u4e0b\u4e86\u4e94\u516d\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u77e5 \u662f\u7ed9\u4e00\u8fd9\u4e24\u4e2a\u602a\u5ba2\u6253\u5012\u7684\u8fd8\u662f\u7ed9\u6768\u4ef2\u82f1\u7684\u638c\u529b\u9707\u5012\u7684\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u602a\u53eb\u4e00\u58f0\uff0c\u558a\u9053\uff1a\u201c\u4f60\u4eec\u5173\u4e1c\u56db\u4fa0\uff0c\u7adf\u7136\u4e5f\u6765\u8d9f\u8fd9\u6dcc\u6d51\u6c34\uff01\u90a3\u662f\u4f60\u4eec\u7684\u6b7b\u671f\u5230 \u4e86\uff01\u201d\u540e\u6765\u7684\u90a3\u77ee\u5b50\u563b\u563b\u7b11\u9053\uff1a\u201c\u6211\u4eec\u56db\u5144\u5f1f\u5929\u4e0d\u6015\uff0c\u5730\u4e0d\u6015\uff0c\u8fde\u8001\u9b54\u5934\u6211\u4eec\u4e5f\u8981\u4f1a\u4ed6\u4e00 \u4f1a\uff0c\u4f55\u60e7\u4f60\u8fd9\u4e2a\u5c0f\u5352\uff01\u8463\u592a\u6e05\uff0c\u4f60\u53eb\u4f60\u7684\u5e08\u5085\u5e08\u53d4\u51fa\u6765\uff0c\u6211\u4eec\u624b\u4e0b\uff0c\u4e0d\u6740\u65e0\u540d\u4e4b\u8f88\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u8fd9\u624d\u77e5\u9053\u521a\u624d\u4e0e\u6768\u4ef2\u82f1\u6076\u6597\u7684\u90a3\u4e2a\u6c49\u5b50\u53eb\u8463\u592a\u6e05\uff0c\u6697\u60f3\u8463\u592a\u6e05\u4e0d\u77e5\u662f\u4f55\u7b49\u6837\u4eba\uff0c \u5468\u4f2f\u4f2f\u5e73\u65f6\u7eb5\u8c08\u6b66\u6797\u4eba\u7269\uff0c\u4ece\u672a\u63d0\u8d77\u6b64\u4eba\uff0c\u770b\u4ed6\u6b66\u529f\u5df2\u4e0d\u5728\u5468\u4f2f\u4f2f\u4e4b\u4e0b\uff0c\u8fd9\u4e24\u4e2a\u4eba\u72b9\u81ea\u79f0\u4ed6 \u201c\u65e0\u540d\u5c0f\u5352\u201d\uff0c\u90a3\u4e48\u4ed6\u4eec\u7684\u6b66\u529f\u4e00\u5b9a\u66f4\u52a0\u6df1\u4e0d\u53ef\u6d4b\u4e86\u3002\u8463\u592a\u6e05\u79f0\u4ed6\u4eec\u4e3a\u5173\u4e1c\u56db\u4fa0\uff0c\u800c\u6765\u7684\u53ea \u662f\u4e24\u4eba\uff0c\u60f3\u5fc5\u8fd8\u6709\u4e24\u4e2a\u672a\u5230\u3002 \u3000\u3000\u8463\u592a\u6e05\u78d4\u78d4\u602a\u7b11\uff0c\u60f3\u662f\u6012\u6781\u6c14\u6781\uff0c\u4e00\u4f38\u624b\u5c31\u5411\u90a3\u9ad8\u4e2a\u5b50\u6293\u6765\uff0c\u9ad8\u4e2a\u5b50\u53eb\u9053\uff1a\u201c\u56db\u5f1f\uff0c\u4f60 \u628a\u90a3\u4e9b\u4eba\u6254\u51fa\u53bb\u3002\u201d\u84e6\u5730\u4e00\u58f0\u957f\u5578\uff0c\u5341\u6307\u4f38\u51fa\uff0c\u6bcf\u53ea\u624b\u6307\u90fd\u6234\u7740\u4e00\u679a\u94a2\u73af\uff0c\u8463\u592a\u6e05\u6293\u5230\u534a \u9014\uff0c\u6025\u5fd9\u7f29\u56de\uff0c\u8eab\u8eaf\u4e00\u77ee\uff0c\u53d8\u6293\u4e3a\u638c\uff0c\u653b\u4ed6\u4e0b\u76d8\u3002\u90a3\u4eba\u8eab\u957f\u624b\u957f\uff0c\u53cc\u624b\u5411\u4e0b\u4e00\u6309\uff0c\u4e24\u4eba\u95ea\u7535 \u822c\u62c6\u4e86\u51e0\u62db\uff0c\u8463\u592a\u6e05\u7a81\u7136\u602a\u53eb\u4e00\u58f0\uff0c\u8e72\u5728\u5730\u4e0a\uff0c\u76d8\u9f99\u7ed5\u6b65\uff0c\u5feb\u4f3c\u98ce\u8f66\uff0c\u7f29\u6210\u4e00\u56e2\u7070\u5f71\uff0c\u4e13\u653b \u654c\u4eba\u7684\u4e0b\u4e09\u8def\uff0c\u62db\u6570\u602a\u7edd\u3002\u90a3\u9ad8\u4e2a\u5b50\u5982\u661f\u4e5d\u8df3\u63b7\uff0c\u95ea\u8f6c\u817e\u632a\uff0c\u8463\u592a\u6e05\u653b\u5f97\u6025\uff0c\u4ed6\u4e5f\u8df3\u5f97\u5feb\uff0c \u4e24\u4eba\u6253\u5f97\u96be\u89e3\u96be\u5206\u3002 \u3000\u3000\u8463\u592a\u6e05\u77e5\u9053\u654c\u4eba\u6b66\u529f\u975e\u540c\u5c0f\u53ef\uff0c\u6b3a\u4ed6\u8eab\u957f\uff0c\u6240\u4ee5\u7528\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\u7684\u8eab\u6cd5\u3001\u6293\u811a\u8e22\u88c6\uff0c\u653b \u4ed6\u5f31\u70b9\u3002\u90a3\u6599\u6765\u4eba\u8f7b\u529f\uff0c\u8fd8\u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c\u800c\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\u7684\u5730\u5802\u529f\u592b\uff0c\u53c8\u4e0d\u80fd\u6301\u4e45\uff0c\u6697\u6697\u5fc3 \u6025\u3002\u8fd9\u65f6\u53ea\u542c\u5f97\u5ba2\u5e97\u91cc\u9b3c\u54ed\u795e\u568e\uff0c\u60e8\u53eb\u4e4b\u58f0\uff0c\u54cd\u6210\u4e00\u7247\u3002\u90a3\u77ee\u5b50\u5927\u5c55\u795e\u5a01\uff0c\u6216\u5288\u6216\u6293\uff0c\u638c\u5982 \u5954\u96f7\uff0c\u6293\u4f3c\u94c1\u94a9\uff0c\u8840\u6ef4\u5b50\u56db\u4e0b\u5954\u9003\u3002\u77ee\u5b50\u529b\u5927\u5f02\u5e38\uff0c\u5916\u5bb6\u529f\u592b\uff0c\u7adf\u4f3c\u767b\u5cf0\u9020\u6781\uff0c\u6293\u7740\u654c\u4eba\u5f80 \u5916\u4fbf\u6454\uff0c\u4e00\u624b\u4e00\u4e2a\uff0c\u72b9\u5982\u6349\u5c0f\u9e21\u4e00\u822c\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u90a3\u6279\u8840\u6ef4\u5b50\u7adf\u7ed9\u4ed6\u4e00\u4e2a\u4e2a\u6254\u4e86\u51fa\u53bb\u3002\u8463\u592a \u6e05\u84e6\u5730\u7eb5\u8eab\uff0c\u865a\u51fb\u4e00\u638c\uff0c\u9ad8\u4e2a\u5b50\u53c8\u662f\u4e00\u58f0\u957f\u5578\uff0c\u53cc\u638c\u9f50\u626c\uff0c\u5957\u5728\u6307\u4e0a\u7684\u5341\u53ea\u94a2\u73af\uff0c\u4e00\u9f50\u98de \u51fa\u3002\u638c\u98ce\u73af\u5f71\u4e2d\uff0c\u8463\u592a\u6e05\u5389\u53eb\u4e00\u58f0\uff0c\u84e6\u5730\u4e00\u4e2a\u7b4b\u6597\u7ffb\u4e86\u51fa\u53bb\u3002\u77ee\u5b50\u5c1a\u5f85\u5916\u8ffd\uff0c\u9ad8\u4e2a\u5b50\u53eb\u9053\uff1a \u201c\u4ed6\u4e2d\u4e86\u6211\u4e09\u679a\u94a2\u73af\uff0c\u6253\u6b63\u7a74\u9053\uff0c\u5c1a\u80fd\u9003\u8d70\uff0c\u4e5f\u7b97\u5f97\u662f\u4e2a\u597d\u624b\uff0c\u7531\u4ed6\u53bb\u5427\u3002\u201d\u5173\u4e1c\u56db\u4fa0\uff0c\u51fa \u9053\u4ee5\u6765\uff0c\u82e5\u654c\u4eba\u80fd\u5728\u4ed6\u4eec\u7684\u72ec\u95e8\u7edd\u6280\u4e4b\u4e0b\u9003\u51fa\uff0c\u5012\u4e0d\u8d76\u5c3d\u6740\u7edd\u3002\u77ee\u5b50\u505c\u8eab\u6b62\u6b65\uff0c\u5f20\u76ee\u8bf4\u9053\uff1a \u201c\u90a3\u8001\u602a\u7684\u5f92\u5f1f\u4e5f\u6709\u8fd9\u4e48\u5389\u5bb3\u4e48\uff1f\u201d\u9ad8\u4e2a\u5b50\u9053\uff1a\u201c\u56db\u5f1f\uff0c\u4e8b\u5230\u5982\u4eca\uff0c\u53ea\u6709\u5c3d\u529b\u800c\u4e3a\u4e86\uff01\u201d\u8bed \u6c14\u4e4b\u95f4\u4f3c\u6709\u91cd\u5fe7\uff01 \u3000\u3000\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\u91cd\u4f24\u4e4b\u540e\uff0c\u53c8\u7ecf\u8fc7\u4e00\u8f6e\u6fc0\u6218\uff0c\u9762\u8272\u60e8\u767d\uff0c\u6447\u6447\u6b32\u5012\uff0c\u5401\u58f0\u8bf4\u9053\uff1a\u201c\u4e09\u54e5 \u56db\u54e5\uff0c\u8001\u673d\u4e0d\u6d4e\uff0c\u7d2f\u4f60\u4eec\u7ed3\u4e0b\u5f3a\u4ec7\uff0c\u5982\u4f55\u662f\u597d\uff1f\u201d\u77ee\u5b50\u9053\uff1a\u201c\u6768\u5927\u54e5\uff0c\u4f60\u6211\u795e\u4ea4\u5df2\u4e45\uff0c\u4eca\u65e5 \u89c1\u9762\uff0c\u4f55\u5e78\u5982\u4e4b\uff01\u6b6a\u98ce\u5927\u54e5\u6258\u6211\u4eec\u95ee\u5019\u4f60\uff0c\u5e76\u66ff\u4f60\u5e26\u4e86\u89e3\u836f\u3002\u4ed6\u8bf4\u592a\u884c\u5c71\u4f1a\u540e\uff0c\u82e5\u8fd8\u80fd\u7559\u7740 \u9ab8\u9aa8\uff0c\u5f53\u518d\u5230\u5b9d\u5e84\u62dc\u8bbf\u3002\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u591a\u8c22\u4f60\u4eec\u5927\u54e5\u6545\u4eba\u60c5\u91cd\uff0c\u53ea\u662f\u4f60\u4eec\u53ef\u771f\u5f97\u5f53\u5fc3 \u70b9\uff01\u201d\u9ad8\u4e2a\u5b50\u62f1\u624b\u9053\uff1a\u201c\u77e5\u9053\u4e86\uff01\u6768\u5927\u54e5\uff0c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u4f60\u5feb\u8d70\uff01\u201d\u4e0a\u524d\u6276\u6768\u4ef2\u82f1\uff0c\u90a3\u5c11\u5973\u548c \u4e24\u4e2a\u5927\u6c49\uff0c\u90fd\u6014\u6014\u7684\u770b\u7740\u4ed6\u4eec\u3002\u738b\u9675\u8fd9\u65f6\u60ca\u9b42\u65b9\u5b9a\uff0c\u521a\u521a\u7ad9\u8d77\uff0c\u5ffd\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u4e00\u80a1\u51b7\u98ce\u5439 \u4e86\u8fdb\u6765\u3002 \u3000\u3000\u738b\u9675\u53eb\u9053\uff1a\u201c\u5510\u5e08\u5f1f\uff0c\u5510\u5e08\u5f1f\uff01\u201d\u5510\u6653\u6f9c\u4e5f\u89c9\u52b2\u98ce\u6251\u9762\uff0c\u6025\u56de\u5934\u65f6\uff0c\u4ec0\u4e48\u4e5f\u6ca1\u6709\u89c1\u7740\u3002 \u95e8\u5916\u5ffd\u7136\u4f20\u6765\u94f6\u94c3\u822c\u6e05\u8106\u7684\u7b11\u58f0\uff0c\u90a3\u5c11\u5973\u626c\u624b\u8bf4\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u591a\u8c22\u4f60\u4e86\uff01\u201d\u5510\u6653\u6f9c\u518d\u5f20\u671b \u65f6\uff0c\u6768\u4ef2\u82f1\u90a3\u4e00\u4f19\u4eba\u548c\u90a3\u5173\u4e1c\u4e24\u4fa0\uff0c\u5168\u90fd\u8d70\u4e86\uff01 \u3000\u3000\u6fc0\u6218\u8fc7\u540e\uff0c\u5ba2\u5e97\u91cc\u7684\u4eba\u624d\u614c\u4e71\u8d77\u6765\uff0c\u7eb7\u7eb7\u6253\u5f00\u623f\u95e8\u63a2\u671b\u3002\u909d\u7ec3\u971e\u5728\u5367\u5ba4\u5185\u53eb\u9053\uff1a\u201c\u738b\u5e08 \u5144\uff0c\u5510\u5e08\u5f1f\uff0c\u5feb\u6765\uff01\u5feb\u6765\uff01\u201d\u738b\u9675\u548c\u5510\u6653\u6f9c\u8fdb\u5165\u5185\u5ba4\uff0c\u53ea\u89c1\u909d\u7ec3\u971e\u62b1\u7740\u51af\u745b\uff0c\u5750\u5728\u5e8a\u4e0a\uff0c\u9762 \u8272\u60ca\u60f6\u3002\u51af\u745b\u821e\u7740\u4e00\u53cc\u5c0f\u624b\uff0c\u5440\u5440\u7684\u54ed\u4e86\u8d77\u6765\u3002\u738b\u9675\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u6ca1\u5413\u7740\u4e48\uff1f\u201d\u90ae\u7ec3 \u971e\u6307\u7740\u684c\u9762\u9053\uff1a\u201c\u4f60\u4eec\u770b\uff01\u201d\u684c\u4e0a\u4e00\u67c4\u5315\u9996\uff0c\u9489\u7740\u4e00\u5f20\u5b57\u6761\uff0c\u5199\u9053\uff1a\u201c\u901f\u8d70\u56de\u5934\u8def\uff0c\u83ab\u4e0a\u592a \u884c\u5c71\uff01\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u7559\u5b57\u7684\u4eba\u662f\u756a\u597d\u610f\uff0c\u82e5\u4ed6\u60f3\u4f24\u5bb3\u6211\u4eec\uff0c\u6211\u4eec\u8fd8\u6709\u547d\u5417\uff1f\u5ac2\u5ac2\u4e0d\u8981\u62c5\u5fc3 \u5bb3\u6015\uff01\u201d\u909d\u7ec3\u971e\u9053\uff1a\u201c\u6211\u7684\u516c\u516c\u548c\u4e08\u592b\u5168\u90fd\u6b7b\u4e86\uff0c\u6211\u8fd8\u5bb3\u6015\u4ec0\u4e48\uff0c\u53ea\u662f\u4e24\u4e2a\u5973\u5a03\u5982\u6b64\u53ef\u7231\uff0c \u6211\u600e\u6837\u4e5f\u5f97\u628a\u5979\u4eec\u517b\u5927\u5440\uff01\u201d\u51af\u745b\u5341\u5206\u4e56\u5de7\uff0c\u521a\u624d\u5916\u95f4\u6fc0\u6218\u4e4b\u65f6\uff0c\u6bcd\u4eb2\u628a\u5979\u7d27\u7d27\u62b1\u7740\uff0c\u5979\u770b \u7740\u6bcd\u4eb2\u7684\u8138\u8272\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u800c\u4eca\u770b\u89c1\u6bcd\u4eb2\u8138\u8272\u96be\u770b\uff0c\u8fd9\u624d\u54ed\u4e86\u8d77\u6765\u3002\u909d\u7ec3\u971e\u8f7b\u543b\u5979\u7684\u82f9\u679c\u9762 \u988a\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5b9d\u8d1d\uff0c\u522b\u54ed\uff0c\u522b\u54ed\uff0c\u5988\u5988\u5728\u8fd9\u91cc\u5462\uff01\u201d\u51af\u745b\u4e00\u5bf9\u5b9d\u77f3\u822c\u7684\u5c0f\u773c\u775b\u6ef4\u6e9c\u6e9c\u7684\u77a7 \u7740\u5979\u7684\u6bcd\u4eb2\uff0c\u89c1\u6bcd\u4eb2\u7b11\u4e86\uff0c\u5979\u4e5f\u505c\u54ed\u7b11\u4e86\u3002\u5510\u6653\u6f9c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8d70\u51fa\u5916\u5802\uff0c\u53ea\u89c1\u5899\u58c1\u4e0a\u4eae\u6676\u6676 \u7684\u4e5f\u63d2\u7740\u4e00\u67c4\u5315\u9996\uff0c\u909d\u7ec3\u971e\u62b1\u7740\u51af\u745b\u8ddf\u4e86\u51fa\u6765\uff0c\u95ee\u9053\uff1a\u201c\u5510\u5e08\u5f1f\uff0c\u4ec0\u4e48\u4e8b\uff1f\u201d\u5f20\u773c\u89c1\u7740\u90a3\u67c4 \u5315\u9996\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u5510\u6653\u6f9c\u5c06\u90a3\u67c4\u5315\u9996\u62d4\u4e86\u4e0b\u6765\uff0c\u5315\u9996\u5c16\u4e5f\u7a7f\u7740\u4e00\u5f20\u5b57\u6761\uff0c\u909d\u7ec3\u971e\u5c06\u90a3\u5b57\u6761\u626f \u4e86\u4e0b\u6765\uff0c\u4e00\u6837\u7684\u7b14\u8ff9\u4e00\u6837\u7684\u6587\u5b57\u5199\u9053\uff1a\u201c\u901f\u8d70\u56de\u5934\u8def\uff0c\u83ab\u4e0a\u592a\u884c\u5c71\uff01\u201d\u909d\u7ec3\u971e\u76b1\u8d77\u7709\u5934\uff0c\u8bf4 \u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u770b\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u5e74\u7eaa\u867d\u8f7b\uff0c\u9605\u5386\u5374\u662f\u4e0d\u5c11\uff0c\u6c89\u601d\u6709\u9877\uff0c\u62ac\u5934\u8bf4\u9053\uff1a\u201c\u60f3\u662f\u524d\u8f88\u9ad8\u4eba\u6307\u70b9\uff0c\u6211\u770b\uff0c \u4e0d\u4e0a\u592a\u884c\u5c71\u4e5f\u5c31\u7f62\u4e86\u3002\u201d\u738b\u9675\u8fd9\u65f6\u4e5f\u5df2\u8d70\u4e86\u51fa\u6765\uff0c\u5ffd\u7136\u9634\u607b\u607b\u7684\u8bf4\u9053\uff1a\u201c\u8bf4\u8981\u4e0a\u5c71\u7684\u662f\u4f60\uff0c \u8bf4\u4e0d\u8981\u4e0a\u5c71\u7684\u4e5f\u662f\u4f60\uff0c\u4f60\u554a\uff0c\u96be\u9053\u662f\u5f53\u5c0f\u5b69\u5b50\u73a9\u7684\u5417\uff1f\u201d\u5510\u6653\u6f9c\u5f3a\u5fcd\u4f4f\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff0c \u90bb\u5c45\u90a3\u8001\u5934\u5b50\u662f\u5a01\u9707\u5317\u4e94\u7701\u7684\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\u3002\u201d\u738b\u9675\u9053\uff1a\u201c\u662f\u6768\u4ef2\u82f1\u53c8\u600e\u6837\uff1f\u201d\u5510\u6653\u6f9c \u9053\uff1a\u201c\u6628\u665a\u4e2d\u79cb\uff0c\u662f\u5317\u4e94\u7701\u8c6a\u6770\u5728\u592a\u884c\u5c71\u5927\u4f1a\u4e4b\u65f6\uff0c\u4ee5\u6768\u4ef2\u82f1\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u5c31\u7b97\u4e0d\u662f\u76df\u4e3b\u4e5f \u5f53\u53c2\u52a0\uff0c\u4f46\u4ed6\u5374\u76f8\u53cd\u7684\u4ece\u592a\u884c\u5c71\u90a3\u8fb9\u51fa\u6765\uff0c\u60f3\u5fc5\u662f\u5c71\u4e0a\u51fa\u4e86\u4ec0\u4e48\u4e8b\u4e86\u3002\u201d\u738b\u9675\u9053\uff1a\u201c\u4f60\u8fd8\u662f \u80e1\u731c\u4e71\u60f3\uff0c\u800c\u4e14\u6768\u4ef2\u82f1\u5206\u660e\u53d7\u4e86\u91cd\u4f24\uff0c\u8d70\u52a8\u4e5f\u8270\u96be\uff0c\u4ed6\u53c8\u90a3\u80fd\u5728\u7247\u523b\u4e4b\u95f4\uff0c\u5728\u4e24\u5904\u7559\u5200\u5bc4\u7b80 \u5440\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u53c8\u6ca1\u6709\u8bf4\u8fd9\u5b57\u6761\u662f\u6768\u4ef2\u82f1\u7559\u7684\u3002\u4f46\u662f\u4ed6\u670b\u53cb\u6216\u5bb6\u4eba\u7559\u7684\uff0c\u4e5f\u662f\u4e86\u6837\u3002 \u518d\u8005\u524d\u5929\u78b0\u89c1\u7684\u90a3\u98de\u706b\u5f39\u5b5f\u5efa\u96c4\uff0c\u4e5f\u662f\u4ece\u592a\u884c\u5c71\u90a3\u8fb9\u6765\uff0c\u8d70\u7684\u662f\u56de\u5934\u8def\uff0c\u5c06\u4e24\u4ef6\u4e8b\u8fde\u5728\u4e00 \u8d77\uff0c\u524d\u53bb\u53ef\u80fd\u771f\u662f\u51f6\u591a\u5409\u5c11\uff01\u201d\u738b\u9675\u6413\u7740\u53cc\u624b\uff0c\u5ffd\u7136\u51b7\u7b11\u3002 \u3000\u3000\u909d\u7ec3\u971e\u6709\u70b9\u4e0d\u5feb\uff0c\u95ee\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u738b\u9675\u9053\uff1a\u201c\u5510\u5e08\u5f1f\u539f\u6765\u5982\u6b64\u80c6\u5c0f\uff0c\u65e9\u77e5 \u5982\u6b64\uff0c\u65e9\u542c\u611a\u5144\u4e4b\u8ba1\uff0c\u524d\u5f80\u4eac\u5e08\uff0c\u4e0d\u662f\u514d\u8d70\u8fd9\u4e48\u591a\u51a4\u6789\u8def\u4e48\uff1f\u201d\u909d\u7ec3\u971e\u65b9\u5bf8\u5df2\u4e71\uff0c\u53f9\u9053\uff1a \u201c\u5230\u5904\u90fd\u662f\u654c\u4eba\uff0c\u83ab\u4e0d\u6210\u771f\u4e2a\u5bf8\u6b65\u96be\u884c\uff1f\u201d\u5510\u6653\u6f9c\u609a\u7136\u5fc3\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u82e5\u7136\u4e0d\u4e0a\u592a\u884c\uff0c\u90a3\u4e48 \u5fc5\u7136\u8981\u968f\u738b\u51cc\u53bb\u4eac\u5e08\u4e86\u3002\u738b\u9675\u5fc3\u672f\u5982\u4f55\uff0c\u4e0d\u5f97\u800c\u77e5\uff0c\u6211\u4e0d\u6253\u7d27\uff0c\u53ea\u6050\u5e08\u5ac2\u4e0a\u4ed6\u5708\u5957\u3002\u201d\u53c8\u60f3 \u9053\uff1a\u201c\u5e08\u5ac2\u6240\u8bf4\u4e5f\u662f\u4e0d\u5dee\uff0c\u5230\u5904\u90fd\u662f\u654c\u4eba\uff0c\u907f\u5f97\u4e1c\u6765\u907f\u4e0d\u4e86\u897f\uff0c\u4e94\u7701\u8c6a\u6770\u96c6\u4f1a\uff0c\u53c8\u662f\u5728\u5d07\u5c71 \u5cfb\u5cad\u4e4b\u4e2d\uff0c\u5c31\u7b97\u6709\u6570\u4e07\u5b98\u5175\uff0c\u4e5f\u5948\u4f55\u4ed6\u4eec\u4e0d\u5f97\u3002 \u3000\u3000\u738b\u9675\u89c1\u5510\u6653\u6f9c\u4f4e\u5934\u9ed8\u60f3\uff0c\u55e4\u58f0\u7b11\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u4e0d\u4e0a\u592a\u884c\u5c71\u4e86\u5427\uff1f\u201d\u5510\u6653\u6f9c\u7a81\u7136\u6602\u5934\u8bf4 \u9053\uff1a\u201c\u5c31\u662f\u9f99\u6f6d\u864e\u7a74\uff0c\u4e5f\u8981\u95ef\u4ed6\u4e00\u95ef\uff0c\u660e\u5929\u5c31\u4e0a\u5c71\u53bb\uff01\u201d\u909d\u7ec3\u971e\u548c\u738b\u9675\u867d\u662f\u4ece\u5c0f\u5728\u4e00\u4e2a\u6751\u5b50 \u91cc\u957f\u5927\uff0c\u4f46\u7ecf\u4e86\u8fd9\u573a\u5927\u53d8\uff0c\u540c\u884c\u6570\u65e5\uff0c\u53cd\u4f3c\u89c9\u5f97\u5510\u6653\u6f9c\u66f4\u6709\u631a\u6027\u771f\u60c5\uff0c\u89c1\u5510\u6653\u6f9c\u5982\u6b64\u4e00\u8bf4\uff0c \u7acb\u523b\u8d5e\u540c\uff0c\u70b9\u5934\u8bf4\u9053\uff1a\u201c\u884c\u5230\u6b64\u5904\uff0c\u592a\u884c\u5c71\u5df2\u7ecf\u5728\u671b\uff0c\u6211\u770b\u4e5f\u662f\u4e0a\u5c71\u7684\u597d\uff0c\u4f46\u613f\u5728\u592a\u884c\u5c71 \u4e0a\uff0c\u80fd\u9047\u89c1\u516c\u516c\u6216\u5468\u5927\u4fa0\u7684\u597d\u53cb\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u65e5\u3002\u4e00\u884c\u4e09\u4eba\u79bb\u5f00\u4fee\u6b66\uff0c\u8d70\u4e86\u4e94\u516d\u5341\u91cc\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u5df2\u5230\u5c71\u811a\u3002\u6cbf\u9014\u884c\u4eba\u7a00\u5c11\uff0c\u8fdb \u5165\u5c71\u533a\uff0c\u66f4\u662f\u6c93\u4e0d\u89c1\u4eba\u3002\u5510\u6d47\u6f9c\u5fc3\u91cc\u6697\u6697\u5600\u5495\uff0c\u60f3\u9053\uff1a\u201c\u4e94\u7701\u8c6a\u6770\u7684\u5927\u96c6\u4f1a\uff0c\u4f55\u4ee5\u4e0d\u89c1\u6709\u4eba \u5728\u5c71\u53e3\u63a5\u5f85\uff1f\u201d\u592a\u884c\u5c71\u5c71\u9ad8\u6797\u5bc6\uff0c\u90c1\u90c1\u82cd\u82cd\uff0c\u7fa4\u5ce6\u8d77\u4f0f\uff0c\u4e91\u96fe\u8ff7\u6f2b\uff0c\u4e09\u4eba\u65a9\u68d8\u62ab\u8346\uff0c\u6500\u85e4\u9644 \u845b\uff0c\u8d70\u4e86\u534a\u5929\uff0c\u5140\u662f\u7a7a\u5c71\u54cd\u5bc2\uff0c\u4f46\u89c1\u9e1f\u98de\uff0c\u4e0d\u95fb\u4eba\u8bed\u3002\u5510\u6653\u6f9c\u6035\u7136\u6b62\u6b65\uff0c\u909d\u7ec3\u971e\u4e5f\u662f\u6ee1\u8179\u7591 \u8651\uff0c\u521a\u8bf4\u5f97\u53e5\uff1a\u201c\u5510\u5e08\u5f1f\uff0c\u4f60\u770b\u8fd8\u4e0a\u4e0d\u4e0a\u53bb\uff1f\u201d\u5ffd\u542c\u5f97\u4e00\u58f0\u80e1\u54e8\uff0c\u5341\u4f59\u4e08\u5916\u8305\u8349\u730e\u730e\u4f5c\u54cd\uff1b \u5510\u6653\u6f9c\u5fd9\u62c9\u7740\u909d\u7ec3\u971e\u4f0f\u4f4e\uff0c\u8346\u68d8\u8513\u8349\u4e4b\u4e2d\uff0c\u5237\u5237\u54cd\u5904\uff0c\u5ffd\u5730\u7a9c\u51fa\u51e0\u4e2a\u4eba\u6765\uff01\u5510\u6653\u6f9c\u4e00\u770b\u5927 \u9a87\uff0c\u4e3a\u9996\u7684\u624b\u6301\u9f99\u5934\u62d0\u6756\uff0c\u7adf\u7136\u662f\u9f99\u6728\u516c\uff01 \u3000\u3000\u738b\u9675\u52a8\u4e86\u4e00\u52a8\uff0c\u5510\u6653\u6f9c\u4e94\u6307\u4e00\u642d\u4ed6\u7684\u80a9\u5934\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u5144\u4e0d\u8981\u4e71\u52a8\uff01\u201d\u4ed6\u6240\u634f\u4e4b \u5904\uff0c\u6b63\u662f\u80a9\u5934\u7435\u7436\u8f6f\u9aa8\uff0c\u738b\u9675\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u9762\u8272\u53d8\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u522b\u5f00\u73a9\u7b11\uff01\u201d\u5510\u6653\u6f9c\u9053\uff1a \u201c\u4f60\u8eba\u4e0b\u6765\uff01\u4f60\u60f3\u7ed9\u654c\u4eba\u770b\u89c1\u5417\uff1f\u201d\u738b\u9675\u548c\u8eab\u5367\u5728\u4e71\u8349\u4e2d\uff0c\u679c\u7136\u52a8\u4e5f\u4e0d\u6562\u4e00\u52a8\u3002\u5510\u6653\u6f9c\u5077\u5077 \u5f20\u671b\uff0c\u53ea\u89c1\u9f99\u6728\u516c\u7741\u5f00\u72ec\u773c\uff0c\u6e38\u76ee\u56db\u987e\uff0c\u5bf9\u540c\u4f34\u8bf4\u9053\uff1a\u201c\u6211\u4f3c\u4e4e\u542c\u5f97\u4eba\u58f0\uff0c\u600e\u4e48\u53c8\u4e0d\u89c1 \u4e86\uff01\u201d\u540c\u6765\u7684\u4eba\u4e00\u5f8b\u9752\u8863\u77ed\u6253\uff0c\u624b\u63d0\u6734\u5200\uff0c\u8170\u60ac\u4e24\u4e2a\u94c1\u7403\uff0c\u663e\u7136\u662f\u8840\u6ef4\u5b50\u3002\u5185\u4e2d\u4e00\u4eba\u53d1\u8bdd \u9053\uff1a\u201c\u5582\uff0c\u670b\u53cb\uff01\u662f\u76d8\u9053\u7684\uff1f\u662f\u63d2\u6869\u7684\uff1f\u8d76\u5feb\u4eae\u4e07\uff01\u201d\u201c\u76d8\u9053\u201d\u610f\u5373\u63a2\u8def\uff0c\u201c\u63d2\u6869\u201d\u610f\u5373\u53c2 \u52a0\u96c6\u4f1a\u3002\u201c\u4eae\u4e07\u201d\u610f\u601d\u9732\u51fa\u6765\u9762\u6765\u3002\u8fd9\u51e0\u53e5\u8bdd\u4e43\u662f\u6c5f\u6e56\u201c\u5507\u5178\u201d\uff08\u9ed1\u8bdd\uff09\uff0c\u909d\u7ec3\u971e\u4e00\u6982\u4e0d \u61c2\uff0c\u5510\u6653\u6f9c\u53ef\u662f\u6697\u6697\u5fc3\u60ca\uff0c\u53ea\u9053\u8e2a\u8ff9\u5df2\u7ecf\u8d25\u9732\uff0c\u5c4f\u6c14\u51dd\u795e\uff0c\u4ecd\u7136\u52a8\u4e5f\u4e0d\u52a8\u3002\u8fd9\u4e2a\u4eba\u53d1\u5b8c\u8bdd\uff0c \u7a0d\u5fae\u4e00\u6c89\uff0c\u8ddf\u7740\u53c8\u4e00\u4e2a\u6c99\u58f0\u7684\u559d\u9053\uff1a\u201c\u5582\uff0c\u670b\u53cb\uff0c\u4f60\u4eec\u82e5\u8fd8\u662f\u7d27\u81ea\u95f7\u6761\u5b50\u4e0d\u4eae\u94a2\uff0c\u6211\u4eec\u53ef\u8981 \u7528\u6697\u9752\u5b50\u62db\u547c\u4e86\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u85cf\u5728\u4e71\u8349\u4e4b\u4e2d\uff0c\u8eab\u8fb9\u53c8\u6709\u5ca9\u77f3\u8d77\u4f0f\u9519\u843d\uff0c\u5fc3\u60f3\uff1a\u8d3c\u4eba\u8fd9\u6837\u4e71\u558a\uff0c\u51c6\u662f\u4e0d\u77e5\u81ea\u5df1\u85cf \u8eab\u4e4b\u6240\uff0c\u4e14\u83ab\u7406\u4ed6\u3002\u521a\u624d\u90a3\u4e2a\u4eba\u8bf4\u7684\u9ed1\u8bdd\u662f\uff1a\u4f60\u4eec\u82e5\u8fd8\u662f\u4e0d\u5f00\u53e3\uff08\u95f7\u6761\u5b50\uff09\uff0c\u4e0d\u7b54\u8bdd\uff08\u4e0d\u4eae \u94a2\uff09\uff0c\u6211\u4eec\u53ef\u8981\u7528\u6697\u5668\u6253\u4e86\u3002\u8bf4\u5b8c\u4e4b\u540e\uff0c\u89c1\u4ecd\u6ca1\u6709\u7b54\u8154\u7684\uff0c\u548c\u540c\u4f34\u4ea4\u6362\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u4f4e\u58f0\u8bf4 \u9053\uff1a\u201c\u4e94\u7701\u8c6a\u6770\u5168\u7ed9\u6253\u5f97\u6b7b\u7684\u6b7b\uff0c\u4f24\u7684\u4f24\uff0c\u800c\u4eca\u5df2\u8fc7\u4e24\u5929\uff0c\u9664\u4e86\u81ea\u5df1\u4eba\uff0c\u8fd8\u6709\u8c01\u6562\u4e0a\u5c71\uff01\u9f99 \u5927\u54e5\u6015\u8d70\u4e86\u773c\u5427\uff01\u201d\u9f99\u6728\u516c\u9f99\u5934\u62d0\u6756\u51fb\u77f3\u4f5c\u54cd\uff0c\u72ec\u773c\u5706\u7741\uff0c\u5927\u58f0\u9a82\u9053\uff1a\u201c\u6211\u4e00\u53ea\u773c\u775b\u6bd4\u4f60\u4eec \u5341\u51e0\u53cc\u773c\u775b\u8fd8\u4eae\u5802\uff0c\u6211\u660e\u660e\u542c\u5f97\u4eba\u58f0\uff0c\u4f60\u4eec\u662f\u804b\u7684\u5417\uff1f\u201d\u9f99\u6728\u516c\u4e94\u5929\u4e4b\u524d\u548c\u5468\u9752\u6076\u6218\u5e26\u4f24\uff0c \u4f46\u9664\u778e\u4e86\u4e00\u773c\u4e4b\u5916\uff0c\u5176\u4f59\u7684\u4f24\u5374\u975e\u5185\u4f24\uff0c\u773c\u836f\u4e4b\u540e\uff0c\u4ed7\u7740\u529f\u529b\u6df1\u539a\uff0c\u548c\u96f7\u6d77\u97f3\u4e00\u8def\u8ffd\u8d76\u5510\u6653 \u6f9c\uff0c\u6cbf\u9014\u67e5\u95ee\u3002\u5510\u6653\u6f9c\u7b49\u4e00\u884c\u4e09\u4eba\uff0c\u4e24\u7537\u4e00\u5973\uff0c\u909d\u7ec3\u971e\u662f\u4e00\u4e2a\u7f8e\u8273\u5c11\u5987\uff0c\u62b1\u4e00\u4e2a\u7c89\u96d5\u7389\u7422\u7684 \u5973\u5a74\uff0c\u66f4\u662f\u4ee4\u4eba\u77a9\u76ee\uff0c\u9f99\u6728\u516c\u6cbf\u9014\u67e5\u95ee\uff0c\u8ddf\u7740\u4ed6\u4eec\u8e2a\u8ff9\uff0c\u8ffd\u4e0a\u592a\u884c\u5c71\u6765\u3002\u800c\u4e14\u5728\u4e0a\u5c71\u4e4b\u65f6\uff0c \u56e0\u4e3a\u6240\u8d70\u5c71\u8def\u4e0d\u540c\uff0c\u8fd8\u7ed9\u4ed6\u4eec\u8d76\u8fc7\u4e86\u5934\uff0c\u5148\u5230\u5c71\u4e0a\u3002\u5230\u4e86\u5c71\u4e0a\uff0c\u56db\u7687\u5b50\u6d3e\u6765\u7684\u4eba\u5c1a\u672a\u64a4\u8d70\uff0c \u4ed6\u4eec\u5148\u62dc\u89c1\u4e86\u4e24\u4e2a\u9b54\u5934\uff0c\u96f7\u6d77\u97f3\u7ed9\u7559\u7740\u529e\u4e8b\uff0c\u9f99\u6728\u516c\u5374\u53e6\u5916\u9886\u4e86\u4e03\u516b\u540d\u8840\u6ef4\u5b50\u6ee1\u5c71\u4e71\u641c\u3002\u9f99 \u6728\u516c\u6b64\u65f6\u4f24\u52bf\u5df2\u6108\uff0c\u72c2\u5984\u6545\u6001\uff0c\u53c8\u590d\u53d1\u4f5c\uff0c\u8840\u6ef4\u5b50\u90fd\u4e0d\u6562\u4f5c\u58f0\uff0c\u9f99\u6728\u516c\u94c1\u62d0\u987f\u5730\uff0c\u5927\u58f0\u53eb \u9053\uff1a\u201c\u4f60\u4eec\u4f5c\u4ec0\u4e48\u7684\uff0c\u52a8\u624b\u641c\u5440\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u4e0d\u6562\u4eae\u5251\uff0c\u624b\u91cc\u6697\u6697\u63e1\u7740\u4e00\u628a\u98de\u8292\uff0c\u53ea\u5f85\u8840\u6ef4\u5b50\u6765\uff0c\u5c31\u548c\u4ed6\u786c\u62fc\u3002\u8840\u6ef4\u5b50\u5468\u56f4\u4e71 \u641c\uff0c\u773c\u770b\u641c\u5230\uff0c\u9f99\u6728\u516c\u94c1\u62d0\u5ffd\u7136\u5411\u4e1c\u4e00\u6307\uff0c\u559d\u9053\uff1a\u201c\u654c\u4eba\u6765\u4e86\uff01\u201d\u8840\u6ef4\u5b50\u4eec\u7eb7\u7eb7\u56de\u8eab\uff0c\u5510\u6653 \u6f9c\u5410\u4e86\u53e3\u6c14\uff0c\u501a\u7740\u5ca9\u77f3\uff0c\u63a2\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u5c71\u62d7\u90a3\u8fb9\u4e24\u4e2a\u9ed1\u70b9\uff0c\u500f\u5ffd\u8f6c\u5927\uff0c\u8f6c\u77ac\u5230\u4e86\u8fd9\u8fb9\uff0c\u73b0 \u51fa\u5168\u8eab\uff0c\u4e3a\u9996\u7684\u662f\u4e2a\u9ed1\u8863\u9053\u58eb\uff0c\u5de6\u624b\u94c1\u62d0\u53f3\u624b\u957f\u5251\uff0c\u813e\u7768\u4f5c\u6001\uff0c\u610f\u6c14\u751a\u8c6a\uff0c\u8ddf\u5728\u540e\u9762\u7684\u662f\u4e2a \u80d6\u548c\u5c1a\uff0c\u4e24\u624b\u7a7a\u7a7a\u5168\u65e0\u5175\u5668\uff0c\u8170\u95f4\u5374\u60ac\u7740\u4e00\u4e2a\u5927\u846b\u82a6\u3002 \u3000\u3000\u9f99\u6728\u516c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4ec0\u4e48\u4eba\uff1f\u662f\u56db\u7687\u5b50\u5dee\u6765\u7684\uff0c\u8fd8\u662f\u6768\u4ef2\u82f1\u8001\u513f\u9080\u6765\u7684\uff1f\u201d\u9ed1\u8863\u9053\u58eb \u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u4f60\u8bf4\uff0c\u4f60\u4eec\u662f\u56db\u7687\u5b50\u5dee\u6765\u7684\uff1f\u54c8\uff0c\u6211\u6b63\u8981\u627e\u4f60\u4eec\uff01\u201d\u94c1\u62d0\u4e00\u6325\uff0c\u957f\u5251 \u5237\u7684\u523a\u51fa\u3002\u9f99\u6728\u516c\u6a2a\u62d0\u4e00\u5c01\uff0c\u9000\u540e\u51e0\u6b65\uff0c\u9ed1\u8863\u9053\u58eb\u7b11\u9053\uff1a\u201c\u5514\uff0c\u4f60\u8fd8\u4e0d\u9519\uff01\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u5251 \u62db\u4e1d\u6beb\u4e0d\u7f13\uff0c\u5237\u5237\u51e0\u5251\uff0c\u4e24\u540d\u8840\u6ef4\u5b50\u8840\u6d41\u6ee1\u9762\uff0c\u56db\u53ea\u8033\u6735\u5168\u7ed9\u5272\u843d\uff0c\u7ed9\u4ed6\u5377\u8fdb\u8896\u4e2d\u3002\u8fd9\u51e0\u4e0b \u5feb\u5f97\u60ca\u4eba\uff0c\u9f99\u6728\u516c\u7adf\u662f\u751f\u5e73\u672a\u89c1\uff0c\u6025\u6325\u624b\u9053\uff1a\u201c\u653e\u6697\u5668\uff01\u201d\u970e\u65f6\u95f4\u6ee1\u7a7a\u602a\u5578\uff0c\u4e94\u516d\u4e2a\u94c1\u7403\u547c \u547c\u98de\u6765\uff0c\u9ed1\u8863\u9053\u58eb\u957f\u7b11\u4e00\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u5b69\u5b50\u7684\u73a9\u5177\uff0c\u4e5f\u62ff\u6765\u73b0\u4e16\uff01\u201d\u4e00\u4e2a\u94c1\u7403\u98de\u5230\u5934\u4e0a\uff0c \u9ed1\u8863\u9053\u58eb\u94c1\u62d0\u8fce\u5934\u4e00\u70b9\uff0c\u94c1\u7403\u5012\u98de\u56de\u53bb\uff0c\u78b0\u5728\u53e6\u4e00\u4e2a\u8840\u6ef4\u5b50\u4e0a\uff0c\u767b\u65f6\u88c2\u5f00\uff0c\u91cc\u9762\u98de\u5200\u7eb7\u7eb7\u5c04 \u51fa\uff0c\u9ed1\u8863\u9053\u58eb\u94c1\u62d0\u6a2a\u626b\u76f4\u51fb\uff0c\u628a\u4e94\u516d\u4e2a\u94c1\u7403\u5168\u90e8\u51fb\u788e\uff0c\u957f\u5251\u98de\u821e\uff0c\u6ee1\u5929\u5200\u96e8\uff0c\u7ed9\u4ed6\u626b\u8361\u5f97\u56db \u5904\u6fc0\u5c04\uff0c\u649e\u5728\u5ca9\u77f3\u4e4b\u4e0a\uff0c\u98de\u51fa\u4e00\u6e9c\u6e9c\u7684\u706b\u82b1\uff0c\u6b66\u58eb\u4eec\u7eb7\u7eb7\u9003\u907f\u3002 \u3000\u3000\u9f99\u6728\u516c\u9762\u8272\u5927\u53d8\uff0c\u98de\u8eab\u8dc3\u8d77\uff0c\u9ed1\u8863\u9053\u58eb\u559d\u9053\uff1a\u201c\u54ea\u91cc\u8d70\uff01\u201d\u8eab\u5f62\u4e00\u5f13\uff0c\u98de\u7bad\u822c\u75be\u5c04\u800c \u6765\uff0c\u5de6\u624b\u94c1\u62d0\u201c\u66b4\u9f99\u6270\u6d77\u201d\uff0c\u65cb\u98ce\u5377\u5230\uff0c\u9f99\u6728\u516c\u6a2a\u62d0\u4e00\u5c01\uff0c\u53ea\u89c9\u4e00\u80a1\u5927\u529b\uff0c\u72b9\u5982\u5de8\u96f7\u51fb\u9876\uff0c \u5cb1\u5cb3\u98de\u6765\uff0c\u9f99\u6728\u516c\u529f\u529b\u672c\u975e\u5bfb\u5e38\uff0c\u5403\u8fd9\u4e00\u51fb\uff0c\u9f99\u5934\u62d0\u6756\u7adf\u8131\u624b\u98de\u53bb\uff0c\u8fd9\u652f\u62d0\u6756\u662f\u4ed6\u8fc7\u6d1b\u9633\u65f6 \u8fde\u591c\u94f8\u9020\u7684\uff08\u539f\u6765\u90a3\u652f\u5df2\u7ed9\u5468\u9752\u62d7\u65ad\uff09\uff0c\u4efd\u91cf\u8f83\u8f7b\uff0c\u94a2\u8d28\u4e0d\u7eaf\uff0c\u53d7\u8fd9\u4e00\u9707\u4e4b\u529b\uff0c\u5728\u534a\u7a7a\u4e2d\u88c2 \u4e3a\u6570\u6bb5\uff01\u9ed1\u8863\u9053\u58eb\u62d0\u5251\u9f50\u53d1\uff0c\u53f3\u624b\u957f\u5251\u4e00\u62db\u201c\u5012\u6cfb\u5929\u6cb3\u201d\uff0c\u5251\u82b1\u5982\u6d6a\u98de\u6d12\u4e0b\u6765\uff0c\u9f99\u6728\u516c\u8fd0\u72ec \u95e8\u8f7b\u529f\u201c\u98de\u82b1\u5377\u96e8\u201d\uff0c\u4ee5\u788e\u6b65\u817e\u632a\u7684\u8eab\u6cd5\u6b65\u6cd5\uff0c\u5728\u5251\u5149\u7f1d\u4e2d\u94bb\u51fa\u3002\u9976\u662f\u4ed6\u8f7b\u529f\u8d85\u5353\uff0c\u4e5f\u89c9\u8033 \u9645\u4e00\u51c9\uff0c\u80cc\u540e\u53ea\u542c\u5f97\u9ed1\u8863\u9053\u58eb\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u80fd\u907f\u6211\u534a\u62db\uff0c\u4e5f\u7b97\u4e0d\u9519\uff0c\u7531\u4f60\u53bb\u5427\uff01\u201d\u539f\u6765\u9ed1 \u8863\u9053\u58eb\u90a3\u4e00\u62db\uff0c\u539f\u60f3\u628a\u4ed6\u4e24\u53ea\u8033\u6735\u9f50\u90fd\u524a\u843d\uff0c\u4f46\u9f99\u6728\u516c\u8eab\u6cd5\u751a\u5feb\uff0c\u7ed3\u679c\u9ed1\u8863\u9053\u58eb\u4e00\u62db\u4e03\u5f0f\uff0c \u77ac\u606f\u4e4b\u95f4\uff0c\u4f7f\u4e86\u51fa\u6765\uff0c\u4e5f\u53ea\u80fd\u524a\u6389\u4ed6\u7684\u53f3\u8033\u3002\u9ed1\u8863\u9053\u58eb\u5251\u6cd5\u72ec\u6b65\u5317\u65b9\uff0c\u5e73\u751f\u4ee5\u6b64\u81ea\u8d1f\uff0c\u80fd\u5728 \u4ed6\u5251\u4e0b\u9003\u51fa\u7684\uff0c\u4ed6\u4f8b\u4e0d\u8ffd\u8d76\u3002 \u3000\u3000\u5176\u4ed6\u7684\u4e03\u516b\u540d\u8840\u6ef4\u5b50\u5728\u5931\u6389\u8840\u6ef4\u5b50\u540e\uff0c\u4e5f\u7eb7\u7eb7\u8f6c\u8eab\u5954\u9003\u3002\u90a3\u80d6\u548c\u5c1a\u8eab\u6cd5\u5feb\u6781\uff0c\u53cc\u811a\u4e00 \u70b9\uff0c\u8eab\u5f62\u98de\u8d77\uff0c\u90a3\u73ed\u8840\u6ef4\u5b50\u773c\u524d\u4e00\u9ed1\uff0c\u4f3c\u89c9\u4e00\u7247\u9ed1\u4e91\uff0c\u4ece\u5934\u9876\u98de\u8fc7\uff0c\u7741\u5f00\u773c\u65f6\uff0c\u80d6\u548c\u5c1a\u5728\u4ed6 \u4eec\u9762\u524d\u7b11\u563b\u563b\u7684\u7ad9\u4f4f\uff0c\u624b\u91cc\u6367\u7740\u4e00\u4e2a\u5927\u846b\u82a6\uff0c\u6447\u5934\u6446\u8111\u7684\u8bf4\u9053\uff1a\u201c\u522b\u8fd9\u4e48\u5feb\u8d70\u5440\uff0c\u8d2b\u50e7\u8bf7\u4f60 \u4eec\u559d\u9152\uff01\u201d \u3000\u3000\u8840\u6ef4\u5b50\u7eb7\u7eb7\u51b2\u4e0a\uff0c\u80d6\u548c\u5c1a\u5ffd\u7136\u5f20\u53e3\u4e00\u55b7\uff0c\u9152\u9999\u56db\u6ea2\uff0c\u201c\u9152\u6d6a\u201d\u8fce\u9762\u55b7\u6765\uff0c\u90a3\u73ed\u8840\u6ef4\u5b50\u53ea \u89c9\u773c\u524d\u767d\u8499\u8499\u4e00\u7247\uff0c\u773c\u775b\u8fa3\u75db\uff0c\u500f\u5ffd\u5929\u660f\u5730\u6697\uff0c\u8033\u9645\u542c\u5f97\u80d6\u548c\u5c1a\u54c8\u54c8\u5927\u7b11\u4e4b\u58f0\uff0c\u60ca\u9b42\u6b32\u7edd\uff0c \u518d\u4e5f\u987e\u4e0d\u5f97\u773c\u775b\u75bc\u75db\uff0c\u5ca9\u77f3\u78f7\u5ccb\uff0c\u4e03\u516b\u4e2a\u8840\u6ef4\u5b50\u540c\u4e00\u5fc3\u601d\uff0c\u52a8\u8eab\u4e00\u6eda\uff0c\u4ece\u5c71\u4e0a\u76f4\u6eda\u4e0b\u53bb\u3002\u80d6 \u548c\u5c1a\u4e5f\u4e0d\u8ffd\u8d76\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u9053\u5144\uff0c\u5f97\u624b\u6ca1\u6709\uff1f\u201d\u9ed1\u8863\u9053\u58eb\u5e94\u9053\uff1a\u201c\u53ea\u5f97\u4e86\u4e00\u534a\uff0c\u4f60\u5462\uff1f\u201d \u80d6\u548c\u5c1a\u9053\uff1a\u201c\u6211\u4e5f\u672a\u7adf\u5168\u529f\uff0c\u53ea\u55b7\u778e\u4e86\u5341\u4e09\u53ea\u72d7\u773c\u3002\u201d\u539f\u6765\u516b\u4e2a\u8840\u6ef4\u5b50\u4e2d\uff0c\u6709\u4e94\u4eba\u53cc\u773c\u5168 \u778e\uff0c\u4f46\u6709\u4e09\u4eba\u53ea\u778e\u4e86\u4e00\u53ea\u773c\u775b\uff0c\u738b\u9675\u5fc3\u9b44\u9707\u88c2\uff0c\u4fa5\u5e78\u81ea\u5df1\u521a\u624d\u7ed9\u5510\u6653\u6f9c\u6309\u4f4f\uff0c\u5e76\u672a\u4e71\u52a8\u3002 \u3000\u3000\u5510\u6653\u6f9c\u4e5f\u81ea\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u89c1\u4e86\u8fd9\u4e00\u50e7\u4e00\u9053\u7684\u72ec\u7279\u6b66\u529f\uff0c\u5ffd\u7136\u60f3\u4e86\u8d77\u6765\uff1a\u539f\u6765\u5728\u8fd9\u4e24\u65e5 \u95f4\uff0c\u63a5\u8fde\u78b0\u89c1\u7684\u8fd9\u56db\u4e2a\u5f02\u4eba\uff0c\u5c31\u662f\u5173\u4e1c\u56db\u4fa0\uff0c\u4e3a\u9996\u7684\u53eb\u505a\u7384\u98ce\u9053\u957f\uff0c\u5c31\u662f\u90a3\u9ed1\u8863\u9053\u4eba\u3002\u4ed6\u5de6 \u624b\u4f7f\u62d0\uff0c\u53f3\u624b\u4f7f\u5251\uff0c\u4ed6\u7684\u5251\u6cd5\u540d\u4e3a\u201c\u4e71\u62ab\u98ce\u201d\uff0c\u5c24\u5176\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u548c\u5468\u9752\u7684\u201c\u8ffd\u98ce\u5251\u6cd5\u201d\u5f02 \u66f2\u540c\u5de5\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u85cf\u7740\u8bb8\u591a\u53d8\u5316\uff0c\u6bd4\u201c\u8ffd\u98ce\u5251\u6cd5\u201d\u8fd8\u66f4\u72e0\u8fa3\uff0c\u5468\u9752\u548c\u7384\u98ce\u795e\u4ea4\u5df2\u4e45\uff0c\u5eff\u591a \u5e74\u524d\uff0c\u65b9\u59cb\u5f97\u5728\u6768\u4ef2\u82f1\u5bb6\u91cc\u76f8\u4f1a\uff0c\u4e92\u76f8\u7814\u7a76\u5251\u6cd5\uff0c\u7ed3\u4e3a\u77e5\u4ea4\u3002\u5468\u9752\u9664\u4e86\u5e08\u5085\u51cc\u672a\u98ce\u5916\uff0c\u6700\u4f69 \u670d\u7684\u5c31\u662f\u7384\u98ce\u3002\u5176\u4e2d\u4e09\u4fa0\uff0c\u5468\u9752\u867d\u672a\u89c1\u8fc7\uff0c\u4f46\u4e5f\u66fe\u542c\u7384\u98ce\u63d0\u53ca\u3002\u5468\u9752\u628a\u4ece\u7384\u98ce\u53e3\u4e2d\u542c\u6765\u7684\u8bf4 \u7ed9\u5510\u6653\u6f9c\u77e5\u9053\uff0c\u662f\u4ee5\u5173\u4e1c\u56db\u4fa0\u7684\u5f62\u8c8c\u6b66\u529f\uff0c\u65e9\u5df2\u6df1\u5370\u4ed6\u7684\u8111\u6d77\u3002 \u3000\u3000\u5173\u4e1c\u56db\u4fa0\uff0c\u7b2c\u4e8c\u4e2a\u5c31\u662f\u90a3\u80d6\u548c\u5c1a\uff0c\u540d\u53eb\u6717\u6708\u7985\u5e08\uff0c\u751f\u6027\u6ed1\u7a3d\uff0c\u4eba\u79f0\u7b11\u5f25\u52d2\uff0c\u6700\u559c\u996e\u9152\uff0c \u4ed6\u7684\u72ec\u95e8\u6b66\u529f\uff0c\u5c31\u662f\u4ee5\u7f8e\u9152\u4f5c\u4e3a\u6697\u5668\uff0c\u4e13\u95e8\u5c04\u4eba\u53cc\u76ee\uff0c\u5389\u5bb3\u65e0\u6bd4\uff0c\u55b7\u51fa\u7684\u9152\u73e0\u5c31\u5982\u94c5\u5f39\u4e00 \u6837\uff0c\u4e5f\u662f\u6b66\u6797\u4e00\u7edd\uff01\u7b2c\u4e09\u4e2a\u5219\u662f\u5510\u6653\u6f9c\u5728\u5ba2\u5e97\u4e2d\u78b0\u5230\u7684\u90a3\u4e2a\u9ad8\u4e2a\u5b50\uff0c\u540d\u53eb\u67f3\u5148\u5f00\uff0c\u8f7b\u529f\u5353 \u7edd\uff0c\u5ba2\u5e97\u4e2d\u7559\u5200\u5bc4\u7b80\uff0c\u5c31\u662f\u4ed6\u7684\u628a\u620f\u3002\u4ed6\u53c8\u5584\u4e8e\u4ee5\u6307\u4e0a\u94a2\u73af\u6253\u7a74\uff0c\u82e5\u4e0d\u7528\u4f5c\u6697\u5668\u4e4b\u65f6\uff0c\u90a3\u5341 \u6307\u4e0a\u7684\u94a2\u73af\u4e5f\u662f\u4e00\u79cd\u5175\u5668\u3002\u7b2c\u56db\u4e2a\u5219\u662f\u5728\u5ba2\u5e97\u4e2d\u9047\u5230\u7684\u90a3\u4e2a\u77ee\u5b50\uff0c\u540d\u53eb\u9648\u5143\u9738\uff0c\u5916\u5bb6\u529f\u592b\u767b \u5cf0\u9020\u6781\uff0c\u529b\u5927\u65e0\u7a77\uff0c\u4ed6\u7684\u72ec\u95e8\u6b66\u529f\u662f\u201c\u5927\u6454\u7891\u624b\u201d\u548c\u201c\u5206\u7b4b\u9519\u9aa8\u624b\u201d\uff0c\u7b49\u95f2\u7684\u4eba\u7ed9\u4ed6\u6293\u7740\uff0c \u5c31\u5982\u6293\u5230\u4e00\u6839\u7a3b\u8349\u4e00\u822c\u3002\u5468\u9752\u548c\u7384\u98ce\u7f14\u4ea4\u4e4b\u65f6\uff0c\u67f3\u5148\u5f00\u4e0e\u9648\u5143\u9738\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u529f\u592b\u867d\u9ad8\uff0c\u5c1a\u672a \u201c\u7acb\u4e07\u201d\uff0c\u6240\u4ee5\u90a3\u65f6\u8fd8\u672a\u6709\u201c\u5173\u4e1c\u56db\u4fa0\u201d\u4e4b\u540d\uff0c\u540e\u6765\u67f3\u5148\u5f00\u9648\u5143\u9738\u5728\u6c5f\u6e56\u4e0a\u95ef\u51fa\u540d\u5934\uff0c\u56db\u4eba \u53c8\u5e38\u805a\u5728\u4e00\u8d77\uff0c\u8fd9\u624d\u88ab\u5408\u79f0\u4e3a\u201c\u5173\u4e1c\u56db\u4fa0\u201d\u3002 \u3000\u3000\u5510\u6653\u6f9c\u89c1\u4e86\u90a3\u548c\u5c1a\u7684\u7edd\u6280\uff0c\u60f3\u8d77\u8fd9\u56db\u4eba\u5b9a\u662f\u5173\u4e1c\u56db\u4fa0\u65e0\u7591\uff0c\u5fc3\u4e2d\u72c2\u559c\uff0c\u6b63\u60f3\u51fa\u53bb\u62db\u547c\uff0c \u5ffd\u542c\u5f97\u90a3\u9ed1\u8863\u9053\u58eb\u8bf4\u9053\uff1a\u201c\u4e8c\u5f1f\uff0c\u90a3\u4e24\u4e2a\u8001\u602a\u5c1a\u672a\u73b0\u8eab\uff0c\u4e09\u5f1f\u56db\u5f1f\u4e0e\u6211\u4eec\u76f8\u7ea6\u4eca\u65e5\u4e0a\u5c71\uff0c\u4e5f \u672a\u9047\u5230\uff0c\u53ea\u6015\u4ed6\u4eec\u5148\u78b0\u7740\u90a3\u4e24\u4e2a\u8001\u602a\uff0c\u53ef\u8981\u5403\u4e8f\uff0c\u4f60\u5728\u5c71\u5357\uff0c\u6211\u5728\u5c71\u5317\uff0c\u53bb\u627e\u4ed6\u4eec\uff0c\u7b49\u4e0b\u518d \u5728\u6b64\u5730\u76f8\u4f1a\u3002\u201d\u80d6\u548c\u5c1a\u563b\u563b\u7b11\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u6837\uff01\u201d\u4e24\u4eba\u8eab\u5f62\u4e00\u6643\uff0c\u500f\u5ffd\u4e0d\u89c1\u3002 \u3000\u3000\u738b\u9675\u5618\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5389\u5bb3\uff01\u201d\u909d\u7ec3\u971e\u4e5f\u662f\u6ee1\u9762\u6c57\u73e0\uff0c\u4ee5\u8896\u63e9\u62b9\u3002\u5598\u606f\u7565\u5b9a\uff0c\u5510\u6653 \u6f9c\u521a\u8bf4\u5f97\u53e5\uff1a\u201c\u90a3\u9ed1\u8863\u9053\u4eba\u662f\u5468\u4f2f\u4f2f\u7684\u597d\u53cb\u3002\u201d\u5ffd\u542c\u5f97\u8fdc\u5904\u53c8\u662f\u4e24\u58f0\u602a\u5578\uff0c\u5176\u58f0\u5c16\u9510\u523a\u8033\uff0c \u5510\u6653\u6f9c\u6025\u5fd9\u62c9\u7740\u738b\u9675\u53c8\u4f0f\u4e0b\u6765\u3002\u58f0\u5230\u4eba\u5230\uff0c\u5510\u6653\u6f9c\u5f20\u76ee\u5077\u7aa5\uff0c\u53ea\u89c1\u521a\u624d\u6253\u6597\u7684\u573a\u6240\uff0c\u53c8\u51fa\u73b0 \u4e86\u4e24\u4eba\u3002\u5f62\u8c8c\u90fd\u662f\u9762\u8272\u7126\u9ec4\u7684\u5e72\u7626\u8001\u5934\uff0c\u7a7f\u7740\u4e00\u8eab\u9ec4\u9ebb\u8863\u88f3\uff0c\u9762\u76ee\u6728\u7136\u6beb\u65e0\u8868\u60c5\u3002\u4e24\u4eba\u624b\u4e2d \u90fd\u63d0\u7740\u4e00\u4e2a\u5927\u76ae\u56ca\uff0c\u4e00\u4e2a\u5de6\u811a\u5fae\u8ddb\uff0c\u4e00\u4e2a\u53f3\u811a\u5fae\u8ddb\uff0c\u592a\u9633\u7a74\u575f\u8d77\uff0c\u663e\u89c1\u5185\u529f\u6781\u4e3a\u6df1\u6e5b\u3002\u5510\u6653 \u6f9c\u6697\u6697\u5947\u5f02\uff1a\u8fd9\u4e24\u4eba\u5404\u8ddb\u4e00\u8db3\uff0c\u884c\u52a8\u5374\u5982\u6b64\u8fc5\u6377\uff01 \u3000\u3000\u4e24\u4eba\u9ed8\u9ed8\u65e0\u8bed\uff0c\u8d70\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u5bdf\u770b\u90a3\u88ab\u8df5\u8e0f\u5f97\u5012\u4f0f\u51cc\u4e71\u7684\u5c71\u8305\u91ce\u8349\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u5de6\u9762 \u90a3\u4e2a\u8001\u5934\u8bf4\u9053\uff1a\u201c\u558f\uff0c\u51c6\u662f\u5173\u4e1c\u90a3\u56db\u4e2a\u4e0d\u77e5\u6b7b\u6d3b\u7684\u4e1c\u897f\u6765\u4e86\uff01\u201d\u53f3\u9762\u90a3\u4e2a\u8001\u5934\u8bf4\u9053\uff1a\u201c\u54b1\u4eec \u8981\u4e0d\u8981\u53bb\u627e\u4ed6\uff1f\u201d\u5de6\u9762\u90a3\u4e2a\u8001\u5934\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\u3002\u201d\u7a81\u7136\u64ae\u5507\u602a\u5578\uff0c\u8fd9\u756a\u8ddd\u79bb\u66f4\u8fd1\uff0c\u5510\u6653\u6f9c\u7b49 \u4e09\u4eba\u4ece\u672a\u542c\u8fc7\u8fd9\u79cd\u523a\u8033\u7684\u58f0\u97f3\uff0c\u53ea\u89c9\u5fc3\u810f\u6b32\u88c2\uff0c\u96be\u8fc7\u4e4b\u6781\u3002\u51af\u745b\u5f20\u53e3\u6b32\u558a\uff0c\u909d\u7ec3\u971e\u624b\u5feb\uff0c\u8d76 \u5fd9\u6495\u4e0b\u8863\u895f\uff0c\u56e2\u6210\u4e00\u56e2\uff0c\u585e\u8fdb\u5979\u7684\u5c0f\u53e3\uff0c\u51af\u745b\u5c0f\u624b\u4e71\u821e\uff0c\u909d\u7ec3\u971e\u8f7b\u8f7b\u629a\u62cd\uff0c\u5e78\u5728\u90a3\u4e24\u4e2a\u9b54\u5934 \u4e13\u5fc3\u5bdf\u770b\uff0c\u597d\u4f3c\u5e76\u672a\u5bdf\u89c9\u5341\u4f59\u4e08\u5916\u5ca9\u77f3\u80cc\u540e\uff0c\u5c31\u85cf\u7740\u8fd9\u4e48\u591a\u4eba\u3002 \u3000\u3000\u5de6\u9762\u90a3\u8001\u4eba\u9053\uff1a\u201c\u5173\u4e1c\u8fd9\u56db\u4e2a\u4e0d\u77e5\u6b7b\u6d3b\u7684\u4e1c\u897f\uff0c\u65e2\u7136\u51b2\u7740\u54b1\u4eec\u800c\u6765\uff0c\u542c\u5f97\u5578\u58f0\uff0c\u81ea\u4f1a\u5bfb \u5230\u3002\u201d\u4e24\u4eba\u7ad9\u5728\u5ca9\u77f3\u4e0a\u8fdc\u671b\uff0c\u8fc7\u4e86\u4e0d\u4e45\uff0c\u5ffd\u7136\u89c1\u6709\u4e24\u6761\u4eba\u5f71\uff0c\u4ece\u5c71\u8170\u90a3\u8fb9\u8dd1\u6765\u3002 \u3000\u3000\u5510\u6653\u6f9c\u4ee5\u4e3a\u5fc5\u662f\u5173\u4e1c\u56db\u4fa0\u65e0\u7591\uff0c\u5c4f\u606f\u547c\u5438\uff0c\u7b49\u5f85\u9759\u770b\u4e00\u573a\u6076\u6597\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u6765\u4eba\u4e0a\u5230\u5c71 \u5934\uff0c\u5374\u975e\u5173\u4e1c\u56db\u4fa0\u3002\u4e00\u4eba\u53d1\u7ea2\u5982\u706b\uff0c\u4e00\u4eba\u9f3b\u5982\u9e70\u96bc\uff0c\u7ea2\u53d1\u7684\u8fd9\u4e2a\u6b63\u662f\u56db\u7687\u5b50\u5e9c\u4e2d\u201c\u56db\u9738\u201d\u4e4b \u4e00\u7684\u96f7\u6d77\u97f3\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u9b54\u5934\u4e5f\u4f3c\u9887\u611f\u610f\u5916\uff0c\u540c\u58f0\u95ee\u9053\uff1a\u201c\u96f7\u6d77\u97f3\uff0c\u53eb\u4f60\u9547\u5b88\u8425\u5be8\uff0c\u4f60\u6765\u8fd9\u91cc\u505a\u751a\uff1f\u201d\u96f7 \u6d77\u97f3\u4ee5\u8896\u63e9\u6c57\uff0c\u5598\u6c14\u8bf4\u9053\uff1a\u201c\u5927\u5be8\u7ed9\u4eba\u6311\u4e86\uff01\u201d\u90a3\u4e24\u4e2a\u8001\u5934\u84e6\u5730\u6012\u543c\uff0c\u52a8\u4f5c\u5982\u4e00\uff0c\u8df3\u8d77\u6765 \u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u7ed9\u4eba\u6311\u4e86\uff01\u662f\u5173\u4e1c\u56db\u4fa0\u6478\u6765\u4e86\u4e48\uff1f\u201d\u96f7\u6d77\u97f3\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u662f\u4e0d\u662f\u5173\u4e1c\u56db\u4fa0\uff0c \u6765\u7684\u4e24\u4eba\u4e00\u9ad8\u4e00\u77ee\uff0c\u9ad8\u7684\u90a3\u4e2a\u5341\u6307\u6234\u7740\u94a2\u73af\uff0c\u8fc5\u5982\u98d8\u98ce\uff0c\u5c48\u8d77\u5341\u6307\uff0c\u9022\u4eba\u4fbf\u51ff\uff1b\u77ee\u7684\u90a3\u4e2a\u66f4 \u662f\u5389\u5bb3\uff0c\u6211\u4eec\u7684\u4eba\u4e00\u4e2a\u4e2a\u7ed9\u4ed6\u6293\u7740\u540e\u5fc3\uff0c\u6254\u4e0b\u5c71\u8c37\uff01\u201d\u53f3\u8db3\u8ddb\u7684\u90a3\u8001\u5934\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5514\uff0c\u8fd9 \u662f\u56db\u4fa0\u4e2d\u7684\u8001\u4e09\u8001\u56db\uff0c\u4e07\u91cc\u8ffd\u98ce\u67f3\u5148\u5f00\u548c\u5355\u638c\u5f00\u7891\u9648\u5143\u9738\uff01\u201d\u96f7\u6d77\u97f3\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u820d\u547d \u51b2\u51fa\u53bb\uff0c\u90a3\u6599\u53c8\u6765\u4e86\u4e24\u4e2a\u654c\u4eba\uff0c\u6bd4\u5148\u5934\u90a3\u4e24\u4e2a\u8fd8\u8981\u5389\u5bb3\uff0c\u4e00\u4e2a\u662f\u7626\u9053\u58eb\uff0c\u4e00\u4e2a\u662f\u80d6\u548c\u5c1a\uff0c\u90a3 \u4e2a\u9053\u58eb\u5de6\u624b\u4f7f\u94c1\u62d0\uff0c\u53f3\u624b\u4f7f\u957f\u5251\uff0c\u4e71\u523a\u4e71\u5288\uff0c\u6211\u63a5\u4e86\u4e00\u62db\uff0c\u5175\u5668\u5c31\u7ed9\u524a\u6389\uff0c\u548c\u8eab\u6eda\u4e86\u4e0b\u6765\uff0c \u4fa5\u5e78\u90a3\u9053\u58eb\u6ca1\u6709\u523a\u7b2c\u4e8c\u5251\uff01\u201d\u5de6\u8db3\u8ddb\u7684\u90a3\u8001\u5934\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5514\uff0c\u90a3\u4f60\u5b9a\u662f\u5e26\u4e86\u82b1\u4e86\uff01\u54e6\uff0c\u4e0d \u9519\uff0c\u4f60\u53f3\u8fb9\u7684\u8033\u6735\u6ca1\u6709\u4e86\uff01\u8fd8\u597d\uff0c\u4f60\u53ea\u7ed9\u90a3\u6076\u9053\u524a\u4e86\u4e00\u53ea\u8033\u6735\uff01\u201d\u96f7\u6d77\u97f3\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a \u201c\u90a3\u6076\u9053\u524a\u4e86\u6211\u4e00\u53ea\u8033\u6735\uff0c\u5c31\u5728\u80cc\u540e\u53eb\u9053\uff1a\u2018\u4f60\u4e5f\u7b97\u4e00\u6761\u6c49\u5b50\uff0c\u53ef\u60dc\uff01\u53ef\u60dc\uff01\u597d\u597d\u4fdd\u7740\u5de6\u9762 \u90a3\u53ea\u8033\u6735\u5427\uff01\u2019\u201d\u4e24\u4e2a\u8001\u5934\u53c8\u6c14\u53c8\u607c\uff0c\u8bf4\u9053\uff1a\u201c\u5173\u4e1c\u56db\u4fa0\u7684\u81ed\u89c4\u77e9\u771f\u53eb\u4eba\u6c14\u3002\u201d\u56de\u987e\u90a3\u9e70\u9f3b \u7684\u6c49\u5b50\u9053\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u7684\u5de6\u773c\u662f\u4e0d\u662f\u7ed9\u80d6\u548c\u5c1a\u7528\u9152\u55b7\u778e\u7684\uff1f\u201d\u90a3\u9e70\u9f3b\u6c49\u5b50\u5de6\u773c\u8840\u6d41\u672a\u6b62\uff0c\u6b63 \u6495\u4e0b\u8863\u895f\u6cbe\u836f\u6577\u4f24\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u5085\uff0c\u6211\uff0c\u6211\u4e0d\u4e2d\u7528\uff0c\u662f\u7ed9\u90a3\u80d6\u548c\u5c1a\u7528\u9152\u55b7\u778e\u7684\uff01\u201d\u4e24\u4e2a \u8001\u5934\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u5ffd\u7136\u53c8\u5404\u81ea\u602a\u5578\u4e09\u58f0\uff0c\u8eb2\u5728\u5ca9\u540e\u7684\u4e09\u4eba\u76f8\u987e\u5931\u8272\u3002\u909d\u7ec3\u971e\u6000\u4e2d\u7684\u51af\u745b\u5589\u5934\u4f5c \u54cd\uff0c\u773c\u4e2d\u6ef4\u6cea\uff0c\u624b\u821e\u8db3\u8e48\uff0c\u5728\u5988\u5988\u6000\u4e2d\u6323\u624e\uff0c\u909d\u7ec3\u971e\u5fc3\u5934\u4f5c\u75db\uff0c\u4f46\u53c8\u4e0d\u6562\u628a\u5979\u53e3\u4e2d\u7684\u5e03\u56e2\u53d6 \u51fa\u6765\u3002\u201d \u3000\u3000\u5de6\u9762\u90a3\u8001\u5934\u9053\uff1a\u201c\u770b\u6765\u5173\u4e1c\u56db\u4fa0\u7684\u529f\u592b\u679c\u662f\u4e0d\u5f31\uff01\u201d\u53f3\u9762\u7684\u8001\u5934\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5927 \u54e5\uff0c\u51ed\u6211\u4eec\u795e\u9b54\u53cc\u8001\u4e4b\u540d\uff0c\u7b97\u4ed6\u662f\u5173\u4e1c\u516b\u4fa0\u53c8\u6709\u4f55\u4ff1\uff1f\u201d\u5510\u6653\u6f9c\u542c\u5f97\u5206\u660e\uff0c\u8fd9\u4e00\u9707\u60ca\u975e\u540c\u5c0f \u53ef\u3002\u5468\u9752\u4ee5\u524d\u63d0\u8fc7\u795e\u9b54\u53cc\u8001\u4e4b\u540d\uff0c\u636e\u8bf4\u8fd9\u4e24\u4eba\u672c\u662f\u5b6a\u751f\u5144\u5f1f\uff0c\u54e5\u54e5\u540d\u53eb\u8428\u5929\u523a\uff0c\u5f1f\u5f1f\u540d\u53eb\u8428 \u5929\u90fd\u3002\u4e24\u5144\u5f1f\u4e0d\u77e5\u4ece\u90a3\u91cc\u5b66\u6765\u4e00\u8eab\u6b66\u529f\uff0c\u54e5\u54e5\u662f\u5185\u5916\u517c\u4fee\uff0c\u5f1f\u5f1f\u5b66\u7684\u5374\u662f\u897f\u85cf\u9b54\u6559\u4e2d\u7684\u5c0f\u8bf8 \u5929\u5927\u91d1\u94a2\u624b\u3002\u54e5\u54e5\u88ab\u79f0\u4e3a\u201c\u81c2\u795e\u9b54\u201d\uff0c\u5f1f\u5f1f\u88ab\u79f0\u4e3a\u201c\u5927\u529b\u795e\u9b54\u201d\u3002\u4e24\u4eba\u4f4f\u5728\u65c5\u987a\u53e3\u5916\u4e00\u4e2a\u53eb \u201c\u732b\u9e70\u5c9b\u201d\u7684\u6d77\u5c9b\u4e0a\uff0c\u548c\u90bb\u5c45\u201c\u86c7\u5c9b\u201d\u4e0a\u7684\u4e00\u4f4d\u5f02\u4eba\u6bd2\u9f99\u5c0a\u8005\u53c8\u5408\u79f0\u8fbd\u4e1c\u4e09\u602a\u3002\u65c5\u987a\u53e3\u5916\u7684 \u201c\u86c7\u5c9b\u201d\u548c\u201c\u732b\u9e70\u5c9b\u201d\uff0c\u662f\u6e24\u6d77\u5916\u4e24\u4e2a\u6700\u795e\u79d8\u7684\u5c9b\u5c7f\uff0c\u5343\u767e\u5e74\u6765\uff0c\u4ece\u6ca1\u4eba\u6562\u5230\u5c9b\u4e0a\u63a2\u9669\uff0c\u6e14 \u4eba\u6253\u9c7c\uff0c\u56fa\u7136\u8981\u8fdc\u8fdc\u7ed5\u8fc7\uff0c\u5c31\u662f\u6b66\u6797\u8c6a\u4fa0\uff0c\u4e5f\u4e0d\u6562\u4e00\u5c65\u65af\u571f\uff01 \u3000\u3000\u636e\u8bf4\u65c5\u987a\u53e3\u5916\u7684\u201c\u86c7\u5c9b\u201d\uff0c\u5c9b\u4e0a\u6bd2\u86c7\u904d\u5e03\uff0c\u5618\u6c14\u6210\u96fe\uff0c\u800c\u201c\u732b\u9e70\u5c9b\u201d\u4e0a\u5219\u51fa\u4ea7\u4e00\u79cd\u602a \u9e1f\uff0c\u539f\u5c5e\u6d77\u9e25\u7684\u4e00\u79cd\uff0c\u9e23\u65f6\u6709\u5982\u732b\u53eb\uff0c\u5229\u722a\u53c8\u5982\u732b\u722a\uff0c\u6240\u4ee5\u88ab\u540d\u4e3a\u201c\u732b\u9e70\u201d\u3002\u732b\u9e70\u4e00\u98de\u4e5f\u5fc5 \u5b9a\u4e00\u5927\u7fa4\uff0c\u5e38\u5e38\u548c\u201c\u86c7\u5c9b\u201d\u4e0a\u7684\u6bd2\u86c7\u6076\u6597\uff0c\u732b\u9e70\u4f4e\u98de\u4e0b\u6765\uff0c\u5e38\u7ed9\u6bd2\u86c7\u7f20\u6bd9\uff0c\u6216\u4e2d\u6bd2\u6c14\u8dcc\u843d\uff0c \u4f46\u6bd2\u86c7\u4e5f\u5e38\u7ed9\u732b\u9e70\u7a81\u88ad\uff0c\u4e00\u6293\u6293\u4e0a\u534a\u7a7a\u3002\u6e24\u6d77\u6e14\u6c11\uff0c\u4e00\u89c1\u732b\u9e70\u4e0e\u6bd2\u86c7\u76f8\u6597\uff0c\u90fd\u8fdc\u8fdc\u907f\u5f00\uff0c\u5f85 \u6076\u6597\u8fc7\u540e\uff0c\u624d\u6253\u6d9d\u843d\u5230\u6d77\u9762\u7684\u732b\u9e70\u548c\u7ed9\u732b\u9e70\u6293\u88c2\u7684\u6bd2\u86c7\u3002\u5317\u65b9\u4eba\u4e0d\u98df\u86c7\uff0c\u636e\u8bf4\u67d0\u5e74\u6709\u4e00\u4e2a\u5e7f \u4e1c\u540d\u53a8\u5e08\u5230\u65c5\u987a\u4f5c\u5ba2\uff1b\u6070\u503c\u732b\u9e70\u4e0e\u6bd2\u86c7\u76f8\u6597\uff0c\u6709\u6e14\u6c11\u635e\u83b7\u6bd2\u86c7\u56de\u6765\uff0c\u539f\u62df\u6d78\u5236\u836f\u9152\u7684\uff0c\u8fd9\u4f4d \u540d\u53a8\u5e08\u4e70\u4e86\u4e24\u6761\uff0c\u5f04\u4f5c\u86c7\u7fb9\uff0c\u636e\u8bf4\u86c7\u8089\u4e4b\u7f8e\uff0c\u8fdc\u5728\u5404\u5730\u4e4b\u4e0a\u3002\u8fd9\u662f\u9898\u5916\u4e4b\u8bdd\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u5468\u9752\u542c\u8bf4\u5345\u591a\u5e74\u7684\uff0c\u5927\u529b\u795e\u9b54\u8428\u5929\u90fd\u5728\u897f\u85cf\uff0c\u548c\u897f\u57df\u4e09\u9b54\u7ed3\u4e3a\u515a\u7fbd\uff0c\u897f\u57df\u4e09\u9b54\u4e27\u4e8e\u51cc\u672a \u98ce\u4e4b\u624b\uff0c\u8428\u5929\u90fd\u540e\u6765\u4e5f\u7ed9\u51cc\u672a\u98ce\u8d76\u51fa\u897f\u57df\u3002\uff08\u201c\u897f\u57df\u4e09\u9b54\u201d\uff09\u6076\u6597\u51cc\u672a\u98ce\u4e4b\u4e8b\u89c1\u62d9\u8457\u300a\u4e03\u5251 \u4e0b\u5929\u5c71\u300b\uff0c\u8428\u5929\u90fd\u88ab\u9010\u51fa\u897f\u57df\u540e\uff0c\u4e0e\u6f2b\u6e38\u4e1c\u5317\u7684\u54e5\u54e5\u8428\u5929\u523a\u4f1a\u5408\uff0c\u5728\u201c\u732b\u9e70\u5c9b\u201d\u4e0a\u7ed3\u4e86\u5de2 \u7a74\u3002\u53e6\u4e00\u4f4d\u201c\u6bd2\u9f99\u5c0a\u8005\u201d\u5584\u6cbb\u6bd2\u86c7\uff0c\u9690\u5c45\u5728\u201c\u86c7\u5c9b\u201d\u4e4b\u4e0a\u3002\u201c\u6bd2\u9f99\u5c0a\u8005\u201d\u4e00\u751f\u4f4f\u5728\u86c7\u5c9b\uff0c\u4ece \u4e0d\u5916\u51fa\uff0c\u6240\u4ee5\u6b66\u529f\u5982\u4f55\uff0c\u65e0\u4eba\u77e5\u9053\u3002\u201c\u516b\u81c2\u795e\u9b54\u201d\u8428\u5929\u523a\u548c\u201c\u5927\u529b\u795e\u9b54\u201d\u8428\u5929\u90fd\u5219\u6bcf\u9694\u4e24\u5e74 \u5c31\u51fa\u5916\u4e00\u6b21\uff0c\u6ecb\u4e8b\u751f\u7aef\uff0c\u548c\u8bb8\u591a\u6b66\u4fa0\u8c6a\u82f1\uff0c\u7ed3\u4e86\u4ec7\u51a4\u3002\u5341\u4f59\u5e74\u524d\uff0c\u53cc\u9b54\u5ffd\u7136\u9500\u58f0\u533f\u8ff9\uff0c\u4e0d\u518d \u5728\u6c5f\u6e56\u9732\u9762\uff0c\u6709\u4eba\u8bf4\u4ed6\u4eec\u662f\u78b0\u5230\u5f3a\u654c\uff0c\u53d7\u4e86\u632b\u6298\uff0c\u6240\u4ee5\u8eb2\u56de\u201c\u732b\u9e70\u5c9b\u201d\u53bb\u7ec3\u72ec\u95e8\u6b66\u529f\u4e86\uff0c\u8fd9 \u4e8b\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff0c\u6b66\u6797\u4e2d\u7684\u4fa0\u58eb\u4e5f\u4e0d\u6562\u5230\u201c\u732b\u9e70\u5c9b\u201d\u4e0a\u53bb\u627e\u4ed6\u4eec\u3002\u4e0d\u6599\u8fd9\u65f6\u5374\u5ffd\u7136\u51fa\u73b0\u5728\u592a \u884c\u5c71\u4e0a\u3002 \u3000\u3000\u4f60\u9053\u8fd9\u4e24\u4e2a\u9b54\u5934\uff0c\u5982\u4f55\u4f1a\u7a81\u7136\u590d\u51fa\u6c5f\u6e56\u3002\u539f\u6765\u4ed6\u4eec\u4e5f\u662f\u56db\u7687\u5b50\u5141\u796f\u5351\u8f9e\u91cd\u5b9d\u793c\u8058\u6765\u7684\u3002 \u56db\u7687\u5b50\u6d3e\u4e86\u4e00\u4e2a\u6b66\u529f\u9ad8\u5f3a\u7684\u5587\u561b\uff0c\u62ab\u6234\u5168\u8eab\u76d4\u7532\uff0c\u8fde\u773c\u775b\u4e5f\u85cf\u5728\u73bb\u7483\u9576\u5d4c\u7684\u5934\u76d4\u4e4b\u5185\uff0c\u590d\u5e26 \u4e86\u5927\u5185\u4e13\u89e3\u86c7\u6bd2\u4e0e\u9884\u9632\u7ed9\u732b\u9e70\u6293\u4f24\u7684\u91d1\u521b\u5723\u836f\u524d\u5f80\uff0c\u5148\u5230\u201c\u86c7\u5c9b\u201d\u8c12\u89c1\u6bd2\u9f99\u5c0a\u8005\uff0c\u6bd2\u9f99\u5c0a\u8005 \u65e0\u8bba\u5982\u4f55\u4e0d\u80af\u51fa\u5c71\uff0c\u518d\u5230\u201c\u732b\u9e70\u5c9b\u201d\u4e0a\u53bb\u89c1\u53cc\u9b54\uff0c\u53cc\u9b54\u72ec\u95e8\u6b66\u529f\u5df2\u7ecf\u7ec3\u597d\uff0c\u9759\u6781\u601d\u52a8\uff0c\u5fc3 \u60f3\uff1a\u4ee5\u56db\u7687\u5b50\u7684\u82f1\u660e\uff0c\u5c06\u6765\u5fc5\u767b\u5927\u5b9d\uff0c\u81ea\u5df1\u82e5\u80fd\u52a9\u4ed6\u593a\u4f4d\uff0c\u5c06\u6765\u53ef\u80fd\u8eab\u4e3a\u56fd\u5e08\uff0c\u540d\u626c\u5929\u4e0b\u3002 \u53cc\u9b54\u4e0d\u7231\u91cd\u5b9d\uff0c\u5374\u7231\u540d\u4f4d\uff0c\u7adf\u7136\u63a5\u4e86\u56db\u7687\u5b50\u5141\u796f\u4e4b\u8058\uff0c\u79bb\u5f00\u6d77\u5c9b\u3002 \u3000\u3000\u56db\u7687\u5b50\u5141\u796f\u95e8\u4e0b\u5947\u4eba\u5f02\u58eb\u6700\u591a\uff0c\u5141\u796f\u77e5\u5317\u4e94\u7701\u8c6a\u6770\u4eca\u5e74\u5728\u592a\u884c\u5c71\u96c6\u4f1a\uff0c\u7adf\u601d\u4e00\u7f51\u6253\u5c3d\uff0c \u4ee5\u53d6\u7236\u7687\u5ba0\u7231\uff0c\u800c\u4e3a\u593a\u4f4d\u4e4b\u8c0b\uff0c\u4e8e\u662f\u6d3e\u51fa\u4e09\u767e\u540d\u6b66\u58eb\uff0c\u5176\u4e2d\u6709\u8840\u6ef4\u5b50\u767e\u4f59\uff0c\u8ffd\u6355\u5468\u9752\u7684\u53ea\u662f \u5176\u4e2d\u4e00\u6279\u3002\u53e6\u5916\u4e00\u5927\u7fa4\u6b66\u58eb\u5219\u7531\u53cc\u9b54\u7387\u9886\uff0c\u76f4\u6251\u592a\u884c\u5c71\uff0c\u6cbf\u9014\u5df2\u4f24\u4e86\u8bb8\u591a\u5e2e\u4f1a\u7684\u4eba\uff0c\u4e2d\u79cb\u4e4b \u5915\uff0c\u66f4\u5728\u592a\u884c\u5c71\u4e0a\u548c\u5317\u4e94\u7701\u6570\u767e\u8c6a\u6770\u5927\u6218\uff0c\u6768\u4ef2\u82f1\u94c1\u638c\u795e\u5f39\uff0c\u8fde\u6bd9\u5341\u4f59\u6b66\u571f\uff0c\u5374\u88ab\u516b\u81c2\u795e\u9b54 \u8428\u5929\u523a\u6bd2\u6293\u6293\u4f24\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u5927\u6218\uff0c\u5317\u4e94\u7701\u8c6a\u6770\u6b7b\u4f24\u8fc7\u534a\uff0c\u6240\u4ee5\u5510\u6653\u6f9c\u7b49\u8fde\u65e5\u884c\u6765\uff0c\u6cbf\u9014\u6240\u89c1\u7684\u6c5f\u6e56\u4eba\u7269\uff0c\u90fd \u662f\u4ece\u592a\u884c\u5c71\u7a81\u56f4\u51fa\u6765\u7684\u3002\u5176\u4e2d\u7684\u9c81\u897f\u5927\u8c6a\u98de\u706b\u5f39\u5b5f\u5efa\u96c4\u521a\u5230\u592a\u884c\u5c71\u811a\uff0c\u5c31\u5f97\u4eba\u62a5\u8b66\uff0c\u8fde\u5fd9\u6298 \u56de\uff0c\u5f97\u4ee5\u6beb\u53d1\u65e0\u4f24\u3002\u6768\u4ef2\u82f1\u4e2d\u4e86\u6bd2\u6293\uff0c\u53c8\u7ed9\u516b\u81c2\u795e\u9b54\u7684\u552f\u4e00\u5f1f\u5b50\u8463\u592a\u6e05\u7387\u4f17\u56f4\u653b\uff0c\u51e0\u906d\u4e0d\u6d4b\u3002 \u3000\u3000\u53cc\u9b54\u63a5\u5230\u8b66\u62a5\uff0c\u77e5\u9053\u5173\u4e1c\u56db\u4fa0\u5df2\u7ecf\u4e0a\u5c71\uff0c\u76f8\u987e\u800c\u7b11\uff01\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u9053\uff1a\u201c\u4eca\u65e5\u82e5\u80fd\u4e00 \u4e3e\u51fb\u8d25\u5173\u4e1c\u56db\u4fa0\uff0c\u5317\u65b9\u8c6a\u6770\u90fd\u4f1a\u671b\u98ce\u62dc\u670d\uff0c\u7136\u540e\u54b1\u4eec\u518d\u4e0b\u6c5f\u5357\uff0c\u526a\u9664\u6c5f\u5357\u516b\u4fa0\u3002\u201d\u5927\u529b\u795e\u9b54 \u8428\u5929\u90fd\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u5148\u7ed9\u5173\u4e1c\u56db\u4fa0\u4e00\u4e2a\u4e0b\u9a6c\u5a01\u3002\u201d\u4e24\u4eba\u53c8\u7ed5\u5730\u8d70\u4e86\u4e00\u5708\uff0c\u8428\u5929\u90fd\u7a81\u7136\u4e00\u58f0 \u5927\u559d\uff0c\u5411\u5510\u6653\u6f9c\u85cf\u8eab\u4e4b\u5904\u884c\u6765\uff0c\u5510\u6653\u6f9c\u909d\u7ec3\u971e\u90fd\u5413\u5f97\u6ee1\u8eab\u51b7\u6c57\uff0c\u9762\u65e0\u4eba\u8272\uff0c\u770b\u90a3\u8428\u5929\u90fd\uff0c\u53ea \u89c1\u4ed6\u5ffd\u7136\u5728\u524d\u9762\u505c\u4e86\u4e0b\u6765\uff0c\u53cc\u624b\u62b1\u7740\u4e00\u5757\u7a81\u51fa\u6765\u7684\u5ca9\u77f3\uff0c\u559d\u58f0\u201c\u5012\uff01\u201d\u628a\u90a3\u5757\u5ca9\u77f3\u6500\u4e86\u4e0b \u6765\uff01\u82e5\u975e\u4e24\u81c2\u6709\u5343\u4e07\u65a4\u795e\u529b\uff0c\u8fd9\u5ca9\u77f3\u4e5f\u6500\u5b83\u4e0d\u52a8\uff0c\u5510\u6d47\u6f9c\u7eb5\u7136\u80c6\u5927\u5305\u5927\uff0c\u4e5f\u5413\u5f97\u5168\u8eab\u8f6f\u4e86\uff0c \u909d\u7ec3\u971e\u5fc3\u91cc\u6697\u53eb\u201c\u83e9\u8428\u4fdd\u4f51\u201d\uff0c\u51af\u745b\u4e00\u5bf9\u773c\u775b\u95ea\u5440\u95ea\u7684\uff0c\u6cea\u73e0\u5df2\u6ef4\u6e7f\u5979\u7684\u56f4\u5dfe\uff0c\u60f3\u662f\u56e0\u4e3a\u53e3 \u4e2d\u5e03\u56e2\u585e\u5f97\u8fc7\u4e45\uff0c\u547c\u5438\u6709\u70b9\u56f0\u96be\uff0c\u6240\u4ee5\u4e00\u9762\u6d41\u6cea\uff0c\u4e00\u9762\u77a7\u7740\u5979\u7684\u6bcd\u4eb2\uff0c\u4f3c\u5728\u54c0\u6c42\u5979\u7684\u6bcd\u4eb2\uff0c \u53d6\u51fa\u5e03\u56e2\uff0c\u8ba9\u5979\u900f\u6c14\u4f3c\u7684\u3002 \u3000\u3000\u8428\u5929\u90fd\u6500\u4e0b\u5ca9\u77f3\uff0c\u8d70\u56de\u539f\u5730\uff0c\u5c06\u5ca9\u77f3\u5e73\u653e\u8349\u5730\u4e4b\u4e0a\uff0c\u7b11\u5bf9\u516b\u81c2\u795e\u9b54\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u770b\u8fd9 \u5ca9\u77f3\u591a\u5e73\u6ed1\uff0c\u6070\u4f3c\u4e00\u5f20\u5706\u684c\u3002\u5f85\u6211\u518d\u627e\u5b83\u51e0\u5757\uff01\u201d\u5510\u6653\u6f9c\u8fd9\u624d\u77e5\u9053\u8428\u5929\u90fd\u6500\u4e0b\u8fd9\u5757\u5ca9\u77f3\uff0c\u539f \u6765\u662f\u7279\u522b\u9009\u6765\u5f53\u4f5c\u684c\u9762\u7528\u7684\uff0c\u53ea\u4e0d\u77e5\u4ed6\u8981\u8fd9\u77f3\u684c\u5e72\u4ec0\u4e48\uff0c\u96be\u9053\u662f\u60f3\u5728\u6df1\u5c71\u4e4b\u4e0a\u6446\u9152\u8bf7\u5ba2\uff1f \u3000\u3000\u8428\u5929\u90fd\u56db\u56f4\u4e00\u8d70\uff0c\u53c8\u6500\u4e0b\u4e86\u4e94\u5757\u5ca9\u77f3\uff0c\u8fde\u524d\u516d\u5757\uff0c\u6574\u6574\u9f50\u9f50\u7684\u6446\u597d\uff0c\u56db\u9762\u56db\u5757\uff0c\u4e2d\u95f4\u4e8c \u5757\uff0c\u7b11\u9053\u201c\u884c\u4e86\u201d\uff0c\u628a\u5e26\u6765\u7684\u5927\u76ae\u888b\u6253\u5f00\uff0c\u5c06\u91cc\u9762\u7684\u4e1c\u897f\u4e00\u4e2a\u4e00\u4e2a\u7684\u62ff\u51fa\u6765\uff0c\u909d\u7ec3\u971e\u4e00\u77a7\u767b \u65f6\u6655\u4e86\u8fc7\u53bb\u3002\u539f\u6765\u8428\u5929\u90fd\u5728\u76ae\u888b\u4e2d\u62ff\u51fa\u6765\u7684\uff0c\u7adf\u7136\u662f\u4e00\u4e2a\u4e00\u4e2a\u7684\u4eba\u5934\uff01\u6bcf\u4e2a\u4eba\u5934\u90fd\u7ed9\u4ed6\u7528\u836f \u6c34\u70bc\u8fc7\uff0c\u9762\u76ee\u5b8c\u6574\uff0c\u795e\u60c5\u5982\u751f\uff0c\u53ea\u662f\u6bd4\u751f\u524d\u7f29\u5c0f\u4e86\u4e00\u534a\u6709\u591a\uff0c\u4eba\u5934\u4e2d\u7684\u8111\u9ad3\u5df2\u5168\u90e8\u53d6\u51fa\uff0c\u4e2d \u95f4\u6316\u7a7a\uff0c\u8428\u5929\u90fd\u5c06\u4e00\u4e2a\u4e2a\u7684\u4eba\u5934\u5b89\u653e\u5728\u77f3\u684c\u4e4b\u4e0a\uff0c\u6bcf\u5f20\u77f3\u684c\u6070\u597d\u516d\u4e2a\u4eba\u5934\u3002\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a \u4e5f\u6253\u5f00\u5e26\u6765\u7684\u76ae\u56ca\uff0c\u9152\u9999\u56db\u6ea2\uff0c\u539f\u6765\u662f\u4e00\u888b\u7f8e\u9152\uff0c\u4e24\u5144\u5f1f\u5c06\u56ca\u4e2d\u7f8e\u9152\u503e\u5165\u4eba\u5934\u4e4b\u4e2d\uff0c\u5934\u76d6\u5411 \u4e0b\uff0c\u9888\u8154\u5411\u4e0a\uff0c\u4ecd\u7136\u5e73\u653e\u684c\u4e0a\uff0c\u62cd\u624b\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u5c31\u8fd9\u6837\u8bf7\u5173\u4e1c\u56db\u4fa0\u559d\u9152\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u7684\u5fc3\u535c\u535c\u7684\u8df3\uff0c\u89c1\u909d\u7ec3\u971e\u6655\u5012\uff0c\u6025\u5fd9\u6276\u5979\u8d77\u6765\uff0c\u5ffd\u7136\u77a7\u89c1\u738b\u9675\uff0c\u867d\u7136\u4f0f\u5728\u5730\u4e0a\uff0c \u795e\u8272\u5374\u5e76\u4e0d\u600e\u6837\u60ca\u60f6\uff0c\u5510\u6653\u6f9c\u4e0d\u7981\u5947\u5f02\uff0c\u5fc3\u60f3\uff1a\u8fd9\u4f4d\u5e08\u54e5\u600e\u4e48\u5982\u6b64\u5927\u80c6\u5440\uff01 \u3000\u3000\u909d\u7ec3\u971e\u60a0\u60a0\u9192\u8f6c\uff0c\u795e\u667a\u8ff7\u7cca\uff0c\u4e00\u9192\u8fc7\u6765\u89c1\u51af\u745b\u9762\u8272\u82cd\u767d\uff0c\u6c57\u73e0\u6cea\u73e0\u6df7\u5728\u4e00\u8d77\uff0c\u6323\u624e\u6b32 \u8d77\uff0c\u5c0f\u53e3\u5f20\u5f00\uff0c\u909d\u7ec3\u971e\u4e00\u65f6\u5fc3\u75db\uff0c\u5728\u795e\u667a\u8ff7\u7cca\u4e2d\uff0c\u7adf\u628a\u51af\u745b\u53e3\u4e2d\u7684\u5e03\u56e2\u53d6\u51fa\uff0c\u51af\u745b\u201c\u54c7\u201d\u7684 \u4e00\u58f0\u5927\u54ed\u8d77\u6765\uff01 \u3000\u3000\u8428\u5929\u523a\u602a\u53eb\u4e00\u58f0\uff0c\u53cc\u811a\u70b9\u5730\uff0c\u8eab\u5f62\u5e73\u5730\u62d4\u8d77\uff0c\u4fe8\u5982\u4e00\u53ea\u51b2\u5929\u5927\u96c1\uff0c\u500f\u53c8\u51cc\u7a7a\u6251\u4e0b\uff0c\u4e00\u6293 \u5411\u5510\u6653\u6f9c\u85cf\u8eab\u4e4b\u5904\u6293\u6765\uff0c\u5510\u6653\u6f9c\u4e00\u628a\u98de\u8292\u8fce\u7a7a\u6d12\u53bb\uff0c\u5206\u660e\u679d\u679d\u90fd\u6253\u4e2d\u516b\u81c2\u795e\u9b54\u8eab\u4e0a\uff0c\u4f46\u5374\u53ee \u53ee\u8fde\u58f0\uff0c\u7eb7\u7eb7\u843d\u4e0b\uff0c\u98de\u8292\u89e6\u53ca\u4ed6\u7684\u8eab\u4f53\u5c31\u5982\u89e6\u53ca\u94c1\u77f3\u4e00\u822c\uff01\u5510\u6653\u6f9c\u5fc3\u80c6\u4ff1\u5bd2\uff0c\u9ed1\u5f71\u5f53\u7a7a\u7f69 \u4e0b\uff0c\u568e\u7684\u4e00\u58f0\u5ca9\u77f3\u788e\u88c2\uff0c\u706b\u661f\u84ec\u98de\uff0c\u539f\u6765\u662f\u8428\u5929\u523a\u6765\u52bf\u592a\u75be\uff0c\u4e00\u6293\u6293\u88c2\u5510\u6653\u6f9c\u9762\u524d\u90a3\u5757\u5ca9 \u77f3\uff0c\u518d\u98de\u8d77\u4e00\u817f\uff0c\u628a\u5ca9\u77f3\u8e0f\u8fc7\u4e00\u8fb9\uff0c\u94dc\u94c3\u822c\u7684\u53cc\u773c\uff0c\u77aa\u7740\u5510\u6653\u6f9c\u4e09\u4eba\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u662f \u8c01\uff0c\u5feb\u5feb\u6eda\u51fa\uff01\u201d \u3000\u3000\u51af\u745b\u201c\u54c7\u54c7\u201d\u5927\u54ed\uff0c\u628a\u5934\u4f0f\u5728\u6bcd\u4eb2\u80a9\u4e0a\uff0c\u4e0d\u6562\u770b\u201c\u516b\u81c2\u795e\u9b54\u201d\u7684\u51f6\u76f8\u3002\u909d\u7ec3\u971e\u521a\u624d\u672c\u5df2 \u5413\u5f97\u5168\u8eab\u9ebb\u8f6f\uff0c\u8fd9\u65f6\u5ffd\u7136\u5de6\u624b\u6478\u5200\uff0c\u53f3\u624b\u7d27\u62b1\u7740\u5b69\u5b50\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u4e0d\u51c6\u52a8\u6211\u7684\u5c0f\u5b9d\u5b9d\uff01\u201d \u9762\u8272\u51db\u7136\uff0c\u795e\u60c5\u50b2\u5140\uff0c\u6bcd\u6027\u7684\u672c\u80fd\uff0c\u9661\u7136\u4f7f\u5979\u5145\u6ee1\u52c7\u6c14\uff0c\u9762\u5bf9\u51f6\u6076\u9b54\u5934\uff0c\u7adf\u662f\u6beb\u65e0\u6240\u60e7\uff01 \u3000\u3000\u516b\u81c2\u795e\u9b54\u7a92\u4e86\u4e00\u7a92\uff0c\u4e0d\u89c9\u9000\u540e\u51e0\u6b65\uff0c\u5510\u6653\u6f9c\u7684\u6e38\u9f99\u5251\u84e6\u7136\u51fa\u624b\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u4e00\u62db\u201c\u98de\u4e91 \u63a3\u7535\u201d\uff0c\u5411\u8428\u5929\u523a\u8fce\u9762\u523a\u6765\uff0c\u8428\u5929\u523a\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u98d8\u8eab\u95ea\u8fc7\u3002\u8fd9\u65f6\u5927\u529b\u795e\u9b54\u8428\u5929\u90fd\u4e5f\u5df2\u8d76 \u5230\uff0c\u4e00\u638c\u51fb\u4e0b\uff0c\u516b\u81c2\u795e\u9b54\u5df2\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u4f24\u4ed6\uff01\u201d\u8428\u5929\u90fd\u638c\u5230\u4e2d\u9014\uff0c\u7a81\u7136\u53d8\u6293\uff0c\u5510\u6653\u6f9c \u5251\u5cf0\u4e00\u8f6c\uff0c\u72b9\u5f85\u523a\u51fa\uff0c\u7a81\u89c9\u624b\u8155\u4e00\u75db\uff0c\u4f3c\u7ed9\u94c1\u7b8d\u7b8d\u4f4f\u4e00\u822c\uff0c\u5b9d\u5251\u7adf\u7ed9\u5288\u624b\u593a\u53bb\uff0c\u4eba\u4e5f\u88ab\u631f\u4e86 \u8d77\u6765\uff01 \u3000\u3000\u909d\u7ec3\u971e\u62b1\u7740\u51af\u745b\uff0c\u5140\u7acb\u5f53\u5730\uff0c\u51af\u745b\u8d8a\u54ed\u8d8a\u5927\u58f0\uff0c\u909d\u7ec3\u971e\u7adf\u7136\u628a\u5200\u63d2\u56de\u9798\u4e2d\uff0c\u5de6\u624b\u8f7b\u8f7b\u629a \u62cd\uff0c\u4f4e\u4f4e\u8bf4\u9053\uff1a\u201c\u5c0f\u5b9d\u5b9d\uff0c\u4e0d\u8981\u6015\uff0c\u597d\u597d\u7761\u4e00\u89c9\uff0c\u660e\u513f\u5988\u5988\u4e70\u7cd6\u7ed9\u4f60\u5403\uff0c\u5e26\u4f60\u4e0a\u5c71\u6293\u4e4c \u9e26\uff01\u201d\u5979\u4e0d\u7406\u773c\u524d\u51f6\u9669\uff0c\u7adf\u7136\u7ed9\u5c0f\u5b9d\u5b9d\u5531\u8d77\u50ac\u7720\u66f2\u6765\u4e86\uff01 \u3000\u3000\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u7ed9\u54ed\u58f0\u5531\u58f0\u6405\u5f97\u5fc3\u70e6\uff0c\u626c\u7a7a\u4e00\u6293\uff0c\u909d\u7ec3\u971e\u53cc\u773c\u4e00\u7741\uff0c\u5149\u8292\u51db\u51db\uff0c\u8428\u5929\u523a \u4fa7\u8fc7\u4e86\u8138\uff0c\u624b\u81c2\u4e00\u8f6c\uff0c\u628a\u51af\u745b\u62a2\u5230\u624b\u4e2d\uff0c\u559d\u9053\uff1a\u201c\u4f60\u54ed\uff01\u201d\u4e3e\u8d77\u51af\u745b\uff0c\u8981\u5411\u5ca9\u77f3\u6454\u53bb\uff01 \u3000\u3000\u51af\u745b\u54ed\u5f97\u75b2\u5026\uff0c\u6e10\u6e10\u6536\u58f0\uff0c\u7ed9\u8428\u5929\u523a\u4e3e\u5230\u534a\u7a7a\uff0c\u89c9\u5f97\u597d\u73a9\uff0c\u6536\u4e86\u773c\u6cea\uff0c\u5ffd\u7136\u4e00\u7b11\uff0c\u8428\u5929 \u523a\u548c\u5b69\u5b50\u9762\u5bf9\u7740\u9762\uff0c\u77a7\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u6ee1\u8154\u6740\u6c14\uff0c\u5728\u5b69\u5b50\u4e00\u7b11\u4e4b\u4e0b\uff0c\u7a81\u7136\u6d88\u5931\uff0c\u624b\u81c2\u6162\u6162\u5782\u4e86 \u4e0b\u6765\u3002\u51af\u745b\u53c8\u7b11\u4e86\u4e00\u7b11\uff0c\u988a\u4e0a\u9152\u6da1\u9690\u73b0\uff0c\u5c0f\u8138\u751f\u6625\uff0c\u8428\u5929\u523a\u53ea\u89c9\u624b\u4e2d\u7684\u5b69\u5b50\u7389\u96ea\u53ef\u7231\uff0c\u4ed6\u5e73 \u751f\u6740\u4eba\u5982\u8349\uff0c\u4ece\u4e0d\u76b1\u7709\uff0c\u73b0\u5728\u5374\u600e\u4e48\u4e5f\u52a8\u4e0d\u4e86\u624b\uff01\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u7981\u597d\u751f\u5947\u5f02\uff0c\u53cd\u624b\u628a\u5b69\u5b50\u8d1f\u5728 \u80cc\u540e\uff0c\u7b11\u9053\uff1a\u201c\u54b3\uff0c\u8fd9\u771f\u662f\u7f18\u6cd5\uff01\u201d \u3000\u3000\u4f0f\u5728\u8513\u8349\u91cc\u7684\u738b\u9675\uff0c\u8815\u8815\u800c\u52a8\uff0c\u4e0d\u6562\u7ad9\u4e86\u8d77\u6765\uff0c\u8428\u5929\u523a\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u909d\u7ec3\u971e\u5931\u4e86 \u5b69\u5b50\uff0c\u62fc\u547d\u51b2\u4e0a\uff0c\u8428\u5927\u523a\u5e76\u8d77\u4e2d\u98df\u6307\uff0c\u8f7b\u8f7b\u4e00\u70b9\uff0c\u909d\u7ec3\u971e\u5168\u8eab\u9ebb\u8f6f\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c\u738b\u9675\u5ffd\u7136\u51b2 \u4e86\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u56fd\u5e08\u7237\uff0c\u8bf7\u770b\u5728\u5c0f\u7684\u9762\u4e0a\uff0c\u4e0d\u8981\u4f24\u5979\uff01\u201d \u3000\u3000\u8428\u5929\u523a\u5f20\u76ee\u6ce8\u89c6\uff0c\u4f9d\u7a00\u8ba4\u5f97\uff0c\u96f7\u6d77\u97f3\u65e9\u51d1\u4e86\u4e0a\u6765\uff0c\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u53eb\u505a\u738b\u9675\uff0c\u662f \u6211\u4eec\u6d3e\u5230\u51af\u5bb6\u5367\u5e95\u7684\uff01\u201d\u539f\u6765\u738b\u9675\u5728\u51af\u5e7f\u6f6e\u95e8\u4e0b\u4e60\u6280\uff0c\u4e0e\u909d\u7ec3\u971e\u540c\u4e00\u6751\u5b50\u957f\u5927\uff0c\u5bf9\u5979\u65e9\u6709\u60c5 \u61e6\uff0c\u4e0d\u6599\u5979\u540e\u6765\u5374\u8bb8\u914d\u7ed9\u51af\u82f1\u5947\uff0c\u738b\u9675\u6ee1\u6000\u5fc3\u4e8b\uff0c\u8bf4\u4e0d\u51fa\u53e3\u3002\u4e0d\u4e45\u5b66\u6210\u51fa\u5e08\uff0c\u5230\u4eac\u90fd\u53bb\u5e72\u9556 \u884c\u751f\u610f\uff0c\u4e0e\u4eba\u95f2\u8c08\uff0c\u8bf4\u8d77\u81ea\u5df1\u7684\u8001\u5e08\u58ee\u5e74\u5f52\u9690\u4e4b\u4e8b\uff0c\u4f20\u5230\u56db\u7687\u5b50\u95e8\u4e0b\u6b66\u58eb\u8033\u4e2d\uff0c\u8d77\u4e86\u6000\u7591\uff0c \u9042\u7528\u5a01\u80c1\u5229\u8bf1\uff0c\u628a\u738b\u9675\u8bf1\u5165\u56db\u7687\u5b50\u95e8\u4e0b\u3002\u738b\u9675\u5230\u4eac\u5e08\u4e4b\u540e\uff0c\u89e6\u76ee\u7e41\u534e\uff0c\u6709\u4e86\u529f\u540d\u5229\u7984\u4e4b\u60f3\uff0c \u66f4\u517c\u5bf9\u909d\u7ec3\u971e\u5ff5\u5ff5\u4e0d\u5fd8\uff0c\u7adf\u7136\u5229\u4ee4\u667a\u660f\uff0c\u505a\u4e86\u56db\u7687\u5b50\u7684\u8d70\u72d7\u3002\u8fd9\u6b21\u56db\u7687\u5b50\u95e8\u4e0b\u6b66\u4e0a\u503e\u5de2\u800c \u51fa\uff0c\u8981\u5230\u592a\u884c\u5c71\u6b7c\u706d\u4e94\u7701\u8c6a\u6770\uff0c\u8840\u6ef4\u5b50\u603b\u7ba1\u54c8\u5e03\u9640\u60f3\u8d77\u738b\u9675\u662f\u5c71\u4e1c\u7701\u4eba\uff0c\u5c31\u628a\u4ed6\u5148\u6d3e\u56de\u51af\u5bb6 \u5367\u5e95\uff0c\u987a\u4fbf\u4fa6\u5bdf\u4e94\u7701\u8c6a\u6770\u884c\u8e2a\u3002\u54c8\u5e03\u9640\u539f\u4e5f\u5e76\u672a\u60f3\u5230\u51af\u5e7f\u6f6e\u7adf\u662f\u8ffd\u98ce\u5251\u7684\u4f20\u4eba\uff0c\u4e0d\u8fc7\u987a\u4fbf\u6446 \u4e0b\u4e00\u53ea\u68cb\u5b50\uff0c\u4f5c\u4e3a\u8840\u6ef4\u5b50\u7684\u5916\u56f4\u7fbd\u7ffc\u800c\u5df2\u3002\u4e0d\u6599\u5374\u649e\u4e2a\u6b63\u7740\uff0c\u56db\u7687\u5b50\u6240\u8981\u8ffd\u6355\u7684\u5468\u9752\uff0c\u6b63\u597d \u5c31\u662f\u738b\u9675\u7684\u5e08\u516c\u3002 \u3000\u3000\u8428\u5929\u523a\u542c\u8bf4\uff0c\u8bb0\u4e86\u8d77\u6765\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u5f88\u597d\uff01\u201d\u738b\u9675\u53c8\u8dea\u4e0b\u53bb\u78d5\u5934\u9053\uff1a\u201c\u6c42\u56fd\u5e08 \u7237\u628a\u8fd9\u5987\u4eba\u8d4f\u4e0e\u5c0f\u7684\uff01\u201d\u8428\u5929\u523a\u602a\u773c\u4e00\u7ffb\uff0c\u5fc3\u60f3\uff1a\u201c\u4e0d\u77e5\u8fd9\u4eba\u662f\u4e0d\u662f\u56db\u6bbf\u4e0b\u7684\u4eb2\u4fe1\uff0c\u987a\u624b\u505a \u4e2a\u4eba\u60c5\u4e5f\u597d\uff01\u201d\u63b8\u624b\u8bf4\u9053\uff1a\u201c\u96f7\u6d77\u97f3\u3001\u90dd\u6d69\u660c\uff0c\u90a3\u4f60\u4eec\u5c31\u548c\u738b\u9675\u5e26\u8fd9\u5987\u4eba\u5148\u56de\u4eac\u5e08\uff0c\u514d\u5f97\u5728 \u6b64\u788d\u624b\u788d\u811a\uff01\u201d\u909d\u7ec3\u971e\u5168\u5fc3\u8d2f\u6ce8\u5b69\u5b50\u8eab\u4e0a\uff0c\u72b9\u81ea\u4e0d\u77e5\u3002\u5510\u6653\u6f9c\u867d\u88ab\u8428\u5929\u90fd\u631f\u5f97\u52a8\u5f39\u4e0d\u5f97\uff0c\u5374 \u5927\u58f0\u9a82\u4e86\u8d77\u6765\uff0c\u8428\u5929\u90fd\u4f38\u6307\u4e00\u6233\uff0c\u628a\u4ed6\u70b9\u4e86\u54d1\u7a74\u3002\u909d\u7ec3\u971e\u542c\u5f97\u9a82\u58f0\uff0c\u624d\u77e5\u9053\u738b\u9675\u7adf\u7136\u662f\u5982\u6b64 \u4e00\u4e2a\u4e27\u5fc3\u75c5\u72c2\u7684\u53db\u8d3c\uff0c\u653e\u58f0\u9a82\u9053\uff1a\u201c\u738b\u9675\uff0c\u6211\u516c\u516c\u5f85\u4f60\u6709\u5982\u7236\u5b50\uff0c\u4f60\u5374\u8fd9\u6837\u7b97\u8ba1\u6211\u4eec\u6bcd\u5973\uff0c \u4f60\u662f\u4eba\u8fd8\u662f\u79bd\u517d\uff01\u201d\u738b\u9675\u51d1\u4e86\u4e0a\u6765\uff0c\u909d\u7ec3\u971e\u201c\u5478\u201d\u7684\u4e00\u53e3\uff0c\u628a\u4ed6\u55b7\u5f97\u6ee1\u8138\u553e\u6d8e\uff0c\u738b\u9675\u4e3e\u8896\u63e9 \u9762\uff0c\u4ecd\u662f\u6ee1\u9762\u7b11\u5bb9\uff0c\u51d1\u5230\u5979\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u971e\u59b9\uff0c\u4f60\u7684\u5973\u513f\u8fd8\u5728\u654c\u4eba\u624b\u4e2d\uff0c\u4f60\u53ef\u4e0d\u80fd\u52a8\u5f3a\u3002\u4e8b \u81f3\u5982\u4eca\uff0c\u4f60\u53ea\u6709\u548c\u6211\u5230\u4eac\u5e08\u53bb\uff0c\u7136\u540e\u624d\u80fd\u8bbe\u6cd5\u628a\u4f84\u5973\u63a5\u51fa\u6765\u3002\u8bf7\u4f60\u4ed4\u7ec6\u60f3\u60f3\u3002\u201d\u9093\u7ec3\u971e\u5fc3\u5934 \u4e00\u9707\uff0c\u9a82\u4e0d\u51fa\u53e3\u3002\u8428\u5929\u90fd\u89e3\u5f00\u5979\u7684\u7a74\u9053\uff0c\u96f7\u6d77\u9752\u90dd\u6d69\u660c\u5c06\u5979\u53cc\u624b\u53cd\u7f1a\u8d77\u6765\uff0c\u4ea4\u7ed9\u738b\u9675\u9053\uff1a \u201c\u597d\uff0c\u628a\u4f60\u7684\u5e08\u5ac2\u5e26\u53bb\uff01\u201d\u8428\u5929\u90fd\u9053\uff1a\u201c\u6d69\u660c\uff0c\u4f60\u82e5\u89c1\u5230\u592a\u6e05\uff0c\u53eb\u4ed6\u4e5f\u5148\u56de\u4eac\u5e08\u3002\u201d\u8463\u592a\u6e05 \u662f\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u7684\u5f92\u68a8\uff0c\u90dd\u6d69\u660c\u5219\u662f\u5927\u529b\u795e\u9b54\u8428\u5929\u90fd\u7684\u5f92\u5f1f\uff0c\u4f46\u56e0\u5165\u95e8\u8f83\u8fdf\uff0c\u8d44\u8d28\u8f83\u949d\uff0c \u6b66\u529f\u9020\u8be3\u8981\u6bd4\u8463\u592a\u6e05\u5dee\u8bb8\u591a\u3002 \u3000\u3000\u5510\u6653\u6f9c\u76ee\u7779\u909d\u7ec3\u971e\u88ab\u738b\u9675\u7b49\u9563\u62e5\u800c\u53bb\uff0c\u6c14\u6781\u6068\u6781\uff0c\u5374\u662f\u51fa\u4e0d\u4e86\u4e00\u58f0\uff0c\u53ea\u628a\u7259\u9f7f\u54ac\u5f97\u683c\u683c \u4f5c\u54cd\u3002\u51af\u745b\u54ed\u4e86\u8bb8\u4e45\uff0c\u75b2\u5026\u5df2\u6781\uff0c\u7adf\u5728\u8428\u5929\u523a\u80cc\u4e0a\u719f\u7761\u8d77\u6765\uff0c\u4e0d\u77e5\u5988\u5988\u5df2\u7ed9\u654c\u4eba\u6349\u53bb\u3002\u8428\u5929 \u90fd\u628a\u51af\u745b\u770b\u4e86\u4e00\u9635\uff0c\u4e5f\u662f\u6ee1\u5fc3\u559c\u7231\uff0c\u7a81\u7136\u628a\u5510\u6653\u6f9c\u6254\u4e0b\uff0c\u53cc\u624b\u629a\u6478\u51af\u745b\u7684\u5934\u9762\u3002 \u3000\u3000\u8428\u5929\u523a\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u6478\u4e86\uff0c\u8fd9\u5b69\u5b50\u662f\u5929\u751f\u4e60\u6b66\u7684\u80da\u5b50\u3002\u201d\u8428\u5929\u90fd\u653e\u4e0b\u51af\u745b\uff0c\u5c06\u5510\u6653\u6f9c\u631f \u8d77\uff0c\u5510\u6653\u6f9c\u53cc\u76ee\u5706\u7741\uff0c\u76f4\u77aa\u7740\u4ed6\uff0c\u8428\u5929\u90fd\u7b11\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u5012\u4e0d\u754f\u6b7b\uff01\u201d\u5c06\u62a2\u5f97\u7684\u6e38\u9f99\u5251\u5f39\u4e86 \u4e24\u5f39\uff0c\u84e6\u5730\u4e00\u58f0\u602a\u5578\uff0c\u6325\u821e\u8d77\u6765\uff0c\u5251\u950b\u6240\u5230\uff0c\u89e6\u7740\u7684\u5ca9\u77f3\uff0c\u77f3\u5c51\u5e94\u624b\u98de\u8d77\uff01\u8428\u5929\u90fd\u957f\u5578\u53eb \u9053\uff1a\u201c\u6e38\u9f99\u5251\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01\u201d\u56de\u9996\u89e3\u4e86\u5510\u6653\u6f9c\u54d1\u7a74\uff0c\u5389\u58f0\u95ee\u9053\uff1a\u201c\u51cc\u672a\u98ce\u662f\u4f60\u4f55\u4eba\uff1f\u201d\u5510 \u6653\u6f9c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u6211\u592a\u5e08\u79df\u5389\u5bb3\uff01\u201d\u8428\u5929\u90fd\u53c8\u628a\u6e38\u9f99\u5251\u5f39\u4e86\u4e24\u5f39\uff0c\u72de\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f \u4f19\u5b50\u5012\u5014\u5f3a\u5f97\u7d27\uff01\u201d\u53cc\u6307\u5411\u4ed6\u80a9\u5934\u4e00\u642d\uff0c\u4fbf\u5f85\u5c06\u4ed6\u7684\u7435\u7436\u9aa8\u634f\u788e\uff0c\u4ee4\u4ed6\u6162\u6162\u53d7\u82e6\uff0c\u518d\u53d6\u4e0b\u4ed6 \u7684\u5934\u9885\u3002\u8428\u5929\u523a\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u7ad9\u5230\u9762\u524d\uff0c\u4ed4\u7ec6\u770b\u4e86\u5510\u6653\u6f9c\u4e00\u9635\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u6b63\u5c11 \u8fd9\u6837\u4e00\u4e2a\u5f92\u5f1f\uff01\u201d\u53cc\u9b54\u6a2a\u884c\u534a\u4e16\uff0c\u5140\u662f\u672a\u627e\u5230\u4e00\u4e2a\u79f0\u5fc3\u5408\u610f\u3001\u8d28\u7f8e\u597d\u5b66\u7684\u5f92\u5f1f\uff0c\u5c24\u5176\u662f\u8428\u5929 \u90fd\uff0c\u4ed6\u6536\u7684\u90dd\u6d69\u660c\uff0c\u7adf\u7136\u6321\u4e0d\u4e86\u80d6\u548c\u5c1a\u4e00\u62db\uff0c\u4e00\u89c1\u9762\u5c31\u7ed9\u4eba\u55b7\u778e\u4e86\u5de6\u773c\u3002\u542c\u54e5\u54e5\u4e00\u8bf4\uff0c\u84e6\u7136 \u5fc3\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u82e5\u80fd\u628a\u51cc\u672a\u98ce\u7684\u7b2c\u56db\u4ee3\u95e8\u4eba\u6536\u4e3a\u5f92\u5f1f\uff0c\u4e0d\u4f46\u53ef\u4ee5\u7ee7\u627f\u81ea\u5df1\u7684\u7edd\u6280\uff0c\u5728\u6c5f\u6e56\u4e0a \u4e5f\u662f\u4e2a\u5927\u5927\u626c\u540d\u9732\u9762\u4e4b\u4e8b\u3002\u5f53\u4e0b\u9762\u8272\u7f13\u548c\uff0c\u5c06\u6e38\u9f99\u5251\u63d2\u56de\u9798\u4e2d\uff0c\u4ecd\u60ac\u5728\u5510\u6653\u6f9c\u8170\u4e0a\uff0c\u6162\u58f0\u8bf4 \u9053\uff1a\u201c\u4f60\u90a3\u592a\u5e08\u7956\u65e9\u5df2\u6b7b\u5728\u5929\u5c71\uff0c\u5c31\u662f\u4e0d\u6b7b\uff0c\u4ed6\u4e5f\u4e0d\u662f\u6211\u4eec\u5144\u5f1f\u7684\u5bf9\u624b\u3002\u4f60\u4e0d\u5982\u6539\u6295\u6211\u4eec\u95e8 \u4e0b\uff0c\u6211\u4eec\u5144\u5f1f\u4fe9\u5305\u4f60\u5b66\u6210\u7edd\u4e16\u6b66\u529f\u3002\u201d\u5510\u6653\u6f9c\u6012\u9053\uff1a\u201c\u5b81\u6b7b\u4e0d\u505a\u4f60\u4eec\u5f92\u5f1f\uff01\u201d\u8428\u5929\u90fd\u9762\u8272\u4e00 \u6c89\uff0c\u6b63\u5f85\u53d1\u4f5c\uff0c\u5ffd\u542c\u5f97\u8fdc\u5904\u5578\u58f0\u6447\u66f3\u957f\u7a7a\uff0c\u8428\u5929\u523a\u9053\uff1a\u201c\u5173\u4e1c\u56db\u4fa0\u6765\u4e86\uff01\u201d\u8428\u5929\u90fd\u9053\uff1a \u201c\u597d\uff0c\u6536\u5f92\u4e4b\u4e8b\uff0c\u4ee5\u540e\u518d\u8bf4\uff01\u201d\u91cd\u628a\u5510\u6653\u6f9c\u70b9\u4e86\u9ebb\u7a74\uff0c\u653e\u5728\u4e24\u5757\u5ca9\u77f3\u4e0a\u4e0b\u5408\u76d6\u7684\u4e2d\u7a7a\u4e4b\u5904\uff0c \u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u597d\u597d\u8eba\u7740\uff01\u770b\u770b\u6211\u4eec\u7684\u672c\u9886\uff01\u201d \u3000\u3000\u5173\u4e1c\u56db\u4fa0\uff0c\u6311\u4e86\u8840\u6ef4\u5b50\u5728\u592a\u884c\u5c71\u7684\u8425\u5be8\u4e4b\u540e\uff0c\u8d70\u4e86\u4e0b\u6765\uff0c\u542c\u5f97\u53cc\u9b54\u60ca\u5fc3\u523a\u8033\u7684\u602a\u5578\uff0c\u4e00 \u8def\u5bfb\u6765\uff0c\u56de\u5230\u539f\u5904\u3002\u4e0a\u5230\u5c71\u5934\uff0c\u84e6\u89c1\u4e24\u4e2a\u9ebb\u8863\u8001\u8005\uff0c\u7ad9\u5728\u4e2d\u95f4\u7684\u4e24\u5f20\u684c\u5b50\u4e4b\u65c1\uff0c\u5468\u56f4\u56db\u9762\uff0c \u6446\u7740\u56db\u5f20\u77f3\u684c\uff0c\u684c\u4e0a\u6446\u6ee1\u4eba\u5934\uff0c\u89e6\u76ee\u60ca\u5fc3\u3002\u5173\u4e1c\u56db\u4fa0\u4e4b\u9996\uff0c\u201c\u94c1\u62d0\u62ab\u98ce\u5251\u201d\u7384\u98ce\u9053\u957f\u559d\u9053\uff1a \u201c\u5140\u4f60\u8fd9\u4e8c\u4eba\u5c31\u662f\u4ec0\u4e48\u795e\u9b54\u53cc\u8001\u4e48\uff1f\u4f60\u4eec\u5f04\u4ec0\u4e48\u7384\u865a\uff1f\u201d\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u7f13\u7f13\u8d77\u7acb\uff0c\u9634\u607b\u607b \u7684\u7b11\u9053\uff1a\u201c\u56db\u4fa0\u8fdc\u6765\uff0c\u6709\u5173\u8fce\u63a5\uff0c\u4ffa\u4eec\u4e24\u5144\u5f1f\u6446\u4e0b\u8584\u9152\uff0c\u5148\u66ff\u56db\u4fa0\u63a5\u98ce\uff01\u201d\u8428\u5929\u90fd\u7ee7\u7eed\u8bf4 \u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u8bf7\u4e86\u4e00\u6279\u597d\u670b\u53cb\u4eec\u7ed9\u56db\u4fa0\u4f5c\u966a\u5ba2\uff01\u201d\u4f38\u624b\u4e00\u6307\uff0c\u56db\u4fa0\u4e2d\u7684\u9648\u5143\u9738\u5148\u53eb\u4e86\u8d77\u6765\uff0c \u684c\u4e0a\u7684\u4eba\u5934\u867d\u7136\u5012\u653e\uff0c\u795e\u60c5\u9762\u76ee\u4ecd\u5982\u751f\u524d\uff0c\u77a7\u5f97\u6e05\u695a\uff0c\u4e00\u773c\u77a5\u53bb\uff0c\u5176\u4e2d\u7adf\u6709\u8bb8\u591a\u662f\u81ea\u5df1\u7684\u597d \u53cb\u3002\u8428\u5929\u523a\u8eac\u8170\u8bf4\u9053\uff1a\u201c\u7384\u98ce\u9053\u957f\u8bf7\u5750\u4e0a\u5e2d\uff01\u8fd9\u5e2d\u6709\u4e94\u864e\u5200\u9a6c\u6606\u7b49\u8d35\u5bbe\u4f5c\u966a\uff0c\u6717\u6708\u5927\u5e08\u8bf7\u5750 \u6b21\u5e2d\uff0c\u8fd9\u5e2d\u6709\u91d1\u67aa\u5f90\u5e94\u9f99\u7b49\u8d35\u5bbe\u4f5c\u966a\uff0c\u67f3\u4e09\u54e5\u8bf7\u5750\u897f\u9996\u8fd9\u5e2d\uff0c\u8fd9\u5e2d\u6709\u864e\u68cd\u6768\u5343\u5f6a\u7b49\u8d35\u5bbe\u4f5c \u966a\uff0c\u9648\u56db\u54e5\u8bf7\u5750\u4e1c\u9996\u8fd9\u5e2d\uff0c\u8fd9\u5e2d\u6709\u65e5\u6708\u8f6e\u534e\u56db\u628a\u7b49\u8d35\u5bbe\u4f5c\u966a\uff0c\u8bf7\u5440\uff01\u8bf7\u5440\uff01\u8bf7\u5750\u4e0b\u6765\u5440\uff01\u201d \u3000\u3000\u9a6c\u6606\u3001\u5f90\u5e94\u9f99\u3001\u6768\u5343\u5f6a\u3001\u534e\u56db\u628a\u7b49\u90fd\u662f\u5317\u4e94\u7701\u6210\u540d\u7684\u8c6a\u6770\uff0c\u5404\u4ee5\u65ad\u95e8\u5200\u3001\u864e\u5c3e\u68cd\u3001\u5c0f\u91d1 \u67aa\u3001\u65e5\u6708\u8f6e\u9a70\u8a89\u4e00\u65f6\uff0c\u8fd9\u56db\u4eba\u4e5f\u90fd\u662f\u5173\u4e1c\u56db\u4fa0\u7684\u591a\u5e74\u597d\u53cb\uff0c\u4e0d\u6599\u800c\u4eca\u7adf\u906d\u4e86\u795e\u9b54\u53cc\u8001\u7684\u6bd2 \u624b\uff0c\u5272\u6765\u4eba\u5934\uff0c\u70bc\u6210\u9152\u5177\uff0c\u8fd8\u7528\u6765\u6b3e\u5f85\u4ed6\u4eec\u3002\u9648\u5143\u9738\u9996\u5148\u5fcd\u4e0d\u4f4f\uff0c\u6c14\u5f80\u4e0a\u51b2\uff0c\u53cc\u76ee\u5706\u7741\uff0c\u4fbf \u5f85\u53d1\u4f5c\uff0c\u7384\u98ce\u9053\u957f\u94c1\u62d0\u4e00\u6446\uff0c\u793a\u610f\u53eb\u4ed6\u6682\u5fcd\uff0c\u5750\u4e0a\u9996\u5e2d\uff0c\u628a\u201c\u4e94\u864e\u65ad\u95e8\u5200\u201d\u9a6c\u6606\u7684\u9996\u7ea7\u653e\u4eba \u8349\u56ca\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u9a6c\u5927\u54e5\u4f5c\u966a\u3002\u201d\u6717\u6708\u7985\u5e08\u8ddf\u7740\u4e5f\u5750\u4e0a\u6b21\u5e2d\uff0c\u628a\u5f90\u5e94\u9f99\u7684\u9996\u7ea7\u6536 \u4e86\u3002\u67f3\u5148\u5f00\u548c\u9648\u5143\u9738\u767b\u65f6\u9192\u609f\uff0c\u77e5\u9053\u5927\u54e5\u7528\u610f\uff1a\u65e2\u7136\u4e00\u573a\u6fc0\u6597\uff0c\u52bf\u6240\u96be\u514d\uff0c\u90a3\u4e48\u5148\u6536\u4e0b\u6545\u4eba \u9996\u7ea7\uff0c\u514d\u53d7\u6bc1\u4f24\uff0c\u4e5f\u662f\u6b63\u7406\u3002\u4e8e\u662f\u4e00\u843d\u5750\uff0c\u5c06\u6768\u5343\u5f6a\u548c\u534e\u56db\u628a\u7684\u5934\u9885\u6536\u5165\u8349\u56ca\u3002 \u3000\u3000\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5173\u4e1c\u56db\u4fa0\u679c\u662f\u5feb\u4eba\uff0c\u8bf7\u5148\u628a\u4e09\u676f\u5e72\u4e86\uff0c\u518d\u8c08\u6b63\u4e8b\uff01\u201d\u8bf4\u65f6 \u4e0e\u8428\u5929\u90fd\u5404\u628a\u4e09\u4e2a\u4eba\u5934\u4e2d\u6240\u76db\u7684\u7f8e\u9152\u5012\u4e0b\u53e3\u4e2d\uff0c\u996e\u540e\u628a\u4eba\u5934\u6254\u4e0b\u5c71\u8c37\uff0c\u54c8\u54c8\u5927\u7b11\u3002\u5173\u4e1c\u56db\u4fa0 \u7aef\u5750\u4e0d\u52a8\uff0c\u5927\u529b\u795e\u9b54\u53eb\u9053\uff1a\u201c\u5173\u4e1c\u56db\u4fa0\uff0c\u8bf7\u559d\u9152\u5440\uff01\u201d\u7384\u98ce\u9053\u957f\u5ffd\u7136\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6709\u9152\u65e0 \u80b4\uff0c\u5c82\u975e\u7f8e\u4e2d\u4e0d\u8db3\uff1f\u5f85\u8d2b\u9053\u501f\u82b1\u656c\u4f5b\uff0c\u5c06\u53d6\u81ea\u4f60\u4eec\u7684\u4f73\u80b4\u656c\u56de\u4e24\u4f4d\u5427\u3002\u201d\u53cc\u9b54\u4e00\u6115\u4e0d\u77e5\u4ed6\u5f04 \u4ec0\u4e48\u628a\u620f\u3002\u7741\u773c\u770b\u65f6\uff0c\u7384\u98ce\u9053\u957f\u5927\u8896\u4e00\u6296\uff0c\u4e00\u5bf9\u5bf9\u9c9c\u8840\u6dcb\u6f13\u7684\u4eba\u8033\u7eb7\u7eb7\u843d\u4e0b\uff0c\u8fd9\u4e9b\u8033\u6735\uff0c\u90fd \u662f\u9002\u624d\u6240\u5272\u3002\u603b\u6709\u51e0\u5341\u5bf9\u4e4b\u591a\uff0c\u5176\u4e2d\u81ea\u7136\u4e5f\u6709\u9f99\u6728\u516c\u4e0e\u96f7\u6d77\u97f3\u7684\u8033\u6735\u5728\u5185\u3002\u7384\u98ce\u9053\u957f\u5230\u592a\u884c \u5c71\u4e0d\u8fc7\u534a\u5929\uff0c\u5c31\u5272\u4e0b\u8fd9\u4e48\u591a\u6b66\u58eb\u7684\u8033\u6735\uff0c\u5251\u6cd5\u4e4b\u72e0\u51c6\u5feb\u6377\uff0c\u53cc\u9b54\u4e5f\u81ea\u6697\u6697\u60ca\u5fc3\u3002\u5927\u529b\u795e\u9b54\u72de \u7b11\u8bf4\u9053\uff1a\u201c\u54c8\uff0c\u4e00\u4e2a\u4eba\u5934\u914d\u4e00\u53cc\u8033\u6735\uff0c\u8fd8\u662f\u9152\u591a\u83dc\u5c11\uff01\u201d\u7384\u98ce\u9053\u957f\u51b7\u7b11\u9053\uff1a\u201c\u5982\u4e24\u4f4d\u8fd8\u5acc\u4e0d \u591f\uff0c\u7b49\u4e0b\u8d2b\u9053\u518d\u6dfb\u3002\u201d \u3000\u3000\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u602a\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u5fc5\u6597\u53e3\uff0c\u56db\u4f4d\u8fdc\u6765\uff0c\u5982\u4e0d\u5acc\u9152\u8584\uff0c\u8bf7\u5148\u6da6\u6da6\u67af\u80a0\u3002\u201d \u968f\u624b\u53c8\u628a\u4e00\u4e2a\u4eba\u5934\u4e2d\u6240\u76db\u7684\u7f8e\u9152\u5012\u4e0b\u5589\u5499\uff0c\u5927\u7b11\u53eb\u9053\uff1a\u201c\u4eba\u5934\u4f5c\u9152\u676f\uff0c\u559d\u5c3d\u4ec7\u4eba\u8840\uff01\u201d\u9648\u5143 \u9738\u5927\u6012\u8d77\u7acb\uff0c\u5927\u529b\u795e\u9b54\u8428\u5929\u90fd\u7a81\u7136\u4e00\u8dc3\u800c\u524d\uff0c\u4e00\u6293\u5c06\u77f3\u684c\u6293\u8d77\uff0c\u5411\u9648\u5143\u9738\u4e00\u9001\uff0c\u6076\u7b11\u8bf4\u9053\uff1a \u201c\u9648\u56db\u54e5\u60f3\u907f\u5e2d\u4e48\uff1f\u4e0d\u884c\uff0c\u4e0d\u884c\uff0c\u4e00\u5b9a\u8981\u559d\u51e0\u676f\uff01\u201d\u9648\u5143\u9738\u53cc\u638c\u5411\u77f3\u684c\u4e00\u62b5\uff0c\u63a8\u5c06\u8fc7\u53bb\uff0c\u8428 \u5929\u90fd\u731b\u559d\u4e00\u58f0\uff1a\u201c\u559d\u9152\uff01\u201d\u9648\u5143\u9738\u5ffd\u89c9\u52b2\u98ce\u8d2f\u8033\uff0c\u77f3\u684c\u5df2\u5411\u81ea\u5df1\u8fd9\u8fb9\u63a8\u6765\uff0c\u6025\u5fd9\u51dd\u795e\u594b\u529b\uff0c \u632f\u8d77\u795e\u5a01\uff0c\u53cc\u638c\u62b5\u4f4f\u77f3\u684c\u5f80\u5916\u4e00\u7529\uff0c\u4e24\u4eba\u5916\u5bb6\u529f\u592b\u90fd\u662f\u767b\u5cf0\u9020\u6781\uff0c\u529b\u5927\u65e0\u7a77\uff0c\u8fd9\u4e00\u53cc\u53cc\u7528 \u529b\uff0c\u731b\u542c\u5f97\u8f70\u7136\u5de8\u54cd\uff0c\u77f3\u684c\u788e\u88c2\u6210\u65e0\u6570\u5c0f\u5757\uff0c\u6ee1\u7a7a\u98de\u821e\uff0c\u9648\u5143\u9738\u7ed9\u9707\u9000\u6570\u6b65\uff0c\u53cc\u81c2\u9178\u9ebb\uff0c\u8428 \u5929\u90fd\u5728\u77f3\u5f39\u5982\u96e8\u4e2d\u5140\u7acb\u4e0d\u52a8\uff0c\u54c8\u54c8\u5927\u7b11\u3002\u8fd9\u4e00\u8f83\u52b2\uff0c\u8868\u9762\u770b\u6765\u662f\u4e24\u65e0\u4f24\u635f\uff0c\u5176\u5b9e\u662f\u9648\u5143\u9738\u5df2 \u8f93\u4e86\u5185\u529b\uff0c\u201c\u5355\u638c\u5f00\u7891\u201d\u7684\u5a01\u98ce\uff0c\u7ade\u6298\u5728\u5927\u529b\u795e\u9b54\u4e4b\u624b\u3002 \u3000\u3000\u7384\u98ce\u9053\u957f\u4e0e\u6717\u6708\u7985\u5e08\u4ecd\u7136\u5140\u5750\u4e0d\u52a8\uff0c\u201c\u4e07\u91cc\u8ffd\u98ce\u201d\u67f3\u5148\u5f00\u5df2\u6c89\u4e0d\u4f4f\u6c14\u7ad9\u4e86\u8d77\u6765\u3002\u516b\u81c2\u795e \u9b54\u84e6\u7136\u53c8\u662f\u4e00\u58f0\u602a\u5578\uff0c\u624b\u633d\u4e24\u4e2a\u4eba\u5934\uff0c\u5411\u67f3\u5148\u5f00\u98de\u7eb5\u8fc7\u6765\uff0c\u53e3\u4e2d\u559d\u9053\uff1a\u201c\u8bf7\u67f3\u4e09\u54e5\u559d\u9152\uff01\u201d \u67f3\u5148\u5f00\u5355\u638c\u4e00\u6309\u684c\u9762\uff0c\u4eba\u4f3c\u7ed9\u5f39\u7c27\u5f39\u7740\u4e00\u6837\uff0c\u98de\u4e86\u8d77\u6765\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7b4b\u6597\u843d\u5230\u573a\u5fc3\uff0c\u4e24\u4eba \u64e6\u81c2\u800c\u8fc7\uff0c\u67f3\u5148\u5f00\u624b\u4e2d\u4e5f\u633d\u7740\u4e24\u4e2a\u4eba\u5934\uff0c\u53e3\u4e2d\u559d\u9053\uff1a\u201c\u5148\u656c\u4e3b\u4eba\u7684\u9152\uff01\u201d\u4e24\u4e2a\u4eba\u5934\u98de\u63b7\u8fc7 \u53bb\uff0c\u8428\u5929\u523a\u7684\u4e24\u4e2a\u4eba\u5934\u4e5f\u98de\u63b7\u8fc7\u6765\uff0c\u4e24\u5bf9\u4eba\u5934\uff0c\u4e92\u76f8\u4ea4\u6362\uff0c\u58f0\u5230\u5934\u5230\uff0c\u5f7c\u6b64\u63a5\u5728\u624b\u4e2d\uff0c\u6ef4\u9152 \u4e0d\u6f0f\uff0c\u5404\u81ea\u6a2a\u8dc3\u4e09\u6b65\uff0c\u51dd\u795e\u6ce8\u89c6\u3002 \u3000\u3000\u516b\u81c2\u795e\u9b54\u7684\u8f7b\u529f\u539f\u5df2\u767b\u5cf0\u9020\u6781\uff0c\u4f46\u67f3\u5148\u5f00\u53f7\u79f0\u201c\u4e07\u91cc\u8ffd\u98ce\u201d\uff0c\u8f7b\u529f\u72b9\u81ea\u80dc\u4ed6\u4e00\u7b79\uff0c\u8fd9\u4e00 \u6697\u4e2d\u79fb\u52a8\uff0c\u8428\u5929\u523a\u8d77\u6b65\u5728\u5148\uff0c\u67f3\u5148\u5f00\u98de\u8eab\u5728\u540e\uff0c\u4e24\u4eba\u540c\u5230\u573a\u5fc3\uff0c\u64e6\u81c2\u800c\u8fc7\uff0c\u5206\u660e\u662f\u67f3\u5148\u5f00\u80dc \u4e86\u3002\u8428\u5929\u523a\u5185\u5916\u517c\u4fee\uff0c\u6b66\u529f\u7edd\u9876\uff0c\u5374\u504f\u504f\u5728\u8f7b\u529f\u8f83\u91cf\u4e0a\u8f93\u4e86\uff0c\u9762\u7ea2\u8033\u8d64\uff0c\u624b\u633d\u4e24\u4e2a\u4eba\u5934\uff0c\u5411 \u201c\u7b11\u5f25\u52d2\u201d\u6717\u6708\u7985\u5e08\u8d70\u6765\uff0c\u53c8\u53eb\u9053\uff1a\u201c\u656c\u6717\u6708\u5927\u5e08\u8584\u9152\uff0c\u201d\u80d6\u548c\u5c1a\u54c8\u54c8\u5927\u7b11\uff0c\u63a5\u8fc7\u98de\u63b7\u6765\u7684 \u4eba\u5934\uff0c\u5f20\u53e3\u4e00\u5438\uff0c\u628a\u9152\u5168\u5438\u5165\u53e3\u4e2d\uff0c\u84e6\u7136\u4e00\u55b7\uff0c\u201c\u9152\u6d6a\u201d\u8fce\u9762\u98de\u6765\uff0c\u516b\u81c2\u795e\u9b54\u65e9\u77e5\u6717\u6708\u7985\u5e08 \u6709\u6b64\u7edd\u6280\uff0c\u65e9\u6709\u9632\u5907\uff0c\u4eba\u5934\u63b7\u51fa\uff0c\u4eba\u4e5f\u98de\u8eab\u63a0\u8d77\uff0c\u201c\u9152\u6d6a\u201d\u5728\u811a\u5e95\u5c04\u8fc7\uff0c\u4e1d\u6beb\u4e0d\u6e7f\u3002\u5927\u529b\u795e \u9b54\u8428\u5929\u90fd\u98de\u6b65\u8d76\u5230\uff0c\u6717\u6708\u7684\u5589\u5499\u5495\u549a\u4f5c\u54cd\uff0c\u683c\u683c\u7b11\u9053\uff1a\u201c\u8bf7\u4f60\u4e5f\u559d\u4e00\u676f\u3002\u201d\u5927\u53e3\u518d\u5f20\uff0c\u9152\u82b1 \u56db\u55b7\uff0c\u8428\u5929\u90fd\u53ea\u9053\u4ed6\u9152\u5df2\u55b7\u5b8c\uff0c\u4e0d\u52a0\u9632\u5907\uff0c\u84e6\u7136\u773c\u524d\u767d\u8499\u8499\u4e00\u7247\uff0c\u6025\u5fd9\u53cc\u638c\u62a4\u9762\uff0c\u9152\u82b1\u96e8\u70b9 \u822c\u843d\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u9ebb\u8863\u88ab\u5c04\u7a7f\u6210\u4e00\u4e2a\u4e2a\u5c0f\u6d1e\uff0c\u6709\u5982\u8702\u5de2\uff0c\u82e5\u662f\u5e73\u5e38\u6b66\u58eb\uff0c\u4e2d\u4e86\u8fd9\u4e9b\u9152\u73e0\uff0c\u5b9a \u5982\u53d7\u94c5\u5f39\u88ad\u51fb\uff0c\u7981\u53d7\u4e0d\u4f4f\u3002\u8428\u5929\u90fd\u94dc\u76ae\u94c1\u9aa8\uff0c\u88ab\u9152\u55b7\u4e86\u6ee1\u8eab\uff0c\u5374\u4e0d\u8fc7\u5982\u540c\u7ed9\u8681\u556e\u4e00\u6837\u3002\u5f53\u4e0b \u4e00\u58f0\u5927\u559d\uff0c\u5411\u80d6\u548c\u5c1a\u51b2\u6765\u3002 \u3000\u3000\u53e6\u4e00\u8fb9\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u907f\u5f00\u9152\u96e8\uff0c\u98de\u8eab\u4ece\u6717\u6708\u7985\u5e08\u5934\u9876\u63a0\u8fc7\uff0c\u843d\u5728\u7384\u98ce\u9053\u957f\u4e4b\u524d\uff0c\u521a\u8bf4 \u5f97\u4e00\u58f0\uff1a\u201c\u656c\u7384\u98ce\u9053\u957f\u8584\u9152\u3002\u201d\u7384\u98ce\u9053\u957f\u624b\u8d77\u4e00\u6756\uff0c\u628a\u77f3\u684c\u6253\u7ffb\uff0c\u5e78\u7136\u559d\u9053\uff1a\u201c\u90a3\u6709\u5982\u6b64\u656c \u9152\u4e4b\u7406\uff01\u201d\u53f3\u624b\u957f\u5251\u4e00\u6296\uff0c\u5251\u5149\u95ea\u70c1\uff0c\u76f4\u88f9\u8fc7\u6765\u3002\u8428\u5929\u523a\u4e00\u58f0\u602a\u5578\uff0c\u8eab\u5f62\u6643\u52a8\uff0c\u968f\u7740\u7384\u98ce\u9053 \u957f\u7684\u5251\u62db\u4e1c\u98d8\u897f\u8361\uff0c\u77ac\u606f\u4e4b\u95f4\u5df2\u95ea\u8fc7\u4e86\u4e03\u516b\u62db\u8fa3\u62db\uff0c\u8fd9\u65f6\u4ed6\u80cc\u4e0a\u8d1f\u7740\u7684\u51af\u745b\uff0c\u5df2\u7ecf\u60ca\u9192\uff0c\u5ffd \u7136\u53c8\u201c\u5440\u5440\u201d\u7684\u54ed\u4e86\u8d77\u6765\u3002\u7384\u98ce\u9053\u957f\u7684\u201c\u4e71\u62ab\u98ce\u5251\u6cd5\u201d\u51cc\u5389\u975e\u5e38\uff0c\u8fde\u8fdb\u51e0\u62db\uff0c\u8fde\u516b\u81c2\u795e\u9b54\u7684 \u8863\u88f3\u90fd\u672a\u523a\u7740\u3002\u8fd9\u65f6\u53c8\u89c1\u4ed6\u80cc\u4e0a\u7684\u5973\u5a74\u201c\u5440\u5440\u201d\u5927\u54ed\uff0c\u4e0d\u89c9\u7f13\u4e86\u4e00\u7f13\u3002\u8428\u5929\u523a\u7a81\u7136\u51ed\u7a7a\u63a0 \u8d77\uff0c\u5341\u6307\u9f50\u4f38\uff0c\u5411\u7384\u98ce\u9053\u957f\u5f53\u5934\u6293\u4e0b\uff01\u8fd9\u4e00\u62db\u8fc5\u731b\u5f02\u5e38\uff0c\u7384\u98ce\u9053\u957f\u6025\u95ea\u8eab\u65f6\uff0c\u4ed6\u80cc\u7740\u5c0f\u5b69\uff0c \u8eab\u5f62\u5c45\u7136\u8fd8\u80fd\u591f\u5728\u534a\u7a7a\u4e00\u8f6c\uff0c\u6709\u5982\u98de\u9e1f\u56de\u7fd4\uff0c\u7d27\u8ffd\u6293\u5230\u3002\u7384\u98ce\u7684\u5251\u5c1a\u672a\u64a4\u56de\uff0c\u5de6\u624b\u94c1\u62d0\u6a2a\u62d0 \u4e00\u6321\uff0c\u7adf\u7136\u7ed9\u4ed6\u6293\u7740\uff0c\u7384\u98ce\u9053\u957f\u957f\u5251\u6025\u5fd9\u53cd\u5251\u4e00\u5708\uff0c\u8eab\u5f62\u5df2\u7ed9\u4ed6\u626f\u5f97\u79fb\u52a8\u4e24\u6b65\uff0c\u5de6\u624b\u7adf\u81ea\u6293 \u5230\u9762\u95e8\uff0c\u7384\u98ce\u9053\u957f\u9661\u7136\u5411\u540e\u4e00\u7f29\uff0c\u5934\u5411\u540e\u4ef0\uff0c\u8fd9\u970e\u90a3\u95f4\uff0c\u659c\u523a\u91cc\u4e00\u6761\u9ed1\u5f71\uff0c\u75be\u98de\u800c\u6765\u3002\u8428\u5929 \u523a\u602a\u5578\u4e00\u58f0\uff0c\u53cc\u624b\u653e\u677e\uff0c\u7384\u98ce\u9053\u957f\u6643\u4e86\u4e24\u6643\uff0c\u8428\u5929\u523a\u5df2\u75be\u63a0\u51fa\u53bb\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u53cc\u9b54\u9022\u56db\u4fa0\uff0c\u5404\u81ea\u663e\u795e\u901a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u56db\u56de\u3000\u98ce\u6025\u5929\u9ad8\u3000\u8352\u5c71\u817e\u5251\u6c14\u3000\u6708\u660e\u6797\u4e0b\u3000\u4e00\u5973\u656c\u51f6\u987d \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u56db\u56de \u98ce\u6025\u5929\u9ad8\u3000\u8352\u5c71\u817e\u5251\u6c14 \u6708\u660e\u6797\u4e0b\u3000\u4e00\u5973\u656c\u51f6\u987d \u3000\u3000\u8fd9\u51b2\u6765\u7684\u4eba\u662f\u201c\u4e07\u91cc\u8ffd\u98ce\u201d\u67f3\u5148\u5f00\uff0c\u4ed6\u89c1\u5927\u54e5\u5982\u6b64\u51cc\u5389\u7684\u5251\u6cd5\uff0c\u7adf\u7136\u53d7\u56f0\uff0c\u8fd9\u662f\u4ece\u6240\u672a \u6709\u7684\u4e8b\uff0c\u6697\u81ea\u5fc3\u5bd2\uff0c\u4ed7\u7740\u8f7b\u529f\u5353\u7edd\uff0c\u98de\u8eab\u6765\u6551\u3002\u8428\u5929\u523a\u521a\u624d\u8f7b\u529f\u8f93\u4e86\u4e00\u62db\uff0c\u8ba4\u4e3a\u5947\u803b\uff0c\u4ec7\u4eba \u89c1\u9762\uff0c\u5206\u5916\u773c\u7ea2\uff0c\u7adf\u81ea\u653e\u8fc7\u7384\u98ce\u9053\u957f\uff0c\u8ff3\u53d6\u201c\u4e07\u91cc\u8ffd\u98ce\u201d\u3002\u67f3\u5148\u5f00\u53cc\u638c\u53d8\u62f3\uff0c\u5341\u6307\u94a2\u73af\uff0c\u5411 \u8428\u5929\u523a\u8fce\u5934\u51ff\u4e0b\uff0c\u8428\u5929\u523a\u53cc\u81c2\u4e00\u4f38\uff0c\u5341\u6307\u6307\u5934\uff0c\u84e6\u7136\u4f38\u51fa\u5c3a\u8bb8\u957f\u7684\u6307\u7532\uff0c\u548c\u94a2\u73af\u4e00\u78b0\uff0c\u94ff\u94ff \u6709\u58f0\uff0c\u67f3\u5148\u5f00\u5927\u5403\u4e00\u60ca\uff0c\u654c\u4eba\u9a88\u6307\u5982\u621f\uff0c\u76f4\u70b9\u9762\u4e0a\u53cc\u775b\uff0c\u67f3\u5148\u5f00\u7ffb\u8eab\u95ea\u8eb2\uff0c\u7384\u98ce\u53f3\u624b\u957f\u5251\uff0c \u5de6\u624b\u94c1\u62d0\uff0c\u6025\u4ece\u80cc\u540e\u63a9\u6765\uff0c\u8428\u5929\u523a\u8df3\u52a8\u5982\u98de\uff0c\u5341\u6307\u6495\u3001\u6293\u3001\u70b9\u3001\u52fe\uff0c\u771f\u5982\u9e70\u722a\u4e00\u822c\uff01 \u3000\u3000\u53cc\u9b54\u5728\u201c\u732b\u9e70\u5c9b\u201d\u4f4f\u4e86\u4e09\u5341\u591a\u5e74\uff0c\u5e38\u770b\u732b\u9e70\u4e0e\u6bd2\u86c7\u76f8\u6597\uff0c\u609f\u4e86\u4e0d\u5c11\u6b66\u529f\u3002\u5c24\u5176\u662f\u516b\u81c2\u795e \u9b54\u8428\u5929\u523a\uff0c\u4ee5\u8f7b\u529f\u8eab\u6cd5\uff0c\u5185\u5bb6\u52b2\u529b\u89c1\u957f\uff0c\u66f4\u4ee5\u732b\u9e70\u4e3a\u5e08\uff0c\u5b66\u5b83\u51cc\u7a7a\u6251\u51fb\u4e4b\u6280\uff0c\u6240\u4ee5\u53ea\u8bba\u8f7b\u529f \u5feb\u6377\u4e4b\u5904\uff0c\u4ed6\u8981\u7565\u8f93\u201c\u4e07\u91cc\u8ffd\u98ce\u201d\uff0c\u4f46\u5176\u4ed6\u529f\u592b\u5374\u80dc\u67f3\u5148\u5f00\u8fdc\u751a\uff0c\u5c31\u662f\u8f7b\u529f\u4e2d\u7684\u6251\u51fb\u53d8\u5316\u4e4b \u6280\uff0c\u67f3\u5148\u5f00\u4e5f\u4e0d\u53ca\u4ed6\u3002\u8428\u5929\u523a\u66f4\u7ec3\u4e86\u4e00\u79cd\u72ec\u95e8\u6b66\u529f\uff0c\u5176\u540d\u5c31\u79f0\u4e3a\u201c\u732b\u9e70\u722a\u201d\uff0c\u4ed6\u5341\u6307\u6307\u7532\uff0c \u6570\u5341\u5e74\u6765\u4ece\u4e0d\u4fee\u526a\uff0c\u6bcf\u53ea\u6307\u7532\uff0c\u957f\u53ef\u76c8\u5c3a\uff0c\u5176\u575a\u5982\u77f3\uff0c\u5e73\u65f6\u5377\u7740\u76d8\u5728\u6307\u5934\uff0c\u7528\u65f6\u4f38\u51fa\uff0c\u4fbf\u53d8 \u6210\u5229\u722a\uff0c\u800c\u4e14\u5341\u53ea\u6307\u7532\u90fd\u5728\u6bd2\u86c7\u7684\u6bd2\u6db2\u4e2d\u6d78\u8fc7\uff0c\u88ab\u4ed6\u5229\u722a\u6293\u4f24\uff0c\u82e5\u65e0\u80fd\u89e3\u86c7\u6bd2\u4e4b\u836f\uff0c\u5341\u4e8c\u65f6 \u8fb0\u4e4b\u5185\uff0c\u5fc5\u6b7b\u65e0\u7591\uff01\u4ee5\u524d\u4e0d\u77e5\u591a\u5c11\u6c5f\u6e56\u8c6a\u6770\uff0c\u5c31\u662f\u4e27\u751f\u5728\u4ed6\u5341\u722a\u4e4b\u4e0b\uff01 \u3000\u3000\u7384\u98ce\u9053\u957f\u5251\u62d0\u9f50\u65bd\uff0c\u548c\u516b\u81c2\u795e\u9b54\u518d\u5ea6\u6076\u6597\uff0c\u201c\u4e71\u62ab\u98ce\u201d\u5251\u6cd5\u4f7f\u5f97\u51cc\u5389\u65e0\u6bd4\uff0c\u5251\u5149\u6325\u970d\uff0c \u5251\u98ce\u864e\u864e\uff0c\u9e70\u7fd4\u96bc\u523a\uff0c\u771f\u5982\u72c2\u98ce\u9aa4\u8d77\uff0c\u66b4\u96e8\u5377\u6765\uff0c\u66f4\u517c\u5de6\u624b\u94c1\u62d0\u6a2a\u6321\u76f4\u5288\uff0c\u6253\u5f97\u5c71\u77f3\u7eb7\u98de\uff0c \u5c18\u6c99\u853d\u7a7a\uff0c\u5389\u5bb3\u975e\u51e1\u3002\u516b\u81c2\u795e\u9b54\u5728\u5251\u62d0\u7a81\u51fb\u4e4b\u4e0b\uff0c\u4f38\u957f\u5341\u722a\uff0c\u5c55\u5f00\u63cf\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u5ffd\u5982\u5de8\u81ba \u76c8\u7a7a\uff0c\u5ffd\u5982\u4f26\u86c7\u75be\u8d70\uff0c\u6bcf\u6bcf\u6b3a\u8fdb\u8fdb\u62db\u3002\u6293\u3001\u70b9\u3001\u52fe\uff0c\u6495\uff0c\u8eab\u6cd5\u638c\u6cd5\uff0c\u4e00\u4f7f\u5f00\u6765\uff0c\u7adf\u7136\u56db\u9762\u516b \u65b9\uff0c\u90fd\u53ea\u89c1\u8428\u5929\u523a\u7684\u8eab\u5f62\u5728\u8f6c\uff0c\u771f\u5982\u4e00\u4eba\u516b\u81c2\uff0c\u4ece\u56db\u9762\u6251\u51fb\u800c\u6765\uff01\u7384\u98ce\u9053\u957f\u4e0d\u7531\u5f97\u5012\u5438\u4e00\u53e3 \u51b7\u6c14\uff0c\u8fd9\u516b\u81c2\u795e\u9b54\u786e\u662f\u5e73\u751f\u672a\u89c1\u7684\u5f3a\u654c\uff01 \u3000\u3000\u516b\u81c2\u795e\u9b54\u4e5f\u662f\u6697\u7779\u60ca\u5fc3\uff0c\u4ed6\u7edd\u6599\u4e0d\u5230\u201c\u4e71\u62ab\u98ce\u201d\u5251\u6cd5\u5982\u6b64\u5389\u5bb3\uff0c\u4e24\u4eba\u4ee5\u653b\u5bf9\u653b\uff0c\u5f80\u5f80\u53ea \u4e89\u77ac\u606f\u5148\u540e\uff0c\u5728\u7384\u98ce\u9053\u957f\u6765\u8bf4\uff0c\u5e38\u89c9\u5229\u722a\u903c\u8fd1\u9762\u95e8\uff0c\u95ea\u8eb2\u8270\u96be\uff0c\u5728\u516b\u81c2\u795e\u9b54\u6765\u8bf4\uff0c\u4e5f\u662f\u5e38\u89c9 \u5251\u5149\u95ea\u52a8\uff0c\u4e0d\u79bb\u8981\u5bb3\u3002\u8fd9\u756a\u5927\u6218\uff0c\u53ea\u89c1\u94c1\u62d0\u5982\u5c71\uff0c\u5251\u5149\u5982\u7ec3\uff0c\u5341\u722a\u7ffb\u98de\uff0c\u4e24\u4eba\u7a0d\u4e00\u4e0d\u614e\uff0c\u90fd \u6709\u8840\u6e85\u9ec4\u7802\u4e4b\u9669\uff01\u67f3\u5148\u5f00\u89c1\u5e08\u5144\u5371\u6025\uff0c\u4ed7\u7740\u5353\u7edd\u7684\u8f7b\u529f\uff0c\u65f6\u6765\u95ea\u51fb\uff0c\u8428\u5929\u523a\u95ea\u8eab\u6765\u6293\u4ed6\u65f6\uff0c \u4ed6\u53c8\u98d8\u8eab\u6025\u8eb2\uff0c\u7384\u98ce\u9053\u957f\u5f97\u5e08\u5f1f\u5408\u51fb\u4e4b\u529b\uff0c\u7565\u7565\u5360\u4e86\u4e0a\u98ce\u3002\u6076\u6218\u6b63\u9163\uff0c\u731b\u542c\u5f97\u5927\u529b\u795e\u9b54\u4e0e\u9648 \u5143\u9738\u9707\u5929\u4ef7\u7684\u4e00\u58f0\u5927\u53eb\uff01 \u3000\u3000\u539f\u6765\u90a3\u8fb9\u7b11\u5f25\u52d2\u6717\u6708\u7985\u5e08\u4e0e\u201c\u5355\u638c\u5f00\u7891\u201d\u9648\u5143\u9738\u4e8c\u4eba\uff0c\u5408\u6218\u5927\u529b\u795e\u9b54\u8428\u5929\u90fd\uff0c\u4e5f\u5df2\u5230\u4e86 \u5f3a\u5b58\u5f31\u4ea1\uff0c\u6b7b\u751f\u4fc4\u9877\u4e4b\u9645\uff01\u6717\u6708\u7985\u5e08\u51e0\u53e3\u731b\u9152\u628a\u8428\u5929\u90fd\u7684\u9ebb\u8863\u5c04\u6210\u65e0\u6570\u5c0f\u6d1e\uff0c\u8428\u5929\u90fd\u5927\u543c\u4e00 \u58f0\uff0c\u8fd0\u529b\u4e00\u6323\uff0c\u628a\u9ebb\u8863\u903c\u6210\u65e0\u6570\u788e\u7247\uff0c\u968f\u98ce\u98de\u821e\uff0c\u4e0a\u534a\u8eab\u8d64\u6761\u6761\u7684\uff0c\u9732\u51fa\u8d64\u94dc\u8272\u7684\u76ae\u80a4\uff0c\u53cc \u81c2\u575f\u8d77\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u80d6\u548c\u5c1a\u4f60\u6709\u591a\u5c11\u6697\u5668\uff0c\u6d12\u5bb6\u4e5f\u4e0d\u6015\u4f60\uff01\u53ea\u628a\u9152\u55b7\u6765\u4f5c\u751a\uff1f\u201d\u4e00\u62db\u201c\u53cc \u9f99\u51fa\u6d77\u201d\u5de6\u53f3\u4e24\u62f3\uff0c\u7a81\u51fb\u6717\u6708\u7985\u5e08\u7684\u201c\u592a\u9633\u7a74\u201d\uff0c\u6717\u6708\u54c8\u54c8\u4e00\u7b11\uff0c\u8eab\u968f\u638c\u8f6c\uff0c\u89e3\u4e0b\u62a4\u8eab\u8f6f \u97ad\uff0c\u4e00\u97ad\u626b\u53bb\uff0c\u9648\u5143\u9738\u62cd\u62cd\u4e24\u638c\uff0c\u72b9\u5982\u87b3\u8782\u6355\u8749\uff0c\u9ec4\u96c0\u5728\u540e\uff0c\u4e5f\u5206\u5411\u8428\u5929\u90fd\u5de6\u53f3\u201c\u592a\u9633\u7a74\u201d \u62cd\u6765\uff0c\u8428\u5929\u90fd\u7a81\u7136\u56de\u8098\u4e00\u649e\uff0c\u731b\u7136\u7ffb\u8eab\uff0c\u63a5\u7740\u4e00\u4e2a\u201c\u8098\u5e95\u770b\u6376\u201d\uff0c\u53f3\u62f3\u5728\u8098\u5e95\u9aa4\u7136\u51fb\u51fa\uff0c\u9648 \u5143\u9738\u7a0d\u9000\u534a\u6b65\uff0c\u89d1\u4e2a\u771f\u5207\uff0c\u5de6\u638c\u5411\u4e0a\u4e00\u6258\uff0c\u987a\u52bf\u628a\u4ed6\u7684\u62f3\u5934\u6258\u8d77\uff0c\u53f3\u624b\u4e94\u6307\u52fe\u7740\u4ed6\u80f3\u5f2f\u5173\u8282 \u4e00\u626d\uff0c\u8fd9\u7740\u6b63\u662f\u9648\u5143\u9738\u5e73\u751f\u7edd\u6280\u4e43\u201c\u5206\u7b4b\u9519\u9aa8\u624b\u201d\u4e2d\u7684\u5229\u5bb3\u6740\u624b\uff0c\u4e00\u626d\u4e4b\u4e0b\uff0c\u7b4b\u65ad\u9aa8\u788e\uff0c\u4efb \u662f\u6b66\u529f\u591a\u5f3a\u7684\u4eba\uff0c\u88ab\u4ed6\u642d\u4f4f\uff0c\u4ea6\u662f\u65e0\u6cd5\u62b5\u6297\uff0c\u4e0d\u6599\u8428\u5929\u90fd\u624b\u8098\u4e00\u6c89\uff0c\u6d51\u5982\u672a\u89c9\uff0c\u5de6\u638c\u7a81\u7136\u6253 \u51fa\uff0c\u547c\u7684\u4e00\u638c\uff0c\u76f4\u5288\u9876\u95e8\uff0c\u9648\u5143\u9738\u614c\u5fd9\u677e\u5f00\uff0c\u8eb2\u95ea\u4e0d\u53ca\uff0c\u903c\u5f97\u633a\u80a9\u4e00\u649e\uff0c\u786c\u751f\u751f\u7684\u63a5\u4e86\u5927\u529b \u795e\u9b54\u4e00\u638c\uff0c\u8e09\u8e09\u8dc4\u8dc4\u76f4\u649e\u51fa\u53bb\u3002\u8fd9\u65f6\u90ce\u6708\u7985\u5e08\u5237\u5237\u4e24\u97ad\uff0c\u4e5f\u90fd\u6253\u4e2d\u4e86\u8428\u5929\u90fd\u7684\u540e\u5fc3\uff0c\u8428\u5929\u90fd \u4e00\u58f0\u6012\u543c\uff0c\u53cd\u81c2\u4e00\u635e\uff0c\u7ade\u628a\u8f6f\u97ad\u635e\u7740\uff0c\u7528\u529b\u4e00\u626f\uff0c\u6717\u6708\u7985\u5e08\u8eab\u4e0d\u7531\u5df1\u7ed9\u626f\u8fc7\u6765\uff0c\u6025\u5fd9\u628a\u624b\u4e00 \u677e\uff0c\u8428\u5929\u90fd\u6536\u529b\u4e0d\u53ca\uff0c\u5495\u549a\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\uff0c\u9648\u5143\u9738\u98de\u8eab\u6251\u4e0a\uff0c\u8428\u5929\u90fd\u5728\u5730\u4e0a\u6253\u4e86\u4e24\u4e2a\u76d8 \u65cb\uff0c\u731b\u7684\u5c06\u9648\u5143\u9738\u62b1\u7740\uff0c\u80cc\u4e0a\u53d7\u4e86\u51e0\u62f3\uff0c\u4f46\u9648\u5143\u9738\u5374\u7ed9\u4ed6\u538b\u5728\u4e0b\u9762\uff01\u6717\u6708\u7985\u5e08\u62a2\u56de\u8f6f\u97ad\uff0c\u5927 \u60ca\u5931\u8272\uff0c\u770b\u7740\u4e24\u4eba\u5728\u5730\u4e0b\u626d\u6253\uff0c\u7ffb\u7ffb\u6eda\u6eda\uff0c\u6717\u6708\u63b8\u5e08\u63d0\u97ad\u6ce8\u89c6\uff0c\u4e0d\u6562\u4e0b\u624b\u3002\u7247\u523b\u4e4b\u540e\uff0c\u9648\u5143 \u9738\u53cc\u624b\u88ab\u6309\uff0c\u8428\u5929\u90fd\u6a2a\u8098\u62b5\u7740\u4ed6\u7684\u5fc3\u53e3\uff0c\u6b63\u5728\u7528\u529b\uff0c\u6717\u6708\u7985\u5e08\u6025\u5fd9\u4e00\u97ad\u626b\u4e0b\uff0c\u731b\u6253\u8428\u5929\u90fd\u540e \u8111\uff0c\u8428\u5929\u90fd\u53d7\u4e86\u4e00\u97ad\uff0c\u75bc\u75db\u4e0d\u582a\uff0c\u62b1\u8d77\u9648\u5143\u9738\u5f80\u524d\u4e00\u6254\uff0c\u7ffb\u8d77\u8eab\u6765\uff0c\u53c8\u4e0e\u6717\u6708\u7985\u5e08\u6076\u6218\uff01 \u3000\u3000\u67f3\u5148\u5f00\u89c1\u72b6\u5927\u60ca\uff0c\u820d\u4e0b\u516b\u81c2\u795e\u9b54\uff0c\u98de\u8eab\u4e00\u63a0\uff0c\u628a\u9648\u5143\u9738\u63a5\u7740\uff0c\u653e\u4e86\u4e0b\u6765\uff0c\u53ea\u89c1\u9648\u5143\u9738\u5934 \u9762\u9752\u80bf\uff01 \u3000\u3000\u95ee\u9053\uff1a\u201c\u5982\u4f55\uff1f\u201d \u3000\u3000\u9648\u5143\u9738\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u6b64\u4f24\u65e0\u59a8\uff01\u201d\u53cc\u638c\u4e00\u9519\uff0c\u518d\u6740\u4e0a\u53bb\uff01\u4f46\u6b64\u65f6\u7384\u98ce\u9053\u957f\u548c\u6717\u6708\u7985\u5e08\u5374 \u662f\u7ed9\u53cc\u9b54\u6253\u5f97\u8fde\u8fde\u540e\u9000\u4e86\u3002 \u3000\u3000\u9648\u5143\u9738\u4ece\u6765\u53d7\u8fc7\u5982\u6b64\u632b\u8d25\uff0c\u6012\u706b\u5982\u711a\uff0c\u518d\u5ea6\u4ea4\u950b\uff0c\u66f4\u662f\u52c7\u731b\uff0c\u6253\u4e86\u7247\u523b\uff0c\u9648\u5143\u9738\u518d\u7528 \u201c\u5206\u7b4b\u9519\u9aa8\u201d\u624b\u6cd5\uff0c\u5192\u9669\u903c\u8fd1\uff0c\u634f\u7740\u4ed6\u7684\u80a9\u5934\u8981\u5bb3\u4e4b\u5904\u4e00\u626d\uff0c\u90a3\u6599\u8428\u5929\u90fd\u53c8\u662f\u6d51\u5982\u672a\u89c9\uff0c\u624b \u8d77\u4e00\u638c\uff0c\u53c8\u628a\u9648\u5143\u9738\u6253\u8dcc\u5730\u4e0a\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u5c61\u6b21\u6765\u4e0e\u6211\u5475\u75d2\uff0c\u662f\u4f55\u7528\u610f\uff1f\u201d\u9648\u5143 \u9738\u7ffb\u8d77\u8eab\u6765\uff0c\u4f5c\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u8428\u5929\u90fd\u94dc\u76ae\u94c1\u9aa8\uff0c\u975e\u4f46\u666e\u901a\u7684\u5200\u67aa\u4e0d\u5165\uff0c\u5c31\u8fde\u6781\u5389\u5bb3\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u6cd5\uff0c\u7adf\u4e5f\u4f24\u4e0d\u4e86\u4ed6\u3002 \u4ed6\u521a\u624d\u8fde\u53d7\u97ad\u6572\u62f3\u6253\uff0c\u9664\u4e86\u6717\u6708\u7985\u5e08\u6253\u4ed6\u540e\u8111\u90a3\u97ad\uff0c\u89c9\u5f97\u75bc\u75db\u4e4b\u5916\uff0c\u5176\u4ed6\u6253\u51fb\uff0c\u7b80\u76f4\u4e0d\u5f53\u4f5c \u4e00\u56de\u4e8b\u513f\u3002\u6717\u6708\u7985\u5e08\u5185\u529f\u867d\u9ad8\uff0c\u5374\u4e5f\u62ff\u4ed6\u65e0\u6cd5\u3002\u7b11\u5f25\u52d2\u7b11\u4e0d\u51fa\u53e3\uff0c\u53ea\u597d\u6c89\u7740\u5e94\u62db\uff0c\u4e14\u6218\u4e14\u9000\uff01 \u3000\u3000\u9648\u5143\u9738\u6076\u6218\u4e4b\u540e\uff0c\u518d\u53d7\u4e00\u638c\uff0c\u9aa8\u9abc\u9690\u9690\u4f5c\u75db\uff0c\u7384\u98ce\u9053\u957f\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e8c\u5f1f\u56db\u5f1f\uff0c\u79fb\u8fc7\u8fd9 \u8fb9\uff0c\u54b1\u4eec\u5e76\u80a9\u5b50\u6597\u4ed6\uff01\u201d\u6717\u6708\u7985\u5e08\u5077\u7a7a\u53c8\u559d\u4e86\u4e00\u53e3\u9152\uff0c\u7528\u9152\u55b7\u4ed6\u53cc\u76ee\uff0c\u8428\u5929\u90fd\u6a2a\u638c\u62a4\u775b\uff0c\u653b \u51fb\u4e4b\u52bf\u7a0d\u7f13\uff0c\u90ce\u6708\u7985\u5e08\u4e0e\u9648\u5143\u9738\u817e\u8eab\u6025\u8d70\uff0c\u4e0e\u7384\u98ce\u9053\u957f\u4f1a\u5408\u4e00\u5904\uff0c\u5a01\u529b\u5927\u589e\uff0c\u516b\u81c2\u795e\u9b54\u95ea\u8eab \u75be\u9000\uff0c\u9648\u5143\u9738\u62a1\u524d\u4e00\u638c\u5288\u53bb\uff0c\u5ffd\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u8428\u5929\u523a\u957f\u957f\u7684\u6307\u7532\u5df2\u63d2\u4e2d\u80a9\u5934\uff0c\u7384\u98ce\u9053\u957f\u84e6\u5730 \u4e00\u5251\uff0c\u8428\u5929\u523a\u8df3\u8eab\u907f\u5f00\uff0c\u7384\u98ce\u9053\u957f\u6478\u51fa\u4e00\u5757\u836f\u997c\uff0c\u75be\u5fd9\u629b\u7ed9\u9648\u5143\u9738\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u722a\u5b50\u6709 \u6bd2\uff0c\u5feb\u628a\u836f\u997c\u56bc\u788e\u54bd\u4e0b\u3002\u201d\u9648\u5143\u9738\u609a\u7136\u4e00\u60ca\uff0c\u4f9d\u8a00\u628a\u836f\u997c\u56bc\u4e0b\uff0c\u53ea\u89c9\u80a9\u5934\u9ebb\u8fa3\u8fa3\u7684\u4f5c\u75db\u3002 \u3000\u3000\u8fd9\u5f53\u513f\uff0c\u5927\u529b\u795e\u9b54\u8428\u5929\u90fd\u4e5f\u5df2\u8d76\u5230\uff0c\u67f3\u5148\u5f00\u8fd0\u7edd\u9876\u8f7b\u529f\uff0c\u5c48\u7740\u5341\u6307\uff0c\u7a81\u7136\u6251\u51fb\uff0c\u8428\u5929\u90fd \u5929\u7075\u76d6\u4e0a\u4e2d\u4e86\u53f3\u624b\u4e94\u6307\u94a2\u73af\uff0c\u8111\u75db\u6b32\u88c2\uff0c\u53cc\u81c2\u594b\u529b\u4e00\u6296\uff0c\u628a\u67f3\u5148\u5f00\u5f39\u51fa\u6570\u4e08\u4e4b\u5916\uff0c\u7384\u98ce\u9053\u957f \u7684\u201c\u4e71\u62ab\u98ce\u201d\u5251\u6cd5\u72e0\u6377\u5f02\u5e38\uff0c\u957f\u5251\u4e00\u6307\uff0c\u5df2\u5230\u8428\u5929\u90fd\u81c2\u4e0a\uff0c\u8428\u5929\u90fd\u808c\u8089\u4e00\u7f29\uff0c\u957f\u5251\u6ed1\u5f00\uff0c\u7384 \u98ce\u9053\u957f\u5fc3\u5ff5\u4e00\u52a8\uff0c\u77e5\u4ed6\u5916\u529f\u767b\u5cf0\u9020\u6781\uff0c\u8981\u523a\u4e5f\u523a\u4e0d\u5165\uff0c\u6697\u8fd0\u5185\u529b\uff0c\u8d81\u52bf\u4e00\u7ede\uff0c\u8428\u5929\u90fd\u72c2\u53eb\u4e00 \u58f0\uff0c\u76ae\u7834\u8840\u6d41\uff0c\u8df3\u4e86\u5f00\u53bb\uff01\u8428\u5929\u523a\u5df2\u81ea\u4e58\u865a\u6293\u5230\uff0c\u4e00\u722a\u6293\u5230\u7384\u98ce\u8111\u95e8\uff0c\u7384\u98ce\u7f29\u8eab\u4e00\u95ea\uff0c\u80a9\u5934 \u4e5f\u53d7\u4e86\u4e00\u6293\uff0c\u6025\u6025\u8df3\u5f00\uff0c\u731b\u56bc\u836f\u997c\u3002\u8428\u5929\u90fd\u4e2d\u4e86\u4e00\u5251\uff0c\u5de6\u81c2\u8f6c\u52a8\u7a0d\u4e3a\u8fdf\u949d\uff0c\u4f46\u4ecd\u662f\u5f3a\u653b\u731b \u6251\uff0c\u72b7\u5389\u5143\u4fe6\uff01 \u3000\u3000\u56db\u4fa0\u53cc\u9b54\u8fde\u756a\u731b\u6597\uff0c\u7384\u98ce\u4e0e\u9648\u5143\u9738\u6597\u4e86\u4e00\u9635\uff0c\u5ffd\u89c9\u6076\u5fc3\u6b32\u5455\uff0c\u539f\u6765\u8428\u5929\u523a\u6307\u7532\u7684\u6bd2\uff0c\u4e43 \u662f\u7528\u86c7\u5c9b\u4e2d\u6700\u6bd2\u7684\u201c\u91d1\u7ebf\u86c7\u201d\u53e3\u6d8e\u6240\u70bc\uff0c\u7384\u98ce\u9884\u5907\u7684\u836f\u997c\u867d\u7136\u662f\u89e3\u6cbb\u86c7\u6bd2\u7684\u4e0a\u7b49\u597d\u836f\uff0c\u5374\u4e5f \u53ea\u80fd\u9632\u6b62\u6bd2\u6db2\u5728\u4f53\u5185\u8513\u5ef6\u5f00\u6765\uff0c\u4f46\u6bd2\u6db2\u5728\u80c3\u4e2d\u4f5c\u602a\uff0c\u5341\u5206\u96be\u53d7\uff01\u53cc\u9b54\u4e00\u4e2a\u64c5\u4e8e\u8f7b\u529f\uff0c\u6251\u51fb\u51cc \u5389\uff0c\u4e00\u4e2a\u94dc\u76ae\u94c1\u9aa8\uff0c\u529b\u5927\u65e0\u7a77\uff0c\u957f\u77ed\u4e92\u8865\uff0c\u6740\u6cd5\u51f6\u7eb6\uff0c\u56db\u4fa0\u867d\u4e5f\u5404\u6709\u72ec\u95e8\u6b66\u529f\uff0c\u7adf\u81ea\u6162\u6162\u62b5 \u654c\u4e0d\u4f4f\u3002 \u3000\u3000\u7384\u98ce\u9053\u957f\u594b\u529b\u6321\u4e86\u4e00\u9635\uff0c\u4e00\u53e3\u9ec4\u6c34\u5455\u4e86\u51fa\u6765\uff0c\u8428\u5929\u523a\u98d8\u8eab\u95ea\u8eb2\uff0c\u7384\u98ce\u9053\u957f\u5ffd\u7136\u9192\u8d77\u53eb \u9053\uff1a\u201c\u4e8c\u5f1f\u3001\u4e09\u5f1f\uff0c\u8d70\u51fa\u5708\u5916\uff01\u4e8c\u5f1f\u7528\u9152\u55b7\u4ed6\u4eec\u7684\u62db\u5b50\uff01\u4e09\u5f1f\u7528\u94a2\u73af\u51ff\u4ed6\u4eec\u7684\u8111\u888b\uff01\u201d\u8428\u5929 \u523a\u626c\u722a\u6025\u6293\uff0c\u67f3\u5148\u5f00\u548c\u6717\u6708\u7985\u5e08\u65e9\u5df2\u53cc\u53cc\u7eb5\u51fa\uff0c\u6717\u6708\u7985\u5e08\u5f20\u53e3\u4e00\u55b7\uff0c\u4e24\u6761\u767d\u7ec3\uff0c\u8ff3\u53d6\u516b\u81c2\u795e \u9b54\u9762\u4e0a\u53cc\u77b3\uff01 \u3000\u3000\u8428\u5929\u523a\u4e00\u8f6c\u8eab\u5f62\uff0c\u907f\u5f00\u767d\u7ec3\u822c\u7684\u9152\u6d6a\uff0c\u6717\u6708\u63b8\u5e08\u5f20\u53e3\u4e00\u55b7\uff0c\u53c8\u662f\u4e24\u6761\u767d\u7ec3\u5411\u8428\u5929\u90fd\u5de6\u53f3 \u53cc\u76ee\u5c04\u6765\uff0c\u8428\u5929\u90fd\u8f7b\u529f\u4e0d\u53ca\u4e43\u5144\uff0c\u4ecd\u4ee5\u4e00\u638c\u62a4\u775b\uff0c\u4e00\u638c\u5e94\u654c\uff0c\u201c\u9152\u7ec3\u201d\u98de\u6765\uff0c\u7ed9\u4ed6\u84b2\u6247\u822c\u7684 \u5927\u624b\u8fce\u98ce\u4e00\u6247\uff0c\u9152\u96e8\u7eb7\u98de\uff0c\u843d\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u4ed6\u94c1\u9aa8\u94dc\u76ae\uff0c\u81ea\u7136\u4e0d\u6015\u3002\u4f46\u5c31\u5728\u8fd9\u970e\u90a3\u95f4\uff0c\u7384\u98ce \u9053\u957f\u7684\u957f\u5251\u5df2\u5530\u5530\u5530\u8fde\u523a\u4e09\u4e0b\uff0c\u8428\u5929\u90fd\u817e\u632a\u95ea\u907f\u4e2d\uff0c\u95f9\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u5934\u4e0a\u53c8 \u4e2d\u4e86\u67f3\u5148\u5f00\u7684\u4e94\u6307\u94a2\u73af\u3002\u5927\u51e1\u6709\u5916\u5bb6\u201c\u6a2a\u7ec3\u201d\u529f\u592b\u7684\u4eba\uff0c\u9664\u4e86\u201c\u7ec3\u95e8\u201d\u8106\u5f31\uff0c\u662f\u81f4\u547d\u4f24\u4e4b \u5916\uff0c\u5176\u4f59\u8111\u95e8\u3001\u592a\u9633\u7a74\u3001\u4e0b\u9634\u7b49\u5904\uff0c\u4e5f\u6bd4\u8f83\u8106\u5f31\uff0c\u867d\u7136\u4e0d\u81f4\u4e00\u51fb\u6bd9\u547d\uff0c\u4f46\u7ed9\u5185\u5bb6\u91cd\u624b\u6cd5\u6253 \u4e2d\uff0c\u4e5f\u662f\u75bc\u75db\u96be\u5f53\uff01\u8428\u5929\u90fd\u91d1\u661f\u706b\u7206\uff0c\u75db\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u7384\u98ce\u9053\u957f\u5251\u5149\u95ea\u95ea\uff0c\u76f4\u6307\u8981\u5bb3\uff0c\u8428\u5929 \u523a\u6251\u5730\u63a0\u6765\uff0c\u5341\u6307\u5982\u94a9\uff0c\u6025\u5fd9\u4ece\u540e\u88ad\u51fb\uff0c\u7384\u98ce\u65cb\u6b65\u98de\u8eab\uff0c\u53c8\u518d\u540e\u9000\uff0c\u5f85\u8428\u5929\u90fd\u53cc\u638c\u5288\u98ce\u4e0e\u8428 \u5929\u523a\u5e76\u80a9\u518d\u4e0a\u65f6\uff0c\u6717\u6708\u63b8\u5e08\u53c8\u662f\u4e00\u53e3\u6025\u9152\uff0c\u55b7\u6210\u767d\u7ec3\uff0c\u5148\u540e\u5206\u53d6\u4e24\u4eba\u53cc\u76ee\uff01 \u3000\u3000\u56db\u4fa0\u4e2d\uff0c\u6717\u6708\u7985\u5e08\u5185\u529f\u4e4b\u6df1\u4ec5\u5728\u7384\u98ce\u9053\u957f\u4e4b\u4e0b\uff0c\u4ed6\u55b7\u9152\u4f24\u4eba\u7684\u529f\u592b\uff0c\u5df2\u7ec3\u81f3\u51fa\u795e\u5165\u5316\u4e4b \u5883\uff0c\u53ef\u4ee5\u8fd0\u9152\u6210\u7ec3\uff0c\u540c\u53d1\u6570\u6761\uff0c\u53c8\u53ef\u4ee5\u6fc0\u6d12\u6210\u5f39\uff0c\u5982\u51b0\u96f9\u98de\u964d\uff0c\u53d6\u4eba\u53cc\u76ee\uff0c\u9632\u4e0d\u80dc\u9632\u3002\u53cc\u9b54 \u867d\u662f\u9876\u5c16\u513f\u7684\u4eba\u7269\uff0c\u5e94\u4ed8\u8fd9\u6837\u5947\u5f02\u7684\u6697\u5668\uff0c\u53c8\u5728\u4e09\u4fa0\u5408\u51fb\u4e4b\u4e0b\uff0c\u4e5f\u81ea\u6709\u70b9\u624b\u5fd9\u811a\u4e71\uff01 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u5f62\u52bf\u6025\u8f6c\uff0c\u521a\u624d\u662f\u53cc\u9b54\u5360\u5c3d\u4e0a\u98ce\uff0c\u800c\u73b0\u5728\u5374\u662f\u56db\u4fa0\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u5404\u5c55\u72ec\u95e8\u7edd\u6280\uff0c \u903c\u5f97\u53cc\u9b54\u4e1c\u7a9c\u897f\u907f\uff0c\u9163\u6218\u4e2d\u8428\u5929\u523a\u6025\u5f39\u94a2\u73af\uff0c\u4f38\u722a\u8981\u6293\u9648\u5143\u9738\u8109\u95e8\uff0c\u7384\u98ce\u9053\u957f\u5251\u6cd5\u72e0\u6377\u65e0 \u4f26\uff0c\u8fde\u4f7f\u201c\u8ffd\u98ce\u516b\u5251\u201d\uff0c\u4e0a\u4e0b\u5de6\u53f3\uff0c\u5251\u5251\u4e0d\u79bb\u516b\u81c2\u795e\u9b54\u8981\u5bb3\u3002\u8428\u5929\u523a\u903c\u5f97\u8fd0\u201c\u732b\u9e70\u722a\u201d\u7684\u4e0a \u4e58\u6b66\u529f\uff0c\u8eab\u5b50\u84e6\u5730\u98de\u817e\uff0c\u4e00\u6293\u5c06\u67f3\u5148\u5f00\u5413\u9000\uff0c\u4e00\u4e2a\u7b4b\u6597\u5012\u7ffb\u4e0b\u5730\uff0c\u6b63\u5f85\u62db\u547c\u8428\u5929\u90fd\u6025\u9000\uff0c\u80cc \u4e0a\u7684\u51af\u745b\u98a0\u7c38\u8fc7\u751a\uff0c\u53c8\u201c\u54c7\u54c7\u201d\u7684\u5927\u54ed\u8d77\u6765\uff01\u8428\u5929\u523a\u84e6\u7136\u4e00\u6014\uff01\u6717\u6708\u7985\u5e08\u55b7\u9152\u6210\u7ec3\uff01\u731b\u7136\u98de \u81f3\u9762\u95e8\uff01\u8428\u5929\u523a\u56de\u8eab\u95ea\u907f\uff0c\u7384\u98ce\u9053\u957f\u5251\u950b\u4e00\u6307\uff0c\u9752\u5149\u4e00\u95ea\uff0c\u5df2\u81ea\u659c\u4fa7\u76f4\u6251\u54bd\u5589\uff0c\u8428\u5929\u523a\u4f4e\u5934 \u8eac\u8170\uff0c\u5df2\u89c9\u5251\u98ce\u98d2\u7136\uff0c\u6cbe\u88f3\u523a\u8089\uff0c\u8428\u5929\u523a\u672c\u80fd\u7684\u8eab\u5f62\u4e00\u4fa7\uff0c\u808c\u8089\u6536\u7f29\uff0c\u672c\u6765\u9884\u6599\u8fd9\u5251\u5fc5\u96be\u9003 \u907f\uff0c\u6240\u4ee5\u8fd0\u6c14\u7f29\u808c\uff0c\u5e0c\u671b\u51cf\u5c11\u4f24\u5bb3\u3002\u4e0d\u610f\u5c31\u5728\u8fd9\u970e\u90a3\u95f4\uff0c\u51af\u745b\u5389\u58f0\u53eb\u558a\uff0c\u539f\u6765\u7384\u98ce\u957f\u5251\u523a \u5012\uff0c\u8428\u5929\u523a\u8eac\u8170\u8eb2\u95ea\uff0c\u628a\u51af\u745b\u7ea2\u55b7\u55b7\u7684\u5c0f\u8138\u663e\u9732\u51fa\u6765\uff0c\u7384\u98ce\u8fd9\u5251\u672c\u8981\u523a\u8428\u5929\u523a\u9888\u9879\u8981\u5bb3\uff0c\u51af \u745b\u5c0f\u8138\u4e00\u626d\uff0c\u6070\u6070\u6321\u4f4f\uff0c\u5979\u89c1\u7740\u5251\u5149\u95ea\u95ea\uff0c\u90a3\u80fd\u4e0d\u5389\u58f0\u53eb\u558a\uff1f\u7384\u98ce\u597d\u4e0d\u5bb9\u6613\u624d\u5f97\u7740\u8fd9\u4e00\u5251\u4e4b \u673a\uff0c\u4f46\u7ed9\u51af\u745b\u6321\u4f4f\uff0c\u5c31\u7b97\u56db\u4fa0\u4e0e\u53cc\u9b54\u6709\u8840\u6d77\u6df1\u4ec7\uff0c\u8fd9\u4e00\u5251\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u5fcd\u5fc3\u523a\u4e0b\uff01\u9ad8\u624b\u6bd4 \u6b66\uff0c\u53ea\u4e89\u77ac\u606f\u5148\u540e\uff0c\u7384\u98ce\u8fd9\u4e00\u8e0c\u8e87\uff0c\u505c\u5251\u4e0d\u523a\uff0c\u8428\u5929\u523a\u5df2\u84e6\u7136\u53cd\u624b\u4e00\u6293\uff0c\u628a\u4ed6\u7684\u624b\u8155\u6293\u4e86\u4e94 \u9053\u4f24\u75d5\uff0c\u957f\u5251\u5f53\u7684\u4e00\u58f0\u5815\u5730\uff01 \u3000\u3000\u8428\u5929\u90fd\u89c1\u5144\u957f\u5f97\u624b\uff0c\u4e00\u58f0\u602a\u5578\uff0c\u8ddf\u8e2a\u6251\u4e0a\uff0c\u547c\u7684\u4e00\u638c\uff0c\u8fc5\u5982\u5954\u96f7\uff0c\u8fce\u5934\u5288\u4e0b\uff0c\u4ed6\u521a\u624d\u53d7 \u4e86\u7384\u98ce\u4e00\u5251\u4e4b\u4f24\uff0c\u6c14\u6124\u4e4b\u6781\uff0c\u8fd9\u4e00\u638c\u7528\u5c3d\u5168\u529b\uff0c\u8981\u628a\u7384\u98ce\u9707\u6210\u7c89\u788e\uff01\u9648\u5143\u9738\u4e0d\u987e\u751f\u6b7b\uff0c\u62fc\u7740 \u6027\u547d\uff0c\u53cc\u638c\u9f50\u98de\uff0c\u659c\u523a\u91cc\u51b2\u51fa\u6551\u63f4\uff0c\u4e09\u638c\u76f8\u4ea4\uff0c\u567c\u556a\u4e00\u58f0\uff0c\u6709\u5982\u88c2\u5e1b\uff0c\u5de6\u638c\u53d7\u529b\u6700\u91cd\uff0c\u5de6\u624b \u8155\u9aa8\uff0c\u7adf\u7136\u9707\u88c2\uff0c\u624b\u81c2\u540a\u4e86\u4e0b\u6765\uff01\u8428\u5929\u90fd\u638c\u529b\u672a\u5378\u4ecd\u5411\u7384\u98ce\u6309\u53bb\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u53c8\u628a\u7384\u98ce\u9707\u51fa \u4e08\u8bb8\uff01\u8428\u5929\u90fd\u653e\u5f00\u9648\u5143\u9738\uff0c\u8ff3\u81ea\u98de\u8eab\u731b\u6251\uff0c\u8981\u53d6\u7384\u98ce\u6027\u547d\uff01 \u3000\u3000\u5e78\u5728\u7384\u98ce\u9053\u957f\u5185\u529f\u6df1\u6e5b\uff0c\u53c8\u6709\u9648\u5143\u9738\u62a2\u5728\u524d\u5934\u6321\u4e86\u4e00\u638c\uff0c\u628a\u8428\u5929\u90fd\u638c\u529b\u6d88\u53bb\u5927\u534a\uff0c\u8fd9\u4e00 \u638c\u624d\u80fd\u4e0d\u53d7\u5185\u4f24\uff0c\u4f46\u4e5f\u5df2\u662f\u8eab\u5f62\u4e0d\u7a33\uff0c\u67f3\u5148\u5f00\u4e0e\u6717\u6708\u63b8\u5e08\uff0c\u6025\u5fd9\u8d76\u4e0a\uff0c\u7f20\u7740\u516b\u81c2\u795e\u9b54\uff0c\u56e0\u4e3a \u4e24\u4eba\u6df1\u77e5\u8428\u5929\u523a\u8eab\u5f62\u5982\u98ce\uff0c\u5185\u529b\u6df1\u539a\uff0c\u8fd8\u5728\u8428\u5929\u90fd\u4e4b\u4e0a\uff0c\u7384\u98ce\u9053\u957f\u53d7\u4f24\u4e4b\u540e\uff0c\u8981\u62b5\u5fa1\u8428\u5929\u90fd \u8c05\u8fd8\u53ef\u4ee5\uff0c\u8981\u62b5\u5fa1\u8428\u5929\u523a\u5374\u662f\u4e07\u4e07\u4e0d\u80fd\uff0c\u6240\u4ee5\u5b81\u53ef\u653e\u8fc7\u8428\u5929\u90fd\u53bb\u6551\u7384\u98ce\uff0c\u5374\u62a2\u5148\u62e6\u7740\u4e86\u8428\u5929 \u523a\uff01 \u3000\u3000\u7384\u98ce\u9053\u957f\u4e2d\u4e86\u6bd2\u722a\uff0c\u53f3\u624b\u8109\u95e8\u706b\u8fa3\u8fa3\u7684\u9ebb\u75d2\u4f5c\u75db\uff0c\u6599\u77e5\u6240\u4f24\u975e\u8f7b\uff0c\u8428\u5929\u90fd\u53c8\u6076\u72e0\u72e0\u7684\u81ea \u540e\u6251\u51fb\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u9053\u7237\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u8fd0\u8db3\u5185\u529b\uff0c\u94e3\u62d0\u62ab\u98ce\uff0c\u7ffb\u8eab\u6025\u51fb\uff0c\u8428\u5929 \u90fd\u81ea\u6043\u94dc\u76ae\u94c1\u9aa8\uff0c\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u547c\u547c\u53d1\u638c\uff0c\u6b3a\u8eab\u76f4\u8fdb\uff0c\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u8170\u80c1\u4e4b\u5904\uff0c\u53d7\u4e86\u4e00\u62d0\uff0c \u672c\u6765\u6b64\u5904\u4e00\u975e\u2018\u7ec3\u95e8\u201d\uff0c\u4e8c\u975e\u8981\u5bb3\uff0c\u5e73\u5e38\u5200\u67aa\u4e0d\u5165\uff0c\u800c\u4eca\u53d7\u4e86\u4e00\u62d0\uff0c\u5374\u9aa8\u75db\u6b32\u88c2\uff0c\u5927\u529b\u795e\u9b54 \u5916\u5bb6\u529f\u592b\u867d\u7136\u767b\u5cf0\u9020\u6781\uff0c\u4e5f\u81ea\u7981\u53d7\u4e0d\u4f4f\uff0c\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u5410\u4e86\u4e00\u5927\u53e3\u9c9c\u8840\uff0c\u8170\u80c1\u7684\u4e24\u6839\u808b\u9aa8 \u7adf\u81ea\u65ad\u4e86\uff01\u7384\u98ce\u9053\u957f\u542b\u55d4\u53d1\u62d0\uff0c\u7adf\u5982\u66b4\u98ce\u6025\u96e8\u822c\u626b\u6765\uff0c\u8428\u5929\u90fd\u4e0d\u6562\u518d\u4ee5\u8840\u8089\u4e4b\u8eaf\uff0c\u63a5\u4ed6\u94c1 \u62d0\uff0c\u8fde\u8fde\u95ea\u907f\uff0c\u4e58\u52bf\u4ee5\u201c\u5927\u91d1\u521a\u624b\u201d\u8361\u5f00\u4ed6\u7684\u62d0\u6756\uff0c\u6fc0\u6218\u4e2d\u5f7c\u6b64\u8ffd\u9010\uff0c\u8428\u5929\u90fd\u56fa\u7136\u89c9\u5f97\u4ed6\u7684 \u62d0\u529b\u975e\u6bd4\u5bfb\u5e38\uff0c\u7384\u98ce\u9053\u957f\u4e5f\u89c9\u4ed6\u7684\u638c\u529b\u6709\u5982\u96f7\u9707\uff0c\u53cc\u65b9\u90fd\u4e0d\u6562\u7a0d\u5b58\u8f7b\u89c6\u4e4b\u5fc3\u3002\u6fc0\u6218\u4e2d\u7384\u98ce\u8fd0 \u8db3\u5185\u65b9\uff0c\u547c\u547c\u6570\u62d0\uff0c\u8428\u5929\u90fd\u864e\u8df3\u907f\u5f00\uff0c\u7384\u98ce\u4e00\u62d0\u6253\u4e2d\u65c1\u8fb9\u7684\u5ca9\u77f3\uff0c\u77f3\u5c51\u7eb7\u98de\u4e2d\uff0c\u5ffd\u7136\u8dcc\u4e0b\u4e00 \u4eba\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u7384\u98ce\u9053\u957f\uff0c\u5feb\u6765\u6551\u6211\u3002\u201d \u3000\u3000\u8fd9\u4eba\u6b63\u662f\u5510\u6653\u6f9c\uff0c\u4ed6\u88ab\u8428\u5929\u523a\u70b9\u4e86\u9ebb\u7a74\uff0c\u653e\u5728\u4e24\u5757\u5c71\u5ca9\u4e0a\u4e0b\u5408\u76d6\u4e2d\u7a7a\u4e4b\u5904\uff0c\u4e0d\u80fd\u8f6c\u52a8\uff0c \u76ee\u7779\u8fd9\u573a\u60e8\u5217\u7684\u6076\u6218\uff0c\u76ee\u77aa\u53e3\u5446\uff0c\u6e10\u6e10\u53f1\u54a4\u8ffd\u9010\u4e4b\u58f0\u6e10\u8fd1\uff0c\u731b\u7136\u5ca9\u5d29\u77f3\u88c2\uff0c\u8eab\u53d7\u5de8\u9707\uff0c\u8840\u8109 \u5ffd\u7136\u7545\u901a\uff0c\u539f\u6765\u76f8\u5e94\u7684\u7a74\u9053\uff0c\u521a\u597d\u53d7\u9707\u89e3\u4e86\u3002 \u3000\u3000\u7384\u98ce\u53eb\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u5728\u77f3\u5c51\u6c99\u96fe\u4e2d\u8dc3\u8d77\uff0c\u5927\u58f0\u5e94\u9053\uff1a\u201c\u6211\u662f\u5468\u9752\u6536\u517b\u7684\u90a3\u4e2a\u5a03\u513f\uff0c\u201d\u7384\u98ce\u201c\u554a\u5440\u201d\u4e00 \u58f0\uff0c\u547c\u547c\u6570\u62d0\uff0c\u628a\u8428\u5929\u90fd\u903c\u9000\u6570\u6b65\uff0c\u62a2\u4e0a\u524d\u6765\uff0c\u53eb\u9053\uff1a\u201c\u54e6\uff01\u539f\u6765\u4f60\u5728\u6b64\u5904\uff01\u201d\u5510\u6653\u6f9c\u5c06\u6e38 \u9f99\u5251\u4e00\u628a\u9012\u8fc7\uff0c\u53eb\u9053\uff1a\u201c\u9053\u957f\uff0c\u4f60\u4f7f\u8fd9\u628a\u5b9d\u5251\uff01\u201d \u3000\u3000\u5468\u9752\u5728\u8d74\u51af\u5e7f\u6f6e\u5341\u5e74\u65e7\u7ea6\u4e4b\u524d\uff0c\u66fe\u5230\u5173\u4e1c\u4f1a\u8fc7\u7384\u98ce\u9053\u957f\u3002\u5468\u9752\u4e09\u5341\u5e74\u6765\u4ea1\u547d\u6c5f\u6e56\uff0c\u5c61\u9022 \u51f6\u9669\uff0c\u6df1\u6015\u81ea\u5df1\u4e00\u671d\u4e0d\u4fdd\uff0c\u6240\u4ee5\u9884\u5148\u4fbf\u6258\u7384\u98ce\u9053\u957f\uff0c\u65e5\u540e\u5728\u6c5f\u6e56\u4e0a\u7167\u987e\u4ed6\u552f\u4e00\u7684\u7231\u5f92\uff0c\u5f53\u65f6 \u7384\u98ce\u95ee\u9053\uff1a\u201c\u5c11\u5e74\u76f8\u8c8c\u6613\u4e8e\u6539\u53d8\uff0c\u6211\u600e\u80fd\u51ed\u7740\u4f60\u6240\u8bf4\u7684\u5f62\u8c8c\uff0c\u8ba4\u51fa\u4ed6\u6765\uff1f\u201d\u5468\u9752\u6c89\u601d\u6709\u9877\uff0c \u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5bb9\u6613\uff0c\u4f60\u5c06\u6765\u82e5\u78b0\u89c1\u4f7f\u6e38\u9f99\u5251\u7684\u5c11\u5e74\uff0c\u5c31\u662f\u4ed6\u4e86\u3002\u201d\u7384\u98ce\u9053\u957f\u4e0e\u5468\u9752\u662f\u809d\u80c6\u76f8 \u4ea4\u3001\u4e24\u5fc3\u76f8\u7167\u7684\u597d\u53cb\uff0c\u5f53\u4e0b\u6168\u7136\u5e94\u8bfa\u4e0b\u6765\uff01 \u3000\u3000\u5176\u540e\u5468\u9752\u5728\u4eac\u5e08\u4e0e\u8840\u6ef4\u5b50\u6076\u6597\uff0c\u4f17\u5be1\u4e0d\u654c\uff0c\u4ece\u4eac\u5e08\u4e00\u76f4\u7ed9\u8ffd\u5230\u6cb3\u5357\u3002\u5468\u9752\u7ed9\u8ffd\u51fa\u4eac\u5e08 \u65f6\uff0c\u5173\u4e1c\u56db\u4fa0\u5df2\u6709\u6240\u95fb\uff0c\u7384\u98ce\u4e00\u6765\u4e3a\u6551\u8001\u53cb\uff0c\u4e8c\u6765\u4e5f\u60f3\u53c2\u52a0\u5317\u4e94\u7701\u8c6a\u6770\u4e4b\u4f1a\uff0c\u4e8e\u662f\u7ea6\u9f50\u56db\u4e2a \u5144\u5f1f\uff0c\u8ddf\u8e2a\u8ffd\u6765\uff0c\u90a3\u77e5\u521a\u5230\u5b5f\u6d25\uff0c\u5c31\u78b0\u5230\u4ece\u592a\u884c\u5c71\u9003\u547d\u51fa\u6765\u7684\u8c6a\u6770\uff0c\u77e5\u9053\u53cc\u9b54\u8086\u8650\uff0c\u8bb8\u591a\u597d \u53cb\u60e8\u906d\u6a2a\u6b7b\uff0c\u4e8b\u60c5\u7d27\u6025\uff0c\u4e8e\u662f\u5148\u8054\u8882\u4e0a\u5c71\uff0c\u9080\u6597\u53cc\u9b54\uff01\u53c8\u4e0d\u6599\u5728\u6fc0\u6218\u4e4b\u4e2d\uff0c\u5510\u6653\u6f9c\u7a81\u7136\u51fa\u73b0\uff01 \u3000\u3000\u7384\u98ce\u9053\u957f\u63a5\u8fc7\u5b9d\u5251\uff0c\u5f39\u4e86\u4e24\u5f39\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u5c71\u9e23\u8c37\u5e94\uff0c\u6b23\u7136\u8bf4\u9053\uff1a\u201c\u662f\u4e86\uff0c\u968f\u6211\u51b2\u51fa \u6765\u3002\u201d\u516b\u81c2\u795e\u9b54\u4e00\u8f6e\u6025\u653b\uff0c\u628a\u90ce\u6708\u7985\u5e08\u548c\u67f3\u5148\u5f00\u6740\u9000\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u7384\u98ce\u6076\u9053\uff0c\u4f60\u6562\u62a2\u6211\u7684 \u5f92\u513f\uff01\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u4f7f\u51fa\u732b\u9e70\u6251\u51fb\u7684\u51cc\u7a7a\u7edd\u6280\uff0c\u4e09\u4f0f\u4e09\u8d77\uff0c\u9661\u7136\u8dc3\u8d77\u4e09\u4e08\u591a\u9ad8\uff0c\u4f38\u51fa\u5341\u6307\u957f \u7532\uff0c\u5411\u7384\u98ce\u5f53\u5934\u6293\u4e0b\uff01\u7384\u98ce\u6e38\u9f99\u5251\u6a2a\u7a7a\u4e00\u8361\uff0c\u8428\u5929\u523a\u8eab\u5b50\u60ac\u7a7a\uff0c\u4e00\u4e2a\u56de\u65cb\uff0c\u5341\u6307\u4e00\u5c48\u4e00\u4f38\uff0c \u4ecd\u7136\u6293\u4e0b\uff0c\u4ed6\u7684\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u767e\u4e0d\u5931\u4e00\uff0c\u672c\u4ee5\u4e3a\u5df2\u907f\u8fc7\u957f\u5251\uff0c\u90a3\u77e5\u6e38\u9f99\u5251\u4e43\u662f\u5b9d\u7269\uff0c\u5251\u5c16 \u5149\u8292\u95ea\u52a8\uff0c\u968f\u7740\u4e00\u8361\u4e4b\u52bf\uff0c\u66b4\u957f\u534a\u5c3a\uff0c\u8428\u5929\u523a\u53f3\u624b\u4e94\u6307\u957f\u53ef\u76c8\u5c3a\u7684\u6307\u7532\uff0c\u7adf\u7ed9\u524a\u53bb\u4e00\u534a\u6709 \u591a\u3002\u8428\u5929\u523a\u6012\u543c\u4e00\u58f0\uff0c\u7ffb\u8eab\u843d\u5730\uff0c\u53f3\u624b\u4e00\u6293\u6293\u5411\u7384\u98ce\u5bf8\u5173\u5c3a\u5904\uff0c\u5de6\u624b\u4e00\u6293\u5374\u5411\u5510\u6653\u6f9c\u80a9\u5934\u6293 \u6765\uff0c\u7384\u98ce\u8fd0\u5251\u5982\u98ce\uff0c\u81ea\u4e0b\u53cd\u524a\uff0c\u8428\u5929\u523a\u53f3\u722a\u6025\u7f29\uff0c\u5de6\u722a\u5374\u6293\u4f4f\u4e86\u5510\u6653\u6f9c\u80a9\u5934\u3002\u7384\u98ce\u53f3\u8155\u500f \u7ffb\uff0c\u5176\u75be\u5982\u7535\uff0c\u6e38\u9f99\u5251\u201c\u91d1\u96d5\u5c55\u7fc5\u201d\uff0c\u6a2a\u622a\u8428\u5929\u523a\u624b\u8155\uff0c\u8428\u5929\u523a\u4f3c\u754f\u5b9d\u5251\u5389\u5bb3\uff0c\u8eab\u5b50\u65cb\u98ce\u4e00 \u8f6c\uff0c\u7384\u98ce\u7684\u957f\u5251\u5728\u4ed6\u80c1\u4e0b\u60a0\u7136\u7a7f\u8fc7\uff0c\u4ed6\u7684\u5de6\u722a\u4e5f\u81ea\u7136\u677e\u5f00\uff0c\u5510\u6653\u6f9c\u9762\u8272\u60e8\u767d\uff0c\u201c\u54ce\u54df\u201d\u4e00\u58f0 \u53eb\u4e86\u51fa\u6765\uff01 \u3000\u3000\u7384\u98ce\u5b9d\u5251\u5728\u624b\uff0c\u5251\u8d77\u5904\uff0c\u201c\u7389\u5973\u6295\u68ad\u201d\u201c\u91d1\u9e21\u593a\u7c9f\u201d\uff0c\u4e00\u8fde\u51e0\u62db\uff0c\u622a\u8170\u65a9\u808b\uff0c\u8428\u5929\u523a\u5c55 \u5f00\u732b\u9e70\u95ea\u51fb\u4e4b\u6280\uff0c\u907f\u62db\u8fdb\u62db\uff0c\u8428\u5929\u523a\u529f\u529b\u80dc\u8fc7\u7384\u98ce\uff0c\u66f4\u517c\u7384\u98ce\u5728\u53d7\u4f24\u4e4b\u540e\uff0c\u672c\u6765\u4e07\u96be\u62b5\u6321\uff0c \u4f46\u4ed6\u5b9d\u5251\u5728\u624b\uff0c\u65e0\u5f62\u4e2d\u5360\u4e86\u4fbf\u5b9c\uff0c\u8428\u5929\u523a\u64d2\u62ff\u4e4b\u65f6\uff0c\u603b\u8981\u907f\u5f00\u5251\u5c16\u5149\u8292\uff0c\u4e0d\u6562\u6b3a\u8eab\u76f4\u8fdb\u3002\u8fd9 \u65f6\u95f4\uff0c\u201c\u4e07\u91cc\u8ffd\u98ce\u201d\u67f3\u5148\u5f00\u5148\u81ea\u6251\u5230\uff0c\u5341\u6307\u94a2\u73af\u4e00\u51ff\uff0c\u4e0e\u7384\u98ce\u524d\u540e\u5939\u51fb\uff0c\u628a\u8428\u5929\u523a\u51cc\u5389\u653b\u52bf \u6321\u4f4f\uff0c\u7384\u98ce\u62d0\u4ea4\u53f3\u624b\uff0c\u6025\u5fd9\u6478\u51fa\u4e24\u5757\u836f\u997c\uff0c\u4e00\u5757\u629b\u7ed9\u5510\u6653\u6f9c\uff0c\u4e00\u5757\u7ed9\u81ea\u5df1\u56bc\u4e0b\uff0c\u5c31\u5728\u8fd9\u77ac\u606f \u95f4\uff0c\u67f3\u5148\u5f00\u53c8\u5df2\u7ed9\u8428\u5929\u523a\u903c\u8d70\uff01\u800c\u5927\u529b\u795e\u9b54\u8428\u5929\u90fd\u4e5f\u628a\u6717\u6708\u7985\u5e08\u548c\u9648\u5143\u9738\u903c\u5f97\u5de6\u5954\u53f3\u7a9c\uff0c\u7384 \u98ce\u4e00\u6025\uff01\u53ea\u89c9\u53f3\u8155\u9ebb\u75db\u6bd4\u524d\u66f4\u751a\uff0c\u5fc3\u80c3\u4f5c\u95f7\uff0c\u6c14\u5f80\u4e0a\u51b2\uff01 \u3000\u3000\u5510\u6653\u6f9c\u529f\u529b\u66f4\u4f4e\uff0c\u53d7\u4e86\u722a\u4f24\uff0c\u867d\u56bc\u89e3\u836f\u4e5f\u662f\u7981\u53d7\u4e0d\u4f4f\uff0c\u53ea\u89c9\u76ee\u7729\u5934\u6655\uff0c\u6447\u6447\u6b32\u5012\uff0c\u8fd9\u65f6 \u6717\u6708\u4e5f\u4e2d\u4e86\u4e00\u722a\uff0c\u7384\u98ce\u81ea\u601d\u56db\u4eba\u4e4b\u4e2d\uff0c\u5df2\u4f24\u5176\u4e09\uff0c\u518d\u6218\u4e0b\u53bb\uff0c\u5fc5\u8d25\u65e0\u7591\uff0c\u601d\u91cf\u65f6\uff0c\u8428\u5929\u523a\u53c8 \u5c55\u732b\u9e70\u6280\u6251\u51fb\uff0c\u51cc\u7a7a\u6293\u4e0b\uff0c\u8981\u5c06\u5510\u6653\u6f9c\u6293\u53bb\u3002\u7384\u98ce\u9053\u957f\u5927\u559d\u4e00\u58f0\uff0c\u6e38\u9f99\u5251\u81ea\u4e0a\u4e00\u64a9\uff0c\u8d81\u7740\u8428 \u5929\u523a\u56de\u65cb\u8eb2\u95ea\u4e4b\u9645\uff0c\u5de6\u624b\u94c1\u62d0\uff0c\u731b\u7136\u8131\u624b\u98de\u51fa\uff0c\u8fd9\u662f\u4ed6\u201c\u4f0f\u51db\u62d0\u201d\u6cd5\u4e2d\u6551\u6025\u7edd\u62db\uff0c\u540d\u4e3a\u201c\u767d \u8679\u8d2f\u65e5\u201d\uff0c\u8428\u5929\u523a\u8eab\u672a\u7740\u5730\uff0c\u6a2a\u638c\u4e00\u6321\uff0c\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u638c\u5fc3\u7adf\u7ed9\u62d0\u5934\u6253\u5165\u534a\u5bf8\uff0c\u5947\u75db\u6f88 \u9aa8\uff0c\u5728\u534a\u7a7a\u4e00\u4e2a\u5012\u7ffb\uff0c\u98de\u8eab\u5760\u5730\uff0c\u80cc\u4e0a\u7684\u51af\u745b\u53c8\u54ed\u8d77\u6765\uff0c\u54ed\u58f0\u4e5f\u5df2\u7ecf\u5636\u54d1\u4e86\uff01\u7384\u98ce\u9053\u957f\u6025\u5fd9 \u5c06\u5510\u6653\u6f9c\u4e00\u628a\u6293\u8d77\uff0c\u4ea4\u7ed9\u67f3\u5148\u5f00\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u4f60\u5e26\u4ed6\u9003\uff0c\u8d70\u897f\u5357\uff0c\u4e0a\u9099\u5c71\uff01\u201d\u67f3\u5148\u5f00\u597d\u751f\u5947 \u5f02\uff0c\u4e0d\u77e5\u4f55\u4ee5\u8981\u4e0a\u9099\u5c71\uff01\u4f46\u6b64\u9645\u90a3\u80fd\u591a\u95ee\uff0c\u80cc\u8d77\u5510\u6653\u6f9c\u4fbf\u8d70\uff0c\u7384\u98ce\u9053\u957f\u53c8\u53eb\u9053\uff1a\u201c\u5206\u6279\u8d70\uff0c \u4e0d\u5fc5\u7b49\u6211\u3002\u201d\u67f3\u5148\u5f00\u7d20\u6765\u656c\u670d\u5e08\u5144\uff0c\u4e0d\u53d1\u4e00\u8a00\uff0c\u65bd\u5c55\u201c\u4e07\u91cc\u8ffd\u98ce\u201d\u7edd\u6280\uff0c\u76f4\u5954\u4e0b\u5c71\uff0c\u7384\u98ce\u9053 \u957f\u4ed7\u5251\u6bbf\u540e\uff0c\u63a9\u62a4\u7740\u6717\u6708\u63b8\u5e08\u548c\u9648\u5143\u9738\uff0c\u4e5f\u4ece\u53e6\u4e00\u9762\u4e0b\u5c71\u3002 \u3000\u3000\u8428\u5929\u523a\u6577\u836f\u88f9\u4f24\uff0c\u5305\u624e\u597d\u65f6\uff0c\u56db\u4fa0\u5df2\u5206\u4e24\u8def\u9003\u53bb\uff0c\u8428\u5929\u523a\u76b1\u7709\u4e00\u60f3\uff0c\u5bf9\u8428\u5929\u90fd\u9053\uff1a\u201c\u4f60 \u53bb\u8ffd\u90a3\u725b\u9f3b\u5b50\uff0c\u6211\u53bb\u6349\u90a3\u5c0f\u4f19\u5b50\uff01\u201d\u8428\u5929\u523a\u867d\u7136\u6068\u6781\u7384\u98ce\uff0c\u4f46\u4ed6\u60f3\uff1a\u7384\u98ce\u8fd9\u8def\uff0c\u4e09\u4eba\u90fd\u5df2\u53d7 \u4f24\uff0c\u65e9\u665a\u6bd2\u53d1\uff0c\u8428\u5929\u90fd\u529f\u529b\u867d\u7136\u8f83\u4f4e\uff0c\u5374\u662f\u53ea\u53d7\u5916\u4f24\uff0c\u4ee5\u4e00\u654c\u4e09\u90fd\u53ef\u4ee5\uff1b\u800c\u67f3\u5148\u5f00\u5219\u8f7b\u529f\u5353 \u7edd\uff0c\u53eb\u8428\u5929\u90fd\u53bb\u8ffd\uff0c\u90a3\u662f\u7edd\u5bf9\u8ffd\u4ed6\u4e0d\u4e0a\uff0c\u800c\u4e14\u67f3\u5148\u5f00\u6240\u5e26\u8d70\u7684\u5510\u6653\u6f9c\uff0c\u53c8\u662f\u6b66\u6797\u7f55\u9047\u7684\u7f8e\u8d28 \u5f02\u624d\uff0c\u8428\u5929\u523a\u6536\u5f92\u4e4b\u5ff5\u5140\u81ea\u672a\u62bf\uff01 \u3000\u3000\u6309\u4e0b\u7384\u98ce\u9053\u957f\u8fd9\u8def\u4e0d\u8868\u3002\u4e14\u8bf4\u8428\u5929\u523a\u98de\u6b65\u4e0b\u5c71\uff0c\u76f4\u8ffd\u201c\u4e07\u91cc\u8ffd\u98ce\u201d\uff0c\u4e24\u4eba\u8f7b\u529f\uff0c\u6240\u5dee\u6709 \u9650\uff0c\u67f3\u5148\u5f00\u80cc\u4e0a\u80cc\u7684\u662f\u5927\u4eba\uff0c\u8428\u5929\u523a\u80cc\u7684\u5374\u662f\u5b69\u5b50\uff0c\u4e24\u76f8\u6bd4\u8f83\uff0c\u67f3\u5148\u5f00\u7a0d\u7a0d\u5403\u4e8f\uff0c\u4f46\u9976\u662f\u5982 \u6b64\uff0c\u67f3\u5148\u5f00\u5360\u4e86\u5148\u8d77\u6b65\u7684\u4fbf\u5b9c\uff0c\u8428\u5929\u523a\u8ffd\u4e86\u534a\u5929\uff0c\u8fd8\u662f\u672a\u80fd\u671b\u89c1\u4ed6\u7684\u80cc\u5f71\u3002 \u3000\u3000\u9099\u5c71\u5728\u6cb3\u5357\u897f\u90e8\uff0c\u662f\u79e6\u5cad\u5c71\u8109\u7684\u5317\u652f\uff0c\u8ddd\u79bb\u592a\u884c\u5c71\u56db\u4e94\u767e\u91cc\u3002\u7b2c\u4e8c\u65e5\u4e2d\u5348\uff0c\u67f3\u5148\u5f00\u5230\u4e86 \u65b0\u5b89\uff0c\u518d\u51fa\u53bb\u4fbf\u662f\u51fd\u8c37\u5173\uff0c\u9099\u5c71\u4e5f\u5df2\u7ecf\u5728\u671b\u4e86\u3002\u5510\u6653\u6f9c\u4f24\u53e3\u53d1\u4f5c\uff0c\u6bd2\u6c14\u653b\u5fc3\uff0c\u5230\u65b0\u5b89\u65f6\u5df2\u4e0d \u80fd\u8bf4\u8bdd\u3002\u67f3\u5148\u5f00\u80cc\u4ed6\u6295\u5bbf\u5ba2\u5e97\uff0c\u7ed9\u4ed6\u653e\u8840\u89e3\u6bd2\uff0c\u8fd9\u624d\u60a0\u60a0\u9192\u8f6c\u3002\u67f3\u5148\u5f00\u672c\u60f3\u5728\u8fd9\u5ba2\u5e97\u4e2d\u6682\u907f \u98ce\u5934\uff0c\u5f85\u8428\u5929\u523a\u8ffd\u8fc7\u4e4b\u540e\u518d\u884c\u9732\u9762\uff0c\u4e0d\u6599\u508d\u665a\u65f6\u5206\uff0c\u5ffd\u542c\u5f97\u5ba2\u5e97\u5916\u9762\u4e00\u9635\u5b69\u5b50\u7684\u54ed\u58f0\uff0c\u63ed\u5e18 \u4e00\u770b\uff0c\u7adf\u662f\u516b\u81c2\u795e\u9b54\u5728\u5916\u9762\u5582\u51af\u745b\u5403\u7ca5\uff0c\u539f\u6765\u8fd9\u4e2a\u9b54\u5934\u4e5f\u6295\u5165\u4e86\u8fd9\u95f4\u5ba2\u5e97\u3002\u67f3\u5148\u5f00\u521a\u4e00\u9732 \u9762\uff0c\u8428\u5929\u523a\u5df2\u7ecf\u77a7\u89c1\u3002\u628a\u51af\u745b\u80cc\u597d\uff0c\u5927\u6b65\u8d70\u6765\uff0c\u67f3\u5148\u5f00\u5728\u623f\u4e2d\u6293\u8d77\u5510\u6653\u6f9c\uff0c\u4e00\u638c\u6253\u788e\u697c\u683c\uff0c \u7834\u7a97\u9003\u9038\uff0c\u8428\u5929\u523a\u8e22\u5f00\u623f\u95e8\uff0c\u4e5f\u8ddf\u7740\u7a7f\u51fa\u540e\u7a97\uff0c\u5ba2\u5e97\u4e3b\u4eba\u5728\u80cc\u540e\u5927\u558a\u201c\u6349\u8d3c\u201d\uff01\u8fd9\u4e24\u4eba\u65e9\u5df2 \u7ecf\u5230\u8857\u5916\u4e86\uff01 \u3000\u3000\u65b0\u5b89\u662f\u4e2a\u5c0f\u9547\uff0c\u4f46\u5929\u8fd8\u672a\u9ed1\uff0c\u8857\u53e3\u4e5f\u4e0d\u4e4f\u884c\u4eba\u3002\u4e24\u4eba\u957f\u8857\u8ffd\u9010\uff0c\u8857\u4e0a\u767b\u65f6\u5927\u4e71\uff0c\u8428\u5929\u523a \u5fc3\u6025\u5982\u711a\uff0c\u628a\u8857\u4e0a\u884c\u4eba\u7eb7\u7eb7\u649e\u8dcc\uff0c\u8fd9\u6837\u4e00\u95f9\uff0c\u67f3\u5148\u5f00\u53c8\u5df2\u9003\u51fa\u90ca\u5916\u3002\u8428\u5929\u523a\u6c14\u6781\uff0c\u5c55\u5f00\u72ec\u95e8 \u8f7b\u529f\uff0c\u8ffd\u9010\u67f3\u5148\u5f00\u80cc\u5f71\uff0c\u5728\u80cc\u540e\u5927\u58f0\u9a82\u9053\uff1a\u201c\u4f60\u9003\u5230\u5929\u8fb9\uff0c\u8001\u5b50\u4e5f\u8981\u628a\u4f60\u638f\u51fa\u6765\uff01\u201d\u67f3\u5148\u5f00 \u95f7\u58f0\u4e0d\u54cd\uff0c\u65bd\u5c55\u201c\u8ffd\u98ce\u201d\u7edd\u6280\uff0c\u4e00\u8def\u98de\u5954\uff0c\u8fc7\u4e86\u51e0\u5929\uff0c\u53c8\u628a\u516b\u81c2\u795e\u9b54\u8fdc\u8fdc\u7529\u5728\u540e\u9762\u3002 \u3000\u3000\u9ec4\u660f\u65e5\u843d\uff0c\u5c71\u95f4\u660e\u6708\u5347\u8d77\u3002\u67f3\u5148\u5f00\u542c\u5f97\u8fdc\u5904\u6c34\u58f0\u8f70\u9e23\uff0c\u6ce2\u6d9b\u62cd\u5cb8\uff0c\u77e5\u9053\u5df2\u5230\u4e86\u9ec4\u6cb3\u4e4b \u8fb9\u3002\u5d24\u5c71\u3001\u9099\u5c71\u903c\u8fd1\u9ec4\u6cb3\uff0c\u4e24\u5c71\u6a2a\u5c55\uff0c\u4e92\u4e3a\u7284\u89d2\uff0c\u53e4\u79f0\u5d0e\u51fd\u5929\u9669\u3002\u67f3\u5148\u5f00\u62ac\u5934\u4e00\u671b\uff0c\u9099\u5c71\u5df2 \u77d7\u7acb\u9762\u524d\uff0c\u4e24\u5cf0\u5939\u6301\uff0c\u5ced\u58c1\u9661\u7acb\uff0c\u5c71\u7684\u5357\u9762\uff0c\u4fbf\u662f\u9ec4\u6cb3\u3002\u67f3\u5148\u5f00\u5fc3\u91cc\u6697\u559c\uff0c\u8fd9\u5c71\u5982\u6b64\u9669\u5cfb\uff0c \u8428\u5929\u523a\u8f7b\u529f\u4e0d\u53ca\u81ea\u5df1\uff0c\u4e0a\u5f97\u5c71\u6765\uff0c\u81ea\u5df1\u5df2\u7ecf\u7ffb\u8fc7\u5c71\u7684\u90a3\u8fb9\u4e86\u3002 \u3000\u3000\u67f3\u5148\u5f00\u722c\u4e0a\u4e1c\u9762\u4e3b\u5cf0\uff0c\u8d8a\u5165\u4e1b\u6df1\uff0c\u4e00\u5904\u5904\u4e1b\u83bd\u5bc6\u83c1\uff0c\u8346\u68d8\u6ee1\u9053\uff0c\u5939\u6742\u7740\u4e0d\u6210\u884c\u5217\u7684\u6986\u67f3 \u6768\u69d0\u4e4b\u7c7b\u7684\u6811\u6728\uff0c\u67f3\u5148\u5f00\u8e51\u8db3\u6f5c\u8e2a\uff0c\u53c8\u8d70\u4e86\u4e00\u9635\uff0c\u524d\u9762\u9ed1\u538b\u538b\u73b0\u51fa\u4e00\u7247\u5371\u5d16\uff0c\u5cf0\u5ce8\u7a81\u5140\uff0c\u67f3 \u5148\u5f00\u80cc\u7740\u5927\u4eba\uff0c\u597d\u4e0d\u5bb9\u6613\u501f\u7740\u661f\u6708\u4e4b\u5149\uff0c\u62e3\u62e9\u90a3\u51f9\u51f8\u4e0d\u5e73\u7684\u5730\u65b9\u7740\u8db3\uff0c\u8f7b\u767b\u5371\u77f3\uff0c\u5de7\u7740\u6500 \u63f4\uff0c\u5347\u5230\u4e03\u516b\u4e08\u5904\u3002\u5230\u4e86\u4e0a\u9762\uff0c\u53ea\u89c1\u5904\u5904\u602a\u77f3\u5947\u5ca9\uff0c\u5728\u9ed1\u591c\u4e2d\u770b\u7740\uff0c\u66f4\u89c9\u9634\u68ee\u53ef\u6016\u3002\u67f3\u5148\u5f00 \u805a\u62e2\u76ee\u5149\uff0c\u56db\u4e0b\u8fa8\u4e86\u8fa8\u5f62\u52bf\uff0c\u9042\u4ece\u90a3\u4e71\u5c71\u76d8\u77f3\u95f4\uff0c\u5f80\u91cc\u7a7f\u884c\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u9635\uff0c\u773c\u775b\u5ffd\u7136\u4e00\u4eae\uff0c\u524d\u9762\u5730\u52bf\u5f00\u65f7\uff0c\u5f62\u6210\u4e00\u4e2a\u5728\u5c71\u5cf0\u56f4\u7ed5\u4e0b\u7684\u5c0f\u5c71\u8c37\uff0c\u4fa7\u9762\u5c71\u5cf0 \u6302\u4e0b\u4e00\u6761\u7011\u5e03\uff0c\u5c71\u6cc9\u98de\u7011\uff0c\u5728\u6708\u5149\u4e0b\u5982\u73cd\u73e0\u56db\u6e85\uff0c\u666f\u8272\u6e05\u7edd\u3002\u67f3\u5148\u5f00\u65e0\u6687\u6b23\u8d4f\uff0c\u6b63\u62df\u6a2a\u8fc7\u5c71 \u8c37\uff0c\u6500\u5347\u5cf0\u5dc5\uff0c\u6d41\u6cc9\u98de\u7011\u4e4b\u65c1\uff0c\u5ffd\u7136\u5189\u5189\u5347\u8d77\u4e00\u4eba\uff0c\u67f3\u5148\u5f00\u4e00\u770b\uff0c\u60ca\u5f97\u5446\u4e86\uff01 \u3000\u3000\u8fd9\u662f\u4e00\u4e2a\u5bb9\u989c\u8273\u7eda\u7684\u5c11\u5973\uff0c\u74dc\u5b50\u8138\u513f\uff0c\u5927\u5927\u7684\u773c\u775b\uff0c\u957f\u7709\u5982\u753b\uff0c\u663e\u5f97\u5341\u5206\u79c0\u6c14\uff0c\u67f3\u5148\u5f00 \u7edd\u6599\u4e0d\u5230\u5728\u8fd9\u6837\u9669\u5cfb\u7684\u5c71\u4e2d\uff0c\u4f1a\u85cf\u6709\u5982\u6b64\u4f73\u4e3d\u3002\u90a3\u5c11\u5973\u8f7b\u542f\u6731\u5507\uff0c\u67d4\u58f0\u95ee\u9053\uff1a\u201c\u5ba2\u4eba\uff0c\u8fd9\u6837 \u665a\u4f60\u4e0a\u5c71\u6765\u505a\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u67f3\u5148\u5f00\u5f3a\u6444\u5fc3\u795e\uff0c\u66fc\u58f0\u5e94\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4e0d\u5fc5\u7ba1\u6211\uff01\u201d\u8fd9\u5c11\u5973\u62bf\u5634\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u504f \u7231\u591a\u7ba1\u95f2\u4e8b\uff01\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7eb5\u8eab\u4e00\u8dc3\uff0c\u5c71\u98ce\u5439\u9001\uff0c\u8863\u8882\u8f7b\u98d8\uff0c\u76f4\u5982\u59d1\u5c04\u4ed9\u4eba\uff0c\u51cc\u7a7a\u98de\u964d\uff0c\u7adf \u7136\u906e\u5728\u67f3\u5148\u5f00\u9762\u524d\u3002\u8fd9\u4efd\u8f7b\u529f\uff0c\u8d85\u51e1\u7edd\u4fd7\uff0c\u67f3\u5148\u5f00\u53f7\u79f0\u201c\u4e07\u91cc\u8ffd\u98ce\u201d\u4e5f\u4e0d\u7981\u6697\u6697\u53f9\u670d\uff01 \u3000\u3000\u67f3\u5148\u5f00\u5408\u638c\u4e00\u63d6\uff0c\u53c8\u9053\uff1a\u201c\u6211\u77e5\u59d1\u5a18\u6b66\u529f\u7edd\u4e16\uff0c\u8bf7\u4e0d\u8981\u4e3a\u96be\u6211\u8fd9\u4ea1\u547d\u4e4b\u4eba\uff01\u201d\u5c11\u5973\u53cc\u7738 \u4e00\u8f6c\uff0c\u79cb\u6c34\u6a2a\u6ce2\uff0c\u4e0d\u80fd\u903c\u89c6\uff0c\u8be7\u7136\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u4ea1\u547d\u4e4b\u4eba\uff1f\u4f60\u4e3a\u4f55\u4ea1\u547d\uff1f\u8bf7\u7ec6\u8bf4\u6765\uff01\u201d\u67f3\u5148 \u5f00\u7126\u6025\u5f02\u5e38\uff0c\u8bf4\u9053\uff1a\u201c\u654c\u4eba\u5c31\u8981\u8ffd\u6765\uff0c\u59d1\u5a18\uff0c\u4f60\u884c\u884c\u597d\uff0c\u653e\u6211\u8fc7\u53bb\u5427\uff01\u201d\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u4e0d \u884c\uff01\u201d\u8fdc\u8fdc\u4f20\u6765\u602a\u5578\u4e4b\u58f0\uff0c\u67f3\u5148\u5f00\u4e0d\u7981\u607c\u6068\u90a3\u5c11\u5973\u6b6a\u7f20\uff0c\u53cc\u8db3\u4e00\u70b9\uff0c\u5411\u659c\u4fa7\u98de\u63a0\u51fa\u53bb\uff0c\u90a3\u77e5 \u521a\u521a\u7740\u5730\uff0c\u90a3\u5c11\u5973\u53c8\u5df2\u662f\u76c8\u76c8\u4e00\u7b11\uff0c\u4f38\u624b\u62e6\u5728\u524d\u9762\uff01 \u3000\u3000\u67f3\u5148\u5f00\u53f7\u79f0\u201c\u4e07\u91cc\u8ffd\u98ce\u201d\u8f7b\u8eab\u529f\u592b\uff0c\u6280\u538b\u6b66\u6797\uff0c\u60f3\u4e0d\u5230\u7adf\u8f93\u7ed9\u8fd9\u4e2a\u5c11\u5973\uff0c\u5fc3\u91cc\u4e0d\u670d\uff0c\u98d8 \u8eab\u6025\u8d77\uff0c\u518d\u5f80\u4e1c\u9762\u63a0\u53bb\uff0c\u4e0d\u6599\u811a\u65b9\u7740\u5730\uff0c\u90a3\u5c11\u5973\u53c8\u5df2\u7ad9\u5728\u9762\u524d\uff0c\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u4f60\u80cc\u7740\u4eba\uff0c\u7eb5 \u8dc3\u4e0d\u4fbf\uff0c\u628a\u8fd9\u4e2a\u5927\u5c0f\u5b50\u653e\u4e0b\u6765\u5427\uff01\u201d\u67f3\u5148\u5f00\u5e73\u751f\u4ee5\u8f7b\u529f\u81ea\u8d1f\uff0c\u4e89\u80dc\u4e4b\u5ff5\uff0c\u6cb9\u7136\u800c\u751f\uff0c\u628a\u5510\u6653 \u6f9c\u5f80\u5730\u4e0a\u4e00\u653e\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u5e73\u5730\u98de\u8d77\uff0c\u5b9b\u5982\u51b2\u9704\u5927\u9e64\uff0c\u63a0\u4e0a\u5ced\u62d4\u7684\u5c71\u9686\uff0c\u8033\u9645\u5ffd\u542c\u5f97\u547c\u547c\u98ce \u54cd\uff0c\u4e00\u56e2\u767d\u5f71\u5728\u8eab\u7554\u63a0\u8fc7\uff0c\u4e0a\u5230\u5c71\u5934\uff0c\u4ecd\u662f\u90a3\u5c11\u5973\u62a2\u5148\u4e00\u6b65\uff0c\u62e6\u5728\u524d\u5934\uff0c\u7389\u81c2\u4e00\u677e\u628a\u4e00\u4e2a\u4eba \u4ece\u80cc\u4e0a\u653e\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u5982\u4f55\uff1f\u201d\u5979\u7adf\u7136\u628a\u5510\u6653\u6f9c\u4ece\u5730\u4e0a\u80cc\u8d77\uff0c\u7136\u540e\u518d\u65bd\u5c55\u8f7b\u529f\uff0c\u67f3\u5148\u5f00\u72b9\u81ea \u8f93\u4e86\uff01\u7531\u4e0d\u5f97\u6c14\u6cae\u795e\u4f24\uff0c\u53f9\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff0c\u6211\u53ea\u9053\u8f7b\u529f\u76d6\u4e16\uff0c\u6599\u4e0d\u5230\u4e16\u4e0a\u8fd8\u6709\u5982\u6b64\u80fd \u4eba\uff01\u201d\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u7b97\u4e0d\u9519\u4e86\uff01\u201d\u5510\u6653\u6f9c\u6bd2\u4f24\u53d1\u4f5c\uff0c\u6d51\u8eab\u65e0\u529b\uff0c\u4f46\u4ecd\u6709\u77e5\u89c9\uff0c\u7ed9\u8fd9\u5c11 \u5973\u631f\u7740\u98de\u4e0a\u5c71\u5934\uff0c\u5c31\u5982\u817e\u4e91\u9a7e\u96fe\u4e00\u822c\uff0c\u7741\u5927\u4e24\u4e2a\u773c\u775b\uff0c\u6014\u6014\u7684\u770b\u7740\u90a3\u4e2a\u5c11\u5973\uff0c\u5c11\u5973\u5bb9\u5149\u903c \u4eba\uff0c\u5510\u6653\u6f9c\u4e0d\u7981\u53eb\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u4eba\u8fd8\u662f\u5c71\u7075\uff1f\u201d\u5c11\u5973\u5657\u55e4\u4e00\u7b11\uff0c\u5ffd\u7136\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48 \u4f24\u5f97\u8fd9\u6837\u91cd\u554a\uff01\u201d \u3000\u3000\u602a\u5578\u8d8a\u6765\u8d8a\u8fd1\uff0c\u67f3\u5148\u5f00\u8df3\u8d77\u6765\u9053\uff1a\u201c\u9b54\u5934\u6765\u4e86\uff0c\u5feb\u8ba9\u6211\u9003\u3002\u201d\u5c71\u8c37\u5916\u9ed1\u5f71\u8d8a\u6765\u8d8a\u5927\uff0c\u970e \u773c\u4e4b\u95f4\uff0c\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u5168\u8eab\u73b0\u51fa\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u67f3\u5148\u5f00\uff0c\u4f60\u9003\u5230\u5929\u8fb9\uff0c\u6211\u4e5f\u8ffd\u5230\u5929\u8fb9\uff01\u201d \u67f3\u5148\u5f00\u6025\u5fd9\u4f38\u624b\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5feb\u8ba9\u6211\u9003\uff01\u201d\u5c11\u5973\u5c06\u5510\u6653\u6f9c\u5f80\u65c1\u4e00\u5e26\uff0c\u95ee\u9053\uff1a\u201c\u662f\u4e0d\u662f\u90a3\u4e2a \u4eba\u5c06\u4f60\u6293\u4f24\u7684\uff1f\u201d\u5510\u6653\u6f9c\u6307\u7740\u516b\u81c2\u795e\u9b54\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u6b64\u4eba\uff01\u201d\u5c11\u5973\u6012\u9053\uff1a\u201c\u597d\uff0c\u6211\u66ff\u4f60\u523a\u4ed6 \u4e00\u5251\uff01\u201d\u5c06\u5510\u6653\u6f9c\u4ea4\u7ed9\u67f3\u5148\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u597d\u770b\u62a4\u7740\u4ed6\uff0c\u4e0d\u5fc5\u9003\u8d70\uff01\u201d\u7ea4\u8170\u4e00\u626d\uff0c\u8f7b\u98d8\u98d8\u7684 \u843d\u4e0b\u5c71\u5934\uff01 \u3000\u3000\u8428\u5929\u523a\u773c\u775b\u84e6\u7136\u4e00\u4eae\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u5973\u5a03\u5b50\u5feb\u5feb\u8eb2\u8fc7\u4e00\u65c1\uff0c\u6211\u4e0d\u4f24\u4f60\uff01\u201d\u8428\u5929\u523a \u6740\u4eba\u4e0d\u76b1\u773c\u7709\uff0c\u53ea\u56e0\u89c1\u8fd9\u5c11\u5973\u7f8e\u8273\u5f02\u5e38\uff0c\u7a0d\u5b58\u601c\u60dc\uff0c\u4e0d\u7136\uff0c\u65e9\u5acc\u5979\u788d\u624b\u788d\u811a\uff0c\u5c06\u5979\u4f24\u4e86\u3002\u5c11 \u5973\u7b11\u9053\uff1a\u201c\u600e\u89c1\u5f97\u4f60\u80fd\u4f24\u6211\uff1f\u201d\u8428\u5929\u523a\u98de\u8eab\u4e00\u63a0\uff0c\u6b63\u5f85\u8f7b\u8eab\u63d0\u6c14\u7331\u5347\u5c71\u5cf0\uff0c\u4e0d\u6599\u811a\u65b9\u7740\u5730\uff0c \u4e00\u4e2a\u5c11\u5973\u6e05\u8106\u7684\u58f0\u97f3\u5df2\u5728\u8033\u8fb9\u559d\u9053\uff1a\u201c\u4e0d\u51c6\u4e0a\u53bb\uff01\u201d \u3000\u3000\u8428\u5929\u523a\u6012\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8fd9\u662f\u81ea\u5df1\u9001\u547d\uff0c\u602a\u6211\u4e0d\u5f97\uff01\u201d\u5341\u722a\u4e00\u4f38\uff0c\u731b\u7684\u6293\u535c\uff0c\u5c11\u5973\u683c\u683c\u4e00 \u7b11\uff0c\u5fae\u98ce\u98d2\u7136\uff0c\u8eab\u5f71\u4e0d\u89c1\uff01\u8428\u5929\u523a\u5de6\u638c\u62a4\u8eab\uff0c\u53f3\u638c\u5bfb\u8eab\u4e00\u6293\uff0c\u5c11\u5973\u559d\u9053\uff1a\u201c\u597d\u6bd2\u7684\u62db\u6570\uff01\u201d \u9752\u5149\u4e00\u95ea\uff0c\u5b9d\u5251\u51fa\u624b\uff0c\u5530\u5530\u4e24\u5251\uff0c\u5206\u523a\u8428\u5929\u523a\u5370\u5802\u8981\u7a74\uff0c\u5251\u6cd5\u53c8\u51c6\u53c8\u5feb\uff0c\u4f3c\u4e4e\u8fd8\u5728\u7384\u98ce\u4e4b \u4e0a\uff0c\u8428\u5929\u523a\u609a\u7136\u4e00\u60ca\uff0c\u77e5\u662f\u52b2\u654c\uff0c\u559d\u58f0\u201c\u6765\u5f97\u597d\uff01\u201d\u659c\u95ea\u6b65\uff0c\u9aa4\u7ffb\u8eab\u3002\u7adf\u7528\u201c\u98ce\u5377\u843d\u82b1\u201d\u4e4b \u5f0f\uff0c\u8fde\u907f\u4e24\u5251\uff0c\u4ed6\u624b\u5e95\u4e5f\u4e0d\u6020\u6162\uff0c\u8d81\u5c11\u5973\u5251\u52bf\u65b9\u6536\uff0c\u5251\u62db\u672a\u53d8\u4e4b\u9645\u8ddf\u8e2a\u76f4\u8fdb\uff0c\u53f3\u638c\u4e00\u6258\u8098 \u5c16\uff0c\u5de6\u624b\u4e94\u6307\uff0c\u5df2\u6293\u5230\u5c11\u5973\u80c1\u4e0b\u3002\u770b\u5f97\u67f3\u5148\u5f00\u5fc3\u60ca\u80c6\u6218\uff01 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u7edd\u4ee3\u98ce\u534e\u5947\u5973\u5b50\uff0c\u53ea\u51ed\u4e00\u5251\u6597\u795e\u9b54\uff1b \u3000\u3000\u6b32\u77e5\u4e8c\u4eba\u80dc\u8d25\u5982\u4f55\uff0c\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e94\u56de\u3000\u94c1\u638c\u795e\u5f39\u3000\u6b66\u5e08\u4f20\u7edd\u6280\u3000\u7eb5\u60c5\u4f7f\u6c14\u3000\u7389\u5973\u8086\u5a07\u55d4 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e94\u56de \u94c1\u638c\u795e\u5f39\u3000\u6b66\u5e08\u4f20\u7edd\u6280 \u7eb5\u60c5\u4f7f\u6c14\u3000\u7389\u5973\u8086\u5a07\u55d4 \u3000\u3000\u5341\u6307\u5982\u94a9\uff0c\u5251\u5149\u4f3c\u7ec3\uff0c\u67f3\u5148\u5f00\u5206\u660e\u770b\u89c1\u516b\u81c2\u795e\u9b54\u5df2\u6293\u5230\u5c11\u5973\u80c1\u4e0b\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5374\u7a81\u7136\u602a \u543c\u4e00\u58f0\uff0c\u62d4\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u659c\u63a0\u51fa\u53bb\u3002\u770b\u4ed6\u8eab\u6cd5\u7cbe\u7eaf\uff0c\u4e0d\u4f3c\u53d7\u4f24\uff0c\u731c\u4e0d\u900f\u4ed6\u4f55\u4ee5\u5373\u5c06\u5f97\u624b\uff0c\u5374 \u53c8\u4ed3\u7687\u64a4\u8d70\uff1f\u518d\u770b\u90a3\u5c11\u5973\u65f6\uff0c\u53ea\u89c1\u5979\u5de6\u624b\u634f\u7740\u5251\u8bc0\uff0c\u53f3\u624b\u5b9d\u5251\u659c\u6307\u524d\u65b9\uff0c\u9762\u8272\u51dd\u91cd\uff0c\u76ee\u4e0d\u8f6c \u6674\u7684\u6ce8\u89c6\u7740\u8428\u5929\u523a\uff0c\u663e\u89c1\u4e5f\u662f\u5341\u5206\u7d27\u5f20\u3002\u8428\u5929\u523a\u53cc\u81c2\u7b95\u5f20\uff0c\u773c\u4f3c\u94dc\u94c3\uff0c\u4e0e\u90a3\u5c11\u5973\u9762\u5bf9\u7740\u9762\uff0c \u8c01\u90fd\u4e0d\u6562\u632a\u52a8\u811a\u6b65\u3002\u67f3\u5148\u5f00\u770b\u5f97\u5341\u5206\u7eb3\u7f55\u3002 \u3000\u3000\u67f3\u5148\u5f00\u4e0d\u77e5\uff0c\u5c31\u5728\u521a\u624d\u90a3\u95ea\u7535\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u4ea4\u6362\u4e86\u51e0\u62db\uff0c\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u4e00\u722a\u6293\u53bb\uff0c\u628a \u5bf9\u65b9\u95ea\u907f\u7684\u901f\u5ea6\u90fd\u5df2\u8ba1\u7b97\u5728\u5185\uff0c\u7b97\u51c6\u8fd9\u4e00\u6293\u5b9a\u53ef\u6293\u7740\uff0c\u90a3\u77e5\u6307\u7532\u5fae\u4e00\u6cbe\u88f3\uff0c\u5c11\u5973\u811a\u6b65\u4e0d\u52a8\uff0c \u4e00\u4e2a\u541e\u80f8\u5438\u8179\uff0c\u9165\u80f8\u51f9\u4e86\u534a\u5bf8\u3002\u5047\u5982\u8428\u5929\u523a\u7684\u6307\u7532\u80fd\u518d\u957f\u534a\u5bf8\uff0c\u4fbf\u53ef\u529b\u8d2f\u6307\u5c16\uff0c\u628a\u90a3\u5c11\u5973\u7684 \u80f8\u819b\u6495\u5f00\uff0c\u4f46\u8428\u5929\u523a\u7684\u957f\u81c2\u4e1a\u5df2\u653e\u5c3d\uff0c\u65e0\u80fd\u4e3a\u529b\uff0c\u5c31\u5dee\u4e86\u8fd9\u4e48\u534a\u5bf8\uff0c\u5c11\u5973\u7684\u5251\u950b\u5df2\u662f\u659c\u524a\u8fc7 \u6765\u3002\u8428\u5929\u523a\u5934\u9876\u4e00\u51c9\uff0c\u8d76\u5fd9\u53cc\u81c2\u786c\u5f80\u4e0b\u6c89\uff0c\u5934\u4ef0\u8098\u7ffb\uff0c\u653b\u5b88\u517c\u65bd\uff0c\u4ee4\u90a3\u5c11\u5973\u65e0\u6687\u518d\u523a\uff0c\u8fd9\u624d \u5f97\u4ee5\u5251\u5e95\u9003\u751f\uff0c\u4f46\u9976\u662f\u5982\u6b64\uff0c\u5934\u9876\u4e71\u83b2\u84ec\u7684\u957f\u53d1\u5df2\u7ed9\u5251\u950b\u524a\u53bb\u4e00\u7efa\uff01 \u3000\u3000\u8fd9\u5c11\u5973\u4e5f\u6697\u81ea\u60ca\u5f02\uff0c\u5979\u7684\u7384\u5973\u5251\u6cd5\uff0c\u81ea\u4fe1\u5df2\u7ec3\u5230\u51fa\u795e\u5165\u5316\uff0c\u4e0d\u6599\u4ecd\u7ed9\u654c\u4eba\u9003\u8131\u3002\u5fc3\u9053\uff1a \u516b\u81c2\u795e\u9b54\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u602a\u4e0d\u5f97\u5e08\u5085\u53eb\u6211\u5c0f\u5fc3\u5728\u610f\uff01 \u3000\u3000\u4e24\u4eba\u5168\u795e\u8d2f\u6ce8\uff0c\u4f3a\u673a\u88ad\u654c\uff0c\u53cc\u65b9\u90fd\u4e0d\u6562\u5148\u884c\u53d1\u96be\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u5c11\u5973\u5ffd\u7136\u5657\u55e4\u4e00\u7b11\uff0c\u53eb \u9053\uff1a\u201c\u518d\u6765\u597d\uff01\u201d\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u8428\u5929\u523a\u8eab\u5f62\u9aa4\u8d77\uff0c\u5341\u6307\u51cc\u7a7a\u6293\u4e0b\uff0c\u4ed6\u662f\u60f3\u8d81\u8fd9\u5c11\u5973\u4e00\u7b11\u5206 \u5fc3\uff0c\u628a\u5979\u51fb\u5012\u3002\u90a3\u6599\u5c11\u5973\u662f\u5f15\u4ed6\u5148\u53d1\uff0c\u65e9\u6709\u9632\u5907\uff0c\u5b9d\u5251\u4e00\u6296\uff0c\u5728\u5934\u9876\u4e0a\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\uff0c\u91d1\u5200 \u631f\u98ce\uff0c\u4e00\u51b2\u4e00\u7ede\uff0c\u89e3\u62db\u8fd8\u62db\u3002\u8428\u5929\u523a\u8eab\u5f62\u6025\u8f6c\uff0c\u5de6\u638c\u53d8\u6293\u4e3a\u62ff\uff0c\u53cc\u65b9\u6362\u4e86\u4e00\u62db\uff0c\u8428\u5929\u523a\u811a\u8e0f \u5b9e\u5730\uff0c\u653b\u52bf\u53d1\u52a8\uff0c\u6251\u51fb\u51cc\u5389\uff0c\u5176\u4e2d\u53c8\u5939\u4ee5\u6293\u88c2\u3001\u70b9\u6253\u4e4b\u6cd5\uff0c\u5341\u6307\u957f\u7532\u5c31\u5982\u5229\u5203\u4e00\u822c\uff0c\u4e00\u6d3e\u51f6 \u72c2\u4e4b\u52bf\uff0c\u624b\u811a\u8d77\u5904\uff0c\u5168\u5e26\u52b2\u98ce\uff01\u6bd4\u5927\u6218\u5173\u4e1c\u56db\u4fa0\u4e4b\u65f6\uff0c\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\uff01\u67f3\u5148\u5f00\u770b\u5f97\u6697\u6697\u60ca \u5fc3\uff0c\u90a3\u5c11\u5973\u5374\u662f\u6c14\u5b9a\u795e\u95f2\uff0c\u5251\u5149\u95ea\u95ea\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u7fe9\u5982\u60ca\u9e3f\u3002\u6218\u5230\u5206\u9645\uff0c\u76c8\u76c8\u4e00\u7b11\uff0c\u5251\u62db\u500f \u53d8\uff0c\u5bd2\u5149\u56db\u5c04\uff0c\u5ffd\u805a\u5ffd\u6563\uff0c\u770b\u6765\u6beb\u4e0d\u51f6\u72e0\uff0c\u5374\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u6781\u5f97\u8f7b\u7075\u7fd4\u52a8\u4e4b\u5999\uff01\u539f\u6765\u90a3\u5c11\u5973 \u662f\u5f15\u4ed6\u5148\u53d1\uff0c\u7136\u540e\u632b\u5176\u9510\u6c14\uff0c\u51fb\u5176\u66ae\u5f52\uff0c\u8428\u5929\u523a\u7ed9\u88f9\u5728\u5251\u5149\u4e4b\u4e2d\uff0c\u62fc\u547d\u820d\u6597\uff01\u67f3\u5148\u5f00\u8fd9\u624d\u5598 \u4e86\u4e00\u53e3\u5927\u6c14\uff0c\u5b9a\u4e86\u5fc3\u795e\u3002\u6697\u60f3\uff1a\u5927\u54e5\u7684\u5251\u6cd5\u5df2\u662f\u7f55\u89c1\u7684\u7edd\u6280\u4e86\uff0c\u8fd9\u5c11\u5973\u53c8\u4f3c\u4e4e\u8fd8\u5728\u5927\u54e5\u4e4b \u4e0a\uff0c\u771f\u4e0d\u77e5\u5979\u8fd9\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5982\u4f55\u7ec3\u5f97\u5230\u8fd9\u79cd\u5730\u6b65\uff1f\u5fc3\u5ff5\u4e00\u52a8\uff1a\u5927\u54e5\u53eb\u6211\u907f\u4e0a\u9099\u5c71\uff0c\u83ab\u975e\u4e0e\u6b64 \u5c11\u5973\u6709\u5173\u3002\u6b63\u601d\u91cf\u95f4\uff0c\u8fdc\u5904\u53c8\u662f\u4e00\u58f0\u602a\u5578\uff01 \u3000\u3000\u67f3\u5148\u5f00\u542c\u5f97\u5206\u660e\uff0c\u8fd9\u5578\u58f0\u7ade\u662f\u5927\u529b\u795e\u9b54\u8428\u5929\u90fd\u6240\u53d1\uff0c\u6697\u6697\u7740\u6025\u3002\u731b\u542c\u5f97\u90a3\u5c11\u5973\u53eb\u58f0\uff1a \u201c\u7740\uff01\u201d\u51dd\u7738\u770b\u65f6\uff0c\u8fd9\u4e00\u573a\u60ca\u5fc3\u9a87\u76ee\u7684\u6076\u6597\u5df2\u5206\u51fa\u80dc\u8d25\uff01\u8428\u5929\u523a\u80a9\u5934\u7ea2\u4e86\u4e00\u7247\uff0c\u8df3\u51fa\u573a\u5fc3\u3002 \u5510\u6653\u6f9c\u5728\u65c1\u5636\u58f0\u558a\u9053\uff1a\u201c\u53eb\u4ed6\u628a\u80cc\u4e0a\u7684\u5b69\u5b50\u653e\u4e0b\u6765\uff01\u201d\u558a\u58f0\u672a\u505c\uff0c\u8428\u5929\u90fd\u5df2\u5728\u5c71\u5761\u4e0a\u73b0\u51fa\u8eab \u5f62\u3002 \u3000\u3000\u8428\u5929\u523a\u4e00\u89c1\u5f1f\u5f1f\u6765\u5230\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u5341\u6307\u98de\u626c\uff0c\u8d1f\u4f24\u518d\u6251\uff0c\u8428\u5929\u90fd\u9762\u76ee\u9752\u80bf\uff0c\u864e\u543c\u4e00\u58f0\uff0c \u6325\u62f3\u6025\u4e0a\u52a9\u653b\u3002\u5c11\u5973\u5b9d\u5251\u4e00\u6307\uff0c\u76f4\u5954\u8428\u5929\u90fd\u6740\u6765\uff0c\u8428\u5929\u523a\u5229\u722a\u5411\u4e0b\u4e00\u63a2\uff0c\u6293\u5411\u5c11\u5973\u7684\u201c\u8840\u6d77 \u7a74\u201d\uff0c\u5c11\u5973\u5251\u6cd5\u8fc5\u6377\u4e4b\u6781\uff0c\u5251\u950b\u4e00\u98a4\u5df2\u628a\u8428\u5929\u90fd\u5212\u4e00\u9053\u4f24\u53e3\u3002\u8428\u5929\u523a\u7684\u6307\u7532\u65b9\u81ea\u6cbe\u8863\uff0c\u90a3\u5c11 \u5973\u8eab\u5f62\u4e00\u95ea\uff0c\u4e09\u5c3a\u9752\u950b\u731b\u7529\u56de\u6765\uff0c\u201c\u4e4c\u9f99\u5377\u5c3e\u201d\u53cd\u5411\u8428\u5929\u523a\u53cc\u817f\u5377\u53bb\u3002\u8428\u5929\u523a\u53cc\u81c2\u4e00\u6296\uff0c\u8eab \u5f62\u6273\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u7528\u91cd\u624b\u6cd5\u6253\u5979\uff01\u201d\u8428\u5929\u90fd\u8fd9\u4e24\u4e0b\u8fde\u53d7\u521b\u4f24\uff0c\u5927\u6012\u82e5\u72c2\uff0c\u53cc\u638c\u7ffb\u817e\uff0c\u8fde\u73af\u731b \u626b\uff0c\u76f4\u5982\u5de8\u65a7\u5f00\u5c71\uff0c\u94c1\u9524\u51ff\u77f3\u3002\u5c11\u5973\u4e0d\u6562\u8fc7\u5206\u903c\u8fd1\uff0c\u5251\u5c16\u95ea\u52a8\uff0c\u4e58\u9699\u8fdb\u62db\u3002\u8428\u5929\u523a\u8eab\u624b\u8fc5 \u75be\uff0c\u5341\u6307\u957f\u5c39\uff0c\u53c8\u6293\u8fc7\u6765\uff0c\u53cc\u9b54\u5de6\u53f3\u5939\u653b\uff0c\u987f\u65f6\u95f4\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u628a\u5c11\u5973\u56f0\u5728\u6838\u5fc3\uff01 \u3000\u3000\u8428\u5929\u90fd\u529f\u529b\u539f\u81ea\u4e0d\u5f31\uff0c\u521a\u624d\u4e0d\u77e5\u654c\u4eba\u865a\u5b9e\uff0c\u8e81\u5984\u8fdb\u653b\uff0c\u5403\u4e86\u4e00\u5251\uff0c\u518d\u5ea6\u8fdb\u653b\uff0c\u5728\u54e5\u54e5\u63a9 \u62a4\u4e4b\u4e0b\uff0c\u8fd0\u201c\u91d1\u521a\u5927\u529b\u624b\u6cd5\u201d\u786c\u62a2\u5c11\u5973\u7684\u5b9d\u5251\uff0c\u638c\u98ce\u864e\u864e\uff0c\u638c\u98ce\u5251\u98ce\uff0c\u4e92\u76f8\u6fc0\u8361\uff0c\u5c11\u5973\u53d1 \u62db\uff0c\u53d7\u4e86\u5f71\u54cd\uff0c\u5251\u70b9\u843d\u5904\uff0c\u51c6\u786e\u5df2\u4e0d\u5982\u524d\u3002\u5f80\u5f80\u5728\u654c\u4eba\u8981\u7a74\u4e4b\u9645\uff0c\u53d7\u638c\u98ce\u4e00\u9707\uff0c\u504f\u51fa\u5c11\u8bb8\uff0c \u800c\u8428\u5929\u523a\u7684\u5229\u722a\u968f\u5373\u95ea\u7535\u653b\u6765\uff01\u8981\u77e5\u5c11\u5973\u5251\u6cd5\u80dc\u5728\u8f7b\u7075\u8fc5\u6377\uff0c\u62db\u6570\u5947\u5999\uff0c\u8bba\u529f\u529b\u5374\u6bd4\u8428\u5929\u523a \u8fd8\u5dee\u4e00\u7b79\uff0c\u8fd9\u756a\u53cc\u9b54\u5939\u51fb\uff0c\u5404\u6709\u72ec\u95e8\u6b66\u529f\uff0c\u76f8\u8f85\u76f8\u6210\uff0c\u5a01\u529b\u4f55\u6b62\u589e\u52a0\u4e00\u500d\uff0c\u5c11\u5973\u88ab\u8feb\u8f6c\u653b\u4e3a \u5b88\uff0c\u5251\u6cd5\u518d\u53d8\uff0c\u6d51\u8eab\u4e0a\u4e0b\uff0c\u4e00\u7247\u5bd2\u5149\u95ea\u95ea\u3002\u4e09\u4eba\u5728\u5c71\u77f3\u4e4b\u95f4\uff0c\u8fdb\u653b\u9000\u5b88\uff0c\u5de6\u53f3\u76d8\u65cb\uff0c\u53cc\u9b54\u867d \u5360\u4e0a\u98ce\uff0c\u5c11\u5973\u4e5f\u81ea\u4e0d\u5f31\uff0c\u53ea\u6253\u5f97\u4e2a\u96be\u89e3\u96be\u5206\uff01 \u3000\u3000\u67f3\u5148\u5f00\u7591\u795e\u89c2\u6218\uff0c\u6b63\u81ea\u7d27\u5f20\uff0c\u5ffd\u542c\u5f97\u8eab\u65c1\u5510\u6653\u6f9c\u75db\u82e6\u547b\u541f\uff0c\u53cc\u76ee\u534a\u5f00\u534a\u95ed\uff0c\u9762\u8272\u7600\u9ed1\uff0c \u6599\u662f\u4f53\u5185\u6bd2\u6c14\u5df2\u8513\u5ef6\u5f00\u6765\uff01\u67f3\u5148\u5f00\u8fd9\u4e00\u6025\u975e\u540c\u5c0f\u53ef\uff0c\u987e\u4e0d\u5f97\u518d\u770b\u573a\u4e2d\u6fc0\u6597\uff0c\u8d76\u5fd9\u628a\u5510\u6653\u6f9c\u80f8 \u8863\u6495\u5f00\uff0c\u7528\u788e\u77f3\u5212\u4e86\u4e00\u9053\u4f24\u53e3\uff0c\u7ed9\u4ed6\u653e\u8840\uff0c\u5510\u6653\u6f9c\u6c14\u606f\u5401\u5401\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u67f3\u5927\u4fa0\u4f60\u522b\u7ba1 \u6211\uff0c\u5feb\u9003\u547d\u5427\uff01\u201d\u67f3\u5148\u5f00\u9053\uff1a\u201c\u4e0d\u8981\u80e1\u60f3\uff0c\u4f60\u6b7b\u4e0d\u4e86\uff0c\u54b1\u4eec\u8d70\uff01\u201d\u628a\u5510\u6653\u6f9c\u631f\u8d77\uff0c\u6b63\u5f85\u4ece\u53e6 \u4e00\u9762\u4e0b\u5c71\uff0c\u731b\u542c\u5f97\u53cc\u9b54\u9ad8\u547c\u9163\u6597\u4e4b\u58f0\uff0c\u5c11\u5973\u5251\u6cd5\u5df2\u6e10\u6563\u4e71\uff0c\u67f3\u5148\u5f00\u4e0d\u89c9\u8e0c\u8e87\uff0c\u60f3\u5230\uff1a\u201c\u8fd9\u5c11 \u5973\u4e00\u7247\u597d\u5fc3\uff0c\u62d4\u5251\u76f8\u52a9\uff0c\u66ff\u81ea\u5df1\u6321\u7740\u53cc\u9b54\uff0c\u5982\u4f55\u597d\u820d\u5979\u800c\u53bb\uff1f\u201d\u5f85\u653e\u4e0b\u5510\u6653\u6f9c\u65f6\uff0c\u53c8\u89c1\u4ed6\u53cc \u76ee\u7d27\u95ed\uff0c\u8109\u606f\u751a\u7ec6\uff0c\u53ea\u6015\u81ea\u5df1\u82e5\u53bb\u52a8\u624b\u52a9\u90a3\u5c11\u5973\uff0c\u672a\u80fd\u53d6\u80dc\uff0c\u5510\u6653\u6f9c\u4ea6\u65e0\u6cd5\u6551\u6cbb\uff01\u4e00\u65f6\u6ca1\u4e86 \u4e3b\u610f\u3002\u53cc\u9b54\u8d8a\u6740\u8d8a\u51f6\uff0c\u547c\u58f0\u64bc\u5730\uff0c\u67f3\u5148\u5f00\u4e00\u54ac\u7259\u6839\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u4e49\u6c14\u4e3a\u5148\uff0c\u5b81\u6559\u8eab\u6b7b\uff0c \u4e0d\u6559\u540d\u706d\u3002\u8fd9\u4f4d\u5c0f\u54e5\uff0c\u4f46\u613f\u4f60\u5409\u4eba\u5929\u76f8\uff0c\u7edd\u5904\u9022\u751f\u3002\u201d\u628a\u5510\u6653\u6f9c\u5f80\u5730\u4e0b\u4e00\u653e\uff0c\u98de\u6b65\u4e0b\u5c71\u3002 \u3000\u3000\u521a\u8dd1\u5f97\u51e0\u6b65\uff0c\u5c71\u9876\u4e0a\u7a7a\u84e6\u5730\u4f20\u6765\u4e86\u5de8\u9e1f\u957f\u9e23\u4e4b\u58f0\uff0c\u4e1b\u6797\u8302\u8349\u4e4b\u95f4\uff0c\u5c71\u79bd\u4e71\u9e23\uff0c\u548c\u548c\u98de \u8d77\u3002\u67f3\u5148\u5f00\u4e0d\u89c9\u4e00\u6014\uff0c\u5fc3\u60f3\u662f\u4ec0\u4e48\u602a\u9e1f\u731b\u79bd\uff0c\u5982\u6b64\u58f0\u52bf\uff1f\u8f6c\u77ac\u95f4\u5934\u9876\u547c\u547c\u98ce\u54cd\uff0c\u4e24\u53ea\u5927\u9e4f\uff0c \u4e00\u9ed1\u4e00\u767d\uff0c\u53cc\u7fc5\u5f20\u5f00\uff0c\u7adf\u5982\u78e8\u76d8\u5927\u5c0f\uff0c\u75be\u98de\u800c\u8fc7\u3002\u53cc\u9b54\u5404\u81ea\u4e00\u58f0\u602a\u5578\uff0c\u8dc3\u51fa\u573a\u5fc3\u3002\u5c31\u5728\u6b64 \u9645\uff0c\u6d41\u6cc9\u98de\u7011\u4e4b\u65c1\uff0c\u84e6\u7136\u51fa\u73b0\u4e00\u4eba\uff0c\u7adf\u662f\u4e2a\u72ec\u81c2\u7684\u8001\u5c3c\uff01\u67f3\u5148\u5f00\u5168\u795e\u8d2f\u6ce8\u5927\u9e1f\uff0c\u6ca1\u6ce8\u610f\u5230\u8001 \u5c3c\u662f\u4ec0\u4e48\u65f6\u5019\u6765\u7684\u3002 \u3000\u3000\u72ec\u81c2\u8001\u5c3c\u98a4\u5dcd\u5dcd\u7684\u8d70\u4e86\u51e0\u6b65\uff0c\u626c\u58f0\u53eb\u9053\uff1a\u201c\u5f92\u513f\uff0c\u8fd8\u672a\u4e86\u7ed3\u4e48\uff1f\u201d\u53cc\u9b54\u84e6\u7136\u8f6c\u8eab\uff0c\u5982\u98de \u9003\u8dd1\uff0c\u6254\u4e0b\u8bdd\u9053\uff1a\u201c\u72ec\u81c2\u8001\u5c3c\uff0c\u6709\u80c6\u7684\u5c31\u5230\u732b\u9e70\u5c9b\u6765\u627e\u6211\u4eec\uff01\u201d\u8001\u5c3c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u53eb\u9053\uff1a \u201c\u4f60\u7b49\u7740\u5427\uff0c\u81ea\u7136\u6709\u4eba\u6311\u4f60\u8001\u5de2\uff0c\u73b0\u5728\u5148\u53eb\u4f60\u7559\u4e0b\u4e00\u70b9\u4e1c\u897f\uff01\u201d\u64ae\u5507\u4e00\u5439\uff0c\u4e00\u53cc\u5927\u9e4f\u75be\u98de\u800c \u53bb\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u53c8\u518d\u98de\u56de\uff0c\u843d\u5728\u72ec\u81c2\u8001\u5c3c\u53cc\u80a9\u4e0a\uff0c\u957f\u5599\u5220\u7740\u53cc\u9b54\u9876\u4e0a\u7684\u5934\u5dfe\u3002\u5c11\u5973\u7b11\u7740\u98de\u8dd1 \u4e0a\u6765\uff0c\u629a\u5f04\u4e24\u53ea\u5927\u9e4f\uff0c\u5ffd\u7136\u5658\u7740\u5634\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u53eb\u5c0f\u9ed1\u5c0f\u767d\u5544\u4ed6\u4eec\u4e00\u53e3\uff1f\u201d\u8001\u5c3c\u7b11\u9053\uff1a \u201c\u4f60\u4e5f\u66fe\u548c\u4ed6\u4eec\u8bd5\u4e86\u62db\u6765\uff0c\u96be\u9053\u8fd8\u4e0d\u77e5\u9053\u4ed6\u4eec\u6df1\u6d45\uff1f\u5c0f\u9ed1\u5c0f\u767d\u5982\u4f55\u4f24\u5f97\u4e86\u4ed6\u4eec\uff1f\u4ed6\u4eec\u662f\u6015\u6211 \u7684\u58f0\u5a01\uff0c\u4e0d\u6562\u548c\u5c0f\u9ed1\u5c0f\u767d\u7ea0\u7f20\uff0c\u731b\u4e0d\u9632\u624d\u7740\u4e86\u9053\u513f\uff01\u201d\u5c11\u5973\u53c8\u5a07\u7b11\u9053\uff1a\u201c\u5e08\u5085\uff0c\u6211\u7684\u5251\u6cd5\u600e \u6837\uff1f\u53ef\u4ee5\u51fa\u9053\u4e86\u5427\uff1f\u201d\u8001\u5c3c\u9053\uff1a\u201c\u4f60\u7684\u5251\u6cd5\u6bd4\u5e08\u5144\u4eec\u90fd\u5f3a\uff0c\u53ea\u662f\u4f60\u7684\u4ec7\u4eba\u6bd4\u53cc\u9b54\u4f55\u6b62\u5389\u5bb3\u5343 \u500d\u4e07\u500d\uff1f\u6211\u7684\u529f\u592b\u5df2\u503e\u56ca\u76f8\u6388\uff0c\u4f60\u73b0\u5728\u5df2\u6709\u4e86\u4e03\u516b\u6210\u706b\u5019\uff0c\u518d\u78e8\u7ec3\u51e0\u5e74\uff0c\u53cc\u9b54\u4e0d\u662f\u4f60\u7684\u5bf9 \u624b\uff01\u81f3\u4e8e\u80fd\u5426\u62a5\u4ec7\uff0c\u90a3\u5c31\u53ea\u80fd\u770b\u4f60\u7684\u8fd0\u6c14\u4e86\u3002\u201d\u8bf4\u7f62\u5411\u67f3\u5148\u5f00\u5904\u7f13\u7f13\u884c\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u5e08 \u5f92\u53ea\u7ba1\u8bf4\u95f2\u8bdd\uff0c\u53d9\u5bb6\u5e38\uff0c\u53ef\u628a\u8d35\u5ba2\u51b7\u843d\u4e86\uff01\u201d \u3000\u3000\u67f3\u5148\u5f00\u53c8\u60ca\u53c8\u559c\uff0c\u60f3\u4e0d\u5230\u7adf\u5728\u9099\u5c71\u4e4b\u4e0a\uff0c\u9047\u5230\u524d\u8f88\u795e\u5c3c\u3002\u8fd9\u72ec\u81c2\u8001\u5c3c\uff0c\u5251\u6cd5\u7cbe\u7edd\uff0c\u53ea\u662f \u6781\u5c11\u4e0e\u4eba\u4e89\u6597\uff0c\u8fd1\u4e09\u5341\u5e74\u6765\uff0c\u6ca1\u4eba\u77e5\u5979\u8e2a\u8ff9\u3002\u636e\u6b66\u6797\u524d\u8f88\u6240\u4f20\uff0c\u5979\u672c\u662f\u660e\u671d\u6700\u540e\u4e00\u4e2a\u7687\u5e1d\u5141 \u796f\u7687\u5e1d\u7684\u5e7c\u5973\uff0c\u540d\u53eb\u957f\u5e73\u516c\u4e3b\u3002\u95ef\u738b\u8fdb\u4eac\uff0c\u5d07\u796f\u5728\u7164\u5c71\u81ea\u7f22\uff0c\u4e34\u6b7b\u4e4b\u524d\uff0c\u62c5\u5fc3\u957f\u5e73\u516c\u4e3b\u53d7\u5230 \u4fae\u8fb1\uff0c\u7528\u5251\u65a9\u5979\uff0c\u65a9\u65ad\u4e00\u81c2\uff0c\u957f\u5e73\u516c\u4e3b\u5012\u5728\u8840\u6cca\u4e2d\u6323\u624e\u547c\u53f7\uff0c\u795f\u796f\u62e5\u5251\u53f9\u9053\uff1a\u201c\u8c01\u6559\u4f60\u751f\u5728 \u7687\u5bb6\uff01\u201d\u957f\u5e73\u516c\u4e3b\u624d\u5f97\u4ee5\u4fdd\u5168\u4e00\u547d\u3002\u540e\u6765\u95ef\u738b\u5165\u5bab\uff0c\u4f18\u793c\u660e\u5ba4\uff0c\u89c1\u957f\u5e73\u516c\u4e3b\u60e8\u72b6\uff0c\u53f9\u9053\uff1a \u201c\u4e0a\u4f55\u592a\u5fcd\uff1f\u201d\u53eb\u5bab\u5973\u6276\u5979\u56de\u5bab\u8c03\u517b\uff0c\u5e76\u8bf7\u5fa1\u533b\u7ed9\u5979\u6cbb\u7597\u3002\u6b64\u4e8b\u4e0d\u72ec\u89c1\u4e8e\u6b66\u6797\u4f20\u8bf4\uff0c\u800c\u4e14\u8f7d \u4e8e\u5386\u4ee3\u901a\u9274\u8f91\u89c8\uff0c\u8c05\u975e\u865a\u5047\u3002\u81f3\u4e8e\u540e\u6765\uff0c\u957f\u5e73\u516c\u4e3b\u600e\u6837\u51fa\u5bab\u5b66\u827a\uff0c\u72ec\u521b\u4e00\u5bb6\uff0c\u90a3\u5c31\u4eba\u8a00\u4eba \u6b8a\uff0c\u6f14\u4e3a\u8bb8\u591a\u795e\u8bdd\u6027\u7684\u4f20\u8bf4\u4e86\u3002 \u3000\u3000\u67f3\u5148\u5f00\u4e00\u7b97\u6e05\u671d\u5165\u5173\u5df2\u516d\u5341\u591a\u5e74\uff0c\u60f3\u4e0d\u5230\u5979\u8fd8\u5065\u5728\uff0c\u6025\u5fd9\u65bd\u793c\u3002\u72ec\u81c2\u8001\u5c3c\u9053\uff1a\u201c\u5173\u4e1c\u56db \u4fa0\uff0c\u8c6a\u4fa0\u4ed7\u4e49\uff0c\u540d\u4e0d\u865a\u4f20\uff01\u201d\u6307\u7740\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4ed6\u5b9a\u662f\u53d7\u4e86\u516b\u81c2\u795e\u9b54\u6bd2\u722a\u6240\u4f24\u4e86\uff01\u201d\u67f3\u5148\u5f00 \u9053\uff1a\u201c\u8fd8\u671b\u795e\u5c3c\u89e3\u6551\uff01\u201d\u72ec\u81c2\u8001\u5c3c\u9053\uff1a\u201c\u522b\u7684\u6211\u4e0d\u6562\u5938\u53e3\uff0c\u89e3\u6cbb\u86c7\u6bd2\uff0c\u6211\u8fd8\u53ef\u4ee5\u3002\u201d\u67f3\u5148\u5f00 \u4e0a\u524d\u6276\u8d77\u4e86\u5510\u6653\u6f9c\uff0c\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u6ca1\u4e8b\u4e86\uff0c\u53cc\u9b54\u5df2\u7ed9\u6253\u8dd1\u4e86\u3002\u201d\u5510\u6653\u6f9c\u53cc\u76ee\u5fae\u5f00\uff0c\u4f4e\u58f0 \u95ee\u9053\uff1a\u201c\u6211\u7684\u4f84\u5973\u513f\u5462\uff1f\u593a\u4e86\u8fc7\u6765\u6ca1\u6709\uff1f\u201d \u3000\u3000\u67f3\u5148\u5f00\u9053\uff1a\u201c\u7ed9\u4ed6\u5e26\u8d70\u4e86\uff01\u201d\u5510\u6653\u6f9c\u53cc\u773c\u4e00\u7ffb\uff0c\u53c8\u6655\u8fc7\u53bb\u3002\u72ec\u81c2\u8001\u5c3c\u9053\uff1a\u201c\u662f\u6211\u758f\u5ffd\uff0c \u53ea\u60f3\u770b\u83b9\u513f\u8bd5\u62db\uff0c\u6ca1\u60f3\u5230\u90a3\u5973\u5a03\u5b50\u662f\u9b54\u5934\u62a2\u6765\u7684\u3002\u201d\u67f3\u5148\u5f00\u9053\uff1a\u201c\u6551\u547d\u4e4b\u6069\uff0c\u5df2\u4e0d\u6562\u5fd8\uff01\u201d \u72ec\u81c2\u8001\u5c3c\u9053\uff1a\u201c\u4f60\u4e14\u968f\u6211\u540c\u5230\u5c71\u5c45\uff0c\u7b49\u4f1a\u8fd8\u6709\u4f60\u7684\u51e0\u4f4d\u597d\u53cb\u6765\u8bbf\u3002\u201d\u67f3\u5148\u5f00\u5fc3\u60f3\uff0c\u81ea\u5df1\u5728\u6cb3 \u5357\u9664\u4e86\u6768\u4ef2\u82f1\u5916\uff0c\u53ef\u5e76\u6ca1\u6709\u4ec0\u4e48\u597d\u53cb\uff0c\u5fc3\u4e2d\u9887\u89c9\u5947\u602a\u3002 \u3000\u3000\u5510\u6653\u6f9c\u81ea\u5df1\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u60a0\u60a0\u9192\u8f6c\uff0c\u53ea\u89c9\u5e7d\u9999\u88ad\u4eba\uff0c\u7741\u773c\u4e00\u770b\uff0c\u67f3\u5148\u5f00\u5df2\u4e0d\u5728 \u8eab\u65c1\uff0c\u81ea\u5df1\u8eba\u5728\u4e00\u95f4\u7cbe\u5ba4\u4e4b\u4e2d\uff0c\u623f\u95f4\u5e03\u7f72\u5f97\u6e05\u96c5\u7edd\u4fd7\uff0c\u51a5\u5fc3\u6f5c\u7d22\uff0c\u53ea\u8bb0\u8d77\u90a3\u5c11\u5973\u4e0e\u53cc\u9b54\u76f8 \u6597\uff0c\u67f3\u5148\u5f00\u628a\u81ea\u5df1\u653e\u5728\u5730\u4e0a\uff0c\u4ee5\u540e\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u5fc3\u60f3\uff0c\u83ab\u975e\u662f\u90a3\u5c11\u5973\u628a\u53cc\u9b54\u6253\u8d25\uff0c\u5c06\u81ea\u5df1\u6551 \u4e86\uff0c\u8fd9\u91cc\u662f\u5979\u7684\u5367\u623f\uff1f\u6323\u624e\u6b32\u8d77\uff0c\u4f46\u89c9\u767e\u9ab8\u6b32\u6563\uff0c\u7ef5\u8f6f\u65e0\u529b\uff0c\u518d\u7741\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u5899\u4e0a\u4e00\u526f\u5bf9 \u8054\uff0c\u5199\u9053\uff1a \u3000\u3000\u201c\u94c1\u80a9\u62c5\u9053\u4e49 \u3000\u3000\u8fa3\u624b\u8457\u6587\u5355\u201d \u3000\u3000\u4e2d\u95f4\u4e00\u5e45\u4e2d\u5802\uff0c\u5199\u7740\u4e00\u9996\u957f\u8bcd\uff0c\u8bcd\u9053\uff1a \u3000\u3000\u201c\u6e21\u6c5f\u5929\u9a6c\u5357\u6765\uff0c\u51e0\u4eba\u53d1\u662f\u7ecf\u7eb6\u624b\uff1f\u957f\u5b89\u7236\u8001\uff0c\u65b0\u4ead\u98ce\u597d\uff0c\u53ef\u601c\u4f9d\u65e7\uff01\u5937\u752b\u8bf8\u4eba\uff0c\u795e\u5dde \u6c88\u9646\uff0c\u51e0\u66fe\u56de\u9996\uff1f\u7b97\u5e73\u620e\u4e07\u91cc\uff0c\u529f\u540d\u672c\u662f\uff0c\u771f\u5112\u4e8b\uff0c\u541b\u77e5\u5426\uff1f\u51b5\u6709\u6587\u65e5\u5c71\u6597\uff0c\u5bf9\u6850\u9634\uff0c\u6ee1\u80a9 \u6e05\u663c\u3002\u5f53\u5e74\u5815\u5730\uff1b\u800c\u4eca\u8bd5\u770b\uff1b\u98ce\u4e91\u5954\u8d70\u3002\u7eff\u91ce\u98ce\u70df\uff0c\u5e73\u6cc9\u8349\u6728\uff0c\u4e1c\u5c71\u6b4c\u9152\u3002\u5f85\u4ed6\u5e74\uff0c\u6574\u987f\u4e7e \u5764\u4e8b\u4e86\uff0c\u4e3a\u5148\u751f\u5bff\u3002\u201d \u3000\u3000\u4e0a\u4e0b\u6b3e\u5199\u7684\u662f\uff1a\u201c\u5199\u8f9b\u5f03\u75be\u6c34\u9f99\u541f\u8bcd\u4e3a\u7559\u826f\u5148\u751f\u5bff\u665a\u534e\u4ead\u9648\u5367\u5b50\u4e66\u3002\u201d\u5510\u6653\u6f9c\u5e38\u542c\u5468\u9752 \u8c08\u8bba\u524d\u671d\u7684\u5fd7\u58eb\u8c6a\u82f1\uff0c\u77e5\u9053\u9648\u5367\u5b50\uff08\u5373\u9648\u5b50\u9f99\uff09\u662f\u660e\u672b\u7684\u6297\u6e05\u82f1\u96c4\uff0c\u5e76\u4ee5\u8bcd\u540d\u4e3a\u4e16\u6240\u91cd\u3002 \uff08\u7fbd\u751f\u6309\uff1a\u8fd1\u4eba\u9f99\u6986\u751f\u7f16\u300a\u8fd1\u4e09\u767e\u5e74\u540d\u5bb6\u8bcd\u9009\u300b\uff0c\u5373\u4ee5\u9648\u5b50\u9f99\u8bcd\u51a0\u5176\u9996\uff09\u6ee1\u6e05\u5165\u5173\u540e\uff0c\u4ed6\u5728 \u592a\u6e56\u4e3e\u5175\uff0c\u4e8b\u4e0d\u5bc6\uff0c\u7adf\u88ab\u4fd8\u864f\uff0c\u5728\u62bc\u89e3\u9014\u4e2d\uff0c\u6295\u6c34\u81ea\u6740\u3002\u5510\u6653\u6f9c\u7c97\u89e3\u8bd7\u4e66\uff0c\u5927\u81f4\u9886\u7565\u8f9b\u5f03\u75be \u8fd9\u4e00\u9996\u8bcd\u662f\u60b2\u56fd\u571f\u6ca6\u4ea1\uff0c\u4ee5\u6062\u590d\u795e\u5dde\u4e3a\u5fd7\uff0c\u5e76\u4e0e\u53cb\u4eba\u5171\u52c9\u7684\u3002\u540c\u4e2d\u6709\u201c\u6587\u7ae0\u5c71\u6597\u201d\u4e4b\u53e5\uff0c\u90a3 \u4e48\u9648\u5b50\u9f99\u5199\u6b64\u9001\u7ed9\u201c\u7559\u826f\u201d\uff0c\u8fd9\u4f4d\u201c\u7559\u826f\u201d\u5148\u751f\u5fc5\u7136\u662f\u4e00\u4ee3\u5927\u5112\u4e86\uff01\u84e6\u7136\u60f3\u8d77\u6765\u9053\uff1a\u201c\u8fd9\u4f4d \u7559\u826f\u5148\u751f\uff0c\u83ab\u975e\u5c31\u662f\u6d59\u4e1c\u540d\u5112\u5415\u7559\u826f\uff01\u201d\u5415\u7559\u826f\u5728\u660e\u4ea1\u4e4b\u540e\uff0c\u62d2\u7edd\u6e05\u5ef7\u5fae\u8058\uff0c\u524a\u53d1\u4e3a\u50e7\uff0c\u8457 \u4e66\u5ba3\u4f20\u6518\u5937\uff0c\u5f71\u54cd\u6781\u5927\u3002\u5510\u6653\u6f9c\u81ea\u5e7c\u5728\u6c5f\u6e56\u6d41\u6d6a\uff0c\u672a\u66fe\u597d\u597d\u8bfb\u4e66\uff0c\u5415\u7559\u826f\u7684\u4e66\u4ed6\u4e5f\u5e76\u672a\u8bfb \u8fc7\uff0c\u53ef\u662f\u4e45\u95fb\u5176\u540d\uff0c\u5fc3\u4e2d\u4e45\u5df2\u4f69\u670d\u3002 \u3000\u3000\u6b63\u6c89\u601d\u95f4\uff0c\u623f\u95e8\u5ffd\u7136\u8f7b\u8f7b\u5f00\u4e86\uff0c\u6628\u665a\u6240\u89c1\u7684\u5c11\u5973\u8d70\u4e86\u8fdb\u6765\uff0c\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u54ce\uff0c\u4f60\u9192\u4e86 \u4e48\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u591a\u8c22\u5973\u4fa0\u6551\u547d\u4e4b\u6069\uff0c\u8bf7\u6055\u6211\u4e0d\u80fd\u884c\u793c\u3002\u201d\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u90a3\u662f\u6211\u5e08\u5085\u6551 \u4f60\u7684\u547d\uff0c\u4e0e\u6211\u65e0\u5173\u3002\u5582\uff0c\u4f60\u4e0d\u5fc5\u2018\u5973\u4fa0\u2019\u957f\u2018\u5973\u4fa0\u2019\u77ed\u7684\u53eb\u6211\uff0c\u6211\u8fd8\u672a\u51fa\u5e08\u5462\uff01\u4f60\u53eb\u6211\u5415\u56db \u5a18\u597d\u4e86\uff01\u201d\u5510\u6653\u6f9c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8f7b\u8f7b\u53eb\u9053\uff1a\u201c\u5415\u56db\u5a18\uff1f\u54ce\uff0c\u90a3\u4e48\u4f60\u662f\u5415\u7559\u826f\u5148\u751f\u7684\u2014\u2014\u201d\u5415\u56db \u5a18\u7b11\u7740\u63a5\u9053\uff1a\u201c\u5b59\u5973\u3002\u201d\u5510\u6653\u6f9c\u4e0d\u7981\u5446\u5446\u7684\u671b\u7740\u5979\uff0c\u60f3\u4e0d\u5230\u8fd9\u4f4d\u4e00\u4ee3\u5927\u5112\u7684\u5b59\u5973\uff0c\u7adf\u6709\u7edd\u9876 \u6b66\u529f\uff01 \u3000\u3000\u5415\u56db\u5a18\u8f7b\u8f7b\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u5f1f\uff1a\u4f60\u4eca\u5e74\u51e0\u5c81\u4e86\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5341\u516d\u5c81\u4e86\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a \u201c\u5341\u516d\u5c81\u6709\u8fd9\u6837\u7684\u529f\u5927\uff0c\u4e5f\u5f88\u4e0d\u9519\u4e86\uff0c\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5c45\u7136\u6709\u6b64\u80c6\u91cf\uff0c\u6562\u4e0e\u53cc\u9b54\u4e89\u6597\uff0c\u602a\u4e0d\u5f97\u6211 \u5e08\u5085\u8bf4\u4f60\u662f\u53ef\u9020\u4e4b\u6750\uff0c\u66ff\u4f60\u6089\u5fc3\u7597\u6cbb\u3002\u6211\u6bd4\u4f60\u5927\u4e09\u5e74\uff0c\u4f60\u5e72\u8106\u53eb\u6211\u5415\u83b9\u59d0\u59d0\u4e5f\u884c\u3002\u201d\u5510\u6653\u6f9c \u8fd9\u624d\u77e5\u9053\u8fd9\u5c11\u5973\u540d\u53eb\u5415\u83b9\uff0c\u201c\u56db\u5a18\u201d\u5927\u7ea6\u662f\u5979\u5728\u5bb6\u4e2d\u7684\u6392\u884c\u3002\u5fc3\u60f3\uff1a\u5979\u6bd4\u6211\u53ea\u5927\u4e09\u5e74\uff0c\u6b66\u529f \u5374\u8fd8\u5728\u5173\u4e1c\u56db\u4fa0\u3001\u795e\u9b54\u53cc\u8001\u4e4b\u4e0a\uff0c\u6211\u518d\u7ec3\u5341\u5e74\uff0c\u6015\u4e5f\u672a\u5fc5\u8d76\u5f97\u4e0a\u5979\uff0c\u4e0d\u89c9\u6697\u81ea\u60ed\u6127\u3002\u5415\u56db\u5a18 \u53c8\u9053\uff1a\u201c\u4f60\u8bfb\u8fc7\u6211\u7956\u7236\u7684\u4e66\u5417\uff1f\u201d\u5510\u6653\u6f9c\u7f9e\u8d67\u7b54\u9053\uff1a\u201c\u6ca1\u6709\u3002\u4f46\u5bf9\u4ed6\u8001\u4eba\u5bb6\u5927\u540d\uff0c\u5374\u662f\u4e45\u5df2 \u5982\u96f7\u8d2f\u8033\u3002\u201d\u5415\u56db\u5a18\u53c8\u7b11\u9053\uff1a\u201c\u5b66\u6b66\u7684\u4eba\u4e5f\u8be5\u8bfb\u4e00\u4e9b\u4e66\uff0c\u4f60\u73b0\u5728\u6b63\u662f\u6c42\u5b66\u7684\u5e74\u7eaa\uff0c\u6211\u9001\u4ed6\u8001 \u4eba\u5bb6\u8457\u7684\u4e00\u672c\u300a\u6518\u5937\u5f55\u300b\u7ed9\u4f60\u5427\u3002\u201d\u5510\u6653\u6f9c\u8d8a\u53d1\u4e0d\u597d\u610f\u601d\uff0c\u4f4e\u5934\u9053\u4e86\u58f0\uff1a\u201c\u591a\u8c22\u59d0\u59d0\uff01\u201d\u5bf9 \u5415\u56db\u5a18\u4f69\u670d\u4e4b\u6781\uff0c\u53ea\u89c9\u5979\u4fe8\u5982\u5929\u4eba\uff0c\u4ee4\u4eba\u4e0d\u6562\u903c\u89c6\u3002 \u3000\u3000\u6b47\u4e86\u4e00\u9635\uff0c\u6709\u4e00\u4e2a\u82cd\u8001\u7684\u5973\u58f0\u5728\u90bb\u623f\u95ee\u9053\uff1a\u201c\u90a3\u5b69\u5b50\u6ca1\u4e8b\u4e86\u5417\uff1f\u201d\u5415\u56db\u5a18\u5e94\u4e86\u4e00\u58f0\uff1a \u201c\u6ca1\u4e8b\u4e86\uff01\u201d\u8f6c\u8fc7\u5934\u6765\u5bf9\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u5e08\u5085\u53eb\u4f60\u5462\uff01\u4f60\u4e0b\u5e8a\u8d70\u8d70\u770b\uff0c\u770b\u884c\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u4e0b \u5e8a\u884c\u4e86\u51e0\u6b65\uff0c\u53ea\u89c9\u6c14\u723d\u795e\u60c5\uff0c\u6beb\u65e0\u75db\u82e6\uff0c\u5927\u559c\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u5e26\u6211\u53bb\u8c12\u89c1\u5979\u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u90bb\u623f\u5e03\u7f6e\u597d\u50cf\u4e00\u4e2a\u5eb5\u5802\uff0c\u6b63\u4e2d\u7684\u795e\u50cf\u5374\u7ed9\u4e00\u5e45\u9ec4\u5e03\u906e\u4f4f\uff0c\u770b\u4e0d\u6e05\u695a\u3002\u5510\u6653\u6f9c\u4e00\u8d70\u8fdb\u6765\uff0c \u5c31\u542c\u5f97\u4e00\u4e2a\u719f\u6089\u7684\u58f0\u97f3\u53eb\u4ed6\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u4f60\u7684\u547d\u603b\u7b97\u662f\u62fe\u56de\u6765\u4e86\uff0c\u5feb\u8fc7\u6765\uff0c\u89c1\u8fd9\u4f4d\u795e\u5c3c\u3002\u201d \u53eb\u4ed6\u7684\u4eba\u6b63\u662f\u4e07\u91cc\u8ffd\u98ce\u67f3\u5148\u5f00\u3002\u5728\u4ed6\u65c1\u8fb9\u7aef\u5750\u7740\u90a3\u4e2a\u72ec\u81c2\u8001\u5c3c\uff0c\u8fd8\u6709\u4e00\u4e2a\u8001\u5934\u5b50\u5374\u4e0d\u77e5\u662f \u8c01\u3002\u5510\u6653\u6f9c\u8fc7\u6765\u53e9\u8c22\uff0c\u5374\u4e0d\u77e5\u9053\u8fd9\u8001\u5c3c\u59d1\u7684\u6cd5\u8bb3\uff0c\u8be5\u662f\u600e\u4e48\u4e2a\u79f0\u547c\u3002\u8001\u5c3c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u6211\u6ca1 \u6709\u6cd5\u53f7\uff0c\u540d\u5b57\u5462\uff0c\u4e5f\u65e9\u5df2\u4e0d\u7528\u4e86\uff0c\u6c5f\u6e56\u4e0a\u90fd\u79f0\u6211\u4e3a\u72ec\u81c2\u8001\u5c3c\uff0c\u4f60\u4e5f\u5c31\u8fd9\u6837\u53eb\u6211\u5427\u3002\u54ce\uff0c\u4f60\u4e0d \u5fc5\u591a\u8c22\u6211\uff0c\u4f60\u5e94\u8be5\u591a\u8c22\u67f3\u5927\u4fa0\uff0c\u4ed6\u4ece\u592a\u884c\u5c71\u4e00\u76f4\u628a\u4f60\u8d1f\u5230\u8fd9\u513f\uff01\u201d\u5510\u6653\u6f9c\u53c8\u606d\u606d\u656c\u656c\u7684\u5411\u67f3 \u5148\u5f00\u78d5\u4e86\u4e09\u4e2a\u54cd\u5934\u3002\u67f3\u5148\u5f00\u4e00\u7b11\u628a\u4ed6\u62c9\u8d77\u3002 \u3000\u3000\u5415\u56db\u5a18\u89c1\u8fc7\u4e86\u5e08\u5085\u540e\uff0c\u5bf9\u65c1\u8fb9\u8001\u5934\u5b50\u9053\uff1a\u201c\u4e25\u53d4\u53d4\u8fdc\u9053\u800c\u6765\uff0c\u83ab\u975e\u5bb6\u7236\u6709\u4ec0\u4e48\u4e8b\u4e48\uff1f\u201d \u72ec\u81c2\u8001\u5c3c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u4e25\u53d4\u53d4\u53eb\u4f60\u56de\u53bb\u3002\u201d\u5415\u56db\u5a18\u9661\u7136\u4e00\u9707\uff0c\u90a3\u59d3\u4e25\u7684\u8001\u5934\u5b50\u9053\uff1a\u201c\u4f60\u7238\u7238 \u5e74\u8001\uff0c\u8fd1\u6765\u53c8\u6709\u70b9\u5c0f\u75c5\uff0c\u5f88\u60f3\u5ff5\u4f60\u3002\u201d\u72ec\u81c2\u8001\u5c3c\u9053\uff1a\u201c\u83b9\u513f\uff0c\u4f60\u5728\u6211\u95e8\u4e0b\u4e5d\u5e74\uff0c\u6b66\u529f\u6bd4\u4f60\u5e08 \u5144\u4eec\u90fd\u5b66\u5f97\u591a\uff0c\u6211\u4e5f\u6ca1\u6709\u4ec0\u4e48\u6559\u7ed9\u4f60\u4e86\u3002\u4f60\u8d76\u660e\u513f\u5c31\u56de\u53bb\u5427\u3002\u201d\u5415\u56db\u5a18\u4e00\u9635\u96be\u8fc7\uff0c\u72ec\u81c2\u8001\u5c3c \u9053\uff1a\u201c\u5929\u4e0b\u65e0\u4e0d\u6563\u4e4b\u7b75\u5e2d\uff0c\u4f60\u662f\u6211\u6700\u5f97\u610f\u7684\u5f92\u513f\uff0c\u4f60\u8981\u5207\u8bb0\u6211\u7684\u6559\u8bad\u3002\u4f60\u56de\u5bb6\u63a2\u4eb2\u4e4b\u540e\uff0c\u53bb \u6253\u542c\u4f60\u5927\u5e08\u5144\u7684\u8e2a\u8ff9\uff0c\u770b\u770b\u4ed6\u7684\u4e3a\u4eba\uff0c\u82e5\u4ed6\u771f\u4e2a\u80cc\u53db\u5e08\u95e8\uff0c\u4f60\u5c31\u628a\u4ed6\u7684\u9996\u7ea7\u62ff\u6765\u89c1\u6211\uff01\u201d\u67f3 \u5148\u5f00\u542c\u5f97\u5927\u60ca\uff0c\u6b63\u5f53\u6b64\u9645\uff0c\u5c71\u95e8\u5916\u51e0\u58f0\u957f\u5578\uff0c\u72ec\u81c2\u8001\u5c3c\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u55ef\uff0c\u4ed6\u4eec\u4e5f\u90fd\u6765\u4e86\uff01\u201d \u3000\u3000\u4e24\u6247\u5c71\u95e8\uff0c\u6162\u6162\u63a8\u5f00\uff0c\u72ec\u81c2\u8001\u5c3c\u6b22\u7136\u8bf4\u9053\uff1a\u201c\u7384\u98ce\u9053\u957f\uff0c\u522b\u6765\u65e0\u6059\uff01\u201d\u5c71\u95e8\u5916\u5f71\u7ef0\u7ef0\u7684 \u7acb\u7740\u4e09\u4eba\uff0c\u6b63\u662f\u5931\u4e1c\u56db\u4fa0\u4e2d\u7684\u7384\u98ce\u3001\u6717\u6708\u548c\u9648\u5143\u9738\u3002\u7384\u98ce\u9053\u957f\u957f\u63d6\u5230\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u4ef0\u4ed7\u795e\u5c3c \u4e4b\u529b\uff0c\u5413\u8d70\u4e24\u4e2a\u9b54\u5934\uff0c\u8d2b\u9053\u8fd9\u53a2\u6709\u793c\u4e86\u3002\u201d\u67f3\u5148\u5f00\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u5927\u54e5\u53eb\u4ed6\u907f\u4e0a\u9099\u5c71\uff0c \u7528\u610f\u5c31\u662f\u5f15\u90a3\u4e24\u4e2a\u9b54\u5934\u6765\u89c1\u72ec\u81c2\u8001\u5c3c\u3002 \u3000\u3000\u72ec\u81c2\u8001\u5c3c\u5c06\u5173\u4e1c\u56db\u4fa0\u5e26\u56de\u5eb5\u5802\uff0c\u4ecb\u7ecd\u4e86\u90a3\u4e2a\u59d3\u4e25\u7684\u8001\u5934\u5b50\u3002\u8fd9\u4eba\u5374\u4e0d\u662f\u4ec0\u4e48\u6b66\u6797\u4e2d\u4eba\uff0c \u800c\u662f\u5415\u7559\u826f\u7684\u95e8\u751f\uff0c\u4e5f\u662f\u6e10\u4e1c\u7684\u4e00\u4e2a\u540d\u5112\u3002\u6570\u5341\u5e74\u6765\uff0c\u5728\u4e1c\u5357\u6cbf\u6d77\u4f20\u64ad\u5415\u7559\u826f\u7684\u5b66\u8bf4\uff0c\u548c\u5415 \u56db\u5a18\u7684\u7236\u4eb2\u5415\u7559\u826f\u540c\u662f\u5bc6\u8c0b\u6297\u6e05\u7684\u4e49\u571f\uff0c\u7384\u98ce\u9053\u957f\u4e5f\u4e45\u95fb\u5176\u540d\uff0c\u62f1\u624b\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u4e00\u6587\u4e00 \u6b66\uff0c\u6b8a\u9014\u540c\u5f52\uff0c\u5357\u5317\u9f50\u5fc3\uff0c\u4f55\u6101\u4e0d\u5149\u590d\u6c49\u5bb6\u6545\u4e1a\u3002\u201d\u72ec\u81c2\u8001\u5c3c\u773c\u5708\u4e00\u7ea2\uff0c\u671b\u7740\u4f5b\u5802\u4e2d\u7684\u795e \u5eb5\uff0c\u6014\u6014\u51fa\u795e\u3002\u5173\u4e1c\u56db\u4fa0\u90fd\u77e5\u9053\u5979\u662f\u524d\u671d\u516c\u4e3b\uff0c\u5fc3\u4f24\u6545\u56fd\uff0c\u6005\u89e6\u524d\u5c18\uff0c\u4e00\u65f6\u95f4\u90fd\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u72ec\u81c2\u8001\u5c3c\u970d\u7136\u8bf4\u9053\uff1a\u201c\u8d2b\u5c3c\u9053\u5fc3\u672a\u51c0\uff0c\u8d3b\u7b11\u9053\u5144\u3002\u201d\u7384\u98ce\u9053\u957f\u54b3\u4e86\u4e00\u58f0\uff0c\u5c06 \u8bdd\u9898\u5f15\u4e86\u5f00\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u51e0\u5e74\u524d\u95fb\u8bf4\u53cc\u9b54\u5404\u7834\u4e00\u8db3\uff0c\u6211\u5c31\u6599\u5230\u662f\u5927\u5e08\u6240\u4e3a\u3002\u201d\u72ec\u81c2\u8001\u5c3c\u7b11 \u9053\uff1a\u201c\u4e09\u5e74\u524d\uff0c\u6211\u4e91\u6e38\u81f3\u516b\u8fbe\u5cad\uff0c\u5de7\u9047\u8fd9\u4e24\u4e2a\u9b54\u5934\uff0c\u4ed6\u4eec\u4e0d\u77e5\u8fdb\u9000\uff0c\u5b9a\u8981\u4e0e\u6211\u6bd4\u6b66\u3002\u90a3\u65f6\u4ed6 \u4ecd\u6076\u8ff9\u672a\u5f70\uff0c\u6240\u4ee5\u6211\u53ea\u7565\u65bd\u60e9\u6212\u3002\u201d\u7384\u98ce\u9053\u957f\u9053\uff1a\u201c\u5e78\u597d\u5927\u5e08\u523a\u4f24\u4ed6\u4eec\uff0c\u8981\u4e0d\u7136\u8fd9\u6b21\u592a\u884c\u5c71 \u4e4b\u4f1a\uff0c\u6211\u4eec\u66f4\u96be\u9003\u6bd2\u624b\u3002\u201d\u5f53\u4e0b\u5c06\u53cc\u9b54\u53d7\u5141\u796f\u4e4b\u8058\uff0c\u5728\u592a\u884c\u5c71\u4e0a\uff0c\u6740\u5bb3\u5317\u4e94\u7701\u8c6a\u6770\u4e4b\u4e8b\u8bf4 \u4e86\uff0c\u72ec\u81c2\u8001\u5c3c\u773c\u7736\u6b32\u88c2\uff0c\u6068\u6068\u8bf4\u9053\uff1a\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u4eca\u65e5\u6211\u5fc5\u4e0d\u653e\u4ed6\u4eec\u8fc7\u53bb\u3002\u201d \u3000\u3000\u8bf4\u4e86\u4e00\u9635\uff0c\u72ec\u81c2\u8001\u5c3c\u53c8\u9053\uff1a\u201c\u56db\u4fa0\u8fd1\u5e74\u53ef\u6709\u5230\u8fc7\u6c5f\u5357\u4e48\uff1f\u201d\u7384\u98ce\u9053\uff1a\u201c\u6211\u4eec\u56db\u5144\u5f1f\u5341\u5e74 \u6765\u8db3\u8ff9\u672a\u8fc7\u957f\u6c5f\u3002\u201d\u72ec\u81c2\u8001\u5c3c\u9053\uff1a\u201c\u542c\u8bf4\u6211\u90a3\u5927\u5f92\u5f1f\u52fe\u7ed3\u6c5f\u6e56\u5de8\u76d7\uff0c\u4e3a\u60a3\u5ba2\u5546\uff0c\u56db\u4f4d\u4ea6\u6709\u6240 \u95fb\u4e48\uff1f\u201d\u7384\u98ce\u6447\u4e86\u6447\u5934\u3002\u72ec\u81c2\u8001\u5c3c\u9053\uff1a\u201c\u6211\u5728\u4e16\u95f4\uff0c\u4ed6\u5c1a\u6709\u6240\u987e\u5fcc\uff0c\u6240\u4ee5\u867d\u7136\u4e0d\u5b88\u4f5b\u95e8\u6e05 \u6212\uff0c\u4ecd\u4e0d\u6562\u516c\u7136\u4f5c\u6b79\u4e3a\u975e\uff0c\u53ea\u6050\u6211\u6b7b\u4e86\u4e4b\u540e\uff0c\u6ca1\u4eba\u80fd\u5236\u670d\u4ed6\u3002\u201d\u7384\u98ce\u5403\u4e86\u4e00\u60ca\uff0c\u539f\u6765\u72ec\u81c2\u8001 \u5c3c\u4f20\u4e0b\u516b\u4e2a\u5f1f\u5b50\uff0c\u9664\u5415\u56db\u5a18\u5916\uff0c\u5176\u4ed6\u4e03\u4eba\u90fd\u5df2\u51fa\u5e08\uff0c\u6563\u5728\u6c5f\u5357\uff0c\u53f7\u79f0\u6c5f\u5357\u4e03\u4fa0\u3002\u4e3a\u9996\u7684\u540d\u53eb \u4e86\u56e0\uff0c\u662f\u4e2a\u548c\u5c1a\uff0c\u6b66\u529f\u6700\u9ad8\uff0c\u66fe\u4ee5\u4e00\u6839\u7985\u6756\uff0c\u8fde\u8d25\u5341\u4e8c\u4e2a\u9ad8\u624b\uff0c\u6280\u538b\u6c5f\u5357\u3002\u4e03\u4fa0\u867d\u540c\u51fa\u4e00 \u95e8\uff0c\u6b66\u529f\u672c\u9886\u5374\u662f\u53c2\u5dee\u4e0d\u9f50\uff0c\u6392\u884c\u7b2c\u4e03\u7684\u7518\u51e4\u6c60\u5a01\u540d\u6700\u76db\uff0c\u4f46\u5185\u529f\u5916\u529f\uff0c\u6bd4\u8d77\u4e86\u56e0\uff0c\u5374\u8fd8\u76f8 \u5dee\u9887\u8fdc\u3002\u518d\u5176\u6b21\u662f\u6392\u884c\u7b2c\u4e94\u7684\u767d\u6cf0\u5b98\uff0c\u81f3\u4e8e\u8def\u6c11\u77bb\u3001\u674e\u6e90\u3001\u5468\u6e05\u7b49\u53c8\u7b49\u800c\u4e0b\u4e4b\uff0c\u5e76\u4e0d\u89c1\u5f97\u5982 \u4f55\u51fa\u7c7b\u62d4\u8403\u4e86\u3002\u8def\u6c11\u77bb\u548c\u5468\u6e05\u66fe\u5230\u8fc7\u5173\u4e1c\uff0c\u4ee5\u524d\u8f88\u4e4b\u793c\uff0c\u89c1\u8fc7\u7384\u98ce\uff0c\u7384\u98ce\u548c\u4ed6\u4eec\u8bd5\u62db\uff0c\u4e0d\u8fc7 \u4e09\u5341\u62db\uff0c\u4e24\u4eba\u90fd\u8d25\u4e86\u4e0b\u6765\u3002\u5f53\u65f6\u7384\u98ce\u8fd8\u8fd9\u6837\u5fc3\u60f3\uff1a\u4f55\u4ee5\u72ec\u81c2\u8001\u5c3c\u7684\u5f1f\u5b50\u5982\u6b64\u5e73\u5eb8\uff0c\u60f3\u90a3\u7518\u51e4 \u6c60\u548c\u4e86\u56e0\uff0c\u867d\u7136\u5a01\u9707\u6c5f\u5357\uff0c\u597d\u8c61\u4e5f\u662f\u6709\u9650\uff0c\u800c\u4eca\u89c1\u72ec\u81c2\u8001\u5c3c\u5982\u6b64\u4e00\u8bf4\uff0c\u4e0d\u7981\u60ca\u7591\u3002\u72ec\u81c2\u8001\u5c3c \u6307\u4e86\u6307\u5415\u56db\u5a18\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u5979\u4eca\u5929\u8bd5\u5251\uff0c\u80fd\u72ec\u81ea\u6218\u8d25\u516b\u81c2\u795e\u9b54\u4e5f\u7b97\u96be\u5f97\u4e86\u3002\u4f46\u8fd8\u8981\u518d\u953b\u70bc \u51e0\u5e74\uff0c\u624d\u5236\u5f97\u4f4f\u5979\u7684\u5927\u5e08\u5144\u3002\u201d \u3000\u3000\u7384\u98ce\u542c\u4e86\u66f4\u662f\u5403\u60ca\uff0c\u770b\u90a3\u5415\u56db\u5a18\u98a6\u98a6\u6d45\u7b11\uff0c\u4fe8\u7136\u8fd8\u662f\u4e2a\u5929\u771f\u672a\u51ff\u7684\u5c0f\u59d1\u5a18\uff0c\u771f\u4e0d\u6562\u76f8\u4fe1 \u5979\u5251\u6cd5\u5982\u6b64\u5389\u5bb3\u3002\u67f3\u5148\u5f00\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u7684\u6027\u547d\u5c31\u662f\u8fd9\u4f4d\u59d1\u5a18\u6551\u7684\u3002\u201d\u7384\u98ce\u4e0d\u7531\u4e0d\u4fe1\uff0c\u8bf4 \u9053\uff1a\u201c\u539f\u6765\u4e0d\u5f85\u795e\u5c3c\u51fa\u624b\uff0c\u5df2\u628a\u8fd9\u4e24\u4e2a\u9b54\u5934\u6253\u8d70\u4e86\u3002\u201d\u72ec\u81c2\u8001\u5c3c\u7b11\u9053\uff1a\u201c\u8fd9\u53c8\u4e0d\u7136\uff0c\u5979\u6ca1\u6709 \u90a3\u6837\u7684\u529f\u592b\uff0c\u540e\u6765\u5927\u529b\u795e\u9b54\u52a0\u5165\u6218\u56e2\uff0c\u662f\u6211\u73b0\u8eab\u4ed6\u4eec\u624d\u72fc\u72c8\u9003\u9041\u3002\u201d\u7f13\u4e86\u4e00\u7f13\u53c8\u9053\uff1a\u201c\u8fd9\u4e24 \u5e74\u6765\uff0c\u6211\u5bf9\u4e86\u56e0\u4e0d\u5b88\u6e05\u89c4\u4e4b\u4e8b\uff0c\u9690\u6709\u6240\u95fb\uff0c\u6240\u4ee5\u7279\u522b\u4f20\u4e86\u83b9\u513f\u7384\u5973\u5251\u6cd5\uff0c\u82e5\u4ed6\u5c06\u6765\u4e3a\u975e\u4f5c \u6b79\uff0c\u5c31\u53eb\u83b9\u513f\u66ff\u6211\u6e05\u7406\u5e08\u95e8\u3002\u4f46\u6050\u5979\u529f\u529b\u672a\u6df1\uff0c\u5230\u65f6\u8fd8\u671b\u56db\u4fa0\u52a9\u4e00\u81c2\u4e4b\u529b\u3002\u201d\u7384\u98ce\u542c\u4e86\uff0c\u505a \u58f0\u4e0d\u5f97\u3002\u72ec\u81c2\u8001\u5c3c\u53c8\u9053\uff1a\u201c\u83b9\u513f\u660e\u5929\u4e0b\u5c71\uff0c\u4ee5\u540e\u5728\u6c5f\u6e56\u95ef\u8361\uff0c\u8fd8\u671b\u56db\u4fa0\u62db\u6276\u62db\u6276\u3002\u201d\u5173\u4e1c\u56db \u4fa0\uff0c\u8fde\u79f0\u4e0d\u6562\uff0c\u9648\u5143\u9738\u7b11\u9053\uff1a\u201c\u5973\u4fa0\u4e00\u51fa\uff0c\u521a\u597d\u51d1\u6210\u6c5f\u5357\u516b\u4fa0\uff0c\u6bd4\u6211\u4eec\u4eba\u591a\u4e00\u500d\uff0c\u5357\u5317\u547c \u5e94\uff0c\u4e5f\u53ef\u4ee5\u4e92\u58ee\u58f0\u52bf\u3002\u201d\u72ec\u81c2\u8001\u5c3c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u4fbf\u613f\u5982\u6b64\uff01\u201d\u9648\u5143\u9738\u4e00\u60f3\uff0c\u624d\u77e5\u5931\u8a00\uff0c\u90a3\u4e86 \u56e0\u82e5\u5165\u4e86\u6b67\u9014\uff0c\u5982\u4f55\u7b97\u5f97\u4fa0\u58eb\uff0c\u642d\u8baa\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u5973\u4fa0\u8981\u5230\u6c5f\u5357\uff0c\u8981\u4e0d\uff0c\u54b1\u4eec\u540c\u5f80\u4eac\u5e08\u628a\u7d2b \u7981\u57ce\u4e5f\u95f9\u4e2a\u5929\u7ffb\u5730\u8986\uff01\u201d\u5415\u56db\u5a18\u84e6\u7136\u626c\u7709\u8bf4\u9053\uff1a\u201c\u603b\u4f1a\u6709\u8fd9\u4e48\u4e00\u5927\uff01\u201d\u5173\u4e1c\u56db\u4fa0\uff0c\u76f8\u987e\u60ca \u8be7\uff0c\u72ec\u81c2\u8001\u5c3c\u5374\u8f7b\u8f7b\u8bf4\u4e86\u53e5\uff1a\u201c\u58ee\u5fd7\u53ef\u5609\uff0c\u4f46\u8fd8\u8981\u80c6\u5927\u5fc3\u7ec6\u3002\u201d \u3000\u3000\u6d59\u4e1c\u540d\u5112\u4e25\u6d2a\u9035\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f84\u5973\uff0c\u4f60\u7956\u7236\u8457\u4e66\u7acb\u8bf4\uff0c\u53cd\u864f\u6518\u5937\uff0c\u6240\u63ed\u7684\u662f\u5802\u5802\u6b63\u6b63\u4e4b \u65d7\uff0c\u6211\u4eec\u8981\u9010\u6ee1\u4eba\u51fa\u5173\uff0c\u6050\u4e0d\u662f\u8346\u8f72\u8981\u79bb\u4e4b\u884c\uff0c\u6240\u80fd\u6d4e\u4e8b\u3002\u201d\u8981\u79bb\u8346\u8f72\u662f\u6625\u79cb\u6218\u56fd\u65f6\u7684\u4fa0 \u58eb\uff0c\u8346\u8f72\u523a\u79e6\u738b\uff0c\u8981\u79bb\u523a\u5e86\u5fcc\uff0c\u90fd\u662f\u540d\u4f20\u5343\u8f7d\u7684\u6e38\u4fa0\u884c\u4e8b\u3002\u4e25\u6d2a\u8fd9\u6b64\u8bf4\uff0c\u610f\u601d\u662f\u4e0d\u8d5e\u6210\u7528\u6697 \u6740\u7684\u624b\u6bb5\uff0c\u53bb\u89e3\u51b3\u56fd\u5bb6\u5927\u4e8b\u3002\u800c\u4e14\u542b\u6709\u8d2c\u6291\u6e38\u4fa0\u7684\u610f\u601d\u3002\u7384\u98ce\u542c\u4e86\u5927\u4e3a\u4e0d\u60a6\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u53ea\u6050\u5112\u751f\u7a7a\u8a00\uff0c\u4e5f\u65e0\u8865\u4e8e\u4e8b\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u7c89\u8138\u4e00\u7ea2\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u53d4\u7236\u6559\u8bad\u3002\u6211\u770b\u8fd8\u662f\u592a\u53f2\u516c\u8bf4\u7684\u6709\u7406\uff0c\u4ee5\u771f\u5112\u4e4b \u8bc6\uff0c\u914d\u4fa0\u58eb\u4e4b\u4e49\uff0c\u7136\u540e\u5927\u4e8b\u624d\u6709\u53ef\u4e3a\u3002\u201d\u5415\u56db\u5a18\u6240\u5f15\u7684\u8bdd\uff0c\u51fa\u4e8e\u53f8\u9a6c\u8fc1\uff08\u592a\u53f2\u516c\uff09\u300a\u53f2\u8bb0\u300b \u4e2d\u7684\u201c\u6e38\u4fa0\u5217\u4f20\u5e8f\u201d\u4e25\u6d2a\u9035\u542c\u4e86\uff0c\u62c8\u987b\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd9\u4e9b\u5e74\u6765\uff0c\u4e5f\u8fd8\u672a\u629b\u8352\u4e66\u672c\u3002\u201d\u5415\u56db \u5a18\u5477\u4e86\u53e3\u8336\uff0c\u4f4e\u63a0\u4e91\u9b13\uff0c\u5ffd\u5e7d\u5e7d\u95ee\u9053\uff1a\u201c\u5728\u5bbd\u53ef\u957f\u5927\u4e86\uff1f\u8fd8\u8ddf\u6211\u7238\u7238\u8bfb\u4e66\u5417\uff1f\u201d\u4e25\u6d2a\u9035\u9053\uff1a \u201c\u4ed6\u957f\u5f97\u6bd4\u4f60\u7238\u8fd8\u9ad8\u534a\u4e2a\u5934\u5462\uff01\u4ed6\u8bfb\u4e66\u6781\u52e4\uff0c\u8bf8\u5b50\u767e\u5bb6\uff0c\u65e0\u6240\u4e0d\u7aa5\uff0c\u770b\u6765\u5c06\u6765\u80fd\u4f20\u4f60\u7956\u7236\u8863 \u94b5\u7684\uff0c\u5c31\u662f\u4ed6\u4e86\u3002\u201d\u5510\u6653\u6f9c\u5728\u65c1\u8fb9\u542c\u5f97\u51fa\u795e\uff0c\u867d\u7136\u4e0d\u77e5\u201c\u5728\u5bbd\u201d\u662f\u8c01\uff0c\u542c\u5f97\u5415\u56db\u5a18\u5982\u6b64\u4eb2\u5207 \u7684\u8c08\u4ed6\uff0c\u5fc3\u4e2d\u5ffd\u5982\u7535\u6d41\u901a\u8fc7\uff0c\u6ee1\u4e0d\u662f\u5473\u513f\u3002 \u3000\u3000\u7384\u98ce\u9053\u957f\u62cd\u4e86\u62cd\u5510\u6653\u6f9c\u80a9\u5934\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5468\u5927\u4fa0\u662f\u6211\u51e0\u5341\u5e74\u8001\u53cb\uff0c\u4ed6\u628a\u4f60\u91cd\u6258\u4e8e\u6211\uff0c \u6211\u4e0d\u80fd\u4e0d\u7ba1\uff0c\u4f46\u6211\u4eec\u56db\u4eba\u6d41\u6d6a\u6c5f\u6e56\uff0c\u65b0\u8fd1\u53c8\u548c\u56db\u7687\u5b50\u4f5c\u5bf9\uff0c\u66f4\u4e0d\u80fd\u5b89\u5b9a\u4e0b\u6765\uff0c\u6559\u4f60\u6b66\u827a\u3002\u201d \u8bf4\u5230\u6b64\u5904\uff0c\u987f\u4e86\u4e00\u987f\uff0c\u5bf9\u72ec\u81c2\u8001\u5c3c\u8bf4\u9053\uff1a\u201c\u8fd8\u671b\u795e\u5c3c\u5ff5\u4ed6\u662f\u51cc\u5927\u4fa0\u5ae1\u7cfb\uff0c\u5c06\u4ed6\u6536\u4f5c\u5f1f\u5b50\u3002\u201d \u5510\u6653\u6f9c\u5927\u559c\uff0c\u8d76\u5fd9\u8fc7\u6765\u5c31\u8981\u78d5\u5934\uff0c\u72ec\u81c2\u8001\u5c3c\u5374\u4e0d\u5f85\u4ed6\u78d5\u5934\uff0c\u5c31\u4e00\u628a\u5c06\u4ed6\u6276\u8d77\u3002 \u3000\u3000\u72ec\u81c2\u8001\u5c3c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u5e74\u7eaa\u8001\u8fc8\uff0c\u6536\u4e86\u83b9\u513f\u4e4b\u540e\uff0c\u5df2\u53d1\u8a93\u4e0d\u518d\u6536\u5f92\u3002\u5317\u4e94\u7701\u8fd8\u6709\u4e00\u4f4d \u5927\u540d\u9f0e\u9f0e\u7684\u82f1\u96c4\uff0c\u7384\u98ce\u9053\u5144\u4f55\u4e0d\u5c06\u8fd9\u5b69\u5b50\u9001\u5230\u90a3\u91cc\uff1f\u201d\u7384\u98ce\u62cd\u638c\u8bf4\u9053\uff1a\u201c\u795e\u5c3c\u8bf4\u7684\u662f\u94c1\u638c\u795e \u5f39\u6768\u4ef2\u82f1\u4e48\uff1f\u201d\u72ec\u81c2\u8001\u5c3c\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u7384\u98ce\u4e00\u60f3\uff1a\u6768\u4ef2\u82f1\u6280\u827a\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\uff0c\u6b64\u756a\u592a\u884c \u5c71\u4e4b\u4f1a\uff0c\u4ed6\u5c45\u7136\u80fd\u5728\u53cc\u9b54\u638c\u4e0b\u9003\u8131\u51fa\u6765\uff0c\u53ef\u77e5\u5b9d\u5200\u672a\u8001\uff0c\u6280\u4e1a\u6709\u957f\u3002\u4ed6\u548c\u81ea\u5df1\u53c8\u662f\u51e0\u5341\u5e74\u6df1 \u4ea4\uff0c\u628a\u5510\u6653\u6f9c\u6258\u4ed8\u7ed9\u4ed6\uff0c\u6781\u4e3a\u5408\u9002\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u795e\u5c3c\u65e2\u4e0d\u80af\u6536\u5f92\uff0c\u90a3\u53ea\u6709\u9ebb\u70e6\u6768\u8001\u82f1\u96c4 \u4e86\u3002\u201d \u3000\u3000\u4e00\u5bbf\u6613\u8fc7\uff0c\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u5173\u4e1c\u56db\u4fa0\u548c\u5415\u56db\u5a18\u7b49\u4e0b\u4e86\u9099\u5c71\uff0c\u5206\u6210\u4e24\u8def\uff0c\u56db\u4fa0\u5e26\u4e86\u5510\u6653\u6f9c\u53bb \u6295\u6768\u4ef2\u82f1\uff0c\u5415\u56db\u5a18\u548c\u4e25\u6d2a\u9035\u56de\u5357\u65b9\u8001\u5bb6\uff0c\u5c71\u4e0b\u9053\u522b\uff0c\u5510\u6653\u6f9c\u5446\u5446\u7684\u770b\u7740\u5415\u56db\u5a18\u7edd\u5c18\u800c\u53bb\u3002\u5415 \u56db\u5a18\u5728\u9a6c\u4e0a\u626c\u624b\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u5f1f\uff0c\u8fc7\u51e0\u5e74\u6211\u5230\u4e1c\u5e73\u770b\u4f60\uff01\u201d \u3000\u3000\u6768\u4ef2\u82f1\u5bb6\u4f4f\u5c71\u4e1c\u4e1c\u5e73\u53bf\uff0c\u4e1c\u5e73\u53bf\u6709\u4e00\u4e2a\u5927\u6e56\u540d\u4e3a\u4e1c\u5e73\u6e56\uff0c\u6768\u5bb6\u80cc\u5c71\u9762\u6e56\uff0c\u671d\u8f89\u5915\u9634\uff0c\u98ce \u666f\u4f73\u4e3d\uff0c\u8fd9\u65e5\u5510\u6653\u6f9c\u968f\u5173\u4e1c\u56db\u4fa0\u6765\u5230\u6768\u5bb6\u5e84\u5916\uff0c\u4f46\u89c1\u5c71\u5ce6\u8d77\u4f0f\uff0c\u6e56\u6c34\u6676\u83b9\uff0c\u6e56\u6ee8\u67f3\u6811\u6210\u884c\uff0c \u5c71\u5c97\u79c0\u8349\u6ca1\u80eb\uff0c\u5510\u6653\u6f9c\u672a\u81f3\u6768\u5bb6\uff0c\u5df2\u81ea\u7231\u4e0a\u4e86\u8fd9\u4e2a\u5730\u65b9\u3002\u4e0a\u5230\u534a\u5c71\uff0c\u5ffd\u89c1\u51e0\u5ea7\u5e73\u623f\uff0c\u4f9d\u5c71\u5efa \u7b51\uff0c\u6811\u836b\u4e2d\u4e00\u5ea7\u5e73\u53f0\uff0c\u53f0\u4e0a\u4e00\u4e2a\u5973\u5b69\u5b50\u6b63\u5728\u7ec3\u6b66\uff0c\u624b\u6301\u4e00\u5f20\u5f39\u5f13\uff0c\u5c06\u5f39\u5b50\u6253\u4e0a\u534a\u5929\uff0c\u7136\u540e\u518d \u53d1\u5f39\u5b50\u4e0e\u5b83\u76f8\u649e\uff0c\u5f39\u5b50\u8d8a\u53d1\u8d8a\u591a\uff0c\u5728\u534a\u7a7a\u4e2d\u76f8\u4e92\u78b0\u649e\uff0c\u5b9b\u5982\u6d41\u661f\u8d76\u6708\uff0c\u5341\u5206\u597d\u770b\uff0c\u7384\u98ce\u8d5e \u9053\uff1a\u201c\u795e\u5f39\u7edd\u6280\uff0c\u5bb6\u5b66\u6e0a\u6e90\uff0c\u5c06\u95e8\u864e\u5973\uff0c\u540d\u4e0d\u865a\u4f20\uff01\u201d\u90a3\u5973\u5b69\u5b50\u56de\u8fc7\u5934\u6765\uff0c\u770b\u89c1\u5510\u6653\u6f9c\u5657\u55e4 \u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u5929\u665a\u4e0a\uff0c\u6ca1\u6709\u628a\u4f60\u5413\u6b7b\u5440\uff01\u201d\u7384\u98ce\u9053\u957f\u9053\uff1a\u201c\u67f3\u9752\uff0c\u4f60\u56de\u53bb\u544a\u8bc9\u7239\u7239\uff0c\u8bf4 \u5173\u4e1c\u56db\u4fa0\u6c42\u89c1\uff01\u201d\u90a3\u5973\u5b69\u5b50\u8fde\u7b11\u5e26\u8df3\u7684\u8dd1\u56de\u5bb6\u53bb\u4e86\u3002\u7384\u98ce\u9053\uff1a\u201c\u6768\u4ef2\u82f1\u819d\u4e0b\u65e0\u513f\uff0c\u53ea\u6b64\u4e00 \u5973\uff0c\u628a\u5979\u5b9d\u8d1d\u5f97\u4e86\u4e0d\u5f97\u3002\u201d\u67f3\u5148\u5f00\u9053\uff1a\u201c\u6211\u542c\u5c71\u4e1c\u6b66\u6797\u540c\u9053\u8bf4\uff0c\u6709\u4e2a\u5973\u795e\u7ae5\u53eb\u6768\u67f3\u9752\uff0c\u60f3\u5fc5 \u5c31\u662f\u5979\u4e86\u3002\u201d\u7384\u98ce\u9053\uff1a\u201c\u6b63\u662f\u3002\u5979\u7238\u7238\u559c\u7231\u6768\u67f3\uff0c\u6240\u4ee5\u7ed9\u5979\u8d77\u4e86\u8fd9\u53e4\u602a\u7684\u540d\u5b57\u3002\u201d\u8bf4\u8bdd\u4e4b \u95f4\uff0c\u6768\u4ef2\u82f1\u5df2\u8fce\u4e86\u51fa\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u98ce\u628a\u4f60\u4eec\u5439\u6765\u7684\u5440\uff01\u201d\u8bf4\u7f62\u53c8\u5411\u67f3\u5148\u5f00\u548c\u9648\u5143 \u9738\u8c22\u8fc7\u90a3\u665a\u76f8\u52a9\u4e4b\u6069\uff0c\u6768\u67f3\u9752\u5728\u65c1\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u8fd9\u4f4d\u5c0f\u54e5\uff0c\u90a3\u665a\u6253\u4e86\u4e00\u5927\u628a\u98de\u8292\uff0c\u4f60\u4e5f\u8be5\u5411 \u4eba\u9053\u8c22\u5440\uff01\u201d\u6768\u4ef2\u82f1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u8001\u6e56\u6d82\u4e86\uff0c\u8fd9\u4f4d\u5c0f\u54e5\u7684\u6697\u5668\u6253\u5f97\u4e0d\u574f\uff01\u201d\u7384\u98ce\u4f7f\u4e86\u4e00\u4e2a \u773c\u8272\uff0c\u5510\u6653\u6f9c\u6251\u901a\u8dea\u5012\uff0c\u53e9\u4e86\u4e2a\u54cd\u5934\uff0c\u626c\u4ef2\u82f1\u8fde\u5fd9\u62c9\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u7384\u98ce \u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u5b64\u9732\u65e0\u4f9d\uff0c\u6c42\u8001\u54e5\u6536\u4ed6\u4e3a\u5f92\u3002\u201d\u6768\u4ef2\u82f1\u76b1\u76b1\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u56de\u53bb\u518d\u8bf4\uff01\u201d \u3000\u3000\u6768\u4ef2\u82f1\u628a\u4f17\u4eba\u8fce\u56de\u5bb6\u4e2d\u4e4b\u540e\uff0c\u628a\u7384\u98ce\u62c9\u8fc7\u4e00\u8fb9\uff0c\u8c08\u4e86\u597d\u4e45\uff0c\u8fd9\u624d\u56de\u8fc7\u5934\u6765\u5bf9\u5510\u6653\u6f9c\u9053\uff1a \u201c\u4f60\u628a\u4ee5\u524d\u5b66\u8fc7\u7684\u529f\u592b\u6f14\u7ed9\u6211\u770b\u770b\uff01\u201d\u5510\u6653\u6f9c\u89e3\u4e0b\u6e38\u9f99\u5b9d\u5251\uff0c\u6b20\u8eab\u884c\u4e86\u4e00\u793c\uff0c\u628a\u8ffd\u98ce\u5251\u6cd5\u65bd \u5c55\u5f00\u6765\uff0c\u53ea\u89c1\u51b7\u7535\u7cbe\u8292\uff0c\u6ee1\u5ead\u98d8\u8679\u3002\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u597d\uff0c\u884c\u4e86\uff01\u201d\u6768\u67f3\u9752\u77aa\u7740\u4e00\u53cc\u5c0f\u773c\uff0c\u76ef\u7740 \u90a3\u628a\u6e38\u9f99\u5b9d\u5251\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u51ed\u7740\u4f60\u90a3\u665a\u7684\u4e00\u628a\u98de\u8292\u548c\u8fd9\u624b\u8ffd\u98ce\u5251\u6cd5\uff0c\u6211\u6536\u4f60\u4e3a\u5f92\uff01\u201d\u5510\u6653\u6f9c\u5927\u559c\uff0c\u5f53 \u7740\u5173\u4e1c\u56db\u4fa0\u4e4b\u9762\uff0c\u606d\u606d\u656c\u656c\u7684\u884c\u4e86\u62dc\u5e08\u4e4b\u793c\u3002\u7384\u98ce\u4e3e\u624b\u5411\u6768\u4ef2\u82f1\u9053\u8d3a\uff0c\u8bf4\u9053\uff1a\u201c\u5f92\u62e9\u5e08\u5e08\u4e5f \u62e9\u5f92\uff0c\u5927\u54e5\uff0c\u8fd9\u4e2a\u5f92\u513f\uff0c\u6211\u62c5\u4fdd\u4f60\u79f0\u5fc3\u6ee1\u610f\uff01\u201d\u6768\u4ef2\u82f1\u7b11\u4e86\u4e00\u7b11\uff0c\u5ffd\u7136\u6b63\u8272\u5bf9\u5510\u6653\u6f9c\u9053\uff1a \u201c\u6211\u5d69\u9633\u95e8\u4e0b\uff0c\u6212\u5f8b\u7d20\u4e25\uff0c\u73b0\u5728\u6211\u5c06\u5341\u4e8c\u6212\u6761\uff0c\u9010\u6761\u5ff5\u7ed9\u4f60\u542c\uff0c\u4f60\u8981\u8be6\u7ec6\u5fd6\u5ea6\uff0c\u82e5\u4e0d\u4f9d\u5f97\uff0c \u65e9\u65e9\u51fa\u58f0\uff0c\u6211\u4e0d\u5f3a\u4f60\u3002\u201d\u5510\u6653\u6f9c\u5782\u624b\u65c1\u7acb\uff0c\u542c\u4ed6\u5ff5\u9053\uff1a\u201c\u7b2c\u4e00\u6761\u4e0d\u8bb8\u5978\u6deb\u5077\u76d7\uff01\u201d\u5510\u6653\u6f9c\u70b9 \u4e86\u70b9\u5934\uff0c\u6768\u4ef2\u82f1\u7ee7\u7eed\u5ff5\u9053\uff1a\u201c\u7b2c\u4e8c\u6761\u4e0d\u8bb8\u5356\u53cb\u6c42\u8363\uff0c\u7b2c\u4e09\u6761\u4e0d\u8bb8\u6043\u5f3a\u51cc\u5f31\uff0c\u7b2c\u56db\u6761\u4e0d\u8bb8\u6cbe\u5b98 \u8fd1\u5e9c\uff0c\u5d69\u9633\u95e8\u4e0b\u4e0d\u51c6\u4e0e\u5b98\u5e9c\u4e2d\u4eba\u5f80\u6765\uff0c\u4f60\u4f9d\u5f97\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u4e49\u7236\u5468\u5927\u4fa0\u5c31\u662f\u7ed9\u6e05\u5ef7 \u6b66\u58eb\u5bb3\u6b7b\u7684\uff0c\u6211\u6068\u5b98\u5e9c\u4e2d\u4eba\u6709\u5982\u523a\u9aa8\uff01\u201d\u6768\u4ef2\u82f1\u53c8\u7ee7\u7eed\u5ff5\u9053\uff1a\u201c\u7b2c\u4e94\u6761\u4e0d\u8bb8\u7ed3\u6d3e\u6597\u6bb4\uff0c\u7b2c\u516d \u6761\u4e0d\u8bb8\u9157\u9152\u95f9\u4e8b\u2026\u2026\u201d\u4e00\u76f4\u5ff5\u4e0b\u53bb\uff0c\u5ff5\u5230\u7b2c\u5341\u4e8c\u6761\u9053\uff1a\u201c\u8fd9\u4e00\u6761\u6700\u5173\u91cd\u8981\uff0c\u4e0d\u8bb8\u6b3a\u5e08\u706d\u7956\uff01 \u4ec0\u4e48\u4e8b\u60c5\u90fd\u4e0d\u8bb8\u7792\u7740\u5e08\u5085\uff0c\u4e00\u5207\u8981\u8bf4\u771f\u8bdd\uff0c\u66f4\u4e0d\u8bb8\u52fe\u7ed3\u532a\u4eba\uff0c\u4fae\u8fb1\u5c0a\u957f\u3002\u72af\u6b64\u6761\u8005\uff0c\u8f7b\u5219\u5e9f \u53bb\u6b66\u529f\uff0c\u91cd\u5219\u4e94\u9a6c\u5206\u5c38\uff0c\u4f60\u4f9d\u5f97\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u4e00\u9635\u8e0c\u8e87\uff0c\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u6211\u77e5\u4f60\u6765\u5386\u6709\u4e9b\u5947 \u602a\uff0c\u4f60\u4ee5\u5f80\u7684\u6765\u5386\uff0c\u6211\u4e0d\u7406\u4f60\uff0c\u4eca\u540e\u4e00\u5207\uff0c\u5374\u4e0d\u8bb8\u5bf9\u6211\u6709\u4e00\u4e8b\u6b3a\u7792\uff01\u201d\u5510\u6653\u6f9c\u53e9\u5934\u9053\uff1a\u201c\u65e2 \u5f80\u6765\u5386\uff0c\u6211\u81ea\u5df1\u4e5f\u4e0d\u6e05\u695a\uff0c\u4eca\u540e\u4e00\u5207\u81ea\u5f53\u542c\u547d\u6069\u5e08\u3002\u201d\u6768\u4ef2\u82f1\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8d77\u6765\u5427\uff01\u51e0 \u5341\u5e74\u6765\u6211\u4ece\u672a\u6536\u5f92\uff0c\u4ece\u4eca\u540e\u4f60\u5c31\u662f\u5979\u7684\u5e08\u5144\uff01\u67f3\u9752\uff0c\u8fc7\u6765\u62dc\u89c1\u5e08\u5144\uff01\u201d\u6768\u67f3\u9752\u62bf\u7740\u5634\u9053\uff1a \u201c\u6211\u8981\u548c\u4ed6\u8bd5\u4e00\u8bd5\u62db\uff0c\u4ed6\u82e5\u8d62\u5f97\u6211\uff0c\u6211\u5c31\u53eb\u4ed6\u5e08\u5144\uff01\u201d\u5510\u6653\u6f9c\u5fd9\u9053\uff1a\u201c\u6211\u672c\u9886\u4f4e\u5fae\uff0c\u5982\u4f55\u662f \u5e08\u59b9\u2014\u2014\u4e0d\uff0c\u5e08\u59d0\u5bf9\u624b\uff0c\u4e14\u6211\u5165\u95e8\u5728\u540e\uff0c\u66f4\u4e0d\u6562\u5f53\u3002\u201d\u6768\u4ef2\u82f1\u77aa\u773c\u9053\uff1a\u201c\u67f3\u9752\uff0c\u80e1\u8bf4\u516b\u9053\uff0c \u4e0d\u6015\u5e08\u4f2f\u4eec\u7b11\u8bdd\u4e48\uff1f\u6653\u6f9c\uff0c\u4f60\u4eca\u5e74\u51e0\u5c81\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5341\u516d\u3002\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u6bd4\u67f3\u9752\u5927\u4e24 \u5c81\uff0c\u6211\u95e8\u4e0b\u6392\u884c\u4e0d\u8bba\u5165\u95e8\u524d\u540e\uff0c\u53ea\u4f9d\u957f\u5e7c\u4e4b\u522b\u3002\u67f3\u9752\uff0c\u8fc7\u6765\u78d5\u5934\uff0c\u4ee5\u540e\u8981\u542c\u5e08\u5144\u7684\u8bdd\uff01\u201d\u6768 \u67f3\u9752\u4f38\u957f\u820c\u5c16\uff0c\u5410\u5410\u820c\u9053\uff1a\u201c\u8fd8\u8981\u78d5\u5934\uff01\u201d\u6768\u4ef2\u82f1\u559d\u9053\uff1a\u201c\u5feb\u78d5\u5934\uff01\u201d\u5510\u6653\u6f9c\u6025\u5fd9\u6276\u8d77\uff0c\u6768 \u67f3\u9752\u628a\u624b\u4e00\u6454\uff0c\u5510\u6653\u6f9c\u51fa\u5176\u4e0d\u610f\uff0c\u51e0\u4e4e\u7ed9\u5979\u6454\u5012\uff0c\u6768\u4ef2\u82f1\u5bf9\u7384\u98ce\u7b11\u9053\uff1a\u201c\u9053\u957f\u4e0d\u8981\u89c1\u7b11\uff0c\u6211 \u8fd9\u4e2a\u5973\u513f\u81ea\u5c0f\u6ca1\u6709\u5988\u5988\uff0c\u662f\u6211\u628a\u5979\u5ba0\u574f\u4e86\uff0c\u5341\u56db\u5c81\u4e86\uff0c\u8fd8\u8fd9\u6837\u5b69\u5b50\u6c14\uff01\u201d\u8bf4\u4e86\u4e4b\u540e\uff0c\u53c8\u5bf9\u5510 \u6653\u6f9c\u9053\uff1a\u201c\u672c\u95e8\u6b66\u529f\u6700\u91cd\u624e\u6839\u57fa\u7684\u529f\u592b\uff0c\u6211\u770b\u4f60\u5251\u672f\u867d\u6709\u53ef\u89c2\uff0c\u6839\u57fa\u5374\u662f\u4e0d\u591f\uff0c\u660e\u65e5\u8d77\u4f60\u5c31 \u8ddf\u6211\u5b66\u7ad9\u693f\u3001\u5410\u7eb3\u3001\u8170\u817f\u3001\u6865\u624b\u7b49\u57fa\u672c\u529f\u592b\uff0c\u5faa\u5e8f\u6e10\u8fdb\uff0c\u4e0d\u5fc5\u8d2a\u591a\uff0c\u4f60\u662f\u8ddf\u8fc7\u540d\u5e08\u7684\u4e86\uff0c\u4f60 \u5bf9\u6211\u6240\u6559\uff0c\u6709\u4ec0\u4e48\u610f\u89c1\u5417\uff1f\u201d\u5510\u6653\u6f9c\u5ffd\u9053\uff1a\u201c\u6211\u60f3\u767d\u5929\u4e60\u6b66\uff0c\u665a\u4e0a\u5b66\u6587\uff0c\u591a\u5c11\u8bfb\u4e00\u70b9\u4e66\uff01\u201d \u5173\u4e1c\u56db\u4fa0\uff0c\u76f8\u987e\u6115\u7136\uff0c\u6b66\u6797\u4e2d\u6536\u5f92\u4f20\u827a\uff0c\u4ece\u6765\u5c31\u662f\u53ea\u8bb2\u62f3\u811a\u5175\u5203\u7684\u529f\u592b\uff0c\u5bf9\u6587\u7ec9\u7ec9\u7684\u5112\u751f\uff0c \u53ef\u4e0d\u653e\u5728\u773c\u5185\uff0c\u4e5f\u4ece\u6765\u6ca1\u5f92\u5f1f\u4f1a\u63d0\u51fa\u8fd9\u6837\u7684\u8981\u6c42\uff0c\u6768\u4ef2\u82f1\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u5730\u54c8\u54c8\u5927\u7b11\uff01 \u3000\u3000\u6768\u4ef2\u82f1\u62c8\u987b\u7b11\u9053\uff1a\u201c\u4f60\u8a00\u6b63\u5408\u6211\u610f\uff01\u884c\uff01\u4f60\u8fd9\u4e2a\u5f92\u5f1f\uff0c\u5f88\u5bf9\u6211\u7684\u610f\u601d\uff01\u7384\u98ce\u9053\u5144\uff0c\u5b66\u6b66 \u7684\u4eba\uff0c\u5e38\u5931\u4e4b\u66b4\u8e81\uff0c\u6211\u5c11\u5e74\u65f6\u6c14\u76db\uff0c\u4e0d\u77e5\u95ef\u8fc7\u591a\u5c11\u7978\u3002\u6211\u8fd9\u5b9d\u8d1d\u5973\u513f\uff0c\u4f1a\u4e86\u4e00\u70b9\u6b66\u827a\uff0c\u5c31\u50cf \u4e2a\u91ce\u4e2b\u5934\u4f3c\u7684\uff0c\u53ea\u77e5\u9a6c\u4e0a\u9a6c\u4e0b\uff0c\u62c8\u5200\u5f04\u67aa\uff0c\u4e0d\u61c2\u4e00\u70b9\u793c\u4eea\u3002\u6211\u770b\u5440\uff0c\u5979\u5c06\u6765\u627e\u5a46\u5bb6\u90fd\u5f88\u96be\u3002 \u6211\u65e9\u5c31\u60f3\u8bf7\u4eba\u6559\u5979\u8bfb\u4e00\u70b9\u4e66\uff0c\u6539\u4e00\u6539\u5979\u7684\u91ce\u6027\u3002\u6653\u6f9c\u613f\u610f\u6587\u6b66\u53cc\u4fee\uff0c\u90a3\u662f\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\u3002\u6211 \u6709\u4e2a\u5802\u5f1f\uff0c\u867d\u7136\u662f\u4e2a\u843d\u7b2c\u79c0\u624d\uff0c\u5374\u4e5f\u9887\u901a\u6587\u58a8\u3002\u660e\u5929\u6211\u5c31\u628a\u4ed6\u627e\u6765\u6559\u4ed6\u4eec\u5e08\u5144\u59b9\u5ff5\u4e66\u3002\u201d\u7384 \u98ce\u542c\u4e86\uff0c\u5185\u5fc3\u6697\u7b11\uff0c\u60f3\u9053\uff1a\u4f60\u8fd9\u5973\u513f\uff0c\u5206\u660e\u662f\u4f60\u5ba0\u574f\u7684\uff0c\u4e0e\u8bfb\u4e66\u4f55\u5173\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u6536\u4e86\u5f92\u5f1f\uff0c\u6ee1\u5fc3\u559c\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u9752\u513f\uff0c\u4f60\u5e26\u5e08\u5144\u5468\u56f4\u8d70\u8d70\u3002\u201d\u4ed6\u4e0e\u5173\u4e1c\u56db\u4fa0\u6d77\u9614 \u5929\u7a7a\u7684\u8c08\u4e86\u4e00\u9635\uff0c\u4e34\u5230\u56db\u4fa0\u8981\u544a\u8f9e\u65f6\uff0c\u624d\u8fdb\u53bb\u627e\u5510\u6653\u6f9c\u3002\u627e\u5230\u5185\u8fdb\u5ead\u9662\uff0c\u95fb\u5f97\u6325\u62f3\u64e6\u638c\u4e4b \u58f0\uff0c\u77aa\u76ee\u4e00\u770b\uff0c\u53ea\u89c1\u81ea\u5df1\u7684\u5973\u513f\uff0c\u8fd0\u638c\u5982\u98ce\uff0c\u628a\u5510\u6653\u6f9c\u903c\u5f97\u6b65\u6b65\u540e\u9000\uff01 \u3000\u3000\u539f\u6765\u6768\u67f3\u9752\u5c0f\u5b69\u5fc3\u6027\uff0c\u62dc\u4e86\u5e08\u5144\uff01\u5fc3\u4e0d\u670d\uff0c\u7275\u4ed6\u624b\u9053\uff1a\u201c\u5582\uff0c\u6211\u548c\u4f60\u5230\u540e\u9762\u7684\u5ead\u5b50 \u53bb\uff01\u201d\u5510\u6653\u6f9c\u4e0d\u6562\u4e0d\u4f9d\uff0c\u5230\u4e86\u5ead\u5b50\uff0c\u6768\u67f3\u9752\u5ffd\u9053\uff1a\u201c\u5582\uff0c\u501f\u4f60\u7684\u5b9d\u5251\u6765\u770b\u3002\u201d\u5510\u6653\u6f9c\u4e00\u9635\u8e0c \u8e87\uff0c\u6768\u67f3\u9752\u9053\uff1a\u201c\u5440\uff0c\u4f60\u8fd9\u4eba\u600e\u7684\u4e00\u70b9\u4e5f\u4e0d\u723d\u5feb\uff0c\u53c8\u4e0d\u662f\u8981\u4f60\u7684\u3002\u201d\u5510\u6653\u6f9c\u65e0\u5948\uff0c\u5c06\u5251\u89e3 \u4e86\uff0c\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u5e08\u59b9\u5c0f\u5fc3\uff0c\u8fd9\u5251\u950b\u5229\u5f97\u5f88\uff0c\u4e0d\u8981\u7ed9\u5b83\u78b0\u4f24\u4e86\u624b\uff01\u201d\u6768\u67f3\u9752\u54fc\u4e86\u4e00\u58f0\uff0c\u62d4\u5251 \u821e\u4e86\u4e00\u9635\uff0c\u51fa\u624b\u867d\u7136\u4e0d\u53ca\u8ffd\u98ce\u5251\u6cd5\u7684\u8fc5\u75be\uff0c\u5374\u4e5f\u5982\u94f6\u86c7\u4e71\u63a3\uff0c\u7d2b\u7535\u76d8\u7a7a\uff0c\u751a\u4e3a\u4e86\u5f97\u3002\u5510\u6653\u6f9c \u8d5e\u9053\uff1a\u201c\u5e08\u59b9\u771f\u884c\uff0c\u5341\u516b\u822c\u6b66\u827a\u4ef6\u4ef6\u7686\u80fd\uff01\u201d\u6768\u67f3\u9752\u53c8\u54fc\u4e86\u4e00\u58f0\uff0c\u677f\u7740\u8138\u8bf4\u9053\uff1a\u201c\u8c01\u8981\u4f60\u4e71 \u6234\u9ad8\u5e3d\uff0c\u5582\uff0c\u6211\u7239\u7239\u8bf4\u4f60\u5251\u6cd5\u5f88\u597d\uff0c\u6211\u5012\u8981\u51ed\u7740\u4e00\u53cc\u8089\u638c\uff0c\u9886\u6559\u9886\u6559\u4f60\u7684\u5251\u62db\uff01\u201d\u5510\u6653\u6f9c\u6025 \u5fd9\u9053\uff1a\u201c\u5e08\u59b9\u6b66\u529f\u9ad8\u5f3a\uff0c\u611a\u5144\u7518\u62dc\u4e0b\u98ce\uff0c\u4e0d\u5fc5\u8bd5\u4e86\u3002\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u6162\u7740\uff0c\u6211\u8fd8\u672a\u8bf4\u5b8c\u5462\uff01 \u6211\u82e5\u8f93\u4e86\uff0c\u5411\u4f60\u518d\u78d5\u4e09\u4e2a\u54cd\u5934\uff0c\u4f60\u82e5\u8f93\u4e86\uff0c\u53ef\u5f97\u628a\u8fd9\u628a\u5251\u7ed9\u6211\uff01\u597d\uff01\u4f60\u5148\u628a\u5251\u62ff\u56de\u53bb\uff0c\u63a5 \u7740\uff01\u201d\u9752\u950b\u5012\u8f6c\uff0c\u5411\u5510\u6653\u6f9c\u63b7\u6765\uff0c\u56b7\u9053\uff1a\u201c\u4f60\u63a5\u597d\u4e86\uff0c\u600e\u4e48\u6837\uff0c\u4eae\u62db\u52a8\u624b\u5440\uff01\u201d\u5510\u6653\u6f9c\u6025\u5f97 \u6ee1\u5934\u5927\u6c57\uff0c\u8fde\u8fde\u6447\u624b\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u6210\uff1f\u8fd9\u600e\u4e48\u6210\uff1f\u201d\u6768\u67f3\u9752\u51b7\u7b11\u9053\uff1a\u201c\u54fc\uff0c\u77a7\u4f60\u8fd9\u5c0f\u5bb6\u6837\uff0c \u4f60\u5c31\u662f\u6015\u8f93\u6389\u8fd9\u628a\u5b9d\u5251\uff0c\u4f60\u5f53\u6211\u4e0d\u77e5\u9053\u4f60\u7684\u5fc3\u601d\u5440\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u672c\u6765\u4e5f\u662f\u4e2a\u673a\u7075\u7684\u5b69\u5b50\uff0c\u4f46\u6b64\u523b\u7ed9\u8fd9\u4f4d\u5e08\u59b9\u5f04\u5f97\u6beb\u65e0\u529e\u6cd5\uff0c\u7a98\u8feb\u5f02\u5e38\uff0c\u8fd9\u628a\u5b9d\u5251 \u4e43\u5468\u9752\u6240\u4f20\u4e4b\u7269\uff0c\u53c8\u4e0d\u80fd\u9001\u7ed9\u6768\u67f3\u9752\uff0c\u5f04\u5f97\u4ed6\u6ee1\u5934\u5927\u6c57\uff0c\u7ad9\u5728\u90a3\u513f\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u3002\u6768\u67f3\u9752\u903c\u8fd1 \u4e24\u6b65\uff0c\u53cc\u638c\u4e00\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d\u5510\u6653\u6f9c\u54ac\u4e86\u54ac\u7259\uff0c\u63d2\u5251\u5f52\u9798\uff0c\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u8fd9 \u628a\u5251\u9001\u7ed9\u4f60\uff01\u201d\u58f0\u8c03\u98a4\u6296\uff0c\u6768\u67f3\u9752\u79c0\u7709\u4e00\u7ad6\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u54fc\uff0c\u8c01\u5e0c\u7f55\u4f60\u9001\uff01\u5feb\u5feb\u4eae\u5251\uff0c\u6211 \u82e5\u4e0d\u80fd\u7a7a\u624b\u593a\u4f60\u624b\u4e0a\u5229\u5203\uff0c\u7ed9\u4f60\u78d5\u5934\uff01\u201d\u5510\u6653\u6f9c\u8fde\u8fde\u9000\u540e\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u611a\u5144\u4e07\u4e07\u4e0d\u6562\uff01\u201d\u51b7 \u7b11\u58f0\u4e2d\u6768\u67f3\u9752\u5ffd\u7136\u547c\u7684\u4e00\u638c\uff0c\u6253\u5c06\u8fc7\u6765\uff01 \u3000\u3000\u5510\u6653\u6f9c\u95ea\u8eab\u4e00\u907f\uff0c\u6ca1\u6709\u907f\u5f00\uff0c\u816e\u5e2e\u4e0a\u7adf\u7136\u6328\u4e86\u4e00\u638c\uff0c\u706b\u8fa3\u8fa3\u7684\u75db\uff0c\u5510\u6653\u6f9c\u51e0\u66fe\u53d7\u8fc7\u8fd9\u6837 \u4fae\u8fb1\uff0c\u4e0d\u7531\u5f97\u6c14\u4e0a\u6765\uff01\u6768\u67f3\u9752\u53c8\u8fde\u53d1\u6570\u638c\uff0c\u638c\u638c\u51f6\u72e0\u3002\u5510\u6653\u6f9c\u95ea\u5c55\u817e\u632a\u4e09\u4e0b\uff0c\u80a9\u5934\u53c8\u7ed9\u626b\u4e86 \u4e00\u638c\uff0c\u5e78\u5979\u5e74\u5c0f\u529b\u5f31\uff0c\u8981\u4e0d\u8fd9\u4e24\u638c\u5c31\u5403\u4e0d\u6d88\uff0c\u5510\u6653\u6f9c\u63b7\u5251\u843d\u5730\uff0c\u54b3\u4e00\u58f0\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u611a\u5144\u7ed9 \u6324\u5f97\u6ca1\u6cd5\uff0c\u5c31\u966a\u5e08\u59b9\u73a9\u73a9\u5427\uff0c\u5e08\u59b9\uff0c\u4f60\u53ef\u5f97\u624b\u4e0b\u7559\u60c5\u3002\u201d\u6768\u67f3\u9752\u5a07\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u5230\u5e95\u7ed9\u903c \u51fa\u771f\u7ae0\u6765\u4e86\uff01\u201d\u5176\u5b9e\uff0c\u5979\u5b8c\u5168\u662f\u5c0f\u5b69\u5b50\u813e\u6027\uff0c\u89c1\u8fd9\u4f4d\u65b0\u6765\u7684\u5e08\u5144\u7740\u6025\uff0c\u5c31\u8d8a\u53d1\u8981\u9017\u4ed6\u800d\uff0c\u5012 \u5e76\u4e0d\u4e00\u5b9a\u8981\u4ed6\u7684\u5b9d\u5251\u3002\u5510\u6653\u6f9c\u5916\u67d4\u5185\u521a\uff0c\u6328\u4e86\u4e24\u638c\uff0c\u5374\u52a8\u4e86\u771f\u6c14\uff0c\u8863\u8896\u4e00\u62c2\uff0c\u53cc\u81c2\u4e00\u5206\uff0c\u8eab \u968f\u638c\u8d70\uff0c\u547c\u547c\u4e24\u638c\uff0c\u6253\u5c06\u51fa\u53bb\uff0c\u6768\u67f3\u9752\u7b11\u9053\uff1a\u201c\u597d\u72e0\u7684\u62db\u6570\uff01\u201d\u8eab\u5f62\u5fae\u6643\uff0c\u7acb\u523b\u53cd\u638c\u622a\u51fb\u5510 \u6653\u6f9c\u53f3\u81c2\uff0c\u5510\u6653\u6f9c\u5de6\u638c\u5f80\u4e0a\u4e00\u62db\uff0c\u6768\u67f3\u9752\u53d8\u62db\u5947\u5feb\uff0c\u53f3\u624b\u201c\u91d1\u9f99\u63a2\u722a\u201d\u5530\u7684\u53c8\u671d\u5510\u6653\u6f9c\u9762\u95e8 \u6293\u5230\uff01 \u3000\u3000\u5510\u6653\u6f9c\u672c\u60f3\u8fd8\u5979\u4e00\u70b9\u989c\u8272\uff0c\u6740\u5979\u4e00\u4e2a\u4e0b\u9a6c\u5a01\uff0c\u6559\u5979\u77e5\u96be\u800c\u9000\u3002\u4e0d\u6599\u6768\u4ef2\u82f1\u53f7\u79f0\u94c1\u638c\u795e \u5f39\uff0c\u5728\u638c\u6cd5\u4e0a\u5b9e\u6709\u8fc7\u4eba\u7684\u6280\u4e1a\uff0c\u6768\u67f3\u9752\u81ea\u5c0f\u8ddf\u4ece\u7236\u4eb2\u5b66\u6b66\uff0c\u5e74\u7eaa\u867d\u8f7b\uff0c\u638c\u6cd5\u5374\u662f\u4e0a\u4e58\u3002\u89c1\u5510 \u6653\u6f9c\u4f3c\u4e4e\u52a8\u4e86\u771f\u6c14\uff01\u53d1\u7684\u5168\u662f\u8fdb\u624b\u62db\u6570\uff0c\u51b7\u7b11\u6570\u58f0\uff0c\u7acb\u523b\u4e5f\u5c55\u8eab\u624b\uff0c\u53cc\u638c\u500f\u4e0a\u500f\u4e0b\u3002\u4ea4\u4e92\u6253 \u51fa\uff0c\u5979\u7684\u62db\u6570\u65e2\u5de7\u6ed1\uff0c\u53c8\u77dc\u614e\uff0c\u65e2\u7cbe\u7ec6\uff0c\u53c8\u5927\u80c6\uff0c\u5ffd\u653b\u5ffd\u5b88\uff0c\u500f\u8fdb\u500f\u9000\uff0c\u53d8\u5316\u591a\u7aef\uff0c\u4e0d\u4f4f\u624b \u7684\u653b\u51fb\u4e0a\u6765\uff0c\u5510\u6653\u6f9c\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u4e0d\u6599\u5979\u7684\u638c\u6cd5\u771f\u4e2a\u7cbe\u5947\u3002\u4e24\u4eba\u8f97\u8f6c\u6597\u4e86\u6570\u5341\u56de\u5408\uff0c\u5510\u6653 \u6f9c\u6e10\u6e10\u4e0d\u652f\u3002\u6768\u67f3\u9752\u4e0d\u4f4f\u53e3\u7684\u53d6\u7b11\u9053\uff1a\u201c\u5e08\u5144\uff0c\u600e\u4e48\u5566\uff1f\u7d2f\u4e86\u5417\uff1f\u628a\u5251\u4ea4\u7ed9\u6211\u5427\uff0c\u8fd9\u662f\u6211\u7684 \u5f69\u7269\uff0c\u6211\u53ef\u4e0d\u9886\u4f60\u7684\u60c5\u3002\u201d\u5510\u6653\u6f9c\u8fd9\u65f6\u6068\u5979\u5201\u86ee\uff0c\u5fc3\u4e2d\u4e0d\u613f\u5c06\u5468\u9752\u6240\u9001\u7684\u6e38\u9f99\u5b9d\u5251\u8f6c\u9001\u7ed9 \u5979\uff0c\u54ac\u7740\u7259\u6839\u652f\u6491\uff0c\u5fc3\u60f3\uff1a\u4f60\u8fd8\u662f\u4e2a\u5c0f\u5973\u5b69\u5b50\uff0c\u770b\u4f60\u6709\u591a\u5927\u6c14\u529b\u3002\u6253\u4e45\u4e86\uff0c\u4f60\u53ef\u62b5\u53d7\u4e0d\u4f4f\u3002 \u4ed6\u638c\u6cd5\u4e00\u53d8\uff0c\u7d27\u5b88\u95e8\u6237\uff0c\u60f3\u628a\u5979\u62d6\u7d2f\uff0c\u4e0d\u6599\u53c8\u62c6\u4e86\u51e0\u5341\u62db\uff0c\u6768\u67f3\u9752\u4e0d\u4f46\u6c14\u529b\u4e0a\u4f9d\u7136\u652f\u6301\u5f97 \u4f4f\uff0c\u800c\u4e14\u638c\u6cd5\u8d8a\u53d1\u51cc\u5389\uff0c\u556a\u3001\u556a\u4e24\u58f0\uff0c\u5510\u6653\u6f9c\u80f8\u819b\u53c8\u4e2d\u4e24\u638c\uff0c\u867d\u8bf4\u6768\u67f3\u9752\u5e74\u8f7b\u529b\u5c0f\uff0c\u53ef\u4e5f\u75bc \u75db\u975e\u5e38\u3002\u5510\u6653\u6f9c\u53c8\u6c14\u53c8\u6025\uff0c\u8fde\u8fde\u540e\u9000\uff0c\u5728\u6b64\u65f6\uff0e\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\u6765\u5382\uff0c\u5510\u6653\u6f9c\u5982\u91ca\u91cd\u8d1f\uff0c\u614c \u5fd9\u5f80\u5916\u4e00\u7a9c\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u5085\uff01\u5e08\u5085\uff01\u201d \u3000\u3000\u6768\u4ef2\u82f1\u9762\u8272\u4e00\u7ef7\uff0c\u65a5\u9053\uff1a\u201c\u9752\u513f\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8ddf\u5e08\u5144\u6253\u8d77\u6765\uff1f\u201d\u6768\u67f3\u9752\u563b\u563b\u7b11\u9053\uff1a\u201c\u5e08\u5144 \u9080\u6211\u548c\u4ed6\u8bd5\u62db\uff0c\u4f60\u8bf4\u8fc7\u561b\uff0c\u8981\u542c\u5e08\u5144\u7684\u8bdd\uff0c\u6240\u4ee5\u6211\u53ea\u597d\u966a\u4ed6\u52a8\u624b\u3002\u201d\u6768\u4ef2\u82f1\u773c\u89c1\u5510\u6653\u6f9c\u6253\u5f97 \u5341\u5206\u8ba4\u771f\uff0c\u4e0d\u4f3c\u513f\u620f\uff0c\u5c06\u4fe1\u5c06\u7591\uff0c\u5bf9\u5510\u6653\u6f9c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5e08\u59b9\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u5168\u4e0d\u61c2\u4e8b\uff0c\u4f60\u4e0d\u8981 \u4f38\u91cf\u5979\uff01\u201d\u5510\u6653\u6f9c\u5fd9\u9053\uff1a\u201c\u662f\u5e08\u59b9\u4e00\u5b9a\u8981\u903c\u6211\u8fc7\u62db\uff0c\u6324\u5f97\u6211\u6ca1\u6cd5\u5b50\u7684\uff01\u201d\u6768\u4ef2\u82f1\u6307\u7740\u5730\u4e0a\u7684 \u5b9d\u5251\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u8bb2\uff1f\u201d\u5510\u6653\u6f9c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u5e08\u59b9\u559c\u6b22\u8fd9\u628a\u5251\uff0c\u6211\u672c\u5f85\u9001\u7ed9\u5979\u2026\u2026\u201d\u6768 \u4ef2\u82f1\u52c3\u7136\u5927\u6012\uff0c\u65a5\u9053\uff1a\u201c\u9752\u513f\uff0c\u4f60\u8d8a\u6765\u8d8a\u80c6\u5927\u4e86\uff0c\u80e1\u4e71\u8981\u4eba\u4e1c\u897f\uff0c\u4f60\u77e5\u9053\u8fd9\u628a\u5251\u7684\u6765\u5386 \u5417\uff1f\u201d\u6768\u67f3\u9752\u54c7\u7684\u4e00\u58f0\uff0c\u54ed\u4e86\u8d77\u6765\uff0c\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u8c01\u5e0c\u7f55\u4ed6\u7684\u5b9d\u5251\uff1f\u201d\u5510\u6653\u6f9c\u5c34\u5c2c\u4e4b\u6781\uff0c\u795e \u8272\u4e0d\u5b89\u3002 \u3000\u3000\u5510\u6653\u6f9c\u8fde\u5fd9\u7ed9\u5979\u8fa9\u89e3\u9053\uff1a\u201c\u5e08\u59b9\u5e76\u6ca1\u6709\u8bf4\u8981\uff0c\u5979\u53ea\u8bf4\u5979\u559c\u6b22\u8fd9\u628a\u5b9d\u5251\uff0c\u8981\u7a7a\u624b\u548c\u6211\u8fc7 \u62db\u3002\u201d\u6768\u4ef2\u82f1\u77a7\u4ed6\u9762\u988a\u9752\u80bf\u4e86\u597d\u5927\u4e00\u5757\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u6837\uff0c\u4f60\u4eec\u4fbf\u6bd4\u638c\u6765\u4e86\u3002\u201d\u4e24\u4eba\u4f4e\u4e0b\u4e86 \u5934\uff0c\u4e0d\u6562\u56de\u7b54\uff0c\u6768\u4ef2\u82f1\u6700\u75bc\u7231\u5973\u513f\uff0c\u660e\u77e5\u662f\u5979\u4e0d\u5bf9\uff0c\u4fbf\u4e0d\u518d\u6df1\u7a76\u4e0b\u53bb\uff0c\u53ea\u5f97\u65a5\u9053\uff1a\u201c\u91ce\u4e2b \u5934\uff0c\u672c\u95e8\u6700\u91cd\u957f\u5e7c\u5c0a\u5351\u4e4b\u522b\uff0c\u4ed6\u867d\u4eca\u65e5\u5165\u95e8\uff0c\u5374\u662f\u4f60\u7684\u957f\u8f88\uff0c\u540e\u8f88\u5bf9\u957f\u8f88\uff0c\u52a1\u5fc5\u8981\u5c0a\u656c\u3002\u4ee5 \u540e\u4e0d\u53ef\u901e\u80fd\u6b3a\u957f\uff0c\u5c31\u662f\u5c06\u6765\u8981\u8bd5\u62db\uff0c\u4e5f\u53ea\u53ef\u70b9\u5230\u4e3a\u6b62\u3002\u53c8\u4e0d\u662f\u548c\u654c\u4eba\u640f\u6597\uff0c\u5e72\u561b\u597d\u50cf\u8981\u62fc\u4e2a \u4f60\u6b7b\u6211\u6d3b\u4f3c\u7684\uff01\u201d\u4e24\u4eba\u4f4e\u5934\u5e94\u4e86\u58f0\uff1a\u201c\u662f\u3002\u201d\u5510\u6653\u6f9c\u6ee1\u8179\u59d4\u5c48\uff0c\u9762\u8272\u9752\u767d\u3002\u6768\u4ef2\u82f1\u643a\u4ed6\u7684 \u624b\uff0c\u8bf4\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u4f60\u5e08\u59b9\u8fd8\u662f\u4e2a\u5c0f\u5b69\u5b50\uff0c\u4f60\u591a\u62c5\u5f85\u5979\u4e9b\u513f\u3002\u5173\u4e1c\u56db\u4fa0\u8981\u8d70\u4e86\uff0c\u4f60\u51fa\u53bb\u7ed9\u4ed6 \u4eec\u53e9\u5934\u9053\u522b\u3002\u201d\u5c06\u5510\u6653\u6f9c\u5e26\u51fa\u5916\u9762\uff0c\u5173\u4e1c\u56db\u4fa0\u89c1\u4ed6\u9762\u988a\u9752\u80bf\uff0c\u76f8\u987e\u5fae\u7b11\u3002\u5510\u6653\u6f9c\u5bf9\u56db\u4fa0\u9053\u4e86 \u6551\u547d\u4e4b\u6069\uff0c\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u6211\u5e7c\u906d\u5b64\u9732\uff0c\u8499\u5468\u5927\u4fa0\u629a\u517b\u548c\u51af\u5e08\u5085\u4f20\u827a\uff0c\u5468\u51af\u4e24\u4f4d\u5e08\u5085\u90fd\u5df2\u906d\u6a2a \u6b7b\uff0c\u6211\u7684\u5e08\u5ac2\u4f84\u5973\u90fd\u88ab\u63b3\u53bb\uff0c\u8fd8\u671b\u56db\u4fa0\u7559\u5fc3\u63a2\u8bbf\u4ed6\u4eec\u7684\u8e2a\u8ff9\uff0c\u52a0\u6069\u65bd\u6551\u3002\u201d\u7384\u98ce\u7b11\u9053\uff1a\u201c\u4f60 \u8fd9\u4e2a\u5b69\u5b50\u5012\u6709\u631a\u6027\u771f\u60c5\uff0c\u53ea\u6050\u6211\u4eec\u5fc3\u6709\u4f59\u800c\u529b\u4e0d\u8db3\uff0c\u597d\u5728\u72ec\u81c2\u8001\u5c3c\u7684\u6700\u5f97\u610f\u5f1f\u5b50\u5415\u56db\u5a18\u5df2\u7ecf \u51fa\u5c71\u3002\u8fd9\u6837\u5427\uff0c\u6211\u4eec\u627e\u5230\u5979\u8bf7\u5979\u5e2e\u4f60\u7684\u5fd9\u3002\u201d\u5510\u6653\u6f9c\u542c\u7384\u98ce\u8bf4\u8d77\u56db\u5a18\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8fde\u5fd9\u9053 \u8c22\u3002\u5f53\u4e0b\u56db\u4fa0\u4e3e\u624b\u9053\u522b\uff0c\u7384\u98ce\u8bf4\u9053\uff1a\u201c\u518d\u8fc7\u51e0\u5e74\uff0c\u5f85\u4f60\u5b66\u6210\u4e4b\u540e\uff0c\u6211\u4eec\u518d\u6765\u63a5\u4f60\u3002\u201d \u3000\u3000\u81ea\u6b64\uff0c\u5510\u6653\u6f9c\u5c31\u5728\u6768\u5bb6\u4f4f\u4e86\u4e0b\u6765\u767d\u5929\u5b66\u6b66\uff0c\u665a\u4e0a\u5b66\u6587\u3002\u8d77\u521d\uff0c\u4ed6\u8fd8\u975e\u5e38\u5bb3\u6015\u6768\u67f3\u9752\u548c\u4ed6 \u6b6a\u7f20\uff0c\u4e0d\u77e5\u5bf9\u8fd9\u4f4d\u5389\u5bb3\u7684\u5c0f\u5e08\u59b9\u8be5\u5982\u4f55\u5e94\u4ed8\u3002\u4e0d\u6599\u6768\u67f3\u9752\u56e0\u4ed6\u90a3\u65e5\u5728\u7236\u4eb2\u9762\u524d\u4e3a\u5979\u906e\u7792\uff0c\u5bf9 \u4ed6\u53cd\u6709\u597d\u611f\u3002\u867d\u7136\u813e\u6c14\u8fd8\u662f\u5201\u86ee\uff0c\u5374\u4e0d\u518d\u627e\u4ed6\u6666\u6c14\u4e86\u3002\u4e24\u4e2a\u5b69\u5b50\u4e5f\u5c31\u8fd9\u6837\u7684\u76f8\u5b89\u4e0b\u6765\u3002\u5982\u6b64 \u5306\u5306\u7684\u8fc7\u4e86\u4e94\u8f7d\u3002 \u3000\u3000\u5d69\u9633\u6d3e\u662f\u5185\u5bb6\u6b63\u5b97\uff0c\u5510\u6653\u6f9c\u5b66\u4e86\u4e94\u5e74\uff0c\u6839\u57fa\u5df1\u624e\u5f97\u751a\u4e3a\u7a33\u56fa\uff0c\u8ffd\u98ce\u5251\u6cd5\u4e5f\u7ec3\u5f97\u7cbe\u5999\u7edd \u4f26\u3002\u95f2\u65f6\u548c\u6768\u67f3\u9752\u5e38\u5e38\u8fc7\u62db\uff0c\u5728\u638c\u6cd5\u5f39\u5f13\u4e0a\u867d\u7136\u8fd8\u662f\u7a0d\u900a\u4e00\u7b79\uff0c\u4f46\u5df2\u4e0d\u4f3c\u521d\u6b21\u4ea4\u624b\u4e00\u6837\uff0c\u53ea \u6709\u9000\u8ba9\u7684\u4efd\u513f\u4e86\u3002\u81f3\u4e8e\u5728\u8bfb\u4e66\u65b9\u9762\uff0c\u6768\u67f3\u9752\u4efb\u6027\u8d2a\u61d2\uff0c\u5374\u8fdc\u6bd4\u4e0d\u4e0a\u5510\u6653\u6f9c\uff0c\u529f\u8bfe\u4f5c\u4e1a\uff0c\u65f6\u65f6 \u8981\u627e\u4ed6\u4f5c\u67aa\u624b\u3002\u56e0\u4e3a\u8fd9\u4e2a\u7f18\u6545\uff0c\u6768\u67f3\u9752\u6709\u65f6\u8fd8\u8981\u5df4\u7ed3\u4ed6\uff0c\u5510\u6653\u6f9c\u5bf9\u7740\u8fd9\u4f4d\u4e00\u4f1a\u513f\u5a07\u7b11\u4e00\u4f1a\u513f \u55d4\u6012\u7684\u5c0f\u5e08\u59b9\uff0c\u89c9\u5f97\u5f88\u662f\u96be\u53d7\u3002 \u3000\u3000\u8fd9\u65f6\u5510\u6653\u6f9c\u5eff\u4e00\u5c81\uff0c\u6768\u67f3\u9752\u4e5f\u5341\u4e5d\u5c81\u4e86\u3002\u6768\u4ef2\u82f1\u82f1\u96c4\u5782\u66ae\uff0c\u770b\u7740\u773c\u524d\u8fd9\u5bf9\u4f73\u513f\u4f73\u5973\uff0c\u5fc3 \u4e2d\u9887\u6709\u5fae\u5999\u4e4b\u611f\uff0c\u4e00\u65e5\u4ed6\u6084\u6084\u7684\u95ee\u5973\u513f\u9053\uff1a\u201c\u4f60\u89c9\u5f97\u8fd9\u4f4d\u5e08\u5144\u600e\u6837\uff1f\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u6ca1\u600e\u4e48 \u6837\uff1f\u7239\u95ee\u8fd9\u4e2a\u5e72\u561b\uff1f\u201d\u6768\u4ef2\u82f1\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u50bb\u4e2b\u5934\uff0c\u4f60\u5e74\u7eaa\u4e5f\u4e0d\u5c0f\u4e86\uff0c\u6664\uff0c\u4e5f\u8be5\u4e3a\u672a\u6765\u7684 \u5f52\u5bbf\u6253\u7b97\u4e86\uff0c\u90a3\uff0c\u4f60\u89c9\u5f97\u5e08\u5144\u7684\u4eba\u54c1\u600e\u6837\uff1f\u6211\u770b\u4ed6\u4e3a\u4eba\u5012\u662f\u6ee1\u8001\u5b9e\u7684\u3002\u201d\u6768\u67f3\u9752\u7c89\u8138\u4e00\u7ea2\uff0c \u5a07\u55d4\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u5f97\u4ec0\u4e48\u4eba\u54c1\u4e0d\u4eba\u54c1\uff0c\u4ed6\u6628\u5929\u548c\u6211\u6bd4\u638c\u8fd8\u6bd4\u4e0d\u8fc7\u6211\u5462\uff01\u201d\u6768\u4ef2\u82f1\u5fae\u5fae\u4e00\u7b11\uff0c \u4e0d\u518d\u8ffd\u95ee\u4e0b\u53bb\u3002\u5fc3\u60f3\u5973\u513f\u5927\u7ea6\u662f\u5acc\u4ed6\u6b66\u529f\u4e0d\u9ad8\uff0c\u4e0d\u559c\u6b22\u4ed6\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u4f3c\u559c\u4f3c\u55d4\u8fd8\u4f3c\u7231\uff0c\u5973\u513f\u5fc3\u4e8b\u6700\u96be\u731c\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u516d\u56de\u3000\u6167\u679c\u5170\u56e0\u3000\u6df1\u5fc3\u6258\u6beb\u7d20\u3000\u8f7b\u98a6\u6d45\u7b11\u3000\u4f55\u5904\u4e0d\u5173\u60c5 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u516d\u56de \u6167\u679c\u5170\u56e0\u3000\u6df1\u5fc3\u6258\u6beb\u7d20 \u8f7b\u98a6\u6d45\u7b11\u3000\u4f55\u5904\u4e0d\u5173\u60c5 \u3000\u3000\u6768\u4ef2\u82f1\u672c\u6765\u5f88\u559c\u6b22\u5510\u6653\u6f9c\uff0c\u4f46\u5fc3\u60f3\u5973\u513f\u65e2\u4e0d\u559c\u6b22\u4ed6\uff0c\u4e5f\u5c31\u7f62\u4e86\u3002\u53e6\u4e00\u65b9\u9762\uff0c\u4ed6\u53c8\u60f3\u5230\u5510 \u6653\u6f9c\u7a76\u7adf\u662f\u4e2a\u6765\u5386\u4e0d\u660e\u4e4b\u4eba\uff0c\u82e5\u5c06\u552f\u4e00\u7684\u7231\u5973\u8bb8\u914d\u7ed9\u4ed6\uff0c\u5230\u5e95\u4e0d\u65e0\u987e\u8651\uff0c\u4ed6\u5374\u4e0d\u77e5\u81ea\u5df1\u7684\u5973 \u513f\uff0c\u5bf9\u5510\u6653\u6f9c\u5df2\u662f\u60c5\u6839\u6df1\u79cd\u3002 \u3000\u3000\u6768\u67f3\u9752\u548c\u5510\u6653\u6f9c\u4e94\u5e74\u6765\u8033\u9b13\u53ae\u78e8\uff0c\u867d\u7136\u5979\u5a07\u7eb5\u6210\u6027\uff0c\u4f46\u5510\u6653\u6f9c\u5374\u9887\u80fd\u4f4e\u58f0\u4e0b\u6c14\uff0c\u4e45\u800c\u4e45 \u4e4b\uff0c\u5979\u5df2\u4e00\u523b\u4e5f\u5c11\u4e0d\u4e86\u5510\u6653\u6f9c\u3002\u4f46\u5979\u5374\u4e0d\u81ea\u77e5\u8fd9\u4fbf\u662f\u7231\u60c5\uff0c\u76f4\u5230\u4ed6\u7236\u4eb2\u4e0e\u5979\u8c08\u8d77\u7ec8\u8eab\u5927\u4e8b\u4e4b \u65f6\uff0c\u5979\u624d\u84e6\u7136\u9192\u89c9\uff0c\u5bf9\u7ec8\u8eab\u5927\u4e8b\uff0c\u4e0d\u80fd\u4e0d\u6ce8\u610f\u4e86\u3002\u4f46\u5979\u4ece\u672a\u60f3\u8fc7\u7ed3\u5a5a\u7684\u4e8b\uff0c\u771f\u4e2a\u662f\uff1a\u5973\u513f\u5bb6 \u5fc3\u4e71\u5982\u9ebb\uff0c\u6b32\u8bf4\u8fd8\u4f11\u96be\u4f5c\u7b54\u3002\u56e0\u6b64\u4f6f\u4f5c\u4e0d\u77e5\u7236\u4eb2\u7528\u610f\uff0c\u4e1c\u62c9\u897f\u626f\uff0c\u5c06\u8bdd\u6df7\u8fc7\u3002 \u3000\u3000\u4e00\u65e5\u4e0b\u5348\uff0c\u6768\u67f3\u9752\u60c5\u601d\u660f\u660f\uff0c\u56de\u5230\u4e66\u623f\uff0c\u77a7\u89c1\u5510\u6653\u6f9c\u6b63\u5728\u7528\u529f\uff0c\u53ea\u89c9\u8138\u76ae\u53d1\u70ed\uff0c\u773c\u76ae\u53d1 \u8df3\uff0c\u60f3\u548c\u5510\u6653\u6f9c\u4e00\u6837\u7528\u529f\uff0c\u5374\u65e0\u8bba\u5982\u4f55\u9759\u4e0d\u4e0b\u6765\u3002\u5979\u5077\u77a7\u5510\u6653\u6f9c\uff0c\u5510\u6653\u6f9c\u6b63\u5728\u51dd\u795e\u770b\u4e66\uff0c\u597d \u50cf\u4e0d\u77e5\u9053\u5979\u8fdb\u6765\u4f3c\u7684\u3002\u6768\u67f3\u9752\u628a\u4e66\u672c\u4e00\u629b\uff0c\u7b11\u563b\u563b\u9053\uff1a\u201c\u5e08\u5144\uff0c\u54b1\u4eec\u722c\u5c71\u53bb\uff01\u201d\u5510\u6653\u6f9c\u6115\u7136 \u8bf4\u9053\uff1a\u201c\u600e\u4e48\u4eca\u65e5\u53c8\u8981\u722c\u5c71\uff1f\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u6211\u559c\u6b22\u561b\uff0c\u4f60\u966a\u4e0d\u966a\uff1f\u201d\u5510\u6653\u6f9c\u82e6\u7b11\u4e00\u58f0\uff0c\u5c06 \u4e66\u5377\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u65e2\u7136\u4f60\u559c\u6b22\u53bb\uff0c\u6211\u966a\u4f60\u4fbf\u662f\u3002\u201d\u6768\u67f3\u9752\u53d6\u4e86\u5f39\u5f13\uff0c\u7b11\u9053\uff1a\u201c\u77a7\u4f60\u8fd9 \u526f\u54ed\u4e27\u8138\uff0c\u54b1\u4eec\u53bb\u6253\u9e1f\u513f\u53bb\uff0c\u4e0d\u6bd4\u4f60\u8bfb\u8fd9\u4e9b\u635e\u4ec0\u5b50\u7684\u4e66\u672c\u597d\u73a9\u5f97\u591a\uff01 \u3000\u3000\u6768\u5bb6\u4f9d\u5c71\u9762\u6e56\uff0c\u722c\u4e0a\u540e\u5c71\uff0c\u8fdc\u5904\u6e56\u5149\u63a9\u6620\uff0c\u5468\u56f4\u90c1\u90c1\u82cd\u82cd\uff0c\u6ee1\u5c71\u4e0a\u4e0b\uff0c\u5c3d\u662f\u91ce\u82b1\uff0c\u707f\u5982 \u4e91\u971e\u3002\u5510\u6653\u6f9c\u767b\u9ad8\u8fdc\u773a\uff0c\u5fc3\u65f7\u795e\u6021\uff0c\u628a\u5fc3\u4e2d\u4e0d\u5feb\uff0c\u6d88\u4e86\u4e00\u534a\uff0c\u7ea2\u82b1\u7eff\u6811\u4e1b\u4e2d\uff0c\u9e1f\u513f\u5531\u5f97\u6b63 \u6b22\uff0c\u6768\u67f3\u9752\u66f3\u8d77\u5f39\u5f13\uff0c\u6253\u51fa\u4e24\u5f39\uff0c\u628a\u4e24\u53ea\u9ec4\u83ba\u6253\u4e86\u4e0b\u6765\u3002\u5510\u6653\u6f9c\u9053\uff1a\u201c\u9e1f\u513f\u53eb\u5f97\u8fd9\u6837\u597d\u542c\uff0c \u4f60\u628a\u5b83\u4eec\u6253\u4e0b\u6765\u4f5c\u751a\uff1f\u771f\u662f\u716e\u9e64\u711a\u7434\uff0c\u5927\u6740\u98ce\u666f\uff01\u201d\u6362\u5728\u5e73\u65f6\uff0c\u6768\u67f3\u9752\u4e00\u5b9a\u5927\u53d1\u813e\u6c14\uff0c\u6b64 \u523b\uff0c\u5374\u53ea\u5a07\u55d4\u7b11\u9053\uff1a\u201c\u9178\u79c0\u624d\uff0c\u53c8\u629b\u4e66\u56ca\u4e86\uff0c\u672c\u59d1\u5a18\u504f\u504f\u7231\u6253\u3002\u201d\u5510\u6653\u6f9c\u6b63\u5f85\u529d\u5979\uff0c\u5ffd\u7136\u505c \u4e0b\uff0c\u6768\u67f3\u9752\u968f\u7740\u4ed6\u773c\u5149\u671b\u53bb\uff0c\u53ea\u89c1\u7eff\u6811\u4e1b\u4e2d\uff0c\u73b0\u51fa\u4e86\u4e24\u4e2a\u4eba\u5f71\uff0c\u4e00\u8001\u4e00\u5c11\uff0c\u7b11\u563b\u563b\u7684\u671b\u7740\u4ed6 \u4eec\u6253\u9e1f\u513f\u3002 \u3000\u3000\u6768\u67f3\u9752\u6697\u6697\u751f\u6c14\uff0c\u89c1\u662f\u964c\u751f\u5ba2\u4eba\uff0c\u4e0d\u597d\u53d1\u4f5c\uff0c\u5f3a\u81ea\u6309\u637a\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u5bf9\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60 \u77e5\u9053\u672c\u59d1\u5a18\u5982\u4f55\u6253\u6cd5\uff0c\u4e0d\u770b\u6e05\u695a\uff0c\u5c31\u6765\u8d23\u5907\u3002\u6211\u8fd9\u5f39\u5f13\uff0c\u53eb\u505a\uff1a\u6253\u751f\u4e0d\u6253\u6b7b\uff0c\u6298\u7ffc\u4e0d\u4f24\u76ae\uff0c \u4f60\u77e5\u9053\u4e48\uff1f\u201d\u55d6\u7684\u4e00\u5f39\uff0c\u53c8\u628a\u4e00\u53cc\u9ec4\u83ba\u6253\u4e86\u4e0b\u6765\uff0c\u5510\u6653\u6f9c\u62fe\u8d77\u4e00\u770b\uff0c\u9ec4\u83ba\u5728\u4ed6\u638c\u5fc3\u8df3\u4e86\u4e24 \u4e0b\uff0c\u632f\u7fc5\u6b32\u98de\uff0c\u5374\u98de\u4e0d\u8d77\u3002\u539f\u6765\u6768\u67f3\u9752\u4e00\u5f39\u628a\u9ec4\u83ba\u7fc5\u8180\u7684\u8f6f\u9aa8\u6253\u7740\uff0c\u5374\u5e76\u4e0d\u4f24\u7740\u9ec4\u83ba\u76ae\u8089\uff0c \u53ea\u8981\u8ba9\u5b83\u4f11\u606f\u4e9b\u65f6\uff0c\u4fbf\u80fd\u632f\u7ffc\u98de\u7fd4\u3002\u5510\u6653\u6f9c\u867d\u4e0e\u5979\u65e5\u5915\u76f8\u5904\uff0c\u5374\u8fd8\u4e0d\u77e5\u5979\u795e\u5f39\u7edd\u6280\uff0c\u7cbe\u5999\u5982 \u65af\uff0c\u4e0d\u4f46\u767e\u53d1\u767e\u4e2d\uff0c\u800c\u4e14\u6240\u7528\u7684\u529b\u5ea6\uff0c\u4e5f\u6070\u5230\u597d\u5904\u3002\u50cf\u8fd9\u6837\u5f39\u53d6\u7a7a\u4e2d\u98de\u9e1f\uff0c\u6d3b\u751f\u751f\u7684\u624b\u4e2d\u64d2 \u5230\uff0c\u5510\u6653\u6f9c\u4fbf\u4e0d\u80fd\u591f\u3002 \u3000\u3000\u6768\u67f3\u9752\u77a7\u5510\u6653\u6f9c\u9762\u8272\uff0c\u77e5\u4ed6\u5fc3\u6298\uff0c\u5927\u4e3a\u9ad8\u5174\uff0c\u5f39\u5f13\u518d\u66f3\uff0c\u90a3\u5c11\u5e74\u5ba2\u4eba\u5ffd\u7136\u632a\u524d\u4e00\u6b65\uff0c\u6768 \u67f3\u9752\u5f13\u5982\u6ee1\u6708\uff0c\u5f39\u4f3c\u6d41\u661f\uff0c\u55b3\u55b3\u4e24\u5f39\uff0c\u53c8\u5411\u9ec4\u83ba\u6253\u53bb\uff0c\u4e0d\u6599\u98de\u5f39\u63a0\u8fc7\uff0c\u6811\u4e0a\u7684\u4e24\u53ea\u9ec4\u83ba\u53eb\u4e86 \u4e00\u58f0\uff0c\u7adf\u7136\u632f\u7fc5\u98de\u5f00\uff0c\u8fd9\u662f\u4ece\u6240\u672a\u6709\u4e4b\u4e8b\uff0c\u6768\u67f3\u9752\u9762\u7ea2\u8033\u70ed\uff0c\u5927\u60d1\u4e0d\u89e3\u3002\u5510\u6653\u6f9c\u6717\u58f0\u9053\uff1a \u201c\u8fd9\u4f4d\u5ba2\u4eba\u597d\u624b\u6cd5\uff01\u201d\u539f\u6765\u5728\u6768\u67f3\u9752\u6253\u9e1f\u513f\u65f6\uff0c\u90a3\u5c11\u5e74\u5ba2\u4eba\u53cc\u6307\u4e00\u5f39\uff0c\u4e24\u6307\u95f4\u5939\u7740\u7684\u201c\u83e9\u63d0 \u5b50\u201d\uff08\u4e00\u79cd\u7ec6\u5c0f\u7684\u6697\u5668\uff09\u7adf\u628a\u6768\u67f3\u9752\u7684\u5f39\u4e38\u78b0\u6b6a\u4e86\u51c6\u5934\u3002\u6768\u67f3\u9752\u805a\u7cbe\u4f1a\u795e\uff0c\u4e0d\u77e5\u662f\u4ed6\u5f04\u7684\u7384 \u865a\uff0c\u5510\u6653\u6f9c\u6253\u60ef\u98de\u8292\uff0c\u98de\u8292\u662f\u6bd4\u83e9\u63d0\u5b50\u66f4\u5c0f\u7684\u6697\u5668\uff0c\u89c1\u4ed6\u624b\u6307\u5fae\u52a8\uff0c\u5df2\u81ea\u770b\u51fa\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5ba2\u4eba\u7ed9\u5510\u6653\u6f9c\u559d\u7834\uff0c\u5634\u5507\u4e00\u52a8\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u6768\u67f3\u9752\u5ffd\u7136\u5f39\u4f3c\u51b0\u96f9\uff0c\u8fde\u73af\u98de\u5c04\uff0c\u5239 \u90a3\u4e4b\u95f4\uff0c\u5c04\u51fa\u4e86\u4e03\u516b\u679a\u5f39\u5b50\uff0c\u5c11\u5e74\u5ba2\u4eba\u888d\u8896\u6025\u6325\uff0c\u8eab\u5f62\u95ea\u52a8\u628a\u6768\u67f3\u9752\u6253\u6765\u7684\u5f39\u5b50\u6216\u62c2\u843d\u5c18 \u57c3\uff0c\u6216\u95ea\u8eab\u907f\u8fc7\uff0c\u5f20\u53e3\u53eb\u9053\uff1a\u201c\u8fd9\u4f60\u5c0f\u59d1\u5a18\u597d\u6ca1\u9053\u7406\uff01\u201d\u5ffd\u7136\u535c\u7684\u4e00\u5f39\u98de\u6765\uff0c\u8981\u8eb2\u5df1\u6765\u4e0d \u53ca\uff0c\u6b63\u4e2d\u5634\u5507\uff0c\u628a\u95e8\u7259\u6253\u5f97\u6447\u6447\u6b32\u52a8\uff0c\u7259\u6839\u51fa\u8840\uff0c\u75bc\u75db\u96be\u5f53\u3002\u90a3\u5e74\u8001\u7684\u5ba2\u4eba\u539f\u662f\u7b11\u563b\u563b\u7684\u5728 \u65c1\u89c2\u770b\uff0c\u8fd9\u65f6\u4e5f\u6025\u5f97\u8dc3\u5c06\u4e0a\u6765\uff0c\u5c06\u5c11\u5e74\u4e00\u6276\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u6ca1\u4e8b\u4e48\uff1f\u201d\u5c11\u5e74\u5fcd\u75db\u7b54\u9053\uff1a\u201c\u6ca1 \u4e8b\u3002\u201d\u5f20\u53e3\u628a\u8840\u6c34\u55b7\u51fa\uff0c\u5e78\u559c\u95e8\u7259\u8fd8\u672a\u6298\u65ad\uff01 \u3000\u3000\u6768\u67f3\u9752\u6536\u8d77\u5f39\u5f13\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u672c\u59d1\u5a18\u6253\u9e1f\u513f\u4e0d\u788d\u4f60\u4eec\u7684\u4e8b\uff0c\u4f60\u4eec\u5e72\u561b\u70ab\u672c\u9886\uff0c\u5f04\u7384 \u865a\uff0c\u54fc\uff0c\u6211\u8fd8\u4ee5\u4e3a\u6709\u591a\u5927\u672c\u9886\uff0c\u539f\u6765\u5374\u4e5f\u7981\u53d7\u4e0d\u4e86\u5c0f\u5c0f\u4e00\u5f39\uff0c\u8fd9\u53eb\u505a\u5440\uff0c\u5b54\u592b\u5b50\u95e8\u524d\u5356\u767e\u5bb6 \u59d3\uff01\u201d\u5c11\u5e74\u5ba2\u4eba\u9762\u8272\u4e00\u53d8\uff0c\u5fc3\u91cc\u6697\u9a82\u4e86\u4e00\u53e5\uff1a\u201c\u91ce\u4e2b\u5934\uff0c\u4e0d\u7ed9\u4f60\u70b9\u989c\u8272\u770b\u770b\uff0c\u4f60\u4e5f\u4e0d\u77e5\u672c\u5c11 \u7237\u5389\u5bb3\u3002\u201d\u4f46\u4ed6\u5fc3\u4e2d\u867d\u7136\u6012\u9a82\uff0c\u5374\u4e0d\u6562\u8bf4\u5c06\u51fa\u6765\u3002\u90a3\u8001\u5e74\u5ba2\u4eba\u4f3c\u662f\u4ed6\u7684\u7236\u4eb2\uff0c\u4f4e\u4f4e\u53f9\u4e86\u53e3 \u6c14\uff0c\u5374\u53c8\u4f3c\u6015\u4ed6\u52a8\u6012\u4f3c\u7684\uff0c\u4f38\u624b\u5c06\u4ed6\u62e6\u7740\uff0c\u8dc3\u524d\u4e00\u6b65\uff0c\u548c\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u59d1\u5a18\u53ef\u662f\u94c1\u638c\u795e \u5f39\u6768\u4ef2\u82f1\u7684\u638c\u73e0\u4e48\uff1f\u201d\u6768\u67f3\u9752\u5c06\u5934\u4e00\u626d\uff0c\u5374\u4e0d\u7b54\u8bdd\uff0c\u5979\u4f59\u6012\u672a\u606f\uff0c\u8fd8\u60f3\u627e\u90a3\u5c11\u5e74\u5ba2\u4eba\u7684\u6666 \u6c14\uff0c\u5fc3\u60f3\uff0c\u8fd9\u8001\u5bb6\u4f19\u77e5\u9053\u6211\u7236\u4eb2\u7684\u540d\u5b57\uff0c\u60f3\u5fc5\u662f\u6211\u7239\u7239\u7684\u670b\u53cb\u4e86\u3002\u6211\u82e5\u7b54\u5e94\uff0c\u8fd9\u573a\u67b6\u5c31\u6253\u4e0d \u6210\uff0c\u7d22\u6027\u7ed9\u4ed6\u4e2a\u4e0d\u7406\u4e0d\u776c\uff0c\u628a\u4ed6\u4eec\u6fc0\u6012\uff0c\u7136\u540e\u6211\u548c\u5510\u5e08\u5144\u628a\u4ed6\u4eec\u6253\u4e2a\u843d\u82b1\u6d41\u6c34\u3002 \u3000\u3000\u5510\u6653\u6f9c\u5e74\u5c81\u8f83\u5927\uff0c\u9605\u5386\u8f83\u6df1\uff0c\u770b\u89c1\u6768\u67f3\u9752\u626d\u5934\u4e0d\u7406\uff0c\u90a3\u8001\u8005\u9762\u8272\u5c34\u5c2c\uff0c\u62c5\u5fc3\u4e8b\u60c5\u5f04\u50f5\uff0c \u4e0d\u7981\u4e00\u624b\u642d\u7740\u5e08\u59b9\u7684\u80a9\u5934\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u522b\u751f\u6c14\u5566\uff0c\u4f60\u8bb0\u5f97\u5e08\u5085\u6559\u8bad\u5417\uff1f\u5bf9\u5ba2\u4eba\u8981\u5c0a\u656c\uff0c\u4e0d \u53ef\u8fd9\u6837\u3002\u201d\u56de\u8fc7\u5934\u6765\uff0c\u53c8\u5bf9\u90a3\u8001\u4eba\u9053\uff1a\u201c\u4e24\u4f4d\u5ba2\u4eba\u606f\u6012\uff0c\u6211\u4eec\u6b63\u662f\u2026\u2026\u201d\u94c1\u638c\u795e\u5f39\u7684\u5f92\u5f1f\u8fd9 \u51e0\u4e2a\u5b57\u8fd8\u672a\u8bf4\uff0c\u90a3\u5c11\u5e74\u5ba2\u4eba\u5ffd\u7136\u4ece\u4e2d\u622a\u65ad\uff0c\u6012\u58f0\u559d\u9053\uff1a\u201c\u5173\u4f60\u8fd9\u5c0f\u6742\u79cd\u4ec0\u4e48\u4e8b\uff1f\u201d\u8001\u8005\u9053\uff1a \u201c\u9521\u4e5d\uff0c\u4f11\u5f97\u51fa\u53e3\u4f24\u4eba\uff01\u201d\u5510\u6653\u6f9c\u6115\u7136\u6536\u58f0\uff0c\u90a3\u5c11\u5e74\u53cc\u77b3\u55b7\u706b\uff0c\u50cf\u4e00\u5934\u72fc\u4f3c\u7684\u76ef\u7740\u81ea\u5df1\uff0c\u4e5f \u4e0d\u77e5\u4ed6\u8fd9\u6837\u66b4\u6012\uff0c\u4e3a\u4ec0\u6765\u7531\uff1f\u6309\u8bf4\u6253\u4f24\u4ed6\u7684\u4e43\u662f\u5e08\u59b9\uff0c\u81ea\u5df1\u597d\u5fc3\u529d\u67b6\uff0c\u4ed6\u5374\u4e0d\u5411\u5e08\u59b9\u53d1\u6012\uff0c \u53cd\u800c\u8fb1\u9a82\u81ea\u5df1\uff0c\u771f\u662f\u592a\u4e0d\u8bb2\u7406\uff01 \u3000\u3000\u5c11\u5e74\u5ba2\u4eba\u7ed9\u7236\u4eb2\u4e00\u8bf4\uff0c\u4ecd\u662f\u4f59\u6012\u672a\u6d88\uff0c\u53c8\u8dc3\u524d\u4e00\u6b65\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u60f3\u662f\u6768\u8001\u62f3\u5e08\u7684\u5f97 \u610f\u5f1f\u5b50\uff0c\u533a\u533a\u4e0d\u624d\uff0c\u613f\u9886\u6559\u540d\u5bb6\u5f1f\u5b50\u7684\u9ad8\u62db\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u5f3a\u6291\u6012\u6c14\uff0c\u542b\u55d4\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u7d20\u65e0\u8fc7\u8282\uff0c\u4e3a\u4f55\u8981\u6bd4\u6b66\u8bd5\u62db\uff01\u201d\u6768\u67f3\u9752\u674f\u773c\u5706\u7741\uff0c \u8f6c\u8fc7\u8eab\u6765\u5c06\u5510\u6653\u6f9c\u4e00\u63a8\uff0c\u6012\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u522b\u4eba\u9a82\u4f60\u7956\u5b97\u4e09\u4ee3\u4f60\u4e5f\u7adf\u81ea\u4f4e\u5934\uff0c\u4e0d\u6015 \u522b\u4eba\u628a\u4f60\u5f53\u6210\u7a9d\u56ca\u5e9f\uff08\u6ca1\u7528\u7684\u5e9f\u7269\uff09\uff1f\u4f60\u4e0d\u5bb3\u7f9e\uff0c\u6211\u4e5f\u66ff\u4f60\u9762\u7ea2\uff0c\u8d76\u5feb\u4e0a\u53bb\u628a\u78b4\u5b50\u63a5\u4e0b\u6765\uff0c \u8981\u4e0d\u6211\u5c31\u4e0d\u8ba4\u4f60\u505a\u5e08\u5144\uff1f\u201d\u90a3\u8001\u5e74\u5ba2\u4eba\u5fd9\u9053\uff1a\u201c\u6211\u548c\u6768\u8001\u82f1\u96c4\u662f\u591a\u5e74\u77e5\u4ea4\uff0c\u5c0f\u513f\u6027\u5b50\u66b4\u8e81\uff0c \u4e0d\u61c2\u8bf4\u8bdd\uff0c\u5f97\u7f6a\u4e86\u8fd9\u4f4d\u5c0f\u54e5\uff0c\u6211\u5728\u8fd9\u91cc\u66ff\u4ed6\u966a\u7f6a\uff01\u201d\u6768\u67f3\u9752\u63d2\u5634\u9053\uff1a\u201c\u966a\u7f6a\u6211\u4eec\u9886\u4e86\uff0c\u4f46\u6211 \u4eec\u65e2\u627f\u6307\u540d\u6311\u6218\uff0c\u5c11\u4e0d\u5f97\u5728\u62f3\u811a\u4e0a\u8fd8\u8981\u9886\u6559\u51e0\u62db\uff01\u201d\u8bdd\u950b\u5484\u5484\u903c\u4eba\uff0c\u8001\u8005\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u9053\uff1a \u201c\u6768\u4ef2\u82f1\u7684\u5973\u513f\u600e\u4e48\u8fd9\u6837\u7c97\u91ce\uff01\u201d\u5c11\u5e74\u5ba2\u4eba\u65e9\u628a\u4e0a\u8863\u8131\u4e0b\uff0c\u671d\u5730\u4e00\u629b\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u5148 \u8bf7\u6559\u8fd9\u4f4d\u5c0f\u54e5\u51e0\u62db\uff0c\u5982\u679c\u662f\u4fa5\u5e78\u6253\u8d62\u7684\u8bdd\uff0c\u6211\u518d\u63a5\u59d1\u5a18\u4f60\u7684\u9ad8\u62db\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u53d7\u4e24\u9762\u4e00\u63a8\u4e00\u6324\uff0c\u52a0\u4e0a\u5fc3\u4e2d\u4e5f\u6012\u90a3\u5c11\u5e74\u65e0\u793c\uff0c\u628a\u6768\u67f3\u9752\u62ff\u7740\u81ea\u5df1\u7684\u624b\u4e00\u7529\uff0c\u8df3\u5165 \u573a\u5fc3\uff0c\u53cc\u62f3\u4e00\u62b1\uff0c\u53eb\u9053\uff1a\u201c\u9601\u4e0b\u65e2\u7136\u5b9a\u8981\u8bd5\u62db\uff0c\u5c0f\u5f1f\u53ea\u597d\u627f\u6559\uff01\u201d\u5c11\u5e74\u5ba2\u4eba\u7b54\u9053\uff1a\u201c\u597d\u8bf4\uff0c \u597d\u8bf4\uff01\u201d\u7a81\u7136\u547c\u7684\u4e00\u638c\u5f53\u5934\u6253\u5230\uff0c\u5510\u6653\u6f9c\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u76f4\u5230\u654c\u638c\u8ddd\u80a9\u4e0d\u53ca\u4e00\u5c3a\uff0c\u65b9\u731b\u7136\u4e00\u4fa7 \u8eab\uff0c\u6a2a\u638c\u5f80\u4e0a\u4e00\u524a\uff0c\u53cc\u638c\u4e00\u4ea4\uff0c\u84ec\u7684\u4e00\u58f0\uff0c\u6765\u4eba\u7adf\u7ed9\u9707\u9000\u4e24\u6b65\u3002\u5510\u6653\u6f9c\u8fd9\u51e0\u5e74\u6765\u5185\u529f\u7cbe\u8fdb\uff0c \u94c1\u638c\u7684\u6280\u827a\u9020\u8be3\u4ea6\u9887\u4e0d\u51e1\uff0c\u6362\u4e86\u5e38\u4eba\uff0c\u8fd9\u4e00\u638c\u6015\u4e0d\u628a\u80f3\u818a\u6253\u65ad\uff01\u90a3\u5c11\u5e74\u4e5f\u771f\u4e86\u5f97\uff0c\u4e00\u9000\u4e00 \u6643\uff0c\u628a\u5bf9\u624b\u773c\u795e\u5f80\u4e0a\u4e00\u9886\uff0c\u8fde\u73af\u6b65\u5f80\u524d\u4e00\u51b2\uff0c\u7a81\u7136\u98de\u8d77\u4e00\u817f\uff0c\u5510\u6653\u6f9c\u5de6\u638c\u4e00\u4e2a\u201c\u4f0f\u5730\u65a9 \u864e\u201d\uff0c\u5c11\u5e74\u53f3\u817f\u4e00\u6536\uff0c\u5de6\u817f\u53c8\u8d77\uff0c\u8fde\u73af\u98de\u811a\u51f6\u731b\u5f02\u5e38\u3002\u5510\u6653\u6f9c\u4e0d\u7531\u5f97\u8fde\u9000\u6570\u6b65\u3002\u6768\u67f3\u9752\u5728\u65c1 \u51b7\u7b11\u9053\uff1a\u201c\u638c\u4e0a\u7684\u529f\u592b\u4e0d\u662f\u4eba\u5bb6\u5bf9\u624b\uff0c\u8de4\u5b50\u4e5f\u8e22\u51fa\u6765\u4e86\uff01\u201d\u5c11\u5e74\u5ba2\u4eba\u5f80\u524d\u4e00\u51b2\uff0c\u53cc\u817f\u5f80\u4e0b\u7ad9 \u5e84\uff0c\u5de6\u624b\u62a4\u8eab\uff0c\u53f3\u624b\u4e00\u62f3\u5f53\u80f8\u6363\u51fa\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u518d\u89c1\u8bc6\u89c1\u8bc6\u4f60\u6768\u5bb6\u94c1\u638c\u7684\u5a01\u540d\uff01\u201d\u5510\u6653\u6f9c \u970d\u5730\u8f6c\u8eab\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u54ea\u77e5\u5c11\u5e74\u7684\u624b\u6cd5\u771f\u5feb\uff0c\u4e0a\u76d8\u4e0d\u52a8\uff0c\u4e0b\u76d8\u4e00\u6362\uff0c\u628a\u5510\u6653\u6f9c\u53cc\u638c\u4e00\u67b6\uff0c\u8fde \u67b6\u5e26\u653b\uff0c\u5530\u5730\u4e00\u58f0\uff0c\u638c\u631f\u52b2\u98ce\uff0c\u53c8\u81ea\u6253\u5230\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5c11\u5e74\u5b66\u7684\u662f\u4e94\u884c\u62f3\uff0c\u4e94\u884c\u62f3\u7684\u62f3\u62db\u5168\u53d6\u653b\u52bf\uff0c\u7b2c\u4e00\u62db\u65f6\u5510\u6653\u6f9c\u786c\u63a5\u786c\u67b6\uff0c\u90a3\u80a1\u529b \u91cf\u76f8\u78b0\u76f8\u649e\uff0c\u5c11\u5e74\u529b\u91cf\u8f83\u5f31\uff0c\u8eab\u5f62\u9707\u9000\uff0c\u653b\u52bf\u53d1\u4e0d\u51fa\u6765\uff0c\u903c\u5f97\u6539\u7528\u9e33\u9e2f\u8fde\u73af\u817f\u963b\u654c\u53cd\u653b\u3002\u8fde \u73af\u817f\u4e0d\u80fd\u4e45\u6218\uff0c\u56e0\u6b64\u8d81\u7740\u6768\u67f3\u9752\u53d1\u8bdd\uff0c\u800c\u5510\u6653\u6f9c\u653b\u52bf\u53d7\u632b\u4e4b\u9645\uff0c\u6539\u56de\u672c\u95e8\u62f3\u672f\u3002\u5c11\u5e74\u8fd9\u65f6\u5df1 \u77e5\u53cc\u65b9\u957f\u77ed\uff0c\u77e5\u9053\u81ea\u5df1\u5185\u529b\u4e0d\u53ca\u6653\u6f9c\uff0c\u4e8e\u662f\u907f\u5176\u6b63\u950b\uff0c\u7eaf\u7528\u4fa7\u88ad\uff0c\u5e76\u4ee5\u5feb\u6377\u7684\u638c\u6cd5\uff0c\u4e00\u62a2\u5148 \u624b\uff0c\u4f7f\u5982\u66b4\u98ce\u96e8\u822c\u7684\u8fdb\u653b\uff0c\u53eb\u5510\u6653\u6f9c\u817e\u4e0d\u51fa\u624b\u6765\u65bd\u7528\u94c1\u638c\u529f\u592b\u51fb\u4ed6\u8981\u5bb3\u3002\u4e24\u4eba\u8d8a\u6597\u8d8a\u70c8\uff0c\u90a3 \u5c11\u5e74\u7684\u4e94\u884c\u62f3\u62f3\u62db\uff0c\u5168\u53d6\u653b\u52bf\uff0c\u4e00\u62db\u624d\u53d1\uff0c\u4e8c\u62db\u53c8\u5230\uff0c\u8fde\u7528\u201c\u5288\u3001\u94bb\u3001\u70ae\u3001\u6a2a\u3001\u5d29\u201d\u4e94\u5b57 \u8bc0\uff0c\u4e94\u884c\u751f\u514b\uff0c\u75be\u5982\u72c2\u98ce\uff01\u5510\u6653\u6f9c\u4e0b\u76d8\u6781\u7a33\uff0c\u62f3\u62f3\u6709\u529b\uff0c\u5728\u62f3\u6cd5\u4e2d\u517c\u65bd\u64d2\u62ff\u5316\u89e3\u4e4b\u6280\uff0c\u6597\u5230 \u4e94\u4e03\u5341\u62db\uff0c\u90a3\u5c11\u5e74\u7a81\u53d1\u4e00\u62f3\uff0c\u7528\u201c\u5288\u201d\u5b57\u8bc0\uff0c\u76f4\u5288\u4e0b\u6765\uff01 \u3000\u3000\u8fd9\u4e00\u62f3\u62f3\u529b\u6781\u731b\uff0c\u5510\u6653\u6f9c\u6a2a\u638c\u4e00\u6321\uff0c\u62f3\u638c\u76f8\u62b5\uff0c\u638c\u5fc3\u75bc\u75db\uff0c\u5510\u6653\u6f9c\u968f\u638c\u4e00\u62e8\uff0c\u628a\u5c11\u5e74\u7684 \u53f3\u62f3\u7c98\u51fa\u5916\u95e8\uff0c\u987a\u638c\u4e00\u63a8\uff0c\u5c11\u5e74\u715e\u662f\u6e9c\u6ed1\uff0c\u4e00\u4e2a\u201c\u72ee\u5b50\u6447\u5934\u201d\uff0c\u7a81\u7136\u6539\u7528\u94bb\u638c\uff0c\u4e0a\u51fb\u654c\u9762\uff0c \u8fd9\u4e00\u62f3\u6709\u4e2a\u540d\u5802\uff0c\u53eb\u505a\u201c\u51b2\u5929\u70ae\u201d\uff0c\u70ae\u6253\u4e0a\u76d8\uff0c\u5510\u6653\u6f9c\u638c\u80cc\u4e00\u6325\uff0c\u6539\u63a8\u4e3a\u6302\uff0c\u7528\u5d29\u638c\u5f80\u5916\u4e00 \u6302\uff0c\u5c11\u5e74\u7684\u94bb\u62f3\u53c8\u7ed9\u6302\u5f00\u3002\u5510\u6653\u6f9c\u84e6\u7136\u7ffb\u8eab\u4e00\u626d\uff0c\u559d\u58f0\uff1a\u201c\u7740\uff01\u201d\u53cc\u638c\u8fc5\u5982\u75be\u98ce\uff0c\u65bd\u5c55\u5927\u64d2 \u62ff\u624b\u6cd5\uff0c\u628a\u5c11\u5e74\u7684\u80f3\u818a\u6263\u7740\u4e00\u626d\uff0c\u4e0d\u6599\u5c11\u5e74\u4fef\u8eab\u4e00\u8dcc\uff0c\u731b\u7136\u65bd\u5c55\u5f39\u817f\u529f\u592b\uff0c\u75be\u5982\u9a87\u7535\uff0c\u7167\u5510 \u6653\u6f9c\u808b\u4e0b\u8e22\u53bb\uff01\u5510\u6653\u6f9c\u5927\u53eb\u4e00\u58f0\uff0c\u4e00\u626d\u4e00\u9001\uff0c\u53cc\u624b\u4e00\u677e\uff0c\u4ef0\u9762\u8dcc\u5012\u3002\u90a3\u5c11\u5e74\u4e5f\u662f\u5927\u53eb\u4e00\u58f0\uff0c \u4fef\u8eab\u8dcc\u5012\u3002\u6768\u67f3\u9752\u5927\u60ca\u5931\u8272\uff0c\u55d6\u55d6\u55d6\u8fde\u53d1\u6570\u5f39\uff0c\u62d2\u654c\u6551\u53cb\u3002\u90a3\u8001\u8005\u54c8\u54c8\u4e00\u7b11\uff0c\u53cc\u8896\u8d77\u5904\uff0c\u53ea \u89c1\u5f39\u98de\uff0c\u4e0d\u89c1\u5f39\u843d\uff0c\u4f3c\u4e4e\u90fd\u7ed9\u4ed6\u63a5\u8fc7\u53bb\u4e86\u3002\u8001\u8005\u7a81\u7136\u5f53\u7a7a\u4e00\u63d6\uff0c\u53eb\u9053\uff1a\u201c\u6768\u5927\u54e5\uff0c\u4e45\u8fdd\uff0c\u4e45 \u8fdd\uff01\u201d\u6768\u67f3\u9752\u7741\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u4e00\u4eba\u75be\u4f3c\u6d41\u661f\uff0c\u5728\u5c71\u95f4\u90a3\u8fb9\u5982\u98de\u63a0\u5230\uff0c\u53ef\u4e0d\u6b63\u662f\u81ea\u5df1\u7684\u7236\u4eb2\u3002 \u3000\u3000\u5510\u6653\u6f9c\u548c\u90a3\u5c11\u5e74\u53cc\u53cc\u722c\u8d77\uff0c\u90a3\u5c11\u5e74\u53cc\u81c2\u4e0b\u5782\uff0c\u54fc\u54fc\u5577\u5577\uff0c\u5510\u6653\u6f9c\u8170\u5f2f\u817f\u8f6f\uff0c\u808b\u9aa8\u4f5c\u75db\uff0c \u4e24\u4eba\u90fd\u88ab\u5bf9\u65b9\u731b\u529b\u6240\u4f24\u3002\u6768\u67f3\u9752\u624b\u6307\u72b9\u81ea\u6263\u7740\u5f13\u5f26\uff0c\u6014\u6014\u7684\u7ad9\u5728\u4e00\u8fb9\uff0c\u6768\u4ef2\u82f1\u62c8\u987b\u65a5\u9053\uff1a \u201c\u9752\u513f\uff0c\u53c8\u662f\u4f60\u95ef\u7684\u7978\u4e48\uff1f\u201d\u6768\u67f3\u9752\u4e0d\u6562\u56de\u7b54\uff0c\u5510\u6653\u6f9c\u9762\u7ea2\u7ea2\u7684\u9053\uff1a\u201c\u4e0d\u5173\u5e08\u59b9\u7684\u4e8b\uff0c\u662f\u8fd9 \u4f4d\u82f1\u96c4\u4e00\u5b9a\u8981\u548c\u5f92\u5f1f\u8fc7\u62db\u3002\u201d\u90a3\u5c11\u5e74\u89c1\u5510\u6653\u6f9c\u5904\u5904\u56de\u62a4\u6768\u67f3\u9752\uff0c\u4e0d\u7981\u53c8\u662f\u6a2a\u7709\u6012\u76ee\uff0c\u76ef\u4e86\u5510 \u6653\u6f9c\u4e00\u773c\u3002\u8001\u4eba\u770b\u5728\u773c\u91cc\uff0c\u5fc3\u4e2d\u53c8\u6c14\u53c8\u7b11\u3002\u6768\u4ef2\u82f1\u4fa7\u8138\u77a7\u4e86\u90a3\u5c11\u5e74\u5ba2\u4eba\u4e00\u9635\uff0c\u6b22\u7136\u8bf4\u9053\uff1a \u201c\u4f60\u7684\u513f\u5b50\u90fd\u8fd9\u4e48\u5927\u4e86\uff0c\u4ed6\u7684\u540d\u5b57\u662f\u53eb\u505a\u9521\u4e5d\u4e48\uff1f\u201d\u5c11\u5e74\u53e9\u5934\u884c\u793c\u3002\u90a3\u8001\u8005\u9053\uff1a\u201c\u6768\u5927\u54e5\uff0c \u6211\u5e26\u4f60\u4f84\u513f\u6765\u770b\u4f60\u4e86\uff0c\u4f60\u53ef\u6599\u4e0d\u5230\u5427\uff1f\u201d\u6768\u4ef2\u82f1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u9521\u4e5d\u7684\u6b66\u529f\u5927\u6709\u8fdb\u5883\u4e86\uff0c\u521a\u624d \u4ed6\u90a3\u62db\u5f39\u817f\uff0c\u4f7f\u5f97\u4e0d\u9519\uff01\u6765\uff0c\u6653\u6f9c\uff0c\u89c1\u8fc7\u8fd9\u4f4d\u5e08\u5144\uff0c\u4f60\u4eec\u4e24\u4eba\u600e\u4e48\u4e00\u89c1\u9762\u5c31\u8bd5\u62db\u554a\uff01\u201d\u90a3\u5c11 \u5e74\u9762\u8272\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u9752\u59b9\u7684\u6b66\u529f\u771f\u5f3a\uff0c\u5f39\u5b50\u6253\u5f97\u597d\u6781\u4e86\uff01\u201d\u6768\u4ef2\u82f1\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u5f20\u7738\u6380 \u987b\uff0c\u53cc\u76ee\u5a01\u4e25\uff0c\u76ef\u7740\u5973\u513f\u9053\uff1a\u201c\u4f60\u53c8\u5356\u5f04\u4f60\u7684\u5f39\u5f13\u4e86\uff1f\u201d\u6768\u67f3\u9752\u4f4e\u5934\u4fa7\u9762\uff0c\u53cc\u7738\u5fae\u62ac\uff0c\u5c11\u5e74 \u5fd9\u9053\uff1a\u201c\u6ca1\u6709\uff01\u6ca1\u6709\uff01\u201d\u90a3\u8001\u8005\u672c\u60f3\u8981\u6768\u4ef2\u82f1\u6559\u8bad\u4ed6\u7684\u5973\u513f\u4e00\u987f\uff0c\u4f46\u60f3\u8d77\u81ea\u5df1\u7684\u513f\u5b50\u4e5f\u6709\u4e0d \u662f\uff0c\u6b32\u8bf4\u53c8\u7f62\uff0c\u8fd9\u65f6\u89c1\u6768\u4ef2\u82f1\u8ffd\u95ee\uff0c\u81ea\u5df1\u7684\u513f\u5b50\u7b54\u5f97\u5931\u9b42\u843d\u9b44\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u63a5\u8fc7\u8bdd\u78b4\u9053\uff1a \u201c\u6ca1\u6709\uff0c\u6ca1\u6709\uff01\u9752\u513f\u8868\u6f14\u6298\u7ffc\u4e0d\u4f24\u76ae\u7684\u795e\u5f39\u5999\u6280\uff0c\u628a\u51e0\u53ea\u9ec4\u83ba\u513f\u6253\u4e86\u4e0b\u6765\u3002\u201d\u6768\u4ef2\u82f1\u8fd9\u624d\u563f \u7136\u4e00\u7b11\uff0c\u65cb\u53c8\u548c\u5bb9\u8bf4\u9053\uff1a\u201c\u9ec4\u83ba\u513f\u5728\u5929\u4e0a\u98de\u5f97\u81ea\u7531\u81ea\u5728\uff0c\u5173\u4f60\u751a\u4e48\u4e8b\uff1f\u4ee5\u540e\u8981\u6253\uff0c\u4e5f\u53ea\u51c6\u4f60 \u6253\u9ebb\u9e70\u90a3\u7c7b\u731b\u79bd\u3002\u201d\u6768\u67f3\u9752\u5e94\u4e86\u58f0\uff1a\u201c\u7239\u8bf4\u5f97\u662f\u3002\u201d\u6768\u4ef2\u82f1\u7a81\u7136\u5de6\u624b\u643a\u90a3\u5c11\u5e74\uff0c\u53f3\u624b\u643a\u5510\u6653 \u6f9c\uff0c\u5c06\u4e24\u4eba\u51d1\u5728\u4e00\u8d77\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\uff0c\u4f60\u4eec\u5144\u5f1f\u89c1\u8fc7\u4e86\uff0c\u4ee5\u540e\u597d\u6709\u4e2a\u62db\u547c\uff01\u90b9 \u5927\u54e5\uff0c\u8fd9\u4f4d\u662f\u6211\u65b0\u6536\u7684\u5f92\u5f1f\uff0c\u59d3\u5510\u540d\u6653\u6f9c\u3002\u6653\u6f9c\uff0c\u8fd9\u4f4d\u8001\u82f1\u96c4\u4fbf\u662f\u6211\u5e38\u5bf9\u4f60\u8bf4\u8d77\u7684\u63d2\u7ffc\u795e\u72ee \u90b9\u9e23\u768b\u8001\u524d\u8f88\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u5531\u4e86\u4e2a\u558f\uff0c\u9053\uff1a\u201c\u4e45\u4ef0\uff0c\u4e45\u4ef0\uff01\u201d\u5c11\u5e74\u5ba2\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5f97\u7f6a\uff0c\u5f97\u7f6a\uff01\u201d\u4fa7\u76ee\u56de \u7768\uff0c\u6b63\u773c\u4e5f\u4e0d\u77a7\u5510\u6653\u6f9c\u4e00\u773c\uff0c\u5510\u6653\u6f9c\u5341\u5206\u7eb3\u7f55\uff0c\u4e0d\u77e5\u81ea\u5df1\u4ec0\u4e48\u5730\u65b9\u5f97\u7f6a\u4e86\u4ed6\u3002 \u3000\u3000\u5510\u6653\u6f9c\u4e0d\u77e5\uff0c\u8fd9\u4e00\u8001\u4e00\u5c11\u4e43\u662f\u6c42\u5a5a\u6765\u7684\u3002\u63d2\u7ffc\u795e\u72ee\u90b9\u9e23\u768b\u548c\u6768\u4ef2\u82f1\u662f\u751f\u6b7b\u4e4b\u4ea4\uff0c\u5eff\u591a\u5e74 \u524d\uff0c\u5e76\u79f0\u6cb3\u6714\u53cc\u96c4\u3002\u90b9\u9e23\u768b\u7684\u513f\u5b50\u9521\u4e5d\u6bd4\u6768\u67f3\u9752\u5927\u56db\u5c81\uff0c\u4e24\u4eba\u5728\u4e00\u8d77\u957f\u5927\uff0c\u9752\u6885\u7af9\u9a6c\uff0c\u73a9\u5f97 \u751a\u6b21\u3002\u5230\u6768\u67f3\u9752\u4e03\u5c81\u65f6\uff0c\u90b9\u9e23\u768b\u6709\u4e8b\u8fdc\u884c\uff0c\u72ec\u51fa\u8fbd\u4e1c\uff0c\u4e34\u884c\u65f6\u7b11\u5bf9\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u770b \u4ed6\u4eec\u4e24\u4e2a\u5b69\u5b50\u4e5f\u662f\u4e34\u522b\u4f9d\u4f9d\uff0c\u4e0d\u5fcd\u5206\u79bb\u5462\uff01\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u56de\u6765\uff0c\u6211\u628a\u9752\u513f\u7559\u7ed9\u9521\u4e5d \u505a\u5ab3\u5987\u513f\u597d\u5417\uff1f\u201d\u90b9\u9e23\u768b\u6c89\u601d\u6709\u503e\uff0c\u6168\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u6562\u60c5\u597d\uff01\u4f46\u5c0f\u5f1f\u6b64\u6b21\u51fa\u5173\uff0c\u5bf9\u4ed8\u672c\u95e8\u5f3a \u654c\uff0c\u5409\u51f6\u7978\u798f\uff0c\u4e8b\u5c5e\u96be\u6599\u3002\u5e78\u800c\u5f97\u80dc\uff0c\u6c5f\u6e56\u98ce\u6d6a\uff0c\u5175\u706b\u6d6e\u5bb6\uff0c\u4e5f\u4e0d\u77e5\u4f55\u65e5\u65b9\u5f97\u5f52\u6765\uff0c\u4e0e\u5927\u54e5 \u628a\u9152\u8bdd\u65e7\uff1f\u82e5\u6b64\u65f6\u7ed9\u8fd9\u4e24\u4e2a\u5b69\u5b50\u8ba2\u4e0b\u7ec8\u8eab\uff0c\u53ea\u6015\u4ed6\u65e5\u82e5\u6709\u5dee\u8fdf\uff0c\u8bef\u4e86\u4f84\u5973\u9752\u6625\u5e74\u534e\u3002\u6b64\u4e8b\u4e0d \u5982\u6162\u8c08\uff0c\u5f85\u4ed6\u65e5\u6211\u7236\u5b50\u5f52\u6765\uff0c\u4f84\u5973\u53c8\u8fd8\u672a\u8bb8\u914d\u4eba\u5bb6\u7684\u8bdd\uff0c\u90a3\u65f6\u518d\u63d0\u5427\uff01\u201d\u6768\u4ef2\u82f1\u4e00\u60f3\uff0c\u4e5f\u662f \u9053\u7406\uff0c\u5a5a\u4e8b\u4fbf\u6401\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u8c01\u77e5\u4e24\u4eba\u4e00\u522b\u4fbf\u662f\u4e00\u5341\u4e8c\u5e74\uff0c\u90b9\u9521\u4e5d\u662f\u4e2a\u4ec0\u4e48\u6837\u513f\uff0c\u6768\u67f3\u9752\u4e5f\u5168\u90fd\u5fd8\u4e86\uff0c\u6768\u4ef2\u82f1\u4e5f\u4ee5\u4e3a \u8001\u53cb\u5df2\u6b7b\uff0c\u65e5\u6e10\u6de1\u5fd8\u3002\u4e0d\u6599\u90b9\u9e23\u768b\u8fd8\u8bb0\u7740\u6b64\u4e8b\uff0c\u643a\u5b50\u5357\u5f52\uff0c\u767b\u95e8\u9020\u8bbf\u3002\u53c8\u4e0d\u6599\u5728\u540e\u5c71\u76f8\u9047\uff0c \u89c1\u5510\u6653\u6f9c\u548c\u6768\u67f3\u9752\u4eb2\u6635\u7684\u6837\u513f\uff0c\u4e24\u7236\u5b50\u90fd\u4e0d\u7981\u5f15\u8d77\u731c\u7591\u3002\u90a3\u90b9\u9521\u4e5d\u4e0e\u6768\u67f3\u9752\u4e00\u6837\uff0c\u4e5f\u662f\u72ec \u5b50\uff0c\u81ea\u5c0f\u5a07\u751f\u60ef\u517b\uff0c\u813e\u6c14\u4e5f\u662f\u9a84\u7eb5\u4e0d\u582a\uff0c\u56e0\u6b64\u7adf\u7136\u8fc1\u6012\u5230\u5510\u6653\u6f9c\u8eab\u4e0a\u3002 \u3000\u3000\u518d\u8bf4\u6768\u67f3\u9752\u6253\u4e86\u90b9\u9521\u4e5d\u4e00\u5f39\uff0c\u5fc3\u4e2d\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u6df1\u6015\u7236\u4eb2\u8d23\u5907\u3002\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u5411\u7236\u4eb2\u8bf7 \u5b89\uff0c\u4e0d\u6599\u7236\u4eb2\u5374\u6ee1\u9762\u5806\u6b22\uff0c\u62c8\u987b\u7b11\u9053\uff1a\u201c\u9752\u513f\uff0c\u4f60\u7684\u5c0f\u4f19\u4f34\u6765\u4e86\uff0c\u600e\u4e48\u4f60\u4e0d\u62db\u547c\u4ed6\u53bb\u73a9 \u513f\uff1f\u201d\u626c\u67f3\u9752\u628a\u5934\u4e00\u626d\uff0c\u683c\u683c\u7b11\u9053\uff1a\u201c\u73b0\u5728\u53c8\u4e0d\u662f\u5c0f\u5b69\u5b50\uff01\u201d\u626c\u4ef2\u82f1\u5e72\u54b3\u4e00\u58f0\uff0c\u7b11\u9053\uff1a\u201c\u662f \u554a\uff01\u662f\u5341\u4e5d\u5c81\u7684\u5927\u59d1\u5a18\u4e86\uff01\u9521\u4e5d\u8fd9\u5b69\u5b50\u8fd1\u5e74\u5728\u5173\u5916\u968f\u4ed6\u7239\u7239\uff0c\u9887\u95ef\u51fa\u4e86\u4e00\u70b9\u4e07\u513f\u3002\u770b\u4ed6\u6b66\u529f \u6280\u4e1a\uff0c\u4e5f\u662f\u4e0a\u4e58\u4e4b\u9009\uff0c\u4e0d\u77e5\u4f60\u610f\u4e0b\u5982\u4f55\uff1f\u201d\u6768\u67f3\u9752\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff01\u201d\u6768\u4ef2\u82f1 \u9053\uff1a\u201c\u90b9\u4f2f\u4f2f\u60f3\u8981\u4f60\u505a\u4ed6\u7684\u5ab3\u5987\u5462\uff01\u201d\u6768\u67f3\u9752\u500f\u7136\u53d8\u8272\uff0c\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u5ac1\uff01\u201d\u626c\u4ef2\u82f1\u6b63 \u8272\u8bf4\u9053\uff1a\u201c\u9752\u513f\uff0c\u4f60\u4e0d\u5c0f\u5566\uff0c\u8fd8\u8fd9\u6837\u6d51\uff0c\u7239\u96be\u9053\u8fd8\u80fd\u628a\u4f60\u517b\u8fc7\u4e16\uff1f\u4f60\u4e5f\u8be5\u61c2\u70b9\u4eba\u4e8b\u5566\uff01\u90b9\u5bb6 \u548c\u54b1\u4eec\u662f\u4e16\u4ea4\uff0c\u9521\u4e5d\u4eba\u53c8\u4e0d\u9519\uff0c\u4f60\u8fd8\u6709\u90a3\u70b9\u4e0d\u79f0\u5fc3\u7684\uff1f\u201d\u6768\u67f3\u9752\u672c\u6b32\u6492\u5a07\uff0c\u89c1\u7236\u4eb2\u8fd9\u6837\u8ba4 \u771f\uff0c\u4e00\u65f6\u95f4\u5012\u4e0d\u6562\u8bf4\u8bdd\u3002\u6768\u4ef2\u82f1\u53c8\u9053\uff1a\u201c\u8fd9\u56de\u4f60\u4e0d\u7b54\u5e94\u4e5f\u4e0d\u884c\uff0c\u4f60\u4e03\u5c81\u7684\u65f6\u5019\uff0c\u6211\u5df2\u5c06\u4f60\u8bb8 \u7ed9\u4eba\u5bb6\u4e86\uff01\u201d\u6768\u67f3\u9752\u773c\u73e0\u4e00\u8f6c\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u7239\u8981\u5973\u513f\u6210\u5a5a\uff0c\u4e5f\u5f97\u4f9d\u5973\u513f\u4e00\u4e8b\u3002\u201d\u6768\u4ef2\u82f1 \u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u4f60\u8bf4\uff01\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u7239\u7239\u5a01\u9707\u6cb3\u6714\uff0c\u5973\u5a7f\u4e5f\u5f53\u662f\u4e2a\u51fa\u7c7b\u62d4\u8403\u7684\u82f1\u96c4\uff01\u201d\u6768 \u4ef2\u82f1\u4e50\u9053\uff1a\u201c\u662f\u5440\uff0c\u8bf4\u5f97\u4e0d\u9519\uff01\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6211\u8981\u548c\u4ed6\u5148\u884c\u6bd4\u6b66\uff0c\u7136\u540e\u8bba\u5a5a\uff01\u201d \u3000\u3000\u6768\u4ef2\u82f1\u6115\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u548c\u4eba\u5bb6\u6bd4\u6b66\uff1f\u201d\u6768\u67f3\u9752\u7b11\u9053\uff1a\u201c\u4ed6\u82e5\u8d62\u5f97\u4e86\u5973\u513f\uff0c\u5973\u513f\u81ea\u7136 \u7518\u5fc3\u60c5\u613f\u505a\u4ed6\u5ab3\u5987\uff0c\u82e5\u8d62\u4e0d\u4e86\u5462\uff0c\u7239\u8981\u8fd9\u6837\u6ca1\u6709\u672c\u4e8b\u7684\u5973\u5a7f\uff0c\u9762\u4e0a\u4e5f\u6ca1\u5149\u5f69\u3002\u201d\u6768\u4ef2\u82f1\u9053\uff1a \u201c\u5973\u513f\u5bb6\u901e\u5f3a\u9738\u9053\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5916\u9762\u5e18\u5b50\u4e00\u63ed\uff0c\u90b9\u5bb6\u7236\u5b50\u8fc8\u6b65\u8fdb\u6765\u3002\u6768\u67f3\u9752\u8bf7\u4e86\u4e2a\u5b89\uff0c \u4e00\u6e9c\u70df\u8dd1\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u90b9\u9521\u4e5d\u7ed9\u6768\u67f3\u9752\u6253\u574f\u95e8\u7259\uff0c\u4e24\u65e5\u6765\u5140\u81ea\u6c14\u95f7\uff0c\u8fd9\u65e5\u4e00\u65e9\u968f\u7236\u8981\u6765\u7ed9\u6768\u4ef2\u82f1\u8bf7\u5b89\uff0c\u4e00\u5230\u95e8 \u5916\uff0c\u5c31\u542c\u5f97\u6768\u67f3\u9752\u5927\u58f0\u8bf4\u8bdd\uff0c\u4e0d\u89c9\u505c\u4e0b\u811a\u6b65\uff0c\u90a3\u77e5\u4e0d\u542c\u72b9\u53ef\uff0c\u4e00\u542c\u4e4b\u4e0b\uff0c\u9762\u8272\u5168\u90fd\u53d8\u4e86\u3002\u90b9 \u9e23\u768b\u5fc3\u60f3\uff1a\u201c\u6768\u4ef2\u82f1\u5973\u513f\u8fd9\u6837\u7c97\u91ce\uff0c\u9521\u513f\u4e5f\u5f97\u663e\u663e\u672c\u4e8b\uff0c\u4ee5\u540e\u624d\u597d\u7ba1\u675f\uff01\u5979\u662f\u4e2a\u5973\u5b69\u513f\u5bb6\uff0c \u672c\u4e8b\u518d\u597d\uff0c\u4e5f\u8d5b\u4e0d\u8fc7\u9521\u513f\u3002\u5979\u7684\u5e08\u5144\u4e5f\u8fd8\u4e0d\u662f\u9521\u513f\u5bf9\u624b\uff0c\u9521\u513f\u5bf9\u5979\u600e\u6837\u4e5f\u8f93\u4e0d\u4e86\u3002\u201d\u548c\u6768\u4ef2 \u82f1\u5bd2\u6684\u8fc7\u540e\uff0c\u5f00\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6628\u65e5\u63d0\u4eb2\uff0c\u8499\u5927\u54e5\u4e0d\u5acc\u5c0f\u513f\u7c97\u52a3\uff0c\u6168\u7136\u4fef\u5141\uff0c\u4f46\u513f\u5927\u5973 \u5927\uff0c\u7236\u6bcd\u4e5f\u4e0d\u597d\u4e13\u65ad\u4e13\u884c\uff0c\u4e0d\u77e5\u4f84\u5973\u7684\u5fc3\u610f\u5982\u4f55\uff1f\u201d\u6768\u4ef2\u82f1\u652f\u543e\u5bf9\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u2026\u2026\u201d\u90b9\u9521 \u4e5d\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u4f2f\u4f2f\u5a01\u9707\u6cb3\u6714\uff0c\u5c06\u95e8\u864e\u5973\uff0c\u9752\u59b9\u81ea\u7136\u662f\u5dfe\u5e3c\u82f1\u96c4\u3001\u5973\u4e2d\u4fca\u6770\u7684\u4e86\u3002\u4f84\u513f\u4e0d\u81ea \u91cf\u529b\uff0c\u60f3\u8bf7\u9752\u59b9\u6307\u70b9\u51e0\u62db\uff0c\u82e5\u7136\u76f8\u5dee\u8fc7\u8fdc\uff0c\u4f84\u513f\u4e5f\u65e0\u989c\u518d\u5f85\u51e0\u6756\uff0c\u90a3\u5c31\u8981\u8bf7\u4ee4\u5ad2\u522b\u8ba2\u826f\u7f18\uff0c \u53e6\u9009\u9ad8\u624d\u3002\u201d\u6768\u4ef2\u82f1\u4e00\u542c\uff0c\u77e5\u9053\u5973\u513f\u6240\u8bf4\u7684\u8bdd\uff0c\u5df2\u5168\u7ed9\u4ed6\u4eec\u7236\u5b50\u542c\u53bb\uff0c\u5fd9\u4e0d\u8fed\u7684\u529d\u9053\uff1a\u201c\u4f60 \u7684\u9752\u59b9\u662f\u5c0f\u5b69\u813e\u6c14\uff0c\u4e0d\u77e5\u8f7b\u91cd\uff0c\u8d24\u4f84\u8981\u591a\u591a\u62c5\u5f85\u3002\u201d\u90b9\u9e23\u768b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u8001\u5144\u5f1f\u4e86\uff0c\u8fd8 \u8bf4\u8fd9\u4e2a\u5e72\u561b\uff1f\u4fd7\u8bed\u8bf4\u5f97\u597d\uff0c\u5339\u914d\u5339\u914d\uff0c\u8981\u624d\u8c8c\u767b\u5bf9\uff0c\u624d\u662f\u826f\u597d\u59fb\u7f18\u3002\u54b1\u4eec\u5e38\u542c\u8bf4\u4e66\uff0c\u8bf4\u8d77\u8bfb \u4e66\u4eba\u5bb6\u7684\u2018\u624d\u5973\u2019\u90fd\u8981\u96be\u96be\u65b0\u90ce\uff0c\u8003\u5f97\u5408\u683c\uff0c\u624d\u8bb8\u6d1e\u623f\u82b1\u70db\u3002\u54b1\u4eec\u7ec3\u6b66\u4eba\u5bb6\uff0c\u8ba9\u513f\u5973\u6bd4\u6bd4\u62f3 \u811a\uff0c\u7136\u540e\u8ba2\u5a5a\uff0c\u8fd9\u4e5f\u662f\u6b66\u6797\u4f73\u8bdd\u5440\uff01\u53c8\u4e0d\u662f\u771f\u5200\u771f\u67aa\uff0c\u62fc\u547d\u4e4b\u4e8b\uff0c\u70b9\u5230\u5373\u6b62\uff0c\u65e0\u4f24\u5927\u7a1a\uff0c\u53c8 \u6709\u4f55\u59a8\uff01\u201d\u6768\u4ef2\u82f1\u6c89\u541f\u534a\u664c\uff0c\u89c1\u90b9\u9521\u4e5d\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5b69\u5b50\u4e5f\u6709\u5fd7\u6c14\uff0c\u82e5\u4e0d\u8ba9\u4ed6\u4eec\u6bd4 \u8bd5\u4e00\u4e0b\uff0c\u8fd9\u6bb5\u59fb\u7f18\u4e5f\u96be\u64ae\u5408\u3002\u201d\u5f53\u4e0b\u6168\u7136\u5141\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u542c\u5f97\u90b9\u5bb6\u63d0\u4eb2\u4e4b\u4e8b\uff0c\u6ee1\u5fc3\u6b22\u559c\uff0c\u4ed6\u867d\u7136\u4e0d\u6ee1\u610f\u90b9\u9521\u4e5d\u7684\u9a84\u6a2a\uff0c\u4f46\u60f3\u8d77\u7537\u5973\u4e24\u5bb6\u95e8 \u5f53\u6237\u5bf9\uff0c\u800c\u4e14\u5e08\u59b9\u4e5f\u662f\u90a3\u6837\u7684\u6027\u5b50\uff0c\u4e24\u4e2a\u6027\u60c5\u76f8\u540c\u7684\u4eba\u51d1\u5728\u4e00\u8d77\uff0c\u4e5f\u8bb8\u76f8\u5904\u5f97\u6765\u3002\u56e0\u6b64\u8877\u5fc3 \u9ad8\u5174\uff0c\u53bb\u5411\u5e08\u59b9\u9053\u8d3a\uff0c\u6768\u67f3\u9752\u7768\u4e86\u4ed6\u4e00\u773c\uff0c\u5ffd\u7136\u683c\u683c\u7b11\u9053\uff1a\u201c\u50bb\u5e08\u5144\uff0c\u4f60\u77a7\u7740\u597d\u4e86\uff01\u201d \u3000\u3000\u5f53\u665a\u6768\u5bb6\u7684\u7ec3\u6b66\u573a\u4e0a\u706b\u628a\u901a\u660e\uff0c\u6768\u67f3\u9752\u7a7f\u7740\u6e56\u6c34\u7eff\u77ed\u8863\uff0c\u8170\u7cfb\u5927\u7ea2\u624b\u5dfe\uff0c\u5728\u573a\u5fc3\u7b11\u541f\u541f \u7ad9\u5b9a\uff0c\u90b9\u9521\u4e5d\u77a7\u5f97\u5fc3\u75d2\u75d2\u7684\uff0c\u5fc3\u60f3\uff1a\u201c\u770b\u5979\u7684\u6837\u513f\uff0c\u4e0d\u8fc7\u662f\u60f3\u8003\u8f83\u6211\u7684\u529f\u592b\uff0c\u5fc3\u91cc\u5df2\u662f\u5343\u80af \u767e\u613f\u4e86\uff0c\u6211\u4e5f\u5f97\u89c1\u597d\u4fbf\u6536\uff0c\u4e0d\u80fd\u771f\u4e2a\u548c\u5979\u76f8\u6253\u3002\u201d\u90a3\u6599\u4e24\u4eba\u62b1\u62f3\u4e00\u63d6\uff0c\u90b9\u9521\u4e5d\u521a\u8bf4\u5f97\u58f0\uff1a \u201c\u9752\u59b9\uff0c\u8bf7\u8fdb\u62db\u3002\u201d\u6768\u67f3\u9752\u5c0f\u81c2\u4e00\u5f2f\uff0c\u84e6\u7136\u5c31\u662f\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u6708\u201d\uff0c\u624b\u6307\u70b9\u5411\u80f8\u819b\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u7adf\u662f\u6768\u5bb6\u201c\u51cc\u4e91\u638c\u201d\u4e2d\u7684\u5389\u5bb3\u6740\u624b\uff0c\u4f3c\u865a\u4f3c\u5b9e\uff0c\u4f3c\u6309\u4f3c\u70b9\u3002\u90b9\u9521\u4e5d\u60ca\u53eb\u4e00\u58f0\uff0c\u626d \u8170\u75be\u95ea\uff0c\u4e24\u817f\u7075\u6d3b\uff0c\u7528\u201c\u98ce\u521a\u843d\u82b1\u201d\u7684\u8eab\u6cd5\uff0c\u8fde\u8eb2\u4e09\u62db\u3002\u6768\u67f3\u9752\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5927\u54e5\u4e0d\u5fc5\u5ba2\u6c14 \u5440\uff01\u201d\u624b\u5e95\u4e1d\u6beb\u4e0d\u7f13\uff0c\u8ddf\u8e2a\u76f4\u8fdb\uff0c\u7528\u638c\u4e00\u6258\u9521\u4e5d\u8098\u5c16\uff0c\u624b\u638c\u9aa4\u7136\u4ece\u53f3\u8098\u4e0b\u7a7f\u51fa\uff0c\u4e00\u62db\u201c\u53f6\u5e95 \u5077\u6843\u201d\uff0c\u76f4\u5411\u654c\u65b9\u53f3\u80c1\u731b\u88ad\uff0c\u62db\u52bf\u7d27\u75be\uff0c\u7adf\u4f3c\u654c\u6211\u6b7b\u751f\u76f8\u62fc\uff0c\u54ea\u662f\u597d\u53cb\u6bd4\u6b66\u8bd5\u62db\uff01\u5510\u6653\u6f9c \u201c\u554a\u5440\u201d\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u90b9\u9521\u4e5d\u8eab\u5f62\u4e00\u659c\uff0c\u624b\u8155\u4e00\u7ed5\uff0c\u628a\u5168\u8eab\u5f2f\u6210\u4fa7\u7acb\u7684\u5f13\u5f62\uff0c\u4e24\u638c\u5e73\u63a8\u4f3c \u7bad\uff0c\u529b\u731b\u5982\u5c71\uff0c\u5982\u679c\u662f\u7528\u5b9e\uff0c\u6768\u67f3\u9752\u5fc5\u7136\u8981\u8dcc\u5012\u573a\u5fc3\uff0c\u4f46\u90b9\u9521\u4e5d\u4e0d\u6562\u7528\u5b9e\uff0c\u529b\u53d1\u4fbf\u6536\uff0c\u800c\u6768 \u67f3\u9752\u4e5f\u6e9c\u6ed1\u975e\u5e38\uff0c\u4f3c\u65e9\u5df2\u9884\u6599\u4ed6\u6709\u8fd9\u4e00\u62db\u3001\u4e00\u65cb\u8eab\uff0c\u4f3c\u628a\u540e\u80cc\u4ea4\u7ed9\u654c\u4eba\uff0c\u90b9\u9521\u4e5d\u638c\u529b\u672a\u5230\uff0c \u5979\u5df2\u7ea4\u8170\u4e00\u626d\uff0c\u8f7b\u98d8\u98d8\u7684\u4e00\u63a0\uff0c\u7a81\u7136\u62d4\u9ad8\u4e00\u4e08\u4e94\u516d\uff0c\u500f\u7136\u843d\u5230\u90b9\u9521\u4e5d\u80cc\u540e\u3002\u90b9\u9521\u4e5d\u6025\u65cb\u8eab\uff0c \u63a2\u81c2\u6765\u6293\uff0c\u201c\u556a\u5566\u201d\u4e00\u58f0\uff0c\u80a9\u5934\u5df1\u4e2d\u4e86\u4e00\u638c\u3002\u6768\u4ef2\u82f1\u53eb\u9053\uff1a\u201c\u4f84\u513f\uff0c\u4f60\u653e\u5fc3\u6253\u7f62\uff0c\u4e0d\u5fc5\u8001\u662f \u9000\u8ba9\uff01\u201d\u90b9\u9521\u4e5d\u811a\u8ddf\u4e00\u8f6c\uff0c\u4e00\u4e2a\u201c\u602a\u87d2\u7ffb\u8eab\u201d\uff0c\u8eab\u5f62\u534a\u8f6c\uff0c\u4e94\u884c\u62f3\u5f80\u4e0a\u4e00\u51b2\uff0c\u8f69\u7709\u7ed5\u638c\uff0c\u4e00 \u51b2\u4e00\u7ed5\uff0c\u75be\u5982\u95ea\u7535\uff0c\u6293\u7740\u6768\u67f3\u9752\u53f3\u81c2\u5411\u5916\u4e00\u5f2f\uff0c\u6559\u5979\u5de6\u81c2\u4e0d\u80fd\u76f8\u6551\uff0c\u6b63\u5f85\u7528\u811a\u4e00\u63d2\uff0c\u5411\u5916\u4e00 \u62d6\uff0c\u628a\u67f3\u770b\u6482\u5012\uff0c\u90b9\u9e23\u768b\u548c\u5510\u6653\u6f9c\u559c\u5f62\u4e8e\u8272\uff0c\u6ee1\u4ee5\u4e3a\u90b9\u9521\u4e5d\u6b64\u62db\u5fc5\u80dc\uff0c\u5a5a\u4e8b\u80fd\u8c10\uff0c\u4e0d\u6599\u6768\u67f3 \u9752\u4e00\u7ffb\u4e00\u7ed5\uff0c\u65e9\u5df2\u593a\u51fa\u624b\u6765\uff0c\u5510\u6653\u6f9c\u7adf\u672a\u77a7\u51fa\u6768\u67f3\u9752\u600e\u6837\u8131\u9669\u7834\u62db\uff0c\u4f46\u542c\u90b9\u9521\u4e5d\u201c\u54ce\u54df\u201d\u4e00 \u58f0\uff0c\u80a9\u5934\u53c8\u4e2d\u4e86\u4e00\u638c\uff01 \u3000\u3000\u90b9\u9e23\u768b\u9053\uff1a\u201c\u4f84\u5973\u8fd9\u4e24\u62db\u7384\u5973\u6446\u8896\u3001\u4e09\u73af\u5957\u6708\uff0c\u7528\u5f97\u4e0d\u9519\uff01\u201d\u6768\u4ef2\u82f1\u76b1\u7709\u9053\uff1a\u201c\u5176\u5b9e\u5979 \u7684\u529f\u592b\u5728\u4ee4\u90ce\u4e4b\u4e0b\uff0c\u53ea\u662f\u5929\u751f\u597d\u80dc\uff0c\u4e0d\u80af\u670d\u8f93\uff0c\u9521\u4f84\u53ea\u8981\u4ee5\u6c89\u7a33\u7684\u4e0b\u76d8\u529f\u592b\u5bf9\u5979\u7684\u98d8\u5ffd\u8eab \u6cd5\uff0c\u4e0d\u5fc5\u6025\u4e8e\u6c42\u653b\uff0c\u5c31\u53ef\u8d62\u4e86\uff01\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u5f88\u5927\u58f0\uff0c\u5206\u660e\u662f\u60f3\u8ba9\u573a\u4e2d\u90b9\u9521\u4e5d\u542c\u89c1\uff01 \u3000\u3000\u90b9\u9521\u4e5d\u58f0\u4eba\u5fc3\u901a\uff0c\u4e94\u884c\u62f3\u4e00\u4e2a\u53d8\u62db\uff0c\u5f3a\u5f13\u786c\u9a6c\uff0c\u4e0a\u76d8\u4e0d\u52a8\uff0c\u4e0b\u76d8\u4e00\u6362\uff0c\u547c\u547c\u4e24\u62f3\uff0c\u7a7f\u68ad \u822c\u6253\u51fa\u53bb\u3002\u6768\u67f3\u9752\u672c\u529b\u4e0d\u53ca\u4eba\u5bb6\uff0c\u4e58\u6687\u8e48\u9699\uff0c\u62a2\u653b\u6570\u62db\uff0c\u6ca1\u6709\u653b\u8fdb\uff0c\u970d\u5730\u98d8\u8eab\uff0c\u4ece\u90b9\u9521\u4e5d\u8eab \u4fa7\u63a0\u8fc7\uff0c\u7528\u4e00\u79cd\u8f7b\u89c6\u4e4b\u6781\u7684\u53e3\u543b\u5728\u4ed6\u8033\u8fb9\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u6015\u4f60\u5f97\u4eba\u6307\u70b9\uff0c\u4f60\u4e5f\u53ea\u6709\u6328\u6253\u7684\u4efd \u513f\uff01\u201d\u8bed\u58f0\u8bf4\u5f97\u6781\u4f4e\uff0c\u573a\u8fb9\u7684\u4eba\u90fd\u542c\u4e0d\u89c1\uff0c\u90b9\u9521\u4e5d\u5374\u5982\u7ed9\u5229\u8292\u523a\u4e86\u4e00\u4e0b\uff0c\u66b4\u8df3\u5982\u96f7\uff0c\u95f7\u58f0\u4e0d \u54cd\uff0c\u637b\u62f3\u653b\u4e0a\uff0c\u5fc3\u60f3\uff1a\u6211\u90b9\u9521\u4e5d\u7eb5\u6a2a\u5173\u5916\uff0c\u8c01\u4e0d\u8d5e\u6211\u5c11\u5e74\u82f1\u96c4\uff0c\u5c82\u5bb9\u4f60\u8fd9\u91ce\u4e2b\u5934\u5c0f\u89c6\uff01\u5de6\u638c \u6a2a\u80f8\uff0c\u53f3\u62f3\u731b\u6363\uff0c\u8fde\u7528\u201c\u6076\u864e\u638f\u5fc3\u201d\u201c\u91ce\u9a6c\u8df3\u6da7\u201d\u201c\u5927\u87d2\u541e\u9e70\u201d\u7b49\u51f6\u731b\u62db\u6570\u3002\u8d8a\u6597\u8d8a\u70c8\uff0c\u62f3 \u884c\u5982\u98ce\uff0c\u6768\u67f3\u9752\u7684\u7cfb\u8170\u7ea2\u5dfe\uff0c\u4e5f\u7ed9\u9707\u8361\u5f97\u98d8\u98d8\u6b32\u8d77\uff0c\u6768\u67f3\u9752\u5b9b\u4f3c\u7a7f\u82b1\u8774\u8776\uff0c\u5728\u62f3\u98ce\u4e2d\u98d8\u6765\u6643 \u53bb\uff0c\u5510\u6653\u6f9c\u5b9a\u795e\u89c2\u770b\uff0c\u6768\u67f3\u9752\u867d\u7136\u5916\u4f3c\u8f7b\u677e\uff0c\u5185\u91cc\u7ade\u662f\u8fde\u4e0b\u6740\u624b\uff01 \u3000\u3000\u5510\u6653\u6f9c\u6697\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d\u770b\u5e08\u5085\u65f6\uff0c\u4e5f\u662f\u7709\u5934\u6df1\u9501\uff0c\u795e\u8272\u7d27\u5f20\u3002\u5510\u6653\u6f9c\u76f4\u6d12\u51b7\u6c57\uff0c\u770b\u573a \u4e2d\u4e24\u4eba\u7ffb\u7ffb\u6eda\u6eda\uff0c\u8df3\u8dc3\u5982\u98de\uff0c\u76d8\u65cb\u8f6c\u6218\uff0c\u53c8\u5df2\u6298\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u8d8a\u6597\u8d8a\u7d27\uff0c\u90b9\u9521\u4e5d\u62db\u52bf\u6025\u4f3c\u72c2 \u98ce\u66b4\u96e8\uff0c\u6768\u67f3\u9752\u8eab\u5f62\u8f7b\u82e5\u843d\u7d6e\u98de\u82b1\uff0c\u7ee3\u5e26\u7ea2\u5dfe\uff0c\u968f\u98ce\u98d8\u821e\u3002\u90b9\u9e23\u768b\u672c\u6765\u795e\u8272\u8f7b\u677e\uff0c\u8c08\u7b11\u81ea \u82e5\uff0c\u800c\u4eca\u4e5f\u53d8\u4e86\u989c\u8272\uff0c\u4e0d\u81ea\u89c9\u7684\u968f\u7740\u6768\u4ef2\u82f1\u4e00\u6b65\u6b65\u632a\u8fd1\u573a\u5fc3\u3002 \u3000\u3000\u6768\u67f3\u9752\u7684\u638c\u6cd5\u4e43\u5bb6\u4f20\u7edd\u6280\uff0c\u6bd4\u5510\u6653\u6f9c\u8fd8\u8981\u5389\u5bb3\u51e0\u5206\u3002\u90b9\u9521\u4e5d\u529f\u592b\u867d\u6bd4\u5979\u9ad8\uff0c\u6c14\u529b\u867d\u6bd4\u5979 \u5927\uff0c\u4f46\u5728\u638c\u6cd5\u4e0a\u5374\u8981\u900a\u4e86\u4e00\u7b79\u3002\u52a0\u4ee5\u521d\u4e0a\u6765\u65f6\uff0c\u5fc3\u5b58\u987e\u5fcc\uff0c\u62f3\u811a\u7559\u60c5\uff0c\u5148\u5403\u4e86\u4e8f\uff0c\u7ee7\u8fd9\u7ed9\u6768 \u67f3\u9752\u62ff\u8bdd\u4e00\u6fc0\uff0c\u53c8\u52a8\u4e86\u6c14\uff0c\u6bd4\u6b66\u6700\u5fcc\u6025\u8e81\u987e\u5fcc\uff0c\u6025\u8e81\u5219\u6d6e\u52a8\u4e0d\u5b89\uff0c\u6613\u4e3a\u654c\u4e58\uff0c\u987e\u5fcc\u5219\u6bcf\u5931\u673a \u5148\uff0c\u6613\u4e3a\u654c\u5236\u3002\u90b9\u9521\u4e5d\u731b\u653b\u4e0d\u4e0b\uff0c\u9669\u8c61\u73af\u751f\uff0c\u84e6\u4f7f\u9669\u62db\uff0c\u4e00\u62db\u201c\u7389\u5973\u7a7f\u68ad\u201d\u5411\u524d\u4e00\u653b\uff0c\u6768\u67f3 \u9752\u970d\u5730\u4e00\u8f6c\u4e8c\uff0c\u63a9\u5230\u654c\u4eba\u8eab\u540e\uff0c\u8d81\u90b9\u9521\u4e5d\u672a\u53ca\u53d8\u62db\uff0c\u53cc\u638c\u7c98\u7740\u540e\u5fc3\uff0c\u8fd0\u529b\u4e00\u63a8\uff0c\u90b9\u9521\u4e5d\u84e6\u89c9 \u9510\u98ce\u8d34\u8eab\u800c\u8fdb\uff0c\u8981\u5411\u524d\u7a9c\uff0c\u6015\u5979\u5c31\u62db\u8d76\u62db\uff0c\u529b\u4e0a\u52a0\u529b\uff0c\u518d\u63a8\u4e00\u4e0b\uff0c\u81ea\u5df1\u5fc5\u7136\u8dcc\u5012\uff0c\u8981\u5411\u65c1 \u7a9c\uff0c\u53c8\u6015\u5979\u501f\u52bf\u7275\u5f13\uff0c\u638c\u51fb\u7a7a\u95e8\u3002\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u5239\u90a3\uff0c\u90b9\u9521\u4e5d\u6076\u6c14\u987f\u751f\uff0c\u65e0\u6687\u8003\u8651\uff0c\u7acb\u5373 \u4e00\u4e2a\u201c\u65cb\u8f6c\u4e7e\u5764\u201d\uff0c\u56de\u8fc7\u8eab\u6765\uff0c\u7adf\u4e0d\u6551\u62db\uff0c\u53cd\u53d6\u653b\u52bf\uff0c\u53f3\u638c\u5411\u5916\u4e00\u6302\uff0c\u5de6\u62f3\u7ffb\u8d77\uff0c\u4e00\u4e2a\u201c\u7f9a \u7f8a\u6302\u89d2\u201d\uff0c\u6076\u72e0\u72e0\u7167\u6768\u67f3\u9752\u9762\u95e8\u6253\u6765\u3002\u5510\u6653\u6f9c\u770b\u5f97\u80c6\u6218\u5fc3\u60ca\uff0c\u521a\u624d\u662f\u6015\u90b9\u9521\u4e5d\u8840\u6e85\u5c18\u57c3\uff0c\u800c \u4eca\u5219\u662f\u6015\u5e08\u59b9\u5f53\u573a\u53d7\u635f\uff0c\u4e00\u58f0\u201c\u90b9\u5144\u5f1f\u624b\u4e0b\u7559\u60c5\uff01\u201d\u5c1a\u672a\u51fa\u53e3\uff0c\u573a\u8fb9\u7684\u4e24\u4e2a\u8001\u4eba\u5bb6\u5df2\u5927\u58f0\u547c \u53eb\uff0c\u90b9\u9e23\u768b\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u6211\u4eec\u8ba4\u8f93\u4e86\uff0c\u59d1\u5a18\u4f60\u4e0d\u8981\u8d76\u5c3d\u6740\u7edd\uff01\u201d\u6768\u4ef2\u82f1\u6025\u58f0\u53eb\u9053\uff1a\u201c\u9752\u513f\uff0c \u4e0d\u8bb8\u80e1\u6765\uff01\u201d\u5510\u6653\u6f9c\u4e00\u6115\uff0c\u84e6\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u90b9\u9521\u4e5d\u6740\u732a\u822c\u72c2\u55e5\u6012\u53eb\uff0c\u5012\u5728\u5730\u4e0b\u6eda\u6210\u4e00 \u4e2a\u571f\u7403\u4e00\u822c\uff0c\u90b9\u9e23\u768b\u4e00\u628a\u5c06\u4ed6\u6276\u8d77\uff0c\u9762\u76ee\u5b8c\u5168\u53d8\u8272\uff0c\u90b9\u9521\u4e5d\u7684\u53f3\u81c2\u5173\u8282\u5904\u5df2\u7ecf\u6298\u65ad\uff0c\u624b\u81c2\u540a \u4e86\u4e0b\u6765\uff0c\u75db\u5f97\u9ec4\u8c46\u822c\u7684\u6c57\u73e0\u9897\u9897\u6ef4\u4e0b\uff0c\u989d\u4e0a\u9752\u7b4b\u6bd5\u73b0\u3002\u539f\u6765\u662f\u6768\u67f3\u9752\u8d81\u4ed6\u4f7f\u7528\u9669\u62db\u4e4b\u9645\uff0c\u9aa4 \u4e0b\u6740\u624b\uff0c\u638c\u671d\u4ed6\u81c2\u5f2f\u4e4b\u5904\u6253\u53bb\uff0c\u8d81\u52bf\u5411\u5916\u4e00\u62d7\uff0c\u6768\u5bb6\u94c1\u638c\uff0c\u5c82\u6bd4\u5bfb\u5e38\uff0c\u5173\u8282\u5904\u4e2d\u4e86\u4e00\u638c\u5df2\u4e0d \u5f97\u4e86\uff0c\u66f4\u90a3\u582a\u6768\u67f3\u9752\u53c8\u4e00\u62d7\u4e00\u626d\u3002\u90b9\u9521\u4e5d\u547b\u541f\u558a\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u597d\u72e0\uff01\u201d\u90b9\u9e23\u768b\u4e00\u58f0\u4e0d\u54cd\uff0c \u6258\u8d77\u4ed6\u7684\u624b\u81c2\uff0c\u786c\u751f\u751f\u5f80\u4e0a\u4e00\u8fde\uff0c\u6495\u788e\u6c57\u886b\uff0c\u6025\u884c\u5305\u88f9\u3002\u6768\u4ef2\u82f1\u5439\u987b\u77aa\u773c\uff0c\u6012\u6781\u6c14\u6781\uff0c\u84e6\u7136 \u8de8\u524d\u4e00\u6b65\uff0c\u624b\u8d77\u4e00\u638c\uff0c\u7adf\u671d\u7231\u5973\u5929\u7075\u76d6\u6253\u4e0b\uff0c\u6da9\u58f0\u65a5\u9053\uff1a\u201c\u6211\u628a\u4f60\u8fd9\u91ce\u4e2b\u5934\u5e9f\u4e86\uff01\u201d\u94c1\u638c\u9ad8 \u4e3e\uff0c\u5c06\u843d\u672a\u843d\uff0c\u90b9\u9e23\u768b\u84e6\u7136\u8dc3\u8d77\uff0c\u5f80\u4e0a\u4e00\u67b6\uff0c\u9510\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u602a\u53ea\u602a\u5c0f\u513f\u5b66\u6280\u672a\u7cbe\uff0c\u4ed6 \u867d\u62dc\u9886\u59d1\u5a18\u94c1\u638c\uff0c\u8fd8\u672a\u6b8b\u5e9f\u5f97\u4e86\uff01\u7eed\u7b4b\u9a73\u9aa8\uff0c\u6211\u5c1a\u72b9\u4e3a\uff0c\u5927\u54e5\u4f60\u4e0d\u5fc5\u62c5\u5fc3\uff01\u81f3\u4e8e\u5a5a\u4e8b\uff0c\u518d\u4e5f \u4f11\u63d0\uff0c\u4f8d\u5c0f\u513f\u82e6\u5b66\u5341\u5e74\uff0c\u90a3\u65f6\u82e5\u6709\u5bf8\u8fdb\uff0c\u518d\u8bf7\u59d1\u5a18\u6307\u6559\uff01\u201d\u6768\u4ef2\u82f1\u542c\u4ed6\u53e3\u6c14\u8f6f\u4e2d\u5e26\u786c\uff0c\u60f3\u662f \u6124\u6168\u5df2\u6781\uff01\u773c\u6cea\u4e0d\u7531\u6d8c\u51fa\uff0c\u50f5\u5728\u90a3\u513f\uff01 \u3000\u3000\u6768\u4ef2\u82f1\u7edd\u672a\u6599\u5230\u51e0\u5341\u5e74\u8001\u53cb\uff0c\u7adf\u95f9\u5230\u8fd9\u4e2a\u7530\u5730\uff0c\u6cea\u6d8c\u5fc3\u9178\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u90b9\u9e23\u768b\u84e6\u7136\u5c06\u513f \u5b50\u80cc\u4e0a\u4e00\u642d\uff0c\u5982\u98de\u8dd1\u51fa\uff0c\u6768\u4ef2\u82f1\u6014\u5728\u5f53\u573a\uff0c\u6b32\u5f85\u524d\u8ffd\uff0c\u53ea\u89c9\u4e24\u817f\u6d6e\u8f6f\u65e0\u529b\uff0c\u4f46\u542c\u5f97\u90b9\u9e23\u768b\u7684 \u8bdd\u58f0\u65ad\u7eed\u98d8\u6765\uff1a\u201c\u54b1\u4eec\u5144\u5f1f\u4e4b\u60c5\u72b9\u5728\uff0c\u513f\u5973\u4e4b\u4e8b\u4f11\u63d0\uff01\u201d\u4e24\u4eba\u7ffb\u4e0b\u5c71\u5761\uff0c\u80cc\u5f71\u4e5f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u94c1\u9752\u7740\u8138\uff0c\u5411\u5973\u513f\u65a5\u8bf4\uff1a\u201c\u91ce\u4e2b\u5934\uff0c\u4f60\u968f\u6211\u6765\u3002\u201d\u5510\u6653\u6f9c\u6218\u6218\u5162\u5162\uff0c\u968f\u5728\u540e\u9762\uff0c \u4ed6\u6df1\u6015\u5e08\u5085\u6012\u706b\u5934\u4e0a\uff0c\u5211\u8d23\u8fc7\u5f53\uff0c\u6216\u8005\u4f1a\u628a\u5e08\u59b9\u5f04\u6210\u6b8b\u5e9f\uff0c\u5e9f\u53bb\u6b66\u529f\uff0c\u56e0\u6b64\u60f4\u60f4\u4e0d\u5b89\uff0c\u4ea6\u6b65 \u4ea6\u8d8b\uff0c\u60f3\u5728\u7d27\u6025\u5173\u5934\uff0c\u7ed9\u4ed6\u4eec\u7236\u5973\u8c03\u89e3\u3002\u4e0d\u6599\u6768\u4ef2\u82f1\u53cc\u773c\u4e00\u7ffb\uff0c\u4e0d\u5ba2\u6c14\u7684\u65a5\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u4f60 \u8ddf\u6765\u4f5c\u751a\uff1f\u4e0d\u5e72\u4f60\u7684\u4e8b\uff0c\u4f60\u81ea\u4e2a\u513f\u73a9\u53bb\u3002\u201d\u5510\u6653\u6f9c\u9762\u76d8\u53d1\u70e7\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5927\u80c6\u8bf4\u9053\uff1a\u201c\u5e08\u59b9 \u521d\u6b21\u4e34\u573a\uff0c\u5076\u7136\u5931\u624b\uff0c\u8fd8\u671b\u5e08\u5085\u5ff5\u5979\u5e74\u8f7b\u5386\u6d45\uff0c\u5904\u7f5a\u4ece\u5bbd\u3002\u201d\u6768\u4ef2\u82f1\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u500f\u53c8\u5fc3 \u91cc\u4e00\u9178\uff0c\u6454\u624b\u8bf4\u9053\uff1a\u201c\u4f60\u53bb\u5427\uff0c\u6211\u81ea\u6709\u5206\u6559\uff01\u201d \u3000\u3000\u6768\u67f3\u9752\u89c1\u7236\u4eb2\u5982\u6b64\u8ba4\u771f\uff0c\u4e0d\u6562\u518d\u4f3c\u5e73\u65e5\u6492\u5a07\uff0c\u8ddf\u5230\u4e66\u623f\uff0c\u53cc\u819d\u8dea\u4e0b\uff0c\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u91ce\u4e2b \u5934\uff0c\u4f60\u4e5f\u77e5\u7f6a\u4e86\u4e48\uff1f\u8bf4\u660e\u6bd4\u6b66\u8bd5\u62db\uff0c\u4f60\u4e3a\u4f55\u7adf\u4e0b\u6740\u624b\uff1f\u201d\u6768\u67f3\u9752\u53cc\u813e\u5fae\u62ac\uff0c\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u4ed6 \u4e5f\u4e0b\u6740\u624b\u54e9\uff0c\u7239\u7239\u6ca1\u77a7\u89c1\u4e48\uff1f\u201d\u6768\u4ef2\u82f1\u6012\u9053\uff1a\u201c\u4f60\u8fd8\u6562\u5f3a\u8fa9\uff0c\u4e0d\u662f\u4f60\u5484\u5484\u903c\u4eba\uff0c\u522b\u4eba\u600e\u4f1a\u771f \u4e2a\u4e0e\u4f60\u76f8\u6253\uff1f\u201d\u6768\u67f3\u9752\u5ffd\u9053\uff1a\u201c\u5973\u513f\u5b9e\u5728\u4e0d\u613f\u5ac1\u4ed6\uff01\u201d\u6768\u4ef2\u82f1\u4e00\u6115\uff0c\u62c8\u987b\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765 \u8fd9\u6837\uff01\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u5973\u513f\u6b32\u8bf4\u4e0d\u613f\uff0c\u53c8\u6015\u7239\u7239\u751f\u6c14\u3002\u8feb\u4e0d\u5f97\u5df2\uff0c\u548c\u4ed6\u6bd4\u6b66\u8bd5\u62db\uff0c\u6b32\u4ed6\u77e5\u96be \u800c\u9000\uff0c\u60f3\u4e0d\u5230\u62f3\u53d1\u96be\u6536\uff0c\u4e00\u65f6\u8bef\u4f24\u4e86\u90b9\u5bb6\u5144\u5f1f\uff01\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u4f60\u901e\u5f3a\u884c\u51f6\uff0c\u96be\u9053\u6211\u5c31\u4e0d\u751f \u6c14\u4e86\u3002\u5478\uff0c\u5e73\u65f6\u6211\u600e\u6837\u6559\u8bad\u4f60\u6765\uff1f\u201d\u6768\u67f3\u9752\u4fef\u4f0f\u5728\u5730\uff0c\u5ffd\u7136\u54ed\u51fa\u58f0\u9053\uff1a\u201c\u6211\u4efb\u7239\u7239\u5904\u7f5a\uff0c\u5e9f \u4e86\u6211\u6211\u4e5f\u4e0d\u6562\u57cb\u6028\u7239\u7239\u3002\u6028\u53ea\u6028\u6211\u5988\u5988\u6b7b\u5f97\u65e9\uff0c\u5c11\u4eba\u7ba1\uff0c\u5c11\u4eba\u6559\uff0c\u60f9\u51fa\u4e8b\u6765\uff0c\u6559\u7239\u7239\u751f \u6c14\u3002\u201d\u6768\u67f3\u9752\u81ea\u5c0f\u4e27\u6bcd\uff0c\u7531\u7236\u4eb2\u4e00\u624b\u629a\u517b\u6210\u4eba\uff0c\u800c\u4eca\u6768\u4ef2\u82f1\u4e00\u542c\u5973\u513f\u63d0\u8d77\u5988\u5988\uff0c\u4e0d\u89c9\u4e00\u9635\u4f24 \u611f\uff0c\u60f3\u8d77\u59bb\u5b50\u6b7b\u540e\uff0c\u81ea\u5df1\u4e00\u8eab\u517c\u7236\u6bcd\u4e4b\u8d23\uff0c\u5bf9\u5973\u513f\u4e5f\u662f\u592a\u9a84\u7eb5\u4e86\u4e9b\uff0c\u517b\u6210\u5979\u8fd9\u6837\u4efb\u6027\uff0c\u81ea\u5df1 \u4e5f\u6709\u4e0d\u662f\uff0c\u4e0d\u89c9\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5c31\u597d\u4e86\uff01\u201d\u6768\u67f3\u9752\u89c1\u7236\u4eb2\u58f0\u8c03\u7f13\u548c\uff0c\u9713\u989c\u76f8\u8bed\uff0c\u65b9\u624d\u653e \u4e0b\u4e86\u5fc3\u3002\u6768\u4ef2\u82f1\u53f9\u6c14\u4e4b\u540e\uff0c\u7559\u610f\u5973\u513f\uff0c\u89c1\u5979\u773c\u89d2\u76fe\u68a2\uff0c\u4f3c\u85cf\u59d4\u5c48\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6325\u624b\u8bf4\u9053\uff1a \u201c\u4f60\u8d77\u6765\uff0c\u6211\u95ee\u4f60\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u613f\u5ac1\u4f60\u9521\u4e5d\u54e5\u54e5\uff0c\u662f\u90a3\u70b9\u4e0d\u5982\u4f60\u610f\uff1f\u8bf4\u5230\u6b66\u529f\u8fd9\u5c42\uff0c\u96be\u9053\u4f60 \u771f\u8fd9\u6837\u7b28\uff0c\u6ca1\u6709\u770b\u51fa\u4ed6\u4e00\u4e0a\u573a\u5c31\u5fc3\u5b58\u9000\u8ba9\uff0c\u529f\u529b\u6bd4\u4f60\u9ad8\u5f97\u591a\u4e48\uff1f\u201d\u6768\u67f3\u9752\u4e00\u62b9\u773c\u6cea\uff0c\u5ffd\u7136\u5657 \u55e4\u4e00\u58f0\u7b11\u9053\uff1a\u201c\u7239\u96be\u9053\u4e5f\u770b\u4e0d\u51fa\u6765\uff0c\u5973\u513f\u5fc3\u76ee\u4e2d\u65e9\u5c31\u6709\u4e86\u4eba\u4e48\uff1f\u201d\u6768\u4ef2\u82f1\u7741\u5927\u773c\u775b\uff0c\u6b63\u5f85\u53d1 \u95ee\uff0c\u6768\u67f3\u9752\u4ee5\u8896\u63a9\u9762\uff0c\u5ffd\u5730\u8f6c\u8eab\u8dd1\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u6768\u67f3\u9752\u5c0f\u5b69\u5fc3\u6027\uff0c\u7ecf\u4e86\u8fd9\u4e00\u4ed7\u540e\uff0c\u6df1\u6015\u7236\u4eb2\u518d\u903c\u5979\u53e6\u5ac1\u4ed6\u4eba\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u6015\u7f9e\uff0c\u7d22\u6027\u6311 \u660e\u8bf4\u4e86\u51fa\u6765\uff0c\u8fd9\u53ef\u60f9\u5f97\u6768\u4ef2\u82f1\u53c8\u60ca\u53c8\u559c\uff0c\u5728\u4e66\u623f\u91cc\u5f98\u5f8a\u4e86\u597d\u4e9b\u65f6\u5019\uff0c\u5140\u81ea\u51b3\u65ad\u4e0d\u6765\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u60f3\u9053\uff1a\u539f\u6765\u8fd9\u4e2b\u5934\u7adf\u7231\u4e0a\u4e86\u5979\u7684\u5e08\u54e5\uff0c\u5f53\u65f6\u4e0d\u6562\u660e\u8bf4\uff0c\u4e8b\u540e\u5374\u5f04\u51fa\u8fd9\u6869\u4e8b\u513f\uff0c\u6559 \u6211\u5982\u4f55\u5bf9\u5f97\u4f4f\u9e23\u768b\u8001\u5f1f\uff01\u500f\u53c8\u60f3\u5230\uff1a\u6653\u6f9c\u8fd9\u5b69\u5b50\u4e5f\u4e0d\u9519\uff0c\u9664\u4e86\u6765\u5386\u4e0d\u660e\u8fd9\u70b9\u4e4b\u5916\uff0c\u4e5f\u4e0d\u4f1a\u8f93 \u7ed9\u9521\u4e5d\u3002\u4e00\u65f6\u601d\u6f6e\u8d77\u4f0f\uff0c\u4ed6\u672c\u60f3\u628a\u5973\u513f\u7f1a\u53bb\u627e\u90b9\u5bb6\u7236\u5b50\u8d1f\u8346\u8bf7\u7f6a\uff0c\u4f46\u542c\u5973\u513f\u5410\u9732\u5fc3\u4e8b\uff0c\u53ea\u6050 \u5c06\u6765\u56db\u9762\u76f8\u5bf9\uff0c\u4f1a\u5f04\u51fa\u66f4\u5c34\u5c2c\u5c40\u9762\u3002\u4e00\u62ac\u5934\uff0c\u770b\u89c1\u58c1\u4e0a\u6302\u7740\u7684\u59bb\u5b50\u9057\u5bb9\u3002\u53f9\u4e86\u53e3\u6c14\uff0c\u84e6\u7136\u63ed \u5f00\u5e18\u5b50\uff0c\u627e\u5510\u6653\u6f9c\u53bb\u3002 \u3000\u3000\u518d\u8bf4\u5510\u6653\u6f9c\u548c\u6768\u67f3\u9752\u76f8\u5904\u4e94\u5e74\uff0c\u867d\u7136\u5bf9\u5979\u90a3\u9a84\u7eb5\u7684\u6027\u60c5\uff0c\u80fd\u591f\u9006\u6765\u987a\u53d7\uff0c\u53ef\u662f\u5fc3\u91cc\u5374\u538c \u70e6\u5230\u6781\uff0c\u538b\u6839\u513f\u4e5f\u4e0d\u66fe\u60f3\u5230\u60c5\u7231\u4e4b\u4e8b\u3002\u5012\u662f\u5bf9\u4e8e\u90a3\u72ec\u81c2\u795e\u5c3c\u7684\u5173\u95e8\u5f92\u5f1f\u5415\u56db\u5a18\uff0c\u867d\u7136\u53ea\u662f\u4e00 \u9762\u4e4b\u7f18\uff0c\u5374\u5df2\u60c5\u6839\u6df1\u79cd\u3002\u5415\u56db\u5a18\u90a3\u723d\u6717\u98ce\u59ff\uff0c\u6e29\u8a00\u7b11\u8bed\uff0c\u4e94\u5e74\u6765\u65f6\u6d8c\u5fc3\u5934\uff0c\u53ea\u662f\u5415\u56db\u5a18\u6b66\u529f \u8d85\u7edd\uff0c\u590d\u88e4\u8bd7\u4e66\uff0c\u5510\u6653\u6f9c\u89c6\u5979\u4fe8\u5982\u5929\u4eba\uff0c\u5bf9\u5979\u4ef0\u5e55\u5f25\u6df1\uff0c\u5374\u4e0d\u6562\u6709\u4eb5\u6e0e\u4e4b\u5ff5\uff0c\u81ea\u5206\u6b64\u610f\u6b64 \u60c5\uff0c\u6c38\u57cb\u5fc3\u5e95\uff0c\u4e00\u751f\u4e00\u4e16\uff0c\u9065\u62dc\u5986\u53f0\uff01\u6768\u4ef2\u82f1\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\uff0c\u8fd9\u5927\u5b69\u5b50\u6709\u8fd9\u4e48\u591a\u5fc3\u4e8b\u3002 \u3000\u3000\u6708\u8fd1\u4e2d\u5929\uff0c\u591c\u51c9\u5982\u6c34\uff0c\u6768\u4ef2\u82f1\u627e\u5230\u5510\u6653\u6f9c\u7684\u4e66\u623f\uff0c\u5374\u6773\u4e0d\u89c1\u4eba\uff0c\u6768\u4ef2\u82f1\u54d1\u7136\u5931\u7b11\u9053\uff1a \u201c\u6211\u4e5f\u592a\u5fc3\u6025\u4e86\uff0c\u8fd9\u4e2a\u65f6\u5019\uff0c\u4ed6\u60f3\u5df2\u65e9\u7761\u4e86\uff0c\u8fd8\u4f1a\u5728\u4e66\u623f\u4e48\uff1f\u54e6\uff0c\u660e\u5929\u548c\u4ed6\u8bf4\u4e5f\u4e0d\u8fdf\u3002\u201d\u6b63 \u60f3\u9000\u51fa\uff0c\u89c1\u684c\u4e0a\u4e00\u5f20\u8bcd\u7b3a\uff0c\u58a8\u8ff9\u72b9\u65b0\uff0c\u597d\u5947\u5fc3\u8d77\uff0c\u60f3\u9053\uff1a\u4e0d\u77e5\u8fd9\u5b69\u5b50\u8bfb\u4e66\u8bfb\u5f97\u5982\u4f55\uff1f\u968f\u624b\u63e3 \u5165\u6000\u4e2d\u3002\u6559\u4e66\u5148\u751f\u4f4f\u5728\u9694\u623f\uff0c\u623f\u4e2d\u706f\u5149\u72b9\u660e\u3002\u6768\u4ef2\u82f1\u8e31\u4e86\u8fdb\u53bb\u3002\u6559\u4e66\u5148\u751f\u662f\u6768\u4ef2\u82f1\u5802\u5f1f\uff0c\u867d \u7136\u662f\u4e2a\u843d\u7b2c\u79c0\u624d\uff0c\u5b66\u95ee\u5374\u5f88\u4e0d\u9519\u3002\u89c1\u6768\u4ef2\u82f1\u95ee\u8d77\u5510\u6653\u6f9c\u8bfb\u4e66\u4e4b\u4e8b\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u5929\u8d44 \u8fc7\u4eba\uff0c\u77ed\u77ed\u4e94\u5e74\uff0c\u7ecf\u53f2\u8bd7\u8bcd\uff0c\u90fd\u5df2\u9887\u6709\u6839\u5e95\uff0c\u867d\u7136\u4e0d\u80fd\u6210\u4e3a\u540d\u5112\uff0c\u4e5f\u53ef\u7b97\u5f97\u4e00\u4e2a\u901a\u4eba\u3002\u201d\u6768 \u4ef2\u82f1\u5c55\u5f00\u8bcd\u7b3a\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u770b\u4ed6\u5199\u7684\u662f\u4ec0\u4e48\uff1f\u50cf\u8bd7\u53c8\u4e0d\u50cf\u8bd7\uff0c\u6211\u8bfb\u4e0d\u65ad\u53e5\uff0c\u4f60\u89e3\u7ed9\u6211\u542c\u542c\u3002\u201d \u3000\u3000\u6559\u4e66\u5148\u751f\u4e00\u770b\uff0c\u539f\u6765\u662f\u9996\u957f\u8bcd\uff0c\u8bcd\u724c\u540d\u4e3a\u201c\u767e\u5b57\u4ee4\u201d\uff0c\u5168\u9996\u8bcd\u6070\u6070\u4e00\u767e\u4e2a\u5b57\uff0c\u8bfb\u90a3\u8bcd\u9053\uff1a \u3000\u3000\u98d8\u840d\u5026\u4fa3\uff0c\u7b97\u832b\u832b\u4eba\u6d77\uff0c\u53cb\u670b\u77e5\u5426\uff1f\u5251\u5323\u8bd7\u56ca\u957f\u4f5c\u4f34\uff0c\u8e0f\u7834\u665a\u98ce\u671d\u9732\u3002\u957f\u5578\u7a7f\u4e91\uff0c\u9ad8\u6b4c \u6563\u96fe\uff0c\u5b64\u96c1\u6765\u8fd8\u53bb\uff01\u76df\u9e25\u793e\u71d5\uff0c\u96ea\u6ce5\u9e3f\u722a\u65e0\u636e\uff01\u4e91\u5c71\u68a6\u5f71\u6a21\u7cca\uff0c\u4e73\u71d5\u5bfb\u5de2\uff0c\u53c8\u4ff1\u91cd\u5e18\u963b\uff01\u9732 \u767d\u846d\u82cd\u80a0\u65ad\u53e5\uff0c\u5374\u60c5\u4f55\u4eba\u4f20\u8bed\uff1f\u8549\u6850\u72ec\u62b1\uff0c\u9713\u88f3\u7ec6\u8c31\uff0c\u671b\u65ad\u5929\u6daf\u8def\uff01\u7d20\u5a25\u9752\u5973\uff0c\u4ed9\u8e2a\u751a\u65e5\u91cd \u9047\uff1f \u3000\u3000\u6559\u4e66\u5148\u751f\u4e00\u9762\u541f\u54e6\uff0c\u9762\u8272\u59cb\u800c\u559c\uff0c\u7ee7\u800c\u5fe7\uff0c\u7ec8\u800c\u6c89\u541f\u4e0d\u8bed\u3002\u6768\u4ef2\u82f1\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u4ed6 \u8bf4\u7684\u662f\u4ec0\u4e48\u5440\uff01\u201d\u6559\u4e66\u5148\u751f\u53cc\u6307\u4e00\u5f39\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u6015\u8fd9\u5b69\u5b50\u4f1a\u5165\u9b54\u9053\uff01\u201d \u3000\u3000\u6768\u4ef2\u82f1\u60ca\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u5b69\u5b50\u6709\u4ec0\u4e48\u574f\u5fc3\u601d\uff0c\u4f60\u770b\u51fa\u6765\u4e86\u4e48\uff1f\u201d\u5148\u751f\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d \u662f\uff01\u201d\u539f\u6765\u8fd9\u9996\u8bcd\u662f\u5510\u6653\u6f9c\u6000\u5ff5\u5415\u56db\u5a18\u4e4b\u8bcd\uff0c\u8bcd\u4e2d\u5c06\u4ed6\u7684\u8eab\u4e16\u548c\u5fe7\u90c1\u7684\u5fc3\u4e8b\uff0c\u5199\u5f97\u975e\u5e38\u7ec6 \u817b\uff0c\u5bf9\u5415\u56db\u5a18\u5219\u4f5c\u4e3a\u795e\u660e\u4e00\u822c\u819c\u62dc\u3002\u6559\u4e66\u5148\u751f\u4e0d\u77e5\u4ed6\u6709\u8fd9\u6bb5\u60c5\u7f18\uff0c\u53ea\u89c9\u8bcd\u610f\u5e7d\u6028\uff0c\u8bcd\u4e2d\u6240\u6000 \u5ff5\u7684\u610f\u4e2d\u4eba\uff0c\u53ef\u671b\u800c\u4e0d\u53ef\u53ca\uff0c\u4f3c\u4e4e\u662f\u5728\u865a\u65e0\u98d8\u6e3a\u95f4\u7684\u4ed9\u5973\uff0c\u9887\u4e3a\u4e0d\u89e3\u3002\u56e0\u9053\uff1a\u201c\u8bf4\u8d77\u6765\u561b\uff0c \u4ed6\u8fd9\u6837\u7684\u5e74\u7eaa\uff0c\u4e5f\u602a\u4e0d\u5f97\u3002\u5173\u5173\u7762\u9e20\uff0c\u541b\u5b50\u597d\u9010\uff0c\u4ed6\u8fd9\u9996\u8bcd\u662f\u6000\u5ff5\u610f\u4e2d\u4eba\u4e4b\u8bcd\uff0c\u53d1\u4e4e\u60c5\uff0c\u6b62 \u4e4e\u793c\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u662f\u574f\u5fc3\u601d\u3002\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u90a3\u5148\u751f\u53c8\u600e\u6837\u8bf4\u4ed6\u5165\u4e86\u9b54\u9053\uff1f\u201d\u5148\u751f\u9053\uff1a\u201c\u8bcd\u4e2d \u4e4b\u610f\uff0c\u597d\u50cf\u4ed6\u7684\u610f\u4e2d\u4eba\u548c\u4ed6\u6781\u96be\u914d\u5408\uff0c\u4ed6\u628a\u610f\u4e2d\u4eba\u89c6\u4e3a\u7d20\u5a25\u9752\u5973\uff0c\u5f53\u6210\u5929\u4e0a\u7684\u795e\u4ed9\u54e9\uff01\u8bcd\u4e2d \u8fd8\u7528\u4e86\u8bd7\u7ecf\u79e6\u98ce\u4e2d\u9732\u767d\u5b7d\u82cd\u4e4b\u5178\u2014\u2014\u201d\u6768\u4ef2\u82f1\u63d2\u53e3\u9053\uff1a\u201c\u90a3\u9996\u8bd7\u8bf4\u7684\u53c8\u662f\u4ec0\u4e48\uff1f\u201d\u5148\u751f\u9053\uff1a \u201c\u90a3\u9996\u8bd7\u539f\u662f\u6625\u79cb\u65f6\u79e6\u56fd\u7684\u6c11\u6b4c\uff0c\u6240\u4ee5\u79f0\u4e3a\u201c\u79e6\u98ce\u2019\uff0c\u6b4c\u9053\uff1a\u2018\u8511\u5b7d\u82cd\u82cd\uff0c\u767d\u9732\u4e3a\u971c\uff0c\u6240\u8c13 \u4f0a\u4eba\uff0c\u5728\u6c34\u4e00\u65b9\u3002\u6eaf\u56de\u4ece\u4e4b\uff0c\u9053\u963b\u4e14\u957f\uff0c\u6eaf\u6e38\u4ece\u4e4b\uff0c\u5b9b\u5728\u6c34\u4e2d\u592e\u3002\u2019\u610f\u601d\u662f\u8bf4\uff1a\u2018\u82a6\u82b1\uff08\u517c \u846d\uff09\u4e00\u7247\u767d\u82cd\u82cd\uff0c\u6e05\u65e9\u9732\u6c34\u53d8\u6210\u971c\uff0c\u5fc3\u4e0a\u7684\u4eba\u513f\u54ea\uff0c\u5728\u6c34\u7684\u90a3\u4e00\u65b9\u3002\u6211\u9006\u7740\u6c34\u6d41\u53bb\u627e\u5979\uff0c\u7ed5 \u6765\u7ed5\u53bb\u9053\u513f\u957f\uff0c\u6211\u987a\u7740\u6c34\u6d41\u53bb\u627e\u5979\uff0c\u5979\u5440\u5374\u50cf\u5728\u56db\u8fb9\u4e0d\u7740\u7684\u6c34\u4e2d\u592e\u3002\u2019\u603b\u4e4b\uff0c\u662f\u53ef\u671b\u800c\u4e0d\u53ef \u53ca\u7684\u4e86\u3002\u9752\u5e74\u4eba\u4e24\u60c5\u76f8\u60a6\u8fd8\u597d\uff0c\u6700\u6015\u5355\u601d\u6210\u75c5\uff0c\u8d70\u706b\u5165\u9b54\uff0c\u53ea\u6050\u8d3b\u5bb3\u7ec8\u751f\uff01\u201d\u6768\u4ef2\u82f1\u522b\u6709\u4f1a \u5fc3\uff0c\u5ffd\u7136\u4e00\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u6653\u6f9c\u4e5f\u5728\u601d\u6155\u9752\u513f\uff0c\u4ed6\u89c1\u9752\u513f\u5a07\u7eb5\uff0c\u81ea\u4ee5\u4e3a\u65e0\u671b\uff0c\u6240\u4ee5\u5728\u8bcd\u4e2d \u8ba4\u4e3a\u662f\u53ef\u671b\u4e0d\u53ef\u53ca\u4e86\u3002\u201d\u56e0\u9053\uff1a\u201c\u5148\u751f\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u4ed6\u5e76\u4e0d\u662f\u5355\u601d\u54e9\uff01\u201d\u4e00\u7b11\u63ed\u5e18\u800c\u51fa\u3002 \u3000\u3000\u5510\u6653\u6f9c\u90a3\u665a\u4e5f\u662f\u5f7b\u591c\u4e0d\u5b81\uff0c\u4ed6\u60f3\u8d77\u5415\u56db\u5a18\uff0c\u53c8\u60f3\u5230\u6768\u67f3\u9752\uff0c\u4e0d\u7981\u6697\u7b11\u3002\u4ed6\u60f3\uff1a\u5415\u56db\u5a18\u6b66 \u529f\u6bd4\u6768\u67f3\u9752\u4e0d\u77e5\u8981\u9ad8\u591a\u5c11\uff0c\u4f46\u5979\u6e29\u67d4\u8fd1\u4eba\uff0c\u800c\u6768\u67f3\u9752\u90a3\u70b9\u80fd\u4e3a\uff0c\u5374\u5c31\u9a84\u6a2a\u653e\u8086\uff0c\u65e5\u95f4\u60c5\u4e8b\uff0c \u84e6\u4e0a\u5fc3\u5934\uff0c\u60f3\u5230\u5979\u5bf9\u90b9\u9521\u4e5d\u90a3\u822c\u72e0\u8fa3\uff0c\u4e0d\u89c9\u6253\u4e86\u5bd2\u5664\uff0c\u4e00\u591c\u6076\u68a6\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u6768\u4ef2\u82f1\u5c06\u5510\u6653\u6f9c\u53eb\u6765\uff0c\u5288\u5934\u5c31\u95ee\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u4f60\u5728\u8fd9\u91cc\u4e94\u5e74\uff0c\u73b0\u5df2\u957f\u5927\u6210 \u4eba\u51fa\u8be5\u6709\u6210\u5bb6\u7acb\u5ba4\u7684\u6253\u7b97\u4e86\u3002\u7384\u98ce\u9053\u957f\u5e26\u4f60\u6765\u65f6\uff0c\u66fe\u8bf4\u4f60\u662f\u4e2a\u5b64\u513f\uff0c\u90a3\u4e48\u60f3\u5fc5\u4f60\u672a\u66fe\u8ba2\u4e0b\u5a5a \u4e8b\u7684\u4e86\uff1f\u201d\u5510\u6653\u6f9c\u609a\u7136\u4e00\u60ca\uff0c\u7b54\u9053\uff1a\u201c\u672a\u66fe\uff01\u201d\u6768\u4ef2\u82f1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u81ea\u5df1\u53ef\u6709\u5408\u610f\u7684 \u4eba\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u6ee1\u9762\u901a\u7ea2\u6447\u4e86\u6447\u5934\uff0c\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u4e1a\u5e08\u5982\u7236\uff0c\u4f46\u8bf4\u4f55\u59a8\u3002\u201d\u5510\u6653\u6f9c\u8baa\u8baa\u8bf4 \u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u5c11\u5e74\u4eba\u513f\uff0c\u679c\u662f\u9762\u5ae9\u3002\u201d\u628a\u90a3\u5f20\u8bcd\u7b3a\uff0c\u638f\u4e86\u51fa\u6765\uff0c\u63b7\u7ed9\u4ed6\u9053\uff1a \u201c\u8fd9\u96be\u9053\u4e0d\u662f\u4f60\u5199\u7684\uff1f\u201d\u5510\u6653\u6f9c\u9762\u7ea2\u8fc7\u8033\uff0c\u6b63\u5f85\u5206\u8bf4\uff0c\u6768\u4ef2\u82f1\u5ffd\u9053\uff1a\u201c\u9752\u513f\u548c\u4f60\u4e5f\u662f\u4e00\u6837\u7684 \u5fc3\u601d\uff0c\u6700\u5f00\u901a\u4e0d\u8fc7\uff0c\u4f60\u4eec\u4e24\u4eba\u5373\u90fd\u6709\u610f\uff0c\u6211\u5c31\u6d3e\u4eba\u627e\u7384\u98ce\u9053\u957f\u6765\u3002\u8bf7\u4ed6\u4f5c\u7537\u5bb6\u7684\u4e3b\u5a5a\uff0c\u8ba9\u4f60 \u4eec\u4fe9\u4eba\u65e9\u6210\u5a5a\u793c\uff0c\u6211\u4e5f\u53ef\u4e86\u751f\u5e73\u4e4b\u613f\u3002\u201d\u5510\u6653\u6f9c\u542c\u4e86\uff0c\u4fe8\u5982\u6674\u5929\u9739\u96f3\uff0c\u534a\u54cd\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u89c1\u5510\u6653\u6f9c\u9762\u8272\u9aa4\u53d8\u3002\u4f4e\u5934\u4e0d\u8bed\uff0c\u9053\u4ed6\u5e74\u5c11\u754f\u7f9e\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u5973\u5ac1\u7537\u5a5a\uff0c\u4eba\u751f\u5927 \u4e8b\uff0c\u6709\u6211\u66ff\u4f60\u4eec\u4f5c\u4e3b\uff0c\u6015\u4ec0\u4e48\u4e0d\u6562\u8bf4\uff1f\u201d\u5510\u6653\u6f9c\u5ffd\u7136\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u5b66\u4e1a\u672a\u6210\uff0c\u4e0d\u6562\u6709\u6210 \u5bb6\u7acb\u5ba4\u4e4b\u60f3\uff0c\u800c\u4e14\u4e5f\u4e0d\u6562\u9ad8\u6500\u5e08\u59b9\uff01\u201d\u6768\u4ef2\u82f1\u53c8\u7b11\u4e86\u4e00\u7b11\uff0c\u770b\u7740\u4ed6\u624b\u4e0a\u7684\u8bcd\u7b3a\uff0c\u5510\u6653\u6f9c\u6479\u7136 \u62ac\u8d77\u4e86\u5934\uff0c\u9f13\u7740\u52c7\u6c14\u8bf4\u9053\uff1a\u201c\u6211\u5bf9\u5e08\u59b9\uff0c\u53ef\u4e1d\u6beb\u6ca1\u6709\u975e\u4efd\u4e4b\u60f3\uff01\u201d \u3000\u3000\u8fd9\u4e00\u7b54\u590d\u5927\u51fa\u6768\u4ef2\u82f1\u610f\u6599\u4e4b\u5916\uff0c\u770b\u4ed6\u795e\u60c5\u4e25\u8083\uff0c\u4e0d\u4f3c\u6015\u7f9e\u63a9\u9970\u4e4b\u8a00\uff0c\u54b3\u4e86\u4e24\u58f0\uff0c\u53cc\u638c\u4e00 \u6309\uff0c\u5ffd\u7136\u4e5f\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u4f60\u4eba\u6211\u95e8\u65f6\uff0c\u66fe\u7acb\u8a93\u9075\u5b88\u5341\u4e8c\u6212\u6761\uff0c\u8fd9\u5341\u4e8c\u6212\u6761\uff0c\u4f60\u53ef\u8fd8\u8bb0\u5f97 \u4e48\uff1f\u201d\u5510\u6653\u6f9c\u6b63\u895f\u5371\u5750\uff0c\u5782\u624b\u7b54\u9053\uff1a\u201c\u8bb0\u5f97\uff01\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u6700\u540e\u4e00\u6761\u662f\u4ec0\u4e48\uff1f\u201d\u5510\u6653\u6f9c \u9053\uff1a\u201c\u4e0d\u5f97\u6b3a\u5e08\u706d\u7956\uff01\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u600e\u6837\u89e3\u91ca\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\u90fd\u4e0d\u8bb8\u7792\u7740\u5e08 \u5085\uff0c\u4e00\u5207\u8981\u8bf4\u771f\u8bdd\uff0c\u66f4\u4e0d\u8bb8\u52fe\u7ed3\u5916\u4eba\uff0c\u4fae\u8fb1\u5c0a\u957f\uff0c\u72af\u6b64\u6761\u8005\uff0c\u8f7b\u5219\u5e9f\u53bb\u6b66\u529f\uff0c\u91cd\u5219\u4e94\u9a6c\u5206 \u5c38\uff01\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff01\u90a3\u4e48\u6211\u95ee\u4f60\uff0c\u4f60\u5199\u7684\u8fd9\u9996\u8bcd\uff0c\u5148\u751f\u8bf4\u8bcd\u4e2d\u610f\u601d\u662f\u6000\u5ff5\u4e00\u4e2a\u5973 \u5b50\uff0c\u53ef\u662f\u771f\u7684\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u662f\u771f\u7684\uff01\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u4f60\u6000\u5ff5\u7684\u5973\u5b50\u662f\u8c01\uff1f\u201d\u5510\u6653\u6f9c\u8116\u5b50 \u7c97\u7ea2\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u6323\u51fa\u58f0\u9053\uff1a\u201c\u4e0d\u662f\u5e08\u59b9\uff01\u201d\u6768\u4ef2\u82f1\u9893\u7136\u5750\u4e0b\uff0c\u6325\u624b\u8bf4\u9053\uff1a\u201c\u4f60\u53bb\u5427\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u5931\u9b42\u843d\u9b44\u822c\u7684\u8d70\u51fa\u5916\u9762\uff0c\u722c\u4e0a\u540e\u5c71\uff0c\u9752\u90c1\u82cd\u5c71\u8272\uff0c\u6ee1\u6edf\u6e56\u5149\uff0c\u5fc3\u4e2d\u6697\u6697\u53f9\u4e86\u53e3\u6c14 \u9053\uff1a\u201c\u6e56\u5c71\u518d\u7f8e\uff0c\u6050\u975e\u4e45\u604b\u4e4b\u4e61\uff0c\u8fd9\u5730\u65b9\u53ea\u6015\u4e0d\u80fd\u518d\u5f85\u4e0b\u53bb\u4e86\uff01\u201d\u4ed6\u60f3\u8d77\u5e08\u59b9\u90a3\u9a84\u6a2a\u6b8b\u9177\u7684 \u6837\u5b50\uff0c\u4ece\u5fc3\u5e95\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u4ed6\u77e5\u9053\u5e08\u59b9\u7684\u6027\u513f\uff0c\u9664\u975e\u5979\u4e0d\u60f3\u8981\uff0c\u82e5\u7136\u5979\u60f3\u8981\u4e00\u4ef6\u4e1c\u897f\uff0c\u90a3 \u5c31\u662f\u4e0d\u5f97\u4e0d\u4f11\uff01\u53ea\u662f\u81ea\u5df1\u5982\u4f55\u6562\u8981\u8fd9\u6837\u7684\u59bb\u5b50\uff1f\u90a3\u665a\u4ed6\u53cd\u590d\u601d\u91cf\uff0c\u7ec8\u4e8e\u5728\u6df1\u591c\u8d77\u6765\uff0c\u6536\u62fe\u597d \u8bd7\u56ca\uff0c\u6084\u6084\u8d70\u4e86\uff01 \u3000\u3000\u518d\u8bf4\u6768\u67f3\u9752\u5411\u7236\u4eb2\u5410\u9732\u4e86\u5fc3\u4e8b\u4e4b\u540e\uff0c\u53c8\u662f\u5bb3\u7f9e\uff0c\u53c8\u662f\u9ad8\u5174\uff0c\u5979\u60f3\u7236\u4eb2\u7d20\u6765\u75bc\u7231\u81ea\u5df1\uff0c\u4e00 \u5b9a\u53bb\u548c\u5e08\u54e5\u8bf4\u4e86\uff0c\u5e08\u54e5\u60f3\u4e5f\u6ca1\u60f3\u5230\uff0c\u4e0d\u77e5\u9053\u6709\u591a\u5f00\u5fc3\u5462\uff01\u5979\u53ef\u5168\u6ca1\u60f3\u5230\uff0c\u5510\u6653\u6f9c\u4f1a\u4e0d\u6b22\u559c \u5979\u3002\u8fd9\u4e00\u65e5\u5979\u4e3a\u4e86\u6015\u7f9e\uff0c\u6545\u610f\u907f\u514d\u548c\u5510\u6653\u6f9c\u89c1\u9762\uff0c\u60f3\u7b49\u7236\u4eb2\u548c\u5e08\u54e5\u8bf4\u597d\u4e4b\u540e\u6765\u544a\u8bc9\u5979\uff0c\u8c01\u77e5 \u7236\u4eb2\u4e5f\u6574\u5929\u4e0d\u6765\u627e\u5979\u3002\u90a3\u665a\u5979\u8f97\u8f6c\u53cd\u4fa7\uff0c\u597d\u4e0d\u5bb9\u6613\u6371\u5230\u5929\u4eae\uff0c\u5979\u518d\u4e5f\u71ac\u4e0d\u4f4f\u4e86\uff0c\u5306\u5306\u62ab\u8863\u8d77 \u5e8a\uff0c\u53bb\u627e\u7236\u4eb2\u3002\u5728\u5ead\u9662\u8537\u8587\u67b6\u4e0b\uff0c\u89c1\u7236\u4eb2\u72ec\u81ea\u5f98\u5f8a\uff0c\u989c\u5bb9\u6194\u60b4\uff0c\u4e0d\u7981\u60ca\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u6709\u75c5 \u5417\uff1f\u201d\u6768\u4ef2\u82f1\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5510\u6653\u6f9c\u8fd9\u5b69\u5b50\u8d70\u4e86\u3002\u201d\u6768\u67f3\u9752\u8df3\u8d77\u6765\u9053\uff1a\u201c\u662f\u4e48\uff1f\u201d\u6768\u4ef2\u82f1\u638f \u51fa\u4e00\u5c01\u4fe1\u6765\uff0c\u63b7\u7ed9\u5979\u9053\uff1a\u201c\u4f60\u770b\u53bb\uff01\u201d\u90a3\u4fe1\u679c\u7136\u662f\u5510\u6653\u6f9c\u7684\u7b14\u8ff9\uff0c\u4e0a\u9762\u5199\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5148\u9053 \u8c22\u5e08\u5085\u4e94\u5e74\u6559\u517b\u4e4b\u6069\uff0c\u7ee7\u800c\u5a49\u8f6c\u63a8\u8f9e\u5a5a\u4e8b\uff0c\u6768\u67f3\u9752\u770b\u4e86\uff0c\u4e0d\u7981\u67f3\u7709\u5012\u7ad6\uff0c\u77aa\u773c\u8bf4\u9053\uff1a\u201c\u7239\uff0c \u6211\u627e\u4ed6\u53bb\u3002\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u50bb\u5b69\u5b50\uff0c\u522b\u4eba\u4e0d\u613f\u610f\uff0c\u4f60\u5f3a\u8feb\u4ed6\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u201d\u6768\u67f3\u9752\u54ac\u5507\u8bf4 \u9053\uff1a\u201c\u8c01\u8981\u5f3a\u8feb\u4ed6\uff1f\u53ea\u662f\u6211\u4e0d\u613f\u518d\u5446\u5728\u5bb6\u91cc\u4e86\uff01\u201d\u6768\u4ef2\u82f1\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u90a3\u4e5f\u597d\u3002\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u60c5\u4e1d\u504f\u7cfb\u9519\uff0c\u6069\u7231\u53cd\u6210\u4ec7\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e03\u56de\u3000\u9152\u5e02\u85cf\u9f99\u3000\u91c7\u82b1\u4f20\u602a\u5be8\u3000\u6e14\u821f\u8574\u7389\u3000\u7834\u6d6a\u89c1\u4ed9\u8e2a \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e03\u56de \u9152\u5e02\u85cf\u9f99\u3000\u91c7\u82b1\u4f20\u602a\u5be8 \u6e14\u821f\u8574\u7389\u3000\u7834\u6d6a\u89c1\u4ed9\u8e2a \u3000\u3000\u4e00\u4e2a\u591a\u6708\u4e4b\u540e\uff0c\u5c71\u4e1c\u534a\u5c9b\u7684\u9ec4\u6d77\u4e4b\u6ee8\uff0c\u51fa\u73b0\u4e86\u4e00\u4e2a\u98ce\u5c18\u4ec6\u4ec6\u7684\u5c11\u5e74\uff0c\u8033\u542c\u5927\u6d77\u6ce2\u6d9b\uff0c\u76ee \u770b\u6d77\u5929\u5e06\u5f71\uff0c\u751f\u51fa\u65e0\u7a77\u611f\u6168\u3002\u8fd9\u4eba\u4f7f\u662f\u5077\u79bb\u5e08\u5085\uff0c\u72ec\u8d70\u6c5f\u6e56\u7684\u5510\u6653\u6f9c\u4e86\uff0c\u4ed6\u79bb\u5f00\u6768\u5bb6\u4e4b\u540e\uff0c \u672c\u60f3\u8fdb\u4eac\u53bb\u6253\u63a2\u5e08\u5ac2\u909d\u7ec3\u971e\u7684\u4e0b\u843d\uff0c\u4f46\u81ea\u5fd6\u6b66\u529f\uff0c\u5c1a\u8fdc\u975e\u795e\u9b54\u53cc\u8001\u7684\u654c\u624b\uff0c\u6df1\u6015\u5230\u4e86\u4eac\u57ce\uff0c \u88ab\u8840\u6ef4\u5b50\u6240\u53d1\u73b0\uff0c\u90a3\u65f6\u6551\u4eba\u4e0d\u6210\uff0c\u53cd\u4e22\u6027\u547d\uff0c\u601d\u7ef4\u518d\u4e09\uff0c\u6539\u53d8\u884c\u7a0b\u3002\u4ed6\u65e0\u53cb\u65e0\u4eb2\uff0c\u60f3\u6765\u60f3 \u53bb\uff0c\u53ea\u6709\u7384\u98ce\u9053\u957f\uff0c\u662f\u81ea\u5df1\u4e49\u7236\u5468\u9752\u7684\u670b\u53cb\uff0c\u800c\u4e14\u5173\u4e1c\u56db\u4fa0\u53c8\u66fe\u7b54\u5141\u8fc7\u81ea\u5df1\u63f4\u6551\u51af\u5bb6\u9057\u5b64\uff0c \u8c6a\u4fa0\u8bfa\u8a00\uff0c\u575a\u5982\u91d1\u77f3\uff0c\u56e0\u6b64\u5510\u6653\u6f9c\u60f3\u4ece\u5c71\u4e1c\u534a\u5c9b\u6e21\u8fc7\u6e24\u6d77\uff0c\u5230\u8fbd\u4e1c\u53bb\u627e\u5173\u4e1c\u56db\u4fa0\u3002 \u3000\u3000\u8fd9\u65e5\u4ed6\u5230\u4e86\u9752\u5c9b\uff0c\u8fdc\u773a\u6d77\u5929\u4e00\u8272\uff0c\u80f8\u895f\u5f00\u9614\uff0c\u4ed6\u4ece\u672a\u89c1\u8fc7\u5927\u6d77\uff0c\u4e0d\u89c9\u88ab\u6d77\u7684\u96c4\u58ee\u6240\u5438 \u5f15\uff0c\u72ec\u81ea\u8d70\u4e0a\u6ee8\u6d77\u7684\u4e00\u95f4\u9152\u697c\uff0c\u53eb\u4e86\u4e00\u58f6\u9ec4\u9152\uff0c\u5750\u4e86\u4e00\u5f20\u8fd1\u7a97\u7684\u5ea7\u4f4d\uff0c\u996e\u9152\u89c2\u6f6e\u3002\u6b63\u81ea\u795e\u79fb \u5fc3\u9189\uff0c\u5ffd\u542c\u5f97\u4e00\u9635\u5608\u6742\u4e4b\u58f0\uff0c\u56de\u5934\u770b\u65f6\uff0c\u7adf\u662f\u4e00\u961f\u5b98\u5dee\uff0c\u8d70\u4e0a\u9152\u697c\u3002\u5510\u6653\u6f9c\u5b9a\u4e86\u5b9a\u795e\uff0c\u628a\u634f \u597d\u7684\u4e00\u5957\u8bdd\u5e94\u4ed8\u5b98\u5dee\uff0c\u8c01\u77e5\u90a3\u4e9b\u5b98\u5dee\u95ee\u5f97\u975e\u5e38\u4ed4\u7ec6\uff0c\u4e0d\u4f46\u76d8\u95ee\u4ed6\u59d3\u540d\u6765\u5386\uff0c\u8fd8\u95ee\u8d77\u5510\u6653\u6f9c\u5728 \u9752\u5c9b\u6709\u4ec0\u4e48\u4eb2\u670b\uff0c\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u662f\u8def\u8fc7\u6b64\u5730\uff0c\u90a3\u6709\u4ec0\u4e48\u53cb\u4eba\uff1f\u201d\u4e00\u4e2a\u5b98\u5dee\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u81ea \u5df1\u8bf4\u662f\u4e1c\u5e73\u53bf\u7684\u79c0\u624d\uff0c\u8981\u5230\u8fbd\u4e1c\u6765\u63a2\u4eb2\uff0c\u5374\u4e00\u65e0\u5b66\u5e9c\u6587\u4e66\uff0c\u4e8c\u6765\u53e3\u97f3\u53c8\u5f88\u751f\u786c\uff0c\u8c01\u6562\u62c5\u4fdd\u4f60 \u7684\u8bdd\u4e0d\u662f\u634f\u9020\u7684\uff01\u5582\uff01\u670b\u53cb\uff0c\u4f60\u505a\u7684\u597d\u4e8b\u60c5\uff01\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u6ca1\u6709\u505a\u4ec0\u4e48\u4e8b\uff01\u201d\u90a3\u4e8c\u540d\u5b98 \u5dee\u54d7\u5566\u5566\u7684\u6296\u5f00\u94c1\u94fe\uff0c\u5f80\u5510\u6653\u6f9c\u8116\u5b50\u4e00\u5957\uff01\u559d\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u4eec\u5230\u5e9c\u91cc\u8bf4\u53bb\u3002\u201d\u5510\u6653\u6f9c\u8f7b\u8f7b\u4e00 \u95ea\uff0c\u90a3\u5b98\u5dee\u6251\u4e86\u4e2a\u7a7a\uff0c\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u6562\u62d2\u6355\uff01\u201d\u62d4\u51fa\u94c1\u5c3a\uff0c\u7adf\u81ea\u8fce\u5934\u6253\u6765\uff0c\u5510\u6653\u6f9c\u6b63\u60f3 \u51fa\u624b\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\uff1a\u201c\u4e14\u4f4f\uff01\u201d\u90bb\u5ea7\u4e00\u4e2a\u5c11\u5e74\u516c\u5b50\uff0c\u6298\u6247\u8f7b\u6447\uff0c\u8f7b\u8f7b\u4e00\u8dc3\uff0c\u62e6\u5728\u4e24\u4eba\u4e2d\u95f4\uff0c \u90a3\u5b98\u5dee\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u4f38\u624b\u5c31\u8981\u6765\u6293\uff0c\u84e6\u7136\u624b\u8155\u4e00\u75db\uff0c\u65c1\u8fb9\u7a9c\u51fa\u4e00\u4e2a\u7cbe\u58ee\u6c49\u5b50\uff0c\u5c06 \u4ed6\u62c9\u4f4f\uff0c\u559d\u9053\uff1a\u201c\u4f60\u627e\u6b7b\uff01\u201d\u5c11\u5e74\u516c\u5b50\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u653e\u4e86\u4ed6\u5427\uff01\u201d\u5b98\u5dee\u56e2\u56e2\u56f4\u4e0a\uff0c\u5c11\u5e74\u516c\u5b50 \u53cc\u773c\u4e00\u7741\uff0c\u95ee\u9053\uff1a\u201c\u8c01\u662f\u6355\u5934\uff1f\u201d\u53cc\u76ee\u795e\u5149\u51db\u5c04\uff0c\u8bdd\u58f0\u867d\u4e0d\u5f88\u9ad8\uff0c\u5374\u4f3c\u5177\u6709\u65e0\u9650\u5a01\u4e25\uff0c\u4ee4\u4eba \u4e0d\u5bd2\u800c\u6817\u3002\u90a3\u540d\u5b98\u5dee\u7ed9\u4ed6\u4e00\u77aa\u5413\u5f97\u5012\u9000\u51e0\u6b65\uff0c\u4e00\u540d\u8001\u6355\u5934\u8d70\u4e0a\u6765\u6253\u4e86\u4e2a\u5e72\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u4f19 \u8ba1\u83bd\u649e\uff0c\u516c\u5b50\u522b\u89c1\u602a\uff01\u8bf7\u95ee\u516c\u5b50\u5c0a\u7fc1\u4f55\u4eba\uff1f\u4e0e\u8fd9\u4f4d\u670b\u53cb\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u8001\u6355\u5934\u5584\u89c2\u98ce\u8272\uff0c\u53ea\u9053 \u4ed6\u662f\u4ec0\u4e48\u5927\u5b98\u7684\u513f\u5b50\uff0c\u624d\u6562\u5982\u6b64\u9738\u9053\u3002\u90a3\u6599\u4ed6\u53c8\u51b7\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u51ed\u4f60\u4e5f\u914d\u67e5\u95ee\u6211\u7684\u5bb6 \u4e16\uff1f\u201d\u628a\u647a\u6247\u4e00\u5f20\uff0c\u7f13\u7f13\u7684\u5728\u4ed6\u9762\u524d\u6447\u4e86\u4e24\u6447\uff0c\u90a3\u8001\u6355\u5934\u9762\u8272\u500f\u53d8\uff0c\u6251\u901a\u7684\u8dea\u5728\u697c\u677f\u4e0a\uff0c\u98a4 \u58f0\u8bf4\u9053\uff1a\u201c\u5192\u72af\uff01\u5192\u72af\uff01\u4f46\u6c42\u516c\u5b50\u4e0d\u77e5\u4e0d\u7f6a\uff01\u201d\u5c11\u5e74\u516c\u5b50\u9053\uff1a\u201c\u4f60\u4eec\u56de\u53bb\u5427\uff0c\u8fd9\u4f4d\u5ba2\u4eba\u662f\u6211 \u7684\u670b\u53cb\uff0c\u6211\u62c5\u4fdd\u4ed6\u7684\u8bdd\u4e0d\u662f\u634f\u9020\u7684\uff01\u201d\u8001\u6355\u5934\u606d\u606d\u656c\u656c\u7684\u53e9\u4e86\u4e2a\u5934\uff0c\u7387\u9886\u5b98\u5dee\u75be\u6b65\u9000\u4e0b\uff01 \u3000\u3000\u5510\u6653\u6f9c\u5927\u611f\u60ca\u5947\uff0c\u8fde\u5fd9\u9053\u8c22\uff0c\u8fd9\u65f6\u5b98\u5dee\u5df2\u5168\u90e8\u9000\u5c3d\uff0c\u9152\u697c\u4e0a\u53fd\u53fd\u55b3\u55b3\uff0c\u7eb7\u7eb7\u8c08\u8bba\uff0c\u53ea\u542c \u5f97\u9152\u4fdd\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u54fc\uff0c\u6349\u91c7\u82b1\u8d3c\u6349\u5230\u8fd9\u91cc\u6765\u4e86\uff01\u201d\u4e00\u4e2a\u9152\u5ba2\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u4e0d\u5e26\u773c\u775b\uff0c\u90a3\u91cc \u6709\u8fd9\u6837\u65af\u6587\u7684\u5ba2\u5b98\u4f1a\u662f\u91c7\u82b1\u5927\u8d3c\uff01\u201d\u53c8\u4e00\u4e2a\u9152\u5ba2\u9053\uff1a\u201c\u8fd9\u4e5f\u96be\u602a\u4ed6\u4eec\uff0c\u91c7\u82b1\u8d3c\u95f9\u5f97\u8fd9\u4e48\u51f6\uff0c \u4ed6\u4eec\u88ab\u77e5\u5e9c\u4e09\u65e5\u4e00\u8ffd\uff0c\u4e94\u65e5\u4e00\u903c\uff0c\u5f53\u7136\u8981\u5230\u5904\u67e5\u8bbf\u3002\u201d\u9152\u4fdd\u9053\uff1a\u201c\u91c7\u82b1\u5927\u8d3c\uff0c\u90a3\u4f1a\u6709\u516c\u7136\u4e0a \u9152\u697c\u7b49\u4f60\u6355\u6349\u7684\u9053\u7406\uff01\u201d\u4e00\u4e2a\u9152\u5ba2\u9053\uff1a\u201c\u8fd9\u53c8\u4e0d\u7136\uff0c\u4e5f\u8bb8\u90a3\u91c7\u82b1\u8d3c\u6280\u9ad8\u80c6\u5927\uff0c\u5c31\u516c\u7136\u5230\u4f60\u7684 \u671b\u6d77\u697c\u6765\u5462\uff01\u518d\u8bf4\u5b98\u5dee\u4eec\u6293\u7d27\u641c\u6355\uff0c\u4e5f\u8fd8\u662f\u4e3a\u6c11\u9664\u5bb3\uff01\u201d\u53e6\u4e00\u4e2a\u9152\u5ba2\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5c31 \u53ea\u6015\u6b63\u70b9\u513f\u6293\u4e0d\u7740\u53cd\u800c\u6ee5\u6355\u65e0\u8f9c\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u542c\u5f97\u9a87\u7136\uff0c\u5524\u8fc7\u9152\u4fdd\u6765\u95ee\u9053\uff1a\u201c\u600e\u4e48\u4f60\u4eec\u8fd9\u91cc\u95f9\u91c7\u82b1\u8d3c\u5417\uff1f\u201d \u3000\u3000\u9152\u4fdd\u9053\uff1a\u201c\u95f9\u5f97\u51f6\u5462\uff01\u8fd9\u5341\u5929\u6765\u5929\u5929\u90fd\u95f9\u91c7\u82b1\u7684\u6848\u5b50\uff0c\u597d\u597d\u7684\u95fa\u5973\uff0c\u534a\u591c\u4e09\u66f4\u5c31\u5931\u4e86 \u8e2a\uff0c\u8fde\u4e09\u767e\u4e07\u548c\u5468\u5b88\u5907\u5973\u513f\u90fd\u7ed9\u8d3c\u4eba\u52ab\u8d70\u4e86\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u7adf\u7136\u6709\u8fd9\u6837\u7684\u4e8b\uff01\u201d\u53cc\u7709\u5012\u7ad6\uff0c\u4e0d\u89c9\u7528\u624b\u62cd\u4e86\u62cd\u5251\u9798\uff0c\u5ffd\u89c9\u90a3\u5c11\u5e74\u516c\u5b50\u53cc \u773c\u803f\u803f\u7684\u76ef\u7740\u4ed6\uff0c\u9762\u4e0a\u4e00\u7ea2\uff0c\u7b11\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u7adf\u628a\u6211\u5f53\u4f5c\u91c7\u82b1\u5927\u76d7\u4e86\uff01\u201d\u6b63\u8bf4\u8bdd\u95f4\uff0c\u9152\u5ba2 \u53c8\u662f\u4e00\u9635\u7eb7\u4e71\uff0c\u7a97\u53e3\u4e34\u8857\u7684\u5ba2\u4eba\u53eb\u9053\uff1a\u201c\u53c8\u6709\u4e00\u961f\u5b98\u5175\u8fdc\u8fdc\u6765\u4e86\uff01\u201d\u5ba2\u4eba\u4eec\u6015\u518d\u60f9\u4e8b\uff0c\u7eb7\u7eb7 \u7ed3\u8d26\u4e0b\u697c\uff0c\u53ea\u5269\u4e0b\u90a3\u5c11\u5e74\u516c\u5b50\u548c\u90a3\u7cbe\u58ee\u6c49\u5b50\u4e0e\u5510\u6653\u6f9c\u4e09\u4eba\u3002\u9152\u4fdd\u77e5\u9053\u90a3\u5c11\u5e74\u516c\u5b50\u5927\u6709\u6765\u5934\uff0c \u8d76\u5feb\u6362\u8fc7\u5c0f\u83dc\uff0c\u91cd\u65b0\u6696\u4e86\u4e09\u58f6\u7f8e\u9152\u3002 \u3000\u3000\u5c11\u5e74\u516c\u5b50\u4e0e\u5510\u6653\u6f9c\u643a\u624b\u5165\u5ea7\uff0c\u7b11\u9053\uff1a\u201c\u7ed9\u8fd9\u73ed\u5974\u624d\u8d25\u4e86\u6e05\u5174\uff01\u201d\u5510\u6653\u6f9c\u91cd\u8c22\u76f8\u6551\u4e4b\u6069\uff0c \u5c11\u5e74\u516c\u5b50\u6447\u4e86\u6447\u647a\u6247\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u5bb6\u7236\u4e0e\u73b0\u4efb\u5c71\u4e1c\u5de1\u629a\u6709\u65e7\uff0c\u8fd9\u628a\u6247\u5b50\u5c31 \u662f\u5c71\u4e1c\u5de1\u629a\u5199\u7684\uff0c\u90a3\u4e2a\u8001\u6355\u5934\u5927\u7ea6\u8ba4\u5f97\u5de1\u629a\u7684\u5b57\uff0c\u6240\u4ee5\u4e0d\u6562\u7f57\u5506\u3002\u201d\u5510\u6653\u6f9c\u773c\u5229\uff0c\u89c1\u6247\u5b50\u4e0a \u843d\u7684\u6b3e\u662f\u201c\u4e8e\u5357\u6e56\u53e9\u5199\u201d\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u4ed6\u7684\u7236\u4eb2\u662f\u671d\u4e2d\u5927\u5b98\uff1f\u201d\u60f3\u8d77\u5e08\u5085\u7684\u6212 \u6761\uff0c\u795e\u60c5\u987f\u65f6\u51b7\u6de1\u3002\u5c11\u5e74\u516c\u5b50\u9053\uff1a\u201c\u8fd9\u4e8e\u5357\u6e56\u662f\u7ff0\u6797\u51fa\u8eab\uff0c\u4e66\u6cd5\u8fd8\u8fc7\u5f97\u53bb\u3002\u4ed6\u672a\u53d1\u8ff9\u65f6\uff0c\u66fe \u662f\u6211\u7236\u4eb2\u7684\u5b66\u751f\u3002\u6240\u4ee5\u5bf9\u6211\u7236\u4eb2\u975e\u5e38\u606d\u656c\u3002\u6211\u7684\u4e00\u5bb6\uff0c\u4ece\u8fdc\u7956\u5230\u4eca\uff0c\u90fd\u6ca1\u6709\u4eba\u505a\u8fc7\u5b98\uff01\u201d\u5510 \u6653\u6f9c\u542c\u4ed6\u5982\u6b64\u8bf4\u6cd5\uff0c\u7a0d\u7a0d\u653e\u4e0b\u70b9\u5fc3\uff0c\u8bf7\u6559\u59d3\u540d\uff0c\u5c11\u5e74\u516c\u5b50\u9053\uff1a\u201c\u6211\u59d3\u738b\u540d\u5c0a\u4e00\uff0c\u4ed6\u662f\u6211\u7684\u5bb6 \u4eba\u54c8\u5e03\u9640\uff0c\u662f\u4e2a\u56de\u5b50\u3002\u201d\u5510\u6653\u6f9c\u628a\u59d3\u540d\u8bf4\u4e86\u3002\u738b\u5c0a\u4e00\u5bf9\u4ed6\u751a\u4e3a\u5ba2\u6c14\uff0c\u95ee\u9053\uff1a\u201c\u5144\u53f0\u8170\u60ac\u5b9d \u5251\uff0c\u6697\u900f\u5149\u8292\uff0c\u60f3\u5fc5\u662f\u4f4d\u5251\u6cd5\u540d\u5bb6\u3002\u201d\u5510\u6653\u6f9c\u5fd9\u9053\uff1a\u201c\u66fe\u5b66\u8fc7\u51e0\u624b\u4e09\u811a\u732b\u7684\u529f\u592b\uff0c\u90a3\u91cc\u8c08\u5f97 \u5230\u5251\u6cd5\u3002\u201d\u5c11\u5e74\u516c\u5b50\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u6447\u4e86\u6447\u6247\u5b50\uff0c\u84e6\u58f0\u541f\u9053\uff1a\u201c\u767d\u65e5\u4f9d\u5c71\u5c3d\uff0c\u9ec4\u6cb3\u5165\u6d77\u6d41\uff0c\u6b32 \u7a77\u5343\u91cc\u76ee\uff0c\u66f4\u4e0a\u4e00\u5c42\u697c\u3002\u8fd9\u671b\u6d77\u697c\u5730\u70b9\u867d\u597d\uff0c\u53ea\u662f\u4e0d\u9ad8\uff0c\u5144\u53f0\u82e5\u60f3\u89c2\u8d4f\u6d77\u666f\uff0c\u6700\u597d\u6cdb\u821f\u6d77 \u4e2d\uff0c\u6e2f\u6e7e\u5916\u4e0d\u8fdc\u4e4b\u5904\u7684\u7530\u6a2a\u5c9b\uff0c\u4e0a\u6709\u5b64\u5cf0\uff0c\u6500\u767b\u5cf0\u9876\uff0c\u770b\u7ea2\u65e5\u4ece\u6d77\u4e2d\u5347\u8d77\uff0c\u90a3\u624d\u662f\u5929\u4e0b\u5947\u666f \u5462\uff01\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5144\u53f0\u771f\u662f\u96c5\u4eba\u3002\u201d\u6b63\u559c\u4ed6\u8bdd\u950b\u5df2\u8f6c\uff0c\u4e0d\u6599\u4ed6\u987f\u4e86\u4e00\u987f\u53c8\u9053\uff1a\u201c\u5728\u5c9b\u4e0a\u5b64 \u5cf0\u8d4b\u8bd7\u821e\u5251\uff0c\u624d\u662f\u4eba\u751f\u4e50\u4e8b\uff0c\u5144\u53f0\u53ef\u5426\u501f\u5b9d\u5251\u4e00\u89c2\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u597d\u751f\u4e3a\u96be\uff0c\u8fd9\u628a\u5b9d\u5251\uff0c\u5468\u9752\u66fe\u90d1\u91cd\u5429\u5490\uff0c\u4e0d\u53ef\u968f\u4fbf\u70ab\u9732\uff0c\u4f46\u8fd9\u738b\u5c0a\u4e00\u5982\u6b64\u5ba2\u6c14\uff0c \u53c8\u5bf9\u81ea\u5df1\u6709\u6069\uff0c\u600e\u597d\u4e0d\u501f\u3002\u6b63\u8e0c\u8e87\u95f4\uff0c\u697c\u68af\u683c\u767b\u683c\u767b\u7684\u54cd\u4e86\u4e00\u9635\uff0c\u4e0a\u6765\u4e86\u4e24\u4e2a\u5973\u4eba\u3002\u524d\u9762\u7684 \u90a3\u4e2a\u9ed1\u53d1\u5782\u80a9\uff0c\u53d1\u5149\u9274\u4eba\uff0c\u9762\u4e0a\u5374\u662f\u76b1\u7eb9\u9690\u73b0\uff0c\u770b\u5979\u90a3\u5934\u79c0\u53d1\uff0c\u53ea\u4f3c\u5eff\u5c81\u5de6\u53f3\u7684\u5c11\u5973\uff0c\u770b\u5979 \u9762\u4e0a\u7684\u76b1\u7eb9\uff0c\u53c8\u4f3c\u5e74\u903e\u4e94\u65ec\u7684\u8001\u5987\u3002\u540e\u9762\u90a3\u4e2a\u957f\u7709\u5982\u753b\uff0c\u7a1a\u6c14\u672a\u6d88\uff0c\u5374\u771f\u662f\u5341\u516d\u4e03\u5c81\u7684\u5c11 \u5973\uff0c\u5c11\u5e74\u516c\u5b50\u53cc\u773c\u4e00\u7ffb\uff0c\u524d\u9762\u90a3\u5987\u4eba\u9053\uff1a\u201c\u5ba2\u5b98\u53ef\u8981\u542c\u652f\u66f2\u513f\u5417\uff1f\u201d\u738b\u5c0a\u4e00\u773c\u73e0\u4e00\u8f6c\uff0c\u5411\u5bb6 \u4e01\u629b\u4e86\u4e2a\u773c\u8272\uff0c\u9053\uff1a\u201c\u4e5f\u597d\uff01\u201d\u9ed1\u53d1\u8001\u5987\u5c06\u624b\u4e2d\u4e24\u7247\u7af9\u677f\u4e00\u6572\uff0c\u90a3\u5c11\u5973\u8f7b\u542f\u6731\u5589\uff0c\u4f4e\u58f0\u5531\u9053\uff1a \u3000\u3000\u201c\u4e00\u7247\u7ea2\u971e\u6d77\u4e0a\u751f\uff0c\u6d77\u4e2d\u6709\u5c9b\u66f0\u7530\u6a2a\uff0c\u5f53\u5e74\u9f50\u56fd\u8d35\u516c\u5b50\uff0c\u56fd\u7834\u5bb6\u4ea1\u6291\u6cea\u884c\uff0c\u8a93\u4e0d\u5e1d\u79e6\u60ac \u6b63\u6c14\uff0c\u6d77\u9685\u6297\u66b4\u89c1\u65d7\u6e21\uff0c\u4e94\u767e\u58ee\u58eb\u8a93\u540c\u6b7b\uff0c\u5f3a\u864f\u4e0d\u706d\u5929\u9053\u76f2\u2026\u2026\u201d \u3000\u3000\u6b4c\u72b9\u672a\u7ec8\uff0c\u738b\u5c0a\u4e00\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u5531\u4e86\uff01\u201d\u5987\u4eba\u9053\uff1a\u201c\u5ba2\u5b98\u9762\u5bf9\u7530\u6a2a\u5c9b\uff0c\u5374\u4e0d\u559c \u542c\u7530\u6a2a\u8f9e\u5417\uff1f\u201d\u738b\u5c0a\u4e00\u7684\u5bb6\u4eba\u54c8\u5e03\u9640\u65a5\u9053\uff1a\u201c\u4f11\u5f97\u7f57\u5506\uff01\u201d\u738b\u5c0a\u4e00\u9053\uff1a\u201c\u8d4f\u5979\u94f6\u5b50\uff0c\u53eb\u5979\u53bb \u5427\uff01\u201d\u54c8\u5e02\u9640\u628a\u624b\u4e00\u626c\uff0c\u4e24\u952d\u5927\u94f6\u9aa4\u7684\u63b7\u53bb\uff0c\u8001\u5987\u4eba\u9053\uff1a\u201c\u8c01\u7a00\u7f55\u4f60\u8fd9\u70b9\u788e\u94f6\uff1f\u201d\u4e3e\u8896\u4e00 \u62c2\uff0c\u4e24\u952d\u5927\u94f6\u843d\u5230\u684c\u9762\uff0c\u788e\u6210\u65e0\u6570\u5c0f\u5757\uff01\u738b\u5c0a\u4e00\u4e0e\u54c8\u5e03\u9640\u4e00\u60ca\uff0c\u90a3\u4e24\u4e2a\u5973\u4eba\u5df2\u7ecf\u4e0b\u697c\u53bb\u4e86\u3002 \u54c8\u5e03\u9640\u4f5c\u52bf\u6b32\u8ffd\uff0c\u5de5\u5c0a\u4e00\u9053\uff1a\u201c\u7531\u5979\u53bb\u5427\u3002\u5510\u5144\uff0c\u9002\u624d\u8c08\u53ca\u8bf7\u501f\u5b9d\u5251\u4e00\u89c2\uff0c\u5e78\u52ff\u89c1\u5374\uff01\u201d\u5510 \u6653\u6f9c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d\u624b\u6307\u6478\u5411\u8170\u95f4\uff0c\u5ffd\u7136\u60ca\u53eb\u8d77\u6765\u9053\uff1a\u201c\u6211\u7684\u5251\u4e0d\u89c1\u4e86\u3002\u201d\u4e24\u4eba\u4e00 \u770b\uff0c\u5510\u6653\u6f9c\u8170\u9645\u7a7a\u65e0\u4e00\u7269\uff0c\u5b9d\u5251\u679c\u7136\u4e0d\u89c1\u4e86\u3002\u54c8\u5e03\u9640\u9053\uff1a\u201c\u8fd9\u8001\u4e5e\u5a46\u624b\u6cd5\u597d\u5feb\uff01\u201d\u5510\u6653\u6f9c\u5931 \u4e86\u5b9d\u5251\uff0c\u5fc3\u610f\u5982\u711a\uff0c\u8fde\u5fd9\u544a\u8f9e\u3002\u738b\u5c0a\u4e00\u5174\u8da3\u7d22\u7136\uff0c\u4e3e\u624b\u8bf4\u9053\uff1a\u201c\u5510\u5144\u4e0d\u5fc5\u5fc3\u7126\uff0c\u6240\u5931\u5b9d\u5251\uff0c \u5c0f\u5f1f\u5f53\u547d\u5bb6\u4eba\u534f\u52a9\u5bfb\u56de\u3002\u201d\u5510\u6653\u6f9c\u9053\u4e86\u53e5\u8c22\uff0c\u5306\u5306\u4e0b\u697c\u8ffd\u90a3\u5987\u4eba\u3002\u6d77\u6ee8\u6797\u836b\u8def\u4e0a\uff0c\u4e24\u5934\u90fd\u6709 \u5b98\u5175\u5de1\u903b\uff0c\u90a3\u91cc\u8fd8\u6709\u90a3\u5987\u4eba\u5f71\u5b50\u3002\u5b98\u5175\u89c1\u5510\u6653\u6f9c\u5306\u5306\u8dd1\u51fa\uff0c\u7adf\u4e5f\u4e0d\u52a0\u62e6\u963b\u3002 \u3000\u3000\u5510\u6653\u6f9c\u8ddf\u6768\u4ef2\u82f1\u5b66\u4e86\u4e94\u5e74\u529f\u592b\uff0c\u5bf9\u6768\u5bb6\u7684\u795e\u5f39\u7edd\u6280\uff0c\u751a\u6709\u5fc3\u5f97\uff0c\u800c\u4e14\u4ed6\u7528\u7684\u6697\u5668\u662f\u98de \u8292\uff0c\u5b66\u4e86\u795e\u5f39\u624b\u6cd5\uff0c\u66f4\u89c1\u5389\u5bb3\uff0c\u98de\u8292\u4efd\u91cf\u6781\u8f7b\uff0c\u53d6\u51c6\u6781\u96be\uff0c\u6240\u4ee5\u773c\u529b\u5fc5\u987b\u7ec3\u5f97\u975e\u5e38\u4e4b\u597d\u3002\u7136 \u800c\u51ed\u4ed6\u8fd9\u6837\u7684\u529f\u592b\uff0c\u5b9d\u5251\u7ed9\u4eba\u5077\u53bb\uff0c\u7adf\u7136\u4e1d\u6beb\u672a\u89c9\uff0c\u5077\u5251\u7684\u4eba\u4e0d\u8bba\u662f\u5426\u8001\u5987\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u90fd \u662f\u4e0d\u53ef\u60f3\u50cf\uff01\u5510\u6653\u6f9c\u6c14\u8bc5\u795e\u4f24\uff0c\u5fc3\u60f3\uff1a\u5b9d\u5251\u7ed9\u8fd9\u6837\u7684\u9ad8\u624b\u5077\u53bb\uff0c\u90a3\u91cc\u8fd8\u6709\u8ffd\u56de\u4e4b\u671b\uff1f\u5782\u5934\u4e27 \u6c14\u5728\u6d77\u6ee8\u4e71\u8d70\uff0c\u8d8a\u8d70\u8d8a\u8fdc\uff0c\u731b\u89c1\u6e2f\u6e7e\u5916\u6709\u51e0\u53ea\u6e14\u8239\u505c\u6cca\uff0c\u4e00\u53ea\u5927\u8239\u8239\u5934\uff0c\u7acb\u7740\u4e00\u4e2a\u5c11\u5e74\u5973 \u5b50\uff0c\u98ce\u9b13\u96fe\u9b13\uff0c\u751a\u662f\u7f8e\u8c8c\uff0c\u5510\u6653\u6f9c\u5b9a\u795e\u4e00\u770b\uff0c\u5374\u4e0d\u662f\u90a3\u4e2a\u5c11\u5973\uff0c\u54d1\u7136\u5931\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u5b9d\u5251\u662f \u5931\u5b9a\u4e86\uff0c\u8fd8\u662f\u5148\u56de\u53bb\u5427\uff01\u201d\u884c\u4e86\u51e0\u6b65\uff0c\u90a3\u5973\u5b50\u5df2\u8fdb\u8231\u4e2d\u3002\u5ffd\u89c1\u4e00\u4e2a\u9762\u5982\u51a0\u7389\u7684\u7f8e\u5c11\u5e74\uff0c\u4e5f\u51dd \u795e\u6ce8\u89c6\u90a3\u53ea\u6e14\u821f\uff01 \u3000\u3000\u5510\u6653\u6f9c\u89c1\u4ed6\u770b\u5f97\u51fa\u795e\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u4eba\u5c31\u662f\u91c7\u82b1\u5927\u8d3c\uff01\u201d\u731b\u89c1\u5c11\u5e74\u8863\u8896\u4e00 \u626c\uff0c\u5510\u6653\u6f9c\u76ee\u529b\u6781\u597d\uff0c\u770b\u51fa\u4ed6\u662f\u8896\u5e95\u98de\u9556\u7684\u6253\u6697\u5668\u624b\u6cd5\uff0c\u800c\u6240\u6253\u7684\u6697\u5668\uff0c\u4efd\u91cf\u53c8\u662f\u6781\u8f7b\uff0c\u666e \u901a\u4eba\u5c31\u662f\u7ad9\u5728\u8eab\u8fb9\u4e5f\u89c9\u5bdf\u4e0d\u51fa\u3002\u5f85\u5c11\u5e74\u884c\u540e\uff0c\u5510\u6653\u6f9c\u8d70\u4e0a\u5824\u5cb8\uff0c\u770b\u90a3\u6e14\u8239\uff0c\u8239\u8237\u4e0a\u5df1\u5e73\u6dfb\u4e86 \u4e00\u6735\u6885\u82b1\uff0c\u82b1\u5f00\u4e94\u74e3\uff0c\u6e05\u6e05\u695a\u695a\uff0c\u5c31\u5982\u5de7\u624b\u5320\u4eba\u523b\u51fa\u6765\u4f3c\u7684\u3002\u5510\u6653\u6f9c\u77e5\u9053\u8fd9\u662f\u90a3\u6885\u82b1\u5f62\u7684\u6697 \u5668\u6253\u51fa\u6765\u7684\u3002\u6697\u53eb\uff1a\u4e0d\u597d\uff01\u60f3\u9053\uff1a\u8fd9\u6b63\u662f\u91c7\u82b1\u5927\u8d3c\u7559\u4e0b\u7684\u6697\u8bb0\u4e86\uff0c\u5341\u6210\u6709\u4e5d\u662f\u4ed6\u770b\u4e0a\u4e86\u90a3\u4e2a \u59d1\u5a18\uff0c\u53ea\u6015\u4eca\u665a\u4ed6\u5c31\u8981\u5230\u8fd9\u8239\u4e0a\u6765\u82b1\u3002\u6b63\u60f3\u53eb\u90a3\u8239\u8001\u5927\u51fa\u6765\uff0c\u731b\u7136\u95f4\u8239\u7bf7\u4e00\u63ed\uff0c\u90a3\u8239\u5a18\u8de8\u4e86 \u51fa\u6765\uff0c\u67f3\u7709\u4e00\u7ad6\uff0c\u5411\u5510\u6653\u6f9c\u6a2a\u4e86\u4e00\u773c\uff0c\u5c06\u6868\u5728\u6c34\u91cc\u4e00\u63fd\uff0c\u731b\u7684\u6296\u8d77\u4e00\u6761\u6c34\u7ebf\uff0c\u5411\u5510\u6653\u6f9c\u5c04 \u6765\uff0c\u5510\u6653\u6f9c\u51b7\u4e0d\u9632\u7ed9\u6c34\u7ebf\u5c04\u6e7f\u5934\u9762\uff0c\u9762\u76ae\u4e0a\u7adf\u8fa3\u8fa3\u4f5c\u75db\u3002\u90a3\u8239\u5a18\u5212\u4e86\u4e24\u4e0b\uff0c\u628a\u6e14\u8239\u5f00\u8d70\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u63e9\u5e72\u6c34\u73e0\uff0c\u6697\u6697\u53eb\u5c48\uff0c\u8fd9\u8239\u5a18\u5b9a\u662f\u628a\u4ed6\u5f53\u6210\u8f7b\u8584\u5c11\u5e74\uff0c\u767b\u5f92\u4e4b\u8f88\uff0c\u6240\u4ee5\u624d\u8fd9\u6837\u5bf9 \u4ed8\u4ed6\u3002\u5fc3\u60f3\uff1a\u5982\u73b0\u5728\u4e0a\u524d\u53bb\u544a\u8bc9\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4e0d\u80af\u76f8\u4fe1\uff0c\u751a\u6216\u7591\u6211\u53e6\u6709\u7528\u5fc3\uff0c\u4e0d\u5982\u4eca\u665a\u518d \u6765\uff0c\u628a\u90a3\u91c7\u82b1\u5927\u8d3c\u6349\u4f4f\uff0c\u4e5f\u597d\u7ed9\u8fd9\u91cc\u7684\u767e\u59d3\u9664\u53bb\u4e00\u5bb3\u3002\u4e3b\u610f\u6253\u5b9a\uff0c\u770b\u90a3\u6e14\u8239\u5728\u4e0b\u6e38\u6e2f\u6e7e\u505c\u6cca \u4e4b\u540e\uff0c\u4fbf\u6298\u56de\u8857\u5e02\uff0c\u56de\u5230\u81ea\u5df1\u6240\u4f4f\u7684\u5ba2\u6808\u3002 \u3000\u3000\u5510\u6653\u6f9c\u79bb\u5f00\u5ba2\u6808\u4e4b\u65f6\uff0c\u7a97\u95e8\u90fd\u5df2\u7ecf\u5173\u4e0a\uff0c\u623f\u95e8\u8fd8\u7528\u94c1\u9501\u9501\u5f97\u597d\u597d\u7684\uff0c\u5c31\u5728\u6253\u5f00\u623f\u95e8\u4e4b \u9645\uff0c\u5ffd\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u5510\u6653\u6f9c\u75be\u5fd9\u56de\u5934\uff0c\u5e76\u4e0d\u89c1\u4eba\u5f71\uff0c\u5fc3\u4e2d\u72b9\u81ea\u6697\u7b11\u81ea\u5df1\u7591\u795e\u7591\u9b3c\uff0c\u4e0d\u6599\u8d70\u8fdb \u623f\u4e2d\uff0c\u731b\u89c9\u5bd2\u5149\u8000\u76ee\uff0c\u8fd9\u4e00\u4e0b\u51e0\u4e4e\u628a\u4ed6\u60ca\u5f97\u558a\u51fa\u58f0\u6765\u3002 \u3000\u3000\u5e8a\u8fb9\u7684\u5c0f\u51e0\u4e0a\u653e\u7740\u4e00\u628a\u5b9d\u5251\uff0c\u6b63\u662f\u81ea\u5df1\u90a3\u628a\u6e38\u9f99\u5b9d\u5251\uff0c\u5251\u9798\u6302\u5728\u5899\u4e0a\uff0c\u5510\u6653\u6f9c\u62ff\u8d77\u5b9d \u5251\uff0c\u5251\u5e95\u538b\u7740\u4e00\u5f20\u5b57\u6761\uff0c\u5199\u9053\uff1a\u201c\u4e09\u65e5\u540e\u5348\u591c\u65f6\u5206\uff0c\u5230\u7530\u6a2a\u5e99\u6765\u89c1\u6211\uff01\u201d\u5510\u6653\u6f9c\u5fc3\u4e0a\u5341\u4e94\u4e2a \u540a\u6876\uff0c\u4e03\u4e0a\u516b\u843d\uff0c\u4e0d\u77e5\u8fd9\u76d7\u5251\u8fd8\u5251\u7684\u9ad8\u4eba\u662f\u4f55\u7528\u610f\uff1f\u53c8\u4e0d\u77e5\u7530\u6a2a\u5e99\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u4e0d\u8fc7\u8ddd\u79bb\u7ea6 \u4f1a\u65f6\u95f4\u8fd8\u6709\u4e09\u65e5\uff0c\u4e09\u65e5\u4e2d\u603b\u53ef\u6253\u542c\u51fa\u6765\u3002\u5510\u6653\u6f9c\u5b9a\u4e86\u5b9a\u5fc3\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u76d7\u5251\u8005\u82e5\u662f\u524d\u8f88\u9ad8 \u4eba\uff0c\u5bf9\u6211\u5b9a\u65e0\u6076\u610f\uff0c\u82e5\u662f\u574f\u4eba\uff0c\u53c8\u65ad\u65e0\u518d\u628a\u5251\u9001\u56de\u4e4b\u7406\u3002\u770b\u6765\u6b64\u4e8b\u867d\u5947\uff0c\u5e76\u65e0\u4f24\u5bb3\u3002\u5012\u662f\u4eca \u665a\u53bb\u6597\u90a3\u91c7\u82b1\u5927\u8d3c\uff0c\u5374\u8981\u5c0f\u5fc3\u3002\u770b\u4ed6\u6253\u6697\u5668\u7684\u624b\u6cd5\uff0c\u5df2\u5c31\u662f\u6b66\u6797\u4e2d\u7f55\u89c1\u7684\u529f\u592b\u3002\u201d\u5f53\u4e0b\u518d\u4e0d \u601d\u91cf\uff0c\u7eb3\u5934\u4fbf\u7761\u3002 \u3000\u3000\u7761\u9192\u5929\u5df2\u508d\u665a\uff0c\u5510\u6653\u6f9c\u5403\u8fc7\u665a\u996d\uff0c\u8de8\u51fa\u5ba2\u5e97\uff0c\u5bf9\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u4eca\u665a\u6211\u4e5f\u8bb8\u8981\u8fdf\u4e9b\u624d\u80fd\u56de \u6765\u3002\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u5ba2\u5b98\u81ea\u4fbf\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u82e5\u6709\u4eba\u6765\u627e\u6211\uff0c\u8bf7\u8bb0\u5f97\u95ee\u4ed6\u59d3\u540d\u3002\u201d\u5e97\u5c0f\u4e8c \u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u201d\u5510\u6653\u6f9c\u8d70\u51fa\u6d77\u6ee8\uff0c\u8fd9\u662f\u4e00\u4e2a\u4e0b\u5f26\u6708\u591c\uff0c\u6de1\u6708\u758f\u661f\uff0c\u628a\u5927\u6d77\u886c\u5f97\u66f4\u662f\u795e\u79d8 \u6df1\u9065\u3002\u5510\u6653\u6f9c\u627e\u7740\u4e86\u90a3\u53ea\u6e14\u8239\uff0c\u501f\u7740\u6d77\u8fb9\u7684\u4e00\u5757\u5ca9\u77f3\u85cf\u8eab\uff0c\u63d0\u5fc3\u540a\u80c6\u7684\u5728\u7b49\u5019\u90a3\u91c7\u82b1\u5927\u8d3c\uff01 \u3000\u3000\u7b49\u4e86\u597d\u4e45\uff0c\u770b\u90a3\u4e0b\u5f26\u65b0\u6708\uff0c\u6e10\u6e10\u5347\u5230\u6d77\u7684\u4e0a\u7a7a\uff0c\u5510\u6653\u6f9c\u5fc3\u60f3\uff1a\u201c\u662f\u65f6\u5019\u4e86\uff01\u201d\u679c\u7136\u518d\u8fc7 \u4e00\u4f1a\uff0c\u4e00\u6761\u4eba\u5f71\u75be\u7684\u98de\u6765\uff0c\u7adf\u662f\u4e00\u8eab\u767d\u8272\u8863\u88f3\uff0c\u867d\u7136\u662f\u6708\u8272\u6726\u80e7\uff0c\u4e5f\u77a7\u5f97\u6e05\u6e05\u695a\u695a\u3002\u5510\u6653\u6f9c \u6697\u6697\u79f0\u5947\uff0c\u767d\u8863\u4e43\u591c\u884c\u4eba\u7684\u5927\u5fcc\uff0c\u4f55\u51b5\u5fd7\u5728\u91c7\u82b1\uff1f\u90a3\u767d\u8863\u4eba\u8dd1\u5230\u6d77\u8fb9\uff0c\u53ef\u4e0d\u6b63\u662f\u65e5\u95f4\u6240\u89c1\u7684 \u90a3\u4e2a\u7f8e\u5c11\u5e74\uff1f\u5510\u6653\u6f9c\u624b\u63e1\u98de\u8292\uff0c\u5c1a\u672a\u53d1\u5c04\uff0c\u90a3\u5c11\u5e74\u8db3\u5c16\u4e00\u70b9\uff0c\u731b\u5982\u4e00\u53ea\u51b2\u5929\u5927\u9e64\uff0c\u8ff3\u81ea\u98de\u4e0a \u6e14\u8239\u90a3\u626f\u7740\u98ce\u5e06\u7684\u6845\u9876\u3002\u5510\u6653\u6f9c\u51b2\u53e3\u558a\u9053\uff1a\u201c\u6349\u91c7\u82b1\u8d3c\u5440\u3002\u201d\u624b\u4e0a\u4e00\u628a\u98de\u8292\uff0c\u626c\u7a7a\u5c04\u51fa\uff01 \u3000\u3000\u767d\u8863\u5c11\u5e74\u53eb\u9053\uff1a\u201c\u662f\u6211\uff0c\u8bf7\u59b9\u5b50\u51fa\u6765\uff01\u201d\u8239\u8231\u7a81\u7136\u62a2\u51fa\u4e00\u4eba\uff0c\u4e00\u5200\u628a\u8239\u6845\u65a9\u65ad\uff0c\u767d\u8863\u5c11 \u5e74\u51cc\u7a7a\u4e00\u4e2a\u5012\u7ffb\uff0c\u843d\u5728\u8239\u9762\uff0c\u8eab\u5f62\u77eb\u6377\u4e4b\u6781\uff0c\u5206\u660e\u662f\u672a\u53d7\u4f24\u3002\u5510\u6653\u6f9c\u8fd9\u628a\u98de\u8292\uff0c\u662f\u767d\u6253\u4e86\uff01 \u3000\u3000\u8239\u8231\u91cc\u62a2\u51fa\u6765\u7684\u662f\u8239\u4e0a\u7684\u8001\u6e14\u592b\uff0c\u5530\u5530\u51e0\u5200\u5411\u767d\u8863\u5c11\u5e74\u65a9\u53bb\uff0c\u53e3\u4e2d\u559d\u9053\uff1a\u201c\u5478\uff0c\u4e0d\u8981\u8138 \u7684\uff0c\u4f60\u8fd8\u5728\u8fd9\u91cc\u7ea0\u7f20\u4f5c\u751a\uff1f\u201d\u767d\u8863\u5c11\u5e74\u53cc\u624b\u7a7a\u7a7a\uff0c\u53ea\u662f\u95ea\u8eb2\uff0c\u5e76\u4e0d\u8fd8\u62db\uff0c\u5140\u662f\u53eb\u9053\uff1a\u201c\u5305 \u59b9\uff0c\u9c7c\u59b9\uff01\u201d\u8239\u4e2d\u4e00\u58f0\u54ed\u6ce3\uff0c\u5510\u6653\u6f9c\u767d\u5929\u6240\u89c1\u7684\u90a3\u4e2a\u7f8e\u8c8c\u6e14\u5a18\u7a9c\u4e86\u51fa\u6765\uff0c\u54ed\u7740\u53eb\u9053\uff1a\u201c\u79e6 \u5b98\uff0c\u4f60\u8d70\u5427\uff01\u201d\u8001\u6e14\u592b\u6012\u559d\u9053\uff1a\u201c\u8d31\u4e2b\u5934\uff0c\u56de\u53bb\uff01\u201d\u767d\u8863\u5c11\u5e74\u8fde\u8eb2\u4e09\u5200\uff0c\u731b\u7684\u4e00\u7a9c\uff0c\u51b2\u5230\u90a3 \u6e14\u5a18\u8eab\u8fb9\uff01\u5510\u6653\u6f9c\u8fd0\u8db3\u8170\u52b2\uff0c\u4e00\u62e7\u8eab\u98de\u4e0a\u8239\u9762\uff0c\u6e38\u9f99\u5251\u4e00\u62db\u201c\u4ed9\u4eba\u6307\u8def\u201d\uff0c\u5411\u767d\u8863\u5c11\u5e74\u80f8\u819b \u523a\u53bb\uff0c\u559d\u9053\uff1a\u201c\u597d\u5927\u80c6\u7684\u91c7\u82b1\u8d3c\u5440\uff01\u201d\u767d\u8863\u5c11\u5e74\u9661\u89c1\u5bdf\u5149\u523a\u76ee\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u582a\u582a\u907f\u5f00\uff0c\u6014\u4e86 \u4e00\u6014\uff0c\u559d\u9053\uff1a\u201c\u8c01\u662f\u91c7\u82b1\u8d3c\u5440\uff1f\u201d\u5510\u6653\u6f9c\u5530\u7684\u53c8\u662f\u4e00\u5251\uff0c\u90a3\u8001\u6e14\u592b\u5c06\u8239\u5a18\u63a8\u56de\u8231\u4e2d\uff0c\u9762\u6709\u60ca \u5947\u4e4b\u8272\uff0c\u624b\u63d0\u864e\u5934\u5200\uff0c\u62e6\u5728\u8239\u5934\uff0c\u5374\u4e0d\u52a8\u624b\u3002 \u3000\u3000\u5510\u6653\u6f9c\u7684\u8ffd\u98ce\u5251\u6cd5\u8fc5\u75be\u5f02\u5e38\uff0c\u767d\u8863\u5c11\u5e74\u7a7a\u624b\u95ea\u907f\uff0c\u9887\u4e3a\u5403\u529b\uff0c\u52a0\u4ee5\u5728\u4e00\u6761\u4e0d\u751a\u5bbd\u5927\u7684\u8239 \u9762\u4e0a\uff0c\u4e0d\u8bba\u5de6\u8eb2\u53f3\u95ea\uff0c\u5168\u5728\u5251\u950b\u6240\u53ca\u7684\u5730\u65b9\uff0c\u5510\u6653\u6f9c\u8fd0\u5251\u5982\u98ce\uff0c\u603b\u523a\u4e86\u4e8c\u4e09\u5341\u5251\uff0c\u5140\u81ea\u672a\u523a \u7740\u90a3\u767d\u8863\u5c11\u5e74\uff0c\u5fc3\u4e2d\u9a87\u5f02\u4e4b\u6781\u3002\u90a3\u767d\u8863\u5c11\u5e74\u5728\u8fd9\u6837\u72ed\u7a84\u7684\u8239\u9762\u4e0a\uff0c\u62c9\u4e0d\u5f00\u811a\u6b65\uff0c\u5bf9\u4ed8\u8fd9\u6837\u51cc \u5389\u7684\u5251\u6cd5\uff0c\u7a7a\u624b\u593a\u767d\u5200\u7684\u529f\u592b\u4e5f\u4f7f\u4e0d\u51fa\u6765\uff0c\u9976\u4ed6\u6b66\u529f\u7cbe\u6e5b\uff0c\u4e5f\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u5510\u6653\u6f9c\u8fde\u523a\u4e0d \u7740\uff0c\u5fc3\u4e2d\u4e00\u6025\u628a\u8ffd\u98ce\u9010\u7535\u7684\u4e0a\u516b\u8def\u5251\u6cd5\u65bd\u5c55\u51fa\u6765\uff0c\u5251\u6c14\u68ee\u68ee\uff0c\u4e13\u523a\u654c\u4eba\u53cc\u76ee\uff0c\u767d\u8863\u5c11\u5e74\u4e00\u58f0 \u559d\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u4e0d\u505c\u624b\uff0c\u6211\u53ef\u8981\u5f97\u7f6a\u4f60\u4e86\uff01\u201d\u731b\u542c\u5f97\u4e00\u58f0\u88c2\u5e1b\uff0c\u767d\u8863\u5c11\u5e74\u6495\u4e0b\u4e00\u5e45\u8863\u88f3\uff0c \u8fce\u98ce\u4e00\u6325\u3002\u628a\u5510\u6653\u6f9c\u7684\u5b9d\u5251\u88f9\u7740\uff0c\u5510\u6653\u6f9c\u864e\u53e3\u4e00\u9707\uff0c\u5982\u540c\u649e\u7740\u94c1\u996d\u4e00\u6837\uff0c\u624b\u52b2\u4e00\u677e\uff0c\u5b9d\u5251\u5df2 \u7ed9\u593a\u53bb\uff0c\u545b\u561f\u4e00\u58f0\uff0c\u7ed9\u767d\u8863\u5c11\u5e74\u629b\u5165\u8231\u4e2d\u3002\u5510\u6653\u6f9c\u4f0f\u7740\u8231\u9762\u4e00\u6eda\uff0c\u5de6\u624b\u98de\u8292\uff0c\u53c8\u8131\u624b\u6253\u51fa\uff0c \u767d\u8863\u5c11\u5e74\u6599\u4e0d\u5230\u4ed6\u5931\u5251\u4e4b\u540e\uff0c\u8fd8\u662f\u5982\u6b64\u987d\u5f3a\uff0c\u4e00\u4e2a\u758f\u795e\uff0c\u8db3\u8e1d\u4e2d\u4e86\u4e24\u8292\uff01\u5411\u524d\u51b2\u51fa\u51e0\u6b65\uff0c\u51b2 \u5230\u8239\u8fb9\uff0c\u5510\u6653\u6f9c\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u7ffb\u4e86\u8d77\u6765\uff0c\u9632\u4ed6\u8fdb\u88ad\u3002 \u3000\u3000\u767d\u8863\u5c11\u5e74\u5374\u4e0d\u8fdb\u88ad\uff0c\u54d1\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u4e08\uff0c\u771f\u7684\u8fd9\u6837\u7edd\u60c5\u4e48\uff1f\u201d\u8001\u6e14\u592b\u731b\u7136\u559d\u9053\uff1a\u201c\u767d\u6cf0 \u5b98\uff0c\u5929\u5802\u6709\u8def\u4f60\u4e0d\u8d70\uff0c\u5730\u72f1\u65e0\u95e8\u4f60\u504f\u5165\u6765\uff0c\u4f60\u4e0d\u8d70\uff0c\u6211\u53ef\u8981\u6253\u53d1\u4f60\u4e86\uff01\u201d\u864e\u5934\u5200\u4e00\u56e2\u4e00\u5377\uff0c \u903c\u8d77\u4e00\u5708\u5200\u5149\uff0c\u5510\u6653\u6f9c\u7ad9\u5728\u65c1\u8fb9\uff0c\u5934\u53d1\u8863\u88f3\u7adf\u7ed9\u5200\u98ce\u903c\u5f97\u98d8\u98d8\u62c2\u52a8\uff0c\u51c9\u900f\u5fc3\u5934\uff0c\u8001\u6e14\u592b\u8fd9\u4efd \u529f\u529b\uff0c\u4e0d\u5728\u5173\u4e1c\u56db\u4fa0\u4e4b\u4e0b\u3002\u767d\u8863\u5c11\u5e74\u8db3\u8e1d\u53d7\u4f24\uff0c\u8df3\u52a8\u4e0d\u4fbf\uff0c\u53eb\u9053\uff1a\u201c\u9c7c\u59b9\uff0c\u9c7c\u59b9\uff0c\u54b1\u4eec\u4eca\u751f \u4eca\u4e16\u4e0d\u80fd\u518d\u89c1\u4e86\uff01\u201d\u8239\u8231\u4e2d\u4e00\u58f0\u53eb\u558a\uff0c\u90a3\u6e14\u5a18\u4e00\u62f3\u5c06\u8231\u95e8\u6253\u788e\uff0c\u4e0d\u987e\u7236\u547d\uff0c\u53c8\u7a9c\u4e86\u51fa\u6765\uff01\u8001 \u6e14\u592b\u624b\u4e2d\u5200\u4e00\u62db\u201c\u9ebb\u59d1\u62e8\u4e91\u201d\uff0c\u84e6\u7136\u4e00\u62ab\u4e00\u65a9\uff0c\u7adf\u4ee5\u5feb\u5200\u65a9\u4e71\u9ebb\u7684\u624b\u6cd5\uff0c\u8981\u5728\u81ea\u5df1\u5973\u513f\u62a2\u6765 \u4e4b\u524d\uff0c\u628a\u767d\u8863\u5c11\u5e74\u62e6\u8170\u65a9\u4e3a\u4e24\u622a\uff01 \u3000\u3000\u767d\u8863\u5c11\u5e74\u4e0a\u8239\u4e4b\u540e\uff0c\u4e00\u756a\u62fc\u6597\uff0c\u7cfb\u8239\u7684\u7c97\u7ef3\u5df2\u65ad\uff0c\u6e14\u8239\u987a\u7740\u6c34\u6d41\uff0c\u5df2\u79bb\u5f00\u4e86\u5cb8\u8fb9\u5341\u4f59\u4e08 \u8fdc\u3002\u5c31\u5728\u8fd9\u8001\u6e14\u592b\u6325\u5200\u731b\u523a\u4e4b\u9645\uff0c\u6c34\u9762\u4e0a\u7a81\u7136\u4e00\u58f0\u6e05\u53f1\uff1a\u201c\u5200\u4e0b\u7559\u4eba\uff01\u201d\u7adf\u7136\u53c8\u662f\u4e00\u4e2a\u767d\u8863 \u5c11\u5e74\uff0c\u51cc\u6ce2\u98de\u63a0\u800c\u6765\uff01\u5510\u6653\u6f9c\u773c\u775b\u4e00\u82b1\uff0c\u6c34\u9762\u4e0a\u7684\u767d\u8863\u5c11\u5e74\u5df2\u8dc3\u5230\u8239\u4e0a\u3002\u5f85\u6e14\u592b\u4e00\u5200\u5288\u4e0b\uff0c \u84e6\u7136\u624b\u8155\u4e00\u9ebb\uff0c\u864e\u5934\u5200\u7adf\u7ed9\u540e\u6765\u7684\u90a3\u5c11\u5e74\u5288\u624b\u593a\u53bb\uff01\u90a3\u8001\u6e14\u592b\u7eb5\u6a2a\u534a\u4e16\uff0c\u540d\u6ee1\u6c5f\u6e56\uff0c\u672a\u9047\u654c \u624b\uff0c\u90a3\u77e5\u4e0d\u8fc7\u4e00\u62db\uff0c\u7adf\u7136\u7ed9\u90a3\u5c11\u5e74\u4e0d\u77e5\u7528\u4ec0\u4e48\u624b\u6cd5\uff0c\u593a\u8fc7\u5b9d\u5200\uff0c\u8fd9\u4e00\u4e0b\u53c8\u9a87\u53c8\u6025\uff0c\u5374\u662f\u4e0d\u6562 \u53d1\u4f5c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u54fc\uff0c\u767d\u6cf0\u5b98\uff0c\u539f\u6765\u4f60\u8fd8\u9080\u6709\u540c\u515a\uff0c\u4f60\u662f\u6210\u5fc3\u6765\u62a2\u4eb2\u4e86\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u60ca\u9b42\u7a0d\u5b9a\uff0c\u770b\u540e\u6765\u7684\u90a3\u767d\u8863\u5c11\u5e74\uff0c\u7709\u6e05\u76ee\u79c0\uff0c\u8863\u8882\u98d8\u98d8\u3002\u7adf\u6bd4\u524d\u5934\u7684\u90a3\u767d\u8863\u5c11\u5e74 \u8fd8\u8981\u4fca\u7f8e\uff01\u518d\u770b\u90a3\u6d77\u9762\u4e0a\u98d8\u7740\u51e0\u5757\u5c0f\u6728\u677f\uff0c\u624d\u77e5\u8fd9\u5c11\u5e74\u7adf\u662f\u8fd0\u7528\u201c\u767b\u840d\u6e21\u6c34\u201d\u7684\u7edd\u9876\u8f7b\u529f\uff0c \u501f\u6728\u677f\u4e4b\u529b\uff0c\u51cc\u6ce2\u98de\u6e21\u800c\u6765\uff01\u8fd9\u79cd\u201c\u767b\u840d\u6e21\u6c34\u201d\u7684\u8f7b\u529f\u5510\u6653\u6f9c\u53ea\u662f\u542c\u4eba\u8bf4\u8fc7\uff0c\u60f3\u4e0d\u5230\u5982\u4eca\u4eb2 \u773c\u770b\u89c1\uff0c\u518d\u7ec6\u770b\u65f6\uff0c\u8fd9\u5c11\u5e74\u9762\u8c8c\u597d\u50cf\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\u4e00\u822c\u3002 \u3000\u3000\u90a3\u540e\u6765\u7684\u767d\u8863\u5c11\u5e74\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8001\u4e08\u4e14\u6162\uff0c\u5f85\u6211\u95ee\u4ed6\uff01\u201d\u5411\u524d\u5934\u90a3\u767d\u8863\u5c11\u5e74\u4e00\u6307\uff0c\u6b63\u5bb9 \u95ee\u9053\uff1a\u201c\u4f60\u53eb\u767d\u6cf0\u5b98\u5417\uff1f\u4f55\u4eba\u95e8\u4e0b\uff1f\u201d\u524d\u5934\u90a3\u5c11\u5e74\u6602\u7136\u8bf4\u9053\uff1a\u201c\u767d\u6cf0\u5b98\u884c\u4e0d\u6539\u540d\uff0c\u5750\u4e2a\u6539 \u59d3\uff0c\u72ec\u81c2\u795e\u5c3c\u95e8\u4e0b\uff0c\u6c5f\u5357\u516b\u4fa0\u4e2d\u6392\u884c\u7b2c\u4e94\uff0c\u591a\u8c22\u5144\u53f0\u76f8\u6551\u4e4b\u601d\uff0c\u8bf7\u95ee\u6709\u4f55\u89c1\u6559\uff1f\u201d\u540e\u6765\u7684\u90a3 \u4e2a\u767d\u8863\u5c11\u5e74\u7709\u5934\u4e00\u76b1\uff0c\u65cb\u53c8\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u72ec\u81c2\u795e\u5c3c\u95e8\u89c4\u7d20\u4e25\uff0c\u4f60\u6df1\u591c\u4e0a\u8fd9\u6e14\u821f\uff0c\u610f\u6b32\u4f55 \u4e3a\uff1f\u201d\u767d\u6cf0\u5b98\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u51fa\u624b\u76f8\u6551\uff0c\u6211\u9886\u4f60\u7684\u6069\u60c5\uff0c\u53ea\u662f\u9664\u6389\u6211\u7684\u5e08\u5085\u4e0e\u540c\u95e8\u4e4b\u5916\uff0c\u4e0d \u8bba\u90a3\u8def\u82f1\u96c4\u90fd\u4e0d\u80fd\u62ac\u51fa\u95e8\u89c4\u538b\u6211\uff01\u6211\u767d\u6cf0\u5b98\u662f\u9876\u5929\u7acb\u5730\u7684\u6c49\u5b50\uff0c\u51fa\u9053\u4ee5\u6765\uff0c\u5dee\u5e78\u8fd8\u6ca1\u6709\u8c01\u7591 \u5fc3\u8fc7\u6211\u4e3a\u975e\u4f5c\u6b79\uff01\u201d\u5510\u6653\u6f9c\u5fcd\u4e0d\u4f4f\u5728\u65c1\u8fb9\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u91c7\u82b1\u6848\u4ef6\u4e0d\u662f\u4f60\u5e72\u7684\u5417\uff1f\u201d\u201c\u4ec0 \u4e48\uff1f\u91c7\u82b1\uff1f\u201d\u767d\u6cf0\u5b98\u54c8\u54c8\u5927\u7b11\uff0c\u6307\u7740\u90a3\u6e14\u821f\u7684\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u4f60\u95ee\u5979\u53bb\uff01\u5979\u662f\u6211\u672a\u5a5a\u59bb\u5b50\uff01\u201d \u3000\u3000\u6e14\u821f\u4e2d\u7684\u5c11\u5973\u6b62\u4e86\u557c\u6ce3\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5bb6\u4e8b\u7ea0\u7eb7\uff0c\u60ca\u52a8\u5404\u4f4d\u82f1\u96c4\uff0c\u5341\u5206\u4e0d\u5b89\u3002\u201d \u3000\u3000\u8001\u6e14\u592b\u5c06\u5979\u4e00\u63a8\uff0c\u8bf4\u9053\uff1a\u201c\u56de\u8231\u4e2d\u53bb\uff01\u201d\u540e\u6765\u7684\u767d\u8863\u5c11\u5e74\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u5979\u679c \u7136\u662f\u4f60\u7684\u672a\u5a5a\u59bb\u5b50\uff0c\u90a3\u600e\u4e48\u5cb3\u4e08\u5973\u5a7f\u52a8\u5200\u52a8\u67aa\u5e72\u561b\uff1f\u201d\u8001\u6a35\u592b\u7ef7\u7d27\u7740\u8138\uff0c\u767d\u6cf0\u5b98\u4e5f\u95ed\u53e3\u4e0d \u8a00\uff0c\u90a3\u767d\u8863\u5c11\u5e74\u9762\u8272\u4e00\u7aef\uff0c\u53c8\u5bf9\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u6211\u95fb\u5f97\u72ec\u81c2\u795e\u5c3c\u7684\u5f92\u5f1f\u5728\u827a\u6210\u51fa\u5e08\u4e4b\u65e5\uff0c\u5fc5\u5728 \u795e\u5ea7\u4e4b\u524d\u656c\u9886\u6559\u6761\uff0c\u8bf7\u95ee\u767d\u5144\uff0c\u7b2c\u516b\u6761\u8bf4\u7684\u662f\u4ec0\u4e48\uff1f\u201d\u767d\u6cf0\u5b98\u4e00\u6123\uff0c\u90a3\u7b2c\u516b\u6761\u8bf4\u7684\u662f\uff1a\u540d\u626c \u4e4b\u540e\uff0c\u6212\u4e4b\u5728\u50b2\uff01\u5207\u4e0d\u53ef\u8bef\u4ee5\u4e3a\u6c14\u9aa8\u81ea\u6301\uff0c\u5373\u662f\u50b2\u6162\uff01\u5fc3\u60f3\uff1a\u8fd9\u5c11\u5e74\u600e\u4f1a\u77e5\u9053\u6211\u5e08\u7684\u6212\u6761\u3002 \u83ab\u975e\u4ed6\u662f\u6211\u7684\u540c\u95e8\u3002\u4f46\u6211\u51fa\u5e08\u4e4b\u540e\uff0c\u636e\u95fb\u5e08\u5085\u53ea\u6536\u4e86\u4e00\u4e2a\u5973\u5b50\uff0c\u4e43\u6d59\u4e1c\u5927\u5112\u5415\u7559\u826f\u7684\u5b59\u5973\uff0c \u540d\u53eb\u5415\u83b9\uff0c\u5c0f\u5b57\u56db\u5a18\uff0c\u8fd9\u51e0\u5e74\u6765\u5728\u6c5f\u6e56\u4e0a\u95ef\u8d77\u540d\u5934\uff0c\u53ea\u6709\u5979\u6211\u672a\u89c1\u8fc7\u3002\u5176\u4f59\u516d\u540d\u7537\u540c\u95e8\uff0c\u6211 \u90fd\u719f\u6089\uff0c\u53ef\u6ca1\u6709\u4ed6\uff01\u96be\u9053\u4ed6\u662f\u6211\u5e08\u5085\u65c1\u652f\uff0c\u4f46\u6211\u5e08\u5085\u4e00\u8f88\uff0c\u53ef\u6ca1\u6709\u540c\u95e8\u5440\uff01\u5fc3\u4e2d\u7591\u60d1\uff0c\u60f3 \u9053\uff1a\u83ab\u975e\u4ed6\u662f\u54ea\u4f4d\u524d\u8f88\u7684\u9ad8\u8db3\uff0c\u4e0e\u6211\u5e08\u5085\u719f\u8bc6\u7684\uff1f \u3000\u3000\u72ec\u81c2\u795e\u5c3c\u5728\u5415\u56db\u5a18\u4e4b\u5148\uff0c\u6536\u6709\u4e03\u4e2a\u7537\u5f92\uff0c\u5934\u4e00\u4f4d\u662f\u4e86\u56e0\u548c\u5c1a\uff0c\u4ee5\u4e0b\u6309\u6b21\u5e8f\u662f\uff1a\u5468\u6e05\u3001\u8def \u6c11\u77bb\u3001\u66f9\u4ec1\u7236\u3001\u767d\u6cf0\u5b98\u3001\u674e\u6e90\u3001\u7518\u51e4\u6c60\u3002\u5415\u56db\u5a18\u5165\u95e8\u65f6\uff0c\u53ea\u6709\u7518\u51e4\u6c60\u8fd8\u672a\u51fa\u5e08\uff0c\u5176\u4ed6\u7684\u90fd\u5df2 \u72ec\u81ea\u95ef\u51fa\u53bb\u4e86\u3002\u6240\u4ee5\u53ea\u6709\u7518\u51e4\u6c60\u8ba4\u5f97\u5415\u56db\u5a18\u3002\u7518\u51e4\u6c60\u5728\u5415\u56db\u5a18\u5165\u95e8\u4e4b\u540e\u4e09\u5e74\u51fa\u5e08\u3002\u548c\u4e86\u56e0\u7b49 \u53f7\u79f0\u6c5f\u5357\u4e03\u4fa0\uff0c\u4e03\u4fa0\u4e2d\u4ee5\u4e86\u56e0\u6b66\u529f\u6700\u9ad8\uff0c\u7518\u51e4\u6c60\u5a01\u540d\u6700\u76db\uff0c\u767d\u6cf0\u5b98\u7684\u6b66\u529f\u6b21\u4e8e\u4e86\u56e0\u548c\u7518\u51e4 \u6c60\uff0c\u5728\u4e03\u4fa0\u4e2d\u4e5f\u662f\u9f0e\u9f0e\u6709\u540d\u3002\u5728\u6c5f\u5357\u95ef\u8361\u4ee5\u6765\uff0c\u4e00\u5411\u672a\u9047\u5bf9\u624b\uff0c\u4ed6\u53c8\u751f\u6210\u98ce\u6d41\u501c\u50a5\uff0c\u653e\u6d6a\u5f62 \u9ab8\uff0c\u4fe8\u7136\u7fe9\u7fe9\u6d4a\u4e16\u7684\u4f73\u516c\u5b50\uff0c\u56e0\u6b64\u517b\u6210\u4e00\u526f\u50b2\u6c14\u3002\u800c\u4eca\u7ed9\u8fd9\u767d\u8863\u5c11\u5e74\u6b63\u5bb9\u4e00\u95ee\uff0c\u53c8\u773c\u89c1\u8fd9\u4eba \u6b66\u529f\uff0c\u8fdc\u5728\u5df1\u4e0a\uff0c\u4e0d\u89c9\u6c14\u9981\uff0c\u5f53\u4e0b\u4e5f\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u8c28\u9886\u5144\u53f0\u660e\u6559\uff01\u6562\u95ee\u9ad8\u59d3\u5927\u540d\u3002\u201d\u540e\u6765\u7684 \u8fd9\u767d\u8863\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u662f\u4ec0\u4e48\u9876\u5929\u7acb\u5730\u7684\u6c49\u5b50\uff0c\u6211\u59d3\u674e\uff0c\u540d\u53eb\u53cc\u53cc\u3002\u201d\u5510\u6653\u6f9c\u5728\u65c1\u8fb9\u542c \u5f97\u4e24\u4eba\u5bf9\u7b54\u3002\u53c8\u89c9\u5f97\u540e\u6765\u7684\u8fd9\u4f4d\u767d\u8863\u5c11\u5e74\u58f0\u97f3\u597d\u719f\uff0c\u597d\u50cf\u5728\u4ec0\u4e48\u5730\u65b9\u542c\u8fc7\u4f3c\u7684\uff01\u542c\u4ed6\u62a5\u51fa\u59d3 \u540d\uff0c\u751a\u4f3c\u5973\u5b50\u3002\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u5230\uff1a\u83ab\u975e\u4ed6\u662f\u5415\u56db\u5a18\u5973\u626e\u7537\u88c5\u3002\u4f46\u89c1\u8fd9\u5c11\u5e74\u6c14\u5b87\u8f69\u6602\uff0c\u82f1\u6c14\u903c \u4eba\u3002\u4e0d\u89c9\u53c8\u6697\u7b11\u81ea\u5df1\u60f3\u5f97\u592a\u75f4\uff0c\u80e1\u4e71\u731c\u6d4b\u3002 \u3000\u3000\u767d\u6cf0\u5b98\u548c\u90a3\u8001\u6e14\u592b\u4e5f\u6014\u4e86\u4e00\u6014\uff0c\u674e\u53cc\u53cc\u7684\u540d\u5b57\u53ef\u4ece\u6765\u6ca1\u6709\u542c\u8fc7\u3002\u674e\u53cc\u53cc\u53c8\u9053\uff1a\u201c\u6309\u8bf4\u767d \u5144\u548c\u8fd9\u4f4d\u8001\u4e08\u7684\u5bb6\u4e8b\uff0c\u522b\u4eba\u53ef\u4e0d\u4fbf\u5e72\u6d89\uff0c\u4f46\u4f3c\u9002\u624d\u90a3\u6837\u6027\u547d\u76f8\u6251\uff0c\u7a0d\u4e00\u4e0d\u614e\uff0c\u5c82\u4e0d\u4f24\u4e86\u4e24\u4f4d \u82f1\u96c4\uff1f\u4f55\u51b5\u53c8\u662f\u7fc1\u5a7f\uff01\u201d\u767d\u6cf0\u5b98\u5411\u5510\u6653\u6f9c\u4e00\u6307\uff0c\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u662f\u8fd9\u4f4d\u5c0f\u54e5\u6a2a\u91cc\u6253\u62b1\u4e0d\u5e73\uff0c\u6211 \u53ef\u6ca1\u6709\u52a8\u624b\u3002\u201d\u4ed6\u6ca1\u6709\u7275\u53ca\u8001\u4e08\uff0c\u4f46\u90a3\u8001\u6e14\u592b\u5df2\u542c\u51fa\u8bdd\u4e2d\u6709\u523a\uff0c\u54b3\u4e86\u4e00\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7236\u5973 \u4e0e\u767d\u5927\u82f1\u96c4\u4e4b\u95f4\uff0c\u6709\u70b9\u5c0f\u5c0f\u8fc7\u8282\uff0c\u65e2\u674e\u5144\u51fa\u5934\u76f8\u529d\uff0c\u90a3\u4fbf\u8bf7\u660e\u65e5\u5230\u6211\u5bb6\u4e2d\uff0c\u676f\u9152\u76f8\u805a\uff01\u201d\u674e \u53cc\u53cc\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u8bf7\u95ee\u8001\u524d\u8f88\u5bb6\u5c45\u4f55\u5904\uff1f\u201d\u8001\u6e14\u592b\u50b2\u7136\u7a81\u9053\uff1a\u201c\u5c31\u5728\u7530\u6a2a\u5c9b\u4e0a\uff01\u201d \u3000\u3000\u674e\u53cc\u53cc\u609a\u7136\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4e0d\u6562\u52a8\u95ee\u8001\u82f1\u96c4\u59d3\u6c0f\uff1f\u201d \u3000\u3000\u8001\u6e14\u592b\u9053\uff1a\u201c\u6211\u6253\u9c7c\u4e3a\u751f\uff0c\u59d3\u540d\u65e9\u5df2\u5fd8\u8bb0\u4e86\uff01\u201d \u3000\u3000\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u6211\u7684\u5cb3\u4e08\u4fbf\u662f\u540d\u9707\u6c5f\u6e56\u7684\u9c7c\u4eae\u5927\u738b\u3002\u201d \u3000\u3000\u674e\u53cc\u53cc\u9053\uff1a\u201c\u4e45\u4ef0\uff0c\u4e45\u4ef0\uff01\u9c7c\u8001\u65e2\u7136\u4e0b\u7ea6\uff0c\u6562\u4e0d\u656c\u966a\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u5374\u4e0d\u77e5\u9c7c\u4eae\u662f\u4ec0\u4e48\u4eba\u7269\u3002\u8001\u6e14\u592b\u4e00\u7b11\u53c8\u5bf9\u5510\u6653\u6f9c\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u54e5\u4e5f\u4e00\u5e76\u8bf7\u4e86\u3002 \u54b3\uff0c\u6211\u5e74\u8001\u7cca\u6d82\uff0c\u8fd8\u672a\u8bf7\u6559\u4f60\u7684\u5e08\u627f\u59d3\u6c0f\u5462\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u9053\u4e86\u59d3\u540d\uff0c\u9053\uff1a\u201c\u6211\u7684\u5e08\u5085\u662f\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\u3002\u201d \u3000\u3000\u674e\u53cc\u53cc\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u9c7c\u4eae\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6768\u4ef2\u82f1\u53ef\u6ca1\u6709\u90a3\u6837\u597d\u7684\u5251\u6cd5\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u4e0d\u77e5\u6240\u7b54\uff0c\u674e\u53cc\u53cc\u9053\uff1a\u201c\u8ffd\u98ce\u5251\u6cd5\u4f20\u81f3\u4e2d\u539f\uff0c\u672a\u6210\u7edd\u54cd\uff0c\u4e5f\u662f\u4ef6\u5927\u5e78\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u539f\u6765\u662f\u5929\u5c71\u6d3e\u7684\u5251\u6cd5\uff0c\u602a\u4e0d\u5f97\u5982\u6b64\u51cc\u5389\uff0c\u8981\u662f\u5510\u5144\u5f1f\u518d\u591a\u4e24\u5e74\u529f\u529b\uff0c\u4eca\u665a \u6211\u7684\u8eab\u4e0a\u6015\u4e0d\u5e73\u6dfb\u51e0\u4e2a\u7a9f\u9686\uff01\u201d\u5510\u6653\u6f9c\u9762\u4e0a\u53d1\u70e7\uff0c\u767d\u6cf0\u5b98\u5374\u4f3c\u6beb\u4e0d\u4ecb\u610f\uff0c\u7275\u7740\u4ed6\u7684\u624b\u54c8\u54c8\u5927 \u7b11\u3002 \u3000\u3000\u674e\u53cc\u53cc\u9053\uff1a\u201c\u65f6\u5019\u4e0d\u65e9\uff0c\u6211\u8981\u8d70\u4e86\u3002\u201d \u3000\u3000\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u6211\u966a\u4f60\u4e00\u540c\u8d70\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u4e5f\u8ddf\u7740\u544a\u8f9e\uff0c\u9c7c\u4eae\u5411\u674e\u53cc\u53cc\u62f1\u62f1\u624b\u9053\uff1a\u201c\u660e\u65e5\u76f8\u4f1a\u3002\u6cf0\u5b98\u4f60\u4eca\u665a\u53ef\u8981\u597d\u597d\u601d\u91cf\uff0c \u6253\u5b9a\u4e3b\u610f\u3002\u201d\u4e09\u4eba\u4e0a\u5cb8\u4e4b\u540e\uff0c\u767d\u6cf0\u5b98\u5ffd\u9053\uff1a\u201c\u674e\u5144\u5f1f\uff0c\u5510\u5144\u5f1f\uff0c\u660e\u665a\u4e4b\u4f1a\uff0c\u6211\u770b\u4f60\u4eec\u8fd8\u662f\u4e0d \u8981\u53bb\u5427\uff01\u201d\u674e\u53cc\u53cc\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u767d\u5144\u5f1f\u4e0d\u613f\u6211\u4eec\u5377\u5165\u4f60\u5bb6\u4e2d\u79c1\u4e8b\u4e48\uff1f\u201d\u767d\u6cf0\u5b98\u5fd9\u9053\uff1a\u201c\u4e0d\u662f \u8fd9\u4e2a\u610f\u601d\uff0c\u6211\u5cb3\u4e08\u6b64\u4f1a\uff0c\u53ea\u6015\u4e0d\u6000\u597d\u610f\uff0c\u674e\u5144\u65e2\u7136\u809d\u80c6\u76f8\u7167\uff0c\u5510\u5144\u4e5f\u662f\u4e00\u89c1\u5982\u6545\uff0c\u6211\u7684\u4e8b\u5c11 \u4e0d\u5f97\u5bf9\u4f60\u4eec\u8bf4\u4e2a\u660e\u767d\uff0c\u6765\uff0c\u54b1\u4eec\u501f\u4e2a\u5730\u65b9\u8c08\u4e00\u8c08\u3002\u201d\u5728\u6d77\u6ee8\u89c5\u5730\u5750\u4e0b\uff0c\u5ffd\u5730\u957f\u5401\u4e00\u58f0\uff0c\u53f9 \u9053\uff1a\u201c\u60c5\u5b7d\u7275\u7f20\uff0c\u65e0\u7531\u81ea\u9175\uff0c\u8bf4\u6765\u89c1\u7b11\u3002\u201d\u987f\u4e86\u4e00\u987f\u53c8\u9053\uff1a\u201c\u4f60\u9053\u6211\u7684\u5cb3\u7236\u662f\u4f55\u7b49\u4eba\u7269\uff1f\u201d \u3000\u3000\u674e\u53cc\u53cc\u9053\uff1a\u201c\u6a2a\u884c\u6d77\u4e0a\u7684\u5927\u76d7\uff0c\u4e94\u6e56\u56db\u6d77\u90fd\u6709\u4ed6\u7684\u515a\u7fbd\uff0c\u662f\u5417\uff1f\u201d\u767d\u6cf0\u5b98\u70b9\u4e86\u70b9\u5934\uff0c\u5f53 \u4e0b\u8bf4\u51fa\u4e00\u6bb5\u5f80\u4e8b\u3002 \u3000\u3000\u4e09\u5e74\u4e4b\u524d\uff0c\u767d\u6cf0\u5b98\u529b\u670d\u9ec4\u6cb3\u4e94\u9738\uff0c\u5a01\u540d\u6781\u76db\uff0c\u4e00\u65e5\u5357\u6e38\u592a\u6e56\uff0c\u78b0\u89c1\u9c7c\u4eae\u7684\u5973\u513f\uff0c\u4e00\u89c1\u503e \u5fc3\uff0c\u767d\u6cf0\u5b98\u6b66\u529f\u65e2\u9ad8\uff0c\u4eba\u53c8\u4fca\u7f8e\uff0c\u51e0\u5e74\u6765\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u7ed9\u4ed6\u4f5c\u5a92\uff0c\u4ed6\u603b\u662f\u65e0\u6240\u5f53\u610f\uff0c\u4f46\u81ea\u9047 \u89c1\u9c7c\u4eae\u7684\u5973\u513f\u4e4b\u540e\uff0c\u5374\u9b42\u8426\u68a6\u7275\uff0c\u518d\u4e5f\u6446\u8131\u4e0d\u4e86\u3002\u8bf4\u5230\u6b64\u5904\uff0c\u5510\u6653\u6f9c\u597d\u5947\u95ee\u9053\uff1a\u201c\u90a3\u767d\u5144\u78b0 \u89c1\u5979\u65f6\uff0c\u4e00\u5b9a\u6709\u6bb5\u5947\u9047\u4e86\uff0c\u8981\u4e0d\u7136\u53ea\u51ed\u8c8c\u7f8e\uff0c\u4e5f\u4e0d\u80fd\u4ee4\u767d\u5144\u8fd9\u6837\u503e\u5fc3\u3002\u201d\u767d\u6cf0\u5b98\u629a\u638c\u7b11\u9053\uff1a \u201c\u5510\u5144\u5f1f\u5e74\u7eaa\u867d\u5c0f\uff0c\u5bf9\u7537\u5973\u4e4b\u60c5\u5374\u662f\u4f53\u4f1a\u6781\u6df1\uff0c\u60f3\u5fc5\u662f\u8fc7\u6765\u4eba\u4e86\u3002\u8bf4\u6765\u4e5f\u4e0d\u7b97\u600e\u6837\u5947\u9047\uff0c\u6211 \u6253\u8d25\u4e86\u9ec4\u6cb3\u4e94\u9738\u4e4b\u540e\uff0c\u5374\u4e0d\u77e5\u4ed6\u4eec\u5c31\u662f\u9c7c\u4eae\u7684\u90e8\u4e0b\uff01\u6beb\u4e0d\u5728\u610f\uff0c\u5230\u4e86\u592a\u6e56\uff0c\u6b63\u662f\u9c7c\u4eae\u7684\u7b2c\u4e8c \u5de2\u7a74\uff0c\u9c7c\u4eae\u6d3e\u4eba\u6349\u6211\uff0c\u6fc0\u6218\u4e4b\u4e0b\uff0c\u6211\u628a\u4ed6\u6d3e\u6765\u7684\u516b\u4e2a\u9ad8\u624b\u6253\u4f24\u4e00\u534a\uff0c\u81ea\u5df1\u4e5f\u53d7\u4e86\u91cd\u4f24\uff0c\u4e0d\u652f \u8d25\u8d70\uff0c\u6b63\u5728\u5371\u6025\uff0c\u9c7c\u4eae\u7684\u5973\u513f\u73b0\u8eab\u51fa\u6765\uff0c\u5c06\u4ed6\u4eec\u559d\u6b62\uff0c\u628a\u6211\u653e\u4e86\u3002\u636e\u540e\u6765\u63a5\u8fd1\u9c7c\u4eae\u7684\u670b\u53cb\u4f20 \u8a00\uff0c\u8bf4\u662f\u5979\u656c\u91cd\u6211\u7684\u6b66\u529f\uff0c\u4e0d\u540c\u610f\u5979\u7236\u4eb2\u6240\u4e3a\uff0c\u6240\u4ee5\u6551\u6211\u4e00\u547d\u3002\u201d\u8bf4\u5b8c\u4e4b\u540e\uff0c\u5f39\u6307\u592a\u606f\uff0c\u6d77 \u4e0a\u6ce2\u6d9b\u5927\u4f5c\uff0c\u5510\u6653\u6f9c\u542c\u5f97\u51fa\u795e\uff0c\u4e5f\u4e0d\u7981\u201c\u566b\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u674e\u53cc\u53cc\u7b11\u9053\uff1a\u201c\u5510\u5144\u597d\u50cf\u5f88\u6709\u5fc3\u4e8b\uff01\u201d\u8bed\u58f0\u67d4\u5a9a\uff0c\u5510\u6653\u6f9c\u5fc3\u4e2d\u4e00\u8361\uff0c\u8fd9\u58f0\u97f3\u592a\u50cf\u5415\u56db\u5a18 \u4e86\uff0c\u83ab\u4e0d\u6210\u4ed6\u662f\u5415\u56db\u5a18\u5144\u5f1f\u3002\u89c1\u4e8c\u4eba\u6ce8\u610f\u81ea\u5df1\uff0c\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u674e\u5144\u4e0d\u8981\u6253\u5c94\uff0c\u8bf7\u767d\u5144\u518d\u8bf4\u4e0b \u53bb\u3002\u201d \u3000\u3000\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u4e8b\u60c5\u8fc7\u540e\uff0c\u6211\u4e5f\u6253\u542c\u51fa\u6765\u5979\u5c31\u662f\u9c7c\u4eae\u7684\u5973\u513f\u9c7c\u5a18\uff0c\u6211\u60f3\u9c7c\u4eae\u867d\u7136\u662f\u4e2a\u6d77\u4e0a \u5927\u76d7\uff0c\u52ab\u8fc7\u4e0d\u5c11\u5ba2\u5546\uff0c\u4f46\u82e5\u80af\u6539\u90aa\u5f52\u6b63\uff0c\u5bf9\u6062\u590d\u6c49\u65cf\u6cb3\u5c71\uff0c\u53ef\u662f\u5927\u6709\u52a9\u529b\u3002\u518d\u8bf4\u90a3\u4e2a\u6d77\u76d7\u6ca1 \u6740\u8fc7\u4eba\u5462\uff1f\u4ed6\u4e5f\u8fd8\u4e0d\u7b97\u662f\u5341\u6076\u4e0d\u8d66\u7684\u5bb6\u4f19\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e5f\u8fd8\u6709\u4fa0\u76d7\u4e4b\u540d\u3002\u56e0\u6b64\u6211\u5c31\u5355\u9a91\u62dc\u5c71\uff0c \u4eb2\u81ea\u5230\u592a\u6e56\u53bb\u627e\u4ed6\uff0c\u8ff3\u9053\u6765\u610f\uff0c\u60f3\u5a36\u4ed6\u7684\u5973\u513f\u3002\u4ed6\u89c1\u6211\u5982\u6b64\u80c6\u5927\uff0c\u9887\u51fa\u610f\u5916\uff0c\u5f53\u4e0b\u9080\u6211\u6bd4\u8bd5 \u6b66\u529f\uff0c\u6bd4\u8bd5\u534a\u5929\uff0c\u6253\u6210\u5e73\u624b\u3002\u4ed6\u56de\u540e\u5802\u4e00\u95ee\u5973\u513f\uff0c\u5973\u513f\u4e5f\u7b54\u5e94\u4e86\u3002\u5f53\u5929\u5c31\u8ba2\u4e0b\u4e86\u8fd9\u95e8\u4eb2 \u4e8b\uff01\u201d\u674e\u53cc\u53cc\u9053\uff1a\u201c\u90a3\u5973\u5b69\u5b50\u5f88\u4e0d\u9519\u5440\uff01\u201d\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u8c01\u8bf4\u5979\u9519\u54df\uff1f\u9519\u7684\u662f\u6211\u7684\u5cb3\u4e08\u3002\u4ed6 \u505a\u6d77\u76d7\u505a\u5f97\u597d\u597d\u7684\uff0c\u4e0d\u6599\u5374\u53d7\u4e86\u4ec0\u4e48\u56db\u7687\u5b50\u7684\u8bf1\u60d1\uff0c\u8981\u4ed6\u6276\u52a9\u767b\u57fa\uff0c\u4e8b\u6210\u4e4b\u540e\uff0c\u628a\u5c71\u4e1c\u5272\u7ed9 \u4ed6\uff0c\u8ba9\u4ed6\u5728\u6d77\u4e0a\u79f0\u738b\uff0c\u517c\u505a\u5c71\u4e1c\u603b\u7763\uff0c\u53ea\u8981\u6765\u671d\uff0c\u4e0d\u987b\u7eb3\u7a0e\u3002\u6211\u7684\u5cb3\u4e08\u5229\u7984\u718f\u5fc3\uff0c\u7adf\u7b54\u5e94 \u4e86\u3002\u6211\u5c61\u529d\u4e0d\u542c\uff0c\u7ec8\u800c\u5212\u5730\u7edd\u4ea4\uff0c\u65ad\u4e86\u5cb3\u5a7f\u60c5\u4efd\uff01\u90a3\u9c7c\u5a18\u5bf9\u6211\u5012\u662f\u6df1\u60c5\u4e00\u7247\uff0c\u6258\u4eba\u5e26\u8bdd\u7ed9 \u6211\uff0c\u8bf4\u662f\u82e5\u7236\u4eb2\u59cb\u7ec8\u4e0d\u8bb8\uff0c\u5979\u5c31\u7ec8\u8eab\u4e0d\u5ac1\u3002\u6240\u4ee5\u6211\u53c8\u4e07\u91cc\u8fdc\u6765\uff0c\u60f3\u5230\u7530\u6a2a\u5c9b\u518d\u627e\u5cb3\u4e08\u7406\u8bba\uff0c \u4e0d\u6599\u5df2\u5148\u5728\u6b64\u5904\u9047\u89c1\u3002\u201d\u767d\u6cf0\u5b98\u4e0d\u77e5\uff0c\u4ed6\u8fdc\u6765\u4e4b\u4e8b\uff0c\u9c7c\u4eae\u65e9\u5df2\u77e5\u9053\uff0c\u539f\u610f\u662f\u7b49\u4ed6\u5230\u7530\u6a2a\u5c9b\u4e0a \u518d\u903c\u4ed6\u5f52\u987a\u7684\uff0c\u9c7c\u5a18\u6df1\u77e5\u767d\u6cf0\u5bab\u6027\u683c\uff0c\u6015\u4e8b\u60c5\u95f9\u50f5\uff0c\u9c7c\u4eae\u624b\u4e0b\u68a6\u60f3\u4ece\u9f99\u4e4b\u8f88\u4e0d\u653e\u4ed6\u8d70\uff0c\u90a3\u65f6 \u5c31\u662f\u5979\u51fa\u5934\u5e87\u62a4\uff0c\u4e5f\u6551\u4e0d\u4e86\uff0c\u6240\u4ee5\u575a\u6301\u8981\u4eb2\u5230\u6d77\u8fb9\u6765\u622a\u4ed6\u3002 \u3000\u3000\u674e\u53cc\u53cc\u542c\u767d\u6cf0\u5b98\u8bf4\u5b8c\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u767d\u83b2\u51fa\u4e8e\u6de4\u6ce5\uff0c\u82b3\u5170\u51fa\u4e8e\u5e7d\u8c37\u3002\u6211\u6700\u7231\u7ba1\u4eba\u95f2\u4e8b\uff0c \u767d\u5144\u7684\u672a\u5a5a\u59bb\u5b50\u65e2\u7136\u8fd9\u6837\u53ef\u656c\uff0c\u6211\u4e00\u5b9a\u5c3d\u529b\u52a9\u4f60\u3002\u660e\u665a\u4e4b\u4f1a\uff0c\u975e\u53bb\u4e0d\u53ef\uff01\u201d\u767d\u6cf0\u5b98\u4e00\u9635\u6c89 \u541f\uff0c\u674e\u53cc\u53cc\u53c8\u9053\uff1a\u201c\u800c\u4e14\u51ed\u6c5f\u5357\u516b\u4fa0\u7684\u5a01\u540d\uff0c\u54ea\u6709\u4e34\u9635\u9000\u7f29\u4e4b\u7406\uff01\u201d\u767d\u6cf0\u5b98\u6115\u7136\u770b\u4ed6\uff0c\u674e\u53cc \u53cc\u7b11\u9053\uff1a\u201c\u6211\u662f\u8bf4\u4f60\uff0c\u6211\u548c\u6c5f\u5357\u516b\u4fa0\u4ea4\u4e0a\u670b\u53cb\uff0c\u54ea\u53ef\u793a\u5f31\u4e8e\u4eba\uff01\u201d\u767d\u6cf0\u5b98\u4f3c\u60f3\u8d77\u4ec0\u4e48\u4f3c\u7684\uff0c \u5ffd\u7136\u95ee\u9053\uff1a\u201c\u6211\u5cb3\u4e08\u6b66\u529f\u975e\u540c\u5c0f\u53ef\uff0c\u674e\u5144\u521a\u624d\u89c1\u9762\u4e00\u62db\uff0c\u5c31\u628a\u4ed6\u624b\u4e2d\u5b9d\u5200\u593a\u53bb\uff0c\u771f\u662f\u795e\u6280\u60ca \u4eba\u3002\u4e0d\u77e5\u674e\u5144\u7528\u7684\u662f\u54ea\u4e00\u6d3e\u7684\u9ad8\u62db\uff0c\u53ef\u4ee5\u8bf4\u51fa\u6765\u7ed9\u6211\u4eec\u89c1\u8bc6\u89c1\u8bc6\u5417\uff1f\u201d \u3000\u3000\u674e\u53cc\u53cc\u9053\uff1a\u201c\u767d\u5144\uff0c\u4f60\u4e5f\u5b66\u8fc7\uff0c\u4f55\u5fc5\u95ee\u6211\uff1f\u201d \u3000\u3000\u767d\u6cf0\u5b98\u66f4\u662f\u60ca\u6115\uff0c\u6b63\u60f3\u9053\uff1a\u201c\u674e\u5144\u4e0d\u5fc5\u53d6\u7b11\u3002\u201d \u3000\u3000\u674e\u53cc\u53cc\u5df2\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u7a7a\u624b\u767d\u5203\u7684\u529f\u592b\uff0c\u4f60\u4e0d\u662f\u5b66\u5f97\u6781\u4e3a\u7eaf\u719f\u5417\uff1f\u4f60\u6240\u5b66\u7684\u7b2c\u4e09\u5341\u516d\u62db \u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u661f\u6d77\u6d6e\u6413\uff01\u201d\u674e\u53cc\u53cc\u9053\uff1a\u201c\u8fd9\u4e0d\u5c31\u662f\u4e86\uff01\u4e0d\u8fc7\u6211\u7528\u5f97\u5feb\u4e00\u4e9b\uff0c\u53c8\u662f\u4e58\u5176\u4e0d \u610f\uff0c\u653b\u5176\u65e0\u5907\uff0c\u6240\u4ee5\u624d\u80fd\u4e00\u51fb\u594f\u529f\uff01\u201d \u3000\u3000\u767d\u6cf0\u5b98\u53eb\u8d77\u6765\u9053\uff1a\u201c\u674e\u5144\uff0c\u4f60\u600e\u4e48\u8fd9\u6837\u7cbe\u719f\u6211\u6d3e\u6b66\u529f\u7684\u79d8\u5965\uff01\u201d \u3000\u3000\u674e\u53cc\u53cc\u6de1\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u89c1\u8d35\u6d3e\u4e2d\u4eba\u7528\u8fc7\u3002\u201d\u767d\u6cf0\u5b98\u81ea\u601d\uff1a\u540c\u95e8\u4e2d\u6709\u6b64\u529f\u529b\u7684\u53ea\u6709 \u4e86\u56e0\u5e08\u5144\uff0c\u4e86\u56e0\u5e08\u5144\u7684\u624b\u6cd5\u4e5f\u672a\u5fc5\u6709\u8fd9\u6837\u5feb\u6377\u3002\u82e5\u662f\u4ed6\u5077\u5b66\u6765\u7684\uff0c\u65e0\u8bba\u5982\u4f55\u6ca1\u6709\u8fd9\u6837\u9020\u8be3\uff0c \u767e\u601d\u4e0d\u89e3\uff0c\u7591\u56e2\u6df1\u853d\u3002\u5510\u6653\u6f9c\u5374\u4e0d\u77e5\u9053\u767d\u6cf0\u5b98\u4f55\u4ee5\u7a81\u7136\u4f1a\u95ee\u4ed6\u7684\u62db\u6570\u3002 \u3000\u3000\u65b0\u6708\u5982\u5f3a\uff0c\u60ac\u6302\u5929\u5fc3\uff0c\u674e\u53cc\u53cc\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u660e\u65e5\u9ec4\u660f\uff0c\u518d\u5728\u6b64\u5904\u76f8\u4f1a\uff01\u201d\u4e09\u4eba\u5206\u624b\uff0c\u5510 \u6653\u6f9c\u884c\u4e86\u51e0\u6b65\uff0c\u53c8\u81ea\u56de\u5934\uff0c\u674e\u53cc\u53cc\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u5510\u5144\u5251\u672f\u5927\u6709\u8fdb\u5883\uff01\u7530\u6a2a\u5c9b\u4e0a\uff0c\u53ef\u4fdd\u65e0 \u59a8\uff01\u201d\u5510\u6653\u6f9c\u60ca\u5f02\u56de\u8eab\uff0c\u674e\u53cc\u53cc\u7b11\u58f0\u5df2\u6e10\u8fdc\u6e10\u6773\uff01 \u3000\u3000\u5510\u6653\u6f9c\u56de\u81f3\u5ba2\u5e97\uff0c\u5e97\u4e2d\u706f\u706b\u72b9\u660e\uff0c\u5e97\u5c0f\u4e8c\u548c\u638c\u67dc\u90fd\u7aef\u5750\u5e97\u4e2d\u3002\u5019\u4ed6\u56de\u6765\u3002\u5510\u6653\u6f9c\u53c8\u662f\u4e00 \u8bb6\uff0c\u53ea\u89c1\u90a3\u638c\u67dc\u606d\u606d\u656c\u656c\u7684\u8d77\u7acb\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u77e5\u4f60\u8001\u662f\u9c7c\u4eae\u5927\u738b\u670b\u53cb\uff0c\u4e14\u6765\u6020\u6162\uff0c\u4f60\u8001\u4f11 \u602a\u3002\u201d\u9012\u8fc7\u4e00\u5f20\u8bf7\u5e16\uff0c\u7adf\u662f\u9c7c\u4eae\u5927\u738b\u6d3e\u4eba\u9001\u6765\u7684\uff0c\u5510\u6653\u6f9c\u4e5f\u4e0d\u7531\u5f97\u6697\u81ea\u60ca\u5fc3\uff1a\u8fd9\u9c7c\u4eae\u5927\u738b\u679c \u7136\u515a\u7fbd\u4f17\u591a\uff0c\u795e\u901a\u5e7f\u5927\uff0c\u53ea\u8fd9\u4e48\u4e00\u4f1a\u513f\u5de5\u592b\uff0c\u4ed6\u5df2\u6d3e\u4eba\u67e5\u51fa\u6211\u7684\u4e0b\u843d\u3002\u5f53\u4e0b\u4e5f\u4e0d\u591a\u8bf4\uff0c\u548c\u638c \u67dc\u5bd2\u55a7\u4e24\u53e5\uff0c\u4fbf\u81ea\u5165\u623f\u4f11\u606f\u3002\u8fd9\u4e24\u65e5\u6765\u602a\u4e8b\u9891\u751f\uff0c\u5510\u6653\u6f9c\u7ffb\u6765\u8986\u53bb\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u80fd\u5165\u7761\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u9ec4\u660f\uff0c\u5510\u6653\u6f9c\u4f9d\u65f6\u6765\u5230\u6d77\u6ee8\uff0c\u767d\u6cf0\u5b98\u548c\u674e\u53cc\u53cc\u5df2\u5728\u90a3\u91cc\u7b49\u5019\uff0c\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4e24\u4f4d \u4ec1\u5144\u597d\u65e9\uff01\u201d\u674e\u53cc\u53cc\u7b11\u9053\uff1a\u201c\u9c7c\u4eae\u5927\u738b\u5df2\u606d\u5019\u6211\u4eec\u9a7e\u4e34\u4e86\uff01\u201d\u64ae\u5507\u4e00\u5578\uff0c\u6d77\u4e2d\u5f00\u6765\u4e00\u53ea\u5927 \u8239\uff0c\u8239\u4e2d\u51e0\u4e2a\u6d53\u7709\u5927\u773c\u7684\u6c49\u5b50\u8eac\u8170\u76f8\u8bf7\uff0c\u5510\u6653\u6f9c\u77e5\u662f\u9c7c\u4eae\u5927\u738b\u7684\u76d7\u8239\uff0c\u4e5f\u4e0d\u60ca\u60e7\uff0c\u548c\u767d\u674e\u4e8c \u4eba\u4e0a\u4e86\u76d7\u8239\uff0c\u9022\u5411\u7530\u6a2a\u5c9b\u5f00\u53bb\u3002 \u3000\u3000\u9752\u5c9b\u5916\u7684\u9ec4\u6d77\u4e2d\u5c9b\u5c7f\u751a\u591a\uff0c\u9664\u7530\u6a2a\u5c9b\u5916\uff0c\u8fd8\u6709\u7075\u5c71\u5c9b\u3001\u626c\u5a01\u5c9b\u3001\u536b\u5c71\u5c9b\u7b49\u3002\u5927\u5c0f\u5c9b\u5c7f\uff0c \u661f\u7f57\u68cb\u5e03\u3002\u9752\u5c9b\u80cc\u4fa7\uff0c\u53c8\u662f\u5cfb\u5c71\uff0c\u8fd9\u5ea7\u5c71\u6a2a\u4e98\u6d77\u5cb8\uff0c\u4e00\u9762\u662f\u6d77\uff0c\u4e00\u9762\u662f\u5c71\uff0c\u4e07\u5d0e\u5982\u5c4f\uff0c\u5343\u5ca9 \u7ade\u79c0\uff0c\u5927\u6d77\u4e2d\u5c9b\u5c7f\u6d6e\u6c89\uff0c\u5c71\u5cf0\u9690\u7ea6\uff0c\u771f\u5982\u6d77\u4e0a\u795e\u5c71\uff0c\u886c\u7740\u70b9\u70b9\u661f\u661f\u6e14\u706b\uff0c\u66f4\u663e\u51fa\u666f\u7269\u4e4b\u5947\uff01 \u5510\u6653\u6f9c\u7684\u6e14\u821f\u5728\u6d77\u6d9b\u4e2d\u8d77\u4f0f\uff0c\u7ed5\u8fc7\u51e0\u4e2a\u5c0f\u5c9b\uff0c\u884c\u4e86\u4e2a\u591a\u65f6\u8fb0\uff0c\u821f\u4eba\u6307\u7740\u4e00\u4e2a\u6d77\u5c9b\u8bf4\u9053\uff1a\u201c\u8fd9 \u5c31\u662f\u7530\u6a2a\u5c9b\u4e86\uff01\u201d\u4e09\u4eba\u820d\u821f\u767b\u9646\uff0c\u5c9b\u4e0a\u77f3\u5c71\u77d7\u7acb\uff0c\u5f62\u5982\u5de8\u517d\u6469\u7a7a\uff0c\u5c71\u5916\u6709\u5341\u4f59\u4e08\u9ad8\u7684\u7889\u697c\u9501 \u4f4f\u5c71\u53e3\uff0c\u7889\u6805\u4e25\u95ed\uff0c\u4e24\u65c1\u780c\u7740\u4e08\u8bb8\u9ad8\u7684\u77f3\u5899\uff0c\u8fde\u5c71\u800c\u8d77\uff0c\u52bf\u5982\u957f\u9f99\uff0c\u7889\u540e\u5cf0\u5c16\u4e71\u62e5\uff0c\u53e4\u6728\u53c2 \u5929\uff0c\u9690\u9690\u542b\u6709\u8083\u6740\u4e4b\u6c14\u3002\u674e\u53cc\u53cc\u7b11\u9053\uff1a\u201c\u4ee4\u5cb3\u7ecf\u8425\u6b64\u5730\uff0c\u5927\u8d39\u5fc3\u673a\uff0c\u8fd9\u6837\u96c4\u58ee\u7684\u6c14\u52bf\uff0c\u8fd8\u771f \u975e\u6211\u610f\u6599\u6240\u53ca\u5462\uff01\u201d\u767d\u6cf0\u5b98\u4e00\u58f0\u5fae\u7b11\uff0c\u968f\u9c7c\u4eae\u6d3e\u6765\u63a5\u5f15\u7684\u4eba\u8fdb\u4e86\u7889\u6805\uff0c\u5185\u9762\u53c8\u662f\u4e00\u756a\u5929\u5730\uff0c \u5c9b\u4e0a\u5947\u82b1\u5f02\u8349\uff0c\u904d\u5730\u90fd\u662f\uff0c\u6700\u60f9\u4eba\u6ce8\u76ee\u7684\u662f\u5ca9\u8fb9\u8840\u7ea2\u7684\u5c71\u8336\u82b1\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u4e5f\u4ee4\u4eba\u8000\u773c\u751f \u7f2c\u3002\u4e09\u4eba\u8fdb\u4e86\u6805\u95e8\uff0c\u4e00\u6761\u5927\u6c49\u6325\u7740\u4ee4\u65d7\u53eb\u9053\uff1a\u201c\u5927\u738b\u8bf7\u4e09\u4f4d\u5609\u5bbe\u5230\u5343\u4e08\u5ca9\u76f8\u4f1a\u3002\u201d \u3000\u3000\u767d\u6cf0\u5b98\u9053\uff0c\u201c\u5c31\u70e6\u9999\u4e3b\u5f15\u8def\u3002\u201d\u90a3\u4eba\u624b\u4e3e\u4ee4\u724c\u3002\u6cfc\u523a\u523a\u4e00\u9a6c\u5f53\u524d\uff0c\u674e\u53cc\u53cc\u7b49\u7d27\u968f\u5728\u540e\uff0c \u56db\u4eba\u90fd\u662f\u4e00\u7b49\u6b66\u529f\uff0c\u811a\u4e0b\u98de\u5feb\uff0c\u5de6\u65cb\u53f3\u8f6c\uff0c\u4e0d\u4e00\u523b\u5df2\u6df1\u5165\u5bc6\u6797\u5e7d\u8c37\u4e4b\u4e2d\uff0c\u8fdc\u671b\u4e30\u8349\u6ca1\u80eb\uff0c\u602a \u77f3\u906e\u4e91\uff0c\u674e\u53cc\u53cc\u7a81\u9000\u540e\u4e00\u6b65\uff0c\u5728\u5510\u6653\u6f9c\u8033\u8fb9\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u7d27\u8d34\u7740\u6211\uff01\u201d\u5510\u6653\u6f9c\u4e0d\u660e\u7528\u610f\uff0c \u5ffd\u542c\u5f97\u524d\u9762\u5e26\u8def\u7684\u4eba\u8bf4\u9053\uff1a\u201c\u4e0a\u5c71\u4e86\uff0c\u5c71\u8def\u96be\u884c\uff0c\u8bf7\u5404\u4f4d\u7559\u795e\uff01\u201d\u4e00\u4f0f\u8eab\u5e73\u7a7a\u63a0\u8d77\u98de\u8d8a\u5c71 \u5761\u3002\u5510\u6653\u6f9c\u4e00\u770b\uff0c\u5c71\u5761\u4e0a\u6ee1\u662f\u8346\u68d8\uff0c\u6742\u7740\u4ed9\u4eba\u638c\u4e4b\u7c7b\u6709\u523a\u7684\u690d\u7269\uff0c\u548c\u5c71\u4e0b\u7684\u82b1\u56e2\u9526\u7c07\u5224\u82e5\u5929 \u6e0a\u3002\u5510\u6653\u6f9c\u5fc3\u60f3\uff1a\u82e5\u7528\u6e38\u9f99\u5251\u5f00\u8def\uff0c\u8fd8\u80fd\u4e0a\u53bb\uff0c\u53ea\u7528\u8f7b\u8eab\u529f\u592b\uff0c\u98de\u8d8a\u8fd9\u4e00\u5927\u7247\u8346\u68d8\uff0c\u5374\u662f\u4e07 \u4e07\u4e0d\u80fd\u3002\u770b\u524d\u9762\u5f15\u8def\u90a3\u4eba\uff0c\u8fc8\u5f00\u5927\u6b65\uff0c\u82e5\u65e0\u5176\u4e8b\uff0c\u6b63\u5728\u8e0c\u8e87\uff0c\u674e\u53cc\u53cc\u628a\u81c2\u4e00\u8d34\uff0c\u84e6\u7136\u4e00\u5e26\uff0c \u5510\u6653\u6f9c\u8eab\u5b50\u7a81\u89c9\u4e00\u8f7b\uff0c\u674e\u53cc\u53cc\u8863\u8882\u8fce\u98ce\uff0c\u8e22\u3001\u8d61\u3001\u8e1e\uff0c\u4e00\u53e3\u6c14\u98de\u51fa\u4e00\u7247\u8346\u68d8\uff0c\u5510\u6653\u6f9c\u5c31\u5982\u817e \u4e91\u9a7e\u96fe\u4e00\u822c\uff0c\u53ea\u89c9\u81ea\u5df1\u624b\u81c2\u6240\u8d34\u4e4b\u5904\uff0c\u8f6f\u7ef5\u7ef5\u9999\u99a5\u99a5\uff0c\u8eab\u5b50\u672c\u80fd\u4e00\u7f29\uff0c\u674e\u53cc\u53cc\u5df2\u5e26\u81ea\u5df1\u5230\u4e86 \u5c71\u5761\u4e4b\u4e0a\u3002\u8346\u68d8\u5916\u6709\u4e00\u6761\u7a84\u7a84\u7684\u5c0f\u5f84\uff0c\u4ece\u53e6\u5916\u65b9\u5411\u76f4\u901a\u5c71\u811a\u3002\u5e26\u8def\u7684\u4eba\u4e0d\u4ece\u5c0f\u5f84\u4e0a\u5c71\uff0c\u5206\u660e \u662f\u60f3\u8003\u91cf\u81ea\u5df1\u7684\u529f\u592b\uff0c\u82e5\u975e\u674e\u53cc\u53cc\u76f8\u52a9\uff0c\u5f53\u573a\u5c31\u8981\u51fa\u4e11\u3002\u90a3\u4eba\u5728\u5c71\u5761\u4e0a\u521a\u7acb\u5b9a\u811a\u6b65\uff0c\u56de\u5934\u4e00 \u671b\uff0c\u4e09\u4eba\u5df2\u6084\u65e0\u58f0\u7684\u7acb\u5728\u81ea\u5df1\u8eab\u540e\u3002\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5217\u4f4d\u771f\u597d\u529f\u592b\uff01\u201d\u8863\u895f\u4e00\u64a9\uff0c\u53c8\u6cbf\u5c0f\u5f84\u4e0a \u5c71\u3002 \u3000\u3000\u4e09\u4eba\u4ea6\u6b65\u4ea6\u8d8b\uff0c\u5c0f\u5f84\u8fc2\u56de\u66f2\u6298\uff0c\u8d8a\u8fc7\u51e0\u91cd\u5c97\u5ce6\uff0c\u8d70\u4e0a\u4e86\u4e00\u5ea7\u5ca9\uff0c\u5ca9\u4e0a\u957f\u677e\u95ed\u65e5\uff0c\u85e4\u8513\u5f15 \u98ce\uff0c\u767e\u9e1f\u7785\u60c6\uff0c\u5982\u9694\u5c18\u4e16\u3002\u8d70\u4e86\u4e00\u56de\uff0c\u84e6\u5730\u4e00\u5c42\u5ced\u58c1\u62d4\u5730\u800c\u8d77\uff0c\u4e0d\u4e0b\u4e8c\u4e09\u5341\u4e08\uff0c\u4ece\u9876\u81f3\u5e95\uff0c \u5929\u7136\u5982\u524a\uff0c\u6beb\u65e0\u501f\u529b\u6500\u63f4\u4e4b\u5904\u3002\u674e\u53cc\u53cc\u6697\u9053\uff1a\u201c\u82e6\u4e5f\uff01\u8fd9\u6837\u7684\u5ced\u58c1\uff0c\u6211\u548c\u6cf0\u5b98\u6216\u8005\u8fd8\u53ef\u4e0a \u53bb\uff0c\u5374\u5982\u4f55\u80fd\u518d\u5e26\u4e00\u4eba\u3002\u201d\u524d\u9762\u5f15\u8def\u7684\u4eba\u6cbf\u7740\u77f3\u5b95\u5468\u56f4\u8d70\u4e86\u4e00\u904d\uff0c\u5ffd\u89c1\u5bf9\u9762\u58c1\u4e0a\uff0c\u6709\u4e00\u5904\u5012 \u5782\u7740\u4e00\u682a\u5343\u5e74\u53e4\u677e\uff0c\u5f62\u5982\u82cd\u9f99\u6a31\u6d77\uff0c\u4e39\u51e4\u671d\u9633\uff0c\u6ee1\u6811\u5e61\u7740\u679d\u85e4\uff0c\u85e4\u68a2\u679d\u679d\u4e0b\u5782\uff0c\u53c8\u50cf\u9f99\u9aef\u51e4 \u6bdb\uff0c\u968f\u98ce\u98d8\u62c2\uff0c\u6709\u51e0\u679d\u85e4\u68a2\u76f4\u8361\u5230\u8fd9\u8fb9\u6765\u3002\u5f15\u8def\u7684\u4eba\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u4eec\u4ece\u8fd9\u91cc\u4e0a\u5343\u4e08\u5ca9\uff01\u201d \u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u6761\u8f6f\u7d22\uff0c\u7d22\u7aef\u7cfb\u6709\u94c1\u94a9\uff0c\u5411\u524d\u4e00\u629b\uff0c\u52fe\u5728\u4e3b\u677e\u6811\u4e0a\uff0c\u8eab\u5b50\u8361\u4e86\u51e0\u8361\uff0c\u4fbf\u817e\u8eab\u800c \u8d77\uff0c\u76f4\u5411\u90a3\u682a\u5d16\u677e\u98de\u53bb\u3002\u539f\u6765\u6b64\u4eba\u8f7b\u529f\u867d\u9ad8\uff0c\u5374\u4e5f\u672a\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u6240\u4ee5\u65e9\u5907\u8f6f\u7d22\uff0c\u501f\u7d22 \u98de\u8eab\u3002\u674e\u53cc\u53cc\u7b11\u4e86\u7b11\uff0c\u6307\u7740\u5e61\u677e\u7684\u91ce\u85e4\u8bf4\u9053\uff1a\u201c\u6709\u6b64\u98de\u6881\uff0c\u4e0d\u5fc5\u591a\u8d39\u6c14\u529b\uff01\u201d\u767d\u6cf0\u5b98\u7565\u4e00\u7ed3 \u675f\uff0c\u811a\u5c16\u4e00\u8d34\uff0c\u4e24\u624b\u5411\u4e0a\u4e00\u64a9\uff0c\u4fbf\u63e1\u7740\u679d\u85e4\uff0c\u5373\u8d81\u8361\u6f3e\u4e4b\u52bf\uff0c\u76f4\u4e0a\u677e\u80cc\uff01\u674e\u53cc\u53cc\u9053\uff1a\u201c\u5510\u5144 \u5f1f\uff0c\u4f60\u51c6\u5907\u597d\u4e86\uff01\u201d\u4e24\u81c2\u4e00\u5206\uff0c\u53cc\u8db3\u70b9\u5904\uff0c\u4e00\u4e2a\u65f1\u5730\u62d4\u8471\uff0c\u63e1\u7740\u98d8\u6765\u7684\u91ce\u85e4\uff0c\u7a81\u7136\u8eab\u5b50\u5012 \u8f6c\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u4f3c\u6b32\u5012\u51b2\u4e0b\u6765\uff0c\u5510\u6653\u6f9c\u4e00\u58f0\u60e8\u547c\uff0c\u674e\u53cc\u53cc\u5df2\u7b11\u76c8\u76c8\u7684\u7528\u53cc\u8db3\u5939\u7740\u91ce\u85e4\uff0c\u4e24\u81c2 \u4e0b\u5782\uff0c\u53eb\u9053\uff1a\u201c\u4e0a\u6765\uff01\u201d\u5510\u6653\u6f9c\u60ca\u5fc3\u548b\u820c\u4e4b\u4f59\uff0c\u84e6\u7136\u5fc3\u5ff5\u4e00\u52a8\uff01 \u3000\u3000\u674e\u53cc\u53cc\u7684\u8f7b\u8eab\u529f\u592b\u4fca\u6781\uff0c\u59ff\u52bf\u4e5f\u7f8e\u5999\u5f02\u5e38\u3002\u5510\u6653\u6f9c\u84e6\u7136\u60f3\u8d77\u5728\u9099\u5c71\u9047\u89c1\u5415\u56db\u5a18\u65f6\uff0c\u5979\u548c \u5173\u4e1c\u56db\u4fa0\u4e2d\u7684\u201c\u4e07\u91cc\u8ffd\u98ce\u201d\u67f3\u5148\u5f00\u8d4c\u8d5b\u8f7b\u529f\uff0c\u4e5f\u66fe\u8d1f\u7740\u81ea\u5df1\u98de\u4e0a\u5371\u5ca9\uff0c\u90a3\u4efd\u529f\u592b\uff0c\u7684\u674e\u53cc\u53cc \u7adf\u662f\u4e00\u6a21\u4e00\u6837\uff01\u5f53\u4e0b\u51cc\u7a7a\u4e00\u8dc3\uff0c\u63e1\u7740\u674e\u53cc\u53cc\u8f6f\u7ef5\u7ef5\u7684\u624b\uff0c\u98de\u4e0a\u7edd\u58c1\u3002 \u3000\u3000\u5f15\u8def\u7684\u4eba\u89c1\u4e09\u4eba\u4e0d\u7528\u8f6f\u7d22\uff0c\u98de\u4e0a\u7edd\u58c1\uff0c\u5185\u5fc3\u4f69\u670d\uff0c\u518d\u4e5f\u4e0d\u6562\u6545\u610f\u5201\u96be\uff0c\u4ece\u77f3\u5cf0\u4e0a\u51ff\u51fa\u6765 \u7684\u5c0f\u5f84\u76f4\u767b\u5ca9\u9876\u3002 \u3000\u3000\u5cad\u4e0a\u98ce\u666f\u53c8\u4e0e\u5c9b\u4e0a\u4e0d\u540c\uff0c\u4e09\u4eba\u4e0a\u5230\u5343\u4e08\u5ca9\uff0c\u987f\u89c9\u5fc3\u65f7\u795e\u6021\uff0c\u5cad\u4e0a\u4e07\u677e\u5939\u9053\uff0c\u4e18\u58d1\u795e\u5947\uff0c \u8fdc\u773a\u9ec4\u6d77\uff0c\u7a7a\u9614\u65e0\u8fb9\uff0c\u6e14\u5e06\u9690\u6ca1\uff0c\u7fe9\u5982\u767d\u7fbd\u3002\u674e\u53cc\u53cc\u4e50\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6b64\u5730\u6bd4\u96c1\u8361\u5929\u53f0\u66f4\u5177\u7a7a \u7075\u4e4b\u80dc\u3002\u201d\u96c1\u8361\u5929\u53f0\u662f\u4e2d\u56fd\u4e24\u5927\u540d\u5c71\uff0c\u767d\u6cf0\u5b98\u7b11\u9053\uff1a\u201c\u96c1\u8361\u5929\u53f0\u9ad8\u62d4\u51fa\u4e91\uff0c\u96c4\u4f1f\u7ee9\u4e3d\u517c\u800c\u6709 \u4e4b\u3002\u6b64\u5cf0\u867d\u5177\u4f73\u80dc\uff0c\u7a76\u53ea\u4e00\u5cf0\u3002\u6709\u5927\u6d77\u76f8\u886c\uff0c\u59cb\u663e\u5176\u5947\uff0c\u5230\u5e95\u5931\u4e4b\u96c4\u4f1f\u3002\u82e5\u4ee5\u4eba\u55bb\u4e4b\uff0c\u5929\u53f0 \u96c1\u8361\u6709\u5982\u540d\u5c06\uff0c\u80f8\u4e2d\u53ef\u85cf\u767e\u4e07\u7532\u5175\uff0c\u6b64\u5c71\u5219\u5982\u6c5f\u6e56\u8c6a\u5ba2\uff0c\u867d\u5fc3\u96c4\u4e07\u592b\uff0c\u7a76\u5acc\u683c\u5c40\u4e0d\u5927\u3002\u201d\u674e \u53cc\u53cc\u77e5\u4ed6\u501f\u5c71\u55bb\u4eba\uff0c\u610f\u6307\u9c7c\u4eae\uff0c\u5fae\u7b11\u4e0d\u8a00\uff0c\u5e26\u8def\u7684\u4eba\u5374\u5df2\u8272\u53d8\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u5c71\u4e0a\u8d70\u4e86\u534a\u91cc\u5149\u666f\uff0c\u5ffd\u7136\u73b0\u51fa\u4e00\u5ea7\u5927\u53a6\uff0c\u7c89\u5899\u767e\u4ede\uff0c\u5bc6\u5e03\u84ba\u85dc\uff0c\u4e2d\u95f4\u4e00\u5ea7\u95e8\u697c\uff0c \u91d1\u78a7\u8f89\u714c\uff0c\u6c14\u8c61\u4e07\u5343\uff0c\u95e8\u697c\u4e0b\u9762\uff0c\u5f00\u7740\u4e24\u6247\u5927\u94c1\u51e0\uff0c\u5de6\u53f3\u6392\u5217\u7740\u6570\u767e\u6b66\u58eb\uff0c\u5251\u621f\u5982\u6797\uff0c\u4ea4\u697c \u906e\u9053\u3002\u4e09\u4eba\u5140\u7136\u4e0d\u60e7\uff0c\u4ece\u5200\u67aa\u5251\u621f\u4e1b\u4e2d\uff0c\u76f4\u7a7f\u8fdb\u53bb\uff0c\u8d70\u8fc7\u4e00\u6761\u957f\u957f\u7684\u752c\u9053\uff0c\u6b65\u5165\u4e00\u5ea7\u82b1\u56ed\uff0c \u56ed\u5185\u5047\u5c71\u73b2\u73b0\uff0c\u56de\u5eca\u66f2\u6298\uff0c\u8fd8\u51ff\u5f15\u5c71\u6cc9\uff0c\u5e03\u7f6e\u6210\u4e00\u6240\u6c34\u6811\uff0c\u6c34\u6805\u4e0a\u5efa\u6709\u4e00\u5e7f\u9614\u7684\u4ead\u5b50\uff0c\u56db\u9762 \u73bb\u7483\u7a97\u5b50\uff0c\u5916\u9762\u904d\u690d\u5c71\u8336\uff0c\u6500\u4e0a\u5047\u5c71\u53ef\u4ee5\u773a\u671b\u9ec4\u6d77\u3002\u5510\u6653\u6f9c\u5fc3\u60f3\u8fd9\u9c7c\u4eae\u5927\u738b\u771f\u4f1a\u4eab\u53d7\uff0c\u5728\u5c9b \u4e2d\u5c71\u4e0a\u5efa\u6b64\u522b\u5885\uff0c\u771f\u4e0d\u77e5\u8981\u82b1\u591a\u5c11\u4eba\u529b\u7269\u529b\uff01 \u3000\u3000\u5e26\u8def\u7684\u4eba\u6253\u8d77\u8f6f\u5e18\uff0c\u9ad8\u58f0\u5531\u9053\uff1a\u201c\u8d35\u5bbe\u5230\uff01\u201d\u9c7c\u4eae\u5927\u738b\u5728\u91cc\u9762\u9ad8\u58f0\u9053\uff1a\u201c\u63d6\u5ba2\u5165\u5ea7\uff01\u201d \u8fd9\u65f6\u65b0\u6708\u5df2\u4e0a\u68a2\u5934\uff0c\u91cc\u9762\u5ffd\u7136\u8d70\u51fa\u4e00\u961f\u5782\u53d1\u7f8e\u5a62\uff0c\u624b\u6267\u7eb1\u706f\uff0c\u606d\u8fce\u5bbe\u5ba2\uff01 \u3000\u3000\u4e09\u4eba\u6b65\u5165\u4e86\u4ead\uff0c\u4f46\u89c9\u8000\u773c\u6b32\u82b1\uff0c\u91cc\u9762\u5b9d\u5668\u5947\u73cd\uff0c\u5546\u5f5d\u5468\u9f0e\uff0c\u7f57\u5217\u6ee1\u76ee\uff0c\u6ca1\u6709\u4e00\u4ef6\u4e0d\u662f\u4ef7 \u503c\u8fde\u57ce\uff0c\u5c31\u662f\u5730\u6bef\u7a97\u8863\uff0c\u4e5f\u662f\u7f28\u73e0\u9970\u7389\uff0c\u4ead\u5b50\u90a3\u9762\uff0c\u53c8\u6709\u4e00\u5ea7\u51c9\u53f0\uff0c\u4e0a\u9762\u5df2\u6446\u597d\u51e0\u684c\u9152\u5e2d\u3002 \u3000\u3000\u9c7c\u4eae\u5927\u738b\u54c8\u54c8\u5927\u7b11\uff0c\u8d77\u7acb\u8bf4\u9053\uff1a\u201c\u4e09\u4f4d\u82f1\u96c4\u679c\u4e0d\u723d\u7ea6\uff0c\u8fd9\u91cc\u8fd8\u6709\u51e0\u4f4d\u6c5f\u6e56\u670b\u53cb\uff0c\u5927\u5bb6\u89c1 \u89c1\uff01\u201d\u5510\u6653\u6f9c\u770b\u90a3\u9c7c\u4eae\u5927\u738b\uff0c\u5df2\u4e0e\u6628\u665a\u6240\u89c1\uff0c\u5927\u4e0d\u76f8\u540c\uff0c\u4ed6\u8eab\u62ab\u7ee3\u888d\uff0c\u9970\u4ee5\u73e0\u7247\uff0c\u9876\u6234\u5929\u5e73 \u51a0\uff0c\u748e\u73de\u7eb7\u5782\uff0c\u4fe8\u7136\u738b\u8005\u6253\u626e\uff0c\u54ea\u91cc\u6709\u534a\u70b9\u50cf\u6628\u591c\u90a3\u5bd2\u9178\u7684\u8001\u6e14\u592b\uff1f\u4ead\u5b50\u91cc\u9ad8\u9ad8\u77ee\u77ee\u5750\u7740\u51e0 \u5341\u4e2a\u4eba\uff0c\u89c1\u767d\u6cf0\u5b98\u5230\u6765\uff0c\u4e00\u9f50\u8d77\u7acb\uff0c\u8f70\u7136\u53eb\u9053\uff1a\u201c\u6c5f\u5357\u516b\u4fa0\u4e2d\u4eba\uff0c\u4e0d\u8fdc\u4e07\u91cc\u800c\u6765\uff0c\u5e78\u4f1a\uff0c\u5e78 \u4f1a\uff01\u201d\u5510\u6653\u6f9c\u653e\u773c\u4e00\u77a7\uff0c\u4e0d\u89c9\u5927\u60ca\u5931\u8272\uff01 \u3000\u3000\u5ba2\u4eba\u4e2d\u6709\u4e24\u4e2a\u9762\u8272\u7126\u9ec4\u7684\u5e72\u7626\u8001\u5934\uff0c\u7a7f\u7740\u4e00\u8eab\u9ec4\u9ebb\u8863\u68e0\uff0c\u9762\u76ee\u6728\u7136\u6beb\u65e0\u8868\u60c5\uff0c\u6b63\u662f\u516b\u81c2 \u795e\u9b54\u8428\u5929\u523a\u548c\u5927\u529b\u795e\u9b54\u8428\u5929\u90fd\uff01 \u3000\u3000\u8428\u5929\u523a\u89c1\u5510\u6653\u6f9c\u8fdb\u5165\u4ead\u4e2d\uff0c\u84e6\u7136\u7ffb\u8d77\u4e00\u53cc\u602a\u773c\uff0c\u957f\u81c2\u4e00\u4f38\uff0c\u9694\u5ea7\u6293\u6765\uff0c\u53e3\u4e2d\u53eb\u9053\uff1a\u201c\u4f60 \u8fd9\u4e2a\u53db\u5f92\uff0c\u8fd8\u6562\u89c1\u6211\uff01\u201d\u767d\u6cf0\u5b98\u4f38\u81c2\u4e00\u6321\uff0c\u674e\u53cc\u53cc\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u722a\u5b50\u6709\u6bd2\uff01\u201d\u767d\u6cf0\u5b98\u53d8\u638c\u4e3a \u62ff\uff0c\u53cc\u6307\u4e00\u52fe\uff0c\u52fe\u7740\u8428\u5929\u523a\u624b\u8155\uff0c\u8428\u5929\u523a\u8fd0\u638c\u4e00\u63a8\uff0c\u4e24\u4eba\u90fd\u9000\u540e\u51e0\u6b65\uff01\u8428\u5929\u523a\u624b\u8155\u9178\u9ebb\uff0c\u88ab \u63e1\u5904\u6709\u5982\u706b\u70d9\uff0c\u767d\u6cf0\u5b98\u4e5f\u80f8\u53e3\u4f5c\u95f7\uff0c\u5982\u4e2d\u94c1\u9524\uff0c\u4e24\u4eba\u4e0d\u8fc7\u4ea4\u6362\u4e00\u62db\uff0c\u90fd\u77e5\u9053\u5bf9\u65b9\u662f\u4e00\u7b49\u4e00\u7684 \u6b66\u6797\u9ad8\u624b\u3002\u9c7c\u4eae\u5927\u738b\u77aa\u773c\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u8fd9\u5c0f\u5b50\u662f\u4f60\u7684\u5f92\u5f1f\uff1f\u201d\u5510\u6653\u6f9c\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8fd9 \u4e2a\u9b54\u5934\u5f92\u5f1f\uff0c\u6211\u5e08\u5085\u662f\u8ffd\u98ce\u5251\u5468\u9752\u548c\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\uff01\u201d\u9c7c\u4eae\u5927\u738b\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u5389\u58f0\u8bf4 \u9053\uff1a\u201c\u6709\u8bdd\u7b49\u4e0b\u518d\u8bf4\uff0c\u6240\u6709\u7cb1\u5b50\uff0c\u4e00\u5e76\u89e3\u51b3\u3002\u201d\u795e\u9b54\u53cc\u8001\u4e00\u89c1\u6709\u6c5f\u6e56\u516b\u4fa0\u7684\u4eba\u76f8\u52a9\uff0c\u4e00\u89c1\u9c7c \u4eae\u5927\u738b\u53d1\u8a00\uff0c\u4e5f\u4e0d\u6562\u6363\u4e71\u8fd9\u4e2a\u573a\u5b50\uff0c\u6012\u800c\u4e0d\u8a00\uff01 \u3000\u3000\u4e00\u884c\u4eba\u8d70\u51fa\u51c9\u53f0\uff0c\u9c7c\u4eae\u5927\u738b\u8bf7\u4e09\u4eba\u5750\u4e0a\u9996\u5e2d\uff0c\u9996\u5e2d\u4e0a\u5ea7\u7a7a\u7740\u4e24\u4f4d\uff0c\u9c7c\u4eae\u5750\u5728\u4e3b\u4f4d\uff0c\u795e\u9b54 \u53cc\u8001\u4e5f\u5750\u4e86\u4e0b\u6765\uff0c\u53e6\u5916\u8fd8\u6709\u56db\u4eba\u4f5c\u966a\uff0c\u9c7c\u4eae\u5927\u738b\u4f9d\u6b21\u4ecb\u7ecd\u9053\uff1a\u201c\u53f3\u5ea7\u8fd9\u4f4d\u662f\u51cc\u4e91\u5c9b\u4e3b\u536b\u626c \u5a01\uff0c\u90a3\u4f4d\u662f\u4e94\u6307\u5c71\u7684\u6d77\u4e91\u548c\u5c1a\uff01\u5de6\u9762\u4e24\u4f4d\u4e00\u4f4d\u662f\u592a\u6e56\u5be8\u4e3b\u5b5f\u6b66\u529f\uff0c\u4e00\u4f4d\u662f\u661f\u5bbf\u6d77\u7684\u836f\u5e08\u5929\u53f6 \u6563\u4eba\uff01\u201d\u8ddf\u7740\u628a\u795e\u9b54\u53cc\u8001\u548c\u767d\u6cf0\u5b98\u4e09\u4eba\u4e5f\u90fd\u4e00\u4e00\u4ecb\u7ecd\u3002\u767d\u6cf0\u5b98\u548c\u5510\u6653\u6f9c\u542c\u4e86\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u4e9b \u4eba\u90fd\u662f\u6b66\u6797\u4e2d\u9876\u513f\u5c16\u513f\u7684\u4eba\u7269\u3002\u51cc\u4e91\u5c9b\u4e3b\u536b\u626c\u5a01\u7cbe\u901a\u6c34\u6027\uff0c\u6c34\u4e0a\u529f\u592b\uff0c\u5e76\u4e16\u65e0\u4e24\uff01\u6d77\u4e91\u548c\u5c1a \u5219\u662f\u5a01\u9707\u5357\u7586\u7684\u5251\u5e08\uff0c\u9690\u5728\u6d77\u5357\u5c9b\u7684\u4e94\u6307\u5c71\uff0c\u5eff\u591a\u5e74\u6765\uff0c\u5b64\u60ac\u6d77\u5916\uff0c\u672a\u5c65\u4e2d\u58eb\u3002\u6740\u6b7b\u5468\u9752\u7684 \u706b\u4e91\u5cd2\u4e3b\u9f99\u6728\u516c\u5c31\u662f\u4ed6\u7684\u5f92\u5f1f\u3002\u592a\u6e56\u5be8\u4e3b\u5b5f\u6b66\u529f\u5374\u662f\u9c7c\u4eae\u7684\u526f\u624b\uff0c\u64c5\u957f\u94c1\u7802\u638c\u529f\u592b\uff0c\u4e00\u53cc\u86fe \u7709\u523a\u66f4\u662f\u6c34\u9646\u4e24\u7528\u7684\u5916\u95e8\u5175\u5668\uff0c\u6b66\u827a\u4e0d\u5728\u9c7c\u4eae\u4e4b\u4e0b\u3002\u661f\u5bbf\u6d77\u7684\u836f\u5e08\u5929\u53f6\u6563\u4eba\uff0c\u5219\u5927\u5bb6\u90fd\u4e0d\u77e5 \u4ed6\u7684\u6765\u5386\uff0c\u4f46\u770b\u4ed6\u7ae5\u989c\u9e64\u53d1\uff0c\u8ffd\u9aa8\u4ed9\u51e4\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u5185\u529f\u9020\u8be3\u6781\u6df1\u7684\u4eba\u7269\u3002\u81f3\u4e8e\u795e\u9b54\u53cc\u8001\u7684 \u5389\u5bb3\uff0c\u5510\u6653\u6f9c\u66f4\u662f\u6df1\u77e5\uff0c\u5fc3\u60f3\uff1a\u8fd9\u4e48\u591a\u4e00\u7b49\u4e00\u7684\u6b66\u6797\u9ad8\u624b\u9f50\u96c6\u6b64\u5730\uff0c\u4e07\u4e00\u95f9\u7ffb\uff0c\u7eb5\u674e\u53cc\u53cc\u548c \u767d\u6cf0\u5b98\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u62b5\u5fa1\u4e0d\u4e86\u3002\u770b\u767d\u674e\u4e8c\u4eba\u65f6\uff0c\u53ea\u89c1\u767d\u6cf0\u5b98\u5df2\u662f\u9762\u8272\u5fae\u53d8\uff0c\u674e\u53cc\u53cc\u5219\u4ecd\u662f\u795e \u8272\u81ea\u82e5\uff0c\u8c08\u7b11\u98ce\u751f\uff01 \u3000\u3000\u5929\u53f6\u6563\u4eba\u5750\u4e0b\u6765\u540e\uff0c\u773c\u76ef\u7740\u9996\u5e2d\u4e0a\u5ea7\uff0c\u51b7\u51b7\u95ee\u9053\uff1a\u201c\u600e\u4e48\u4e3b\u5bbe\u8fd8\u672a\u6765\u4e48\uff1f\u201d\u5929\u53f6\u6563\u4eba\u6b66 \u529f\u5353\u7edd\uff0c\u521d\u5230\u4e2d\u539f\uff0c\u6ee1\u4ee5\u4e3a\u4ed6\u662f\u4e3b\u5bbe\uff0c\u8c01\u77e5\u4e3b\u4eba\u5374\u4e0d\u8bf7\u4ed6\u4e0a\u5750\uff0c\u5fc3\u4e2d\u8001\u4e0d\u9ad8\u5174\uff01 \u3000\u3000\u9c7c\u4eae\u5927\u738b\u628a\u4f17\u4eba\u8083\u8bf7\u5c31\u5ea7\u4e4b\u540e\uff0c\u5176\u4ed6\u5927\u5c0f\u5be8\u4e3b\u548c\u5bbe\u5ba2\u5206\u5750\u4e24\u5e2d\uff0c\u9c7c\u4eae\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u8bf7\u54c8 \u603b\u7ba1\u6765\uff01\u201d\u767d\u6cf0\u5b98\u89c1\u9996\u5e2d\u4e0a\u5ea7\u7a7a\u7740\uff0c\u4e5f\u662f\u8001\u4e0d\u9ad8\u5174\uff0c\u5fc3\u60f3\uff1a\u539f\u6765\u5cb3\u4e08\u4e0d\u662f\u8bf7\u6211\uff0c\u800c\u662f\u53e6\u6709\u4e3b \u5bbe\uff0c\u5012\u8981\u770b\u8fd9\u4e24\u4f4d\u662f\u4f55\u7b49\u4eba\u7269\uff1f \u3000\u3000\u9c7c\u4eae\u5927\u738b\u4e00\u58f0\u5429\u5490\uff0c\u5916\u9762\u9f13\u4e50\u9f50\u9e23\uff0c\u4e0d\u4e00\u4f1a\u513f\u63d0\u706f\u7f8e\u5a62\uff0c\u7c07\u62e5\u7740\u4e00\u4e2a\u56db\u5341\u591a\u5c81\u7684\u7cbe\u58ee\u6c49 \u5b50\uff0c\u63ed\u5e18\u8d70\u8fdb\u3002\u8fd9\u4eba\u56de\u4eba\u88c5\u675f\uff0c\u7738\u5b50\u7cbe\u5149\u56db\u5c04\uff0c\u8170\u60ac\u4e24\u4e2a\u5706\u7403\uff0c\u5927\u8e0f\u6b65\u5750\u4e0a\u9996\u5e2d\uff0c\u795e\u60c5\u5014 \u50b2\uff0c\u795e\u9b54\u53cc\u8001\u9996\u5148\u7ad9\u4e86\u8d77\u6765\uff0c\u5b5f\u6b66\u529f\u536b\u626c\u5a01\u7b49\u4eba\u4e5f\u968f\u7740\u8083\u7acb\uff0c\u53ea\u6709\u5929\u53f6\u6563\u4eba\u548c\u6d77\u4e91\u548c\u5c1a\u5fae\u5fae \u6b20\u8eab\u4f5c\u793c\u3002\u5510\u6653\u6f9c\u770b\u90a3\u4eba\u65f6\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u6084\u6084\u5bf9\u674e\u53cc\u53cc\u9053\uff1a\u201c\u8fd9\u4eba\u6211\u8ba4\u5f97\uff01\u201d\u674e\u53cc\u53cc\u548c\u767d\u6cf0 \u5b98\u90fd\u5728\u51fa\u5947\uff0c\u770b\u8fd9\u4eba\u6b65\u5c65\u5982\u5c71\uff0c\u7cbe\u7738\u70af\u70af\uff0c\u6b66\u529f\u662f\u6df1\u6e5b\u6781\u4e86\uff0c\u4f46\u8bba\u8f88\u4efd\u5374\u5fc5\u5728\u795e\u9b54\u53cc\u8001\u4e4b \u4e0b\uff0c\u4e0d\u77e5\u4ed6\u51ed\u4ec0\u4e48\u5750\u9996\u5e2d\u3002\u89c1\u5510\u6653\u6f9c\u77e5\u9053\u6b64\u4eba\u6765\u5386\uff0c\u6025\u5fd9\u8be2\u95ee\u3002\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4ed6\u662f\u6211\u4e00\u4e2a\u65b0 \u670b\u53cb\u7684\u4ec6\u4eba\uff01\u201d\u767d\u6cf0\u5b98\u7709\u5934\u4e00\u76b1\uff0c\u9887\u607c\u5510\u6653\u6f9c\u5728\u6b64\u65f6\u6b64\u5730\u5374\u8bf4\u7b11\u8bdd\uff0c\u90a3\u6599\u8fd9\u4f4d\u9996\u5e2d\u4e0a\u5bbe\uff0c\u5750 \u4e0b\u6765\u540e\uff0c\u53cc\u773c\u4e00\u626b\uff0c\u5374\u500f\u7684\u53c8\u7acb\u8d77\u6765\uff0c\u4f38\u624b\u7ed9\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5510\u5144\u6765\u8d74\u5bb4\uff0c\u5e78\u4f1a\uff0c\u5e78\u4f1a\uff01\u201d\u5510 \u6653\u6f9c\u548c\u4ed6\u4f38\u624b\u4e00\u63e1\uff0c\u4e5f\u7b11\u95ee\u9053\uff1a\u201c\u738b\u516c\u5b50\u597d\uff1f\u201d\u90a3\u4eba\u606d\u606d\u656c\u656c\u7b54\u9053\uff1a\u201c\u597d\uff01\u591a\u627f\u5173\u6ce8\uff01\u8fd9\u4e00 \u6765\u4e0d\u4f46\u5927\u51fa\u767d\u6cf0\u5b98\u548c\u674e\u53cc\u53cc\u610f\u6599\u4e4b\u5916\uff0c\u5e2d\u4e0a\u81ea\u9c7c\u4eae\u5927\u738b\u4ee5\u4e0b\uff0c\u6240\u6709\u6b66\u6797\u9ad8\u624b\uff0c\u65e0\u4e0d\u6697\u6697\u79f0 \u5947\uff01\u795e\u9b54\u53cc\u8001\uff0c\u795e\u60c5\u987f\u53d8\uff01 \u3000\u3000\u9c7c\u4eae\u5927\u738b\u4eb2\u81ea\u66ff\u9996\u5e2d\u8d35\u5bbe\u659f\u4e86\u4e00\u676f\u7f8e\u9152\uff0c\u90d1\u91cd\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4eac\u5e08\u7b2c\u4e00\u540d\u624b\u54c8\u5e03\u9640\u3001\u54c8 \u603b\u7ba1\uff01\u201d\u674e\u53cc\u53cc\u548c\u767d\u6cf0\u5b98\u867d\u7136\u4e0d\u77e5\u6765\u5386\uff0c\u4e5f\u5f97\u968f\u4f17\u4eba\u9053\u58f0\uff1a\u201c\u4e45\u4ef0\uff01\u201d\u54c8\u5e03\u9640\u5750\u4e0b\u6765\u540e\uff0c\u95ee \u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4f4d\u672a\u5230\uff01\u201d\u9c7c\u4eae\u5927\u738b\u8bf4\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\u8981\u6162\u4e00\u70b9\u624d\u6765\uff0c\u6211\u4eec\u53ef\u5148\u884c\u5f00\u5e2d\u3002\u201d\u767d \u6cf0\u5b98\u53c8\u662f\u4e00\u5947\uff1a\u8fd9\u5b9d\u56fd\u7985\u5e08\u540d\u53f7\u7d20\u672a\u542c\u8fc7\uff0c\u4f55\u4ee5\u4e5f\u5750\u9996\u5e2d\u3002 \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u4ead\u5b50\u5916\u9f13\u4e50\u55a7\u5929\uff0c\u7206\u4ed7\u5982\u96f7\uff0c\u54c8\u5e03\u9640\u63d0\u8d77\u9152\u58f6\uff0c\u7ed9\u9c7c\u4eae\u656c\u9152\uff0c\u8083\u7136\u8bf4\u9053\uff1a \u201c\u606d\u8d3a\u5927\u738b\u5f00\u5e9c\uff01\u201d\u5e2d\u4e0a\u6b22\u58f0\u96f7\u52a8\u3002\u9c7c\u4eae\u5927\u738b\u6625\u98ce\u6ee1\u9762\uff0c\u5f97\u610f\u6d0b\u6d0b\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u5fb7\u8584 \u80fd\u9c9c\uff0c\u591a\u627f\u5404\u4f4d\u5321\u6276\uff0c\u7acb\u6b64\u57fa\u4e1a\uff0c\u53c8\u627f\u56db\u7687\u7237\u9519\u7231\uff0c\u5141\u4e88\u6d77\u5916\u79f0\u738b\u3002\u4eca\u65e5\u5f00\u5e9c\uff0c\u656c\u5bb4\u9ad8\u8d24\uff0c \u65e5\u540e\u5c1a\u6709\u5927\u4e8b\u8981\u5404\u4f4d\u534f\u529b\u76f8\u52a9\uff0c\u8bf7\u4e88\u6307\u6559\uff01\u201d\u767d\u6cf0\u5b98\u6ee1\u8154\u6012\u6c14\uff0c\u60f3\u9053\uff1a\u539f\u6765\u4ed6\u6765\u4e0d\u53ca\u7b49\u56db\u7687 \u5b50\u767b\u57fa\uff0c\u5df2\u5148\u7a83\u4f4d\u81ea\u5a31\u3002\u500f\u7136\u8d77\u7acb\uff0c\u51b2\u53e3\u8bf4\u9053\uff1a\u201c\u5cb3\u4e08\u5927\u4eba\uff0c\u5c0f\u5a7f\u4fbf\u6709\u4e00\u4e8b\u8bf7\u6559\uff01\u201d\u820c\u7efd\u6625 \u96f7\uff0c\u56db\u5ea7\u7686\u60ca\uff01\u9c7c\u4eae\u5927\u738b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u767d\u5927\u82f1\u96c4\uff0c\u7fc1\u5a7f\u4e4b\u79f0\u6682\u7f13\uff0c\u9ad8\u89c1\u8bf7\u5148\u8d50\u6559\uff01\u201d \u3000\u3000\u767d\u6cf0\u5b98\u53cc\u624b\u636e\u684c\uff0c\u6b63\u60f3\u53d1\u8bdd\uff0c\u4ead\u5b50\u5916\u5ffd\u7136\u4e00\u9635\u55a7\u54d7\uff0c\u9c7c\u4eae\u5927\u738b\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d\u624b\u4e0b \u4eba\u7980\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u8001\u4e10\u5a46\u7a81\u7136\u95ef\u6765\uff0c\u5979\u4e5f\u8981\u8d74\u5927\u738b\u4e4b\u5bb4\uff01\u201d\u9c7c\u4eae\u559d\u9053\uff1a\u201c\u53eb\u5979\u8fdb\u6765\uff01\u201d\u8f6f\u5e18 \u4e00\u63ed\uff0c\u4e00\u4e2a\u4e10\u5987\u6b65\u8fc7\u5e7f\u4ead\uff0c\u8d70\u4e0a\u51c9\u53f0\uff0c\u5934\u4e0a\u9752\u4e1d\u8986\u989d\uff0c\u4fe8\u5982\u5c11\u5973\uff0c\u9762\u4e0a\u5374\u76b1\u7eb9\u9690\u73b0\uff0c\u884c\u52a8\u8e52 \u8dda\u3002\u5510\u6653\u6f9c\u89c1\u4e86\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u8fd9\u4eba\u6b63\u662f\u524d\u5929\u5728\u671b\u6d77\u697c\u4e0a\u6240\u89c1\u7684\u5987\u4eba\u3002\u81ea\u5df1\u7684\u6e38\u9f99\u5b9d\u5251\uff0c\u591a\u534a \u5c31\u662f\u5979\u53d6\u53bb\u540e\u590d\u53c8\u5f52\u8fd8\u3002\u5979\u73b0\u5728\u72ec\u4e0a\u5b64\u5cf0\uff0c\u5355\u9a91\u95ef\u5e2d\uff0c\u6b63\u4e0d\u77e5\u662f\u4f55\u7528\u610f\uff01 \u3000\u3000\u9c7c\u4eae\u5927\u738b\u7eb5\u6a2a\u534a\u4e16\uff0c\u7ed3\u7eb3\u5947\u4eba\u5f02\u58eb\uff0c\u4e0d\u77e5\u591a\u5c11\uff0c\u800c\u4eca\u89c1\u4e86\u8fd9\u4e10\u5987\u5f62\u72b6\uff0c\u4e5f\u4e0d\u7981\u6697\u81ea\u7eb3 \u7f55\u3002\u54c8\u5e03\u9640\u4e5f\u8ba4\u51fa\u4e86\u5979\u5c31\u662f\u524d\u65e5\u5728\u9152\u697c\u663e\u6280\u7684\u8001\u5987\u4eba\uff0c\u6697\u52a0\u6212\u5907\u3002\u9c7c\u4eae\u6697\u81ea\u601d\u91cf\uff1a\u4eba\u7684\u5934 \u53d1\uff0c\u6700\u4e0e\u6c14\u8840\u6709\u5173\uff0c\u8870\u8001\u4e4b\u4eba\uff0c\u4e0d\u767d\u4ea6\u79c3\u3002\u6240\u4ee5\u82e5\u517b\u751f\u6709\u672f\uff0c\u80fd\u4fdd\u6301\u81ea\u53d1\u7ae5\u989c\uff0c\u5c1a\u4e0d\u51fa\u5947\uff0c \u50cf\u8fd9\u4e10\u5987\u9762\u6709\u76b1\u7eb9\uff0c\u72b9\u81ea\u9752\u4e1d\u8986\u989d\uff0c\u53d1\u5149\u9274\u4eba\uff0c\u90a3\u53ef\u771f\u662f\u4e0d\u53ef\u601d\u8bae\u4e4b\u4e8b\uff0c\u800c\u4e14\u7530\u6a2a\u5c9b\u4e0a\uff0c\u9632 \u8303\u68ee\u4e25\uff0c\u5c9b\u4e0a\u5b64\u5cf0\u9669\u5cfb\u96be\u4e0a\uff0c\u8fd9\u4e10\u5987\u7adf\u4f3c\u4ece\u5929\u800c\u964d\uff0c\u7a81\u5982\u5176\u6765\uff0c\u76f4\u95ef\u81f3\u7b75\u524d\uff0c\u59cb\u7ed9\u4eba\u53d1\u73b0\uff0c \u82e5\u975e\u5979\u631f\u6709\u60ca\u4eba\u6280\u827a\uff0c\u90a3\u91cc\u80fd\u591f\u3002\u9c7c\u4eae\u5927\u738b\u7a0d\u4e00\u6c89\u541f\uff0c\u6025\u5fd9\u51fa\u53bb\u8fce\u63a5\uff01\u90a3\u4e10\u5987\u54c8\u54c8\u5927\u7b11\u9053\uff1a \u201c\u9c7c\u4eae\u5927\u738b\uff0c\u679c\u7136\u5927\u91cf\uff0c\u4e0d\u7f5a\u6211\u95ef\u5e2d\u4e4b\u7f6a\uff0c\u8fd8\u8bf7\u6211\u559d\u9152\u3002\u6211\u4eca\u65e5\u722c\u4e0a\u6b64\u5cf0\uff0c\u4e5f\u8fd8\u4e0d\u767d\u8d39\u6c14 \u529b\u3002\u201d\u62d0\u6756\u987f\u5730\uff0c\u5dc5\u5dcd\u5dcd\u7684\u5411\u9996\u5e2d\u884c\u6765\u3002 \u3000\u3000\u9c7c\u4eae\u5927\u738b\u731b\u7136\u4e00\u60ca\uff0c\u8fd9\u5e2d\u53ea\u7a7a\u6709\u4e0a\u5ea7\u4e00\u4f4d\uff0c\u4f4d\u5b50\u662f\u865a\u5e2d\u4ee5\u5f85\uff0c\u7b49\u5019\u8d35\u5bbe\u7684\u3002\u5982\u4f55\u80fd\u7ed9\u8fd9 \u4e10\u5987\u5750\uff1f\u5f53\u4e0b\u9762\u8272\u5c34\u5c2c\uff0c\u966a\u7b11\u8bf4\u9053\uff1a\u201c\u8001\u592a\u592a\uff0c\u8bf7\u5230\u90a3\u8fb9\u4e0a\u5ea7\u3002\u201d\u5f85\u5f15\u5979\u8d70\u5411\u897f\u9996\u90a3\u5e2d\uff0c\u8001 \u4e10\u5987\u5374\u4e0d\u79fb\u6b65\u3002\u84e6\u7136\u6307\u7740\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u7ea6\u4f60\u660e\u665a\u6765\uff0c\u4f60\u4eca\u665a\u5c31\u6765\u4e86\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u4e00\u60ca\uff1a\u90a3 \u4f4d\u76d7\u5251\u8fd8\u5251\u7684\u5947\u4eba\uff0c\u679c\u7136\u5c31\u662f\u8fd9\u4e2a\u8001\u4e5e\u5a46\uff0c\u6025\u5fd9\u7ad9\u7acb\u8d77\u6765\uff0c\u606d\u606d\u656c\u656c\u7684\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u4f11 \u602a\uff0c\u5f1f\u5b50\u662f\u9c7c\u4eae\u5927\u738b\u9080\u6765\u8d74\u5bb4\uff0c\u4e0d\u6562\u4e0d\u5230\uff01\u201d\u90a3\u4e10\u5987\u62d0\u6756\u4e00\u987f\uff0c\u5ffd\u7136\u9a82\u9053\uff1a\u201c\u5453\uff0c\u4ec0\u4e48\u5f1f \u5b50\uff01\u4f60\u8fd9\u5c0f\u6df7\u86cb\uff0c\u8fde\u79f0\u547c\u90fd\u5f04\u4e0d\u6e05\u695a\uff01\u4f60\u7684\u5e08\u5085\uff0c\u6ca1\u6709\u5bf9\u4f60\u8bf4\u8fc7\u672c\u95e8\u8f88\u4efd\u5417\uff1f\u201d\u5510\u6653\u6f9c\u60f6\u6050 \u4e07\u5206\uff0c\u624b\u8db3\u65e0\u63aa\uff0c\u9c7c\u4eae\u5927\u738b\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u54e5\u662f\u4f60\u7684\u665a\u8f88\u5417\uff1f\u4e0d\u77e5\u8001\u592a\u592a\u4e0e\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1 \u662f\u600e\u4e2a\u79f0\u547c\uff1f\u201d\u8001\u4e10\u5987\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u94c1\u638c\u795e\u5f39\u4e0d\u94c1\u638c\u795e\u5f39\uff0c\u6211\u53ea\u77e5\u9053\u8fd9\u6d51\u5c0f\u5b50\u6700\u591a\u80fd\u7b97 \u6211\u7684\u5f92\u5b59\uff01\u201d\u51cc\u4e91\u5c9b\u4e3b\u536b\u626c\u5a01\u5947\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8bf4\u662f\u2018\u6700\u591a\u2019\uff0c\u4f60\u4e5f\u5f04\u4e0d\u6e05\u8f88\u4efd\u5417\uff1f\u201d \u3000\u3000\u8001\u4e10\u5987\u62d0\u6756\u4e00\u6302\uff0c\u8001\u6c14\u6a2a\u79cb\u7684\u8bf4\u9053\uff1a\u201c\u6211\u600e\u4e48\u80fd\u5f04\u6e05\u695a\uff0c\u6211\u662f\u524d\u5929\u65b9\u89c1\u7740\u7684\u3002\u5728\u672a\u89c1\u7740 \u4ed6\u524d\u6211\u8fd8\u4e0d\u6253\u7b97\u8ba4\u4ed6\u662f\u5f92\u5b59\u5462\uff01\u4e5f\u8bb8\u4ed6\u6bd4\u6211\u7684\u5f92\u5b59\u8fd8\u4f4e\u4e00\u8f88\uff01\u201d\u5750\u5728\u9996\u5e2d\u7684\u4eba\u4e2a\u4e2a\u90fd\u662f\u6b66\u6797 \u524d\u8f88\uff0c\u6216\u4e00\u6d3e\u5b97\u5e08\uff0c\u542c\u4e86\u90fd\u76b1\u7709\u5934\u3002\u6768\u4ef2\u82f1\u5e74\u903e\u4e94\u65ec\uff0c\u5e08\u5085\u8fd9\u8f88\u5168\u5df2\u8fc7\u4e16\uff0c\u90a3\u91cc\u4f1a\u94bb\u51fa\u8fd9\u8001 \u4e10\u5a46\uff0c\u660e\u660e\u662f\u8bf4\u8c0e\u7684\u4e86\uff0c\u9c7c\u4eae\u5374\u8bb0\u8d77\u5510\u6653\u6f9c\u8fd8\u6709\u4e00\u4e2a\u5e08\u5085\u662f\u8ffd\u98ce\u5251\u5468\u9752\uff0c\u84e6\u7136\u60f3\u8d77\u4e00\u4eba\uff0c\u4e0d \u89c9\u5927\u60ca\u5931\u8272\uff0c\u4f46\u8fd9\u4eba\u662f\u5eb7\u7199\u521d\u5e74\u7684\u4eba\uff0c\u6570\u5341\u5e74\u6765\uff0c\u6beb\u65e0\u6d88\u606f\uff0c\u8fde\u9c7c\u4eae\u4e5f\u662f\u4ece\u524d\u8f88\u53e3\u4e2d\u624d\u77e5\u9053 \u5979\u7684\u540d\u5b57\u3002\u96be\u9053\u5979\u8fd8\u5728\u4e16\u95f4\uff1f\u800c\u53c8\u7a81\u7136\u6765\u6b64\uff1f\u6b63\u81ea\u731c\u7591\uff0c\u90a3\u8001\u4e10\u5a46\u53c8\u9053\uff1a\u201c\u600e\u4e48\u4f60\u8fde\u6211\u7684\u5f92 \u5b59\u90fd\u8bf7\u5750\u4e0a\u9996\u5e2d\uff0c\u5374\u8981\u8d76\u6211\u7ed9\u4ed6\u4f5c\u966a\u5ba2\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u614c\u5fd9\u79bb\u5e2d\uff0c\u7ed9\u8001\u4e10\u5a46\u884c\u4e0b\u5927\u793c\uff0c\u5168\u573a\u5bbe \u5ba2\u65e0\u4e0d\u60ca\u5947\uff01\u9996\u5e2d\u8d35\u5bbe\u54c8\u5e03\u9640\u66f4\u662f\u9762\u76ae\u53d8\u8272\uff01\u4ed6\u548c\u5510\u6653\u6f9c\u9002\u624d\u8fd8\u8ba4\u662f\u670b\u53cb\uff0c\u73b0\u5728\u5e73\u5730\u94bb\u51fa\u4e86 \u8fd9\u8001\u4e10\u5a46\uff0c\u82e5\u6309\u6c5f\u6e56\u793c\u8282\uff0c\u81ea\u5df1\u5c82\u4e0d\u51ed\u7a7a\u77ee\u4e86\u4e09\u8f88\uff1f\u6d77\u4e91\u548c\u5c1a\u6905\u5b50\u4e00\u65cb\uff0c\u84e6\u7136\u4f38\u51fa\u624b\u6765\u3002\u90a3 \u8001\u4e10\u5987\u6b63\u628a\u5510\u6653\u6f9c\u6276\u8d77\uff0c\u6d77\u4e91\u548c\u5c1a\u84e6\u7136\u4f38\u624b\u6765\u62c9\uff0c\u5f53\u4e2d\u4e00\u62e6\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5f52\u5b97\u8ba4\u7956\u4e4b \u793c\uff0c\u4e0d\u5fc5\u5728\u8fd9\u91cc\u6765\u884c\uff01\u201d\u6d77\u4e91\u548c\u5c1a\u81ea\u5357\u6d77\u800c\u6765\uff0c\u5750\u4e0d\u4e0a\u9996\u5e2d\u4e0a\u4f4d\uff0c\u5df2\u81ea\u6709\u70b9\u6c14\u607c\uff0c\u52a0\u4e0a\u8fd9\u8001 \u4e10\u5a46\u8001\u6c14\u6a2a\u79cb\uff0c\u5fc3\u4e2d\u66f4\u4e0d\u8212\u670d\uff0c\u6240\u4ee5\u6697\u8fd0\u5185\u52b2\uff0c\u8981\u5979\u6298\u5728\u5f53\u573a\uff01\u6d77\u4e91\u548c\u5c1a\u51e0\u5341\u5e74\u529f\u529b\uff0c\u9020\u8be3 \u975e\u51e1\uff0c\u8fd9\u4e00\u683c\u529b\u91cf\u4f55\u6b62\u5343\u65a4\uff0c\u90a3\u77e5\u624b\u6240\u89e6\u5904\uff0c\u67d4\u82e5\u65e0\u7269\uff0c\u500f\u7136\u4e00\u60ca\uff0c\u81c2\u4e0a\u818a\u817f\u5f2f\u7a81\u7136\u9178\u9ebb\uff0c \u6905\u5b50\u5411\u540e\u4e00\u503e\uff0c\u8001\u4e10\u5a46\u53eb\u9053\uff1a\u201c\u54ce\u5440\uff0c\u4e0d\u6562\u5f53\uff0c\u4e0d\u6562\u5f53\uff0c\u600e\u4e48\u4f60\u4e5f\u884c\u8d77\u5927\u793c\u6765\u4e86\uff01\u201d\u6d77\u4e91\u548c \u5c1a\u53cc\u819d\u8dea\u5730\uff0c\u5ffd\u5fd9\u6697\u8fd0\u53e3\u6c14\uff0c\u624d\u628a\u8840\u8109\u7545\u901a\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u6ee1\u9762\u901a\u7ea2\uff0c\u8fd9\u8001\u4e10\u5a46\u7adf\u4e0d\u77e5\u662f\u7528\u4ec0 \u4e48\u624b\u6cd5\uff0c\u5728\u7535\u5149\u77f3\u706b\u5239\u90a3\u4e4b\u95f4\uff0c\u5c31\u95ed\u4e86\u81ea\u5df1\u7684\u7a74\u9053\uff01\u6d77\u4e91\u548c\u5c1a\u53d7\u4e86\u6697\u7b97\uff0c\u975e\u5e38\u4e0d\u5fff\uff0c\u4f46\u81ea\u5df1 \u662f\u4e00\u6d3e\u5b97\u5e08\uff0c\u5403\u4e86\u4e8f\u4e5f\u53ea\u80fd\u54d1\u5fcd\uff0c\u6d82\u56fe\u62a5\u590d\uff0c\u4e0d\u6562\u53d1\u4f5c\u5f53\u573a\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u5168\u573a\u9a87\u7136\uff0c\u8fde\u54c8\u5e03\u9640\u4e5f\u7741\u5927\u773c\u775b\u3002\u9c7c\u4eae\u5927\u738b\u5c34\u5c2c\u4e4b\u6781\uff0c\u6025\u5fd9\u5bf9\u592a\u6e56\u5be8\u4e3b\u5b5f\u6b66\u529f \u9053\uff1a\u201c\u5b5f\u8001\u5f1f\uff0c\u5c48\u9a7e\u4f60\u5230\u897f\u9996\u90a3\u5e2d\u4f5c\u6211\u62db\u547c\u5bbe\u5ba2\u3002\u201d\u9c7c\u4eae\u4e0d\u597d\u8bf7\u5176\u4ed6\u8d35\u5bbe\u8ba9\u4f4d\uff0c\u6240\u4ee5\u53ea\u597d\u53eb \u81ea\u5df1\u7684\u526f\u624b\u632a\u5ea7\u3002\u90a3\u8001\u4e10\u5a46\u66f4\u4e0d\u5ba2\u6c14\uff0c\u5927\u6a21\u5927\u6837\u7684\u5750\u4e86\u4e0b\u6765\uff0c\u8865\u4e0a\u5b5f\u6b66\u529f\u7684\u4f4d\u5b50\uff0c\u53c8\u6070\u6070\u662f \u5750\u5728\u6d77\u4e91\u548c\u5c1a\u8eab\u8fb9\u3002\u9c7c\u4eae\u518d\u656c\u4e86\u4e00\u904d\u9152\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u90a3\u8001\u4e10\u5987\u7aef\u5750\u5e2d\u4e0a\uff0c\u4e0d\u89c1\u6709\u4f55\u5f02\u72b6\uff0c\u9c7c \u4eae\u624d\u7a0d\u7a0d\u653e\u5fc3\u3002\u5f53\u4e0b\u91cd\u7eed\u524d\u8a00\uff0c\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u767d\u5927\u82f1\u96c4\uff0c\u521a\u624d\u8bf4\u662f\u6709\u65c1\u6307\u6559\uff0c\u9c7c\u4eae\u4e0d\u624d\uff0c\u613f \u8046\u9ad8\u8bba\uff01\u201d\u767d\u6cf0\u5b98\u6012\u5bb9\u6ee1\u9762\uff0c\u9aa4\u7136\u7ad9\u8d77\uff0c\u5927\u58f0\u95ee\u9053\uff1a\u201c\u8bf7\u95ee\u8001\u4e08\uff0c\u6211\u767d\u6cf0\u5b98\u72af\u4e86\u4ec0\u4e48\u8fc7\u9519\uff1f \u8001\u4e08\u4e0d\u8bb8\u6211\u548c\u672a\u5a5a\u59bb\u5b50\u89c1\u9762\uff01\u201d\u9c7c\u4eae\u5927\u738b\u9762\u8272\u4e00\u6c89\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u8fd8\u8ba4\u6211\u662f\u4f60\u7684\u957f\u8f88\uff0c \u6211\u5f00\u5e9c\u79f0\u738b\uff0c\u4f60\u4e3a\u4f55\u7f6e\u8eab\u4e8b\u5916\uff01\u201d\u54c8\u5e03\u9640\u529d\u9053\uff1a\u201c\u7fc1\u5a7f\u4e4b\u95f4\uff0c\u6709\u4e8b\u53ef\u597d\u597d\u5546\u8c08\uff0c\u4e0d\u5fc5\u53d1\u6c14\u3002 \u4f9d\u6211\u8bf4\uff0c\u767d\u82f1\u96c4\u82e5\u80af\u548c\u6c5f\u5357\u516b\u4fa0\uff0c\u4e00\u540c\u8d5e\u52a9\u4ee4\u5cb3\uff0c\u90a3\u4e48\u9c7c\u4eae\u5927\u738b\u81ea\u53ef\u6536\u56de\u6210\u547d\uff0c\u4e24\u5bb6\u8c6a\u6770\uff0c \u7ed3\u4e3a\u79e6\u664b\u4e4b\u6b22\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u9c7c\u4eae\u70b9\u70b9\u5934\u9053\uff1a\u201c\u90a3\u5c31\u8981\u770b\u4ed6\u4e86\uff01\u201d \u3000\u3000\u767d\u6cf0\u5b98\u5ffd\u5730\u4e00\u58f0\u51b7\u7b11\uff0c\u4f83\u7136\u8bf4\u9053\uff1a\u201c\u8001\u4e08\u5f00\u5e9c\u79f0\u738b\uff0c\u82e5\u7136\u662f\u8a93\u4e3e\u4e49\u65d7\uff0c\u9a71\u9664\u80e1\u864f\uff0c\u90a3\u6cf0 \u5b98\u4e07\u6b7b\u4e0d\u8f9e\u3002\u82e5\u662f\u542c\u4ec0\u4e48\u56db\u7687\u7237\u4e4b\u547d\uff0c\u8d2a\u56fe\u88c2\u571f\u5206\u8305\uff0c\u4f5c\u5f02\u65cf\u5c4f\u85e9\uff0c\u79f0\u9738\u6d77\u5916\uff0c\u90a3\u6cf0\u5b98\u5b81\u6b7b \u4e5f\u4e0d\u6562\u9053\u968f\uff01\u201d\u9c7c\u4eae\u5927\u738b\u52c3\u7136\u5927\u6012\uff0c\u65a5\u9053\uff1a\u201c\u6cf0\u5b98\uff0c\u4f60\u597d\u65e0\u793c\uff01\u6211\u5728\u6d77\u5916\u4e3a\u738b\uff0c\u4e0d\u671d\u4e0d\u8d21\uff0c \u53c8\u6709\u4ec0\u4e48\u8fb1\u6ca1\u6c14\u8282\u4e4b\u5904\uff1f\u201d\u767d\u6cf0\u5b98\u518d\u5fcd\u4e0d\u4f4f\uff0c\u6d41\u6d95\u8bf4\u9053\uff1a\u201c\u8001\u4e08\uff0c\u4f60\u597d\u7cca\u6d82\uff0c\u5c45\u7136\u542c\u4fe1\u6ee1\u5974 \u7684\u8bdd\uff0c\u4f60\u82e5\u6276\u4ec0\u4e48\u56db\u7687\u5b50\u767b\u57fa\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u662f\u4e00\u4e2a\u5434\u4e09\u6842\uff0c\u867d\u5f97\u88c2\u571f\u4e3a\u738b\uff0c\u4e5f\u514d\u4e0d\u4e86\u5154\u6b7b\u72d7 \u70f9\u4e4b\u96be\uff01\u54b1\u4eec\u8981\u5e72\u5c31\u5e72\u4e2a\u6f02\u4eae\u7684\uff0c\u4f55\u82e6\u7ed9\u654c\u4eba\u4e89\u6743\u593a\u4f4d\uff01\u201d\u9c7c\u4eae\u5927\u738b\u628a\u9152\u676f\u4e00\u6454\uff0c\u6012\u9053\uff1a \u201c\u4f60\u771f\u4e2a\u4e0d\u4ece\uff01\u201d\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u6c5f\u5357\u516b\u4fa0\uff0c\u5934\u53ef\u65ad\u800c\u5fd7\u4e0d\u53ef\u8fb1\uff1f\u201d\u9c7c\u4eae\u5927\u738b\u5ffd\u7136\u4e00\u58f0\u51b7\u7b11\uff0c \u5411\u624b\u4e0b\u9053\uff1a\u201c\u8bf7\u5b9d\u56fd\u63b8\u5e08\u6765\uff0c\u6211\u5012\u8981\u770b\u770b\u6c5f\u5357\u516b\u4fa0\u662f\u5426\u90fd\u50cf\u4f60\u4e00\u6837\u611a\u987d\u4e0d\u5316\uff01\u201d\u4ead\u5b50\u5916\u84e6\u7136 \u53c8\u662f\u9f13\u4e50\u9f50\u9e23\uff0c\u4e24\u961f\u536b\u58eb\uff0c\u5927\u58f0\u559d\u9053\uff0c\u5217\u9635\u8fce\u5bbe\uff01\u672d\u4eea\u4e4b\u9686\uff0c\u4e0d\u5728\u63a5\u5f85\u54c8\u5e03\u9640\u4e4b\u4e0b\u3002\u9996\u5e2d\u5e76 \u5217\u4e24\u4e2a\u4e0a\u5ea7\u8d35\u5bbe\u4e4b\u4f4d\uff0c\u5df2\u662f\u51fa\u5947\uff0c\u800c\u8fd9\u4e24\u4f4d\u8d35\u5bbe\u53c8\u90fd\u662f\u6b66\u6797\u4e2d\u4e0d\u89c1\u7ecf\u4f20\u4e4b\u8f88\uff0c\u66f4\u662f\u4ee4\u4eba\u60ca \u8be7\uff0c\u8fd9\u65f6\u4e0d\u4f46\u767d\u6cf0\u5b98\u674e\u53cc\u53cc\u7b49\u7559\u795e\u6ce8\u89c6\uff0c\u6240\u6709\u5bbe\u5ba2\u4e5f\u90fd\u5f15\u9886\u5916\u671b\uff0c\u8981\u770b\u770b\u8fd9\u4f4d\u5b9d\u56fd\u7985\u5e08\uff0c\u53c8 \u662f\u4ec0\u4e48\u4eba\u7269\u3002 \u3000\u3000\u4e09\u901a\u9f13\u7f62\uff0c\u5341\u4e8c\u540d\u536b\u58eb\u6392\u6210\u4e24\u961f\uff0c\u5f15\u4e86\u4e00\u4e2a\u80d6\u548c\u5c1a\u8fdb\u6765\uff0c\u8fd9\u548c\u5c1a\u624b\u63d0\u7897\u53e3\u7c97\u7684\u9563\u94c1\u7985 \u6756\uff0c\u563b\u563b\u54c8\u54c8\uff0c\u4e00\u5bf9\u773c\u775b\uff0c\u8d3c\u5fd2\u5fd2\u7684\u5c3d\u770b\u7740\u4e24\u65c1\u4f8d\u5019\u7684\u7f8e\u5a62\uff0c\u6beb\u65e0\u8d35\u5bbe\u5e94\u6709\u7684\u7aef\u5e84\uff0c\u9c7c\u4eae\u77a7 \u7740\uff0c\u4e5f\u4e0d\u50cf\u4f53\u7edf\uff0c\u62a2\u51fa\u6765\u8fce\u3002\u90a3\u548c\u5c1a\u6447\u6447\u6446\u6446\uff0c\u884c\u4e86\u51e0\u6b65\uff0c\u731b\u7136\u6b62\u4f4f\uff0c\u767d\u6cf0\u5b98\u98a4\u58f0\u53eb\u9053\uff1a \u201c\u4e86\u56e0\u5e08\u5144\uff0c\u4f60\u7adf\u7136\u4e5f\u5728\u6b64\u5730\uff01\u201d\u8bed\u58f0\u4e2d\u65e0\u9650\u60b2\u6124\uff0c\u674e\u53cc\u53cc\u4e5f\u9aa4\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u8001\u4e10\u5987\u7aef\u5750\u5e2d \u4e2d\uff0c\u53ea\u662f\u51b7\u7b11\uff01 \u3000\u3000\u4e86\u56e0\u548c\u5c1a\u4e43\u6c5f\u5357\u516b\u4fa0\u4e4b\u9996\uff0c\u5929\u4e0b\u82f1\u96c4\u65e0\u4e0d\u77e5\u9053\uff01\u6240\u4e0d\u77e5\u9053\u7684\u662f\u4ed6\u4e5f\u53d7\u4e86\u56db\u7687\u5b50\u5141\u796f\u793c \u8058\uff0c\u88ab\u5c01\u4e3a\u201c\u5b9d\u56fd\u7985\u5e08\u201d\u3002\u4e86\u56e0\u548c\u5c1a\u7ed9\u767d\u6cf0\u5b98\u4e00\u559d\uff0c\u731b\u7684\u4e00\u6014\u3002\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u6765\u5f97\u6211\u6765\u4e0d \u5f97\uff1f\u201d\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u6211\u6b64\u6765\u4e00\u662f\u4e3a\u89c1\u672a\u5a5a\u59bb\u5b50\uff0c\u4e8c\u662f\u4e3a\u963b\u5cb3\u4e08\u5f52\u987a\u6e05\u5ef7\uff0c\u6562\u95ee\u5e08\u5144\u6765\u6b64\u53c8\u662f \u4e3a\u4f55\uff1f\u201d\u4e86\u56e0\u548c\u5c1a\u80d6\u9762\u53d8\u7d2b\uff0c\u624b\u63d0\u7985\u6756\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u539f\u6765\u4e86\u56e0\u548c\u5c1a\u5728\u516d\u4e03\u5e74\u524d\u4e0e\u51cc\u4e91\u5c9b\u4e3b\u536b\u626c\u5a01\u76f8\u4ea4\uff0c\u6e10\u6e10\u8d70\u4e0a\u6b67\u9014\uff0c\u4ed6\u672c\u6765\u662f\u4e2a\u5f3a\u76d7\u51fa \u8eab\uff0c\u88ab\u72ec\u81c2\u795e\u5c3c\u6536\u670d\u4e4b\u540e\uff0c\u5f8b\u4ee5\u95e8\u89c4\uff0c\u4e0d\u6562\u4e71\u52a8\uff0c\u5341\u51e0\u5e74\u9752\u706f\u793c\u4f5b\uff0c\u5df2\u662f\u5bc2\u5bde\u96be\u582a\u3002\u51fa\u5e08\u4e4b \u540e\uff0c\u51db\u4e8e\u72ec\u81c2\u795e\u5c3c\u7684\u5389\u5bb3\uff0c\u4e5f\u8fd8\u4e0d\u6562\u516c\u7136\u4f5c\u6076\u3002\u53ea\u662f\u5077\u5077\u5230\u51cc\u4e91\u5c9b\u4e2d\u4e0e\u536b\u626c\u5a01\u996e\u9152\u4f5c\u4e50\u3002\u9976 \u662f\u5982\u6b64\uff0c\u72ec\u81c2\u795e\u5c3c\u4e5f\u5df2\u6709\u98ce\u95fb\uff0c\u6240\u4ee5\u4e94\u5e74\u4e4b\u524d\uff0c\u5510\u6653\u6f9c\u521d\u4e0a\u9099\u5c71\uff0c\u5c31\u66fe\u542c\u5f97\u72ec\u81c2\u795e\u5c3c\u5429\u5490\u5415 \u56db\u5a18\uff0c\u8981\u5979\u4ee3\u5e08\u5085\u6574\u987f\u95e8\u98ce\uff0c\u82e5\u7136\u4e86\u56e0\u548c\u5c1a\u8fd8\u662f\u6019\u6076\u4e0d\u609b\uff0c\u5c31\u53d6\u4ed6\u9996\u7ea7\u3002 \u3000\u3000\u4e24\u5e74\u4e4b\u524d\uff0c\u72ec\u81c2\u795e\u5c3c\u5728\u9099\u5c71\u5706\u5bc2\uff0c\u767d\u6cf0\u5b98\u9080\u4e86\u66f9\u4ec1\u7236\u8def\u6c11\u77bb\u7518\u51e4\u6c60\u5468\u6e05\u7b49\u56de\u5c71\u5954\u4e27\uff0c\u4e86 \u56e0\u548c\u5c1a\u5374\u4e0d\u80af\u540c\u884c\u3002\u90a3\u65f6\u767d\u6cf0\u5b98\u5df2\u77e5\u6709\u5f02\uff0c\u8fd8\u6599\u4e0d\u5230\u4e86\u56e0\u548c\u5c1a\u5728\u5e08\u5085\u6b7b\u540e\uff0c\u6076\u6027\u590d\u9732\uff0c\u66f4\u4e00 \u53d1\u4e0d\u53ef\u6536\u62fe\u3002\u56db\u7687\u5b50\u5141\u796f\u6295\u5176\u6240\u597d\uff0c\u5fae\u670d\u6c5f\u6e56\uff0c\u4e0e\u4ed6\u7ed3\u4e3a\u5144\u5f1f\uff0c\u628a\u51e0\u4e2a\u5bab\u5a25\u7f8e\u5973\uff0c\u5077\u5e26\u51fa \u4eac\uff0c\u9001\u4e0e\u4ed6\u53d7\u7528\u3002\u4e86\u56e0\u548c\u5c1a\u4e0d\u8fa8\u662f\u975e\uff0c\u628a\u5e08\u5085\u6212\u6761\u629b\u4e4b\u8111\u540e\uff0c\u53d7\u4e86\u56db\u7687\u5b50\u5c01\u53f7\uff0c\u77e2\u5fe0\u76f8\u62a5\uff0c \u5e76\u66ff\u4ed6\u5728\u6c5f\u6e56\u4e0a\u62c9\u62e2\u8c6a\u6770\u3002 \u3000\u3000\u72ec\u81c2\u795e\u5c3c\u867d\u6b7b\uff0c\u4f59\u5a01\u72b9\u5728\uff0c\u6b64\u523b\uff0c\u4e86\u56e0\u548c\u5c1a\u88ab\u767d\u6cf0\u5b98\u4e25\u8bcd\u76f8\u8d28\uff0c\u80d6\u9762\u53d8\u8272\uff0c\u5bf9\u7740\u5e08\u5f1f\uff0c \u4e00\u65f6\u95f4\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u9c7c\u4eae\u5927\u738b\u6025\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\u6b66\u529f\u76d6\u4e16\uff0c\u56db\u7687\u5b50\u501a\u4e3a\u80a1\u80b1\uff0c\u767d\u6cf0\u5b98\u4f60\u6562\u4e0d \u5c0a\u5e08\u5144\u7684\u8bdd\u5417\uff1f\u201d\u767d\u6cf0\u5b98\u6012\u706b\u51b2\u5929\uff0c\u5927\u58f0\u95ee\u9053\uff1a\u201c\u5e08\u5144\uff0c\u6b64\u8bdd\u53ef\u771f\uff1f\u201d\u4e86\u56e0\u548c\u5c1a\u8001\u7f9e\u6210\u6012\uff0c \u559d\u9053\uff1a\u201c\u767d\u6cf0\u5b98\uff0c\u4f60\u5bf9\u8c01\u8bf4\u8bdd\uff01\u201d\u767d\u6cf0\u5b98\u5782\u624b\u9053\uff1a\u201c\u6211\u5bf9\u5e08\u5144\u8bf4\u8bdd\uff01\u6211\u95ee\u5e08\u5144\uff0c\u5e08\u5085\u5341\u5927\u6212 \u6761\u4e4b\u9996\uff0c\u8bf4\u7684\u4ec0\u4e48\uff1f\u201d\u8981\u77e5\u72ec\u81c2\u795e\u5c3c\u662f\u660e\u671d\u516c\u4e3b\uff0c\u6240\u4ee5\u5341\u5927\u6212\u6761\u4e4b\u9996\u5c31\u662f\u53cd\u6e05\u590d\u660e\uff0c\u82e5\u6709\u8c01 \u53d8\u8282\u6295\u654c\uff0c\u540c\u95e8\u7b49\u53ef\u5408\u800c\u8bdb\u4e4b\uff01\u4e86\u56e0\u542c\u4e86\uff0c\u9762\u8272\u500f\u53d8\uff01 \u3000\u3000\u767d\u6cf0\u5b98\u53c8\u903c\u4e0a\u4e00\u6b65\u9053\uff1a\u201c\u5927\u5e08\u5144\u662f\u6211\u4eec\u540c\u95e8\u4e4b\u9996\uff0c\u5e08\u5085\u6b7b\u540e\uff0c\u5927\u5e08\u5144\u5e94\u8be5\u66ff\u5e08\u5085\u884c\u9053\uff0c \u8eac\u4e3a\u540c\u95e8\u8868\u7387\u624d\u662f\uff01\u201d\u4e86\u56e0\u548c\u5c1a\u5ffd\u7136\u563b\u563b\u51b7\u7b11\uff0c\u731b\u7684\u8bf4\u9053\uff1a\u201c\u6cf0\u5b98\uff0c\u6b64\u8bdd\u7f13\u63d0\uff0c\u4f60\u6211\u5206\u522b\u6570 \u5e74\uff0c\u4f60\u7684\u529f\u592b\u5982\u4f55\uff0c\u6211\u4eca\u65e5\u8981\u8003\u4f60\u4e00\u4e0b\u3002\u201d\u767d\u6cf0\u5b98\u4e00\u6014\uff0c\u4e86\u56e0\u548c\u5c1a\u9053\uff1a\u201c\u4f60\u8fd8\u8bb0\u5f97\u5e08\u5085\u5429\u5490 \u5417\uff1f\u201d\u72ec\u81c2\u795e\u5c3c\u95e8\u4e0b\uff0c\u5c0a\u5351\u4e4b\u5206\u751a\u4e25\uff0c\u6c5f\u6d77\u4e03\u4fa0\uff08\u5728\u56db\u5a18\u672a\u51fa\u4e4b\u524d\uff09\u5728\u6c5f\u6e56\u5404\u81ea\u95ef\u4e07\u4e4b\u540e\uff0c \u90a3\u65f6\u4e86\u56e0\u548c\u5c1a\u6076\u8ff9\u672a\u5f70\uff0c\u72ec\u81c2\u795e\u5c3c\u8981\u4ed6\u7ecf\u5e38\u8003\u6838\u516d\u4e2a\u5e08\u5f1f\u7684\u6b66\u529f\u4e1a\u7ee9\uff0c\u4e86\u56e0\u529f\u592b\u6700\u9ad8\uff0c\u8fd8\u5e38 \u66ff\u5e08\u4f20\u6280\uff0c\u540e\u6765\u4e86\u56e0\u4e0e\u574f\u4eba\u52fe\u7ed3\uff0c\u8ff7\u4e8e\u9152\u8272\uff0c\u4fbf\u61d2\u5f97\u8003\u6838\u5e08\u5f1f\u4eec\u7684\u6b66\u529f\u4e86\u3002 \u3000\u3000\u767d\u6cf0\u5b98\u89c1\u4e86\u56e0\u4e0d\u7b54\u81ea\u5df1\u8d28\u95ee\uff0c\u53cd\u800c\u201c\u4ee5\u5b50\u4e4b\u77db\uff0c\u653b\u5b50\u4e4b\u76fe\u201d\uff0c\u62ac\u51fa\u5e08\u5085\u9057\u8a00\u3002\u867d\u7136\u6c14 \u6124\uff0c\u5374\u662f\u65e0\u53ef\u5982\u4f55\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u529f\u592b\uff0c\u5927\u534a\u662f\u5e08\u5144\u6240\u4f20\uff0c\u4f46\u51ed\u8003\u6838\uff01\u201d\u4e86\u56e0\u51b7\u7b11\u9053\uff1a \u201c\u4f60\u77e5\u9053\u5c31\u597d\u4e86\uff0c\u4f60\u4e14\u628a\u4f60\u8fd9\u51e0\u5e74\u6240\u7ec3\u6700\u5f97\u610f\u7684\u529f\u592b\u6f14\u7ed9\u6211\u770b\uff01\u201d\u767d\u6cf0\u5b98\u5e94\u4e86\u4e00\u58f0\uff0c\u8d70\u51fa\u5e73 \u53f0\uff0c\u5bf9\u9c7c\u4eae\u5927\u738b\u9053\uff1a\u201c\u4ead\u5b50\u5916\u5c71\u8336\u76db\u5f00\uff0c\u6211\u60f3\u6458\u4e0b\u51e0\u6735\uff01\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u4f60\u6458\u4fbf\u662f\uff01\u201d\u767d\u6cf0\u5b98 \u9053\uff1a\u201c\u5404\u4f4d\u8bf7\u968f\u6211\u5230\u4ead\u5b50\u5916\u770b\u82b1\uff0c\u770b\u90a3\u6735\u5408\u610f\u7684\uff0c\u6211\u518d\u6458\u4e0b\u3002\u201d\u4f17\u4eba\u4e45\u95fb\u6c5f\u5357\u516b\u4fa0\u5a01\u540d\uff0c\u8981 \u770b\u767d\u6cf0\u5b98\u7ec3\u7684\u662f\u4ec0\u4e48\u529f\u592b\uff0c\u4e00\u62e5\u51fa\u5916\u3002\u4ead\u5b50\u5468\u56f4\u90fd\u662f\u7ea2\u8273\u8273\u7684\u5c71\u8336\uff0c\u767d\u6cf0\u5b98\u4e00\u8def\u770b\u4e00\u8def\u54c1 \u8bc4\uff0c\u4f17\u4eba\u9009\u4e86\u5341\u516d\u6735\uff0c\u767d\u6cf0\u5b98\u4e00\u4e00\u4f5c\u4e86\u8bb0\u53f7\uff0c\u5374\u5e76\u4e0d\u5f53\u573a\u6458\u4e0b\uff0c\u53c8\u56de\u5230\u4ead\u4e2d\uff0c\u536b\u626c\u5a01\u9053\uff1a \u201c\u600e\u4e48\u4e0d\u7ec3\u4e86\u5417\uff1f\u201d\u767d\u6cf0\u5b98\u5ffd\u5bf9\u9c7c\u4eae\u8bf4\u9053\uff1a\u201c\u8bf7\u628a\u706f\u706b\u6682\u65f6\u5ab3\u706d\uff01\u201d\u9c7c\u4eae\u9762\u8272\u8fdf\u7591\uff0c\u4e86\u56e0\u548c \u5c1a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6709\u6211\u5728\u6b64\uff0c\u8c05\u4ed6\u4e0d\u6562\u6697\u7b97\uff01\u201d\u9c7c\u4eae\u628a\u624b\u4e00\u6325\uff0c\u706f\u706b\u5168\u706d\uff0c\u4e0b\u5f26\u6708\u8272\u672c\u5c31\u6726 \u80e7\uff0c\u8fd9\u65f6\u6708\u4eae\u53c8\u6070\u5de7\u8eb2\u8fdb\u4e91\u4e2d\uff0c\u4ead\u4e2d\u6f06\u9ed1\u4e00\u7247\uff0c\u5ffd\u542c\u5f97\u55e4\u55e4\u58f0\u54cd\uff0c\u4f17\u4eba\u8fde\u5fd9\u85cf\u5934\u7f29\u9888\uff0c\u9632\u662f \u6697\u5668\u3002\u6f06\u9ed1\u4e2d\u767d\u6cf0\u5bab\u53eb\u9053\uff1a\u201c\u8bf7\u628a\u706f\u706b\u91cd\u660e\uff01\u201d\u7247\u523b\u4e4b\u540e\uff0c\u590d\u4eae\u5982\u767d\u663c\u3002\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u5404\u4f4d \u8bf7\u968f\u6211\u51fa\u53bb\u6458\u82b1\u3002\u201d\u548c\u4f17\u4eba\u51fa\u5230\u4ead\u5916\uff0c\u53ea\u89c1\u5468\u56f4\u5730\u4e0a\u843d\u4e0b\u5341\u51e0\u6735\u7ea2\u8273\u8273\u7684\u5c71\u8336\u82b1\uff0c\u62ff\u8d77\u4e00 \u770b\uff0c\u90fd\u662f\u521a\u624d\u505a\u4e86\u8bb0\u53f7\u7684\u82b1\uff0c\u62ff\u6765\u4e00\u6570\uff0c\u4e0d\u591a\u4e0d\u5c11\uff0c\u6b63\u662f\u5341\u4e03\u6735\u3002\u5728\u5ea7\u7684\u90fd\u662f\u6c5f\u6e56\u8c6a\u5ba2\uff0c\u7cbe \u719f\u6697\u5668\u529f\u592b\uff0c\u89c1\u4e86\u8fd9\u624b\u795e\u6280\uff0c\u4e5f\u7981\u4e0d\u4f4f\u5f20\u53e3\u77eb\u820c\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u8981\u77e5\u5728\u660f\u591c\u4e4b\u4e2d\uff0c\u53d6\u51c6\u5df2 \u96be\uff0c\u4f55\u51b5\u90a3\u4e9b\u82b1\u4e0d\u662f\u805a\u5728\u4e00\u679d\uff0c\u800c\u662f\u5206\u6563\u5728\u82b1\u4e1b\u4e4b\u4e2d\uff0c\u6563\u5728\u4ead\u5b50\u56db\u5468\u3002\u767d\u6cf0\u5b98\u7adf\u7136\u5728\u4ead\u5b50\u4e4b \u4e2d\u9694\u7740\u7a97\u6237\uff0c\u4e00\u4e00\u5c06\u82b1\u6253\u843d\u3002\u8fd9\u79cd\u6697\u5668\u529f\u592b\uff0c\u771f\u662f\u51fa\u795e\u5165\u5316\uff01 \u3000\u3000\u767d\u6cf0\u5b98\u8fd9\u51e0\u5e74\u6765\u82e6\u7ec3\u6697\u5668\u6885\u82b1\u9488\uff0c\u539f\u5c31\u662f\u51c6\u5907\u4e07\u4e00\u5e08\u5144\u80cc\u8282\uff0c\u81ea\u5df1\u6b66\u529f\u8fdc\u4e0d\u53ca\u4ed6\uff0c\u5c31\u7528 \u6697\u5668\u8865\u6b66\u529f\u4e4b\u4e0d\u8db3\u3002\u8fd9\u65f6\u795e\u6280\u4e00\u663e\uff0c\u5782\u624b\u9000\u4e0b\uff0c\u606d\u656c\u8bf4\u9053\uff1a\u201c\u8fd9\u624b\u6697\u5668\uff0c\u4e0d\u77e5\u6210\u4e0e\u4e0d\u6210\uff1f\u8fd8 \u671b\u5e08\u5144\u6307\u70b9\u3002\u201d\u4ed6\u53e3\u867d\u8c26\u900a\uff0c\u5fc3\u5b9e\u5f97\u610f\uff0c\u90a3\u6599\u4e86\u56e0\u6b63\u773c\u4e5f\u4e0d\u77a7\u4ed6\u4e00\u4e0b\uff0c\u5927\u523a\u523a\u7684\u8bf4\u9053\uff1a\u201c\u4e0d \u6210\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u6ee1\u5ea7\u5931\u8272\uff0c\u90fd\u4ee5\u4e3a\u4e86\u56e0\u53e3\u51fa\u5927\u8a00\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u7b95\u8c46\u76f8\u714e\uff0c\u540c\u95e8\u8f83\u6280\uff0c\u5929\u5916\u6709\u5929\uff0c\u81ea\u6127\u4e0d\u5982\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u516b\u56de\u3000\u7b11\u50b2\u5b64\u5cf0\u3000\u5355\u9a91\u6765\u95ef\u5e2d\u3000\u5251\u60ca\u5de8\u76d7\u3000\u4e00\u5973\u663e\u795e\u5a01 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u516b\u56de \u7b11\u50b2\u5b64\u5cf0\u3000\u5355\u9a91\u6765\u95ef\u5e2d \u5251\u60ca\u5de8\u76d7\u3000\u4e00\u5973\u663e\u795e\u5a01 \u3000\u3000\u51cc\u4e91\u5c9b\u4e3b\u536b\u626c\u5a01\u6043\u719f\u5356\u719f\uff0c\u7b11\u9053\uff1a\u201c\u4e86\u56e0\u5927\u5e08\uff0c\u4ee4\u5e08\u5f1f\u6697\u5668\u529f\u592b\uff0c\u795e\u4e4e\u5176\u6280\uff0c\u6211\u4eec\u4e0d\u4f46 \u89c1\u6240\u672a\u89c1\uff0c\u800c\u4e14\u95fb\u6240\u672a\u95fb\uff0c\u5927\u5e08\u8fd8\u8bf4\u4ed6\u4e0d\u6210\uff0c\u672a\u514d\u592a\u4e0d\u516c\u9053\u4e86\u5427\u3002\u201d\u4e86\u56e0\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a \u201c\u4f60\u771f\u662f\u5c11\u89c1\u591a\u602a\uff01\u201d\u8f6c\u8fc7\u8eab\u6765\u5bf9\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u82b1\u662f\u6b7b\u7684\uff0c\u4eba\u662f\u6d3b\u7684\uff01\u4f60\u7684\u6885\u82b1\u9488\u53ea\u80fd\u6253\u4fd7 \u5b50\u51e1\u592b\uff0c\u6b66\u529f\u7a0d\u9ad8\u7684\u4eba\u4f60\u5c31\u6253\u4ed6\u4e0d\u7740\uff01\u201d\u767d\u6cf0\u5b98\u53e3\u4e2d\u5e94\u9053\uff1a\u201c\u5e08\u5144\u8bf4\u7684\u662f\u3002\u201d\u5fc3\u91cc\u5374\u4e07\u5206\u4e0d \u670d\uff01\u522b\u4eba\u6253\u6885\u82b1\u9488\uff0c\u6700\u591a\u4e0d\u8fc7\u4e09\u4e08\uff0c\u4ed6\u7684\u5374\u53ef\u6253\u5230\u4e94\u4e08\u6709\u4f59\uff0c\u800c\u4e14\u53ef\u968f\u610f\u6253\u7a74\u523a\u76ee\uff0c\u4ee4\u4eba\u9632 \u4e0d\u80dc\u9632\uff0c\u5927\u5e08\u5144\u5374\u8bf4\u4ed6\u7684\u6697\u5668\u4e0d\u5207\u5b9e\u7528\uff0c\u5982\u4f55\u80af\u670d\uff1f \u3000\u3000\u4e86\u56e0\u548c\u5c1a\u5411\u767d\u6cf0\u5b98\u77a7\u4e86\u4e00\u4e0b\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u670d\u5417\uff1f\u6211\u7ad9\u51fa\u6765\uff0c\u7ed9\u4f60\u6253\u770b\uff01\u201d\u767d\u6cf0\u5b98 \u9053\uff1a\u201c\u5c0f\u5f1f\u4e0d\u6562\uff01\u201d\u4e86\u56e0\u51b7\u7b11\u9053\uff1a\u201c\u521a\u624d\u8fd8\u62ac\u51fa\u5e08\u5085\u6212\u6761\uff0c\u73b0\u5728\u53c8\u4e0d\u6562\u4e86\uff01\u518d\u8bf4\u4f60\u8fd9\u6837\u5fae\u672b \u4e4b\u6280\uff0c\u5982\u4f55\u4f24\u5f97\u4e86\u6211\uff1f\u201d\u767d\u6cf0\u5b98\u7ed9\u4ed6\u4e00\u6fc0\uff0c\u6012\u6c14\u53c8\u751f\uff0c\u5fc3\u60f3\uff1a\u770b\u6765\u5927\u5e08\u5144\u662f\u7518\u5fc3\u6295\u654c\u4e86\uff0c\u4ed6 \u65e2\u5982\u6b64\uff0c\u4fbf\u662f\u6211\u7684\u654c\u4eba\uff0c\u8fd8\u4e0e\u4ed6\u8bba\u4ec0\u4e48\u5144\u5f1f\u4e4b\u60c5\uff1f\u5f53\u4e0b\u62fc\u51fa\u5e73\u53f0\uff0c\u53eb\u9053\uff1a\u201c\u90a3\u4e48\u5e08\u5144\u8bf7\u7559\u795e \u4e86\uff01\u201d\u4e86\u56e0\u6b65\u51fa\u5e73\u53f0\u4e2d\u592e\uff0c\u5ea7\u4e0a\u5bbe\u5ba2\uff0c\u90fd\u9000\u5230\u5899\u8fb9\uff0c\u4e86\u56e0\u7985\u6756\u4e00\u987f\uff0c\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u62db\u624b\u95ee\u9c7c\u4eae\u9053\uff1a\u201c\u4f60\u6709\u73b0\u6210\u7684\u6697\u5668\u5417\uff1f\u201d\u9c7c\u4eae\u7b11\u9053\uff1a\u201c\u51e4\u5c3e\u7f25\u3001\u94c1\u8521\u85c9\u3001\u83e9\u63d0\u5b50\u3001\u98de\u8757\u77f3\u3001 \u67f3\u53f6\u5200\u3001\u6ca1\u7fbd\u7bad\uff0c\u5e94\u6709\u5c3d\u6709\uff0c\u4f60\u8981\u4ec0\u4e48\uff1f\u201d\u4e86\u56e0\u9053\uff1a\u201c\u90fd\u8981\uff0c\u4f60\u53eb\u4eba\u62ac\u4e24\u7ba9\u6765\uff01\u201d\u9c7c\u4eae\u5927\u738b \u679c\u7136\u53eb\u4eba\u62ac\u4e86\u4e24\u7ba9\uff0c\u4e86\u56e0\u53eb\u653e\u4e00\u7ba9\u5728\u767d\u6cf0\u5b98\u9762\u524d\uff0c\u53e6\u5916\u4e00\u7ba9\u5219\u5206\u7ed9\u536b\u58eb\uff0c\u8bf4\u9053\uff1a\u201c\u6cf0\u5b98\uff0c\u4f60 \u6885\u82b1\u9488\u6253\u5b8c\u4e86\uff0c\u53ef\u4ee5\u53d6\u7ba9\u4e2d\u6697\u5668\uff01\u201d\u767d\u6cf0\u5b98\u624d\u77e5\u9053\u8fd9\u7ba9\u6697\u5668\u7adf\u662f\u7ed9\u81ea\u5df1\u51c6\u5907\u7684\uff0c\u53c8\u6c14\u53c8\u6012\u3002 \u3000\u3000\u4e86\u56e0\u548c\u5c1a\u7ed3\u675f\u505c\u5f53\uff0c\u7985\u6756\u4e00\u6325\uff0c\u4f7f\u4e2a\u96ea\u82b1\u76d6\u9876\uff0c\u987f\u65f6\u547c\u547c\u98ce\u54cd\uff0c\u5e2d\u4e0a\u5de8\u70db\u5168\u706d\uff0c\u7eb1\u706f\u91cc \u706f\u5149\u4e5f\u81ea\u6447\u66f3\u4e0d\u5b9a\uff0c\u4e86\u56e0\u53eb\u9053\uff1a\u201c\u4f60\u6253\u6765\u5427\uff01\u201d\u767d\u6cf0\u5b98\u628a\u624b\u4e00\u626c\uff0c\u53ea\u542c\u5f97\u55e4\u55e4\u6570\u58f0\uff0c\u6885\u82b1\u9488 \u5982\u77f3\u6295\u5927\u6d77\uff0c\u4e86\u56e0\u548c\u5c1a\u4e00\u6839\u7985\u6756\u821e\u6210\u4e00\u4e2a\u5927\u5708\uff0c\u98ce\u96e8\u4e0d\u900f\u3002\u767d\u6cf0\u5b98\u5fc3\u60f3\uff1a\u4ee5\u5e08\u5144\u7684\u529f\u529b\uff0c\u6885 \u82b1\u9488\u7684\u786e\u96be\u4ee5\u6253\u8fdb\uff0c\u6b63\u60f3\u5999\u6cd5\uff0c\u4e86\u56e0\u6756\u6cd5\u5ffd\u6162\uff0c\u4e1c\u4e00\u6307\uff0c\u897f\u4e00\u6307\uff0c\u95e8\u6237\u5927\u5f00\u2014\u2014\u8fc2\u7f13\u4e4b\u6781\uff0c \u767d\u6cf0\u5b98\u89c1\u6709\u9699\u53ef\u4e58\u987f\u65f6\u53cc\u624b\u8fde\u626c\uff0c\u4e00\u5927\u628a\u4e00\u5927\u628a\u7684\u6885\u82b1\u9488\uff0c\u65e0\u5149\u65e0\u58f0\uff0c\u9aa4\u5982\u6025\u96e8\uff0c\u53d1\u5982\u98de \u8757\uff0c\u6362\u4e00\u4e2a\u4eba\u6015\u4e0d\u88ab\u522e\u9488\u5c04\u6210\u523a\u7325\uff01\u4e86\u56e0\u548c\u5c1a\u7acb\u5728\u5706\u5708\u4e2d\u5fc3\uff0c\u5e76\u4e0d\u79fb\u52a8\u534a\u6b65\uff0c\u7985\u6756\u4e5f\u4e0d\u52a0 \u5feb\uff0c\u98de\u9488\u53d1\u51fa\uff0c\u7adf\u4e1d\u6beb\u4e0d\u95fb\u649e\u6fc0\u4e4b\u58f0\uff0c\u98de\u8fd1\u7985\u6756\u6240\u53ca\u7684\u5706\u5708\uff0c\u4fbf\u4f3c\u6ce5\u725b\u5165\u6d77\uff0c\u8e2a\u8ff9\u5168\u65e0\uff0c\u767d \u6cf0\u5b98\u4e0d\u89c9\u5927\u60ca\uff0c\u5fc3\u60f3\uff1a\u6885\u82b1\u9488\u529b\u5c0f\uff0c\u518d\u6362\u5176\u4ed6\u6697\u5668\u8bd5\u770b\uff0c\u72e0\u4e86\u72e0\u5fc3\uff0c\u5728\u7ba9\u4e2d\u6293\u8d77\u5404\u5f0f\u6697\u5668\uff0c \u8fde\u73af\u6512\u51fb\uff0c\u4e86\u56e0\u548c\u5c1a\u5927\u7b11\u9053\uff1a\u201c\u8fd8\u672a\u591f\u75db\u5feb\uff0c\u9c7c\u4eae\u5927\u738b\uff0c\u8bf7\u4f60\u624b\u4e0b\u628a\u90a3\u7ba9\u6697\u5668\uff0c\u4e5f\u4e00\u9f50\u6253 \u6765\uff01\u201d\u9c7c\u4eae\u628a\u624b\u4e00\u6325\uff0c\u98de\u5200\u98de\u5f39\u4ee5\u53ca\u5404\u5f0f\u98de\u9556\u540c\u65f6\u50cf\u96e8\u70b9\u822c\u6512\u51fb\u8fc7\u6765\uff0c\u53ea\u89c1\u4e86\u56e0\u6756\u4e0a\u706b\u82b1\u4e71 \u5c04\uff0c\u53ee\u53ee\u5f53\u5f53\u4e00\u9635\u4ea4\u54cd\uff0c\u767d\u6cf0\u5b98\u7684\u6885\u82b1\u9488\u4e5f\u5939\u5728\u5404\u5f0f\u6697\u5668\u4e4b\u5185\u6253\u53bb\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u6697\u5668\u8d8a\u6765\u8d8a \u5c11\uff0c\u975e\u4f46\u767d\u6cf0\u5b98\u7684\u6885\u82b1\u9488\u5df2\u4e0d\u5269\u4e00\u6839\uff0c\u4e24\u5927\u7ba9\u7684\u6697\u5668\u4e5f\u5168\u90fd\u7528\u5149\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u706f\u706b\u901a\u660e\uff0c\u6ee1\u5ea7\u60ca\u547c\uff0c\u5bbe\u5ba2\u7eb7\u7eb7\u6d8c\u51fa\u770b\u4e2a\u7a76\u7adf\uff0c\u8fd9\u4e00\u770b\uff0c\u4e0d\u7531\u5f97\u9f50\u58f0\u53eb\u9053\uff1a\u201c\u4e86\u56e0 \u5927\u5e08\uff0c\u771f\u662f\u7edd\u4e16\u65e0\u53cc\u7684\u5929\u4eba\uff0c\u6211\u4eec\u8877\u5fc3\u62dc\u670d\uff01\u201d\u4e86\u56e0\u548c\u5c1a\u6625\u98ce\u6ee1\u9762\uff0c\u6a2a\u6756\u5140\u7acb\u3002\u5730\u4e0a\u4e00\u5927\u5806 \u7834\u94dc\u70c2\u94c1\uff0c\u4e24\u5927\u7ba9\u6697\u5668\u6210\u4e86\u65e0\u6570\u788e\u7247\uff0c\u7985\u6756\u5c16\u4e0a\u7ed3\u6210\u9edd\u9ed1\u7684\u4e00\u4e2a\u5706\u7403\uff0c\u767d\u6cf0\u5b98\u770b\u4e86\u4f5c\u58f0\u4e0d \u5f97\uff0c\u90a3\u4e9b\u666e\u901a\u6697\u5668\u7ed9\u4ed6\u6756\u98ce\u9707\u6210\u788e\u7247\uff0c\u4e5f\u8fd8\u7f62\u4e86\uff0c\u81ea\u5df1\u7684\u6885\u82b1\u9488\uff0c\u4efd\u91cf\u6781\u8f7b\uff0c\u51e0\u4e4e\u53ef\u8bf4\u5f97\u662f \u65e0\u5f71\u65e0\u5f62\uff0c\u4e14\u89c1\u9699\u5373\u5165\uff0c\u7adf\u7136\u4e5f\u7ed9\u4ed6\u50cf\u78c1\u77f3\u5438\u4f4f\u4e00\u822c\uff0c\u4e00\u652f\u4e0d\u5269\u90fd\u5438\u5728\u7985\u6756\u5c16\u4e0a\uff01\u4e86\u56e0\u5fae\u4e00 \u5410\u6c14\uff0c\u54d7\u5566\u5566\u4e00\u9635\u602a\u54cd\uff0c\u7985\u6756\u5c16\u4e0a\u7684\u5706\u7403\u5316\u6210\u7c89\u5c51\uff0c\u7eb7\u7eb7\u98d8\u4e0b\uff0c\u5806\u5728\u5730\u4e0a\u3002\u4f17\u4eba\u76ee\u77aa\u53e3\u5446\uff0c \u4e86\u56e0\u548c\u5c1a\u6a2a\u6756\u72c2\u7b11\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6cf0\u5b98\uff0c\u4f60\u670d\u4e5f\u4e0d\u670d\uff01\u201d\u767d\u6cf0\u5b98\u5e94\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u5144\u6b66\u529f\u76d6 \u4e16\u3002\u5c0f\u5f1f\u5c82\u6562\u4e0d\u670d\uff01\u53ea\u662f\u2026\u2026\u201d\u4e86\u56e0\u622a\u7740\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u4ec0\u4e48\uff1f\u662f\u4e0d\u662f\u9664\u4e86\u6b66\u529f\u4e4b\u5916\uff0c\u4f60\u8fd8\u6709 \u4e0d\u670d\u4e4b\u5904\uff01\u201d\u767d\u6cf0\u5b98\u633a\u80f8\u8bf4\u9053\uff1a\u201c\u82e5\u7136\u5e08\u5144\u8fdd\u80cc\u5e08\u5085\u5927\u6212\uff0c\u5c0f\u5f1f\u4e07\u96be\u670d\u4ece\uff01\u201d\u4e86\u56e0\u548c\u5c1a \u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5927\u6012\u8bf4\u9053\uff1a\u201c\u6211\u8981\u4f60\u660e\u5927\u52bf\uff0c\u77e5\u987a\u9006\uff0c\u8ddf\u7740\u9c7c\u4eae\u5927\u738b\uff0c\u6276\u52a9\u56db\u7687\u5b50\u767b\u4f4d\u3002\u4e0d \u4f46\u662f\u4f60\uff0c\u4e00\u4f17\u540c\u95e8\u90fd\u8981\u542c\u6211\u7684\u8bdd\u3002\u201d\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u5e08\u5085\u7684\u5927\u6212\u5e08\u5144\u5c31\u4e0d\u7406\u4e86\uff1f\u201d\u4e86\u56e0\u51b7\u7b11 \u9053\uff1a\u201c\u4ec0\u4e48\u6212\u6761\uff1f\u5e08\u5085\u65e2\u6b7b\uff0c\u552f\u6211\u72ec\u5c0a\uff01\u4f60\u82e5\u4e0d\u4f9d\uff0c\u5c3d\u7ba1\u9080\u96c6\u540c\u95e8\u6765\u4e0e\u6211\u8bb2\u7406\uff01\u6d12\u5bb6\u7684\u9053 \u7406\uff0c\u5c31\u662f\u8fd9\u6839\u7985\u6756\uff01\u767d\u6cf0\u5b98\uff0c\u4f60\u597d\u751f\u5927\u80c6\uff0c\u9876\u649e\u5e08\u5144\uff0c\u4f60\u8dea\u4e0b\u6765\uff0c\u5148\u9886\u5bb6\u6cd5\uff01\u201d\u767d\u6cf0\u5b98\u53c8\u6c14 \u53c8\u6025\uff0c\u51e0\u5e74\u4e0d\u89c1\uff0c\u4e86\u56e0\u529f\u529b\u53c8\u9ad8\u4e86\u8bb8\u591a\uff0c\u770b\u4ed6\u7985\u6756\u5438\u6697\u5668\u7684\u529f\u592b\uff0c\u5185\u529f\u5df2\u5230\u4e86\u767b\u5cf0\u9020\u6781\u7684\u5730 \u6b65\uff0c\u83ab\u8bf4\u81ea\u5df1\u8fdc\u975e\u4ed6\u7684\u5bf9\u624b\uff0c\u5c31\u662f\u9f50\u96c6\u540c\u95e8\uff0c\u4e5f\u672a\u5fc5\u6597\u5f97\u8fc7\u4ed6\uff0c\u8fd9\u773c\u524d\u4e8f\u662f\u5403\u5b9a\u4e86\uff0c\u4e86\u56e0\u548c \u5c1a\u53c8\u65a5\u9053\uff1a\u201c\u767d\u6cf0\u5b98\uff0c\u4f60\u8fd8\u4e0d\u4e0b\u8dea\uff1f\u201d\u5ffd\u5730\u4e00\u58f0\u51b7\u7b11\uff0c\u6709\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u597d\u4e0d\u8981\u8138\uff01\u5e08\u5085\u5c38 \u9aa8\u672a\u5bd2\uff0c\u5c31\u6765\u6b3a\u538b\u5e08\u5f1f\uff01\u201d\u4e86\u56e0\u53cc\u773c\u7741\u5982\u94dc\u94c3\uff0c\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u5728\u6d12\u5bb6\u9762\u524d\u65e0\u793c\uff01\u201d\u8bdd\u58f0\u672a \u6bd5\uff0c\u5e2d\u4e0a\u7b11\u541f\u541f\u7684\u8df3\u51fa\u4e00\u4eba\uff0c\u4fcf\u58f0\u8bf4\u9053\uff1a\u201c\u72ec\u81c2\u795e\u5c3c\u8981\u6211\u6765\u7ba1\u6559\u4f60\u8fd9\u4e0d\u77e5\u6b7b\u6d3b\u7684\u5b7d\u5f92\uff01\u201d\u6b64 \u8a00\u4e00\u51fa\uff0c\u5168\u5ea7\u5bbe\u5ba2\u65e0\u4e0d\u8272\u53d8\uff0c\u6240\u6709\u76ee\u5149\u90fd\u96c6\u4e2d\u5728\u6b64\u4eba\u8eab\u4e0a\uff01\u6b64\u4eba\u7709\u6e05\u76ee\u79c0\uff0c\u770b\u6765\u53ea\u662f\u5e74\u5c06\u5f31 \u51a0\u7684\u6587\u5f31\u4e66\u751f\uff01\u6b63\u662f\u4e0e\u767d\u6cf0\u5b98\u540c\u6765\u7684\u674e\u53cc\u53cc\uff01 \u3000\u3000\u4e86\u56e0\u548c\u5c1a\u4e00\u89c1\u51fa\u6765\u7684\u7adf\u662f\u4e2a\u65e0\u540d\u540e\u8f88\uff0c\u867d\u7136\u66b4\u6012\uff0c\u5374\u4e0d\u80fd\u9a6c\u4e0a\u53d1\u4f5c\u3002\u4e8b\u56e0\u81ea\u5df1\u4e43\u6c5f\u5357\u516b \u4fa0\u4e4b\u9996\uff0c\u5c82\u80fd\u4e0e\u4e00\u540e\u8f88\u8f83\u91cf\uff1f\u6012\u706b\u6291\u4e0b\uff0c\u53cd\u51b7\u7b11\u9053\uff1a\u201c\u72ec\u81c2\u795e\u5c3c\u4f1a\u6258\u4f60\u8fd9\u5c0f\u5b50\u6765\u7ba1\u6559\u6211\u3002\u6211 \u5012\u8981\u95ee\u4f60\u662f\u4f55\u4eba\u5f1f\u5b50\uff1f\u4f55\u65e5\u51fa\u5e08\uff1f\u4f60\u4e73\u81ed\u672a\u5e72\uff0c\u5c31\u6562\u80e1\u8a00\u4e71\u8bed\uff01\u6211\u8981\u628a\u4f60\u7684\u5e08\u5085\u6349\u6765\uff0c\u6cbb\u4ed6 \u4e2a\u4e0d\u7ba1\u95e8\u4eba\u4e4b\u7f6a\uff01\u201d\u4f17\u4eba\u8f70\u7136\u5927\u7b11\uff0c\u7b11\u8fd9\u6587\u5f31\u5c11\u5e74\u80e1\u4e71\u5439\u725b\uff0c\u80c6\u6562\u6500\u9644\u72ec\u81c2\u795e\u5c3c\uff0c\u8981\u7ba1\u4e86\u56e0 \u548c\u5c1a\u3002\u4e86\u56e0\u6b66\u529f\u76d6\u4e16\uff0c\u72ec\u81c2\u795e\u5c3c\u82e5\u771f\u6709\u9057\u547d\uff0c\u6258\u4eba\u7ba1\u4ed6\uff0c\u6258\u7684\u4e5f\u8be5\u662f\u524d\u8f88\u9ad8\u4eba\uff0c\u6b66\u6797\u5b97\u7956\uff0c \u90a3\u4f1a\u6258\u8fd9\u6837\u4e00\u4e2a\u4e73\u81ed\u672a\u5e72\u7684\u6587\u5f31\u4e66\u751f\uff1f \u3000\u3000\u674e\u53cc\u53cc\u5728\u8f70\u7b11\u58f0\u4e2d\u795e\u8272\u81ea\u82e5\uff0c\u7b11\u58f0\u4e00\u6b47\uff0c\u53c8\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e86\u56e0\uff0c\u4f60\u6562\u6349\u6211\u7684\u5e08\u5085\uff1f\u7b97\u4f60 \u6709\u5929\u5927\u672c\u9886\uff0c\u4f60\u89c1\u6211\u7684\u5e08\u5085\u4e5f\u8981\u8dea\u4e0b\u8bf7\u7f6a\uff01\u201d\u4e86\u56e0\u6012\u9053\uff1a\u201c\u4f60\u5e08\u5085\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u9650\u4f60\u4e09\u6708\uff0c \u628a\u4ed6\u53eb\u6765\u89c1\u6211\uff0c\u4f60\u6562\u4e0d\u9075\u547d\uff0c\u6211\u628a\u4f60\u51cc\u8fdf\u788e\u5250\uff01\u4f60\u72af\u4e86\u6211\uff0c\u5c31\u662f\u72af\u4e86\u960e\u738b\u8001\u5b50\uff0c\u9003\u5230\u5929\u8fb9\u4e5f \u9003\u4e0d\u4e86\uff01\u4e09\u4e2a\u6708\u540e\u628a\u4f60\u5e08\u5085\u53eb\u5230\u7530\u6a2a\u5c9b\u6765\uff0c\u542c\u6e05\u695a\u6ca1\u6709\u3002\u201d\u674e\u53cc\u53cc\u5937\u7136\u7b11\u9053\uff1a\u201c\u6211\u5e08\u5085\u6b64\u65f6 \u5c31\u5728\u6b64\u5730\uff0c\u4f55\u987b\u4e09\u6708\u4e4b\u671f\uff01\u201d\u4e86\u56e0\u53cc\u76ee\u4e00\u626b\u5168\u573a\uff0c\u53eb\u9053\uff1a\u201c\u51fa\u6765\uff01\u201d\u674e\u53cc\u53cc\u53f3\u624b\u9ad8\u4e3e\uff0c\u65a5 \u9053\uff1a\u201c\u8dea\u4e0b\uff01\u201d\u624b\u4e0a\u9ad8\u4e3e\u4e00\u9762\u91d1\u724c\uff0c\u4e86\u56e0\u770b\u4e86\uff0c\u9762\u8272\u5927\u53d8\uff01 \u3000\u3000\u8fd9\u91d1\u724c\u6b63\u662f\u72ec\u81c2\u795e\u5c3c\u9057\u97e7\uff0c\u4e0a\u9762\u523b\u6709\u5341\u5927\u6212\u6761\uff0c\u6c5f\u5357\u516b\u4fa0\uff0c\u5165\u95e8\u4e4b\u65e5\uff0c\u90fd\u66fe\u542c\u8fc7\u5e08\u5085\u9ad8 \u4e3e\u91d1\u724c\u5ba3\u8bfb\u6212\u6761\uff0c\u542c\u5b8c\u4e4b\u540e\uff0c\u4e5f\u90fd\u66fe\u5bf9\u91d1\u724c\u8dea\u4e0b\uff0c\u5931\u5fd7\u7688\u4f9d\uff01\u72ec\u81c2\u795e\u5c3c\u867d\u6b7b\uff0c\u9057\u5a01\u72b9\u5728\uff0c\u4e86 \u56e0\u9aa4\u89c1\u91d1\u813e\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u6076\u6c14\u5168\u6d88\uff0c\u9762\u76ae\u53d8\u8272\uff01\u4f17\u4eba\u89c1\u4e86\uff0c\u5567\u5567\u79f0\u5f02\uff0c\u770b\u6765\u6b64\u4eba\u771f\u662f\u72ec\u81c2\u795e \u5c3c\u6d3e\u6765\u7684\u4e86\uff01\u9c7c\u4eae\u4e00\u89c1\u60c5\u5f62\u4e0d\u5bf9\uff0c\u6025\u5728\u4e86\u56e0\u8033\u8fb9\u6084\u6084\u8bf4\u9053\uff1a\u201c\u5927\u5e08\u82e5\u5bf9\u6b64\u5c0f\u5b50\u5c48\u670d\uff0c\u5c82\u4e0d\u8d3b \u7b11\u5929\u4e0b\u82f1\u96c4\uff1f\u201d \u3000\u3000\u4e86\u56e0\u4ff1\u5fc3\u4e00\u987f\uff0c\u6076\u5ff5\u53c8\u751f\uff01\u60f3\u9053\uff1a\u201c\u91d1\u724c\u867d\u662f\u5e08\u5085\u9057\u7269\uff0c\u4f46\u5e08\u5085\u5df2\u6b7b\uff0c\u5929\u4e0b\u65e0\u4eba\u80fd\u591f\u5236 \u6211\uff0c\u6015\u4ed6\u4f5c\u751a\uff1f\u201d\u674e\u53cc\u53cc\u559d\u9053\uff1a\u201c\u4f60\u771f\u6562\u6b3a\u5e08\u706d\u9053\uff1f\u8fd8\u4e0d\u8dea\u4e0b\uff01\u201d\u4e86\u56e0\u7a81\u7136\u66b4\u559d\u4e00\u58f0\uff0c\u547c\u7684 \u4e00\u638c\u9694\u5ea7\u6253\u53bb\uff0c\u8981\u628a\u91d1\u724c\u9707\u6210\u7c89\u788e\uff0c\u674e\u53cc\u53cc\u5de6\u624b\u4e00\u626c\uff0c\u53f3\u624b\u628a\u91d1\u724c\u7eb3\u5165\u6000\u5185\uff0c\u638c\u98ce\u6fc0\u8361\u4e2d\uff0c \u674e\u53cc\u53cc\u5305\u5934\u9752\u5dfe\uff0c\u7adf\u7ed9\u638c\u98ce\u63ed\u53bb\uff0c\u9732\u51fa\u6ee1\u5934\u79c0\u53d1\uff01\u767d\u6cf0\u5b98\u8df3\u8d77\u6765\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u516b\u59b9\uff01\u201d \u3000\u3000\u8fd9\u5316\u540d\u674e\u53cc\u53cc\u7684\u6587\u5f31\u5c11\u5e74\u679c\u7136\u662f\u5415\u56db\u5a18\uff01\u5510\u6653\u6f9c\u53c8\u559c\u53c8\u60ca\uff0c\u5fc3\u5934\u9e7f\u649e\uff0c\u8df3\u4e2a\u4e0d\u505c\uff01\u6697 \u9053\uff1a\u201c\u53cc\u53cc\u4e3a\u56db\uff0c\u8fd9\u53cc\u53cc\u4e4b\u540d\u5206\u660e\u662f\u5415\u56db\u5a18\u522b\u53f7\uff0c\u6211\u771f\u8822\uff0c\u8fde\u8fd9\u4e2a\u4e5f\u60f3\u4e0d\u51fa\u6765\u3002\u201d\u770b\u90a3\u5415\u56db \u5a18\u65f6\uff0c\u4ecd\u662f\u795e\u8272\u81ea\u5982\u4e0e\u4e86\u56e0\u9762\u9762\u76f8\u5bf9\uff01 \u3000\u3000\u4e86\u56e0\u8fd9\u4e00\u638c\u6ca1\u6709\u9707\u5012\u5415\u56db\u5a18\uff0c\u4e5f\u81ea\u6709\u70b9\u8be7\u5f02\uff0c\u5f53\u4e0b\u63d0\u7740\u7897\u53e3\u5927\u7684\u7985\u6756\uff0c\u8d70\u51fa\u5e2d\u6765\uff0c\u5927\u58f0 \u8bf4\u9053\uff1a\u201c\u4f60\u662f\u5e08\u5085\u5173\u95e8\u7684\u5f92\u5f1f\u5415\u56db\u5a18\u5417\uff1f\u672c\u95e8\u7d20\u91cd\u5c0a\u5351\u4e4b\u522b\uff0c\u4f60\u4eca\u65e5\u521d\u89c1\u5e08\u5144\uff0c\u4e3a\u4f55\u4e0d\u8dea\u4e0b \u884c\u793c\uff1f\u201d\u5415\u56db\u5a18\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u7136\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u4f9d\u5e08\u5085\u6212\u5f8b\uff0c\u5df2\u662f\u672c\u95e8\u53db\u5f92\uff1b\u89c1\u4e86\u91d1\u724c\uff0c \u53c8\u4e0d\u4e0b\u8dea\uff0c\u66f4\u65e0\u5c0a\u5351\u4e4b\u793c\u3002\u4f60\u8fd8\u6562\u4e0e\u6211\u8c08\u8bba\u95e8\u89c4\uff1f\u4f60\u8fd8\u6709\u9762\u8981\u505a\u6211\u7684\u5144\u957f\uff1f\u201d\u4e86\u56e0\u9762\u8272\u7531\u9752 \u8f6c\u767d\uff0c\u53c8\u7531\u767d\u8f6c\u7ea2\uff0c\u8001\u7f9e\u6210\u6012\u731b\u7136\u559d\u9053\uff1a\u201c\u5415\u56db\u5a18\uff0c\u4f60\u6562\u628a\u6211\u600e\u6837\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u8981\u9075 \u4ece\u5e08\u5085\u9057\u547d\uff0c\u7ea0\u96c6\u540c\u95e8\uff0c\u5411\u4f60\u5174\u5e08\u95ee\u7f6a\uff01\u4f60\u82e5\u4e0d\u6d17\u9762\u9769\u5fc3\uff0c\u5c31\u628a\u4f60\u9996\u7ea7\u5272\u4e0b\uff0c\u796d\u5960\u5e08\u5085\u5728\u5929 \u4e4b\u7075\uff01\u201d\u4e86\u56e0\u542c\u4e86\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u60f3\u662f\u6012\u6781\u6c14\u6781\uff0c\u53cd\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u5e08\u59b9\uff0c\u4f60\u5b66\u4e86\u51e0\u5e74\u6b66\u529f\uff0c\u5c31 \u6562\u5728\u4f60\u5e08\u5144\u9762\u524d\u653e\u8086\uff1f\u201d\u5415\u56db\u5a18\u53cd\u8eab\u4e00\u8dc3\uff0c\u8df3\u5165\u573a\u4e2d\uff0c\u53eb\u9053\uff1a\u201c\u4e86\u56e0\uff0c\u6211\u4e5f\u8981\u770b\u4f60\u5230\u5e95\u5f97\u4e86 \u5e08\u5085\u591a\u5c11\u672c\u9886\uff01\u201d \u3000\u3000\u4e86\u56e0\u548c\u5c1a\u72c2\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u7eb5\u6a2a\u534a\u4e16\uff0c\u8fd8\u6ca1\u4eba\u6562\u5728\u6211\u9762\u524d\u53eb\u9635\uff0c\u60f3\u4e0d\u5230\u5e08\u59b9\u7adf\u5411\u5e08\u5144\u6311 \u6218\uff01\u201d\u5415\u56db\u5a18\u5b9d\u5251\u51fa\u9798\uff0c\u5411\u524d\u4e00\u6307\uff0c\u65a5\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u5e08\u59b9\uff01\u201d\u4e86\u56e0\u7b11\u58f0\u4e00\u6536\uff0c\u7985\u6756\u4e00\u987f\uff0c \u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u914d\u5411\u6211\u6311\u6218\uff0c\u5728\u5ea7\u9ad8\u670b\uff0c\u54ea\u4f4d\u66ff\u6211\u628a\u8fd9\u8d31\u5a62\u62ff\u4e0b\uff01\u201d\u5e2d\u4e0a\u90fd\u662f\u6c5f\u6e56\u5de8 \u76d7\uff0c\u6b66\u6797\u9ad8\u624b\uff0c\u89c1\u5415\u56db\u5a18\u662f\u4e2a\u5c11\u5e74\u5973\u5b50\uff0c\u6599\u60f3\u6b66\u529f\u8fd8\u6d45\uff0c\u5927\u5bb6\u90fd\u60f3\u8ba8\u597d\u4e86\u56e0\uff0c\u4e0d\u7ea6\u800c\u540c\uff0c\u5907 \u62d4\u5175\u5668\uff0c\u8df3\u4e86\u51fa\u6765\uff0c\u5415\u56db\u5a18\u5b9d\u5251\u4e00\u6325\uff0c\u5bd2\u5149\u56db\u95ea\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\u4e0d\u8981\u8138\uff0c\u60f3\u7fa4\u6bb4\u4e48\uff1f\u201d\u8df3\u51fa \u6765\u7684\u90fd\u662f\u6709\u8eab\u4efd\u7684\u6c5f\u6e56\u4eba\u7269\uff0c\u542c\u4e86\u4e00\u6014\uff0c\u9f50\u9f50\u7f29\u624b\u3002\u8fd9\u5f53\u513f\uff0c\u5750\u5728\u9996\u5e2d\u7684\u54c8\u5e03\u9640\u5ffd\u7136\u53eb\u9053\uff1a \u201c\u8981\u5bf9\u9635\u7684\u5230\u5916\u9762\u53bb\uff01\u4e00\u9897\u841d\u535c\u4e00\u5934\u8471\uff0c\u8981\u4f9d\u6c5f\u6e56\u89c4\u77e9\uff0c\u4e0d\u8981\u4e71\u4e86\uff01\u201d\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53d1\u58f0\u5e76\u4e0d \u5f88\u5927\uff0c\u4f46\u5374\u649e\u5f97\u4f17\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u5c31\u50cf\u7ed9\u4eba\u7528\u53e3\u8d34\u7740\u8033\u6839\uff0c\u5927\u58f0\u547c\u559d\u4e00\u6837\uff0c\u771f\u4e2a\u662f\u5982\u96f7\u8d2f \u8033\uff0c\u5c31\u662f\u8fdc\u8fdc\u7ad9\u5728\u4ead\u5b50\u5916\u9762\u7684\u4eba\uff0c\u542c\u8d77\u6765\u4e5f\u5982\u6674\u5929\u9739\u9732\u3002\u987f\u65f6\u95f4\uff0c\u5e7f\u4ead\u5185\u3001\u51c9\u53f0\u4e0a\u90fd\u9759\u4e86\u4e0b \u6765\uff0c\u5e2d\u4e0a\u7fa4\u96c4\uff0c\u8fd9\u624d\u77e5\u9053\u54c8\u5e03\u9640\u4f4d\u5217\u4e0a\u5bbe\uff0c\u679c\u7136\u662f\u6709\u8d85\u5353\u6b66\u529f\uff0c\u4e0d\u662f\u5f92\u51ed\u52bf\u529b\u3002\u5415\u56db\u5a18\u89c1\u54c8 \u5e03\u9640\u663e\u9732\u5185\u529f\uff0c\u7528\u4f20\u97f3\u5165\u5bc6\u7684\u201c\u72ee\u5b50\u543c\u201d\u529f\u9707\u6151\u7fa4\u8c6a\uff0c\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u6b63\u60f3\u8fd8\u4ee5\u989c\u8272\uff0c\u5929\u53f6 \u6563\u4eba\u8df3\u51fa\u6765\u9053\uff1a\u201c\u54c8\u603b\u7ba1\u4e4b\u8a00\u662f\u4e5f\uff01\u4eca\u65e5\u5230\u4f1a\u8005\u90fd\u662f\u6b66\u6797\u4e4b\u96c4\uff0c\u5230\u5916\u9762\u663e\u663e\u529f\u592b\uff0c\u5b64\u5cf0\u8f83 \u6280\uff0c\u8ba9\u6211\u8fd9\u5c71\u91ce\u5339\u592b\uff0c\u5f00\u5f00\u773c\u754c\uff0c\u4e5f\u5927\u662f\u4f73\u4e8b\uff0c\u8001\u673d\u4e0d\u624d\uff0c\u613f\u4e3a\u5404\u4f4d\u82f1\u96c4\u6e05\u9053\uff01\u201d\u8bf4\u7f62\u7acb\u5728 \u573a\u5fc3\uff0c\u56db\u56f4\u4e00\u63d6\uff0c\u52b2\u98ce\u6025\u5439\uff0c\u53ea\u542c\u5f97\u73bb\u7483\u7a97\u683c\u683c\u4f5c\u54cd\uff0c\u5929\u53f6\u6563\u4eba\u72c2\u5578\u4e00\u58f0\uff0c\u63d6\u505c\u98ce\u6b62\uff0c\u770b\u90a3 \u73bb\u7483\u7a97\u6237\uff0c\u5168\u5df2\u6253\u5f00\uff0c\u5916\u9762\u843d\u82b1\u6ee1\u5730\uff0c\u8fd1\u51c9\u53f0\u5904\uff0c\u679d\u53f6\u5411\u4e24\u8fb9\u5012\u4f0f\uff0c\u7adf\u4f3c\u7528\u4eba\u5de5\u50fb\u51fa\u4e86\u4e00\u6761 \u5c0f\u5f84\u6765\uff01\u5929\u53f6\u6563\u4eba\u638c\u529b\u5389\u5bb3\u5230\u8fd9\u822c\u7530\u5730\uff0c\u5c45\u7136\u80fd\u5728\u5341\u6b65\u4e4b\u5185\uff0c\u9707\u843d\u7e41\u82b1\uff0c\u800c\u73bb\u7483\u4e0d\u788e\uff0c\u83ab\u8bf4 \u5927\u5c0f\u5be8\u4e3b\uff0c\u5c31\u662f\u4e86\u56e0\u3001\u54c8\u5e03\u9640\u3001\u5415\u56db\u5a18\u4e0e\u6d77\u4e91\u548c\u5c1a\u7b49\u9ad8\u624b\u4e5f\u9887\u611f\u610f\u5916\uff0c\u987f\u65f6\u638c\u58f0\u96f7\u52a8\u3002\u5929\u53f6 \u6563\u4eba\u5728\u638c\u58f0\u4e2d\u5f97\u610f\u6d0b\u6d0b\u548c\u9c7c\u4eae\u8d70\u51fa\u5e7f\u4ead\u3002 \u3000\u3000\u767d\u6cf0\u5b98\u89c1\u5e2d\u4e0a\u9ad8\u624b\uff0c\u4e2a\u4e2a\u90fd\u6709\u6781\u6df1\u7684\u6b66\u529f\uff0c\u4e0d\u7981\u5fc3\u60b8\uff0c\u6084\u6084\u5bf9\u5415\u56db\u5a18\u9053\uff1a\u201c\u516b\u59b9\uff0c\u4f60\u53ef \u8981\u5c0f\u5fc3\uff01\u201d\u5415\u56db\u5a18\u6b63\u5728\u76d8\u7b97\u5982\u4f55\u80fd\u5728\u9ad8\u624b\u5305\u56f4\u4e4b\u4e2d\u8131\u9669\uff0c\u81ea\u5df1\u4e5f\u89c9\u5e76\u65e0\u628a\u63e1\u3002\u5ffd\u89c1\u90a3\u4e10\u5987\u5634 \u89d2\u6302\u7740\u51b7\u7b11\uff0c\u4e5f\u8ddf\u7740\u4f17\u4eba\u51fa\u53bb\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u4e0a\u53bb\u6253\u4e2a\u62db\u547c\uff0c\u90a3\u4e10\u5987\u5df2\u94bb\u5728\u4eba\u5806\u4e4b\u4e2d\uff0c\u508d\u7740 \u5929\u53f6\u6563\u4eba\u8d70\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u8d70\u51fa\u4ead\u5b50\uff0c\u8d8a\u8fc7\u5047\u5c71\uff0c\u5c71\u811a\u662f\u4e00\u5927\u7247\u5e7f\u573a\uff0c\u573a\u4e0a\u5175\u5668\u7f57\u5217\u3002\u4f17\u4eba\u56f4\u4e86\u4e00\u4e2a\u5708\u5b50\uff0c\u5415 \u56db\u5a18\u3001\u767d\u6cf0\u5b98\u548c\u5510\u6653\u6f9c\u5750\u5728\u4e00\u5904\uff0c\u54c8\u5e03\u9640\u4e86\u56e0\u9c7c\u4eae\u5750\u5728\u5bf9\u9762\uff0c\u9c7c\u4eae\u7ad9\u51fa\u6765\u9053\uff1a\u201c\u767d\u6cf0\u5b98\u5415\u56db \u5a18\u6043\u5f3a\u72af\u4e0a\uff0c\u4e0d\u670d\u5e08\u5144\uff0c\u5b9d\u56fd\u7985\u5e08\u4e0d\u5c51\u548c\u5c0f\u8f88\u52a8\u624b\uff0c\u73b0\u5728\u8c28\u4f9d\u6b66\u6797\u89c4\u7ae0\uff0c\u4e00\u5bf9\u4e00\u51b3\u4e2a\u80dc\u8d1f\uff0c \u54ea\u4f4d\u82f1\u96c4\u66ff\u5b9d\u56fd\u7985\u5e08\u7ba1\u6559\u5c0f\u8f88\uff1f\u201d\u8bdd\u521a\u8bf4\u5b8c\uff0c\u4e86\u56e0\u5ffd\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u9c7c\u4eae\u5927\u738b\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\u6709\u4f55\u89c1\u6559\uff1f\u201d\u4e86\u56e0\u548c\u5c1a\u9053\uff1a\u201c\u6211\u66fe\u5949\u5e08\u547d\uff0c\u8d1f\u8d23\u8003\u6838\u540c\u95e8\u6b66 \u529f\uff0c\u5415\u56db\u5a18\uff0c\u4f60\u65e2\u662f\u6211\u5e08\u5085\u5173\u95e8\u5f1f\u5b50\uff0c\u53c8\u62ac\u51fa\u5e08\u5085\u6212\u6761\u4e0e\u6211\u4f5c\u5bf9\uff0c\u4eca\u5929\u521d\u89c1\uff0c\u4e0d\u62dc\u89c1\u5e08\u5144\u4e5f \u7f62\u4e86\uff0c\u4f46\u4e5f\u8be5\u628a\u6240\u5b66\u6b66\u529f\uff0c\u5148\u7ec3\u51fa\u6765\uff0c\u8ba9\u6211\u770b\u770b\uff0c\u4f60\u662f\u5426\u591f\u683c\u5217\u4e3a\u6c5f\u5357\u516b\u4fa0\uff01\u201d\u6309\u6b66\u6797\u89c4 \u77e9\uff0c\u82e5\u5e08\u5085\u6b7b\u540e\uff0c\u51fa\u4e86\u53db\u5f92\uff0c\u638c\u95e8\u4eba\u5e94\u8d1f\u6e05\u7406\u95e8\u6237\u4e4b\u8d23\u3002\u800c\u638c\u95e8\u4eba\u5219\u591a\u662f\u9996\u5f92\u3002\u82e5\u662f\u638c\u95e8\u4eba \u80cc\u53db\u672c\u95e8\uff0c\u5219\u5f53\u7531\u4f17\u95e8\u4eba\u516c\u51b3\uff0c\u5728\u5e08\u5085\u7075\u524d\u796d\u544a\uff0c\u5148\u628a\u4ed6\u9010\u51fa\u95e8\u5899\uff0c\u7136\u540e\u624d\u9e23\u9f13\u800c\u653b\u3002\u73b0\u5728 \u5415\u56db\u5a18\u9886\u6709\u5e08\u5085\u9057\u547d\uff0c\u867d\u7136\u53ef\u4ee5\u4fbf\u5b9c\u884c\u4e8b\uff1b\u4f46\u4e5f\u5f97\u7ecf\u8fc7\u8fd9\u756a\u624b\u7eed\uff0c\u662d\u544a\u5929\u4e0b\uff0c\u624d\u80fd\u5426\u5b9a\u4e86\u56e0 \u7684\u672c\u95e8\u8eab\u4efd\uff0c\u5426\u5219\u6b66\u6797\u540c\u9053\uff0c\u4ecd\u7136\u627f\u8ba4\u4e86\u56e0\u662f\u6c5f\u5357\u516b\u4fa0\u4e4b\u9996\u7684\u3002\u73b0\u5728\u4e86\u56e0\u5c31\u4ee5\u5927\u5e08\u5144\u8eab\u4efd\uff0c \u8981\u8003\u6838\u5415\u56db\u5a18\u6b66\u529f\u3002\u5728\u4e86\u56e0\u5fc3\u610f\uff0c\u662f\u6000\u7591\u5e08\u5085\u504f\u5fc3\uff0c\u4e0d\u77e5\u6709\u4ec0\u4e48\u79d8\u4f20\u6b66\u529f\u6388\u7ed9\u4e86\u5415\u56db\u5a18\uff0c\u60f3 \u5148\u770b\u770b\u5979\u7684\u529f\u529b\u3002\u5728\u767d\u6cf0\u5b98\u542c\u6765\uff0c\u5219\u662f\u6545\u610f\u5201\u96be\uff0c\u6298\u8fb1\u81ea\u5df1\u4e0d\u7b97\uff0c\u53c8\u8981\u6298\u8fb1\u5e08\u59b9\u3002\u5728\u4f17\u4eba\u542c \u6765\uff0c\u5219\u4e86\u56e0\u867d\u76db\u6c14\u51cc\u4eba\uff0c\u8fd9\u756a\u8bdd\u5374\u4e5f\u4e0d\u5931\u8eab\u4efd\u3002 \u3000\u3000\u8fd9\u65f6\u5168\u573a\u76ee\u5149\u90fd\u6ce8\u89c6\u7740\u5415\u56db\u5a18\uff0c\u770b\u5979\u662f\u5426\u7518\u4e3a\u5e08\u5144\u6298\u8fb1\u3002\u66f4\u60f3\u770b\u5979\u5230\u5e95\u6709\u4ec0\u4e48\u529f\u592b\u3002\u5415 \u56db\u5a18\u8fde\u8fde\u51b7\u7b11\uff0c\u4e86\u56e0\u65a5\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u4f60\u5230\u5e95\u9075\u4e0d\u9075\u4ece\u672c\u95e8\u89c4\u77e9\uff1f\u201d\u5415\u56db\u5a18\u4e0d\u7406\u4e0d\u8eb2\uff0c\u7b11 \u4e2a\u4e0d\u505c\uff0c\u4e86\u56e0\u59cb\u800c\u66b4\u6012\uff0c\u7ee7\u800c\u8272\u53d8\u3002\u5415\u56db\u5a18\u7684\u7b11\u58f0\u6781\u5176\u6e05\u5cfb\uff0c\u53ea\u89c1\u5979\u5634\u5507\u5fae\u52a8\uff0c\u5728\u573a\u7684\u4eba\u90fd \u542c\u5f97\u6709\u4e00\u79cd\u5e7d\u5fae\u7684\u7b11\u58f0\uff0c\u6447\u66f3\u800c\u51fa\uff0c\u97f3\u7ec6\u800c\u6e05\uff0c\u82d1\u5982\u6e38\u4e1d\u8885\u7a7a\uff0c\u82e5\u65ad\u82e5\u7eed\uff1b\u4e00\u5ffd\u513f\uff0c\u6e10\u9ad8\u6e10 \u8fdc\uff0c\u597d\u50cf\u7b11\u58f0\u5c31\u4ece\u534a\u7a7a\u4e2d\u964d\u4e0b\u6765\u4f3c\u7684\uff0c\u518d\u8fc7\u4e00\u4f1a\u5415\u56db\u5a18\u5927\u58f0\u72c2\u7b11\uff0c\u5c71\u9e23\u8c37\u5e94\uff0c\u54cd\u904f\u884c\u4e91\uff0c\u9690 \u9690\u4e0e\u6f6e\u97f3\u548c\u7b54\uff1b\u7b11\u58f0\u4e2d\u542b\u7740\u9119\u5f03\u6740\u4f10\u4e4b\u58f0\uff0c\u53c8\u5982\u4e07\u9a6c\u5954\u817e\uff0c\u5343\u519b\u8d74\u654c\uff0c\u84e6\u7136\u7b11\u58f0\u505c\u4e86\uff0c\u800c\u4f59 \u97f3\u8885\u8885\uff0c\u72b9\u81ea\u5728\u5c71\u8c37\u4e2d\u56de\u54cd\uff0c\u597d\u4f3c\u5728\u8fd9\u6d77\u5c9b\u5b64\u5cf0\uff0c\u9690\u85cf\u6709\u65e0\u6570\u4ed9\u5973\u5c71\u7075\uff0c\u5728\u540c\u58f0\u5411\u4e86\u56e0\u53d6 \u7b11\uff0c\u4e45\u4e45\u4e0d\u7edd\uff01 \u3000\u3000\u5415\u56db\u5a18\u7684\u51b7\u7b11\uff0c\u6b63\u5c31\u662f\u663e\u9732\u4e86\u5979\u6df1\u6e5b\u7684\u5185\u529f\uff0c\u5185\u529f\u6781\u9ad8\u7684\u4eba\u80fd\u9f13\u6c14\u884c\u8fdc\uff0c\u53d1\u97f3\u7ed5\u6881\u3002\u5415 \u56db\u5a18\u7684\u7b11\u58f0\uff0c\u6b63\u663e\u51fa\u4e86\u5979\u7684\u4e2d\u6c14\u4e4b\u5f3a\uff0c\u4e0e\u5185\u529b\u7684\u6301\u4e45\uff0c\u4e0d\u4f3c\u54c8\u5e03\u9640\u7684\u4ee5\u201c\u70c8\u201d\u53d6\u80dc\u3002\u4f46\u5728\u5185 \u884c\u4eba\u542c\u6765\uff0c\u5979\u8fd9\u6837\u7684\u53d1\u7b11\uff0c\u6bd4\u54c8\u5e03\u9640\u7684\u201c\u72ee\u5b50\u543c\u201d\u529f\u8fd8\u8981\u9ad8\u660e\uff01\u5415\u56db\u5a18\u806a\u660e\u5230\u6781\uff0c\u5979\u501f\u51b7\u7b11 \u800c\u663e\u6b66\u529f\uff0c\u65e2\u4e0d\u8fdd\u80cc\u672c\u95e8\u89c4\u77e9\uff0c\u5e94\u4e86\u4e86\u56e0\u8003\u6838\u7684\u8981\u6c42\uff0c\u53c8\u4e0d\u5931\u6389\u81ea\u5df1\u8eab\u4efd\u3002\u6240\u6709\u6b66\u6797\u9ad8\u624b\uff0c \u5fc3\u4e2d\u90fd\u6697\u6697\u559d\u91c7\uff0c\u4f69\u670d\u5979\u7684\u5927\u80c6\u673a\u667a\u3002\u4e86\u56e0\u867d\u7136\u8272\u53d8\uff0c\u5374\u662f\u65e0\u53ef\u5948\u4f55\uff0c\u5fc3\u4e2d\u6697\u6697\u60ca\u5947\uff0c\u8fd9\u5c0f \u5e08\u59b9\u4f55\u4ee5\u4f1a\u6709\u5982\u6b64\u9ad8\u7684\u529f\u529b\u3002\u770b\u6765\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\u3002 \u3000\u3000\u5415\u56db\u5a18\u7b11\u58f0\u65e2\u6bd5\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u62d4\u5251\u8bf4\u9053\uff1a\u2018\u540c\u95e8\u4e4b\u8c0a\u65e2\u7edd\uff0c\u6211\u73b0\u5728\u5c31\u8981\u66ff\u5e08\u5085\u6e05\u7406\u95e8 \u6237\uff01\u201d\u8bdd\u58f0\u65b9\u505c\uff0c\u84e6\u542c\u5f97\u4e00\u58f0\u72c2\u5578\uff0c\u573a\u4e2d\u5fc3\u5df2\u73b0\u51fa\u4e00\u4eba\uff01 \u3000\u3000\u6b64\u4eba\u5934\u6234\u7f8a\u89d2\u5e3d\uff0c\u8eab\u62ab\u9ed1\u8888\u88df\uff0c\u624b\u4e2d\u4e5f\u63d0\u7740\u4e00\u628a\u5bd2\u5149\u95ea\u95ea\u7684\u957f\u5251\u3002\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u6211\u66ff\u4e86 \u56e0\u5927\u5e08\u7ba1\u675f\u5c0f\u8f88\u3002\u201d\u6b64\u4eba\u6b63\u662f\u5a01\u9707\u5357\u7586\u7684\u6d77\u5357\u5c9b\u4e94\u6307\u5c71\u5251\u5e08\u6d77\u4e91\u548c\u5c1a\uff01\u4ed6\u9002\u624d\u5403\u4e86\u90a3\u4e10\u5987\u7684 \u4e8f\uff0c\u6b63\u81ea\u4e00\u8154\u6012\u6c14\uff0c\u672c\u6765\u521d\u65f6\u4ed6\u8fd8\u4e0d\u5c51\u4e8e\u6597\u5415\u56db\u5a18\uff0c\u540e\u6765\u89c1\u5415\u56db\u5a18\u663e\u4e86\u7384\u95e8\u6b63\u5b97\u7684\u6df1\u539a\u5185 \u529f\uff0c\u89c9\u5f97\u548c\u5979\u76f8\u6597\uff0c\u4e5f\u4e0d\u5931\u6389\u8eab\u4efd\uff0c\u56e0\u6b64\u8981\u4ed7\u81ea\u5df1\u5a01\u9707\u5929\u5357\u7684\u5251\u6cd5\uff0c\u628a\u5979\u6298\u670d\u5f53\u573a\u3002\u4e5f\u597d\u633d \u56de\u521a\u624d\u88ab\u4eba\u8f83\u77ed\u7684\u9762\u5b50\u3002 \u3000\u3000\u5415\u56db\u5a18\u9762\u5bd2\u5982\u6c34\uff0c\u53cc\u80a9\u4e00\u6643\uff0c\u5df2\u9000\u540e\u4e03\u516b\u6b65\u8fdc\uff0c\u628a\u5251\u63a3\u5728\u624b\u4e2d\uff0c\u4f7f\u4e2a\u201c\u65e0\u6781\u542b\u6c14\u201d\u7684\u5251 \u5f0f\uff0c\u4e24\u624b\u4e0b\u5782\uff0c\u76ee\u51dd\u5251\u5c16\uff0c\u811a\u4e0b\u4e0d\u4e01\u4e0d\u516b\uff0c\u7ad9\u4e2a\u6869\u6b65\uff0c\u582a\u79f0\u5f97\u6c89\u5982\u5c71\u5cb3\uff0c\u9759\u82e5\u5e73\u6e56\uff0c\u51b7\u7136\u8bf4 \u9053\uff1a\u201c\u6d77\u4e91\u5927\u5e08\uff0c\u5251\u6cd5\u901a\u7384\uff0c\u6d77\u5185\u77e5\u540d\uff0c\u5357\u5929\u79f0\u9738\uff0c\u4eca\u80af\u60e0\u7136\u8d50\u62db\uff0c\u505a\u665a\u8f88\u7684\u65e0\u9650\u8363\u5e78\uff01\u201d \u8fd9\u51e0\u53e5\u8bdd\u5916\u4f3c\u8c26\u900a\uff0c\u5185\u9690\u950b\u8292\uff0c\u6d77\u4e91\u548c\u5c1a\u9762\u76ae\u4e00\u7ea2\uff0c\u770b\u5979\u51dd\u8eab\u4eae\u5251\uff0c\u529f\u529b\u975e\u6bd4\u5bfb\u5e38\uff0c\u4e0d\u7981\u63e3 \u7136\u6697\u60e7\uff0c\u6df1\u6015\u81ea\u5df1\u4e00\u4e16\u5a01\u540d\uff0c\u80dc\u5f97\u4e5f\u8fd8\u7f62\u4e86\uff0c\u82e5\u7136\u4e0d\u80dc\uff0c\u53ef\u662f\u96be\u582a\u3002\u5fc3\u5ff5\u8e0c\u8e87\uff0c\u957f\u5251\u4e00\u6296\uff0c \u4e0d\u6562\u8d38\u7136\u8fdb\u62db\u3002 \u3000\u3000\u5415\u56db\u5a18\u6df1\u77e5\u5bf9\u65b9\u5389\u5bb3\uff0c\u56e0\u6b64\u4ee5\u9038\u5f85\u52b3\uff0c\u5c01\u597d\u95e8\u6237\uff0c\u5c79\u7136\u8bf4\u9053\uff1a\u201c\u5927\u5e08\u65e2\u8981\u7ba1\u6559\u5c0f\u8f88\uff0c\u4e3a \u4f55\u5c3d\u4e0d\u52a8\u624b\u5440\uff01\u201d\u573a\u4e0a\u4e00\u767e\u51e0\u5341\u53cc\u773c\u775b\u9f50\u6ce8\u6597\u573a\uff0c\u6709\u4eba\u51b7\u51b7\u53d1\u7b11\u3002\u6d77\u4e91\u548c\u5c1a\u6c14\u5f80\u4e0a\u51b2\uff0c\u60f3 \u9053\uff1a\u201c\u4f60\u8fd9\u79cd\u592a\u6781\u5947\u95e8\u7684\u59ff\u52bf\uff0c\u4ee5\u9038\u5f85\u52b3\uff0c\u60f3\u8ba8\u4fbf\u5b9c\uff0c\u6211\u5148\u7ed9\u4f60\u4e2a\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u7834\u6cd5\uff01\u201d \u53f3\u624b\u5012\u63e1\u5251\u628a\uff0c\u84e6\u7136\u559d\u58f0\uff1a\u201c\u770b\u5251\uff01\u201d\u547c\u7684\u4e00\u80a1\u52b2\u98ce\uff0c\u4fbf\u626b\u8fc7\u6765\uff0c\u5415\u56db\u5a18\u5251\u5c16\u4e00\u6296\uff0c\u4e00\u63d0\u4e00 \u7ffb\uff0c\u4e00\u62db\u201c\u5999\u624b\u6458\u661f\u201d\uff0c\u642d\u7740\u4e86\u6d77\u4e91\u548c\u5c1a\u7684\u957f\u5251\uff0c\u5f80\u524d\u4e00\u6307\uff0c\u5251\u5c16\u76f4\u523a\u80a9\u5934\uff0c\u6d77\u4e91\u548c\u5c1a\u4e00\u51fa \u624b\u4fbf\u7ed9\u5979\u5236\u4e86\u673a\u5148\uff0c\u6025\u5fd9\u4e00\u65cb\u4e00\u7ede\uff0c\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u5316\u4e86\u5415\u56db\u5a18\u5251\u52bf\uff0c\u500f\u7684\u64a4\u62db\uff0c\u957f\u5251 \u4e00\u62b1\uff0c\u6ef4\u6e9c\u6e9c\u7684\u4e24\u4e2a\u8f6c\u8eab\uff0c\u53ea\u89c9\u5251\u5149\u6ee1\u573a\uff0c\u9f99\u6f5c\u868a\u8dc3\uff0c\u628a\u5415\u56db\u5a18\u88f9\u5728\u5251\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u4e24\u4eba\u89c1\u9762\u4e00\u62db\uff0c\u5927\u5bb6\u90fd\u77e5\u9053\u78b0\u5230\u4e86\u6781\u5389\u5bb3\u7684\u5bf9\u624b\uff0c\u8fd9\u65f6\u53cc\u65b9\u653b\u52bf\u53d1\u52a8\uff0c\u4ee5\u5feb\u5236\u5feb\uff0c\u970e\u65f6 \u95f4\u62c6\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u76f8\u6301\u4e0d\u4e0b\u3002\u5415\u56db\u5a18\u89c9\u5f97\u5bf9\u65b9\u5251\u6cd5\u751a\u602a\uff0c\u77bb\u4e4b\u5728\u524d\uff0c\u5ffd\u7109\u5728\u540e\uff0c\u77bb\u4e4b\u5728\u5de6\uff0c \u5ffd\u7109\u5728\u53f3\uff0c\u6697\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u8fd9\u53ae\u5a01\u9707\u5929\u5357\uff0c\u5251\u6cd5\u679c\u4e0e\u4e2d\u571f\u4e0d\u540c\uff01\u201d\u5e78\u5f97\u5415\u56db\u5a18\u8f7b\u529f\u6781\u597d\uff0c\u8eab \u6cd5\u8f7b\u7075\uff0c\u867d\u7136\u6478\u4e0d\u5230\u7834\u6cd5\uff0c\u4e5f\u5c1a\u4e0d\u81f3\u5403\u4e8f\u3002 \u3000\u3000\u6597\u4e86\u4e00\u9635\uff0c\u6d77\u4e91\u548c\u5c1a\u5f3a\u653b\u4e0d\u5df2\uff0c\u62db\u62db\u8f9b\u8fa3\uff0c\u53d8\u5316\u591a\u7aef\uff0c\u5415\u56db\u5a18\u5ffd\u7136\u6ee1\u573a\u6e38\u8d70\uff0c\u5f62\u5982\u5f69\u8776 \u7a7f\u82b1\uff0c\u767d\u8863\u98d8\u98d8\uff0c\u7ed5\u5f97\u6025\u65f6\uff0c\u5c31\u5982\u968f\u98ce\u98d8\u7740\u7684\u4e00\u56e2\u767d\u5f71\uff01\u5728\u5251\u5149\u7b3c\u7f69\u4e4b\u4e2d\uff0c\u6e10\u6e10\u5206\u4e0d\u6e05\u5251\u5f71 \u4eba\u5f71\uff0c\u5728\u573a\u9ad8\u624b\uff0c\u89c1\u5415\u56db\u5a18\u6e38\u8d70\u95ea\u907f\uff0c\u5b88\u591a\u653b\u5c11\uff0c\u90fd\u9053\u5979\u6c14\u529b\u4e0d\u52a0\uff0c\u6240\u4ee5\u8981\u4ee5\u5c0f\u5de7\u817e\u632a\u7684\u672c \u9886\uff0c\u6765\u62d6\u5ef6\u6218\u6597\uff0c\u4f3a\u673a\u53cd\u653b\u3002\u53ea\u662f\u6d77\u4e91\u548c\u5c1a\u5251\u6cd5\u75be\u5982\u96f7\u9706\uff0c\u53ea\u51ed\u95ea\u5c55\u817e\u632a\uff0c\u5982\u4f55\u5bf9\u4ed8\u5f97\u4e86\uff1f \u3000\u3000\u5510\u6653\u6f9c\u770b\u5f97\u60ca\u5fc3\uff0c\u624b\u5fc3\u6dcc\u6c57\uff0c\u6390\u7740\u767d\u6cf0\u5b98\u7684\u624b\u9053\uff1a\u201c\u5415\u59d0\u59d0\u6597\u4e0d\u8fc7\u90a3\u4e2a\u79c3\u9a74\uff0c\u5982\u4f55\u662f \u597d\uff1f\u201d\u767d\u6cf0\u5b98\u89c1\u5510\u6653\u6f9c\u9762\u8272\u5168\u53d8\uff0c\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u4e0d\u7528\u5bb3\u6015\uff0c\u5979\u8fd8\u53ef\u4ee5\u62b5\u6321\u5f97\u4f4f\uff01\u201d\u8bdd\u867d\u5982 \u6b64\uff0c\u5176\u5b9e\u4ed6\u81ea\u5df1\u4e5f\u5728\u62c5\u5fc3\u3002 \u3000\u3000\u5728\u5ea7\u4e2d\u5929\u53f6\u6563\u4eba\u548c\u6d77\u4e91\u548c\u5c1a\u751a\u662f\u6295\u7f18\uff0c\u89c1\u6d77\u4e91\u548c\u5c1a\u8fde\u62a2\u653b\u52bf\u5360\u5c3d\u4e0a\u98ce\uff0c\u6b22\u7136\u7b11\u9053\uff1a\u201c\u6d77 \u4e91\u5927\u5e08\u679c\u662f\u4e0d\u51e1\uff0c\u5251\u6cd5\u5947\u5e7b\u65e0\u6bd4\u3002\u8fd9\u5c0f\u5973\u5b50\u80fd\u62b5\u654c\u8fd9\u4e48\u4e9b\u65f6\u5019\uff0c\u4e5f\u771f\u96be\u5f97\u3002\u4e0d\u6127\u662f\u6c5f\u5357\u516b\u4fa0 \u4e2d\u4eba\u3002\u201d\u6b64\u8bdd\u4e00\u6367\u6d77\u4e91\uff0c\u4e00\u6367\u4e86\u56e0\u3002\u4e86\u56e0\u548c\u5c1a\u6de1\u7136\u4e00\u7b11\uff0c\u8e69\u7709\u4e0d\u8bed\u3002\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u5ffd\u9053\uff1a \u201c\u5929\u53f6\u6563\u4eba\uff0c\u4f60\u5185\u529f\u662f\u9ad8\u6781\u4e86\uff0c\u5251\u6cd5\u4f3c\u8fd8\u672a\u6df1\u7814\u3002\u201d\u5929\u53f6\u6563\u4eba\u6012\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u8bf4\u662f\u6211\u770b\u8d70 \u4e86\u773c\uff01\u201d\u8428\u5929\u523a\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff01\u4f46\u636e\u6211\u7684\u62d9\u773c\u770b\u6765\uff0c\u8fd9\u5973\u5b50\u5251\u6cd5\u4f3c\u6bd4\u6d77\u4e91\u5927\u5e08\u8981\u9ad8\u660e\u5f97 \u591a\uff01\u201d\u5176\u5b9e\u8428\u5929\u523a\u4e5f\u5e76\u975e\u7cbe\u901a\u5251\u6cd5\uff0c\u53ea\u56e0\u4ed6\u5728\u9099\u5c71\u66fe\u548c\u5415\u56db\u5a18\u6597\u8fc7\uff0c\u5403\u4e86\u5927\u4e8f\uff0c\u540e\u6765\u5408\u5f1f\u5144 \u4e8c\u4eba\u4e4b\u529b\uff0c\u4e5f\u514b\u5236\u5979\u4e0d\u4f4f\uff0c\u90a3\u8fd8\u662f\u4e94\u5e74\u524d\u4e4b\u4e8b\uff0c\u73b0\u5728\u770b\u5979\u8eab\u6cd5\uff0c\u6bd4\u4e94\u5e74\u524d\u53c8\u4e0d\u77e5\u9ad8\u4e86\u591a\u5c11\uff0c \u8428\u5929\u523a\u9886\u8fc7\u5389\u5bb3\uff0c\u73b0\u5728\u770b\u6d77\u4e91\u548c\u5c1a\u5f3a\u653b\u731b\u6253\uff0c\u6b63\u9677\u4e86\u5f53\u5e74\u81ea\u5df1\u7684\u8986\u8f99\uff0c\u6240\u4ee5\u6562\u4f5c\u5224\u8bed\uff0c\u8981\u5728 \u54c8\u5e03\u9640\u4e4b\u524d\uff0c\u663e\u51fa\u81ea\u5df1\u773c\u5149\u72ec\u5230\uff0c\u632b\u6298\u5929\u53f6\u6563\u4eba\u7684\u5a01\u540d\u3002\u8981\u77e5\u795e\u9b54\u53cc\u8001\uff0c\u662f\u56db\u7687\u5b50\u4ee5\u56fd\u5e08\u4e4b \u793c\uff0c\u8058\u8bf7\u51fa\u5c71\u7684\uff0c\u539f\u4ee5\u4e3a\u53ef\u552f\u6211\u72ec\u5c0a\uff0c\u90a3\u77e5\u540e\u6765\u80fd\u4eba\u8d8a\u8bf7\u8d8a\u591a\uff0c\u8fde\u6c5f\u5357\u516b\u4fa0\u4e4b\u9996\u7684\u4e86\u56e0\u548c\u5c1a \u4e5f\u8bf7\u51fa\u6765\u4e86\u3002\u5982\u4eca\u53c8\u6dfb\u4e86\u6d77\u4e91\u548c\u5c1a\u548c\u5929\u53f6\u6563\u4eba\uff0c\u800c\u5929\u53f6\u6563\u4eba\u7684\u8f88\u4efd\u6b66\u529f\uff0c\u53c8\u4f3c\u66f4\u5728\u81ea\u5df1\u4e4b \u4e0a\uff0c\u6df1\u6015\u81ea\u5df1\u5f1f\u5144\u7684\u5730\u4f4d\u8d8a\u6765\u8d8a\u4f4e\uff0c\u6240\u4ee5\u8d81\u8fd9\u65f6\u673a\uff0c\u6597\u573a\u8bba\u5251\uff0c\u635f\u4f24\u5929\u53f6\u6563\u4eba\u7684\u58f0\u671b\u3002\u5929\u53f6 \u6563\u4eba\u90a3\u6599\u5230\u516b\u81c2\u795e\u9b54\u6709\u6b64\u72ed\u7a84\u5fc3\u601d\uff0c\u5f53\u573a\u5fff\u7136\u8bf4\u9053\uff1a\u201c\u8d24\u6606\u4ef2\u4f3c\u4e4e\u4e5f\u4e0d\u662f\u5251\u672f\u540d\u5bb6\uff01\u201d\u8428\u5929 \u523a\u9053\uff1a\u201c\u4e0d\u662f\u6211\u957f\u654c\u4eba\u5a01\u98ce\uff0c\u6211\u770b\u6d77\u4e91\u548c\u5c1a\u5728\u534a\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u5fc5\u7136\u5403\u8d25\uff01\u6563\u4eba\u4e0d\u4fe1\uff0c\u6562\u4e0e\u6211 \u8d4c\u8d5b\u4e48\uff1f\u201d\u5929\u53f6\u6563\u4eba\u9053\uff1a\u201c\u8d4c\u8d5b\u4ec0\u4e48\uff1f\u201d\u8428\u5929\u523a\u9053\uff1a\u201c\u82e5\u7136\u662f\u6211\u770b\u5dee\uff0c\u6211\u5144\u5f1f\u7acb\u56de\u732b\u9e70 \u5c9b\u3002\u201d\u5929\u53f6\u6563\u4eba\u9053\uff1a\u201c\u597d\uff01\u82e5\u7136\u662f\u6211\u770b\u5dee\uff0c\u6211\u4e5f\u7acb\u56de\u661f\u5bbf\u6d77\uff01\u201d\u6b63\u8981\u51fb\u638c\u7acb\u8a93\uff0c\u54c8\u5e03\u9640\u4e0e\u4e86 \u56e0\u5df2\u62a2\u7740\u62c9\u5f00\u4e24\u4eba\uff0c\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u4f55\u82e6\u5982\u6b64\uff0c\u54b1\u4eec\u90fd\u8981\u534f\u52a9\u56db\u7687\u5b50\u767b\u57fa\uff0c\u90a3\u53ef\u5206\u8584\u4e86\u81ea\u5df1\u529b \u91cf\uff01\u4f9d\u6211\u4eec\u8bf4\uff0c\u4e0d\u5982\u6539\u8fc7\u8d4c\u8d5b\u529e\u6cd5\uff0c\u82e5\u6d77\u4e91\u548c\u5c1a\u8d62\u4e86\u4fbf\u7f62\uff0c\u82e5\u8d62\u4e0d\u4e86\uff0c\u4e24\u4f4d\u518d\u4f9d\u6b21\u548c\u5979\u76f8 \u6597\uff0c\u770b\u8c01\u80fd\u5c06\u5979\u751f\u64d2\uff01\u201d\u8428\u5929\u523a\u95ed\u53e3\u4e0d\u8a00\uff0c\u5929\u53f6\u6563\u4eba\u9053\uff1a\u201c\u6211\u4e0d\u5c51\u548c\u540e\u8f88\u76f8\u6597\u3002\u800c\u4e14\u8fd9\u5c0f\u5973 \u5b50\u4e5f\u5b9a\u975e\u6d77\u4e91\u5bf9\u624b\uff0c\u4f55\u5fc5\u6211\u518d\u51fa\u573a\uff01\u201d\u6012\u6c14\u89c1\u4e8e\u540c\u8272\uff0c\u4e86\u56e0\u548c\u54c8\u5e02\u9640\u6025\u5fd9\u62c9\u5f00\u4e24\u4eba\uff0c\u4e0d\u8ba9\u4ed6 \u4eec\u540c\u5728\u4e00\u5904\u3002\u60f3\u7b49\u4e8b\u5b8c\u4e4b\u540e\uff0c\u4e0d\u8bba\u8c01\u4e2a\u8d4c\u8d62\uff0c\u90fd\u4e3a\u4e8c\u4eba\u597d\u597d\u8c03\u89e3\u3002 \u3000\u3000\u4e00\u573a\u5c0f\u98ce\u6ce2\u521a\u521a\u9759\u4e0b\uff0c\u770b\u90a3\u573a\u4e2d\u6597\u5251\uff0c\u8d8a\u6765\u8d8a\u70c8\uff0c\u5415\u56db\u5a18\u4ecd\u662f\u6ee1\u573a\u6e38\u8d70\uff0c\u6d77\u4e91\u548c\u5c1a\u4ecd\u662f \u731b\u626b\u5f3a\u653b\uff0c\u5916\u8868\u5f62\u52bf\u672a\u53d8\uff0c\u4f46\u4e00\u6d41\u9ad8\u624b\u5df2\u53ef\u770b\u51fa\uff0c\u5415\u56db\u5a18\u5728\u5251\u5149\u7b3c\u7f69\u4e4b\u4e2d\uff0c\u5df2\u662f\u63a5\u8fde\u53cd\u51fb\uff0c \u6709\u5b88\u6709\u653b\uff01 \u3000\u3000\u5f62\u52bf\u4e4b\u53d8\uff0c\u4e86\u56e0\u548c\u5c1a\u8fd8\u4e0d\u600e\u6837\uff0c\u54c8\u5e03\u9640\u53ef\u5927\u611f\u60ca\u5947\uff0c\u5fc3\u60f3\uff1a\u201c\u6562\u60c5\u8428\u5929\u523a\u771f\u4e2a\u770b\u5bf9\uff0c\u8fd9 \u5c0f\u5973\u5b50\u5251\u6cd5\u5947\u5999\uff0c\u8fd8\u5728\u6d77\u4e91\u548c\u5c1a\u4e4b\u4e0a\u4e48\uff1f\u201d\u8fd9\u65f6\u767d\u6cf0\u5b98\u4e5f\u770b\u51fa\u4e86\u82d7\u5934\uff0c\u53ea\u6709\u5510\u6653\u6f9c\u8fd8\u5728\u5fc3\u60ca \u80c6\u6218\u3002 \u3000\u3000\u539f\u6765\u6d77\u4e91\u548c\u5c1a\u4e0e\u5415\u56db\u5a18\u6362\u4e86\u4e00\u62db\uff0c\u5df2\u77e5\u5979\u662f\u4e2a\u751f\u5e73\u4ec5\u89c1\u7684\u5f3a\u52b2\u5bf9\u624b\uff01\u56e0\u6b64\u65bd\u5c55\u6d51\u8eab\u672c \u9886\uff0c\u60f3\u4ee5\u96f7\u9706\u4e07\u94a7\u4e4b\u5a01\uff0c\u4ee5\u6c42\u4e00\u901e\u3002\u539f\u610f\u4ee5\u4e3a\u5415\u56db\u5a18\u5251\u6cd5\u867d\u9ad8\uff0c\u5230\u5e95\u662f\u4e2a\u5e74\u8f7b\u5973\u5b50\uff0c\u6c14\u529b\u7ecf \u9a8c\u5b9a\u5fc5\u8f93\u4e8f\u3002\u90a3\u77e5\u5415\u56db\u5a18\u5b66\u7684\u662f\u72ec\u81c2\u795e\u5c3c\u6700\u5f97\u610f\u7684\u672c\u9886\u3002\u72ec\u81c2\u795e\u5c3c\u7cbe\u7814\u4e86\u51e0\u5341\u5e74\u7384\u5973\u5251\u6cd5\uff0c \u5728\u5415\u56db\u5a18\u5165\u95e8\u4e4b\u540e\u7684\u7b2c\u4e8c\u5e74\uff0c\u624d\u5fc3\u4e0e\u5251\u4f1a\uff0c\u5999\u609f\u901a\u7384\uff0c\u4e0d\u4f46\u5230\u4e86\u51fa\u795e\u5165\u5316\u7684\u5883\u5730\uff0c\u800c\u4e14\u8fd8\u80fd \u878d\u4f1a\u8d2f\u901a\uff0c\u7ed9\u539f\u6765\u7684\u7384\u5973\u5251\u6cd5\uff0c\u589e\u6dfb\u4e86\u8bb8\u591a\u53d8\u5316\u3002\u72ec\u81c2\u795e\u5c3c\u5728\u5251\u6cd5\u672a\u66fe\u5927\u6210\u4e4b\u524d\uff0c\u4e0d\u4f20\u5f92 \u5f1f\uff0c\u6240\u4ee5\u6c5f\u5357\u4e03\u4fa0\uff0c\u90fd\u4e0d\u4ee5\u5251\u6cd5\u89c1\u957f\u3002\u5415\u56db\u5a18\u51d1\u5408\u673a\u7f18\uff0c\u5728\u5979\u665a\u5e74\u5165\u95e8\uff0c\u72ec\u5f97\u7cbe\u9ad3\u3002\u4eca\u65e5\u5e94 \u4ed8\u5f3a\u654c\uff0c\u628a\u6240\u5b66\u65bd\u5c55\u51fa\u6765\uff0c\u6ee1\u573a\u6e38\u8d70\uff0c\u8c8c\u4f3c\u95ea\u907f\uff0c\u5185\u91cc\u5374\u6697\u85cf\u6781\u590d\u6742\u7684\u53d8\u5316\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u53ef \u865a\u53ef\u5b9e\uff0c\u62db\u91cc\u5957\u62db\uff0c\u6597\u5230\u5206\u9645\uff0c\u770b\u6d77\u4e91\u548c\u5c1a\u9510\u6c14\u6e10\u6d88\uff0c\u84e6\u5730\u5251\u62db\u4e00\u53d8\uff0c\u4e09\u5c3a\u971c\u534e\u5b9d\u5251\uff0c\u5bd2\u5149 \u95ea\u95ea\uff0c\u534a\u5b88\u534a\u653b\u3002\u771f\u4e2a\u662f\u9759\u5982\u5904\u5b50\uff0c\u52a8\u5982\u8131\u5154\uff0c\u6d77\u4e91\u548c\u5c1a\u662f\u5251\u672f\u7684\u5927\u884c\u5bb6\uff0c\u770b\u51fa\u4e86\u654c\u4eba\u5251\u6cd5 \u6bd4\u81ea\u5df1\u7684\u66f4\u4e3a\u5947\u5e7b\uff0c\u53c8\u60ca\u53c8\u6025\uff0c\u81ea\u5df1\u662f\u91d1\u521a\u731b\u6251\uff0c\u51fa\u5c3d\u5168\u529b\uff0c\u654c\u4eba\u4ecd\u662f\u6c14\u5b9a\u795e\u95f2\uff0c\u6beb\u53d1\u65e0 \u4f24\u3002\u5012\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u77e5\u9053\u4e0d\u5999\uff0c\u8d25\u4e2d\u6c42\u80dc\uff0c\u8fde\u8d70\u9669\u62db\uff0c\u957f\u5251\u4e00\u62db\u201c\u66b4\u5377\u5929\u6cb3\u201d\uff0c\u50e7\u888d\u8d77 \u5904\uff0c\u5251\u950b\u5012\u5377\u800c\u4e0a\uff0c\u5415\u56db\u5a18\u84e6\u7136\u64a4\u5251\u51dd\u7acb\uff0c\u53cc\u7738\u95ea\u95ea\u53d1\u5149\uff0c\u6d77\u4e91\u548c\u5c1a\u957f\u5251\u5377\u6765\uff0c\u5979\u4ecd\u6d51\u5982\u672a \u89c9\uff0c\u8fd9\u65f6\u95f4\u5168\u573a\u8083\u9759\u65e0\u58f0\uff0c\u4e2a\u4e2a\u60ca\u5fc3\u9a87\u76ee\uff0c\u5510\u6653\u6f9c\u95ed\u4e0a\u53cc\u76ee\uff0c\u4e0d\u6562\u89c2\u770b\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c \u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u5239\u90a3\uff0c\u731b\u542c\u5f97\u5415\u56db\u5a18\u4e00\u58f0\u6e05\u5578\uff0c\u5411\u4e0a\u4e00\u7eb5\uff0c\u5f13\u978b\u7adf\u671d\u654c\u4eba\u7684\u5251\u5c16\u4e00\u8e0f\uff0c\u501f \u7740\u8fd9\u4e00\u8e0f\u4e4b\u52bf\uff0c\u6574\u4e2a\u8eab\u5b50\u7ffb\u817e\u8d77\u6765\uff0c\u75be\u5982\u98de\u9e1f\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u63a0\u8fc7\u6d77\u4e91\u548c\u5c1a\u5934\u9876\uff01\u4e0d\u5f85\u53cc\u8db3\u843d \u5730\uff0c\u971c\u534e\u5251\u5728\u7a7a\u4e2d\u4e00\u65cb\uff0c\u5df2\u4f7f\u51fa\u201c\u767d\u8679\u8d2f\u65e5\u201d\u7684\u7edd\u62db\uff0c\u4e00\u5251\u7167\u6d77\u4e91\u548c\u5c1a\u7684\u79c3\u5934\u523a\u4e0b\uff0c\u6d77\u4e91\u548c \u5c1a\u53eb\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u201d\u957f\u5251\u4e00\u6296\uff0c\u5251\u950b\u63a0\u7a7a\u800c\u4e0a\uff0c\u62a4\u5934\u7855\uff0c\u6d88\u654c\u52bf\uff0c\u5c1a\u6c42\u4fa5\u5e78\u4e8e\u4e07\u4e00\uff0c\u4e24\u5251\u76f8 \u4ea4\uff0c\u5415\u56db\u5a18\u5c45\u9ad8\u4e34\u4e0b\uff0c\u5b9d\u5251\u4e00\u7ffb\u4e00\u7ede\uff0c\u53ea\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u6d77\u4e91\u548c\u5c1a\u7684\u957f\u5251\u65ad\u4e3a\u4e24\u622a\uff0c\u7ed9 \u5415\u56db\u5a18\u64a9\u51fa\u8001\u8fdc\u3002\u4f17\u4eba\u5b9a\u775b\u770b\u65f6\uff0c\u5415\u56db\u5a18\u5df2\u7b11\u76c8\u76c8\u7684\u843d\u5728\u5730\u4e0a\uff0c\u62b1\u5251\u5f53\u80f8\uff0c\u56db\u65b9\u4e00\u63d6\uff0c\u8bf4 \u9053\uff1a\u201c\u6d77\u4e91\u5927\u5e08\uff0c\u5c0f\u8f88\u627f\u8ba9\u4e86\uff01\u201d\u6d77\u4e91\u548c\u5c1a\u9762\u76ae\u7ea2\u5230\u8033\u6839\uff0c\u6068\u4e0d\u5f97\u6709\u4e2a\u5730\u6d1e\u94bb\u5165\u53bb\uff01 \u3000\u3000\u8fd9\u4e00\u573a\u6597\u5251\uff0c\u4ee4\u5230\u5168\u573a\u9ad8\u624b\u65e0\u4e0d\u548b\u820c\u79f0\u5947\uff01\u8428\u5929\u523a\u662f\u4e00\u9762\u5f97\u610f\uff0c\u4e00\u9762\u60ca\u5fc3\uff1b\u5f97\u610f\u7684\u662f\u4ed6 \u4e0e\u5929\u53f6\u6563\u4eba\u7684\u8d4c\u8d5b\u679c\u7136\u80dc\u4e86\uff0c\u60ca\u5fc3\u7684\u662f\u5415\u56db\u5a18\u7684\u672c\u9886\u5df2\u5230\u4e86\u767b\u5cf0\u9020\u6781\u7684\u5730\u6b65\uff0c\u81ea\u5df1\u5403\u5979\u7684 \u4e8f\uff0c\u770b\u6765\u662f\u5f88\u96be\u62a5\u590d\u4e86\u3002\u5929\u53f6\u6563\u4eba\u5219\u9762\u8272\u7531\u7ea2\u8f6c\u9752\uff0c\u54ac\u4e86\u54ac\u7259\uff0c\u731b\u7136\u8d77\u8eab\uff0c\u5c31\u8981\u4e0b\u573a\u4e0e\u5415\u56db \u5a18\u51b3\u6597\uff01 \u3000\u3000\u5929\u53f6\u6563\u4eba\u5219\u521a\u8d77\u7acb\uff0c\u80a9\u5934\u5ffd\u7136\u7ed9\u4eba\u4e00\u6309\uff0c\u4e86\u56e0\u548c\u5c1a\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5929\u53f6\u9053\u5144\uff0c\u5f85\u6211\u6765\u6536\u62fe \u8fd9\u4e2a\u8d31\u5a62\uff01\u201d\u8981\u77e5\u4e86\u56e0\u548c\u5c1a\uff0c\u867d\u7136\u8d70\u5165\u90aa\u95e8\uff0c\u5374\u662f\u4e2a\u6c5f\u6e56\u6c49\u5b50\uff0c\u8981\u4fdd\u6301\u6c5f\u5357\u516b\u4fa0\u9996\u9886\u7684\u8eab \u4efd\u3002\u4ed6\u8d77\u5148\u4e0d\u6562\u548c\u5415\u56db\u5a18\u76f8\u6597\uff0c\u4e43\u662f\u4e0d\u6b32\u843d\u4e2a\u4ee5\u5927\u6b3a\u5c0f\u4e4b\u540d\uff0c\u4ee5\u4e3a\u968f\u4fbf\u6d3e\u4e00\u540d\u9ad8\u624b\uff0c\u5c31\u53ef\u5c06 \u5979\u6d3b\u6349\uff0c\u4e0d\u6599\u4e8b\u60c5\u5927\u51fa\u610f\u6599\uff0c\u4ee5\u6d77\u4e91\u548c\u5c1a\u90a3\u6837\u5251\u6cd5\u901a\u7384\u7684\u4eba\u7269\uff0c\u5c45\u7136\u4e5f\u53d7\u5230\u65ad\u5251\u6298\u540d\u4e4b\u8fb1\uff01 \u4ed6\u867d\u7136\u77e5\u9053\u5929\u53f6\u6563\u4eba\u6b66\u529f\u8d85\u5353\uff0c\u66f4\u5728\u6d77\u4e91\u548c\u5c1a\u4e4b\u4e0a\uff0c\u4f46\u4e5f\u6df1\u6015\u4e07\u4e00\u53c8\u9022\u4e0d\u6d4b\uff0c\u4e0d\u4f46\u6298\u4e86\u5929\u53f6 \u6563\u4eba\u4e00\u4e16\u5a01\u540d\uff0c\u81ea\u5df1\u4e5f\u4f1a\u53d7\u540c\u9053\u7a83\u8bae\u8fb1\u9a82\u3002\u8bf4\u81ea\u5df1\u6545\u610f\u653e\u4efb\u5e08\u59b9\uff0c\u51cc\u8fb1\u524d\u8f88\uff0c\u6298\u6210\u540d\u4eba\u7269\u7684 \u5a01\u98ce\uff0c\u663e\u81ea\u5df1\u672c\u95e8\u7684\u6280\u827a\u3002\u6709\u8fd9\u4e00\u5c42\u5173\u7cfb\uff0c\u6240\u4ee5\u4e86\u56e0\u548c\u5c1a\u62e6\u963b\u4e86\u5929\u53f6\u6563\u4eba\uff0c\u6025\u6025\u51fa\u573a\uff01 \u3000\u3000\u8fd9\u4e00\u6765\u597d\u620f\u767b\u53f0\uff0c\u5168\u573a\u559d\u91c7\u3002\u5728\u5ea7\u9ad8\u624b\uff0c\u90fd\u77e5\u9053\u4e86\u56e0\u7edd\u4e16\u6b66\u529f\uff0c\u51fa\u9053\u4ee5\u6765\uff0c\u672a\u9022\u654c\u624b\u3002 \u90fd\u8981\u770b\u4ed6\u600e\u6837\u751f\u64d2\u5e08\u59b9\uff0c\u8868\u6f14\u6b66\u529f\u3002\u767d\u6cf0\u5b98\u6697\u6697\u7740\u6025\uff0c\u53ef\u662f\u52bf\u6210\u9a91\u864e\uff0c\u4e5f\u65e0\u53ef\u963b\u62e6\u3002 \u3000\u3000\u4e86\u56e0\u548c\u5c1a\u63d0\u7740\u7897\u53e3\u7c97\u7684\u7cbe\u94a2\u7985\u6756\uff0c\u5927\u6b65\u8d70\u6765\uff0c\u5415\u56db\u5a18\u6a2a\u5251\u5f53\u80f8\uff0c\u51dd\u795e\u5f85\u654c\u3002\u4e86\u56e0\u548c\u5c1a\u7985 \u6756\u4e00\u6307\uff0c\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u5415\u56db\u5a18\uff0c\u4f60\u76ee\u65e0\u5c0a\u957f\uff0c\u53ef\u602a\u4e0d\u5f97\u6211\u7985\u6756\u65e0\u60c5\u3002\u4f60\u82e5\u77e5\u673a\uff0c\u5feb\u5feb\u5f03\u5251\u6c42 \u9976\uff0c\u9886\u53d7\u5bb6\u6cd5\uff01\u201d\u5415\u56db\u5a18\u67f3\u7709\u5012\u7ad6\uff0c\u6717\u58f0\u65a5\u9053\uff1a\u201c\u4e86\u56e0\uff0c\u4f60\u5728\u53d7\u5e08\u5085\u591a\u5e74\u6559\u8bf2\uff0c\u5374\u4e0d\u5b88\u6e05 \u89c4\uff0c\u8fdd\u80cc\u5927\u6212\u3002\u5e08\u5085\u9057\u547d\uff0c\u8981\u6211\u7ea0\u96c6\u540c\u95e8\uff0c\u53d6\u4f60\u9996\u7ea7\uff01\u6211\u5ff5\u66fe\u662f\u540c\u95e8\u4e4b\u8c0a\uff0c\u7ed9\u4f60\u6307\u70b9\u4e00\u6761\u751f \u8def\uff0c\u4f60\u82e5\u5e61\u7136\u6539\u609f\uff0c\u901f\u901f\u968f\u6211\u56de\u8f6c\u9099\u5c71\uff0c\u5728\u5e08\u5085\u7075\u524d\u711a\u9999\u544a\u7f6a\uff0c\u7acb\u8a93\u6d17\u803b\uff0c\u90a3\u65f6\u4e00\u4f17\u540c\u95e8\uff0c \u6216\u53ef\u9976\u4f60\u4e0d\u6b7b\uff0c\u8981\u4e0d\u7136\u4f60\u4eca\u65e5\u5c31\u96be\u9003\u516c\u9053\u3002\u6211\u8a00\u5c3d\u4e8e\u6b64\uff0c\u542c\u4e0e\u4e0d\u542c\uff0c\u968f\u4f60\u7684\u4fbf\uff01\u201d\u4e86\u56e0\u548c\u5c1a \u52c3\u7136\u9707\u6012\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5b66\u4e86\u51e0\u5e74\u6b66\u529f\uff0c\u6709\u591a\u5927\u672c\u9886\uff1f\u6562\u5728\u4f60\u5e08\u5144\u9762\u524d\u653e\u8086\u80e1\u4e3a\uff0c\u4f60\u4e0a\u9762\u8fd8 \u6709\u51e0\u4f4d\u5e08\u5144\uff0c\u4f60\u4e5f\u4e0d\u95ee\u95ee\u4ed6\u4eec\uff0c\u662f\u8c01\u6210\u5168\u4e86\u4ed6\u4eec\u6c5f\u5357\u4e03\u4fa0\u7684\u5a01\u540d\uff01\u201d\u8981\u77e5\u4e86\u56e0\u548c\u5c1a\u4eca\u5e74\u4e94\u5341 \u6709\u4f59\uff0c\u5415\u56db\u5a18\u5c1a\u672a\u51fa\u751f\uff0c\u4ed6\u5df2\u88ab\u72ec\u81c2\u795e\u5c3c\u6536\u4e3a\u5f1f\u5b50\u3002\u81ea\u5468\u6e05\u4ee5\u81f3\u7518\u51e4\u6c60\uff0c\u4e60\u6280\u4e4b\u65f6\uff0c\u4ed6\u90fd\u66fe \u4ee3\u72ec\u81c2\u795e\u5c3c\u4f20\u8fc7\u672c\u95e8\u6b66\u827a\uff0c\u6240\u4ee5\u4ed6\u548c\u5176\u4ed6\u516d\u4fa0\uff0c\u540d\u867d\u5144\u5f1f\uff0c\u5b9e\u5219\u201c\u534a\u5e08\u201d\uff0c\u4e00\u4f17\u5e08\u5f1f\uff0c\u5bf9\u4ed6 \u65e0\u4e0d\u5fcc\u60ee\uff0c\u5373\u7b97\u7518\u51e4\u6c60\u5929\u8d44\u6700\u9ad8\uff0c\u7980\u8d4b\u7279\u5f02\uff0c\u5a01\u540d\u6700\u76db\uff0c\u5bf9\u8fd9\u4f4d\u5e08\u5144\u4e5f\u8981\u9000\u8ba9\u4e09\u5206\u3002\u4e5f\u6b63\u56e0 \u6b64\uff0c\u6240\u4ee5\u4e86\u56e0\u6ee1\u5fc3\u81ea\u4fe1\uff0c\u4ee5\u4e3a\u4e00\u4f17\u5e08\u5f1f\uff0c\u5fc5\u552f\u4ed6\u9a6c\u9996\u662f\u77bb\uff0c\u90a3\u6599\u4eca\u5929\u767d\u6cf0\u5b98\u4e0d\u670d\u4e8e\u524d\uff0c\u5415\u56db \u5a18\u66f4\u8f7b\u6301\u864e\u987b\u4e8e\u540e\uff0c\u4e86\u56e0\u90a3\u80fd\u4e0d\u66b4\u8df3\u5982\u96f7\uff01 \u3000\u3000\u5415\u56db\u5a18\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c\u5927\u58f0\u65a5\u9053\uff1a\u201c\u6709\u4f60\u8fd9\u6837\u5e08\u5144\uff0c\u771f\u662f\u6c5f\u5357\u516b\u4fa0\u4e4b\u803b\uff0c\u4e8f\u4f60\u8fd8\u6562\u8bf4\u6210 \u5c31\u4e86\u5e08\u5f1f\u7684\u5a01\u540d\uff01\u4ece\u4eca\u65e5\u8d77\uff0c\u53ea\u6709\u6c5f\u5357\u4e03\u4fa0\uff0c\u518d\u4e0d\u51c6\u4f60\u7528\u5e08\u5085\u7684\u540d\u5934\u62db\u6447\uff01\u201d\u4e86\u56e0\u548c\u5c1a\u90a3\u53d7 \u5f97\u4e86\u5982\u6b64\u8fb1\u9a82\uff0c\u5415\u56db\u5a18\u8bdd\u58f0\u672a\u6bd5\uff0c\u4ed6\u5df2\u4e00\u6756\u626b\u6765\uff01 \u3000\u3000\u8fd9\u4e00\u6756\u731b\u70c8\u4e4b\u6781\uff0c\u52b2\u98ce\u8d77\u5904\uff0c\u7802\u77f3\u7eb7\u98de\uff01\u5415\u56db\u5a18\u51cc\u7a7a\u4e00\u8dc3\uff0c\u7985\u6756\u547c\u7684\u4e00\u58f0\u4ece\u811a\u5e95\u626b\u8fc7\u3002 \u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e86\u56e0\u4e00\u6756\u4e0d\u4e2d\uff0c\u7acb\u628a\u6756\u8eab\u5411\u524d\u4e00\u9001\uff0c\u9aa4\u7136\u4e00\u6307\uff0c\u6756\u5c3e\u8d77\u5904\uff0c\u201c\u6bd2\u86c7\u5bfb \u7a74\u201d\uff0c\u76f4\u53d6\u5415\u56db\u5a18\u7684\u201c\u8840\u6d77\u7a74\u201d\uff0c\u5415\u56db\u5a18\u4e00\u4e2a\u5012\u7ffb\uff0c\u843d\u5728\u5730\u4e0a\uff0c\u7985\u6756\u63a0\u9762\u800c\u8fc7\uff0c\u8eab\u5f62\u672a\u5b9a\uff0c \u4e86\u56e0\u7b2c\u4e09\u6756\u53c8\u5377\u5730\u626b\u6765\uff0c\u4e00\u62db\u201c\u6a2a\u626b\u5343\u519b\u201d\uff0c\u53c8\u5df2\u62e6\u8170\u626b\u5230\uff01\u5415\u56db\u5a18\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u4e09\u5ea6\u95ea \u5f00\u3002\u767d\u6cf0\u5b98\u5510\u6653\u6f9c\u89c1\u5415\u56db\u5a18\u8282\u8282\u9000\u540e\uff0c\u60ca\u9669\u4e07\u5206\uff0c\u5927\u4e3a\u9a87\u60e7\uff01 \u3000\u3000\u5415\u56db\u5a18\u8fde\u907f\u4e09\u6756\uff0c\u9000\u540e\u51e0\u6b65\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5728\u573a\u5217\u4f4d\u82f1\u96c4\u89c1\u8bc1\uff0c\u5f1f\u5b50\u4f9d\u793c\u8ba9\u4e86\u4e09\u62db\uff0c\u540c \u95e8\u4e4b\u8c0a\u5df2\u7edd\uff0c\u4eca\u65e5\u4ee3\u5148\u5e08\u6574\u987f\u95e8\u98ce\uff0c\u8bf7\u5404\u4f4d\u4e0d\u8981\u602a\u8d23\uff01\u201d\u767d\u6cf0\u5b98\u8fd9\u624d\u77e5\u9053\u5415\u56db\u5a18\u6267\u884c\u5e08\u5085\u9057 \u547d\uff0c\u8fd8\u8c28\u5b88\u6b66\u6797\u89c4\u77e9\uff0c\u8ba9\u957f\u8f88\u4e09\u62db\u3002\u6697\u8d5e\u8fd9\u4f4d\u5e08\u59b9\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u505a\u4e8b\u5982\u6b64\u8001\u5230\uff0c\u4ec1\u81f3\u4e49\u5c3d\uff0c\u4ea6 \u67d4\u4ea6\u521a\uff0c\u9053\u7406\u7ad9\u5f97\u4f4f\uff0c\u793c\u8282\u4ea6\u65e0\u4e8f\uff0c\u4e0d\u8bba\u8fd9\u573a\u51b3\u6597\u5982\u4f55\uff0c\u5415\u56db\u5a18\u5728\u6c5f\u6e56\u4e0a\u90fd\u5df2\u5927\u5927\u9732\u9762\uff0c\u5360 \u4e86\u4e0a\u98ce\u4e86\uff01 \u3000\u3000\u4e86\u56e0\u8fde\u73af\u4e09\u6756\uff0c\u6756\u6756\u843d\u7a7a\uff0c\u54ac\u5b9e\u7259\u6839\uff0c\u6c89\u6756\u4e00\u626b\uff0c\u559d\u9053\uff1a\u201c\u8d31\u4e2b\u5934\uff0c\u8c01\u8981\u4f60\u8ba9\uff01\u201d\u5415\u56db \u5a18\u67f3\u7709\u5012\u7ad6\uff0c\u6a31\u53e3\u542b\u55b7\uff0c\u5de6\u624b\u6390\u7740\u5251\u8bc0\uff0c\u5530\u7684\u4e00\u5251\uff0c\u4e00\u62db\u201c\u4ed9\u4eba\u6307\u8def\u201d\uff0c\u76f4\u6307\u4e86\u56e0\u80c1\u4e0b\uff0c\u4e86 \u56e0\u7acb\u8d77\u7985\u6756\uff0c\u4e00\u4e2a\u7ffb\u8eab\uff0c\u201c\u4e4c\u9f99\u76d8\u6811\u201d\uff0c\u6a2a\u626b\u5415\u56db\u5a18\u4e2d\u8def\u3002\u5415\u56db\u5a18\u6258\u5730\u4e00\u8df3\uff0c\u5251\u8eab\u968f\u8fdb\uff0c \u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u6307\u5411\u53f3\u80a9\uff0c\u5251\u5c16\u5410\u51fa\u83b9\u83b9\u5bd2\u5149\uff0c\u76f4\u53d6\u4e86\u56e0\u7684\u201c\u80a9\u4e95\u7a74\u201d\uff0c\u4e86\u56e0\u6756\u5c3e\u4e00\u7ffb\uff0c\u53ee \u5f53\u4e00\u58f0\uff0c\u628a\u5415\u56db\u5a18\u5b9d\u5251\u683c\u5f00\u3002\u5415\u56db\u5a18\u7389\u81c2\u9178\u9ebb\uff0c\u7528\u4e86\u4e00\u62db\u201c\u591c\u53c9\u63a2\u6d77\u201d\uff0c\u968f\u52bf\u5c48\u4f38\uff0c\u628a\u4e86\u56e0 \u7684\u7985\u6756\u5e26\u51fa\u5916\u95e8\uff0c\u6d88\u4e86\u4ed6\u7684\u6076\u52bf\u3002\u4e24\u4eba\u6362\u4e86\u4e00\u62db\uff0c\u5404\u5177\u6212\u5fc3\uff0c\u7ed5\u573a\u76d8\u65cb\uff0c\u5bfb\u9050\u62b5\u9699\uff0c\u8c01\u90fd\u4e0d \u6562\u5192\u8fdb\uff01 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u5168\u573a\u60ca\u5f02\uff0c\u5c31\u662f\u89c1\u8fc7\u5415\u56db\u5a18\u529f\u592b\u7684\u767d\u6cf0\u5b98\u4e5f\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\u5979\u5c45\u7136\u80fd\u548c\u4e86\u56e0\u6253\u5e73 \u624b\u3002\u5929\u53f6\u6563\u4eba\u3001\u795e\u9b54\u53cc\u8001\u3001\u9c7c\u4eae\u5927\u738b\u548c\u54c8\u5e03\u9640\u7b49\u65e0\u4e00\u4e0d\u4f38\u957f\u9888\u9879\uff0c\u6ce8\u89c6\u573a\u5fc3\u3002\u8fd9\u4e9b\u4eba\u90fd\u662f\u4e00 \u7b49\u4e00\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u770b\u51fa\u8fd9\u5bf9\u540c\u95e8\u5e08\u5144\u59b9\uff0c\u6b63\u4ee5\u6700\u4e0a\u4e58\u7684\u6b66\u529f\u62a4\u4e86\u5168\u8eab\uff0c\u4f8d\u673a\u800c\u52a8\uff0c\u90fd\u4e0d\u7981\u548b \u820c\uff01 \u3000\u3000\u4e24\u4eba\u51dd\u795e\u6c89\u6c14\uff0c\u7ed5\u573a\u4e00\u5468\uff0c\u5415\u56db\u5a18\u4e09\u5c3a\u971c\u534e\uff0c\u5411\u524d\u4e00\u5f15\uff0c\u53d1\u4e86\u4e24\u4e2a\u865a\u62db\uff0c\u4e86\u56e0\u7406\u4e5f\u4e0d \u7406\u3002\u5415\u56db\u5a18\u89c1\u4ed6\u4e0d\u5165\u5708\u5957\uff0c\u8ba1\u4e0a\u5fc3\u5934\uff0c\u7528\u7384\u5973\u5251\u6cd5\u4e2d\u4f3c\u865a\u4f3c\u5b9e\u7684\u5251\u62db\uff0c\u8fde\u53d1\u4e86\u5341\u51e0\u7740\u865a\u62db\uff0c \u6270\u4e71\u4e86\u56e0\u773c\u795e\uff0c\u89d1\u4e2a\u771f\u5207\uff0c\u5251\u5149\u95ea\u5904\uff0c\u7a81\u7136\u7531\u865a\u5316\u5b9e\uff0c\u4e00\u62db\u201c\u767d\u9e64\u5254\u7fce\u201d\uff0c\u5251\u631f\u91d1\u98ce\uff0c\u84e6\u5411 \u4e86\u56e0\u5f53\u80f8\u523a\u53bb\u3002\u4e86\u56e0\u706b\u5019\u4f55\u7b49\u8001\u5230\uff0c\u4e00\u89c1\u5415\u56db\u5a18\u624b\u6cd5\uff0c\u4fbf\u77e5\u5979\u7531\u865a\u5316\u5b9e\uff0c\u5c06\u8ba1\u5c31\u8ba1\uff0c\u8eab\u8eaf\u9661 \u7136\u4e00\u7f29\uff0c\u5415\u56db\u5a18\u5251\u5c16\u770b\u770b\u6cbe\u8863\uff0c\u5374\u5ffd\u7136\u6251\u4e86\u4e2a\u7a7a\uff0c\u91cd\u5fc3\u9aa4\u5931\uff0c\u4e86\u56e0\u864e\u543c\u4e00\u58f0\uff0c\u7897\u53e3\u7c97\u7684\u7985\u6756 \u731b\u7684\u4e00\u62a2\uff0c\u5df2\u622a\u7740\u4e86\u5415\u56db\u5a18\u9000\u8def\uff01\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6756\u5f71\u5982\u5c71\uff0c\u6a2a\u626b\u4e0b\u538b\uff0c\u5411\u5415\u56db\u5a18\u5f53\u5934\u7f69 \u4e0b\uff0c\u8fd9\u4e00\u7740\u6bd2\u8fa3\u5f02\u5e38\uff0c\u7adf\u8981\u628a\u5415\u56db\u5a18\u7f6e\u4e8e\u6b7b\u5730\uff01\u5415\u56db\u5a18\u8eab\u4e34\u7edd\u5883\uff0c\u770b\u6765\u5df2\u662f\u4e07\u96be\u9003\u8131\uff01 \u3000\u3000\u5510\u6653\u6f9c\u60c5\u6025\u60ca\u547c\uff0c\u6756\u98ce\u4eba\u5f71\u4e2d\u4e5f\u770b\u4e0d\u6e05\u5415\u56db\u5a18\u662f\u7528\u4ec0\u4e48\u8eab\u6cd5\uff0c\u7adf\u7136\u51cc\u7a7a\u63a0\u8d77\u4e86\u4e09\u4e08\u591a \u9ad8\u3002\u672c\u6765\u5979\u88ab\u4e86\u56e0\u7985\u6756\u5708\u4f4f\uff0c\u5c01\u4e86\u53bb\u8def\uff0c\u4e0d\u8bba\u5411\u65c1\u95ea\u907f\u6216\u5411\u4e0a\u8df3\u8dc3\uff0c\u90fd\u96be\u9003\u4e00\u6756\u4e4b\u707e\uff0c\u4e0d\u6599 \u5979\u5c31\u5728\u8fd9\u6b7b\u751f\u4fc4\u9877\uff0c\u6027\u547d\u547c\u5438\u4e4b\u95f4\uff0c\u663e\u51fa\u4e86\u5353\u8d8a\u8f7b\u529f\uff0c\u975e\u51e1\u5251\u672f\uff0c\u5b9d\u5251\u4e00\u4f38\uff0c\u5251\u5c16\u5728\u6756\u5934\u4e00 \u70b9\u4e00\u6309\uff0c\u501f\u7740\u4e86\u56e0\u7684\u731b\u529b\uff0c\u6574\u4e2a\u8eab\u5b50\u53cd\u5f39\u8d77\u6765\uff0c\u4e00\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\uff0c\u5df2\u5012\u7ffb\u51fa\u6570\u4e08\u5f00\u5916\uff01 \u8fd9\u4e00\u4e0b\u4ee4\u5f97\u5728\u573a\u9ad8\u624b\u90fd\u4e0d\u81ea\u7981\u7684\u559d\u8d77\u91c7\u6765\uff01 \u3000\u3000\u559d\u91c7\u58f0\u4e2d\uff0c\u4e86\u56e0\u548c\u5c1a\u62a1\u6756\u6025\u4e0a\uff0c\u5415\u56db\u5a18\u8eab\u5f62\u672a\u5b9a\uff0c\u53c8\u9047\u9669\u62db\uff0c\u6025\u5fd9\u53d1\u5251\u62b5\u6321\uff0c\u5df1\u88ab\u4e86\u56e0 \u62a2\u5728\u4e0a\u9996\uff0c\u5360\u4e86\u5148\u673a\u3002\u4e86\u56e0\u5185\u529f\u6df1\u6e5b\uff0c\u5916\u529b\u96c4\u539a\uff0c\u62a1\u8d77\u7985\u6756\uff0c\u547c\u547c\u8f70\u8f70\uff0c\u56db\u9762\u516b\u65b9\uff0c\u90fd\u662f\u4e00 \u7247\u6756\u5f71\uff0c\u771f\u6709\u6392\u5c71\u5012\u6d77\u4e4b\u52bf\uff0c\u98ce\u96f7\u7a81\u51fb\u4e4b\u5a01\uff0c\u5e73\u5e38\u7684\u4eba\uff0c\u4f11\u8bf4\u5403\u4ed6\u4e00\u6756\uff0c\u53ea\u53d7\u6756\u98ce\u9707\u8361\uff0c\u4e5f \u8981\u4e94\u810f\u4ff1\u4f24\u3002\u5415\u56db\u5a18\u867d\u4ed7\u7740\u7edd\u9876\u8f7b\u529f\uff0c\u4e0a\u4e58\u5251\u6cd5\uff0c\u5728\u6756\u98ce\u9707\u8361\u4e2d\uff0c\u4e5f\u662f\u65e0\u6cd5\u53cd\u653b\uff0c\u8eab\u5982\u4e00\u53f6 \u8f7b\u821f\uff0c\u5728\u6ce2\u6d9b\u6c79\u8bf5\u3001\u5de8\u6d41\u6025\u6e4d\u4e4b\u4e2d\uff0c\u9707\u5f97\u98d8\u6447\u4e0d\u5b9a\uff0c\u8d77\u4f0f\u56de\u65cb\u3002\u5fc3\u60f3\uff1a\u4e86\u56e0\u662f\u540c\u95e8\u4e4b\u9996\uff0c\u529f \u529b\u6df1\u539a\uff0c\u679c\u7136\u975e\u81ea\u5df1\u6240\u53ca\uff0c\u8fd9\u6837\u56f0\u6597\uff0c\u81ea\u5df1\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u5168\u65e0\u8fd8\u51fb\u4e4b\u529b\uff0c\u65f6\u95f4\u4e00\u957f\uff0c\u5fc5\u65e0 \u5e78\u514d\u3002\u94f6\u7259\u4e00\u54ac\uff0c\u628a\u7384\u5973\u5251\u6cd5\u4e2d\u6700\u7cbe\u5999\u7684\u5251\u62db\u65bd\u5c55\u51fa\u6765\uff0c\u62fc\u547d\u8fdb\u653b\uff0c\u98d2\u98d2\u8fde\u58f0\uff0c\u6d51\u8eab\u4e0a\u4e0b\uff0c \u7adf\u4f3c\u95ea\u8d77\u5343\u767e\u9053\u7cbe\u8292\u51b7\u7535\uff0c\u903c\u5f97\u4e86\u56e0\u773c\u82b1\u64a9\u4e71\uff0c\u4e0d\u7531\u81ea\u4e3b\uff0c\u9000\u4e86\u51e0\u6b65\u3002\u5415\u56db\u5a18\u9e70\u7fd4\u96bc\u523a\uff0c\u8fd0 \u5251\u5982\u98ce\uff0c\u5530\u5530\u4e00\u8fde\u51e0\u5251\uff0c\u4ee5\u653b\u4e3a\u5b88\uff0c\u89e3\u4e86\u56f0\u52bf\uff0c\u8131\u51fa\u5305\u56f4\uff0c\u518d\u62a2\u4e86\u6709\u5229\u65b9\u4f4d\uff0c\u548c\u4e86\u56e0\u6b7b\u6218\uff01 \u3000\u3000\u4e86\u56e0\u89c1\u5415\u56db\u5a18\u5c45\u7136\u80fd\u5728\u4ed6\u4e25\u5bc6\u5c01\u95ed\u4e4b\u4e0b\uff0c\u8131\u9669\u51fa\u53bb\uff0c\u6273\u6210\u5e73\u624b\u5c40\u52bf\uff0c\u4e0d\u7981\u4e5f\u6697\u81ea\u5fc3\u60ca\uff0c \u6697\u6068\u5e08\u5085\u504f\u5fc3\uff0c\u6559\u51fa\u5f92\u5f1f\uff0c\u7adf\u7136\u4e00\u4e2a\u5f3a\u4f3c\u4e00\u4e2a\uff0c\u540e\u6765\u5c45\u4e0a\u3002\u4e03\u5f1f\u7518\u51e4\u6c60\u51fa\u5e08\u672a\u6ee1\u5341\u5e74\uff0c\u5a01\u540d \u5df2\u76d6\u8fc7\u81ea\u5df1\uff0c\u800c\u8fd9\u4e2a\u5415\u56db\u5a18\uff0c\u521d\u6b21\u51fa\u9053\uff0c\u6b66\u529f\u66f4\u662f\u597d\u5f97\u51fa\u5947\uff0c\u81ea\u5df1\u51e0\u5341\u5e74\u529f\u529b\uff0c\u7adf\u62ff\u5979\u65e0 \u6cd5\uff0c\u5c82\u4e0d\u5fc3\u5bd2\u3002\u8981\u77e5\u4e86\u56e0\u5728\u5e08\u5085\u6b7b\u540e\uff0c\u80c6\u6562\u653e\u8086\u80e1\u4e3a\uff0c\u5c31\u56e0\u81ea\u6301\u6b66\u529f\uff0c\u5929\u4e0b\u5df2\u65e0\u4eba\u5236\u5f97\uff0c\u800c \u4eca\u5e08\u5f1f\u5e08\u59b9\uff0c\u4e00\u4e2a\u63a5\u7740\u4e00\u4e2a\u7684\u8d76\u4e86\u4e0a\u6765\uff0c\u6784\u6210\u5a01\u80c1\uff0c\u4e0d\u7981\u6012\u4ece\u5fc3\u91cc\u8d77\uff0c\u6076\u5411\u80c6\u8fb9\u751f\uff0c\u7985\u6756\u4e00 \u6454\uff0c\u7adf\u7528\u51f6\u72b7\u7edd\u4f26\u7684\u6756\u6cd5\uff0c\u5bf9\u4ed8\u8fd9\u521d\u51fa\u9053\u7684\u5e08\u59b9\uff01 \u3000\u3000\u4e86\u56e0\u60ef\u7ecf\u9635\u4ed7\uff0c\u706b\u5019\u8001\u5230\uff0c\u9605\u5386\u6781\u6df1\uff0c\u4e0e\u5415\u56db\u5a18\u6218\u4e86\u4e00\u9635\uff0c\u5df2\u77e5\u5415\u56db\u5a18\u5251\u6cd5\u867d\u9ad8\uff0c\u8f7b\u529f \u867d\u5de7\uff0c\u4f46\u8bba\u5185\u529f\u6df1\u539a\uff0c\u8fdc\u975e\u81ea\u5df1\u53ef\u6bd4\u3002\u56e0\u6b64\uff0c\u4e0d\u60dc\u6d88\u8017\u7cbe\u529b\uff0c\u628a\u6700\u51f6\u72b7\u7684\u4f0f\u9b54\u6756\u6cd5\u65bd\u5c55\u51fa \u6765\uff0c\u6a2a\u6311\u76f4\u683c\uff0c\u5de6\u6321\u53f3\u67b6\uff0c\u4e0a\u4e0b\u7ffb\u98de\uff0c\u5b9b\u5982\u4e00\u6761\u6bd2\u9f99\uff0c\u5f20\u7259\u821e\u722a\uff0c\u6756\u5f71\u5982\u5c71\uff0c\u628a\u5415\u56db\u5a18\u518d\u5ea6 \u56f0\u4f4f\uff01\u4f46\u5415\u56db\u5a18\u8fd0\u5251\u5982\u98ce\uff0c\u864e\u8dc3\u9e70\u7fd4\uff0c\u5e26\u5b88\u5e26\u653b\uff0c\u867d\u7136\u662f\u5904\u5728\u4e0b\u98ce\uff0c\u4e86\u56e0\u5374\u4e5f\u5948\u4f55\u5979\u4e0d\u5f97\uff01 \u3000\u3000\u4e24\u4eba\u8f97\u8f6c\u653b\u62d2\uff0c\u53c8\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u4e86\u56e0\u52c7\u731b\u5982\u521d\uff0c\u800c\u5415\u56db\u5a18\u4e5f\u8f7b\u7075\u4f9d\u65e7\uff0c\u8fd9\u65f6\u65b0\u6708\u5df2\u81f3 \u5929\u5fc3\uff0c\u5c71\u9876\u7684\u6f14\u6b66\u573a\u4e0a\u4ecd\u662f\u706b\u628a\u901a\u660e\u3001\u6ca1\u6709\u4e00\u4e2a\u4eba\u611f\u5230\u534a\u4e1d\u5026\u610f\uff01 \u3000\u3000\u6597\u5230\u5206\u9645\uff0c\u5415\u56db\u5a18\u53c8\u662f\u6ee1\u573a\u6e38\u8d70\uff0c\u60f3\u7528\u5bf9\u4ed8\u6d77\u4e91\u548c\u5c1a\u7684\u6218\u672f\u5bf9\u4ed8\u5e08\u5144\uff0c\u90a3\u6599\u4e86\u56e0\u7985\u6756\u53c8 \u7c97\u53c8\u957f\uff0c\u529f\u529b\u4e5f\u975e\u6d77\u4e91\u53ef\u6bd4\uff0c\u5415\u56db\u5a18\u8fd9\u4e00\u6e38\u8d70\uff0c\u7ed9\u4ed6\u8854\u5c3e\u6025\u8ffd\uff0c\u957f\u5175\u5668\u6070\u628a\u5b9d\u5251\u514b\u4f4f\uff0c\u6756\u5934 \u70b9\u5230\u80cc\u5fc3\uff0c\u4e24\u4eba\u7ed5\u573a\u8ffd\u9010\u4e00\u5468\uff0c\u5415\u56db\u5a18\u9669\u8c61\u9891\u751f\uff0c\u60f3\u8d77\u6218\u672f\u4e43\u56e0\u4eba\u800c\u65bd\uff0c\u5bf9\u4ed8\u4e86\u56e0\uff0c\u9000\u5b88\u793a \u5f31\uff0c\u7edd\u975e\u529e\u6cd5\u3002\u500f\u7136\u4e00\u4e2a\u7ffb\u8eab\uff0c\u518d\u7528\u8fdb\u624b\u7684\u62db\u6570\u548c\u4e86\u56e0\u62a2\u653b\uff01\u63a5\u8fde\u51e0\u5251\uff0c\u201c\u52b2\u98ce\u626b\u53f6\u201d\u201c\u9ad8 \u7956\u65a9\u86c7\u201d\u201c\u731b\u9e21\u593a\u7c9f\u201d\u3001\u201c\u9f99\u9876\u6458\u73e0\u201d\uff0c\u76f4\u523a\u8fc7\u6765\uff01\u4e86\u56e0\u62a1\u52a8\u7985\u6756\uff0c\u4e00\u4e00\u6321\u8fc7\uff0c\u4f46\u5415\u56db\u5a18\u4e5f \u8d81\u6b64\u65f6\u673a\uff0c\u7ad9\u7a33\u811a\u6b65\uff0c\u7f13\u8fc7\u6c14\u6765\uff0c\u548c\u4e86\u56e0\u4ee5\u653b\u5bf9\u653b\uff0c\u53c8\u6273\u6210\u4e86\u5e73\u624b\u5c40\u52bf\uff01 \u3000\u3000\u8fd9\u4e00\u4ed7\u5df2\u6253\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u4e24\u4eba\u8fd8\u662f\u82e6\u6218\u4e0d\u4f11\uff0c\u5404\u65e0\u8fdb\u5c55\u3002\u4e86\u56e0\u80dc\u5728\u529f\u529b\u96c4\u539a\uff0c\u5185\u52b2\u6df1 \u957f\uff0c\u800c\u5415\u56db\u5a18\u5219\u80dc\u5728\u8f7b\u7075\u5de7\u5999\uff0c\u5251\u6cd5\u7cbe\u5947\uff0c\u4e24\u4eba\u5728\u6f14\u6b66\u573a\u4e0a\uff0c\u5154\u8d77\u9e43\u843d\uff0c\u8d8a\u6597\u8d8a\u51f6\uff0c\u8d8a\u6765\u8d8a \u9669\uff0c\u5f80\u5f80\u53ea\u4e89\u77ac\u606f\u5148\u540e\uff0c\u7a0d\u4e00\u4e0d\u614e\uff0c\u5c31\u8981\u8840\u6e85\u9ec4\u6c99\u3002\u5728\u573a\u9ad8\u624b\uff0c\u770b\u5f97\u77a9\u76ee\u60ca\u5fc3\uff0c\u9c7c\u4eae\u5927\u738b\u6084 \u6084\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u62fc\u6597\uff0c\u4f55\u65f6\u7f62\u4f11\uff0c\u54c8\u603b\u7ba1\u3001\u5929\u53f6\u6563\u4eba\uff0c\u4f60\u4eec\u770b\u8fd9\u53ef\u5982\u4f55\u4e86\u5c40\uff1f\u201d\u9c7c\u4eae\u5927\u738b\u5fc3 \u60f3\u53ea\u6709\u5929\u53f6\u6563\u4eba\u4e0e\u54c8\u5e03\u9640\u4e8c\u4eba\u6216\u8005\u6709\u6b64\u529f\u529b\uff0c\u53ef\u5c06\u4e86\u56e0\u548c\u5415\u56db\u5a18\u62c6\u5f00\uff0c\u56e0\u6b64\u51fa\u8a00\u793a\u610f\u3002\u5929\u53f6 \u6563\u4eba\u6de1\u6de1\u4e00\u7b11\uff0c\u54c8\u5e03\u9640\u4e5f\u6447\u4e86\u6447\u5934\u3002\u4e24\u4eba\u6b66\u529f\u8eab\u4efd\u548c\u4e86\u56e0\u90fd\u5dee\u4e0d\u591a\uff0c\u975e\u5230\u6700\u540e\u5173\u5934\uff0c\u90a3\u80af\u51fa \u573a\u6b62\u6597\uff0c\u843d\u4e2a\u4ee5\u5927\u6b3a\u5c0f\u4ee5\u4f17\u51cc\u5be1\u7684\u6076\u540d\u3002 \u3000\u3000\u53c8\u6597\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u4e86\u56e0\u8d8a\u6218\u8d8a\u52c7\uff0c\u5415\u56db\u5a18\u4e5f\u662f\u8d8a\u6218\u8d8a\u7075\u3002\u4e86\u56e0\u53ea\u89c9\u5415\u56db\u5a18\u5251\u6cd5\uff0c\u67d4\u5982\u67f3 \u7d6e\uff0c\u5feb\u82e5\u98de\u9e3f\uff0c\u65e0\u6cd5\u514b\u5f97\u5979\u7740\uff01\u5415\u56db\u5a18\u4e5f\u89c9\u4e86\u56e0\u529b\u731b\u5982\u864e\uff0c\u6756\u91cd\u5982\u5c71\uff0c\u4e07\u96be\u53d6\u80dc\uff01\u4e24\u4eba\u529f\u529b \u6089\u654c\uff0c\u53c8\u90fd\u4e0d\u80fd\u7f62\u6218\uff0c\u53ea\u597d\u5404\u663e\u5947\u80fd\uff0c\u7ee7\u7eed\u62fc\u6597\uff0c\u6218\u5230\u6025\u5904\uff0c\u5415\u56db\u5a18\u51e0\u4e4e\u662f\u8fde\u4eba\u5e26\u5251\u5316\u6210\u4e00 \u9053\u767d\u5149\uff0c\u4e86\u56e0\u4e5f\u51e0\u4e4e\u662f\u8fde\u4eba\u5e26\u6756\u5708\u6210\u94c1\u58c1\u94dc\u5899\uff0c\u597d\u6bd4\u94dc\u949f\u649e\u7740\u94c1\u58c1\uff0c\u731b\u864e\u9047\u7740\u59e3\u9f99\uff0c\u4e00\u5251\u4e00 \u6756\uff0c\u4e0a\u4e0b\u7ffb\u98de\uff0c\u5140\u662f\u6740\u5f97\u80dc\u8d1f\u96be\u5206\uff0c\u76f8\u9022\u654c\u624b\uff01 \u3000\u3000\u8fd9\u65f6\u5019\u4e0d\u4f46\u9c7c\u4eae\u53eb\u82e6\uff0c\u5c31\u662f\u5415\u56db\u5a18\u548c\u4e86\u56e0\u4e5f\u5404\u5728\u5fc3\u4e2d\u53eb\u82e6\uff0c\u5728\u5415\u56db\u5a18\u662f\u5b64\u8eab\u72af\u9669\uff0c\u82e5\u7136 \u4e0d\u80dc\uff0c\u600e\u80fd\u8131\u9669\u4e0b\u5c71\uff1f\u5728\u4e86\u56e0\u662f\u4efd\u5c5e\u5e08\u5144\uff0c\u5f53\u6b64\u4f17\u76ee\u777d\u777d\uff0c\u82e5\u7136\u4e0d\u80dc\uff0c\u600e\u597d\u5411\u5929\u4e0b\u82f1\u96c4\u4ea4 \u4ee3\uff01\u6240\u4ee5\u4e24\u4eba\u90fd\u660e\u77e5\u65e0\u6cd5\u53d6\u80dc\uff0c\u4f46\u5df2\u52bf\u6210\u9a91\u864e\uff0c\u4e0d\u5f97\u4e0d\u54ac\u7259\u82e6\u6597\uff01\u5415\u56db\u5a18\u6218\u4e86\u4e24\u4e2a\u591a\u65f6\u8fb0\uff0c \u5df2\u662f\u9999\u6c57\u6dcb\u6d12\uff0c\u4e86\u56e0\u867d\u5185\u529b\u6df1\u957f\uff0c\u4e5f\u5f00\u59cb\u6709\u4e9b\u6c14\u5598\uff01 \u3000\u3000\u9c7c\u4eae\u89c1\u72b6\uff0c\u53eb\u58f0\u201c\u4e0d\u597d\uff01\u201d\u518d\u6218\u4e0b\u53bb\uff0c\u53ea\u6015\u4e24\u4eba\u90fd\u8981\u540c\u5f52\u4e8e\u5c3d\uff0c\u5415\u56db\u5a18\u6bc1\u6389\uff0c\u4e5f\u8fd8\u7f62 \u4e86\uff0c\u4e86\u56e0\u6bc1\u6389\uff0c\u81ea\u5df1\u5c82\u4e0d\u8981\u53d7\u56db\u7687\u5b50\u602a\u8d23\uff1f\u800c\u4e14\u8fd9\u4e48\u591a\u9ad8\u624b\u5728\u573a\uff0c\u8981\u4ee4\u4e86\u56e0\u6bc1\u6389\uff0c\u4e5f\u5b9e\u65e0\u6b64 \u7406\u3002\u5f53\u4e0b\u518d\u987e\u4e0d\u5f97\u6c5f\u6e56\u89c4\u77e9\uff0c\u6b63\u8981\u8bf7\u54c8\u5e03\u9640\u548c\u5929\u53f6\u6563\u4eba\u51fa\u573a\uff0c\u6697\u52a9\u4e86\u56e0\uff0c\u89e3\u5f00\u6218\u6597\u3002\u5c1a\u672a\u5f00 \u58f0\uff0c\u8fd9\u4e24\u4eba\u5df2\u4e0d\u7ea6\u800c\u540c\uff0c\u53cc\u53cc\u8dc3\u4e0b\u573a\u5b50\uff01 \u3000\u3000\u54c8\u5e03\u9640\u4e0e\u5929\u53f6\u6563\u4eba\u5404\u6709\u5fc3\u601d\u3002\u54c8\u5e03\u9640\u4e0e\u4e86\u56e0\u662f\u540c\u6076\u76f8\u6d4e\uff0c\u4e24\u4f4d\u4e00\u4f53\uff0c\u5230\u6b64\u5173\u5934\u4e0d\u80fd\u4e0d \u6551\uff01\u5929\u53f6\u6563\u4eba\u672c\u6765\u5992\u5fcc\u4e86\u56e0\u4f4d\u5c45\u4e0a\u5ea7\uff0c\u8bda\u5fc3\u8981\u770b\u4ed6\u7684\u7b11\u8bdd\u3002\u5982\u4eca\u89c1\u4ed6\u6218\u5e08\u59b9\u4e0d\u4e0b\uff0c\u201c\u7b11\u8bdd\u201d \u5df2\u6210\uff0c\u81ea\u5df1\u6b63\u597d\u8d81\u6b64\u65f6\u673a\uff0c\u663e\u4e00\u663e\u6b66\u529f\uff0c\u6b62\u6597\u4e4b\u540e\uff0c\u987a\u624b\u628a\u7b4b\u75b2\u529b\u7aed\u7684\u5415\u56db\u5a18\u64d2\u4f4f\uff0c\u633d\u56de\u521a \u624d\u81ea\u5df1\u5931\u6389\u7684\u9762\u5b50\u3002 \u3000\u3000\u573a\u4e2d\u4e86\u56e0\u3001\u5415\u56db\u5a18\u4e8c\u4eba\uff0c\u5404\u4ee5\u6027\u547d\u76f8\u640f\uff0c\u5168\u795e\u8d2f\u6ce8\uff0c\u6839\u672c\u4e0d\u77e5\u6709\u4eba\u8dc3\u8fdb\u573a\u5fc3\uff0c\u5929\u53f6\u6563\u4eba \u4eba\u672a\u5230\uff0c\u638c\u5148\u53d1\uff0c\u547c\u547c\u4e24\u638c\uff0c\u9065\u51fb\u51fa\u6765\uff0c\u4e86\u56e0\u3001\u5415\u56db\u5a18\u8eab\u5f62\u4e00\u8361\uff0c\u5c1a\u672a\u5206\u5f00\uff0c\u54c8\u5e03\u9640\u4e5f\u5df2\u8d76 \u6765\uff0c\u4e24\u4e2a\u5706\u7403\uff0c\u7834\u7a7a\u63b7\u51fa\u3002 \u3000\u3000\u5c31\u5728\u6b64\u9645\uff0c\u4e00\u6761\u9ed1\u5f71\uff0c\u75be\u5982\u98de\u9e1f\uff0c\u4e5f\u7a81\u964d\u573a\u5fc3\uff0c\u54c8\u5e03\u9640\u7684\u4e24\u4e2a\u5706\u7403\uff0c\u7adf\u7ed9\u9ed1\u5f71\u51cc\u7a7a\u6253 \u843d\uff0c\u6563\u4e0b\u6ee1\u5929\u5200\u96e8\uff01\u539f\u6765\u8fd9\u4e24\u4e2a\u5706\u7403\u7adf\u662f\u767e\u6b65\u4e4b\u5185\u53d6\u4eba\u9996\u7ea7\u5feb\u5982\u95ea\u7535\u7684\u8840\u6ef4\u5b50\uff01\u5929\u53f6\u6563\u4eba\u7b2c \u4e09\u7b2c\u56db\u4e24\u638c\uff0c\u521a\u521a\u7eed\u53d1\uff0c\u731b\u89c9\u52b2\u98ce\u5012\u649e\uff0c\u53cd\u6fc0\u56de\u6765\uff01\u6765\u4eba\u8eab\u6cd5\u5feb\u5f97\u51fa\u5947\uff0c\u54c8\u5e03\u9640\u4e0e\u5929\u53f6\u6563\u4eba \u5c1a\u672a\u770b\u6e05\uff0c\u5df2\u7ed9\u6765\u4eba\u5201\u7740\u624b\u8155\uff0c\u4e00\u624b\u4e00\u4e2a\uff0c\u731b\u7684\u62c9\u5f00\uff0c\u4e24\u4eba\u6c89\u80a9\u7f29\u8098\uff0c\u6025\u628a\u8eab\u5f62\u7a33\u4f4f\uff0c\u5b9a\u775b \u770b\u65f6\uff0c\u6765\u4eba\u6b63\u662f\u72ec\u4e0a\u5b64\u5cf0\u3001\u5355\u9a91\u95ef\u5e2d\u7684\u8001\u4e10\u5a46\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u5168\u573a\u8038\u52a8\uff0c\u6bd4\u770b\u5415\u56db\u5a18\u4e0e\u4e86\u56e0\u4e4b\u6218\uff0c\u66f4\u4ee4\u4eba\u60ca\u5f02\uff01\u8981\u77e5\u54c8\u5e03\u9640\u4e0e\u5929\u53f6\u6563\u4eba\u529f\u529b\u4e0d \u5728\u4e86\u56e0\u4e4b\u4e0b\u3002\u54c8\u5e03\u9640\u7684\u8840\u6ef4\u5b50\u5389\u5bb3\u975e\u5e38\uff0c\u800c\u5929\u53f6\u6563\u4eba\u7684\u638c\u529b\u4e5f\u767b\u5cf0\u9020\u6781\uff0c\u4f46\u4e24\u4eba\u51fa\u624b\u6697\u7b97\uff0c \u90fd\u7ed9\u8fd9\u8001\u4e10\u5a46\u5728\u4e3e\u624b\u6295\u8db3\u4e4b\u95f4\uff0c\u5316\u4e8e\u65e0\u5f62\uff0c\u800c\u4e14\u4e00\u4e2a\u7167\u9762\uff0c\u5c31\u5c06\u4e24\u4eba\u62c9\u5f00\uff0c\u8fd9\u771f\u662f\u4f55\u7b49\u529f\u529b\uff01 \u3000\u3000\u8001\u4e10\u5a46\u624b\u63d0\u53eb\u5316\u68d2\uff0c\u563b\u563b\u51b7\u7b11\uff0c\u731b\u7136\u65a5\u9053\uff1a\u201c\u597d\u4e0d\u8981\u8138\uff0c\u90a3\u6709\u8fd9\u6837\u529d\u67b6\u7684\u9053\u7406\uff1f\u4f60\u770b\u6211 \u7684\uff01\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u5728\u4e86\u56e0\u4e0e\u5415\u56db\u5a18\u4e2d\u95f4\u4e00\u63d2\uff0c\u4e86\u56e0\u7684\u8361\u9b54\u6756\u6cd5\uff0c\u6b63\u4f7f\u5230\u201c\u7ffb\u6c5f\u6405\u6d77\u201d\u8fd9\u62db\uff0c \u7528\u5c3d\u5168\u529b\uff0c\u7ed9\u90a3\u53eb\u5316\u68d2\u4e00\u9694\uff0c\u706b\u661f\u84ec\u98de\uff0c\u7985\u6756\u7f3a\u4e86\u4e00\u4e2a\u53e3\uff0c\u90a3\u53eb\u5316\u68d2\u5374\u7eb9\u4e1d\u4e0d\u52a8\uff01\u5415\u56db\u5a18\u4e5f \u6b63\u6070\u6070\u7528\u5230\u201c\u9e70\u51fb\u957f\u7a7a\u201d\u7684\u7edd\u62db\uff0c\u4e00\u5251\u523a\u53bb\uff0c\u4e5f\u6b63\u6b63\u523a\u5728\u53eb\u5316\u68d2\u4e0a\uff0c\u4e5f\u662f\u706b\u661f\u84ec\u98de\uff0c\u7f3a\u4e86\u4e2a \u53e3\u3002\u8001\u4e10\u5a46\u628a\u68d2\u4e00\u62bd\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u624d\u53eb\u505a\u516c\u5e73\u529d\u67b6\uff0c\u8c01\u8981\u6697\u7b97\uff0c\u51b2\u7740\u6211\u6765\uff01\u201d \u3000\u3000\u4e86\u56e0\u548c\u5415\u56db\u5a18\u500f\u7684\u5206\u5f00\uff0c\u4e86\u56e0\u77aa\u5927\u773c\u775b\uff0c\u770b\u90a3\u9752\u4e1d\u8986\u989d\uff0c\u53d1\u5149\u9274\u4eba\u7684\u8001\u4e10\u5a46\uff0c\u534a\u664c\u8bf4\u4e0d \u51fa\u8bdd\uff0c\u8fd9\u8001\u4e10\u5a46\u529f\u529b\uff0c\u4e86\u56e0\u51fa\u9053\u4ee5\u6765\uff0c\u975e\u89c1\u6240\u672a\u89c1\uff0c\u800c\u4e14\u95fb\u6240\u672a\u95fb\uff0c\u53ea\u51ed\u521a\u624d\u8fd9\u4e00\u62db\uff0c\u5df2\u6df1 \u89c9\u8fd9\u8001\u4e10\u5a46\u529f\u529b\u4e4b\u9ad8\uff0c\u7eb5\u81ea\u5df1\u5e08\u5085\u72ec\u81c2\u795e\u5c3c\u590d\u751f\uff0c\u4e5f\u4e0d\u8fc7\u5982\u6b64\uff01\u4e86\u56e0\u5012\u62d6\u7985\u6756\uff0c\u9a84\u6a2a\u4e4b\u6c14\u987f \u6d88\uff0c\u7a3d\u9996\u95ee\u9053\uff1a\u201c\u8bf7\u95ee\u8001\u524d\u8f88\u6cd5\u8bb3\uff01\u201d\u8001\u4e10\u5a46\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5e08\u5085\u6ca1\u6709\u5411\u4f60\u63d0\u8d77\u6211\u4e48\uff1f \u4f60\u7684\u5e08\u5085\u4e09\u5341\u5e74\u524d\uff0c\u521d\u5b66\u7384\u5973\u5251\u6cd5\uff0c\u66fe\u5230\u5929\u5c71\u89c1\u6211\uff01\u201d\u4e86\u56e0\u731b\u5403\u4e00\u60ca\uff0c\u9aa4\u7136\u60f3\u8d77\u4e00\u4eba\uff0c\u98a4\u58f0 \u95ee\u9053\uff1a\u201c\u524d\u8f88\u6562\u662f\u5929\u5c71\u4e03\u5251\u4e2d\u7684\u6613\u5973\u4fa0\u4e48\uff1f\u201d\u4e86\u56e0\u6b64\u8a00\u4e00\u51fa\uff0c\u5168\u573a\u65e0\u4e0d\u60ca\u9a87\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u585e\u5916\u5f52\u6765\u5934\u672a\u767d\uff0c\u95f4\u5173\u4e07\u91cc\u89c5\u4f20\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e5d\u56de\u3000\u76f8\u4e92\u8ffd\u8e2a\u3000\u6d77\u9685\u9022\u5f02\u58eb\u3000\u8fde\u73af\u63a2\u6848\u3000\u5927\u53a6\u89c1\u5947\u60c5 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e5d\u56de \u76f8\u4e92\u8ffd\u8e2a\u3000\u6d77\u9685\u9022\u5f02\u58eb \u8fde\u73af\u63a2\u6848\u3000\u5927\u53a6\u89c1\u5947\u60c5 \u3000\u3000\u8001\u4e10\u5a46\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\u6211\u7684\u540d\u5b57\u4e48\uff1f\u201d\u9c7c\u4eae\u5927\u738b\u9762\u8272\u60e8\u767d\uff0c\u7a81\u7136\u7259\u6839\u4e00\u54ac\uff0c\u628a\u624b\u4e00 \u6325\uff0c\u573a\u4e2d\u9ad8\u624b\u5168\u90fd\u6d8c\u51fa\uff0c\u4e86\u56e0\u548c\u5c1a\u5012\u62d6\u7985\u6756\uff0c\u9000\u540e\u51e0\u6b65\uff0c\u4e0e\u9c7c\u4eae\u5927\u738b\u5e76\u80a9\u4e00\u7ad9\uff0c\u5de6\u6709\u5929\u53f6\u6563 \u4eba\u3001\u6d77\u4e91\u548c\u5c1a\u3001\u795e\u9b54\u53cc\u8001\uff0c\u53f3\u6709\u51cc\u4e91\u5c9b\u4e3b\u536b\u626c\u5a01\u3001\u592a\u6e56\u5be8\u4e3b\u5b5f\u6b66\u529f\u3001\u9996\u5e2d\u8d35\u5bbe\u54c8\u5e03\u9640\uff0c\u4e5d\u4e2a \u4e00\u7b49\u4e00\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u8054\u6210\u4e00\u7ebf\uff0c\u4e2a\u4e2a\u91d1\u775b\u706b\u773c\uff0c\u76ee\u4e0d\u8f6c\u77ac\u7684\u6ce8\u89c6\u7740\u8001\u4e10\u5a46\uff0c\u4e24\u8fb9\u5f62\u52bf\uff0c\u4e00\u89e6 \u5373\u53d1\u3002 \u3000\u3000\u539f\u6765\u8fd9\u8001\u4e10\u5a46\u6b63\u662f\u201c\u5929\u5c71\u4e03\u5251\u201d\u4e2d\u7684\u6613\u5170\u73e0\uff01\u201c\u5929\u5c71\u4e03\u5251\u201d\u6210\u540d\u4e8e\u5eb7\u7199\u521d\u5e74\uff0c\uff08\u4e8b\u8ff9\u8be6 \u89c1\u62d9\u8457\u300a\u4e03\u5251\u4e0b\u5927\u5c71\u300b\uff09\u9694\u4e86\u56db\u4e94\u5341\u5e74\uff0c\u98de\u7ea2\u5dfe\u3001\u51cc\u672a\u98ce\u3001\u5f20\u534e\u662d\u3001\u6842\u4ef2\u660e\u3001\u5192\u6d63\u83b2\u7b49\u4eba\u5148 \u540e\u901d\u4e16\uff0c\u201c\u5929\u5c71\u4e03\u5251\u201d\u5c31\u53ea\u5269\u4e0b\u6613\u5170\u73e0\u548c\u6b66\u743c\u7476\u4e24\u4eba\u4e86\u3002\u201c\u5929\u5c71\u4e03\u5251\u201d\u8fdc\u5904\u897f\u9672\uff0c\u5916\u4eba\u4e0d \u77e5\uff0c\u90fd\u4ee5\u4e3a\u4ed6\u4eec\u65e9\u5df2\u6b7b\u5c3d\uff0c\u4e0d\u6599\u201c\u4e03\u5251\u201d\u4e4b\u4e00\u7684\u6613\u5170\u73e0\u4f1a\u7a81\u7136\u4ece\u5929\u5c71\u6765\u5230\u6d77\u9685\uff01 \u3000\u3000\u6613\u5170\u73e0\u7684\u5251\u6cd5\u5728\u201c\u5929\u5c71\u4e03\u5251\u201d\u4e4b\u4e2d\u9996\u5c48\u4e00\u6307\uff0c\uff08\u672c\u6765\u662f\u51cc\u672a\u98ce\u6700\u5f3a\uff0c\u540e\u6765\u51cc\u672a\u98ce\u65ad\u4e86\u53f3 \u624b\u62c7\u6307\uff0c\u4f7f\u5251\u4e0d\u4fbf\uff0c\u628a\u6bd5\u751f\u5fc3\u5f97\u5168\u90fd\u4f20\u7ed9\u4e86\u6613\u5170\u73e0\u3002\uff09\u72ec\u81c2\u795e\u5c3c\u5728\u4e09\u5341\u5e74\u524d\uff0c\u5f97\u4e86\u7384\u5973\u5251 \u8bc0\uff0c\u90a3\u65f6\u5979\u6b66\u529f\u867d\u5df2\u6781\u9ad8\uff0c\u5251\u6cd5\u5374\u662f\u521d\u5b66\uff0c\u8bc0\u7a8d\u4e4b\u5904\uff0c\u82e6\u4e8e\u65e0\u4eba\u6307\u70b9\uff0c\u56e0\u6b64\u72ec\u4e0a\u5929\u5c71\uff0c\u5411\u6613 \u5170\u73e0\u8ba8\u6559\uff0c\u72ec\u81c2\u795e\u5c3c\u6bd4\u6613\u5170\u73e0\u5e74\u957f\uff0c\u6613\u5170\u73e0\u548c\u5979\u5e73\u8f88\u8bba\u4ea4\uff0c\u4e24\u4eba\u5728\u5929\u5c71\u63a2\u8ba8\u5251\u6cd5\uff0c\u805a\u4e86\u534a\u5e74 \u624d\u6563\u3002\u6709\u8fd9\u4e00\u6bb5\u6e0a\u6e90\uff0c\u6240\u4ee5\u6613\u5170\u73e0\u4e5f\u7b97\u5f97\u662f\u6c5f\u5357\u516b\u4fa0\u7684\u957f\u8f88\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u8bba\u5e74\u7eaa\u6613\u5170\u73e0\u867d\u5df2\u516d\u65ec\u6709\u591a\uff0c\u4f46\u5979\u66fe\u670d\u8fc7\u5317\u5929\u5c71\u9a86\u9a7c\u5cf0\u4e0a\u7684\u4f18\u6619\u4ed9\u82b1\uff0c\u53ef\u4fdd\u5934\u53d1 \u6c38\u4e16\u4e0d\u767d\uff0c\u52a0\u4ee5\u5185\u529f\u6df1\u6e5b\uff0c\u6240\u4ee5\u770b\u6765\u4e0d\u8fc7\u56db\u5341\u4f59\u5c81\u3002\u5979\u624b\u4e2d\u62ff\u7684\u53eb\u5316\u68d2\uff0c\u8bf4\u8d77\u4e5f\u5927\u6709\u6765\u5934\uff0c \u8fd9\u4e43\u662f\u5979\u4e08\u592b\u5f20\u534e\u662d\u7684\u9057\u7269\uff0c\u539f\u6765\u662f\u51cc\u672a\u98ce\u524a\u5929\u5c71\u7684\u964d\u9f99\u6728\u6240\u5236\u3002\u9001\u7ed9\u5f20\u534e\u662d\u7684\uff0c\u5176\u540d\u5c31\u79f0 \u4e3a\u201c\u964d\u9f99\u5b9d\u6756\u201d\uff0c\u5929\u5c71\u7684\u964d\u9f99\u6728\u575a\u903e\u94a2\u94c1\uff0c\u5200\u5251\u4e0d\u5165\uff0c\u6240\u4ee5\u5415\u56db\u5a18\u7684\u971c\u534e\u5251\uff0c\u4e86\u56e0\u7684\u8361\u9b54 \u6756\uff0c\u90fd\u7ed9\u5979\u78b0\u5f97\u706b\u82b1\u84ec\u98de\u3002 \u3000\u3000\u5510\u6653\u6f9c\u65e9\u542c\u5468\u9752\u8bf4\u8fc7\u6613\u5170\u73e0\u7684\u8eab\u4efd\uff0c\u8fd9\u65f6\u5fc3\u4e2d\u72c2\u559c\uff0c\u6025\u8dd1\u4e0a\u6765\uff0c\u4e5f\u4e0d\u77e5\u79f0\u547c\u5979\u505a\u4ec0\u4e48\u624d \u5bf9\uff0c\uff08\u5468\u9752\u662f\u51cc\u672a\u98ce\u7684\u6302\u540d\u5f1f\u5b50\uff0c\u51af\u5e7f\u6f6e\u53c8\u662f\u5468\u9752\u7684\u6302\u540d\u5f1f\u5b50\u3002\u5510\u6653\u6f9c\u5219\u5f97\u5468\u9752\u542f\u8499\uff0c\u800c\u5b66 \u5251\u4e8e\u51af\u5e7f\u6f6e\uff09\u53ea\u597d\u5927\u53eb\u201c\u7956\u5a46\u201d\uff0c\u6613\u5170\u73e0\u51dd\u795e\u4e0d\u7b54\uff0c\u5bf9\u65b9\u4e5d\u4e2a\u9ad8\u624b\u4e5f\u4e0d\u6562\u53d1\u96be\u3002\u5415\u56db\u5a18\u7389\u81c2 \u4e00\u4f38\uff0c\u628a\u5510\u6653\u6f9c\u62c9\u4f4f\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u4e71\u8dd1\uff01\u201d\u6613\u5170\u73e0\u7a81\u7136\u4f38\u624b\u628a\u5510\u6653\u6f9c\u7684\u6e38\u9f99\u5251\u62bd\u4e86\u51fa \u6765\uff0c\u6756\u4ea4\u53f3\u624b\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u771f\u8981\u4e0e\u6211\u8fd9\u8001\u4e10\u5a46\u4e3a\u96be\uff1f\u201d \u3000\u3000\u4e86\u56e0\u548c\u5c1a\u8eac\u8170\u8bf4\u9053\uff1a\u201c\u5c0f\u8f88\u600e\u6562\u4e0e\u6613\u5973\u4fa0\u4e3a\u96be\u3002\u201d\u8bf4\u7740\u7528\u7985\u6756\u6307\u4e86\u767d\u6cf0\u5b98\u548c\u5415\u56db\u5a18\u4e00 \u4e0b\uff0c\u6717\u7136\u8bf4\u9053\uff1a\u201c\u4f46\u8fd9\u4e24\u4eba\u4e43\u662f\u8d2b\u618e\u7684\u5e08\u5f1f\u5e08\u59b9\uff0c\u8fd8\u4e0d\u6562\u6709\u52b3\u524d\u8f88\u7ba1\u6559\uff01\u201d\u6613\u5170\u73e0\u6012\u9053\uff1a \u201c\u90a3\u4f60\u662f\u60f3\u7559\u4e0b\u4ed6\u4eec\u4e8c\u4eba\u4e48\uff1f\u201d\u4e86\u56e0\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d\u4e86\u56e0\u81ea\u601d\uff1a\u81ea\u5df1\u867d\u7136\u4e0d\u662f\u6613\u5170\u73e0\u5bf9\u624b\uff0c \u4f46\u5408\u4e5d\u4eba\u4e4b\u529b\uff0c\u5374\u662f\u7a33\u64cd\u80dc\u5238\u3002\u6613\u5170\u73e0\u53cc\u773c\u4e00\u7741\uff0c\u65a5\u9053\uff1a\u201c\u5c31\u662f\u4f60\u5e08\u5085\u5728\u751f\uff0c\u6211\u4e5f\u7ba1\u5f97\uff01\u201d \u6e38\u9f99\u5251\u4e00\u6447\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u771f\u4f3c\u5316\u6210\u4e00\u9053\u767d\u5149\uff0c\u5411\u4e86\u56e0\u548c\u5c1a\u76f4\u5377\u8fc7\u53bb\u3002\u4e86\u56e0\u548c\u5c1a\u6025\u5fd9\u6a2a\u8f6c\u7985 \u6756\uff0c\u632f\u81c2\u4e00\u683c\uff0c\u53ee\u5f53\u4e00\u58f0\uff0c\u6613\u5170\u73e0\u5251\u950b\u8d81\u52bf\u4e00\u8361\uff0c\u8ff3\u81ea\u523a\u5230\u4e86\u56e0\u534f\u4e0b\uff0c\u9c7c\u4eae\u8fd9\u8fb9\u7684\u9ad8\u624b\uff0c\u4e00 \u9f50\u53d1\u52a8\uff0c\u536b\u626c\u5a01\u7684\u86fe\u7709\u523a\u548c\u5b5f\u6b66\u529f\u7684\u864e\u5934\u94a7\uff0c\u7834\u98ce\u6251\u6765\uff0c\u6613\u5170\u73e0\u75be\u98ce\u4e00\u8f6c\uff0c\u624b\u4e2d\u5251\u201c\u529b\u5212\u9e3f \u6c9f\u201d\uff0c\u4e24\u6839\u86fe\u7709\u523a\u65ad\u6210\u56db\u6bb5\uff0c\u4e00\u5bf9\u864e\u5934\u94a9\u9707\u4e0a\u534a\u7a7a\uff01\u4e86\u56e0\u5374\u5df2\u987a\u52bf\u4f7f\u4e2a\u201c\u82cd\u9f99\u5377\u5c3e\u201d\uff0c\u7985\u6756 \u4e00\u8d77\uff0c\u628a\u5b9d\u5251\u71ce\u5f00\uff0c\u4e5d\u540d\u9ad8\u624b\uff0c\u56db\u65b9\u653b\u4e0a\u3002\u6613\u5170\u73e0\u6697\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u8fd9\u53ae\u7316\u72c2\uff0c\u4ed6\u679c\u7136\u5f97\u4e86\u72ec \u81c2\u795e\u5c3c\u771f\u4f20\uff0c\u529f\u529b\u548c\u4ed6\u5e08\u5085\u5dee\u4e0d\u591a\u8fdc\uff01\u201d\u6e05\u53f1\u4e00\u58f0\uff0c\u5c06\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u987b\u5f25\u5251\u5f0f\u4f7f\u5f00\uff0c\u8eab\u5251\u5408 \u4e00\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u5316\u6210\u4e00\u9053\u767d\u5149\uff0c\u5de6\u8361\u53f3\u6289\u3002\u8fd9\u65f6\uff0c\u5ffd\u95fb\u5f97\u5510\u6653\u6f9c\u60e8\u53eb\u4e00\u58f0\uff0c\u539f\u6765\u4ed6\u5de6\u80a9\u5df2\u4e2d \u4e86\u5b5f\u6b66\u529f\u4e00\u638c\u3002\u6613\u5170\u73e0\u5927\u6012\uff0c\u8fde\u4e0b\u6740\u624b\uff01\u5251\u5149\u95ea\u95ea\uff0c\u4e0d\u79bb\u4e86\u56e0\u8981\u5bb3\uff01\u5ffd\u7136\u95f4\u4eba\u58f0\u55a7\u54d7\uff0c\u9c7c\u4eae \u5927\u738b\u5927\u53eb\uff1a\u201c\u6682\u505c\uff01\u201d\u4e5d\u540d\u9ad8\u624b\uff0c\u8fde\u8882\u9000\u4e0b\u3002\u6613\u5170\u73e0\u5b9d\u5251\u6a2a\u80f8\uff0c\u6b62\u6b65\u4e0d\u8ffd\uff0c\u51dd\u795e\u7ec6\u542c\uff0c\u9690\u9690 \u95fb\u5f97\u54ed\u58f0\uff0c\u4ece\u8fdc\u8fdc\u7684\u9ad8\u5904\u4f20\u6765\uff01 \u3000\u3000\u7fa4\u8c6a\u4f4f\u624b\u4e86\u671b\uff0c\u9c7c\u4eae\u5927\u738b\u7684\u5c71\u9876\u522b\u5885\u4e4b\u65c1\uff0c\u539f\u5efa\u6709\u4e00\u5ea7\u5341\u4e09\u5c42\u7684\u767d\u5854\uff0c\u767d\u5854\u9876\u4e0a\uff0c\u9690\u7ea6 \u53ef\u89c1\u4e00\u4e2a\u7ea2\u8863\u5973\u5b50\uff0c\u7ad9\u5728\u6a90\u8fb9\uff0c\u5415\u56db\u5a18\u773c\u5229\uff0c\u5df2\u8ba4\u51fa\u4e86\u90a3\u5973\u5b50\u6b63\u662f\u9c7c\u4eae\u5927\u738b\u7684\u5973\u513f\u9c7c\u5a18\uff0c\u518d \u770b\u6e05\u695a\u65f6\uff0c\u5979\u7adf\u662f\u7f1a\u5728\u6a90\u8fb9\uff0c\u534a\u8eab\u5012\u60ac\uff0c\u53ea\u817e\u51fa\u4e00\u53ea\u53f3\u624b\uff0c\u6267\u7740\u4e00\u67c4\u6717\u6643\u6643\u7684\u5229\u5203\uff0c\u6401\u5728\u7ef3 \u4e0a\uff0c\u53ea\u8981\u5200\u950b\u4e00\u52a8\uff0c\u5c31\u8981\u7ef3\u65ad\u4eba\u5760\uff0c\u4efb\u591a\u597d\u6b66\u529f\uff0c\u4e5f\u6551\u5979\u4e0d\u5f97\uff01\u9c7c\u4eae\u548c\u767d\u6cf0\u5b98\u9f50\u58f0\u60ca\u547c\uff0c\u5446 \u5728\u5f53\u573a\uff01 \u3000\u3000\u8fd9\u65f6\uff0c\u767d\u5854\u5185\u51b2\u51fa\u4e00\u540d\u5934\u76ee\uff0c\u98de\u5954\u5230\u9c7c\u4eae\u5927\u738b\u8ddf\u524d\uff0c\u6253\u4e2a\u5343\u513f\uff0c\u6c14\u6025\u8d25\u574f\u7684\u7980\u9053\uff1a\u201c\u5927 \u738b\uff0c\u4e0d\u597d\u4e86\uff0c\u6211\u4eec\u4e00\u4e2a\u758f\u795e\uff0c\u7adf\u6ca1\u7559\u610f\u5c0f\u59d0\u628a\u81ea\u5df1\u7f1a\u4e86\uff0c\u4f20\u51fa\u6d3b\u6765\uff0c\u8981\u5927\u738b\u5c06\u767d\u6cf0\u5b98\u4ed6\u4eec\u653e \u8d70\uff0c\u4e0d\u7136\u5979\u5c31\u8981\u5272\u65ad\u7ef3\u5b50\uff0c\u548c\u5927\u738b\u6c38\u522b\u4e86\uff01\u201d \u3000\u3000\u9c7c\u5a18\u4e43\u662f\u9c7c\u4eae\u7684\u552f\u4e00\u7231\u5973\uff0c\u9c7c\u4eae\u672c\u5c31\u75bc\u5979\uff0c\u8fd9\u6b21\u53ea\u56e0\u767d\u6cf0\u5b98\u4e0d\u80af\u4f9d\u4ece\uff0c\u6240\u4ee5\u624d\u7981\u6b62\u7231\u5973 \u548c\u4ed6\u76f8\u89c1\uff0c\u5c06\u5979\u56da\u5728\u767d\u5854\u9876\u4e0a\u3002\u9c7c\u5a18\u4e0d\u77e5\u6709\u524d\u8f88\u5973\u4fa0\u72ec\u4e0a\u5b64\u5cf0\uff0c\u51fa\u624b\u76f8\u6551\uff0c\u53ea\u9053\u767d\u6cf0\u5b98\u5728\u6b66 \u6797\u9ad8\u624b\u5305\u56f4\u4e4b\u4e0b\uff0c\u5df2\u9677\u56f0\u5883\uff0c\u4e00\u6a2a\u4e86\u5fc3\uff0c\u7d22\u6027\u4ee5\u6027\u547d\u8981\u631f\uff01\u767d\u6cf0\u5b98\u89c1\u4e86\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u60f3\u4e0d\u5230 \u9c7c\u5a18\u548c\u81ea\u5df1\u76f8\u7231\uff0c\u5982\u6b64\u4e4b\u6df1\uff0c\u5fc3\u4e2d\u611f\u52a8\uff0c\u4e0d\u89c9\u6ef4\u4e0b\u6cea\u6765\uff01 \u3000\u3000\u9c7c\u4eae\u6c89\u601d\u6709\u9877\uff0c\u628a\u624b\u4e00\u6325\uff0c\u8bf4\u9053\uff1a\u201c\u7b97\u4e86\uff0c\u4f60\u4eec\u53bb\u5427\uff01\u201d\u6613\u5170\u73e0\u5728\u4e5d\u540d\u4e00\u7b49\u4e00\u9ad8\u624b\u7684\u73af \u51fb\u4e4b\u4e0b\uff0c\u8981\u81ea\u4fdd\u4e0d\u96be\uff0c\u4f46\u5374\u62c5\u5fc3\u5510\u6653\u6f9c\u4e0e\u767d\u6cf0\u5b98\u4f1a\u906d\u4f24\u5bb3\uff0c\u8d81\u52bf\u6536\u7bf7\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4e86\u56e0\uff0c \u6211\u5e26\u4f60\u7684\u5e08\u5f1f\u5e08\u59b9\u8d70\u4e86\uff0c\u4f60\u80cc\u987a\u8fdd\u6212\u4e4b\u4e8b\uff0c\u81ea\u6709\u4f60\u672c\u95e8\u4e2d\u4eba\u6e05\u7406\u95e8\u6237\uff0c\u6211\u72af\u4e0d\u7740\u4f38\u624b\uff01\u82e5\u4f60 \u53e6\u6709\u4e3a\u975e\u4f5c\u6b79\uff0c\u9c7c\u8089\u5584\u826f\u7684\u4e8b\u649e\u5728\u6211\u7684\u624b\u91cc\uff0c\u6211\u53ef\u4e0d\u8f7b\u9976\u4f60\uff01\u201d\u8bf4\u7f62\uff0c\u5bf9\u9c7c\u4eae\u62f1\u624b\u9053\u58f0\uff1a \u201c\u627f\u8ba9\uff01\u201d\u5c06\u5251\u4ea4\u56de\u5510\u6653\u6f9c\uff0c\u5de6\u624b\u4e00\u5e26\uff0c\u4e0e\u5415\u56db\u5a18\u3001\u767d\u6cf0\u5b98\u5c55\u5f00\u9646\u5730\u98de\u884c\u7684\u672c\u9886\uff0c\u98ce\u9a70\u7535\u63a3 \u822c\u5411\u5c71\u4e0b\u5954\u53bb\u3002 \u3000\u3000\u9c7c\u4eae\u609a\u7136\u4e00\u60ca\uff0c\u84e6\u7136\u9192\u8d77\uff0c\u987f\u8db3\u53eb\u9053\uff1a\u201c\u5feb\uff01\u5feb\uff01\u5feb\u4f20\u4ee4\u4e0b\u53bb\uff01\u53eb\u513f\u90ce\u4eec\u8ba9\u8def\uff01\u201d\u500f\u65f6 \u7ea2\u65d7\u62db\u5c55\uff0c\u55bd\u7f57\u4eec\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6e05\u9053\u9001\u5ba2\uff0c\u4e0d\u5f97\u62e6\u963b\uff01\u201d\u4e00\u7ad9\u4e00\u7ad9\u7684\u4f20\u8fbe\u4e0b\u53bb\u5374\u5df2\u7ecf\u8fdf\u4e86\uff0c \u8fd9\u65f6\u6613\u5170\u73e0\u7b49\u5df2\u81f3\u5c71\u8170\uff0c\u90a3\u4e9b\u5b88\u536b\u5361\u5b50\u7684\u55bd\u5175\uff0c\u672a\u95fb\u5e05\u4ee4\uff0c\u4e00\u58f0\u80e1\u54e8\uff0c\u5728\u5bc6\u6797\u4e1b\u8349\u4e2d\uff0c\u55e4\u55e4 \u8fde\u54cd\uff0c\u65e9\u5df2\u5c04\u51fa\u4e00\u6392\u98de\u8757\u5f29\u7bad\u6765\uff0c\u5415\u56db\u5a18\u54c8\u54c8\u4e00\u7b11\uff0c\u971c\u534e\u5b9d\u5251\u547c\u547c\u63a0\u98ce\uff0c\u524d\u540e\u5de6\u53f3\u5377\u8d77\u5339\u7ec3 \u4f3c\u7684\u4e00\u9053\u5bd2\u5149\uff0c\u98de\u8757\u5f29\u7bad\uff0c\u7eb7\u7eb7\u8dcc\u843d\u5730\u4e0a\u3002\u6de1\u6708\u758f\u661f\u4e4b\u4e0b\uff0c\u56db\u6761\u4eba\u5f71\uff0c\u5b9b\u5982\u56db\u6761\u767d\u7ec3\uff0c\u51b2\u7834 \u98de\u8757\u7bad\u96e8\u3002\u5c71\u8def\u4e24\u8fb9\u57cb\u4f0f\u7684\u6320\u94a9\u624b\uff0c\u54d7\u5566\u5566\u4f38\u51fa\u4e24\u6392\u96ea\u767d\u950b\u5229\u7684\u6320\u94a9\uff0c\u5411\u56db\u4eba\u811a\u4e0b\u75be\u5377\uff0c\u6613 \u5170\u73e0\u964d\u9f99\u5b9d\u6756\u5de6\u53f3\u4e00\u626b\uff0c\u53ea\u542c\u5f97\u5427\u5427\u5427\u5427\uff0c\u4e00\u7247\u65ad\u91d1\u5195\u7389\u4e4b\u58f0\uff0c\u628a\u6320\u94a9\u626b\u65ad\u4e86\u4e03\u516b\u6746\u3002\u90a3\u8fb9 \u53a2\uff0c\u5415\u56db\u5a18\u51fa\u624b\u66f4\u8fa3\uff0c\u4e00\u58f0\u5a07\u53f1\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u5316\u4f5c\u4e00\u9053\u94f6\u8679\uff0c\u5411\u8349\u4e1b\u4e2d\u626b\u53bb\uff0c\u4f0f\u5728\u91cc\u9762\u7684\u56db \u4e2a\u6320\u94a7\u624b\uff0c\u5168\u90e8\u4e2d\u5251\u5012\u5730\uff0c\u8840\u82b1\u56db\u6e85\uff01\u6613\u5170\u73e0\u6025\u9053\uff1a\u201c\u4e0d\u8981\u6740\u8fd9\u4e9b\u5c0f\u55bd\u7f57\uff01\u201d\u8fd9\u65f6\u9c7c\u4eae\u5927\u738b \u53eb\u8ba9\u8def\u7684\u5e05\u4ee4\u624d\u4e00\u7ad9\u4e00\u7ad9\uff0c\u8fdc\u8fdc\u4f20\u6765\uff0c\u57cb\u4f0f\u5728\u5c71\u8170\u5c71\u811a\u7684\u55bd\u7f57\uff0c\u6025\u5fd9\u4e00\u9762\u4f20\u4ee4\uff0c\u4e00\u9762\u907f\u5f00\uff0c \u6613\u5170\u73e0\u7b11\u9053\uff1a\u201c\u8fd9\u624d\u50cf\u4e2a\u9001\u5ba2\u7684\u89c4\u77e9\uff01\u201d\u9c7c\u4eae\u5728\u5cf0\u9876\u4e86\u671b\uff0c\u89c1\u5979\u4eec\u5982\u6b64\u795e\u5a01\uff0c\u4e0d\u7981\u53d8\u8272\uff01 \u3000\u3000\u56db\u4eba\u51fa\u4e86\u7530\u6a2a\u5c9b\uff0c\u4ecd\u4e58\u9c7c\u4eae\u5927\u738b\u9001\u5ba2\u7684\u6d77\u8239\uff0c\u56de\u5230\u9752\u5c9b\u6d77\u6ee8\uff0c\u95f9\u4e86\u4e00\u665a\uff0c\u8fd9\u65f6\u5df2\u662f\u6708\u4eae \u897f\u6c89\uff0c\u6653\u971e\u9690\u73b0\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u4e00\u56e2\u56e2\u767d\u4e91\uff0c\u7d27\u805a\u4e00\u8d77\uff0c\u4e91\u4e2d\u95ea\u53d1\u767d\u5149\uff0c\u4e1c\u65b9\u5929\u8272\u7531\u6726\u80e7\u9010\u6e10 \u53d1\u7ea2\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u4e00\u8f6e\u7ea2\u65e5\u5728\u9065\u8fdc\u7684\u6d77\u9762\u5189\u5189\u5347\u8d77\uff0c\u987f\u65f6\u6620\u8d77\u534a\u5929\u7ea2\u971e\uff0c\u4e3d\u5f69\u971e\u8f89\u5728\u9ec4\u6d77\u4e0a \u5e7b\u6210\u5343\u4e07\u9053\u91d1\u5149\u707f\u76ee\u7684\u5149\u7ebf\u3002\u5510\u6653\u6f9c\u4e0d\u7981\u51fb\u638c\u8d5e\u9053\uff1a\u201c\u671d\u660f\u752b\u655b\uff0c\u6d2a\u6d9b\u4e0d\u60ca\uff1b\u6c34\u9762\u971e\u5149\uff0c\u707f \u70c2\u4e07\u9053\uff1b\u65ed\u8f6e\u7a81\u73b0\uff0c\u9704\u6f20\u987f\u6e05\uff01\u201d\u8fd9\u662f\u6e05\u521d\u624d\u5b50\u4faf\u65b9\u57df\u5199\u4e1c\u6d77\u6d74\u65e5\u7684\u4f73\u53e5\uff0c\u5415\u56db\u5a18\u5fae\u9732\u8bb6 \u610f\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5510\u5144\u5f1f\uff0c\u8fd9\u51e0\u5e74\u6765\u4f60\u8bfb\u4e86\u4e0d\u5c11\u4e66\u554a\uff01\u201d\u5510\u6653\u6f9c\u9762\u4e0a\u4e00\u7ea2\uff0c\u53c8\u662f\u5f97\u610f\uff0c\u53c8\u662f \u60ed\u6127\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u80e1\u4e71\u8bfb\u4e86\u70b9\u4e66\uff0c\u8ba4\u5f97\u51e0\u4e2a\u5b57\u7f62\u4e86\uff01\u59d0\u59d0\u5bb6\u5b66\u6e0a\u6e90\uff0c\u6211\u62dc\u4f60\u505a\u8001\u5e08\uff0c\u53ea\u6015 \u4f60\u8fd8\u4e0d\u80af\u6536\u6211\u8fd9\u6837\u7684\u5b66\u751f\u5462\uff01\u201d\u6613\u5170\u73e0\u767d\u6cf0\u5b98\u7a81\u7136\u542c\u4ed6\u4eec\u8bf4\u8d77\u4e66\u672c\u4e0a\u7684\u8bdd\u6765\uff0c\u751a\u4e3a\u5947\u602a\uff01 \u3000\u3000\u5510\u6653\u6f9c\u4e94\u5e74\u524d\u5728\u9099\u5c71\u521d\u89c1\u5415\u56db\u5a18\u65f6\uff0c\u7a1a\u6c14\u672a\u6d88\uff0c\u5bf9\u5979\u6df1\u5fc3\u503e\u6155\uff0c\u5f53\u65f6\u4ed6\u66fe\u542c\u8fc7\u5415\u56db\u5a18\u79f0 \u8d5e\u5979\u7239\u7239\u4e00\u4e2a\u95e8\u751f\uff0c\u53c8\u66fe\u542c\u8fc7\u5415\u56db\u5a18\u6240\u8bf4\u7684\u201c\u4fa0\u58eb\u4e4b\u4e49\u987b\u914d\u4ee5\u771f\u5112\u4e4b\u8bc6\u201d\u7684\u8bae\u8bba\uff0c\u81ea\u6e10\u5f62 \u79fd\uff0c\u6240\u4ee5\u5728\u6768\u4ef2\u82f1\u95e8\u4e0b\uff0c\u624d\u8981\u6c42\u665a\u4e0a\u8bfb\u4e66\u3002\u4eca\u756a\u6d77\u5c9b\u91cd\u9022\uff0c\u4e0d\u81ea\u89c9\u7684\u629b\u51fa\u4e86\u51e0\u53e5\u4e66\u5305\uff0c\u60f3\u8ba8 \u5415\u56db\u5a18\u7684\u6b22\u559c\u3002\u6613\u5170\u73e0\u54ea\u91cc\u77e5\u9053\u4ed6\u8fd9\u6837\u5fae\u5999\u590d\u6742\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u5415\u56db\u5a18\u542c\u4ed6\u90a3\u4e48\u4e00\u8bf4\uff0c\u7b11\u5f97\u82b1\u679d\u4e71\u98a4\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u770b\u6211\u4f1a\u505a\u4e2a\u6559\u4e66\u5148\u751f\u4e48\uff1f\u201d \u65cb\u800c\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u82e5\u7136\u8c08\u5230\u4e86\u6cbb\u5b66\uff0c\u90a3\u6700\u5c11\u8981\u4e0b\u51e0\u5341\u5e74\u82e6\u529f\uff0c\u4e3b\u4eba\u7693\u9996\u7a77\u7ecf\uff0c\u4f60\u5f53\u662f\u5bb9\u6613\u7684 \u4e48\uff1f\u5bf9\u7ecf\u53f2\u4e4b\u5b66\uff0c\u6211\u81ea\u5df1\u4e5f\u672a\u5165\u95e8\u5462\uff0c\u6211\u7239\u7239\u6709\u4e2a\u95e8\u751f\uff0c\u5e74\u7eaa\u867d\u6bd4\u6211\u4eec\u5927\u4e0d\u4e86\u8bb8\u591a\uff0c\u7ecf\u53f2\u8bcd \u7ae0\uff0c\u5374\u90fd\u5df2\u6709\u4e86\u6839\u5e95\uff0c\u4f60\u82e5\u6709\u5fd7\u4e8e\u5b66\uff0c\u5c06\u6765\u6211\u5012\u53ef\u8350\u4ed6\u7ed9\u4f60\u505a\u8001\u5e08\u3002\u201d\u5415\u56db\u5a18\u80f8\u895f\u5f00\u6717\uff0c\u628a \u4ed6\u5f53\u505a\u5f1f\u5f1f\u770b\u5f85\uff0c\u5fc3\u4e2d\u90a3\u6709\u4e1d\u6beb\u6742\u5ff5\uff0c\u5510\u6653\u6f9c\u542c\u4e86\uff0c\u6005\u7136\u82e5\u5931\uff0c\u4f4e\u4e0b\u4e86\u5934\uff0c\u8bf4\u53e5\u201c\u8c22\u8c22\u201d\u3002 \u3000\u3000\u6613\u5170\u73e0\u542c\u5f97\u4e0d\u8010\u70e6\uff0c\u6253\u65ad\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u8c08\u4e66\u672c\u7684\u4e8b\u4e86\uff0c\u6653\u6f9c\uff0c\u4f60\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u8981\u5bfb\u627e \u4f60\uff0c\u62ff\u4e86\u4f60\u7684\u5251\u53c8\u8fd8\u7ed9\u4f60\u5417\uff1f\u201d\u5510\u6653\u6f9c\u5782\u624b\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u3002\u201d\u6613\u5170\u73e0\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u5c31\u4e3a\u4e86\u4f60 \u8fd9\u628a\u5251\u3002\u201d\u5510\u6653\u6f9c\u60f6\u6050\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u8fd9\u628a\u5251\u662f\u592a\u5e08\u7956\u4f20\u7ed9\u5468\u5e08\u7956\u7684\uff0c\u6211\u5b9e\u5728\u914d\u4e0d\u4e0a\u7528 \u5b83\uff01\u201d\u6613\u5170\u73e0\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u8fd9\u51e0\u5929\u6211\u9ed8\u5bdf\u4f60\u7684\u4eba\u54c1\uff0c\u5c1a\u662f\u6211\u8f88\u4e2d\u4eba\uff0c\u53ea\u662f\u4f60 \u6b66\u529f\u592a\u4f4e\uff0c\u6211\u6015\u4f60\u4e0d\u80fd\u957f\u4fdd\u7740\u5b83\uff01\u201d\u6613\u5170\u73e0\u987f\u4e86\u4e00\u987f\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8fd9\u628a\u5251\u662f\u5929\u5c71\u4e24\u628a\u9547\u5c71 \u4e3b\u5251\u4e4b\u4e00\uff0c\u4e0d\u80fd\u843d\u5728\u5916\u4eba\u624b\u4e0a\u3002\u73b0\u5728\u6666\u660e\u7985\u5e08\u95e8\u4e0b\u53ea\u5269\u6211\u4e00\u4eba\u4e86\uff0c\u800c\u6211\u4e5f\u5df2\u5230\u4e86\u98ce\u70db\u6b8b\u5e74\uff0c \u4e3a\u4e86\u5bf9\u53bb\u4e16\u5e08\u7956\u4f5c\u4e2a\u4ea4\u4ee3\uff0c\u6211\u5fc5\u987b\u627e\u5bfb\u8fd9\u628a\u5251\u7684\u4e0b\u843d\uff0c\u627e\u4e86\u51e0\u5e74\uff0c\u603b\u7b97\u627e\u7740\u4e86\u3002\u4f60\u662f\u5468\u9752\u7684 \u4e49\u5b50\uff0c\u6211\u4e5f\u65e9\u77e5\u9053\u4e86\u3002\u8fd9\u628a\u5251\u73b0\u5728\u6211\u51b3\u5b9a\u7ed9\u4f60\uff0c\u4f46\u4f60\u7684\u5251\u672f\u4e0d\u884c\uff0c\u6709\u5931\u5929\u5c71\u5251\u6d3e\u7684\u5a01\u671b\uff0c\u4f60 \u5f97\u8ddf\u6211\u5b66\u4e09\u5e74\u5251\u672f\uff01\u201d\u5510\u6653\u6f9c\u5927\u559c\uff0c\u6025\u5fd9\u53e9\u5934\u62dc\u5e08\uff0c\u6613\u5170\u73e0\u5c06\u4ed6\u4e00\u624b\u6276\u8d77\uff0c\u8083\u7136\u8bf4\u9053\uff1a\u201c\u6211 \u53ea\u6388\u4f60\u672c\u95e8\u5251\u672f\uff0c\u5176\u4ed6\u6b66\u529f\uff0c\u6765\u4e0d\u53ca\u6559\u4e86\u3002\u6211\u548c\u4f60\u4e5f\u4eff\u51cc\u5927\u4fa0\u4e0e\u5468\u9752\u4e4b\u4f8b\uff0c\u53ea\u80fd\u7b97\u6302\u540d\u5e08 \u5f92\u3002\u5230\u4f60\u5c06\u6765\u82b1\u7532\u4e4b\u5e74\uff0c\u6216\u95ed\u95e8\u5c01\u5200\u4e4b\u65e5\uff0c\u4f60\u5c06\u8fd9\u5251\u7f34\u56de\u5929\u5c71\uff0c\u7ed9\u90a3\u65f6\u7684\u638c\u95e8\u5f1f\u5b50\u3002\u7136\u540e\u7531 \u638c\u95e8\u4eba\u8003\u6838\u4f60\u4e00\u751f\u529f\u8fc7\uff0c\u90a3\u65f6\u624d\u51b3\u5b9a\u8bb8\u4e0d\u8bb8\u4f60\u6b63\u5f0f\u5217\u5165\u95e8\u5899\u3002\u201d\u6b66\u6797\u4e2d\u7684\u6302\u540d\u5f1f\u5b50\uff0c\u7b49\u4e8e\u5b66 \u6821\u4e2d\u7684\u8bd5\u8bfb\u751f\uff0c\u90fd\u662f\u7a0b\u5ea6\u8f83\u4f4e\uff0c\u8fd8\u8981\u7559\u5f85\u8003\u6838\uff0c\u624d\u8bb8\u5347\u7ea7\u7684\u3002 \u3000\u3000\u6613\u5170\u73e0\u5c06\u5510\u6653\u6f9c\u7684\u4e8b\u5904\u7406\u5b8c\u6bd5\uff0c\u5ffd\u7136\u9762\u631f\u5bd2\u971c\uff0c\u5bf9\u767d\u6cf0\u5b98\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u767d\u6cf0\u5b98\uff0c\u5728\u6211\u9762 \u524d\u53ef\u4e0d\u8bb8\u4f60\u8bf4\u8c0e\uff0c\u8fd9\u91cc\u7684\u91c7\u82b1\u6848\u5b50\uff0c\u662f\u4e0d\u662f\u4f60\u5e72\u7684\uff01\u201d \u3000\u3000\u767d\u6cf0\u5b98\u9762\u76ae\u53d8\u8272\uff0c\u6025\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u8fd9\uff0c\u8fd9\u662f\u54ea\u91cc\u8bdd\u6765\uff1f\u6211\uff0c\u6211\u90a3\u4f1a\u5e72\u91c7\u82b1\u7684\u574f \u4e8b\uff1f\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u5f53\u771f\u4e0d\u662f\u4f60\u5417\uff1f\u4f60\u4e14\u7b49\u4e00\u7b49\uff01\u201d\u5ffd\u5730\u957f\u5578\u4e00\u58f0\uff0c\u6d77\u8fb9\u5c0f\u5f84\u7684\u7eff\u6811\u4e1b\u4e2d\uff0c \u7a81\u7136\u73b0\u51fa\u4e00\u4e2a\u7ea2\u8863\u5c11\u5973\uff0c\u53ea\u6709\u5341\u56db\u4e94\u5c81\u5149\u666f\uff0c\u5510\u6653\u6f9c\u8ba4\u5f97\uff0c\u6b63\u662f\u90a3\u65e5\u5728\u9152\u697c\u4e0a\u5531\u66f2\u7684\u59d1\u5a18\uff0c \u6613\u5170\u73e0\u95ee\u9053\uff1a\u201c\u9526\u513f\uff0c\u90a3\u665a\u4ed6\u662f\u4e0d\u662f\u8ddf\u8e2a\u8fc7\u4f60\uff1f\u201d\u5c0f\u59d1\u5a18\u76ef\u4e86\u767d\u6cf0\u5b98\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u6b63\u662f \u4ed6\uff01\u201d\u6613\u5170\u73e0\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u767d\u6cf0\u5b98\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u5979\u662f\u4f60\u7684\u4f84\u5973\uff1f\u600e\u4e48\u4f60\u8ddf\u8e2a\u8d77\u4f60\u7684\u4f84\u5973\u6765 \u4e86\uff01\u201d\u767d\u6cf0\u5b98\u548c\u90a3\u5c0f\u59d1\u5a18\u540c\u65f6\u73b0\u51fa\u60ca\u8bb6\u4e4b\u8272\uff0c\u5c0f\u59d1\u5a18\u9053\uff1a\u201c\u554a\uff01\u539f\u6765\u4f60\u662f\u767d\u4e94\u53d4\uff01\u201d\u767d\u6cf0\u5b98 \u9053\uff1a\u201c\u554a\uff01\u539f\u6765\u4f60\u662f\u9526\u513f\uff0c\u529f\u592b\u7adf\u8fd9\u6837\u7cbe\u8fdb\u4e86\uff01\u6613\u8001\u524d\u8f88\uff0c\u4f60\u8001\u522b\u5f00\u73a9\u7b11\uff0c\u8fd9\u662f\u5929\u5927\u7684\u8bef \u4f1a\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u8bed\u6c14\u7a0d\u7f13\uff0c\u4ecd\u559d\u95ee\u9053\uff1a\u201c\u600e\u4e48\u4e2a\u8bef\u4f1a\uff1f\u201d\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u90a3\u51e0\u5929\u6211\u627e\u6211\u7684\u672a\u5a5a\u59bb\u5b50 \u9c7c\u5a18\uff0c\u627e\u5f97\u5feb\u8981\u53d1\u75af\u4e86\uff0c\u90a3\u665a\u5ffd\u7136\u9526\u513f\u5728\u8fdc\u5904\u6c11\u623f\u98de\u8eab\u63a0\u8fc7\uff0c\u6211\u8ffd\u51fa\u53bb\u4e00\u6bb5\u8def\uff0c\u770b\u6e05\u695a\u4e0d\u662f \u9c7c\u5a18\uff0c\u672c\u5f85\u9000\u4e0b\u3002\u4f46\u4e3a\u4e86\u597d\u5947\uff0c\u60f3\u77e5\u9053\u5979\u662f\u4ec0\u4e48\u8def\u9053\uff0c\u6240\u4ee5\u53c8\u8ddf\u8e2a\u4e86\u4e00\u4f1a\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u8d77\u521d\u89c1\u6613\u5170\u73e0\u58f0\u8272\u4ff1\u5389\uff0c\u60ca\u7591\u4e0d\u5b9a\uff0c\u8fd9\u65f6\u89c1\u767d\u6cf0\u5b98\u8bf4\u5f97\u6709\u7406\uff0c\u5fc3\u60f3\uff1a\u539f\u6765\u8fd9\u5c0f\u59d1 \u5a18\u662f\u4ed6\u7684\u4f84\u5973\uff0c\u4ed6\u91c7\u82b1\u8c05\u4e5f\u4e0d\u4f1a\u6765\u5230\u4f84\u5973\u8eab\u4e0a\u3002\u5ffd\u542c\u5f97\u6613\u5170\u73e0\u53c8\u5389\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u8bdd\u5f53\u771f\uff1f\u6211 \u95ee\u4f60\uff0c\u4f60\u4e3a\u4f55\u4e00\u8fde\u51e0\u665a\u5728\u94a6\u5dee\u884c\u7f72\u9644\u8fd1\u51fa\u6ca1\uff1f\u90a3\u4e9b\u91c7\u82b1\u5927\u76d7\u662f\u4e0d\u662f\u5728\u884c\u7f72\u9644\u8fd1\u843d\u811a\uff1f\u4ed6\u4eec\u96be \u9053\u4e0d\u662f\u4f60\u7684\u540c\u515a\u4e48\uff1f\u201d \u3000\u3000\u767d\u6cf0\u5b98\u5b9a\u4e86\u5b9a\u795e\uff0c\u8fd9\u624d\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u8001\u524d\u8f88\u7591\u5fc3\uff0c\u6211\u6765\u5230\u9752\u5c9b\uff0c\u6070\u6070\u78b0\u4e0a\u8fd9\u91cc\u7684 \u91c7\u82b1\u602a\u6848\uff0c\u6709\u51e0\u4e2a\u8001\u6355\u5934\u4e5f\u7591\u5fc3\u662f\u6211\u3002\u5176\u4e2d\u6709\u4e00\u4e2a\u540d\u53eb\u5f20\u9e23\u7684\u6b66\u529f\u4e0d\u5dee\uff0c\u4eba\u4e5f\u6b63\u76f4\uff0c\u4ed6\u8d70\u4e86 \u8bb8\u591a\u95e8\u8def\uff0c\u7ec8\u4e8e\u627e\u5230\u4e86\u6211\u3002\u6211\u8bf4\u6848\u5b50\u4e0d\u662f\u6211\u5e72\u7684\u3002\u4ed6\u8bf4\u4ed6\u4e5f\u77e5\u9053\u6848\u5b50\u7edd\u5bf9\u4e0d\u662f\u6211\u5e72\u7684\uff0c\u4f46\u65e2 \u7136\u6709\u4eba\u6000\u7591\uff0c\u90a3\u4f60\u5c31\u975e\u51fa\u624b\u7ba1\u7ba1\u4e0d\u53ef\uff01\u6211\u60f3\u8fd9\u4e5f\u6709\u9053\u7406\uff0c\u6240\u4ee5\u867d\u7136\u6025\u4e8e\u5bfb\u627e\u9c7c\u5a18\uff0c\u665a\u4e0a\u4e5f\u817e \u51fa\u5de5\u592b\u6765\u7ed9\u4ed6\u4fa6\u67e5\u3002\u4e8b\u60c5\u5947\u602a\u6781\u4e86\uff0c\u91c7\u82b1\u7684\u4f3c\u4e4e\u4e0d\u6b62\u4e00\u4eba\uff0c\u6bcf\u4e2a\u4eba\u6b66\u529f\u90fd\u6781\u9ad8\u5f3a\uff0c\u6211\u6709\u597d\u51e0 \u6b21\u53d1\u73b0\u8e2a\u8ff9\uff0c\u90fd\u8ffd\u4e4b\u4e0d\u53ca\u3002\u8fd8\u6709\u4e00\u6837\u5947\u602a\u7684\u662f\uff0c\u8fd9\u4e9b\u795e\u51fa\u9b3c\u6ca1\u7684\u5bb6\u4f19\u4e00\u5230\u94a6\u5dee\u884c\u7f72\u9644\u8fd1\u5c31\u6ca1 \u4e86\u8e2a\u8ff9\uff01\u201d\u6613\u5170\u73e0\u6c89\u601d\u534a\u523b\uff0c\u5f39\u6307\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u8d77\u5148\u5e76\u4e0d\u6000\u7591\u4f60\uff0c\u540e\u6765\u542c \u5f97\u6355\u5934\u79c1\u8bae\uff0c\u6307\u660e\u662f\u4f60\uff0c\u6211\u624d\u6697\u5730\u8ddf\u8e2a\u4f60\uff0c\u53ef\u7b11\u4f60\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002\u201d\u767d\u6cf0\u5b98\u9762\u4e0a\u4e00\u9635\u9635\u53d1 \u70ed\uff0c\u4e0d\u4f46\u662f\u56e0\u4e3a\u81ea\u5df1\u88ab\u8ddf\u8e2a\u800c\u6beb\u4e0d\u77e5\u60c5\uff0c\u800c\u4e14\u662f\u56e0\u4e3a\u4e0d\u660e\u4e0d\u767d\u7ed9\u4eba\u6000\u7591\u3002\u539f\u6765\u767d\u6cf0\u5b98\u662f\u4e2a\u5c11 \u5e74\u516c\u5b50\uff0c\u5728\u6c5f\u5357\u516b\u4fa0\u4e2d\u4ee5\u98ce\u6d41\u501c\u50a5\u95fb\u540d\uff0c\u5728\u672a\u8bc6\u9c7c\u5a18\u4ee5\u524d\uff0c\u4e5f\u66fe\u548c\u540d\u5993\u5f80\u6765\uff0c\u541f\u98ce\u5f04\u6708\uff0c\u4f46 \u5176\u5b9e\u53ea\u662f\u540d\u58eb\u98ce\u6d41\uff0c\u7edd\u65e0\u7559\u5bbf\u4e4b\u4e8b\u3002\u4ed6\u53ef\u7b97\u662f\u597d\u8272\u800c\u4e0d\u6deb\u7684\u541b\u5b50\uff0c\u4f46\u4e16\u4fd7\u4e4b\u4eba\uff0c\u5374\u54ea\u91cc\u4f1a\u4e86 \u89e3\u4ed6\u3002 \u3000\u3000\u6613\u5170\u73e0\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u6211\u5012\u662f\u4e00\u5f00\u59cb\u5c31\u7acb\u5fc3\u60f3\u7834\u6848\u7684\uff0c\u4e3a\u4e86\u8ddf\u8e2a\u4f60\uff0c\u5012\u628a\u6b63\u70b9\u513f\u653e\u8fc7 \u4e86\u3002\u540e\u6765\u6211\u4e5f\u770b\u51fa\u4e0d\u662f\u4f60\u4e86\uff0c\u4f46\u5374\u731c\u4e0d\u900f\u4f55\u4ee5\u4f60\u8fde\u665a\u4fa6\u5bdf\u4ed6\u4eec\uff0c\u4ed6\u4eec\u90fd\u4e0d\u5411\u4f60\u52a8\u624b\uff1f\u4ed6\u4eec\u6bcf \u4e2a\u4eba\u7684\u6b66\u529f\u6bd4\u4f60\u90fd\u8981\u9ad8\u5f97\u591a\uff01\u201d\u767d\u6cf0\u5b98\u9762\u4e0a\u53c8\u662f\u4e00\u9635\u53d1\u70e7\uff0c\u5fc3\u60f3\uff1a\u602a\u4e0d\u5f97\u5979\u6000\u7591\u6211\u548c\u91c7\u82b1\u8d3c \u4e43\u662f\u540c\u515a\u3002\u6211\u4ee5\u4e3a\u90a3\u4e9b\u4eba\u662f\u60e7\u6015\u6211\u7684\u5a01\u540d\uff0c\u6240\u4ee5\u624d\u95fb\u98ce\u95ea\u907f\uff0c\u5374\u4e0d\u6599\u4ed6\u4eec\u7684\u6b66\u529f\u90fd\u6bd4\u6211\u9ad8\uff0c \u90a3\u4e48\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u52a8\u624b\u5462\uff1f\u767d\u6cf0\u5b98\u60f3\u6765\u60f3\u53bb\uff0c\u8fde\u81ea\u5df1\u4e5f\u60f3\u4e0d\u900f\u3002 \u3000\u3000\u6613\u5170\u73e0\u53c8\u9053\uff1a\u201c\u6211\u5e73\u751f\u6240\u89c1\u7684\u602a\u4e8b\u751a\u591a\uff0c\u53ef\u5c31\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u7684\u602a\u6848\u5b50\uff0c\u6309\u7406\u8bf4\uff0c\u6c5f\u6e56\u4e0a \u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u6781\u5c11\u80af\u505a\u4e0b\u4e09\u6ee5\u7684\u91c7\u82b1\u8d3c\uff0c\u5c31\u662f\u4f60\u7684\u5e08\u5144\u4e86\u56e0\uff0c\u4ed6\u4e5f\u4e0d\u6562\u516c\u7136\u91c7\u82b1\u3002\u800c\u8fd9\u6b21\u7684 \u91c7\u82b1\u602a\u6848\uff0c\u7adf\u4f3c\u6709\u8bb8\u591a\u6b66\u6797\u4e2d\u9876\u513f\u5c16\u513f\u7684\u4eba\u7269\uff0c\u6765\u5e72\u8fd9\u4e0b\u8d31\u7684\u52fe\u5f53\uff01\u201d\u767d\u6cf0\u5b98\u6c89\u601d\u6709\u9877\uff0c\u5ffd \u7136\u95ee\u9053\uff1a\u201c\u8fd9\u94a6\u5dee\u662f\u4f55\u7b49\u4eba\u7269\uff1f\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u6211\u5df1\u67e5\u8bbf\u8fc7\u4e86\uff0c\u6b64\u4eba\u540d\u53eb\u5f20\u5ef7\u7389\uff0c\u662f\u4e49\u6e0a\u9601 \u5927\u5b66\u58eb\uff0c\u517c\u7ba1\u6237\u90fd\u7ff0\u6797\uff0c\u597d\u8bb2\u5b8b\u660e\u7406\u5b66\uff0c\u867d\u7136\u662f\u4e2a\u5974\u624d\uff0c\u5e73\u65e5\u5b98\u58f0\u5374\u8fd8\u4e0d\u9519\uff0c\u96be\u9053\u4ed6\u4f1a\u5305\u5e87 \u91c7\u82b1\u5de8\u8d3c\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u5f20\u5ef7\u7389\u4e43\u662f\u6e05\u671d\u91cd\u81e3\uff0c\u600e\u7684\u5374\u5ffd\u7136\u6765\u5230\u8fd9\u6d77\u9685\u4e4b \u5730\uff1f\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u90a3\u6211\u53ef\u4e0d\u77e5\u9053\u4e86\u3002\u201d\u6c89\u601d\u826f\u4e45\uff0c\u5ffd\u7136\u62cd\u638c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u53ef\u8d8a\u6765\u8d8a\u5947\u602a \u4e86\uff0c\u5f20\u5ef7\u7389\u6765\u4e86\u4e0d\u5230\u4e09\u5929\uff0c\u91c7\u82b1\u6848\u5c31\u9891\u9891\u53d1\u751f\uff0c\u96be\u9053\u771f\u548c\u4ed6\u6709\u70b9\u7275\u8fde\uff1f\u4e0d\u4f1a\u5440\u4e0d\u4f1a\uff01\u5f20\u5ef7\u7389 \u5f53\u671d\u4e00\u54c1\uff0c\u5c31\u7b97\u662f\u4e2a\u5047\u9053\u5b66\uff0c\u4ed6\u4e5f\u7528\u4e0d\u7740\u91c7\u82b1\uff0c\u518d\u8bf4\u90a3\u4e9b\u6b66\u6797\u4e2d\u9876\u513f\u5c16\u513f\u7684\u4eba\u7269\uff0c\u53c8\u600e\u4f1a\u4e3a \u4ed6\u6240\u7528\uff1f\u201d \u3000\u3000\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u6613\u8001\u524d\u8f88\uff0c\u65e2\u7136\u90a3\u4e9b\u91c7\u82b1\u5927\u8d3c\u90fd\u5728\u94a6\u5dee\u884c\u7f72\u9644\u8fd1\u51fa\u6ca1\uff0c\u6211\u4eec\u4f55\u4e0d\u63a2\u4ed6\u4e00 \u63a2\uff1f\u5c0f\u4f84\u8eab\u53d7\u5acc\u7591\uff0c\u8fd9\u56de\u4e8b\u975e\u5f04\u5b83\u6c34\u843d\u77f3\u51fa\uff0c\u5fc3\u5b9e\u4e0d\u7518\uff01\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u4e5f\u53ea\u6709\u5982\u6b64\u4e86\u3002\u201d \u5f53\u4e0b\u4e94\u4eba\u540c\u5230\u897f\u90ca\u7684\u7384\u5999\u89c2\u6b47\u606f\uff0c\u7384\u5999\u89c2\u7684\u4e3b\u6301\u4e43\u662f\u66f9\u4ec1\u7236\u7684\u59d1\u59d1\uff0c\u6613\u5170\u73e0\u548c\u66f9\u9526\u513f\u524d\u51e0\u5929 \u5c31\u662f\u5728\u89c2\u4e2d\u5bc4\u4f4f\u7684\u3002 \u3000\u3000\u5728\u72ec\u81c2\u795e\u5c3c\u7684\u516b\u4e2a\u5f92\u5f1f\u4e2d\uff0c\u66f9\u4ec1\u7236\u540d\u5217\u7b2c\u56db\uff0c\u5e74\u7eaa\u5374\u662f\u6700\u957f\uff0c\u4e8c\u5341\u5e74\u524d\u4ed6\u548c\u4e8c\u5e08\u5144\u5468\u6d9b \u66fe\u8fdc\u6e38\u56de\u7586\uff0c\u89c1\u8fc7\u6613\u5170\u73e0\u4e00\u9762\uff0c\u8fd9\u756a\u6613\u5170\u73e0\u5230\u4e2d\u539f\u89c5\u5251\uff0c\u4e5f\u66fe\u627e\u8fc7\u4ed6\u3002\u66f9\u4ec1\u7236\u6781\u60f3\u6613\u5170\u73e0\u6536 \u4ed6\u5973\u513f\u4e3a\u5f92\uff0c\u4f46\u6613\u5170\u73e0\u5374\u4e0d\u80af\u7b54\u5e94\u3002\u53ea\u7b54\u5e94\u6559\u5979\u4e00\u8def\u5251\u6cd5\uff0c\u548c\u5e26\u5979\u5230\u6c5f\u6e56\u5386\u7ec3\u3002\u8fd9\u6b21\u6613\u5170\u73e0 \u6545\u610f\u53eb\u5979\u5728\u57ce\u4e2d\u56db\u5904\u8d70\u52a8\uff0c\u76ee\u7684\u5c31\u662f\u60f3\u5f15\u90a3\u4e9b\u91c7\u82b1\u5927\u8d3c\uff0c\u8c01\u6599\u5f15\u4e0d\u5230\u91c7\u82b1\u5927\u8d3c\uff0c\u5374\u5f15\u6765\u4e86\u767d \u6cf0\u5b98\u3002 \u3000\u3000\u6b47\u606f\u4e00\u65e5\u4e4b\u540e\uff0c\u665a\u4e0a\u6613\u5170\u73e0\u548c\u5510\u6653\u6f9c\u4e00\u8def\uff1b\u767d\u6cf0\u5b98\u548c\u5415\u56db\u5a18\u4e00\u8def\uff0c\u8ff3\u81ea\u5230\u94a6\u5dee\u884c\u7f72\u9644\u8fd1 \u57cb\u4f0f\uff0c\u4ece\u4e09\u66f4\u7b49\u5230\u4e94\u66f4\uff0c\u4e00\u4e2a\u591c\u884c\u4eba\u4e5f\u7b49\u4e0d\u7740\u3002\u53ea\u6709\u5e9f\u7136\u800c\u8fd4\u3002\u62c2\u6653\u56de\u5230\u89c2\u4e2d\uff0c\u90a3\u77e5\u53c8\u53d1\u751f \u4e86\u4e00\u4ef6\u5947\u602a\u4e4b\u4e8b\uff0c\u6613\u5170\u73e0\u548c\u767d\u6cf0\u5b98\u7684\u884c\u674e\u90fd\u7ed9\u4eba\u641c\u4e86\uff01\u7559\u5b88\u7384\u5999\u89c2\u7684\u66f9\u9526\u513f\uff0c\u7adf\u534a\u70b9\u4e5f\u4e0d\u77e5 \u9053\u3002 \u3000\u3000\u6613\u5170\u73e0\u7684\u884c\u674e\u7ed9\u4eba\u641c\u4e86\u8fd8\u4e0d\u6253\u7d27\uff0c\u684c\u4e0a\u8fd8\u7559\u4e0b\u4e00\u5f20\u8c22\u7f6a\u8d34\u5b50\uff0c\u4e0a\u9762\u5199\u9053\uff1a\u201c\u5973\u4fa0\u5357\u6765\uff0c \u8d2b\u50e7\u897f\u4e0b\uff0c\u540c\u9022\u602a\u6848\uff0c\u6709\u610f\u4fa6\u67e5\uff0c\u773c\u62d9\u68cb\u5dee\uff0c\u5192\u72af\u8be5\u6253\uff0c\u5973\u4fa0\u91cf\u5927\uff0c\u843d\u4e2a\u54c8\u54c8\uff0c\u8bda\u5fc3\u8bf7\u7f6a\uff0c \u4e5e\u514d\u8d23\u9a82\u3002\u201d\u6613\u5170\u73e0\u76b1\u8d77\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u770b\u6765\uff0c\u6211\u7ed9\u4eba\u8ddf\u8e2a\u4fa6\u67e5\uff0c\u4e5f\u4e0d\u77e5\u9053\u3002\u771f\u662f\u87b3\u8782 \u6355\u8749\uff0c\u4e0d\u77e5\u9ec4\u96c0\u5728\u540e\uff01\u201d\u8fd9\u79cd\u8c22\u7f6a\u8d34\u662f\u6b66\u6797\u4e2d\u5e73\u8f88\u4e4b\u4eba\uff0c\u505a\u9519\u4e86\u4e8b\u4e4b\u540e\uff0c\u6700\u8c26\u4e0b\u7684\u966a\u793c\u3002\u6613 \u5170\u73e0\u8f88\u4efd\u6781\u9ad8\uff0c\u7adf\u60f3\u4e0d\u8d77\u5f53\u4eca\u4e4b\u4e16\u8fd8\u6709\u8c01\u4e0e\u5979\u540c\u8f88\uff1f\u5510\u6653\u6f9c\u4e0d\u61c2\u89c4\u77e9\uff0c\u8d38\u7136\u95ee\u9053\uff1a\u201c\u7559\u8d34\u7684 \u4eba\u60f3\u662f\u4e86\u56e0\u90a3\u79c3\u9a74\u4e86\uff1f\u6211\u4eec\u53bb\u641c\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u6765\u641c\u6211\u4eec\uff0c\u771f\u662f\u80c6\u5927\uff01\u201d\u6613\u5170\u73e0\u5fae\u55d4\u8bf4\u9053\uff1a \u201c\u4e86\u56e0\u6709\u8fd9\u6837\u7684\u80c6\uff0c\u4e5f\u6ca1\u6709\u8fd9\u6837\u7684\u6b66\u529f\uff0c\u4ed6\u8ddf\u8e2a\u6211\uff0c\u6211\u54ea\u80fd\u4e0d\u77e5\uff1f\u201d\u5415\u56db\u5a18\u767d\u6cf0\u5b98\u4e0d\u6562\u8bf4 \u8bdd\u3002\u6613\u5170\u73e0\u9053\uff1a\u201c\u8c22\u7f6a\u8d34\u4e0a\u8bf4\u5f97\u660e\u767d\uff0c\u770b\u6765\u6b64\u4eba\u662f\u4e2a\u6709\u9053\u9ad8\u618e\uff0c\u4e0e\u6211\u4eec\u62b1\u7740\u540c\u6837\u5fc3\u601d\uff0c\u60f3\u7834 \u91c7\u82b1\u602a\u6848\u7684\u4e86\u3002\u53ea\u662f\u4e16\u4e8b\u96be\u6d4b\uff0c\u65e2\u6709\u5982\u6b64\u80fd\u4eba\u51fa\u73b0\uff0c\u6211\u4eec\u53ef\u5f97\u5206\u5916\u5c0f\u5fc3\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u95f7\u95f7\u4e0d\u4e50\uff0c\u5415\u56db\u5a18\u8ba8\u5979\u6b22\u5fc3\uff0c\u8bf7\u5979\u6307\u70b9\u5251\u6cd5\uff0c\u6613\u5170\u73e0\u9053\uff1a\u201c\u4f60\u5e08\u5085\u6240\u5f97\u7684\u7384\u5973\u5251 \u8bc0\uff0c\u4e43\u53e4\u4ee3\u771f\u4f20\uff0c\u4e0d\u5728\u5929\u5c71\u5251\u6cd5\u4e4b\u4e0b\u3002\u5f53\u4eca\u4e4b\u4e16\uff0c\u53ea\u6709\u4e09\u5bb6\u5251\u6cd5\u53ef\u4ee5\u5e76\u9a7e\u9f50\u9a71\uff0c\u4f60\u6211\u4e24\u5bb6\u4e4b \u5916\uff0c\u4fbf\u662f\u6842\u4ef2\u660e\u4f20\u4e0b\u6765\u7684\u8fbe\u6469\u5251\u6cd5\u3002\u4e09\u5bb6\u5251\u6cd5\u5f02\u66f2\u540c\u5de5\uff0c\u4f60\u5df2\u5f97\u5e08\u5085\u771f\u4f20\uff0c\u4f55\u987b\u5411\u6211\u8ba8 \u6559\uff1f\u201d\u5415\u56db\u5a18\u60f6\u7136\u8bf4\u9053\uff1a\u201c\u6211\u7684\u5e08\u5085\u4e5f\u66fe\u627f\u4f60\u6307\u70b9\u8fc7\u5462\uff01\u201d\u6613\u5170\u73e0\u7b11\u9053\uff1a\u201c\u90a3\u4e2a\u4e0d\u540c\uff0c\u90a3\u65f6 \u4f60\u5e08\u5085\u521d\u5f97\u5251\u8c31\uff0c\u5251\u6cd5\u5c1a\u672a\u5165\u95e8\uff0c\u6240\u4ee5\u8981\u4eba\u6307\u70b9\u8bc0\u7a8d\uff0c\u4f60\u73b0\u5728\u5df2\u4e0d\u4f46\u5347\u5802\uff0c\u800c\u4e14\u5feb\u5c06\u5165\u5ba4\uff0c \u5251\u6cd5\u4e0a\u662f\u4e0d\u5fc5\u6211\u6307\u70b9\u7684\u4e86\uff0c\u6211\u5c06\u6765\u6559\u4f60\u4e00\u70b9\u7ec3\u529f\u7684\u79d8\u8bc0\u5427\uff01\u201d\u5415\u56db\u5a18\u5927\u559c\u62dc\u8c22\u3002\u6613\u5170\u73e0\u5ffd \u9053\uff1a\u201c\u4f60\u5e08\u5085\u5185\u529f\u6781\u9ad8\uff0c\u82e5\u662f\u5979\u8ddf\u8e2a\u6211\uff0c\u6211\u6216\u8bb8\u4e0d\u77e5\uff0c\u5176\u4ed6\u7684\u4eba\u8fd8\u6709\u8c01\u6709\u8fd9\u6837\u80fd\u8010\uff1f\u8fd9\u51e0\u5341 \u5e74\u50fb\u5904\u585e\u5916\uff0c\u4e0d\u77e5\u4e2d\u539f\u5404\u6d3e\u5b97\u7956\uff0c\u8fd8\u5269\u51e0\u4eba\uff1f\u201d\u767d\u6cf0\u5b98\u8bf4\u4e86\u51e0\u6d3e\u638c\u95e8\u4eba\u7684\u540d\u5b57\uff0c\u6613\u5170\u73e0\u6447\u6447 \u5934\u9053\uff1a\u201c\u90fd\u662f\u6211\u7684\u665a\u8f88\uff01\u201d\u7ee7\u800c\u95ee\u9053\uff1a\u201c\u5ce8\u5d4b\u6d3e\u7684\u91d1\u5149\u5927\u5e08\u548c\u5c11\u6797\u6d3e\u7684\u672c\u7a7a\u5927\u5e08\u8fd8\u5728\u4e16 \u5417\uff1f\u201d\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u90fd\u5df2\u6b7b\u4e86\uff01\u201d\u6613\u5170\u73e0\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u5185\u5fc3\u8d8a\u53d1\u60ca\u5f02\u3002 \u3000\u3000\u5f53\u665a\u56db\u4eba\u4ecd\u5206\u4e24\u8def\uff0c\u5728\u94a6\u5dee\u884c\u7f72\u9644\u8fd1\u57cb\u4f0f\uff0c\u4e09\u66f4\u6572\u8fc7\uff0c\u84e6\u5730\u4e00\u6761\u4eba\u5f71\u75be\u5982\u98de\u9e1f\u7684\u8dc3\u8fdb\u7f72 \u8859\uff0c\u9ed1\u8863\u7384\u88f3\u9762\u76ee\u770b\u4e0d\u6e05\u695a\uff0c\u80c1\u4e0b\u631f\u7740\u4e00\u4e2a\u5c11\u5e74\u5973\u5b50\uff0c\u60f3\u662f\u5df2\u7ed9\u4ed6\u70b9\u4e86\u54d1\u7a74\uff0c\u6240\u4ee5\u6beb\u4e0d\u58f0 \u5f20\uff0c\u518d\u8fc7\u4e9b\u65f6\uff0c\u53c8\u662f\u4e00\u6761\u9ed1\u5f71\u75be\u8dc3\u8fdb\u8859\uff0c\u80c1\u4e0b\u4e5f\u631f\u7740\u4e00\u4e2a\u5c11\u5e74\u5973\u5b50\uff0c\u5510\u6653\u6f9c\u4e00\u77a5\u4e4b\u4e0b\uff0c\u8ba4\u5f97 \u662f\u54c8\u5e03\u9640\uff0c\u6697\u9053\uff1a\u201c\u539f\u6765\u4ed6\u662f\u91c7\u82b1\u5927\u8d3c\uff01\u201d\u6b63\u60f3\u51fa\u58f0\uff0c\u6613\u5170\u73e0\u5c06\u4ed6\u4e00\u5e26\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u7d27 \u968f\u7740\u6211\uff01\u628a\u98de\u8292\u6263\u597d\uff0c\u82e5\u6709\u5371\u9669\uff0c\u5148\u53d1\u6697\u5668\uff01\u201d\u4e0e\u5415\u56db\u5a18\u767d\u6cf0\u5b98\u6253\u4e2a\u62db\u547c\uff0c\u56db\u4eba\u540c\u65f6\u8dc3\u8fdb\u8859 \u5185\u3002\u5510\u6653\u6f9c\u8f7b\u529f\u867d\u4e0e\u6613\u5415\u4e8c\u4eba\u76f8\u53bb\u8fdc\u751a\uff0c\u4f46\u9020\u8be3\u4ea6\u5df2\u4e0d\u51e1\uff0c\u56db\u4eba\u98d8\u8eab\u8fdb\u7f72\uff0c\u843d\u5730\u65e0\u58f0\uff0c\u9065\u89c1 \u54c8\u5e03\u9640\u7684\u5f71\u5b50\u5728\u697c\u53f0\u4ead\u6805\u4e4b\u95f4\u9690\u6ca1\u3002 \u3000\u3000\u884c\u7f72\u5185\u6c60\u5858\u5047\u5c71\uff0c\u7e41\u82b1\u5bc6\u53f6\uff0c\u5e03\u7f6e\u5f97\u9976\u6709\u56ed\u6797\u4e4b\u80dc\uff0c\u5929\u4e0a\u4e00\u94a9\u5bd2\u6708\uff0c\u7b3c\u7f69\u7740\u98de\u697c\u7fe0\u9601\u3002 \u6613\u5170\u73e0\u4e00\u770b\u56db\u9762\u65e0\u4eba\uff0c\u98de\u8eab\u7eb5\u4e0a\u5047\u5c71\u9876\u4e0a\uff0c\u53ea\u89c1\u4e00\u5ea7\u5bab\u6bbf\u5f0f\u5927\u53a6\uff0c\u517d\u73af\u9ad8\u8038\uff0c\u4fbf\u5728\u8d70\u5eca\u53f3 \u9996\uff0c\u53cc\u8db3\u4e00\u70b9\uff0c\u98de\u5230\u5eca\u9876\uff0c\u4e00\u57ab\u8db3\uff0c\u53c8\u4ece\u5eca\u9876\u4f7f\u4e86\u4e00\u624b\u201c\u71d5\u5b50\u94bb\u4e91\u201d\u5411\u90a3\u5ea7\u5927\u53a6\u5c4b\u9876\u98de\u53bb\uff0c \u7acb\u5b9a\u8eab\u8eaf\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u5415\u56db\u5a18\uff0c\u767d\u6cf0\u5b98\uff0c\u5510\u6653\u6f9c\u76f8\u7ee7\u8dc3\u4e0a\uff0c\u56db\u4e0b\u4e00\u671b\uff0c\u597d\u5927\u7684\u4e00\u6240\u6d77\u5c4b\uff0c\u697c \u53f0\u4ead\u69ad\uff0c\u4e0d\u8ba1\u5176\u6570\uff0c\u6613\u5170\u73e0\u6b63\u4e0d\u77e5\u4ece\u4f55\u5904\u5165\u624b\u3002\u53c2\u5dee\u9519\u843d\u7684\u623f\u5c4b\u4e4b\u95f4\uff0c\u6709\u4e00\u5e26\u4e07\u5b57\u8d70\u5eca\uff0c\u66f2 \u66f2\u6298\u6298\uff0c\u6302\u7740\u51e0\u5341\u76cf\u5782\u82cf\u516b\u89d2\u98ce\u706f\uff0c\u5ffd\u542c\u94c3\u58f0\u54cd\u5904\uff0c\u4e2d\u95f4\u4e00\u5ea7\u5927\u5c4b\u5440\u7684\u4e00\u58f0\u95e8\u54cd\uff0c\u62e5\u51fa\u5341\u591a \u4e2a\u9ad8\u5927\u6c49\u5b50\uff0c\u8eac\u8170\u8e51\u8db3\uff0c\u597d\u50cf\u6015\u60ca\u52a8\u4ec0\u4e48\u4eba\u4f3c\u7684\uff0c\u5f62\u72b6\u5341\u5206\u6ed1\u7a3d\uff0c\u6700\u540e\u8d70\u51fa\u4e00\u4e2a\u5b98\u5458\uff0c\u7ea2\u5462 \u515c\u98ce\u9ae6\uff0c\u6c14\u6982\u4e0d\u51e1\uff0c\u8fd9\u884c\u4eba\u8fd8\u672a\u8d70\u51fa\u846b\u9053\uff0c\u8d70\u5eca\u90a3\u5934\u524d\u547c\u540e\u62e5\uff0c\u53c8\u6765\u4e86\u4e00\u7c07\u4eba\uff0c\u4e5f\u662f\u4e00\u8272\u6253 \u626e\uff0c\u4e24\u7c07\u4eba\u5feb\u8981\u76f8\u9047\uff0c\u4ece\u5916\u9762\u6765\u7684\u8fd9\u7c07\u4eba\u987f\u65f6\u8083\u9759\u65e0\u54d7\uff0c\u4e00\u4e2a\u6234\u7740\u7fe1\u7fe0\u9876\u63d2\u6709\u53cc\u773c\u82b1\u7fce\u7684\u5927 \u5b98\u8eac\u8170\u95ee\u9053\uff1a\u201c\u5f20\u5927\u4eba\uff0c\u5351\u804c\u8bf7\u5b89\uff01\u201d\u90a3\u4e2a\u62ab\u7740\u7ea2\u5462\u515c\u98ce\u9ae6\u7684\u5b98\u5458\u8bf4\u9053\uff1a\u201c\u7530\u5927\u4eba\uff0c\u4f60\u4e0d\u5fc5 \u8fdb\u53bb\u4e86\uff01\u201d\u8fc8\u524d\u4e00\u6b65\uff0c\u4f4e\u58f0\u8bf4\u4e86\u51e0\u53e5\uff0c\u90a3\u4e2a\u201c\u7530\u5927\u4eba\u201d\u9762\u9732\u8be1\u5f02\u7b11\u5bb9\uff0c\u8eac\u8170\u4fbf\u9000\u3002\u767d\u6cf0\u5b98\u66fe \u5728\u5c71\u4e1c\u884c\u4fa0\u4ed7\u4e49\uff0c\u5927\u7565\u77e5\u9053\u5b98\u573a\u60c5\u5f62\uff0c\u542c\u4ed6\u4eec\u79f0\u547c\uff0c\u6599\u77e5\u90a3\u62ab\u7ea2\u5462\u515c\u98ce\u9ae6\u7684\u5fc5\u662f\u94a6\u5dee\u5f20\u5ef7 \u7389\uff0c\u800c\u90a3\u4e2a\u63d2\u53cc\u773c\u82b1\u7fce\uff0c\u5b98\u670d\u7a7f\u5f97\u9f50\u9f50\u6574\u6574\u7684\u5219\u662f\u5c71\u4e1c\u5de1\u629a\u7530\u6587\u955c\u3002\u8fd9\u7530\u6587\u955c\u4e5f\u662f\u6e05\u4ee3\u540d \u58eb\uff0c\u4ed6\u5728\u5f53\u65f6\u5404\u7701\u629a\u53f0\u4e4b\u4e2d\uff0c\u751a\u6709\u5a01\u671b\u3002\u767d\u6cf0\u5b98\u5fc3\u60f3\uff1a\u5f20\u5ef7\u7389\u867d\u662f\u94a6\u5dee\uff0c\u4f46\u4e24\u4eba\u5b98\u9636\u76f8\u5dee\u4e0d \u5230\u4e00\u7ea7\uff0c\uff08\u5f20\u662f\u6b63\u4e00\u54c1\uff0c\u7530\u662f\u4ece\u4e00\u54c1\uff09\u6309\u5b98\u573a\u89c4\u77e9\uff0c\u5f20\u5ef7\u7389\u82e5\u5230\u629a\u53f0\u6240\u5728\u5730\u7684\u6d4e\u5357\uff0c\u7530\u6587\u955c \u81ea\u5f53\u9686\u91cd\u8fce\u63a5\uff0c\u4f46\u73b0\u5728\u5f20\u5ef7\u7389\u51fa\u5de1\u9752\u5c9b\uff0c\u7530\u6587\u955c\u53ef\u4e0d\u5fc5\u4eb2\u6765\u62dc\u8c12\u5440\uff01\u4f55\u4ee5\u7530\u6587\u955c\u8fd9\u6837\u5bb3\u6015\u94a6 \u5dee\uff0c\u4e0e\u4ed6\u5e73\u65e5\u4e3a\u5b98\u4f5c\u98ce\u751a\u4e0d\u76f8\u4f3c\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u4e24\u7c07\u4eba\u90fd\u5df2\u53bb\u8fdc\uff0c\u6613\u5170\u73e0\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u53bb\u641c\u4e2d\u95f4\u90a3\u95f4\u5927\u5c4b\uff01\u6653\u6f9c\uff0c\u4f60\u968f \u7740\u6211\u3002\u56db\u5a18\uff0c\u4f60\u8981\u7b49\u6211\u5148\u53d1\u624d\u53ef\u51fa\u624b\uff01\u201d\u53cc\u8db3\u4e00\u70b9\uff0c\u5e26\u5510\u6653\u6f9c\u4ece\u4e03\u516b\u4e08\u9ad8\u7684\u753b\u6a90\u4e0a\uff0c\u98d8\u843d\u5927 \u53a6\u74e6\u80cc\uff0c\u62ac\u5934\u4ed4\u7ec6\u4e00\u770b\uff0c\u53ea\u89c1\u516b\u6247\u5c4f\u98ce\u4e0a\u9762\uff0c\u8fd8\u6709\u4e00\u6392\u96d5\u82b1\u6392\u7a97\uff0c\u987f\u65f6\u8ba1\u4e0a\u5fc3\u6765\uff0c\u4e00\u4e2a\u201c\u65f1 \u5730\u62d4\u8471\u201d\uff0c\u76f4\u5411\u5eca\u6a90\u5927\u82b1\u677f\u9876\u7eb5\u53bb\uff0c\u5de6\u81c2\u4e00\u4e3e\uff0c\u4e24\u6307\u4e00\u94b3\uff0c\u4fbf\u628a\u6574\u4e2a\u8eab\u5b50\u540a\u5728\u4e0a\u9762\uff0c\u7528\u7cbe\u6df1 \u7684\u5185\u529f\u8f7b\u8f7b\u9707\u5f00\u4e00\u4e9b\u88c2\u75d5\uff0c\u4e00\u70b9\u58f0\u606f\u90fd\u6ca1\u6709\uff0c\u5415\u56db\u5a18\u767d\u6cf0\u5b98\u5510\u6653\u6f9c\u4e5f\u7167\u7740\u5979\u505a\uff0c\u5f20\u773c\u5077\u7aa5\uff0c \u5927\u5c4b\u4ece\u6881\u4e0a\u540a\u4e0b\u4e00\u76cf\u6e9c\u91d1\u5d4c\u5b9d\u7f28\u7edc\u7f24\u7eb7\u7684\u957f\u660e\u706f\uff0c\u653e\u51fa\u4e00\u9053\u6de1\u6de1\u9ec4\u5149\uff0c\u7167\u51fa\u56db\u6839\u76d8\u9f99\u821e\u51e4\u7684 \u901a\u5929\u5927\u67f1\uff0c\u5927\u5c4b\u6b63\u4e2d\u5750\u7740\u4e00\u4e2a\u5c11\u5e74\u516c\u5b50\uff0c\u5510\u6653\u6f9c\u770b\u5f97\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u5c11\u5e74\u516c\u5b50\u6b63\u662f\u524d\u51e0\u5929\u5728\u6ee8 \u6d77\u697c\u4e0a\u6240\u9047\u7684\u738b\u516c\u5b50\uff01\u5929\u53f6\u6563\u4eba\u3001\u6d77\u4e91\u548c\u5c1a\u3001\u795e\u9b54\u53cc\u8001\u7ad9\u5728\u4e24\u8fb9\uff0c\u795e\u60c5\u7adf\u662f\u5bf9\u4ed6\u5341\u5206\u606d\u656c\uff01 \u5415\u56db\u5a18\u548c\u767d\u6cf0\u5b98\u4e5f\u662f\u9762\u9762\u76f8\u89d1\uff01\u4e0d\u77e5\u8fd9\u7a76\u7adf\u662f\u4ec0\u4e48\u8def\u9053\uff1f \u3000\u3000\u738b\u516c\u5b50\u4f38\u4e86\u4e00\u4e2a\u61d2\u8170\uff0c\u51fb\u638c\u8bf4\u9053\uff1a\u201c\u6b63\u4e8b\u505a\u5b8c\uff0c\u54b1\u4eec\u53ef\u8981\u5e72\u4e9b\u5f00\u5fc3\u7684\u4e8b\u4e86\u3002\u5f20\u5ef7\u7389\u90a3\u53ae \u597d\u4e0d\u77e5\u8da3\uff0c\u8fc7\u4e86\u4e09\u8707\u624d\u8d70\u3002\u201d\u5bf9\u4e00\u4e2a\u9ed1\u8863\u536b\u58eb\u9053\uff1a\u201c\u53eb\u54c8\u603b\u7ba1\u6765\uff01\u201d\u9ed1\u8863\u536b\u58eb\u201c\u55b3\u201d\u7684\u4e00 \u58f0\uff0c\u5782\u624b\u9000\u4e0b\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4e00\u9635\u5e7d\u9999\u6251\u5165\u9f3b\u51a0\uff0c\u4fa7\u95e8\u5f00\u5904\uff0c\u54c8\u5e03\u9640\u4e0e\u53e6\u4e00\u4e2a\u9ed1\u8863\u536b\u58eb\u6276\u7740\u4e24\u4e2a\u5c11\u5973\u8fdb \u6765\uff0c\u8fd9\u4e24\u4e2a\u5c11\u5973\u6b63\u662f\u4ed6\u4eec\u4eca\u665a\u52ab\u6765\u7684\uff0c\u8fd9\u65f6\u5df2\u6362\u4e86\u88c5\u675f\uff0c\u8f7b\u88d9\u957f\u8896\uff0c\u7fe0\u7fbd\u660e\u6321\uff0c\u59ff\u5bb9\u4e0d\u4fd7\uff0c \u53ea\u662f\u9762\u5bb9\u7070\u6697\uff0c\u4e24\u773c\u65e0\u795e\u3002\u738b\u516c\u5b50\u7b11\u563b\u563b\u7684\u7aef\u8be6\u4e86\u4e00\u9635\uff0c\u626d\u8f6c\u4e86\u5934\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5973\u5b50\u5fd2\u5c0f \u5bb6\u5b50\u6c14\uff0c\u867d\u6709\u51e0\u5206\u59ff\u8272\uff0c\u4e5f\u50cf\u6ce5\u5851\u6728\u96d5\u3002\u5148\u9001\u53bb\u8bad\u7ec3\uff0c\u8fc7\u4e00\u4e2a\u6708\u540e\uff0c\u518d\u5e26\u6765\u89c1\u6211\uff01\u201d\u9ed1\u8863\u536b \u58eb\u201c\u55b3\u201d\u7684\u4e00\u58f0\uff0c\u6b63\u5f85\u5e26\u4e24\u4e2a\u5c11\u5973\u9000\u4e0b\uff0c\u738b\u516c\u5b50\u53c8\u9053\uff1a\u201c\u54b1\u4eec\u6765\u5230\u5c71\u4e1c\uff0c\u5148\u540e\u89c5\u5f97\u591a\u5c11\u79c0 \u5973\uff1f\u201d\u9ed1\u8863\u536b\u571f\u9053\uff1a\u201c\u4e00\u5171\u5df2\u662f\u5341\u4e8c\u4e2a\u4e86\uff01\u201d\u738b\u516c\u5b50\u9053\uff1a\u201c\u90fd\u53eb\u5b98\u5a92\u9a8c\u8fc7\u4e86\u4e48\uff1f\u201d\u536b\u58eb\u9053\uff1a \u201c\u9664\u8fd9\u4e24\u4e2a\u4e4b\u5916\uff0c\u5176\u4ed6\u90fd\u9a8c\u8fc7\u4e86\uff0c\u6709\u516b\u4e2a\u7b26\u5408\u89c4\u683c\uff01\u201d\u738b\u516c\u5b50\u9053\uff1a\u201c\u4e0d\u5408\u89c4\u683c\u7684\uff0c\u9001\u5979\u4eec\u56de \u53bb\uff0c\u4e0d\u51c6\u96be\u4e3a\u5979\u4eec\u3002\u201d\u54c8\u5e03\u9640\u7b11\u9053\uff1a\u201c\u4f55\u4e0d\u8d50\u7ed9\u5b9d\u56fd\u7985\u5e08\uff1f\u201d\u738b\u516c\u5b50\u9053\uff1a\u201c\u82e5\u662f\u4e0d\u80fd\u8fdb\u5165\u5185 \u5ef7\u7684\u79c0\u5973\uff0c\u6050\u4e5f\u672a\u5fc5\u80fd\u5165\u5b9d\u56fd\u7985\u5e08\u6cd5\u773c\uff0c\u6211\u53e6\u9009\u4f73\u4e3d\u9001\u4ed6\u597d\u4e86\u3002\u201d\u6613\u5170\u73e0\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6697\u60f3\uff1a \u91c7\u82b1\u8d3c\u90a3\u6709\u5982\u6b64\u6c14\u6d3e\uff1f\u96be\u9053\u4ed6\u4eec\u662f\u671d\u5ef7\u6697\u4e2d\u6d3e\u6765\u6311\u9009\u79c0\u5973\u7684\uff1f\u8fd9\u738b\u516c\u5b50\u4e0d\u77e5\u53c8\u662f\u4f55\u7b49\u4eba\u7269\uff1f \u6b63\u601d\u91cf\u95f4\uff0c\u5ffd\u95fb\u5f97\u5929\u53f6\u6563\u4eba\u5927\u559d\u4e00\u58f0\uff1a\u201c\u597d\u5927\u80c6\u7684\u5978\u7ec6\uff0c\u8fd8\u4e0d\u7ed9\u6211\u6eda\u4e0b\uff01\u201d\u53cc\u638c\u9f50\u626c\uff0c\u5eca\u6a90 \u5d29\u6298\uff0c\u5c4b\u74e6\u7eb7\u98de\uff0c\u6613\u5170\u73e0\u7b49\u56db\u4eba\u5728\u6ce5\u7802\u74e6\u7247\u7684\u70df\u96fe\u4e2d\u98de\u8eab\u4e0b\u5730\u3002\u539f\u6765\u662f\u767d\u6cf0\u5b98\u6124\u6012\u96be\u6291\uff0c\u65e0 \u610f\u4e4b\u95f4\uff0c\u54ac\u4e86\u54ac\u7259\uff0c\u8eab\u8eaf\u7a0d\u6c89\uff0c\u4f20\u51fa\u4e00\u5fae\u7ec6\u7684\u58f0\u54cd\uff0c\u53ea\u8fd9\u4e00\u70b9\u513f\u97f3\u54cd\uff0c\u7acb\u523b\u5c31\u8d25\u4e86\u4e8b\uff01 \u3000\u3000\u767d\u6cf0\u5b98\u811a\u672a\u6cbe\u5c18\uff0c\u4e24\u67c4\u5315\u9996\u62cd\u62cd\u4e24\u58f0\uff0c\u76f4\u5411\u738b\u5c0a\u4e00\u98de\u53bb\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u91c7\u82b1\u8d3c\u539f\u6765\u662f \u4f60\uff01\u201d\u738b\u5c0a\u4e00\u8eab\u5f62\u9aa4\u8d77\uff0c\u4e00\u67c4\u5315\u9996\u556a\u7684\u4e00\u58f0\u9489\u5728\u6905\u80cc\u4e0a\u9762\uff0c\u6df1\u5165\u4e94\u5bf8\uff0c\u53e6\u4e00\u67c4\u76f4\u98de\u8fc7\u6765\uff0c\u5374 \u7ed9\u738b\u5c0a\u4e00\u53cc\u6307\u4e00\u7bb1\uff0c\u5c06\u5315\u9996\u7bb1\u4f4f\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5415\u56db\u5a18\u7eb5\u8dc3\u5982\u98ce\uff0c\u53ea\u4e00\u8d77\u843d\u4e4b\u95f4\uff0c\u5df2\u6251 \u5230\u738b\u5c0a\u4e00\u8eab\u8fb9\uff0c\u971c\u534e\u5251\u626c\u7a7a\u4e00\u6296\uff0c\u4e00\u62db\u201c\u9f99\u9876\u6458\u73e0\u201d\uff0c\u5954\u4ed6\u54bd\u5589\u523a\u53bb\u3002\u738b\u5c0a\u4e00\u6ed1\u6b65\u65c1\u7a9c\uff0c\u8f6c \u5230\u4e00\u6839\u201c\u6ee1\u5802\u7ea2\u201d\u7684\u65c1\u8fb9\uff0c\u90a3\u201c\u6ee1\u5802\u7ea2\u201d\u662f\u6839\u94f6\u94c1\u6775\uff0c\u4e0b\u9762\u6709\u811a\uff0c\u4e0a\u9762\u6258\u7740\u83b2\u82b1\uff0c\u83b2\u82b1\u4e0a\u660e \u6643\u6643\u7684\u70b9\u7740\u56db\u652f\u7ea2\u70db\uff0c\u738b\u5c0a\u4e00\u6025\u5207\u4e4b\u95f4\u627e\u4e0d\u7740\u5175\u5668\uff0c\u53cc\u81c2\u4e00\u62a1\uff0c\u628a\u201c\u6ee1\u5802\u7ea2\u201d\u63d0\u4e86\u8d77\u6765\uff0c\u5415 \u56db\u5a18\u7b2c\u4e8c\u62db\u201c\u98de\u7011\u6d41\u6cc9\u201d\uff0c\u767d\u5149\u95ea\u95ea\uff0c\u7adf\u4f3c\u5341\u51e0\u53e3\u5229\u5251\u540c\u65f6\u523a\u5230\uff0c\u738b\u5c0a\u4e00\u624b\u8155\u4e00\u7ffb\uff0c\u628a\u201c\u6ee1 \u5802\u7ea2\u201d\u5f53\u94c1\u68cd\u4f7f\uff0c\u4e00\u63a8\u4e00\u626b\uff0c\u4e00\u62db\u201c\u6a2a\u626b\u5343\u519b\u201d\uff0c\u628a\u5415\u56db\u5a18\u7684\u5b9d\u5251\u683c\u4e86\u5f00\u53bb\uff01\u5415\u56db\u5a18\u6014\u4e86\u4e00 \u6014\uff0c\u60f3\u4e0d\u5230\u7389\u5c0a\u4e00\u4f7f\u7684\u7adf\u662f\u5c11\u6797\u6d3e\u4e0a\u4e58\u7684\u6b63\u5b97\u4f0f\u864e\u68cd\u6cd5\uff01 \u3000\u3000\u90a3\u738b\u5c0a\u4e00\u597d\u4e0d\u5389\u5bb3\uff0c\u628a\u5251\u683c\u5f00\uff0c\u968f\u5373\u4e00\u62db\u201c\u631f\u5c71\u8d85\u6d77\u201d\uff0c\u98de\u8eab\u4e00\u8dc3\uff0c\u8df3\u5230\u5415\u56db\u5a18\u5de6\u4fa7\uff0c \u201c\u6ee1\u5802\u7ea2\u201d\u5411\u524d\u4e00\u9001\uff0c\u96ea\u4eae\u7684\u94f6\u94c1\u6775\u5c16\uff0c\u75be\u5982\u7bad\u9a76\uff0c\u53d8\u6210\u4e86\u5c11\u6797\u6d3e\u7684\u5927\u67aa\u62db\u6570\u201c\u4e4c\u9f99\u51fa \u6d1e\u201d\uff0c\u76f4\u5411\u5415\u56db\u5a18\u5c0f\u8179\u6311\u6765\uff0c\u5415\u56db\u5a18\u89c1\u4ed6\u67aa\u6cd5\u8f7b\u8584\uff0c\u52c3\u7136\u5927\u6012\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u5251\u5149\u5982\u5339\u7ec3\u822c\u4e00 \u95ea\uff0c\u81ea\u5de6\u5411\u53f3\u4e00\u65cb\uff0c\u65bd\u5c55\u5185\u5bb6\u529f\u529b\uff0c\u7adf\u628a\u201c\u6ee1\u5802\u7ea2\u201d\u62e6\u81f3\u5916\u95e8\uff0c\u968f\u624b\u4e00\u5251\u201c\u98de\u9e70\u640f\u5154\u201d\uff0c\u53c8 \u5411\u738b\u5c0a\u4e00\u4e0b\u4e09\u8def\u523a\u5230\u3002\u738b\u5c0a\u4e00\u4e5f\u4e0d\u7531\u5f97\u731b\u5403\u4e00\u60ca\uff0c\u8fd9\u5c11\u5973\u5251\u6cd5\u679c\u7136\u5c11\u89c1\uff0c\u628a\u201c\u6ee1\u5802\u7ea2\u201d\u4e00 \u756a\uff0c\u7528\u4e2a\u201c\u5c06\u519b\u4e0b\u9a6c\u201d\uff0c\u683c\u767b\u4e00\u58f0\uff0c\u6070\u628a\u5b9d\u5251\u6321\u4f4f\u3002\u4e24\u4e2a\u8ffd\u98ce\u9010\u7535\u822c\uff0c\u5728\u5bbd\u9614\u7684\u5385\u5802\u5927\u6218\u8d77 \u6765\u3002 \u3000\u3000\u5415\u56db\u5a18\u8fd9\u8fb9\u6253\u5f97\u5df2\u591f\u6fc0\u70c8\uff0c\u6613\u5170\u73e0\u90a3\u8fb9\uff0c\u6253\u5f97\u8fd8\u8981\u6fc0\u70c8\u4e07\u5206\uff01\u5929\u53f6\u6563\u4eba\u4e00\u89c1\u6613\u5170\u73e0\u98de\u8eab \u4e0b\u5730\uff0c\u970e\u5730\u4e00\u4e2a\u201c\u91d1\u9f99\u63a2\u722a\u201d\uff0c\u547c\u547c\u4e24\u638c\uff0c\u63a5\u8fde\u53d1\u51fa\uff0c\u4ed6\u5feb\uff0c\u6613\u5170\u73e0\u66f4\u5feb\uff0c\u638c\u98ce\u4eba\u5f71\u4e2d\uff0c\u6613 \u5170\u73e0\u7684\u964d\u9f99\u5b9d\u6756\u5df2\u70b9\u5411\u4ed6\u9762\u4e0a\u53cc\u775b\uff0c\u8fd9\u4e00\u62db\uff0c\u6362\u662f\u65c1\u4eba\u7edd\u9003\u4e0d\u4e86\uff0c\u90a3\u5929\u53f6\u6563\u4eba\u4e43\u662f\u897f\u57df\u7b2c\u4e00 \u9ad8\u624b\uff0c\u8f88\u4efd\u6bd4\u4e86\u56e0\u8fd8\u8981\u9ad8\u534a\u8f88\uff0c\u6b66\u529f\u4e5f\u786e\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u888d\u8896\u4e00\u62c2\uff0c\u53cd\u624b\u4e00\u638c\u5288\u51fa\uff0c\u4ee5\u653b\u4e3a \u5b88\uff0c\u6d88\u4e86\u6765\u52bf\uff0c\u6613\u5170\u73e0\u9053\u58f0\uff1a\u201c\u53ef\u60dc\uff0c\u4f60\u8fd9\u6837\u6b66\u529f\uff0c\u7adf\u7136\u81ea\u7518\u4e0b\u8d31\uff01\u201d\u624b\u8155\u4e00\u7ffb\uff0c\u964d\u9f99\u5b9d\u6756 \u53c8\u5377\u5730\u626b\u6765\uff0c\u5929\u53f6\u6563\u4eba\u7eb5\u8eab\u8dc3\u907f\uff0c\u53d8\u638c\u4e3a\u62ff\uff0c\u65bd\u5c55\u5206\u7b4b\u9519\u9aa8\u4e2d\u7684\u7edd\u62db\uff0c\u5411\u6613\u5170\u73e0\u7a7a\u95e8\u88ad\u51fb\u3002 \u90a3\u6599\u6613\u5170\u73e0\u7a81\u628a\u964d\u9f99\u5b9d\u6756\u5f53\u6210\u9752\u94a2\u5251\u7528\uff0c\u53f3\u624b\u5012\u63e1\u68d2\u68a2\uff0c\u76d8\u7a7a\u4e00\u7ed5\uff0c\u8eab\u79fb\u6b65\u6362\uff0c\u907f\u62db\u8fdb\u62db\uff0c \u964d\u9f99\u5b9d\u6756\u500f\u7684\u7ffb\u8d77\uff0c\u523a\u5230\u8170\u95f4\uff0c\u90a3\u8fb9\u53a2\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u6321\u4f4f\u4e86\u767d\u6cf0\u5b98\uff0c\u5927\u529b\u795e\u9b54\u8428\u5929\u90fd\u89c1\u6613 \u5170\u73e0\u6756\u6cd5\u5389\u5bb3\uff0c\u864e\u543c\u4e00\u58f0\uff0c\u5377\u8d77\u8863\u8896\uff0c\u9732\u51fa\u7c97\u5982\u6728\u67f1\u7684\u53cc\u81c2\u6765\u3002\u5929\u53f6\u6563\u4eba\u6b63\u88ab\u6613\u5170\u73e0\u903c\u5f97\u65e0 \u6cd5\u62db\u67b6\uff0c\u8428\u5929\u90fd\u6043\u7740\u795e\u529b\uff0c\u86ee\u51b2\u86ee\u6253\uff0c\u62f3\u8db3\u5e76\u7528\uff0c\u5de6\u811a\u4e00\u6311\uff0c\u53f3\u62f3\u8ddf\u7740\u5288\u80f8\u6253\u51fa\uff0c\u6070\u6070\u66ff\u5929 \u53f6\u6563\u4eba\u6321\u4f4f\u3002\u6613\u5170\u73e0\u7684\u5b9d\u6756\u6765\u5f97\u75be\u5982\u96f7\u9706\uff0c\u4e00\u6756\u6233\u5728\u8428\u5929\u90fd\u8170\u95f4\uff0c\u8428\u5929\u90fd\u6043\u7740\u94dc\u76ae\u94c1\u9aa8\uff0c\u5468 \u8eab\u5200\u67aa\u4e0d\u5165\uff0c\u5411\u524d\u4e00\u633a\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u8001\u4e10\u5a46\u4f60\u5948\u5f97\u6211\u4f55\uff1f\u2026\u2026\u3002\u54ce\u54df\uff0c\u4f60\u4f7f\u7684\u4ec0\u4e48\u5996 \u6cd5\uff1f\u201d\u8170\u95f4\u53c8\u75db\u53c8\u75d2\uff0c\u5495\u549a\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\uff0c\u6ee1\u5730\u6253\u6eda\uff01\u5ffd\u5730\u6367\u8179\u5927\u7b11\u8d77\u6765\uff0c\u5468\u8eab\u9178\u8f6f\uff0c\u539f \u6765\u6613\u5170\u73e0\u8fd9\u6756\uff0c\u6b63\u6b63\u6233\u5728\u4ed6\u7684\u7b11\u8170\u7a74\u4e0a\uff0c\u4ed6\u672c\u6765\u4e0d\u6015\u70b9\u7a74\uff0c\u65e0\u5948\u6613\u5170\u73e0\u6570\u5341\u5e74\u529f\u529b\uff0c\u7528\u7684\u53c8 \u662f\u5185\u5bb6\u771f\u529b\uff0c\u8428\u5929\u90fd\u5230\u5e95\u8fd8\u4e0d\u662f\u91d1\u521a\u4e0d\u574f\u4e4b\u8eaf\uff0c\u5982\u4f55\u62b5\u53d7\u5f97\u4e86\uff1f \u3000\u3000\u54c8\u5e03\u9640\u672c\u5f85\u52a9\u738b\u5c0a\u4e00\u53cc\u6218\u5415\u56db\u5a18\uff0c\u89c1\u5929\u53f6\u6563\u4eba\u5371\u9669\uff0c\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\u901f\u6765\uff01\u201d\u4e0e \u6d77\u4e91\u548c\u5c1a\u4e24\u4fa7\u631f\u51fb\uff0c\u54c8\u5e03\u9640\u4f7f\u7684\u662f\u6d41\u661f\u9524\uff0c\u6d77\u4e91\u548c\u5c1a\u524d\u665a\u88ab\u5415\u56db\u5a18\u524a\u65ad\u957f\u5251\uff0c\u73b0\u5728\u5df2\u65b0\u6362\u4e86 \u4e00\u628a\uff0c\u54c8\u5e03\u9640\u5148\u5230\uff0c\u7ed9\u6613\u5170\u73e0\u5b9d\u6756\u4e00\u6311\uff0c\u628a\u4e24\u4e2a\u6d41\u661f\u9524\u6311\u8fc7\u4e00\u8fb9\uff0c\u6d77\u4e91\u548c\u5c1a\u51b2\u6765\uff0c\u957f\u5251\u4e00\u62db \u201c\u957f\u8679\u7ecf\u5929\u201d\uff0c\u5206\u5fc3\u523a\u5230\u3002\u84e6\u7136\u95f4\u773c\u524d\u4eba\u5f71\u4e00\u6643\uff0c\u53ea\u542c\u5f97\u6613\u5170\u73e0\u7b11\u9053\uff1a\u201c\u4f60\u6765\u5f97\u597d\uff01\u201d\u6d77\u4e91 \u548c\u5c1a\u7a81\u611f\u624b\u8155\u4e00\u9635\u9178\u9ebb\uff0c\u624b\u4e2d\u957f\u5251\u5df2\u7ed9\u593a\u53bb\uff01 \u3000\u3000\u6613\u5170\u73e0\u672c\u6765\u6709\u4e00\u628a\u77ed\u5251\uff0c\u540d\u4e3a\u201c\u65ad\u7389\u201d\uff0c\u4e43\u662f\u6666\u660e\u7985\u5e08\u7559\u4e0b\u7684\u9547\u5c71\u53cc\u5251\u4e4b\u4e00\uff0c\u4e0e\u6e38\u9f99\u5251 \u540c\u6709\u524a\u94c1\u5982\u6ce5\u7684\u529f\u6548\u3002\u4f46\u5979\u56e0\u8f88\u4efd\u6781\u5c0a\uff0c\u8fd9\u6b21\u5357\u6765\u5374\u5e76\u4e0d\u628a\u5251\u5e26\u5728\u8eab\u8fb9\u3002\uff08\u5979\u5df2\u662f\u5929\u4e0b\u5251\u6cd5 \u7684\u7b2c\u4e00\u628a\u597d\u624b\uff0c\u5bf9\u4ed8\u540e\u8f88\uff0c\u4e0d\u503c\u5f97\u7528\u5251\u4e86\u3002\uff09\u4e0d\u6599\u8fd9\u65f6\u9aa4\u7136\u78b0\u7740\u51e0\u4e2a\u4e00\u6d41\u9ad8\u624b\u88ad\u51fb\uff0c\u7528\u964d\u9f99 \u5b9d\u6756\uff0c\u867d\u7136\u4e0d\u60e7\uff0c\u4f46\u5230\u5e95\u4e0d\u662f\u975e\u5e38\u719f\u624b\u3002\u8fd9\u65f6\u89c1\u6d77\u4e91\u548c\u5c1a\u957f\u5251\u523a\u5230\uff0c\u6b63\u5408\u5fc3\u610f\uff0c\u5288\u624b\u593a\u8fc7\uff0c \u5927\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u7136\u56f4\u653b\uff0c\u90a3\u53ef\u602a\u4e0d\u5f97\u6211\u62ff\u4f60\u4eec\u796d\u5251\uff01\u201d\u957f\u5251\u5bd2\u5149\u95ea\u95ea\uff0c\u8fde\u4e0b\u6740\u624b\uff0c\u4e0d\u8fc7 \u7247\u523b\uff0c\u6d77\u4e91\u548c\u5c1a\u5df2\u5148\u4e2d\u4e86\u4e00\u5251\uff0c\u8df3\u51fa\u5708\u5b50\uff0c\u53d6\u5251\u518d\u6597\uff01\u5929\u53f6\u6563\u4eba\u4e0e\u54c8\u5e03\u9640\u62fc\u547d\u62b5\u6321\uff0c\u5140\u662f\u5904 \u5728\u4e0b\u98ce\uff01 \u3000\u3000\u8fd9\u65f6\u95e8\u5916\u4eba\u58f0\u9f0e\u6cb8\uff0c\u5927\u95e8\u7830\u7684\u4e00\u58f0\u7ed9\u4eba\u8e22\u5f00\uff0c\u4e86\u56e0\u548c\u5c1a\u4e3a\u9996\uff0c\u63d0\u7740\u7897\u53e3\u7c97\u7684\u7985\u6756\uff0c\u5927\u8e0f \u6b65\u8d70\u6765\uff0c\u53cc\u773c\u4e00\u626b\uff0c\u4e0d\u7981\u60ca\u547c\uff1a\u201c\u539f\u6765\u662f\u4f60\uff01\u201d\u62a1\u52a8\u7985\u6756\uff0c\u98de\u8eab\u6251\u4e0a\uff0c\u201c\u8fc5\u96f7\u51fb\u9876\u201d\uff0c\u76f4\u5411 \u6613\u5170\u73e0\u540e\u8111\u6253\u843d\u3002\u6613\u5170\u73e0\u500f\u7684\u4e00\u4e2a\u8f6c\u8eab\uff0c\u5de6\u624b\u964d\u9f99\u5b9d\u6756\u4e00\u683c\uff0c\u53f3\u624b\u957f\u5251\u659c\u659c\u5411\u5916\u4e00\u63a8\uff0c\u4e00\u62db \u201c\u767d\u9e64\u5544\u9c7c\u201d\uff0c\u76f4\u70b9\u4e86\u56e0\u80f8\u819b\uff0c\u4e86\u56e0\u7acb\u8d77\u7985\u6756\uff0c\u4e00\u4e2a\u7ffb\u8eab\u201c\u4e4c\u9f99\u76d8\u6811\u201d\uff0c\u6a2a\u626b\u6613\u5170\u73e0\u4e2d\u8def\uff0c \u6613\u5170\u73e0\u957f\u5251\u4e00\u683c\uff0c\u7ffb\u8fc7\u4e00\u8fb9\uff0c\u54c8\u5e03\u9640\u4e0e\u5929\u53f6\u6563\u4eba\u4e24\u8fb9\u62a2\u4e0a\uff0c\u8fd9\u4e09\u4eba\u529f\u529b\uff0c\u90fd\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u4e86 \u56e0\u7684\u795e\u529b\u5c24\u5176\u60ca\u4eba\uff0c\u4e00\u652f\u7985\u6756\uff0c\u524d\u6311\u540e\u76d6\uff0c\u5de6\u6321\u53f3\u67b6\uff0c\u547c\u547c\u8f70\u8f70\uff0c\u6613\u5170\u73e0\u529b\u654c\u4e09\u4eba\uff0c\u582a\u582a\u6253 \u6210\u5e73\u624b\u3002 \u3000\u3000\u5415\u56db\u5a18\u4e0e\u738b\u5c0a\u4e00\u6597\u5f97\u6b63\u9163\uff0c\u89c1\u8bb8\u591a\u9526\u8863\u536b\u58eb\u6d8c\u8fdb\uff0c\u5251\u6cd5\u50ac\u7d27\uff0c\u75be\u5982\u7535\u63a3\uff0c\u5530\uff01\u5530\uff01\u5530\uff01 \u6d51\u8eab\u4e0a\u4e0b\uff0c\u5377\u8d77\u51e0\u9053\u767d\u8679\u4f3c\u7684\u5251\u5149\uff0c\u7f24\u7eb7\u98de\u821e\uff0c\u738b\u5c0a\u4e00\u6b66\u529f\u867d\u5f3a\uff0c\u51e0\u66fe\u89c1\u8fc7\u8fd9\u6837\u5251\u6cd5\uff1f\u614c\u5fd9 \u9000\u65f6\uff0c\u5415\u56db\u5a18\u4e00\u5251\u6a2a\u524a\uff0c\u8d34\u7740\u94c1\u6775\uff0c\u201c\u987a\u6c34\u63a8\u821f\u201d\u6a2a\u524a\u738b\u5c0a\u4e00\u624b\u6307\uff0c\u738b\u5c0a\u4e00\u5927\u543c\u4e00\u58f0\uff0c\u51e0\u5341 \u65a4\u91cd\u7684\u201c\u6ee1\u5802\u7ea2\u201d\u8131\u624b\u63b7\u51fa\uff0c\u5415\u56db\u5a18\u98d8\u8eab\u4e00\u95ea\uff0c\u90a3\u6839\u201c\u6ee1\u5802\u7ea2\u201d\u76f4\u98de\u8fc7\u6765\uff0c\u94c1\u6775\u5c16\uff0c\u6b63\u4e2d\u4e00 \u540d\u9ed1\u8863\u536b\u58eb\u7684\u54bd\u5589\uff0c\u54ce\u54df\u4e00\u58f0\uff0c\u4ef0\u7ffb\u5012\u5730\uff0c\u9888\u8840\u56db\u6e85\uff0c\u94c1\u6775\u7adf\u662f\u8d2f\u5589\u800c\u8fc7\uff01 \u3000\u3000\u5510\u6653\u6f9c\u4ed7\u7740\u6e38\u9f99\u5b9d\u5251\uff0c\u8fde\u524a\u536b\u58eb\u4eec\u7684\u5175\u5203\uff0c\u8fd9\u65f6\u8428\u5929\u90fd\u9178\u9ebb\u6e10\u6d88\uff0c\u53ea\u662f\u6c14\u529b\u8fd8\u672a\u5b8c\u5168\u6062 \u590d\uff0c\u89c1\u4f17\u536b\u58eb\u62b5\u6321\u4e0d\u4f4f\uff0c\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u8df3\u8d77\u6765\u6251\u5411\u5510\u6653\u6f9c\u3002\u5510\u6653\u6f9c\u5251\u5c16\u4e00\u6446\uff0c\u4fa7\u8eab\u75be \u523a\u3002\u8428\u5929\u90fd\u754f\u4ed6\u5b9d\u5251\uff0c\u5012\u4e0d\u6562\u786c\u63a5\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u62ff\u8fc7\u5251\u6765\uff01\u201d\u8eab\u5b50\u4e00\u817e\uff0c\u8d70\u504f \u950b\uff0c\u7a7f\u4fa7\u7ffc\uff0c\u5de6\u638c\u4e00\u5708\uff0c\u53f3\u6307\u4e00\u6263\uff0c\u8fd0\u7528\u64d2\u62ff\u624b\u6cd5\u6765\u62a2\u5510\u6653\u6f9c\u7684\u5b9d\u5251\uff0c\u672c\u4ee5\u4e3a\u5510\u6653\u6f9c\u6b66\u827a\u4f4e \u5fae\uff0c\u4e0d\u582a\u4e00\u51fb\uff0c\u90a3\u77e5\u5510\u6653\u6f9c\u8ddf\u6768\u4ef2\u82f1\u5b66\u4e86\u4e94\u5e74\u529f\u592b\uff0c\u6768\u4ef2\u82f1\u867d\u4e0d\u662f\u9876\u513f\u5c16\u513f\u7684\u4eba\u7269\uff0c\u4f46\u4e0e\u53cc \u9b54\u76f8\u8f83\uff0c\u4e5f\u5dee\u4e0d\u4e86\u591a\u5c11\uff0c\u800c\u4e14\u6768\u4ef2\u82f1\u7ec3\u7684\u662f\u6b63\u5b97\u7684\u5d69\u9633\u6d3e\u5185\u529f\u5fc3\u6cd5\uff0c\u4f20\u7ed9\u5510\u6653\u6f9c\uff0c\u5510\u6653\u6f9c\u7ec3 \u4e86\u4e94\u5e74\uff0c\u6839\u57fa\u5df2\u7a33\uff0c\u5251\u6cd5\u4e0a\u65e0\u5f62\u4e2d\u9ad8\u4e86\u8bb8\u591a\u3002\u4ed6\u7684\u8ffd\u98ce\u5251\u6cd5\u8fc5\u6377\u5f02\u5e38\uff0c\u8428\u5929\u90fd\u592a\u8fc7\u5927\u610f\uff0c\u731b \u6251\u64d2\u62ff\uff0c\u51b7\u4e0d\u9632\u5510\u6653\u6f9c\u624b\u8155\u4e00\u7ffb\uff0c\u5b9d\u5251\u81ea\u4e0b\u800c\u4e0a\uff0c\u6025\u6311\u4e0a\u6765\uff0c\u559d\u58f0\u201c\u7740\uff01\u201d\u8428\u5929\u90fd\u521a\u521a\u88ab\u6613 \u5170\u73e0\u6253\u4e00\u6756\uff0c\u884c\u52a8\u7a0d\u6ede\uff0c\u5de6\u81c0\u53c8\u4e2d\u4e86\u4e00\u5251\uff01 \u3000\u3000\u6e38\u9f99\u5251\u950b\u5229\u5f02\u5e38\uff0c\u5251\u5c16\u5165\u8089\u4e94\u5bf8\u3002\u8428\u5929\u90fd\u75db\u5f97\u5927\u543c\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u62ff\u547d\u6765\uff01\u201d\u66f4\u4e0d\u6362\u62db\uff0c \u53cc\u638c\u5411\u4e0b\u4fbf\u6309\u3002\u8428\u5929\u90fd\u53f7\u79f0\u201c\u5927\u529b\u795e\u9b54\u201d\uff0c\u8fd9\u53cc\u638c\u4e4b\u529b\uff0c\u4f55\u6b62\u5343\u65a4\uff0c\u5510\u6653\u6f9c\u521a\u624d\u90a3\u5251\uff0c\u4e0d\u8fc7 \u4e00\u65f6\u4fa5\u5e78\uff0c\u8fd9\u65f6\u5251\u672a\u64a4\u56de\uff0c\u8428\u5929\u90fd\u53cc\u638c\u5df2\u5982\u8fc5\u96f7\u4e0b\u51fb\uff0c\u7109\u80fd\u8eb2\u907f\uff0c\u53ea\u9053\u6b64\u547d\u4f11\u77e3\uff0c\u4e0d\u6599\u638c\u98ce \u8fc7\u5904\uff0c\u53ea\u89c9\u5934\u9876\u4f3c\u7ed9\u5200\u524a\u4e00\u6837\uff0c\u706b\u8fa3\u4f5c\u75db\uff0c\u4f46\u5374\u5e76\u6ca1\u53d7\u4f24\u3002\u5510\u6653\u6f9c\u7741\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u5415\u56db\u5a18\u8fd0 \u5251\u5982\u98ce\uff0c\u5df2\u628a\u8428\u5929\u90fd\u76f4\u903c\u51fa\u53bb\uff01\u4e00\u6478\u5934\u9876\uff0c\u5fae\u6709\u8840\u4e1d\uff0c\u5934\u53d1\u8131\u843d\u4e86\u4e00\u5927\u7247\uff01 \u3000\u3000\u8fd9\u65f6\u536b\u58eb\u518d\u5ea6\u56f4\u6765\uff0c\u5510\u6653\u6f9c\u89c1\u5f3a\u654c\u5df2\u9000\uff0c\u5fc3\u795e\u7a0d\u5b9a\uff0c\u53f3\u624b\u4e00\u626c\uff0c\u6ee1\u63e1\u98de\u8292\uff0c\u7535\u5c04\u800c\u51fa\uff0c \u5636\u5636\u4e71\u54cd\uff0c\u4f3c\u6d41\u661f\u98de\u5760\uff0c\u60ca\u96f7\u9aa4\u843d\uff0c\u4f17\u536b\u571f\u672a\u66fe\u89c1\u8fc7\u98de\u8292\u6697\u5668\uff0c\u6b66\u529f\u7a0d\u4f4e\u7684\uff0c\u5df2\u662f\u7ed9\u4ed6\u4f24\u4e86 \u51e0\u4e2a\u3002\u738b\u5c0a\u4e00\u5728\u536b\u58eb\u624b\u4e2d\uff0c\u62a2\u8fc7\u4e00\u53e3\u5355\u5200\uff0c\u7b11\u9053\uff1a\u201c\u5510\u5144\u5f1f\uff0c\u5c06\u5251\u4ea4\u7ed9\u611a\u5144\u4fdd\u7ba1\u4e86\u5427\uff01\u4f60\u6211 \u4e00\u89c1\u6295\u7f18\uff0c\u6211\u7edd\u4e0d\u80fd\u53eb\u4ed6\u4eec\u4f24\u4f60\uff01\u201d\u5510\u6653\u6f9c\u6012\u9053\uff1a\u201c\u4e8f\u4f60\u4e00\u8868\u65af\u6587\uff0c\u539f\u6765\u5374\u5e72\u91c7\u82b1\u52fe\u5f53\uff0c\u8c01 \u4e0e\u4f60\u8fd9\u4e0b\u4e09\u95e8\u7684\u5c0f\u8d3c\u505a\u5144\u5f1f\uff01\u201d\u6e38\u9f99\u5251\u75be\u53d1\u5982\u98ce\uff0c\u5168\u4f7f\u8fa3\u62db\u201c\u7334\u733f\u732e\u8eab\u201d\u201c\u4ed9\u4eba\u6307\u8def\u201d\u201c\u731b \u9e21\u593a\u7c9f\u201d\uff0c\u5411\u738b\u5c0a\u4e00\u8fdb\u653b\u3002\u738b\u5c0a\u4e00\u9762\u62db\u67b6\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u4ec0\u4e48\u91c7\u82b1\uff1f\u5510\u5144\u5f1f\uff0c\u4f60\u8bf4\u6211 \u91c7\u82b1\uff1f\u4f60\u4e0d\u6015\u7b11\u6298\u4e86\u8fd9\u91cc\u6b66\u6797\u5b97\u5320\u7684\u7259\u9f7f\uff1f\u201d\u5510\u6653\u6f9c\u72e0\u72e0\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u6b66\u6797\u5b97\u5320\uff1f\u4f60\u8fd9\u4e9b\u72d0 \u7fa4\u72d7\u515a\uff0c\u8fd8\u4e0d\u90fd\u662f\u4e00\u4e18\u4e4b\u8c89\uff01\u201d\u6e38\u9f99\u5251\u56de\u5251\u9707\u5f00\uff0c\u738b\u5c0a\u4e00\u54c8\u54c8\u5927\u7b11\uff0c\u624b\u8d77\u4e00\u8361\uff0c\u628a\u5510\u6653\u6f9c\u5b9d \u5251\u9707\u5f00\uff0c\u4e00\u5200\u5411\u5510\u6653\u6f9c\u624b\u8155\u5288\u6765\uff0c\u5510\u6653\u6f9c\u5251\u8bc0\u4e00\u9886\uff0c\u201c\u5b64\u9e64\u51cc\u6ce2\u201d\uff0c\u8dc3\u8eab\u907f\u62db\u8fd8\u62db\uff0c\u548c\u738b\u5c0a \u4e00\u6597\u5728\u4e00\u8d77\u3002\u738b\u5c0a\u4e00\u6b66\u529f\u867d\u6bd4\u4ed6\u9ad8\uff0c\u60f3\u593a\u5b9d\u5251\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u4e0d\u80fd\u591f\u3002 \u3000\u3000\u8fd9\u65f6\u4f8d\u536b\u8d8a\u6765\u8d8a\u591a\uff0c\u5415\u56db\u5a18\u5c55\u5f00\u7384\u5973\u5251\u6cd5\uff0c\u5c06\u8428\u5929\u90fd\u53c8\u523a\u4e86\u4e00\u5251\u4e4b\u540e\uff0c\u6025\u5fd9\u9000\u56de\u4e0e\u5510\u6653 \u6f9c\u5e76\u80a9\u4f5c\u6218\u3002\u4ed7\u7740\u5415\u56db\u5a18\u7684\u5251\u6cd5\u901a\u7384\uff0c\u5510\u6653\u6f9c\u7684\u5b9d\u5251\u950b\u5229\uff0c\u4f17\u536b\u58eb\u4e0d\u6562\u8fd1\u8eab\u3002\u53ef\u662f\u5df1\u5c11\u654c \u591a\uff0c\u738b\u5c0a\u4e00\u7684\u9ad8\u624b\uff0c\u591a\u662f\u5c11\u6797\u6d3e\u7684\u4e0a\u4e58\u529f\u592b\uff0c\u5415\u56db\u5a18\u548c\u5510\u6653\u6f9c\u4e8c\u4eba\u8fed\u906d\u51f6\u9669\uff0c\u5341\u5206\u5371\u6025\uff01 \u3000\u3000\u90a3\u8fb9\u53a2\u6613\u5170\u73e0\u6076\u6218\u4e86\u56e0\u548c\u5c1a\u3001\u5929\u53f6\u6563\u4eba\u4e0e\u54c8\u5e03\u9640\u4e09\u4e2a\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u867d\u662f\u4e0d\u80fd\u53d6\u80dc\uff0c\u5374 \u5df2\u5b8c\u5168\u5360\u4e86\u4e0a\u98ce\u3002\u4e86\u56e0\u7b49\u4e09\u4eba\u53ea\u80fd\u9760\u4e92\u76f8\u547c\u5e94\uff0c\u8fde\u73af\u5939\u51fb\u4e4b\u529b\uff0c\u624d\u80fd\u52c9\u5f3a\u62b5\u6321\u5f97\u4f4f\u3002\u6613\u5170\u73e0 \u89c1\u536b\u58eb\u7eb7\u7eb7\u6d8c\u6765\uff0c\u5927\u5802\u4e2d\u5230\u5904\u662f\u4eba\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u6697\u4e2d\u7980\u9053\uff1a\u7956\u5e08\u5728\u4e0a\uff0c\u6055\u6211\u5927\u5f00\u6740\u6212\uff01\u5251\u6cd5 \u500f\u53d8\uff0c\u53ea\u89c1\u5251\u82b1\u9519\u843d\uff0c\u51b7\u7535\u7cbe\u8292\uff0c\u98d8\u6e3a\u65e0\u5b9a\uff0c\u4f7f\u5230\u6025\u5904\uff0c\u5b9b\u4f3c\u5343\u65b9\u6761\u94f6\u86c7\u4e71\u63a3\uff0c\u4e86\u56e0\u7b49\u4e09\u4eba \u8fde\u8fde\u540e\u9000\u3002\u6613\u5170\u73e0\u8eab\u6cd5\u5982\u98ce\uff0c\u7ed5\u573a\u75be\u63a0\uff0c\u4e1c\u4e00\u5251\uff0c\u897f\u4e00\u5251\uff0c\u51fa\u624b\u8fc5\u6377\u65e0\u4f26\uff0c\u536b\u58eb\u4eec\u65b9\u89c1\u4eba\u5f71 \u6643\u5904\uff0c\u8eab\u4e0a\u5df2\u7ecf\u4e2d\u5251\uff0c\u7247\u523b\u4e4b\u95f4\u6d8c\u8fdb\u5927\u5802\u7684\u51e0\u5341\u540d\u9ed1\u8863\u536b\u58eb\u3002\u7adf\u7136\u5012\u4e0b\u4e86\u4e00\u5927\u534a\uff0c\u6bcf\u4e2a\u4eba\u8eab \u4e0a\u90fd\u53d7\u4e86\u4e00\u4e24\u5904\u5251\u4f24\uff01 \u3000\u3000\u6613\u5170\u73e0\u4f7f\u7684\u8fd9\u8def\u5251\u6cd5\uff0c\u4e5f\u662f\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u8ffd\u98ce\u5251\u6cd5\uff0c\u4e0d\u8fc7\u867d\u7136\u540c\u662f\u4e00\u8def\u5251\u6cd5\uff0c\u5728\u6613\u5170\u73e0 \u4f7f\u6765\uff0c\u6bd4\u5510\u6653\u6f9c\u5389\u5bb3\u5c82\u6b62\u6570\u5341\u767e\u500d\uff01\u8fd8\u5e78\u6613\u5170\u73e0\u4e00\u5ff5\u6148\u60b2\uff0c\u8d1f\u5cf0\u6240\u523a\uff0c\u90fd\u662f\u5173\u8282\u4e0e\u4e0d\u81f4\u547d\u7684 \u7a74\u9053\u4e4b\u5904\uff0c\u76ee\u7684\u53ea\u5728\u4f7f\u654c\u4eba\u6d88\u5931\u6218\u6597\u529b\u91cf\u3002 \u3000\u3000\u6613\u5170\u73e0\u7ed5\u573a\u4e00\u5468\uff0c\u536b\u58eb\u4eec\u5012\u4e86\u4e00\u5927\u534a\uff0c\u6b66\u529f\u7a0d\u5f3a\u7684\u672a\u53d7\u5251\u4f24\u4e5f\u7eb7\u7eb7\u540e\u9000\uff0c\u4e86\u56e0\u548c\u5c1a\u7b49\u4eba \u5927\u60ca\uff0c\u6025\u5fd9\u8d77\u6765\u7f20\u6597\uff0c\u6613\u5170\u73e0\u5fc3\u60f3\uff1a\u64d2\u8d3c\u64d2\u738b\uff0c\u770b\u6765\u8fd9\u738b\u5c0a\u4e00\u662f\u4ed6\u4eec\u9996\u9886\uff0c\u628a\u8fd9\u53ae\u64d2\u4f4f\uff0c\u5148 \u7834\u4e86\u91c7\u82b1\u602a\u6848\u518d\u8bf4\u3002\u4e3b\u610f\u6253\u8d70\uff0c\u957f\u5251\u4e00\u6296\uff0c\u4e00\u4e2a\u201c\u697c\u819d\u62d7\u6b65\u201d\u98d8\u98ce\u822c\u95ea\u5230\u738b\u5c0a\u4e00\u53f3\u4fa7\uff0c\u4e00\u62db \u201c\u7389\u5973\u6295\u68ad\u201d\u6a2a\u523a\u8fc7\u6765\uff0c\u738b\u5c0a\u4e00\u5355\u5200\u4e00\u6401\uff0c\u53ee\u5f53\u4e00\u58f0\uff0c\u65ad\u4e3a\u4e24\u622a\u3002\u4e86\u56e0\u5413\u5f97\u5fc3\u80c6\u7686\u88c2\uff0c\u7985\u6756 \u6025\u6025\u626b\u6765\uff0c\u54c8\u5e03\u9640\u7684\u6d41\u661f\u9524\u4e5f\u8fde\u73af\u6253\u5230\uff0c\u6613\u5170\u73e0\u4e00\u51fb\u4e0d\u4e2d\uff0c\u5df2\u7ed9\u4ed6\u4eec\u4e8c\u4eba\u62e6\u4f4f\u3002 \u3000\u3000\u5415\u56db\u5a18\u89c1\u6613\u5170\u73e0\u5982\u6b64\u51fa\u624b\uff0c\u4e5f\u731b\u7136\u9192\u609f\uff0c\u971c\u534e\u5251\u75be\u53d1\u5982\u98ce\uff0c\u63a5\u8fde\u95ef\u8fc7\u51e0\u540d\u536b\u58eb\u7684\u5835\u622a\uff0c \u811a\u5c16\u4e00\u70b9\uff0c\u817e\u8eab\u63a0\u8d77\uff0c\u5ffd\u5730\uff0c\u4e00\u62db\u201c\u5929\u5c71\u96ea\u5d29\u201d\uff0c\u534a\u7a7a\u6740\u4e0b\uff0c\u5b9d\u5251\u76f4\u523a\u738b\u5c0a\u4e00\u9888\u9879\u3002\u8428\u5929\u90fd \u5f13\u8eab\u4e00\u8dc3\uff0c\u6025\u4fbf\u732b\u9e70\u6251\u51fb\u7684\u7edd\u6280\uff0c\u4e5f\u817e\u8d77\u8eab\u6765\uff0c\u820d\u6b7b\u5fd8\u751f\uff0c\u5728\u534a\u7a7a\u622a\u51fb\uff0c\u5341\u53ea\u957f\u53ef\u9038\u5c3a\u7684\u6307 \u7532\uff0c\u4e00\u9f50\u523a\u5230\uff0c\u4e24\u4eba\u529f\u592b\u90fd\u662f\u9ad8\u5f3a\u4e4b\u6781\uff0c\u534a\u7a7a\u4e2d\u8c01\u90fd\u96be\u4e8e\u95ea\u8eb2\uff0c\u5415\u56db\u5a18\u80a9\u5934\u4e2d\u4e86\u4e00\u722a\uff0c\u8428\u5929 \u90fd\u80f8\u819b\u4e5f\u4e2d\u4e86\u4e00\u5251\uff0c\u8fd8\u662f\u5415\u56db\u5a18\u529f\u529b\u8f83\u9ad8\uff0c\u4e2d\u4e86\u4e00\u722a\uff0c\u8fde\u4eba\u5e26\u5251\u8f6c\u4e86\u4e2a\u5706\u5708\uff0c\u628a\u6d8c\u6765\u7684\u536b\u58eb \u53c8\u4f24\u4e86\u51e0\u4e2a\uff0c\u5b9d\u5251\u4e00\u6325\uff0c\u4ecd\u7136\u6307\u5230\u738b\u5c0a\u4e00\u80cc\u540e\uff01\u800c\u8428\u5929\u90fd\u5403\u4e86\u4e00\u5251\uff0c\u535c\u58f0\u5760\u5730\uff0c\u5df2\u662f\u4e0d\u80fd\u52a8 \u5f39\uff01 \u3000\u3000\u738b\u5c0a\u4e00\u7ed5\u573a\u75be\u8d70\uff0c\u5ffd\u89c9\u80cc\u540e\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u8eab\u5f62\u4e00\u77ee\uff0c\u84e6\u5730\u201c\u7ffb\u8eab\u5c04\u864e\u201d\u53cd\u624b\u4e00\u62ff\uff0c\u5de6 \u624b\u53cc\u6307\u75be\u70b9\u5415\u56db\u5a18\u7684\u201c\u7a8d\u9634\u7a74\u201d\uff0c\u53f3\u62f3\u5982\u7bad\uff0c\u51b2\u6253\u5415\u56db\u5a18\u524d\u5fc3\uff0c\u8fd9\u662f\u5c11\u6797\u6d3e\u201c\u4f0f\u864e\u62f3\u201d\u4e2d\u7684 \u6551\u547d\u7edd\u62db\uff0c\u8d25\u4e2d\u6c42\u80dc\u3002\u5415\u56db\u5a18\u903c\u5f97\u6539\u653b\u4e3a\u5b88\uff0c\u541e\u80f8\u5438\u8179\uff0c\u6643\u8eab\u6025\u95ea\uff0c\u971c\u534e\u5251\u53d1\u51fa\u53bb\uff0c\u5708\u56de \u6765\uff0c\u731b\u5730\u7b2c\u4e8c\u5251\u53c8\u5377\u5730\u63a0\u6765\u3002\u738b\u5c0a\u4e00\u7edd\u9669\u5df2\u8fc7\uff0c\u5fc3\u795e\u7a0d\u5b9a\uff0c\u5de6\u62f3\u53f3\u638c\uff0c\u53cd\u51fb\u5415\u56db\u5a18\u4e0b\u76d8\uff0c\u4e00 \u540d\u9ed1\u8863\u536b\u58eb\u7eb5\u8dc3\u5982\u98ce\uff0c\u624b\u63d0\u4e24\u67c4\u94dc\u9524\uff0c\u5de6\u7838\u53f3\u538b\uff0c\u53ca\u65f6\u8d76\u5230\uff0c\u8fd9\u540d\u536b\u58eb\u540d\u53eb\u5f6d\u4e91\u5e94\uff0c\u4e43\u662f\u54c8 \u5e03\u9640\u526f\u624b\uff0c\u529f\u529b\u4e5f\u975e\u5bfb\u5e38\u53ef\u6bd4\uff0c\u5415\u56db\u5a18\u770b\u770b\u5f97\u624b\uff0c\u5ffd\u906d\u963b\u622a\uff0c\u52c3\u7136\u5927\u6012\uff0c\u731b\u7136\u4e00\u632f\u624b\u8155\uff0c\u5251 \u950b\u5012\u524a\uff0c\u4f7f\u51fa\u7384\u5973\u5251\u4e2d\u7684\u7edd\u62db\u201c\u79cb\u6c34\u6a2a\u821f\u201d\uff0c\u4ece\u53cc\u94dc\u4e4b\u4e0b\u94bb\u8fc7\uff0c\u4ecd\u5411\u738b\u5c0a\u4e00\u80f8\u819b\u523a\u53bb\u3002\u5f6d\u4e91 \u5e94\u6b66\u529f\u7cbe\u719f\uff0c\u6a2a\u9000\u4e24\u6b65\uff0c\u53cc\u94dc\u6025\u7838\uff0c\u5415\u56db\u5a18\u672c\u4ee5\u4e3a\u53ef\u4ece\u94dc\u5e95\u94bb\u8fc7\uff0c\u90a3\u6599\u5f6d\u4e91\u5e94\u5148\u9000\u540e\u590d\u4e0a\uff0c \u65b9\u4f4d\u6070\u5230\u597d\u5904\uff0c\u773c\u89c1\u8fd9\u53cc\u94dc\u843d\u4e0b\uff0c\u5c31\u7b97\u738b\u5c0a\u4e00\u7ed9\u5979\u5b9d\u5251\u523a\u4e2d\u3002\u5979\u4e5f\u514d\u4e0d\u4e86\u5934\u7834\u8840\u6d41\u4e4b\u707e\u3002 \u3000\u3000\u5415\u56db\u5a18\u906d\u9022\u7edd\u9669\uff0c\u9000\u5df2\u65e0\u53ca\uff0c\u4e0d\u987e\u4e00\u5207\uff0c\u971c\u534e\u5251\u4ecd\u7136\u5411\u524d\u731b\u523a\u3002\u6b63\u5728\u8fd9\u6b7b\u751f\u4fc4\u9877\u4e4b\u9645\uff0c \u5f6d\u4e91\u5e94\u4e0e\u5415\u56db\u5a18\u90fd\u84e6\u7136\u7ed9\u4eba\u4e00\u626f\u3002\u5206\u5f00\u4e24\u8fb9\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5f3a\u4e2d\u66f4\u6709\u5f3a\u4e2d\u624b\uff0c\u67f3\u6697\u82b1\u660e\u53c8\u4e00\u6751 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u56de\u3000\u6212\u5f8b\u96be\u6301\u3000\u7985\u5e08\u8ff7\u56f0\u60d1\u3000\u7b3a\u6587\u8bef\u91ca\u3000\u7ae5\u5b50\u5f04\u6743\u8c0b \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u5341\u56de \u6212\u5f8b\u96be\u6301\u3000\u7985\u5e08\u8ff7\u56f0\u60d1 \u7b3a\u6587\u8bef\u91ca\u3000\u7ae5\u5b50\u5f04\u6743\u8c0b \u3000\u3000\u5415\u56db\u5a18\u5f6d\u4e91\u5e94\u4e92\u76f8\u51b2\u51fb\uff0c\u5982\u7bad\u79bb\u5f26\uff0c\u5176\u52bf\u6781\u731b\uff0c\u7ed9\u6765\u4eba\u4e00\u4e0b\u5206\u5f00\uff0c\u751a\u4e3a\u60ca\u8bb6\u3002\u5415\u56db\u5a18\u6a2a \u8dc3\u4e09\u6b65\uff0c\u6536\u5251\u770b\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u6e05\u7626\u548c\u5c1a\uff0c\u8eab\u7a7f\u6708\u767d\u618e\u888d\uff0c\u811a\u767b\u53cc\u8033\u9ebb\u978b\uff0c\u624b\u8155\u4e0a\u6302\u7740\u4e00\u4e32\u4f5b \u73e0\uff0c\u53cc\u76ee\u4e0d\u6012\u800c\u5a01\uff0c\u6e0a\u4eab\u5cb3\u5cd9\uff0c\u72b6\u8c8c\u5a01\u4e25\u3002\u6b63\u62e6\u5728\u738b\u5c0a\u4e00\u4e0e\u81ea\u5df1\u7684\u4e2d\u95f4\u3002 \u3000\u3000\u6613\u5170\u73e0\u4e00\u53e3\u957f\u5251\u795e\u51fa\u9b3c\u6ca1\uff0c\u5149\u8292\u56db\u5c04\uff0c\u5b9b\u5982\u6c34\u94f6\u6cfb\u5730\uff0c\u628a\u4e86\u56e0\u3001\u54c8\u5e03\u9640\u4e0e\u5929\u53f6\u6563\u4eba\u4e09\u4e2a \u4e00\u7b49\u4e00\u7684\u9ad8\u624b\u6740\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u731b\u7136\u95f4\u89c1\u4e00\u4e2a\u548c\u5c1a\u534a\u7a7a\u8dc3\u4e0b\uff0c\u4e00\u624b\u62c9\u5f00\u4e86\u5415\u56db\u5a18\uff0c\u4e0d\u7981\u5927\u60ca\uff0c \u6a2a\u5251\u4e00\u5c01\uff0c\u628a\u4e86\u56e0\u4e09\u4eba\u903c\u51fa\u5916\u5708\uff0c\u6b63\u5f85\u8d76\u53bb\uff0c\u5ffd\u542c\u5f97\u90a3\u548c\u5c1a\u5927\u58f0\u559d\u9053\uff1a\u201c\u738b\u5c0a\u4e00\uff0c\u4f60\u8fd8\u4e0d\u4e56 \u4e56\u8ddf\u6211\u56de\u5c71\u3002\u201d\u6613\u5170\u73e0\u95fb\u8a00\u4e00\u6014\uff0c\u53ea\u542c\u5f97\u90a3\u738b\u5c0a\u4e00\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u53d4\u8fdc\u6765\uff0c\u5c0f\u4f84\u6709\u77e2\u8fce\u8fd3\uff0c \u5c31\u8bf7\u5e08\u53d4\u5728\u6b64\u76d8\u6853\u51e0\u5929\uff0c\u5c0f\u4f84\u6709\u4e8b\u7eca\u8eab\uff0c\u56de\u5c71\u8c12\u7075\uff0c\u6682\u65f6\u8fd8\u529e\u4e0d\u5230\u3002\u201d\u90a3\u548c\u5c1a\u62c2\u5c18\u4e00\u6307\uff0c\u5389 \u58f0\u65a5\u9053\uff1a\u201c\u4f60\u5728\u6211\u8ddf\u524d\uff0c\u8fd8\u88c5\u4ec0\u4e48\u849c\uff1f\u4f60\u6076\u8d2f\u6ee1\u76c8\uff0c\u4e0d\u8ddf\u6211\u56de\u5c71\uff0c\u96be\u9053\u8981\u903c\u6211\u5728\u6b64\u4e0b\u624b\u5417\uff1f\u201d \u3000\u3000\u6613\u5170\u73e0\u751a\u89c9\u51fa\u5947\uff0c\u8d70\u524d\u51e0\u6b65\uff0c\u90a3\u548c\u5c1a\u53cc\u638c\u5408\u8ba1\uff0c\u4f5c\u4e86\u4e00\u793c\uff0c\u6b49\u7136\u8bf4\u9053\uff1a\u201c\u6613\u5973\u4fa0\u8bf7\u6055\u65e0 \u793c\uff0c\u5c0f\u50e7\u662f\u5d69\u5c71\u5c11\u6797\u7684\u76d1\u5bfa\uff0c\u5c11\u6797\u4e0d\u5e78\uff0c\u51fa\u6b64\u4e0b\u8d31\u4e4b\u5f92\uff0c\u4e0d\u4f46\u6709\u73b7\u5bb6\u95e8\u800c\u4e14\u8fb1\u53ca\u6b66\u6797\uff0c\u7d2f\u5f97 \u6613\u5973\u4fa0\u8d39\u795e\u51fa\u529b\uff0c\u6211\u4eec\u5c11\u6797\u5bfa\u975e\u5e38\u8fc7\u610f\u4e0d\u53bb\uff01\u4eca\u5929\u6211\u5c31\u628a\u4ed6\u62bc\u89e3\u56de\u5c71\uff0c\u4f9d\u6cd5\u60e9\u6cbb\uff0c\u6613\u5973\u4fa0\u548c \u8fd9\u51e0\u4f4d\u670b\u53cb\u82e5\u80af\u8d4f\u5149\uff0c\u8bf7\u5230\u5d69\u5c71\u5c11\u6797\u5bfa\u9017\u7559\u6570\u65e5\uff0c\u6211\u4eec\u5d69\u5c71\u5c11\u6797\uff0c\u51b3\u4e0d\u5305\u5e87\u95e8\u5f92\uff0c\u5973\u4fa0\u4e5f\u53ef \u4f5c\u4e2a\u89c1\u8bc1\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u672c\u6765\u7591\u5fc3\u738b\u5c0a\u4e00\u662f\u738b\u5ba4\u5b50\u5f1f\uff0c\u6700\u5c11\u4e5f\u662f\u671d\u5ef7\u4e2d\u4eba\uff0c\u6240\u4ee5\u624d\u80fd\u628a\u94a6\u5dee\u884c\u7f72\u5f53\u4f5c\u85cf\u5978 \u4e4b\u5730\uff0c\u81f3\u6b64\u5927\u611f\u51fa\u4e4e\u610f\u5916\u3002\u5d69\u5c71\u5c11\u6797\u5bfa\u4e43\u5929\u4e0b\u6b66\u672f\u603b\u6c47\uff0c\u95e8\u5f92\u904d\u5e03\u56fd\u5185\uff0c\u58f0\u52bf\u4e4b\u5927\uff0c\u5176\u4ed6\u5404 \u6d3e\u671b\u5c18\u4e0d\u53ca\u3002\u4ee5\u524d\u7684\u4e3b\u6301\u672c\u7a7a\u5927\u5e08\u66f4\u662f\u56db\u65b9\u94a6\u4ef0\u7684\u6709\u9053\u9ad8\u50e7\uff0c\u738b\u5c0a\u4e00\u82e5\u7136\u662f\u7687\u5ba4\u4e2d\u4eba\uff0c\u90a3\u7edd \u4e0d\u80fd\u51fa\u4e8e\u5c11\u6797\u95e8\u4e0b\u3002\u5f53\u4e0b\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u52a8\u95ee\u5927\u5e08\u4e0e\u672c\u7a7a\u4e3b\u6301\u662f\u600e\u4e48\u4e2a\u79f0\u547c\uff1f\u201d\u90a3\u548c\u5c1a \u9053\uff1a\u201c\u672c\u7a7a\u5927\u5e08\u6b63\u662f\u8d2b\u50e7\u7684\u5e08\u5144\uff0c\u4ed6\u4e0d\u5e78\u53bb\u5e74\u5df2\u5706\u5bc2\u53bb\u4e86\u3002\u8fd9\u4e2a\u53db\u5f92\uff0c\u6b63\u662f\u4ed6\u7684\u4fd7\u5bb6\u5f1f\u5b50\u3002 \u73b0\u5728\u7684\u4e3b\u6301\u662f\u4e09\u5e08\u5f1f\u65e0\u4f4f\u7985\u5e08\u3002\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u672c\u65e0\u5927\u5e08\u4e86\uff1f\u201d\u90a3\u548c\u5c1a\u7a3d\u9996\u8bf4\u9053\uff1a \u201c\u6211\u4e0e\u51cc\u5927\u4fa0\u66fe\u6709\u4e00\u9762\u4e4b\u7f18\uff0c\u6211\u65e9\u6b32\u4e0a\u5929\u5c71\u62dc\u8c12\u5973\u4fa0\uff0c\u53ea\u56e0\u8def\u9014\u9065\u8fdc\uff0c\u5bfa\u52a1\u7f20\u8eab\uff0c\u8fdf\u8fdf\u672a\u80fd \u6210\u884c\u3002\u8fd9\u6b21\u5192\u72af\u5973\u4fa0\uff0c\u751a\u4e3a\u60ed\u6127\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u4e5f\u6697\u81ea\u53eb\u58f0\u201c\u60ed\u6127\u201d\uff0c\u600e\u4e48\u4e5f\u60f3\u4e0d\u8d77\u4ed6\u6765\u3002\u8fd9\u672c\u65e0\u7985\u5e08\u7cbe\u901a\u5c11\u6797\u795e\u62f3\uff0c\u529f\u529b\u4e0d\u5728 \u4ed6\u5e08\u5144\u672c\u7a7a\u4e4b\u4e0b\u3002\u8bba\u8d77\u8f88\u4efd\uff0c\u548c\u81ea\u5df1\u6b63\u662f\u540c\u8f88\u3002\u8fd9\u6b21\u548c\u4ed6\u5206\u5934\u67e5\u8fd9\u602a\u6848\uff0c\u82e5\u7136\u81ea\u5df1\u65e9\u53d1\u73b0\u6709 \u50cf\u4ed6\u90a3\u6837\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\u7684\u4eba\u7269\uff0c\u4e5f\u5fc5\u7136\u4f1a\u6000\u7591\u4ed6\u4e0e\u602a\u6848\u6709\u5173\uff0c\u53bb\u641c\u4ed6\u7684\u884c\u674e\u7684\u3002\u6613\u5170\u73e0\u8fd9\u6837 \u4e00\u60f3\uff0c\u5012\u4e5f\u4e0d\u602a\u672c\u65e0\u7985\u5e08\uff0c\u53ea\u662f\u6709\u70b9\u60ca\u5f02\uff0c\u770b\u672c\u65e0\u7985\u5e08\uff0c\u5e74\u903e\u82b1\u7532\uff0c\u8f7b\u529f\u4f55\u4ee5\u5c1a\u5982\u6b64\u4e86\u5f97\uff1f \u5176\u5b9e\u672c\u65e0\u5927\u5e08\u7684\u6b66\u529f\u4e0e\u6613\u5170\u73e0\u539f\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u4f46\u6b64\u756a\u63a2\u6848\uff0c\u6613\u5170\u73e0\u5148\u662f\u4e13\u5fc3\u6ce8\u610f\u767d\u6cf0\u5b98\uff0c\u6240 \u4ee5\u6ca1\u7559\u795e\u5230\u672c\u65e0\u5927\u5e08\u4e5f\u8ddf\u8e2a\u5979\u7f62\u4e86\u3002 \u3000\u3000\u4e86\u56e0\u548c\u5c1a\u3001\u5929\u53f6\u6563\u4eba\u4e0e\u54c8\u5e03\u9640\u4e09\u4eba\uff0c\u7eb5\u8eab\u6025\u9000\uff0c\u5e03\u6210\u7284\u89d2\u4e4b\u52bf\uff0c\u62a4\u7740\u738b\u5c0a\u4e00\u3002\u672c\u65e0\u5927\u5e08 \u5411\u5929\u53f6\u6563\u4eba\u5408\u4ec0\u4f5c\u793c\uff0c\u5f00\u58f0\u8bf4\u9053\uff1a\u201c\u5929\u53f6\u9053\u5144\uff0c\u8d2b\u50e7\u4e45\u95fb\u5927\u540d\uff0c\u5982\u96f7\u8d2f\u8033\u3002\u4e0d\u77e5\u9053\u5144\u4f55\u4ee5\u5e87 \u62a4\u53db\u5f92\uff0c\u52a9\u7ea3\u4e3a\u8650\uff1f\u201d\u4e86\u56e0\u559d\u9053\uff1a\u201c\u672c\u65e0\u5927\u5e08\uff0c\u4f60\u5728\u5d69\u5c71\u6e05\u4fee\uff0c\u4e5f\u8fd8\u7f62\u4e86\uff0c\u4f55\u4ee5\u5230\u6b64\u5e72\u9884\u95f2 \u4e8b\uff1f\u201d\u672c\u65e0\u5927\u5e08\u62c2\u5c18\u4e00\u626b\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u60f3\u5fc5\u662f\u6c5f\u5357\u516b\u4fa0\u4e4b\u9996\uff0c\u4e86\u56e0\u5927\u5e08\u4e86\uff0c\u542c\u8bf4\u5927\u5e08 \u8fd1\u6765\u6625\u98ce\u5f97\u610f\uff0c\u5e94\u6e05\u5ef7\u4ec0\u4e48\u7687\u5b50\u4e4b\u8058\uff0c\u53d7\u5c01\u4e3a\u5b9d\u56fd\u7985\u5e08\u4e86\uff0c\u8d2b\u50e7\u4ecd\u5c71\u91ce\u5c0f\u6c11\uff0c\u4e0d\u6562\u4e0e\u8d35\u4e3a\u56fd \u5e08\u7684\u4eba\u6765\u5f80\u3002\u8d2b\u50e7\u867d\u4e0e\u72ec\u81c2\u795e\u5c3c\u4e5f\u6709\u70b9\u5c0f\u5c0f\u7684\u4ea4\u60c5\uff0c\u4f46\u95e8\u6237\u4e0d\u540c\uff0c\u8d2b\u50e7\u6b63\u56e0\u4e0d\u80af\u591a\u7ba1\u95f2\u4e8b\uff0c \u6240\u4ee5\u867d\u4e0e\u795e\u5c3c\u4efd\u5c5e\u6545\u4ea4\uff0c\u5bf9\u5979\u7684\u53db\u5f92\u4e5f\u8fd8\u4e0d\u613f\u51fa\u624b\u6599\u7406\uff0c\u6211\u60f3\u72ec\u81c2\u795e\u5c3c\u9057\u89c4\u5c1a\u5728\uff0c\u81ea\u6709\u5979\u7684 \u95e8\u6237\u4e2d\u4eba\u51fa\u5934\u3002\u5927\u56fd\u5e08\u8d23\u6211\u591a\u7ba1\u95f2\u4e8b\uff0c\u771f\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\uff1f\u201d \u3000\u3000\u672c\u65e0\u5927\u5e08\u53ea\u77e5\u4e86\u56e0\u53d7\u56db\u7687\u5b50\u4e4b\u8058\uff0c\u5374\u4e0d\u77e5\u5929\u53f6\u6563\u4eba\u4e5f\u53d7\u4e86\u8058\u3002\u8fd9\u756a\u8bf4\u8bdd\uff0c\u6697\u5b58\u8ba5\u523a\uff0c\u660e \u660e\u77e5\u9053\u4e86\u56e0\u4e43\u662f\u72ec\u81c2\u795e\u5c3c\u7684\u9996\u5f92\uff0c\u5374\u504f\u4e0d\u63d0\u8d77\uff0c\u6bd4\u660e\u9a82\u4e86\u56e0\u8fd8\u66f4\u5389\u5bb3\uff01\u4e0d\u4f46\u4e86\u56e0\u8001\u7f9e\u6210\u6012\uff0c \u5929\u53f6\u6563\u4eba\u4e5f\u662f\u9762\u7ea2\u8fc7\u8033\u3002\u4e86\u56e0\u7985\u6756\u4e00\u6446\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u672c\u65e0\u8001\u79c3\uff0c\u6211\u656c\u4f60\u662f\u957f\u8f88\uff0c\u624d\u597d\u5fc3\u529d \u4f60\uff0c\u4f60\u5f53\u6211\u771f\u6015\u4f60\u4e48\uff1f\u201d\u672c\u65e0\u5927\u5e08\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5e74\u8fc8\u65e0\u80fd\uff0c\u90a3\u6562\u50cf\u4e00\u4e9b\u540e\u751f\u5c0f\u8f88\u5984\u5728\u6c5f\u6e56 \u79f0\u5f3a\u9053\u9738\uff1f\u6211\u5df2\u9075\u5927\u56fd\u5e08\u4e4b\u529d\u4e0d\u6562\u518d\u5728\u6c5f\u6e56\u4e0a\u591a\u7ba1\u95f2\u4e8b\u4e86\uff0c\u4f46\u6211\u672c\u95e8\u5e08\u4f84\uff0c\u6211\u603b\u8be5\u8fd8\u7ba1\u5f97\uff01 \u563f\uff0c\u6211\u4e5f\u8981\u529d\u5927\u56fd\u5e08\u4e0d\u8981\u7ba1\u6211\u5c11\u6797\u95e8\u6237\u4e2d\u4e8b\uff01\u5927\u56fd\u5e08\u82e5\u4e00\u5b9a\u8981\u7ba1\u5462\uff0c\u90a3\u4e48\u5c31\u8bf7\u5927\u56fd\u5e08\u77e5\u4f1a\u5929 \u4e0b\u82f1\u96c4\uff0c\u5230\u5d69\u5c71\u8d50\u6559\uff01\u201d\u4e86\u56e0\u864e\u543c\u4e00\u58f0\uff0c\u7897\u53e3\u7c97\u7684\u7985\u6756\u9661\u7136\u6253\u51fa\uff0c\u672c\u65e0\u5927\u5e08\u62c2\u5c18\u4e00\u626c\uff0c\u628a\u7985 \u6756\u7f20\u7740\uff0c\u4ee5\u4e86\u56e0\u7684\u795e\u529b\uff0c\u7adf\u7136\u88ab\u4ed6\u963b\u4f4f\uff01\u6b63\u60f3\u53d8\u62db\uff0c\u5ffd\u95fb\u5f97\u738b\u5c0a\u4e00\u4e5f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5e08\u53d4\u4e00\u6765 \u5c31\u4ee5\u5bb6\u6cd5\u76f8\u8d23\uff0c\u4e0d\u77e5\u5c11\u6797\u5bb6\u6cd5\u7b2c\u5341\u4e09\u6761\u8bf4\u7684\u662f\u4ec0\u4e48\uff1f\u201d\u672c\u65e0\u5927\u5e08\u4e00\u6014\uff0c\u539f\u6765\u7b2c\u5341\u4e09\u6761\u8bf4\u7684 \u662f\uff0c\u82e5\u7136\u5c11\u6797\u95e8\u4e0b\uff0c\u88ab\u8bef\u4e3a\u72af\u4e86\u6e05\u89c4\u5927\u6212\u7684\uff0c\u51c6\u8bb8\u7533\u8fa9\u3002\u5bf9\u76d1\u5bfa\u6240\u5224\uff0c\u4e0d\u670d\u8005\u53ef\u9080\u8bf7\u8bc1\u4eba\uff0c \u5230\u5d69\u5c71\u7533\u8ff0\u8bc4\u7406\u3002\u6700\u591a\u8bb8\u4ee5\u4e00\u6708\u4e3a\u671f\u3002\u5c11\u6797\u8fd9\u6761\u201c\u5bb6\u6cd5\u201d\uff0c\u7528\u610f\u662f\u6015\u6709\u4eba\u53d7\u4e86\u51a4\u5c48\uff0c\u76d1\u5bfa\u8bef \u5224\uff0c\u4ee5\u81f3\u6c89\u51a4\u8499\u767d\u7684\u3002\u8981\u77e5\u5c11\u6797\u95e8\u4eba\u751a\u591a\uff0c\u6c5f\u6e56\u4e0a\u826f\u840e\u4e0d\u9f50\uff0c\u6709\u65f6\u4e0d\u514d\u8bef\u53d7\u7275\u7d2f\uff1b\u76d1\u5bfa\u62a4 \u6cd5\uff0c\u6709\u65f6\u5bdf\u770b\u4e5f\u5bb9\u6709\u4e0d\u5468\uff0c\u6240\u4ee5\u7acb\u6cd5\u4ee5\u5bbd\u6d4e\u4e25\uff0c\u4e0d\u50cf\u5176\u4ed6\u5404\u6d3e\uff0c\u505a\u638c\u95e8\u7684\u4fbf\u53ef\u751f\u6740\u4e88\u593a\uff0c\u5177 \u6709\u65e0\u4e0a\u6743\u5a01\u3002 \u3000\u3000\u738b\u5c0a\u4e00\u6b64\u8a00\u4e00\u51fa\uff0c\u672c\u65e0\u7985\u5e08\u53f3\u624b\u4e00\u677e\uff0c\u628a\u62c2\u5c18\u6536\u56de\uff0c\u7741\u773c\u8bf4\u9053\uff1a\u201c\u6211\u4eb2\u773c\u89c1\u4f60\u540c\u515a\u52ab\u593a \u5c11\u5973\uff0c\u732e\u6765\u4e0e\u4f60\uff0c\u4f60\u4e43\u662f\u91c7\u82b1\u6848\u7684\u4e3b\u51f6\uff0c\u96be\u9053\u6211\u8fd8\u8bec\u4f60\u4e0d\u6210\uff1f\u201d\u738b\u5c0a\u4e00\u5937\u7136\u81ea\u82e5\uff0c\u53ea\u662f\u5fae\u5fae \u53d1\u7b11\uff01 \u3000\u3000\u672c\u65e0\u5927\u5e08\u89c1\u5e08\u4f84\u4e0d\u7406\u4e0d\u7b54\uff0c\u53d8\u8272\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u4f60\u8981\u7533\u8fa9\uff0c\u6211\u4fbf\u8bb8\u4f60\u4e00\u6708\u4e3a\u671f\uff0c\u4efb\u4f60\u9080\u8bf7 \u8bc1\u4eba\uff0c\u4e0a\u5d69\u5c71\u8bc4\u7406\uff01\u4f60\u82e5\u4ee5\u4e3a\u6709\u4eba\u4f5c\u4f60\u9760\u5c71\uff0c\u5984\u60f3\u9003\u533f\uff0c\u90a3\u4f60\u53ef\u529e\u4e0d\u5230\uff01\u201d\u738b\u5c0a\u4e00\u50b2\u7136\u7b11 \u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u9003\uff1f\u4e00\u6708\u540e\u6211\u51c6\u5230\u5d69\u5c71\u4fbf\u662f\uff01\u201d\u672c\u65e0\u5927\u5e08\u89c1\u4ed6\u6001\u5ea6\u96cd\u5bb9\uff0c\u6beb\u65e0\u80c6\u602f\u4e4b\u610f\uff0c \u597d\u751f\u5947\u602a\uff01\u5fc3\u60f3\u82e5\u975e\u81ea\u5df1\u4eb2\u81ea\u7834\u6848\uff0c\u771f\u4e0d\u6562\u76f8\u4fe1\u4ed6\u5c31\u662f\u91c7\u82b1\u6848\u7684\u4e3b\u4f7f\u4eba\u3002\u770b\u4ed6\u5112\u7a1a\u5a01\u6b66\u4e24\u4ff1 \u6709\u4e4b\uff0c\u9762\u4e0a\u4e0d\u73b0\u534a\u70b9\u90aa\u6c14\u3002\u8c01\u6599\u4ed6\u4f1a\u505a\u51fa\u8fd9\u79cd\u6700\u72af\u6c5f\u6e56\u4e4b\u5fcc\u7684\u4e0b\u8d31\u4e4b\u4e8b\u3002 \u3000\u3000\u5f53\u4e0b\u672c\u65e0\u5927\u5e08\u518d\u5bf9\u6613\u5170\u73e0\u9053\uff1a\u201c\u5230\u671f\u4e5f\u5949\u8bf7\u5973\u4fa0\u4e0a\u5c71\u4f5c\u4e2a\u89c1\u8bc1\uff0c\u8fd9\u51e0\u4f4d\u670b\u53cb\u4e5f\u4e00\u5e76\u8bf7 \u4e86\u3002\u201d\u6613\u5170\u73e0\u7b11\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u4fbf\u662f\u4e86\u56e0\u7684\u5e08\u5f1f\u5e08\u59b9\uff0c\u767d\u6cf0\u5b98\u548c\u5415\u56db\u5a18\u3002\u201d\u672c\u65e0\u5927\u5e08\u9053\uff1a\u201c\u90a3 \u66f4\u597d\u4e86\uff0c\u54b1\u4eec\u5148\u8d70\uff01\u201d\u8fd9\u65f6\u95e8\u5916\u706b\u628a\u5982\u6797\uff0c\u5c71\u4e1c\u5de1\u629a\u7530\u6587\u955c\u4eb2\u7387\u5175\u4e01\u628a\u5927\u53a6\u56e2\u56e2\u56f4\u4f4f\uff0c\u738b\u5c0a \u4e00\u6325\u4e86\u62e9\u624b\uff0c\u54c8\u5e03\u9640\u51fa\u5916\u4e00\u9635\uff0c\u738b\u5c0a\u4e00\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u8bf7\u6055\u5c0f\u4f84\u4e0d\u8fdc\u9001\u4e86\uff01\u201d\u672c\u65e0\u5411\u5916\u4e00\u671b\uff0c \u53ea\u89c1\u95e8\u5916\u5175\u4e01\uff0c\u970e\u5ffd\u4e4b\u95f4\u5df2\u9000\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u770b\u4f60\u4e0d\u51fa\uff0c\u539f\u6765\u4f60\u8fd8\u52fe\u7ed3\u6ee1\u5974\uff0c\u662f \u94a6\u5dee\u5927\u4eba\u7684\u8d35\u5ba2\uff01\u201d\u738b\u5c0a\u4e00\u6717\u7136\u8bf4\u9053\uff1a\u201c\u8bf7\u5e08\u53d4\u4e00\u5e76\u8bb0\u5728\u5c0f\u4f84\u5e10\u4e0a\uff0c\u8be5\u6740\u8be5\u5250\uff0c\u5230\u65f6\u8bf7\u4e3b\u6301 \u548c\u6b66\u6797\u524d\u8f88\u5224\u83f2\u4fbf\u662f\uff01\u201d\u672c\u65e0\u5927\u5e08\u6c14\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u4e00\u6446\u62c2\u5c18\uff0c\u5f80\u5916\u4fbf\u8d70\u3002\u6613\u5170\u73e0\u788d\u4e8e\u738b\u5c0a \u4e00\u662f\u5c11\u6797\u6d3e\u7684\u4eba\uff0c\u65e2\u6709\u672c\u65e0\u5927\u5e08\u51fa\u5934\uff0c\u81ea\u5df1\u53ea\u597d\u653e\u624b\u3002 \u3000\u3000\u4e94\u4eba\u56de\u5230\u4e86\u7384\u5999\u89c2\uff0c\u672c\u65e0\u5927\u5e08\u5411\u6613\u5170\u73e0\u4e00\u518d\u9053\u6b49\u3002\u8c08\u4e86\u4e24\u5929\u6b66\u5b66\uff0c\u5404\u81ea\u94a6\u4f69\u3002\u4e24\u65e5\u540e\u672c \u65e0\u5927\u5e08\u81ea\u56de\u5d69\u5c71\uff0c\u767d\u6cf0\u5b98\u5219\u9080\u5415\u56db\u5a18\u53bb\u8bbf\u7518\u51e4\u6c60\uff0c\u51c6\u5907\u5148\u4e0a\u9099\u5c71\u796d\u626b\u5e08\u7236\u4e4b\u5893\uff0c\u7136\u540e\u518d\u5230\u5d69 \u5c71\u4f5c\u8bc1\u3002 \u3000\u3000\u6613\u5170\u73e0\u8ba1\u7b97\u8def\u7a0b\uff0c\u5c71\u4e1c\u4e0e\u6cb3\u5357\u76f8\u90bb\uff0c\u4ece\u9752\u5c9b\u5230\u5d69\u5c71\uff0c\u4ee5\u5979\u548c\u5510\u6653\u6f9c\u7684\u811a\u7a0b\uff0c\u6700\u591a\u534a\u6708\u53ef \u5230\uff0c\u4f7f\u53eb\u5510\u6653\u8c30\u5728\u7384\u5999\u89c2\u4e2d\u7559\u4e0b\uff0c\u5148\u4f20\u4ed6\u5185\u529f\u7684\u5410\u7eb3\u4e4b\u9053\uff0c\u5510\u6653\u6f9c\u5728\u6768\u4ef2\u82f1\u95e8\u4e0b\u4e94\u5e74\uff0c\u6240\u4e60 \u672c\u662f\u6b63\u5b97\uff0c\u6709\u4e86\u6839\u5e95\uff0c\u518d\u7ecf\u6613\u5170\u73e0\u4e00\u70b9\u7a8d\u8981\uff0c\u987f\u65f6\u610f\u4e0e\u795e\u4f1a\uff0c\u4e0a\u624b\u751a\u6613\u3002 \u3000\u3000\u534a\u6708\u4e4b\u540e\uff0c\u6613\u5510\u4e8c\u4eba\u4ece\u9752\u5c9b\u5357\u4e0b\uff0c\u7ecf\u4e34\u6c82\u518d\u5411\u897f\u6298\uff0c\u4ece\u66f2\u961c\u76f4\u4e0b\u6d4e\u5b81\uff0c\u8fdb\u5165\u6cb3\u5357\u5546\u4e18\uff0c \u518d\u8fc7\u51e0\u5929\uff0c\u6765\u5230\u5d69\u5c71\u3002\u53ea\u89c1\u90a3\u5c11\u6797\u5bfa\u5c4b\u5b87\u8fde\u4e91\uff0c\u9cde\u6b21\u6809\u6bd4\uff0c\u679c\u7136\u4e0d\u6127\u662f\u4e00\u4e2a\u4f5b\u5bfa\u7684\u5927\u4e1b\u6797\u3002 \u8fdb\u5230\u5bfa\u95e8\uff0c\u65e9\u6709\u6267\u638c\u5ba4\u7ecf\u5802\u7684\u50e7\u4eba\uff0c\u5230\u77e5\u5ba2\u5802\u63a5\u5f15\uff0c\u7ecf\u8fc7\u5927\u96c4\u5b9d\u6bbf\uff0c\u8fdb\u5165\u7f57\u6c49\u5802\uff0c\u672c\u65e0\u5927\u5e08 \u4e5f\u5df2\u4eb2\u81ea\u51fa\u8fce\uff0c\u8bf4\u8d77\u65e5\u671f\uff0c\u539f\u6765\u660e\u65e5\u5c31\u662f\u3002\u5f53\u4e0b\u672c\u65e0\u66ff\u6613\u5170\u73e0\u5f15\u89c1\u5c11\u6797\u5bfa\u7684\u65b0\u4e3b\u6301\u65e0\u4f4f\u7985 \u5e08\uff0c\u65e0\u4f4f\u7985\u5e08\u6148\u7709\u5584\u76ee\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u4e2a\u6709\u9053\u7684\u9ad8\u50e7\uff0c\u65e0\u4f4f\u867d\u7136\u662f\u672c\u65e0\u7684\u5e08\u5f1f\uff0c\u4f46\u4ed6\u5bf9\u4f5b\u7ecf\u6f5c \u5fc3\u7814\u7a76\uff0c\u5584\u8bf4\u4e0a\u4e58\u4e4b\u6cd5\uff0c\u6b66\u529f\u867d\u7565\u900a\u5e08\u5144\uff0c\u9053\u5fb7\u4fee\u884c\uff0c\u5374\u662f\u960e\u82bd\u7b2c\u4e00\uff0c\u6240\u4ee5\u505a\u4e86\u4e3b\u6301\uff0c\u4f46\u8fd9 \u65e0\u4f4f\u7985\u5e08\u7684\u957f\u5904\u4e5f\u6b63\u662f\u4ed6\u7684\u77ed\u5904\uff0c\u4ed6\u529b\u4e3b\u6e05\u8179\uff0c\u4e0d\u95ee\u4fd7\u4e8b\uff0c\u867d\u7136\u4e0d\u8bb8\u95e8\u5f92\u4e0e\u5b98\u5e9c\u5f80\u6765\uff0c\u4f46\u4e5f \u4e0d\u9f13\u52b1\u4ed6\u4eec\u4e0e\u5b98\u5e9c\u4f5c\u5bf9\u3002\u4ed6\u53ea\u6c42\u9020\u9065\u5316\u5916\uff0c\u5b8f\u626c\u4f5b\u6cd5\uff0c\u4fbf\u8ba4\u4e3a\u662f\u6c42\u6b63\u679c\u7684\u4e0d\u4e8c\u6cd5\u95e8\uff0c\u8fd9\u4e14\u6309 \u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u4e14\u8bf4\u5510\u6653\u6f9c\u8fdb\u4e86\u5c11\u6797\u5bfa\u540e\uff0c\u81ea\u6709\u77e5\u5ba2\u5e26\u4ed6\u5230\u7985\u623f\u5b89\u6b47\u3002\u5510\u6653\u6f9c\u4f4f\u7684\u7985\u623f\u6b63\u5728\u7f57\u6c49\u5802\u4fa7 \u8fb9\uff0c\u5802\u4e2d\u4e00\u76cf\u5de8\u5927\u7409\u7483\u706f\uff0c\u60ac\u5728\u6bbf\u9876\uff0c\u706f\u7130\u8db3\u6709\u7897\u53e3\u5927\u5c0f\uff0c\u653e\u51fa\u7f24\u7eb7\u5f02\u5f69\uff0c\u795e\u684c\u4e0a\u70b9\u7740\u7c97\u5982 \u513f\u81c0\u7684\u5de8\u70db\uff0c\u70db\u7130\u7a9c\u8d77\u534a\u5c3a\u591a\u9ad8\uff0c\u5510\u6653\u6f9c\u76d8\u8db3\u8dc3\u5750\u7985\u5e8a\u4e4b\u4e0a\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4f46\u89c9\u4e07\u7c41\u4ff1 \u5bc2\uff0c\u53ea\u6709\u5802\u4e2d\u70db\u7130\uff0c\u5076\u800c\u53d1\u51fa\u201c\u5fc5\u5265\u201d\u4e4b\u58f0\u3002\u5510\u6653\u6f9c\u5fc3\u60f3\uff0c\u8fd9\u5c11\u6797\u5bfa\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u542c\u767d\u6cf0 \u5b98\u8bf4\uff0c\u5b83\u6709\u4e09\u5341\u516d\u5ea7\u6bbf\uff0c\u4e94\u767e\u591a\u50e7\u4fa3\uff0c\u5165\u591c\u4e4b\u540e\uff0c\u5c45\u7136\u5982\u6b64\u5bc2\u9759\uff0c\u7684\u786e\u662f\u4e2a\u6212\u5f8b\u8c28\u4e25\u7684\u5bfa \u9662\u3002\u6b63\u601d\u91cf\u95f4\uff0c\u5ffd\u95fb\u5f97\u5916\u9762\u6709\u8f7b\u5fae\u7684\u58f0\u606f\uff0c\u5510\u6653\u6f9c\u6084\u6084\u4e0b\u4e86\u7985\u5e8a\uff0c\u5728\u95e8\u9699\u4e00\u671b\uff0c\u53ea\u89c1\u5927\u5802\u4e0a \u7adf\u7136\u6709\u4e2a\u5341\u4e09\u56db\u5c81\u7684\u5b69\u5b50\uff0c\u88f8\u7740\u53cc\u8db3\uff0c\u91d1\u73af\u675f\u53d1\uff0c\u4e24\u53cc\u7389\u96ea\u53ef\u7231\u7684\u5c0f\u81c2\u4e0a\uff0c\u4e5f\u675f\u7740\u4e24\u53cc\u91d1 \u73af\uff0c\u5c31\u5982\u300a\u897f\u6e38\u8bb0\u300b\u4e2d\u6240\u63cf\u7ed8\u7684\u7ea2\u5b69\u513f\u4e00\u822c\uff0c\u5728\u5927\u5802\u4e2d\u624b\u821e\u8db3\u8e48\uff0c\u5411\u8fd9\u4e2a\u7f57\u6c49\u4f38\u4e00\u62f3\uff0c\u5411\u90a3 \u4e2a\u7f57\u6c49\u8e22\u4e00\u817f\uff0c\u5ffd\u800c\u53e3\u4e2d\u8bb7\u8bb7\u6709\u8f9e\uff0c\u5ffd\u800c\u6602\u5934\u5411\u5929\u957f\u7b11\uff0c\u5f62\u72b6\u8be1\u79d8\uff01\u5510\u6653\u6f9c\u5927\u4e3a\u8bb6\u5f02\uff0c\u5982\u6b64 \u5e84\u4e25\u8083\u7a46\u7684\u5c11\u6797\u5bfa\u4e2d\uff0c\u600e\u4e48\u5ffd\u7136\u4f1a\u94bb\u51fa\u8fd9\u6837\u4e00\u4e2a\u5b69\u7ae5\uff0c\u800c\u4e14\u7f57\u6c49\u5802\u7684\u91cd\u8981\u53c8\u4ec5\u6b21\u4e8e\u5927\u96c4\u5b9d \u6bbf\uff0c\u4e3a\u4ec0\u4e48\u5c11\u6797\u5bfa\u7684\u5bfa\u50e7\uff0c\u53c8\u5bb9\u5f97\u4e00\u4e2a\u987d\u76ae\u7684\u5b69\u5b50\u5728\u6b64\u6492\u91ce\uff1f \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4ecd\u4e0d\u89c1\u6709\u5bfa\u4e2d\u50e7\u4eba\u51fa\u6765\u5e72\u6d89\uff0c\u5510\u6653\u6f9c\u4e00\u65f6\u597d\u5947\uff0c\u4f38\u624b\u62c9\u5f00\u95e8\u95e9\uff0c\u6b63\u5f85\u51fa\u53bb\uff0c \u5ffd\u7136\u773c\u524d\u4e00\u82b1\uff0c\u7f57\u6c49\u5802\u7684\u6a90\u9876\uff0c\u7a81\u7136\u843d\u4e0b\u4e00\u4eba\uff0c\u90a3\u5c0f\u5b69\u51b2\u7740\u4ed6\u4e00\u7b11\uff0c\u90a3\u4eba\u4e00\u6253\u624b\u5f0f\u3001\u5c0f\u5b69\u7a81 \u7136\u5728\u6000\u4e2d\u6478\u51fa\u4e00\u5305\u4e1c\u897f\uff0c\u54d7\u5730\u6253\u53bb\uff0c\u90a3\u4eba\u4f38\u624b\u4e00\u63a5\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u5374\u662f\u5929\u53f6\u6563\u4eba\u3002\u5510\u6653\u6f9c\u624b\u62c9 \u95e8\u95e9\uff0c\u6025\u5fd9\u95e9\u4e0a\u3002\u5929\u53f6\u6563\u4eba\u602a\u5578\u4e00\u58f0\uff0c\u90a3\u5c0f\u5b69\u5ffd\u7136\u558a\u9053\uff1a\u201c\u6709\u4eba\u6765\u554a\uff01\u201d\u5929\u53f6\u6563\u4eba\u9065\u51fb\u4e00 \u638c\uff0c\u7985\u623f\u623f\u95e8\u5982\u4e2d\u94c1\u9524\uff0c\u5982\u53d7\u5de8\u98ce\uff0c\u7a81\u88ab\u9707\u5f00\uff0c\u5510\u6653\u6f9c\u8dcc\u5728\u5730\u4e0a\uff01 \u3000\u3000\u5510\u6653\u6f9c\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u8dc3\u4e86\u8d77\u6765\uff0c\u53ea\u89c1\u7f57\u6c49\u5802\u56db\u9762\uff0c\u5df2\u8d6b\u7136\u7684\u7acb\u7740\u56db\u4e2a\u50e7\u4eba\uff0c\u5510\u6653\u6f9c\u53ea \u8ba4\u5f97\u4e00\u4e2a\u662f\u4e45\u5df1\u5165\u5bfa\u7684\u77e5\u5ba2\u50e7\u609f\u865a\u7985\u5e08\u3002\u609f\u865a\u7985\u5e08\u5f00\u58f0\u559d\u9053\uff1a\u201c\u5484\uff0c\u4f60\u662f\u90a3\u91cc\u6765\u7684\uff1f\u5c11\u6797\u5bfa \u4e2d\u7684\u7f57\u6c49\u5802\uff0c\u5c82\u5bb9\u5f97\u4f60\u968f\u610f\u4e71\u95ef\u4e48\uff1f\u201d\u5929\u53f6\u6563\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8bf7\u4f60\u4eec\u7684\u4e3b\u6301\u65e0\u4f4f\u5927\u5e08\u6253 \u8bdd\u3002\u201d\u56db\u4e2a\u548c\u5c1a\u540c\u58f0\u65a5\u9053\uff1a\u201c\u6211\u4eec\u7684\u4e3b\u6301\uff0c\u4e0d\u89c1\u4f60\u8fd9\u65e0\u540d\u4e4b\u8f88\u3002\u201d\u5929\u53f6\u6563\u4eba\u4e00\u9635\u72c2\u7b11\uff0c\u6717\u7136 \u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8fde\u6211\u90fd\u4e0d\u77e5\u9053\uff0c\u771f\u662f\u4e22\u5c3d\u5c11\u6797\u7684\u9762\uff01\u201d\u62d4\u8eab\u4fbf\u8d77\uff0c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u628a\u4e3b\u6301 \u53eb\u6765\uff0c\u96be\u9053\u6211\u4e0d\u4f1a\u81ea\u5df1\u53bb\u627e\u4e48\uff1f\u201d\u56db\u4e2a\u50e7\u4eba\u4e5f\u4e0d\u89c1\u600e\u6837\u817e\u632a\u4f5c\u52bf\uff0c\u5df2\u500f\u5730\u56db\u9762\u9f50\u4e0a\uff0c\u628a\u5929\u53f6 \u6563\u4eba\u56f4\u5728\u4e2d\u95f4\u3002\u5929\u53f6\u6563\u4eba\u53c8\u662f\u51b7\u7b11\u4e00\u58f0\uff0c\u51fa\u624b\u5982\u7535\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u4e24\u540d\u50e7\u4eba\u76f4\u63bc\u51fa\u53bb\uff0c\u53e6\u4e24\u540d \u50e7\u4eba\u4e5f\u8e09\u8e09\u8dc4\u8dc4\u9000\u4e86\u51e0\u6b65\u3002\u5929\u53f6\u6563\u4eba\u638c\u529b\u5389\u5bb3\u975e\u5e38\uff0c\u5e78\u5728\u8fd9\u56db\u540d\u50e7\u4eba\u90fd\u662f\u73b0\u4e0b\u5c11\u6797\u7b2c\u4e8c\u8f88\u4e2d \u7684\u9ad8\u624b\uff0c\u8981\u4e0d\u7136\u66f4\u53d7\u4e0d\u4f4f\u3002\u5929\u53f6\u6563\u4eba\u5f97\u610f\u6d0b\u6d0b\uff0c\u6b63\u5f85\u524d\u95ef\uff0c\u51b7\u4e0d\u9632\u5927\u5802\u4e1c\u9762\uff0c\u4eba\u5f71\u4e00\u6643\uff0c\u5929 \u53f6\u6563\u4eba\u6b63\u5f85\u56de\u8eab\uff0c\u80a9\u5934\u5df2\u88ab\u4eba\u8f7b\u8f7b\u62cd\u4e86\u4e00\u4e0b\uff0c\u4e00\u4e2a\u4f4e\u6c89\u7684\u58f0\u97f3\uff0c\u5728\u8033\u8fb9\u54cd\u9053\uff1a\u201c\u963f\u5f25\u9640 \u4f5b\uff01\u201d\u5929\u53f6\u6563\u4eba\u5413\u4e86\u4e00\u8df3\uff0c\u672a\u6562\u56de\u5934\uff0c\u5148\u884c\u8eb2\u907f\uff0c\u6025\u5fd9\u5411\u65c1\u6a2a\u8dc3\u4e09\u6b65\u3002 \u3000\u3000\u5929\u53f6\u6563\u4eba\u5de6\u638c\u62a4\u80f8\uff0c\u8db3\u5c16\u4e00\u65cb\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u4e00\u4e2a\u6148\u7709\u5584\u76ee\u7684\u548c\u5c1a\uff0c\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u963f \u5f25\u9640\u4f5b\uff0c\u5929\u53f6\u6563\u4eba\u4e0d\u8fdc\u4e07\u91cc\uff0c\u8fdc\u6765\u4e2d\u571f\uff0c\u53ef\u6709\u4ec0\u4e48\u4e8b\u8d50\u6559\u4e48\uff1f\u201d\u5929\u53f6\u6563\u4eba\u9053\uff1a\u201c\u8bf7\u95ee\u5927\u5e08\u6cd5 \u53f7\uff1f\u201d\u90a3\u8001\u548c\u5c1a\u7a3d\u9996\u7b54\u9053\uff1a\u201c\u8001\u8872\u6b63\u662f\u6563\u4eba\u6b32\u627e\u4e4b\u4eba\uff01\u201d\u5929\u53f6\u6563\u4eba\u9053\uff1a\u201c\u65e0\u4f4f\u7985\u5e08\u540d\u4e0d\u865a \u4f20\uff0c\u679c\u7136\u662f\u4e2a\u8c26\u51b2\u6709\u9002\u7684\u9ad8\u50e7\u3002\u53ea\u662f\u4f60\u95e8\u4e0b\u56db\u4f4d\u50e7\u4eba\uff0c\u5fd2\u662f\u65e0\u793c\u3002\u201d\u65e0\u4f4f\u7985\u5e08\u7b11\u9053\uff1a\u201c\u4ed6\u4eec \u56db\u4eba\u600e\u77e5\u662f\u6563\u4eba\u6765\u5230\uff0c\u4ed6\u4eec\u53ea\u5f53\u662f\u6c5f\u6e56\u4e0a\u72c2\u5f92\uff01\u5c31\u662f\u8001\u8872\uff0c\u82e5\u4e0d\u89c1\u6563\u4eba\u7684\u7075\u5c71\u638c\u6cd5\uff0c\u4e5f\u4e0d\u77e5 \u9053\u662f\u4f60\u9ed1\u591c\u5230\u6765\u3002\u6563\u4eba\u8bf7\u904f\u6012\u6c14\uff0c\u8d2b\u50e7\u5728\u6b64\u8d54\u7f6a\uff01\u201d\u8fd9\u756a\u8bdd\u4ea6\u8f6f\u4ea6\u786c\uff0c\u660e\u662f\u9053\u6b49\u966a\u7f6a\uff0c\u5b9e\u662f \u6697\u8d23\u65e0\u53f6\u6563\u4eba\u4e0d\u8be5\u8511\u89c6\u6b66\u6797\u89c4\u77e9\uff0c\u64c5\u95ef\u5c71\u95e8\uff0c\u5929\u53f6\u6563\u4eba\u7684\u7075\u5c71\u6d3e\u4e0e\u5c11\u6797\u6d3e\u867d\u7136\u8fdc\u9694\u4e07\u91cc\uff0c\u95e8 \u6237\u6beb\u4e0d\u76f8\u6d89\uff0c\u4f46\u5929\u53f6\u6563\u4eba\u7684\u5e08\u5085\u7075\u5c71\u4e0a\u4eba\u4e94\u5341\u5e74\u524d\u66fe\u5230\u5c11\u6797\u5bfa\u542c\u8fc7\u65e0\u4f4f\u7684\u5e08\u7236\u8bf4\u7ecf\uff0c\u5949\u4ee5 \u201c\u534a\u5e08\u201d\u4e4b\u793c\uff0c\u6240\u4ee5\u82e5\u8ba4\u771f\u6392\u5217\u8d77\u6765\uff0c\u5929\u53f6\u6563\u4eba\u6bd4\u65e0\u4f4f\u7985\u5e08\u5374\u77ee\u4e86\u534a\u8f88\u3002\u4e71\u95ef\u957f\u8f88\u95e8\u5ead\uff0c\u8bf4 \u8d77\u6765\u5148\u662f\u81ea\u5df1\u4e0d\u5bf9\uff0c\u5c24\u5176\u65e0\u4f4f\u7985\u5e08\u5982\u6b64\u8c26\u865a\uff0c\u5929\u53f6\u6563\u4eba\u5012\u4e0d\u7531\u5f97\u4e0d\u6536\u8d77\u9a84\u72c2\u4e4b\u6c14\uff0c\u5f53\u4e0b\u8fd8\u4e86 \u4e00\u793c\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u4ee4\u5e08\u4f84\u738b\u5c0a\u4e00\u9053\u5fb7\u6b66\u529f\uff0c\u6c5f\u6e56\u63a8\u91cd\u3002\u8d35\u76d1\u5bfa\u672c\u65e0\u5927\u5e08\u4e0d\u5bdf\uff0c\u52a0\u4ee5\u7f6a\u540d\uff0c \u4f46\u867d\u534a\u751f\u95f2\u6563\uff0c\u4e5f\u770b\u4e0d\u8fc7\u53bb\u3002\u4ee4\u5e08\u4f84\u660e\u65e5\u4fbf\u5230\u5d69\u5c71\u8bf7\u7f6a\uff0c\u4ffa\u4e0e\u51e0\u4f4d\u6b66\u6797\u524d\u8f88\uff0c\u4e5f\u613f\u5728\u5c11\u6797\u5bfa \u4e2d\u5f97\u4e00\u65c1\u542c\u4e4b\u5e2d\uff0c\u65ad\u6b64\u662f\u975e\u3002\u201d\u539f\u6765\u6309\u6b66\u6797\u89c4\u77e9\uff0c\u672c\u6d3e\u6e05\u7406\u95e8\u6237\uff0c\u5916\u6d3e\u4e0d\u5f97\u5e72\u9884\u3002\u4f46\u82e5\u4e8b\u51fa \u975e\u5e38\uff0c\u800c\u53d7\u6574\u8083\u7684\u95e8\u5f92\uff0c\u53c8\u516c\u7136\u4e0d\u670d\u8005\uff0c\u4e5f\u53ef\u8bf7\u522b\u6d3e\u5b97\u7956\uff0c\u53c2\u4e0e\u8bc4\u7406\uff0c\u53ea\u662f\u6b64\u79cd\u4e8b\u60c5\uff0c\u767e\u5e74 \u96be\u9047\u4e00\u6b21\uff0c\u4e8b\u7531\u82e5\u6709\u5982\u6b64\u4e0d\u670d\u672c\u6d3e\u957f\u8001\u7684\u95e8\u4eba\uff0c\u7eb5\u8bb8\u8bc4\u7406\u5f97\u76f4\uff0c\u611f\u60c5\u5df2\u4f24\uff0c\u975e\u8131\u79bb\u672c\u6d3e\u53e6\u7acb \u95e8\u6237\u4e0d\u53ef\u7684\u4e86\u3002 \u3000\u3000\u65e0\u4f4f\u7985\u5e08\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u4ecd\u7136\u5e73\u9759\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5c11\u6797\u5bfa\u5343\u767e\u6765\u5e74\u90fd\u4ee5\u6212\u5f8b\u81ea\u6301\uff0c\u7edd\u4e0d\u5305 \u5e87\u95e8\u5f92\uff0c\u4e5f\u7edd\u4e0d\u5984\u8d23\u95e8\u5f92\u3002\u4f46\u53e4\u8bed\u6709\u4e91\uff1a\u517c\u542c\u5219\u806a\uff0c\u504f\u542c\u5219\u853d\uff0c\u5929\u53f6\u6563\u4eba\u4e0e\u522b\u6d3e\u6b66\u6797\u5b97\u7956\u84e6 \u7136\u80af\u6765\uff0c\u5171\u65ad\u66f2\u76f4\uff0c\u90a3\u8d2b\u50e7\u662f\u6c42\u4e4b\u4e0d\u5f97\uff01\u201d\u5929\u53f6\u6563\u4eba\u9053\u58f0\u201c\u5f97\u7f6a\uff01\u201d\u8f6c\u8eab\u4fbf\u8d70\uff0c\u4fa7\u95e8\u4e00\u542f\uff0c \u5ffd\u7136\u95ef\u51fa\u4e00\u4eba\uff0c\u5408\u638c\u4e00\u63d6\uff0c\u53eb\u9053\uff1a\u201c\u5929\u53f6\u6563\u4eba\u8fdc\u6765\uff0c\u6055\u6211\u4eec\u4e0d\u9001\u4e86\uff01\u201d\u5929\u53f6\u6563\u4eba\u7a81\u89c9\u5de8\u98ce\u9707 \u64bc\uff0c\u5408\u638c\u56de\u63d6\uff0c\u7adf\u81ea\u62b5\u6321\u4e0d\u4f4f\uff0c\u8eab\u4e0d\u7531\u5df2\uff0c\u76f4\u9000\u51fa\u5802\u95e8\uff01\u5929\u53f6\u6563\u4eba\u7d20\u4ee5\u638c\u529b\u81ea\u9e23\uff0c\u4e0d\u6599\u5374\u654c \u8fd9\u4eba\u4e0d\u8fc7\uff0c\u5b9a\u795e\u770b\u65f6\uff0c\u539f\u6765\u5374\u662f\u672c\u65e0\u5927\u5e08\uff0c\u9053\u58f0\uff1a\u201c\u627f\u6559\uff01\u201d\u518d\u4e5f\u4e0d\u6562\u591a\u8bf4\uff0c\u75be\u5fd9\u4e0b\u5c71\u3002 \u3000\u3000\u90a3\u4e2a\u91d1\u73af\u675f\u53d1\u7684\u5b69\u5b50\uff0c\u5f53\u65e0\u4f4f\u7985\u5e08\u4e0e\u5929\u53f6\u6563\u4eba\u5bf9\u8bdd\u4e4b\u65f6\uff0c\u59cb\u7ec8\u5728\u65c1\u9759\u542c\u3002\u65e0\u4f4f\u7985\u5e08\u9001\u8d70 \u5929\u53f6\u6563\u4eba\u4e4b\u540e\uff0c\u8f7b\u629a\u4ed6\u7684\u5934\u53d1\uff0c\u7231\u601c\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u4f24\u53ca\u4f60\u5417\uff1f\u201d\u5c0f\u5b69\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d\u672c\u65e0\u5927 \u5e08\u9053\uff1a\u201c\u8c05\u90a3\u53ae\u4e5f\u4e0d\u6562\u3002\u201d\u5bf9\u5c0f\u5b69\u9053\uff0c\u201c\u597d\u4e86\uff0c\u4f60\u56de\u53bb\u6b47\u606f\u5427\uff0c\u4eca\u665a\u4e0d\u5fc5\u7ec3\u529f\u4e86\uff0c\u4f60\u7684\u5e08\u5085 \u7b49\u7740\u4f60\u5462\u3002\u201d\u5c0f\u5b69\u5e94\u4e86\u4e00\u58f0\uff0c\u8f6c\u5165\u540e\u5802\u3002\u5510\u6653\u6f9c\u672c\u60f3\u95ee\u90a3\u5c0f\u5b69\u6765\u5386\uff0c\u4f46\u81ea\u5df1\u65e2\u662f\u521d\u6765\uff0c\u8f88\u4efd \u53c8\u4f4e\uff0c\u5374\u662f\u4e0d\u4fbf\u64c5\u95ee\uff0c\u53ea\u597d\u7eb3\u7740\u4e00\u809a\u5b50\u95f7\uff0c\u81ea\u53bb\u7761\u89c9\u3002\u9065\u9065\u542c\u5f97\u5916\u9762\u672c\u65e0\u7985\u5e08\u58f0\u8c03\u9ad8\u6602\uff0c\u4f3c \u5728\u548c\u65e0\u4f4f\u7985\u5e08\u4e89\u8fa9\uff01 \u3000\u3000\u5929\u53f6\u6563\u4eba\u8d70\u540e\uff0c\u65e0\u4f4f\u7985\u5e08\u4e0e\u672c\u65e0\u5927\u5e08\u643a\u624b\u540c\u5165\u201c\u521d\u7956\u5eb5\u201d\u4e2d\u3002\u8fd9\u521d\u7956\u5eb5\u4e43\u7eaa\u5ff5\u8fbe\u6469\u7985\u5e08 \u7684\u5efa\u7b51\uff0c\uff08\u76f8\u4f20\u8fbe\u6469\u7985\u5e08\uff0c\u5728\u5357\u671d\u6881\u6b66\u5e1d\u65f6\uff0c\u81ea\u5370\u6765\u534e\uff0c\u4e00\u82c7\u6e21\u6c5f\uff0c\u5728\u6cb3\u5357\u5d69\u5c71\u5c11\u6797\u9762\u58c1\u5341 \u5e74\uff0c\u521b\u7acb\u7985\u5b97\uff0c\u662f\u4e3a\u201c\u521d\u7956\u201d\uff09\u5c11\u6797\u5bfa\u6709\u91cd\u5927\u4e8b\u60c5\u65f6\uff0c\u9996\u8111\u4eba\u7269\uff0c\u624d\u5165\u201c\u521d\u7956\u5eb5\u201d\u4e2d\u76f8\u5546\u3002 \u65e0\u4f4f\u7985\u5e08\u5750\u5b9a\u4e4b\u540e\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5e08\u5144\u59dc\u6842\u4e4b\u6027\uff0c\u706b\u6c14\u81f3\u4eca\u672a\u655b\uff0c\u4eca\u665a\u4f55\u82e6\u4e0e\u6765\u4eba\u4e3a\u96be\uff1f\u201d \u672c\u65e0\u5927\u5e08\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u60f3\u6210\u4f5b\uff0c\u90a3\u5b66\u5f97\u5e08\u5f1f\u4f60\u7684\u6db5\u517b\u529f\u592b\u3002\u5929\u53f6\u6563\u4eba\u660e\u77e5\u6211\u5bfa\u662f\u6b66\u6797\u7684\u6cf0 \u5c71\u5317\u6597\uff0c\u5c45\u7136\u64c5\u95ef\u4f5b\u95e8\uff0c\u4e0d\u7ed9\u4ed6\u70b9\u5389\u5bb3\uff0c\u4ed6\u8fd8\u4ee5\u4e3a\u6211\u5c11\u6797\u5bfa\u50e7\u662f\u53ef\u6b3a\u4e4b\u8f88\u3002\u201d\u65e0\u4f4f\u7985\u5e08\u9053\uff1a \u201c\u4ed6\u65e2\u6309\u6b66\u6797\u89c4\u77e9\uff0c\u8981\u66ff\u738b\u5c0a\u4e00\u6491\u8170\uff0c\u660e\u65e5\u4fbf\u662f\u65e5\u671f\uff0c\u4e8b\u51fa\u975e\u5e38\u3002\u4ed6\u65e9\u4e00\u665a\u901a\u77e5\uff0c\u867d\u7136\u4e0d\u591f \u793c\u8c8c\uff0c\u4e5f\u4e0d\u5fc5\u602a\u8d23\u4ed6\u4e86\uff01\u201d\u672c\u65e0\u5927\u5e08\u9053\uff1a\u201c\u6211\u5728\u9752\u5c9b\u53bb\u6349\u738b\u5c0a\u4e00\u65f6\uff0c\u5df2\u77e5\u6709\u8bb8\u591a\u6b66\u6797\u9ad8\u624b\uff0c \u4e0e\u4ed6\u52a9\u7ea3\u4e3a\u8650\uff0c\u53ea\u6599\u4e0d\u5230\u5929\u53f6\u6563\u4eba\u4e5f\u5728\u5176\u5185\u3002\u738b\u5c0a\u4e00\u65e2\u7136\u4e0d\u670d\u7ba1\u675f\uff0c\u4e14\u53c8\u6deb\u66b4\u4e0b\u6d41\uff0c\u660e\u65e5\u4f1a \u540e\uff0c\u5e9f\u4e86\u4ed6\u5427\uff01\u201d \u3000\u3000\u65e0\u4f4f\u7985\u5e08\u4f4e\u7709\u9601\u76ee\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u624d\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff0c\u8fd9\u4e8b\u592a\u4e0d\u5bfb\u5e38\uff01\u201d\u672c\u65e0\u7985\u5e08\u53f9 \u9053\uff1a\u201c\u6599\u4e0d\u5230\u6211\u4eec\u5927\u5e08\u5144\u6700\u5ba0\u7231\u7684\u5f92\u5f1f\uff0c\u4eca\u65e5\u53d8\u6210\u8fd9\u6837\uff01\u201d\u672c\u65e0\u7684\u5e08\u5144\u672c\u7a7a\u4e43\u662f\u524d\u4efb\u5c11\u6797\u4e3b \u6301\uff0c\u5728\u6b66\u6797\u4e2d\u5a01\u671b\uff0c\u8fdc\u5728\u4e24\u4e2a\u5e08\u5f1f\u4e4b\u4e0a\uff0c\u6240\u4ee5\u6c5f\u6e56\u4e2d\u4eba\u63d0\u5230\u5c11\u6797\uff0c\u5341\u4e5d\u53ea\u77e5\u672c\u7a7a\uff0c\u4e0d\u77e5\u65e0\u4f4f \u672c\u65e0\uff0c\u5f53\u5e74\u738b\u5c0a\u4e00\u4e43\u662f\u4e86\u56e0\u4fdd\u8350\u6765\u7684\uff0c\u90a3\u65f6\u672c\u65e0\u5c31\u7591\u738b\u5c0a\u4e00\u6765\u5386\u4e0d\u6e05\uff0c\u529d\u5e08\u5144\u4e0d\u8981\u6536\u4ed6\u3002\u4f46 \u672c\u7a7a\u89c1\u738b\u5c0a\u4e00\u76f8\u8c8c\u975e\u51e1\uff0c\u806a\u6167\u5f02\u5e38\uff0c\u975e\u4f46\u4e0d\u542c\u5e08\u5f1f\u4e4b\u529d\uff0c\u800c\u4e14\u628a\u4e00\u8eab\u672c\u9886\uff0c\u90fd\u6388\u4e86\u7ed9\u4ed6\u3002 \u3000\u3000\u65e0\u4f4f\u9053\uff1a\u201c\u6211\u8bf4\u8fd9\u4e8b\u592a\u4e0d\u5bfb\u5e38\uff0c\u4e0d\u53ea\u662f\u56e0\u4e3a\u738b\u5c0a\u4e00\u4e43\u662f\u6211\u4eec\u5e08\u5144\u7684\u7231\u5f92\uff0c\u800c\u662f\u4e3a\u4ec0\u4e48\u6709 \u90a3\u4e48\u591a\u6b66\u6797\u9ad8\u624b\u66ff\u4ed6\u6491\u8170\uff1f\u201d\u672c\u65e0\u7985\u5e08\u9ed8\u7136\u4e0d\u8bed\uff0c\u65e0\u4f4f\u7ee7\u9053\uff1a\u201c\u4f60\u60f3\u7eb5\u4f7f\u738b\u5c0a\u4e00\u662f\u540e\u8d77\u4e4b \u79c0\uff0c\u4ed6\u51fa\u5e08\u624d\u6709\u51e0\u5e74\uff1f\u6709\u4f55\u5fb7\u4f55\u80fd\uff0c\u5c45\u7136\u4ee4\u4e00\u6d3e\u5b97\u7956\u7684\u5929\u53f6\u6563\u4eba\uff0c\u4e5f\u5982\u6b64\u503e\u5012\u3002\u8fd8\u6709\u90a3\u51f6\u50e7 \u4e86\u56e0\uff0c\u81ea\u6043\u662f\u6c5f\u5357\u516b\u4fa0\u4e4b\u9996\uff0c\u628a\u8c01\u90fd\u4e0d\u653e\u5728\u773c\u91cc\uff0c\u53c8\u600e\u7684\u7adf\u4f3c\u7ed9\u4ed6\u505a\u4e86\u4fdd\u9556\uff1f\u201d\u672c\u65e0\u7985\u5e08\u6c89 \u541f\u6709\u9877\uff0c\u4e00\u62f3\u51fb\u5728\u638c\u4e0a\uff0c\u62d6\u957f\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u2026\u2026\u201d\u65e0\u4f4f\u7985\u5e08\u9762\u8272\u60e8\u767d\uff0c\u622a\u4f4f\u672c\u65e0\u7684\u8bdd \u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u8981\u968f\u4fbf\u731c\u6d4b\uff0c\u9a91\u7740\u9a74\u513f\u770b\u5531\u672c\uff0c\u8d70\u7740\u77a7\u5427\uff01\u53ea\u662f\u6211\u6709\u4e00\u8a00\u5949\u529d\uff0c\u5e08\u5144\u4f60\u6027\u5b50 \u8f83\u66b4\uff0c\u660e\u65e5\u4e4b\u4e8b\uff0c\u8bf4\u4e0d\u5b9a\u6709\u5173\u5c11\u6797\u52ab\u6570\uff0c\u4f60\u8981\u5fcd\u7740\u4e9b\u513f\u3002\u201d\u672c\u65e0\u7985\u5e08\u6124\u7136\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60 \u662f\u4e00\u5bfa\u7684\u4e3b\u6301\uff0c\u4f60\u8bf4\u4ec0\u4e48\u5c31\u7b97\u4ec0\u4e48\uff0c\u611a\u5144\u542c\u4f60\u5429\u5490\u4fbf\u662f\u3002\u201d\u65e0\u4f4f\u7985\u5e08\u4e00\u7b11\u8d77\u7acb\uff0c\u8bf4\u9053\uff1a\u201c\u54b1 \u4eec\u5144\u5f1f\uff0c\u8bf4\u8fd9\u4e2a\u5e72\u561b\uff1f\u5e08\u5144\uff0c\u4f60\u522b\u591a\u5fc3\u3002\u201d\u4e24\u5e08\u5144\u5f1f\u643a\u624b\u8d70\u51fa\u5eb5\u5802\uff0c\u770b\u4e09\u5341\u516d\u6bbf\u6d78\u5728\u6eb6\u6eb6\u6708 \u8272\u4e4b\u4e2d\uff0c\u65e0\u4f4f\u7985\u5e08\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6c42\u4f5b\u7956\u6148\u60b2\u4fdd\u4f51\uff0c\u8fd9\u5927\u597d\u57fa\u4e1a\uff0c\u4e0d\u8981\u6bc1\u5728\u6211\u7684\u624b \u91cc\uff01\u201d \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u5c11\u6797\u5bfa\u7684\u5927\u96c4\u5b9d\u6bbf\uff0c\u9999\u70db\u64a9\u7ed5\uff0c\u8fbe\u6469\u7956\u5e08\u7684\u4f5b\u50cf\u6446\u5728\u6b63\u4e2d\uff0c\u7b49\u5f85\u4e3e\u884c\u4e00\u4e2a \u4e0d\u5e73\u5e38\u7684\u4eea\u5f0f\u3002 \u3000\u3000\u5510\u6653\u6f9c\u4ee5\u89c1\u8bc1\u4eba\u7684\u8eab\u4efd\uff0c\u4e5f\u88ab\u9080\u8bf7\u53c2\u89c2\uff0c\u53ea\u662f\u4ed6\u4e43\u665a\u8f88\uff0c\u4e0d\u4f3c\u6613\u5170\u73e0\u767d\u6cf0\u5b98\u7b49\u7531\u4e0a\u9662\u9ad8 \u50e7\u63a5\u5f85\uff0c\u5217\u5e2d\u7684\u5e2d\u4f4d\u4e5f\u6709\u4e0d\u540c\u3002\u62db\u5f85\u4ed6\u7684\u4ecd\u662f\u6628\u65e5\u63a5\u5f15\u4ed6\u7684\u77e5\u5ba2\u50e7\u609f\u865a\u3002\u5510\u6653\u6f9c\u4e00\u65e9\u8d77\u6765\u609f \u865a\u5c31\u8bf7\u4ed6\u6c90\u6d74\u51c0\u8eab\u7b49\u5019\uff0c\u5348\u724c\u65f6\u5206\uff0c\u609f\u865a\u5e26\u4ed6\u8d70\u51fa\u7985\u623f\uff0c\u53ea\u89c1\u503c\u6bbf\u50e7\u4eba\uff0c\u4e24\u6392\u6392\u5217\uff0c\u5168\u662f\u9c9c \u660e\u7684\u50e7\u8863\uff0c\u8fdb\u5230\u5927\u96c4\u5b9d\u6bbf\uff0c\u4e94\u767e\u50e7\u4eba\uff0c\u65e9\u5df2\u6309\u7167\u539f\u5b9a\u7684\u4f4d\u7f6e\u7ad9\u597d\uff0c\u5927\u96c4\u6bbf\u5185\u8083\u7a46\u5f02\u5e38\uff0c\u51e0\u4e4e \u8fde\u547c\u5438\u58f0\u97f3\u90fd\u53ef\u542c\u89c1\u3002\u6bbf\u53f0\u4e0a\u7684\u4e24\u884c\u53f8\u793c\u50e7\u4eba\uff0c\u624b\u6572\u4e91\u677f\u7b1b\u97f5\u60a0\u626c\uff0c\u4e00\u58f0\u58f0\u4f20\u4e86\u4e0b\u53bb\u3002\u5510\u6653 \u6f9c\u5750\u5728\u897f\u4fa7\u7684\u7b2c\u5341\u4e09\u5bbe\u5e2d\uff0c\u8fc7\u4e86\u4e00\u76cf\u8336\u65f6\u523b\uff0c\u7a81\u7136\u949f\u9e23\u9f13\u54cd\uff0c\u6708\u95e8\u5f00\u5904\uff0c\u6a80\u9999\u8885\u8885\u5982\u96fe\uff0c\u5728 \u9999\u70df\u64a9\u7ed5\u4e2d\u5e76\u6392\u8d70\u8fdb\u4e09\u4eba\uff0c\u6b63\u662f\u4e3b\u6301\u65e0\u4f4f\u7985\u5e08\u3001\u76d1\u5bfa\u672c\u65e0\u7985\u5e08\uff0c\u548c\u5c11\u6797\u5bfa\u7684\u8d35\u5bbe\u6613\u5170\u73e0\uff0c\u767d \u6cf0\u5b98\u4e0e\u5415\u56db\u5a18\u968f\u540e\uff0c\u65e0\u4f4f\u7985\u5e08\u8d70\u5230\u8fbe\u6469\u4f5b\u50cf\u4e4b\u4e0b\uff0c\u8dc3\u5750\u5728\u6848\u524d\u7684\u4e00\u4e2a\u9ec4\u5e03\u62dc\u57ab\u4e4b\u4e0a\uff0c\u672c\u65e0\u5927 \u5e08\u8dc3\u5750\u53f3\u4fa7\u84b2\u56e2\uff0c\u6613\u5170\u73e0\u3001\u767d\u6cf0\u5b98\u3001\u5415\u56db\u5a18\u4e09\u4eba\u5750\u5728\u53f3\u9996\u5bbe\u5e2d\u3002\u8fbe\u6469\u5802\u3001\u7f57\u6c49\u5802\u3001\u638c\u7ecf\u5802\u7684 \u4e0a\u4e09\u5802\u9ad8\u50e7\uff0c\u5168\u90e8\u7a7f\u7740\u8888\u88df\uff0c\u606d\u8eab\u5408\u4ec0\uff0c\u5411\u638c\u6559\u65b9\u4e08\u3001\u5c11\u6797\u5bfa\u7684\u4e3b\u6301\u65e0\u4f4f\u7985\u5e08\u65bd\u793c\uff0c\u65e0\u4f4f\u7985 \u5e08\u9762\u5bb9\u8083\u7a46\uff0c\u5411\u8fbe\u6469\u7956\u5e08\u4f5b\u50cf\u884c\u793c\u4e4b\u540e\uff0c\u5f00\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5d69\u5c71\u5c11\u6797\u5bfa\u5efa\u5bfa\u4e00\u5343\u4e09\u767e\u4f59\u5e74\uff0c\u6212 \u5f8b\u7cbe\u4e25\uff0c\u540d\u95fb\u6d77\u5185\uff0c\u8001\u8872\u4e0d\u5fb7\uff0c\u65b0\u4efb\u4e3b\u6301\uff0c\u4e0d\u610f\u6709\u672c\u5bfa\u95e8\u4eba\u738b\u5c0a\u4e00\uff0c\u7f54\u987e\u6e05\u89c4\uff0c\u6709\u8fdd\u5927\u6212\uff0c \u4e3a\u76d1\u5bfa\u672c\u65e0\u5927\u5e08\u53d1\u73b0\uff0c\u7adf\u5728\u9752\u5c9b\u5e72\u4e0b\u91c7\u82b1\u6076\u884c\uff0c\u672c\u5e94\u7acb\u5373\u7f09\u62ff\u5f52\u5c71\uff0c\u4f46\u738b\u5c0a\u4e00\u58f0\u79f0\u4e0d\u670d\uff0c\u8981 \u6c42\u4e0e\u76d1\u5bfa\u5bf9\u8d28\uff0c\u5e76\u9080\u6709\u522b\u6d3e\u957f\u8001\uff0c\u5171\u540c\u8bc4\u7406\u3002\u6b64\u4e8b\u5173\u7cfb\u5c11\u6797\u8363\u8fb1\uff0c\u7b49\u4e0b\u660e\u65ad\u66f2\u76f4\uff1b\u960e\u5bfa\u50e7 \u4eba\uff0c\u4ff1\u5f53\u5f15\u4e3a\u9274\u6212\u3002\u201d\u6709\u8bb8\u591a\u50e7\u4fa3\uff0c\u672a\u77e5\u6b64\u4e8b\uff0c\u4e00\u65f6\u55b7\u5f02\u4e4b\u58f0\u5927\u4f5c\uff0c\u5c11\u6797\u5bfa\u4e2d\u7adf\u7136\u51fa\u4e86\u91c7\u82b1 \u5927\u8d3c\uff0c\u8fd9\u771f\u662f\u4ece\u6240\u672a\u6709\u4e4b\u4e8b\u3002 \u3000\u3000\u672c\u65e0\u5927\u5e08\u7a3d\u9996\u8bf4\u9053\uff1a\u201c\u638c\u9662\u65b9\u4e08\uff0c\u82e5\u7136\u65ad\u4e86\u66f2\u76f4\u4e4b\u540e\uff0c\u738b\u5c0a\u4e00\u679c\u7136\u6709\u7f6a\uff0c\u4f46\u522b\u6d3e\u957f\u8001\uff0c \u51fa\u5934\u5e87\u62a4\uff0c\u90a3\u53c8\u5982\u4f55\uff1f\u201d\u65e0\u4f4f\u7985\u5e08\u9053\uff1a\u201c\u82e5\u6709\u6b64\u4e8b\uff0c\u53ea\u6709\u7f54\u5bfa\u4e00\u81f4\uff0c\u529d\u670d\u5916\u5bbe\uff0c\u60e9\u6cbb\u53db \u5f92\u3002\u201d\u672c\u65e0\u5927\u5e08\u53c8\u9053\uff1a\u201c\u82e5\u7136\u5916\u5bbe\u4e0d\u670d\uff0c\u90a3\u53c8\u5982\u4f55\uff1f\u201d\u65e0\u4f4f\u7985\u5e08\u542b\u55d4\u8bf4\u9053\uff1a\u201c\u5fc5\u65e0\u662f\u7406\uff01\u201d \u638c\u7ecf\u5802\u7684\u5f18\u6cd5\u5927\u5e08\u9053\uff1a\u201c\u91c7\u82b1\u6deb\u884c\uff0c\u7f6a\u5728\u4e0d\u6055\uff0c\u82e5\u771f\u6709\u5916\u5bbe\u6043\u5f3a\u5e87\u62a4\uff0c\u90a3\u5168\u5bfa\u50e7\u4eba\uff0c\u5747\u6709\u62a4 \u6cd5\u4e4b\u8d23\uff01\u5373\u4f7f\u53db\u5f92\u8981\u522b\u6295\u95e8\u6237\uff0c\u4e5f\u4e0d\u53ef\u4ee5\uff01\u201d\u638c\u7ecf\u5802\u4e3b\u6301\u662f\u6267\u638c\u5386\u4ee3\u7684\u4f20\u7ecf\u6212\u5f8b\u7684\uff0c\u672c\u65e0\u5927 \u5e08\u5c31\u662f\u8981\u903c\u4ed6\u8bf4\u51fa\u8fd9\u6837\u7684\u8bdd\uff0c\u65e0\u4f4f\u7985\u5e08\u76b1\u7709\u4e0d\u8bed\uff0c\u9762\u6709\u91cd\u5fe7\u3002 \u3000\u3000\u65f6\u4ea4\u6b63\u5348\uff0c\u5510\u6653\u6f9c\u5fc3\u60f3\uff1a\u738b\u5c0a\u4e00\u600e\u7684\u8fd8\u4e0d\u89c1\u6765\uff0c\u83ab\u975e\u4ed6\u754f\u7f6a\u4e0d\u6562\u6765\u4e48\uff1f\u5ffd\u95fb\u5bfa\u95e8\u5916\u6e05\u78d0 \u4e4b\u58f0\uff0c\u5bfa\u95e8\u5f00\u5904\uff0c\u738b\u5c0a\u4e00\u6d0b\u6d0b\u81ea\u5f97\uff0c\u5728\u4e00\u5927\u5806\u4eba\u7c07\u62e5\u4e4b\u4e0b\uff0c\u8d70\u8fdb\u4e86\u5927\u96c4\u5b9d\u6bbf\u3002\u8fd9\u4e00\u5806\u4eba\u662f\uff1a \u4e86\u56e0\u548c\u5c1a\u3001\u5929\u53f6\u6563\u4eba\u3001\u54c8\u5e03\u9640\u4e0e\u795e\u9b54\u53cc\u8001\uff0c\u53e6\u5916\u8fd8\u6709\u4e24\u4eba\uff0c\u5510\u6653\u6f9c\u5374\u4e0d\u8ba4\u5f97\uff0c\u6084\u6084\u7ec6\u95ee\u609f \u865a\uff0c\u624d\u77e5\u8fd9\u4e24\u4eba\u4e00\u662f\u897f\u5317\u7684\u8457\u540d\u5de8\u5bc7\u7518\u5929\u9f99\uff0c\u4e00\u662f\u5f62\u610f\u62f3\u7684\u5143\u8001\u8463\u5de8\u5ddd\uff0c\u90fd\u662f\u6c5f\u6e56\u4e0a\u7684\u6210\u540d \u4eba\u7269\u3002 \u3000\u3000\u738b\u5c0a\u4e00\u8d70\u8fdb\u5927\u6bbf\uff0c\u5411\u65e0\u4f4f\u7985\u5e08\u884c\u4e86\u4e00\u793c\u3002\u65e0\u4f4f\u7985\u5e08\u9053\uff1a\u201c\u4f60\u613f\u5750\u5f85\u7f6a\u5e2d\u4e0a\uff0c\u8fd8\u662f\u613f\u5750\u5728 \u7533\u8fa9\u5e2d\u4e0a\uff0c\u7531\u4f60\u6289\u62e9\uff01\u201d\u539f\u6765\u6309\u6b66\u6797\u89c4\u77e9\uff0c\u53d1\u751f\u4e86\u8fd9\u6837\u4e0d\u5bfb\u5e38\u4e4b\u4e8b\uff0c\u88ab\u6574\u8083\u7684\u95e8\u5f92\uff0c\u82e5\u5750\u5728 \u5f85\u7f6a\u5e2d\u4e0a\uff0c\u5373\u8868\u660e\u81ea\u5df1\u59cb\u7ec8\u613f\u7688\u4f9d\u672c\u6d3e\uff0c\u4ee5\u5f85\u7f6a\u4e4b\u8eab\uff0c\u6c42\u540c\u95e8\u8c05\u89e3\u3002\u82e5\u5750\u5728\u7533\u8fa9\u5e2d\u4e0a\uff0c\u5373\u662f \u4ee5\u4e24\u9053\u4e4b\u4e00\u81ea\u5c45\uff0c\u4e0e\u672c\u6d3e\u4e3b\u6301\u7ad9\u5728\u5e73\u7b49\u7684\u5730\u4f4d\u8fa9\u8bba\u3002\u5982\u6b64\u5219\u4e0d\u7ba1\u7ed3\u679c\u5982\u4f55\uff0c\u90fd\u8981\u8131\u79bb\u672c\u6d3e\u7684 \u4e86\u3002\u738b\u5c0a\u4e00\u4e3e\u5934\u4e00\u671b\uff0c\u90a3\u7533\u8fa9\u5e2d\u4e0e\u5916\u5bbe\u5e2d\u7d27\u7d27\u76f8\u8fde\uff0c\u518d\u4e0d\u601d\u5ea6\uff0c\u8ff3\u81ea\u5230\u7533\u8fa9\u5e2d\u5750\u4e0b\u3002\u4e86\u56e0\u3001 \u5929\u53f6\u7b49\u4eba\u4e5f\u4f9d\u6b21\u7684\u5750\u5230\u5de6\u4fa7\u5916\u5bbe\u5e2d\u4e0a\u3002 \u3000\u3000\u4f17\u4eba\u5750\u597d\u4e4b\u540e\uff0c\u65e0\u4f4f\u7985\u5e08\u5c06\u5c11\u6797\u6212\u5f8b\u8bf4\u4e86\u4e00\u904d\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u76d1\u5bfa\u7684\u672c\u65e0\u5e08\u5144\uff0c\u4f60\u662f\u539f \u544a\uff0c\u8bf7\u4f60\u5c06\u738b\u5c0a\u4e00\u72af\u6212\u4e4b\u4e8b\uff0c\u5bf9\u7f54\u9662\u50e7\u4eba\u4e00\u8bf4\u3002\u201d\u672c\u65e0\u5927\u5e08\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u6211\u4e0a\u6708\u5949\u65b9\u4e08\u4e4b \u547d\uff0c\u5230\u5c71\u4e1c\u5404\u5730\u8003\u5bdf\u5c11\u6797\u95e8\u5f92\uff0c\u5728\u9752\u5c9b\u9017\u7559\u65f6\u95f4\uff0c\u6070\u503c\u5f53\u5730\u53d1\u751f\u91c7\u82b1\u6848\u4ef6\uff0c\u5148\u540e\u6709\u5341\u4e8c\u540d\u5c11 \u5973\uff0c\u88ab\u91c7\u82b1\u8d3c\u52ab\u593a\u65e0\u8e2a\uff0c\u7ecf\u6211\u7ec6\u5fc3\u4fa6\u5bdf\uff0c\u8bc1\u5b9e\u662f\u738b\u5c0a\u4e00\u6240\u4e3a\uff01\u201d\u738b\u5c0a\u4e00\u51b7\u7b11\u9053\uff1a\u201c\u82e5\u4f60\u89c1\u6211 \u91c7\u82b1\uff0c\u4f55\u4e0d\u5f53\u573a\u5c06\u6211\u64d2\u4e0b\u3002\u201d\u672c\u65e0\u5927\u5e08\u77aa\u773c\u8bf4\u9053\uff1a\u201c\u4f60\u5e76\u975e\u4eb2\u81ea\u91c7\u82b1\uff0c\u4f46\u5374\u662f\u4e3b\u4f7f\u3002\u201d\u987f\u4e86 \u4e00\u987f\uff0c\u9762\u5bf9\u5bfa\u50e7\u8bf4\u9053\uff1a\u201c\u738b\u5c0a\u4e00\u975e\u552f\u72af\u4e0b\u91c7\u82b1\u5927\u7f6a\uff0c\u800c\u4e14\u4f9d\u9644\u6ee1\u5b98\u3002\u4ed6\u4f4f\u5728\u94a6\u5dee\u884c\u7f72\u4e4b\u5185\uff0c \u8fde\u665a\u6d3e\u4eba\u52ab\u593a\u5c11\u5973\uff0c\u4fbf\u662f\u4ee5\u94a6\u5dee\u884c\u7f72\uff0c\u4f5c\u4e3a\u91c7\u82b1\u7684\u5de2\u7a74\uff01\u201d\u738b\u5c0a\u4e00\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u4f4f\u5728\u94a6\u5dee\u884c \u7f72\uff0c\u4e5f\u6709\u7f6a\u4e48\uff1f\u201d\u638c\u7ecf\u5802\u7684\u5f18\u6cd5\u5927\u5e08\u8d77\u7acb\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5c11\u6797\u5386\u4ee3\u6240\u4f20\uff0c\u53ea\u5b88\u6e05\u89c4\uff0c\u4e0d\u7406\u671d \u653f\u3002\u4f4f\u5728\u94a6\u5dee\u884c\u7f72\uff0c\u4e0d\u7b97\u662f\u7f6a\u3002\u4f46\u52ab\u593a\u5c11\u5973\uff0c\u91c7\u82b1\u884c\u6deb\uff0c\u5374\u662f\u5927\u7f6a\uff0c\u8bf7\u4e24\u4f4d\u4e0d\u8981\u8282\u5916\u751f\u679d\uff0c \u53ea\u8bf4\u5230\u5e95\u738b\u5c0a\u4e00\u6709\u6ca1\u6709\u4e3b\u4f7f\u515a\u7fbd\u66ff\u4ed6\u52ab\u593a\u5c11\u5973\u4e4b\u4e8b\u597d\u4e86\u3002\u201d\u5f18\u6cd5\u4e00\u8bf4\uff0c\u672c\u65e0\u4e5f\u6709\u70b9\u5c34\u5c2c\u3002\u539f \u6765\u5c11\u6797\u5386\u4ee3\u76f8\u4f20\uff0c\u867d\u7136\u662f\u4e0d\u7406\u671d\u653f\u3002\u4f46\u660e\u4ea1\u4e4b\u540e\uff0c\u5f02\u65cf\u5165\u4fb5\uff0c\u5bfa\u50e7\u90fd\u4ee5\u4e0d\u4f9d\u9644\u6ee1\u5974\uff0c\u4e0e\u5e94\u5e2e \u52a9\u524d\u660e\u5fd7\u58eb\u4f5c\u4e3a\u4e0d\u6210\u6587\u6cd5\uff0c\u672c\u65e0\u5927\u5e08\u5e76\u66fe\u63d0\u8fc7\u8981\u5c06\u53cd\u6e05\u590d\u660e\uff0c\u5217\u4e3a\u660e\u6587\uff0c\u4f46\u65e0\u4f4f\u7985\u5e08\u4ee5\u5173\u7cfb \u592a\u5927\uff0c\u575a\u4e0d\u7b54\u5e94\u3002\u5728\u672c\u65e0\u5927\u5e08\u5fc3\u4e2d\uff0c\u4f9d\u9644\u6ee1\u5b98\u6bd4\u91c7\u82b1\u66f4\u4e0d\u53ef\u6055\uff0c\u6240\u4ee5\u4e00\u65f6\u51b2\u53e3\u800c\u51fa\uff0c\u8d23\u5907\u53db \u5f92\uff0c\u4e00\u65f6\u60f3\u4e0d\u8d77\u7956\u5e08\u6240\u4f20\u5bb6\u6cd5\u7adf\u6ca1\u8fd9\u6761\u3002 \u3000\u3000\u738b\u5c0a\u4e00\u8fa9\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u4e3b\u4f7f\u91c7\u82b1\uff0c\u6709\u4f55\u8bc1\u636e\uff1f\u201d\u6613\u5170\u73e0\u500f\u5730\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u6211\u6765\u4f5c\u8bc1\uff01\u201d \u6307\u4e86\u6307\u54c8\u5e03\u9640\u548c\u7518\u5929\u9f99\u9053\uff1a\u201c\u6211\u66fe\u773c\u89c1\u6b64\u4eba\u52ab\u63a0\u5c11\u5973\uff0c\u732e\u7ed9\u738b\u5c0a\u4e00\u53d7\u7528\u3002\u201d\u5f53\u4e0b\u5c06\u5f53\u665a\u60c5\u5f62 \u7ec6\u8bf4\u4e00\u904d\u3002\u5168\u9662\u50e7\u4eba\uff0c\u65e0\u4e0d\u9a87\u5f02\uff01 \u3000\u3000\u65e0\u4f4f\u7985\u5e08\u4e91\u677f\u4e00\u6572\uff0c\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u6613\u5973\u4fa0\u4e43\u662f\u5f53\u4eca\u7855\u679c\u4ec5\u5b58\u7684\u5929\u5c71\u5251\u4f20\u4eba\uff0c\u5728\u6b66\u6797 \u4e2d\u8f88\u4efd\u6700\u5c0a\uff0c\u5979\u7edd\u4e0d\u4f1a\u8bec\u8d56\u5c0f\u8f88\uff0c\u738b\u5c0a\u4e00\u4f60\u8fd8\u6709\u4f55\u8bdd\u53ef\u8bf4\uff1f\u201d \u3000\u3000\u738b\u5c0a\u4e00\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u521a\u624d\u638c\u7ecf\u5802\u7684\u5f18\u6cd5\u5927\u5e08\u8bf4\uff0c\u672c\u5bfa\u4ece\u6765\u4e0d\u7406\u671d\u5ef7\u4e4b\u4e8b\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d \u65e0\u4f4f\u9053\uff1a\u201c\u91c7\u82b1\u4e4b\u4e8b\u4e0e\u671d\u5ef7\u4f55\u5e72\uff1f\u6211\u518d\u95ee\u4f60\uff0c\u6613\u5973\u4fa0\u6240\u8bf4\uff0c\u662f\u5047\u662f\u771f\uff1f\u201d\u738b\u5c0a\u4e00\u6602\u7136\u8bf4\u9053\uff1a \u201c\u662f\u771f\uff01\u201d\u987f\u65f6\u5168\u5bfa\u5927\u54d7\uff0c\u5f18\u6cd5\u5927\u5e08\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u4f9d\u7956\u5e08\u6240\u7acb\u6212\u6761\uff0c\u8eab\u72af\u91c7\u82b1\u4e4b\u7f6a\u8005\uff0c\u5e94\u53d7 \u706b\u711a\u4e4b\u5211\uff01\u201d \u3000\u3000\u65e0\u4f4f\u7985\u5e08\u5411\u8fbe\u6469\u4f5b\u50cf\u53e9\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u7f13\u7f13\u8d77\u7acb\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u5c11\u6797\u4e0d\u8096\u5f92\u738b\u5c0a\u4e00\uff0c\u6307\u4f7f \u91c7\u82b1\uff0c\u52ab\u593a\u5c11\u5973\uff0c\u7f6a\u8bc1\u786e\u51ff\u3002\u7ecf\u638c\u7ecf\u5802\u65b9\u4e08\uff0c\u67e5\u660e\u6212\u6761\uff0c\u7f6a\u8be5\u5904\u6b7b\uff0c\u4eca\u65e5\u7acb\u5373\u6267\u884c\uff0c\u51e1\u6211\u5c11 \u6797\u95e8\u5f92\uff0c\u5747\u5e94\u6c38\u5782\u5927\u6212\uff01\u201d\u628a\u624b\u4e00\u6325\uff0c\u8fbe\u6469\u9662\u7684\u56db\u4e2a\u6267\u884c\u50e7\u4eba\uff0c\u4e00\u5f0f\u5927\u7ea2\u8888\u88df\uff0c\u79bb\u5ea7\u8d70\u51fa\u3002 \u6613\u5170\u73e0\u548c\u672c\u65e0\u5927\u5e08\uff0c\u56db\u76ee\u6ce8\u89c6\uff0c\u7d27\u7d27\u76ef\u7740\u4e86\u56e0\u548c\u5c1a\u548c\u5929\u53f6\u6563\u4eba\uff0c\u9632\u4ed6\u4eec\u52a9\u738b\u5c0a\u4e00\u62d2\u6355\u53d1\u96be\u3002 \u90a3\u6599\u738b\u5c0a\u4e00\u5e26\u6765\u7684\u4e03\u540d\u9ad8\u624b\uff0c\u5374\u7aef\u5750\u5e2d\u4e0a\uff0c\u7eb9\u4e1d\u4e0d\u52a8\u3002 \u3000\u3000\u56db\u4e2a\u638c\u5211\u50e7\u4eba\u7f13\u7f13\u884c\u8fdb\uff0c\u5927\u96c4\u6bbf\u5185\uff0c\u4e25\u8083\u5f02\u5e38\uff0c\u4e94\u767e\u50e7\u4eba\uff0c\u5c4f\u606f\u4ee5\u5f85\uff0c\u738b\u5c0a\u4e00\u500f\u5730\u8d77 \u7acb\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u8c01\u6562\u6355\u6211\uff1f\u201d\u628a\u5916\u8863\u4e00\u8131\uff0c\u73b0\u51fa\u91cc\u9762\u7d27\u8eab\u5185\u6263\uff0c\u4e0a\u9762\u7ee3\u6709\u4e94\u722a\u91d1 \u9f99\uff0c\u56db\u56f4\u7f00\u7740\u732b\u513f\u773c\u5b9d\u73e0\uff0c\u5149\u5f69\u593a\u76ee\uff0c\u56db\u4e2a\u638c\u5211\u50e7\u4eba\u4e0d\u7531\u5f97\u51dd\u8eab\u6b62\u6b65\uff0c\u53ea\u542c\u5f97\u54c8\u5e03\u9640\u5927\u559d \u9053\uff1a\u201c\u8fd9\u662f\u5f53\u4eca\u7684\u56db\u7687\u5b50\uff0c\u4f60\u4eec\u8fd8\u4e0d\u8dea\u63a5\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u738b\u5c0a\u4e00\u662f\u5141\u796f\u7684\u5316\u540d\uff0c\u4ed6\u7acb\u5fc3\u4e89\u593a\u7687\u4f4d\uff0c\u6240\u4ee5\u4e0d\u60dc\u5fae\u670d\u51fa\u5bab\uff0c\u7ed3\u4ea4\u5929\u4e0b\u82f1\u96c4\u8c6a \u6770\uff0c\u81ea\u5df1\u4e5f\u6295\u5230\u5c11\u6797\u95e8\u4e0b\uff0c\u5b66\u4e86\u4e09\u5e74\u7684\u4e0a\u4e58\u6b66\u529f\u3002 \u3000\u3000\u53bb\u5e74\u5141\u796f\u56de\u5230\u5317\u4eac\uff0c\u5949\u7236\u7687\u4e4b\u547d\uff0c\u4e0e\u94ae\u94b4\u7984\u6c0f\u6210\u5a5a\uff0c\u8fd9\u94ae\u94b4\u7984\u6c0f\u76f8\u8c8c\u5e73\u5e73\uff0c\u4e0d\u5f97\u5141\u796f\u559c \u7231\uff0c\u56e0\u6b64\u5f53\u4ed6\u518d\u5ea6\u5fae\u670d\u51fa\u4eac\u4e4b\u9645\uff0c\u7a81\u53d1\u5947\u60f3\uff0c\u4ed6\u60f3\u5bab\u4e2d\u867d\u7136\u6bcf\u9694\u4e09\u5e74\u4e94\u5e74\uff0c\u5c31\u8981\u6311\u9009\u4e00\u6b21\u79c0 \u5973\uff0c\u4f46\u6709\u94b1\u4eba\u5bb6\uff0c\u4e3a\u4e86\u5973\u513f\u7ec8\u8eab\u5e78\u798f\uff0c\u5f80\u5f80\u4e0d\u60dc\u91cd\u91d1\u8d3f\u8d42\uff0c\u79c1\u8d42\u5185\u52a1\u5e9c\u7684\u4eba\uff0c\u5c06\u4ed6\u4eec\u7684\u5973\u513f \u8c41\u514d\uff0c\u5c31\u662f\u4e00\u4e9b\u7a77\u5bb6\u5973\u5b50\uff0c\u95fb\u5f97\u6709\u6311\u9009\u79c0\u5973\u7684\u98ce\u58f0\uff0c\u4e5f\u7eb7\u7eb7\u628a\u5973\u513f\u5ac1\u51fa\uff0c\u6216\u5e26\u5973\u513f\u9003\u907f\u3002 \uff08\u6240\u4ee5\u5728\u5c01\u5efa\u7687\u671d\uff0c\u6bcf\u6311\u9009\u4e00\u6b21\u79c0\u5973\uff0c\u6c11\u95f4\u5c31\u5982\u53d7\u4e00\u6b21\u707e\u5bb3\uff09\u56e0\u6b64\u6bcf\u6b21\u6311\u9009\u79c0\u5973\uff0c\u867d\u7136\u90fd\u6709 \u4e00\u5343\u591a\u4eba\uff0c\u4f46\u582a\u79f0\u7edd\u8272\u7684\u6781\u5c11\uff0c\u5eb7\u7199\u513f\u5b50\u53c8\u591a\uff0c\u518d\u5206\u5230\u5404\u738b\u5e9c\u65f6\uff0c\u66f4\u4e4f\u5408\u610f\u7684\u4e86\u3002\u5141\u796f\u5fc3 \u60f3\uff0c\u6211\u624b\u4e0b\u80fd\u4eba\u751a\u591a\uff0c\u4f55\u4e0d\u53eb\u4ed6\u4eec\u66ff\u6211\u627e\u5bfb\u7f8e\u8c8c\u5973\u5b50\uff0c\u4e5f\u514d\u4e86\u8981\u6311\u9009\u90a3\u4e48\u9ebb\u70e6\uff0c\u56e0\u6b64\u4fbf\u60f9\u51fa \u9752\u5c9b\u7684\u91c7\u82b1\u602a\u6848\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u4e8b\u60c5\u5927\u51fa\u4f17\u4eba\u610f\u5916\uff0c\u738b\u5c0a\u4e00\u7adf\u7136\u662f\u5f53\u4eca\u7687\u5b50\uff0c\u5df2\u7ecf\u591f\u602a\uff0c\u800c\u7687\u5b50\u91c7\u82b1\uff0c\u66f4\u662f\u60f3 \u4e0d\u5230\u7684\u4e8b\u3002\u970e\u65f6\u95f4\uff0c\u5927\u96c4\u6bbf\u5185\u98a4\u58f0\u56db\u8d77\uff0c\u50e7\u4fa3\u4eec\u7a83\u7a83\u79c1\u8bae\uff0c\u672c\u65e0\u5927\u5e08\u989d\u73b0\u7ea2\u7b4b\uff0c\u76ee\u95ea\u91d1\u5149\uff0c \u9661\u7136\u559d\u9053\uff1a\u201c\u7687\u5b50\u72af\u6cd5\uff0c\u4e0e\u5eb6\u6c11\u540c\u7f6a\uff01\u201d\u5141\u796f\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u7387\u571f\u4e4b\u6ee8\uff0c\u83ab\u975e\u7687\u81e3\uff0c\u5973\u5b50\u7389 \u5e1b\uff0c\u7686\u662f\u543e\u5bb6\u6240\u6709\uff0c\u6211\u53d6\u51e0\u4e2a\u6c11\u95f4\u5973\u5b50\uff0c\u5374\u514d\u4e86\u591a\u6311\u79c0\u5973\u7684\u9ebb\u70e6\uff0c\u8fd9\u6b63\u662f\u4e00\u6869\u5fb7\u653f\uff0c\u600e\u80fd\u8bf4 \u6211\u72af\u6cd5\uff1f\u518d\u8bf4\u6211\u7eb5\u72af\u6cd5\uff0c\u81ea\u6709\u5b97\u4eba\u5e9c\u7ba1\uff0c\u5c11\u6797\u5bfa\u4e5f\u7ba1\u4e0d\u7740\uff01\u201d \u3000\u3000\u5141\u796f\u4f36\u7259\u4fd0\u9f7f\uff0c\u51e0\u53e5\u8bdd\u7adf\u628a\u672c\u65e0\u5927\u5e08\u9a73\u5012\u3002\u9616\u5bfa\u50e7\u4eba\uff0c\u65e0\u4e0d\u6c14\u6124\u3002\u638c\u7ecf\u5802\u7684\u5f18\u6cd5\u5927\u5e08\u5ffd \u7136\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u77e5\u4f60\u662f\u5c11\u6797\u95e8\u5f92\u738b\u5c0a\u4e00\uff0c\u4e0d\u77e5\u4f60\u662f\u4ec0\u4e48\u56db\u7687\u5b50\u3002\u6211\u4eec\u8fd9\u91cc\u4e0d\u662f\u5b97\u4eba\u5e9c\uff0c \u53ea\u6309\u5c11\u6797\u5bb6\u6cd5\u5904\u6cbb\uff01\u201d\u672c\u65e0\u5927\u5e08\u7ed9\u4ed6\u4e00\u8bed\u63d0\u9192\uff0c\u62c2\u5c18\u4e00\u6307\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u671d\u5ef7\u6709\u56fd\u6cd5\uff0c\u6b66\u6797 \u4e5f\u6709\u95e8\u89c4\uff0c\u4f60\u662f\u5c11\u6797\u95e8\u5f92\uff0c\u5373\u7b97\u4f60\u662f\u5f53\u4eca\u7687\u5e1d\uff0c\u4e5f\u5f97\u7167\u6c5f\u6e56\u89c4\u77e9\uff0c\u9886\u638c\u95e8\u4eba\u7684\u5904\u7f5a\uff01\u201d\u4e86\u56e0 \u5927\u53eb\uff1a\u201c\u53cd\u4e86\uff01\u53cd\u4e86\uff01\u201d \u3000\u3000\u5929\u53f6\u6563\u4eba\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u867d\u8bf4\u6b66\u6797\u5404\u6d3e\uff0c\u81ea\u6709\u95e8\u89c4\uff0c\u4f46\u4e8b\u51fa\u975e\u5e38\uff0c\u4e5f\u5b9c\u4ece\u6743\u5904\u7406\u3002\u56db\u7687\u5b50 \u81ea\u6311\u6c11\u95f4\u79c0\u5973\uff0c\u600e\u80fd\u7b97\u662f\u91c7\u82b1\uff01\u5c11\u6797\u5bfa\u89c4\u867d\u4e25\uff0c\u4e5f\u5f53\u9075\u5b88\u56fd\u6cd5\uff01\u201d\u65e0\u4f4f\u7985\u5e08\u9ed8\u9ed8\u4e0d\u8bed\uff0c\u672c\u65e0 \u5927\u5e08\u53cc\u76ee\u5706\u7741\uff0c\u731b\u7136\u559d\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u82e5\u7136\u754f\u60e7\u6743\u8d35\uff0c\u7eb5\u6cd5\u5f87\u60c5\uff0c\u4ee5\u540e\u600e\u80fd\u9886\u8896\u6b66\u6797\uff1f\u4eca\u65e5\u4e4b \u4e8b\uff0c\u6b63\u662f\u7ed9\u6211\u5c11\u6797\u7684\u8003\u9a8c\uff01\u5c11\u6797\u5bfa\uff0c\u6709\u6b64\u4e0b\u4f5c\u95e8\u5f92\uff0c\u4e43\u662f\u9616\u5bfa\u4e4b\u803b\uff0c\u884c\u5211\u50e7\u4eba\uff0c\u4f60\u4eec\u4f46\u4f9d\u4e3b \u6301\u5429\u5490\uff0c\u5c06\u4e0d\u8096\u5f92\u738b\u5c0a\u4e00\u62ff\u4e0b\uff0c\u6309\u6cd5\u884c\u5211\uff01\u201d\u5929\u53f6\u6563\u4eba\u8df3\u51fa\u5ea7\u4f4d\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u6709\u5982 \u6b64\u89c4\u6a21\uff0c\u4e5f\u771f\u5927\u4e0d\u5bb9\u6613\u3002\u672c\u65e0\u5927\u5e08\u4e0d\u7eb3\u826f\u8a00\uff0c\u4f46\u6c42\u51b3\u610f\uff0c\u96be\u9053\u5c31\u4e0d\u987e\u5c11\u6797\u5386\u4ee3\u7956\u5e08\u7684\u5fc3\u8840\uff0c \u60f3\u628a\u5343\u5e74\u57fa\u4e1a\u6bc1\u4e8e\u4e00\u65e6\u4e48\uff1f\u201d\u6b64\u8bdd\u4e00\u51fa\uff0c\u65e0\u4f4f\u7985\u5e08\u4e0e\u8fbe\u6469\u9662\u7684\u957f\u8001\uff0c\u4e0d\u7981\u8e0c\u8e87\uff0c\u800c\u4e00\u4e9b\u5e74\u8f7b \u6c14\u76db\u7684\u50e7\u4eba\uff0c\u5374\u66f4\u589e\u6124\u6168\uff0c\u4e94\u767e\u50e7\u4eba\u7eb7\u7eb7\u8bae\u8bba\uff0c\u65e0\u5f62\u4e4b\u4e2d\uff0c\u5206\u4e3a\u4e24\u6d3e\uff0c\u4e00\u6d3e\u5fc3\u867d\u4e0d\u6e0d\uff0c\u4f46\u4e3a \u4fdd\u5168\u5c11\u6797\u57fa\u4e1a\uff0c\u5374\u4e3b\u5f20\u4ece\u5bbd\u5904\u7406\uff0c\u4e0d\u95ee\u7f6a\u540d\uff0c\u53e6\u4e00\u6d3e\u5374\u6177\u6168\u6fc0\u6602\uff0c\u5b81\u613f\u6bc1\u5bfa\u4ea1\u8eab\uff0c\u4e5f\u8981\u4fdd\u6301 \u5c11\u6797\u58f0\u8a89\u3002 \u3000\u3000\u65e0\u4f4f\u7985\u5e08\u53e3\u5ba3\u4f5b\u53f7\uff0c\u76ee\u95ea\u91d1\u5149\uff0c\u4e91\u677f\u4e00\u6572\uff0c\u5f00\u58f0\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u6b66\u6797\u524d\u8f88\u4e0e\u9616\u5bfa\u50e7\u4eba\u8bf7\u6682 \u9759\u4e0b\u3002\u4eca\u65e5\u4e4b\u4e8b\uff0c\u65e2\u51fa\u975e\u5e38\uff0c\u8001\u8872\u4e5f\u4e0d\u6562\u64c5\u81ea\u4f5c\u4e3b\u3002\u4e0a\u4e09\u5802\u4e3b\u6301\uff0c\u4e0e\u8fbe\u6469\u9662\u957f\u8001\uff0c\u8bf7\u968f\u8001\u8872 \u56de\u521d\u7956\u5eb5\uff0c\u7980\u8fc7\u7956\u5e08\uff0c\u8ba1\u8bae\u4e4b\u540e\u518d\u884c\u5ba3\u5e03\u3002\u5217\u4f4d\u8d35\u5bbe\uff0c\u8bf7\u5c11\u5f85\u4e9b\u65f6\u3002\u201d\u50e7\u888d\u4e00\u62c2\uff0c\u7387\u9886\u5c11\u6797 \u5341\u4e8c\u9ad8\u50e7\uff0c\u9000\u8fdb\u5185\u5802\u5bc6\u8bae\u3002\u4e94\u767e\u50e7\u5f92\uff0c\u5219\u5217\u6ee1\u6bbf\u4e2d\uff0c\u4e25\u5bc6\u6212\u5907\u3002\u9976\u662f\u5141\u796f\u80c6\u5927\u5305\u5929\uff0c\u4e5f\u81ea\u6709 \u70b9\u60f4\u7136\u3002 \u3000\u3000\u65e0\u4f4f\u7985\u5e08\u9000\u4e0b\u4e4b\u540e\uff0c\u826f\u4e45\uff0c\u826f\u4e45\uff0c\u672a\u89c1\u51fa\u6765\uff0c\u5510\u6653\u6f9c\u5076\u7136\u6e38\u76ee\u6bbf\u5916\uff0c\u5ffd\u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u6b32 \u8fdb\u4e0d\u8fdb\uff0c\u5916\u5bbe\u5e2d\u4e0a\u7684\u795e\u9b54\u53cc\u8001\uff0c\u5ffd\u7136\u7ad9\u8d77\u8eab\u6765\uff0c\u63a2\u5934\u5916\u671b\uff0c\u90a3\u80cc\u5f71\u4e00\u95ea\u5373\u901d\uff0c\u7adf\u4f3c\u751a\u4e3a\u7a14\u719f \u4e4b\u4eba\u3002\u6bbf\u5185\u50e7\u4f17\u89c1\u795e\u9b54\u53cc\u8001\u8d77\u7acb\uff0c\u7eb7\u7eb7\u56f4\u4e0a\uff0c\u795e\u9b54\u53cc\u8001\u9893\u7136\u5750\u4e0b\u3002\u7eb7\u4e71\u4e2d\uff0c\u6bbf\u5185\u8d70\u8fdb\u4e86\u4e00\u4e2a \u5c0f\u5b69\uff0c\u91d1\u73af\u675f\u53d1\uff0c\u6df7\u5728\u50e7\u4eba\u4e2d\uff0c\u6b63\u662f\u5510\u6653\u6f9c\u6628\u665a\u6240\u89c1\u7684\u5947\u602a\u5b69\u5b50\u3002\u5510\u6653\u6f9c\u4e0d\u7531\u5f97\u632a\u8fd1\u51e0\u6b65\uff0c \u542c\u5f97\u4e00\u4e2a\u50e7\u4eba\u7b11\u9053\uff1a\u201c\u7fb9\u5c27\uff0c\u4f60\u4e5f\u51fa\u6765\u770b\u70ed\u95f9\u4e48\uff1f\u4f60\u7684\u5e08\u5085\u4e3a\u4f55\u4e0d\u6765\uff1f\u201d\u5510\u6653\u6f9c\u5fc3\u5ff5\u4e00\u52a8\uff0c \u7779\u60f3\uff1a\u201c\u7fb9\u5c27\u201d\u8fd9\u540d\u5b57\uff0c\u597d\u50cf\u5728\u90a3\u91cc\u542c\u8fc7\uff1f\u90a3\u5c0f\u5634\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u600e\u4e48\u77e5\u9053\u5462\uff1f\u201d\u5510 \u6653\u6f9c\u6b63\u60f3\u9760\u8fd1\u53bb\uff0c\u65e0\u4f4f\u7985\u5e08\u7387\u5c11\u6797\u5bfa\u7684\u5341\u4e8c\u9ad8\u50e7\uff0c\u5df2\u81ea\u540e\u5802\u8fde\u7fe9\u8d70\u51fa\u3002 \u3000\u3000\u65e0\u4f4f\u7985\u5e08\u8001\u6210\u6301\u91cd\uff0c\u672c\u610f\u4e0d\u60f3\u60f9\u6b64\u9ebb\u70e6\uff0c\u5728\u521d\u7956\u5eb5\u4e2d\uff0c\u5546\u8bae\u826f\u4e45\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u53f6\u6563\u4eba\u4e4b \u8a00\uff0c\u867d\u8ff9\u8fd1\u5a01\u80c1\uff0c\u4f46\u82e5\u4ee4\u5c11\u6797\u57fa\u4e1a\u6bc1\u4e8e\u543e\u8f88\u4e4b\u624b\uff0c\u5bf9\u5217\u4ee3\u7956\u5e08\uff0c\u600e\u751f\u4ea4\u4ee3\u8fc7\u53bb\uff1f\u201d\u5f18\u6cd5\u5927\u5e08 \u5ffd\u9053\uff1a\u201c\u4e3b\u6301\uff0c\u8bf7\u95ee\u5c11\u6797\u5bfa\u5efa\u5bfa\u4ee5\u6765\uff0c\u5df2\u5386\u591a\u5c11\u5e74\u4ee3\uff1f\u201d\u65e0\u4f4f\u7985\u5e08\u8be7\u9053\uff1a\u201c\u600e\u4e48\u4f60\u8fd8\u95ee\u6211\uff1f \u6211\u5d69\u5c71\u5c11\u6797\uff0c\u5efa\u5bfa\u5df2\u5386\u4e00\u5343\u4e09\u767e\u4f59\u5e74\uff0c\u9616\u5bfa\u50e7\u4eba\uff0c\u8c01\u4e0d\u77e5\u9053\uff1f\u201d\u5f18\u6cd5\u5927\u5e08\u5e84\u4e25\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31 \u662f\u4e86\uff01\u8bd5\u95ee\u4e00\u5343\u4e09\u767e\u5e74\u6765\uff0c\u6362\u4e86\u591a\u5c11\u671d\u4ee3\uff01\u5e1d\u7687\u4e4b\u5a01\uff0c\u53ef\u901e\u4e8e\u4e00\u65f6\uff0c\u5374\u7edd\u4e0d\u80fd\u541b\u4e34\u767e\u4e16\u3002\u671d \u4ee3\u53ef\u66f4\u6362\uff0c\u6211\u5c11\u6797\u7684\u5bfa\u89c4\u5374\u4e0d\u80fd\u64c5\u6539\uff0c\u96be\u9053\u6211\u5c11\u6797\u5343\u5e74\u58f0\u8a89\uff0c\u7adf\u4e0d\u80fd\u548c\u4e00\u4e2a\u7687\u5b50\u76f8\u6bd4\u5417\uff1f\u201d \u3000\u3000\u672c\u65e0\u5927\u5e08\u4e5f\u9053\uff1a\u201c\u5f18\u6cd5\u4e4b\u8a00\u6709\u7406\uff0c\u4eca\u65e5\u6211\u4eec\u82e5\u4e0d\u6267\u884c\u7956\u5e08\u9057\u6212\uff0c\u5c11\u6797\u5bfa\u7eb5\u53ef\u82df\u5b58\u4e00\u65f6\uff0c \u4f46\u58f0\u8a89\u5c3d\u4e27\uff0c\u4e5f\u4e0d\u8fc7\u662f\u540d\u5b58\u5b9e\u4ea1\u7f62\u4e86\u3002\u82e5\u6211\u4eec\u6bc5\u7136\u6574\u8083\uff0c\u7ef4\u62a4\u6211\u5c11\u6797\u7684\u5c0a\u4e25\uff0c\u5c11\u6797\u5bfa\u867d\u540d\u4ea1 \u800c\u5b9e\u5b58\uff0c\u6c38\u4e3a\u6b66\u6797\u4e2d\u7684\u6cf0\u5c71\u5317\u6597\uff0c\u6211\u4eec\u773c\u5149\u5e94\u53ca\u540e\u4e16\uff0c\u5e08\u5f1f\uff0c\u4f60\u662f\u4e00\u5bfa\u4e3b\u6301\uff0c\u5e94\u8be5\u6709\u7edd\u5927\u7684 \u7891\u5200\uff0c\u987b\u77e5\u521b\u4e1a\u867d\u7136\u8270\u96be\uff0c\u4f46\u5bfa\u6bc1\u53ef\u4ee5\u91cd\u5efa\uff0c\u4eba\u4ea1\u6280\u53ef\u6c38\u4f20\u3002\u53ea\u6709\u8fd9\u5343\u767e\u5e74\u6765\uff0c\u6240\u79ef\u4e0b\u7684\u58f0 \u671b\uff0c\u6240\u5efa\u7acb\u7684\u7cbe\u795e\uff0c\u4e00\u65e6\u8d25\u574f\uff0c\u5374\u518d\u96be\u6062\u590d\u4e86\u3002\u201d\u65e0\u4f4f\u7985\u5e08\u95ed\u76ee\u6c89\u601d\uff0c\u8fc7\u4e86\u597d\u4e45\uff0c\u624d\u53f9\u53e3 \u6c14\uff0c\u500f\u5730\u8d77\u7acb\uff0c\u7387\u9886\u4f17\u4eba\u51fa\u521d\u7956\u5eb5\uff0c\u8fdb\u5927\u96c4\u6bbf\u3002\u4f17\u4eba\u89c1\u4ed6\u9762\u8272\u6c89\u91cd\uff0c\u4e0d\u77e5\u4ed6\u51b3\u5b9a\u5982\u4f55\uff0c\u8c01\u4e5f \u4e0d\u6562\u53d1\u95ee\uff01 \u3000\u3000\u65e0\u4f4f\u7985\u5e08\u518d\u5ea6\u5347\u6bbf\uff0c\u6267\u4e8b\u50e7\u4eba\u7acb\u5373\u6572\u8d77\u949f\u6765\uff0c\u5168\u5802\u8083\u9759\uff0c\u638c\u7ecf\u5802\u7684\u5f18\u6cd5\u5927\u5e08\uff0c\u606d\u8eab\u5408 \u4ec0\uff0c\u5408\u638c\u95ee\u9053\uff1a\u201c\u738b\u5c0a\u4e00\u6709\u7f6a\u65e0\u7f6a\uff1f\u201d\u65e0\u4f4f\u7985\u5e08\u6c89\u58f0\u7b54\u9053\uff1a\u201c\u6709\u7f6a\uff01\u201d\u5141\u796f\u5e26\u6765\u7684\u4eba\uff0c\u9f50\u90fd \u53d8\u8272\u3002\u5929\u53f6\u6563\u4eba\u9053\uff1a\u201c\u613f\u95fb\u4e3b\u6301\u4e4b\u7406\u3002\u201d\u65e0\u4f4f\u7985\u5e08\u9053\uff1a\u201c\u738b\u5c0a\u4e00\u867d\u7136\u662f\u5f53\u4eca\u7687\u5b50\uff0c\u4f46\u4ed6\u5165\u6211 \u5c11\u6797\u95e8\u4e0b\u4e4b\u65f6\uff0c\u5374\u662f\u4ee5\u666e\u901a\u4eba\u7684\u8eab\u4efd\u6765\u7684\u3002\u672c\u7a7a\u5927\u5e08\u662f\u4ed6\u7684\u6b66\u6797\u5e08\u5c0a\uff0c\u5e76\u975e\u4ed6\u7684\u5bab\u4e2d\u6559\u4e60\u3002 \u5c11\u6797\u5bfa\u4e3a\u6b66\u6797\u4e00\u8109\uff0c\u95e8\u5f92\u72af\u4e86\u91c7\u82b1\u5927\u6212\uff0c\u5fc5\u5b9a\u8981\u4f9d\u6212\u5f8b\u6267\u884c\uff01\u201d \u3000\u3000\u56db\u4e2a\u5211\u5802\u618e\u4eba\u9053\u58f0\uff1a\u201c\u9886\u65e8\u201d\uff0c\u7f13\u7f13\u884c\u8fdb\uff0c\u4e86\u56e0\u4e0e\u54c8\u5e03\u9640\u4e00\u5de6\u4e00\u53f3\uff0c\u5206\u7acb\u5141\u796f\u4e24\u4fa7\uff0c\u5211 \u5802\u50e7\u4eba\u89c6\u82e5\u65e0\u7779\uff0c\u4ecd\u662f\u9762\u5bb9\u8083\u7a46\uff0c\u7ee7\u7eed\u524d\u884c\u3002\u5141\u796f\u5411\u4e86\u56e0\u6447\u4e86\u6447\u624b\uff0c\u7a81\u7136\u559d\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u65e0\u4f4f\u7985\u5e08\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d\u5141\u796f\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u4e2a\u7eb8\u5305\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u5165\u95e8\u4e4b\u540e\uff0c \u5373\u5bf9\u672c\u7a7a\u5e08\u5085\u8bf4\u660e\u8eab\u4efd\uff0c\u6211\u51fa\u5bfa\u4e4b\u65e5\uff0c\u4ed6\u4eb2\u5199\u6709\u8d1d\u53f6\u7b3a\u6587\u4e0e\u6211\u6536\u6267\uff0c\u638c\u6559\u65b9\u4e08\uff0c\u7b3a\u6587\u5728\u6b64\uff0c \u4f60\u770b\u4e86\u5c31\u660e\u767d\u4e86\u3002\u201d\u54c8\u5e03\u9640\u63a5\u8fc7\u7eb8\u5305\uff0c\u8f6c\u4ea4\u65e0\u4f4f\u7985\u5e08\u3002\u65e0\u4f4f\u7985\u5e08\u770b\u540e\uff0c\u9762\u9732\u8bb6\u5f02\u4e4b\u5bb9\uff0c\u4f4e\u5934 \u4e0d\u8bed\u3002\u672c\u65e0\u4e0e\u5f18\u6cd5\u4e8c\u4eba\u6025\u5fd9\u51d1\u8fc7\u53bb\u770b\uff0c\u770b\u540e\u672c\u65e0\u6012\u9053\uff1a\u201c\u6211\u672c\u7a7a\u5e08\u5144\uff0c\u7edd\u4e0d\u662f\u8fd9\u6837\u7684\u4eba\u3002\u4f60 \u8fd9\u7b3a\u6587\u4e43\u662f\u5047\u7684\u3002\u201d\u539f\u6765\u8d1d\u53f6\u7b3a\u6587\u5199\u9053\uff1a \u3000\u3000\u201c\u5c11\u6797\u7b2c\u56db\u5341\u4e03\u4ee3\u638c\u6559\u65b9\u4e08\u672c\u7a7a\uff0c\u8c15\u77e5\u540e\u8f88\u65b9\u4e08\uff0c\u672c\u5bfa\u7b2c\u56db\u5341\u516b\u4ee3\u5f1f\u5b50\u738b\u5c0a\u4e00\u4e43\u5f53\u4eca\u56db \u7687\u5b50\u5141\u796f\uff0c\u6155\u6211\u5c11\u6797\u4e4b\u540d\uff0c\u4e0d\u8f9e\u8270\u82e6\uff0c\u5165\u5bfa\u7688\u4f9d\uff0c\u6b32\u4ee5\u671d\u5ef7\u4e4b\u529b\uff0c\u5021\u6211\u5c11\u6797\u6b66\u827a\uff0c\u5e76\u613f\u4e3a\u62a4 \u6cd5\uff0c\u6c38\u4fdd\u5e84\u4e25\u3002\u5c11\u6797\u6709\u5e78\uff0c\u7687\u5b50\u7688\u4f9d\uff0c\u8c28\u4f9d\u541b\u81e3\u4e4b\u4e49\uff0c\u6b66\u6797\u4e4b\u89c4\uff0c\u5141\u796f\u4ecd\u4e3a\u5c11\u6797\u5f1f\u5b50\uff0c\u4f46\u5e9f \u53bb\u5e08\u5f92\u4e4b\u540d\uff0c\u5141\u796f\u5165\u5bfa\uff0c\u51c6\u514d\u5bf9\u957f\u8f88\u62dc\u8dea\u4e4b\u793c\uff0c\u4e0d\u53d7\u5c11\u6797\u5bb6\u6cd5\u7ea6\u675f\u3002\u7b3a\u4ea4\u5141\u796f\u6536\u6267\uff0c\u5728\u6211\u5706 \u5bc2\u4e4b\u540e\u8f6c\u63a5\u4f4d\u65b9\u4e08\u3002\u672c\u7a7a\u8c15\u3002\u201d \u3000\u3000\u672c\u65e0\u5fc3\u60f3\uff1a\u672c\u7a7a\u5e08\u5144\u4e43\u662f\u521a\u76f4\u4e4b\u4eba\uff0c\u867d\u7136\u4e3b\u5239\u6e05\u4fee\uff0c\u5374\u662f\u5fc3\u5b58\u6c49\u5ba4\uff0c\u82e5\u7136\u77e5\u4ed6\u662f\u4e2a\u7687 \u5b50\uff0c\u5fc5\u4e0d\u80af\u6536\u4ed6\u4e3a\u5f92\u3002\u7eb5\u6536\u4ed6\u4e3a\u5f92\uff0c\u5706\u5bc2\u4e4b\u65f6\uff0c\u4e5f\u5e94\u5bf9\u6211\u8f88\u8a00\u53ca\u3002\u8fd9\u7b3a\u6587\u7edd\u5bf9\u662f\u5047\uff01\u65e0\u4f4f\u7985 \u5e08\u5374\u60f3\u9053\uff1a\u5e08\u5144\u4e3a\u4fdd\u57fa\u4e1a\uff0c\u60f3\u662f\u51fa\u4e8e\u65e0\u5948\uff0c\u6545\u4e0d\u5f97\u4e0d\u6536\u3002\u8fd9\u4e8b\u4e43\u7edd\u5927\u673a\u5bc6\uff0c\u6240\u4ee5\u4ed6\u5f25\u7559\u4e4b \u65f6\uff0c\u4e0d\u6562\u5bf9\u6211\u8f88\u8a00\u53ca\u3002\u4e24\u4eba\u5fc3\u601d\u4e0d\u540c\uff0c\u672c\u65e0\u5486\u54ee\u5982\u96f7\uff0c\u65e0\u4f4f\u7985\u5e08\u5374\u662f\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u5141\u796f\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u76d1\u5bfa\u8bf4\u8fd9\u7b3a\u6587\u662f\u5047\uff0c\u8bf7\u95ee\u4e3b\u6301\uff0c\u672c\u7a7a\u5927\u5e08\u7684\u5b57\u8ff9\uff0c\u53ef\u662f\u8fd9\u6837\u7684\u4e48\uff1f\u201d \u672c\u65e0\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u5b57\u8ff9\u4e0d\u8db3\u4e3a\u51ed\uff0c\u4f60\u5c1a\u6709\u4f55\u4eba\u8bc1\u7269\u8bc1\uff1f\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u5ffd\u7136\u6709\u4e2a\u6e05\u8106\u7684\u5b69\u5b50\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6211\u6765\u4f5c\u8bc1\uff01\u201d\u90a3\u4e2a\u91d1\u73af\u675f\u53d1\u7684\u5b69\u5b50\u7a81\u7136\u8df3 \u4e0a\u53f3\u9996\u7ecf\u684c\uff0c\u5f18\u6cd5\u559d\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\uff0c\u4f60\u8fd9\u5b69\u5b50\u77e5\u9053\u4ec0\u4e48\uff1f\u4e0d\u51c6\u5728\u6b64\u80e1\u95f9\u3002\u201d\u5510\u6653\u6f9c\u500f\u7136\u60f3 \u8d77\uff0c\u5468\u9752\u7684\u597d\u53cb\u949f\u4e07\u5802\u4f3c\u66fe\u8bf4\u8fc7\uff0c\u4ed6\u6536\u6709\u4e00\u4e2a\u5929\u4e0b\u6700\u987d\u76ae\u7684\u5b69\u5b50\u4e3a\u5f92\uff0c\u59d3\u540d\u53eb\u505a\u5e74\u7fb9\u5c27\uff0c\u83ab \u975e\u5c31\u662f\u8fd9\u4e2a\u5b69\u5b50\uff1f\u4f55\u4ee5\u4ed6\u4f1a\u5728\u5c11\u6797\u5bfa\u51fa\u73b0\uff0c\u800c\u949f\u4e07\u5802\u53c8\u4e0d\u89c1\u6765\uff1f \u3000\u3000\u5510\u6653\u6f9c\u4e0d\u77e5\uff0c\u949f\u4e07\u5802\u539f\u6765\u4e5f\u5728\u5bfa\u4e2d\u3002\u539f\u6765\u949f\u4e07\u5802\u4e43\u5085\u9752\u4e3b\u7684\u5f92\u5b59\uff0c\u5085\u9752\u4e3b\u548c\u672c\u7a7a\u7684\u5e08 \u5085\uff0c\u5373\u5c11\u6797\u5bfa\u7684\u7b2c\u56db\u5341\u516d\u4ee3\u65b9\u4e08\u75db\u7985\u4e0a\u4eba\u4ea4\u60c5\u751a\u597d\uff0c\u6240\u4ee5\u540e\u8f88\u4e5f\u6709\u4ea4\u60c5\uff0c\u949f\u4e07\u5802\u6bcf\u9694\u4e00\u4e24 \u5e74\uff0c\u90fd\u8981\u5230\u5d69\u5c71\u5c11\u6797\uff0c\u52fe\u7559\u6570\u6708\u3002\u5e74\u7fb9\u5c27\u5bb6\u5728\u6cb3\u5357\u9648\u7559\uff0c\u4e0e\u5d69\u5c71\u76f8\u53bb\u4e0d\u8fdc\uff0c\u949f\u4e07\u5802\u6781\u7231\u5e74\u7fb9 \u5c27\uff0c\u6240\u4ee5\u4e5f\u5e38\u643a\u4ed6\u4e0a\u5c71\u6e38\u73a9\u3002\u201c\u5c11\u6797\u4e09\u8001\u201d\u672c\u7a7a\u3001\u672c\u65e0\uff0c\u65e0\u4f4f\uff0c\u89c1\u5e74\u7fb9\u5c27\u806a\u660e\u7edd\u9876\uff0c\u95fb\u4e00\u77e5 \u5341\uff0c\u5927\u5bb6\u90fd\u5f88\u7231\u4ed6\uff0c\u5c24\u5176\u662f\u672c\u7a7a\u65b9\u4e08\uff0c\u66f4\u628a\u4ed6\u5b9d\u8d1d\u5f97\u4e0d\u5f97\u4e86\uff0c\u5e38\u7559\u4ed6\u4e00\u5ba4\u4f4f\u5bbf\uff0c\u6388\u4ed6\u6b66\u529f\u3002 \u6628\u665a\u5510\u6653\u6f9c\uff0c\u89c1\u4ed6\u5728\u7f57\u6c49\u5802\u624b\u821e\u8db3\u8e48\uff0c\u5c31\u6b63\u662f\u4ed6\u4f9d\u7167\u4f5b\u50cf\u59ff\u52bf\uff0c\u7ec3\u5c11\u6797\u5bfa\u7684\u9547\u5c71\u62f3\u6cd5\u2014\u2014\u7f57 \u6c49\u4f0f\u864e\u62f3\u3002\u672c\u7a7a\u6b7b\u540e\uff0c\u5e74\u7fb9\u5c27\u8fd8\u662f\u65f6\u5e38\u4e0a\u5c71\uff0c\u8fd9\u6b21\u4ed6\u548c\u949f\u4e07\u5802\u540c\u6765\uff0c\u4f4f\u4e86\u4e2a\u591a\u6708\uff0c\u4e2d\u95f4\u53c8\u66fe \u56de\u5bb6\u4e00\u6b21\uff0c\u56de\u5c71\u65f6\u6070\u6070\u78b0\u5230\u6b64\u4e8b\u3002 \u3000\u3000\u949f\u4e07\u5802\u4e0d\u662f\u8bc1\u4eba\uff0c\u6240\u4ee5\u65e0\u4f4f\u7985\u5e08\u4e0d\u9080\u4ed6\u5217\u5e2d\u3002\u540e\u6765\u5927\u96c4\u5b9d\u6bbf\u55a7\u54d7\u6270\u6518\uff0c\u5e74\u7fb9\u5c27\u5fcd\u8010\u4e0d \u4f4f\uff0c\u8981\u949f\u4e07\u5802\u5e26\u4ed6\u51fa\u6765\uff0c\u5728\u6bbf\u5916\u89c2\u671b\uff0c\u4e0d\u6599\u949f\u4e07\u5802\u4e0d\u770b\u8fd8\u7f62\uff0c\u4e00\u770b\u7adf\u53d1\u73b0\u81ea\u5df1\u7684\u4e24\u4e2a\u5bf9\u5934\u795e \u9b54\u53cc\u8001\uff0c\u4e5f\u5728\u6bbf\u5185\u3002\u8981\u77e5\u9053\u8fd9\u5341\u5e74\u6765\uff0c\u949f\u4e07\u5802\u9690\u59d3\u57cb\u540d\uff0c\u56db\u5904\u9003\u907f\uff0c\u6015\u7684\u5c31\u662f\u795e\u9b54\u53cc\u8001\uff0c\u8fd9 \u4e00\u53d1\u73b0\uff0c\u4ee4\u5f97\u4ed6\u6025\u6025\u8d70\u907f\uff0c\u800c\u5e74\u7fb9\u5c27\u5374\u5df2\u6df7\u5165\u5bfa\u50e7\u4e4b\u5185\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u7ed9\u5f18\u6cd5\u4e00\u559d\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u600e\u89c1\u5f97\u6211\u4e0d\u77e5\u9053\uff1f\u6211\u77e5\u9053\u8fd9\u738b\u5c0a\u4e00\u5c31\u662f\u5f53\u4eca\u7684\u56db\u7687 \u5b50\uff0c\u672c\u7a7a\u5927\u5e08\u5bf9\u6211\u8bf4\u7684\uff0c\u4ed6\u5199\u8d1d\u53f6\u6587\u65f6\uff0c\u6211\u8fd8\u5728\u65c1\u8fb9\u5462\uff01\u4ed6\u518d\u4e09\u53ee\u5631\u53eb\u6211\u4e0d\u8981\u8bf4\u51fa\uff0c\u6211\u624d\u9690 \u5fcd\u4e86\u8fd9\u4e48\u591a\u5e74\u3002\u5f53\u65f6\u6211\u56e0\u597d\u5947\uff0c\u5728\u65c1\u8fb9\u89c2\u671b\uff0c\u8fd8\u628a\u7b3a\u6587\u7262\u8bb0\u4e86\u5462\uff01\u201d\u5e74\u7fb9\u5c27\u8fc7\u76ee\u6210\u8bf5\uff0c\u672c \u65e0\u3001\u65e0\u4f4f\u90fd\u77e5\u4ed6\u6709\u6b64\u672c\u9886\u3002\u5f53\u4e0b\u65e0\u4f4f\u8bf4\u9053\uff1a\u201c\u5c0f\u5b69\u5b50\u4e0d\u51c6\u4e71\u6253\u8c0e\u8bed\uff0c\u4f60\u5c06\u7b3a\u6587\u80cc\u6765\u542c\u542c\u3002\u201d \u5e74\u7fb9\u5c27\u5728\u7ecf\u684c\u4e0a\u9ad8\u58f0\u6717\u8bf5\uff0c\u679c\u7136\u4e00\u5b57\u4e0d\u5dee\uff0c\u672c\u65e0\u542c\u4e86\u505a\u58f0\u4e0d\u5f97\uff0c\u5e74\u7fb9\u5c27\u80cc\u5b8c\u4e4b\u540e\u53c8\u9053\uff1a\u201c\u672c \u7a7a\u5927\u5e08\u5f25\u7559\u524d\u4e24\u5929\uff0c\u8fd8\u7559\u6709\u9057\u4e66\u7ed9\u6211\uff0c\u53eb\u6211\u5c06\u6765\u82e5\u6709\u5927\u4e8b\uff0c\u5c31\u53bb\u627e\u8fd9\u4f4d\u7687\u5b50\u5e08\u5144\uff0c\u4e3b\u6301\uff0c\u4f60 \u8bf7\u770b\u770b\u3002\u201d\u628a\u4fe1\u53d6\u51fa\uff0c\u672c\u65e0\u4e5f\u51d1\u8fc7\u6765\u770b\uff0c\u8fd9\u5c01\u4fe1\u4e0e\u7b3a\u6587\u5b57\u8ff9\uff0c\u679c\u7136\u90fd\u662f\u672c\u7a7a\u4eb2\u7b14\u3002\u672c\u65e0\u53f9 \u9053\uff1a\u201c\u7f62\u4e86\uff01\u7f62\u4e86\uff01\u201d\u9893\u7136\u5750\u4e0b\uff0c\u9616\u5bfa\u50e7\u4fa3\uff0c\u7eb7\u4e71\u8d77\u6765\uff0c\u738b\u5c0a\u4e00\u6602\u7136\u8d77\u7acb\uff0c\u5bf9\u65e0\u4f4f\u7985\u5e08\u6253\u4e2a \u7a3d\u9996\uff0c\u5c31\u5f80\u5916\u95ef\u3002\u672c\u65e0\u5ffd\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u5c31\u60f3\u8fd9\u6837\u8d70\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u4f5b\u95e8\u72ee\u5b50\u543c\uff0c\u7ae5\u7a1a\u620f\u7985\u5e08\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u4e00\u56de\u3000\u6ca5\u8840\u5455\u5fc3\u3000\u540d\u5e08\u906d\u6bd2\u5bb3\u3000\u72e0\u5fc3\u8fa3\u624b\u3000\u5de8\u5ba4\u51fa\u67ad\u96c4 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u5341\u4e00\u56de \u6ca5\u8840\u5455\u5fc3\u3000\u540d\u5e08\u906d\u6bd2\u5bb3 \u72e0\u5fc3\u8fa3\u624b\u3000\u5de8\u5ba4\u51fa\u67ad\u96c4 \u3000\u3000\u5141\u796f\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u672c\u65e0\u5927\u5e08\u559d\u9053\uff1a\u201c\u4f60\u65e2\u9080\u5916\u6d3e\u4eba\u7269\u8bc4\u7406\uff0c\u90a3\u4e48\u4f60\u4eca\u540e\u8fd8\u7b97\u4e0d \u7b97\u5c11\u6797\u5bfa\u5f1f\u5b50\uff1f\u201d\u82e5\u4f9d\u6b66\u6797\u89c4\u77e9\uff0c\u7ecf\u6b64\u4e00\u95f9\uff0c\u5f53\u4e8b\u4eba\u591a\u534a\u4f1a\u8131\u79bb\u95e8\u6237\uff0c\u6539\u6295\u522b\u6d3e\u3002\u5141\u796f\u9053\uff1a \u201c\u662f\u53c8\u600e\u6837\uff0c\u4e0d\u662f\u53c8\u600e\u6837\uff1f\u201d\u672c\u65e0\u5927\u5e08\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u4e0d\u662f\u5c11\u6797\u5f1f\u5b50\uff0c\u90a3\u5c31\u8bf7\u628a\u5c11\u6797\u8fd9\u70b9\u5fae \u672a\u4e4b\u6280\u8fd8\u6765\uff01\u201d\u9a88\u6307\u5982\u655b\uff0c\u84e6\u7136\u7167\u5141\u796f\u80a9\u5934\u75be\u70b9\uff1b\u8fd9\u662f\u5c11\u6797\u6d3e\u7684\u7edd\u6280\uff0c\u540d\u4e3a\u201c\u94c1\u6307\u7985\u201d\uff0c\u53ea \u8981\u88ab\u4ed6\u70b9\u7740\uff0c\u8fd9\u4e00\u8eab\u6b66\u529f\u5c31\u7b97\u5b8c\u4e86\u3002\u536b\u62a4\u5728\u5141\u796f\u4e24\u4fa7\u7684\u7518\u5929\u9f99\u548c\u8463\u5de8\u5ddd\u9f50\u9f50\u51fa\u624b\uff0c\u53ea\u89c1\u638c\u98ce \u8d77\u5904\uff0c\u4eba\u5f71\u7ffb\u817e\uff0c\u7518\u8463\u4e8c\u4eba\u6643\u80a9\u9000\u51fa\u516d\u4e03\u6b65\u5916\u3002\u5141\u796f\u7d20\u77e5\u8fd9\u4f4d\u5e08\u53d4\u4e43\u662f\u59dc\u6842\u4e4b\u6027\uff0c\u60f9\u607c\u4e86 \u4ed6\uff0c\u53ef\u80fd\u4ed6\u4f1a\u86ee\u5e72\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u6211\u5f53\u7136\u8fd8\u7b97\u662f\u5c11\u6797\u5f1f\u5b50\uff0c\u5e08\u5085\u7684\u8d1d\u53f6\u7b3a\u6587\uff0c\u4e0d\u662f\u5199\u5f97\u660e\u660e \u767d\u767d\uff0c\u53ea\u5e9f\u53bb\u5e08\u5f92\u4e4b\u540d\uff0c\u4ecd\u5f53\u6211\u4e3a\u5c11\u6797\u5f1f\u5b50\u5417\uff1f\u201d\u65e0\u4f4f\u7985\u5e08\u8d76\u51fa\u6765\u9053\uff1a\u201c\u5e08\u5144\uff0c\u8ba9\u4ed6\u8d70 \u5427\u3002\u201d\u672c\u65e0\u5927\u5e08\u4ecd\u7136\u559d\u9053\uff1a\u201c\u65e2\u662f\u5c11\u6797\u5f1f\u5b50\uff0c\u4e3a\u4f55\u5bf9\u957f\u8f88\u8fd9\u6837\u65e0\u793c\uff1f\u201d\u5141\u796f\u6025\u5fd9\u606d\u8eab\u65bd\u793c\uff0c \u672c\u65e0\u5927\u5e08\u53cc\u76ee\u4e00\u7741\uff0c\u5141\u796f\u4e24\u817f\u4e00\u9178\u8f6f\uff0c\u4e0d\u7531\u5f97\u8dea\u5728\u5730\u4e0a\uff0c\u5f85\u8981\u78d5\u5934\uff0c\u4e86\u56e0\u3001\u54c8\u5e03\u9640\u9762\u8272\u5927 \u53d8\uff0c\u6025\u5fd9\u6765\u6276\uff0c\u5141\u796f\u65e2\u7136\u9732\u51fa\u8eab\u4efd\uff0c\u7687\u5b50\u5982\u4f55\u80fd\u8dea\u62dc\u5eb6\u6c11\u3002\u65e0\u4f4f\u7985\u5e08\u62c2\u5c18\u671d\u4ed6\u80a9\u5934\u4e00\u5e26\uff0c\u6697 \u8fd0\u5185\u529b\uff0c\u5c06\u4ed6\u626f\u4e86\u8d77\u6765\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u524d\u8f88\u65b9\u4e08\uff0c\u65e2\u7279\u51c6\u4f60\u514d\u53bb\u8dea\u7985\u4e4b\u793c\uff0c\u90a3\u4f60\u4e5f\u5c31\u968f\u5fc3\u4e4b \u6240\u5b89\uff0c\u514d\u4e86\u53bb\u5427\u3002\u201d\u65e0\u4f4f\u6b64\u8a00\uff0c\u6148\u7965\u4e4b\u4e2d\uff0c\u5374\u53c8\u610f\u6709\u53cc\u5173\uff0c\u610f\u601d\u662f\u8bf4\uff1a\u4f60\u82e5\u8ba4\u4e3a\u5bf9\u957f\u8f88\u4e0d\u5fc5 \u5c0a\u656c\uff0c\u5fc3\u5b89\u7406\u5f97\uff0c\u90a3\u4e5f\u5c31\u7b97\u4e86\u5427\u3002\u5141\u796f\u4f55\u7b49\u806a\u660e\uff0c\u542c\u51fa\u6b64\u610f\uff0c\u4f6f\u4f5c\u4e0d\u77e5\uff0c\u5728\u4e86\u56e0\u7b49\u4eba\u7c07\u62e5\u4e4b \u4e0b\uff0c\u75be\u5fd9\u9000\u51fa\u5c71\u95e8\u3002 \u3000\u3000\u7eb7\u4e71\u8fc7\u540e\uff0c\u5415\u56db\u5a18\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u5510\u6653\u6f9c\u5462\uff1f\u201d\u6613\u5170\u73e0\u6e38\u76ee\u56db\u987e\uff0c\u679c\u7136\u4e0d\u89c1\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9 \u5b69\u5b50\u6df7\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u65e0\u4f4f\u7985\u5e08\u4e5f\u95ee\u9053\uff1a\u201c\u949f\u5e08\u5085\u5462\uff1f\u201d\u5bfa\u50e7\u627e\u4e86\u4e00\u904d\uff0c\u56de\u9053\uff1a\u201c\u949f\u5e08\u5085\u4e5f \u4e0d\u89c1\u4e86\u3002\u201d\u5415\u56db\u5a18\u95ee\u9053\uff1a\u201c\u54ea\u4f4d\u949f\u5e08\u5085\uff1f\u201d\u672c\u65e0\u5927\u5e08\u7b54\u9053\uff1a\u201c\u5c31\u662f\u90a3\u4f4d\u65e0\u6781\u5251\u7684\u4f20\u4eba\u949f\u4e07 \u5802\u3002\u201d\u6613\u5170\u73e0\u201c\u54e6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u5e08\u4f84\u3002\u6211\u548c\u4ed6\u7684\u5e08\u7956\u5085\u9752\u4e3b\u5f53\u5e74\u66fe\u540c\u5230\u56de\u7586\uff0c\u600e \u4e48\u4ed6\u73b0\u5728\u5728\u8fd9\u91cc\u5417\uff1f\u201d\u65e0\u4f4f\u7985\u5e08\u9053\uff1a\u201c\u4ed6\u5728\u9648\u7559\u6559\u4e66\uff0c\u521a\u624d\u8bf4\u8bdd\u7684\u90a3\u4e2a\u5b69\u5b50\u5c31\u662f\u4ed6\u7684\u5f92 \u5f1f\u3002\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u90a3\u4e2a\u5b69\u5b50\u771f\u662f\u5389\u5bb3\uff0c\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u672c\u65e0\u5927\u5e08\u9053\uff1a\u201c\u53eb\u5e74\u7fb9\u5c27\u3002\u201d\u7709\u5934 \u4e00\u76b1\u53c8\u67e5\u95ee\u9053\uff1a\u201c\u7fb9\u5c27\u5b69\u5b50\u5462\uff1f\u201d\u5bfa\u50e7\u56de\u9053\uff1a\u201c\u7fb9\u5c27\u4e5f\u6e9c\u8d70\u4e86\u3002\u201d\u672c\u65e0\u5927\u5e08\u5947\u9053\uff1a\u201c\u600e\u4e48\u4ed6 \u4eec\u5e08\u5f92\u4fe9\u7adf\u4e0d\u8f9e\u800c\u884c\u3002\u201d\u6613\u5170\u73e0\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5374\u4e0d\u8a00\u8bed\u3002 \u3000\u3000\u4e14\u8bf4\u949f\u4e07\u5802\u7a81\u7136\u78b0\u89c1\u4e86\u795e\u9b54\u53cc\u8001\uff0c\u6027\u547d\u4ea4\u5173\uff0c\u6025\u5fd9\u9003\u8d70\uff0c\u8fde\u591c\u6e9c\u56de\u9648\u7559\uff0c\u5e74\u7fb9\u5c27\u7684\u7236\u4eb2 \u5e74\u9050\u9f84\u4e43\u4e00\u65b9\u5de8\u5bcc\uff0c\u82b1\u56ed\u751a\u5927\uff0c\u56f4\u7740\u9ad8\u5899\u3002\u949f\u4e07\u5802\u8dc3\u8fdb\u56ed\u4e2d\uff0c\u56db\u987e\u65e0\u4eba\uff0c\u6025\u5fd9\u8eb2\u8eb2\u95ea\u95ea\uff0c\u56de \u5230\u81ea\u5df1\u623f\u4e2d\uff0c\u8001\u5de5\u4eba\u4e01\u798f\u5c1a\u719f\u7761\u672a\u9192\uff0c\u623f\u4e2d\u4e00\u4e2a\u4e03\u516b\u5c81\u7684\u5973\u5b69\u5b50\u6781\u4e3a\u8b66\u9192\uff0c\u542c\u5230\u58f0\u606f\u3002\u7741\u5f00 \u4e00\u53cc\u5c0f\u773c\u73e0\uff0c\u5706\u5706\u7684\u773c\u73e0\u5c31\u4f3c\u9ed1\u591c\u7684\u5b9d\u77f3\uff0c\u53d1\u51fa\u6b22\u60a6\u7684\u773c\u5149\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u4e00\u4e86\u628a\u5c06\u949f\u4e07\u5802\u62b1 \u4f4f\uff0c\u8df3\u8dc3\u95ee\u9053\uff1a\u201c\u949f\u4f2f\u4f2f\uff0c\u4f60\u56de\u6765\u4e86\uff1f\u7fb9\u5c27\u54e5\u54e5\u5462\uff1f\u6211\u60f3\u5f97\u4f60\u4eec\u597d\u82e6\u5440\uff01\u201d\u949f\u4e07\u5802\u6025\u5fd9\u5618\u58f0 \u8bf4\u9053\uff1a\u201c\u7433\u513f\uff0c\u7981\u58f0\uff01\u8eb2\u5230\u623f\u91cc\u53bb\u5427\uff01\u201d \u3000\u3000\u8fd9\u5973\u5b69\u5b50\u5c31\u662f\u51af\u5bb6\u7684\u5b6a\u751f\u5b50\u51af\u7433\u3002\u5979\u5468\u5c81\u4e4b\u591c\uff0c\u7ed9\u949f\u4e07\u5802\u6551\u51fa\uff08\u8be6\u89c1\u7b2c\u4e00\u56de\uff09\u5e26\u5230\u5e74 \u5bb6\uff0c\u970e\u5ffd\u8fc7\u4e86\u516d\u5e74\u6709\u591a\uff0c\u73b0\u5728\u5df2\u662f\u516b\u5c81\u7684\u5973\u5a03\u513f\u4e86\u3002\u949f\u4e07\u5802\u7cbe\u901a\u836f\u6027\uff0c\u6bcf\u65e5\u7528\u836f\u6c34\u7ed9\u5979\u6d17 \u6fa1\uff0c\u4f20\u5979\u6b66\u827a\uff0c\u6240\u4ee5\u5979\u9aa8\u9abc\u51dd\u5b9e\uff0c\u8033\u806a\u76ee\u660e\uff0c\u8eab\u624b\u77eb\u6377\uff0c\u7ec3\u6210\u4e86\u6b63\u5b97\u7684\u7ae5\u5b50\u529f\uff0c\u6b64\u523b\uff0c\u5979\u89c1 \u949f\u4e07\u5802\u5982\u6b64\u614c\u5f20\uff0c\u5c0f\u5c0f\u7684\u5fc3\u7075\u4e5f\u4e0d\u7981\u9707\u6817\u8d77\u6765\uff0c\u7741\u5927\u773c\u775b\u95ee\u9053\uff1a\u201c\u4f2f\u4f2f\uff0c\u662f\u4ec0\u4e48\u4e8b\u554a\uff1f\u201d\u949f \u4e07\u5802\u9053\uff1a\u201c\u6709\u574f\u4eba\u6765\uff0c\u7b49\u4e0b\u6211\u4e0e\u574f\u4eba\u6253\u6597\uff0c\u4e0d\u7ba1\u6211\u6253\u8d62\u6253\u8f93\uff0c\u574f\u4eba\u672a\u8d70\uff0c\u4f60\u5c31\u4e0d\u8981\u51fa\u6765\u3002\u201d \u51af\u7433\u5728\u6795\u5934\u4e0b\u4e00\u6478\uff0c\u6478\u51fa\u4e24\u53e3\u4e09\u5bf8\u957f\u7684\u5c0f\u5200\uff0c\u9f13\u816e\u8bf4\u9053\uff1a\u201c\u574f\u4eba\u6765\u4e86\uff0c\u6211\u62ff\u8fd9\u4e2a\u6253\u4ed6\u3002\u201d\u949f \u4e07\u5802\u9762\u4e0a\u53d8\u8272\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u542c\u4f2f\u4f2f\u7684\u8bdd\uff0c\u4f2f\u4f2f\u4ee5\u540e\u6c38\u4e0d\u7406\u4f60\u3002\u201d\u51af\u7433\u4ece\u672a\u89c1\u201c\u4f2f\u4f2f\u201d \u53d1\u8fc7\u813e\u6c14\uff0c\u5413\u5f97\u5c0f\u8138\u4e00\u7ef7\uff0c\u773c\u6ce2\u6b32\u6655\uff0c\u949f\u4e07\u5802\u5728\u5979\u8033\u8fb9\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u4f60\u4e56\u4e56\u542c\u8bdd\uff0c\u522b\u4f5c\u58f0 \u4e86\u3002\u201d\u628a\u5979\u63a8\u8fdb\u91cc\u623f\u3002\u51af\u7433\u773c\u775b\u91cc\u8574\u85cf\u7740\u56f0\u60d1\u4e0e\u60ca\u5947\uff0c\u5706\u5706\u7684\u773c\u73e0\u5c3d\u7785\u7740\u949f\u4e07\u5802\uff0c\u949f\u4e07\u5802\u4fa7 \u8033\u4e00\u542c\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u5ffd\u7136\u53c8\u628a\u51af\u7433\u62c9\u4e86\u51fa\u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u672c\u6765\u6211\u4e0d\u60f3\u628a\u4f60\u7684\u6765\u5386\u8fc7 \u65e9\u8bf4\u7ed9\u4f60\u77e5\uff0c\u73b0\u5728\u903c\u7740\u8981\u8bf4\u7ed9\u4f60\u542c\u4e86\u3002\u5b69\u5b50\uff0c\u4f60\u5e76\u4e0d\u59d3\u5e74\uff0c\u4f60\u662f\u59d3\u51af\u7684\u3002\u4f60\u7684\u7236\u4eb2\u65e9\u6b7b\u53bb \u4e86\uff0c\u6b7b\u5f97\u5f88\u60e8\uff0c\u4f60\u7684\u6bcd\u4eb2\u751f\u6b7b\u672a\u77e5\uff0c\u4f60\u8fd8\u6709\u4e00\u4e2a\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\u3001\u9ed1\u591c\u91cc\u7a81\u7136\u4f20\u6765\u4e00\u58f0\u602a \u5578\uff0c\u949f\u4e07\u5802\u5413\u5f97\u5c06\u672a\u8bf4\u5b8c\u7684\u8bdd\u541e\u4e86\u56de\u53bb\uff0c\u5c06\u51af\u7433\u4e00\u628a\u63a8\u5165\u91cc\u623f\u3002 \u3000\u3000\u602a\u58f0\u66f3\u7a7a\uff0c\u7531\u8fdc\u800c\u8fd1\u3002\u949f\u4e07\u5802\u7ed3\u675f\u505c\u5f53\uff0c\u5df2\u542c\u5f97\u95e8\u5916\u9634\u6c89\u6c89\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u949f\u4e07\u5802\uff0c\u4f60 \u8fd8\u4e0d\u51fa\u6765\uff0c\u8981\u6211\u6253\u95e8\u8fdb\u53bb\u5417\uff1f\u201d\u949f\u4e07\u5802\u51b7\u7b11\u4e00\u58f0\uff0c\u500f\u7684\u62bd\u5f00\u95e8\u95e9\uff0c\u4e24\u67c4\u98de\u5200\u8131\u624b\u800c\u51fa\uff0c\u95ea\u5728 \u95e8\u540e\u3002\u5916\u9762\u4e00\u58f0\u602a\u7b11\u3002\u98de\u5200\u98de\u4e86\u56de\u6765\uff0c\u63d2\u5728\u5899\u4e0a\uff0c\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u548c\u5927\u529b\u795e\u9b54\u8428\u5929\u90fd\u53cc\u53cc\u62a2 \u8fdb\u5c4b\u5185\u3002 \u3000\u3000\u8428\u5929\u523a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c06\u8fd1\u5eff\u5e74\u672a\u89c1\uff0c\u4e0d\u56fe\u4eca\u65e5\u76f8\u9022\u3002\u201d\u949f\u4e07\u5802\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u949f\u67d0\u5019\u6559\u5df2 \u4e45\uff0c\u8fd9\u91cc\u4e0d\u591f\u5730\u65b9\u65bd\u5c55\uff0c\u8bf7\u5230\u82b1\u56ed\u5916\u53bb\u3002\u201d\u8428\u5929\u523a\u4e00\u58f0\u602a\u7b11\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u9009\u62e9\u6b7b\u5730 \u4e48\uff1f\u201d\u53cc\u624b\u4e00\u5f20\uff0c\u5341\u6307\u6307\u7532\u957f\u53ef\u76c8\u5c3a\uff0c\u5f39\u4e86\u51fa\u6765\uff0c\u5b9b\u5982\u5341\u67c4\u5c16\u5200\uff0c\u9f50\u5411\u949f\u4e07\u5802\u63d2\u5230\u3002\u949f\u4e07\u5802 \u8eab\u56de\u6b65\u8f6c\uff0c\u9752\u94a2\u5251\u5411\u4e0a\u4e00\u5c01\uff0c\u8eab\u5f62\u4e00\u65cb\uff0c\u5251\u631f\u5bd2\u5149\uff0c\u5411\u654c\u4eba\u8170\u808b\u65a9\u53bb\u3002\u8428\u5929\u523a\u8eab\u5f62\u4e00\u7eb5\uff0c\u7ffb \u51fa\u95e8\u5916\uff0c\u5c31\u5728\u95e8\u5916\u7684\u5ead\u9662\u4e2d\u7ad9\u5b9a\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u8fd8\u4e0d\u574f\uff0c\u518d\u6765\uff0c\u518d\u6765\uff01\u201d\u949f\u4e07\u5802\u81ea\u5fd6\uff0c\u4eca \u665a\u7edd\u96be\u9003\u8fc7\uff0c\u4e5f\u5c31\u5904\u4e4b\u6cf0\u7136\uff0c\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u7a7f\u7a97\u800c\u51fa\u3002\u8428\u5929\u90fd\u4e00\u58f0\u5927\u543c\uff0c\u53cc\u638c\u75be\u53d1\uff0c\u4e00 \u62db\u201c\u6392\u5c71\u5012\u6d77\u201d\uff0c\u5288\u9762\u6253\u6765\uff0c\u638c\u98ce\u5341\u5206\u52b2\u75be\uff0c\u949f\u4e07\u5802\u9752\u94a2\u5251\u5f80\u5916\u4e00\u5c01\uff0c\u5de6\u81c2\u4e00\u6325\uff0c\u9a78\u98df\u4e2d\u4e8c \u6307\uff0c\u7167\u8428\u5929\u90fd\u201c\u5173\u5143\u7a74\u201d\u70b9\u53bb\uff0c\u8428\u5929\u90fd\u5927\u7b11\uff1a\u201c\u8001\u5b50\u6015\u4f60\u70b9\u7a74\uff1f\u201d\u5de6\u638c\u6539\u638c\u4e3a\u62f3\uff0c\u4ecd\u7136\u5288 \u4e0b\uff0c\u90a3\u6599\u949f\u4e07\u5802\u5eff\u5e74\u82e6\u7ec3\uff0c\u529f\u529b\u5927\u8fdb\uff0c\u5173\u5143\u7a74\u53c8\u662f\u4eba\u8eab\u4e09\u5341\u516d\u9053\u5927\u7a74\u4e4b\u4e00\uff0c\u8428\u5929\u90fd\u5403\u4ed6\u4e00 \u70b9\uff0c\u7a81\u7136\u4e00\u9635\u9178\u75db\uff0c\u62f3\u672a\u843d\u4e0b\uff0c\u5df2\u611f\u65e0\u529b\uff0c\u949f\u4e07\u5802\u5251\u62db\u4f55\u7b49\u5feb\u6377\uff0c\u9752\u94a2\u5251\u6a2a\u91cc\u4e00\u5212\uff0c\u4eae\u6676\u6676 \u7684\u5251\u5c16\u5df2\u5212\u5230\u8428\u5929\u90fd\u9762\u95e8\u3002 \u3000\u3000\u8428\u5929\u523a\u8eab\u6cd5\u597d\u5feb\uff0c\u89c1\u5f1f\u5f1f\u5371\u6025\uff0c\u5f13\u8eab\u4e00\u8dc3\uff0c\u75be\u5982\u98de\u7bad\uff0c\u5de6\u624b\u628a\u8428\u5929\u90fd\u62c9\u5f00\uff0c\u53f3\u638c\u7528\u201c\u526a \u6885\u6307\u201d\u5f80\u949f\u4e07\u5802\u6301\u5251\u7684\u772f\u95e8\u4e0a\u5212\u6765\u3002\u4e24\u4eba\u90fd\u662f\u8fc5\u5982\u7535\u706b\uff0c\u949f\u4e07\u5802\u65e0\u6687\u4f24\u654c\uff0c\u9752\u94a2\u5251\u75be\u53d1\u75be \u6536\uff0c\u62a4\u7740\u8eab\u8eaf\uff0c\u6a2a\u8dc3\u51fa\u4e94\u516d\u6b65\u5916\u3002\u8428\u5929\u523a\u671d\u8428\u5929\u90fd\u7684\u201c\u6108\u6c14\u7a74\u201d\u4e00\u62cd\uff0c\u8bf4\u9053\uff1a\u201c\u8363\u5f1f\uff0c\u4f60\u5c06 \u606f\u4e9b\u513f\uff0c\u7b49\u4f1a\u518d\u6253\uff0c\u53ef\u4e0d\u8981\u8fd9\u6837\u83bd\u649e\u4e86\u3002\u201d\u8428\u5929\u90fd\u7ed9\u949f\u4e07\u5802\u7528\u91cd\u624b\u6cd5\u70b9\u4e2d\uff0c\u5e78\u662f\u7ec3\u5c31\u94dc\u76ae\u94c1 \u9aa8\uff0c\u5426\u5219\u5f53\u573a\u5c31\u8981\u6bd9\u547d\u3002\u5f53\u4e0b\u4e0d\u6562\u901e\u5f3a\uff0c\u501a\u5728\u5899\u8fb9\uff0c\u8c03\u5300\u547c\u5438\uff0c\u884c\u8840\u89e3\u7a74\u3002 \u3000\u3000\u949f\u4e07\u5802\u4f24\u4e86\u4e00\u4eba\uff0c\u7a98\u52bf\u6e10\u89e3\uff0c\u9752\u94a2\u5251\u98de\u4e91\u63a3\u7535\uff0c\u55d6\uff0c\u55d6\uff0c\u55d6\uff0c\u8fde\u73af\u75be\u53d1\uff0c\u8428\u5929\u523a\u8eab\u5f62\u4e00 \u7eb5\uff0c\u84e6\u5730\u91cc\u51ed\u7a7a\u817e\u8d77\uff0c\u8eab\u8eaf\u626b\u7740\u6811\u68a2\uff0c\u5411\u4e00\u68f5\u957f\u5728\u5ead\u5fc3\u7684\u6811\u6748\u5b50\u4e00\u62c2\uff0c\u84e6\u5730\u8eab\u8eaf\u659c\u7740\u98de\u51fa\u4e24 \u4e08\u6709\u591a\uff0c\u949f\u4e07\u5802\u5389\u58f0\u559d\u9053\uff1a\u201c\u90a3\u91cc\u8d70\uff01\u201d\u9f99\u5f62\u4e00\u5f0f\uff0c\u5251\u5149\u95ea\u95ea\uff0c\u638c\u98ce\u547c\u547c\uff0c\u6377\u4f3c\u7075\u733f\uff0c\u75be\u62a2 \u4e0a\u53bb\uff0c\u54ea\u6599\u8428\u5929\u523a\u7ec3\u5c31\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u8eab\u8eaf\u7adf\u5728\u534a\u7a7a\u4e2d\u4e00\u5c48\u4e00\u4f38\uff0c\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u638c\u968f \u8eab\u7ffb\uff0c\u5341\u6307\u5229\u7532\uff0c\u51cc\u7a7a\u523a\u4e0b\uff0c\u53ea\u542c\u5f97\u55e4\u7684\u4e00\u58f0\uff0c\u8428\u5929\u523a\u7684\u8863\u8896\u88ab\u949f\u4e07\u5802\u5229\u5251\u5272\u65ad\uff0c\u949f\u4e07\u5802\u7684 \u5de6\u80a9\uff0c\u7adf\u4e5f\u4e2d\u4e86\u4e00\u6293\uff0c\u5e78\u800c\u95ea\u8eb2\u5f97\u8bc0\uff0c\u672a\u88ab\u6293\u65ad\u7b4b\u9aa8\u3002\u8428\u5929\u523a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e00\u5251\u6362\u4e00\u6293\uff0c\u54b1 \u4eec\u603b\u7b97\u626f\u5e73\u4e86\u3002\u201d\u949f\u4e07\u5802\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u8fd8\u8d5a\u4e86\u4f60\u534a\u53ea\u8896\u5b50\u3002\u201d\u8428\u5929\u523a\u602a\u7b11\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211 \u518d\u8ba8\u5229\u606f\u3002\u201d\u8eab\u5f62\u843d\u5730\u4fbf\u8d77\uff0c\u518d\u5c55\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u5411\u949f\u4e07\u5802\u6251\u6765\u3002 \u3000\u3000\u7ea6\u5eff\u5e74\u524d\uff0c\u949f\u4e07\u5802\u81ea\u897f\u57df\u6f2b\u6e38\u5f52\u6765\uff0c\u5728\u9752\u6d77\u7684\u4e4c\u853d\u5580\u4ec0\u6e56\u8fb9\uff0c\u66fe\u4e0e\u8428\u5929\u523a\u76f8\u9047\uff0c\u5f53\u65f6\u949f \u4e07\u5802\u76db\u5e74\u6c14\u58ee\uff0c\u77e5\u4ed6\u662f\u6c5f\u6e56\u4e0a\u75db\u6068\u7684\u5996\u90aa\uff0c\u5f53\u5373\u62bd\u5251\u4e0e\u4ed6\u76f8\u6597\uff0c\u5927\u6218\u534a\u65e5\uff0c\u8428\u5929\u523a\u4e2d\u4e86\u4e00 \u5251\uff0c\u72fc\u72c8\u9003\u53bb\uff0c\u56e0\u6b64\u6709\u8fd9\u4e00\u5251\u6362\u4e00\u6293\u7684\u8bf4\u8bdd\u3002 \u3000\u3000\u5eff\u5e74\u540e\u518d\u5ea6\u91cd\u9022\uff0c\u4e24\u756a\u5267\u6597\uff0c\u8428\u5929\u523a\u5df2\u7ec3\u5c31\u4e86\u201c\u732b\u9e70\u722a\u201d\u7684\u72ec\u95e8\u6b66\u529f\uff0c\u6251\u51fb\u51cc\u5389\uff0c\u8fc5\u75be \u65e0\u6bd4\uff0c\u949f\u4e07\u5802\u548c\u4ed6\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u4ee5\u5feb\u6597\u5feb\uff0c\u7adf\u7136\u6e10\u611f\u4e0d\u652f\uff0c\u8428\u5929\u523a\u5f97\u610f\u6d0b\u6d0b\uff0c\u6293\u3001\u6253\u3001 \u5288\u3001\u6495\uff0c\u8d8a\u53d1\u51f6\u72e0\uff0c\u949f\u4e07\u5802\u6c89\u7740\u5e94\u4ed8\uff0c\u84e6\u5730\u5251\u62db\u4e00\u53d8\uff0c\u4e1c\u6307\u897f\u5212\uff0c\u624b\u4e0a\u5b9b\u5982\u6362\u7740\u5343\u65a4\u91cd\u7269\u4e00 \u6837\uff0c\u663e\u5f97\u5f88\u662f\u5403\u529b\uff0c\u5251\u62db\u6bd4\u524d\u6162\u4e86\uff0c\u4f46\u5374\u662f\u5251\u5149\u64a9\u7ed5\uff0c\u5c31\u4f3c\u5728\u8eab\u5b50\u5468\u56f4\u7b51\u8d77\u4e86\u94c1\u58c1\u94dc\u5899\u3002\u8981 \u77e5\u949f\u4e07\u5802\u4e43\u65e0\u6781\u5251\u7684\u5ae1\u4f20\uff0c\u8fd9\u624b\u5251\u6cd5\u4e43\u662f\u5085\u9752\u4e3b\u5f53\u5e74\u6151\u4f0f\u6c5f\u6e56\u7684\u6210\u540d\u5251\u6cd5\uff0c\u540d\u4e3a\u201c\u6d6a\u5143\u65e0\u6781 \u5251\u201d\uff0c\u653b\u5b88\u4e24\u5229\uff0c\u6536\u5219\u62a4\u7740\u5168\u8eab\uff0c\u653e\u5219\u516b\u65b9\u9f50\u5230\uff0c\u8428\u5929\u523a\u7684\u201c\u732b\u9e70\u722a\u201d\u867d\u7136\u5389\u5bb3\u975e\u51e1\uff0c\u7adf\u662f \u6251\u4e0d\u8fdb\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u8d8a\u6597\u8d8a\u70c8\uff0c\u7ea6\u8fc7\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u4ecd\u662f\u672a\u5206\u9ad8\u4e0b\u3002\u6b63\u6fc0\u6597\u95f4\uff0c\u949f\u4e07\u5802\u7a81\u611f\u80a9\u5934\u5fae\u5fae\u9ebb \u75d2\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u77e5\u662f\u8428\u5929\u523a\u722a\u4e0a\u6709\u6bd2\u3002\u4ed7\u7740\u5185\u529f\u6df1\u6e5b\uff0c\u6025\u5fd9\u8fd0\u6c14\u62b5\u5fa1\uff0c\u540c\u65f6\u5251\u62db\u7531\u5b88\u53d8\u653b\uff0c \u60f3\u628a\u516b\u81c2\u795e\u9b54\u65e9\u70b9\u51fb\u9000\uff0c\u4ed7\u7740\u81ea\u5df1\u6df1\u901a\u836f\u6027\uff0c\u518d\u56fe\u89e3\u6551\u4e4b\u65b9\u3002\u90a3\u6599\u8428\u5929\u523a\u867d\u88ab\u903c\u5f97\u9000\u540e\uff0c\u5374 \u53c8\u5f3a\u653b\u4e0a\u6765\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u949f\u4e07\u5802\u4f60\u4e2d\u4e86\u6bd2\u722a\uff0c\u8fd8\u60f3\u6d3b\u547d\u4e48\uff1f\u201d \u3000\u3000\u949f\u4e07\u5802\u52c3\u7136\u5927\u6012\uff0c\u5f3a\u5fcd\u7740\u6c14\uff0c\u9752\u94a2\u5251\u731b\u7684\u4e00\u6447\uff0c\u4f7f\u51fa\u201c\u516b\u65b9\u98ce\u96e8\u4f1a\u4e2d\u5dde\u201d\u7684\u5251\u6cd5\uff0c\u53ea\u89c1 \u94f6\u5149\u531d\u5730\uff0c\u7d2b\u7535\u98de\u7a7a\uff0c\u56db\u9762\u516b\u65b9\uff0c\u90fd\u662f\u5251\u5149\u4eba\u5f71\u3002\u8428\u5929\u523a\u77e5\u4ed6\u4ee5\u6b7b\u76f8\u62fc\uff0c\u75be\u9000\u51e0\u6b65\u3002\u949f\u4e07\u5802 \u53f1\u54a4\u4e00\u58f0\uff0c\u5251\u62db\u4f3c\u5de6\u5ffd\u53f3\uff0c\u5251\u5c16\u4e00\u65cb\uff0c\u8428\u5929\u523a\u75be\u9000\u65e0\u53ca\uff0c\u624b\u8155\u4e0a\u7adf\u7ed9\u5212\u4e86\u4e00\u9053\u4f24\u53e3\u3002\u949f\u4e07\u5802 \u4e58\u80dc\u8ffd\u51fb\uff0c\u90a3\u501a\u5728\u5899\u8fb9\u7684\u5927\u529b\u795e\u9b54\u8428\u5929\u90fd\u84e6\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u66b4\u559d\u4e00\u58f0\uff0c\u9707\u5f97\u6ee1\u56ed\u5b50\u6c99\u98de\u77f3\u8d70\uff0c \u539f\u6765\u4ed6\u6b47\u606f\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u65e9\u5df2\u6c14\u529f\u6062\u590d\uff0c\u8fd9\u4e00\u8dc3\u4e00\u559d\uff0c\u6c14\u52bf\u715e\u662f\u60ca\u4eba\uff0c\u949f\u4e07\u5802\u6014\u4e86\u4e00\u6014\u3002\u53ea \u89c1\u4ed6\u8131\u4e86\u4e0a\u8863\uff0c\u9732\u51fa\u6bdb\u8338\u8338\u7684\u80f8\u819b\uff0c\u8df3\u5230\u5ead\u5fc3\uff0c\u53cc\u624b\u62b1\u7740\u90a3\u9897\u5927\u6811\uff0c\u559d\u9053\uff1a\u201c\u8d77\uff01\u201d\u628a\u90a3\u9897 \u5927\u6811\u8fde\u6839\u62d4\u4e86\u8d77\u6765\uff0c\u5411\u949f\u4e07\u5802\u5377\u5730\u626b\u53bb\u3002\u949f\u4e07\u5802\u8eab\u5f62\u6025\u8d77\uff0c\u811a\u70b9\u6811\u8eab\uff0c\u9752\u94a2\u5251\u5411\u5927\u529b\u795e\u9b54\u75be \u523a\uff0c\u90a3\u77e5\u80cc\u540e\u5fae\u98ce\u98d2\u7136\uff0c\u8428\u5929\u523a\u53c8\u5df2\u4e58\u673a\u88ad\u5230\u3002 \u3000\u3000\u8fd9\u9897\u6811\u957f\u8fbe\u4e09\u4e08\uff0c\u4ece\u9662\u5b50\u8fd9\u8fb9\u53ef\u626b\u5230\u90a3\u8fb9\uff0c\u949f\u4e07\u5802\u867d\u4ed7\u7740\u8f7b\u7075\u7684\u8eab\u6cd5\uff0c\u7a9c\u9ad8\u7eb5\u4f4e\uff0c\u817e\u632a \u95ea\u5c55\uff0c\u5230\u5e95\u4e0d\u80fd\u4e0d\u53d7\u4e86\u6781\u5927\u7684\u5a01\u80c1\uff0c\u5e78\u5f97\u6811\u8eab\u7c97\u5927\uff0c\u8f6c\u52a8\u4e0d\u4fbf\uff0c\u8981\u4e0d\u7136\u66f4\u662f\u96be\u5f53\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u4e3b\u5ba2\u6613\u52bf\uff0c\u8428\u5929\u523a\u51f6\u731b\u6251\u51fb\uff0c\u7d27\u7d27\u76ef\u7740\u949f\u4e07\u5802\u7684\u8eab\u5f62\u3002\u4e1d\u6beb\u4e5f\u4e0d\u653e\u677e\uff0c\u6218\u4e86\u7247 \u523b\uff0c\u949f\u4e07\u5802\u624b\u81c2\u53c8\u4e2d\u4e86\u4e00\u6293\uff0c\u6df1\u5165\u808c\u8089\u9ed1\u8272\u7684\u8840\u6db2\u6cae\u6cae\u6d41\u51fa\uff0c\u540c\u65f6\u80a9\u5934\u8d8a\u53d1\u9ebb\u75d2\u96be\u8010\uff0c\u8428\u5929 \u523a\u53eb\u9053\uff1a\u201c\u949f\u4e07\u5802\uff0c\u4f60\u8fd8\u4e0d\u5feb\u5f03\u5251\u6295\u964d\uff01\u201d\u949f\u4e07\u5802\u54ac\u5b9e\u7259\u6839\uff0c\u7a81\u7136\u63d2\u5251\u5f52\u9798\uff0c\u8d81\u7740\u5927\u6811\u626b\u6765 \u4e4b\u52bf\uff0c\u53cc\u8db3\u4e00\u62b5\u6811\u6839\uff0c\u8eab\u5b50\u98de\u51fa\u53bb\uff0c\u964d\u843d\u53f0\u9636\uff0c\u8428\u5929\u523a\u559d\u58f0\uff1a\u201c\u90a3\u91cc\u8d70\uff01\u201d\u53cc\u638c\u4e00\u7a7f\uff0c\u95ea\u7535 \u822c\u7684\u6251\u4e0a\uff0c\u949f\u4e07\u5802\u56de\u5934\u5927\u559d\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u53cc\u624b\u8fde\u626c\uff0c\u53f3\u624b\u516d\u628a\u98de\u5200\uff0c\u98de\u5411\u8428\u5927\u523a\uff0c\u5de6\u624b\u516d \u628a\u98de\u5200\uff0c\u98de\u5411\u8428\u5929\u90fd\u3002\u8fd9\u593a\u547d\u795e\u5200\uff0c\u6dec\u6709\u5267\u6bd2\uff0c\u4e43\u662f\u949f\u4e07\u5802\u5e73\u751f\u7edd\u6280\uff0c\u975e\u5230\u6700\u5371\u9669\u65f6\uff0c\u4e0d\u80af \u65bd\u653e\u3002\u8428\u5929\u523a\u51cc\u7a7a\u4e00\u8dc3\uff0c\u5728\u534a\u7a7a\u4e2d\u5f3a\u626d\u8eab\u8eaf\u3002\u4e24\u67c4\u98de\u5200\u8d34\u7740\u811a\u677f\u98de\u8fc7\uff0c\u4e24\u53e3\u98de\u5200\u8d34\u7740\u5de6\u808b\u98de \u8fc7\uff0c\u8fd8\u6709\u4e24\u53e3\u98de\u5200\u8fce\u9762\u5c04\u6765\uff0c\u7ed9\u4ed6\u957f\u8896\u4e00\u62c2\u8dcc\u843d\u5c18\u57c3\u3002\u8428\u5929\u523a\u51fa\u5c3d\u5e73\u751f\u7edd\u6280\uff0c\u624d\u9003\u4e86\u98de\u5200\u63d2 \u4f53\u4e4b\u5371\uff0c\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u5c48\u4e00\u4f38\uff0c\u786c\u751f\u751f\u7684\u5c06\u8eab\u5b50\u5012\u7eb5\u56de\u53bb\u3002\u90a3\u4e00\u8fb9\u8428\u5929\u90fd\u62a1\u52a8 \u5927\u6811\uff0c\u56db\u67c4\u98de\u5200\u90fd\u5c04\u5165\u4e86\u6811\u4e2d\uff0c\u6ca1\u4e86\u950b\u5203\u3002\u53e6\u4e24\u67c4\u98de\u5200\uff0c\u5374\u4f3c\u4f1a\u62d0\u5f2f\u4e00\u6837\uff0c\u64e6\u7740\u6811\u76ae\uff0c\u7a81\u7136 \u4ece\u4e24\u8fb9\u5c04\u51fa\uff0c\u8428\u5929\u90fd\u624b\u62b1\u5927\u6811\uff0c\u95ea\u5c55\u4e0d\u7075\uff0c\u4e24\u808b\u7adf\u7ed9\u98de\u5200\u63d2\u5165\uff0c\u9976\u662f\u4ed6\u94dc\u76ae\u94c1\u9aa8\uff0c\u4e5f\u6321\u4e0d \u4f4f\uff0c\u75db\u5f97\u5927\u53eb\u4e00\u58f0\uff0c\u53cc\u624b\u5411\u524d\u4e00\u6254\uff0c\u90a3\u68f5\u5927\u6811\u76f4\u98de\u4e0a\u53f0\u9636\uff0c\u949f\u4e07\u5802\u6025\u95ea\u5f00\u65f6\uff0c\u731b\u542c\u5f97\u9707\u5929\u4ef7 \u54cd\uff0c\u5927\u6811\u76f4\u98de\u5165\u5c4b\u5185\uff0c\u60f3\u662f\u649e\u5230\u4e86\u8336\u6848\u5e8a\u67dc\uff0c\u54d7\u5566\u5566\u4e00\u7247\u4ea4\u54cd\u3002\u5728\u7269\u4ef6\u788e\u88c2\u3001\u5bb6\u5177\u78b0\u649e\u58f0 \u4e2d\uff0c\u4e00\u4e2a\u5973\u5b69\u5b50\u7684\u9a87\u53eb\u58f0\uff0c\u7a81\u7136\u4f20\u4e86\u51fa\u6765\u3002 \u3000\u3000\u949f\u4e07\u5802\u5927\u9a87\uff0c\u6b63\u60f3\u6251\u8ff8\u5c4b\u5185\uff0c\u90a3\u77e5\u4e2d\u4e86\u722a\u4f24\u7684\u53f3\u81c2\uff0c\u56e0\u7528\u529b\u8fc7\u751a\uff0c\u53c8\u731b\u5403\u4e00\u60ca\uff0c\u6563\u4e86\u771f \u6c14\uff0c\u7adf\u7136\u4e3e\u4e0d\u8d77\u6765\u3002\u90a3\u8428\u5929\u90fd\u4e2d\u4e86\u4e24\u5200\uff0c\u51f6\u6027\u5927\u53d1\uff0c\u7adf\u7136\u626d\u8f6c\u624b\u81c2\u628a\u4e24\u53e3\u593a\u547d\u795e\u529b\u786c\u62d4\u51fa \u6765\uff0c\u4e00\u8dc3\u51fb\u524d\uff0c\u628a\u949f\u4e07\u5802\u62b1\u7740\uff0c\u4e92\u76f8\u626d\u6253\uff0c\u6eda\u843d\u53f0\u9636\uff0c\u800c\u8428\u5929\u523a\u5374\u4e00\u58f0\u72de\u7b11\uff0c\u8dc3\u5165\u5c4b\u5185\u3002\u731b \u7684\u773c\u775b\u4e00\u4eae\uff0c\u53ea\u89c1\u4e00\u4e2a\u5973\u5b69\u5b50\u5750\u5728\u5730\u4e0b\uff0c\u5b9b\u5982\u7c89\u96d5\u7389\u7422\uff0c\u975e\u5e38\u53ef\u7231\u3002\u6025\u58f0\u53eb\u9053\uff1a\u201c\u554a\uff0c\u71d5 \u513f\uff0c\u539f\u6765\u4f60\u5728\u8fd9\u91cc\uff0c\u4f60\u7684\u51e0\u4e2a\u4e49\u7236\uff0c\u90fd\u6025\u7740\u627e\u4f60\u5462\uff01\u4f60\u7ed9\u574f\u4eba\u52ab\u6765\uff0c\u4e0d\u5bb3\u6015\u5417\uff1f\u201d\u51af\u7433\u7741\u7740 \u4e24\u53ea\u773c\u775b\uff0c\u5341\u5206\u56f0\u60d1\uff0c\u4e0d\u77e5\u9053\u4ed6\u8bf4\u7684\u4ec0\u4e48\uff0c\u5916\u9762\u949f\u4e07\u5802\u542c\u4e86\uff0c\u4e5f\u6781\u4e3a\u5947\u8be7\uff0c\u8428\u5929\u523a\u53c8\u9053\uff1a \u201c\u771f\u53ef\u601c\uff0c\u770b\u4f60\u7ed9\u6298\u78e8\u6210\u4ec0\u4e48\u6837\u513f\uff0c\u624d\u4e0d\u8fc7\u4e00\u5e74\uff0c\u5c31\u4ec0\u4e48\u90fd\u8bb0\u4e0d\u5f97\u4e86\uff0c\u8fde\u6211\u4e5f\u8ba4\u4e0d\u51fa\u6765 \u5417\uff1f\u201d\u4f38\u624b\u4fbf\u62b1\uff0c\u51af\u7433\u5c0f\u624b\u5ffd\u626c\uff0c\u4e24\u67c4\u5c0f\u5200\u7a81\u7136\u5c04\u51fa\uff0c\u8428\u5929\u523a\u90a3\u91cc\u6599\u5230\u6709\u8fd9\u4e00\u7740\uff0c\u76f8\u8ddd\u53c8 \u8fd1\uff0c\u51b7\u4e0d\u53ca\u9632\uff0c\u4e24\u53e3\u98de\u5200\u5c04\u4e2d\u80f8\u819b\uff0c\u5e78\u597d\u51af\u7433\u529b\u5c0f\uff0c\u4e0d\u7136\u5c31\u8981\u63d2\u5165\u5fc3\u623f\uff1b\u8428\u5929\u523a\u8fd0\u529b\u4e00\u5f39\uff0c \u628a\u4e24\u53e3\u98de\u5200\u5f39\u51fa\uff0c\u957f\u81c0\u4e00\u635e\uff0c\u628a\u51af\u7433\u80cc\u5728\u80cc\u540e\uff0c\u51af\u7433\u5f20\u53e3\u5927\u53eb\uff0c\u4f7f\u5c3d\u5403\u5976\u4e4b\u529b\u6323\u624e\uff0c\u5374\u5df2\u52a8 \u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u53f0\u9636\u4e0b\u949f\u4e07\u5802\u548c\u8428\u5929\u90fd\u4e92\u76f8\u626d\u6253\uff0c\u8428\u5929\u90fd\u529b\u5927\u5982\u864e\uff0c\u4f46\u949f\u4e07\u5802\u5374\u5185\u529f\u6df1\u6e5b\uff0c\u521d\u65f6\u867d\u662f\u8428 \u5929\u90fd\u5360\u5c3d\u4e0a\u98ce\uff0c\u538b\u5728\u4ed6\u8eab\u4e0a\uff0c\u4e3e\u8d77\u94c1\u62f3\uff0c\u624d\u6253\u5f97\u4e24\u62f3\uff0c\u98de\u5200\u7684\u6bd2\u6c14\u5df2\u5728\u4f53\u5185\u884c\u6563\u5f00\u6765\uff0c\u53ea\u89c9 \u53e3\u4e2d\u7126\u6e34\uff0c\u5934\u8111\u6655\u7729\uff0c\u53eb\u5f97\u53e5\u201c\u54e5\u54e5\u5feb\u6765\u201d\uff0c\u4eba\u5df2\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u949f\u4e07\u5802\u53d7\u6bd2\u4e5f\u662f\u4e0d\u8f7b\uff0c\u4ed7\u7740\u6df1\u6e5b\u7684\u5185\u529f\uff0c\u624d\u652f\u6301\u4e86\u8fd9\u4e48\u4e9b\u65f6\u5019\uff0c\u6b64\u9645\u6cb9\u5c3d\u706f\u67af\uff0c\u5df2\u662f\u6d51 \u8eab\u9ebb\u8f6f\u65e0\u529b\u3002\u8428\u5929\u523a\u80cc\u7740\u51af\u7433\uff0c\u5927\u8e0f\u6b65\u8d70\u4e86\u51fa\u6765\u3002\u949f\u4e07\u5802\u6697\u53eb\u4e00\u58f0\uff1a\u201c\u6211\u547d\u4f11\u77e3\uff01\u201d\u4f0f\u5730\u4e00 \u6eda\uff0c\u6eda\u51fa\u4e24\u4e08\u5f00\u5916\uff0c\u8428\u5929\u523a\u89c1\u5f1f\u5f1f\u9762\u76ee\u9edd\u9ed1\uff0c\u77e5\u662f\u4e2d\u6bd2\u751a\u6df1\uff0c\u81ea\u5df1\u4f53\u5185\uff0c\u4e5f\u89c9\u6709\u4e9b\u7ede\u75db\uff0c\u53c8 \u6c14\u53c8\u6012\uff0c\u5706\u7741\u53cc\u76ee\uff0c\u4e00\u6b65\u6b65\u5411\u949f\u4e07\u5802\u884c\u6765\u3002\u949f\u4e07\u5802\u53cc\u62f3\u7d27\u63e1\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4f60\u518d\u884c\u4e09\u6b65\uff0c\u6211 \u5c31\u7528\u6bd2\u5200\u53d6\u4f60\u6027\u547d\uff01\u201d\u8428\u5929\u90fd\u5728\u660f\u8ff7\u4e2d\u542c\u5f97\u54e5\u54e5\u811a\u6b65\u58f0\uff0c\u6323\u624e\u8f6c\u52a8\uff0c\u5636\u58f0\u53eb\u9053\uff1a\u201c\u6211\u8981\u559d \u6c34\uff0c\u6c34\uff0c\u6c34\u2026\u2026\u201d\u8428\u5929\u523a\u80f8\u819b\u53d7\u4f24\uff0c\u66f4\u6015\u949f\u4e07\u5802\u7684\u98de\u5200\uff0c\u54ac\u7740\u7259\u6839\uff0c\u8fd0\u6c14\u62b5\u5fa1\u4f53\u5185\u6bd2\u6c14\uff0c\u5de6 \u624b\u4e00\u631f\uff0c\u628a\u8428\u5929\u90fd\u631f\u4e86\u8d77\u6765\uff0c\u8dc3\u4e0a\u5899\u5934\uff0c\u8df3\u51fa\u82b1\u56ed\u53bb\u4e86\u3002 \u3000\u3000\u949f\u4e07\u5802\u53cc\u62f3\u5f20\u5f00\uff0c\u677e\u4e86\u53e3\u6c14\u3002\u4ed6\u521a\u624d\u4f7f\u7684\u662f\u7a7a\u57ce\u4e4b\u8ba1\uff0c\u624b\u4e2d\u90a3\u91cc\u85cf\u6709\u4ec0\u4e48\u98de\u5200\uff1f\u5c31\u662f\u6709 \u98de\u5200\uff0c\u4ed6\u4e5f\u6ca1\u6c14\u529b\u518d\u53d1\u3002\u6b64\u9645\u53cc\u9b54\u5df2\u53bb\uff0c\u4ed6\u76ca\u89c9\u96be\u5f53\uff0c\u4ee5\u8098\u652f\u5730\uff0c\u4e00\u6b65\u6b65\u722c\u56de\u5c4b\u5185\u3002\u7275\u4e86\u4e00 \u5e8a\u68c9\u88ab\uff0c\u94fa\u5728\u5730\u4e0a\uff0c\u9694\u7edd\u5730\u6c14\uff0c\u5367\u5728\u4e0a\u9762\uff0c\u4f4e\u53eb\u4e86\u51e0\u58f0\uff1a\u201c\u4e01\u798f\uff0c\u4e01\u798f\uff01\u201d\u6ee1\u56ed\u5b50\u9759\u5bc2\u5bc2 \u7684\uff0c\u53ea\u6709\u79cb\u8749\u87cb\u87c0\u4e4b\u58f0\uff0c\u8fd9\u4f4d\u6c5f\u5357\u5251\u672f\u7684\u540d\u5bb6\uff0c\u65e0\u6781\u6d3e\u5ae1\u4f20\u7684\u5f1f\u5b50\uff0c\u864e\u76ee\u4e2d\u4e0d\u7531\u5f97\u6ef4\u51fa\u51e0\u70b9 \u773c\u6cea\uff0c\u4f4e\u58f0\u5524\u9053\uff1a\u201c\u7433\u513f\uff0c\u7433\u513f\uff01\u201d\u8fc7\u4e86\u4e00\u9635\uff0c\u53c8\u4f4e\u58f0\u5524\u9053\uff1a\u201c\u7fb9\u5c27\uff0c\u7fb9\u5c27\uff0c\u8fde\u4f60\u4e5f\u4e0d\u80fd\u6765 \u9001\u6211\u7684\u7ec8\u5417\uff1f\u201d\u4ed6\u6323\u624e\u7740\u60f3\u66ff\u81ea\u5df1\u653e\u8840\uff0c\u4f46\u5374\u662f\u529b\u4e0d\u4ece\u5fc3\uff0c\u8fd9\u65f6\u6bd2\u6c14\u4e0a\u51b2\uff0c\u660f\u7729\u66f4\u751a\uff0c\u9669\u4e9b \u5c31\u8981\u6655\u6b7b\u8fc7\u53bb\u3002\u4e00\u4e2a\u5ff5\u5934\uff0c\u7a81\u7136\u4ece\u8111\u6d77\u4e2d\u63a0\u8fc7\uff1a\u6211\u7684\u62f3\u7ecf\u5251\u8bc0\u5175\u6cd5\u533b\u4e66\uff0c\u8fd8\u672a\u66fe\u4f20\u7ed9\u7fb9\u5c27 \u5462\uff0c\u4e0d\u884c\uff0c\u6211\u600e\u6837\u4e5f\u5f97\u6323\u624e\u7740\u7b49\u4ed6\u56de\u6765\uff0c\u9f13\u8d77\u6c42\u751f\u7684\u610f\u5fd7\uff0c\u5f20\u53e3\u5728\u624b\u81c2\u4e00\u54ac\uff0c\u628a\u6bd2\u8840\u5438\u4e86\u51e0 \u53e3\u51fa\u6765\uff0c\u795e\u667a\u7a0d\u7a0d\u6e05\u9192\uff0c\u51c4\u7136\u53eb\u9053\uff1a\u201c\u7fb9\u5c27\uff0c\u4f60\u56de\u6765\u554a\uff01\u4f60\u600e\u4e48\u8fd8\u4e0d\u56de\u6765\u554a\uff01\u201d \u3000\u3000\u504c\u5927\u7684\u4e00\u4e2a\u82b1\u56ed\u9759\u4fcf\u6084\u7684\uff0c\u53ea\u6709\u7a7a\u6c14\u4e2d\u8361\u6f3e\u7740\u5fae\u5f31\u7684\u56de\u58f0\u3002\u949f\u4e07\u5802\u53f9\u4e86\u53e3\u6c14\uff0c\u5e74\u7fb9\u5c27\u7684 \u5f71\u5b50\u84e6\u7136\u4ece\u6cea\u5149\u4e2d\u6cdb\u51fa\uff0c\u201c\u8fd9\u662f\u4e00\u4e2a\u4f55\u5176\u987d\u76ae\u800c\u53c8\u4f55\u5176\u53ef\u7231\u7684\u5b69\u5b50\u554a\uff01\u201d\u4ed6\u60f3\u4e86\u81ea\u5df1\u662f\u600e\u6837 \u8d39\u5c3d\u5fc3\u8840\uff0c\u628a\u8fd9\u4e0d\u7f81\u7684\u91ce\u9a6c\u5957\u4e0a\u7b3c\u5934\uff0c\u662f\u600e\u6837\u4e0d\u7406\u53cb\u597d\u7684\u529d\u544a\uff0c\u5192\u7740\u57f9\u517b\u51fa\u4e00\u4ee3\u8885\u96c4\u7684\u5371 \u9669\uff0c\u8981\u628a\u4ed6\u8c03\u6559\u6210\u6587\u6b66\u53cc\u5168\u7684\u5c06\u76f8\u4e4b\u6750\u3002\u8fd9\u7a7a\u65f7\u7684\u5927\u82b1\u56ed\u4e5f\u662f\u81ea\u5df1\u8bbe\u8ba1\u7684\uff0c\u800c\u4eca\u81ea\u5df1\u4e2d\u4e86\u5267 \u6bd2\uff0c\u5374\u65e0\u4eba\u6765\u5e2e\u5fd9\u89e3\u6551\uff0c\u8fd9\u53c8\u5c82\u662f\u59cb\u6240\u6599\u53ca\u3002\u5728\u6781\u5ea6\u7684\u5bc2\u5bde\u4e0e\u4f24\u5fc3\u4e2d\uff0c\u949f\u4e07\u5802\u4e0d\u7531\u5f97\u60f3\u8d77\u4e86 \u5f80\u4e8b\uff1a\u5f53\u5e74\u81ea\u5df1\u626e\u6210\u4e00\u4e2a\u8d70\u65b9\u90ce\u4e2d\uff0c\u6765\u5230\u5e74\u5bb6\uff0c\u89c1\u4e86\u5e74\u7fb9\u5c27\u5947\u5f02\u7684\u6027\u683c\u4e0e\u8fc7\u4eba\u7684\u667a\u6167\u4e4b\u540e\uff0c \u5c31\u51b3\u610f\u6536\u4ed6\u4e3a\u5f92\uff0c\u4e0d\u4f46\u662f\u60f3\u4e3a\u65e0\u6781\u5251\u7559\u4e0b\u4f20\u4eba\uff0c\u800c\u4e14\u662f\u60f3\u4e3a\u6c49\u65cf\u57f9\u517b\u4e00\u4f4d\u80fd\u9886\u5bfc\u7fa4\u4f26\u63a8\u7ffb\u80e1 \u864f\u7684\u8c6a\u6770\uff0c\u90a3\u5e74\u9050\u9f84\u6b63\u4e3a\u513f\u5b50\u53d1\u6101\uff0c\u8bb8\u591a\u4f4d\u6559\u5e08\u90fd\u7ed9\u4ed6\u6253\u8d70\u4e86\uff0c\u6ca1\u6709\u4eba\u6562\u6559\u4ed6\u3002\u81ea\u5df1\u663e\u4e86\u4e00 \u70b9\u80fd\u4e3a\uff0c\u548c\u5e74\u9050\u9f84\u957f\u8c08\u4e86\u4e00\u591c\uff0c\u5e74\u9050\u9f84\u4e5f\u771f\u7b97\u5f97\u8c6a\u723d\uff0c\u5f53\u4e0b\u5c31\u8bf4\uff1a\u201c\u597d\uff0c\u6211\u628a\u72ac\u5b50\u91cd\u6258\u7ed9\u4f60 \u4e86\u3002\u4e00\u5207\u542c\u51ed\u4f60\u7684\u4e3b\u610f\uff0c\u8981\u591a\u5c11\u94b1\u90fd\u53ef\u4ee5\u3002\u201d\u81ea\u5df1\u90a3\u665a\u4fbf\u548c\u4ed6\u8ba2\u4e86\u4e24\u6761\u534f\u5b9a\uff0c\u4e00\u6761\u662f\u8981\u4ed6\u62ff \u51fa\u4e94\u4e07\u4e24\u94f6\u5b50\u6765\uff0c\u4e00\u6761\u662f\u8981\u5230\u5e74\u7fb9\u5c27\u5b66\u6210\u4e4b\u65e5\uff0c\u624d\u8bb8\u4ed6\u548c\u7236\u6bcd\u76f8\u89c1\u3002\u5e74\u9050\u9f84\u5bf9\u7b2c\u4e00\u6761\u7acb\u523b\u5c31 \u7b54\u5e94\u4e86\uff0c\u9a6c\u4e0a\u5f00\u51fa\u4e94\u4e07\u4e24\u7684\u94b1\u5e84\u6298\u5b50\uff0c\u4efb\u51ed\u81ea\u5df1\u4f7f\u7528\u3002\u5bf9\u7b2c\u4e8c\u6761\u5374\u6709\u70b9\u8e0c\u8e87\uff0c\u4ed6\u95ee\u9053\uff1a\u201c\u90a3 \u4e48\u8981\u591a\u5c11\u5e74\u5462\uff1f\u201d\u6211\u60f3\u8fd9\u662f\u4e00\u4e2a\u5173\u952e\u4e86\uff0c\u4e0d\u7ba1\u7fb9\u5c27\u7236\u6bcd\u5982\u4f55\u75bc\u7231\u4ed6\uff0c\u90fd\u8981\u575a\u6301\u81ea\u5df1\u7684\u610f\u89c1 \u4e86\u3002\u4e8e\u662f\u6211\u5c31\u8bf4\uff1a\u201c\u5341\u5e74\u516b\u5e74\u90fd\u8bf4\u4e0d\u5b9a\uff0c\u4f60\u82e5\u820d\u4e0d\u5f97\u4ed6\uff0c\u6211\u5c31\u65e0\u6cd5\u4f7f\u4ed6\u6210\u6750\u3002\u201d\u5e74\u9050\u9f84\u60f3\u4e86 \u53c8\u60f3\uff0c\u7ec8\u4e8e\u7b54\u5e94\u4e86\u3002\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u949f\u4e07\u5802\u9762\u4e0a\u6cdb\u51fa\u4e00\u70b9\u7b11\u5bb9\uff0c\u81ea\u5df1\u4e3a\u8fd9\u4e2a\u5b69\u5b50\uff0c\u8d39\u4e86\u591a\u5c11\u5fc3\u529b\u554a\uff01\u81ea\u5df1\u62ff\u5230\u4e86 \u94f6\u5b50\u540e\uff0c\u5c31\u5728\u5e74\u5e9c\u540e\u9762\u4e70\u4e86\u4e00\u65b9\u7a7a\u5730\uff0c\u96c7\u4e86\u8bb8\u591a\u5de5\u5320\uff0c\u7acb\u523b\u76d6\u9053\u8d77\u4e00\u5ea7\u82b1\u56ed\u6765\uff0c\u697c\u53f0\u66f2\u6298\uff0c \u82b1\u6728\u91cd\u91cd\uff0c\u4e2d\u95f4\u53c8\u9020\u4e00\u5ea7\u7cbe\u7f8e\u7684\u4e66\u5ba4\uff0c\u76f4\u5230\u6b8b\u51ac\uff0c\u624d\u628a\u82b1\u56ed\u9020\u6210\uff0c\u56db\u9762\u9ad8\u9ad8\u7684\u6253\u4e86\u4e00\u91cd\u56f4 \u5899\uff0c\u72ec\u7559\u7740\u897f\u5357\u65b9\u4e00\u4e2a\u7f3a\u53e3\u3002\u8fd9\u5ea7\u5927\u82b1\u56ed\u5c31\u53ea\u662f\u81ea\u5df1\u548c\u5e74\u5bb6\u7684\u8001\u5bb6\u4eba\u4e01\u798f\u4e09\u4e2a\u4eba\u5728\u91cc\u9762\u4f4f\u3002 \u8bb0\u5f97\u90a3\u5929\u662f\u6b63\u6708\u5341\u516d\uff0c\u662f\u5e74\u7fb9\u5c27\u4e0a\u5b66\u7684\u597d\u65e5\u5b50\uff0c\u5e74\u9050\u9f84\u5907\u4e0b\u9152\u5e2d\uff0c\u8bf7\u4e86\u8bb8\u591a\u4eb2\u53cb\u6765\u966a\u6211\u5403 \u9152\uff0c\u5403\u5b8c\u4e86\u9152\uff0c\u5e74\u9050\u9f84\u4eb2\u81ea\u9001\u513f\u5b50\u4e0a\u5b66\uff0c\u5411\u6211\u4f5c\u4e86\u4e09\u4e2a\u63d6\uff0c\u8bf4\u4e86\u79cd\u79cd\u62dc\u6258\u7684\u8bdd\uff0c\u6211\u628a\u4ed6\u9001\u51fa \u56f4\u5899\u7684\u7f3a\u53e3\uff0c\u5c31\u5429\u5490\u5de5\u5320\uff0c\u628a\u7f3a\u53e3\u5835\u585e\u8d77\u6765\uff0c\u53ea\u7559\u4e0b\u4e00\u4e2a\u5c0f\u5c0f\u7684\u7a97\u6d1e\uff0c\u4f5c\u9012\u98df\u7269\u4e4b\u7528\u3002\u81ea\u6b64 \u6211\u4eec\u4e09\u4eba\u5c31\u548c\u5916\u9762\u9694\u7edd\u4e86\u3002\u6211\u7ec8\u65e5\u5750\u5728\u4e66\u623f\u91cc\uff0c\u8bfb\u5175\u6cd5\u533b\u4e66\uff0c\u7ec3\u5185\u529f\u5251\u672f\u3002\u5bf9\u5e74\u7fb9\u5c27\u4e0d\u95fb\u4e0d \u95ee\uff0c\u8fd9\u5b69\u5b50\u4e5f\u4e50\u5f97\u81ea\u7531\u81ea\u5728\uff0c\u5728\u82b1\u56ed\u91cc\u6e38\u6765\u73a9\u53bb\uff0c\u4ece\u4e0d\u66fe\u8fdb\u623f\u91cc\u4e00\u6b65\uff0c\u4e5f\u4ece\u4e0d\u66fe\u548c\u6211\u4ea4\u8c08\u4e00 \u53e5\uff0c\u9ad8\u5174\u8d77\u6765\uff0c\u4fbf\u8131\u4e0b\u8863\u670d\uff0c\u8df3\u4e0b\u6c60\u4e2d\u6e38\u4e00\u56de\u6c34\uff0c\u6216\u8005\u722c\u5230\u6811\u4e0a\u6349\u9e1f\u513f\uff0c\u6625\u5929\u653e\u98ce\u7b5d\uff0c\u590f\u5929 \u9493\u9c7c\uff0c\u79cb\u5929\u6349\u87cb\u8749\uff0c\u51ac\u5929\u56e2\u96ea\u7403\uff0c\u6709\u65f6\u73a9\u538c\u4e86\uff0c\u4fbf\u642c\u6ce5\u571f\uff0c\u62d4\u82b1\u8349\uff0c\u8db3\u8db3\u73a9\u4e86\u4e00\u5e74\uff0c\u597d\u597d\u4e00 \u5ea7\u82b1\u56ed\uff0c\u88ab\u4ed6\u5f04\u5f97\u5899\u574d\u58c1\u5012\uff0c\u82b1\u8c22\u6c34\u5e72\uff0c\u751a\u81f3\u90a3\u5899\u89d2\u77f3\u6839\uff0c\u4e5f\u90fd\u88ab\u4ed6\u5f04\u5f97\u65ad\u788e\u524a\u843d\uff0c\u53ea\u6709\u6211 \u4f4f\u7684\u4e66\u623f\uff0c\u4ed6\u6ca1\u6709\u6765\u8fc7\u3002\u201c\u4ed6\u4ec0\u4e48\u65f6\u5019\u6765\u7684\u5462\uff1f\u201d\u949f\u4e07\u5802\u5728\u6c89\u601d\u4e2d\uff0c\u5ffd\u7136\u4f3c\u89c1\u5e74\u7fb9\u5c27\u63d0\u7740\u4e00 \u6839\u6728\u6367\uff0c\u72e0\u72e0\u7684\u5411\u81ea\u5df1\u6253\u6765\uff01 \u3000\u3000\u949f\u4e07\u5802\u5fc3\u7075\u4e00\u9707\uff0c\u201c\u8fd9\u662f\u5e7b\u89c9\uff1f\u8fd9\u4e0d\u662f\u5e7b\u89c9\uff01\u201d\u90a3\u662f\u4f4f\u8fdb\u82b1\u56ed\u91cc\u4e00\u5e74\u4e4b\u540e\u7684\u4e8b\u4e86\u3002\u5e74\u7fb9 \u5c27\u5b9e\u5728\u73a9\u5f97\u817b\u70e6\u4e86\uff0c\u8001\u5bb6\u4eba\u4e01\u8f90\u4e5f\u6ca1\u7cbe\u795e\u548c\u4ed6\u73a9\uff0c\u6211\u770b\u4ed6\u7ffb\u6c5f\u5012\u6d77\uff0c\u4ece\u4e0d\u54fc\u4e00\u58f0\u513f\uff0c\u53ef\u662f\u4ed6 \u5374\u6765\u627e\u6211\u4e86\u3002\u4ed6\u8dd1\u5230\u4e66\u623f\u91cc\u9053\uff1a\u201c\u8fd9\u4e48\u591a\u5148\u751f\uff0c\u7b97\u4f60\u6700\u597d\u4e86\uff0c\u4ece\u6765\u4e0d\u6562\u7ba1\u6211\u3002\u4f46\u6211\u73b0\u5728\u73a9\u817b \u4e86\uff0c\u6211\u8981\u51fa\u53bb\uff0c\u4f60\u5feb\u66ff\u6211\u5f00\u4e2a\u95e8\u513f\u3002\u201d\u6211\u51b7\u51b7\u7684\u8bf4\uff1a\u201c\u8fd9\u82b1\u56ed\u662f\u6ca1\u6709\u95e8\u7684\uff0c\u4f60\u8981\u51fa\u53bb\uff0c\u987b\u4ece \u5899\u4e0a\u8df3\u51fa\u53bb\uff01\u201d\u56f4\u5899\u6709\u4e09\u4e08\u6765\u9ad8\uff0c\u4e0d\u662f\u8f7b\u529f\u6781\u597d\u7684\u4eba\u600e\u80fd\u8df3\u51fa\u53bb\u5462\u3002\u4ed6\u89c1\u6211\u4e0d\u80af\u5f00\u95e8\uff0c\u51b7\u7b11 \u4e00\u58f0\uff0c\u62ff\u8d77\u4e00\u6839\u6746\u6367\u4fbf\u5411\u6211\u9762\u95e8\u6253\u6765\uff0c\u6211\u4f38\u624b\u4e00\u683c\uff0c\u90a3\u6761\u6746\u68d2\u65ad\u6210\u4e24\u622a\uff0c\u6211\u628a\u4ed6\u7684\u81c2\u8180\u8f7b\u8f7b \u4e00\u6309\uff0c\u4ed6\u4e0d\u89c9\u554a\u5608\u8fde\u58f0\uff0c\u53eb\u8d77\u75db\u6765\uff0c\u6211\u559d\u4ed6\u8dea\u4e0b\uff0c\u4ed6\u6015\u75db\u4e0d\u6562\u4e0d\u8dea\uff0c\u4f46\u6211\u4e00\u653e\u624b\uff0c\u4ed6\u53c8\u4e00\u6e9c \u70df\u7684\u9003\u51fa\u4e66\u623f\u53bb\u4e86\uff0c\u4ece\u6b64\u4e00\u8fde\u4e24\u4e09\u4e2a\u6708\uff0c\u4ed6\u4e0d\u6562\u8e0f\u8fdb\u4e66\u623f\u3002\u8f6c\u77ac\u590f\u53bb\u79cb\u6765\uff0c\u666f\u8c61\u8427\u7d22\uff0c\u8fd9\u5b69 \u5b50\u5b9e\u5728\u73a9\u4e0d\u51fa\u65b0\u9c9c\u82b1\u6837\u4e86\uff0c\u4fbf\u6084\u6084\u7684\u8d70\u8fdb\u4e66\u623f\uff0c\u6211\u6b63\u5728\u4f4e\u5934\u770b\u5b59\u5b50\u5175\u6cd5\uff0c\u4ed6\u5728\u4e66\u623f\u9ed8\u9ed8\u7684\u770b \u4e86\u534a\u5929\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u5927\u7684\u4e00\u5ea7\u82b1\u56ed\uff0c\u6211\u4e5f\u73a9\u5f97\u538c\u70e6\u6781\u4e86\uff0c\u4f60\u8fd9\u5c0f\u5c0f\u7684\u4e00\u672c\u4e66\uff0c\u671d\u770b\u5230 \u591c\uff0c\u591c\u53c8\u770b\u5230\u671d\uff0c\u6709\u4ec0\u4e48\u597d\u73a9\uff1f\u201d\u6211\u5475\u5475\u7b11\u9053\uff1a\u201c\u5c0f\u5b69\u5b50\uff0c\u4f60\u61c2\u5f97\u4ec0\u4e48\uff1f\u8fd9\u4e66\u91cc\u9762\u6709\u6bd4\u82b1\u56ed \u5927\u51e0\u5343\u500d\u7684\u4e16\u754c\uff0c\u7ec8\u751f\u7ec8\u4e16\u4e5f\u73a9\u4e0d\u5b8c\uff01\u201d\u4ed6\u628a\u9888\u5b50\u4e00\u6b6a\uff0c\u8bf4\uff1a\u201c\u6211\u4e0d\u4fe1\uff0c\u4f60\u8bf4\u7ed9\u6211\u542c\u542c\uff0c\u662f \u600e\u4e48\u4e2a\u597d\u73a9\u6cd5\uff1f\u201d\u6211\u6447\u5934\u9053\uff1a\u201c\u4f60\u5148\u751f\u4e5f\u4e0d\u62dc\uff0c\u4fbf\u8bf4\u7ed9\u4f60\u542c\uff0c\u6ca1\u8fd9\u4e2a\u9053\u7406\u3002\u201d\u4ed6\u53cc\u7709\u4e00\u7ad6\uff0c \u684c\u5b50\u4e00\u62cd\uff0c\u8bf4\u9053\uff1a\u201c\u62dc\u4ec0\u4e48\u9e1f\u5148\u751f\uff0c\u4ffa\u4e5f\u4e0d\u7a00\u7f55\u3002\u201d\u6211\u53cc\u773c\u4e00\u77aa\uff0c\u4ed6\u6015\u6211\u6253\u4ed6\uff0c\u4e00\u6e9c\u70df\u53c8\u8dd1 \u4e86\u3002\u5982\u662f\u8005\u53c8\u8fc7\u4e86\u5341\u6765\u5929\uff0c\u4ed6\u5b9e\u5728\u5fcd\u8010\u4e0d\u4f4f\u4e86\uff0c\u8dd1\u8fdb\u4e66\u623f\u91cc\u9053\uff1a\u201c\u4f60\u81c2\u818a\u4e00\u683c\uff0c\u4fbf\u80fd\u628a\u4e00\u6839 \u6728\u68d2\u6298\u65ad\uff0c\u8fd9\u4e5f\u662f\u4ece\u4e66\u672c\u4e2d\u5b66\u6765\u7684\uff1f\u201d\u6211\u8bf4\uff1a\u201c\u4e66\u672c\u4e2d\u6709\u8bb8\u591a\u79cd\uff0c\u6298\u65ad\u6728\u68d2\u7684\u90a3\u662f\u666e\u901a\u4e0d\u8fc7 \u4e86\uff0c\u6700\u6709\u7528\u7684\u4e66\u8fd8\u53ef\u4ee5\u6559\u4f60\u6cbb\u56fd\u5e73\u5929\u4e0b\u5462\uff01\u201d\u4ed6\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u5ffd\u53c8\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff0c\u8bfb \u4e86\u4e66\u4e5f\u4e0d\u80fd\u51fa\u8fd9\u4e2a\u56ed\u5b50\u3002\u201d\u6211\u7b11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\uff0c\u5b66\u597d\u4e86\u672c\u4e8b\uff0c\u8981\u4ec0\u4e48\u65f6\u5019\u51fa\u53bb\u5c31\u4ec0\u4e48\u65f6 \u5019\u51fa\u53bb\u3002\u201d\u6211\u62c9\u4ed6\u7684\u624b\u8d70\u51fa\u56ed\u4e2d\uff0c\u8f7b\u8f7b\u4e00\u8dc3\uff0c\u4fbf\u8df3\u4e0a\u4e86\u5899\u5934\uff0c\u7136\u540e\u53c8\u8df3\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60 \u77a7\uff0c\u6211\u4e0d\u662f\u968f\u65f6\u53ef\u51fa\u53bb\u4e48\uff1f\u8bfb\u4e86\u4e66\uff0c\u7ec3\u597d\u672c\u4e8b\uff0c\u83ab\u8bf4\u8fd9\u5835\u56f4\u5899\uff0c\u5c31\u662f\u5343\u519b\u4e07\u9a6c\u4e5f\u6321\u4f60\u4e0d \u4f4f\u3002\u201d\u4ed6\u7eb3\u5934\u4fbf\u62dc\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u670d\u4e86\uff0c\u5148\u751f\u4f60\u6559\u7ed9\u6211\u5427\uff01\u201d\u6211\u6276\u4ed6\u8d77\u6765\uff0c\u7b2c\u4e00\u672c\u4fbf\u8bb2\u201c\u6c34 \u6d52\u201d\u7ed9\u4ed6\u542c\uff0c\u542c\u5f97\u4ed6\u624b\u821e\u8db3\u8e48\uff0c\u63a5\u7740\u53c8\u8bb2\u201c\u4e09\u56fd\u5fd7\u201d\u3001\u201c\u5cb3\u4f20\u201d\u548c\u53e4\u5f80\u4eca\u6765\u82f1\u96c4\u7684\u4e8b\u8ff9\uff0c\u4fa0 \u5ba2\u7684\u4f20\u8bb0\uff0c\u63a5\u7740\u53c8\u8bb2\u5175\u4e66\u3001\u53f2\u8bb0\u3001\u7a7a\u4e0b\u6765\u53c8\u6559\u4ed6\u6697\u5668\u3001\u62f3\u811a\uff0c\u4ed6\u4e5f\u771f\u806a\u660e\uff0c\u8bfb\u4e66\u662f\u8fc7\u76ee\u6210 \u8bf5\uff0c\u7ec3\u6b66\u662f\u4e00\u904d\u5373\u4f1a\u3002\u5230\u4e86\u7b2c\u4e09\u5e74\u79cb\u5929\uff0c\u6211\u56e0\u4e3a\u8d74\u5468\u9752\u4e4b\u7ea6\uff0c\u53eb\u4ed6\u81ea\u4fee\uff0c\u6211\u5077\u5077\u8d8a\u5899\u51fa\u8d70\uff0c \u6765\u5230\u6c5d\u5dde\uff0c\u4e0d\u6599\u78b0\u4e0a\u4e86\u8840\u6ef4\u5b50\uff0c\u5468\u5927\u4fa0\u6b7b\u4e86\uff0c\u6211\u5e26\u4e86\u51af\u7433\u56de\u6765\uff0c\u90a3\u65f6\u5979\u521a\u624d\u8fc7\u5468\u5c81\uff0c\u6e10\u6e10\u4f1a \u8bf4\u4f1a\u884c\uff0c\u5e74\u7fb9\u5c27\u7a7a\u4e0b\u6765\u5c31\u9017\u5979\u73a9\uff0c\u6bd4\u5144\u59b9\u8fd8\u8981\u4eb2\u70ed\uff0c\u6211\u9a97\u4ed6\u8bf4\u8fd9\u662f\u6211\u4e00\u4e2a\u8001\u670b\u53cb\u7684\u5973\u513f\uff0c\u4ed6 \u5374\u4e0d\u77e5\u6211\u6709\u4e00\u4e2a\u7279\u522b\u7684\u4e3b\u610f\u3002 \u3000\u3000\u949f\u4e07\u5802\u60f3\u5230\u8fd9\u91cc\uff0c\u9762\u4e0a\u53c8\u9732\u51fa\u4e00\u4e1d\u7b11\u610f\uff0c\u611f\u5230\u4e00\u80a1\u5174\u594b\uff0c\u8fde\u75db\u82e6\u4e5f\u6e10\u6e10\u5fd8\u8bb0\u3002\u4ed6\u60f3\u9053\uff1a \u201c\u51af\u7433\u4e5f\u662f\u4e2a\u806a\u660e\u7edd\u9876\u7684\u5b69\u5b50\uff0c\u8bb2\u5230\u7ec3\u6b66\uff0c\u5979\u66f4\u662f\u4ece\u5468\u5c81\u8d77\u5c31\u624e\u6839\u57fa\uff0c\u6b64\u5e74\u7fb9\u5c27\u7684\u57fa\u7840\u66f4\u4e3a \u575a\u56fa\u3002\u8fd9\u4e24\u4e2a\u5b69\u5b50\u771f\u662f\u5929\u9020\u5730\u8bbe\u7684\u4e00\u5bf9\uff0c\u5c06\u6765\u957f\u5927\u4e86\u7ed3\u6210\u592b\u59bb\uff0c\u6211\u8001\u5e74\u4e5f\u611f\u6b23\u6170\u3002\u53ea\u662f\uff0c\u73b0 \u5728\uff0c\u73b0\u5728\u53c8\u906d\u4e86\u8fd9\u573a\u6a2a\u7978\uff0c\u7433\u513f\u88ab\u90a3\u4e2a\u9b54\u5934\u52ab\u8d70\uff0c\u800c\u6211\u5462\uff0c\u81ea\u5df1\u7cbe\u901a\u533b\u7406\uff0c\u9020\u4e86\u8fd9\u6837\u7684\u4e00\u4e2a \u82b1\u56ed\uff0c\u5e74\u5bb6\u65e0\u4eba\u80fd\u591f\u8fdb\u672a\uff0c\u53c8\u6709\u8c01\u80fd\u7ed9\u6211\u522e\u6bd2\u7597\u4f24\uff0c\u6d3b\u8840\u6577\u521b\u3002\u54ce\uff0c\u7fb9\u5c27\uff0c\u4f60\u600e\u4e48\u8fd8\u4e0d\u56de\u6765 \u5462\uff1f\u201d \u3000\u3000\u949f\u4e07\u5802\u4ece\u5927\u529b\u795e\u9b54\u649e\u7834\u7684\u623f\u95e8\u5916\u671b\uff0c\u53ea\u89c1\u9ad8\u9ad8\u7684\u56f4\u5899\uff0c\u516b\u5e74\u524d\u79cd\u4e0b\u7684\u69d0\u6811\u4e5f\u8fd8\u672a\u957f\u5230\u5899 \u5934\uff0c\u5fc3\u60f3\u5c31\u662f\u7fb9\u5c27\u56de\u6765\uff0c\u4ed6\u4e5f\u8fd8\u672a\u80fd\u8df3\u8fc7\u8fd9\u4e2a\u56f4\u5899\u3002\u54ce\uff0c\u5c11\u6797\u5bfa\u7684\u4e8b\u4e0d\u77e5\u600e\u4e48\u6837\u4e86\uff1f\u7fb9\u5c27\u8fd9 \u6837\u7cbe\u7075\uff0c\u4ed6\u4e0d\u89c1\u4e86\u6211\uff0c\u5e94\u8be5\u627e\u5c11\u6797\u5bfa\u7684\u4eba\u548c\u4ed6\u540c\u6765\u5440\u3002\u201d\u60f3\u5230\u5c11\u6797\u5bfa\uff0c\u4ed6\u53c8\u611f\u5230\u4e00\u4e9b\u5feb\u6170\uff0c \u5fc5\u91cc\u60f3\u9053\uff1a\u201c\u7fb9\u5c27\u8fd9\u5b69\u5b50\u4e5f\u771f\u5e78\u8fd0\uff0c\u5c11\u6797\u4e09\u8001\u5168\u90e8\u75bc\u4ed6\uff0c\u628a\u4ed6\u5f53\u6210\u5b9d\u8d1d\u3002\u6211\u662f\u5728\u4ed6\u5b66\u827a\u4e4b\u540e \u7684\u7b2c\u4e09\u5e74\u5c31\u5077\u5077\u5e26\u4ed6\u53bb\u7684\uff0c\u6211\u53bb\u540e\u51af\u7433\u5c31\u4ea4\u4e01\u798f\u770b\u7ba1\uff0c\u600e\u4e48\u4e01\u798f\u4e5f\u4e0d\u77e5\u54ea\u91cc\u53bb\u4e86\uff1f\u8fd9\u6837\u4e00\u573a \u7ffb\u5929\u8986\u5730\u7684\u5927\u6253\uff0c\u4ed6\u90fd\u6ca1\u6709\u9192\u5417\uff1f\u5230\u4e86\u53bb\u5e74\uff0c\u6709\u65f6\u6211\u628a\u7fb9\u5c27\u9001\u51fa\u56f4\u5899\uff0c\u5c31\u7531\u4ed6\u81ea\u53bb\u3002\u4eca\u5e74\u4e09 \u6708\uff0c\u6709\u4e00\u5929\u4ed6\u72ec\u81ea\u53bb\u4e86\u534a\u4e2a\u6708\u624d\u56de\u6765\uff0c\u8bf4\u7684\u8bdd\u597d\u5947\u602a\u5440\uff1f\u600e\u4e48\u6211\u60f3\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u949f\u4e07\u5802\u5b9a \u4e86\u5b9a\u795e\uff0c\u5ffd\u7136\u611f\u5230\u4e00\u9635\u98a4\u6817\uff0c\u4ed6\u60f3\u8d77\u4e86\u5e74\u7fb9\u5c27\u90a3\u6b21\u56de\u6765\u5bf9\u4ed6\u8bf4\u9053\uff1a\u201c\u5e08\u5085\uff0c\u7518\u7f57\u5341\u4e8c\u4e3a\u4e1e \u76f8\uff0c\u6211\u4eca\u5e74\u5341\u56db\u5c81\u4e86\uff0c\u6bd4\u7518\u7f57\u8fd8\u5927\u4e24\u5e74\uff0c\u6211\u4e5f\u4e0d\u7a00\u7f55\u505a\u4e1e\u76f8\uff0c\u6700\u597d\u662f\u505a\u5927\u5c06\u519b\uff0c\u7edf\u7387\u5168\u56fd\u5175 \u9a6c\uff0c\u4e1e\u76f8\u4e5f\u5bb3\u6015\u5927\u5c06\u519b\u7684\u3002\u5e08\u5085\uff0c\u4f60\u8bf4\uff0c\u6211\u80fd\u505a\u5927\u5c06\u519b\u5417\uff1f\u201d\u6211\u9053\uff1a\u201c\u8fd9\u4e5f\u672a\u5c1d\u4e0d\u53ef\u4ee5\uff0c \u4f46\uff0c\u4f46\u8fd8\u8981\u5f88\u957f\u7684\u65f6\u95f4\u2026\u2026\u201d\u6211\u672c\u60f3\u5bf9\u4ed6\u8bf4\uff0c\u4f60\u628a\u5175\u6cd5\u5b66\u6210\u6b66\u827a\u7ec3\u597d\uff0c\u5c06\u6765\u7ea0\u96c6\u82f1\u8c6a\uff0c\u5171\u4e3e \u4e49\u65d7\uff0c\u9a71\u9664\u80e1\u864f\uff0c\u5149\u590d\u4e2d\u534e\uff0c\u90a3\u65f6\u5c82\u6b62\u662f\u5927\u5c06\u519b\uff0c\u5f00\u56fd\u5143\u52cb\u4e5f\u6709\u5f97\u505a\u5462\uff01\u53ef\u662f\u4ed6\u5e74\u7eaa\u8fd8\u5c0f\uff0c \u6050\u4ed6\u53e3\u758f\u4e71\u8bf4\u51fa\u53bb\uff0c\u6211\u4e5f\u5c31\u6ca1\u5bf9\u4ed6\u8bf4\u3002\u4e0d\u6599\u4ed6\u5374\u8bef\u4f1a\u6211\u7684\u610f\u601d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u8981\u5f88\u957f\u65f6\u95f4\uff0c \u662f\u8bf4\u8981\u7b49\u8003\u6b66\u4e3e\uff0c\u4e0a\u4eac\u4f1a\u8bd5\uff0c\u518d\u7edf\u5175\u51fa\u8eab\uff0c\u90a3\u624d\u505a\u5927\u5c06\u519b\u5417\uff1f\u201d\u6211\u8bf4\u4e0d\u8981\u3002\u4ed6\u53c8\u8bf4\uff1a\u201c\u7518\u7f57 \u5341\u4e8c\u4e3a\u4e1e\u76f8\uff0c\u53ea\u662f\u56e0\u4e3a\u7687\u5e1d\u77e5\u4ed6\u806a\u660e\u7edd\u9876\uff0c\u6709\u6b64\u80fd\u4e3a\uff0c\u4fbf\u7acb\u523b\u628a\u4ed6\u63d0\u62d4\u8d77\u6765\u7684\uff0c\u5047\u5982\u6709\u4e2a\u7687 \u5e1d\uff0c\u6216\u8005\u7687\u5b50\u4e5f\u884c\uff0c\u4ed6\u77e5\u9053\u6211\u7684\u672c\u4e8b\u4e5f\u8bb8\u4e0d\u7528\u7ecf\u8fc7\u79d1\u4e3e\uff0c\u5c31\u8ba9\u6211\u505a\u5927\u5c06\u519b\u5462\uff01\u201d\u6211\u542c\u4e86\u53c8\u60ca \u53c8\u6c14\uff0c\u516b\u5e74\u6765\uff0c\u4ed6\u4e0d\u5728\u8fd9\u56ed\u91cc\u7ec3\u529f\uff0c\u5c31\u662f\u5728\u5c11\u6797\u5bfa\u5185\uff0c\u600e\u4e48\u4f1a\u77e5\u9053\u8fd9\u4e48\u4e9b\u4e8b\u60c5\uff1f\u53c8\u600e\u4e48\u4f1a\u6709 \u8fd9\u4e48\u4e2a\u60f3\u6cd5\uff1f\u5f53\u4e0b\u6211\u72e0\u72e0\u7684\u8bad\u4ed6\u4e00\u987f\uff0c\u76f4\u5230\u4ed6\u8dea\u4e0b\u8ba4\u9519\u624d\u7f62\u3002\u54ce\uff0c\u6211\u4e5f\u592a\u4e25\u5389\u4e86\uff0c\u4ed6\u8fd8\u662f\u4e2a \u5b69\u5b50\uff0c\u77e5\u9053\u4e9b\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u8fd9\u65f6\uff0c\u949f\u4e07\u5802\u6240\u4e2d\u7684\u6bd2\uff0c\u6bd2\u6c14\u5df2\u6e10\u884c\u8fd1\u5fc3\u7a9d\uff0c\u949f\u4e07\u5802\u54bd\u4e86\u53e3\u6c14\uff0c\u5f3a\u8fd0\u5185\u529b\u6291\u5236\u7740\u5b83\uff0c\u53e3 \u91cc\u66f4\u611f\u8165\u6e34\uff0c\u534a\u660f\u8ff7\u4e2d\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u6709\u4eba\u54fc\u54df\u60e8\u53eb\u4e00\u58f0\uff0c\u8ddf\u7740\u53eb\u9053\uff1a\u201c\u5e08\u5085\uff0c\u6211\u53d7\u4f24\u4e86\uff0c\u4f60 \u4e3a\u4ec0\u4e48\u628a\u6bd2\u5200\u63d2\u5728\u5730\u4e0a\u5440\uff01\u201d\u8fd9\u58f0\u97f3\u6b63\u662f\u5e74\u7fb9\u5c27\u7684\u3002 \u3000\u3000\u949f\u4e07\u5802\u5728\u6fd2\u5371\u4e4b\u9645\uff0c\u7a81\u7136\u542c\u89c1\u5e74\u7fb9\u5c27\u7684\u58f0\u97f3\uff0c\u5c31\u50cf\u56f0\u5728\u6c99\u6f20\u4e0a\u7684\u4eba\u7a81\u7136\u78b0\u5230\u7518\u9716\u4e00\u6837\uff0c \u7cbe\u795e\u9661\u632f\uff0c\u53eb\u9053\uff1a\u201c\u7fb9\u5c27\uff0c\u4f60\u5feb\u8fdb\u6765\u3002\u201d\u6cea\u73e0\u70b9\u70b9\u6ef4\u5728\u5730\u4e0a\uff0c\u6cea\u5149\u4e2d\u89c1\u679c\u7136\u662f\u5e74\u7fb9\u5c27\u8e09\u8e09\u8dc4 \u8dc4\u7684\u8d70\u4e86\u8fdb\u6765\uff0c\u4e0d\u7981\u8bb7\u8bb7\u81ea\u8bed\uff1a\u201c\u8c22\u5929\u8c22\u5730\uff0c\u8fd9\u5b69\u5b50\u679c\u7136\u56de\u6765\u4e86\uff01\u949f\u4e07\u5802\u672c\u6765\u4eba\u6781\u7cbe\u660e\uff0c\u4f46 \u5728\u534a\u660f\u8ff7\u4e2d\u8fc7\u5ea6\u5174\u594b\uff0c\u7adf\u7136\u6ca1\u6709\u60f3\u5230\uff1a\u5e74\u7fb9\u5c27\u4f55\u4ee5\u8df3\u8fc7\u4e09\u4e08\u591a\u9ad8\u7684\u56f4\u5899\u3002 \u3000\u3000\u5e74\u7fb9\u4eae\u8df3\u4e86\u8fdb\u6765\uff0c\u4e00\u628a\u62b1\u4f4f\u5e08\u5085\uff0c\u54ed\u9053\uff1a\u201c\u5e08\u5085\uff0c\u6211\u7684\u811a\u53c8\u75db\u53c8\u75d2\uff0c\u8e39\u4e2d\u593a\u547d\u795e\u5200 \u4e86\u3002\u201d\u949f\u4e07\u5802\u6323\u624e\u7740\u7528\u624b\u629a\u6a21\u4ed6\u7684\u5934\u53d1\uff0c\u7231\u601c\u8bf4\u9053\uff1a\u201c\u4e0d\u7d27\u8981\u7684\uff0c\u4f60\u5230\u91cc\u623f\u628a\u6211\u7684\u836f\u56ca\u62ff \u6765\u3002\u201d\u5e74\u7fb9\u5c27\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u8fd9\u624d\u6ce8\u89c6\u5e08\u5085\uff0c\u95ee\u9053\uff1a\u201c\u5e08\u5085\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u4e66\u623f\u7ed9\u4eba\u6253\u5f97\u7834\u7834 \u70c2\u70c2\uff0c\u4f60\u4e5f\u8eba\u5728\u5730\u4e0a\uff0c\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u60c5\uff0c\u4f60\u4e0d\u8981\u7d27\u5417\uff1f\u201d\u949f\u4e07\u5802\u90a3\u91cc\u8fd8\u6709\u6c14\u529b\u548c\u4ed6\u7ec6\u8bf4\uff0c \u53ea\u662f\u671b\u7740\u91cc\u623f\uff0c\u7528\u773c\u5149\u50ac\u4ed6\u5feb\u53bb\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u5e74\u7fb9\u5c27\u5728\u91cc\u9762\u60ca\u53eb\u4e00\u58f0\uff1a\u201c\u7433\u59b9\u5462\uff1f\u201d\u949f\u4e07\u5802\u53c8\u662f\u4e00\u9635\u7ede\u75db\uff0c\u5e74\u7fb9\u5c27\u63d0\u7740\u836f \u56ca\u51fa\u6765\u4e86\uff0c\u949f\u4e07\u5802\u70b9\u4e86\u70b9\u5934\uff0c\u5e74\u7fb9\u5c27\u4f0f\u5728\u4ed6\u7684\u8eab\u8fb9\uff0c\u949f\u4e07\u5802\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u836f\u56ca\u91cc\u6709\u4e00\u4e2a\u7f8a\u8102 \u767d\u7389\u74f6\uff0c\u74f6\u91cc\u6709\u4e24\u8272\u836f\u4e38\uff0c\u4e00\u79cd\u7c89\u7ea2\uff0c\u4e00\u79cd\u78a7\u7eff\uff0c\u7c89\u7ea2\u7684\u548c\u6c34\u5185\u670d\uff0c\u78a7\u7eff\u8272\u7684\u56bc\u788e\u5916\u6577\uff0c\u8fd9 \u662f\u89e3\u593a\u547d\u795e\u5200\u7684\u6bd2\u4f24\u7684\u3002\u201d\u8bf4\u5b8c\u4e4b\u540e\uff0c\u6c14\u5598\u5401\u5401\uff0c\u773c\u89c1\u5e74\u7fb9\u5c27\u628a\u5185\u670d\u5916\u6577\u7684\u836f\u4e38\u90fd\u4f7f\u7528\u4e86\uff0c \u8fd9\u624d\u5636\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u7528\u5c0f\u5200\u5728\u6211\u5de6\u80a9\u4e95\u7a74\u65c1\u534a\u5bf8\u4e4b\u5904\u5272\u5f00\u4e00\u6761\u6e9c\u53e3\uff0c\u66ff\u6211\u628a\u6bd2\u8840\u6324\u51fa\u6765\u3002\u7136 \u540e\u5728\u836f\u56ca\u91cc\u628a\u90a3\u91d1\u8272\u7684\u76d2\u5b50\u62ff\u51fa\u6765\u3002\u201d\u5e74\u7fb9\u5c27\u53c8\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u5374\u5e76\u4e0d\u5373\u65f6\u52a8\u624b\uff0c\u4e24\u53ea\u773c\u775b \u4e1c\u5f20\u897f\u671b\uff0c\u84e6\u7136\u95f4\u5916\u9762\u4eba\u5f71\u6643\u52a8\uff0c\u4e00\u4e2a\u5c11\u5e74\u516c\u5b50\u548c\u4e00\u4e2a\u56db\u5341\u591a\u5c81\u3001\u56de\u4eba\u88c5\u675f\u7684\u7cbe\u58ee\u6c49\u5b50\u8d70\u4e86 \u8fdb\u6765\u3002\u949f\u4e07\u5802\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u5c11\u5e74\u516c\u5b50\u6b63\u662f\u5c11\u6797\u5bfa\u7684\u53db\u5f92\u738b\u5c0a\u4e00\uff0c\u600e\u4e48\u4ed6\u4f1a\u77e5\u9053\u81ea\u5df1\u7684\u4f4f\u5740\uff0c \u7a81\u7136\u6765\u5230\u6b64\u95f4\uff1f \u3000\u3000\u738b\u5c0a\u4e00\u8f69\u7709\u7b11\u9053\uff1a\u201c\u949f\u5927\u4fa0\uff0c\u5e78\u4f1a\uff0c\u5e78\u4f1a\uff01\u201d\u949f\u4e07\u5802\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4f60\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u738b\u5c0a \u4e00\u9053\uff1a\u201c\u6211\u4e0e\u4ee4\u5f92\u6709\u4e2a\u5c0f\u5c0f\u7684\u7ea6\u4f1a\u3002\u201d\u5e74\u7fb9\u5c27\u7b11\u563b\u563b\u7684\u4e00\u8dc3\u4e0a\u524d\uff0c\u628a\u7f8a\u8102\u767d\u7389\u74f6\u9012\u7ed9\u4e86\u90a3\u4e2a \u56de\u4eba\uff0c\u949f\u4e07\u5802\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u90a3\u56de\u4eba\u7b11\u9053\uff1a\u201c\u949f\u5927\u4fa0\uff0c\u4f60\u53d7\u4f24\u5f88 \u91cd\uff0c\u5343\u4e07\u4e0d\u80fd\u52a8\u6012\uff0c\u4ffa\u662f\u5317\u4eac\u8840\u6ef4\u5b50\u7684\u603b\u7ba1\uff0c\u5982\u4eca\u6765\u62dc\u8bbf\u4f60\u5566\uff01\u201d\u949f\u4e07\u5802\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u6655 \u4e86\u8fc7\u53bb\u3002\u54c8\u5e03\u9640\u85cf\u597d\u836f\u74f6\uff0c\u7b11\u9053\uff1a\u201c\u795e\u9b54\u53cc\u8001\u53ef\u7b49\u5f97\u7126\u6025\u4e86\uff0c\u6211\u5148\u628a\u4ed6\u4eec\u6551\u9192\u518d\u6765\u3002\u201d\u5411\u5e74 \u7fb9\u5c27\u7ad6\u8d77\u62c7\u6307\uff0c\u5938\u9053\uff1a\u201c\u5c0f\u54e5\uff0c\u4f60\u771f\u884c\uff01\u6211\u8fd9\u8001\u6c5f\u6e56\u4e5f\u7518\u62dc\u4e0b\u98ce\u3002\u201d \u3000\u3000\u8fc7\u4e86\u7247\u5230\uff0c\u949f\u4e07\u5802\u60a0\u60a0\u9192\u8f6c\uff0c\u5e74\u7fb9\u5c27\u6b63\u7528\u51b7\u6c34\u55b7\u4ed6\u3002\u949f\u4e07\u5802\u5b9b\u5982\u7f6e\u8eab\u6076\u68a6\u4e4b\u4e2d\uff0c\u8bd5\u7528\u529b \u54ac\u54ac\u820c\u5934\uff0c\u5267\u75db\u653b\u5fc3\uff0c\u59cb\u4fe1\u5e76\u975e\u6076\u68a6\u3002\u5e74\u7fb9\u5c27\u5c48\u4e86\u534a\u819d\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u5e08\u5085\uff0c\u8fd9\u4f4d\u516c\u5b50\u662f\u5f53 \u4eca\u7684\u56db\u7687\u5b50\uff0c\u6211\u548c\u4ed6\u5df2\u7ed3\u6210\u516b\u62dc\u4e4b\u4ea4\u3002\u201d\u949f\u4e07\u5802\u8fd9\u4e00\u6c14\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u7b11\u563b\u563b\u9053\uff1a\u201c\u5e08\u5085\uff0c\u56db\u7687\u5b50\u60f3\u8bf7\u4f60\u8001\u4eba\u5bb6\u4e5f\u5230\u5317\u4eac\u53bb\u3002\u5317\u4eac\u597d\u5f97\u5f88\u5462\uff0c\u5403\u7684\u73a9 \u7684\uff0c\u4ec0\u4e48\u90fd\u6709\u3002\u201d\u949f\u4e07\u5802\u5df2\u6c14\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u5fc3\u91cc\u8fde\u9a82\u4e86\u51e0\u53e5\u201c\u755c\u7272\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u6b21\u5728\u5c11\u6797\u5bfa\u4f2a\u9020\u53f6\u7b3a\u6587\uff0c\u5047\u4f20\u672c\u7a7a\u9057\u547d\uff0c\u6551\u51fa\u5141\u796f\u7b49\u4e8b\u60c5\uff0c\u90fd\u662f\u5e74\u7fb9\u5c27\u4e00\u624b\u5e72 \u7684\u3002\u4e09\u5e74\u524d\u5141\u796f\u8fd8\u5728\u5c11\u6797\u5bfa\u4e4b\u65f6\uff0c\u8ba4\u8bc6\u4e86\u5e74\u7fb9\u5c27\uff0c\u6df1\u89c9\u8fd9\u4e2a\u5b69\u7ae5\uff0c\u4e0d\u662f\u5bfb\u5e38\u4eba\u7269\uff0c\u6697\u5730\u91cc\u548c \u4ed6\u7ed3\u7eb3\uff0c\u5c06\u4ed6\u5f53\u5927\u4eba\u770b\u5f85\u3002\u4eca\u5e74\u521d\u5e74\u7fb9\u5c27\u72ec\u4e0a\u5d69\u5c71\uff0c\u534a\u8def\u4e0a\u53c8\u9047\u5230\u5141\u796f\uff0c\u5141\u796f\u5f15\u4ed6\u89c1\u5929\u53f6\u6563 \u4eba\u3001\u4e86\u56e0\u548c\u5c1a\u7b49\u6b66\u6797\u524d\u8f88\uff0c\u8fd9\u4e9b\u4eba\u89c1\u4ed6\u8fe5\u5f02\u5e38\u7ae5\uff0c\u5bf9\u4ed6\u90fd\u662f\u8d5e\u4e0d\u7edd\u53e3\u3002\u5e74\u7fb9\u5c27\u89c1\u4e86\u56e0\u7b49\u4eba\u6b66 \u529f\u66f4\u5728\u81ea\u5df1\u5e08\u5085\u4e4b\u4e0a\uff0c\u4e5f\u81ea\u4f69\u670d\u3002\u5c24\u5176\u5bf9\u5141\u796f\u7684\u5e1d\u738b\u6c14\u5ea6\uff0c\u5927\u5fd7\u96c4\u56fe\uff0c\u66f4\u89c9\u5fc3\u6027\u76f8\u6295\u3002\u5230\u4e86 \u540e\u6765\uff0c\u5141\u5e27\u7d22\u6027\u8bf4\u660e\u6765\u5386\uff0c\u548c\u5e74\u7fb9\u5c27\u7ed3\u4e3a\u5144\u5f1f\u3002\u6240\u4ee5\u5e74\u7fb9\u5c27\u56de\u6765\u540e\uff0c\u624d\u6709\u8bd5\u63a2\u949f\u4e07\u5802\u7684\u8bf4 \u8bdd\u3002\u5230\u4e86\u4e00\u4e2a\u6708\u524d\uff0c\u5141\u796f\u5c06\u8981\u5230\u5c11\u6797\u5bfa\u548c\u672c\u65e0\u5927\u5e08\u5bf9\u6267\uff0c\u6b63\u5728\u7b79\u5212\u4e07\u5168\u4e4b\u7b56\uff0c\u84e6\u7136\u60f3\u8d77\u4e86\u5e74 \u7fb9\u5c27\u867d\u7136\u4e0d\u8fc7\u662f\u5341\u56db\u5c81\u7684\u5b69\u5b50\uff0c\u5374\u6781\u5176\u8db3\u667a\u591a\u8c0b\uff0c\u800c\u4e14\u53c8\u7d20\u5f97\u5c11\u6797\u4e09\u8001\u5ba0\u7231\uff0c\u4e8e\u662f\u53c8\u6d3e\u5929\u53f6 \u6563\u4eba\u53bb\u627e\u4ed6\uff0c\u5e74\u7fb9\u5c27\u5bc6\u5ba4\u8bbe\u8c0b\uff0c\u4ed7\u7740\u81ea\u5df1\u90a3\u4efd\u9b3c\u806a\u660e\uff0c\u7adf\u7136\u6a21\u4eff\u4e86\u672c\u7a7a\u5927\u5e08\u7684\u5b57\u8ff9\uff0c\u4f2a\u9020\u4e86 \u8d1d\u53f6\u7b3a\u6587\uff0c\u8fde\u65e0\u4f4f\u7985\u5e08\u90a3\u6837\u6df1\u6c89\u8001\u7ec3\u7684\u4eba\u90fd\u9a97\u8fc7\u4e86\u3002 \u3000\u3000\u518d\u8bf4\u5141\u796f\u89c1\u949f\u4e07\u5802\u4e24\u773c\u7ffb\u767d\uff0c\u989d\u73b0\u7ea2\u7b4b\uff0c\u7528\u5c11\u6797\u5bfa\u6240\u4f20\u7684\u63a8\u62ff\u4e4b\u672f\uff0c\u5728\u4ed6\u8eab\u4e0a\u6309\u4e86\u4e24 \u4e0b\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u949f\u5148\u751f\u4f55\u5fc5\u6c14\u82e6\uff1f\u4ee4\u5f92\u662f\u767e\u5e74\u96be\u9047\u7684\u5947\u624d\uff0c\u5148\u751f\u4e5f\u662f\u5f53\u4eca\u6709\u6570\u7684\u9ad8\u58eb\uff0c\u5929 \u751f\u5947\u624d\uff0c\u5fc5\u6709\u5927\u7528\uff0c\u4e0e\u5176\u7f6e\u8eab\u8349\u83bd\uff0c\u4f55\u5982\u5217\u4f4d\u671d\u5ef7\u3002\u201d\u949f\u65b9\u5802\u54bd\u4e86\u53e3\u6c14\uff0c\u51dd\u4e86\u51dd\u795e\uff0c\u54ac\u5b9e\u7259 \u5173\uff0c\u62db\u62db\u624b\u9053\uff1a\u201c\u7fb9\u5c27\uff0c\u4f60\u8fc7\u6765\u3002\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u6043\u7740\u5e08\u5085\u7d20\u6765\u5ba0\u7231\uff0c\u4f5c\u51fa\u4e00\u526f\u6492\u5a07\u7684\u6837\u5b50\uff0c\u563b\u76ae\u7b11\u8138\u7684\u8bf4\u9053\uff1a\u201c\u5e08\u5085\uff0c\u4f60\u7b54\u5e94\u4e86 \u7687\u5b50\u54e5\u54e5\u7684\u9080\u8bf7\u4e86\u5427\u3002\u4f60\u7684\u6bd2\u4f24\u5df2\u5f88\u91cd\u4e86\uff0c\u7b54\u5e94\u4e86\uff0c\u6211\u8fd8\u8981\u66ff\u4f60\u533b\u6cbb\u5462\uff01\u201d\u949f\u4e07\u5802\u66f4\u662f\u6c14\u5f80 \u4e0a\u6d8c\uff0c\u4e07\u4e0d\u6599\u5230\u5e74\u7fb9\u5c27\u7684\u5fc3\u672f\u7adf\u662f\u5982\u6b64\u4e4b\u574f\uff0c\u5c45\u7136\u62ff\u81ea\u5df1\u7684\u6027\u547d\u6765\u8981\u631f\u3002 \u3000\u3000\u949f\u4e07\u5802\u6570\u5341\u5e74\u529f\u592b\uff0c\u975e\u6bd4\u5bfb\u5e38\uff0c\u8fd9\u65f6\u867d\u5df2\u5782\u5371\uff0c\u72b9\u6709\u6740\u624b\uff0c\u4ed6\u62fc\u7740\u6700\u540e\u4e00\u53e3\u6c14\uff0c\u6253\u8d77\u7cbe \u795e\uff0c\u5f85\u5f97\u5e74\u7fb9\u5c27\u8d70\u8fd1\u8eab\u8fb9\uff0c\u84e6\u7136\u4f38\u51fa\u624b\u6765\uff0c\u4e09\u6307\u4e00\u6263\uff0c\u628a\u5e74\u7fb9\u5c27\u7684\u8109\u95e8\u62ff\u7740\uff0c\u5389\u58f0\u559d\u9053\uff1a \u201c\u5b7d\u5f92\uff0c\u4f60\u8981\u51fa\u5356\u5e08\u5c0a\uff0c\u6211\u5148\u628a\u4f60\u6740\u4e86\uff01\u201d\u8fd9\u4e00\u624b\u4e43\u662f\u65e0\u6781\u6d3e\u64d2\u62ff\u593a\u547d\u624b\u7684\u7edd\u62db\uff0c\u5e74\u7fb9\u5c27\u7ed9 \u4ed6\u4e00\u628a\u62ff\u7740\uff0c\u5168\u8eab\u9178\u8f6f\uff0c\u90a3\u91cc\u8fd8\u80fd\u52a8\u5f39\u3002 \u3000\u3000\u8fd9\u4e00\u7740\u4e5f\u5927\u51fa\u5141\u796f\u548c\u54c8\u5e03\u9640\u7684\u610f\u6599\u4e4b\u5916\uff0c\u8981\u60f3\u62a2\u6551\u5df2\u6765\u4e0d\u53ca\u3002\u949f\u4e07\u5802\u7a81\u7136\u60f3\u8d77\u5468\u9752\u4e34\u7ec8 \u65f6\u5429\u5490\uff1a\u201c\u4f60\u6536\u7684\u90a3\u4e2a\u5f92\u5f1f\uff0c\u82e5\u53d1\u89c9\u4ed6\u5fc3\u672f\u4e0d\u6b63\uff0c\u4f60\u5c31\u628a\u4ed6\u5f04\u6210\u6b8b\u5e9f\uff0c\u5207\u52ff\u59d1\u606f\uff01\u201d\u6697\u6697\u53f9 \u9053\uff1a\u5230\u5e95\u5468\u9752\u6bd4\u81ea\u5df1\u6709\u9884\u77e5\u4e4b\u660e\uff0c\u4e09\u6307\u7528\u529b\uff0c\u6162\u6162\u6263\u7d27\uff01 \u3000\u3000\u5e74\u7fb9\u5c27\u9762\u8272\u60e8\u767d\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u5085\uff0c\u8bf7\u4f60\u5ff5\u5728\u4e03\u5e74\u6765\u7684\u5e08\u5f92\u4e4b\u60c5\uff0c\u9976\u4e86\u6211\u7684\u547d\u5427\uff01\u201d \u949f\u4e07\u5802\u5fc3\u5934\u4e00\u9707\uff0c\u4e03\u5e74\u6765\u7684\u4e8b\u60c5\u4e00\u5e55\u5e55\u5728\u8111\u4e2d\u7ffb\u8fc7\uff0c\u81ea\u5df1\u5455\u5fc3\u6ca5\u8840\u6240\u57f9\u517b\u51fa\u6765\u7684\u4eba\u624d\uff0c\u81ea\u5df1 \u7231\u4ed6\u6bd4\u7231\u4eb2\u751f\u513f\u5b50\u66f4\u751a\u7684\u5f92\u5f1f\uff0c\u96be\u9053\u771f\u8981\u7531\u81ea\u5df1\u4eb2\u624b\u6bc1\u706d\u4e86\u5417\uff1f\u5e74\u7fb9\u5c27\u53c8\u53eb\u9053\uff1a\u201c\u5e08\u5085\u5440\uff0c \u65e0\u6781\u6d3e\u4e00\u8109\u76f8\u4f20\uff0c\u81f3\u6211\u800c\u65a9\uff0c\u5e08\u5085\u5440\uff0c\u4f60\u4e0b\u5f97\u8fd9\u4e2a\u624b\u5417\uff1f\u201d\u949f\u4e07\u5168\u5fc3\u5934\u53c8\u662f\u4e00\u9707\uff0c\u65e0\u6781\u6d3e\u7684 \u6b66\u529f\u5965\u79d8\uff0c\u5df2\u5168\u4f20\u7ed9\u4e86\u5e74\u7fb9\u5c27\uff0c\u82e5\u7136\u628a\u4ed6\u5e9f\u4e86\uff0c\u65e0\u4eba\u518d\u4f20\u8863\u94b5\u3002\u5e74\u7fb9\u5c27\u53c8\u53eb\u9053\uff1a\u201c\u5e08\u5085\u5440\uff0c \u6211\u4ee5\u540e\u4f1a\u597d\u597d\u542c\u4f60\u7684\u8bdd\uff0c\u665a\u4e0a\u4f9d\u65f6\u7761\u89c9\uff0c\u65e9\u4e0a\u4f9d\u65f6\u8d77\u5e8a\u3002\u201d\u539f\u6765\u5e74\u7fb9\u5c27\u81ea\u5c0f\u653e\u8361\u4e0d\u7f81\uff0c\u88ab\u949f \u4e07\u5802\u6536\u670d\u4e4b\u540e\uff0c\u5176\u4ed6\u8fd8\u597d\uff0c\u53ea\u662f\u4efb\u6027\u7684\u813e\u6c14\uff0c\u8fd8\u672a\u80fd\u5b8c\u5168\u6539\u6389\uff0c\u5e38\u5e38\u6df1\u591c\u4e0d\u7720\uff0c\u5929\u660e\u61d2\u8d77\u3002 \u949f\u4e07\u5802\u5bf9\u4ed6\u5982\u6148\u7236\u4e4b\u6559\u5b50\uff0c\u5e38\u529d\u4ed6\u4f5c\u606f\u8981\u6709\u5b9a\u65f6\u3002\u8fd9\u65f6\uff0c\u542c\u4ed6\u8fd9\u4e48\u4e00\u53eb\uff0c\u4e0d\u89c9\u76f8\u6d8c\u5fc3\u9178\uff0c\u5e74 \u7fb9\u5c27\u7684\u8bdd\u6253\u52a8\u4e86\u4ed6\u7684\u631a\u7231\u4e4b\u60c5\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u5468\u9752\u529d\u4ed6\u522b\u59d1\u606f\u517b\u5978\u7684\u8bdd\u4e86\uff0c\u624b\u6307\u4e00\u677e\uff0c\u60e8\u7136\u53eb \u9053\uff1a\u201c\u7fb9\u5c27\uff0c\u4f60\u597d\uff01\u201d\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u624b\u811a\u4e00\u4f38\uff0c\u5bc2\u7136\u4e0d\u52a8\u3002\u54c8\u5e03\u9640\u4e0a\u524d\u628a\u8109\uff0c\u949f\u4e07\u5802 \u8109\u606f\u5df2\u505c\uff0c\u54c8\u5e03\u9640\u644a\u5f00\u53cc\u624b\uff0c\u7f29\u80a9\u7b11\u9053\uff1a\u201c\u8fd9\u8001\u987d\u56fa\u5df2\u7ecf\u6b7b\u4e86\uff01\u201d\u5e74\u7fb9\u5c27\u5fc3\u80a0\u867d\u72e0\uff0c\u4e0d\u89c9\u4e5f \u6ef4\u51fa\u51e0\u70b9\u773c\u6cea\u3002 \u3000\u3000\u5141\u4fa6\u9053\uff1a\u201c\u8fd9\u91cc\u4e8b\u60c5\u5b8c\u5168\u4e86\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d\u5e74\u7fb9\u5c27\u8ddf\u6cea\u4e00\u6536\uff0c\u60f3\u8d77\u4e86\u5e08\u5085\u7684\u5175\u6cd5\u533b\u4e66\u62f3 \u7ecf\u5251\u8bc0\u8fd8\u672a\u53d6\u5230\uff0c\u5bf9\u5141\u796f\u9053\uff1a\u201c\u7687\u5b50\u54e5\u54e5\uff0c\u8bf7\u518d\u7b49\u6211\u4e00\u4f1a\u3002\u201d\u6b63\u60f3\u52a8\u624b\u641c\u5bfb\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u602a \u58f0\u66f3\u7a7a\uff0c\u4e00\u9053\u8d64\u8272\u5149\u534e\u51b2\u5929\u800c\u8d77\u3002\u54c8\u5e03\u9640\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u98ce\u7d27\uff0c\u626f\u547c\uff01\u201d\u62c9\u4e86\u5e74\u7fb9\u5c27\u5f80\u5916\u6025\u8dd1\u3002 \u3000\u3000\u518d\u8bf4\u90a3\u5929\u6613\u5170\u73e0\u5728\u5c11\u6797\u5bfa\u7eb7\u4e71\u4e4b\u540e\uff0c\u4e0d\u89c1\u4e86\u5510\u6653\u6f9c\uff0c\u53c8\u5f97\u77e5\u949f\u4e07\u5802\u548c\u5e74\u7fb9\u5c27\u4e5f\u4e0d\u89c1\u540e\uff0c \u6025\u5fd9\u5bf9\u65e0\u4f4f\u7985\u5e08\u544a\u8f9e\uff0c\u548c\u5415\u56db\u5a18\u767d\u6cf0\u5b98\u5306\u5306\u5c31\u8ffd\uff0c\u8d76\u8d74\u9648\u7559\u3002\u4e09\u4eba\u811a\u7a0b\u7edd\u5feb\uff0c\u9ece\u660e\u52a8\u8eab\uff0c\u9664 \u4e86\u5728\u8def\u4e0a\u51c9\u4ead\u5403\u4e9b\u7ca5\u997c\uff0c\u7a0d\u4e3a\u6b47\u606f\u4e4b\u5916\uff0c\u4e00\u8def\u6284\u6377\u5f84\u98de\u5954\uff0c\u665a\u9ed1\u4e09\u66f4\uff0c\u5df2\u8fdb\u4e86\u9648\u7559\u53bf\u754c\u4e8c\u4e09 \u5341\u91cc\u3002\u6613\u5170\u73e0\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u7684\u5bb6\u4e0d\u77e5\u5ea7\u843d\u4f55\u65b9\u3002\u627e\u5230\u5e74\u5bb6\uff0c\u5c31\u53ef\u4ee5\u627e\u5230\u6211\u7684\u5e08\u4f84\uff0c\u6211\u731c\u6653\u6f9c \u8fd9\u5b69\u5b50\u4e00\u5b9a\u662f\u53bb\u627e\u4ed6\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5e74\u5bb6\u4e43\u662f\u6cb3\u5357\u5de8\u5bcc\uff0c\u968f\u4fbf\u627e\u4e00\u4e2a\u4eba\u95ee\u90fd\u53ef\u4ee5\u95ee\u5230\u3002\u201d \u53ef\u662f\u591c\u5df2\u4e09\u66f4\uff0c\u8def\u5c11\u4eba\u884c\uff0c\u6b63\u5728\u7126\u6025\uff0c\u5ffd\u89c1\u8fdc\u5904\u4e1b\u6797\uff0c\u9690\u6709\u70b9\u70b9\u706b\u5149\uff0c\u6613\u5170\u73e0\u9053\uff1a\u201c\u5947\u4e86\uff0c \u8fd9\u65f6\u5019\u8fd8\u6709\u4eba\u805a\u5728\u6797\u4e2d\u4f5c\u751a\uff1f\u201d\u8d76\u4e0a\u524d\u53bb\uff0c\u6797\u4e2d\u4e00\u80a1\u51c9\u98ce\u5439\u6765\uff0c\u6613\u5170\u73e0\u8fce\u98ce\u547c\u5438\uff0c\u98ce\u4e2d\u7adf\u4f3c \u4ea4\u6709\u836f\u5473\uff0c\u6613\u5170\u73e0\u827a\u9ad8\u80c6\u5927\uff0c\u5411\u5415\u56db\u5a18\u3001\u767d\u6cf0\u5b98\u62db\u4e86\u62db\u624b\uff0c\u4e0d\u7406\u6c5f\u6e56\u201c\u9022\u6797\u83ab\u5165\u201d\u7684\u544a\u8beb\uff0c \u4e00\u7eb5\u8eab\uff0c\u8dc3\u8fdb\u4e86\u4e1b\u6797\u4e4b\u5185\u3002 \u3000\u3000\u6797\u6df1\u5730\u9ed1\uff0c\u679d\u53f6\u7e41\u5bc6\uff0c\u6613\u5170\u73e0\u6b63\u805a\u62e2\u76ee\u5149\uff0c\u8dc3\u4e0a\u6811\u4e0a\uff0c\u67e5\u770b\u706b\u5149\u6240\u5728\u3002\u5ffd\u542c\u5f97\u65c1\u8fb9\u6709\u4eba \u51b7\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u4e5e\u5a46\uff0c\u7b97\u4f60\u6709\u80c6\u91cf\uff0c\u5c45\u7136\u6562\u4ece\u5c11\u6797\u5bfa\u8ffd\u5230\u8fd9\u513f\uff0c\u73b0\u5728\u6211\u4eec\u9009\u4e86\u8fd9\u5757\u597d \u98ce\u6c34\uff0c\u505a\u4f60\u846c\u8eab\u4e4b\u5730\u3002\u4f60\u5c31\u4e0d\u5fc5\u518d\u56de\u5230\u5929\u5c71\u90a3\u4e48\u8fdc\u4e86\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u6012\u9053\uff0c\u201c\u597d\uff0c\u8ba9\u6211\u8001\u5a46\u5b50\u89c1\u8bc6\u89c1\u8bc6\u4f60\u4eec\u5c0f\u8f88\u6709\u591a\u5927\u672c\u9886\uff1f\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u4e00\u4e2a\u201c\u9f99 \u5f62\u7a7f\u638c\u201d\uff0c\u98de\u7bad\u822c\u7a7f\u5165\u6797\u5185\uff0c\u90a3\u4eba\u72b9\u5f85\u53d1\u8a00\u8ba5\u7b11\uff0c\u84e6\u89c9\u638c\u98ce\u98d2\u7136\uff0c\u4e1a\u5df2\u88ad\u5230\u3002\u8fd9\u4eba\u867d\u7136\u5403\u60ca \u975e\u5c0f\uff0c\u5374\u4e5f\u673a\u8b66\u5f02\u5e38\uff0c\u4e00\u89c9\u638c\u98ce\u88ad\u6765\uff0c\u4fbf\u77e5\u5389\u5bb3\uff0c\u4e0d\u8bba\u56de\u8eab\u8fce\u654c\uff0c\u6216\u524d\u7a9c\u95ea\u907f\uff0c\u90fd\u9003\u4e0d\u51fa\u6613 \u5170\u73e0\u638c\u4e0b\uff0c\u4ed6\u7adf\u5229\u7528\u8fd1\u8eab\u5730\u5f62\u548c\u51e0\u679d\u5408\u62b1\u7684\u677e\u6811\uff0c\u65cb\u98ce\u4f3c\u7684\u4e00\u8f6c\u8eab\uff0c\u5de6\u76d8\u53f3\u7ed5\uff0c\u5206\u6563\u6613\u5170\u73e0 \u7684\u638c\u529b\uff0c\u63a5\u8fde\u51e0\u8f6c\uff0c\u8eb2\u5165\u6697\u5904\u3002\u6613\u5170\u73e0\u5fae\u5fae\u4e00\u8bb6\uff0c\u8fd9\u4eba\u8eab\u6cd5\u597d\u602a\uff01\u6b63\u5f85\u8fa8\u58f0\u8fdb\u51fb\uff0c\u84e6\u542c\u5f97\u545c \u545c\u58f0\u54cd\uff0c\u4e00\u4ef6\u9ed1\u5ffd\u5ffd\u7684\u4e1c\u897f\u81ea\u8111\u540e\u98de\u6765\uff0c\u6613\u5170\u73e0\u542c\u58f0\u8fa8\u5668\uff0c\u5f15\u8eab\u4e00\u95ea\uff0c\u90a3\u6697\u5668\u95ea\u7535\u822c\u7684\u4ece\u5934 \u9876\u98de\u8fc7\uff0c\u5374\u5ffd\u7136\u53c8\u6298\u4e86\u56de\u6765\uff0c\u6613\u5170\u73e0\u5927\u5403\u4e00\u60ca\uff0c\u4ed7\u7740\u7edd\u9876\u8f7b\u529f\uff0c\u4e00\u7eb5\u8eab\u8dc3\u4e0a\u6811\u679d\u3002\u90a3\u6697\u5668\u5f62 \u5982\u66f2\u5c3a\uff0c\u5c45\u7136\u7ed5\u6811\u4e00\u531d\uff0c\u76d8\u65cb\u98de\u4e0a\uff0c\u72b9\u5982\u6709\u7075\u6027\u4e00\u822c\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6613\u5170\u73e0\u65e9\u5df2\u62d4\u5251\u5728 \u624b\uff0c\u8fce\u7740\u6697\u5668\u4e00\u6311\uff0c\u90a3\u6697\u5668\u545c\u7684\u4e00\u58f0\u6d41\u661f\u6b92\u77f3\u822c\u8dcc\u843d\u5730\u4e0a\uff0c\u6613\u5170\u73e0\u4e00\u5251\u800c\u4e0b\uff0c\u5374\u4e0d\u6599\u90a3\u6697\u5668 \u5728\u5730\u4e0a\u4e00\u4e2a\u6253\u6eda\uff0c\u5ffd\u7136\u53c8\u98de\u817e\u8d77\u6765\uff0c\u6a2a\u65ab\u4e0b\u76d8\uff0c\u6613\u5170\u73e0\u5fc3\u5934\u4e00\u9707\uff0c\u84e6\u7136\u4f38\u51fa\u4e24\u6307\uff0c\u5f3a\u7528\u91d1\u521a \u6307\u529b\uff0c\u5411\u524d\u4e00\u7b9d\uff0c\u771f\u4e2a\u662f\u8eab\u624b\u5982\u7535\uff0c\u53ea\u4e00\u62db\u624b\u7684\u529f\u592b\uff0c\u5df2\u628a\u90a3\u6697\u5668\u7b9d\u5230\u624b\u4e2d\uff0c\u5927\u58f0\u559d\u9053\uff1a \u201c\u97e9\u91cd\u5c71\u4f60\u8fd9\u602a\u7269\uff0c\u6562\u6765\u620f\u800d\u8001\u5a18\uff01\u201d\u6e38\u9f99\u5251\u98de\u4e91\u63a3\u7535\uff0c\u5530\uff0c\u5530\uff0c\u5530\uff0c\u4e00\u8fde\u51e0\u5251\u75be\u5411\u90a3\u4eba\u523a \u53bb\uff01 \u3000\u3000\u539f\u6765\u8fd9\u4eba\u540d\u53eb\u97e9\u91cd\u5c71\uff0c\u4e0e\u5929\u53f6\u6563\u4eba\u4e43\u662f\u540c\u95e8\u5144\u5f1f\uff0c\u5728\u6606\u4ed1\u5c71\u4e2d\uff0c\u7ec3\u5c31\u4e86\u4e00\u79cd\u6781\u9634\u6bd2\u7684\u6697 \u5668\uff0c\u540d\u4e3a\u201c\u56de\u73af\u94a9\u201d\uff0c\u5f62\u5982\u66f2\u5c3a\uff0c\u4e24\u7aef\u5c16\u5229\uff0c\u957f\u4e24\u5c3a\u6709\u591a\uff0c\u53ea\u4e2d\u592e\u4e09\u5bf8\u53ef\u4ee5\u638c\u63e1\uff0c\u5176\u4f59\u5730\u65b9 \u90fd\u88c5\u6709\u660e\u6643\u6643\u7684\u5012\u94a9\uff0c\u8fd9\u8fd8\u4e0d\u7b97\u5389\u5bb3\uff0c\u5389\u5bb3\u7684\u662f\u5b83\u80fd\u51ed\u7740\u53d1\u6697\u5668\u65f6\u7684\u529b\u5ea6\u64cd\u7eb5\uff0c\u53ef\u4ee5\u56de\u73af\u8f6c \u6298\uff0c\u4e0a\u4e0b\u98de\u817e\u3002\u97e9\u91cd\u5c71\u5728\u6606\u4ed1\u5c71\u65f6\uff0c\u66fe\u89c1\u5c71\u4e0b\u5c45\u6c11\u7528\u8fd9\u79cd\u4e1c\u897f\u730e\u9e1f\uff0c\u7075\u673a\u4e00\u52a8\uff0c\u4fbf\u4eff\u9020\u7ec3\u6210 \u4e00\u79cd\u72ec\u95e8\u6697\u5668\uff0c\u7ecf\u8fc7\u4e86\u4e09\u5341\u4f59\u5e74\u7684\u7ec3\u4e60\u6539\u8fdb\uff0c\u5df2\u5230\u4e86\u795e\u4e4e\u5176\u6280\u7684\u5730\u6b65\uff0c\u4e0d\u6599\u5374\u78b0\u7740\u4e86\u6b66\u6797\u4e2d \u8f88\u4efd\u6700\u5c0a\u7684\u6613\u5170\u73e0\uff0c\u7ed9\u5979\u7528\u91d1\u521a\u6307\u529b\uff0c\u5f3a\u7b9d\u4e86\u53bb\uff01 \u3000\u3000\u97e9\u91cd\u5c71\u7684\u529f\u529b\u6bd4\u5929\u53f6\u6563\u4eba\u5c1a\u8981\u7a0d\u9ad8\u5c11\u8bb8\uff0c\u5929\u53f6\u6563\u4eba\u81ea\u5728\u7530\u6a2a\u5c9b\u4e0a\uff0c\u53d7\u4e86\u6613\u5170\u73e0\u7684\u632b\u6298 \u540e\uff0c\u5c31\u7acb\u523b\u8bf7\u4ed6\u51fa\u6765\uff0c\u4e24\u5e08\u5144\u5f1f\u7acb\u5fc3\u8981\u6597\u4e00\u6597\u8fd9\u4f4d\u5929\u5c71\u5973\u4fa0\u3002\u8fd9\u65f6\u6613\u5170\u73e0\u5251\u4f3c\u60ca\u98d8\uff0c\u97e9\u91cd\u5c71 \u4e5f\u65e9\u628a\u80cc\u4e0a\u836f\u9504\u53d6\u5728\u624b\u4e2d\uff0c\u6321\u4e86\u4e09\u5251\uff0c\u751a\u4e3a\u5403\u529b\u3002\u6613\u5170\u73e0\u8fde\u8fde\u8fdb\u903c\uff0c\u5ffd\u542c\u5f97\u5415\u56db\u5a18\u5927\u58f0\u53eb \u9053\uff1a\u201c\u5510\u6653\u6f9c\u5728\u8fd9\u513f\uff01\u201d \u3000\u3000\u539f\u6765\u6613\u5170\u73e0\u9047\u97e9\u91cd\u5c71\u4f0f\u51fb\u4e4b\u65f6\uff0c\u5415\u56db\u5a18\u5df2\u76f4\u5954\u706b\u5149\u8d77\u5904\uff0c\u731b\u7136\u95f4\u4e00\u6761\u4eba\u5f71\u7a9c\u4e86\u51fa\u6765\uff0c\u6a2a \u7a7a\u4e00\u638c\uff0c\u628a\u5415\u56db\u5a18\u7684\u971c\u534e\u5b9d\u5251\u9707\u5f97\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u5415\u56db\u5a18\u6536\u5251\u98d8\u8eab\uff0c\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c1\u6765\u7684\u4e43\u662f\u5929 \u53f6\u6563\u4eba\uff0c\u4e0d\u8fdc\u5904\u4e00\u5806\u91ce\u706b\uff0c\u4e86\u56e0\u548c\u5c1a\u6a2a\u5b88\u5728\u706b\u5806\u4e4b\u65c1\uff0c\u795e\u9b54\u53cc\u8001\u76d8\u819d\u95ed\u76ee\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5510\u6653 \u6f9c\u5374\u88ab\u7f1a\u5728\u4e00\u68f5\u6811\u4e0a\u3002 \u3000\u3000\u5415\u56db\u5a18\u8fd0\u5251\u5982\u98ce\uff0c\u65bd\u5c55\u7384\u5973\u5251\u4e2d\u7684\u7edd\u62db\u5411\u5929\u53f6\u6563\u4eba\u731b\u523a\uff0c\u5929\u53f6\u6563\u4eba\u638c\u529b\u867d\u7136\u5389\u5bb3\uff0c\u5374\u662f \u6253\u5979\u4e0d\u7740\uff0c\u5929\u53f6\u6563\u4eba\u89c1\u5979\u5251\u6cd5\u5389\u5bb3\uff0c\u81ea\u5df1\u867d\u7136\u4e0d\u81f4\u843d\u8d25\uff0c\u8981\u80dc\u4e5f\u751a\u8270\u96be\uff0c\u6b63\u60f3\u62db\u547c\u4e86\u56e0\u5939 \u653b\uff0c\u5ffd\u89c1\u6613\u5170\u73e0\u5c06\u97e9\u91cd\u5c71\u76f4\u903c\u8fdb\u6797\u5185\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u5ffd\u53eb\u9053\uff1a\u201c\u4e86\u56e0\u5927\u5e08\uff0c\u5148\u628a\u81ea\u5df1\u4eba\u6551 \u8d70\uff01\u201d\u4e86\u56e0\u89c1\u6613\u5170\u73e0\u5415\u56db\u5a18\u53cc\u53cc\u62a2\u5230\uff0c\u5fc3\u91cc\u4e5f\u81ea\u7740\u614c\uff0c\u6025\u5fd9\u628a\u795e\u9b54\u53cc\u8001\uff0c\u4e00\u624b\u4e00\u4e2a\uff0c\u6293\u4e86\u8d77 \u6765\uff0c\u98de\u5954\u51fa\u6797\u3002\u5415\u56db\u5a18\u88ab\u5929\u53f6\u6563\u4eba\u7eca\u4f4f\uff0c\u65e0\u6cd5\u8ffd\u8d76\u3002 \u3000\u3000\u6613\u5170\u73e0\u67aa\u5165\u6797\u5185\uff0c\u89c1\u4e86\u56e0\u5df2\u8f6c\u5165\u4e1b\u6797\u4e4b\u5185\uff0c\u6599\u60f3\u65e0\u6cd5\u8ffd\u8d76\uff0c\u53eb\u9053\uff1a\u201c\u5415\u56db\u5a18\uff0c\u4f60\u5148\u628a\u5510 \u6653\u6f9c\u6551\u9192\uff0c\u6211\u6765\u6253\u53d1\u8fd9\u4e24\u4e2a\u5996\u5b7d\u3002\u201d\u6e38\u9f99\u5251\u66b4\u7136\u4e00\u4f38\uff0c\u5c06\u97e9\u91cd\u5c71\u548c\u5929\u53f6\u6563\u4eba\u90fd\u7b3c\u7f69\u5728\u5251\u5149\u4e4b \u5185\uff0c\u767d\u6cf0\u5b98\u62d4\u5200\u76f8\u52a9\uff0c\u7ed9\u97e9\u91cd\u5c71\u7684\u836f\u9504\u4e00\u683c\uff0c\u9707\u5f97\u80f3\u818a\u9178\u9ebb\uff0c\u5929\u53f6\u6563\u4eba\u6b63\u88ab\u6613\u5170\u73e0\u5251\u62db\u903c\u5f97 \u624b\u5fd9\u811a\u4e71\uff0c\u89c1\u767d\u6cf0\u5b98\u6765\u5230\uff0c\u84e6\u7136\u5f97\u8ba1\uff0c\u8fde\u73af\u4e24\u638c\uff0c\u628a\u767d\u6cf0\u5b98\u903c\u5411\u81ea\u5df1\u7684\u5de6\u9762\u7a7a\u95e8\uff0c\u6070\u6070\u963b\u6b62 \u4e86\u6613\u5170\u73e0\u7684\u5251\u62db\u3002\u9ad8\u624b\u6bd4\u5251\uff0c\u6bcf\u4e89\u77ac\u606f\u5148\u540e\uff0c\u6613\u5170\u73e0\u8fd9\u4e00\u53d7\u963b\uff0c\u97e9\u91cd\u5c71\u4e0e\u5929\u53f6\u6563\u4eba\u5df2\u8131\u51fa\u8eab \u6765\uff0c\u6613\u5170\u73e0\u4e00\u6025\uff0c\u5c06\u767d\u6cf0\u5b98\u4e00\u628a\u62c9\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5e2e\u5e08\u59b9\u6551\u4eba\u5427\uff01\u201d\u767d\u6cf0\u5b98\u9762\u4e0a\u4e00\u7ea2\uff0c\u53ea\u597d \u9000\u4e0b\u3002 \u3000\u3000\u5415\u56db\u5a18\u5c06\u5510\u6653\u6f9c\u89e3\u4e0b\uff0c\u89c1\u4ed6\u8ff7\u7cca\u7cca\u7684\uff0c\u77e5\u662f\u53d7\u4e86\u70b9\u7a74\uff0c\u6025\u5fd9\u5c06\u4ed6\u6551\u9192\u3002\u5510\u6653\u6f9c\u53ea\u89c1\u5415\u56db \u5a18\u5bb9\u5149\u7167\u4eba\uff0c\u7ad9\u5728\u9762\u524d\uff0c\u53c8\u60ca\u53c8\u559c\u3002\u611f\u6fc0\u8bf4\u9053\uff1a\u201c\u5415\u83b9\u59d0\u59d0\uff0c\u591a\u8c22\u4f60\u53c8\u6551\u6211\u6027\u547d\u3002\u201d\u5415\u56db\u5a18 \u7b11\u9053\uff1a\u201c\u662f\u4f60\u672c\u95e8\u957f\u8f88\u6551\u4f60\uff0c\u5e72\u6211\u4f55\u4e8b\u3002\u201d\u5510\u6653\u6f9c\u5b9a\u4e86\u5b9a\u795e\uff0c\u60f3\u8d77\u524d\u4e8b\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u5415\u59d0 \u59d0\uff0c\u8bf7\u901f\u53bb\u6551\u949f\u5927\u4fa0\uff01\u201d\u539f\u6765\u5510\u6653\u6f9c\u81ea\u5c11\u6797\u4e00\u76f4\u8ffd\u6765\uff0c\u95ef\u5165\u6797\u5185\uff0c\u5373\u88ab\u4e86\u56e0\u64d2\u4f4f\u3002\u5176\u65f6\u6b63\u662f \u795e\u9b54\u53cc\u8001\u4e2d\u4e86\u593a\u547d\u98de\u5200\uff0c\u8eb2\u5728\u6797\u4e2d\u517b\u4f24\u7684\u65f6\u5019\u3002\u8fc7\u4e86\u4e0d\u4e45\uff0c\u5e74\u7fb9\u5c27\u9a97\u4e86\u5e08\u5085\u7684\u89e3\u836f\u6765\u6551\u53cc \u9b54\uff0c\u5141\u796f\u628a\u5e74\u7fb9\u5c27\u5938\u5956\u4e00\u756a\uff0c\u968f\u5373\u5e26\u4e86\u54c8\u5e03\u9640\u548c\u4ed6\u4e00\u8d77\u53bb\u4e86\u3002\u5510\u6653\u6f9c\u542c\u5f97\u5206\u660e\uff0c\u53c8\u60ca\u53c8\u6025\uff0c \u53eb\u56b7\u8d77\u6765\uff0c\u7ed9\u4e86\u56e0\u7528\u91cd\u624b\u6cd5\u70b9\u6655\uff0c\u8ff7\u832b\u4e2d\u89c1\u4e00\u4e2a\u8001\u5987\u80cc\u7740\u4e00\u4e2a\u5973\u7ae5\u5148\u8d70\uff0c\u6b64\u540e\u5c31\u4e0d\u7701\u4eba\u4e8b\u3002 \u73b0\u5728\u7ed9\u5415\u56db\u5a18\u6551\u9192\uff0c\u5c48\u6307\u4e00\u7b97\uff0c\u8fd9\u4e03\u516b\u5c81\u7684\u5973\u7ae5\u60f3\u5fc5\u662f\u51af\u7433\uff0c\u5510\u6653\u6f9c\u66fe\u53d7\u51af\u5e7f\u6f6e\u5927\u6069\uff0c\u5ff5\u5ff5 \u4e0d\u5fd8\u8fd9\u4e00\u5bf9\u5b6a\u751f\u59d0\u59b9\uff0c\u56e0\u6b64\u8fde\u58f0\u50ac\u4fc3\u5415\u56db\u5a18\u5feb\u53bb\u3002\u4ed6\u5374\u4e0d\u77e5\u51af\u7433\u8fd9\u65f6\u5df2\u88ab\u97e9\u91cd\u5c71\u7684\u59bb\u5b50\u5e26\u51fa \u4e09\u5341\u91cc\u5916\u4e86\u3002 \u3000\u3000\u6613\u5170\u73e0\u773c\u89c2\u56db\u9762\u3001\u8033\u542c\u516b\u65b9\uff0c\u5510\u6653\u6f9c\u7684\u8bf4\u8bdd\u5979\u5b8c\u5168\u542c\u5230\uff0c\u5251\u62db\u4e00\u7d27\uff0c\u628a\u5929\u53f6\u6563\u4eba\u4e24\u5e08\u5144 \u5f1f\u903c\u9000\u51e0\u6b65\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5e74\u5bb6\u5fc5\u5b9a\u5c31\u5728\u9644\u8fd1\uff0c\u5415\u56db\u5a18\u4f60\u548c\u5e08\u5144\u5f1f\u5e26\u5510\u6653\u6f9c\u53bb\uff0c\u6211\u968f\u540e\u5c31 \u6765\u3002\u201d\u5415\u56db\u5a18\u6599\u60f3\u6613\u5170\u73e0\u5fc5\u80fd\u6253\u53d1\u8fd9\u4e24\u4e2a\u9b54\u5934\uff0c\u5e94\u4e86\u4e00\u58f0\uff0c\u548c\u767d\u5510\u4e8c\u4eba\u8054\u8882\u51fa\u6797\u3002\u6613\u5170\u73e0\u5927 \u663e\u795e\u5a01\uff0c\u5251\u52bf\u5982\u8679\uff0c\u5947\u5e7b\u65e0\u6bd4\uff0c\u72ec\u6218\u97e9\u91cd\u5c71\u7684\u201c\u8f9f\u4e91\u9504\u201d\u548c\u5929\u53f6\u6563\u4eba\u7684\u201c\u5927\u6454\u7891\u624b\u201d\uff0c\u53ea\u6253 \u5f97\u6797\u4e2d\u5bbf\u9e1f\u60ca\u98de\uff0c\u6811\u53f6\u7c07\u7c07\u843d\u4e0b\u3002 \u3000\u3000\u97e9\u91cd\u5c71\u7684\u4e00\u767e\u96f6\u516b\u8def\u8f9f\u4e91\u9504\u6cd5\uff0c\u72ec\u521b\u4e00\u5bb6\uff0c\u867d\u7136\u4e0d\u53ca\u5929\u5c71\u5251\u6cd5\u7684\u795e\u5999\uff0c\u4f46\u52a0\u4e0a\u5929\u53f6\u6563\u4eba \u7684\u638c\u529b\uff0c\u4e00\u65f6\u95f4\u6613\u5170\u73e0\u5012\u4e5f\u672a\u80fd\u5f97\u624b\uff0c\u53c8\u6218\u4e86\u4e00\u4e2a\u65f6\u523b\uff0c\u6613\u5170\u73e0\u5de6\u624b\u634f\u7740\u5251\u8bc0\u4e00\u6307\uff0c\u53f3\u624b\u5251 \u5149\u4e00\u95ea\uff0c\u4e00\u62db\u201c\u63a2\u9a8a\u53d6\u73e0\u201d\uff0c\u5251\u950b\u5411\u5929\u53f6\u6563\u4eba\u54bd\u5589\u75be\u70b9\uff0c\u97e9\u91cd\u5c71\u4e00\u9504\u5288\u7a7a\uff0c\u4fa7\u8eab\u6536\u62db\uff0c\u6539\u76f4 \u4e3a\u6a2a\uff0c\u8f9f\u4e91\u9504\u8f6c\u950b\u4e0b\u622a\uff0c\u4e00\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u649e\u5b9d\u5251\uff0c\u9504\u8170\u808b\uff0c\u6551\u5e08\u5f1f\u3002\u6613\u5170\u73e0\u62db\u5f0f\u4e0d\u53d8\uff0c \u8eab\u5f62\u5fae\u52a8\uff0c\u5df2\u4ece\u201c\u63a2\u9a97\u53d6\u73e0\u201d\u53d8\u4e3a\u201c\u5de7\u5973\u7a7f\u9488\u201d\u5de6\u624b\u4e00\u6289\u53f3\u81c2\uff0c\u731b\u4e00\u8fdb\u6b65\uff0c\u53cd\u64a9\u654c\u4eba\u7897\u5e95\uff0c \u5e26\u6302\u8170\u80c1\uff0c\u5530\u5530\u51e0\u5251\uff0c\u523a\u5c16\u541e\u5410\u5982\u98ce\u3001\u4e00\u62db\u7d27\u4f3c\u4e00\u62db\uff0c\u9163\u6218\u4e2d\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5929\u53f6\u6563 \u4eba\u7684\u8863\u8896\u7ed9\u5272\u53bb\u4e00\u622a\u3002\u97e9\u91cd\u5c71\u89c1\u4e0d\u662f\u8def\uff0c\u63a2\u8170\u53d6\u51fa\u4e00\u679a\u7409\u78fa\u5f39\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u63b7\u4e0a\u9065\u7a7a\uff0c\u987f\u65f6 \u53d1\u51fa\u4e00\u9053\u8d64\u8272\u5149\u534e\uff0c\u5389\u58f0\u53eb\u9053\uff1a\u201c\u8001\u4f34\uff0c\u4f60\u8fd8\u4e0d\u5feb\u6765\u5440\uff01\u201d\u8f9f\u4e91\u9504\u4e00\u62d6\u4e00\u683c\uff0c\u6321\u8fc7\u4e86\u6e38\u9f99\u5b9d \u5251\uff0c\u4e0e\u5929\u53f6\u6563\u4eba\u5e76\u80a9\u75be\u9000\u3002\u6613\u5170\u73e0\u84e6\u7136\u60f3\u8d77\uff0c\u8fd9\u97e9\u91cd\u5c71\u4e43\u662f\u592b\u59bb\u53cc\u4fee\uff0c\u59bb\u5b50\u53f6\u6a2a\u6ce2\u6b66\u529f\u4e5f\u6781 \u5389\u5bb3\u3002\u5fc3\u60f3\uff0c\u82e5\u518d\u52a0\u4e0a\u4e00\u4e2a\u9ad8\u624b\uff0c\u4ee5\u4e00\u654c\u4e09\uff0c\u80dc\u8d1f\u672a\u53ef\u9884\u6599\u3002\u53c8\u60f3\u8d77\u5929\u53f6\u6563\u4eba\u4e0e\u4e86\u56e0\u7b49\u65e2\u7136 \u5728\u573a\uff0c\u90a3\u4e48\u5141\u796f\u6240\u8058\u7684\u90a3\u73ed\u6b66\u6797\u9ad8\u624b\uff0c\u60f3\u5fc5\u4e5f\u540c\u6765\u4e86\u3002\u6df1\u6015\u5415\u56db\u5a18\u4e0e\u5510\u6653\u6f9c\u53c8\u906d\u4e0d\u6d4b\uff0c\u4e5f\u5c31 \u4e0d\u518d\u8ffd\u8d76\uff0c\u8ba9\u5929\u53f6\u656c\u4eba\u4e0e\u97e9\u91cd\u5c71\u9003\u51fa\u6797\u5916\u3002 \u3000\u3000\u518d\u8bf4\u5415\u56db\u5a18\u7b49\u4eba\u5148\u51fa\u6797\u5b50\uff0c\u8d70\u4e86\u4e8c\u4e09\u91cc\u8def\uff0c\u679c\u7136\u89c1\u6709\u5de8\u53a6\u8fde\u4e91\uff0c\u56f4\u5899\u9ad8\u8038\uff0c\u5415\u56db\u5a18\u76ee\u5149 \u9510\u5229\uff0c\u89c1\u82b1\u56ed\u540e\u9762\u51e0\u6761\u9ed1\u5f71\u8d8a\u5899\u800c\u51fa\uff0c\u8bf4\u9053\uff1a\u201c\u949f\u5927\u4fa0\u4e00\u5b9a\u662f\u4f4f\u5728\u82b1\u56ed\u91cc\u4e86\u3002\u201d\u4e0e\u767d\u6cf0\u5b98\u7eb5 \u8eab\u8df3\u5165\u5899\u5185\u3002\u5510\u6653\u6f9c\u4e5f\u7528\u6362\u638c\u79fb\u6b65\u7684\u529f\u592b\uff0c\u8dc3\u4e0a\u4e09\u4e08\u9ad8\u5899\u3002\u4e09\u4eba\u8fdb\u5230\u56ed\u5185\uff0c\u53ea\u89c1\u788e\u77f3\u9893\u57a3\uff0c \u679d\u53f6\u6ee1\u5730\uff0c\u5206\u660e\u8fd9\u91cc\u7ecf\u8fc7\u4e00\u573a\u6076\u6597\uff0c\u4e8e\u662f\u5faa\u7740\u6253\u6597\u7684\u75d5\u8ff9\uff0c\u89c5\u5230\u4e86\u949f\u4e07\u5802\u7684\u4e66\u623f\uff0c\u623f\u95e8\u5df2\u7ed9 \u5927\u529b\u795e\u9b54\u7528\u5de8\u6728\u649e\u788e\uff0c\u5510\u6653\u6f9c\u4e00\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u949f\u4e07\u5802\u8eba\u5728\u5730\u4e0a\uff0c\u6025\u5fd9\u8fdb\u53bb\uff0c\u4fef\u8eab\u5524\u9053\uff1a\u201c\u949f \u5927\u4fa0\uff0c\u949f\u5927\u4fa0\uff01\u201d\u4e0d\u89c1\u56de\u7b54\uff0c\u7528\u624b\u4e00\u6478\uff0c\u949f\u4e07\u5802\u8eab\u4f53\u51b0\u51b7\uff0c\u5510\u6653\u6f9c\u4e0d\u89c9\u6cea\u5982\u6cc9\u6d8c\uff0c\u54ed\u4e86\u8d77\u6765\uff01 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u6613\u5170\u73e0\u4e5f\u5df2\u6765\u5230\uff0c\u89c1\u72b6\u9a87\u7136\uff0c\u5510\u6653\u6f9c\u9a82\u9053\uff1a\u201c\u90fd\u662f\u5e74\u7fb9\u5c27\u8fd9\u5c0f\u8d3c\u4e0d\u597d\uff01\u201d\u6613 \u5170\u73e0\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u90a3\u5b69\u5b50\u5c45\u7136\u6562\u5c31\u5f11\u5e08\u5c0a\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4e0e\u5f11\u5e08\u4e5f\u5dee\u4e0d\u591a\u3002\u201d\u5f53\u4e0b\u628a \u5728\u6797\u4e2d\u6240\u89c1\u6240\u95fb\u8bf4\u4e86\uff0c\u6613\u5170\u73e0\u4e0d\u89c9\u957f\u53f9\u4e00\u58f0\uff0c\u5fc3\u60f3\uff1a\u201c\u6536\u5f92\u771f\u662f\u4e0d\u53ef\u4e0d\u614e\uff0c\u6666\u660e\u7985\u5e08\u6536\u9519\u695a \u662d\u5357\uff0c\u72ec\u81c2\u795e\u5c3c\u6536\u9519\u4e86\u56e0\uff0c\u90fd\u95f9\u51fa\u6781\u5927\u7684\u98ce\u6ce2\uff0c\u5e74\u7fb9\u5c27\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5c31\u8fd9\u822c\u5389\u5bb3\uff0c\u5c06\u6765\u6240\u505a\u7684 \u6076\u4e8b\uff0c\u53ea\u6015\u6bd4\u695a\u662d\u5357\u548c\u4e86\u56e0\u8fd8\u8981\u66f4\u5927\u66f4\u591a\u3002\u201d\u5510\u6653\u6f9c\u91cc\u91cc\u5916\u5916\u53c8\u5bfb\u4e86\u4e00\u904d\uff0c\u4e0d\u89c1\u51af\u7433\uff0c\u51c4\u7136 \u8bf4\u9053\uff1a\u201c\u6211\u7684\u4f84\u5973\u4e5f\u7ed9\u8d3c\u4eba\u52ab\u53bb\u4e86\u3002\u201d\u6613\u5170\u73e0\u8be7\u9053\uff1a\u201c\u4f60\u6709\u4f84\u5973\uff1f\u201d\u5510\u6653\u6f9c\u5c06\u5f80\u4e8b\u8bf4\u4e86\u4e00 \u904d\uff0c\u6613\u5170\u73e0\u5ffd\u7136\u76ee\u95ea\u7cbe\u5149\uff0c\u8fde\u58f0\u79f0\u5947\u3002\u7a81\u7136\u5bf9\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u968f\u6211\u5230\u5929\u5c71\uff0c\u6211\u8fd8\u4f60\u4e00\u4e2a\u4f84 \u5973\u3002\u201d\u5510\u6653\u6f9c\u83ab\u540d\u5176\u5999\uff0c\u6b63\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u56f4\u5899\u5916\u4eba\u58f0\u9f0e\u6cb8\uff0c\u6709\u4eba\u7528\u65a7\u5934\u94c1\u9504\u5728\u6316\u6398\u56f4\u5899\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5de8\u5ba4\u60ca\u5947\u53d8\uff0c\u8885\u96c4\u521d\u73b0\u5f62\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u6e05\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u4e8c\u56de\u3000\u8bed\u9690\u673a\u950b\u3000\u5fae\u8bcd\u523a\u5de1\u629a\u3000\u6280\u60ca\u56db\u5ea7\u3000\u5927\u4fa0\u663e\u795e\u901a \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u5341\u4e8c\u56de \u8bed\u9690\u673a\u950b\u3000\u5fae\u8bcd\u523a\u5de1\u629a \u6280\u60ca\u56db\u5ea7\u3000\u5927\u4fa0\u663e\u795e\u901a \u3000\u3000\u6613\u5170\u73e0\u5c06\u949f\u4e07\u5802\u7684\u533b\u4e66\u5251\u8bc0\u85cf\u5165\u56ca\u4e2d\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u4e9b\u4e1c\u897f\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u624d\u80fd\u4ea4 \u7ed9\u65e0\u6781\u6d3e\u7684\u4f20\u4eba\u3002\u201d\u56f4\u5899\u5916\u4eba\u58f0\u8d8a\u6765\u8d8a\u5927\uff0c\u539f\u6765\u90a3\u966a\u4f34\u5e74\u7fb9\u5c27\u7684\u8001\u5bb6\u4eba\u4e01\u798f\uff0c\u9887\u662f\u7cbe\u7075\uff0c\u5f53 \u53cc\u9b54\u4e0e\u949f\u4e07\u5802\u6076\u6597\u4e4b\u9645\uff0c\u4ed6\u6084\u6084\u722c\u5230\u5899\u8fb9\uff0c\u5728\u5c0f\u6d1e\u5916\u653e\u51fa\u544a\u6025\u7684\u8baf\u53f7\uff0c\u5e74\u5e9c\u7684\u5bb6\u4e01\u805a\u96c6\u4e86 \u6765\uff0c\u5374\u6ca1\u6709\u4e00\u4e2a\u80fd\u8df3\u8fc7\u9ad8\u5899\uff0c\u53ea\u597d\u7528\u94c1\u9524\u94c1\u51ff\uff0c\u52a8\u571f\u6316\u5899\u3002 \u3000\u3000\u6613\u5170\u73e0\u6536\u62fe\u505c\u5f53\uff0c\u60e8\u7136\u8bf4\u9053\uff1a\u201c\u949f\u4e07\u5802\u7684\u8eab\u540e\u4e8b\uff0c\u81ea\u6709\u5e74\u5e9c\u7684\u4eba\u7167\u6599\uff0c\u54b1\u4eec\u53ef\u4ee5\u4e0d\u5fc5\u7ba1 \u4e86\u3002\u201d\u548c\u5415\u3001\u767d\u3001\u5510\u4e09\u4eba\uff0c\u98de\u8eab\u4e0a\u4e86\u5899\u5934\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5e74\u9050\u9f84\u542c\u7740\uff0c\u949f\u5e08\u5085\u4e3a\u4f60\u7684\u513f\u5b50\u8017 \u5c3d\u5fc3\u8840\uff0c\u8fde\u8001\u547d\u4e5f\u9001\u5728\u4f60\u513f\u5b50\u624b\u4e0a\uff0c\u4f60\u53ef\u5f97\u628a\u4ed6\u597d\u597d\u846c\u4e86\u3002\u201d\u5e74\u5bb6\u7684\u4eba\u54d7\u7136\u5927\u547c\uff0c\u6613\u5170\u73e0\u56db \u4eba\u4ece\u56f4\u5899\u7684\u53e6\u4e00\u8fb9\u98d8\u8eab\u4e0b\u5730\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u98de\u6b65\u8d70\u4e86\u3002 \u3000\u3000\u5929\u660e\u65f6\u5206\uff0c\u56db\u4eba\u5df2\u79bb\u5f00\u4e86\u9648\u7559\uff0c\u6613\u5170\u73e0\u6e89\u7136\u53f9\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u91cd\u5230\u4e2d\u539f\uff0c\u60f3\u4e0d\u5230\u8bb8\u591a\u8001\u53cb \u90fd\u5df2\u5148\u6211\u800c\u53bb\uff0c\u56db\u5a18\uff0c\u6211\u8981\u4e0a\u9099\u5c71\u796d\u5960\u4f60\u7684\u5e08\u5085\uff0c\u624d\u5f97\u5fc3\u5b89\u3002\u201d\u5415\u56db\u5a18\u6d41\u6d95\u62dc\u8c22\u3002\u9099\u5c71\u8ddd\u79bb \u9648\u7559\u516b\u767e\u591a\u91cc\uff0c\u56db\u4eba\u811a\u7a0b\u751a\u5feb\uff0c\u8d70\u4e86\u4e09\u5929\uff0c\u4fbf\u5230\u4e86\u5c71\u4e0a\uff0c\u5510\u6653\u6f9c\u89c1\u540d\u5c71\u4f9d\u65e7\uff0c\u4eba\u4e8b\u5df2\u975e\uff0c\u60f3 \u8d77\u72ec\u81c2\u795e\u5c3c\u5f53\u5e74\u6551\u547d\u4e4b\u6069\u4e0d\u7981\u6006\u7136\u4f24\u6000\u3002\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u56db\u4eba\u540c\u5230\u72ec\u81c2\u795e\u5c3c\u7684\u5893\u56ed\u796d\u626b\uff0c\u53ea\u89c1 \u5893\u7891\u4e0a\u51e0\u4e2a\u5927\u5b57\u5199\u9053\uff1a\u201c\u524d\u660e\u516c\u4e3b\u6b66\u6797\u4fa0\u5c3c\u4e4b\u5893\u3002\u201d\u6613\u5170\u73e0\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u4e2a\u5893\u7891\u9898\u5f97\u5f88 \u597d\u3002\u201d\u60f3\u8d77\u72ec\u81c2\u795e\u5c3c\u4e00\u751f\u574e\u5777\uff0c\u548c\u81ea\u5df1\u7684\u547d\u8fd0\u76f8\u4f3c\uff0c\u53c8\u60f3\u8d77\u4ece\u4eca\u4ee5\u540e\uff0c\u6b66\u6797\u4e2d\u5df2\u518d\u6ca1\u6709\u5251\u672f \u53ef\u4ee5\u548c\u81ea\u5df1\u5339\u654c\u7684\u4eba\uff0c\u66f4\u6709\u4e00\u79cd\u5bc2\u5bde\u4e4b\u611f\uff0c\u6b63\u55df\u53f9\u95f4\uff0c\u5ffd\u89c1\u4e24\u53ea\u5927\u96d5\u4e00\u9ed1\u4e00\u767d\uff0c\u5c55\u5f00\u78e8\u76d8\u5927 \u7684\u7fc5\u8180\uff0c\u5728\u5893\u4e0a\u76d8\u65cb\uff0c\u5415\u56db\u5a18\u62db\u4e86\u62db\u624b\uff0c\u53cc\u96d5\u843d\u4e0b\uff0c\u54c0\u9e23\u4e0d\u5df2\u3002\u6613\u5170\u73e0\u53f9\u9053\uff1a\u201c\u9e1f\u72b9\u5982\u6b64\uff0c \u4eba\u4f55\u4ee5\u582a\uff1f\u201d\u6005\u7136\u56de\u5230\u5eb5\u5802\uff0c\u5bf9\u5415\u56db\u5a18\u51dd\u89c6\u826f\u4e45\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u56db\u5a18\uff0c\u6211\u7b54\u5e94\u4f20\u4f60\u4e00\u70b9\u5185\u529f \u7684\u7a8d\u51b3\uff0c\u4f60\u968f\u6211\u5230\u9759\u5ba4\u6765\u5427\u3002\u201d \u3000\u3000\u539f\u6765\u6613\u5170\u73e0\u89c1\u5415\u56db\u5a18\u989c\u5bb9\u7f8e\u8273\uff0c\u60f3\u7eea\u5979\u591a\u4fdd\u7559\u51e0\u5e74\u9752\u6625\u7f8e\u8c8c\uff0c\u56e0\u6b64\u4fbf\u5e26\u5979\u5230\u9759\u5ba4\u91cc\uff0c\u4f20 \u5979\u201c\u655b\u7cbe\u5185\u89c6\u201d\u4e4b\u6cd5\uff0c\u8fd9\u662f\u53ea\u6709\u5973\u6027\u65b9\u80fd\u4fee\u7ec3\u7684\u5185\u529f\uff0c\u6613\u5170\u73e0\u5e76\u975e\u5f97\u81ea\u6666\u660e\u7985\u5e08\uff0c\u800c\u662f\u5f97\u4e4b \u767d\u53d1\u9b54\u5973\u3002\u539f\u6765\u5f53\u5e74\u767d\u53d1\u9b54\u5973\u56e0\u60c5\u573a\u4e0d\u5e78\uff0c\u9752\u6625\u767d\u53d1\uff0c\u5979\u6700\u7231\u60dc\u989c\u5bb9\uff0c\u56e0\u6b64\u6f5c\u5fc3\u4fee\u7ec3\u4fdd\u5bb9\u4e4b \u9053\uff0c\u76f4\u5230\u66ae\u5e74\uff0c\u624d\u60f3\u51fa\u4e00\u79cd\u53ea\u6709\u5973\u6027\u80fd\u4fee\u7ec3\u7684\u5185\u529f\u517c\u53ef\u4fdd\u5bb9\u7684\u65b9\u6cd5\uff0c\u8fd9\u79cd\u529f\u592b\u867d\u7136\u4e0d\u80fd\u957f\u6625 \u4e0d\u8001\uff0c\u4f46\u5374\u6709\u9a7b\u989c\u4e4b\u6548\uff0c\u82e5\u884c\u4e4b\u5f97\u6cd5\uff0c\u56db\u4e94\u5341\u5c81\u671b\u4e4b\u4ecd\u4f3c\u4e8c\u5341\u8bb8\u4eba\u3002\u5176\u65f6\u767d\u53d1\u9b54\u5973\u5df2\u5c06\u8fd1\u767e \u5c81\uff0c\u81ea\u5df1\u662f\u4e0d\u80fd\u7528\u672c\u8eab\u6765\u8bd5\u9a8c\u4e86\uff0c\u6240\u4ee5\u4f20\u7ed9\u4e86\u6613\u5170\u73e0\u3002\u6613\u5170\u73e0\u521d\u65f6\u4e5f\u6709\u4fee\u7ec3\uff0c\u540e\u6765\u4e08\u592b\u6b7b \u4e86\uff0c\u81ea\u5df1\u72ec\u5904\u7a7a\u5c71\uff0c\u4e5f\u6ca1\u5fc3\u601d\u4fdd\u6b64\u9752\u6625\u8272\u76f8\uff0c\u5c31\u4e0d\u518d\u7ec3\u4e86\u3002\u5982\u4eca\u89c1\u5415\u56db\u5a18\u4e4b\u7f8e\uff0c\u4eba\u95f4\u5c11\u6709\uff0c \u9042\u628a\u201c\u655b\u7cbe\u5185\u89c6\u201d\u7684\u529f\u592b\u4f20\u4e86\u7ed9\u5979\u3002 \u3000\u3000\u8fc7\u4e86\u51e0\u5929\u5415\u56db\u5a18\u5df2\u7ecf\u719f\u4e60\uff0c\u6613\u5170\u73e0\u643a\u4e86\u5510\u6653\u6f9c\u56de\u5929\u5c71\u7ec3\u5251\uff0c\u5415\u56db\u5a18\u9001\u4e0b\u9099\u5c71\uff0c\u4f9d\u4f9d\u4e0d \u820d\u3002\u6613\u5170\u73e0\u9053\uff1a\u201c\u518d\u8fc7\u5341\u5e74\uff0c\u4f60\u7684\u5251\u672f\u5f53\u53ef\u65e0\u654c\u4e8e\u5929\u4e0b\uff0c\u6211\u6709\u4e00\u4e2a\u5f92\u5f1f\uff0c\u90a3\u65f6\u5927\u7ea6\u6b63\u5728\u6c5f\u6e56 \u95ef\u8361\uff0c\u8fd8\u671b\u4f60\u591a\u591a\u62db\u6276\u5979\u3002\u201d\u5415\u56db\u5a18\u8be7\u9053\uff1a\u201c\u6613\u524d\u8f88\u5251\u6cd5\u901a\u7384\uff0c\u4ee4\u5f92\u4e5f\u5fc5\u662f\u9ad8\u624b\u7684\u4e86\uff0c\u4f55\u987b \u5341\u5e74\uff0c\u624d\u80fd\u51fa\u9053\u3002\u201d\u6613\u5170\u73e0\u7b11\u9053\uff1a\u201c\u5979\u73b0\u5728\u8fd8\u53ea\u662f\u4e03\u5c81\u7684\u5973\u5a03\u513f\u5462\uff01\u201d\u5510\u6653\u6f9c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3 \u8d77\u6613\u5170\u73e0\u65e5\u524d\u4e4b\u8a00\uff0c\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u513f\u53ef\u662f\u6211\u8ba4\u8bc6\u7684\uff1f\u201d\u6613\u5170\u73e0\u7b11\u9053\uff1a\u201c\u7b49\u4f60\u5230\u5929\u5c71\u65f6 \u81ea\u5df1\u53bb\u8ba4\u5427\u3002\u201d \u3000\u3000\u6613\u5170\u73e0\u53bb\u540e\uff0c\u5415\u56db\u5a18\u548c\u767d\u6cf0\u5b98\u591a\u9017\u7559\u4e24\u65e5\uff0c\u628a\u5e08\u5085\u7684\u5893\u56ed\u4fee\u8338\u4e00\u65b0\uff0c\u7136\u540e\u5206\u624b\u3002\u4e24\u4eba\u76f8 \u7ea6\u5206\u9080\u540c\u95e8\uff0c\u5411\u4e86\u56e0\u95ee\u7f6a\uff0c\u5206\u624b\u4e4b\u9645\uff0c\u767d\u6cf0\u5b98\u82e5\u6709\u6240\u601d\uff0c\u5ffd\u5bf9\u5415\u56db\u5a18\u9053\uff1a\u201c\u516b\u59b9\uff0c\u4f60\u770b\u5510\u6653 \u6f9c\u8fd9\u4eba\u600e\u6837\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5f88\u4e0d\u9519\u5440\uff01\u201d\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u518d\u8fc7\u51e0\u5e74\uff1a\u4ed6\u5f97\u5230\u5929\u5c71\u5251\u6cd5\u7684\u771f \u4f20\uff0c\u90a3\u5c31\u66f4\u4e0d\u9519\u4e86\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u662f\u5440\uff0c\u5e08\u5144\u8bf4\u8fd9\u4e2a\u5e72\u561b\uff1f\u201d\u767d\u6cf0\u5b98\u7b11\u9053\uff1a\u201c\u516b\u59b9\u8bf7\u6055\u5192 \u6627\uff0c\u6211\u662f\u5728\u60c5\u573a\u4e2d\u6253\u6eda\u7684\u4eba\uff0c\u6211\u770b\u6653\u6f9c\u5bf9\u4f60\u2026\u2026\u201d\u5415\u56db\u5a18\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u5bf9 \u4f60\u4f3c\u4e4e\u9887\u6709\u610f\u601d\u3002\u201d\u5415\u56db\u5a18\u7b11\u5f97\u4f3c\u82b1\u679d\u4e71\u98a4\uff0c\u8bf4\u9053\uff1a\u201c\u4e94\u54e5\uff0c\u4f60\u4e5f\u5fd2\u591a\u5fc3\u4e86\uff0c\u6211\u628a\u6653\u8c30\u5f53\u6210 \u5f1f\u5f1f\uff0c\u90a3\u4f1a\u626f\u5230\u8fd9\u4e2a\u4e0a\u5934\uff01\u201d\u767d\u6cf0\u5b98\u6697\u9053\uff1a\u201c\u53ea\u6015\u522b\u4eba\u4e0d\u662f\u4ec5\u628a\u4f60\u5f53\u6210\u59d0\u59d0\u3002\u201d\u5415\u56db\u5a18\u5140\u81ea \u7b11\u4e2a\u4e0d\u4f11\uff0c\u767d\u6cf0\u5b98\u77a7\u5979\u4e00\u773c\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u4e48\u516b\u59b9\u662f\u53e6\u6709\u610f\u4e2d\u4eba\u4e86\uff1f\u4e0d\u77e5\u662f\u54ea\u4f4d\u6b66\u6797\u8c6a\u6770\uff1f\u201d \u5415\u56db\u5a18\u628a\u5934\u4e00\u6602\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u8981\u6b66\u6797\u4e2d\u7684\u4eba\u4e48\uff1f\u4e94\u54e5\uff0c\u522b\u8c08\u8fd9\u4e2a\u4e86\u3002\u54b1\u4eec\u9080\u4e86\u540c \u95e8\uff0c\u518d\u5230\u9099\u5c71\u805a\u4f1a\u3002\u201d\u626c\u624b\u4f5c\u522b\uff0c\u8ff3\u81ea\u7edd\u5c18\u53bb\u4e86\u3002 \u3000\u3000\u5230\u5f97\u5bb6\u95e8\uff0c\u5df2\u662f\u51ac\u53bb\u6625\u6765\uff0c\u6843\u82b1\u521d\u653e\u3002\u5415\u56db\u5a18\u6ee1\u5fc3\u6b22\u60a6\uff0c\u884c\u5230\u95e8\u524d\uff0c\u731b\u7136\u5403\u4e86\u4e00\u60ca\uff0c\u5927 \u95e8\u5df2\u8d34\u4e0a\u5b98\u5e9c\u7684\u5c01\u6761\uff0c\u5c4b\u524d\u5c4b\u540e\u9759\u6084\u6084\u7684\u4e00\u4e2a\u4eba\u4e5f\u6ca1\u6709\uff0c\u5415\u56db\u5a18\u6b63\u8981\u62d4\u5251\u95ef\u8fdb\uff0c\u5ffd\u542c\u5f97 \u201c\u545c\uff0c\u545c\uff0c\u545c\uff01\u201d\u4e09\u679d\u54cd\u7bad\uff0c\u4e00\u58f0\u957f\uff0c\u4e24\u58f0\u77ed\uff0c\u5728\u5c4b\u540e\u7684\u5c71\u4e0a\u53d1\u51fa\uff0c\u8fd9\u4e43\u662f\u6c5f\u5357\u4e03\u4fa0\u7684\u8054\u7edc \u4fe1\u53f7\uff0c\u5415\u56db\u5a18\u6025\u5c55\u201c\u9646\u5730\u98de\u884c\u201d\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u76f4\u5954\u4e0a\u5c71\uff0c\u5230\u4e86\u5c71\u9876\uff0c\u679c\u7136\u770b\u89c1\u4e8c\u5e08\u5144\u5468\u6e05\u7ad9 \u5728\u4e0a\u9762\uff0c\u6ee1\u9762\u60ca\u60f6\u4e4b\u8272\u3002 \u3000\u3000\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e8c\u54e5\u51e0\u65f6\u6765\u7684\uff1f\u6211\u7239\u7239\u600e\u4e48\u6837\u4e86\uff1f\u201d\u5468\u6e05\u9053\uff1a\u201c\u8d24\u59b9\u8bf7\u968f\u6211\u6765\uff0c\u201d\u5e26\u5415\u56db \u5a18\u8d70\u5165\u5c71\u4e2d\uff0c\u8fdb\u5165\u4e00\u6240\u5e99\u5b87\uff0c\u8fd9\u5ea7\u5e99\u540d\u5524\u201c\u671d\u5143\u5bfa\u201d\uff0c\u4e3b\u6301\u4e00\u5ff5\u548c\u5c1a\u4e43\u662f\u5415\u7559\u826f\u751f\u524d\u597d\u53cb\uff0c \u5415\u56db\u5a18\u8fdb\u5165\u7985\u623f\uff0c\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u81ea\u5df1\u7684\u7236\u4eb2\u9762\u8272\u7126\u9ec4\uff0c\u6c14\u5598\u5401\u5401\u8eba\u5728\u5e8a\u4e0a\uff0c\u4e00\u5ff5\u548c \u5c1a\u7684\u5e08\u5f1f\u4e00\u74e2\u7ad9\u5728\u65c1\u8fb9\uff0c\u6cea\u6d41\u6ee1\u9762\u3002\u5415\u8446\u4e2d\u542c\u5f97\u811a\u6b65\u58f0\u54cd\uff0c\u7741\u5f00\u773c\u775b\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u83b9\u513f \u56de\u6765\u4e86\u5417\uff1f\u201d\u5415\u56db\u5a18\u6025\u5fd9\u8dea\u5728\u5e8a\u524d\uff0c\u62b1\u7740\u7236\u4eb2\uff0c\u53ea\u542c\u5f97\u7236\u4eb2\u65ad\u65ad\u7eed\u7eed\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5728\u5bbd\u54e5 \u54e5\u5df2\u88ab\u6355\u53bb\uff0c\u4e00\u5ff5\u5927\u5e08\u4e3a\u4e86\u6551\u6211\u5df2\u727a\u7272\u4e86\uff0c\u4f60\u8981\u4e3a\u6211\u4eec\u62a5\u4ec7\uff01\u201d\u58f0\u97f3\u8d8a\u8bf4\u8d8a\u5f31\uff0c\u8bf4\u7f62\uff0c\u4e24\u811a \u4e00\u4f38\uff0c\u65ad\u4e86\u547c\u5438\uff01 \u3000\u3000\u5415\u56db\u5a18\u53f7\u9676\u5927\u54ed\uff0c\u5468\u6e05\u9053\uff1a\u201c\u516b\u59b9\u8282\u54c0\uff0c\u5e94\u53d8\u8981\u7d27\u3002\u201d\u5415\u56db\u5a18\u5fcd\u4e86\u773c\u6cea\uff0c\u542c\u5468\u6e05\u9053\uff1a \u201c\u6c88\u5148\u751f\u88ab\u6355\u4e0d\u8fc7\u4e24\u65e5\uff0c\u56da\u8f66\u8fd8\u8981\u597d\u591a\u5929\u624d\u80fd\u5230\u7701\uff0c\u516d\u5f1f\u5728\u524d\u9762\u76f8\u5019\uff0c\u516b\u59b9\uff0c\u4f60\u8d76\u4e0a\u53bb\uff0c\u8fd8 \u6765\u5f97\u53ca\u3002\u62a5\u6740\u7236\u4ec7\uff0c\u6551\u751f\u8005\uff0c\u91cd\u4e8e\u62ab\u9ebb\u6234\u5b5d\uff0c\u4f2f\u7236\u7684\u57cb\u846c\u6709\u6211\u548c\u5bfa\u50e7\u6599\u7406\uff0c\u4f60\u5feb\u53bb\u628a\u6c88\u5148\u751f \u6551\u51fa\u6765\u5427\u3002\u201d \u3000\u3000\u539f\u6765\u5415\u56db\u5a18\u7684\u7956\u7236\u5415\u7559\u826f\uff0c\u7737\u6000\u6545\u56fd\uff0c\u7acb\u8bba\u8457\u4e66\uff0c\u65a5\u864f\u6518\u5937\uff0c\u4e0d\u4f59\u9057\u529b\u3002\u4ed6\u7684\u513f\u5b50\u5415\u8446 \u4e2d\u3001\u5415\u6bc5\u4e2d\u3001\u95e8\u4eba\u4e25\u6d2a\u9035\u7b49\uff0c\u5728\u4ed6\u53bb\u4e16\u4e4b\u540e\uff0c\u4ecd\u63a8\u5c0a\u8bf5\u6cd5\uff0c\u5907\u8ff0\u9057\u8a00\u3002\u6c88\u5728\u5bbd\u5219\u662f\u5415\u8446\u4e2d\u7684 \u95e8\u751f\uff0c\u548c\u5415\u56db\u5a18\u6781\u4e3a\u76f8\u6295\u3002\u8fd9\u6b21\u7684\u4e8b\u4ef6\uff0c\u8d77\u56e0\u4e8e\u4e25\u6d2a\u9035\u7684\u65e5\u8bb0\u3002\u4e25\u6240\u8457\u7684\u65e5\u8bb0\uff0c\u6781\u610f\u4f4e\u65a5\u6ee1 \u5dde\uff0c\u51e1\u5f53\u65f6\u707e\u5f02\u7978\u4e71\uff0c\u90fd\u8be6\u52a0\u53d9\u8ff0\uff0c\u4e0d\u7a0d\u9690\u8bb3\u3002\u8fd9\u672c\u65e5\u8bb0\u88ab\u4ed6\u7684\u4e00\u4e2a\u5b66\u751f\u76d7\u53bb\uff0c\u5077\u5077\u544a\u53d1\uff0c \u5b98\u5dee\u6765\u6355\uff0c\u4e25\u6d2a\u9035\u548c\u5415\u6bc5\u4e2d\u6070\u5de7\u51fa\u95e8\u3002\u5415\u8446\u4e2d\u548c\u6c88\u5728\u5bbd\u5219\u88ab\u6355\u4e86\u3002\u5176\u65f6\u5468\u6e05\u6b63\u5de7\u56e0\u8bbf\u5415\u56db\u5a18 \u800c\u4f4f\u5728\u5415\u5bb6\uff0c\u4ed6\u9003\u51fa\u540e\uff0c\u6025\u548c\u671d\u5143\u5bfa\u7684\u4e3b\u6301\u4e00\u5ff5\u7985\u5e08\u8d76\u51fa\u4e09\u5341\u91cc\u5916\u622a\u52ab\uff0c\u4e00\u573a\u5267\u6218\uff0c\u4e00\u5ff5\u7985 \u5e08\u53d7\u4e86\u4e03\u5904\u91cd\u4f24\uff0c\u5468\u6e05\u4e5f\u4e2d\u4e86\u4e00\u5251\uff0c\u62fc\u6b7b\u628a\u5415\u8446\u4e2d\u52ab\u4e86\u51fa\u6765\uff0c\u9001\u56de\u5bfa\u5185\uff0c\u4e00\u5ff5\u7985\u5e08\u5df2\u7ecf\u56e0\u4f24 \u91cd\u6b7b\u4e86\u3002 \u3000\u3000\u5415\u56db\u5a18\u542c\u5f97\u54ac\u7259\u5207\u9f7f\uff0c\u5bf9\u5468\u6e05\u4e00\u63d6\u5230\u5730\uff0c\u6124\u7136\u8bf4\u9053\uff1a\u201c\u6b64\u4ec7\u4e0d\u62a5\uff0c\u8a93\u4e0d\u4e3a\u4eba\uff0c\u5e08\u5144\uff0c\u4f60 \u597d\u597d\u517b\u4f24\uff0c\u6211\u8981\u628a\u90a3\u4e9b\u9791\u5b50\u7684\u5934\u9885\uff0c\u53d6\u6765\u796d\u5960\u3002\u201d\u95ee\u4e86\u5468\u6e05\u4e0e\u8def\u6c11\u77bb\u76f8\u7ea6\u7684\u5730\u70b9\uff0c\u4fbf\u5373\u98de\u5954 \u800c\u53bb\u3002 \u3000\u3000\u6e05\u5ef7\u8fd9\u6b21\u9063\u6765\u6349\u62ff\u94a6\u72af\u7684\u5fa1\u6797\u519b\u7531\u7edf\u9886\u79e6\u4e2d\u8d8a\u7387\u9886\uff0c\u6b64\u4eba\u4f7f\u5224\u5b98\u53cc\u7b14\uff0c\u662f\u4e2a\u6253\u7a74\u540d\u5bb6\u3002 \u53e6\u5916\u56db\u7687\u5b50\u5141\u796f\u4e5f\u63a8\u8350\u4e86\u4e24\u4eba\u76f8\u52a9\u3002\u8fd9\u4e24\u4eba\u4e00\u4e2a\u662f\u897f\u5317\u8457\u540d\u7684\u5de8\u5bc7\u7518\u5929\u9f99\uff0c\u4e00\u4e2a\u662f\u5f62\u610f\u6d3e\u7684 \u5143\u8001\u8463\u5de8\u5ddd\uff0c\u90fd\u662f\u6c5f\u6e56\u4e0a\u7684\u6210\u540d\u4eba\u7269\uff0c\u88ab\u56db\u7687\u5b50\u7f51\u7f57\u95e8\u4e0b\uff0c\u6708\u524d\u4e14\u66fe\u966a\u8fc7\u5141\u5e27\u5230\u5c11\u6797\u5bfa\u7684\u3002 \u8fd9\u4e09\u4eba\u90fd\u662f\u6c5f\u6e56\u4e0a\u7684\u4e00\u6d41\u597d\u624b\uff0c\u4e0d\u6599\u4e3b\u72af\u5415\u8446\u4e2d\u8fd8\u662f\u5728\u534a\u8def\u4e0a\u7ed9\u4eba\u52ab\u53bb\uff0c\u56e0\u6b64\u4e00\u8def\u63d0\u5fc3\u540a \u80c6\uff0c\u53ea\u76fc\u80fd\u65e9\u65e5\u5230\u8fbe\u7701\u57ce\uff0c\u7136\u540e\u7531\u6d59\u629a\u674e\u536b\u52a0\u6d3e\u597d\u624b\uff0c\u62bc\u5f80\u4eac\u5e08\u3002 \u3000\u3000\u8fd9\u65e5\u62bc\u89e3\u6c88\u5728\u5bbd\u7684\u5b98\u5dee\u5df2\u8fc7\u4e86\u5b5d\u611f\uff0c\u6b63\u884c\u8fdb\u5929\u76ee\u5c71\u533a\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u9a6c\u94c3\u53ee\u5f53\uff0c\u5415\u56db\u5a18\u5750 \u7740\u4e00\u9a91\u767d\u9a6c\uff0c\u7edd\u5c18\u98de\u6765\uff0c\u7518\u8463\u4e8c\u4eba\u9762\u8272\u500f\u53d8\uff0c\u50ac\u5fa1\u6797\u519b\u6025\u8d70\uff0c\u79e6\u4e2d\u8d8a\u9053\uff1a\u201c\u4e00\u4e2a\u5b64\u8eab\u5973\u5b50\uff0c \u4e24\u4f4d\u6015\u5979\u4f5c\u751a\uff1f\u201d\u7518\u5929\u9f99\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u79e6\u7edf\u65ad\u540e\uff0c\u6211\u4eec\u5728\u524d\u5f00\u8def\u3002\u201d\u8463\u5de8 \u5ddd\u9053\uff1a\u201c\u6765\u7684\u4e43\u662f\u5415\u7559\u826f\u7684\u5b59\u5973\u5415\u56db\u5a18\uff0c\u5979\u7684\u5251\u672f\u5f88\u662f\u624e\u624b\uff0c\u79e6\u7edf\u9886\u53ef\u8981\u7559\u5fc3\u3002\u201d\u79e6\u4e2d\u8d8a \u9053\uff1a\u201c\u90a3\u6b63\u597d\u4e86\uff0c\u8d70\u4e86\u53db\u9006\uff0c\u628a\u5415\u8d3c\u7684\u5973\u513f\u64d2\u6765\u4e5f\u662f\u4e00\u529f\u3002\u201d\u7518\u3001\u8463\u4e8c\u4eba\u6df1\u77e5\u5415\u56db\u5a18\u5389\u5bb3\uff0c \u4ed6\u4eec\u4f30\u8ba1\uff0c\u5355\u6253\u72ec\u6597\uff0c\u7edd\u5bf9\u4e0d\u662f\u5415\u56db\u5a18\u5bf9\u624b\uff0c\u82e5\u4ee5\u4e8c\u654c\u4e00\uff0c\u5374\u53c8\u6298\u4e86\u6c5f\u6e56\u4e0a\u7684\u540d\u5934\uff0c\u5728\u79e6\u4e2d \u8d8a\u9762\u524d\u4e5f\u4e0d\u597d\u770b\u3002\u6240\u4ee5\u7d20\u6027\u8ba9\u79e6\u4e2d\u8d8a\u65ad\u540e\uff0c\u6210\u5fc3\u8981\u8ba9\u5415\u56db\u5a18\u6298\u6298\u4ed6\u7684\u5a01\u98ce\uff0c\u7136\u540e\u518d\u53bb\u6551\u4ed6\u3002 \u7518\u3001\u8463\u4e8c\u4eba\u8001\u5978\u5de8\u6ed1\uff0c\u79e6\u4e2d\u8d8a\u54ea\u91cc\u77e5\u9053\u4ed6\u4eec\u7528\u610f\uff0c\u5fc3\u4e2d\u8fd8\u5728\u6697\u7b11\u3002 \u3000\u3000\u79e6\u4e2d\u8d8a\u5fc3\u4e2d\u6697\u7b11\uff1a\u8fd9\u4e24\u4eba\u771f\u662f\u6d6a\u5f97\u865a\u540d\uff0c\u8fde\u4e00\u4e2a\u5355\u8eab\u5973\u5b50\uff0c\u4e5f\u8fd9\u822c\u5bb3\u6015\u3002\u5f53\u4e0b\u62e8\u8f6c\u9a6c \u5934\uff0c\u8fce\u4e0a\u524d\u53bb\uff0c\u5415\u56db\u5a18\u5feb\u9a6c\u5636\u98ce\uff0c\u500f\u7684\u6765\u5230\uff0c\u79e6\u4e2d\u8d8a\u53cc\u7b14\u4e00\u626c\uff0c\u559d\u9053\uff1a\u201c\u597d\u4e2a\u5927\u80c6\u7684\u5973 \u8d3c\uff01\u201d\u628a\u9a6c\u4e00\u5939\uff0c\u8fce\u9762\u649e\u53bb\uff0c\u53cc\u7b14\u201c\u98ce\u96f7\u5939\u51fb\u201d\uff0c\u53cc\u70b9\u5415\u56db\u5a18\u7684\u201c\u5370\u5802\u7a74\u201d\u90a3\u6599\u773c\u524d\u4e00\u82b1\uff0c \u5415\u56db\u5a18\u5728\u9a6c\u80cc\u4e0a\u7a81\u7136\u63a0\u8d77\uff01\u957f\u5251\u5728\u534a\u7a7a\u6296\u8d77\u4e86\u6597\u5927\u7684\u5251\u82b1\uff0c\u9aa4\u7136\u4e0b\u5288\uff0c\u79e6\u4e2d\u8d8a\u6025\u5fd9\u4e00\u4e2a\u201c\u956b \u91cc\u85cf\u8eab\u201d\uff0c\u53ea\u542c\u5f97\u5750\u9a91\u5ffd\u7136\u60e8\u53eb\uff0c\u56db\u811a\u671d\u5929\uff0c\u79e6\u4e2d\u8d8a\u4e00\u6eda\u4e0b\u5730\uff0c\u90a3\u5339\u9a6c\u5df2\u7ed9\u5415\u56db\u5a18\u65a9\u4e86\u3002\u5415 \u56db\u5a18\u811a\u5c16\u70b9\u5730\uff0c\u5251\u5149\u95ea\u5904\uff0c\u8fde\u4f24\u4e86\u51e0\u540d\u5fa1\u6797\u519b\u5352\uff0c\u79e6\u4e2d\u8d8a\u52c3\u7136\u5927\u6012\uff0c\u5728\u5730\u4e0a\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253 \u633a\u201d\uff0c\u7ffb\u4e86\u8d77\u6765\uff0c\u7bad\u6b65\u7a9c\u524d\uff0c\u5224\u5b98\u7b14\u5411\u4e0a\u6a2a\u8fce\uff0c\u53ea\u542c\u5f97\u201c\u53ee\u5f53\u201d\u4e00\u58f0\uff0c\u5200\u7b14\u76f8\u649e\uff0c\u53d1\u51fa\u5c16\u9510 \u60a0\u957f\u7684\u54cd\u97f3\uff0c\u706b\u661f\u84ec\u84ec\u4e71\u7206\uff0c\u79e6\u4e2d\u8d8a\u53cc\u81c2\u9165\u9ebb\uff0c\u5415\u56db\u5a18\u4e5f\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u8fd9\u9e70\u722a\u5b59\u529f\u592b\u4e0d \u5f31\uff01\u201d\u971c\u534e\u5251\u76f4\u653b\u8fc7\u6765\uff0c\u4e00\u62db\u4e09\u5f0f\uff0c\u622a\u8170\u65a9\u808b\u523a\u80f8\uff0c\u75be\u5982\u95ea\u7535\u3002\u79e6\u4e2d\u8d8a\u6643\u8eab\u9000\u6b65\uff0c\u5de6\u7b14\u6a2a\u622a \u5251\u8eab\uff0c\u53f3\u7b14\u201c\u91d1\u9f99\u63a2\u722a\u201d\uff0c\u9aa4\u7167\u808b\u9aa8\u201c\u592a\u4e59\u7a74\u201d\u6253\u53bb\u3002\u5415\u56db\u5a18\u4e00\u58f0\u51b7\u7b11\uff0c\u7528\u4e2a\u201c\u79cb\u6c34\u6a2a\u821f\u201d \u4e4b\u52bf\uff0c\u4e00\u65cb\u4e00\u5c01\uff0c\u53cc\u7b14\u53c8\u7ed9\u8361\u5f00\uff0c\u5415\u56db\u5a18\u5530\u5530\u4e24\u5251\uff0c\u4ed6\u523a\u79e6\u4e2d\u8d8a\u5de6\u53f3\u8981\u5bb3\uff0c\u79e6\u4e2d\u8d8a\u8fde\u8fde\u9000 \u540e\uff0c\u7ed9\u5415\u56db\u5a18\u6740\u5f97\u624b\u5fd9\u811a\u4e71\u3002\u8463\u5de8\u5ddd\u4e0e\u7518\u5929\u9f99\u76f8\u89c6\u800c\u7b11\uff0c\u8463\u5de8\u5ddd\u9053\uff1a\u201c\u884c\u4e86\uff0c\u8001\u5f1f\uff0c\u8be5\u51fa\u624b \u6551\u4ed6\u4e86\u3002\u201d\u7518\u5929\u9f99\u5e94\u58f0\u4e0b\u9a6c\uff0c\u957f\u5251\u4e00\u6296\uff0c\u5411\u5415\u56db\u5a18\u5206\u5fc3\u523a\u6765\u3002 \u3000\u3000\u5415\u56db\u5a18\u8ba4\u5f97\u7518\u5929\u9f99\u5c31\u662f\u966a\u5141\u796f\u95ef\u5c11\u6797\u5bfa\u7684\u4eba\uff0c\u65a5\u9053\uff1a\u201c\u8001\u8d3c\uff0c\u5c11\u6797\u5bfa\u9976\u4e86\u4f60\u7684\u72d7\u547d\uff0c\u4f60 \u53c8\u5230\u8fd9\u91cc\u4f5c\u6076\u3002\u201d\u5b9d\u5251\u4e00\u62bd\uff0c\u4e00\u62db\u201c\u767d\u9e64\u4eae\u7fc5\u201d\uff0c\u628a\u7518\u5929\u9f99\u957f\u5251\u6321\u5f00\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u201c\u795e\u9f99\u6389 \u5c3e\u201d\uff0c\u53c8\u628a\u79e6\u4e2d\u8d8a\u903c\u9000\u3002\u7518\u5929\u9f99\u4e0e\u79e6\u4e2d\u8d8a\u6253\u4e86\u4e00\u4e2a\u62db\u547c\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u70b9\u7a74\uff0c\u6211\u6765\u53d6\u5979\uff01\u201d\u957f \u5251\u6a2a\u5c55\uff0c\u518d\u5ea6\u6251\u4e0a\u3002\u5415\u56db\u5a18\u8fde\u53d1\u4e09\u5251\uff0c\u90fd\u7ed9\u4ed6\u4e00\u4e00\u6321\u5f00\u3002\u8fd9\u7518\u5929\u9f99\u6b66\u529f\u8fdc\u5728\u79e6\u4e2d\u8d8a\u4e4b\u4e0a\uff0c\u62db \u672f\u6e9c\u6ed1\u5f02\u5e38\uff0c\u5415\u56db\u5a18\u5927\u6012\uff0c\u5251\u5149\u970d\u970d\u5c55\u5f00\uff0c\u75be\u5982\u98ce\u96e8\uff0c\u628a\u7518\u5929\u9f99\u6740\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8 \u624b\u4e4b\u529b\uff0c\u5e78\u6709\u79e6\u4e2d\u8d8a\u5728\u65c1\u4fa7\u88ad\uff0c\u53cc\u7b14\u5728\u5251\u5149\u98de\u821e\u4e2d\u5bfb\u7455\u8e48\u9699\uff0c\u4f3a\u673a\u70b9\u7a74\uff0c\u4ee4\u5415\u56db\u5a18\u4e0d\u80fd\u5168\u529b \u8fdb\u653b\uff0c\u8981\u4e0d\u7136\u7518\u5927\u9f99\u4e5f\u65e9\u5df2\u843d\u8d25\u4e86\u3002 \u3000\u3000\u7518\u79e6\u4e8c\u4eba\u4ee5\u4e8c\u654c\u4e00\uff0c\u5140\u662f\u5904\u5728\u4e0b\u98ce\uff0c\u5415\u56db\u5a18\u653b\u5982\u96f7\u9706\u75be\u53d1\uff0c\u5b88\u5982\u6c5f\u6d77\u51dd\u5149\uff0c\u5fa1\u6797\u519b\u867d \u591a\uff0c\u5374\u63d2\u4e0d\u8fdb\u624b\u3002\u8463\u5de8\u5ddd\u624b\u81c2\u4e00\u6325\uff0c\u4e24\u679d\u900f\u9aa8\u9489\u7834\u7a7a\u5c04\u6765\uff0c\u5728\u4e09\u4eba\u8d70\u9a6c\u706f\u822c\u7684\u53ae\u6740\u4e2d\u5c45\u7136\u8ba4 \u7a74\u5947\u51c6\uff0c\u4e24\u679d\u6bd2\u9489\uff0c\u6253\u5411\u5415\u56db\u5a18\u8111\u540e\u7684\u201c\u9b42\u98de\u7a74\u201d\u548c\u7709\u5c16\u7684\u201c\u8d1e\u767d\u7a74\u201d\uff0c\u5415\u56db\u5a18\u7528\u5251\u4e00\u683c\uff0c \u628a\u7b2c\u4e00\u679a\u6bd2\u9489\u6253\u843d\uff0c\u63a5\u7740\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\u628a\u7b2c\u4e8c\u679a\u6bd2\u9489\u4e5f\u907f\u8fc7\u4e86\uff0c\u5bf9\u654c\u4eba\u8ba4\u7a74\u7684\u51c6\u786e\uff0c\u4e5f\u4e0d \u7531\u4e00\u9707\u3002\u8463\u5de8\u5ddd\u7b2c\u4e09\u3001\u7b2c\u56db\u3001\u7b2c\u4e94\u53ea\u6bd2\u9489\uff0c\u53c8\u8fde\u73af\u98de\u81f3\u3002\u5415\u56db\u5a18\u867d\u4ed7\u7740\u7cbe\u5999\u7684\u5251\u672f\uff0c\u8f7b\u7075\u7684 \u8eab\u6cd5\uff0c\u4e00\u4e00\u907f\u8fc7\uff0c\u4f46\u4e5f\u611f\u5230\u9887\u4e3a\u5403\u529b\uff0c\u987f\u5bf9\u5f3a\u5f31\u6613\u52bf\uff0c\u5728\u7518\u79e6\u4e8c\u4eba\u73af\u653b\u4e4b\u4e0b\u8f6c\u5904\u4e0b\u98ce\u3002 \u3000\u3000\u7518\u5929\u9f99\u5927\u559c\uff0c\u957f\u5251\u5288\u524a\u62b9\u523a\uff0c\u6539\u5b88\u4e3a\u653b\uff0c\u62db\u62db\u8f9b\u8fa3\uff0c\u79e6\u4e2d\u8d8a\u5224\u5b98\u53cc\u7b14\u4e00\u7f29\u4e00\u4f38\uff0c\u4e5f\u662f\u4e0d \u79bb\u5979\u7684\u4e09\u5341\u516d\u9053\u5927\u7a74\uff0c\u5415\u56db\u5a18\u8981\u907f\u8463\u5de8\u5ddd\u7684\u900f\u9aa8\u6bd2\u9489\uff0c\u5206\u4e86\u5fc3\u795e\uff0c\u5f04\u5f97\u9669\u8c61\u73af\u751f\uff0c\u94f6\u7259\u4e00 \u54ac\uff0c\u9661\u7136\u6a2a\u5251\u4e00\u5c01\uff0c\u628a\u7518\u5929\u9f99\u7684\u957f\u5251\uff0c\u79e6\u4e2d\u8d8a\u7684\u53cc\u7b14\u5168\u90fd\u683c\u5f00\uff0c\u5c31\u4ece\u7518\u5929\u9f99\u7684\u5de6\u80a9\u5934\u4e0a\uff0c\u4e00 \u63a0\u8fc7\u53bb\uff0c\u5389\u58f0\u65a5\u9053\uff1a\u201c\u6211\u5148\u628a\u4f60\u8fd9\u653e\u6697\u5668\u4f24\u4eba\u7684\u65e0\u803b\u8001\u8d3c\u6740\u6389\uff01\u201d\u6325\u5251\u76f4\u5954\u524d\u5934\u7684\u8463\u5de8\u5ddd\u3002 \u8463\u5de8\u5ddd\u559d\u58f0\u201c\u6765\u5f97\u597d\uff01\u201d\u4e00\u6296\u624b\uff0c\u628a\u4e09\u679d\u900f\u9aa8\u6bd2\u9489\uff0c\u7528\u201c\u8fce\u95e8\u4e09\u4e0d\u8fc7\u201d\u7684\u6253\u6cd5\uff0c\u5206\u4e0a\u4e2d\u4e0b\u4e09 \u8def\uff0c\u9f50\u9f50\u6253\u6765\uff0c\u4e09\u9489\u9f50\u5230\uff0c\u8fd9\u79cd\u624b\u6cd5\uff0c\u5341\u5206\u5389\u5bb3\uff0c\u654c\u4eba\u672c\u9886\u7eb5\u9ad8\uff0c\u4e5f\u96be\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u95ea\u67b6 \u8eb2\u907f\u3002\u90a3\u6599\u5415\u56db\u5a18\u4e0d\u614c\u4e0d\u5fd9\uff0c\u4e24\u81c2\u4e00\u6296\uff0c\u4f7f\u4e2a\u201c\u767d\u9e64\u51b2\u5929\u201d\uff0c\u5e73\u5730\u62d4\u8d77\u4e24\u4e08\u591a\u9ad8\uff0c\u4e09\u679a\u6bd2 \u9489\uff0c\u8d34\u7740\u811a\u4e0b\u6253\u8fc7\uff0c\u98de\u51fa\u4e94\u516d\u4e08\u5916\u3002\u5415\u56db\u5a18\u5728\u534a\u7a7a\u4e2d\u4e00\u58f0\u5927\u559d\uff0c\u970d\u5730\u8fde\u4eba\u5e26\u5251\uff0c\u76f4\u98de\u4e0b\u6765\u3002 \u5fa1\u4f11\u519b\u7eb7\u7eb7\u56f4\u4e0a\uff0c\u5415\u56db\u5a18\u5b9d\u5251\u5de6\u62ab\u53f3\u8361\uff0c\u6740\u4f24\u4e86\u5341\u4f59\u4eba\uff0c\u4ecd\u60f3\u76f4\u5954\u8463\u5de8\u5ddd\u770b\u5b88\u7684\u56da\u8f66\uff0c\u5fa1\u6797 \u519b\u4ee5\u4e8b\u5173\u94a6\u72af\uff0c\u62fc\u6b7b\u62b5\u6321\uff0c\u4eba\u591a\u52bf\u4f17\uff0c\u4e00\u65f6\u4e0d\u6613\u95ef\u8fc7\u3002\u7518\u5929\u9f99\u4e0e\u79e6\u4e2d\u8d8a\u6025\u5fd9\u8d76\u6765\uff0c\u5415\u56db\u5a18\u5de6 \u81c2\u8fde\u626c\uff0c\u653e\u51fa\u4e09\u652f\u54cd\u7bad\uff0c\u545c\u9e23\u8fde\u58f0\uff0c\u4e00\u58f0\u957f\u3001\u4e24\u58f0\u77ed\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u9053\u65c1\u7684\u5c71\u6797\u5ffd\u5730\u91cc\u54e8\u58f0\u56db \u8d77\uff0c\u51b2\u51fa\u4e86\u51e0\u5341\u6761\u6c49\u5b50\uff0c\u98de\u8757\u7ba9\u7bad\uff0c\u7eb7\u843d\u5982\u96e8\uff0c\u5fa1\u6797\u519b\u6025\u5fd9\u4f0f\u5730\u5bf9\u5c04\u3002\u8fd9\u961f\u4eba\u4e2d\u6709\u4e2a\u767d\u8863\u5c11 \u5e74\uff0c\u7a81\u9aa4\u51b2\u51fa\uff0c\u5728\u7bad\u96e8\u4e2d\u6325\u5200\u76f4\u8fdb\uff0c\u5e26\u9886\u4eba\u9a6c\uff0c\u51b2\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u767d\u8863\u5c11\u5e74\u6b63\u662f\u72ec\u81c2\u795e\u5c3c\u7684\u7b2c\u516d\u4e2a\u5f92\u5f1f\uff0c\u540d\u53eb\u8def\u6c11\u77bb\uff0c\u8def\u6c11\u77bb\u4e43\u6d59\u6c5f\u4e8e\u6f5c\u7684\u5bcc\u5bb6\u5b50\u5f1f\uff0c \u8fd9\u56de\u62fc\u4e86\u8eab\u5bb6\uff0c\u628a\u5bb6\u4e01\u5e26\u4e86\u51fa\u6765\uff0c\u52a9\u5415\u56db\u5a18\u62a2\u52ab\u56da\u8f66\u3002 \u3000\u3000\u8463\u5de8\u5ddd\u770b\u8def\u6c11\u77bb\u6251\u5230\uff0c\u559d\u9053\uff1a\u201c\u8def\u516c\u5b50\uff0c\u4f60\u4e5f\u6562\u9020\u53cd\u4e48\uff1f\u201d\u8def\u6c11\u77bb\u6068\u4ed6\u8511\u89c6\uff0c\u54d7\u54d7\u4e24 \u5200\uff0c\u76f4\u5288\u4e0b\u6765\uff0c\u8463\u5de8\u5ddd\u7528\u4e2a\u201c\u9738\u738b\u5378\u7532\u201d\uff0c\u53cc\u638c\u4e00\u5f15\u4e00\u63a8\uff0c\u52a8\u4f5c\u751a\u67d4\uff0c\u5374\u662f\u5185\u85cf\u52b2\u529b\uff0c\u8def\u6c11 \u77bb\u4e24\u5200\u843d\u7a7a\uff0c\u7ed9\u4ed6\u638c\u529b\u903c\u9000\u3002\u8463\u5de8\u5ddd\u4e58\u80dc\u8ffd\u51fb\uff0c\u817e\u7684\u98de\u8d77\u4e00\u817f\uff0c\u8def\u6c11\u77bb\u9632\u4e0d\u53ca\u9632\uff0c\u624b\u4e2d\u5200\u7ed9 \u4ed6\u8e22\u5f97\u98de\u4e0a\u534a\u5929\u3002\u8463\u5de8\u5ddd\u4e43\u5f62\u610f\u6d3e\u7684\u540d\u5bbf\uff0c\u638c\u529b\u4ee5\u67d4\u514b\u521a\uff0c\u5df2\u7ec3\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u4e00\u811a\u8e22\u98de \u5175\u5203\uff0c\u4e58\u9699\u8fdb\u8eab\uff0c\u5de6\u81c2\u4e00\u8d77\uff0c\u4f3c\u70b9\u4f3c\u6233\uff0c\u5374\u662f\u865a\u5f0f\uff0c\u53f3\u81c2\u4e00\u7a7f\uff0c\u638c\u5982\u5377\u74e6\uff0c\u559d\u58f0\u201c\u5012\u4e0b\uff01\u201d \u638c\u5fc3\u4e00\u6309\uff0c\u53c8\u52b2\u53c8\u75be\u3002\u8def\u6c11\u77bb\u541e\u80f8\u5438\u8179\uff0c\u6025\u4f7f\u72ec\u81c2\u795e\u5c3c\u6240\u6388\u7684\u9632\u8eab\u638c\u6cd5\uff0c\u624b\u81c2\u4e00\u7275\uff0c\u8eab\u5b50\u540e \u4ef0\uff0c\u53ea\u6643\u4e86\u51e0\u6643\uff0c\u5374\u5e76\u672a\u8dcc\u5012\u3002\u8def\u6c11\u77bb\u5728\u6c5f\u5357\u4e03\u4fa0\u4e4b\u4e2d\uff0c\u6b66\u529f\u5e73\u5e38\uff0c\u8463\u5de8\u5ddd\u8f7b\u654c\u8fc7\u751a\uff0c\u5374\u4e0d \u6599\u5927\u8c61\u867d\u7626\uff0c\u4ea6\u6709\u5343\u65a4\uff0c\u540d\u5e08\u95e8\u4e0b\uff0c\u90a3\u53ef\u8f7b\u89c6\uff1f\u8def\u6c11\u77bb\u4e58\u7740\u8463\u5de8\u5ddd\u62db\u5f0f\u7528\u8001\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u53cc \u638c\u8fde\u73af\u53d1\u51fa\uff0c\u731d\u51fb\u8463\u5de8\u5ddd\u4e0b\u76d8\uff01\u8463\u5de8\u5ddd\u5403\u4e86\u4e00\u60ca\uff0c\u53cc\u638c\u5408\u62e2\uff0c\u5f80\u4e0b\u4e00\u5206\uff0c\u582a\u582a\u628a\u8def\u6c11\u77bb\u62db\u5f0f \u7834\u5f00\u3002\u6df7\u6218\u4e2d\u5415\u56db\u5a18\u8fd0\u5251\u5982\u98ce\uff0c\u51b2\u51fa\u4e86\u5fa1\u6797\u519b\u5305\u56f4\uff0c\u76f4\u5f80\u56da\u8f66\u62a2\u53bb\u3002 \u3000\u3000\u79e6\u4e2d\u8d8a\u4e0e\u7518\u5929\u9f99\u4e8c\u4eba\u62e6\u5979\u4e0d\u4f4f\uff0c\u8463\u5de8\u5ddd\u751f\u6015\u94a6\u72af\u88ab\u52ab\uff0c\u65e0\u5fc3\u604b\u6218\uff0c\u8eab\u5f62\u4e00\u9000\uff0c\u8def\u6c11\u77bb\u8ddf \u6b65\u8fdb\u51fb\uff0c\u7ed9\u4ed6\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u62bd\u6492\u4e4b\u95f4\uff0c\u5df2\u7ecf\u53d8\u4e3a\u638c\u5fc3\u5411\u4e0b\uff0c\u624b\u80cc\u5411\u4e0a\uff0c\u53cc\u638c\u9a88\u98df\u4e2d\u4e8c\u6307\uff0c \u5f80\u4e0a\u4e00\u6233\uff0c\u53cd\u70b9\u8def\u6c11\u77bb\u4e24\u814b\u4e0b\u7684\u201c\u671f\u95e8\u7a74\u201d\uff0c\u8def\u6c11\u77bb\u5230\u5e95\u706b\u5019\u672a\u6df1\uff0c\u7edd\u6599\u4e0d\u5230\u4ed6\u4ee5\u9000\u4e3a\u8fdb\uff0c \u53d8\u62db\u5982\u6b64\u8fc5\u901f\uff0c\u4e24\u814b\u90fd\u7ed9\u70b9\u7740\uff0c\u5411\u540e\u4fbf\u5012\uff0c\u5e78\u6709\u5bb6\u4e01\u6276\u4f4f\uff0c\u4f46\u5df2\u662f\u9762\u9752\u5507\u767d\uff0c\u6c57\u73e0\u4e00\u7c92\u7c92\u7684 \u6ef4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5415\u56db\u5a18\u6251\u5230\u56da\u8f66\uff0c\u8463\u5de8\u5ddd\u4e5f\u5df2\u56de\u5230\u8f66\u4e0a\u3002\u5415\u56db\u5a18\u4e00\u5251\u5272\u88c2\u8f66\u7bf7\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6c88\u54e5\u54e5\uff0c \u6c88\u54e5\u54e5\uff01\u201d\u56da\u8f66\u4e2d\u6709\u4eba\u5e94\u9053\uff1a\u201c\u83b9\u59b9\uff0c\u4f60\u522b\u5192\u9669\u3002\u201d\u58f0\u97f3\u5fae\u5f31\uff0c\u4f46\u5415\u56db\u5a18\u4e00\u5b57\u4e00\u53e5\uff0c\u542c\u5f97\u5206 \u660e\uff0c\u7cbe\u795e\u9661\u957f\uff0c\u7eb5\u8eab\u4e00\u8dc3\uff0c\u8df3\u4e0a\u8f66\u9876\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u8f66\u84ec\u5ffd\u7136\u63ed\u5f00\uff0c\u5415\u56db\u5a18\u4e00\u5251\u6a2a\u80f8\uff0c\u8df3\u8fdb\u8f66 \u5185\uff0c\u53ea\u89c1\u8463\u5de8\u5ddd\u563f\u563f\u7b11\u9053\uff1a\u201c\u5415\u56db\u5a18\uff0c\u4f60\u8fd8\u4e0d\u4e0b\u53bb\uff0c\u6211\u5c31\u5148\u628a\u8fd9\u4e2a\u56da\u5f92\u6740\u4e86\u3002\u201d\u56da\u8f66\u524d\u5ea7\uff0c \u8463\u5de8\u5ddd\u50cf\u4e00\u5c0a\u5f25\u52d2\u4f5b\u76d8\u819d\u7aef\u5750\uff0c\u6c88\u5728\u5bbd\u7684\u5934\u6795\u5728\u4ed6\u7684\u819d\u4e0a\uff0c\u4ed6\u4e00\u624b\u6276\u7740\uff0c\u53e6\u4e00\u624b\u53c9\u7740\u6c88\u5728\u5bbd \u7684\u54bd\u5589\uff0c\u4e94\u6307\u53ea\u8981\u7a0d\u4e00\u7528\u529b\uff0c\u7acb\u523b\u53ef\u4ee5\u7f6e\u4e4b\u6b7b\u5730\u3002 \u3000\u3000\u5415\u56db\u5a18\u51b7\u6c57\u6c81\u808c\uff0c\u4e00\u65f6\u65b9\u5bf8\u5927\u4e71\uff0c\u6c88\u5728\u5bbd\u7741\u5f00\u53cc\u773c\uff0c\u53c8\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u53ea\u8981\u5e08\u5085\u5e73\u5b89\uff0c\u6211 \u6b7b\u4e0d\u8db3\u60dc\u3002\u83b9\u59b9\u4f60\u56de\u53bb\u5427\uff01\u201d\u8fd9\u65f6\u771f\u662f\u54ab\u5c3a\u5929\u6daf\uff0c\u53ef\u671b\u4e0d\u53ef\u5373\u3002\u5415\u56db\u5a18\u6cea\u54bd\u5fc3\u9178\uff0c\u731b\u53c8\u542c\u5f97 \u8f66\u4e0b\u6740\u58f0\u558a\u58f0\u54cd\u6210\u4e00\u7247\u3002\u8def\u6c11\u77bb\u53d7\u4e86\u91cd\u4f24\u4e4b\u540e\uff0c\u4ed6\u5e26\u6765\u7684\u5bb6\u4e01\uff0c\u90a3\u91cc\u6321\u5f97\u5982\u72fc\u4f3c\u864e\u7684\u5fa1\u6797 \u519b\uff0c\u53ea\u7ed9\u6740\u5f97\u4f24\u4ea1\u904d\u5730\uff0c\u4fdd\u62a4\u8def\u6c11\u77bb\u7684\u5341\u4f59\u540d\u7cbe\u5bb6\u58ee\u4e01\uff0c\u4e5f\u5df2\u88ab\u56f4\u5728\u6838\u5fc3\u3002\u5415\u56db\u5a18\u51c4\u7136\u53eb \u9053\uff1a\u201c\u6c88\u54e5\u54e5\uff0c\u4f60\u597d\u597d\u4fdd\u91cd\uff0c\u6211\u8a93\u5fc5\u6551\u4f60\uff01\u201d\u970d\u5730\u4f7f\u4e2a\u201c\u98de\u9e70\u6251\u5154\u201d\uff0c\u5728\u56da\u8f66\u4e0a\u76f4\u8df3\u4e0b\u6765\uff0c \u821e\u8d77\u4e08\u8bb8\u957f\u77ed\u4e00\u6735\u5251\u82b1\uff0c\u5728\u4eba\u4e1b\u4e2d\u4e00\u843d\uff0c\u5fa1\u6797\u519b\u51e0\u66fe\u89c1\u8fc7\u5982\u6b64\u5a01\u52bf\uff0c\u7eb7\u7eb7\u8d70\u907f\uff0c\u5415\u56db\u5a18\u51fa\u624b \u5982\u7535\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u628a\u5fa1\u574f\u519b\u6740\u5f97\u65ad\u624b\u6298\u8db3\uff0c\u904d\u5730\u547c\u53f7\uff0c\u6740\u5165\u6838\u5fc3\uff0c\u628a\u8def\u6c11\u77bb\u7a74\u9053\u89e3\u5f00\uff0c\u4f46\u56e0 \u4e3a\u65f6\u8fc7\u4e45\uff0c\u8def\u6c11\u77bb\u4ecd\u662f\u4e0d\u80fd\u6d3b\u52a8\u3002\u5415\u56db\u5a18\u633a\u5251\u5f53\u524d\uff0c\u7387\u9886\u8def\u5e9c\u5bb6\u4e01\u76f4\u6740\u51fa\u53bb\uff0c\u8463\u5de8\u5ddd\u558a\u9053\uff1a \u201c\u7531\u5979\u53bb\u5427\u3002\u201d\u79e6\u4e2d\u8d8a\u8de8\u524d\u4e24\u6b65\uff0c\u5415\u56db\u5a18\u4e00\u67c4\u98de\u5200\u5c04\u6765\uff0c\u8d34\u80a9\u800c\u8fc7\uff0c\u79e6\u4e2d\u8d8a\u5413\u5f97\u8fde\u5fd9\u540e\u9000\uff0c \u5415\u56db\u5a18\u5df2\u8fdb\u5165\u5929\u76ee\u5c71\u53bb\u4e86\u3002\u8fd9\u4e00\u5f79\u4e5f\uff0c\u5fa1\u6797\u519b\u6b7b\u4f24\u6570\u5341\uff0c\u8def\u5e9c\u5bb6\u4e01\u4e5f\u6b7b\u4f24\u4e00\u534a\uff0c\u8fd8\u4f24\u4e86\u8def\u6c11 \u77bb\u3002 \u3000\u3000\u8463\u5de8\u5ddd\u5429\u5490\u628a\u6b7b\u7684\u6254\u6389\uff0c\u4f24\u7684\u7528\u9a6c\u9a6e\uff0c\u6574\u597d\u961f\u5f62\uff0c\u6025\u6025\u8d76\u8def\u3002\u79e6\u4e2d\u8d8a\u75db\u5b9a\u601d\u75db\uff0c\u8fde\u58f0\u8bf4 \u9053\uff1a\u201c\u8fd9\u5973\u5b50\u597d\u5389\u5bb3\uff01\u201d\u8463\u5de8\u5ddd\u7b11\u9053\uff1a\u201c\u79e6\u5144\u4e07\u5b89\uff0c\u8fc7\u4e86\u4e8e\u6f5c\uff0c\u524d\u9762\u5df2\u662f\u5766\u9014\u3002\u4ee5\u540e\u7684\u4e8b\u6709 \u6d59\u6c5f\u5de1\u629a\u4e0e\u6211\u4eec\u5206\u62c5\u4e86\u3002\u201d\u4e00\u884c\u8d70\u4e86\u4e24\u5929\uff0c\u679c\u7136\u5e73\u5b89\u5230\u4e86\u676d\u5dde\u3002\u6d59\u6c5f\u5de1\u629a\u674e\u536b\u4e43\u662f\u5eb7\u7199\u665a\u5e74 \u5ba0\u4fe1\u7684\u5927\u81e3\uff0c\u4e0e\u5c71\u4e1c\u5de1\u629a\u7530\u6587\u955c\u9f50\u540d\uff0c\u90fd\u662f\u5f53\u65f6\u5f97\u4ee4\u7684\u5927\u5b98\u3002\u674e\u536b\u95fb\u8baf\u51fa\u6765\u8fce\u63a5\uff0c\u89c1\u5fa1\u6797\u519b \u961f\u5f62\u6563\u4e71\uff0c\u4f24\u5175\u7d2f\u7d2f\uff0c\u95fb\u5f97\u60c5\u7531\uff0c\u4e0d\u7981\u60ca\u5fc3\u548b\u820c\uff01 \u3000\u3000\u674e\u536b\u5728\u5de1\u629a\u8859\u4e2d\uff0c\u7656\u4e86\u5bc6\u5ba4\uff0c\u52a0\u6d3e\u9ad8\u624b\u62a4\u536b\uff0c\u7518\u5929\u9f99\u3001\u79e6\u4e2d\u8d8a\u3001\u8463\u5de8\u5ddd\u4e09\u4eba\u5219\u8f6e\u6d41\u5b88\u5728 \u6c88\u5728\u5bbd\u8eab\u65c1\u3002\u5eb7\u7199\u665a\u5e74\uff0c\u5f03\u6b66\u4fee\u6587\uff0c\u9887\u601d\u7b3c\u7edc\u5929\u4e0b\u4eba\u624d\uff0c\u56e0\u6b64\u66fe\u6709\u5bc6\u4ee4\u7ed9\u5de1\u629a\u674e\u536b\uff0c\u53eb\u4ed6\u5c31 \u8fd1\u8baf\u95ee\u72af\u9006\uff0c\u7b2c\u4e00\u8981\u4ed6\u4eec\u4f9b\u51fa\u540c\u515a\uff0c\u597d\u6309\u540d\u6355\u62ff\uff0c\u7b2c\u4e8c\u8981\u4ed6\u529d\u964d\u5415\u8446\u4e2d\u548c\u4e25\u6d2a\u9035\u4e24\u540d\u6d59\u4e1c\u540d \u5112\uff0c\u82e5\u7136\u4e0d\u4ece\uff0c\u7136\u540e\u518d\u62bc\u89e3\u6765\u4eac\u3002\u5982\u4eca\u5415\u8446\u4e2d\u548c\u4e25\u6d2a\u9035\u90fd\u6349\u4e0d\u7740\uff0c\u53ea\u6349\u5230\u4e86\u5415\u8446\u4e2d\u7684\u5b66\u751f\u6c88 \u5728\u5bbd\uff0c\u674e\u536b\u5fc3\u4e2d\u9887\u4e3a\u5931\u671b\uff0c\u8f6c\u5ff5\u4e00\u60f3\uff0c\u8fd9\u6c88\u5728\u5bbd\u4e5f\u9887\u6709\u6587\u540d\uff0c\u4f55\u59a8\u5ba1\u4ed6\u4e00\u756a\u3002\u674e\u536b\u6709\u4e2a\u5973 \u513f\uff0c\u540d\u53eb\u674e\u660e\u73e0\uff0c\u5a07\u751f\u60ef\u517b\uff0c\u751a\u4e3a\u6dd8\u6c14\uff0c\u542c\u8bf4\u8859\u4e2d\u6349\u6765\u4e86\u4e00\u4e2a\u53db\u9006\uff0c\u662f\u4e2a\u5c11\u5e74\u4e66\u751f\uff0c\u597d\u5947\u5fc3 \u8d77\uff0c\u7f20\u7740\u7236\u4eb2\uff0c\u4e5f\u8981\u53bb\u770b\u3002\u674e\u536b\u65a5\u9053\uff1a\u201c\u671d\u5ef7\u5927\u4e8b\uff0c\u4f60\u5973\u5b69\u513f\u5bb6\uff0c\u7406\u4ed6\u4f5c\u751a\uff1f\u201d\u660e\u73e0\u9053\uff1a \u201c\u6211\u672a\u89c1\u8fc7\u53db\u9006\uff0c\u53ea\u770b\u4e00\u770b\u561b\uff0c\u6709\u4ec0\u4e48\u788d\u4e8b\uff1f\u201d\u674e\u536b\u88ab\u5979\u7f20\u4e0d\u8fc7\uff0c\u53ea\u5f97\u8bf4\u9053\uff1a\u201c\u5b88\u536b\u7684\u90fd\u662f \u7537\u4eba\uff0c\u4f60\u4e00\u4e2a\u5973\u5b69\u513f\u5bb6\uff0c\u600e\u597d\u53bb\u770b\u5ba1\u8baf\uff0c\u4e0d\u6015\u4e0b\u4eba\u7b11\u9a82\u4f60\u7763\u629a\u5343\u91d1\uff0c\u4e0d\u61c2\u672d\u6cd5\u5417\uff1f\u201d\u674e\u660e\u73e0 \u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5bb9\u6613\u3002\u201d\u8fdb\u5165\u5185\u5ba4\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u8d70\u51fa\u6765\u65f6\uff0c\u5df2\u6362\u4e86\u4e00\u8eab\u7537\u5b50\u670d\u88c5\uff0c\u6602\u9996\u6446\u8896\uff0c \u884c\u4e86\u51e0\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u5973\u513f\u626e\u505a\u7239\u7239\u7684\u4e66\u7ae5\uff0c\u7239\u7239\u5ba1\u8baf\u4e4b\u65f6\uff0c\u5973\u513f\u4e0d\u51fa\u58f0\uff0c\u8c01\u77e5\u9053\u6211\u662f\u4e54\u88c5\u6253 \u626e\u3002\u201d\u674e\u536b\u53c8\u597d\u6c14\uff0c\u53c8\u597d\u7b11\uff0c\u88ab\u5979\u7f20\u4e0d\u8fc7\uff0c\u53ea\u597d\u4f9d\u5979\u3002 \u3000\u3000\u5f53\u665a\u674e\u536b\u5e26\u5973\u513f\u8d70\u8fdb\u56da\u623f\uff0c\u6c88\u5728\u5bbd\u7ecf\u8fc7\u4e00\u65e5\u5c06\u606f\uff0c\u7cbe\u795e\u60ed\u590d\u3002\u674e\u536b\u89c1\u4ed6\u867d\u5728\u56da\u623f\u4e4b\u4e2d\uff0c \u4ecd\u662f\u795e\u91c7\u5955\u5955\uff0c\u76f8\u8c8c\u4e0d\u51e1\u3002\u4e0d\u89c9\u6697\u6697\u79f0\u8d5e\u3002\u5fc3\u60f3\uff1a\u8fd9\u6837\u4eba\u6750\uff0c\u82e5\u80af\u5f52\u987a\uff0c\u5165\u9601\u62dc\u76f8\u4e5f\u975e\u96be \u4e8b\u3002\u89c1\u5973\u513f\u4e5f\u5728\u51dd\u795e\u770b\u4ed6\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u4e00\u52a8\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u8db3\u4e0b\u535a\u8bfb\u8bd7\u4e66\uff0c\u5982\u4eca\u5723\u4e0a\u7231\u624d\u82e5 \u6e34\uff0c\u8db3\u4e0b\u82e5\u77e5\u987a\u9006\uff0c\u8f9f\u90aa\u8bf4\uff0c\u5f52\u5723\u671d\uff0c\u6015\u4e0d\u662f\u4e2a\u91d1\u9a6c\u7389\u5802\u7684\u5b66\u571f\uff1f\u4f55\u82e6\u62b1\u4e00\u5b54\u4e4b\u89c1\uff0c\u4f5c\u611a\u6627 \u4e4b\u884c\uff0c\u62db\u8d25\u5bb6\u706d\u65cf\u4e4b\u7978\uff1f\u201d\u6c88\u5728\u5bbd\u9053\uff1a\u201c\u629a\u53f0\u662f\u4e24\u699c\u51fa\u8eab\uff0c\u4e60\u77e5\u6587\u4e8b\u3002\u8bf7\u95ee\u629a\u53f0\u5927\u4eba\uff0c\u524d\u8f88 \u624d\u4eba\u5434\u6885\u6751\u5148\u751f\u5982\u4f55\uff1f\u201d\u5434\u6885\u6751\u662f\u660e\u672b\u624d\u5b50\uff0c\u699c\u773c\u51fa\u8eab\uff0c\u540e\u6765\u6295\u987a\u6e05\u671d\uff0c\u505a\u5230\u56fd\u5b50\u76d1\u796d\u9152\u3002 \u674e\u536b\u89c1\u4ed6\u8bf4\u8bdd\u5c31\u63d0\u8d77\u5434\u6885\u6751\uff0c\u5fc3\u4e2d\u6697\u559c\u9053\uff1a\u201c\u6709\u51e0\u5206\u9053\u7406\u4e86\uff01\u56e0\u9053\uff1a\u201c\u5434\u6885\u6751\u4e00\u4ee3\u624d\u4eba\uff0c\u53c8 \u660e\u987a\u9006\uff0c\u77e5\u5927\u52bf\u3002\u6211\u8f88\u6b63\u5f53\u4ee5\u4ed6\u4e3a\u8303\u3002\u201d\u6c88\u5728\u5bbd\u7b11\u9053\uff1a\u201c\u662f\u4e48\uff1f\u201d\u541f\u9053\uff1a\u201c\u6545\u4eba\u6177\u6168\u591a\u5947 \u8282\uff0c\u4e3a\u5f53\u5e74\u6c89\u541f\u4e0d\u65ad\uff0c\u8349\u95f4\u5077\u6d3b\u3002\u201d\u674e\u536b\u9762\u8272\u4e00\u53d8\uff0c\u6c88\u5728\u5bbd\u9053\uff1a\u201c\u6211\u60f3\u8bf7\u6559\u629a\u53f0\uff0c\u6885\u6751\u8fd9\u51e0 \u53e5\u8bcd\u662f\u600e\u4e48\u4e2a\u8bb2\u6cd5\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u9996\u8bcd\u662f\u5434\u6885\u6751\u7684\u7edd\u547d\u8bcd\uff0c\u5434\u6885\u6751\u5728\u75c5\u91cd\u4e4b\u9645\uff0c\u81ea\u6094\u5931\u8282\uff0c\u56e0\u800c\u5728\u4e34\u7ec8\u524d\u5199\u4e86\u4e00\u9996 \u201c\u8d3a\u65b0\u90ce\u201d\u8bcd\uff0c\u8bcd\u9053\uff1a \u3000\u3000\u201c\u4e07\u7f8e\u6467\u534e\u53d1\uff0c\u53f9\u9f9a\u751f\u5929\u5e74\u7adf\u5929\uff0c\u9ad8\u540d\u96be\u6ca1\u3002\u543e\u75c5\u96be\u5c06\u533b\u836f\u6cbb\uff0c\u803f\u803f\u80f8\u4e2d\u70ed\u8840\uff0c\u5f85\u6d12\u5411 \u897f\u98ce\u6b8b\u6708\u3002\u5256\u5374\u5fc3\u809d\u4eca\u7f6e\u5730\uff0c\u95ee\u534e\u9640\u89e3\u6211\u80a0\u5343\u7ed3\u3002\u8ffd\u5f80\u4e8b\uff0c\u500d\u51c4\u54bd\u3002 \u3000\u3000\u6545\u4eba\u6177\u6168\u591a\u5947\u8282\u3002\u4e3a\u5f53\u5e74\u6c89\u541f\u4e0d\u65ad\uff0c\u8349\u95f4\u5077\u6d3b\u3002\u827e\u7099\u7709\u5934\u74dc\u55b7\u9f3b\uff0c\u4eca\u65e5\u987b\u96be\u51b3\u7edd\u3002\u65e9\u60a3 \u82e6\u91cd\u6765\u5343\u77d7\u3002\u8131\u5c65\u59bb\u7fe0\u975e\u6613\u4e8b\uff0c\u7adf\u4e00\u94b1\u4e0d\u503c\u4f55\u987b\u8bf4\uff01\u4eba\u4e16\u4e8b\uff0c\u51e0\u5b8c\u7f3a\uff1f\u201d \u3000\u3000\u8fd9\u9996\u8bcd\u81ea\u6028\u81ea\u827e\uff0c\u60b2\u611f\u4e07\u7aef\uff0c\u4e00\u79cd\u75db\u6068\u81ea\u5df1\u201c\u5931\u8282\u201d\u7684\u5fc3\u60c5\uff0c\u8dc3\u7136\u7eb8\u4e0a\u3002\u674e\u536b\u8bf4\u5434\u6885\u6751 \u53ef\u4e3a\u98ce\u8303\uff0c\u6c88\u5728\u5bbd\u5c31\u504f\u504f\u63d0\u4ed6\u8fd9\u9996\u81ea\u6094\u505a\u4e86\u6c49\u5978\u7684\u7edd\u547d\u8bcd\uff0c\u8fde\u523a\u5e26\u8bbd\uff0c\u674e\u536b\u542c\u4e86\uff0c\u5c34\u5c2c\u4e4b \u6781\uff0c\u642d\u8baa\u95ee\u9053\uff1a\u201c\u5148\u751f\u8bd7\u6587\u540d\u5bb6\uff0c\u53ef\u6709\u4ec0\u4e48\u8fd1\u4f5c\u4e48\uff1f\u201d\u6c88\u5728\u5bbd\u5e94\u58f0\u9053\uff1a\u201c\u6709\u3002\u6211\u6b64\u6b21\u81ea\u77e5\u5fc5 \u6b7b\uff0c\u6628\u65e5\u5728\u56da\u8f66\u4e0a\u66fe\u53e3\u5360\u4e24\u53e5\uff1a\u2018\u9646\u6c88\u4e0d\u5fc5\u7531\u6d2a\u6c34\uff0c\u8c01\u4e3a\u795e\u5dde\u7406\u65e7\u7586\uff1f\u2019\u5c1a\u672a\u7eed\u6210\uff0c\u629a\u53f0\u5927 \u4eba\u624d\u9ad8\u516b\u6597\uff0c\u53ef\u613f\u4e3a\u665a\u751f\u4e00\u7eed\u4e48\uff1f\u201d\u674e\u536b\u4e00\u542c\uff0c\u6c88\u5728\u5bbd\u5c45\u7136\u6697\u91cc\u8bbd\u793a\uff0c\u4ee5\u5927\u4e49\u76f8\u8d23\uff0c\u53eb\u4ed6\u4e3a \u795e\u5dde\u7406\u65e7\u7586\uff0c\u4e0d\u6562\u518d\u8c08\uff0c\u62c2\u8896\u9000\u51fa\u3002 \u3000\u3000\u9000\u51fa\u56da\u623f\uff0c\u674e\u660e\u73e0\u6084\u6084\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u8fd9\u4eba\u624d\u60c5\u4e0d\u9519\uff0c\u8bf4\u8bdd\u5389\u5bb3\u5f97\u5f88\u5440\uff01\u201d\u674e\u536b\u9762\u8272\u94c1 \u9752\uff0c\u4e0d\u7406\u5973\u513f\uff0c\u81ea\u56de\u4e66\u623f\u5199\u594f\u6298\u53bb\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u4e09\u65e5\uff0c\u5fa1\u6797\u519b\u7684\u7edf\u9886\u79e6\u4e2d\u8d8a\u6765\u8bf7\u793a\uff0c\u8bf4\u662f\u8981\u62bc\u89e3\u72af\u4eba\u8fdb\u4eac\uff0c\u8bf7\u4ed6\u52a0\u6d3e\u597d\u624b\u76f8\u52a9\uff0c\u674e \u536b\u9053\uff1a\u201c\u4f60\u6765\u5f97\u597d\uff0c\u4ea6\u5e9c\u6b63\u8981\u6311\u9009\u65b0\u536b\u58eb\uff0c\u4f60\u4eec\u4e09\u4f4d\u7cbe\u901a\u6b66\u529f\uff0c\u8bf7\u7ed9\u6211\u4f5c\u8bc4\u5224\u3002\u201d\u79e6\u4e2d\u8d8a\u81ea \u59cb\u5e94\u4e86\u3002 \u3000\u3000\u629a\u53f0\u6311\u9009\u536b\u58eb\u6781\u4e3a\u4e25\u683c\uff0c\u5148\u8981\u6709\u53ef\u9760\u7684\u4eba\u6df1\u7b97\uff0c\u7136\u540e\u624d\u662f\u8f83\u91cf\u6b66\u529f\u3002\u5230\u4e86\u90a3\u5929\uff0c\u674e\u536b\u5728 \u5e9c\u8859\u91cc\u7684\u6f14\u6b66\u5385\u524d\u7f6e\u9152\u9ad8\u4f1a\uff0c\u770b\u5165\u9009\u7684\u536b\u58eb\u6f14\u6b66\uff0c\u79e6\u4e2d\u8d8a\u56e0\u8981\u770b\u5b88\u6c88\u5728\u5bbd\uff0c\u4e0d\u80fd\u4f5c\u966a\uff0c\u7531\u7518 \u5929\u9f99\u3001\u8463\u5de8\u5ddd\u548c\u629a\u8859\u4e2d\u7684\u4e24\u4f4d\u536b\u58eb\u603b\u7ba1\uff0c\u62c5\u4efb\u8bc4\u5224\u3002\u8fd9\u6b21\u6311\u9009\u536b\u58eb\uff0c\u4ece\u5341\u516d\u4eba\u4e2d\u9009\u51fa\u4e09\u4eba\uff0c \u674e\u536b\u53eb\u4e0a\u5802\u524d\u4e00\u770b\uff0c\u53ea\u89c1\u4e24\u4e2a\u662f\u96c4\u7ea0\u7ea0\u7684\u6c49\u5b50\uff0c\u53e6\u4e00\u4e2a\u5374\u9762\u9ec4\u808c\u7626\uff0c\u4e2d\u7b49\u8eab\u6750\uff0c\u6d3b\u50cf\u4e00\u4e2a\u75c5 \u592b\u3002\u674e\u536b\u76b1\u4e86\u76b1\u7709\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4e09\u4eba\u662f\u8c01\u4fdd\u8350\u6765\u7684\uff1f\u201d\u8d1f\u8d23\u6311\u9009\u7684\u88e8\u5c06\u56de\u9053\uff1a\u201c\u4e00\u4f4d\u662f\u5de6\u85e9 \u53f8\u4fdd\u8350\u7684\uff0c\u8ddf\u968f\u4ed6\u591a\u5e74\u7684\u6b66\u5b98\u738b\u594b\uff1a\u4e00\u4f4d\u662f\u4e16\u88ad\u5df4\u56fe\u9c81\u6c49\u519b\u65d7\u4eba\u97e9\u5bb6\u7684\u5b50\u5f1f\uff0c\u53eb\u505a\u97e9\u632f\u751f\uff0c \u60f3\u51fa\u6765\u56fe\u4e2a\u529f\u540d\u3002\u201d\u674e\u536b\u201c\u5514\u201d\u4e86\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u4e2a\u9762\u9ec4\u808c\u7626\u7684\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u8c01\u4fdd\u8350\u4ed6 \u7684\uff1f\u4ed6\u4e5f\u8986\u9009\u5408\u683c\u4e86\u5417\uff1f\u201d\u88e8\u5c06\u966a\u7b11\u9053\uff1a\u201c\u5927\u4eba\u7231\u5fd8\u4e8b\uff0c\u8bb0\u4e0d\u8d77\u6765\u4e86\u3002\u8fd9\u4eba\u662f\u5927\u4eba\u7684\u624b\u4ee4\u4fdd \u8350\u7684\u3002\u5927\u4eba\u6cd5\u773c\uff0c\u4ed6\u7684\u529f\u592b\u8fd8\u771f\u4e0d\u9519\u5462\uff01\u5728\u5341\u516d\u4e2a\u4faf\u9009\u7684\u536b\u58eb\u4e2d\uff0c\u6050\u6015\u8981\u6570\u4ed6\u7684\u529f\u592b\u6700\u597d \u4e86\uff0c\u771f\u662f\u4eba\u4e0d\u53ef\u8c8c\u76f8\u3002\u201d \u3000\u3000\u674e\u536b\u6014\u4e86\u4e00\u6014\uff0c\u60f3\u4e86\u4e00\u4e0b\uff0c\u624d\u8bb0\u8d77\u6709\u8fd9\u4e48\u4e00\u56de\u4e8b\u3002\u4e00\u4e2a\u6708\u524d\uff0c\u81ea\u5df1\u4e3a\u6bcd\u4eb2\u795d\u5bff\uff0c\u8bf7\u4e86\u5531 \u620f\u7684\u3001\u5356\u827a\u7684\uff0c\u597d\u51e0\u73ed\u4eba\uff0c\u6709\u4e00\u73ed\u800d\u6742\u6280\u7684\u6c5f\u6e56\u827a\u4eba\uff0c\u6f14\u5f97\u5f88\u597d\uff0c\u5c24\u5176\u662f\u5176\u4e2d\u4e00\u4e2a\u5973\u5b50\uff0c\u8e29 \u7ef3\uff0c\u800d\u6c34\u7897\uff0c\u6f14\u9a6c\u6280\uff0c\u90fd\u6781\u7cbe\u5f69\u3002\u5973\u513f\u770b\u4e86\uff0c\u9ad8\u5174\u5f97\u5f88\uff0c\u5c31\u53eb\u90a3\u5356\u827a\u7684\u5973\u4eba\u5230\u5185\u8859\u6765\u95ee\uff0c\u4ee5 \u540e\u6bcf\u9694\u51e0\u5929\uff0c\u5c31\u8bf7\u90a3\u5356\u827a\u7684\u5973\u4eba\u6765\u966a\u5979\u73a9\u800d\uff0c\u6f14\u6742\u6280\u7ed9\u5979\u89e3\u95f7\u3002\u81ea\u5df1\u867d\u7136\u4e0d\u559c\u6b22\u5973\u513f\u548c\u6c5f\u6e56 \u827a\u4eba\u6765\u5f80\uff0c\u4f46\u60f3\u8fd9\u4e5f\u65e0\u4f24\u5927\u96c5\uff0c\u90a3\u5973\u5b50\u6765\u65f6\u53c8\u603b\u662f\u5355\u8eab\u4e00\u4eba\uff0c\u6599\u4e5f\u4e0d\u4f1a\u95f9\u51fa\u4ec0\u4e48\u4e8b\u6765\uff0c\u5c31\u7531 \u5979\u53bb\u4e86\u3002\u5341\u591a\u5929\u524d\uff0c\u6311\u9009\u536b\u58eb\u7684\u4e8b\u7ed9\u5979\u77e5\u9053\u4e86\uff0c\u5979\u8bf4\u5979\u4e5f\u8981\u4fdd\u8350\u4e00\u4eba\u3002\u60f3\u5230\u8fd9\u91cc\uff0c\u674e\u536b\u4e0d\u7531 \u5f97\u770b\u4e86\u90a3\u9762\u9ec4\u808c\u7626\u7684\u6c49\u5b50\u51e0\u773c\u3002 \u3000\u3000\u674e\u536b\u770b\u4e86\u51e0\u773c\uff0c\u4f9d\u7a00\u8bb0\u5f97\u8fd9\u4eba\u4f3c\u662f\u90a3\u5973\u5b50\u73ed\u4e2d\u7684\u4e00\u4e2a\u4f19\u8ba1\uff0c\u5f53\u65e5\u81ea\u5df1\u7684\u5973\u513f\u8bf4\u4e5f\u8981\u4fdd\u8350 \u4e00\u4eba\uff0c\u81ea\u5df1\u95ee\u5979\u8981\u4fdd\u8350\u8c01\uff0c\u5979\u8bf4\u662f\u90a3\u4e2a\u5356\u827a\u5973\u4eba\u7684\u8868\u54e5\u3002\u60f3\u5fc5\u5c31\u662f\u8fd9\u4eba\u4e86\u3002 \u3000\u3000\u539f\u6765\u674e\u660e\u73e0\u5c0f\u5b69\u5fc3\u6027\uff0c\u548c\u90a3\u4e2a\u5356\u827a\u5973\u4eba\u73a9\u5f97\u5f88\u662f\u6295\u7f18\uff0c\u90a3\u5973\u4eba\u5c31\u8bf4\u5979\u7684\u8868\u54e5\u6b66\u827a\u5f88\u597d\uff0c \u542c\u8bf4\u629a\u8859\u6311\u9009\u536b\u58eb\uff0c\u4ed6\u4e5f\u60f3\u56fe\u4e2a\u51fa\u8eab\uff0c\u53eb\u5c0f\u59d0\u6258\u4eba\u4fdd\u8350\u3002\u674e\u660e\u73e0\u89c9\u5f97\u597d\u73a9\uff0c\u5c31\u5bf9\u7236\u4eb2\u8bf4\u4e86\u3002 \u674e\u536b\u8d77\u5148\u89c9\u5f97\u53ef\u7b11\uff1a\u4e00\u4e2a\u6c5f\u6e56\u5356\u827a\u7684\u4f1a\u6709\u4ec0\u4e48\u771f\u5b9e\u672c\u9886\uff1b\u4f46\u88ab\u4ed6\u5973\u513f\u7f20\u4e0d\u8fc7\uff0c\u5fc3\u60f3\uff0c\u53cd\u6b63\u6311 \u9009\u536b\u58eb\u8981\u7ecf\u8fc7\u4e09\u6b21\u8003\u8bd5\uff0c\u4ed6\u90a3\u70b9\u6c5f\u6e56\u6280\u827a\uff0c\u53ea\u6015\u521d\u9009\u5c31\u8981\u843d\u699c\u3002\u8fd9\u4e9b\u4eba\u603b\u662f\u4f01\u56fe\u4fa5\u5e78\uff0c\u5c31\u7531 \u4ed6\u53bb\u4e00\u8bd5\uff0c\u53eb\u4ed6\u77e5\u9053\u629a\u53f0\u7684\u536b\u58eb\uff0c\u4e0d\u662f\u4ed6\u90a3\u79cd\u6c5f\u6e56\u5356\u827a\u7684\u4eba\u53ef\u4ee5\u8003\u5f97\u4e0a\u7684\uff0c\u4e0d\u6599\u4ed6\u5c45\u7136\u9009\u4e0a \u4e86\u3002 \u3000\u3000\u674e\u536b\u8bb0\u8d77\u5973\u513f\u53eb\u4ed6\u5199\u7684\u540d\u5b57\u662f\u201c\u5510\u9f99\u201d\uff0c\u5c31\u628a\u90a3\u9762\u9ec4\u808c\u7626\u7684\u6c49\u5b50\u53eb\u4e0a\u6765\u9053\uff1a\u201c\u5510\u9f99\uff0c\u4f60 \u4e0d\u662f\u800d\u6742\u6280\u7684\u4e48\uff1f\u54ea\u91cc\u7ec3\u6765\u7684\u6b66\u529f\uff1f\u201d\u5510\u9f99\u9053\uff1a\u201c\u6211\u662f\u5bb6\u4f20\u6b66\u827a\uff0c\u4e0d\u5f97\u5df2\u624d\u51fa\u6765\u5356\u827a\u7684\u3002\u201d \u674e\u536b\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u4e0b\u53bb\u6bd4\u8bd5\u3002\u201d \u3000\u3000\u4e09\u901a\u9f13\u7f62\uff0c\u674e\u536b\u59d4\u6258\u8463\u5de8\u5ddd\u505a\u4e3b\u8003\uff0c\u8463\u5de8\u5ddd\u5148\u628a\u738b\u594b\u53eb\u4e0a\u6765\u95ee\u9053\uff1a\u201c\u4f60\u7ec3\u7684\u54ea\u95e8\u6b66 \u529f\uff1f\u201d\u738b\u594b\u9053\uff1a\u201c\u6211\u7ec3\u7684\u662f\u94c1\u7802\u638c\u3002\u201d\u8463\u5de8\u5ddd\u9053\uff1a\u201c\u597d\uff0c\u4f60\u7ec3\u7ed9\u6211\u770b\u3002\u201d\u738b\u594b\u8981\u4e09\u53e0\u9752\u7802\u7816 \u6765\uff0c\u5e73\u653e\u5728\u5385\u524d\u7684\u77f3\u9f13\u4e0a\uff0c\u7ec3\u4e86\u4e24\u4e2a\u62f3\u5f0f\uff0c\u8d70\u8fd1\u77f3\u9f13\uff0c\u7a81\u7136\u547c\u7684\u4e00\u62ff\u5288\u53bb\uff0c\u628a\u7b2c\u4e00\u53e0\u9752\u7816\u6253 \u5f97\u7c89\u788e\u3002\u629a\u8859\u4e2d\u7684\u536b\u58eb\u603b\u7ba1\u8bb8\u6210\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u5916\u529f\u6709\u70b9\u6839\u5e95\u4e86\u3002\u201d\u738b\u594b\u53c8\u8d70\u4e86\u56de\u6765\uff0c\u5bf9\u8463\u5de8 \u5ddd\u9053\uff1a\u201c\u6bcf\u53e0\u9752\u7802\u662f\u5341\u53ea\uff0c\u73b0\u5728\u6211\u8981\u7528\u638c\u529b\u51fb\u788e\u7b2c\u4e8c\u53e0\u4e2d\u7684\u4e00\u53ea\uff0c\u8bf7\u95ee\u4e3b\u8003\uff0c\u8981\u6211\u51fb\u788e\u90a3\u4e00 \u53ea\uff1f\u201d\u8463\u5de8\u5ddd\u968f\u53e3\u5e94\u9053\uff1a\u201c\u5c31\u662f\u7b2c\u4e03\u53ea\u5427\u3002\u201d\u90a3\u4eba\u9053\u4e86\u4e00\u58f0\u201c\u9075\u4ee4\u201d\uff01\u8d70\u8fd1\u77f3\u9f13\uff0c\u4e00\u638c\u62cd \u4e0b\uff0c\u6309\u4e86\u4e00\u4e0b\u540e\uff0c\u5782\u624b\u8bf4\u9053\uff1a\u201c\u8bf7\u9a8c\uff01\u201d\u88e8\u5c06\u628a\u7816\u4e00\u53ea\u53ea\u79fb\u5f00\uff0c\u79fb\u5230\u7b2c\u4e03\u53ea\u65f6\uff0c\u679c\u7136\u5df2\u662f\u7c89 \u788e\uff0c\u628a\u8fd9\u53ea\u788e\u7816\u7528\u624b\u626b\u53bb\u4e4b\u540e\uff0c\u518d\u9a8c\u7b2c\u516b\u53ea\u548c\u7b2c\u4e5d\u53ea\u9752\u7816\uff0c\u5374\u4ecd\u662f\u5b8c\u597d\u65e0\u7f3a\u3002\u8bb8\u6210\u7fd8\u8d77\u62c7\u6307 \u9053\uff1a\u201c\u597d\uff01\u201d\u7518\u5929\u9f99\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u5185\u529f\u4e5f\u6709\u70b9\u6839\u5e95\u4e86\u3002\u201d\u738b\u594b\u53c8\u8d70\u4e86\u4e0a\u6765\uff0c\u8463\u5de8\u5ddd\u7b11\u9053\uff1a \u201c\u8fd9\u7b2c\u4e09\u53e0\u9752\u7816\uff0c\u4f60\u8981\u600e\u4e48\u6837\u7ec3\u6cd5\uff1f\u201d\u738b\u594b\u9053\uff1a\u201c\u628a\u5b83\u6253\u6210\u7c89\u788e\u3002\u201d\u8bb8\u6210\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bd5\u8fc7 \u4e86\u4e48\uff1f\u7ec3\u70b9\u65b0\u5947\u7684\u6765\u3002\u201d\u738b\u594b\u7980\u9053\uff1a\u201c\u8fd9\u6b21\u548c\u6253\u7b2c\u4e00\u53e0\u9752\u7816\u7684\u65b9\u6cd5\u4e0d\u540c\uff0c\u603b\u7ba1\u5927\u4eba\uff0c\u4f60\u8bf7\u770b \u597d\u4e86\u3002\u201d\u8d70\u8fd1\u77f3\u9f13\uff0c\u53cc\u81c2\u4e00\u5c48\u4e00\u4f38\uff0c\u5438\u4e86\u51e0\u53e3\u5927\u6c14\uff0c\u8f7b\u98d8\u98d8\u7684\u4e00\u638c\u62cd\u4e0b\uff0c\u968f\u5373\u5782\u624b\u8df3\u5f00\uff0c\u90a3 \u53e0\u7816\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u4ecd\u662f\u597d\u7aef\u7aef\u7684\u53e0\u5728\u90a3\u513f\uff0c\u8bb8\u6210\u9887\u89c9\u5947\u602a\uff0c\u8463\u5de8\u5ddd\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d\u53eb\u8bb8 \u6210\u7528\u624b\u53bb\u6478\uff0c\u8bb8\u6210\u624b\u4e00\u89e6\u53ca\uff0c\u90a3\u53e0\u9752\u7816\u7acb\u523b\u54d7\u5566\u5566\u7684\u5012\u4e0b\uff0c\u5730\u4e0a\u5806\u6ee1\u7c89\u5c51\u3002\u539f\u6765\u8fd9\u53e0\u9752\u7816\u5df2 \u5b8c\u5168\u7ed9\u4ed6\u7528\u5185\u529b\u9707\u5f97\u5982\u540c\u8c46\u8150\u4e00\u822c\u3002\u8bb8\u6210\u5927\u60ca\uff0c\u89c9\u5f97\u8fd9\u738b\u594b\u529f\u529b\u5df2\u5728\u81ea\u5df1\u4e4b\u4e0a\u3002\u8463\u5de8\u5ddd\u5bf9\u674e \u536b\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u94c1\u7802\u638c\u5df2\u6709\u516b\u6210\u706b\u5019\uff0c\u53ef\u4ee5\u5165\u9009\u4e86\u3002\u201d\u4e8e\u662f\u53c8\u53eb\u97e9\u632f\u751f\u4e0a\u6765\u3002 \u3000\u3000\u8463\u5de8\u5ddd\u95ee\u97e9\u632f\u751f\u9053\uff1a\u201c\u4f60\u7ec3\u7684\u53c8\u662f\u54ea\u95e8\u6b66\u529f\uff1f\u201d\u97e9\u632f\u751f\u9053\uff1a\u201c\u6211\u7ec3\u7684\u662f\u5f13\u9a6c\u529f\u592b\uff0c\u8bb2\u7a76 \u4e0b\u76d8\u817f\u52b2\u3002\u201d\u8463\u5de8\u5ddd\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u77bb\u4f60\u7684\u4e0b\u76d8\u817f\u52b2\u3002\u201d\u97e9\u632f\u751f\u53eb\u4eba\u53d6\u4e86\u4e8c\u5341\u53ea\u6c99\u5305\u6765\uff0c\u6bcf \u53ea\u6c99\u5305\u91cd\u4e8c\u767e\u65a4\uff0c\u4e5f\u662f\u5341\u4e2a\u53e0\u6210\u4e00\u53e0\uff0c\u4e24\u53e0\u6c99\u5305\u6446\u5728\u6f14\u6b66\u573a\u4e0a\u3002\u97e9\u632f\u751f\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u4e00\u811a\u628a \u4e00\u53e0\u6c99\u5305\u4e2d\u7684\u968f\u4fbf\u4e00\u5305\u8e22\u98de\u3002\u201d\u8463\u5de8\u5ddd\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u4f60\u5c31\u8e22\u7b2c\u4e00\u53e0\u4e2d\u7684\u7b2c\u56db\u5305\uff0c\u7b2c\u4e8c\u53e0\u4e2d \u7684\u7b2c\u516d\u5305\u3002\u201d\u97e9\u632f\u751f\u53eb\u4eba\u505a\u4e86\u8bb0\u53f7\uff0c\u7ed5\u573a\u75be\u8dd1\u4e00\u531d\uff0c\u8dd1\u8fd1\u6c99\u5305\uff0c\u95ea\u7535\u822c\u7684\u8d77\u4e86\u8fde\u73af\u98de\u811a\uff0c\u53ea \u542c\u8f70\u7136\u5de8\u54cd\uff0c\u4e24\u4e2a\u6c99\u5305\u98de\u51fa\u4e94\u516d\u4e08\u5916\uff0c\u53eb\u4eba\u4e00\u9a8c\uff0c\u679c\u7136\u662f\u7b2c\u4e00\u53e0\u4e2d\u7684\u7b2c\u56db\u5305\u548c\u7b2c\u4e8c\u53e0\u4e2d\u7684\u7b2c \u516d\u5305\u3002\u8463\u5de8\u5ddd\u628a\u4ed6\u53eb\u4e86\u4e0a\u6765\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u522b\u7684\u529f\u592b\u5417\uff1f\u201d\u97e9\u632f\u751f\u9053\uff1a\u201c\u8fd8\u6709\u5c31\u662f\u5f13\u9a6c\u4e0a\u7684\u529f \u592b\u4e86\u3002\u201d\u674e\u536b\u53eb\u4ed6\u8bd5\u8bd5\uff0c\u4ed6\u63a5\u8fde\u62c9\u65ad\u4e86\u4e09\u628a\u4e94\u77f3\u5f3a\u5f13\uff0c\u53c8\u63a5\u8fde\u4e09\u7bad\u5c04\u4e2d\u7ea2\u5fc3\u3002\u674e\u536b\u9053\uff1a\u201c\u8fd9 \u4eba\u5012\u662f\u4e2a\u51b2\u950b\u9677\u9635\u7684\u5c06\u624d\u3002\u201d\u8463\u5de8\u5ddd\u7b11\u9053\uff1a\u201c\u4ed6\u662f\u4e2a\u4e16\u88ad\u5df4\u56fe\u9c81\uff0c\u8fd9\u5f13\u9a6c\u4e0a\u7684\u529f\u592b\u81ea\u7136\u8be5\u662f \u719f\u7ec3\u7684\u4e86\u3002\u8bba\u5230\u771f\u5b9e\u672c\u9886\uff0c\u4ed6\u6bd4\u4e0d\u4e0a\u521a\u624d\u7684\u90a3\u4e2a\u738b\u594b\u3002\u5927\u4eba\u53ef\u4ee5\u5916\u653e\u4ed6\u505a\u4e2a\u5175\u8425\u7edf\u5e26\u3002\u201d\u6700 \u540e\u628a\u5510\u9f99\u53eb\u4e86\u4e0a\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u7ec3\u7684\u53c8\u662f\u54ea\u95e8\u529f\u592b\uff1f\u201d\u5510\u9f99\u9053\uff1a\u201c\u6211\u54ea\u4e00\u95e8\u90fd\u4e0d\u7ec3\u3002\u201d\u674e\u536b \u9053\uff1a\u201c\u90a3\u4e48\u4f60\u6709\u4ec0\u4e48\u7279\u957f\uff1f\u201d\u5510\u9f99\u7980\u9053\uff1a\u201c\u6211\u7684\u7279\u957f\u5c31\u662f\u6328\u6253\u3002\u201d\u674e\u536b\u6115\u4e86\u4e00\u6115\uff0c\u6b63\u60f3\u9a82\u4ed6 \u201c\u6df7\u5e10\u201d\uff01\u8463\u5de8\u5ddd\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u7ec3\u4f60\u6328\u6253\u7684\u529f\u592b\u5427\uff01\u600e\u4e48\u7ec3\u5462\uff1f\u201d\u5510\u9f99\u9053\uff1a\u201c\u53eb\u4ed6\u4eec\u4e8c \u4eba\uff0c\u4e00\u4e2a\u7528\u638c\u6253\u6211\uff0c\u4e00\u4e2a\u7528\u817f\u8e22\u6211\uff0c\u6211\u7edd\u4e0d\u8fd8\u624b\u3002\u201d\u674e\u536b\u548c\u7518\u5929\u9f99\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u738b\u594b\u548c\u97e9\u632f \u751f\uff0c\u4e00\u4eba\u53ef\u4ee5\u638c\u788e\u9752\u7816\uff0c\u4e00\u4eba\u53ef\u4ee5\u811a\u8e22\u6c99\u5305\uff0c\u4ed6\u5c45\u7136\u6562\u53d7\u4ed6\u4eec\u638c\u5288\u811a\u8e22\uff0c\u8fd9\u5c82\u975e\u8352\u5510\u3002\u8463\u5de8 \u5ddd\u6325\u6325\u624b\u9053\uff1a\u201c\u597d\uff0c\u5c31\u662f\u8fd9\u4e48\u8bd5\u5427\uff01\u201d\u5510\u9f99\u8df3\u51fa\u573a\u5fc3\uff0c\u4e24\u624b\u8d34\u819d\uff0c\u738b\u594b\u547c\u547c\u4e24\u638c\u5411\u4ed6\u80f8\u819b\u5288 \u53bb\uff0c\u97e9\u632f\u751f\u4e5f\u8fde\u73af\u98de\u811a\uff0c\u5411\u4ed6\u4e0b\u76d8\u8e22\u53bb\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u542c\u5f97\u5509\u5509\u8fde\u58f0\uff0c\u738b\u594b\u7ed9\u9707\u51fa\u4e00 \u4e08\u5f00\u5916\uff0c\u59cb\u7a33\u5f97\u4f4f\u8eab\u5f62\uff0c\u97e9\u632f\u751f\u66f4\u60e8\uff0c\u7adf\u7136\u8dcc\u5230\u5730\u4e0a\uff0c\u722c\u4e0d\u52a8\u4e86\uff01\u8463\u5de8\u5ddd\u6025\u5fd9\u8d70\u51fa\uff0c\u628a\u97e9\u632f \u751f\u631f\u8d77\uff0c\u53ea\u89c1\u4ed6\u53cc\u817f\u80bf\u80c0\uff0c\u5510\u9f99\u8d70\u4e86\u8fc7\u6765\uff0c\u9053\u58f0\u201c\u5f97\u7f6a\u201d\uff01\u5728\u97e9\u632f\u751f\u817f\u4e0a\u6478\u4e86\u4e24\u628a\uff0c\u8bf4\u9053\uff1a \u201c\u4f60\u56de\u53bb\u5367\u5e8a\u4e09\u65e5\uff0c\u81ea\u7136\u4f1a\u597d\u3002\u201d\u53c8\u5bf9\u8463\u5de8\u5ddd\u9053\uff1a\u201c\u4e3b\u8003\u5927\u4eba\uff0c\u6211\u7684\u6328\u6253\u53ef\u5408\u683c\u4e48\uff1f\u201d\u8463\u5de8 \u5ddd\u9053\uff1a\u201c\u4f60\u8bf7\u7b49\u4e00\u7b49\uff0c\u6211\u8981\u95ee\u8fc7\u629a\u53f0\u5927\u4eba\u3002\u201d \u3000\u3000\u8463\u5de8\u5ddd\u4f4e\u5934\u601d\u7d22\uff0c\u8d70\u56de\u5e2d\u4e0a\u3002\u674e\u536b\u7ed9\u521a\u624d\u90a3\u5e55\u60ca\u5f97\u76ee\u77aa\u53e3\u5446\u3002\u7b49\u5230\u8463\u5de8\u5ddd\u56de\u6765\uff0c\u6025\u5fd9\u95ee \u9053\uff1a\u201c\u90a3\u4e2a\u5510\u9f99\u53ef\u662f\u4f1a\u5996\u6cd5\u7684\u4e48\uff1f\u201d\u8463\u5de8\u5ddd\u5fc3\u5ff5\u4e0a\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u5927\u4eba\u4fdd\u8350\u7684\uff0c\u8bf7\u5927\u4eba \u793a\u77e5\u4ed6\u7684\u6765\u5386\u3002\u201d\u7518\u5929\u9f99\u63d2\u53e3\u9053\uff1a\u201c\u4ed6\u7ec3\u7684\u662f\u2018\u6cbe\u8863\u5341\u516b\u8dcc\u2019\u7684\u6700\u4e0a\u4e58\u5185\u529f\uff01\u6211\u751f\u5e73\u53ea\u89c1\u8fc7 \u4e09\u4e2a\u4eba\u4f1a\u8fd9\u79cd\u529f\u592b\u3002\u4e00\u4e2a\u662f\u4e86\u56e0\u548c\u5c1a\u3001\u4e00\u4e2a\u662f\u5929\u53f6\u6563\u4eba\u3001\u4e00\u4e2a\u662f\u8840\u6ef4\u5b50\u7684\u603b\u7ba1\u54c8\u5e03\u9640\uff0c\u73b0\u5728 \u8fde\u4ed6\u662f\u7b2c\u56db\u4e2a\u3002\u201d\u674e\u536b\u5927\u63a0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u6765\u5386\uff0c\u6211\u4e5f\u4e0d\u77e5\u3002\u201d\u8463\u5de8\u5ddd\u9053\uff1a\u201c\u90a3\u4e48\u5927\u4eba\u4e3a\u4f55 \u4f1a\u4fdd\u8350\u4ed6\uff1f\u201d\u674e\u536b\u9762\u4e0a\u4e00\u7ea2\uff0c\u53ea\u597d\u5c06\u4ed6\u5973\u513f\u8bf7\u6258\u7684\u4e8b\u60c5\u8bf4\u4e86\u3002\u8463\u5de8\u5ddd\u6c89\u601d\u4e0d\u8bed\u3002 \u3000\u3000\u674e\u536b\u9053\uff1a\u201c\u53ef\u6709\u4ec0\u4e48\u4e0d\u5bf9\u4e48\uff1f\u201d\u8463\u5de8\u5ddd\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u4e2a\u98ce\u5c18\u5f02\u4eba\uff0c\u4ed6\u600e\u4e48\u80af\u6bdb\u9042\u81ea\u8350\uff0c \u591a\u65b9\u8bf7\u6258\uff0c\u6765\u8003\u9009\u4e00\u4e2a\u629a\u8859\u7684\u536b\u58eb\uff1f\u201d\u674e\u536b\u542c\u4e86\uff0c\u62c2\u7136\u4e0d\u60a6\uff0c\u5fc3\u60f3\u81ea\u5df1\u4e43\u662f\u7687\u5e1d\u5ba0\u4fe1\u7684\u5927 \u81e3\uff0c\u4e00\u7701\u7684\u65b9\u9762\u5927\u5458\uff0c\u505a\u6211\u7684\u536b\u58eb\u8fd8\u6709\u4ec0\u4e48\u59d4\u5c48\u3002\u56e0\u9053\uff1a\u201c\u5f53\u4eca\u5723\u5929\u5b50\u5728\u4e0a\uff0c\u56db\u6d77\u5347\u5e73\uff0c\u5947 \u624d\u8f88\u51fa\u3002\u624d\u667a\u4e4b\u58eb\uff0c\u81ea\u7136\u8981\u56fe\u51fa\u8eab\u3002\u4ed6\u65e2\u662f\u98ce\u5c18\u5f02\u4eba\uff0c\u90a3\u4e48\u5e94\u53d7\u4f18\u793c\u3002\u201d\u53eb\u4eba\u8bf7\u5510\u9f99\u4e0a\u6765\uff0c \u4eb2\u81ea\u7b5b\u4e86\u4e09\u676f\u7f8e\u9152\uff0c\u656c\u7ed9\u4ed6\u559d\u3002\u5510\u9f99\u5728\u674e\u536b\u624b\u4e2d\u62e8\u8fc7\u9152\u676f\uff0c\u7a81\u7136\u624b\u8155\u4e00\u7ffb\uff0c\u628a\u674e\u536b\u7684\u8109\u95e8\u62ff \u7740\uff0c\u4e00\u628a\u63d0\u4e86\u8d77\u6765\uff0c\u7518\u5929\u9f99\u957f\u5251\u51fa\u624b\uff0c\u5530\u5530\u4e24\u5251\uff0c\u523a\u4ed6\u80cc\u5fc3\uff0c\u8fd9\u51e0\u4e0b\u52a8\u4f5c\uff0c\u90fd\u662f\u5feb\u5982\u7535\u5149\u77f3 \u706b\uff0c\u5510\u9f99\u5de6\u624b\u53cd\u624b\u4e00\u638c\uff0c\u597d\u50cf\u80cc\u540e\u957f\u7740\u773c\u775b\u4e00\u6837\uff0c\u628a\u7518\u5929\u9f99\u7684\u957f\u5251\u51fb\u843d\uff0c\u5c06\u674e\u536b\u821e\u4e86\u4e00\u4e2a\u5706 \u5708\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u6c5f\u5357\u5927\u4fa0\u7518\u51e4\u6c60\u5728\u6b64\uff0c\u8c01\u6562\u4e0a\u6765\uff01\u201d \u3000\u3000\u8463\u5de8\u5ddd\u9661\u7136\u4e00\u9707\uff0c\u8fd9\u7518\u51e4\u6c60\u540d\u9707\u5927\u6c5f\u5357\u5317\uff0c\u540d\u6c14\u6bd4\u4ed6\u7684\u5e08\u5144\u4e86\u56e0\u8fd8\u5927\uff01\u81ea\u5df1\u4e5f\u66fe\u542c\u4eba\u8bf4 \u8fc7\u4ed6\u7684\u76f8\u8c8c\uff0c\u600e\u4e48\u5374\u4f1a\u662f\u4ed6\uff1f\u629a\u8859\u7684\u536b\u58eb\u56db\u9762\u56f4\u7740\uff0c\u5374\u662f\u6295\u9f20\u5fcc\u5668\uff0c\u4e0d\u6562\u903c\u8fd1\u3002\u536b\u58eb\u603b\u7ba1\u8bb8 \u6210\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u5192\u7518\u5927\u4fa0\u7684\u540d\u4e49\u6b32\u4f55\u4e3a\uff1f\u201d\u5510\u9f99\u4e3e\u8896\u4e00\u62b9\uff0c\u53cc\u76ee\u795e\u5149\u5955\u5955\uff0c\u987f\u65f6\u53d8\u4e86\u9762 \u8c8c\u3002\u8bb8\u6210\u4e03\u516b\u5e74\u524d\uff0c\u66fe\u5728\u4e00\u6b21\u6b66\u6797\u524d\u8f88\u7684\u5bb4\u4f1a\u4e2d\uff0c\u89c1\u8fc7\u7518\u51e4\u6c60\u4e00\u9762\uff0c\u8fd9\u65f6\u89c1\u4ed6\u4e30\u795e\u4f9d\u65e7\uff0c\u4e0d \u89c9\u614c\u4e86\u624b\u811a\uff0c\u957f\u63d6\u8bf4\u9053\uff1a\u201c\u7518\u5927\u4fa0\uff0c\u5c0f\u7684\u4e0d\u77e5\u4f60\u8001\u6765\u5230\u7701\u57ce\uff0c\u6709\u5931\u8fce\u8fdb\uff0c\u96be\u602a\u4f60\u8001\u751f\u6c14\u3002\u8bf7 \u4f60\u8001\u9ad8\u62ac\u8d35\u624b\uff0c\u653e\u4e86\u655d\u4e3b\u4eba\u5427\u3002\u5c0f\u7684\u7ed9\u4f60\u78d5\u5934\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u563f\u563f\u51b7\u7b11\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u8c01\u751f\u4f60\u8fd9\u9e70\u722a\u5b59\u7684\u6c14\uff0c\u4f60\u4eec\u628a\u6c88\u5728\u5bbd\u653e\u51fa\u6765\uff0c\u54b1\u4eec\u4e00\u4e2a \u6362\u4e00\u4e2a\uff0c\u8981\u4e0d\u7136\u6211\u5c31\u628a\u4f60\u4eec\u629a\u53f0\u7684\u9888\u5b50\u626d\u65ad\u3002\u201d\u8bb8\u6210\u9053\uff1a\u201c\u7980\u7518\u5927\u4fa0\uff0c\u8fd9\u6c88\u5728\u5bbd\u662f\u671d\u5ef7\u6d3e\u4eba \u6349\u7684\uff0c\u4e0d\u5173\u6211\u4eec\u629a\u53f0\u5927\u4eba\u7684\u4e8b\u3002\u201d\u7518\u51e4\u6c60\u53c8\u628a\u674e\u536b\u821e\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u76ef\u7740\u8463\u5de8\u5ddd\u9053\uff1a\u201c\u54e6\uff0c\u4f60 \u8fd9\u4f4d\u5f62\u610f\u62f3\u7684\u5927\u540d\u5bb6\u4e5f\u505a\u4e86\u5bab\u4e2d\u4f8d\u536b\uff0c\u5931\u656c\uff0c\u5931\u656c\u3002\u4f60\u4eec\u867d\u7136\u662f\u5949\u65e8\u6349\u62ff\u94a6\u72af\uff0c\u4f46\u4f60\u4eec\u4e5f\u603b \u8be5\u77e5\u9053\uff0c\u8fd9\u674e\u536b\u4e43\u662f\u7687\u5e1d\u8001\u513f\u5ba0\u4fe1\u7684\u5927\u81e3\uff0c\u5728\u7687\u5e1d\u5fc3\u4e0a\uff0c\u674e\u536b\u7684\u4efd\u91cf\u53ef\u8981\u6bd4\u6c88\u5728\u5bbd\u91cd\u5f97\u591a\u3002 \u5c06\u4e00\u4e2a\u7763\u629a\u6362\u4e00\u4e2a\u624b\u65e0\u5bf8\u94c1\u7684\u4e66\u751f\uff0c\u4f60\u4eec\u4e5f\u4e0d\u5403\u4e8f\u3002\u8981\u4e0d\u7136\uff0c\u5728\u4f60\u4eec\u62a4\u536b\u4e4b\u4e0b\uff0c\u6211\u628a\u5927\u81e3\u6740 \u6b7b\uff0c\u4f60\u4eec\u7eb5\u6709\u591a\u5927\u529f\u52b3\uff0c\u7687\u5e1d\u4e5f\u8981\u602a\u8d23\uff0c\u4f60\u4eec\u7ec6\u60f3\u5427\u3002\u201d \u3000\u3000\u8463\u5de8\u5ddd\u7728\u4e86\u7728\u773c\uff0c\u6168\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u7518\u51e4\u6c60\uff0c\u8fd9\u4e2a\u4e70\u5356\u54b1\u4eec\u505a\u4e86\u3002\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00 \u97ad\uff0c\u6211\u628a\u6c88\u5728\u5bbd\u4ea4\u4f60\uff0c\u4f60\u53ef\u4e0d\u80fd\u4f24\u629a\u53f0\u5927\u4eba\u6beb\u53d1\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u201d\u8463\u5de8\u5ddd\u4e00\u628a \u62d6\u4e86\u8bb8\u6210\u4fbf\u8dd1\uff0c\u7518\u5929\u9f99\u624b\u8db3\u65e0\u63aa\uff0c\u6697\u60f3\u5927\u54e5\u600e\u4e48\u64c5\u81ea\u7b54\u5e94\uff0c\u5c06\u6765\u600e\u4e48\u597d\u4ea4\u5dee\u554a\uff01 \u3000\u3000\u8463\u5de8\u5ddd\u62d6\u7740\u8bb8\u6210\u98de\u8dd1\uff0c\u8bb8\u6210\u5c71\u6115\u7136\u4e0d\u89e3\uff0c\u8463\u5de8\u5ddd\u8bf4\u9053\uff1a\u201c\u8bb8\u603b\u7ba1\uff0c\u4f60\u5feb\u5e26\u6211\u5230\u540e\u5802\u53bb\u89c1 \u674e\u5c0f\u59d0\uff0c\u4f60\u5c31\u8bf4\u662f\u5927\u4eba\u5dee\u4f60\u56de\u6765\u8bf7\u5979\u7684\uff0c\u5207\u4e0d\u53ef\u8bf4\u5927\u4eba\u88ab\u7518\u51e4\u6c60\u64d2\u4e86\u3002\u201d\u8bb8\u6210\u4e0d\u77e5\u7528\u610f\uff0c\u4f46 \u4e5f\u53ea\u597d\u7b54\u5141\u3002\u5230\u4e86\u540e\u5802\uff0c\u7980\u4e86\u4e0a\u53bb\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u73af\u4f69\u53ee\u5f53\uff0c\u674e\u660e\u73e0\u679c\u7136\u51fa\u6765\u4e86\u3002\u95e8\u5e18\u540e\u8fd8\u6709 \u4e00\u4e2a\u5973\u5b50\u7684\u8eab\u5f62\u4e00\u95ea\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u8986\u96e8\u7ffb\u4e91\u624b\uff0c\u82b1\u660e\u67f3\u6697\u65f6\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u4e09\u56de\u3000\u771f\u4e2a\u60c5\u75f4\u3000\u5341\u5e74\u5982\u4e00\u65e5\u3000\u51e0\u7591\u68a6\u5e7b\u3000\u5345\u8f7d\u56f0\u5e7d\u5bab \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u5341\u4e09\u56de \u771f\u4e2a\u60c5\u75f4\u3000\u5341\u5e74\u5982\u4e00\u65e5 \u51e0\u7591\u68a6\u5e7b\u3000\u5345\u8f7d\u56f0\u5e7d\u5bab \u3000\u3000\u8fd9\u8bb8\u603b\u7ba1\u662f\u674e\u536b\u4eb2\u4fe1\uff0c\u5e73\u65f6\u4e5f\u5230\u5185\u5802\uff0c\u6240\u4ee5\u674e\u660e\u73e0\u5e76\u4e0d\u89c1\u7591\uff0c\u63ed\u5e18\u95ee\u9053\uff1a\u201c\u7239\u7239\u5524\u6211\u4f55 \u4e8b\uff1f\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8463\u5de8\u5ddd\u8eab\u5b50\u4e00\u5f13\uff0c\u75be\u5982\u98de\u7bad\uff0c\u84e6\u7136\u51b2\u8fdb\u5185\u5ba4\u3002\u5e18\u540e\u85cf\u7740\u7684\u5973\u5b50\u8981\u8eb2 \u5df2\u6765\u4e0d\u53ca\uff0c\u5f13\u978b\u4e00\u8d77\uff0c\u53f3\u8db3\u659c\u98de\u8e22\u51fa\uff0c\u8463\u5de8\u5ddd\u8eab\u5b50\u9661\u7136\u4e00\u7f29\uff0c\u8db3\u6839\u4e00\u65cb\uff0c\u53cc\u638c\u4e00\u9634\u4e00\u9633\uff0c\u731b \u7684\u53d1\u51fa\uff0c\u90a3\u5973\u5b50\u4e00\u811a\u8e22\u7a7a\uff0c\u500f\u89c9\u638c\u98ce\u626b\u9888\uff0c\u8eab\u5b50\u4e00\u4ef0\uff0c\u7adf\u7136\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u4e2d\u907f\u4e86\u5f00\u53bb\u3002\u8463\u5de8 \u5ddd\u5e76\u4e0d\u6536\u52bf\uff0c\u5de6\u624b\u4e00\u6293\u654c\u8155\uff0c\u53f3\u624b\u731d\u51fb\u9762\u95e8\uff0c\u653b\u52bf\u7ef5\u7ef5\u4e0d\u7edd\uff0c\u90a3\u5973\u5b50\u8eab\u5f62\u95ea\u52a8\uff0c\u624b\u80cc\u4e00\u62e9\uff0c \u7528\u201c\u68da\u5f0f\u201d\u5316\u5f00\u4e86\u8463\u5de8\u5ddd\u8fce\u9762\u7684\u5288\u638c\uff0c\u5de6\u8155\u5411\u524d\u51b2\u51fb\uff0c\u53c8\u628a\u654c\u4eba\u5de6\u62f3\u7684\u653b\u52bf\u4e5f\u5316\u89e3\u4e86\uff0c\u8463\u5de8 \u5ddd\u559d\u9053\uff1a\u201c\u9648\u7f8e\u5a18\uff0c\u4f60\u7684\u4e08\u592b\u5df2\u7ed9\u64d2\u4e86\uff0c\u4f60\u8fd8\u6562\u987d\u6297\uff1f\u201d\u90a3\u5973\u5b50\u9661\u7136\u4e00\u9707\uff0c\u8463\u5de8\u5ddd\u5de6\u624b\u4e00 \u6c89\uff0c\u53f3\u638c\u76f4\u653b\u90a3\u5973\u5b50\u4e24\u4e73\u4e4b\u95f4\u7684\u201c\u7384\u673a\u7a74\u201d\uff0c\u90a3\u5973\u5b50\u5927\u6012\uff0c\u4e00\u4e2a\u6eda\u8eab\uff0c\u5de6\u53f3\u4e24\u8098\uff0c\u524d\u649e\u540e \u649e\uff0c\u8fd9\u4e00\u62db\u7a81\u7136\u4ece\u5185\u5bb6\u62f3\u7684\u201c\u5982\u5c01\u4f3c\u56e2\u201d\uff0c\u53d8\u4e3a\u5916\u5bb6\u62f3\u7684\u201c\u8c79\u98df\u864e\u513f\u201d\uff0c\u6765\u52bf\u6781\u731b\u3002\u8463\u5de8\u5ddd \u662f\u5f62\u610f\u6d3e\u540d\u5bbf\uff0c\u7ecf\u9a8c\u8001\u5230\uff0c\u4ed6\u77e5\u9053\u53ea\u51ed\u672c\u8eab\u529f\u529b\uff0c\u867d\u7136\u4e5f\u53ef\u53d6\u80dc\uff0c\u4f46\u5374\u5fc5\u6709\u4e00\u7ffb\u6076\u6597\uff0c\u53ea\u6050 \u8bef\u4e86\u65f6\u523b\uff0c\u6240\u4ee5\u4e00\u5f00\u9996\u5c31\u8bd3\u79f0\u5979\u7684\u4e08\u592b\u88ab\u64d2\uff0c\u4f7f\u5979\u5fc3\u4e71\uff0c\u7ee7\u800c\u7528\u8f7b\u8584\u7684\u638c\u6cd5\uff0c\u5f15\u5979\u53d1\u6012\uff0c\u4e71 \u5219\u6c14\u6d6e\uff0c\u6012\u5219\u5fc3\u8e81\uff0c\u8463\u5de8\u5ddd\u89d1\u4e2a\u6b63\u7740\uff0c\u5de6\u624b\u4e00\u6258\u654c\u8155\uff0c\u53f3\u624b\u638c\u5fc3\u4e00\u7ffb\uff0c\u8fc5\u5982\u95ea\u7535\u822c\u7684\u6263\u7740\u4e86 \u90a3\u5973\u5b50\u81c2\u5f2f\u7684\u201c\u66f2\u6c60\u7a74\u201d\uff0c\u65bd\u5c55\u64d2\u62ff\u624b\u6cd5\u628a\u90a3\u5973\u5b50\u6349\u4e86\u8fc7\u6765\u3002\u674e\u660e\u73e0\u60ca\u9053\uff1a\u201c\u8bb8\u603b\u7ba1\uff0c\u8fd9\u4eba \u662f\u8c01\uff1f\u4e3a\u4f55\u5230\u6211\u7684\u5367\u5ba4\u6765\u6349\u4eba\uff1f\u201d\u8463\u5de8\u5ddd\u7b11\u9053\uff1a\u201c\u4e0d\u628a\u5979\u6349\u53bb\uff0c\u4f60\u7684\u7239\u7239\u53ef\u8981\u6027\u547d\u4e0d\u4fdd \u54e9\uff01\u201d\u8fc8\u5f00\u5927\u6b65\uff0c\u4e0e\u8bb8\u6210\u547c\u5578\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u5973\u5b50\u540d\u5524\u9648\u7f8e\u5a18\uff0c\u6b63\u662f\u6c5f\u5357\u5927\u4fa0\u7518\u51e4\u6c60\u7684\u59bb\u5b50\u3002\u9648\u7f8e\u5a18\u6b66\u529f\u867d\u6bd4\u7518\u51e4\u6c60\u5dee\u8bb8\u591a\uff0c\u4f46\u5728 \u6c5f\u6e56\u4e0a\u4e5f\u5df2\u7ecf\u5c11\u6709\u3002\u4ed6\u4eec\u592b\u59bb\u4e8c\u4eba\uff0c\u6700\u597d\u6e38\u620f\u98ce\u5c18\uff0c\u5728\u6c5f\u5357\u4e00\u5e26\u884c\u4fa0\u4ed7\u4e49\u3002\u4e00\u4e2a\u6708\u524d\uff0c\u4ed6\u4eec \u642d\u4e86\u4e00\u4e2a\u6c5f\u6e56\u73ed\u5b50\uff0c\u6765\u5230\u676d\u5dde\u5356\u827a\u3002\u7518\u51e4\u6c60\u56e0\u540d\u5934\u592a\u54cd\uff0c\u6240\u4ee5\u7528\u836f\u6613\u5bb9\uff0c\u5230\u4e86\u676d\u5dde\uff0c\u6070\u5de7\u78b0 \u7740\u629a\u53f0\u674e\u536b\u4e3a\u6bcd\u4eb2\u795d\u5bff\uff0c\u62db\u4ed6\u4eec\u7684\u73ed\u5b50\u8fdb\u8859\u8868\u6f14\uff1b\u53c8\u6070\u5de7\u629a\u53f0\u7684\u5973\u513f\u6b22\u559c\u9648\u7f8e\u5a18\u7684\u6742\u6280\uff0c\u65f6 \u65f6\u62db\u5979\u8fdb\u8859\uff0c\u7518\u51e4\u6c60\u8eab\u65e0\u522b\u4e8b\uff0c\u4e5f\u5c31\u7559\u4e86\u4e0b\u6765\u3002\u4e3a\u4e86\u60f3\u770b\u770b\u629a\u8859\u5185\u6709\u4ec0\u4e48\u80fd\u4eba\uff0c\u6545\u610f\u53c2\u52a0\u4e86 \u536b\u58eb\u7684\u9009\u62d4\uff0c\u7518\u51e4\u6c60\u5230\u676d\u5dde\u65f6\u66fe\u548c\u8def\u6c11\u77bb\u901a\u8fc7\u6d88\u606f\uff0c\u5415\u56db\u5a18\u548c\u8def\u6c11\u77bb\u4e00\u5230\u676d\u5dde\uff0c\u7acb\u523b\u627e\u5230\u4e86 \u7518\u51e4\u6c60\uff0c\u8bf7\u4ed6\u8bbe\u6cd5\u3002\u8fd9\u65e5\u6070\u9022\u629a\u53f0\u9762\u8bd5\uff0c\u7518\u51e4\u6c60\u5f53\u573a\u663e\u6280\uff0c\u7528\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\u5c06\u629a\u53f0\u64d2 \u4e86\u8fc7\u6765\u3002\u90a3\u6599\u8463\u5de8\u5ddd\u8001\u5978\u5de8\u6ed1\uff0c\u542c\u5f97\u629a\u53f0\u8bf4\u8d77\u7518\u51e4\u6c60\uff08\u5316\u540d\u5510\u9f99\uff09\u7684\u6765\u5386\uff0c\u4e8b\u53d1\u4e4b\u540e\uff0c\u5fc3\u60f3 \u90a3\u4e2a\u6742\u6280\u73ed\u7684\u5973\u5b50\uff0c\u5fc5\u662f\u9648\u7f8e\u5a18\u65e0\u7591\u3002\u8bd5\u5f80\u5185\u5802\u4e00\u649e\u679c\u7136\u649e\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u4e14\u8bf4\u7518\u51e4\u6c60\u5c06\u674e\u536b\u631f\u4e3a\u4eba\u8d28\uff0c\u5728\u5802\u4e0a\u5927\u53e3\u5927\u53e3\u7684\u559d\u9152\uff0c\u795e\u91c7\u98de\u626c\uff0c\u629a\u53f0\u7684\u536b\u58eb\uff0c\u5728\u5802\u4e0b \u7a7f\u68ad\u6765\u5f80\uff0c\u4e00\u4e2a\u4e2a\u80c6\u6218\u5fc3\u60ca\u3002\u7518\u51e4\u6c60\u7b49\u5f97\u5fc3\u7126\uff0c\u559d\u9053\uff1a\u201c\u5140\u90a3\u59d3\u8463\u7684\u8001\u8d3c\uff0c\u4e3a\u4f55\u8fd8\u4e0d\u56de \u6765\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5802\u4e0b\u4e00\u58f0\u5e94\u9053\uff1a\u201c\u7518\u5927\u4fa0\u5c11\u5b89\u6bcb\u8e81\uff0c\u4ffa\u6765\u4e86\uff01\u201d \u3000\u3000\u8463\u5de8\u5ddd\u4e09\u6307\u6263\u7740\u9648\u7f8e\u5a18\u7684\u8109\u95e8\uff0c\u7b11\u563b\u563b\u7684\u5c06\u5979\u62d6\u4e0a\u5802\u6765\uff0c\u7518\u51e4\u6c60\u89c1\u4e86\uff0c\u53c8\u60ca\u53c8\u6012\u3002\u8463\u5de8 \u5ddd\u7b11\u9053\uff1a\u201c\u5c06\u629a\u53f0\u5927\u4eba\u4ea4\u6362\u8d24\u5ac2\uff0c\u8fd9\u603b\u7b97\u4e24\u4e0d\u5403\u4e8f\u5427\u3002\u201d\u7518\u51e4\u6c60\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u6697\u9a82\u201c\u5978 \u8d3c\u201d\uff0c\u4f46\u4ed6\u592b\u59bb\u6069\u7231\uff0c\u7eb5\u7136\u751f\u6c14\uff0c\u4e5f\u8981\u4ea4\u6362\u3002\u5f53\u4e0b\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u628a\u5979\u653e\u5f00\uff0c\u6211\u5c06\u629a\u53f0 \u8fd8\u4f60\u3002\u201d\u8463\u5de8\u5ddd\u9053\uff1a\u201c\u4f60\u53ef\u4e0d\u8bb8\u6697\u4e0b\u6bd2\u624b\u3002\u201d\u7518\u51e4\u6c60\u6012\u9053\uff1a\u201c\u6c5f\u5357\u5927\u4fa0\uff0c\u8bf4\u4e00\u4e0d\u4e8c\u3002\u201d\u8463\u5de8 \u5ddd\u5c06\u9648\u7f8e\u5a18\u5f80\u524d\u4e00\u63a8\uff0c\u7518\u51e4\u6c60\u4e5f\u628a\u674e\u536b\u653e\u4e0b\u3002\u674e\u536b\u8dd1\u4e0b\u53f0\u9636\uff0c\u9648\u7f8e\u5a18\u8dd1\u4e0a\u5802\u4e0a\u3002\u8fd9\u65f6\u5185\u5802\u91cc \u4eba\u58f0\u5608\u6742\uff0c\u79e6\u4e2d\u8d8a\u6240\u5e26\u7684\u5fa1\u6797\u519b\u5ffd\u7136\u4ece\u91cc\u9762\u51b2\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e14\u8bf4\u674e\u660e\u73e0\u76ee\u7779\u8463\u5de8\u5ddd\u5c06\u9648\u7f8e\u5a18\u64d2\u53bb\uff0c\u53c8\u60ca\u53c8\u6c14\uff0c\u5979\u7edd\u60f3\u4e0d\u5230\u8fd9\u5356\u89e3\u7684\u5973\u5b50\u6709\u90a3\u4e48\u5927\u7684 \u6765\u5934\uff0c\u8dd1\u5165\u5367\u623f\uff0c\u7830\u4e00\u58f0\u5173\u4e86\u623f\u95e8\uff0c\u6eda\u5728\u5e8a\u4e0a\u75db\u54ed\uff0c\u6c14\u607c\u7236\u4eb2\u7684\u536b\u58eb\u5bf9\u5979\u6ca1\u6709\u793c\u8c8c\u3002\u6b63\u54ed\u6ce3 \u95f4\uff0c\u8863\u6a71\u5ffd\u7136\u6253\u5f00\uff0c\u91cc\u9762\u94bb\u51fa\u4e86\u4e00\u4e2a\u4eba\u6765\uff0c\u7adf\u662f\u4e00\u4e2a\u7f8e\u82e5\u5929\u4ed9\u7684\u5c11\u5973\uff0c\u674e\u660e\u73e0\u60ca\u9a87\u4e4b\u6781\uff0c\u6536 \u4e86\u54ed\u58f0\uff0c\u90a3\u5c11\u5973\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u5c0f\u59d0\u4f60\u751f\u8c01\u7684\u6c14\u554a\uff1f\u8bf4\u7ed9\u6211\u542c\uff0c\u6211\u6709\u529e\u6cd5\u66ff\u4f60\u62a5\u4ec7\u3002\u201d\u8bdd\u58f0\u67d4 \u7f8e\u4eb2\u5207\uff0c\u674e\u660e\u73e0\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6211\u662f\u90a3\u5356\u89e3\u5973\u5b50\u7684\u73ed\u4e2d\u59d0\u59b9\u3002\u201d\u674e\u660e \u73e0\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u6211\u4e0d\u89c1\u4f60\u8fdb\u6765\uff1f\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u636e\u542c\u8bf4\u5c0f\u59d0\u5929\u59ff\u56fd\u8272\uff0c\u6211\u4e5f\u60f3\u8c61\u9648\u59d0\u59d0\u4e00\u9053 \u6765\u770b\u4f60\uff0c\u53ef\u662f\u4f60\u53ea\u8bf7\u9648\u59d0\u59d0\u4e00\u4eba\uff0c\u6240\u4ee5\u6211\u53ea\u597d\u6084\u6084\u7684\u8ddf\u5979\u8fdb\u6765\u4e86\u3002\u201d\u674e\u660e\u73e0\u5c0f\u5b69\u5fc3\u6027\uff0c\u542c\u90a3 \u5c11\u5973\u8d5e\u5979\u7f8e\u8c8c\uff0c\u5341\u5206\u9ad8\u5174\u3002\u7b11\u9053\uff1a\u201c\u4f60\u624d\u7f8e\u5462\uff01\u4f60\u662f\u65b0\u8fd1\u642d\u73ed\u7684\u5417\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u662f\u5440\uff0c \u6ca1\u6709\u89c1\u7740\u5c0f\u59d0\u4ee5\u524d\uff0c\u6211\u4e5f\u4ee5\u4e3a\u81ea\u5df1\u5f88\u7f8e\uff0c\u89c1\u4e86\u5c0f\u59d0\uff0c\u624d\u77e5\u9053\u81ea\u5df1\u5dee\u5f97\u8fdc\u5462\uff01\u201d\u674e\u660e\u73e0\u8d8a\u53d1\u9ad8 \u5174\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u4f60\u6709\u529e\u6cd5\u66ff\u6211\u62a5\u4ec7\uff0c\u4f60\u6709\u4ec0\u4e48\u529e\u6cd5\u5462\uff1f\u4f60\u7684\u59d0\u59d0\u4e5f\u7ed9\u4ed6\u4eec \u6349\u53bb\u4e86\u3002\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u662f\u4eac\u57ce\u6765\u7684\u90a3\u4e9b\u5fa1\u6797\u519b\u5417\uff1f\u201d\u674e\u660e\u73e0\u60f3\u8d77\u90a3\u65e5\u5728\u56da\u623f\u91cc\uff0c\u65c1\u8fb9\u6709\u51e0 \u4e2a\u4eba\u770b\u5b88\u72af\u4eba\uff0c\u521a\u624d\u6765\u6349\u4eba\u7684\u90a3\u4e2a\u5bb6\u4f19\u4f3c\u4e4e\u5c31\u662f\u770b\u5b88\u4e4b\u4e00\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5927\u7ea6\u662f\u5427\u3002\u201d\u90a3\u5c11 \u5973\u9053\uff1a\u201c\u90a3\u6613\u529e\u4e86\uff0c\u6211\u548c\u4f60\u53bb\u628a\u72af\u4eba\u5077\u653e\u51fa\u6765\u2026\u2026\u201d\u674e\u660e\u73e0\u60ca\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u7239\u7239\u8981\u9a82\u7684\u3002\u201d \u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u4f60\u542c\u6211\u8bf4\u5440\uff0c\u54b1\u4eec\u628a\u4ed6\u653e\u51fa\u6765\uff0c\u6084\u6084\u7684\u85cf\u8d77\u6765\uff0c\u7136\u540e\u4ea4\u7ed9\u4f60\u7684\u7236\u4eb2\u770b\u7ba1\u3002\u8fd9 \u6837\uff0c\u72af\u4eba\u8fd8\u662f\u5728\u629a\u8859\u5185\uff0c\u53ef\u662f\u8ba9\u90a3\u4e9b\u5fa1\u6797\u519b\u683d\u4e00\u4e2a\u8ddf\u6597\u3002\u8c01\u53eb\u4ed6\u4eec\u4e0d\u5c0a\u91cd\u4f60\u7239\uff0c\u8fd8\u6b3a\u4fae\u4f60 \u5462\uff1f\u201d\u674e\u660e\u73e0\u9053\uff1a\u201c\u4ed6\u4eec\u6709\u4eba\u770b\u5b88\u7684\u5440\u3002\u201d\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u53ea\u8981\u4f60\u5e26\u6211\u5230\u56da\u623f\uff0c\u6211\u5c31\u6709\u4ea6 \u6cd5\u3002\u201d\u674e\u660e\u73e0\u8fd8\u662f\u4e2a\u4e0d\u61c2\u4e8b\u7684\u5973\u5b69\uff0c\u90a3\u77e5\u5929\u9ad8\u5730\u539a\uff0c\u5979\u89c9\u5f97\u8fd9\u4e8b\u60c5\u5012\u4e5f\u597d\u73a9\uff0c\u800c\u4e14\u5979\u5bf9\u90a3\u56da \u72af\u4e5f\u9887\u597d\u611f\uff0c\u5fc3\u60f3\uff1a\u628a\u4ed6\u85cf\u8d77\u6765\u548c\u4ed6\u804a\u804a\u5929\uff0c\u4e00\u5b9a\u5f88\u6709\u8da3\u3002\u90a3\u56da\u72af\u4e00\u8868\u65af\u6587\uff0c\u8fd8\u4f1a\u505a\u8bd7\u7684 \u5462\uff01\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u597d\uff01\u54b1\u4eec\u5c31\u53bb\u3002\u201d\u53d6\u51fa\u4e24\u5957\u7537\u5b50\u8863\u88f3\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u6362\u4e86\u8863\u68e0\u53bb\u5427\u3002\u201d\u90a3 \u5c11\u5973\u8d5e\u9053\uff1a\u201c\u4f60\u771f\u806a\u660e\u3002\u201d\u4e0d\u4e00\u4f1a\u6362\u597d\u670d\u88c5\uff0c\u674e\u660e\u73e0\u5c06\u5979\u5e26\u5230\u56da\u623f\u3002\u5916\u9762\u7684\u536b\u58eb\u559d\u9053\uff1a\u201c\u4ec0 \u4e48\u4eba\uff1f\u201d\u674e\u660e\u73e0\u4e00\u65f6\u5fc3\u614c\uff0c\u7adf\u7136\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u629a\u53f0\u5927\u4eba\u542c\u8bf4\u94a6\u72af\u6709\u75c5\uff0c\u53eb\u6211\u6765\u770b\u770b\u662f\u5426\u8981\u8bf7\u5927\u592b\uff1f\u201d\u5b88\u95e8\u7684\u201c\u566b\u201d\u4e86\u4e00 \u58f0\u9053\uff1a\u201c\u629a\u53f0\u5927\u4eba\u600e\u4e48\u77e5\u9053\uff1f\u201d\u90a3\u5c11\u5973\u7528\u8098\u8f7b\u8f7b\u649e\u4e86\u674e\u660e\u73e0\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5148\u56de\u53bb\u7980\u544a\u603b \u7ba1\uff0c\u53eb\u4ed6\u8bf7\u5927\u592b\u53bb\u3002\u6211\u8fdb\u53bb\u770b\u770b\u3002\u201d\u674e\u660e\u73e0\u521d\u65f6\u8d2a\u73a9\uff0c\u73b0\u5728\u89c1\u5b88\u95e8\u536b\u571f\u4e2a\u4e2a\u51f6\u795e\u6076\u715e\u822c\u76ef\u7740 \u81ea\u5df1\uff0c\u4e0d\u89c9\u5fc3\u614c\u3002\u731b\u9192\u8d77\u81ea\u5df1\u73b0\u5728\u5df2\u7ecf\u4e0d\u662f\u629a\u53f0\u5343\u91d1\u7684\u8eab\u4efd\uff0c\u82e5\u7136\u53d7\u5230\u4ec0\u4e48\u4fae\u8fb1\uff0c\u90a3\u5c82\u4e0d\u662f \u81ea\u8ba8\u6ca1\u8da3\uff0c\u542c\u4e86\u5c11\u5973\u7684\u8bdd\uff0c\u7acb\u523b\u8f6c\u8fc7\u8eab\u8eaf\uff0c\u626c\u624b\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u4e86\u94a6\u72af\u4e4b\u540e\uff0c\u8d76\u5feb\u6765\u627e\u6211\uff01\u201d \u5fa1\u6797\u519b\u7684\u7edf\u9886\u79e6\u4e2d\u8d8a\u5728\u91cc\u9762\u5927\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u4e0d\u8bb8\u8fdb\u6765\uff01\u201d\u674e\u660e\u73e0\u5df2\u7ecf\u8dd1\u5f00\uff0c\u5b88\u95e8\u7684\u536b\u58eb \u4f38\u624b\u62e6\u90a3\u4e54\u88c5\u5c11\u5973\uff0c\u5c11\u5973\u9053\uff1a\u201c\u629a\u53f0\u5927\u4eba\u8981\u770b\u4e5f\u4e0d\u884c\u5417\uff1f\u201d\u536b\u58eb\u9053\uff1a\u201c\u628a\u629a\u53f0\u7684\u4ee4\u7b26\u62ff \u6765\u3002\u201d\u5c11\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u624b\u6307\u4e00\u5f39\uff0c\u5df2\u70b9\u4e2d\u4e86\u90a3\u536b\u58eb\u7a74\u9053\uff0c\u79e6\u4e2d\u8d8a\u5728\u623f\u5185\u542c\u5f97\u5916\u9762\u201c\u6251\u901a\u201d\u5012 \u5730\u4e4b\u58f0\uff0c\u614c\u5fd9\u8df3\u8d77\uff0c\u53ea\u89c1\u4e00\u4e2a\u5c11\u5e74\u75be\u62a2\u8fdb\u6765\uff0c\u9a88\u6307\u5982\u621f\uff0c\u70b9\u4ed6\u9762\u4e0a\u53cc\u775b\u3002\u79e6\u4e2d\u8d8a\u5927\u559d\u4e00\u58f0\uff1a \u201c\u6709\u523a\u5ba2\uff01\u201d\u53cc\u7b14\u659c\u98de\uff0c\u5de6\u53f3\u4ea4\u523a\uff0c\u90a3\u5c11\u5e74\u8eab\u6cd5\u7adf\u662f\u8fc5\u75be\u5f02\u5e38\uff0c\u8eab\u5f62\u4e00\u77ee\uff0c\u5c31\u5728\u53cc\u7b14\u65b9\u5206\u672a \u5408\u4e4b\u9645\uff0c\u8e0f\u4e2d\u5bab\u76f4\u62a2\u8fdb\u6765\uff0c\u62db\u5f0f\u672a\u53d8\uff0c\u53cc\u6307\u7565\u6c89\uff0c\u6233\u5411\u80f8\u53e3\u7684\u201c\u5f53\u95e8\u7a74\u201d\uff0c\u8fd9\u201c\u5f53\u95e8\u7a74\u201d\u53c8 \u540d\u201c\u8840\u7a74\u201d\uff0c\u4e43\u662f\u4eba\u8eab\u4e5d\u4e2a\u201c\u6b7b\u7a74\u201d\u4e4b\u4e00\uff0c\u79e6\u4e2d\u8d8a\u5927\u5403\u4e00\u60ca\uff0c\u8eb2\u95ea\u4e0d\u53ca\uff0c\u4f0f\u5730\u4e00\u6eda\uff0c\u5de6\u624b\u5224 \u5b98\u7b14\u9aa4\u7684\u63b7\u51fa\uff0c\u963b\u654c\u8fdb\u653b\uff0c\u90a3\u5c11\u5e74\u4e94\u6307\u4e00\u62e2\uff0c\u7adf\u7136\u628a\u79e6\u4e2d\u8d8a\u7684\u5175\u5668\u6293\u5728\u624b\u4e2d\uff0c\u53cd\u624b\u4e00\u63b7\uff0c\u5982 \u540c\u80cc\u540e\u6709\u773c\u775b\u4e00\u822c\uff0c\u5c06\u62a2\u8fdb\u56da\u623f\u7684\u4e00\u4e2a\u536b\u58eb\u51fb\u5012\u3002\u6b65\u4f3c\u7075\u733f\uff0c\u4ecd\u7136\u8ffd\u51fb\uff0c\u79e6\u4e2d\u8d8a\u662f\u5fa1\u6797\u519b\u7684 \u7edf\u9886\u4e4b\u4e00\uff0c\u6b66\u529f\u4e0d\u5f31\uff0c\u8fd9\u65f6\u5df2\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u7ffb\u4e86\u8d77\u6765\uff0c\u628a\u5269\u4e0b\u7684\u4e00\u652f\u5224\u5b98\u7b14\u5f53\u4e94\u884c\u5251 \u7528\uff0c\u76d8\u65cb\u98de\u821e\uff0c\u524d\u906e\u540e\u6321\uff0c\u800c\u95e8\u5916\u51e0\u4e2a\u5f53\u73ed\u7684\u5fa1\u6797\u519b\uff0c\u4e5f\u95fb\u58f0\u6d8c\u8fdb\u3002\u8fd9\u5c11\u5e74\u597d\u4e0d\u5389\u5bb3\uff0c\u53cd\u8eab \u4e00\u8dc3\uff0c\u628a\u6700\u5148\u6d8c\u8fdb\u7684\u4e24\u540d\u519b\u58eb\u76f4\u63bc\u51fa\u53bb\u3002\u79e6\u4e2d\u8d8a\u7a0d\u6709\u4f59\u6687\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5954\u5411\u623f\u4e2d\u7684\u56da\u72af\uff0c\u90a3 \u6599\u547c\u547c\u98ce\u54cd\uff0c\u773c\u775b\u4e00\u82b1\uff0c\u90a3\u5c11\u5e74\u7adf\u5982\u5927\u9e1f\u4e00\u822c\uff0c\u5728\u81ea\u5df1\u5934\u9876\u98de\u8fc7\uff0c\u62e6\u5728\u94a6\u72af\u69db\u524d\uff0c\u53cc\u638c\u4e00 \u63a8\uff0c\u6253\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u5de6\u624b\u4e0a\u6311\uff0c\u53f3\u624b\u4e0b\u6309\uff0c\u79e6\u4e2d\u8d8a\u628a\u7b14\u4e00\u6321\uff0c\u90a3\u5c11\u5e74\u53cc\u638c\u53d8\u6307\uff0c\u4e00\u70b9\u201c\u671f \u95e8\u201d\uff0c\u4e00\u70b9\u201c\u5c06\u53f0\u201d\uff0c\u8fd9\u4e24\u5904\u7a74\u9053\uff0c\u90fd\u662f\u4eba\u8eab\u201c\u6655\u7a74\u201d\uff0c\u79e6\u4e2d\u8d8a\u672c\u662f\u6253\u7a74\u7684\u597d\u624b\uff0c\u89c1\u8fd9\u5c11\u5e74 \u70b9\u7a74\u5947\u5feb\uff0c\u5413\u4e86\u4e00\u8eab\u51b7\u6c57\u3002\u75be\u5fd9\u9000\u65f6\uff0c\u90a3\u91cc\u8fd8\u6765\u5f97\u53ca\uff0c\u521a\u4e00\u8f6c\u8eab\uff0c\u80cc\u540e\u7acb\u89c9\u5947\u75db\uff0c\u5de6\u808b\u7684 \u201c\u7cbe\u4fc3\u7a74\u201d\u5df2\u7ed9\u70b9\u7740\u3002\u8fd9\u201c\u7cbe\u4fc3\u7a74\u201d\u5728\u80cc\u540e\u7531\u4e0b\u6570\u4e0a\u7684\u7b2c\u4e8c\u6761\u4e0e\u7b2c\u4e09\u6761\u7684\u9aa8\u7f1d\u4e2d\uff0c\u9002\u5f53\u813e \u4f4d\uff0c\u4e43\u662f\u4eba\u8eab\u4e5d\u4e2a\u201c\u54d1\u7a74\u201d\u4e4b\u4e00\uff0c\u4e00\u88ab\u70b9\u4e2d\uff0c\u6d51\u8eab\u762b\u75ea\u3002 \u3000\u3000\u5c11\u5e74\u5f97\u624b\u4e4b\u540e\uff0c\u53cd\u8eab\u5c06\u69db\u4e0a\u7684\u72af\u4eba\u62b1\u8d77\uff0c\u4f4e\u58f0\u5524\u9053\uff0c\u201c\u6c88\u54e5\u54e5\uff01\u201d\u6c88\u5728\u5bbd\u5589\u5934\u560e\u54bd\uff0c\u5e94 \u58f0\u5fae\u5f31\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u6551\u6211\u4e86\u3002\u201d\u8fd9\u4e54\u88c5\u5c11\u5e74\u6b63\u662f\u5415\u56db\u5a18\uff0c\u5979\u542c\u5f97\u6c88\u5728\u5bbd\u80fd\u591f\u8bf4\u8bdd\uff0c\u653e\u4e0b\u4e86 \u5fc3\uff0c\u5de6\u624b\u73af\u62b1\u8170\u56f4\uff0c\u5c06\u4ed6\u80cc\u8d77\uff0c\u53f3\u624b\u4ece\u8863\u895f\u62d4\u51fa\u4e00\u53e3\u7cbe\u5149\u95ea\u76ee\u7684\u817c\u5251\uff0c\u53cd\u8eab\u4fbf\u5f80\u5916\u95ef\uff01 \u3000\u3000\u95e8\u5916\u7684\u5fa1\u6797\u519b\u7eb7\u7eb7\u547c\u559d\uff0c\u90a3\u91cc\u963b\u62e6\u5f97\u4f4f\uff1f\u5415\u56db\u5a18\u7eb5\u9ad8\u7a9c\u4f4e\uff0c\u76f4\u51b2\u51fa\u5916\u9762\u5927\u5802\u3002\u7518\u51e4\u6c60\u5927 \u58f0\u6b22\u547c\uff0c\u8463\u5de8\u5ddd\u5927\u4e3a\u9707\u52a8\u3002\u674e\u536b\u53eb\u9053\uff1a\u201c\u5feb\u622a\uff01\u201d\u8463\u5de8\u5ddd\u7518\u5929\u9f99\u53cc\u62a2\u4e0a\u53bb\u3002\u7518\u51e4\u6c60\u5927\u559d\u4e00 \u58f0\uff0c\u8fd0\u7528\u5927\u64d2\u62ff\u624b\uff0c\u75be\u7684\u6293\u7740\u4e00\u540d\u536b\u58eb\u80cc\u5fc3\uff0c\u671d\u4e24\u4eba\u6454\u53bb\u3002\u9648\u7f8e\u5a18\u521a\u624d\u5403\u4e86\u5927\u4e8f\uff0c\u5fc3\u4e2d\u6c14 \u607c\uff0c\u62d4\u51fa\u5315\u9996\uff0c\u62a2\u5728\u7518\u51e4\u6c60\u4e4b\u524d\uff0c\u5411\u8463\u5de8\u5ddd\u75be\u523a\uff0c\u8463\u5de8\u5ddd\u4e24\u624b\u865a\u63a9\u9762\u95e8\uff0c\u817e\u7684\u98de\u8d77\u53f3\u8db3\uff0c\u5411 \u9648\u7f8e\u5a18\u7684\u5315\u9996\u8e22\u6765\uff0c\u9648\u7f8e\u5a18\u8f6c\u8eab\u4e00\u95ea\uff0c\u7518\u5929\u9f99\u5237\u7684\u4e00\u5251\u4fa7\u9762\u88ad\u5230\uff0c\u9648\u7f8e\u5a18\u5315\u9996\u4e00\u683c\uff0c\u8463\u5de8\u5ddd \u75be\u51b2\u4e0a\u53bb\uff0c\u60f3\u628a\u9648\u7f8e\u5a18\u518d\u6b21\u64d2\u7740\u3002\u5ffd\u7136\u547c\u7684\u4e00\u58f0\uff0c\u7518\u51e4\u6c60\u5982\u7bad\u98de\u81f3\uff0c\u53cc\u624b\u4e00\u5206\uff0c\u56db\u81c2\u76f8\u683c\uff0c \u8463\u5de8\u5ddd\u8eab\u5f62\u4e0d\u7a33\uff0c\u51e0\u4e4e\u4ec6\u5730\uff0c\u4ece\u6000\u4e2d\u4e00\u63a2\uff0c\u53d6\u51fa\u900f\u9aa8\u6bd2\u9489\uff0c\u4e09\u652f\u9f50\u53d1\uff0c\u6025\u52b2\u5f02\u5e38\uff0c\u7518\u51e4\u6c60\u5927 \u559d\u4e00\u58f0\uff0c\u5de6\u624b\u53c8\u6293\u8d77\u4e00\u540d\u536b\u58eb\uff0c\u5f80\u524d\u4e00\u63a8\uff0c\u4e09\u679d\u6bd2\u9489\uff0c\u90fd\u6253\u5165\u536b\u58eb\u8eab\u4e2d\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u53c8\u6765\u6251 \u51fb\u3002\u8463\u5de8\u5ddd\u6025\u5fd9\u6e38\u8d70\uff0c\u5415\u56db\u5a18\u5df2\u529b\u65a9\u5341\u4f59\u536b\u58eb\uff0c\u51b2\u81f3\u6708\u95e8\uff0c\u56de\u5934\u53eb\u9053\uff1a\u201c\u4e03\u54e5\u4e03\u5ac2\uff0c\u54b1\u4eec\u5feb \u8d70\uff01\u201d\u7518\u5929\u9f99\u4e0e\u9648\u7f8e\u5a18\u6b66\u827a\u76f8\u5f53\uff0c\u4ecd\u5728\u7f20\u6218\uff0c\u536b\u58eb\u4ece\u65c1\u76f8\u52a9\uff0c\u5c06\u5979\u56f4\u5728\u6838\u5fc3\u3002\u7518\u51e4\u6c60\u638c\u5288\u6307 \u6233\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u628a\u56f4\u653b\u9648\u7f8e\u5a18\u7684\u536b\u58eb\u5168\u90e8\u6253\u4f24\u3002\u7518\u5929\u9f99\u80c6\u6218\u5fc3\u60ca\uff0c\u614c\u5fd9\u9000\u65f6\uff0c\u9648\u59dc\u5a18\u5315\u9996\u4e00 \u9001\uff0c\u63d2\u5165\u4ed6\u80c1\u4e0b\u4e09\u5bf8\uff0c\u95f7\u6c14\u987f\u8212\uff0c\u548c\u7518\u51e4\u6c60\u5e76\u80a9\u5916\u95ef\u3002\u629a\u8859\u7684\u536b\u58eb\u51e0\u66fe\u89c1\u8fc7\u5982\u6b64\u58f0\u52bf\uff0c\u5c31\u5982 \u95ee\u7ed9\u72c2\u6f6e\u51b2\u51fb\u4e00\u822c\uff0c\u5411\u4e24\u8fb9\u5206\u88c2\u5f00\u53bb\u3002\u72c2\u7b11\u58f0\u4e2d\uff0c\u7518\u51e4\u6c60\u592b\u5987\u8ffd\u4e0a\u4e86\u5415\u56db\u5a18\uff0c\u4e09\u4eba\u90fd\u51b2\u51fa\u629a \u8859\u53bb\u4e86\u3002 \u3000\u3000\u5415\u56db\u5a18\u5230\u4e86\u7518\u51e4\u6c60\u5bb6\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u501f\u9759\u5ba4\u4e00\u7528\u3002\u201d\u9648\u7f8e\u5a18\u5c06\u5979\u5e26\u5165\u5367\u623f\uff0c\u5fae\u7b11\u9000\u51fa\uff0c \u987a\u624b\u5c06\u623f\u95e8\u865a\u63a9\u3002\u5415\u56db\u5a18\u9762\u4e0a\u4e00\u7ea2\uff0c\u5e26\u7b11\u9a82\u9053\uff0c\u201c\u6dd8\u6c14\u7684\u5ac2\u5b50\uff0c\u597d\u4e0d\u6b63\u7ecf\u3002\u201d\u5c06\u6c88\u5728\u5bbd\u653e\u5728 \u5e8a\u4e0a\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6c88\u54e5\u54e5\uff0c\u6211\u5728\u4f60\u7684\u8eab\u8fb9\u5462\uff01\u201d\u6c88\u5728\u5bbd\u9762\u8272\u60e8\u767d\uff0c\u53cc\u7738\u534a\u5f20\uff0c\u53f9\u53e3\u6c14\u9053\uff1a \u201c\u4f60\u4f55\u5fc5\u6551\u6211\uff1f\u201d\u5415\u56db\u5a18\u5fc3\u513f\u4e71\u8df3\uff0c\u6025\u5fd9\u89e3\u8863\u5ba1\u89c6\uff0c\u5374\u4e0d\u89c1\u6709\u751a\u4f24\u75d5\uff0c\u624b\u81c0\u4e00\u677e\uff0c\u6c88\u5728\u5bbd\u6251 \u901a\u53c8\u5012\u3002\u5415\u56db\u5a18\u662f\u4e2a\u5927\u884c\u5bb6\uff0c\u628a\u8033\u8d34\u5728\u4ed6\u80f8\u524d\u4e00\u542c\uff0c\u4f38\u624b\u5728\u4ed6\u817f\u5f2f\u4e00\u626d\uff0c\u6c88\u5728\u5bbd\u201c\u54df\u201d\u7684\u4e00 \u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u5374\u4ecd\u662f\u4e0d\u80fd\u52a8\u5f39\u3002\u5415\u56db\u5a18\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u53ea\u542c\u6c88\u5728\u5bbd\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u6210\u5e9f \u4eba\u4e86\uff0c\u4f60\u6551\u6211\u4e5f\u6ca1\u7528\u3002\u201d\u539f\u6765\u8463\u5de8\u5ddd\u8001\u5978\u5de8\u6ed1\uff0c\u751f\u4f2f\u6709\u4eba\u628a\u6c88\u5728\u5bbd\u6551\u8d70\uff0c\u7adf\u7136\u4e0b\u4e86\u6bd2\u624b\uff0c\u7528 \u5185\u5bb6\u638c\u529b\u9707\u88c2\u4ed6\u4e0b\u80a2\u7b4b\u7edc\uff0c\u53c8\u7528\u9634\u529b\u4f7f\u4ed6\u53d7\u4e86\u5185\u4f24\uff0c\u628a\u4ed6\u5f04\u6210\u762b\u75ea\uff0c\u9020\u6210\u75e8\u75c7\u3002\u7eb5\u6709\u534e\u9640\u518d \u4e16\uff0c\u4e5f\u96be\u6551\u6cbb\u3002\u5415\u56db\u5a18\u5982\u53d7\u96f7\u8f70\uff0c\u9b42\u98de\u9b44\u6563\uff0c\u4f46\u4e00\u89e6\u53ca\u6c88\u5728\u5bbd\u90a3\u51c4\u7136\u7edd\u671b\u7684\u773c\u5149\uff0c\u6025\u5fd9\u4ee5\u7edd \u5927\u7684\u5b9a\u529b\uff0c\u9547\u4f4f\u5fc3\u795e\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u6ca1\u7528\uff0c\u5728\u5bbd\uff0c\u4f60\u5728\u4ee5\u540d\u61e6\u81ea\u8d1f\uff0c\u5c82\u4e0d\u95fb\uff1a\u5de6\u4e18\u5931 \u660e\uff0c\u53a5\u6709\u56fd\u8bed\uff1b\u5b59\u5b50\u8191\u811a\uff0c\u800c\u8bba\u5175\u6cd5\uff1b\u592a\u53f2\u516c\u8eab\u53d7\u8150\u5211\u4e43\u8457\u53f2\u8bb0\u3002\u8fd9\u4e09\u4eba\u4f55\u5c1d\u4e0d\u662f\u6b8b\u5e9f\uff1f\u4f46 \u5374\u540d\u5782\u5343\u79cb\u4e07\u4e16\uff01\u5728\u5bbd\uff0c\u4e00\u6b7b\u6613\u8033\uff0c\u5927\u4e08\u592b\u5f53\u5728\u7edd\u96be\u4e4b\u4e2d\u4ee5\u6c42\u751f\uff01\u201d\u5415\u56db\u5a18\u4f83\u4f83\u800c\u8c08\uff0c\u8bf4\u5230 \u540e\u6765\uff0c\u7adf\u7136\u58f0\u6cea\u4ff1\u4e0b\u3002 \u3000\u3000\u201c\u5de6\u4e18\u5931\u660e\uff0c\u53a5\u6709\u56fd\u8bed\uff1b\u5b59\u5b50\u8191\u811a\uff0c\u800c\u8bba\u5175\u6cd5\u3002\u201d\u8fd9\u662f\u300a\u592a\u53f2\u516c\u81ea\u5e8f\u300b\u91cc\u7684\u4e24\u53e5\u8bdd\uff0c \u300a\u53f2\u8bb0\u300b\u7684\u4f5c\u8005\u53f8\u9a6c\u8fc1\uff0c\u662f\u6c49\u6b66\u5e1d\u65f6\u4ee3\u7684\u4eba\uff0c\u4ed6\u4e3a\u4e86\u674e\u9675\u8d25\u964d\u5308\u5974\u7684\u4e8b\u8bf4\u4e86\u51e0\u53e5\u516c\u9053\u8bdd\uff0c\u89e6 \u6012\u4e86\u6c49\u6b66\u5e1d\uff0c\u7adf\u7136\u8eab\u53d7\u201c\u8150\u5211\u201d\uff08\u9609\u5272\uff09\uff0c\u6240\u4ee5\u4ed6\u4ee5\u5de6\u4e18\u660e\u548c\u5b59\u8179\u81ea\u51b5\uff0c\u8bf4\u4e18\u6c0f\u5728\u5931\u660e\u4e4b \u540e\uff0c\u5b59\u8179\u5728\u88ab\u524a\u53bb\u819d\u76d6\u9aa8\u4e4b\u540e\uff0c\u8fd8\u80fd\u8457\u4e66\u7acb\u8bf4\uff0c\u4ed6\u4eec\u4e0d\u662f\u50cf\u81ea\u5df1\u4e00\u6837\u90fd\u662f\u201c\u5e9f\u4eba\u201d\u5417\uff1f\u4ed6\u4ee5 \u524d\u4eba\u575a\u5fcd\u4e0d\u62d4\u7684\u7cbe\u795e\u9f13\u821e\u4e86\u81ea\u5df1\uff0c\u56e0\u800c\u4e5f\u5199\u51fa\u4e86\u4f1f\u5927\u7684\u53f2\u4e66\u3002\u800c\u4eca\u5415\u56db\u5a18\u4ee5\u5de6\u4e18\u660e\u3001\u5b59\u8191\u548c \u53f8\u9a6c\u8fc1\u4e09\u4eba\u4e3a\u4f8b\uff0c\u6fc0\u52b1\u6c88\u5728\u5bbd\u81ea\u52c9\u3002\u6c88\u5728\u5bbd\u70ed\u6cea\u76c8\u7736\uff0c\u9661\u7136\u89c9\u5f97\u751f\u547d\u91cd\u65b0\u5145\u5b9e\u8d77\u6765\uff0c\u4ed6\u7d27\u63e1 \u7740\u5415\u56db\u5a18\u7684\u624b\uff0c\u5415\u56db\u5a18\u4e5f\u662f\u6ee1\u8138\u6cea\u5149\uff0c\u7136\u800c\u8fd9\u5df2\u7ecf\u4e0d\u662f\u7edd\u671b\u7684\u773c\u6cea\u4e86\uff0c\u8fd9\u6cea\u5149\u9a71\u6563\u4e86\u6c88\u5728\u5bbd \u773c\u524d\u7684\u7070\u6697\uff0c\u4ed6\u770b\u5230\u4e86\u751f\u547d\u7684\u610f\u4e49\u4e86\u3002 \u3000\u3000\u6c88\u5728\u5bbd\u7d27\u63e1\u7740\u5415\u56db\u5a18\u7684\u624b\uff0c\u826f\u4e45\u3001\u826f\u4e45\uff0c\u5401\u58f0\u8bf4\u9053\uff1a\u201c\u83b9\u59b9\uff0c\u8c22\u8c22\u4f60\u3002\u201d\u5415\u56db\u5a18\u5fae\u5fbd\u4e00 \u7b11\uff0c\u773c\u6cea\u6ef4\u5728\u4ed6\u7684\u8138\u4e0a\u3002\u6c88\u5728\u5bbd\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u8f9c\u8d1f\u4e86\u4f60\u7684\u5fc3\u610f\u4e86\u3002\u201d\u5415\u56db\u5a18\u773c\u6ce2\u4e00\u8f6c\uff0c \u6df1\u60c5\u7684\u770b\u671b\u7740\u4ed6\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u5728\u5bbd\uff0c\u4f60\u7684\u8eab\u4f53\u6b8b\u5e9f\uff0c\u4f60\u7684\u5fc3\u7075\u53ef\u5e76\u6ca1\u6709\u6b8b\u5e9f\u554a\uff01\u4f60\u518d\u8fd9\u6837 \u8bf4\uff0c\u5c31\u662f\u628a\u6211\u5f53\u4f5c\u5916\u4eba\u4e86\u3002\u201d\u8fd9\u4e9b\u8bdd\u8bf4\u5f97\u975e\u5e38\u575a\u5b9a\uff0c\u6c88\u5728\u5bbd\u611f\u52a8\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u53c8\u8fc7\u4e86\u4e00 \u9635\uff0c\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e14\u8bf4\u4f60\u4e0d\u4f1a\u6c38\u8fdc\u6b8b\u5e9f\uff0c\u53ea\u8981\u4f60\u6709\u8654\u5fc3\u6bc5\u529b\uff0c\u6211\u53ef\u4ee5\u6559\u4f60\u81ea\u7597\u4e4b\u6cd5\u3002\u201d\u5728\u5bbd \u9053\uff1a\u201c\u4f60\u73b0\u5728\u5df2\u7ecf\u662f\u4e00\u4e2a\u6781\u597d\u7684\u533b\u751f\u4e86\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u8fd8\u8981\u505a\u66f4\u597d\u7684\u533b\u751f\uff0c\u6211\u6559\u4f60\u5410\u7eb3 \u4e4b\u6cd5\uff0c\u5230\u4f60\u81ea\u5df1\u80fd\u5bfc\u5f15\u8840\u6c14\uff0c\u4ee5\u610f\u884c\u6c14\u7684\u65f6\u5019\uff0c\u4f60\u5c31\u4f1a\u5b8c\u5168\u75ca\u6108\uff0c\u800c\u4e14\u6bd4\u5e38\u4eba\u8fd8\u8981\u5065\u5eb7\u3002\u201d \u6c88\u5728\u5bbd\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4f60\u4eec\u4fee\u7ec3\u7684\u5185\u529f\u5417\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6b63\u662f\uff0c\u4f60\u7ed9\u522b\u4eba\u7528\u5185\u529f\u5f04\u6210\u6b8b\u5e9f\uff0c \u53d7\u4e86\u6697\u4f24\uff0c\u4e5f\u53ea\u6709\u4fee\u7ec3\u5185\u529f\u6765\u62b5\u5fa1\uff01\u201d\u6c88\u5728\u5bbd\u9053\uff1a\u201c\u8981\u591a\u5c11\u65f6\u5019\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e5f\u8bb8\u8981\u5341 \u5e74\u3002\u201d\u6c88\u5728\u5bbd\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6b63\u662f\u7ed9\u6211\u7684\u78e8\u7ec3\u3002\u201d \u3000\u3000\u9648\u7f8e\u5a18\u5728\u5916\u9762\u542c\u5f97\u54ed\u58f0\uff0c\u8f7b\u8f7b\u6572\u95e8\uff0c\u5415\u56db\u5a18\u5f00\u95e8\u51fa\u6765\uff0c\u5c06\u4e8b\u60c5\u5bf9\u7518\u51e4\u6c60\u592b\u5987\u8bf4\u4e86\u3002\u9648\u7f8e \u5a18\u6697\u6697\u611f\u53f9\uff0c\u5fc3\u60f3\uff0c\u6559\u4e00\u4e2a\u6b8b\u5e9f\u7684\u4eba\u4e60\u7ec3\u5185\u529f\uff0c\u800c\u4e14\u8fd8\u662f\u4e00\u4e2a\u5168\u65e0\u6839\u5e95\u3001\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\u7684\u4e66 \u751f\uff0c\u5341\u5e74\u4e5f\u672a\u5fc5\u80fd\u591f\uff0c\u8fd9\u5c82\u4e0d\u8bef\u4e86\u5e08\u59b9\u9752\u6625\uff01\u7518\u51e4\u6c60\u9053\uff1a\u201c\u5341\u5e74\u4fbf\u662f\u5341\u5e74\uff0c\u53e4\u4eba\u9ad8\u4e49\uff0c\u6b63\u987b \u6211\u8f88\u884c\u4e4b\u3002\u6211\u53ea\u662f\u6015\u5e08\u59b9\u6ca1\u6709\u9759\u4fee\u4e4b\u5730\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e00\u5ff5\u5927\u5e08\u7684\u5e08\u5f1f\u4e00\u74e2\u548c\u5c1a\uff0c\u65e5\u5185\u5c31 \u5c06\u79fb\u5c45\u4ed9\u971e\uff0c\u6211\u53ef\u4ee5\u5728\u90a3\u91cc\u7ed3\u5e90\u508d\u5c45\uff0c\u7167\u6599\u5728\u5bbd\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u597d\uff0c\u6211\u4eec\u592b\u5987\u9001\u4f60\u4e0a\u5c71\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u96c7\u4e86\u9a74\u8f66\uff0c\u9760\u7740\u7518\u51e4\u6c60\u7684\u6613\u5bb9\u4e4b\u672f\u3001\u53d8\u4e86\u9762\u8c8c\u5148\u56de\u5bb6\u4e61\uff0c\u628a\u7236\u4eb2\u5b89\u846c\u4e4b\u540e\uff0c\u5c31\u968f \u4e00\u74e2\u548c\u5c1a\u5230\u4ed9\u971e\u5cad\u9690\u5c45\u3002\u7518\u51e4\u6c60\u4e0b\u5c71\u65f6\u5bf9\u5415\u56db\u5a18\u9053\uff1a\u201c\u5c06\u6765\u6211\u4eec\u6597\u90a3\u4e86\u56e0\u8d3c\u79c3\u4e4b\u65f6\uff0c\u53ea\u6015\u8fd8 \u8981\u5e08\u59b9\u76f8\u52a9\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff0c\u6211\u5728\u5c71\u4e2d\uff0c\u4e5f\u6b63\u53ef\u4ee5\u8d81\u6b64\u65f6\u673a\u52e4\u4fee\u5251\u8bc0\u3002\u201d \u3000\u3000\u81ea\u6b64\uff0c\u6c88\u5728\u5bbd\u5728\u5415\u56db\u5a18\u7684\u7167\u6599\u4e0b\uff0c\u65e5\u6709\u8fdb\u6b65\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u8fc7\u4e86\u4e94\u5e74\u3002 \u3000\u3000\u4e00\u65e5\u6e05\u6668\uff0c\u5415\u56db\u5a18\u7167\u7740\u65e5\u5e38\u4e60\u60ef\u5230\u6c88\u5728\u5bbd\u623f\u4e2d\uff0c\u672a\u5165\u623f\u95e8\uff0c\u5728\u7a97\u53e3\u671b\u8fdb\uff0c\u5ffd\u89c1\u4ed6\u4e00\u4e2a\u4eba \u6276\u7740\u5899\u58c1\u8d70\u8def\uff0c\u8fd9\u4e00\u559c\u975e\u540c\u5c0f\u53ef\uff0c\u8d76\u5fd9\u63a8\u95e8\u8fdb\u53bb\u3002\u6c88\u5728\u5bbd\u9053\uff1a\u201c\u6628\u665a\u6211\u505a\u4e86\u5410\u7eb3\u529f\u8bfe\u4e4b\u540e\uff0c \u5ffd\u89c9\u6c14\u8fbe\u91cd\u5173\uff0c\u4e0a\u4e0b\u5347\u6c89\uff0c\u53c8\u597d\u4f3c\u80f8\u8179\u4e4b\u95f4\uff0c\u6709\u4e00\u56e2\u4e1c\u897f\uff0c\u53ef\u4ee5\u4e0a\u4e0b\u8f6c\u8fc2\u3002\u6211\u8bd5\u7528\u529b\uff0c\u5c45\u7136 \u80fd\u5750\u8d77\u6765\uff0c\u4eca\u671d\u6211\u6276\u7740\u5899\u58c1\uff0c\u5df2\u7ecf\u4ece\u5e8a\u524d\u884c\u5230\u4e66\u6848\uff0c\u6765\u56de\u4e09\u6b21\u4e86\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4f60\u8fdb\u5883\u795e \u901f\uff0c\u8fd9\u6837\u770b\u6765\uff0c\u4e0d\u5fc5\u5341\u5e74\u3002\u4f46\u4f60\u521d\u521d\u5b66\u884c\uff0c\u4e0d\u5b9c\u8fc7\u52b3\uff0c\u8fd8\u662f\u8eba\u56de\u5e8a\u4e0a\u4f11\u606f\u5427\u3002\u201d\u5ffd\u89c1\u4e66\u684c\u4e0a \u6709\u4e00\u7eb8\u8bcd\u7b3a\uff0c\u5199\u4e86\u51e0\u884c\u5927\u5b57\u3002\u55d4\u9053\uff1a\u201c\u4f60\u624d\u597d\u4e00\u70b9\uff0c\u53c8\u52b3\u795e\u4f5c\u8bd7\u4e86\uff0c\u6211\u8981\u7f5a\u4f60\u3002\u201d\u6293\u8d77\u8bcd \u7b3a\uff0c\u6c88\u5728\u5bbd\u6025\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u8fd8\u6211\uff01\u201d\u8eab\u5b50\u98a0\u5dcd\u5dcd\u7684\uff0c\u7adf\u7136\u79bb\u5f00\u5899\u58c1\u6765\u62a2\uff0c\u7acb\u8db3\u4e0d\u7a33\uff0c\u4e00\u8de4\u8dcc \u5012\u5415\u56db\u5a18\u6000\u4e2d\uff0c\u5415\u56db\u5a18\u6025\u5fd9\u628a\u4ed6\u6276\u4e0a\u5e8a\u4e0a\uff0c\u53ea\u89c1\u4ed6\u9762\u7ea2\u8fc7\u8033\uff0c\u5415\u56db\u5a18\u5df2\u4e00\u773c\u628a\u90a3\u51e0\u884c\u5b57\u770b \u5b8c\uff0c\u539f\u6765\u4e0d\u662f\u4f5c\u8bd7\uff0c\u800c\u662f\u96c6\u53e5\uff0c\u5c06\u524d\u4eba\u8bd7\u8bcd\uff0c\u96c6\u6210\u4e86\u4e00\u9996\u201c\u70f7\u6eaa\u6c99\u201d\u8c03\uff0c\u8bcd\u9053\uff1a \u3000\u3000\u201c\u8c01\u9053\u74e2\u7fe0\u4e0d\u53ef\u601c\uff0c\u91d1\u7089\u65ad\u5c3d\u5c0f\u7bc6\u9999\uff0c\u4eba\u751f\u4f55\u5904\u4f3c\u5c0a\u524d\uff1f\u89c1\u4e86\u53c8\u4f11\u8fd8\u4f3c\u68a6\uff0c\u5750\u91c7\u867d\u8fd1\u8fdc \u5982\u5929\uff0c\u65ad\u6765\u80fd\u6709\u51e0\u56de\u80a0\uff1f\u201d \u3000\u3000\u56db\u5a18\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u5bf9\u6211\u8fd8\u6709\u7591\u8651\u3002\u4ed6\u5bf9\u6211\u6df1\u60c5\u7737\u604b\uff0c\u5374\u53c8\u81ea\u60ed\u5f62\u79fd\uff0c\u6015\u8fd9\u59fb\u7f18\u7ec8\u5982\u955c \u82b1\u6c34\u6708\uff0c\u4e0d\u6562\u5bf9\u6211\u5410\u9732\u8877\u80a0\uff0c\u6240\u4ee5\u624d\u6709\u8fd9\u201c\u89c1\u4e86\u53c8\u4f11\u8fd8\u4f3c\u68a6\uff0c\u5750\u6765\u867d\u8fd1\u8fdc\u5982\u5929\u3002\u201d\u7684\u611f\u89e6\u3002 \u6c88\u5728\u5bbd\u5446\u5446\u7684\u770b\u7740\u5979\uff0c\u5415\u56db\u5a18\u53f9\u9053\uff1a\u201c\u50bb\u54e5\u54e5\uff0c\u4e94\u5e74\u6765\u96be\u9053\u4f60\u8fd8\u6ca1\u6709\u660e\u767d\u6211\u7684\u5fc3\u4e8b\uff1f\u4e0d\u7ba1\u4f60 \u600e\u6837\uff0c\u6211\u90fd\u4f34\u7740\u4f60\u3002\u201d\u6c88\u5728\u5bbd\u773c\u5708\u4e00\u7ea2\uff0c\u4e0d\u89c9\u6ef4\u4e0b\u6cea\u6765\u3002\u4e24\u4eba\u6267\u624b\u76f8\u770b\uff0c\u8bf4\u4e0d\u5c3d\u67d4\u60c5\u871c\u610f\u3002 \u6b63\u5728\u9676\u9189\u4e4b\u4e2d\uff0c\u5ffd\u542c\u5f97\u4e00\u74e2\u548c\u5c1a\u5728\u5916\u53d1\u58f0\u76f8\u5524\u3002 \u3000\u3000\u5415\u56db\u5a18\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u597d\u751f\u5c06\u606f\uff0c\u4e0d\u8981\u80e1\u601d\u4e71\u60f3\uff0c\u6211\u53bb\u53bb\u4fbf\u56de\u3002\u201d\u51fa\u4e86\u5c71\u5c45\uff0c\u53ea\u89c1\u4e00\u74e2 \u548c\u5c1a\u7acb\u5728\u5cf0\u5934\u62db\u624b\u3002\u5415\u56db\u5a18\u8dd1\u4e86\u4e0a\u53bb\uff0c\u95ee\u9053\uff1a\u201c\u5927\u5e08\u62db\u5524\uff0c\u53ef\u6709\u4f55\u4e8b\uff1f\u201d\u4e00\u74e2\u548c\u5c1a\u5411\u5c71\u4e0b\u4e00 \u6307\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u770b\uff01\u201d\u5415\u56db\u5a18\u51dd\u7738\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4eba\u75be\u4f3c\u661f\u4e38\uff0c\u4ece\u5c71\u4e0b\u5954\u4e0a\uff0c\u5df2\u5230\u4e86\u4e0b\u9762\u5cf0 \u8170\uff0c\u4e00\u74e2\u548c\u5c1a\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u77e5\u9053\u6211\u4eec\u9690\u5c45\u5728\u6b64\uff1f\u83ab\u975e\u662f\u6e05\u5ef7\u9e70\u72ac\u4e48\uff1f\u201d\u5415\u56db\u5a18\u51b7\u7b11\u9053\uff1a\u201c\u82e5 \u662f\u6e05\u5ef7\u9e70\u72ac\uff0c\u90a3\u5c31\u662f\u9001\u6b7b\u6765\u4e86\u3002\u201d\u8fc7\u4e86\u4e00\u9635\uff0c\u4eba\u5f71\u8d8a\u6765\u8d8a\u73b0\uff0c\u8f6e\u5ed3\u4f9d\u7a00\u53ef\u8fa8\u3002\u5415\u56db\u5a18\u5947\u9053\uff1a \u201c\u600e\u4e48\u4f1a\u662f\u4ed6\uff1f\u201d\u8bdd\u521a\u8bf4\u5b8c\uff0c\u90a3\u4eba\u5df2\u8dc3\u4e0a\u5cf0\u9876\uff0c\u53d1\u58f0\u53eb\u9053\uff1a\u201c\u5415\u59d0\u59d0\uff0c\u4f60\u679c\u7136\u5728\u6b64\uff01\u201d\u5415\u56db \u5a18\u559c\u9053\uff1a\u201c\u5510\u6653\u6f9c\uff0c\u554a\uff0c\u51e0\u5e74\u4e0d\u89c1\uff0c\u4f60\u53d8\u6210\u5927\u4eba\u4e86\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u66ff\u4e00\u74e2\u548c\u5c1a\u4ecb\u7ecd\u4e4b\u540e\uff0c\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5415\u59d0\u59d0\uff0c\u6211\u627e\u5f97\u4f60\u597d\u82e6\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u662f \u4e48\uff1f\u8c01\u544a\u8bc9\u4f60\u7684\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u7518\u5927\u4fa0\u3002\u4f46\u4ed6\u53c8\u4e0d\u76f8\u4fe1\u6211\u3002\u5415\u59d0\u59d0\u2014\u2014\u201d\u4e00\u74e2\u548c\u5c1a\u542c\u4ed6\u8bf4 \u8bdd\u6ca1\u5934\u6ca1\u8111\uff0c\u548c\u5415\u56db\u5a18\u53c8\u5f88\u4eb2\u70ed\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u56de\u53bb\u7ed9\u4f60\u4eec\u70f9\u8336\u3002\u201d\u5510\u6653\u6f9c\u5f85\u5f97\u4e00 \u74e2\u548c\u5c1a\u8d70\u540e\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u5415\u59d0\u59d0\uff0c\u4f60\u8981\u6551\u6211\uff01\u201d\u5415\u56db\u5a18\u5947\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u6211\u6551\u4f60\uff1f\u201d\u5510\u6653 \u6f9c\u9053\uff1a\u201c\u5415\u59d0\u59d0\uff0c\u522b\u4eba\u51a4\u6789\u6211\uff0c\u53ea\u6709\u4f60\u80fd\u89e3\u6551\uff0c\u4f60\u76f8\u4fe1\u6211\u5417\uff1f\u201d\u5415\u56db\u5a18\u67d4\u58f0\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f \u5f1f\uff0c\u6211\u4ee5\u4e3a\u4f60\u957f\u5927\u4e86\uff0c\u4f60\u8fd8\u662f\u4ece\u524d\u90a3\u5c0f\u5b69\u6a21\u6837\u3002\u4f60\u8bf4\uff0c\u4ec0\u4e48\u4eba\u51a4\u6789\u4f60\u4e86\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u7684\u4fa0\u4e49\u9053\u3002\u201d\u5415\u56db\u5a18\u5947\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\uff1f\u662f\u8c01\u4eba\u9886\u5934\uff1f\u201d \u5510\u6653\u6f9c\u9053\uff1a\u201c\u662f\u6211\u4ee5\u524d\u7684\u5e08\u5085\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u8fd8\u6709\u8c01\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u8fd8 \u6709\u5173\u4e1c\u56db\u4fa0\u4ed6\u4eec\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5173\u4e1c\u56db\u4fa0\u4e0d\u662f\u4f60\u4e49\u7236\u5468\u9752\u7684\u670b\u53cb\uff0c\u800c\u4e14\u548c\u4f60\u4e00\u8def\u7684\u5417\uff1f\u201d \u5510\u6653\u6f9c\u9053\uff1a\u201c\u4ed6\u4eec\u672c\u6765\u5bf9\u6211\u5f88\u597d\uff0c\u7231\u6211\u5982\u540c\u5b50\u4f84\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u90a3\u4f60\u4e0d\u8981\u5fc3\u614c\uff0c\u597d\u597d\u8bf4\u7ed9 \u6211\u542c\u3002\u201d \u3000\u3000\u4e8e\u662f\u5510\u6653\u6f9c\u4ece\u4e94\u5e74\u524d\u968f\u6613\u5170\u73e0\u4e0a\u5929\u5c71\u7684\u65f6\u5019\u8bf4\u8d77\u3002\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5415\u59d0\u59d0\uff0c\u4f60\u8bb0\u5f97\u5417\uff1f\u6211 \u4ee5\u524d\u5bf9\u4f60\u8bf4\u8fc7\uff0c\u6211\u6709\u4e00\u5bf9\u5b6a\u751f\u7684\u4f84\u5973\uff0c\u751f\u5f97\u975e\u5e38\u53ef\u7231\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u6211\u600e\u4e48\u4f1a\u4e0d\u8bb0\u5f97 \u5462\uff1f\u201d\u6211\u548c\u4f60\u5728\u9099\u5c71\u521d\u4f1a\u4e4b\u65f6\uff0c\u516b\u81c2\u795e\u9b54\u80cc\u7740\u7684\u90a3\u4e2a\u5973\u5a03\u513f\u4e0d\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a \u201c\u90a3\u662f\u59d0\u59d0\uff0c\u540d\u5524\u51af\u745b\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u90a3\u4e48\u540e\u6765\u53cc\u9b54\u5728\u949f\u4e07\u5802\u624b\u4e2d\u53c8\u62a2\u53bb\u4e00\u4e2a\uff0c\u90a3\u4e2a\u662f\u59b9 \u59b9\u4e86\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6b63\u662f\u3002\u5979\u540d\u53eb\u51af\u7433\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4e00\u5bf9\u5b6a\u751f\u59d0\u59b9\u5c82\u4e0d\u662f\u90fd \u5230\u4e86\u53cc\u9b54\u624b\u4e2d\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4f60\u542c\u6211\u8bf4\uff0c\u8fd9\u624d\u53eb\u5947\u7279\u5462\u3002\u6211\u968f\u6613\u7956\u5a46\u5230\u4e86\u5929\u5c71\uff0c\u6613 \u7956\u5a46\u9053\uff1a\u6211\u5728\u8def\u4e0a\u8bf4\u8fc7\u8981\u8fd8\u4f60\u4e00\u4e2a\u4f84\u5973\u2026\u201d\u5415\u56db\u5a18\u63d2\u53e3\u9053\uff1a\u201c\u6211\u4e5f\u8bb0\u5f97\u5979\u8bf4\u8fc7\u8fd9\u6837\u7684\u8bdd\uff0c\u662f \u5728\u5e74\u5bb6\u8bf4\u7684\u3002\u662f\u4e0d\u662f\u5979\u6536\u517b\u4e86\u4e00\u4e2a\u5973\u5a03\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4e0d\u662f\u6536\u517b\u7684\uff0c\u662f\u62a2\u6765\u7684\uff01\u201d\u5415\u56db\u5a18 \u5947\u9053\uff1a\u201c\u62a2\u8c01\u7684\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u62a2\u53cc\u9b54\u7684\uff01\u539f\u6765\u6613\u7956\u5a46\u5728\u4e0e\u6211\u4f1a\u5408\u4e4b\u524d\uff0c\u4e3a\u4e86\u5230\u5904\u627e\u6211\uff0c \u66fe\u7ecf\u5230\u8fc7\u5317\u65b9\u3002\u4e00\u65e5\u5728\u56db\u7687\u5b50\u7684\u5e9c\u95e8\u524d\uff0c\u89c1\u5230\u51af\u745b\u548c\u53cc\u9b54\u73a9\u800d\uff0c\u6613\u7956\u5a46\u770b\u4e86\u4e00\u773c\uff0c\u5c31\u7231\u4e0a\u4e86 \u90a3\u4e2a\u5973\u5a03\uff0c\u5979\u5e74\u7eaa\u5df2\u8001\uff0c\u6b63\u60f3\u627e\u4e00\u4e2a\u7075\u654f\u7684\u5f92\u513f\u5462\u2026\u201d\u5979\u975e\u5e38\u5947\u602a\uff0c\u8fd9\u6837\u53ef\u7231\u7684\u5973\u5a03\u513f\uff0c\u600e \u4e48\u4f1a\u4e0e\u53cc\u9b54\u5728\u4e00\u8d77\uff1f\u800c\u4e14\u4ed6\u4eec\u770b\u6765\u53c8\u7edd\u5bf9\u4e0d\u662f\u7236\u5973\u3002\u4e8e\u662f\u4e00\u65f6\u5174\u8d77\uff0c\u7adf\u7136\u79c1\u5165\u56db\u7687\u5e9c\uff0c\u63a2\u51fa \u8fd9\u5973\u5a03\u4e5f\u662f\u53cc\u9b54\u62a2\u6765\u7684\u3002\u5c31\u6beb\u4e0d\u5ba2\u6c14\uff0c\u534a\u591c\u4e09\u66f4\u628a\u51af\u745b\u62b1\u8d70\u4e86\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u56db\u7687\u5b50\u5e9c \u4e2d\u9ad8\u624b\u4e91\u96c6\uff0c\u6613\u8001\u524d\u8f88\u8fd9\u4e00\u4e0b\u53ef\u4e0d\u628a\u4ed6\u4eec\u5413\u5f97\u4e71\u6210\u4e00\u56e2\u3002\u201d\u5510\u6653\u6f9c\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u6211\u5230\u4e86\u5929 \u5c71\uff0c\u6613\u7956\u5a46\u5c06\u51af\u745b\u53eb\u51fa\u6765\u89c1\u6211\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6162\u7740\uff0c\u6613\u524d\u8f88\u79bb\u5f00\u5929\u5c71\u4e4b\u65f6\uff0c\u662f\u8c01\u7167\u6599\u90a3\u5973 \u5a03\u513f\u5462\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u662f\u6b66\u8001\u524d\u8f88\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u5929\u5c71\u4e03\u5251\u4e2d\u7684\u7684\u6b66\u743c\u7476\u8fd8\u5728 \u4eba\u95f4\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6b66\u8001\u524d\u8f88\u4e5f\u5f88\u559c\u6b22\u51af\u745b\uff0c\u51e0\u4e4e\u8981\u548c\u6613\u7956\u5a46\u4e89\u593a\u5f92\u5f1f\u5462\uff1f\u201d\u505c\u4e86\u4e00\u9635\uff0c \u53c8\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u6211\u89c1\u4e86\u51af\u745b\uff0c\u5979\u54a7\u5634\u4e00\u7b11\uff0c\u5de6\u8138\u73b0\u51fa\u9152\u6da1\uff0c\u6211\u8bb0\u8d77\u4e86\u5979\u5c31\u662f\u53cc\u9b54\u62a2\u53bb\u7684\u51af \u745b\uff0c\u6b63\u60f3\u53eb\u5979\u3002\u6613\u7956\u5a46\u5374\u5c06\u6211\u6b62\u4f4f\uff0c\u540e\u6765\u5979\u5bf9\u6211\u8bf4\uff0c\u51af\u745b\u6b63\u5728\u8ddf\u5979\u7ec3\u624e\u57fa\u7684\u529f\u592b\uff0c\u5fc3\u795e\u5b9a\u8981 \u4e13\u4e00\uff0c\u5c24\u5176\u662f\u5979\u8fd8\u53ea\u662f\u4e03\u5c81\u7684\u5c0f\u5b69\uff0c\u53ef\u4e0d\u80fd\u8ba9\u5979\u77e5\u9053\u8eab\u4e16\uff0c\u5415\u56db\u5a18\u9053\uff1a\u201c\u597d\uff0c\u2018\u6765\u9f99\u2019\u6211\u5df2 \u6e05\u695a\uff0c\u2018\u53bb\u8109\u2019\u5219\u5c1a\u672a\u660e\uff0c\u8bf7\u95ee\u8fd9\u5973\u5a03\u513f\u7684\u4e8b\u4e0e\u4f60\u73b0\u5728\u7684\u4e8b\u53c8\u6709\u4f55\u5e72\uff1f\u201d\u5510\u6653\u6f9c\u6b63\u60f3\u518d\u8bf4\u4e0b \u53bb\uff0c\u5415\u56db\u5a18\u5ffd\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u7ad9\u4e86\u8d77\u6765\uff0c\u7acb\u5728\u4e00\u5757\u5927\u5ca9\u77f3\u4e0a\u3002 \u3000\u3000\u5415\u56db\u5a18\u7acb\u5728\u5ca9\u77f3\u4e4b\u4e0a\uff0c\u5411\u4e0b\u9762\u50e7\u820d\u62db\u624b\u8bf4\u9053\uff1a\u201c\u4e00\u74e2\u5927\u5e08\uff0c\u6211\u518d\u8fc7\u4e00\u4f1a\u56de\u6765\uff0c\u4e0d\u5fc5\u7b49 \u6211\u3002\u201d\u8f6c\u8fc7\u5934\u6765\uff0c\u6458\u4e0b\u51e0\u679a\u91ce\u679c\uff0c\u9012\u7ed9\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u5403\u8fd9\u4e2a\u3002\u201d\u679c\u5473\u6e05\u751c\uff0c\u679c\u6d46\u5fae\u6709\u9152 \u5473\uff0c\u5510\u6653\u6f9c\u4e0d\u89c9\u9676\u7136\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u54b1\u4eec\u5230\u90a3\u8fb9\u53bb\u5750\uff0c\u90a3\u8fb9\u7684\u98ce\u666f\u6bd4\u8fd9\u8fb9\u597d\u5f97\u591a\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u968f\u5415\u56db\u5a18\u8d70\u4e86\u4e00\u9635\uff0c\u5ffd\u89c1\u4e00\u5904\u5c71\u6cc9\u98de\u6e85\uff0c\u6c47\u6210\u6f6d\u6c34\uff0c\u5415\u56db\u5a18\u548c\u5510\u6653\u6f9c\u89c5\u5730\u5750\u4e0b\uff0c \u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u6700\u7231\u9759\uff0c\u5c24\u5176\u7231\u5728\u8fd9\u91cc\u542c\u6cc9\u3002\u201d\u5510\u6653\u6f9c\u5fc3\u795e\u4e00\u8361\uff0c\u4f46\u89c1\u5415\u56db\u5a18\u8bf4\u5f97\u6781\u4e3a\u5766 \u7136\uff0c\u6e29\u67d4\u4e4b\u4e2d\u53c8\u6781\u7aef\u5e84\uff0c\u4ee4\u4eba\u4e0d\u6562\u903c\u89c6\uff0c\u6025\u5fd9\u5b9a\u4e0b\u795e\u6765\uff0c\u508d\u7740\u5415\u56db\u5a18\u5750\u4e86\u3002\u8bf4\u9053\uff1a\u201c\u6211\u66fe\u53d7 \u6c5d\u5dde\u51af\u6b66\u5e08\u7684\u5927\u6069\uff0c\u5341\u4e8c\u5e74\u524d\u4ed6\u5bb6\u906d\u53d7\u5927\u7978\uff0c\u8fd9\u4e8b\u4f60\u662f\u77e5\u9053\u7684\u4e86\uff0c\u5f53\u65f6\u6211\u66fe\u8bb8\u4e0b\u5fc3\u613f\uff0c\u4e00\u5b9a \u8981\u628a\u53cc\u9b54\u63b3\u53bb\u7684\u4f84\u5973\u5bfb\u56de\uff0c\u73b0\u5728\u59d0\u59d0\u5df2\u6709\u7740\u843d\uff0c\u59b9\u59b9\u5374\u8fd8\u5728\u53cc\u9b54\u624b\u4e2d\u3002\u6240\u4ee5\u6211\u5b66\u5251\u4e09\u5e74\uff0c\u4e0b \u5c71\u4e4b\u540e\uff0c\u5c31\u60f3\u53bb\u5bfb\u51af\u7433\u6d88\u606f\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5979\u5728\u56db\u7687\u5b50\u5e9c\u4e2d\uff0c\u4f60\u672c\u9886\u518d\u9ad8\uff0c\u6050\u6015\u4e5f\u96be\u5982 \u613f\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u53ef\u4e0d\u6b63\u662f\uff1f\u6240\u4ee5\u540e\u6765\u624d\u60f9\u51fa\u90a3\u4e9b\u4e8b\u6765\u3002\u8fde\u6211\u4e5f\u59cb\u6599\u4e0d\u53ca\u3002\u201d\u5415\u56db\u5a18\u7b11 \u9053\uff1a\u201c\u4f60\u5403\u4e86\u53cc\u9b54\u90a3\u4e9b\u4eba\u7684\u4e8f\u4e86\uff1f\u201d\u5979\u5fc3\u60f3\uff1a\u56db\u7687\u5e9c\u4e2d\u9ad8\u624b\u5982\u4e91\uff0c\u4f60\u4e0d\u5403\u4e8f\u624d\u602a\uff0c\u8fd9\u6709\u4ec0\u4e48 \u610f\u6599\u4e0d\u5230\u3002\u5510\u6653\u8c30\u9053\uff1a\u201c\u4e0d\u662f\u5403\u4e8f\u7684\u5c0f\u4e8b\uff0c\u8fd9\u4e8b\u771f\u5982\u505a\u68a6\u4e00\u822c\u3002\u59d0\u59d0\uff0c\u4f60\u522b\u602a\u6211\uff0c\u6211\u603b\u628a\u4f60 \u5f53\u6210\u4eb2\u4eba\uff0c\u8bf7\u4f60\u542c\u6211\u7ec6\u8bf4\u3002\u201d\u4e8e\u662f\u5728\u6d41\u6cc9\u7684\u5c71\u7011\u4e4b\u65c1\uff0c\u5510\u6653\u6f9c\u8bf4\u51fa\u4e86\u4e00\u6bb5\u79bb\u5947\u66f2\u6298\u7684\u6545\u4e8b\u3002 \u3000\u3000\u539f\u6765\u5510\u6653\u6593\u80c6\u5927\u5fc3\u7ec6\uff0c\u4e0b\u5c71\u4e4b\u540e\uff0c\u4e5f\u4e0d\u662f\u4e00\u4e0b\u5b50\u5c31\u83bd\u83bd\u649e\u649e\u53bb\u627e\u53cc\u9b54\u3002\u4ed6\u6f2b\u6e38\u6c5f\u6e56\uff0c\u5148 \u5230\u8fbd\u4e1c\u534a\u5c9b\uff0c\u627e\u5230\u4e86\u5173\u4e1c\u56db\u4fa0\u3002\u8fd9\u65f6\u4ed6\u5df2\u662f\uff1a\u5341\u4e03\u516b\u5c81\u7684\u5c11\u5e74\uff0c\u5173\u4e1c\u56db\u4fa0\u89c1\u4ed6\u957f\u5927\u6210\u4eba\uff0c\u5341 \u5206\u6b22\u559c\u3002\u7384\u98ce\u9053\u957f\u542c\u4ed6\u8981\u5230\u5317\u4eac\uff0c\u53bb\u63a2\u51af\u7433\u6d88\u606f\uff0c\u5c31\u5bf9\u4ed6\u9053\uff1a\u201c\u6211\u6709\u4e00\u4e2a\u719f\u4eba\uff0c\u540d\u53eb\u803f\u4e94\uff0c \u5728\u56db\u7687\u5b50\u5e9c\u4e2d\u5f53\u5dee\uff0c\u4f60\u5230\u4e86\u5317\u4eac\uff0c\u53ef\u4ee5\u627e\u4ed6\u3002\u201d\u5510\u6653\u6f9c\u7528\u7384\u98ce\u79d8\u4f20\u4e39\u65b9\uff0c\u53d8\u8c8c\u6613\u5bb9\uff0c\u5230\u4e86\u5317 \u65b9\uff0c\u53ef\u5de7\u90a3\u803f\u4e94\u5df2\u51fa\u5dee\u53bb\u4e86\u3002\u5510\u6653\u6f9c\u53ea\u597d\u5bc4\u5c45\u897f\u5c71\u50e7\u820d\uff0c\u957f\u65e5\u65e0\u804a\uff0c\u904d\u6e38\u4eac\u4e2d\u540d\u80dc\uff0c\u4e00\u65e5\u5ffd \u8d77\u6000\u65b9\u5e7d\u60c5\uff0c\u5fc3\u60f3\u65e2\u7136\u5230\u5317\u4eac\uff0c\u90a3\u5c45\u5eb8\u5173\u9ad8\u4eac\u53ea\u6709\u4e00\u767e\u4f59\u91cc\uff0c\u4e07\u91cc\u957f\u57ce\u5c31\u5728\u90a3\u91cc\u873f\u8712\u800c\u8fc7\uff0c \u8fd9\u5386\u53f2\u4e0a\u6700\u4f1f\u5927\u7684\u5efa\u7b51\uff0c\u53ef\u4e0d\u80fd\u9519\u8fc7\u6e38\u8e2a\u3002\u4e8e\u662f\u4e00\u65e5\u7edd\u65e9\u8d77\u6765\uff0c\u4e58\u9a6c\u8f66\u5230\u4e86\u5357\u53e3\uff0c\u5c31\u4e0b\u8f66\u5f92 \u6b65\u767b\u5c71\u3002 \u3000\u3000\u4e07\u91cc\u957f\u57ce\u4ece\u5609\u5cea\u5173\u5230\u5c71\u6d77\u5173\uff0c\u5728\u4e1b\u5c71\u5cfb\u5cad\u4e2d\u5a49\u8712\u4e00\u4e07\u4e8c\u5343\u4f59\u91cc\uff0c\u5c45\u5eb8\u5173\u8fd9\u6bb5\uff0c\u901a\u8fc7\u516b\u8fbe \u5cad\u3002\u5510\u6653\u6f9c\u722c\u4e0a\u9661\u5cfb\u7684\u5c71\u5c97\uff0c\u53ea\u89c1\u4e07\u91cc\u957f\u57ce\u5728\u7fa4\u5c71\u4e4b\u4e2d\u8d77\u4f0f\uff0c\u5c31\u50cf\u4e00\u6761\u5176\u957f\u65e0\u6bd4\u7684\u957f\u86c7\u3002\u5c45 \u5eb8\u5173\u5173\u57ce\u5140\u7acb\u5728\u5357\u53e3\u5317\u9762\uff0c\u4e24\u65c1\u9ad8\u5c71\u5939\u7740\u4e00\u6761\u72ed\u5c0f\u7684\u5c71\u6c9f\uff0c\u5c71\u5c97\u4e0a\u5c71\u82b1\u91ce\u8349\u8471\u5ba0\u90c1\u8302\uff0c\u597d\u50cf \u662f\u78a7\u6ce2\u7fe0\u6d6a\uff0c\u7ec7\u6210\u4e00\u5e45\u7f8e\u4e3d\u7684\u56fe\u6848\u3002\u8fd9\u5c31\u662f\u6709\u540d\u7684\u71d5\u4eac\u516b\u666f\u4e4b\u4e00\u2014\u2014\u5c45\u5eb8\u53e0\u7fe0\u3002\u5510\u6653\u6f9c\u6e38\u8d4f \u4e86\u4e00\u4f1a\uff0c\u518d\u7ecf\u8fc7\u201c\u516d\u90ce\u5f71\u201d\u3001\u201c\u7a46\u6842\u82f1\u70b9\u5c06\u53f0\u201d\u7b49\u540d\u80dc\uff0c\u5230\u4e86\u516b\u8fbe\u5cad\u9ad8\u5cf0\uff0c\u53ea\u89c1\u5728\u4e00\u5904\u60ac\u5d16 \u4e0a\u51ff\u4e86\u201c\u5929\u9669\u201d\u4e8c\u5b57\uff0c\u5c71\u52bf\u6781\u4e3a\u9669\u5cfb\uff0c\u4e07\u91cc\u957f\u57ce\u5728\u5c71\u9698\u5904\u722c\u8fc7\uff0c\u5510\u6653\u6f9c\u8c6a\u60c5\u52c3\u53d1\uff0c\u4e0a\u4e86\u57ce \u5899\uff0c\u62ab\u895f\u5f53\u98ce\uff0c\u987f\u89c9\u5929\u5730\u4e4b\u5927\u4e0e\u4e2a\u4eba\u4e4b\u5c0f\u3002 \u3000\u3000\u4e07\u91cc\u957f\u57ce\u5efa\u7b51\u5f97\u96c4\u4f1f\u65e0\u60c5\uff0c\u57ce\u5899\u4e0a\u53ef\u5bb9\u4e94\u9a6c\u5e76\u9a70\u3002\u5510\u6653\u6f9c\u5728\u57ce\u5899\u4e0a\u7eb5\u76ee\u8fdc\u773a\uff0c\u53ea\u89c1\u5c71\u5cf0 \u91cd\u53e0\u4e00\u671b\u65e0\u5c3d\uff0c\u5c45\u5eb8\u5173\u5140\u7acb\u5317\u65b9\uff0c\u4e07\u671b\u957f\u57ce\u6709\u5982\u4e00\u6761\u770b\u4e0d\u89c1\u9996\u5c3e\u7684\u957f\u86c7\uff0c\u5728\u7ffb\u5c71\u8d8a\u5cad\u3002\u5348\u65e5 \u5929\u6c14\u775b\u660e\uff0c\u5728\u57ce\u5899\u8fdc\u773a\uff0c\u8fd8\u9690\u7ea6\u53ef\u89c1\u4eac\u57ce\u4e2d\u7684\u5317\u6d77\u767d\u5854\u3002\u5510\u6653\u6f9c\u56db\u987e\u65e0\u4eba\uff0c\u5ffd\u7136\u5174\u8d77\uff0c\u89e3\u4e0b \u4e86\u6e38\u9f99\u5b9d\u5251\uff0c\u5c31\u5728\u57ce\u5899\u4e0a\u821e\u52a8\u8d77\u6765\uff0c\u6b63\u81ea\u5f97\u610f\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u8d5e\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff01\u201d\u5510\u6653\u6f9c\u5403\u4e86 \u4e00\u60ca\uff0c\u6536\u5251\u770b\u65f6\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u57ce\u5899\u4e0a\u5df2\u4e0a\u6765\u4e86\u4e00\u4e2a\u8001\u8005\uff0c\u5510\u6653\u6f9c\u9053\uff1a\u201c\u60ca\u52a8\u8001\u8005\uff0c\u6055\u7f6a \u6055\u7f6a\u3002\u201d\u90a3\u8001\u8005\u7b11\u772f\u772f\u7684\u8d70\u4e86\u8fc7\u6765\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u5468\u9752\u662f\u4f60\u4f55\u4eba\uff1f\u201d\u5510\u6653\u6f9c\u90a3\u91cc\u6562\u8bf4\uff0c\u53ea \u9053\uff1a\u201c\u665a\u8f88\u89c1\u95fb\u4e0d\u5e7f\uff0c\u8bf7\u793a\u8c01\u662f\u5468\u9752\u3002\u201d\u90a3\u8001\u8005\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u662f\u6000\u7591\u6211\u4e86\uff1f\u6211\u548c\u5468\u9752\u76f8\u8bc6\u7684 \u65f6\u5019\uff0c\u53ea\u6015\u4f60\u8fd8\u672a\u51fa\u4e16\u5462\u3002\u628a\u4f60\u7684\u5251\u7ed9\u6211\u4e00\u770b\u3002\u201d\u5468\u9752\u751f\u524d\u4e43\u662f\u6e05\u5bab\u6700\u5fcc\u7684\u94a6\u72af\uff0c\u5510\u6653\u6f9c\u4e0d \u77e5\u8fd9\u8001\u8005\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u5fc3\u6000\u6050\u60e7\uff0c\u90a3\u8001\u8005\u5484\u5484\u903c\u4eba\uff0c\u4e0d\u7981\u6c14\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u7ed9\u4f60\u770b\uff1f\u201d\u90a3\u8001 \u8005\u5ffd\u9053\uff1a\u201c\u542c\u8bf4\u5468\u9752\u5df2\u7ecf\u6b7b\u4e86\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u65e2\u8bf4\u662f\u4ed6\u597d\u53cb\uff0c\u5982\u4f55\u4e0d\u77e5\uff1f\u201d \u90a3\u8001\u8005\u9762\u8272\u4e00\u53d8\uff0c\u559d\u9053\uff1a\u201c\u662f\u8c01\u628a\u4ed6\u6740\u6b7b\u7684\uff1f\u4ece\u5b9e\u62db\u6765\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u662f\u5728\u5bab\u4e2d\u5f53\u5dee\u7684 \u5417\uff1f\u201d\u90a3\u8001\u8005\u9762\u8272\u4e00\u5bbd\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u65e2\u7136\u4f60\u77e5\u9053\u6211\u662f\u5bab\u4e2d\u536b\u58eb\uff0c\u90a3\u4e48\u5468\u9752\u4e00\u5b9a\u5bf9\u4f60\u8bf4\u8fc7\u7684 \u4e86\uff0c\u628a\u4f60\u7684\u5251\u62ff\u6765\uff0c\u8ba9\u6211\u770b\u770b\u662f\u4e0d\u662f\u90a3\u628a\u6e38\u9f99\u5b9d\u5251\uff1f\u201d\u5510\u6653\u6f9c\u90a3\u53e5\u95ee\u8bdd\u672c\u662f\u8bd5\u63a2\uff0c\u89c1\u4ed6\u5766\u7136 \u8ba4\u4e86\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u671d\u5ef7\u9e70\u72ac\uff0c\u8fd9\u67c4\u5251\u4f60\u6709\u672c\u4e8b\u5c31\u6765\u62ff\u53bb\uff01\u201d\u5237\u7684\u4eae\u51fa\u5251 \u950b\uff0c\u90a3\u8001\u8005\u6014\u4e86\u4e00\u6014\uff0c\u7ed9\u4ed6\u4e00\u9a82\uff0c\u4f3c\u4e4e\u5f88\u662f\u751f\u6c14\uff0c\u559d\u9053\uff1a\u201c\u771f\u662f\u5c11\u4e0d\u66f4\u4e8b\uff01\u201d\u5510\u6653\u6f9c\u62c5\u5fc3\u4ed6 \u8fd8\u6709\u540c\u515a\uff0c\u5530\u7684\u4e00\u5251\u523a\u6765\uff0c\u90a3\u8001\u8005\u4e00\u95ea\u95ea\u5f00\uff0c\u559d\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e0d\u77e5\u597d\u574f\uff01\u201d\u5510\u6653\u6f9c\u51fa\u624b\u8fc5 \u6377\uff0c\u4e0d\u80fd\u81ea\u4f11\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u5df2\u8fde\u8fdb\u4e86\u4e94\u62db\uff0c\u90a3\u8001\u8005\u771f\u6599\u4e0d\u5230\u4ed6\u7684\u5251\u6cd5\u5982\u6b64\u51cc\u5389\u7cbe\u5947\uff0c\u7adf\u5206\u4e0d \u51fa\u795e\u6765\u548c\u4ed6\u8bf4\u8bdd\uff0c\u53cc\u638c\u7ffb\u98de\uff0c\u53ea\u597d\u548c\u4ed6\u54d1\u6597\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u5510\u6653\u6f9c\u4e00\u5251\u5feb\u4f3c\u4e00\u5251\uff0c\u628a\u8001\u8005\u6740\u5f97 \u624b\u5fd9\u811a\u4e71\uff0c\u8001\u8005\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u600e\u7684\u8fd9\u5c11\u5e74\u5251\u6cd5\u8fd8\u5728\u5468\u9752\u4e4b\u4e0a\uff0c\u96be\u9053\u662f\u6211\u770b\u9519\u4e86\uff0c\u4ed6\u5e76 \u4e0d\u662f\u6211\u6240\u8981\u627e\u7684\u4eba\u3002\u201d\u8fde\u8fde\u540e\u9000\uff0c\u5510\u6653\u6f9c\u6740\u5f97\u6027\u8d77\uff0c\u6b65\u6b65\u8fdb\u903c\u3002\u90a3\u8001\u8005\u5ffd\u7136\u4e00\u58f0\u5927\u559d\uff0c\u4e5f\u5728 \u8170\u95f4\u62d4\u51fa\u4e00\u53e3\u5251\u6765\uff0c\u53ea\u542c\u5f97\u94ff\u9535\u6709\u58f0\uff0c\u8001\u8005\u7684\u5251\u5728\u6e38\u9f99\u5251\u4e0a\u4e00\u642d\uff0c\u4e00\u62db\u201c\u4e58\u9f99\u5f15\u51e4\u201d\uff0c\u5c31\u628a \u5510\u6653\u6f9c\u7684\u5b9d\u5251\u52c3\u4e86\u5f00\u53bb\u3002\u5510\u6653\u6f9c\u9aa4\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u987a\u52bf\u4e00\u63a2\uff0c\u5316\u4e86\u6765\u52bf\uff0c\u5237\uff0c\u5237\u4e24\u5251\uff0c\u671d\u654c\u4eba \u5175\u5668\u786c\u524a\uff0c\u90a3\u8001\u8005\u8eab\u5f62\u98d8\u5ffd\uff0c\u5251\u62db\u65e0\u5b9a\uff0c\u4e0d\u8ba9\u5510\u6653\u6f9c\u7684\u5b9d\u5251\u78b0\u4e0a\uff0c\u5b9e\u5728\u8eb2\u4e0d\u5f00\u65f6\uff0c\u5c31\u62ff\u634f\u65f6 \u5019\uff0c\u8ba9\u8fc7\u5251\u950b\uff0c\u7528\u4ee5\u67d4\u5236\u521a\u4e4b\u6cd5\uff0c\u5316\u4ed6\u653b\u52bf\u3002\u5510\u6653\u6f9c\u5728\u6613\u5170\u73e0\u95e8\u4e0b\u5b66\u4e86\u4e09\u5e74\uff0c\u5251\u6cd5\u5df2\u5f97\u771f \u4f20\uff0c\u65bd\u5c55\u5f00\u6765\uff0c\u771f\u5982\u95ea\u7535\u60ca\u98d8\uff0c\u72c2\u6d9b\u9a87\u6d6a\uff0c\u5e78\u5728\u90a3\u8001\u4eba\u529f\u529b\u751a\u9ad8\uff0c\u5251\u6cd5\u867d\u4e0d\u5982\u4ed6\uff0c\u5374\u5c1a\u4e0d\u81f4 \u843d\u8d25\u3002\u5510\u6653\u6f9c\u5251\u6cd5\u7cbe\u7edd\uff0c\u4f46\u5185\u529b\u8fdc\u4e0d\u5982\u4eba\uff0c\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u5f04\u5f97\u6ee1\u8eab\u5927\u6c57\uff0c\u5fc3\u91cc\u6781\u4e3a\u7126\u8e81\uff0c \u6599\u4e0d\u5230\u81ea\u5df1\u5b66\u4e86\u5929\u5c71\u5251\u6cd5\uff0c\u8fd8\u662f\u4e00\u51fa\u4fbf\u9022\u52b2\u654c\uff0c\u8fde\u4e00\u4e2a\u8001\u5934\u4e5f\u5236\u4e0d\u4f4f\uff01 \u3000\u3000\u5510\u6653\u6f9c\u4e0d\u77e5\uff0c\u82e5\u975e\u4ed6\u8ddf\u6613\u5170\u73e0\u5b66\u4e86\u8fd9\u4e09\u5e74\u5251\u6cd5\u65e9\u5c31\u8d25\u4e86\u3002\u53c8\u6597\u4e00\u4f1a\uff0c\u5510\u6653\u6f9c\u5251\u52bf\u4e1d\u6beb\u672a \u7f13\uff0c\u53ef\u662f\u5df2\u6e10\u89c9\u6c14\u4fc3\u795e\u75b2\u3002\u6b63\u6076\u6597\u95f4\uff0c\u5ffd\u542c\u5f97\u57ce\u5899\u4e0b\u6709\u4eba\u53eb\u9053\uff1a\u201c\u4faf\u8001\u7237\u5b50\uff0c\u4f60\u548c\u8c01\u5728\u4e0a\u9762 \u73a9\u554a\uff1f\u201d\u5510\u6653\u6f9c\u77e5\u662f\u8001\u8005\u540c\u515a\uff0c\u5fc3\u60f3\u4e09\u5341\u516d\u8ba1\u8d70\u4e3a\u4e0a\u8ba1\uff0c\u5237\u5237\u51e0\u5251\uff0c\u8fc5\u5982\u6012\u72ee\uff0c\u660e\u662f\u62a2\u653b\uff0c \u5b9e\u662f\u8d70\u52bf\uff0c\u8001\u8005\u906e\u6321\u4e4b\u95f4\uff0c\u5510\u6653\u6f9c\u865a\u6643\u4e00\u5251\u98de\u8eab\u4fbf\u9003\uff0c\u57ce\u811a\u90a3\u51e0\u4e2a\u4eba\u5df2\u767b\u4e0a\u57ce\u5934\uff0c\u5510\u6653\u6f9c\u767e \u5fd9\u4e2d\u56de\u5934\u4e00\u671b\uff0c\u89c1\u679c\u7136\u662f\u536b\u58eb\u7684\u88c5\u675f\uff0c\u8dd1\u5f97\u8d8a\u53d1\u8fc5\u75be\uff0c\u53ef\u602a\u7684\u662f\u90a3\u8001\u8005\u7adf\u7136\u4e0d\u8ffd\u3002\u5510\u6653\u6f9c\u5c31 \u5728\u4e07\u91cc\u957f\u57ce\u4e4b\u4e0a\uff0c\u8dd1\u4e86\u5341\u591a\u91cc\u624d\u6562\u6b47\u811a\uff0c\u5df2\u5c06\u8fd1\u5c45\u5eb8\u5173\u4e86\uff0c\u5c45\u5eb8\u5173\u4e0a\u9690\u6709\u620d\u5352\uff0c\u5510\u6653\u6f9c\u8df3\u4e0b \u57ce\u5899\uff0c\u85cf\u5728\u5c71\u4e2d\uff0c\u76f4\u5230\u9ec4\u660f\u624d\u6562\u56de\u53bb\u3002 \u3000\u3000\u5510\u6653\u6f9c\u63d0\u5fc3\u540a\u80c6\u8fc7\u4e86\u51e0\u5929\uff0c\u5e78\u5f97\u6beb\u65e0\u610f\u5916\u3002\u4e8e\u662f\u53c8\u53bb\u803f\u4e94\u5bb6\u4e2d\u63a2\u8bbf\uff0c\u539f\u6765\u803f\u4e94\u524d\u5929\u5df2\u56de \u6765\u4e86\u3002\u5510\u6653\u6f9c\u53d6\u51fa\u7384\u98ce\u9053\u957f\u7684\u4fe1\uff0c\u548c\u803f\u4e94\u5546\u91cf\u3002\u803f\u4e94\u9053\uff1a\u201c\u7687\u5e9c\u4e2d\u7528\u4eba\u751a\u591a\uff0c\u4f60\u6df7\u8fdb\u53bb\u5f53\u4e2a \u5dee\u4f7f\u5427\u3002\u4e0d\u7528\u5bf9\u603b\u7ba1\u8bf4\uff0c\u53ea\u548c\u6709\u5934\u9762\u7684\u6267\u4e8b\u8bf4\u8bf4\u4fbf\u6210\u3002\u201d\u5510\u6653\u6f9c\u5fc3\u60f3\u6df7\u8fdb\u53bb\u89d1\u4e2a\u673a\u4f1a\u628a\u51af\u7433 \u5e26\u8d70\u4e5f\u597d\uff0c\u4e8e\u662f\u5c31\u592e\u803f\u4e94\u8bf4\u60c5\uff0c\u5728\u7687\u5e9c\u5185\u5f53\u4e86\u4e00\u540d\u6253\u626b\u56ed\u5b50\u7684\u4ec6\u5f79\u3002 \u3000\u3000\u56db\u7687\u5b50\u7684\u5e9c\u90b8\u4e0d\u5728\u7d2b\u7981\u57ce\u5185\uff0c\u800c\u662f\u5728\u57ce\u5317\u738b\u5927\u4eba\u80e1\u540c\u540e\u9762\uff0c\u672c\u6765\u662f\u7687\u5ba4\u4f9b\u517b\u5587\u561b\u7684\u795e \u671d\uff0c\u73b0\u5728\u5374\u7ed9\u56db\u7687\u5b50\u5c06\u4e00\u534a\u6539\u4e3a\u4f4f\u5b85\uff0c\u540e\u6765\u5230\u56db\u7687\u5b50\u767b\u4f4d\u4e4b\u540e\uff0c\u53c8\u6539\u56de\u9ec4\u6559\u4e0a\u9662\uff0c\u5e76\u8d50\u540d\u4e3a \u201c\u96cd\u548c\u5bab\u201d\uff0c\u662f\u5317\u4eac\u57ce\u4e2d\u51fa\u540d\u7684\u795e\u79d8\u5730\u65b9\u3002\u8fd9\u5ea7\u5bab\u6bbf\u5360\u5730\u751a\u5e7f\uff0c\u6bbf\u5b87\u96c4\u4f1f\u3002\u91cc\u9762\u82b1\u56ed\u5e7f\u9614\uff0c \u4e3b\u6811\u53c2\u5929\u3002\u5510\u6653\u6f9c\u8fdb\u4e86\u7687\u5ba4\uff0c\u53ea\u89c1\u5230\u5904\u90fd\u6709\u5947\u5947\u602a\u602a\u7684\u4f5b\u50cf\uff0c\u751a\u89c9\u65b0\u5947\u3002\u5728\u91cc\u9762\u5f53\u4e86\u534a\u6708\u5dee \u4f7f\uff0c\u4e0d\u4f46\u89c1\u4e0d\u7740\u51af\u7433\uff0c\u8fde\u4e86\u56e0\u3001\u54c8\u5e03\u9640\u548c\u53cc\u9b54\u7b49\u4e5f\u89c1\u4e0d\u7740\u3002 \u3000\u3000\u4e00\u6708\u9ec4\u660f\uff0c\u5510\u6653\u6f9c\u5b8c\u4e86\u5dee\u4f7f\uff0c\u672c\u5f53\u540c\u803f\u4e94\u56de\u5bb6\uff0c\u5ffd\u7136\u542c\u5f97\u540e\u56ed\u4e2d\u6709\u5973\u5b69\u5b50\u7684\u7b11\u58f0\uff0c\u5077\u5077 \u8fdb\u53bb\u4e00\u770b\uff0c\u53ea\u89c1\u51e0\u4e2a\u5b69\u5b50\u5728\u56ed\u5b50\u91cc\u6349\u8ff7\u85cf\uff1b\u5176\u4e2d\u4e00\u4e2a\u5973\u5b69\uff0c\u7ea6\u6709\u5341\u5c81\uff0c\u8eab\u5b50\u975e\u5e38\u8f7b\u76c8\uff0c\u5b69\u5b50 \u4eec\u603b\u6349\u5979\u4e0d\u7740\uff0c\u5510\u6653\u6f9c\u770b\u4e86\u4e00\u9635\uff0c\u90a3\u5973\u5b69\u5b50\u73a9\u5f97\u9ad8\u5174\uff0c\u683c\u683c\u7684\u7b11\u4e2a\u4e0d\u4f11\uff0c\u53f3\u9762\u8138\u4e0a\u73b0\u51fa\u9152 \u6da1\uff0c\u5341\u5206\u53ef\u7231\uff0c\u5510\u6653\u6f9c\u7b49\u4e86\u4e00\u4f1a\uff0c\u90a3\u4e9b\u5973\u5b69\u5b50\u9053\uff1a\u201c\u6349\u4f60\u4e0d\u7740\uff0c\u4e0d\u548c\u4f60\u73a9\u4e86\u3002\u201d\u5929\u8272\u6e10\u9ed1\uff0c \u5404\u81ea\u6563\u53bb\u3002\u5510\u6653\u6f9c\u6084\u6084\u7684\u884c\u8fd1\u5979\u7684\u8eab\u8fb9\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u51af\u7433\uff0c\u4f60\u8fd8\u8bb0\u5f97\u949f\u4f2f\u4f2f\u5417\uff1f\u201d\u90a3\u5973\u5b69 \u5b50\u773c\u73e0\u4e00\u6e9c\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u8c01\u662f\u51af\u7433\uff1f\u8c01\u662f\u949f\u4f2f\u4f2f\uff0c\u6211\u6709\u8bb8\u591a\u4f2f\u4f2f\u5462\uff0c\u53ef\u5c31\u6ca1\u6709\u59d3\u949f \u7684\uff0c\u949f\u4f2f\u4f2f\u662f\u65b0\u6765\u7684\u5417\uff1f\u201d\u5510\u6653\u6f9c\u5fc3\u4e2d\u4f24\u75db\uff0c\u60f3\u9053\uff1a\u201c\u949f\u4e07\u5802\u6b7b\u65f6\uff0c\u8fd9\u5b69\u5b50\u4e5f\u5df2\u6709\u4e86\u4e03\u5c81\uff0c \u5982\u4f55\u5c31\u8fd9\u4e2a\u6837\u5b50\uff0c\u4ec0\u4e48\u4e5f\u8bb0\u4e0d\u8d77\u6765\u4e86\u3002\u56e0\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u5bb3\u4f2f\uff0c\u6211\u548c\u4f60\u7684\u7238\u7238\u5988\u5988\u662f\u597d\u670b \u53cb\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u6709\u8bb8\u591a\u4e49\u7236\u5462\uff0c\u4f60\u548c\u54ea\u4e00\u4e2a\u662f\u597d\u670b\u53cb\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u548c\u4f60\u4eb2\u751f\u7684\u7238 \u5988\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4eb2\u751f\u7684\u7238\u5988\uff1f\u4f60\u8bf4\u8c0e\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u7684\u7238\u7238\u7ed9\u574f\u4eba\u5bb3\u6b7b\u4e86\uff0c\u4f60 \u7684\u5988\u5988\u8fd8\u6ca1\u6709\u6b7b\u3002\u4f60\u5c0f\u65f6\u5019\u6211\u62b1\u8fc7\u4f60\u5462\uff0c\u6211\u5e26\u4f60\u51fa\u53bb\u5b66\u672c\u4e8b\uff0c\u627e\u4f60\u7684\u5988\u5988\u3002\u201d\u51af\u7433\u9122\u7136\u4e00 \u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5e26\u6211\u51fa\u53bb\uff1f\u5b66\u672c\u4e8b\uff1f\u54fc\uff0c\u4f60\u5c31\u662f\u574f\u4eba\uff01\u201d\u5ffd\u7136\u5c0f\u624b\u4e00\u626c\uff0c\u51e0\u67c4\u98de\u5200\uff0c\u6fc0\u65f6\u800c\u6765\uff01 \u3000\u3000\u5510\u6653\u6f9c\u8eab\u56de\u52bf\u8f6c\uff0c\u4e00\u53e3\u98de\u5200\u8d34\u7740\u808b\u65c1\uff0c\u500f\u7136\u7a7f\u8fc7\uff0c\u5510\u6653\u6f9c\u6025\u8f6c\u8eab\u5f62\u6a2a\u4f38\u4e8c\u6307\uff0c\u5c06\u7b2c\u4e8c\u53e3 \u98de\u5200\u5939\u7740\uff0c\u5f80\u5916\u4e00\u7529\uff0c\u5c06\u7b2c\u4e09\u53e3\u98de\u5200\u4e5f\u6253\u843d\u4e86\uff0c\u51af\u7433\u62cd\u624b\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u518d\u8bd5\u8bd5\uff0c\u4f60\u82e5\u8fd8 \u95ea\u907f\u5f97\u4e86\uff0c\u6211\u5c31\u53eb\u4f60\u4f2f\u4f2f\uff0c\u5b66\u4f60\u7684\u672c\u4e8b\uff01\u201d\u5c0f\u624b\u4e00\u626c\uff0c\u516d\u53e3\u98de\u5200\uff0c\u7adf\u5206\u4e09\u7ec4\u6253\u6765\uff0c\u6bcf\u7ec4\u4e24 \u67c4\uff0c\u5e73\u6392\u800c\u51fa\uff0c\u5230\u5f97\u4e2d\u9014\uff0c\u5374\u5ffd\u7136\u5de6\u53f3\u5206\u5f00\uff0c\u8fd9\u662f\u949f\u4e07\u5802\u7684\u98de\u5200\u7edd\u6280\uff0c\u51af\u7433\u5df2\u5f97\u4ed6\u7684\u8863\u94b5\u771f \u4f20\uff0c\u5510\u6653\u6f9c\u672c\u5f85\u7a7a\u624b\u63a5\u5200\uff0c\u5374\u65e0\u6cd5\u7167\u987e\u56db\u65b9\uff0c\u95ea\u5f97\u4e24\u95ea\uff0c\u54e9\u54e9\u4e24\u58f0\uff0c\u4e24\u53e3\u98de\u5200\u7adf\u8d34\u7740\u8033\u8fb9\u98de \u8fc7\u3002\u51af\u7433\u5c0f\u624b\u8fde\u626c\uff0c\u98de\u5200\u63a5\u7eed\u800c\u81f3\uff0c\u8d8a\u6765\u8d8a\u6025\uff0c\u5510\u6653\u6f9c\u8feb\u4e8e\u65e0\u5948\uff0c\u95ea\u907f\u4e4b\u9645\uff0c\u6e38\u9f99\u5b9d\u5251\uff0c\u5237 \u7684\u51fa\u9798\uff0c\u4e00\u8361\u4e00\u626b\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u65ad\u91d1\u5207\u7389\u4e4b\u58f0\uff0c\u987f\u65f6\u95f4\uff0c\u6d12\u4e0b\u4e86\u6ee1\u7a7a\u5200\u96e8\uff0c\u51af\u7433\u5c04\u6765\u7684\u98de \u5200\uff0c\u5168\u88ab\u6e38\u9f99\u5251\u524a\u65ad\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u4e24\u58f0\u602a\u5578\uff0c\u6709\u4eba\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u5728\u6b64\u80e1\u95f9\uff1f\u201d\u51af\u7433 \u53eb\u9053\uff1a\u201c\u8428\u4f2f\u4f2f\uff0c\u8fd9\u4eba\u53eb\u6211\u8ddf\u4ed6\u8d70\u5462\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u8eab\u5f62\u6025\u8d77\uff0c\u6b63\u60f3\u6251\u4e0a\u5899\u5934\uff0c\u5899\u5934\u4e0a\u500f\u7136\u98de\u4e0b\u4e00\u6761\u9ed1\u5f71\uff0c\u5b9b\u5982\u5de8\u9e1f\u6469\u4e91\uff0c\u5f53\u5934\u6251 \u81f3\uff0c\u5341\u722a\u5982\u94a9\uff0c\u51cc\u7a7a\u51fb\u4e0b\uff0c\u5510\u6653\u6f9c\u5251\u5149\u4e00\u95ea\uff0c\u865a\u6643\u4e00\u62db\uff0c\u8eab\u5f62\u5012\u8f6c\uff0c\u5411\u51af\u7433\u7acb\u8db3\u4e4b\u5904\u6251\u6765\uff0c \u5ffd\u542c\u5f97\u53c8\u662f\u4e00\u58f0\u5927\u559d\uff0c\u5927\u529b\u795e\u9b54\u8428\u5929\u90fd\u53cc\u81c2\u5982\u693d\uff0c\u7adf\u7136\u62e6\u7740\u4e86\u53bb\u8def\u3002\u51af\u7433\u5c31\u8eb2\u5728\u8428\u5929\u90fd\u8eab \u540e\uff0c\u624b\u4e2d\u6390\u7740\u98de\u5200\uff0c\u7b11\u563b\u563b\u7684\u5728\u770b\u70ed\u95f9\u3002 \u3000\u3000\u4ece\u5899\u5934\u4e0a\u98de\u4e0b\u90a3\u4eba\u6b63\u662f\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\uff0c\u5144\u5f1f\u4e8c\u4eba\u628a\u5510\u6653\u6f9c\u5939\u5728\u5f53\u4e2d\u3002\u8428\u5929\u523a\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u5510\u6653\u8c30\uff0c\u539f\u6765\u662f\u4f60\uff01\u4f60\u60f3\u6765\u62dc\u5e08\u5417\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4ed6\u53eb\u6211\u8ddf\u4ed6\u8d70\uff01\u4ed6\u8fd8\u8bf4\u6709\u672c \u4e8b\u6559\u6211\u5462\uff01\u201d\u8428\u5929\u90fd\u559d\u9053\uff1a\u201c\u597d\uff0c\u770b\u4f60\u5b66\u4ec0\u4e48\u9ad8\u62db\uff1f\u201d\u53cc\u638c\u4e00\u8d77\uff0c\u76f4\u62a2\u8fc7\u6765\uff0c\u5de6\u638c\u659c\u5288\u80f8 \u819b\uff0c\u53f3\u638c\u786c\u64d2\u624b\u8155\uff0c\u5510\u6653\u6f9c\u5251\u5149\u4e00\u95ea\uff0c\u75be\u53d1\u5982\u98ce\uff0c\u8428\u5929\u90fd\u6599\u4e0d\u5230\u4ed6\u5251\u6cd5\u7adf\u7136\u5982\u6b64\u5feb\u6377\uff0c\u4e00\u638c \u5288\u7a7a\uff0c\u6025\u5fd9\u659c\u95ea\u3002\u5510\u6653\u6f9c\u5251\u5c16\u4e00\u98a4\uff0c\u628a\u4ed6\u7684\u80a9\u5934\u753b\u4e86\u4e00\u9053\u53e3\u5b50\uff0c\u8428\u5929\u523a\u6025\u5fd9\u65bd\u5c55\u732b\u9e70\u6251\u51fb\u4e4b \u6280\uff0c\u5e73\u7a7a\u63a0\u8d77\uff0c\u518d\u5ea6\u4e0b\u6251\uff0c\u5510\u6653\u6f9c\u6362\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u628a\u5b9d\u5251\u821e\u6210\u4e00\u5708\u94f6\u8679\uff0c\u62a4\u4e86\u5934\u9876\uff0c\u8428\u5929\u523a \u5728\u534a\u7a7a\u4e00\u8f6c\uff0c\u56de\u7fd4\u4e09\u6251\uff0c\u65e0\u9699\u53ef\u4e58\uff0c\u53ea\u5f97\u843d\u5730\u3002\u8428\u5929\u90fd\u6012\u543c\u5982\u96f7\uff0c\u6325\u62f3\u518d\u4e0a\u3002\u5510\u6653\u6f9c\u5237\u5237\u4e24 \u5251\uff0c\u5206\u88ad\u53cc\u9b54\uff0c\u8428\u5929\u523a\u559d\u9053\uff1a\u201c\u597d\u5251\u6cd5\u3002\u201d\u53cd\u81c2\u4e00\u6293\uff0c\u5341\u6307\u957f\u7532\uff0c\u53c8\u5411\u624b\u8155\u6293\u6765\uff0c\u5510\u6653\u6f9c\u4e00 \u62db\u201c\u79cb\u6c34\u6a2a\u821f\u201d\u6a2a\u5207\u8fc7\u53bb\uff0c\u90a3\u8428\u5929\u523a\u98d8\u5ffd\u5982\u98ce\uff0c\u4e00\u8f6c\u53c8\u6293\u5de6\u80c1\uff0c\u8428\u5929\u90fd\u53cc\u638c\u631f\u98ce\uff0c\u4e5f\u8fce\u9762\u5288 \u5230\u3002\u5510\u6653\u6f9c\u5b9d\u5251\u4e00\u6296\uff0c\u6025\u628a\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u5bd2\u6d9b\u5251\u6cd5\u65bd\u5c55\u51fa\u6765\u3001\u53ea\u89c1\u51b7\u7535\u7cbe\u8292\uff0c\u5251\u82b1\u5982\u6d6a\uff0c\u5343 \u70b9\u4e07\u70b9\uff0c\u76f4\u6d12\u4e0b\u6765\uff0c\u8428\u5929\u523a\u5728\u5251\u5149\u4e2d\u7a7f\u6765\u63d2\u53bb\uff0c\u548c\u5510\u6653\u6f9c\u4e92\u76f8\u8ffd\u9010\uff0c\u5510\u6653\u6f9c\u9003\u4e0d\u51fa\u53bb\uff0c\u8428\u5929 \u523a\u4e5f\u6251\u4e0d\u8fd1\u8eab\u3002\u8428\u5929\u90fd\u5219\u5728\u5916\u56f4\u53d1\u638c\uff0c\u638c\u638c\u6709\u52b2\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u638c\u98ce\u6fc0\u8361\uff0c\u6bcf\u6bcf\u628a\u5510\u6653\u6f9c\u7684\u5251 \u70b9\u9707\u6b6a\uff0c\u8ba9\u54e5\u54e5\u4e58\u9699\u8fdb\u51fb\uff0c\u5510\u6653\u6f9c\u5251\u62db\u867d\u7136\u795e\u5999\uff0c\u529f\u529b\u5374\u662f\u4e0d\u6df1\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u4fbf\u96be\u652f\u6301\uff0c\u8428 \u5929\u523a\u5f20\u7259\u821e\u722a\uff0c\u8d8a\u53d1\u51cc\u5389\uff01 \u3000\u3000\u51af\u7433\u5728\u65c1\u62cd\u624b\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u4e0d\u8bc6\u7f9e\uff0c\u4f60\u7684\u672c\u4e8b\u8ddf\u6211\u4e5f\u5dee\u4e0d\u591a\uff0c\u5374\u60f3\u505a\u6211\u7684\u5e08\u5085\u3002\u201d\u5510\u6653 \u6f9c\u5927\u58f0\u559d\u9053\uff1a\u201c\u51af\u7433\uff0c\u8fd9\u4e24\u4eba\u4e43\u662f\u4f60\u7684\u4ec7\u4eba\uff0c\u4ed6\u672c\u4e8b\u518d\u597d\uff0c\u4f60\u4e5f\u4e0d\u80fd\u8ba4\u8d3c\u4f5c\u7236\uff01\u201d\u5510\u6653\u6f9c\u8eab \u9677\u91cd\u56f4\uff0c\u81ea\u77e5\u96be\u9003\uff0c\u56e0\u6b64\u4e0d\u7ba1\u51af\u7433\u662f\u5426\u61c2\u4e8b\uff0c\u4e5f\u8981\u63d0\u9192\u4e8e\u5979\u3002\u8428\u65e0\u523a\u9762\u8272\u4e00\u53d8\uff0c\u53eb\u9053\uff1a\u201c\u71d5 \u513f\uff0c\u522b\u542c\u4ed6\u7684\u8bf4\u8bdd\u3002\u201d\u5341\u722a\u6293\u3001\u70b9\u3001\u52fe\uff0c\u6495\uff0c\u6251\u5f97\u66f4\u51f6\uff0c\u5510\u6653\u6f9c\u51b2\u4e86\u51e0\u6b21\uff0c\u90fd\u88ab\u4ed6\u5f3a\u622a\u56de \u6765\uff0c\u9669\u8c61\u73af\u751f\uff0c\u770b\u770b\u5c31\u5c06\u4e0d\u514d\u3002\u8428\u5929\u523a\u559d\u9053\uff1a\u201c\u8fd8\u4e0d\u5f03\u5251\u6295\u964d\uff01\u201d\u5510\u6653\u6f9c\u54ac\u5b9e\u7259\u6839\uff0c\u6325\u5251\u529b \u6218\u3002\u8fd9\u65f6\u7687\u5e9c\u91cc\u7684\u4eba\u90fd\u88ab\u60ca\u8d77\uff0c\u7eb7\u7eb7\u62e5\u5230\u540e\u56ed\uff0c\u770b\u64d2\u523a\u5ba2\u3002\u803f\u4e94\u5728\u4eba\u5806\u4e2d\u6697\u6697\u53eb\u82e6\u3002\u6b63\u5728\u60f3 \u9003\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u559d\u9053\u58f0\u58f0\uff0c\u56f4\u89c2\u7684\u4eba\u4e24\u8fb9\u8d70\u907f\uff0c\u6709\u4e00\u7fa4\u4eba\u8d70\u4e86\u8fdb\u6765\u3002\u4e3a\u9996\u7684\u4eba\u559d\u9053\uff1a\u201c\u4f60\u4eec \u7684\u54c8\u603b\u7ba1\u5462\uff1f\u53eb\u5141\u796f\u51fa\u6765\u89c1\u6211\u3002\u201d\u8428\u5929\u523a\u500f\u7684\u8df3\u51fa\u5708\u5b50\uff0c\u5510\u6653\u6f9c\u83ab\u540d\u5176\u5999\uff0c\u5f85\u8981\u9003\u65f6\uff0c\u56db\u56f4 \u90fd\u662f\u4eba\u5899\uff0c\u54ea\u91cc\u8fd8\u9003\u5f97\u51fa\uff1f \u3000\u3000\u8fd9\u4e3a\u9996\u7684\u4eba\uff0c\u7adf\u662f\u5eb7\u7199\u7687\u5e1d\u7684\u957f\u5b50\u3001\u76f4\u90e1\u738b\u5141\u5824\u3002\u5eb7\u7199\u867d\u7136\u672a\u7acb\u50a8\u541b\uff0c\u4f46\u5728\u4e00\u7fa4\u7687\u5b50\u4e4b \u4e2d\uff0c\u957f\u5b50\u6743\u5a01\uff0c\u7ec8\u7a76\u8f83\u5927\u3002\u8fd9\u65f6\u8bf8\u7687\u5b50\u4e89\u4f4d\u751a\u70c8\uff0c\u5c24\u5176\u4ee5\u56db\u7687\u5b50\u5141\u796f\u3001\u516b\u7687\u5b50\u5141\u50ac\u3001\u5341\u7687\u5b50 \u5927\u997f\u3001\u5341\u56db\u7687\u5b50\u5141\u5824\u7b49\u8fd0\u52a8\u6700\u529b\u3002\u5141\u5824\u662f\u957f\u5b50\u8eab\u4efd\uff0c\u5f53\u7136\u4e0d\u80af\u653e\u8fc7\u7687\u4f4d\uff0c\u6240\u4ee5\u4e5f\u52fe\u7ed3\u4e00\u73ed\u5ef7 \u81e3\uff0c\u517b\u4e86\u4e00\u73ed\u6b7b\u58eb\u3002\u56db\u7687\u5b50\u65f6\u65f6\u5fae\u670d\u51fa\u4eac\uff0c\u5510\u6653\u6f9c\u5165\u4eac\u4e4b\u65e5\uff0c\u6b63\u662f\u56db\u7687\u5b50\u5916\u51fa\u4e4b\u65f6\uff0c\u6240\u4ee5\u54c8 \u5e03\u9640\u3001\u4e86\u56e0\u548c\u5929\u53f6\u6563\u4eba\u7b49\u4e00\u73ed\u9ad8\u624b\uff0c\u90fd\u88ab\u4ed6\u5e26\u51fa\u4eac\u4e86\u3002\u56db\u7687\u5b50\u79c1\u81ea\u51fa\u4eac\uff0c\u672c\u662f\u4ef6\u7edd\u79d8\u4e4b\u4e8b\uff0c \u4e0d\u6599\u5374\u7ed9\u5141\u5824\u4fa6\u77e5\u3002 \u3000\u3000\u7687\u5b50\u79c1\u81ea\u51fa\u4eac\uff0c\u4e43\u662f\u72af\u6cd5\u4e4b\u4e8b\u3002\u56db\u7687\u5b50\u5141\u796f\u6bcf\u6b21\u51fa\u4eac\uff0c\u90fd\u5411\u7236\u7687\u79f0\u75c5\u3002\u597d\u5728\u5eb7\u7199\u5b50\u5973\u4f17 \u591a\uff0c\u800c\u4e14\u5bf9\u56db\u7687\u5b50\u618e\u6076\uff0c\u5f88\u5c11\u53ec\u4ed6\u8fdb\u89c1\uff0c\u5c31\u662f\u5076\u800c\u8981\u53ec\u4ed6\u65f6\uff0c\u542c\u4ed6\u6709\u75c5\uff0c\u4e5f\u5c31\u7f62\u4e86\u3002\u6240\u4ee5\u56db \u7687\u5b50\u80c6\u5b50\u8d8a\u6765\u8d8a\u5927\uff0c\u5728\u5916\u7684\u65f6\u65e5\u6bd4\u5728\u4eac\u7684\u65f6\u65e5\u8fd8\u591a\u3002\u5927\u63d0\u8fd9\u6b21\u67e5\u77e5\u5141\u796f\u79c1\u81ea\u51fa\u4eac\uff0c\u5fc3\u751f\u4e00 \u8ba1\uff0c\u7adf\u7136\u4eb2\u81ea\u524d\u6765\u62dc\u8bbf\u3002\u5165\u4e86\u7687\u5e9c\uff0c\u542c\u5f97\u53ae\u6740\u4e4b\u58f0\uff0c\u5927\u4e3a\u5947\u5f02\uff0c\u6240\u4ee5\u8fdb\u4e86\u540e\u56ed\u3002 \u3000\u3000\u8428\u5929\u523a\u4e00\u89c1\uff0c\u614c\u4e86\u624b\u811a\uff0c\u6025\u5fd9\u7b54\u9053\uff1a\u201c\u56db\u6bbf\u4e0b\u5367\u75c5\u672a\u8d77\uff0c\u4e0d\u80fd\u4eb2\u63a5\u6bbf\u4e0b\u3002\u201d\u5141\u5824\u9053\uff1a \u201c\u6211\u53bb\u770b\u4ed6\u7684\u75c5\u3002\u201d\u559d\u4ee4\u8428\u5929\u523a\u5e26\u8def\uff0c\u8428\u5929\u523a\u9053\uff1a\u201c\u5f85\u6211\u5148\u64d2\u8fd9\u4e2a\u523a\u5ba2\u3002\u201d\u5141\u5824\u5ffd\u7136\u56de\u987e\u8eab \u8fb9\u4e00\u4e2a\u536b\u58eb\uff0c\u8bf4\u9053\uff1a\u201c\u4faf\u4e09\u53d8\uff0c\u4f60\u628a\u90a3\u4e2a\u523a\u5ba2\u64d2\u6765\u3002\u201d\u4ecd\u5bf9\u8428\u5929\u523a\u9053\uff1a\u201c\u4e0d\u5fc5\u4f60\u8d39\u529b\uff0c\u4f60\u5f15 \u6211\u89c1\u5141\u5e27\u3002\u201d \u3000\u3000\u5141\u5824\u8eab\u8fb9\u7684\u536b\u58eb\uff0c\u5e94\u4e86\u58f0\u8bfa\uff0c\u55d6\u5730\u7a9c\u51fa\uff0c\u5510\u6653\u6f9c\u62ac\u5934\u4e00\u770b\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u536b\u58eb\u4e0d\u662f\u522b \u4eba\uff0c\u6b63\u662f\u5728\u4e07\u91cc\u957f\u57ce\u4e0a\u4e0e\u4ed6\u76f8\u6597\u7684\u90a3\u4e2a\u8001\u8005\u3002\u8fd9\u65f6\u5510\u6653\u6f9c\u6597\u5fd7\u5df2\u5931\uff0c\u5fc3\u60f3\uff1a\u53cd\u6b63\u9003\u4e0d\u51fa\u53bb\uff0c \u843d\u5728\u4ed6\u7684\u624b\u4e2d\uff0c\u603b\u80dc\u4e8e\u843d\u5728\u53cc\u9b54\u624b\u4e2d\uff0c\u865a\u6597\u51e0\u62db\uff0c\u4faf\u4e09\u53d8\u65bd\u5c55\u5927\u64d2\u62ff\u624b\u4e00\u628a\u5c06\u4ed6\u6293\u8d77\u3002 \u3000\u3000\u5141\u5824\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5148\u62bc\u523a\u5ba2\u56de\u53bb\u3002\u201d\u539f\u6765\u5141\u5824\u6000\u7591\u8fd9\u4e2a\u523a\u5ba2\u4e0e\u5144\u5f1f\u95f4\u7684\u593a\u4f4d\u6709\u5173\uff0c\u6240\u4ee5 \u4e0d\u5c06\u4ed6\u4ea4\u56de\u5141\u796f\u5e9c\u4e2d\u4eba\uff0c\u5374\u8981\u5019\u4e09\u53d8\u628a\u4ed6\u62bc\u56de\uff0c\u51c6\u5907\u4eb2\u81ea\u5ba1\u95ee\u3002 \u3000\u3000\u5141\u5824\u518d\u559d\u8428\u5929\u523a\u5e26\u8def\uff0c\u8428\u5929\u523a\u9053\uff1a\u201c\u6211\u53ea\u662f\u4e00\u4e2a\u536b\u58eb\uff0c\u4e0d\u6562\u79c1\u8fdb\u5185\u5e9c\u3002\u201d\u5141\u5824\u52c3\u7136\u53d8\u8272\u3002 \u3000\u3000\u6b63\u5f53\u6b64\u9645\uff0c\u4e00\u4e2a\u5973\u5b69\u5b50\u5ffd\u7136\u5728\u8428\u5929\u90fd\u80cc\u540e\u95ea\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u6211\u5e26\u4f60\u53bb\u3002\u201d \u3000\u3000\u5141\u796f\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u6211\u662f\u670d\u4f8d\u56db\u6bbf\u4e0b\u7684\u5a62\u5973\u3002\u201d \u3000\u3000\u5141\u796f\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8fd9\u4e2a\u5973\u5a03\u5b50\u5012\u5f88\u4e56\uff0c\u8fdb\u53bb\u5427\u3002\u201d \u3000\u3000\u51af\u7433\u5411\u8428\u5929\u523a\u7728\u7728\u773c\u775b\uff0c\u5e26\u5141\u796f\u76f4\u8fdb\u5185\u5e9c\u3002 \u3000\u3000\u4faf\u4e09\u53d8\u5c06\u5510\u6653\u6f9c\u64d2\u4e86\uff0c\u75be\u5fd9\u51fa\u5916\uff0c\u548c\u4e24\u4e2a\u968f\u6765\u7684\u592a\u76d1\uff0c\u5957\u8d77\u9a6c\u8f66\uff0c\u76f4\u5165\u7981\u57ce\u3002\u4ed6\u7684\u4e3b\u4eba \u5141\u5824\u4ee5\u957f\u5b50\u8eab\u4efd\uff0c\u4f4f\u5728\u5bab\u4e2d\u3002\u9a6c\u8f66\u8d70\u4e86\u4e00\u4f1a\uff0c\u4ece\u540e\u9762\u7684\u795e\u6b66\u95e8\u5165\u5bab\u3002\u4e0b\u4e86\u9a6c\u8f66\uff0c\u4e24\u4e2a\u592a\u76d1\u8d70 \u5728\u524d\u9762\uff0c\u4faf\u4e09\u53d8\u631f\u7740\u5510\u6653\u6f9c\u8d70\u5728\u540e\u9762\uff0c\u53c8\u8d70\u4e86\u597d\u4e45\uff0c\u5929\u5df2\u5165\u9ed1\uff0c\u5404\u5bab\u90fd\u5df2\u5173\u4e86\u56ed\u5b50\uff0c\u53ea\u6709\u4e9b \u5f53\u503c\u592a\u76d1\u5de1\u6765\u5de1\u53bb\u3002\u5141\u5824\u4f4f\u5728\u4e1c\u8fb9\u7684\u627f\u4e7e\u5bab\uff0c\u897f\u8fb9\u4e09\u5341\u516d\u6240\u5bab\u6bbf\u662f\u7687\u5e1d\u540e\u5983\u6240\u4f4f\uff0c\u5404\u7687\u5b50\u7684 \u5bb6\u4eba\u536b\u58eb\u82e5\u975e\u5949\u53ec\u4e5f\u4e0d\u80fd\u8fdb\u53bb\u3002\u8d70\u4e86\u4e00\u4f1a\uff0c\u7ecf\u8fc7\u4e86\u4e1c\u897f\u4e8c\u5bab\u7684\u4ea4\u754c\u4e4b\u5904\uff0c\u4faf\u4e09\u53d8\u5ffd\u7136\u5728\u5510\u6653 \u6f9c\u8033\u8fb9\u6084\u6084\u8bf4\u9053\uff1a\u201c\u4f60\u8d76\u5feb\u9003\u8fdb\u897f\u5bab\uff0c\u8de8\u8fc7\u62a4\u5899\uff0c\u5728\u82b1\u56ed\u91cc\u5411\u5317\u8d70\u4e8c\u767e\u6b65\uff0c\u6709\u4e00\u6240\u5047\u5c71\uff0c\u7ed5 \u8fc7\u5047\u5c71\uff0c\u6709\u4e00\u4e2a\u8377\u5858\uff0c\u5728\u8377\u5858\u5de6\u8fb9\u4e94\u5341\u6b65\uff0c\u6709\u4e00\u95f4\u9ed1\u77f3\u5c4b\u5b50\uff0c\u4f60\u63a8\u95e8\u8fdb\u53bb\uff0c\u89c1\u4e86\u4eba\u4e0d\u8981\u60ca \u6015\uff0c\u4fdd\u4f60\u8131\u9669\uff01\u201d\u8bf4\u4e86\u4e4b\u540e\uff0c\u7a81\u7136\u628a\u624b\u4e00\u677e\uff0c\u53eb\u9053\uff1a\u201c\u54ce\u963f\uff0c\u597d\u5389\u5bb3\u7684\u523a\u5ba2\uff01\u201d\u62d4\u51fa\u4f69\u5251\uff0c \u95ea\u7535\u822c\u5728\u81ea\u5df1\u817f\u4e0a\u523a\u4e86\u4e00\u5251\uff0c\u4e24\u4e2a\u592a\u76d1\u56de\u8fc7\u5934\u6765\uff0c\u5510\u6653\u6f9c\u5df2\u98de\u8eab\u63a0\u8fc7\u5899\u5934\uff0c\u8dd1\u5230\u7687\u5bab\u7684\u897f\u9662 \u53bb\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u8111\u5b50\u91cc\u4e71\u54c4\u54c4\u7684\uff0c\u4e0d\u77e5\u9053\u4faf\u4e09\u53d8\u4f55\u4ee5\u8981\u653e\u81ea\u5df1\uff0c\u4f46\u8eab\u9677\u7687\u5bab\uff0c\u53ea\u597d\u7167\u4ed6\u7684\u6307\u5f15\u4e00 \u8bd5\u3002\u7687\u5bab\u6781\u5927\uff0c\u5510\u6653\u6f9c\u8de8\u8fc7\u62a4\u5899\uff0c\u8eb2\u8eb2\u95ea\u95ea\uff0c\u95ef\u5165\u82b1\u56ed\uff0c\u5411\u5317\u8d70\u4e8c\u767e\u6b65\uff0c\u679c\u7136\u89c1\u6709\u4e00\u5ea7\u5047 \u5c71\uff0c\u7ed5\u8fc7\u5047\u5c71\uff0c\u679c\u7136\u6709\u4e00\u4e2a\u8377\u5858\uff0c\u5510\u6653\u6f9c\u5411\u5de6\u8fb9\u884c\u4e86\u4e94\u5341\u6b65\uff0c\u627e\u5230\u90a3\u6240\u9ed1\u77f3\u5c4b\u5b50\u3002\u65c1\u7684\u5bab\u6bbf \u90fd\u662f\u91d1\u78a7\u8f89\u714c\uff0c\u53ea\u6709\u8fd9\u6240\u9ed1\u77f3\u5c4b\u5b50\u5efa\u7b51\u7b80\u964b\uff0c\u8d70\u5230\u95e8\u524d\uff0c\u53ea\u89c9\u51b7\u610f\u68ee\u68ee\uff0c\u5c4b\u6a90\u7ed3\u6709\u86db\u7f51\uff0c\u60f3 \u662f\u5e73\u65f6\u4eba\u8ff9\u5c11\u5230\uff0c\u5510\u6653\u6f9c\u63a8\u95e8\u8fdb\u53bb\uff0c\u8d70\u4e0a\u53f0\u9636\uff0c\u5ffd\u542c\u5f97\u53a2\u623f\u5185\u6709\u5fae\u5f31\u7684\u58f0\u97f3\uff0c\u95ee\u9053\uff1a\u201c\u662f \u8c01\uff1f\u201d\u5510\u6653\u6f9c\u4e00\u628a\u63a8\u5f00\u623f\u95e8\uff0c\u5ffd\u89c1\u91cc\u9762\u5750\u7740\u4e00\u4e2a\u5934\u53d1\u6591\u767d\u7684\u5973\u4eba\uff0c\u5f62\u5bb9\u67af\u69c1\uff0c\u6b63\u5728\u624b\u5f04\u7476 \u7434\u3002\u89c1\u5510\u6653\u6f9c\u8fdb\u6765\uff0c\u7741\u5927\u4e86\u773c\u3002 \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u662f\u4faf\u4e09\u53d8\u53eb\u6211\u6765\u7684\uff01\u201d\u90a3\u5973\u4eba\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u773c\u775b\u5ffd\u7136\u660e\u4eae\u8d77\u6765\uff0c\u76ef\u7740\u6653 \u6f9c\uff0c\u9762\u4e0a\u808c\u8089\u62bd\u6410\uff0c\u4e24\u6ef4\u773c\u6cea\uff0c\u5ffd\u7136\u843d\u4e86\u4e0b\u6765\uff0c\u5510\u6653\u6f9c\u5ffd\u89c9\u8fd9\u5973\u4eba\u4f3c\u4e4e\u5728\u90a3\u91cc\u89c1\u8fc7\u4f3c\u7684\u3002\u5fc3 \u5934\u611f\u5230\u4e00\u9635\u9635\u5bd2\u610f\u3002 \u3000\u3000\u90a3\u5973\u4eba\u770b\u4e86\u597d\u4e45\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u5750\u4e0b\u6765\u3002\u201d\u58f0\u97f3\u867d\u5c0f\uff0c\u5374\u4f3c\u4e4e\u6709\u4e0d\u53ef\u6297\u62d2\u7684\u529b\u91cf\u3002\u5510 \u6653\u6f9c\u4f9d\u8a00\u5750\u4e0b\uff0c\u53c8\u542c\u5f97\u90a3\u5973\u4eba\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u628a\u4f60\u7684\u4e0a\u8863\u89e3\u4e0b\u6765\uff01\u201d\u5510\u6653\u6f9c\u5403\u4e86\u4e00\u60ca\uff0c\u90a3\u5973\u4eba \u9053\uff1a\u201c\u6211\u53eb\u4f60\u8131\u8863\uff0c\u4f60\u81ea\u5df1\u770b\u770b\uff0c\u4f60\u7684\u80f8\u53e3\u6709\u4e00\u7c92\u5927\u9ed1\u75d4\uff0c\u4e24\u4e73\u65c1\u8fb9\u6709\u4e24\u7c92\u5c0f\u9ed1\u75d4\uff0c\u6210\u54c1\u5b57 \u5f62\uff0c\u4f60\u81ea\u5df1\u6478\u6478\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u5510\u6653\u6f9c\u66f4\u662f\u5403\u60ca\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u8eab\u4e16\u771f\u5982\u68a6\uff0c\u6df1\u5bab\u5f02\u4e8b\u591a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u6e05\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u56db\u56de\u3000\u68a6\u5e7b\u5c18\u7f18\u3000\u6df1\u5bab\u85cf\u6068\u4e8b\u3000\u74e2\u96f6\u84ec\u6897\u3000\u4e00\u5251\u4e0a\u4ed9\u971e \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u5341\u56db\u56de \u68a6\u5e7b\u5c18\u7f18\u3000\u6df1\u5bab\u85cf\u6068\u4e8b \u74e2\u96f6\u84ec\u6897\u3000\u4e00\u5251\u4e0a\u4ed9\u971e \u3000\u3000\u5510\u6653\u6f9c\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u5973\u4eba\u5982\u4f55\u77e5\u9053\u5f97\u8fd9\u822c\u6e05\u695a\u3002\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u8131\u4e86\uff0c\u6211\u8eab\u4e0a\u662f\u6709\u8fd9 \u4e48\u4e09\u9897\u75d4\u3002\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u4f60\u7684\u4e49\u7236\u662f\u4e0d\u662f\u53eb\u505a\u5468\u9752\uff1f\u201d\u5510\u6653\u6f9c\u66f4\u662f\u5403\u60ca\uff0c\u7b54\u9053\uff1a\u201c\u662f\u3002\u201d \u90a3\u5973\u4eba\u5ffd\u7136\u54ed\u9053\uff1a\u201c\u513f\u554a\uff0c\u4f60\u957f\u5f97\u8fd9\u4e48\u9ad8\u4e86\uff01\u201d\u5510\u6653\u6f9c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u6211\u7684\u7238\u7238\u5988\u5988\u65e9\u5df2\u6b7b\u53bb \u4e86\uff0c\u4f60\u3001\u4f60\u3001\u2026\u2026\u201d\u4ed6\u672c\u60f3\u8bf4\uff1a\u201c\u4f60\u75af\u4e86\u5417\uff1f\u201d\u4f46\u88ab\u90a3\u5973\u4eba\u7684\u773c\u5149\u6240\u6151\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5374\u8bf4\u4e0d \u51fa\u6765\u3002 \u3000\u3000\u90a3\u5973\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u7136\u63e9\u4e86\u773c\u6cea\uff0c\u60e8\u7136\u7b11\u9053\uff1a\u201c\u96be\u602a\u4f60\u4e0d\u77e5\u9053\u3002\u4f60\u5750\u4e0b\u6765\u3002\u201d\u5510\u6653\u6f9c\u53c8 \u518d\u4f9d\u8a00\u5750\u4e0b\u3002\u90a3\u5973\u4eba\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4f60\u7684\u4eb2\u751f\u7238\u7238\u662f\u5510\u4e07\u82f1\u5417\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u662f \u8c01\uff1f\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u662f\u5f53\u4eca\u7687\u4e0a\uff01\u201d\u5510\u6653\u6f9c\u7a81\u7136\u5982\u53d7\u68d2\u51fb\uff0c\u518d\u4e5f\u5fcd\u53d7\u4e0d\u4f4f\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4f60 \u80e1\u8bf4\uff01\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u4f60\u5750\u4e0b\u6765\uff0c\u5750\u4e0b\u6765\uff0c\u542c\u6211\u8bf4\u3002\u6709\u4e00\u4e2a\u505a\u7687\u5e1d\u7684\u7236\u4eb2\u867d\u7136\u5f88\u4e0d\u597d\uff0c\u4f46\u4ed6 \u7ec8\u662f\u4f60\u7684\u7236\u4eb2\uff0c\u6211\u5df2\u7ecf\u98ce\u70db\u6b8b\u5e74\uff0c\u4e0d\u4e45\u4eba\u4e16\u4e86\u3002\u6709\u5e78\u4e0a\u5929\u53eb\u4f60\u6211\u76f8\u4f1a\uff0c\u6211\u603b\u4e0d\u80fd\u53eb\u4f60\u4e00\u751f\u8499 \u5728\u9f13\u91cc\u3002\u4f60\u522b\u5c3d\u77a7\u6211\uff0c\u4f60\u5148\u5750\u4e0b\u6765\uff0c\u5750\u4e0b\u6765\uff01\u542c\u6211\u8bf4\uff0c\u542c\u6211\u8bf4\u3002\u201d\u5510\u6653\u6f9c\u5750\u4e0b\u6765\u9053\uff1a\u201c\u597d\uff0c \u4f60\u8bf4\u3002\u201d \u3000\u3000\u90a3\u5973\u4eba\u9053\uff1a\u201c\u4e09\u5341\u591a\u5e74\u524d\uff0c\u90a3\u65f6\u6211\u53ea\u6709\u5341\u516d\u5c81\uff0c\u548c\u4f60\u7684\u5916\u7236\u5728\u897f\u95e8\u5916\u4f4f\uff0c\u6211\u5df2\u7ecf\u8ba2\u4e86 \u5a5a\uff0c\u672a\u5a5a\u592b\u53eb\u795d\u5bb6\u6f8e\uff0c\u5728\u5185\u52a1\u90e8\u5f53\u4e0a\u4e00\u540d\u5c0f\u5c0f\u5dee\u4f7f\uff0c\u90a3\u65f6\u5468\u9752\u8fd8\u5728\u5bab\u5185\u5f53\u536b\u58eb\uff0c\u672a\u66fe\u53db\u53d8\u51fa \u4eec\uff0c\u4e09\u4e2a\u4eba\u662f\u5e38\u5728\u4e00\u8d77\u7684\u597d\u670b\u53cb\u3002\u6709\u4e00\u5e74\u6311\u9009\u79c0\u5973\uff0c\u6211\u7adf\u7136\u88ab\u9009\u4e0a\u4e86\u3002\u5bb6\u4e2d\u6ca1\u94b1\u8d3f\u8d42\uff0c\u5c31\u8fd9 \u6837\u88ab\u8feb\u8fdb\u5bab\u3002\u5f53\u65f6\u6211\u672c\u60f3\u4e00\u6b7b\u660e\u5fd7\uff0c\u4f46\u5bb6\u6f8e\u8bf4\uff0c\u5bab\u5a25\u6bcf\u5341\u5e74\u6dd8\u6c70\u4e00\u6b21\uff0c\u53ea\u8981\u5728\u5bab\u4e2d\u4fdd\u5f97\u4f4f\u8eab \u5b50\uff0c\u5341\u5e74\u4e4b\u540e\uff0c\u5e74\u7eaa\u5927\u4e86\uff0c\u7687\u540e\u5c31\u4f1a\u5f00\u6069\u653e\u56de\u5bb6\u4e2d\u5a5a\u914d\uff0c\u8981\u4e0d\u7136\uff0c\u79c0\u5973\u5e74\u5e74\u589e\u591a\uff0c\u5e74\u8001\u7684\u4e0d \u653e\u51fa\u6765\uff0c\u5bab\u4e2d\u90a3\u5bb9\u7eb3\u5f97\u4e86\u3002\u6211\u60f3\u90a3\u4e48\u591a\u79c0\u5973\u8fdb\u5bab\uff0c\u53ea\u8981\u6211\u4e0d\u51fa\u98ce\u5934\uff0c\u7687\u5e1d\u4e5f\u672a\u5fc5\u6ce8\u610f\u5230\u6211\u3002 \u5bb6\u6f8e\u65e2\u613f\u7b49\u6211\u5341\u5e74\u3002\u5982\u679c\u6211\u73b0\u5728\u5bfb\u6b7b\uff0c\u5c82\u4e0d\u8f9c\u8d1f\u4e86\u4ed6\u7684\u5fc3\u610f\uff0c\u5c31\u8fd9\u6837\u6211\u8fdb\u5bab\u53bb\u4e86\u3002 \u3000\u3000\u201c\u5728\u5bab\u4e2d\u8fc7\u4e86\u4e94\u5e74\uff0c\u6211\u8fd8\u672a\u89c1\u8fc7\u7687\u5e1d\u7684\u9762\uff0c\u95f2\u6765\u65e0\u4e8b\uff0c\u6211\u5b66\u4f1a\u4e86\u5f39\u7434\uff0c\u6709\u4e00\u5929\u6211\u5f39\u6c5f\u5357 \u7684\u5c0f\u8c03\uff0c\u6211\u4eec\u5bb6\u662f\u4ece\u6c5f\u5357\u8fc1\u5c45\u5317\u4eac\u7684\uff0c\u8fd9\u4e9b\u5c0f\u8c03\u6211\u81ea\u5e7c\u8033\u719f\u80fd\u4f6f\u3002\u504f\u504f\u6709\u8fd9\u4e48\u51d1\u5de7\u7684\u4e8b\uff0c\u7687 \u5e1d\u7ecf\u8fc7\uff0c\u542c\u4e86\u6211\u7684\u7434\u58f0\uff0c\u975e\u5e38\u6b22\u559c\uff0c\u5f53\u665a\u5c31\u628a\u6211\u53ec\u5e78\u4e86\u3002\u90a3\u65f6\u6211\u60f3\u6b7b\u4e5f\u4e0d\u80fd\u6b7b\u4e86\uff0c\u56e0\u4e3a\u51e1\u5728 \u5bab\u4e2d\u7684\u540e\u5983\u5bab\u5a25\uff0c\u82e5\u7136\u81ea\u6740\uff0c\u7f6a\u8fde\u4e5d\u65cf\uff0c\u6211\u53ea\u597d\u5fcd\u8fb1\u5077\u751f\u4e0b\u53bb\uff0c\u90a3\u65f6\u5468\u9752\u5df2\u7ecf\u53db\u53d8\uff0c\u4faf\u4e09\u53d8 \u6709\u65f6\u4f8d\u5019\u7687\u4e0a\uff0c\u8fdb\u5165\u5185\u5ef7\uff0c\u6211\u5c31\u53eb\u4ed6\u544a\u8bc9\u5bb6\u6f8e\uff0c\u53eb\u5bb6\u6f8e\u53e6\u627e\u6dd1\u5973\uff0c\u4e0d\u8981\u518d\u7b49\u6211\u4e86\uff0c\u90a3\u77e5\u5bb6\u6f8e \u975e\u5e38\u75f4\u5fc3\uff0c\u7b2c\u4e8c\u5929\u5c31\u628a\u5dee\u4f7f\u8f9e\u4e86\uff0c\u540e\u6765\u6211\u624d\u77e5\u9053\u4ed6\u662f\u53bb\u627e\u5468\u9752\uff0c\u81ea\u6b64\u4e0d\u77e5\u4ed6\u7684\u4e0b\u843d\uff0c\u5468\u9752\u4e5f \u6ca1\u6709\u78b0\u5230\u4ed6\u3002 \u3000\u3000\u201c\u518d\u8fc7\u4e00\u5e74\uff0c\u4f60\u51fa\u4e86\u4e16\u3002\u90a3\u65f6\u7687\u4e0a\u5df2\u7ecf\u6709\u5341\u56db\u4f4d\u7687\u5b50\uff0c\u7acb\u50a8\u95ee\u9898\uff0c\u5df2\u7ecf\u5f00\u59cb\u5728\u95f9\u3002\u5bab\u4e2d \u89c4\u77e9\uff0c\u672c\u6765\u4e0d\u51c6\u6c49\u5973\u4e3a\u5983\uff0c\u81ea\u524d\u7687\uff08\u987a\u6cbb\uff09\u548c\u8463\u9102\u5983\u95f9\u51fa\u4e8b\u540e\uff0c\u8fd9\u89c4\u77e9\u6267\u5f97\u66f4\u4e25\u3002\u751f\u6709\u513f\u5b50 \u7684\u8d35\u5983\u5992\u5fcc\u6211\u4ee5\u6c49\u5973\u627f\u6069\uff0c\u5c31\u5728\u7687\u540e\u9762\u524d\u8fdb\u8c17\uff0c\u5c06\u6211\u6253\u5165\u51b7\u5b98\uff0c\u800c\u4e14\u60f3\u8c0b\u6740\u4f60\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u542c\u5f97\u5fc3\u60ca\u8089\u8df3\uff0c\u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u7687\u5e1d\u77e5\u9053\u5417\uff1f\u201d\u90a3\u5973\u4eba\u60e8\u7b11\u9053\uff1a\u201c\u5bab \u4e2d\u5bab\u5a25\u5983\u5ad4\uff0c\u4f55\u6b62\u5343\u6570\uff0c\u7ecf\u4ed6\u53ec\u5e78\u8fc7\u7684\uff0c\u4e5f\u4e0d\u77e5\u591a\u5c11\uff0c\u4ed6\u90a3\u91cc\u628a\u6211\u653e\u5728\u5fc3\u4e0a\uff0c\u7687\u540e\u628a\u6211\u6253\u5165 \u51b7\u5bab\uff0c\u4ed6\u662f\u5426\u77e5\u9053\uff0c\u6211\u4e5f\u4e0d\u6653\u5f97\u3002\u201d\u5510\u6653\u6f9c\u53ea\u89c9\u5fc3\u5934\u51b0\u51b7\uff0c\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u90a3 \u4e48\u4f60\u5c31\u8fd9\u6837\u4e0d\u660e\u4e0d\u767d\u7684\u5728\u51b7\u5bab\u91cc\u8fc7\u4e86\u4e8c\u5341\u591a\u5e74\u3002\u201d\u90a3\u5973\u4eba\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e5f\u8fc7\u60ef\u4e86\u3002\u8d77\u521d\u6709\u4eba \u770b\u5b88\uff0c\u4e0d\u51c6\u6211\u51fa\u8fd9\u95f4\u77f3\u5c4b\uff0c\u540e\u6765\u65e5\u5b50\u4e45\u4e86\uff0c\u7687\u540e\u6b7b\u4e86\uff0c\u6211\u4e5f\u8001\u4e86\uff0c\u6ca1\u6709\u4eba\u518d\u6ce8\u610f\u6211\u4e86\uff0c\u4e8e\u662f \u5979\u4eec\u5c31\u8ba9\u6211\u81ea\u751f\u81ea\u706d\uff0c\u6bcf\u5929\u6709\u4eba\u9001\u4e24\u987f\u996d\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u5c31\u6ca1\u4eba\u518d\u7406\u6211\u4e86\uff0c\u6211\u53ef\u4ee5\u5728\u56ed\u5b50\u91cc\u81ea \u7531\u8d70\u52a8\uff0c\u4f46\u662f\u6211\u4f4f\u60ef\u4e86\u51b7\u5bab\uff0c\u8fde\u9633\u5149\u4e5f\u6015\u89c1\u4e86\u3002\u6211\u5c31\u5929\u5929\u5750\u5728\u8fd9\u5c4b\u5b50\u91cc\u7b49\u6b7b\uff01\u201d\u5510\u6653\u6f9c\u518d\u4e5f \u5fcd\u53d7\u4e0d\u4f4f\uff0c\u5c06\u6bcd\u4eb2\u4e00\u628a\u62b1\u7740\uff0c\u4f4e\u58f0\u54ed\u9053\uff1a\u201c\u6211\u7684\u7684\u5988\u5988\u5440\uff01\u201d \u3000\u3000\u90a3\u5973\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u8f7b\u8f7b\u629a\u6478\u513f\u5b50\u7684\u5934\u53d1\uff0c\u5583\u5583\u7684\u8bf4\u9053\uff1a\u201c\u60ef\u4e86\u3001\u60ef\u4e86\uff0c\u773c\u6cea\u4e5f\u6d41\u5e72\u4e86\u3002 \u8981\u4e0d\u662f\u5fc3\u91cc\u5934\u8fd8\u60e6\u7740\u4f60\uff0c\u6211\u6015\u65e9\u6b7b\u6389\u4e86\uff01\u201d\u5510\u6653\u6f9c\u75db\u54ed\u5931\u58f0\uff0c\u90a3\u5973\u4eba\u9053\uff1a\u201c\u522b\u54ed\uff0c\u8c22\u8c22\u5929\uff0c \u4f60\u603b\u7b97\u6765\u4e86\u3002\u8bb0\u5f97\u6211\u6258\u4faf\u4e09\u53d8\u5077\u5077\u628a\u4f60\u9001\u51fa\u5bab\u65f6\uff0c\u4f60\u8fd8\u672a\u6ee1\u6708\uff0c\u54e6\uff0c\u7b97\u7b97\u770b\uff0c\u6211\u4e5f\u8ba1\u4e0d\u6e05\u695a \u4e86\uff0c\u4f60\u73b0\u5728\u51e0\u5c81\u4e86\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4e8c\u5341\u516b\u5c81\u4e86\u3002\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4f4f\u5728\u51b7\u5bab\u4e5f\u6709\u4e8c\u5341 \u516b\u5e74\u4e86\u3002\u591a\u60a0\u957f\u7684\u5c81\u6708\u5440\uff01\u771f\u4e0d\u77e5\u662f\u600e\u4e48\u71ac\u8fc7\u6765\u7684\uff1f\u4f60\u51fa\u751f\u540e\uff0c\u6211\u6258\u656c\u4e8b\u623f\u7684\u592a\u76d1\u53bb\u62a5\u544a\u7687 \u5e1d\uff0c\u53ef\u662f\u592a\u76d1\u5374\u544a\u8bc9\u4e86\u7687\u540e\u3002\u6211\u7b49\u4e86\u51e0\u5929\uff0c\u4e0d\u89c1\u4ed6\u6765\uff0c\u4e5f\u4e0d\u89c1\u6709\u5b97\u4eba\u5e9c\u7684\u5b98\u513f\u6765\uff0c\u6211\u77e5\u9053\u4e8b \u60c5\u4e0d\u5999\uff0c\u7687\u5ba4\u4e2d\u9aa8\u8089\u76f8\u6b8b\u7684\u4e8b\u60c5\u6211\u4e5f\u77e5\u9053\u4e00\u4e9b\u3002\u6211\u5f88\u5bb3\u6015\u3002\u4e8e\u662f\u6211\u628a\u6240\u6709\u9996\u9970\uff0c\u90fd\u9001\u7ed9\u4e86\u4e00 \u4e2a\u5c0f\u592a\u76d1\uff0c\u53eb\u4ed6\u628a\u4f60\u62b1\u51fa\u5bab\u53bb\uff0c\u4ea4\u7ed9\u4faf\u4e09\u53d8\uff0c\u5728\u5bab\u4e2d\u5c31\u8bd3\u8bf4\u4f60\u5df2\u592d\u6298\u4e86\u3002\u53cd\u6b63\u7687\u5e1d\u8fd8\u6ca1\u77e5 \u9053\uff0c\u4e5f\u65e0\u4eba\u67e5\u95ee\uff0c\u628a\u4f60\u9001\u51fa\u5bab\u540e\u7684\u7b2c\u4e09\u5929\uff0c\u6211\u5c31\u88ab\u7687\u540e\u6253\u5165\u51b7\u5bab\u3002\u8bf4\u6211\u5984\u5411\u541b\u7687\u732e\u5a9a\uff0c\u4e71\u4e86 \u7956\u5b97\u6cd5\u7eaa\uff0c\u5c31\u8fd9\u6837\u4e0d\u660e\u4e0d\u767d\u7684\u5728\u51b7\u5bab\u91cc\u5173\u4e86\u4e8c\u5341\u516b\u5e74\uff01\u201d\u5510\u6653\u6f9c\u54fd\u54bd\u95ee\u9053\uff1a\u201c\u540e\u6765\u5462\uff1f\u6211\u600e \u4e48\u4f1a\u5230\u4e86\u5510\u5bb6\uff1f\u201d \u3000\u3000\u90a3\u5973\u4eba\u505c\u4e86\u4e00\u505c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5fd8\u8bb0\u5bf9\u4f60\u8bf4\uff0c\u6211\u8fd8\u6709\u4e00\u4e2a\u59b9\u59b9\uff0c\u7ecf\u5468\u9752\u4f5c\u5a92\uff0c\u5ac1\u7ed9\u4e00\u4e2a\u59d3 \u5510\u7684\u9556\u5e08\u3002\u4faf\u4e09\u53d8\u5c06\u4f60\u5e26\u5230\u5510\u5bb6\uff0c\u6211\u59b9\u59b9\u6ca1\u5b69\u5b50\uff0c\u5c31\u5c06\u4f60\u5f53\u4eb2\u751f\u513f\u5b50\u629a\u517b\u3002\u201d\u5510\u6653\u6f9c\u8fd9\u65f6\u5982 \u540c\u5728\u6076\u68a6\u4e2d\u521d\u9192\uff0c\u5931\u58f0\u8bf4\u9053\uff1a\u201c\u539f\u6765\u6211\u90a3\u60e8\u6b7b\u7684\u53cc\u4eb2\uff0c\u5374\u662f\u6211\u7684\u59e8\u7236\u59e8\u6bcd\u3002\u201d\u90a3\u5973\u4eba\u9053\uff1a \u201c\u6b63\u662f\uff0c\u6211\u5e7d\u7981\u5728\u51b7\u5bab\u91cc\u4e8c\u5341\u516b\u5e74\uff0c\u4faf\u4e09\u53d8\u66fe\u6084\u6084\u6765\u770b\u8fc7\u6211\u56db\u4e94\u6b21\uff0c\u6211\u624d\u77e5\u9053\u4e0d\u77e5\u662f\u54ea\u4f4d\u7687 \u5b50\u63a2\u51fa\u4f60\u5728\u5510\u5bb6\uff0c\u6d3e\u4eba\u6349\u4f60\uff0c\u628a\u4f60\u7684\u517b\u7236\u3001\u6211\u7684\u59b9\u592b\u6740\u4e86\u3002\u4faf\u4e09\u53d8\u6709\u6b21\u51fa\u5dee\uff0c\u5728\u6c5f\u6e56\u66fe\u78b0\u89c1 \u8fc7\u5468\u9752\uff0c\u5468\u9752\u544a\u8bc9\u4ed6\u8bf4\uff0c\u4ed6\u5df2\u5c06\u4f60\u6536\u4e3a\u4e49\u5b50\uff0c\u5e76\u51c6\u5907\u628a\u6e38\u9f99\u5b9d\u5251\u4ea4\u4f60\u4f7f\u7528\uff0c\u53eb\u4faf\u4e09\u53d8\u7559\u5fc3\uff0c \u4ed6\u5e74\u82e5\u78b0\u5230\u6709\u4f7f\u6e38\u9f99\u5251\u7684\u5c11\u5e74\u5c31\u662f\u4f60\u4e86\u3002\u201d\u5510\u6653\u6f9c\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u8fd9\u65f6\u4e00\u5207\u771f\u76f8\u90fd\u5df2\u5927\u767d\u3002 \u539f\u6765\u81ea\u5df1\u5728\u4e07\u91cc\u957f\u57ce\u4e4b\u4e0a\uff0c\u821e\u52a8\u6e38\u9f99\u5b9d\u5251\uff0c\u8fd9\u624d\u88ab\u4faf\u4e09\u53d8\u8ba4\u4e86\u51fa\u6765\u3002\u90a3\u5973\u4eba\u95ee\u9053\uff1a\u201c\u5468\u9752 \u5462\uff1f\u73b0\u5728\u8fd8\u5728\u4e16\u5417\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5df2\u6b7b\u4e86\u5341\u4e8c\u5e74\u4e86\u3002\u201d\u5f53\u4e0b\u628a\u81ea\u5df1\u548c\u59e8\u6bcd\u7ed9\u6e05\u5bab\u4f8d\u536b\u8ffd\u5230 \u585e\u5916\uff0c\u59e8\u6bcd\u60e8\u6b7b\uff0c\u5468\u9752\u628a\u81ea\u5df1\u6551\u51fa\u6765\uff0c\u540e\u6765\u4ea4\u7ed9\u51af\u5bb6\uff0c\u53c8\u540e\u6765\u7ed9\u8840\u6ef4\u5b50\u8ffd\u6355\uff0c\u51af\u5bb6\u7236\u5b50\u53cc \u4ea1\uff0c\u6bcd\u5973\u79bb\u6563\uff0c\u5468\u9752\u8eab\u6b7b\u7b49\u7b49\u4e8b\u60c5\u5168\u90fd\u8bf4\u4e86\uff0c\u90a3\u5973\u4eba\u6f78\u7136\u6cea\u4e0b\uff0c\u54fd\u54bd\u9053\uff1a\u201c\u6211\u5df2\u597d\u591a\u5e74\u6765\u6ca1 \u6709\u773c\u6cea\u4e86\uff0c\u4eca\u5929\u8981\u75db\u75db\u5feb\u5feb\u54ed\u5b83\u4e00\u573a\u3002\u201d\u5510\u6653\u6f9c\u770b\u7740\u6bcd\u4eb2\uff0c\u601d\u6f6e\u4e71\u6d8c\u3002\u4ed6\u591a\u5e74\u6765\u5728\u5468\u9752\u6559\u5bfc \u4e4b\u4e0b\uff0c\u65e9\u628a\u6e05\u5ef7\u6068\u4e4b\u5165\u9aa8\u3002\u5468\u9752\u53c8\u59cb\u7ec8\u7792\u7740\u4ed6\u7684\u8eab\u4e16\uff0c\u6240\u4ee5\u5510\u6653\u6f9c\u603b\u4ee5\u4e3a\u81ea\u5df1\u662f\u4e2a\u6c49\u4eba\uff0c\u4e45 \u6709\u53cd\u6e05\u590d\u660e\u4e4b\u5fd7\u3002\u4e07\u4e07\u6599\u4e0d\u5230\u81ea\u5df1\u7adf\u7136\u662f\u4e2a\u6ee1\u6d32\u7687\u5b50\uff0c\u65e0\u60c5\u7684\u4e8b\u5b9e\u4f3c\u4e00\u4e2a\u5de8\u5927\u7684\u94c1\u9524\uff0c\u628a\u4ed6 \u7684\u5fc3\u6253\u5f97\u7c89\u788e\uff0c\u4ed6\u5e0c\u671b\u8fd9\u662f\u4e00\u4e2a\u6076\u68a6\uff0c\u4f46\u53ef\u60dc\u8fd9\u5374\u4e0d\u662f\u6076\u68a6\u3002\u79cd\u65cf\u7684\u4ec7\u6068\uff0c\u8eab\u4e16\u7684\u4ec7\u6068\uff0c\u7eb7 \u5982\u4e71\u4e1d\u7ed3\u5728\u4e00\u8d77\uff0c\u4ed6\u832b\u7136\u95ee\u9053\uff1a\u201c\u5988\u5988\uff0c\u4f60\u53eb\u6211\u600e\u4e48\u597d\u5462\uff01\u201d \u3000\u3000\u6bcd\u4eb2\u518d\u6b21\u629a\u6478\u5b69\u5b50\u7684\u5934\u53d1\uff0c\u8bb8\u4e45\uff0c\u8bb8\u4e45\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u5173\u5728\u51b7\u5bab\u91cc\u7684\u5934\u51e0\u5e74\uff0c\u82b1\u5f88\u4f24 \u5fc3\uff0c\u75db\u6068\u7687\u4e0a\u3002\u540e\u6765\u5462\uff0c\u65e5\u65e5\u591c\u591c\u5750\u5728\u8fd9\u91cc\u7b49\u6b7b\uff0c\u597d\u50cf\u4eba\u4e5f\u9ebb\u6728\u4e86\uff0c\u4ec0\u4e48\u90fd\u4e0d\u4f1a\u60f3\u4e86\u3002\u5440\uff0c \u591a\u5bd2\u51b7\u5440\uff01\u7231\u5440\u6068\u5440\uff0c\u90fd\u597d\u50cf\u51b7\u5f97\u51dd\u7ed3\u8d77\u6765\uff0c\u51dd\u7ed3\u5728\u5fc3\u91cc\u3002\u4f60\u53eb\u6211\u7ed9\u4f60\u60f3\uff0c\u60f3\u4e9b\u4ec0\u4e48\uff1f\u6211\u4e0d \u77e5\u9053\uff0c\u4f60\u5f97\u8ba9\u6211\u6162\u6162\u7684\u60f3\u3002\u554a\uff01\u4f60\u5e94\u8be5\u662f\u4e2a\u7687\u5b50\uff0c\u4f46\u6211\u5374\u4e0d\u613f\u4f60\u505a\u4e2a\u7687\u5b50\u3002\u201d\u5510\u6653\u6f9c\u75db\u82e6\u7684 \u53eb\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\u95ee\u9898\uff0c\u5988\u5988\uff0c\u6211\u7edd\u4e0d\u4f1a\u505a\u7687\u5b50\u7684\u3002\u6211\u4e0d\u613f\u610f\u3002\u4e0d\u662f\u8fd9\u4e2a\u95ee\u9898\u3002\u201d\u90a3\u5973\u4eba \u9053\uff1a\u201c\u90a3\u4e48\u4f60\u60f3\u7684\u662f\u4ec0\u4e48\u5462\uff1f\u201d\u5979\u62ac\u8d77\u5934\u6765\uff0c\u63a5\u89e6\u5230\u513f\u5b50\u90a3\u75db\u82e6\u7684\u56f0\u60d1\u7684\u773c\u5149\uff0c\u5979\u8eab\u5c45\u51b7 \u5bab\uff0c\u5979\u660e\u767d\u4e86\u513f\u5b50\u5728\u60f3\u4e9b\u4ec0\u4e48\u3002\u5979\u62c5\u5fc3\u513f\u5b50\u4f1a\u5728\u65e0\u7a77\u65e0\u5c3d\u7684\u98ce\u6d6a\u91cc\u4e27\u751f\u3002\u5979\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u597d \u4e86\uff0c\u6cea\u5df2\u7ecf\u6d41\u5f97\u591f\u4e86\uff0c\u8ba9\u54b1\u4eec\u6bcd\u5b50\u597d\u597d\u7684\u805a\u4e00\u4f1a\u5427\u3002\u201d \u3000\u3000\u6bcd\u4eb2\u6402\u7740\u513f\u5b50\uff0c\u8fc7\u4e86\u8bb8\u4e45\uff0c\u6cea\u75d5\u6ee1\u9762\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u542c\u6211\u5f39\u7434\u5427\uff0c\u4f60\u8fd8\u6ca1\u6709\u542c\u8fc7\u5bb6\u4e61\u7684\u5c0f \u8c03\u5462\uff01\u201d\u5979\u629a\u5f04\u7476\u7434\uff0c\u53ee\u53ee\u549a\u549a\u5f39\u4e86\u8d77\u6765\uff0c\u672c\u6765\u662f\u5f88\u6109\u5feb\u7684\u5c0f\u8c03\uff0c\u5374\u5f39\u5f97\u751a\u662f\u60b2\u82e6\uff0c\u513f\u5b50\u5728 \u51fa\u795e\u7684\u542c\uff0c\u51fa\u795e\u7684\u60f3\uff0c\u5ffd\u7136\u4e00\u4e2a\u9aef\u7709\u7686\u767d\u7684\u8001\u4eba\u6084\u4fcf\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u4e2a\u8001\u4eba\u6b63\u662f\u5eb7\u7199\u7687\u5e1d\u3002\u4ed6\u516b\u5c81\u767b\u57fa\uff0c\u5728\u4f4d\u5dee\u4e0d\u591a\u516d\u5341\u5e74\uff0c\u4eba\u4e5f\u8fd1\u4e03\u5341\u5c81\u4e86\u3002\u8fd1\u5e74\u6765\u4ed6 \u5df2\u4e0d\u5927\u7406\u4e8b\uff0c\u5728\u517b\u5fc3\u6bbf\u91cc\u4f18\u6e38\u5c81\u6708\uff0c\u8fd9\u665a\u6708\u8272\u5f88\u597d\uff0c\u4ed6\u4e00\u65f6\u5174\u8d77\uff0c\u5e26\u4e86\u4e24\u4e2a\u592a\u76d1\uff0c\u5728\u56ed\u5b50\u91cc \u6162\u6162\u7684\u901b\uff0c\u60f3\u8d77\u81ea\u5df1\u4e00\u751f\u6587\u4e8b\u6b66\u529f\uff0c\u90fd\u5df2\u5230\u4e86\u9876\u70b9\uff0c\u5750\u5e1d\u4f4d\u4e4b\u4eba\uff0c\u66f4\u662f\u5386\u4ee3\u5c11\u6709\uff0c\u4f46\u4e00\u751f\u5c31 \u5feb\u8fc7\u53bb\u4e86\uff0c\u8fd9\u4e9b\u6587\u4e8b\u6b66\u529f\u4e5f\u5c06\u5982\u70df\u6d88\u4e91\u6563\uff0c\u4ed6\u5ffd\u7136\u611f\u5230\u5bc2\u5bde\uff0c\u60f3\u627e\u4e9b\u8001\u670b\u53cb\u8c08\u8c08\uff0c\u4f46\u8001\u670b\u53cb \u4e5f\u6ca1\u6709\u51e0\u4eba\u3002\u7687\u540e\u548c\u81ea\u5df1\u5c11\u5e74\u65f6\u5ba0\u7231\u7684\u5983\u5ad4\u4e5f\u90fd\u5dee\u4e0d\u591a\u6b7b\u5149\u4e86\u3002\u4ed6\u5728\u6708\u5149\u4e0b\u6162\u6162\u7684\u8d70\uff0c\u8d70\u8fc7 \u4e86\u8377\u5858\u8fb9\u90a3\u5ea7\u51b7\u5bab\u3002 \u3000\u3000\u51b7\u5bab\u91cc\u98d8\u51fa\u4e00\u9635\u7434\u58f0\uff0c\u597d\u50cf\u662f\u4ec0\u4e48\u65f6\u5019\u542c\u8fc7\u7684\uff1f\u54e6\uff0c\u90a3\u5df2\u7ecf\u662f\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\u7684\u4e8b\u60c5\u4e86\uff0c \u5eb7\u7199\u7687\u5e1d\u5728\u7434\u58f0\u4e2d\u9759\u9759\u601d\u7d22\uff0c\u95ee\u592a\u76d1\u9053\uff1a\u201c\u8c01\u5728\u8fd9\u51b7\u5bab\u91cc\uff1f\u201d\u592a\u76d1\u9053\uff1a\u201c\u542c\u8bf4\u662f\u4e00\u4e2a\u5e74\u7eaa\u5f88 \u8001\u7684\u5bab\u5a25\u3002\u201d\u5eb7\u7199\u5947\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u653e\u51fa\u53bb\uff1f\u5173\u4e86\u591a\u4e45\u4e86\uff1f\u201d\u592a\u76d1\u9053\uff1a\u201c\u56de\u7687\u4e0a\uff0c\u5974\u624d\u4e5f\u4e0d \u6e05\u695a\u3002\u5974\u624d\u8fdb\u5bab\u65f6\uff0c\u90a3\u4e2a\u5bab\u5a25\u5df2\u7ecf\u5728\u91cc\u9762\u4e86\uff0c\u4e5f\u65f6\u65f6\u5f39\u7434\uff0c\u8c01\u4e5f\u6ca1\u7406\u5979\u3002\u201d\u5eb7\u7199\u7687\u5e1d\u53c8\u9759\u542c \u4e86\u4e00\u4f1a\uff0c\u84e6\u7136\u60f3\u8d77\u6709\u90a3\u4e48\u4e00\u4e2a\u5bab\u5a25\uff0c\u81ea\u5df1\u5728\u7ea6\u4e09\u5341\u5e74\u524d\u66fe\u53ec\u5e78\u8fc7\u5979\uff0c\u90a3\u4e00\u665a\u5979\u5f39\u7684\u4e5f\u597d\u50cf\u5c31 \u662f\u8fd9\u4e2a\u66f2\u5b50\uff0c\u8fc7\u540e\u81ea\u5df1\u4e8b\u591a\uff0c\u4e5f\u5c31\u5fd8\u4e86\u3002\u60f3\u4e86\u4e00\u60f3\uff0c\u95ee\u592a\u76d1\u9053\uff1a\u201c\u8fd9\u4e2a\u5bab\u5a25\u662f\u4e0d\u662f\u6c49\u5973\uff1f\u201d \u592a\u76d1\u9053\uff1a\u201c\u542c\u8bf4\u662f\u3002\u201d\u5eb7\u7199\u9053\uff1a\u201c\u662f\u4e0d\u662f\u74dc\u5b50\u9762\u513f\uff0c\u7709\u6bdb\u5f88\u957f\u7684\u3002\u201d\u592a\u76d1\u9053\uff1a\u201c\u7980\u7687\u4e0a\uff0c\u5974 \u624d\u6ca1\u89c1\u8fc7\u3002\u5979\u5728\u51b7\u5bab\u91cc\u603b\u4e0d\u80af\u51fa\u6765\u3002\u201d\u53e6\u4e00\u4e2a\u592a\u76d1\u63d2\u53e3\u9053\uff1a\u201c\u662f\u5440\uff0c\u7687\u540e\u6b7b\u4e86\uff0c\u770b\u5b88\u7684\u4e5f\u64a4 \u4e86\uff0c\u5979\u8fd8\u662f\u4e0d\u80af\u51fa\u6765\u8d70\u52a8\u3002\u201d\u5eb7\u7199\u76b1\u7709\u9053\uff1a\u201c\u54ea\u6709\u5173\u8fd9\u4e48\u4e45\u7684\uff1f\u4f60\u4eec\u5728\u5916\u9762\u7b49\u4faf\uff0c\u6211\u8fdb\u53bb\u770b \u770b\u3002\u201d\u51b7\u5bab\u91cc\u5510\u6653\u6f9c\u6bcd\u5b50\u5728\u7434\u58f0\u4e2d\u51dd\u7ed3\uff0c\u5ffd\u7136\u542c\u5f97\u6709\u811a\u6b65\u58f0\uff0c\u5510\u6653\u6f9c\u4e00\u95ea\u95ea\u5230\u5e10\u540e\uff0c\u5eb7\u7199\u7687 \u5e1d\u5df2\u63a8\u5f00\u623f\u95e8\uff0c\u8d70\u4e86\u8fdb\u6765\u3002\u90a3\u5973\u4eba\u62ac\u8d77\u5934\u6765\u3002\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u56db\u76ee\u4ea4\u6295\uff0c\u987f\u65f6\u5446\u4e86\u3002\u5eb7 \u7199\u7687\u5e1d\u770b\u4e86\u4e00\u9635\uff0c\u4f9d\u7a00\u8bb0\u5f97\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u6d77\u68e0\u5417\uff1f\u201d \u3000\u3000\u90a3\u5973\u4eba\u52a8\u4e5f\u4e0d\u52a8\uff0c\u6728\u7136\u8bf4\u9053\uff1a\u201c\u4e07\u5c81\u7237\uff0c\u6d77\u68e0\u5728\u4e8c\u5341\u516b\u5e74\u524d\u5df2\u7ecf\u6b7b\u4e86\uff01\u201d\u5eb7\u7199\u9053\uff1a\u201c\u4f60 \u4e0d\u662f\u6d77\u68e0\uff1f\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u4f60\u770b\u6211\u5728\u8fd9\u91cc\u548c\u6b7b\u5dee\u4ec0\u4e48\uff1f\u201d\u5eb7\u7199\u7687\u5e1d\u770b\u5979\u767d\u53d1\u6591\u6591\uff0c\u60f3\u8d77\u81ea\u5df1 \u4e5f\u8001\u4e86\uff0c\u8fd9\u5eff\u516b\u5e74\u6765\u81ea\u5df1\u5b89\u7136\u505a\u592a\u5e73\u7687\u5e1d\uff0c\u5979\u5374\u5728\u51b7\u5bab\u91cc\u7b49\u6b7b\uff0c\u5ffd\u7136\u611f\u5230\u6709\u4e9b\u6127\u610f\uff0c\u5750\u4e0b\u6765 \u9053\uff1a\u201c\u7687\u540e\u4e5f\u592a\u5fcd\u5fc3\u4e86\uff0c\u628a\u4f60\u5173\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u6211\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002\u4f60\u72af\u4e86\u4ec0\u4e48\u7f6a\u5462\uff1f\u201d\u90a3\u5973\u4eba \u9053\uff1a\u201c\u7687\u540e\u8bf4\u6211\u79c1\u81ea\u732e\u5a9a\uff0c\u8ff7\u60d1\u7687\u4e0a\uff0c\u8d25\u574f\u6cd5\u7eaa\u3002\u201d\u5eb7\u7199\u53f9\u9053\uff1a\u201c\u90a3\u771f\u662f\u59d4\u5c48\u4f60\u4e86\uff0c\u4e0d\u8fc7\u7687 \u540e\u4e5f\u6b7b\u4e86\u5341\u591a\u5e74\u4e86\uff0c\u8fd9\u7b14\u5e10\u4e5f\u4e0d\u5fc5\u7b97\u4e86\u3002\u6211\u660e\u5929\u628a\u4f60\u653e\u51fa\u6765\uff0c\u5c01\u4f60\u505a\u6dd1\u5983\u3002\u201d\u5eb7\u7199\u4ee5\u4e3a\u5979\u5fc5 \u5b9a\u4e0b\u8dea\u8c22\u6069\uff0c\u90a3\u77e5\u5979\u8fd8\u662f\u6728\u7136\u4e0d\u52a8\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8c22\u7687\u4e0a\uff0c\u7687\u4e0a\u4e0d\u8981\u518d\u628a\u6211\u6d88\u9063\u4e86\uff0c\u73b0\u5728\u6211 \u7684\u5bb6\u4eba\u90fd\u6b7b\u5149\u5566\uff0c\u6211\u4e5f\u4e0d\u6015\u6b7b\u4e86\u3002\u201d\u5eb7\u7199\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u4f60\u6068\u6211\u5417\uff1f\u54b1\u4eec\u90fd\u8001\u5566\uff0c\u8fd8 \u80fd\u6709\u591a\u5c11\u5c81\u6708\uff1f\u4f60\u51fa\u6765\u966a\u6211\u804a\u804a\uff0c\u6c14\u4e5f\u4f1a\u6162\u6162\u5e73\u4e86\u3002\u201d\u90a3\u5973\u4eba\u624b\u6309\u7476\u7434\uff0c\u4ecd\u6728\u7136\u4e0d\u52a8\u3002\u5eb7\u7199 \u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u60f3\u8981\u4ec0\u4e48\uff1f\u201d\u90a3\u5973\u4eba\u773c\u775b\u4e00\u4eae\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6211\u8981\u4f60\u8ba9\u6211\u7684\u513f \u5b50\u5b89\u7136\u51fa\u5bab\uff01\u201d\u5eb7\u7199\u9661\u7136\u4e00\u9707\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u7684\u513f\u5b50\uff1f\u90a3\u4e00\u665a\u4f60\u5c31\u6709\u4e86\uff1f\u656c\u4e8b\u623f\u7684\u592a\u76d1 \u4e3a\u4ec0\u4e48\u4e0d\u544a\u8bc9\u6211\uff1f\u4f60\u771f\u7684\u4f1a\u6709\u4e00\u4e2a\u513f\u5b50\uff1f\u90a3\u8fd9\u4e48\u591a\u5e74\uff0c\u4ed6\u85cf\u5728\u54ea\u513f\uff1f\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74 \u6765\uff0c\u4ed6\u5728\u6c5f\u6e56\u4e0a\u98d8\u8361\uff0c\u73b0\u5728\u5462\uff0c\u5374\u5728\u8fd9\u91cc\uff0c\u5c31\u5728\u8fd9\u4e2a\u623f\u5b50\u91cc\uff01\u201d\u5eb7\u7199\u7687\u5e1d\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u5510\u6653\u6f9c\u500f\u7684\u4ece\u5e10\u540e\u8df3\u4e86\u51fa\u6765\uff0c\u6fc0\u52a8\u7684\u56b7\u9053\uff1a\u201c\u4f60\u628a\u6211\u7684\u5988\u5988\u5bb3\u5f97\u8fd8\u4e0d\u591f\u5417\uff1f\u4f60\u8ba9\u6211\u4eec\u6bcd \u5b50\u90fd\u51fa\u5bab\u53bb\uff01\u201d\u5eb7\u7199\u7687\u5e1d\u5ffd\u89c1\u4e00\u4e2a\u9ad8\u5927\u5c11\u5e74\uff0c\u7ad9\u5728\u81ea\u5df1\u7684\u5bf9\u9762\uff0c\u773c\u5149\u6709\u5982\u5bd2\u51b0\u5229\u5251\uff0c\u9762\u8c8c\u679c \u7136\u6709\u70b9\u50cf\u81ea\u5df1\uff0c\u4e0d\u89c9\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u9661\u7136\u60f3\u8d77\u81ea\u5df1\u5728\u4e94\u53f0\u5c71\u4e0a\u5bb3\u6b7b\u7236\u4eb2\u7684\u4e8b\uff0c\uff08\u89c1\u62d9\u4f5c\u300a\u4e03 \u5251\u4e0b\u5929\u5c71\u300b\u7b2c\u4e00\u96c6\uff09\u5931\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u60f3\u66ff\u6bcd\u4eb2\u62a5\u4ec7\u5417\uff1f\u201d\u5510\u6653\u6f9c\u9893\u7136\u5750\u4e0b\uff0c\u6325\u6325\u624b \u9053\uff1a\u201c\u4f60\u4e0d\u613f\u653e\u6211\uff0c\u4f60\u5c31\u8d70\u5427\u3002\u201d\u5eb7\u7199\u5b9a\u4e86\u5b9a\u795e\uff0c\u770b\u8fd9\u5c11\u5e74\u867d\u662f\u98ce\u5c18\u6ee1\u9762\uff0c\u5374\u81ea\u6b63\u6c14\u51db\u7136\uff0c \u4e0d\u89c9\u9887\u4e3a\u5185\u759a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5c31\u7559\u4e0b\u6765\u5427\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u5b81\u6b7b\u4e5f\u4e0d\u7559\u5728\u8fd9\u513f\uff01\u201d\u5eb7\u7199\u53f9\u53e3 \u6c14\u9053\uff1a\u201c\u4f60\u8d70\u4e5f\u597d\u3002\u201d\u4ed6\u4e5f\u77e5\u9053\u81ea\u5df1\u5341\u51e0\u4e2a\u513f\u5b50\u6b63\u5728\u4e89\u6743\u593a\u4f4d\uff0c\u82e5\u518d\u6dfb\u4e00\u4e2a\uff0c\u66f4\u4e0d\u5f97\u4e86\u3002\u4f55 \u51b5\u4ed6\u662f\u6c49\u5973\u6240\u751f\uff1a\u591a\u5e74\u5728\u5916\uff0c\u5b97\u4eba\u5e9c\u7684\u540d\u518c\u4e5f\u6ca1\u4ed6\u7684\u540d\u5b57\uff0c\u5c31\u662f\u8981\u8ba9\u4ed6\u590d\u59d3\u5f52\u5b97\uff0c\u4e5f\u8981\u5927\u8d39 \u5468\u7ae0\u3002\u4f46\u773c\u770b\u8fd9\u5bf9\u6bcd\u5b50\uff0c\u5fc3\u91cc\u9887\u4e3a\u96be\u8fc7\u3002\u53c8\u4e0d\u60f3\u81ea\u5df1\u7684\u9aa8\u8089\u5728\u6c5f\u6e56\u6d41\u6d6a\uff0c\u60f3\u4e86\u53c8\u60f3\uff0c\u53c8\u9053\uff1a \u201c\u90a3\u4e48\u6211\u7ed9\u4f60\u4e00\u4e2a\u5b98\u804c\u5427\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u66f4\u4e0d\u7a00\u7f55\uff01\u201d\u5eb7\u7199\u9053\uff1a\u201c\u54ea\u4e48\u4f60\u5c31\u8fde\u7236\u4eb2\u4e5f\u4e0d \u8ba4\u4e86\u5417\uff1f\u201d\u5510\u6653\u6f9c\u5ffd\u7136\u75db\u54ed\u5931\u58f0\u201c\u7236\u7687\u201d\u4e8c\u5b57\u600e\u4e48\u4e5f\u53eb\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5eb7\u7199\u9053\uff1a\u201c\u4f60\u51fa\u53bb\u6253\u7b97\u505a\u4ec0\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u522b\u903c\u6211\uff01\u201d\u5eb7\u7199\u5947\u9053\uff1a\u201c\u6211\u903c\u4f60\u4ec0\u4e48 \u6765\u4e86\uff1f\u201d\u5ffd\u7136\u773c\u775b\u4e00\u95ea\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u60f3\u4e89\u593a\u7687\u4f4d\uff1f\u9aa8\u8089\u76f8\u6b8b\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u8981\u62a2\u4f60\u5bb6\u7687 \u4f4d\u7684\u662f\u6c49\u4eba\u3002\u6211\u4ec0\u4e48\u4e5f\u4e0d\u8981\u3002\u201d\u5eb7\u7199\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e00\u5b9a\u8981\u51fa\u53bb\uff0c\u6211\u5c31\u8ba9\u4f60\u51fa\u53bb\u3002\u4f60\u4ee5\u540e\u8fd8\u8981 \u89c1\u6211\u5417\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u4f46\u613f\u968f\u6bcd\u4eb2\u7ec8\u8001\uff0c\u8d70\u5f97\u8fdc\u8fdc\u7684\uff0c\u6c38\u4e0d\u518d\u6253\u6270\u4f60\u3002\u201d\u5eb7\u7199\u66f4\u89c9\u96be \u8fc7\uff0c\u53c8\u9053\uff1a\u201c\u6211\u613f\u610f\u7b54\u5e94\u4f60\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4f60\u6709\u4ec0\u4e48\u8981\u6c42\u5417\uff1f\u201d\u597d\u50cf\u4e0d\u7ed9\u4ed6\u4eec\u6bcd\u5b50\u4e00\u70b9\u4e1c\u897f\uff0c\u5c31 \u4e8e\u5fc3\u4e0d\u5b89\u3002\u5510\u6653\u6f9c\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u60f3\u89c1\u56db\u6bbf\u4e0b\uff0c\u6c42\u4ed6\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4ed6\u4e00\u5b9a \u505a\u5f97\u5230\u7684\u3002\u201d\u5eb7\u7199\u76b1\u4e86\u76b1\u7709\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5012\u6709\u4e8b\u60c5\u6c42\u4ed6\uff0c\u8fde\u6211\u90fd\u505a\u4e0d\u4e86\u7684\uff1f\u4f60\u7684\u56db\u54e5\u8fd1\u5e74\u5f88 \u4e3a\u8dcb\u6248\uff0c\u4e5f\u8bb8\u4ed6\u771f\u6709\u4e00\u4e9b\u672c\u4e8b\u3002\u201d\u5eb7\u7199\u5bf9\u5141\u796f\u9887\u4e3a\u618e\u538c\uff0c\u4ed6\u60f3\u96be\u9053\u8fd9\u5b69\u5b50\u8ba4\u4e3a\u4ed6\u7684\u56db\u54e5\u4e00\u5b9a \u80fd\u7ee7\u627f\u7687\u4f4d\uff0c\u6240\u4ee5\u8981\u9884\u5148\u5df4\u7ed3\u4ed6\uff1f\u53c8\u9053\uff1a\u201c\u597d\uff0c\u6211\u4f9d\u4f60\u4fbf\u662f\u3002\u4f46\u6211\u5012\u613f\u610f\u4f60\u4eb2\u8fd1\u4f60\u7684\u5341\u56db \u54e5\u3002\u6211\u53ef\u4ee5\u8ba9\u4f60\u505a\u4ed6\u7684\u526f\u624b\u3002\u201d\u5eb7\u7199\u6700\u6b22\u559c\u7b2c\u5341\u56db\u5b50\uff0c\u8fd9\u65f6\u6b63\u7edf\u5175\u897f\u5f81\u3002\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u53ea \u8981\u89c1\u56db\u6bbf\u4e0b\u3002\u201d\u5eb7\u7199\u9053\uff1a\u201c\u597d\uff0c\u4f60\u968f\u6211\u51fa\u53bb\uff0c\u660e\u5929\u6211\u628a\u4f60\u7684\u5988\u5988\u653e\u51fa\u5bab\u3002\u201d\u643a\u4e86\u5510\u6653\u6f9c\u7684 \u624b\uff0c\u7f13\u6b65\u8d70\u51fa\u51b7\u5bab\uff0c\u4e24\u4e2a\u592a\u76d1\u5927\u5403\u4e00\u60ca\uff0c\u5eb7\u7199\u9053\uff1a\u201c\u8fd9\u662f\u65b0\u6765\u7684\u536b\u58eb\uff0c\u662f\u6211\u53eb\u4ed6\u8fdb\u51b7\u5bab\u6765 \u7684\u3002\u201d\u4e24\u4e2a\u592a\u76d1\u81ea\u7136\u4e0d\u6562\u51fa\u58f0\uff0c\u770b\u770b\u6708\u4eae\uff0c\u6708\u4eae\u5df2\u8fd1\u5929\u5fc3\uff0c\u592a\u76d1\u9053\uff1a\u201c\u7687\u4e0a\u8be5\u5b89\u6b47\u4e86\uff0c\u660e\u5929 \u53c8\u514d\u65e9\u671d\u5427\uff1f\u201d\u5eb7\u7199\u9053\uff1a\u201c\u514d\uff01\u201d\u9ed8\u9ed8\u7684\u548c\u5510\u6653\u6f9c\u7a7f\u82b1\u62c2\u67f3\uff0c\u8d70\u51fa\u56ed\u5b50\uff0c\u6b65\u8fc7\u6c38\u5bff\u5bab\uff0c\u6b65\u5411 \u517b\u5fc3\u6bbf\uff0c\u5510\u6653\u6f9c\u5ffd\u7136\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u62c9\u7740\u5eb7\u7199\u5411\u82b1\u836b\u6df1\u5904\u4e00\u4f0f\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u591c\u884c\u4eba\u98de\u4e0a\u5916\u9762 \u7684\u5927\u6bbf\uff01\u201d \u3000\u3000\u5eb7\u7199\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u6211\u770b\u4e0d\u89c1\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u90a3\u4eba\u7684\u8eab\u6cd5\u592a\u5feb\u4e86\uff01\u201d\u5eb7\u7199\u5fc3\u60f3\uff1a\u82e5\u662f\u5bab \u4e2d\u536b\u571f\uff0c\u4e5f\u4e0d\u6562\u98de\u8eab\u4e0a\u5916\u9762\u5927\u6bbf\u3002\u4f46\u82e5\u8981\u8bf4\u662f\u5916\u4eba\uff0c\u90a3\u5374\u662f\u4e07\u4e0d\u80fd\u591f\u3002\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6084\u6084\u8bf4 \u9053\uff1a\u201c\u4f60\u4ece\u8fd9\u91cc\u5411\u5de6\u8d70\uff0c\u9760\u7740\u9662\u5b50\u7684\u767d\u77f3\u680f\u6746\uff0c\u6570\u5230\u7b2c\u5341\u4e09\u5757\u77f3\u5934\uff0c\u628a\u77f3\u5934\u63ed\u8d77\uff0c\u6709\u5730\u9053\u901a \u5230\u5916\u9762\u7684\u5927\u6bbf\u67f1\u540e\uff0c\u4f60\u5077\u5077\u51fa\u6765\uff0c\u770b\u6709\u6ca1\u6709\u4eba\u5728\u91cc\u9762\uff0c\u82e5\u7136\u6709\u4eba\uff0c\u4f60\u8bb0\u7740\u4ed6\u7684\u9762\u8c8c\u3002\u201d\u53c8\u628a \u968f\u8eab\u4f69\u5e26\u7684\u4e00\u5757\u6c49\u7389\u53d6\u4e0b\uff0c\u4ea4\u7ed9\u4ed6\u9053\uff1a\u201c\u82e5\u662f\u7ed9\u4eba\u53d1\u73b0\uff0c\u5c31\u4ee5\u6b64\u4e3a\u51ed\uff0c\u8bf4\u662f\u65b0\u6765\u7684\u536b\u58eb\u597d \u4e86\u3002\u201d\u5510\u6653\u6f9c\u63a5\u8fc7\u6c49\u7389\uff0c\u4ece\u5730\u9053\u91cc\u8fdb\u5165\u5927\u6bbf\uff0c\u8eb2\u5728\u67f1\u540e\uff0c\u5ffd\u89c1\u5916\u9762\u8d70\u8fdb\u4e24\u4eba\uff0c\u62ac\u8d77\u4e86\u5934\uff0c\u54ac \u7740\u8033\u6735\u8bf4\u8bdd\u3002\u5510\u6653\u6f9c\u4e5f\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u4e0a\u9762\u60ac\u7740\u4e00\u5757\u5927\u533e\uff0c\u6b63\u9762\u9898\u7740\u201c\u6b63\u5927\u5149\u660e\u201d\u56db\u5b57\uff0c\u5510 \u6653\u6f9c\u6697\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u5c3d\u770b\u7740\u533e\u989d\u505a\u4ec0\u4e48\uff1f\u201d\u7b49\u4e86\u4e00\u4f1a\uff0c\u90a3\u4e24\u4eba\u4f4e\u4e0b\u5934\u6765\u53c8\u5728\u5546\u8bae\uff0c\u533e\u989d\u540e\u9762 \u6a2a\u7cb1\uff0c\u5ffd\u7136\u4f38\u51fa\u4e00\u4e2a\u4eba\u5934\uff0c\u5510\u6653\u6f9c\u4e00\u89c1\uff0c\u51e0\u4e4e\u53eb\u51fa\u58f0\u6765\uff0c\u539f\u6765\u8eb2\u5728\u6bbf\u4e0a\u6a2a\u7cb1\u540e\u7684\u7adf\u662f\u4e00\u4e2a\u5973 \u5b69\uff0c\u800c\u4e14\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u51af\u7433\uff01 \u3000\u3000\u539f\u6765\u5f53\u65e5\u51af\u7433\u5e26\u5141\u5824\u53bb\u63a2\u5141\u796f\u7684\u75c5\u3002\u66f2\u66f2\u6298\u6298\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u5e26\u5141\u5824\u4e0a\u4e86\u4e66\u697c\uff0c\u63a8\u5f00\u4e00\u95f4 \u623f\u95e8\uff0c\u8bf4\u9053\uff1a\u201c\u56db\u6bbf\u4e0b\u5c31\u5728\u8fd9\u91cc\u517b\u75c5\u3002\u201d\u5141\u5824\u63a2\u5934\u4e00\u671b\uff0c\u5ffd\u7136\u91cc\u9762\u5636\u5636\u6709\u58f0\uff0c\u6ee1\u5c4b\u5b50\u91cc\u5927\u86c7 \u5c0f\u86c7\u8fde\u7ed3\u6210\u56e2\u3002\u51af\u7433\u5c16\u53eb\u9053\uff1a\u201c\u6211\u5f00\u9519\u623f\u95e8\u4e86\u3002\u201d\u8eab\u5b50\u4e00\u6251\uff0c\u624b\u8098\u7a81\u7136\u5728\u5141\u5824\u8170\u9645\u78b0\u4e86\u4e00 \u4e0b\uff0c\u5141\u5824\u5982\u4e2d\u94c1\u9524\uff0c\u75db\u5f97\u5927\u53eb\uff0c\u51af\u7433\u5df2\u4e00\u4e2a\u7b4b\u6597\u7ffb\u4e0b\u697c\u4e86\u3002\u5c4b\u5185\u7684\u86c7\u8815\u8815\u800c\u51fa\uff0c\u5141\u5824\u5e26\u6765\u7684 \u536b\u58eb\u4e0d\u6562\u79c1\u95ef\u5165\u623f\uff0c\u5141\u5824\u53c8\u5df2\u660f\u8ff7\uff0c\u53ea\u597d\u80cc\u8d77\u4ed6\u6025\u8d70\u3002 \u3000\u3000\u5141\u5824\u53bb\u540e\uff0c\u5728\u56db\u7687\u5b50\u5e9c\u4e2d\u7559\u5b88\u7684\u97e9\u91cd\u5c71\u548c\u53cc\u9b54\u90fd\u8d23\u5907\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u5b69\u5b50\u600e\u4e48\u5982\u6b64\u80e1 \u95f9\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u4ed6\u56de\u53bb\u751f\u6c14\u4e5f\u6ca1\u7528\uff0c\u6211\u53ea\u662f\u4e00\u4e2a\u5c0f\u4e2b\u5934\uff0c\u5230\u4ed6\u8d23\u5907\u56db\u6bbf\u4e0b\u65f6\uff0c\u4f60\u4eec\u53ef\u4ee5\u63a8 \u8bf4\u7687\u5e9c\u4e2d\u7684\u4e2b\u5934\u6570\u4ee5\u767e\u8ba1\uff0c\u77e5\u9053\u662f\u8c01\uff1f\u800c\u4e14\u5c31\u662f\u4ed6\u4eec\u4fe9\u5144\u5f1f\u5230\u7687\u5e1d\u9762\u524d\u4e89\u8bba\uff0c\u7687\u4e0a\u4e5f\u4e0d\u4f1a\u76f8 \u4fe1\uff0c\u4e00\u4e2a\u5c0f\u5973\u5b69\u4f1a\u628a\u4ed6\u6253\u6655\u3002\u201d\u97e9\u91cd\u5c71\u9053\uff1a\u201c\u54fc\uff0c\u77a7\u4e0d\u51fa\u4f60\u8fd9\u5a03\u513f\u8fd9\u6837\u5389\u5bb3\uff01\u201d\u5fc3\u4e2d\u6697\u60f3\uff1a \u597d\u5728\u53cc\u9b54\u6709\u5148\u89c1\u4e4b\u660e\uff0c\u7528\u602a\u836f\u628a\u5979\u8ff7\u4e86\u672c\u6027\uff0c\u4ee4\u5979\u5230\u7687\u5e9c\u4ee5\u524d\u4e8b\u90fd\u8bb0\u4e0d\u8d77\u6765\uff0c\u8981\u4e0d\u7136\u771f\u96be\u4ee5 \u957f\u4e45\u7684\u54c4\u9a97\u5979\u5462\u3002 \u3000\u3000\u97e9\u91cd\u5c71\u548c\u53cc\u9b54\u5546\u8bae\u4e00\u4f1a\uff0c\u6b63\u60f3\u6d3e\u4e00\u4eba\u53bb\u8bf7\u56db\u7687\u5b50\u56de\u4eac\uff0c\u56db\u7687\u5b50\u5141\u796f\u5ffd\u7136\u56de\u5230\u7687\u5e9c\u3002\u4e00\u5929 \u6101\u8651\uff0c\u987f\u65f6\u89e3\u5f00\uff0c\u97e9\u91cd\u5c71\u7b11\u9053\uff1a\u201c\u5927\u963f\u54e5\u82e5\u6562\u8bf4\u51fa\u56db\u6bbf\u4e0b\u79c1\u81ea\u51fa\u4eac\uff0c\u56db\u6bbf\u4e0b\u6b63\u597d\u544a\u4ed6\u8bec \u544a\u3002\u201d\u5141\u796f\u95ee\u4e86\u60c5\u5f62\uff0c\u77e5\u9053\u5141\u5824\u56fe\u8c0b\u751a\u6025\uff0c\u60f3\u8d77\u4e00\u4e8b\u3002\u539f\u6765\u6e05\u671d\u7687\u4f4d\u7ee7\u627f\u4e0d\u4f9d\u957f\u5e7c\u6b21\u5e8f\uff0c\u7531 \u7687\u5e1d\u7559\u4e0b\u9057\u8bcf\uff0c\u6307\u5b9a\u4e00\u4e2a\uff0c\u653e\u5728\u4e7e\u6e05\u5bab\u7684\u201c\u6b63\u5927\u5149\u660e\u201d\u533e\u989d\u4e4b\u540e\uff0c\u56db\u7687\u5b50\u8fd9\u6b21\u610f\u6025\u56de\u4eac\uff0c\u5c31 \u662f\u56e0\u4e3a\u5728\u5916\u9762\u542c\u5f97\u56fd\u8205\u9686\u79d1\u591a\u7684\u62a5\u8baf\uff0c\u8bf4\u662f\u7687\u4e0a\u5df1\u7acb\u4e86\u9057\u8bcf\uff0c\u81f3\u4e8e\u6307\u5b9a\u8c01\u4eba\uff0c\u5374\u6ca1\u4eba\u77e5\u3002 \u3000\u3000\u5141\u5824\u628a\u7687\u5e1d\u5df2\u7acb\u9057\u8bcf\u7684\u4e8b\u544a\u8bc9\u5fc3\u8179\u6b7b\u58eb\u3002\u5929\u53f6\u6563\u4eba\u9053\uff1a\u201c\u65e2\u7136\u8fd9\u6837\uff0c\u6211\u4eec\u975e\u628a\u8fd9\u9057\u8bcf\u5077 \u5230\u624b\u4e2d\u4e0d\u53ef\u3002\u201d\u5141\u796f\u9053\uff1a\u201c\u5077\u4e0d\u662f\u529e\u6cd5\uff0c\u7687\u4e0a\u53d1\u73b0\u9057\u8bcf\u88ab\u5077\uff0c\u53ef\u4ee5\u518d\u5199\uff0c\u800c\u4e14\u4e5f\u5fc5\u5b9a\u7591\u5fc3\u662f \u6211\u3002\u53ea\u80fd\u5077\u5077\u53bb\u6298\u5b83\u4e00\u770b\uff0c\u597d\u77e5\u9053\u9057\u8bcf\u4e2d\u5199\u7684\u662f\u8c01\u3002\u201d\u5f53\u4e0b\u5929\u53f6\u6563\u4eba\u3001\u4e86\u56e0\u3001\u54c8\u5e03\u9640\u7b49\u90fd\u8bf4 \u613f\u53bb\u3002\u201d\u5141\u796f\u9053\uff1a\u201c\u5077\u5165\u4e7e\u6e05\u5bab\u62c6\u5f00\u9057\u8bcf\uff0c\u8fd9\u4e8b\u975e\u540c\u5c0f\u53ef\u3002\u53bb\u7684\u4eba\u4e0d\u53ef\u591a\uff0c\u53ea\u8981\u8f7b\u529f\u7edd\u597d\u7684 \u4e00\u4e24\u4f4d\u53bb\u5c31\u884c\u4e86\u3002\u8bba\u8f7b\u529f\u662f\u5929\u53f6\u6563\u4eba\u6700\u9ad8\uff0c\u51af\u7433\u6742\u5728\u4f17\u4eba\u4e2d\u9759\u542c\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u968f\u5929\u53f6 \u4f2f\u4f2f\u53bb\u3002\u201d\u516b\u81c2\u795e\u9b54\u9053\uff1a\u201c\u4f60\u53bb\u505a\u4ec0\u4e48\uff1f\u201d\u5929\u53f6\u6563\u4eba\u4e00\u60f3\uff0c\u8fd9\u51af\u7433\u8eab\u8eaf\u5e7c\u5c0f\uff0c\u6b63\u597d\u8d34\u5728\u6a2a\u6881 \u4e0b\u9762\uff0c\u800c\u4e14\u5979\u7684\u8f7b\u529f\u8fd8\u5728\u5927\u529b\u795e\u9b54\u4e4b\u4e0a\uff0c\u6b63\u662f\u4e2a\u597d\u5e2e\u624b\uff0c\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u6211\u5e26\u4f60\u53bb\u3002\u4f60\u53ef\u4e0d\u51c6 \u6dd8\u6c14\u3002\u201d \u3000\u3000\u518d\u8bf4\u5eb7\u7199\u7684\u957f\u5b50\u5141\u5824\u56de\u5230\u5e9c\u4e2d\uff0c\u65e9\u5df2\u9192\u6765\uff0c\u6068\u6068\u4e0d\u5df2\u3002\u5ffd\u62a5\u5927\u5b66\u571f\u738b\u5955\u6e05\u6765\u8bbf\uff0c\u8fd9\u738b\u5955 \u6e05\u4e43\u662f\u5141\u5824\u4e00\u515a\uff0c\u548c\u56fd\u8205\u9686\u79d1\u591a\u540c\u53d7\u5eb7\u7199\u5ba0\u4fe1\uff0c\u4ed6\u4eca\u65e5\u59cb\u77e5\u9053\u7687\u5e1d\u7acb\u4e86\u9057\u8bcf\uff0c\u8d76\u5fd9\u6765\u62a5\u3002\u5141 \u5824\u548c\u5fc3\u8179\u6b7b\u58eb\u5546\u8bae\uff0c\u6240\u89c1\u548c\u5141\u796f\u76f8\u540c\uff0c\u4e5f\u6d3e\u4e86\u4e09\u540d\u8f7b\u529f\u7edd\u9876\u7684\u536b\u58eb\u53bb\u5077\u770b\u9057\u8bcf\u3002\u8fd9\u5c31\u662f\u5510\u6653 \u6f9c\u6240\u89c1\u7684\u4e24\u540d\u5927\u6c49\u4e86\u3002 \u3000\u3000\u5141\u5824\u9063\u6765\u7684\u4e09\u540d\u536b\u58eb\u5546\u8bae\u4e00\u9635\uff0c\u4e24\u4eba\u628a\u98ce\uff0c\u4e00\u4eba\u6cbf\u7740\u6bbf\u4e2d\u5fc3\u7684\u5927\u67f1\u6500\u4e0a\uff0c\u5ffd\u7136\u6a2a\u6881\u540e \u9762\uff0c\u5bd2\u5149\u7535\u5c04\uff0c\u4e24\u53e3\u98de\u5200\u90fd\u63d2\u4e2d\u4e86\u90a3\u540d\u536b\u58eb\uff0c\u767b\u65f6\u8dcc\u4e86\u4e0b\u6765\u3002\u51af\u7433\u500f\u7684\u98de\u51fa\u5c4b\uff0c\u8dc3\u5230\u7409\u7483\u74e6 \u4e0a\uff0c\u968f\u5373\u6709\u4eba\u5927\u547c\u201c\u523a\u5ba2\uff01\u201d\u8fd9\u6b63\u662f\u5929\u53f6\u6563\u4eba\u79fb\u7978\u6c5f\u4e1c\u7684\u8ba1\u7b56\uff0c\u5230\u5f97\u5927\u5185\u7684\u536b\u58eb\u8d76\u6765\uff0c\u4ed6\u548c \u51af\u7433\u65e9\u5df2\u4ece\u82f1\u534e\u6bbf\u4fa7\u8fb9\u8dd1\u51fa\uff0c\u8eb2\u4e0a\u666f\u5c71\u53bb\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u6025\u4ece\u5730\u9053\u8dd1\u51fa\uff0c\u53ea\u89c1\u4e7e\u6e05\u5bab\u5916\uff0c\u5df2\u6709\u5251\u5f71\u5200\u5149\u3002\u4ed6\u8dd1\u5230\u517b\u5fc3\u6bbf\u5916\uff0c\u5eb7\u7199\u4ece\u82b1\u836b\u6df1 \u5904\u63a2\u5934\u51fa\u6765\uff0c\u6b63\u60f3\u62db\u5524\uff0c\u5ffd\u7136\u4e00\u6761\u4eba\u5f71\u75be\u5982\u98de\u9e1f\uff0c\u548c\u5510\u6653\u6f9c\u51e0\u4e4e\u662f\u540c\u65f6\u8d76\u5230\u3002\u592a\u76d1\u559d\u9053\uff1a \u201c\u4ec0\u4e48\u4eba\u6562\u60ca\u5723\u9a7e\uff1f\u201d\u90a3\u4eba\u4e00\u58f0\u957f\u5578\uff0c\u5389\u58f0\u53eb\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u5c31\u662f\u7687\u5e1d\uff01\u201d\u4e00\u63a2\u8eab\uff0c\u547c\u7684\u4e00\u638c \u5411\u5eb7\u7199\u5288\u53bb\uff0c\u4e24\u540d\u592a\u76d1\u6251\u8eab\u6765\u6551\uff0c\u7ed9\u4e00\u638c\u6253\u5f97\u6655\u6b7b\u5730\u4e0a\u3002\u90a3\u4eba\u638c\u950b\u4e0b\u626b\uff0c\u5de6\u811a\u53c8\u8d77\uff0c\u7236\u5b50\u4e4b \u60c5\u51fa\u4e8e\u5929\u6027\uff0c\u5510\u6653\u6f9c\u55d6\u7684\u4e00\u58f0\uff0c\u62d4\u51fa\u5b9d\u5251\uff0c\u90a3\u4eba\u201c\u54e7\u201d\u7684\u4e00\u58f0\uff0c\u817e\u8eab\u6251\u8d77\uff0c\u5510\u6653\u6f9c\u7684\u8ffd\u98ce\u5251 \u6cd5\u8fc5\u6377\u65e0\u4f26\uff0c\u4e00\u62a2\u653b\u52bf\uff0c\u7ef5\u7ef5\u4e0d\u65ad\uff0c\u90a3\u4eba\u7a7a\u624b\u62c6\u4e86\u51e0\u62db\uff0c\u5eb7\u7199\u5df2\u8eb2\u5165\u4e86\u517b\u5fc3\u6bbf\u5185\u3002 \u3000\u3000\u90a3\u4eba\u5927\u543c\u4e00\u58f0\uff0c\u638c\u6cd5\u4e00\u53d8\uff0c\u7ffb\u7ffb\u6eda\u6eda\uff0c\u7adf\u7136\u5728\u5251\u5149\u7b3c\u7f69\u4e2d\u76f4\u6251\u8fc7\u6765\uff0c\u5b8c\u5168\u662f\u4e00\u526f\u62fc\u547d\u795e \u6c14\uff0c\u5510\u6653\u6f9c\u89c1\u5eb7\u7199\u5df2\u7ecf\u8eb2\u5f00\uff0c\u628a\u5251\u4e00\u6536\uff0c\u8df3\u51fa\u5708\u5b50\uff0c\u4e0d\u6599\u90a3\u4eba\u8eab\u6cd5\u5feb\u6781\uff0c\u5510\u6653\u6f9c\u7eb5\u8eab\u4e00\u8df3\uff0c \u4ed6\u5374\u4e58\u52bf\u903c\u4e0a\uff0c\u4e00\u6293\u6293\u7740\u4e86\u5510\u6653\u6f9c\u80a9\u5477\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u51b7\u5bab\u5728\u54ea\u9762\uff1f\u201d\u5510\u6653\u6f9c\u5fc3\u4e2d\u4e00\u9707\uff0c\u7528 \u6613\u5170\u73e0\u6240\u6388\u7684\u6551\u547d\u7edd\u62db\uff0c\u8eab\u5b50\u5411\u524d\u4e00\u8eac\uff0c\u53cd\u624b\u4e00\u5251\u4ece\u80c1\u4e0b\u523a\u51fa\uff0c\u90a3\u4eba\u82e5\u4e0d\u653e\u624b\uff0c\u8fd9\u4e00\u5251\u4fbf\u662f \u7a7f\u5fc3\u523a\u8179\u4e4b\u707e\u3002 \u3000\u3000\u8fd9\u4eba\u6599\u4e0d\u5230\u5510\u6653\u6f9c\u5251\u6cd5\u5982\u6b64\u5389\u5bb3\uff0c\u624b\u638c\u4e00\u64a4\uff0c\u7ffb\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u5510\u6653\u6f9c\u4e5f\u7ed9\u4ed6\u7684\u638c\u529b\u9707\u5f97 \u8e09\u8e09\u8dc4\u8dc4\uff0c\u9000\u4e86\u4e03\u516b\u6b65\u624d\u7a33\u5f97\u4f4f\u8eab\u5f62\uff0c\u80a9\u5934\u72b9\u81ea\u706b\u8fa3\u8fa3\u7684\u523a\u75db\u3002 \u3000\u3000\u5510\u6653\u6f9c\u4e0d\u77e5\uff0c\u8fd9\u4eba\u5374\u662f\u4ed6\u6bcd\u4eb2\u4ee5\u524d\u7684\u672a\u5a5a\u592b\u795d\u5bb6\u6f8e\uff0c\u4ed6\u5931\u8e2a\u4e86\u4e09\u5341\u591a\u5e74\uff0c\u539f\u6765\u662f\u5728\u6c5f\u6e56 \u5230\u5904\u6f2b\u6e38\uff0c\u8bbf\u5e08\u5b66\u6280\uff0c\u6700\u540e\u6295\u5230\u7ec8\u5357\u6d3e\u540d\u5bbf\u6b66\u6210\u5316\u7684\u95e8\u4e0b\uff0c\u82e6\u5b66\u4e86\u5341\u4f59\u5e74\uff0c\u62f3\u5251\u4e24\u95e8\uff0c\u90fd\u6709 \u6781\u6df1\u7684\u9020\u8be3\u3002\u5b66\u6210\u4e4b\u540e\uff0c\u4e5f\u66fe\u4e24\u5230\u4eac\u5e08\uff0c\u4f1a\u89c1\u8fc7\u4faf\u4e09\u53d8\uff0c\u5f97\u77e5\u6d77\u68e0\u88ab\u6253\u5165\u51b7\u5bab\uff0c\u66f4\u662f\u5fc3\u4f24\u3002 \u4ed6\u672c\u60f3\u5165\u5bab\uff0c\u5374\u662f\u4e09\u53d8\u82e6\u82e6\u628a\u4ed6\u529d\u4f4f\u3002\u4faf\u4e09\u53d8\u8bf4\uff1a\u4f60\u529f\u592b\u867d\u597d\uff0c\u4f46\u5bab\u4e2d\u9ad8\u624b\u5982\u4e91\uff0c\u5f04\u5f97\u4e0d \u597d\uff0c\u975e\u4f46\u767d\u9001\u6027\u547d\uff0c\u800c\u4e14\u5bb3\u4e86\u6d77\u68e0\uff0c\u82e6\u529d\u795d\u5bb6\u6f8e\u6b7b\u4e86\u8fd9\u6761\u5fc3\uff0c\u795d\u5bb6\u6f8e\u8fd9\u624d\u6006\u7136\u8fdc\u53bb\u3002 \u3000\u3000\u4f46\u4e09\u5341\u591a\u5e74\u8fc7\u53bb\u4e86\uff0c\u795d\u5bb6\u6f8e\u8fd9\u6761\u5fc3\u4ecd\u662f\u672a\u6b7b\u3002\u4ed6\u5fc3\u60f3\u81ea\u5df1\u548c\u6d77\u68e0\u5df1\u662f\u5782\u8001\u4e4b\u5e74\uff0c\u518d\u4e0d\u76f8 \u89c1\uff0c\u5019\u5f85\u4ed6\u751f\u3002\u56e0\u6b64\u62fc\u4e86\u6027\u547d\u5192\u9669\u5165\u5bab\u3002\u60f3\u4e0d\u5230\u5c31\u5728\u8fd9\u665a\uff0c\u51e0\u4e2a\u7687\u5b50\u6d3e\u536b\u58eb\u8fdb\u6765\uff0c\u5510\u6653\u6f9c\u4e5f \u5728\u5bab\u5185\u3002 \u3000\u3000\u4e14\u8bf4\u795d\u5bb6\u6f8e\u88ab\u5510\u6653\u6f9c\u5251\u6cd5\u6740\u9000\uff0c\u53ea\u89c1\u4e7e\u6e05\u5bab\u91cc\u5200\u706b\u5251\u5f71\uff0c\u5927\u5185\u536b\u58eb\uff0c\u7eb7\u7eb7\u8d76\u6765\u3002\u4ed6\u9009\u62e9 \u50fb\u8def\uff0c\u7ed5\u7740\u5047\u5c71\uff0c\u501f\u7269\u969c\u5f62\uff0c\u89d1\u51c6\u4e00\u4e2a\u843d\u5355\u536b\u58eb\uff0c\u51b7\u4e0d\u9632\u6251\u5c06\u51fa\u53bb\uff0c\u5c06\u4ed6\u4e00\u62f3\u6253\u6655\uff0c\u66f3\u5165\u5047 \u5c71\u6d1e\u53e3\uff0c\u5265\u4e86\u670d\u9970\uff0c\u626e\u6210\u536b\u58eb\uff0c\u5927\u6447\u5927\u6446\u7684\u8d70\u51fa\uff0c\u4e0d\u77e5\u4e0d\u89c9\u8d70\u5230\u4e86\u51b7\u5bab\u524d\u9762\u7684\u8377\u5858\uff0c\u5ffd\u89c1\u4e00 \u7fa4\u5bab\u5973\uff0c\u62ac\u7740\u7af9\u5e8a\uff0c\u5e8a\u4e0a\u8eba\u7740\u4e00\u4e2a\u5973\u4eba\uff0c\u8eab\u4e0a\u76d6\u7740\u767d\u5e03\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u6b63\u662f\u795d\u5bb6\u6f8e\u4e09\u5341\u5e74\u6765\u65e5\u601d\u591c\u60f3\u7684\u60c5\u4eba\uff0c\u5979\u81ea\u5510\u6653\u6f9c\u968f\u5eb7\u7199\u53bb\u540e\uff0c\u54ed\u4e86\u4e00\u4f1a\u53c8\u7b11\u4e86 \u4e00\u4f1a\uff0c\u5ffd\u89c9\u4e07\u5ff5\u7686\u7a7a\uff0c\u597d\u50cf\u8eab\u5b50\u51cc\u865a\uff0c\u751f\u547d\u7684\u529b\u91cf\u5168\u5df2\u6d88\u6563\uff0c\u8ff7\u8ff7\u7cca\u7cca\u7684\u4e00\u8de4\u8dcc\u843d\u5730\u4e0a\u3002 \u3000\u3000\u5eb7\u7199\u4eb2\u5165\u51b7\u5bab\u7684\u4e8b\uff0c\u65e9\u6709\u592a\u76d1\u5f97\u77e5\uff0c\u90a3\u4e9b\u592a\u76d1\uff0c\u6700\u4e3a\u52bf\u5229\uff0c\u5f85\u5eb7\u7199\u53bb\u540e\uff0c\u4ed6\u4eec\u6025\u7980\u77e5\u7ba1 \u7406\u51b7\u5bab\u7684\u5973\u5b98\uff0c\u524d\u6765\u63a2\u89c6\uff0c\u53d1\u73b0\u6d77\u68e0\u6655\u6b7b\u5728\u5730\u4e0a\uff0c\u5546\u8bae\u4e4b\u540e\uff0c\u51b3\u5b9a\u628a\u5979\u8fc1\u51fa\u51b7\u5bab\uff0c\u7136\u540e\u7980\u544a \u7687\u4e0a\u3002 \u3000\u3000\u795d\u5bb6\u6f8e\u4ece\u8377\u5858\u8fb9\u8d70\u8fc7\uff0c\u8fce\u9762\u78b0\u7740\u8fd9\u7fa4\u5bab\u5973\uff0c\u5bab\u5973\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff0c\u4e71\u8dd1\u4e71\u95ef\uff01\u201d\u795d\u5bb6\u6f8e \u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u7af9\u5e8a\u4e0a\u767d\u5e03\u76d6\u7740\u7684\u5973\u4eba\uff0c\u5934\u53d1\u7a00\u758f\u6591\u767d\uff0c\u9762\u8272\u5341\u5206\u53ef\u6015\uff0c\u9732\u51fa\u6765\u7684\u4e24\u53ea\u624b\uff0c \u624b\u6307\u6709\u5982\u9e21\u722a\uff0c\u4e0d\u89c9\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u5fc3\u91cc\u6697\u9053\u6666\u6c14\u3002\u4e09\u5341\u591a\u5e74\u4e0d\u89c1\uff0c\u6d77\u68e0\u65e9\u5df2\u4e0d\u662f\u4ed6\u5fc3\u76ee\u4e2d \u7ee3\u5e74\u7389\u8c8c\u7684\u4f73\u4eba\u4e86\uff0c\u4ed6\u7adf\u7136\u76f8\u9022\u4e0d\u8bc6\uff0c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u8fd9\u5f62\u5bb9\u53ef\u6015\u7684\u8001\u5973\u4eba\u5c31\u662f\u4ed6\u820d\u547d\u6765\u4f1a\u7684 \u60c5\u4eba\u3002 \u3000\u3000\u6d77\u68e0\u5728\u660f\u8ff7\u4e2d\u5ffd\u95fb\u559d\u58f0\uff0c\u8f7b\u8f7b\u7741\u5f00\u4e86\u773c\u76ae\uff0c\u795d\u5bb6\u6f8e\u5df2\u7ecf\u8d70\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u5141\u5824\u6d3e\u6765\u7684\u4e24\u4e2a\u536b\u58eb\u5df2\u88ab\u64d2\u4f4f\uff0c\u5176\u4e2d\u4e00\u540d\uff0c\u56e0\u5148\u524d\u4e2d\u4e86\u51af\u7433\u7684\u593a\u547d\u795e\u5200\uff0c\u6076\u6597\u65f6\u53c8 \u7528\u529b\u8fc7\u5ea6\uff0c\u5f85\u5230\u675f\u624b\u53d7\u64d2\u4e4b\u65f6\uff0c\u5df2\u662f\u6bd2\u53d1\u8eab\u6b7b\u3002 \u3000\u3000\u5eb7\u7199\u7687\u5e1d\u4eb2\u81ea\u5ba1\u95ee\u88ab\u64d2\u7684\u536b\u571f\uff0c\u5ba1\u51fa\u8fd9\u536b\u58eb\u7adf\u662f\u81ea\u5df1\u7684\u957f\u5b50\u6d3e\u6765\uff0c\u8d6b\u7136\u9707\u6012\uff0c\u7acb\u523b\u4e0b\u8bcf \u5e9f\u4e86\u5141\u5824\u7684\u7687\u5b50\u5c01\u53f7\uff0c\u4ea4\u5b97\u4eba\u5e9c\u770b\u7ba1\u3002 \u3000\u3000\u5f53\u665a\u7eb7\u7eb7\u6270\u6270\uff0c\u5f85\u4e8b\u60c5\u5e73\u606f\uff0c\u5df2\u662f\u56db\u66f4\u3002\u5eb7\u7199\u7559\u5510\u6653\u6f9c\u5728\u517b\u5fc3\u6bbf\u4f4f\u4e86\u4e00\u665a\uff0c\u7b2c\u4e8c\u5929\u5bab\u4e2d \u7684\u7ba1\u4e8b\u592a\u76d1\u6765\u62a5\uff0c\u8bf4\u662f\u88ab\u7981\u5728\u51b7\u5bab\u7684\u90a3\u4e2a\u5bab\u5a25\u6628\u591c\u8eab\u4ea1\uff0c\u5eb7\u7199\u542c\u4e86\uff0c\u6168\u53f9\u4e0d\u5df2\uff0c\u5429\u5490\u4ee5\u6dd1\u5983 \u4e4b\u793c\u539a\u846c\uff0c\u53ec\u5510\u6653\u6f9c\u8fdb\u5165\u4e66\u623f\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u7b49\u4f60\u7684\u6bcd\u4eb2\u4e86\uff0c\u5979\u6628\u591c\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d\u5510 \u6653\u6f9c\u672c\u5df2\u4f24\u5fc3\uff0c\u8fd9\u65f6\u5168\u8eab\u9ebb\u6728\uff0c\u6b32\u54ed\u65e0\u6cea\uff0c\u8fc7\u4e86\u8bb8\u4e45\uff0c\u624d\u6062\u590d\u8fc7\u6765\uff0c\u54d1\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u8d70 \u4e86\u3002\u201d\u5eb7\u7199\u9053\uff1a\u201c\u4f60\u7b49\u4e00\u7b49\u3002\u201d\u53eb\u4e86\u4e00\u4e2a\u9ec4\u95e8\u5b98\u6765\uff0c\u5199\u4e86\u4e00\u7eb8\u8bcf\u4e66\u7ed9\u5510\u6653\u6f9c\uff0c\u53eb\u9ec4\u95e8\u5b98\u5e26\u5510 \u6653\u6f9c\u53bb\u89c1\u5141\u796f\u3002 \u3000\u3000\u5510\u6653\u6f9c\u8ff7\u8ff7\u60af\u60af\u7684\u51fa\u4e86\u5bab\u95e8\uff0c\u5230\u4e86\u5141\u796f\u7684\u7687\u5e9c\uff0c\u9ec4\u95e8\u5b98\u63a8\u4ed6\u4e00\u628a\uff0c\u8bf4\u9053\uff1a\u201c\u5230\u4e86\u3002\u201d\u4ed6 \u624d\u5982\u68a6\u521d\u9192\u7684\u8e6c\u4e0b\u9a6c\u8f66\u3002\u9ec4\u95e8\u5b98\u6697\u60f3\uff1a\u7687\u4e0a\u4e5f\u771f\u8001\u7cca\u6d82\u4e86\uff0c\u8fd9\u6837\u75f4\u5446\u7684\u4eba\u5982\u4f55\u80fd\u9009\u4f5c\u536b\u58eb\u3002 \u3000\u3000\u5510\u6653\u6f9c\u5728\u5bab\u4e2d\u5df2\u5c06\u9762\u4e0a\u6613\u5bb9\u7684\u836f\u818f\u6d17\u53bb\uff0c\u53cc\u9b54\u4e00\u89c1\uff0c\u5927\u4e3a\u5403\u60ca\u3002\u53ef\u662f\u4ed6\u6709\u9ec4\u95e8\u5b98\u5e26\u6765\uff0c \u542c\u9ec4\u95e8\u5b98\u8bf4\u4ed6\u8fd8\u662f\u5f97\u5ba0\u7684\u536b\u58eb\uff0c\u53ea\u597d\u8ba9\u4ed6\u53bb\u89c1\u5141\u796f\u3002 \u3000\u3000\u5141\u796f\u89c1\u4e86\u5510\u6653\u6f9c\u4e5f\u751a\u4e3a\u60ca\u5f02\uff0c\u4e86\u56e0\u548c\u54c8\u5e03\u9640\u968f\u4f8d\u5728\u4fa7\uff0c\u5168\u7741\u5927\u4e86\u773c\u775b\u3002\u9ec4\u95e8\u5b98\u53bb\u540e\uff0c\u4e86 \u56e0\u7981\u4e0d\u4f4f\u9053\uff1a\u201c\u5510\u6653\u6f9c\uff0c\u4f60\u5230\u5e95\u662f\u5f04\u4ec0\u4e48\u7384\u865a\uff1f\u201d\u5141\u796f\u5374\u6446\u4e86\u6446\u624b\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4ee5\u5510\u5144\u7684 \u9ad8\u624d\uff0c\u6b63\u8be5\u4ece\u6b63\u9014\u51fa\u8eab\u3002\u5510\u5144\u662f\u4f55\u65f6\u5165\u5bab\uff1f\u7687\u4e0a\u8eab\u4f53\u53ef\u597d\uff1f\u201d\u5510\u6653\u6f9c\u7b54\u9053\uff1a\u201c\u597d\u3002\u201d\u987f\u4e86\u4e00 \u987f\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6562\u8bf7\u8d1d\u52d2\u5c4f\u9000\u5de6\u53f3\u3002\u201d\u5141\u796f\u53d8\u8272\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u7686\u662f\u5fc3\u8179\uff0c\u5510\u5144\u6709\u8bdd\uff0c\u4f46\u8bf4 \u65e0\u59a8\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u8fd8\u662f\u54b1\u4eec\u4e24\u4eba\u8c08\u8c08\u7684\u597d\u3002\u201d\u4e86\u56e0\u6012\u9053\uff1a\u201c\u4f60\u6562\u5982\u6b64\u65e0\u793c\uff01\u201d\u5141\u796f\u773c\u73e0 \u4e00\u8f6c\uff0c\u6b63\u60f3\u53eb\u4e86\u56e0\u548c\u54c8\u5e03\u9640\u9000\u4e0b\uff0c\u5ffd\u7136\u697c\u4e0b\u4e00\u9635\u55a7\u54d7\uff0c\u9ad8\u547c\u523a\u5ba2\uff0c\u4e86\u56e0\u5012\u63d0\u7985\u6756\uff0c\u970d\u7684\u7acb\u8d77 \u8eab\u6765\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u547c\u547c\u98ce\u54cd\uff0c\u4e66\u623f\u7684\u95e8\uff0c\u500f\u5ffd\u9707\u5f00\uff0c\u4e00\u4eba\u624b\u63d0\u957f\u5251\uff0c\u5982\u98de \u6251\u8fdb\uff01\u5510\u6653\u6f9c\u4e00\u770b\uff0c\u6b63\u662f\u6628\u665a\u90a3\u4eba\u3002 \u3000\u3000\u795d\u5bb6\u6f8e\u957f\u5251\u4e00\u6296\uff0c\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u5237\u7684\u5411\u5141\u796f\u523a\u53bb\uff0c\u4e86\u56e0\u7985\u6756\u4e00\u7acb\uff0c\u53ea\u542c\u5f97\u53ee\u5f53\u4e00 \u58f0\uff0c\u90a3\u4eba\u7684\u5251\u7ed9\u8361\u4e86\u5f00\u53bb\uff0c\u5251\u950b\u4e00\u504f\uff0c\u76f4\u5954\u5510\u6653\u6f9c\u80c1\u4e0b\uff0c\u5510\u6653\u6f9c\u8eab\u56de\u6b65\u6362\uff0c\u5374\u4e0d\u8fd8\u62db\uff0c\u54c8\u5e03 \u9640\u5de6\u638c\u6a2a\u626b\uff0c\u53f3\u62f3\u6363\u51fa\uff0c\u62a2\u653b\u4ed6\u7684\u7a7a\u95e8\uff0c\u90a3\u4eba\u63a5\u4e86\u4e00\u62db\uff0c\u77e5\u9053\u4e86\u56e0\u4e0e\u54c8\u5e03\u9640\u7684\u529f\u529b\uff0c\u90fd\u5728\u81ea \u5df1\u4e4b\u4e0a\uff0c\u865a\u5288\u4e24\u5251\uff0c\u500f\u7684\u4ece\u7a97\u53e3\u53c8\u7a7f\u51fa\u53bb\u3002\u54c8\u5e03\u9640\u9996\u5148\u8ffd\u51fa\uff0c\u5510\u6653\u6f9c\u5c1a\u5728\u8fdf\u7591\uff0c\u4e86\u56e0\u559d\u9053\uff1a \u201c\u4f60\u65e2\u662f\u5927\u5185\u536b\u58eb\uff0c\u4e3a\u4f55\u5374\u4e0d\u8fd8\u62db\uff1f\u201d\u5de6\u624b\u659c\u659c\u4e00\u5e26\uff0c\u5510\u6653\u6f9c\u731d\u4e0d\u53ca\u9632\uff0c\u7ed9\u4ed6\u7275\u51fa\u697c\u5916\uff0c\u6536 \u52bf\u4e0d\u53ca\uff0c\u98d8\u8eab\u4e0b\u5730\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u5175\u5203\u78b0\u78d5\u4e4b\u58f0\uff0c\u6709\u4eba\u5927\u58f0\u60ca\u53eb\uff01 \u3000\u3000\u5510\u6653\u6f9c\u51dd\u795e\u4e00\u770b\uff0c\u4e0d\u7981\u53eb\u82e6\uff0c\u5ead\u9662\u91cc\u5251\u5149\u970d\u970d\uff0c\u4eba\u5f71\u7a7f\u68ad\uff0c\u5173\u4e1c\u56db\u4fa0\u5168\u90fd\u5230\u4e86\u3002\u66f4\u7cdf\u7684 \u662f\u81ea\u5df1\u7684\u4e1a\u5e08\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\u5e26\u7740\u5973\u513f\uff0c\u4e5f\u6b63\u5728\u5ead\u56ed\u4e2d\u6076\u6597\u3002\u6768\u4ef2\u82f1\u9aa4\u89c1\u6653\u6f9c\uff0c\u60ca\u53eb\u4e00\u58f0\uff0c \u4ece\u97e9\u91cd\u5c71\u7684\u8f9f\u4e91\u9504\u4e0b\u7fe9\u7136\u63a0\u51fa\uff0c\u5510\u6653\u6f9c\u53eb\u9053\uff1a\u201c\u5e08\u5085\uff0c\u662f\u6211\uff01\u201d\u795d\u5bb6\u6f8e\u5927\u53eb\u9053\uff1a\u201c\u4ed6\u662f\u5978 \u7ec6\uff01\u201d\u6768\u4ef2\u82f1\u9762\u8272\u500f\u53d8\uff0c\u547c\u7684\u4e00\u638c\u5288\u6765\uff01 \u3000\u3000\u539f\u6765\u5173\u4e1c\u56db\u4fa0\u81ea\u4ece\u90a3\u6b21\u5728\u9634\u5c71\u548c\u53cc\u9b54\u6076\u6597\uff0c\u529f\u8d25\u5782\u6210\uff0c\u53cd\u800c\u5403\u4e86\u5927\u4e8f\uff0c\u5341\u4e8c\u5e74\u6765\uff0c\u82e6\u5fc3 \u7ec3\u6280\uff0c\u603b\u60f3\u548c\u53cc\u9b54\u518d\u8f83\u77ed\u957f\uff0c\u4f46\u53cc\u9b54\u5728\u56db\u7687\u5b50\u5e9c\u4e2d\uff0c\u5173\u4e1c\u56db\u4fa0\u4e0d\u6562\u8f7b\u53bb\u3002\u8fd9\u6b21\u4ed6\u4eec\u6253\u542c\u5f97\u56db \u7687\u5b50\u5e26\u4e86\u4e00\u73ed\u9ad8\u624b\u51fa\u95e8\uff0c\u53ea\u7559\u4e0b\u53cc\u9b54\u548c\u4e00\u4e2a\u59d3\u97e9\u7684\u9547\u5b88\u3002\u8fd9\u65f6\u5173\u4e1c\u56db\u4fa0\u7684\u72ec\u95e8\u529f\u592b\uff0c\u6bd4\u524d\u53c8 \u9ad8\u4e86\u8bb8\u591a\uff0c\u4e8e\u662f\u8054\u8882\u6765\u4eac\uff0c\u51c6\u5907\u5927\u95f9\u7687\u5e9c\uff0c\u62a5\u90a3\u5929\u7684\u4e00\u7bad\u4e4b\u4ec7\u3002 \u3000\u3000\u6070\u5de7\u6768\u4ef2\u82f1\u548c\u5973\u513f\u6768\u67f3\u9752\uff0c\u4e3a\u4e86\u5bfb\u8bbf\u6653\u6f9c\uff0c\u5728\u6c5f\u6e56\u5356\u827a\uff0c\u4e5f\u5230\u4e86\u4eac\u5e08\u3002\u4e00\u65e5\uff0c\u5728\u5929\u6865 \uff08\u5317\u4eac\u7684\u4e00\u5904\u5730\u540d\uff0c\u4e3a\u6c5f\u6e56\u827a\u4eba\u805a\u96c6\u4e4b\u5730\uff09\u9047\u89c1\u4e86\u795d\u5bb6\u6f8e\uff0c\u4e24\u4eba\u672c\u6765\u76f8\u8bc6\uff0c\u805a\u5728\u4e00\u8d77\u3002\u8fc7\u4e86 \u51e0\u65e5\uff0c\u53c8\u78b0\u5230\u4e86\u5173\u4e1c\u56db\u4fa0\uff0c\u5173\u4e1c\u56db\u4fa0\u9080\u795d\u5bb6\u6f8e\u540c\u6597\u53cc\u9b54\uff0c\u795d\u5bb6\u6f8e\u90a3\u65f6\u6b63\u4e00\u5fc3\u60f3\u5165\u7687\u5bab\uff0c\u5a49\u8a00 \u62d2\u4e86\uff0c\u5173\u4e1c\u56db\u4fa0\u5f88\u4e0d\u9ad8\u5174\u3002\u5230\u795d\u5bb6\u6f8e\u5931\u8d25\u56de\u6765\uff0c\u4ed6\u4eec\u7ec6\u95ee\u60c5\u7531\uff0c\u624d\u77e5\u9053\u795d\u5bb6\u6f8e\u6709\u8fd9\u4e00\u6bb5\u4f24\u5fc3 \u4e4b\u4e8b\u3002\u4e8e\u662f\u795d\u5bb6\u6f8e\u65e7\u4e8b\u91cd\u63d0\uff0c\u548c\u5173\u4e1c\u56db\u4fa0\u4e0e\u6768\u4ef2\u82f1\u7236\u5973\uff0c\u5f53\u65e5\u5c31\u5230\u56db\u7687\u5b50\u5e9c\u4e2d\u9080\u6597\u53cc\u9b54\u3002 \u3000\u3000\u5510\u6653\u6f9c\u89c1\u5e08\u5085\u4e0d\u8c05\uff0c\u638c\u98ce\u5288\u9762\uff0c\u614c\u5fd9\u95ea\u8fc7\uff0c\u6768\u4ef2\u82f1\u638c\u6cd5\u96c4\u5947\uff0c\u8de8\u524d\u4e00\u6b65\uff0c\u4e00\u4e2a\u201c\u5de6\u53f3\u5f00 \u5f13\u201d\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u5510\u6653\u6f9c\u907f\u65e0\u53ef\u907f\uff0c\u672c\u80fd\u7684\u51fa\u638c\u76f8\u6297\uff0c\u4e00\u62db\u201c\u89e3\u7532\u8131\u888d\u201d\uff0c\u628a\u6768\u4ef2\u82f1\u7684\u638c\u52bf \u62c6\u4e86\uff0c\u817e\u8eab\u8df3\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u6768\u4ef2\u82f1\u89c1\u4ed6\u6b66\u529f\u5927\u8fdb\uff0c\u63c9\u8eab\u8fdb\u51fb\uff0c\u6768\u67f3\u9752\u5927\u53eb\u201c\u7239\u7239\uff01\u201d\u6768\u4ef2\u82f1 \u51dd\u8eab\u6536\u638c\uff0c\u54c8\u5e03\u9640\u547c\u7684\u6251\u6765\uff0c\u6325\u81c2\u4e00\u683c\uff0c\u628a\u6768\u4ef2\u82f1\u9707\u9000\u51e0\u6b65\u3002\u6768\u67f3\u9752\u6025\u5c55\u795e\u5f39\u7edd\u6280\uff0c\u63a9\u62a4\u7236 \u4eb2\u3002 \u3000\u3000\u5510\u6653\u6f9c\u7a81\u906d\u5de8\u53d8\uff0c\u832b\u7136\u4e0d\u77e5\u6240\u63aa\u3002\u7384\u98ce\u9053\u4eba\u6027\u5b50\u6700\u70c8\uff0c\u95ee\u795d\u5bb6\u6f8e\u9053\uff1a\u201c\u4f60\u8bdd\u53ef\u771f\uff1f\u201d\u795d \u5bb6\u6f8e\u9053\uff1a\u201c\u4ed6\u6628\u665a\u8fd8\u548c\u7687\u5e1d\u8001\u513f\u540c\u5728\u4e00\u8d77\uff0c\u6211\u82e5\u4e0d\u662f\u9003\u8d70\u5f97\u5feb\uff0c\u65e9\u5df2\u4e27\u5728\u4ed6\u7684\u5251\u4e0b\u3002\u201d\u7384\u98ce \u9053\u4eba\u52c3\u7136\u5927\u6012\uff0c\u5ffd\u800c\u4e00\u60f3\uff1a\u8fd9\u795d\u5bb6\u6f8e\u6b66\u529f\u4e0d\u5728\u6211\u4e0b\uff0c\u600e\u7684\u5374\u4f1a\u4e0d\u662f\u6653\u6f9c\u5bf9\u624b\uff1f\u4e00\u63a0\u800c\u524d\uff0c\u53f3 \u624b\u957f\u5251\uff0c\u5de6\u624b\u94c1\u62d0\uff0c\u4e00\u9f50\u53d1\u51fa\u3002\u5510\u6653\u6f9c\u77e5\u9053\u7384\u98ce\u624b\u5e95\u6781\u8fa3\uff0c\u4e07\u96be\u95ea\u907f\uff0c\u53ea\u597d\u62d4\u5251\u62b5\u6321\uff0c\u7384\u98ce \u7684\u62db\u6570\u672c\u6765\u53ef\u865a\u53ef\u5b9e\uff0c\u5b58\u5fc3\u8981\u8bd5\u6653\u6f9c\u6b66\u827a\uff0c\u5510\u6653\u6f9c\u4e0d\u77e5\u5c31\u91cc\u3002\u7adf\u628a\u5929\u5c71\u7edd\u5999\u7684\u9632\u8eab\u5251\u6cd5\u65bd\u5c55 \u51fa\u6765\uff0c\u5de6\u4e00\u5251\u201c\u51b0\u7f51\u89e3\u51bb\u201d\uff0c\u53f3\u4e00\u5251\u201c\u9f99\u8dc3\u6df1\u6e0a\u201d\uff0c\u5e26\u5b88\u5e26\u653b\uff0c\u7adf\u628a\u7384\u98ce\u7684\u94c1\u62d0\u5c01\u51fa\u4e86\u5916\uff0c \u7384\u98ce\u8fd9\u624d\u77e5\u9053\u795d\u5bb6\u6f8e\u6240\u8bf4\u975e\u5047\uff0c\u5251\u62db\u500f\u53d8\uff0c\u66f4\u4e0d\u4f6f\u653b\uff0c\u5251\u5251\u5411\u5510\u6653\u6f9c\u8981\u5bb3\u523a\u6765\uff01 \u3000\u3000\u5510\u6653\u6f9c\u8fde\u6321\u6570\u5251\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u5085\uff0c\u5e08\u4f2f\uff0c\u6211\u6709\u8bdd\u8bf4\u3002\u201d\u7384\u98ce\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5fd8\u601d\u8d1f\u4e49 \u7684\u53db\u5f92\uff0c\u8c01\u8fd8\u542c\u4f60\u8bf4\u8bdd\u3002\u201d\u5251\u62db\u50ac\u7d27\uff0c\u75be\u82e5\u60ca\u89ce\u3002\u5510\u6653\u6f9c\u95ea\u5f97\u7a0d\u8fdf\uff0c\u7ed9\u7384\u98ce\u5237\u7684\u4e00\u5251\u523a\u7a7f\u8863 \u8896\uff0c\u903c\u5f97\u6a2a\u5251\u4e00\u626b\uff0c\u4e0a\u4e0b\u4e00\u8361\uff0c\u7528\u7684\u7adf\u662f\u5929\u5c71\u5251\u6cd5\u4e2d\u6700\u7cbe\u5999\u7684\u62db\u6570\uff0c\u5510\u6653\u6f9c\u672c\u610f\u539f\u662f\u9632\u8eab\uff0c \u7ade\u4e0d\u81ea\u77e5\u8fd9\u4e00\u62db\u5a01\u529b\u5947\u5927\uff0c\u4e00\u53d1\u96be\u6536\uff0c\u53ea\u542c\u5f97\u5f53\u7684\u4e00\u58f0\uff0c\u7384\u98ce\u5de6\u624b\u7684\u94c1\u62d0\uff0c\u7adf\u7ed9\u6e38\u9f99\u5b9d\u5251\u622a \u53bb\u4e00\u6bb5\uff01\u7384\u98ce\u5446\u4e86\u4e00\u5446\uff0c\u5510\u6653\u6f9c\u6251\u5730\u8df3\u51fa\uff0c\u90a3\u6599\u773c\u524d\u4e00\u4eae\uff0c\u9152\u9999\u6251\u9f3b\uff0c\u6717\u6708\u7985\u5e08\u55b7\u9152\u6210\u7ec3\uff0c \u76f4\u53d6\u5510\u6653\u6f9c\u9762\u4e0a\u53cc\u775b\u3002\u5510\u6653\u6f9c\u95ea\u5934\u6025\u907f\uff0c\u6717\u6708\u7985\u5e08\u547c\u7684\u4e00\u55b7\uff0c\u767d\u7ec3\u5316\u6210\u9152\u6d6a\uff0c\u6709\u5982\u94c5\u5f39\uff0c\u5510 \u6653\u6f9c\u4e0a\u8863\u88ab\u5c04\u5f97\u6709\u5982\u8749\u5de2\uff0c\u4e07\u91cc\u8ffd\u98ce\u67f3\u5148\u5f00\u8eab\u98de\u5982\u7bad\uff0c\u500f\u5ffd\u63a0\u5230\uff0c\u4e00\u4f38\u624b\u628a\u5510\u6653\u6f9c\u8863\u88f3\u6293 \u7834\uff0c\u7687\u5e1d\u6240\u5199\u7684\u4eb2\u7b14\u51fd\u4ef6\uff0c\u7adf\u88ab\u62a2\u53bb\u3002 \u3000\u3000\u5510\u6653\u6f9c\u5446\u82e5\u6728\u9e21\uff0c\u8fde\u58f0\u8bf4\u9053\uff1a\u201c\u6211\uff0c\u6211\uff0c\u2026\u2026\u201d\u58f0\u8c03\u54fd\u54bd\u60b2\u82e6\uff0c\u8bf4\u4e0d\u4e0b\u53bb\u3002\u6768\u67f3\u9752\u9053\uff1a \u201c\u6211\u4ec0\u4e48\uff1f\u201d\u53c8\u7231\u53c8\u6068\uff0c\u5f20\u5f00\u5f39\u5f13\uff0c\u4e00\u5f39\u6253\u53bb\uff0c\u5510\u6653\u6f9c\u5fc3\u4e71\u795e\u4f24\uff0c\u5df2\u4e0d\u77e5\u8d70\u907f\uff0c\u4e86\u56e0\u548c\u5c1a\u5927 \u543c\u4e00\u58f0\uff0c\u7985\u6756\u4e00\u62a1\uff0c\u5f39\u4e38\u5012\u5c04\uff0c\u4ed6\u89c1\u6768\u67f3\u9752\u751f\u5f97\u7f8e\u8c8c\uff0c\u6076\u5ff5\u7a81\u751f\uff0c\u7985\u6756\u70b9\u5730\uff0c\u817e\u8eab\u98de\u8d77\uff0c\u5de6 \u624b\u5f20\u5f00\uff0c\u5b9b\u5982\u5de8\u9e70\u6251\u5154\uff0c\u4e00\u6293\u5c31\u5411\u6768\u67f3\u9752\u6293\u6765\uff01\u7384\u98ce\u5927\u60ca\uff0c\u5251\u8d70\u504f\u950b\uff0c\u9752\u5149\u4e00\u95ea\uff0c\u5251\u5c16\u76f4\u523a \u4e86\u56e0\u5934\u9888\u540e\u810a\u9aa8\u4e0a\u7684\u201c\u5929\u9699\u7a74\u201d\uff0c\u4e86\u56e0\u4ee5\u6756\u4e3a\u8f74\uff0c\u811a\u8ddf\u4e00\u8f6c\uff0c\u4ecd\u4e0d\u653e\u677e\uff0c\u5de6\u624b\u6293\u5230\u6768\u67f3\u9752\u80cc \u5fc3\u3002\u7384\u98ce\u5251\u6cd5\u8fc5\u6377\uff0c\u66f4\u4e0d\u6536\u62db\uff0c\u5251\u5c16\u4e00\u98a4\uff0c\u8ff3\u5316\u6210\u201c\u6768\u679d\u6ef4\u9732\u201d\u7684\u62db\u6570\uff0c\u659c\u70b9\u4e86\u56e0\u810a\u9aa8\u7684 \u201c\u7cbe\u4fc3\u7a74\u201d\uff0c\u4e86\u56e0\u89c1\u4ed6\u5251\u6cd5\u7cbe\u5999\uff0c\u4e0d\u6562\u653e\u8086\uff0c\u8eab\u5b50\u4e00\u8f6c\uff0c\u62a1\u6756\u63a5\u62db\u3002\u4e86\u56e0\u529f\u529b\u6df1\u539a\uff0c\u6756\u98ce\u5982 \u5200\uff0c\u7384\u98ce\u8fde\u6321\u5341\u62db\uff0c\u81ea\u77e5\u4e0d\u654c\u3002\u8fd9\u65f6\u6ee1\u5ead\u9662\u6df7\u6218\uff0c\u56db\u4fa0\u8fd9\u8fb9\u7684\u4eba\u5df2\u5904\u4e0b\u98ce\uff0c\u6768\u67f3\u9752\u66f4\u662f\u5c8c\u5c8c \u53ef\u5371\uff0c\u7384\u98ce\u5927\u53eb\u9053\uff1a\u201c\u8d70\uff01\u201d\u5411\u8428\u5929\u523a\u3001\u8428\u5929\u90fd\u5206\u523a\u4e24\u5251\uff0c\u5c06\u67f3\u5148\u5f00\u7b49\u4eba\u6551\u4e86\u51fa\u6765\u3002\u6768\u4ef2\u82f1 \u643a\u7740\u5973\u513f\uff0c\u9760\u795d\u5bb6\u6f8e\u5728\u540e\u63a9\u62a4\uff0c\u4e5f\u8df3\u51fa\u56f4\u5899\u3002\u4e86\u56e0\u5012\u63d0\u7985\u6756\uff0c\u8fd8\u60f3\u8ffd\u8d76\uff0c\u5141\u796f\u5728\u697c\u4e0a\u501a\u680f\u558a \u9053\uff1a\u201c\u8ba9\u4ed6\u4eec\u8d70\u5427\uff01\u201d\u539f\u6765\u5141\u796f\u6b63\u4e0e\u5929\u53f6\u6563\u4eba\u8c08\u8bba\u9057\u8bcf\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u6709\u4e8b\uff0c\u6df1\u6015\u4e8b\u60c5\u95f9\u5927\uff0c\u88ab \u5176\u4ed6\u7687\u5b50\u4e58\u673a\u653b\u51fb\uff0c\u6240\u4ee5\u626c\u58f0\u6b62\u6597\uff0c\u62db\u56de\u4e86\u56e0\u7b49\u4eba\u3002 \u3000\u3000\u5510\u6653\u6f9c\u8fd9\u65f6\u5fc3\u4e71\u5982\u9ebb\uff0c\u672c\u6765\u4ed6\u6628\u665a\u4e4d\u77e5\u8eab\u4e16\uff0c\u5df2\u662f\u54c0\u75db\u6b32\u7edd\uff0c\u4e07\u5ff5\u4ff1\u7070\uff0c\u60f3\u4e0d\u5230\u5982\u4eca\u53c8 \u88ab\u4e1a\u5e08\u8bef\u4f1a\uff0c\u4ed6\u5c31\u662f\u60f3\u6b7b\u4e5f\u4e0d\u80fd\u5c31\u6b7b\u3002\u83ab\u8bf4\u6768\u4ef2\u82f1\u548c\u5173\u4e1c\u56db\u4fa0\u5bf9\u81ea\u5df1\u6069\u6df1\u4e49\u91cd\uff0c\u800c\u4e14\u5192\u7136\u4e00 \u6b7b\uff0c\u6076\u540d\u66f4\u96be\u6d17\u8131\uff0c\u4ed6\u4e3e\u5251\u7684\u624b\u7f13\u7f13\u653e\u4e86\u4e0b\u6765\u3002\u4e86\u56e0\u51b7\u7b11\u9053\uff1a\u201c\u8d3c\u4eba\u5df2\u7ecf\u53bb\u4e86\uff0c\u4f60\u8fd8\u5446\u5728\u8fd9 \u91cc\u505a\u751a\uff1f\u201d\u5510\u6653\u6f9c\u60b2\u6124\u4e4b\u6781\uff0c\u7eb5\u8eab\u4e00\u8dc3\uff0c\u53eb\u9053\uff1a\u201c\u6211\u7eb5\u6b7b\u4e5f\u4e0d\u6c42\u4f60\u4eec\u3002\u201d\u8df3\u8fc7\u5899\u5934\u8d70\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u832b\u7136\u7684\u8dd1\u51fa\u5317\u95e8\u3002\u57ce\u5899\u4e0a\u5ffd\u7136\u6709\u4eba\u53eb\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u8fd8\u6562\u8ffd\u6765\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u731b\u7136\u4e00\u60ca\uff0c\u7acb\u5728\u57ce\u5899\u4e0a\u53d1\u8bdd\u7684\u7adf\u662f\u6768\u4ef2\u82f1\uff0c\u539f\u6765\u7384\u98ce\u8fd9\u4e00\u884c\u4eba\u9003\u51fa\u7687\u5e9c\uff0c\u4e5f\u662f\u6cbf \u7740\u8fd9\u4e00\u6761\u8def\uff0c\u4ed6\u4eec\u8de8\u4e0a\u57ce\u5899\uff0c\u6b63\u60f3\u7ffb\u51fa\u57ce\u5916\uff0c\u6768\u4ef2\u82f1\u62c5\u5f53\u6bbf\u540e\uff0c\u84e6\u89c1\u5510\u6653\u6f9c\u4e00\u4eba\u75be\u5954\uff0c\u53ea\u9053 \u4ed6\u662f\u524d\u6765\u8ffd\u51fb\u3002 \u3000\u3000\u5510\u6653\u6f9c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u5085\uff0c\u4f60\u5bb9\u6211\u7ec6\u8bf4\u3002\u201d\u6768\u67f3\u9752\u8d70\u5728\u524d\u5934\uff0c\u5939\u5728\u7384\u98ce\u9053\u4eba\u548c\u6717\u6708\u7985\u5e08 \u4e4b\u95f4\uff0c\u95fb\u58f0\u6b62\u6b65\u3002\u542c\u5f97\u5510\u6653\u6f9c\u8bed\u58f0\u5341\u5206\u60b2\u82e6\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u8ba9\u4ed6\u4e00\u8bf4\u5427\uff0c\u4e5f\u8bb8\u53e6\u6709\u522b\u60c5\u3002\u201d \u3000\u3000\u7384\u98ce\u9053\uff1a\u201c\u4e0d\u80fd\u5c06\u6027\u547d\u513f\u620f\uff01\u201d \u3000\u3000\u626c\u58f0\u53eb\u9053\uff1a\u201c\u6768\u5144\uff0c\u63d0\u9632\u6709\u4eba\u8ffd\u6765\uff01\u201d\u6768\u4ef2\u82f1\u66f3\u8d77\u5f39\u5f13\uff0c\u535c\u535c\u4e24\u5f39\uff0c\u5510\u6653\u6f9c\u5931\u9b42\u843d\u9b44\uff0c \u65e0\u610f\u95ea\u907f\uff0c\u989d\u89d2\u81c2\u5f2f\uff0c\u5404\u4e2d\u4e00\u9897\uff0c\u8dcc\u5728\u5730\u4e0a\u3002\u53ea\u542c\u5f97\u6768\u4ef2\u82f1\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u72af\u4e86\u5e08\u95e8\u5927\u6212\uff0c \u6211\u7edd\u4e0d\u9976\u4f60\uff01\u201d\u5510\u6653\u6f9c\u7ad9\u8d77\u6765\u65f6\uff0c\u6768\u4ef2\u82f1\u8fd9\u4e00\u884c\u4eba\u5df2\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u8e3d\u8e3d\u72ec\u884c\uff0c\u60f3\u8d77\u5d69\u9633\u95e8\u4e0b\uff0c\u6212\u5f8b\u6700\u4e25\uff0c\u81ea\u5df1\u5165\u95e8\u4e4b\u65e5\uff0c\u5c31\u66fe\u9886\u8fc7\u5341\u4e8c\u6212\u6761\uff0c\u5176\u4e2d \u7b2c\u56db\u6761\u4e0d\u8bb8\u6cbe\u5b98\u8fd1\u5e9c\uff0c\u7b2c\u5341\u4e8c\u6761\u4e0d\u8bb8\u6b3a\u5e08\u706d\u7956\uff0c\u81ea\u5df1\u5168\u90fd\u72af\u4e86\u3002\u5e08\u5085\u60f3\u662f\u6015\u4e86\u56e0\u4ed6\u4eec\u8ddf\u5728\u540e \u9762\uff0c\u8981\u4e0d\u7136\u4e00\u5b9a\u5c06\u81ea\u5df1\u6349\u4e86\u3002\u60f3\u5230\u8fd9\u671b\uff0c\u4e0d\u5bd2\u800c\u6817\u3002\u4ed6\u4e0d\u662f\u6015\u6b7b\uff0c\u800c\u662f\u6015\u8fd9\u4e00\u6bb5\u51a4\u60c5\uff0c\u6c38\u96be \u6e05\u767d\u3002 \u3000\u3000\u5510\u6653\u6f9c\u5728\u897f\u5c71\u50e7\u4f1a\u51a5\u601d\u9ed8\u60f3\uff0c\u8fd9\u832b\u832b\u4eba\u6d77\uff0c\u81ea\u5df1\u4e3e\u76ee\u65e0\u4eb2\uff0c\u8fde\u4e00\u4e2a\u53ef\u8bc9\u8877\u80a0\u7684\u670b\u53cb\u90fd\u6ca1 \u6709\uff0c\u4e0d\u7981\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u80fd\u81ea\u5df1\u3002\u601d\u6765\u60f3\u53bb\uff0c\u84e6\u7136\u60f3\u8d77\u4e86\u5415\u56db\u5a18\uff0c\u81ea\u5df1\u548c\u5415\u56db\u5a18\u867d\u7136\u76f8\u4ea4\u65e5 \u6d45\uff0c\u53ef\u662f\u5979\u662f\u540d\u5112\u4e4b\u5973\uff0c\u89c1\u8bc6\u4e0d\u51e1\uff0c\u5bf9\u81ea\u5df1\u4e5f\u5f88\u5173\u5fc3\u3002\u5510\u6653\u6f9c\u60f3\u6765\u60f3\u53bb\uff0c\u4f3c\u4e4e\u5929\u5730\u4e4b\u5927\uff0c\u53ea \u6709\u5415\u56db\u5a18\u53ef\u4ee5\u4fe1\u8d56\uff0c\u4e8e\u662f\u4e00\u5251\u5355\u8eab\uff0c\u53c8\u98d8\u7136\u79bb\u5f00\u4e86\u4eac\u5e08\uff0c\u4e1c\u4e0b\u6d59\u6c5f\uff0c\u6cbf\u9014\u4e0a\u65f6\u4e0d\u65f6\u78b0\u6709\u6b66\u6797 \u4eba\u7269\u622a\u51fb\uff0c\u539f\u6765\u6768\u4ef2\u82f1\u7adf\u7136\u628a\u5510\u6653\u6f9c\u53db\u5e08\u4e4b\u4e8b\uff0c\u4f20\u904d\u6c5f\u6e56\uff0c\u5e78\u597d\u76d8\u67e5\u622a\u51fb\u7684\u90fd\u4e0d\u662f\u597d\u624b\uff0c\u5510 \u6653\u6f9c\u5f97\u4ee5\u5b89\u7136\u6765\u5230\u6d59\u6c5f\u3002\u5230\u4e86\u6d59\u6c5f\uff0c\u624d\u77e5\u9053\u4e94\u5e74\u524d\u6c88\u5728\u5bbd\u88ab\u6355\uff0c\u5415\u8446\u4e2d\u8eab\u6b7b\uff0c\u5415\u56db\u5a18\u8fc1\u5c45\u7684 \u4e8b\u3002\u660e\u67e5\u6697\u8bbf\u90fd\u4e0d\u77e5\u5415\u56db\u5a18\u8fc1\u5f80\u4f55\u5904\uff0c\u5e78\u597d\u7518\u51e4\u6c60\u540d\u5934\u751a\u54cd\uff0c\u5176\u65f6\u6070\u597d\u4e5f\u5728\u6d59\u6c5f\uff0c\u5510\u6653\u6f9c\u5c31 \u786c\u7740\u5934\u76ae\uff0c\u53bb\u62dc\u89c1\u8fd9\u4f4d\u6c5f\u5357\u5927\u4fa0\uff0c\u7518\u51e4\u6c60\u867d\u4e0d\u4fe1\u4ed6\uff0c\u5374\u9887\u901a\u60c5\u8fbe\u7406\uff0c\u5c31\u628a\u5e08\u59b9\u7684\u4f4f\u5740\u544a\u8bc9\u4e86 \u4ed6\u3002\u4f46\u4e5f\u63d0\u9632\u4ed6\u9080\u6709\u515a\u7fbd\uff0c\u6697\u4e2d\u8ddf\u5728\u4ed6\u7684\u540e\u9762\uff0c\u4e00\u76f4\u8ddf\u5230\u4ed9\u971e\u5cad\uff0c\u770b\u4ed6\u72ec\u81ea\u767b\u5c71\uff0c\u8fd9\u624d\u4f5c\u7f62\u3002 \u3000\u3000\u5510\u6653\u6f9c\u548c\u5415\u56db\u5a18\u5750\u5728\u6d41\u6cc9\u5c71\u7011\u4e4b\u65c1\uff0c\u5a13\u5a13\u957f\u8c08\uff0c\u5510\u6653\u6f9c\u6570\u6708\u79ef\u90c1\uff0c\u76d8\u7ed3\u5fc3\u4e2d\uff0c\u4e00\u65e6\u503e \u5410\uff0c\u4eba\u4e5f\u8f7b\u677e\u4e86\u8bb8\u591a\u3002\u4e0d\u77e5\u4e0d\u89c9\u6de1\u4e86\u534a\u5929\uff0c\u70c8\u65e5\u5f53\u7a7a\uff0c\u5c71\u7011\u6d41\u6cc9\uff0c\u7ed9\u9633\u5149\u5e7b\u6210\u4e3d\u5f69\u3002\u5415\u56db\u5a18 \u4e00\u7b11\u800c\u8d77\uff0c\u62c9\u5510\u6653\u6f9c\u767b\u4e0a\u4e00\u5757\u5ca9\u77f3\uff0c\u7b11\u6307\u5c71\u4e0b\u9053\uff1a\u201c\u4f60\u767b\u9ad8\u8bd5\u671b\u3002\u201d\u5510\u6653\u6f9c\u4e0d\u77e5\u5176\u610f\uff0c\u767b\u9ad8 \u4e00\u671b\uff0c\u53ea\u89c9\u65f7\u91ce\u5e73\u7574\uff0c\u5c3d\u6536\u773c\u5e95\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u5f00\u6717\uff0c\u95f7\u6c14\u6e10\u6d88\uff0c\u5415\u56db\u5a18\u9053\uff1a\u201c\u5c71\u5ddd\u5947\u666f\uff0c\u53ef\u6da4 \u6d4a\u6c1b\uff0c\u5929\u5730\u65e0\u7a77\uff0c\u5e94\u589e\u8c6a\u6c14\u3002\u4eba\u751f\u4e0d\u5982\u610f\u4e8b\u5e38\u516b\u4e5d\uff0c\u4f55\u8db3\u4ecb\u6000\u3002\u201d\u626c\u7709\u4e00\u7b11\uff0c\u5510\u6653\u6f9c\u987f\u89c9\u4e00 \u5929\u9634\u973e\uff0c\u7ed9\u5979\u6570\u8bed\u9a71\u6563\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5c71\u5ddd\u98a4\u7075\u79c0\uff0c\u5dfe\u5e3c\u80dc\u987b\u7709\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u4e94\u56de\u3000\u8bba\u4e16\u8c08\u8bcd\u3000\u5fae\u8a00\u6653\u5927\u4e49\u3000\u5bfb\u5e7d\u63a2\u9690\u3000\u6e38\u4fa0\u9732\u950b\u8292 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u5341\u4e94\u56de \u8bba\u4e16\u8c08\u8bcd\u3000\u5fae\u8a00\u6653\u5927\u4e49 \u5bfb\u5e7d\u63a2\u9690\u3000\u6e38\u4fa0\u9732\u950b\u8292 \u3000\u3000\u5415\u56db\u5a18\u53c8\u7b11\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u5728\u6768\u4ef2\u82f1\u95e8\u4e0b\u4e4b\u65f6\uff0c\u767d\u5929\u4e60\u6b66\uff0c\u665a\u4e0a\u5b66\u6587\uff0c\u8fd8\u66fe\u586b\u8fc7\u4e00\u9053\u2018\u767e \u5b57\u4ee4\u2019\u7684\u8bcd\uff1f\u201d\u5510\u6653\u6f9c\u9762\u4e0a\u4e00\u7ea2\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u8fd9\u9996\u8bcd\u4e0d\u8fc7\u662f\u5c11\u5e74\u65f6\u5019\u7684\u6e38\u620f\u4e4b\u4f5c\uff0c\u6839\u672c\u4e0d \u6210\u5176\u4e3a\u8bcd\u3002\u201d\u539f\u6765\u90a3\u9996\u8bcd\u6b63\u662f\u4ed6\u601d\u5ff5\u5415\u56db\u5a18\u800c\u4f5c\u7684\uff0c\u4e0d\u77e5\u4f55\u4ee5\u4f1a\u7ed9\u5979\u77e5\u9053\uff0c\u662f\u5979\u63d0\u8d77\uff0c\u5fc3\u4e2d \u63e3\u63e3\u4e0d\u5b89\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u4f60\u90a3\u9996\u8bcd\u6211\u8bfb\u8fc7\u4e86\uff0c\u201c\u8bcd\u5473\u201d\u662f\u6709\u7684\uff0c\u4f46\u592a\u4f24\u611f\u4e86\u3002\u5c11\u5e74\u4eba\u4e0d\u5e94\u6709 \u6b64\u3002\u4f60\u5f00\u9996\u90a3\u51e0\u53e5\u2018\u98d8\u840d\u5026\u4fa3\uff0c\u7b97\u832b\u832b\u4eba\u6d77\uff0c\u53cb\u670b\u77e5\u5426\uff1f\u2019\u4fbf\u5145\u6ee1\u4e86\u5b64\u72ec\u81ea\u4f24\u7684\u60c5\u610f\u3002\u5176\u5b9e \u5728\u832b\u832b\u4eba\u6d77\u4e4b\u4e2d\uff0c\u5c3d\u591a\u77e5\u5df1\uff0c\u800c\u4e14\u53ea\u8981\u4f60\u884c\u5408\u4e4e\u4e49\uff0c\u505a\u7684\u4e8b\u80fd\u4e3a\u5927\u591a\u6570\u4eba\u7740\u60f3\uff0c\u90a3\u53c8\u4f55\u5fc5\u5b9a \u8981\u4eba\u77e5\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u8c08\u8bcd\u8bba\u4e16\uff0c\u6653\u4ee5\u5fae\u8a00\uff0c\u8bbd\u4ee5\u5927\u4e49\uff0c\u5bf9\u4ed6\u8bcd\u4e2d\u7684\u513f\u5973\u4e4b\u60c5\u5374\u534a\u53e5\u4e0d\u63d0\u3002\u5510\u6653\u6f9c\u4f4e\u5934 \u4e0d\u8bed\uff0c\u5fc3\u4e2d\u601d\u60f3\uff0c\u8d77\u4f0f\u5982\u6f6e\u3002 \u3000\u3000\u5415\u56db\u5a18\u76c8\u76c8\u4e00\u7b11\uff0c\u53c8\u9053\uff0c\u201c\u6211\u5c11\u5e74\u65f6\u4e5f\u66fe\u586b\u8fc7\u4e00\u9996\u2018\u6c34\u9f99\u541f\u2019\u8bcd\uff0c\u5176\u4e2d\u6709\u4e24\u53e5\u9053\uff1a\u2018\u83ab \u5b66\u65b0\u58f0\u540e\u4e3b\uff0c\u6050\u8bcd\u4ed9\u7b11\u4f9d\u4f55\u82e6\uff1f\u2019\u6211\u4ee5\u4e3a\u65e0\u75c5\u547b\u541f\u56fa\u7136\u4e0d\u597d\uff0c\u6709\u75c5\u547b\u541f\u4e5f\u5927\u53ef\u4e0d\u5fc5\u3002\u5927\u4e08\u592b \u82e5\u9047\u5371\u96be\uff0c\u5f53\u7acb\u5b9a\u811a\u6839\uff0c\u80a9\u8d1f\u91cd\u8377\uff0c\u95ef\u8fc7\u5173\u53bb\u3002\u5b66\u8bcd\u5f53\u5b66\u82cf\u3001\u8f9b\uff0c\u50cf\u674e\u540e\u4e3b\u90a3\u79cd\u4ea1\u56fd\u4e4b\u97f3\uff0c \u5b66\u5b83\u4f5c\u751a\uff1f\u4f60\u8bfb\u8fc7\u8f9b\u5f03\u75be\u90a3\u9996\u2018\u8d3a\u65b0\u90ce\u2019\u8bcd\u5427\uff0c\u5f00\u9996\u90a3\u4e09\u53e5\uff0c\u4e5f\u50cf\u4f60\u90a3\u9996\u767e\u5b57\u4ee4\uff0c\u5f00\u5934\u7684\u4e09 \u53e5\u4e00\u6837\uff0c\u53f9\u4ea4\u6e38\u96f6\u843d\uff0c\u4f46\u4ed6\u90a3\u9996\u8bcd\u5374\u4e00\u7247\u8c6a\u6c14\uff0c\u548c\u4f60\u5927\u4e0d\u76f8\u540c\u3002\u4f60\u8fd8\u8bb0\u5f97\u4e48\uff1f\u4f60\u8bd5\u5ff5\u6765\u542c \u542c\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u62ac\u8d77\u5934\u6765\uff0c\u5ff5\u9053\uff1a \u3000\u3000\u201c\u751a\u77e3\u543e\u8870\u77e3\uff01\u6005\u5e73\u751f\u4ea4\u6e38\u96f6\u843d\uff0c\u53ea\u4eca\u4f59\u51e0\u3002\u767d\u53d1\u7a7a\u5782\u4e09\u5343\u4e08\uff0c\u4e00\u7b11\u4eba\u95f4\u4e07\u4e8b\uff0c\u95ee\u4f55\u7269 \u80fd\u4ee4\u516c\u559c\uff1f\u6211\u89c1\u9752\u5c71\u591a\u59a9\u5a9a\uff0c\u6599\u9752\u5c71\u89c1\u6211\u5e94\u5982\u662f\u3002\u60c5\u4e0e\u8c8c\uff0c\u7565\u76f8\u4f3c\u3002\u4e00\u5c0a\u6414\u9996\u4e1c\u7a97\u91cc\uff0c\u60f3\u6e0a \u660e\u3002\u505c\u4e91\u8bd7\u5c31\uff0c\u6b64\u65f6\u98ce\u5473\uff0c\u6c5f\u5de6\u6c88\u9163\u6c42\u540d\u8005\uff0c\u5c82\u8bc6\u6d4a\u91aa\u5999\u7406\uff01\u56de\u9996\u53eb\u4e91\u98de\u98ce\u8d77\u3002\u4e0d\u6068\u53e4\u4eba\u543e \u4e0d\u89c1\uff0c\u6068\u53e4\u4eba\u4e0d\u89c1\u543e\u72c2\u8033\uff01\u77e5\u6211\u8005\uff0c\u4e8c\u4e09\u5b50\uff01\u201d \u3000\u3000\u8fd9\u9996\u8bcd\u4e43\u8f9b\u5f03\u75be\u66ae\u5e74\u6240\u5199\uff0c\u4fcf\u5370\u8c61\u8fc8\u8d85\u8131\uff0c\u80dc\u4e8e\u5c11\u5e74\u3002\u5510\u6653\u6f9c\u5ff5\u5b8c\u4e4b\u540e\uff0c\u987f\u89c9\u81ea\u5df1\u5fc3\u80f8 \u4fa0\u7a84\uff0c\u771f\u4e0d\u514d\u4e3a\u53e4\u4eba\u6240\u7b11\u3002\u5415\u56db\u5a18\u5e76\u6ca1\u6709\u6709\u6e29\u8a00\u5b89\u6170\u4e8e\u4ed6\uff0c\u4f46\u5374\u5728\u8be5\u8bcd\u4e2d\u5f15\u9886\u4ed6\u81ea\u5df1\u601d\u7d22\u3002 \u5510\u6653\u6f9c\u5fc3\u73af\u6e10\u7545\uff0c\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u59d0\u59d0\u628a\u4f60\u4f5c\u7684\u90a3\u9996\u2018\u6c34\u9f99\u541f\u2019\u8bcd\u4e5f\u4e00\u5e76\u5ff5\u7ed9\u6211\u542c\u5427\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u60f3\u4e86\u4e00\u60f3\uff0c\u7b11\u9053\uff1a\u201c\u4e5f\u597d\u3002\u201d\u5ff5\u9053\uff1a \u3000\u3000\u201c\u5929\u8fb9\u7f25\u7f08\u5947\u5cf0\uff0c\u66fe\u662f\u6211\u65e7\u65f6\u5bb6\u5904\u3002\u62c2\u8896\u53bb\u6765\uff0c\u8f6f\u5c18\u521d\u8e0f\uff0c\u77f3\u95e8\u897f\u4f4f\u3002\u77ed\u9504\u683d\u82b1\uff0c\u957f\u8bd7 \u4f50\u9152\uff0c\u51e0\u56de\u51dd\u4ed5\u3002\u60ef\u88c2\u7b1b\u53f9\u4e91\uff0c\u9ad8\u6b4c\u6563\u96fe\uff0c\u632f\u5728\u4e0a\uff0c\u5343\u5ca9\u6811\u3002\u83ab\u5b66\u65b0\u58f0\u540e\u4e3b\uff0c\u6050\u8bcd\u4ed9\u7b11\u4f9d\u4f55 \u82e6\uff1f\u6458\u6597\u79fb\u661f\uff0c\u60ca\u6c99\u843d\u6708\uff0c\u8f9f\u5f00\u4e91\u8def\u3002\u84ec\u5c9b\u65e7\u6e38\uff0c\u5458\u5ce8\u65b0\u5883\uff01\u4ece\u5934\u98de\u6e21\u3002\u4e14\u7b14\u6cfb\u897f\u6c5f\uff0c\u6587\u7ffb \u5317\u6d77\uff0c\u5524\u795e\u9f99\u821e\u3002\u201d \u3000\u3000\u8fd9\u9996\u8bcd\u8c6a\u60c5\u80dc\u6168\uff0c\u62b1\u8d1f\u65e2\u9ad8\uff0c\u80f8\u895f\u4ea6\u5e7f\u3002\u82e5\u975e\u5415\u56db\u5a18\u81ea\u627f\u5df1\u4f5c\uff0c\u5510\u6653\u6f9c\u771f\u4e0d\u6562\u76f8\u4fe1\u8fd9\u662f \u51fa\u4e8e\u5973\u5b50\u624b\u7b14\u3002 \u3000\u3000\u4e24\u4eba\u8c08\u5f97\u751a\u662f\u6295\u673a\uff0c\u5510\u6653\u6f9c\u95f7\u6c14\u867d\u6d88\uff0c\u4f46\u8fd8\u60f3\u8bf7\u95ee\u5979\u7acb\u8eab\u5904\u4e16\u4e4b\u9053\uff0c\u6b63\u601d\u7d22\u95f4\uff0c\u5ffd\u542c\u5f97 \u4e00\u74e2\u548c\u5c1a\u5728\u4e0b\u9762\u558a\u9053\uff1a\u201c\u56db\u5a18\uff0c\u6c88\u5148\u751f\u5348\u7761\u9192\u4e86\uff0c\u6b63\u627e\u4f60\u5462\u3002\u201d\u5415\u56db\u5a18\u62ac\u5934\u4e00\u770b\u8bf4\u9053\uff1a\u201c\u771f \u662f\u7545\u8c08\u4e0d\u77e5\u65f6\u523b\uff0c\u65e5\u5934\u90fd\u5df2\u504f\u897f\u4e86\u3002\u4f60\u7684\u809a\u5b50\u4e5f\u8be5\u997f\u5566\uff0c\u56de\u5bfa\u9662\u5403\u996d\u53bb\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u968f\u5415\u56db\u5a18\u4e0b\u5c71\uff0c\u95ee\u9053\uff1a\u201c\u54ea\u4f4d\u6c88\u5148\u751f\uff1f\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u5c31\u662f\u6211\u5e38\u5411\u4f60\u63d0\u8d77\u7684\uff0c \u6211\u7239\u7239\u6700\u5f97\u610f\u7684\u95e8\u751f\u6c88\u5728\u5bbd\u3002\u201d\u5510\u6653\u6f9c\u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u4e94\u5e74\u524d\u5df2\u88ab\u6355\u4e86\uff1f\u201d \u5415\u56db\u59d0\u9053\uff1a\u201c\u7518\u5e08\u5144\u8fd8\u6ca1\u5bf9\u4f60\u8bf4\u8fc7\u5427\uff0c\u540e\u6765\u6211\u4eec\u628a\u4ed6\u6551\u51fa\u6765\u4e86\u3002\u201d\u5510\u6653\u6f9c\u5148\u662f\u5fc3\u795e\u4e00\u8361\uff0c\u540e \u6765\u4e00\u60f3\uff1a\u5415\u56db\u5a18\u5bf9\u81ea\u5df1\u7684\u59d0\u5f1f\u4e4b\u60c5\uff0c\u5df2\u8db3\u4ee4\u81ea\u5df1\u94ed\u5fc3\u523b\u9aa8\uff0c\u90a3\u80fd\u518d\u5b58\u5962\u671b\uff1f\u8fd9\u6837\u4e00\u60f3\uff0c\u5fc3\u6e56 \u5e73\u9759\uff0c\u5fc3\u5883\u6f84\u660e\uff0c\u9ed8\u9ed8\u7684\u968f\u5415\u56db\u5a18\u8fdb\u4e86\u7985\u9662\u3002 \u3000\u3000\u6c88\u5728\u5bbd\u5348\u7761\u521d\u9192\uff0c\u56de\u5473\u5415\u56db\u5a18\u6668\u95f4\u6240\u8bf4\u7684\u8a00\u8bed\uff0c\u53ea\u89c9\u871c\u610f\u67d4\u60c5\uff0c\u7d0a\u56de\u5fc3\u5e95\uff0c\u518d\u770b\u81ea\u5df1\u5370 \u53f8\u6240\u96c6\u524d\u4eba\u65ad\u53e5\u7684\u90a3\u9996\u5c0f\u8bcd\uff0c\u91cd\u8bfb\u4e00\u904d\uff0c\u8bfb\u5230\uff1a\u201c\u89c1\u4e86\u53c8\u4f11\u8fd8\u4f3c\u68a6\uff0c\u5750\u6765\u867d\u8fd1\u8fdc\u5982\u5929\u3002\u201d\u4e24 \u53e5\uff0c\u4e0d\u89c9\u54d1\u7136\u5931\u7b11\u3002\u5fc3\u60f3\uff0c\u5415\u56db\u5a18\u5982\u6b64\u6df1\u60c5\u7737\u604b\uff0c\u81ea\u5df1\u8fd8\u81ea\u5bfb\u82e6\u607c\uff0c\u8fd9\u771f\u662f\u7740\u751a\u6765\u7531\uff1f\u53c8\u60f3 \u9053\uff1a\u53e4\u4eba\u76f8\u4ea4\u4ee5\u8bda\uff0c\u50cf\u5415\u56db\u5a18\u90a3\u6837\u7ee9\u5e74\u7389\u8c8c\uff0c\u4e94\u5e74\u6765\u5374\u5fcd\u53d7\u7a7a\u5c71\u9759\u5bc2\uff0c\u4f34\u966a\u81ea\u5df1\u8fd9\u6837\u4e00\u4e2a\u6b8b \u75be\u4e4b\u4eba\uff0c\u800c\u4e14\u8fd8\u613f\u4ee5\u8eab\u76f8\u6258\uff0c\u8fd9\u771f\u662f\u60c5\u771f\u610f\u8bda\uff0c\u5728\u4e3b\u4eba\u4e2d\u4e5f\u4e0d\u591a\u89c1\u3002\u8fd9\u65f6\uff0c\u5fc3\u5e95\u9634\u973e\uff0c\u5c3d\u7686 \u626b\u51c0\u3002 \u3000\u3000\u5415\u56db\u5a18\u5e26\u4e86\u5510\u6653\u6f9c\u8fdb\u5165\u7985\u9662\uff0c\u4e24\u4eba\u5e76\u80a9\u800c\u884c\uff0c\u5510\u6653\u6f9c\u5df2\u957f\u5f97\u6bd4\u5415\u56db\u5a18\u8fd8\u9ad8\uff0c\u7985\u9662\u524d\u6709\u5c71 \u6cc9\u6c47\u6210\u5c0f\u6f6d\uff0c\u6f6d\u6c34\u7167\u5f71\uff0c\u53ea\u89c1\u4e00\u4e2a\u82f1\u4fca\u5c11\u5e74\uff0c\u4e00\u4e2a\u554a\u5a1c\u5c11\u5973\uff0c\u6709\u5982\u5e76\u8482\u4e4b\u83b2\uff0c\u5728\u6c34\u4e2d\u6447\u6643\u3002 \u521a\u624d\u5415\u56db\u5a18\u5728\u6d41\u6cc9\u98de\u7011\u4e4b\u65c1\uff0c\u542c\u5510\u6653\u6f9c\u7533\u8bc9\uff0c\u5168\u5fc3\u60f3\u66ff\u4ed6\u6d88\u89e3\u4f18\u5371\uff0c\u5fc3\u4e2d\u6beb\u65e0\u522b\u5ff5\uff0c\u5bf9\u6c34\u4e2d \u5f71\u5b50\uff0c\u4ea6\u65e0\u611f\u89c9\uff0c\u5982\u4eca\u7ecf\u8fc7\u5c0f\u6f6d\uff0c\u6b65\u5165\u7985\u9662\uff0c\u7a81\u7136\u60f3\u8d77\u4e86\u6c88\u5728\u5bbd\u90a3\u9996\u96c6\u53e5\u5c0f\u8bcd\uff0c\u53ea\u6015\u6c88\u5728\u5bbd \u5bf9\u81ea\u5df1\u8fd8\u672a\u80fd\u5168\u5fc3\u4fe1\u8d56\uff0c\u89c1\u4e86\u6653\u6f9c\uff0c\u82e5\u751f\u8bef\u4f1a\uff0c\u8fd9\u5c82\u4e0d\u52a0\u91cd\u4ed6\u7684\u75c5\u60c5\uff1f\u601d\u5ff5\u53ca\u6b64\uff0c\u811a\u6b65\u5ffd \u7f13\u3002\u5510\u6653\u6f9c\u82e5\u6709\u6240\u89c9\uff0c\u56de\u5934\u95ee\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u60f3\u4ec0\u4e48\uff1f\u201d\u5415\u56db\u5a18\u62ac\u5934\u4e00\u671b\uff0c\u9633\u5149\u660e\u6717\uff0c\u5c71\u82b1 \u5982\u7b20\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\uff1f\u201d\u8de8\u524d\u4e24\u6b65\uff0c\u5e26\u5510\u6653\u6f9c\u8fdb\u4e86\u7985\u9662\uff0c\u5728\u4e00\u95f4\u9759\u5ba4\u4e4b\u524d\u53e9\u95e8\u53eb\u9053\uff1a \u201c\u5728\u5bbd\uff0c\u6709\u5ba2\u4eba\u6765\u5462\uff01\u201d \u3000\u3000\u6c88\u5728\u5bbd\u7684\u5e8a\u8d34\u8fd1\u623f\u95e8\uff0c\u4f38\u624b\u4fbf\u53ef\u62d4\u6367\u95e8\u95e9\uff0c\u4ed6\u5374\u8d70\u4e0b\u5e8a\u6765\uff0c\u4e00\u624b\u6276\u7740\u5899\u58c1\uff0c\u4e00\u624b\u5f00\u95e8\uff0c \u5415\u56db\u5a18\u6025\u5fd9\u5c06\u4ed6\u6276\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u521a\u521a\u80fd\u8fd0\u52a8\u56db\u80a2\uff0c\u4e0d\u5b9c\u8fc7\u52b3\u3002\u201d\u6c88\u5728\u5bbd\u89c1\u6709\u5510\u6653\u6f9c\u5728\u65c1\uff0c \u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u8bf4\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u5148\u62db\u5f85\u5ba2\u4eba\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u5f88\u719f\u7684\u670b\u53cb\u3002\u201d\u77a7\u4e86\u6c88 \u5728\u5bbd\u4e00\u773c\uff0c\u89c1\u4ed6\u6beb\u65e0\u5f02\u5bb9\uff0c\u5c06\u4ed6\u6276\u56de\u5e8a\u4e0a\uff0c\u66ff\u4e24\u4eba\u4ecb\u7ecd\uff0c\u6c88\u5728\u5bbd\u9053\uff1a\u201c\u5510\u5144\u8bf7\u5750\uff0c\u6211\u884c\u52a8\u4e0d \u4fbf\uff0c\u8bf7\u6055\u5931\u793c\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u89c1\u6b64\u60c5\u51b5\uff0c\u624d\u77e5\u5415\u56db\u5a18\u4e94\u5e74\u6765\u966a\u4f34\u7684\u7adf\u7136\u662f\u4e2a\u5e9f\u4eba\uff0c\u5fc3\u4e2d\u611f\u52a8\uff0c\u66f4\u89c9\u5415\u56db\u5a18\u771f\u975e \u5e38\u4eba\u53ef\u53ca\uff01 \u3000\u3000\u5415\u56db\u5a18\u5230\u9999\u79ef\u53a8\u4e2d\u53d6\u4e86\u658b\u996d\uff0c\u7aef\u8fdb\u623f\u4e2d\uff0c\u5510\u6653\u6f9c\u548c\u6c88\u5728\u5bbd\u8c08\u5f97\u751a\u6b22\u3002\u5403\u4e86\u996d\u540e\uff0c\u5510\u6653\u6f9c \u9053\uff1a\u201c\u6211\u6709\u4e00\u4e8b\u60f3\u8bf7\u6559\u6c88\u5144\u3002\u201d\u6c88\u5728\u5bbd\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u5c06\u524d\u4e8b\u518d\u8bf4\u4e00\u904d\u3002\u6c88\u5728\u5bbd\u542c\u5b8c\u4e4b\u540e\uff0c\u5ffd\u7136\u5750\u4e86\u8d77\u6765\u3002\u8bf4\u9053\uff1a\u201c\u5510\u5144\u65e2\u4e0d\u89c1\u5916\uff0c\u6211 \u4e5f\u613f\u732e\u4e00\u5f97\u4e4b\u611a\uff0c\u83b9\u59b9\uff0c\u4f60\u966a\u5510\u5144\u8d70\u4e00\u8d9f\uff01\u201d\u5415\u56db\u5a18\u60ca\u9053\uff1a\u201c\u90a3\u4f60\u5462\uff1f\u201d\u6c88\u5728\u5bbd\u9053\uff1a\u201c\u6211\u73b0 \u5728\u8eab\u4f53\u65e5\u6709\u8fdb\u5883\uff0c\u5185\u529f\u4ea6\u5df2\u6478\u5230\u95e8\u8def\uff0c\u6709\u4e00\u74e2\u5927\u5e08\u7167\u987e\u5c31\u884c\u4e86\u3002\u5510\u5144\u7684\u4e8b\uff0c\u5374\u975e\u4f60\u66ff\u4ed6\u6392\u89e3 \u4e0d\u53ef\u3002\u4e8b\u6709\u7f13\u6025\u8f7b\u91cd\uff0c\u8f7b\u91cd\u5012\u7f6e\uff0c\u5219\u4e8b\u6b86\u77e3\u3002\u6211\u4eec\u8bfb\u4e66\uff0c\u5c31\u662f\u8981\u8bc6\u5f97\u5206\u522b\u91cd\u548c\u8f7b\u3002\u4f55\u51b5\u4e3b\u4eba \u9ad8\u4e49\uff0c\u539f\u5c31\u4e0d\u6b62\u9650\u4e8e\u7537\u5b50\uff0c\u5510\u5144\u548c\u6211\u4eec\u65e2\u662f\u77e5\u4ea4\uff0c\u4ed6\u7684\u5371\u96be\uff0c\u6211\u4eec\u5c82\u53ef\u5750\u89c6\uff1f\u201d\u5415\u56db\u5a18\u6599\u4e0d \u5230\u4ed6\u7684\u5fc3\u80f8\u5982\u6b64\u5f00\u9614\uff0c\u4e0d\u89c9\u611f\u52a8\u5f97\u6ef4\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u6c88\u5728\u5bbd\u53c8\u9053\uff1a\u201c\u5510\u5144\u8fd9\u6b21\u906d\u9022\u5927\u53d8\uff0c\u636e\u6211\u770b\u6765\uff0c\u662f\u5916\u6765\u4e4b\u96be\u6613\u89e3\uff0c\u800c\u5fc3\u4e2d\u4e4b\u8d3c\u96be\u9664\u3002\u201d \u5510\u6653\u6f9c\u8fd9\u65f6\u5df2\u5c06\u6c88\u5728\u5bbd\u5f53\u5927\u54e5\u770b\u5f85\uff01\u8bf4\u9053\uff1a\u201c\u613f\u8046\u6559\u8bf2\u3002\u201d\u6c88\u5728\u5bbd\u9053\uff1a\u201c\u5510\u5144\u88ab\u4ee4\u5e08\u8bef\u4f1a\u4e4b \u4e8b\uff0c\u6709\u56db\u5a18\u51fa\u5934\u6392\u89e3\uff0c\u8c05\u53ef\u5316\u4e3a\u65e0\u4e8b\u3002\u53ea\u662f\u5510\u5144\u4e4d\u660e\u8eab\u4e16\uff0c\u5bf9\u4eca\u540e\u51fa\u5904\uff0c\u5927\u7ea6\u9887\u611f\u4e3a\u96be\u3002\u201d \u8fd9\u8bdd\u4e00\u9488\u89c1\u8840\uff0c\u5510\u6653\u6f9c\u6b63\u56e0\u4e3a\u81ea\u5df1\u662f\u7687\u5ba4\u8840\u7edf\u800c\u611f\u5230\u82e6\u607c\u4e07\u5206\u3002\u6c88\u5728\u5bbd\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u767e\u59d3\u4e4b \u6240\u597d\u8005\u597d\u4e4b\uff0c\u767e\u59d3\u4e4b\u6240\u6076\u8005\u6076\u4e4b\uff0c\u7acb\u8eab\u5904\u4e16\u4e4b\u9053\uff0c\u5c3d\u4e8e\u6b64\u77e3\u3002\u201d\u5510\u6653\u6f9c\u4f4e\u9996\u6c89\u601d\uff0c\u826f\u4e45\u826f \u4e45\uff0c\u59cb\u62ac\u8d77\u5934\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u6c88\u5144\u6559\u5bfc\u3002\u201d \u3000\u3000\u6c88\u5728\u5bbd\u9053\uff1a\u201c\u83b9\u59b9\uff0c\u4f60\u660e\u5929\u5c31\u966a\u5510\u5144\u4e0b\u5c71\u53bb\u5427\u3002\u201d\u5415\u56db\u5a18\u5fc3\u60c5\u6fc0\u8361\uff0c\u5ffd\u9053\uff1a\u201c\u518d\u8fc7\u51e0\u5929 \u4fbf\u662f\u4e03\u65e5\u4e86\u3002\u201d\u6c88\u5728\u5bbd\u77e5\u5979\u820d\u4e0d\u5f97\u81ea\u5df1\uff0c\u7b11\u9053\uff1a\u201c\u5c11\u6e38\u8bcd\u4e91\uff1a\u2018\u91d1\u51e4\u7389\u9732\u4e00\u76f8\u9022\uff0c\u4fbf\u80dc\u5374\u4eba \u95f4\u65e0\u6570\u3002\u2019\u53c8\u9053\uff1a\u2018\u4e24\u60c5\u82e5\u662f\u4e45\u957f\u65f6\uff0c\u53c8\u5c82\u5728\u671d\u671d\u66ae\u66ae\u3002\u2019\u6211\u4eec\u4e94\u5e74\u6765\u671d\u5915\u76c8\u6853\uff0c\u5df2\u4e0d\u77e5\u80dc \u8fc7\u725b\u90ce\u7ec7\u5973\u5343\u4e07\u500d\uff0c\u4f55\u5fc5\u4e3a\u5c0f\u522b\u4f24\u5fc3\u3002\u201d\u5415\u56db\u5a18\u542c\u4e86\u8fd9\u8bdd\uff0c\u77e5\u4ed6\u5df2\u662f\u5168\u5fc3\u4fe1\u7231\uff0c\u66f4\u65e0\u534a\u70b9\u7591 \u8651\uff0c\u867d\u7136\u79bb\u60c5\u522b\u7eea\u5c1a\u81ea\u7d22\u6000\uff0c\u4f46\u4e00\u74e3\u82b3\u5fc3\u4e5f\u81ea\u6697\u6697\u5feb\u6170\u3002 \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u4e00\u74e2\u548c\u5c1a\u5ffd\u7136\u6263\u95e8\u53eb\u9053\uff1a\u201c\u56db\u5a18\uff0c\u4eca\u5929\u4e0d\u77e5\u5439\u4ec0\u4e48\u98ce\uff0c\u53c8\u6709\u7a00\u5ba2\u6765\u4e86\uff01\u201d\u5415 \u56db\u5a18\u9053\uff1a\u201c\u8c01\u5440\uff01\u201d\u4e00\u74e2\u9053\uff1a\u201c\u4f60\u7684\u5e08\u5144\u6c5f\u5357\u5927\u4fa0\u7518\u51e4\u6c60\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u7518\u5927\u4fa0\u6307\u5f15\u6211\u5230 \u4ed9\u971e\uff0c\u6211\u4ee5\u4e3a\u4ed6\u81ea\u5df1\u4e0d\u6765\u4e86\uff0c\u600e\u4e48\u4ed6\u53c8\u8d76\u6765\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u7518\u5e08\u5144\u6765\u5fc5\u7136\u6709\u4e8b\u3002\u201d\u548c\u5510\u6653 \u6f9c\u51fa\u53bb\u8fce\u63a5\u3002 \u3000\u3000\u7518\u51e4\u6c60\u89c1\u5510\u6653\u6f9c\u4f34\u7740\u5415\u56db\u5a18\u51fa\u6765\uff0c\u77e5\u4ed6\u4ee5\u524d\u6240\u8bf4\u4e0d\u5047\uff0c\u6001\u5ea6\u6bd4\u524d\u4eb2\u70ed\u8bb8\u591a\uff0c\u6267\u7740\u4ed6\u7684\u624b \u7b11\u9053\uff1a\u201c\u6211\u6697\u4e2d\u4fdd\u62a4\u4f60\u4e0a\u5c71\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u9762\u6709\u6127\u8272\uff0c\u7b54\u9053\uff1a\u201c\u4e1d\u6beb\u4e0d\u77e5\u3002\u201d\u539f\u6765\u7518 \u51e4\u6c60\u7cbe\u660e\u5e72\u7ec3\uff0c\u628a\u5e08\u59b9\u7684\u4f4f\u5740\u544a\u8bc9\u4e86\u5510\u6653\u6f9c\u540e\uff0c\u4e00\u65b9\u9762\u6015\u4ed6\u8bf4\u7684\u662f\u5047\u8bdd\uff0c\u6697\u4e2d\u9080\u6709\u6e05\u5ef7\u9e70\u72ac \u4e0a\u5c71\uff1b\u4e00\u65b9\u9762\u5374\u53c8\u987e\u53ca\u82e5\u4ed6\u6240\u8bf4\u662f\u771f\uff0c\u4e5f\u96be\u4fdd\u6ca1\u6709\u4eba\u8ddf\u8e2a\u3002\u6240\u4ee5\u4fbf\u6697\u4e2d\u8ddf\u5728\u4ed6\u7684\u540e\u9762\u3002\u5f85\u5510 \u6653\u6f9c\u4e0a\u5c71\u4e4b\u540e\uff0c\u8fd9\u624d\u6298\u56de\uff0c\u8c01\u77e5\u521a\u521a\u8e0f\u4e0a\u56de\u7a0b\uff0c\u5728\u4ed9\u971e\u5cad\u811a\u4e0d\u8fdc\u4e4b\u5904\uff0c\u53c8\u9047\u89c1\u4e86\u4e00\u4ef6\u5947\u4e8b\u3002 \u3000\u3000\u5415\u56db\u5a18\u884c\u793c\u4e4b\u540e\uff0c\u95ee\u9053\uff1a\u201c\u4e00\u522b\u4e94\u5e74\uff0c\u5404\u540c\u95e8\u53ef\u597d\u4e48\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8fd1\u4e00\u4e24\u5e74\u6765\u6211\u5f88 \u5c11\u548c\u540c\u95e8\u89c1\u9762\u3002\u60f3\u4e0d\u5230\u521a\u624d\u5728\u65e0\u610f\u4e4b\u4e2d\uff0c\u5012\u5f97\u7740\u540c\u95e8\u7684\u4fe1\u7269\u3002\u201d\u5415\u56db\u5a18\u5947\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u662f\u54ea \u4f4d\u5e08\u5144\u6258\u4eba\u6765\u627e\u6211\u5417\uff1f\u201d\u7518\u51e4\u6c60\u5728\u6000\u4e2d\u53d6\u51fa\u4e00\u5e45\u753b\u6765\uff0c\u9012\u7ed9\u5415\u56db\u5a18\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u662f\u8c01\u7684\u624b \u7b14\uff1f\u201d\u753b\u4e2d\u4e00\u53ea\u5de8\u9e70\uff0c\u5a01\u6b66\u4e4b\u6781\uff0c\u4f46\u5374\u88ab\u5173\u5728\u7b3c\u4e2d\uff0c\u9e70\u50cf\u4f38\u51fa\u7b3c\u6765\uff0c\u53cc\u7ffc\u5f20\u5f00\uff0c\u4f3c\u6b32\u9e23\u53eb\u3002 \u7b3c\u65c1\u6709\u4e00\u4e2a\u5c11\u5973\uff0c\u5f62\u8c8c\u9887\u4f3c\u6d59\u6c5f\u5de1\u629a\u7684\u5973\u513f\u674e\u660e\u73e0\u3002\u5415\u56db\u5a18\u770b\u4e86\u4e00\u9635\uff0c\u53eb\u8d77\u6765\u9053\uff1a\u201c\u96be\u9053\u662f \u8def\u5e08\u5144\u88ab\u6d59\u6c5f\u5de1\u629a\u8f6f\u7981\u4e86\uff1f\u201d \u3000\u3000\u8def\u6c11\u77bb\u662f\u4e16\u5bb6\u5b50\u5f1f\uff0c\u6b66\u529f\u867d\u7136\u4e0d\u9ad8\uff0c\u753b\u5374\u5f88\u662f\u51fa\u540d\uff0c\u5c24\u5176\u64c5\u957f\u753b\u9e70\u3002\u8fd9\u5e45\u753b\u628a\u795e\u9e70\u56da\u5728 \u7b3c\u4e2d\uff0c\u4f3c\u4e4e\u662f\u4ee5\u9e70\u55bb\u4eba\uff0c\u6697\u793a\u81ea\u5df1\u88ab\u7981\u3002\u5415\u56db\u5a18\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u7518\u5e08\u5144\uff0c\u8fd9\u5e45\u753b\u4f60\u662f\u600e\u4e48\u5f97\u6765 \u7684\uff1f\u201d \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u76ee\u9001\u5510\u5144\u3002\u4e0a\u5c71\u4e4b\u540e\uff0c\u5c31\u72ec\u81ea\u6298\u56de\uff0c\u8d70\u4e86\u4e09\u56db\u91cc\u8def\uff0c\u5ffd\u95fb\u5f97\u5c71\u540e\u6709\u9a6c\u5636 \u4e4b\u58f0\uff0c\u5c71\u98ce\u5439\u6765\uff0c\u8fd8\u9690\u9690\u6709\u51c4\u5389\u7684\u53eb\u58f0\u3002\u201d\u5415\u56db\u5a18\u9762\u8272\u500f\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u9053\u6709\u6e05\u5ef7\u9e70\u72ac\u77e5\u6211 \u9690\u5c45\u6b64\u5730\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u4e5f\u662f\u8fd9\u6837\u62c5\u5fc3\uff0c\u6240\u4ee5\u6025\u5fd9\u8dd1\u5230\u5c71\u540e\u53bb\u770b\uff0c\u53ea\u89c1\u9a7f\u9053\u4e0a\u5c18\u58eb\u98de \u626c\uff0c\u51e0\u5339\u9a6c\u5df2\u53bb\u5f97\u8fdc\u4e86\u3002\u6211\u81ea\u5ff5\u8ffd\u4e4b\u4e0d\u53ca\uff0c\u53ea\u597d\u5728\u9644\u8fd1\u4ed4\u7ec6\u5bdf\u89c6\uff0c\u5ffd\u89c1\u5c71\u811a\u7684\u5ca9\u77f3\u4e0a\uff0c\u6709\u51e0 \u5904\u6709\u70b9\u70b9\u8840\u8ff9\uff0c\u60f3\u662f\u521a\u624d\u6709\u4eba\u5728\u9a7f\u9053\u4e0a\u6fc0\u6597\uff0c\u76f4\u6253\u5230\u5c71\u8fb9\uff0c\u624d\u88ab\u6349\u53bb\u4e86\u7684\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a \u201c\u4f9d\u7518\u5927\u4fa0\u4e4b\u89c1\uff0c\u4ed6\u4eec\u662f\u4e0d\u662f\u60f3\u4e0a\u5c71\u6765\uff01\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u770b\u4e0d\u662f\u3002\u770b\u811a\u5370\u548c\u8840\u8ff9\uff0c\u4f3c\u662f\u4ece \u9a7f\u9053\u6253\u5230\u5c71\u8fb9\uff0c\u540e\u6765\u53c8\u8d8a\u6597\u8d8a\u8fdc\u3002\u770b\u60c5\u5f62\u4f3c\u662f\u51e0\u4e2a\u4eba\u56f4\u7740\u4e00\u4eba\uff0c\u540e\u6765\u8fd9\u4eba\u5c31\u88ab\u6349\u53bb\u4e86\u3002\u5047\u5982 \u90a3\u4e9b\u4eba\u662f\u60f3\u4e0a\u5c71\u7684\u8bdd\uff0c\u4ed6\u4eec\u5c31\u4e0d\u5fc5\u5728\u83b7\u80dc\u4e4b\u540e\uff0c\u6025\u6025\u7eb5\u9a6c\u98de\u9a70\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u4e0d\u89c9\u6709\u70b9\u62c5\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u88ab\u6349\u53bb\u7684\u90a3\u4eba\u4f1a\u4e0d\u4f1a\u662f\u8def\u5e08\u5144\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u770b\u4e5f \u4e0d\u662f\u3002\u8def\u5e08\u5144\u6ca1\u6709\u90a3\u4e48\u597d\u7684\u6b66\u529f\u3002\u201d\u5510\u6653\u6f9c\u5947\u9053\uff1a\u201c\u7518\u5927\u4fa0\u672a\u7ecf\u76ee\u51fb\uff0c\u600e\u4e48\u4f1a\u5206\u8fa8\u5f97\u51fa\u4ed6\u6b66 \u529f\u7684\u597d\u574f\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u5c71\u8fb9\u7684\u6ce5\u5730\u6e7f\u6ed1\uff0c\u811a\u5370\u5206\u660e\uff0c\u4ece\u811a\u5370\u7684\u5206\u5e03\u548c\u79fb\u52a8\u7684\u75d5\u8ff9\u6765\u770b\uff0c \u90a3\u662f\u51e0\u4eba\u5408\u653b\u4e00\u4eba\uff0c\u800c\u88ab\u653b\u7684\u4eba\u6b65\u6cd5\u5e76\u4e0d\u51cc\u4e71\uff0c\u8fdb\u9000\u4e4b\u95f4\uff0c\u751a\u6709\u6cd5\u5ea6\u3002\u8def\u5e08\u5144\u867d\u7136\u4e5f\u6709\u90a3\u7b49 \u6b66\u529f\uff0c\u4f46\u4ed6\u5c11\u7ecf\u9635\u4ed7\uff0c\u4e34\u573a\u672a\u5fc5\u80fd\u6709\u5982\u6b64\u9547\u5b9a\u3002\u201d\u7518\u51e4\u6c60\u5206\u6790\u5165\u5fae\uff0c\u4e0d\u552f\u5510\u6653\u6f9c\u4f69\u670d\uff0c\u8fde\u5415 \u56db\u5a18\u4e5f\u89c9\u5f97\u8fd9\u4f4d\u5e08\u54e5\uff0c\u7684\u786e\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u9605\u5386\u4e4b\u6df1\uff0c\u8fdc\u975e\u81ea\u5df1\u53ef\u6bd4\u3002 \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u6700\u8fd1\u6b63\u60f3\u51fa\u4e00\u6b21\u8fdc\u95e8\uff0c\u53bb\u62dc\u8bbf\u51e0\u4f4d\u5e08\u5144\u3002\u8fd8\u6709\uff0c\u6211\u524d\u5e74\u542c\u5f97\u4e8c\u5e08\u5144\uff08\u5468 \u6e05\uff09\u8bf4\uff0c\u5173\u4e1c\u56db\u4fa0\u5f88\u60f3\u548c\u6211\u89c1\u9762\uff0c\u4e5f\u8bb8\u6211\u4f1a\u5317\u4e0a\u4eac\u5e08\uff0c\u8fdc\u8d74\u8fbd\u4e1c\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a \u201c\u90a3\u597d\u6781\u4e86\uff0c\u6211\u548c\u5510\u6653\u6f9c\u660e\u65e5\u4e5f\u5c06\u8fdc\u884c\uff0c\u5c31\u548c\u5e08\u5144\u4e00\u9053\u5427\u3002\u6709\u5e08\u5144\u5728\u4e00\u8d77\uff0c\u6211\u4eec\u5b89\u5fc3\u5f97 \u591a\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u90a3\u6c88\u5148\u751f\u5462\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4ed6\u8fd1\u6765\u4f3c\u6709\u8fdb\u5883\uff0c\u4eca\u65e5\u5df2\u80fd\u6276\u58c1\u800c\u884c\u4e86\u3002 \u4ed6\u77e5\u9053\u4e86\u6653\u6f9c\u7684\u4e8b\uff0c\u5c31\u53eb\u6211\u66ff\u4ed6\u5411\u6c5f\u6e56\u4e0a\u7684\u4fa0\u4e49\u9053\u758f\u901a\u89e3\u91ca\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6c88\u5148\u751f\u809d\u80c6\u7167 \u4eba\uff0c\u867d\u7136\u662f\u4e2a\u4e66\u751f\uff0c\u4f46\u4fa0\u4e49\u4e4b\u98ce\uff0c\u72b9\u80dc\u4e8e\u543e\u8f88\uff01\u201d\u5f53\u4e0b\u8bf7\u5415\u56db\u5a18\u5f15\u89c1\uff0c\u5230\u9759\u5ba4\u91cc\u62dc\u89c1\u4e86\u6c88\u5728 \u5bbd\u3002\u5728\u5bbd\u542c\u5f97\u7518\u51e4\u6c60\u548c\u4e8c\u4eba\u540c\u884c\uff0c\u4ed6\u672c\u6765\u5df2\u65e0\u6742\u5ff5\uff0c\u73b0\u5728\u66f4\u662f\u653e\u5fc3\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u4e09\u4eba\u5c31\u4e0b\u5c71\u5317\u4e0a\u3002\u4e3a\u4e86\u65c5\u9014\u65b9\u4fbf\uff0c\u5415\u56db\u5a18\u5973\u626e\u7537\u88c5\uff0c\u7518\u51e4\u6c60\u64c5\u4e8e\u53d8\u8c8c\u6613\u5bb9 \u4e4b\u672f\uff0c\u7528\u836f\u66ff\u5510\u6653\u6f9c\u53d8\u4e86\u989c\u5bb9\uff0c\u4e00\u8def\u884c\u6765\uff0c\u679c\u7136\u65e0\u4eba\u80fd\u8bc6\u3002 \u3000\u3000\u4ed9\u971e\u5cad\u7684\u5317\u9762\u4fbf\u662f\u6d59\u6c5f\uff0c\u4e09\u4eba\u53d6\u9053\u9f99\u6e38\u3001\u91d1\u534e\u3001\u4e1c\u4e0b\u4e49\u4e4c\u3001\u7ecd\u5174\u3002\u76f4\u81f3\u8427\u5c71\uff0c\u51c6\u5907\u5148\u5230 \u8def\u6c11\u77bb\u5bb6\u4e61\u8bbf\u95ee\u3002\u8427\u5c71\u51fa\u53bb\uff0c\u4fbf\u662f\u676d\u5dde\u6cbf\u6d77\u4e00\u5e26\u3002\u5415\u56db\u5a18\u4e45\u56f0\u5c71\u4e2d\uff0c\u6b64\u65f6\u65b9\u5f97\u91cd\u89c1\u5927\u6d77\uff0c\u80f8 \u895f\u5bbd\u7545\uff0c\u8fd9\u65e5\u4ed6\u4eec\u6b63\u5411\u8def\u6c11\u77bb\u7684\u5bb6\u4e61\u8fdb\u53d1\uff0c\u5927\u8def\u9760\u5c71\u9762\u6d77\uff0c\u5415\u56db\u5a18\u4e00\u65f6\u5174\u8d77\uff0c\u548c\u7518\u5510\u4e8c\u4eba\uff0c \u8d70\u4e0a\u5c71\u4e0a\uff0c\u8fdc\u773a\u6d77\u6d0b\uff0c\u53ea\u89c1\u5927\u5c0f\u5c9b\u5c7f\uff0c\u661f\u7f57\u68cb\u5e03\uff0c\u6e14\u821f\u70b9\u70b9\uff0c\u6709\u5982\u6c34\u9e1f\u822c\u5728\u6ce2\u6d9b\u4e2d\u968f\u610f\u51fa \u6ca1\u3002\u6b63\u81ea\u5fc3\u65f7\u795e\u6021\uff0c\u5ffd\u542c\u5f97\u7518\u51e4\u6c60\u53d1\u58f0\u53eb\u5524\u3002 \u3000\u3000\u5415\u56db\u5a18\u56de\u8fc7\u5934\u6765\uff0c\u6115\u7136\u95ee\u9053\uff1a\u201c\u4e03\u54e5\u4f55\u4e8b\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u4e2a\u3002\u201d\u5e26\u5415\u56db\u5a18\u5230\u4e00 \u5757\u7a81\u51fa\u6765\u7684\u77f3\u5ca9\u4e0b\u9762\uff0c\u62c2\u4e86\u77f3\u4e0a\u5c18\u571f\uff0c\u53ea\u89c1\u4e0a\u9762\u753b\u4e00\u4e9b\u5947\u5947\u602a\u602a\u7684\u7b26\u53f7\uff0c\u5415\u56db\u5a18\u4e00\u4e2a\u4e5f\u4e0d\u8ba4 \u5f97\uff0c\u5947\u9053\uff1a\u201c\u8fd9\u4e9b\u7b26\u53f7\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u61c2\uff0c\u5927\u7ea6\u662f\u6c5f\u6e56\u5e2e\u4f1a\u7684\u6697\u8bb0\u3002\u201d \u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u4eec\u4f55\u5fc5\u7406\u8fd9\u4e9b\u95f2\u4e8b\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u4f60\u518d\u770b\u770b\u3002\u201d\u5e26\u5415\u56db\u5a18\u4e0b\u53bb\u770b\uff0c \u753b\u6709\u5404\u79cd\u7b26\u53f7\u7684\u5c71\u77f3\uff0c\u7adf\u6709\u4e94\u516d\u5904\u4e4b\u591a\u3002 \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6709\u4e24\u79cd\u7b26\u53f7\u6211\u7a0d\u4e3a\u61c2\u5f97\uff0c\u610f\u601d\u5927\u6982\u662f\u7ea6\u5b9a\u65e5\u671f\u5230\u8fd9\u91cc\u805a\u4f1a\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a \u201c\u96be\u9053\u8fd9\u5c71\u4e2d\u5367\u864e\u85cf\u9f99\uff0c\u5c45\u7136\u6709\u6c5f\u6e56\u8c6a\u6770\u4e48\uff1f\u4f46\uff0c\u8fd9\u4e5f\u4e0d\u5173\u6211\u4eec\u7684\u4e8b\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u516b \u59b9\uff0c\u4f60\u548c\u8def\u5e08\u5144\u4e00\u5728\u6d59\u4e1c\uff0c\u4e00\u5728\u6d59\u897f\uff0c\u6211\u4e5f\u7ecf\u5e38\u5f80\u6765\u6d59\u7696\u82cf\u8d63\u5404\u7701\uff0c\u6d59\u6c5f\u7684\u6210\u540d\u4eba\u7269\uff0c\u5e2e\u4f1a \u9996\u9886\uff0c\u6211\u4eec\u5168\u90fd\u77e5\u9053\uff0c\u53ef\u6ca1\u542c\u8fc7\u5728\u8def\u5e08\u5144\u5bb6\u4e61\uff0c\u4e5f\u6709\u6c5f\u6e56\u8c6a\u5ba2\u9690\u5c45\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e03\u54e5\u662f \u5426\u7591\u5fc3\u6b64\u4e8b\u4e0e\u8def\u5e08\u5144\u6709\u5173\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u8fd8\u4e0d\u6562\u65ad\u5b9a\uff0c\u4f46\u6b64\u53bb\u8def\u5e08\u5144\u5bb6\uff0c\u4e0d\u8fc7\u516b\u5341\u4f59 \u91cc\uff0c\u6211\u4ece\u672a\u542c\u8def\u5144\u8bf4\u8fc7\u4ed6\u7684\u5bb6\u4e61\u6709\u4ec0\u4e48\u6b66\u529f\u9ad8\u5f3a\u7684\u4eba\uff0c\u6240\u4ee5\u751a\u89c9\u5947\u602a\u3002\u6211\u4eec\u7d22\u6027\u518d\u641c\u5b83\u4e00 \u641c\u3002\u201d\u4e09\u4eba\u8f6c\u8fc7\u5c71\u8170\uff0c\u7ed5\u8fc7\u51e0\u5c42\u5cf0\u5cfb\u7684\u77f3\u5d0e\uff0c\u89c1\u5bf9\u9762\u5c71\u8170\uff0c\u6709\u7f15\u7f15\u708a\u70df\uff0c\u5415\u56db\u5a18\u9053\uff1a\u201c\u90a3\u8fb9 \u5c71\u4e2d\u4f3c\u6709\u4eba\u5bb6\u3002\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u6211\u4eec\u7d22\u6027\u5230\u90a3\u8fb9\u5cad\u4e0a\u53bb\u770b\u3002\u201d\u4e09\u4eba\u7ed5\u8fc7\u5c71\u80cc\uff0c\u8d70\u4e0b\u5cad\u6765\uff0c \u5cad\u4e0b\u9762\u5c45\u7136\u6709\u4e00\u5c42\u5c42\u7684\u5c71\u7530\uff0c\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u4e0d\u6b62\u6709\u4eba\u5bb6\uff0c\u8fd8\u6709\u6751\u843d\u5462\uff01\u201d \u3000\u3000\u4e09\u4eba\u6a2a\u8fc7\u4e24\u5c71\u73af\u62b1\u7684\u5e7d\u8c37\uff0c\u5230\u4e86\u5bf9\u9762\u5c71\u811a\uff0c\u7518\u51e4\u6c60\u653e\u6162\u811a\u6b65\uff0c\u4e00\u9762\u4e0a\u5c71\uff0c\u4e00\u9762\u7528\u76ee\u5149\u641c \u7d22\uff0c\u4e0a\u5230\u534a\u5c71\uff0c\u53c8\u53d1\u73b0\u4e86\u4e24\u4e09\u5904\u5e2e\u4f1a\u7684\u6697\u8bb0\u3002\u884c\u4e86\u4e00\u9635\uff0c\u5c06\u5230\u5c71\u9876\uff0c\u53ea\u89c1\u5c71\u7684\u90a3\u8fb9\u3001\u708a\u59fb\u56db \u8d77\uff0c\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8d70\u4e86\u534a\u5929\uff0c\u6df1\u5165\u5c71\u5730\uff0c\u5c71\u4e0b\u4eba\u5bb6\u5df2\u6536\u5de5\u6b47\u606f\uff0c\u708a\u8d77\u665a\u996d\u6765\u4e86\u3002\u6211\u4eec \u8fd8\u4e0d\u51fa\u53bb\uff0c\u4eca\u665a\u53ef\u8981\u5728\u8fd9\u513f\u501f\u5bbf\u4e86\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u518d\u770b\u4e00\u770b\u3002\u201d\u5ffd\u7136\u505c\u4e0b\u6b65\u6765\uff0c\u9732\u51fa\u60ca\u5f02 \u4e4b\u5bb9\u3002 \u3000\u3000\u7518\u51e4\u6c60\u5e73\u65e5\u4e3a\u4eba\uff0c\u6df1\u6c89\u4e0d\u9732\u3002\u7eb5\u7136\u672a\u6709\u201c\u6cf0\u5c71\u5d29\u4e8e\u524d\u800c\u76ee\u4e0d\u77ac\u201d\u7684\u4fee\u517b\uff0c\u4e34\u4e8b\u9547\u5b9a\u7684\u529f \u592b\uff0c\u5728\u540c\u95e8\u4e4b\u4e2d\u5374\u9996\u5c48\u4e00\u6307\u3002\u5415\u56db\u5a18\u5947\u9053\uff1a\u201c\u4e03\u54e5\uff0c\u4f60\u53c8\u77a7\u89c1\u4ec0\u4e48\uff1f\u201d\u7518\u51e4\u6c60\u6c89\u541f\u4e0d\u8bed\uff0c\u8fc7 \u4e86\u534a\u664c\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u6562\u60c5\u6709\u54ea\u4f4d\u5e08\u5144\u88ab\u56f0\u6b64\u5730\uff0c\u8fd9\u53ef\u771f\u53eb\u6211\u731c\u4e0d\u900f\u4e86\uff01\u201d\u5e26\u5415\u56db\u5a18\u8d70\u5230\u4e00 \u5757\u77f3\u5ca9\u5e95\u4e0b\uff0c\u77f3\u5ca9\u4fa7\u8fb9\u6709\u4e00\u5757\u5c16\u77f3\uff0c\u7a81\u51fa\u5982\u5251\uff0c\u5149\u6ed1\u5982\u955c\uff0c\u77f3\u4e0a\u523b\u7684\u7b26\u53f7\uff0c\u7adf\u7136\u662f\u4ed6\u4eec\u540c\u95e8 \u95f4\u6240\u7528\u7684\u6697\u53f7\uff0c\u201d\u7b26\u53f7\u751a\u4e3a\u7b80\u5355\u6f66\u8349\uff0c\u753b\u7684\u662f\u201c\u88ab\u56f0\uff0c\u76fc\u63f4\u201d\u56db\u5b57\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f60\u770b\uff0c\u8fd9 \u662f\u4e0d\u662f\u6211\u4eec\u540c\u95e8\u6240\u7559\u7684\u6697\u8bb0\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6709\u4f55\u7591\u70b9\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u82e5\u662f\u6211\u4eec\u540c\u95e8\u7684\u6697 \u8bb0\uff0c\u4f55\u4ee5\u6ca1\u6709\u6570\u5b57\u7b26\u53f7\uff1f\u201d\u539f\u6765\u72ec\u81c2\u8001\u5c3c\u95e8\u4e0b\uff0c\u5728\u4e92\u901a\u8baf\u606f\u65f6\uff0c\u7f72\u540d\u7684\u6697\u53f7\uff0c\u5fc5\u4ee5\u6392\u884c\u6b21\u5e8f \u66ff\u4ee3\u3002\u4f8b\u5982\u7518\u51e4\u6c60\u5e94\u7f72\u201c\u4e03\u201d\u5b57\uff0c\u5415\u56db\u5a18\u5e94\u7f72\u201c\u516b\u201d\u5b57\uff0c\u5415\u56db\u5a18\u4e0d\u662f\u4e0d\u77e5\uff0c\u53ef\u662f\u4e4d\u89c1\u77f3\u4e0a\u7b26 \u53f7\uff0c\u786e\u662f\u672c\u95e8\u6697\u8bb0\uff0c\u5c31\u4e0d\u7ec6\u770b\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8fd8\u6709\u66f4\u5947\u7684\u5462\uff0c\u4f60\u770b\u5f97\u51fa\u6765\u5417\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e03\u54e5\uff0c\u53ea\u51ed\u8fd9\u51e0\u4e2a\u7b80\u7b80\u5355\u5355\u7684\u7b26\u53f7\uff0c\u4f60\u600e\u4e48\u770b\u5f97\u51fa\u90a3\u4e48\u591a\u4e1c\u897f\uff1f\u8fd8\u6709\u4ec0\u4e48 \u66f4\u5947\u7684\u4e8b\u5462\uff1f\u6211\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\u6765\uff1b\u8bf7\u4f60\u63ed\u660e\uff0c\u4ee5\u5f00\u8305\u585e\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u4e9b\u6697\u53f7\u4e43\u662f \u4ee5\u6307\u4ee3\u7b14\uff0c\u7528\u6307\u529b\u5728\u77f3\u5934\u4e0a\u523b\u5212\u51fa\u6765\u7684\u3002\u770b\u8fd9\u4eba\u529f\u529b\u5728\u8def\u5e08\u5144\u4e4b\u4e0a\u800c\u5728\u767d\u5e08\u5144\u4e4b\u4e0b\uff0c\u5012\u79f0\u5468 \uff08\u6e05\uff09\u66f9\uff08\u4ec1\u7236\uff09\u4e8c\u4f4d\u5e08\u5144\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u4f46\u82e5\u662f\u5468\u66f9\u4e8c\u4eba\u6240\u2018\u5199\u2019\uff0c\u7b14\u8ff9\uff0c\u5fc5\u7136\u82cd\u8001\uff0c\u4f46\u8fd9 \u4e9b\u7b26\u53f7\uff0c\u70b9\u5212\u4e4b\u95f4\uff0c\u9887\u5e26\u7a1a\u6c14\uff0c\u6211\u6562\u65ad\u5b9a\uff0c\u7559\u8fd9\u4e2a\u6697\u8bb0\u7684\u7edd\u4e0d\u662f\u672c\u95e8\u4e4b\u4eba\u3002\u201d\u5415\u56db\u5a18\u6697\u6697\u4f69 \u670d\u3002\u7518\u51e4\u6c60\u770b\u4e86\u4e00\u9635\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u7559\u6697\u8bb0\u7684\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u6211\u6b64\u523b\u5df2\u5927\u81f4\u53ef\u4ee5\u63e3\u5ea6\u51fa\u6765\u4e86\u3002\u201d \u5415\u56db\u5a18\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u4e03\u54e5\uff0c\u4f60\u4e14\u6162\u8bf4\uff0c\u8ba9\u6211\u731c\u4e00\u731c\u770b\u3002\u201d\u5510\u6653\u6f9c\u4e5f\u5728\u51dd\u795e\u6ce8\u89c6\uff0c\u4f46\u5374\u770b\u4e0d\u51fa \u4ec0\u4e48\u9053\u7406\uff0c\u6b63\u5728\u7eb3\u95f7\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u7559\u6697\u8bb0\u7684\u4eba\u662f\u4e2a\u5973\u5b50\uff0c\u6bd4\u6211\u8fd8\u8981\u5e74\u8f7b\u3002\u201d\u7518\u51e4\u6c60\u62cd\u624b\u7b11 \u9053\uff1a\u201c\u5bf9\u4e86\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u5230\u5e95\u600e\u4e48\u770b\u51fa\u6765\u7684\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4f60\u7814\u7a76\u8fc7\u4e66\u6cd5\u6ca1\u6709\uff1f\u201d\u5510\u6653\u6f9c \u9053\uff1a\u201c\u6211\u5e7c\u5e74\u5931\u5b66\uff0c\u540e\u6765\u5728\u6768\u5e08\u95e8\u4e0b\uff0c\u624d\u6709\u673a\u4f1a\u8bfb\u4e66\uff0c\u90a3\u65f6\u53ea\u662f\u8d2a\u8bfb\u8bd7\u4e66\uff0c\u5f88\u5c11\u6267\u7b14\u7ec3 \u5b57\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5973\u5b50\u5199\u7684\u5b57\u603b\u6bd4\u7537\u5b50\u67d4\u5a9a\uff0c\u8fd9\u4f60\u5e94\u8be5\u77e5\u9053\u7684\u4e86\u3002\u8fd9\u4e9b\u7b26\u53f7\u867d\u7136\u5e76\u4e0d\u662f\u65b9 \u5757\u5b57\uff0c\u800c\u4e14\u662f\u7528\u6307\u529b\u6240\u5212\uff0c\u4f46\u4e5f\u79bb\u4e0d\u5f00\u70b9\u5212\u52fe\u6487\uff0c\u548c\u5199\u5b57\u6709\u5171\u901a\u4e4b\u5904\uff0c\u5982\u4f55\u770b\u4e0d\u51fa\u6765\u3002\u201d\u7518 \u51e4\u6c60\u9053\uff1a\u201c\u6211\u5012\u4e0d\u662f\u4ece\u4e66\u6cd5\u4e0a\u9886\u609f\uff0c\u800c\u662f\u770b\u90a3\u4e9b\u7b26\u53f7\uff0c\u7ebf\u6761\u7ea4\u7ec6\uff0c\u53ef\u4ee5\u731c\u51fa\u90a3\u662f\u5973\u5b50\u7684\u6307\u5934 \u5212\u7684\u3002\u516b\u59b9\uff0c\u4f60\u867d\u6b20\u7f3a\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u5374\u771f\u806a\u660e\u3002\u201d\u5415\u56db\u5a18\u9762\u7ea2\u7b11\u9053\uff1a\u201c\u6211\u8fde\u662f\u4e0d\u662f\u540c\u95e8\u6240\u7559\u7684 \u90fd\u770b\u4e0d\u51fa\u6765\u5462\uff0c\u8fd8\u8bf4\u806a\u660e\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u90a3\u602a\u4e0d\u5f97\u4f60\uff0c\u4f60\u548c\u597d\u51e0\u4f4d\u5e08\u5144\u8fd8\u672a\u89c1\u8fc7\u9762\uff0c\u5bf9\u4ed6 \u4eec\u7684\u529f\u529b\u5982\u4f55\uff0c\u81ea\u7136\u6ca1\u6709\u6211\u8fd9\u6837\u719f\u6089\u3002\u201d\u7518\u51e4\u6c60\u60f3\u4e86\u4e00\u60f3\uff0c\u53c8\u9053\uff1a\u201c\u540c\u95e8\u4e2d\u9664\u4f60\u4e4b\u5916\uff0c\u522b\u65e0 \u5973\u5b50\uff0c\u5979\u5982\u4f55\u8bc6\u5f97\u6211\u4eec\u7684\u6697\u8bb0\uff0c\u8fd9\u5012\u5947\u4e86\u3002\u65e2\u5230\u6b64\u5730\uff0c\u6211\u4eec\u7d22\u6027\u5230\u4e0b\u9762\u5c71\u6751\uff0c\u63a2\u5b83\u4e00\u63a2\u3002\u201d \u3000\u3000\u4e09\u4eba\u6500\u4e0a\u5c71\u5934\uff0c\u4fef\u9996\u4e0b\u89c6\uff0c\u540e\u5c71\u7684\u60c5\u666f\u53c8\u662f\u4e0d\u540c\uff0c\u53ea\u89c1\u5c42\u5c42\u7684\u5c0f\u5c71\u5cf0\uff0c\u66f2\u66f2\u6298\u6298\uff0c\u5b9b\u82e5 \u91cd\u95e8\u53e0\u6237\uff0c\u4f46\u534a\u5c71\u8170\u5904\uff0c\u5374\u7528\u4eba\u5de5\u8f9f\u6210\u76d8\u65cb\u7684\u5c71\u9053\u3002\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u5f62\u52bf\u5012\u8fd8\u4e0d \u9519\u3002\u201d\u4e09\u4eba\u5c31\u4ece\u5c71\u9053\u4e0a\u4e00\u8def\u8d70\u4e0b\u6765\uff0c\u8d70\u5230\u534a\u5c71\uff0c\u5df2\u89c1\u5c71\u811a\u6709\u51e0\u5341\u5bb6\u4eba\u5bb6\u3002\u8fd9\u65f6\u7ea2\u65e5\u5c31\u5c06\u897f \u4e0b\uff0c\u767e\u9e1f\u5f52\u5de2\uff0c\u5431\u5431\u55b3\u55b3\u7684\u53eb\u5f97\u597d\u4e0d\u70ed\u95f9\u3002 \u3000\u3000\u4e09\u4eba\u4e0b\u5c71\u4e4b\u540e\uff0c\u884c\u8fdb\u6751\u4e2d\uff0c\u6751\u4eba\u5927\u90fd\u5df2\u5728\u5c4b\u5185\u7528\u81b3\uff0c\u53ea\u6709\u5be5\u5be5\u51e0\u4e2a\u5728\u5916\u9762\u95f2\u901b\uff0c\u89c1\u4e86\u8fd9 \u4e09\u4e2a\u964c\u751f\u7684\u5ba2\u4eba\uff0c\u751a\u4e3a\u60ca\u8be7\u3002\u6751\u4e2d\u6709\u4e00\u5bb6\u6731\u95e8\u5927\u6237\uff0c\u5728\u5468\u56f4\u7684\u6c11\u623f\u6620\u886c\u4e0b\uff0c\u5206\u5916\u77a9\u76ee\uff0c\u7518\u51e4 \u6c60\u4fbf\u5411\u90a3\u5bb6\u4eba\u5bb6\u8d70\u53bb\uff0c\u6709\u4eba\u4e0a\u6765\u95ee\u9053\uff1a\u201c\u8d35\u5ba2\u53ef\u662f\u6765\u627e\u5c1a\u5e84\u4e3b\u5417\uff1f\u201d\u7518\u51e4\u6c60\u5e94\u9053\uff1a\u201c\u6b63 \u662f\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u8bf7\u7b49\u4e00\u7b49\u3002\u201d\u8dd1\u6b65\u5982\u98de\uff0c\u5148\u5165\u5c4b\u4e2d\uff0c\u7518\u51e4\u6c60\u6084\u58f0\u5bf9\u5415\u56db\u5a18\u9053\uff1a\u201c\u7b49\u4f1a\u6211\u4eec \u8fdb\u53bb\uff0c\u7531\u6211\u7b54\u8bdd\uff0c\u82e5\u6709\u4ec0\u4e48\u4e8b\u53d8\uff0c\u8bf7\u770b\u611a\u5144\u773c\u8272\u884c\u4e8b\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u4eec\u5f53\u7136\u662f\u552f\u5e08\u5144\u7684 \u9a6c\u9996\u662f\u77bb\u3002\u201d\u8bf4\u8bdd\u95f4\uff0c\u5df2\u5230\u4e86\u90a3\u5bb6\u95e8\u524d\uff0c\u4e24\u6247\u6731\u6f06\u5927\u95e8\uff0c\u5ffd\u7136\u6253\u5f00\u3002 \u3000\u3000\u4e24\u540d\u5927\u6c49\u8d70\u51fa\u95e8\u5916\uff0c\u4f38\u51fa\u4e2d\u6307\uff0c\u5411\u5730\u4e0a\u4e00\u6307\u5531\u4e86\u4e2a\u558f\uff0c\u95ee\u9053\uff1a\u201c\u4e09\u4f4d\u662f\u9053\u4e0a\u540c\u6e90\uff0c\u8fd8\u662f \u8fc1\u91d1\u4eba\u58eb\uff1f\u201d\u8fd9\u662f\u5e2e\u4f1a\u4e2d\u5e38\u7528\u7684\u9ed1\u8bdd\uff0c\u610f\u601d\u662f\u95ee\uff1a\u4f60\u4eec\u662f\u540c\u9053\u7684\u522b\u4e00\u5e2e\u6d3e\u7684\u4eba\u5462\uff0c\u8fd8\u662f\u672c\u5e2e \u5144\u5f1f\u6765\u671d\u89c1\u9f99\u5934\u5927\u54e5\u7684\uff1f\u7518\u51e4\u6c60\u7ad6\u8d77\u62c7\u6307\uff0c\u5411\u5929\u4e0a\u4e00\u62ac\uff0c\u5927\u523a\u523a\u7684\u8bf4\u9053\uff1a\u201c\u65e5\u6708\u661f\u8fb0\uff0c\u4e0d\u5f52 \u6ce5\u571f\u3002\u201d\u4e24\u540d\u5927\u6c49\u9762\u8272\u4e00\u53d8\uff0c\u6025\u5fd9\u606d\u606d\u656c\u656c\u7684\u8bf4\u9053\uff1a\u201c\u4e09\u4f4d\u8bf7\u8fdb\uff0c\u5f85\u5c0f\u7684\u7980\u544a\u5e84\u4e3b\u3002\u201d\u539f\u6765 \u90a3\u4e24\u53e5\u8bdd\u662f\u7518\u51e4\u6c60\u81ea\u8868\u8eab\u4efd\uff0c\u8bf4\u660e\u81ea\u5df1\u4e0d\u662f\u5e2e\u4f1a\u4e2d\u4eba\uff0c\u8c01\u4eba\u4e5f\u7ba1\u4ed6\u4e0d\u7740\u3002\u8bf4\u8fd9\u8bdd\u7684\u4eba\uff0c\u82e5\u975e \u4e00\u6d3e\u9996\u9886\uff0c\u5c31\u662f\u6210\u540d\u4eba\u7269\u3002 \u3000\u3000\u4e24\u540d\u5927\u6c49\u5c06\u4e09\u4eba\u5f15\u8fdb\u5ba2\u5385\uff0c\u5750\u4e86\u4e00\u9635\uff0c\u53ea\u89c1\u4e00\u4e2a\u5e74\u7ea6\u516d\u65ec\u7684\u58ee\u5065\u8001\u4eba\uff0c\u4ece\u540e\u5802\u8d70\u4e86\u51fa \u6765\uff0c\u60f3\u5fc5\u5c31\u662f\u4ec0\u4e48\u5c1a\u5e84\u4e3b\u4e86\u3002\u5510\u6653\u6f9c\u6b63\u60f3\u8d77\u7acb\uff0c\u7518\u51e4\u6c60\u8f7b\u8f7b\u7528\u624b\u8098\u649e\u4e86\u4ed6\u4e00\u4e0b\uff0c\u62a2\u5148\u7ad9\u4e86\u8d77 \u6765\uff0c\u5411\u4e2d\u95f4\u884c\u8fdb\u4e09\u6b65\uff0c\u7136\u540e\u5411\u5de6\u4fa7\u8de8\u4e00\u6b65\uff0c\u518d\u5411\u53f3\u4fa7\u8de8\u4e00\u6b65\uff0c\u7136\u540e\u53c8\u4fa7\u9000\u4e09\u6b65\uff0c\u8fd9\u624d\u793a\u610f\u53eb \u5415\u5510\u4e8c\u4eba\u8d77\u6765\uff0c\u62b1\u62f3\u884c\u793c\u3002\u8fd9\u8001\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u9887\u4e3a\u6c14\u607c\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5c1a\u5e84\u4e3b\u6765\u5934\u9887\u5927\uff0c\u542c\u5f97\u624b\u4e0b\u4eba\u7980\u544a\uff0c\u8bf4\u662f\u6709\u8fd9\u4e48\u6837\u7684\u4e09\u4e2a\u4eba\u6765\u89c1\uff0c\u6025\u5fd9\u51fa\u6765\u8fce \u63a5\uff0c\u4e00\u89c1\u4e4b\u4e0b\u5927\u4e3a\u5931\u671b\uff0c\u4ed6\u4ee5\u4e3a\u6765\u7684\u5fc5\u662f\u6210\u540d\u4eba\u7269\uff0c\u8c01\u77e5\u5168\u4e0d\u8ba4\u5f97\u3002\u800c\u4e14\u5415\u5510\u4e8c\u4eba\uff0c\u90fd\u5f88\u5e74 \u9752\uff0c\u5c24\u5176\u662f\u5415\u56db\u5a18\u56e0\u4e3a\u5f97\u4e86\u6613\u5170\u73e0\u201c\u655b\u7cbe\u5185\u89c6\u201d\u4e4b\u672f\uff0c\u770b\u6765\u4e0d\u8fc7\u662f\u4e8c\u5341\u5c81\u5de6\u53f3\u7684\u6587\u5f31\u4e66\u751f\uff1b \u4e09\u4eba\u4e2d\u7518\u51e4\u6c60\u5e74\u7eaa\u8f83\u5927\uff0c\u6001\u5ea6\u4e5f\u8f83\u4e3a\u8001\u6210\uff0c\u4f46\u4e5f\u4e0d\u8fc7\u662f\u4e09\u5341\u591a\u5c81\u7684\u4e2d\u5e74\u4eba\uff0c\u800c\u4e14\u9762\u9ec4\u808c\u7626\uff0c \u6d3b\u50cf\u4e00\u4e2a\u75c5\u592b\u3002\u5c1a\u5e84\u4e3b\u5fc3\u4e2d\u5600\u5495\uff0c\u4ee5\u4e3a\u4ed6\u4eec\u662f\u6c5f\u6e56\u9a97\u5b50\u3002\u4f46\u7518\u51e4\u6c60\u8fdb\u800c\u590d\u9000\uff0c\u884c\u7684\u5374\u662f\u5e73\u8f88 \u4e4b\u793c\uff0c\u5c1a\u5e84\u4e3b\u521d\u65f6\u6c14\u607c\uff0c\u4f46\u4e00\u60f3\u4ed6\u4eec\u65e2\u7136\u80fd\u627e\u5230\u6b64\u5730\uff0c\u5fc5\u77e5\u81ea\u5df1\u540d\u5934\uff0c\u65e2\u6562\u7528\u5e73\u8f88\u4e4b\u672d\u76f8 \u89c1\uff0c\u5f53\u975e\u7b49\u95f2\u4eba\u7269\u3002\u6c5f\u6e56\u4e0a\u5f02\u4eba\u751a\u591a\uff0c\u4ed6\u4e5f\u5c31\u4e0d\u6562\u6020\u6162\u3002\u5f53\u4e0b\u54c8\u54c8\u4e00\u7b11\uff0c\u62b1\u62f3\u8fd8\u793c\uff0c\u6697\u8fd0\u5185 \u5bb6\u771f\u529b\uff0c\u62f3\u98ce\u5411\u7518\u51e4\u6c60\u649e\u6765\uff0c\u7518\u51e4\u6c60\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u8fd8\u4e86\u4e00\u63d6\uff0c\u5c1a\u5e84\u4e3b\u8eab\u4f53\u5fae\u5fae\u4e00\u6643\uff0c\u6025\u5fd9\u5b9a \u7740\u3002\u7b11\u9053\uff1a\u201c\u8001\u5144\u597d\u529f\u592b\uff0c\u8fd9\u4e24\u4f4d\u5c0f\u54e5\u662f\u8001\u5144\u95e8\u4e0b\u4e48\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u96c1\u884c\u5e76\u5217\uff0c\u90fd\u662f\u540c \u8f88\u3002\u201d\u5c1a\u5e84\u4e3b\u53c8\u6014\u4e86\u4e00\u6014\uff0c\u4f38\u51fa\u624b\u6765\uff0c\u5411\u5415\u56db\u5a18\u9053\uff1a\u201c\u8001\u673d\u5931\u8a00\uff0c\u8bf7\u6055\u65e0\u77e5\u3002\u201d\u5415\u56db\u5a18\u4f38\u624b \u4e0e\u4ed6\u4e00\u63e1\uff0c\u5c1a\u5e84\u4e3b\u7a81\u89c9\u624b\u8155\u9178\u9ebb\uff0c\u59cb\u77e5\u5415\u56db\u5a18\u529f\u592b\u8fd8\u5728\u7518\u51e4\u6c60\u4e4b\u4e0a\u3002\u4e09\u4eba\u4e2d\u5510\u6653\u6f9c\u6700\u4e3a\u58ee\u5065 \u82f1\u4fca\uff0c\u5c1a\u5e84\u4e3b\u4e0d\u6562\u518d\u8bd5\uff0c\u6025\u5fd9\u8083\u8bf7\u4e09\u4eba\u5c31\u5ea7\u3002 \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u542c\u8bf4\u5e84\u4e3b\u505a\u5bff\uff0c\u6211\u4eec\u5144\u5f1f\u4e09\u4eba\uff0c\u7279\u6765\u53e8\u6270\u3002\u201d\u5510\u6653\u6f9c\u83ab\u540d\u5176\u5999\uff0c\u5fc3\u60f3\uff1a\u7518 \u5927\u4fa0\u600e\u4e48\u77e5\u9053\u8fd9\u4e2a\u8001\u5934\u505a\u5bff\uff1f\u8c01\u77e5\u8fd9\u53c8\u662f\u5e2e\u4f1a\u7684\u9ed1\u8bdd\u3002\u7518\u51e4\u6c60\u4e00\u8def\u884c\u6765\uff0c\u89c1\u4e86\u5341\u591a\u5904\u5e2e\u4f1a\u7684 \u6697\u8bb0\uff0c\u9ed8\u5bdf\u60c5\u5f62\uff0c\u6599\u60f3\u8fd9\u91cc\u7684\u4e3b\u4eba\uff0c\u5fc5\u7136\u662f\u4e00\u4e2a\u5927\u5e2e\u4f1a\u7684\u5934\u5b50\uff0c\u53ef\u80fd\u5c31\u5728\u6700\u8fd1\uff0c\u8981\u9080\u8bf7\u5404\u5e2e \u4f1a\u7684\u4eba\u6765\u8fd9\u91cc\u79d8\u5bc6\u805a\u4f1a\u3002\u8fd9\u79cd\u805a\u4f1a\uff0c\u79f0\u4e3a\u201c\u505a\u5bff\u201d\uff0c\u6240\u4ee5\u7518\u51e4\u6c60\u51fa\u8a00\u8bd5\u649e\u3002\u679c\u7136\u4e00\u649e\u4fbf\u5bf9\uff0c \u5c1a\u5e84\u4e3b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e09\u4f4d\u9ad8\u8d24\uff1b\u60e0\u7136\u80af\u6765\uff0c\u771f\u662f\u589e\u5149\u4e0d\u5c11\uff0c\u53ea\u662f\u8fd8\u6709\u51e0\u5929\uff0c\u624d\u662f\u5bff\u671f\uff0c\u8981\u59d4 \u5c48\u4e09\u4f4d\u9ad8\u8d24\u5728\u820d\u4e0b\u5c0f\u4f4f\u4e86\uff01\u201d \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u53ea\u6709\u53e8\u6270\u4e86\u3002\u201d\u5f53\u4e0b\u8bf7\u6559\u59d3\u540d\uff0c\u7518\u51e4\u6c60\u5316\u540d\u5510\u9f99\uff0c\u5415\u56db\u5a18\u5316 \u540d\u674e\u53cc\u53cc\uff0c\u5510\u6653\u6f9c\u5316\u540d\u51af\u5c27\u3002\u5c1a\u5e84\u4e3b\u95ee\u9053\uff1a\u201c\u4e09\u4f4d\u5728\u54ea\u91cc\u5f00\u5c71\u7acb\u67dc\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6d41\u6c34\u884c \u4e91\uff0c\u6ca1\u4e2a\u5b9a\u5904\u3002\u201d\u5c1a\u5f80\u4e3b\u53c8\u9053\uff1a\u201c\u90a3\u4e48\u4e09\u4f4d\u662f\u4e0a\u7ebf\u6302\u724c\u7684\u4e86\uff1f\u201d\u7518\u51e4\u6c60\u53c8\u7b11\u9053\uff1a\u201c\u4e0d\u5f52\u6807\uff0c \u4e0d\u7acb\u67dc\uff0c\u6709\u82b1\u8d4f\u82b1\uff0c\u6709\u9152\u559d\u9152\uff0c\u4e94\u6e56\u56db\u6d77\u7686\u670b\u53cb\u3002\u201d\u4e24\u4eba\u7528\u6c5f\u6e56\u2018\u5507\u5178\u2019\uff08\u6697\u8bed\uff09\u95ee\u7b54\uff0c\u542c \u5f97\u5510\u6653\u6f9c\u76ca\u53d1\u832b\u7136\uff0c\u539f\u6765\u5c1a\u5e84\u4e3b\u60ca\u7591\u4e0d\u5b9a\uff0c\u4e00\u518d\u8bd5\u63a2\uff0c\u5148\u95ee\u4ed6\u662f\u4e0d\u662f\u5360\u636e\u5c71\u5934\u7684\u5be8\u4e3b\u6216\u8005\u5f00 \u9999\u5802\u7684\u5927\u54e5\uff0c\u7518\u51e4\u6c60\u8bf4\u4e0d\u662f\uff0c\u4e8e\u662f\u5c1a\u5e84\u4e3b\u53c8\u95ee\u4ed6\u4eec\u662f\u4e0d\u662f\u72ec\u884c\u5927\u76d7\uff08\u4e0a\u7ebf\u6302\u724c\u610f\u5373\u5728\u6c5f\u6e56\u6d41 \u7a9c\uff0c\u56db\u51fa\u52ab\u63a0\u3002\uff09\u7518\u51e4\u6c60\u53c8\u8bf4\u4e0d\u662f\uff0c\u800c\u4e14\u8bf4\u660e\u4ed6\u4eec\u548c\u9ed1\u767d\u4e24\u9053\u90fd\u6ca1\u7275\u8fde\uff08\u4e0d\u5f52\u6807\uff09\uff0c\u4f46\u5728\u6c5f \u6e56\u4e0a\u5374\u5230\u5904\u90fd\u6709\u670b\u53cb\u3002\u8fd9\u6837\u7684\u8eab\u4efd\u975e\u540c\u5c0f\u53ef\uff0c\u4e0d\u662f\u524d\u8f88\u9ad8\u4eba\u5c31\u662f\u6210\u540d\u4fa0\u5ba2\uff0c\u9976\u662f\u5c1a\u5e84\u4e3b\u89c1\u591a \u8bc6\u5e7f\uff0c\u4e5f\u81ea\u6349\u6478\u4e0d\u5b9a\u3002\u4ed6\u628a\u6c5f\u6e56\u4e0a\u7684\u6210\u540d\u4eba\u7269\uff0c\u5728\u5fc3\u4e2d\u6697\u6570\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u90fd\u6ca1\u6709\u50cf\u4ed6\u4eec\u90a3\u6837 \u5e74\u9752\u7684\u4eba\u3002\u5c1a\u5e84\u4e3b\u65e0\u6cd5\uff0c\u53ea\u597d\u4ee5\u4e0a\u5bbe\u4e4b\u793c\u76f8\u5f85\uff0c\u53eb\u5e84\u4e01\u5e26\u4ed6\u4eec\u5230\u5ba2\u623f\u5b89\u6b47\u3002\u5c1a\u5e84\u4e3b\u5fc3\u60f3\uff0c\u97e9 \u8001\u602a\u4e0d\u662f\u4eca\u665a\uff0c\u4fbf\u662f\u660e\u5929\uff0c\u5c31\u8981\u6765\u5230\u3002\u4ed6\u4e00\u5b9a\u4f1a\u770b\u51fa\u8fd9\u4e09\u4e2a\u4eba\u7684\u6765\u5386\u3002 \u3000\u3000\u4e09\u4eba\u8fdb\u4e86\u5ba2\u623f\uff0c\u5415\u56db\u5a18\u6084\u6084\u95ee\u9053\uff1a\u201c\u4e03\u54e5\u4e00\u5411\u6df1\u6c89\u4e0d\u9732\uff0c\u4f55\u4ee5\u8fd9\u56de\u81ea\u8868\u8eab\u4efd\u3002\u201d\u7518\u51e4\u6c60 \u9053\uff1a\u201c\u8fd9\u4e2a\u5c1a\u5e84\u4e3b\u5b9a\u662f\u5e2e\u4f1a\u7684\u9996\u9886\u65e0\u7591\u3002\u6211\u4eec\u5e73\u767d\u95ef\u6765\uff0c\u82e5\u975e\u7a0d\u4e3a\u70ab\u9732\u4e00\u4e0b\uff0c\u4ed6\u90a3\u91cc\u80af\u62db \u5f85\uff0c\u6211\u4eec\u65e2\u975e\u5e2e\u4f1a\u4e2d\u4eba\uff0c\u90a3\u5c31\u53ea\u597d\u5047\u6258\u6c5f\u6e56\u6e38\u4fa0\u7684\u8eab\u4efd\u4e86\u3002\u201d\u5176\u5b9e\uff0c\u7518\u51e4\u6c60\u6b63\u662f\u5f53\u65f6\u6c5f\u6e56\u4e0a \u58f0\u540d\u6700\u5927\u7684\u4e00\u4f4d\u6e38\u4fa0\uff0c\u6839\u672c\u4e0d\u662f\u201c\u5047\u6258\u201d\u3002\u4e5f\u6b63\u56e0\u6b64\uff0c\u5c1a\u5e84\u4e3b\u600e\u6837\u4e5f\u4e0d\u6562\u60f3\u5230\u7518\u51e4\u6c60\u5c31\u662f\u4ed6\u3002 \u3000\u3000\u6b47\u4e86\u4e00\u9635\uff0c\u5c1a\u5e84\u4e3b\u9063\u4eba\u9001\u6765\u665a\u996d\uff0c\u6781\u4e3a\u4e30\u76db\uff0c\u5415\u56db\u5a18\u72b9\u6709\u7591\u8651\uff0c\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u4ed6\u6478\u4e0d \u900f\u6211\u4eec\u7684\u9053\u8def\uff0c\u5c82\u6562\u6697\u7b97\uff1f\u201d\u5927\u7897\u9152\u5927\u5757\u8089\u7684\u5403\u4e86\uff0c\u5e84\u4e01\u8fdb\u6765\u6536\u62fe\uff0c\u8bf4\u9053\uff1a\u201c\u5e84\u4e3b\u5411\u4e09\u4f4d\u8bf7 \u5b89\uff0c\u8bf7\u6055\u4ed6\u4e0d\u6765\u966a\u5ba2\u4e86\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u5e84\u4e3b\u6709\u4e8b\uff0c\u4e0d\u5fc5\u5ba2\u6c14\u3002\u201d \u3000\u3000\u8fd9\u665a\u4e09\u4eba\u540c\u5ba4\uff0c\u5230\u4e86\u5348\u591c\uff0c\u7518\u51e4\u6c60\u9053\uff1a\u201c\u516b\u59b9\uff0c\u4f60\u51fa\u53bb\u63a2\u5b83\u4e00\u63a2\uff0c\u770b\u8fd9\u4e2a\u5c71\u5e84\uff0c\u6709\u4ec0\u4e48 \u53e4\u602a\uff1f\u201d\u72ec\u81c2\u795e\u5c3c\u95e8\u4e0b\uff0c\u4ee5\u5415\u56db\u5a18\u7684\u8f7b\u529f\u6700\u9ad8\uff0c\u7518\u51e4\u6c60\u81ea\u6127\u4e0d\u5982\uff0c\u6240\u4ee5\u53eb\u5979\u53bb\u63a2\u3002\u5415\u56db\u5a18\u8d70 \u8fd1\u7a97\u53e3\uff0c\u53ea\u89c1\u7a97\u5916\u9ed1\u5f71\u5e62\u5e62\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e03\u54e5\uff0c\u5916\u9762\u6709\u4eba\u76d1\u89c6\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u6709\u529e\u6cd5 \u5f15\u5f00\u4ed6\u4eec\u3002\u201d\u4f38\u51fa\u4e2d\u6307\uff0c\u5728\u7a97\u4e2d\u5bf9\u9762\u7684\u5899\u4e0a\u4e00\u63d2\uff0c\u767b\u65f6\u63d2\u7a7f\u4e86\u4e00\u4e2a\u5c0f\u6d1e\uff0c\u968f\u624b\u5728\u5899\u89d2\u6361\u8d77\u4e00 \u7247\u7af9\u7247\uff0c\u4f38\u5165\u6d1e\u4e2d\uff0c\u6405\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u884c\u4e86\u3002\u201d\u4ece\u767e\u5ba4\u56ca\u4e2d\u53d6\u51fa\u4e00\u9897\u5f39\u5b50\uff0c\u53cc\u6307\u631f\u7740\uff0c\u5bf9 \u7740\u6d1e\u53e3\u4e00\u5f39\uff0c\u53ea\u542c\u5f97\u55e4\u7684\u4e00\u58f0\uff0c\u5f39\u5b50\u98de\u51fa\u5916\u9762\u3002\u968f\u540e\u4fbf\u542c\u5230\u6709\u8f7b\u5fae\u7684\u811a\u6b65\u5954\u8dd1\u58f0\u97f3\uff0c\u5415\u56db\u5a18 \u4f55\u7b49\u806a\u660e\u3002\u77e5\u9053\u8fd9\u662f\u7518\u51e4\u6c60\u58f0\u4e1c\u51fb\u897f\u4e4b\u8ba1\uff0c\u8863\u895f\u4e00\u64a9\uff0c\u7a7f\u7a97\u98de\u51fa\uff01\u5916\u9762\u7684\u770b\u5b88\uff0c\u542c\u5f97\u5f39\u5b50\u58f0 \u54cd\uff0c\u4ee5\u4e3a\u6709\u591c\u884c\u4eba\u6765\uff0c\u201c\u6295\u77f3\u95ee\u8def\u201d\uff0c\u7fa4\u5411\u5f39\u5b50\u843d\u5904\u5954\u53bb\u3002\u5230\u56de\u8fc7\u5934\u65f6\uff0c\u5415\u56db\u5a18\u5df2\u98de\u63a0\u8fc7\u4e24 \u95f4\u5c4b\u9762\uff0c\u8eb2\u5728\u5047\u5c71\u4e4b\u540e\u4e86\u3002 \u3000\u3000\u8fd9\u5ea7\u5c1a\u5e84\u4e3b\u7684\u82b1\u56ed\uff0c\u5360\u5730\u751a\u5e7f\uff0c\u5e03\u7f6e\u4e0d\u4fd7\uff0c\u53ea\u89c1\u56db\u9762\u5047\u5c71\u73b2\u73d1\uff0c\u6e38\u5eca\u66f2\u6298\uff0c\u4e2d\u95f4\u8fd8\u6709\u4e00 \u5ea7\u9ad8\u8038\u7684\u7889\u697c\u3002\u5415\u56db\u5a18\u6697\u6697\u79f0\u5947\uff1a\u8fd9\u5730\u65b9\u597d\u50cf\u6765\u8fc7\u4f3c\u7684\uff1f\u60f3\u4e86\u4e00\u9635\uff0c\u624d\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u8fd9\u91cc \u7684\u56ed\u6797\u5e03\u7f6e\uff0c\u7adf\u662f\u6a21\u4eff\u9c7c\u4eae\u5728\u7530\u6a2a\u5c9b\u5343\u6587\u5ca9\u4e0a\u6240\u5efa\u90a3\u5ea7\u522b\u5885\u7684\u683c\u5c40\uff0c\u867d\u7136\u6ca1\u6709\u9c7c\u4eae\u522b\u5885\u7684\u96c4 \u4f1f\u548c\u9669\u5cfb\uff0c\u4f46\u4e5f\u9881\u4e3a\u53ef\u89c2\u3002\u5415\u56db\u5a18\u8eb2\u5728\u5047\u5c71\u80cc\u540e\uff0c\u56db\u987e\u65e0\u4eba\uff0c\u6b63\u60f3\u8dc3\u51fa\uff0c\u5ffd\u542c\u5f97\u6709\u811a\u6b65\u58f0 \u54cd\uff0c\u53ea\u89c1\u56db\u4e2a\u5973\u90ce\uff0c\u63d0\u7740\u7eb1\u706f\uff0c\u8054\u6289\u8d70\u8fc7\u3002\u5176\u4e2d\u4e00\u4e2a\u5c11\u5973\u9053\uff1a\u201c\u90e1\u4e3b\u813e\u6c14\u597d\u5927\uff0c\u770b\u5979\u90a3\u6837\u767e \u5a9a\u5343\u5a07\uff0c\u8c01\u4e5f\u6599\u4e0d\u5230\u5979\u6b66\u529f\u90a3\u4e48\u9ad8\u660e\u3002\u201d\u53c8\u4e00\u4e2a\u5c11\u5973\u9053\uff1a\u201c\u662f\u5440\uff0c\u6628\u5929\u5979\u4e0d\u80af\u5403\u996d\uff0c\u5b5f\u585e\u4e3b \u53bb\u529d\u5979\uff0c\u4e0d\u77e5\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff0c\u5979\u4e00\u5df4\u638c\u4fbf\u6253\u8fc7\u6765\uff0c\u5b5f\u585e\u4e3b\u6025\u5fd9\u95ea\u5f00\uff0c\u5979\u4e00\u638c\u4fbf\u628a\u6a80\u6728\u684c\u5b50\u6253\u574f \u4e86\u3002\u201d\u53c8\u4e00\u4e2a\u5c11\u5973\u9053\uff1a\u201c\u540e\u6765\u662f\u5e84\u4e3b\u8fdb\u53bb\u8bf4\u597d\u8bf4\u574f\uff0c\u5979\u624d\u80af\u5403\u3002\u201d\u7b2c\u56db\u4e2a\u5c11\u5973\u9053\uff1a\u201c\u542c\u8bf4\u5b5f \u585e\u4e3b\u6b66\u529f\u672c\u5728\u6211\u4eec\u5e84\u4e3b\u4e4b\u4e0a\uff0c\u4e43\u662f\u6c5f\u6e56\u4e0a\u6210\u540d\u7684\u4eba\u7269\uff0c\u4ed6\u5012\u5f88\u5fcd\u5f97\u3002\u201d\u7b2c\u4e00\u4e2a\u5c11\u5973\u9053\uff1a\u201c\u662f \u90e1\u4e3b\u561b\uff01\u4ed6\u600e\u4e48\u4e5f\u5f97\u5fcd\u7740\u3002\u201d\u5415\u56db\u5a18\u4f0f\u5730\u542c\u58f0\uff0c\u56db\u4e2a\u5973\u90ce\u6e10\u8d70\u6e10\u8fdc\uff0c\u58f0\u97f3\u8d8a\u6765\u8d8a\u5f31\uff0c\u5415\u56db\u5a18 \u751a\u4e3a\u5947\u602a\uff1a\u90a3\u91cc\u6765\u7684\u90e1\u4e3b\uff1f\u96be\u9053\u4e00\u4e2a\u5e2e\u4f1a\u9996\u9886\uff0c\u8fd8\u6562\u628a\u738b\u7237\u7684\u5c0f\u59d0\u52ab\u4e86\uff1f\u597d\u5947\u5fc3\u8d77\uff0c\u4e00\u63a0\u800c \u51fa\uff0c\u8f7b\u98d8\u98d8\u7684\u8e51\u5728\u90a3\u56db\u4e2a\u5973\u90ce\u8eab\u540e\uff0c\u5415\u56db\u5a18\u8f7b\u529f\u5df2\u7ecf\u6781\u5cf0\uff0c\u771f\u6709\u767b\u840d\u6e21\u6c34\u4e4b\u80fd\uff0c\u98de\u7d6e\u65e0\u58f0\u4e4b \u5999\uff0c\u8ddf\u5728\u90a3\u56db\u4e2a\u5973\u90ce\u8eab\u540e\uff0c\u5979\u4eec\u7adf\u81ea\u534a\u70b9\u4e0d\u77e5\u3002 \u3000\u3000\u8ddf\u4e86\u4e00\u9635\uff0c\u53c8\u542c\u5f97\u4e00\u4f4d\u5973\u90ce\u8bf4\u9053\uff1a\u201c\u5927\u540e\u5929\u662f\u6211\u4eec\u5e2e\u4e2d\u7684\u67dc\u7956\u5f00\u5e2e\u5927\u5178\uff0c\u4e0d\u77e5\u5927\u738b\u6765\u4e0d \u6765\uff1f\u201d\u53e6\u4e00\u4f4d\u5c11\u5973\u9053\uff1a\u201c\u5979\u5973\u513f\u5728\u8fd9\u91cc\uff0c\u603b\u4f1a\u6765\u5427\uff1f\u201d\u53c8\u4e00\u540d\u5c11\u5973\u9053\uff1a\u201c\u6211\u542c\u5c11\u4e3b\u8bf4\u5927\u738b\u5fd9 \u7740\u5462\uff0c\u672a\u5fc5\u5206\u5f97\u51fa\u8eab\u3002\u201d\u5148\u5934\u90a3\u540d\u5c11\u5973\u9053\uff1a\u201c\u5c11\u4e3b\u5012\u548c\u4f60\u5f88\u8981\u597d\uff0c\u65f6\u65f6\u548c\u4f60\u8bf4\u4f53\u5df1\u8bdd\u513f \u5462\u3002\u201d\u90a3\u540d\u5c11\u5973\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u53c8\u8bf4\u8bf4\u7b11\u7b11\uff0c\u8fc7\u4e00\u9635\uff0c\u5148\u5934\u90a3\u5c11\u5973\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u90e1\u4e3b\u813e\u6c14 \u574f\uff0c\u6211\u5374\u8bf4\u5979\u597d\u3002\u6628\u5929\u6211\u670d\u4f8d\u5979\u68b3\u5934\uff0c\u5979\u6267\u7740\u6211\u7684\u624b\u95ee\u957f\u95ee\u77ed\uff0c\u5c31\u50cf\u6211\u7684\u59d0\u59d0\u4e00\u822c\u3002\u201d\u5148\u5934 \u90a3\u5c11\u5973\u53c8\u9053\uff1a\u201c\u542c\u8bf4\u662f\u5927\u738b\u4e0d\u51c6\u5979\u5ac1\u4eba\uff0c\u6240\u4ee5\u5979\u624d\u9003\u51fa\u6765\u3002\u201d\u5176\u4f59\u4e09\u540d\u5c11\u5973\u5431\u5431\u55b3\u55b3\u7684\u95ee \u9053\uff1a\u201c\u771f\u7684\uff0c\u4f60\u600e\u77e5\u9053\uff1f\u201d\u201c\u5979\u4e0d\u5bb3\u7f9e\u5417\uff1f\u600e\u4e48\u95f9\u7740\u8981\u5ac1\u4eba\uff1f\u201d\u201c\u5ac1\u4e0d\u5230\u5408\u610f\u7684\u4eba\uff0c\u96be\u602a\u813e \u6c14\u574f\u4e86\u3002\u201d\u4e00\u4eba\u4e00\u53e5\uff0c\u542c\u5f97\u5415\u56db\u5a18\u6697\u6697\u597d\u7b11\u3002 \u3000\u3000\u56db\u4e2a\u5973\u90ce\u7ed5\u8fc7\u5047\u5c71\u4ead\u53f0\uff0c\u66f2\u66f2\u6298\u6298\u8d70\u5230\u56ed\u5b50\u897f\u89d2\uff0c\u53ea\u89c1\u4e00\u5ea7\u4e09\u5c42\u697c\u5b87\uff0c\u697c\u89d2\u6302\u7740\u5341\u51e0\u76cf \u5782\u7a57\u516b\u89d2\u98ce\u706f\uff0c\u7b2c\u4e09\u5c42\u697c\u4e0a\u7a97\u95e8\u6253\u5f00\uff0c\u706f\u5149\u7167\u58c1\uff0c\u4f9d\u7a00\u770b\u89c1\u4e00\u4e2a\u5c11\u5973\u7684\u80cc\u5f71\uff0c\u7adf\u4f3c\u719f\u4eba\uff0c\u4f46 \u5374\u60f3\u4e0d\u8d77\u662f\u8c01\u3002\u5415\u56db\u5a18\u6b63\u60f3\u6284\u5c0f\u5f84\u62a2\u8fc7\u56db\u4e2a\u5973\u90ce\u524d\u5934\uff0c\u98de\u8eab\u4e0a\u697c\u3002\u697c\u4e0a\u706f\u5149\u5ffd\u706d\uff0c\u56ed\u5b50\u8f6c\u89d2 \u5904\uff0c\u84e6\u7136\u8d70\u51fa\u4e24\u4e2a\u4eba\u3002\u56db\u4e2a\u5973\u90ce\u5782\u624b\u8083\u7acb\uff0c\u5415\u56db\u5a18\u6025\u5fd9\u95ea\u8fdb\u82b1\u6811\u4e1b\u4e2d\u3002\u659c\u773c\u5077\u7aa5\uff0c\u4e0d\u7531\u5f97\u5403 \u4e86\u4e00\u60ca\uff0c\u539f\u6765\u8fd9\u4e24\u4e2a\u4eba\u4e00\u4e2a\u662f\u5c1a\u5e84\u4e3b\uff0c\u4e00\u4e2a\u7adf\u662f\u5929\u53f6\u6563\u4eba\u7684\u5e08\u5144\u97e9\u91cd\u5c71\u3002\u53ea\u542c\u5f97\u5c1a\u5e84\u4e3b\u9053\uff1a \u201c\u4f60\u4eca\u665a\u5c31\u8981\u8d76\u53bb\u5417\uff0c\u660e\u5929\u518d\u8d70\u6210\u4e0d\u6210\uff1f\u4eca\u5929\u6765\u4e86\u4e09\u4e2a\u5c0f\u5b50\uff0c\u53e3\u6c14\u6781\u5927\uff0c\u6211\u60f3\u8bf7\u4f60\u66ff\u6211\u6478\u6478 \u4ed6\u4eec\u7684\u6d77\u5e95\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u66f4\u662f\u5403\u60ca\uff0c\u5fc3\u60f3\uff0c\u8fd9\u97e9\u91cd\u5c71\u6b66\u529f\u8d85\u5353\uff0c\u6697\u5668\u5c24\u5176\u5389\u5bb3\u3002\u800c\u4e14\u82e5\u53ea\u662f\u4ed6\u4e00\u4eba\u4e5f\u8fd8\u7f62 \u4e86\uff0c\u53ea\u6015\u4e86\u56e0\u90a3\u4e00\u73ed\u4eba\u4e5f\u548c\u4ed6\u540c\u6765\uff0c\u6b63\u601d\u91cf\u95f4\uff0c\u53ea\u542c\u5f97\u97e9\u91cd\u5c71\u9053\uff1a\u201c\u6211\u7684\u8001\u4f34\u7b49\u7740\u6211\u5462\uff0c\u5927 \u540e\u5929\u6211\u4e00\u5b9a\u8d76\u6765\u66ff\u4f60\u795d\u5bff\u4fbf\u662f\u3002\u81f3\u4e8e\u4f60\u8bf4\u7684\u90a3\u4e09\u4e2a\u5c0f\u5b50\uff0c\u7b49\u4e0b\u6211\u53bb\u77a7\u4ed6\u4eec\u4e00\u77a7\uff0c\u5018\u82e5\u771f\u662f\u6210 \u540d\u4eba\u7269\uff0c\u6211\u603b\u4e0d\u81f3\u4e8e\u4e0d\u8ba4\u8bc6\u7684\u3002\u201d\u5c1a\u5e84\u4e3b\u9053\uff1a\u201c\u597d\uff0c\u6211\u5148\u770b\u770b\u90e1\u4e3b\uff0c\u5979\u8fd9\u4e24\u5929\u813e\u6c14\u53ef\u5927 \u5462\uff01\u201d\u97e9\u91cd\u5c71\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u8d31\u4e2b\u5934\uff01\u201d\u548c\u5c1a\u5e84\u4e3b\u4e0a\u697c\uff0c\u5415\u56db\u5a18\u6025\u5fd9\u4e00\u95ea\uff0c\u5c55\u5f00\u7edd\u9876\u8f7b \u529f\uff0c\u5954\u56de\u5ba2\u820d\u3002 \u3000\u3000\u7518\u5510\u4e8c\u4eba\u7b49\u5f97\u6b63\u5728\u5fc3\u7126\uff0c\u7a97\u95e8\u4e00\u52a8\uff0c\u5415\u56db\u5a18\u5982\u98de\u9e1f\u822c\u63a0\u8fdb\uff0c\u5411\u7518\u51e4\u6c60\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u6211\u5b66 \u4e86\u4f60\u90a3\u624b\u529f\u592b\uff0c\u5c06\u5230\u5ba2\u820d\uff0c\u5c31\u968f\u4fbf\u7528\u4e00\u7c92\u5c0f\u77f3\u5b50\uff0c\u5f15\u4ed6\u4eec\u8d70\u5f00\u4e86\u3002\u4ed6\u4eec\u4eca\u665a\u8981\u53d7\u4e24\u573a\u865a \u60ca\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f60\u63a2\u51fa\u4ec0\u4e48\u6ca1\u6709\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u660e\u5929\u518d\u548c\u4f60\u8bf4\uff0c\u7b49\u4e0b\u97e9\u91cd\u5c71\u90a3\u602a\u7269\u8981 \u6765\u770b\u6211\u4eec\uff0c\u6211\u4eec\u88c5\u7761\u3002\u201d\u5ba2\u623f\u5f88\u5927\uff0c\u4e09\u5e8a\u5e76\u5217\uff0c\u7518\u51e4\u6c60\u7761\u5728\u4e2d\u95f4\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u5ffd\u542c\u5f97\u6709\u6572\u95e8 \u4e4b\u58f0\uff0c\u7518\u51e4\u6c60\u6545\u610f\u7b49\u4e86\u4e00\u4f1a\uff0c\u8fd9\u624d\u4f5c\u51fa\u7ed9\u60ca\u9192\u4e4b\u72b6\uff0c\u5f00\u4e86\u623f\u95e8\uff0c\u97e9\u91cd\u5c71\u548c\u5c1a\u5e84\u4e3b\u7ad9\u5728\u95e8\u53e3\uff0c \u5c1a\u5e84\u4e3b\u9053\uff1a\u201c\u521a\u624d\u6709\u591c\u884c\u4eba\u63a2\u5e84\uff0c\u4e09\u4f4d\u53ef\u53d7\u60ca\u4e86\uff1f\u201d\u7518\u51e4\u6c60\u9053\u58f0\u201c\u60ed\u6127\u201d\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7761 \u5f97\u592a\u719f\uff0c\u4e00\u70b9\u4e5f\u542c\u4e0d\u51fa\u6765\u3002\u201d\u5c1a\u5e84\u4e3b\u9053\uff1a\u201c\u6211\u5c31\u6015\u60ca\u4e86\u8d35\u5ba2\uff0c\u4e0d\u597d\u610f\u601d\u3002\u201d\u968f\u7740\u4ecb\u7ecd\u97e9\u91cd\u5c71 \u7ed9\u4e09\u4eba\u76f8\u89c1\uff0c\u97e9\u91cd\u5c71\u53cc\u7738\u70af\u70af\u770b\u4e86\u4e00\u9635\uff0c\u5415\u56db\u5a18\u548c\u5510\u6653\u6f9c\u867d\u7136\u90fd\u4f1a\u8fc7\u97e9\u91cd\u5c71\uff0c\u53ef\u662f\u73b0\u5728\u53d8\u4e86 \u989c\u5bb9\uff0c\u5415\u56db\u5a18\u53c8\u6539\u4e86\u7537\u88c5\u3002\u5728\u6697\u6de1\u706f\u5149\u4e4b\u4e0b\uff0c\u53c8\u9002\u503c\u97e9\u91cd\u5c71\u5fc3\u4e2d\u6709\u4e8b\uff0c\u7adf\u770b\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5bd2\u55a7\u51e0\u53e5\uff0c\u97e9\u91cd\u5c71\u9053\u58f0\u201c\u6253\u6270\u201d\uff0c\u4fbf\u5373\u544a\u8f9e\uff0c\u5c1a\u5e84\u4e3b\u8ddf\u4e86\u51fa\u6765\uff0c\u97e9\u91cd\u5c71\u9053\uff1a\u201c\u8fd9\u4e09\u4e2a\u5c0f \u5b50\u4e0d\u662f\u4ec0\u4e48\u6210\u540d\u4eba\u7269\uff0c\u4f46\u770b\u6837\u5b50\u6b66\u529f\u5374\u8fd8\u4e0d\u9519\uff0c\u5927\u7ea6\u662f\u540d\u5bb6\u5f1f\u5b50\uff0c\u6545\u4f5c\u5927\u8a00\uff0c\u60f3\u626c\u540d\u95ef\u4e07\u6765 \u7684\u3002\u201d\u5c1a\u5e84\u4e3b\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u662f\u5929\u5c71\u5251\u5ba2\u7684\u95e8\u4e0b\uff1f\u201d\u97e9\u91cd\u5c71\u9053\uff1a\u201c\u5929\u5c71\u5251\u4f20\u4eba\u5df1\u7edd\uff0c\u4f60 \u8fd8\u8001\u662f\u6000\u6068\u505a\u4ec0\u4e48\uff1f\u201d\u53c8\u9053\uff1a\u201c\u5e84\u4e3b\uff0c\u8bf7\u6055\u6211\u5931\u8a00\uff0c\u5176\u5b9e\u4ee5\u4f60\u7684\u6b66\u529f\uff0c\u53c8\u6709\u536b\u5c9b\u4e3b\u548c\u5b5f\u5be8\u4e3b \u5728\u6b64\uff0c\u96be\u9053\u8fd8\u6015\u8fd9\u4e09\u4e2a\u5c0f\u5b50\u4f5c\u53cd\uff1f\u201d\u5c1a\u5e84\u4e3b\u89c1\u4ed6\u7126\u6025\u4e4b\u5bb9\uff0c\u73b0\u4e8e\u8f9e\u8272\uff0c\u4e0d\u6562\u518d\u8bf4\u3002\u97e9\u91cd\u5c71\u62f1 \u4e86\u62f1\u624b\uff0c\u8eab\u5f62\u8d77\u5904\uff0c\u4f3c\u4e00\u6e9c\u8f7b\u70df\u822c\u7684\u98de\u51fa\u5c71\u5e84\uff0c\u8fde\u591c\u8d76\u529e\u4ed6\u7684\u4e8b\u60c5\u53bb\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u7b2c\u4e09\u5929\uff0c\u9646\u7eed\u6709\u5e2e\u4f1a\u7684\u9996\u9886\u5230\u6765\uff0c\u5c1a\u5e84\u4e3b\u5fd9\u4e2a\u4e0d\u4e86\uff0c\u4f46\u767e\u5fd9\u4e2d\u65e9\u665a\u4ecd\u7136\u62bd\u7a7a\u6765 \u770b\u7518\u51e4\u6c60\u4ed6\u4eec\u4e09\u4eba\uff0c\u7518\u51e4\u6c60\u8001\u4e8e\u6c5f\u6e56\uff0c\u8a00\u8c08\u4e4b\u95f4\uff0c\u7edd\u4e0d\u53eb\u4ed6\u6478\u5f97\u5e95\u7ec6\u3002\u5230\u4e86\u7b2c\u56db\u65e5\u4e2d\u5348\uff0c\u5c1a \u5e84\u4e3b\u5ffd\u7136\u4eb2\u81ea\u6765\u8bf7\uff0c\u4e09\u4eba\u968f\u4ed6\u8d70\u5230\u56ed\u4e2d\uff0c\u56ed\u4e2d\u6446\u6709\u51e0\u684c\u9152\u5e2d\u3002\u5415\u56db\u5a18\u5ffd\u7136\u60f3\u8d77\u9c7c\u4eae\u5927\u738b\u4e4b \u5bb4\uff0c\u60c5\u666f\u548c\u4eca\u65e5\u9887\u4e3a\u76f8\u4f3c\u3002\u5c1a\u5e84\u4e3b\u8bf7\u4ed6\u4eec\u5750\u4e0a\u5e2d\uff0c\u5415\u56db\u5a18\u548c\u5510\u6653\u6f9c\u4e00\u770b\uff0c\u5728\u9996\u5e2d\u5176\u4ed6\u4e03\u4e2a\u5ba2 \u4eba\u4e4b\u4e2d\uff0c\u7adf\u7136\u8ba4\u5f97\u4e8c\u4eba\uff0c\u4e00\u4e2a\u662f\u51cc\u4e91\u5c9b\u4e3b\u536b\u626c\u5a01\uff0c\u4e00\u4e2a\u662f\u592a\u6e56\u585e\u4e3b\u5b5f\u6b66\u529f\uff0c\u4e0d\u7981\u6697\u6697\u79f0\u5f02\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5c71\u5e84\u56da\u7389\u5973\uff0c\u5e2d\u4e0a\u89c1\u7fa4\u9b54\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u516d\u56de\u3000\u8f83\u6280\u7d22\u9556\u3000\u5f53\u573a\u663e\u8eab\u624b\u3000\u89e3\u7eb7\u5f98\u96be\u3000\u4e00\u8bed\u91ca\u524d\u5acc \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u5341\u516d\u56de \u8f83\u6280\u7d22\u9556\u3000\u5f53\u573a\u663e\u8eab\u624b \u89e3\u7eb7\u6392\u96be\u3000\u4e00\u8bed\u91ca\u524d\u5acc \u3000\u3000\u7518\u51e4\u6c60\u7b49\u4e09\u4eba\u5750\u5728\u4e0a\u5ea7\uff0c\u53ea\u89c1\u4e0d\u65ad\u6709\u4eba\u6765\u5411\u5e84\u4e3b\u9053\u8d3a\uff0c\u7518\u51e4\u6c60\u542c\u5f97\u8fd9\u4e9b\u5ba2\u4eba\u662f\u6765\u795d\u8d3a\u5f00 \u5e2e\u5927\u5178\u7684\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u600e\u4e48\u4e00\u4e2a\u65b0\u5efa\u7684\u5e2e\u4f1a\uff0c\u6709\u8fd9\u6837\u6c14\u6d3e\uff01\u201d\u6b63\u81ea\u7eb3\u95f7\uff0c\u5c1a\u5f80\u4e3b\u5ffd\u7136\u7aef\u8d77 \u9152\u676f\uff0c\u5de6\u624b\u5728\u6000\u4e2d\u53d6\u51fa\u4e00\u628a\u6247\u5b50\uff0c\u8fce\u98ce\u4e00\u6643\uff0c\u500f\u7684\u5f20\u5f00\uff0c\u6247\u5b50\u5916\u9762\uff0c\u4e4c\u6f06\u5149\u4eae\uff0c\u6247\u9aa8\u95ea\u95ea\u53d1 \u5149\uff0c\u7518\u51e4\u6c60\u4e00\u89c1\uff0c\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u662f\u6c5f\u5357\u7684\u94c1\u6247\u5e2e\u6b7b\u7070\u590d\u71c3\uff01\u201d \u3000\u3000\u4e94\u5341\u5e74\u524d\uff0c\u94c1\u6247\u5e2e\u662f\u6c5f\u5357\u7684\u4e00\u5927\u5e2e\u4f1a\uff0c\u5e2e\u4e3b\u5c1a\u4e91\u4ead\u6b66\u529f\u6781\u9ad8\uff0c\u8f6f\u7684\u786c\u7684\u5168\u90fd\u4e0d\u5403\uff0c\u9ed1\u9053 \u767d\u9053\uff0c\u5747\u4e0d\u5356\u5e10\uff0c\u867d\u7136\u6043\u5f3a\u79f0\u9738\uff01\u5374\u4e5f\u7b97\u5f97\u4e2a\u54cd\u5f53\u5f53\u7684\u89d2\u8272\u3002\u4e0d\u6599\u540e\u6765\u5c1a\u4e91\u4ead\u7ed9\u4eba\u5996\u90dd\u98de\u51e4 \u76c5\u60d1\uff0c\u7adf\u7136\u4e0e\u5929\u5c71\u4e03\u5251\u4f5c\u5bf9\uff0c\u88ab\u51cc\u672a\u98ce\u6240\u64d2\uff0c\u7ec8\u4e8e\u4e27\u547d\u3002\uff08\u4e8b\u8be6\u62d9\u8457\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\uff09\u5c1a\u4e91 \u4ead\u6b7b\u540e\uff0c\u94c1\u6247\u5e2e\u7fa4\u9f99\u5927\u9996\uff0c\u5ba3\u544a\u74e6\u89e3\uff0c\u5c1a\u4e91\u4eab\u6709\u4e2a\u5b59\u5b50\uff0c\u5f53\u5c1a\u4e91\u4eab\u6b7b\u65f6\uff0c\u672a\u6ee1\u5341\u5c81\uff0c\u957f\u5927\u4e4b \u540e\uff0c\u9887\u601d\u6062\u590d\u7956\u4e1a\uff0c\u904d\u8bbf\u540d\u5e08\uff0c\u5b66\u6210\u6b66\u827a\uff0c\u81ea\u5df1\u53d6\u540d\u4e3a\u5c1a\u590d\u521d\u3002\u5954\u8d70\u4e86\u5341\u591a\u5e74\uff0c\u5f97\u9c7c\u4eae\u4e4b \u52a9\uff0c\u624d\u628a\u94c1\u6247\u5e2e\u91cd\u7ec4\u8d77\u6765\uff0c\u4eca\u65e5\u4fbf\u662f\u4ed6\u7956\u7956\u5f00\u5e2e\u7684\u5927\u5178\u3002\u540d\u662f\u5f00\u5e2e\uff0c\u5b9e\u662f\u91cd\u5efa\u3002\u7518\u51e4\u6c60\u9002\u9022 \u5176\u4f1a\uff0c\u81ea\u7136\u4e5f\u53ea\u597d\u968f\u4f17\u4e3e\u676f\uff0c\u5411\u5c1a\u590d\u521d\u9053\u8d3a\u3002 \u3000\u3000\u5c1a\u590d\u521d\u6447\u7740\u94c1\u6247\uff0c\u5f00\u58f0\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u655d\u5e2e\u91cd\u5efa\uff0c\u5f00\u5e2e\u796d\u7956\uff0c\u627f\u5404\u4f4d\u9053\u4e0a\u540c\u50da\uff0c\u4e0d\u5f03\u4e0b \u611a\uff0c\u5149\u4e34\u5bd2\u820d\u3002\u66f4\u627f\u9c7c\u4eae\u5927\u738b\uff0c\u51fa\u529b\u76f8\u52a9\uff0c\u655d\u5e2e\u4e0a\u4e0b\uff0c\u65e0\u4e0d\u611f\u6fc0\u3002\u201d\u5415\u56db\u5a18\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4ed6 \u662f\u9c7c\u4eae\u6276\u8d77\u6765\u7684\uff0c\u602a\u4e0d\u5f97\u5b5f\u6b66\u529f\u548c\u536b\u626c\u5a01\u8fdc\u9053\u800c\u6765\uff0c\u8fd9\u4e8c\u4eba\u5b9a\u662f\u4ee3\u8868\u9c7c\u4eae\u6765\u53c2\u52a0\u5178\u793c\u7684\u4e86\u3002 \u5c1a\u590d\u521d\u987f\u4e86\u4e00\u987f\uff0c\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\uff0c\u53c8\u9053\uff1a\u201c\u60f3\u6211\u6c5f\u5357\u5404\u5e2e\u5404\u8235\uff0c\u4e00\u5411\u6563\u5982\u6ce5\u6c99\uff0c\u4e94\u5341\u5e74\u524d\u7684 \u76db\u51b5\uff0c\u5df2\u4f3c\u70df\u6d88\u4e91\u6563\uff0c\u5c82\u4e0d\u6127\u5bf9\u524d\u4eba\u3002\u800c\u4eca\u9c7c\u4eae\u5927\u738b\u6d77\u5916\u79f0\u738b\uff0c\u4e3a\u6211\u8f88\u626c\u7709\u5410\u6c14\uff0c\u6211\u4eec\u6c5f\u5357 \u5404\u5e2e\uff0c\u4f55\u4e0d\u6b47\u8840\u7ed3\u76df\uff0c\u52a9\u9c7c\u4eae\u5927\u738b\u4e00\u81c2\u4e4b\u529b\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u5404\u5e2e\u9996\u9886\uff0c\u8bae\u8bba\u7eb7\u7eb7\uff0c\u867d\u7136\u4ed6\u4eec \u8d74\u5bb4\u4e4b\u524d\uff0c\u5df2\u77e5\u5c1a\u590d\u521d\u4e4b\u610f\uff0c\u4f46\u5179\u4e8b\u4f53\u5927\uff0c\u800c\u4eca\u6b63\u5f0f\u63d0\u51fa\uff0c\u8c01\u4e5f\u4e0d\u6562\u9996\u5148\u53d1\u8a00\uff0c\u7518\u51e4\u6c60\u5415\u56db \u5a18\u51b7\u773c\u65c1\u89c2\uff0c\u77e5\u5404\u5e2e\u9996\u9886\uff0c\u610f\u89c1\u7eb7\u6b67\uff0c\u6b63\u60f3\u6709\u6240\u4f5c\u4e3a\uff0c\u5c1a\u590d\u521d\u5df2\u9053\uff1a\u201c\u6b64\u4e8b\u5173\u7cfb\u6211\u4eec\u5404\u5e2e\u5174 \u8870\uff0c\u636e\u6211\u770b\u6765\u662f\u6709\u767e\u5229\u800c\u65e0\u4e00\u5bb3\u3002\u201d\u6d77\u9633\u5e2e\u5e2e\u4e3b\u5c60\u666f\u662d\u63a5\u7740\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u5c1a\u5e84\u4e3b\u6240\u8a00\u751a\u662f\u3002 \u6b64\u4e8b\u5173\u7cfb\u751a\u5927\uff0c\u5f85\u6211\u4eec\u4ece\u957f\u8ba1\u8bae\u5982\u4f55\uff1f\u201d\u8fd9\u4e00\u756a\u8bdd\uff0c\u660e\u662f\u6367\u626c\uff0c\u5b9e\u662f\u62d6\u5ef6\u3002\u5c1a\u590d\u521d\u81ea\u77e5\u5a01\u671b \u672a\u591f\uff0c\u5404\u5e2e\u5e2e\u4e3b\u4e4b\u6765\uff0c\u4e00\u534a\u662f\u770b\u9c7c\u4eae\u9762\u5b50\uff0c\u4e00\u534a\u8fd8\u662f\u9760\u81ea\u5df1\u7956\u5b97\u7684\u9057\u836b\uff1b\u89c1\u6b64\u60c5\u5f62\uff0c\u4e0d\u6562\u52c9 \u5f3a\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u5c60\u5e2e\u4e3b\u6df1\u8c0b\u8fdc\u8651\uff0c\u8001\u6210\u6301\u91cd\uff0c\u5c0f\u5f1f\u5c82\u6562\u4e0d\u5c0a\u3002\u6b64\u4e8b\u660e\u65e5\u518d\u8bf4\u3002\u201d\u5b5f\u6b66\u529f\u9887 \u611f\u5931\u671b\uff0c\u6b63\u60f3\u8d77\u7acb\u53d1\u8a00\uff0c\u5ffd\u7136\u6709\u4e24\u4e2a\u5e2e\u53cb\uff0c\u4ff8\u4e0a\u63cf\u91d1\u76d2\u5b50\uff0c\u5c1a\u590d\u521d\u6253\u5f00\u53e3\u770b\uff0c\u53ea\u89c1\u91cc\u9762\u6709\u4e00 \u952d\u9ec4\u91d1\uff0c\u4e00\u5f20\u5927\u7ea2\u62dc\u5e16\u3002 \u3000\u3000\u5c1a\u590d\u521d\u62ff\u8d77\u5e16\u5b50\u4e00\u770b\uff0c\u9762\u8272\u5fae\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u4ed6\u4eec\u8fdb\u6765\uff01\u201d\u8fc7\u4e86\u4e00\u9635\uff0c\u5916\u9762\u8fdb\u6765\u4e86\u4e24\u8001 \u4e00\u5c11\u3002\u5510\u6653\u6f9c\u89c1\u4e86\uff0c\u51e0\u4e4e\u53eb\u51fa\u58f0\u6765\u3002\u539f\u6765\u8fd9\u4e24\u8001\u4e4b\u4e2d\uff0c\u6709\u4e00\u4e2a\u7adf\u7136\u662f\u63d2\u7ffc\u795e\u72ee\u90b9\u9e23\u768b\uff0c\u90a3\u5c11 \u7684\u4e43\u662f\u4ed6\u7684\u513f\u5b50\u90b9\u9521\u4e5d\uff0c\u60f3\u5f53\u5e74\u8fd9\u4e24\u7236\u5b50\u5230\u6768\u5bb6\u63d0\u4eb2\uff0c\u6768\u67f3\u9752\u6545\u610f\u8981\u548c\u90b9\u9521\u4e5d\u8f83\u6280\uff0c\u628a\u4ed6\u7684 \u624b\u81c2\u90fd\u51e0\u4e4e\u6298\u65ad\u3002\u5510\u6653\u6f9c\u79bb\u5f00\u6768\u5bb6\uff0c\u4e0e\u6b64\u4e8b\u4e5f\u591a\u5c11\u6709\u5173\uff0c\u4e00\u522b\u51e0\u5e74\uff0c\u60f3\u4e0d\u5230\u4ed6\u4eec\u7adf\u5728\u8fd9\u91cc\u51fa \u73b0\u3002\u5415\u56db\u5a18\u89c1\u4ed6\u770b\u5f97\u51fa\u795e\uff0c\u6084\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u4ed6\u4eec\uff1f\u201d\u5510\u6653\u6f9c\u70b9\u5934\uff0c\u4e00\u65f6\u95f4\uff0c\u524d\u5c18\u5f80\u4e8b\uff0c \u90fd\u4e0a\u5fc3\u5934\u3002\u5fc3\u60f3\uff1a\u8fd9\u4e2a\u90b9\u9521\u4e5d\u4e0d\u77e5\u73b0\u5728\u8fd8\u6068\u4e0d\u6068\u6211\u3002 \u3000\u3000\u90b9\u9e23\u768b\u5927\u6b65\u8d70\u6765\uff0c\u5411\u5c1a\u590d\u521d\u62f1\u4e86\u62f1\u624b\uff0c\u53cc\u76ee\u73af\u626b\u5168\u573a\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4eca\u65e5\u5c1a\u5e84\u4e3b\u904d \u8bf7\u6b66\u6797\u9ad8\u8d24\uff0c\u563f\uff0c\u563f\uff0c\u5c0f\u8001\u5934\u4e5f\u7b97\u6765\u5f97\u771f\u5de7\uff01\u201d\u5c1a\u590d\u521d\u4e5f\u62f1\u62f1\u624b\u9053\uff1a\u201c\u63d2\u7ffc\u795e\u72ee\uff0c\u4e45\u4ef0\uff0c\u4e45 \u4ef0\uff01\u4eca\u65e5\u4e0e\u51cc\u9556\u5934\u540c\u6765\uff0c\u6709\u4f55\u89c1\u6559\uff1f\u201d\u90b9\u9e23\u768b\u62b1\u62f3\u5411\u5404\u5e2e\u9996\u9886\u4f5c\u4e86\u4e2a\u5706\u63d6\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u5728 \u4e0b\u540d\u53eb\u90b9\u9e23\u768b\uff0c\u8fd9\u4f4d\u670b\u53cb\u662f\u5357\u4eac\u901a\u6e90\u9556\u884c\u7684\u603b\u7f25\u5934\u51cc\u5cb3\u3002\u8fd8\u6709\u8fd9\u4f4d\u4e43\u662f\u5c0f\u513f\u90b9\u9521\u4e5d\u3002\u4e24\u4e2a\u6708 \u524d\uff0c\u5c0f\u513f\u52a9\u51cc\u9556\u5934\u8d70\u4e00\u8d9f\u9556\u5230\u5e7f\u4e1c\uff0c\u4e2d\u9014\u7ed9\u94c1\u6247\u5e2e\u7684\u670b\u53cb\u52ab\u4e86\uff0c\u9556\u94f6\u867d\u7136\u4e0d\u591a\uff0c\u53ea\u6709\u4e94\u4e07\u4f59 \u4e24\uff0c\u5728\u5c1a\u5e84\u4e3b\u773c\u4e2d\u4e0d\u5f53\u4e00\u56de\u4e8b\uff0c\u53ef\u662f\u51cc\u9556\u5934\u4e0e\u8001\u673d\u90fd\u662f\u5149\u68cd\u4e00\u6761\uff0c\u8981\u8d54\u4e5f\u8d54\u4e0d\u8d77\uff0c\u6ca1\u5948\u4f55\uff0c \u53ea\u597d\u539a\u7740\u8138\u76ae\uff0c\u8bf7\u5c1a\u5e84\u4e3b\u8d4f\u4e2a\u9762\u5b50\uff0c\u5c06\u8fd9\u70b9\u9556\u94f6\u53d1\u8fd8\u3002\u6211\u90b9\u9e23\u768b\u8fd9\u53a2\u6709\u793c\u4e86\uff01\u201d \u3000\u3000\u6c5f\u6e56\u4e0a\u52ab\u9556\u8ba8\u9556\u90fd\u662f\u6781\u5bfb\u5e38\u7684\u4e8b\u60c5\u3002\u4f55\u51b5\u90b9\u9e23\u768b\u548c\u6768\u4ef2\u82f1\u5e76\u79f0\u5317\u56fd\u53cc\u96c4\uff0c\u5357\u65b9\u7684\u7eff\u6797\u9053 \u4e5f\u66fe\u4e45\u95fb\u5176\u540d\uff0c\u5e76\u975e\u6ca1\u6709\u6765\u5934\u7684\u53ef\u6bd4\u3002\u5404\u5e2e\u9996\u9886\u90fd\u60f3\uff1a\u8fd9\u4e2a\u9762\u5b50\u5c1a\u590d\u521d\u4e00\u5b9a\u7ed9\u4e86\u3002\u4e94\u4e07\u4e24\u94f6 \u5b50\u53c8\u4e0d\u591a\uff0c\u5c1a\u590d\u521d\u65ad\u4e0d\u4f1a\u548c\u6210\u540d\u4eba\u7269\u7ed3\u8fd9\u79cd\u6881\u5b50\u3002\u90a3\u6599\u5c1a\u590d\u521d\u542c\u4e86\u4e4b\u540e\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a \u201c\u6309\u7406\u8bf4\u561b\uff0c\u90b9\u8001\u82f1\u96c4\u4eb2\u81ea\u6765\u8ba8\u9556\u94f6\uff0c\u8fd9\u5df2\u662f\u7ed9\u5c0f\u53ef\u5929\u5927\u7684\u9762\u5b50\u3002\u8fd9\u9556\u94f6\u65e0\u8bba\u5982\u4f55\u90fd\u8981\u5f52\u8fd8 \u7684\u4e86\uff0c\u53ea\u662f\u4eca\u65e5\u4e43\u655d\u5e2e\u628a\u7956\u5f00\u5e2e\u4e4b\u65e5\uff0c\u767d\u82b1\u82b1\u7684\u94f6\u5b50\u62ff\u51fa\u95e8\u53bb\uff0c\u603b\u662f\u6709\u70b9\u5fcc\u8bb3\u3002\u4e0d\u5982\u8fd9\u6837 \u5427\uff0c\u8fd9\u4e94\u4e07\u4e24\u94f6\u5b50\u5c31\u5f53\u4f5c\u5f69\u94f6\uff0c\u6211\u4eec\u4e0e\u90b9\u8001\u82f1\u96c4\u7236\u5b50\u4e0e\u51cc\u603b\u7f25\u5934\u6bd4\u8bd5\u4e09\u573a\uff0c\u7ed9\u5404\u4f4d\u9ad8\u670b\u52a9\u5174 \u52a9\u5174\uff0c\u90b9\u8001\u82f1\u96c4\u5b9a\u5360\u8d62\u9762\uff0c\u6211\u4eec\u8f93\u4e86\u4e5f\u8f93\u5f97\u9ad8\u5174\uff01\u201d \u3000\u3000\u5c1a\u590d\u521d\u8bf4\u51fa\u8fd9\u4e00\u756a\u8bdd\uff0c\u7fa4\u96c4\u90fd\u662f\u9887\u611f\u610f\u5916\uff0c\u7518\u51e4\u6c60\u4e5f\u89c9\u6b64\u4eba\u6ca1\u6709\u5e2e\u4e3b\u98ce\u5ea6\u3002\u4ed6\u5374\u4e0d\u77e5\u5c1a \u590d\u521d\u53e6\u6709\u60f3\u6cd5\u3002\u5c1a\u590d\u521d\u5341\u591a\u5e74\u6765\u4e3a\u91cd\u5efa\u94c1\u6247\u5e2e\u800c\u5954\u8d70\uff0c\u867d\u7136\u95ef\u51fa\u4e07\u513f\uff0c\u4f46\u672c\u8eab\u7684\u5a01\u671b\u5230\u5e95\u8fd8 \u5acc\u4e0d\u591f\uff0c\u5c24\u5176\u4ee5\u9002\u624d\u7684\u5efa\u8bae\u6ca1\u4eba\u9644\u548c\uff0c\u4ee4\u4ed6\u66f4\u611f\u5c34\u5c2c\u3002\u90b9\u9e23\u768b\u9002\u5f53\u8fd9\u4e2a\u65f6\u5019\u6765\u8ba8\u9556\u94f6\uff0c\u4ed6\u4e00 \u809a\u76ae\u5b50\u6c14\u6ca1\u5904\u53d1\u6cc4\uff0c\u56e0\u6b64\u60f3\u8d81\u6b64\u673a\u4f1a\uff0c\u632b\u6298\u6210\u540d\u4eba\u7269\uff0c\u589e\u52a0\u81ea\u5df1\u5a01\u98ce\u3002 \u3000\u3000\u90b9\u9e23\u768b\u6012\u706b\u4e2d\u70e7\uff0c\u4ef0\u5929\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u637b\u987b\u8bf4\u9053\uff1a\u201c\u8001\u673d\u8fd9\u628a\u9aa8\u5934\uff0c\u5c82\u6562\u8f83\u6280\u8ba8\u9556\uff0c\u4f46\u5e2e \u4e3b\u65e2\u4e0d\u8d4f\u9762\u5b50\uff0c\u6211\u4eec\u4e5f\u4e0d\u5fc5\u52c9\u5f3a\u4e8e\u4ed6\uff0c\u65e2\u7136\u5e2e\u4e3b\u5212\u51fa\u9053\u513f\uff0c\u6211\u4eec\u8981\u4e0d\u63a5\u4e48\uff0c\u8fd9\u7b14\u9556\u94f6\u53c8\u8d4c\u4e0d \u8d77\u3002\u6ca1\u5948\u4f55\uff0c\u6211\u4eec\u53ea\u597d\u542c\u5e2e\u4e3b\u5429\u5490\u4e86\u3002\u201d\u5c1a\u590d\u521d\u9053\uff1a\u201c\u597d\uff0c\u4e09\u573a\u4e2d\u8c01\u8d62\u4e24\u573a\uff0c\u8fd9\u4e94\u4e07\u4e24\u5f69\u94f6 \u4fbf\u5f52\u4ed6\u6240\u6709\u3002\u4f60\u4eec\u662f\u5ba2\uff0c\u8bf7\uff01\u201d\u90b9\u9e23\u768b\u6b63\u60f3\u4eb2\u81ea\u51fa\u9a6c\uff0c\u6311\u6218\u5c1a\u590d\u521d\uff0c\u9556\u5934\u51cc\u5cb3\u5df2\u4e00\u8dc3\u800c\u51fa\uff0c \u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u9556\u4e43\u662f\u5c0f\u5f1f\u6240\u4fdd\uff0c\u6211\u5c31\u8bf7\u5e2e\u4e3b\u8d50\u6559\u4e24\u62db\u5427\u3002\u201d\u5c1a\u590d\u521d\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u610f\u4f3c\u4e0d \u5c51\uff0c\u628a\u624b\u4e00\u6325\uff0c\u4e00\u4e2a\u9ed1\u9762\u6c49\u5b50\u8df3\u4e86\u51fa\u6765\uff0c\u8fd9\u4eba\u4e43\u662f\u94c1\u6247\u5e2e\u4e09\u4e2a\u526f\u5e2e\u4e3b\u4e4b\u4e00\uff0c\u540d\u53eb\u66f9\u5143\u6717\uff0c\u9ad8 \u58f0\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u60ca\u52a8\u5e2e\u4e3b\uff0c\u6211\u6765\u63a5\u51cc\u9556\u5934\u7684\u9ad8\u62db\u5427\u3002\u201d \u3000\u3000\u51cc\u5cb3\u95ee\u4e86\u59d3\u540d\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u6b64\u6765\uff0c\u4e3a\u7684\u53ea\u662f\u8ba8\u9556\uff0c\u53ef\u4e0d\u6562\u5c06\u8d35\u5e2e\u89c6\u540c\u4ec7\u654c\u3002\u5f7c\u6b64 \u70b9\u5230\u4e3a\u6b62\uff0c\u8bf7\u526f\u5e2e\u4e3b\u624b\u4e0b\u7559\u60c5\u3002\u201d\u8f83\u6280\u8ba8\u9556\uff0c\u662f\u6c5f\u6e56\u4e0a\u5e38\u6709\u4e4b\u4e8b\uff0c\u51cc\u5cb3\u6b63\u662f\u6309\u6c5f\u6e56\u89c4\u77e9\uff0c\u628a \u8bdd\u5148\u653e\u4e0b\u6765\u3002\u66f9\u5143\u6717\u5374\u662f\u4e00\u4e2a\u7c97\u8c6a\u6c49\u5b50\uff0c\u624b\u5e95\u867d\u7136\u5f88\u786c\uff0c\u5bf9\u6c5f\u6e56\u793c\u8282\u53ef\u4e0d\u5927\u61c2\uff0c\u5fc3\u91cc\u6697\u7b11\u51cc \u5cb3\u672a\u5a49\u5148\u6043\uff0c\u5f53\u4e0b\u8d70\u884c\u95e8\uff0c\u8fc8\u8fc7\u6b65\uff0c\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff01\u201d\u7acb\u5373\u5f00\u62db\uff0c\u53cc\u81c2\u4e00\u4f38\uff0c\u9aa8\u8282\u683c\u683c \u4f5c\u54cd\uff0c\u9aa4\u7136\u8fdb\u6b65\u6b3a\u8eab\uff0c\u6597\u5927\u7684\u62f3\u5934\u5288\u9762\u6253\u53bb\uff0c\u51cc\u5cb3\u662f\u592a\u6781\u6d3e\u7684\u597d\u624b\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u4e00\u62db\u201c\u659c\u6302 \u5355\u97ad\u201d\u786c\u524a\u654c\u4eba\u624b\u8155\uff0c\u66f9\u5143\u6717\u8eab\u624b\u4e5f\u9887\u8fc5\u75be\uff0c\u4e00\u4e2a\u7ffb\u8eab\uff0c\u547c\u7684\u4e00\u62f3\u53c8\u5411\u51cc\u5cb3\u808b\u4e0b\u649e\u53bb\uff0c\u51cc\u5cb3 \u4f7f\u4e86\u4e00\u62db\u201c\u91ce\u9a6c\u5206\u9b03\u201d\uff0c\u5de6\u817f\u5b9e\uff0c\u53f3\u817f\u865a\uff0c\u5de6\u624b\u4e0a\u63d0\uff0c\u53f3\u624b\u4e0b\u6c89\uff0c\u5e76\u4e0d\u89c1\u4ed6\u600e\u6837\u4f7f\u529b\uff0c\u66f9\u5143 \u6717\u5df2\u7ed9\u4ed6\u903c\u9000\u51e0\u6b65\u3002\u5c1a\u590d\u521d\u76b1\u8d77\u7709\u5934\uff0c\u5bf9\u536b\u626c\u5a01\u9053\uff1a\u201c\u5bf9\u592a\u6781\u62f3\u4f55\u5fc5\u86ee\u653b\u3002\u201d\u8fd9\u8bdd\u672c\u60f3\u8bf4\u7ed9 \u66f9\u5143\u6717\u542c\uff0c\u4f46\u66f9\u5143\u6717\u8f93\u4e86\u4e00\u62db\uff0c\u6b63\u81ea\u66b4\u8e81\uff0c\u90a3\u91cc\u7559\u5fc3\u5e2e\u4e3b\u8bf4\u8bdd\u3002\u4ed6\u7ec3\u7684\u662f\u9ed1\u864e\u62f3\uff0c\u4ed7\u7740\u4e00\u8eab \u86ee\u529b\uff0c\u5fc3\u60f3\u6211\u4fbf\u6371\u4f60\u4e24\u62f3\u4e5f\u65e0\u9632\u788d\uff0c\u865a\u6643\u4e00\u62db\uff0c\u500f\u53c8\u51b2\u4e0a\uff0c\u4e00\u62db\u201c\u6076\u864e\u638f\u5fc3\u201d\uff0c\u53f3\u62f3\u95ea\u7535\u822c \u7684\u5411\u51cc\u5cb3\u80f8\u53e3\u6253\u53bb\uff0c\u51cc\u5cb3\u5fae\u5fae\u4e00\u95ea\uff0c\u7a81\u7136\u626d\u8eab\u53cd\u8155\uff0c\u628a\u654c\u4eba\u53f3\u8098\u52fe\u4f4f\uff0c\u592a\u6781\u62f3\u501f\u529b\u4f7f\u529b\uff0c\u66f9 \u5143\u6717\u7528\u8db3\u529b\u91cf\uff0c\u7ed9\u4ed6\u4e00\u5e26\uff0c\u4e00\u4e2a\u8e09\u8dc4\uff0c\u8eab\u5b50\u5411\u524d\u6251\u53bb\uff0c\u51cc\u5cb3\u53f3\u624b\u4e00\u9001\uff0c\u5de6\u638c\u4e00\u63a8\uff0c\u66f9\u5143\u6717\u4e00 \u4ea4\u4ec6\u4e0b\uff0c\u6323\u624e\u4e86\u597d\u4e00\u4f1a\uff0c\u624d\u7ad9\u5f97\u8d77\u6765\u3002\u8fd9\u8fd8\u662f\u51cc\u5cb3\u624b\u4e0b\u7559\u60c5\uff0c\u53ea\u7528\u4e86\u4e09\u6210\u529b\u91cf\uff0c\u8981\u4e0d\u7136\u4ed6\u7684 \u624b\u8155\u4e5f\u8981\u88ab\u62d7\u65ad\u4e86\u3002 \u3000\u3000\u90b9\u9e23\u768b\u6b22\u5bb9\u6ee1\u9762\uff0c\u62f1\u62f1\u624b\u9053\uff1a\u201c\u627f\u8ba9\u4e86\u7b2c\u4e00\u573a\uff0c\u201d\u5c1a\u590d\u521d\u628a\u9152\u676f\u4e00\u987f\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u63a5 \u4f60\u7b2c\u4e8c\u573a\uff01\u201d \u3000\u3000\u90b9\u9e23\u768b\u7acb\u5373\u4e0b\u573a\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5e2e\u4e3b\u80af\u4eb2\u81ea\u6307\u6559\uff0c\u90a3\u597d\u6781\u4e86\u3002\u201d\u5c1a\u590d\u521d\u628a\u5916\u8863\u8131\u4e0b\uff0c\u6b63 \u60f3\u51fa\u573a\uff0c\u80cc\u540e\u4e00\u4e2a\u5c11\u5e74\u95ea\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\u8ba9\u5b69\u513f\u66ff\u4f60\u6bd4\u8fd9\u573a\u5427\uff01\u201d\u8fd9\u5c11\u5e74\u6b63\u662f\u5c1a\u590d\u521d \u7684\u513f\u5b50\uff0c\u540d\u53eb\u5c1a\u5c11\u4ead\uff0c\u5c1a\u590d\u521d\u5b66\u8fc7\u597d\u51e0\u95e8\u6b66\u529f\uff0c\u4ed6\u513f\u5b50\u4e5f\u662f\u4ece\u5c0f\u4fbf\u5185\u5916\u53cc\u4fee\uff0c\u6839\u57fa\u624e\u5f97\u7a33\uff0c \u5c1a\u590d\u521d\u6c89\u541f\u534a\u664c\uff0c\u5fc3\u60f3\u513f\u5b50\u867d\u7136\u706b\u5019\u8f83\u5dee\uff0c\u4f46\u6b66\u529f\u7cbe\u719f\uff0c\u800c\u4e14\u5e74\u9752\u529b\u58ee\uff0c\u5148\u81ea\u5360\u4e86\u4fbf\u5b9c\uff0c\u8ba9 \u4ed6\u53bb\u548c\u90b9\u9e23\u768b\u6bd4\u8bd5\uff0c\u8d25\u4e0d\u4e3a\u8fb1\uff0c\u80dc\u5229\u7acb\u5373\u53ef\u4ee5\u9707\u52a8\u6c5f\u6e56\uff0c\u5f53\u4e0b\u70b9\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5c0f\u5fc3\u4e00 \u70b9\uff01\u201d \u3000\u3000\u5c1a\u5c11\u4ead\u8131\u8863\u4e0b\u573a\uff0c\u90b9\u9e23\u768b\u5fae\u5fae\u4e00\u7b11\uff0c\u9000\u8fc7\u4e00\u65c1\uff0c\u5c1a\u5c11\u4ead\u6115\u7136\u6ce8\u89c6\uff0c\u90b9\u9521\u4e5d\u65e9\u8df3\u4e86\u51fa\u6765\uff0c \u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u63a5\u5c11\u5e2e\u4e3b\u8fd9\u573a\u3002\u201d\u5c1a\u5c11\u4ead\u8fd9\u624d\u604d\u7136\uff0c\u539f\u6765\u662f\u90b9\u9e23\u768b\u4e0d\u5c51\u548c\u81ea\u5df1\u52a8\u624b\u3002 \u3000\u3000\u90b9\u9521\u4e5d\u53d7\u4e86\u6768\u67f3\u9752\u90a3\u6b21\u6559\u8bad\u4e4b\u540e\uff0c\u9a84\u77dc\u4e4b\u6c14\u5df2\u51cf\uff0c\u53cc\u62f3\u4e00\u62b1\u5728\u4e0b\u9996\u7acb\u4e86\u4e2a\u95e8\u6237\uff0c\u51dd\u795e\u5f85 \u654c\u3002\u5c1a\u5c11\u4ead\u9053\uff1a\u201c\u90b9\u82f1\u96c4\u8bf7\u5f00\u62db\u5427\uff01\u201d\u90b9\u9521\u4e5d\u9053\uff1a\u201c\u5c82\u6562\uff0c\u6211\u672a\u5b66\u540e\u8f88\uff0c\u8981\u8bf7\u5c11\u5e2e\u4e3b\u6307 \u6559\u3002\u201d\u5c1a\u5c11\u4ead\u9053\uff1a\u201c\u4f60\u8fdc\u6765\u4e3a\u5ba2\uff0c\u8fd8\u662f\u8bf7\u4f60\u5148\u8d50\u62db\u5427\uff01\u201d\u90b9\u9521\u4e5d\u8bf4\u4e86\u4e24\u53e5\u95e8\u9762\u8bdd\uff0c\u9053\u58f0\u201c\u6709 \u8bf7\u201d\uff0c\u5de6\u638c\u5411\u5916\u4e00\u7ffb\uff0c\u53f3\u62f3\u547c\u7684\u6253\u51fa\uff0c\u5c1a\u5c11\u4ead\u53cc\u638c\u4e00\u6452\uff0c\u4f7f\u4e86\u4e00\u62db\u201c\u4e91\u624b\u201d\uff0c\u628a\u4ed6\u7684\u62db\u6570\u7834 \u4e86\u3002\u90b9\u9521\u4e5d\u6697\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4e5f\u662f\u592a\u6781\u6d3e\u201d\uff0c\u7ad9\u7a33\u9a6c\u6b65\uff0c\u53cc\u62f3\u8fdb\u640f\u3002\u90b9\u9521\u4e5d\u7684\u4e0b\u76d8\u529f\u592b\u6781\u7a33\uff0c \u5c1a\u5c11\u4ead\u60f3\u501f\u529b\u6253\u529b\uff0c\u5c06\u4ed6\u6253\u5012\u53ef\u4e0d\u80fd\u591f\uff0c\u6597\u4e86\u4e8c\u5341\u591a\u56de\u5408\uff0c\u521a\u67d4\u4e92\u649e\uff0c\u53cc\u65b9\u90fd\u6ca1\u4fbf\u5b9c\uff0c\u90b9\u9521 \u4e5d\u62f3\u62f3\u6709\u529b\uff0c\u864e\u864e\u751f\u98ce\uff0c\u5510\u6653\u6f9c\u5168\u795e\u6ce8\u89c6\uff0c\u4e0d\u89c9\u8d5e\u9053\uff1a\u201c\u4ed6\u7684\u4e94\u884c\u62f3\u5927\u6709\u8fdb\u5883\u4e86\uff01\u201d\u5415\u56db\u5a18 \u5728\u4ed6\u8eab\u65c1\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u662f\u65e9\u76f8\u8bc6\u7684\uff1f\u201d\u5510\u6653\u6f9c\u70b9\u4e86\u70b9\u5934\uff0c\u76ee\u4e0d\u65c1\u77ac\uff0c\u5fc3\u4e2d\u6697\u6697\u5e0c\u671b\u90b9\u9521\u4e5d \u5f97\u80dc\u3002 \u3000\u3000\u6253\u4e86\u4e00\u4f1a\uff0c\u90b9\u9521\u4e5d\u6e10\u5360\u4e0a\u98ce\uff0c\u518d\u4e0d\u50cf\u4ee5\u524d\u7684\u6c89\u7a33\uff0c\u4e94\u884c\u62f3\u7684\u62f3\u62db\u672c\u6765\u5229\u4e8e\u91c7\u53d6\u653b\u52bf\uff0c\u8981 \u5148\u62db\u624d\u53d1\uff0c\u540e\u62db\u5373\u5230\uff0c\u624d\u663e\u5f97\u51fa\u96c4\u52b2\uff0c\u90b9\u9521\u4e5d\u5360\u4e86\u4e0a\u98ce\uff0c\u89c9\u5f97\u6b64\u4eba\u4e4b\u6280\u4ea6\u4e0d\u8fc7\u5982\u662f\uff0c\u4fbf\u5373\u659c \u8eab\u4e0a\u8dc3\uff0c\u4ece\u201c\u767b\u5c71\u8de8\u864e\u52bf\u201d\u53d8\u4f5c\u201c\u62bd\u7cb1\u6362\u67f1\u201d\uff0c\u5de6\u638c\u62a4\u80f8\uff0c\u53f3\u638c\u5954\u654c\u4eba\u80f8\u53e3\u731b\u6363\u3002\u5c1a\u5c11\u4ead\u5fae \u5fae\u4e00\u95ea\uff0c\u5ffd\u7136\u6539\u4e86\u7334\u62f3\uff0c\u5411\u524d\u4e00\u4ec6\uff0c\u624b\u722a\u8d77\u5904\uff0c\u7adf\u7167\u90b9\u9521\u4e5d\u53cc\u775b\u6293\u53bb\uff0c\u8fd9\u4e00\u53d8\u62db\uff0c\u5927\u51fa\u90b9\u9521 \u4e5d\u610f\u5916\uff0c\u6025\u6c47\u6536\u52bf\u64a4\u62db\uff0c\u5c06\u62a4\u80f8\u7684\u5de6\u638c\u5f80\u4e0b\u4e00\u7ffb\uff0c\u5f80\u4e0a\u4e00\u683c\uff0c\u90a3\u6599\u5c1a\u5c11\u4ead\u5316\u6293\u4e3a\u638c\uff0c\u7a81\u5411\u90b9 \u9521\u4e5d\u5bf8\u5173\u5c3a\u6263\u6765\uff0c\u8fd9\u4e00\u624b\u5374\u662f\u201c\u64d2\u62ff\u624b\u201d\uff0c\u90b9\u9521\u4e5d\u7f29\u80a9\u9000\u6b65\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c1a\u5c11\u4eab\u53f3\u624b \u4e00\u62f3\uff0c\u731b\u7136\u6363\u51fa\uff0c\u6377\u4f3c\u5954\u96f7\u95ea\u7535\uff0c\u90b9\u9521\u4e5d\u907f\u4e4b\u4e0d\u53ca\uff0c\u53ea\u597d\u7528\u80a9\u5934\u4e00\u9876\uff0c\u53ea\u542c\u84ec\u7684\u4e00\u58f0\uff0c\u90b9\u9521 \u4e5d\u6328\u4e86\u4e00\u62f3\uff0c\u9000\u51fa\u51e0\u6b65\uff0c\u5e78\u672a\u8dcc\u5012\uff0c\u94c1\u6247\u5e2e\u4eba\uff0c\u54d7\u7136\u5927\u7b11\uff01 \u3000\u3000\u90b9\u9521\u4e5d\u7ecf\u4e86\u51e0\u5e74\u5386\u7ec3\uff0c\u6db5\u517b\u867d\u6bd4\u5c11\u5e74\u65f6\u4e3a\u597d\uff0c\u4f46\u5f53\u573a\u51fa\u5f69\uff0c\u4e5f\u81ea\u6309\u637a\u4e0d\u4f4f\uff0c\u970d\u5730\u4e00\u4e2a\u7ffb \u8eab\uff0c\u62f3\u884c\u5982\u98ce\uff0c\u8fde\u7528\u5288\u3001\u94bb\u3001\u70ae\u3001\u6a2a\u3001\u5d29\u7684\u4e94\u5b57\u8bc0\uff0c\u628a\u4e94\u884c\u62f3\u4f7f\u5f97\u5982\u72c2\u98ce\u805a\u96e8\u4e00\u822c\uff0c\u5411\u5c1a\u5c11 \u4ead\u72e0\u72e0\u653b\u51fb\u3002 \u3000\u3000\u5c1a\u5c11\u4ead\u8fd9\u65f6\u5df2\u6478\u719f\u4e86\u90b9\u9521\u4e5d\u7684\u62f3\u8def\uff0c\u4ed6\u81ea\u5c0f\u4fbf\u968f\u7236\u4eb2\u5954\u8d70\u6c5f\u6e56\uff0c\u7ecf\u5e38\u548c\u5404\u5e2e\u5404\u6d3e\u7684\u9ad8\u624b \u6bd4\u62f3\u6597\u5251\uff0c\u4e34\u573a\u7684\u529f\u592b\u6bd4\u90b9\u9521\u4e5d\u5f3a\u5f97\u591a\uff0c\u52a0\u4ee5\u4ed6\u6240\u5b66\u53c8\u6742\uff0c\u4e0d\u62d8\u4e00\u683c\uff0c\u90b9\u9521\u4e5d\u8fd9\u4e00\u5f3a\u653b\uff0c\u6b63 \u5408\u4e86\u4ed6\u7684\u5fc3\u610f\uff0c\u53ea\u89c1\u4ed6\u4e0d\u614c\u4e0d\u5fd9\uff0c\u968f\u7740\u90b9\u9521\u4e5d\u7684\u62f3\u98ce\u98d8\u6765\u6643\u53bb\uff0c\u5916\u884c\u7684\u770b\u6765\uff0c\u4f3c\u4e4e\u4ed6\u5df2\u88ab\u90b9 \u9521\u4e5d\u903c\u5f97\u53ea\u6709\u9000\u5b88\u7684\u4efd\u513f\uff1b\u5185\u884c\u7684\u770b\u6765\uff0c\u4ed6\u6b63\u662f\u5904\u5904\u5236\u654c\u673a\u5148\uff0c\u62f3\u8def\u517c\u6c89\u6377\u4e0e\u98d8\u5ffd\u4e24\u8005\u4e4b \u957f\uff0c\u53ea\u7b49\u90b9\u9521\u4e5d\u62f3\u52bf\u7a0d\u7f13\uff0c\u4ed6\u5c31\u8981\u7acb\u4e0b\u6740\u624b\uff01 \u3000\u3000\u90b9\u9e23\u768b\u770b\u5f97\u6697\u6697\u53f9\u6c14\uff0c\u6697\u9a82\u8fd9\u5b69\u5b50\u8ddf\u4e86\u81ea\u5df1\u8fd9\u4e48\u591a\u5e74\uff0c\u4ecd\u7136\u4e0d\u61c2\u62f3\u7406\u3002\u81ea\u5df1\u4e0d\u77e5\u5bf9\u4ed6\u8bf4 \u8fc7\u591a\u5c11\u904d\uff0c\u8981\uff1a\u201c\u80dc\u52ff\u9a84\uff0c\u8d25\u52ff\u4e71\u3002\u201d\u4ed6\u5374\u5148\u9a84\u540e\u4e71\uff0c\u5982\u6b64\u4e0b\u53bb\uff0c\u5b89\u5f97\u4e0d\u8d25\u3002 \u3000\u3000\u90b9\u9e23\u768b\u4e00\u9762\u6697\u9a82\uff0c\u4e00\u9762\u5fc3\u6025\uff0c\u770b\u90a3\u5c1a\u5c11\u4ead\u638c\u6cd5\u9634\u8fa3\uff0c\u5b9a\u662f\u60f3\u8d81\u6b64\u673a\u4f1a\uff0c\u75db\u4e0b\u6bd2\u624b\uff0c\u597d\u7acb \u4e07\u626c\u540d\u3002\u4f46\u90b9\u9e23\u768b\u7a7a\u81ea\u7740\u6025\uff0c\u4ee5\u4ed6\u7684\u58f0\u671b\u8eab\u4efd\uff0c\u5374\u65ad\u4e0d\u80fd\u51fa\u62f3\u76f8\u52a9\uff0c\u8fde\u559d\u6b62\u8ba4\u8f93\u4e5f\u4e0d\u80fd\u591f\u3002 \u6b63\u7740\u6025\u95f4\uff0c\u5ffd\u89c1\u90b9\u9521\u4e5d\u5f3a\u7528\u4e94\u884c\u62f3\u4e2d\u7684\u201c\u51b2\u4e91\u5288\u96fe\u201d\u4e00\u62db\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u51b2\u51fb\u654c\u624b\u4e0a\u76d8\uff0c\u5c1a\u5c11 \u4ead\u4e00\u58f0\u51b7\u7b11\uff0c\u75be\u51fa\u53f3\u638c\uff0c\u53cd\u624b\u4e00\u62ff\uff0c\u5de6\u624b\u6302\u62f3\uff0c\u5411\u5bf9\u719f\u91a2\u592a\u9633\u7a74\u201d\u6a2a\u5288\uff0c\u82e5\u7136\u7ed9\u4ed6\u51fb\u4e2d\uff0c\u90b9 \u9521\u4e5d\u53ea\u6050\u6709\u6027\u547d\u4e4b\u5371\uff01 \u3000\u3000\u90b9\u9e23\u768b\u60ca\u53eb\u4e00\u58f0\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u8eab\u4efd\uff0c\u7eb5\u8eab\u4fbf\u8d77\uff0c\u80a9\u5934\u5ffd\u7136\u7ed9\u4eba\u4e00\u6309\uff0c\u8033\u8fb9\u542c\u5f97\u90a3\u4eba\u559d \u9053\uff1a\u201c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u6309\u4ed6\u7684\u4eba\u6b63\u662f\u51cc\u4e91\u5c9b\u4e3b\u536b\u626c\u5a01\u3002\u90b9\u9e23\u768b\u5fc3\u610f\u5982\u711a\uff0c\u80a9\u5934\u4e00\u8038\uff0c\u6b63\u60f3\u786c\u628a \u8eab\u5f62\u62d4\u8d77\uff0c\u8fd9\u4e00\u77ac\u95f4\uff0c\u5ffd\u542c\u5f97\u5c1a\u590d\u521d\u547c\u559d\u4e4b\u58f0\uff0c\u4e00\u6761\u9ed1\u5f71\u75be\u5982\u98de\u7bad\u51b2\u5411\u573a\u5fc3\uff0c\u5c1a\u5c11\u4ead\u548c\u90b9\u9521 \u4e5d\u500f\u7684\u5411\u4e24\u8fb9\u5206\u5f00\uff0c\u8fd9\u4e00\u4e0b\u53d8\u51fa\u610f\u5916\uff0c\u536b\u626c\u5a01\u677e\u4e86\u624b\u638c\uff0c\u90b9\u9e23\u768b\u548c\u5c1a\u590d\u521d\u53cc\u53cc\u8dc3\u51fa\uff0c\u53ea\u89c1\u573a \u4e2d\u4e00\u4e2a\u82f1\u4fca\u5c11\u5e74\uff0c\u7b11\u541f\u541f\u7684\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u4e24\u864e\u76f8\u6597\uff0c\u5fc5\u6709\u4e00\u4f24\uff0c\u665a\u8f88\u5192\u6627\uff0c\u5f3a\u4f5c\u2018\u4ef2\u8fde\u2019 \uff08\u8c03\u89e3\u4eba\u4e4b\u610f\uff09\uff0c\u8bf7\u4e24\u4f4d\u8001\u524d\u8f88\u6055\u7f6a\u3002\u201d \u3000\u3000\u90b9\u9e23\u768b\u7236\u5b50\u548c\u5510\u6653\u6f9c\u4e00\u522b\u6570\u5e74\uff0c\u66f4\u517c\u4ed6\u6613\u4e86\u989c\u5bb9\uff0c\u7adf\u4e0d\u8ba4\u5f97\u3002\u90b9\u9521\u4e5d\u5341\u5206\u611f\u6fc0\uff0c\u5c1a\u5c11\u4ead \u867d\u7136\u6c14\u6124\uff0c\u4f46\u4ed6\u521a\u624d\u7ed9\u5510\u6653\u6f9c\u4e00\u624b\u62c9\u5f00\uff0c\u5df2\u77e5\u6b64\u4eba\u6b66\u529f\u8fdc\u5728\u5df1\u4e0a\uff0c\u4e0d\u6562\u4e0a\u524d\u6311\u8845\uff0c\u53ea\u662f\u7ad9\u5728 \u4e00\u65c1\u53d1\u8bdd\u9053\uff1a\u201c\u4f60\u61c2\u4e0d\u61c2\u6c5f\u6e56\u89c4\u77e9\uff1f\u4f60\u5206\u660e\u89c1\u6211\u8d62\u4e86\uff0c\u8fd9\u624d\u786c\u6765\u63d2\u624b\u3002\u8fd9\u53ea\u80fd\u7b97\u662f\u52a9\u62f3\uff0c\u90a3 \u80fd\u7b97\u662f\u6392\u89e3\uff1f\u201d \u3000\u3000\u7518\u51e4\u6c60\u89c1\u5510\u6653\u6f9c\u9aa4\u7136\u51fa\u624b\uff0c\u4e5f\u9887\u611f\u60ca\u8bb6\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u53ef\u8981\u7cdf\uff01\u201d\u679c\u7136\u94c1\u6247\u5e2e\u7684\u4eba\uff0c\u7eb7\u7eb7 \u6012\u9a82\uff0c\u5c1a\u590d\u521d\u51b7\u7b11\u9053\uff1a\u201c\u90b9\u8001\u82f1\u96c4\u4ea4\u6e38\u771f\u5e7f\uff0c\u968f\u5904\u90fd\u53ef\u9080\u4eba\u52a9\u62f3\u3002\u53ea\u662f\u8fd9\u6837\u95f7\u8336\u5b50\u4e0d\u4eae\u94a2 \uff08\u4e8b\u5148\u4e0d\u5148\u58f0\u660e\u4e4b\u610f\uff09\uff0c\u5374\u6697\u5730\u91cc\u5077\u6253\u4e00\u62f3\uff0c\u6697\u88ad\u4e00\u638c\uff0c\u6050\u6015\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u82f1\u96c4\u884c\u5f84\u3002\u597d\uff0c \u8fd9\u4f4d\u5c0f\u54e5\u65e2\u7136\u66ff\u90b9\u8001\u82f1\u96c4\u52a9\u62f3\uff0c\u90a3\u5c31\u8bf7\u4e00\u5e76\u66ff\u90b9\u8001\u82f1\u96c4\u63a5\u4e86\u8fd9\u4e00\u573a\u5427\uff01\u201d \u3000\u3000\u5c1a\u590d\u521d\u8fd9\u4e00\u756a\u8bdd\u8bf4\u5f97\u5341\u5206\u8001\u8fa3\uff0c\u8bcd\u950b\u5484\u5484\uff0c\u5c06\u90b9\u9e23\u768b\u595a\u843d\u4e00\u756a\uff0c\u53c8\u76f4\u63a5\u5411\u5510\u6653\u6f9c\u6311\u6218\u3002 \u8fd9\u65f6\u5168\u573a\u76ee\u5149\u90fd\u76ef\u7740\u5510\u6653\u6f9c\uff0c\u5415\u56db\u5a18\u7518\u51e4\u6c60\u6697\u6697\u7740\u6025\uff01 \u3000\u3000\u90b9\u9e23\u768b\u9762\u8272\u53d8\u7d2b\uff0c\u987b\u7709\u6380\u52a8\uff0c\u4ed6\u662f\u4e2a\u6210\u540d\u82f1\u96c4\uff0c\u5982\u4f55\u5fcd\u5f97\u5c1a\u590d\u521d\u7684\u595a\u843d\uff0c\u770b\u5c1a\u590d\u521d\u5484\u5484 \u903c\u4eba\u8fc8\u6b65\u79fb\u8eab\uff0c\u5c31\u8981\u548c\u5510\u6653\u6f9c\u4ea4\u624b\uff0c\u90b9\u9e23\u768b\u5927\u559d\u4e00\u58f0\u201c\u4e14\u6162\uff01\u201d\u62e6\u5728\u5510\u6653\u6f9c\u9762\u524d\uff0c\u9ad8\u58f0\u53eb \u9053\uff1a\u201c\u6211\u6709\u8bdd\u8bf4\uff01\u201d \u3000\u3000\u5c1a\u590d\u521d\u81ea\u89c9\u9053\u7406\u4e0a\u5360\u4e86\u4e0a\u98ce\uff0c\u9634\u4fa7\u4fa7\u7684\u7b11\u9053\uff1a\u201c\u90b9\u8001\u82f1\u96c4\u6709\u4f55\u89c1\u6559\uff1f\u201d\u90b9\u9e23\u768b\u6717\u58f0\u8bf4 \u9053\uff1a\u201c\u54b1\u4eec\u7ea6\u5b9a\u6bd4\u8bd5\u4e09\u573a\uff0c\u7b2c\u4e00\u573a\u627f\u8d35\u5e2e\u8ba9\u4e86\uff0c\u7b2c\u4e8c\u573a\u867d\u672a\u51b3\u96cc\u96c4\uff0c\u4f46\u5c11\u5e2e\u4e3b\u786e\u662f\u5360\u4e86\u4e0a \u98ce\uff0c\u6211\u4eec\u4fbf\u8ba4\u683d\u3002\u81f3\u4e8e\u8fd9\u4f4d\u5c11\u5e74\u82f1\u96c4\u548c\u6211\u7d20\u5473\u5e73\u751f\uff0c\u4ed6\u6392\u89e3\u662f\u51fa\u4e8e\u597d\u610f\uff0c\u6211\u5728\u8fd9\u513f\u62dc\u8c22\u4e86\u3002 \u6211\u53ef\u4e0d\u6562\u8bf7\u4ed6\u66ff\u6211\u9876\u9505\uff0c\u54b1\u4eec\u4ecd\u7167\u524d\u7ea6\uff0c\u8fd9\u7b2c\u4e09\u573a\u7531\u6211\u9886\u6559\u5e2e\u4e3b\u7684\u9ad8\u62db\u3002\u5e2e\u4e3b\u662f\u6c5f\u5357\u54cd\u5f53\u5f53 \u7684\u811a\u8272\uff0c\u6211\u82e5\u7ed9\u5e2e\u4e3b\u8f83\u77ed\uff0c\u90a3\u662f\u867d\u8d25\u72b9\u8363\u3002\u4e07\u4e00\u6211\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\u8fd8\u7ecf\u5f97\u8d77\u6253\uff0c\u6216\u8005\u627f\u5e2e\u4e3b\u8ba9 \u6211\u90b9\u67d0\u4e00\u62db\u4e24\u62db\u5462\uff0c\u90a3\u4e48\u6211\u5c31\u5f97\u62dc\u9886\u5e2e\u4e3b\u7684\u5927\u6069\uff0c\u655d\u53cb\u4e5f\u5f97\u514d\u503e\u5bb6\u8361\u4ea7\u4e4b\u7978\uff01 \u3000\u3000\u90b9\u9e23\u768b\u8fd9\u756a\u8bdd\u6263\u5f97\u5f88\u7d27\uff0c\u5148\u8ba4\u90b9\u9521\u4e5d\u8fd9\u573a\u8f93\u4e86\uff0c\u7136\u540e\u4ecd\u7533\u524d\u7ea6\uff0c\u4ecd\u8981\u8f83\u6280\u8ba8\u94f6\uff0c\u8bf4\u8bdd\u4e4b \u95f4\uff0c\u6620\u51fa\u5c1a\u590d\u521d\u6c14\u91cf\u7684\u72ed\u7a84\u3002\u5404\u5e2e\u9996\u9886\u7a83\u7a83\u79c1\u8bae\uff0c\u89c9\u5f97\u5c1a\u590d\u521d\u8981\u5411\u4e00\u5c11\u5e74\u540e\u8f88\u62a5\u590d\uff0c\u5148\u5df2\u5931 \u4e86\u8eab\u5206\uff1b\u4e3a\u8fd9\u4e94\u4e07\u4e24\u94f6\u5b50\uff0c\u8981\u7d2f\u4eba\u503e\u5bb6\u8361\u4ea7\u66f4\u662f\u4e0d\u8be5\u3002\u56e0\u6b64\u5728\u90b9\u9e23\u768b\u8bdd\u5b8c\u4e4b\u540e\uff0c\u7adf\u6ca1\u4eba\u66ff\u5c1a \u590d\u521d\u5e2e\u8154\uff0c\u53cd\u800c\u8bb8\u591a\u4eba\u90fd\u8fd9\u6837\u8d5e\u9053\uff1a\u201c\u5230\u5e95\u59dc\u662f\u8001\u7684\u8fa3\uff0c\u4f60\u77a7\u63d2\u7ffc\u795e\u72ee\u7684\u8bf4\u8bdd\u628a\u672c\u6765\u662f\u4e0b\u98ce \u7684\u5c40\u9762\u4e00\u4e0b\u5b50\u4fbf\u626d\u8fc7\u6765\u4e86\u3002\u201d \u3000\u3000\u5c1a\u590d\u521d\u9762\u8272\u4e00\u53d8\uff0c\u6325\u6325\u624b\u9053\uff1a\u201c\u5c11\u4ead\u4f60\u9000\u4e0b\uff0c\u90b9\u8001\u82f1\u96c4\uff0c\u54b1\u4eec\u662f\u62f3\u811a\u4e0a\u89c1\u9ad8\u4f4e\uff0c\u8fd8\u662f\u5175 \u5203\u4e0a\u5206\u80dc\u8d1f\uff1f\u201d\u90b9\u9e23\u768b\u6325\u4e86\u6325\u624b\uff0c\u5510\u6653\u6f9c\u548c\u90b9\u8e22\u4e5d\u4e5f\u4e00\u9f50\u9000\u4e86\uff0c\u90b9\u9e23\u768b\u968f\u4fbf\u7acb\u4e86\u4e2a\u95e8\u6237\uff0c\u8bf4 \u9053\uff1a\u201c\u4e0d\u7ba1\u62f3\u811a\u5175\u5203\uff0c\u6211\u4e00\u5e76\u5949\u966a\uff01\u201d \u3000\u3000\u5c1a\u590d\u521d\u52c3\u7136\u5927\u6012\uff0c\u628a\u957f\u8896\u4e00\u629b\uff0c\u5c31\u8981\u52a8\u624b\uff0c\u5ffd\u7136\u95e8\u5916\u6709\u4eba\u8dd1\u8fdb\u6765\uff0c\u662f\u62c5\u4efb\u201c\u77e5\u5ba2\u201d\u7684\u9999 \u4e3b\uff0c\u9ad8\u4e3e\u5927\u7ea2\u62dc\u5323\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\u548c\u5173\u4e1c\u56db\u4fa0\u62dc\u4f1a\u4f60\u8001\uff01\u201d\u5c1a\u590d\u521d\u548c \u90b9\u9e23\u768b\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u5c1a\u590d\u521d\u970d\u5730\u8df3\u5f00\uff0c\u62ab\u56de\u957f\u886b\uff0c\u5411\u90b9\u9e23\u768b\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u573a\u6682\u4e14\u62bc \u540e\uff01\u201d\u4eb2\u81ea\u7387\u9886\u5e2e\u4e2d\u51e0\u4e2a\u5934\u76ee\uff0c\u8d70\u51fa\u752c\u9053\u8fce\u5bbe\u3002 \u3000\u3000\u90b9\u9e23\u768b\u4e00\u9635\u5fc3\u8df3\uff0c\u548c\u8fd9\u4f4d\u8001\u670b\u53cb\u6570\u5e74\u4e0d\u89c1\uff0c\u5374\u4e0d\u60f3\u5728\u8fd9\u91cc\u76f8\u9047\u3002\u4ed6\u6b63\u4e0d\u77e5\u89c1\u4e86\u6768\u4ef2\u82f1\u53ef \u8bf4\u4e9b\u4ec0\u4e48\u8bdd\u597d\uff1f\u5510\u6653\u6f9c\u7684\u5fc3\u66f4\u66f4\u662f\u535c\u535c\u7684\u8df3\uff0c\u7518\u51e4\u6c60\u5fae\u5fae\u4e00\u7b11\uff0c\u63e1\u7740\u4ed6\u7684\u624b\u8bf4\u9053\uff1a\u201c\u5510\u5144\u4e0d \u5fc5\u62c5\u5fc3\uff0c\u7b49\u4e0b\u4f60\u4e5f\u4e0d\u5fc5\u58f0\u5f20\uff0c\u7531\u6211\u66ff\u4f60\u89e3\u5f00\u8fd9\u4e2a\u7ed3\u5427\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u6768\u4ef2\u82f1\u548c\u5173\u4e1c\u56db\u4fa0\u5df2\u8054 \u8882\u8d70\u8fdb\uff01 \u3000\u3000\u6768\u4ef2\u82f1\u53cc\u773c\u4e00\u626b\uff0c\u77a7\u89c1\u4e86\u90b9\u9e23\u768b\u7236\u5b50\uff0c\u62a2\u4e0a\u524d\u53bb\u70ae\u62f3\u8bf4\u9053\uff1a\u201c\u90b9\u5927\u54e5\uff0c\u522b\u6765\u53ef\u597d\uff1f\u201d\u90b9 \u9e23\u768b\u54fc\u4e86\u4e00\u58f0\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u597d\u3002\u201d\u6768\u4ef2\u82f1\u5ffd\u7136\u8f6c\u8fc7\u5934\u6765\uff0c\u5bf9\u5c1a\u590d\u521d\u9053\uff1a\u201c\u5c1a\u5e2e\u4e3b\uff0c\u8d35\u5e2e\u4eca \u65e5\u91cd\u5f00\u9999\u5802\uff0c\u6211\u6768\u67d0\u4e00\u6765\u5411\u4f60\u9053\u559c\uff0c\u4e8c\u6765\u53ef\u6709\u4e8b\u76f8\u6c42\u3002\u201d\u5c1a\u590d\u521d\u9053\uff1a\u201c\u6768\u8001\u82f1\u96c4\u4f55\u4e8b\u76f8\u5546\uff0c \u4f46\u8bf4\u65e0\u59a8\uff01\u201d\u6768\u4ef2\u82f1\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u60f3\u8bf7\u5e2e\u4e3b\u501f\u4e94\u4e07\u4e24\u94f6\u5b50\uff01\u201d \u3000\u3000\u5c1a\u590d\u521d\u9762\u8272\u500f\u53d8\uff0c\u968f\u5373\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u6768\u8001\u82f1\u96c4\u82e5\u7f3a\u94f6\u5b50\u4f7f\u7528\u5c3d\u7ba1\u62ff\u53bb\uff0c\u4f46\u4e0d\u77e5\u4f55\u4ee5\u5b9a\u8981 \u4e94\u4e07\uff1f\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u6211\u6709\u4e00\u4f4d\u670b\u53cb\uff0c\u548c\u6211\u662f\u751f\u6b7b\u4e4b\u4ea4\uff0c\u4ed6\u7684\u513f\u5b50\u4e5f\u5c31\u662f\u6211\u7684\u4f84\u513f\u3002\u6211\u7684\u4f84 \u513f\u66ff\u4eba\u4fdd\u9556\uff0c\u5931\u4e86\u4e94\u4e07\u4e24\u94f6\u5b50\uff0c\u4ed6\u4eec\u6d41\u6d6a\u6c5f\u6e56\uff0c\u6ca1\u50cf\u4f60\u5e84\u4e3b\u90a3\u6837\u79ef\u6709\u767e\u4e07\u8eab\u5bb6\u3002\u8d54\u4e0d\u8d77\u94f6 \u5b50\uff0c\u6211\u8fd9\u53d4\u7236\u600e\u80fd\u4e0d\u66ff\u4ed6\u5f20\u7f57\uff1f\u201d\u5c1a\u590d\u521d\u9053\uff1a\u201c\u554a\uff0c\u4f60\u662f\u4e3a\u63d2\u7ffc\u795e\u72ee\u8ba8\u9556\u94f6\u6765\u4e86\uff01\u201d\u6768\u4ef2\u82f1 \u9053\uff1a\u201c\u6b63\u662f\uff0c\u6211\u548c\u5173\u4e1c\u56db\u4fa0\u8981\u8bf7\u4f60\u8001\u8d4f\u4e2a\u9762\u5b50\uff01\u201d \u3000\u3000\u6768\u4ef2\u82f1\u5728\u6b66\u6797\u4e2d\u5fb7\u9ad8\u671b\u91cd\uff0c\u5173\u4e1c\u56db\u4fa0\u66f4\u662f\u6c5f\u6e56\u4e0a\u6210\u540d\u7684\u82f1\u96c4\u3002\u5c1a\u590d\u521d\u4e00\u77a7\u81ea\u5df1\u8fd9\u8fb9\uff0c\u6b66 \u529f\u6700\u5f3a\u7684\u97e9\u91cd\u5c71\u8fd8\u6ca1\u6709\u56de\u6765\uff0c\u81ea\u5df1\u548c\u5b5f\u6b66\u529f\u536b\u626c\u5a01\u4e09\u4eba\u53ea\u6050\u4e0d\u662f\u4ed6\u4eec\u5bf9\u624b\uff0c\u867d\u7136\u5fc3\u4e2d\u6c14\u6124\uff0c \u4e5f\u53ea\u597d\u88c5\u51fa\u7b11\u5bb9\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u8fd9\u70b9\u5c0f\u4e8b\uff0c\u7d2f\u5f97\u4e94\u4f4d\u8fdc\u9053\u800c\u6765\uff0c\u5728\u4e0b\u5b9e\u5728\u8fc7\u610f\u4e0d\u53bb\uff0c\u51b2\u7740\u6768 \u8001\u82f1\u96c4\u548c\u5173\u4e1c\u56db\u4fa0\u7684\u9762\u5b50\uff0c\u83ab\u8bf4\u4e94\u4e07\u4e24\u94f6\u5b50\uff0c\u518d\u591a\u5c0f\u5f1f\u4e5f\u8be5\u5949\u4e0a\u3002\u201d\u8bf4\u7f62\uff0c\u4e00\u9762\u53eb\u4eba\u53bb\u6253\u94f6 \u7968\uff0c\u4e00\u9762\u53eb\u4eba\u6dfb\u4e86\u4e00\u684c\u9152\u5e2d\uff0c\u8bf7\u6768\u4ef2\u82f1\u548c\u5173\u4e1c\u56db\u4fa0\u4e0a\u5ea7\uff0c\u53c8\u5230\u90b9\u9e23\u768b\u548c\u51cc\u5cb3\u4e4b\u524d\u966a\u4e86\u4e2a\u624e\uff0c \u8bf7\u4ed6\u4eec\u548c\u6768\u4f53\u82f1\u5750\u5728\u4e00\u8d77\u3002 \u3000\u3000\u90b9\u9e23\u768b\u521a\u624d\u89c1\u4e86\u5c1a\u5c11\u4ead\u7684\u529f\u592b\uff0c\u60c5\u77e5\u5c1a\u590d\u521d\u4e00\u5b9a\u66f4\u9ad8\uff0c\u6bd4\u8bd5\u8d77\u6765\uff0c\u81ea\u5df1\u5b9e\u65e0\u628a\u63e1\u3002\u6768\u4ef2 \u82f1\u66ff\u4ed6\u89e3\u4e86\u56f0\u5371\uff0c\u4ed6\u5fc3\u4e2d\u81ea\u662f\u611f\u6fc0\uff0c\u4f46\u56e0\u6709\u4ee5\u524d\u90a3\u6bb5\u8fc7\u8282\uff0c\u4e00\u65f6\u95f4\u5374\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u7384\u98ce\u548c\u90b9 \u9e23\u768b\u4e5f\u662f\u8001\u76f8\u8bc6\u4e86\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6768\u5927\u54e5\u542c\u5230\u4e86\u90b9\u8001\u82f1\u96c4\u4e4b\u4e8b\uff0c\u6025\u5f97\u4e0d\u5f97\u4e86\uff0c\u50ac\u6211\u4eec\u5144\u5f1f\u8fde \u591c\u8d76\u6765\uff0c\u5e78\u597d\u6b63\u662f\u65f6\u5019\u3002\u201d\u90b9\u9e23\u768b\u5fc3\u60f3\u539f\u6765\u4ed6\u4e13\u7a0b\u8d76\u6765\uff0c\u5168\u4e3a\u7684\u662f\u6211\u3002\u6768\u4ef2\u82f1\u659f\u6ee1\u4e86\u9152\uff0c\u5bf9 \u90b9\u9e23\u768b\u9053\uff1a\u201c\u90a3\u5e74\u4e4b\u4e8b\uff0c\u5c0f\u5f1f\u975e\u5e38\u62b1\u6b49\u3002\u201d\u90b9\u9e23\u768b\u63a5\u4e86\u676f\u9152\uff0c\u4e00\u996e\u800c\u5c3d\uff0c\u5927\u7b11\u8bf4\u9053\uff1a\u201c\u60a3\u96c4 \u89c1\u4ea4\u60c5\uff0c\u513f\u5973\u4e4b\u4e8b\uff0c\u518d\u4e5f\u4f11\u63d0\u3002\u201d \u3000\u3000\u6768\u4ef2\u82f1\u8fd9\u8fb9\u8c08\u7b11\u98ce\u751f\uff0c\u5c1a\u590d\u521d\u5341\u5206\u6ca1\u8da3\uff0c\u4e00\u8154\u6012\u6c14\uff0c\u53d1\u6cc4\u5230\u5510\u6653\u6f9c\u8eab\u4e0a\uff0c\u5728\u5e2d\u4e0a\u5927\u58f0\u8bf4 \u9053\uff1a\u201c\u4eca\u65e5\u655d\u5e2e\u91cd\u5f00\u9999\u5802\uff0c\u5404\u4f4d\u82f1\u96c4\u4e0d\u7ea6\u800c\u5230\uff0c\u5b9e\u5728\u589e\u5149\u4e0d\u5c11\u3002\u9002\u624d\u8fd9\u4f4d\u51af\u5144\uff08\u5510\u6653\u6f9c\u5316\u540d \u51af\u5c27\uff09\uff0c\u663e\u4e86\u90a3\u4e48\u6f02\u4eae\u7684\u529f\u592b\uff0c\u6211\u4eec\u5c24\u5176\u4f69\u670d\u3002\u53ea\u53ef\u60dc\u4e09\u62db\u4e24\u5f0f\uff0c\u96be\u7aa5\u5168\u8c79\uff0c\u6211\u4eec\u8fd8\u60f3\u8bf7\u51af \u5144\u518d\u663e\u663e\u529f\u592b\uff0c\u8ba9\u6211\u4eec\u5f00\u5f00\u773c\u754c\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u4e0d\u77e5\u6240\u7b54\uff0c\u536b\u626c\u5a01\u4eba\u751a\u7cbe\u7075\uff0c\u770b\u5510\u6653\u6f9c\u8eab\u6750\uff0c\u542c\u5510\u6653\u6f9c\u8bf4\u8bdd\uff0c\u4f3c\u4e4e\u5728\u90a3\u513f\u89c1\u8fc7\u4f3c \u7684\u3002\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u4e0d\u5982\u6211\u548c\u8fd9\u4f4d\u51af\u5144\u5bf9\u62c6\u51e0\u62db\uff0c\u7ed9\u5e2e\u4e3b\u52a9\u5174\u3002\u201d\u5510\u6653\u6f9c\u751a\u662f\u4e0d\u5b89\uff0c\u4e00\u65f6\u4f17\u4eba \u773c\u5149\uff0c\u5168\u96c6\u4e2d\u5728\u5510\u6653\u6f9c\u8eab\u4e0a\u3002 \u3000\u3000\u7384\u98ce\u9053\u957f\u8001\u4e8e\u6c5f\u6e56\uff0c\u8bb0\u6027\u5c24\u4f73\uff0c\u548c\u4ed6\u89c1\u8fc7\u9762\u7684\u4eba\uff0c\u5341\u5e74\u4e4b\u540e\u76f8\u9022\uff0c\u4ed6\u8fd8\u80fd\u53eb\u51fa\u4eba\u7684\u540d \u5b57\u3002\u770b\u4e86\u4e00\u9635\uff0c\u5ffd\u7136\u5bf9\u6768\u4ef2\u82f1\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u4eba\u50cf\u4e0d\u50cf\u5510\u6653\u6f9c\uff1f\u201d\u6768\u4ef2\u82f1\u4e5f\u8d77\u4e86\u7591\u5fc3\uff0c\u8bf4 \u9053\uff1a\u201c\u771f\u6709\u70b9\u4f3c\u3002\u201d\u9648\u5143\u9738\u9053\uff1a\u201c\u9762\u8c8c\u4e0d\u4f3c\u561b\uff01\u201d\u90b9\u9e23\u768b\u6025\u5c06\u5510\u6653\u6f9c\u9002\u624d\u5e2e\u5fd9\u81ea\u5df1\u7684\u4e8b\u8bf4 \u4e86\uff0c\u4ed6\u672c\u610f\u662f\u60f3\u7ed9\u7384\u98ce\u4ecb\u7ecd\u8fd9\u4f4d\u5c11\u5e74\u82f1\u96c4\uff0c\u90a3\u77e5\u7384\u98ce\u5ffd\u7136\u628a\u94c1\u62d0\u5728\u5730\u4e0a\u4e00\u70b9\uff0c\u8eab\u5f62\u9aa4\u7136\u98de\u7eb5 \u51fa\u53bb\uff01 \u3000\u3000\u536b\u626c\u5a01\u6b63\u5728\u53eb\u9635\uff0c\u5ffd\u89c1\u7384\u98ce\u98de\u7eb5\u51fa\u6765\uff0c\u4e0d\u77e5\u4ed6\u6765\u610f\u5982\u4f55\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u53ea\u542c\u5f97\u7384\u98ce\u56b7\u9053\uff1a \u201c\u6211\u8001\u9053\u6700\u559c\u6b22\u8d81\u70ed\u95f9\uff0c\u8fd9\u4f4d\u5c11\u5e74\u82f1\u96c4\u8170\u60ac\u5b9d\u5251\uff0c\u5b9a\u5fc5\u662f\u5251\u672f\u540d\u5bb6\u7684\u5f1f\u5b50\uff0c\u6211\u8001\u9053\u60f3\u548c\u4ed6\u6bd4 \u6bd4\u5251\u6cd5\u3002\u201d\u5c1a\u590d\u521d\u53c8\u60ca\u53c8\u559c\u3002\u7384\u98ce\u7684\u4e71\u62ab\u98ce\u5251\u6cd5\u6d77\u5185\u77e5\u540d\uff0c\u4e0d\u6599\u4ed6\u7adf\u4f1a\u51fa\u5934\u5e2e\u52a9\u81ea\u5df1\uff0c\u800c\u4e14 \u80af\u548c\u4e00\u4f4d\u5c11\u5e74\u6bd4\u5251\u3002 \u3000\u3000\u7518\u51e4\u6c60\u628a\u5510\u6653\u6f9c\u4e00\u63a8\uff0c\u8bf4\u9053\uff1a\u201c\u957f\u8005\u6709\u547d\uff0c\u540e\u8f88\u4e0d\u5e94\u63a8\u8f9e\uff0c\u4f60\u5c31\u966a\u90a3\u4f4d\u9053\u7237\u8d70\u4e00\u8d9f\u5251 \u5427\uff01\u201d\u5510\u6653\u6f9c\u89c1\u7518\u51e4\u6c60\u5982\u6b64\u8bf4\u6cd5\uff0c\u53ea\u597d\u786c\u7740\u5934\u76ae\u51fa\u53bb\uff0c\u7384\u98ce\u8feb\u4e0d\u53ca\u5f85\uff0c\u5de6\u624b\u94c1\u62d0\u4e00\u626b\uff0c\u53f3\u624b \u957f\u5251\u4ece\u62d0\u5e95\u76f4\u7a7f\u51fa\u6765\uff0c\u7384\u98ce\u7684\u5251\u6cd5\u72b9\u5982\u72c2\u98ce\u66b4\u96e8\uff0c\u8f9b\u8fa3\u8fc5\u75be\uff0c\u5510\u6653\u6f9c\u907f\u4e86\u4e24\u62db\uff0c\u9669\u4e9b\u88ab\u5251\u523a \u4e2d\uff0c\u907f\u65e0\u53ef\u907f\uff0c\u53ea\u597d\u628a\u6e38\u9f99\u5b9d\u5251\u62d4\u4e86\u51fa\u6765\uff0c\u987f\u65f6\u4e00\u7f15\u5bd2\u5149\uff0c\u6620\u65e5\u751f\u8f89\uff0c\u7384\u98ce\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60 \u8fd9\u53db\u5f92\uff0c\u8fd8\u4e0d\u5f03\u5251\u5c31\u7f1a\uff01\u201d\u5237\u5237\u4e24\u5251\uff0c\u5206\u523a\u5510\u6653\u6f9c\u8eab\u4e0a\u8981\u7a74\uff0c\u5de6\u624b\u94c1\u62d0\uff0c\u53c8\u5377\u5730\u5288\u626b\uff0c\u5510\u6653 \u6f9c\u7ed9\u903c\u5f97\u51dd\u795e\u5bf9\u4ed8\uff0c\u5206\u4e0d\u51fa\u5fc3\u795e\u8bf4\u8bdd\u3002 \u3000\u3000\u5e2d\u4e0a\u90b9\u9e23\u768b\u7236\u5b50\u5927\u4e3a\u9707\u60ca\uff0c\u90b9\u9e23\u768b\u4e00\u628a\u62c9\u7740\u4e86\u6768\u4ef2\u82f1\u7684\u8863\u8896\uff0c\u6025\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u4f4d \u5c11\u5e74\u82f1\u96c4\u66fe\u6551\u5c0f\u513f\u4e00\u547d\uff0c\u770b\u6765\u4e0d\u662f\u574f\u4eba\uff0c\u6709\u8bdd\u597d\u8bf4\uff0c\u4f60\u51fa\u53bb\u628a\u7384\u98ce\u9053\u5144\u529d\u6b62\u4e86\u5427\uff01\u201d\u6768\u4ef2\u82f1 \u9762\u631f\u5bd2\u971c\uff0c\u628a\u8863\u8896\u4e00\u6446\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u80cc\u53db\u5e08\u95e8\uff0c\u6295\u987a\u6ee1\u5974\uff0c\u7f6a\u5f53\u4e07\u6b7b\uff01\u201d\u7ad9\u8d77\u6765\u53eb\u9053\uff1a\u201c\u7384 \u98ce\u9053\u5144\uff0c\u4f60\u66ff\u6211\u628a\u8fd9\u5b7d\u5f92\u64d2\u4e86\u5427\uff0c\u8981\u751f\u7684\uff0c\u4e0d\u8981\u6b7b\u7684\uff01\u201d\u672c\u6765\u6768\u4ef2\u82f1\u4e5f\u60f3\u51fa\u53bb\uff0c\u53ef\u662f\u65e2\u6709\u7384 \u98ce\u52a8\u624b\uff0c\u4ed6\u4ee5\u5e08\u5085\u4e4b\u5c0a\uff0c\u5c31\u4e0d\u4fbf\u51fa\u53bb\u4e86\u3002\u90b9\u9e23\u768b\u89c1\u4ed6\u8fde\u9a82\u201c\u5b7d\u5f92\u201d\uff0c\u9a87\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u4eba\u662f \u8c01\uff1f\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u4ed6\u662f\u5510\u6653\u6f9c\uff0c\u5f53\u5e74\u548c\u9521\u4e5d\u66fe\u4ea4\u8fc7\u624b\u3002\u201d\u90b9\u9521\u4e5d\u7741\u5927\u4e86\u773c\uff0c\u90b9\u9e23\u768b\u4e5f\u89c9\u5927 \u51fa\u610f\u5916\u3002\u90b9\u9521\u4e5d\u5fc3\u5730\u72ed\u7a84\uff0c\u4f46\u5374\u4e5f\u601d\u6028\u5206\u660e\uff0c\u5510\u6653\u6f9c\u521a\u624d\u51fa\u624b\u76f8\u6551\uff0c\u603b\u662f\u5bf9\u81ea\u5df1\u6709\u6069\uff0c\u60f3\u5411 \u4e16\u4f2f\u8bf4\u60c5\uff0c\u53ef\u662f\u89c1\u6768\u4ef2\u82f1\u90a3\u6837\u4e25\u91cd\u7684\u9762\u8272\uff0c\u5982\u4f55\u6562\u8bf4\uff0c\u53ea\u597d\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u54b3\uff0c\u6599\u4e0d\u5230\u4ed6\u4f1a\u8fd9 \u6837\uff01\u201d \u3000\u3000\u7384\u98ce\u5251\u62d0\u9f50\u65bd\uff0c\u62db\u62db\u5389\u5bb3\uff0c\u5510\u6653\u6f9c\u903c\u5f97\u628a\u5929\u5c71\u5251\u6cd5\u65bd\u5c55\u51fa\u6765\uff0c\u94f6\u5149\u56db\u5c04\uff0c\u500f\u5982\u72c2\u6d9b\u5377 \u5730\uff0c\u500f\u5982\u957f\u8679\u7ecf\u5929\uff0c\u4ed6\u5fd7\u5728\u9632\u536b\uff0c\u4e0d\u5728\u8fdb\u653b\uff0c\u7384\u98ce\u7684\u5251\u62d0\u867d\u7136\u5a01\u98ce\uff0c\u7adf\u662f\u65e0\u9699\u53ef\u5165\uff0c\u6c14\u5f97\u5927 \u58f0\u9a82\u9053\uff1a\u201c\u5929\u5c71\u4e00\u6d3e\uff0c\u4ee3\u51fa\u82f1\u96c4\u201d\u4f60\u8fd9\u53ae\u9a97\u4e86\u5929\u5c71\u5251\u6cd5\uff0c\u5374\u505a\u6ee1\u5974\u9e70\u72ac\uff0c\u771f\u662f\u6709\u8fb1\u5148\u4eba\uff01\u201d \u8bdd\u58f0\u672a\u5b8c\uff0c\u94c1\u6247\u5e2e\u7684\u4eba\u5ffd\u7136\u8f70\u52a8\u8d77\u6765\uff0c\u5c1a\u590d\u521d\u624b\u6325\u94c1\u6247\uff0c\u4e00\u8dc3\u800c\u51fa\uff0c\u6247\u5b50\u5728\u4e24\u4eba\u5f53\u4e2d\u4e00\u683c\uff0c \u706b\u661f\u84ec\u98de\uff0c\u5510\u6653\u6f9c\u8d81\u52bf\u9000\u4e86\u4e24\u6b65\u3002\u5c1a\u590d\u521d\u5927\u53eb\u9053\uff1a\u201c\u7384\u98ce\u9053\u5144\uff0c\u6b64\u4eba\u662f\u655d\u5e2e\u4ec7\u4eba\uff0c\u8bf7\u4f60\u8ba9\u7ed9 \u6211\u5427\uff01\u201d\u7384\u98ce\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u8981\u8ba9\u7ed9\u4f60\uff01\u201d\u5c1a\u590d\u521d\u9053\uff1a\u201c\u4f60\u8fdc\u6765\u662f\u5ba2\uff0c\u4f60\u5c31\u662f\u548c\u4ed6\u6709\u6df1\u4ec7\u5927 \u6068\uff0c\u4e5f\u8be5\u8ba9\u6211\u4eec\u505a\u4e3b\u4eba\u7684\u66ff\u4f60\u4ee3\u52b3\u3002\u201d\u8fd9\u65f6\u5b5f\u6b66\u529f\u3001\u536b\u626c\u5a01\u548c\u5e2e\u4f17\u7eb7\u7eb7\u8dc3\u51fa\uff0c\u56f4\u5728\u5916\u5708\uff0c\u7384 \u98ce\u77aa\u773c\u6643\u5251\uff0c\u4ecd\u662f\u4e0d\u80af\u9000\u4e0b\u3002\u5c1a\u590d\u521d\u9053\uff1a\u201c\u6211\u5c06\u8fd9\u53ae\u64d2\u4e86\uff0c\u518d\u4ea4\u4f60\u5904\u7f6e\u5982\u4f55\uff1f\u201d\u7384\u98ce\u89c1\u6b64\u60c5 \u5f62\uff0c\u63d2\u5251\u5f52\u9798\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u53ae\u5251\u672f\u5df2\u5f97\u5929\u5c71\u5fc3\u6cd5\uff0c\u4f60\u5c0f\u5fc3\u4e86\uff01\u201d\u5c1a\u590d\u521d\u94c1\u6247\u4e00\u6325\uff0c\u5510\u6653 \u6f9c\u9053\uff1a\u201c\u6211\u548c\u5e2e\u4e3b\u7d20\u5473\u5e73\u751f\uff0c\u548c\u8d35\u5e2e\u4ea6\u4ece\u65e0\u6765\u5f80\uff0c\u7ed3\u4ec7\u4e00\u4e8b\u4ece\u4f55\u8bf4\u8d77\uff1f\u201d\u5c1a\u590d\u521d\u9053\uff1a\u201c\u4f60\u867d \u662f\u540e\u751f\u5c0f\u8f88\uff0c\u4f46\u65e2\u662f\u5929\u5c71\u95e8\u4e0b\uff0c\u96be\u9053\u5bf9\u4ee5\u524d\u7684\u4e8b\uff0c\u4e00\u70b9\u4e5f\u4e0d\u77e5\u4e48\uff1f\u6211\u4eec\u7684\u59cb\u7956\u4e94\u5341\u5e74\u524d\u88ab\u5929 \u5c71\u4e03\u5251\u6240\u5bb3\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u95e8\u7956\u5f00\u5e2e\uff0c\u7956\u5e08\u7237\u679c\u7136\u663e\u7075\uff0c\u795e\u5dee\u9b3c\u4f7f\uff0c\u5c06\u4f60\u9001\u6765\uff0c\u563f\uff0c\u524d\u4eba\u79cd\u679c \u540e\u4eba\u6536\uff0c\u524d\u4eba\u8840\u503a\u540e\u4eba\u507f\uff0c\u4f60\u8fd8\u60f3\u9003\u547d\u4e48\uff1f\u201d\u5e2e\u4f17\u8f70\u7136\u547c\u559d\uff0c\u5450\u558a\u52a9\u5a01\u3002\u770b\u770b\u5c31\u8981\u52a8\u624b\uff0c\u7518 \u51e4\u6c60\u5ffd\u7136\u8dc3\u51fa\uff0c\u820c\u7efd\u6625\u96f7\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5582\uff0c\u62a5\u4ec7\u4e5f\u4e0d\u662f\u8fd9\u6837\u62a5\u6cd5\uff0c\u4f60\u4eec\u60f3\u7fa4\u6bb4\u4e48\uff1f\u201d \u3000\u3000\u7518\u51e4\u6c60\u9762\u9ec4\u808c\u7626\uff0c\u8c8c\u4e0d\u60ca\u4eba\uff0c\u8fd9\u4e00\u559d\u5374\u5982\u6d2a\u949f\u5de8\u96f7\uff0c\u9707\u5f97\u6ee1\u56ed\u5b50\u55e1\u55e1\u4f5c\u54cd\u3002\u94c1\u6247\u5e2e\u4eba\u4e0d \u89c9\u80c6\u5bd2\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u811a\u6b65\u79fb\u540e\u3002\u5c1a\u590d\u521d\u94c1\u6247\u4e00\u4e3e\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6211\u5fd8\u4e86\u8fd8\u6709\u4e24\u4f4d\u9ad8\u4eba \u5728\u6b64\uff0c\u4e24\u4f4d\u65e2\u548c\u6c60\u96c1\u884c\u5e76\u5217\uff0c\u662f\u4e0d\u662f\u4e5f\u60f3\u66ff\u4ed6\u5206\u62c5\u7f6a\u8d23\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u662f\u5929\u5c71\u95e8 \u4e0b\uff0c\u8c01\u7ba1\u4f60\u7684\u95f2\u4e8b\uff0c\u4f46\u6c5f\u6e56\u9053\u4e0a\uff0c\u62a5\u4ec7\u4e5f\u6709\u89c4\u77e9\uff0c\u6211\u5c31\u770b\u4e0d\u60ef\u4ee5\u591a\u4e3a\u80dc\uff0c\u4ed7\u52bf\u6b3a\u4eba\uff01\u201d\u5c1a\u590d \u521d\u9762\u7ea2\u8033\u8d64\uff0c\u672c\u6765\u4ed6\u4ee5\u4e00\u5e2e\u4e4b\u4e3b\u7684\u8eab\u4efd\uff0c\u6311\u6218\u540e\u751f\u5c0f\u8f88\uff0c\u5df2\u6709\u5931\u8eab\u4efd\uff0c\u4f55\u51b5\u53c8\u7ed9\u7518\u51e4\u6c60\u8bf4\u4ed6 \u7fa4\u6bb4\uff0c\u5f53\u4e0b\u8fa9\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u7741\u5f00\u773c\u775b\u770b\u770b\uff0c\u8c01\u7fa4\u6bb4\u6765\u4e86\u3002\u655e\u5e2e\u5e2e\u4f17\uff0c\u75db\u5fc3\u7956\u5e08\u7684\u8840\u4ec7\uff0c\u4e00\u65f6 \u6fc0\u52a8\uff0c\u8981\u4e0a\u6765\u770b\u6e05\u4ec7\u4eba\u9762\u76ee\uff0c\u6709\u54ea\u70b9\u4e71\u4e86\u89c4\u77e9\uff1f\u4e00\u9897\u841d\u535c\u4e00\u5934\u8471\uff0c\u4ed6\u662f\u5929\u5c71\u4e03\u5251\u7684\u540e\u4ee3\u95e8 \u4eba\uff0c\u6211\u662f\u94c1\u6247\u5e2e\u59cb\u7956\u7684\u5ae1\u5b59\uff0c\u4eca\u65e5\u7531\u6211\u548c\u4ed6\u4f5c\u4e2a\u4e86\u7ed3\u3002\u6211\u4eec\u94c1\u6247\u5e2e\u7edd\u4e0d\u4ee5\u591a\u4e3a\u80dc\uff0c\u4f46\u82e5\u4e8c\u4f4d \u8981\u4e3a\u53cb\u52a9\u62f3\uff0c\u6211\u4eec\u4e5f\u5b9a\u6709\u4eba\u627f\u63a5\u3002\u201d\u5c1a\u590d\u521d\u672c\u6765\u60f3\u53eb\u4ed6\u7684\u513f\u5b50\u548c\u5510\u6653\u6f9c\u52a8\u624b\uff0c\u8fd9\u624d\u5408\u4e4e\u8eab \u4efd\u3002\u53ef\u662f\u4ed6\u89c1\u4e86\u5510\u6653\u6f9c\u5251\u6cd5\uff0c\u77e5\u9053\u81ea\u5df1\u513f\u5b50\u8fdc\u975e\u5176\u654c\uff0c\u6240\u4ee5\u903c\u5f97\u8001\u7740\u9762\u76ae\uff0c\u81ea\u5df1\u51fa\u9a6c\u3002\u7518\u51e4 \u6c60\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u672c\u6765\u5411\u540e\u4ee3\u62a5\u4ec7\uff0c\u4e43\u662f\u6c5f\u6e56\u5e2e\u4f1a\u7684\u964b\u4e60\uff0c\u8fd9\u79cd\u964b\u4e60\uff0c\u4eca\u65e5\u4ea6\u5df2\u6e10\u8d8b\u6d88 \u706d\u3002\u4f60\u4ee5\u4e00\u5e2e\u4e4b\u4e3b\uff0c\u5982\u6b64\u884c\u4e8b\uff0c\u672c\u6765\u4e0d\u8db3\u670d\u4eba\u3002\u4f46\u4f60\u8981\u5982\u6b64\uff0c\u6211\u4e5f\u7531\u5f97\u4f60\u53bb\u3002\u53ea\u662f\u54b1\u4eec\u53ef\u8981 \u8a00\u660e\uff0c\u4f60\u82e5\u8f93\u4e86\u5462\uff1f\u201d\u5c1a\u590d\u521d\u9053\uff1a\u201c\u94c1\u6247\u5e2e\u518d\u4e0d\u4e0e\u4ed6\u4e3a\u96be\uff01\u6211\u82e5\u8d62\u4e86\u5462\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8ba9 \u4ed6\u7531\u4f60\u5904\u7f6e\uff01\u201d\u7384\u98ce\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u4eba\u4e43\u662f\u94c1\u638c\u795e\u5f39\u7684\u95e8\u4e0b\u53db\u5f92\uff0c\u94c1\u6247\u5e2e\u7684\u8f93\u8d62\u6211\u4eec\u4e0d \u7ba1\uff01\u201d\u7518\u51e4\u6c60\u5fae\u5fae\u4e00\u7b11\uff0c\u5411\u7384\u98ce\u9065\u9065\u62f1\u624b\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u201d\u5c1a\u590d\u521d\u53c8\u628a\u94c1\u6247\u4e00\u6325\uff0c\u5e2e\u4f17\u5168 \u90e8\u9000\u4e0b\uff0c\u5b5f\u6b66\u529f\u548c\u536b\u626c\u5a01\u4e24\u4e2a\u9ad8\u624b\uff0c\u628a\u8eab\u5b50\u79fb\u8fd1\u7518\u51e4\u6c60\u8eab\u8fb9\uff0c\u5e2e\u4f17\u4e5f\u5728\u56db\u56f4\u76d1\u89c6\uff0c\u7518\u51e4\u6c60\u795e \u8272\u81ea\u5982\uff0c\u5927\u676f\u559d\u9152\uff01 \u3000\u3000\u90a3\u8fb9\u5e2d\u4e0a\u7384\u98ce\u548c\u6768\u4ef2\u82f1\u90fd\u5728\u6697\u6697\u7eb3\u7f55\uff0c\u770b\u8fd9\u7518\u51e4\u6c60\u8c8c\u4e0d\u60ca\u4eba\uff0c\u8bf4\u8bdd\u5374\u5982\u6b64\u5389\u5bb3\uff01\u800c\u4e14\u542c \u4ed6\u53e3\u6c14\uff0c\u8eab\u4efd\u7edd\u975e\u5bfb\u5e38\u3002\u56db\u4fa0\u548c\u6768\u4ef2\u82f1\u4e0e\u7518\u51e4\u6c60\u90fd\u672a\u89c1\u8fc7\uff0c\u8c01\u4e5f\u731c\u4e0d\u5230\u662f\u4ed6\u3002\u5927\u5bb6\u5728\u5fc3\u91cc\u6697 \u6697\u63e3\u5ea6\uff0c\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5510\u6653\u6f9c\u6562\u53db\u5e08\u4f5c\u6076\uff0c\u539f\u6765\u80cc\u540e\u6709\u4eba\u7ed9\u4ed6\u6491\u8170\u3002\u201d\u7384\u98ce\u76b1\u4e86\u7709 \u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u770b\u6765\uff0c\u6df1\u660e\u6c5f\u6e56\u9053\u4e49\uff0c\u4e43\u662f\u6211\u8f88\u4e2d\u4eba\u3002\u201d\u6768\u4ef2\u82f1\u4e5f\u76b1\u7709\u9053\uff1a\u201c\u5982\u6b64\u9ad8\u4eba\u600e \u4e48\u4f1a\u52a9\u8fd9\u4e2a\u5b7d\u5f92\u3002\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u573a\u4e2d\u5510\u5c1a\u4e8c\u4eba\uff0c\u5df2\u52a8\u4e86\u624b\u3002 \u3000\u3000\u5c1a\u590d\u521d\u6df1\u77e5\u5929\u5c71\u5251\u6cd5\u5389\u5bb3\uff0c\u4f46\u770b\u5510\u6653\u6f9c\u5982\u6b64\u5e74\u8f7b\uff0c\u6599\u4ed6\u529f\u529b\u4e0d\u9ad8\uff0c\u706b\u5019\u672a\u591f\uff0c\u4e00\u51fa\u624b\u5c31 \u865a\u5b9e\u5e76\u7528\uff0c\u8d70\u504f\u950b\uff0c\u7529\u8155\u5b50\uff0c\u6247\u631f\u52b2\u98ce\uff0c\u5411\u5510\u6653\u6f9c\u9762\u95e8\u4e00\u6643\uff0c\u500f\u5730\u659c\u659c\u4e00\u6307\uff0c\u8fc5\u5982\u7535\u5149\u6d41 \u706b\uff0c\u5954\u4ed6\u53f3\u201c\u80a9\u4e95\u7a74\u201d\u6253\u6765\u3002\u5510\u6653\u6f9c\u5251\u628a\u4e00\u6296\uff0c\u5251\u950b\u8d77\u5904\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u7a81\u7136\u5212\u4e86\u534a\u4e2a\u5f27\u5f62\uff0c \u628a\u4e0a\u76d8\u4e2d\u76d8\u5168\u90e8\u62a4\u4f4f\uff0c\u5251\u950b\u53cd\u524a\uff0c\u8fd9\u4e00\u62db\u662f\u5929\u5c71\u5251\u7684\u8d77\u624b\u5f0f\uff0c\u540d\u53eb\u201c\u4e91\u9501\u5929\u5c71\u201d\uff0c\u8f6c\u773c\u95f4\u4fbf \u5316\u6210\u4e86\u7b2c\u4e8c\u62db\u201c\u8f9f\u4e91\u671b\u6708\u201d\uff0c\u6e38\u9f99\u5251\u6a2a\u91cc\u4e00\u626b\uff0c\u8d81\u7740\u626b\u8361\u4e4b\u52bf\uff0c\u5251\u5c16\u7a81\u7136\u81ea\u4e0b\u53cd\u5f39\u800c\u4e0a\uff0c\u4e0a \u523a\u654c\u4eba\u9762\u95e8\u3002\u5c1a\u590d\u521d\u89c1\u4ed6\u5251\u6cd5\u679c\u7136\u5389\u5bb3\uff0c\u6025\u5fd9\u5411\u4e0b\u4e00\u4e2a\u6251\u8eab\uff0c\u500f\u5730\u4e00\u4e2a\u76d8\u65cb\uff0c\u6247\u5b50\u4e00\u5f20\uff0c\u5f53 \u6210\u4e94\u884c\u5251\u4f7f\uff0c\u4e00\u62db\u201c\u5e73\u6c99\u843d\u96c1\u201d\uff0c\u524a\u4ed6\u624b\u8155\uff0c\u8fd9\u4e00\u62db\u7528\u5f97\u751a\u4e3a\u8001\u7ec3\uff0c\u5510\u6653\u6f9c\u6025\u628a\u5251\u950b\u4e00\u8f6c\uff0c \u786c\u5c01\u51fa\u53bb\u3002\u5c1a\u590d\u521d\u8fd9\u4e00\u62db\u82e5\u7528\u5b9e\u4e86\uff0c\u5510\u6653\u6f9c\u5fc5\u5b9a\u53d7\u4f24\uff0c\u4f46\u5c1a\u590d\u521d\u6015\u4ed6\u7684\u6e38\u9f99\u5b9d\u5251\u524a\u94c1\u5982\u6ce5\uff0c \u4e0d\u6562\u653e\u5c3d\uff0c\u4ee5\u7559\u56de\u65cb\u4e4b\u5730\uff0c\u56e0\u6b64\u5510\u6653\u6f9c\u6a2a\u5251\u5916\u5c01\uff0c\u4ed6\u7684\u94c1\u6247\u5df2\u5f80\u540e\u64a4\uff0c\u4e24\u4eba\u4ff1\u65e0\u4f24\u635f\u3002\u5510\u6653 \u6f9c\u62e9\u5251\u518d\u4e0a\uff0c\u53c8\u662f\u4e00\u62db\u8ffd\u98ce\u5251\u6cd5\u91cc\u7684\u7edd\u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\uff0c\u5251\u950b\u582a\u582a\u523a\u5230\u654c\u624b\u8109\u95e8\uff0c\u5c1a\u590d\u521d\u5408 \u6247\u4e00\u70b9\uff0c\u6247\u5934\u5728\u5251\u8eab\u8f7b\u8f7b\u4e00\u78b0\uff0c\u5510\u6653\u6f9c\u5251\u70b9\u7a0d\u6b6a\uff0c\u5c1a\u590d\u521d\u53c8\u5df2\u817e\u8eab\u63a0\u8d77\uff0c\u5f85\u5f97\u5510\u6653\u6f9c\u8ffd\u4e0a\u4e4b \u65f6\uff0c\u4ed6\u5df2\u7ecf\u8f7b\u98d8\u98d8\u7684\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4e24\u4eba\u8d8a\u6597\u8d8a\u70c8\uff0c\u9669\u62db\u4e92\u89c1\u3002\u5510\u6653\u6f9c\u80dc\u5728\u5251\u6cd5\u7cbe\u5947\uff0c\u4f7f\u7684\u4e43\u662f\u5b9d\u5251\uff1b\u5c1a\u590d\u521d\u80dc\u5728\u529f\u529b\u8f83 \u9ad8\uff0c\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u4e24\u4eba\u6597\u4e86\u4e00\u767e\u4f59\u62db\uff0c\u5c1a\u590d\u521d\u7684\u4e00\u63fd\u94c1\u6247\uff0c\u500f\u5f53\u4e94\u884c\u5251\u7528\uff0c\u500f\u5f53\u70b9\u7a74\u6289\u4f7f\uff0c\u53d8 \u5316\u591a\u7aef\uff0c\u865a\u5b9e\u5e76\u7528\u3002\u5510\u6653\u6f9c\u4e0d\u7406\u4ed6\u7684\u8bf1\u62db\uff0c\u53ea\u662f\u5c55\u5f00\u4e86\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u201c\u987b\u5f25\u5251\u6cd5\u201d\u9632\u8eab\uff0c\u5076 \u800c\u4e5f\u7a81\u4ee5\u8ffd\u98ce\u5251\u6cd5\u7684\u7edd\u62db\u53cd\u51fb\u3002\u5c1a\u590d\u521d\u867d\u7136\u8001\u7ec3\uff0c\u53ef\u662f\u5510\u6653\u6f9c\u7684\u5251\u6cd5\u65e0\u7f1d\u53ef\u51fb\uff0c\u53ea\u597d\u903c\u5f97\u548c \u4ed6\u6e38\u6597\u3002\u94c1\u6247\u5e2e\u5e2e\u4f17\u770b\u5f97\u60ca\u5fc3\u9a87\u76ee\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u540e\u751f\u5c0f\u5b50\u5c45\u7136\u80fd\u548c\u4ed6\u4eec\u89c6\u82e5\u795e\u660e\u7684\u5e2e\u4e3b\u6597\u5f97 \u96be\u89e3\u96be\u5206\u3002 \u3000\u3000\u5415\u56db\u5a18\u5168\u795e\u89c2\u6218\uff0c\u628a\u5510\u6653\u6f9c\u7684\u5929\u5c71\u5251\u6cd5\u548c\u81ea\u5df1\u7684\u7384\u5973\u5251\u6cd5\u7ec6\u7ec6\u6bd4\u8f83\uff0c\u89c9\u5f97\u81ea\u5df1\u7684\u7384\u5973\u5251 \u6cd5\u867d\u7136\u5965\u5999\u7cbe\u5947\uff0c\u4f46\u82e5\u8bba\u5230\u6c89\u96c4\u7a33\u6377\uff0c\u517c\u6709\u5404\u5bb6\u4e4b\u957f\uff0c\u90a3\u5c31\u6bd4\u4e0d\u4e0a\u5929\u5c71\u5251\u6cd5\u4e86\u3002\u8fd9\u6837\u4e00\u770b\uff0c \u6e10\u609f\u5251\u7406\uff0c\u5929\u5c71\u5251\u6cd5\u80dc\u5728\u201c\u535a\u201d\u7384\u5973\u5251\u6cd5\u80dc\u5728\u201c\u4e13\u201d\uff0c\u5510\u6653\u6f9c\u662f\u535a\u800c\u4e0d\u4e13\uff0c\u81ea\u5df1\u662f\u4e13\u800c\u4e0d \u535a\uff0c\u5fc3\u4e2d\u5ffd\u8d77\u5f02\u60f3\uff0c\u8981\u628a\u5929\u5c71\u5251\u6cd5\u4e0e\u7384\u5973\u5251\u6cd5\u7194\u4e8e\u4e00\u7089\uff0c\u4f46\u4e00\u6765\u6613\u5170\u73e0\u65ad\u4e0d\u80fd\u6536\u81ea\u5df1\u4e3a\u5f92\uff0c \u5510\u6653\u6f9c\u672a\u591f\u7cbe\u7eaf\uff0c\u548c\u4ed6\u5207\u78cb\uff0c\u4e5f\u609f\u4e0d\u4e86\u5929\u5c71\u5251\u6cd5\u7684\u795e\u9ad3\u3002\u800c\u4e14\u81ea\u5df1\u56fd\u4ec7\u5bb6\u6068\uff0c\u6574\u65e5\u52b3\u5fc3\uff0c\u53c8 \u90a3\u80fd\u6709\u51e0\u5341\u5e74\u7684\u529f\u592b\uff0c\u7ed9\u81ea\u5df1\u6f5c\u5fc3\u4fee\u7ec3\uff0c\u601d\u5ff5\u53ca\u6b64\uff0c\u4e0d\u7981\u54d1\u7136\u5931\u7b11\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u516b\u59b9\uff0c\u6b66 \u5b66\u7cbe\u6df1\uff0c\u6709\u5982\u5927\u6d77\u3002\u6211\u4ee5\u524d\u521d\u89c1\u6653\u6f9c\uff0c\u89c1\u4ed6\u529f\u529b\u4e0d\u9ad8\uff0c\u5c1a\u4ee5\u4e3a\u4ed6\u662f\u5e73\u51e1\u4e4b\u8f88\u3002\u60f3\u4e0d\u5230\u4ed6\u5c45\u7136 \u80fd\u4ee5\u5251\u6cd5\u8865\u529f\u529b\u4e4b\u4e0d\u8db3\uff0c\u53ef\u77e5\u6b66\u5b66\u4e4b\u9053\uff0c\u4e0d\u8bba\u90a3\u65b9\u5165\u624b\uff0c\u53ea\u8981\u80fd\u6709\u4e13\u957f\uff0c\u4fbf\u53ef\u671b\u6210\u5927\u5668\u3002\u201d \u5415\u56db\u5a18\u9053\uff1a\u201c\u6587\u6b66\u4e4b\u7406\u76f8\u901a\uff0c\u529f\u529b\u6709\u5982\u5b66\u8bc6\uff1b\u79ef\u5b66\u4e4b\u58eb\uff0c\u80dc\u4e8e\u5f92\u8bb2\u6587\u7b14\u7ae0\u6cd5\u4e4b\u4eba\u3002\u201d\u7518\u51e4\u6c60 \u542c\u4e86\uff0c\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u539f\u6765\u7518\u51e4\u6c60\u5728\u540c\u95e8\u4e4b\u4e2d\uff0c\u9664\u4e86\u4e86\u56e0\u548c\u5c1a\u4e4b\u5916\uff0c\u4ee5\u4ed6\u6839\u57fa\u624e\u5f97\u6700\u7a33\uff0c\u4f46\u82e5\u8bba\u5230\u5251\u6cd5\u548c\u8f7b \u529f\uff0c\u5219\u4ee5\u5415\u56db\u5a18\u6700\u5999\u3002\u7518\u51e4\u6c60\u77e5\u9053\u5e08\u59b9\u6b64\u8a00\uff0c\u6697\u4e2d\u4e43\u662f\u6367\u4ed6\u3002\u56e0\u6b64\u7b11\u9053\uff1a\u201c\u82e5\u7136\u9971\u8bfb\u7ecf\u4e66\uff0c \u5374\u65e0\u5999\u7b14\u4ee5\u4f20\uff0c\u90a3\u5b66\u8bc6\u4e5f\u65e0\u7531\u8868\u73b0\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u5e08\u5144\u6240\u8a00\u751a\u662f\uff0c\u5e08\u5144\u5f97\u5e08\u5085\u4e4b\u62f3\uff0c\u6211 \u5f97\u5e08\u5085\u4e4b\u5251\uff0c\u6211\u4eec\u6b63\u5e94\u5f7c\u6b64\u5207\u78cb\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5510\u6653\u6f9c\u548c\u5c1a\u590d\u521d\u6076\u6597\uff0c\u4ecd\u662f\u96be\u5206\u9ad8\u4e0b\uff0c\u5510\u6653\u6f9c\u7684\u5251\u70b9\uff0c\u6bcf\u88ab\u5c1a\u590d\u521d\u7684\u6247\u98ce\u6247\u6b6a\uff0c\u4f46 \u5c1a\u590d\u521d\u4e5f\u53ea\u80fd\u4ed7\u7740\u529f\u529b\u89e3\u5371\uff0c\u653b\u4e0d\u8fdb\u53bb\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u6b64\u4eba\u529f\u529b\u4e0d\u5728\u4e94\u5e08\u5144\u4e4b\u4e0b\u3002\u201d\u7518\u51e4\u6c60 \u7b11\u9053\uff1a\u201c\u82e5\u7136\u78b0\u5728\u4f60\u6211\u624b\u4e0a\uff0c\u4ed6\u5927\u7ea6\u53ef\u654c\u4e94\u4e03\u5341\u62db\u3002\u201d\u4e24\u4eba\u6597\u573a\u8bba\u6b66\uff0c\u65c1\u82e5\u65e0\u4eba\uff0c\u628a\u5b5f\u6b66\u529f \u548c\u536b\u626c\u5a01\u542c\u5f97\u6697\u6697\u5fc3\u60ca\u3002\u536b\u626c\u5a01\u548c\u5415\u56db\u5a18\u66fe\u5728\u7530\u6a2a\u5c9b\u4e0a\u4f1a\u8fc7\uff0c\u4ed6\u770b\u4e86\u53c8\u770b\uff0c\u89c9\u8fd9\u5c11\u5e74\u795e\u60c5\u7b11 \u8c8c\uff0c\u5374\u4f3c\u5728\u90a3\u513f\u89c1\u8fc7\u4e86\u7684\uff0c\u5ffd\u7136\u60f3\u8d77\u4e86\u5415\u56db\u5a18\u6765\uff0c\u4e0d\u7981\u5927\u60ca\uff0c\u4f46\u60f3\u7eb5\u7b97\u5415\u56db\u5a18\u80fd\u5973\u626e\u7537\u88c5\uff0c \u4e5f\u51b3\u65e0\u5982\u6b64\u5e74\u8f7b\u4e4b\u7406\uff0c\u8fd9\u4e2a\u5c11\u5e74\u4e0d\u8fc7\u662f\u4e8c\u5341\u5c81\u5de6\u53f3\u7684\u5927\u5b69\u5b50\u7f62\u4e86\uff0c\u4f46\u53e3\u6c14\u504f\u53c8\u5982\u6b64\u4e4b\u5927\uff0c\u9976 \u4ed6\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u4e5f\u81ea\u731c\u7591\u4e0d\u5b9a\u3002 \u3000\u3000\u8fd9\u65f6\u573a\u4e2d\u6597\u5f97\u66f4\u6025\uff0c\u90a3\u8fb9\u5e2d\u4e0a\uff0c\u7384\u98ce\u7ad9\u4e86\u8d77\u6765\uff0c\u5ffd\u7136\u773c\u671d\u5916\u671b\uff0c\u9762\u8272\u6709\u5f02\uff0c\u7518\u51e4\u6c60\u5ffd\u5fd9 \u968f\u7740\u7384\u98ce\u773c\u5149\u6ce8\u89c6\u4e4b\u5904\u770b\u53bb\uff0c\u8fd9\u4e00\u770b\u5927\u51fa\u610f\u5916\uff0c\u53ea\u89c1\u97e9\u91cd\u5c71\u548c\u8463\u5de8\u5ddd\u4e8c\u4eba\uff0c\u6b63\u6392\u5f00\u5e2e\u4f17\u8d70 \u8fdb\uff0c\u8fd9\u8fd8\u7f62\u4e86\uff0c\u97e9\u8463\u4e8c\u4eba\u4e4b\u4e2d\uff0c\u8fd8\u6709\u4e00\u4eba\uff0c\u5de6\u81c2\u88ab\u97e9\u91cd\u5c71\u62d6\u7740\uff0c\u53f3\u81c2\u88ab\u8463\u5de8\u5ddd\u62d6\u7740\uff0c\u660e\u660e\u662f \u88ab\u631f\u6301\u800c\u884c\uff0c\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u5728\u72ec\u81c2\u795e\u5c3c\u95e8\u4e0b\uff0c\u6392\u884c\u7b2c\u4e94\u7684\u767d\u6cf0\u5b98\uff01 \u3000\u3000\u97e9\u91cd\u5c71\u548c\u8463\u5de8\u5ddd\u90fd\u662f\u5949\u56db\u7687\u5b50\u4e4b\u547d\uff0c\u6765\u53c2\u52a0\u94c1\u6247\u5e2e\u7684\u5f00\u5e2e\u5927\u5178\u7684\uff0c\u97e9\u91cd\u5c71\u53e6\u6709\u79c1\u4e8b\uff0c\u6240 \u4ee5\u5728\u90a3\u665a\u4e0a\u8fde\u591c\u4ece\u5c1a\u5bb6\u8d70\u51fa\uff0c\u4eca\u65e5\u59cb\u56de\uff1b\u8463\u5de8\u5ddd\u5219\u5148\u5230\u6d59\u629a\u674e\u536b\u7684\u8859\u4e2d\u4f5c\u5ba2\uff0c\u4f1a\u9f50\u4e86\u97e9\u91cd\u5c71 \u4e4b\u540e\u624d\u6765\u3002\u4e8c\u4eba\u6b63\u8fdb\u5165\u5c71\u53e3\uff0c\u5c31\u78b0\u89c1\u4e86\u767d\u6cf0\u5b98\u8e3d\u8e3d\u72ec\u884c\uff0c\u4e24\u4eba\u6b66\u529f\u90fd\u5728\u767d\u6cf0\u5b98\u4e4b\u4e0a\uff0c\u53c8\u662f\u5408 \u529b\u51fa\u51fb\u7684\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u5c31\u628a\u767d\u6cf0\u5b98\u64d2\u4e86\u3002\u4e0d\u8fc7\u4ed6\u4eec\u77e5\u9053\u767d\u6cf0\u5b98\u662f\u9c7c\u4eae\u5973\u5a7f\uff0c\u867d\u7136\u4f20\u6709\u5a5a\u53d8\uff0c \u5374\u4e0d\u77e5\u5e95\u7ec6\u5982\u4f55\uff1b\u66f4\u517c\u767d\u6cf0\u5b98\u53c8\u662f\u4e86\u56e0\u5e08\u5f1f\uff0c\u867d\u7136\u4ed6\u4eec\u5144\u5f1f\u4e5f\u662f\u4e0d\u548c\uff0c\u4f46\u97e9\u8463\u4e8c\u4eba\u56e0\u6709\u6b64\u4e24 \u91cd\u5173\u7cfb\uff0c\u5230\u5e95\u4e0d\u6562\u628a\u767d\u6cf0\u5b98\u96be\u4e3a\uff0c\u53ea\u662f\u7528\u64d2\u62ff\u624b\u6cd5\uff0c\u6263\u7740\u4ed6\u7684\u8109\u95e8\uff0c\u5c06\u4ed6\u62d6\u5165\u5e84\u5185\u3002 \u3000\u3000\u4e24\u4eba\u8d70\u5165\u5c71\u5e84\uff0c\u53ea\u89c1\u4eba\u5934\u7c07\u62e5\uff0c\u4e00\u95ee\u4e4b\u4e0b\uff0c\u59cb\u77e5\u662f\u5e2e\u4e3b\u548c\u4eba\u6bd4\u6b66\uff0c\u4e0d\u7981\u5927\u5947\uff0c\u6025\u5fd9\u6392\u5f00 \u5e2e\u4f17\u8d70\u8fdb\u3002\u5173\u4e1c\u56db\u4fa0\u548c\u6768\u4ef2\u82f1\u662f\u66fe\u7ecf\u5728\u56db\u7687\u5b50\u5e9c\u4e2d\u548c\u97e9\u91cd\u5c71\u6076\u6597\u8fc7\u4e00\u573a\u7684\uff0c\u8fd9\u65f6\u89c1\u4e86\uff0c\u6012\u4ece \u5fc3\u8d77\u3002\u7384\u98ce\u9053\uff1a\u201c\u8fd9\u94c1\u6247\u5e2e\u4e00\u5b9a\u662f\u548c\u5141\u796f\u6709\u5173\u7cfb\u7684\uff0c\u54b1\u4eec\u4eca\u65e5\u8fd8\u8981\u5927\u5f00\u6740\u6212\u3002\u201d\u6768\u4ef2\u82f1\u9053\uff1a \u201c\u4f60\u4eec\u56db\u4f4d\u53bb\u654c\u90a3\u4e24\u4e2a\u9b54\u5934\uff0c\u6211\u53bb\u53d6\u90a3\u5b7d\u5f92\uff01\u201d \u3000\u3000\u97e9\u91cd\u5c71\u884c\u8fd1\u6597\u573a\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5c1a\u5e2e\u4e3b\u4f60\u4e3a\u4ec0\u4e48\u548c\u4e00\u4e2a\u5c0f\u8f88\u6bd4\u5f0f\uff1f\u201d\u8bdd\u58f0\u672a\u505c\uff0c\u5173\u4e1c\u56db \u4fa0\u5df2\u7ecf\u4ece\u5e2e\u4f17\u5934\u4e0a\u98de\u63a0\u51fa\u6765\uff01\u97e9\u91cd\u5c71\u5927\u5403\u4e00\u60ca\uff0c\u5728\u4e0a\u9996\u7684\u4e00\u684c\u9152\u5e2d\u4e0a\uff0c\u53c8\u6709\u4e00\u6761\u4eba\u5f71\u51cc\u7a7a\u98de \u8d77\uff01\u201d \u3000\u3000\u8fd9\u6761\u51cc\u7a7a\u98de\u8d77\u7684\u4eba\u5f71\u6b63\u662f\u5415\u56db\u5a18\u3002\u536b\u626c\u5a01\u559d\u9053\uff1a\u201c\u4f60\u4eec\u505a\u4ec0\u4e48\uff1f\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7518 \u51e4\u6c60\u5df2\u547c\u7684\u4e00\u638c\u5288\u6765\uff01\u536b\u626c\u5a01\u53cc\u81c2\u4e00\u683c\u3002\u7ed9\u4ed6\u9707\u9000\u4e24\u6b65\uff0c\u5415\u56db\u5a18\u8f7b\u529f\u8d85\u7edd\uff0c\u5728\u638c\u98ce\u4eba\u5f71\u4e2d\uff0c \u5df2\u7fe9\u5982\u5927\u96c1\u822c\u7684\u98de\u51fa\u53bb\u4e86\uff01 \u3000\u3000\u5173\u4e1c\u56db\u4fa0\u5148\u51fa\uff0c\u4f46\u5415\u56db\u5a18\u5374\u662f\u540e\u53d1\u5148\u81f3\uff0c\u5979\u4eba\u5728\u534a\u7a7a\uff0c\u5df3\u628a\u971c\u534e\u5b9d\u5251\uff0c\u62d4\u4e86\u51fa\u6765\uff0c\u5c31\u5728 \u7a7a\u4e2d\u821e\u8d77\u4e00\u6735\u5251\u82b1\uff0c\u4e00\u62db\u201c\u767d\u733f\u51fb\u679d\u201d\uff0c\u5411\u97e9\u91cd\u5c71\u5f53\u5934\u523a\u4e0b\uff0c\u97e9\u91cd\u5c71\u662f\u5929\u53f6\u6563\u4eba\u5e08\u5144\uff0c\u6b66\u529f \u6781\u9ad8\uff0c\u6025\u5fd9\u5c06\u6263\u767d\u6cf0\u5b98\u8109\u95e8\u7684\u624b\u638c\u4e00\u677e\uff0c\u8eab\u5f62\u5e73\u5730\u62d4\u8d77\uff0c\u5c45\u7136\u786c\u62a2\u5415\u56db\u5a18\u624b\u4e2d\u7684\u5b9d\u5251\u3002\u4f46\u4ed6 \u6599\u4e0d\u5230\u5415\u56db\u5a18\u8f7b\u529f\u5df2\u5230\u51fa\u795e\u5165\u5316\u4e4b\u5883\uff0c\u4ed6\u7684\u64d2\u62ff\u624b\u6cd5\u521a\u4f7f\u5f97\u534a\u62db\uff0c\u6307\u5934\u521a\u5f39\u7740\u5415\u56db\u5a18\u7684\u5251 \u8eab\uff0c\u6b63\u60f3\u62d6\u5979\u4e00\u540c\u843d\u4e0b\uff0c\u5415\u56db\u5a18\u5df2\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u5012\u7ffb\uff0c\u4ecd\u662f\u90a3\u62db\u201c\u767d\u733f\u51fb\u679d\u201d\uff0c\u5251\u52bf\u5982\u8679\uff0c \u671d\u7740\u4ed6\u7684\u8111\u95e8\u76f4\u523a\u4e0b\u6765\u3002 \u3000\u3000\u8463\u5de8\u5ddd\u6b66\u529f\u8f83\u4f4e\uff0c\u4e0d\u6562\u50cf\u97e9\u91cd\u5c71\u90a3\u6837\u786c\u62a2\u5b9d\u5251\uff0c\u903c\u5f97\u4e5f\u5c06\u6263\u7740\u767d\u6cf0\u5b98\u8109\u95e8\u7684\u624b\u638c\u677e\u5f00\uff0c \u95ea\u8eab\u5de6\u53f3\u53d1\u638c\u3002\u767d\u6cf0\u5b98\u8131\u51fa\u4e86\u8eab\uff0c\u624b\u638c\u8f6c\u4e86\u51e0\u8f6c\uff0c\u8212\u7b4b\u6d3b\u7edc\uff0c\u97e9\u91cd\u5c71\u8eab\u5f62\u843d\u5730\uff0c\u8f9f\u4e91\u9504\u5411\u4ed6 \u65ab\u53bb\u3002\u5173\u4e1c\u56db\u4fa0\u5df2\u5230\uff0c\u7384\u98ce\u9053\u4eba\u94c1\u62d0\u66b4\u4f38\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u683c\u5728\u8f9f\u4e91\u9504\u4e0a\uff0c\u706b\u661f\u98de\u7206\uff0c\u53f3\u624b \u957f\u5251\u4e5f\u5237\u7684\u523a\u6765\uff0c\u97e9\u91cd\u5c71\u5c06\u9504\u4e00\u8f6c\uff0c\u75be\u7684\u821e\u8d77\u4e00\u4e2a\u5706\u5708\uff0c\u5c06\u7384\u98ce\u7684\u62d0\u5251\uff0c\u4e00\u9f50\u8361\u5f00\u3002\u6717\u6708\u7985 \u5e08\u5f20\u53e3\u4e00\u55b7\uff0c\u9152\u5982\u767d\u7ec3\uff0c\u7a7a\u4e2d\u98de\u81f3\uff0c\u7ed9\u97e9\u91cd\u5c71\u8f9f\u4e91\u9504\u6fc0\u8d77\u7684\u52b2\u98ce\u4e00\u903c\uff0c\u7eb7\u6d12\u4e0b\u6765\uff0c\u6709\u5982\u9aa4 \u96e8\u3002\u97e9\u91cd\u5c71\u4e5f\u5fae\u5fae\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\u8fd9\u4eba\u5185\u529f\u4e0d\u9519\u3002\u7384\u98ce\u5251\u5c16\u6643\u52a8\uff0c\u5237\u7684\u53c8\u662f\u4e00\u5251\u523a\u6765\uff0c\u97e9\u91cd \u5c71\u5c06\u9504\u4e00\u62a1\uff0c\u7384\u98ce\u7684\u5251\u7a81\u7136\u6539\u4e86\u65b9\u5411\uff0c\u4e00\u62ab\u4e00\u65a9\uff0c\u987a\u7740\u9504\u52bf\uff0c\u8d34\u9504\u4e0b\u65a9\u53cc\u8db3\u3002\u97e9\u91cd\u5c71\u4e00\u58f0\u5927 \u543c\uff0c\u5de6\u638c\u98de\u51fa\uff0c\u9000\u540e\u534a\u6b65\uff0c\u7384\u98ce\u7684\u5251\u518d\u6b21\u7ed9\u638c\u98ce\u8361\u5f00\uff0c\u94c1\u62d0\u51fb\u53bb\uff0c\u53c8\u7ed9\u9504\u5934\u622a\u7740\u3002\u6717\u6708\u7985\u5e08 \u8d76\u4e0a\uff0c\u518d\u55b7\u9152\u6d6a\uff0c\u97e9\u91cd\u5c71\u4e0d\u6562\u8fdb\u62db\uff0c\u903c\u5f97\u5c06\u8f9f\u4e91\u9504\u821e\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u4f3a\u9699\u53cd\u51fb\u3002 \u3000\u3000\u90a3\u8fb9\u53a2\u5415\u56db\u5a18\u4e00\u8fde\u51e0\u5251\uff0c\u5c06\u8463\u5de8\u5ddd\u903c\u9000\uff0c\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u516b\u59b9\uff0c\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\uff0c\u5148\u6551\u9c7c \u5a18\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u5251\u52bf\u4e00\u677e\uff0c\u8463\u5de8\u5ddd\u64a4\u638c\u540e\u9000\uff0c\u67f3\u5148\u5f00\u811a\u5c16\u70b9\u5730\uff0c\u8eab\u5f62\u9aa4\u8d77\uff0c\u5c48 \u7740\u5341\u6307\uff0c\u7a81\u7136\u4ece\u7a7a\u6251\u51fb\u3002\u9664\u4e86\u6613\u5170\u73e0\u7b49\u51e0\u4e2a\u8001\u524d\u8f88\u5916\uff0c\u67f3\u5148\u5f00\u7684\u8f7b\u529f\u4ec5\u6b21\u4e8e\u5415\u56db\u5a18\uff0c\u4ed6\u624b\u6307 \u5957\u7740\u94a2\u73af\uff0c\u51fa\u5176\u4e0d\u610f\uff0c\u5728\u8463\u5de8\u5ddd\u5934\u9876\u51ff\u4e86\u4e00\u4e0b\uff0c\u8463\u5de8\u5ddd\u75db\u5f97\u6ee1\u5929\u661f\u6597\uff0c\u4e0d\u8fa8\u897f\u4e1c\uff0c\u6025\u5fd9\u65bd\u5c55 \u4e00\u62db\u201c\u4e91\u624b\u201d\uff0c\u659c\u63a8\u51fa\u53bb\u3002\u67f3\u5148\u5f00\u5728\u5730\u4e0a\u7684\u529f\u592b\u4e0d\u9ad8\uff0c\u7ed9\u4ed6\u4e00\u5806\uff0c\u51e0\u4e4e\u8dcc\u5012\u3002\u9648\u5143\u9738\u5927\u543c\u4e00 \u58f0\uff0c\u8fd0\u201c\u5927\u6454\u7891\u624b\u201d\uff0c\u4e00\u638c\u5288\u51fa\uff0c\u638c\u98ce\u547c\u547c\uff0c\u8463\u5de8\u5ddd\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u6562\u786c\u63a5\u3002 \u3000\u3000\u9648\u5143\u9738\u7684\u5916\u5bb6\u529f\u592b\u767b\u5cf0\u9053\u6781\uff0c\u4f46\u5185\u529f\u9020\u8be3\uff0c\u5374\u8fd8\u672a\u7aa5\u95e8\u5f84\uff0c\u8fdc\u4e0d\u53ca\u8463\u5de8\u5ddd\u3002\u53ef\u662f\u8463\u5de8\u5ddd \u4e0d\u77e5\u4ed6\u7684\u6df1\u6d45\uff0c\u89c1\u4ed6\u5a01\u52bf\u60ca\u4eba\uff0c\u4e0d\u514d\u6697\u5177\u6212\u5fc3\uff0c\u53ea\u6562\u65bd\u5c55\u5f62\u610f\u6d3e\u7684\u62ff\u624b\u529f\u592b\uff0c\u8eab\u5982\u98de\u7d6e\uff0c\u7ed5 \u7740\u9648\u5143\u9738\u7684\u8eab\u5f62\u75be\u8f6c\u3002\u67f3\u5148\u5f00\u518d\u5ea6\u98de\u6251\uff0c\u7ed9\u4ed6\u95ea\u5f00\uff0c\u9648\u5143\u9738\u8d81\u52bf\u4e00\u638c\u6253\u53bb\uff0c\u7ed9\u4ed6\u53cc\u638c\u4e00\u5faa\uff0c \u51e0\u4e4e\u4ec6\u5012\uff0c\u5bb0\u5f97\u67f3\u5148\u5f00\u4e09\u5ea6\u98de\u6765\uff0c\u94a2\u73af\u731b\u6251\uff0c\u8463\u5de8\u5ddd\u8981\u56de\u8eab\u95ea\u8eb2\uff0c\u9648\u5143\u9738\u8fd9\u624d\u7a33\u4f4f\u8eab\u5f62\uff0c\u6325 \u62f3\u518d\u6597\u3002 \u3000\u3000\u5415\u56db\u5a18\u4e0e\u767d\u6cf0\u5b98\u89c1\u56db\u4fa0\u5206\u6597\u654c\u4eba\uff0c\u4e00\u65f6\u96be\u5206\u80dc\u8d1f\uff0c\u8fd9\u65f6\u573a\u4e2d\u4e71\u6210\u4e00\u7247\uff0c\u7518\u51e4\u6c60\u6076\u6597\u536b\u626c \u5a01\u548c\u5b5f\u6b66\u529f\uff0c\u628a\u9152\u5e2d\u6253\u5f97\u7a00\u70c2\u3002\u5415\u56db\u5a18\u6325\u52a8\u5ba4\u5251\uff0c\u5f00\u8def\u5165\u573a\uff0c\u767d\u6cf0\u5b98\u4e5f\u62a2\u4e86\u4e00\u53e3\u5355\u5200\uff0c\u968f\u540e \u6740\u4eba\u3002\u5415\u56db\u5a18\u5fd9\u4e2d\u95ee\u9053\uff1a\u201c\u9c7c\u5a18\u600e\u4e48\u4e86\uff1f\u201d\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u9c7c\u5a18\u9003\u4e86\u51fa\u6765\uff0c\u8981\u6765\u627e\u6211\uff0c\u9884\u5148\u6258 \u4eba\u62a5\u8baf\uff0c\u6cbf\u9014\u8fd8\u7559\u4e0b\u6697\u8bb0\uff0c\u6211\u8ffd\u8e2a\u800c\u6765\uff0c\u770b\u6765\u5979\u662f\u88ab\u94c1\u6247\u5e2e\u56da\u4e86\u3002\u201d\u5415\u56db\u59a8\u5ffd\u9053\uff1a\u201c\u597d\uff0c\u4f60 \u53bb\u52a9\u7518\u5e08\u5144\uff0c\u6211\u66ff\u4f60\u628a\u9c7c\u5a18\u627e\u6765\uff01\u201d\u767d\u6cf0\u5b98\u559c\u9053\uff1a\u201c\u4f60\u77e5\u5979\u7684\u4e0b\u843d\uff1f\u201d\u5415\u56db\u5a18\u6765\u4e0d\u53ca\u56de\u7b54\uff0c \u5982\u98de\u7684\u5411\u897f\u5317\u65b9\u6740\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u7518\u51e4\u6c60\u529b\u654c\u4e24\u540d\u9ad8\u624b\uff0c\u65d7\u9f13\u76f8\u5f53\uff0c\u5ffd\u89c1\u6768\u4ef2\u82f1\u5954\u51fa\u573a\u5fc3\uff0c\u5411\u5510\u6653\u6f9c\u8fdb\u51fb\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u89c1 \u767d\u6cf0\u5b98\u5954\u6765\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u4f60\u66ff\u6211\u6682\u6321\u4e00\u9635\u3002\u201d\u865a\u6643\u4e00\u62db\uff0c\u6251\u5411\u573a\u5fc3\uff0c\u8fd9\u65f6\u573a\u4e2d\u5f62\u6210\u6df7\u6218\uff0c \u5404\u5e2e\u9996\u9886\u548c\u94c1\u6247\u5e2e\u4eba\uff0c\u89c1\u5bbe\u5ba2\u4e92\u6597\uff0c\u800c\u5bbe\u5ba2\u4e2d\u53c8\u6709\u4eba\u66ff\u5e2e\u4e3b\u52a9\u62f3\uff0c\u5f04\u5f97\u83ab\u540d\u5176\u5999\u3002\u4e00\u65f6\u4e0d\u6562 \u52a8\u624b\u3002 \u3000\u3000\u5c1a\u590d\u521d\u548c\u5510\u6653\u6f9c\u6076\u6597\uff0c\u6b63\u611f\u9a91\u864e\u96be\u4e0b\uff0c\u6768\u949f\u82f1\u5ffd\u6765\u76f8\u52a9\uff0c\u559c\u51fa\u671b\u5916\uff0c\u94c1\u6247\u70b9\u3001\u6253\u3001\u6572\u3001 \u524a\uff0c\u8d8a\u53d1\u7cbe\u795e\u3002\u6768\u949f\u82f1\u559d\u9053\uff1a\u201c\u4f60\u9000\u4e0b\uff01\u201d\u4f38\u638c\u6765\u6293\u6653\u6f9c\uff0c\u5c1a\u590d\u521d\u6115\u7136\u6536\u62db\u3002\u5510\u6653\u6f9c\u4e0d\u6562\u548c \u4ee5\u524d\u7684\u4e1a\u5e08\u5bf9\u654c\uff0c\u95ea\u5f97\u4e24\u95ea\uff0c\u5fc3\u91cc\u53d1\u614c\uff0c\u6768\u4ef2\u82f1\u559d\u9053\uff1a\u201c\u5b7d\u5f92\u4f60\u8fd8\u6562\u62d2\u6355\uff1f\u201d\u4e00\u638c\u671d\u4ed6\u9876\u95e8 \u62cd\u4e0b\uff0c\u5510\u6653\u6f9c\u773c\u4e2d\u542b\u6cea\uff0c\u95ed\u76ee\u5f85\u6b7b\uff0c\u7518\u51e4\u6c60\u6070\u597d\u8d76\u5230\uff0c\u4f38\u81c2\u4e00\u683c\uff0c\u6768\u4ef2\u82f1\u4e00\u638c\u5982\u51fb\u94a2\u94c1\uff0c\u7adf \u7ed9\u9707\u9000\u6570\u6b65\u3002\u6768\u4ef2\u82f1\u53f7\u79f0\u94c1\u638c\u795e\u5f39\uff0c\u4e0d\u6599\u5728\u638c\u529b\u4e0a\u7adf\u7136\u7ed9\u4eba\u8f83\u77ed\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u6025\u5fd9\u559d \u9053\uff1a\u201c\u5c0a\u9a7e\u4e3a\u4f55\u52a9\u8fd9\u4e0d\u4e49\u4e4b\u4eba\uff1f\u201d\u7518\u51e4\u6c60\u6258\u5730\u8df3\u4e0a\u524d\u53bb\uff0c\u4e3e\u624b\u5728\u8138\u4e0a\u4e00\u62b9\uff0c\u5c06\u9762\u4e0a\u6240\u6577\u7684\u836f \u5242\u62b9\u6389\uff0c\u53cc\u7738\u7cbe\u5149\u95ea\u95ea\uff0c\u987f\u65f6\u6362\u4e86\u4e00\u4eba\uff0c\u6768\u4ef2\u82f1\u76ee\u77aa\u53e3\u5446\uff0c\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u6768\u8001\u524d\u8f88\uff0c\u4f60\u6211 \u4e45\u5df2\u95fb\u540d\uff0c\u4e0d\u56fe\u4eca\u65e5\u59cb\u5f97\u67cf\u89c1\uff01\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6c5f\u5357\u7518\u51e4\u6c60\uff01\u201d \u6768\u4ef2\u82f1\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u6025\u5fd9\u65bd\u793c\uff0c\u7518\u51e4\u6c60\u4e5f\u662f\u6025\u5fd9\u8fd8\u793c\u3002 \u3000\u3000\u7518\u51e4\u6c60\u51fa\u9053\u4ee5\u6765\uff0c\u884c\u4fa0\u4ed7\u4e49\uff0c\u6d4e\u6025\u6276\u5371\uff0c\u7aef\u7684\u662f\u540d\u52a8\u6c5f\u6e56\uff0c\u9ed1\u9053\u767d\u9053\uff0c\u65e0\u4e0d\u4f69\u670d\u3002\u6768\u5fe1 \u82f1\u5728\u6b66\u6797\u4e2d\u4e5f\u662f\u5fb7\u9ad8\u671b\u91cd\u4e4b\u4eba\uff0c\u53ea\u662f\u6768\u4ef2\u82f1\u5bb6\u5c45\u7684\u65f6\u65e5\u591a\uff0c\u800c\u7518\u51e4\u6c60\u5219\u56db\u6d77\u98d8\u6e38\uff0c\u968f\u5904\u884c \u4fa0\uff0c\u6240\u4ee5\u58f0\u540d\u66f4\u5927\uff0c\u4e24\u4eba\u60fa\u60fa\u76f8\u60dc\uff0c\u5f7c\u6b64\u65bd\u793c\u3002\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u6562\u95ee\u7518\u5927\u4fa0\u4e3a\u4f55\u5e87\u62a4\u5b7d\u5f92\uff1f\u201d \u8fd9\u65f6\u5c1a\u590d\u521d\u53c8\u5df2\u5728\u548c\u5510\u6653\u6f9c\u76f8\u6597\uff0c\u542c\u5f97\u6765\u4eba\u5c31\u662f\u6c5f\u5357\u5927\u4fa0\uff0c\u4e5f\u4e0d\u7981\u5fc3\u91cc\u53d1\u614c\uff01 \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4ee4\u5f92\u6709\u7edd\u5927\u7684\u82e6\u8877\uff0c\u6709\u7edd\u79d8\u7684\u9690\u60c5\uff0c\u4ed6\u7edd\u4e0d\u662f\u53db\u5e08\u80cc\u4e49\u4e4b\u4eba\uff01\u201d\u7518\u51e4\u6c60\u4e00 \u8fde\u7528\u4e86\u4e09\u4e2a\u201c\u7edd\u201d\u5b57\uff0c\u6768\u4ef2\u82f1\u4e0d\u89c9\u52a8\u5bb9\uff0c\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8bf7\u4f60\u770b\u5728\u7518\u67d0\u9762\u4e0a\uff0c\u4e0d\u8981\u518d\u903c\u4ee4\u5f92\uff0c \u8be6\u60c5\u6211\u4ee5\u540e\u81ea\u5f53\u5949\u544a\uff01\u201d\u6768\u4ef2\u82f1\u6168\u7136\uff0c\u8bf4\u9053\uff1a\u201c\u7518\u5927\u4fa0\u4e00\u8a00\u4e5d\u9f0e\uff0c\u65e2\u7136\u5982\u6b64\u8bf4\u6cd5\uff0c\u60f3\u662f\u6211\u9519 \u602a\u4e86\u5c0f\u5f92\uff01\u201d\u8fd9\u65f6\u767d\u6cf0\u5b98\u548c\u5b5f\u536b\u4e8c\u4eba\u6597\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u4e0d\u652f\u8d25\u9000\uff0c\u5954\u5165\u573a\u5fc3\uff0c\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6768 \u8001\u524d\u8f88\u8bf7\u52a9\u6211\u5e08\u5144\u4e00\u81c2\u4e4b\u529b\u3002\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u7406\u6240\u5f53\u7136\uff01\u201d\u98de\u5954\u800c\u4e0a\uff0c\u8fce\u7740\u5b5f\u6b66\u529f\u5c31\u662f\u4e00\u638c\u3002 \u3000\u3000\u7518\u51e4\u6c60\u4e00\u8dc3\u800c\u524d\uff0c\u53eb\u9053\uff1a\u201c\u5510\u8d24\u5f1f\uff0c\u4f8d\u6211\u53d6\u4ed6\uff01\u201d\u5c1a\u590d\u521d\u614c\u4e86\u624b\u811a\uff0c\u6025\u5fd9\u8df3\u51fa\u6838\u5fc3\uff0c\u5927 \u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u52a8\u624b\uff0c\u66ff\u6211\u628a\u8fd9\u51e0\u4e2a\u4eba\u64d2\u4e0b\u6765\uff01\u201d\u94c1\u6247\u5e2e\u7684\u4eba\u7eb7\u7eb7\u56f4\u4e0a\uff0c\u7518\u51e4\u6c60\u594b\u8eab\u4e00 \u8dc3\uff0c\u8df3\u4e0a\u4e86\u4e00\u5757\u5047\u5c71\u77f3\u4e0a\uff0c\u632f\u81c2\u559d\u9053\uff1a\u201c\u5c1a\u590d\u521d\u4f9d\u9644\u9c7c\u4eae\uff0c\u4f5c\u6e05\u5ef7\u85e9\u5e10\uff0c\u66ff\u5141\u796f\u56fe\u8c0b\u6c5f\u5c71\uff0c \u66f4\u8981\u628a\u94c1\u6247\u5e2e\u9677\u4e8e\u4e0d\u4e49\u4e4b\u5730\uff0c\u4eca\u65e5\u649e\u5728\u6211\u4eec\u6c5f\u5357\u4e03\u4fa0\u624b\u4e0a\uff0c\u7edd\u4e0d\u80fd\u53eb\u4ed6\u9634\u8c0b\u5f97\u901e\uff0c\u4f60\u4eec\u542c\u660e \u767d\u4e86\uff0c\u4f11\u5f97\u4e3a\u864e\u4f5c\u6005\uff01\u201d\u5404\u5e2e\u7684\u8425\u9886\u6709\u8ba4\u5f97\u7518\u51e4\u6c60\u7684\uff0c\u7eb7\u7eb7\u544a\u8bc9\u540c\u4f34\uff1a\u201c\u8fd9\u662f\u6c5f\u5357\u5927\u4fa0\uff01\u201d \u53c8\u6709\u4eba\u9053\uff1a\u201c\u4e0d\u77e5\u4e03\u4fa0\u662f\u4e0d\u662f\u90fd\u6765\u4e86\uff1f\u201d\u6709\u4eba\u5c31\u6307\u70b9\u9053\uff1a\u201c\u4f60\u770b\u767d\u6cf0\u5b98\u4e5f\u5728\u90a3\u8fb9\u3002\u201d\u5404\u5e2e\u9996 \u9886\u4e4b\u6765\u8d74\u5bb4\uff0c\u4e00\u534a\u662f\u5356\u5c1a\u590d\u521d\u7956\u5148\u7684\u9762\u5b50\uff0c\u4e00\u534a\u662f\u6151\u4e8e\u9c7c\u4eae\u4e4b\u52bf\uff0c\u542c\u5f97\u7518\u51e4\u6c60\u5982\u6b64\u8bf4\u6cd5\uff0c\u4ed6 \u4eec\u5b81\u613f\u5f97\u7f6a\u9c7c\u4eae\uff0c\u4e5f\u4e0d\u6562\u5f97\u7f6a\u6c5f\u5357\u4e03\u4fa0\u3002\u6709\u4e9b\u80c6\u5c0f\u7684\u5df2\u5e26\u4e86\u968f\u4ece\u79bb\u573a\uff0c\u94c1\u6247\u5e2e\u7684\u4eba\u6709\u4e00\u5927\u534a \u4e0d\u6562\u52a8\u624b\uff0c\u6709\u4e00\u5c0f\u534a\u56f4\u4e0a\u53bb\uff0c\u88ab\u5510\u6653\u6f9c\u821e\u52a8\u5b9d\u5251\uff0c\u6740\u5f97\u4f24\u624b\u6298\u8db3\uff0c\u5934\u7834\u8840\u6d41\uff0c\u5175\u5203\u7eb7\u7eb7\u622a \u65ad\u3002\u90b9\u9e23\u768b\u7236\u5b50\u4e5f\u90fd\u62d4\u51fa\u5175\u5203\uff0c\u6740\u5165\u91cd\u56f4\uff0c\u52a9\u5510\u6653\u6f9c\u5fa1\u654c\u3002 \u3000\u3000\u53e6\u4e00\u9762\u5415\u56db\u5a18\u5c55\u5f00\u7edd\u9876\u8f7b\u529f\uff0c\u5154\u8d77\u9e43\u843d\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u5df2\u5230\u4e86\u56ed\u5b50\u897f\u89d2\u3002\u90a3\u5ea7\u4e09\u5c42\u697c\u5b87\u77d7 \u7acb\u9762\u524d\uff0c\u5c4b\u4e2d\u8d70\u51fa\u4e24\u4e2a\u5973\u90ce\uff0c\u6b63\u662f\u90a3\u665a\u6240\u89c1\u7684\u56db\u4e2a\u5973\u90ce\u4e2d\u4e4b\u4e8c\u3002\u5979\u4eec\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u4e0a\u524d\u95ee \u9053\uff1a\u201c\u8c01\u53eb\u4f60\u6765\u7684\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u542c\u8bf4\u90e1\u4e3b\u8eab\u4f53\u4e0d\u9002\uff0c\u5e2e\u4e3b\u53eb\u6211\u9001\u836f\u6765\u3002\u201d\u4e24\u4e2a\u5973\u90ce\u9762\u73b0 \u60ca\u5947\u4e4b\u8272\uff0c\u540c\u58f0\u8bf4\u9053\uff1a\u201c\u8c01\u8bf4\u5979\u75c5\u4e86\uff1f\u5979\u597d\u7aef\u7aef\u7684\uff0c\u521a\u624d\u8fd8\u6709\u8bf4\u6709\u7b11\u5462\uff01\u201d\u5415\u56db\u5a18\u51fa\u8a00\u8bd5 \u63a2\uff0c\u8bc1\u5b9e\u4e86\u9c7c\u5a18\u5c31\u5728\u4e0a\u8fb9\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u66f4\u4e0d\u6253\u8bdd\uff0c\u62d4\u51fa\u5b9d\u5251\uff0c\u7eb5\u8eab\u4e00\u8dc3\uff0c\u8df3\u4e0a\u4e8c\u697c\u6a90\u89d2\uff0c\u628a \u5251\u4e00\u70b9\uff0c\u8eab\u5b50\u76f4\u5f39\u4e0a\u53bb\uff0c\u5230\u4e86\u4e09\u697c\uff0c\u521a\u521a\u8df3\u4e0b\uff0c\u5ffd\u7136\u6a2a\u523a\u91cc\u4e00\u5251\u98de\u6765\uff0c\u5415\u56db\u5a18\u5c06\u5251\u4e00\u5f15\uff0c\u90a3 \u4eba\u5251\u5c16\u5411\u524d\u4e00\u63a2\uff0c\u5c45\u7136\u89e3\u4e86\u8fd9\u62db\uff0c\u5415\u56db\u5a18\u7acb\u7a33\u811a\u6b65\uff0c\u770b\u6e05\u524d\u9762\u7684\u4eba\uff0c\u7adf\u7136\u662f\u4e2a\u767d\u53d1\u6ee1\u5934\u7684\u8001 \u5a46\u5a46\u3002 \u3000\u3000\u5415\u56db\u5a18\u9053\uff1a\u201c\u4f60\u8d70\u5f00\uff0c\u6211\u4e0d\u4f24\u4f60\uff01\u201d\u90a3\u8001\u5a46\u5a46\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u540e\u751f\u665a\u8f88\uff0c\u5c45\u7136\u6562\u6765\u7aa5\u63a2 \u90e1\u4e3b\uff01\u201d\u6402\u5934\u4e00\u5251\u5288\u4e0b\uff0c\u5415\u56db\u5a18\u7565\u4e00\u6643\u80a9\uff0c\u8eab\u5f62\u4f3c\u7bad\u822c\u4ece\u5251\u5e95\u7a7f\u8fc7\uff0c\u8fdb\u5165\u623f\u4e2d\uff0c\u53ea\u89c1\u4e00\u4e2a\u5c11 \u5973\u8eba\u5728\u5e8a\u4e0a\uff0c\u6b63\u662f\u9c7c\u5a18\uff01\u5415\u56db\u5a18\u9053\uff1a\u201c\u767d\u6cf0\u5b98\u5728\u5916\u9762\u7b49\u4f60\uff01\u201d\u9c7c\u5a18\u4e00\u8dc3\u800c\u8d77\uff0c\u5ffd\u53c8\u8fdf\u7591\u9053\uff1a \u201c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u5415\u56db\u5a18\u6b63\u60f3\u7b54\u8bdd\uff0c\u90a3\u8001\u5a46\u5a46\u5df2\u6325\u5251\u6251\u8fdb\uff01 \u3000\u3000\u9c7c\u5a18\u53eb\u9053\uff1a\u201c\u4f60\u8ba9\u6211\u51fa\u53bb\uff01\u201d\u8001\u5a46\u5a46\u9053\uff1a\u201c\u6ca1\u5f97\u4f60\u7236\u4eb2\u51c6\u8bb8\uff0c\u8c01\u4e5f\u4e0d\u80fd\u653e\u4f60\uff01\u201d\u5415\u56db\u5a18 \u51b7\u7b11\u9053\uff1a\u201c\u6211\u89c1\u4f60\u504c\u5927\u5e74\u7eaa\uff0c\u597d\u610f\u529d\u4f60\u8d70\u5f00\uff0c\u4f60\u8fd8\u6562\u62e6\u963b\uff1f\u201d\u8001\u5a46\u5a46\u5927\u6012\uff0c\u4e00\u5251\u523a\u6765\uff0c\u5415\u56db \u5a18\u968f\u7740\u5251\u98ce\u98d8\u6643\uff0c\u90a3\u8001\u5a46\u5a46\u8fde\u523a\u4e86\u5341\u4f59\u5251\uff0c\u7adf\u7136\u6cbe\u4e0d\u7740\u5415\u56db\u5a18\u8863\u88f3\uff0c\u5415\u56db\u5a18\u559d\u9053\uff1a\u201c\u4f60\u8ba9\u4e0d \u8ba9\uff1f\u201d\u4e09\u5c3a\u9752\u950b\uff0c\u4e00\u5708\u4e00\u65cb\uff0c\u201c\u53ee\u5f53\u201d\u4e00\u58f0\uff0c\u90a3\u8001\u5a46\u5a46\u7684\u5251\u51e0\u4e4e\u8131\u624b\u98de\u53bb\uff0c\u81ea\u77e5\u4e0d\u654c\uff0c\u76ef\u4e86 \u56db\u5a18\u4e00\u773c\uff0c\u72e0\u72e0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u8bb0\u7740\u4f60\u8fd9\u5c0f\u5b50\uff01\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4f60\u8bb0\u7740\u597d\u4e86\uff01\u201d\u8eab\u5f62\u4e00\u6643\uff0c \u5251\u5149\u7ed5\u5904\uff0c\u5237\u7684\u53c8\u5954\u8001\u5a46\u5a46\u5de6\u80a9\u523a\u53bb\uff0c\u8001\u5a46\u5a46\u4e0d\u6562\u518d\u8bf4\uff0c\u6321\u4e86\u4e00\u5251\uff0c\u6025\u5fd9\u7a7f\u7a97\u800c\u51fa\u3002 \u3000\u3000\u9c7c\u5a18\u89c1\u5415\u56db\u5a18\u5251\u6cd5\u5982\u6b64\u795e\u5947\uff0c\u7741\u5927\u4e86\u773c\uff0c\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u7684\u597d\u59d1\u5a18\uff0c\u522b\u4eba\u7b49\u7740\u4f60\u5462\uff0c\u8fd8 \u4e0d\u51fa\u53bb\uff1f\u201d\u9c7c\u5a18\u89c1\u5415\u56db\u5a18\u5f00\u5979\u73a9\u7b11\uff0c\u8bf4\u8bdd\u53c8\u4e0d\u6b63\u7ecf\uff0c\u53cd\u800c\u4e0d\u6562\u76f8\u4fe1\uff0c\u4e00\u9635\u8fdf\u7591\uff0c\u5415\u56db\u5a18\u4f38\u624b \u62c9\u5979\uff0c\u9c7c\u5a18\u628a\u8896\u4e00\u6325\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u767d\u6cf0\u5b98\u4e00\u5b9a\u6ca1\u6709\u4f60\u8fd9\u6837\u7684\u670b\u53cb\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4e3a\u4f55 \u6ca1\u6709\uff1f\u201d\u9c7c\u5a18\u9053\uff1a\u201c\u4f60\u65e2\u7136\u77e5\u6211\u662f\u767d\u6cf0\u5b98\u4f55\u4eba\uff0c\u521d\u521d\u89c1\u9762\uff0c\u4e3a\u4f55\u5982\u6b64\u76f8\u620f\uff1f\u201d\u53e4\u65f6\u7537\u5973\u6388\u53d7 \u4e0d\u4eb2\uff0c\u6c5f\u6e56\u513f\u5973\u867d\u7136\u6bd4\u8f83\u8131\u7565\uff0c\u4f46\u82e5\u975e\u637b\u719f\uff0c\u4e5f\u4e0d\u4f1a\u4f38\u624b\u76f8\u6276\u3002\u5415\u56db\u5a18\u8fd9\u624d\u8bb0\u8d77\u81ea\u5df1\u4e43\u662f\u5973 \u626e\u7537\u88c5\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u628a\u5e3d\u5b50\u9664\u6389\uff0c\u9732\u51fa\u6ee1\u5934\u9752\u4e1d\uff0c\u9c7c\u5a18\u53c8\u60ca\u53c8\u559c\uff0c\u604d\u7136\u5927\u609f\uff0c\u6251\u4e0a\u524d\u53bb\uff0c\u62b1 \u7740\u5415\u56db\u5a18\u9053\uff1a\u201c\u597d\u59d0\u59d0\uff0c\u4f60\u4e00\u5b9a\u662f\u767d\u6cf0\u5b98\u7684\u5e08\u59b9\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u4e0d\u9a82\u6211\u4e86\u5427\uff1f\u201d \u9c7c\u5a18\u9053\uff1a\u201c\u6211\u771f\u60f3\u4e0d\u5230\u59d0\u59d0\u8fd9\u6837\u5e74\u9752\uff01\u201d\u643a\u7740\u5415\u56db\u5a18\u7684\u624b\uff0c\u53cc\u53cc\u4e0b\u697c\u3002 \u3000\u3000\u8fd9\u65f6\u573a\u4e2d\u6df7\u6218\u751a\u70c8\uff0c\u6768\u4ef2\u82f1\u548c\u767d\u6cf0\u5b98\u5e76\u80a9\u529b\u6218\uff0c\u548c\u5b5f\u6b66\u529f\u536b\u626c\u5a01\u4e8c\u4eba\u6070\u6070\u6253\u6210\u5e73\u624b\u3002\u90b9 \u5bb6\u7236\u5b50\u548c\u5510\u6653\u6f9c\u5c06\u5e2e\u4f17\u6740\u5f97\u56db\u6563\u5954\u9003\uff0c\u7518\u51e4\u6c60\u548c\u5c1a\u590d\u521d\u4e5f\u4ea4\u4e0a\u4e86\u624b\uff0c\u5c1a\u590d\u521d\u6325\u6247\u529b\u6218\uff0c\u5df2\u7ed9 \u4ed6\u903c\u5230\u5047\u5c71\u80cc\u540e\uff0c\u6709\u4e24\u540d\u526f\u5e2e\u4e3b\u8d76\u6765\u76f8\u52a9\uff0c\u8fd8\u662f\u843d\u5728\u4e0b\u98ce\u3002 \u3000\u3000\u5415\u56db\u5a18\u6e38\u76ee\u56db\u987e\uff0c\u89c1\u5173\u4e1c\u56db\u4fa0\u5904\u5728\u4e0b\u98ce\uff0c\u5bf9\u9c7c\u5a18\u9053\uff1a\u201c\u4f60\u53bb\u52a9\u767d\u6cf0\u5b98\uff0c\u6211\u52a9\u5173\u4e1c\u56db \u4fa0\u3002\u201d\u9c7c\u5a18\u81ea\u7136\u613f\u610f\uff0c\u62d4\u51fa\u67f3\u53f6\u5200\u8dd1\u4e0a\u524d\u53bb\uff0c\u767d\u6cf0\u5b98\u4e00\u89c1\u9c7c\u5a18\uff0c\u7cbe\u795e\u9661\u632f\uff0c\u8fb9\u6253\u8fb9\u53eb\u9053\uff1a \u201c\u9c7c\u59b9\u59b9\u3002\u201d\u9c7c\u5a18\u8138\u6cdb\u7ea2\u6f6e\uff0c\u52a0\u5165\u6218\u56e2\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u591a\u4eba\uff0c\u4f60\u4e71\u53eb\u4e71\u56b7\u505a\u4ec0\u4e48\u5440\uff1f\u201d \u767d\u6cf0\u5b98\u5fae\u5fae\u4e00\u7b11\uff0c\u6325\u5200\u594b\u6218\uff0c\u5168\u662f\u8fdb\u624b\u7684\u62db\u6570\uff0c\u5b5f\u6b66\u529f\u4e43\u662f\u9c7c\u4eae\u7684\u526f\u624b\uff0c\u89c1\u9c7c\u5a18\u7adf\u52a9\u767d\u6cf0\u5b98 \u4e0e\u81ea\u5df1\u4e3a\u654c\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u4f60\u771f\u7684\u8981\u80cc\u53db\u7236\u4eb2\u4e48\uff1f\u201d\u9c7c\u5a18\u9053\uff1a\u201c\u5b5f\u53d4\u53d4\uff0c\u4f60\u4e0d\u8d70\u5f00\uff0c \u6211\u53ef\u4e0d\u5bb9\u6c14\u4e86\uff0c\u4f60\u56de\u53bb\u5bf9\u6211\u7239\u8bf4\u5427\uff0c\u53eb\u4ed6\u4e0d\u5fc5\u518d\u7406\u6211\u4e86\uff0c\u5c31\u5f53\u6ca1\u6709\u751f\u8fc7\u8fd9\u4e2a\u5973\u513f\u5427\uff01\u201d\u5b5f\u536b \u4e8c\u4eba\u548c\u6768\u767d\u5bf9\u654c\uff0c\u5df2\u611f\u5403\u529b\uff0c\u9c7c\u5a18\u52a0\u5165\uff0c\u81ea\u7136\u66f4\u611f\u4e0d\u652f\uff0c\u800c\u4e14\u9c7c\u5a18\u53c8\u662f\u4e3b\u516c\u7231\u5973\uff0c\u4e07\u4e00\u9519\u624b \u4f24\u5979\uff0c\u66f4\u662f\u4e0d\u597d\uff0c\u4e24\u4eba\u6253\u4e86\u4e00\u4e2a\u62db\u547c\uff0c\u64a4\u62db\u9003\u8dd1\u3002 \u3000\u3000\u5b5f\u536b\u4e00\u9003\uff0c\u5c1a\u590d\u521d\u66f4\u662f\u5fc3\u8c0e\uff0c\u4e5f\u60f3\u9003\u65f6\uff0c\u4f46\u7518\u51e4\u6c60\u62f3\u98ce\u751a\u7d27\uff0c\u62db\u62db\u8f9b\u8fa3\uff0c\u90a3\u91cc\u9003\u5f97\u51fa \u53bb\uff1f\u6218\u4e86\u7247\u523b\uff0c\u4e24\u4e2a\u526f\u5e2e\u4e3b\u5148\u540e\u4e2d\u62f3\u5012\u5730\u3002\u5c1a\u590d\u521d\u7259\u6839\u4e00\u8de4\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211 \u4ea1\uff01\u201d\u7a81\u7136\u7528\u529b\u4e00\u62d7\uff0c\u5c06\u94c1\u6247\u62d7\u65ad\uff0c\u5341\u51e0\u679d\u77ed\u7bad\uff0c\u9aa4\u7684\u5c04\u51fa\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5fd7\u5927\u624d\u758f\uff0c\u5fc3\u52b3\u529b\u7ecc\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u4e03\u56de\u3000\u5251\u6756\u4ea4\u950b\u3000\u51f6\u50e7\u95f9\u6e56\u4e0a\u3000\u6027\u7075\u672a\u6627\u3000\u7389\u5973\u51fa\u4eac\u534e \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u5341\u4e03\u56de \u5251\u6756\u4ea4\u950b\u3000\u51f6\u50e7\u95f9\u6e56\u4e0a \u6027\u7075\u672a\u6627\u3000\u7389\u5973\u51fa\u4eac\u534e \u3000\u3000\u573a\u4e2d\u6076\u6597\u505c\u6b62\uff0c\u94c1\u6247\u5e2e\u7684\u4eba\u7ed9\u7518\u51e4\u6c60\u4e00\u559d\uff0c\u9f50\u90fd\u5782\u624b\u4ef0\u5934\uff0c\u53ea\u542c\u5f97\u7518\u51e4\u6c60\u8bf4\u9053\uff1a\u201c\u4f60\u4eec \u90fd\u662f\u82e6\u54c8\u54c8\u7684\u5f1f\u5144\uff0c\u4e8e\u9ed1\u9053\u4e70\u5356\uff0c\u52ab\u4e0d\u4e49\u4e4b\u8d22\uff0c\u6211\u7518\u67d0\u51b3\u4e0d\u62e6\u963b\uff0c\u4f46\u82e5\u7ed9\u6e05\u5ef7\u5229\u7528\uff0c\u90a3\u6211\u7518 \u67d0\u53ef\u4e0d\u5141\u8bb8\u3002\u4f60\u4eec\u4e0d\u4e4f\u660e\u7406\u4e4b\u4eba\uff0c\u4ed4\u7ec6\u60f3\u60f3\u3002\u201d\u94c1\u6247\u5e2e\u7684\u4eba\uff0c\u4e00\u534a\u6151\u4e8e\u201c\u6c5f\u5357\u5927\u4fa0\u201d\u7684\u58f0 \u5a01\uff0c\u4e00\u534a\u9707\u4e8e\u5927\u52bf\u5df2\u53bb\uff0c\u7eb7\u7eb7\u8bf4\u9053\uff1a\u201c\u542c\u7518\u5927\u4fa0\u5429\u5490\uff01\u201d \u3000\u3000\u7518\u51e4\u6c60\u628a\u811a\u4e00\u63d0\uff0c\u5c1a\u590d\u521d\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u5410\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u4f38\u8170\u5750\u8d77\uff0c\u7518\u51e4\u6c60\u9053\uff1a\u201c\u5c1a\u590d \u521d\u4f60\u867d\u6709\u91ce\u5fc3\uff0c\u5c1a\u65e0\u5927\u6076\uff0c\u4f60\u82e5\u80af\u6539\u90aa\u5f52\u6b63\uff0c\u6211\u4e5f\u53ef\u4ee5\u9976\u4f60\u4e00\u547d\u3002\u201d\u5c1a\u590d\u521d\u8fd9\u65f6\u4f46\u6c42\u9976\u547d\uff0c \u90a3\u8fd8\u6562\u9053\u534a\u4e2a\u201c\u4e0d\u201d\u5b57\u3002 \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f60\u8981\u9976\u547d\uff0c\u7b2c\u4e00\uff0c\u4ee5\u540e\u4e0d\u8bb8\u5728\u6c5f\u6e56\u53ae\u6df7\u3002\u201d\u5c1a\u590d\u521d\u5fd9\u9053\uff1a\u201c\u4f9d\u5f97\uff01\u4f9d\u5f97\uff01 \u6211\u4ece\u4eca\u65e5\u8d77\u5c31\u89e3\u6563\u672c\u5e2e\uff0c\u643a\u5c0f\u513f\u56de\u4e61\u8015\u7530\uff0c\u95ed\u95e8\u5c01\u5200\uff0c\u6d17\u624b\u4e0d\u5e72\uff01\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u7b2c\u4e8c\uff0c\u4f60 \u79ef\u655b\u7684\u94b1\u8d22\uff0c\u90fd\u4ea4\u7ed9\u6211\u5904\u7f6e\uff0c\u4f60\u9664\u8eab\u4e0a\u6240\u6709\u4e4b\u5916\uff0c\u4e0d\u8bb8\u5e26\u4e00\u4e2a\u94b1\u51fa\u95e8\u3002\u201d\u5c1a\u590d\u521d\u5341\u5e74\u79ef\u805a\uff0c \u52ab\u63a0\u6240\u5f97\uff0c\u4f55\u6b62\u767e\u4e07\uff0c\u542c\u7518\u51e4\u6c60\u4e0d\u8bb8\u4ed6\u5e26\u4e00\u4e2a\u94b1\u51fa\u95e8\uff0c\u5341\u5206\u8089\u75bc\uff0c\u4f46\u4e5f\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u8bf4 \u9053\uff1a\u201c\u94b1\u8d22\u8eab\u5916\u4e4b\u7269\uff0c\u7518\u5927\u4fa0\u53d6\u53bb\u4fbf\u662f\uff01\u201d \u3000\u3000\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u8981\u4f60\u7684\u3002\u201d\u53eb\u5c1a\u590d\u521d\u548c\u94c1\u6247\u5e2e\u7ba1\u8d22\u52a1\u7684\u4eba\uff0c\u628a\u73e0\u5b9d\u94b1\u94f6\u90fd\u53d6\u4e86\u51fa \u6765\uff0c\u5c06\u73e0\u5b9d\u7559\u4e0b\uff0c\u628a\u94b1\u94f6\u5206\u7ed9\u5e2e\u4f17\uff0c\u5fd9\u4e86\u5927\u534a\u5929\uff0c\u8fd9\u624d\u5904\u7f6e\u5b8c\u6bd5\uff0c\u7518\u51e4\u6c60\u559d\u9053\uff1a\u201c\u597d\uff0c\u73b0\u5728 \u4f60\u53ef\u4ee5\u8d70\u4e86\uff01\u4eca\u540e\u83ab\u8ba9\u6211\u5728\u6c5f\u6e56\u4e0a\u89c1\u5230\u4f60\uff0c\u6211\u8ba4\u5f97\u4f60\uff0c\u6211\u62f3\u5934\u53ef\u8ba4\u4e0d\u5f97\u4f60\uff01\u201d\u5c1a\u590d\u521d\u677e\u4e86\u53e3 \u6c14\uff0c\u6025\u5fd9\u548c\u513f\u5b50\u62b1\u5934\u9f20\u7a9c\uff0c\u8dd1\u51fa\u5c71\u6751\u3002 \u3000\u3000\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u4eec\u5728\u8fd9\u91cc\u5927\u95f9\u4e00\u573a\uff0c\u53c8\u653e\u4ed6\u4eec\u51fa\u53bb\u3002\u4e0d\u6015\u4ed6\u7ea0\u96c6\u515a\u7fbd\u518d\u6765\uff0c\u6216\u52fe\u7ed3\u5b98\u5175 \u6765\u56f4\u6355\u6211\u4eec\u5417\uff1f\u201d\u7518\u51e4\u4ed6\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u987e\u8651\u3002\u53bf\u57ce\u53d1\u5175\uff0c\u6700\u5c11\u8981\u4e24\u5929\u624d\u5230\uff0c\u9c7c\u4eae\u6d3e\u4eba\uff0c\u90a3\u66f4 \u4e0d\u6613\u3002\u4f55\u51b5\u8fd9\u6751\u5e84\u5728\u7fa4\u5c71\u4e4b\u4e2d\uff0c\u9669\u5cfb\u96be\u884c\u5462\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u4e00\u60f3\uff0c\u679c\u662f\u9053\u7406\u3002\u8bf4\u9053\uff1a\u201c\u4e03\u54e5\u660e\u65ad\uff0c\u786e\u4e3a\u5c0f\u59b9\u6240\u4e0d\u53ca\u3002\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u5c0f\u5fc3 \u987e\u8651\uff0c\u4e5f\u662f\u597d\u7684\u3002\u201d\u8fd9\u65f6\u5929\u8272\u5df2\u9ed1\uff0c\u56ed\u5b50\u91cc\u6811\u679d\u4e0a\u6302\u6ee1\u7684\u78a7\u7eb1\u706f\u7b3c\uff0c\u672c\u6765\u662f\u51c6\u5907\u5f00\u5e2e\u796d\u7956\u7684 \u559c\u5e86\u4e4b\u4e8b\u7528\u7684\uff0c\u8fd9\u65f6\u6b63\u597d\u6d3e\u4e86\u7528\u573a\u3002\u7518\u51e4\u6c60\u5927\u7b11\u9053\uff1a\u201c\u534e\u706f\u591c\u5bb4\uff0c\u8ba9\u6211\u7b49\u4e5f\u4eab\u7528\u4eab\u7528\uff01\u201d\u53eb \u548c\u5c1a\u5bb6\u672a\u8d70\u5c3d\u7684\u53a8\u5b50\u4ec6\u5a62\uff0c\u5f00\u4e86\u4e24\u684c\u9152\u5e2d\u51fa\u6765\uff0c\u73af\u9996\u56db\u987e\uff0c\u5374\u4e0d\u89c1\u767d\u6cf0\u5b98\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4e94 \u54e5\u5462\uff1f\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u9002\u624d\u6211\u89c1\u4ed6\u548c\u9c7c\u5a18\u5728\u5047\u5c71\u77f3\u540e\u9685\u9685\u7ec6\u8bed\uff0c\u60f3\u662f\u4e45\u522b\u91cd\u9022\uff0c\u8fde\u996d\u4e5f\u5fd8 \u8bb0\u5403\u4e86\u3002\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u4f60\u628a\u4ed6\u4eec\u627e\u6765\u3002\u201d\u5415\u56db\u5a18\u5e94\u4e86\u4e00\u58f0\uff0c\u6b63\u60f3\u8d70\u5f00\uff0c\u7518\u51e4\u6c60\u5ffd\u53c8\u7b11 \u9053\uff1a\u201c\u5728\u5c71\u77f3\u4e0a\u7559\u4e0b\u672c\u95e8\u6697\u8bb0\u7684\uff0c\u60f3\u5fc5\u5c31\u662f\u90a3\u4f4d\u9c7c\u5a18\u4e86\u3002\u4e94\u54e5\u4e5f\u771f\u662f\uff0c\u600e\u4e48\u628a\u672c\u95e8\u6697\u8bb0\u544a\u8bc9 \u5916\u4eba\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u9c7c\u5a18\u4e5f\u4e0d\u7b97\u5916\u4eba\u4e86\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u867d\u7136\u4ed6\u4eec\u5df2\u7ed3\u4e3a\u592b\u5987\uff0c\u4f46\u9c7c\u5a18\u4e0d \u662f\u672c\u95e8\u4e2d\u4eba\uff0c\u4e94\u54e5\u6240\u4e3a\uff0c\u603b\u662f\u6b20\u59a5\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5f85\u4ed6\u65e5\u4fbf\u65f6\uff0c\u6211\u529d\u4ed6\u4fbf\u662f\u3002\u201d\u7518\u51e4\u6c60\u70b9 \u4e86\u70b9\u5934\u3002\u539f\u6765\u767d\u6cf0\u5b98\u7d20\u6027\u4e0d\u7f81\uff0c\u5c0f\u8282\u4e0a\u5e38\u5e38\u4e0d\u5927\u6ce8\u610f\u3002\u4f46\u72ec\u81c2\u795e\u5c3c\u867d\u7136\u5728\u5251\u6cd5\u4e0a\u65e9\u5df2\u72ec\u521b\u4e00 \u5bb6\uff0c\u5374\u5e76\u672a\u5f00\u5b97\u7acb\u6d3e\uff0c\u6240\u4ee5\u672a\u8bbe\u638c\u95e8\u3002\u7518\u51e4\u6c60\u662f\u5e08\u5f1f\uff0c\u53c8\u4e0d\u597d\u8bf4\u4ed6\uff0c\u53ea\u597d\u6697\u793a\u5415\u56db\u5a18\u53bb\u8bf4\u3002 \u5415\u56db\u5a18\u65e2\u662f\u540d\u5112\u4e4b\u5973\uff0c\u6b66\u529f\u53c8\u6781\u9ad8\u5f3a\uff0c\u6027\u60c5\u4e5f\u548c\u987a\u8fd1\u4eba\uff0c\u767d\u6cf0\u5b98\u5bf9\u5979\u5012\u66f4\u4e3a\u4eb2\u8fd1\u3002 \u3000\u3000\u7518\u51e4\u6c60\u5fd9\u4e86\u4e00\u65e5\uff0c\u8fd9\u65f6\u65b9\u5f97\u7a7a\u95f2\uff0c\u548c\u5173\u4e1c\u56db\u4fa0\u3001\u63d2\u7ffc\u795e\u72ee\u7b49\uff0c\u4f9d\u6b21\u89c1\u4e86\uff0c\u4e92\u9053\u4ef0\u6155\u4e4b \u60c5\u3002\u4ed6\u89c1\u5510\u6653\u6f9c\u968f\u4f8d\u5728\u6768\u4ef2\u82f1\u8eab\u4fa7\uff0c\u4fbf\u5bf9\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u6768\u8001\u82f1\u96c4\u5bf9\u4ee4\u5f92\u7684\u8bef\u4f1a\uff0c\u8be5\u91ca\u7136\u4e8e\u6000 \u4e86\u5427\uff01\u201d\u6768\u4ef2\u82f1\u70b9\u5934\u5fae\u7b11\uff0c\u62b1\u62f3\u79f0\u8c22\u3002\u539f\u6765\u5510\u6653\u6593\u5728\u8fd9\u534a\u65e5\u4e4b\u4e2d\uff0c\u65e9\u628a\u9690\u60c5\u7ec6\u8bf4\uff0c\u6768\u4ef2\u82f1\u771f \u6599\u4e0d\u5230\u4ed6\u6709\u8fd9\u4e48\u590d\u6742\u7684\u8eab\u4e16\uff0c\u6768\u4ef2\u82f1\u672c\u6765\u7231\u4ed6\uff0c\u53ea\u56e0\u8bef\u4f1a\u4ed6\u53db\u5e08\u80cc\u4e49\uff0c\u6240\u4ee5\u624d\u7231\u4e4b\u6df1\u6068\u4e4b \u5207\uff0c\u8981\u628a\u4ed6\u5904\u6b7b\u3002\u5982\u4eca\u542c\u4e86\u89e3\u91ca\uff0c\u8bef\u4f1a\u51b0\u6d88\uff0c\u4e0d\u7981\u628a\u4ed6\u63fd\u5728\u6000\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u59d4\u5c48\u4f60 \u4e86\uff01\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u8fd9\u4e2a\u602a\u4e0d\u5f97\u5e08\u5085\u3002\u201d\u53c8\u628a\u6c88\u5728\u5bbd\u6559\u5bfc\u4ed6\u7684\u8bdd\u8bf4\u4e86\u3002\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u6c88\u5148\u751f \u4e4b\u8a00\u6df1\u5f97\u6211\u5fc3\uff0c\u5230\u5e95\u4ed6\u662f\u4e2a\u8bfb\u4e66\u4eba\uff0c\u8bf4\u8bdd\u771f\u6709\u89c1\u8bc6\u3002\u201d \u3000\u3000\u90b9\u9521\u4e5d\u7ecf\u8fc7\u4e86\u51e0\u5e74\u5386\u7ec3\uff0c\u4eba\u60c5\u4e16\u6545\uff0c\u901a\u8fbe\u8bb8\u591a\uff0c\u89c1\u6768\u5510\u4e8c\u4eba\u4eb2\u5982\u7236\u5b50\uff0c\u4ed6\u5bf9\u6768\u67f3\u9752\u4e4b \u5fc3\uff0c\u65e9\u5df2\u6e10\u6de1\uff0c\u5982\u4eca\u66f4\u662f\u534a\u70b9\u90fd\u65e0\u3002\u8fc7\u6765\u5411\u5510\u6653\u6f9c\u9053\u8c22\u3002\u6768\u4ef2\u82f1\u8001\u6000\u5927\u60a6\uff0c\u8c6a\u5174\u9057\u98de\uff0c\u548c\u7518 \u51e4\u6c60\u5927\u676f\u559d\u9152\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u559d\u4e86\u51e0\u676f\uff0c\u62c8\u987b\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u6653\u6f9c\u8981\u968f\u4f60\u4eec\u8fdb\u4eac\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u566b\uff0c\u600e\u4e48\uff1f\u201d \u6768\u4ef2\u82f1\u9053\uff1a\u201c\u6211\u60f3\u5e26\u4ed6\u56de\u5bb6\u4e00\u8f6c\u3002\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u6211\u4eec\u540c\u4ed6\u51fa\u6765\uff0c\u672c\u6765\u5c31\u662f\u60f3\u627e\u8001\u82f1\u96c4\u89e3 \u91ca\uff0c\u5982\u4eca\u4f60\u4eec\u65e2\u7136\u89c1\u4e86\u9762\uff0c\u8bef\u4f1a\u53c8\u5df2\u6d88\u9664\uff0c\u5df2\u6ca1\u6709\u6211\u4eec\u7684\u4e8b\u4e86\uff0c\u4ed6\u81ea\u7136\u8be5\u4f8d\u5019\u4f60\u8001\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5415\u56db\u5a18\u548c\u767d\u6cf0\u5b98\u9c7c\u5a18\u4e09\u4eba\u4ece\u5047\u5c71\u90a3\u8fb9\u6025\u6b65\u884c\u6765\u3002\u5415\u56db\u5a18\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4e03\u54e5\uff0c \u8def\u5e08\u5144\u7684\u4e0b\u843d\u5df2\u7ecf\u6709\u4e86\uff01\u201d \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\uff01\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u8fd8\u662f\u8ba9\u9c7c\u5a18\u59b9\u8bf4\u5427\uff0c\u8def\u5e08\u5144\u88ab\u64d2\u90a3\u5929\uff0c\u6070\u597d\u9c7c \u59b9\u4e5f\u5728\u573a\u3002\u201d \u3000\u3000\u9c7c\u5a18\u4f9d\u504e\u5728\u767d\u6cf0\u5b98\u8eab\u8fb9\uff0c\u8138\u6655\u8f7b\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u81ea\u4ece\u90a3\u6b21\u5415\u59d0\u59d0\u548c\u6cf0\u5b98\u5728\u7530\u6a2a\u5c9b\u4e0a\u5927\u95f9\u4e4b \u540e\uff0c\u7239\u7239\u628a\u6211\u770b\u7ba1\u5f97\u5f88\u4e25\uff0c\u6211\u5047\u88c5\u987a\u4ed6\u7684\u610f\uff0c\u4e0d\u5435\u4e0d\u95f9\uff0c\u8fc7\u4e86\u51e0\u5e74\uff0c\u7239\u7239\u770b\u7ba1\u5f97\u6e10\u6e10\u677e\u4e86\uff0c \u4f46\u8fd8\u662f\u6ca1\u673a\u53ef\u4e58\uff0c\u9003\u8d70\u4e0d\u4e86\u3002\u76f4\u5230\u4e0a\u6708\uff0c\u6211\u7236\u4eb2\u5e94\u6bd2\u9f99\u5c0a\u8005\u4e4b\u7ea6\uff0c\u6e21\u6d77\u5230\u65c5\u987a\u53e3\u5916\u4e00\u4e2a\u5c0f\u5c9b \u548c\u4ed6\u76f8\u4f1a\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u7518\u51e4\u6c60\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6bd2\u9f99\u5c0a\u8005\u4e00\u751f\u4f4f\u5728\u86c7\u5c9b\uff0c\u4ece\u4e0d\u5916\u51fa\uff0c \u600e\u4e48\u4f1a\u7ea6\u4ee4\u5c0a\u76f8\u4f1a\uff1f\u201d\u9c7c\u5a18\u9053\uff1a\u201c\u8fd9\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\uff01\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u90a3\u6bd2\u9f99\u5c0a\u8005\u540d\u5934\u5f88\u5927\uff0c \u4e0d\u77e5\u6b66\u529f\u5230\u5e95\u5982\u4f55\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u53ea\u77e5\u4ed6\u4e0e\u8428\u6c0f\u53cc\u9b54\u76f8\u4ea4\u751a\u539a\uff0c\u6b66\u529f\u5982\u4f55\uff0c\u65e0\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u9c7c\u5a18\u7eed\u9053\uff1a\u201c\u7239\u7239\u79bb\u5bb6\u4e4b\u540e\uff0c\u6211\u8d81\u7740\u770b\u5b88\u7684\u4eba\u4e0d\u6ce8\u610f\uff0c\u4e00\u5929\u665a\u4e0a\uff0c\u5077\u5077\u4e58\u4e00\u8258\u6d77\u8239\uff0c\u8fde \u591c\u5f00\u8d70\u3002\u8239\u4e0a\u7c73\u7cae\u751a\u591a\uff0c\u6211\u53c8\u9887\u8bc6\u6c34\u6027\uff0c\u90a3\u665a\u6f6e\u6c34\u53c8\u5927\uff0c\u6211\u6599\u60f3\u4ed6\u4eec\u53d1\u89c9\u4e4b\u65f6\uff0c\u6211\u5df2\u51fa\u6d77\u6570 \u5341\u91cc\uff0c\u4ed6\u4eec\u8981\u8ffd\u4e5f\u8ffd\u4e0d\u53ca\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u53d1\u51fa\u723d\u6717\u7b11\u58f0\u3002\u7518\u51e4\u6c60\u6709\u611f\u4e8e\u5979\u4e0e\u767d\u6cf0\u5b98\u76f8\u7231\u4e4b\u8bda\u3002 \u8d5e\u9053\uff1a\u201c\u59d1\u5a18\u52c7\u6c14\uff0c\u4ee4\u4eba\u4f69\u670d\u3002\u201d\u9c7c\u5a18\u7b11\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u4ec0\u4e48\uff0c\u5c31\u662f\u5929\u5929\u5403\u9c7c\u3002\u5355\u8c03\u6781\u4e86\u3002\u201d \u5415\u56db\u5a18\u4e00\u7b11\uff0c\u9012\u4e86\u4e00\u676f\u9152\u8fc7\u6765\uff0c\u8fd8\u7ed9\u5979\u5939\u4e86\u4e00\u5757\u70e7\u9e21\u9053\uff1a\u201c\u597d\uff0c\u6170\u52b3\u4f60\u4e00\u4e0b\u3002\u201d\u9c7c\u5a18\u542b\u7b11\u5403 \u4e86\uff0c\u7eed\u9053\uff1a\u201c\u6211\u4ee5\u524d\u542c\u6cf0\u5b98\u8bf4\u8fc7\u4ed6\u7684\u540c\u95e8\uff0c\u77e5\u9053\u8def\u4e09\u54e5\u4f4f\u5728\u6d59\u6c5f\u6cbf\u6d77\u7684\u8427\u5c71\uff0c\u6211\u5c31\u628a\u8239\u5f00\u5230 \u90a3\u513f\u53bb\u5566\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4e94\u516d\u5e74\u524d\uff0c\u6211\u548c\u8def\u5e08\u54e5\u5415\u5e08\u59b9\u4e3a\u6551\u6c88\u5148\u751f\uff0c\u66fe\u548c\u5fa1\u6797\u519b\u5927\u6253\u4e00 \u4ed7\uff0c\u4e8b\u540e\u6211\u9001\u5415\u5e08\u59b9\u4e0a\u4ed9\u971e\u5cad\uff0c\u8def\u5e08\u5144\u4e5f\u9003\u4ea1\u5230\u5173\u4e1c\u53bb\u4e86\u3002\u4f60\u5927\u7ea6\u4e0d\u77e5\u9053\u6b64\u4e8b\u3002\u201d\u5415\u56db\u5a18\u7b11 \u9053\uff1a\u201c\u5979\u5f53\u7136\u4e0d\u77e5\u9053\uff0c\u53ef\u662f\u4e8b\u60c5\u4e5f\u771f\u6709\u8fd9\u4e48\u5de7\uff0c\u5979\u5230\u8427\u5c71\u90a3\u5929\uff0c\u6070\u5de7\u8def\u5e08\u5144\u4e5f\u5077\u5077\u6e9c\u56de\u6765\u3002\u201d \u3000\u3000\u9c7c\u5a18\u559d\u4e86\u53e3\u9152\uff0c\u9053\uff1a\u201c\u5047\u5982\u6211\u77e5\u9053\uff0c\u6211\u5c31\u4e0d\u4f1a\u8fd9\u6837\u50bb\u4e86\uff0c\u6211\u5230\u4e86\u8427\u5c71\uff0c\u95ee\u8d77\u8def\u5bb6\uff0c\u4eba\u5bb6 \u90fd\u4e0d\u6562\u8bf4\uff0c\u6b63\u8be2\u95ee\u95f4\uff0c\u5ffd\u7136\u6709\u4e00\u961f\u5b98\u5175\uff0c\u7c07\u62e5\u7740\u4e00\u4e2a\u5c11\u5973\uff0c\u90a3\u5c11\u5973\u8d70\u8fc7\u6765\u9053\uff1a\u2018\u4f60\u627e\u8def\u6c11\u77bb \u505a\u4ec0\u4e48\uff1f\u4f60\u662f\u4ed6\u7684\u4ec0\u4e48\u4eba\uff1f\u2019\u6211\u7ed9\u5979\u95ee\u4f4f\u4e86\uff0c\u770b\u5979\u6765\u610f\u4e0d\u5584\uff0c\u5c31\u60f3\u9003\u8d70\uff0c\u90a3\u77e5\u8fd9\u5c11\u5973\u6b66\u529f\u751a \u9ad8\uff0c\u5728\u9a6c\u80cc\u4e0a\u98de\u63a0\u800c\u4e0b\uff0c\u62e6\u5728\u6211\u7684\u9762\u524d\u3002\u6211\u548c\u5979\u62c6\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u624d\u80fd\u7a0d\u5360\u4e0a\u98ce\u3002\u201d\u5415\u56db\u5a18 \u9053\uff1a\u201c\u90a3\u5c11\u5973\u662f\u4e0d\u662f\u74dc\u5b50\u8138\u513f\uff0c\u7709\u6bdb\u5f88\u957f\uff0c\u4e00\u6d3e\u5929\u771f\u7684\u6a21\u6837\uff1f\u201d\u9c7c\u5a18\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u5415\u56db\u5a18 \u5947\u9053\uff1a\u201c\u90a3\u662f\u6d59\u6c5f\u5de1\u629a\u674e\u536b\u7684\u5973\u513f\u674e\u660e\u73e0\uff0c\u5979\u672c\u6765\u4e0d\u61c2\u6b66\u529f\uff0c\u600e\u4e48\u5728\u8fd9\u4e94\u516d\u5e74\u95f4\uff0c\u5c31\u7ec3\u5f97\u90a3 \u4e48\u51fa\u8272\u7684\u672c\u9886\uff0c\u5c45\u7136\u80fd\u591f\u548c\u4f60\u6253\u5230\u4e09\u4e94\u5341\u62db\uff1f\u201d\u9c7c\u5a18\u7eed\u9053\uff1a\u201c\u6211\u521a\u521a\u5360\u4e86\u4e0a\u98ce\uff0c\u5ffd\u7136\u5728\u5b98\u5175 \u961f\u4e2d\uff0c\u8d70\u51fa\u4e00\u4e2a\u9752\u8863\u5987\u4eba\uff0c\u53cc\u624b\u7a7a\u7a7a\uff0c\u52a8\u624b\u4e0d\u8fc7\u4e09\u62db\uff0c\u5c31\u628a\u6211\u7684\u5175\u5203\u62a2\u53bb\u3002\u201d\u5415\u56db\u5a18\u95ee\u9053\uff1a \u201c\u90a3\u9752\u8863\u5987\u4eba\u662f\u4e0d\u662f\u540e\u6765\u770b\u7ba1\u4f60\u7684\u90a3\u4f4d\u767d\u53d1\u6ee1\u5934\u7684\u8001\u5a46\u5a46\uff1f\u201d\u9c7c\u5a18\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4e0d\u8fc7\u5979\u4eec\u662f \u4e00\u8def\u7684\u4eba\u3002\u201d\u7518\u51e4\u6c60\u542c\u4e86\uff0c\u6c89\u601d\u4e0d\u8bed\u3002\u5fc3\u60f3\u4ee5\u9c7c\u5a18\u7684\u6b66\u529f\uff0c\u81ea\u5df1\u4e5f\u672a\u5fc5\u80fd\u5728\u4e09\u62db\u4e4b\u5185\u593a\u5979\u5175 \u5203\uff0c\u8fd9\u9752\u8863\u5987\u4eba\u53c8\u662f\u4f55\u4eba\uff1f \u3000\u3000\u5415\u56db\u5a18\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u5979\u4eec\u662f\u540c\u4e00\u8def\u7684\u4eba\uff1f\u201d\u9c7c\u5a18\u7eed\u9053\uff1a\u201c\u90a3\u9752\u8863\u5987\u4eba\u628a\u6211\u64d2\u4e86\u4e4b \u540e\uff0c\u5c31\u5728\u8def\u5bb6\u5bf9\u9762\u7684\u4e00\u68f5\u67f3\u6811\u4e0a\uff0c\u5c06\u6211\u5012\u540a\u8d77\u6765\uff0c\u5f85\u97ad\u62f7\u6253\uff0c\u521a\u6253\u5f97\u4e00\u97ad\uff0c\u8def\u5bb6\u7684\u70ae\u6402\u4e0a\u7a81 \u7136\u98de\u4e0b\u4e00\u4eba\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u2018\u8def\u6c11\u77bb\u5728\u6b64\uff0c\u4f60\u4eec\u8981\u6355\u4fbf\u6355\uff0c\u53ef\u4e0d\u8bb8\u7275\u7d2f\u65e0\u8f9c\u3002\u201d\u90a3\u9752\u8863\u5987\u4eba\u789f \u789f\u602a\u7b11\uff0c\u9053\uff1a\u2018\u54c8\uff0c\u4f60\u8fd9\u53ef\u662f\u81ea\u6295\u7f57\u7f51\uff01\u2019\u8dc3\u4e0a\u524d\u53bb\uff0c\u5927\u7ea6\u4e5f\u662f\u5341\u62db\u5de6\u53f3\uff0c\u5c31\u628a\u8def\u7237\u64d2 \u4e86\u3002\u201d\u7518\u51e4\u6c60\u6697\u9053\uff1a\u8def\u5e08\u5144\u867d\u662f\u516c\u5b50\u54e5\u513f\uff0c\u5012\u4e5f\u9887\u6709\u7537\u5b50\u6c14\u6982\u3002\u9c7c\u5a18\u7eed\u9053\uff1a\u201c\u90a3\u9752\u8863\u5987\u4eba\u6301 \u97ad\u559d\u9053\uff1a\u2018\u5979\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u2019\u8def\u7237\u9053\uff1a\u2018\u6211\u4ece\u672a\u89c1\u8fc7\u5979\u3002\u2019\u9752\u8863\u5987\u4eba\u9053\uff1a\u2018\u5979\u4e3a\u4f55\u627e \u4f60\uff1f\u2019\u8def\u7237\u51dd\u7738\u77a7\u6211\u3002\u6211\u7ed9\u4ed6\u77a7\u5f97\u9762\u90fd\u7ea2\u5566\uff0c\u5728\u90a3\u6837\u60c5\u666f\u4e0b\uff0c\u6211\u53c8\u4e0d\u597d\u8bf4\u51fa\u6211\u548c\u6cf0\u5b98\u7684\u5173 \u7cfb\u3002\u201d\u5415\u56db\u5a18\u201c\u54c8\u201d\u7684\u4e00\u7b11\uff0c\u9c7c\u5a18\u55d4\u9053\uff1a\u201c\u4eba\u5bb6\u96be\u8fc7\uff0c\u4f60\u5374\u597d\u7b11\u3002\u201d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u540e\u6765\u90a3\u4eba \u53eb\u674e\u660e\u73e0\u628a\u6211\u548c\u8def\u7237\u90fd\u5e26\u5230\u629a\u8859\u91cc\u53bb\uff0c\u9752\u8863\u5987\u4eba\u9053\uff1a\u201c\u628a\u94c1\u6247\u5e2e\u7684\u4eba\u627e\u6765\u8ba4\u4e00\u8ba4\u5427\uff0c\u4ed6\u4eec\u5e38 \u5728\u6c5f\u6e56\u884c\u8d70\uff0c\u4e5f\u8bb8\u4f1a\u77e5\u9053\u8fd9\u4e2b\u5934\u7684\u6765\u5386\u3002\u2019\u7b2c\u4e8c\u5929\u90a3\u4e2a\u8001\u5a46\u5a46\u5c31\u6765\u5566\uff0c\u6211\u4e0d\u8ba4\u5f97\u5979\uff0c\u5979\u5374\u8ba4 \u5f97\u6211\uff0c\u4e00\u89c1\u9762\u5c31\u53eb\u51fa\u6211\u7684\u540d\u5b57\uff0c\u9752\u8863\u5987\u4eba\u7acb\u523b\u53d8\u4e86\u6001\u5ea6\uff0c\u628a\u6211\u89e3\u4e86\u4e0b\u6765\uff0c\u5c31\u4ea4\u7ed9\u90a3\u4e2a\u8001\u5a46\u5a46 \u5c06\u6211\u5e26\u5230\u94c1\u6247\u5e2e\u53bb\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u542c\u5b8c\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u516b\u59b9\u6240\u6599\u4e0d\u5dee\uff0c\u4e09\u54e5\u679c\u662f\u88ab\u7981\u5728\u6d59\u629a\u8859\u4e2d\u3002\u90a3\u4e48\u6211\u4eec\u4e0d\u5fc5\u518d \u5230\u4e09\u54e5\u7684\u5bb6\u4e61\u4e86\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u65e5\u7fa4\u96c4\u5206\u9053\u626c\u9573\u3002\u6768\u4ef2\u82f1\u643a\u5510\u6653\u6f9c\u56de\u5c71\u4e1c\u6545\u91cc\u3002\u5173\u4e1c\u56db\u4fa0\u548c\u63d2\u7ffc\u795e\u72ee\u7236\u5b50\u4e5f\u7b54\u5e94\u4e86 \u6768\u4ef2\u82f1\u7684\u9080\u8bf7\uff0c\u5230\u4ed6\u5bb6\u91cc\u4f5c\u5ba2\u3002\u4e34\u522b\u65f6\uff0c\u7518\u51e4\u6c60\u5ffd\u9053\uff1a\u201c\u6768\u8001\u82f1\u96c4\u548c\u5173\u4e1c\u56db\u4fa0\uff0c\u4f60\u4eec\u4e00\u5b9a\u8981 \u5e2e\u6211\u70b9\u5c0f\u5fd9\u3002\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u7518\u5927\u4fa0\u5c3d\u7ba1\u5429\u5490\u3002\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u94c1\u6247\u5e2e\u7684\u73e0\u5b9d\uff0c\u6211\u4eec\u643a\u5e26 \u4e0d\u4fbf\uff0c\u8bf7\u5404\u4f4d\u4ee3\u4e3a\u4fdd\u7ba1\uff0c\u4e5f\u4ee3\u4e3a\u4f7f\u7528\uff0c\u884c\u4fa0\u4ed7\u4e49\uff0c\u6709\u65f6\u4e5f\u8981\u7528\u4e00\u70b9\u94b1\uff0c\u201d\u6768\u4ef2\u82f1\u4e00\u7b11\u5141\u8bfa\u3002 \u3000\u3000\u5510\u6653\u6f9c\u4e0e\u5415\u56db\u5a18\u518d\u4e09\u9053\u522b\uff0c\u751a\u4e3a\u6005\u60af\u3002\u4ed6\u5bf9\u5415\u56db\u5a18\u867d\u7136\u65e9\u65e0\u6742\u5ff5\uff0c\u4f46\u6069\u6df1\u4e49\u91cd\uff0c\u5230\u5e95\u4e0d \u80dc\u4f9d\u4f9d\u3002\u5c24\u5176\u662f\u60f3\u8d77\u6768\u67f3\u9752\u65f6\uff0c\u66f4\u89c9\u5f97\u5415\u56db\u5a18\u7684\u53ef\u7231\u53ef\u656c\u3002\u6768\u4ef2\u82f1\u77a7\u5728\u773c\u91cc\u5fd9\u50ac\u5510\u6653\u6f9c\u5feb\u8d70\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u8d70\u540e\uff0c\u7518\u5415\u767d\u9c7c\u56db\u4eba\u4e5f\u6536\u62fe\u884c\u674e\uff0c\u8ff3\u8d74\u676d\u5dde\u3002\u7b2c\u4e09\u5929\u4e00\u65e9\uff0c\u5230\u4e86\u676d\u57ce\uff0c\u5728\u6e56\u6ee8\u4e00 \u95f4\u65c5\u820d\u6295\u5bbf\uff0c\u5546\u8bae\u665a\u4e0a\u63a2\u8859\u3002\u65f6\u95f4\u5c1a\u65e9\uff0c\u56db\u4eba\u96c7\u4e86\u4e00\u8258\u8239\u8247\u6e38\u6e56\uff0c\u6e56\u5e73\u5982\u955c\uff0c\u6e38\u9c7c\u53ef\u6570\uff0c\u821f \u884c\u7247\u523b\uff0c\u5ffd\u89c1\u6709\u4e09\u5ea7\u5854\u5c16\uff0c\u6d6e\u51fa\u6c34\u9762\uff0c\u98ce\u59ff\u53e4\u6734\uff0c\u5012\u5f71\u5947\u5e7b\uff1a\u77f3\u5854\u8fb9\u662f\u4e00\u5c0f\u5c9b\uff0c\u5c9b\u4e0a\u82b1\u8349\u4e1b \u751f\uff0c\u697c\u53f0\u9690\u7ea6\u3002\u9c7c\u5a18\u559c\u9053\uff1a\u201c\u8fd9\u91cc\u771f\u7f8e\uff01\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u897f\u6e56\u6700\u7f8e\u7684\u5730\u65b9\uff0c\u540d\u4e3a\u4e09\u6f6d \u5370\u6708\uff0c\u6e56\u4e2d\u6709\u6e56\uff0c\u5c9b\u4e2d\u6709\u5c9b\uff0c\u56ed\u6797\u5e03\u7f6e\u4e4b\u4f73\uff0c\u51a0\u4e8e\u4e1c\u5357\u3002\u636e\u8bf4\u8fd8\u662f\u82cf\u4e1c\u5761\u6240\u5efa\u7684\u5462\u3002\u9c7c\u59b9\u65e2 \u7136\u559c\u6b22\uff0c\u54b1\u4eec\u4e0a\u53bb\u73a9\u73a9\u3002\u201d\u56db\u4eba\u820d\u821f\u767b\u9646\uff0c\u6b65\u8fc7\u4e5d\u66f2\u6865\u680f\uff0c\u9c7c\u5a18\u6ee1\u6000\u6b22\u60a6\u3002\u5415\u56db\u5a18\u5ffd\u7136\u628a\u624b \u4e00\u6307\uff0c\u9053\uff1a\u201c\u6e56\u5c71\u80dc\u5904\uff0c\u4e0d\u4e4f\u96c5\u4eba\u3002\u4f60\u770b\u90a3\u4e2a\u5c11\u5e74\uff01\u201d \u3000\u3000\u9c7c\u5a18\u653e\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u6e56\u9762\u4e0a\u4e00\u8258\u753b\u822a\uff0c\u7f13\u7f13\u6447\u6765\uff0c\u821f\u4e2d\u5c0f\u5c11\u5e74\uff0c\u7ea6\u83ab\u5341\u5c81\u5149\u666f\uff0c\u751f\u5f97\u9762 \u5982\u51a0\u7389\uff0c\u8c8c\u6bd4\u6f58\u5b89\u3002\u821f\u4e2d\u5b89\u4e86\u4e00\u526f\u8336\u51e0\uff0c\u4e0a\u6709\u6e05\u8336\u4e00\u58f6\uff0c\u7476\u7434\u4e00\u5177\uff0c\u8fd9\u7f8e\u5c11\u5e74\u5f15\u7434\u6b4c\u9053\uff1a \u201c\u6e3a\u6e3a\u6f84\u6ce2\u4e00\u955c\u5f00\uff0c\u78a7\u5c71\u79cb\u8272\u4eba\u676f\u6765\uff1b\u5c0f\u821f\u6491\u51fa\u4e39\u67ab\u91cc\uff0c\u843d\u53f6\u8f7b\u98ce\u626b\u7eff\u53f0\u3002\u201d\u6b4c\u58f0\u987a\u7740\u6e56\u9762 \u8361\u53bb\uff0c\u66f2\u6298\u60a0\u626c\uff0c\u5341\u5206\u60a6\u8033\u3002\u767d\u6cf0\u5b98\u4e5f\u8d5e\u9053\uff1a\u201c\u6b64\u4eba\u4e0d\u4fd7\u3002\u201d \u3000\u3000\u201c\u4e09\u6f6d\u5370\u6708\u201d\u662f\u897f\u6e56\u4e0a\u4e00\u4e2a\u5c0f\u5c9b\uff0c\u8fd9\u4e2a\u201c\u5c9b\u201d\u5b9e\u9645\u662f\u4e00\u4e2a\u73af\u5f62\u7684\u5824\u5cb8\uff0c\u56f4\u6210\u5c0f\u5c0f\u7684\u5185 \u6e56\uff0c\u4e2d\u95f4\u53c8\u6709\u4e00\u4e2a\u66f4\u5c0f\u7684\u5c9b\uff0c\u6240\u4ee5\u8bf4\u662f\u201c\u6e56\u4e2d\u6709\u6e56\uff0c\u5c9b\u4e2d\u6709\u5c9b\u201d\u3002\u800c\u5728\u6e56\u4e0e\u6e56\u3001\u5c9b\u4e0e\u5c9b\u4e4b \u95f4\uff0c\u7f00\u4ee5\u4ead\u53f0\u697c\u69ad\uff0c\u9ad8\u4f4e\u9690\u73b0\uff0c\u73b2\u73d1\u6d6e\u7a81\uff0c\u65e0\u4e00\u5904\u4e0d\u663e\u5320\u5fc3\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u54b1\u4eec\u5230\u91cc\u9762\u53bb \u5427\u3002\u201d\u6b65\u8fc7\u4e5d\u66f2\u6865\u680f\uff0c\u7a7f\u8fc7\uff38\u5b57\u4ead\u3001\u4e00\u5bc4\u697c\u7b49\u5904\uff0c\u66f2\u66f2\u6298\u6298\uff0c\u8d70\u5230\u4e86\u5782\u6768\u6df1\u5904\uff0c\u53ea\u89c1\u4e00\u5ea7\u8336 \u4ead\uff0c\u5341\u5206\u7cbe\u96c5\uff0c\u4e0a\u9898\u4e3a\u201c\u5341\u8fce\u7fe0\u8f69\u201d\uff0c\u4e24\u65c1\u4e00\u526f\u5bf9\u8054\uff0c\u5199\u7684\uff1a\u201c\u4e07\u9877\u6e56\u5e73\u957f\u4f3c\u955c\uff1b\u56db\u65f6\u6708\u597d \u6700\u5b9c\u79cb\u3002\u201d\u5415\u56db\u5a18\u8d5e\u9053\uff1a\u201c\u8fd9\u526f\u8054\u5be5\u5be5\u5341\u56db\u5b57\uff0c\u6d3b\u753b\u51fa\u897f\u6e56\u666f\u8272\uff0c\u4e0e\u5e73\u6e56\u79cb\u6708\u4e4b\u8054\uff0c\u53ef\u5e76\u79f0 \u53cc\u7edd\u3002\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u516b\u59b9\u6e38\u8e2a\u6240\u81f3\uff0c\u6700\u597d\u8bb0\u90a3\u4e9b\u540d\u80dc\u5730\u65b9\u7684\u8bd7\u8bcd\u8054\u8bed\u4e4b\u7c7b\uff0c\u6211\u53ef\u6ca1\u6709\u8fd9 \u4efd\u8010\u5fc3\u3002\u201d\u9c7c\u5a18\u8fd9\u51e0\u5e74\u5e7d\u5c42\u8352\u5c9b\uff0c\u95f2\u65f6\u4e5f\u8bfb\u8bd7\u4e66\uff0c\u89c1\u5415\u56db\u5a18\u8bf4\u5f97\u9ad8\u5174\uff0c\u4fbf\u9053\uff1a\u201c\u5415\u59d0\u59d0\uff0c\u4f60 \u628a\u5e73\u6e56\u79cb\u6708\u90a3\u8054\u4e00\u5e76\u5ff5\u7ed9\u5c0f\u59b9\u542c\u5427\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4f60\u5fd9\u4ec0\u4e48\uff0c\u7b49\u4f1a\u6211\u4eec\u518d\u5230\u2018\u5e73\u6e56\u79cb \u6708\u2019\u53bb\u73a9\uff0c\u4f60\u5927\u53ef\u628a\u90a3\u4e9b\u4f73\u8054\u90fd\u6284\u4e0b\u6765\u3002\u201d\u4f46\u8fd8\u662f\u5ff5\u9053\uff1a\u201c\u51ed\u680f\u770b\u4e91\u5f71\u6ce2\u5149\uff0c\u6700\u597d\u662f\u7ea2\u86ee\u82b1 \u758f\uff0c\u65e5\u82f9\u79cb\u8001\uff1b\u628a\u9152\u5bf9\u743c\u697c\u7389\u5b87\uff0c\u83ab\u8f9c\u8d1f\u5929\u5fc3\u6708\u6ee1\uff0c\u6c34\u9762\u98ce\u6765\u3002\u201d\u9c7c\u5a18\u542c\u5728\u8033\u91cc\uff0c\u5ff5\u5728\u53e3 \u91cc\uff0c\u4e00\u4e2a\u4e2a\u5b57\u5728\u820c\u5c16\u6253\u6eda\uff0c\u4f46\u89c9\u5982\u56bc\u6a44\u6984\uff0c\u6ee1\u53e3\u7518\u82b3\u3002 \u3000\u3000\u56db\u4eba\u8fdb\u4e86\u8336\u697c\uff0c\u51ed\u680f\u5750\u4e0b\uff0c\u8336\u535a\u58eb\u8fc7\u6765\u95ee\u9053\uff1a\u201c\u56db\u4f4d\u5404\u51b2\u4e00\u676f\u85d5\u7c89\uff0c\u518d\u6ce1\u4e24\u58f6\u9f99\u4e95\u5982 \u4f55\uff1f\u201d\u897f\u6e56\u85d5\u7c89\u548c\u9f99\u4e95\u8336\u6700\u662f\u6709\u540d\uff0c\u5415\u56db\u5a18\u70b9\u5934\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u6837\u3002\u201d \u3000\u3000\u8fce\u7fe0\u8f69\u4e2d\u8336\u5ba2\u5be5\u5be5\uff0c\u4e1c\u9996\u4e00\u684c\uff0c\u72ec\u5750\u4e00\u4e2a\u8001\u5934\uff0c\u89c1\u7518\u5415\u7b49\u56db\u4eba\u8fdb\u6765\uff0c\u4f3c\u4e4e\u9887\u662f\u7559\u795e\uff0c\u770b \u4e86\u53c8\u770b\u3002\u5415\u56db\u5a18\u89c1\u8fd9\u4eba\u9762\u8c8c\u9887\u719f\uff0c\u4e00\u65f6\u5374\u60f3\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u5750\u4e86\u4e00\u4f1a\uff0c\u7af9\u5e18\u5f00\u5904\uff0c\u90a3\u821f\u4e2d\u7684\u7f8e\u5c11\u5e74\u8d70\u4e86\u8fdb\u6765\u3002\u7518\u51e4\u6c60\u89c1\u4ed6\u6c14\u5b87\u8f69\u6602\uff0c\u82f1\u534e\u5185\u8574\uff0c\u6697 \u81ea\u7559\u5fc3\u3002\u90a3\u5c11\u5e74\u4e5f\u51b2\u4e86\u676f\u85d5\u7c89\uff0c\u6ce1\u4e86\u4e00\u58f6\u9f99\u4e95\uff0c\u51ed\u680f\u5750\u4e0b\u3002\u53cc\u76ee\u70af\u70af\uff0c\u76ee\u5149\u5bf9\u7518\u51e4\u6c60\u8fd9\u8fb9\u6295 \u5c04\u8fc7\u6765\u3002 \u3000\u3000\u5415\u56db\u5a18\u548c\u9c7c\u5a18\u90fd\u6539\u4e86\u7537\u88c5\uff0c\u90a3\u5c11\u5e74\u76ee\u5149\u6a2a\u626b\u8fc7\u6765\uff0c\u9c7c\u5a18\u4e0d\u77e5\u4e0d\u89c9\u4f4e\u4e0b\u4e86\u5934\u3002\u5415\u56db\u5a18\u6084\u6084 \u5728\u684c\u5b50\u5e95\u7528\u811a\u78b0\u4e86\u5979\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u4e94\u54e5\uff0c\u4f60\u770b\u8fd9\u6e56\u4e0a\u7684\u7761\u83b2\uff0c\u53e4\u4eba\u8bd7\u4e91\uff1a\u7559\u5f97\u6b8b\u8377\u542c\u96e8\u58f0\uff0c \u4f46\u542c\u90a3\u6e38\u9c7c\u78d4\u78d4\u4e4b\u58f0\u3002\u73b0\u5728\u867d\u65e0\u7ec6\u7f51\uff0c\u8377\u4e5f\u672a\u6b8b\uff0c\u770b\u90a3\u8377\u4e0a\u5706\u73e0\u6eda\u52a8\u4e4b\u72b6\uff0c\u4ee4\u4eba\u76ca\u589e\u559c \u60a6\u3002\u201d\u9c7c\u5a18\u4e00\u542c\uff0c\u77e5\u662f\u5415\u56db\u5a18\u6697\u4e2d\u63d0\u9192\u4e8e\u5979\uff0c\u6545\u610f\u53eb\u5979\u505a\u4e94\u54e5\uff0c\u8ba9\u5979\u8bb0\u8d77\u81ea\u5df1\u662f\u4e2a\u201c\u7537 \u5b50\u201d\u3002\u5fc3\u4e2d\u4e0d\u89c9\u597d\u7b11\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u53c8\u4e0d\u7981\u609a\u7136\u6697\u60ca\uff0c\u81ea\u5df1\u8fd9\u4e00\u65e0\u610f\u4e4b\u4e2d\uff0c\u9732\u51fa\u4e86\u5973\u513f\u7f9e \u6001\uff0c\u82e5\u7136\u7ed9\u8fd9\u5c11\u5e74\u770b\u7834\uff0c\u5c82\u975e\u4e0d\u4fbf\u3002 \u3000\u3000\u90a3\u7f8e\u5c11\u5e74\u5374\u4f3c\u5e76\u4e0d\u600e\u4e48\u6ce8\u610f\uff0c\u626b\u4e86\u5415\u56db\u5a18\u4e00\u773c\u4e4b\u540e\uff0c\u773c\u5149\u53c8\u79fb\u5230\u5b64\u5355\u8001\u5934\u8eab\u4e0a\uff0c\u90a3\u8001\u5934 \u4f3c\u6709\u4e86\u51e0\u5206\u9152\u610f\uff0c\u501a\u680f\u541f\u9053\uff1a\u201c\u95ee\u8baf\u6e56\u8fb9\u6625\u8272\uff0c\u91cd\u6765\u53c8\u662f\u4e09\u5e74\u3002\u4e1c\u98ce\u5439\u6211\u8fc7\u6e56\u8239\uff0c\u6768\u67f3\u4e1d\u4e1d \u62c2\u9762\u3002\u4e16\u8def\u5982\u4eca\u5df2\u60ef\uff0c\u6b64\u5fc3\u5230\u5904\u60a0\u7136\u3002\u5bd2\u5149\u4ead\u4e0b\u6c34\u8fde\u5929\uff0c\u98de\u8d77\u6c99\u9e25\u4e00\u7247\u3002\u201d \u3000\u3000\u8fd9\u662f\u5357\u5b8b\u8bcd\u4eba\u5f20\u4e8e\u6e56\u7684\u201c\u897f\u6c5f\u6708\u201d\u8bcd\uff0c\u90a3\u8001\u8005\u5531\u6765\uff0c\u4f3c\u9887\u591a\u6005\u89e6\u3002\u90a3\u7f8e\u5c11\u5e74\u51fb\u8282\u79f0\u8d4f\uff0c \u4e00\u6b4c\u65e2\u7ec8\uff0c\u679c\u7136\u60ca\u8d77\u51e0\u53ea\u82a6\u82c7\u4e2d\u7684\u6c99\u9e25\uff0c\u632f\u7fc5\u98de\u53bb\uff0c\u90a3\u7f8e\u5c11\u5e74\u5ffd\u7136\u7ad9\u8d77\u8eab\u6765\uff0c\u8d70\u5230\u90a3\u8001\u8005\u684c \u524d\uff0c\u6df1\u6df1\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u4e08\u4e00\u5b9a\u662f\u8f66\u8001\u4f2f\u4e86\u3002\u201d\u90a3\u8001\u8005\u8fd8\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u6211\u4e0e\u5c0a \u7fc1\u4e00\u522b\u4e09\u5341\u4f59\u5e74\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u8fd8\u80fd\u89c1\u4f60\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u5fc3\u5934\u4e00\u89e6\uff0c\u731b\u7136\u8bb0\u4e86\u8d77\u6765\uff0c\u8fd9\u8001\u8005\u4e00\u5b9a\u662f\u5bff\u660c\u4e66\u9662\u7684\u201c\u5c71\u957f\u201d\u8f66\u9f0e\u4e30\u65e0\u7591\u4e86\u3002\u539f \u6765\u6614\u65e5\u5415\u56db\u5a18\u7684\u7956\u7236\u5415\u7559\u826f\u8bbe\u5e10\u8bb2\u7ecf\uff0c\u6d59\u897f\u6d59\u4e1c\u8bb8\u591a\u5112\u751f\u90fd\u66fe\u6765\u542c\u4ed6\u8bb2\u5b66\uff0c\u5bff\u660c\u4e66\u9662\u7684\u201c\u5c71 \u957f\u201d\uff08\u76f8\u5f53\u4e8e\u4eca\u65e5\u7684\u6821\u957f\uff09\u8f66\u9f0e\u4e30\u4e5f\u66fe\u6765\u542c\u8fc7\uff0c\u90a3\u65f6\u5415\u56db\u5a18\u5e74\u7eaa\u5f88\u5c0f\uff0c\u5927\u7ea6\u8fd8\u672a\u6ee1\u5341\u9f84\uff0c\u4e4b \u540e\u5415\u56db\u5a18\u5728\u9099\u5c71\u72ec\u81c2\u8001\u5c3c\u95e8\u4e0b\u5b66\u6280\uff0c\u5c31\u518d\u4e5f\u6ca1\u89c1\u8fc7\u4e86\u3002\u53ea\u540e\u6765\u542c\u7236\u4eb2\u8bf4\u8fc7\uff0c\u8fd9\u8f66\u9f0e\u4e30\u867d\u5728\u5bff \u660c\u53bf\u51fa\u751f\uff0c\u4f46\u5374\u5728\u56db\u5ddd\u957f\u5927\uff0c\u542c\u8bf4\u4ed6\u5c11\u5e74\u65f6\u9887\u5e72\u8fc7\u4e00\u756a\u4e8b\u4e1a\uff0c\u81f3\u4e8e\u662f\u4ec0\u4e48\u4e8b\u4e1a\uff0c\u7236\u4eb2\u5e76\u672a\u8a00 \u660e\uff0c\u5415\u56db\u5a18\u5f53\u65f6\u5e74\u8f7b\uff0c\u4e5f\u6ca1\u6709\u95ee\u3002\u540e\u6765\u5076\u7136\u66fe\u542c\u4e61\u5148\u8f88\u8c08\u8d77\uff0c\u8fd9\u8f66\u9f0e\u4e30\u5eff\u4e03\u516b\u5c81\u65f6\u59cb\u5f52\u6545 \u91cc\uff0c\u95ed\u95e8\u8bfb\u4e66\uff0c\u4e0d\u5230\u5341\u5e74\uff0c\u5c45\u7136\u6210\u4e86\u901a\u4eba\uff0c\u867d\u7136\u4e00\u534a\u662f\u5415\u7559\u826f\u6307\u70b9\u4e4b\u529f\uff0c\u4f46\u4ed6\u672c\u4eba\u7684\u5929\u8d44\u6bc5 \u529b\uff0c\u4e5f\u771f\u4ee4\u4eba\u4f69\u670d\u3002 \u3000\u3000\u8fd9\u65f6\u90a3\u201c\u674e\u516c\u5b50\u201d\u548c\u8f66\u9f0e\u4e30\u6b63\u5728\u5a13\u5a13\u800c\u8c08\uff0c\u8bdd\u58f0\u8bf4\u5f97\u5f88\u4f4e\uff0c\u597d\u50cf\u6015\u4eba\u542c\u89c1\u3002\u5415\u56db\u5a18\u5fc3 \u60f3\uff1a\u8fd9\u4e24\u4eba\u770b\u6765\u4f3c\u662f\u4e16\u4ea4\uff0c\u4f46\u542c\u8fd9\u8f66\u9f0e\u4e30\u6240\u8bf4\uff0c\u4ed6\u548c\u8fd9\u5c11\u5e74\u7684\u7236\u4eb2\u4e00\u522b\u4e09\u5341\u4f59\u5e74\uff0c\u90a3\u4e48\u4ed6\u4eec \u79bb\u522b\u4e4b\u65f6\uff0c\u8fd9\u5c11\u5e74\u4e00\u5b9a\u8fd8\u6ca1\u6709\u51fa\u751f\uff0c\u4f55\u4ee5\u8f66\u9f0e\u4e30\u4e00\u89c1\u4ed6\u4fbf\u53eb\u4ed6\u505a\u201c\u674e\u516c\u5b50\u201d\uff0c\u597d\u50cf\u65e9\u5df2\u77e5\u9053 \u4e86\u8fd9\u5c11\u5e74\u7684\u6765\u5934\uff1f \u3000\u3000\u90a3\u5c11\u5e74\u548c\u8f66\u9f0e\u4e30\u8c08\u4e86\u4e00\u4f1a\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u53eb\u8336\u535a\u58eb\u8fc7\u6765\u7ed3\u5e10\uff0c\u8001\u8005\u4e5f\u7ad9\u4e86\u8d77\u6765\uff0c\u4f5c\u52bf\u6b32 \u8d70\u3002\u90a3\u5c11\u5e74\u5ffd\u7136\u53c8\u5750\u4e86\u4e0b\u6765\uff0c\u773c\u770b\u5916\u9762\uff0c\u9732\u51fa\u60ca\u8bb6\u4e4b\u5bb9\u3002\u5415\u56db\u5a18\u8f6c\u5934\u4e00\u671b\uff0c\u4f46\u89c1\u7af9\u5e18\u5f00\u5904\uff0c \u8d70\u8fdb\u6765\u4e09\u4e2a\u5973\u4eba\uff0c\u4e8c\u4e2a\u662f\u9752\u8863\u5987\u4eba\uff0c\u4e00\u4e2a\u662f\u674e\u660e\u73e0\uff0c\u8fd8\u6709\u4e00\u4e2a\u662f\u53ea\u6709\u5341\u56db\u4e94\u5c81\u6837\u5b50\u7684\u5c0f\u59d1 \u5a18\uff0c\u751f\u5f97\u975e\u5e38\u53ef\u7231\uff0c\u8fdb\u6765\u65f6\u5fae\u5fae\u4e00\u7b11\uff0c\u53f3\u8138\u73b0\u51fa\u4e00\u4e2a\u6df1\u6df1\u7684\u9152\u6da1\uff0c\u987f\u89c9\u6ee1\u5ea7\u751f\u6625\uff0c\u5e73\u6dfb\u751f\u6c14\u3002 \u3000\u3000\u5415\u56db\u5a18\u5fc3\u4e2d\u4e00\u60ca\uff0c\u4f46\u89c9\u9c7c\u5a18\u7684\u624b\u5fae\u5fae\u98a4\u6296\u3002\u5415\u56db\u5a18\u77e5\u9053\u8fd9\u9752\u8863\u5c11\u5973\u4e00\u5b9a\u5c31\u662f\u90a3\u65e5\u6349\u5979\u7684 \u4eba\u4e86\uff0c\u6025\u5fd9\u628a\u5979\u7684\u624b\u7d27\u7d27\u634f\u4e86\u4e00\u4e0b\uff0c\u793a\u610f\u53eb\u5979\u9547\u9759\u3002 \u3000\u3000\u9c7c\u5a18\u4e00\u60f3\uff0c\u81ea\u5df1\u5df2\u7ecf\u6539\u4e86\u7537\u88c5\uff0c\u90a3\u9752\u8863\u5987\u4eba\u672a\u5fc5\u770b\u5f97\u51fa\u6765\u3002\u800c\u4e14\u53c8\u6709\u6c5f\u5357\u5927\u4fa0\u7518\u51e4\u6c60\u548c \u5415\u56db\u5a18\u5728\u5ea7\uff0c\u5373\u8bb8\u771f\u7684\u7ed9\u5979\u9752\u7834\uff0c\u6253\u5c06\u8d77\u6765\uff0c\u81ea\u5df1\u8fd9\u8fb9\u4e5f\u4e00\u5b9a\u4e0d\u4f1a\u843d\u8d25\uff0c\u4f55\u5fc5\u6015\u5979\u3002\u5982\u6b64\u4e00 \u60f3\uff0c\u5fc3\u91cc\u5bbd\u4e86\u8bb8\u591a\uff0c\u88c5\u505a\u82e5\u65e0\u5176\u4e8b\u7684\u770b\u6e56\u4e0a\u98ce\u666f\u3002 \u3000\u3000\u674e\u660e\u73e0\u8d70\u4e86\u8fdb\u6765\uff0c\u4e5f\u62e3\u4e00\u5f20\u9760\u7740\u680f\u6746\u7684\u684c\u5b50\u5750\u4e0b\uff0c\u62c9\u7740\u90a3\u5973\u5b69\u5b50\u7684\u624b\u7b11\u9053\uff1a\u201c\u5c0f\u59b9\u59b9\uff0c \u4f60\u770b\u8fd9\u91cc\u7684\u666f\u8272\u6bd4\u4eac\u57ce\u5317\u6d77\u5982\u4f55\uff1f\u201d\u90a3\u5973\u5b69\u5b50\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u4e24\u53ea\u773c\u775b\u5706\u6e9c\u6e9c\u7684\u56db\u9762\u626b\u5c04\u3002 \u3000\u3000\u6b63\u5f53\u6b64\u9645\uff0c\u90a3\u7f8e\u5c11\u5e74\u84e6\u7136\u53c8\u7ad9\u4e86\u8d77\u6765\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u745b\u59b9\uff0c\u4f60\u600e\u4e48\u4e5f\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u201d\u674e \u660e\u73e0\u90a3\u684c\u4e09\u4eba\u90fd\u7ffb\u8d77\u773c\u775b\u770b\u4ed6\uff0c\u5374\u65e0\u4e00\u4eba\u56de\u7b54\u3002\u90a3\u7f8e\u5c11\u5e74\u6025\u6b65\u884c\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u745b\u59b9\uff0c\u4f60\u600e\u4e48 \u5566\uff1f\u4e09\u5e74\u4e0d\u89c1\uff0c\u4f60\u5c31\u5fd8\u8bb0\u6211\u4e86\uff1f\u201d\u4f38\u624b\u62c9\u90a3\u5973\u5b69\u3002 \u3000\u3000\u90a3\u5973\u8be5\u8f7b\u8f7b\u4e00\u95ea\uff0c\u53cd\u624b\u4e00\u638c\u63b4\u53bb\uff0c\u7f8e\u5c11\u5e74\u51e0\u4e4e\u7ed9\u5979\u6253\u4e2d\uff0c\u6025\u5fd9\u8df3\u5f00\u4e24\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u745b \u59b9\uff0c\u4f60\u75af\u4e86\u5417\uff1f\u201d\u90a3\u5973\u5b69\u5b50\u9a82\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u745b\u59b9\uff1f\u201d\u53cc\u8db3\u4e00\u8dc3\uff0c\u6325\u638c\u53c8\u62cd\uff0c\u5c11\u5e74\u8db3\u8ddf\u4e00 \u65cb\uff0c\u8f6c\u4e86\u4e24\u4e2a\u5706\u5708\uff0c\u90a3\u5973\u5b69\u5b50\u8eab\u6cd5\u597d\u5feb\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u5df2\u53d1\u4e86\u51e0\u638c\uff0c\u800c\u4e14\u6bcf\u4e00\u5f0f\u90fd\u4e0d\u76f8\u540c\uff0c\u6bcf \u4e00\u62db\u90fd\u662f\u8fa3\u624b\u3002\u7518\u51e4\u6c60\u5927\u5403\u4e00\u60ca\uff1a\u8fd9\u5973\u5b69\u5b50\u5206\u660e\u662f\u5f97\u8fc7\u9ad8\u4eba\u4f20\u6388\uff0c\u800c\u4e14\u6240\u5b66\u7684\u4e0d\u6b62\u4e00\u5bb6\uff01 \u3000\u3000\u8fd9\u5973\u5b69\u5b50\u51fa\u624b\u5947\u7edd\uff0c\u800c\u90a3\u5c11\u5e74\u7684\u8eab\u6cd5\u66f4\u5947\uff0c\u53ea\u89c1\u4ed6\u75be\u8f6c\u51e0\u4e2a\u5706\u5708\uff0c\u90a3\u5973\u5b69\u5b50\u6bcf\u4e00\u638c\u90fd\u4f3c \u4e4e\u5c31\u8981\u6253\u4e2d\uff0c\u5374\u53c8\u638c\u638c\u843d\u7a7a\u3002\u5c11\u5e74\u53c8\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4e0d\u8ba4\u5f97\u6211\uff0c\u96be\u9053\u8fd9\u5957\u529f\u592b\u90fd\u5fd8\u8bb0\u4e86\uff1f\u6211 \u4e0d\u662f\u6559\u4f60\u7ec3\u8fc7\u7684\u5417\uff1f\u201d \u3000\u3000\u5973\u5b69\u5b50\u9a82\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u53ea\u51ed\u8fd9\u70b9\u4f0e\u4fe9\uff0c\u4f60\u5c31\u914d\u505a\u6211\u7684\u6559\u5e08\u3002\u4f60\u8981\u505a\u6211\u7684\u6559\u5e08\uff0c\u8fd8\u5f97 \u62ff\u51fa\u4e00\u70b9\u771f\u5b9e\u7684\u529f\u592b\u6765\uff01\u201d\u638c\u6cd5\u66f4\u7d27\uff0c\u800c\u4e14\u5ffd\u62f3\u5ffd\u638c\uff0c\u5ffd\u7136\u53c8\u9a88\u6307\u5982\u621f\uff0c\u70b9\u90a3\u5c11\u5e74\u7a74\u9053\uff0c\u5c11 \u5e74\u53ea\u662f\u8eb2\u95ea\uff0c\u51e0\u4e4e\u7ed9\u5979\u70b9\u4e2d\uff0c\u6025\u5fd9\u817e\u51fa\u53f3\u638c\u76f8\u6297\uff0c\u5e76\u4f38\u51fa\u5de6\u624b\u51fb\u5979\u3002\u90a3\u5973\u5b69\u5b50\u500f\u7684\u51cc\u7a7a\u6251 \u8d77\uff0c\u4f38\u5f00\u5341\u6307\uff0c\u5411\u4ed6\u8111\u95e8\u6293\u4e0b\uff0c\u5c11\u5e74\u5927\u5403\u4e00\u60ca\uff0c\u75be\u5fd9\u540e\u9000\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u771f\u7684\u75af\u4e86\u5417\uff1f\u4f60\u90a3\u91cc \u5b66\u6765\u7684\u8fd9\u79cd\u90aa\u95e8\u6b6a\u9053\u7684\u529f\u592b\uff1f\u201d\u5415\u56db\u5a18\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u5973\u5b69\u5b50\u529f\u592b\u4e4b\u6742\uff0c\u7adf\u662f\u5979\u751f\u5e73\u4ece\u6240\u672a \u89c1\uff0c\u5c11\u6797\u6d3e\u3001\u65e0\u6781\u6d3e\u3001\u96ea\u5c71\u6d3e\u7684\u62e5\u6709\uff0c\u800c\u4e14\u521a\u624d\u8fd9\u4e00\u6293\u8fd8\u662f\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u7684\u72ec\u95e8\u529f\u592b\u3002 \u3000\u3000\u90a3\u5973\u5b69\u5b50\u6325\u62f3\u518d\u4e0a\uff0c\u9752\u8863\u5987\u4eba\u5ffd\u7136\u8dc3\u8d77\uff0c\u7eb5\u8fc7\u51e0\u5f20\u684c\u9762\uff0c\u4e00\u6293\u5411\u90a3\u5c11\u5e74\u6293\u6765\uff0c\u53eb\u9053\uff1a \u201c\u5c0f\u59b9\u59b9\uff0c\u4f60\u9000\u4e0b\u3002\u8ba9\u6211\u6765\u6349\u8fd9\u4e2a\u75af\u5b50\uff01\u201d\u5c11\u5e74\u4e00\u95ea\uff0c\u51e0\u4e4e\u7ed9\u5979\u6293\u7740\u80a9\u5934\uff0c\u614c\u5fd9\u8df3\u8fc7\u4e00\u5f20\u684c \u5b50\uff0c\u9752\u8863\u5987\u4eba\u624b\u81c2\u66b4\u4f38\uff0c\u4e00\u638c\u51fb\u53bb\uff0c\u90a3\u5c11\u5e74\u53cc\u638c\u4e00\u62b5\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u8eab\u8eaf\u6447\u6643\uff0c\u53c8 \u8df3\u8fc7\u4e00\u5f20\u85e4\u684c\u3002\u9752\u8863\u5987\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u914d\u95ee\u6211\uff1f\u201d\u731b\u8d77\u4e00\u638c\uff0c\u8feb\u51fb\u8fc7\u53bb\uff0c\u638c\u98ce\u52b2\u75be\u975e \u51e1\uff0c\u5c11\u5e74\u7a81\u7136\u4e3e\u8d77\u684c\u5b50\u4e00\u6321\uff0c\u53ea\u542c\u5f97\u7830\u7136\u5de8\u54cd\uff0c\u684c\u5b50\u7ed9\u638c\u529b\u9707\u6210\u7c89\u788e\uff01\u8336\u535a\u58eb\u60ca\u53eb\u9053\uff1a\u201c\u5ba2 \u5b98\uff0c\u6709\u8bdd\u597d\u8bf4\uff0c\u53ef\u522b\u5728\u8fd9\u91cc\u6253\u67b6\u554a\uff01\u201d \u3000\u3000\u9752\u8863\u5987\u4eba\u90a3\u80af\u7406\u4f1a\uff0c\u5728\u8336\u5ea7\u91cc\u7a7f\u6765\u63d2\u53bb\uff0c\u8ffd\u90a3\u5c11\u5e74\u3002\u8336\u535a\u58eb\u8eb2\u5230\u91cc\u5ea7\uff0c\u6709\u51e0\u4e2a\u8336\u5ba2\u65e9\u9003 \u4e86\u51fa\u53bb\u3002\u90a3\u8f66\u8001\u5934\u8eb2\u5230\u5899\u8fb9\uff0c\u4e5f\u662f\u8fde\u58f0\u53eb\u9053\uff1a\u201c\u597d\u7aef\u7aef\u7684\u6253\u4ec0\u4e48\u67b6\u554a\uff01\u201d \u3000\u3000\u5415\u7518\u7b49\u56db\u4eba\u4e5f\u8eb2\u5230\u5899\u8fb9\uff0c\u770b\u90a3\u9752\u8863\u5987\u4eba\u8d8a\u6253\u8d8a\u8d77\u52b2\uff0c\u638c\u98ce\u547c\u547c\uff0c\u90a3\u8fce\u7fe0\u8f69\u4e2d\u7684\u5341\u51e0\u5f20\u684c \u5b50\uff0c\u6709\u597d\u51e0\u5f20\u5df2\u7ed9\u6253\u7ffb\uff0c\u5176\u4f59\u8336\u684c\u4e0a\u7684\u676f\u5b50\u4e5f\u5168\u7ed9\u638c\u98ce\u9707\u788e\uff0c\u54d7\u5566\u5566\u7684\u4e00\u7247\u54cd\u58f0\uff01 \u3000\u3000\u9752\u8863\u5987\u4eba\u51fa\u624b\u52b2\u75be\uff0c\u663e\u7136\u529f\u529b\u6781\u9ad8\uff1b\u4f46\u90a3\u5c11\u5e74\u4e5f\u81ea\u4e0d\u5f31\uff0c\u8eab\u6cd5\u98d8\u5ffd\u5982\u98ce\uff0c\u672c\u6765\u8336\u5c4b\u4e4b \u5185\uff0c\u5730\u65b9\u867d\u4e5f\u4e0d\u7b97\u5f88\u5c0f\uff0c\u4f46\u5230\u5e95\u4e0d\u6bd4\u7a7a\u65f7\u4e4b\u5730\uff0c\u53ef\u4ee5\u968f\u610f\u65bd\u5c55\uff0c\u66f4\u517c\u90a3\u88ab\u6253\u7ffb\u7684\u684c\u6905\uff0c\u6a2a\u4e03 \u76f4\u516b\u7684\u963b\u5728\u5730\u4e0a\uff0c\u8fdb\u653b\u9000\u5b88\u90fd\u53d7\u9650\u5236\u3002\u90a3\u5c11\u5e74\u4ed7\u7740\u8eab\u6cd5\u8f7b\u7075\uff0c\u5de6\u8fb9\u4e00\u515c\uff0c\u53f3\u9762\u4e00\u7ed5\uff0c\u5c45\u7136\u4f3c \u5f69\u8776\u7a7f\u82b1\u822c\u7684\u5728\u684c\u6905\u95f4\u7a7f\u6765\u7ed5\u53bb\u3002\u9752\u8863\u5987\u4eba\u4e00\u8fde\u6253\u7ffb\u4e86\u4e03\u516b\u526f\u684c\u6905\uff0c\u4ecd\u662f\u6253\u4ed6\u4e0d\u7740\u3002\u5c11\u5e74\u559d \u9053\uff1a\u201c\u8001\u4e5e\u5a46\uff0c\u4f60\u600e\u4e48\u8fd9\u6837\u86ee\u4e0d\u8bb2\u7406\uff1f\u201d\u9752\u8863\u5987\u4eba\u9053\uff1a\u201c\u8c01\u548c\u4f60\u8fd9\u75af\u5b50\u8bb2\u7406\uff1f\u201d\u638c\u98ce\u66f4\u70c8\uff0c \u4e0d\u4e00\u523b\u5e97\u4e2d\u7684\u684c\u6905\u5168\u7ed9\u6253\u7ffb\uff0c\u9752\u8863\u5987\u4eba\u7d22\u6027\u8e0f\u5728\u88ab\u6253\u7ffb\u7684\u684c\u6905\u4e4b\u6b62\uff0c\u5411\u5c11\u5e74\u8ffd\u51fb\u3002\u5c11\u5e74\u9053\uff1a \u201c\u4e0d\u8981\u6bc1\u4e86\u4eba\u5bb6\u7684\u5730\u65b9\uff0c\u8981\u6253\u54b1\u4eec\u53e6\u7ea6\u4e2a\u5730\u65b9\uff01\u201d\u9752\u8863\u5987\u4eba\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u5230\u5916\u9762\u53bb\uff01\u201d\u5c11 \u5e74\u559d\u9053\uff1a\u201c\u8fd9\u91cc\u4e43\u6e56\u5c71\u80dc\u5730\uff0c\u5e76\u975e\u8f83\u6280\u4e4b\u573a\uff0c\u4f60\u8981\u6253\uff0c\u54b1\u4eec\u660e\u65e5\u5230\u4e5d\u6eaa\u5341\u516b\u6da7\u53bb\u89c1\u4e2a\u9ad8 \u4e0b\u3002\u201d\u5415\u56db\u5a18\u6697\u6697\u70b9\u5934\uff0c\u5fc3\u60f3\uff1a\u8fd9\u5c11\u5e74\u61c2\u5f97\u7231\u62a4\u98ce\u666f\uff0c\u5012\u662f\u53ef\u4eba\u3002 \u3000\u3000\u90a3\u6599\u9752\u8863\u5987\u4eba\u5374\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u8c01\u4e2d\u4f60\u8fd9\u7f13\u5175\u4e4b\u8ba1\uff01\u201d\u624b\u5e95\u4e1d\u6beb\u4e0d\u7f13\uff0c\u8fd9\u65f6\u684c\u6905\u90fd\u5df2\u6253 \u7ffb\uff0c\u9752\u8863\u5987\u4eba\u5c55\u5f00\u6885\u82b1\u6869\u7684\u8eab\u6cd5\u6b65\u6cd5\uff0c\u8fde\u8fde\u5411\u5c11\u5e74\u8fdb\u903c\u3002\u5c11\u5e74\u907f\u65e0\u53ef\u907f\uff0c\u53ea\u597d\u4e5f\u8e0f\u5728\u6253\u7ffb\u7684 \u684c\u6905\u4e0a\u548c\u5979\u5bf9\u6297\uff1b\u6253\u4e86\u4e00\u9635\uff0c\u6e10\u6e10\u5904\u5728\u4e0b\u98ce\u3002 \u3000\u3000\u7518\u51e4\u6c60\u5fcd\u65e0\u53ef\u5fcd\uff0c\u6b63\u60f3\u51fa\u624b\u529d\u5f00\u3002\u5415\u56db\u59d0\u5ffd\u7136\u62c9\u4ed6\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8d70\uff01\u201d\u7518\u51e4\u6c60\u6115\u7136 \u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u8d70\uff1f\u201d\u5415\u56db\u5a18\u671d\u5916\u9762\u4e00\u6307\uff0c\u53ea\u89c1\u6e56\u9762\u4e0a\u53c8\u662f\u4e00\u53ea\u5c0f\u8247\u6447\u6765\uff0c\u8239\u5934\u7ad9\u7740\u4e2a\u80d6\u548c \u5c1a\uff0c\u624b\u63d0\u4e00\u6839\u6597\u5927\u7684\u7985\u6756\uff0c\u62ab\u895f\u8fce\u98ce\uff0c\u8fd9\u548c\u5c1a\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u81ea\u5df1\u7684\u5927\u5e08\u5144\u4e86\u56e0\uff01 \u3000\u3000\u7518\u51e4\u6c60\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u7684\u6b66\u529f\u534a\u662f\u4e86\u56e0\u6240\u6388\uff0c\u867d\u7136\u4ed6\u65e9\u5df2\u77e5\u9053\u4e86\u56e0\u80cc\u5e08\u53db\u4e49\uff0c\u65e9\u5df2\u548c\u4ed6\u5272 \u65ad\u4e86\u5144\u5f1f\u4e4b\u60c5\uff0c\u4f46\u8fd9\u6b21\u8fd8\u662f\u4e86\u56e0\u53db\u5e08\u540e\u548c\u4ed6\u7684\u7b2c\u4e00\u6b21\u76f8\u89c1\uff0c\u5728\u8fd9\u6b21\u521d\u89c1\u7684\u5239\u90a3\uff0c\u4ed6\u672c\u80fd\u7684\u8fd8 \u628a\u4e86\u56e0\u5f53\u5e08\u5144\u770b\u5f85\uff0c\u60f3\u8d77\u4ed6\u66fe\u4f20\u8fc7\u81ea\u5df1\u6b66\u529f\uff0c\u4e00\u65f6\u95f4\u62ff\u4e0d\u5b9a\u4e3b\u610f\u8981\u4e0d\u8981\u4e0a\u53bb\u548c\u4ed6\u5bf9\u654c\u3002 \u3000\u3000\u5415\u56db\u5a18\u7684\u6b66\u529f\u5168\u662f\u72ec\u81c2\u795e\u5c3c\u6240\u6388\uff0c\u5fc3\u7406\u4e0a\u5bf9\u4e86\u56e0\u65e0\u6240\u754f\u4ff1\uff0c\u89c1\u7518\u51e4\u6c60\u7741\u5927\u4e86\u773c\uff0c\u4fbf\u9053\uff1a \u201c\u54b1\u4eec\u4e24\u4eba\u8054\u624b\u6597\u4ed6\uff0c\u7a33\u64cd\u80dc\u7b97\uff0c\u4f46\u4e00\u6765\u4e0d\u77e5\u4ed6\u540e\u9762\u8fd8\u6709\u6ca1\u6709\u51f6\u4eba\uff0c\u4e8c\u6765\u6050\u9632\u6bc1\u574f\u6e56\u5c71\u80dc \u8ff9\uff0c\u8fd8\u662f\u8d70\u5427\uff01\u201d \u3000\u3000\u90a3\u8f66\u8001\u5934\u8eab\u5b50\u8d34\u5899\uff0c\u770b\u5f97\u60ca\u5fc3\u548b\u820c\uff0c\u8fde\u8fde\u53eb\u9053\uff1a\u201c\u505c\u624b\uff0c\u505c\u624b\uff0c\u6709\u8bdd\u597d\u8bf4\uff0c\u8fd9\u6837\u86ee\u6253\u5e72 \u561b\uff1f\u201d\u9752\u8863\u5987\u4eba\u5ffd\u5730\u51b7\u7b11\u9053\uff1a\u201c\u7740\u5440\uff01\u6211\u51e0\u4e4e\u5fd8\u8bb0\u4e86\u8fd9\u75af\u5b50\u8fd8\u6709\u4e2a\u540c\u4f34\uff0c\u745b\u513f\uff0c\u628a\u90a3\u7cdf\u8001\u5934 \u62ff\u4e0b\u6765\uff01\u201d\u90a3\u5973\u5b69\u5b50\u7b11\u5e94\u4e00\u58f0\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u5c0f\u624b\u4e00\u4f38\u5411\u8f66\u9f0e\u4e30\u5f53\u5934\u6293\u4e0b\u3002 \u3000\u3000\u8f66\u9f0e\u4e30\u53cc\u638c\u4e00\u5206\uff0c\u4ee5\u4e3a\u6765\u4eba\u5230\u5e95\u662f\u4e2a\u5341\u591a\u5c81\u7684\u5c0f\u5b69\uff0c\u53ea\u60f3\u683c\u5f00\u4fbf\u7b97\uff0c\u90a3\u6599\u8fd9\u5973\u5b69\u5b50\u5e74\u7eaa \u867d\u5c0f\uff0c\u624b\u5e95\u6781\u8fa3\uff0c\u4e00\u6293\u672a\u843d\uff0c\u75be\u7684\u53d8\u62db\uff0c\u5de6\u638c\u4e00\u6258\u654c\u4eba\u8098\u5c16\uff0c\u53f3\u624b\u4e00\u4e2a\u9f99\u5f62\u7a7f\u638c\uff0c\u5728\u654c\u624b\u81c2 \u5f2f\u4e00\u65ab\u4e00\u62c9\uff0c\u8f66\u8001\u5934\u60e8\u53eb\u4e00\u58f0\uff0c\u53f3\u624b\u767b\u65f6\u8131\u81fc\uff0c\u5973\u5b69\u5de6\u638c\u4e00\u62db\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u53c8\u6253\u4e2d\u4e86\u4ed6\u7684\u80f8 \u819b\uff01 \u3000\u3000\u8fd9\u65f6\u4e86\u56e0\u7684\u5c0f\u8239\u5df2\u8d8a\u6765\u8d8a\u8fd1\u3002 \u3000\u3000\u5415\u56db\u5a18\u548c\u7518\u51e4\u6c60\u6b63\u60f3\u8dd1\u51fa\uff0c\u8033\u542c\u90a3\u8001\u5934\u60e8\u53eb\u4e4b\u58f0\uff0c\u52c3\u7136\u5927\u6012\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f60\u53bb\u6551\u90a3\u8001 \u5934\uff0c\u6211\u52a9\u8fd9\u5c11\u5e74\u4e00\u638c\u3002\u201d\u5415\u56db\u5a18\u4e0d\u5f85\u7518\u51e4\u6c60\u8bf4\u5b8c\uff0c\u8eab\u5f62\u65e9\u5df2\u98de\u8d77\u3002\u500f\u5730\u51cc\u7a7a\u6251\u4e0b\uff0c\u4e00\u6293\u6293\u7740 \u8f66\u9f0e\u4e30\u7684\u540e\u5fc3\uff0c\u5c06\u4ed6\u63d0\u4e86\u8d77\u6765\u3002\u90a3\u5973\u5b69\u5b50\u7adf\u662f\u521d\u751f\u4e4b\u728a\u4e0d\u754f\u864e\uff0c\u773c\u89c1\u654c\u4eba\u8f7b\u529f\u5353\u7edd\uff0c\u7adf\u7136\u6beb \u4e0d\u754f\u60e7\uff0c\u4e58\u7740\u5415\u56db\u5a18\u8eab\u5f62\u672a\u7a33\uff0c\u53cc\u6307\u4e00\u6233\uff0c\u75be\u5982\u7535\u5149\u77f3\u706b\uff0c\u7adf\u5411\u5415\u56db\u5a18\u7684\u201c\u66f2\u5730\u7a74\u201d\u6233\u6765\u3002 \u8fd9\u4e00\u62db\u4f7f\u5f97\u597d\u4e0d\u9634\u8fa3\uff01 \u3000\u3000\u4f46\u5415\u56db\u5a18\u662f\u4f55\u7b49\u4eba\u7269\uff01\u90a3\u5973\u5b69\u5b50\u8fd9\u4e00\u62db\u201c\u9634\u624b\u201d\uff0c\u5982\u4f55\u4f1a\u6233\u5f97\u4e2d\u5979\uff1f\u53ea\u89c1\u5979\u8eab\u5f62\u5fae\u95ea\uff0c \u5de6\u638c\u201c\u5530\u201d\u7684\u4e00\u58f0\u63a8\u51fa\uff0c\u628a\u5973\u5b69\u8fd9\u4e00\u62db\u5316\u4e86\uff0c\u90a3\u5973\u5b69\u8eab\u4f53\u6643\u4e86\u4e24\u6643\uff0c\u7a81\u7136\u5c0f\u817f\u4e00\u4f38\uff0c\u4e00\u8bb0 \u201c\u6a2a\u6c5f\u8e22\u6597\u201d\u6a2a\u626b\u8fc7\u6765\uff0c\u53f3\u638c\u53cd\u624b\u4e00\u5288\uff0c\u5de6\u624b\u5408\u6307\u4e00\u62ff\uff0c\u5c45\u7136\u662f\u64d2\u62ff\u624b\u7684\u62db\u6570\u3002\u5415\u56db\u5a18\u5411\u540e \u4e00\u659c\u8eab\uff0c\u5c06\u8f66\u9f0e\u4e30\u5e26\u8fc7\u8eab\u540e\uff0c\u5355\u624b\u4e00\u7a7f\uff0c\u628a\u5973\u5b69\u7684\u64d2\u62ff\u624b\u89e3\u5f00\uff1a\u90a3\u5973\u5b69\u5b50\u72b9\u81ea\u4e0d\u77e5\u8fdb\u9000\uff0c\u55b3 \u7684\u4e24\u638c\u659c\u5206\uff0c\u638c\u52bf\u76f4\u5288\u51fa\u53bb\u3002\u5415\u56db\u5a18\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u5355\u638c\u4e00\u6c89\u4e00\u63a8\uff0c\u5316\u4e3a\u201c\u987a\u6c34\u63a8\u821f\u201d\uff0c\u8fd9 \u4e00\u638c\u6697\u542b\u5185\u529b\uff0c\u4e0d\u4f46\u628a\u5973\u5b69\u7684\u638c\u52bf\u62c6\u5f00\uff0c\u800c\u4e14\u628a\u5979\u7684\u638c\u5c01\u4f4f\uff0c\u53eb\u5979\u62db\u5f0f\u53d1\u4e0d\u51fa\u53bb\uff0c\u8981\u64a4\u4e5f\u64a4 \u4e0d\u56de\u6765\u3002\u8fd9\u65f6\u5415\u56db\u5a18\u53ea\u8981\u7a0d\u5fae\u7528\u52b2\uff0c\u90a3\u5973\u5b69\u51c6\u53d7\u5185\u4f24\uff0c\u90a3\u5973\u5b69\u5b50\u5c0f\u8138\u6cdb\u7ea2\uff0c\u5b9e\u5728\u53ef\u7231\u3002\u5415\u56db \u5a18\u54ea\u820d\u5f97\u4f24\u5979\u3002\u75be\u7684\u5355\u638c\u4e00\u6536\uff0c\u6293\u8d77\u4e86\u8f66\u9f0e\u4e30\u5f80\u5916\u4fbf\u95ef\u3002\u5973\u5b69\u5b50\u5413\u5f97\u5446\u4e86\uff0c\u501a\u5728\u5899\u8fb9\u5598\u6c14\u3002 \u3000\u3000\u518d\u8bf4\u90a3\u9752\u8863\u5987\u4eba\u6b63\u5c06\u5f97\u624b\uff0c\u84e6\u7136\u7518\u51e4\u6c60\u6a2a\u8dc3\u51fa\u6765\uff0c\u547c\u7684\u4e00\u638c\u5288\u51fa\uff0c\u9752\u8863\u5987\u4eba\u4e00\u542c\u638c\u98ce\u77e5 \u662f\u52b2\u654c\uff0c\u53cc\u638c\u4e00\u5408\uff0c\u54e9\u7684\u4e00\u5206\uff0c\u5e73\u63a8\u51fa\u53bb\uff0c\u8fd9\u4e00\u4e0b\u4ee5\u786c\u78b0\u786c\uff0c\u53cc\u65b9\u624b\u81c2\u90fd\u89c9\u9178\u9ebb\u3002\u9752\u8863\u5987\u4eba \u5403\u4e86\u4e00\u60ca\uff0c\u638c\u6cd5\u4e00\u53d8\uff0c\u5f13\u6b65\u9633\u638c\uff0c\u500f\u7684\u63a8\u51fa\uff0c\u8fd9\u4e00\u62db\u540d\u4e3a\u201c\u8de8\u864e\u767b\u5c71\u201d\uff0c\u6697\u85cf\u9634\u52b2\u3002\u7518\u51e4\u6c60 \u7a81\u4f3c\u9189\u6c49\u6447\u6643\uff0c\u8eab\u8eaf\u5411\u540e\u4e00\u503e\uff0c\u7a81\u7136\u5f80\u4e0b\u4e00\u715e\uff0c\u53f3\u638c\u5f80\u91cc\u4e00\u7a7f\uff0c\u9752\u8863\u5987\u4eba\u638c\u529b\u5230\u5904\uff0c\u53ea\u89c9\u8f6f \u7ef5\u7ef5\u7684\uff0c\u7518\u51e4\u6c60\u731b\u7684\u559d\u58f0\uff1a\u201c\u5012\uff01\u201d\u4e0a\u7a7f\u7684\u53f3\u638c\u5df2\u642d\u5728\u9752\u8863\u5987\u4eba\u5de6\u81c2\u5e95\u4e0b\uff0c\u5de6\u638c\u4e5f\u5e73\u51fb\u5979\u8033 \u65c1\u7684\u201c\u592a\u9633\u7a74\u201d\u3002\u8fd9\u4e00\u624b\u4e43\u662f\u72ec\u81c2\u795e\u5c3c\u79d8\u4f20\u7684\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u529f\u592b\uff0c\u72ec\u81c2\u795e\u5c3c\u95e8\u4e0b\uff0c\u53ea\u6709 \u4e86\u56e0\u548c\u7518\u51e4\u6c60\u4f1a\u4f7f\uff0c\u8fde\u5415\u56db\u5a18\u90fd\u56e0\u4e3a\u5728\u5e08\u95e8\u65f6\u5e74\u7eaa\u5c1a\u8f7b\uff0c\u529f\u529b\u672a\u5230\uff0c\u672a\u5f97\u4f20\u6388\u3002\u5bf9\u4ed8\u6b66\u529f\u5e73 \u5eb8\u7684\u4eba\uff0c\u4e0d\u7528\u51fa\u624b\uff0c\u53ea\u987b\u501f\u529b\u4f7f\u529b\uff0c\u4fbf\u53ef\u5c06\u654c\u4eba\u6454\u5012\u3002\u7518\u51e4\u6c60\u56e0\u77e5\u8fd9\u9752\u8863\u5987\u4eba\u4e43\u662f\u52b2\u654c\uff0c\u6240 \u4ee5\u4e00\u9762\u7528\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u4e0a\u4e58\u5185\u5bb6\u529f\u592b\u6d88\u5979\u638c\u529b\uff0c\u4e00\u9762\u8fde\u7528\u5206\u7b4b\u9519\u9aa8\u548c\u5927\u6454\u7891\u624b\u7684\u529f\u592b\u53cd \u51fb\u654c\u4eba\uff01 \u3000\u3000\u90a3\u9752\u8863\u5987\u4eba\u731d\u4e0d\u53ca\u9632\uff0c\u5de6\u81c2\u5df2\u88ab\u7518\u51e4\u6c60\u62ff\u7740\uff0c\u8eab\u5b50\u5411\u524d\u4fef\u8dcc\uff0c\u4f46\u5979\u5185\u529f\u6df1\u6e5b\uff0c\u7adf\u7136\u5728\u6447 \u6447\u6b32\u5012\u4e4b\u9645\uff0c\u624b\u8098\u4e00\u649e\uff0c\u501f\u7518\u51e4\u6c60\u90a3\u4e00\u51fb\u4e4b\u529b\uff0c\u5411\u524d\u4e00\u51b2\uff0c\u7518\u51e4\u6c60\u542b\u80f8\u5438\u8179\uff0c\u6b63\u60f3\u518d\u8fd0\u201c\u6cbe \u8863\u5341\u516b\u8dcc\u201d\u7684\u529f\u592b\u6d88\u5979\u90a3\u4e00\u649e\u4e4b\u52bf\uff0c\u90a3\u77e5\u5979\u90a3\u4e00\u649e\u7adf\u662f\u865a\u62db\uff0c\u8eab\u5b50\u5411\u524d\u4e00\u51b2\uff0c\u6c89\u80a9\u5782\u8098\uff0c\u5c45 \u7136\u5316\u4e86\u7518\u51e4\u6c60\u7684\u5185\u5bb6\u771f\u529b\u3002 \u3000\u3000\u7518\u51e4\u6c60\u53f3\u638c\u62cd\u53bb\uff0c\u638c\u98ce\u4ece\u9752\u8863\u5987\u4eba\u8033\u65c1\u626b\u8fc7\uff0c\u7adf\u6ca1\u4f24\u5f97\u7740\u5979\u3002\u9752\u8863\u5987\u4eba\u5de6\u81c2\u5df2\u8131\u4e86\u51fa \u6765\uff0c\u8e09\u8e09\u8dc4\u8dc4\u659c\u51b2\u6570\u6b65\uff0c\u8fd9\u51e0\u62db\u9669\u6781\u51f6\u6781\uff0c\u770b\u5f97\u90a3\u7f8e\u5c11\u5e74\u4e5f\u76ee\u7729\u795e\u6447\u3002 \u3000\u3000\u7518\u51e4\u6c60\u8fde\u8fd0\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u529f\u592b\uff0c\u5c45\u7136\u6ca1\u6709\u6454\u5012\u90a3\u9752\u8863\u5987\u4eba\uff0c\u6697\u81ea\u5947\u5f02\uff0c\u8fd9\u65f6\u5415\u56db\u5a18 \u5df2\u51fa\u4e86\u8336\u5c45\uff0c\u767d\u6cf0\u5b98\u548c\u9c7c\u5a18\u4e5f\u8ddf\u4e86\u51fa\u53bb\uff0c\u7518\u51e4\u6c60\u53eb\u90a3\u7f8e\u5c11\u5e74\u9053\uff1a\u201c\u5feb\u9003\uff0c\u8fdf\u5c31\u6765\u4e0d\u53ca\u4e86\uff01\u201d \u7a81\u7136\u4e00\u4fef\u8eab\u5c06\u4e24\u5f20\u51f3\u5b50\u6293\u8d77\uff0c\u5411\u9752\u8863\u5987\u4eba\u731b\u529b\u63b7\u53bb\uff0c\u9752\u8863\u5987\u4eba\u53cc\u638c\u5411\u5916\u4e00\u63a8\uff0c\u4e24\u5f20\u51f3\u5b50\u788e\u6210 \u56db\u5757\u3002\u90a3\u7f8e\u5c11\u5e74\u4e5f\u9003\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u7518\u51e4\u6c60\u6025\u5fd9\u8ffd\u4e0a\u5415\u56db\u5a18\uff0c\u94bb\u8fdb\u8239\u4e2d\uff0c\u626c\u5e06\u5f00\u8d70\u3002\u90a3\u5c11\u5e74\u7684\u5c0f\u8239\u5374\u505c\u5728\u53e6\u4e00\u5904\uff0c\u8fd9\u65f6\u521a\u722c \u4e0a\u8239\u3002\u9752\u8863\u5987\u4eba\u8ffd\u4e86\u51fa\u6765\uff0c\u731b\u89c1\u4e86\u56e0\u7ad9\u5728\u8239\u5934\uff0c\u5c31\u8981\u6765\u5230\u3002\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\uff0c\u8ffd\u90a3\u5c0f \u8d3c\uff01\u201d\u4e86\u56e0\u9053\uff1a\u201c\u54ea\u4e2a\u5c0f\u8d3c\u5440\uff1f\u201d\u9752\u8863\u5987\u4eba\u9053\uff1a\u201c\u54ea\u4e24\u6761\u8239\u4e0a\u7684\u4eba\uff0c\u90fd\u662f\u4ec7\u5bb6\uff0c\u4f60\u5148\u66ff\u6211\u6349 \u5de6\u8fb9\u753b\u80aa\u4e0a\u7684\u90a3\u4e2a\u5c0f\u8d3c\u5427\uff01\u201d \u3000\u3000\u90a3\u7f8e\u5c11\u5e74\u8239\u5df2\u5f00\u51fa\u5341\u4f59\u4e08\u8fdc\uff0c\u4e86\u56e0\u5750\u4e86\u4e0b\u6765\uff0c\u7528\u7985\u6756\u62e8\u6c34\uff0c\u5c0f\u821f\u5982\u7bad\uff0c\u76f4\u5411\u90a3\u5c11\u5e74\u8ffd \u53bb\u3002\u5415\u56db\u5a18\u7684\u8239\u548c\u4e86\u56e0\u7684\u8239\u76f8\u8ddd\u6570\u5341\u4e08\uff0c\u773c\u89c1\u4e86\u56e0\u5c31\u5c06\u53ca\u90a3\u5c11\u5e74\u7684\u753b\u822b\uff0c\u5ffd\u7136\u6389\u8fc7\u8239\u5934\uff0c\u8bf4 \u9053\uff1a\u201c\u4e03\u54e5\uff0c\u90a3\u5c11\u5e74\u770b\u6765\u662f\u6211\u8f88\u4e2d\u4eba\uff0c\u54b1\u4eec\u56de\u53bb\u6551\u4ed6\u4e00\u547d\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u597d\uff0c\u5c0f\u5fc3\u4e00 \u70b9\uff01\u201d\u6389\u8fc7\u8239\u5934\uff0c\u5411\u4e24\u8239\u4e2d\u95f4\u6447\u53bb\u3002 \u3000\u3000\u4e86\u56e0\u7684\u8239\u6765\u5f97\u8fc5\u75be\uff0c\u4e0d\u4e00\u523b\u5c31\u8ffd\u4e0a\u90a3\u652f\u753b\u822b\uff0c\u7f8e\u5c11\u5e74\u9661\u89c1\u4e00\u4e2a\u80d6\u548c\u5c1a\u8ffd\u6765\uff0c\u5a01\u98ce\u51db\u51db\uff0c \u5c31\u50cf\u628a\u5b88\u5c71\u95e8\u7684\u91d1\u521a\u4e00\u822c\uff0c\u4e0d\u7981\u6012\u9053\uff1a\u201c\u6211\u7a76\u7adf\u4e0e\u4f60\u4eec\u4f55\u51a4\u4f55\u4ec7\uff1f\u4f60\u4eec\u8fd9\u4e0d\u662f\u5e73\u767d\u6b3a\u8d1f\u4eba \u5417\uff1f\u201d\u4e86\u56e0\u4e0d\u7406\u4e0d\u776c\uff0c\u63d0\u8d77\u6597\u5927\u7684\u7985\u6756\uff0c\u7ad9\u5728\u8239\u5934\u4e0a\uff0c\u5c31\u547c\u7684\u4e00\u6756\u6253\u6765\uff01\u5c11\u5e74\u89c1\u4ed6\u6765\u52bf\u51f6 \u731b\uff0c\u62d4\u51fa\u5b9d\u5251\u4e00\u683c\uff0c\u201c\u53ee\u5f53\u201d\u4e00\u58f0\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u5b9d\u5251\u51e0\u4e4e\u8131\u624b\u98de\u53bb\u3002 \u3000\u3000\u672c\u6765\u8fd9\u5c11\u5e74\u5251\u6cd5\u6781\u9ad8\uff0c\u82e5\u7136\u662f\u5728\u9646\u4e0a\u548c\u4e86\u56e0\u53ae\u62fc\uff0c\u7eb5\u4e0d\u80fd\u654c\uff0c\u4e5f\u53ef\u6597\u4e2a\u4e94\u4e03\u5341\u62db\uff0c\u4f46\u73b0 \u5728\u5404\u81ea\u7ad9\u5728\u8239\u5934\u4e0a\u52a8\u624b\uff0c\u90a3\u7edd\u5999\u7684\u5251\u62db\uff0c\u65e0\u5904\u65bd\u5c55\uff0c\u5251\u6756\u4ea4\u950b\uff0c\u529b\u5f3a\u8005\u80dc\uff0c\u529b\u5f31\u8005\u8d25\uff0c\u4e86\u56e0 \u62a1\u8d77\u7985\u6756\uff0c\u547c\u547c\u8f70\u8f70\uff0c\u6709\u5982\u6cf0\u5c71\u538b\u9876\uff0c\u76f4\u626b\u4e0b\u6765\u3002\u5c11\u5e74\u62b5\u6321\u4e0d\u4f4f\uff0c\u6643\u8eab\u98de\u4e0a\u8239\u84ec\uff0c\u4e86\u56e0\u7741\u773c \u5927\u559d\u4e00\u58f0\uff0c\u7985\u6756\u843d\u5904\uff0c\u628a\u8239\u8237\u6253\u88c2\uff0c\u6728\u7247\u7eb7\u98de\uff0c\u5c11\u5e74\u5728\u5e06\u5e03\u84ec\u4e0a\uff0c\u7ed9\u9707\u8361\u5f97\u628a\u6301\u4e0d\u4f4f\uff0c\u4e86\u56e0 \u559d\u4e00\u58f0\uff0c\u626b\u4e00\u6756\uff0c\u7b2c\u4e8c\u6756\u626b\u4e0b\uff0c\u8239\u8eab\u503e\u4fa7\uff0c\u7b2c\u4e09\u6756\u626b\u4e0b\uff0c\u8239\u677f\u4e5f\u7ed9\u6253\u5f97\u88c2\u5f00\uff0c\u5c11\u5e74\u5728\u84ec\u4e0a\u4e00 \u4e2a\u5012\u683d\uff0c\u8dcc\u4e0b\u6e56\u4e2d\uff0c\u90a3\u5c0f\u821f\u8986\u5728\u6e56\u9762\uff01 \u3000\u3000\u4e86\u56e0\u7ad9\u5728\u8239\u5934\u5927\u7b11\uff0c\u5ffd\u7136\u5012\u63d0\u7985\u6756\uff0c\u731b\u7684\u63d2\u5165\u6c34\u4e2d\u4e00\u9635\u4e71\u6405\u3002\u6b63\u5728\u6b64\u65f6\uff0c\u5415\u56db\u5a18\u7684\u5c0f\u821f \u75be\u5982\u98de\u7bad\u9a76\u81f3\uff0c\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u90a3\u5c11\u5e74\u5df2\u7ed9\u6253\u7ffb\u6e56\u5e95\uff0c\u600e\u751f\u662f\u597d\uff1f\u201d\u9c7c\u5a18\u9053\uff1a\u201c\u65e0\u59a8\uff0c\u53ea\u8981\u4ed6 \u6ca1\u6253\u6b7b\uff0c\u6211\u53ef\u6551\u4ed6\uff01\u201d\u7a81\u7136\u6251\u901a\u4e00\u58f0\uff0c\u8df3\u4e0b\u6c34\u53bb\u3002 \u3000\u3000\u4e86\u56e0\u5c06\u7985\u6756\u5728\u6c34\u4e2d\u4e00\u9635\u4e71\u6405\uff0c\u7518\u5415\u4e8c\u4eba\u7684\u5c0f\u821f\u5df2\u7ecf\u6765\u5230\u3002\u7518\u51e4\u6c60\u548c\u767d\u6cf0\u5b98\u867d\u7136\u7528\u836f\u5242\u53d8 \u6613\u989c\u5bb9\uff0c\u53ef\u662f\u90a3\u8eab\u88c1\u4f53\u6001\u5374\u7792\u4e0d\u8fc7\u4e86\u56e0\u7684\u773c\u3002\u5176\u5b9e\u53e4\u4ee3\u7684\u201c\u6613\u5bb9\u672f\u201d\u8fd8\u6bd4\u4e0d\u4e0a\u73b0\u4ee3\u9ad8\u660e\u7684\u5316 \u5986\u672f\uff0c\u7792\u4e0d\u5f88\u719f\u7684\u4eba\u81ea\u53ef\uff0c\u8981\u7792\u4eb2\u4eba\u53ef\u96be\u3002\u4e86\u56e0\u53ef\u662f\u770b\u7740\u4ed6\u4eec\u957f\u5927\u7684\u3002\u8fd9\u4e00\u6012\u975e\u540c\u5c0f\u53ef\uff0c\u731b \u7136\u5c06\u7985\u6756\u63d0\u51fa\u6c34\u6765\uff0c\u8fce\u7740\u6765\u8239\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u7518\u51e4\u6c60\uff0c\u4f60\u4e5f\u6765\u4e0e\u6211\u4f5c\u5bf9\uff1f\u201d \u3000\u3000\u7518\u51e4\u6c60\u63d0\u5200\u5728\u624b\uff0c\u7b54\u9053\uff1a\u201c\u5c0f\u5f1f\u4e95\u975e\u6562\u4e0e\u5e08\u5144\u4f5c\u5bf9\uff0c\u82e5\u5e08\u5144\u5c4f\u9664\u540d\u5229\u4e4b\u5fc3\uff0c\u91cd\u9075\u5e08\u5085\u6559 \u8bf2\uff0c\u6211\u4eec\u613f\u5949\u5e08\u5144\u4e3a\u957f\u2026\u2026\u201d\u4e86\u56e0\u4e0d\u5f85\u4ed6\u8bf4\u5b8c\uff0c\u731b\u53c8\u559d\u9053\uff1a\u201c\u82e5\u4e0d\u7136\u5462\uff1f\u201d\u7518\u51e4\u6c60\u51b7\u51b7\u8bf4 \u9053\uff1a\u201c\u82e5\u5e08\u5144\u5b9a\u8981\u56fa\u6301\u5df1\u89c1\uff0c\u5229\u6b32\u718f\u5fc3\uff0c\u80cc\u53db\u5e08\u95e8\uff0c\u4e0d\u987e\u5927\u4e49\uff0c\u90a3\u4e48\u4f60\u5c31\u4e0d\u662f\u672c\u95e8\u4e2d\u4eba\uff0c\u5c0f \u5f1f\u4e5f\u4e0d\u6562\u5949\u4f60\u4e3a\u5144\u957f\u4e86\u3002\u201d\u8fd9\u8bdd\u5c31\u662f\u8bf4\u8981\u201c\u5927\u4e49\u706d\u4eb2\u201d\uff0c\u4e86\u56e0\u6c14\u5f97\u6d53\u7709\u5012\u7ad6\uff0c\u5927\u559d\u9053\uff1a\u201c\u7518 \u51e4\u6c60\uff0c\u90a3\u4eba\u8981\u4e0e\u6211\u4f5c\u5bf9\u72b9\u81ea\u7f62\u4e86\uff0c\u4f60\u4e5f\u8981\u4e0e\u6211\u4f5c\u5bf9\uff1f\u4f60\u4e5f\u4e0d\u60f3\u60f3\u662f\u8c01\u4f20\u4f60\u7684\u6b66\u827a\uff0c\u662f\u8c01\u6210\u5168 \u4e86\u4f60\u6c5f\u5357\u5927\u4fa0\u7684\u5a01\u540d\uff1f\u4f60\u73b0\u5728\u4eba\u5927\u5fd7\u5927\uff0c\u957f\u4e86\u7fc5\u8180\u5c31\u8981\u98de\u4e86\uff1f\u4f60\u77e5\u6069\u4e0d\u62a5\uff0c\u7b97\u4ec0\u4e48\u6c5f\u5357\u5927 \u4fa0\uff1f\u201d\u7518\u51e4\u6c60\u5929\u7980\u6781\u9ad8\uff0c\u4e86\u56e0\u5728\u672a\u53cd\u53db\u4e4b\u524d\uff0c\u5bf9\u4ed6\u9887\u4e3a\u7231\u62a4\uff0c\u6307\u70b9\u4ed6\u7684\u6b66\u827a\u65f6\u4e5f\u7279\u522b\u7528\u5fc3\uff0c \u6240\u4ee5\u5e38\u631f\u6069\u81ea\u91cd\u3002\u4e0d\u8fc7\uff0c\u5b9e\u9645\u8bf4\u6765\uff0c\u7518\u51e4\u6c60\u5f97\u4ed6\u7279\u522b\u7528\u5fc3\u4f20\u6280\u662f\u771f\uff0c\u82e5\u8bf4\u5230\u201c\u6c5f\u5357\u5927\u4fa0\u201d\u7684 \u540d\u5934\uff0c\u5374\u662f\u7518\u51e4\u6c60\u81ea\u5df1\u6323\u6765\u7684\uff0c\u4e0e\u4e86\u56e0\u65e0\u5173\uff0c\u4f46\u4e86\u56e0\u5728\u5e08\u5f1f\u51fa\u5404\u4e4b\u540e\uff0c\u5fc3\u4e2d\u4e0d\u65e0\u5992\u610f\uff0c\u5c24\u5176 \u5728\u5e08\u5f1f\u7684\u540d\u5934\u6bd4\u81ea\u5df1\u66f4\u5927\u4e4b\u540e\uff0c\u66f4\u662f\u4e0d\u6ee1\uff0c\u6240\u4ee5\u9022\u4eba\u9053\u53ca\uff0c\u603b\u662f\u8bf4\u201c\u6c5f\u5357\u5927\u4fa0\u201d\u7684\u540d\u5934\u4e43\u662f \u4ed6\u6240\u529f\u5df1\u5168\u3002\u751a\u81f3\u9762\u5bf9\u7740\u7518\u51e4\u6c60\u4e5f\u5982\u6b64\u8bf4\u3002 \u3000\u3000\u7518\u51e4\u6c60\u5f53\u7136\u7edd\u4e0d\u8ba1\u8f83\u8fd9\u4e9b\uff0c\u542c\u4ed6\u8fd9\u4e48\u4e00\u9a82\uff0c\u5fc3\u4e2d\u53cd\u89c9\u8f9b\u9178\uff0c\u53f9\u606f\u81ea\u5df1\u7684\u5e08\u5144\u53d8\u6210\u4e86\u8fd9 \u6837\u3002\u4e86\u56e0\u89c1\u4ed6\u773c\u5708\u7ea2\u6da6\uff0c\u53ea\u9053\u4ed6\u662f\u5728\u53cd\u6094\u81ea\u8d23\uff0c\u5f53\u4e0b\u5c06\u7985\u6756\u5728\u8239\u5934\u4e00\u987f\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u80fd\u77e5\u9519 \u4fbf\u597d\uff0c\u4f60\u73b0\u5728\u5c31\u968f\u6211\u53bb\u5427\u3002\u6664\uff0c\u767d\u6cf0\u5b98\u4f60\u5462\uff1f\u4f60\u8fd8\u8981\u4e0e\u6211\u4f5c\u5bf9\u5230\u5e95\u5417\uff1f\u201d\u7518\u51e4\u6c60\u6728\u89c9\u4e0d\u5fcd\uff0c \u542c\u4e86\u4e86\u56e0\u6b64\u8bdd\uff0c\u5ffd\u7136\u53cc\u773c\u4e00\u7ffb\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff0c\u77e5\u8fc7\u80fd\u6539\uff0c\u90a3\u662f\u6700\u597d\u6ca1\u6709\uff01\u4f46\u613f\u5e08\u5144\u53cd \u8eac\u81ea\u95ee\uff0c\u5230\u5e95\u662f\u8c01\u9519\u4e86\uff1f\u5e08\u5085\u5341\u516d\u6212\u6761\u7684\u7b2c\u4e00\u6761\u8bf4\u7684\u662f\u4ec0\u4e48\uff1f\u5e08\u5144\u4e4b\u6069\u867d\u6df1\uff0c\u5e08\u95e8\u4e4b\u4e49\u66f4 \u91cd\uff0c\u6211\u5b81\u53ef\u8fdd\u80cc\u5e08\u5144\uff0c\u4e5f\u4e0d\u80fd\u8fdd\u80cc\u5e08\u5085\u7684\u5927\u6212\uff01\u201d \u3000\u3000\u542c\u5230\u6b64\u5904\uff0c\u5415\u56db\u5a18\u7a81\u7136\u6717\u58f0\u53d1\u8bdd\u9053\uff1a\u201c\u4ec0\u4e48\u5e08\u5144\u4e0d\u5e08\u5144\uff0c\u4ed6\u65e9\u5df2\u4e0d\u662f\u6211\u4eec\u7684\u5e08\u5144\u4e86\uff0c\u4e03 \u54e5\uff0c\u4f60\u8fd8\u4e0e\u4ed6\u53d9\u4ec0\u4e48\u5144\u5f1f\u4e4b\u8c0a\uff1f\u201d\u4e86\u56e0\u6012\u773c\u5706\u7741\uff0c\u7985\u6756\u4e00\u987f\uff0c\u602a\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u662f\u4f60\u8fd9\u8d3c \u5a62\u5728\u4e2d\u95f4\u6311\u62e8\uff01\u201d\u7528\u8db3\u5341\u6210\u529b\u91cf\uff0c\u547c\u7684\u4e00\u6756\u626b\u53bb\uff0c\u7518\u51e4\u6c60\u7ad9\u5728\u6700\u524d\uff0c\u4e3e\u5200\u4e00\u6321\uff0c\u9707\u5f97\u864e\u53e3\u6d41 \u8840\uff0c\u4e86\u56e0\u4e5f\u6643\u4e86\u51e0\u6643\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u602a\u4e0d\u5f97\u4ed6\u6709\u90a3\u4e48\u5927\u7684\u540d\u58f0\uff0c\u529f\u592b\u679c\u7136\u662f\u6bd4\u4ee5\u524d\u9ad8\u5f3a\u5f97\u591a\u4e86\uff01 \u3000\u3000\u7518\u51e4\u6c60\u6321\u4e86\u4e00\u62db\uff0c\u77e5\u9053\u81ea\u5df1\u4ecd\u975e\u5e08\u5144\u4e4b\u654c\uff0c\u4e86\u56e0\u7b2c\u4e8c\u6756\u53c8\u5230\uff0c\u7518\u51e4\u6c60\u53cc\u8db3\u8fd0\u52b2\uff0c\u5728\u8239\u9762 \u4e00\u6491\uff0c\u5c0f\u8239\u6a2a\u8fc7\u4e00\u8fb9\uff0c\u907f\u5f00\u8fd9\u6756\uff0c\u4e86\u56e0\u7b2c\u4e09\u6756\u518d\u8d77\uff0c\u5415\u56db\u5a18\u5ffd\u7136\u4e00\u58f0\u957f\u7b11\uff0c\u4ece\u8239\u5934\u4e0a\u7a81\u7136\u98de \u8d77\uff0c\u971c\u534e\u5b9d\u5251\u633d\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u51cc\u7a7a\u51fb\u4e0b\u3002\u4e86\u56e0\u5411\u4e0a\u4e00\u6756\u6325\u53bb\uff0c\u5415\u56db\u5a18\u7684\u5251\u5728\u6756\u8eab\u4e00\u6309\uff0c\u8eab\u5b50 \u7adf\u7ed9\u5f39\u4e0a\u534a\u7a7a\uff0c\u7518\u51e4\u6c60\u5927\u5403\u4e00\u60ca\uff0c\u5415\u56db\u5a18\u5728\u534a\u7a7a\u6253\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u53c8\u4fef\u51b2\u800c\u4e0b\uff0c\u4e86 \u56e0\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4f60\u627e\u6b7b\uff01\u201d\u7985\u6756\u518d\u5411\u4e0a\u64a9\uff0c\u52b2\u98ce\u8361\u5904\uff0c\u5415\u56db\u5a18\u8863\u8882\u98d8\u626c\uff0c\u771f\u5982\u4ed9\u5b50\u51cc\u98ce\uff0c\u59ff \u52bf\u7f8e\u5999\u4e4b\u6781\uff01\u4e86\u56e0\u8fd9\u4e00\u6756\u7528\u8db3\u5185\u529b\uff0c\u5415\u56db\u5a18\u5251\u5c16\u5728\u6756\u5934\u4e00\u70b9\uff0c\u53c8\u7ed9\u53cd\u5f39\u4e0a\u53bb\u3002\u7518\u51e4\u6c60\u628a\u624b\u4e00 \u626c\uff0c\u4e24\u67c4\u5315\u9996\u95ea\u7535\u822c\u7684\u5411\u4e86\u56e0\u63b7\u53bb\uff0c\u5415\u56db\u5a18\u5728\u7a7a\u4e2d\u8f6c\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u8fde\u4eba\u5e26\u5251\u53c8\u843d\u4e86\u4e0b\u6765\uff0c\u5149 \u73af\u98de\u964d\uff0c\u5a01\u52bf\u66f4\u8db3\u60ca\u4eba\u3002\u597d\u4e2a\u4e86\u56e0\uff0c\u5de6\u624b\u4e00\u4f4e\u628a\u7518\u51e4\u6c60\u63b7\u6765\u7684\u4e24\u628a\u5315\u9996\u5df2\u90fd\u63a5\u5230\u624b\uff0c\u7985\u6756\u4e00 \u6325\uff0c\u53c8\u5411\u5149\u73af\u6a2a\u626b\u8fc7\u53bb\uff0c\u5415\u56db\u5a18\u5f13\u978b\u671d\u7740\u7985\u6756\u4e00\u8e0f\uff0c\u8fd9\u4e00\u4e0b\u7ed9\u5f39\u5f97\u66f4\u9ad8\uff0c\u6e56\u4e0a\u7684\u821f\u5b50\u5df2\u90fd\u7ad9 \u5728\u8239\u5934\u89c2\u671b\uff0c\u771f\u7591\u5fc3\u662f\u592a\u6e56\u4ed9\u5973\uff0c\u98de\u843d\u897f\u6e58\u3002 \u3000\u3000\u4e86\u56e0\u548c\u5c1a\u5de6\u624b\u4e00\u626c\uff0c\u4e24\u67c4\u5315\u9996\u53cd\u5411\u7518\u51e4\u6c60\u63b7\u53bb\uff0c\u7518\u51e4\u6c60\u529f\u529b\u4e0d\u53ca\u5e08\u5144\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u8eab\u8eaf \u4e00\u95ea\uff0c\u4e24\u67c4\u98de\u5200\u9489\u5728\u8239\u8231\u677f\u4e0a\u3002\u8fd9\u65f6\u5415\u56db\u5a18\u53c8\u4ece\u7a7a\u4e2d\u98d8\u964d\u4e0b\u6765\uff0c\u5251\u5149\u98de\u6d12\uff0c\u56db\u5ea6\u523a\u843d\uff0c\u8fd9\u6837 \u6253\u6cd5\uff0c\u771f\u662f\u53e4\u4eca\u5c11\u6709\uff0c\u8fde\u7518\u51e4\u6c60\u4e5f\u770b\u5f97\u5446\u4e86\u3002\u4e86\u56e0\u6697\u6697\u5bd2\u5fc3\uff0c\u60f3\u4e0d\u5230\u8fd9\u5c0f\u5e08\u59b9\u7684\u8f7b\u529f\uff0c\u7adf\u7136 \u7cbe\u5999\u5982\u65af\uff0c\u6bd4\u8d77\u5728\u7530\u6a2a\u5c9b\u4e0a\u5b64\u5cf0\u8f83\u6280\u4e4b\u65f6\uff0c\u53c8\u9ad8\u4e86\u4e0d\u77e5\u591a\u5c11\uff01\u5415\u56db\u5a18\u6b63\u5728\u4ed9\u971e\u5cad\u4e94\u5e74\u82e6\u7ec3\uff0c \u8fd9\u65f6\u65bd\u5c55\u51fa\u6765\uff0c\u672c\u4ee5\u662f\u4e00\u51fb\u53ef\u4ee5\u594f\u6548\uff0c\u90a3\u6599\u56db\u5ea6\u88ad\u51fb\uff0c\u4ecd\u662f\u65e0\u9699\u53ef\u5165\uff0c\u4e5f\u81ea\u5fc3\u614c\u3002\u8fd9\u6837\u6253 \u6cd5\uff0c\u6700\u8017\u7cbe\u795e\uff0c\u53ea\u8981\u6709\u4e1d\u6beb\u758f\u5ffd\uff0c\u5c31\u8981\u7ed9\u7985\u6756\u626b\u5f97\u7c89\u8eab\u788e\u9aa8\uff0c\u57cb\u7389\u897f\u6e56\u3002 \u3000\u3000\u4e86\u56e0\u51dd\u795e\u6325\u6756\uff0c\u521a\u6321\u5f00\u4e86\u5415\u56db\u5a18\u4ece\u7a7a\u4e2d\u51fb\u4e0b\u7684\u7b2c\u56db\u5251\uff0c\u5ffd\u7136\u8239\u8eab\u4e00\u9635\u52a8\u8361\uff0c\u8231\u677f\u5ffd\u7136\u88c2 \u5f00\uff0c\u6e56\u6c34\u6eda\u6eda\u6d8c\u8fdb\u821f\u4e2d\uff0c\u8239\u8eab\u6e10\u6e10\u4e0b\u6c89\uff01\u539f\u6765\u8fd9\u4e43\u662f\u9c7c\u5a18\u7684\u7edd\u7740\uff01 \u3000\u3000\u9c7c\u5a18\u5728\u6d77\u4e0a\u957f\u5927\uff0c\u7cbe\u901a\u6c34\u6027\uff0c\u60ca\u6d9b\u9a87\u6d6a\uff0c\u4e5f\u90fd\u4e0d\u6015\uff0c\u4f55\u51b5\u8fd9\u5e73\u9759\u7684\u897f\u6e56\uff1f\u5979\u6f5c\u5165\u6e56\u4e2d\uff0c \u672c\u6765\u662f\u60f3\u6551\u90a3\u5c11\u5e74\uff0c\u4f46\u56db\u89c5\u4e0d\u89c1\uff0c\u800c\u8239\u4e0a\u5415\u56db\u5a18\u5df2\u548c\u4e86\u56e0\u6253\u4e86\u8d77\u6765\uff0c\u5979\u9732\u51fa\u5934\u6765\u89c2\u671b\uff0c\u89c1\u5415 \u56db\u5a18\u5f62\u52bf\u5947\u9669\uff0c\u7a81\u7136\u60f3\u8d77\u4e86\u4e00\u62db\u7edd\u62db\uff0c\u6f5c\u5230\u4e86\u56e0\u8239\u5e95\uff0c\u62d4\u51fa\u4f69\u5200\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5c31\u628a\u4e86\u56e0\u7684\u5c0f \u821f\u5f04\u4e86\u4e00\u4e2a\u5927\u6d1e\uff01 \u3000\u3000\u4e86\u56e0\u6b66\u529f\u6781\u9ad8\uff0c\u5374\u4e0d\u901a\u6c34\u6027\uff0c\u4e0d\u7981\u614c\u4e86\u624b\u811a\uff0c\u5415\u56db\u5a18\u7b2c\u4e94\u6b21\u4ece\u7a7a\u4e2d\u6251\u51fb\u4e0b\u6765\uff0c\u4e86\u56e0\u5927\u53eb \u4e00\u58f0\uff1a\u201c\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d\u811a\u5c16\u4e00\u70b9\u8239\u5934\uff0c\u4e5f\u7eb5\u8d77\u4e24\u4e08\u9ad8\uff0c\u4e00\u6756\u5411\u5415\u56db\u5a18\u63b7\u53bb\uff0c\u540c\u65f6 \u5de6\u624b\u75be\u4f38\uff0c\u8981\u628a\u5415\u56db\u5a18\u5728\u7a7a\u4e2d\u6d3b\u6349\uff01 \u3000\u3000\u7518\u51e4\u6c60\u767d\u6cf0\u5b98\u540c\u58f0\u53eb\u558a\uff0c\u7518\u51e4\u6c60\u63b7\u51fa\u5315\u9996\uff0c\u767d\u6cf0\u5b98\u6492\u51fa\u6885\u82b1\u9488\uff0c\u4e86\u56e0\u7985\u6756\u4e00\u62a1\uff0c\u6756\u98ce\u547c \u547c\u4e2d\uff0c\u5415\u56db\u5a18\u7a81\u4f3c\u5f39\u4e38\u4e00\u822c\uff0c\u98de\u5c04\u56de\u5c0f\u8239\u4e4b\u4e0a\uff01 \u3000\u3000\u7518\u51e4\u6c60\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u8dc3\u8fdb\u8231\u4e2d\uff0c\u5415\u56db\u5a18\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u7ffb\u8eab\u5750\u8d77\uff0c\u7b11\u9053\uff1a\u201c\u597d \u9669\uff01\u201d\u7518\u51e4\u6c60\u77e5\u5979\u6ca1\u4e8b\uff0c\u653e\u4e0b\u4e86\u5fc3\uff0c\u5ffd\u7136\u5c0f\u8239\u4e00\u9635\u6447\u6643\uff0c\u53ea\u542c\u5f97\u767d\u6cf0\u5b98\u53eb\u9053\uff1a\u201c\u4e03\u54e5\u51fa \u6765\uff01\u201d\u7518\u51e4\u6c60\u8d76\u5fd9\u51fa\u6765\uff0c\u53ea\u89c1\u4e86\u56e0\u7684\u8239\u5c31\u5c06\u6c89\u6ca1\uff0c\u4e86\u56e0\u8df3\u5728\u8239\u84ec\u4e4b\u4e0a\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u6325\u52a8\u7985 \u6756\uff0c\u5411\u81ea\u5df1\u8fd9\u53ea\u8239\u731b\u51fb\uff0c\u4e24\u8239\u76f8\u8ddd\u5728\u4e09\u4e08\u4e4b\u5916\uff0c\u7985\u6756\u65e0\u6cd5\u6253\u5012\uff0c\u53ef\u662f\u90a3\u6756\u98ce\u547c\u547c\uff0c\u5a01\u52bf\u4e5f\u6781 \u731b\u70c8\u3002\u4e86\u56e0\u6b63\u52aa\u529b\u8bbe\u6cd5\u4f7f\u4e24\u8239\u63a5\u8fd1\uff0c\u767d\u6cf0\u5b98\u63d0\u7740\u6734\u5200\uff0c\u7ad9\u5728\u8239\u5934\uff0c\u9762\u8272\u5df2\u5413\u5f97\u9752\u767d\uff01 \u3000\u3000\u7518\u51e4\u6c60\u53eb\u9053\uff1a\u201c\u4e94\u54e5\uff0c\u5f85\u5c0f\u5f1f\u63a5\u4ed6\u4e00\u6756\u3002\u201d\u62a2\u5728\u767d\u6cf0\u5b98\u524d\u9762\uff0c\u4e86\u56e0\u8db3\u8ddf\u4e00\u65cb\uff0c\u90a3\u8239\u6253\u4e86 \u4e00\u4e2a\u5708\u5708\uff0c\u4e24\u8239\u76f8\u8ddd\u4e0d\u5230\u4e00\u4e08\uff0c\u4e86\u56e0\u5927\u559d\u4e00\u58f0\uff0c\u84e6\u7136\u51cc\u7a7a\u6251\u4e0b\uff0c\u4e00\u6756\u5411\u7518\u51e4\u6c60\u6253\u6765\uff0c\u7518\u51e4\u6c60 \u594b\u8d77\u795e\u529b\uff0c\u6a2a\u5200\u4e00\u6321\uff0c\u91d1\u94c1\u4ea4\u9e23\uff0c\u7518\u51e4\u6c60\u53ea\u89c9\u4e00\u80a1\u5927\u529b\u63a8\u6765\uff0c\u864e\u53e3\u9707\u88c2\uff0c\u624b\u4e2d\u7684\u7ea2\u6bdb\u5200\u98de\u4e0a \u534a\u7a7a\uff0c\u8eab\u4e0d\u7531\u5df1\u7684\u76f4\u7ed9\u9707\u56de\u8231\u5185\u3002 \u3000\u3000\u7518\u51e4\u6c60\u8dcc\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u4e86\u56e0\u4e5f\u51e0\u4e4e\u8dcc\u843d\u6e56\u4e2d\u3002\u539f\u6765\u7518\u51e4\u6c60\u7684\u529f\u529b\u867d\u7136\u4e0d\u53ca\u4e86\u56e0\uff0c\u4f46\u76f8\u5dee \u4e5f\u5e76\u4e0d\u8fdc\uff0c\u4e86\u56e0\u88ab\u4ed6\u594b\u529b\u4e00\u6321\uff0c\u4eba\u5728\u534a\u7a7a\uff0c\u53ea\u51ed\u4e00\u51fb\u4e4b\u5a01\uff0c\u4e00\u51fb\u4e4b\u540e\uff0c\u4fbf\u4e0d\u80fd\u518d\u53d1\u51fa\u529b\u6765\uff0c \u88ab\u7518\u51e4\u6c60\u5185\u5bb6\u771f\u529b\u4e00\u9707\uff0c\u5728\u534a\u7a7a\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u6025\u5fd9\u8dc3\u56de\u5371\u8239\uff01\u9c7c\u5a18\u5728\u6c34\u5e95\u7528\u529b\u4e00\u677f\uff0c\u5c0f\u821f \u4e0a\u4e0b\u53d7\u529b\uff0c\u767b\u65f6\u503e\u8986\uff01 \u3000\u3000\u4e86\u56e0\u4e0d\u61c2\u6c34\u6027\uff0c\u8fd9\u65f6\u811a\u8ddf\u5df1\u6d78\u5230\u6c34\u4e2d\uff0c\u9c7c\u5a18\u7528\u5251\u65ab\u4ed6\uff0c\u88ab\u4ed6\u7528\u7985\u6756\u5728\u6c34\u4e2d\u4e00\u63fd\uff0c\u6756\u5c3e\u89e6 \u7740\u5251\u5c16\uff0c\u529b\u9053\u867d\u7136\u4e0d\u5f3a\uff0c\u9c7c\u5a18\u4e5f\u5df1\u628a\u6301\u4e0d\u4f4f\uff0c\u4f69\u5251\u8dcc\u843d\u6e56\u5e95\uff0c\u6025\u5fd9\u6f5c\u4e0b\u53bb\u62fe\uff0c\u4e0d\u6562\u518d\u60f9\u4ed6\u4e86\u3002 \u3000\u3000\u4e86\u56e0\u6025\u4e2d\u751f\u667a\uff0c\u4fef\u8eab\u4e00\u6293\uff0c\u786c\u751f\u751f\u7684\u628a\u8239\u677f\u6293\u88c2\u4e00\u5757\u51fa\u6765\uff0c\u5411\u6c34\u4e2d\u4e00\u4e22\uff0c\u8eab\u5f62\u98de\u8d77\uff0c\u8db3 \u5c16\u5411\u90a3\u6d6e\u5728\u6c34\u9762\u7684\u8239\u677f\u4e00\u70b9\uff0c\u53c8\u518d\u7eb5\u9ad8\uff0c\u7eb5\u9ad8\u4e4b\u65f6\uff0c\u7528\u7985\u6756\u5c06\u90a3\u8239\u677f\u4e00\u62e8\uff0c\u8ba9\u5b83\u6f02\u51fa\u5c11\u8bb8\uff0c \u518d\u843d\u4e0b\u65f6\uff0c\u4ecd\u7528\u8fd9\u4e2a\u6cd5\u5b50\uff0c\u501f\u90a3\u4e00\u5c0f\u5757\u6728\u677f\uff0c\u4f5c\u4e3a\u8e0f\u811a\u4e4b\u7528\uff0c\u5c45\u7136\u7ed9\u4ed6\u8df3\u4e0a\u767d\u5824\u3002 \u3000\u3000\u4e86\u56e0\u7684\u8239\u503e\u8986\u4e4b\u540e\uff0c\u9c7c\u5a18\u4e5f\u6d6e\u51fa\u6c34\u9762\uff0c\u722c\u56de\u5c0f\u8239\uff0c\u6025\u5fd9\u62e8\u8f6c\u8239\u5934\uff0c\u5411\u5b64\u5c71\u90a3\u8fb9\u6447\u53bb\uff0c\u5c0f \u821f\u5982\u7bad\uff0c\u5230\u4e86\u56e0\u4e0a\u5cb8\u4e4b\u65f6\uff0c\u5415\u56db\u5a18\u7b49\u4e5f\u5df2\u5728\u53e6\u4e00\u8fb9\u4e0a\u5cb8\u4e86\u3002 \u3000\u3000\u7518\u51e4\u6c60\u80cc\u8d77\u8f66\u9f0e\u4e30\uff0c\u6545\u610f\u7ed5\u4e00\u6bb5\u8def\uff0c\u518d\u56de\u5230\u6e56\u6ee8\u5ba2\u5bd3\uff0c\u5e78\u559c\u6ca1\u78b0\u7740\u5bf9\u5934\uff0c\u60f3\u6765\u90a3\u4e86\u56e0\u4e5f \u7b4b\u75b2\u529b\u5026\uff0c\u4e0d\u6562\u518d\u8ffd\u8e2a\u641c\u6355\u4e86\u3002 \u3000\u3000\u5415\u56db\u5a18\u5401\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u53db\u8d3c\u597d\u5389\u5bb3\uff01\u201d\u5bf9\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u4f60\u5148\u7ed9\u8fd9\u4f4d\u8001\u4e08\u6577\u4f24\uff0c\u7b49\u4f1a \u6211\u548c\u4e03\u54e5\u7ed9\u4ed6\u63a5\u81fc\u3002\u201d\u7acb\u5373\u76d8\u819d\u800c\u5750\uff0c\u505a\u8d77\u5410\u7eb3\u529f\u592b\u3002\u7518\u51e4\u6c60\u4e5f\u662f\u4e00\u6837\u3002\u9c7c\u5a18\u770b\u5f97\u83ab\u540d\u5176 \u5999\uff0c\u767d\u6cf0\u5b98\u548b\u820c\u9053\uff1a\u201c\u5e78\u4e8f\u4e03\u54e5\u66ff\u6211\u63a5\u4e86\u4e00\u6756\u3002\u201d\u539f\u6765\u7518\u5415\u53d7\u4e86\u4e86\u56e0\u7684\u6756\u529b\u9707\u8361\uff0c\u6050\u9632\u53d7\u4e86 \u5185\u4f24\uff0c\u6240\u4ee5\u90fd\u76d8\u819d\u9759\u5750\uff0c\u8fd0\u6c14\u8c03\u5143\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u4e24\u4eba\u7ad9\u4e86\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5e78\u597d\u6ca1\u4e8b\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u540c\u95e8\u6076\u6597\uff0c\u6035\u81ea\u60ca\u5fc3\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u516b\u56de  \u7591\u96e8\u7591\u4e91\u3000\u8c0e\u8a00\u8c08\u65e7\u4e8b",content:(0,t.jsx)(t.Fragment,{children:"\u4ea6\u771f\u4ea6\u5e7b\u3000\u6709\u610f\u6eaf\u524d\u60c5 \u3000\u3000\u9c7c\u5a18\u5e26\u7b11\u8fc7\u6765\uff0c\u62c9\u7740\u5415\u56db\u5a18\u7684\u624b\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u771f\u770b\u4e0d\u51fa\u4f60\u8fd9\u6837\u7f8e\u82e5\u5929\u4ed9\uff0c\u529f\u592b\u5374\u5982\u6b64\u4e86 \u5f97\uff1f\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u5c0f\u9b3c\u5934\uff0c\u4f60\u51e0\u65f6\u4e5f\u5b66\u4f1a\u4e86\u8fd9\u4e00\u5957\uff0c\u61c2\u5f97\u7ed9\u4eba\u6234\u9ad8\u5e3d\u4e86\u3002\u4e0d\u8fc7\u8bf4\u6765\u6211\u4eec \u8fd8\u8981\u591a\u8c22\u4f60\u5462\uff0c\u8981\u4e0d\u662f\u4f60\u628a\u90a3\u6076\u8d3c\u7684\u8239\u5f04\u7ffb\uff0c\u8fd9\u573a\u6076\u6597\u771f\u4e0d\u77e5\u5982\u4f55\u4e86\u5c40\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u767d\u6cf0\u5b98\u5df2\u7ed9\u8f66\u9f0e\u4e30\u6577\u4e86\u91d1\u521b\u5723\u836f\uff0c\u8f66\u9f0e\u4e30\u53cc\u773c\u534a\u5f20\uff0c\u9762\u5982\u91d1\u7eb8\u3002\u7518\u51e4\u6c60\u6012\u9053\uff1a\u201c\u90a3 \u5973\u5b69\u5b50\u624b\u5e95\u597d\u8fa3\uff01\u201d\u5415\u56db\u5a18\u8fc7\u6765\u770b\uff0c\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u5e78\u90a3\u5973\u5b69\u5b50\u6c14\u529b\u4e0d\u5927\uff0c\u53ea\u662f\u6298\u65ad\u4e86\u4e00\u6839\u808b \u9aa8\u3002\u201d\u7518\u51e4\u6c60\u64c5\u4e8e\u7eed\u7b4b\u63a5\u9aa8\u4e4b\u672f\uff0c\u5148\u66ff\u4ed6\u63a5\u4e0a\u53f3\u624b\u8155\u9aa8\u6240\u8131\u7684\u81fc\uff0c\u7136\u540e\u66ff\u4ed6\u7f1d\u597d\u7b4b\u9aa8\u7684\u4f24\uff0c \u5305\u624e\u8d77\u6765\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u8f66\u9f0e\u4e30\u7cbe\u795e\u7a0d\u7a0d\u6062\u590d\uff0c\u5f20\u5f00\u4e86\u773c\uff0c\u5fae\u5fae\u70b9\u5934\uff0c\u8868\u793a\u8c22\u610f\u3002\u5415\u56db\u5a18\u9053\uff1a \u201c\u4f60\u518d\u8eba\u4e00\u4f1a\u513f\u3002\u201d\u8f66\u9f0e\u4e30\u5fae\u5598\u95ee\u9053\uff1a\u201c\u8001\u673d\u591a\u627f\u76f8\u6551\uff0c\u8bf7\u95ee\u6069\u516c\u9ad8\u59d3\u5927\u540d\uff1f\u201d\u5415\u56db\u5a18\u7b11 \u9053\uff1a\u201c\u5148\u7956\u59d3\u5415\uff0c\u8bb3\u665a\u6751\uff0c\u8001\u4e08\u60f3\u5fc5\u76f8\u8bc6\uff1f\u201d\u8f66\u9f0e\u4e30\u201c\u554a\u5440\u201d\u4e00\u58f0\u53eb\u8d77\u6765\u9053\uff1a\u201c\u6069\u4eba\u662f\u8446\u4e2d \u5148\u751f\u7684\u516c\u5b50\u4e48\uff1f\u4ee4\u7956\u5f53\u5e74\u7ec8\u5e10\u4f20\u7ecf\uff0c\u6211\u867d\u672a\u5f97\u5217\u95e8\u5899\uff0c\u4e5f\u66fe\u53d7\u4ed6\u6307\u70b9\u3002\u201d\u6323\u624e\u7740\u5c31\u8981\u8d77\u6765\u62dc \u8c22\uff0c\u5415\u56db\u5a18\u8f7b\u8f7b\u5c06\u4ed6\u6309\u4e0b\uff0c\u9053\uff1a\u201c\u8001\u4e08\u4e43\u662f\u6211\u7684\u957f\u8f88\uff0c\u5148\u4eba\u5e38\u5e38\u8bf4\u53ca\uff0c\u5982\u6b64\u5ba2\u6c14\uff0c\u5c82\u4e0d\u6298\u6740 \u5c0f\u8f88\u3002\u201d\u8f66\u9f0e\u4e30\u808b\u9aa8\u5c1a\u75db\uff0c\u89c1\u5415\u56db\u5a18\u4ee5\u957f\u8f88\u4e4b\u793c\u5f85\u4ed6\uff0c\u4e5f\u5c31\u7f62\u4e86\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4e0e\u8001\u5927\u540c\u6e38 \u7684\u90a3\u4f4d\u5c11\u5e74\u662f\u8c01\uff1f\u201d\u8f66\u9f0e\u4e30\u770b\u4e86\u7518\u51e4\u6c60\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4ed6\uff0c\u4ed6\u2026\u2026\u201d\u5415\u56db\u5a18\u77e5\u4ed6\u5fc3\u6709\u987e\u5fcc\uff0c \u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u5e08\u5144\u3002\u201d\u8f66\u9f0e\u4e30\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5ffd\u7136\u60f3\u8d77\u5415\u8446\u4e2d\u5e76\u65e0\u513f\u5b50\uff0c\u5b9a\u795e\u770b\u4e86\u5415\u56db\u5a18\u4e00 \u4f1a\uff0c\u9053\uff1a\u201c\u8bf7\u6055\u5192\u6627\uff0c\u6211\u8bb0\u5f97\u8446\u4e2d\u5148\u751f\u6709\u4f4d\u638c\u73e0\uff0c\u5c0f\u5b57\u83b9\u513f\u7684\uff0c\u662f\u5426\u548c\u5148\u751f\u4e00\u8d77\uff1f\u201d\u5415\u56db\u5a18 \u4e00\u7b11\u63ed\u4e0b\u5934\u5dfe\uff0c\u9053\uff1a\u201c\u957f\u8005\u4e4b\u524d\uff0c\u4e0d\u6562\u9690\u7792\uff0c\u5415\u83b9\u6b63\u662f\u6211\u3002\u201d\u8f66\u9f0e\u4e30\u5927\u559c\u9053\uff1a\u201c\u4e45\u95fb\u5973\u4fa0\u7edd \u4e16\u6b66\u529f\uff0c\u4eca\u65e5\u76ee\u7779\uff0c\u679c\u7136\u4e0d\u5047\u3002\u4f2f\u9053\u65e0\u513f\uff0c\u4e2d\u90ce\u6709\u5973\uff0c\u4ee4\u5c0a\u6cc9\u4e0b\u4ea6\u5f53\u7791\u76ee\u3002\u201d\u5415\u56db\u5a18\u542c\u4ed6\u63d0 \u8d77\u7236\u4eb2\uff0c\u4e0d\u89c9\u6cea\u4e0b\u3002\u8f66\u9f0e\u4e30\u53c8\u9053\uff1a\u201c\u90a3\u4e48\u8fd9\u4f4d\u5f53\u662f\u6c5f\u5357\u4e03\u4fa0\u4e2d\u4eba\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684 \u4e03\u5e08\u5144\u7518\u51e4\u6c60\u3002\u201d\u8f66\u9f0e\u4e30\u8fd9\u4e00\u559c\u975e\u540c\u5c0f\u53ef\uff0c\u4ee5\u8098\u652f\u5e8a\uff0c\u5c06\u8eab\u501a\u6795\uff0c\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e0e\u6c5f\u5357\u5927 \u4fa0\u5728\u6b64\u76f8\u4f1a\uff01\u201d\u6b22\u559c\u8fc7\u5ea6\uff0c\u52c9\u5f3a\u8d77\u6765\uff0c\u5ffd\u7136\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u6655\u8fc7\u53bb\u3002 \u3000\u3000\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5148\u751f\u771f\u662f\u6027\u60c5\u4e2d\u4eba\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5e08\u5144\u5927\u540d\uff0c\u8c01\u4e0d\u4ef0\u66b4\u3002\u8fd9\u4f4d \u8001\u5148\u751f\u7275\u52a8\u7b4b\u9aa8\uff0c\u4e00\u65f6\u6025\u75db\uff0c\u6599\u53ef\u65e0\u59a8\u3002\u53ea\u662f\u90a3\u4f4d\u4ec0\u4e48\u2018\u674e\u516c\u5b50\u2019\u7684\u6765\u5386\uff0c\u53ef\u5f97\u5f85\u4ed6\u597d\u65f6\u518d \u95ee\u4e86\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u770b\u8fd9\u4f4d\u8001\u4e08\u4e5f\u662f\u6c5f\u6e56\u4e2d\u4eba\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u8d77\u521d\u53ea\u5f53\u4ed6\u662f\u4e2a\u5148\u8f88 \u5bbf\u5112\uff0c\u73b0\u5728\u770b\u6765\uff0c\u4ed6\u5bf9\u6211\u8f88\u6765\u5386\uff0c\u9887\u4e3a\u6e05\u695a\uff0c\u90a3\u7eb5\u975e\u6c5f\u6e56\u4e2d\u4eba\uff0c\u4e5f\u5fc5\u5b9a\u662f\u4e0e\u6c5f\u6e56\u4e2d\u7684\u4eba\u6709\u6765 \u5f80\u4e86\u3002\u201d \u3000\u3000\u5e08\u5144\u59b9\u8c08\u4e86\u4e00\u9635\uff0c\u8f66\u9f0e\u4e30\u4ecd\u7136\u672a\u9192\uff0c\u5929\u8272\u5df2\u9ed1\uff0c\u7518\u51e4\u6c60\u518d\u8981\u4e86\u4e00\u4e2a\u623f\u95f4\uff0c\u5b89\u6b47\u5415\u56db\u5a18\u548c \u9c7c\u5a18\u3002\u5403\u8fc7\u665a\u996d\uff0c\u5415\u56db\u5a18\u7518\u51e4\u6c60\u767d\u6cf0\u5b98\u56f4\u5750\u5546\u8bae\uff0c\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u6211\u4eca\u65e5\u5c31\u60f3\u53bb\u63a2 \u5b83\u4e00\u63a2\u3002\u201d \u3000\u3000\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u90a3\u4e48\u8ba9\u9c7c\u5a18\u7559\u5b88\uff0c\u6211\u4eec\u540c\u53bb\u3002\u201d\u7518\u51e4\u6c60\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u4eba\u4e00\u591a\uff0c\u53cd\u800c\u6253 \u8349\u60ca\u86c7\uff0c\u8fd8\u662f\u8ba9\u516b\u59b9\u4e00\u4eba\u5148\u53bb\u5427\uff0c\u5f85\u63a2\u5f97\u865a\u5b9e\u4e4b\u540e\uff0c\u6211\u4eec\u518d\u5b9a\u4e0b\u6b65\u8ba1\u7b56\uff0c\u4e5f\u8fd8\u672a\u8fdf\u3002\u201d\u539f\u6765 \u7518\u51e4\u6c60\u662f\u56e0\u987e\u5fcc\u4e86\u56e0\u6b63\u5728\u629a\u8859\uff0c\u786c\u62fc\u6b8a\u975e\u5584\u7b56\uff0c\u800c\u5415\u56db\u5a18\u7684\u8f7b\u529f\u8d85\u5353\uff0c\u5219\u662f\u4ed6\u6240\u6df1\u77e5\uff0c\u662f\u4ee5 \u6709\u6b64\u52a8\u8bae\u3002\u767d\u6cf0\u5b98\u4e00\u60f3\uff0c\u4e5f\u660e\u767d\u4e86\u7518\u51e4\u6c60\u7684\u610f\u601d\uff0c\u4fbf\u4e0d\u518d\u6301\u5f02\u8bae\u3002 \u3000\u3000\u5415\u56db\u5a18\u5403\u8fc7\u665a\u996d\uff0c\u5c0f\u7761\u4e00\u4f1a\uff0c\u542c\u5230\u4e09\u66f4\u9f13\u54cd\uff0c\u65b9\u624d\u8d77\u6765\uff0c\u6362\u8fc7\u9ed1\u8272\u7684\u591c\u884c\u8863\u670d\uff0c\u5e26\u4e86\u767e \u5b9d\u56ca\uff0c\u548c\u7518\u51e4\u6c60\u767d\u6cf0\u5b98\u70b9\u9996\u9053\u522b\uff0c\u53ea\u4e00\u8038\u8eab\uff0c\u4fbf\u8f7b\u5982\u71d5\u5b50\u822c\u7684\u7a7f\u7a97\u98de\u53bb\u3002\u767d\u6cf0\u5b98\u53f9\u9053\uff1a\u201c\u516b \u59b9\u7684\u8f7b\u529f\u63d0\u7eb5\u672f\uff0c\u6211\u8f88\u671b\u5c18\u83ab\u53ca\uff0c\u505a\u4e3a\u5e08\u5144\uff0c\u771f\u662f\u60ed\u6127\u6781\u4e86\uff01\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u957f\u6c5f\u540e\u6d6a\u63a8 \u524d\u6d6a\uff0c\u8fd9\u662f\u5fc5\u7136\u4e4b\u7406\u3002\u8981\u4e0d\uff0c\u90a3\u6b66\u5b66\u4e00\u9053\uff0c\u8fd8\u80fd\u6709\u4ec0\u4e48\u53d1\u5c55\u5462\uff1f\u201d\u767d\u6cf0\u5b98\u70b9\u4e86\u70b9\u5934\uff0c\u5ffd\u9053\uff1a \u201c\u6211\u770b\u4eca\u65e5\u6240\u8c13\u7684\u90a3\u4e2a\u5973\u5b69\u5b50\uff0c\u5c06\u6765\u4e5f\u5b9a\u5728\u6211\u8f88\u4e4b\u4e0a\uff0c\u53ea\u662f\u624b\u6bb5\u592a\u8fa3\u4e86\uff01\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a \u201c\u8981\u4e0d\u662f\u5979\u751f\u5f97\u90a3\u6837\u53ef\u7231\uff0c\u6211\u771f\u60f3\u6253\u5979\u8033\u5149\u3002\u201d \u3000\u3000\u4e0d\u8bf4\u7518\u51e4\u6c60\u548c\u767d\u6cf0\u5b98\u5728\u5ba2\u5bd3\u8c08\u8bba\u3002\u4e14\u8bf4\u5415\u56db\u5a18\u51fa\u4e86\u6e56\u6ee8\u65c5\u820d\uff0c\u4e00\u6e9c\u70df\u7684\u5954\u5165\u57ce\u4e2d\u3002\u629a\u8859 \u9053\u8def\u5979\u672c\u719f\u6089\uff0c\u6084\u65e0\u58f0\u54cd\u7684\u8df3\u4e0a\u5973\u5899\uff0c\u7ffb\u5165\u540e\u56ed\uff0c\u56ed\u4e2d\u867d\u4e5f\u6709\u536b\u5175\u5de1\u903b\uff0c\u4f46\u5415\u56db\u5a18\u7684\u8f7b\u529f\u5df2 \u6709\u767b\u840d\u6e21\u6c34\u4e4b\u80fd\u3001\u98de\u7d6e\u65e0\u58f0\u4e4b\u5999\uff0c\u5c31\u662f\u4ece\u536b\u58eb\u8eab\u8fb9\u63a0\u8fc7\uff0c\u4ed6\u4eec\u4e5f\u61f5\u7136\u4e0d\u89c9\u3002 \u3000\u3000\u540e\u56ed\u8fde\u7740\u629a\u8859\u5185\u5ba4\uff0c\u5415\u56db\u5a18\u8df3\u4e0a\u5c4b\u810a\uff0c\u5fc3\u60f3\uff1a\u5148\u627e\u674e\u660e\u73e0\u4e00\u95ee\u3002\u5ffd\u89c1\u8fdc\u5904\u5c4b\u9762\uff0c\u4e00\u6761\u4eba \u5f71\uff0c\u75be\u63a0\u800c\u8fc7\uff0c\u5415\u56db\u5a18\u6697\u4e2d\u7b11\u9053\uff1a\u5c45\u7136\u8fd8\u6709\u540c\u9053\u4e2d\u4eba\uff0c\u4e14\u770b\u770b\u4ed6\u662f\u8c01\uff1f\u8eab\u5f62\u4e00\u8d77\uff0c\u7d27\u8e51\u8fd9\u591c \u884c\u4eba\u80cc\u540e\uff0c\u7528\u4e2d\u98df\u4e24\u6307\u76f8\u642d\uff0c\u201c\u62cd\u201d\u7684\u5f39\u4e86\u4e00\u58f0\uff0c\u8d76\u7d27\u8df3\u5f00\uff0c\u90a3\u4eba\u56de\u5934\u6765\uff0c\u4e0b\u5f26\u65b0\u6708\uff0c\u867d\u975e \u660e\u4eae\uff0c\u5415\u56db\u5a18\u5374\u5df2\u770b\u5f97\u6e05\u695a\uff0c\u539f\u6765\u5c31\u662f\u65e5\u95f4\u7684\u90a3\u4f4d\u201c\u674e\u516c\u5b50\u201d\u3002\u4ed6\u56de\u8fc7\u5934\u6765\uff0c\u4e0d\u89c1\u4eba\u5f71\uff0c\u751a \u662f\u60ca\u8be7\uff0c\u8f6c\u8fc7\u8eab\u98de\u5165\u5185\u9662\u3002 \u3000\u3000\u5415\u56db\u5a18\u8ddf\u5728\u4ed6\u7684\u8eab\u540e\uff0c\u8f7b\u8f7b\u8df3\u4e0b\uff0c\u5185\u623f\u5ffd\u7136\u8d70\u51fa\u4e24\u4e2a\u4e2b\u5934\uff0c\u90a3\u201c\u674e\u516c\u5b50\u201d\u8eb2\u5728\u5ead\u4e2d\u4e00\u68f5 \u6811\u540e\uff0c\u5415\u56db\u5a18\u5374\u4e00\u8038\u8eab\uff0c\u8df3\u4e0a\u4fa7\u5c4b\u7684\u6a2a\u7cb1\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u4e2b\u5934\u9053\uff1a\u201c\u5c0f\u59d0\u548c\u5e08\u5085\u5230\u5916\u8fb9\u53bb\u4e86\uff0c \u542c\u8bf4\u53bb\u89c1\u4ec0\u4e48\u5b9d\u56fd\u7985\u5e08\uff0c\u4f60\u90a3\u51b0\u5858\u71d5\u7a9d\uff0c\u53ef\u4e0d\u5fc5\u8fd9\u6837\u5feb\u7aef\u51fa\u6765\u3002\u201d\u53e6\u4e00\u4e2a\u9053\uff1a\u201c\u90a3\u4e2a\u5973\u5b69\u5b50 \u4e5f\u8981\u5462\u3002\u54fc\uff0c\u5979\u4e0d\u77e5\u662f\u4ec0\u4e48\u5927\u5bcc\u5927\u8d35\u4eba\u5bb6\u7684\u5343\u91d1\uff0c\u8fde\u6211\u4eec\u5c0f\u59d0\u5403\u7684\u71d5\u7a9d\u5979\u4e5f\u5acc\u4e0d\u597d\uff0c\u8bf4\u662f\u8fdc \u6bd4\u4e0d\u4e0a\u5979\u5bb6\u4e2d\u7684\u5462\uff01\u201d\u4e24\u4e2a\u4e2b\u5934\u5431\u5431\u55b3\u55b3\u8d70\u51fa\u5916\u95f4\u53bb\u4e86\u3002 \u3000\u3000\u5415\u56db\u5a18\u4e0d\u518d\u7406\u90a3\u201c\u674e\u516c\u5b50\u201d\uff0c\u98de\u8eab\u637b\u5728\u4e24\u4e2a\u4e2b\u5934\u524d\u9762\uff0c\u8d70\u5230\u5916\u8859\uff0c\u5ffd\u89c1\u90a3\u9752\u8863\u5987\u4eba\u548c\u4e86 \u56e0\u548c\u5c1a\u53cc\u53cc\u8d70\u6765\uff0c\u5415\u56db\u5a18\u4e00\u60ca\uff0c\u4f0f\u5728\u5c4b\u810a\u4e0a\u4e0d\u6562\u7a0d\u52a8\uff0c\u4e86\u56e0\u548c\u90a3\u5987\u4eba\u8d70\u5165\u5c4b\u4e2d\uff0c\u6b63\u6b63\u5c31\u5728\u5979 \u7684\u4e0b\u9762\u3002\u5415\u56db\u5a18\u8f7b\u8f7b\u7684\u79fb\u5f00\u4e86\u4e00\u70b9\u74e6\u7f1d\uff01\u5f20\u76ee\u4e0b\u671b\uff0c\u4e86\u56e0\u521a\u521a\u8d70\u8fdb\uff0c\u5ffd\u542c\u5f97\u90a3\u9752\u8863\u5987\u4eba\u5728\u4e86 \u56e0\u8eab\u540e\uff0c\u72e0\u72e0\u8bf4\u9053\uff1a\u201c\u54fc\uff0c\u5415\u56db\u5a18\uff01\u201d\u4e86\u56e0\u7ffb\u773c\u8bf4\uff1a\u201c\u5415\u56db\u5a18\u600e\u4e48\uff1f\u201d \u3000\u3000\u9752\u8863\u5973\u4eba\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\uff0c\u4e0d\u662f\u6211\u8bf4\u4f60\uff0c\u4f60\u600e\u4e48\u8fd9\u6837\u7eb5\u5bb9\u5e05\u59b9\uff1f\u201d\u4e86\u56e0\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c \u6709\u82e6\u8bf4\u4e0d\u51fa\u3002\u9752\u8863\u5987\u4eba\u53c8\u9053\uff1a\u201c\u96be\u9053\u4f60\u7684\u5e08\u5f1f\u6ca1\u6709\u4e00\u4e2a\u542c\u4f60\u7684\u8bdd\uff1f\u201d\u4e86\u56e0\u6012\u9053\uff1a\u201c\u6ca1\u4ed6\u4eec\u4e5f \u6210\uff01\u201d\u6b47\u4e86\u534a\u664c\uff0c\u7eed\u9053\uff1a\u201c\u6211\u7684\u6765\u610f\uff0c\u60f3\u4f60\u5df2\u660e\u767d\u4e86\uff1f\u201d\u9752\u8863\u5987\u4eba\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u66ff\u56db\u8d1d\u52d2\u5e26 \u5bc6\u793c\u7ed9\u674e\u5927\u4eba\u5417\uff1f\u201d\u4e86\u56e0\u9053\uff1a\u201c\u53e6\u5916\u8fd8\u6709\u4e09\u4ef6\u4e8b\u60c5\u3002\u201d\u9752\u8863\u5987\u4eba\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d\u4e86\u56e0\u9053\uff1a \u201c\u7b2c\u4e00\u4ef6\uff0c\u90a3\u8def\u6c11\u77bb\u867d\u7136\u72af\u4e86\u4f60\u5bb6\u5927\u4eba\uff0c\u5374\u662f\u6211\u7684\u5e08\u5f1f\uff0c\u53ef\u5f97\u7531\u6211\u5904\u7f6e\u3002\u201d\u9752\u8863\u5987\u4eba\u7b11\u4e86\u4e00 \u7b11\u9053\uff1a\u201c\u672c\u6765\u7406\u8be5\u5982\u6b64\u3002\u4f46\u5176\u4e2d\u5c1a\u6709\u672a\u4fbf\u4e4b\u5904\u3002\u4ed6\u5c31\u56da\u5728\u8fd9\u95f4\u5c4b\u91cc\uff0c\u7b49\u4f1a\u4f60\u8fdb\u5165\u590d\u58c1\u5bc6\u5ba4\u53bb \u770b\u4ed6\u5c31\u77e5\u9053\u4e86\u3002\u8fd8\u6015\u4e0d\u6b62\u4ed6\u4e00\u4e2a\u4eba\u5728\u91cc\u9762\u5462\uff01\u201d\u4e86\u56e0\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u4f55\u4eba\u770b\u5b88\u4ed6\uff1f\u201d\u9752 \u8863\u5987\u4eba\u9053\uff1a\u201c\u6211\u4eec\u7684\u674e\u5c0f\u59d0\u5440\uff01\u201d\u5415\u56db\u5a18\u65b9\u59cb\u604d\u7136\u5927\u609f\u8fd9\u9752\u8863\u5987\u4eba\u4e3a\u4ec0\u4e48\u5e26\u4e86\u56e0\u8d70\u8fdb\u8fd9\u95f4\u7a7a \u5c4b\uff0c\u539f\u6765\u8def\u6c11\u77bb\u56da\u5728\u8fd9\u513f\u3002 \u3000\u3000\u5415\u56db\u5a18\u51dd\u795e\u7ec6\u542c\uff0c\u53ea\u89c1\u4e86\u56e0\u53c8\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u7684\u5f92\u5f1f\u597d\u4e0d\u8981\u8138\uff01\u201d\u9752\u8863\u5987\u4eba\u9762\u8272 \u4e00\u53d8\uff0c\u9053\uff1b\u201c\u5b9d\u56fd\u7985\u5e08\uff0c\u4f60\u53ef\u4e0d\u80fd\u4e71\u8bb2\uff01\u6211\u7684\u5f92\u5f1f\u96be\u9053\u8fd8\u914d\u4e0d\u4e0a\u4f60\u7684\u5e08\u5f1f\u5417\uff1f\u201d\u4e86\u56e0\u9053\uff1a \u201c\u90a3\u4e5f\u5f97\u7531\u6211\u505a\u4e3b\u3002\u201d\u9752\u8863\u5987\u4eba\u5ffd\u53c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u5fc5\u4f60\u64cd\u5fc3\u5566\uff0c\u6211\u770b\u4ed6\u4eec\u5df2\u79c1\u8ba2\u7ec8\u8eab\u5566\uff0c \u4f60\u90a3\u5e08\u5f1f\u5440\uff0c\u521d\u65f6\u5012\u786c\u5f97\u5f88\uff0c\u534a\u70b9\u4e0d\u7406\u6211\u4eec\u5c0f\u59d0\uff0c\u73b0\u5728\u5462\uff0c\u53ef\u662f\u6709\u8bf4\u6709\u7b11\uff0c\u4eb2\u4eb2\u5bc6\u5bc6\u7684\u50cf\u5bf9 \u5c0f\u592b\u59bb\u5566\u3002\u201d\u4e86\u56e0\u9053\uff1a\u201c\u597d\u5427\uff0c\u5c31\u7b97\u4ed6\u4eec\u6210\u4eb2\uff0c\u6c11\u77bb\u4e5f\u5f97\u968f\u6211\u5230\u4eac\u57ce\u53bb\u3002\u201d\u9752\u8863\u5987\u4eba\u53c8\u7b11 \u9053\uff1a\u201c\u53ea\u8981\u4f60\u80fd\u8bf4\u5f97\u52a8\u4ed6\uff0c\u6211\u542c\u4ed6\u5e73\u65e5\u53e3\u6c14\uff0c\u5bf9\u4f60\u4f3c\u9887\u4e3a\u4e0d\u82df\u6587\u5462\uff01\u201d\u4e86\u56e0\u5927\u6012\uff0c\u201c\u62cd\u201d\u7684 \u4e00\u638c\u5c06\u684c\u5b50\u6253\u584c\u4e00\u89d2\uff0c\u9053\uff1a\u201c\u4ed6\u7adf\u6562\u5982\u6b64\uff01\u201d\u9752\u8863\u5987\u4eba\u5fd9\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\u606f\u6012\uff0c\u53cd\u6b63\u4f60\u7684\u5e08 \u5f1f\u9003\u4e0d\u6389\uff0c\u4f60\u53ef\u4ee5\u6162\u6162\u6559\u8bad\u4ed6\u3002\u8bf7\u95ee\u7b2c\u4e8c\u4ef6\u5462\uff1f\u201d \u3000\u3000\u4e86\u56e0\u9762\u8272\u7a0d\u5bbd\uff0c\u5ffd\u7136\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5ac2\u5b50\uff0c\u97e9\u5927\u54e5\u89c1\u8fc7\u4f60\u5566\uff1f\u201d\u9752\u8863\u5987\u4eba\u9053\uff1a\u201c\u89c1\u8fc7 \u5566\uff01\u201d\u4e86\u56e0\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u522b\u626d\u95f9\u4e86\u5341\u5e74\uff0c\u8001\u592b\u8001\u59bb\uff0c\u8be5\u548c\u89e3\u5566\u3002\u201d\u9752\u8863\u5987\u4eba\u9053\uff1a\u201c\u4f60\u662f\u7ed9\u4ed6 \u4f5c\u8bf4\u5ba2\u4e86\uff1f\u201d\u4e86\u56e0\u6b6a\u7740\u773c\u775b\u7b11\u9053\uff1a\u201c\u6709\u4e9b\u98ce\u6d41\u4e8b\u60c5\uff0c\u672c\u662f\u9022\u573a\u4f5c\u620f\uff0c\u8fc7\u4e86\u4e5f\u5c31\u7b97\u4e86\uff0c\u5ac2\u5b50\uff0c \u4f60\u8bf4\u4e0d\u662f\u4e48\uff01\u201d\u9752\u8863\u5987\u4eba\u201c\u5478\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5c0f\u5b50\u6ca1\u6b63\u7ecf\u3002\u7b2c\u4e09\u4ef6\u5462\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u4e00\u4e00\u542c\uff0c\u624d\u77e5\u9752\u8863\u5987\u4eba\u4e43\u662f\u97e9\u91cd\u5c71\u7684\u59bb\u5b50\u3002\u5fc3\u60f3\uff1a\u5979\u5012\u4fdd\u517b\u5f97\u597d\uff0c\u770b\u6765\u8fd8\u53ea\u662f\u56db \u5341\u591a\u5c81\u7684\u4eba\u3002\u5979\u542c\u5e08\u5085\u8bf4\u8fc7\uff09\u8fd9\u97e9\u91cd\u5c71\u4e43\u5929\u53f6\u6563\u4eba\u5e08\u5144\uff0c\u4e24\u5144\u5f1f\u5404\u6709\u6240\u957f\uff0c\u5927\u53f6\u7684\u638c\u529b\u5728\u5f53 \u4eca\u6b66\u6797\u4e4b\u4e2d\uff0c\u53ef\u5728\u4e94\u540d\u4e4b\u5185\uff1b\u800c\u97e9\u91cd\u5c71\u7684\u6697\u5668\u4e4b\u5de7\uff0c\u5219\u8981\u6570\u5230\u524d\u4e09\u540d\uff0c\u4ed6\u7684\u59bb\u5b50\u53f6\u6a2a\u6ce2\u6b66\u529f \u4e5f\u6781\u9ad8\u5f3a\uff0c\u539f\u6765\u5c31\u662f\u8fd9\u9752\u8863\u5987\u4eba\u3002\u602a\u4e0d\u5f97\u7518\u51e4\u6c60\u4e5f\u53ea\u662f\u548c\u5979\u6253\u4e2a\u5e73\u624b\u3002 \u3000\u3000\u4e86\u56e0\u505c\u4e86\u4e00\u505c\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u4e2a\u5973\u8f83\u513f\u5462\uff1f\u53eb\u5979\u968f\u6211\u56de\u53bb\uff01\u201d\u9752\u8863\u5987\u4eba\u9053\uff1a\u201c\u6211\u8981\u6536\u5979\u505a \u4e49\u5973\u5462\uff01\u201d\u4e86\u56e0\u9053\uff1a\u201c\u4f60\u522b\u5f00\u73a9\u7b11\u5566\uff0c\u6211\u975e\u628a\u5979\u5e26\u56de\u53bb\u4e0d\u53ef\uff01\u201d\u9752\u8863\u5987\u4eba\u9053\uff1a\u201c\u600e\u4e48\u5979\u662f\u79c1 \u81ea\u79bb\u4eac\u7684\u4e48\uff01\u201d\u4e86\u56e0\u9053\uff1a\u201c\u4f60\u522b\u591a\u7406\u95f2\u4e8b\uff0c\u603b\u4e4b\u4f60\u628a\u5979\u4ea4\u51fa\u6765\u4fbf\u662f\u3002\u201d \u3000\u3000\u9752\u8863\u5987\u4eba\u597d\u50cf\u5f88\u4e0d\u9ad8\u5174\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u56de\u4eac\uff01\u201d\u4e86\u56e0\u9053\uff1a\u201c\u540e\u5929\u5c31\u56de\u53bb\u3002\u201d\u9752\u8863\u5987\u4eba \u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e0d\u7ba1\u5415\u56db\u5a18\u4e86\uff1f\u8981\u77e5\u9053\u5979\u4e5f\u662f\u94a6\u72af\u5462\u3002\u201d\u4e86\u56e0\u6697\u60f3\uff1a\u5415\u56db\u5a18\u3001\u7518\u51e4\u6c60\u3001\u767d\u6cf0 \u5b98\u662f\u540c\u95e8\u4e2d\u9664\u4e86\u81ea\u5df1\u4e4b\u5916\u6b66\u529f\u6700\u5f3a\u7684\u4e09\u4eba\uff0c\u81ea\u5df1\u82e5\u548c\u53f6\u6a2a\u6ce2\u5408\u6597\u4ed6\u4eec\u4e09\u4eba\uff0c\u53ea\u6050\u8fd8\u8981\u843d\u8d25\u3002 \u6c89\u541f\u534a\u664c\uff0c\u5ffd\u9053\uff1a\u201c\u97e9\u5927\u54e5\u8fd8\u8981\u6765\u7684\uff0c\u662f\u4e48\uff01\u201d\u9752\u8863\u5987\u4eba\u9053\uff1a\u201d\u8c01\u7ba1\u5f97\u7740\u4ed6\uff01\u201d\u4e86\u56e0\u9053\uff1a \u201c\u82e5\u4f60\u4eec\u592b\u5987\u540c\u5fc3\u5408\u529b\uff0c\u90a3\u6211\u5c31\u5c06\u7518\u51e4\u6c60\u6349\u6765\uff0c\u8ba9\u4f60\u6d88\u4e00\u53e3\u6c14\u3002\u201d\u9752\u8863\u5987\u4eba\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3 \u4f60\u591a\u7559\u4e24\u5929\uff0c\u7b49\u90a3\u8001\u9b3c\u6765\u4e86\u518d\u8bf4\u3002\u6211\u4e5f\u771f\u820d\u4e0d\u5f97\u71d5\u513f\u5462\uff01\u201d \u3000\u3000\u4e86\u56e0\u5ffd\u53c8\u7aef\u4e86\u9762\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5c31\u5c06\u90a3\u5973\u5a03\u513f\u53eb\u6765\uff0c\u8ba9\u6211\u95ee\u5979\u3002\u201d\u9752\u8863\u5987\u4eba\u4f5c\u51fa\u65e0 \u53ef\u5948\u4f55\u7684\u6837\u5b50\uff0c\u8dd1\u51fa\u5c4b\u5916\uff0c\u8fde\u51fb\u4e09\u638c\uff0c\u64ae\u5507\u957f\u5578\u4e00\u58f0\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u65e5\u95f4\u6240\u89c1\u7684\u90a3\u5973\u5b69\u5b50\u4e0d\u77e5 \u4ece\u4ec0\u4e48\u89d2\u843d\u8dd1\u4e86\u51fa\u6765\uff0c\u9752\u8863\u5987\u4eba\u5c06\u5979\u4e00\u628a\u62c9\u7740\uff0c\u9053\uff1a\u201c\u6709\u8d35\u5ba2\u8981\u89c1\u4f60\u3002\u201d\u90a3\u5973\u5b69\u5b50\u201c\u561f\u201d\u7740 \u5c0f\u5634\u513f\u9053\uff1a\u201c\u6211\u4e0d\u89c1\u5b9d\u56fd\u7985\u5e08\u3002\u201d\u9752\u8863\u5987\u4eba\u4e00\u628a\u5c06\u5979\u63a8\u8fdb\u5c4b\u5185\uff0c\u8d23\u9053\uff1a\u201c\u4e0d\u51c6\u8fd9\u6837\u65e0\u793c\u3002\u201d \u3000\u3000\u90a3\u5973\u5b69\u5b50\u89c1\u4e86\u56e0\uff0c\u4f3c\u4e4e\u6709\u70b9\u754f\u7f29\uff0c\u4e86\u56e0\u62db\u62db\u624b\u9053\uff1a\u201c\u4f60\u8fc7\u6765\uff01\u201d\u90a3\u5973\u5b69\u5b50\u6447\u6447\u5934\u8bf4\u9053\uff1a \u201c\u6211\u4e0d\uff01\u201d\u4e86\u56e0\u751f\u6c14\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u6363\u4e71\uff0c\u4ed6\u4eec\u628a\u4f60\u5ba0\u574f\u5566\uff01\u201d\u90a3\u5973\u5b69\u5b50\u5ffd\u9053\uff1a\u201c\u56db\u8d1d\u52d2\u53eb\u6211 \u4e0d\u5fc5\u542c\u4f60\u7684\u8bdd\u3002\u4e0d\u8981\u4f60\u4eb2\u8fd1\u3002\u201d\u4e86\u56e0\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u80e1\u8bf4\uff01\u201d\u5c0f\u5973\u5b69\u5b50\u7b11\u9053\uff1a\u201c\u4ed6\u8bf4 \u4f60\u662f\u4e2a\u6deb\u50e7\uff0c\u5582\uff0c\u5927\u5e08\u5085\uff0c\u4ec0\u4e48\u53eb\u505a\u6deb\u50e7\u5440\uff1f\u201d\u4e86\u56e0\u9762\u8272\u9752\u91cc\u6cdb\u7ea2\uff0c\u5341\u5206\u5c34\u5c2c\uff0c\u62ff\u4e0d\u51c6\u56db\u7687 \u5b50\u662f\u4e0d\u662f\u8bf4\u8fc7\u8fd9\u8bdd\u3002 \u3000\u3000\u5415\u56db\u5a18\u5728\u4e0a\u9762\u542c\u5f97\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u6c14\u8005\u662f\u81ea\u5df1\u5e08\u5085\uff0c\u5728\u6b66\u6797\u4e2d\u88ab\u5c0a\u4e3a\u201c\u5723\u5c3c\u201d\uff0c\u5374\u6709\u8fd9 \u6837\u4e00\u4e2a\u5f1f\u5b50\uff0c\u80cc\u4e0a\u201c\u6deb\u50e7\u201d\u4e4b\u540d\uff0c\u5e08\u5085\u4e5d\u6cc9\u6709\u77e5\uff0c\u6b7b\u4e0d\u7791\u76ee\uff1b\u7b11\u8005\u662f\u4e86\u56e0\u5728\u8fd9\u5973\u5a03\u4e4b\u524d\uff0c\u4f5c \u51fa\u4e00\u80a1\u5c0a\u4e25\u4e4b\u72b6\uff0c\u88ab\u5979\u90a3\u4e48\u4e00\u7b11\uff0c\u771f\u662f\u65e0\u5730\u81ea\u5bb9\u3002\u6b63\u5728\u597d\u6c14\u597d\u7b11\u4e4b\u9645\uff0c\u5c4b\u5916\u6811\u836b\u4e0b\u4eba\u5f71\u4e00 \u95ea\uff0c\u4e00\u4e2a\u4eba\u5530\u7684\u7a9c\u4e86\u51fa\u6765\uff0c\u8eb2\u5230\u5c4b\u89d2\u6697\u9edd\u4e4b\u5904\uff0c\u628a\u8033\u8d34\u5899\uff0c\u5077\u542c\u91cc\u9762\u7684\u8bdd\u3002\u5415\u56db\u5a18\u5fc3\u9053\uff1a \u201c\u5514\uff0c\u4ed6\u4e5f\u6765\u4e86\uff0c\u80c6\u5b50\u5012\u771f\u4e0d\u5c0f\uff01\u201d\u8fd9\u4eba\u6b63\u662f\u90a3\u4e2a\u201c\u674e\u516c\u5b50\u201d\u3002 \u3000\u3000\u5c4b\u4e2d\u90a3\u5973\u5b69\u5b50\u53c8\u9053\uff1a\u201c\u6211\u5728\u5bab\u91cc\u95f7\u5f97\u53d1\u614c\uff0c\u51fa\u6765\u73a9\u73a9\uff0c\u4f60\u4eec\u4f55\u5fc5\u8fd9\u6837\u7d27\u5f20\uff0c\u660e\u5929\u6211\u5c31\u81ea \u5df1\u56de\u53bb\u3002\u201d\u4e86\u56e0\u9053\uff1a\u201c\u56db\u8d1d\u52d2\u53eb\u4f60\u548c\u6211\u4e00\u540c\u56de\u53bb\u3002\u201d\u90a3\u5973\u5b69\u5b50\u9053\uff1a\u201d\u4ed6\u771f\u7684\u8fd9\u6837\u8bf4\uff1f\u201d\u4e86\u56e0 \u751f\u6c14\u9053\uff1a\u201c\u4f60\u5728\u80e1\u8bf4\uff0c\u770b\u6211\u8d4f\u4f60\u8033\u74dc\u5b50\u3002\u201d\u7ad9\u4e86\u8d77\u6765\uff0c\u4f38\u51fa\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u4f5c\u52bf\u6293\u5979\u3002\u9752\u8863 \u5987\u4eba\u6025\u5fd9\u62e6\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\uff0c\u4f60\u4e0d\u5bb3\u81ca\uff0c\u5413\u552c\u5b69\u5b50\u5417\uff1f\u201d\u5973\u5b69\u5b50\u4e00\u6e9c\u70df\u8dd1\u51fa\u5c4b\u53bb\u3002\u5c4b\u89d2\u90a3\u4e2a \u201c\u674e\u516c\u5b50\u201d\u55d6\u7684\u63a0\u51fa\uff0c\u62d4\u6b65\u4fbf\u8ffd\uff01 \u3000\u3000\u4e86\u56e0\u5927\u53eb\u9053\uff1a\u201c\u6709\u8d3c\uff01\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u98de\u63a0\u51fa\u53bb\u3002\u5415\u56db\u5a18\u77e5\u9053\u90a3\u674e\u516c\u5b50\u7edd\u975e\u4ed6\u7684\u5bf9\u624b\uff0c\u77ac \u606f\u4e4b\u95f4\uff0c\u5df2\u98de\u8fc7\u4e24\u95f4\u5c4b\u9762\uff0c\u9752\u8863\u5987\u4eba\u4e5f\u8ffd\u4e86\u51fa\u6765\uff0c\u5415\u56db\u5a18\u4e00\u8038\u8eab\uff0c\u63a0\u8fc7\u4e00\u95f4\u5c4b\u9762\uff0c\u6717\u58f0\u53eb \u9053\uff1a\u201c\u4e86\u56e0\uff0c\u4f60\u6562\u6765\u4e0e\u6211\u51b3\u4e00\u6b7b\u6218\u5417\uff1f\u201d\u4e86\u56e0\u5927\u5403\u4e00\u60ca\uff0c\u731b\u7136\u6536\u6b65\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u5415\u56db\u5a18\u53cc\u624b \u8fde\u626c\uff0c\u516d\u628a\u7cbe\u5149\u95ea\u76ee\u7684\u5c0f\u5315\u9996\u5316\u6210\u516d\u9053\u94f6\u8679\uff0c\u9f50\u5411\u4e86\u56e0\u5954\u53bb\u3002 \u3000\u3000\u72ec\u81c2\u795e\u5c3c\u95e8\u4e0b\uff0c\u9664\u4e86\u56e0\u5916\uff0c\u6bcf\u4eba\u90fd\u7ec3\u6709\u81ea\u5df1\u7684\u72ec\u95e8\u6697\u5668\uff0c\u5982\u767d\u6cf0\u5b98\u7ec3\u7684\u662f\u6885\u82b1\u9488\uff0c\u7518\u51e4 \u6c60\u5415\u56db\u5a18\u7ec3\u7684\u662f\u98de\u5200\uff0c\u5415\u56db\u5a18\u7684\u98de\u5200\u4e0e\u7518\u51e4\u6c60\u53c8\u6709\u4e0d\u540c\uff0c\u9664\u4e86\u6bd4\u7518\u51e4\u6c60\u7684\u5200\u66f4\u77ed\u4e4b\u5916\uff0c\u800c\u4e14 \u5200\u67c4\u697c\u7a7a\uff0c\u98de\u51fa\u6765\uff0c\u53d1\u51fa\u53ee\u53ee\u5f53\u5f53\u54cd\uff0c\u60ca\u5fc3\u52a8\u9b44\uff0c\u4e86\u56e0\u529f\u529b\u6781\u9ad8\uff0c\u62a1\u8d77\u7985\u6756\uff0c\u516d\u70b3\u5315\u9996\uff0c\u5168 \u7ed9\u9707\u98de\uff0c\u7136\u800c\u90a3\u98de\u5200\u602a\u54cd\uff0c\u4e5f\u6270\u5f97\u4eba\u5fc3\u5934\u70e6\u8e81\uff0c\u5c31\u5728\u4e86\u56e0\u5fc3\u795e\u4e0d\u5b9a\u4e4b\u9645\uff0c\u5415\u56db\u5a18\u65bd\u5c55\u7edd\u9876\u8f7b \u529f\uff0c\u547c\u7684\u4e00\u58f0\u4ece\u4ed6\u8eab\u65c1\u63a0\u8fc7\uff0c\u4e00\u56de\u9996\uff0c\u53c8\u662f\u516d\u628a\u98de\u5200\uff0c\u5f85\u4e86\u56e0\u5c06\u98de\u5200\u5168\u6253\u843d\u65f6\uff0c\u5415\u56db\u5a18\u5df2\u98de \u8eab\u51fa\u629a\u8859\u53bb\u4e86\u3002\u4e86\u56e0\u77e5\u9053\u8ffd\u5979\u4e0d\u6025\uff0c\u5012\u62d6\u7985\u6756\u8ffd\u98de\u4e0b\u5c4b\u6765\u3002\u9752\u8863\u5987\u4eba\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\uff0c\u71d5\u513f \u5462\uff1f\u201d\u4e86\u56e0\u9053\uff1a\u201c\u4e5f\u8d70\u4e86\uff01\u201d\u9752\u8863\u5987\u4eba\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u8ffd\u5440\u3002\u201d\u4e86\u56e0\u8d4c\u6c14\u9053\uff1a\u201c\u4f60\u53bb\u8ffd\u5427\uff01\u6211 \u8ffd\u8fd9\u5c0f\u5b50\uff0c\u6709\u5982\u6b64\u5927\u80c6\uff0c\u539f\u6765\u662f\u5415\u56db\u5a18\u8fd9\u8d3c\u5a62\u6697\u4e2d\u5e2e\u5979\u3002\u201d\u9752\u8863\u5987\u4eba\u89c1\u8fc7\u5415\u56db\u5a18\u672c\u9886\uff0c\u5355\u8eab \u90a3\u91cc\u6562\u8ffd\uff1f \u3000\u3000\u5415\u56db\u5a18\u8df3\u51fa\u629a\u8859\uff0c\u8dc3\u4e0a\u6c11\u623f\uff0c\u805a\u62e2\u76ee\u5149\uff0c\u56db\u4e0b\u4e00\u671b\uff0c\u53ea\u89c1\u897f\u5317\u89d2\u4e00\u6761\u9ed1\u5f71\uff0c\u75be\u5982\u5954\u9a6c\uff0c \u76f4\u5954\u51fa\u57ce\uff0c\u5728\u9ed1\u5f71\u4e4b\u524d\uff0c\u9690\u9690\u89c1\u7740\u4e00\u70b9\u9ed1\u70b9\uff0c\u6eda\u52a8\u6709\u5982\u6d41\u661f\u3002\u5415\u56db\u5a18\u77e5\u9053\u9ed1\u70b9\u5b9a\u662f\u90a3\u5973\u5b69 \u5b50\uff0c\u80cc\u540e\u90a3\u9ed1\u5f71\u5f53\u7136\u662f\u90a3\u4e2a\u5c11\u5e74\u4e86\u3002\u5415\u56db\u5a18\u5fc3\u60f3\uff1a\u8fd9\u4e24\u4eba\u751a\u662f\u602a\u5f02\uff0c\u4e14\u53bb\u8ffd\u4ed6\u3002\u9ed1\u5f71\u5df2\u51fa\u4e86 \u57ce\uff0c\u5415\u56db\u5a18\u624d\u98de\u8eab\u8ffd\u8d76\uff0c\u8ffd\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u9ed1\u5f71\u6e10\u73b0\uff0c\u5415\u56db\u5a18\u8fd9\u624d\u653e\u6162\u811a\u6b65\u3002\u90a3\u5c11\u5e74\u8f7b\u529f\uff0c\u4e5f \u662f\u7b2c\u4e00\u6d41\u4e86\uff0c\u7136\u800c\u5415\u56db\u5a18\u7d27\u8ddf\u4ed6\u7684\u8eab\u540e\uff0c\u4ed6\u7adf\u7136\u4e1d\u6beb\u4e5f\u4e0d\u77e5\u9053\uff01 \u3000\u3000\u5c11\u5e74\u5df2\u5954\u5230\u6e56\u6ee8\u897f\u5cb8\uff0c\u5ffd\u7136\u8dd1\u4e0a\u4e00\u5ea7\u4e34\u6e56\u7684\u9ad8\u5c71\uff0c\u6b64\u5c71\u540d\u4e3a\u201c\u845b\u5cad\u201d\uff0c\u5728\u5b9d\u77f3\u5c71\u4e0e\u6816\u971e \u5cad\u4e4b\u95f4\uff0c\u76f8\u4f20\u53e4\u4ed9\u4eba\u845b\u6d2a\u66fe\u5728\u8fd9\u5ea7\u5c71\u4e0a\u50cf\u8fc7\u4e39\uff0c\u6240\u4ee5\u540e\u4eba\u628a\u8fd9\u5ea7\u5c71\u53eb\u505a\u845b\u5cad\u3002\u8fd9\u65f6\u8dd1\u5728\u524d\u9762 \u7684\u90a3\u5973\u5b69\u5b50\u5df2\u8dd1\u5230\u5c71\u4e0a\uff0c\u5c11\u5e74\u8ffd\u5230\u5c71\u4e0a\uff0c\u53ea\u89c1\u602a\u77f3\u6797\u7acb\uff0c\u5973\u5b69\u5f71\u5b50\u5df2\u7ecf\u4e0d\u89c1\uff0c\u5c11\u5e74\u5927\u53eb\u9053\uff1a \u201c\u745b\u59b9\uff0c\u745b\u59b9\uff01\u201d\u5c71\u98ce\u9001\u58f0\uff0c\u7fa4\u5cf0\u56de\u54cd\uff0c\u5374\u4e0d\u89c1\u4eba\u56de\u7b54\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u53c8\u53eb\u4e86\u4e24\u58f0\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u5728\u80cc\u540e\u7b11\u9053\uff1a\u201c\u5979\u4e0d\u8ba4\u4f60\uff0c\u4f60\u53eb\u5979\u505a\u751a\uff1f\u201d\u5c11\u5e74\u5927\u5403\u4e00 \u60ca\uff0c\u4e0d\u6562\u56de\u5934\uff0c\u5148\u6a2a\u8dc3\u4e09\u6b65\uff0c\u62d4\u51fa\u5251\u6765\uff0c\u7136\u540e\u65cb\u8eab\u51dd\u89c6\u3002\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u606d\u559c\u9601\u4e0b\uff0c\u4eca\u65e5\u5927 \u96be\u4e0d\u6b7b\uff0c\u5fc5\u6709\u540e\u798f\u3002\u201d\u5c11\u5e74\u770b\u6e05\u695a\u4e86\u5415\u56db\u5a18\u662f\u8c01\uff0c\u7591\u5fc3\u5927\u8d77\uff0c\u559d\u9053\uff1a\u201c\u4f60\u9ed1\u591c\u8ddf\u8e2a\u610f\u6b32\u4f55 \u4e3a\uff1f\u201d\u539f\u6765\u65e5\u95f4\u5728\u8336\u5c45\u4e4b\u65f6\uff0c\u90a3\u5c11\u5e74\u773c\u89c1\u5415\u56db\u5a18\u5c06\u8f66\u9f0e\u4e30\u6293\u53bb\uff0c\u5e76\u4e0d\u77e5\u5979\u662f\u4e3a\u4e86\u6551\u4eba\uff0c\u8fd8\u6000 \u7591\u5979\u4e5f\u662f\u90a3\u201c\u75af\u5a46\u5b50\u201d\u4e00\u8def\uff1b\u4e4b\u540e\u5415\u56db\u5a18\u7518\u51e4\u6c60\u5728\u6e56\u4e0a\u6076\u6597\u4e86\u56e0\u4e4b\u65f6\uff0c\u4ed6\u53c8\u5df2\u8dcc\u843d\u6e56\u5e95\uff0c\u6f5c \u5165\u897f\u6e56\u91cc\u6e56\uff0c\u5e76\u672a\u770b\u89c1\u3002\u6240\u4ee5\u6478\u4e0d\u51c6\u5415\u56db\u5a18\u662f\u53cb\u662f\u654c\u3002 \u3000\u3000\u672c\u6765\u5415\u56db\u5a18\u53ea\u8981\u5c06\u4e8b\u60c5\u8bf4\u6e05\uff0c\u5c06\u540d\u5b50\u4eae\u51fa\uff0c\u5c31\u53ef\u65e0\u4e8b\u3002\u4f46\u5415\u56db\u5a18\u8eab\u8d1f\u56fd\u4ec7\u5bb6\u6068\uff0c\u4e0d\u80fd\u4e0d \u4efd\u5916\u5c0f\u5fc3\uff0c\u8fd9\u5c11\u5e74\u770b\u6765\u867d\u7136\u4e0d\u662f\u574f\u4eba\uff0c\u4f46\u5230\u5e95\u662f\u4e2a\u964c\u751f\u7684\u5916\u4e61\u6765\u5ba2\uff0c\u5415\u56db\u5a18\u81ea\u7136\u4e0d\u613f\u4e00\u4e0b\u5b50 \u5c06\u8eab\u4efd\u6296\u9732\u3002\u5c11\u5e74\u89c1\u5979\u4e45\u4e45\u4e0d\u7b54\uff0c\u6012\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u90a3\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff0c\u6211\u4e0e\u4f60\u7d20\u5473\u5e73\u751f\uff0c\u4f60 \u4e3a\u4f55\u8981\u591a\u7ba1\u95f2\u4e8b\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u60f3\u8d77\u8fd9\u5c11\u5e74\u602a\u5f02\u7684\u8eab\u6cd5\uff0c\u5fc3\u60f3\uff1a\u6211\u4e14\u8bd5\u8bd5\u4ed6\u7684\u672c\u4e8b\u3002\u6545\u610f\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u548c\u8f66\u8001 \u5934\u5e72\u5f97\u597d\u4e8b\uff01\u201d\u90a3\u5c11\u5e74\u9762\u8272\u500f\u53d8\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e8f\u4f60\u8fd9\u526f\u8eab\u624b\uff0c\u5c45\u7136\u505a\u9e70\u722a\u5b59\uff01\u201d\u80a9 \u5934\u5fae\u52a8\uff0c\u5237\u7684\u4e00\u5251\u523a\u6765\u3002\u5415\u56db\u5a18\u4e00\u95ea\u95ea\u5f00\uff0c\u628a\u5251\u64ce\u5728\u624b\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\u5f1f\u5b50\uff1f\u201d\u5c11\u5e74 \u5237\u5237\u4e24\u5251\uff0c\u51cc\u5389\u5f02\u5e38\uff0c\u6717\u7136\u7b54\u9053\uff1a\u201c\u8bf4\u4e86\u4f60\u4e5f\u4e0d\u77e5\u9053\uff01\u201d\u5728\u5251\u6cd5\u4e0a\u597d\u50cf\u5341\u5206\u81ea\u8d1f\u3002\u5415\u56db\u5a18\u6697 \u6697\u597d\u7b11\uff0c\u5fc3\u60f3\uff1a\u6709\u54ea\u4e00\u5bb6\u7684\u5251\u6cd5\u6211\u4e0d\u77e5\u9053\uff1f\u53ea\u8981\u4f60\u4f7f\u6ee1\u5341\u62db\uff0c\u6211\u4e0d\u628a\u4f60\u63ed\u7834\u624d\u602a\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u628a\u5251\u4e00\u6296\uff0c\u8d70\u504f\u950b\u6025\u4e0a\uff0c\u53c8\u662f\u659c\u8170\u4e00\u5251\u3002\u5415\u56db\u5a18\u518d\u4e0d\u8eb2\u95ea\uff0c\u770b\u51c6\u4ed6\u8fd9\u62db\u4e43\u662f\u6b66\u5f53 \u6d3e\u7684\u201c\u5b54\u96c0\u5254\u7fce\u201d\uff0c\u4f7f\u7684\u4e43\u662f\u5251\u950b\u523a\u6233\u4e4b\u52b2\uff0c\u4e8e\u662f\u5e73\u5251\u4e00\u538b\uff0c\u4f7f\u7684\u662f\u7384\u5973\u5251\u6cd5\u4e2d\u7684\u201c\u5012\u8f6c\u9634 \u9633\u201d\uff0c\u971c\u534e\u5b9d\u5251\u4e00\u6c89\u4e00\u63d0\uff0c\u6ee1\u4ee5\u90a3\u5c11\u5e74\u5fc5\u7136\u88ab\u8feb\u64a4\u5251\uff1b\u4e0d\u6599\u5c11\u5e74\u5251\u62db\u602a\u7edd\uff0c\u89c1\u5415\u56db\u5a18\u5e73\u5251\u6765 \u538b\uff0c\u5251\u628a\u4e00\u6296\uff0c\u5251\u8eab\u4e00\u98a4\uff0c\u5ffd\u7136\u53cd\u524a\u8fc7\u6765\uff01\u5415\u56db\u5a18\u51e0\u4e4e\u7740\u4e86\u9053\u513f\uff0c\u5e78\u800c\u5979\u7684\u7384\u5973\u5251\u6cd5\u5df2\u5230\u7089 \u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u7f29\u5251\u4e00\u7ede\uff0c\u9a6c\u4e0a\u89e3\u4e86\u654c\u4eba\u7684\u62db\u6570\uff0c\u5c11\u5e74\u62a2\u51fa\u4e24\u6b65\uff0c\u53cd\u8eab\u53c8\u662f\u4e00\u5251\uff0c\u8fd9\u5251\u660e\u662f\u5d69 \u9633\u6d3e\u7684\u201c\u51e4\u51f0\u5c55\u7fc5\u201d\uff0c\u5251\u52bf\u5e94\u8be5\u81ea\u5de6\u800c\u53f3\uff0c\u5415\u56db\u5a18\u901a\u6653\u5404\u5bb6\u5251\u6cd5\uff0c\u8eab\u5f62\u5fae\u52a8\uff0c\u5df2\u5148\u622a\u81f3\u5de6 \u65b9\uff0c\u4e0d\u6599\u5c11\u5e74\u5251\u5230\u4e2d\u9014\uff0c\u500f\u7136\u4e00\u53d8\uff0c\u76f4\u523a\u53f3\u80a9\uff0c\u5415\u56db\u5a18\u56de\u5251\u4e0d\u53ca\uff0c\u53ea\u597d\u4ed7\u7740\u7edd\u9876\u8f7b\u529f\uff0c\u8eab\u8eaf \u4e00\u626d\uff0c\u95ea\u7535\u822c\u7684\u907f\u5f00\u8fd9\u5251\u3002 \u3000\u3000\u5415\u56db\u5a18\u5927\u4e3a\u60ca\u5f02\uff0c\u8fd9\u5c11\u5e74\u5251\u62db\u602a\u7edd\uff0c\u771f\u662f\u89c1\u6240\u672a\u89c1\uff0c\u6025\u628a\u7384\u5973\u5251\u6cd5\u4e2d\u7684\u9632\u8eab\u4e09\u5341\u516d\u8def\u8fde \u73af\u5251\u6cd5\u65bd\u5c55\u51fa\u6765\uff0c\u5b9d\u5251\u821e\u6210\u4e00\u4e2a\u5706\u5708\u3001\u9996\u5c3e\u76f8\u8fde\uff0c\u6ef4\u6c34\u4e0d\u5165\u3002\u800c\u5728\u9632\u5b88\u4e4b\u4e2d\uff0c\u4e5f\u6742\u4ee5\u653b\u51fb\u7684 \u62db\u6570\u3002\u5c11\u5e74\u75be\u98ce\u66b4\u96e8\u822c\u7684\u72e0\u72e0\u653b\u51fb\uff0c\u76f4\u62c6\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u5415\u56db\u5a18\u5c1a\u672a\u770b\u51fa\u4ed6\u7684\u5bb6\u6570\uff01 \u3000\u3000\u5c11\u5e74\u5251\u6cd5\u867d\u602a\uff0c\u4f46\u5415\u56db\u5a18\u4f7f\u7684\u4e43\u662f\u6b63\u5b97\u5251\u672f\uff0c\u7cbe\u5999\u5f02\u5e38\uff0c\u867d\u7136\u4e00\u65f6\u95f4\u6478\u4e0d\u7740\u5bf9\u65b9\u8def\u6570\uff0c \u4e0d\u6562\u653e\u624b\u653b\u51fb\uff0c\u7528\u6765\u5e94\u4ed8\uff0c\u5374\u662f\u6e38\u5203\u6709\u4f59\u3002 \u3000\u3000\u5415\u56db\u5a18\u4e0d\u77e5\uff0c\u90a3\u5c11\u5e74\u6bd4\u5979\u66f4\u4e3a\u70e6\u8e81\u3002\u5415\u56db\u5a18\u6478\u4e0d\u7740\u4ed6\u7684\u8def\u6570\uff0c\u4ed6\u4e5f\u540c\u6837\u6478\u4e0d\u7740\u5415\u56db\u5a18\u7684 \u8def\u6570\uff0c\u53ea\u89c9\u5415\u56db\u5a18\u7684\u5251\u6cd5\u7cbe\u5fae\u5965\u5999\uff0c\u4f3c\u4e4e\u53ea\u6709\u5929\u5c71\u5251\u6cd5\u53ef\u582a\u6bd4\u62df\u3002\u66f4\u517c\u5415\u56db\u5a18\u529f\u529b\u53c8\u6bd4\u4ed6 \u9ad8\uff0c\u518d\u6597\u4e09\u4e94\u5341\u62db\uff0c\u4ed6\u5df2\u9762\u7ea2\u6c14\u5598\uff0c\u800c\u5415\u56db\u5a18\u72b9\u662f\u6c14\u5b9a\u795e\u95f2\uff01 \u3000\u3000\u5c11\u5e74\u4e00\u6025\uff0c\u5251\u62db\u5c55\u5f97\u66f4\u5feb\u3002\u5415\u56db\u5a18\u5e26\u653b\u5e26\u5b88\uff0c\u7559\u5fc3\u89c2\u5bdf\uff0c\u53ea\u89c9\u8fd9\u5c11\u5e74\u7684\u5251\u6cd5\u597d\u50cf\u535a\u91c7\u5404 \u5bb6\uff0c\u4f46\u6bcf\u4e00\u62db\u90fd\u548c\u6b63\u5e38\u7684\u5251\u6cd5\u76f8\u53cd\u3002\u4f8b\u5982\u6b66\u5f53\u6d3e\u4e2d\u7684\u201c\u65e0\u5e38\u593a\u547d\u201d\u4e00\u62db\uff0c\u5251\u52bf\u5e94\u81ea\u4e0a\u800c\u4e0b\uff0c \u523a\u5411\u4e0b\u76d8\uff1b\u800c\u5c11\u5e74\u4f7f\u8fd9\u4e00\u62db\u65f6\uff0c\u5374\u662f\u81ea\u4e0b\u800c\u4e0a\uff0c\u523a\u5411\u4e2d\u76d8\u3002\u53c8\u5982\u5d69\u9633\u6d3e\u7684\u201c\u62bd\u64a4\u8fde\u73af\u201d\u4e00 \u62db\uff0c\u5e94\u8be5\u662f\u5de6\u4e09\u5251\uff0c\u53f3\u4e00\u5251\uff0c\u518d\u5411\u4e2d\u95f4\u75be\u523a\u4e24\u5251\uff1b\u800c\u5728\u4ed6\u624b\u4e2d\uff0c\u5374\u662f\u5148\u5411\u53f3\u65b9\u523a\u4e09\u5251\uff0c\u518d\u5411 \u5de6\u65b9\u523a\u4e24\u5251\uff0c\u7136\u540e\u5206\u5fc3\u76f4\u523a\u4e00\u5251\u3002\u5415\u56db\u5a18\u4e0e\u4ed6\u6597\u4e86\u4e00\u767e\u62db\u540e\uff0c\u604d\u7136\u5927\u609f\uff0c\u6a2a\u5251\u4e00\u5c01\uff0c\u5c06\u5c11\u5e74 \u903c\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u662f\u767d\u53d1\u9b54\u5973\u7684\u5ae1\u7cfb\u4f20\u4eba\uff01\u4f60\u5e08\u5085\u4e0d\u662f\u98de\u7ea2\u5dfe\u5c31\u662f\u6b66\u743c\u7476\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u5c06\u90a3\u5c11\u5e74\u7684\u5e08\u627f\u6d3e\u522b\u63ed\u7834\uff0c\u90a3\u5c11\u5e74\u5927\u5403\u4e00\u60ca\uff0c\u6a2a\u5251\u5f53\u80f8\u4e0d\u6562\u8fdb\u62db\uff01\u5415\u56db\u5a18\u5c06\u5251\u63d2 \u5165\u9798\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u6597\u4e86\uff0c\u6211\u548c\u4f60\u6597\u6ee1\u767e\u62db\uff0c\u624d\u77e5\u4f60\u7684\u5bb6\u6570\uff0c\u6211\u5df2\u662f\u7518\u62dc\u4e0b\u98ce\uff01\u201d \u3000\u3000\u5c11\u5e74\u77aa\u5927\u53cc\u773c\uff0c\u53c8\u662f\u7591\u60d1\uff0c\u53c8\u662f\u7f9e\u60ed\uff0c\u5bf9\u65b9\u7684\u5251\u6cd5\u660e\u660e\u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c\u600e\u4e48\u5374\u53cd\u800c\u8ba4\u8f93\uff1f \u800c\u4e14\u66f4\u4ee4\u4ed6\u60ed\u6127\u662f\uff1a\u5415\u56db\u5a18\u770b\u51fa\u4e86\u4ed6\u7684\u5bb6\u6570\uff0c\u800c\u4ed6\u5bf9\u5415\u56db\u5a18\u7684\u5251\u6cd5\u5374\u8fd8\u6a21\u4e0d\u7740\u5934\u8111\u3002\u5f53\u4e0b\u7531 \u4e0d\u5f97\u62b1\u5251\u4f5c\u63d6\uff0c\u9053\uff1a\u201c\u6211\u8ba4\u8f93\u4e86\uff0c\u4f60\u82e5\u8981\u6349\u6211\uff0c\u6211\u675f\u624b\u5c31\u64d2\uff01\u201d\u5415\u56db\u5a18\u5927\u7b11\u9053\uff1a\u201c\u8c01\u8981\u6349 \u4f60\uff0c\u4f60\u542c\u8fc7\u72ec\u81c2\u795e\u5c3c\u7684\u540d\u5b57\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5c11\u5e74\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u4e00\u63d6\u5230\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u5144\u53f0\u662f\u72ec\u81c2\u795e\u5c3c\u95e8\u4e0b\uff0c\u6c5f\u5357\u4e03\u4fa0\u4e2d\u4eba \u4e86\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u90a3\u5c11\u5e74\u77a7\u4e86\u4e00\u773c\uff0c\u5ffd\u7136\u9762\u5448\u7591\u60d1\u4e4b\u5bb9\u3002\u539f\u6765\u4ed6\u66fe\u968f\u6bcd\u4eb2\u6b66\u743c \u7476\uff0c\u5728\u5929\u5c71\u5317\u9ad8\u5cf0\u548c\u6613\u5170\u73e0\u95f2\u5750\u8bba\u5251\uff0c\u6613\u5170\u73e0\u8bf4\uff1a\u201c\u5f53\u4eca\u5929\u4e0b\uff0c\u6709\u56db\u6d3e\u5251\u6cd5\uff0c\u5404\u64c5\u80dc\u573a\uff0c\u96be \u5206\u8f7b\u91cd\u3002\u8fd9\u56db\u6d3e\u4e00\u662f\u6666\u660e\u7985\u5e08\u4f20\u4e0b\u7684\u5929\u5c71\u5251\u6cd5\uff0c\u4e8c\u662f\u8fbe\u6469\u7956\u5e08\u4f20\u4e0b\uff0c\u6b66\u5f53\u6d3e\u5317\u652f\u5b97\u5e08\u6842\u4ef2\u660e \u8c31\u8865\u7684\u8fbe\u6469\u5251\u6cd5\uff1b\u4e09\u662f\u72ec\u81c2\u795e\u5c3c\u6240\u7528\u7684\u7384\u5973\u5251\u6cd5\uff0c\u7b2c\u56db\u5c31\u662f\u767d\u53d1\u9b54\u5973\u4f20\u4e0b\u7684\u72ec\u95e8\u5251\u6cd5\u4e86\u3002\u201d \u5f53\u65f6\u6b66\u743c\u7476\u9053\uff1a\u201c\u5929\u5c71\u5251\u6cd5\u4e4b\u535a\uff0c\u8fbe\u6469\u5251\u6cd5\u4e4b\u5947\uff0c\u7384\u5973\u5251\u6cd5\u4e4b\u5999\uff0c\u4e09\u5bb6\u5e76\u79f0\uff0c\u90a3\u7684\u786e\u662f\u96be\u5206 \u9ad8\u4e0b\uff0c\u6211\u8fd9\u95e8\u5251\u6cd5\u5931\u4e4b\u4e8e\u504f\uff0c\u59d0\u59d0\u4e5f\u62ff\u6765\u76f8\u63d0\u5e76\u8bba\uff0c\u90a3\u5c82\u4e0d\u4ee4\u6211\u6c57\u989c\u3002\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u59d0\u59d0 \u672a\u514d\u592a\u8c26\u4e86\uff0c\u8bba\u5230\u5947\u8be1\u8f9b\u8fa3\uff0c\u59d0\u59d0\u8fd9\u4e00\u95e8\u5251\u6cd5\u8fd8\u8981\u80dc\u8fc7\u4e0a\u8ff0\u4e09\u5bb6\u3002\u201d\u6b66\u63a0\u7476\u53f9\u9053\uff1a\u201c\u4e09\u5bb6\u5251 \u6cd5\u4e4b\u4e2d\uff0c\u53ef\u60dc\u7384\u5973\u5251\u6cd5\u6211\u5c1a\u672a\u89c1\u8fc7\u3002\u201d\u6b66\u743c\u7476\u53ea\u63d0\u4e09\u5bb6\uff0c\u59cb\u7ec8\u4e0d\u6562\u5c06\u81ea\u5df1\u7684\u5251\u6cd5\u4e0e\u4e4b\u4e95\u5217\uff0c \u90a3\u81ea\u7136\u662f\u5979\u7684\u8c26\u865a\u3002\u6613\u5170\u73e0\u7b11\u9053\uff1a\u201c\u542c\u59d0\u59d0\u6b64\u8a00\uff0c\u6211\u5ffd\u53d1\u5947\u60f3\uff0c\u82e5\u8bf7\u56db\u6d3e\u5251\u5ba2\u5230\u5929\u5c71\u4e00\u4f1a\u4e5f \u662f\u76db\u4e8b\u3002\u53ea\u6050\u4eba\u5bff\u6709\u9650\uff0c\u5947\u60f3\u6210\u7a7a\u3002\u90a3\u7384\u5973\u5251\u6cd5\u6211\u5012\u89c1\u8fc7\uff0c\u4e09\u5341\u5e74\u524d\u72ec\u81c2\u795e\u5c3c\u4e0a\u5929\u5c71\u4e4b\u65f6\uff0c \u53ef\u60dc\u59d0\u59d0\u4e0d\u5728\u8fd9\u91cc\u3002\u5979\u7684\u5251\u6cd5\u53ea\u4f20\u5173\u95e8\u7684\u5973\u5f1f\u5b50\u5415\u56db\u5a18\uff0c\u5415\u56db\u5a18\u8eab\u8d1f\u56fd\u4ec7\u5bb6\u6068\uff0c\u7ec8\u65e5\u5728\u6c5f\u6e56 \u5954\u8dd1\uff0c\u6050\u6015\u672a\u5fc5\u80fd\u5230\u5929\u5c71\u5462\u3002\u201d \u3000\u3000\u5c11\u5e74\u5fc6\u8d77\u5f53\u5e74\u4e4b\u8bdd\uff0c\u5fc3\u4e2d\u6697\u5947\u4f55\u4ee5\u9762\u524d\u8fd9\u4eba\u5374\u662f\u7537\u5b50\u3002\u5415\u56db\u5a18\u4e00\u7b11\u63ed\u4e0b\u5934\u5dfe\uff0c\u9053\uff1a\u201c\u6211 \u5c31\u662f\u5415\u56db\u5a18\uff0c\u6562\u8bf7\u5144\u53f0\u9ad8\u59d3\u5927\u540d\uff1f\u201d\u5c11\u5e74\u9053\uff1a\u201c\u6211\u53eb\u674e\u6cbb\uff0c\u59d0\u59d0\u6240\u8bf4\u7684\u6b66\u743c\u7476\u6b63\u662f\u5bb6\u6bcd\u3002\u201d \u5415\u56db\u5a18\u62f1\u624b\u9053\uff1a\u201c\u539f\u6765\u5144\u53f0\u4e43\u662f\u95ef\u738b\u540e\u88d4\uff0c\u5931\u656c\uff0c\u5931\u656c\uff01\u201d \u3000\u3000\u539f\u6765\u5f53\u5e74\u201c\u4e03\u5251\u201d\u5f52\u9690\u5929\u5c71\uff0c\u6b66\u743c\u7476\u4e0e\u674e\u601d\u6c38\u6210\u4eb2\uff0c\u5728\u767d\u53d1\u9b54\u5973\u7684\u65e7\u65e5\u5c45\u5929\u5c71\u5357\u9ad8\u5cf0\u4e0a \u9690\u5c45\uff0c\u53ea\u751f\u4e00\u5b50\uff0c\u5c31\u662f\u674e\u6cbb\u3002\u674e\u6cbb\u5341\u5c81\u4e4b\u65f6\uff0c\u7236\u4eb2\u53bb\u4e16\uff0c\u7531\u6bcd\u4eb2\u4f20\u4ed6\u72ec\u95e8\u5251\u6cd5\u3002\u518d\u8fc7\u4e24\u5e74\uff0c \u6613\u5170\u73e0\u628a\u51af\u745b\u5e26\u5230\u5929\u5c71\u5317\u9ad8\u5cf0\uff0c\u90a3\u65f6\u51af\u745b\u8fd8\u53ea\u6709\u4e03\u5c81\u3002\u5929\u5c71\u5357\u5317\u4e24\u5cf0\u76f8\u8ddd\u5343\u91cc\uff0c\u6b66\u743c\u7476\u6bcf\u5e74 \u5fc5\u6765\u62dc\u4f1a\u6613\u5170\u73e0\u4e00\u6b21\u3002\u6240\u4ee5\u674e\u6cbb\u548c\u51af\u745b\u7b97\u5f97\u662f\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\uff0c\u674e\u6cbb\u6bd4\u4ed6\u5e74\u957f\u516d\u5e74\uff0c\u4e00\u5411\u62ff\u5979 \u5f53\u59b9\u59b9\u770b\u5f85\u3002\u516d\u4e03\u5e74\u524d\uff0c\u6613\u5170\u73e0\u518d\u5230\u4e2d\u539f\uff0c\u51af\u745b\u5c31\u6258\u6b66\u743c\u7476\u7167\u987e\u3002\u5230\u6613\u5170\u73e0\u56de\u6765\u540e\uff0c\u65b9\u518d\u628a \u5979\u9886\u56de\u3002\u6709\u6b64\u4e00\u6bb5\u6e0a\u6e90\uff0c\u6240\u4ee5\u674e\u6cbb\u548c\u51af\u745b\u5341\u5206\u8981\u597d\u3002 \u3000\u3000\u5415\u56db\u5a18\u4e0e\u674e\u6cbb\u4e92\u901a\u59d3\u540d\uff0c\u5f7c\u6b64\u656c\u4ef0\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u674e\u5144\u79bb\u5929\u5c71\u591a\u4e45\u4e86\uff0c\u90a3\u8f66\u8001\u5934\u5b50\u53c8\u662f \u4f55\u4eba\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u6211\u9ad8\u5929\u5c71\u672a\u6ee1\u4e24\u5e74\uff0c\u8f66\u4e16\u4f2f\u4ee5\u524d\u5728\u56db\u5ddd\u4e4b\u65f6\u66fe\u548c\u5bb6\u7236\u5171\u4e8b\u3002\u6211\u8fd9\u6b21\u4e0b \u5c71\uff0c\u5bb6\u6bcd\u66fe\u5f00\u5217\u540d\u5355\u7ed9\u6211\uff0c\u53eb\u6211\u904d\u8bbf\u7236\u4eb2\u65e7\u90e8\uff0c\u770b\u6709\u51e0\u4eba\u8fd8\u5728\u4eba\u95f4\uff1f\u524d\u51e0\u5929\u6211\u6258\u670b\u53cb\u5c06\u6211\u5230 \u676d\u5dde\u7684\u6d88\u606f\u7980\u77e5\u8f66\u8001\u4f2f\uff0c\u4ed6\u5c31\u7ea6\u6211\u6628\u65e5\u5728\u4e09\u6f6d\u660e\u6708\u76f8\u4f1a\uff0c\u4e0d\u6599\u5374\u98de\u6765\u4e86\u90a3\u573a\u6a2a\u7978\uff0c\u5e78\u6211\u5c0f\u65f6 \u5e38\u5728\u5929\u5c71\u7edd\u9876\u7684\u2018\u5929\u6c60\u2019\u6e38\u6cf3\uff0c\u8fd8\u7565\u901a\u6c34\u6027\uff0c\u8981\u4e0d\u7136\u90a3\u5c31\u65e0\u8f9c\u906d\u53d7\u706d\u9876\u4e4b\u707e\u4e86\u3002\u4e0d\u77e5\u90a3\u51f6\u618e \u662f\u4ec0\u4e48\u4eba\uff1f\u6b66\u529f\u5982\u6b64\u4e86\u5f97\uff01\u201d\u5415\u56db\u5a18\u9053\u58f0\u201c\u60ed\u6127\u201d\uff0c\u628a\u4e86\u56e0\u6765\u5386\u544a\u8bc9\u4e86\u4ed6\uff0c\u627c\u8155\u55df\u53f9\u3002 \u3000\u3000\u674e\u6cbb\u9053\uff1a\u201c\u53ef\u6240\u89c1\u7684\u90a3\u4e2a\u5973\u5b69\u4e43\u6613\u8001\u524d\u8f88\u7231\u5f92\uff0c\u4e5f\u662f\u5979\u552f\u4e00\u7684\u4f20\u4eba\uff0c\u8bf7\u59d0\u59d0\u5e2e\u6211\u4e00\u540c\u5bfb \u627e\u3002\u201d\u5415\u56db\u5a18\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u60f3\uff1a\u90a3\u5973\u5b69\u6b66\u529f\u6781\u6742\uff0c\u600e\u4f1a\u662f\u6613\u5170\u73e0\u7684\u5f92\u5f1f\uff1f\u9053\uff1a\u201c\u674e\u5144\u6050\u6015\u8ba4 \u9519\u4eba\u5427\uff1f\u201d\u674e\u6cbb\u7b11\u9053\uff1a\u201c\u6211\u4e0e\u5979\u4e00\u540c\u73a9\u5927\uff0c\u600e\u4f1a\u8ba4\u9519\uff1f\u53ea\u662f\u4e0d\u77e5\u600e\u4e48\u5979\u5374\u4f3c\u8ff7\u5931\u672c\u6027\u4f3c\u7684\uff0c \u4ee4\u6211\u5341\u5206\u5fe7\u8651\uff01\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u65e2\u7136\u662f\u6613\u8001\u524d\u8f88\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u90a3\u6211\u5f53\u5f97\u6548\u52b3\uff0c\u5c3d\u5fc3\u5bfb\u627e\u3002\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u542c\u5f97\u201c\u545c\uff0c\u545c\uff0c\u545c\uff01\u201d\u4e09\u679d\u54cd\u7bad\uff0c\u4e00\u58f0\u957f\u4e24\u58f0\u77ed\uff0c\u4ece\u4e1c\u5357\u89d2\u53d1\u51fa\u3002\u5415\u56db\u5a18 \u5403\u4e86\u4e00\u60ca\uff0c\u5bf9\u674e\u6cbb\u9053\uff1a\u201c\u8bf7\u5144\u53f0\u89c1\u8c05\uff0c\u6211\u6709\u6025\u4e8b\uff0c\u5fc5\u987b\u8d76\u56de\u5ba2\u5bd3\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u600e\u4e48\u6df1\u591c\u6709 \u4eba\u53d1\u5c04\u90a3\u54cd\u7bad\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u90a3\u662f\u6211\u4eec\u540c\u95e8\u8054\u7edc\u7684\u4fe1\u53f7\uff01\u201d\u674e\u6cbb\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u65e2\u7136 \u5982\u6b64\uff0c\u5973\u4fa0\u8bf7\u4fbf\uff01\u201d\u5415\u56db\u5a18\u62f1\u4e86\u62f1\u624b\uff0c\u6b63\u60f3\u4e0b\u5c71\uff0c\u5ffd\u53c8\u8bf4\u9053\uff1a\u201c\u674e\u5144\uff0c\u4f60\u7684\u8f66\u8001\u4f2f\u5728\u6211\u90a3 \u513f\uff0c\u4f60\u660e\u65e5\u6765\u5427\u3002\u201d\u5f53\u4e0b\u5c06\u5ba2\u5bd3\u5730\u5740\u8bf4\u4e86\uff0c\u5c31\u5728\u845b\u5cad\u5c71\u811a\u7684\u4e1c\u5357\u89d2\u4e0a\uff0c\u501a\u5c71\u9762\u6e56\uff0c\u662f\u676d\u5dde\u6700 \u8457\u540d\u7684\u65c5\u9986\u3002\u674e\u6cbb\u559c\u9053\uff1a\u201c\u6211\u660e\u65e5\u7edd\u65e9\u4fbf\u53bb\u3002\u201d\u5415\u56db\u5a18\u8db3\u5c16\u4e00\u70b9\uff0c\u8eab\u8eaf\u6643\u5904\uff0c\u75be\u82e5\u6d41\u57a6\uff0c\u500f \u5ffd\u4e0d\u89c1\uff01\u674e\u6cbb\u5927\u4e3a\u4f69\u670d\u3002 \u3000\u3000\u95f9\u4e86\u534a\u591c\uff0c\u4e00\u94a9\u659c\u6708\uff0c\u6e10\u5411\u897f\u6c89\uff0c\u60f3\u5df2\u8fc7\u4e86\u4e94\u66f4\u4e86\u3002\u674e\u6cbb\u8df3\u4e0a\u4e00\u5757\u4e34\u7a7a\u7a81\u51fa\u7684\u5ca9\u77f3\u4e4b \u4e0a\uff0c\u56db\u5904\u4fcf\u671b\uff0c\u7a7a\u5c71\u9759\u5bc2\uff0c\u53ea\u6709\u677e\u98ce\u8fc7\u8033\uff0c\u8fdc\u5904\u6f6e\u97f3\u3002\u674e\u6cbb\u5927\u4e3a\u5931\u671b\uff0c\u5ffd\u542c\u5f97\u683c\u683c\u7684\u7b11\u58f0\uff0c \u8d77\u81ea\u8eab\u7554\uff0c\u674e\u6d3d\u6025\u5fd9\u8df3\u4e0b\uff0c\u53eb\u9053\uff1a\u201c\u745b\u59b9\uff0c\u745b\u59b9\uff01\u201d\u65e5\u95f4\u90a3\u5973\u5b69\u5b50\u7a81\u7136\u4ece\u5c71\u77f3\u540e\u9762\u95ea\u4e86\u51fa \u6765\uff0c\u683c\u683c\u7b11\u9053\uff1a\u201c\u6211\u5728\u8fd9\u513f\uff01\u201d \u3000\u3000\u674e\u6cbb\u5927\u559c\uff0c\u90a3\u5973\u5b69\u5b50\u62db\u62db\u624b\u9053\uff1a\u201d\u4f60\u6765\u5440\uff01\u201d\u674e\u6cbb\u4e00\u9635\u8fdf\u7591\uff0c\u5973\u5b69\u5b50\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u6253\u4f60 \u4e86\uff0c\u6211\u521a\u624d\u8ddf\u4f60\u95f9\u7740\u73a9\u5462\uff0c\u4f60\u8fd8\u751f\u6211\u7684\u6c14\u5417\uff1f\u201d\u674e\u6cbb\u8d70\u8fc7\u53bb\u62c9\u5979\u7684\u624b\uff0c\u90a3\u5973\u5b69\u5b50\u4e00\u7b11\u6323\u8131\u3002 \u674e\u6cbb\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u7136\u60f3\u8d77\u5979\u73b0\u5728\u5df2\u662f\u5341\u56db\u5c81\u7684\u5c0f\u59d1\u5a18\u4e86\uff0c\u53ef\u80fd\u61c2\u5f97\u5bb3\u81ca\u4e86\u5462\u3002\u4e5f\u4fbf\u7b11\u4e86\u4e00 \u7b11\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u9752\u8863\u5987\u4eba\u662f\u8c01\uff0c\u4f60\u600e\u8ba4\u5f97\u5979\u7684\uff1f\u201d\u90a3\u5973\u5b69\u5b50\u9053\uff1a\u201c\u4f60\u7ba1\u5f97\u7740\uff1f\u6211\u8ba4\u5f97\u7684\u4eba\u90fd \u8981\u5bf9\u4f60\u8bf4\u5417\uff1f\u201d\u674e\u6cbb\u53c8\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u60f3\uff1a\u600e\u4e48\u5979\u7684\u6027\u60c5\u5168\u90e8\u53d8\u4e86\uff0c\u5979\u4ee5\u524d\u53ef\u4e0d\u662f\u8fd9\u4e2a\u6837\u513f\u5440\uff01 \u3000\u3000\u8981\u77e5\u674e\u6cbb\u548c\u51af\u745b\u5728\u5929\u5c71\u4e4b\u65f6\u60c5\u540c\u5144\u59b9\u3002\u51af\u745b\u5bf9\u4ed6\u5341\u5206\u67d4\u987a\u3002\u674e\u6cbb\u771f\u60f3\u4e0d\u5230\u4e24\u5e74\u591a\u4e0d\u89c1\uff0c \u5979\u5374\u7528\u8fd9\u6837\u7684\u53e3\u543b\u56de\u7b54\u81ea\u5df1\u3002\u90a3\u5973\u5b69\u5b50\u53c8\u683c\u683c\u7b11\u9053\uff1a\u201c\u4f60\u5750\u4e0b\u6765\u5440\uff0c\u4f60\u5c3d\u5446\u5446\u7684\u770b\u7740\u6211\u5e72 \u561b\uff1f\u201d\u674e\u6cbb\u5750\u5728\u5979\u7684\u8eab\u65c1\uff0c\u95ee\u9053\uff1a\u201c\u6613\u4f2f\u6bcd\u597d\u5417\uff1f\u201d\u90a3\u5973\u5b69\u5b50\u9053\uff1a\u201c\u5f88\u597d\u5440\uff0c\u5979\u4e5f\u60e6\u8bb0\u4f60 \u5462\uff01\u201d\u674e\u6cbb\u9053\uff1a\u201c\u5979\u7684\u5934\u53d1\u600e\u4e48\u6837\u4e86\uff1f\u201d\u90a3\u5973\u5b69\u5b50\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u50cf\u4ece\u524d\u4e00\u6837\u6591\u767d\u3002\u4f60\u95ee\u5979\u7684 \u5934\u53d1\u5e72\u5417\uff1f\u201d\u674e\u6cbb\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u6613\u4f2f\u6bcd\u7684\u5934\u53d1\u767d\u4e86\uff1f\u201d\u8981\u77e5\u6613\u5170\u73e0\u7684\u5934\u53d1\uff0c\u56e0\u4e3a\u51e0\u5341 \u5e74\u524d\u66fe\u670d\u4e86\u5fe7\u6619\u4ed9\u82b1\uff0c\u53ef\u4fdd\u6c38\u4e16\u4e0d\u767d\u3002\u82e5\u7136\u4e00\u767d\uff0c\u5c31\u662f\u6b7b\u671f\u5230\u4e86\uff0c\u6240\u4ee5\u674e\u6cbb\u95ee\u5979\u5934\u53d1\uff0c\u5b9e\u9645 \u5c31\u7b49\u4e8e\u95ee\u5979\u5065\u5eb7\u5982\u4f55\uff0c\u5982\u4eca\u542c\u8fd9\u5973\u5b69\u5b50\u8bf4\u5979\u53d1\u5df2\u6591\u767d\uff0c\u5982\u4f55\u4e0d\u614c\u3002\u90a3\u5973\u5b69\u5b50\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u6211 \u9a97\u4f60\u5462\uff0c\u4f60\u672c\u6765\u5f88\u806a\u660e\u561b\uff0c\u600e\u4e48\u8fd9\u6b21\u7b28\u4e86\uff1f\u6211\u4e0d\u662f\u8bf4\u5979\u7684\u5934\u53d1\u50cf\u4ece\u524d\u4e00\u6837\u5417\uff1f\u5979\u7684\u5934\u53d1\u4ee5\u524d \u600e\u6837\uff0c\u96be\u9053\u4f60\u4e0d\u77e5\u9053\uff1f\u4f60\u4e0b\u5c71\u4e5f\u4e0d\u8fc7\u4e24\u5e74\u3002\u201d\u674e\u6cbb\u4e00\u542c\uff0c\u679c\u7136\u5979\u662f\u8bf4\u8fc7\u8fd9\u8bdd\u3002\u7b11\u9a82\u9053\uff1a\u201c\u4f60 \u600e\u4e48\u5b66\u5f97\u8fd9\u6837\u987d\u76ae\u4e86\uff1f\u62ff\u8fd9\u4e2a\u6765\u5413\u6211\uff01\u6613\u4f2f\u6bcd\u7684\u5934\u53d1\u662f\u6c38\u8fdc\u4e0d\u4f1a\u767d\u7684\uff0c\u4f60\u8bf4\u5979\u767d\u4e86\uff0c\u4e0d\u662f\u8bc5 \u5492\u5979\u8981\u6b7b\u5417\uff1f\u4e8f\u5979\u90a3\u4e48\u75bc\u4f60\uff0c\u4f60\u5f00\u73a9\u7b11\u4e5f\u4e0d\u5e94\u8fd9\u6837\u5f00\uff01\u201d\u90a3\u5973\u5b69\u5b50\u4f38\u4f38\u820c\u5934\u9053\uff1a\u4ee5\u540e\u6211\u4e0d\u6562 \u4e86\uff01\u201d \u3000\u3000\u770b\u5b98\u4eec\u90fd\u77e5\u9053\u8fd9\u5973\u5b69\u5b50\u5b9e\u9645\u4e0d\u662f\u51af\u745b\u800c\u662f\u51af\u7433\u4e86\u3002\u5979\u8eba\u5728\u5c71\u77f3\u4e4b\u540e\uff0c\u628a\u5415\u56db\u5a18\u548c\u674e\u6cbb\u7684 \u5bf9\u8bdd\u5168\u90fd\u542c\u53bb\uff0c\u5fc3\u91cc\u53c8\u60ca\u53c8\u559c\u3002\u5979\u5e74\u7eaa\u867d\u5c0f\uff0c\u53ef\u662f\u4e5f\u542c\u4eba\u8bf4\u8fc7\u6613\u5170\u73e0\u548c\u6b66\u743c\u7476\u7684\u540d\u5934\uff0c\u77e5\u9053 \u8fd9\u4e24\u4eba\u662f\u5f53\u4eca\u4e4b\u4e16\u6700\u5389\u5bb3\u7684\u5973\u5251\u5ba2\uff0c\u5c24\u5176\u662f\u6613\u5170\u73e0\u66f4\u662f\u5185\u5bb6\u6b63\u5b97\uff0c\u5251\u672f\u5927\u5bb6\uff0c\u4e86\u56e0\u548c\u5c1a\u5929\u53f6 \u6563\u4eba\u4ed6\u4eec\u5e38\u5e38\u9a82\u5979\uff0c\u8fd8\u8bf4\u8fc7\u8981\u9080\u96c6\u5341\u540d\u4e00\u6d41\u597d\u624b\u53bb\u6597\u5979\u3002\u51af\u7433\u867d\u5c0f\uff0c\u4eba\u5374\u806a\u660e\uff0c\u89c1\u8fd9\u4e9b\u201c\u4f2f \u53d4\u201d\u5982\u6b64\u6068\u5979\u5fcc\u5979\uff0c\u5c31\u77e5\u8fd9\u6613\u5170\u73e0\u7684\u672c\u4e8b\u4e00\u5b9a\u5927\u5f97\u4e0d\u5f97\u4e86\u3002\u5fc3\u4e2d\u597d\u751f\u4ef0\u6155\u3002 \u3000\u3000\u9002\u624d\u5979\u5728\u5c71\u77f3\u4e4b\u540e\uff0c\u542c\u5f97\u674e\u6cbb\u539f\u6765\u662f\u6b66\u743c\u7476\u7684\u513f\u5b50\uff0c\u53c8\u542c\u674e\u6cbb\u8bf4\u81ea\u5df1\u201c\u662f\u201d\u6613\u5170\u73e0\u7684\u552f \u4e00\u4f20\u4eba\uff0c\u800c\u4e14\u8fd8\u548c\u81ea\u5df1\u4e00\u540c\u73a9\u5927\uff0c\u5fc3\u4e2d\u6697\u6697\u5947\u602a\uff0c\u96be\u9053\u4e16\u754c\u4e0a\u771f\u6709\u4e00\u4e2a\u548c\u81ea\u5df1\u5341\u5206\u76f8\u4f3c\u7684 \u4eba\uff1f\u53ef\u4e0d\u77e5\u5979\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u5fc3\u4e2d\u5ffd\u7136\u8d77\u4e86\u4e00\u4e2a\u9b3c\u4e3b\u610f\uff1a\u5c31\u5192\u8ba4\u662f\u90a3\u4e0d\u77e5\u540d\u7684\u5973\u5b69\u5b50\uff0c\u9017\u9017\u674e \u6cbb\u3002 \u3000\u3000\u674e\u6cbb\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u773c\u524d\u8fd9\u5973\u5b69\u5b50\u4e0d\u662f\u51af\u745b\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u5e74\u89c1\u8fc7\u6211\u7684\u6bcd\u4eb2\u5417\uff1f\u201d\u51af \u7433\u6664\u6664\u5440\u5440\uff0c\u542b\u7cca\u8bf4\u9053\uff1a\u201c\u89c1\u8fc7\u4e00\u6b21\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u5979\u600e\u4e48\u6837\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u5979\u5728\u7ec3\u5251\u3002\u201d \u674e\u6cbb\u5947\u9053\uff1a\u201c\u600e\u4e48\u5979\u5728\u7ec3\u5251\uff1f\u5979\u4e0d\u5750\u5173\u4e86\u5417\uff1f\u201d\u539f\u6765\u674e\u6cbb\u4e0b\u5c71\u65f6\uff0c\u5979\u6bcd\u4eb2\u5df2\u5f00\u59cb\u201c\u5750\u5173\u201d\uff0c \u4ee5\u4e03\u65e5\u4e3a\u4e00\u671f\uff0c\u5373\u6bcf\u6b21\u9759\u5750\u4e03\u5929\uff0c\u9759\u5750\u4e4b\u65f6\uff0c\u53ea\u98df\u82b1\u679c\uff0c\u8fc7\u4e86\u4e03\u5929\uff0c\u7136\u540e\u518d\u98df\u70df\u706b\u3002\u7136\u540e\u4f11 \u606f\u4e09\u5929\uff0c\u53c8\u518d\u9759\u5750\u3002\u8fd9\u79cd\u957f\u671f\u201c\u5750\u5173\u201d\uff0c\u4e43\u662f\u4fee\u4e60\u6700\u4e0a\u4e58\u5185\u529f\u8005\u6700\u540e\u8981\u8fc7\u7684\u4e00\u5173\u3002\u201c\u5750\u5173\u201d \u671f\u4e2d\uff0c\u4e0d\u7406\u4fd7\u52a1\uff0c\u66f4\u65e0\u9700\u7ec3\u5251\u3002\u6240\u4ee5\u674e\u6cbb\u4e00\u542c\u51af\u7433\u8bf4\u5979\u6bcd\u4eb2\u7ec3\u5251\uff0c\u5927\u4e3a\u5947\u602a\u3002\u51af\u7433\u542c\u8a00\u5bdf \u8272\uff0c\u77e5\u9053\u4e00\u5b9a\u662f\u81ea\u5df1\u8bf4\u9519\u4e86\u8bdd\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u6211\u548c\u5e08\u5085\u4e00\u540c\u53bb\u7684\uff0c\u5e08\u5085\u8bf4\u4f60\u6bcd\u4eb2\u8d70\u706b\u5165\u9b54\uff01\u201d \u3000\u3000\u674e\u6cbb\u8fd9\u4e00\u60ca\u66f4\u751a\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u5979\u8d70\u706b\u5165\u9b54\uff1f\u54ce\u5440\uff0c\u90a3\u4e48\u5979\u8eab\u4f53\u600e\u6837\uff1f\u201d\u51af\u7433\u5728\u56db\u7687\u5b50 \u5e9c\u4e2d\u957f\u5927\uff0c\u904d\u4e60\u5404\u6d3e\u6b66\u529f\u3002\u7136\u800c\u56db\u7687\u5b50\u95e8\u4e0b\u5f02\u4eba\uff0c\u9664\u4e86\u56e0\u4e4b\u5916\uff0c\u8c01\u90fd\u4e0d\u61c2\u6b63\u5b97\u7684\u7384\u95e8\u5185\u529f\uff0c \u5176\u4ed6\u5404\u6d3e\u504f\u95e8\u4fee\u4e60\u5185\u529f\u7684\u5e38\u4f1a\u201c\u8d70\u706b\u5165\u9b54\u201d\uff0c\u6240\u4ee5\u51af\u7433\u5bf9\u8fd9\u4e2a\u540d\u8bcd\u5341\u5206\u719f\u6089\u3002\u56e0\u9053\uff1a\u201c\u597d\u5728 \u6211\u5e08\u5085\u53ca\u65f6\u8d76\u5230\uff0c\u674e\u4f2f\u6bcd\u5fc3\u7075\u6b63\u8d77\u5f02\u72b6\u4e4b\u9645\uff0c\u9762\u8089\u75c9\u631b\uff0c\u6211\u5e08\u5085\u4e00\u77a7\uff0c\u5c31\u77e5\u5979\u662f\u8d70\u706b\u5165\u9b54\uff0c \u8d76\u5fd9\u8fd0\u5185\u5bb6\u771f\u6c14\uff0c\u52a9\u5979\u547c\u5438\uff0c\u5979\u8fd9\u624d\u6062\u590d\u6b63\u5e38\u3002\u636e\u5e08\u5085\u8bf4\u8981\u4e0d\u662f\u5979\u53ca\u65f6\u8d76\u5230\uff0c\u4f2f\u6bcd\u5c31\u8981\u534a\u8eab \u4e0d\u9042\u5566\u3002\u6240\u4ee5\u4f2f\u6bcd\u540e\u6765\u4e0d\u5750\u5173\u4e86\uff0c\u8bf4\u8981\u628a\u5251\u7ec3\u5230\u51fa\u795e\u5165\u5316\u4e4b\u540e\uff0c\u7136\u540e\u518d\u5750\u3002\u201d\u8fd9\u756a\u8bdd\u8bf4\u5f97\u6d3b \u7075\u6d3b\u73b0\uff0c\u800c\u4e14\u5f88\u6709\u6839\u636e\uff0c\u4e0d\u7531\u674e\u6cbb\u4e0d\u4fe1\u3002\u5fc3\u60f3\u767d\u53d1\u9b54\u5973\u4f20\u4e0b\u7684\u6b66\u529f\uff0c\u672c\u6765\u4e0d\u662f\u7384\u95e8\u6b63\u5b97\uff0c\u6211 \u4ee5\u4e3a\u5979\u529f\u529b\u6df1\u6e5b\uff0c\u4fee\u7ec3\u6700\u4e0a\u4e58\u5185\u529f\uff0c\u4e5f\u53ef\u65e0\u788d\uff0c\u8c01\u77e5\u8fd8\u662f\u8d70\u706b\u5165\u9b54\u3002\u5fc3\u60f3\uff1a\u4ee5\u6bcd\u4eb2\u7684\u597d\u80dc\uff0c \u53d7\u6b64\u632b\u6298\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u4f24\u5fc3\u5462\uff01\u4e00\u5ff5\u53ca\u6b64\uff0c\u4e0d\u89c9\u95f7\u95f7\u4e0d\u4e50\u3002 \u3000\u3000\u51af\u7433\u53c8\u7b11\u9053\uff1a\u201c\u6211\u5e08\u5085\u8bf4\u8fd9\u4e0d\u7d27\u8981\uff0c\u4f60\u95f7\u4ec0\u4e48\uff1f\u5979\u8bf4\u4f60\u6bcd\u4eb2\u6709\u8fc7\u6b64\u756a\u7ecf\u5386\uff0c\u4ee5\u540e\u518d\u2018\u5750 \u5173\u2019\u65f6\u5c31\u77e5\u6240\u8d8b\u907f\u4e86\uff0c\u5979\u8fd8\u6307\u70b9\u4f60\u6bcd\u4eb2\u4fee\u4e60\u6700\u4e0a\u4e58\u5185\u529f\u7684\u8bc0\u7a8d\u5462\uff0c\u53ef\u60dc\u6211\u542c\u4e0d\u61c2\u3002\u201d\u674e\u6cbb\u5927 \u559c\uff0c\u9053\uff1a\u201c\u6664\uff0c\u90a3\u6211\u6bcd\u4eb2\u5012\u662f\u56e0\u7978\u5f97\u798f\u4e86\u3002\u201d\u51af\u7433\u8fd9\u4e00\u756a\u8bdd\uff0c\u65e0\u610f\u4e4b\u4e2d\u649e\u4e2a\u6b63\u7740\u3002\u539f\u6765\u8bba\u8d77 \u8f88\u4efd\uff0c\u6b66\u743c\u7476\u6bd4\u6613\u5170\u73e0\u5c1a\u9ad8\u534a\u8f88\uff0c\uff08\u6b66\u8c05\u7476\u662f\u767d\u53d1\u9b54\u5973\u7684\u5173\u95e8\u5f92\u5f1f\uff0c\u6613\u5170\u73e0\u867d\u7136\u662f\u6666\u660e\u7985\u5e08 \u629a\u517b\u957f\u5927\uff0c\u4f46\u6b66\u529f\u5927\u534a\u662f\u51cc\u672a\u98ce\u6240\u4f20\u3002\u6666\u660e\u7985\u5e08\u548c\u767d\u53d1\u9b54\u5973\u662f\u540c\u8f88\uff09\u6240\u4ee5\u6613\u5170\u73e0\u548c\u6b66\u8c05\u7476\u867d \u7136\u79c1\u4ea4\u6781\u597d\uff0c\u4f46\u8bf4\u5230\u6b66\u529f\uff0c\u603b\u662f\u8c26\u900a\uff0c\u66f4\u4e0d\u597d\u610f\u601d\u201c\u6307\u70b9\u201d\u6b66\u743c\u7476\u4e86\u3002\u674e\u6cbb\u5fc3\u60f3\uff1a\u60f3\u5fc5\u662f\u6613 \u5170\u73e0\u89c1\u81ea\u5df1\u6bcd\u4eb2\u7ecf\u8fc7\u8fd9\u573a\u707e\u96be\uff0c\u6240\u4ee5\u4e0d\u62d8\u4fd7\u5957\uff0c\u4e0d\u56fa\u6267\u4e8e\u8f88\u4efd\uff0c\u613f\u610f\u201c\u6307\u70b9\u201d\u4e86\u3002 \u3000\u3000\u51af\u7433\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u90a3\u72ec\u95e8\u5251\u6cd5\u80fd\u4e0d\u80fd\u6559\u6211\u5440\uff1f\u201d\u674e\u6cbb\u4e00\u6115\uff0c\u9053\uff1a\u201c\u4f60\u5b66\u7684\u5929\u5c71 \u5251\u6cd5\uff0c\u535a\u5927\u7cbe\u5fae\uff0c\u4e3a\u4f55\u8fd8\u8981\u5b66\u6211\u7684\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u5e08\u7236\u8bf4\uff0c\u6211\u4eec\u4e24\u5bb6\u5251\u6cd5\u4e00\u6b63\u4e00\u53cd\u672c\u6765\u540c \u51fa\u4e00\u6e90\uff0c\u6240\u4ee5\u6211\u60f3\uff0c\u5982\u679c\u540c\u65f6\u517c\u5b66\uff0c\u5c82\u4e0d\u66f4\u597d\uff1f\u6211\u672c\u8981\u4f2f\u6bcd\u6559\u7684\uff0c\u4f46\u53ef\u60dc\u6211\u5306\u5306\u4e0b\u5c71\uff0c\u6ca1\u6709 \u673a\u4f1a\u518d\u5b66\u4e86\u3002\u201d\u674e\u6cbb\u5ffd\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u6211\u8fd9\u4e00\u95e8\u5251\u6cd5\uff0c\u4f60\u5e08\u5085\u4e5f\u61c2\u7684\u3002\u4ee5\u524d\u6211\u6bcd\u4eb2\u7684\u5e08\u59d0\u98de\u7ea2 \u5dfe\u66fe\u6559\u8fc7\u5979\u3002\u201d\u51af\u7433\u6697\u5403\u4e00\u60ca\uff0c\u60f3\u4e0d\u5230\u8bf4\u8bdd\u4e4b\u95f4\u53c8\u9732\u7834\u7efd\u3002\u5e78\u597d\u674e\u6cbb\u4e00\u7b11\u4e4b\u540e\u53c8\u9053\uff1a\u201c\u4f60\u5e08 \u5085\u4e5f\u4e0d\u6559\u4f60\uff0c\u60f3\u5fc5\u662f\u89c1\u4f60\u5e74\u7eaa\u592a\u5c0f\uff0c\u6050\u4f60\u5b66\u5f97\u592a\u6742\uff0c\u6240\u4ee5\u53eb\u4f60\u4e13\u7ec3\u5929\u5c71\u5251\u6cd5\uff01\u201d\u674e\u6cbb\u8bf4\u5230\u8fd9 \u91cc\uff0c\u5ffd\u7136\u5fc3\u5ff5\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4e0b\u5c71\u591a\u4e45\u4e86\uff1f\u201d\u51af\u7433\u60f3\u4e86\u4e00\u4e0b\uff0c\u7b54\u9053\uff1a\u201c\u6664\uff0c\u534a\u5e74\u591a\u4e86\u3002\u201d \u674e\u6cbb\u9053\uff1a\u201c\u534a\u5e74\u4e4b\u95f4\uff0c\u4f60\u4e3a\u4ec0\u4e48\u5b66\u4e86\u90a3\u4e48\u591a\u522b\u6d3e\u7684\u6b66\u529f\uff1f\u201d\u51af\u7433\u561f\u7740\u5c0f\u5634\u513f\u9053\uff1a\u201c\u6211\u6b22\u559c \u561b\uff0c\u4f60\u4e3a\u4ec0\u4e48\u603b\u7231\u7ba1\u6211\uff1f\u6211\u73b0\u5728\u5e74\u7eaa\u6e10\u6e10\u5927\u4e86\uff0c\u591a\u5b66\u4e00\u70b9\u4e5f\u4e0d\u7d27\u8981\u3002\u6211\u77e5\u9053\u5566\uff0c\u4f60\u4e0d\u613f\u610f\u6559 \u6211\uff0c\u6240\u4ee5\u6545\u610f\u8fd9\u6837\u9a82\u6211\u3002\u201d\u674e\u6cbb\u751a\u7231\u8fd9\u4e2a\u5c0f\u59b9\u59b9\uff0c\u95fb\u8a00\u76b1\u8d77\u7709\u5934\u9053\uff1a\u201c\u4f60\u8bf4\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u597d \u50cf\u4f60\u548c\u6211\u662f\u5916\u4eba\u4f3c\u7684\uff1f\u4f60\u771f\u7684\u8981\u5b66\uff0c\u6211\u5f53\u7136\u53ef\u4ee5\u6559\u4f60\u3002\u201d \u3000\u3000\u51af\u7433\u5927\u559c\uff0c\u53c8\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u505a\u2018\u540e\u5929\u4e4b\u6c14\u2019\u4ec0\u4e48\u53eb\u505a\u2018\u5148\u5929\u4e4b\u6c14\u2019\uff1f\u2018\u5185\u4e39\u2019\u4fee\u7ec3\u53c8\u662f \u5982\u4f55\uff1f\u201d\u674e\u6cbb\u53c8\u662f\u4e00\u9635\u6115\u7136\uff0c\u5fc3\u60f3\u600e\u4e48\u6613\u4f2f\u6bcd\u8fde\u8fd9\u4e9b\u6700\u57fa\u672c\u7684\u5185\u529f\u4fee\u4e60\u5e38\u8bc6\u90fd\u6ca1\u6559\u5979\u3002\u539f\u6765 \u6240\u8c13\u201c\u540e\u5929\u4e4b\u6c14\u201d\u201c\u5148\u5929\u4e4b\u6c14\u201d\u90fd\u662f\u9053\u5bb6\u7684\u8bf4\u6cd5\uff0c\u5176\u540e\u4ea6\u4e3a\u4fee\u4e60\u5185\u529f\u65f6\u6240\u4e60\u7528\u3002\u6240\u8c13\u53eb\u201c\u540e \u5929\u4e4b\u6c14\u201d\u5c31\u662f\u6307\u80f8\u80ba\u4e2d\u7684\u6c14\uff0c\u56e0\u4e3a\u90a3\u662f\u7531\u5916\u95f4\u5438\u8fdb\u6765\u7684\uff0c\u6240\u4ee5\u53eb\u201c\u540e\u5929\u4e4b\u6c14\u201d\uff0c\u4e39\u7530\u6c14\u6d77\u4e2d \u7684\u6c14\uff0c\u53c8\u53eb\u5c0f\u5148\u5929\u4e4b\u6c14\uff0c\u4e43\u662f\u4eba\u7c7b\u81ea\u6bcd\u4f53\u4ea7\u51fa\u540e\u5c31\u8d4b\u6709\u7684\u3002\u666e\u901a\u4eba\u547c\u5438\u65f6\uff0c\u80f8\u80ba\u4e2d\u7684\u6c14\u4e0e\u4e39 \u7530\u4e4b\u6c14\u4e0d\u80fd\u6c9f\u901a\u6df7\u5408\uff1b\u4f46\u82e5\u5bf9\u5410\u7eb3\u529f\u592b\u6709\u4e86\u4fee\u517b\u7684\u4eba\uff0c\u5219\u53ef\u4ee4\u4e8c\u6c14\u6df7\u800c\u4e3a\u4e00\uff0c\u79f0\u4e3a\u201c\u6c14 \u901a\u201d\uff0c\u5230\u4e86\u201c\u6c14\u901a\u201d\u7684\u5883\u754c\uff0c\u201c\u5148\u5929\u4e4b\u6c14\u201d\u4e0e\u201c\u540e\u5929\u4e4b\u6c14\u201d\u4e0a\u4e0b\u4ea4\u7ed3\uff0c\u65e0\u5f62\u4e2d\u5c31\u4f3c\u5728\u4f53\u5185\u7ed3 \u6210\u4e00\u7c92\u201c\u4e39\u4e38\u201d\u4e4b\u97e7\uff0c\u53ef\u4e0a\u4e0b\u8f6c\u52a8\uff0c\u8fd9\u4fbf\u662f\u9053\u5bb6\u6240\u8c13\u7684\u201c\u5185\u4e39\u201d\uff0c\u5176\u5b9e\u4e43\u662f\u4f53\u5185\u6240\u517b\u6210\u7684\u4e00 \u80a1\u6c14\u52b2\uff0c\u5e76\u4e0d\u5e26\u4ec0\u4e48\u8ff7\u4fe1\u7684\u8272\u5f69\u7684\u3002 \u3000\u3000\u51af\u7433\u5bf9\u4e8e\u5185\u529f\u7a8d\u8981\uff0c\u832b\u7136\u65e0\u77e5\uff0c\u6240\u4ee5\u6709\u6b64\u4e00\u95ee\u3002\u53ca\u89c1\u674e\u6cbb\u6123\u7136\uff0c\u773c\u775b\u4e00\u6e9c\uff0c\u5df2\u77e5\u6240\u4ee5\uff0c \u7b11\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u5947\u602a\u5e08\u5085\u4e3a\u4f55\u4e0d\u6559\u6211\u4e86\uff1f\u5979\u8bf4\u6211\u5e74\u7eaa\u5c0f\uff0c\u4e0d\u8010\u9759\u5750\uff0c\u6240\u4ee5\u53ea\u6559\u6211\u7ec3\u5251\uff0c\u5e76\u672a \u6559\u6211\u5185\u529f\u3002\u201d\u51af\u745b\u4e03\u516b\u5c81\u4e4b\u65f6\uff0c\u7531\u6b66\u743c\u7476\u7167\u987e\uff0c\u516b\u5c81\u540e\u56de\u5929\u5c71\u5317\u9ad8\u5cf0\uff0c\u5230\u5341\u4e8c\u5c81\u6b62\uff0c\u8fd9\u56db\u5e74 \u95f4\uff0c\u674e\u6cbb\u6bcf\u5e74\u89c1\u5979\u4e00\u6b21\uff0c\u6bcf\u6b21\u76f8\u805a\u7ea6\u534a\u4e2a\u6708\uff0c\u674e\u6cbb\u5f53\u5979\u5b69\u5b50\u770b\u5f85\uff0c\u6240\u4ee5\u5e76\u6ca1\u95ee\u8d77\u5979\u6709\u5426\u4fee\u7ec3 \u5185\u529f\u3002\u8fd9\u65f6\u5fc3\u60f3\uff1a\u6613\u4f2f\u6bcd\u53ea\u6388\u6b66\u827a\u4e0d\u4f20\u5185\u529f\uff0c\u8fd9\u6837\u6559\u6cd5\uff0c\u5c82\u975e\u751a\u6709\u7f3a\u9677\uff1f\u56e0\u9053\uff1a\u201c\u6211\u8bf4\u7ed9\u4f60 \u542c\uff0c\u4e5f\u4e0d\u7d27\u8981\uff0c\u53ea\u662f\u82e5\u7ed9\u4f2f\u6bcd\u77e5\u9053\uff0c\u90a3\u53ef\u771f\u662f\u8d3b\u7b11\u65b9\u5bb6\u4e86\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u4e0d\u544a\u8bc9\u5979\u4fbf\u662f\u3002 \u5979\u672c\u6765\u53eb\u6211\u5728\u6c5f\u6e56\u5386\u7ec3\u4e09\u5e74\u4e4b\u540e\uff0c\u518d\u56de\u5c71\u65f6\u624d\u5c06\u5185\u529f\u4fee\u4e60\u4e4b\u9053\u6559\u6211\u7684\u3002\u6211\u53ea\u4f2f\u5979\u5e74\u7eaa\u8001\u8fc8\uff0c \u82e5\u6709\u610f\u5916\uff0c\u5c82\u975e\u4e00\u751f\u96be\u5b66\uff01\u201d\u674e\u6cbb\u95fb\u8a00\uff0c\u7709\u5934\u53c8\u662f\u4e00\u76b1\uff0c\u8fde\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\u3002\u201d\u5fc3\u60f3\uff1a\u8fd9\u5b69 \u5b50\u7d20\u6765\u6e29\u67d4\u6566\u539a\uff0c\u600e\u4e48\u51fa\u6765\u534a\u5e74\uff0c\u5fc3\u672f\u5c31\u53d8\u5f97\u5982\u6b64\u574f\u4e86\u3002\u201d\u53ea\u987e\u81ea\u5df1\u3002\u82e5\u771f\u4e2a\u6069\u5e08\u6b7b\u4e86\uff0c\u60b2 \u75db\u8fd8\u6765\u4e0d\u53ca\uff0c\u90a3\u4f1a\u60f3\u5230\u5176\u5b83\u3002\u8fd9\u5973\u5b69\u5b50\u5982\u6b64\u8bb2\u6cd5\uff0c\u82e5\u7ed9\u6613\u4f2f\u6bcd\u542c\u5230\uff0c\u771f\u4f1a\u6c14\u6b7b\u3002 \u3000\u3000\u51af\u7433\u89c1\u4ed6\u53c8\u6273\u8d77\u4e86\u9762\uff0c\u201c\u54ce\u54df\u201d\u53eb\u9053\uff1a\u201c\u6211\u77e5\u9053\u6211\u53c8\u8bf4\u9519\u8bdd\u4e86\uff0c\u597d\u54e5\u54e5\u4f60\u522b\u5bf9\u6211\u751f\u6c14\uff0c \u6211\u4ee5\u540e\u4e0d\u4e71\u8bf4\u4e86\u3002\u201d\u674e\u6cbb\u5fc3\u60f3\uff1a\u8fd9\u5973\u5b69\u5b50\u4e0b\u5c71\u540e\u4e0d\u77e5\u4ea4\u4e86\u4e9b\u4ec0\u4e48\u670b\u53cb\uff0c\u7ed9\u5f15\u574f\u4e86\u3002\u4eca\u540e\u975e\u5f97 \u5bf9\u5979\u591a\u7167\u987e\u4e0d\u6210\uff0c\u518d\u4e0d\u80fd\u8ba9\u5979\u548c\u9752\u8863\u5987\u4eba\u4e4b\u7c7b\u86ee\u4e0d\u8bb2\u7406\u7684\u201c\u75af\u5a46\u5b50\u201d\u9b3c\u6df7\u4e86\u3002\u56e0\u9053\uff1a\u201c\u597d \u5427\uff0c\u6211\u4e0d\u751f\u6c14\u3002\u4f60\u8981\u5b66\u5185\u529f\uff0c\u6211\u628a\u57fa\u672c\u8981\u8bc0\u4f20\u4f60\u3002\u201d\u8bf4\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u51af\u7433\u5fc3\u9886\u795e\u4f1a\uff0c\u5927\u559c\u9053 \u8c22\u3002\u674e\u6cbb\u7709\u5934\u53c8\u76b1\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u7b80\u76f4\u548c\u4ee5\u524d\u50cf\u4e24\u4e2a\u4eba\u4e86\uff1f\u201d\u51af\u7433\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u6211\u4ee5\u524d \u662f\u600e\u4e48\u6837\u7684\uff1f\u4f60\u8bf4\u7ed9\u6211\u542c\u3002\u201d\u674e\u6cbb\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u6709\u5341\u4e09\u56db\u5c81\u5566\uff01\u8fde\u81ea\u5df1\u672c\u6765\u662f \u600e\u6837\u7684\u90fd\u5fd8\u8bb0\u4e86\u4e48\uff1f\u201d\u51af\u7433\u4e00\u7b11\u8df3\u8d77\uff0c\u4f3c\u4e4e\u662f\u56e0\u5f97\u4ed6\u7684\u6307\u70b9\uff0c\u6781\u4e3a\u9ad8\u5174\u3002\u674e\u6cbb\u8bf4\u5b8c\u8fd9\u8bdd\uff0c\u5fc3 \u91cc\u5ffd\u7136\u611f\u5230\u98a4\u6817\uff0c\u5fc3\u60f3\uff1a\u96be\u9053\u771f\u6709\u8fd9\u6837\u5feb\u5fd8\u4e86\u672c\u6027\u7684\u4eba\uff1f\u4e0d\u89c9\u5446\u770b\u7740\u5979\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u8c08\u4e86\u534a\u591c\uff01\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\u4e1c\u65b9\u5df2\u767d\uff0c\u4e94\u5f69\u671d\u971e\u51fa\u73b0\u5929\u8fb9\u3002\u201c\u845b\u5cad\u671d\u58a9\u201d\u539f\u5c31\u662f\u201c\u94b1\u5858\u516b \u666f\u201d\u4e4b\u4e00\u3002\u4ece\u845b\u5cad\u5411\u4e1c\u8fdc\u773a\uff0c\u8d8a\u8fc7\u5e02\u533a\uff0c\u5728\u8fdc\u5904\u662f\u4e00\u7247\u6d69\u6e3a\u7684\u94b1\u5858\u6c5f\uff0c\u4e00\u76f4\u4f38\u5c55\u5230\u9065\u8fdc\u7684\u4e1c \u6d77\u3002\u8fd9\u65f6\u592a\u9633\u5df2\u6162\u6162\u5730\u4ece\u6d77\u9762\u4e0a\u5347\u8d77\u6765\uff0c\u5c31\u50cf\u4e00\u9762\u7d2b\u7ea2\u8272\u7684\u5927\u94dc\u76d8\u4f3c\u7684\u3002\u674e\u6cbb\u8ff7\u60af\u7684\u5fc3\u60c5\uff0c \u7ed9\u6e05\u6668\u7684\u51b7\u98ce\u5439\u5f97\u6682\u65f6\u6e05\u6563\uff0c\u7ad9\u5728\u201c\u521d\u9633\u53f0\u201d\u4e0a\uff0c\u770b\u90a3\u4e00\u9762\u7d2b\u7ea2\u8272\u7684\u5927\u94dc\u76d8\u8d8a\u5347\u8d8a\u9ad8\uff0c\u9010\u6e10 \u7531\u7d2b\u7ea2\u53d8\u6210\u6a59\u9ec4\uff0c\u7ee7\u800c\u53c8\u7531\u6a59\u9ec4\u53d8\u4e86\u8000\u773c\u7684\u767d\u5149\u3002\u4fef\u77b0\u897f\u6e56\uff0c\u6e56\u9762\u95ea\u8000\u7740\u4e07\u9053\u91d1\u5149\uff0c\u56db\u5468\u7684 \u9752\u5c71\u7eff\u6811\u90fd\u67d3\u4e0a\u4e86\u7f8e\u4e3d\u7684\u671d\u971e\u5f69\u8272\uff01 \u3000\u3000\u674e\u6cbb\u5438\u4e86\u4e00\u53e3\u6e05\u6668\u7684\u7a7a\u6c14\uff0c\u8bb0\u8d77\u4e86\u5415\u56db\u5a18\u7684\u8bf4\u8bdd\uff0c\u5bf9\u51af\u7433\u9053\uff1a\u201c\u6211\u548c\u4f60\u770b\u5415\u56db\u5a18\u53bb\uff01\u201d \u51af\u7433\u4f6f\u4f5c\u4e0d\u77e5\uff0c\u95ee\u9053\uff1a\u201c\u54ea\u4e2a\u5415\u56db\u5a18\u5440\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u5c31\u662f\u6628\u665a\u548c\u6211\u5728\u8fd9\u91cc\u6bd4\u5251\u7684\u90a3\u4e2a\u5973 \u4eba\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u6015\u5979\u7684\u90a3\u4e2a\u540c\u4f34\uff0c\u90a3\u4e2a\u9ec4\u8138\u6c49\u5b50\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\u662f\u6c5f\u5357\u5927\u4fa0\u7518\u51e4 \u6c60\u5462\uff0c\u4e3a\u4eba\u6700\u662f\u884c\u4fa0\u4ed7\u4e49\uff0c\u6709\u4ec0\u4e48\u53ef\u6015\uff1f\u4f60\u4e5f\u5e94\u8be5\u4ea4\u4ea4\u8fd9\u4e9b\u6b63\u6d3e\u4e4b\u4eba\u3002\u201d\u51af\u7433\u65e0\u5948\uff0c\u53ea\u597d\u968f \u4ed6\u540c\u53bb\u3002 \u3000\u3000\u7518\u767d\u4e8c\u4eba\u81ea\u6628\u665a\u5415\u56db\u5a18\u53bb\u540e\uff0c\u4e45\u4e45\u4e0d\u89c1\u56de\u6765\uff0c\u5fc3\u4e2d\u60ac\u60ac\uff0c\u90a3\u7761\u5f97\u7740\u3002\u542c\u5f97\u6572\u4e86\u56db\u66f4\uff0c\u7518 \u51e4\u6c60\u72ec\u81ea\u8d77\u6765\uff0c\u5728\u65c5\u820d\u7684\u5ead\u9662\u4e2d\u6563\u6b65\uff0c\u770b\u90a3\u4e00\u94a9\u659c\u6708\uff0c\u6162\u6162\u7684\u4ece\u5934\u9876\u79fb\u8fc7\u3002\u5728\u8fd9\u4e07\u7c41\u4ff1\u5bc2\u4e4b \u9645\uff0c\u5ffd\u542c\u5f97\u6709\u4e00\u9635\u5973\u4eba\u7684\u5c16\u53eb\u58f0\uff0c\u597d\u50cf\u5c31\u5728\u8fd9\u65c5\u820d\u4e4b\u5185\u3002\u7518\u51e4\u6c60\u5929\u751f\u7684\u4fa0\u4e49\u5fc3\u80a0\uff0c\u867d\u7136\u5fc3\u4e2d \u6709\u4e8b\uff0c\u4e5f\u7981\u4e0d\u4f4f\u5faa\u58f0\u5bfb\u8bbf\uff0c\u8fd9\u65c5\u5e97\u5360\u5730\u9887\u5e7f\uff0c\u603b\u6709\u4e8c\u4e09\u5341\u95f4\u5ba2\u623f\u3002\u7518\u51e4\u6c60\u8df3\u4e0a\u5c4b\u9762\uff0c\u542c\u5f97\u53eb \u58f0\u53d1\u81ea\u4e1c\u9996\u4e00\u95f4\u623f\u5185\uff0c\u6025\u5fd9\u4ece\u5c4b\u9762\u98de\u8fc7\uff0c\u5bfb\u5230\u90a3\u95f4\u623f\u95f4\uff0c\u4f7f\u4e2a\u201c\u73cd\u73e0\u5012\u60ac\u5e18\u201d\uff0c\u53cc\u8db3\u94a9\u7740\u6a90 \u693d\uff0c\u63a2\u5934\u5185\u671b\uff0c\u8fd9\u4e00\u671b\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u53ea\u89c1\u5c4b\u5185\u4e00\u4e2a\u8001\u5934\uff0c\u80cc\u5411\u7a97\u53e3\u3002\u9762\u5411\u4e00\u4e2a\u4e2d\u5e74\u5973\u4eba\uff0c\u51b7\u4e00\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u518d\u53eb\uff01\u4f60\u518d\u53eb\u6211 \u5c31\u4ee4\u4f60\u6c42\u751f\u4e0d\u5f97\u6c42\u6b7b\u4e0d\u80fd\uff01\u201d\u90a3\u5973\u4eba\u9762\u8272\u9752\u767d\uff0c\u597d\u50cf\u662f\u5f88\u662f\u5bb3\u6015\uff1a\u4f46\u4ecd\u662f\u6068\u6068\u8bf4\u9053\uff1a\u201c\u4f60\u771f \u662f\u4eba\u9762\u517d\u5fc3\uff0c\u5be1\u6e05\u8584\u4e49\uff0c\u628a\u6211\u9a97\u5230\u676d\u5dde\uff0c\u539f\u6765\u662f\u8981\u4e0b\u6bd2\u624b\uff01\u201d\u90a3\u8001\u5934\u72de\u7b11\u9053\uff1a\u201c\u6211\u5bb6\u7684\u90a3\u4e2a \u5a46\u5b50\u5bb9\u4f60\u4e0d\u5f97\uff0c\u6ca1\u5948\u4f55\u53ea\u597d\u8bf7\u4f60\u501f\u4e00\u53ea\u8033\u6735\uff0c\u5341\u6839\u6307\u5934\u4f5c\u4e3a\u6211\u8d4e\u7f6a\u4e4b\u7269\u3002\u5ff5\u5728\u4ee5\u5f80\u6069\u4e49\uff0c\u4f60 \u81ea\u5df1\u4e0b\u624b\u5427\uff0c\u6211\u6709\u7075\u4e39\u7ed9\u4f60\u6b62\u75db\uff01\u201d\u90a3\u5973\u4eba\u6296\u4e2a\u4e0d\u505c\uff0c\u8001\u5934\u55d6\u7684\u4e00\u58f0\uff0c\u62d4\u51fa\u4e86\u4e00\u53e3\u89e3\u8155\u8170\u5200\u3002 \u3000\u3000\u7518\u51e4\u6c60\u542c\u5f97\u8fd9\u8001\u4eba\u58f0\u97f3\u597d\u719f\uff0c\u89c1\u4ed6\u62d4\u51fa\u4e86\u5200\uff0c\u84e6\u7136\u53eb\u9053\uff1a\u201c\u97e9\u91cd\u5c71\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u4e00\u53e3\u98de \u5200\u7a7f\u7a97\u76f4\u5165\uff01 \u3000\u3000\u97e9\u91cd\u5c71\u6b66\u529f\u6781\u9ad8\uff0c\u53ea\u56e0\u5168\u5fc3\u5bf9\u4ed8\u90a3\u4e2a\u5973\u4eba\uff0c\u6ca1\u6709\u542c\u51fa\u58f0\u606f\u3002\u8fd9\u65f6\u53cd\u624b\u4e00\u62ff\uff0c\u5df2\u628a\u98de\u5200\u62ff \u7740\u3002\u7518\u51e4\u6c60\u864e\u543c\u4e00\u58f0\uff0c\u8df3\u4e86\u8fdb\u6765\uff01\u97e9\u91cd\u5c71\u987a\u624b\u5c06\u5315\u9996\u4e00\u63d2\uff0c\u7518\u51e4\u6c60\u4e00\u4e2a\u7ffb\u8eab\uff0c\u4e00\u62db\u201c\u8986\u96e8\u7ffb \u4e91\u201d\uff0c\u7528\u64d2\u62ff\u624b\u4e00\u62c2\uff0c\u5411\u97e9\u91cd\u5c71\u6301\u5200\u7684\u624b\u8155\u76f4\u622a\u8fc7\u6765\uff0c\u7518\u51e4\u6c60\u7684\u64d2\u62ff\u624b\u5728\u540c\u95e8\u4e2d\u6e5b\u79f0\u4e00\u7edd\uff0c \u97e9\u91cd\u5c71\u624b\u8155\u4e00\u7ffb\uff0c\u5315\u9996\u843d\u5730\u3002\u5c4b\u4e2d\u7684\u5973\u4eba\uff0c\u6025\u5fd9\u7a7f\u7a97\u8df3\u51fa\u3002\u97e9\u91cd\u5c71\u5927\u6012\uff0c\u53cc\u638c\u4e00\u63a8\uff0c\u7518\u51e4\u6c60 \u51fa\u638c\u76f8\u6297\uff0c\u53ea\u89c9\u4e00\u80a1\u5927\u529b\uff0c\u7518\u51e4\u6c60\u8eab\u4e0d\u7531\u5df1\uff0c\u76f4\u5411\u95e8\u5916\u649e\u53bb\uff0c\u7830\u7136\u4e00\u58f0\uff0c\u6728\u95e8\u5df2\u7ed9\u649e\u5f00\uff0c\u90a3 \u97e9\u91cd\u5c71\u4e5f\u7ed9\u7518\u51e4\u6c60\u795e\u529b\u63a8\u5012\uff0c\u8dcc\u843d\u5e8a\u4e0a\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u516b\u4e24\u534a\u65a4\uff0c\u529f\u529b\u6089\u654c\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u5341\u4e5d\u56de\u3000\u68a6\u788e\u9b42\u6d88\u3000\u7981\u5bab\u6101\u6c38\u522b\u3000\u5929\u5357\u5730\u5317\u3000\u519b\u65c5\u559c\u76f8\u9022 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u5341\u4e5d\u56de \u68a6\u788e\u9b42\u6d88\u3000\u7981\u5bab\u6101\u6c38\u522b \u5929\u5357\u5730\u5317\u3000\u519b\u65c5\u559c\u76f8\u9022 \u3000\u3000\u97e9\u91cd\u5c71\u5927\u6012\uff0c\u628a\u624b\u4e00\u626c\uff0c\u53d1\u51fa\u72ec\u95e8\u6697\u5668\u201c\u56de\u73af\u94a7\u201d\uff0c\u545c\u545c\u4f5c\u54cd\uff0c\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c \u897f\uff1f\u201d\u4e24\u53e3\u98de\u5200\u5288\u7a7a\u6253\u53bb\uff0c\u56de\u73af\u94a9\u5f62\u5982\u66f2\u5c3a\uff0c\u7518\u51e4\u6c60\u4e24\u53e3\u98de\u5200\u4e00\u5148\u4e00\u540e\uff0c\u9f50\u9f50\u65ab\u4e2d\uff0c\u4e0d\u6599\u90a3 \u6697\u5668\u545c\u7684\u4e00\u58f0\uff0c\u7ed9\u98de\u5200\u4e00\u649e\uff0c\u7a81\u7136\u56de\u73af\u8f6c\u6298\uff0c\u659c\u523a\u98de\u6765\uff0c\u7518\u51e4\u6c60\u5403\u4e86\u4e00\u60ca\uff0c\u770b\u5b83\u7684\u6765\u52bf\u5411 \u4e1c\uff0c\u6025\u5411\u897f\u907f\uff0c\u90a3\u6599\u97e9\u91cd\u5c71\u7684\u56de\u73af\u94a7\u5341\u5206\u602a\u5f02\uff0c\u7a81\u7136\u5728\u534a\u7a7a\u4e00\u8f6c\uff0c\u95ea\u7535\u822c\u7684\u5411\u7518\u51e4\u6c60\u5934\u9876\u98de \u6765\uff0c\u660e\u6643\u6643\u7684\u5229\u94a9\u76f4\u5411\u7518\u51e4\u6c60\u7684\u9888\u9879\u94a9\u4e0b\uff0c\u7518\u51e4\u6c60\u8eb2\u95ea\u4e0d\u53ca\uff0c\u4f38\u624b\u4e00\u62db\uff0c\u5229\u94a9\u94a9\u5728\u638c\u4e0a\u3002\u7518 \u51e4\u5730\u8fd0\u4e0a\u4e58\u5185\u529f\uff0c\u638c\u5fc3\u5185\u9677\uff0c\u808c\u8089\u4e00\u6536\uff0c\u5229\u94a9\u94a9\u5728\u638c\u4e0a\uff0c\u5c31\u5982\u63d2\u5230\u68c9\u82b1\u5806\u4e2d\u4e00\u6837\uff0c\u7adf\u7ed9\u6d88\u4e86 \u6765\u52bf\u3002\u7518\u51e4\u6c60\u4e94\u6307\u4e00\u64ae\uff0c\u628a\u56de\u73af\u94a9\u62ff\u5728\u624b\u4e2d\uff0c\u62d7\u4e3a\u4e24\u6bb5\u3002 \u3000\u3000\u8fd9\u56de\u73af\u94a9\u4e43\u662f\u97e9\u91cd\u5c71\u6210\u540d\u6697\u5668\uff0c\u7adf\u7136\u7ed9\u7518\u51e4\u6c60\u6536\u53bb\uff0c\u97e9\u91cd\u5c71\u4e0d\u7981\u53c8\u60ca\u53c8\u6012\uff0c\u4ece\u623f\u5185\u8df3\u51fa \u6765\uff0c\u53cc\u638c\u9f50\u53d1\uff0c\u7518\u51e4\u6c60\u4e00\u95ea\u4e00\u52fe\uff0c\u518d\u8fd0\u64d2\u62ff\u624b\u4e2d\u7684\u201c\u4e95\u5200\u80dc\u526a\u201d\u4e00\u62db\uff0c\u53cc\u6307\u9661\u7b8d\u97e9\u91cd\u5c71\u864e \u53e3\uff0c\u97e9\u91cd\u5c71\u53cd\u624b\u4e00\u63a8\uff0c\u6c89\u8098\u4e00\u649e\uff0c\u4e24\u4eba\u7531\u5408\u800c\u5206\uff0c\u81ea\u65c5\u820d\u7684\u8d70\u5eca\u8dc3\u4e0b\u5ead\u9662\u3002 \u3000\u3000\u5230\u4e86\u843d\u5730\u4e4b\u65f6\uff0c\u97e9\u91cd\u5c71\u5df2\u53d6\u51fa\u8f9f\u4e91\u9504\u6765\uff0c\u8fd9\u9504\u539f\u662f\u97e9\u91cd\u5c71\u91c7\u836f\u6240\u7528\uff0c\u867d\u7136\u957f\u4ec5\u4e09\u5c3a\uff0c\u5374 \u662f\u7cbe\u91d1\u6240\u70bc\uff0c\u4e00\u9504\u5288\u53bb\uff0c\u52b2\u98ce\u5e26\u7740\u5149\u8292\uff0c\u5728\u660f\u591c\u4e4b\u4e2d\uff0c\u5a01\u52bf\u7279\u522b\u663e\u5f97\u731b\u70c8\uff01 \u3000\u3000\u7518\u51e4\u6c60\u5140\u7136\u4e0d\u4ff1\uff0c\u5c55\u5f00\u6c89\u96c4\u638c\u6cd5\uff0c\u4e5f\u662f\u6253\u5f97\u864e\u864e\u751f\u98ce\u3002\u6b63\u9163\u6218\u95f4\uff0c\u97e9\u91cd\u5c71\u4e00\u58f0\u602a\u5578\uff0c\u897f \u9996\u5ba2\u623f\u53c8\u8df3\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u7518\u51e4\u6c60\u6b63\u4f7f\u5230\u201c\u8de8\u6b65\u8fdb\u638c\u201d\u4e00\u62db\uff0c\u5de6\u624b\u5411\u8f9f\u4e91\u9504\u4e00\u6258\uff0c\u53f3\u638c\u5954\u654c\u524d \u5fc3\uff0c\u90a3\u4eba\u7a81\u63d2\u8fdb\u6765\uff0c\u8fd0\u638c\u4e00\u5e26\uff0c\u628a\u7518\u51e4\u6c60\u7684\u638c\u529b\u5316\u4e86\u3002\u97e9\u91cd\u5c71\u9504\u950b\u4e0b\u6233\uff0c\u5374\u500f\u7684\u6536\u56de\u3002\u7518\u51e4 \u6c60\u4e00\u770b\uff0c\u8fd9\u4eba\u4e43\u662f\u5f62\u610f\u6d3e\u7684\u6210\u540d\u4eba\u7269\u3001\u8001\u597d\u5de8\u6ed1\u7684\u8463\u5de8\u5ddd\u3002\u7518\u51e4\u6c60\u53eb\u58f0\u82e6\u4e5f\uff0c\u8fd9\u97e9\u91cd\u5c71\u5df2\u662f \u96be\u6597\uff0c\u66f4\u90a3\u582a\u53c8\u52a0\u4e0a\u4e00\u4e2a\u9ad8\u624b\u3002 \u3000\u3000\u97e9\u5f97\u5c71\u53eb\u9053\uff1a\u201c\u8463\u514b\uff0c\u4f60\u622a\u4ed6\u540e\u8def\uff0c\u4e0d\u8981\u8ba9\u4ed6\u9003\u8d70\u3002\u201d\u97e9\u91cd\u5c71\u8f88\u4efd\u751a\u9ad8\uff0c\u6b64\u8bdd\u4e43\u662f\u4e0d\u60f3 \u5939\u653b\uff0c\u8463\u5de8\u5ddd\u4e00\u7b11\u9000\u4e0b\uff0c\u624b\u4e2d\u6263\u7740\u4e09\u679a\u900f\u9aa8\u9489\uff0c\u76ee\u4e0d\u8f6c\u775b\u6ce8\u89c6\u6597\u573a\u3002 \u3000\u3000\u7518\u51e4\u6c60\u548c\u97e9\u91cd\u5c71\u4e00\u573a\u6076\u6597\uff0c\u5ba2\u820d\u7684\u4eba\u5168\u7ed9\u60ca\u9192\u3002\u767d\u6cf0\u5b98\u8dd1\u8fc7\u90bb\u5ba4\uff0c\u5bf9\u9c7c\u5a18\u9053\uff1a\u201c\u4f60\u770b\u7740 \u8f66\u8001\u4f2f\uff0c\u5207\u4e0d\u53ef\u64c5\u81ea\u79bb\u5f00\u3002\u201d\u8dd1\u51fa\u5916\u9762\uff0c\u8463\u5de8\u5ddd\u4e00\u89c1\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u767d\u6cf0\u5b98\u539f\u6765\u662f\u4f60\uff01\u4f60\u7684\u8001 \u4e08\u4eba\u6b63\u751f\u4f60\u7684\u6c14\u5462\uff01\u201d\u767d\u6cf0\u5b98\u6012\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u201d\u8463\u5de8\u5ddd\u624b\u81c2\u4e00\u6325\uff0c\u4e09\u679a\u900f\u9aa8\u9489\u7834\u7a7a\u5c04\u6765\uff0c\u5168 \u5954\u767d\u6cf0\u5b98\u7a74\u9053\uff0c\u767d\u6cf0\u5b98\u62d4\u5200\u5728\u624b\uff0c\u8fce\u7a7a\u4e00\u78d5\u5c06\u5934\u4e00\u679a\u900f\u9aa8\u9489\u78d5\u98de\uff0c\u5de6\u624b\u4e00\u62db\uff0c\u628a\u7b2c\u4e8c\u679a\u900f\u9aa8 \u9489\u631f\u5728\u4e2d\u98df\u4e8c\u6307\u4e4b\u95f4\uff0c\u5411\u4e0a\u4e00\u5f39\uff0c\u5c06\u7b2c\u4e09\u679a\u900f\u9aa8\u9489\u4e5f\u6253\u843d\u4e86\u3002 \u3000\u3000\u767d\u6cf0\u5b98\u6b66\u529f\u5728\u540c\u95e8\u4e4b\u4e2d\u8981\u6570\u5230\u7b2c\u56db\uff0c\u4f46\u6697\u5668\u4e4b\u7cbe\u5374\u662f\u6570\u4e00\u6570\u4e8c\uff0c\u6240\u4ee5\u63a5\u6697\u5668\u624b\u6cd5\u6781\u4e3a\u7eaf \u719f\uff0c\u8463\u5de8\u5ddd\u4e0d\u77e5\u6df1\u6d45\uff0c\u5fc3\u60f3\uff1a\u600e\u4e48\u8fd9\u767d\u6cf0\u5b98\u4e5f\u5982\u6b64\u5389\u5bb3\u3002\u4e0d\u6562\u6020\u6162\uff0c\u4e00\u63a0\u6570\u4e08\uff0c\u53cc\u638c\u4e00\u5806\u4e00 \u5e26\uff0c\u5288\u9762\u4fbf\u4f7f\u51fa\u5f62\u610f\u6d3e\u7684\u7edd\u62db\u6765\u3002 \u3000\u3000\u767d\u6cf0\u5b98\u6a2a\u5200\u4e00\u524a\uff0c\u8463\u5de8\u5ddd\u53f3\u638c\u500f\u7136\u4e00\u7ffb\uff0c\u638c\u98ce\u52b2\u75be\uff0c\u5df1\u5288\u5230\u767d\u6cf0\u5b98\u53f3\u808b\uff0c\u767d\u6cf0\u5b98\u54e9\u54e9\u4e24 \u5200\u5168\u90e8\u843d\u7a7a\uff0c\u5de6\u95ea\u53f3\u907f\uff0c\u7518\u51e4\u6c60\u89c1\u72b6\u5403\u4e86\u4e00\u60ca\uff0c\u638c\u6cd5\u7a0d\u758f\uff0c\u51e0\u4e4e\u7ed9\u8f9f\u4e91\u9504\u6240\u4f24\u3002\u8463\u5de8\u5ddd\u8fd0\u638c \u5982\u98ce\uff0c\u67d4\u4e2d\u5e26\u521a\uff0c\u9022\u62a2\u767d\u6cf0\u5b98\u624b\u4e2d\u5175\u5203\u3002\u767d\u6cf0\u5b98\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u8fde\u4eba\u5e26\u5200\u4e00\u8f6c\uff0c\u500f\u5730\u4e00 \u62db\u201c\u96c1\u843d\u5e73\u6c99\u201d\uff0c\u8d25\u4e2d\u6c42\u80dc\u3002\u90a3\u6599\u8463\u5de8\u5ddd\u638c\u6cd5\u5df2\u5f97\u5f62\u610f\u95e8\u7cbe\u9ad3\uff0c\u82e5\u5b9e\u82e5\u865a\uff0c\u53cc\u81c2\u4e00\u5206\uff0c\u5de6\u638c \u4e00\u62d4\u5200\u628a\uff0c\u53f3\u638c\u4e00\u6309\uff0c\u9053\u58f0\uff1a\u201c\u7740\uff01\u201d\u767d\u6cf0\u5b98\u4e00\u4e2a\u5012\u7ffb\uff0c\u8dcc\u5728\u5730\u4e0a\uff0c\u8463\u5de8\u5ddd\u5927\u559c\uff0c\u521a\u521a\u8de8\u51fa \u4e00\u6b65\uff0c\u767d\u6cf0\u5b98\u5de6\u624b\u4e00\u626c\uff0c\u7a81\u7136\u559d\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff01\u201d\u987f\u65f6\u4e00\u7247\u94f6\u5149\u707f\u70c2\uff0c\u55e4\u55e4\u58f0\u54cd\u3002 \u8463\u5de8\u5ddd\u5927\u5403\u4e00\u60ca\uff0c\u4e00\u8dc3\u4e08\u8bb8\uff0c\u888d\u8896\u6025\u6325\uff0c\u867d\u7136\u628a\u90a3\u4e00\u5927\u628a\u6885\u82b1\u9488\u90fd\u626b\u8361\u51c0\u5c3d\uff0c\u4f46\u5df2\u95f9\u5f97\u4e2a\u624b \u5fd9\u811a\u4e71\uff01 \u3000\u3000\u767d\u6cf0\u5b98\u6240\u7ec3\u7684\u6885\u82b1\u9488\u539f\u662f\u51c6\u5907\u5bf9\u4ed8\u4e86\u56e0\u7684\uff0c\u5389\u5bb3\u5f02\u5e38\u3002\u5f53\u5e74\u5728\u7530\u6a2a\u5c9b\u7684\u5b64\u5cf0\u4e4b\u4e0a\uff0c\u5c31\u66fe \u9732\u8fc7\u4e00\u624b\u3002\u8463\u5de8\u5ddd\u529f\u529b\u4e0d\u53ca\u4e86\u56e0\uff0c\u5bf9\u4ed8\u8fd9\u79cd\u7ec6\u5c0f\u7684\u6697\u5668\uff0c\u4e0d\u5f97\u4e0d\u51db\u7136\u6212\u60e7\u3002 \u3000\u3000\u8463\u5de8\u5ddd\u98d8\u8eab\u4e0b\u5730\uff0c\u53d1\u638c\u518d\u653b\uff0c\u8eab\u6cd5\u5927\u53d8\uff0c\u53ea\u89c1\u4ed6\u8eab\u5982\u98de\u7d6e\uff0c\u7ed5\u7740\u767d\u6cf0\u5b98\u75be\u8f6c\uff0c\u53eb\u767d\u6cf0\u5b98 \u8981\u5904\u5904\u63d0\u9632\uff0c\u817e\u4e0d\u51fa\u624b\u6765\u638f\u6478\u6697\u5668\u3002\u8fd9\u79cd\u6e38\u6597\u7684\u529f\u592b\uff0c\u4e43\u662f\u529f\u529b\u9ad8\u8005\u5bf9\u4ed8\u4f4e\u624b\u65f6\u53ef\u7a33\u64cd\u80dc\u7b97 \u7684\u6218\u7565\u3002\u4f46\u597d\u5728\u767d\u6cf0\u5b98\u6b66\u529f\u867d\u7136\u8f83\u4f4e\uff0c\u8fd8\u4e0d\u81f3\u76f8\u5dee\u8fc7\u8fdc\uff0c\u5728\u4ed6\u7f13\u653b\u7684\u6218\u7565\u4e4b\u4e0b\uff0c\u8fd8\u52c9\u5f3a\u53ef\u4ee5 \u62b5\u6321\u5f97\u4f4f\u3002 \u3000\u3000\u53c8\u6218\u4e86\u7247\u523b\uff0c\u8463\u5de8\u5ddd\u638c\u6cd5\u6e10\u7d27\uff0c\u98d8\u5ffd\u5982\u98ce\uff01\u767d\u6cf0\u5b98\u5200\u5149\u970d\u970d\uff0c\u603b\u6cbe\u4e0d\u7740\u654c\u4eba\uff0c\u53ea\u6709\u7d27\u5bc6 \u5c01\u95ed\u95e8\u6237\u3002\u4f46\u9976\u662f\u5982\u6b64\uff0c\u8fd8\u662f\u89c9\u5f97\u654c\u4eba\u53cc\u638c\uff0c\u77eb\u82e5\u795e\u9f99\uff0c\u5728\u81ea\u5df1\u9762\u95e8\u4e71\u6643\u3002 \u3000\u3000\u90a3\u4e00\u8fb9\u7518\u51e4\u6c60\u548c\u97e9\u91cd\u5c71\u529f\u529b\u6089\u654c\uff0c\u539f\u53ef\u6253\u6210\u5e73\u624b\uff0c\u4f46\u4e3a\u4e86\u5fc3\u60ac\u767d\u6cf0\u5b98\u7684\u5b89\u5371\uff0c\u4e0d\u514d\u53d7\u4e86 \u5f71\u54cd\uff0c\u97e9\u91cd\u5c71\u7684\u4e00\u767e\u96f6\u516b\u8def\u8f9f\u4e91\u9504\u6cd5\uff0c\u53d8\u5316\u65e0\u65b9\uff0c\u89c1\u7518\u51e4\u6c60\u6c14\u9981\uff0c\u8fde\u4e0b\u6740\u624b\uff0c\u6b63\u5728\u7d27\u8feb\u4e4b \u9645\uff0c\u7518\u51e4\u6c60\u4e00\u58f0\u957f\u5578\uff0c\u731b\u53d1\u4e00\u638c\uff0c\u628a\u8f9f\u4e91\u9504\u8361\u5f00\uff0c\u63a0\u51fa\u4e08\u4f59\uff0c\u7a81\u7136\u559d\u9053\uff1a\u201c\u770b\u9556\uff01\u201d\u97e9\u91cd\u5c71 \u6a2a\u9504\u4e00\u6321\uff0c\u7518\u51e4\u6c60\u5df2\u653e\u51fa\u4e09\u679d\u54cd\u7bad\uff0c\u4e00\u58f0\u957f\u4e24\u58f0\u77ed\uff0c\u54cd\u7bad\u76f4\u4e0a\u9065\u7a7a\uff0c\u9e23\u545c\u4e4b\u58f0\uff0c\u5341\u5206\u523a\u8033\u3002 \u97e9\u91cd\u5c71\u89c1\u7518\u51e4\u4ed6\u7684\u54cd\u7bad\u5e76\u4e0d\u5411\u81ea\u5df1\u5c04\u6765\uff0c\u9053\uff1a\u201c\u4f60\u6363\u4ec0\u4e48\u9b3c\uff1f\u201d\u8fd8\u4ee5\u4e3a\u4ed6\u7684\u6697\u5668\u53e6\u6709\u602a\u5f02\uff0c \u6a2a\u9504\u6212\u5907\uff0c\u4e0d\u6562\u7a0d\u61c8\u3002\u7518\u51e4\u6c60\u731b\u7684\u98de\u8eab\u5411\u8463\u5de8\u5ddd\u6251\u53bb\uff0c\u9aa4\u7136\u4e00\u62db\u201c\u91d1\u9f99\u63a2\u722a\u201d\uff0c\u5411\u8463\u5de8\u5ddd\u540e \u5fc3\u72e9\u51fb\uff0c\u8463\u5de8\u5ddd\u53cd\u624b\u4e00\u638c\uff0c\u5374\u6321\u4e0d\u4f4f\u7518\u51e4\u6c60\u795e\u529b\uff0c\u7ed9\u63a8\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u51e0\u4e4e\u8dcc\u5012\u3002\u97e9\u91cd\u5c71\u5927 \u6012\uff0c\u4e00\u8dc3\u800c\u4e0a\uff0c\u8f9f\u4e91\u9504\u4e00\u5c55\uff0c\u53c8\u628a\u7518\u51e4\u6c60\u622a\u4f4f\u3002 \u3000\u3000\u8463\u5de8\u5ddd\u5403\u4e86\u4e00\u638c\uff0c\u5f53\u65f6\u8fd8\u4e0d\u89c9\u600e\u7684\uff0c\u8fc7\u540e\u9aa8\u9abc\u7adf\u7136\u9690\u9690\u4f5c\u75db\u3002\u5728\u540c\u95e8\u4e2d\uff0c\u7518\u51e4\u6c60\u5185\u529f\u4ec5 \u5728\u4e86\u56e0\u4e4b\u4e0b\uff0c\u8fd9\u4e00\u638c\u4f7f\u7684\u4e43\u662f\u5185\u5bb6\u9634\u529b\uff0c\u9976\u662f\u8463\u5de8\u5ddd\u90a3\u6837\u7684\u9ad8\u624b\uff0c\u5403\u4e86\u4e00\u638c\uff0c\u4e5f\u635f\u5143\u6c14\uff0c\u518d \u7528\u6e38\u6597\u6765\u56f0\u767d\u6cf0\u5b98\u65f6\uff0c\u8eab\u6cd5\u5df2\u4e0d\u4f3c\u4ee5\u524d\u7075\u6d3b\u4e86\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u6b64\u6d88\u5f7c\u957f\uff0c\u767d\u6cf0\u5b98\u867d\u7136\u8fd8\u662f\u5fae\u5904 \u4e0b\u98ce\uff0c\u4f46\u5df2\u8fdc\u4e0d\u4f3c\u521a\u624d\u90a3\u6837\u5403\u529b\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u6076\u6218\uff0c\u6253\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u65c5\u5e97\u7684\u4eba\u5168\u90fd\u60ca\u9192\uff0c\u6709\u4e9b\u80c6\u5927\u7684\uff0c\u4fbf\u5728\u7a97\u53e3\u63a2\u5934\u51fa\u6765\u3002\u65c5 \u5e97\u4e3b\u4eba\u614c\u4e86\u624b\u811a\uff0c\u5374\u4e0d\u6562\u4e0a\u524d\u529d\u67b6\u3002\u6709\u4eba\u9053\uff1a\u201c\u5feb\u62a5\u5b98\u5440\uff01\u201d\u7518\u51e4\u6c60\u4e00\u542c\uff0c\u6697\u53eb\u4e0d\u5999\uff0c\u731b\u653b \u6570\u62db\uff0c\u4ee5\u8fdb\u4e3a\u9000\uff0c\u97e9\u91cd\u5c71\u7aa5\u7834\u7528\u610f\uff0c\u51b7\u7b11\u559d\u9053\uff1a\u201c\u5478\uff0c\u4f60\u8fd8\u60f3\u9003\uff01\u201d\u8f9f\u4e91\u9504\u76d8\u65cb\u98de\u821e\uff0c\u4e00\u6b65 \u4e5f\u4e0d\u80af\u9000\u8ba9\u3002\u90a3\u8fb9\u53a2\u767d\u6cf0\u5b98\u867d\u7136\u62b5\u6321\u5f97\u4f4f\uff0c\u4ecd\u662f\u8131\u4e0d\u4e86\u8463\u5de8\u5ddd\u638c\u529b\u8303\u56f4\u3002 \u3000\u3000\u5415\u56db\u5a18\u5728\u845b\u5cad\u4e0a\u542c\u5230\u540c\u95e8\u54cd\u7bad\uff0c\u5306\u5306\u8d76\u56de\u3002\u65c5\u820d\u5efa\u5728\u5c71\u9e93\uff0c\u501a\u5c71\u9762\u6e56\uff0c\u5415\u56db\u5a18\u65b9\u5230\u6e56\u6ee8 \u9053\u4e0a\uff0c\u5ffd\u89c1\u4e00\u9a91\u9a6c\u8fce\u9762\u98de\u6765\uff0c\u9a70\u5411\u57ce\u5185\u3002\u5415\u56db\u5a18\u5fc3\u5ff5\u4e00\u52a8\uff0c\u811a\u5c16\u70b9\u5730\uff0c\u731b\u7684\u98de\u4e0a\u9a6c\u80cc\uff0c\u53ea\u4e00 \u63ea\uff0c\u5c31\u628a\u9a6c\u4e0a\u4eba\u63ea\u4e0b\u5730\u6765\uff0c\u90a3\u9a6c\u60ca\u53eb\u4e00\u58f0\uff0c\u8dd1\u8fc7\u4e00\u8fb9\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u90a3\u4eba\u5ffd\u9053\uff1a \u201c\u4f60\u4e0d\u662f\u5728\u5c0f\u5e97\u897f\u4fbf\u4e0a\u623f\u4f4f\u7684\u674e\u76f8\u516c\u4e48\uff1f\u201d\u5415\u56db\u5a18\u8fd9\u624d\u6e05\u695a\u662f\u5e97\u4e2d\u7167\u6599\u9a6c\u5339\u7684\u4eba\u3002\u90a3\u4eba\u9053\uff1a \u201c\u5e97\u91cc\u6765\u4e86\u5f3a\u76d7\u6253\u52ab\uff0c\u6b63\u548c\u4f60\u7684\u540c\u4f34\u6253\u5462\uff0c\u5ba2\u5b98\u5feb\u653e\u6211\u8d70\u3002\u201d\u8fd9\u4e2a\u770b\u9a6c\u7684\u5c0f\u4e8c\u80c6\u5b50\u5012\u5927\uff0c\u5415 \u56db\u5a18\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u53bb\u62a5\u5b98\uff0c\u6211\u56de\u53bb\u5e2e\u4f60\u4eec\u88ad\u76d7\u3002\u201d\u6697\u5730\u91cc\u5374\u62fe\u8d77\u4e00\u679a\u5c0f\u77f3\uff0c\u628a\u624b\u4e00\u626c\uff0c\u5c06 \u9a6c\u817f\u6253\u4f24\u3002\u90a3\u9a6c\u5012\u662f\u826f\u9a6c\uff0c\u5636\u53eb\u4e00\u58f0\uff0c\u8dd1\u5230\u4e0d\u8fdc\u5904\u505c\u4f4f\uff0c\u4ecd\u7136\u7b49\u5019\u4e3b\u4eba\u3002\u770b\u9a6c\u7684\u5c0f\u4e8c\u4e0d\u77e5\u5415 \u56db\u5a18\u662f\u597d\u4eba\u8fd8\u662f\u574f\u4eba\uff0c\u89c1\u5979\u80af\u653e\u81ea\u5df1\uff0c\u6025\u5fd9\u4e00\u6e9c\u70df\u7684\u8dd1\u53bb\uff0c\u8de8\u4e0a\u9a6c\u80cc\u8d70\u4e86\u3002 \u3000\u3000\u7518\u51e4\u6c60\u548c\u767d\u6cf0\u5b98\u6b63\u5728\u5403\u7d27\uff0c\u767d\u6cf0\u5b98\u5f62\u52bf\u5c24\u5176\u5371\u6025\uff0c\u8463\u5de8\u5ddd\u8fde\u8fde\u8fdb\u903c\uff0c\u767d\u6cf0\u5b98\u5ffd\u5730\u53eb\u9053\uff1a \u201c\u5e08\u59b9\uff01\u201d\u8463\u5de8\u5ddd\u9053\uff1a\u201c\u53eb\u5988\u5988\u4e5f\u6ca1\u6709\u7528\uff01\u201d\u8bdd\u58f0\u672a\u505c\uff0c\u53ea\u89c1\u767d\u5149\u4e00\u95ea\uff0c\u5415\u56db\u5a18\u8fde\u4eba\u5e26\u5251\uff0c \u65cb\u98ce\u822c\u7684\u6251\u5230\u9762\u524d\uff0c\u8463\u5de8\u5ddd\u5927\u5403\u4e00\u60ca\uff0c\u4fa7\u8eab\u4e00\u95ea\uff0c\u4e00\u638c\u5411\u5415\u56db\u5a18\u80a9\u5934\u6253\u53bb\uff0c\u5415\u56db\u5a18\u4f55\u7b49\u5feb \u6377\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u4e00\u62db\u201c\u795e\u9f99\u6dd6\u9996\u201d\uff0c\u5b9d\u5251\u547c\u7684\u5708\u8f6c\u8fc7\u6765\uff0c\u9976\u662f\u8463\u5de8\u5ddd\u90a3\u6837\u7684\u540d\u624b\uff0c\u7f29\u8eab\u95ea \u65f6\uff0c\u8863\u8896\u4e5f\u7ed9\u524a\u53bb\u4e00\u622a\u3002\u8463\u5de8\u5ddd\u5927\u53eb\uff1a\u201c\u98ce\u7d27\uff0c\u626f\u547c\uff01\u201d\u97e9\u91cd\u5c71\u628a\u8170\u4e00\u8eac\uff0c\u4e00\u679d\u86c7\u7130\u7bad\u7a81\u7136 \u5c04\u51fa\uff0c\u8fd9\u662f\u4ed6\u6551\u547d\u7684\u6697\u5668\u4e4b\u4e00\uff0c\u5415\u56db\u5a18\u4f38\u5251\u4e00\u683c\uff0c\u5ffd\u7136\u201c\u84ec\u201d\u7684\u70b8\u5f00\uff0c\u5415\u56db\u5a18\u5413\u4e86\u4e00\u8df3\uff0c\u5411 \u65c1\u659c\u8dc3\u6570\u6b65\uff0c\u5e78\u672a\u70e7\u7740\u3002\u97e9\u91cd\u5c71\u89c1\u662f\u5415\u56db\u5a18\u6765\uff0c\u90a3\u91cc\u8fd8\u6562\u604b\u6218\uff0c\u6025\u5fd9\u98de\u8eab\u4e0a\u5c4b\uff0c\u548c\u8463\u5de8\u5ddd\u4e00 \u9053\u9003\u8d70\uff0c\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8fd9\u91cc\u4e0d\u80fd\u4f4f\u4e86\u3002\u201d\u6025\u5fd9\u53bb\u89c1\u5e97\u4e3b\uff0c\u9053\uff1a\u201c\u660e\u544a\u5e97\u4e3b\uff0c\u6211\u4eec\u90fd\u662f\u5e2e\u4f1a\u4e2d \u4eba\uff0c\u5728\u8fd9\u91cc\u78b0\u5230\u4ec7\u5bb6\uff0c\u6211\u4eec\u4e0d\u60f3\u7275\u7d2f\u4e8e\u4f60\uff0c\u8bf7\u628a\u623f\u94b1\u7b97\u6e05\uff0c\u6211\u4eec\u73b0\u5728\u5c31\u8d70\u3002\u201d\u5e2e\u4f1a\u4e2d\u4eba\u6597 \u6bb4\uff0c\u5f53\u65f6\u4e43\u662f\u5e38\u4e8b\uff0c\u5e97\u4e3b\u4eba\u5413\u5f97\u9762\u9752\u5507\u767d\uff0c\u90a3\u91cc\u8fd8\u6562\u6536\u94b1\uff0c\u7518\u51e4\u6c60\u4e22\u4e0b\u5341\u4e24\u94f6\u5b50\uff0c\u4e5f\u4e0d\u7406\u4ed6\u3002 \u3000\u3000\u9c7c\u5a18\u5728\u623f\u5185\u6b63\u7b49\u5f97\u5fc3\u7126\uff0c\u542c\u5916\u9762\u53ae\u6740\u58f0\u58f0\uff0c\u53c8\u4e0d\u6562\u5f00\u7a97\u5916\u9cd7\uff0c\u6e10\u6e10\u5916\u9762\u55a7\u58f0\u6e10\u5bc2\uff0c\u4e0d\u4e45 \u767d\u6cf0\u5b98\u7b49\u4e09\u4eba\u56de\u5230\u623f\u4e2d\u3002\u9c7c\u5a18\u9053\uff1a\u201c\u600e\u4e48\u5566\uff1f\u7518\u5927\u4fa0\u78b0\u5230\u4ec0\u4e48\u4eba\u4e86\uff1f\u201d\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u522b\u591a\u95ee \u5566\uff0c\u5feb\u6536\u62fe\u5427\uff0c\u54b1\u4eec\u73b0\u5728\u5c31\u8d70\uff01\u201d\u7518\u51e4\u6c60\u53eb\u9053\uff1a\u201c\u8f66\u8001\u6015\uff01\u201d\u5e8a\u4e0a\u8f66\u9f0e\u4e30\u7ffb\u4e86\u4e2a\u8eab\uff0c\u5ffd\u7136\u5750 \u4e86\u8d77\u6765\u3002 \u3000\u3000\u5415\u56db\u5a18\u559c\u9053\uff1a\u201c\u8f66\u8001\u4f2f\uff0c\u4f60\u6ca1\u4e8b\u4e86\uff1f\u201d\u8f66\u9f0e\u4e30\u9053\uff1a\u201c\u90a3\u4e2a\u5973\u5b69\u5b50\u51fa\u624b\u597d\u8fa3\uff0c\u5e78\u8499\u4e24\u4f4d\u76f8 \u6551\u3002\u201d\u7518\u51e4\u4ed6\u9053\uff1a\u201c\u4e94\u54e5\uff0c\u4f60\u80cc\u8f66\u8001\u4f2f\u3002\u6211\u548c\u516b\u59b9\u65ad\u540e\u3002\u201d\u8f66\u9f0e\u4e30\u9053\uff1a\u201c\u5bff\u660c\u4e66\u9662\u8bf8\u751f\uff0c\u90fd \u662f\u5fc3\u6000\u6545\u56fd\u7684\u70ed\u8840\u5c11\u5e74\uff0c\u7518\u5927\u4fa0\u5982\u6ca1\u9002\u5f53\u5730\u65b9\u6b47\u8db3\uff0c\u4e0d\u59a8\u5728\u90a3\u91cc\u6682\u9a7b\u4fa0\u8e2a\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a \u201c\u90a3\u597d\u6781\u4e86\u3002\u201d\u5415\u56db\u5a18\u5ffd\u9053\uff1a\u201c\u4e03\u54e5\uff0c\u6211\u518d\u5230\u629a\u8859\u4e00\u8d9f\u3002\u201d\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u600e\u4e48\u4f60\u8fd8\u8981\u53bb\uff1f\u201d \u5415\u56db\u5a18\u9053\uff1a\u201c\u65c5\u820d\u7684\u4eba\u5df2\u53bb\u62a5\u5b98\uff0c\u4e86\u56e0\u90a3\u53ae\u77e5\u9053\u6211\u4eec\u5728\u6b64\uff0c\u5fc5\u7136\u4eb2\u6765\u3002\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u516b \u59b9\u7528\u610f\u6211\u77e5\u9053\u4e86\uff0c\u90a3\u662f\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\u3002\u4e86\u56e0\u6765\u6349\u6211\u4eec\uff0c\u6211\u4eec\u5c31\u53bb\u6551\u8def\u5e08\u5144\u3002\u201d\u9c7c\u5a18\u9053\uff1a\u201c\u5415 \u59d0\u59d0\uff0c\u8fd9\u8ba1\u7b56\u867d\u597d\uff0c\u53ea\u662f\u4f60\u7d2f\u4e86\u4e00\u665a\uff0c\u4e5f\u8be5\u6b47\u6b47\u4e86\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4e0d\u8981\u7d27\u3002\u201d\u5403\u4e86\u51e0\u5757 \u5e72\u7cae\uff0c\u559d\u4e86\u4e00\u5927\u676f\u6c34\uff0c\u8eab\u5b50\u4e00\u626d\uff0c\u5c55\u5f00\u7edd\u9876\u8f7b\u529f\uff0c\u4e0a\u5c4b\u98de\u8d70\u3002 \u3000\u3000\u674e\u6cbb\u548c\u51af\u7433\u4e0b\u4e86\u845b\u5cad\uff0c\u51af\u7433\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u674e\u6cbb\u9053\uff1a\u201c\u745b\u59b9\uff0c\u7518\u5927\u4fa0\u77e5\u9053\u4f60\u662f\u5c0f\u5b69\uff0c\u4e0d\u4f1a\u602a \u4f60\u7684\u3002\u201d\u4e24\u4eba\u8d70\u4e86\u4e00\u4f1a\uff0c\u5df2\u5230\u65c5\u5e97\u9644\u8fd1\uff0c\u5ffd\u89c1\u4e00\u961f\u5b98\u5175\uff0c\u5728\u5916\u5de1\u903b\uff0c\u51af\u7433\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u54b1\u4eec \u5feb\u9003\u3002\u201d\u65c5\u820d\u4e2d\u8dd1\u51fa\u4e00\u4e2a\u548c\u5c1a\uff0c\u6b63\u662f\u4e86\u56e0\uff01 \u3000\u3000\u4e86\u56e0\u4e00\u89c1\u51af\u7433\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u54fc\uff01\u4f60\u8fd9\u5c0f\u6363\u4e71\uff0c\u5f80\u90a3\u91cc\u8dd1\uff1f\u201d\u50e7\u888d\u4e00\u4f5b\uff0c\u63d0\u8d77\u6597\u5927\u7684\u7985 \u6756\uff0c\u547c\u547c\u8ffd\u6765\uff0c\u51af\u7433\u9053\uff1a\u201c\u674e\u54e5\u54e5\uff0c\u4f60\u66ff\u6211\u6321\u4ed6\u4e00\u9635\uff0c\u6211\u7528\u6697\u5668\u5e2e\u4f60\u3002\u201d\u4e86\u56e0\u8f7b\u529f\u867d\u7136\u4e0d\u53ca \u5415\u56db\u5a18\uff0c\u4f46\u6bd4\u8d77\u51af\u7433\u5374\u4e0d\u77e5\u9ad8\u660e\u591a\u5c11\uff0c\u4e00\u5ffd\u513f\u5c31\u8ffd\u5230\u80cc\u540e\uff0c\u4f38\u5f00\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u5f53\u5934\u6293\u4e0b\u3002 \u5ffd\u7136\u5bd2\u5149\u4e00\u95ea\uff0c\u674e\u6cbb\u5237\u7684\u4e00\u5251\u659c\u4fa7\u523a\u6765\uff0c\u5251\u62db\u5947\u5feb\uff0c\u4e86\u56e0\u7f29\u638c\u659c\u5288\uff0c\u51af\u7433\u4e00\u56de\u624b\u5c04\u51fa\u4e24\u67c4\u98de \u5200\uff0c\u4e86\u56e0\u4e3e\u6756\u4e00\u64a9\uff0c\u4e24\u67c4\u98de\u5200\u90fd\u88ab\u53cd\u51fb\u9707\u4e0a\u9ad8\u7a7a\uff0c\u8fdc\u8fdc\u7684\u629b\u843d\u6e56\u5fc3\u3002\u51af\u7433\u53d1\u8db3\u72c2\u5954\uff0c\u5341\u51e0\u540d \u6355\u5feb\u9a91\u9a6c\u6025\u8ffd\uff0c\u4e86\u56e0\u8eab\u5f62\u4e00\u52a8\uff0c\u674e\u6cbb\u5237\u5237\u4e24\u5251\u53c8\u8fce\u9762\u523a\u6765\u4e86\uff0c\u4e86\u56e0\u559d\u9053\uff1a\u201c\u4f60\u627e\u6b7b\uff01\u201d\u547c\u7684 \u4e00\u6756\uff0c\u201c\u8fc5\u96f7\u51fb\u9876\u201d\uff0c\u76f4\u5411\u674e\u6cbb\u5934\u9885\u6253\u843d\uff0c\u674e\u6cbb\u8eab\u5f62\u4e00\u6643\uff0c\u5251\u950b\u70b9\u5411\u4e86\u56e0\u80f8\u819b\uff0c\u8fd9\u4e00\u62db\u672c\u662f \u767d\u53d1\u9b54\u5973\u72ec\u95e8\u5251\u6cd5\u4e2d\u7684\u6740\u624b\uff0c\u4e86\u56e0\u4e00\u6756\u51fb\u51fa\uff0c\u95e8\u6237\u5927\u5f00\uff0c\u674e\u6cbb\u4ee5\u4e3a\u5fc5\u7136\u5f97\u624b\uff0c\u90a3\u6599\u4e86\u56e0\u7684\u7985 \u6756\u5728\u534a\u7a7a\u6253\u4e86\u4e00\u4e2a\u5708\u5708\uff0c\u4e0d\u7528\u64a4\u6756\u62a4\u8eab\uff0c\u674e\u6cbb\u5df2\u89c9\u5f97\u597d\u4f3c\u4e00\u80a1\u5927\u529b\u63a8\u6765\uff0c\u8eab\u5f62\u4e0d\u7531\u5f97\u5012\u9000\u4e24 \u6b65\uff0c\u5251\u70b9\u4e5f\u7ed9\u6756\u98ce\u9707\u6b6a\u3002\u4e86\u56e0\u5927\u559d\u4e00\u58f0\uff0c\u6756\u5c3e\u4e00\u8d77\uff0c\u547c\u547c\u58f0\u54cd\uff0c\u53c8\u518d\u626b\u6765\u3002 \u3000\u3000\u674e\u6cbb\u5927\u5403\u4e00\u60ca\uff0c\u4e0d\u6562\u786c\u67b6\uff0c\u5728\u6756\u98ce\u4e2d\u4e00\u4e2a\u7ffb\u8eab\uff0c\u4ed7\u7740\u5251\u6cd5\u8f7b\u7075\uff0c\u7a81\u7136\u62a2\u653b\u4ed6\u5de6\u9762\u7a7a\u95e8\uff0c \u4e86\u56e0\u7985\u6756\u4e00\u7acb\uff0c\u6321\u4e86\u5f00\u53bb\u3002\u4e24\u4eba\u6597\u4e86\u5341\u62db\uff0c\u674e\u6cbb\u4e00\u53e3\u5251\u75be\u5982\u64ce\u7535\uff0c\u603b\u4e0d\u8ba9\u4ed6\u7985\u6756\u78b0\u7740\uff0c\u6597\u5230 \u9163\u65f6\uff0c\u674e\u6cbb\u5251\u5149\u4e00\u95ea\uff0c\u518d\u53d6\u4ed6\u80a9\u4e0a\u7684\u201c\u98ce\u5e9c\u7a74\u201d\uff0c\u4e86\u56e0\u8fce\u7740\u4ed6\u7684\u5251\u52bf\uff0c\u6756\u8eab\u4e00\u9001\uff0c\u90a3\u6599\u674e\u6cbb \u7684\u5251\u6cd5\u5168\u4e0e\u5e73\u5e38\u5251\u6cd5\u76f8\u53cd\uff0c\u660e\u660e\u770b\u4ed6\u662f\u523a\u5de6\u9762\u504f\u950b\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5374\u500f\u7136\u6539\u5411\uff0c\u4e86\u56e0\u6756\u5934\u4e00 \u70b9\uff0c\u7a81\u89c9\u51b7\u98ce\u6025\u52b2\uff0c\u5251\u950b\u5df2\u5230\u5de6\u80a9\uff0c\u4e86\u56e0\u80a9\u5934\u4e00\u7f29\uff0c\u5de6\u638c\u5f80\u524d\u4e00\u6293\uff0c\u674e\u6cbb\u7684\u5251\u950b\u5df2\u70b9\u5230\u4e86\u56e0 \u80a9\u4e0a\uff0c\u7a81\u7136\u4e00\u6ed1\u5931\u4e86\u91cd\u5fc3\uff0c\u7adf\u88ab\u4e86\u56e0\u4e00\u6293\u6293\u7740\u624b\u8155\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c\u957f\u5251\u68a2\u6994\u4e00\u58f0\uff0c\u8dcc\u843d\u5730\u4e0a\u3002 \u3000\u3000\u4e86\u56e0\u5de6\u624b\u4e00\u4e3e\uff0c\u628a\u674e\u6cbb\u5e73\u4e3e\u8d77\u6765\uff0c\u5f85\u8981\u4e0b\u6454\uff0c\u5ffd\u53c8\u7f29\u4f4f\u3002\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\u4eba\u95e8\u4e0b\uff1f\u201d\u674e \u6cbb\u9053\uff1a\u201c\u4f60\u8981\u6740\u4fbf\u6740\uff0c\u4f55\u5fc5\u591a\u95ee\uff1f\u201d\u4e86\u56e0\u5fc3\u60f3\u8fd9\u4eba\u5251\u6cd5\u5947\u8be1\uff0c\u4f3c\u4e4e\u4e0d\u5728\u5415\u56db\u5a18\u4e4b\u4e0b\uff0c\u5012\u4e0d\u53ef \u968f\u4fbf\u6740\u4ed6\u3002\u9053\uff1a\u201c\u4f60\u80fd\u63a5\u6211\u4e09\u5341\u4f59\u62db\uff0c\u4e5f\u7b97\u4e00\u6761\u597d\u6c49\u3002\u6682\u4e14\u9976\u4f60\u4e00\u547d\u3002\u201d\u4e94\u6307\u4e00\u7d27\uff0c\u7528\u6700\u5389 \u5bb3\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u6cd5\uff0c\u628a\u674e\u6cbb\u634f\u5f97\u5168\u8eab\u9ebb\u8f6f\uff0c\u9aa8\u5934\u677e\u6563\uff0c\u89c1\u674e\u6cbb\u989d\u4e0a\u6c57\u73e0\u4f3c\u9ec4\u8c46\u822c\u4e00\u9897\u9897\u6ef4 \u4e0b\uff0c\u5c45\u7136\u54fc\u4e5f\u4e0d\u54fc\u3002\u4e86\u56e0\u4e5f\u4e0d\u7981\u6697\u6697\u8d5e\u4ed6\u786c\u6c49\uff0c\u53eb\u8fc7\u5b98\u5dee\u628a\u4ed6\u7ed1\u4e86\uff0c\u8de8\u4e0a\u9a8f\u9a6c\uff0c\u518d\u5411\u524d\u8ffd\u3002 \u3000\u3000\u51af\u7433\u53d1\u8db3\u72c2\u5954\uff0c\u80cc\u540e\u5341\u51e0\u9a91\u5feb\u9a6c\u8ffd\u6765\uff0c\u51af\u7433\u5f85\u4ed6\u4eec\u8ffd\u5f97\u8fd1\u65f6\uff0c\u53cd\u624b\u4e00\u626c\uff0c\u7a81\u7136\u53d1\u51fa\u4e24\u67c4 \u98de\u5200\uff0c\u5979\u7684\u98de\u5200\u4e4b\u6280\uff0c\u51fa\u4e8e\u949f\u4e07\u5802\u6240\u4f20\uff0c\u542b\u6709\u5267\u6bd2\uff0c\u98de\u5200\u63d2\u5165\u9a6c\u773c\uff0c\u6bd2\u6027\u767b\u65f6\u53d1\u4f5c\uff0c\u4e24\u5339\u9a6c \u53d8\u4e86\u778e\u9a6c\uff0c\u72c2\u53eb\u4e71\u5954\uff0c\u9a6c\u4e0a\u7684\u6355\u5feb\u7ed9\u6454\u4e0b\u5730\u6765\uff0c\u5e78\u597d\u6ca1\u6709\u8dcc\u6b7b\u3002 \u3000\u3000\u8feb\u9a91\u53d7\u963b\uff0c\u51af\u7433\u7f13\u4e86\u53e3\u6c14\uff0c\u53c8\u518d\u98de\u5954\u3002\u90a3\u5e2e\u6355\u5feb\u76f8\u987e\u5931\u8272\u3002\u6355\u5934\u9053\uff1a\u201c\u4e00\u4e2a\u5c0f\u5b69\u5b50\u4e5f\u6349 \u4e0d\u7740\uff0c\u6211\u4eec\u8fd8\u5f53\u4ec0\u4e48\u516c\u5dee\uff1f\u201d\u7387\u4f17\u518d\u8ffd\uff0c\u8ffd\u5f97\u7d27\u65f6\uff0c\u51af\u7433\u53c8\u4f9d\u524d\u6cd5\uff0c\u5c04\u5012\u4e24\u5339\u5feb\u9a6c\u3002\u5982\u6b64\u8fc7 \u4e86\u51e0\u6b21\uff0c\u51af\u7433\u6697\u5668\u56ca\u4e2d\u53ea\u5269\u4e0b\u4e24\u67c4\u98de\u5200\uff0c\u4e0d\u6562\u518d\u653e\u3002 \u3000\u3000\u6355\u5feb\u4eec\u8ffd\u5f97\u66f4\u7d27\uff0c\u8ffd\u51fa\u4e00\u6bb5\u5f2f\u8def\uff0c\u84e6\u7136\u524d\u9762\u5c18\u5934\u5927\u8d77\uff0c\u4e00\u652f\u519b\u961f\u8fce\u9762\u5954\u6765\uff0c\u4e09\u4e08\u591a\u9ad8\u7684 \u5e05\u5b57\u65d7\u4e0a\u5199\u7740\u6597\u5927\u7684\u4e00\u4e2a\u201c\u5e74\u201d\u5b57\uff0c\u88ab\u897f\u98ce\u5377\u5f97\u730e\u730e\u4f5c\u54cd\u3002\u7b56\u9a6c\u524d\u8ffd\u7684\u65d7\u724c\u5b98\u89c1\u4e00\u4e2a\u5973\u5b69\u8dd1 \u5f97\u98de\u5feb\uff0c\u80cc\u540e\u4e03\u516b\u9a91\u516c\u5dee\u98de\u9a91\u8ffd\u8d76\uff0c\u9887\u4e3a\u60ca\u5f02\u3002\u4e00\u5458\u4ffe\u5c06\u7b56\u9a6c\u4e0a\u524d\uff0c\u63d0\u8d77\u957f\u77db\u671d\u7740\u51af\u7433\u4e00 \u6307\uff0c\u559d\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u201d\u51af\u7433\u5728\u56db\u7687\u5b50\u5e9c\u4e2d\u957f\u5927\uff0c\u4ec0\u4e48\u5b98\u513f\u6ca1\u6709\u89c1\u8fc7\uff0c\u89c1\u957f\u77db\u6307\u5230\uff0c\u5c45\u7136\u4e0d\u614c \u4e0d\u5fd9\uff0c\u4f38\u51fa\u5c0f\u624b\uff0c\u4e00\u628a\u5c06\u957f\u77db\u63e1\u7740\uff0c\u53ea\u4e00\u626f\uff0c\u90a3\u5458\u4ffe\u5c06\u7adf\u7136\u7ed9\u5979\u626f\u4e0b\u9a6c\u6765\uff0c\u51af\u7433\u4e5f\u5b66\u4ed6\u7684\u795e \u6c14\u559d\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u201d\u90a3\u5458\u4ffe\u5c06\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b69\u5b50\u597d\u5927\u80c6\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60 \u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u90a3\u4e03\u516b\u9a91\u6355\u5feb\u5148\u540e\u8ffd\u5230\uff0c\u89c1\u5927\u519b\u5728\u524d\uff0c\u4e0d\u6562\u5984\u52a8\u3002\u6355\u5934\u4e0a\u524d\u5411\u65d7\u724c\u5b98\u89c1\u4e86\u4e2a\u793c\uff0c\u7980\u9053\uff1a \u201c\u8fd9\u5c0f\u5b69\u5b50\u662f\u5b9d\u56fd\u7985\u5e08\u8981\u6211\u4eec\u8ffd\u7684\u3002\u201d\u65d7\u724c\u5b98\u9053\uff1a\u201c\u8c01\u662f\u5b9d\u56fd\u7985\u5e08\uff1f\u201d\u4e86\u56e0\u88ab\u5c01\u5b9d\u56fd\u7985\u5e08\uff0c \u519b\u4e2d\u5e76\u4e0d\u77e5\u9053\u3002\u6355\u5934\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u5c0f\u5b69\u5b50\u662f\u56db\u7687\u5e9c\u7684\u4eba\uff0c\u79c1\u9003\u51fa\u6765\uff0c\u56db\u7687\u5b50\u6d3e\u4eba\u6349\u5979\uff0c\u5b9d\u56fd \u7985\u5e08\u4fbf\u662f\u56db\u7687\u5b50\u6240\u6d3e\u7684\u4eba\u3002\u201d\u65d7\u724c\u5b98\u8083\u7136\u53d8\u8272\uff0c\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u4f60\u7b49\u4e00\u7b49\u3002\u201d\u56de\u9a6c\u7980\u544a\u4e2d \u519b\u3002 \u3000\u3000\u90a3\u5458\u4ffe\u5c06\u7ed9\u51af\u7433\u6c14\u5f97\u4e03\u7a83\u751f\u70df\uff0c\u5175\u58eb\u4eec\u56db\u9762\u56f4\u7740\uff0c\u89c1\u51af\u7433\u4e00\u526f\u5927\u4eba\u795e\u6c14\uff0c\u53c8\u53ef\u7231\u53c8\u53ef \u7b11\uff0c\u90fd\u56f4\u7740\u770b\u70ed\u95f9\u3002\u90a3\u5458\u4ffe\u5c06\u7ed9\u5979\u63e1\u7740\u957f\u77db\uff0c\u5c34\u5c2c\u4e4b\u6781\uff0c\u8c6a\u7136\u633a\u77db\u4e00\u6420\uff0c\u559d\u9053\uff1a\u201c\u4f60\u653e\u4e0d\u653e \u624b\uff1f\u201d\u51af\u7433\u4e00\u7b11\u559d\u9053\uff1a\u201c\u4f60\u653e\u4e0d\u653e\u624b\uff1f\u201d\u6697\u8fd0\u5185\u529b\uff0c\u5c06\u957f\u77db\u4e00\u626f\uff0c\u90a3\u5458\u4ffe\u5c06\u53cc\u624b\u4e00\u677e\uff0c\u4e00\u8de4 \u8dcc\u5012\u5730\u4e0a\u3002 \u3000\u3000\u5175\u58eb\u4eec\u89c1\u4ed6\u4eec\u7684\u7ba1\u5e26\u8dcc\u7ffb\u5728\u4e00\u4e2a\u5973\u5b69\u5b50\u624b\u4e0a\uff0c\u90fd\u6697\u6697\u597d\u7b11\u3002\u90a3\u540d\u519b\u5b98\u8001\u7f9e\u6210\u6012\uff0c\u8df3\u8d77\u6765 \u62a1\u62f3\u5c31\u6253\u3002\u5468\u56f4\u7684\u58eb\u5175\u5ffd\u7136\u56db\u4e0b\u6563\u5f00\uff0c\u4e00\u4e2a\u5c11\u5e74\u5c06\u519b\u9a91\u7740\u9ad8\u5934\u5927\u9a6c\u7f13\u7f13\u800c\u6765\uff0c\u559d\u9053\uff1a\u201c\u8c01\u5728 \u8fd9\u91cc\u95f9\u4e8b\uff1f\u201d\u90a3\u540d\u519b\u5b98\u6025\u5fd9\u4f4f\u624b\uff0c\u7980\u9053\uff1a\u201c\u662f\u8fd9\u4e2a\u5973\u5b69\u5b50\u95f9\u4e8b\u3002\u201d\u51af\u7433\u4e5f\u62a2\u7740\u9053\uff1a\u201c\u4f60\u662f\u5e26 \u5175\u7684\u5c06\u519b\u5417\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u7ba1\u90e8\u4e0b\uff1f\u5927\u4eba\u6b3a\u8d1f\u5b69\u5b50\uff01\u201d\u90a3\u5c11\u5e74\u5c06\u519b\u4e00\u770b\uff0c\u8fd9\u5973\u5b69\u5b50\u7b11\u9765\u751f\u6625\uff0c\u5341 \u5206\u53ef\u7231\uff0c\u800c\u90a3\u540d\u519b\u5b98\u5219\u9762\u9752\u5507\u80bf\uff0c\u519b\u88c5\u6ce5\u6c61\uff0c\u72fc\u72c8\u975e\u5e38\u3002\u770b\u6765\u660e\u660e\u662f\u519b\u5b98\u5403\u4e86\u5927\u4e8f\uff0c\u53c8\u662f\u60ca \u8bb6\uff0c\u53c8\u662f\u597d\u7b11\u3002\u95ee\u9053\uff1b\u201c\u4f60\u8fd9\u5b69\u5b50\u54ea\u91cc\u6765\u7684\uff1f\u4e3a\u4f55\u548c\u6211\u7684\u519b\u5b98\u6253\u67b6\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u8d70\u5f97\u597d \u597d\u7684\uff0c\u4ed6\u504f\u504f\u8981\u6765\u62e6\u6211\u3002\u201d\u4e2d\u519b\u8fc7\u6765\u7980\u9053\uff1a\u201c\u7980\u526f\u5e05\uff0c\u542c\u676d\u5dde\u7684\u6355\u5feb\u8bf4\uff0c\u8fd9\u5973\u5b69\u5b50\u662f\u56db\u7687\u5e9c \u7684\u4eba\u3002\u201d\u5c11\u5e74\u5c06\u519b\u4e5f\u5fae\u5fae\u53d8\u8272\uff0c\u9053\uff1a\u201c\u4f60\u8bf7\u5927\u5e05\u51fa\u6765\u3002\u201d \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u4f60\u7ba1\u6211\u662f\u54ea\u91cc\u7684\u4eba\uff0c\u6211\u4e0d\u5077\u4e0d\u62a2\u4e0d\u72af\u7687\u6cd5\uff0c\u5c31\u662f\u7687\u5e1d\u8001\u5b50\u4e5f\u4e0d\u80fd\u62e6\u6211\u3002\u201d\u5c11 \u5e74\u5c06\u519b\u7b11\u9053\uff1a\u201c\u4f60\u5012\u5634\u5201\u3002\u201d\u505c\u4e86\u4e00\u505c\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u7684\u6b66\u827a\u662f\u8ddf\u8c01\u5b66\u7684\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u504f\u4e0d \u544a\u8bc9\u4f60\u3002\u201d\u5c11\u5e74\u5c06\u519b\u7b11\u9053\uff1a\u201c\u4f60\u7ec3\u4e00\u8d9f\u62f3\u7ed9\u6211\u770b\u770b\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u662f\u6c5f\u6e56\u5356\u827a\u7684\u5973 \u4eba\uff0c\u4e3a\u4ec0\u4e48\u8981\u4f7f\u7ed9\u4f60\u770b\uff0c\u8981\u4e48\u4f60\u6211\u6bd4\u5212\u6bd4\u5212\u3002\u201d\u5c11\u5e74\u5c06\u519b\u4e00\u7b11\u4e0b\u9a6c\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u5c31\u548c\u4f60 \u6bd4\u5212\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u8d62\u4e86\u4f60\u4f60\u53ef\u5f97\u653e\u6211\u8d70\u3002\u201d\u5c11\u5e74\u5c06\u519b\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u53d1\u62db\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u652f\u519b\u961f\u6b63\u662f\u5e74\u7fb9\u5c27\u7387\u9886\uff0c\u8fd9\u5c11\u5e74\u5c06\u519b\u4e43\u662f\u4ed6\u7684\u526f\u5c06\u5cb3\u949f\u6dc7\u3002\u5e74\u7fb9\u5c27\u81ea\u8ddf\u4e86\u56db\u7687\u5b50 \u540e\uff0c\u53c8\u8bfb\u4e86\u56db\u5e74\u5175\u4e66\uff0c\u5230\u5341\u516b\u5c81\u90a3\u5e74\uff0c\u56db\u7687\u5b50\u624d\u4fdd\u4ed6\u51fa\u6765\u5e26\u5175\uff0c\u968f\u5927\u5c06\u5085\u6df1\u8fdc\u5f81\u51c6\u5676\u5c14\u90e8\u7acb \u4e86\u5927\u529f\uff0c\u56de\u6765\u540e\u5347\u4e3a\u603b\u5175\uff0c\u518d\u5347\u4e3a\u63d0\u7763\uff0c\u5148\u540e\u4e0d\u8fc7\u4e09\u5e74\uff0c\u4ece\u4e00\u5458\u4ffe\u5c06\u5347\u81f3\u4e00\u519b\u4e3b\u5e05\uff0c\u5347\u8fc1\u4e4b \u901f\uff0c\u5728\u6e05\u4ee3\u4e2d\u53ef\u7b97\u7b2c\u4e00\u4eba\u3002\u8fd9\u65f6\u5e74\u7fb9\u5c27\u624d\u4e0d\u8fc7\u4e8c\u5341\u4e00\u5c81\u3002\u5cb3\u949f\u6dc7\u636e\u8bf4\u662f\u5b8b\u540d\u5c06\u5cb3\u98de\u4e4b\u540e\uff0c\u4eca \u5e74\u4ea6\u4e0d\u8fc7\u4e8c\u5341\u4e8c\u5c81\uff0c\u4e5f\u662f\u56db\u7687\u5b50\u63d0\u62d4\u7684\u4eba\u3002\u4ed6\u548c\u5e74\u7fb9\u5c27\u4e00\u6837\uff0c\u7cbe\u901a\u6b66\u827a\uff0c\u719f\u8bfb\u5175\u6cd5\uff0c\u4e24\u4eba\u5e74 \u9f84\u76f8\u82e5\uff0c\u5fd7\u8da3\u76f8\u6295\uff0c\u5408\u4f5c\u6cbb\u519b\uff0c\u5341\u5206\u76f8\u5f97\u3002 \u3000\u3000\u5cb3\u949f\u6dc7\u8c41\u8fbe\u5927\u5ea6\uff0c\u5fa1\u4e0b\u751a\u5bbd\uff0c\u5e74\u7fb9\u5c27\u5219\u5bdf\u5bdf\u4e3a\u660e\uff0c\u6cbb\u519b\u6781\u4e25\u3002\u6240\u4ee5\u519b\u4e2d\u4e0b\u5c5e\uff0c\u5bf9\u5e74\u7fb9\u5c27 \u662f\u754f\u4e4b\u5982\u864e\uff0c\u5bf9\u5cb3\u949f\u6dc7\u53cd\u4e3a\u4eb2\u8fd1\u3002\u5cb3\u949f\u6dc7\u89c1\u51af\u7433\u8fd9\u6837\u4e00\u4e2a\u5341\u4e09\u56db\u5c81\u7684\u5973\u5b69\u5b50\u5c45\u7136\u80fd\u628a\u90a3\u540d\u519b \u5b98\u6253\u5012\uff0c\u53c8\u89c1\u5979\u5341\u5206\u53ef\u7231\uff0c\u4e00\u65f6\u5174\u8d77\uff0c\u60f3\u9017\u5979\u73a9\u73a9\uff0c\u7b54\u5e94\u548c\u5979\u6bd4\u8bd5\u3002 \u3000\u3000\u8fd9\u51af\u7433\u597d\u4e0d\u8c03\u76ae\uff0c\u5cb3\u949f\u6dc7\u4e00\u58f0\u201c\u597d\u5427\u3002\u201d\u521a\u521a\u8bf4\u51fa\uff0c\u5979\u5c0f\u817f\u4e00\u4f38\uff0c\u5df1\u7a81\u5411\u5cb3\u949f\u6dc7\u819d\u76d6\u8e22 \u6765\uff0c\u5cb3\u949f\u6dc7\u5f2f\u8170\u4e00\u63a5\uff0c\u51af\u7433\u62cd\u62cd\u4e24\u638c\uff0c\u65cb\u98ce\u822c\u7684\u75be\u6253\u8fc7\u6765\u3001\u5cb3\u949f\u6dc7\u9053\u58f0\u201c\u597d\uff01\u201d\u53cc\u638c\u659c\u76f4\u622a \u4e0b\uff0c\u5de6\u53f3\u4e00\u5206\uff0c\u7528\u5cb3\u5bb6\u6563\u624b\u4e2d\u7684\u201c\u6491\u693d\u624b\u201d\u53cd\u51fb\uff0c\u5cb3\u949f\u6dc7\u81c2\u529b\u6c89\u96c4\uff0c\u53ea\u56e0\u6015\u4f24\u4e86\u51af\u7433\uff0c\u4e0d\u6562 \u7528\u51fa\u5168\u529b\uff0c\u90a3\u77e5\u6b66\u5bb6\u8f83\u6280\uff0c\u6700\u6015\u6709\u6240\u987e\u5fcc\uff0c\u4ed6\u8fd9\u7a0d\u4e00\u8fdf\u7591\uff0c\u51af\u7433\u5df2\u5316\u638c\u4e3a\u62f3\uff0c\u4e00\u62db\u201c\u6d41\u661f\u8d76 \u6708\u201d\uff0c\u6253\u5230\u4ed6\u7684\u5c0f\u8179\u8110\u95e8\u8981\u7a74\uff01 \u3000\u3000\u5cb3\u949f\u6dc7\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u4e00\u4e2a\u7ffb\u8eab\uff0c\u53cc\u638c\u4e00\u8361\uff0c\u5316\u5f00\u5979\u7684\u653b\u52bf\u3002\u5fc3\u91cc\u6697\u9053\uff1a\u8fd9\u5973\u5b69\u5b50\u5c0f\u5c0f \u5e74\u7eaa\uff0c\u600e\u4e48\u624b\u811a\u5982\u6b64\u9634\u8fa3\uff0c\u4e0d\u89c9\u628a\u559c\u6b22\u5979\u7684\u5fc3\u60c5\u51cf\u4e86\u4e00\u534a\u3002\u51af\u7433\u624b\u811a\u5e76\u7528\uff0c\u8de8\u8fdb\u4e00\u6b65\uff0c\u53f3\u62f3 \u6536\u56de\u62a4\u8170\uff0c\u5de6\u624b\u53d8\u638c\uff0c\u5411\u5de6\u524d\u4e0b\u65b9\u62b9\u51fb\uff0c\u8fd9\u4e00\u62db\u4e43\u662f\u5c11\u6797\u62f3\u4e2d\u7684\u201c\u5c1a\u6863\u5207\u638c\u201d\u3002\u5cb3\u949f\u6dc7\u6a2a\u638c \u4e00\u622a\uff0c\u5979\u53cc\u811a\u53c8\u8fde\u73af\u75be\u8d77\uff0c\u5de6\u811a\u8e22\u5cb3\u949f\u6dc7\u819d\u76d6\u4e0a\u7684\u201c\u767d\u6d77\u7a74\u201d\uff0c\u53f3\u811a\u8e22\u819d\u76d6\u4e0b\u7684\u201c\u7b51\u5bbe \u7a74\u201d\uff0c\u8fd9\u5374\u662f\u5317\u6d3e\u201c\u592a\u7956\u62f3\u201d\u4e2d\u7684\u8fde\u73af\u817f\u6cd5\uff0c\u5cb3\u949f\u6dc7\u8fde\u8fd0\u5cb3\u5bb6\u6563\u624b\u4e2d\u7684\u201c\u5de6\u53f3\u5f00\u5f13\u201d\u3001\u201c\u6258 \u5929\u6362\u65e5\u201d\u51e0\u62db\uff0c\u624d\u582a\u582a\u628a\u5979\u6321\u5f00\u3002 \u3000\u3000\u51af\u7433\u8d8a\u6253\u8d8a\u72e0\uff0c\u62db\u6570\u53d8\u5316\u65e0\u5e38\uff0c\u5404\u5bb6\u6742\u9648\uff0c\u5ffd\u62f3\u5ffd\u638c\u3002\u5cb3\u949f\u6dc7\u6697\u6697\u79f0\u5947\uff0c\u771f\u60f3\u4e0d\u5230\u5979\u8fd9 \u4e48\u4e00\u70b9\u5e74\u7eaa\uff0c\u5982\u4f55\u5b66\u5f97\u8fd9\u4e48\u591a\u79bb\u5947\u53e4\u602a\u7684\u6b66\u529f\u3002\u51af\u7433\u4e2a\u5b50\u53c8\u5c0f\uff0c\u8fd0\u7528\u5404\u6d3e\u6b66\u529f\u65f6\uff0c\u4e13\u62e3\u653b\u654c \u4e0b\u76d8\u7684\u6765\u5e94\u7528\uff0c\u5cb3\u949f\u6dc7\u4e0d\u80fd\u4e0d\u5f2f\u8170\u5e94\u6218\uff0c\u5341\u5206\u5403\u529b\u3002 \u3000\u3000\u6253\u4e86\u7247\u523b\uff0c\u56db\u5468\u7684\u5175\u58eb\u7a81\u7136\u8083\u9759\u65e0\u58f0\uff0c\u5cb3\u949f\u6dc7\u77e5\u9053\u4e00\u5b9a\u662f\u5e74\u7fb9\u5c27\u5230\u4e86\u3002\u5fc3\u60f3\uff1a\u81ea\u5df1\u6253\u4e00 \u4e2a\u5973\u5b69\u5b50\u4e0d\u8fc7\uff0c\u5c82\u4e0d\u53eb\u4ed6\u89c1\u7b11\u3002\u9762\u4e0a\u53d1\u70e7\uff0c\u62f3\u98ce\u4e00\u7d27\uff0c\u4e0d\u518d\u9000\u8ba9\u3002\u5cb3\u949f\u6dc7\u4e43\u540d\u5c06\u4e4b\u540e\uff0c\u5cb3\u5bb6 \u6563\u624b\u7cbe\u5999\u65e0\u4f26\uff0c\u51af\u7433\u867d\u4f1a\u5404\u6d3e\u6b66\u529f\uff0c\u4ff1\u5230\u5e95\u662f\u5e74\u8f7b\u529b\u5f31\uff0c\u706b\u5019\u672a\u5230\uff0c\u5cb3\u949f\u6dc7\u8ba4\u771f\u51fa\u624b\uff0c\u51af\u7433 \u7acb\u523b\u8f6c\u5904\u4e0b\u98ce\u3002\u5cb3\u949f\u6dc7\u6b65\u4f10\u4f3c\u733f\u7334\uff0c\u51fa\u62f3\u5982\u864e\u8c79\uff0c\u5341\u62db\u4e00\u8fc7\uff0c\u51af\u7433\u6839\u672c\u6328\u4e0d\u8fdb\u8eab\u6765\u3002\u5cb3\u949f\u6dc7 \u7b11\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\u4f60\u670d\u8f93\u4e86\u5427\uff1f\u201d\u51af\u7433\u4e00\u58f0\u4e0d\u54cd\uff0c\u9000\u540e\u4e24\u6b65\uff0c\u7a81\u7136\u53cd\u624b\u4e00\u626c\uff0c\u4e24\u9053\u4e4c\u91d1\u5149\u8292\uff0c \u7535\u5c04\u800c\u51fa\u3002\u5cb3\u949f\u6dc7\u5413\u4e86\u4e00\u8df3\uff0c\u77e5\u9053\u8fd9\u6697\u5668\u5fc5\u5b9a\u6709\u6bd2\u3002\u4fa7\u8eab\u9a88\u6307\uff0c\u75be\u7684\u4e00\u5f39\uff0c\u5f39\u5728\u5200\u67c4\u4e4b\u4e0a\uff0c \u5c06\u7b2c\u4e00\u67c4\u98de\u5200\u5f39\u843d\u5730\u4e0a\u3002\u7b2c\u4e8c\u67c4\u6765\u5f97\u5feb\u6781\uff0c\u5cb3\u949f\u6dc7\u4e0d\u53ca\u5f39\u5b83\uff0c\u53c8\u4e0d\u6562\u624b\u63a5\uff0c\u53ea\u597d\u7f29\u80a9\u4e00\u95ea\uff0c \u90a3\u67c4\u98de\u5200\u545c\u7684\u4e00\u58f0\u98de\u8fc7\u5934\u9876\u3002\u53ea\u542c\u5f97\u5e74\u7fb9\u5c27\u5927\u559d\u4e00\u58f0\u201c\u4f4f\u624b\uff01\u201d\u5cb3\u949f\u6dc7\u655b\u624b\u8df3\u5f00\uff0c\u8fd9\u4e00\u559d\u597d \u50cf\u5177\u6709\u65e0\u9650\u5a01\u4e25\uff0c\u51af\u7433\u90a3\u6837\u8c03\u76ae\uff0c\u4e5f\u5413\u4e86\u4e00\u8df3\uff0c\u8d76\u5fd9\u7f29\u624b\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u624b\u4e2d\u62ff\u7740\u98de\u5200\uff0c\u53cd\u8986\u628a\u89c6\uff0c\u6c89\u541f\u4e0d\u8bed\u3002\u5cb3\u949f\u6dc7\u8d70\u8fc7\u6765\u9053\uff1a\u201c\u8fd9\u98de\u5200\u6709\u6bd2\uff0c\u5927\u5e05\u5343 \u91d1\u8d35\u4f53\uff0c\u4f55\u5fc5\u5192\u9669\u63a5\u5b83\u3002\u201d\u5e74\u7fb9\u5c27\u53ea\u9053\u4e86\u4e24\u4e2a\u5b57\u201c\u65e0\u59a8\u201d\u3002\u5cb3\u949f\u6dc7\u9053\uff1a\u201c\u8fd9\u5973\u5b69\u5b50\u597d\u602a\uff0c\u53ea \u6015\u771f\u662f\u56db\u7687\u5e9c\u7684\u4eba\u3002\u201d\u5e74\u7fb9\u5c27\u201c\u5514\u201d\u4e86\u4e00\u58f0\uff0c\u9762\u4e0a\u53d8\u8272\uff0c\u5e76\u4e0d\u7b54\u8bdd\u3002\u5cb3\u949f\u6dc7\u5927\u4e3a\u5947\u602a\u3002\u4ed6\u4e0e \u5e74\u7fb9\u5c27\u5171\u4e8b\u4e09\u5e74\uff0c\u4ece\u672a\u89c1\u4ed6\u6709\u8fc7\u5982\u6b64\u5f20\u60f6\u5931\u63aa\u7684\u795e\u8272\u3002\u5f80\u65f6\u5728\u5343\u519b\u4e07\u9a6c\u4e4b\u4e2d\uff0c\u67aa\u6797\u7bad\u96e8\u4e4b \u4e0b\uff0c\u5e74\u7fb9\u5c27\u90fd\u662f\u6307\u6325\u82e5\u5b9a\uff1b\u5cb3\u949f\u8fdb\u5fc3\u60f3\uff1a\u5c31\u7b97\u8fd9\u5973\u5b69\u5b50\u662f\u56db\u7687\u5e9c\u7684\u4eba\uff0c\u5927\u5e05\u4e5f\u4e0d\u5fc5\u6015\u5979\uff0c\u4f55 \u5fc5\u5982\u6b64\u6c89\u541f\u601d\u8003\u3002 \u3000\u3000\u5cb3\u949f\u6dc7\u6709\u6240\u4e0d\u77e5\uff0c\u5e74\u7fb9\u5c27\u4e0e\u51af\u7433\u4e00\u540c\u957f\u5927\uff0c\u5e74\u7fb9\u5c27\u6bd4\u5979\u5e74\u957f\u4e03\u5c81\uff0c\u5c0f\u65f6\u5e38\u5e38\u62b1\u5979\uff0c\u60c5\u5982 \u5144\u59b9\u3002\u81ea\u4ed6\u4eec\u7684\u5e08\u5085\u949f\u4e07\u5802\u4e2d\u8428\u5929\u523a\u6bd2\u722a\u6b7b\u540e\uff0c\u51af\u7433\u88ab\u53cc\u9b54\u62a2\u53bb\uff0c\u6df1\u5c45\u7687\u5e9c\u4e4b\u4e2d\uff0c\u81ea\u6b64\u4e8c\u4eba \u4fbf\u4e0d\u518d\u89c1\u3002\u5e74\u7fb9\u5c27\u53ea\u542c\u5f97\u4e86\u56e0\u8bf4\u8fc7\uff0c\u8bf4\u662f\u56db\u7687\u5b50\u4e5f\u751a\u559c\u6b22\u51af\u7433\uff0c\u53cc\u9b54\u628a\u5979\u62b1\u6765\u4e4b\u540e\uff0c\u56db\u7687\u5b50 \u5c31\u628a\u5927\u5185\u7684\u79d8\u836f\u6df7\u5165\u8336\u4e2d\uff0c\u53eb\u5979\u670d\u4e0b\u3002\u5403\u4e86\u8fd9\u79cd\u79d8\u836f\uff0c\u4ee5\u5f80\u7ecf\u5386\uff0c\u4f1a\u5168\u90fd\u5fd8\u6389\uff0c\u5bf9\u4e8e\u5b69\u5b50\uff0c \u5c24\u5176\u89c1\u6548\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u5f53\u65f6\u542c\u4e86\u4e5f\u5e76\u4e0d\u600e\u6837\u5728\u610f\uff0c\u5fc3\u60f3\uff1a\u8fd9\u5c0f\u4e2b\u5934\u9b3c\u7075\u7cbe\uff0c\u8ba9\u5979\u5fd8\u4e86\u5f80\u4e8b\u4e5f\u597d\u3002\u4e24\u4eba\u4e0d \u89c1\u970e\u5ffd\u4e03\u5e74\uff0c\u5e74\u7fb9\u5c27\u6e10\u6e10\u957f\u5927\u6210\u4eba\uff0c\u6700\u8fd1\u4e00\u4e24\u5e74\uff0c\u4e5f\u6709\u4eac\u4e2d\u6743\u8d35\u7ed9\u4ed6\u8bf4\u4eb2\u4e86\u3002\u4e0d\u77e5\u600e\u7684\uff0c\u6bcf \u5f53\u6709\u4eba\u8bf4\u4eb2\uff0c\u5e74\u7fb9\u5c27\u5c31\u4f1a\u60f3\u8d77\u51af\u7433\uff0c\u4e0d\u77e5\u5979\u957f\u5f97\u600e\u6837\u4e86\uff0c\u60f3\u5979\u5931\u4e86\u8bb0\u5fc6\uff0c\u89c1\u4e86\u6211\u6050\u6015\u4e5f\u4e0d\u8ba4 \u8bc6\u4e86\uff0c\u601d\u5ff5\u53ca\u6b64\uff0c\u6bcf\u89c9\u832b\u7136\u3002 \u3000\u3000\u521a\u624d\u5e74\u7fb9\u5c27\u542c\u5f97\u4e2d\u519b\u62a5\u544a\uff0c\u5c31\u6709\u8fd9\u4e48\u4e00\u4e2a\u5973\u5b69\u5b50\u5728\u5916\u9762\u95f9\u4e8b\uff0c\u5fc3\u4e2d\u5df2\u662f\u4e00\u52a8\u3002\u51fa\u6765\u519b\u524d \u4e00\u770b\uff0c\u89c1\u51af\u7433\u548c\u5cb3\u949f\u6dc7\u6253\u5f97\u96be\u5206\u96be\u89e3\uff0c\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u60f3\u8fd9\u5973\u5b69\u5b50\u591a\u534a\u4f1a\u662f\u5979\u4e86\u3002\u53ca\u81f3\u63a5\u4e86\u98de \u5200\uff0c\u66f4\u52a0\u8bc1\u5b9e\u3002\u8981\u77e5\u5e74\u7fb9\u5c27\u4e43\u949f\u4e07\u5802\u552f\u4e00\u4f20\u4eba\uff0c\u98de\u5200\u4e4b\u6280\u6bd4\u51af\u7433\u8fd8\u8981\u9ad8\u660e\uff0c\u81ea\u7136\u80fd\u63a5\u80fd\u6536\uff0c \u800c\u4e14\u4e00\u89c1\u98de\u5200\uff0c\u4fbf\u77e5\u6765\u5386\u3002 \u3000\u3000\u51af\u7433\u89c1\u4e86\u5e74\u7fb9\u5c27\uff0c\u5c0f\u5c0f\u5fc3\u7075\uff0c\u4e5f\u662f\u9661\u7136\u4e00\u9707\uff0c\u5fc3\u60f3\u600e\u4e48\u8fd9\u4eba\u597d\u50cf\u5728\u90a3\u513f\u89c1\u8fc7\u4f3c\u7684\uff1f\u5374\u600e \u6837\u4e5f\u60f3\u4e0d\u8d77\u6765\u3002\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\uff0c\u4f60\u968f\u6211\u6765\uff0c\u6211\u6709\u8bdd\u95ee\u4f60\u3002\u201d\u51af\u7433\u7728\u7728\u773c\u775b\uff0c\u9053\uff1a\u201c\u597d \u5427\uff0c\u4f60\u95ee\u3002\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u53eb\u53c2\u5c06\u62db\u547c\u5979\u4e0a\u81ea\u5df1\u7684\u8f66\u5b50\uff0c\u7a81\u7136\u5429\u5490\u5cb3\u949f\u6dc7\u9053\uff1a\u201c\u628a\u90a3\u4e9b\u676d\u5dde\u7684\u6355\u5feb\u5168\u6263\u8d77 \u6765\uff0c\u4f20\u4ee4\u519b\u4e2d\uff0c\u4e0d\u8bb8\u628a\u8fd9\u4e8b\u6cc4\u6f0f\u51fa\u53bb\u3002\u201d\u5cb3\u949f\u6dc7\u597d\u751f\u5947\u602a\uff0c\u4f46\u5e74\u7fb9\u5c27\u5c06\u4ee4\u5982\u5c71\uff0c\u53ea\u597d\u4f9d\u4ece\uff0c \u4e0d\u6562\u53d1\u95ee\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u8ddf\u4e0a\u8f66\u5b50\uff0c\u53eb\u51af\u7433\u5750\u5728\u81ea\u5df1\u8eab\u65c1\uff0c\u4ed4\u7ec6\u770b\u5979\uff0c\u89c1\u5979\u82f9\u679c\u8138\u513f\uff0c\u9152\u6da1\u9690\u73b0\u3002\u513f\u65f6\u9762 \u8c8c\uff0c\u4f9d\u7a00\u53ef\u8fa8\u3002\u95ee\u9053\uff1a\u201c\u4f60\u4ece\u5b9e\u8bf4\u6765\uff0c\u4f60\u662f\u56db\u7687\u5e9c\u7684\u4eba\u5417\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff1f\u201d\u5e74\u7fb9 \u5c27\u9053\uff1a\u201c\u5728\u56db\u7687\u5e9c\u91cc\u4f4f\u5f97\u597d\u597d\u7684\u4e3a\u4ec0\u4e48\u8981\u8dd1\u51fa\u6765\uff1f\u201d\u51af\u7433\u9762\u4e0a\u4e00\u7ea2\uff0c\u5ffd\u9053\uff1a\u201c\u4e0d\u544a\u8bc9\u4f60\u3002\u201d \u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u56db\u8d1d\u52d2\u547d\u5b9d\u56fd\u7985\u5e08\u6765\u8ffd\u4f60\u56de\u53bb\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u4e0d\u56de\u53bb\u3002\u201d\u5e74\u7fb9\u5c27 \u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u56de\u53bb\uff1f\u201d\u51af\u7433\u55d4\u9053\uff1a\u201c\u4e0d\u56de\u53bb\u5c31\u662f\u4e0d\u56de\u53bb\u561b\uff0c\u6709\u4ec0\u4e48\u597d\u95ee\u7684\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a \u201c\u56db\u8d1d\u52d2\u5bf9\u4f60\u4e0d\u662f\u633a\u597d\u5417\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u5514\uff0c\u597d\u2014\u2014\u201d\u5ffd\u7136\u773c\u5708\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u5c06 \u519b\uff0c\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u95ee\u6211\u3002\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u4e00\u6014\uff0c\u7ee7\u800c\u4e00\u51db\uff0c\u5fc3\u60f3\u8981\u662f\u5979\u56de\u53bb\u8bf4\u8d77\u6211\u5982\u6b64\u95ee\u5979\uff0c\u53ea\u6015\u5141\u796f\u4f1a\u8d77\u7591\u5fc3\uff0c\u4fbf\u9053\uff1a \u201c\u6211\u529d\u4f60\u8fd8\u662f\u56de\u53bb\u7684\u597d\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u54fc\uff0c\u539f\u6765\u4f60\u8fd9\u4eba\u5f88\u574f\u3002\u201d\u5e74\u7fb9\u5c27\u5947\u9053\uff1a\u201c\u6211\u600e\u4e48\u5f88 \u574f\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u5728\u5916\u9762\u5e26\u5175\u81ea\u7531\u81ea\u5728\uff0c\u5374\u8981\u529d\u6211\u56de\u5230\u5bab\u91cc\u53bb\u53d7\u6c14\u3002\u201d\u5e74\u7fb9\u5c27\u7b11\u9053\uff1a \u201c\u54e6\uff0c\u539f\u6765\u4f60\u8fd8\u662f\u90a3\u6837\u597d\u73a9\u3002\u201d\u51af\u7433\u7741\u5927\u773c\u775b\u8bf4\u9053\uff1a\u201c\u6211\u600e\u4e48\u77e5\u9053\u6211\u597d\u73a9\uff1f\u201d\u5e74\u7fb9\u5c27\u53c8\u662f\u4e00 \u6014\uff0c\u5f3a\u7b11\u9053\uff1a\u201c\u77a7\u4f60\u7684\u6837\u513f\uff0c\u5c31\u77e5\u4f60\u597d\u73a9\u561b\uff01\u4f60\u597d\u73a9\u4e5f\u4e0d\u8981\u7d27\uff0c\u53ea\u8981\u4f60\u4e0d\u662f\u79c1\u9003\uff0c\u4f60\u8981\u51fa\u7687 \u5e9c\u6e9c\u6e9c\uff0c\u56db\u8d1d\u52d2\u4e5f\u4e0d\u4f1a\u4e0d\u8bb8\uff0c\u504c\u5927\u4e00\u4e2a\u5317\u4eac\u8fd8\u4e0d\u591f\u4f60\u73a9\u7684\uff01\u201d\u51af\u7433\u773c\u5708\u53c8\u4e00\u7ea2\uff0c\u6012\u9053\uff1a\u201c\u4f60 \u8fd9\u4eba\u7684\u786e\u5f88\u574f\uff01\u201d\u5e74\u7fb9\u5c27\u76b1\u7709\u9053\uff1a\u201c\u600e\u4e48\u6211\u53c8\u574f\u4e86\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u603b\u60f3\u903c\u6211\u56de\u56db\u7687 \u5e9c\u53bb\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u529d\u4f60\u56de\u7687\u5e9c\u53bb\u4eab\u798f\u7adf\u662f\u574f\u4e48\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u90a3\u8fd8\u4e0d\u574f\uff1f\u6211\u6b7b\u4e5f\u4e0d\u613f\u56de \u53bb\uff01\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u5fc3\u4e2d\u4e00\u9707\uff0c\u51af\u7433\u8bf4\u5f97\u5982\u6b64\u575a\u51b3\uff0c\u60f3\u6765\u5176\u4e2d\u5b9a\u6709\u5185\u60c5\u3002\u4fbf\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u4e0d\u529d\u4f60\u4fbf \u662f\u3002\u4f60\u5728\u8fd9\u8f86\u8f66\u4e0a\u8eb2\u7740\uff0c\u53ef\u4e0d\u8bb8\u4e71\u52a8\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u597d\u5440\uff0c\u90a3\u4e48\u5b9d\u56fd\u7985\u5e08\u6765\u4e86\uff0c\u4f60\u53ef\u4e5f\u4e0d\u8bb8 \u8bf4\u7ed9\u4ed6\u77e5\u9053\u3002\u201d\u5e74\u7fb9\u5c27\u4e0d\u7b54\uff0c\u64ae\u5e18\u4e0b\u8f66\uff0c\u53eb\u9053\uff1a\u201c\u4e2d\u519b\u6765\uff01\u201d\u5429\u5490\u9053\uff1a\u201c\u628a\u90a3\u4e9b\u676d\u5dde\u6355\u5feb\u901a \u901a\u7ed9\u6211\u65a9\u4e86\uff01\u201d\u4e2d\u519b\u5403\u4e86\u4e00\u60ca\uff0c\u5e74\u7fb9\u5c27\u6325\u624b\u9053\uff1a\u201c\u5feb\u53bb\uff0c\u4f20\u4ee4\u519b\u4e2d\u4e0d\u8bb8\u6cc4\u6f0f\uff01\u201d\u5e74\u7fb9\u5c27\u6cbb \u519b\uff0c\u4e00\u4e0d\u5982\u610f\uff0c\u4fbf\u8981\u6740\u4eba\uff0c\u4e2d\u519b\u5df2\u662f\u53f8\u7a7a\u89c1\u60ef\u3002\u4f46\u60f3\u4e0d\u5230\u4ed6\u8fde\u676d\u5dde\u7684\u6355\u5feb\u4e5f\u6740\uff0c\u89c1\u5e74\u7fb9\u5c27\u9762 \u8272\u751a\u574f\uff0c\u4e0d\u6562\u4f5c\u58f0\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u4e03\u516b\u540d\u6355\u5feb\u5168\u90fd\u8eab\u9996\u5f02\u5904\u3002\u5cb3\u949f\u6dc7\u77e5\u9053\u4e4b\u540e\uff0c\u8981\u529d\u5df2\u6765\u4e0d \u53ca\u3002\u5e74\u7fb9\u5c27\u5429\u5490\u7acb\u523b\u5c06\u5c38\u706b\u5316\uff0c\u9aa8\u7070\u6492\u5165\u6cb3\u4e2d\u3002 \u3000\u3000\u6bc1\u5c38\u706d\u8ff9\u4e4b\u540e\uff0c\u5e74\u7fb9\u5c27\u4e0b\u4ee4\u62d4\u519b\u5f00\u5165\u676d\u5dde\u3002\u8d70\u4e86\u4e00\u9635\uff0c\u65d7\u724c\u5b98\u62a5\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u548c\u5c1a\uff0c\u63d0 \u7740\u4e00\u6839\u7897\u53e3\u5927\u7684\u7985\u6756\uff0c\u76f8\u8c8c\u51f6\u6076\uff0c\u81ea\u79f0\u5b9d\u56fd\u7985\u5e08\uff0c\u8bf4\u662f\u5927\u5e05\u670b\u53cb\uff0c\u8981\u6765\u6c42\u89c1\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a \u201c\u597d\uff0c\u6211\u4eb2\u81ea\u63a5\u4ed6\uff01\u201d \u3000\u3000\u4e86\u56e0\u64d2\u4e86\u674e\u6cbb\u4e4b\u540e\uff0c\u7528\u5206\u7b4b\u9519\u9aa8\u624b\u6cd5\uff0c\u626d\u4f24\u674e\u6cbb\u5173\u8282\uff0c\u4ee4\u4ed6\u5728\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u4e0d\u80fd\u6062 \u590d\uff0c\u7136\u540e\u5c06\u4ed6\u4ea4\u4e0e\u629a\u8859\u7684\u536b\u58eb\u738b\u594b\u548c\u97e9\u632f\u751f\u3002\u8fd9\u4e24\u4eba\u4e43\u662f\u5f53\u5e74\u8463\u5de8\u5ddd\u66ff\u674e\u536b\u4e3b\u8003\uff0c\u4ece\u6570\u767e\u4eba \u4e2d\u9009\u62d4\u51fa\u6765\u7684\u536b\u58eb\uff0c\u738b\u594b\u7684\u94c1\u7802\u638c\u529f\u592b\uff0c\u9020\u8be3\u9887\u6df1\uff0c\u97e9\u632f\u751f\u7684\u4e0b\u76d8\u817f\u52b2\uff0c\u4e5f\u5f88\u6709\u65a4\u4e24\uff0c\u674e\u6cbb \u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u5728\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u5185\u5f62\u540c\u5e9f\u4eba\uff0c\u6709\u8fd9\u4e24\u4eba\u770b\u5b88\uff0c\u8c05\u4ed6\u4e0d\u80fd\u9003\u8d70\u3002\u4e86\u56e0\u53eb\u738b\u97e9\u4e8c\u4eba \u628a\u674e\u6cbb\u5148\u5e26\u56de\u629a\u8859\uff0c\u8de8\u4e0a\u9a8f\u9a6c\uff0c\u7eed\u5411\u524d\u8ffd\u3002 \u3000\u3000\u8d70\u4e86\u5341\u4f59\u91cc\u5149\u666f\uff0c\u7530\u91ce\u95f4\u6eda\u51fa\u4e24\u540d\u6355\u5feb\uff0c\u9ad8\u547c\u5b9d\u56fd\u7985\u5e08\uff0c\u4e86\u56e0\u89c1\u4ed6\u4eec\u8dcc\u5f97\u9762\u9752\u5507\u80bf\uff0c\u559d \u95ee\u4f55\u4e8b\u3002\u4e24\u540d\u6355\u5feb\u5c06\u51af\u7433\u98de\u5200\u5c04\u9a6c\u4e4b\u4e8b\u8bf4\u4e86\uff0c\u4e86\u56e0\u5927\u6012\u9a82\u9053\uff1a\u201c\u8fd9\u91ce\u4e2b\u5934\uff0c\u7b80\u76f4\u53cd\u4e86\uff01\u201d\u7ee7 \u800c\u4e00\u60f3\uff0c\u5979\u53ea\u662f\u5c04\u9a6c\u4e0d\u6562\u5c04\u4eba\uff0c\u770b\u6765\u8fd8\u4e0d\u6562\u516c\u7136\u80cc\u53db\u3002 \u3000\u3000\u4e86\u56e0\u6325\u624b\u53eb\u8fd9\u4e24\u540d\u6355\u5feb\u5148\u56de\u629a\u8859\uff0c\u7eed\u5411\u524d\u8ffd\uff0c\u4e0d\u8fc7\u4e8c\u4e09\u91cc\u5149\u666f\uff0c\u53c8\u662f\u4e24\u540d\u6355\u5feb\u62e6\u8def\u6295 \u8bc9\uff0c\u4e86\u56e0\u76ca\u53d1\u6c14\u6124\u3002\u5982\u662f\u8005\u6bcf\u8d70\u51e0\u91cc\u5c31\u78b0\u5230\u4e24\u540d\u8dcc\u4f24\u7684\u6355\u5feb\uff0c\u4e00\u5171\u78b0\u5230\u4e86\u516b\u540d\u4e4b\u591a\u3002\u4e86\u56e0\u4e00 \u7b97\u8ffd\u8d76\u51af\u7433\u7684\u6355\u5feb\uff0c\u5df2\u4f24\u4e86\u4e00\u534a\uff0c\u5fc3\u9053\uff1a\u201c\u5979\u90a3\u6bd2\u5200\u4e00\u5171\u6709\u5341\u4e8c\u628a\uff0c\u4f24\u4e86\u516b\u9a91\u5feb\u9a6c\u7528\u4e86\u516b \u628a\uff0c\u88ab\u6211\u6253\u843d\u4e24\u628a\uff0c\u90a3\u4e48\u5979\u8eab\u4e0a\u6700\u591a\u8fd8\u6709\u4e24\u628a\uff0c\u6700\u591a\u8fd8\u80fd\u518d\u4f24\u4e24\u5339\u9a6c\u513f\uff0c\u4e14\u770b\u90a3\u672a\u53d7\u4f24\u7684\u6355 \u5feb\u600e\u6837\u3002 \u3000\u3000\u4e86\u56e0\u62cd\u9a6c\u8ffd\u51fa\u4e94\u516d\u91cc\u8def\uff0c\u518d\u4e0d\u89c1\u6709\u53d7\u4f24\u7684\u6355\u5feb\uff0c\u6697\u6697\u5947\u602a\u3002\u6479\u7136\u95f4\u5fae\u98ce\u98d2\u7136\uff0c\u5750\u9a91\u5ffd\u5730 \u957f\u5636\uff0c\u6492\u817f\u98de\u5954\u3002\u4e86\u56e0\u5927\u6012\uff0c\u53cc\u811a\u4e00\u5939\uff0c\u90a3\u5339\u5065\u9a6c\u54c0\u9e23\u4e00\u58f0\uff0c\u56db\u819d\u8dea\u4e0b\u3002\u4e86\u56e0\u5403\u4e86\u4e00\u60ca\uff0c\u4e0b \u9a6c\u559d\u9053\uff1a\u201c\u8c01\u6562\u6697\u7b97\uff1f\u201d\u5ffd\u542c\u5f97\u94c3\u58f0\u53ee\u53ee\uff0c\u4e00\u4e2a\u6c5f\u6e56\u90ce\u4e2d\u6311\u7740\u836f\u56ca\uff0c\u6447\u7740\u94dc\u94c3\uff0c\u4ece\u8def\u65c1\u7684\u5c71 \u5761\u8d70\u4e0b\u3002\u5531\u9053\uff1a\u201c\u795e\u533b\u8d5b\u534e\u9640\uff0c\u7cbe\u6653\u795d\u7531\u79d1\uff0c\u4e0d\u8bba\u4eba\u548c\u9a6c\uff0c\u53d7\u4f24\u53ef\u95ee\u6211\uff01\u201d\u8fb9\u5531\u8fb9\u6447\uff0c\u94c3\u94c3 \u4e4b\u58f0\uff0c\u54cd\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u4e86\u56e0\u5fc3\u5ff5\u4e00\u52a8\uff0c\u770b\u81ea\u5df1\u90a3\u5339\u9a6c\u65f6\uff0c\u53ea\u89c1\u5b83\u56db\u8e44\u671d\u5929\uff0c\u5df2\u662f\u5012\u6bd9\u8def\u4e0a\u3002 \u3000\u3000\u4e86\u56e0\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u81ea\u8d1f\u6b66\u529f\u7edd\u4e16\u65e0\u53cc\uff0c\u5750\u9a91\u88ab\u4eba\u6697\u7b97\uff0c\u5c45\u7136\u8fd8\u4e0d\u77e5\u9053\u522b\u4eba\u662f\u7528\u4ec0\u4e48\u624b \u6cd5\u3002\u53ea\u89c1\u90a3\u8d70\u65b9\u90ce\u4e2d\u53c8\u5531\u9053\uff1a\u201c\u5c04\u9a6c\u4e0d\u5c04\u4eba\uff0c\u8fd8\u56fe\u534a\u70b9\u60c5\uff1b\u6b32\u533b\u5b9c\u8d81\u65e9\uff0c\u83ab\u8fc7\u5348\u65f6\u8fb0\u3002\u201d\u4e86 \u56e0\u9762\u8272\u4e00\u53d8\uff0c\u6697\u8fd0\u5185\u529f\u62a4\u4e86\u5168\u8eab\uff0c\u62db\u624b\u9053\uff1a\u201c\u597d\uff0c\u6765\u7ed9\u6211\u533b\uff01\u201d\u8d70\u65b9\u90ce\u4e2d\u53d6\u4e0b\u836f\u56ca\uff0c\u63d0\u7740 \u201c\u864e\u6491\u2019\uff08\u6c5f\u6e56\u90ce\u4e2d\u6311\u4e1c\u897f\u7528\u7684\u5668\u5177\uff0c\u53c8\u53ef\u7528\u4f5c\u9632\u8eab\u5175\u5668\uff09\uff0c\u4e00\u6b65\u4e00\u6b65\u7684\u8d70\u4e86\u8fc7\u6765\uff0c\u4e86\u56e0\u731b \u7136\u5927\u559d\u4e00\u58f0\uff0c\u7985\u6756\u62a1\u5706\uff0c\u547c\u7684\u4e00\u6756\uff0c\u5f53\u5934\u5288\u4e0b\u3002 \u3000\u3000\u90a3\u8d70\u65b9\u90ce\u4e2d\u51b7\u7b11\u9053\uff1a\u201c\u51fa\u5bb6\u4eba\u8fd9\u6837\u6a2a\u86ee\uff0c\u8fd8\u8bf4\u4ec0\u4e48\u7688\u4f9d\u4e09\u7b7e\uff1f\u201d\u8bf4\u65f6\uff0c\u4e86\u56e0\u7684\u7985\u6756\u5df2\u5f53 \u5934\u6253\u5230\uff0c\u60f3\u662f\u89c1\u4ed6\u6beb\u65e0\u62b5\u6297\uff0c\u60f3\u7559\u6d3b\u53e3\uff0c\u7565\u7565\u95ea\u5f00\u5934\u9876\uff0c\u5954\u80a9\u5934\u626b\u4e0b\u3002\u90a3\u8d70\u65b9\u90ce\u4e2d\u7eb9\u4e1d\u4e0d \u52a8\uff0c\u76f4\u5230\u7985\u6756\u8ddd\u79bb\u80a9\u5934\u4e0d\u5230\u4e00\u5c3a\uff0c\u624d\u731b\u7136\u4fa7\u8eab\uff0c\u4e3e\u8d77\u624b\u4e2d\u864e\u6491\u5f80\u4e0a\u4e00\u6321\uff0c\u965b\u7136\u5de8\u54cd\uff0c\u706b\u82b1\u84ec \u98de\u3002\u4e86\u56e0\u53ea\u89c9\u624b\u8155\u9178\u9ebb\uff0c\u7985\u6756\u51e0\u4e4e\u8131\u624b\u3002\u90a3\u8d70\u65b9\u90ce\u4e2d\u4e5f\u6447\u6447\u6643\u6643\u9000\u4e86\u51e0\u6b65\uff0c\u8fde\u9053\uff1a\u201c\u53ef\u60dc\uff01\u201d \u3000\u3000\u4e86\u56e0\u53c8\u60ca\u53c8\u6012\uff0c\u8fd9\u4eba\u5185\u529f\u4e4b\u6df1\uff0c\u5c45\u7136\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\u3002\u7985\u6756\u4e00\u6446\uff0c\u4e00\u62db\u201c\u6a2a\u626b\u5343\u519b\u201d\uff0c\u7167 \u51c6\u654c\u4eba\u8170\u808b\u518d\u626b\uff0c\u8d70\u65b9\u90ce\u4e2d\u4e3e\u8d77\u864e\u6491\u6a2a\u67b6\uff0c\u628a\u7985\u6756\u518d\u8361\u5f00\u53bb\u3002\u4e86\u56e0\u9519\u6b65\u62a2\u8fdb\uff0c\u7985\u6756\u75be\u7684\u4e00 \u70b9\uff0c\u8fd9\u4e00\u62db\u540d\u4e3a\u201c\u9752\u9f99\u51fa\u6d77\u201d\uff0c\u662f\u4e86\u56e0\u6740\u624b\u4e4b\u4e00\uff0c\u90a3\u8d70\u65b9\u90ce\u4e2d\u7684\u864e\u6491\u5df2\u5c01\u51fa\u5916\u95e8\uff0c\u56de\u6551\u4e0d \u53ca\uff0c\u4e86\u56e0\u7528\u4e86\u5168\u529b\uff0c\u610f\u6599\u5fc5\u4e2d\uff0c\u90a3\u77e5\u8d70\u65b9\u90ce\u4e2d\u5fae\u4e00\u4fa7\u8eab\uff0c\u7528\u864e\u6491\u7684\u62a4\u624b\u94dc\u67c4\u8f7b\u8f7b\u4e00\u5e26\uff0c\u4e86\u56e0 \u7684\u7985\u6756\u7adf\u7136\u4e5f\u7ed9\u6491\u51fa\u5916\u95e8\uff0c\u6025\u5fd9\u5f80\u524d\u4e00\u70b9\uff0c\u89e3\u4e86\u4ed6\u7684\u9634\u52b2\uff0c\u6258\u5730\u8df3\u51fa\u4e08\u5916\u3002\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60 \u662f\u4f55\u4eba\uff0c\u62a5\u4e0a\u540d\u6765\uff0c\u4f5b\u7237\u7985\u6756\u4e0d\u6253\u65e0\u540d\u5c0f\u5352\uff01\u201d \u3000\u3000\u8d70\u65b9\u90ce\u4e2d\u9634\u607b\u607b\u7b11\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u6211\u65e2\u975e\u738b\u7237\uff0c\u53c8\u975e\u5bcc\u6237\uff0c\u4f60\u600e\u5411\u6211\u5316\u7f18\uff0c\u51c6\u4f1a\u5931\u671b\uff01 \u4e86\u56e0\u6012\u9053\uff1a\u201c\u8c01\u8ddf\u4f60\u5316\u7f18\uff01\u201d\u7985\u6756\u4e00\u5c55\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u548c\u8d70\u65b9\u90ce\u4e2d\u5927\u6218\u8d77\u6765\uff01 \u3000\u3000\u4e86\u56e0\u53d1\u529b\u4f7f\u6756\uff0c\u7aef\u7684\u975e\u540c\u5c0f\u53ef\uff0c\u6756\u5f71\u5982\u5c71\uff0c\u547c\u547c\u8f70\u8f70\uff0c\u6d3b\u4f3c\u4e00\u6761\u602a\u9f99\uff0c\u5f20\u7259\u821e\u722a\u3002\u90a3\u77e5 \u8fd9\u8d70\u65b9\u90ce\u4e2d\u7684\u864e\u6491\u65bd\u5c55\u5f00\u6765\u7adf\u7136\u4e5f\u662f\u98ce\u58f0\u547c\u547c\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u4e24\u4eba\u5404\u4e0d\u76f8\u8ba9\uff0c\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c \u96be\u5206\u9ad8\u4e0b\u3002\u7985\u6756\u4e0e\u864e\u6491\u6bcf\u4e00\u76f8\u78b0\uff0c\u4fbf\u53d1\u51fa\u4e00\u6e9c\u706b\u82b1\uff0c\u4e24\u4eba\u90fd\u662f\u864e\u53e3\u53d1\u70ed\uff0c\u7528\u4e86\u5168\u529b\u63e1\u7740\u5175 \u5668\uff0c\u8fd9\u624d\u4e0d\u88ab\u5bf9\u65b9\u9707\u98de\u3002 \u3000\u3000\u4e86\u56e0\u6697\u81ea\u5403\u60ca\uff0c\u5fc3\u4e2d\u6697\u6570\u5f53\u4e16\u9ad8\u4eba\uff0c\u80fd\u591f\u548c\u81ea\u5df1\u6253\u6210\u5e73\u624b\u7684\u5df2\u662f\u6709\u9650\uff0c\u90a3\u91cc\u8df3\u6765\u8fd9\u6837\u4e00 \u4e2a\u6c5f\u6e56\u90ce\u4e2d\uff0c\u5c45\u7136\u597d\u50cf\u8fd8\u5728\u81ea\u5df1\u4e4b\u4e0a\uff1f \u3000\u3000\u518d\u6597\u7247\u523b\uff0c\u90a3\u8d70\u65b9\u90ce\u4e2d\u62db\u6570\u4e00\u53d8\uff0c\u5de6\u624b\u638f\u51fa\u94dc\u94c3\uff0c\u4e86\u56e0\u4e00\u6756\u5377\u5730\u626b\u53bb\uff0c\u8d70\u65b9\u90ce\u4e2d\u7eb5\u8eab\u4e00 \u8dc3\uff0c\u94dc\u94c3\u7a81\u7136\u53ee\u53ee\u5f53\u5f53\u5728\u4e86\u56e0\u8033\u8fb9\u54cd\u4e86\u8d77\u6765\uff0c\u4e86\u56e0\u7985\u6756\u4e00\u6302\uff0c\u628a\u4ed6\u7684\u864e\u6491\u8361\u5f00\uff0c\u6012\u9053\uff1a\u201c\u4f60 \u6562\u620f\u800d\u4f5b\u7237\uff01\u201d\u7985\u6756\u4e00\u6405\uff0c\u767b\u65f6\u56db\u9762\u516b\u65b9\u90fd\u662f\u4e86\u56e0\u5f71\u5b50\uff0c\u4e00\u6839\u7985\u6756\u7adf\u7136\u4f3c\u5316\u4e86\u5343\u767e\u6761\u6756\u5f71\uff0c \u628a\u8d70\u65b9\u90ce\u4e2d\u56f4\u5f97\u4e2a\u98ce\u96e8\u4e0d\u900f\uff0c\u8fd9\u662f\u4e86\u56e0\u7cbe\u7814\u72ec\u521b\u7684\u201c\u5929\u9b54\u6756\u6cd5\u201d\uff0c\u4e0d\u662f\u9047\u7740\u6700\u5f3a\u7684\u654c\u4eba\u4e0d\u80af \u8f7b\u7528\uff01 \u3000\u3000\u90a3\u6c5f\u6e56\u90ce\u4e2d\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u591a\u5c11\u5bb6\u5f53\uff0c\u4e00\u5e76\u62ff\u51fa\u6765\u5427\u3002\u201d\u53e3\u91cc\u8bf4\u7b11\uff0c\u624b\u4e2d\u5374\u662f\u6beb\u4e0d\u7f13 \u6162\uff0c\u4e00\u67c4\u864e\u6491\uff0c\u524d\u906e\u540e\u76d6\uff0c\u6a2a\u6311\u76f4\u6321\uff0c\u628a\u95e8\u6237\u5c01\u95ed\u5f97\u5341\u5206\u4e25\u5bc6\u3002\u5728\u6756\u98ce\u547c\u547c\u4e4b\u4e2d\uff0c\u94dc\u94c3\u4ecd\u662f \u53ee\u53ee\u5f53\u5f53\u7684\u54cd\u4e2a\u4e0d\u505c\uff1b\u4e86\u56e0\u5c55\u5f00\u6700\u51f6\u72e0\u7684\u201c\u5929\u9b54\u6756\u6cd5\u201d\uff0c\u4ecd\u662f\u672a\u80fd\u5f97\u624b\uff0c\u90a3\u94c3\u58f0\u8d8a\u54cd\u8d8a\u5bc6\uff0c \u6c5f\u6e56\u90ce\u4e2d\u88c5\u6a21\u4f5c\u6837\uff0c\u8bb7\u8bb7\u5ff5\u5492\uff0c\u5c31\u50cf\u7ed9\u4eba\u4f5c\u6055\u62db\u9b42\u4e00\u822c\uff0c\u4e86\u56e0\u7ed9\u4ed6\u6447\u5f97\u5fc3\u70e6\u610f\u4e71\uff0c\u5929\u9b54\u6756\u6cd5 \u6e10\u6e10\u758f\u6563\uff0c\u6c5f\u6e56\u90ce\u4e2d\u4e58\u52bf\u53cd\u653b\uff0c\u53cd\u5bbe\u4e3a\u4e3b\u3002\u6b63\u6218\u5f97\u5403\u7d27\u4e4b\u9645\uff0c\u90a3\u6c5f\u6e56\u90ce\u4e2d\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u5927\u519b \u6765\u4e86\uff0c\u5c11\u966a\u5c11\u966a\uff01\u5230\u4f60\u8981\u5f80\u897f\u5929\u65f6\uff0c\u6211\u518d\u6765\u7ed9\u4f60\u62db\u9b42\uff01\u201d\u864e\u6491\u4e00\u6536\uff0c\u98de\u8eab\u4fbf\u8d77\uff0c\u4e86\u56e0\u6012\u9053\uff1a \u201c\u90a3\u91cc\u8d70\uff1f\u201d\u4e00\u6756\u626b\u53bb\uff0c\u6c5f\u6e56\u90ce\u4e2d\u5de6\u624b\u4e00\u6447\uff0c\u957f\u8896\u98d8\u8d77\uff0c\u53cd\u8eab\u4e00\u62cd\uff0c\u4e86\u56e0\u4f46\u89c9\u773c\u795e\u4e00\u4e71\uff0c\u6025 \u5fd9\u64a4\u6756\u62a4\u8eab\uff0c\u5f85\u5f97\u518d\u7741\u5f00\u773c\u65f6\uff0c\u90a3\u8d70\u65b9\u90ce\u4e2d\u5df2\u8df3\u4e0a\u5c71\u5761\uff0c\u53bb\u5f97\u8fdc\u4e86\uff01 \u3000\u3000\u4e86\u56e0\u5b9a\u4e86\u5b9a\u795e\uff0c\u5fc3\u60f3\u80fd\u4ee5\u8863\u8896\u4f5c\u4e3a\u5175\u5203\u7684\uff0c\u53ea\u6709\u65e0\u6781\u6d3e\u5085\u9752\u4e3b\u4f20\u4e0b\u7684\u201c\u6d41\u4e91\u98de\u8896\u201d\u529f \u592b\uff0c\u8fd9\u6c5f\u6e56\u90ce\u4e2d\u90a3\u62db\u83ab\u975e\u5c31\u662f\u8fd9\u79cd\u7f55\u89c1\u7684\u79d8\u7b08\uff1f\u90a3\u4e48\u4ed6\u8be5\u662f\u5085\u9752\u4e3b\u8fd9\u4e00\u6d3e\u7684\u4f20\u4eba\u4e86\uff1f\u4f46\u65e0\u6781 \u6d3e\u7684\u4f20\u4eba\u660e\u660e\u53ea\u6709\u4e00\u4e2a\u949f\u4e07\u5802\uff0c\u800c\u4e14\u8fd9\u4eba\u7684\u8eab\u6cd5\u4e5f\u4e0d\u662f\u65e0\u6781\u6d3e\u7684\u3002\u600e\u4e48\u4ed6\u5374\u80fd\u4f7f\u51fa\u8fd9\u201c\u6d41\u4e91 \u98de\u8896\u201d\u7684\u7edd\u62db\uff1f \u3000\u3000\u4e86\u56e0\u7985\u6756\u70b9\u5730\uff0c\u832b\u7136\u82e5\u5931\uff0c\u8fd9\u8fd8\u662f\u4ed6\u51fa\u9053\u4ee5\u6765\uff0c\u9664\u4e86\u5bf9\u6613\u5170\u73e0\u4e4b\u5916\uff0c\u7b2c\u4e00\u6b21\u5403\u7684\u8d25\u4ed7\u3002 \u6613\u5170\u73e0\u548c\u4ed6\u5e08\u5085\u540c\u8f88\uff0c\u5403\u8d25\u4ed7\u72b9\u81ea\u53ef\u8bf4\uff0c\u8fd9\u8d70\u65b9\u90ce\u4e2d\u4e0d\u89c1\u7ecf\u4f20\uff0c\u8fd9\u632b\u6298\u53ef\u53d7\u4e0d\u4e86\u3002 \u3000\u3000\u4e86\u56e0\u6b63\u81ea\u601d\u91cf\uff0c\u524d\u9762\u5c18\u5934\u5927\u8d77\uff0c\u4e00\u652f\u519b\u961f\u8fce\u9762\u5f00\u6765\u3002\u4e86\u56e0\u6697\u60f3\uff1a\u90a3\u90ce\u4e2d\u7684\u8033\u6735\u5012\u771f\u7075 \u654f\uff0c\u5728\u75be\u6218\u4e2d\u5c45\u7136\u80fd\u5206\u51fa\u5fc3\u795e\u542c\u51fa\u5c71\u62d7\u90a3\u8fb9\u8def\u4e0a\u7684\u884c\u519b\u4e4b\u58f0\u3002\u4ef0\u5934\u89c1\u5199\u7740\u201c\u5e74\u201d\u5b57\u7684\u5e05\u65d7\u8fce \u98ce\u62db\u5c55\uff0c\u4e0d\u7981\u5927\u559c\u3002\u5fc3\u60f3\uff1a\u5e74\u7fb9\u5c27\u8fd9\u5b69\u5b50\u5c45\u7136\u56de\u5f97\u8fd9\u6837\u5feb\uff0c\u90a3\u5c0f\u4e2b\u5934\u5b9a\u88ab\u4ed6\u515c\u622a\u4e86\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u628a\u51af\u7433\u85cf\u597d\u4e4b\u540e\uff0c\u7b56\u9a6c\u51fa\u8fce\uff0c\u5230\u4e86\u519b\u524d\uff0c\u4e0b\u9a6c\u62f1\u624b\uff0c\u4ffe\u5b98\u5c0f\u5352\uff0c\u8083\u7acb\u65e0\u58f0\u3002\u4e86\u56e0 \u5927\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u771f\u6709\u4f60\u7684\uff0c\u771f\u50cf\u620f\u53f0\u4e0a\u7684\u5927\u5c06\u519b\u3002\u201d\u5e74\u7fb9\u5c27\u9762\u8272\u4e0d\u8c6b\uff0c\u4f46\u8fc5\u5373\u5fcd\u4f4f\uff0c\u542b\u7b11 \u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\uff0c\u5c0f\u5c06\u888d\u7532\u5728\u8eab\uff0c\u4e0d\u80fd\u5168\u793c\u3002\u8bf7\u7985\u5e08\u6362\u9a6c\uff0c\u54b1\u4eec\u4e14\u5e76\u8f94\u4e00\u8c08\u3002\u201d\u4e2d\u519b\u7275\u6765\u4e86 \u4e00\u9a91\u8499\u53e4\u5065\u9a6c\uff0c\u4e86\u56e0\u8de8\u4e0a\u9a6c\u80cc\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u4e00\u505a\u4e86\u5c06\u519b\uff0c\u5206\u5916\u591a\u793c\uff0c\u6211\u8fd9\u8001\u7c97\uff0c\u53ef \u4e0d\u61c2\u5ba2\u6c14\uff0c\u5582\uff0c\u4f60\u770b\u89c1\u90a3\u5c0f\u4e2b\u5934\u5417\uff1f\u201d \u3000\u3000\u4e86\u56e0\u4ee5\u5e74\u7fb9\u5c27\u7684\u957f\u8f88\u81ea\u5c45\uff0c\u8001\u6c14\u6a2a\u79cb\uff0c\u5e74\u7fb9\u5c27\u9881\u4e3a\u4e0d\u5feb\uff0c\u4f46\u4ed6\u57ce\u5e9c\u751a\u6df1\uff0c\u4ee5\u4e86\u56e0\u662f\u56db\u7687 \u5b50\u8ddf\u524d\u5f97\u529b\u4e4b\u4eba\uff0c\u6240\u4ee5\u4e00\u5411\u5bf9\u4ed6\u751a\u4e3a\u606d\u656c\u3002\u5f53\u4e0b\u4f6f\u4f5c\u4e0d\u77e5\uff0c\u95ee\u9053\uff1a\u201c\u54ea\u4e2a\u5c0f\u4e2b\u5934\u5440\uff1f\u201d\u4e86\u56e0 \u8be7\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u770b\u89c1\u5417\uff1f\u8fd8\u6709\u54ea\u4e2a\u5c0f\u4e2b\u5934\uff1f\u5f53\u7136\u662f\u6307\u90a3\u4e2a\u548c\u4f60\u4e00\u540c\u957f\u5927\u7684\u91ce\u4e2b\u5934\u3002\u201d\u5e74\u7fb9\u5c27 \u9053\uff1a\u201c\u5979\u4e0d\u662f\u5728\u56db\u7687\u5b50\u5e9c\u4e2d\u4f4f\u5f97\u597d\u597d\u7684\u4e48\uff1f\u201d\u4e86\u56e0\u9053\uff1a\u201c\u54e6\uff0c\u90a3\u4f60\u771f\u662f\u672a\u89c1\u7740\u5979\u4e86\u3002\u90a3\u4e48\u90a3 \u4e9b\u676d\u5dde\u6355\u5757\u5462\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4ec0\u4e48\u676d\u5dde\u6355\u5feb\uff0c\u5b9d\u56fd\u7985\u5e08\uff0c\u4f60\u522b\u5c3d\u7ed9\u54d1\u8c1c\u6211\u731c\u4e86\uff0c\u6211\u521a\u4ece\u798f \u5efa\u88ad\u532a\u56de\u6765\uff0c\u8fd9\u91cc\u7684\u4e8b\u60c5\uff0c\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u4e86\u56e0\u9053\uff1a\u201c\u4f60\u771f\u662f\u80fd\u8005\u591a\u52b3\uff0c\u521a\u4ece\u9752\u6d77\u56de\u6765\uff0c\u53c8\u5230\u798f\u5efa\u88ad\u532a\uff0c\u73b0\u5728\u60f3\u662f\u5949\u4e86\u56db\u7687\u5b50\u5bc6 \u4ee4\uff0c\u53c8\u8981\u8d76\u7740\u56de\u4eac\u4e86\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6b63\u662f\u3002\u6211\u8def\u8fc7\u676d\u5dde\uff0c\u8fd8\u8981\u987a\u4fbf\u628a\u4e00\u6279\u94a6\u72af\u5e26\u53bb\u3002\u201d\u4e86 \u56e0\u9053\uff1a\u201c\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u6253\u4ed7\u5012\u6709\u4e24\u624b\uff0c\u602a\u4e0d\u5f97\u56db\u8d1d\u52d2\u770b\u91cd\u4e8e\u4f60\u3002\u201d\u5e74\u7fb9\u5c27\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u54ea\u53ca\u5f97\u5b9d\u56fd\u7985\u5e08\u6b66\u529f\u76d6\u4e16\u65e0\u53cc\u3002\u201d\u4e86\u56e0\u5e73\u65f6\u6700\u559c\u522b\u4eba\u6367\u4ed6\u6b66\u529f\u7b2c\u4e00\uff0c\u8fd9\u65f6\u65b0\u8d25\u4e4b\u540e\uff0c\u542c\u4e86 \u5374\u53cd\u5c34\u5c2c\uff0c\u642d\u8baa\u95ee\u9053\uff1a\u201c\u56db\u7687\u5b50\u65e2\u6709\u5bc6\u4ee4\u53eb\u4f60\u56de\u4eac\uff0c\u96be\u9053\u6ca1\u6709\u5411\u4f60\u63d0\u8d77\u90a3\u91ce\u4e2b\u5934\u4e4b\u4e8b\u5417\uff1f\u201d \u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6ca1\u6709\u5440\uff01\u201d\u4e86\u56e0\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u770b\u6765\u56db\u8d1d\u52d2\u5f88\u559c\u6b22\u8fd9\u4e2a\u4e2b\u5934\u3002\u201d\u5e74\u7fb9\u5c27\u5fc3\u8df3\u8033 \u70ed\uff0c\u541e\u4e86\u53e3\u6c34\uff0c\u5f3a\u884c\u5fcd\u4f4f\uff0c\u7b11\u9053\uff1a\u201c\u662f\u5417\uff0c\u90a3\u4e2b\u5934\u53c8\u7cbe\u7075\u53c8\u597d\u770b\uff0c\u672c\u6765\u5c31\u9017\u4eba\u7231\u3002\u201d\u4e86\u56e0\u7b11 \u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u6837\u3002\u6211\u770b\u56db\u8d1d\u52d2\u662f\u6709\u610f\u7559\u5979\uff0c\u51c6\u5907\u5979\u65e5\u7eb3\u5165\u540e\u5bab\u3002\u201d\u5e74\u7fb9\u5c27\u5f3a\u7b11\u9053\uff1a\u201c\u83ab\u4e0d\u662f \u5927\u5e08\u591a\u5fc3\u5427\uff1f\u201d\u4e86\u56e0\u5728\u9a6c\u4e0a\u5927\u7b11\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u56db\u8d1d\u52d2\u6211\u53ef\u6bd4\u4f60\u719f\u6089\u5f97\u591a\u3002\u4ed6\u548c\u6211 \u4e00\u6837\uff0c\u90fd\u662f\u8272\u4e2d\u997f\u9b3c\u3002\u90a3\u91ce\u4e2b\u5934\u5e74\u7eaa\u867d\u5c0f\uff0c\u5374\u662f\u5929\u751f\u7684\u7f8e\u4eba\u80da\u5b50\uff0c\u82e5\u4e0d\u662f\u6211\u770b\u51fa\u56db\u8d1d\u52d2\u5bf9\u5979 \u6709\u610f\uff0c\u6211\u4e5f\u8981\u52a8\u5979\u5ff5\u5934\uff01\u518d\u8bf4\u5979\u5e74\u7eaa\u867d\u5c0f\uff0c\u4e5f\u6709\u5341\u56db\u5c81\u4e86\uff0c\u518d\u8fc7\u4e24\u5e74\uff0c\u5c31\u662f\u4e2a\u9876\u6807\u81f4\u7684\u5927\u59d1 \u5a18\uff01\u201d\u5e74\u7fb9\u5c27\u5fc3\u5934\u53c8\u6068\u53c8\u75d2\u53c8\u60ca\u614c\u3002\u5fc3\u60f3\uff1a\u539f\u6765\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u51af\u7433\u4e0d\u80af\u56de\u53bb\uff0c\u53ea\u662f\u56db\u7687\u5b50\u65e2 \u7136\u5bf9\u5979\u6709\u610f\uff0c\u6211\u600e\u80fd\u628a\u5979\u7559\u4f4f\uff1f \u3000\u3000\u4e24\u4eba\u4e95\u8f94\u800c\u884c\uff0c\u8fc7\u4e86\u4e00\u4e2a\u65f6\u523b\uff0c\u5230\u4e86\u6e56\u6ee8\u5927\u9053\uff0c\u4e86\u56e0\u5ffd\u89c1\u97e9\u632f\u751f\u548c\u738b\u594b\u5728\u7518\u51e4\u6c60\u6240\u4f4f\u8fc7 \u7684\u65c5\u820d\u4e2d\u4e00\u6b65\u4e00\u62d0\uff0c\u884c\u4e86\u51fa\u6765\uff0c\u6025\u5fd9\u52d2\u9a6c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u4e86\uff1f\u72af\u4eba\u5462\uff1f\u201d\u738b\u594b\u7980\u9053\uff1a\u201c\u7ed9 \u5f3a\u4eba\u52ab\u53bb\u4e86\uff01\u201d\u4e86\u56e0\u9a82\u9053\uff1a\u201d\u8113\u5305\uff0c\u996d\u6876\uff0c\u4e24\u4e2a\u4eba\u770b\u5b88\u4e00\u4e2a\u5e9f\u4eba\u90fd\u5b88\u4e0d\u7740\uff01\u662f\u4ec0\u4e48\u5f3a\u4eba\u8fd9\u6837 \u5927\u80c6\uff0c\u767d\u65e5\u9752\u5929\u6765\u62a2\u72af\u4eba\uff1f\u201d\u97e9\u632f\u751f\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u6c5f\u6e56\u90ce\u4e2d\uff0c\u5f3a\u95ef\u8fdb\u6765\uff0c\u90a3\u4e2a\u5c11\u5e74\u4e00\u89c1\u4ed6\u4fbf \u53eb\u8205\u8205\uff0c\u6211\u4eec\u6b63\u5f85\u559d\u95ee\uff0c\u90a3\u6599\u4ed6\u8eab\u6cd5\u771f\u5feb\uff0c\u6211\u4eec\u8fd8\u672a\u770b\u6e05\uff0c\u5c31\u7ed9\u4ed6\u7528\u8896\u5b50\u4e00\u62c2\uff0c\u6454\u5012\u5730\u4e0a\u3002 \u7741\u5f00\u773c\u65f6\uff0c\u72af\u4eba\u5df2\u4e0d\u89c1\u4e86\u3002\u201d\u4e86\u56e0\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u6562\u518d\u884c\u8d23\u9a82\u3002\u5e74\u7fb9\u5c27\u5ffd\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u7528\u8896\u5b50 \u4e00\u62c2\u4f60\u4eec\u5c31\u6454\u5012\u5730\u4e86\uff1f\u8fc7\u6765\u7ed9\u6211\u770b\u770b\u3002\u201d\u4e24\u4eba\u4e00\u6b65\u4e00\u62d0\u8d70\u4e86\u8fc7\u6765\uff0c\u5e74\u7fb9\u5c27\u53eb\u4ed6\u5377\u8d77\u88e4\u5b50\u4e00 \u770b\uff0c\u53ea\u89c1\u4e24\u4eba\u5927\u817f\u53c8\u7ea2\u53c8\u80bf\u3002\u5e74\u7fb9\u5c27\u7a81\u7136\u7528\u529b\u5728\u4ed6\u4eec\u817f\u4e0a\u4e00\u626d\uff0c\u4e24\u4eba\u201c\u54ce\u54df\u201d\u4e00\u58f0\u5927\u53eb\u8d77 \u6765\uff0c\u5927\u53eb\u4e4b\u540e\uff0c\u7eb5\u8eab\u4e00\u8df3\uff0c\u75db\u695a\u82e5\u5931\uff0c\u5c45\u7136\u884c\u8d70\u81ea\u5982\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u7b11\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\u4e0d\u53ef\u9a82\u4ed6\uff0c\u4ed6\u4eec\u9047\u4e86\u6b66\u6797\u4e2d\u9876\u513f\u5c16\u513f\u7684\u786c\u624b\u4e86\uff01\u201d\u4e86\u56e0\u5947\u9053\uff1a \u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u5fd8\u8bb0\u4e86\u6211\u90a3\u6b7b\u9b3c\u5e08\u5085\u662f\u65e0\u6781\u6d3e\u7684\u5ae1\u7cfb\u4f20\u4eba\u4e48\uff1f\u8fd9\u79cd\u8863\u8896\u62c2 \u7a74\u7684\u529f\u592b\u6b63\u662f\u6211\u4eec\u65e0\u6781\u6d3e\u4e2d\u2018\u6d41\u4e91\u98de\u8896\u2019\u7684\u5bb6\u6570\u3002\u4e0d\u8fc7\u8fd9\u4eba\u529f\u529b\u6bd4\u6211\u5e08\u5085\u8fd8\u9ad8\uff0c\u6240\u4ee5\u83ab\u8bf4\u662f \u8fd9\u4e24\u4f4d\u6355\u5934\uff0c\u5c31\u662f\u6b66\u529f\u6bd4\u4ed6\u4eec\u66f4\u597d\u7684\u4eba\u4e5f\u6050\u7981\u4e0d\u4f4f\u6b64\u4eba\u4e00\u62c2\u3002\u201d\u4e86\u56e0\u542c\u4e86\u5927\u5947\uff0c\u95ee\u9053\uff1a\u201c\u4f60 \u4eec\u7684\u7956\u5e08\u5085\u9752\u4e3b\u8fd8\u4f20\u4e86\u8c01\u4eba\uff1f\u96be\u9053\u65e0\u6781\u6d3e\u7684\u957f\u8f88\uff0c\u9664\u4e86\u4f60\u5e08\u5085\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u5f97\u8fc7\u5085\u9752\u4e3b\u7684 \u771f\u4f20\u4e48\uff1f\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6211\u7684\u5e08\u5085\u662f\u65e0\u6781\u6d3e\u552f\u4e00\u4f20\u4eba\u3002\u201d\u4e86\u56e0\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u53c8\u600e\u4e48\u94bb\u51fa\u8fd9\u4e2a\u6c5f \u6e56\u90ce\u4e2d\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4ed6\u4e0d\u80fd\u7b97\u662f\u65e0\u6781\u6d3e\u7684\u4eba\uff0c\u4f46\u548c\u6211\u4eec\u7684\u592a\u7956\u5e08\uff08\u6307\u5085\u9752\u4e3b\u3015\u5012\u5f88\u6709\u6e0a \u6e90\u3002\u201d\u4e86\u56e0\u9053\uff1a\u201c\u5230\u5e95\u662f\u8c01\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u5929\u5c71\u4e03\u5251\u4e2d\u7684\u6b66\u743c\u7476\u4f60\u662f\u77e5\u9053\u7684\u4e86\u3002\u201d\u4e86\u56e0\u6120 \u9053\uff1a\u201c\u90a3\u8fd8\u80fd\u4e0d\u77e5\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u8fd9\u6c5f\u6e56\u90ce\u4e2d\u662f\u6b66\u743c\u7476\u7684\u5f1f\u5f1f\u3002\u201d\u4e86\u56e0\u8be7\u9053\uff1a\u201c\u6b66\u743c\u7476\u8fd8 \u6709\u4e00\u4e2a\u5f1f\u5f1f\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u5979\u7684\u5f1f\u5f1f\u540d\u53eb\u6b66\u6210\u5316\u3002\u81ea\u5e7c\u968f\u7236\u4eb2\u59d0\u59d0\u8fdc\u8d74\u585e\u5916\uff0c\u2018\u4e03\u5251\u2019\u5f52 \u9690\u65f6\uff0c\u4ed6\u5927\u7ea6\u8fd8\u53ea\u662f\u5341\u591a\u5c81\u7684\u5b69\u5b50\uff0c\u51e0\u5341\u5e74\u6765\u50fb\u5904\u585e\u5916\uff0c\u5728\u6b66\u6797\u4e2d\u4ea6\u65e0\u4e8b\u8ff9\u7559\u4f20\uff0c\u96be\u602a\u5927\u5e08 \u4e0d\u77e5\u9053\u4e86\uff1f\u201d\u4e86\u56e0\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4ed6\u4e0e\u4f60\u4eec\u65e0\u6781\u6d3e\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6211\u4e5f\u662f \u542c\u5f97\u5e08\u5085\u8bf4\u7684\uff0c\u542c\u8bf4\u592a\u5e08\u7956\u548c\u6b66\u6210\u5316\u7684\u7236\u4eb2\u2014\u2014\u7ec8\u5357\u6d3e\u540d\u5bbf\u6b66\u5143\u82f1\u4e43\u662f\u751f\u6b7b\u4e4b\u4ea4\uff0c\u6240\u4ee5\u66fe\u4f20 \u4e86\u4ed6\u6d41\u4e91\u98de\u8896\u7684\u7edd\u6280\u3002\u201d\uff08\u4f5c\u8005\u6309\uff1a\u5085\u9752\u4e3b\u4f20\u6b66\u6210\u5316\u7edd\u6280\u4e4b\u4e8b\uff0c\u8be6\u89c1\u62d9\u8457\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\uff09 \u3000\u3000\u4e86\u56e0\u542c\u4e86\uff0c\u5fc3\u4e2d\u8212\u7545\u3002\u5fc3\u60f3\uff1a\u539f\u6765\u8fd9\u6c5f\u6e56\u90ce\u4e2d\u6709\u7edd\u5927\u7684\u6765\u5934\uff0c\u90a3\u4e48\u8f93\u4e00\u62db\u534a\u62db\u7ed9\u4ed6\u4e5f\u8fd8 \u503c\u5f97\u3002\u4e24\u4eba\u5e76\u9a6c\u800c\u8c08\uff0c\u4e86\u56e0\u5ffd\u9053\uff1a\u201c\u5728\u676d\u5dde\u7684\u4e00\u6279\u94a6\u72af\u4e2d\uff0c\u6709\u4e00\u4eba\u548c\u4f60\u4e5f\u5f88\u6709\u6e0a\u6e90\u5462\uff01\u201d\u5e74 \u7fb9\u5c27\u9053\uff1a\u201c\u5927\u5e08\u4f11\u5f97\u8bf4\u7b11\uff01\u201d\u4e86\u56e0\u9053\uff1a\u201c\u8fd9\u53ef\u4e0d\u662f\u8bf4\u7b11\u3002\u4f60\u548c\u5c11\u6797\u6d3e\u7684\u5173\u7cfb\u7d20\u6765\u4e0d\u4e0b\u4e8e\u90a3\u6b66 \u6210\u5316\u548c\u4f60\u4eec\u65e0\u6781\u6d3e\u7684\u5173\u7cfb\u5427\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff0c\u6211\u7684\u6b66\u529f\u6709\u4e00\u534a\u5c31\u662f\u51fa\u4e8e\u5c11\u6797\u4e09\u8001 \u6240\u4f20\u3002\u201d\u4e86\u56e0\u9053\uff1a\u201c\u6211\u90a3\u4e0d\u8096\u5e08\u5f1f\u8def\u6c11\u77bb\u6709\u4e00\u4e2a\u597d\u53cb\u5370\u5b8f\u548c\u5c1a\uff0c\u662f\u5c11\u6797\u76d1\u5bfa\u672c\u65e0\u7985\u5e08\u7684\u5f92 \u5f1f\u3002\u8def\u6c11\u77bb\u8fd9\u6b21\u88ab\u64d2\uff0c\u542c\u8bf4\u5370\u5b8f\u6d89\u5acc\u7ed9\u4ed6\u9001\u4fe1\uff0c\u88ab\u629a\u8859\u9ad8\u624b\u8ffd\u8e2a\u5230\u4ed9\u971e\u5cad\u811a\u7f09\u62ff\u5f52\u6848\u3002\u4f60\u8fd9 \u6b21\u5949\u547d\u62bc\u89e3\u72af\u4eba\u8fdb\u4eac\uff0c\u90a3\u5370\u5b8f\u548c\u5c1a\u6d59\u629a\u4e00\u5b9a\u4f1a\u79fb\u4ea4\u7ed9\u4f60\u3002\u201d\u5e74\u7fb9\u5c27\u7b11\u9053\uff1a\u201c\u6211\u73b0\u5728\u8eab\u4e3a\u671d\u5ef7 \u5927\u5c06\uff0c\u53ea\u77e5\u6267\u6cd5\uff0c\u7edd\u4e0d\u5faa\u79c1\u3002\u90a3\u5370\u5b8f\u548c\u5c1a\u6211\u867d\u8ba4\u8bc6\uff0c\u8bf4\u4e0d\u5f97\u4e5f\u8981\u628a\u4ed6\u4e00\u5e76\u62bc\u89e3\u8fdb\u4eac\uff01\u201d\u4e86\u56e0 \u548c\u5c1a\u5728\u9a6c\u4e0a\u5927\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u6cd5\u4e0d\u6cd5\u5440\uff01\u5c0f\u5e74\uff0c\u522b\u7b11\u75bc\u6211\u7684\u809a\u5b50\u3002\u738b\u6cd5\u662f\u4ec0\u4e48\u4e00\u56de\u4e8b\uff0c\u6211\u77e5\u9053 \u4f60\u4e5f\u77e5\u9053\uff0c\u5728\u6211\u7684\u9762\u524d\u4f55\u5fc5\u8bf4\u8fd9\u4e2a\u6f02\u4eae\u8bdd\u513f\u3002\u5176\u5b9e\u90a3\u5370\u5b8f\u548c\u5c1a\u5230\u5e95\u662f\u5426\u66fe\u7ed9\u8def\u6c11\u77bb\u9001\u4fe1\uff0c\u73b0 \u5728\u4e5f\u8fd8\u6ca1\u6709\u8bc1\u636e\u3002\u201d\u4e86\u56e0\u501a\u8001\u5356\u8001\u3002\u6240\u8bf4\u5374\u662f\u5b9e\u60c5\u3002 \u3000\u3000\u4e24\u4eba\u5728\u519b\u4e2d\u5e76\u9a6c\u800c\u8c08\uff0c\u664c\u5348\u65f6\u5206\uff0c\u8fdb\u5165\u676d\u57ce\uff0c\u53ea\u89c1\u57ce\u4e2d\u904d\u5e03\u54e8\u5c97\u3001\u6bcf\u9694\u5341\u6b65\u5c31\u6709\u58eb\u5175\u7ad9 \u5c97\uff0c\u4e86\u56e0\u5947\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u8fd9\u6837\u7d27\u5f20\uff1f\u201d\u4e00\u9a6c\u524d\u9a71\uff0c\u5230\u629a\u8859\u5148\u62a5\u5e74\u7fb9\u5c27\u5165\u57ce\u7684\u6d88\u606f\uff0c\u53ea\u89c1\u6d59\u629a \u674e\u536b\u6c14\u6025\u8d25\u574f\u7684\u51fa\u6765\u3002 \u3000\u3000\u4f60\u9053\u4ed6\u4f55\u6545\u5982\u6b64\uff0c\u539f\u6765\u662f\u56e0\u4e3a\u5415\u56db\u5a18\u5927\u95f9\u629a\u8859\u4e4b\u6545\uff0c\u5415\u56db\u5a18\u534f\u52a9\u7518\u3001\u767d\u4e8c\u4eba\u6253\u9000\u97e9\u91cd\u5c71 \u8463\u5de8\u5ddd\u4e4b\u540e\uff0c\u9884\u6599\u4e86\u56e0\u5fc5\u6765\u8ffd\u6355\uff0c\u629a\u8859\u7f3a\u4e4f\u9ad8\u624b\uff0c\u6b63\u597d\u4e58\u673a\u6551\u4eba\uff0c\u4e8e\u662f\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u4e00\u591c \u4e4b\u4e2d\uff0c\u4e24\u63a2\u8859\u7f72\u3002\u5230\u4e86\u629a\u8859\uff0c\u6668\u9e21\u5df2\u9e23\uff0c\u6653\u98ce\u62c2\u9762\uff0c\u536b\u5175\u6b63\u5728\u6362\u73ed\u3002 \u3000\u3000\u5415\u56db\u5a18\u5bf9\u629a\u8859\u9053\u8def\u672c\u5c31\u719f\u6089\uff0c\u6628\u665a\u63a2\u8859\uff0c\u53c8\u5df2\u77e5\u9053\u8def\u6c11\u77bb\u56da\u623f\u6240\u5728\uff0c\u4fbf\u76f4\u5954\u5916\u8859\u90a3\u95f4\u9752 \u7816\u5927\u5c4b\u3002\u5728\u5c4b\u9876\u4e0a\u63ed\u5f00\u4e00\u70b9\u74e6\u7f1d\uff0c\u8d34\u76ee\u5077\u7aa5\uff0c\u5ffd\u542c\u5f97\u6709\u4e2a\u5973\u5b69\u5b50\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5415\u56db \u5a18\u2026\u2026\u201d\u5415\u56db\u5a18\u5403\u4e86\u4e00\u60ca\uff0c\u4ee5\u4e3a\u7ed9\u4eba\u53d1\u73b0\u3002\u53ea\u542c\u5f97\u90a3\u5c11\u5973\u9053\uff1a\u201c\u5415\u56db\u5a18\u4e0d\u6127\u662f\u5973\u4e2d\u4e08\u592b\uff0c\u53ea \u6068\u6211\u65e0\u6cd5\u5b66\u5979\u7684\u6837\u5b50\u3002\u201d\u4e0b\u9762\u5899\u89d2\u6697\u95e8\u500f\u7684\u6253\u5f00\uff0c\u6d59\u6c5f\u5de1\u629a\u674e\u536b\u7684\u5973\u513f\u674e\u660e\u73e0\u7275\u7740\u8def\u6c11\u77bb\u7f13 \u7f13\u8d70\u51fa\u3002\u8def\u6c11\u77bb\u60f3\u662f\u5904\u5728\u6697\u5ba4\u591a\u65e5\uff0c\u773c\u775b\u5f88\u4e0d\u4e60\u60ef\uff0c\u7728\u5440\u7728\u5440\u7684\uff0c\u597d\u4e45\u624d\u80fd\u7741\u5f00\u773c\u775b\u3002 \u3000\u3000\u5415\u56db\u5a18\u5fc3\u4e2d\u5947\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u4f4d\u5927\u5c0f\u59d0\u8c08\u8bba\u8d77\u6211\u6765\u4e86\uff0c\u5979\u4e3a\u4ec0\u4e48\u53c8\u8fd9\u6837\u5927\u80c6\uff0c\u6562\u628a\u72af\u4eba\u4ece \u5bc6\u5ba4\u91cc\u5e26\u51fa\u6765\u3002\u201d\u53ea\u542c\u5f97\u90a3\u674e\u660e\u73e0\u53c8\u9053\uff1a\u201c\u5415\u56db\u5a18\u786e\u662f\u5973\u4e2d\u4e08\u592b\uff0c\u4f46\u5979\u6b22\u559c\u7684\u90a3\u4e2a\u4e66\u751f\u66f4\u662f \u4eba\u4e2d\u4fca\u6770\u3002\u201d\u5415\u56db\u5a18\u9762\u4e0a\u4e00\u9635\u53d1\u70ed\uff0c\u5fc3\u4e2d\u5374\u662f\u5341\u5206\u6b22\u559c\u3002\u8def\u6c11\u77bb\u7b11\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u539f \u6765\u8def\u6c11\u77bb\u5e76\u672a\u89c1\u8fc7\u6c88\u5728\u5bbd\uff0c\u53ea\u662f\u5728\u540c\u95e8\u53e3\u4e2d\u9690\u9690\u7ea6\u7ea6\u77e5\u9053\u6c88\u5728\u5bbd\u7684\u4e3a\u4eba\u800c\u5df2\u3002\u674e\u660e\u73e0\u7b11\u9053\uff1a \u201c\u4ed6\u4ee5\u524d\u4e5f\u66fe\u88ab\u56da\u5728\u8fd9\u513f\uff0c\u6211\u7236\u4eb2\u5bf9\u4ed6\u5a01\u80c1\u5229\u8bf1\uff0c\u4ed6\u4e00\u70b9\u4e5f\u4e0d\u5c48\u670d\u3002\u82e5\u7136\u4ed6\u662f\u50cf\u4f60\u4eec\u4e00\u6837\u7684 \u4fa0\u58eb\u5012\u4e0d\u51fa\u5947\uff0c\u4ed6\u5374\u53ea\u662f\u4e00\u4e2a\u6587\u7ec9\u7ec9\u7684\u4e66\u751f\u5462\uff01\u201d\u5415\u56db\u5a18\u5728\u4e0a\u9762\u542c\u5f97\u82b3\u5fc3\u5927\u60a6\uff0c\u5bf9\u674e\u660e\u73e0\u751a \u4e3a\u597d\u611f\u3002 \u3000\u3000\u674e\u660e\u73e0\u672c\u6765\u662f\u4e00\u4e2a\u4e0d\u77e5\u4e16\u4e8b\u7684\u5b98\u5bb6\u5c0f\u59d0\uff0c\u81ea\u90a3\u6b21\u968f\u7236\u4eb2\u89c1\u4e86\u6c88\u5728\u5bbd\u4e4b\u540e\uff0c\u542c\u5230\u4ed6\u90a3\u756a\u6fc0 \u6602\u6177\u6168\u7684\u8bae\u8bba\uff0c\u5c24\u5176\u662f\u542c\u5230\u4ed6\u501f\u5434\u6885\u6751\u7684\u7edd\u547d\u8bcd\u6697\u8bbd\u7236\u4eb2\u4e4b\u540e\uff0c\u50cf\u5728\u6697\u5ba4\u7684\u4eba\u5ffd\u7136\u770b\u89c1\u4e86\u9633 \u5149\uff0c\u53d7\u4e86\u523a\u6fc0\uff0c\u5fc3\u4e2d\u6ce2\u52a8\uff0c\u5979\u672c\u6765\u662f\u4e2a\u597d\u5947\u7684\u5973\u5b69\u5b50\uff0c\u81ea\u6b64\u7adf\u7136\u5077\u770b\u8d77\u671d\u5ef7\u7684\u201c\u7981\u4e66\u201d\uff0c\u8fde \u5415\u665a\u6751\u7684\u201c\u6518\u5937\u5f55\u201d\u5979\u4e5f\u5077\u5077\u627e\u6765\u770b\u4e86\u3002\u6240\u4ee5\u8fd9\u6b21\u5979\u4e4b\u6562\u4e8e\u5e87\u62a4\u8def\u6c11\u77bb\uff0c\u9664\u4e86\u6b22\u559c\u4ed6\u82f1\u4fca\u7684 \u98ce\u5ea6\u4e4b\u5916\uff0c\u548c\u8bfb\u4e86\u5415\u665a\u6751\u5199\u7684\u201c\u7981\u4e66\u201d\uff0c\u4e5f\u4e0d\u65e0\u5173\u7cfb\u3002 \u3000\u3000\u8def\u6c11\u77bb\u542c\u51fa\u5979\u5bf9\u5415\u56db\u5a18\u548c\u6c88\u5728\u5bbd\u7684\u503e\u6155\u4e4b\u60c5\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u8981\u5b66\u4ed6\u4eec\u4e5f\u5e76\u4e0d\u96be\uff0c\u6211 \u4eec\u4e00\u540c\u9003\u8d70\uff0c\u627e\u4ed6\u4eec\u53bb\u3002\u201d\u674e\u660e\u73e0\u9762\u76ee\u500f\u53d8\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u6211\u4e0d\u80fd\u79bb\u5f00\u6211\u7684\u7239\u5a18\uff01\u201d \u5979\u867d\u7136\u4e0e\u524d\u6709\u6240\u4e0d\u540c\uff0c\u4f46\u8fd8\u672a\u575a\u51b3\u5230\u53ef\u4ee5\u629b\u5f00\u5bb6\u5ead\uff0c\u629b\u5f00\u5343\u91d1\u5c0f\u59d0\u5730\u4f4d\u7684\u7a0b\u5ea6\u3002 \u3000\u3000\u8def\u6c11\u77bb\u597d\u4f3c\u751a\u4e3a\u5931\u671b\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002\u674e\u660e\u73e0\u9053\uff1a\u201c\u4f60\u5012\u53ef\u4ee5\u8d81\u8fd9\u673a\u4f1a\u9003\u8d70\u3002\u4e86\u56e0\u90a3\u8d3c\u79c3\u5929 \u4eae\u4e4b\u524d\u5e26\u4e86\u4e00\u73ed\u6355\u5feb\u5306\u5306\u51fa\u8859\u53bb\u4e86\u3002\u6211\u7684\u5e08\u5085\u8fd8\u5728\u68a6\u4e2d\uff0c\u8859\u4e2d\u6ca1\u6709\u9ad8\u624b\u62e6\u963b\uff0c\u4f60\u653e\u5fc3\u8d70\u5427\uff01\u201d \u3000\u3000\u8def\u6c11\u77bb\u5927\u51fa\u610f\u5916\uff0c\u5728\u8fd9\u4e00\u4e2a\u591a\u6708\u88ab\u8f6f\u7981\u7684\u751f\u6d3b\u4e2d\uff0c\u4ed6\u5df2\u5bdf\u77e5\u674e\u660e\u73e0\u5bf9\u4ed6\u7684\u60c5\u610f\uff0c\u5fc3\u4e2d\u8fd8 \u5bb3\u6015\u5979\u4f1a\u7f20\u7740\u81ea\u5df1\uff0c\u54ea\u6599\u5979\u5374\u80af\u653e\u81ea\u5df1\u5077\u8d70\uff0c\u5fc3\u4e2d\u611f\u52a8\uff0c\u5012\u53cd\u72b9\u7591\u3002\u674e\u660e\u73e0\u63a8\u4ed6\u9053\uff1a\u201c\u5feb \u8d70\uff0c\u5feb\u8d70\uff01\u7b49\u4e00\u4f1a\u5929\u5927\u4eae\u4e86\uff0c\u8981\u9003\u8d70\u5c31\u4e0d\u5bb9\u6613\u4e86\uff01\u201d\u8bf4\u5b8c\u4e4b\u540e\uff0c\u773c\u5708\u4e00\u7ea2\uff0c\u8def\u6c11\u77bb\u66f4\u662f\u5fc3\u795e \u52a8\u8361\u3002 \u3000\u3000\u6b63\u5728\u6b64\u65f6\uff0c\u5ffd\u95fb\u5f97\u4e00\u58f0\u51b7\u7b11\uff1a\u201c\u597d\u5440\uff0c\u5973\u751f\u5916\u5411\uff0c\u4f60\u8981\u653e\u4ed6\u8d70\u4e86\uff1f\u201d\u9752\u8863\u5987\u4eba\u9634\u607b\u607b\u7684 \u63a8\u95e8\u8fdb\u6765\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u867d\u6709\u7ea2\u989c\u77e5\u5df1\u5728\uff0c\u5374\u9632\u9b44\u9b41\u6697\u7aa5\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56de\u3000\u6697\u5668\u8fde\u73e0\u3000\u98de\u9488\u4f24\u5973\u4fa0\u3000\u8be1\u8c0b\u5bc6\u8fd0\u3000\u6bd2\u624b\u5bb3\u7985\u5e08 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u56de \u6697\u5668\u8fde\u73e0\u3000\u98de\u9488\u4f24\u5973\u4fa0 \u8be1\u8c0b\u5bc6\u8fd0\u3000\u6bd2\u624b\u5bb3\u7985\u5e08 \u3000\u3000\u674e\u660e\u73e0\u60ca\u53eb\u201c\u5e08\u5085\u201d\uff0c\u90a3\u9752\u8863\u5987\u4eba\u6273\u8138\u4e0d\u7406\uff0c\u5411\u8def\u6c11\u77bb\u4e00\u628a\u6293\u6765\uff0c\u559d\u9053\uff1a\u201c\u56de\u53bb\uff01\u201d\u5415 \u56db\u5a18\u7a81\u7136\u4e00\u58f0\u957f\u5578\uff0c\u4ece\u5c4b\u9876\u76f4\u7a9c\u4e0b\u6765\uff01 \u3000\u3000\u8fd9\u9752\u8863\u5987\u4eba\u6b63\u662f\u97e9\u91cd\u5c71\u7684\u59bb\u5b50\u53f6\u6a2a\u6ce2\uff0c\u5979\u672c\u6765\u548c\u97e9\u91cd\u5c71\u5728\u5bde\u5df7\u5c71\u7ed3\u5e90\u53cc\u4fee\uff0c\u540e\u6765\u97e9\u91cd\u5c71 \u5230\u897f\u57df\u91c7\u836f\uff0c\u5e76\u63a2\u8bbf\u4ed6\u7684\u5e08\u5f1f\u5929\u53f6\u6563\u4eba\uff0c\u4e00\u53bb\u51e0\u5e74\uff0c\u5c1a\u672a\u56de\u5c71\uff0c\u53f6\u6a2a\u6ce2\u4e0b\u5c71\u63a2\u542c\uff0c\u624d\u77e5\u4ed6\u548c \u897f\u5317\u4e00\u4e2a\u5973\u98de\u8d3c\u7ea2\u9526\u5a18\u52fe\u642d\u4e0a\u4e86\uff0c\u53f6\u6a2a\u6ce2\u5927\u4e3a\u751f\u6c14\uff0c\u592b\u59bb\u53cd\u76ee\u3002\u540e\u6765\u97e9\u91cd\u5c71\u6295\u5165\u4e86\u56db\u7687\u5b50\u95e8 \u4e0b\uff0c\u53f6\u6a2a\u6ce2\u4e5f\u5230\u629a\u8859\uff0c\u505a\u4e86\u674e\u660e\u73e0\u7684\u5e08\u5085\u3002\u53f6\u6a2a\u6ce2\u5185\u529f\u6df1\u6e5b\uff0c\u4e94\u5341\u4f59\u5c81\u8fd8\u4f3c\u56db\u5341\u8bb8\u4eba\u3002\u97e9\u91cd \u5c71\u5bf9\u90a3\u5973\u98de\u8d3c\u672c\u662f\u96fe\u6c34\u59fb\u7f18\uff0c\u5e76\u65e0\u8bda\u610f\uff0c\u65e5\u5b50\u4e00\u4e45\uff0c\u6e10\u751f\u538c\u5026\uff0c\u4e0d\u7981\u53c8\u601d\u5ff5\u8d77\u81ea\u5df1\u59bb\u5b50\u6765\u3002 \u4e8e\u662f\u592e\u4e86\u56e0\u7ed9\u4ed6\u548c\u89e3\u3002\u53f6\u6a2a\u6ce2\u8d77\u5148\u575a\u4e0d\u7b54\u5e94\uff0c\u540e\u6765\u5ff5\u8d77\u592b\u59bb\u4e4b\u60c5\uff0c\u5141\u8bb8\u548c\u89e3\uff0c\u53ef\u662f\u5374\u63d0\u51fa\u4e00 \u4e2a\u6bd2\u8fa3\u7684\u6761\u4ef6\uff0c\u8981\u97e9\u91cd\u5c71\u5c06\u7ea2\u9526\u5a18\u7684\u4e00\u53ea\u8033\u6735\u3001\u5341\u6839\u6307\u5934\uff0c\u5e26\u6765\u4f5c\u8d4e\u7f6a\u4e4b\u7269\u3002\u6628\u665a\u7518\u51e4\u6c60\u5728 \u65c5\u820d\u770b\u89c1\u7684\u90a3\u4e2a\u5973\u4eba\uff0c\u4fbf\u6b63\u662f\u90a3\u4e2a\u5973\u98de\u8d3c\u3002\u4e5f\u5e78\u4e8f\u7518\u51e4\u6c60\u53ca\u65f6\u95ef\u6765\uff0c\u7ea2\u9526\u5a18\u624d\u80fd\u4ece\u97e9\u91cd\u5c71\u7684 \u9b54\u638c\u4e0b\u8131\u9003\u3002 \u3000\u3000\u5415\u56db\u5a18\u4e00\u8dc3\u800c\u4e0b\uff0c\u971c\u534e\u5b9d\u5251\u65e9\u5df2\u62d4\u5728\u624b\u4e2d\u3002\u53f6\u6a2a\u6ce2\u6a2a\u8dc3\u4e09\u6b65\uff0c\u4e5f\u62d4\u51fa\u5b9d\u5251\uff0c\u5415\u56db\u5a18\u9053\uff1a \u201c\u8def\u5e08\u5144\uff0c\u968f\u6211\u51fa\u53bb\uff01\u201d\u53f6\u6a2a\u6ce2\u5927\u6012\u559d\u9053\uff1a\u201c\u5415\u56db\u5a18\uff0c\u522b\u4eba\u6015\u4f60\uff0c\u6211\u4e0d\u6015\u4f60\uff01\u201d\u5530\u7684\u4e00\u5251\u5241 \u6765\uff01\u5415\u56db\u5a18\u4e0d\u614c\u4e0d\u5fd9\u7528\u4e86\u4e00\u62db\u201c\u767d\u9e64\u5254\u7fce\u201d\uff0c\u5411\u5979\u53f3\u8155\u4e00\u524a\uff0c\u53f6\u6a2a\u6ce2\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c \u5b9d\u5251\u62ab\u98ce\uff0c\u4e00\u62db\u201c\u997f\u9e70\u63a0\u7fbd\u201d\uff0c\u6025\u5982\u7535\u706b\uff0c\u5251\u950b\u53cd\u524a\u5415\u56db\u5a18\u5de6\u81c2\u3002\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5251\u4f7f \u5f97\u4e0d\u4fd7\u3002\u201d\u5251\u8bc0\u4e00\u9886\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u7a81\u7136\u8d34\u7740\u53f6\u6a2a\u6ce2\u7684\u5251\u8eab\u4e00\u7ede\uff0c\u53f6\u6a2a\u6ce2\u7684\u5251\u51e0\u4e4e\u7ed9\u5979\u7ede\u5f97\u8131 \u624b\u98de\u53bb\uff01\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u4f7f\u4e2a\u201c\u8749\u66f3\u6b8b\u58f0\u201d\u7684\u62db\u6570\uff0c\u6697\u8fd0\u5185\u529b\uff0c\u8f7b\u8f7b\u4e00\u5378\uff0c\u89e3\u62db\u8fd8\u62db\u3002\u5415\u56db \u5a18\u5251\u6cd5\u7cbe\u5999\u5feb\u6377\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u8fdb\u4e86\u4e09\u62db\uff0c\u628a\u53f6\u6a2a\u6ce2\u6740\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u53f6\u6a2a\u6ce2\u4ecd\u7136\u6beb\u4e0d\u9000 \u8ba9\uff0c\u4e00\u8fb9\u6321\u4e00\u9762\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6765\u4eba\u5440\uff01\u201d \u3000\u3000\u53f6\u6a2a\u6ce2\u6b66\u529f\u4e0d\u5728\u4e08\u592b\u4e4b\u4e0b\uff0c\u5415\u56db\u5a18\u4e0d\u613f\u4e45\u6218\uff0c\u971c\u534e\u5251\u54e9\u54e9\u7684\u8fde\u8fdb\u51e0\u62db\uff0c\u53eb\u9053\uff1a\u201c\u8def\u5e08 \u5144\uff0c\u4f60\u5148\u4e0a\u5c4b\u3002\u201d\u8def\u6c11\u77bb\u5411\u674e\u660e\u73e0\u4e00\u63d6\u5230\u5730\uff0c\u63a8\u7a97\u8dc3\u51fa\u3002\u5415\u56db\u5a18\u8fd0\u5251\u5982\u98ce\uff0c\u5341\u62db\u4e4b\u540e\u53f6\u6a2a\u6ce2 \u7ed9\u5251\u70b9\u8000\u5f97\u773c\u82b1\u64a9\u4e71\uff0c\u9000\u4e86\u4e24\u6b65\uff0c\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u5931\u966a\uff01\u201d\u7ea4\u8170\u4e00\u626d\uff0c\u7a7f\u7a97\u98de\u51fa\u3002\u53f6\u6a2a\u6ce2\u6c14 \u547c\u547c\u7684\u63d0\u5251\u8ffd\u53bb\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u5415\u56db\u5a18\u5df2\u8df3\u8fc7\u4e09\u91cd\u9662\u843d\u3002 \u3000\u3000\u731b\u7136\u95f4\uff0c\u5ffd\u542c\u5f97\u8def\u6c11\u77bb\u5728\u524d\u9762\u5927\u58f0\u547c\u53eb\uff01\u5415\u56db\u5a18\u8eab\u5f62\u6025\u8d77\uff0c\u75be\u5982\u98de\u7bad\uff0c\u53c8\u518d\u7a7f\u8fc7\u4e00\u91cd\u9662 \u843d\uff0c\u53ea\u89c1\u4e00\u4eba\u53cc\u638c\u4f5c\u52bf\u64d2\u62ff\uff0c\u628a\u8def\u6c11\u77bb\u903c\u5f97\u56e2\u56e2\u4e71\u8f6c\uff0c\u53e6\u4e00\u4eba\u624b\u63d0\u4e00\u9877\u9504\u5934\u622a\u4e86\u53bb\u8def\uff0c\u8fd9\u4e24 \u4eba\u6b63\u662f\u8463\u5de8\u5ddd\u548c\u97e9\u91cd\u5c71\u3002 \u3000\u3000\u539f\u6765\u8463\u5de8\u5ddd\u8001\u5978\u5de8\u6ed1\uff0c\u4ed6\u548c\u97e9\u91cd\u5c71\u5728\u65c5\u820d\u4e2d\u7ed9\u7518\u5415\u767d\u4e09\u4eba\u5408\u529b\u6740\u9000\u4e4b\u540e\uff0c\u9884\u6599\u5415\u56db\u5a18\u5fc5 \u7136\u4e58\u865a\u6551\u4eba\uff0c\u56e0\u6b64\u548c\u97e9\u91cd\u5c71\u6025\u7ed5\u6377\u5f84\uff0c\u5954\u56de\u57ce\u8f84\u521a\u597d\u53ca\u65f6\u6765\u5230\u3002 \u3000\u3000\u5415\u56db\u5a18\u89c1\u8def\u6c11\u77bb\u5f62\u52bf\u5947\u9669\uff0c\u8eab\u5f62\u672a\u5230\uff0c\u6697\u5668\u5148\u53d1\uff0c\u545c\u545c\u4e24\u58f0\uff0c\u4e24\u67c4\u5315\u9996\u7834\u7a7a\u98de\u51fa\uff0c\u97e9\u91cd \u5c71\u662f\u6697\u5668\u540d\u5bb6\uff0c\u4e00\u626c\u624b\u4e09\u56e2\u5bd2\u5149\u4e5f\u8131\u624b\u98de\u53bb\uff0c\u8fd9\u6697\u5668\u4e43\u662f\u4ed6\u6240\u7ec3\u7684\u201c\u5bd2\u5149\u98de\u94b1\u201d\uff0c\u56db\u8fb9\u950b \u5229\uff0c\u5415\u56db\u5a18\u7684\u4e24\u67c4\u5315\u9996\u5168\u7ed9\u6253\u843d\uff0c\u4e2d\u95f4\u90a3\u56e2\u5bd2\u5149\u5df2\u76f4\u671d\u5979\u80f8\u53e3\u98de\u6765\uff0c\u5415\u56db\u5a18\u5c06\u5251\u4e00\u64a9\uff0c\u628a\u98de \u9556\u64a9\u8fc7\u5934\u9876\uff0c\u987a\u624b\u53c8\u6253\u51fa\u4e24\u67c4\u5315\u9996\uff0c\u5206\u53d6\u97e9\u91cd\u5c71\u548c\u8463\u5de8\u5ddd\u3002 \u3000\u3000\u97e9\u91cd\u5c71\u8eab\u5f62\u66b4\u8d77\uff0c\u8f9f\u4e91\u9504\u5c06\u5315\u9996\u4ece\u534a\u7a7a\u6253\u843d\uff0c\u76f4\u6251\u5415\u56db\u5a18\uff1b\u8463\u5de8\u5ddd\u4e00\u95ea\u628a\u5315\u9996\u95ea\u5f00\uff0c\u4f46 \u5374\u7f13\u4e86\u4e00\u7f13\uff0c\u8def\u6c11\u77bb\u7f13\u4e86\u53e3\u6c14\uff0c\u8131\u51fa\u8eab\u6765\u3002\u8463\u5de8\u5ddd\u559d\u9053\uff1a\u201c\u54ea\u91cc\u8d70\uff01\u201d\u8df3\u8fc7\u5047\u5c71\u53c8\u518d\u62e6\u622a\uff01 \u3000\u3000\u8fd9\u8fb9\u53a2\u5415\u56db\u5a18\u6321\u4e86\u97e9\u91cd\u5c71\u5288\u5934\u4e00\u9504\uff0c\u8fd8\u4e86\u4e00\u5251\uff0c\u4e0d\u613f\u7ed9\u4ed6\u7f20\u7740\uff0c\u4ed7\u7740\u8eab\u6cd5\u8f7b\u7075\uff0c\u8f7b\u529f\u5353 \u7edd\uff0c\u97e9\u91cd\u5c71\u4e00\u9504\u6a2a\u65ab\uff0c\u5979\u987a\u52bf\u5c06\u5251\u5c16\u5728\u9504\u5934\u4e00\u70b9\uff0c\u501f\u7740\u97e9\u91cd\u5c71\u7684\u731b\u529b\uff0c\u6574\u4e2a\u8eab\u5b50\u53cd\u5f39\u8d77\u6765\uff0c \u7fe9\u5982\u5de8\u96c1\uff0c\u5411\u8463\u5de8\u5ddd\u4fef\u51b2\u800c\u4e0b\uff0c\u8463\u5de8\u5ddd\u75be\u5fd9\u95ea\u907f\uff0c\u5415\u56db\u5a18\u5251\u5149\u4e00\u95ea\uff0c\u76f4\u523a\u4ed6\u80cc\u540e\u201c\u51e4\u5e9c \u7a74\u201d\uff0c\u8463\u5de8\u5ddd\u662f\u5f62\u610f\u6d3e\u540d\u5bbf\uff0c\u6b66\u529f\u4e0d\u5f31\uff0c\u767e\u5fd9\u4e2d\u7ffb\u8eab\u7f29\u8098\uff0c\u7a81\u7136\u53cc\u638c\u4e00\u63a8\u4e00\u5e26\uff0c\u4e58\u5415\u56db\u5a18\u7acb \u8db3\u672a\u7a33\uff0c\u500f\u7684\u6251\u653b\u5979\u4e2d\u8def\u7a7a\u95e8\uff0c\u8fd9\u4e00\u62db\u4e43\u662f\u4ed6\u7684\u6740\u624b\u7edd\u62db\uff0c\u4e0d\u6599\u5415\u56db\u5a18\u5251\u6cd5\u795e\u5999\u65e0\u6bd4\uff0c\u53d8\u5e7b \u65e0\u65b9\uff0c\u5728\u534a\u7a7a\u98de\u843d\u4e4b\u65f6\uff0c\u4f3c\u4e4e\u65e9\u5df2\u6599\u5230\u4ed6\u6709\u6b64\u4e00\u62db\u4e58\u8179\u8fdb\u51fb\uff0c\u971c\u534e\u5b9d\u5251\u500f\u7684\u53d1\u51fa\u53bb\uff0c\u5708\u56de \u6765\uff0c\u62ff\u634f\u65f6\u5019\uff0c\u53c8\u5feb\u53c8\u51c6\uff0c\u8463\u5de8\u5ddd\u4e00\u4e2a\u6392\u5c71\u638c\u521a\u5230\u80f8\u524d\uff0c\u5979\u7684\u5251\u950b\u5df2\u53cd\u5708\u56de\u6765\uff01\u5e78\u5728\u8463\u5de8\u5ddd \u706b\u5019\u8001\u5230\uff0c\u6025\u5fd9\u4f0f\u5730\u4e00\u6eda\uff0c\u624d\u8131\u4e86\u5341\u6307\u88ab\u524a\u4e4b\u707e\uff0c\u9976\u662f\u8fd9\u6837\uff0c\u8fd8\u662f\u7ed9\u5415\u56db\u5a18\u8e6c\u4e86\u4e00\u811a\uff0c\u6eda\u51fa \u4e09\u4e08\u4e4b\u5916\uff0c\u624d\u722c\u5f97\u8d77\u6765\uff01 \u3000\u3000\u5415\u56db\u5a18\u53eb\u9053\uff1a\u201c\u8def\u5e08\u5144\uff0c\u4f60\u5feb\u8d70\uff0c\u6211\u7ed9\u4f60\u65ad\u540e\u3002\u201d\u63a5\u7740\uff0c\u8bf4\u4e86\u4e24\u53e5\u672c\u95e8\u6697\u8bed\uff0c\u544a\u8bc9\u4ed6\u7518 \u51e4\u6c60\u6240\u5728\uff0c\u97e9\u91cd\u5c71\u65e9\u5df2\u8dd1\u6765\uff0c\u8f9f\u4e91\u9504\u697c\u5934\u518d\u5288\uff0c\u5415\u56db\u5a18\u7ea4\u8170\u4e00\u8eac\uff0c\u4e0d\u9000\u53cd\u8fdb\uff0c\u5237\u5237\u4e24\u5251\uff0c\u5206 \u523a\u4ed6\u4e24\u80c1\u7684\u201c\u7ae0\u95e8\u7a74\u201d\uff0c\u97e9\u91cd\u5c71\u8feb\u5f97\u8fde\u9000\u4e09\u6b65\uff0c\u6a2a\u9504\u4e00\u5c01\u3002\u8def\u6c11\u77bb\u65e9\u5df2\u8d8a\u51fa\u56f4\u5899\uff0c\u98de\u5954\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u53f6\u6a2a\u6ce2\u4e5f\u5df2\u8d76\u5230\uff0c\u89c1\u97e9\u91cd\u5c71\u6b63\u548c\u5415\u56db\u5a18\u6076\u6218\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u60b2\u559c\u4ea4\u96c6\uff0c\u56b7\u9053\uff1a\u201c\u8001 \u9b3c\uff0c\u4f60\u6765\u4f5c\u751a\uff1f\u201d\u97e9\u91cd\u5c71\u53eb\u9053\uff1a\u201c\u597d\u5a46\u5a18\uff0c\u54b1\u4eec\u7684\u5e10\u4ee5\u540e\u518d\u7b97\u3002\u4f60\u5feb\u6765\u7ed9\u6211\u62e6\u4f4f\u8fd9\u4e2a\u8d31 \u5a62\uff01\u201d\u8463\u5de8\u5ddd\u4ece\u5730\u4e0a\u722c\u8d77\uff0c\u6012\u706b\u4e2d\u70e7\uff0c\u8fd0\u638c\u5982\u5200\uff0c\u4e5f\u4ece\u504f\u950b\u6025\u653b\uff0c\u628a\u5415\u56db\u5a18\u903c\u5f97\u8f6c\u653b\u4e3a\u5b88\u3002 \u3000\u3000\u5415\u56db\u5a18\u4e00\u60f3\uff0c\u81ea\u5df1\u672c\u9886\u518d\u9ad8\uff0c\u4e5f\u6321\u4e0d\u4f4f\u4e09\u4e2a\u4e00\u6d41\u9ad8\u624b\u7684\u5939\u51fb\uff0c\u865a\u6643\u4e00\u5251\uff0c\u5de6\u624b\u634f\u7740\u5251\u8bc0 \u7684\u624b\u6307\u7a81\u7136\u5f20\u5f00\uff0c\u5411\u8463\u81e3\u5ddd\u9762\u95e8\u4e00\u5212\uff0c\u8463\u5de8\u5ddd\u5230\u5e95\u662f\u60ca\u5f13\u4e4b\u9e1f\uff0c\u4f4e\u5934\u4e00\u95ea\uff0c\u5415\u56db\u5a18\u53cd\u624b\u4e00 \u5251\uff0c\u5411\u97e9\u91cd\u5c71\u75be\u70b9\u4e00\u4e0b\uff0c\u8eab\u5b50\u5df2\u4ece\u8463\u5de8\u5ddd\u638c\u5e95\u7a7f\u51fa\u3002\u53f6\u6a2a\u6ce2\u65e9\u5954\u5728\u524d\u9762\uff0c\u6301\u5251\u62e6\u622a\u3002\u5415\u56db\u5a18 \u8db3\u5c16\u70b9\u5730\uff0c\u5e73\u7a7a\u8dc3\u8d77\u4e08\u4f59\uff0c\u633d\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u547c\u7684\u4e00\u58f0\u4ece\u53f6\u6a2a\u6ce2\u5934\u9876\u63a0\u8fc7\uff0c\u53f6\u6a2a\u6ce2\u4e3e\u5251\u4e00\u64a9\uff0c \u6070\u6070\u7ed9\u5415\u56db\u5a18\u4e0b\u6233\u7684\u5b9d\u5251\u8361\u5f00\uff0c\u5415\u56db\u5a18\u5df2\u98de\u4e0a\u5bf9\u9762\u5047\u5c71\uff0c\u53ea\u8981\u518d\u8dc3\uff0c\u5c31\u53ef\u4ee5\u98de\u51fa\u5973\u5899\u4e0a\u3002\u5c31 \u5728\u6b64\u65f6\uff0c\u97e9\u91cd\u5c71\u4e00\u58f0\u602a\u5578\uff0c\u4e94\u53e3\u98de\u94b1\uff0c\u4e00\u9f50\u51fa\u624b\uff01 \u3000\u3000\u5415\u56db\u5a18\u542c\u5f97\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u53c8\u5feb\u53c8\u75be\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u5c06\u971c\u534e\u5b9d\u5251\u76d8\u5934\u4e00\u626b\uff0c\u90a3\u4e94\u628a\u98de\u94b1\u90fd \u5728\u79bb\u5934\u9876\u4e09\u5c3a\u4ee5\u4e0a\u98de\u8fc7\uff0c\u5b9d\u5251\u6ca1\u6709\u78b0\u7740\u3002\u5415\u56db\u5a18\u5947\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u51c6\u5934\u4f55\u4ee5\u5982\u6b64\u4e4b\u5dee\uff1f\u201d\u5fc3\u4e2d \u7591\u8651\uff0c\u4f46\u5f62\u52bf\u7d27\u8feb\uff0c\u9003\u8d70\u7684\u673a\u4f1a\u7a0d\u7eb5\u5373\u901d\uff0c\u5415\u56db\u5a18\u4e0d\u6687\u601d\u7d22\uff0c\u98de\u94b1\u521a\u4ece\u5934\u9876\u98de\u8fc7\uff0c\u5979\u5c31\u811a\u70b9 \u5047\u5c71\u5c16\u77f3\uff0c\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u201c\u4e00\u9e64\u51b2\u5929\u201d\uff0c\u76f4\u5411\u5973\u5899\u98de\u53bb\u3002 \u3000\u3000\u90a3\u77e5\u8eab\u5b50\u521a\u521a\u8dc3\u8d77\uff0c\u90a3\u4e94\u628a\u98de\u94b1\u5ffd\u7136\u4e00\u9f50\u6389\u5934\uff0c\u98de\u4e86\u8f6c\u6765\uff0c\u4e00\u628a\u5728\u4e0a\uff0c\u770b\u6765\u52bf\u4e43\u662f\u53d6\u4e0a \u76d8\u989d\u5934\uff1b\u4e00\u628a\u5728\u4e0b\uff0c\u770b\u6765\u52bf\u4e43\u662f\u8981\u524a\u4e0b\u76d8\u53cc\u8db3\uff1b\u8fd8\u6709\u4e00\u628a\u5728\u53f3\u4e00\u628a\u5728\u5de6\uff0c\u770b\u6765\u52bf\u662f\u8981\u524a\u5415\u56db \u5a18\u53cc\u81c2\uff1b\u8fd8\u6709\u4e00\u628a\u7a7f\u5fc3\u98de\u6765\uff0c\u52bf\u66f4\u51f6\u731b\uff01\u4e94\u628a\u98de\u94b1\uff0c\u53ea\u8981\u4e2d\u4e86\u4e00\u628a\uff0c\u5c31\u7b97\u4e0d\u4f24\u6027\u547d\uff0c\u4e5f\u5f97\u65ad \u4f53\u6b8b\u80a2\uff01 \u3000\u3000\u8fd9\u201c\u5bd2\u5149\u98de\u94b1\u201d\u548c\u201c\u56de\u73af\u94a9\u201d\u90fd\u662f\u97e9\u91cd\u5c71\u7684\u6210\u540d\u6697\u5668\uff0c\u201c\u56de\u73af\u94a9\u201d\u80fd\u591f\u56de\u65cb\u8f6c\u6298\u5df2\u662f\u6c5f \u6e56\u4e0a\u7f55\u89c1\u7684\u5947\u95e8\u6697\u5668\uff1b\u201c\u5bd2\u5149\u98de\u94ec\u201d\u80fd\u4ee5\u7529\u624b\u6cd5\u6389\u5934\u5206\u88ad\uff0c\u66f4\u662f\u9632\u4e0d\u80dc\u9632\uff01\u97e9\u91cd\u5c71\u4e94\u62d4\u9f50 \u98de\uff0c\u9884\u6599\u5415\u56db\u5a18\u5fc5\u96be\u9003\u8fc7\uff0c\u4e0d\u89c9\u54c8\u54c8\u5927\u7b11\uff0c\u4f46\u60f3\u5230\u5415\u56db\u5a18\u7f8e\u82e5\u5929\u4ed9\uff0c\u7ade\u5982\u6b64\u7389\u6b92\u9999\u9500\uff0c\u53c8\u4e0d \u89c9\u6697\u547c\u53ef\u60dc\uff01 \u3000\u3000\u5415\u56db\u5a18\u9aa4\u9022\u7edd\u9669\uff0c\u8eab\u5b50\u60ac\u7a7a\uff0c\u8fdb\u9000\u4e0d\u5f97\uff01\u5c31\u5728\u8fd9\u751f\u6b7b\u4fc4\u9877\u4e4b\u95f4\uff0c\u663e\u51fa\u4e86\u5979\u7684\u751f\u5e73\u7edd\u6280\uff01 \u53ea\u89c1\u5979\u4eba\u5728\u534a\u7a7a\uff0c\u6a2a\u5251\u5de6\u53f3\u4e00\u6321\uff0c\u53d6\u53cc\u81c2\u7684\u98de\u94b1\u53ee\u5f53\u4e00\u58f0\u5de6\u53f3\u98de\u5f00\uff1b\u9752\u950b\u4e00\u8f6c\uff0c\u5251\u67c4\u5012\u6301\uff0c \u5f80\u5916\u4e00\u9876\uff0c\u4e2d\u95f4\u90a3\u628a\u98de\u94b1\u4e5f\u76f4\u5c04\u51fa\u53bb\u3002\u4f46\u5934\u9876\u548c\u811a\u4e0b\u8fd8\u5404\u6709\u4e00\u628a\u98de\u94b1\uff0c\u4e07\u96be\u9003\u907f\uff0c\u5415\u56db\u5a18\u7a81 \u5c06\u8eab\u5b50\u4e00\u4fa7\uff0c\u53cc\u811a\u63d0\u8d77\uff0c\u8eab\u5b50\u51ed\u7a7a\u77ee\u4e86\u534a\u5c3a\uff0c\u4e24\u628a\u98de\u94b1\u547c\u7684\u4e00\u58f0\u5939\u5934\u5939\u811a\u98de\u8fc7\uff01\u5415\u56db\u5a18\u7adf\u7136 \u6beb\u53d1\u65e0\u4f24\uff0c\u98de\u4e0a\u5973\u5899\uff01 \u3000\u3000\u5415\u56db\u5a18\u521a\u521a\u677e\u4e86\u53e3\u6c14\uff0c\u8df3\u4e0b\u5973\u5899\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u545c\u545c\u4e4b\u58f0\u53c8\u5230\uff0c\u7591\u662f\u5de8\u62d4\uff0c\u4e0d\u6562\u524d\u8dc3\uff0c\u6025 \u628a\u971c\u534e\u5251\u5f80\u540e\u4e00\u626b\uff0c\u4f7f\u62db\u201c\u56de\u98ce\u626b\u67f3\u201d\u5411\u6697\u5668\u6765\u5904\u4e00\u626b\uff0c\u53ea\u89c1\u4e00\u67c4\u5f62\u5982\u66f2\u5c3a\u7684\u4e1c\u897f\u8dcc\u843d\u5730 \u4e0a\uff0c\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4f60\u6697\u5668\u867d\u7136\u5389\u5bb3\uff0c\u80fd\u5948\u6211\u4f55\uff1f\u201d\u8bdd\u58f0\u672a\u505c\uff0c\u7a81\u7136\u4e00\u80a1\u52b2\u98ce\uff0c\u8fce\u9762\u6251\u6765\uff0c \u7adf\u7136\u662f\u5929\u53f6\u6563\u4eba\u9661\u7136\u4ece\u6697\u9edd\u4e4b\u5904\u73b0\u8eab\uff0c\u4e00\u7167\u9762\u4fbf\u7528\u5927\u6454\u7891\u624b\u5288\u6765\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u8d31\u5a62\uff0c\u8fd8\u6709 \u6211\u5728\u8fd9\u91cc\u7167\u987e\u4f60\u5462\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u7eb5\u7136\u827a\u9ad8\u80c6\u5927\uff0c\u5251\u6cd5\u901a\u7384\uff0c\u8fd9\u65f6\u4e5f\u4e0d\u7531\u5f97\u5fc3\u5bd2\u6c14\u6cae\uff01\u5929\u53f6\u6563\u4eba\u7684\u529f\u529b\u8fd8\u5728\u4ed6\u5e08\u5144 \u97e9\u91cd\u5c71\u4e4b\u4e0a\uff0c\u800c\u4e14\u5415\u56db\u5a18\u53c8\u6000\u7591\u4ed6\u4eec\u662f\u5e03\u4e0b\u9677\u9631\uff0c\u4e0d\u77e5\u9664\u4e86\u5929\u53f6\u6563\u4eba\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u9ad8\u624b\u7aa5 \u4f3a\u5728\u65c1\uff1f \u3000\u3000\u5176\u5b9e\u5929\u53f6\u6563\u4eba\u5012\u4e0d\u662f\u9884\u5148\u57cb\u4f0f\uff0c\u800c\u662f\u627e\u5e08\u5144\u6765\uff0c\u4ed6\u542c\u5230\u5e08\u5144\u72ec\u95e8\u6697\u5668\u7684\u5636\u98ce\u4e4b\u58f0\uff0c\u5faa\u58f0 \u89c5\u8ff9\uff0c\u6070\u6070\u9047\u5230\u5415\u56db\u5a18\u5916\u95ef\uff0c\u4ed6\u6df1\u77e5\u5415\u56db\u5a18\u8f7b\u529f\u8d85\u5353\uff0c\u6240\u4ee5\u4e00\u7167\u9762\u4fbf\u7528\u5927\u6454\u7891\u624b\u628a\u5979\u9707\u9000\u51e0 \u6b65\uff0c\u4ee5\u5f85\u5e08\u5144\u6765\u5230\u800c\u6536\u5939\u51fb\u4e4b\u529f\u3002 \u3000\u3000\u5415\u56db\u5a18\u4e0d\u6562\u786c\u63a5\u654c\u4eba\u638c\u529b\uff0c\u679c\u7136\u6a2a\u8dc3\u4e09\u6b65\uff0c\u90a3\u6389\u5728\u5730\u4e0a\u7684\u6697\u5668\uff0c\u5ffd\u7136\u4e00\u9635\u7ffb\u817e\uff0c\u7a81\u7136\u522e \u5730\u76d8\u65cb\uff0c\u500f\u7136\u5411\u5415\u56db\u5a18\u53cc\u8db3\u65ab\u5230\uff01 \u3000\u3000\u8fd9\u6697\u5668\u6b63\u662f\u97e9\u91cd\u5c71\u7684\u201c\u56de\u73af\u94a9\u201d\uff0c\u5415\u56db\u5a18\u672a\u66fe\u89c1\u8fc7\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u51e0\u4e4e\u7ed9\u5b83\u94a9\u7740\uff01\u6025\u628a\u5251 \u5c16\u5f80\u4e0b\u4e00\u70b9\uff0c\u8eab\u5f62\u98de\u8d77\uff0c\u201c\u56de\u73af\u94a9\u201d\u5728\u5979\u811a\u4e0b\u545c\u545c\u98de\u8fc7\uff0c\u5929\u53f6\u6563\u4eba\u98de\u6b65\u8feb\u6765\uff1b\u547c\u547c\u4e24\u638c\u8fde\u73af \u5288\u5230\uff0c\u5415\u56db\u5a18\u8df3\u9ad8\u7eb5\u4f4e\uff0c\u817e\u632a\u95ea\u5c55\uff0c\u582a\u582a\u907f\u5f00\uff0c\u90a3\u56de\u73af\u94a9\u5728\u5899\u4e0a\u4e00\u78b0\uff0c\u53c8\u6298\u56de\u6765\u3002\u8fd9\u65f6\u97e9\u91cd \u5c71\u4ea6\u5df2\u8df3\u4e0b\u5973\u5899\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u7528\u638c\u529b\u9707\u98de\u5979\u7684\u5b9d\u5251\uff01\u201d\u624b\u4e00\u626c\uff0c\u6700\u540e\u4e24\u628a\u98de\u94b1\u98de \u51fa\uff0c\u800c\u4e14\u98de\u51fa\u65f6\u7528\u4e86\u6781\u5176\u9634\u6bd2\u7684\u624b\u6cd5\uff0c\u4e00\u628a\u98de\u94b1\u7528\u5e73\u65f6\u53d1\u6697\u5668\u7684\u624b\u6cd5\uff0c\u8ff3\u53d6\u540e\u5fc3\uff0c\u53e6\u4e00\u628a\u5374 \u7528\u7529\u624b\u6cd5\uff0c\u98de\u51fa\u4e4b\u540e\u80fd\u591f\u6389\u5934\uff0c\u4e24\u628a\u98de\u94b1\u4e4b\u95f4\u8fd8\u5939\u4e86\u4e00\u679d\u4e03\u715e\u9488\u3002 \u3000\u3000\u5415\u56db\u5a18\u907f\u5f00\u5929\u53f6\u6563\u4eba\u638c\u529b\uff0c\u90a3\u56de\u73af\u94a9\u5148\u5230\uff0c\u5415\u56db\u5a18\u4e00\u542c\u98ce\u58f0\uff0c\u77e5\u5b83\u98de\u817e\u4e09\u6298\u4e4b\u540e\uff0c\u4f59\u52bf \u5df2\u8870\uff0c\u971c\u534e\u5251\u6a2a\u91cc\u4e00\u5288\uff0c\u628a\u56de\u73af\u94a9\u524a\u6210\u4e24\u622a\uff0c\u56de\u73af\u94a9\u8dcc\u5728\u5730\u4e0b\u51e0\u81ea\u76d8\u65cb\u4e0d\u5df2\uff0c\u5374\u518d\u4e5f\u98de\u4e0d\u8d77 \u6765\u3002\u5415\u56db\u5a18\u51dd\u8eab\u4e0d\u52a8\uff0c\u4ed7\u5251\u62a4\u8eab\uff0c\u60f3\u7b49\u90a3\u98de\u94b1\u6389\u5934\u98de\u56de\u4e4b\u65f6\uff0c\u518d\u7528\u5b9d\u5251\u524a\u5b83\uff0c\u90a3\u6599\u53d6\u540e\u5fc3\u90a3 \u628a\u98de\u94b1\u5374\u662f\u5e73\u5e38\u6697\u5668\u7684\u6253\u6cd5\uff0c\u53c8\u75be\u53c8\u51c6\uff0c\u5415\u56db\u5a18\u84e6\u542c\u5f97\u6697\u5668\u5636\u98ce\u4e4b\u58f0\uff0c\u98de\u94b1\u5df1\u5230\u80cc\u540e\uff0c\u767e\u5fd9 \u4e2d\u53cd\u5251\u4e00\u62cd\uff0c\u521a\u628a\u90a3\u628a\u98de\u94b1\u62cd\u843d\uff0c\u524d\u5934\u90a3\u628a\u98de\u94b1\u5df2\u95ea\u7535\u80a1\u98de\u56de\uff0c\u5415\u56db\u5a18\u56de\u5251\u4e00\u6311\uff0c\u5929\u53f6\u6563\u4eba \u9661\u7136\u5927\u559d\u4e00\u58f0\uff0c\u8df3\u5230\u79bb\u5415\u56db\u5a18\u4e08\u4f59\u4e4b\u5730\uff0c\u8fd0\u8db3\u5185\u5bb6\u771f\u529b\uff0c\u9065\u53d1\u4e00\u638c\uff0c\u5415\u56db\u5a18\u5251\u950b\u7adf\u7ed9\u9707\u6b6a\uff0c \u90a3\u628a\u98de\u94b1\u5728\u5b9d\u5251\u5200\u53e3\u4e0a\u4e00\u64e6\uff0c\u659c\u5207\u4e0b\u6765\uff0c\u5415\u56db\u5a18\u8e0a\u8eab\u4e00\u8df3\uff0c\u7a81\u7136\u5c0f\u817f\u4e00\u9635\u5267\u75db\uff0c\u90a3\u679d\u4e03\u715e\u9488 \u5df2\u5c04\u5165\u8089\uff01\u97e9\u91cd\u5c71\u8fde\u7528\u4e09\u79cd\u5947\u95e8\u6697\u5668\uff0c\u52a0\u4e0a\u5929\u53f6\u6563\u4eba\u638c\u529b\uff0c\u7ec8\u4e8e\u4ee4\u5415\u56db\u5a18\u5403\u4e86\u5927\u4e8f\uff01 \u3000\u3000\u8463\u5de8\u5ddd\u548c\u53f6\u6a2a\u6ce2\u8fd9\u65f6\u4e5f\u7d27\u968f\u97e9\u91cd\u5c71\u4e4b\u540e\uff0c\u8df3\u4e0b\u5973\u5899\u3002\u5415\u56db\u5a18\u4e00\u9635\u5fc3\u60ca\uff0c\u6697\u9053\uff1a\u4e0d\u9053\u6211\u4eca \u65e5\u547d\u4e27\u4e8e\u6b64\u3002\u9661\u7136\u60f3\u8d77\u56fd\u4ec7\u672a\u62a5\uff0c\u5bb6\u6068\u96be\u5fd8\uff0c\u7231\u4fa3\u75c5\u69bb\u7f20\u7ef5\uff0c\u826f\u670b\u8fdc\u65b9\u671f\u671b\uff0c\u84e6\u7136\u95f4\u52c7\u6c14\u5927 \u589e\uff0c\u60f3\u9053\uff1a\u201c\u6211\u7edd\u4e0d\u80fd\u5c31\u6b64\u6b7b\u53bb\uff01\u201d\u5fcd\u7740\u75bc\u75db\uff0c\u971c\u534e\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u76f4\u5982\u9e70\u96bc\u7a7f\u6797\uff0c\u5de8\u9e1f\u63a0 \u6ce2\uff0c\u7fe9\u7136\u4ece\u5929\u53f6\u6563\u4eba\u5de6\u4fa7\u7a7f\u51fa\uff0c\u5929\u53f6\u6563\u4eba\u89c1\u5979\u4e2d\u4e86\u6697\u5668\uff0c\u4ecd\u7136\u786c\u95ef\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u53cc\u638c\u4e00\u9634\u4e00 \u9633\uff0c\u5de6\u6309\u53f3\u51fb\uff0c\u5415\u56db\u5a18\u5251\u628a\u4e00\u6296\uff0c\u51fa\u624b\u5982\u7535\uff0c\u5251\u5c16\u500f\u7684\u4ece\u4e24\u638c\u865a\u888d\u7684\u5f27\u5f62\u4e2d\u76f4\u523a\u8fdb\u6765\uff0c\u6307\u5411 \u5929\u53f6\u6563\u4eba\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\uff0c\u8fd9\u65f6\u5929\u53f6\u6563\u4eba\u82e5\u7136\u53cc\u638c\u4e00\u5408\uff0c\u5415\u56db\u5a18\u6027\u547d\u96be\u4fdd\uff0c\u4f46\u5929\u53f6\u6563\u4eba\u4e5f \u6d3b\u4e0d\u6210\u3002\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u5239\u90a3\uff0c\u5929\u53f6\u6563\u4eba\u672c\u80fd\u7684\u541e\u80f8\u5438\u8179\uff0c\u5de6\u624b\u4e00\u6258\u5251\u628a\uff0c\u53f3\u624b\u53cc\u6307\u671d\u5415\u56db \u5a18\u624b\u8155\u4e00\u5212\uff0c\u534a\u653b\u534a\u5b88\uff0c\u5148\u89e3\u654c\u62db\uff0c\u5415\u56db\u5a18\u4e58\u52bf\u4e00\u4e2a\u4fa7\u8eab\uff0c\u5b9d\u5251\u4e00\u659c\uff0c\u523a\u5230\u5929\u53f6\u6563\u4eba\u808b\u4e0b\uff0c \u5929\u53f6\u6563\u4eba\u4e5f\u8feb\u5f97\u5fae\u4e00\u4fa7\u8eab\uff0c\u5415\u56db\u5a18\u5b9d\u5251\u4e00\u65cb\uff0c\u9661\u7136\u633d\u4e86\u6597\u5927\u7684\u4e00\u4e2a\u5251\u82b1\uff0c\u547c\u7684\u4e00\u58f0\u4ece\u5929\u53f6\u6563 \u4eba\u5934\u9876\u8dc3\u8fc7\uff0c\u53d1\u529b\u72c2\u5954\u3002\u5929\u53f6\u6563\u4eba\u5927\u6012\uff0c\u8ddf\u8e2a\u6025\u8d76\uff0c\u80cc\u540e\u97e9\u91cd\u5c71\u592b\u59bb\u548c\u8463\u5de8\u5ddd\u4e00\u4e2a\u63a5\u7740\u4e00 \u4e2a\uff0c\u4e5f\u7d27\u7d27\u8ffd\u6765\u3002 \u3000\u3000\u672c\u6765\u82e5\u8bba\u8f7b\u529f\u7684\u672c\u4e8b\uff0c\u5415\u56db\u5a18\u8981\u6bd4\u5929\u53f6\u6563\u4eba\u9ad8\u51fa\u4e00\u7b79\uff0c\u6bd4\u8d77\u97e9\u91cd\u5c71\u592b\u59bb\u548c\u8463\u5de8\u5ddd\u5219\u66f4\u8981 \u9ad8\u51fa\u8bb8\u591a\u3002\u4f46\u5979\u5c0f\u817f\u4e2d\u4e86\u97e9\u91cd\u5c71\u7684\u4e03\u715e\u9488\uff0c\u8f7b\u529f\u51cf\u5f31\uff0c\u65bd\u5c55\u90a3\u9646\u5730\u98de\u817e\u7684\u529f\u592b\uff0c\u4e0d\u514d\u5927\u53d7\u5f71 \u54cd\u3002 \u3000\u3000\u5415\u56db\u5a18\u5728\u524d\uff0c\u5929\u53f6\u6563\u4eba\u7b49\u56db\u4eba\u5728\u540e\uff0c\u98ce\u9a70\u7535\u9010\uff0c\u4e0d\u4e00\u523b\u5df2\u8ffd\u51fa\u676d\u5dde\u57ce\u5916\u3002\u5728\u57ce\u4e2d\u7ad9\u5c97\u7684 \u5175\u571f\uff0c\u4f46\u89c1\u51e0\u56e2\u767d\u5f71\u631f\u98ce\u800c\u8fc7\uff0c\u8fde\u662f\u7537\u662f\u5973\u90fd\u770b\u4e0d\u6e05\u695a\uff0c\u66f4\u4e0d\u8981\u8bf4\u80fd\u591f\u62e6\u622a\u4e86\u3002 \u3000\u3000\u8d77\u521d\u534a\u4e2a\u65f6\u8fb0\uff0c\u5415\u56db\u5a18\u8fd8\u80fd\u52c9\u529b\u652f\u6491\uff0c\u548c\u5929\u53f6\u6563\u4eba\u4fdd\u6301\u4e94\u4e08\u5f00\u5916\u7684\u8ddd\u79bb\uff0c\u5176\u4ed6\u97e9\u91cd\u5c71\u7b49 \u4e09\u4eba\u5219\u66f4\u843d\u5728\u5341\u4f59\u4e08\u540e\u3002\u8fc7\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5415\u56db\u5a18\u7684\u817f\u8d8a\u6765\u8d8a\u75db\uff0c\u5929\u53f6\u6563\u4eba\u4e58\u52bf\u53d1\u529b\uff0c\u79bb\u5415\u56db \u5a18\u5df2\u4e0d\u5230\u4e09\u4e08\u3002 \u3000\u3000\u5415\u56db\u5a18\u542c\u80cc\u540e\u6b65\u58f0\uff0c\u8d8a\u6765\u8d8a\u8fd1\u3002\u5fc3\u60f3\u81ea\u5df1\u5df1\u5c3d\u529b\u6c42\u751f\uff0c\u770b\u6765\u662f\u4ecd\u662f\u96be\u9003\uff0c\u4e0d\u5982\u4e0e\u4ed6\u4eec\u51b3 \u4e00\u6b7b\u6218\u3002\u867d\u7136\u77e5\u9053\u6b64\u65f6\u6b64\u9645\uff0c\u53ea\u5929\u53f6\u6563\u4e00\u4eba\u81ea\u5df1\u5df2\u96be\u5bf9\u4ed8\uff0c\u4f46\u4e0e\u5176\u88ab\u8fb1\uff0c\u4f55\u5982\u6b7b\u6218\uff0c\u628a\u5fc3\u4e00 \u6a2a\uff0c\u7a81\u7136\u811a\u6b65\u4e00\u7f13\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u5929\u53f6\u6563\u4eba\u4e0d\u6599\u5979\u6709\u6b64\u4e00\u7740\uff0c\u6b63\u81ea\u72c2\u8ffd\uff0c\u51e0\u4e4e\u7ed9\u5979\u53cd\u624b\u5251\u7a7f\u5fc3 \u800c\u8fc7\uff0c\u6025\u5fd9\u4e00\u4e2a\u5012\u7ffb\uff0c\u907f\u5f00\u5251\u950b\uff0c\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\u3002\u5415\u56db\u5a18\u6b64\u4e00\u7a81\u51fb\uff0c\u5c45\u7136\u6536\u6548\uff0c\u5fc3\u4e2d\u4e00\u559c\uff0c \u5fcd\u7740\u75bc\u75db\uff0c\u7edd\u5c18\u98de\u5954\uff01 \u3000\u3000\u5929\u53f6\u6563\u4eba\u5b9a\u4e86\u5fc3\u795e\uff0c\u559d\u9053\uff1a\u201c\u8d3c\u5a62\u6562\u5c14\uff01\u201d\u7d27\u8ffd\u4e0d\u820d\uff0c\u53c8\u8fc7\u4e86\u7247\u523b\uff0c\u5415\u56db\u5a18\u5c0f\u817f\u80bf\u75db\uff0c \u4e00\u542c\u6b65\u58f0\uff0c\u5929\u53f6\u6563\u4eba\u8ddd\u79bb\u66f4\u8fd1\uff0c\u5df2\u5230\u4e86\u4e8c\u4e08\u4e4b\u5185\uff01\u5415\u56db\u5a18\u53c8\u65bd\u524d\u6cd5\uff0c\u500f\u7136\u51dd\u8eab\u6b62\u6b65\uff0c\u53cd\u624b\u4e00 \u5251\uff0c\u90a3\u77e5\u6b64\u6b21\u5929\u53f6\u6563\u4eba\u5df2\u6709\u9632\u5907\uff0c\u8fd0\u8db3\u638c\u529b\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u75be\u53d1\uff0c\u5415\u56db\u5a18\u8eab\u5f62\u4e00\u505c\uff0c\u9661\u89c9 \u52b2\u98ce\u8d2f\u80f8\uff0c\u5728\u7b4b\u75b2\u529b\u7aed\u4e4b\u9645\uff0c\u7ed9\u4e00\u638c\u98ce\u9707\u5f97\u7acb\u8db3\u4e0d\u4f4f\uff0c\u76f4\u649e\u5165\u8def\u65c1\u6811\u6797\uff0c\u4e00\u8de4\u8dcc\u5012\uff0c\u6b63\u5728\u5371 \u6025\uff0c\u5ffd\u7136\u7ed9\u4eba\u4e00\u5e26\u4e00\u63b7\uff0c\u8033\u8fb9\u542c\u5f97\u4e00\u58f0\u201c\u963f\u5f25\u9640\u4f5b\uff01\u201d\u7741\u5f00\u773c\u65f6\u81ea\u5df1\u5df2\u5b89\u7136\u7acb\u5728\u5730\u4e0a\u3002\u8fd9\u4eba \u7684\u63b7\u6cd5\uff0c\u6070\u5230\u597d\u5904\uff0c\u5c31\u5982\u7ed9\u4eba\u63d0\u7740\uff0c\u8f7b\u8f7b\u653e\u4e0b\u4e00\u6837\u3002 \u3000\u3000\u518d\u8bf4\u5929\u53f6\u6563\u4eba\u89c1\u5415\u56db\u5a18\u7ed9\u81ea\u5df1\u638c\u529b\u9707\u5012\uff0c\u5fc3\u4e2d\u72c2\u559c\uff0c\u62a2\u5165\u6811\u6797\u3002\u5ffd\u542c\u4e00\u58f0\u201c\u963f\u5f25\u9640 \u4f5b\u201d\uff0c\u53ea\u4f3c\u6709\u4eba\u5c31\u5728\u8033\u8fb9\u5531\u9053\uff1a\u201c\u5f97\u653e\u624b\u65f6\u987b\u653e\u624b\uff0c\u5f97\u9976\u4eba\u5904\u4e14\u9976\u4eba\uff01\u201d\u4e00\u5b57\u4e00\u53e5\uff0c\u6781\u5176\u6e05 \u5cfb\uff01\u5929\u53f6\u6563\u4eba\u5de6\u638c\u6a2a\u80f8\u62a4\u8eab\uff0c\u53f3\u638c\u534a\u4f38\u5fa1\u654c\uff0c\u5b9a\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u6e05\u7626\u548c\u5c1a\uff0c\u8eab\u7a7f\u6708\u767d\u50e7 \u888d\uff0c\u811a\u767b\u53cc\u8033\u9ebb\u978b\uff0c\u624b\u63d0\u62c2\u5c18\uff0c\u8155\u4e0a\u6302\u7740\u4e00\u4e32\u4f5b\u73e0\uff0c\u62e6\u5728\u81ea\u5df1\u9762\u524d\uff0c\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u5c11\u6797\u5bfa \u7684\u76d1\u5bfa\u672c\u65e0\u5927\u5e08\uff01 \u3000\u3000\u5929\u53f6\u6563\u4eba\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u5728\u5d69\u5c71\u5c11\u6797\u5bfa\u65f6\u4ed6\u5df2\u89c1\u8bc6\u8fc7\u672c\u65e0\u5927\u5e08\u7684\u672c\u9886\uff0c\u4e0d\u6562\u901e\u5f3a\u3002\u672c\u65e0 \u5927\u5e08\u62c2\u5c18\u4e00\u6302\uff0c\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u6563\u4eba\u522b\u6765\u65e0\u6059\u3002\u201d\u5929\u53f6\u6563\u4eba\u8fd8\u4e86\u4e00\u672d\uff0c\u4e5f\u9053\uff1a\u201c\u7985\u5e08\u6cd5\u4f53\u5b89 \u5eb7\uff01\u201d\u672c\u65e0\u9053\uff1a\u201c\u6258\u5e87\u5c1a\u597d\uff0c\u6563\u4eba\u4e00\u6d3e\u5b97\u4e3b\uff0c\u4e5f\u6709\u7a7a\u5230\u676d\u5dde\u73a9\u6c34\u6e38\u5c71\u4e48\uff1f\u201d\u672c\u65e0\u7985\u5e08\u662f\u660e\u77e5 \u6545\u95ee\uff0c\u5929\u53f6\u6563\u4eba\u9762\u4e0a\u4e00\u7ea2\uff0c\u55eb\u55eb\u5685\u5685\uff0c\u6b32\u7b54\u975e\u7b54\uff0c\u97e9\u91cd\u5c71\u592b\u59bb\u548c\u8463\u5de8\u5ddd\u4e09\u4eba\u5df2\u7136\u8d76\u5230\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u5374\u672a\u89c1\u8fc7\u672c\u65e0\u5927\u5e08\uff0c\u89c1\u5929\u53f6\u6563\u4eba\u548c\u4e00\u4e2a\u548c\u5c1a\u65bd\u793c\u95ee\u7b54\uff0c\u9887\u4e3a\u606d\u8c28\uff0c\u800c\u5415\u56db\u5a18\u5c31\u7ad9 \u5728\u65c1\u8fb9\uff0c\u4e0d\u7981\u60ca\u5f02\u3002\u97e9\u91cd\u5c71\u9053\uff1a\u201c\u5e08\u5f1f\u4e3a\u4f55\u4f4f\u624b\uff1f\u201d\u8f9f\u4e91\u9504\u4e00\u6446\u5c31\u5411\u5415\u56db\u5a18\u5954\u53bb\u3002\u672c\u65e0\u5927\u5e08 \u5ffd\u7136\u4e0a\u524d\u62e6\u4f4f\uff0c\u5408\u4ec0\u7b11\u9053\uff1a\u201c\u65bd\u4e3b\u4f55\u5fc5\u4e0e\u4e00\u4e2a\u5c0f\u5973\u5b50\u4e3a\u96be\uff0c\u770b\u8d2b\u50e7\u8584\u9762\uff0c\u9976\u4e86\u5979\u5427\uff01\u201d \u3000\u3000\u97e9\u91cd\u5c71\u6012\u9053\uff1a\u201c\u4f60\u7ba1\u5f97\u7740\u3002\u201d\u8f9f\u4e91\u9504\u626c\u7a7a\u4e00\u5288\u76f4\u51b2\u8fc7\u53bb\uff0c\u5929\u53f6\u6563\u4eba\u6025\u9053\uff1a\u201c\u4f7f\u4e0d\u5f97\uff01\u201d \u672c\u65e0\u5927\u5e08\u5fae\u5fae\u4e00\u7b11\uff0c\u62c2\u5c18\u4e00\u6325\uff0c\u5f80\u8f9f\u4e91\u9504\u4e0a\u4e00\u642d\uff0c\u97e9\u91cd\u5c71\u987f\u89c9\u4f3c\u6709\u5343\u65a4\u91cd\u7269\u76f4\u538b\u4e0b\u6765\uff0c\u8f9f\u4e91 \u9504\u7684\u53bb\u52bf\u7ade\u88ab\u963b\u4f4f\uff01\u5929\u53f6\u6563\u4eba\u9053\uff1a\u201c\u5e08\u5144\uff0c\u8fd9\u4f4d\u9ad8\u50e7\u662f\u5c11\u6797\u7684\u76d1\u5bfa\u672c\u65e0\u5927\u5e08\u3002\u201d\u97e9\u91cd\u5c71\u5403\u4e86 \u4e00\u60ca\uff0c\u672c\u65e0\u62c2\u5c18\u4e00\u677e\uff0c\u97e9\u91cd\u5c71\u5c06\u9504\u5934\u62bd\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u8fd9\u5973\u8d3c\u662f\u53db\u9006\u3002\u5415\u7559\u826f\u7684\u5b59\u5973\uff0c\u5927\u5e08 \u662f\u6709\u9053\u9ad8\u50e7\uff0c\u4e3a\u4f55\u62a4\u5979\uff1f\u201d \u3000\u3000\u672c\u65e0\u5927\u5e08\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u665a\u6751\u5148\u751f\u662f\u5426\u53db\u9006\u59d1\u7f6e\u4e0d\u8bba\uff0c\u4f46\u4e24\u4f4d\u662f\u6b66\u6797\u540d\u5bbf\uff0c\u4e00\u6d3e\u5b97 \u5e08\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u5230\u4e86\u516c\u95e8\u529e\u4e8b\uff0c\u53ef\u6709\u5730\u65b9\u5b98\u5e9c\u6240\u53d1\u7684\u516c\u6587\u6355\u5f15\u4e48\uff1f\u201d\u672c\u65e0\u660e\u77e5\u4ed6\u4eec\u5df2\u88ab\u56db \u7687\u5b50\u6240\u7528\uff0c\u5374\u6545\u610f\u95ee\u4ed6\uff0c\u51fa\u8bed\u4ea6\u6697\u5b58\u8ba5\u8bbd\uff0c\u8bd5\u60f3\u4ee5\u97e9\u91cd\u5c71\u5144\u5f1f\u7684\u8eab\u4efd\uff0c\u5982\u4f55\u80fd\u5728\u516c\u95e8\u5f53\u5dee\uff1f \u97e9\u91cd\u5c71\u5fc3\u4e2d\u6c14\u6012\uff0c\u4f46\u5374\u4e0d\u6562\u53d1\u4f5c\u3002 \u3000\u3000\u5415\u56db\u5a18\u6b47\u4e86\u4e00\u4f1a\uff0c\u6c14\u529b\u6e10\u6e10\u6062\u590d\uff0c\u4e00\u626c\u624b\u5c04\u51fa\u4e09\u679d\u54cd\u7bad\uff0c\u201c\u545c\uff0c\u545c\uff0c\u9e23\uff01\u201d\u4e09\u58f0\uff0c\u4e00\u58f0 \u957f\uff0c\u4e24\u58f0\u77ed\uff0c\u76f4\u4e0a\u9065\u7a7a\uff0c\u971c\u534e\u5251\u6a2a\u5728\u80f8\u524d\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5929\u53f6\u6563\u4eba\uff0c\u4f60\u6709\u5e08\u5144\uff0c\u6211\u4e5f\u6709\u5e08\u5144\u3002 \u4f60\u82e5\u60f3\u7fa4\u6bb4\uff0c\u6211\u4eec\u4ea6\u6709\u4eba\u63a5\u4f60\u3002\u4f60\u82e5\u8981\u5355\u6253\u72ec\u6597\uff0c\u5c31\u8bf7\u6307\u5b9a\u65e5\u671f\uff0c\u968f\u4f60\u5212\u51fa\u9053\u6765\uff0c\u6211\u4e00\u51c6\u5949 \u966a\u3002\u201d\u5929\u53f6\u6563\u4eba\u9762\u4e0a\u53d1\u70ed\uff0c\u5341\u5206\u5c34\u5c2c\u3002\u987b\u77e5\u5929\u53f6\u6563\u4eba\u5144\u5f1f\u6210\u540d\u591a\u5e74\uff0c\u5373\u4f7f\u4e0e\u5415\u56db\u5a18\u5355\u6253\u72ec \u6597\uff0c\u5df2\u6709\u4ee5\u5927\u538b\u5c0f\u4e4b\u5acc\uff0c\u600e\u80fd\u5728\u672c\u65e0\u5927\u5e08\u9762\u524d\uff0c\u5408\u56db\u4e2a\u4e00\u6d41\u9ad8\u624b\u4e4b\u529b\uff0c\u8054\u624b\u6597\u5979\uff1f \u3000\u3000\u97e9\u91cd\u5c71\u89c1\u5415\u56db\u5a18\u5c04\u51fa\u54cd\u7bad\uff0c\u77e5\u9053\u8fd9\u662f\u5979\u62db\u96c6\u540c\u95e8\u7684\u8baf\u53f7\u3002\u5fc3\u60f3\uff1a\u8fd9\u672c\u65e0\u8001\u79c3\uff0c\u540d\u4e0d\u865a \u4f20\uff0c\u521a\u624d\u6240\u9732\u90a3\u624b\u529f\u592b\uff0c\u975e\u540c\u5c0f\u53ef\u3002\u5415\u56db\u5a18\u8fd9\u8d3c\u5a62\u867d\u7136\u53d7\u4f24\uff0c\u4f46\u4ecd\u582a\u4e00\u6218\u3002\u6211\u4eec\u56db\u4eba\u6597\u4ed6\u4eec \u4e8c\u4eba\u5df2\u672a\u5fc5\u80fd\u80dc\uff1b\u82e5\u7518\u51e4\u6c60\u767d\u6cf0\u5b98\u518d\u4e00\u8d76\u6765\uff0c\u90a3\u5c31\u5fc5\u7136\u843d\u8d25\u3002\u672c\u65e0\u7985\u5e08\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u56db\u5a18\uff0c\u5728\u524d\u8f88\u9762\u524d\uff0c\u4f11\u8981\u901e\u5f3a\uff01\u51a4\u5bb6\u5b9c\u89e3\u4e0d\u5b9c\u7ed3\uff0c\u4f60\u4eec\u4e4b\u95f4\u53c8\u6ca1\u6709\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\uff0c\u4f55\u5fc5\u7ea6 \u4f1a\u6bd4\u6b66\uff1f\u4f9d\u8001\u8872\u4e4b\u89c1\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u4e0d\u5982\u4e24\u4f5c\u7f62\u4f11\uff0c\u5404\u6563\u4e86\u5427\uff01\u201d\u672c\u65e0\u4e4b\u8a00\uff0c\u660e\u4f3c\u8d23\u5907\u5415\u56db \u5a18\uff0c\u5b9e\u662f\u8d23\u5907\u5929\u53f6\u6563\u4eba\u5144\u5f1f\u3002\u672c\u65e0\u7985\u5e08\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\u4e0e\u6613\u5170\u73e0\u4e00\u6837\uff0c\u8f88\u4efd\u6781\u5c0a\uff0c\u97e9\u91cd\u5c71\u4e0e \u5929\u53f6\u6563\u4eba\u6bd4\u4ed6\u5c1a\u77ee\u534a\u8f88\u3002\u6b64\u8a00\u4e00\u51fa\uff0c\u5929\u53f6\u6563\u4eba\u9996\u5148\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u656c\u4f9d\u5927\u5e08\u4e4b\u547d\u3002\u201d\u97e9\u91cd\u5c71 \u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\u4f5c\u7f62\uff0c\u4ee5\u540e\u4e4b\u4e8b\u518d\u63d0\u3002\u201d\u672c\u65e0\u7985\u5e08\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u8d2b\u50e7\u4e0d\u7ba1\uff01\u201d \u3000\u3000\u97e9\u91cd\u5c71\u7b49\u56db\u4eba\u53bb\u540e\uff0c\u672c\u65e0\u7985\u5e08\u9053\uff1a\u201c\u4f60\u4e5f\u771f\u5927\u80c6\uff0c\u600e\u4e48\u72ec\u6297\u8fd9\u56db\u4e2a\u9b54\u5934\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a \u201c\u8fd9\u662f\u8feb\u4e8e\u65e0\u5948\u3002\u201d\u628a\u524d\u4e8b\u8bf4\u4e86\u3002\u672c\u65e0\u7985\u5e08\u53f9\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u4e0d\u5e78\uff0c\u51fa\u4e86\u738b\u5c0a\u4e00\u8fd9\u4e2a\u53db\u5f92\uff0c\u7d2f \u4f60\u4eec\u6c5f\u6e56\u4fa0\u58eb\u53d7\u4e86\u8bb8\u591a\u707e\u96be\u3002\u201d\u738b\u5c0a\u4e00\u5373\u662f\u56db\u7687\u5b50\u5141\u796f\u7684\u5316\u540d\uff0c\u672c\u65e0\u7985\u5e08\u53eb\u60ef\u4e86\uff0c\u867d\u7136\u5df2\u77e5 \u4ed6\u5373\u5141\u796f\uff0c\u4ecd\u7136\u4e0d\u60ef\u6539\u53e3\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u5c31\u7b97\u5141\u796f\u4e0d\u6295\u8d35\u6d3e\u95e8\u4e0b\uff0c\u4e5f\u4e00\u6837\u8981\u4e0e\u6211\u4eec\u4e3a \u96be\u3002\u8fd9\u4e0d\u5173\u5c11\u6797\u4e4b\u4e8b\uff0c\u5927\u5e08\u4e0d\u8981\u96be\u8fc7\u3002\u201d\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u542c\u545c\u545c\u54cd\u7bad\u4e4b\u58f0\uff0c\u4e24\u957f\u4e00\u77ed\uff0c\u5415\u56db\u5a18 \u6b22\u7136\u8bf4\u9053\uff1a\u201c\u7518\u4e03\u54e5\u4ed6\u4eec\u6765\u4e86\uff01\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u7518\u51e4\u6c60\u4e0e\u767d\u6cf0\u5b98\u679c\u7136\u6765\u5230\u3002\u4ed6\u4eec\u89c1\u5415\u56db\u5a18\u5f62\u5bb9\u6194\u60b4\uff0c\u5403\u4e86\u4e00\u60ca\uff1b\u89c1\u672c\u65e0\u5927\u5e08 \u5728\u65c1\uff0c\u53c8\u662f\u4e00\u559c\u3002\u5415\u56db\u5a18\u5c06\u672c\u65e0\u5927\u5e08\u76f8\u6551\u4e4b\u4e8b\u8bf4\u4e86\u3002\u7518\u767d\u4e8c\u4eba\u6025\u5fd9\u62f1\u624b\u9053\u8c22\u3002\u7518\u51e4\u6c60\u9053\uff1a \u201c\u516b\u59b9\u7684\u4f24\u600e\u6837\uff1f\u201d\u5415\u56db\u5a18\u628a\u624b\u5728\u4f24\u5904\u4e00\u6307\uff0c\u7b11\u9053\uff1a\u201c\u5e78\u597d\u4ed6\u7684\u6697\u5668\u65e0\u6bd2\u3002\u201d\u767d\u6cf0\u5b98\u9053\uff1a \u201c\u4ec0\u4e48\u6697\u5668\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e00\u679a\u5c0f\u5c0f\u7684\u94f6\u9488\u3002\u201d\u767d\u6cf0\u5b98\u662f\u6253\u6885\u82b1\u9488\u7684\u80fd\u624b\uff0c\u9053\uff1a\u201c\u82e5\u7136\u65e0 \u6bd2\uff0c\u90a3\u4fbf\u597d\u529e\uff0c\u53ea\u8981\u525c\u5f00\u4f24\u53e3\uff0c\u7528\u78c1\u77f3\u628a\u5b83\u5438\u51fa\u6765\u4fbf\u662f\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u672c\u65e0\u5927\u5e08\u4e0b\u5c71\u4f55 \u4e8b\uff1f\u201d\u672c\u65e0\u9053\uff1a\u201c\u6211\u6709\u4e00\u4e2a\u5f92\u5f1f\u5728\u8427\u5c71\u53bf\u6148\u6069\u5bfa\u5f53\u4e3b\u6301\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u554a\uff01\u90a3\u662f\u5370\u5b8f\u5e08\u5144 \u4e86\uff1f\u4ed6\u548c\u6211\u4eec\u7684\u8def\u5e08\u5144\u6700\u4e3a\u76f8\u5f97\u3002\u6211\u4eec\u65e5\u524d\u66fe\u5230\u8427\u5c71\uff0c\u672c\u6765\u8981\u53bb\u627e\u4ed6\uff0c\u53ef\u60dc\u4e00\u8fde\u78b0\u5230\u610f\u5916\u4e4b \u4e8b\uff0c\u8fd8\u672a\u5f97\u4e0e\u4ed6\u89c1\u9762\u3002\u201d\u672c\u65e0\u7985\u5e08\u9053\uff1a\u201c\u5e78\u597d\u7518\u5927\u4fa0\u6ca1\u6709\u627e\u4ed6\uff0c\u82e5\u53bb\u627e\u4ed6\uff0c\u90a3\u662f\u767d\u884c\u4e00 \u8d9f\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4ed6\u4e0d\u5728\u8427\u5c71\u4e86\uff1f\u201d\u672c\u65e0\u9053\uff1a\u201c\u4ed6\u5df2\u88ab\u6d59\u8859\u9ad8\u624b\u6349\u53bb\u4e86\u3002\u542c\u8bf4\u662f\u6d89 \u5acc\u7ed9\u8def\u4fa0\u58eb\u9001\u4fe1\u3002\u201d\u7518\u51e4\u6c60\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u60f3\u8d77\u90a3\u65e5\u5728\u4ed9\u971e\u5cad\u6240\u542c\u89c1\u7684\u6fc0\u6597\u4e4b\u58f0\uff0c\u4e0e\u7559\u4e0b\u7684 \u90a3\u5f20\u753b\uff0c\u60f3\u6765\u88ab\u6355\u53bb\u7684\u4eba\u5b9a\u662f\u5370\u5b8f\u548c\u5c1a\u3002\u4fbf\u9053\uff1a\u201c\u5370\u5b8f\u6cd5\u5e08\u4e3a\u6211\u4eec\u8def\u5e08\u5144\u800c\u906d\u7f27\u7ec3\u4e4b\u707e\uff0c\u5927 \u5e08\u82e5\u6709\u8981\u6211\u4eec\u5144\u5f1f\u6548\u529b\u4e4b\u5904\uff0c\u5c3d\u7ba1\u5429\u5490\u3002\u201d\u672c\u65e0\u5927\u5e08\u7b11\u9053\uff0c\u201c\u73b0\u5728\u65e0\u9700\u3002\u6211\u6253\u7b97\u53bb\u5411\u5e74\u7fb9\u5c27 \u8981\u4eba\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u5947\u9053\uff1a\u201c\u600e\u4e48\u95ee\u5e74\u7fb9\u5c27\u8981\u4eba\u3002\u201d\u672c\u65e0\u5927\u5e08\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u8fd9\u5b69\u5b50\u73b0\u5728\u6296\u8d77\u6765\u4e86\uff0c\u6211 \u6253\u542c\u5f97\u4ed6\u81ea\u798f\u5efa\u7387\u519b\u56de\u4eac\uff0c\u4eca\u65e5\u4fbf\u5230\u676d\u5dde\u3002\u6d59\u629a\u8981\u5c06\u4e00\u6279\u671d\u5ef7\u7684\u94a6\u72af\u548c\u7591\u72af\u90fd\u4ea4\u4e0e\u4ed6\u3002\u6211\u660e \u65e5\u5c31\u770b\u4ed6\u53bb\u3002\u201d\u7518\u51e4\u6c60\u6025\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8fd9\u53ef\u8981\u4e09\u601d\u800c\u884c\uff01\u201d\u672c\u65e0\u9053\uff1a\u201c\u7518\u5927\u4fa0\u6709\u4f55\u9ad8\u89c1\uff1f\u201d \u7518\u51e4\u6c60\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u65e2\u7136\u505a\u4e86\u6e05\u671d\u7684\u5c06\u519b\uff0c\u53ea\u6015\u5bf9\u5927\u5e08\u4e0d\u5229\u3002\u201d\u672c\u65e0\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u8fd9\u5b69\u5b50\u6211 \u81ea\u5c0f\u770b\u4ed6\u957f\u5927\uff0c\u4ed6\u7684\u7f57\u6c49\u62f3\u6cd5\u8fd8\u662f\u6211\u4eb2\u81ea\u6240\u4f20\uff0c\u8c05\u4ed6\u4e0d\u6562\u5bf9\u6211\u65e0\u793c\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8fd8\u662f\u5c0f \u5fc3\u7684\u597d\u3002\u201d\u672c\u65e0\u53f9\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u5929\u751f\u9896\u5f02\uff0c\u662f\u5343\u767e\u5e74\u6765\u96be\u5f97\u4e00\u89c1\u7684\u4eba\u6750\uff0c\u5c31\u7b97\u4ed6\u53d8\u574f\u4e86\uff0c\u6211 \u4e5f\u8981\u4eb2\u81ea\u53bb\u770b\u4e00\u770b\uff0c\u770b\u4ed6\u574f\u5230\u4ec0\u4e48\u7a0b\u5ea6\uff01\u201d\u8981\u77e5\u672c\u65e0\u5927\u5e08\u5e74\u5df2\u516d\u65ec\u5f00\u5916\uff0c\u867d\u4e91\u52d8\u7834\u8272\u7a7a\uff0c\u4f46 \u8001\u5e74\u4eba\u7231\u5b69\u5b50\u7684\u672c\u6027\u5374\u751a\u4e3a\u5f3a\u70c8\uff0c\u5e74\u7fb9\u5c27\u5c0f\u65f6\uff0c\u4e00\u5e74\u4e2d\u6709\u534a\u5e74\u5728\u5c11\u6797\u5bfa\uff0c\u5c11\u6797\u4e09\u8001\u7279\u522b\u7231 \u4ed6\uff0c\u4e00\u534a\u56fa\u7136\u662f\u7531\u4e8e\u4ed6\u7684\u806a\u660e\u9896\u5f02\uff0c\u4e00\u534a\u4e5f\u662f\u56e0\u4e3a\u548c\u5c1a\u65e0\u59bb\u65e0\u5b50\uff0c\u5230\u4e86\u5e74\u8001\uff0c\u7279\u522b\u6b22\u559c\u5b69\u5b50 \u7684\u539f\u6545\u3002 \u3000\u3000\u7518\u51e4\u6c60\u5c1a\u5f85\u8fdb\u8a00\uff0c\u672c\u65e0\u5927\u5e08\u9e9d\u5c3e\u4e00\u62c2\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u518d\u8bf4\uff0c\u8d2a\u50e7\u867d\u7136\u5e74\u8001\u529b\u8870\uff0c\u5e74\u7fb9\u5c27\u90a3 \u70b9\u5175\u9a6c\uff0c\u4e5f\u8fd8\u672a\u5fc5\u80fd\u56f0\u5f97\u4f4f\u6211\uff01\u201d\u5c11\u6797\u4e09\u8001\u4e2d\uff0c\u672c\u65e0\u6700\u4e3a\u5f3a\u9879\uff0c\u706b\u6c14\u4e5f\u5927\u3002\u7518\u51e4\u6c60\u4e0d\u6562\u591a \u8bf4\uff0c\u4fbf\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4eec\u5728\u5bff\u660c\u4e66\u9662\u542c\u5019\u4f73\u97f3\u3002\u201d\u672c\u65e0\u5927\u5e08\u4e3e\u5c65\u65bd\u793c\uff0c\u5355\u8eab\u81ea\u53bb\u3002 \u3000\u3000\u674e\u536b\u63a5\u4e86\u5e74\u7fb9\u5c27\u548c\u4e86\u56e0\u8fdb\u5165\u676d\u57ce\uff0c\u4e86\u56e0\u542c\u5f97\u8def\u6c11\u77bb\u5df2\u88ab\u5415\u56db\u5a18\u6551\u53bb\uff0c\u5486\u54ee\u5982\u96f7\uff0c\u5e74\u7fb9\u5c27 \u5374\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4e00\u4e2a\u8def\u6c11\u77bb\u6709\u4ec0\u4e48\u8981\u7d27\uff1f\u5929\u4e0b\u90fd\u5728\u6211\u4eec\u638c\u63e1\u4e4b\u4e2d\uff0c\u4ed6\u7eb5\u9003\u53bb\uff0c\u4e5f\u505a\u4e0d\u51fa\u4ec0\u4e48 \u4e8b\u6765\u3002\u201d\u4e86\u56e0\u6012\u6c14\u7a0d\u89e3\uff0c\u4e0d\u4e45\u97e9\u91cd\u5c71\u548c\u5929\u53f6\u6563\u4eba\u6765\u89c1\uff0c\u62a5\u8bf4\u5c11\u6797\u76d1\u5bfa\u672c\u65e0\u5927\u5e08\u73b0\u8eab\u6b64\u5730\uff0c\u6551 \u4e86\u5415\u56db\u5a18\u4e4b\u4e8b\uff0c\u5e74\u7fb9\u5c27\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u8fd9\u8001\u5bb6\u4f19\u6700\u7231\u7406\u4eba\u95f2\u4e8b\u3002\u201d\u4e86\u56e0\u524d\u5728\u5c71\u4e1c\u94a6\u5dee\u884c \u7f72\uff0c\u66fe\u5403\u8fc7\u672c\u65e0\u7684\u4e8f\uff0c\u6b64\u6c14\u81f3\u4eca\u672a\u6d88\uff0c\u6012\u9053\uff1a\u201c\u4ed6\u82e5\u649e\u5728\u6211\u7684\u624b\u4e0a\uff0c\u6211\u8d70\u8981\u4ed6\u518d\u5403\u6211\u4e00 \u6756\u3002\u201d\u97e9\u91cd\u5c71\u5fc3\u4e2d\u6697\u7b11\uff0c\u5fc3\u60f3\uff1a\u4f60\u90a3\u7985\u6756\u672a\u5fc5\u5f3a\u5f97\u8fc7\u6211\u7684\u8f9f\u4e91\u9504\uff0c\u4f55\u5fc5\u80e1\u5439\uff01 \u3000\u3000\u5e74\u7fb9\u5c27\u548c\u4e86\u56e0\u3001\u97e9\u91cd\u5c71\u7b49\u4eba\u90fd\u662f\u65e7\u8bc6\uff0c\u4fbf\u9080\u4ed6\u4eec\u5230\u519b\u8425\u53bb\u4f4f\uff0c\u7545\u53d9\u8054\u6b22\u3002\u5f53\u65e5\u6d59\u629a\u674e\u536b \u4fbf\u5c06\u94a6\u72af\u4e00\u5341\u516b\u540d\u70b9\u4ea4\uff1b\u5e74\u7fb9\u5c27\u4e00\u770b\uff0c\u679c\u7136\u6709\u5370\u5b8f\u5728\u5185\uff0c\u5f53\u4e0b\u4e5f\u4e0d\u4f5c\u58f0\uff0c\u53eb\u526f\u5e05\u5cb3\u949f\u6dc7\u628a\u72af \u4eba\u62bc\u89e3\u56de\u8425\u3002\u81ea\u5df1\u548c\u674e\u536b\u5bd2\u6684\u4e00\u9635\uff0c\u540c\u4e86\u56e0\u7b49\u4eba\u544a\u9000\u3002 \u3000\u3000\u662f\u591c\u519b\u4e2d\u70b9\u8d77\u725b\u6cb9\u5de8\u70db\uff0c\u5927\u5bb4\u4e86\u56e0\u8fd9\u4e00\u73ed\u4eba\u3002\u4e86\u56e0\u7b49\u4eba\u90fd\u4ee5\u5e74\u7fb9\u5c27\u7684\u957f\u8f88\u81ea\u5c45\uff0c\u800c\u4eca\u89c1 \u4ed6\u6210\u4e86\u4e00\u519b\u4e3b\u5e05\uff0c\u53c8\u7fa1\u53c8\u5992\uff0c\u4e86\u56e0\u9053\uff1a\u201c\u8fd8\u662f\u5c0f\u5e74\u6709\u51fa\u606f\uff0c\u54b1\u4eec\u5c11\u8bfb\u5175\u4e66\uff0c\u5f04\u6765\u5f04\u53bb\u90fd\u53ea\u662f \u62ff\u5200\u5f04\u6756\u3002\u201d\u5e74\u7fb9\u5c27\u5fd9\u966a\u7b11\u9053\uff1a\u201c\u90a3\u91cc\u8bdd\u6765\uff0c\u5927\u5e08\u5c06\u6765\u8eab\u4e3a\u56fd\u5e08\uff0c\u90a3\u662f\u4f55\u7b49\u6e05\u8d35\uff01\u201d\u6bb7\u52e4\u529d \u9152\uff0c\u628a\u4e00\u5e2e\u4eba\u704c\u5f97\u9169\u914a\u5927\u9189\u3002 \u3000\u3000\u5e2d\u6563\u4e4b\u540e\uff0c\u5e74\u7fb9\u5c27\u56de\u5230\u81ea\u5df1\u5e10\u4e2d\uff0c\u542c\u5f97\u519b\u4e2d\u51fb\u9f13\uff0c\u5df2\u662f\u4e09\u66f4\u3002\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u5e10\u4e2d\u9694\u7740\u7684 \u4e00\u91cd\u5e18\u5b50\u62c9\u5f00\uff0c\u51af\u7433\u500f\u7684\u8df3\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u8fd9\u4eba\u662f\u4e2a\u9152\u5f92\uff0c\u559d\u5f97\u9189\u91ba\u91ba\u7684\uff0c\u5feb\u8d70\u5f00 \u4e00\u70b9\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b69\u5b50\u77e5\u9053\u4ec0\u4e48\uff1f\u6211\u4e0d\u559d\u9152\uff0c\u4f60\u4fbf\u8981\u88ab\u62bc\u56de\u7687\u5e9c\u3002\u201d\u51af\u7433\u201c\u5657 \u55e4\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u6bd4\u6211\u5927\u4e0d\u4e86\u591a\u5c11\uff0c\u4f60\u624d\u662f\u8bf4\u5b69\u5b50\u8bdd\u5462\uff0c\u4f60\u559d\u9152\u548c\u6211\u56de\u7687\u5e9c\u6709\u4ec0\u4e48\u5173 \u7cfb\uff1f\u201d\u5e74\u7fb9\u5c27\u201c\u5618\u201d\u58f0\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\u5728\u8fd9\u91cc\uff0c\u6211\u548c\u4ed6\u4eec\u559d\u9152\uff0c\u628a\u4ed6\u4eec\u90fd\u704c\u9189\u4e86\u3002\u201d\u51af\u7433\u773c \u775b\u6ef4\u6e9c\u6e9c\u7684\u8f6c\uff0c\u5ffd\u7136\u62cd\u624b\u7b11\u9053\uff1a\u201c\u5475\uff0c\u4f60\u771f\u806a\u660e\uff0c\u4f60\u8981\u628a\u4ed6\u95e8\u704c\u9189\u4e86\uff0c\u7136\u540e\u653e\u6211\u9003\u8d70\uff0c\u90a3\u4e48 \u676d\u5dde\u57ce\u4e2d\uff0c\u5c31\u6ca1\u6709\u4eba\u80fd\u6349\u5f97\u6211\u4e86\u3002\u201d\u8fc8\u6b65\u4fbf\u8d70\u3002\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u51af\u7433\u8f6c\u8fc7\u8eab\u6765\uff0c\u9053\uff1a \u201c\u4f60\u53c8\u4e0d\u60f3\u653e\u6211\u8d70\u4e86\u4e48\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u4f60\u7684\u7238\u7238\u5988\u5988\u5462\uff1f\u201d\u5e74\u7fb9\u5c27\u662f\u60f3\u8bd5 \u63a2\u5979\u5bf9\u81ea\u5df1\u8eab\u4e16\u77e5\u9053\u591a\u5c11\u3002\u5176\u5b9e\u5979\u7684\u6765\u5386\uff0c\u5e74\u7fb9\u5c27\u4e5f\u4e0d\u77e5\u9053\u3002\u53ea\u77e5\u9053\u5979\u662f\u5e08\u5085\u949f\u4e07\u5802\u5e26\u6765\u7684 \u5973\u5a03\u513f\uff0c\u51af\u7433\u4e00\u6115\uff0c\u7709\u5c16\u7d27\u8e59\uff0c\u9053\uff1a\u201c\u4ece\u6765\u6ca1\u4eba\u95ee\u6211\u8fd9\u8bdd\uff01\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u73b0\u5728\u6211\u5c31\u95ee \u4f60\uff01\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u95ee\u6211\u6211\u4e5f\u4e0d\u77e5\u9053\u5440\u3002\u8428\u4f2f\u4f2f\u8bf4\u6211\u81ea\u5e7c\u6b7b\u4e86\u53cc\u4eb2\uff0c\u662f\u4ed6\u628a\u6211\u62b1\u517b\u5927\u7684\u3002\u4ed6 \u4eec\u90fd\u53eb\u6211\u505a\u7433\u513f\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u4e0d\u56de\u7687\u5e9c\uff0c\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u51af\u7433\u4e00\u7b11\uff0c\u5e74\u7fb9\u5c27\u770b\u5979\u8138\u4e0a\u68a8 \u6da1\uff0c\u5341\u5206\u53ef\u7231\u3002\u9053\uff1a\u201c\u5440\uff0c\u4f60\u771f\u987d\u76ae\uff0c\u522b\u4eba\u95ee\u6b63\u7ecf\u7684\uff0c\u4f60\u5374\u5c3d\u7b11\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u95ee\u5f97\u597d\u5947 \u602a\uff0c\u96be\u9053\u4f60\u6015\u6211\u6492\u8c0e\uff0c\u4f4f\u5230\u4f60\u5bb6\u91cc\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u5fc3\u5ff5\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u95ee\u4f60\u53bb\u54ea\u91cc\u6709\u4ec0\u4e48\u5947\u602a\uff1f\u4e00\u5e74\u4e24\u5e74\u7684\uff0c\u4f60\u8fd9\u4e2a\u5c0f\u599e\u513f\u5c31\u8981\u53d8\u6210 \u5927\u59d1\u5a18\u5566\uff0c\u96be\u9053\u8fd8\u597d\u610f\u601d\u4e1c\u98d8\u897f\u8361\uff0c\u8d70\u8352\u5c71\u5bbf\u91ce\u5e99\u7684\u8fc7\u65e5\u5b50\uff1f\u201d\u51af\u7433\u7b11\u9053\uff1a\u201c\u90a3\u6709\u4ec0\u4e48\u4e0d \u597d\uff0c\u6211\u7a77\u4e86\u4fbf\u5077\uff0c\u5077\u4e1c\u897f\u5bb9\u6613\u6781\u4e86\uff0c\u53c8\u975e\u5e38\u6709\u8da3\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u5e74\u7fb9\u5c27\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u9053\uff1a \u201c\u56db\u7687\u5e9c\u7684\u4eba\u5e38\u5728\u6c5f\u6e56\u8d70\u52a8\uff0c\u4f60\u4e0d\u6015\u7ed9\u4ed6\u4eec\u78b0\u5230\u4e48\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u7684\u773c\u9876\u5229\uff0c\u4e00\u89c1\u4ed6\u4eec\u5f71 \u513f\u6211\u4fbf\u8dd1\u4e86\u3002\u800c\u4e14\u9664\u4e86\u5b9d\u56fd\u7985\u5e08\u4e4b\u5916\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u77e5\u9053\u6211\u5077\u5077\u6e9c\u51fa\u6765\u7684\u3002\u90a3\u4e2a\u9a9a\u5a46\u5b50\u5c31\u4e0d\u77e5 \u9053\uff0c\u89c1\u4e86\u6211\u8fd8\u62c9\u7740\u95ee\u957f\u95ee\u77ed\uff0c\u95ee\u56db\u7687\u5b50\u6d3e\u6211\u51fa\u6765\u5e72\u4ec0\u4e48\u5462\u3002\u201d\u5e74\u7fb9\u5c27\u77e5\u9053\u5979\u6240\u6307\u7684\u9a9a\u5a46\u5b50\u5c31 \u662f\u97e9\u91cd\u5c71\u7684\u59bb\u5b50\u53f6\u6a2a\u6ce2\uff0c\u4e0d\u89c9\u4e00\u7b11\u3002\u53c8\u559c\u5979\u5bf9\u81ea\u5df1\u8bf4\u51fa\u5fc3\u4e2d\u8bdd\uff0c\u975e\u5e38\u9ad8\u5174\uff0c\u4fbf\u9053\uff1a\u201c\u73b0\u5728\u53ea \u662f\u5b9d\u56fd\u7985\u5e08\u77e5\u9053\uff0c\u5c06\u6765\u96be\u4fdd\u6ca1\u66f4\u591a\u7684\u4eba\u77e5\u9053\u3002\u56db\u7687\u5b50\u89c1\u4f60\u4e45\u4e0d\u56de\u5e9c\uff0c\u4ed6\u4f1a\u6d3e\u4eba\u6349\u4f60\u7684\u3002\u201d\u51af \u7433\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u522b\u552c\u6211\uff0c\u6211\u4e0d\u5bb3\u6015\uff01\u201d\u5e74\u7fb9\u5c27\u770b\u5979\u5c0f\u8138\u53d1\u9752\uff0c\u77e5\u9053\u5979\u5176\u5b9e\u5f88\u6015\u3002\u4fbf\u9053\uff1a\u201c\u4f60 \u4e0d\u5982\u771f\u7684\u4f4f\u5230\u6211\u7684\u5bb6\u53bb\u5427\u3002\u6211\u4e0d\u6015\u4f60\u64a4\u8d56\u4e0d\u8d70\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u54a6\uff0c\u4f4f\u5230\u4f60\u7684\u5bb6\u91cc\uff0c\u4f60\u7684\u5bb6\u91cc \u6709\u4ec0\u4e48\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u53ea\u6709\u7238\u7238\u548c\u5988\u5988\uff0c\u518d\u6709\u5c31\u662f\u4e0b\u4eba\u4e86\u3002\u6211\u5bb6\u6709\u4e2a\u5927\u56ed\u5b50\uff0c\u91cc\u9762\u6709\u82b1\u6709 \u9e1f\uff0c\u5f88\u597d\u73a9\u7684\uff0c\u4f60\u53ef\u4ee5\u5f80\u5230\u56ed\u5b50\u91cc\u53bb\u3002\u201d\u51af\u7433\u4e00\u7b11\uff0c\u4e0d\u7f6e\u53ef\u5426\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u53d6\u51fa\u4e00\u5757\u6c49\u7389\uff0c\u9012\u7ed9\u51af\u7433\u9053\uff1a\u201c\u6211\u7684\u7238\u7238\u53eb\u5e74\u9050\u9f84\uff0c\u4f4f\u5728\u6cb3\u5357\u7701\u9648\u7559\u53bf\u4e61\u4e0b\uff0c\u4f60 \u4e00\u5230\u9648\u7559\uff0c\u968f\u4fbf\u95ee\u54ea\u4e00\u4e2a\u4eba\u90fd\u77e5\u9053\u7684\uff0c\u4f60\u89c1\u4e86\u6211\u7238\u7238\uff0c\u628a\u6c49\u7389\u7ed9\u4ed6\uff0c\u8bf4\u662f\u6211\u53eb\u4f60\u6765\u7684\u4fbf\u884c \u4e86\u3002\u5bf9\u522b\u4eba\u4f60\u53ef\u4e0d\u8981\u4e71\u8bf4\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u51af\u7433\u5c06\u6c49\u7389\u62ff\u8fc7\uff0c\u9053\uff1a\u201c\u5514\uff0c\u8fd9\u4e1c\u897f\u5012\u5f88\u597d\u73a9\u3002\u4f60 \u771f\u7f57\u5506\uff0c\u6211\u8fd8\u4e0d\u5b9a\u51c6\u8981\u5230\u4f60\u5bb6\u53bb\u5462\uff01\u201d\u672c\u6765\u5e74\u7fb9\u5c27\u6b63\u8981\u9760\u5141\u796f\u63d0\u62d4\uff0c\u4e0d\u5e94\u5192\u6b64\u5371\u9669\u628a\u5141\u796f\u559c \u6b22\u7684\u4eba\u5077\u5077\u653e\u8d70\u3002\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u51af\u7433\u90a3\u5929\u771f\u7684\u7b11\u5bb9\u5374\u4ee4\u4ed6\u5fd8\u4e86\u4e00\u5207\u5371\u9669\uff0c\u800c\u4ed6\u4f5c\u51fa\u4e86\u8fd9\u51b3\u5b9a \u4e4b\u540e\uff0c\u4e5f\u65e9\u5728\u5fc3\u4e2d\u76d8\u7b97\u597d\u4e86\u5bf9\u7b56\uff0c\u7eb5\u8bb8\u8d25\u9732\uff0c\u4e5f\u81ea\u65e0\u59a8\u3002 \u3000\u3000\u51af\u7433\u63a5\u8fc7\u6c49\u7389\u4fbf\u8d70\uff0c\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u5440\uff0c\u50bb\u4e2b\u5934\uff0c\u4f60\u8fd9\u6837\u5b50\u5c31\u80fd\u8d70\u51fa\u53bb\u4e48\uff1f\u201d\u53d6\u51fa\u4e00\u5957\u5c0f \u9a6c\u4e95\u7a7f\u7684\u53f7\u8863\uff0c\u63b7\u7ed9\u5979\u9053\uff1a\u201c\u5230\u91cc\u9762\u6362\u8863\u53bb\u3002\u201d \u3000\u3000\u5230\u51af\u7433\u6362\u597d\u8863\u670d\u4e4b\u65f6\uff0c\u5e74\u7fb9\u5c27\u5df2\u5c06\u503c\u591c\u7684\u519b\u5b98\u53eb\u6765\uff0c\u547d\u4ed6\u628a\u51af\u7433\u6084\u6084\u5e26\u51fa\u8425\u5916\uff0c\u770b\u7740\u5979 \u7626\u5c0f\u7684\u80cc\u5f71\u6162\u9992\u6d88\u901d\uff0c\u4e0d\u89c9\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u7b2c\u4e8c\u6e05\u6668\uff0c\u5e74\u7fb9\u5c27\u5230\u5e10\u540e\u5de1\u89c6\uff0c\u89c1\u4e86\u56e0\u7b49\u5bbf\u9152\u672a\u9192\uff0c\u5fc3\u91cc\u7b11\u9053\uff1a\u201c\u771f\u662f\u5339\u592b\u4e4b\u52c7\u3002\u201d\u547d \u4eba\u5f04\u9192\u9152\u6c64\u670d\u4f8d\u4ed6\u4eec\uff0c\u81ea\u5230\u524d\u9762\u4e2d\u519b\u864e\u5e10\u5750\u5802\uff0c\u51c6\u5907\u628a\u90a3\u4e9b\u7591\u72af\u63d0\u6765\u5ba1\u95ee\u3002\u5ffd\u7136\u6208\u54c8\u4ec0\uff08\u526f \u5b98\uff09\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u8001\u548c\u5c1a\u8bf4\u548c\u5927\u5e05\u662f\u8001\u76f8\u8bc6\uff0c\u8981\u6765\u89c1\u4f60\u3002\u201d\u5e74\u7fb9\u5c27\u7709\u5934\u4e00\u76b1\uff0c\u6446\u4e86\u6446 \u624b\uff0c\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u597d\uff0c\u53eb\u4ed6\u6765\u89c1\uff0c\u90a3\u4e9b\u7591\u72af\uff0c\u6682\u65f6\u4e0d\u8981\u63d0\u6765\uff01\u201d\u8fc7\u4e86\u4e00\u9635\uff0c\u672c\u65e0\u7985\u5e08\u63d0\u7740\u62c2 \u5c18\uff0c\u5927\u6b65\u8d70\u8fdb\uff01\u5e74\u7fb9\u5c27\u614c\u5fd9\u8fce\u63a5\uff0c\u6570\u5e74\u4e0d\u89c1\uff0c\u53ea\u89c1\u672c\u65e0\u7985\u5e08\u5065\u70c1\u5982\u6614\u3002\u53cc\u76ee\u5bd2\u5149\u51db\u7136\uff0c\u4e0d\u6012 \u800c\u5a01\uff0c\u628a\u5e74\u7fb9\u5c27\u76ef\u5f97\u5fc3\u4e2d\u60b8\u6151\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u6025\u5fd9\u65bd\u793c\uff0c\u9053\uff1a\u201c\u5927\u5e08\u8fdc\u6765\uff0c\u8bf7\u6055\u672a\u66fe\u8fce\u63a5\u3002\u201d\u672c\u65e0\u5927\u5e08\u9f3b\u5b50\u201c\u5514\u201d\u4e86\u4e00\u58f0\uff0c\u5de6 \u624b\u5fae\u62ac\uff0c\u9053\uff1a\u201c\u5514\uff0c\u4e0d\u6562\u5f53\uff01\u4f60\u662f\u5927\u5c06\u519b\uff0c\u600e\u6562\u8981\u4f60\u8fce\u63a5\uff01\u201d\u5e74\u7fb9\u5c27\u6b63\u81ea\u5f2f\u8170\u65bd\u793c\uff0c\u5ffd\u89c9\u4e00 \u80a1\u5927\u529b\u628a\u81ea\u5df1\u62ac\u8d77\uff0c\u53c8\u60ca\u6050\u53c8\u5c34\u5c2c\uff0c\u672c\u65e0\u5927\u5e08\u7adf\u7136\u4e0d\u80af\u53d7\u81ea\u5df1\u7684\u793c\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u4eb2\u81ea\u7aef\u8fc7\u864e\u76ae\u4ea4\u6905\uff0c\u4fa7\u8eab\u966a\u672c\u65e0\u5927\u5e08\u5750\u4e0b\uff0c\u9053\uff1a\u201c\u665a\u8f88\u6323\u6b64\u529f\u540d\uff0c\u5168\u4ed7\u5927\u5e08\u5f53\u5e74 \u8bad\u8bf2\u6307\u70b9\u4e4b\u529f\uff01\u201d\u672c\u65e0\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u8bad\u8bf2\u4f60\u4ec0\u4e48\uff1f\u201d\u5e74\u7fb9\u5c27\u77e5\u9053\u8fd9\u8001\u548c\u5c1a\u59dc\u6842\u4e4b \u6027\uff0c\u8001\u800c\u5f25\u8fa3\uff0c\u4e0d\u6562\u8bf4\u8bdd\u3002\u50f5\u4e86\u7247\u523b\uff0c\u8fd9\u624d\u966a\u7b11\u9053\uff1a\u201c\u8001\u7985\u5e08\u6240\u6388\u7684\u7f57\u6c49\u62f3\uff0c\u665a\u8f88\u73b0\u5728\u6bcf\u5929 \u90fd\u7ec3\u3002\u201d\u672c\u65e0\u5927\u5e08\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7f57\u6c49\u62f3\u6709\u4ec0\u4e48\u7528\uff1f\u7f57\u6c49\u62f3\u53ef\u52a9\u4e0d\u4e86\u4f60\u6323\u8fd9\u4e48\u5927\u7684\u529f\u540d\u3002\u201d\u5e74 \u7fb9\u5c27\u4e0d\u6562\u56de\u8bdd\u3002\u672c\u65e0\u5927\u5e08\u89c1\u4ed6\u72b6\u8c8c\u606d\u987a\uff0c\u6012\u6c14\u7a0d\u5e73\uff0c\u9053\uff1a\u201c\u4f60\u4ea4\u7684\u597d\u670b\u53cb\uff01\u4f60\u7684\u529f\u540d\u662f\u738b\u5c0a \u4e00\u62db\u6276\u7684\u5427\uff01\u201d\u5e74\u7fb9\u5c27\u966a\u7b11\u9053\uff1a\u201c\u56db\u7687\u5b50\u4e5f\u662f\u4f60\u8001\u5e08\u4f84\u3002\u201d\u672c\u65e0\u6012\u9053\uff1a\u201c\u6211\u6ca1\u6709\u90a3\u4e48\u9614\u7684\u5e08 \u4f84\uff01\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4e0a\u8f88\u672c\u7a7a\u4e3b\u6301\u7684\u8d1d\u53f6\u7b3a\u6587\u8f7d\u660e\u56db\u7687\u5b50\u8fd8\u662f\u5c11\u6797\u5bfa\u5f1f\u5b50\uff0c\u4ed6\u867d\u5c0a\u8d35\uff0c\u5bf9\u5c11 \u6797\u7684\u601d\u60c5\u5012\u4e0d\u6562\u5fd8\uff0c\u6211\u51fa\u4eac\u65f6\uff0c\u4ed6\u8fd8\u5bf9\u6211\u8bf4\uff0c\u5c06\u6765\u82e5\u767b\u4e86\u5927\u5b9d\uff0c\u8fd8\u8981\u5230\u5c11\u6797\u5bfa\u672d\u62dc\u3002\u201d\u5176\u5b9e \u5141\u796f\u5bf9\u4ed6\u8bf4\u7684\u662f\u82e5\u767b\u4e86\u7687\u4f4d\uff0c\u5c31\u8981\u628a\u5c11\u6797\u94f2\u5e73\uff01\u672c\u65e0\u7985\u5e08\u6012\u6781\u6c14\u6781\uff0c\u53cd\u800c\u51b7\u7b11\u3002\u5ffd\u9053\uff1a\u201c\u4f60 \u5f53\u5e74\u529b\u8bc1\u8d1d\u53f6\u7b3a\u6587\u662f\u6211\u5e08\u5144\u624b\u7b14\uff0c\u8fd9\u4ef6\u529f\u52b3\u5927\u6781\u4e86\uff01\u201d\u5e74\u7fb9\u5c27\u5fc3\u5934\u4e00\u9707\u3002\u6697\u6697\u76d8\u7b97\u5982\u4f55\u5bf9\u4ed8\u3002 \u3000\u3000\u672c\u65e0\u7985\u5e08\u76ef\u4e86\u5e74\u7fb9\u5c27\u4e24\u773c\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5b69\u5b50\u679c\u7136\u53d8\u4e86\uff0c\u53ea\u77e5\u529f\u540d\u5229\u7984\uff0c\u5fd8\u4e86\u81ea\u5df1\u662f\u6c49\u4eba \u4e86\u3002\u201d\u4f46\u5c11\u6797\u5bb6\u89c4\uff0c\u7d20\u6765\u4e0d\u7406\u671d\u653f\uff0c\u4e5f\u4e0d\u7981\u95e8\u5f92\u4e3a\u5b98\uff0c\u4f55\u51b5\u5e74\u7fb9\u5c27\u53c8\u4e0d\u662f\u5c11\u6797\u6d3e\u7684\u6b63\u5f0f\u95e8 \u4eba\uff0c\u672c\u65e0\u66f4\u7ba1\u4ed6\u4e0d\u7740\uff0c\u5e74\u7fb9\u5c27\u89c1\u672c\u65e0\u5927\u5e08\u4e0d\u8bed\uff0c\u9762\u8272\u4f3c\u8f83\u7f13\u548c\uff0c\u53c8\u966a\u7b11\u9053\uff1a\u201c\u65e0\u4f4f\u7985\u5e08\u6cd5\u4f53 \u53ef\u597d\uff1f\u201d\u672c\u5143\u9053\uff1a\u201c\u597d\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u81ea\u53e4\u9053\u5e08\u5c0a\u5982\u7236\uff0c\u6211\u867d\u7136\u65e0\u798f\u5f97\u5217\u95e8\u5899\uff0c\u4f46\u66fe\u8499\u8001 \u7985\u5e08\u6307\u70b9\uff0c\u4e00\u5411\u628a\u4f60\u8001\u5f53\u5e08\u5c0a\u770b\u5f85\uff0c\u8001\u7985\u5e08\u8fdc\u6765\uff0c\u8bf7\u5bb9\u5f1f\u5b50\u5907\u529e\u658b\u5e2d\u3002\u201d\u5429\u5490\u4e0b\u53bb\uff0c\u672c\u65e0\u5ffd \u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u5927\u5e08\u6709\u4f55\u5429\u5490\uff1f\u201d\u672c\u65e0\u9053\uff1a\u201c\u6211\u6765\u6b64\u4e0d\u662f\u5316\u658b\uff0c\u6211\u95ee\u4f60\uff0c\u6d59\u629a \u4ea4\u7ed9\u4f60\u62bc\u4eac\u7684\u7591\u72af\uff0c\u6211\u7684\u5f92\u5f1f\u5370\u5b8f\u53ef\u5728\u5185\u4e48\uff1f\u201d\u5e74\u7fb9\u5c27\u7a0d\u4e00\u8fdf\u7591\uff0c\u7b54\u9053\uff1a\u201c\u5728\u5185\u3002\u201d\u672c\u65e0 \u9053\uff1a\u201c\u4ed6\u72af\u4e86\u4ec0\u4e48\u5acc\u7591\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6d89\u5acc\u7ed9\u53db\u8d3c\u8def\u6c11\u77bb\u9001\u4fe1\u3002\u201d\u672c\u65e0\u9053\uff1a\u201c\u6709\u8bc1\u636e \u4e48\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u5c1a\u672a\u641c\u51fa\u3002\u201d\u672c\u65e0\u9053\uff1a\u201c\u90a3\u4e48\u8bf7\u5927\u5c06\u519b\u51c6\u6211\u5c06\u4ed6\u4fdd\u91ca\u3002\u201d\u5e74\u7fb9\u5c27\u6025\u9053\uff1a \u201c\u5927\u5e08\u8a00\u91cd\u4e86\uff01\u201d\u672c\u65e0\u9053\uff1a\u201c\u5ba2\u6c14\u8bdd\u4e0d\u5fc5\u591a\u8bf4\uff01\u4f60\u5e72\u8106\u8bf4\u51c6\u8fd8\u662f\u4e0d\u51c6\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u8fd9\uff0c \u8fd9\u2026\u2026\u201d \u3000\u3000\u672c\u65e0\u51b7\u7b11\u9053\uff1a\u201c\u65e2\u65e0\u5b9e\u636e\uff0c\u5c31\u7167\u4f60\u4eec\u671d\u5ef7\u7684\u6cd5\u4f8b\uff0c\u4e5f\u53ef\u4ea4\u4fdd\u5019\u4f20\uff0c\u96be\u9053\u5c11\u6797\u5bfa\u7684\u76d1\u5bfa\u505a \u4e00\u4e2a\u4fdd\u4eba\uff0c\u4f60\u5e74\u5927\u5c06\u519b\u8fd8\u4fe1\u4e0d\u8fc7\u5417\uff1f\u201d\u5e74\u7fb9\u5c27\u53ea\u597d\u8bf4\u51fa\u6765\u9053\uff1a\u201c\u8fd9\u662f\u56db\u7687\u5b50\u6240\u8981\u7684\u4eba\u3002\u201d\u672c \u65e0\u706b\u6c14\u4e0a\u51b2\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5c31\u5bf9\u56db\u7687\u5b50\u8bf4\u662f\u6211\u5e26\u8d70\u7684\uff0c\u4ed6\u82e5\u8981\u4eba\uff0c\u53ef\u5230\u5c11\u6797\u5bfa\u53bb\u8981\uff01\u201d \u3000\u3000\u672c\u65e0\u5927\u5e08\u52a8\u4e86\u771f\u6012\uff0c\u5fc3\u4e2d\u5df2\u662f\u51c6\u5907\u786c\u8981\u3002\u4e0d\u6599\u5e74\u7fb9\u5c27\u5ffd\u7136\u966a\u7b11\uff0c\u957f\u63d6\u5230\u5730\uff0c\u9053\uff1a\u201c\u5927\u5e08 \u4e0d\u8981\u751f\u6c14\uff0c\u665a\u8f88\u9a6c\u4e0a\u628a\u5370\u5b8f\u5e08\u5144\u8bf7\u6765\uff0c\u966a\u7f6a\u4fbf\u662f\u3002\u6709\u4ec0\u4e48\u5e72\u7cfb\uff0c\u7531\u6211\u627f\u62c5\u3002\u5927\u5e08\u8bf7\u7a0d\u5019\u7247 \u523b\u3002\u201d\u628a\u4e2d\u519b\u5524\u6765\uff0c\u5429\u5490\u51e0\u53e5\uff0c\u8fc7\u4e86\u534a\u523b\uff0c\u536b\u5175\u679c\u7136\u5c06\u5370\u5b8f\u548c\u5c1a\u5e26\u81f3\u5e10\u524d\u3002\u5370\u5b8f\u5341\u5e74\u524d\u66fe\u56de \u5d69\u5c71\u672c\u5bfa\u793c\u62dc\uff0c\u90a3\u5e74\u7fb9\u5c27\u8fd8\u662f\u5341\u4e00\u5c81\u7684\u5b69\u5b50\uff0c\u4e24\u4eba\u66fe\u89c1\u8fc7\u9762\u3002\u5e74\u7fb9\u5c27\u4eb2\u81ea\u628a\u4ed6\u9563\u94d0\u89e3\u5f00\uff0c\u5370 \u5b8f\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5e08\u5085\uff01\u201d\u672c\u65e0\u9053\uff1a\u201c\u4f60\u4e5f\u8be5\u8c22\u5e74\u5c06\u519b\u91ca\u653e\u4e4b\u6069\u3002\u201d\u5370\u5b8f\u548c\u5c1a\u7591\u56e2\u6ee1\u8179\uff0c\u8feb\u4e8e \u5e08\u547d\u4f5c\u4e86\u4e00\u793c\uff01 \u3000\u3000\u672c\u65e0\u6012\u7130\u5df2\u7184\uff0c\u5fc3\u60f3\u5e74\u7fb9\u5c27\u5230\u5e95\u662f\u6709\u6167\u6839\u7684\u4eba\uff0c\u8fd8\u672a\u5b8c\u5168\u53d8\u73af\u3002\u4e2d\u519b\u6367\u4e0a\u4f73\u8317\u658b\u70b9\uff0c\u5e74 \u7fb9\u5c27\u5012\u4e86\u4e09\u676f\u70ed\u8336\uff0c\u6e05\u9999\u6251\u9f3b\uff0c\u4e3e\u676f\u5411\u672c\u65e0\u7985\u5e08\u9053\uff1a\u201c\u5927\u5e08\u8fdc\u6765\uff0c\u8bf7\u7565\u8fdb\u658b\u70b9\u3002\u201d\u672c\u65e0\u7aef\u8d77 \u8336\u676f\uff0c\u5370\u5b8f\u5ffd\u9053\uff1a\u201c\u5e08\u5085\uff0c\u54b1\u4eec\u522b\u518d\u53e8\u6270\u5e74\u5c06\u519b\uff0c\u8fd8\u662f\u53ca\u65e9\u8d70\u5427\uff01\u201d\u5e74\u7fb9\u5c27\u4e00\u53e3\u5c06\u8336\u559d\u5c3d\uff0c \u9053\uff1a\u201c\u5370\u5b8f\u5e08\u5144\u600e\u4e48\u89c1\u5916\uff1f\u6211\u6628\u65e5\u65b9\u5230\u676d\u5dde\uff0c\u7d2f\u5e08\u5144\u4e45\u53d7\u7f27\u7ec1\u4e4b\u707e\uff0c\u5fc3\u4e2d\u5b9e\u5728\u8fc7\u610f\u4e0d\u53bb\uff0c\u5e08 \u5144\u82e5\u7136\u4e0d\u80af\u8d4f\u9762\uff0c\u90a3\u4e0d\u662f\u8bda\u5fc3\u602a\u8d23\u5c0f\u5f1f\u4e86\u3002\u201d\u672c\u65e0\u89c1\u5370\u5b8f\u50ac\u8d70\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u53ca\u89c1\u5e74\u7fb9\u5c27\u5c06\u8336 \u559d\u5149\uff0c\u6697\u7b11\u5370\u5b8f\u591a\u7591\uff0c\u4e3e\u8d77\u8336\u676f\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4ece\u6765\u4e0d\u53d7\u5b98\u5e9c\u5e03\u65bd\uff0c\u4eca\u65e5\u7834\u4f8b\u559d\u4f60\u4e00\u676f\u3002\u201d\u5c06 \u8336\u559d\u4e86\u3002\u5370\u5b8f\u5c06\u8336\u676f\u653e\u5728\u5507\u8fb9\uff0c\u8fdf\u7591\u4e00\u9635\uff0c\u672c\u65e0\u7985\u5e08\u5ffd\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u4e00\u638c\u5c06\u5370\u5b8f\u7684\u8336\u676f\u6253 \u788e\u3002\u5927\u58f0\u559d\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\uff0c\u4f60\u6562\u65bd\u6697\u7b97\uff01\u201d\u5e74\u7fb9\u5c27\u54c8\u54c8\u5927\u7b11\uff0c\u65e9\u5df2\u7f29\u8fdb\u5e10\u540e\u3002\u672c\u65e0\u5927\u5e08\u62c2\u5c18 \u4e00\u626b\uff0c\u5e10\u5e18\u5012\u5377\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u4e86\u56e0\u548c\u5c1a\u4e00\u6756\u6253\u51fa\uff01 \u3000\u3000\u672c\u65e0\u9053\uff1a\u201c\u5370\u5b8f\uff0c\u968f\u6211\u95ef\uff01\u201d\u62c2\u5c18\u4e00\u5377\uff0c\u5c06\u4e86\u56e0\u7985\u6756\u5377\u7740\uff0c\u4e00\u638c\u5288\u4ed6\u5de6\u80a9\uff0c\u5929\u53f6\u6563\u4eba\u500f \u7136\u8df3\u51fa\uff0c\u53cc\u638c\u4e00\u5806\uff0c\u8fd0\u5168\u529b\u63a5\u4e86\u672c\u65e0\u4e00\u638c\uff0c\u4e86\u56e0\u7985\u6756\u4e00\u98a4\uff0c\u8131\u4e86\u51fa\u6765\uff0c\u97e9\u91cd\u5c71\u548c\u8463\u5de8\u5ddd\u4e5f\u4ece \u5bf9\u9762\u5e10\u4e2d\u6740\u51fa\uff0c\u56db\u540d\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\u628a\u672c\u65e0\u5e08\u5f92\u56f4\u5728\u5e10\u4e2d\uff01\u5e74\u7fb9\u5c27\u518d\u8d70\u51fa\u6765\uff0c\u8fdc\u8fdc\u7684\u5750\u5728\u864e\u76ae \u6905\u4e0a\uff0c\u5750\u89c2\u864e\u6597\u3002 \u3000\u3000\u672c\u65e0\u52c3\u7136\u9707\u6012\uff0c\u62c2\u5c18\u4e00\u4e3e\uff0c\u5530\u7684\u5411\u8463\u5de8\u5ddd\u62c2\u6765\uff0c\u8463\u5de8\u5ddd\u6025\u5fd9\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u8d76\u5feb\u95ea\u5f00\uff0c \u62a2\u5230\u4fa7\u9762\u53d1\u638c\uff1b\u672c\u65e0\u5927\u5e08\u7fe9\u7136\u63a0\u51fa\uff0c\u5411\u5929\u53f6\u6563\u4eba\u731b\u4e0b\u6740\u624b\uff0c\u5929\u53f6\u5de6\u638c\u5e73\u80f8\uff0c\u53f3\u638c\u4e00\u626b\uff0c\u672c\u65e0 \u559d\u9053\uff1a\u201c\u7740\uff01\u201d\u638c\u4f3c\u5954\u96f7\uff0c\u628a\u5929\u53f6\u6563\u4eba\u9707\u51fa\u4e00\u4e08\u5f00\u5916\uff01\u53f3\u624b\u62c2\u5c18\u53c8\u5df2\u540c\u65f6\u5411\u4e86\u56e0\u9762\u95e8\u62c2\u53bb\uff01 \u672c\u65e0\u6700\u6068\u4e86\u56e0\uff0c\u8fd9\u4e00\u62db\u4e43\u662f\u6740\u624b\uff0c\u540d\u4e3a\u201c\u4e94\u9f99\u6293\u9762\u201d\uff01\u97e9\u91cd\u5c71\u957f\u8896\u4e00\u6325\uff0c\u6b79\u6bd2\u6697\u5668\u98de\u8757\u9488\u6025 \u5982\u9aa4\u96e8\uff0c\u5411\u672c\u65e0\u9762\u95e8\u6025\u5c04\uff0c\u672c\u65e0\u5927\u5e08\u8feb\u5f97\u5c06\u62c2\u5c18\u4e00\u626b\uff0c\u6570\u5341\u679d\u98de\u8757\u9488\u5168\u7ed9\u626b\u6210\u7c89\u5c51\uff01\u4f46\u4e86\u56e0 \u4e5f\u89e3\u4e86\u62c2\u5c18\u6bc1\u9762\u4e4b\u707e\uff0c\u6025\u5fd9\u9000\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u53eb\u9053\uff1a\u201c\u56f0\u7740\u4ed6\uff01\u4ed6\u5df2\u4e2d\u4e86\u5267\u6bd2\uff0c\u51b3\u9003\u4e0d\u4e86\uff01\u201d \u7985\u6756\u5c55\u5f00\uff0c\u547c\u547c\u8f70\u8f70\uff0c\u4e0d\u8ba9\u672c\u65e0\u62a2\u8fd1\u8eab\u524d\uff0c\u5929\u53f6\u6563\u4eba\u8463\u5de8\u5ddd\u97e9\u91cd\u5c71\u4e5f\u540c\u65f6\u8fdb\u51fb\uff01\u672c\u65e0\u7684\u62c2\u5c18 \u867d\u731b\uff1a\u53ef\u4ee5\u5377\u593a\u5175\u5668\uff0c\u4f46\u5728\u56db\u540d\u9ad8\u624b\u5408\u653b\u4e4b\u4e0b\uff0c\u82e5\u5377\u4e86\u4e00\u4eba\u5175\u5668\uff0c\u52bf\u5c06\u9732\u51fa\u7a7a\u95e8\uff0c\u88ab\u5176\u4ed6\u4e09 \u4eba\u51fb\u6bd9\uff01\u53ea\u5f97\u548c\u4ed6\u4eec\u6e38\u6597\uff01 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u7985\u5e08\u906d\u56f0\u5371\uff0c\u7ad6\u5b50\u5f04\u5978\u8c0b\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u6c89\u75db\u91ca\u771f\u7985\u3000\u4f20\u7ecf\u907f\u52ab\u3000\u51c4\u51c9\u8c08\u5f80\u4e8b\u3000\u501f\u9152\u6d47\u6101 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u4e00\u56de \u6c89\u75db\u91ca\u771f\u7985\u3000\u4f20\u7ecf\u907f\u52ab \u51c4\u51c9\u8c08\u5f80\u4e8b\u3000\u501f\u9152\u6d47\u6101 \u3000\u3000\u4e86\u56e0\u548c\u5c1a\u3001\u5929\u53f6\u6563\u4eba\u3001\u8463\u5de8\u5ddd\u3001\u97e9\u91cd\u5c71\u7b49\u56db\u540d\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u8054\u6210\u7284\u89d2\u4e4b\u52bf\uff0c\u628a\u672c\u65e0\u5e08 \u5f92\u56f4\u5728\u5f53\u4e2d\u3002\u672c\u6765\u82e5\u8bba\u6b66\u529f\u5b9e\u529b\uff0c\u672c\u65e0\u5927\u5e08\u8eab\u4e3a\u5c11\u6797\u76d1\u5bfa\uff0c\u5728\u6b66\u6797\u4e2d\u5df2\u662f\u6570\u4e00\u6570\u4e8c\u7684\u4eba\u7269\uff0c \u4ee5\u4e00\u654c\u56db\uff0c\u7eb5\u7136\u672a\u5fc5\u80fd\u80dc\uff0c\u4e5f\u5c3d\u53ef\u62b5\u6321\u5f97\u4f4f\uff0c\u4f46\u4e0d\u5e78\u4ed6\u4e2d\u4e86\u5e74\u7fb9\u5c27\u8be1\u8ba1\uff0c\u559d\u4e0b\u63ba\u6709\u5b54\u96c0\u80c6\u836f \u672b\u7684\u72ee\u5cf0\u9f99\u4e95\u8336\uff0c\u5b54\u96c0\u80c6\u4e43\u662f\u5267\u6bd2\u4e4b\u7269\uff0c\u6597\u4e86\u7247\u523b\uff0c\u6e10\u6e10\u773c\u775b\u53d1\u9ed1\uff0c\u4f53\u5982\u9488\u523a\u3002\u672c\u65e0\u5927\u5e08\u4ed7 \u7740\u6570\u5341\u5e74\u7cbe\u7eaf\u7684\u5185\u529f\uff0c\u63d0\u4e00\u53e3\u6c14\uff0c\u628a\u6bd2\u6c14\u5f3a\u538b\u4e0b\u53bb\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u4f5b\u5c18\u5411\u97e9\u91cd\u5c71\u7684\u8f9f\u4e91\u9504\u4e00 \u7ed5\uff0c\u4e86\u56e0\u7985\u6756\u547c\u7684\u4e00\u58f0\u5411\u4e0b\u4e09\u8def\u626b\u6765\uff0c\u672c\u65e0\u8eab\u5f62\u4e00\u95ea\uff0c\u62cd\u7684\u4e00\u638c\u5c06\u8463\u5de8\u5ddd\u9707\u51fa\u5e10\u5916\uff0c\u5929\u53f6\u6563 \u4eba\u6025\u5fd9\u98de\u8eab\u8fdb\u638c\uff0c\u672c\u65e0\u5927\u5e08\u5df2\u4ece\u7a7a\u7f3a\u51b2\u51fa\uff0c\u4e86\u56e0\u56db\u4eba\u6240\u5e03\u7684\u9635\u5f62\u4e00\u4e71\uff0c\u5f85\u518d\u53d8\u9635\u5408\u56f4\u4e4b\u65f6\uff0c \u672c\u65e0\u5df2\u51b2\u5230\u5e10\u8fb9\u3002 \u3000\u3000\u5370\u5b8f\u548c\u5c1a\u662f\u672c\u65e0\u7684\u9996\u5f92\uff0c\u6b66\u529f\u81ea\u662f\u4e0d\u5f31\uff0c\u4f46\u6bd4\u8d77\u4e86\u56e0\u7b49\u56db\u5927\u9ad8\u624b\uff0c\u7a76\u7adf\u8981\u900a\u4e00\u7b79\uff0c\u4ed6\u7d27 \u968f\u672c\u65e0\u8eab\u540e\uff0c\u53ea\u4ee5\u4e00\u6b65\u4e4b\u5dee\uff0c\u88ab\u5929\u53f6\u6563\u4eba\u6a2a\u8098\u4e00\u649e\uff0c\u53cc\u638c\u8fc5\u51fb\uff0c\u7adf\u88ab\u9694\u4e86\u5f00\u6765\u3002\u672c\u65e0\u5927\u5e08\u542c \u5f97\u80cc\u540e\u62f3\u638c\u4ea4\u51fb\u4e4b\u58f0\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u5929\u53f6\u6563\u4eba\u8df3\u5230\u897f\u9996\uff0c\u5f85\u672c\u65e0\u5e08\u5f92\u4f1a\u5408\u4e4b\u540e\uff0c\u4ed6\u4eec\u56db\u4eba\u53c8\u5df2 \u8054\u6210\u4e86\u7284\u89d2\u4e4b\u52bf\u3002 \u3000\u3000\u56db\u4eba\u518d\u5ea6\u56f4\u653b\uff0c\u8d8a\u903c\u8d8a\u7d27\u3002\u4e86\u56e0\u7684\u7985\u6756\u5b9b\u5982\u602a\u83bd\u6bd2\u9f99\u62c5\u5f53\u4e2d\u8def\uff1b\u97e9\u91cd\u5c71\u7684\u8f9f\u4e91\u9504\u6a2a\u626b\u76f4 \u5288\uff0c\u7eca\u4f4f\u5370\u5b8f\uff1b\u5929\u53f6\u6563\u4eba\u548c\u8463\u5de8\u5ddd\u4ece\u4e24\u7ffc\u534f\u52a9\uff0c\u6559\u672c\u65e0\u5927\u5e08\u65e0\u6cd5\u4e13\u5bf9\u4e00\u4eba\uff0c\u4e0b\u5176\u6740\u624b\u3002\u53c8\u6597 \u4e86\u7247\u523b\uff0c\u672c\u65e0\u5927\u5e08\u989d\u4e0a\u89c1\u6c57\uff0c\u81ea\u77e5\u4e0d\u5999\uff0c\u84e6\u7136\u559d\u9053\uff1a\u201c\u5370\u5b8f\uff0c\u6211\u4eca\u65e5\u6b7b\u5728\u6b64\u5730\uff0c\u4f60\u56de\u5d69\u5c71\u544a \u8bc9\u5e08\u53d4\u3002\u4e0d\u8981\u987e\u6211\uff01\u201d\u731b\u7136\u5bf9\u8463\u5de8\u5ddd\u75be\u53d1\u4e24\u638c\uff0c\u8463\u5de8\u5ddd\u521a\u624d\u4e2d\u4ed6\u4e00\u638c\uff0c\u529f\u529b\u5927\u51cf\uff0c\u60ca\u5f13\u4e4b \u9e1f\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u4e86\u56e0\u7684\u7985\u6756\u4e58\u673a\u76f4\u626b\u4e0b\u6765\uff0c\u4e00\u4e2a\u201c\u8fc5\u96f7\u51fb\u9876\u201d\uff0c\u6253\u5230\u4e86\u672c\u65e0\u5927\u5e08 \u5934\u4e0a\u3002\u8fd9\u65f6\u672c\u65e0\u5927\u5e08\u53f3\u624b\u62c2\u5c18\u6321\u4f4f\u5929\u53f6\u6563\u4eba\uff0c\u5de6\u638c\u51fb\u9000\u8463\u5de8\u5ddd\uff0c\u672a\u53ca\u64a4\u62db\uff0c\u770b\u6765\u4e07\u4e07\u9003\u95ea\u4e0d \u4e86\uff01 \u3000\u3000\u5c31\u5728\u6b64\u9645\uff0c\u663e\u51fa\u4e86\u672c\u65e0\u5927\u5e08\u8d85\u51e1\u4eba\u5723\u7684\u6b66\u529f\uff0c\u53ea\u89c1\u4ed6\u80a9\u5934\u4e00\u633a\uff0c\u84ec\u7684\u4e00\u58f0\uff0c\u4e86\u56e0\u7985\u6756\u5982 \u51fb\u5728\u94c1\u677f\u4e4b\u4e0a\uff0c\u76f4\u5f39\u8d77\u6765\uff0c\u672c\u65e0\u5927\u559d\u4e00\u58f0\uff0c\u63a0\u8fc7\u5929\u53f6\u6563\u4eba\u5934\u9876\uff0c\u76f4\u5954\u5750\u5728\u5e10\u5185\u7684\u5e74\u7fb9\u5c27\u6740\u53bb\uff01 \u3000\u3000\u4e86\u56e0\u7b49\u5927\u60ca\uff0c\u56db\u4eba\u4e00\u9f50\u56de\u9632\uff0c\u672c\u65e0\u559d\u9053\uff1a\u201c\u5370\u5b8f\u5feb\u8d70\uff01\u201d\u5370\u5b8f\u7a0d\u4e00\u72b9\u7591\uff0c\u53ea\u542c\u5f97\u5e08\u5085\u53c8 \u559d\u9053\uff1a\u201c\u4e0d\u8096\u5f92\uff0c\u4f60\u60f3\u6211\u6b7b\u8baf\u65e0\u4eba\u77e5\u6653\u5417\uff1f\u201d\u8fd9\u65f6\uff0c\u5e74\u7fb9\u5c27\u5df2\u8eb2\u5165\u5e10\u5185\uff0c\u672c\u65e0\u4e00\u9762\u5927\u559d\uff0c\u811a \u5e95\u4ecd\u662f\u4e1d\u6beb\u4e0d\u7f13\uff0c\u76f4\u5954\u5185\u5e10\u6740\u6765\uff0c\u5370\u5b8f\u6014\u4e86\u4e00\u6014\uff0c\u5e10\u5916\u6821\u5c09\u5200\u67aa\u7eb7\u4e3e\uff0c\u622a\u4ed6\u53bb\u8def\u3002\u5370\u5b8f\u54ac\u5b9e \u7259\u6839\uff0c\u4e00\u58f0\u6d2a\u559d\uff0c\u628a\u4e00\u6746\u957f\u62a2\u593a\u5230\u624b\u4e2d\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u5085\u6211\u53bb\u4e86\uff01\u201d\u628a\u957f\u67aa\u5c55\u5f00\uff0c\u5de6\u70b9\u53f3\u6233\uff0c \u66b4\u98ce\u9aa4\u96e8\u822c\u76f4\u6740\u51fa\u53bb\uff01\u90a3\u4e9b\u6821\u5c09\u6b66\u827a\u4f4e\u5fae\uff0c\u5982\u4f55\u6321\u5f97\u5f80\uff1f\u970e\u5ffd\u7ed9\u4ed6\u6740\u51fa\u5e10\u5916\u3002\u4e86\u56e0\u7b49\u56db\u5927\u9ad8 \u624b\uff0c\u7d27\u8e51\u672c\u65e0\u8eab\u540e\uff0c\u5e74\u7fb9\u5c27\u5728\u5e10\u5185\u5c06\u4e24\u628a\u4ea4\u6905\u731b\u7684\u63b7\u51fa\uff01 \u3000\u3000\u672c\u65e0\u5927\u5e08\u201c\u5580\u5580\u201d\u4e24\u638c\uff0c\u628a\u4ea4\u6905\u6253\u5f97\u88c2\u6210\u6570\u5341\u5c0f\u5757\uff0c\u6728\u7247\u7eb7\u98de\u3002\u4e86\u56e0\u7985\u6756\u4e00\u633a\uff0c\u6756\u5c16\u582a \u582a\u70b9\u5230\u80cc\u5fc3\uff0c\u672c\u65e0\u731b\u559d\u4e00\u58f0\uff0c\u62c2\u5c18\u53cd\u7ed5\uff0c\u628a\u7985\u6756\u7f20\u7740\uff0c\u9976\u662f\u4e86\u56e0\u795e\u529b\u60ca\u4eba\uff0c\u7adf\u81ea\u4e0d\u80fd\u79fb\u52a8\u534a \u6b65\u3002\u5929\u53f6\u6563\u4eba\u53cc\u638c\u9f50\u53d1\uff0c\u62b5\u7740\u4e86\u672c\u65e0\u5927\u5e08\u5de6\u638c\u638c\u529b\uff0c\u8463\u5de8\u5ddd\u97e9\u91cd\u5c71\u5de6\u53f3\u653b\u4e0a\uff0c\u672c\u65e0\u5de6\u638c\u4e00 \u7f29\uff0c\u5929\u53f6\u6563\u4eba\u4e00\u4e2a\u8e09\u8dc4\uff0c\u51e0\u4e4e\u8dcc\u5012\u3002\u4e86\u56e0\u8d81\u52bf\u6c89\u6756\u4e00\u6296\uff0c\u672c\u65e0\u5df2\u628a\u62c2\u5c18\u89e3\u4e86\u51fa\u6765\uff0c\u5411\u97e9\u91cd\u5c71 \u731b\u65bd\u6740\u624b\uff01 \u3000\u3000\u5929\u53f6\u6563\u4eba\u7d20\u4ee5\u638c\u529b\u81ea\u9e23\uff0c\u90a3\u6599\u521a\u624d\u8fd9\u62db\uff0c\u672c\u65e0\u5927\u5e08\u53f3\u624b\u62c2\u5c18\u529b\u62d2\u7985\u6756\uff0c\u53ea\u4ee5\u5355\u638c\u4e4b\u529b\uff0c \u5df2\u81ea\u80dc\u4ed6\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u5fc3\u5bd2\u3002\u5e74\u7fb9\u5c27\u5728\u5e10\u5185\u53eb\u9053\uff1a\u201c\u4e0d\u7528\u6015\u4ed6\uff0c\u4ed6\u559d\u4e86\u5b54\u96c0\u80c6\u6bd2\u8336\uff0c\u51b3\u6d3b\u4e0d \u4e86\uff01\u201d\u5929\u53f6\u6563\u4eba\u9f13\u52c7\u8fd0\u638c\uff0c\u529b\u6551\u5e08\u5144\uff01\u4e0d\u8fc7\u4e09\u62db\uff0c\u53c8\u7ed9\u672c\u65e0\u5927\u5e08\u9707\u51fa\u4e00\u4e08\u5f00\u5916\uff01 \u3000\u3000\u672c\u65e0\u5927\u5e08\u7eb5\u58f0\u72c2\u7b11\uff1a\u201c\u54c8\u54c8\uff0c\u5e74\u7fb9\u5c27\u4f60\u597d\uff01\u201d\u5fc3\u4f24\u4e4b\u6781\uff0c\u53d1\u4e3a\u72c2\u7b11\uff01\u9976\u662f\u5e74\u7fb9\u5c27\u90a3\u6837\u8885 \u96c4\uff0c\u542c\u6765\u4e5f\u89c9\u5fc3\u80c6\u6b32\u88c2\u3002\u5e74\u7fb9\u5c27\u6697\u9053\uff1a\u201c\u4f60\u867d\u4e8e\u6211\u6709\u6069\uff0c\u4f46\u56db\u8d1d\u52d2\u8981\u628a\u5c11\u6797\u5bfa\u94f2\u5e73\uff0c\u6211\u4e5f\u65e0 \u6cd5\u4e0d\u5148\u9664\u4f60\u3002\u201d\u4f46\u5df2\u4e0d\u6562\u63ed\u5e10\u89c2\u6218\uff0c\u5728\u536b\u58eb\u73af\u7acb\u4e0b\uff0c\u9000\u5230\u4e86\u5e10\u540e\u3002 \u3000\u3000\u72c2\u7b11\u58f0\u4e2d\uff0c\u672c\u65e0\u5927\u5e08\u6124\u6781\u6c14\u6781\uff0c\u75db\u4e0b\u6740\u624b\uff01\u97e9\u91cd\u5c71\u6b63\u4f7f\u5230\u201c\u5f00\u5c71\u8f9f\u77f3\u201d\u4e00\u62db\uff0c\u6325\u9504\u6025 \u65ab\uff0c\u672c\u65e0\u5927\u5e08\u9661\u7136\u5927\u559d\uff0c\u80a9\u5934\u4e00\u633a\uff0c\u53c8\u786c\u63a5\u4e86\u4e86\u56e0\u4e00\u6756\uff0c\u5de6\u638c\u4e00\u6325\uff0c\u628a\u97e9\u91cd\u5c71\u7684\u8f9f\u4e91\u9504\u6253\u5f97 \u8131\u624b\u98de\u53bb\uff01\u8463\u5de8\u5ddd\u6b63\u5728\u80cc\u540e\u53d1\u62db\uff0c\u672c\u65e0\u5de6\u638c\u5411\u524d\u4e00\u6309\uff0c\u559d\u58f0\u201c\u53bb\uff01\u201d\u628a\u97e9\u91cd\u5c71\u4e00\u62ff\u6253\u7ffb\uff0c\u62c2 \u5c18\u81ea\u80a9\u4e0a\u53cd\u626b\u8fc7\u6765\uff0c\u628a\u8463\u5de8\u5ddd\u624b\u8155\u7f20\u7740\u3002\u4e86\u56e0\u6025\u633a\u6756\u6765\u6551\uff0c\u672c\u65e0\u5df2\u500f\u5730\u56de\u8eab\uff0c\u62c2\u5c18\u628a\u8463\u5de8\u5ddd \u5377\u4e86\u8d77\u6765\uff0c\u8fce\u7740\u4e86\u56e0\u7684\u7985\u6756\u4fbf\u9001\uff0c\u4e86\u56e0\u614c\u4e0d\u8fed\u7684\u7f29\u624b\uff0c\u53ea\u542c\u5f97\u672c\u65e0\u53c8\u662f\u5927\u559d\u4e00\u58f0\uff1a\u201c\u53bb\uff01\u201d \u594b\u529b\u4e00\u6325\uff0c\u628a\u8463\u5de8\u5ddd\u63b7\u51fa\u5e10\u5916\uff01 \u3000\u3000\u8fd9\u51e0\u62db\u75be\u82e5\u7535\u5149\u77f3\u706b\uff0c\u672c\u65e0\u62fc\u8840\u8089\u4e4b\u8eaf\uff0c\u786c\u63a5\u4e24\u6756\uff0c\u628a\u97e9\u91cd\u5c71\u8463\u5de8\u5ddd\u6253\u5f97\u91cd\u4f24\uff0c\u6655\u5012\u5730 \u4e0a\u4e0d\u80fd\u52a8\u5f39\uff01\u4e86\u56e0\u548c\u5929\u53f6\u90fd\u5df2\u80c6\u5bd2\u3002\u672c\u65e0\u54c8\u54c8\u72c2\u7b11\uff0c\u62c2\u5c18\u5411\u5929\u53f6\u8fce\u5934\u4e00\u51fb\uff0c\u5929\u53f6\u6563\u4eba\u6025\u5fd9\u5012 \u7eb5\u51fa\u53bb\uff0c\u4e86\u56e0\u4e00\u6756\u626b\u6765\uff0c\u672c\u65e0\u53f3\u624b\u4e00\u6325\uff0c\u62c2\u5c18\u5012\u8f6c\uff0c\u5982\u5931\u98de\u51fa\uff0c\u5929\u53f6\u6563\u4eba\u6599\u4e0d\u5230\u4ed6\u6709\u6b64\u6740 \u624b\uff0c\u60e8\u53eb\u4e00\u58f0\uff0c\u5f53\u573a\u4ec6\u5012\uff01\u8fd9\u65f6\u4e86\u56e0\u7985\u6756\u521a\u521a\u6253\u5230\u3002\u672c\u65e0\u5de6\u81c2\u4e00\u67b6\uff0c\u559d\u58f0\u201c\u7740\uff01\u201d\u53f3\u624b\u4e00 \u62ac\uff0c\u628a\u4e86\u56e0\u7684\u7985\u6756\u63e1\u5728\u624b\u4e2d\uff0c\u4e86\u56e0\u7adf\u7ed9\u626f\u4e86\u8fc7\u6765\uff0c\u6025\u5fd9\u677e\u624b\u6b32\u9003\uff0c\u672c\u65e0\u6251\u5730\u817e\u8d77\u4e00\u817f\uff0c\u6b63\u6b63 \u8e22\u4e2d\u4e86\u56e0\u524d\u5fc3\uff0c\u4e86\u56e0\u98de\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u54c7\u7684\u4e00\u58f0\uff0c\u5410\u51fa\u4e00\u5927\u53e3\u9c9c\u8840\uff0c\u767b\u65f6\u6655\u5012\u5730\u4e0a\u3002 \u3000\u3000\u672c\u65e0\u5927\u5e08\u54c8\u54c8\u72c2\u7b11\uff0c\u7a81\u8d77\u5750\u5728\u5730\u4e0a\uff0c\u5e74\u7fb9\u5c27\u95fb\u58f0\u80c6\u788e\uff0c\u4e0d\u6562\u51fa\u6765\uff0c\u4f17\u6821\u5c09\u745f\u7f29\u56db\u9685\uff0c\u90fd \u5413\u7834\u4e86\u80c6\uff01\u672c\u65e0\u7b11\u58f0\u6e10\u5f31\uff0c\u5ffd\u7136\u5782\u9996\u80f8\u81c6\uff0c\u8bb7\u8bb7\u8bed\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u4f60\u597d\uff0c\u5e74\u7fb9\u5c27\u4f60\u597d\uff01\u4f60 \u597d\u2026\u2026\u201d\u8bed\u58f0\u6e10\u5f31\u6e10\u5bc2\u3002\u8fc7\u4e86\u597d\u4e45\uff0c\u6709\u4e00\u4e2a\u80c6\u5927\u7684\u536b\u58eb\u6084\u6084\u4e0a\u524d\uff0c\u4f38\u624b\u63a8\u4ed6\uff0c\u672c\u65e0\u52a8\u4e5f\u4e0d \u52a8\u3002\u90a3\u536b\u58eb\u653e\u80c6\u6478\u4ed6\u80f8\u53e3\uff0c\u5ffd\u5730\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u6076\u548c\u5c1a\u5df2\u7ecf\u6b7b\u4e86\uff01\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u95fb\u58f0\u8d70\u51fa\uff0c\u5140\u662f\u4e0d\u6562\u4e0a\u524d\uff0c\u536b\u58eb\u53c8\u7980\u9053\uff1a\u201c\u5927\u5e05\uff0c\u8fd9\u6076\u548c\u5c1a\u5df2\u7ecf\u6b7b\u4e86\uff01\u201d\u5e74\u7fb9\u5c27 \u7a81\u7136\u653e\u58f0\u5927\u54ed\uff0c\u9053\uff1a\u201c\u7fb9\u5c27\u4e3a\u56fd\u4ea1\u79c1\uff0c\u5168\u5fe0\u4e0d\u80fd\u5c3d\u4e49\u3002\u8fd9\u548c\u5c1a\u867d\u975e\u6211\u5e08\uff0c\u4f46\u6211\u66fe\u627f\u4ed6\u6307\u70b9\u6b66 \u827a\uff1b\u4eca\u65e5\u4ed6\u56fe\u8c0b\u52ab\u8d70\u53db\u9006\uff0c\u6211\u4e0d\u80fd\u4e0d\u4e3a\u671d\u5ef7\u8bdb\u4e4b\uff0c\u4e8e\u5fc3\u5374\u662f\u4e0d\u5fcd\u3002\u201d\u8bf4\u5b8c\u4e4b\u540e\uff0c\u62b9\u5e72\u773c\u6cea\uff0c \u5429\u5490\u6821\u5c09\u9053\uff1a\u201c\u7ed9\u4ed6\u4e70\u526f\u4e0a\u597d\u7684\u68fa\u6750\uff0c\u5c06\u4ed6\u539a\u846c\u4e86\u3002\u201d\u5cb3\u949f\u6dc7\u6697\u81ea\u9f7f\u51b7\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u8fd9\u732b\u54ed \u8001\u9f20\u5047\u6148\u60b2\uff0c\u505a\u7ed9\u8c01\u770b\u3002\u201d\u81ea\u6b64\u66f4\u770b\u900f\u5e74\u7fb9\u5c27\u9762\u76ee\uff0c\u5728\u754f\u60e7\u4e4b\u4e2d\u6697\u52a0\u6212\u5907\u3002 \u3000\u3000\u4e86\u56e0\u7b49\u56db\u4eba\u4f24\u5f97\u751a\u91cd\uff0c\u5929\u53f6\u6563\u4eba\u88ab\u62c2\u5c18\u67c4\u63d2\u7a7f\u808b\u9aa8\uff0c\u5c24\u5176\u4f24\u5f97\u5389\u5bb3\u3002\u5e74\u7fb9\u5c27\u547d\u624b\u4e0b\u5c06\u4ed6 \u4eec\u6551\u9192\uff0c\u4e00\u4e2a\u4e2a\u90fd\u54fc\u54fc\u561f\u561f\uff0c\u4e0d\u80fd\u52a8\u5f39\u3002\u4e86\u56e0\u7684\u7985\u6756\u7ed9\u672c\u65e0\u5927\u5e08\u62d7\u5f97\u5f2f\u5f2f\u66f2\u66f2\uff0c\u521a\u521a\u9192\u6765\uff0c \u53c8\u6c14\u5f97\u6655\u4e86\u8fc7\u53bb\uff0c\u5e78\u5728\u8fd9\u56db\u4eba\u90fd\u662f\u529f\u529b\u6df1\u539a\uff0c\u867d\u53d7\u91cd\u4f24\uff0c\u5c1a\u672a\u81f4\u547d\uff0c\u5e74\u7fb9\u5c27\u662f\u949f\u4e07\u5802\u7684\u5f1f \u5b50\uff0c\u9887\u61c2\u533b\u7406\uff0c\u6025\u8f9f\u9759\u5ba4\u7ed9\u4ed6\u4eec\u8c03\u6cbb\uff0c\u540c\u65f6\u5fc3\u4e2d\u76d8\u7b97\u5bf9\u4ed8\u5c11\u6797\u4e4b\u7b56\u3002 \u3000\u3000\u7518\u51e4\u6c60\u548c\u5415\u56db\u5a18\u7b49\u5728\u8f66\u9f0e\u4e30\u5bb6\u91cc\u85cf\u8eb2\uff0c\u8f66\u9f0e\u4e30\u4f24\u52bf\u5df2\u6108\uff0c\u5415\u56db\u5a18\u4e2d\u7684\u4e0d\u662f\u6bd2\u9488\uff0c\u7528\u78c1\u77f3 \u5438\u51fa\u4e4b\u540e\uff0c\u8c03\u517b\u4e24\u65e5\uff0c\u4ea6\u5df2\u884c\u52a8\u5982\u5e38\u3002\u8fd9\u65e5\u805a\u5728\u5bb6\u4e2d\u95f2\u8bdd\uff0c\u5415\u56db\u5a18\u9053\uff1a\u201c\u90a3\u65e5\u5e78\u4e8f\u6709\u672c\u65e0\u5927 \u5e08\uff0c\u8981\u4e0d\u7136\u5c0f\u59b9\u53ea\u6050\u4e0d\u80fd\u4e0e\u8bf8\u5144\u76f8\u89c1\u4e86\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u672c\u65e0\u5927\u5e08\u65b9\u9053\u70ed\u80a0\uff0c\u786e\u662f\u4ee4\u4eba\u94a6 \u4f69\u3002\u4f46\u4ed6\u884c\u4e8b\u4efb\u6027\u7387\u771f\uff0c\u5bf9\u5e74\u7fb9\u5c27\u90a3\u53ae\uff0c\u53e3\u867d\u75db\u9a82\uff0c\u5fc3\u5b9e\u7231\u4e4b\u3002\u6211\u5012\u4e0d\u80fd\u4e0d\u4e3a\u4ed6\u62c5\u5fc3\u5462\uff01\u201d \u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u6709\u4eba\u62a5\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u548c\u5c1a\uff0c\u50e7\u8863\u7834\u788e\uff0c\u6ee1\u9762\u8840\u6c61\uff0c\u6c42\u89c1\u7518\u5927\u4fa0\u3002\u201d\u7518\u51e4\u6c60\u201c\u554a \u5440\u201d\u4e00\u58f0\uff0c\u6025\u5fd9\u5954\u53bb\u5f00\u95e8\uff0c\u4e00\u4e2a\u548c\u5c1a\u8e09\u8e09\u8dc4\u8dc4\u7684\u51b2\u4e86\u8fdb\u6765\uff0c\u4e00\u8de4\u8dcc\u843d\u5730\u4e0a\uff0c\u7518\u51e4\u6c60\u4e00\u770b\u51fa\u4e0d \u662f\u672c\u65e0\u5927\u5e08\uff0c\u5fc3\u4e2d\u60ca\u7591\u4e0d\u5df2\u3002\u8def\u6c11\u77bb\u95fb\u58f0\u8d70\u51fa\uff0c\u5927\u53eb\u9053\uff1a\u201c\u5370\u5b8f\u5e08\u5144\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u6025\u53d6\u51b7 \u6c34\u5c06\u4ed6\u55b7\u9192\u3002\u5370\u5b8f\u5927\u54ed\u9053\uff1a\u201c\u6211\u7684\u5e08\u5085\u53ea\u6015\u5df2\u906d\u6bd2\u624b\u4e86\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u5fc3\u75db\u5982\u7ede\uff0c\u6025\u95ee\u8be6\u60c5\u3002\u5370\u5b8f\u4e00\u4e00\u8bf4\u4e86\u3002\u5415\u56db\u5a18\u7518\u51e4\u6c60\u6012\u9053\uff1a\u201c\u597d\uff0c\u672c\u65e0\u5927\u5e08\u82e5\u6709 \u4e09\u957f\u4e8c\u77ed\uff0c\u6211\u4eec\u8a93\u5fc5\u4e3a\u4ed6\u62a5\u4ec7\u3002\u201d\u5370\u5b8f\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u624b\u63e1\u5927\u519b\uff0c\u5e10\u4e2d\u9ad8\u624b\u5982\u4e91\uff0c\u8fd9\u4ec7\u6781\u4e0d\u6613 \u62a5\uff01\u6211\u60f3\u5728\u6b64\u7a0d\u606f\u4e4b\u540e\uff0c\u4fbf\u56de\u5d69\u5c71\uff0c\u544a\u8bc9\u4e3b\u6301\u65b9\u4e08\u77e5\u9053\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u597d\uff0c\u6211\u9001\u4f60\u5230\u5d69 \u5c71\u3002\u201d \u3000\u3000\u5f85\u5230\u665a\u95f4\uff0c\u8f66\u9f0e\u4e30\u6d3e\u53bb\u6253\u542c\u7684\u4eba\u56de\u6765\uff0c\u8bc1\u5b9e\u4e86\u672c\u65e0\u6b7b\u8baf\u3002\u8bf8\u4fa0\u5927\u54ed\u4e00\u573a\uff0c\u8bbe\u7075\u796d\u5960\u3002\u6b63 \u81ea\u4f24\u5fc3\uff0c\u5ffd\u95fb\u5f97\u6709\u201c\u53ee\u53ee\u201d\u4e4b\u58f0\uff0c\u8fdc\u8fdc\u4f20\u6765\uff0c\u7518\u51e4\u6c60\u4e00\u8dc3\u800c\u8d77\uff0c\u63a8\u95e8\u51fa\u671b\u3002\u5bff\u660c\u4e66\u9662\u8bbe\u5728\u5fc3 \u9e93\uff0c\u5c71\u98ce\u9001\u58f0\uff0c\u66f4\u4e3a\u6e05\u6f88\uff0c\u7518\u51e4\u6c60\u767b\u9ad8\u773a\u671b\uff0c\u4e0d\u89c1\u4eba\u5f71\uff0c\u6b63\u81ea\u60ca\u5947\uff0c\u5ffd\u95fb\u5f97\u94c3\u58f0\u53c8\u8d77\uff0c\u4e00\u6761 \u4eba\u5f71\u7a81\u5728\u5c71\u62d7\u51fa\u73b0\uff0c\u500f\u7136\u4e4b\u95f4\uff0c\u5c31\u5230\u534a\u5c71\u3002\u7518\u51e4\u6c60\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u516b\u59b9\uff0c\u4f60\u6765\uff01\u201d\u8bdd\u58f0 \u672a\u505c\uff0c\u90a3\u4eba\u5df2\u5230\u9762\u524d\uff0c\u662f\u4e00\u4e2a\u624b\u63d0\u201c\u864e\u6491\u201d\u3001\u957f\u7740\u4e09\u7efa\u957f\u987b\u7684\u6c5f\u6e56\u90ce\u4e2d\uff0c\u5411\u7518\u51e4\u4ed6\u6253\u4e86\u4e00\u4e2a \u7a3d\u9996\uff0c\u95ee\u9053\uff1a\u201c\u8f66\u9f0e\u4e30\u8001\u5148\u751f\u5728\u8fd9\u513f\u5417\uff1f\u201d\u5415\u56db\u5a18\u4e0e\u8f66\u9f0e\u4e30\u81ea\u5185\u8d70\u51fa\uff0c\u4e00\u770b\u5168\u4e0d\u8ba4\u8bc6\u3002\u7518\u51e4 \u6c60\u8d77\u4e86\u7591\u5fc3\uff0c\u6b63\u60f3\u51fa\u8a00\u8bd5\u63a2\uff0c\u8f66\u9f0e\u4e30\u8d77\u5148\u4e0d\u6562\u8868\u9732\u8eab\u4efd\uff0c\u53ca\u81f3\u89c1\u4e86\u4ed6\u7684\u864e\u6491\uff0c\u7aef\u8be6\u4e00\u9635\uff0c\u5ffd \u7136\u53eb\u9053\uff1a\u201c\u6765\u7684\u83ab\u975e\u662f\u6b66\u8001\u524d\u8f88\u5417\uff1f\u201d \u3000\u3000\u90a3\u6c5f\u6e56\u90ce\u4e2d\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u5c0f\u59d3\u6b66\uff0c\u8001\u5148\u751f\u5982\u4f55\u5f97\u77e5\uff1f\u201d\u8f66\u9f0e\u4e30\u9053\uff1a\u201c\u674e\u516c\u5b50\u66fe\u7ecf\u63d0 \u53ca\u3002\u201d\u6c5f\u6e56\u90ce\u4e2d\u9053\uff0c\u201c\u539f\u6765\u5c0a\u9a7e\u4fbf\u662f\u8f66\u8001\u5148\u751f\u3002\u201d\u8f66\u9f0e\u4e30\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u8001\u524d\u8f88\u53ef\u89c1\u5230\u674e\u516c\u5b50 \u4e48\uff1f\u201d\u90a3\u6c5f\u6e56\u90ce\u4e2d\u9762\u73b0\u60ca\u8be7\u4e4b\u5bb9\uff0c\u770b\u4e86\u7518\u51e4\u6c60\u548c\u5415\u56db\u5a18\u4e00\u773c\u3002\u8f66\u9f0e\u4e30\u8fde\u5fd9\u7ed9\u4ed6\u4eec\u4ecb\u7ecd\uff0c\u7518\u51e4 \u6c60\u542c\u8bf4\u6b64\u4eba\u4fbf\u662f\u6b66\u743c\u6447\u7684\u5f1f\u5f1f\u6b66\u6210\u5316\uff0c\u6025\u4ee5\u5148\u8f88\u4e4b\u793c\u53c2\u89c1\uff0c\u6b66\u6210\u5316\u9053\uff1a\u201c\u6b20\u4ef0\u6c5f\u5357\u5927\u4fa0\u76db \u540d\uff0c\u6211\u4e0e\u5c0a\u5e08\u867d\u66fe\u6709\u4e00\u9762\u4e4b\u7f18\uff0c\u4f46\u95e8\u6237\u6beb\u4e0d\u76f8\u8fde\uff0c\u54b1\u4eec\u8fd8\u662f\u5404\u4ea4\u5404\u7684\uff0c\u4ee5\u5e73\u8f88\u76f8\u79f0\u597d\u4e86\u3002\u201d \u7518\u51e4\u6c60\u6267\u610f\u4e0d\u80af\uff0c\u6b66\u6210\u5316\u65e0\u5948\u53d7\u4e86\u4ed6\u534a\u793c\uff0c\u968f\u4f17\u4eba\u540c\u5165\u5bff\u660c\u4e66\u9662\u3002 \u3000\u3000\u5750\u5b9a\u4e4b\u540e\uff0c\u6b66\u6210\u5316\u9053\uff1a\u201c\u674e\u6cbb\u4e0d\u5728\u8fd9\u91cc\u4e48\uff1f\u201d\u8f66\u9f0e\u4e30\u9053\uff1a\u201c\u6211\u4eec\u6b63\u60f3\u627e\u4ed6\u3002\u201d\u6b66\u6210\u5316 \u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u771f\u662f\u5c11\u4e0d\u66f4\u4e8b\uff0c\u6211\u5343\u8f9b\u4e07\u82e6\u628a\u4ed6\u6551\u51fa\u6765\uff0c\u53eb\u4ed6\u4e0d\u8981\u968f\u4fbf\u8d70\u52a8\uff0c\u90a3\u77e5\u8f6c\u4e00\u8f6c\u773c\uff0c \u4ed6\u5c31\u8dd1\u5931\u4e86\u3002\u201d \u3000\u3000\u8f66\u9f0e\u4e30\u83ab\u540d\u5176\u5999\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u6b66\u6210\u5316\u9053\uff1a\u201c\u6211\u59d0\u59d0\u8fd9\u6b21\u53eb\u4ed6\u4e0b\u5c71\u5386\u7ec3\uff0c\u751a\u4e0d \u653e\u5fc3\uff0c\u6240\u4ee5\u6258\u6211\u6697\u4e2d\u4fdd\u62a4\u3002\u90a3\u77e5\u4ed6\u5728\u6e56\u6ee8\u88ab\u4e86\u56e0\u6240\u64d2\uff0c\u4f24\u4e86\u7b4b\u9aa8\u3002\u6211\u628a\u4ed6\u4ece\u6e10\u629a\u536b\u58eb\u7684\u624b\u4e2d \u62a2\u4e86\u51fa\u6765\u3002\u53c8\u7528\u6d41\u4e91\u98de\u8896\u7684\u7edd\u62db\u5c06\u90a3\u51f6\u50e7\u5413\u8d70\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u554a\uff0c\u674e\u516c\u5b50\u771f\u662f\u4fe1\u4eba\uff0c\u4ed6\u679c \u7136\u90a3\u5929\u7edd\u65e9\u5c31\u627e\u6211\u4eec\u3002\u8fd8\u6709\u90a3\u4e2a\u5c0f\u59d1\u5a18\u5462\u3002\u201d\u6b66\u6210\u5316\u9053\uff1a\u201c\u6211\u6ca1\u6709\u89c1\u7740\u4ec0\u4e48\u5c0f\u59d1\u5a18\uff1f\u201d \u3000\u3000\u6b66\u6210\u5316\u5477\u4e86\u4e00\u53e3\u70ed\u8336\uff0c\u7eed\u9053\uff1a\u201c\u6211\u5c06\u4ed6\u6551\u51fa\u4e4b\u540e\u4fbf\u628a\u5730\u5e26\u56de\u9053\u89c2\u2014\u2014\u6211\u5728\u5b9d\u77f3\u5c71\u7684\u9ec4\u9f99 \u89c2\u5bc4\u5c45\u3002\u6211\u53eb\u4ed6\u5728\u9053\u89c2\u4e2d\u7597\u4f24\uff0c\u7b49\u5e74\u7fb9\u5c27\u5927\u519b\u53bb\u540e\uff0c\u624d\u51fa\u6765\u8d70\u52a8\u3002\u6628\u5929\u6211\u51fa\u57ce\u66ff\u4ed6\u914d\u836f\uff0c\u56de \u6765\u65f6\u542c\u9053\u58eb\u8bf4\u4ed6\u5df2\u51fa\u53bb\u4e86\u3002\u76f4\u7b49\u5230\u534a\u591c\uff0c\u90fd\u4e0d\u89c1\u4ed6\u56de\u6765\uff0c\u53c8\u6ca1\u6709\u7559\u4e0b\u4e66\u4fe1\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u4e00\u5b9a \u662f\u5230\u5bff\u660c\u4e66\u9662\u6765\u627e\u4f60\u5462\uff01\u201d \u3000\u3000\u8f66\u9f0e\u4e30\u548c\u7518\u51e4\u6c60\u9762\u9762\u76f8\u89d1\uff0c\u751a\u662f\u62c5\u5fe7\uff0c\u5415\u56db\u5a18\u9053\uff1a\u201c\u674e\u516c\u5b50\u4eba\u5f88\u7cbe\u660e\uff0c\u5251\u672f\u53c8\u9ad8\uff0c\u6599\u65e0 \u610f\u5916\u3002\u201d\u6b66\u6210\u5316\u9053\uff1a\u201c\u676d\u5dde\u9ad8\u624b\u4e91\u96c6\uff0c\u53ea\u4e00\u4e2a\u4e86\u56e0\u548c\u5c1a\u4ed6\u5df2\u5bf9\u4ed8\u4e0d\u4e86\uff0c\u6211\u5982\u4f55\u4e0d\u66ff\u4ed6\u62c5 \u5fc3\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u6599\u4e86\u56e0\u4ed6\u4eec\u4e5f\u5fc5\u7136\u53d7\u4e86\u91cd\u4f24\u3002\u4ee5\u674e\u516c\u5b50\u7684\u6b66\u529f\uff0c\u9664\u4e86\u4e86\u56e0\u8fd9\u73ed\u4eba\u4e4b \u5916\uff0c\u5176\u4ed6\u7684\u4eba\u4e5f\u4f24\u4ed6\u4e0d\u5f97\uff01\u201d\u6b66\u6210\u5316\u9053\uff1a\u201c\u7518\u5927\u4fa0\u4f55\u4ee5\u6599\u90a3\u51f6\u50e7\u4f1a\u53d7\u91cd\u4f24\uff1f\u201d\u4ed6\u60f3\uff1a\u4ee5\u81ea\u5df1 \u7684\u6b66\u529f\u4e5f\u53ea\u80fd\u628a\u4e86\u56e0\u6253\u8d25\uff0c\u800c\u4e0d\u80fd\u4f24\u4ed6\uff0c\u8fd8\u6709\u4f55\u4eba\u6709\u6b64\u672c\u9886\uff1f\u7518\u51e4\u6c60\u628a\u672c\u65e0\u5927\u5e08\u9047\u5bb3\u7684\u4e8b\u8bf4 \u4e86\uff0c\u9053\uff1a\u201c\u4f9d\u6b66\u8001\u524d\u8f88\u8bf4\u6765\uff0c\u4e86\u56e0\u88ab\u5413\u8d70\u4e4b\u540e\uff0c\u6b63\u597d\u4e0e\u5e74\u7fb9\u5c27\u7684\u5927\u519b\u76f8\u9047\uff0c\u5e74\u7fb9\u5c27\u5fc5\u9080\u4ed6\u8fdb \u5e10\u65e0\u7591\u7684\u4e86\u3002\u672c\u65e0\u5927\u5e08\u662f\u5f53\u4eca\u6570\u4e00\u6570\u4e8c\u7684\u4eba\u7269\uff0c\u5c82\u6709\u675f\u624b\u88ab\u5bb3\u4e4b\u7406\u3002\u201d\u5370\u5b8f\u548c\u5c1a\u8fd9\u65f6\u4f24\u4e5f\u597d \u4e86\uff0c\u51fa\u6765\u89c1\u8fc7\u6b66\u6210\u5316\uff0c\u9053\u53ca\u5f53\u65f6\u4e4b\u4e8b\uff0c\u4e5f\u6599\u4e86\u56e0\u7b49\u56db\u540d\u9ad8\u624b\u5fc5\u4f24\u65e0\u7591\uff0c\u6b66\u6210\u5316\u8fd9\u624d\u7565\u653e\u4e86\u5fc3\u3002 \u3000\u3000\u6b66\u6210\u5316\u5bf9\u672c\u65e0\u5927\u5e08\u662f\u4e45\u5df1\u4ef0\u6155\uff0c\u95fb\u4ed6\u5669\u8017\uff0c\u81ea\u4e5f\u4e0d\u514d\u4f24\u5fc3\uff0c\u5728\u7518\u51e4\u6c60\u7b49\u4eba\u6240\u8bbe\u7684\u7075\u524d\u796d \u4e86\uff0c\u9053\uff1a\u201c\u8fd8\u8981\u53bb\u627e\u674e\u6cbb\u3002\u201d\u8f66\u9f0e\u4e30\u9053\uff1a\u201c\u591c\u5df2\u6df1\u4e86\uff0c\u6b47\u4e00\u5bb5\u5427\u3002\u660e\u65e5\u5927\u5bb6\u5206\u5934\u53bb\u627e\u3002\u201d \u3000\u3000\u8f66\u9f0e\u4e30\u5728\u5bff\u660c\u4e66\u9662\uff0c\u540d\u867d\u8bb2\u5b66\uff0c\u5b9e\u662f\u5ba3\u626c\u5415\u7559\u826f\u6518\u5937\u536b\u56fd\u4e4b\u8bf4\uff0c\u6240\u4ee5\u805a\u96c6\u6709\u4e00\u73ed\u6709\u8840\u6c14 \u7684\u9752\u5e74\uff0c\u4fe8\u7136\u6210\u4e3a\u6d59\u6c5f\u79d8\u5bc6\u53cd\u6e05\u5e2e\u4f1a\u7684\u4e00\u652f\u3002\u8f66\u9f0e\u4e30\u6697\u4e2d\u6d3e\u4eba\u5bfb\u627e\u674e\u6cbb\uff0c\u4e00\u8fde\u4e09\u5929\uff0c\u7adf\u662f\u6beb \u65e0\u6d88\u606f\u3002 \u3000\u3000\u518d\u8fc7\u51e0\u65e5\uff0c\u5e74\u7fb9\u5c27\u7684\u5927\u519b\u4e5f\u5f00\u8d70\u4e86\uff0c\u674e\u6cbb\u7684\u6d88\u606f\uff0c\u4ecd\u7136\u63a2\u4e0d\u51fa\u6765\u3002\u6b66\u6210\u5316\u53f9\u4e86\u53e3\u6c14\uff0c \u9053\uff1a\u201c\u770b\u6765\u53ea\u6709\u5e7f\u6258\u6c5f\u6e56\u7684\u670b\u53cb\u4ee3\u4e3a\u5bfb\u627e\u4e86\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6c5f\u5357\u4e00\u5e26\uff0c\u6211\u53ef\u5c3d\u529b\u3002\u201d\u5415\u56db \u5a18\u95ee\u6b66\u6210\u5316\u4eca\u540e\u884c\u6b62\uff0c\u6b66\u6210\u5316\u9053\uff1a\u201c\u6211\u60f3\u5230\u5e74\u7fb9\u5c27\u6545\u91cc\u4e00\u884c\u3002\u628a\u949f\u4e07\u5802\u9057\u9aa8\u8fc1\u846c\u3002\u201d\u4f17\u4eba\u77e5 \u9053\u4ed6\u66fe\u5f97\u65e0\u6781\u6d3e\u5148\u7956\u5085\u9752\u4e3b\u6307\u70b9\uff0c\u7b97\u8d77\u6765\u6bd4\u949f\u4e07\u5802\u8fd8\u9ad8\u4e00\u8f88\uff0c\u949f\u4e07\u5802\u6b7b\u540e\uff0c\u65e0\u6781\u6d3e\u6ca1\u6709\u4f20 \u4eba\uff0c\u540e\u4e8b\u81ea\u7136\u8be5\u4ed6\u7167\u6599\u3002 \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u4eec\u9001\u5370\u5b8f\u5927\u5e08\u56de\u5d69\u5c71\uff0c\u6b63\u597d\u4e0e\u524d\u8f88\u4e00\u8def\u3002\u201d\u7b2c\u4e8c\u65e5\uff0c\u7518\u51e4\u6c60\u5415\u56db\u5a18\u8def\u6c11 \u77bb\u767d\u6cf0\u5b98\u9c7c\u5a18\u7b49\u4e00\u884c\uff0c\u548c\u8f66\u9f0e\u4e30\u90d1\u91cd\u9053\u522b\uff0c\u62a4\u9001\u5370\u5b8f\u56de\u5c71\uff0c\u6b66\u6210\u5316\u548c\u4ed6\u4eec\u4e00\u8def\u5230\u4e86\u767b\u5c01\u4e4b \u540e\uff0c\u4fbf\u5404\u81ea\u5206\u624b\u3002 \u3000\u3000\u4e00\u522b\u6570\u5e74\uff0c\u6c5f\u5c71\u4ecd\u65e7\uff0c\u5415\u56db\u5a18\u4e00\u9762\u767b\u5c71\uff0c\u4e00\u9762\u6168\u53f9\uff0c\u53ea\u6015\u8fd9\u5343\u5e74\u53e4\u5239\u4f1a\u5316\u52ab\u7070\u3002\u5370\u5b8f\u66f4 \u662f\u795e\u4f24\u3002\u4e0a\u5230\u5c71\u6765\uff0c\u65e9\u6709\u77e5\u5ba2\u50e7\u8fce\u63a5\u5165\u5bfa\u3002 \u3000\u3000\u5230\u4e86\u89e3\u884c\u7cbe\u820d\uff0c\u5f18\u6cd5\u5927\u5e08\u4eb2\u81ea\u51fa\u8fce\uff0c\u5f18\u6cd5\u662f\u638c\u7ecf\u5802\u7684\u9996\u5ea7\u9ad8\u50e7\uff0c\u5730\u4f4d\u4ec5\u5728\u5c11\u6797\u4e09\u8001\u4e4b \u4e0b\uff0c\u5370\u5b8f\u6025\u5fd9\u4e0a\u524d\u53c2\u89c1\u3002\u5f18\u6cd5\u5927\u5e08\u9762\u8272\u6c89\u91cd\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u76d1\u5bfa\u7684\u5669\u8017\uff0c\u4e3b\u6301\u5df2\u7ecf\u77e5\u9053 \u4e86\u3002\u201d\u5370\u5b8f\u9053\uff1a\u201c\u8bf7\u5e08\u53d4\u4ee3\u7980\u4e3b\u6301\uff0c\u7b2c\u56db\u5341\u516b\u4ee3\u5f1f\u5b50\u5370\u5b8f\u53c2\u8c12\u3002\u201d\u5f18\u6cd5\u9053\uff1a\u201c\u4e3b\u6301\u6b63\u5728\u8fbe\u6469 \u9662\u8bb2\u7ecf\uff0c\u6211\u5df2\u66ff\u4f60\u7559\u4e0b\u5ea7\u4f4d\uff0c\u4f60\u53bb\u542c\u5427\uff0c\u8fd9\u6b21\u6050\u6015\u662f\u4ed6\u5728\u5d69\u5c71\u672c\u5bfa\u6700\u540e\u4e00\u6b21\u7684\u8bb2\u7ecf\u4e86\u3002\u201d \u3000\u3000\u5f18\u6cd5\u5927\u5e08\u966a\u8d35\u5bbe\u5728\u89e3\u884c\u7cbe\u820d\u8bf4\u8bdd\uff0c\u5370\u5b8f\u548c\u5c1a\u6000\u7740\u6c89\u91cd\u7684\u5fc3\u60c5\uff0c\u6084\u6084\u7684\u8d70\u8fdb\u8fbe\u6469\u9662\u672b\u4f4d\u5750 \u4e0b\uff0c\u53ea\u89c1\u672c\u5bfa\u5341\u4e8c\u540d\u5927\u5f1f\u5b50\u90fd\u5782\u9996\u80f8\u81c6\uff0c\u51dd\u5750\u542c\u7ecf\u3002 \u3000\u3000\u65e0\u4f4f\u7985\u5e08\u9762\u5bb9\u8083\u7a46\uff0c\u58f0\u8c03\u4f4e\u6c89\uff0c\u8bb2\u7684\u662f\u201c\u6cd5\u534e\u7ecf\u201d\u4e2d\u7684\u4e00\u8282\uff0c\u7ecf\u5802\u7684\u6c14\u6c1b\u867d\u7136\u51c4\u6006\uff0c\u7ecf \u6587\u7684\u6545\u4e8b\u5374\u751a\u6709\u8da3\u3002\u5927\u610f\u662f\u8bf4\uff1a\u5f53\u4e94\u767e\u963f\u7f57\u6c49\u4e8e\u4f5b\u524d\u53d7\u6212\u4e4b\u65e5\uff0c\u4f5b\u7956\u5f15\u5bfc\u4ed6\u4eec\u6094\u8fc7\u81ea\u8d23\u3002\u9996 \u5e2d\u7f57\u6c49\u9053\uff1a\u201c\u4e16\u5c0a\uff0c\u6211\u7b49\u5e38\u4f5c\u662f\u5ff5\uff0c\u81ea\u8c13\u5df2\u5f97\u7a76\u7adf\u706d\u5ea6\uff08\u6309\uff1a\u53ef\u4f5c\u5f7b\u609f\u771f\u7406\u89e3\uff09\uff0c\u4eca\u4e43\u77e5 \u4e4b\uff0c\u5982\u65e0\u667a\u8005\u3002\u6240\u4ee5\u8005\u4f55\uff1f\u6211\u7b49\u5e94\u5f97\u5982\u6765\u667a\u6167\uff0c\u800c\u4fbf\u81ea\u4ee5\u5c0f\u667a\u4e3a\u8db3\u3002\u201d\u4f5b\u7956\u53eb\u4ed6\u4e3e\u4f8b\uff0c\u4ed6\u4fbf \u8bf4\u4e86\u4e00\u4e2a\u6545\u4e8b\uff0c\u8bf4\u662f\u5370\u5ea6\u65b9\u65f6\u6709\u4e00\u4e2a\u4eba\uff0c\u4ed6\u7684\u4eb2\u53cb\u9001\u4ed6\u4e00\u4ef6\u8863\u670d\uff0c\u8863\u91cc\u85cf\u6709\u4e00\u9897\u65e0\u4ef7\u5b9d\u73e0\uff0c \u4ed6\u5374\u4e0d\u77e5\u9053\uff0c\u56e0\u4e3a\u8d2b\u7a77\uff0c\u5230\u5904\u6d41\u6d6a\uff0c\u201c\u4e3a\u8863\u98df\u6545\uff0c\u52e4\u529b\u6c42\u7d22\uff0c\u751a\u5927\u8270\u96be\uff0c\u5c11\u6709\u6240\u5f97\uff0c\u4fbf\u4ee5\u4e3a \u8db3\u201d\u540e\u6765\u90a3\u4eb2\u53cb\u89c1\u4e86\u4ed6\uff0c\u8bf4\u4f60\u6709\u5b9d\u73e0\u800c\u4e0d\u81ea\u77e5\uff0c\u201c\u52e4\u82e6\u5fe7\u52b3\uff0c\u4ee5\u6c42\u81ea\u6d3b\uff0c\u751a\u4e3a\u75f4\u4e5f\u3002\u201d\u90a3\u4eba \u56e0\u6b64\u800c\u609f\u4e86\u4f5b\u7406\u3002 \u3000\u3000\u8fd9\u6545\u4e8b\u5370\u5b8f\u4e5f\u66fe\u542c\u8fc7\uff0c\u4ee5\u524d\u53ea\u89c9\u6709\u8da3\uff0c\u5e76\u672a\u9886\u7565\u5176\u4e2d\u5999\u8c1b\u3002\u800c\u4eca\u91cd\u542c\uff0c\u5ffd\u7136\u5982\u6709\u6240\u89e6\uff0c \u53ea\u542c\u5f97\u65e0\u4f4f\u8bb2\u9053\uff1a\u201c\u6211\u5d69\u5c71\u5c11\u6797\uff0c\u5efa\u5bfa\u5df2\u5386\u4e00\u5343\u4e09\u767e\u4f59\u5e74\uff0c\u5386\u4ee3\u52e4\u52b3\uff0c\u59cb\u6709\u4e86\u4eca\u65e5\u7684\u89c4\u6a21\u3002 \u4f46\u4e5f\u6b63\u56e0\u6b64\uff0c\u6709\u4e9b\u4eba\u4fbf\u56e0\u4e3a\u7ecf\u8fc7\u8270\u96be\uff0c\u5c11\u6709\u6240\u5f97\uff0c\u4fbf\u4ee5\u4e3a\u8db3\u3002\u6211\u81ea\u5df1\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u5176\u5b9e\u5c11 \u6797\u7684\u89c4\u6a21\u3002\u6bd4\u8d77\u8fbe\u6469\u7956\u5e08\u6240\u4f20\u7684\u7ecf\u6587\u81f3\u7406\uff0c\u6b63\u7b49\u5982\u90a3\u4eba\u6240\u83b7\u7684\u2018\u8863\u98df\u2019\u4e0e\u90a3\u2018\u65e0\u4ef7\u5b9d\u73e0\u2019\u4e4b \u6bd4\uff0c\u82e5\u2018\u53ea\u6c42\u81ea\u6d3b\u2019\u2018\u52e4\u82e6\u5fe7\u52b3\u2019\u90a3\u4fbf\u2018\u5815\u5165\u4e0b\u4e58\u2019\u4e86\u3002\u4f60\u4eec\u5c06\u6765\u820d\u4e86\u57fa\u4e1a\uff0c\u5230\u5904\u6d41\u6d6a\u4e4b \u65f6\uff0c\u5e94\u8be5\u8bb0\u7740\u4f60\u4eec\u672c\u6765\u6709\u4e00\u9897\u2018\u65e0\u4ef7\u5b9d\u73e0\u2019\u4e0d\u8981\u53ea\u56e0\u8863\u98df\u4e4b\u6545\uff0c\u800c\u5815\u7d6e\u6cbe\u6ce5\uff0c\u90a3\u624d\u662f\u6211\u4f5b\u95e8 \u5f1f\u5b50\u3002\u201d\u8bb2\u5b8c\u4e4b\u540e\u5341\u4e8c\u9ad8\u50e7\u548c\u5370\u5b8f\u548c\u5c1a\u90fd\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u65e0\u4f4f\u7985\u5e08\u9053\uff1a\u201c\u8bf7\u7518\u5927\u4fa0\u6765\u3002\u201d\u5370\u5b8f\u4e0a\u524d\u53c2\u8c12\uff0c\u65e0\u4f4f\u7985\u5e08\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8bf4\u4e86\u3002\u201d\u8fc7\u4e86\u4e00 \u9635\uff0c\u77e5\u5ba2\u50e7\u966a\u7518\u51e4\u6c60\u8fdb\u6765\uff0c\u65e0\u4f4f\u7985\u5e08\u62f1\u624b\u9053\uff1a\u201c\u7518\u5927\u4fa0\u4e49\u8584\u4e91\u5929\uff0c\u8fdc\u6765\u62a5\u8baf\uff0c\u6211\u4eec\u5c11\u6797\u5bfa\u50e7 \u65e0\u4e0d\u611f\u6fc0\u3002\u201d\u7518\u51e4\u6c60\u6025\u5fd9\u8fd8\u793c\uff0c\u9053\uff1a\u201c\u672c\u65e0\u5927\u5e08\u4e00\u4ee3\u5b97\u5e08\uff0c\u7adf\u906d\u6697\u5bb3\uff0c\u51e4\u6c60\u8eab\u5728\u676d\u5dde\uff0c\u4e0d\u80fd \u5206\u96be\uff0c\u60ed\u6127\u65e0\u6bd4\u3002\u201d\u65e0\u4f4f\u7985\u5e08\u9053\uff1a\u201c\u67ad\u96c4\u5f53\u9053\uff0c\u4eba\u529b\u96be\u633d\u6d69\u52ab\uff0c\u672c\u65e0\u5e08\u5144\u867d\u6b7b\uff0c\u4e8b\u60c5\u53ea\u6050\u8fd8 \u672a\u4e86\u5462\uff01\u201d\u7518\u51e4\u6c60\u9ed8\u7136\u4e0d\u8bed\u3002\u65e0\u4f4f\u9053\uff1a\u201c\u5141\u796f\u4e0e\u5e74\u7fb9\u5c27\u90fd\u51fa\u81ea\u5c11\u6797\uff0c\u5141\u796f\u82e5\u767b\u5927\u5b9d\uff0c\u6709\u5e74\u7fb9 \u5c27\u52a9\u6076\uff0c\u90a3\u5c31\u662f\u5c11\u6797\u7684\u5927\u52ab\u5230\u4e86\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4ee5\u7985\u5e08\u5927\u529b\uff0c\u96be\u9053\u6ca1\u6709\u633d\u56de\u7684\u4f59\u5730\u5417\uff1f\u201d \u65e0\u4f4f\u9053\uff1a\u201c\u9664\u975e\u6362\u4e86\u6ca7\u6851\uff0c\u5426\u5219\u8fd9\u573a\u6d69\u52ab\u5fc5\u514d\u4e0d\u4e86\u3002\u201d\u7518\u51e4\u6c60\u60f3\u9053\uff1a\u201c\u53cd\u4e86\u5427\uff01\u201d\u89c1\u65e0\u4f4f\u7985 \u5e08\u53cc\u7738\u70af\u70af\uff0c\u773a\u671b\u8fdc\u65b9\uff0c\u77e5\u4ed6\u6b63\u5728\u6c89\u601d\uff0c\u4e0d\u6562\u8a00\u8bed\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u65e0\u4f4f\u7985\u5e08\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u660e\u65e5 \u8d77\u5c11\u6797\u5bfa\u50e7\u4fbf\u8981\u6e10\u6e10\u758f\u6563\u4e86\uff0c\u6211\u60f3\u5728\u798f\u5efa\u7684\u8386\u7530\u548c\u5e7f\u4e1c\u7684\u5357\u6d77\u518d\u5efa\u6839\u57fa\uff0c\u5c06\u6765\u53ea\u6015\u8fd8\u6709\u8981\u4ef0 \u4ed7\u7518\u5927\u4fa0\u4e4b\u5904\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u7985\u5e08\u82e5\u6709\u6240\u9700\uff0c\u53ea\u7ba1\u5429\u5490\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u7b49\u5728\u5c11\u6797\u5bfa\u4f4f\u4e86\u51e0\u65e5\uff0c\u89c1\u5c11\u6797\u5bfa\u5fd9\u4e8e\u642c\u8fc1\uff0c\u4fbf\u5373\u544a\u8f9e\u3002\u4e0b\u5c71\u4e4b\u540e\uff0c\u5415\u56db\u5a18\u9053\uff1a \u201c\u6211\u95fb\u5f97\u5f18\u6cd5\u5927\u5e08\u8bf4\uff0c\u66fe\u9759\u5df2\u5230\u5317\u4eac\u3002\u201d\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u53cd\u6b63\u6211\u4eec\u5df2\u5230\u6cb3\u5357\uff0c\u4f55\u4e0d\u4e0a\u4eac\u4e00 \u8d9f\u3002\u201d\u66fe\u9759\u662f\u5415\u7559\u826f\u7684\u5f97\u610f\u751f\uff0c\u51e0\u5341\u5e74\u6765\u4ec6\u4ec6\u98ce\u5c18\uff0c\u5bc6\u8c0b\u590d\u56fd\uff0c\u548c\u4e25\u6d2a\u9035\u6c88\u5728\u5bbd\u7b49\uff0c\u90fd\u662f\u5fd8 \u5e74\u4e4b\u4ea4\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u548c\u5173\u4e1c\u56db\u4fa0\uff0c\u4e5f\u6709\u6765\u5e74\u5728\u4eac\u76f8\u89c1\u4e4b\u7ea6\uff0c\u53bb\u5c31\u53bb\u5427\uff0c\u4e0d\u8fc7\u5927\u5bb6\u8981\u5c0f\u5fc3 \u4e00\u70b9\u3002\u201d\u7518\u51e4\u6c60\u70bc\u6709\u6613\u5bb9\u4e39\uff0c\u5f53\u4e0b\u66ff\u5404\u4eba\u5316\u88c5\uff0c\u6613\u4e86\u5bb9\u8c8c\uff0c\u8ff3\u8d74\u4eac\u534e\u3002 \u3000\u3000\u79cb\u53bb\u51ac\u6765\uff0c\u65f6\u79fb\u5e8f\u6362\uff0c\u5230\u4e86\u4eac\u57ce\uff0c\u5df2\u662f\u4ef2\u51ac\u5b63\u8282\uff0c\u9e45\u6bdb\u4f3c\u7684\u96ea\u82b1\u4e0b\u5f97\u6b63\u7d27\u3002\u4f17\u4eba\u8fdb\u4e86\u57ce \u95e8\uff0c\u5ffd\u89c1\u4e00\u961f\u5587\u561b\uff0c\u6392\u7740\u4eea\u4ed7\uff0c\u5411\u7687\u5bab\u90a3\u65b9\u8fdb\u53d1\u3002\u4f17\u4eba\u8eb2\u5728\u4e00\u95f4\u5e97\u94fa\u7684\u6a90\u4e0b\uff0c\u542c\u5f97\u5e97\u4e2d\u7684\u4eba \u95f2\u8c08\u9053\uff1a\u201c\u542c\u8bf4\u8001\u7687\u5e1d\u5f97\u75c5\uff0c\u7279\u5730\u4ece\u897f\u85cf\u8bf7\u8fd9\u73ed\u5927\u5587\u561b\u6765\u66ff\u7687\u4e0a\u5ff5\u7ecf\u6518\u89e3\uff0c\u4f60\u770b\u90a3\u6d3e\u5934\u591a \u5927\u3002\u201d\u5415\u56db\u5a18\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5374\u4e0d\u8a00\u8bed\uff0c\u7b49\u5587\u561b\u8fc7\u540e\uff0c\u6084\u6084\u7684\u5bf9\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u4eec\u6765\u5f97\u9002\u65f6\uff0c\u4e5f \u8bb8\u4f1a\u770b\u5230\u5141\u796f\u90a3\u53ae\u767b\u4f4d\u7684\u5927\u5178\u5462\uff01\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u542c\u5f97\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u4f20\u8a00\uff0c\u5eb7\u7199\u7684\u5341\u51e0\u4e2a \u7687\u5b50\u6697\u5730\u91cc\u90fd\u5728\u52fe\u5fc3\u6597\u89d2\uff0c\u4e89\u593a\u7687\u4f4d\uff0c\u672a\u5fc5\u89c1\u5f97\u5c31\u662f\u5141\u796f\u767b\u4f4d\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5141\u796f\u5904\u5fc3\u79ef \u8651\u5df2\u4e45\uff0c\u7ed3\u7eb3\u7684\u5947\u4eba\u5f02\u58eb\u6700\u591a\uff0c\u5176\u4ed6\u7687\u5b50\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u3002\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u7ba1\u4ed6\u662f\u8c01\u767b\u4f4d\uff0c \u5bf9\u6211\u4eec\u6c49\u4eba\u90fd\u6ca1\u597d\u5904\uff0c\u4f55\u5fc5\u8d39\u5fc3\u731c\u5b83\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u867d\u7136\u672a\u5230\u8fc7\u5317\u4eac\uff0c\u4f46\u4ed6\u4ea4\u6e38\u5e7f\u9614\uff0c\u5728\u5317\u4eac\u4e5f\u6709\u5f88\u591a\u670b\u53cb\uff0c\u95fb\u5f97\u4ed6\u6765\uff0c\u8bb8\u591a\u4eba\u90fd\u9080 \u4ed6\u5230\u5bb6\u4e2d\u5c45\u4f4f\uff0c\u7518\u51e4\u6c60\u4e00\u4e00\u63a8\u8f9e\uff0c\u5728\u65c5\u820d\u4f4f\u4e86\u4e24\u65e5\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u4eec\u867d\u53d8\u4e86\u5bb9\u8c8c\uff0c\u65c5\u820d\u7a76 \u975e\u957f\u4f4f\u4e4b\u5730\u3002\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u6211\u7b97\u597d\u8fd8\u6709\u4e00\u4e2a\u4eba\u8981\u6765\u8bf7\u6211\u4eec\u3002\u201d\u5415\u56db\u5a18\u95ee\u4ed6\u662f\u8c01\uff0c\u7518\u51e4\u6c60 \u7b11\u9053\uff1a\u201c\u8bf4\u4f60\u4e5f\u4e0d\u77e5\u9053\u3002\u4e0d\u8fc7\u8fd9\u4eba\u7684\u5e08\u5085\u4f60\u5012\u89c1\u8fc7\u3002\u201d\u5415\u56db\u5a18\u77e5\u4ed6\u6709\u5fc3\u5356\u4e2a\u5173\u5b50\uff0c\u4e00\u7b11\u4e0d \u95ee\u3002\u679c\u7136\u5230\u4e86\u7b2c\u4e09\u5929\uff0c\u5e97\u5c0f\u4e8c\u8fdb\u6765\u7980\u9053\uff1a\u201c\u5510\u7237\uff0c\u6709\u4e00\u548c\u5c1a\u5e26\u4e86\u6350\u518c\u6765\u6307\u540d\u8981\u5411\u4f60\u5316\u7f18\uff01\u201d \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u597d\uff0c\u8bf7\u4ed6\u8fdb\u6765\u3002\u201d\u5e97\u5c0f\u4e8c\u597d\u751f\u5947\u602a\uff0c\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u778e\u649e\uff0c\u539f\u6765\u679c\u7136 \u548c\u5ba2\u5b98\u76f8\u8bc6\u3002\u201d\u8fc7\u4e86\u7247\u523b\uff0c\u5f15\u8fdb\u4e86\u4e00\u4e2a\u548c\u5c1a\uff0c\u9762\u5bb9\u6e05\u7626\uff0c\u770b\u6765\u7ea6\u6709\u4e94\u5341\u5c81\u5de6\u53f3\u3002\u7518\u51e4\u6c60\u63a9\u4e86 \u623f\u95e8\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u662f\u7a7a\u660e\u548c\u5c1a\uff0c\u539f\u662f\u662f\u4f60\u3002\u4f60\u600e\u4e48\u505a\u4e86\u548c\u5c1a\u4e86\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u7279 \u6765\u5411\u4f60\u5316\u7f18\uff01\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u4f60\u7684\u8033\u6735\u5012\u957f\uff0c\u5c45\u7136\u77e5\u9053\u6211\u53d1\u4e86\u94c1\u6247\u5e2e\u7684\u6a2a\u8d22\u3002\u201d\u90a3\u548c\u5c1a\u5ffd \u60e8\u7136\u4e00\u7b11\uff1b\u9053\uff1a\u201c\u62ab\u4e0a\u8888\u88df\u4e8b\u66f4\u591a\uff0c\u8fc7\u4e86\u5e74\u6211\u4e5f\u8981\u79bb\u5f00\u5317\u4eac\u4e86\u3002\u201d\u7518\u51e4\u6c60\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60 \u73b0\u5728\u771f\u7684\u52d8\u7834\u8272\u7a7a\uff1f\u201d\u90a3\u4eba\u53c8\u662f\u4e00\u7b11\uff0c\u7b11\u5f97\u6781\u4e3a\u51c4\u60e8\uff0c\u9053\uff1a\u201c\u5230\u6211\u7684\u7834\u5bfa\u559d\u9152\u53bb\uff01\u201d\u7518\u51e4\u6c60 \u7ed3\u4e86\u5e97\u94b1\u3002\u548c\u90a3\u548c\u5c1a\u5411\u90ca\u5916\u8d70\u53bb\u3002\u8d70\u5230\u90ca\u5916\uff0c\u7518\u51e4\u6c60\u624d\u66ff\u4ed6\u4eec\u4ecb\u7ecd\u3002\u539f\u6765\u8fd9\u548c\u5c1a\u4fd7\u540d\u53eb\u795d\u5bb6 \u6f8e\uff0c\u6b63\u662f\u6b66\u6210\u5316\u7684\u552f\u4e00\u4f20\u4eba\u3002\u5415\u56db\u5a18\u4e5f\u66fe\u542c\u6b66\u6210\u5316\u5728\u9014\u4e2d\u8bf4\u8fc7\u4ed6\u6709\u8fd9\u4e48\u4e00\u4e2a\u5f92\u5f1f\uff0c\u5374\u6599\u4e0d\u5230 \u539f\u6765\u5c31\u662f\u8fd9\u4e2a\u548c\u5c1a\u3002\u5fc3\u4e2d\u5947\u602a\u4e3a\u4ec0\u4e48\u7518\u51e4\u6c60\u4ece\u6765\u4e0d\u8bf4\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u9635\uff0c\u5230\u4e86\u897f\u5c71\uff0c\u90a3\u548c\u5c1a\u5728\u524d\u9886\u8def\uff0c\u8d70\u5230\u5c71\u9e93\u4e00\u4e2a\u7834\u7834\u70c2\u70c2\u7684\u5e99\u5b87\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u5c31 \u662f\u6211\u7684\u5c45\u5904\u4e86\u3002\u201d\u8fd9\u5e99\u539f\u662f\u5e9f\u5f03\u7684\u65b9\u5e99\uff0c\u867d\u7136\u7834\u70c2\uff0c\u91cc\u9762\u5730\u65b9\u5012\u8fd8\u5bbd\u655e\u3002 \u3000\u3000\u90a3\u548c\u5c1a\u7565\u626b\u7070\u5c18\uff0c\u5728\u5899\u58c1\u4e0a\u53d6\u4e0b\u4e24\u5927\u846b\u82a6\u8001\u9152\uff0c\u9080\u4f17\u4eba\u5171\u914c\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8fd8\u672a\u8bf7\u6559\u4f60 \u7684\u6cd5\u53f7\uff1f\u201d\u90a3\u548c\u5c1a\u9053\uff1a\u201c\u6211\u66ff\u81ea\u5df1\u53d6\u4e86\u4e2a\u6cd5\u540d\uff0c\u53eb\u505a\u51b7\u7985\u3002\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u540d\u4e3a\u51b7\u7985\uff0c\u53ea \u6050\u4f60\u7684\u5fc3\u672a\u5fc5\u771f\u51b7\u3002\u201d\u51b7\u7985\u53c8\u662f\u60e8\u7136\u4e00\u7b11\uff0c\u5927\u53e3\u5927\u53e3\u7684\u559d\u9152\uff0c\u8f6c\u77ac\u628a\u4e00\u5927\u846b\u82a6\u8001\u9152\uff0c\u559d\u5f97\u5e72 \u5e72\u51c0\u51c0\u3002 \u3000\u3000\u539f\u6765\u8fd9\u795d\u5bb6\u6f8e\u56e0\u610f\u4e2d\u4eba\u88ab\u5e7d\u6df1\u5bab\uff0c\u4e09\u5341\u5e74\u6765\u68a6\u5bd0\u4e0d\u5fd8\uff0c\u53bb\u5e74\u5192\u9669\u63a2\u5bab\uff0c\u5931\u8d25\u4e4b\u540e\uff0c\u5fc3\u7070 \u610f\u51b7\uff0c\u56e0\u800c\u524a\u53d1\u505a\u4e86\u548c\u5c1a\u3002\u4f46\u4ed6\u8fd8\u4e0d\u77e5\u9053\u610f\u4e2d\u4eba\u5df2\u6b7b\uff0c\u6240\u4ee5\u867d\u7136\u505a\u4e86\u548c\u5c1a\u8fd8\u662f\u820d\u4e0d\u5f97\u79bb\u5f00\u5317 \u4eac\u3002 \u3000\u3000\u7518\u51e4\u6c60\u89c1\u4ed6\u610f\u5174\u8427\u7d22\uff0c\u559d\u4e86\u4e00\u5927\u53e3\u9152\uff0c\u6447\u5934\u7b11\u9053\uff1a\u201c\u6211\u5144\u5982\u6b64\u81ea\u82e6\uff0c\u771f\u662f\u4f55\u5fc5\u62ab\u4e0a\u8888 \u88df\uff1f\u201d\u51b7\u7985\u9053\uff1a\u201c\u5982\u679c\u53bb\u5e74\u78b0\u89c1\u4f60\u4eec\uff0c\u4e5f\u8bb8\u6211\u4f1a\u5f97\u507f\u5fc3\u613f\uff0c\u201d\u5f53\u4e0b\u628a\u4ed6\u53bb\u5e74\u63a2\u5bab\u5931\u8d25\u4e4b\u4e8b\u8bf4 \u4e86\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u5c06\u6765\u6211\u4eec\u518d\u548c\u4f60\u53bb\u3002\u201d\u51b7\u7985\u82e6\u7b11\u9053\uff1a\u201c\u6211\u5df2\u505a\u4e86\u548c\u5c1a\uff0c\u8fd9\u4e8b\u4e0d\u5fc5\u63d0\u4e86\u3002\u201d \u3000\u3000\u51b7\u7985\u77e5\u9053\u5415\u56db\u5a18\u662f\u7518\u51e4\u6c60\u7684\u5e08\u59b9\uff0c\u53c8\u662f\u540d\u5112\u5415\u665a\u6751\u7684\u5b59\u5973\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u7985\u7406\u91cd\u5728\u7a7a \u660e\uff0c\u5112\u5bb6\u8981\u4eba\u514b\u5df1\uff0c\u4f46\u6027\u60c5\u4e0e\u751f\u4ff1\u6765\uff0c\u82e6\u8981\u4eba\u5982\u592a\u4e0a\u4e4b\u5fd8\u60c5\uff0c\u5c82\u4e0d\u662f\u8fdd\u53cd\u4e86\u81ea\u7136\uff1f\u770b\u6765\u6211\u8fd9 \u4e00\u751f\uff0c\u662f\u65e2\u4e0d\u80fd\u4e3a\u4fa0\u5ba2\u4e5f\u4e0d\u80fd\u4e3a\u9ad8\u50e7\u7684\u4e86\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5112\u4f5b\u4e24\u5bb6\uff0c\u90fd\u662f\u5bfc\u4eba\u4e3a\u5584\uff0c\u987a\u5176 \u81ea\u7136\uff0c\u4e0d\u5fc5\u52c9\u5f3a\u7684\u3002\u6b63\u552f\u6709\u81f3\u6027\u81f3\u60c5\uff0c\u6240\u4ee5\u624d\u80fd\u5272\u8089\u5582\u9e70\uff0c\u820d\u8eab\u6551\u864e\u3002\u62e7\u975e\u4e00\u8bb2\u7a7a\u660e\uff0c\u4fbf\u662f \u53ea\u6c42\u81ea\u4e86\uff01\u201d\u51b7\u7985\u8d77\u7acb\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u656c\u95fb\u5999\u8bba\uff0c\u9192\u6211\u8ff7\u7cca\u3002\u201d\u5415\u56db\u5a18\u614c\u5fd9\u8fd8\u793c\uff0c\u9053\uff1a\u201c\u524d\u8f88 \u5982\u6b64\uff0c\u6298\u6740\u6211\u4e86\u3002\u201d\u51b7\u7985\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u548c\u4f60\u5e08\u5144\u5144\u5f1f\u76f8\u79f0\uff0c\u4f60\u5c82\u53ef\u53eb\u6211\u524d\u8f88\u3002\u201d\u8bf4\u8bdd\u81f3 \u6b64\uff0c\u773c\u4e2d\u59cb\u6d41\u9732\u51fa\u5174\u594b\u7684\u5149\u8f89\u3002 \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u795d\u5927\u54e5\u867d\u4ee5\u51b7\u7985\u4e3a\u53f7\uff0c\u8c6a\u60c5\u80dc\u6168\u8fd8\u662f\u6f5c\u5728\u5fc3\u4e2d\u3002\u201d\u51b7\u7985\u82e6\u7b11\u9053\uff1a\u201c\u5345\u5e74\u56de \u9996\uff0c\u5982\u68a6\u5982\u70df\uff0c\u4e0d\u5fc5\u8bf4\u4e86\uff0c\u4e0d\u5fc5\u8bf4\u4e86\u3002\u54b1\u4eec\u559d\u9152\u3002\u201d\u8fc7\u4e86\u4e00\u4f1a\uff0c\u51b7\u7985\u5148\u81ea\u9169\u914a\u9189\u5012\u3002\u767d\u6cf0\u5b98 \u7b11\u9053\uff1a\u201c\u8fd9\u548c\u5c1a\u771f\u6709\u610f\u601d\u3002\u201d\u7518\u51e4\u6c60\u6276\u4ed6\u8fdb\u7985\u623f\u5b89\u6b47\uff0c\u51b7\u7985\u9189\u5f97\u8ff7\u8ff7\u7cca\u7cca\uff0c\u541f\u9053\uff1a\u201c\u53e4\u5239\u8352 \u51c9\u7559\u5ba2\u4f4f\uff0c\u91ce\u50e7\u65e0\u793c\u6162\u5609\u5bbe\u3002\u201d\u7518\u51e4\u6c60\u670d\u4f8d\u4ed6\u7761\u4e86\uff0c\u5728\u5bfa\u4e2d\u5de1\u89c6\u4e00\u904d\uff0c\u53ea\u89c1\u51e0\u95f4\u8033\u623f\u90fd\u5df2\u5907 \u597d\u5e8a\u94fa\uff0c\u5415\u56db\u5a18\u9053\uff1a\u201c\u8fd9\u4e2a\u548c\u5c1a\u770b\u6765\u867d\u7136\u758f\u72c2\uff0c\u5176\u5b9e\u7ec6\u5fc3\u5f97\u5f88\u3002\u4e5f\u662f\u6027\u60c5\u4e2d\u4eba\u3002\u201d\u7518\u51e4\u6c60 \u9053\uff1a\u201c\u8981\u4e0d\u7136\u4ed6\u600e\u80fd\u4e3a\u4e86\u4e00\u4e2a\u5973\u5b50\uff0c\u7b49\u5019\u4e09\u5341\u591a\u5e74\uff1f\u201d \u3000\u3000\u7518\u51e4\u6c60\u8fd9\u624d\u8bf4\u51fa\u51b7\u7985\u6765\u5386\uff0c\u4f17\u4eba\u90fd\u4e0d\u7981\u9eef\u7136\u53f9\u606f\u3002\u8def\u6c11\u77bb\u611f\u89e6\u5c24\u591a\uff0c\u53c8\u559d\u4e86\u4e00\u5927\u53e3\u9152\uff0c \u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u8def\u5e08\u5144\u4e0d\u5fc5\u4f24\u611f\uff0c\u674e\u660e\u73e0\u867d\u7136\u662f\u4faf\u95e8\u5c0f\u59d0\uff0c\u4f46\u770b\u5979\u4e3e\u6b62\u8a00\u884c\uff0c\u5374\u7edd\u4e0d\u662f\u4e00\u4e2a \u61e6\u5f31\u7684\u5bab\u5a25\u53ef\u6bd4\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u53c8\u8bf4\u51fa\u4ed6\u4e0e\u795d\u5bb6\u6f8e\u7ed3\u8bc6\u7684\u7ecf\u8fc7\uff0c\u90a3\u4e0d\u8fc7\u662f\u4e09\u5e74\u4e4b\u524d\u7684\u4e8b\uff0c\u795d\u5bb6\u6f8e\u521d\u5230\u6c5f\u5357\uff0c\u4eba\u5730 \u751f\u758f\uff0c\u7f3a\u4e86\u76d8\u7f20\uff0c\u5728\u4e00\u4e2a\u5c0f\u5e02\u9547\u67d4\u836f\u8ba8\u94b1\uff0c\u4ed6\u4e0d\u61c2\u89c4\u77e9\uff0c\u672a\u62dc\u5f53\u5730\u7684\u201c\u5927\u54e5\u201d\uff0c\u90a3\u201c\u5927\u54e5\u201d \u7684\u624b\u4e0b\u786c\u6536\u201c\u573a\u89c4\u201d\uff0c\u4ed6\u7565\u4e3a\u51fa\u624b\uff0c\u5c31\u628a\u90a3\u4e9b\u4eba\u6253\u5f97\u62b1\u5934\u9f20\u7a9c\u3002\u672c\u6765\u4e8b\u6781\u5bfb\u5e38\uff0c\u4e0d\u6599\u90a3\u5f53\u5730 \u201c\u5927\u54e5\u201d\u5374\u662f\u4e00\u4e2a\u76d7\u515a\u9996\u9886\u7684\u5f92\u5f1f\uff0c\u89c1\u4ed6\u4e00\u4e2a\u5f02\u4e61\u6765\u5ba2\uff0c\u9732\u51fa\u90a3\u60ca\u4eba\u7684\u6b66\u529f\uff0c\u4e0d\u7981\u751f\u4e86\u7591 \u8651\uff0c\u4ee5\u4e3a\u4eac\u4e2d\u6d3e\u6765\u7684\u6355\u5934\uff0c\u5c31\u6697\u4e2d\u901a\u77e5\u90a3\u76d7\u515a\u9996\u9886\u3002\u90a3\u76d7\u515a\u9996\u9886\u542c\u8bf4\u6709\u8fd9\u6837\u7684\u4eba\u7269\u5230\u6765\uff0c\u8fde \u5fd9\u5e26\u4eba\u53bb\u76d8\u95ee\u4ed6\uff0c\u4e24\u65b9\u8a00\u8bed\u4e0d\u5408\uff0c\u6253\u4e86\u8d77\u6765\uff0c\u795d\u5bb6\u6f8e\u5be1\u4e0d\u654c\u4f17\uff0c\u7adf\u7ed9\u64d2\u4e86\u3002\u6070\u597d\u7518\u51e4\u6c60\u4e5f\u5230 \u90a3\u4e2a\u5730\u65b9\uff0c\u542c\u8bf4\u6709\u8fd9\u6837\u4e00\u6869\u4e8b\u60c5\uff0c\u8d76\u5fd9\u53bb\u62dc\u4f1a\u90a3\u76d7\u515a\u9996\u9886\uff0c\u6c42\u89c1\u88ab\u4ed6\u6240\u64d2\u7684\u5f02\u4e61\u602a\u5ba2\u3002\u7518\u51e4 \u6c60\u53ea\u770b\u4e86\u795d\u5bb6\u6f8e\u4e00\u773c\uff0c\u4fbf\u65ad\u5b9a\u6b64\u4eba\u5fc5\u975e\u6355\u5feb\uff0c\u7acb\u523b\u5411\u90a3\u76d7\u515a\u9996\u9886\u8bf4\u60c5\uff0c\u628a\u795d\u5bb6\u6f8e\u91ca\u653e\u51fa\u6765\u3002 \u795d\u5bb6\u6f8e\u89c1\u7518\u51e4\u6c60\u4e0e\u81ea\u5df1\u6beb\u4e0d\u76f8\u8bc6\uff0c\u5c45\u7136\u8fd9\u6837\u7684\u4e3a\u81ea\u5df1\u5c3d\u529b\uff0c\u5bf9\u4ed6\u7684\u4e49\u6c14\u5341\u5206\u4f69\u670d\uff0c\u53ca\u81f3\u4e92\u901a \u59d3\u540d\uff0c\u77e5\u9053\u4ed6\u5c31\u662f\u201c\u6c5f\u5357\u5927\u4fa0\u201d\u4e4b\u540e\uff0c\u66f4\u4e3a\u503e\u4f69\u3002\u4e24\u4eba\u8a00\u8bed\u6295\u673a\uff0c\u7ed3\u6210\u4e86\u98ce\u5c18\u77e5\u5df1\u3002\u90a3\u65f6\u795d \u5bb6\u6f8e\u5c31\u628a\u4ed6\u7684\u6765\u5386\u5411\u7518\u51e4\u6c60\u8bf4\u4e86\uff0c\u5e76\u76f8\u7ea6\u65e5\u540e\u5728\u4eac\u76f8\u89c1\u3002\u7518\u51e4\u6c60\u56e0\u4e8b\u6d89\u5bab\u5ef7\u79d8\u5bc6\uff0c\u800c\u4e14\u662f\u795d \u5bb6\u6f8e\u4e2a\u4eba\u7684\u79c1\u4e8b\uff0c\u56e0\u6b64\u4e00\u5411\u6ca1\u5bf9\u540c\u95e8\u9053\u53ca\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u51b7\u7985\u4e00\u89c9\u9192\u6765\uff0c\u53c8\u7ea6\u7518\u51e4\u6c60\u559d\u9152\u3002\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u559d\u6cd5\uff0c\u53ea\u987b\u8fde\u559d\u4e09\u65e5\uff0c\u4fbf \u8981\u628a\u4f60\u8fd9\u7834\u5e99\u4e5f\u559d\u5149\u4e86\u3002\u201d\u8bf4\u7740\u638f\u51fa\u94f6\u5b50\u6765\u9053\uff1a\u201c\u4eca\u5929\u6211\u8bf7\u5ba2\u5427\u3002\u201d\u51b7\u7985\u767d\u773c\u4e00\u7ffb\uff0c\u9053\uff1a \u201c\u5c0f\u5bb6\u5b50\u6c14\uff0c\u8fd9\u70b9\u94f6\u5b50\u591f\u4ec0\u4e48\u7528\uff1f\u201d\u7518\u51e4\u6c60\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u4f60\u8fd9\u548c\u5c1a\u5403\u5f97\u591a\u5c11\uff0c\u5341\u4e24\u94f6\u5b50 \u8fd8\u4e0d\u591f\uff1f\u95ee\u9053\uff1a\u201c\u8981\u591a\u5c11\u624d\u591f\uff1f\u201d\u51b7\u7985\u9053\uff1a\u201c\u4f60\u62ff\u51fa\u4e00\u4e07\u4e24\u6765\u5427\u3002\u201d\u7518\u51e4\u6c60\u53c8\u662f\u4e00\u6014\uff0c\u51b7\u7985 \u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u5411\u4f60\u5316\u7f18\uff0c\u4f60\u5f53\u662f\u5047\u7684\u5417\uff1f\u201d\u7518\u51e4\u6c60\u4e5f\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u662f\u8bf4\u7b11\u7684\u5462\uff01\u4f60 \u65e2\u7136\u8981\uff0c\u83ab\u8bf4\u4e00\u4e07\uff0c\u4e8c\u4e07\u4e5f\u6709\u3002\u201d\u4ece\u56ca\u4e2d\u53d6\u51fa\u4e00\u4e32\u5b9d\u73e0\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u94c1\u6247\u5e2e\u52ab\u63a0\u6765\u7684\u4e0d\u4e49\u4e4b \u8d22\uff0c\u5927\u54e5\u62ff\u53bb\u7528\u5427\u3002\u201d\u51b7\u7985\u653e\u5165\u6000\u4e2d\uff0c\u8fd9\u624d\u7b11\u9053\uff1a\u201c\u6211\u8981\u4f60\u7684\u94b1\uff0c\u4e3a\u7684\u662f\u8981\u62ff\u53bb\u6551\u6d4e\u4e00\u4e9b\u5b64 \u513f\u5be1\u5987\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f60\u505a\u4e86\u4f5b\u95e8\u5f1f\u5b50\uff0c\u96be\u602a\u8981\u5e7f\u7ed3\u5584\u7f18\u3002\u201d\u51b7\u7985\u773c\u775b\u4e00\u7ffb\uff0c\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u6211\u8981 \u6551\u6d4e\u7684\u4e0d\u662f\u666e\u901a\u4eba\u5bb6\u7684\u5b64\u513f\u5be1\u5987\u3002\u201d\u7518\u51e4\u6c60\u968f\u53e3\u95ee\u9053\uff1a\u201c\u4e0d\u662f\u666e\u901a\u4eba\u5bb6\u7684\u96be\u9053\u8fd8\u662f\u5bcc\u8d35\u4eba\u5bb6 \u7684\u4e0d\u6210\uff1f\u201d\u51b7\u7985\u9053\uff1a\u201c\u6211\u4e13\u95e8\u6551\u6d4e\u4f60\u5bf9\u5934\u51a4\u5bb6\u7684\u5b64\u513f\u5be1\u5987\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u53c8\u8bf4\u7b11 \u4e86\u3002\u201d\u51b7\u7985\u5ffd\u6b63\u8272\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u662f\u8bf4\u7b11\u3002\u6211\u95ee\u4f60\uff0c\u4f60\u4eec\u8fd9\u4e9b\u6c5f\u6e56\u4fa0\u5ba2\u548c\u5bab\u4e2d\u536b\u58eb\u662f\u4e0d\u662f\u5bf9 \u5934\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u82e5\u4ed6\u4eec\u6b7b\u5fc3\u584c\u5730\u7684\u4e3a\u7687\u5e1d\u8001\u513f\u5356\u547d\uff0c\u7f09\u6355\u6211\u4eec\uff0c\u90a3\u5f53\u7136\u662f\u5bf9\u5934\u4e86\u3002\u201d\u51b7 \u7985\u9053\uff1a\u201c\u53ef\u4e0d\u662f\uff1f\u6211\u6551\u6d4e\u7684\u5c31\u662f\u4e00\u4e9b\u536b\u58eb\u7684\u5be1\u5987\u5b64\u513f\u3002\u4f60\u77e5\u9053\u6211\u5728\u4e09\u5341\u591a\u5e74\u524d\uff0c\u66fe\u5728\u5bab\u4e2d\u5916 \u5ef7\u7684\u5185\u90e8\u5f53\u5dee\uff0c\u90a3\u65f6\u5468\u9752\u8fd8\u5728\u5bab\u5185\u5f53\u536b\u58eb\uff0c\u672a\u66fe\u53db\u53d8\u5462\u3002\u6211\u548c\u5468\u9752\u4ee5\u53ca\u53e6\u5916\u4e00\u540d\u536b\u58eb\u53eb\u4faf\u4e09 \u53d8\u7684\u662f\u597d\u670b\u53cb\u3002\u201d\u7518\u51e4\u6c60\u63d2\u53e3\u9053\uff1a\u201c\u6211\u542c\u4f60\u8bf4\u8fc7\uff0c\u5468\u5927\u4fa0\u662f\u6211\u751f\u5e73\u656c\u4ef0\u7684\u4eba\uff0c\u4faf\u7237\u867d\u5728\u5bab \u5ef7\uff0c\u5bf9\u6c5f\u6e56\u4e0a\u7684\u4fa0\u4e49\u540c\u9053\uff0c\u4e5f\u5e38\u52a0\u63a9\u62a4\uff0c\u50cf\u4ed6\u4eec\u8fd9\u6837\u7684\u536b\u58eb\uff0c\u975e\u4f46\u4e0d\u662f\u6211\u4eec\u7684\u5bf9\u5934\uff0c\u800c\u4e14\u662f \u6211\u4eec\u7684\u670b\u53cb\u3002\u201d\u51b7\u7985\u7eed\u9053\uff1a\u201c\u5f53\u5bab\u4e2d\u536b\u58eb\uff0c\u5176\u5b9e\u4e5f\u60e8\u5f97\u5f88\u3002\u6211\u56e0\u4e3a\u548c\u5468\u4faf\u4e8c\u4eba\u4ea4\u597d\uff0c\u6240\u4ee5\u4e5f \u8ba4\u8bc6\u4e00\u4e9b\u536b\u58eb\u670b\u53cb\u3002\u53bb\u5e74\u6211\u56de\u4eac\u4e4b\u540e\uff0c\u53d1\u73b0\u4ed6\u4eec\u5341\u4e5d\u90fd\u5df2\u6b7b\u53bb\u3002\u6709\u4e9b\u536b\u58eb\u7559\u4e0b\u5be1\u5987\u5b64\u513f\uff0c\u7687 \u5e1d\u65e2\u6ca1\u6069\u6064\uff0c\u5e73\u65f6\u90bb\u91cc\u53c8\u56e0\u618e\u6068\u90a3\u4e9b\u536b\u58eb\u751f\u524d\u7684\u4f5c\u5a01\u4f5c\u798f\uff0c\u8c01\u90fd\u4e0d\u7167\u6599\u4ed6\u4eec\u3002\u201d\u7518\u51e4\u6c60\u53f9 \u9053\uff1a\u201c\u8fd9\u771f\u662f\u7978\u5ef6\u5b50\u5b59\u3002\u201d\u4e24\u4eba\u8c08\u8bb2\u4e00\u9635\uff0c\u51b7\u7985\u4fbf\u5230\u57ce\u4e2d\u6cbd\u9152\u53bb\u4e86\u3002 \u3000\u3000\u665a\u4e0a\u51b7\u7985\u56de\u6765\uff0c\u7518\u51e4\u6c60\u95ee\u9053\uff1a\u201c\u53ef\u6709\u5173\u4e1c\u56db\u4fa0\u7684\u6d88\u606f\u4e48\uff1f\u201d\u51b7\u7985\u9053\uff1a\u201c\u4ed6\u4eec\u8fd8\u672a\u6765\u3002\u5012 \u662f\u6211\u78b0\u5230\u4e00\u4e9b\u6b66\u6797\u670b\u53cb\uff0c\u4ed6\u4eec\u542c\u8bf4\u4f60\u6765\uff0c\u5230\u5904\u7684\u6253\u63a2\u6d88\u606f\u3002\u6211\u6015\u4f60\u4e0d\u613f\u592a\u8fc7\u9732\u9762\uff0c\u6240\u4ee5\u4e00\u6982 \u63a8\u8bf4\u4e0d\u77e5\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u63a8\u5f97\u5f88\u597d\uff0c\u5176\u5b9e\u90a3\u5e2e\u670b\u53cb\uff0c\u5341\u4e5d\u90fd\u662f\u95fb\u540d\u7684\u670b\u53cb\uff0c\u6211\u53c8\u6539\u4e86\u5bb9 \u8c8c\uff0c\u4ed6\u4eec\u89c1\u4e86\u6211\u4e5f\u4e0d\u8ba4\u5f97\u3002\u2019\u767d\u6cf0\u5b98\u95ee\u4eac\u57ce\u6709\u4ec0\u4e48\u65b0\u95fb\uff0c\u51b7\u7985\u9053\uff1a\u201c\u9664\u4e86\u8001\u7687\u5e1d\u7684\u75c5\u4e4b\u5916\uff0c \u4fbf\u662f\u5341\u56db\u8d1d\u52d2\u7684\u56de\u4eac\u3002\u201d\u5341\u56db\u7687\u5b50\u540d\u53eb\u5141\u796f\uff0c\u6700\u5f97\u5eb7\u7199\u5ba0\u7231\uff0c\u4ed6\u66fe\u5c61\u6b21\u7edf\u5175\u897f\u5f81\uff0c\u73b0\u5728\u8fd8\u5728 \u8f9e\u804c\uff0c\u88ab\u5c01\u4e3a\u629a\u8fdc\u5c06\u519b\u3002\u5415\u56db\u5a18\u542c\u51b7\u7985\u8c08\u8d77\u5341\u56db\u7687\u5b50\uff0c\u84e6\u7136\u60f3\u8d77\u5510\u6653\u6f9c\u6240\u8bf4\u7684\u5bab\u4e2d\u89c1\u95fb\uff0c \u9053\uff1a\u201c\u5341\u56db\u8d1d\u52d2\u56de\u6765\uff0c\u83ab\u975e\u662f\u4e3a\u4e86\u4e89\u4f4d\uff1f\u201d\u51b7\u7985\u9053\uff1a\u201c\u8fd9\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u6211\u8fc7\u53bb\u5bf9\u5bab\u5ef7\u7684\u4e8b\u77e5 \u5f97\u9887\u591a\uff0c\u73b0\u5728\u53cd\u4e0d\u613f\u95fb\u95ee\u4e86\u3002\u201d\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u4e00\u4e2a\u7687\u5b50\u56de\u6765\uff0c\u5176\u5b9e\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u65b0\u95fb\u3002\u201d\u51b7 \u7985\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u8fd9\u5341\u56db\u7687\u5b50\u662f\u4ece\u9752\u6d77\u5e26\u519b\u8d76\u56de\u7684\uff0c\u5927\u519b\u9a7b\u624e\u5728\u57ce\u5916\u5462\uff0c\u6b64\u5176\u4e00\u3002\u5176\u4e8c\u662f\u4ed6\u56de \u6765\u4e4b\u540e\uff0c\u5c31\u5728\u62a4\u56fd\u5bfa\u524d\u8bbe\u4e86\u4e00\u4e2a\u8f83\u6280\u573a\uff0c\u7acb\u6709\u4e24\u4e2a\u64c2\u53f0\uff0c\u4e00\u4e2a\u662f\u6bd4\u8bd5\u6454\u8de4\u7684\uff0c\u4e00\u4e2a\u662f\u6bd4\u8bd5\u62f3 \u811a\u7684\u3002\u4e3b\u64c2\u7684\u53f0\u67f1\uff0c\u90fd\u662f\u4ed6\u519b\u4e2d\u7684\u52c7\u58eb\u3002\u5e76\u7acb\u4e0b\u91cd\u8d4f\uff0c\u8c01\u80fd\u628a\u53f0\u4e3b\u6454\u5012\u7684\u8d4f\u9ec4\u91d1\u767e\u4e24\uff0c\u80fd\u6253 \u53f0\u4e3b\u4e00\u62f3\u7684\uff0c\u8d4f\u9ec4\u91d1\u5341\u4e24\u3002\u8fd9\u8f83\u6280\u573a\u5df2\u5f00\u4e86\u4e09\u5929\u4e86\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e4b\u4e2d\u9c7c\u5a18\u4e00\u5411\u751f\u957f\u6c34\u8fb9\u4e14\u4e45\u5df2\u503e\u6155\u4eac\u57ce\u7684\u7e41\u534e\uff0c\u4e0d\u6599\u5230\u4e86\u5317\u4eac\u4e4b\u540e\uff0c\u5374\u5173\u5728\u8fd9\u6837\u8352 \u51c9\u7684\u5e99\u5b87\u91cc\uff0c\u81ea\u89c9\u6c14\u95f7\uff0c\u95fb\u8a00\u4e0d\u7981\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4f55\u4e0d\u8fdb\u57ce\u53bb\u77a7\u77a7\u70ed\u95f9\u3002\u201d\u767d\u6cf0\u5b98\u4e5f\u662f\u597d\u52a8\u7684 \u4eba\uff0c\u5e2e\u8154\u8bf4\u9053\uff1a\u201c\u53cd\u6b63\u6211\u4eec\u5df2\u6539\u4e86\u5bb9\u8c8c\uff0c\u5230\u57ce\u91cc\u6e9c\u6e9c\u4e5f\u4e0d\u63d6\u4e8b\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u867d\u7136\u4e0d\u5927\u60f3\u53bb\uff0c\u4f46\u767d\u6cf0\u5b98\u662f\u5e08\u5144\uff0c\u4e0d\u4fbf\u626b\u4ed6\u7684\u5174\uff0c\u4fbf\u9053\uff1a\u201c\u65e2\u7136\u9c7c\u59d1\u5a18\u548c\u767d\u5e08\u5144 \u90fd\u60f3\u53bb\uff0c\u90a3\u4e48\u6211\u4eec\u4e00\u9053\u53bb\u597d\u4e86\u3002\u201d \u3000\u3000\u62a4\u56fd\u5bfa\u662f\u5317\u4eac\u7684\u5927\u5e99\u5e02\u4e4b\u4e00\uff0c\u5f80\u5e74\u6bcf\u9022\u5143\u5bb5\u7aef\u5348\u4e2d\u79cb\u7b49\u4f73\u8282\uff0c\u4fbf\u5f00\u5e99\u4e94\u5929\uff0c\u5341\u5206\u70ed\u95f9\u3002 \u73b0\u5728\u867d\u975e\u5e99\u5e02\u4e4b\u671f\uff0c\u4f46\u56e0\u5341\u56db\u7687\u5b50\u5728\u5bfa\u524d\u7a7a\u5730\u8bbe\u4e86\u4e2a\u8f83\u6280\u573a\uff0c\u770b\u5ba2\u6bd4\u8d77\u5e99\u5e02\u7684\u6e38\u4eba\u8fd8\u8981\u62e5 \u6324\u3002\u7518\u51e4\u6c60\u7b49\u4e94\u4eba\u8eab\u6000\u7edd\u6280\uff0c\u4e0d\u4e00\u56de\u5c31\u6324\u5230\u53f0\u524d\u3002\u8fd9\u65e5\u6b63\u9022\u6454\u4ea4\u90a3\u4e00\u53f0\u5f00\u573a\uff0c\u4e3b\u573a\u7684\u662f\u4e00\u4e2a \u8499\u53e4\u6b66\u58eb\uff0c\u751f\u5f97\u5341\u5206\u5a01\u6b66\u3002\u7518\u51e4\u6c60\u7b49\u770b\u4e86\u534a\u5929\uff0c\u5df2\u63a5\u8fde\u89c1\u4e94\u540d\u62f3\u5e08\u8d25\u5728\u4ed6\u7684\u624b\u4e0b\u3002\u90a3\u8499\u53e4\u6b66 \u58eb\u5341\u5206\u5f97\u610f\uff0c\u5927\u8a00\u9053\uff1a\u201c\u4e45\u95fb\u4eac\u5e08\u4e43\u4eba\u6587\u8513\u82f9\u4e4b\u533a\uff0c\u5367\u864e\u85cf\u9f99\uff0c\u4e0d\u77e5\u591a\u5c11\uff0c\u4e3a\u4f55\u603b\u4e0d\u89c1\u80fd\u8005 \u8d50\u6559\u3002\u96be\u9053\u4eac\u4e2d\u6b66\u58eb\uff0c\u679c\u771f\u4e0d\u8fc7\u5c14\u5c14\u5417\uff1f\u201d\u767d\u6cf0\u5b98\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u4e03\u54e5\uff0c\u4f60\u82e5\u65bd\u5c55\u51fa\u90a3\u6cbe\u8863\u5341 \u516b\u8dcc\u7684\u529f\u592b\uff0c\u5305\u7ba1\u4ed6\u5f53\u573a\u51fa\u5f69\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4e94\u54e5\uff0c\u5343\u4e07\u4e0d\u53ef\u62db\u60f9\u9ebb\u70e6\u3002\u201d\u767d\u6cf0\u5b98\u9053\uff1a \u201c\u6211\u4e0d\u8fc7\u8bf4\u8bf4\u7f62\u4e86\uff0c\u8c01\u771f\u7684\u60f3\u53bb\u6253\u64c2\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u6ca1\u4eba\u8fdb\u573a\u6bd4\u8bd5\uff0c\u8499\u53e4\u6b66\u58eb\u9053\uff1a\u201c\u518d\u6ca1\u4eba\u6765\uff0c\u6211\u8981\u56de\u53bb\u5403\u5348\u996d\u4e86\uff0c\u4e0b\u5348\u518d\u89c1 \u5427\u3002\u201d\u8bdd\u58f0\u65b9\u505c\uff0c\u5ffd\u7136\u5728\u4eba\u4e1b\u4e2d\u6324\u51fa\u4e00\u4e2a\u5c11\u5e74\uff0c\u8dc3\u4e0a\u53f0\u4e0a\uff0c\u9053\uff1a\u201c\u6211\u6765\u9886\u6559\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u5c11\u5e74\u5f62\u5bb9\u867d\u6539\uff0c\u97f3\u8c03\u672a\u53d8\uff0c\u7ec6\u7ec6\u4e00\u770b\uff0c\u5206\u660e\u662f\u5510\u6653\u6f9c\u65e0\u7591\uff01\u5fc3\u4e2d\u60f3 \u9053\uff1a\u600e\u4e48\u5510\u6653\u6f9c\u53c8\u5230\u4eac\u57ce\u6765\u4e86\uff1f\u4ed6\u7684\u8eab\u4e16\u6709\u7edd\u5927\u9690\u79d8\uff0c\u4ed6\u53c8\u4e0d\u662f\u7231\u95f9\u4e8b\u7684\u4eba\uff0c\u600e\u7684\u5374\u5728\u8fd9\u91cc \u629b\u5934\u9732\u9762\uff1f\u6768\u4ef2\u82f1\u7236\u5973\u4e0d\u77e5\u6765\u4e86\u6ca1\u6709\uff1f\u6e38\u76ee\u56db\u987e\uff0c\u4e0d\u89c1\u719f\u4eba\uff0c\u53f0\u4e0a\u4e24\u4eba\u5df2\u52a8\u4e86\u624b\u3002 \u3000\u3000\u8499\u53e4\u6b66\u58eb\u662f\u6454\u4ea4\u80fd\u624b\uff0c\u6839\u672c\u4e0d\u628a\u5510\u6653\u6f9c\u653e\u5728\u773c\u5185\uff0c\u53cc\u624b\u626d\u7740\u5510\u6653\u6f9c\u624b\u81c2\uff0c\u4e24\u817f\u5fae\u5f2f\uff0c\u819d \u76d6\u5411\u524d\u4e00\u9876\uff0c\u559d\u58f0\uff1a\u201c\u53bb\uff01\u201d\u90a3\u6599\u5510\u6653\u6f9c\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u8499\u53e4\u6b66\u58eb\u80a2\u7a9d\u4e00\u75d2\uff0c\u53c8\u9178\u53c8\u9ebb\uff0c\u6251\u901a\u4e00 \u58f0\uff0c\u7ed9\u5510\u6653\u6f9c\u6454\u5012\u5730\u4e0a\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4f60\u8fd9\u4e0d\u662f\u6454\u4ea4\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u662f\uff1f\u4f60\u660e\u660e\u662f \u7ed9\u6211\u6454\u8dcc\u7684\u561b\uff01\u201d\u53f0\u4e0b\u89c2\u4f17\u4e5f\u54d7\u7136\u4e71\u53eb\u3002\u90a3\u8499\u53e4\u6b66\u58eb\u867d\u7136\u660e\u77e5\u4ed6\u7528\u7684\u4e0d\u662f\u201c\u6454\u4ea4\u201d\u624b\u6cd5\uff0c\u4f46 \u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u600e\u4e48\u4f1a\u7a81\u7136\u9178\u8f6f\u65e0\u529b\uff0c\u7ed9\u4ed6\u6454\u5012\uff0c\u6c14\u6124\u6124\u7684\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u518d\u6765\u4e00\u6b21\uff0c \u660e\u5200\u660e\u67aa\uff0c\u4f60\u771f\u80fd\u6454\u5012\u6211\uff0c\u6211\u4fbf\u670d\u4e86\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u786e\u5b9e\u4e0d\u61c2\u201c\u6454\u4ea4\u201d\u624b\u6cd5\uff0c\u521a\u624d\u90a3\u7740\u662f\u4ed6\u6697\u7528\u201c\u62c2\u7a74\u201d\u624b\u6cd5\uff0c\u62c2\u4e86\u90a3\u8499\u65b9\u6b66\u58eb\u7684\u9ebb \u75d2\u7a74\uff0c\u786c\u751f\u751f\u5c06\u4ed6\u6454\u5012\u7684\u3002\u89c1\u8499\u53e4\u6b66\u58eb\u4e0d\u670d\uff0c\u5fc3\u9053\uff1a\u201c\u597d\uff0c\u6211\u518d\u7ed9\u4f60\u4e00\u4e2a\u771f\u7684\u3002\u201d\u5b66\u4e86\u8499\u53e4 \u6b66\u58eb\u7684\u59ff\u52bf\uff0c\u4e24\u817f\u5fae\u5f2f\uff0c\u53cc\u81c2\u5e73\u4f38\uff0c\u9053\uff1a\u201c\u6765\u5427\uff01\u201d\u8499\u53e4\u6b66\u58eb\u548c\u8eab\u4e00\u6251\uff0c\u6349\u7740\u5510\u6653\u6f9c\u7684\u624b\u8155 \u4fbf\u626d\u3002\u5510\u6653\u6f9c\u65e9\u5148\u5728\u53f0\u4e0b\u770b\u4e86\u534a\u5929\uff0c\u5fc3\u60f3\u8fd9\u6454\u4ea4\u4e4b\u6280\u548c\u64d2\u62ff\u624b\u6cd5\u4f3c\u4e4e\u76f8\u901a\uff0c\u6211\u4f55\u4e0d\u7528\u4ed6\u7684\u62db \u6570\u52a0\u4ee5\u53d8\u5316\uff1f\u4e3b\u610f\u6253\u8d70\uff0c\u7528\u529b\u548c\u90a3\u8499\u53e4\u6b66\u58eb\u76f8\u6297\u3002\u90a3\u8499\u53e4\u6b66\u58eb\u7684\u529b\u6bd4\u5510\u6653\u6f9c\u5927\u5f97\u591a\uff0c\u624b\u8155\u4e00 \u6c89\uff0c\u819d\u76d6\u4e00\u9876\uff0c\u770b\u770b\u5c31\u8981\u628a\u5510\u6653\u6f9c\u6454\u5012\uff0c\u90a3\u65f6\u5510\u6653\u6f9c\u7684\u624b\u81c2\u6ed1\u4f3c\u6e38\u9c7c\uff0c\u84e6\u7136\u8131\u4e86\u51fa\u6765\uff0c\u8eab\u5b50 \u5fae\u5fae\u4e00\u95ea\uff0c\u5de6\u624b\u4e00\u63a8\uff0c\u53f3\u624b\u4e00\u51fb\uff0c\u501f\u529b\u6253\u529b\uff0c\u90a3\u8499\u53e4\u6b66\u58eb\u5e9e\u5927\u7684\u8eab\u8eaf\uff0c\u7adf\u7136\u76f4\u5411\u53f0\u4e0b\u98de\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u6b63\u662f\u6454\u4ea4\u4e2d\u7684\u5389\u5bb3\u62db\u6570\uff0c\u540d\u4e3a\u201c\u7280\u725b\u671b\u6708\u201d\u3002\u662f\u5510\u6653\u6f9c\u521a\u4ece\u90a3\u6b66\u58eb\u5b66\u6765\uff0c\u52a0\u4e0a\u5185 \u529f\u52b2\u529b\uff0c\u6697\u4e2d\u6742\u4ee5\u64d2\u62ff\u624b\u6cd5\uff0c\u5c06\u4ed6\u6454\u5012\u7684\u3002\u8499\u53e4\u6b66\u58eb\u7ed9\u4ed6\u4e00\u6454\uff0c\u672c\u4ee5\u4e3a\u4f1a\u5934\u7834\u8840\u6d41\uff0c\u90a3\u6599\u5510 \u6653\u6f9c\u8fd9\u4e00\u6454\u529f\u592b\u5341\u5206\u5230\u5bb6\uff0c\u5c31\u597d\u50cf\u628a\u4ed6\u9001\u5230\u5730\u4e0a\u4f3c\u7684\u3002\u90a3\u8499\u53e4\u6b66\u58eb\u5341\u5206\u4f69\u670d\uff0c\u7fd8\u8d77\u62c7\u6307\u9053\uff1a \u201c\u884c\uff01\u8fd9\u4e00\u767e\u4e24\u9ec4\u91d1\u8be5\u662f\u4f60\u7684\u4e86\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u5fae\u5fae\u4e00\u7b11\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u53f0\u4e0b\u53c8\u8df3\u4e0a\u4e00\u4eba\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u884c\uff01\u6454\u4ea4\u4e2d\u6742\u4ee5\u64d2\u62ff\u624b \u6cd5\uff0c\u786e\u662f\u96be\u5f97\uff0c\u6211\u7684\u64c2\u53f0\u4f60\u4e5f\u4e00\u5e76\u6253\u4e86\u5427\uff01\u201d\u5510\u6653\u6f9c\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u4eba\u80fd\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u770b\u7834\u81ea \u5df1\u7684\u624b\u6cd5\uff0c\u975e\u5927\u884c\u5bb6\u4e0d\u53ef\u3002\u62f1\u624b\u8bf7\u6559\uff0c\u624d\u77e5\u4ed6\u5c31\u662f\u8f83\u91cf\u62f3\u811a\u7684\u64c2\u53f0\u4e3b\uff0c\u5341\u56db\u7687\u5b50\u7684\u519b\u4e2d\u6559\u5934 \u4e91\u5927\u9e4f\u3002 \u3000\u3000\u5510\u6653\u6f9c\u4e5f\u4e0d\u63a8\u8f9e\uff0c\u65bd\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u8bf7\u4e91\u6559\u5934\u8d50\u62db\u3002\u201d\u5415\u56db\u5a18\u5728\u53f0\u4e0b\u8d8a\u53d1\u7eb3\u7f55\uff0c\u4e0d\u77e5\u5510 \u6653\u6f9c\u4e00\u518d\u6253\u64c2\uff0c\u662f\u4f55\u7528\u610f\u3002 \u3000\u3000\u4e91\u5927\u9e4f\u89c1\u4ed6\u6c14\u5b9a\u795e\u95f2\uff0c\u82e5\u65e0\u5176\u4e8b\uff0c\u77e5\u4ed6\u5fc5\u662f\u5185\u5bb6\u9ad8\u624b\uff0c\u60f3\u8bd5\u4ed6\u529f\u529b\uff0c\u53f3\u624b\u4f5c\u638c\uff0c\u5de6\u624b\u62b1 \u62f3\uff0c\u4e00\u58f0\u201c\u6709\u8bf7\u201d\uff0c\u5410\u6c14\u626c\u58f0\uff0c\u62cd\u7684\u4e00\u638c\u5411\u5510\u6653\u6f9c\u5f53\u80f8\u6309\u4e0b\uff0c\u638c\u5fc3\u5185\u5377\uff0c\u6697\u85cf\u5c0f\u5929\u661f\u638c\u529b\uff0c \u5510\u6653\u6f9c\u53cd\u638c\u4e00\u63a8\uff0c\u53ea\u89c9\u5bf9\u65b9\u638c\u529b\u5df1\u53d8\uff0c\u4e0d\u662f\u5e73\u6309\u800c\u662f\u659c\u5e26\uff0c\u53f3\u624b\u6025\u5fd9\u5411\u4e0a\u4e00\u634e\uff0c\u5c06\u4ed6\u52fe\u5e26\u4e4b \u52b2\u5316\u5f00\uff0c\u53f3\u624b\u753b\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u201c\u54e7\u201d\u7684\u4e00\u58f0\uff0c\u75be\u63a8\u51fa\u53bb\uff0c\u4e91\u5927\u9e4f\u9053\u58f0\uff1a\u201c\u597d\uff01\u201d\u8eab\u5f62\u4e00\u95ea\uff0c \u6251\u653b\u5510\u6653\u6f9c\u5de6\u9762\u7a7a\u95e8\u3002 \u3000\u3000\u5415\u56db\u5a18\u6084\u58f0\u9053\uff1a\u201c\u6653\u6f9c\u5927\u6709\u8fdb\u6b65\u4e86\u3002\u201d\u7518\u51e4\u6c60\u201c\u5514\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u4e2d\u5f88\u4e0d\u9ad8\u5174\u3002\u4ed6\u4e5f\u50cf\u5415 \u56db\u5a18\u4e00\u6837\uff0c\u731c\u4e0d\u900f\u5510\u6653\u6f9c\u7528\u610f\u3002\u6697\u9053\uff1a\u5c31\u662f\u60f3\u8bd5\u81ea\u5df1\u6280\u827a\u7684\u8fdb\u5883\u5982\u4f55\uff0c\u4e5f\u4e0d\u8be5\u5728\u8fd9\u79cd\u573a\u4e2d\u70ab \u8000\u3002 \u3000\u3000\u4e91\u5927\u9e4f\u8bd5\u51fa\u5510\u6653\u6f9c\u529f\u529b\u548c\u81ea\u5df1\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u500f\u7684\u4e94\u6307\u4e00\u62e2\uff0c\u5411\u5510\u6653\u6f9c\u5de6\u80c1 \u201c\u5544\u201d\u4e0b\uff0c\u8fd9\u79cd\u6307\u6cd5\uff0c\u540d\u4e3a\u201c\u96d5\u624b\u201d\uff0c\u4e13\u7834\u5185\u5bb6\u6c14\u529f\uff0c\u6307\u529b\u96c4\u52b2\u65e0\u6bd4\uff1b\u5510\u6653\u6f9c\u8eab\u5f62\u534a\u8f6c\uff0c\u638c \u6839\u7528\u52b2\u4e00\u63a8\uff0c\u53f3\u62f3\u7531\u6000\u5185\u5411\u4e0a\u4e00\u51b2\uff0c\u4e91\u5927\u9e4f\u8eab\u5f62\u540e\u4ef0\uff0c\u5510\u6653\u6f9c\u5de6\u638c\u4e00\u5806\u4e00\u62e8\uff0c\u628a\u4ed6\u7684\u96d5\u624b\u62e8 \u8fc7\u4e00\u8fb9\uff0c\u731b\u7136\u8fdb\u62db\uff0c\u7ffb\u8eab\u5288\u51fb\uff0c\u5de6\u638c\u5728\u53f3\u62f3\u80cc\u4e0a\u4e00\u62ec\u800c\u8fc7\uff0c\u53f3\u62f3\u62ab\u9762\uff0c\u5de6\u638c\u659c\u5207\uff0c\u4e91\u5927\u9e4f\u53c8 \u9053\u4e00\u58f0\uff1a\u201c\u597d\uff01\u201d\u53cc\u624b\u5982\u73af\uff0c\u4e00\u7ffb\u4e00\u7ede\uff0c\u5c06\u5510\u6653\u6f9c\u51f6\u6076\u7684\u62db\u6570\u7834\u5f00\uff0c\u4e24\u4eba\u4e00\u5408\u5373\u5206\uff0c\u5404\u81ea\u6212 \u60e7\u3002 \u3000\u3000\u4e91\u5927\u9e4f\u9053\u58f0\uff1a\u201c\u518d\u6765\uff01\u201d\u518d\u5ea6\u6251\u4e0a\uff0c\u5f7c\u6b64\u7ecf\u8fc7\u8bd5\u62db\u4e4b\u540e\uff0c\u638c\u6cd5\u5747\u53d8\uff0c\u4e91\u5927\u9e4f\u53cc\u624b\u7ffb\u7ede\uff0c \u5982\u98ce\u8f66\u75be\u8f6c\uff0c\u5411\u5510\u6653\u6f9c\u7740\u7740\u8fdb\u62db\u3002\u8fd9\u79cd\u201c\u98ce\u8f66\u624b\u201d\u7684\u638c\u6cd5\uff0c\u4e43\u5317\u65b9\u6280\u51fb\u540d\u5bb6\u970d\u7389\u5802\u6240\u521b\uff0c\u5229 \u4e8e\u8fd1\u653b\uff0c\u5510\u6653\u6f9c\u9000\u4e86\u4e24\u6b65\uff0c\u53cc\u81c2\u4e00\u633a\uff0c\u91c7\u7528\u957f\u62f3\u6253\u6cd5\uff0c\u63a5\u4e86\u4ed6\u4e8c\u5341\u62db\uff0c\u89c9\u5f97\u751a\u4e3a\u5403\u529b\u3002 \u3000\u3000\u5415\u56db\u5a18\u770b\u5f97\u76b1\u4e86\u7709\u5934\uff0c\u5fc3\u60f3\uff1a\u4ee5\u5510\u6653\u6f9c\u7684\u80fd\u8010\uff0c\u7edd\u4e0d\u5728\u654c\u4eba\u4e4b\u4e0b\uff0c\u5982\u4f55\u8fd9\u79cd\u638c\u6cd5\u4e5f\u4e0d\u4f1a \u5e94\u4ed8\u3002\u518d\u8fc7\u7247\u523b\uff0c\u5510\u6653\u6f9c\u770b\u770b\u7ed9\u903c\u5230\u53f0\u8fb9\uff0c\u8fd1\u53f0\u7684\u89c2\u4f17\u7eb7\u7eb7\u907f\u5f00\uff0c\u9632\u4ed6\u8dcc\u4e0b\u3002\u90a3\u77e5\u5510\u6653\u6f9c\u8eab \u5f62\u4e00\u8d77\uff0c\u547c\u7684\u4e00\u58f0\u4ece\u4e91\u5927\u9e4f\u5934\u9876\u98de\u8fc7\uff0c\u53cd\u8f6c\u8eab\u6765\uff0c\u638c\u6cd5\u5927\u53d8\uff01 \u3000\u3000\u53f0\u4e0b\u7684\u89c2\u4f17\u8f70\u7136\u559d\u91c7\uff0c\u4e91\u5927\u9e4f\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u89c1\u5510\u6653\u6f9c\u98d8\u5ffd\u5982\u98ce\uff0c\u638c\u6cd5\u795e\u5999\u53d8\u5e7b\uff0c\u65e0\u53ef \u6349\u6478\uff01\u81ea\u5df1\u7684\u201c\u98ce\u8f66\u624b\u201d\u903c\u5230\u4e1c\u65b9\uff0c\u654c\u4eba\u5df2\u907f\u5230\u897f\u65b9\uff0c\u5230\u903c\u5230\u897f\u65b9\u65f6\uff0c\u654c\u4eba\u53c8\u5df2\u5728\u5317\u65b9\u51fa \u73b0\u3002\u5c31\u50cf\u4ed6\u5df2\u719f\u77e5\u4e86\u81ea\u5df1\u7684\u62db\u6570\u4f3c\u7684\uff0c\u4e00\u51fa\u624b\u4e00\u6295\u8db3\uff0c\u90fd\u5168\u5728\u5bf9\u65b9\u610f\u6599\u4e4b\u4e2d\u3002 \u3000\u3000\u4f60\u9053\u5510\u6653\u6f9c\u4f55\u4ee5\u4f1a\u7a81\u7136\u62a2\u5c3d\u4e0a\u98ce\uff1f\u539f\u6765\u5510\u6653\u6f9c\u5728\u6613\u5170\u73e0\u95e8\u4e0b\u4e09\u5e74\uff0c\u5b66\u7684\u662f\u4ee5\u5929\u5c71\u5251\u6cd5\u4e3a \u4e3b\uff0c\u638c\u6cd5\u901a\u800c\u4e0d\u7cbe\u3002\u4f46\u867d\u7136\u5982\u6b64\uff0c\u5929\u5c71\u638c\u6cd5\u5230\u5e95\u662f\u535a\u91c7\u5404\u5bb6\u800c\u6210\uff0c\u53ea\u8981\u8fd0\u7528\u5f97\u5b9c\uff0c\u5bf9\u4ed8\u4efb\u4f55 \u5bb6\u6570\uff0c\u90fd\u53ef\u4fdd\u6301\u4e0d\u8d25\u3002\u5510\u6653\u6f9c\u5bf9\u654c\u7684\u4e34\u573a\u7ecf\u9a8c\u4e0d\u591a\uff0c\u672a\u66fe\u89c1\u8fc7\u201c\u98ce\u8f66\u624b\u201d\u8fd9\u6837\u7684\u53e4\u602a\u6253\u6cd5\uff0c \u6240\u4ee5\u521d\u65f6\u843d\u5728\u4e0b\u98ce\u3002\u5230\u63a5\u4e86\u4e8c\u4e09\u5341\u62db\u4e4b\u540e\uff0c\u6e10\u77e5\u8def\u6570\uff0c\u4fbf\u7528\u5929\u5c71\u638c\u6cd5\u4e2d\u7684\u201c\u6e38\u9f99\u638c\u201d\u6765\uff0c\u4e13 \u514b\u8fd9\u79cd\u8fd1\u8eab\u7684\u6253\u6cd5\u3002\u5510\u6653\u6f9c\u7684\u5929\u5c71\u638c\u6cd5\u867d\u7136\u53ea\u5b66\u4e86\u4e94\u6210\uff0c\u4f46\u5bf9\u4ed8\u8d77\u4e91\u5927\u9e4f\u6765\u5df1\u7ef0\u7ef0\u6709\u4f59\u3002\u6240 \u4ee5\u5510\u6653\u6f9c\u7684\u638c\u6cd5\u5728\u7518\u51e4\u6c60\u7b49\u4e00\u6d41\u7684\u9ad8\u624b\u770b\u6765\uff0c\u867d\u7136\u8fd8\u662f\u9887\u591a\u7834\u7efd\uff0c\u4f46\u5728\u4e91\u5927\u9e4f\u770b\u6765\uff0c\u5df2\u662f\u795e \u5999\u65e0\u6bd4\u3002 \u3000\u3000\u5510\u6653\u6f9c\u8d8a\u6253\u8d8a\u5feb\uff0c\u4e91\u5927\u9e4f\u6b63\u60f3\u559d\u505c\uff0c\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u80a9\u5934\u5df2\u4e2d\u4e86\u4e00\u638c\uff0c\u5e78\u5728\u4ed6\u7684\u6839\u5e95\u751a \u597d\uff0c\u8eab\u4f53\u58ee\u5b9e\uff0c\u6643\u4e86\u4e24\u6643\uff0c\u5e76\u672a\u8dcc\u4e0b\u3002\u5510\u6653\u6f9c\u9053\u58f0\uff1a\u201c\u5f97\u7f6a\uff01\u201d\u53cc\u638c\u4e00\u6536\uff0c\u5782\u624b\u7ad9\u7acb\u3002 \u3000\u3000\u53f0\u4e3b\u8fd9\u65b9\u5728\u64c2\u53f0\u540e\u9762\u63e9\u6709\u4e00\u4e2a\u5e10\u84ec\uff0c\u559d\u91c7\u58f0\u4e2d\uff0c\u5e10\u4e2d\u8d70\u51fa\u4e00\u4e2a\u519b\u5b98\uff0c\u9053\uff1a\u201c\u8bf7\u8fd9\u4f4d\u82f1\u96c4 \u8fdb\u5e10\u9886\u8d4f\u3002\u201d\u4e91\u5927\u9e4f\u9762\u5411\u89c2\u4f17\uff0c\u5ba3\u5e03\u4eca\u65e5\u64c2\u53f0\u7ed3\u675f\u3002\u4f46\u770b\u70ed\u95f9\u7684\u89c2\u4f17\uff0c\u8fd8\u662f\u6324\u6ee1\u53f0\u524d\u3002 \u3000\u3000\u5510\u6653\u6f9c\u968f\u90a3\u519b\u5b98\u8fdb\u4e86\u5e10\u4e2d\uff0c\u90a3\u519b\u5b98\u4f38\u51fa\u624b\u6765\u4e0e\u5510\u6653\u6f9c\u76f8\u63e1\uff0c\u4e00\u63e1\u4e4b\u4e0b\uff0c\u5510\u6653\u6f9c\u53ea\u89c9\u5bf9\u65b9 \u638c\u529b\u5947\u52b2\uff0c\u81ea\u5df1\u4e94\u6307\u7adf\u88ab\u634f\u5f97\u9690\u9690\u4f5c\u75db\u3002\u90a3\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u653e\u677e\u4e86\u624b\uff0c\u9053\uff1a\u201c\u679c\u7136\u662f\u771f\u6709\u672c\u9886 \u7684\u4eba\uff01\u201d\u968f\u5373\u8bf7\u95ee\u59d3\u540d\uff0c\u5510\u6653\u6f9c\u4e71\u634f\u4e2a\u5047\u540d\u8bf4\u4e86\u3002\u90a3\u4eba\u9053\uff1a\u201c\u5c0f\u5f1f\u867d\u4e3a\u5341\u56db\u8d1d\u52d2\u7684\u8fd1\u536b\u519b\u7edf \u9886\uff0c\u4e00\u5411\u968f\u519b\u897f\u5f81\uff0c\u65e0\u6687\u7ed3\u7eb3\u4e2d\u539f\u8c6a\u6770\uff0c\u89c1\u7b11\u89c1\u7b11\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u539f\u6765\u662f\u65b9\u7edf\u9886\uff0c\u5931\u656c\u5931 \u656c\uff01\u201d\u5341\u56db\u7687\u5b50\u519b\u4e2d\uff0c\u6709\u4e24\u4e2a\u672c\u9886\u6781\u4e3a\u9ad8\u5f3a\u7684\u4eba\u7269\uff0c\u8fd1\u536b\u519b\u7684\u7edf\u9886\u65b9\u4eca\u660e\u4fbf\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u5510 \u6653\u6f9c\u5728\u6253\u64c2\u4e4b\u524d\uff0c\u65e9\u5df2\u542c\u4eba\u8bf4\u8fc7\u3002 \u3000\u3000\u65b9\u4eca\u660e\u9053\uff1a\u201c\u58ee\u58eb\u7a0d\u5f85\uff0c\u6211\u53eb\u4ed6\u4eec\u628a\u9ec4\u91d1\u62ff\u6765\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5c0f\u5f1f\u4e45\u5e55\u5341\u56db\u8d1d\u52d2\u519b\u4e2d \u7684\u82f1\u96c4\uff0c\u6b64\u756a\u524d\u6765\uff0c\u5fd7\u5728\u6c42\u6559\uff0c\u4e0d\u5728\u9ec4\u91d1\u3002\u4e0d\u77e5\u65b9\u7edf\u9886\u7edd\u4e16\u6b66\u529f\uff0c\u4f55\u4ee5\u4e0d\u53bb\u4e3b\u64c2\u3002\u201d\u65b9\u4eca\u660e \u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u8bf7\u8fdb\u5e10\u540e\u4e00\u8c08\u5982\u4f55\uff1f\u201d\u5176\u5b9e\u5510\u6653\u6f9c\u65e9\u77e5\u5341\u56db\u7687\u5b50\u6446\u64c2\u7684\u7528\u610f\uff0c\u4e5f\u65e9\u77e5\u65b9\u4eca \u660e\u5728\u5e55\u540e\u9009\u8d24\u7684\u7528\u610f\uff0c\u4ed6\u8fd9\u4e00\u95ee\uff0c\u76ee\u7684\u4e0d\u8fc7\u662f\u6545\u610f\u63a9\u853d\u81ea\u5df1\u6253\u64c2\u663e\u6280\u7684\u771f\u610f\u3002 \u3000\u3000\u518d\u8bf4\u5415\u56db\u5a18\u7b49\u6ee1\u8179\u7591\u56e2\uff0c\u5728\u53f0\u524d\u7b49\u4e86\u591a\u65f6\uff0c\u5ffd\u89c1\u5510\u6653\u6f9c\u548c\u4e00\u7fa4\u519b\u5b98\u8d70\u51fa\u5e10\u84ec\uff0c\u8de8\u4e0a\u9a8f \u9a6c\uff0c\u4e00\u7fa4\u4eba\u626c\u97ad\u547c\u559d\uff0c\u95ef\u51fa\u4eba\u4e1b\uff0c\u64c2\u53f0\u548c\u5e10\u84ec\u4e2d\u7684\u529e\u4e8b\u4eba\u5458\uff0c\u4e5f\u968f\u540e\u79bb\u5f00\uff0c\u4eba\u7fa4\u6e10\u6563\u3002\u7518\u51e4 \u6c60\u6124\u7136\u9053\uff1a\u201c\u54b1\u4eec\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u4e00\u8def\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u56de\u5230\u4e86\u51b7\u7985\u7684\u7834\u5e99\u4e4b\u540e\uff0c\u7518\u51e4\u6c60\u4ee5\u62f3\u51fb\u638c\uff0c\u6b63\u60f3\u53d1\u8bdd\uff0c\u5415\u56db\u5a18\u7a81 \u7136\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u6211\u770b\u5510\u6653\u6f9c\u7edd\u4e0d\u662f\u6c42\u8363\u5356\u53cb\u4e4b\u4eba\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u4e0e\u7518\u51e4\u6c60\u6700\u4e3a\u76f8\u8fd1\uff0c\u5fc3\u610f\u76f8\u901a\uff0c\u6240\u4ee5\u5415\u56db\u5a18\u89c1\u7518\u51e4\u6c60\u50b2\u7136\u4e8e\u8272\uff0c\u4fbf\u77e5\u4ed6\u5bf9\u5510\u6653\u6f9c \u8d77\u4e86\u5f88\u5927\u7684\u6000\u7591\uff0c\u56e0\u6b64\u62a2\u7740\u8bf4\u8bdd\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u516b\u59b9\uff0c\u5bf9\u4e8e\u5510\u6653\u6f9c\u6b64\u4eba\uff0c\u4f60\u5f53\u7136\u77e5\u9053\u5f97\u6bd4\u6211 \u6e05\u695a\u3002\u53ef\u662f\u4ed6\u5230\u5e95\u662f\u51e4\u5b50\u9f99\u5b59\uff0c\u4f60\u4e5f\u4e0d\u80fd\u8fc7\u4e8e\u8f7b\u4fe1\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4ed6\u51fa\u751f\u672a\u6ee1\u4e00\u6708\uff0c\u5373\u79bb \u5bab\u5ef7\uff0c\u4e00\u5411\u5728\u4fa0\u4e49\u95e8\u4e0b\uff0c\u6211\u4eec\u600e\u80fd\u5c06\u4ed6\u4e0e\u6ee1\u5dde\u7687\u5b50\u4e00\u4f8b\u770b\u5f85\uff1f\u800c\u4e14\u4ed6\u82e5\u60f3\u6c42\u8363\uff0c\u5f53\u65e5\u5728\u6df1\u5bab \u89c1\u4e86\u7687\u5e1d\uff0c\u5c31\u5f53\u8ba4\u7236\u5f52\u5b97\uff0c\u6c42\u5b98\u8d50\u7984\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u5230\u5e95\u4e0d\u80fd\u4e0d\u9632\u3002\u4eba\u5fc3\u6613 \u53d8\uff0c\u660e\u672a\u7684\u6d2a\u627f\u7574\u4f55\u5c1d\u4e0d\u4ee5\u5927\u5fe0\u81e3\u81ea\u547d\uff0c\u5230\u540e\u6765\u4e0d\u4e5f\u6295\u964d\u4e86\u6ee1\u5974\uff0c\u505a\u4e86\u8d30\u81e3\u4f20\u4e2d\u7684\u9996\u8981\u4eba \u7269\uff1f\u7109\u77e5\u5510\u6653\u6f9c\u4e0d\u662f\u53d8\u4e86\u5fc3\u610f\uff0c\u89c1\u5141\u796f\u505a\u7687\u5e1d\u7684\u5e0c\u671b\u6700\u5927\uff0c\u5c31\u60f3\u6295\u9760\u4ed6\u5462\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a \u201c\u4e03\u54e5\u987e\u8651\u4e0d\u65e0\u9053\u7406\uff0c\u4f46\u54b1\u4eec\u8fd8\u662f\u518d\u653e\u8fdc\u5728\u770b\uff0c\u624d\u80fd\u65ad\u5b9a\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u597d\uff0c\u6211\u5728\u4e09\u65e5\u4e4b \u5185\uff0c\u51c6\u80fd\u628a\u4ed6\u7684\u6d88\u606f\u6253\u63a2\u51fa\u6765\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e09\u65e5\uff0c\u7518\u51e4\u6c60\u679c\u7136\u628a\u5510\u6653\u6f9c\u7684\u6d88\u606f\u6253\u63a2\u51fa\u6765\uff0c\u6124\u7136\u7684\u5bf9\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u6599\u5f97\u4e0d\u9519\uff0c \u5510\u6653\u6f9c\u679c\u7136\u6295\u9760\u4e86\u5141\u796f\uff0c\u5728\u5141\u796f\u7684\u8fd1\u536b\u519b\u4e2d\uff0c\u5f53\u4e0a\u4e00\u540d\u90fd\u7edf\u4e86\u3002\u201d\u5415\u56db\u5a18\u79c0\u7709\u4e00\u6574\uff0c\u6c89\u601d\u534a \u664c\uff0c\u9053\uff1a\u201c\u5373\u7b97\u5982\u6b64\uff0c\u6050\u6015\u5176\u4e2d\u4e5f\u522b\u6709\u56e0\u7531\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u51e1\u4e8b\u4e5f\u4e0d\u80fd\u8001\u4ece\u597d\u5904\u7740\u60f3\uff0c\u8fc7 \u53bb\u6211\u4e5f\u66fe\u66ff\u4ed6\u5411\u6768\u8001\u82f1\u96c4\u548c\u5173\u4e1c\u56db\u4fa0\u7b49\u6b66\u6797\u540c\u9053\u8bf4\u60c5\uff0c\u4f46\u5982\u4eca\u4ed6\u5df2\u5f53\u4e86\u6ee1\u6d32\u519b\u804c\uff0c\u6211\u4eec\u53ef\u5c31 \u4e0d\u80fd\u8fd8\u628a\u4ed6\u5f53\u6210\u4ee5\u524d\u4e00\u6837\u770b\u5f85\u4e86\u3002\u987b\u77e5\u6b63\u56e0\u5510\u6653\u6f9c\u51fa\u8eab\u4fa0\u4e49\u95e8\u4e0b\uff0c\u548c\u6211\u4eec\u53c8\u90fd\u719f\u8bc6\uff0c\u4ed6\u82e5\u53d8 \u4e86\u5fc3\u80a0\uff0c\u6276\u52a9\u6ee1\u6d32\uff0c\u548c\u6211\u4eec\u4f5c\u5bf9\uff0c\u90a3\u624d\u662f\u4e00\u4e2a\u6700\u5389\u5bb3\u7684\u654c\u4eba\u5462\uff01\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4f9d\u4f60\u8bf4\u600e \u6837\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8d81\u4ed6\u8fd8\u672a\u624b\u63e1\u5927\u6743\u7684\u65f6\u5019\uff0c\u628a\u4ed6\u6740\u4e86\uff01\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u628a\u4ed6\u6740\u4e86\uff1f\u201d\u7518 \u51e4\u6c60\u9053\uff1a\u201c\u96be\u9053\u8fd8\u8981\u517b\u864e\u8d3b\u60a3\u5417\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u603b\u5f97\u89c1\u4ed6\u4e00\u9762\uff0c\u95ee\u4e2a\u9752\u7ea2\u7682\u767d\u3002\u201d\u7518\u51e4\u6c60 \u9053\uff1a\u201c\u516b\u59b9\u7a76\u662f\u5fc3\u8f6f\uff0c\u4e5f\u7f62\uff0c\u4f60\u65e2\u7136\u8981\u89c1\u4ed6\uff0c\u660e\u65e5\u548c\u6211\u5230\u660e\u5341\u4e09\u9675\u53bb\u3002\u201d\u5415\u56db\u5a18\u559c\u9053\uff1a\u201c\u4f60 \u5df2\u7ecf\u548c\u4ed6\u7ea6\u597d\u4e86\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4e0d\u662f\u6211\u548c\u4ed6\u7ea6\u597d\uff0c\u662f\u5141\u5824\u548c\u4ed6\u7ea6\u597d\u3002\u201d\u5415\u56db\u5a18\u5947\u9053\uff1a\u201c\u8fd9 \u600e\u4e48\u8bf4\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u5141\u5824\u660e\u65e5\u5e26\u4e00\u5e2e\u4eb2\u4fe1\u536b\u58eb\u5230\u660e\u9675\u72e9\u730e\uff0c\u6211\u6253\u542c\u5f97\u5510\u6653\u6f9c\u4e5f\u4f1a\u968f\u884c\u3002\u201d \u3000\u3000\u660e\u5341\u4e09\u9675\u5728\u5317\u4eac\u5317\u90ca\u957f\u5bff\u5c71\u4e0b\uff0c\u4e43\u662f\u5341\u4e09\u5ea7\u660e\u4ee3\u7687\u5e1d\u9675\u5893\u7684\u603b\u79f0\uff0c\u8fd9\u4e9b\u9675\u5893\u6563\u5e03\u5728\u65b9\u5706 \u7ea6\u767e\u91cc\u7684\u5c0f\u76c6\u5730\u5e26\uff0c\u4e1c\u897f\u5317\u4e09\u9762\u5cf0\u5ce6\u8038\u7acb\uff0c\u597d\u50cf\u5c4f\u969c\uff0c\u6b63\u5357\u4e00\u9762\u5982\u540c\u5587\u53ed\u53e3\u4f38\u5411\u5317\u4eac\u5e73\u539f\uff0c \u53e3\u7684\u5de6\u53f3\u6709\u4e24\u5ea7\u5c71\u2014\u2014\u9f99\u5c71\u548c\u864e\u5c71\uff0c\u6784\u6210\u4e86\u5408\u62b1\u7684\u5f62\u52bf\u3002\u6bcf\u4e2a\u9675\u5893\u7684\u9644\u8fd1\uff0c\u90fd\u6709\u4e00\u4e2a\u4ee5\u9675\u4e3a \u540d\u7684\u6751\u843d\uff0c\u5982\u6c38\u9675\u6751\u5eb7\u9675\u6751\u7b49\uff0c\u6ee1\u6d32\u5165\u5173\u4e4b\u540e\uff0c\u8f9f\u660e\u9675\u4e00\u90e8\u4e3a\u72e9\u730e\u573a\uff0c\u6751\u6c11\u4e0d\u8bb8\u5165\u5185\u6253\u730e\u3002 \u5cea\u5728\u9644\u8fd1\u5c71\u4e18\uff0c\u5374\u53ef\u4ee5\u770b\u5230\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u7518\u51e4\u6c60\u548c\u5415\u56db\u5a18\u4fbf\u5230\u660e\u9675\u4e2d\u7684\u201c\u4e3b\u9675\u201d\u2014\u2014\u957f\u9675\uff08\u660e\u6210\u7956\u5893\uff09\u9644\u8fd1\u7684\u5c71\u5934 \u7b49\u5019\u3002\u957f\u9675\u5728\u5341\u4e09\u9675\u4e2d\u5efa\u7b51\u5f97\u6700\u4e3a\u96c4\u4f1f\uff0c\u5893\u5730\u672c\u8eab\u867d\u7136\u5217\u4e3a\u7981\u533a\uff0c\u4f46\u5728\u5c71\u5934\u671b\u4e0b\uff0c\u7891\u4ead\u534e\u8868 \u77d7\u7acb\uff0c\u7fc1\u4ef2\u77f3\u517d\u5206\u5217\uff0c\u5374\u4e5f\u770b\u5f97\u9887\u4e3a\u6e05\u695a\u3002\u5415\u56db\u5a18\u767b\u9ad8\u773a\u671b\uff0c\u4e0d\u7981\u8d77\u4e86\u6545\u56fd\u4e4b\u601d\uff0c\u6f78\u7136\u6cea\u4e0b\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u6545\u56fd\u5c71\u6cb3\u5728\uff0c\u5b64\u81e3\u5b7d\u5b50\u5fc3\u3002 \u3000\u3000\u6b32\u77e5\u5510\u6653\u6f9c\u89c1\u7740\u4e86\u5415\u56db\u5a18\u4e4b\u540e\u600e\u6837\uff0c\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u70db\u5f71\u6447\u7ea2\u3000\u5141\u796f\u767b\u5927\u5b9d\u3000\u5251\u5149\u60ca\u68a6\u3000\u4fa0\u5973\u5165\u6df1\u5bab \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u4e8c\u56de \u70db\u5f71\u6447\u7ea2\u3000\u5141\u796f\u767b\u5927\u5b9d \u5251\u5149\u60ca\u68a6\u3000\u4fa0\u5973\u5165\u6df1\u5bab \u3000\u3000\u7518\u51e4\u6c60\u51fa\u8a00\u529d\u6170\uff0c\u5415\u56db\u5a18\u51c4\u7136\u9053\uff1a\u201c\u5982\u679c\u5e08\u5085\u5728\u6b64\uff0c\u5979\u8001\u4eba\u5bb6\u6050\u6015\u8981\u66f4\u4f24\u5fc3\u5462\u3002\u201d\u72ec\u81c2 \u795e\u5c3c\u4e43\u662f\u660e\u672b\u5d07\u796f\u5e1d\u7684\u516c\u4e3b\uff0c\u7518\u51e4\u6c60\u60f3\u8d77\u5e08\u5085\uff0c\u4e5f\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5230\u4e86\u8fd1\u5348\u65f6\u5206\uff0c\u9675\u9053\u4e0a\u5c18\u5934\u5927\u8d77\uff0c\u5341\u51e0\u9a91\u9a8f\u9a6c\u5982\u98de\u5954\u6765\uff0c\u7518\u5415\u4e8c\u4eba\u8eb2\u5728\u5927\u6811\u4e4b\u540e\uff0c\u8fc7\u4e86 \u4e00\u9635\uff0c\u90a3\u4e9b\u4eba\u5df2\u5230\u4e86\u957f\u9675\u7684\u5893\u5bab\u201c\u7a37\u601d\u6bbf\u201d\u524d\u4f11\u606f\uff0c\u5415\u56db\u5a18\u7eb5\u76ee\u89c2\u770b\uff0c\u679c\u89c1\u5510\u6653\u6f9c\u6742\u5728\u536b\u58eb \u4e4b\u4e2d\uff0c\u800c\u4e14\u5bf9\u5141\u5824\u72b6\u8c8c\u5341\u5206\u606d\u656c\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u5982\u4f55\uff1f\u201d\u5415\u56db\u5a18\u9ed8\u7136\u4e0d\u8bed\uff0c\u8fc7\u4e86\u4e00\u9635\u65b9\u9053\uff1a \u201c\u6709\u4f55\u529e\u6cd5\u5f15\u4ed6\u51fa\u6765\u8bb2\u8bdd\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u96be\uff0c\u96be\uff01\u201d\u60f3\u4e86\u4e00\u60f3\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u5e26\u4e86\u6697\u5668\u6ca1 \u6709\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6709\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u7b49\u4f1a\u4f60\u884c\u523a\u5141\u5824\u3002\u6545\u610f\u73b0\u8eab\u7ed9\u5510\u6653\u6f9c\u770b\u89c1\uff0c\u770b\u4ed6\u600e \u6837\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u6211\u82e5\u4e00\u51fb\u800c\u4e2d\uff0c\u5f53\u771f\u6740\u4e86\u5141\u5824\uff0c\u5c82\u4e0d\u662f\u5e2e\u4e86\u5141\u796f\u90a3\u53ae\u7684\u5927\u5fd9\uff01\u867d\u8bf4\u90a3 \u4e2a\u6ee1\u6d32\u7687\u5b50\u767b\u4f4d\u5bf9\u6211\u4eec\u6c49\u4eba\u90fd\u662f\u4e00\u6837\uff0c\u4f46\u6211\u6700\u6068\u5141\u796f\uff0c\u4e24\u5bb3\u76f8\u6743\u53d6\u5176\u8f7b\uff0c\u6211\u5b81\u613f\u89c1\u5141\u5824\u767b \u4f4d\u3002\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u8c01\u4e0d\u6068\u5141\u796f\uff1f\u6211\u53eb\u4f60\u884c\u523a\u5141\u5824\uff0c\u53ef\u5e76\u4e0d\u662f\u8981\u4f60\u4e00\u5b9a\u628a\u4ed6\u523a\u6b7b\uff0c\u7ed9\u4ed6\u6302 \u70b9\u5f69\u4e5f\u5c31\u591f\u4e86\u3002\u770b\u5510\u6653\u6f9c\u5bf9\u4f60\u600e\u6837\uff1f\u4f60\u8f7b\u529f\u8d85\u5353\uff0c\u73b0\u8eab\u4e4b\u540e\uff0c\u5c31\u53ef\u5f15\u536b\u58eb\u4eec\u8ffd\u4f60\u3002\u4ed6\u4eec\u4e5f\u8d70 \u8ffd\u4f60\u4e0d\u4e0a\u3002\u5510\u6653\u6f9c\u5982\u679c\u5e2e\u5b9a\u5141\u5824\uff0c\u548c\u4f60\u4f5c\u5bf9\uff0c\u6211\u5c31\u628a\u4ed6\u5e72\u6389\u3002\u201d\u5415\u56db\u5a18\u5fc3\u5934\u4e00\u9707\uff0c\u4f46\u7518\u51e4\u6c60 \u8bf4\u8bdd\u65a9\u9489\u622a\u94c1\uff0c\u800c\u4e14\u4e8b\u5173\u91cd\u5927\uff0c\u4e0d\u4fbf\u53cd\u5bf9\u3002\u5fc3\u91cc\u4f46\u613f\u5510\u6653\u6f9c\u4e0d\u662f\u771f\u7684\u4f9d\u9644\u6e05\u5ef7\u3002 \u3000\u3000\u5141\u5824\u7b49\u4f11\u606f\u4e00\u4f1a\uff0c\u5e26\u4e86\u730e\u72ac\u8d70\u51fa\u957f\u9675\u5893\u5730\uff0c\u72e9\u730e\u573a\u5728\u957f\u9675\u4e4b\u897f\uff0c\u6070\u6070\u8981\u7ecf\u8fc7\u7518\u5415\u4e8c\u4eba\u57cb \u4f0f\u7684\u5c71\u9e93\uff0c\u5415\u56db\u5a18\u624b\u5fc3\u6dcc\u6c57\uff0c\u7740\u7740\u5141\u5824\u7ecf\u8fc7\uff0c\u5510\u6653\u6f9c\u5c31\u5728\u4ed6\u7684\u4fa7\u8fb9\uff0c\u7518\u51e4\u6c60\u5634\u89d2\u4e00\u5476\uff0c\u793a\u610f \u53eb\u5979\u5feb\u653e\uff0c\u5415\u56db\u5a18\u500f\u5730\u98de\u8eab\u6251\u51fa\uff0c\u53f3\u624b\u4e00\u626c\uff0c\u4e09\u67c4\u5c0f\u5315\u9996\u5e26\u7740\u545c\u545c\u4e4b\u58f0\uff0c\u5206\u4e09\u8def\u5411\u5141\u5824\u6253\u5230\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u53ea\u89c1\u5510\u6653\u6f9c\u4eae\u51fa\u6e38\u9f99\u5251\u4e00\u64a9\uff0c\u628a\u5f53\u4e2d\u7684\u5315\u9996\u6253\u843d\uff0c\u5141\u5824\u4e45\u7ecf\u6218\u9635\uff0c\u8eab\u624b \u4e5f\u662f\u4e0d\u51e1\uff0c\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u628a\u5de6\u9762\u90a3\u67c4\u5315\u9996\u4e5f\u95ea\u8fc7\u4e86\uff0c\u53e6\u4e00\u540d\u536b\u58eb\u53cc\u6307\u4e00\u7bb1\uff0c\u628a\u53f3\u9762 \u90a3\u628a\u5315\u9996\u7bb1\u7740\uff0c\u53cd\u624b\u6253\u51fa\uff0c\u5927\u53eb\uff1a\u201c\u6709\u523a\u5ba2\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u9aa4\u89c1\u4e00\u4e2a\u5c11\u5973\u6251\u51fa\uff0c\u867d\u7136\u6539\u4e86\u5bb9\u8c8c\uff0c\u4f46\u4ed6\u5df2\u77e5\u9053\u662f\u5415\u56db\u5a18\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u4f17\u536b\u571f\u5df2 \u7eb7\u7eb7\u6251\u4e0a\uff0c\u5141\u5824\u9053\uff1a\u201c\u628a\u5979\u62ff\u4e0b\uff01\u201d\u5510\u6653\u6f9c\u7565\u4e00\u8fdf\u7591\u4e4b\u540e\uff0c\u821e\u5251\u8d76\u53bb\u3002 \u3000\u3000\u7518\u51e4\u6c60\u89c1\u5510\u6653\u6f9c\u6551\u62a4\u5141\u5824\uff0c\u5341\u5206\u5356\u529b\uff0c\u52c3\u7136\u5927\u6012\u3002\u4f17\u536b\u58eb\u8ffd\u8d76\u5415\u56db\u5a18\u5df2\u5230\u5357\u9762\u5c71\u9e93\uff0c\u5510 \u6653\u6f9c\u8d77\u6b65\u68a2\u8fdf\uff0c\u843d\u5728\u540e\u9762\u3002\u7518\u51e4\u6c60\u4e0d\u5047\u601d\u7d22\uff0c\u4e00\u626c\u624b\u516d\u67c4\u98de\u5200\u95ea\u7535\u5c04\u51fa\uff0c\u5168\u662f\u98de\u5411\u5510\u6653\u6f9c\u7684 \u8981\u5bb3\u4e4b\u5904\uff01 \u3000\u3000\u6b63\u5728\u7518\u51e4\u6c60\u626c\u624b\u98de\u5200\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u5f39\u5f13\u8fde\u54cd\uff0c\u7518\u51e4\u6c60\u7684\u516d\u53e3\u98de\u5200\u7ade\u5728\u534a\u7a7a\u7ed9\u4eba\u6253\u843d\uff01\u7518 \u51e4\u6c60\u5927\u5403\u4e00\u60ca\uff01\u53ea\u542c\u5f97\u53c8\u662f\u55d6\u55d6\u4e24\u58f0\uff0c\u4ece\u81ea\u5df1\u5934\u9876\u98de\u8fc7\u3002\u7518\u51e4\u6c60\u731b\u7136\u60f3\u8d77\u4e00\u4eba\uff0c\u56de\u8eab\u4fbf\u9000\uff0c \u5317\u9762\u5c71\u5934\u4e0a\u4e00\u4e2a\u7626\u957f\u8eab\u5f71\uff0c\u4fe8\u5982\u602a\u9e1f\u98de\u817e\uff0c\u500f\u5ffd\u5230\u4e86\u5c71\u811a\uff0c\u7518\u51e4\u6c60\u65bd\u5c55\u51fa\u201c\u516b\u6b65\u8d76\u8749\u201d\u7684\u672c \u9886\uff0c\u7d27\u7d27\u8ffd\u8e51\u524d\u9762\u90a3\u4eba\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u8d8a\u8fc7\u4e24\u4e2a\u5c71\u5934\u3002 \u3000\u3000\u7518\u51e4\u6c60\u53eb\u9053\uff1a\u201c\u6768\u8001\u524d\u8f88\uff0c\u4f55\u6545\u76f8\u620f\uff1f\u201d\u524d\u9762\u90a3\u5e72\u7626\u8001\u5934\u513f\u500f\u7136\u6b62\u6b65\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u957f\u987b \u98d8\u98d8\uff0c\u7b11\u9053\uff1a\u201c\u7518\u5927\u4fa0\uff0c\u51e0\u4e4e\u7ed9\u4f60\u574f\u4e86\u5927\u4e8b\uff01\u201d\u6b63\u662f\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\u3002\u7518\u51e4\u6c60\u4e00\u6115\uff0c\u4ee5\u4e3a\u4ed6 \u662f\u6270\u728a\u60c5\u6df1\uff0c\u7231\u5f92\u610f\u5207\uff0c\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u8001\u524d\u8f88\u6562\u662f\u4e3a\u5510\u6653\u6f9c\u800c\u6765\u5417\uff1f\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u6b63 \u662f\u3002\u201d\u7518\u51e4\u6c60\u8be7\u9053\uff1a\u201c\u8001\u524d\u8f88\u6b66\u6797\u9886\u8896\uff0c\u4fa0\u4e49\u611f\u4eba\uff0c\u96be\u9053\u4e5f\u5305\u5e87\u53db\u5f92\u5417\uff1f\u201d\u6768\u4ef2\u82f1\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u8fd9\u56de\u8f6e\u5230\u6211\u66ff\u655d\u5f92\u8bf4\u60c5\u4e86\uff0c\u6653\u6f9c\u6709\u7edd\u5927\u7684\u82e6\u8877\uff0c\u6709\u7edd\u79d8\u7684\u9690\u60c5\uff0c\u4ed6\u7edd\u4e0d\u662f\u6c42\u8363\u5356\u53cb\u7684 \u4eba\uff01\u201d\u7518\u51e4\u6c60\u53c8\u662f\u4e00\u6115\uff0c\u8fd9\u4e9b\u8bf4\u8bdd\u6b63\u662f\u4ed6\u4ee5\u524d\u66ff\u5510\u6653\u6f9c\u8bf4\u60c5\uff0c\u529d\u8fc7\u6768\u4ef2\u82f1\u7684\uff0c\u5f53\u4e0b\u4e0d\u89c9\u52a8 \u5bb9\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u8001\u524d\u8f88\u4e5f\u5982\u6b64\u8bf4\uff0c\u90a3\u4e48\u662f\u7518\u67d0\u83bd\u649e\u4e86\u3002\u201d\u7518\u51e4\u6c60\u4e0e\u6768\u4ef2\u82f1\uff0c\u4e00\u5357\u4e00 \u5317\uff0c\u90fd\u662f\u4ee5\u4fa0\u4e49\u5a01\u5fb7\u670d\u4eba\u7684\u6b66\u6797\u9886\u8896\uff0c\u6240\u4ee5\u4ee5\u524d\u6768\u4ef2\u82f1\u542c\u7518\u51e4\u6c60\u4e00\u8a00\uff0c\u4fbf\u51b0\u6d88\u4e86\u5bf9\u5510\u6653\u6f9c\u7684 \u8bef\u4f1a\uff0c\u800c\u4eca\u7518\u51e4\u6c60\u542c\u6768\u4ef2\u82f1\u4e00\u8a00\uff0c\u4e5f\u76f8\u4fe1\u4e86\u5510\u6653\u6f9c\u4e0d\u662f\u574f\u4eba\u3002 \u3000\u3000\u7518\u51e4\u6c60\u6b63\u60f3\u7ec6\u95ee\u6839\u7531\uff0c\u6768\u4ef2\u82f1\u7b11\u9053\uff1a\u201c\u4ee4\u5e08\u59b9\u4e5f\u6765\u4e86\u3002\u201d\u7518\u51e4\u6c60\u4ef0\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u5415\u56db\u5a18 \u4ece\u5bf9\u9762\u5c71\u4e0a\u8df3\u4e0b\uff0c\u7247\u523b\u4fbf\u5230\u8ddf\u524d\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u5e2e\u536b\u58eb\u7ed9\u6211\u5e26\u7740\u515c\u4e86\u51e0\u4e2a\u5708\u5b50\uff0c\u73b0\u5728\u53ea\u6015\u8fd8\u5728 \u5c71\u8c37\u4e4b\u4e2d\u7591\u9b3c\u7591\u795e\uff0c\u5f80\u6765\u4e71\u7a9c\u5462\uff01\u201d\u53c8\u9053\uff1a\u201c\u6211\u5728\u524d\u9762\u5c71\u5934\u89c1\u6768\u8001\u524d\u8f88\u5f15\u5e08\u5144\u6765\u6b64\uff0c\u60f3\u6765\u4f60 \u4eec\u5df2\u548c\u6653\u6f9c\u8c08\u8fc7\u4e86\uff1f\u201d\u7518\u51e4\u6c60\u6447\u4e86\u6447\u5934\uff0c\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u4e0d\u5fc5\u548c\u4ed6\u8c08\u4e86\uff0c\u6709\u4e00\u4f4d\u975e\u5e38\u4eba\u7269\uff0c\u5c31 \u4f4f\u5728\u9644\u8fd1\u6751\u843d\uff0c\u4ed6\u5012\u60f3\u548c\u4f60\u4eec\u4e00\u8c08\u3002\u201d\u7518\u51e4\u6c60\u53c8\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\u4ec0\u4e48\u4eba\u7269\uff0c\u503c\u5f97\u6768\u4ef2\u82f1\u5982\u6b64 \u63a8\u5d07\uff1f\u5415\u56db\u5a18\u9053\uff1a\u201c\u662f\u54ea\u4f4d\u4fa0\u5ba2\uff1f\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u4f60\u89c1\u4e86\u4ed6\u81ea\u7136\u77e5\u9053\u3002\u201d\u5e26\u4e24\u4eba\u5411\u5c71\u8c37\u4e2d\u8d70 \u53bb\uff0c\u6e10\u89c1\u519c\u6751\u5c0f\u5c4b\uff0c\u6563\u5e03\u4e18\u9675\uff0c\u6768\u4ef2\u82f1\u5230\u4e86\u4e00\u95f4\u5c0f\u5c4b\u5916\u9762\uff0c\u505c\u4e0b\u6b65\u6765\uff0c\u53ea\u542c\u5f97\u91cc\u9762\u6709\u4eba\u541f \u9053\uff1a\u201c\u4e16\u4e71\u540c\u5357\u53bb\uff0c\u65f6\u6e05\u72ec\u5317\u8fd8\uff0c\u4ed6\u4e61\u751f\u767d\u53d1\uff0c\u65e7\u56fd\u89c1\u9752\u5c71\u3002\u6653\u6708\u8fc7\u6b8b\u5792\uff0c\u7e41\u661f\u5bbf\u6545\u5173\u3002\u5bd2 \u79bd\u4e0e\u8870\u8349\uff0c\u5904\u5904\u4f34\u6101\u989c\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u559c\u9053\uff1a\u201c\u539f\u6765\u662f\u66fe\u4f2f\u4f2f\u5728\u6b64\u3002\u201d\u6025\u5fd9\u6263\u95e8\uff0c\u91cc\u9762\u8bd7\u58f0\u505c\u4e0b\uff0c\u95e8\u5f00\u5904\uff0c\u4e00\u4e2a\u7070\u6734\u6734 \u7684\u4e61\u4e0b\u8001\u5934\u8d70\u4e86\u51fa\u6765\uff0c\u4f46\u867d\u7136\u662f\u519c\u592b\u6253\u626e\uff0c\u5374\u63a9\u4e0d\u4f4f\u53cc\u76ee\u7684\u795e\u5149\u3002\u90a3\u8001\u5934\u770b\u4e86\u5415\u56db\u5a18\u4e00\u773c\uff0c \u7b11\u9053\uff1a\u201c\u70e7\u4e86\u7070\u6211\u4e5f\u8ba4\u5f97\u4f60\u8fd9\u5c0f\u59d0\u5b50\uff0c\u8fd9\u4f4d\u60f3\u662f\u4f60\u7684\u5e08\u5144\u6c5f\u5357\u7518\u5927\u4fa0\u4e86\u3002\u201d\u7518\u51e4\u6c60\u62b1\u62f3\u4f5c \u63d6\uff0c\u9053\uff1a\u201c\u8001\u4e08\u662f\u84b2\u6f6d\u66fe\u8001\u5148\u751f\uff1f\u201d\u90a3\u8001\u5934\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u4eec\u95fb\u540d\u5df2\u4e45\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u5728\u6b64\u89c1 \u9762\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u8001\u5934\u540d\u53eb\u66fe\u9759\uff0c\u662f\u6e56\u5357\u84b2\u6f6d\u4eba\uff0c\u9053\u5fb7\u6587\u7ae0\uff0c\u7d20\u4e3a\u4e16\u4eba\u63a8\u91cd\uff0c\u522b\u4eba\u4e3a\u4e86\u5c0a\u5d07\u4ed6\uff0c\u53ea \u79f0\u4ed6\u4e3a\u201c\u84b2\u6f6d\u5148\u751f\u201d\u800c\u4e0d\u540d\u3002\u4ed6\u5728\u4e09\u5341\u4f59\u5e74\u4e4b\u524d\uff0c\u8fd8\u662f\u4e00\u4e2a\u666e\u901a\u7684\u8bfb\u4e66\u4eba\uff0c\u867d\u7136\u6587\u7ae0\u5df2\u505a\u5f97 \u4e0d\u9519\uff0c\u4f46\u5e76\u65e0\u7279\u522b\u8fc7\u4eba\u4e4b\u5904\u3002\u540e\u6765\u4ed6\u5230\u6c38\u5174\u5e9c\u5e94\u8bd5\uff0c\u89c1\u5415\u7559\u826f\u6240\u8bc4\u65f6\u6587\u5185\uff0c\u6709\u8bba\u5937\u590f\u4e4b\u9632\uff0c \u53ca\u8bba\u4e95\u7530\u5c01\u5efa\u653f\u5236\u7684\u6587\u5b57\uff0c\u5ffd\u7136\u5e61\u7136\u5927\u609f\uff0c\u9053\uff1a\u201c\u771f\u8bfb\u4e66\u4eba\uff0c\u539f\u5e94\u5982\u6b64\u3002\u201d\u81ea\u6b64\u70e7\u4e86\u516b\u80a1\u6587 \u7ae0\uff0c\u518d\u4e0d\u5e94\u8003\u3002\u5e76\u9053\u4ed6\u7684\u95e8\u751f\u5f20\u7199\uff08\u5b57\u656c\u537f\uff0c\u6e56\u5357\u8861\u5dde\u4eba\u3002\u4e5f\u662f\u6e05\u4ee3\u7684\u4e00\u4e2a\u540d\u5112\uff09\u5230\u5415\u7559\u826f \u5bb6\u4e2d\uff0c\u8bbf\u6c42\u4e66\u7c4d\uff0c\u90a3\u65f6\u5415\u7559\u826f\u5df2\u6b7b\uff0c\u5415\u56db\u5a18\u7684\u4f2f\u7236\u6bc5\u4e2d\uff0c\u628a\u7236\u4eb2\u7684\u9057\u4e66\u90fd\u9001\u7ed9\u4ed6\u3002 \u3000\u3000\u66fe\u9759\u5f97\u4e86\u5415\u7559\u826f\u7684\u9057\u4e66\u4e4b\u540e\uff0c\u4e5f\u7ee7\u627f\u4e86\u5415\u7559\u826f\u7684\u9057\u5fd7\uff0c\u4ee5\u6392\u6ee1\u4e3a\u5df1\u4efb\uff0c\u4ed6\u867d\u7136\u4e0d\u662f\u5415\u7559 \u826f\u4eb2\u81ea\u6559\u51fa\u7684\u5b66\u751f\uff0c\u4f46\u5374\u771f\u6b63\u627f\u7ee7\u4e86\u5415\u7559\u826f\u7684\u8863\u94b5\u3002\u66fe\u9759\u540e\u6765\u53c8\u4eb2\u5230\u6d59\u6c5f\u5415\u5bb6\uff0c\u4e0e\u5415\u8446\u4e2d \uff08\u5415\u56db\u5a18\u4e4b\u7236\uff09\u3001\u5415\u6bc5\u4e2d\u3001\u4e25\u6d2a\u9035\u7b49\u5171\u7814\u5415\u7559\u826f\u7684\u5b66\u8bf4\uff0c\u6240\u4ee5\u5415\u56db\u5a18\u81ea\u5c0f\u5c31\u548c\u4ed6\u76f8\u719f\u3002 \u3000\u3000\u5415\u56db\u5a18\u95ee\u9053\uff1a\u201c\u66fe\u4f2f\u4f2f\u51e0\u65f6\u6765\u4eac\u7684\uff1f\u201d\u66fe\u9759\u7b11\u9053\uff1a\u201c\u6bd4\u4f60\u4eec\u6765\u65e9\u51e0\u5929\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a \u201c\u6211\u4eec\u7684\u884c\u8e2a\u4f60\u90fd\u77e5\u9053\u4e86\uff1f\u201d\u66fe\u9759\u7b11\u9053\uff1a\u201c\u89c1\u9762\u7684\u90fd\u662f\u8fd9\u5e2e\u670b\u53cb\uff0c\u600e\u80fd\u4e0d\u77e5\u9053\u5462\uff1f\u4e0d\u8fc7\u4f60\u4eec \u7684\u4f4f\u5904\u6211\u8fd8\u672a\u6253\u63a2\u51fa\u6765\uff0c\u8981\u4e0d\u7136\u6211\u5c31\u5148\u53bb\u770b\u4f60\u4eec\u4e86\uff0c\u7528\u4e0d\u7740\u8d39\u8fd9\u4e48\u5927\u7684\u52b2\u8bf7\u6768\u8001\u5148\u751f\u628a\u4f60\u4eec \u5f15\u6765\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u4eec\u4eca\u65e5\u6b64\u6765\uff0c\u66fe\u5148\u751f\u4e5f\u77e5\u9053\u4e86\u3002\u201d\u66fe\u9759\u9053\uff1a\u201c\u5510\u6653\u6f9c\u662f\u6211\u8bbe\u8ba1\u9001\u4ed6 \u5230\u5141\u5824\u5e9c\u4e0a\u53bb\u7684\uff0c\u6211\u81ea\u7136\u4e0d\u80fd\u4e0d\u66ff\u4ed6\u7559\u610f\u3002\u6628\u65e5\u6211\u542c\u5f97\u7518\u5927\u4fa0\u6253\u542c\u6653\u6f9c\u7684\u6d88\u606f\u4fbf\u77e5\u4f60\u4eec\u4eca\u65e5 \u5fc5\u7136\u5728\u6b64\u7b49\u4ed6\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u95ee\u9053\uff1a\u201c\u66fe\u4f2f\u4f2f\u4e3a\u4f55\u8981\u628a\u6653\u6f9c\u5b89\u6392\u5728\u5141\u5824\u5e9c\u4e2d\uff1f\u201d\u66fe\u9759\u5477\u4e86\u4e00\u53e3\u6d53\u8336\uff0c\u9053\uff1a\u201c\u6ee1 \u6d32\u5165\u5173\u4e03\u516b\u5341\u5e74\uff0c\u6839\u57fa\u5df2\u7a33\uff0c\u8981\u805a\u4e49\u6c11\uff0c\u4e3e\u4e49\u65d7\uff0c\u6b63\u5f0f\u53d1\u96be\uff0c\u63a8\u5012\u6e05\u5ef7\uff0c\u6050\u6015\u662f\u5f88\u96be\u7684\u4e86\u3002 \u6240\u4ee5\u6211\u60f3\u4ece\u4e24\u65b9\u9762\u5165\u624b\uff0c\u4e00\u65b9\u9762\u662f\u7b56\u52a8\u6e05\u519b\u4e2d\u7684\u6c49\u4eba\u5c06\u9886\u9020\u53cd\uff1b\u53e6\u65b9\u9762\u662f\u8bbe\u6cd5\u4ee4\u4ed6\u4eec\u81ea\u76f8\u6b8b \u6740\u3002\u201d\u7518\u51e4\u6c60\u542c\u4e86\u5fc3\u4e2d\u5f88\u4e0d\u4ee5\u4e3a\u7136\uff0c\u5fc3\u60f3\uff1a\u590d\u56fd\u5927\u4e1a\uff0c\u7109\u80fd\u56e0\u4eba\u6210\u4e8b\uff1f\u7b56\u53cd\u56fa\u7136\u91cd\u8981\uff0c\u4f46\u5230 \u5e95\u4e0d\u80fd\u6043\u4e3a\u4e3b\u529b\u3002\u4f46\u66fe\u9759\u4e43\u4e00\u4ee3\u540d\u5112\uff0c\u7518\u51e4\u6c60\u521d\u6b21\u548c\u4ed6\u89c1\u9762\uff0c\u5bf9\u4ed6\u7684\u7b56\u7565\u867d\u4e0d\u8c13\u7136\uff0c\u5374\u4e5f\u4e0d \u4fbf\u7acb\u523b\u548c\u4ed6\u4e89\u8bba\u3002 \u3000\u3000\u5415\u56db\u5a18\u63d2\u53e3\u9053\uff1a\u201c\u4f2f\u4f2f\u7684\u610f\u601d\u662f\u60f3\u53eb\u5510\u6653\u6f9c\u717d\u52a8\u5141\u796f\u548c\u5141\u5824\u4f5c\u5bf9\uff0c\u8ba9\u4ed6\u4eec\u81ea\u76f8\u6b8b\u6740\u3002\u201d \u66fe\u9759\u9053\uff1a\u201c\u6b63\u662f\u3002\u4ed6\u4eec\u706b\u62fc\uff0c\u4e0d\u7ba1\u8c01\u76fc\u8c01\u8d25\uff0c\u90fd\u4f24\u4e86\u6ee1\u6d32\u5143\u6c14\u3002\u4ed6\u4eec\u5143\u6c14\u635f\u4e00\u5206\uff0c\u4fbf\u662f\u6211\u4eec \u7684\u5b9e\u529b\u589e\u4e00\u5206\u3002\u5f53\u4e0b\u8bf4\u51fa\u4ed6\u548c\u6768\u4ef2\u82f1\u5317\u6765\u4e4b\u4e8b\u3002 \u3000\u3000\u539f\u6765\u66fe\u9759\u8fde\u5e74\u5954\u8d70\u6c5f\u6e56\uff0c\u7ed3\u4ea4\u4e49\u58eb\uff0c\u548c\u6768\u4ef2\u82f1\u4e5f\u662f\u8001\u76f8\u8bc6\u4e86\u3002\u4e09\u6708\u524d\u4ed6\u5230\u6768\u5bb6\uff0c\u542c\u8bf4\u4e86 \u5510\u6653\u6f9c\u590d\u6742\u7684\u8eab\u4e16\uff0c\u8ba4\u4e3a\u5927\u53ef\u5229\u7528\u3002\u6240\u4ee5\u6025\u6025\u548c\u4ed6\u6765\u4eac\u3002\u5230\u4e86\u4eac\u57ce\uff0c\u77e5\u9053\u4e86\u5eb7\u7199\u75c5\u91cd\uff0c\u66f4\u8ba4 \u4e3a\u662f\u7edd\u597d\u7684\u65f6\u673a\u3002\u6240\u4ee5\u53eb\u5510\u6653\u6f9c\u6545\u610f\u6253\u64c2\u663e\u6280\uff0c\u6df7\u8fdb\u5141\u5824\u5e9c\u5185\u3002 \u3000\u3000\u66fe\u9759\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\u4f60\u4eec\u8fd8\u672a\u77e5\u9053\u5462\uff0c\u5141\u5824\u6765\u4eac\u534a\u6708\uff0c\u8fd8\u672a\u66fe\u89c1\u8fc7\u5eb7\u7199\u7684\u9762\u3002\u201d\u5415\u56db\u5a18 \u5947\u9053\uff1a\u201c\u662f\u4e48\uff1f\u5eb7\u7199\u6700\u5ba0\u7231\u4ed6\uff0c\u4e3a\u4f55\u4e0d\u8ba9\u4ed6\u5165\u5bab\u89c1\u9762\u3002\u201d\u66fe\u9759\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u5141\u796f\u4ece\u4e2d\u6363\u9b3c\uff0c \u53eb\u9686\u79d1\u591a\u7b49\u66ff\u4ed6\u5c01\u4f4f\u5bab\u95e8\u5417\uff01\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5eb7\u7199\u96c4\u624d\u5927\u7565\uff0c\u4e5f\u4e0d\u662f\u4e00\u4e2a\u597d\u76f8\u4e0e\u7684\u4eba\u3002\u800c\u4eca \u5bab\u5ef7\u4e4b\u5185\uff0c\u7adf\u88ab\u5141\u796f\u4e00\u515a\u628a\u5f85\uff0c\u60f3\u5fc5\u4ed6\u4e5f\u5df2\u75c5\u5165\u818f\u76f2\u4e86\u3002\u201d\u66fe\u9759\u9053\uff1a\u201c\u6211\u770b\u4e5f\u662f\u5982\u6b64\u3002\u6240\u4ee5 \u60f3\u6311\u52a8\u4ed6\u4eec\u5185\u54c4\uff0c\u5fc5\u987b\u4e9f\u4e9f\u8fdb\u884c\u3002\u201d \u3000\u3000\u4f17\u4eba\u8c08\u8bba\u4e00\u4f1a\uff0c\u5415\u56db\u5a18\u95ee\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u4ee4\u5a9b\u5462\uff1f\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u6211\u6015\u5979\u60f9\u4e8b\uff0c\u4e0d\u6562\u8ba9\u5979 \u540c\u6765\u3002\u5979\u548c\u6653\u6f9c\u5df2\u8ba2\u5a5a\u4e86\u3002\u201d\u5415\u56db\u5a18\u8fde\u58f0\u201c\u606d\u559c\u201d\uff0c\u5fc3\u4e2d\u5374\u6697\u60f3\u9053\uff1a\u5510\u6653\u6f9c\u4e00\u5411\u618e\u6076\u5979\uff0c\u8fd9 \u5a5a\u4e8b\u53ea\u6015\u8fd8\u6709\u53d8\u5366\uff1f\u5fc3\u9887\u4e0d\u5b89\uff0c\u4f46\u5374\u4e0d\u6562\u8bf4\u51fa\u3002 \u3000\u3000\u66fe\u9759\u7b11\u9053\uff1a\u201c\u83b9\u4f84\u5973\uff0c\u4f60\u7684\u559c\u4e8b\u4e5f\u6015\u5feb\u4e86\uff1f\u6211\u5230\u6768\u8001\u82f1\u96c4\u5bb6\u4e2d\u4e4b\u524d\uff0c\u66fe\u4e0a\u4ed9\u971e\u5cad\u89c1\u8fc7\u5728 \u5bbd\uff0c\u4ed6\u5df2\u7ecf\u80fd\u51fa\u5bfa\u95e8\u6563\u6b65\u4e86\u3002\u201d\u5415\u56db\u5a18\u674f\u9762\u6cdb\u7ea2\uff0c\u5fc3\u4e2d\u751a\u662f\u6b22\u559c\u3002\u66fe\u9759\u9053\uff1a\u201c\u6211\u5230\u4ed6\u7684\u4e66\u623f \u53bb\u5750\uff0c\u8fd8\u62a2\u4e86\u4ed6\u4e00\u9996\u8bcd\u5462\u3002\u201d\u5415\u56db\u5a18\u5fcd\u4e0d\u7740\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u62a2\u4ed6\u7684\uff1f\u201d\u66fe\u9759\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u4f60\u770b\u4e86\u5c31\u77e5\u9053\u4e86\u3002\u201d\u638f\u51fa\u4e00\u7eb8\u8bcd\u7b3a\uff0c\u679c\u7136\u662f\u6c88\u5728\u5bbd\u7684\u7b14\u8ff9\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u9053\uff1a \u3000\u3000\u68a6\u6df1\u5e7d\uff0c\u5ea6\u5173\u5c71\u5343\u91cc\uff0c\u5bfb\u89c5\u65e7\u65f6\u6e38\u3002\u6811\u8001\u8352\u5858\uff0c\u82d4\u6df1\u82c7\u66f2\uff0c\u66fe\u5bc4\u5fc3\u4e8b\u60a0\u60a0\u3002\u53ea\u800c\u4eca\uff0c\u98de \u9e3f\u6e10\u6773\uff0c\u7b97\u534e\u5e74\u53c8\u8fc7\u51e0\u6e05\u79cb\uff1f\u4e1c\u6d77\u6f6e\u751f\uff0c\u971e\u5cea\u7fe0\u62e5\uff0c\u5c3d\u6041\u51dd\u9635\u3002\u56de\u9996\u6b8a\u4e61\u4f5c\u4fa3\uff0c\u51e0\u540c\u6d88\u6b8b \u6f0f\uff0c\u5171\u8bfb\u897f\u697c\u3002\u73ed\u56fa\u4e66\u6210\uff0c\u76f8\u5982\u8d4b\u5c31\uff0c\u95f2\u62db\u9e33\u76df\u9e25\u3002\u95ee\u5f81\u4eba\u5f52\u6765\u4f55\u65e5\uff1f\u5411\u9f99\u5c71\u9189\u4e0e\u767d\u4e91\u6d6e\u3002 \u6b63\u662f\u83ca\u52b3\u5170\u79c0\uff0c\u5929\u6daf\u4f55\u82e6\u6df9\u7559\uff1f \u3000\u3000\u60f3\u5fc6\u4e4b\u6df1\uff0c\u8dc3\u7136\u7eb8\u4e0a\u3002\u5415\u56db\u5a18\u770b\u4e86\uff0c\u66f4\u662f\u53c8\u559c\u53c8\u7f9e\u3002\u5fc3\u60f3\u9053\uff1a\u8fd9\u91cc\u7684\u4e8b\u60c5\u4e00\u5b8c\uff0c\u6211\u4e5f\u8be5 \u56de\u53bb\u770b\u4ed6\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u90a3\u65e5\u9aa4\u7136\u89c1\u4e86\u5415\u56db\u5a18\u548c\u7518\u51e4\u6c60\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u8bda\u6050\u88ab\u5141\u5824\u770b\u51fa\u7834\u7efd\uff0c\u5230\u540e\u6765\u7518\u51e4\u6c60 \u88ab\u6768\u4ef2\u82f1\u5f15\u8d70\uff0c\u4ed6\u4e5f\u968f\u4f17\u8ffd\u8fc7\u51e0\u4e2a\u5c71\u5934\uff0c\u76f4\u5230\u5415\u56db\u5a18\u8e2a\u8ff9\u5df2\u6773\uff0c\u8fd9\u624d\u56de\u6765\u3002\u5141\u5824\u95f7\u95f7\u4e0d\u4e50\uff0c \u9053\uff1a\u201c\u5c0f\u5c0f\u4e24\u4e2a\u523a\u5ba2\uff0c\u90fd\u6349\u4e0d\u7740\uff0c\u8981\u4f60\u4eec\u4f55\u7528\uff1f\u201d\u4f17\u536b\u58eb\u4e0d\u6562\u4f5c\u58f0\u3002\u5141\u5824\u53c8\u5bf9\u5510\u6653\u6f9c\u9053\uff1a \u201c\u8fd8\u7b97\u4f60\u6709\u70b9\u80fd\u8010\uff0c\u90a3\u4e2a\u7528\u5f39\u5f13\u6697\u52a9\u4f60\u7684\u4eba\u662f\u8c01\u5440\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u3002\u201d\u597d\u5728\u5141\u5824 \u5e76\u4e0d\u8ffd\u7a76\uff0c\u8349\u8349\u6536\u961f\u56de\u57ce\u3002 \u3000\u3000\u5510\u6653\u6f9c\u6b63\u559c\u65e0\u4e8b\uff0c\u4e0d\u6599\u56de\u5230\u7687\u5e9c\uff0c\u5141\u5824\u5ffd\u7136\u5411\u4ed6\u4e00\u6307\uff0c\u559d\u9053\uff1a\u201c\u628a\u8fd9\u5c0f\u5b50\u62ff\u4e0b\u6765\uff01\u201d\u4e24 \u65c1\u536b\u58eb\u7a81\u7136\u6251\u4e0a\uff0c\u5510\u6653\u6f9c\u6beb\u4e0d\u62b5\u6297\uff0c\u675f\u624b\u8ba9\u4ed6\u4eec\u64d2\u4e86\uff0c\u5141\u5824\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u6697\u85cf\u5978\u8bc8\uff0c\u5206\u660e \u662f\u548c\u523a\u5ba2\u4e00\u8def\uff0c\u4f60\u5f53\u6211\u4e0d\u77e5\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u558a\u51a4\u9053\uff1a\u201c\u5c0f\u7684\u4fdd\u536b\u4e0d\u5468\uff0c\u7f6a\u5f53\u4e07\u6b7b\u3002\u4f46\u82e5\u8bf4\u5c0f\u7684 \u52fe\u7ed3\u532a\u4eba\uff0c\u90a3\u5374\u662f\u6b7b\u4e0d\u7791\u76ee\u3002\u201d\u5141\u5824\u9053\uff1a\u201c\u8ffd\u90a3\u5973\u8d3c\u65f6\uff0c\u4f60\u4e3a\u4f55\u843d\u5728\u536b\u58eb\u4e4b\u540e\u3002\u201d\u5510\u6653\u6f9c \u9053\uff1a\u201c\u6211\u53d7\u4e86\u4e00\u70b9\u4f24\uff0c\u867d\u7136\u4e0d\u91cd\uff0c\u4f46\u5f53\u65f6\u5374\u672a\u514d\u4e00\u60ca\uff0c\u6240\u4ee5\u8d77\u6b65\u8fdf\u4e86\u3002\u201d\u9732\u51fa\u624b\u8155\uff0c\u679c\u7136\u6709 \u4e00\u9053\u4e09\u5bf8\u6765\u957f\u7684\u5200\u75d5\uff0c\u539f\u6765\u5510\u6653\u6f9c\u5728\u64a9\u5415\u56db\u5a18\u98de\u5200\u4e4b\u65f6\uff0c\u6545\u610f\u5c06\u5251\u950b\u4e00\u6302\uff0c\u8ba9\u98de\u5200\u843d\u5730\u4e4b \u9645\uff0c\u64e6\u8fc7\u81ea\u5df1\u624b\u8155\u3002\u5141\u5824\u9762\u8272\u7a0d\u89c1\u7f13\u548c\uff0c\u559d\u9053\uff0c\u201c\u4e3a\u4f55\u4f60\u4e0d\u65e9\u8bf4\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4e00\u70b9\u8f7b\u4f24 \u4e0d\u6562\u5f20\u626c\u5938\u529f\u3002\u201d\u5141\u5824\u9762\u8272\u66f4\u597d\uff0c\u9053\uff0c\u201c\u90a3\u4e48\u8bf4\uff0c\u4f60\u5bf9\u6211\u5012\u5f88\u5fe0\u5fc3\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u7687\u7237\u660e \u9274\u3002\u201d\u5141\u5824\u53cc\u7738\u70af\u70af\uff0c\u773c\u5149\u5728\u5510\u6653\u6f9c\u9762\u4e0a\u626b\u6765\u626b\u53bb\uff0c\u5510\u6653\u6f9c\u60f3\u8d77\u4e86\u66fe\u9759\u201c\u80c6\u5927\u5fc3\u7ec6\u201d\u7684\u5631 \u8a00\uff0c\u5140\u7136\u7ad9\u7acb\uff0c\u4e0d\u52a8\u795e\u8272\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u5141\u5824\u624d\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u662f\u4ffa\u9519\u602a\u4e86\u4f60\u3002\u5de6\u53f3\u66ff\u4ed6\u89e3 \u7f1a\u3002\u201d\u5510\u6653\u6f9c\u53e9\u5934\u8c22\u6069\uff0c\u5141\u5824\u5ffd\u7136\u548c\u989c\u60a6\u8272\u7684\u9053\uff1a\u201c\u4f60\u679c\u7136\u5fe0\u5fc3\uff0c\u660e\u65e5\u5347\u4f60\u505a\u8fd1\u536b\u519b\u4e2d\u7684\u4e00 \u4e2a\u90fd\u7edf\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u8fd9\u4e00\u591c\u6ca1\u6709\u597d\u7761\uff0c\u6697\u60f3\u5341\u56db\u7687\u5b50\u8fd9\u6837\u7cbe\u660e\uff0c\u53ea\u6015\u4ed6\u7684\u7591\u5fc3\u4e0d\u6613\u6d88\u6c13\u3002\u679c\u7136\u5230\u4e86\u7b2c \u4e8c\u5929\u665a\u4e0a\uff0c\u5141\u5824\u53c8\u6d3e\u4eba\u628a\u4ed6\u5355\u72ec\u53eb\u5165\u5bc6\u5ba4\u3002 \u3000\u3000\u5510\u6653\u6f9c\u5fc3\u4e2d\u60f4\u60f4\uff0c\u53ea\u542c\u5f97\u5141\u5824\u9053\uff1a\u201c\u4f60\u66ff\u6211\u529e\u4e00\u4ef6\u4e8b\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u542c\u7687\u7237\u5429\u5490\u3002\u201d \u5141\u5824\u9053\uff1a\u201c\u8fd9\u4e8b\u6613\u529e\u5f97\u5f88\u3002\u201d\u8bf4\u7740\u62ff\u51fa\u4e00\u6761\u7ef3\u5b50\u548c\u4e00\u4e2a\u836f\u74f6\u6765\uff0c\u7eed\u9053\uff1a\u201c\u4f60\u66ff\u6211\u53bb\u6740\u4e00\u4e2a\u72af \u4eba\u3002\u4f60\u7528\u8fd9\u6761\u7ef3\u5b50\u5c06\u4ed6\u7ede\u6740\u4e4b\u540e\uff0c\u7528\u836f\u6c34\u6d47\u4ed6\u5c38\u4f53\uff0c\u8fd9\u662f\u5927\u5185\u7684\u79d8\u836f\uff0c\u6d47\u4e86\u4e4b\u540e\uff0c\u4ed6\u5c38\u8eab\u4fbf \u5316\u4e3a\u8840\u6c34\u3002\u72af\u4eba\u56da\u5728\u7687\u5e9c\u4e1c\u9662\u7b2c\u4e09\u95f4\u623f\u7684\u697c\u4e0a\u3002\u4f60\u53bb\u5427\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u542c\u5f97\u6bdb\u9aa8\u609a\u7136\uff0c\u63a5\u8fc7\u7ef3\u5b50\u836f\u74f6\uff0c\u5141\u5824\u53c8\u9053\uff1a\u201c\u4f60\u5e26\u4e86\u5b9d\u5251\u6ca1\u6709\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a \u201c\u5e26\u4e86\u3002\u201d\u5141\u5824\u9053\uff1a\u201c\u4f60\u5c06\u4ed6\u7ede\u6b7b\u4e4b\u540e\uff0c\u524a\u4ed6\u7684\u4e2d\u6307\u56de\u6765\u89c1\u6211\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u5949\u4ee4\u800c\u53bb\uff0c\u63a8\u5f00\u56da\u623f\uff0c\u53ea\u542c\u5f97\u91cc\u9762\u9ed1\u9edd\u9edd\u7684\uff0c\u6709\u4e00\u4e2a\u4eba\u5728\u547b\u541f\u3002\u5510\u6653\u6f9c\u5173\u4e0a\u623f \u95e8\uff0c\u6253\u71c3\u706b\u77f3\uff0c\u53ea\u89c1\u4e00\u4e2a\u7537\u4eba\u84ec\u9996\u57a2\u9762\uff0c\u745f\u7f29\u5c4b\u89d2\uff0c\u547b\u541f\u9053\uff1a\u201c\u597d\uff0c\u4f60\u628a\u6211\u6740\u4e86\u5427\uff0c\u6211\u5927\u6c49 \u4e49\u6c11\uff0c\u8a93\u6b7b\u4e0d\u8fb1\uff0c\u770b\u4f60\u4eec\u8fd9\u4e9b\u80e1\u72d7\uff0c\u6a2a\u884c\u5230\u51e0\u65f6\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u5927\u5403\u4e00\u60ca\uff0c\u542c\u8fd9\u8bed\u6c14\uff0c\u6b64\u4eba\u7adf\u662f\u81ea\u5df1\u540c\u9053\u4e2d\u4eba\u3002\u4e0a\u524d\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u6b7b\u56da\uff0c\u4eca\u65e5\u662f \u4f60\u6b7b\u671f\u5230\u4e86\u3002\u4f60\u6709\u4ec0\u4e48\u9057\u8a00\u8981\u7559\u4e0b\u4e48\uff1f\u201d\u90a3\u4eba\u7741\u5f00\u4e86\u773c\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u662f\u80e1\u4eba\u8fd8\u662f\u6c49\u4eba\uff1f\u201d\u5510 \u6653\u6f9c\u9053\uff1a\u201c\u4f60\u7ba1\u6211\u662f\u80e1\u662f\u6c49\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u770b\u6765\u4f60\u662f\u6c49\u4eba\uff0c\u4e3a\u4f55\u5374\u505a\u80e1\u864f\u9e70\u72ac\uff1f\u201d\u5510\u6653\u6f9c\u53d6 \u51fa\u7ef3\u5b50\uff0c\u5fc3\u4e2d\u601d\u91cf\u4e0d\u5b9a\uff1a\u5230\u5e95\u662f\u6740\u4ed6\u8fd8\u662f\u6551\u4ed6\uff1f\u82e5\u7136\u6740\u4ed6\uff0c\u4e8e\u5fc3\u4f55\u5fcd\uff1f\u82e5\u4e0d\u6740\u4ed6\uff0c\u66fe\u9759\u6240\u6258 \u7684\u5927\u4e8b\uff0c\u5fc5\u8981\u56e0\u6b64\u8bef\u4e86\u3002\u6b63\u81ea\u8e0c\u8e87\uff0c\u90a3\u4eba\u5ffd\u9053\uff1a\u201c\u6211\u518d\u95ee\u4f60\u4e00\u53e5\u8bdd\uff0c\u73b0\u5728\u662f\u4ec0\u4e48\u65f6\u5019\uff1f\u201d\u5510 \u6653\u6f9c\u9053\uff1a\u201c\u5feb\u5230\u5348\u591c\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u672c\u9886\u6781\u9ad8\u4e4b\u4eba\uff0c\u7ea6\u597d\u5348\u591c\u6551\u6211\u3002\u4f60\u548c\u6211\u4eec\u4e00\u9053\u8d70 \u5427\u3002\u201d\u5510\u6653\u6f9c\u601d\u6f6e\u6c79\u6d8c\uff0c\u903c\u8fd1\u4e24\u6b65\uff0c\u90a3\u4eba\u53c8\u9053\uff1a\u201c\u4f60\u6740\u4e86\u6211\uff0c\u5c06\u6c38\u4e3a\u5927\u6c49\u7f6a\u4eba\u3002\u201d\u5510\u6653\u6f9c\u5fc3 \u5ff5\u4e00\u52a8\uff0c\u5ffd\u7136\u51b7\u7b11\u9053\uff1a\u201c\u6211\u53ea\u77e5\u8d1d\u52d2\u4e4b\u547d\uff0c\u4eca\u5fc5\u8981\u9001\u4f60\u5f52\u5929\u3002\u201d\u90a3\u4eba\u6012\u9053\uff1a\u201c\u6211\u662f\u897f\u5317\u4e49\u519b \u9996\u9886\uff0c\u4f60\u6740\u4e86\u6211\uff0c\u6211\u7684\u5f1f\u5144\u4e5f\u4e0d\u9976\u4f60\uff01\u201d\u5510\u6653\u6f9c\u559d\u9053\uff1a\u201c\u6b7b\u56da\u95ed\u53e3\uff01\u201d\u5c06\u4ed6\u4e00\u628a\u63e1\u4e86\u8d77\u6765\uff0c \u5de6\u624b\u53d6\u51fa\u7ef3\u7d22\uff0c\u5957\u5728\u4ed6\u7684\u9888\u4e0a\u3002 \u3000\u3000\u90a3\u4eba\u53eb\u9053\uff0c\u201c\u4e8c\u54e5\u6765\u5440\u3002\u201d\u7a97\u5916\u547c\u7684\u4e00\u58f0\uff0c\u94c1\u679d\u9f50\u65ad\uff0c\u9ed1\u591c\u4e2d\u98de\u8fdb\u4e00\u4eba\uff0c\u5e73\u63d0\u4e00\u628a\u91d1\u5149 \u95ea\u95ea\u7684\u957f\u5251\uff0c\u5510\u6653\u6f9c\u8eab\u5f62\u4e00\u95ea\uff0c\u90a3\u4eba\u559d\u9053\uff1a\u201c\u5feb\u653e\u6211\u7684\u5927\u54e5\uff01\u201d\u5510\u6653\u6f9c\u5927\u53eb\u9053\uff1a\u201c\u6709\u523a \u5ba2\uff01\u201d\u907f\u4e86\u4e24\u62db\uff0c\u6e38\u9f99\u5251\u65e9\u5df2\u62d4\u5728\u624b\u4e2d\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u90a3\u4eba\u8fde\u8fdb\u4e94\u62db\uff0c\u5510\u6653\u6f9c\u4e5f\u8fd8\u4e86\u56db\u5251\u3002 \u3000\u3000\u90a3\u4eba\u8fb9\u6253\u8fb9\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u8eab\u624b\u5374\u6548\u5fe0\u6ee1\u6d32\u8d1d\u52d2\u7f9e\u4e5f\u4e0d\u7f9e\u3002\u201d\u5510\u6653\u6f9c\u4e5f\u559d\u9053\uff1a\u201c\u6b3a\u541b\u72af \u4e0a\uff0c\u5927\u9006\u4e0d\u9053\uff0c\u4f11\u5f97\u80e1\u8a00\uff01\u201d\u6e38\u9f99\u5251\u8fc5\u82e5\u98d8\u98ce\uff0c\u6b3a\u8eab\u76f4\u8fdb\uff0c\u5251\u5149\u4e2d\u7167\u89c1\u90a3\u4eba\u5e26\u7740\u9ed1\u8272\u9762\u5177\uff0c \u72f0\u72de\u53ef\u6015\uff01\u5510\u6653\u6f9c\u8fde\u4f7f\u8ffd\u98ce\u5251\u4e2d\u7684\u201c\u7a46\u738b\u795e\u9a8f\u201d\u201c\u738b\u6bcd\u9752\u79bd\u201d\u4e24\u62db\uff0c\u4e00\u5251\u523a\u4ed6\u4e0b\u76d8\uff0c\u518d\u4e00\u6296 \u5251\u950b\u76f4\u4e0a\uff0c\u523a\u4ed6\u9762\u90e8\uff0c\u8fd9\u4e24\u5251\u4e00\u4e0b\u4e00\u4e0a\uff0c\u8fd0\u7528\u8d77\u6765\u6781\u4e3a\u8270\u96be\uff0c\u4f46\u5374\u662f\u8ffd\u98ce\u5251\u4e2d\u6700\u51f6\u7684\u7edd\u62db\u3002 \u90a3\u4eba\u8eab\u624b\u6781\u4e3a\u4e86\u5f97\uff0c\u5e73\u5251\u4e00\u62e9\uff0c\u8f6c\u4e86\u534a\u4e2a\u5706\u5f27\uff0c\u5251\u98ce\u9707\u8361\uff0c\u7adf\u628a\u5510\u6653\u6f9c\u7684\u6e38\u9f99\u5251\u5c01\u51fa\u5916\u95e8\uff01 \u3000\u3000\u5510\u6653\u6f9c\u5927\u5403\u4e00\u60ca\uff0c\u6e38\u9f99\u5251\u5411\u524d\u4e00\u63c9\uff0c\u628a\u654c\u4eba\u653b\u52bf\u89e3\u5f00\uff0c\u5251\u628a\u4e00\u65cb\uff0c\u5251\u5203\u6a2a\u524a\uff0c\u90a3\u8499\u9762\u4eba \u6a2a\u5251\u4e00\u6321\uff0c\u706b\u661f\u7bf7\u98de\u4e2d\u5251\u5203\u7f3a\u4e86\u4e00\u53e3\uff0c\u8d5e\u9053\uff1a\u201c\u597d\u5251\uff01\u201d\u5510\u6653\u6f9c\u8d81\u52bf\u75be\u53d1\uff0c\u9661\u89c9\u5251\u5c16\u4f3c\u7ed9\u4ec0 \u4e48\u4e1c\u897f\u4e00\u5438\uff0c\u5251\u5c16\u843d\u7a7a\uff0c\u90a3\u4eba\u5237\u7684\u4e00\u5251\u523a\u5230\u5c0f\u8179\u3002\u5510\u6653\u6f9c\u6643\u80a9\u659c\u95ea\uff0c\u90a3\u4eba\u4f3c\u4e4e\u624b\u4e0b\u7559\u60c5\uff0c\u559d \u9053\uff1a\u201c\u5f03\u6697\u6295\u660e\uff0c\u9976\u4f60\u4e0d\u6b7b\uff01\u201d\u5510\u6653\u6f9c\u9a82\u9053\uff1a\u201c\u53cd\u8d3c\uff0c\u5403\u6211\u4e00\u5251\u3002\u201d\u6e38\u9f99\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u4e00\u62db \u201c\u98de\u7011\u6d41\u6cc9\u201d\uff0c\u5251\u82b1\u5982\u6d6a\uff0c\u5343\u70b9\u4e07\u70b9\uff0c\u76f4\u6d12\u4e0b\u6765\u3002\u90a3\u8499\u9762\u4eba\u597d\u50cf\u751a\u4e3a\u6fc0\u6012\uff0c\u957f\u5251\u4e00\u6296\uff0c\u7adf\u5728 \u6e38\u9f99\u5251\u7684\u5b9d\u5149\u7b3c\u7f69\u4e4b\u4e2d\u76f4\u523a\u8fc7\u6765\uff01 \u3000\u3000\u5510\u6653\u6f9c\u4e4d\u9022\u5f3a\u654c\uff0c\u6296\u64de\u7cbe\u795e\uff0c\u628a\u5929\u5c71\u5251\u6cd5\u7684\u7cbe\u5999\u62db\u6570\u5c3d\u91cf\u65bd\u5c55\u51fa\u6765\uff0c\u62db\u91cc\u5957\u62db\uff0c\u5f0f\u4e2d\u6709 \u5f0f\uff0c\u4f3c\u865a\u4f3c\u5b9e\uff0c\u53d8\u5316\u65e0\u65b9\uff0c\u90a3\u8499\u9762\u6c49\u5b50\u5251\u6cd5\u8fdc\u4e0d\u5982\u4ed6\u7cbe\u5999\uff0c\u4f46\u529f\u529b\u6781\u9ad8\uff0c\u53ea\u7528\u74e2\u3001\u7ede\u3001\u51fb\u3001 \u523a\u51e0\u79cd\u624b\u6cd5\uff0c\u4fbf\u628a\u5510\u6653\u6f9c\u7684\u653b\u52bf\uff0c\u4e00\u4e00\u6d88\u89e3\uff0c\u6253\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5510\u6653\u6f9c\u628a\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u516d\u5341\u4e8c \u8def\u8ffd\u98ce\u5251\u5168\u90e8\u4f7f\u5b8c\uff0c\u5140\u81ea\u5948\u4ed6\u4e0d\u5f97\uff0c\u6b63\u60f3\u8f6c\u4e3a\u5e26\u653b\u5e26\u5b88\u7684\u987b\u5f25\u5251\u6cd5\uff0c\u90a3\u4eba\u957f\u5251\u4e00\u6307\uff0c\u95ea\u7535\u822c \u7684\u642d\u5728\u6e38\u9f99\u5251\u4e0a\uff0c\u53cd\u624b\u4e00\u7ede\uff0c\u5510\u6653\u6f9c\u7684\u5251\u4e0d\u7531\u81ea\u5df1\u8ddf\u7740\u4ed6\u8f6c\uff0c\u8f6c\u4e86\u4e24\u8f6c\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u8131\u624b\u98de \u53bb\uff01 \u3000\u3000\u90a3\u8499\u9762\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u5c31\u5728\u6b64\u9645\uff0c\u6697\u5ba4\u91cc\u7a81\u7136\u5927\u653e\u5149\u660e\uff0c\u53ea\u89c1\u5341\u56db\u7687\u5b50\u7acb\u5728\u623f\u4e2d\uff0c\u8138\u6709\u7b11 \u5bb9\uff0c\u62db\u624b\u8bf4\u9053\uff1a\u201c\u4f60\u679c\u7136\u5fe0\u5fc3\u4e8e\u6211\u3002\u8fc7\u6765\u5427\uff01\u201d\u8737\u7f29\u5c4b\u7528\u7684\u201c\u6b7b\u56da\u201d\u4e5f\u4e00\u8dc3\u800c\u8d77\uff0c\u8eab\u4e0a\u67b7\u9501 \u4e0d\u77e5\u662f\u4ec0\u4e48\u65f6\u5019\u5168\u89e3\u5f00\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u62b9\u4e86\u4e00\u989d\u51b7\u6c57\uff0c\u5fc3\u9053\uff1a\u201c\u597d\u9669\uff01\u201d\u672c\u6765\u5510\u6653\u6f9c\u521d\u65f6\u5e76\u672a\u6599\u5230\u5141\u5824\u4f1a\u7528\u8fd9\u79cd\u9634\u9669\u7684 \u65b9\u6cd5\u8bd5\u4ed6\uff0c\u51e0\u4e4e\u4e2d\u4e86\u5708\u5957\uff0c\u60f3\u628a\u90a3\u201c\u6b7b\u56da\u201d\u653e\u8d70\u3002\u5e78\u5f97\u4ed6\u8fd8\u591f\u673a\u7075\uff0c\u5c31\u5728\u60f3\u653e\u201c\u72af\u4eba\u201d\u4e4b \u65f6\uff0c\u731b\u7136\u770b\u51fa\u7834\u7efd\uff0c\u4ee5\u540e\u5f85\u5230\u8499\u9762\u4eba\u4e00\u6765\uff0c\u7834\u7efd\u9732\u5f97\u66f4\u591a\uff0c\u5510\u6653\u6f9c\u4fbf\u4e5f\u5c06\u8ba1\u5c31\u8ba1\uff0c\u7d22\u6027\u7528\u51fa \u5168\u529b\u4e0e\u4ed6\u5468\u65cb\uff0c\u663e\u51fa\u81ea\u5df1\u5bf9\u5141\u5824\u7684\u5fe0\u5fc3\u803f\u803f\u4e86\u3002 \u3000\u3000\u4f60\u731c\u5510\u6653\u6f9c\u770b\u51fa\u7684\u6709\u90a3\u4e9b\u7834\u7efd\uff1f\u7b2c\u4e00\uff1a\u90a3\u72af\u4eba\u65e2\u662f\u6781\u4e3a\u91cd\u8981\u7684\u6b7b\u56da\uff0c\u5c31\u8be5\u88ab\u6253\u5f97\u91cd\u4f24\u5230 \u4e0d\u80fd\u52a8\u5f39\uff0c\u6216\u8005\u662f\u88ab\u5c01\u4e86\u7a74\u9053\uff0c\u6216\u8005\u662f\u6709\u9ad8\u624b\u5728\u65c1\u76d1\u5b88\u3002\u4f46\u8fd9\u4e09\u6837\u90fd\u6ca1\u6709\u3002\u72af\u4eba\u53ea\u662f\u5e26\u4e86\u666e \u901a\u7684\u67b7\u9501\uff0c\u5185\u529f\u9ad8\u5f3a\u7684\u4eba\uff0c\u5927\u53ef\u6323\u8131\u3002\u7b2c\u4e8c\uff0c\u72af\u4eba\u6545\u610f\u70ab\u8000\u4ed6\u8eab\u4efd\u7684\u91cd\u8981\uff0c\u5728\u201c\u523d\u5b50\u624b\u201d\u9762 \u524d\u8868\u9732\u51fa\u4ed6\u662f\u201c\u897f\u5317\u4e49\u519b\u9996\u9886\u201d\uff0c\u5927\u4e3a\u53ef\u7591\u3002\u7b2c\u4e09\uff0c\u82e5\u7136\u72af\u4eba\u6240\u8bf4\u7684\u662f\u771f\uff0c\u90a3\u4e48\u5510\u6653\u6f9c\u4ee5\u4e00 \u4e2a\u65b0\u5165\u7687\u5e9c\u4e4b\u4eba\uff0c\u5141\u5824\u90a3\u80fd\u653e\u5fc3\u53eb\u4ed6\u72ec\u81ea\u529e\u7406\u6b64\u4e8b\u3002\u7b2c\u56db\uff0c\u90a3\u8499\u9762\u523a\u5ba2\u6765\u540e\uff0c\u4e0d\u5148\u6551\u53cb\uff0c\u5374 \u548c\u4ed6\u7f20\u6597\uff0c\u592a\u4e0d\u8fd1\u60c5\u7406\uff0c\u82e5\u771f\u7684\u662f\u6c5f\u6e56\u4e0a\u7684\u4fa0\u4e49\u9053\uff0c\u6240\u6551\u7684\u53c8\u662f\u8fd9\u6837\u91cd\u8981\u7684\u4eba\u7269\uff0c\u65ad\u65e0\u629b\u5f00 \u6240\u6551\u4e4b\u4eba\uff0c\u5374\u5148\u529d\u654c\u6295\u964d\u7684\u3002\u7b2c\u4e94\uff0c\u523a\u5ba2\u529d\u5510\u6653\u6f9c\u65f6\uff0c\u53eb\u4ed6\u4e0d\u8981\u505a\u6ee1\u6d32\u201c\u8d1d\u52d2\u201d\u7684\u5974\u624d\uff0c\u82e5 \u662f\u6c49\u65cf\u4fa0\u58eb\uff0c\u79f0\u547c\u4e0a\u4e0d\u5e94\u7528\u6ee1\u6d32\u4eba\u6240\u7528\u7684\u5c0a\u53f7\u3002\u7b2c\u516d\uff0c\u523a\u5ba2\u6765\u4e86\u51e0\u4e4e\u6709\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5510\u6653\u6f9c\u53c8 \u5927\u58f0\u75be\u547c\uff0c\u7687\u5e9c\u91cc\u9ad8\u624b\u751a\u591a\uff0c\u5374\u65e0\u4eba\u76f8\u52a9\u3002\u8fd9\u660e\u660e\u662f\u5141\u5824\u5e03\u4e0b\u7684\u9677\u9631\u3002\u8fd9\u516d\u4e2a\u7834\u7efd\u81ea\u5510\u6653\u6f9c \u8e0f\u5165\u201c\u56da\u623f\u201d\u8d77\u81f3\u5141\u5824\u51fa\u73b0\u6b62\uff0c\u4e00\u4e2a\u4e2a\u663e\u9732\u51fa\u6765\uff0c\u4f46\u867d\u7136\u5982\u6b64\uff0c\u82e5\u975e\u673a\u7075\u5fc3\u7ec6\u7684\u4eba\uff0c\u4e5f\u770b\u4e0d \u51fa\u3002 \u3000\u3000\u5141\u5824\u8eb2\u5728\u590d\u58c1\u4e4b\u4e2d\uff0c\u5bf9\u5510\u6653\u6f9c\u7684\u4e00\u4e3e\u4e00\u52a8\uff0c\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002\u539f\u6765\u90a3\u201c\u56da\u623f\u201d\u7adf\u662f\u7687\u5e9c\u4e2d \u7684\u673a\u5bc6\u5730\u65b9\uff0c\u6709\u5730\u9053\u76f4\u901a\u5916\u9762\uff0c\u5141\u5824\u5c31\u662f\u4ece\u5730\u9053\u4e2d\u6765\u7684\u3002 \u3000\u3000\u5510\u6653\u6f9c\u9aa4\u89c1\u5141\u5824\u73b0\u8eab\uff0c\u6545\u4f5c\u60ca\u60f6\uff0c\u4e00\u4e2a\u7bad\u6b65\uff0c\u906e\u5728\u5141\u5824\u4e0e\u90a3\u8499\u9762\u4eba\u4e4b\u95f4\uff0c\u5141\u5824\u9053\uff1a\u201c\u4ed6 \u4e0d\u662f\u523a\u5ba2\uff0c\u8f66\u5c06\u519b\u628a\u9762\u5177\u8131\u4e0b\u6765\u4f60\u4eec\u4e24\u4eba\u89c1\u89c1\u3002\u201d\u90a3\u8499\u9762\u4eba\u5e94\u58f0\u628a\u9762\u5177\u8131\u4e0b\uff0c\u539f\u6765\u662f\u5141\u5824\u519b \u4e2d\u7684\u7b2c\u4e00\u628a\u5251\u672f\u597d\u624b\u8f66\u8f9f\u90aa\u3002\u8fd9\u8f66\u8f9f\u90aa\u4e43\u662f\u65d7\u4eba\uff0c\u5251\u672f\u6df1\u5f97\u957f\u767d\u5c71\u98ce\u96f7\u5251\u6cd5\u4e4b\u5999\uff0c\u548c\u8fd1\u536b\u519b \u7684\u7ed5\u9886\u65b9\u4eca\u660e\u5e76\u79f0\u519b\u4e2d\u4e8c\u5b9d\u3002\u800c\u8f66\u8f9f\u90aa\u56e0\u662f\u65d7\u4eba\uff0c\u5c24\u5f97\u5141\u5824\u5ba0\u7231\u3002 \u3000\u3000\u5510\u6653\u6f9c\u9053\u58f0\u201c\u5f97\u7f6a\u201d\uff0c\u53c8\u9053\uff1a\u201c\u597d\u5728\u8f66\u5927\u4eba\u53ea\u662f\u5b58\u5fc3\u76f8\u8bd5\uff0c\u82e5\u7136\u771f\u662f\u523a\u5ba2\uff0c\u6211\u6027\u547d\u65e9\u5df2 \u5b8c\u4e86\u3002\u6211\u5b66\u827a\u672a\u7cbe\uff0c\u5b9e\u5728\u60f6\u6050\u3002\u201d\u8f66\u8f9f\u90aa\u88ab\u4ed6\u4e00\u6367\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8bba\u5251\u6cd5\u4f60\u6bd4\u6211\u9ad8\u660e\u5f97\u591a\uff0c \u518d\u8fc7\u51e0\u5e74\uff0c\u5f85\u4f60\u7684\u529f\u529b\u6e10\u589e\uff0c\u6211\u5c31\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u5141\u5824\u751a\u662f\u9ad8\u5174\uff0c\u5bf9\u5510\u6653\u6f9c\u9053\uff1a\u201c\u628a\u90a3\u836f\u74f6\u62ff\u6765\u3002\u201d\u5510\u6653\u6f9c\u4ece\u6000\u4e2d\u638f\u51fa\uff0c\u5e78\u597d\u6ca1\u6709\u9707\u88c2\uff0c \u5141\u5824\u62d4\u5f00\u74f6\u585e\uff0c\u9aa8\u561f\u561f\u7684\u559d\u4e86\u4e00\u5927\u53e3\uff0c\u9012\u7ed9\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u4eec\u7d2f\u5f97\u4e4f\u4e86\uff0c\u5404\u81ea\u559d\u4e00\u53e3\u5427\u3002\u201d \u5510\u6653\u6f9c\u4e00\u559d\uff0c\u53ea\u89c9\u5f02\u9999\u900f\u9f3b\uff0c\u539f\u6765\u7adf\u662f\u7edd\u597d\u7684\u7f8e\u9152\u3002\u8f66\u8f9f\u90aa\u534a\u5c48\u7740\u819d\uff0c\u8ddf\u7740\u63a5\u8fc7\u9152\u559d\uff0c\u63a5\u7740 \u90a3\u4f2a\u88c5\u201c\u6b7b\u56da\u201d\u7684\u536b\u58eb\u4e5f\u559d\u4e86\u3002\u539f\u6765\u8fd9\u6837\u8d50\u9152\uff0c\u7531\u7edf\u5e05\u559d\u8d77\uff0c\u6bcf\u4eba\u8f6e\u559d\u4e00\u53e3\uff0c\u4e43\u662f\u6ee1\u6d32\u519b\u4e2d \u7684\u201c\u8363\u5178\u201d\uff0c\u53ea\u6709\u6709\u529f\u7684\u5c06\u58eb\u624d\u80fd\u5f97\u5230\u7edf\u5e05\u5982\u6b64\u656c\u9152\u3002 \u3000\u3000\u5141\u5824\u559d\u4e86\u9152\u540e\uff0c\u9762\u5b54\u5ffd\u53c8\u4e00\u677f\uff0c\u5bf9\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u8eab\u6000\u7edd\u6280\uff0c\u4f55\u6545\u8981\u6bdb\u9042\u81ea\u8350\uff0c\u4ee5\u524d\u5728 \u4ec0\u4e48\u5730\u65b9\u529e\u4e8b\u5440\uff1f\u201d\u5510\u6653\u6f9c\u80f8\u6709\u6210\u7af9\uff0c\u773c\u775b\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u8d1d\u52d2\u8bf7\u6055\u5192\u6627\uff0c\u5c0f\u4eba\u6709\u8a00\u7980 \u544a\u3002\u201d\u5141\u5824\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u6211\u7684\u5fc3\u8179\uff0c\u4f60\u4f46\u8bf4\u4e0d\u59a8\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u4ece\u8170\u5e26\u4e0a\u89e3\u4e0b\u5eb7\u7199\u7ed9\u4ed6\u7684\u90a3\u5757\u6c49\u7389\uff0c\u9012\u7ed9\u5141\u5824\u9053\uff1a\u201c\u8d1d\u52d2\u60f3\u5fc5\u89c1\u8fc7\u3002\u201d\u5141\u5824\u63a5 \u7740\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u6c49\u7389\u660e\u660e\u662f\u7236\u7687\u4f69\u7269\uff0c\u600e\u7684\u5374\u5230\u4e86\u6b64\u4eba\u624b\u91cc\u3002\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5c0f\u4eba\u539f\u662f\u7687\u5e1d \u7684\u8d34\u8eab\u4f8d\u536b\uff0c\u4e3a\u4e86\u7edd\u5bc6\u4e4b\u4e8b\uff0c\u6240\u4ee5\u624d\u8fdb\u7687\u5e9c\u3002\u201d\u5141\u5824\u54e6\u4e86\u4e00\u58f0\uff0c\u6697\u9053\uff1a\u539f\u6765\u679c\u662f\u5927\u6709\u6765\u5934\u3002 \u53cc\u773c\u76ef\u7740\u5510\u6653\u6f9c\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u8fdb\u5bab\u7684\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u662f\u53bb\u5e74\u624d\u8ff8\u5bab\u7684\u3002\u201d\u5141\u5824\u9053\uff1a \u201c\u539f\u6765\u5982\u6b64\uff0c\u53bb\u5e74\u6211\u4e00\u76f4\u5728\u9752\u6d77\uff0c\u602a\u4e0d\u5f97\u672a\u89c1\u8fc7\u3002\u4f60\u6709\u4ec0\u4e48\u673a\u5bc6\u7684\u4e8b\u8981\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u7687\u4e0a\u65e9\u9009\u4e2d\u4e86\u8d1d\u52d2\u7ee7\u4f4d\uff0c\u8d1d\u52d2\u53ef\u77e5\u9053\u4e48\uff1f\u201d\u5141\u5824\u867d\u77e5\u7236\u7687\u6700\u7231\u81ea\u5df1\uff0c\u4f46\u5bf9 \u4e8e\u7ee7\u627f\u5927\u5b9d\u4e4b\u4e8b\uff0c\u56e0\u4e0a\u6709\u5341\u4e09\u4f4d\u963f\u54e5\uff0c\u4e0d\u6562\u8fc7\u4efd\u5e0c\u671b\uff0c\u6240\u4ee5\u4e4d\u95fb\u6b64\u8baf\uff0c\u4e0d\u7981\u53c8\u559c\u53c8\u60ca\u3002\u5510\u6653 \u6f9c\u7eed\u9053\uff1a\u201c\u56db\u7687\u7237\u8c0b\u4f4d\u6700\u6025\uff0c\u8d1d\u52d2\u5f53\u7136\u4e5f\u662f\u77e5\u9053\u7684\u4e86\uff01\u201d\u5141\u5824\u53cc\u773c\u4e00\u7ffb\uff0c\u5510\u6653\u6f9c\u6025\u9053\uff1a\u201c\u5974 \u624d\u4e0d\u6562\u79bb\u95f4\u8d1d\u52d2\u9aa8\u8089\u4e4b\u4eb2\uff0c\u4f46\u2014\u2014\u201d\u5141\u5824\u622a\u7740\u9053\uff1a\u201c\u4f46\u4e8b\u5b9e\u786e\u662f\u5982\u6b64\uff0c\u662f\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u8dea\u4e0b \u53e9\u5934\uff0c\u5141\u5824\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u65e9\u77e5\u5141\u796f\u8fd9\u53ae\u5fc3\u6000\u4e0d\u8f68\uff01\u201d\u5510\u6653\u6f9c\u62ac\u5934\u8bf4\u9053\uff1a\u201c\u8d1d\u52d2\u82e5\u4e0d\u65e9\u4e3a\u4e4b \u8c0b\uff0c\u53ea\u6050\u716e\u719f\u4e86\u7684\u9e2d\u5b50\u8fd8\u4f1a\u98de\u8d70\uff01\u201d \u3000\u3000\u5141\u5824\u53cc\u773c\u4e00\u7ffb\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u600e\u4e48\u8bf4\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u7687\u4e0a\u517b\u75c5\u81f3\u4eca\uff0c\u5df2\u6709\u534a\u6708\uff1b\u8d1d \u52d2\u56de\u6765\u4e5f\u5c06\u5341\u5929\u4e86\u3002\u4e3a\u4f55\u7687\u4e0a\u603b\u4e0d\u89c1\u5ba3\u53ec\u8d1d\u52d2\uff1f\u201d\u5141\u5824\u62cd\u6848\u9053\uff1a\u201c\u96be\u9053\u6709\u5978\u4eba\u4ece\u4e2d\u6363\u9b3c\uff1f\u201d \u5510\u6653\u6f9c\u9053\uff1a\u201c\u56fd\u8205\u9686\u79d1\u591a\u3001\u5c06\u519b\u9102\u5c14\u6cf0\u3001\u5927\u5b66\u58eb\u5f20\u5ef7\u7389\uff0c\u8fd9\u4e09\u4eba\u90fd\u662f\u56db\u8d1d\u52d2\u7684\u4e00\u515a\u3002\u201d\u5141\u5824 \u9053\uff1a\u201c\u6211\u4e5f\u542c\u8bf4\u5982\u4eca\u5728\u7236\u7687\u8ddf\u524d\u7684\u9664\u4e86\u51e0\u4f4d\u5fa1\u533b\u548c\u51e0\u4e2a\u4eb2\u8fd1\u7684\u5185\u76d1\u5bab\u5973\u4e4b\u5916\uff0c\u5c31\u662f\u8fd9\u4e09\u4e2a\u4eba \u4e86\u3002\u8fd9\u4e8b\u679c\u662f\u53ef\u8651\u3002\u4f9d\u4f60\u8bf4\u600e\u6837\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u603b\u5f97\u8bbe\u6cd5\u89c1\u7740\u7687\u4e0a\u3002\u201d\u5141\u5824\u9053\uff1a\u201c\u672a\u5949\u8bcf \u4e66\uff0c\u5982\u4f55\u53ef\u89c1\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5fc5\u8981\u4e4b\u65f6\uff0c\u4fbf\u95ef\u8fdb\u53bb\u3002\u800c\u4e14\u8d1d\u52d2\u624b\u63e1\u5927\u519b\uff0c\u82e5\u7136\u53ca\u65e9\u5e03\u7f6e\u2014 \u2014\u201d\u5141\u5824\u9762\u8272\u500f\u53d8\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u4e00\u7247\u5fe0\u5fc3\u4e86\u3002\u4e0d\u8981\u4e71\u8bf4\uff0c\u4f60\u9000\u4e0b\u53bb\u5427\u3002\u201d \u3000\u3000\u5176\u5b9e\u5141\u5824\u65e9\u5df2\u6709\u4e86\u5e03\u7f6e\uff0c\u4ed6\u4e5f\u77e5\u9053\u5141\u796f\u624b\u4e0b\u6709\u672c\u9886\u7684\u6b66\u58eb\u6700\u591a\uff0c\u60df\u6050\u53d7\u4e86\u6697\u7b97\uff0c\u6240\u4ee5\u628a \u5927\u519b\u5c6f\u5728\u57ce\u5916\uff0c\u7531\u5fc3\u8179\u5927\u5c06\u5085\u514b\u56fe\u638c\u63e1\uff0c\u5631\u5490\u4ed6\u82e5\u81ea\u5df1\u4e07\u4e00\u53d7\u4e86\u6263\u62bc\u6216\u5176\u4ed6\u610f\u5916\uff0c\u5c31\u52a8\u7528\u5927 \u519b\uff0c\u5bf9\u4ed8\u5141\u796f\u3002 \u3000\u3000\u4e0d\u8bf4\u5141\u5824\u8fd9\u8fb9\u7684\u5e03\u7f6e\u3002\u4e14\u8bf4\u5eb7\u7199\u7687\u5e1d\u8eab\u4f53\u4e00\u5411\u58ee\u5065\uff0c\u5728\u4f4d\u5df2\u516d\u5341\u4e00\u5e74\uff0c\u5c31\u5728\u8fd9\u5e74\u5341\u6708\uff0c \u4ed6\u8fd8\u201c\u9a7e\u5e78\u201d\u5357\u82d1\uff0c\u4e3e\u884c\u56f4\u730e\uff0c\u8dd1\u9a6c\u5c04\u9e7f\uff0c\u9887\u89c1\u52c7\u6b66\u3002\u4e0d\u6599\u56f4\u730e\u4e4b\u540e\uff0c\u5ffd\u7136\u5bb3\u8d77\u75c5\u6765\uff0c\u5927\u51e1 \u8eab\u4f53\u58ee\u5065\uff0c\u5e73\u7d20\u5c11\u75c5\u7684\u8001\u4eba\uff0c\u4e00\u65e6\u5bb3\u8d77\u75c5\u6765\uff0c\u5c31\u5f88\u96be\u7597\u6cbb\uff0c\u6240\u4ee5\u75c5\u4e86\u4e0d\u5230\u51e0\u5929\uff0c\u4fbf\u5341\u5206\u6c89 \u91cd\u3002\u5eb7\u7199\u79fb\u9a7e\u5230\u7545\u6625\u56ed\u7684\u79bb\u5bab\u517b\u75c5\uff0c\u521d\u65f6\u8fd8\u80fd\u6323\u624e\u6599\u7406\u56fd\u4e8b\uff0c\u540e\u6765\u8d8a\u770b\u8d8a\u4e0d\u884c\u4e86\uff0c\u8fd9\u624d\u53eb\u56fd \u8205\u9686\u79d1\u591a\u548c\u5927\u5b66\u58eb\u5f20\u5ef7\u7389\u6444\u7406\u671d\u653f\u3002 \u3000\u3000\u5eb7\u7199\u662f\u4e2a\u6781\u5176\u597d\u5f3a\u7684\u4eba\uff0c\u4e00\u751f\u5357\u5f81\u5317\u8ba8\uff0c\u6cbb\u6cb3\u4fee\u4e66\uff0c\u653f\u6559\u6b66\u529f\uff0c\u90fd\u9887\u6709\u5efa\u6811\uff0c\u4e0d\u60f3\u5230\u4e86 \u665a\u5e74\uff0c\u5341\u51e0\u4e2a\u513f\u5b50\u660e\u4e89\u6697\u6597\uff0c\u987a\u8f67\u6392\u6324\uff0c\u5eb7\u7199\u5374\u662f\u65e0\u53ef\u5948\u4f55\u3002\u6240\u4ee5\u4e00\u75c5\u4e4b\u540e\uff0c\u5341\u5206\u70e6\u607c\uff0c\u7adf \u4e0d\u613f\u89c1\u5bb6\u4eba\u9aa8\u8089\uff0c\u56e0\u6b64\u4e0d\u4ec5\u5141\u5824\uff0c\u5c31\u662f\u5141\u796f\u5343\u65b9\u767e\u8ba1\u6c42\u89c1\uff0c\u4e5f\u53ea\u80fd\u5728\u5916\u9762\u9065\u53e9\u201c\u5723\u5b89\u201d\uff0c\u5141 \u5824\u4e0e\u5510\u6653\u6f9c\u4e4b\u731c\u7591\u201c\u5978\u4eba\u6363\u9b3c\u201d\uff0c\u5176\u5b9e\u4e5f\u53ea\u731c\u4e2d\u4e00\u534a\u3002\u4e0d\u8fc7\u5141\u796f\u9760\u4e86\u9686\u79d1\u591a\u3001\u9102\u5c14\u6cf0\u3001\u5f20\u5ef7 \u7389\u7b49\u4eba\u505a\u8033\u76ee\uff0c\u53c8\u8d3f\u8d42\u4e86\u5eb7\u7199\u7684\u8fd1\u8eab\u5bab\u5973\u4e0e\u592a\u76d1\uff0c\u6240\u4ee5\u5bf9\u5eb7\u7199\u7684\u4e00\u4e3e\u4e00\u52a8\uff0c\u4e00\u8a00\u4e00\u8bed\uff0c\u90fd\u4e86 \u5982\u6307\u638c\u3002 \u3000\u3000\u8fd9\u65e5\u2014\u2014\u5eb7\u7199\u516d\u5341\u4e00\u5e74\u4e00\u6708\u5341\u4e09\u65e5\u2014\u2014\u5eb7\u7199\u5df2\u75c5\u5f97\u8ff7\u8ff7\u7cca\u7cca\uff0c\u8fdb\u4e86\u4e00\u7897\u53c2\u6c64\uff0c\u795e\u667a\u7565\u89c1 \u6e05\u9192\uff0c\u731b\u7136\u60f3\u516d\u5341\u4f59\u5e74\u4e4b\u4e8b\uff0c\u53ea\u89c9\u5c0a\u8363\u4e4b\u6781\uff0c\u4ea6\u5982\u8fc7\u773c\u4e91\u70df\uff0c\u513f\u5b50\u867d\u591a\uff0c\u4f46\u4ed6\u4eec\u6240\u4e89\u7684\u4e0d\u8fc7 \u662f\u4e00\u4e2a\u5b9d\u5ea7\uff0c\u5e76\u65e0\u771f\u631a\u7684\u7236\u5b50\u4e4b\u60c5\u3002\u5982\u6b64\u601d\u91cf\uff0c\u53ea\u89c9\u5f97\u201c\u5bc2\u5bde\u201d\u6781\u4e86\uff0c\u4e0d\u7531\u5f97\u60f3\u8d77\u513f\u65f6\u7684\u597d \u53cb\u7eb3\u5170\u5bb9\u82e5\u6765\uff0c\u53ef\u60dc\u7eb3\u5170\u77ed\u547d\uff0c\u7a7a\u8d1f\u4e00\u4ee3\u8bcd\u540d\uff0c\u53ea\u4e09\u5341\u4e00\u5c81\u5c31\u6b7b\u4e86\uff0c\u8981\u4e0d\u7136\u665a\u5e74\u6700\u5c11\u8fd8\u53ef\u6709 \u4e00\u4eba\u966a\u4f34\u8bf4\u8bdd\u3002 \u3000\u3000\u5185\u76d1\u89c1\u7687\u5e1d\u6b20\u8eab\u6b32\u8d77\uff0c\u8d70\u6765\u95ee\u5019\u3002\u5eb7\u7199\u9053\uff1a\u201c\u4e66\u67b6\u4e2d\u95f4\u90a3\u683c\uff0c\u6709\u4e00\u628a\u6247\u5b50\uff0c\u4f60\u66ff\u6715\u628a\u90a3 \u6247\u5b50\u62ff\u6765\u3002\u201d\u5185\u76d1\u751a\u4e3a\u8be7\u5f02\uff0c\u8fd9\u65f6\u5df2\u662f\u9686\u51ac\u5929\u6c14\uff0c\u8981\u6247\u5b50\u505a\u4ec0\u4e48\uff1f\u4f46\u662f\u5723\u4e0a\u5429\u5490\uff0c\u4e0d\u6562\u4e0d \u4f9d\u3002\u5eb7\u7199\u63a5\u8fc7\u6247\u5b50\uff0c\u4e00\u58f0\u957f\u53f9\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6\u60f3\u8d77\u4e86\u56db\u5341\u4f59\u5e74\u4e4b\u524d\uff0c\u548c\u7eb3\u5170\u5bb9\u82e5\u8fdc\u5f81\u585e\u5916\u7684\u4e8b\uff0c\u90a3\u65f6\u662f\u5728\u5410\u9c81\u8543\u9644\u8fd1\uff0c \u767d\u5929\u708e\u70ed\uff0c\u665a\u4e0a\u82e6\u5bd2\uff0c\u5927\u6f20\u98ce\u7802\uff0c\u8352\u51c9\u4e00\u7247\uff0c\u81ea\u5df1\u66fe\u4e0e\u7eb3\u5170\u6307\u70b9\u5c71\u6cb3\uff0c\u8bdd\u5929\u4e0b\u5174\u4ea1\u4e8b\u8ff9\u3002\u7eb3 \u5170\u66fe\u529d\u81ea\u5df1\u4e0d\u8981\u5f92\u6043\u6b66\u529f\uff0c\u81ea\u5df1\u8fd8\u7b11\u4ed6\u662f\u4e66\u751f\u4e4b\u89c1\uff0c\u5982\u4eca\u770b\u6765\uff0c\u897f\u5317\u8fde\u5e74\u5f81\u6218\uff0c\u5404\u65cf\u59cb\u7ec8\u4e0d \u670d\uff0c\u7eb3\u5170\u7684\u8bdd\uff0c\u4e5f\u672a\u5c1d\u6ca1\u6709\u9053\u7406\u3002\u90a3\u65f6\u7eb3\u5170\u66fe\u66ff\u4ed6\u5199\u4e86\u4e00\u628a\u6247\u5b50\uff0c\u81ea\u5df1\u4e0d\u6b22\u559c\u90a3\u4e9b\u8bcd\u53e5\uff0c\u6240 \u4ee5\u4e00\u76f4\u6401\u5728\u4e66\u67b6\u4e0a\u3002 \u3000\u3000\u5eb7\u7199\u5728\u601d\u6f6e\u6c79\u6d8c\u4e2d\u6253\u5f00\u4e86\u90a3\u628a\u6247\u5b50\uff0c\u8bfb\u4e0a\u9762\u7684\u5b57\u9053\uff1a\u201c\u4eca\u53e4\u6cb3\u5c71\u65e0\u5b9a\u636e\uff0c\u753b\u89d2\u58f0\u4e2d\uff0c\u7267 \u9a6c\u9891\u6765\u53bb\u3002\u6ee1\u76ee\u8352\u51c9\u8c01\u53ef\u8bed\uff1f\u897f\u98ce\u5439\u8001\u4e39\u67ab\u6811\u3002\u4ece\u524d\u5e7d\u6028\u5e94\u65e0\u6570\uff0c\u94c1\u9a6c\u91d1\u6208\uff0c\u9752\u5bb6\u9ec4\u660f\u8def\u3002 \u4e00\u5f80\u60c5\u6df1\u6df1\u51e0\u8bb8\uff1f\u6df1\u5c71\u5915\u7167\u6df1\u79cb\u96e8\u3002\u201d\uff08\u300a\u8776\u604b\u82b1\u300b\u8c03\u548f\u201c\u51fa\u585e\u201d\u3002\uff09 \u3000\u3000\u5eb7\u7199\u7ec6\u7ec6\u5480\u56bc\u201c\u4eca\u53e4\u6cb3\u5c71\u65e0\u5b9a\u636e\u201d\u4e0e\u201c\u6ee1\u76ee\u8352\u51c9\u8c01\u53ef\u8bed\u201d\u7b49\u53e5\uff0c\u53ea\u89c9\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u53ef\u65ad \u7edd\u3002\u9686\u79d1\u591a\u548c\u9102\u5c14\u6cf0\u968f\u4f8d\u5728\u4fa7\uff0c\u89c1\u7687\u4e0a\u770b\u51fa\u4e86\u795e\uff0c\u54ea\u91cc\u60f3\u5f97\u5230\uff1a\u8fd9\u4f4d\u5eb7\u7199\u7687\u5e1d\uff0c\u4eab\u4f4d\u5982\u6b64\u4e4b \u4e45\uff0c\u4eab\u5e74\u5982\u6b64\u4e4b\u9ad8\uff0c\u5bcc\u8d35\u8363\u534e\uff0c\u5230\u4e86\u6781\u70b9\uff0c\u4e34\u6b7b\u4e4b\u524d\uff0c\u5fc3\u60c5\u5374\u662f\u8fd9\u6837\u7684\u5bc2\u83ab\u51c4\u51c9\u3002 \u3000\u3000\u9686\u79d1\u591a\u8f7b\u8f7b\u8d70\u8fd1\u5fa1\u69bb\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u7cbe\u795e\u521d\u590d\uff0c\u4e0d\u53ef\u52b3\u5fc3\u3002\u201d\u5eb7\u7199\u51c4\u7136\u4e00\u7b11\uff0c\u6325\u624b\u8bf4\u9053\uff1a \u201c\u5feb\u4f20\u5341\u56db\u8d1d\u52d2\u5141\u5824\u6765\uff01\u201d\u4ed6\u81ea\u77e5\u4e0d\u8d77\uff0c\u8fd9\u65f6\u5df2\u5728\u51c6\u5907\u5429\u5490\u540e\u4e8b\u4e86\u3002 \u3000\u3000\u5404\u7687\u5b50\u8c0b\u4f4d\u5fc3\u6025\uff0c\u8fd9\u4e24\u5929\u542c\u8bf4\u5eb7\u7199\u75c5\u91cd\uff0c\u90fd\u662f\u5927\u6e05\u65e9\u8d76\u5230\u7545\u6625\u56ed\u5916\uff0c\u76f4\u5230\u6df1\u591c\u624d\u56de\u53bb\u7a0d \u6b47\uff0c\u7b2c\u4e8c\u65e5\u7edd\u65e9\u53c8\u6765\uff0c\u6bcf\u4eba\u90fd\u62b1\u7740\u201c\u9e3f\u9e3d\u5c06\u81f3\u201d\u7684\u5fc3\u60c5\uff0c\u5192\u7740\u9686\u51ac\u7684\u5bd2\u98ce\uff0c\u5728\u56ed\u5b50\u5916\u7b49\u5019\uff0c \u5144\u5f1f\u4eec\u89c1\u9762\uff0c\u53ea\u662f\u51b7\u51b7\u62db\u547c\uff0c\u5927\u5bb6\u90fd\u62b1\u7740\u731c\u5fcc\u4e4b\u5fc3\uff0c\u4e92\u4e0d\u4ea4\u8c08\u3002 \u3000\u3000\u8fd9\u65e5\u4f17\u7687\u5b50\u6b63\u7b49\u5f97\u5fc3\u7126\uff0c\u5ffd\u89c1\u9686\u79d1\u591a\u98de\u8dd1\u51fa\u6765\uff0c\u5927\u5bb6\u54c4\u7136\u56f4\u4e0a\u3002\u9686\u79d1\u591a\u5927\u53eb\u9053\uff1a\u201c\u5723\u4e0a \u6709\u65e8\uff0c\u5404\u7687\u5b50\u5230\u56ed\uff0c\u4e0d\u5fc5\u8fdb\u5185\uff0c\u5355\u53ec\u56db\u7687\u5b50\u89c1\u9a7e\uff01\u201d\u5141\u796f\u5927\u559c\uff0c\u4e00\u8dc3\u4e0a\u524d\uff0c\u62c9\u7740\u9686\u79d1\u591a\u98de\u5954 \u8fdb\u56ed\u3002 \u3000\u3000\u4f17\u7687\u5b50\u6115\u7136\u5931\u671b\uff0c\u4e5d\u7687\u5b50\u5141\u642a\u6700\u4e3a\u6a2a\u86ee\uff0c\u9996\u5148\u6518\u81c2\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u7ba1\u4ed6\uff0c\u54b1\u4eec\u90fd\u8fdb\u53bb\uff01\u201d \u4f17\u7687\u5b50\u9f50\u58f0\u54cd\u5e94\uff0c\u5e26\u7740\u968f\u4ece\uff0c\u4e00\u9f13\u62e5\u5165\uff0c\u5b88\u56ed\u7684\u536b\u58eb\u54ea\u6562\u963b\u62e6\u3002\u5510\u6653\u6f9c\u548c\u8f66\u8f9f\u90aa\u662f\u5341\u56db\u7687\u5b50 \u5141\u5824\u7684\u968f\u4ece\uff0c\u8fd9\u65f6\u4e5f\u968f\u4f17\u62e5\u5165\u56ed\u5185\u3002 \u3000\u3000\u5eb7\u7199\u7687\u5e1d\u5ba3\u53ec\u4e86\u5141\u5824\u540e\uff0c\u795e\u667a\u53c8\u6e10\u6a21\u7cca\uff0c\u6726\u6726\u80e7\u80e7\u4e2d\u5ffd\u4f3c\u7f6e\u8eab\u5728\u4e94\u53f0\u5c71\u4e0a\uff0c\u4e00\u4e2a\u6e05\u7626\u7684 \u8001\u548c\u5c1a\u5411\u81ea\u5df1\u77aa\u76ee\u6012\u89c6\uff0c\u6b63\u662f\u7236\u7687\u987a\u6cbb\uff0c\u4e0d\u7981\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u9a87\u53eb\u9053\uff1a\u201c\u7236\u7687\u9976\u6211\uff01\u201d\u9102\u5c14 \u6cf0\u4e0a\u524d\u6447\u4ed6\u9053\uff1a\u201c\u7687\u4e0a\u9192\u6765\uff0c\u5341\u56db\u8d1d\u52d2\u5c31\u6765\uff01\u201d\u5eb7\u7199\u7687\u5e1d\u4e00\u8eab\u51b7\u6c57\uff0c\u8f6c\u4e86\u4e2a\u8eab\uff0c\u7a81\u7136\u95ee\u9053\uff1a \u201c\u8fd9\u91cc\u662f\u4ec0\u4e48\u6240\u5728\uff1f\u201d\u9102\u5c14\u6cf0\u9053\uff1a\u201c\u7545\u6625\u56ed\u5440\uff01\u201d\u5eb7\u7199\u9053\uff1a\u201c\u4f60\u9a97\u6211\uff0c\u8fd9\u91cc\u662f\u4e94\u53f0\u5c71\uff01\u201d\u9102 \u5c14\u6cf0\u6697\u53eb\u4e00\u58f0\u82e6\u4e5f\uff0c\u7687\u4e0a\u5df2\u660f\u8ff7\u81f3\u6b64\uff0c\u56db\u7687\u5b50\u8fd8\u672a\u89c1\u6765\u3002\u5eb7\u7199\u53c8\u8f6c\u4e86\u4e2a\u8eab\uff0c\u5ffd\u7136\u5927\u53eb\u9053\uff1a \u201c\u4f60\u4eec\u51b3\u628a\u90a3\u8001\u548c\u5c1a\u6253\u51fa\u53bb\uff01\u5feb\u5440\uff01\u4e0d\u8981\u8ba9\u4ed6\u8fdb\u6765\uff01\u201d \u3000\u3000\u8fd9\u65f6\u5141\u796f\u548c\u9686\u79d1\u591a\u5df2\u98de\u8dd1\u8fdb\u6765\u3002\u9102\u5c14\u6cf0\u8dea\u7980\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u5341\u56db\u7687\u5b50\u6765\u4e86\uff01\u201d\u5eb7\u7199\u60a0\u60a0\u9192 \u8f6c\uff0c\u5141\u796f\u8dea\u5728\u5e8a\u524d\u3002\u5eb7\u7199\u4f38\u624b\u8fc7\u53bb\u6478\u4ed6\u7684\u8138\uff0c\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u4e0d\u662f\u5141\u5824\uff01\u201d\u5141\u796f \u9053\uff1a\u201c\u81e3\u513f\u5949\u7236\u7687\u4e4b\u8bcf\uff01\u201d\u5eb7\u7199\u5ffd\u7136\u56de\u5149\u53cd\u7167\uff0c\u5927\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u8fd8\u6ca1\u6b7b\uff0c\u4f60\u4eec\u5c31\u4f19\u540c\u9a97 \u6211\uff01\u201d\u62ff\u8d77\u4e00\u4e32\u7389\u5ff5\u73e0\uff0c\u7167\u5141\u796f\u5288\u9762\u63b7\u53bb\uff01\u9686\u79d1\u591a\u5927\u60ca\u5931\u58f0\u3002\u6b64\u65f6\uff0c\u95e8\u5916\u4eba\u58f0\u9f0e\u6cb8\uff0c\u5141\u796f\u54ac \u4e86\u54ac\u7259\uff0c\u7a81\u7136\u6251\u5230\u5e8a\u4e0a\u3002\u5eb7\u7199\u60e8\u53eb\u4e00\u58f0\uff0c\u4e00\u53e3\u6c14\u8f6c\u4e0d\u8fc7\u6765\uff0c\u4fbf\u6b7b\u8fc7\u53bb\u4e86\uff01\u5eb7\u7199\u5728\u4e94\u53f0\u5c71\u4e0a\u8c0b \u6740\u7236\u4eb2\uff08\u8be6\u89c1\u62d9\u8457\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\uff09\uff0c\u800c\u4eca\u4e5f\u6b7b\u5728\u513f\u5b50\u624b\u4e0a\u3002 \u3000\u3000\u4f17\u7687\u5b50\u5e26\u9886\u968f\u4ece\uff0c\u4e00\u62e5\u5165\u5185\uff0c\u5fa1\u623f\u5916\u4e00\u961f\u5fa1\u6797\u519b\u62e6\u7740\u53bb\u8def\uff0c\u539f\u6765\u9686\u79d1\u591a\u4e5f\u987e\u8651\u5230\u4f17\u7687\u5b50 \u4e0d\u542c\u7389\u4ee4\uff0c\u6240\u4ee5\u9884\u5148\u5b89\u6392\u4e0b\u6765\u7684\u3002\u5510\u6653\u6f9c\u63a8\u5f00\u4f17\u4eba\u4e00\u628a\uff0c\u6084\u6084\u9053\uff1a\u201c\u8d1d\u52d2\u5e94\u5f53\u673a\u7acb\u65ad\uff01\u201d\u5141 \u5824\u5927\u53eb\u9053\uff1a\u201c\u6211\u4eec\u95ee\u5019\u7236\u7687\uff0c\u8c01\u6562\u62e6\u963b\uff1f\u201d\u4f17\u7687\u5b50\u8f70\u7136\u5927\u53eb\uff0c\u5fa1\u6797\u519b\u624d\u81ea\u987e\u5931\u8272\uff0c\u5200\u67aa\u7eb7 \u4e3e\uff0c\u5374\u662f\u624b\u98a4\u811a\u9707\uff01 \u3000\u3000\u5c31\u5728\u6b64\u9645\uff0c\u5185\u623f\u91cc\u4f20\u51fa\u4e00\u58f0\u60e8\u53eb\uff0c\u4f17\u7687\u5b50\u4e00\u60ca\uff0c\u4e00\u6761\u4eba\u5f71\uff0c\u9661\u7136\u98de\u4e86\u8d77\u6765\uff0c\u4ece\u524d\u6392\u5fa1\u6797 \u519b\u7684\u5934\u9876\u98de\u63a0\u8fc7\u53bb\uff0c\u4ece\u7a97\u53e3\u4e00\u8dc3\u800c\u5165\u3002 \u3000\u3000\u56db\u7687\u5b50\u5141\u796f\u627c\u6b7b\u7236\u7687\uff0c\u53cc\u624b\u4e00\u677e\uff0c\u4e00\u8de4\u8dcc\u843d\u5e8a\u524d\u3002\u9686\u79d1\u591a\u9053\uff1a\u201c\u606d\u559c\u7687\u4e0a\uff0c\u5927\u4e8b\u5df2 \u4e86\uff01\u201d\u9661\u89c1\u4e00\u6761\u9ed1\u5f71\uff0c\u7a81\u7136\u4ece\u7a97\u53e3\u98de\u5165\uff0c\u9102\u5c14\u591a\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u4e0a\u524d\u62e6\u963b\uff0c\u90a3\u4eba\u95f7\u58f0\u4e0d \u54cd\uff0c\u7a81\u7136\u53d1\u51fa\u4e00\u62f3\uff0c\u5c06\u9102\u5c14\u6cf0\u6253\u8dcc\u5730\u4e0a\u3002\u8dea\u5230\u5fa1\u69bb\u4e4b\u524d\uff0c\u4e3e\u5934\u4e00\u671b\uff0c\u5ffd\u7136\u8dea\u4e0b\u54ed\u9053\uff1a\u201c\u6211\u6765 \u8fdf\u4e86\uff01\u201d \u3000\u3000\u8fd9\u4eba\u6b63\u662f\u5510\u6653\u6f9c\uff0c\u4ed6\u5728\u5eb7\u7199\u751f\u524d\uff0c\u4e0d\u80af\u8ba4\u7236\uff0c\u800c\u4eca\u89c1\u4ed6\u6b7b\u4e86\uff01\u7236\u5b50\u4e4b\u60c5\uff0c\u5230\u5e95\u51fa\u4e8e\u5929\u6027 \u4e0d\u89c9\u8dea\u4e0b\u3002\u5141\u796f\u795e\u667a\u5df2\u590d\uff0c\u6025\u5fd9\u8dc3\u8d77\uff0c\u9a88\u6307\u671d\u5510\u6653\u6f9c\u7684\u201c\u80a9\u4e95\u7a74\u201d\u4e00\u6233\uff0c\u5510\u6653\u6f9c\u767b\u65f6\u5012\u5728\u5730 \u4e0a\uff0c\u53e3\u8fd8\u5f20\u5f00\uff0c\u6cea\u72b9\u6ee1\u9762\u3002\u6309\u8bf4\u6b64\u65f6\u5510\u6653\u6f9c\u6b66\u529f\u5df2\u8f83\u56db\u7687\u5b50\u4e3a\u9ad8\uff0c\u4f46\u8fd9\u4e2a\u65f6\u5019\uff0c\u4ed6\u90a3\u8fd8\u6709\u5fc3 \u9632\u5907\uff1f \u3000\u3000\u9686\u79d1\u591a\u9053\uff1a\u201c\u7687\u4e0a\u4e0d\u8981\u62c5\u5fc3\u3002\u201d\u62c9\u7740\u5141\u796f\u8d70\u51fa\u623f\u5916\uff0c\u2014\u2014\u5141\u796f\u867d\u7136\u672a\u767b\u5927\u5b9d\uff0c\u4f46\u4ed6\u5df2\u6539 \u53e3\u4ee5\u201c\u7687\u4e0a\u201d\u76f8\u79f0\u3002\u5141\u796f\u5b9a\u4e86\u5b9a\u795e\uff0c\u4e3e\u8896\u4e00\u62b9\u53cc\u773c\uff0c\u767b\u65f6\u568e\u9676\u5927\u54ed\u8d77\u6765\uff01 \u3000\u3000\u8fd9\u65f6\u4f17\u7687\u5b50\u6b63\u5728\u55a7\u95f9\uff0c\u9661\u95fb\u54ed\u58f0\uff0c\u535c\u535c\u4e89\u5148\uff0c\u5fa1\u6797\u519b\u4e3e\u8d77\u5200\u67aa\uff0c\u53ea\u662f\u4f5c\u4e2a\u52bf\u5b50\u800c\u5df2\uff0c\u89c1 \u4ed6\u4eec\u786c\u6d8c\u8fdb\u6765\uff0c\u7eb7\u7eb7\u95ea\u5f00\uff0c\u9686\u79d1\u591a\u5927\u53eb\u9053\uff1a\u201c\u7687\u5e1d\u9f99\u9a6d\u4e0a\u5bbe\uff0c\u56fd\u4e0d\u53ef\u4e00\u65e5\u65e0\u541b\uff0c\u6c11\u4e0d\u53ef\u4e00\u65e5 \u65e0\u4e3b\uff0c\u672c\u5927\u81e3\u53d7\u5148\u5e1d\u5bc4\u6258\u4e4b\u91cd\uff0c\u8bf7\u8bf8\u4f4d\u90e1\u738b\u5feb\u5230\u6b63\u5927\u5149\u660e\u6bbf\u53bb\u542c\u672c\u5927\u81e3\u5ba3\u8bfb\u9057\u4e66\uff01\u201d\u5404\u7687\u5b50 \u679c\u7136\u9759\u4e86\u4e0b\u6765\uff0c\u7687\u5e1d\u5df2\u6b7b\uff0c\u8c01\u4e5f\u4e0d\u60f3\u8fdb\u5185\u770b\u671b\uff0c\u4e00\u7a9d\u8702\u7684\u90fd\u8d76\u53bb\u6b63\u5927\u5149\u660e\u6bbf\u5019\u65e8\u3002 \u3000\u3000\u9686\u79d1\u591a\u5c06\u5510\u6653\u6f9c\u4ea4\u7ed9\u5fa1\u6797\u519b\u5148\u5e26\u5165\u5185\u5ef7\u62bc\u5019\uff0c\u5f53\u573a\u95ee\u9053\uff1a\u201c\u6b64\u4eba\u662f\u54ea\u4f4d\u7687\u7237\u7684\u968f\u4ece\uff1f\u201d \u4f17\u7687\u5b50\u90fd\u8d76\u7740\u8fdb\u5bab\uff0c\u8c01\u4e5f\u4e0d\u7406\u3002\u5141\u5824\u5fc3\u4e2d\u607c\u6068\u5510\u6653\u6f9c\u83bd\u649e\uff0c\u751f\u6015\u8bef\u4e86\u5927\u4e8b\uff0c\u66f4\u662f\u4e0d\u6562\u5f00\u8154\u3002 \u5fc3\u60f3\uff1a\u5f85\u6211\u767b\u4e0a\u4e86\u7687\u4f4d\u4e4b\u540e\u518d\u628a\u4ed6\u6740\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u8272\u8fd1\u665a\uff0c\u5348\u95e8\u672c\u5df2\u5173\u95ed\u3002\u4e3a\u4e86\u5ba3\u8bfb\u9057\u8bcf\uff0c\u53ea\u5f97\u6253\u5f00\u3002\u7687\u4eb2\u56fd\u621a\u6587\u6b66\u5927\u81e3\uff0c\u95fb\u8baf\u7eb7 \u7eb7\u8d76\u6765\uff0c\u5bab\u4e2d\u5983\u5ad4\uff0c\u4e5f\u90fd\u5230\u504f\u6bbf\u9759\u542c\u3002\u505c\u4e86\u4e00\u4f1a\uff0c\u90a3\u6ee1\u671d\u6587\u6b66\uff0c\u90fd\u5df2\u5230\u9f50\u3002\u9636\u4e0b\u4e09\u5343\u540d\u5fa1\u6797 \u519b\uff0c\u6392\u5f97\u5bc6\u5bc6\u5c42\u5c42\u3002\u4f17\u7687\u5b50\u90fd\u6324\u5230\u6bbf\u5185\uff0c\u95f9\u5f97\u4e71\u54c4\u54c4\u7684\u3002\u5141\u5824\u7684\u5fc3\u535c\u535c\u7684\u8df3\uff0c\u4f38\u957f\u9888\u5b50\uff0c\u770b \u6bbf\u4e2d\u592e\u60ac\u7740\u7684\u90a3\u5757\u5199\u7740\u201c\u6b63\u5927\u5149\u660e\u201d\u7684\u533e\u989d\u3002\u5c31\u5728\u8fd9\u6781\u5ea6\u7d27\u5f20\u7684\u6c14\u6c1b\u4e4b\u4e2d\uff0c\u5ffd\u7136\u6709\u4eba\u6084\u6084\u7684 \u62c9\u4e86\u4ed6\u4e00\u4e0b\uff0c\u5141\u5824\u5413\u4e86\u4e00\u8df3\uff0c\u53ea\u89c1\u662f\u4ed6\u7684\u5fc3\u8179\uff0c\u8fd1\u536b\u519b\u7684\u7edf\u9886\u65b9\u4eca\u660e\u3002\u65b9\u4eca\u660e\u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0 \u8bf4\u9053\uff1a\u201c\u519b\u4e2d\u6709\u53d8\uff01\u201d\u5141\u5824\u5927\u5403\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u2018\u600e\u4e48\uff1f\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u8bf4\u662f\u5949\u4e86\u5723 \u65e8\uff0c\u5c31\u4e86\u629a\u8fdc\u526f\u5c06\u519b\u4e4b\u804c\uff0c\u6682\u4ee3\u8d1d\u52d2\u5904\u7406\u519b\u961f\uff0c\u4ed6\u7684\u4e00\u4e07\u94c1\u9a91\u519b\uff0c\u4e5f\u5df2\u5728\u6211\u4eec\u7684\u9a7b\u519b\u4e4b\u6240\uff0c \u5b89\u4e86\u8425\u5be8\u3002\u201d\u5141\u5824\u5927\u60d1\u4e0d\u89e3\uff0c\u5e74\u7fb9\u5c27\u4e0d\u8fc7\u662f\u4e00\u4e2a\u63d0\u7763\uff0c\u4ed6\u7684\u519b\u961f\u867d\u9a81\u52c7\u5584\u6218\uff0c\u4f46\u6bd4\u8d77\u81ea\u5df1\u7684 \u4e8c\u5341\u4e07\u5927\u519b\uff0c\u4f55\u5f02\u4ee5\u5375\u51fb\u77f3\uff0c\u4f55\u4ee5\u4f1a\u7ed9\u4ed6\u63a5\u6536\u3002\u6025\u95ee\u9053\uff1a\u201c\u535a\u514b\u56fe\u7684\u5175\u6743\u6ca1\u88ab\u524a\u5427\uff1f\u5404\u8425\u519b \u5b98\u662f\u4e0d\u662f\u8fd8\u6548\u5fe0\u4e8e\u6211\uff1f\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u535a\u514b\u56fe\u8fd8\u5728\u3002\u8fd1\u536b\u519b\u548c\u5176\u4ed6\u5341\u4e8c\u4e2a\u8425\u5c06\u5e74\u7fb9\u5c27\u7684\u519b\u961f \u5305\u56f4\u76d1\u89c6\uff0c\u8bf7\u8d1d\u52d2\u7acb\u5373\u56de\u53bb\uff01\u5141\u5824\u542c\u8bf4\u535a\u514b\u56fe\u65e0\u4e8b\uff0c\u5fc3\u4e2d\u7a0d\u5b89\uff0c\u9053\uff1a\u201c\u53ea\u8981\u5175\u6743\u8fd8\u5728\uff0c\u5175\u58eb \u672a\u53d8\uff0c\u5c31\u4e0d\u5fc5\u5bb3\u6015\uff0c\u4f60\u5148\u56de\u53bb\u5427\u3002\u5c31\u4f20\u6211\u7684\u4e3b\u610f\uff0c\u53eb\u4ed6\u4eec\u8bbe\u6cd5\u628a\u5e74\u7fb9\u5c27\u5148\u6263\u62bc\u8d77\u6765\u3002\u201d\u65b9\u4eca \u660e\u9762\u6709\u96be\u8272\uff0c\u4f17\u7687\u5b50\u542c\u5f97\u5141\u5824\u548c\u4eba\u8bf4\u8bdd\uff0c\u7eb7\u7eb7\u552c\u58f0\u6ce8\u89c6\uff0c\u5141\u5824\u6025\u63a8\u4ed6\u9053\uff1a\u201c\u5feb\u56de\u53bb\uff01\u201d\u65b9\u4eca \u660e\u65e0\u5948\uff0c\u53ea\u597d\u5728\u4eba\u5806\u4e2d\u53c8\u6324\u51fa\u3002 \u3000\u3000\u7eb7\u4e71\u4e2d\u5ba3\u793c\u7684\u592a\u76d1\u51fb\u8d77\u6bbf\u4e03\u5927\u949f\uff0c\u53eb\u9053\uff1a\u201c\u5ba3\u9057\u8bcf\uff01\u201d\u4e00\u5239\u90a3\u95f4\uff0c\u4e71\u54c4\u54c4\u7684\u5927\u6bbf\u9759\u4e86\u4e0b \u6765\uff0c\u8dcc\u4e00\u6839\u9488\u5728\u5730\u4e0b\u90fd\u542c\u5f97\u89c1\u54cd\u3002\u53ea\u89c1\u90a3\u9686\u79d1\u591a\u9102\u5c14\u6cf0\u5f20\u5ef7\u7389\u4e09\u4eba\u8d70\u4e0a\u6bbf\u53bb\uff0c\u6bbf\u4e0a\u8bbe\u4e86\u9999 \u6848\uff0c\u4e09\u4eba\u671b\u7a7a\u884c\u8fc7\u4e86\u793c\uff0c\u536b\u58eb\u5b89\u597d\u6276\u68af\uff0c\u9686\u79d1\u591a\u722c\u4e0a\u53bb\u5728\u533e\u989d\u540e\u5c06\u7389\u5323\u9057\u8bcf\u5dc5\u5dcd\u5dcd\u7684\u6367\u4e86\u4e0b \u6765\u3002\u4f17\u7687\u5b50\u4e2a\u4e2a\u4f38\u957f\u9888\u5b50\uff0c\u53ea\u89c1\u90a3\u9686\u79d1\u591a\u7ad9\u5728\u6bbf\u4e2d\uff0c\u9ad8\u58f0\u5ba3\u8bfb\u3002\u8bfb\u5230\u201c\u4f20\u4f4d\u4e8e\u2014\u2014\u201d\u4e4b\u65f6\uff0c \u6545\u610f\u62d6\u957f\u58f0\u97f3\uff0c\u5fc3\u6025\u7684\u7687\u5b50\u4e0d\u81ea\u89c9\u8de8\u6b65\u51fa\u53bb\uff0c\u9686\u79d1\u591a\u54b3\u4e86\u4e00\u58f0\uff0c\u63a5\u7740\u8bfb\u9053\uff1a\u201c\u56db\u7687\u5b50\uff01\u201d\u987f \u65f6\u9636\u4e0b\u54d7\u7136\u5927\u95f9\uff01\u4f17\u4eba\u90fd\u77e5\u9053\u5eb7\u7199\u7687\u5e1d\u6700\u50e7\u6076\u56db\u7687\u5b50\u5141\u796f\uff0c\u600e\u4f1a\u4f20\u4f4d\u7ed9\u4ed6\u3002\u6b8a\u4e0d\u77e5\u90a3\u9057\u8bcf\u672c \u6765\u662f\u5199\u7740\uff1a\u201c\u4f20\u4f4d\u5341\u56db\u7687\u5b50\u7684\uff0c\u5141\u796f\u5bc6\u6d3e\u5929\u53f6\u6563\u4eba\u548c\u51af\u7433\u5165\u53bb\u5077\u770b\uff0c\u77e5\u9053\u4e4b\u540e\uff0c\u7531\u9686\u79d1\u591a\u732e \u8ba1\uff0c\u5728\u5341\u5b57\u4e4b\u4e0a\u52a0\u4e86\u4e00\u6a2a\uff0c\u4e0b\u9762\u52a0\u4e86\u4e00\u94a9\uff0c\u53d8\u6210\u5341\u5b57\uff0c\u4e8e\u662f\u672c\u6765\u662f\u201c\u4f20\u4f4d\u5341\u56db\u7687\u5b50\u201d\u7684\u53d8\u6210 \u201c\u4f20\u4f4d\u4e8e\u56db\u7687\u5b50\u201d\u4e86\uff01 \u3000\u3000\u54c4\u95f9\u58f0\u4e2d\uff0c\u4e5d\u7687\u5b50\u5141\u642a\u3001\u5341\u7687\u5b50\u5141\u4fc4\u9996\u5148\u4e0d\u670d\uff0c\u8d8a\u4f17\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff01\u201d\u6bbf\u4e0a\u5e03\u7f6e\u597d\u7684 \u4f8d\u536b\u62e6\u4e0a\u524d\u6765\uff0c\u5141\u642a\u5141\u4fc4\u90fd\u662f\u5168\u8eab\u6b66\u529f\uff0c\u53c8\u4ee5\u4e3a\u90a3\u4e9b\u4f8d\u536b\u4e5f\u50cf\u5fa1\u6797\u519b\u4e00\u6837\uff0c\u53ea\u662f\u865a\u5f20\u58f0\u52bf\uff0c \u4e0d\u6562\u62e6\u4ed6\uff0c\u6043\u5f3a\u51b2\u53bb\uff0c\u5c31\u60f3\u62a2\u593a\u9057\u8bcf\uff0c\u4e0d\u6599\u5141\u796f\u677f\u9762\u559d\u9053\uff1a\u201c\u62ff\u4e0b\u6765\uff01\u201d\u4f8d\u536b\u4e2d\u4e24\u4eba\u9aa4\u7136\u6251 \u4e0a\uff0c\u5141\u642a\u5141\u5a25\u540c\u58f0\u5927\u559d\u9053\uff1a\u201c\u8c01\u6562\u62e6\u6211\uff1f\u201d \u3000\u3000\u5141\u642a\u5141\u4fc4\u61f5\u7136\u4e0d\u77e5\uff1a\u90a3\u4e24\u540d\u536b\u58eb\u5374\u4e0d\u662f\u666e\u901a\u536b\u58eb\uff0c\u800c\u662f\u56db\u7687\u5b50\u9884\u77e5\u6709\u4eca\u65e5\u4e4b\u4e8b\uff0c\u4e0d\u4f46\u628a \u5fa1\u6797\u519b\u548c\u6bbf\u524d\u4f8d\u536b\u90fd\u6536\u4e70\u4e86\uff0c\u800c\u4e14\u5728\u524d\u4e24\u5929\u5c31\u628a\u5341\u51e0\u540d\u5fc3\u8179\u597d\u624b\u5b89\u63d2\u8fdb\u53bb\uff0c\u8fd9\u4e24\u540d\u536b\u58eb\u4e43\u662f \u97e9\u91cd\u5c71\u548c\u8463\u5de8\u5ddd\uff0c\u5141\u642a\u5141\u4fc4\u53d1\u6012\u6325\u62f3\uff0c\u4e0d\u8fc7\u51e0\u4e2a\u7167\u9762\uff0c\u5c31\u7ed9\u70b9\u4e86\u7a74\u9053\uff0c\u6454\u5230\u9636\u4e0b\uff0c\u5fa1\u6797\u519b\u4e2d \u56db\u7687\u5b50\u6240\u57cb\u4f0f\u7684\u4eba\u62a2\u8fc7\u6765\u5c06\u4ed6\u4eec\u7f1a\u4e86\uff0c\u9886\u5934\u9ad8\u547c\u201c\u4e07\u5c81\uff01\u201d\u4e09\u5343\u5fa1\u6797\u519b\u547c\u58f0\u9707\u5929\uff0c\u767e\u5bab\u5931 \u8272\u3002\u8fd9\u65f6\u5168\u73ed\u4f8d\u536b\u4e0b\u6765\uff0c\u628a\u5141\u796f\u8fce\u4e0a\u6bbf\u53bb\uff0c\u5141\u796f\u4e5f\u5c31\u8001\u5b9e\u4e0d\u5ba2\u6c14\u5730\u628a\u7687\u5e1d\u7684\u51a0\u670d\u5168\u526f\u62ab\u6302\u8d77 \u6765\uff0c\u5728\u9686\u79d1\u591a\u7b49\u515a\u7fbd\u7c07\u62e5\u4e4b\u4e0b\uff0c\u767b\u4e86\u5b9d\u5ea7\u3002\u6bbf\u4e0b\u5fa1\u6797\u519b\u4e09\u547c\u201c\u4e07\u5c81\uff01\u201d\u90a3\u6587\u6b66\u767e\u5b98\uff0c\u6709\u4e00\u5927 \u534a\u5df2\u7ed9\u5141\u796f\u6536\u4e70\uff0c\u53e6\u4e00\u5c0f\u534a\u8feb\u4e8e\u5a01\u52bf\uff0c\u4e5f\u53ea\u5f97\u4e00\u4e2a\u4e2a\u4e0a\u6765\u671d\u89c1\u3002\u4f17\u7687\u5b50\u5446\u82e5\u6728\u9e21\uff0c\u8feb\u4e0d\u5f97\u5df2 \u90fd\u4e0a\u524d\u671d\u62dc\u3002 \u3000\u3000\u793c\u6210\u4e4b\u540e\uff0c\u5141\u796f\u9053\uff1a\u201c\u5141\u642a\u5141\u4fc4\u6270\u4e71\u671d\u5802\uff0c\u72af\u5927\u4e0d\u656c\u7f6a\uff0c\u7740\u5373\u9769\u53bb\u7235\u4f4d\uff0c\u4ea4\u5b97\u4eba\u5e9c\u5ba1 \u95ee\uff01\u201d\u53c8\u9053\uff1a\u201c\u5148\u5e1d\u9057\u8bcf\uff0c\u90e1\u738b\u672c\u65e0\u6743\u62c6\u8bfb\uff0c\u4f46\u4eca\u65e5\u65e2\u95f9\u4e86\u6b64\u4e8b\uff0c\u4e3a\u4e86\u662d\u793a\u5927\u516c\uff0c\u7279\u51c6\u5907\u4eb2 \u738b\u62dc\u8bfb\uff0c\u628a\u9057\u8bcf\u9881\u4e0b\uff01\u201d\u4f17\u7687\u5b50\u4f26\u7740\u4f20\u9605\uff0c\u89c1\u679c\u7136\u662f\u5eb7\u7199\u4eb2\u7b14\uff0c\u800c\u4e14\u679c\u7136\u5199\u5f97\u660e\u660e\u767d\u767d\u662f\uff1a \u201c\u4f20\u4f4d\u4e8e\u56db\u7687\u5b50\u201d\uff0c\u4f17\u4eba\u5fc3\u867d\u4e0d\u670d\uff0c\u5374\u90fd\u4e0d\u6562\u8bf4\u8bdd\u4e86\u3002\u5141\u796f\u53c8\u9053\uff1a\u201c\u5148\u5e1d\u5f25\u7559\u4e4b\u9645\uff0c\u6267\u610f\u8981 \u6211\u7ee7\u627f\u5927\u5b9d\uff0c\u5e76\u7ed9\u6211\u7389\u5ff5\u73e0\u4e3a\u51ed\uff0c\u6211\u529b\u63a8\u8f9e\u4e0d\u83b7\uff0c\u53ea\u597d\u5c0a\u7236\u7687\u9057\u5fd7\uff0c\u8fd8\u671b\u5404\u4f4d\u90e1\u738b\u76f8\u52a9\uff0c\u5171 \u6cbb\u5929\u4e0b\u3002\u201d\u8bf4\u7740\u53d6\u51fa\u5eb7\u7199\u63b7\u4ed6\u7684\u90a3\u4e32\u7389\u73e0\u6765\uff0c\u6545\u4f5c\u4f24\u611f\u4e4b\u72b6\uff0c\u6f78\u7136\u6cea\u4e0b\u3002\u5141\u796f\u77e5\u5404\u7687\u5b50\u90fd\u8fd8 \u6709\u6f5c\u52bf\u529b\u5b58\u5728\uff0c\u6240\u4ee5\u4e0d\u80fd\u4e0d\u5047\u610f\u7b3c\u7edc\u4eba\u5fc3\u3002\u5341\u56db\u7687\u5b50\u6c14\u5f97\u624b\u8db3\u51b0\u51b7\uff0c\u9996\u5148\u4e0b\u671d\uff0c\u4f17\u5f1f\u5b50\u4e5f\u8ddf \u7740\u6563\u4e86\u3002 \u3000\u3000\u5141\u796f\u5f53\u665a\u5c31\u642c\u5165\u7687\u5bab\uff0c\u5728\u4e7e\u6e05\u5bab\u5c45\u4f4f\uff0c\u8fde\u591c\u53ec\u96c6\u5fc3\u8179\uff0c\u5f7b\u591c\u529e\u516c\uff0c\u4f8b\u5982\u62df\u8ba2\u5404\u90e8\u5927\u81e3\u540d \u5355\uff0c\u8c03\u6362\u5404\u7701\u7763\u629a\uff0c\u76d1\u89c6\u4eb2\u738b\uff0c\u6536\u63fd\u5175\u6743\u7b49\u7b49\uff0c\u60f3\u4ee5\u5feb\u5200\u65a9\u4e71\u9ebb\u4e4b\u52bf\uff0c\u5de9\u56fa\u7687\u4f4d\u3002\u628a\u51e0\u4ef6\u5927 \u4e8b\u529e\u4e86\u4e0b\u6765\uff0c\u5df2\u662f\u56db\u66f4\uff0c\u9686\u79d1\u591a\u7b49\u65b9\u624d\u9000\u51fa\uff0c\u5141\u796f\u53c8\u6d3e\u4eba\u53bb\u4e0e\u5e74\u7fb9\u5c27\u8054\u7edc\uff0c\u5185\u76d1\u594f\u9053\uff1a\u201c\u7687 \u4e0a\u7a0d\u6b47\u4e00\u4f1a\u5427\u3002\u660e\u65e9\u8fd8\u8981\u4e0a\u671d\u5462\uff01\u201d\u5141\u796f\u4e5f\u5b9e\u5728\u56f4\u5026\uff0c\u4fbf\u9053\uff1a\u201c\u82e5\u5e74\u7fb9\u5c27\u6d3e\u4eba\u6765\uff0c\u7acb\u523b\u53eb\u4ed6 \u89c1\u6211\u3002\u201d\u4f0f\u6848\u5047\u5bdd\uff0c\u6726\u6726\u80e7\u80e7\u4f3c\u89c1\u5eb7\u7199\u8840\u6d41\u6ee1\u9762\uff0c\u6b63\u60f3\u547c\u6551\uff0c\u5ffd\u53c8\u89c1\u5415\u56db\u5a18\u63d0\u5251\u6740\u6765\uff0c\u5141\u796f \u4e00\u60ca\u800c\u9192\uff0c\u5185\u76d1\u7980\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\u5728\u5916\u9762\u6c42\u89c1\u3002\u201d\u5141\u796f\u9053\uff1a\u201c\u53eb\u4ed6\u8fdb\u6765\uff01\u201d \u3000\u3000\u4e86\u56e0\u8fdb\u6765\uff0c\u7a3d\u9996\u4e0d\u8dea\uff0c\u9053\uff1a\u201c\u606d\u559c\u4e3b\u516c\u5373\u4f4d\u3002\u201d\u4e86\u56e0\u81ea\u6043\u529f\u52b3\uff0c\u5bf9\u5141\u796f\u4ecd\u5982\u5e73\u65e5\u5728\u7687\u5e9c \u4e4b\u65f6\u3002\u5141\u796f\u6697\u6697\u4e0d\u60a6\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5e2e\u4eba\u5c06\u6765\u603b\u8981\u4e00\u4e2a\u4e2a\u9664\u6389\u624d\u597d\uff0c\u514d\u5f97\u4ed6\u4eec\u62ff\u6211\u5728\u6c5f\u6e56\u4e0a\u7684 \u4e8b\u60c5\u4e71\u8bf4\u3002\u201d\u4f46\u521a\u521a\u767b\u4f4d\uff0c\u8fd8\u6709\u8bb8\u591a\u4e8b\u8981\u501a\u4ed7\u4ed6\u4eec\uff0c\u56e0\u6b64\u4e0d\u9732\u8f9e\u8272\uff0c\u95ee\u9053\uff1a\u201c\u56fd\u5e08\u89c1\u6715\u4f55 \u4e8b\uff1f\u201d\u4e86\u56e0\u9053\uff1a\u201c\u7980\u7687\u4e0a\uff0c\u7687\u4e0a\u4eca\u65e5\u6240\u64d2\u7684\u90a3\u540d\u523a\u5ba2\u5df2\u5ba1\u51fa\u6765\u4e86\u3002\u201d\u5141\u5e27\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u9053\uff1a \u201c\u8fd9\u79cd\u5c0f\u4e8b\u4e5f\u62ff\u6765\u9ebb\u70e6\u6211\u3002\u968f\u53e3\u95ee\u9053\uff1a\u201c\u662f\u54ea\u4e2a\u7687\u5e9c\u7684\u536b\u58eb\uff1f\u201d\u4e86\u56e0\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u77e5\uff0c\u4f46\u8fd9 \u4eba\u5374\u662f\u85cf\u6709\u5148\u5e1d\u9057\u4e66\uff0c\u66fe\u81ea\u79f0\u662f\u5927\u5185\u536b\u58eb\u7684\u5510\u6653\u6f9c\u3002\u201d\u5141\u796f\u9053\uff1a\u201c\u597d\uff0c\u5e26\u4ed6\u8fdb\u6765\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u6709\u7518\u51e4\u6c60\u7ed9\u4ed6\u7684\u6613\u5bb9\u4e39\uff0c\u5165\u4eac\u4e4b\u65f6\uff0c\u5df2\u7528\u836f\u53d8\u4e86\u989c\u5bb9\uff0c\u4f46\u4e8c\u767e\u591a\u5e74\u524d\u7684\u6613\u5bb9\u4e39\u8fd8 \u6bd4\u4e0d\u4e0a\u73b0\u5728\u6700\u597d\u7684\u5316\u88c5\u836f\u54c1\uff0c\u4e86\u56e0\u7b49\u53c8\u662f\u884c\u5bb6\uff0c\u7528\u6e7f\u624b\u5dfe\u5728\u4ed6\u9762\u4e0a\u4e00\u62b9\uff0c\u767b\u65f6\u73b0\u51fa\u539f\u6765\u9762 \u76ee\uff0c\u800c\u4eca\u63a8\u4e86\u8fdb\u6765\uff0c\u5141\u796f\u4e00\u89c1\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8001\u662f\u548c\u6211\u4f5c\u5bf9\uff1f\u201d\u4e86\u56e0\u53d6\u51fa\u5eb7\u7199\u4ee5 \u524d\u7ed9\u5510\u6653\u6f9c\u7684\u8bcf\u4e66\uff0c\u90a3\u662f\u5f53\u5e74\u5510\u6653\u6f9c\u4e3a\u4e86\u8981\u89c1\u5141\u796f\uff0c\u6c42\u5eb7\u7199\u5199\u7684\uff0c\u540e\u6765\u5230\u4e86\u5141\u796f\u7687\u5e9c\uff0c\u672a\u66fe \u638f\u51fa\uff0c\u6070\u9047\u5173\u4e1c\u56db\u4fa0\u524d\u6765\u95f9\u4e8b\uff0c\u6240\u4ee5\u5141\u796f\u6ca1\u6709\u89c1\u8fc7\u3002 \u3000\u3000\u5141\u796f\u4e00\u770b\uff0c\u8bcf\u4e66\u8981\u5141\u796f\u597d\u597d\u7167\u987e\u6b64\u4eba\uff0c\u4e0d\u7981\u5927\u4e3a\u7591\u60d1\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u6765\u5386\uff1f\u201d\u5510\u6653 \u6f9c\u77aa\u76ee\u4e0d\u7b54\uff0c\u5141\u796f\u6b63\u60f3\u7ed9\u70b9\u82e6\u5934\u4ed6\u5403\uff0c\u5ffd\u7136\u5916\u9762\u4e00\u9635\u55a7\u54d7\uff0c\u5185\u76d1\u53eb\u9053\uff1a\u201c\u5bab\u4e2d\u8d77\u706b\u3002\u201d\u5141\u796f \u5927\u5403\u4e00\u60ca\uff0c\u63a8\u95e8\u5916\u671b\uff0c\u84e6\u5730\u91cc\u5bd2\u98ce\u6251\u9762\uff0c\u4fa7\u9762\u8be9\u5764\u5bab\u7684\u7409\u7483\u74e6\u4e0a\uff0c\u7a81\u7136\u8df3\u4e0b\u4e00\u4eba\uff0c\u8fd0\u5251\u5982 \u98ce\uff0c\u5237\uff0c\u5237\u4e24\u5251\uff0c\u76f4\u5411\u5141\u796f\u523a\u6765\uff0c\u8fd9\u4eba\u7adf\u7136\u662f\u5728\u68a6\u4e2d\u5413\u7834\u5141\u796f\u5fc3\u80c6\u7684\u5415\u56db\u5a18\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5de7\u8fd0\u6743\u8c0b\u767b\u5927\u5b9d\uff0c\u6df1\u5bab\u53c8\u89c1\u5251\u5149\u5bd2\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u94c1\u9a6c\u91d1\u6208\u3000\u5c06\u519b\u64c5\u5f81\u6218\u3000\u6653\u98ce\u6b8b\u6708\u3000\u7389\u5973\u5251\u7eb5\u6a2a \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u4e09\u56de \u94c1\u9a6c\u91d1\u6208\u3000\u5c06\u519b\u64c5\u5f81\u6218 \u6653\u98ce\u6b8b\u6708\u3000\u7389\u5973\u5251\u7eb5\u6a2a \u3000\u3000\u5415\u56db\u5a18\u7a81\u7136\u51fa\u73b0\uff0c\u5141\u796f\u5413\u5f97\u5446\u4e86\u3002\u4e86\u56e0\u864e\u543c\u4e00\u58f0\uff0c\u63d0\u8d77\u7897\u5927\u7684\u62f3\u5934\uff0c\u7167\u5415\u56db\u5a18\u9762\u95e8\u4e00 \u6643\uff0c\u9661\u7136\u98de\u8d77\u4e00\u811a\uff0c\u62f3\u865a\u817f\u5b9e\uff0c\u5415\u56db\u5a18\u624b\u4e2d\u5b9d\u5251\u51e0\u4e4e\u7ed9\u4ed6\u8e22\u98de\uff0c\u6025\u5fd9\u820d\u4e86\u5141\u796f\uff0c\u970d\u5730\u4e00\u4e2a \u201c\u51e4\u70b9\u5934\u201d\uff0c\u5251\u628a\u4e00\u7ffb\uff0c\u8fde\u8fdb\u4e24\u62db\uff0c\u4e0a\u523a\u8111\u6d77\uff0c\u4e0b\u523a\u809a\u8110\uff0c\u4e86\u56e0\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u95ea\u5230\u5415 \u56db\u5a18\u80cc\u540e\uff0c\u518d\u8d77\u98de\u811a\uff0c\u8e22\u5979\u540e\u5fc3\uff0c\u4e86\u56e0\u8fd9\u4e24\u62db\u662f\u628a\u201c\u4f0f\u864e\u62f3\u201d\u201c\u8fde\u73af\u817f\u201d\u5408\u8d77\u6765\u7528\uff0c\u51f6\u72b7\u4e4b \u6781\u3002\u5415\u56db\u5a18\u542c\u5f97\u8111\u540e\u98ce\u751f\uff0c\u811a\u5c16\u4e00\u70b9\uff0c\u8eab\u5b50\u51cc\u7a7a\uff1b\u4e86\u56e0\u4e00\u811a\u8e22\u53bb\uff0c\u5ffd\u7136\u5931\u4e86\u654c\u8e2a\uff0c\u8eab\u5b50\u5411\u524d \u51b2\u4e86\u4e24\u6b65\uff0c\u5415\u56db\u5a18\u624b\u6500\u6bbf\u7cb1\uff0c\u5de6\u624b\u4e00\u626c\uff0c\u4e24\u67c4\u5c0f\u5315\u9996\u545c\u545c\u58f0\u54cd\uff0c\u4e00\u53d6\u5141\u796f\uff0c\u4e00\u53d6\u4e86\u56e0\uff01 \u3000\u3000\u8fd9\u51e0\u4e0b\u5feb\u5982\u7535\u5149\u706b\u77f3\uff0c\u5141\u796f\u521a\u624d\u6014\u4e86\u4e00\u6014\uff0c\u5230\u4e86\u56e0\u66ff\u4ed6\u6321\u4f4f\u5415\u56db\u5a18\u65f6\uff0c\u60ca\u9b42\u65b9\u5b9a\uff0c\u4f38\u624b \u8981\u70b9\u5510\u6653\u6f9c\u7684\u6655\u7a74\uff0c\u90a3\u6599\u5415\u56db\u5a18\u5315\u9996\u7a81\u7136\u98de\u6765\uff0c\u5141\u796f\u4f0f\u5730\u4e00\u6eda\uff0c\u5315\u9996\u4ece\u4ed6\u5934\u9876\u98de\u8fc7\u3002\u7ffb\u8d77\u8eab \u65f6\uff0c\u5b9d\u5251\u5df2\u62d4\u5728\u624b\u4e2d\u3002 \u3000\u3000\u53d6\u4e86\u56e0\u90a3\u628a\u5315\u9996\uff0c\u7ed9\u4e86\u56e0\u53cc\u6307\u4e00\u5939\uff0c\u786c\u7528\u91d1\u94a2\u6307\u529b\uff0c\u628a\u5315\u9996\u5939\u4e3a\u4e24\u6bb5\uff0c\u5141\u796f\u53eb\u9053\uff1a\u201c\u5b9d \u56fd\u7985\u5e08\uff0c\u5148\u628a\u90a3\u5510\u6653\u6f9c\u5e9f\u4e86\uff01\u201d\u5141\u796f\u56e0\u5510\u6653\u6f9c\u8eab\u4e0a\u85cf\u6709\u5148\u5e1d\u8bcf\u4e66\uff0c\u4e0d\u77e5\u4ed6\u7684\u6765\u5386\uff0c\u6781\u4e4b\u731c \u7591\uff0c\u53cd\u4ee5\u4e34\u6025\u4e4b\u65f6\uff0c\u5c1a\u4e0d\u5fd8\u8981\u628a\u4ed6\u5e9f\u6389\u3002\u4e86\u56e0\u62d4\u8eab\u8dc3\u53bb\uff0c\u5415\u56db\u5a18\u9661\u7136\u4ece\u7a7a\u98de\u4e0b\uff0c\u4e86\u56e0\u8fd8\u672a\u8d76 \u5230\uff0c\u5979\u5df2\u628a\u5510\u6653\u6f9c\u63d0\u8d77\uff0c\u53c8\u8df3\u4e0a\u5927\u6bbf\u4e3b\u7cb1\uff0c\u8fd9\u65f6\u5916\u9762\u7684\u536b\u58eb\u7eb7\u7eb7\u6d8c\u6765\u6551\u9a7e\uff0c\u5415\u56db\u5a18\u7528\u5251\u65a9\u65ad \u4e86\u5510\u6653\u6f9c\u8eab\u4e0a\u9563\u94d0\uff0c\u95ee\u9053\uff1a\u201c\u6ca1\u53d7\u4f24\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6ca1\u6709\uff01\u201d\u539f\u6765\u4e86\u56e0\u770b\u89c1\u5eb7\u7199\u7684\u8bcf\u4e66 \u4e0a\u5199\u660e\u8981\u5141\u796f\u7167\u987e\u4ed6\uff0c\u5728\u672a\u7980\u660e\u5141\u796f\u4e4b\u524d\uff0c\u4e0d\u6562\u79c1\u7528\u5211\u7f5a\u3002\u5415\u56db\u5a18\u542c\u4ed6\u6ca1\u6709\u53d7\u4f24\uff0c\u5bbd\u4e86\u5bbd \u5fc3\uff0c\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u95ef\u51fa\u53bb\uff01\u201d\u8eab\u5f62\u4e00\u957f\uff0c\u5b9d\u5251\u65cb\u98ce\u4e00\u626b\uff0c\u628a\u7409\u7483\u74e6\u6253\u788e\uff0c\u5c4b\u9876\u7a7f\u4e86\u4e00\u4e2a\u6d1e \u53e3\uff0c\u6709\u4e24\u540d\u8f7b\u529f\u6781\u597d\u7684\u536b\u58eb\uff0c\u98de\u8eab\u4e0a\u53bb\u6293\u5979\uff0c\u5415\u56db\u5a18\u8eab\u5b50\u4e00\u5f13\uff0c\u5de6\u624b\u628a\u5510\u6653\u6f9c\u63b7\u51fa\u6d1e\u5916\uff0c\u53f3 \u624b\u5251\u950b\u4e00\u6233\u4e00\u70b9\uff0c\u4e24\u540d\u536b\u58eb\u7684\u624b\u521a\u521a\u89e6\u7740\u7cb1\u67f1\uff0c\u5c31\u7ed9\u5415\u56db\u5a18\u65a9\u4f24\uff0c\u8dcc\u4e0b\u53bb\u4e86\uff01 \u3000\u3000\u4e86\u56e0\u6b66\u529f\u867d\u7136\u6781\u9ad8\uff0c\u89c1\u72b6\u4e5f\u4e0d\u7981\u6697\u6697\u60ca\u5fc3\u3002\u5141\u796f\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u5feb\u628a\u8fd9\u8d31\u5a62\u66ff\u6715\u64d2 \u6765\u3002\u201d\u4e86\u56e0\u9002\u624d\u4e0e\u5415\u56db\u5a18\u6362\u4e86\u51e0\u62db\uff0c\u89c1\u5979\u7684\u5251\u6cd5\u4f3c\u4e4e\u6bd4\u524d\u66f4\u9ad8\uff0c\u81ea\u5df1\u7684\u7985\u6756\u4e0d\u5728\u624b\u5934\uff0c\u7a7a\u624b \u4e0e\u5979\u5355\u6253\u72ec\u6597\uff0c\u53ea\u6050\u8ba8\u4e0d\u4e86\u4fbf\u5b9c\u3002\u82e5\u548c\u4f17\u536b\u58eb\u56f4\u653b\u5979\uff0c\u53c8\u5931\u4e86\u5e08\u5144\u7684\u8eab\u4efd\uff0c\u56e0\u6b64\u5141\u796f\u4e00\u58f0\u4ee4 \u4e0b\uff0c\u4f17\u536b\u7eb7\u8d76\u51fa\u53bb\uff0c\u53ea\u6709\u4e86\u56e0\u4e0d\u52a8\uff0c\u5411\u5141\u796f\u7980\u9053\uff1a\u201c\u4e3b\u516c\uff0c\u53ea\u6050\u4ed6\u4eec\u8fd8\u6709\u540c\u515a\uff0c\u6211\u5728\u8fd9\u91cc\u4fdd \u62a4\u4e3b\u516c\u3002\u201d\u5141\u796f\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u5728\u8fd9\u91cc\u4e5f\u597d\u3002\u201d\u5fc3\u91cc\u5374\u662f\u4e0d\u60a6\u3002 \u3000\u3000\u5415\u56db\u5a18\u8f7b\u529f\u4fca\u6781\uff0c\u5510\u6653\u6f9c\u4ea6\u8981\u6bd4\u4e00\u822c\u536b\u58eb\u4e3a\u9ad8\uff0c\u500f\u5ffd\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u98de\u8d8a\u51fa\u51e0\u91cd\u6bbf\u5b87\u3002\u5916 \u9762\u8463\u5de8\u5ddd\u4e0e\u7518\u5929\u9f99\u4ece\u4e24\u8fb9\u88ad\u6765\uff0c\u8fd9\u4e24\u4eba\u529f\u592b\uff0c\u5728\u4f17\u536b\u58eb\u4e4b\u4e0a\uff0c\u5415\u56db\u5a18\u5315\u9996\u6025\u98de\uff0c\u7518\u5929\u9f99\u957f\u5251 \u4e00\u683c\uff0c\u628a\u7b2c\u4e00\u67c4\u5315\u9996\u6253\u98de\uff0c\u770b\u51c6\u7b2c\u4e8c\u67c4\u5315\u9996\u6765\u52bf\uff0c\u4e00\u4e2a\u95ea\u8eab\uff0c\u5411\u5de6\u95ea\u5f00\uff0c\u90a3\u6599\u5415\u56db\u5a18\u4f3c\u65e9\u5df2 \u6599\u5230\u4ed6\u6709\u6b64\u4e00\u7740\uff0c\u53d1\u6697\u5668\u4e4b\u65f6\uff0c\u6697\u8fd0\u624b\u6cd5\uff0c\u7b2c\u4e00\u67c4\u5315\u9996\u8ff3\u6025\u76f4\u98de\uff0c\u7b2c\u4e8c\u67c4\u5315\u9996\u98de\u8fd1\u654c\u524d\uff0c\u5374 \u7a81\u7136\u4e00\u504f\uff0c\u5411\u5de6\u4e00\u62d0\uff0c\u5315\u9996\u547c\u7684\u4e00\u58f0\uff0c\u4ece\u7518\u5929\u9f99\u80a9\u5934\u64e6\u8fc7\uff0c\u628a\u80a9\u5934\u7684\u8863\u670d\u5212\u5f00\uff0c\u7518\u5927\u9f99\u5927\u5403 \u4e00\u60ca\uff0c\u4e0d\u6562\u524d\u8ffd\u3002\u8463\u5de8\u5ddd\u5374\u4e00\u6296\u624b\u8fd8\u656c\u4e86\u4e09\u679a\u900f\u9aa8\u9489\uff0c\u4e24\u679a\u7ed9\u5415\u56db\u5a18\u6253\u843d\uff0c\u7b2c\u4e09\u679a\u4e5f\u64e6\u7740\u5510 \u6653\u6f9c\u80a9\u5934\u98de\u8fc7\uff0c\u628a\u5510\u6653\u6f9c\u5413\u4e86\u4e00\u5927\u8df3\u3002 \u3000\u3000\u4e24\u8fb9\u6697\u5668\u4ea4\u950b\uff0c\u963b\u4e86\u4e00\u963b\uff0c\u5bab\u4e2d\u536b\u58eb\u5df2\u4ece\u56db\u9762\u56f4\u6765\uff0c\u5415\u56db\u5a18\u4ed7\u5251\u5728\u524d\u5f00\u8def\uff0c\u5e26\u7740\u5510\u6653\u6f9c \u4e13\u62e3\u50fb\u5904\u9003\u7a9c\uff0c\u8fd9\u65f6\u5df2\u8fdb\u5165\u4e86\u5bab\u540e\u7684\u5fa1\u82b1\u56ed\uff0c\u521a\u521a\u63a0\u8fc7\u4e00\u5ea7\u5047\u5c71\uff0c\u84e6\u5730\u53c8\u51b2\u51fa\u4e00\u961f\u536b\u58eb\uff0c\u524d \u9762\u90a3\u4eba\u8f7b\u767b\u5de7\u7eb5\uff0c\u6377\u82e5\u733f\u7334\uff0c\u5510\u6653\u6f9c\u4e00\u770b\uff0c\u5374\u662f\u4ee5\u524d\u5077\u653e\u8fc7\u81ea\u5df1\u5165\u5bab\u5bfb\u6bcd\u7684\u4faf\u4e09\u53d8\u3002\u4f46\u89c1\u4faf \u4e09\u53d8\u628a\u624b\u4e00\u626c\uff0c\u4e00\u679d\u54cd\u7bad\uff0c\u7834\u7a7a\u98de\u6765\uff0c\u5510\u6653\u6f9c\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4faf\u4e09\u53d8\u4e43\u662f\u6211\u5f00\u8499\u5e08\u5085\u5468\u9752 \u7684\u597d\u53cb\uff0c\u5982\u4f55\u4e5f\u5bf9\u6211\u4e0d\u7559\u60c5\u9762\uff0c\u90a3\u679d\u54cd\u7bad\u4ece\u5415\u56db\u5a18\u5934\u9876\u98de\u8fc7\uff0c\u5415\u56db\u5a18\u8eab\u5f62\u75be\u8d77\uff0c\u5411\u90a3\u679d\u54cd\u7bad \u843d\u5904\u8d76\u53bb\uff0c\u5510\u6653\u6f9c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7d27\u8ddf\u5415\u56db\u5a18\u8eab\u540e\uff0c\u4faf\u4e09\u53d8\u8d8a\u4f17\u6765\u8ffd\uff0c\u8fde\u653e\u51e0\u679d\u54cd\u7bad\uff0c\u6709\u7684\u5de6 \u98de\uff0c\u6709\u7684\u53f3\u5c04\uff0c\u5415\u56db\u5a18\u8ddf\u7740\u54cd\u7bad\u524d\u5954\uff0c\u5c31\u597d\u50cf\u9760\u54cd\u7bad\u7ed9\u5979\u5e26\u8def\u4f3c\u7684\uff0c\u628a\u536b\u58eb\u7529\u5728\u8eab\u540e\uff0c\u8f6c\u5165 \u5047\u5c71\u82b1\u6811\u4e1b\u4e2d\uff0c\u7adf\u7136\u4e00\u8def\u65e0\u4eba\u62e6\u622a\uff01 \u3000\u3000\u54cd\u7bad\u4e00\u505c\uff0c\u5415\u56db\u5a18\u500f\u7136\u6b62\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u51b7\u7985\u771f\u6709\u529e\u6cd5\uff01\u201d\u82b1\u6811\u4e0b\u7a81\u7136\u95ea\u51fa\u4e00\u4eba\uff0c\u5c06\u5510\u6653 \u6f9c\u4e00\u628a\u62c9\u7740\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u6765\u4e86\u3002\u201d\u5510\u6653\u6f9c\u4e00\u770b\uff0c\u5374\u662f\u4e00\u4e2a\u548c\u5c1a\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u624d\u770b\u51fa\u662f\u4ee5\u524d\u548c \u81ea\u5df1\u5728\u5bab\u4e2d\u4ea4\u8fc7\u624b\u7684\u795d\u5bb6\u6f8e\u3002\u51b7\u7985\u8fd9\u4e00\u62c9\uff0c\u5374\u662f\u64d2\u62ff\u624b\u7684\u7edd\u62db\uff0c\u5510\u6653\u6f9c\u7684\u7435\u7436\u9aa8\u84e6\u7136\u7ed9\u4ed6\u4e09 \u6307\u4e00\u6263\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002\u5415\u56db\u5a18\u5fd9\u9053\uff1a\u201c\u662f\u81ea\u5df1\u4eba\u3002\u201d\u51b7\u7985\u8be7\u9053\uff1a\u201c\u600e\u4e48\u4ed6\u4e0d\u662f\u5bab\u4e2d\u7684\u536b\u58eb \u5417\uff1f\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u5979\u662f\u6d77\u68e0\u7684\u513f\u5b50\uff01\u201d\u51b7\u7985\u4e00\u9635\u98a4\u7c9f\uff1a\u6025\u5fd9\u653e\u624b\uff0c\u5e26\u9886\u5415\u5510\u4e8c\u4eba\u8fdb\u5165\u4e00 \u4e2a\u5c71\u6d1e\u4e4b\u4e2d\u3002\u9ed1\u6697\u4e2d\uff0c\u5510\u6653\u6f9c\u4f46\u89c1\u4ed6\u53cc\u773c\u95ea\u95ea\u53d1\u5149\uff0c\u76ef\u7740\u81ea\u5df1\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u4ed6\u662f\u4f60 \u6bcd\u4eb2\u4ee5\u524d\u7684\u597d\u53cb\u3002\u201d\u5510\u6653\u6f9c\u5fc3\u795e\u52a8\u8361\uff0c\u6f78\u7136\u6cea\u4e0b\uff0c\u51b7\u7985\u9053\uff1a\u201c\u4f60\u89c1\u7740\u4e86\u4f60\u6bcd\u4eb2\u4e48\uff1f\u201d\u5510\u6653\u6f9c \u9053\uff1a\u201c\u89c1\u7740\u4e86\uff01\u201d\u51b7\u7985\u9053\uff1a\u201c\u4f60\u5e26\u6211\u5230\u51b7\u5bab\u627e\u5979\u3002\u201d\u5510\u6653\u6f9c\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u518d\u627e\u4e86\uff0c\u6211 \u6bcd\u4eb2\u65e9\u5df2\u6b7b\u4e86\uff01\u201d \u3000\u3000\u51b7\u7985\u4e00\u5446\uff0c\u5bd2\u610f\u76f4\u900f\u5fc3\u5934\uff0c\u4ed6\u7b49\u4e86\u4e09\u5341\u591a\u5e74\uff0c\u505a\u4e86\u548c\u5c1a\uff0c\u72b9\u672a\u5fd8\u60c5\uff0c\u60f3\u4e0d\u5230\u610f\u4e2d\u4eba\u5374\u5df2 \u6b7b\u4e86\u3002 \u3000\u3000\u539f\u6765\u51b7\u7985\u4e09\u5341\u591a\u5e74\u4e4b\u524d\u66fe\u5728\u5bab\u4e2d\u7684\u5185\u52a1\u90e8\u5f53\u5dee\uff0c\u548c\u4e00\u5e2e\u5fa1\u524d\u5f85\u536b\u76f8\u8bc6\uff0c\u81ea\u53bb\u5e74\u6765\u4eac\uff0c\u9690 \u5c45\u897f\u5c71\u4e4b\u540e\uff0c\u53c8\u65f6\u65f6\u5468\u6d4e\u4e00\u4e9b\u5df2\u6b7b\u53bb\u7684\u8001\u536b\u58eb\u6240\u7559\u4e0b\u7684\u5be1\u5987\u5b64\u513f\uff0c\u6240\u4ee5\u4e00\u4e9b\u5c1a\u672a\u9000\u5f79\u7684\u536b\u58eb \u65e7\u4eba\uff0c\u548c\u4ed6\u9887\u4e3a\u76f8\u5f97\u3002\u5eb7\u7199\u9a7e\u5d29\u90a3\u5929\uff0c\u6ca1\u6709\u591a\u4e45\uff0c\u4ed6\u7684\u536b\u58eb\u670b\u53cb\u4e2d\uff0c\u4fbf\u6709\u4eba\u5411\u4ed6\u62a5\u4fe1\uff0c\u8bf4\u662f \u5510\u6653\u6f9c\u88ab\u64d2\uff0c\u5141\u796f\u4e5f\u5df2\u7ecf\u5165\u5bab\u4e86\u3002\u90a3\u4e9b\u8001\u4e00\u8f88\u7684\u536b\u58eb\uff0c\u9664\u4e86\u5019\u4e09\u53d8\u7b49\u6709\u9650\u51e0\u4eba\u5916\uff0c\u5728\u5eb7\u7199\u665a \u5e74\uff0c\u5df2\u56e0\u5e74\u8001\u529b\u8870\uff0c\u591a\u534a\u5931\u52bf\uff0c\u5728\u5bab\u4e2d\u6267\u5f79\uff0c\u53ea\u4e0d\u8fc7\u662f\u4f4d\u5217\u95f2\u800c\u5df2\u3002\u4e00\u5230\u65b0\u5e1d\u5373\u4f4d\uff0c\u514d\u4e0d\u4e86 \u4eba\u5fc3\u60f6\u60f6\uff0c\u627e\u51b7\u7985\u5546\u91cf\u529e\u6cd5\u3002 \u3000\u3000\u51b7\u7985\u548c\u4e00\u73ed\u536b\u58eb\u5728\u53e4\u5e99\u7684\u5927\u6bbf\u503e\u8c08\uff0c\u7518\u51e4\u6c60\u548c\u5415\u56db\u5a18\u7b49\u5728\u623f\u4e2d\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u536b\u58eb\u53bb \u540e\uff0c\u7518\u51e4\u6c60\u9053\uff1a\u201c\u795d\u5927\u54e5\uff0c\u4f60\u8fd8\u60f3\u5165\u5bab\u5417\uff1f\u201d\u51b7\u7985\u9053\uff1a\u201c\u5141\u796f\u95e8\u4e0b\uff0c\u9ad8\u624b\u5982\u4e91\uff0c\u6b64\u540e\u5bab\u4e2d\u5fc5 \u7136\u9632\u536b\u66f4\u4e25\uff0c\u5982\u4f55\u53bb\u5f97\uff1f\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u5728\u8fd9\u65b0\u65e7\u4ea4\u66ff\u4e4b\u65f6\uff0c\u6700\u6613\u6df7\u5165\uff0c\u518d\u8fc7\u4e9b\u65f6 \u65e5\u4fbf\u4e0d\u884c\u4e86\u3002\u201d\u51b7\u7985\u719f\u8bc6\u5bab\u4e2d\u60c5\u5f62\uff0c\u4e00\u60f3\u4fbf\u660e\u5176\u4e2d\u9053\u7406\uff0c\u6240\u4f3c\u5f53\u665a\u4fbf\u548c\u5415\u56db\u5a18\u5077\u8fdb\u5bab\u5185\u3002 \u3000\u3000\u679c\u7136\u5728\u65b0\u65e7\u4ea4\u63a5\u4e4b\u9645\uff0c\u9632\u8303\u8f83\u758f\u3002\u5141\u796f\u5fd9\u4e8e\u5904\u7406\u5927\u4e8b\uff0c\u5bf9\u5bab\u4e2d\u536b\u58eb\u7684\u5dee\u4f7f\u8fd8\u672a\u6709\u5168\u76d8\u5e03 \u7f6e\uff0c\u800c\u54c8\u5e03\u9640\u4e86\u56e0\u7b49\u4eba\uff0c\u521a\u521a\u5165\u5bab\uff0c\u5730\u65b9\u4e5f\u8fd8\u672a\u719f\u6089\u3002\u5415\u56db\u5a18\u7b49\u968f\u4fbf\u5728\u5bab\u4e2d\u51b7\u50fb\u7684\u5730\u65b9\u653e\u4e00 \u628a\u706b\uff0c\u5f15\u5f00\u4e86\u5141\u796f\u7684\u4eba\uff0c\u5415\u56db\u5a18\u8d81\u8fd9\u673a\u4f1a\uff0c\u4fbf\u628a\u5510\u6653\u6f9c\u6551\u51fa\u6765\u4e86\u3002 \u3000\u3000\u51b7\u7985\u542c\u5f97\u610f\u4e2d\u4eba\u5df2\u6b7b\uff0c\u534a\u664c\u4e0d\u8bed\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u54b1\u4eec\u4e8b\u60c5\u5df2\u4e86\uff0c\u51fa\u5bab\u53bb\u5427\u3002\u201d\u51b7\u7985\u4f24\u5fc3 \u4e4b\u6781\uff0c\u95ee\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u6b7b\u7684\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5c31\u662f\u4f60\u4e0a\u6b21\u8fdb\u5bab\u7684\u90a3\u4e2a\u665a\u4e0a\u3002\u201d\u51b7\u7985 \u9762\u5982\u6b7b\u7070\uff0c\u5047\u5c71\u6d1e\u5916\u4eba\u5f71\u4e00\u95ea\uff0c\u4faf\u4e09\u53d8\u8d70\u4e86\u8fdb\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5e78\u4e8f\u5141\u796f\u5e26\u6765\u7684\u90a3\u5e2e\u536b\u58eb\u8fd8\u672a\u719f \u6089\u5bab\u4e2d\u9053\u8def\uff0c\u8001\u536b\u58eb\u4eec\u53c8\u4e0d\u662f\u8bda\u5fc3\u4e3a\u5141\u796f\u5356\u529b\uff0c\u8981\u4e0d\u7136\u4f60\u4eec\u771f\u9003\u4e0d\u4e86\u3002\u201d\u5ffd\u89c9\u6d1e\u4e2d\u6c14\u6c1b\u6709 \u5f02\uff0c\u95ee\u9053\uff1a\u201c\u795d\u5927\u54e5\uff0c\u600e\u4e48\u5566\uff1f\u4f60\u4eec\u90fd\u4e0d\u4f5c\u58f0\u3002\u201d\u51b7\u7985\u9053\uff1a\u201c\u6d77\u68e0\u6b7b\u4e86\uff01\u201d\u4fdf\u4e09\u53d8\u9053\uff1a\u201c\u6d77 \u68e0\u6b7b\u4e86\uff1f\u602a\u4e0d\u5f97\u4e0a\u6b21\u4f60\u4eec\u8fdb\u5bab\u4e4b\u540e\uff0c\u51b7\u5bab\u4fbf\u5c01\u95ed\u4e86\uff0c\u6211\u8fd8\u4ee5\u4e3a\u5979\u662f\u88ab\u79fb\u5230\u522b\u5904\u5e7d\u7981\u5462\u3002\u201d\u51b7 \u7985\u5ffd\u9053\uff1a\u201c\u6d77\u68e0\u867d\u6b7b\uff0c\u6211\u8fd8\u60f3\u5230\u51b7\u5bab\u4e00\u770b\uff0c\u770b\u770b\u5979\u4e8c\u5341\u591a\u5e74\u5c45\u7559\u8fc7\u7684\u5730\u65b9\u3002\u201d\u5019\u4e09\u53d8\u9ed8\u7136\u4e0d \u8bed\uff0c\u9ed1\u6697\u4e2d\uff0c\u5510\u6653\u6f9c\u6cea\u5149\u6447\u6643\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u518d\u53bb\u4e00\u6b21\u3002\u201d\u4faf\u4e09\u53d8\u60f3\u4e86\u4e00\u9635\uff0c\u53f9\u53e3\u6c14\u9053\uff1a \u201c\u4e00\u671d\u5929\u5b50\u4e00\u671d\u81e3\uff0c\u4ee5\u540e\u6211\u4e5f\u4e0d\u60f3\u5728\u5bab\u4e2d\u6df7\u4e0b\u53bb\u4e86\uff0c\u5c31\u5e26\u4f60\u4eec\u53bb\u4e00\u8d9f\u5427\u3002\u201d \u3000\u3000\u8fc7\u4e86\u8bb8\u4e45\uff0c\u5916\u9762\u4eba\u58f0\u6e10\u9759\uff0c\u4faf\u4e09\u53d8\u5e26\u9886\u51b7\u7985\u7b49\u4e09\u4eba\u6284\u5bab\u4e2d\u5c0f\u5f84\uff0c\u76f4\u5954\u51b7\u5bab\uff0c\u6cbf\u9014\u4e0a\u867d\u7136 \u6709\u4e24\u4e09\u5904\u6709\u4eba\u67e5\u95ee\uff0c\u4f46\u90fd\u4e0d\u662f\u5141\u796f\u7684\u4eba\uff0c\u4faf\u4e09\u53d8\u4e00\u6253\u6697\u8bed\uff0c\u4fbf\u901a\u8fc7\u4e86\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u53ea\u89c1\u4e00\u4e2a \u8377\u5858\uff0c\u6c34\u5149\u95ea\u95ea\u53d1\u4eae\uff0c\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u8377\u5858\u8fb9\u90a3\u6240\u9ed1\u77f3\u5c4b\u5b50\u4fbf\u662f\u51b7\u5bab\u4e86\u3002\u201d\u8d70\u5230\u5bab\u524d\uff0c\u5ffd\u89c1\u77f3 \u95e8\u534a\u63a9\uff0c\u5019\u4e09\u53d8\u5927\u4e3a\u8be7\u5f02\uff0c\u51b7\u7985\u62a2\u5728\u524d\u5934\uff0c\u63a8\u95e8\u8fdb\u53bb\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u95ee\u9053\uff1a\u201c\u662f\u738b\u961f\u957f\u5417\uff1f\u201d \u3000\u3000\u51b7\u7985\u548c\u5c1a\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u4e24\u4e2a\u5bab\u5973\u5728\u91cc\u9762\u6253\u626b\u3002\u51b7\u7985\u6014\u4e00\u6014\uff1a\u8fd9\u4e24\u4e2a\u5bab\u5973\u597d\u50cf\u662f\u5728\u90a3\u91cc \u89c1\u8fc7\u4f3c\u7684\u3002\u4e0d\u7406\u5979\u8bf4\u4ec0\u4e48\u201c\u738b\u961f\u957f\u201d\u4e0d\u201c\u738b\u961f\u957f\u201d\uff0c\u51b2\u4e0a\u53bb\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u8ba4\u8bc6\u6d77\u68e0\u5417\uff1f\u201d\u90a3 \u4e24\u4e2a\u5bab\u5973\u5413\u4e86\u4e00\u8df3\uff0c\u60ca\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u548c\u5c1a\u662f\u4ece\u90a3\u91cc\u6765\u7684\uff1f\u201d\u4faf\u4e09\u53d8\u8de8\u4e0a\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u4ed6\u662f\u7687 \u4e0a\u5e26\u6765\u7684\u4eba\uff0c\u4f60\u600e\u4e48\u4e0d\u7b54\u4ed6\u7684\u8bdd\uff1f\u201d\u4e86\u56e0\u548c\u5c1a\u968f\u5141\u796f\u5165\u5bab\uff0c\u5bab\u4e2d\u65e9\u5df2\u4f20\u5f00\uff0c\u90a3\u4e24\u4e2a\u5bab\u5973\u8fd8\u4ee5 \u4e3a\u51b7\u7985\u5c31\u662f\u90a3\u4e2a\u4ec0\u4e48\u201c\u5b9d\u56fd\u7985\u5e08\u201d\uff0c\u5413\u5f97\u53d8\u4e86\u9762\u8272\uff0c\u51b7\u7985\u559d\u9053\uff1a\u201c\u5feb\u8bf4\uff01\u201d\u4e00\u4e2a\u5bab\u5973\u80c6\u5b50\u8f83 \u5927\uff0c\u56de\u9053\uff1a\u201c\u6d77\u68e0\u65e9\u5df2\u6b7b\u4e86\uff0c\u8fd8\u662f\u6211\u4eec\u628a\u5979\u62ac\u51fa\u53bb\u57cb\u7684\uff01\u201d \u3000\u3000\u51b7\u7985\u7684\u773c\u5149\u4e2d\u7a81\u7136\u51fa\u73b0\u4e00\u79cd\u5947\u5f02\u7684\u5149\u8292\uff0c\u75db\u82e6\u7684\u626d\u7740\u624b\u81c2\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u662f\u4e0d\u662f\u7528\u7af9\u5e8a \u62ac\u51fa\u53bb\u7684\uff1f\u201d\u5bab\u5973\u9053\uff1a\u201c\u662f\u5440\uff01\u201d\u51b7\u7985\u987f\u65f6\u5446\u82e5\u6728\u9e21\uff0c\u8111\u6d77\u4e2d\u73b0\u51fa\u4e00\u5e45\u892a\u4e86\u8272\u7684\u56fe\u753b\uff1a\u56db\u540d \u5bab\u5973\u6307\u7740\u4e00\u5f20\u7af9\u5e8a\uff0c\u7af9\u5e8a\u4e0a\u7528\u767d\u5e03\u76d6\u7740\u4e00\u4e2a\u5973\u75c5\u4eba\uff0c\u5934\u53d1\u7a00\u758f\u6591\u767d\uff0c\u9762\u8272\u5341\u5206\u53ef\u6015\uff0c\u9732\u51fa\u6765 \u7684\u4e24\u53ea\u624b\uff0c\u624b\u6307\u5c31\u5982\u9e21\u722a\u4e00\u822c\u3002\u8fd9\u662f\u81ea\u5df1\u7b2c\u4e00\u6b21\u5165\u5bab\u65f6\uff0c\u5076\u7136\u78b0\u5230\u7684\u4e00\u4e2a\u60c5\u666f\u3002\u96be\u9053\u90a3\u5929\u665a \u4e0a\uff0c\u649e\u5230\u7684\u90a3\u4e2a\u50f5\u5c38\u822c\u7684\u4e11\u964b\u5973\u4eba\uff0c\u5c31\u662f\u5f53\u5e74\u7f8e\u5982\u4ed9\u5b50\u7684\u6d77\u68e0\uff1f\u518d\u4e00\u7ec6\u770b\uff0c\u8fd9\u4e24\u4e2a\u4f3c\u66fe\u76f8\u8bc6 \u7684\u5bab\u5973\uff0c\u6b63\u5c31\u662f\u90a3\u5929\u665a\u4e0a\u6240\u78b0\u7684\u5230\u7684\u5bab\u5973\u3002\u90a3\u4e48\uff0c\u90a3\u4e2a\u50f5\u5c38\u822c\u5c06\u8981\u65ad\u6c14\u7684\u5973\u4eba\u81ea\u7136\u662f\u6d77\u68e0\u65e0 \u7591\u4e86\u3002\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u5343\u4e07\u601d\u6f6e\uff0c\u5982\u6ce2\u7ffb\u6d6a\u6d8c\uff0c\u5ffd\u7136\u53c8\u90fd\u5e73\u9759\u4e0b\u53bb\uff0c\u51b7\u7985\u7ecf\u5386\u4e86\u4e00\u4e2a\u524d\u6240\u672a\u6709 \u7684\u7a7a\u7075\u5883\u754c\u3002 \u3000\u3000\u4faf\u4e09\u53d8\u89c1\u51b7\u7985\u5140\u7acb\u5982\u50f5\u77f3\uff0c\u773c\u775b\u5982\u5b9a\u73e0\uff0c\u53ea\u9053\u4ed6\u662f\u75db\u6781\u6210\u75af\uff0c\u6025\u5fd9\u62c9\u4ed6\u4e00\u628a\uff0c\u9053\uff1a\u201c\u795d \u5927\u54e5\uff0c\u4f60\u770b\u5f00\u4e00\u70b9\u3002\u201d\u51b7\u7985\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u72d7\u77e2\u6485\uff01\u72d7\u77e2\u6485\uff01\u201d\u4faf\u4e09\u53d8\u60ca\u9053\uff1a\u201c\u5927 \u54e5\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u51b7\u7985\u7b11\u9053\uff1a\u201c\u89e3\u8131\u81ed\u76ae\u56ca\uff0c\u8fd8\u6211\u5e84\u4e25\u76f8\u3002\u81ed\u76ae\u56ca\u4e0e\u5e84\u4e25\u76f8\u539f\u662f\u4e00\u7269\u3002\u8272\u5373 \u662f\u7a7a\uff0c\u7a7a\u5373\u662f\u8272\uff0c\u6211\u5982\u4eca\u65b9\u624d\u61c2\u5f97\u3002\u201d \u3000\u3000\u4faf\u4e09\u53d8\u89c1\u4ed6\u80e1\u8a00\u4e71\u8bed\uff0c\u5fc3\u6025\u5982\u711a\uff0c\u6b63\u60f3\u51fa\u8a00\u6170\u89e3\u3002\u5415\u56db\u5a18\u76c8\u76c8\u4e00\u7b11\uff0c\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u606d\u559c \u5927\u5e08\uff0c\u5999\u609f\u7985\u7406\uff0c\u6b64\u53bb\u7075\u5c71\u662f\u5766\u9014\u4e86\uff01\u201d\u4faf\u4e09\u53d8\u548c\u5510\u6653\u6f9c\u90fd\u6115\u7136\u4e0d\u89e3\uff0c\u5415\u56db\u5a18\u9053\uff1a\u201c\u4f60\u4eec\u4e0d \u8981\u6253\u6270\u4ed6\uff0c\u4ed6\u73b0\u5728\u6bd4\u4ec0\u4e48\u65f6\u5019\uff0c\u5fc3\u4e2d\u90fd\u8981\u660e\u767d\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u535a\u89c8\u7fa4\u4e66\uff0c\u5bf9\u4f5b\u7ecf\u4e5f\u6df1\u6709\u7814\u7a76\u3002\u4f5b\u7ecf\u300a\u71c3\u706f\u5f55\u300b\u4e2d\u8bf4\u8fc7\u4e00\u6bb5\u6545\u4e8b\uff0c\u8bf4\u6709\u4e00\u4e2a\u9ad8\u50e7 \u95ee\u71c3\u706f\u4f5b\u9053\uff1a\u201c\u4f55\u8c13\u53e4\u4f5b\u5fc3\uff1f\u201d\u71c3\u706f\u4f5b\u7b54\u9053\uff1a\u201c\u5e76\u5dde\u841d\u535c\u91cd\u4e09\u65a4\u3002\u201d\u53c8\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u662f \u9053\uff1f\u201d\u71c3\u706f\u8fde\u9053\uff1a\u201c\u72d7\u77e2\u6485\uff0c\u72d7\u77e2\u6485\uff01\u201d\u518d\u95ee\u65f6\uff0c\u71c3\u706f\u4f5b\u7ad6\u8d77\u4e00\u6307\uff0c\u9053\uff1a\u201c\u4e0d\u53ef\u8bf4\u4e0d\u53ef\u8bf4 \u4e86\u3002\u201d\u8fd9\u4e00\u6bb5\u201c\u8bed\u5f55\u201d\u6b63\u662f\u4f5b\u7ecf\u4e2d\u5927\u4e58\u5999\u8c1b\u6240\u5728\uff0c\u610f\u8c13\u771f\u7406\u65e0\u5904\u4e0d\u5728\uff0c\u5728\u6700\u6c61\u79fd\u7684\u4e8b\u7269\u4e2d\uff0c \u4ea6\u53ef\u89c1\u5230\u6700\u5e84\u4e25\u7684\u4e1c\u897f\uff0c\u6240\u4ee5\u8bf4\u4ece\u201c\u72d7\u77e2\u6485\u201d\u4e5f\u53ef\u609f\u9053\u3002\u6c61\u79fd\u4e0e\u5e84\u4e25\u539f\u662f\u5bf9\u7acb\u7684\uff0c\u53ef\u662f\u5728\u6c61 \u79fd\u4e2d\u4e5f\u5b55\u80b2\u7740\u65b0\u751f\u7684\u79cd\u5b50\uff0c\u5c31\u5982\u6bcf\u4e00\u4e2a\u65b0\u7684\u4e16\u754c\u90fd\u662f\u4ece\u65e7\u7684\u4e16\u754c\u4e2d\u8715\u5316\u51fa\u6765\u4e00\u6837\u3002\u51b7\u7985\u60f3\u5230 \u5f53\u5e74\u7eee\u5e74\u7389\u8c8c\u7684\u6d77\u68e0\uff0c\u4e34\u6b7b\u65f6\u5374\u662f\u90a3\u6837\u4e11\u964b\uff0c\u521d\u65f6\u4e0d\u514d\u611f\u89e6\u4e07\u7aef\uff0c\u4f46\u611f\u60c5\u8fc5\u5373\u51c0\u5316\u5347\u534e\uff0c\u987f \u89c9\u7075\u53f0\u660e\u51c0\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u5bab\u5973\u89c1\u4ed6\u4eec\u72b6\u82e5\u75af\u75f4\uff0c\u51b7\u7985\u7684\u6a21\u6837\u4e5f\u4e0d\u50cf\u592a\u76d1\u4eec\u6240\u8c08\u8d77\u7684\u4e86\u56e0\uff0c\u4e8e\u662f\u5c0f\u5fc3\u7ffc\u7ffc\u7684 \u95ee\u9053\uff1a\u201c\u4f60\u4eec\u8ba4\u8bc6\u738b\u961f\u957f\u5417\uff1f\u4ed6\u5c31\u8981\u6765\u4e86\uff0c\u6211\u4eec\u8fd8\u8981\u6253\u626b\u5462\uff01\u201d \u3000\u3000\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u4ec0\u4e48\u738b\u961f\u957f\uff1f\u201d\u5bab\u5973\u7591\u60d1\u9053\uff1a\u201c\u542c\u8bf4\u53eb\u505a\u738b\u9675\uff0c\u4f60\u4eec\u90fd\u662f\u8ddf\u968f\u5723\u4e0a\u7684\u4eba\uff0c \u96be\u9053\u5f7c\u6b64\u4e0d\u77e5\u9053\u5417\uff1f\u201d\u5510\u6653\u6f9c\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u9053\uff1a\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\uff0c\u5fd9\u9053\uff1a \u201c\u8ba4\u5f97\uff0c\u8ba4\u5f97\uff01\u4ed6\u548c\u6211\u662f\u6700\u719f\u4e0d\u8fc7\u7684\u8001\u670b\u53cb\u4e86\uff0c\u4ed6\u8981\u4f4f\u5728\u8fd9\u91cc\u5417\uff1f\u201d\u5bab\u5973\u9053\uff1a\u201c\u5929\u4e00\u4eae\uff0c\u5c31 \u8981\u642c\u6765\uff0c\u6240\u4ee5\u54c8\u603b\u7ba1\u8981\u6211\u4eec\u8fde\u591c\u6253\u626b\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u90a3\u597d\uff0c\u6211\u4eec\u5c31\u5728\u8fd9\u91cc\u5f85\u4ed6\u3002\u201d\u63a8\u5f00\u53a2 \u623f\uff0c\u95ea\u8eab\u5165\u5185\u3002 \u3000\u3000\u4faf\u4e09\u53d8\u7b49\u4e09\u4eba\u8ddf\u7740\u8fdb\u6765\uff0c\u4faf\u4e09\u53d8\u770b\u770b\u5929\u8272\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u5929\u5feb\u4eae\u4e86\uff0c\u4f60\u4eec\u8fd8\u4e0d\u51fa\u53bb\uff1f\u201d \u5415\u56db\u5a18\u4e5f\u89c9\u5510\u6653\u6f9c\u4e3e\u52a8\u5f02\u602a\uff0c\u95ee\u9053\uff1a\u201c\u738b\u9675\u662f\u4ec0\u4e48\u4eba\uff1f\u4f60\u7b49\u4ed6\u5e72\u5417\uff1f\u5929\u4eae\u4e4b\u540e\uff0c\u5c31\u4e0d\u5bb9\u6613\u51fa \u53bb\u4e86\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u5e08\u5144\u3002\u201d\u628a\u738b\u9675\u53db\u5e08\uff0c\u52ab\u593a\u5e08\u5ac2\u7684\u4e8b\u8bf4\u4e86\u3002\u5415\u56db\u5a18\u539f\u542c\u4ed6\u8bf4 \u8fc7\u8fd9\u6bb5\u6545\u4e8b\uff0c\u53ea\u662f\u8bb0\u4e0d\u8d77\u738b\u9675\u7684\u540d\u5b57\uff0c\u542c\u4ed6\u8bf4\u540e\uff0c\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u54b1\u4eec\u5c31\u7d22\u6027\u518d\u5728\u5bab\u4e2d \u803d\u6401\u4e00\u5929\u3002\u201d\u4faf\u4e09\u53d8\u4e5f\u9053\uff1a\u201c\u4e16\u4e0a\u7adf\u7136\u6709\u8fd9\u6837\u5351\u52a3\u7684\u5c0f\u4eba\uff0c\u6211\u8001\u4faf\u4e5f\u653e\u4ed6\u4e0d\u8fc7\uff0c\u201d\u51b7\u7985\u5374\u9ed8 \u4e0d\u4f5c\u58f0\uff0c\u5728\u623f\u5185\u8d70\u6765\u8d70\u53bb\uff0c\u5728\u5c4b\u89d2\u62ff\u8d77\u4e00\u5177\u7476\u7434\uff0c\u94ee\u94ee\u5f39\u4e86\u4e24\u4e0b\uff0c\u5510\u6653\u6f9c\u60f3\u8d77\u5f80\u4e8b\uff0c\u4e0d\u89c9\u6f78 \u7136\u3002\u5bab\u5973\u8fdb\u6765\u9053\uff1a\u201c\u54ce\uff0c\u8fd9\u671b\u8fd8\u6709\u4e00\u5177\u70c2\u7434\uff0c\u629b\u4e86\u5b83\u5427\uff01\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e0d\u5fc5\uff0c\u6211\u66ff\u4f60\u5e26\u5b83\u51fa \u53bb\u597d\u4e86\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u5929\u8272\u6e10\u4eae\u3002\u5916\u9762\u811a\u6b65\u58f0\u54cd\uff0c\u738b\u9675\u548c\u4e24\u4e2a\u536b\u58eb\u8d70\u4e86\u8fdb\u6765\u3002\u539f\u6765\u4ed6\u5728\u5141\u796f\u95e8\u4e0b\uff0c \u5df2\u505a\u5230\u4e86\u4e00\u4e2a\u536b\u58eb\u5c0f\u961f\u957f\u7684\u4f4d\u7f6e\uff0c\u5141\u796f\u767b\u6781\uff0c\u4ed6\u4e5f\u968f\u7740\u8fdb\u5bab\u3002\u8fd9\u65f6\u54c8\u5e03\u9640\u5df2\u664b\u5347\u4e3a\u5bab\u4e2d\u536b\u58eb \u7684\u603b\u7ba1\uff0c\u54c8\u5e03\u9640\u77e5\u9053\u738b\u9675\u7684\u6b66\u529f\u7a00\u677e\u5e73\u5e38\uff0c\u968f\u4fbf\u7ed9\u4ed6\u5b89\u7f6e\u4e86\u4e00\u4e2a\u95f2\u804c\uff0c\u53eb\u4ed6\u5728\u5fa1\u82b1\u56ed\u7684\u4e00\u89d2 \u770b\u5b88\u3002\u5c31\u628a\u539f\u6765\u5c01\u95ed\u4e86\u7684\u51b7\u5bab\uff0c\u6253\u626b\u7ed9\u4ed6\u5c45\u4f4f\u3002\u738b\u9675\u5c45\u7136\u5206\u914d\u5f97\u4e00\u6240\u201c\u5bab\u6bbf\u201d\u5c45\u4f4f\uff0c\u90a3\u7ba1\u5b83 \u51b7\u5bab\u4e0d\u51b7\u5bab\uff0c\u5fc3\u91cc\u5934\u603b\u662f\u5341\u5206\u5f97\u610f\uff0c\u56e0\u6b64\u4e00\u65e9\u5c31\u628a\u884c\u674e\u5e26\u6765\uff0c\u53e6\u5916\u8fd8\u5e26\u4e86\u4e24\u540d\u4ed6\u5c5e\u4e0b\u7684\u536b\u58eb\u3002 \u3000\u3000\u738b\u9675\u8de8\u8fdb\u51b7\u5bab\uff0c\u5148\u95fb\u5230\u4e00\u80a1\u9709\u70c2\u7684\u6c14\u5473\uff0c\u76b1\u8d77\u7709\u5934\uff0c\u559d\u95ee\u5bab\u5973\uff1a\u201c\u600e\u4e48\u8fd8\u672a\u6253\u626b\u5e72 \u51c0\uff1f\u201d\u53c8\u9053\uff1a\u201c\u8fd9\u5899\u58c1\u4e5f\u8be5\u6f06\u4e00\u904d\u4e86\u3002\u201d\u6b63\u81ea\u4f5c\u5a01\u4f5c\u798f\uff0c\u53a2\u623f\u7a81\u7136\u98de\u8eab\u8dc3\u51fa\u4e00\u4eba\uff0c\u5bab\u5973\u6b63\u60f3 \u9053\uff1a\u201c\u738b\u961f\u957f\uff0c\u4f60\u7684\u670b\u53cb\u5728\u6b64\u5019\u4f60\u3002\u201d\u8bdd\u8fd8\u672a\u66fe\u8bf4\u5f97\u51fa\u53e3\uff0c\u738b\u9675\u548c\u4e24\u4e2a\u536b\u58eb\u5df2\u662f\u60e8\u53eb\u8fde\u58f0\uff0c \u5012\u5728\u5730\u4e0a\uff0c\u53ea\u542c\u5f97\u5510\u6653\u6f9c\u51b7\u7b11\u9053\uff1a\u201c\u738b\u5927\u536b\u58eb\uff0c\u522b\u6765\u65e0\u6059\uff1f\u4f60\u73b0\u5728\u5f97\u610f\u4e86\uff0c\u8fd8\u8ba4\u5f97\u6211\u5417\uff1f\u201d \u3000\u3000\u738b\u9675\u7ed9\u5510\u6653\u6f9c\u7a81\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\u70b9\u5012\uff0c\u5413\u5f97\u9b42\u98de\u5929\u5916\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u5510\u5e08\u5f1f\uff0c \u4f60\uff0c\u4f60\uff0c\u2026\u2026\u201d\u5510\u6653\u6f9c\u63d0\u811a\u4e00\u63e3\uff0c\u8e29\u5728\u4ed6\u7684\u808b\u9aa8\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u51af\u5e08\u5ac2\u5462\uff1f\u201d\u738b\u9675\u9053\uff1a\u201c\u4e0d\u5728 \u8fd9\u91cc\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u628a\u5979\u5bb3\u4e86\uff1f\u201d\u738b\u9675\u9053\uff1a\u201c\u611a\u5144\u4e0d\u6562\u3002\u201d\u5510\u6653\u6f9c\u559d\u9053\uff1a\u201c\u8c01\u548c\u4f60\u79f0\u5144 \u9053\u5f1f\uff1f\u5feb\u8bf4\uff0c\u5e08\u5ac2\u73b0\u5728\u54ea\u91cc\uff1f\u201d\u738b\u9675\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u5979\u5728\u54ea\u91cc\u3002\u201d\u5510\u6653\u6f9c\u5927\u6012\uff0c\u811a\u5c16\u5fae\u4e00 \u7528\u529b\uff0c\u738b\u9675\u75db\u5f97\u6b7b\u53bb\u6d3b\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5979\u65e9\u9003\u8d70\u4e86\uff01\u201d\u5510\u6653\u6f9c\u4e0d\u4fe1\uff0c\u53c8\u7528\u529b\u4e00\u63e3\uff0c\u738b\u9675\u60e8\u53eb\u4e00 \u58f0\uff0c\u6655\u4e86\u8fc7\u53bb\u3002\u4e24\u4e2a\u5bab\u5973\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u745f\u7f29\u4e00\u9685\u3002\u5415\u56db\u5a18\u8d70\u4e86\u51fa\u6765\uff0c\u628a\u5979\u4eec\u62bc\u8fdb\u53a2\u623f\uff0c\u5fae \u7b11\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u5413\u4e86\u5979\u4eec\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u738b\u9675\u60a0\u60a0\u9192\u8f6c\uff0c\u5510\u6653\u6f9c\u559d\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u8bf4\u5b9e\u8bdd\u5417\uff1f\u201d\u738b\u9675\u547b\u541f\u9053\uff1a\u201c\u5979\u771f\u7684 \u8d70\u4e86\uff0c\u4f60\u6253\u6b7b\u6211\u6211\u4e5f\u4e0d\u77e5\u9053\u5979\u5728\u54ea\u91cc\uff1f\u201d\u5510\u6653\u6f9c\u89c1\u4ed6\u75db\u5f97\u6b7b\u53bb\u6d3b\u6765\uff0c\u8fd8\u662f\u5982\u6b64\u8bf4\u6cd5\uff0c\u5fc3\u9053\uff1a \u201c\u5e08\u5ac2\u6b66\u529f\u6bd4\u4ed6\u9ad8\u5f3a\uff0c\u771f\u7684\u9003\u8131\u4e86\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u518d\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u8d70\u7684\uff1f\u201d\u738b\u9675\u9053\uff1a\u201c\u5165\u4eac \u4e4b\u540e\u7684\u7b2c\u4e09\u5929\u8d70\u7684\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u6599\u5f97\u4e0d\u9519\u3002\u4ed6\u7684\u5e08\u5ac2\u909d\u7ec3\u971e\u786e\u662f\u56e0\u4e3a\u6b66\u529f\u6bd4\u738b\u9675\u9ad8\u5f3a\uff0c\u5e78\u800c\u9003\u51fa\u864e\u53e3\u3002\u539f\u6765\u5f53\u5e74 \u909d\u7ec3\u971e\u88ab\u53cc\u9b54\u6240\u64d2\uff0c\u4ea4\u7ed9\u738b\u9675\u4e4b\u540e\uff0c\u738b\u9675\u903c\u5979\u6210\u4eb2\uff0c\u5979\u63a8\u8bf4\u8981\u4e3a\u516c\u516c\u548c\u524d\u592b\u5b88\u5b5d\uff0c\u975e\u6ee1\u767e \u65e5\uff0c\u4e0d\u80fd\u6210\u4eb2\u3002\u738b\u9675\u6b66\u529f\u53c8\u4e0d\u53ca\u5979\uff0c\u8fd1\u5979\u4e0d\u5f97\uff0c\u5230\u4e86\u4eac\u57ce\u4e4b\u540e\uff0c\u53cc\u9b54\u8fdb\u4e86\u7687\u5e9c\uff0c\u5c0a\u5351\u4e0d\u540c\uff0c \u804c\u4f4d\u6709\u522b\uff0c\u548c\u738b\u9675\u5206\u5f00\u3002\u909d\u7ec3\u971e\u5728\u8def\u4e0a\u4e0d\u6562\u9003\u8d70\uff0c\u4e43\u662f\u5fcc\u60ee\u53cc\u9b54\uff0c\u53cc\u9b54\u4e0d\u5728\uff0c\u738b\u9675\u4e00\u4eba\uff0c\u90a3 \u662f\u5979\u7684\u5bf9\u624b\uff0c\u7ed9\u5979\u75db\u6253\u4e00\u987f\uff0c\u4fbf\u81ea\u9003\u4e86\u3002 \u3000\u3000\u4faf\u4e09\u53d8\u8d70\u4e86\u51fa\u6765\uff0c\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u8fd8\u672a\u95ee\u5b8c\u5417\uff1f\u5929\u5c31\u8981\u4eae\u4e86\uff01\u201d\u5510\u6653\u6f9c\u4ef0\u5929\u60e8\u7b11\uff1b\u53eb\u9053\uff1a \u201c\u51af\u5e08\u54e5\uff0c\u6211\u4eca\u65e5\u66ff\u4f60\u62a5\u4ec7\u4e86\uff01\u201d\u4e00\u638c\u5288\u53bb\uff0c\u5c06\u738b\u9675\u5929\u7075\u76d6\u9707\u7834\u3002 \u3000\u3000\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u5feb\u8d70\uff0c\u8fdf\u4e9b\u5c31\u6765\u4e0d\u53ca\u4e86\uff01\u201d\u8fd9\u65f6\u66d9\u8272\u521d\u5f00\uff0c\u591c\u96ea\u672a\u5316\uff0c\u56ed\u5b50\u5916\u9762\u54cd\u8d77\u545c\u545c \u7684\u53f7\u89d2\u58f0\uff0c\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u65b0\u603b\u7ba1\u771f\u5356\u529b\uff0c\u5929\u521a\u4eae\u5c31\u53ec\u96c6\u536b\u58eb\u4e86\uff01\u201d\u8dd1\u51fa\u51b7\u5bab\uff0c\u5e26\u5415\u56db\u5a18\u5510\u6653 \u6f9c\u7b49\u4eba\u6025\u8d70\u3002 \u3000\u3000\u54c8\u5e03\u9640\u65b0\u4efb\u5bab\u4e2d\u536b\u58eb\u603b\u7ba1\uff0c\u5934\u4e00\u5929\u4fbf\u95f9\u51fa\u5927\u4e8b\uff0c\u975e\u5e38\u607c\u6012\u3002\u4e8e\u662f\u4e00\u65e9\u53ec\u96c6\u536b\u58eb\uff0c\u51c6\u5907\u6d17 \u5237\u65e7\u4eba\uff0c\u6e05\u9664\u79ef\u5f0a\u3002\u521a\u521a\u5de1\u5230\u82b1\u56ed\uff0c\u5ffd\u89c1\u51e0\u6761\u4eba\u5f71\uff0c\u5411\u897f\u5317\u89d2\u75be\u63a0\u98de\u53bb\uff0c\u524d\u5934\u7684\u4eba\u7adf\u7136\u662f\u5415 \u56db\u5a18\u3002\u54c8\u5e03\u9640\u5927\u6012\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u8d31\u5a62\u597d\u5927\u80c6\uff0c\u5c45\u7136\u6562\u5728\u5bab\u4e2d\u8fc7\u591c\uff0c\u628a\u624b\u4e00\u626c\uff0c\u4e24\u4e2a\u5706\u7403\uff0c\u7834 \u7a7a\u63b7\u51fa\uff0c\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u8840\u6ef4\u5b50\u80fd\u5948\u6211\u4f55\uff01\u201d\u8eab\u5f62\u98de\u8d77\uff0c\u971c\u534e\u5251\u5411\u4e0a\u4e00\u6311\uff0c\u5bd2\u5149\u95ea\u5904\uff0c\u4e00\u5251 \u5c06\u5f53\u5934\u7684\u8840\u6ef4\u5b50\u5288\u5f00\uff0c\u91cc\u9762\u7684\u5341\u4e8c\u628a\u5feb\u5200\uff0c\u56db\u9762\u6fc0\u5c04\uff0c\u5b9b\u5982\u6d12\u4e0b\u4e86\u6ee1\u5929\u5200\u96e8\uff0c\u4faf\u4e09\u53d8\u5b66\u5979\u6837 \u5b50\uff0c\u547c\u7684\u4e00\u62d0\uff0c\u4e5f\u5c06\u4e00\u4e2a\u8840\u6ef4\u5b50\u626b\u53bb\uff0c\u843d\u5230\u536b\u58eb\u4e1b\u4e2d\uff0c\u536b\u58eb\u7eb7\u7eb7\u8eb2\u907f\uff0c\u54c8\u5e03\u9640\u53eb\u9053\uff1a\u201c\u5feb \u8ffd\uff01\u201d\u5415\u56db\u5a18\u7b49\u4eba\u5df2\u8d8a\u8fc7\u51e0\u5ea7\u5047\u5c71\uff0c\u9003\u5230\u4e86\u987a\u8d1e\u95e8\u4e86\u3002 \u3000\u3000\u4faf\u4e09\u53d8\u677e\u4e86\u53e3\u6c14\uff0c\u731b\u7136\u95f4\u53ea\u542c\u5f97\u53f7\u89d2\u5927\u9e23\uff0c\u5de6\u6709\u8463\u5de8\u5ddd\uff0c\u53f3\u6709\u5929\u53f6\u6563\u4eba\uff0c\u7387\u9886\u536b\u58eb\uff0c\u5982 \u98de\u6251\u6765\uff0c\u5415\u56db\u5a18\u53eb\u58f0\uff1a\u201c\u82e6\u4e5f\uff01\u201d\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u8ddf\u6211\u6765\u3002\u201d\u987a\u8d1e\u95e8\u5916\u4fbf\u662f\u666f\u5c71\uff0c\u5b88\u95e8\u7684\u4eba\u6709 \u4e00\u534a\u662f\u5bab\u4e2d\u7684\u8001\u536b\u58eb\uff0c\u4faf\u4e09\u53d8\u8dd1\u4e0a\u524d\u53bb\uff0c\u559d\u9053\uff1a\u201c\u523a\u5ba2\u9003\u51fa\u53bb\u4e86\uff0c\u4f60\u4eec\u89c1\u4e5f\u4e0d\u89c1\uff1f\u201d\u5b88\u95e8\u7684 \u536b\u58eb\u9053\uff1a\u201c\u6ca1\u6709\u5440\uff01\u201d\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u5feb\u5f00\u95e8\uff0c\u5f85\u6211\u53bb\u8ffd\uff01\u201d\u968f\u5141\u796f\u6765\u7684\u65b0\u536b\u571f\u89c1\u4ed6\u4eec\u4e00\u884c\u56db \u4eba\uff0c\u6709\u548c\u5c1a\u53c8\u6709\u5c11\u5973\uff0c\u5341\u5206\u7591\u60d1\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d\u5019\u4e09\u53d8\u9053\uff1a\u201c\u5fa1\u524d\u4f8d\u536b\uff01\u201d \u5728\u5b88\u95e8\u7684\u536b\u58eb\u4e2d\uff0c\u51b7\u7985\u4e5f\u6709\u719f\u4eba\uff0c\u6253\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u6df7\u4e71\u4e2d\u94c1\u95e8\u500f\u7684\u6253\u5f00\uff0c\u4faf\u4e09\u53d8\u7b49\u56db\u4eba\u5982\u98de \u9003\u51fa\u3002\u5230\u8463\u5de8\u5ddd\u8ffd\u5230\u4e4b\u65f6\uff0c\u94c1\u95e8\u53c8\u5df2\u5173\u4e0a\u3002\u5b88\u95e8\u7684\u8001\u536b\u58eb\u67e5\u4ed6\u8eab\u4efd\uff0c\u5230\u67e5\u5f97\u660e\u767d\u4e4b\u65f6\uff0c\u5415\u56db \u5a18\u7b49\u8e2a\u8ff9\u4e5f\u4e0d\u89c1\u4e86\u3002\u54c8\u5e03\u9640\u7a7a\u81ea\u53d1\u6012\uff0c\u5374\u4e5f\u602a\u4e0d\u5f97\u90a3\u5e2e\u5b88\u95e8\u7684\u8001\u536b\u58eb\u3002\u56e0\u4e3a\u5019\u4e09\u53d8\u786e\u662f\u4ee5\u524d \u5728\u5bab\u4e2d\u5f97\u52bf\u7684\u5fa1\u524d\u4f8d\u536b\uff0c\u8c01\u4e5f\u6599\u4e0d\u5230\u8001\u7687\u5e1d\u4e00\u6b7b\uff0c\u4ed6\u4fbf\u7acb\u5373\u53db\u53d8\u3002 \u3000\u3000\u4e14\u8bf4\u5415\u56db\u5a18\u7b49\u4eba\u5f97\u4faf\u4e09\u53d8\u4e4b\u52a9\uff0c\u9003\u51fa\u7981\u5bab\u5927\u5185\uff0c\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5415\u59d0\u59d0\uff0c\u6211\u7684\u5251\u7ed9\u4e86\u56e0\u90a3 \u53ae\u641c\u53bb\u4e86\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4ee5\u540e\u518d\u627e\u4ed6\u7b97\u5e10\u5427\u3002\u54b1\u4eec\u5148\u56de\u53bb\u548c\u4e03\u54e5\u5546\u91cf\u3002\u201d\u4faf\u4e09\u53d8\u4e5f\u9053\uff1a \u201c\u7ecf\u6b64\u4e00\u6218\uff0c\u4ee5\u524d\u90a3\u5e2e\u8001\u536b\u571f\u60f3\u5fc5\u90fd\u4f1a\u88ab\u8d23\u7f6a\u4e86\u3002\u5bab\u4e2d\u4eba\u4e8b\u8c03\u52a8\uff0c\u9632\u5907\u5fc5\u6781\u4e25\u5bc6\uff0c\u54b1\u4eec\u662f\u4e0d \u80fd\u518d\u53bb\u5192\u9669\u4e86\u3002\u201d \u3000\u3000\u56db\u4eba\u56de\u5230\u897f\u5c71\u50e7\u820d\uff0c\u767d\u6cf0\u5b98\u51fa\u6765\u5f00\u95e8\uff0c\u7b11\u9053\uff1a\u201c\u600e\u4e48\u4f60\u4eec\u73b0\u5728\u624d\u56de\u6765\uff0c\u4e03\u54e5\u51e0\u4e4e\u8981\u548c\u5173 \u4e1c\u56db\u4fa0\u5165\u5bab\u53bb\u627e\u4f60\u4eec\u5462\u3002\u201d\u51b7\u7985\u559c\u9053\uff1a\u201c\u5173\u4e1c\u56db\u4fa0\u6765\u4e86\uff1f\u201d\u98de\u5954\u5165\u5185\uff0c\u53ea\u542c\u5f97\u7384\u98ce\u9ad8\u58f0\u53eb \u9053\uff1a\u201c\u795d\u5927\u54e5\uff0c\u6211\u4eec\u627e\u4f60\u6666\u6c14\u6765\u4e86\uff01\u201d\u51b7\u7985\u7b11\u9053\uff1a\u201c\u6211\u5df2\u62ab\u4e0a\u8888\u88df\uff0c\u4f60\u8fd8\u8981\u627e\u6211\u6666\u6c14\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u548c\u5510\u6653\u6f9c\u7b49\u4f9d\u6b21\u548c\u5173\u4e1c\u56db\u4fa0\u89c1\u8fc7\uff0c\u6717\u6708\u7985\u5e08\u9053\uff1a\u201c\u6211\u4eec\u56db\u5144\u5f1f\u8fd9\u6b21\u6298\u5f97\u597d\u60e8\uff0c\u6298 \u5728\u4e00\u4e2a\u5973\u5b69\u5b50\u548c\u4e00\u4e2a\u6c5f\u6e56\u90ce\u4e2d\u624b\u91cc\u3002\u201d\u51b7\u7985\u60ca\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u4eec\u548c\u8c01\u52a8\u624b\u6765\u4e86\uff1f\u201d\u56db\u4fa0\u4e2d\u7684 \u9648\u5143\u9738\u5377\u8d77\u8863\u8896\uff0c\u81c2\u4e0a\u9732\u51fa\u4e00\u9053\u5200\u75d5\uff0c\u9053\uff1a\u201c\u4f60\u77a7\u90a3\u5c0f\u4e2b\u5934\u591a\u6bd2\uff0c\u82e5\u975e\u7384\u98ce\u5927\u54e5\u61c2\u5f97\u533b\u836f\uff0c \u6211\u8fd9\u6761\u80f3\u818a\u7b97\u662f\u5356\u7ed9\u5979\u4e86\uff01\u201d \u3000\u3000\u67f3\u5148\u5f00\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u6b21\u6765\u4eac\uff0c\u5728\u7ecf\u8fc7\u6cb3\u5357\u8363\u9633\u4e4b\u65f6\uff0c\u7384\u98ce\u5927\u54e5\u6709\u4e8b\uff0c\u53eb\u6211\u4eec\u4e09\u4eba\u5148\u8d70\u4e00 \u7a0b\uff0c\u6211\u4eec\u65b9\u5230\u864e\u7262\u5173\uff0c\u5c31\u78b0\u5230\u90a3\u4e2a\u5c0f\u4e2b\u5934\u548c\u4e00\u4e2a\u5c11\u5e74\u540c\u5728\u4e00\u9053\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u54ea\u4e2a\u5c0f\u4e2b\u5934 \u5440\uff1f\u201d\u9648\u5143\u9738\u6068\u6068\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u5141\u796f\u6536\u517b\u7684\u90a3\u4e2a\u5c0f\u4e2b\u5934\u5440\uff0c\u6211\u4eec\u4ee5\u524d\u5927\u95f9\u56db\u7687\u5e9c\u4e4b\u65f6\uff0c\u548c\u5979 \u5bf9\u8fc7\u201c\u76d8\u5b50\u201d\uff08\u89c1\u8fc7\u9762\uff09\u3002\u8fd9\u6b21\u5728\u8def\u4e0a\u78b0\u89c1\uff0c\u6211\u89c1\u5979\u751f\u5f97\u53ef\u7231\uff0c\u8d70\u4e0a\u53bb\u95ee\u5979\uff0c\u90a3\u6599\u5979\u4e00\u6296\u624b \u4fbf\u662f\u4e09\u67c4\u98de\u5200\uff0c\u8ddd\u79bb\u592a\u8fd1\uff0c\u9003\u907f\u4e0d\u53ca\uff0c\u6211\u4ed7\u7740\u4e00\u8eab\u6a2a\u7ec3\u529f\u592b\uff0c\u4f38\u81c2\u6321\u5b83\u3002\u4e0d\u6599\u8fd9\u5973\u5b69\u5b50\u6b66\u529f \u5c45\u7136\u9887\u6709\u6839\u5e95\uff0c\u5176\u4e2d\u4e00\u67c4\u98de\u5200\uff0c\u7adf\u5c06\u6211\u7684\u624b\u81c2\u5212\u7a7f\u4e86\u4e00\u9053\u53e3\u5b50\uff0c\u76ae\u8089\u767b\u65f6\u7600\u9ed1\uff0c\u539f\u6765\u5979\u4f7f\u7528 \u7684\u7adf\u7136\u662f\u5582\u8fc7\u6bd2\u836f\u7684\u98de\u5200\uff01\u201d\u5510\u6653\u6f9c\u53eb\u9053\uff1a\u201c\u5514\uff0c\u90a3\u4e00\u5b9a\u662f\u51af\u7433\u65e0\u7591\u4e86\uff01\u201d\u5415\u56db\u5a18\u5374\u9053\uff1a \u201c\u548c\u4ed6\u4e00\u9053\u7684\u90a3\u4e2a\u5c11\u5e74\u662f\u4e0d\u662f\u957f\u8eab\u7389\u7acb\uff0c\u624b\u4f7f\u5b9d\u5251\uff0c\u5251\u6cd5\u5341\u5206\u602a\u5f02\u7684\u4eba\uff1f\u201d\u67f3\u5148\u5f00\u9053\uff1a\u201c\u6b63 \u662f\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u90a3\u4e48\u8fd9\u5973\u5b69\u5b50\u4e0d\u662f\u51af\u7433\uff0c\u800c\u662f\u5929\u5c71\u6613\u8001\u524d\u8f88\u7684\u5173\u95e8\u5f92\u5f1f\u4e86\u3002\u201d\u5510\u6653\u6f9c \u9053\uff1a\u201c\u51af\u745b\u51fa\u624b\u4e0d\u4f1a\u8fd9\u6837\u6b79\u6bd2\uff0c\u800c\u4e14\u5979\u4e5f\u4e0d\u4f1a\u7528\u5582\u6bd2\u98de\u5200\u3002\u6211\u5728\u5929\u5c71\u65f6\uff0c\u5e38\u5e38\u89c1\u5979\uff0c\u8fd9\u4e2a\u5b69 \u5b50\u7eaf\u826f\u5f97\u5f88\u3002\u201d\u5415\u56db\u5a18\u5927\u4e3a\u7591\u60d1\uff0c\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u8fd9\u4e24\u4e2a\u4f84\u5973\u4e43\u662f\u5b6a\u751f\u59d0\u59b9\uff0c\u90a3\u4e48\u4e00\u5b9a\u76f8\u4f3c \u5f97\u5f88\u4e86\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u8fde\u6211\u4e5f\u5206\u8fa8\u4e0d\u51fa\u6765\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4e00\u5b9a\u662f\u674e\u6cbb\u628a\u59b9\u59b9\u5f53\u4f5c \u59d0\u59d0\u4e86\uff01\u67f3\u5148\u5f00\u9053\uff1a\u201c\u8c01\u662f\u674e\u6cbb\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5929\u5c71\u4e03\u5251\u4e2d\u6b66\u743c\u7476\u7684\u513f\u5b50\u3002\u201d\u67f3\u5148\u5f00\u201c\u554a \u5440\u201d\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u597d\uff1f\u6211\u548c\u4ed6\u5728\u864e\u7262\u5173\u4ea4\u624b\uff0c\u6211\u4e2d\u4e86\u4ed6\u4e00\u5251\uff0c\u4ed6\u4e5f\u4e2d\u4e86\u6211\u4e00 \u8bb0\u94a2\u73af\u3002\u6b66\u8001\u524d\u8f88\u82e5\u77e5\uff0c\u5c82\u4e0d\u602a\u8d23\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6b66\u8001\u524d\u8f88\u665a\u5e74\uff0c\u4fee\u517b\u5df2\u5230\u7089\u706b\u7eaf\u9752\u4e4b \u5883\uff0c\u60f3\u6765\u4e0d\u4f1a\u4e3a\u4f60\u4eec\u7684\u65e0\u5fc3\u4e4b\u9519\u800c\u751f\u6c14\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u67f3\u5927\u4fa0\uff0c\u4f60\u4eec\u5148\u628a\u6545\u4e8b\u8bf4\u5b8c\uff0c\u7136\u540e \u6211\u518d\u544a\u8bc9\u4f60\u8fd9\u5973\u5b69\u5b50\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u67f3\u5148\u5f00\u9053\uff1a\u201c\u56db\u5f1f\u4e2d\u4e86\u90a3\u5c0f\u4e2b\u5934\u7684\u5582\u6bd2\u98de\u5200\u4e4b\u540e\uff0c\u6211\u548c\u90a3\u5c11\u5e74\u52a8\u624b\uff0c\u5404\u81ea\u53d7\u4f24\uff0c\u90a3\u4e2b\u5934 \u8fd8\u60f3\u8ffd\u6765\uff0c\u5e78\u6211\u8f7b\u529f\u8f83\u597d\uff0c\u624d\u80fd\u628a\u56db\u5f1f\u6551\u8d70\u3002\u4ee5\u540e\u7684\u4e8b\uff0c\u8ba9\u7384\u98ce\u5927\u54e5\u8bf4\u5427\u3002\u201d \u3000\u3000\u7384\u98ce\u9053\uff1a\u201c\u6211\u8ba9\u4ed6\u4eec\u5148\u8d70\u4e00\u7a0b\uff0c\u90a3\u6599\u5c31\u51fa\u4e86\u8fd9\u6837\u7684\u4e71\u5b50\u3002\u4ed6\u4eec\u5728\u56de\u7a0b\u4e0a\u78b0\u5230\u4e86\u6211\uff0c\u662f\u6211 \u4e00\u65f6\u6c14\u6124\uff0c\u975e\u5f97\u627e\u7740\u90a3\u4e2b\u5934\u4e0d\u53ef\u3002\u6211\u60f3\u90a3\u5c11\u5e74\u65e2\u7136\u53d7\u4f24\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u53bb\u8fdc\uff0c\u6211\u66ff\u4e09\u5f1f\u56db\u5f1f\u88f9\u597d \u4f24\u53e3\uff0c\u5c31\u5728\u864e\u7262\u5173\u7684\u9644\u8fd1\u5c71\u5934\u904d\u627e\uff0c\u76f4\u5230\u9ec4\u660f\u624d\u53d1\u73b0\u90a3\u4e2a\u5c11\u5e74\uff0c\u53ef\u662f\u90a3\u5c0f\u4e2b\u5934\u5df2\u4e0d\u89c1\u4e86\u3002\u5374 \u6765\u4e86\u4e2a\u9634\u9633\u602a\u6c14\u7684\u6c5f\u6e56\u90ce\u4e2d\uff0c\u771f\u7684\u610f\u6599\u4e0d\u5230\u3002\u201d\u51b7\u7985\u5fc3\u91cc\u6697\u7b11\uff1a\u4ee5\u5173\u4e1c\u56db\u4fa0\u7684\u5a01\u540d\uff0c\u6298\u5728\u4e2a \u5973\u5b69\u5b50\u7684\u624b\u91cc\uff0c\u602a\u4e0d\u5f97\u7384\u98ce\u6c14\u6124\u3002\u53ef\u662f\u78b0\u5230\u6211\u7684\u5e08\u5085\uff0c\u8fd8\u8981\u901e\u80fd\uff0c\u90a3\u5374\u662f\u6028\u8c01\u4e0d\u5f97\u3002\u5148\u4e0d\u8bf4 \u7a7f\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u7384\u98ce\u9053\u957f\uff0c\u600e\u4e48\u610f\u60f3\u4e0d\u5230\u5462\uff1f\u201d \u3000\u3000\u7384\u98ce\u7eed\u9053\uff1a\u201c\u90a3\u5c11\u5e74\u89c1\u5230\u6211\u4eec\uff0c\u5411\u90a3\u6c5f\u6e56\u90ce\u4e2d\u4f4e\u58f0\u8bf4\u4e86\u51e0\u53e5\uff0c\u60f3\u6765\u5b9a\u662f\u544a\u8bc9\u4ed6\uff1a\u6211\u4eec\u4fbf \u662f\u4f24\u4ed6\u7684\u4eba\u3002\u90a3\u6c5f\u6e56\u90ce\u4e2d\u597d\u5927\u706b\u6c14\uff0c\u4e0d\u7b49\u6211\u4eec\u5f00\u53e3\uff0c\u63d0\u8d77\u864e\u6491\u4fbf\u6253\uff0c\u54ce\uff0c\u540e\u6765\u5462\uff0c\u6253\u4e86\u4e00 \u9635\uff0c\u6211\u4eec\u4fbf\u8d70\u4e86\uff01\u201d \u3000\u3000\u51b7\u7985\u5fae\u5fae\u4e00\u7b11\uff0c\u77e5\u9053\u5173\u4e1c\u56db\u4fa0\u4e4b\u4e2d\uff0c\u7384\u98ce\u5e74\u7eaa\u6700\u957f\uff0c\u672c\u9886\u6700\u9ad8\uff0c\u5374\u4e5f\u6700\u4e3a\u597d\u80dc\u3002\u4fbf\u9053\uff1a \u201c\u7384\u98ce\u9053\u957f\u4e0d\u5fc5\u6c14\u607c\uff0c\u6298\u5728\u90a3\u4f4d\u6c5f\u6e56\u90ce\u4e2d\u624b\u91cc\uff0c\u7b97\u4e0d\u4e86\u4ec0\u4e48\u4e00\u56de\u4e8b\u3002\u201d\u7384\u98ce\u6012\u9053\uff1a\u201c\u4f60\u8fd8\u8bf4 \u5462\uff0c\u6211\u770b\u4ed6\u7684\u624b\u6cd5\u4e0e\u4f60\u9887\u4e3a\u76f8\u4f3c\uff0c\u60f3\u6765\u5fc5\u662f\u548c\u4f60\u540c\u4e00\u95e8\u6237\u7684\u4e86\u3002\u201d\u51b7\u7985\u7b11\u9053\uff1a\u201c\u5c82\u53ea\u540c\u4e00\u95e8 \u6237\uff0c\u4ed6\u662f\u6211\u7684\u4e1a\u5e08\uff0c\u5929\u5c71\u6b66\u8001\u524d\u8f88\u5c31\u662f\u4ed6\u7684\u59d0\u59d0\uff0c\u4ed6\u8001\u4eba\u5bb6\u5f97\u7f6a\u4f60\u4eec\uff0c\u53ea\u597d\u7531\u6211\u8fd9\u505a\u5f92\u5f1f\u7684 \u5411\u4f60\u4eec\u8d54\u7f6a\u4e86\u3002\u201d\u7384\u98ce\u5927\u5403\u4e00\u60ca\uff0c\u505a\u58f0\u4e0d\u5f97\u3002\u51b7\u7985\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u4e09\u5341\u5e74\u6765\u672a\u5230\u4e2d\u539f\uff0c\u6240\u4ee5 \u8ba4\u4e0d\u5f97\u4f60\u4eec\u56db\u4f4d\uff0c\u7384\u98ce\u9053\u957f\u4f11\u8981\u751f\u6c14\u3002\u201d\u7384\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u662f\u4ed6\u8001\u4eba\u5bb6\uff0c\u90a3\u6211\u4eec\u6298\u4e86\u8fd8\u6709\u4f55 \u8bdd\u53ef\u8bf4\u3002\u201d\u6717\u6708\u7985\u5e08\u9053\uff1a\u201c\u4ee4\u5e08\u5e74\u7eaa\u4f3c\u4e4e\u6bd4\u4f60\u5927\u4e0d\u4e86\u591a\u5c11\u3002\u201d\u51b7\u7985\u9053\uff1a\u201c\u6211\u662f\u4e2d\u5e74\u4e4b\u540e\u624d\u6295 \u5e08\u7684\u3002\u201d\u7518\u51e4\u6c60\u5374\u9053\uff1a\u201c\u53ef\u60dc\u90a3\u5c0f\u4e2b\u5934\u53c8\u4e0d\u89c1\u4e86\uff0c\u4f60\u4eec\u548c\u6b66\u8001\u524d\u8f88\u52a8\u624b\uff0c\u5979\u4e00\u76f4\u6ca1\u6709\u51fa\u73b0 \u5417\uff1f\u201d\u7384\u98ce\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u539f\u6765\u51af\u7433\u88ab\u5e74\u7fb9\u5c27\u653e\u8d70\u4e4b\u540e\uff0c\u4e00\u5fc3\u60f3\u5b66\u6b63\u5b97\u5185\u529f\uff0c\u665a\u4e0a\u88c5\u795e\u5f04\u9b3c\uff0c\u5077\u5077\u628a\u674e\u6cbb\u5f15\u8d70\uff0c\u674e \u6cbb\u89c1\u5979\u81ea\u7136\u9ad8\u5174\u3002\u51af\u7433\u9053\uff1a\u201c\u4f60\u7684\u8205\u8205\u6709\u4e8b\u5148\u4e0a\u9099\u5c71\uff0c\u53eb\u6211\u548c\u4f60\u8d76\u53bb\u3002\u201d\u6b66\u743c\u7476\u6258\u5f1f\u5f1f\u7167\u987e \u513f\u5b50\uff0c\u539f\u662f\u6697\u4e2d\u7167\u6599\uff0c\u6240\u4ee5\u76f4\u5230\u676d\u5dde\u6076\u6597\u4e86\u56e0\u4e4b\u524d\uff0c\u6b66\u6210\u5316\u90fd\u672a\u66fe\u9732\u9762\u3002\u51af\u7433\u4e71\u8bf4\u4e00\u901a\uff0c\u6070 \u597d\u649e\u4e2a\u6b63\u7740\u3002\u674e\u6cbb\u5fc3\u60f3\uff1a\u5fc5\u7136\u662f\u6bcd\u4eb2\u548c\u8205\u8205\u8981\u6211\u591a\u5728\u6c5f\u6e56\u5386\u7ec3\uff0c\u6240\u4ee5\u8205\u8205\u4e0d\u613f\u548c\u6211\u540c\u884c\u3002\u53c8 \u60f3\u9053\uff1a\u201c\u51af\u745b\u548c\u6211\u8205\u8205\u5f88\u719f\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u9a97\u6211\u3002\u56e0\u6b64\u4fbf\u5fc3\u5b89\u7406\u5f97\u548c\u51af\u7433\u79bb\u5f00\u676d\u5dde\u3002 \u3000\u3000\u4e00\u8def\u4e0a\u51af\u7433\u60f3\u5c3d\u529e\u6cd5\uff0c\u95ee\u4ed6\u5185\u529f\u7a8d\u8981\uff0c\u674e\u6cbb\u53ea\u5f53\u662f\u51af\u745b\uff0c\u6beb\u65e0\u6212\u5907\uff0c\u628a\u81ea\u5df1\u6240\u77e5\u90fd\u544a\u8bc9 \u4e86\u5979\u3002\u51af\u7433\u5bf9\u674e\u6cbb\u65e2\u65e0\u597d\u611f\uff0c\u4ea6\u65e0\u6076\u611f\uff0c\u4e0e\u4ed6\u540c\u884c\uff0c\u552f\u4e00\u76ee\u7684\u4e0d\u8fc7\u662f\u9a97\u4ed6\u4f20\u6388\u5185\u529f\u5fc3\u6cd5\uff0c\u76ee \u7684\u4e00\u8fbe\uff0c\u5fc3\u91cc\u5c31\u6697\u6697\u7b79\u5212\u600e\u6837\u628a\u4ed6\u6487\u5f00\u3002 \u3000\u3000\u65e0\u5de7\u4e0d\u5de7\uff0c\u4ed6\u4eec\u5c06\u5230\u9099\u5c71\u4e4b\u5148\uff0c\u5728\u864e\u7262\u5173\u78b0\u5230\u4e86\u5173\u4e1c\u56db\u4fa0\u4e2d\u7684\u67f3\u5148\u5f00\u548c\u9648\u5143\u9738\uff0c\u51af\u7433\u7528 \u5582\u6bd2\u98de\u5200\u4f24\u4e86\u9648\u5143\u9738\uff0c\u674e\u6cbb\u4e5f\u523a\u4e86\u67f3\u5148\u5f00\u4e00\u5251\uff0c\u53ef\u662f\u67f3\u5148\u5f00\u8f7b\u529f\u4fca\u6781\uff0c\u674e\u6cbb\u731d\u4e0d\u53ca\u9632\uff0c\u4e5f\u4e2d \u4e86\u4ed6\u4e00\u8bb0\u94a2\u73af\uff0c\u6253\u4e2d\u8981\u5bb3\u7a74\u9053\uff0c\u767b\u65f6\u53d7\u4e86\u91cd\u4f24\u3002\u51af\u7433\u5c06\u4ed6\u6276\u5230\u5bc6\u6797\u6df1\u5904\uff0c\u7559\u4e00\u5305\u89e3\u836f\uff0c\u4fbf\u6084 \u6084\u8d70\u4e86\u3002 \u3000\u3000\u5e78\u597d\u6b66\u6210\u5316\u968f\u540e\u8d76\u6765\uff0c\u7ecf\u8fc7\u6797\u8fb9\uff0c\u542c\u5f97\u674e\u6cbb\u547b\u541f\u547c\u5524\uff0c\u8fdb\u53bb\u4e00\u770b\uff0c\u89c1\u4ed6\u53d7\u4e86\u91cd\u4f24\uff0c\u53c8\u62ff \u8d77\u89e3\u836f\u4e00\u770b\uff0c\u89c1\u7adf\u662f\u6781\u5176\u73cd\u8d35\u7684\u73cd\u73e0\u672b\u6cbb\u4f24\u89e3\u6bd2\u6563\uff0c\u7acb\u523b\u66ff\u4ed6\u6577\u4e0a\uff0c\u95ee\u8d77\u6e05\u7531\uff0c\u5341\u5206\u5947\u602a\u3002 \u9053\uff1a\u201c\u5929\u5c71\u7075\u836f\u867d\u591a\uff0c\u6613\u8001\u524d\u8f88\u53ef\u6ca1\u6709\u8fd9\u79cd\u836f\u6563\u3002\u201d\u6b8a\u4e0d\u77e5\u8fd9\u5374\u662f\u51af\u7433\u5728\u56db\u7687\u5e9c\u4e2d\u5e26\u51fa\u6765\u7684 \u5927\u5185\u5723\u836f\u3002\u674e\u6cbb\u4e5f\u8d77\u4e86\u7591\u5fc3\uff0c\u4e24\u8205\u7525\u8fd8\u672a\u8c08\u8bba\u6e05\u695a\uff0c\u5173\u4e1c\u56db\u4fa0\u5df2\u4e00\u9f50\u6765\u5230\u3002 \u3000\u3000\u672c\u6765\u5173\u4e1c\u56db\u4fa0\u5404\u6709\u72ec\u95e8\u6b66\u529f\uff0c\u82e5\u4ee5\u56db\u654c\u4e00\uff0c\u6b66\u6210\u5316\u7eb5\u4e0d\u843d\u8d25\uff0c\u4e5f\u8ba8\u4e0d\u4e86\u4fbf\u5b9c\u3002\u53ef\u662f\u5173\u4e1c \u56db\u4fa0\u4e2d\u67f3\u5148\u5f00\u548c\u9648\u5143\u9738\u90fd\u53d7\u4e86\u4f24\uff0c\u800c\u674e\u6cbb\u6577\u4e86\u5927\u5185\u5723\u836f\u73cd\u73e0\u89e3\u6bd2\u6563\u540e\uff0c\u624b\u81c2\u5df2\u80fd\u6325\u52a8\u81ea\u5982\uff0c \u8205\u7525\u8054\u624b\uff0c\u628a\u5173\u4e1c\u56db\u4fa0\u6740\u5f97\u5927\u8d25\u800c\u9003\uff0c\u8fd8\u5e78\u662f\u6b66\u6210\u5316\u987e\u5728\u674e\u6cbb\u53d7\u4f24\uff0c\u624d\u6ca1\u6709\u8ffd\u8d76\u3002 \u3000\u3000\u5173\u4e1c\u56db\u4fa0\u8bf4\u5b8c\u4e4b\u540e\uff0c\u5415\u56db\u5a18\u4e5f\u628a\u674e\u6cbb\u548c\u51af\u7433\u7684\u6765\u5386\u8bf4\u51fa\u3002\u4f17\u4eba\u4e00\u9635\u60ca\u53f9\u3002\u5510\u6653\u6f9c\u9053\uff1a \u201c\u6211\u53d1\u8a93\u8981\u628a\u4f84\u5973\u5bfb\u56de\uff0c\u65e2\u77e5\u5979\u7684\u8e2a\u8ff9\uff0c\u6211\u5230\u6cb3\u5357\u8d70\u4e00\u8d9f\u5427\u3002\u201d\u7518\u51e4\u6c60\u60f3\u4e86\u4e00\u9635\uff0c\u8bf4\u9053\uff1a \u201c\u4f60\u5148\u56de\u5230\u5141\u5824\u519b\u4e2d\u3002\u6211\u6599\u5141\u796f\u767b\u6781\u4e4b\u540e\uff0c\u5fc5\u4e0d\u8bb8\u5141\u5824\u4e45\u7559\u4eac\u57ce\uff0c\u82e5\u4ed6\u518d\u7edf\u5175\u897f\u5f81\uff0c\u6cb3\u5357\u662f \u5fc5\u7ecf\u4e4b\u5730\u3002\u4f60\u5230\u519b\u4e2d\uff0c\u770b\u6709\u65e0\u53ef\u4e58\u4e4b\u673a\uff0c\u8ba9\u4ed6\u4eec\u5144\u5f1f\u5927\u6253\u4e00\u573a\u3002\u7eb5\u4e0d\u6210\u4e5f\u53ef\u5ba0\u7edc\u519b\u4e2d\u4e00\u4e9b\u6709 \u8840\u6027\u7684\u6c49\u65cf\u7537\u513f\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u8bf4\u9053\uff1a\u201c\u672c\u6765\u6211\u4eec\u5e94\u805a\u96c6\u4e49\u6c11\uff0c\u81ea\u4e3e\u4e49\u65d7\u3002\u4e0d\u8fc7\uff0c\u4f60\u65e2\u7136\u8d39 \u4e86\u8bb8\u591a\u5fc3\u8840\uff0c\u624d\u53d6\u5f97\u5141\u5824\u4fe1\u4efb\uff0c\u653e\u5f03\u8fd9\u4e00\u673a\u4f1a\uff0c\u4e5f\u672a\u514d\u53ef\u60dc\u3002\u201d\u5415\u56db\u5a18\u773c\u73e0\u4e00\u8f6c\uff0c\u6b63\u60f3\u8bf4 \u8bdd\uff0c\u7518\u51e4\u6c60\u5df2\u7b11\u7740\u7eed\u9053\uff1a\u201c\u516b\u59b9\u60f3\u662f\u6015\u5510\u8d24\u5f1f\u5355\u8eab\u9677\u5728\u519b\u4e2d\uff0c\u8fd9\u4e2a\u867d\u7136\u4e0d\u65e0\u5371\u9669\uff0c\u4f46\u5c31\u662f\u5728 \u5141\u5824\u519b\u4e2d\uff0c\u6211\u4e5f\u8fd8\u6709\u4e9b\u5e2e\u4f1a\u5144\u5f1f\u3002\u800c\u4e14\uff0c\u6211\u4eec\u6253\u542c\u5f97\u5141\u796f\u4f55\u65e5\u51fa\u53d1\u4e4b\u540e\uff0c\u6211\u4eec\u4e5f\u53ef\u5206\u6279\u8d76\u5f80 \u6cb3\u5357\u3002\u201d\u5415\u56db\u5a18\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u6211\u4eec\u4e5f\u8be5\u4e0a\u9099\u5c71\u796d\u626b\u5e08\u5085\u7684\u5893\u4e86\uff0c\u626b\u5893\u4e4b\u540e\uff0c\u6211\u518d \u56de\u4ed9\u971e\u5cad\u5427\u3002\u201d \u3000\u3000\u5141\u796f\u5fc3\u601d\u88ab\u7518\u51e4\u6c60\u6599\u4e2a\u6b63\u7740\uff0c\u4ed6\u679c\u7136\u4e0d\u613f\u5141\u5824\u4e45\u7559\u4eac\u5e08\u3002\u90a3\u65e5\u5141\u5824\u5f85\u5141\u4fa6\u767b\u4f4d\u4e4b\u540e\uff0c\u6ee1 \u8154\u6c14\u6124\uff0c\u8fde\u591c\u8d76\u56de\u519b\u4e2d\uff0c\u60f3\u4e0d\u5230\u5e74\u7fb9\u5c27\u5df2\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6bb5\uff0c\u524a\u5f31\u4e86\u4ed6\u7684\u5175\u6743\u3002\u539f\u6765\u5e74 \u7fb9\u5c27\u4e00\u5230\uff0c\u4fbf\u5c06\u5141\u5824\u7684\u4e8c\u5341\u516b\u4e2a\u8425\u7684\u5e26\u5175\u519b\u5b98\u53ec\u96c6\u4e86\u6765\uff0c\u5ba3\u5e03\u5141\u796f\u5373\u4f4d\uff0c\u8981\u4ed6\u4eec\u6548\u5fe0\u3002\u8fd9\u4e9b \u5e26\u5175\u5c06\u9886\u867d\u7136\u90fd\u662f\u5141\u5824\u7684\u5fc3\u8179\uff0c\u76fc\u671b\u5141\u5824\u80fd\u591f\u767b\u57fa\uff0c\u53ef\u662f\u4e00\u542c\u5230\u5141\u796f\u5df2\u5750\u4e0a\u5b9d\u5ea7\uff0c\u8fc7\u534a\u6570\u7684 \u519b\u5b98\u90fd\u53d8\u4e86\u5fc3\uff0c\u4f46\u6c42\u80fd\u4fdd\u81ea\u5df1\u529f\u540d\u5229\u7984\uff0c\u5df2\u662f\u4e07\u5e78\uff0c\u90a3\u8fd8\u6562\u840c\u53cd\u53db\u4e4b\u5fc3\u3002\u5230\u4e86\u9ec4\u660f\u65f6\u5206\uff0c\u5141 \u796f\u767b\u4f4d\u6539\u5143\u201c\u96cd\u6b63\u201d\u7684\u5927\u8bcf\u5df2\u6b63\u5f0f\u9881\u5e03\uff0c\u8fde\u5141\u5824\u6700\u53ef\u9760\u7684\u98de\u9f99\u519b\u4e2d\u7684\u5341\u4e8c\u4e2a\u8425\u7684\u7edf\u5175\u5b98\u4e5f\u52a8 \u6447\u4e86\uff01 \u3000\u3000\u5141\u5824\u56de\u5230\u519b\u4e2d\uff0c\u8fde\u591c\u53ec\u96c6\u5fc3\u8179\u5c06\u9886\u4f1a\u5546\uff0c\u5341\u4e8c\u4e2a\u98de\u9f99\u519b\u7684\u7edf\u5175\u5bab\u4e2d\u6709\u4e03\u4e2a\u4e0d\u8d5e\u6210\u4e0e\u5e74\u7fb9 \u5c27\u4f5c\u5bf9\uff0c\u5141\u5824\u526f\u624b\u535a\u514b\u56fe\u4e5f\u9053\uff1a\u201c\u56db\u8d1d\u52d2\u5df2\u767b\u4e86\u5927\u5b9d\uff0c\u5e74\u7fb9\u5c27\u631f\u5929\u5b50\u4ee5\u4ee4\u8bf8\u4faf\uff0c\u6211\u4eec\u82e5\u8981\u9664 \u4ed6\uff0c\u53ea\u6050\u519b\u5fc3\u4e0d\u9644\u3002\u201d\u5141\u5824\u9ed8\u7136\u4e0d\u8bed\uff0c\u5fc3\u60f3\uff1a\u6700\u4eb2\u4fe1\u7684\u5c06\u9886\u90fd\u662f\u5982\u6b64\u8bf4\u6cd5\uff0c\u5176\u4ed6\u5404\u8425\u7684\u7edf\u5175 \u519b\u5b98\u66f4\u4e0d\u4fbf\u4e0e\u4ed6\u4eec\u5546\u8bae\u4e86\u3002\u53c8\u60f3\u9053\uff1a\u201c\u7559\u5f97\u9752\u5c71\u5728\u4e0d\u6015\u6ca1\u67f4\u70e7\uff0c\u5e74\u7fb9\u5c27\u518d\u5f3a\u4e5f\u4e0d\u8fc7\u662f\u6211\u7684\u526f \u5c06\uff0c\u5175\u6743\u4e14\u8fd8\u5728\u6211\u7684\u624b\u4e2d\uff0c\u6211\u4fbf\u6682\u65f6\u5fcd\u8010\u4e0b\u6765\uff0c\u5c06\u6765\u51fa\u5f81\u897f\u57df\uff0c\u6211\u5927\u53ef\u4ee5\u7528\u501f\u5200\u6740\u4eba\u4e4b\u8ba1\uff0c \u628a\u5e74\u7fb9\u5c27\u7684\u519b\u961f\u6d3e\u53bb\u6253\u524d\u950b\uff0c\u8ba9\u654c\u4eba\u628a\u4ed6\u6d88\u706d\u3002\u90a3\u65f6\u79bb\u4eac\u4e07\u91cc\uff0c\u6211\u7eb5\u4e0d\u80fd\u4e89\u593a\u5927\u5b9d\uff0c\u4e5f\u53ef\u62e5 \u5175\u81ea\u56fa\uff0c\u897f\u57df\u4e3a\u738b\uff0c\u7701\u5f97\u53d7\u5141\u796f\u90a3\u53ae\u6c14\u3002\u4e3b\u610f\u6253\u5b9a\uff0c\u4fbf\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5c31\u8ba9\u5e74\u7fb9\u5c27\u8fd9\u5c0f\u5b50 \u505a\u4ed6\u7684\u4ec0\u4e48\u526f\u5c06\u519b\u5427\uff0c\u4f46\u54b1\u4eec\u53ef\u5f97\u591a\u9632\u5907\u4ed6\u3002\u201d\u4f17\u519b\u5b98\u6563\u540e\uff0c\u5141\u5824\u518d\u4e0e\u535a\u514b\u56fe\u5546\u91cf\uff0c\u535a\u514b\u56fe \u4e5f\u8d5e\u6210\u6b64\u8bae\uff0c\u7b2c\u4e8c\u65e5\u5141\u5824\u4fbf\u7acb\u523b\u4e0a\u758f\u5165\u594f\uff0c\u8bf7\u6c42\u7ee7\u7eed\u897f\u5f81\uff0c\u4e0a\u4e86\u594f\u7ae0\uff0c\u4e2d\u519b\u4e2d\u62a5\u5510\u6653\u6f9c\u6c42 \u89c1\u3002\u5141\u5824\u5927\u559c\uff0c\u4f20\u4ed6\u8fdb\u5e10\uff0c\u8bf4\u9053\uff1a\u201c\u60a3\u96be\u89c1\u4eba\u5fc3\uff0c\u4f60\u5728\u6211\u5931\u52bf\u4e4b\u65f6\uff0c\u5c1a\u6765\u5f52\u6211\uff0c\u6211\u5fc5\u5b9a\u4e0d\u4e8f \u5f85\u4f60\u3002\u201d\u53c8\u95ee\u5510\u6653\u6f9c\u600e\u80fd\u9003\u8131\u3002\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6628\u665a\u5bab\u4e2d\u5927\u95f9\u523a\u5ba2\uff0c\u6211\u4e58\u7740\u6df7\u4e71\u4fbf\u9003\u51fa\u6765 \u4e86\u3002\u201d\u5141\u5824\u5fc3\u60f3\u90a3\u4e9b\u523a\u5ba2\u662f\u5176\u4ed6\u7687\u5b50\u6240\u6d3e\u7684\uff0c\u90a3\u6837\u7cbe\u660e\u7684\u4eba\u7adf\u7136\u4e0d\u8d77\u4ec0\u4e48\u7591\u5fc3\uff0c\u4fbf\u628a\u5510\u6653\u6f9c \u63d0\u5347\u4e3a\u8fd1\u536b\u519b\u7684\u526f\u603b\u5175\u5b98\uff0c\u548c\u8f66\u8f9f\u90aa\u65b9\u4eca\u660e\u4e8c\u4eba\u5e76\u5217\u3002 \u3000\u3000\u5141\u796f\u63a5\u4e86\u5141\u5824\u594f\u758f\uff0c\u6b63\u5408\u5fc3\u610f\uff0c\u4f20\u4e0b\u5723\u65e8\uff0c\u53eb\u4ed6\u8fc7\u4e86\u65b0\u5e74\uff0c\u4fbf\u7acb\u523b\u7edf\u5175\u897f\u5f81\uff0c\u62e7\u7531\u6b64\u800c \u60f3\u5230\u5904\u7f6e\u4e86\u56e0\u7b49\u4eba\u7684\u529e\u6cd5\uff0c\u628a\u4e86\u56e0\u3001\u8428\u5929\u90fd\u3001\u8428\u5929\u523a\u3001\u8463\u5de8\u5ddd\u3001\u7518\u5929\u9f99\u7b49\u4e94\u4eba\uff0c\u8c03\u5230\u5e74\u7fb9\u5c27 \u519b\u4e2d\uff0c\u53eb\u4ed6\u4eec\u5e2e\u52a9\u5e74\u7fb9\u5c27\u897f\u5f81\uff0c\u6697\u4e2d\u5374\u4f20\u89c1\u5e74\u7fb9\u5c27\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e94\u4eba\u4e2d\u9664\u4e86\u8463\u5de8\u5ddd\u8f83\u4e3a\u61c2\u5f97 \u5927\u4f53\u5916\uff0c\u5176\u4ed6\u56db\u4eba\u90fd\u662f\u91ce\u6027\u96be\u9a6f\uff0c\u5230\u897f\u5f81\u4e4b\u540e\uff0c\u82e5\u8fd9\u56db\u4eba\u672a\u6b7b\uff0c\u4f60\u5c31\u66ff\u6715\u9664\u4e86\u4ed6\u4eec\u5427\u3002\u201d\u5e74 \u7fb9\u5c27\u5fc3\u4e2d\u4e00\u51db\uff0c\u6bdb\u9aa8\u7686\u5bd2\uff0c\u518d\u4e00\u7ec6\u60f3\uff0c\u53c8\u89c9\u5f97\u8fd9\u662f\u5141\u796f\u5ba0\u4fe1\u81ea\u5df1\u7684\u8868\u793a\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u614c\u5fd9\u53e9 \u5934\u63a5\u65e8\u3002\u4ed6\u5374\u7edd\u672a\u60f3\u5230\uff0c\u5728\u53e6\u4e00\u65b9\u9762\uff0c\u5141\u796f\u4e5f\u5429\u5490\u8fd9\u4e94\u4e2a\u4eba\uff0c\u53eb\u4ed6\u4eec\u76d1\u89c6\u5e74\u7fb9\u5c27\u3002\u8fd9\u6b63\u662f\u5141 \u796f\u9a7e\u5fa1\u529f\u81e3\u7684\u624b\u6cd5\u3002 \u3000\u3000\u8f6c\u773c\u8fc7\u4e86\u65b0\u5e74\uff0c\u5141\u5824\u4ee5\u629a\u8fdc\u5927\u5c06\u519b\u7684\u540d\u4e49\uff0c\u7edf\u5175\u897f\u5f81\uff0c\u5e74\u7fb9\u5c27\u5219\u662f\u526f\u5927\u5c06\u519b\uff0c\u5e73\u7a7a\u5347\u4e86 \u4e09\u7ea7\uff0c\u5141\u796f\u53c8\u53e6\u8c03\u4e94\u4e07\u7cbe\u5175\u7ed9\u5e74\u7fb9\u5c27\u7edf\u7387\uff0c\u6240\u4ee5\u5e74\u7fb9\u5c27\u867d\u5c5e\u526f\u804c\uff0c\u5b9d\u9645\u4e0a\u548c\u5141\u5824\u5206\u5ead\u6297\u4e49\uff0c \u5f7c\u6b64\u63d0\u9632\u3002 \u3000\u3000\u5927\u519b\u5f00\u884c\uff0c\u4e00\u6708\u4e4b\u540e\uff0c\u5df2\u5230\u6cb3\u5357\u6731\u4ed9\u9547\uff0c\u79bb\u5e74\u7fb9\u5c27\u7684\u6545\u4e61\u9648\u7559\uff0c\u4e0d\u8fc7\u4e00\u65e5\u8def\u7a0b\uff0c\u5e74\u7fb9\u5c27 \u4e0b\u4ee4\u5927\u519b\u5728\u6b64\u6b47\u606f\u4e09\u65e5\u3002\u8fd9\u65e5\uff0c\u5510\u6653\u6f9c\u548c\u51e0\u4e2a\u8fd1\u536b\u519b\u7684\u4e2d\u4e0b\u519b\u5b98\u5230\u9547\u4e0a\u559d\u9152\uff0c\u5728\u9152\u697c\u4e0a\u5317\u671b \u5f00\u5c01\uff0c\u5357\u671b\u8bb8\u660c\uff0c\u5f62\u52bf\u679c\u7136\u9669\u8981\uff0c\u60f3\u8d77\u8fd9\u4e43\u662f\u5cb3\u6b66\u7a46\u5f53\u5e74\u5927\u7834\u91d1\u5175\u7684\u5730\u65b9\uff0c\u611f\u6168\u4e07\u7aef\uff0c\u5fc3\u60f3 \u6ee1\u6d32\u5165\u5173\uff0c\u66b4\u8650\u66f4\u8fc7\u4e8e\u5f53\u5e74\u7684\u91d1\u5175\u4fb5\u5b8b\uff0c\u53c8\u60f3\u8d77\u81ea\u5df1\u4ee5\u201c\u6ee1\u4eba\u201d\u7684\u8eab\u4efd\uff0c\u5374\u53c2\u4e0e\u6c49\u4eba\u590d\u56fd\u4e4b \u4e1a\uff0c\u4e5f\u771f\u662f\u610f\u6599\u4e0d\u5230\u3002\u6b63\u5728\u80e1\u601d\u4e71\u60f3\uff0c\u5ffd\u542c\u5f97\u697c\u4e0b\u4e71\u54c4\u54c4\u95f9\u6210\u4e00\u7247\u3002 \u3000\u3000\u5510\u6653\u6f9c\u4e0b\u697c\u53bb\u770b\uff0c\u53ea\u89c1\u5c0f\u8d29\u884c\u4eba\uff0c\u7eb7\u7eb7\u8d70\u907f\u3002\u5510\u6653\u6f9c\u62c9\u7740\u4e00\u4e2a\u884c\u4eba\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48 \u4e8b\uff1f\u201d\u90a3\u4eba\u89c1\u5510\u6653\u6f9c\u519b\u5b98\u6253\u626e\uff0c\u53eb\u9053\uff1a\u201c\u5927\u4eba\u9976\u6055\uff0c\u5c0f\u7684\u4e16\u4ee3\u5949\u516c\u5b88\u6cd5\uff0c\u4e0d\u662f\u6b79\u5f92\u3002\u201d\u5510\u6653 \u6f9c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u90a3\u4eba\u89c1\u5510\u6653\u6f9c\u6001\u5ea6\u548c\u5584\uff0c\u7a0d\u7a0d\u653e\u5fc3\uff0c\u9053\uff1a\u201c\u9547\u5916\u6765\u4e86\u4e00\u5927\u961f\u5b98\u5175\uff0c\u90a3 \u4eba\u8981\u6355\u62ff\u4eba\u72af\u3002\u201d\u5510\u6653\u6f9c\u677e\u5f00\u4e86\u624b\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5374\u5947\u4e86\uff0c\u884c\u519b\u4e4b\u4e2d\uff0c\u600e\u4f1a\u6355\u62ff\u4eba\u72af\uff1f\u82e5\u8bf4\u662f \u6563\u5175\u5230\u9547\u4e0a\u9a9a\u6270\uff0c\u5219\u5141\u5824\u548c\u5e74\u7fb9\u5c27\u90fd\u6cbb\u519b\u6781\u4e25\uff0c\u519b\u7eaa\u8fdc\u975e\u5176\u4ed6\u5b98\u5175\u53ef\u6bd4\uff0c\u800c\u4e14\u5927\u519b\u9a7b\u5728\u9547 \u5916\uff0c\u9664\u4e86\u519b\u5b98\u4e4b\u5916\uff0c\u5175\u58eb\u4e0d\u51c6\u5165\u57ce\uff0c\u90a3\u4e48\u8fd9\u961f\u5b98\u5175\u5230\u5e95\u662f\u4ece\u4f55\u6765\u7684\uff1f\u201d\u6b63\u5728\u601d\u7591\uff0c\u9547\u5916\u5c18\u7802 \u6f2b\u5929\uff0c\u4eba\u6f6e\u8d8a\u53d1\u6c79\u6d8c\uff0c\u5510\u6653\u6f9c\u8eab\u4e0d\u7531\u5df1\uff0c\u7ed9\u4eba\u6f6e\u63a8\u7740\u884c\u4e86\u51e0\u6b65\uff0c\u5ffd\u7136\u88ab\u4eba\u91cd\u91cd\u78b0\u4e00\u4e0b\uff0c\u5510\u6653 \u6f9c\u7ec3\u6b66\u591a\u5e74\uff0c\u611f\u89c9\u7075\u654f\uff0c\u7ed9\u4eba\u4e00\u78b0\uff0c\u987f\u89c9\u6709\u5f02\uff0c\u4e00\u6478\u8eab\u4e0a\uff0c\u94f6\u5305\u4f69\u5251\u548c\u5eb7\u7199\u7ed9\u4ed6\u7684\u90a3\u5757\u6c49\u7389 \u90fd\u4e0d\u89c1\u4e86\u3002\u94f6\u5305\u5012\u4e0d\u6253\u7d27\uff0c\u5219\u4f69\u5251\u4e43\u662f\u5141\u5824\u6240\u9001\uff0c\u5374\u975e\u8ffd\u56de\u4e0d\u53ef\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u5728\u4eba\u4e1b\u4e2d\u51b2 \u51fa\uff0c\u53ea\u89c1\u524d\u9762\u4e00\u4eba\u8d3c\u5fd2\u5fd2\u7684\u5411\u81ea\u5df1\u77aa\u773c\uff0c\u81ea\u5df1\u7684\u4f69\u5251\u7ed9\u4ed6\u6302\u5728\u8170\u65c1\uff0c\u6c49\u7389\u5374\u62ff\u5728\u624b\u4e2d\u6447\u6447\u6446 \u6446\u3002\u5510\u6653\u6f9c\u5927\u4e3a\u751f\u6c14\uff0c\u62d4\u6b65\u8ffd\u53bb\uff0c\u90a3\u4eba\u597d\u751f\u5947\u602a\uff0c\u5e76\u4e0d\u6df7\u8fdb\u4eba\u5806\u4e4b\u4e2d\uff0c\u5374\u4e13\u62e3\u4eba\u5c11\u5904\u98de\u9003\uff0c \u5510\u6653\u6f9c\u7591\u5fc3\u5927\u8d77\uff0c\u7d27\u7d27\u8ffd\u8e2a\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u8ffd\u51fa\u9547\u5916\u3002 \u3000\u3000\u90a3\u4eba\u8d8a\u8dd1\u8d8a\u5feb\uff0c\u65b9\u5411\u548c\u9a7b\u519b\u4e4b\u5730\u76f8\u53cd\uff0c\u5510\u6653\u6f9c\u7cbe\u795e\u9661\u632f\uff0c\u65bd\u5c55\u8d77\u9646\u5730\u98de\u817e\u7684\u4e0a\u4e58\u8f7b\u529f\uff0c \u7535\u9010\u98ce\u9a70\uff0c\u8d8a\u8ffd\u8d8a\u8fdc\u3002\u5510\u6653\u6f9c\u65bd\u5c55\u4e86\u5168\u526f\u672c\u9886\uff0c\u59cb\u7ec8\u8ffd\u4ed6\u4e0d\u4e0a\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8981\u77e5\u5510\u6653 \u6f9c\u7684\u8f7b\u529f\u867d\u7136\u672a\u5982\u5415\u56db\u5a18\u4e4b\u767b\u5cf0\u9020\u6781\uff0c\u4f46\u5728\u6c5f\u6e56\u4e0a\u5df2\u4e0d\u53ef\u591a\u89c1\uff0c\u8fd9\u4eba\u8f7b\u529f\u9020\u8be3\u5728\u5510\u6653\u6f9c\u4e4b \u4e0a\uff0c\u770b\u6765\u548c\u201c\u4e07\u91cc\u8ffd\u98ce\u201d\u67f3\u5148\u5f00\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u663e\u7136\u4e0d\u662f\u666e\u901a\u7684\u5c0f\u5077\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6545\u610f\u653e\u7f13\u811a\u6b65\uff0c\u90a3\u4eba\u597d\u50cf\u80cc\u540e\u957f\u7740\u773c\u775b\u4f3c\u7684\uff0c\u811a\u6b65\u4e5f\u8ddf\u7740\u7f13\u6162\u4e0b\u6765\uff0c \u5510\u6653\u6f9c\u53eb\u9053\uff1a\u201c\u524d\u9762\u8fd9\u4f4d\u670b\u53cb\uff0c\u54b1\u4eec\u7d20\u5473\u5e73\u751f\uff0c\u4f55\u6545\u76f8\u620f\uff1f\u201d\u90a3\u4eba\u56de\u5934\u4f5c\u4e86\u4e00\u4e2a\u9b3c\u8138\uff0c\u81ea\u8a00 \u81ea\u8bed\u9053\uff1a\u201c\u8fd9\u628a\u5251\u5f53\u70c2\u94dc\u70c2\u94c1\u5356\u53ef\u503c\u4e0d\u4e86\u51e0\u4e2a\u94b1\uff0c\u8fd9\u5757\u7389\u5012\u53ef\u4ee5\u5356\u4e09\u51e0\u4e24\u94f6\u5b50\uff01\u201d\u5510\u6653\u6f9c\u7a81 \u7136\u98de\u8eab\u63a0\u8d77\uff0c\u4e00\u6293\u5411\u4ed6\u6293\u53bb\uff0c\u90a3\u4eba\u53eb\u9053\uff1a\u201c\u54ce\u54df\uff0c\u4e0d\u597d\uff01\u201d\u80a9\u5934\u4e00\u52a8\uff0c\u51b2\u51fa\u6570\u4e08\u4ee5\u5916\uff0c\u7b11 \u9053\uff1a\u201c\u8fd8\u597d\uff0c\u672a\u66fe\u5931\u53bb\uff01\u201d\u5510\u6653\u6f9c\u6293\u4e86\u51e0\u5757\u788e\u77f3\uff0c\u7528\u8fde\u73e0\u5f39\u624b\u6cd5\u5411\u4ed6\u53d1\u53bb\u3002\u56e0\u4e3a\u6478\u4e0d\u6e05\u7684\u6765 \u8def\uff0c\u6240\u4ee5\u5e76\u4e0d\u5b58\u5fc3\u6253\u4ed6\u3002\u53ea\u7528\u4e86\u51e0\u5206\u529b\u91cf\uff0c\u788e\u77f3\u4e5f\u6545\u610f\u79bb\u4ed6\u5934\u9876\u51e0\u5bf8\uff0c\u76ee\u7684\u4e0d\u8fc7\u662f\u60f3\u5413\u4e00\u5413 \u4ed6\uff0c\u90a3\u4eba\u5374\u7a81\u7136\u5411\u4e0a\u4e00\u7eb5\uff0c\u788e\u77f3\u521a\u597d\u5f39\u5728\u4ed6\u7684\u540e\u8111\uff0c\u535c\u535c\u6709\u58f0\uff0c\u5f39\u4e86\u5f00\u53bb\uff0c\u90a3\u4eba\u62b1\u5934\u53eb\u9053\uff1a \u201c\u597d\u5389\u5bb3\u7684\u6355\u5feb\u554a\uff01\u6211\u53ef\u771f\u8981\u9003\u4e86\uff01\u201d\u811a\u6b65\u4e00\u7d27\uff0c\u8dd1\u5f97\u66f4\u5feb\uff01 \u3000\u3000\u5510\u6653\u6f9c\u8d8a\u53d1\u60ca\u5947\uff0c\u5fc3\u60f3\uff1a\u8fd9\u6837\u7684\u9ad8\u4eba\uff0c\u4e0d\u80fd\u9519\u8fc7\uff0c\u53eb\u9053\uff1a\u201c\u524d\u9762\u8fd9\u4f4d\u82f1\u96c4\uff0c\u4ffa\u7518\u62dc\u4e0b \u98ce\uff0c\u8bf7\u505c\u6b65\u76f8\u89c1\uff01\u201d\u90a3\u4eba\u7406\u4e5f\u4e0d\u7406\uff0c\u4ecd\u7136\u98de\u8dd1\u3002\u5510\u6653\u6f9c\u6c14\u9053\uff1a\u90a3\u6709\u8fd9\u6837\u4e0d\u901a\u60c5\u7406\u7684\u4eba\uff1f\u4e5f\u52a0 \u7d27\u811a\u6b65\uff0c\u5411\u524d\u6025\u8ffd\uff0c\u8ffd\u4e86\u4e00\u9635\uff0c\u8ffd\u5165\u4e86\u4e00\u5ea7\u5c71\u4e2d\u3002 \u3000\u3000\u5510\u6653\u6f9c\u8ba1\u7b97\u811a\u7a0b\uff0c\u79bb\u6731\u4ed9\u9547\u5927\u7ea6\u4e5f\u6709\u4e8c\u4e09\u5341\u91cc\u4e86\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u83ab\u975e\u662f\u6545\u610f \u5f15\u6211\u6765\u6b64\uff1f\u90a3\u4eba\u811a\u6b65\u4e00\u7f13\uff0c\u5510\u6653\u6f9c\u773c\u775b\u500f\u4eae\uff0c\u5df2\u8fdb\u5165\u4e00\u4e2a\u5c71\u8c37\u4e4b\u4e2d\uff0c\u8c37\u4e2d\u904d\u5730\u79ef\u96ea\uff0c\u94f6\u5149\u6cfb \u5730\uff0c\u8c37\u4e2d\u6709\u4e00\u5ea7\u8305\u5c4b\uff0c\u5510\u6653\u6f9c\u505c\u4e86\u4e0b\u6765\uff0c\u4e0d\u6562\u5192\u8fdb\u3002\u90a3\u4eba\u56de\u8fc7\u5934\u6765\uff0c\u628a\u624b\u4e00\u626c\uff0c\u4e00\u4ef6\u4e1c\u897f\u5288 \u9762\u6253\u5230\uff0c\u5510\u6653\u6f9c\u4f38\u624b\u63a5\u8fc7\uff0c\u5374\u539f\u6765\u662f\u81ea\u5df1\u7684\u4f69\u5251\uff0c\u90a3\u4eba\u53c8\u626c\u4e86\u4e00\u626c\u624b\uff0c\u628a\u5510\u6653\u6f9c\u7684\u94f6\u5305\u548c\u6c49 \u7389\u90fd\u629b\u4e86\u8fc7\u6765\uff0c\u5ffd\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u6447\u5934\u6446\u8111\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u5bf9\u8eab\u5916\u4e4b\u4e8b\u5982\u6b64\u770b\u91cd\uff0c\u5bf9\u81ea\u5df1\u6027 \u547d\u5374\u4e0d\u7231\u60dc\uff0c\u771f\u771f\u53ef\u53f9\uff01\u4f60\u65e2\u7136\u7231\u8d22\u5982\u547d\uff0c\u6211\u5c31\u8fd8\u7ed9\u4f60\u5427\uff0c\u7701\u5f97\u4f60\u50cf\u51a4\u9b42\u4e00\u6837\u6765\u7f20\u7ed5\u6211\u3002\u201d \u5510\u6653\u6f9c\u542c\u5f97\u8bdd\u4e2d\u6709\u8bdd\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u665a\u8f88\u5c82\u6562\u7231\u60dc\u94b1\u8d22\uff0c\u8fd8\u671b\u524d\u8f88\u6307\u70b9\u8ff7\u6d25\u3002\u201d\u90a3\u4eba\u56de \u5934\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u524d\u8f88\u665a\u8f88\uff0c\u6211\u6700\u8ba8\u538c\u8fd9\u4e9b\u4fd7\u672d\u865a\u6587\u3002\u6211\u95ee\u4f60\uff0c\u4f60\u4e0d\u7231\u94b1\u8d22\uff0c\u6211\u624d\u4e0d\u8fc7\u62ff \u4e86\u4f60\u4e09\u4ef6\u4e1c\u897f\uff0c\u4f60\u5c31\u62fc\u547d\u6765\u8ffd\u6211\u4f5c\u751a\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u63e3\u5192\u6627\uff0c\u4f46\u60f3\u7ed3\u8bc6\u9ad8\u4eba\u3002\u201d\u90a3 \u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u53e3\u4e0d\u5bf9\u5fc3\uff0c\u6211\u5728\u9547\u4e0a\u62ff\u6389\u4f60\u7684\u4e1c\u897f\u4e4b\u65f6\uff0c\u4f60\u54ea\u91cc\u77e5\u9053\u6211\u662f\u4ec0\u4e48\u2018\u9ad8 \u4eba\u2019\uff1f\u201d\u5510\u6653\u6f9c\u5f53\u65f6\u679c\u662f\u628a\u4ed6\u5f53\u4f5c\u666e\u901a\u5c0f\u5077\uff0c\u54d1\u7136\u65e0\u8bed\u3002\u90a3\u4eba\u9053\uff1a\u201c\u4f60\u660e\u660e\u820d\u4e0d\u5f97\u8fd9\u51e0\u4ef6\u4e1c \u897f\u561b\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u8fd9\u80f8\u5251\u4e43\u662f\u4e00\u4f4d\u670b\u53cb\u6240\u9001\uff0c\u6211\u4e0d\u60f3\u5931\u6389\uff0c\u4f46\u2026\u2026\u201d \u3000\u3000\u6b63\u60f3\u8bf4\u4f46\u8ffd\u4e0b\u53bb\u4e4b\u540e\uff0c\u5c31\u53d1\u73b0\u4f60\u662f\u9ad8\u4eba\uff0c\u90a3\u4eba\u622a\u7740\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u670b\u53cb\uff1f\u662f\u4f60\u7684\u4e0a\u5b98\u9001 \u7684\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u4e00\u6115\uff0c\u90a3\u4eba\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6015\u5931\u4e86\u4f69\u5251\uff0c\u5141\u5824\u95ee\u8d77\u4e0d\u597d\u610f\u601d\uff0c\u4e5f\u635f\u4e86\u4f60\u8fd1\u536b\u519b\u526f \u603b\u9886\u7684\u8eab\u4efd\uff0c\u5bf9\u4e48\uff1f\u8981\u4e0d\u7136\u8fd9\u628a\u5251\u4e5f\u4e0d\u662f\u5b9d\u7269\uff0c\u4f60\u7684\u6e38\u9f99\u5251\u5c1a\u81ea\u53ef\u5931\uff0c\u8fd9\u628a\u5251\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u5931 \u6389\uff1f\u201d\u5510\u6653\u6f9c\u4e00\u542c\uff0c\u8fd9\u4eba\u7adf\u719f\u77e5\u81ea\u5df1\u5e95\u7ec6\uff0c\u66f4\u662f\u83ab\u6d4b\u9ad8\u6df1\u3002\u90a3\u4eba\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u9001\u4f60\u8fd9\u628a\u4f69 \u5251\u7684\u4eba\uff0c\u73b0\u5728\u81ea\u8eab\u96be\u4fdd\uff0c\u54ea\u8fd8\u4f1a\u9053\u95ee\u4f60\u7684\u4f69\u5251\uff1f\u201d\u5510\u6653\u6f9c\u66f4\u662f\u5403\u60ca\uff0c\u90a3\u4eba\u9053\uff1a\u201c\u6211\u6551\u4e86\u4f60\u7684 \u6027\u547d\uff0c\u4f60\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u201d\u5510\u6653\u6f9c\u5413\u4e86\u4e00\u8df3\uff0c\u83ab\u660e\u6240\u4ee5\uff0c\u90a3\u4eba\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u76f8\u4fe1\uff0c\u6211\u53eb\u4f60\u89c1 \u4e00\u4e2a\u4eba\uff01\u201d\u64ae\u5507\u4e00\u5578\uff0c\u8305\u5c4b\u4e2d\u8d70\u51fa\u4e00\u4eba\uff0c\u5510\u6653\u6f9c\u4e00\u89c1\uff0c\u53c8\u662f\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u8fd9\u4eba\u7adf\u662f\u5141\u5824\u519b\u4e2d\u4e8c\u5b9d\u4e4b\u4e00\u7684\u65b9\u4eca\u660e\uff0c\u5510\u6653\u6f9c\u4ee5\u524d\u6253\u64c2\u70ab\u6280\u4e4b\u540e\uff0c\u5c31\u662f\u7531\u4ed6\u5f15\u8fdb\u5141\u5824\u519b \u4e2d\u7684\u3002\u53ea\u542c\u5f97\u65b9\u4eca\u660e\u9053\uff1a\u201c\u5510\u5144\uff0c\u4f60\u53d7\u60ca\u4e86\uff01\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u65b9\u5144\uff0c\u4f60\u600e\u4e48\u4f1a\u5728\u8fd9\u91cc\uff1f\u201d\u65b9 \u4eca\u660e\u9053\uff1a\u201c\u4f60\u8fdb\u6765\u5427\uff0c\u6211\u6162\u6162\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u8fdb\u4e86\u8305\u5c4b\uff0c\u5510\u6653\u6f9c\u5148\u95ee\u90a3\u4eba\u59d3\u540d\uff0c\u90a3\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u53cc\u624b\u9f50\u4f38\uff0c\u5510\u6653\u6f9c\u4e0d\u89e3\u5176\u610f\uff0c\u7ec6\u770b\u4e4b \u65f6\uff0c\u624d\u53d1\u73b0\u6b64\u4eba\u4e24\u624b\uff0c\u5747\u4e0e\u5e38\u4eba\u4e0d\u540c\uff0c\u5e38\u4eba\u6bcf\u624b\u6709\u4e94\u53ea\u624b\u6307\uff0c\u800c\u4ed6\u5374\u5de6\u53f3\u624b\u90fd\u751f\u591a\u4e86\u4e00\u53ea\u624b \u6307\uff0c\u53cc\u624b\u5171\u662f\u5341\u4e8c\u53ea\u624b\u6307\uff0c\u5ffd\u7136\u9192\u609f\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u662f\u5341\u4e8c\u6307\u5999\u624b\u795e\u5077\u9648\u5fb7\u6cf0\u9648\u5927\u54e5\uff01\u201d\u90a3\u4eba \u70b9\u4e86\u70b9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u6b63\u662f\u3002\u4f60\u73b0\u5728\u8be5\u77e5\uff0c\u6211\u4e0d\u662f\u4f60\u7684\u4ec0\u4e48\u524d\u8f88\u4e86\u5417\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u77aa\u5927\u773c\u775b\uff0c\u8d8a\u53d1\u7591\u60d1\uff0c\u4eca\u65e5\u4e00\u5207\uff0c\u5747\u5982\u505a\u68a6\u4e00\u822c\u3002 \u3000\u3000\u4f60\u9053\u5510\u6653\u6f9c\u4f55\u4ee5\u7591\u60d1\uff0c\u539f\u6765\u8fd9\u5341\u4e8c\u6307\u795e\u5077\u9648\u5fb7\u6cf0\u4e43\u662f\u7518\u51e4\u6c60\u7684\u5927\u8205\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e5f\u9887\u6709\u540d \u5934\u3002\u5510\u6653\u6f9c\u5fc3\u60f3\uff1a\u65b9\u4eca\u660e\u548c\u8f66\u8f9f\u90aa\u4e8c\u4eba\uff0c\u4e43\u662f\u5141\u5824\u5fc3\u8179\u4e2d\u7684\u5fc3\u8179\uff0c\u4f55\u4ee5\u65b9\u4eca\u660e\u5374\u4f1a\u548c\u7518\u51e4\u6c60 \u7684\u5927\u8205\u5728\u4e00\u8d77\uff0c\u800c\u4e14\u8fd8\u5982\u8001\u670b\u53cb\u4e00\u822c\uff1f\u65b9\u4eca\u660e\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u4e3b\u516c\u4eca\u65e5\u6050\u6015\u96be\u9003\u5927\u96be\u4e86\uff01\u201d\u5510 \u6653\u6f9c\u53c8\u662f\u5fc3\u4e2d\u4e00\u51db\uff0c\u65b9\u4eca\u660e\u65e2\u7136\u8fd8\u79f0\u5141\u5824\u4e3a\u201c\u4e3b\u516c\u201d\uff0c\u90a3\u4e48\u4ed6\u548c\u7518\u51e4\u6c60\u663e\u7136\u5e76\u975e\u4e00\u8def\uff0c\u4f55\u4ee5 \u53c8\u4f1a\u5982\u6b64\uff1f\u65b9\u4eca\u660e\u53c8\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u662f\u9648\u5927\u54e5\u5f15\u6765\uff0c\u53ea\u6050\u6027\u547d\u96be\u4fdd\uff01\u201d\u9648\u5fb7\u6cf0\u5728\u65c1\u7b11\u9053\uff1a\u201c\u5982 \u4f55\uff1f\u6211\u53ef\u6ca1\u6709\u5938\u5927\u9a97\u4f60\uff0c\u6545\u610f\u79f0\u529f\u5427\uff1f\u201d\u5510\u6653\u6f9c\u62dc\u4e0b\u53bb\u9053\uff1a\u201c\u591a\u8c22\u9648\u5927\u54e5\u6551\u547d\u4e4b\u6069\uff0c\u8fd8\u671b\u660e \u767d\u544a\u77e5\uff0c\u91ca\u6211\u7591\u56e2\u3002\u201d\u9648\u5fb7\u6cf0\u9053\uff1a\u201c\u4f60\u8fd8\u8be5\u591a\u8c22\u8fd9\u4f4d\u65b9\u5927\u54e5\uff0c\u82e5\u4e0d\u662f\u4ed6\uff0c\u6211\u4e5f\u4e0d\u77e5\u4f60\u5728\u9547\u4e0a \u559d\u9152\u3002\u201d \u3000\u3000\u65b9\u4eca\u660e\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4f60\u7684\u6e38\u9f99\u5251\u73b0\u5728\u54ea\u91cc\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u7684\u5251\u7ed9\u4e86\u56e0\u641c\u53bb\uff0c\u60f3\u5fc5\u5728 \u4e86\u56e0\u624b\u4e2d\u3002\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4e86\u56e0\u5728\u54ea\u91cc\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4e0d\u662f\u5728\u5bab\u4e2d\u5417\uff1f\u201d\u4e86\u56e0\u7b49 \u4e94\u4eba\u5728\u5e74\u7fb9\u5c27\u5e10\u4e0b\uff0c\u5e74\u7fb9\u5c27\u7684\u519b\u961f\u53e6\u6210\u7cfb\u7edf\uff0c\u6240\u4ee5\u5510\u6653\u6f9c\u4e0d\u77e5\u3002\u65b9\u4eca\u660e\u9053\uff1a\u201c\u4f46\u4e86\u56e0\u4e0d\u5728\u5bab \u4e2d\uff0c\u4f60\u7684\u5251\u73b0\u5728\u4e5f\u4e0d\u5728\u4e86\u56e0\u624b\u4e2d\u4e86\u3002\u5e74\u7fb9\u5c27\u4eca\u65e5\u4e3e\u4e8b\uff0c\u52ab\u593a\u4e3b\u5e05\uff0c\u4f60\u90a3\u628a\u5b9d\u5251\u4e5f\u52a9\u4e86\u4ed6\u4e00\u81c2 \u4e4b\u529b\u3002\u201d\u5510\u6653\u6f9c\u8d8a\u542c\u8d8a\u5947\uff0c\u9648\u5fb7\u6cf0\u7b11\u9053\uff1a\u201c\u4e86\u56e0\u6b63\u5728\u5e74\u7fb9\u5c27\u5e10\u4e2d\uff0c\u800c\u4f60\u7684\u6e38\u9f99\u5251\u4e5f\u5df2\u5230\u8f66\u8f9f \u90aa\u7684\u624b\u4e0a\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u65b9\u4eca\u660e\u539f\u662f\u6c5f\u5357\u4e00\u4e2a\u9f99\u5934\u5e2e\u4e3b\uff0c\u5141\u5824\u62db\u8d24\u7eb3\u58eb\uff0c\u5341\u5e74\u524d\u5c31\u62db\u63fd\u4e86\u4ed6\u3002\u5728\u4ed6\u672a\u5165\u5141 \u5824\u5e55\u4e2d\u4e4b\u65f6\uff0c\u548c\u7518\u51e4\u6c60\u867d\u975e\u77e5\u4ea4\uff0c\u5374\u662f\u76f8\u8bc6\uff0c\u6240\u4ee5\u548c\u9648\u5fb7\u6cf0\u4e5f\u66fe\u6709\u6570\u9762\u4e4b\u7f18\u3002 \u3000\u3000\u65b9\u4eca\u660e\u5148\u628a\u81ea\u5df1\u7684\u6765\u5386\uff0c\u5411\u5510\u6653\u6f9c\u8bf4\u4e86\uff0c\u7136\u540e\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e3b\u516c\u624b\u63e1\u5175\u6743\uff0c\u5141\u796f\u8fd9\u53ae\u81ea \u7136\u653e\u4ed6\u4e0d\u8fc7\uff0c\u53ef\u662f\u5728\u4eac\u4e2d\u4e4b\u65f6\uff0c\u6015\u6fc0\u8d77\u4f17\u7687\u5b50\u516c\u6124\uff0c\u6240\u4ee5\u4e0d\u6562\u5728\u4eac\u4e2d\u4e0b\u624b\u3002\u5374\u7b49\u5e74\u7fb9\u5c27\u7be1\u593a \u4e86\u5175\u6743\u4e4b\u540e\uff0c\u624d\u53eb\u5e74\u7fb9\u5c27\u4e0b\u624b\u3002\u201d\u8fd9\u539f\u662f\u5510\u6653\u6f9c\u610f\u6599\u4e2d\u4e8b\uff0c\u5374\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u65b9\u4eca \u660e\u9053\uff1a\u201c\u8f66\u8f9f\u90aa\u6700\u7231\u5b9d\u5251\uff0c\u4e86\u56e0\u5c06\u4f60\u7684\u5251\u9001\u4e86\u7ed9\u4ed6\uff0c\u53c8\u8bf1\u4e4b\u4ee5\u529f\u540d\u5229\u7984\uff0c\u53eb\u4ed6\u80cc\u53db\u4e3b\u516c\u3002\u8f66 \u8f9f\u90aa\u7b54\u5e94\u4e86\uff0c\u5e74\u7fb9\u5c27\u53c8\u53eb\u4ed6\u6765\u8bf4\u6211\u3002\u6211\u4e0d\u613f\u80cc\u53db\u4e3b\u516c\uff0c\u4f46\u548c\u8f66\u8f9f\u90aa\u53c8\u6709\u5341\u8f7d\u4ea4\u60c5\uff0c\u4e5f\u4e0d\u60f3\u7acb \u5373\u544a\u53d1\uff0c\u56e0\u6b64\u6211\u7528\u7f13\u5175\u4e4b\u8ba1\uff0c\u8bf7\u4ed6\u5bbd\u9650\u4e00\u4e24\u65e5\u7b54\u89c1\u4ed6\uff0c\u8fd9\u662f\u4eca\u65e9\u7684\u4e8b\u3002\u4f60\u4eec\u51fa\u5e02\u9547\u540e\uff0c\u6211\u672c \u60f3\u53bb\u63d0\u9192\u4e3b\u516c\uff0c\u54ea\u6599\u4ed6\u5df2\u7ecf\u53bb\u8d74\u5e74\u7fb9\u5c27\u4e4b\u5bb4\uff0c\u6211\u77e5\u9053\u4e8b\u60c5\u4e0d\u5999\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5c31\u6709\u4eba\u98de\u62a5\u7ed9 \u6211\uff0c\u8bf4\u662f\u5e74\u7fb9\u5c27\u90a3\u8fb9\u5df2\u7ecf\u52a8\u624b\u3002\u201d\u9648\u5fb7\u6cf0\u5fae\u7b11\u63d2\u53e3\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u7684\u519b\u961f\u4e2d\uff0c\u4e5f\u6709\u6211\u4eec\u7684\u5f1f \u5144\uff0c\u6240\u4ee5\u65b9\u5927\u54e5\u8d76\u5fd9\u8dd1\u6765\uff1a\u628a\u6d88\u606f\u544a\u6211\u3002\u201d \u3000\u3000\u539f\u6765\u65b9\u4eca\u660e\u6b64\u4eba\u6b66\u529f\u867d\u9ad8\uff0c\u5bf9\u4e8e\u7acb\u8eab\u5904\u4e16\u4e4b\u9053\u5374\u662f\u7cca\u6d82\uff0c\u770b\u91cd\u79c1\u60c5\uff0c\u5ffd\u4e8e\u5927\u4e49\u3002\u5141\u5824\u7528 \u5c0f\u6069\u5c0f\u60e0\u7b3c\u7edc\u4ed6\uff0c\u4ed6\u5c31\u613f\u4ee5\u56fd\u58eb\u62a5\u4e4b\u3002\u4f46\u4ed6\u5bf9\u7518\u51e4\u6c60\u7684\u4fa0\u4e49\u4e5f\u751a\u4e3a\u656c\u91cd\uff0c\u6240\u4ee5\u4e00\u65e6\u5927\u96be\u6765 \u65f6\uff0c\u7518\u51e4\u6c60\u7684\u4eba\u53eb\u4ed6\u9003\u8d70\uff0c\u5e76\u544a\u8bc9\u4ed6\u9648\u5fb7\u6cf0\u6070\u5de7\u5728\u6b64\uff0c\u4ed6\u4e5f\u5c31\u8dd1\u6765\u4e86\u3002\u5510\u6653\u6f9c\u542c\u4e86\uff0c\u98a4\u58f0\u95ee \u9053\uff1a\u201c\u7518\u5927\u4fa0\u77e5\u9053\u6b64\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u9648\u5fb7\u6cf0\u9053\uff1a\u201c\u7518\u4e03\u54e5\u6050\u6015\u8981\u8fc7\u4e24\u5929\u624d\u6765\uff0c\u4f46\u5173\u4e1c\u56db\u4fa0\u5374\u5df2\u5230\u4e86\u3002\u201d\u539f\u6765\u7518\u51e4\u6c60\u6015\u4eba\u591a\u4e0d \u4fbf\uff0c\u662f\u4ee5\u5206\u6210\u4e09\u6279\u52a8\u8eab\u3002\u7b2c\u4e00\u6279\u662f\u5173\u4e1c\u56db\u4fa0\uff0c\u7b2c\u4e8c\u6279\u662f\u6768\u4ef2\u82f1\u8def\u6c11\u77bb\u548c\u4ed6\uff0c\u7b2c\u4e09\u6279\u5219\u662f\u5415\u56db \u5a18\u548c\u767d\u6cf0\u5b98\u53ca\u9c7c\u5a18\u3002\u7518\u51e4\u6c60\u867d\u7136\u6ca1\u6765\uff0c\u4f46\u4ed6\u4ea4\u6e38\u904d\u5929\u4e0b\uff0c\u5e74\u7fb9\u5c27\u519b\u4e2d\u4e5f\u6709\u4ed6\u7684\u8033\u76ee\uff0c\u6240\u4ee5\u5510 \u6653\u6f9c\u6697\u4e2d\u5f97\u4eba\u7167\u987e\uff0c\u8fd8\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u5510\u6653\u6f9c\u95ee\u9053\uff1a\u201c\u65b9\u5927\u54e5\uff0c\u90a3\u4f60\u4eca\u540e\u6253\u7b97\u600e\u6837\uff1f\u201d\u65b9\u4eca\u660e\u82e6\u7b11\u9053\uff1a\u201c\u6211\u8981\u4eca\u665a\u89c1\u8fc7\u8f66\u8f9f\u90aa \u4e4b\u540e\u624d\u80fd\u5b9a\u593a\uff01\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u8fd8\u8981\u89c1\u8f66\u8f9f\u90aa\uff1f\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u6211\u548c\u4ed6\u5341\u8f7d\u4ea4\u60c5\uff0c \u4eb2\u5982\u5144\u5f1f\uff0c\u5c31\u7b97\u4eca\u540e\u5272\u5e2d\u7edd\u4ea4\uff0c\u4e5f\u5f97\u8bf4\u4e2a\u660e\u767d\u3002\u800c\u4e14\u6211\u4e5f\u8981\u6253\u542c\u4e3b\u516c\u4e0b\u843d\u3002\u201d\u5510\u6653\u6f9c\u542c\u4e86\u6697 \u53eb\uff1a\u7cca\u6d82\u3002\u4f46\u4ed6\u89c1\u65b9\u4eca\u660e\u8fd8\u53e3\u53e3\u58f0\u58f0\u79f0\u5141\u5824\u4e3a\u201c\u4e3b\u516c\u201d\uff0c\u4e0d\u4fbf\u76f8\u529d\uff0c\u53ea\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u8fd8\u56de \u519b\u8425\u53bb\u5417\uff1f\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u5df2\u6258\u4eba\u7ea6\u4ed6\u660e\u65e5\u4e00\u65e9\u5728\u96ea\u9b42\u8c37\u76f8\u89c1\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u96ea\u9b42 \u8c37\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u9648\u5fb7\u6cf0\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u5916\u9762\u8fd9\u4e2a\u5c71\u8c37\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u8fd9\u91cc\u7684\u96ea \u666f\u5982\u6b64\u4e4b\u7f8e\uff0c\u679c\u7136\u4e0d\u8d1f\u4f73\u540d\u3002\u201d\u53c8\u9053\uff1a\u201c\u8f66\u8f9f\u90aa\u65e2\u7136\u7518\u4e3a\u540d\u5229\u6240\u8bf1\uff0c\u65b9\u5144\u4e0d\u53ef\u4e0d\u9632\uff0c\u660e\u65e9\u4e4b \u4f1a\uff0c\u6211\u548c\u4f60\u4e00\u540c\u53bb\u5427\u3002\u201d\u65b9\u4eca\u660e\u6447\u624b\u9053\uff1a\u201c\u6211\u53ea\u7ea6\u4ed6\u5355\u72ec\u76f8\u89c1\uff0c\u4eba\u591a\u4e0d\u4fbf\u8c08\u8bdd\u3002\u201d\u9648\u5fb7\u6cf0\u5fae \u5fae\u4e00\u7b11\uff0c\u793a\u610f\u53eb\u5510\u6653\u6f9c\u4e0d\u5fc5\u591a\u8a00\u3002 \u3000\u3000\u665a\u4e0a\u6d88\u606f\u4f20\u6765\uff0c\u8bf4\u662f\u5e74\u7fb9\u5c27\u5949\u4e86\u5723\u65e8\uff0c\u5df2\u4ee3\u5141\u5824\u5c31\u4e86\u629a\u8fdc\u5927\u5c06\u519b\u4e4b\u804c\uff0c\u5141\u5824\u7684\u8fd1\u536b\u519b\u5168 \u6570\u88ab\u6b7c\uff0c\u6700\u4eb2\u4fe1\u7684\u4e03\u540d\u519b\u5b98\u4e5f\u88ab\u6740\u4e86\uff0c\u5176\u4e2d\u6709\u4e09\u540d\u519b\u5b98\u5c31\u662f\u548c\u5510\u6653\u6f9c\u4e00\u9053\u559d\u9152\uff0c\u540e\u6765\u5728\u9152\u697c \u4e0a\u88ab\u6349\u53bb\u7684\uff1b\u81f3\u4e8e\u5141\u5824\u548c\u535a\u514b\u56fe\u5219\u5728\u5e2d\u4e0a\u62ab\u64d2\uff0c\u751f\u6b7b\u5982\u4f55\uff0c\u4e0d\u5f97\u800c\u77e5\u3002\u65b9\u4eca\u660e\u542c\u4e86\uff0c\u6376\u80f8\u5927 \u54ed\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u5929\u521a\u65b9\u4eae\uff0c\u65b9\u4eca\u660e\u4fbf\u5230\u5916\u9762\u5c71\u8c37\u76f8\u5019\uff0c\u5929\u7a7a\u98d8\u7740\u9e45\u6bdb\u822c\u7684\u96ea\u82b1\uff0c\u663e\u5f97\u66f4\u662f\u9634\u6c89\u8427 \u745f\uff0c\u65b9\u4eca\u660e\u7b95\u8e1e\u5d16\u77f3\u4e4b\u4e0a\uff0c\u7fd8\u9996\u4e1c\u671b\uff0c\u5fc3\u60f3\u662f\u4e0d\u662f\u6765\u5f97\u592a\u65e9\u4e86\uff1f\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u84e6\u5730\u4f20 \u6765\uff0c\u8f66\u8f9f\u90aa\u7a81\u7136\u4ece\u4fa7\u9762\u7684\u4e24\u5757\u5ca9\u77f3\u4e2d\u95f4\u8df3\u51fa\uff0c\u9053\uff1a\u201c\u65b9\u5144\u6765\u5f97\u771f\u65e9\uff0c\u4f60\u90a3\u4e24\u4f4d\u670b\u53cb\u5462\uff1f\u600e\u4e48 \u4e0d\u4e00\u540c\u6765\uff1f\u201d\u65b9\u4eca\u660e\u5413\u4e86\u4e00\u8df3\uff0c\u5fc3\u60f3\uff1a\u96be\u9053\u4ed6\u6628\u665a\u5c31\u5df2\u6765\u4e86\uff1f\u9053\uff1a\u201c\u6211\u4eec\u65e2\u7ea6\u597d\u5355\u72ec\u4f1a\u9762\uff0c \u600e\u80fd\u8fd8\u7ea6\u65c1\u4eba\uff1f\u201d \u3000\u3000\u8f66\u8f9f\u90aa\u9762\u8272\u9634\u6c89\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6628\u65e5\u6211\u90a3\u756a\u8bf4\u8bdd\uff0c\u65b9\u5144\u53ef\u66fe\u8003\u8651\uff1f\u201d\u65b9\u4eca\u660e\u9053\uff1a \u201c\u4e3b\u516c\u5f85\u6211\u4eec\u4e0d\u8584\u2026\u2026\u201d\u8f66\u8f9f\u90aa\u622a\u7740\u9053\uff1a\u201c\u8bc6\u65f6\u52a1\u8005\u4e3a\u4fca\u6770\uff0c\u4ed6\u4eec\u5144\u5f1f\u4e89\u4f4d\uff0c\u96be\u9053\u4f60\u8fd8\u8981\u4e3a \u4ed6\u6b7b\u8282\u4e0d\u6210\uff1f\u201d\u65b9\u4eca\u660e\u9762\u8272\u5927\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5df2\u628a\u4e3b\u516c\u5bb3\u4e86\uff1f\u201d\u8f66\u8f9f\u90a3\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u6709\u52a8 \u624b\u3002\u201d\u65b9\u4eca\u660e\u864e\u76ee\u6d41\u6cea\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u5982\u6b64\u5fd8\u6069\u8d1f\u4e49\uff01\u201d\u8f66\u8f9f\u90aa\u9053\uff1a\u201c\u65b9\u5144\u5bbd\u5fc3\uff0c\u4e3b\u516c\u8fd8 \u672a\u6b7b\u5462\uff0c\u4f60\u54ed\u4ec0\u4e48\uff1f\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u80af\u628a\u4ed6\u653e\u4e86\u4e48\uff1f\u201d\u8f66\u8f9f\u90aa\u7b11\u9053\uff1a\u201c\u5f53\u4eca\u7687\u4e0a\u4eb2\u81ea \u6d3e\u54c8\u5e03\u9640\u6765\u5c06\u4ed6\u8bf7\u56de\u4eac\u5e08\u53bb\u4e86\u3002\u201d\u65b9\u4eca\u660e\u4e00\u542c\uff0c\u5fc3\u60f3\uff1a\u5141\u796f\u5c06\u4ed6\u79d8\u5bc6\u89e3\u56de\u4eac\u57ce\uff0c\u7ed3\u679c\u8fd8\u662f\u96be \u9003\u4e00\u6b7b\uff0c\u800c\u4e14\u5141\u796f\u5fc3\u72e0\u624b\u6bd2\uff0c\u53ea\u6050\u5141\u5824\u5c06\u6765\u4e4b\u6b7b\uff0c\u8981\u6bd4\u6b7b\u5728\u5200\u5251\u4e4b\u4e0b\u66f4\u60e8\u3002\u6012\u9053\uff1a\u201c\u7687\u4e0a\u8fd9 \u6837\u523b\u8584\u5be1\u6069\uff0c\u6211\u5144\u80fd\u4e0d\u5fc3\u5bd2\u4e48\uff1f\u201d\u8f66\u8f9f\u90aa\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u65b9\u4eca\u660e\u6012\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u8f66\u8f9f\u90aa\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4e0e\u5341\u56db\u8d1d\u52d2\u4e0d\u540c\uff0c\u6211\u4eec\u53c8\u4e0d\u4e0e\u5f53\u4eca\u7687 \u4e0a\u4e89\u4f4d\uff0c\u4ed6\u7eb5\u523b\u8584\u5be1\u6069\uff0c\u4e0e\u6211\u4eec\u6709\u4f55\u5173\u7cfb\uff1f\u201d\u65b9\u4eca\u660e\u4e00\u9635\u5fc3\u5bd2\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u5341\u8f7d\u76f8\u4ea4\uff0c\u60f3 \u4e0d\u5230\u4f60\u662f\u8fd9\u6837\u7684\u5c0f\u4eba\uff01\u201d\u8f66\u8f9f\u90aa\u7709\u6bdb\u4e00\u626c\uff0c\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d\u65b9\u4eca\u660e\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u4f60\u8d70\u5427\uff01\u541b\u5b50\u7edd\u4ea4\u4e0d\u51fa\u6076\u58f0\uff0c\u54b1\u4eec\u4ee5\u5f80\u7684\u4ea4\u60c5\u4e00\u7b14\u52fe\u9500\uff0c\u4f60\u53bb\u505a\u7684\u4f60\u7684\u5b98\uff0c\u6211\u56de\u53bb\u505a\u5f3a \u76d7\u3002\u53ea\u8981\u4f60\u4e0d\u662f\u5949\u547d\u6765\u6349\u6211\uff0c\u6211\u5c31\u4e0d\u548c\u4f60\u52a8\u624b\u3002\u201d\u8f6c\u8eab\u6b32\u8d70\u3002\u8f66\u8f9f\u90aa\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u65b9\u4eca \u660e\u56de\u5934\u9053\uff1a\u201d\u4f60\u60f3\u600e\u4e48\uff1f\u201d\u8f66\u8f9f\u90aa\u9053\uff1a\u201c\u6211\u5144\u4e09\u601d\u800c\u884c\uff01\u201d\u65b9\u4eca\u660e\u5fc3\u4f24\u4e4b\u6781\uff0c\u53d1\u4e3a\u51b7\u7b11\uff0c\u56de \u8eab\u53c8\u8d70\u3002\u521a\u8d70\u5f97\u51e0\u6b65\uff0c\u5ffd\u542c\u5f97\u54c8\u54c8\u5927\u7b11\u4e4b\u58f0\uff0c\u65b9\u4eca\u660e\u56de\u5934\u518d\u770b\uff0c\u53ea\u89c1\u5d16\u77f3\u4e0b\u7a81\u7136\u591a\u51fa\u4e24\u4eba\uff0c \u4e00\u4e2a\u548c\u5c1a\uff0c\u4e00\u4e2a\u80d6\u8001\u5934\uff0c\u8fd9\u4e24\u4eba\u6b63\u662f\u4e86\u56e0\u548c\u8463\u5de8\u5ddd\u3002\u65b9\u4eca\u660e\u6c14\u5f80\u4e0a\u51b2\uff0c\u9053\uff1a\u201c\u8f66\u8f9f\u90aa\uff0c\u4f60\u65e9 \u7ea6\u597d\u5e2e\u624b\u6765\u5bf9\u4ed8\u6211\u4e86\uff1f\u201d\u8f66\u8f9f\u90aa\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4f55\u5fc5\u7ea6\u4eba\u6765\u5bf9\u4ed8\u4f60\uff0c\u4f60\u6211\u65a4\u4e24\u5982\u4f55\uff0c\u5f7c\u6b64\u5fc3\u4e2d \u6709\u6570\uff0c\u6211\u518d\u95ee\u4f60\u4e00\u58f0\uff0c\u4f60\u5230\u5e95\u613f\u4e0d\u613f\u8ddf\u6211\u56de\u53bb\uff1f\u201d\u65b9\u4eca\u660e\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\uff0c\u4f60\u662f\u8981\u628a\u6211\u7559 \u4e0b\u4e86\uff01\u201d\u8463\u5de8\u5ddd\u5728\u65c1\u9634\u607b\u607b\u7684\u8bf4\u9053\uff1a\u201c\u8f66\u7edf\u9886\uff0c\u8fd9\u56de\u6211\u4eec\u770b\u4f60\u7684\u4e86\uff01\u201d\u8f66\u8f9f\u90aa\u55d6\u7684\u4e00\u58f0\u62d4\u51fa \u5251\u6765\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u4e0e\u51b0\u96ea\u76f8\u6620\uff0c\u8000\u773c\u751f\u8f89\uff0c\u5927\u58f0\u9053\uff1a\u201c\u65b9\u4eca\u660e\uff0c\u4f60\u65e2\u6709\u4e0d\u81e3\u4e4b\u5fc3\uff0c\u6211\u4e5f\u65e0\u5144 \u5f1f\u4e4b\u4e49\u4e86\uff01\u201d \u3000\u3000\u65b9\u4eca\u660e\u5927\u53eb\u9053\uff1a\u201c\u597d\u54c7\uff0c\u4f60\u5c06\u6211\u7684\u9888\u8840\u67d3\u7ea2\u4f60\u7684\u9876\u6234\u5427\uff01\u201d\u5de6\u638c\u62a4\u80f8\uff0c\u53f3\u62f3\u638c\u5e95\u7a7f\u51fa\uff0c \u8f66\u8f9f\u90aa\u51b7\u7b11\u4e00\u58f0\uff0c\u56de\u8eab\u62d7\u6b65\uff0c\u6e38\u9f99\u5251\u9752\u5149\u4e00\u95ea\uff0c\u659c\u523a\u80f8\u80c1\uff0c\u65b9\u4eca\u660e\u559d\u58f0\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u7b71\u5730 \u8eab\u5f62\u4e00\u584c\uff0c\u624b\u6cd5\u5982\u7535\uff0c\u4e00\u4e2a\u201c\u5370\u638c\u201d\uff0c\u638c\u98ce\u98d2\u7136\uff0c\u76f4\u88ad\u654c\u80f8\u3002\u8f66\u8f9f\u90aa\u559d\u9053\uff1a\u201c\u4f60\u627e\u6b7b\u4e48\uff1f\u201d \u547c\u7684\u5708\u8f6c\u624b\u6765\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u622a\u4ed6\u81c2\u5f2f\uff0c\u65b9\u4eca\u660e\u7a81\u7136\u957f\u8eab\u6025\u8d77\uff0c\u5de6\u638c\u6258\u4ed6\u8098\u5c16\uff0c\u53f3\u624b\u53d8\u638c\u64d2 \u62ff\uff0c\u53ea\u4e00\u94a9\u5c31\u94a9\u7740\u4e86\u8f66\u8f9f\u90aa\u81c2\u818a\u3002\u65b9\u4eca\u660e\u77e5\u9053\u8f66\u8f9f\u90aa\u5251\u672f\u975e\u540c\u5c0f\u53ef\uff0c\u6240\u4ee5\u4e00\u51fa\u624b\u4fbf\u62fc\u4e86\u6027 \u547d\uff0c\u4f7f\u51fa\u4e86\u6781\u5176\u51f6\u6076\u7684\u9669\u62db\uff01 \u3000\u3000\u8f66\u8f9f\u90aa\u4e34\u5371\u4e0d\u4e71\uff0c\u63a7\u80cc\u5408\u80f8\uff0c\u7a81\u7136\u4e00\u4e2a\u201c\u9000\u6b65\u6a2a\u803e\u201d\uff0c\u6bd4\u5f00\u4e86\u65b9\u4eca\u660e\u64d2\u62ff\u4e4b\u52bf\uff0c\u5251\u67c4\u5411 \u5916\u4e00\u649e\uff0c\u659c\u70b9\u65b9\u4eca\u660e\u5de6\u808b\u7684\u201c\u7b11\u8170\u7a74\u201d\uff0c\u8fd9\u662f\u4ed6\u7684\u6551\u547d\u7edd\u62db\uff0c\u65b9\u4eca\u660e\u6643\u80a9\u6025\u9000\uff0c\u55e4\u7684\u4e00\u58f0\uff0c \u8f66\u8f9f\u90aa\u7684\u5251\u950b\u5728\u4ed6\u80a9\u5934\u524a\u8fc7\u3002\u65b9\u4eca\u660e\u5927\u559d\u9053\uff1a\u201c\u4eca\u65e5\u6211\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u8eab\u5f62\u75be\u8d77\uff0c\u62f3\u5982\u96e8\uff0c\u638c \u7ffb\u98de\uff0c\u6253\u51fa\u4e86\u5341\u516b\u8def\u957f\u62f3\uff0c\u697c\u5934\u76d6\u9876\uff0c\u6376\u808b\u6363\u80f8\uff0c\u5728\u5251\u5149\u4e2d\u7a7f\u6765\u63d2\u53bb\uff01 \u3000\u3000\u8f66\u8f9f\u90aa\u4e0e\u65b9\u4eca\u660e\u7684\u6b66\u529f\u90fd\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u4e00\u4e2a\u7cbe\u4e8e\u62f3\u672f\uff0c\u4e00\u4e2a\u957f\u4e8e\u5251\u6cd5\uff0c\u672c\u6765\u662f\u516b\u4e24\u534a \u65a4\uff0c\u4f46\u8f66\u8f9f\u90aa\u6709\u4e86\u6e38\u9f99\u5b9d\u5251\uff0c\u5a01\u529b\u65e0\u5f62\u4e2d\u589e\u52a0\u4e86\u51e0\u5206\uff0c\u52a0\u4ee5\u65b9\u4eca\u660e\u5931\u4e4b\u4e8e\u8e81\uff0c\u4e00\u4e0a\u6765\u4fbf\u62fc\u8001 \u547d\uff0c\u6c14\u529b\u96be\u7ee7\uff0c\u6253\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u8f66\u8f9f\u90aa\u5251\u62db\u8d8a\u5c55\u8d8a\u5feb\uff0c\u628a\u65b9\u4eca\u660e\u6740\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u518d\u8fc7\u7247 \u523b\uff0c\u65b9\u4eca\u660e\u8d8a\u53d1\u4e0d\u6d4e\uff0c\u96ea\u9b42\u8c37\u4e2d\uff0c\u4f46\u89c1\u4e00\u56e2\u5251\u5149\uff0c\u76d8\u65cb\u98de\u821e\uff0c\u65b9\u4eca\u660e\u7684\u8eab\u5f62\u5df2\u88ab\u88f9\u5728\u5251\u5149\u4e4b \u4e2d\u3002 \u3000\u3000\u8463\u5de8\u5ddd\u4e0e\u4e86\u56e0\u8896\u624b\u65c1\u89c2\uff0c\u76f8\u89c6\u800c\u7b11\u3002\u8463\u5de8\u5ddd\u9053\uff1a\u201c\u8fd9\u4eba\u679c\u7136\u771f\u5fc3\u6295\u5e08\uff0c\u4f60\u9001\u4ed6\u4e00\u628a\u6e38\u9f99 \u5b9d\u5251\u4e5f\u8fd8\u503c\u5f97\u3002\u201d\u4e86\u56e0\u9053\uff1a\u201c\u53cd\u6b63\u662f\u6177\u4ed6\u4eba\u4e4b\u6168\uff0c\u7b97\u5f97\u4ec0\u4e48\uff1f\u201d\u53c8\u9053\uff1a\u201c\u8fd9\u4eba\u5251\u6cd5\u9ad8\u5f3a\uff0c\u8fdc \u5728\u6d77\u4e91\u4e4b\u4e0a\uff0c\u53ea\u4e0d\u77e5\u4ed6\u8f7b\u529f\u5982\u4f55\uff1f\u201d\u8463\u5de8\u5ddd\u77e5\u9053\u4e86\u56e0\u5fc3\u610f\uff0c\u7b11\u9053\uff1a\u201c\u770b\u6765\u8f7b\u529f\u4e5f\u8fd8\u4e0d\u5f31\uff0c\u5c06 \u6765\u518d\u78b0\u5230\u4f60\u7684\u5e08\u59b9\u65f6\uff0c\u53ef\u4ee5\u8ba9\u4ed6\u51fa\u624b\u4e00\u8bd5\u3002\u201d\u539f\u6765\u4e86\u56e0\u529f\u529b\u867d\u6bd4\u5415\u56db\u5a18\u8f83\u9ad8\uff0c\u4f46\u788d\u4e8e\u5979\u7684\u5251 \u6cd5\u8f7b\u529f\uff0c\u51e0\u6b21\u90fd\u53ea\u662f\u6253\u6210\u5e73\u624b\uff0c\u64d2\u5979\u4e0d\u5f97\u3002\u6240\u4ee5\u5f88\u60f3\u7269\u8272\u4e00\u4e2a\u5251\u6cd5\u8f7b\u529f\u9020\u8be3\u6df1\u539a\u7684\u4eba\u505a\u4ed6\u7684 \u52a9\u624b\u3002 \u3000\u3000\u8c37\u4e2d\u4e8c\u4eba\u8d8a\u6597\u8d8a\u70c8\uff0c\u8463\u5de8\u5ddd\u7b11\u9053\uff1a\u201c\u4e0d\u51fa\u4e09\u5341\u62db\uff0c\u65b9\u4eca\u660e\u5fc5\u6b7b\u4e8e\u8f66\u8f9f\u90aa\u5251\u4e0b\u3002\u201d\u4e86\u56e0\u7b11 \u9053\uff1a\u201c\u4fbf\u5b9c\u4e86\u5e74\u7fb9\u5c27\u8fd9\u5c0f\u5b50\uff0c\u6349\u4e86\u5141\u5824\uff0c\u8fde\u4ed6\u624b\u4e0b\u4e24\u540d\u6700\u5f97\u529b\u7684\u6b66\u58eb\u4e5f\u89e3\u51b3\u4e86\uff0c\u62a5\u4e0a\u53bb\u53c8\u662f \u4e00\u4e2a\u5927\u529f\u3002\u201d\u7b11\u8c08\u4e4b\u95f4\u3002\u5ffd\u542c\u5f97\u65b9\u4eca\u660e\u60e8\u53eb\u4e00\u58f0\uff0c\u60f3\u662f\u4e2d\u4e86\u4e00\u5251\uff0c\u8463\u5de8\u5ddd\u629a\u638c\u5927\u7b11\uff0c\u5f97\u610f\u4e8e \u81ea\u5df1\u773c\u529b\u65e0\u5dee\uff0c\u90a3\u6599\u7b11\u58f0\u672a\u505c\uff0c\u559d\u58f0\u9661\u8d77\uff0c\u96ea\u9b42\u5792\u4e0a\u7a81\u7136\u98de\u4e0b\u6570\u4eba\u3002\u4e3a\u9996\u7684\u6b63\u662f\u7384\u98ce\u9053\u957f\uff0c \u5927\u58f0\u559d\u9053\uff1a\u201c\u4e86\u56e0\u79c3\u8d3c\uff0c\u5feb\u6765\u9886\u6b7b\uff01\u201d\u8ddf\u7740\u7684\u9664\u4e86\u5173\u4e1c\u56db\u4fa0\u4e4b\u5916\uff0c\u8fd8\u6709\u9648\u5fb7\u6cf0\u548c\u5510\u6653\u6f9c\u3002 \u3000\u3000\u539f\u6765\u9648\u5fb7\u6cf0\u65e9\u9884\u6599\u5230\u8f66\u8f9f\u90aa\u4f1a\u7ea6\u4eba\u540c\u6765\uff0c\u8fd9\u4e0d\u662f\u8f66\u8f9f\u90aa\u6015\u6597\u4e0d\u8fc7\u65b9\u4eca\u660e\uff0c\u800c\u662f\u5e74\u7fb9\u5c27\u653e \u5fc3\u4e0d\u8fc7\uff0c\u5fc5\u8981\u6d3e\u4eba\u76d1\u89c6\u3002\u9648\u5fb7\u6cf0\u89c1\u65b9\u4eca\u660e\u592a\u8fc7\u7cca\u6d82\uff0c\u6240\u4ee5\u4e8b\u5148\u5e76\u4e0d\u529d\u4ed6\uff0c\u6697\u4e2d\u5374\u7ea6\u4e86\u5173\u4e1c\u56db \u4fa0\uff0c\u5728\u5d16\u4e0a\u73af\u4f3a\u3002\u903c\u624d\u5f53\u8463\u5de8\u5ddd\u548c\u4e86\u56e0\u76f8\u89c6\u800c\u7b11\u4e4b\u65f6\uff0c\u9648\u5fb7\u6cf0\u4e0e\u5510\u6653\u6f9c\u4e5f\u76f8\u89c6\u800c\u7b11\uff0c\u9648\u5fb7\u6cf0 \u9053\uff1a\u201c\u8ba9\u65b9\u4eca\u660e\u773c\u89c1\u4ed6\u8fd9\u4f4d\u597d\u670b\u53cb\u7684\u771f\u9762\u76ee\uff0c\u4ed6\u624d\u4f1a\u5fc3\u6b7b\u3002\u201d\u5510\u6653\u6f9c\u8fd9\u624d\u77e5\u9053\u9648\u5fb7\u6cf0\u7684\u7528\u610f\u3002 \u3000\u3000\u4e86\u56e0\u9aa4\u89c1\u5173\u4e1c\u56db\u4fa0\u98de\u6765\uff0c\u9887\u51fa\u610f\u5916\u3002\u4ed6\u7edd\u672a\u6599\u5230\u65b9\u4eca\u660e\u4f1a\u7ea6\u8fd9\u56db\u4eba\u52a9\u62f3\uff0c\u4f46\u4e5f\u50b2\u7136\u4e0d \u60e7\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f5b\u7237\u8fd8\u6015\u4f60\u4eec\u4e0d\u6210\uff01\u201d\u7985\u6756\u4e00\u6325\uff0c\u628a\u7384\u98ce\u7684\u957f\u5251\u9707\u4e86\u5f00\u53bb\uff1b\u67f3\u5148\u5f00\u8eab\u5f62\u98de \u8d77\uff0c\u5341\u6307\u94a2\u73af\uff0c\u5411\u4e86\u56e0\u7684\u5149\u5934\u731b\u51ff\uff0c\u4e86\u56e0\u7985\u6756\u4e00\u6296\uff0c\u547c\u547c\u5e26\u98ce\uff0c\u4e00\u62db\u201c\u6f5c\u9f99\u5347\u5929\u201d\uff0c\u76f4\u6296\u4e0a \u53bb\uff0c\u67f3\u5148\u5f00\u4e0d\u6562\u4e0b\u843d\uff0c\u5728\u7a7a\u4e2d\u4e00\u626d\u8170\u8eab\uff0c\u659c\u63a0\u98de\u5f00\uff0c\u4f46\u4e86\u56e0\u4e00\u505c\uff0c\u4ed6\u53c8\u98de\u6765\u3002\u4e86\u56e0\u5927\u6012\uff0c\u6697 \u8fd0\u5185\u529f\uff0c\u5411\u7384\u98ce\u731b\u4e0b\u6740\u624b\uff0c\u67f3\u5148\u5f00\u98de\u6765\u4e00\u51ff\uff0c\u4e86\u56e0\u6beb\u4e0d\u7406\u4f1a\uff0c\u4e00\u6756\u5411\u7384\u98ce\u6a2a\u626b\u8fc7\u53bb\uff01\u67f3\u5148\u5f00 \u5341\u6307\u94a2\u73af\uff0c\u9f50\u9f50\u4e0b\u51fb\uff0c\u535c\u535c\u8fde\u58f0\uff0c\u5c31\u5982\u51ff\u5728\u94a2\u677f\u4e00\u822c\uff0c\u67f3\u5148\u5f00\u5927\u5403\u4e00\u60ca\uff0c\u7adf\u7ed9\u53cd\u649e\u56de\u53bb\uff01\u7384 \u98ce\u9053\u957f\u54ea\u91cc\u6321\u5f97\u4f4f\u4e86\u56e0\u7684\u5168\u529b\u8fdb\u51fb\uff0c\u594b\u529b\u6321\u4e86\u4e8c\u62db\uff0c\u864e\u53e3\u6d41\u8840\uff01\u4e86\u56e0\u5f53\u5934\u4e00\u6756\uff0c\u5411\u7384\u98ce\u9876\u95e8 \u51fb\u4e0b\uff0c\u6717\u6708\u7985\u5e08\u5ffd\u7136\u659c\u523a\u51b2\u6765\uff0c\u5927\u53e3\u4e00\u5f20\uff0c\u55b7\u9152\u6210\u6d6a\uff0c\u4e86\u56e0\u7a81\u89c1\u773c\u524d\u767d\u832b\u832b\u4e00\u7247\uff0c\u6025\u5fd9\u4e3e\u8896 \u906e\u773c\uff0c\u7f13\u4e86\u4e00\u7f13\uff0c\u7384\u98ce\u5251\u6cd5\u5feb\u6377\u5f02\u5e38\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u523a\u5230\u4e86\u56e0\u808b\u4e0b\uff0c\u4ee5\u4e3a\u8fd9\u62db\u5fc5\u7136\u5f97\u624b\uff0c\u90a3\u6599 \u4e86\u56e0\u5185\u529f\u786e\u662f\u6df1\u6e5b\uff0c\u542c\u98ce\u8fa8\u62db\uff0c\u808c\u8089\u9661\u7136\u5185\u9677\uff0c\u7384\u98ce\u4e00\u5251\u523a\u53bb\uff0c\u5251\u5c16\u5df2\u7ecf\u7740\u8089\uff0c\u9661\u89c9\u8f6f\u7ef5\u7ef5 \u7684\u65e0\u4ece\u7740\u529b\uff0c\u800c\u7384\u98ce\u7684\u5251\u53c8\u5df2\u653e\u5c3d\uff0c\u5c31\u662f\u76f8\u5dee\u8fd9\u4e48\u534a\u5bf8\uff0c\u65e0\u6cd5\u523a\u8fdb\uff0c\u4e86\u56e0\u5927\u559d\u4e00\u58f0\uff0c\u5de6\u8098\u4e0b \u6c89\uff0c\u731b\u7136\u5411\u7384\u98ce\u649e\u53bb\u3002\u8fd9\u65f6\u4e24\u4eba\u90fd\u5df2\u662f\u6b3a\u8eab\u8089\u640f\u4e86\uff0c\u4e86\u56e0\u7684\u7985\u6756\u672a\u53ca\u64a4\u56de\uff0c\u7384\u98ce\u7684\u5251\u62d0\u4e5f\u5df2 \u4ece\u4e86\u56e0\u4e24\u65c1\u4f38\u51fa\uff0c\u65e0\u6cd5\u56de\u6551\uff01 \u3000\u3000\u9648\u5143\u9738\u89c1\u52bf\u5371\u6025\uff0c\u594b\u4e0d\u987e\u8eab\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u548c\u8eab\u649e\u53bb\uff0c\u786c\u63a5\u4e86\u4e86\u56e0\u8fd9\u62db\uff0c\u9648\u5143\u9738\u7ec3\u5c31\u94dc\u76ae \u94c1\u9aa8\uff0c\u529b\u5927\u65e0\u7a77\uff0c\u4e24\u4eba\u4e00\u78b0\uff0c\u4e86\u56e0\u8e09\u8e09\u8dc4\u8dc4\u5012\u9000\u51e0\u6b65\uff0c\u9648\u5143\u9738\u5374\u88ab\u649e\u5f97\u66f4\u60e8\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u773c \u524d\u91d1\u661f\u4e71\u821e\uff0c\u53e3\u5410\u9c9c\u8840\uff0c\u98de\u6eda\u51fa\u6570\u4e08\u5f00\u5916\u3002\u5e78\u4ed6\u58ee\u4f3c\u86ee\u725b\uff0c\u5410\u4e86\u4e00\u53e3\u9c9c\u8840\uff0c\u4f11\u606f\u7247\u523b\uff0c\u53c8\u5982 \u65e0\u4e8b\uff01\u7ffb\u8eab\u8df3\u8d77\uff0c\u7985\u62f3\u590d\u4e0a\uff01 \u3000\u3000\u4e86\u56e0\u529b\u6321\u5173\u4e1c\u897f\u4fa0\uff0c\u8111\u95e8\u4e2d\u4e86\u67f3\u5148\u5f00\u7684\u5341\u6307\u94a2\u73af\uff0c\u9690\u9690\u4f5c\u75db\uff0c\u4e0d\u6562\u518d\u884c\u786c\u6321\u3002\u6717\u6708\u7985\u5e08 \u53c8\u628a\u9152\u6d6a\u55b7\u6765\uff0c\u4e86\u56e0\u5927\u8896\u4e00\u6325\uff0c\u7092\u98de\u98ce\u8d77\uff0c\u628a\u6717\u6708\u7985\u5e08\u7684\u9152\u6d6a\u6fc0\u5f97\u56db\u5904\u98de\u6e85\uff0c\u9152\u9999\u6251\u9f3b\u3002\u5173 \u4e1c\u56db\u4fa0\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u4f46\u4e86\u56e0\u65e2\u8981\u5206\u5fc3\u9632\u5fa1\u6717\u6708\u7985\u5e08\u55b7\u9152\u6210\u7ec3\u7684\u72ec\u95e8\u6b66\u529f\uff0c\u53c8\u8981\u95ea\u907f\u201c\u4e07\u91cc\u8ffd \u98ce\u201d\u67f3\u5148\u5f00\u7684\u94a2\u73af\u95ea\u51fb\uff0c\u5728\u7384\u98ce\u5feb\u6377\u5f02\u5e38\u7684\u4e71\u62ab\u98ce\u5251\u6cd5\u653b\u51fb\u4e4b\u4e0b\uff0c\u8fd8\u8981\u5e94\u4ed8\u9648\u5143\u9738\u7684\u5927\u6454\u7891 \u624b\uff0c\u4ee5\u4e00\u654c\u56db\uff0c\u7adf\u7136\u5904\u4e86\u4e0b\u98ce\uff01 \u3000\u3000\u65b9\u4eca\u660e\u6597\u5f97\u7cbe\u75b2\u529b\u7aed\uff0c\u81ea\u4efd\u5fc5\u6b7b\uff0c\u5510\u6653\u6f9c\u7a81\u7136\u6251\u5230\uff0c\u6362\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u4e00\u62db\u201c\u5929\u5c71\u98de \u96ea\u201d\uff0c\u51cc\u7a7a\u51fb\u4e0b\uff0c\u5251\u5149\u95ea\u95ea\uff0c\u771f\u5982\u96ea\u82b1\u98d8\u821e\uff0c\u5343\u70b9\u4e07\u767d\uff0c\u76f4\u6d12\u4e0b\u6765\uff0c\u8f66\u8f9f\u90aa\u628a\u5b9d\u5251\u821e\u4e86\u4e00\u9053 \u94f6\u8679\uff0c\u529b\u6321\u5f00\u53bb\u3002\u4e24\u4eba\u90fd\u662f\u4f7f\u5251\u7684\u9ad8\u624b\uff0c\u8bba\u529f\u529b\uff0c\u90a3\u662f\u8f66\u8f9f\u90aa\u8981\u9ad8\u5f97\u591a\uff0c\u4f46\u8bba\u5251\u6cd5\uff0c\u90a3\u5374\u662f \u5510\u6653\u6f9c\u8fdc\u4e3a\u4f18\u80dc\u3002\u8f66\u8f9f\u90aa\u903c\u5f97\u8fd0\u7528\u5185\u529b\uff0c\u4ee5\u7c98\u8fde\u6fc0\u8361\u4e4b\u6cd5\uff0c\u6765\u62b5\u5fa1\u5510\u6653\u6f9c\u7edd\u5999\u7684\u5929\u5c71\u5251\u6cd5\u3002 \u672c\u6765\u82e5\u4ee5\u4e00\u654c\u4e00\uff0c\u65f6\u95f4\u4e00\u4e45\uff0c\u5510\u6653\u6f9c\u4e0d\u662f\u8f66\u8f9f\u90aa\u5bf9\u624b\uff0c\u4f46\u65b9\u4eca\u660e\u5f97\u4e86\u5e2e\u624b\uff0c\u7cbe\u795e\u9661\u632f\uff0c\u4ee5\u4e8c \u654c\u4e00\uff0c\u4e5f\u5360\u4e86\u4e0a\u98ce\uff01 \u3000\u3000\u8fd9\u65f6\u4e24\u8fb9\u4eba\u5206\u6210\u4e09\u5904\u53ae\u6740\uff0c\u5341\u4e8c\u6307\u795e\u5077\u9648\u5fb7\u6cf0\u72ec\u6218\u8001\u5978\u5de8\u6ed1\u7684\u8463\u5de8\u5ddd\uff0c\u8463\u5de8\u5ddd\u63a5\u62db\u8bd5 \u62db\uff0c\u4ee5\u516b\u5366\u6e38\u8eab\u638c\u4e2d\u7684\u76d8\u9f99\u7ed5\u6b65\u8eab\u6cd5\u865a\u51fb\u4e24\u638c\u3002\u516b\u5366\u6e38\u8eab\u638c\u4ee5\u98d8\u5ffd\u89c1\u79f0\uff0c\u82e5\u975e\u4e00\u6d41\u9ad8\u624b\uff0c\u5fc5 \u7136\u7ed9\u4ed6\u8017\u5c3d\u6c14\u529b\uff0c\u9732\u5c3d\u672c\u95e8\u6b66\u529f\uff0c\u7ed9\u654c\u4eba\u4ee5\u53ef\u4e58\u673a\u4f1a\uff0c\u90a3\u77e5\u8463\u5de8\u5ddd\u7684\u8001\u8c0b\u6df1\u7b97\uff0c\u5374\u6070\u6070\u7740\u4e86 \u9648\u5fb7\u6cf0\u7684\u9053\u513f\u3002\u539f\u6765\u9648\u5fb7\u6cf0\u7684\u771f\u5b9e\u6b66\u529f\uff0c\u5728\u8463\u5de8\u5ddd\u5510\u6653\u6f9c\u4e4b\u4e0b\uff0c\u4f46\u4ed6\u7ef0\u53f7\u201c\u795e\u5077\u201d\uff0c\u8eab\u624b\u81ea \u7136\u6e9c\u6ed1\u4e4b\u6781\uff0c\u52a0\u4ee5\u4ed6\u7684\u8f7b\u529f\u672c\u9886\uff0c\u4ea6\u81ea\u4e0d\u51e1\uff0c\u6240\u4ee5\u82e5\u975e\u548c\u4ed6\u4ee5\u5185\u529b\u76f8\u8f83\uff0c\u591a\u534a\u4f1a\u6000\u7591\u4ed6\u662f\u4e00 \u6d41\u9ad8\u624b\uff0c\u5510\u6653\u6f9c\u6628\u65e5\u5c31\u662f\u8fd9\u6837\u9886\u4e86\u5389\u5bb3\uff0c\u4ee5\u81f4\u5931\u58f0\u547c\u4ed6\u201c\u524d\u8f88\u201d\u7684\u3002 \u3000\u3000\u8463\u5de8\u5ddd\u865a\u51fb\u4e24\u638c\uff0c\u9648\u5fb7\u6cf0\u4f5c\u52bf\u6251\u51fb\uff0c\u5176\u5b9e\u4e5f\u662f\u865a\u62db\u3002\u8463\u5de8\u5ddd\u7528\u76d8\u9f99\u7ed5\u6b65\u7684\u8eab\u6cd5\u56f4\u7740\u4ed6\u65cb \u8f6c\uff0c\u9648\u5fb7\u6cf0\u4e5f\u662f\u4e1c\u4e00\u62f3\u3001\u897f\u4e00\u638c\uff0c\u5ffd\u4e1c\u5ffd\u897f\uff0c\u6ed1\u4f3c\u6ce5\u9cc5\u3002\u8463\u5de8\u5ddd\u89c1\u4ed6\u62f3\u6cd5\u6742\u4e71\u65e0\u7ae0\uff0c\u4f46\u8eab\u624b \u5374\u7075\u654f\u5230\u6781\uff0c\u8fd8\u4ee5\u4e3a\u662f\u81ea\u5df1\u5b64\u964b\u5be1\u95fb\uff0c\u770b\u4e0d\u51fa\u4ed6\u7684\u7cbe\u5999\u62f3\u672f\u3002\u8c01\u77e5\u9648\u5fb7\u6cf0\u4e43\u662f\u6545\u5f04\u7384\u865a\uff0c\u771f \u662f\u4e71\u6253\u4e00\u6c14\u7684\u3002\u8463\u5de8\u5ddd\u8d8a\u6253\u8d8a\u60ca\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u8bd5\u7740\u903c\u8fd1\u654c\u4eba\uff0c\u90a3\u77e5\u4ed6\u8fc7\u4efd\u5c0f\u5fc3\uff0c\u53c8\u7740\u4e86\u9648\u5fb7 \u6cf0\u7684\u9053\u513f\uff0c\u9648\u5fb7\u6cf0\u5df2\u770b\u51fa\u4ed6\u7528\u7684\u662f\u865a\u62db\uff0c\u770b\u4ed6\u903c\u8fd1\u81ea\u5df1\uff0c\u9661\u7136\u65bd\u5c55\u795e\u6109\u7edd\u6280\uff0c\u5728\u8463\u5de8\u5ddd\u6000\u4e2d \u4e00\u63a2\uff0c\u7acb\u5373\u8dc3\u5f00\uff0c\u628a\u624b\u4e00\u626c\uff0c\u54c8\u54c8\u5927\u7b11\uff01 \u3000\u3000\u8463\u5de8\u5ddd\u4fa7\u8eab\u4e00\u95ea\uff0c\u628a\u624b\u4e00\u6284\uff0c\u5c06\u9648\u5fb7\u6cf0\u6253\u6765\u7684\u6697\u5668\u63a5\u5728\u624b\u4e2d\u3002\u4e00\u770b\uff0c\u7adf\u7136\u662f\u4ed6\u5e73\u65e5\u60ef\u7528 \u7684\u6697\u5668\u900f\u9aa8\u9489\uff0c\u4e00\u6478\u6000\u4e2d\uff0c\u4e0d\u7531\u5f97\u6bdb\u9aa8\u609a\u7136\uff0c\u81ea\u5df1\u7684\u4e00\u5323\u5eff\u56db\u652f\u900f\u9aa8\u9489\u5168\u90fd\u4e0d\u89c1\u4e86\u3002\u9648\u5fb7\u6cf0 \u624b\u91cc\u96be\u9053\u662f\u7834\u94dc\u70c2\u94c1\u5417\uff1f \u3000\u3000\u8463\u5de8\u5ddd\u5fc3\u80c6\u5df2\u5bd2\uff0c\u9000\u540e\u51e0\u6b65\uff0c\u56db\u9762\u4e00\u770b\uff0c\u53ea\u89c1\u4e86\u56e0\u5728\u5173\u4e1c\u56db\u4fa0\u56f4\u653b\u4e4b\u4e0b\uff0c\u5df2\u663e\u5982\u4e0b\u98ce\uff0c \u8f66\u8f9f\u90aa\u529b\u654c\u5510\u6653\u6f9c\u548c\u65b9\u4eca\u660e\u4e8c\u4eba\uff0c\u4e5f\u65e9\u5df2\u662f\u4f18\u52a3\u6613\u52bf\uff0c\u6068\u6068\u60f3\u9053\uff1a\u201c\u65e9\u77e5\u65b9\u4eca\u660e\u8fd9\u53ae\u4f1a\u7ea6\u5e2e \u624b\uff0c\u771f\u8be5\u591a\u5e26\u51e0\u4e2a\u4eba\u6765\uff01\u201d\u4e86\u56e0\u8fd9\u65f6\u53c8\u4e2d\u4e86\u67f3\u5148\u5f00\u4e00\u8bb0\u94a2\u73af\uff0c\u66b4\u6012\u548c\u96f7\uff0c\u7985\u6756\u62a1\u5f97\u547c\u547c\u98ce \u54cd\uff0c\u7384\u98ce\u9053\u957f\u8d8b\u95ea\u6e38\u6597\uff0c\u6717\u6708\u7985\u5e08\u4e0d\u505c\u7684\u55b7\u9152\u52a9\u6218\uff0c\u4e00\u5927\u846b\u82a6\u9152\u90fd\u51e0\u4e4e\u55b7\u5b8c\uff0c\u4e86\u56e0\u8eab\u4e0a\u7684\u8888 \u88df\u5e72\u75ae\u767e\u5b54\uff0c\u4e5f\u81ea\u6709\u70b9\u60ca\u5fc3\uff0c\u8463\u5de8\u5ddd\u53eb\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\uff0c\u4ed6\u4eec\u4ee5\u591a\u4e3a\u80dc\uff0c\u5c31\u8ba9\u4ed6\u4eec\u591a\u6d3b\u51e0\u5929 \u5427\uff01\u201d\u4e86\u56e0\u5927\u543c\u4e00\u58f0\uff0c\u4e00\u6756\u628a\u7384\u98ce\u7684\u957f\u5251\u9694\u5f00\uff0c\u62d4\u6b65\u51b2\u524d\uff0c\u90a3\u6599\u9648\u5143\u9738\u5728\u6717\u6708\u7985\u5e08\u7684\u9152\u6d6a\u63a9 \u62a4\u4e4b\u4e0b\uff0c\u8d81\u7740\u4e86\u56e0\u4e00\u6756\u6253\u51fa\u5c1a\u672a\u6536\u56de\uff0c\u4f59\u52bf\u5df1\u8870\u4e4b\u9645\uff0c\u7a81\u7136\u594b\u8d77\u5168\u529b\uff0c\u53cc\u624b\u62b1\u7740\u7985\u6756\uff0c\u5411\u4e0b \u4e00\u6309\uff0c\u7384\u98ce\u638c\u6cd5\u4e58\u9699\u5373\u5165\uff0c\u6b3a\u8eab\u4e00\u5251\uff0c\u8ddd\u79bb\u65e2\u8fd1\uff0c\u52bf\u52b2\u529b\u8db3\uff0c\u4e86\u56e0\u5185\u529f\u867d\u9ad8\uff0c\u5de6\u8098\u4e00\u7f29\u4e00 \u683c\uff0c\u4e5f\u88ab\u523a\u7a7f\u81c2\u9aa8\uff0c\u8840\u67d3\u8863\u88f3\u3002\u4e86\u56e0\u5927\u559d\u4e00\u58f0\uff1a\u201c\u53bb\u3002\u201d\u7985\u6756\u4e00\u6296\uff0c\u5c06\u9648\u5143\u9738\u5f39\u4e0a\u534a\u7a7a\uff0c\u5e78 \u5728\u67f3\u5148\u5f00\u6b63\u81ea\u534a\u7a7a\u6251\u4e0b\uff0c\u4e00\u6293\u6293\u7740\u4e86\u9648\u5143\u9738\u7684\u8863\u9886\uff0c\u5b9a\u4f4f\u4e86\u4ed6\u7684\u8eab\u5f62\uff0c\u53cc\u53cc\u843d\u5730\uff0c\u4e86\u56e0\u7985\u6756 \u6025\u5954\uff0c\u4e00\u6756\u5411\u9648\u5fb7\u6cf0\u626b\u53bb\uff0c\u6756\u98ce\u6fc0\u8361\uff0c\u9648\u5fb7\u6cf0\u8eab\u5f62\u4e0d\u7a33\uff0c\u9669\u4e9b\u7ed9\u9707\u5012\uff0c\u4ed7\u7740\u8eab\u624b\u6e9c\u6ed1\uff0c\u6025\u907f \u5f00\u53bb\uff0c\u8463\u5de8\u5ddd\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4e86\u56e0\u9053\uff1a\u201c\u5feb\uff0c\u54b1\u4eec\u8054\u624b\u51b2\u4e0b\u5c71\u53bb\uff01\u201d\u8463\u5de8\u5ddd\u8fd9\u65f6\u5bf9\u9648\u5fb7\u6cf0\u5df2\u662f\u8d77 \u7591\uff0c\u6000\u7591\u9648\u5fb7\u6cf0\u672a\u5fc5\u6709\u771f\u5b9e\u672c\u9886\uff0c\u4f46\u89c1\u4e86\u56e0\u53d7\u4e86\u5251\u4f24\uff0c\u65e0\u5fc3\u604b\u6218\uff0c\u5c06\u521a\u624d\u63a5\u5728\u624b\u4e2d\u7684\u4e00\u679a\u900f \u9aa8\u9489\uff0c\u731b\u5411\u5510\u6653\u6f9c\u63b7\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u8f66\u7edf\u9886\uff0c\u5feb\u8d70\u5440\uff01\u201d \u3000\u3000\u8f66\u8f9f\u90aa\u65e9\u60f3\u51b2\u51fa\uff0c\u65e0\u5948\u5510\u6653\u6f9c\u7eca\u5f97\u751a\u7d27\uff0c\u5ffd\u89c1\u5510\u6653\u6f9c\u80a9\u5934\u4e00\u7f29\uff0c\u5251\u52bf\u4e00\u7f13\uff0c\u8f66\u8f9f\u90aa\u5927 \u559c\uff0c\u8d81\u52bf\u4e00\u62db\u201c\u56de\u98ce\u821e\u67f3\u201d\uff0c\u5b9d\u5251\u4e00\u65cb\uff0c\u201c\u53ee\u5f53\u201d\u4e00\u58f0\u628a\u5510\u6653\u6f9c\u7684\u5251\u7ede\u5f97\u8131\u624b\u98de\u53bb\uff0c\u5510\u6653\u6f9c \u6025\u7740\u8981\u62a2\u56de\u81ea\u5df1\u7684\u5b9d\u5251\uff0c\u4e00\u65f6\u60c5\u6025\uff0c\u5de6\u638c\u4e00\u63a8\u4e00\u62ff\uff0c\u7a7a\u624b\u786c\u62a2\uff0c\u8f66\u8f9f\u90aa\u5b9d\u5251\u4e00\u65cb\uff0c\u8f6c\u950b\u4e0b \u622e\uff0c\u5251\u5c16\u821e\u52a8\uff0c\u770b\u770b\u5c31\u8981\u523a\u5165\u5510\u6653\u6f9c\u5c0f\u8179\u4e4b\u4e2d\uff0c\u65b9\u4eca\u660e\u9661\u8d77\u4e00\u811a\uff0c\u6b63\u6b63\u8e22\u5728\u8f66\u8f9f\u90aa\u8170\u80ef\u4e4b \u4e0a\uff0c\u8f66\u8f9f\u90aa\u54ce\u54df\u4e00\u58f0\uff0c\u8dcc\u5728\u5730\u4e0a\uff0c\u5510\u6653\u6f9c\u6536\u52bf\u4e0d\u53ca\uff0c\u4e5f\u8dcc\u4e86\u4e0b\u6765\uff0c\u5510\u6653\u8c30\u6536\u52bf\u4e0d\u53ca\uff0c\u4e5f\u8dcc\u4e86 \u4e0b\u6765\uff0c\u6070\u6070\u538b\u7740\uff01\u725f\u8f9f\u90aa\u7684\u8eab\u5b50\uff0c\u5510\u6653\u6f9c\u5de6\u624b\u53c9\u5589\uff0c\u53f3\u624b\u62a2\u5251\u3002\u65b9\u4eca\u660e\u5927\u53eb\u9053\uff1a\u201c\u5510\u5144\u5f1f\u5c0f \u5fc3\uff01\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u8f66\u8f9f\u90aa\u84e6\u5730\u4e00\u817f\u817e\u8e22\uff0c\u5510\u6653\u8c30\u98de\u8dcc\u51fa\u6570\u4e08\u5f00\u5916\u3002\u5510\u6653\u6f9c\u5185\u529b\u4e0d\u5982\u654c\u4eba\uff0c\u76f8 \u8fd1\u8089\u640f\uff0c\u9669\u5403\u5927\u4e8f\uff01 \u3000\u3000\u65b9\u4eca\u660e\u65e0\u6687\u987e\u654c\uff0c\u5148\u884c\u6551\u53cb\uff0c\u628a\u5510\u6653\u6f9c\u6276\u8d77\uff0c\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4e0d\u5fc5\u987e\u6211\uff0c\u4f60\u53bb\u8ffd\u654c\uff01\u201d\u65b9 \u4eca\u660e\u4e00\u770b\uff0c\u5510\u6653\u6f9c\u53ea\u662f\u818a\u9aa8\u8131\u81fc\uff0c\u4e00\u9a73\u4fbf\u597d\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u66ff\u4f60\u628a\u5251\u62a2\u56de\uff01\u201d\u98de\u5954\u8ffd\u53bb\uff01 \u3000\u3000\u8fd9\u65f6\u4e86\u56e0\u548c\u8463\u5de8\u5ddd\u8d70\u5728\u6700\u524d\uff0c\u5173\u4e1c\u56db\u4fa0\u548c\u9648\u5fb7\u6cf0\u7d27\u8ddf\u5728\u540e\uff0c\u4e00\u8def\u8ffd\u51fa\u5c71\u8c37\u3002 \u3000\u3000\u8f66\u8f9f\u90aa\u5403\u4e86\u65b9\u4eca\u660e\u4e00\u817f\uff0c\u808b\u9aa8\u4e5f\u81ea\u9690\u9690\u4f5c\u75db\u3002\u4ed6\u9887\u4e3a\u7cbe\u7075\uff0c\u4e0d\u548c\u4e86\u56e0\u8463\u5de8\u5ddd\u540c\u4e00\u8def\u9003\uff0c \u514d\u53d7\u5173\u4e1c\u56db\u4fa0\u5a01\u80c1\uff0c\u72ec\u4e2a\u513f\u4ece\u659c\u523a\u5954\u51fa\uff0c\u65b9\u4eca\u660e\u7d27\u7d27\u8ddf\u4e0a\uff0c\u8f66\u8f9f\u90aa\u8f7b\u529f\u8f83\u4e86\u56e0\u7a0d\u597d\uff0c\u6284\u504f\u65c1 \u5c0f\u8def\uff0c\u9003\u5728\u6700\u540e\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u6211\u6709\u5b9d\u5251\u5728\u624b\uff0c\u65b9\u4eca\u660e\u4e0d\u662f\u6211\u7684\u5bf9\u624b\uff0c\u4ed6\u79bb\u5f00\u5927\u4f19\uff0c\u72ec\u81ea\u8ffd \u6765\uff0c\u53ea\u662f\u81ea\u5bfb\u6b7b\u8def\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5df2\u9003\u51fa\u8c37\u53e3\uff0c\u628a\u4e86\u56e0\u7b49\u4eba\u629b\u540e\u534a\u91cc\u4e4b\u9065\u3002\u5173\u4e1c\u56db\u4fa0\u4e2d\u7684\u67f3\u5148 \u5f00\u8f7b\u529f\u867d\u9ad8\uff0c\u4f46\u4ed6\u4eec\u5fc5\u987b\u56db\u4eba\u8054\u624b\u624d\u80fd\u632b\u8d25\u4e86\u56e0\uff0c\u6240\u4ee5\u67f3\u5148\u5f00\u4e5f\u53ea\u80fd\u65f6\u4e0d\u65f6\u5411\u4e86\u56e0\u9a9a\u6270\uff0c\u8fb9 \u8d70\u8fb9\u6253\uff0c\u4e0d\u6562\u8ffd\u8fc7\u4e86\u5934\u3002\u5173\u4e1c\u56db\u4fa0\u89c1\u4e86\u56e0\u53d7\u4e86\u5251\u4f24\uff0c\u7d27\u7d27\u8ffd\u8d76\uff0c\u7acb\u5fc3\u8981\u5728\u6e38\u6597\u4e4b\u4e2d\uff0c\u5c06\u4ed6\u56f0 \u6b7b\u3002 \u3000\u3000\u8f66\u8f9f\u90aa\u9003\u51fa\u5c71\u8c37\uff0c\u5fc3\u4e2d\u6b63\u81ea\u76d8\u7b97\uff0c\u51c6\u5907\u518d\u9003\u51fa\u4e00\u6bb5\u8def\u7a0b\uff0c\u5c31\u8981\u56de\u8eab\u5bf9\u65b9\u4eca\u660e\u75db\u4e0b\u6740\u624b\u3002 \u5ffd\u7136\u773c\u775b\u4e00\u4eae\uff0c\u8fce\u9762\u4e00\u4e2a\u5c11\u5973\u8d70\u6765\uff0c\u4e0d\u8fc7\u5341\u4e94\u516d\u5c81\u7684\u6837\u5b50\uff0c\u5934\u53d1\u8d77\u4e24\u4e2a\u83f1\u89d2\uff0c\u773c\u5982\u79cb\u6c34\uff0c\u8138 \u6cdb\u6843\u82b1\uff0c\u751f\u5f97\u771f\u5982\u7389\u5973\u4e0b\u51e1\uff0c\u5a07\u5c0f\u73b2\u73d1\uff0c\u5341\u5206\u53ef\u7231\uff01\u8f66\u8f9f\u90aa\u867d\u5728\u7d27\u5f20\u9003\u547d\u4e4b\u65f6\uff0c\u4e5f\u7981\u4e0d\u4f4f\u5411 \u5979\u6ce8\u89c6\u3002\u8fd9\u5c11\u5973\u8170\u60ac\u77ed\u5251\uff0c\u89c1\u8f66\u8f9f\u90aa\u5954\u6765\uff0c\u5ffd\u7136\u559d\u9053\uff1a\u201c\u6b62\u6b65\uff01\u201d \u3000\u3000\u8f66\u8f9f\u90aa\u6115\u7136\u505c\u6b65\uff0c\u90a3\u5c11\u5973\u9053\uff1a\u201c\u628a\u5251\u62ff\u6765\uff01\u201d\u8f66\u8f9f\u90aa\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u90a3\u5c11 \u5973\u8eab\u5f62\u7a81\u7136\u98de\u8d77\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u8d3c\uff0c\u4f60\u4e0d\u62ff\u6765\u59d1\u5a18\u81ea\u53d6\u4e86\uff01\u201d\u8f66\u8f9f\u90aa\u8f7b\u529f\u751a\u9ad8\uff0c\u53c8\u4e0d\u5fcd\u4e0b \u624b\u4f24\u5979\uff0c\u95ea\u8eab\u8eb2\u907f\uff0c\u5de6\u624b\u4e00\u4f38\uff0c\u5f85\u8981\u5c06\u5979\u624b\u8155\u62ff\u7740\uff0c\u4e0d\u6599\u773c\u775b\u4e00\u82b1\uff0c\u90a3\u5c11\u5973\u500f\u7684\u4ece\u5934\u9876\u98de \u8fc7\uff0c\u8f66\u8f9f\u90aa\u7a81\u89c9\u624b\u4e2d\u4e00\u8f7b\uff0c\u6e38\u9f99\u5b9d\u5251\u5df2\u88ab\u90a3\u5c11\u5973\u593a\u4e86\u8fc7\u53bb\uff01 \u3000\u3000\u8f66\u8f9f\u90aa\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8fd9\u5c11\u5973\u8f7b\u529f\u7adf\u7136\u8fdc\u5728\u4ed6\u4e0a\u3002\u800c\u4e14\u867d\u7136\u8bf4\u662f\u81ea\u5df1\u672c\u65e0\u6545\u610f\uff0c\u9632\u8303 \u4e0d\u5468\uff0c\u4f46\u8fd9\u5c11\u5973\u80fd\u5728\u4e3e\u624b\u4e4b\u95f4\uff0c\u5c31\u628a\u81ea\u5df1\u7684\u624b\u4e2d\u5b9d\u5251\u62a2\u53bb\uff0c\u8fd9\u4efd\u6b66\u529f\u4e5f\u786e\u662f\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u90a3\u5c11\u5973\u62a2\u4e86\u5b9d\u5251\uff0c\u62e6\u4f4f\u4e86\u8f66\u8f9f\u90aa\u53bb\u8def\uff0c\u5251\u950b\u4e00\u6307\uff0c\u559d\u9053\uff1a\u201c\u5feb\u8bf4\uff0c\u4f60\u8fd9\u628a\u5251\u662f\u4ece\u54ea\u91cc\u5077 \u6765\u7684\uff1f\u201d\u65b9\u4eca\u660e\u5df2\u5feb\u8ffd\u5230\u80cc\u540e\uff0c\u8f66\u8f9f\u90aa\u80cc\u8179\u53d7\u654c\uff0c\u6a2a\u4e86\u5fc3\uff0c\u500f\u7684\u6251\u8d77\uff0c\u4e00\u62f3\u5411\u5c11\u5973\u5288\u9762\u6253 \u53bb\uff0c\u90a3\u5c11\u5973\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8fd9\u5c0f\u8d3c\u8fd8\u8981\u884c\u51f6\u3002\u201d\u5b9d\u5251\u4e00\u6296\uff0c\u8fc5\u903e\u8ffd\u98ce\uff0c\u5237\u5237\u4e24\u5251\uff0c\u5de6\u523a\u53f3\u8170 \u201c\u7cbe\u4fc3\u7a74\u201d\uff0c\u53f3\u523a\u5de6\u81c2\u201c\u66f2\u6c60\u7a74\u201d\uff0c\u8f66\u8f9f\u90aa\u5f2f\u8170\u8f6c\u6b65\uff0c\u65bd\u5c55\u5168\u8eab\u672c\u9886\u624d\u907f\u5f00\u4e24\u5251\u3002\u5c11\u5973\u4e5f\u5fae \u9732\u8be7\u5f02\u4e4b\u8272\uff0c\u5fc3\u60f3\uff1a\u5e08\u5085\u8bf4\u6211\u7684\u6b66\u529f\u5df2\u5c3d\u53ef\u95ef\u8361\u6c5f\u6e56\uff0c\u4f55\u4ee5\u4e00\u51fa\u6765\u4fbf\u78b0\u89c1\u8fd9\u6837\u7684\u5f3a\u654c\uff0c\u4e24\u5251 \u90fd\u523a\u4ed6\u4e0d\u4e2d\uff1f\u82e5\u7136\u968f\u4fbf\u78b0\u89c1\u7684\u4eba\u90fd\u6709\u8fd9\u6837\u672c\u9886\uff0c\u90a3\u4eca\u540e\u53ef\u5f97\u66f4\u7559\u795e\u4e86\u3002 \u3000\u3000\u8f66\u8f9f\u90aa\u518d\u907f\u4e24\u62db\uff0c\u65b9\u4eca\u660e\u5df2\u7136\u8d76\u5230\uff0c\u89c1\u72b6\u4e5f\u662f\u6781\u4e3a\u60ca\u8be7\uff0c\u6b63\u60f3\u5e2e\u90a3\u5c11\u5973\uff0c\u90a3\u5c11\u5973\u5374\u5148\u559d \u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff0c\u4e0d\u51c6\u4e0a\u6765\uff01\u201d\u65b9\u4eca\u660e\u4e00\u6115\u6b62\u6b65\uff0c\u4f46\u89c1\u90a3\u5c11\u5973\u4e00\u5251\u5feb\u4f3c\u4e00\u5251\uff0c\u628a\u8f66\u8f9f\u90aa\u8ffd\u5f97\u56e2 \u56e2\u4e71\u8f6c\uff0c\u5251\u6cd5\u4e4b\u5999\uff0c\u7adf\u662f\u751f\u5e73\u4ec5\u89c1\uff01\u65b9\u4eca\u660e\u53f9\u4e86\u53e3\u6c14\uff0c\u6697\u9053\uff1a\u5929\u4e0b\u771f\u591a\u80fd\u4eba\uff0c\u8fd9\u6837\u4e00\u4e2a\u5973\u5b69 \u5b50\u4e5f\u6709\u8fd9\u4e48\u9ad8\u672c\u9886\uff0c\u81ea\u5df1\u4ee5\u524d\u76ee\u7a7a\u56db\u6d77\uff0c\u771f\u662f\u4e95\u5e95\u4e4b\u86d9\uff01 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u7389\u5973\u8bd5\u8eab\u624b\uff0c\u5251\u6cd5\u89c1\u96c4\u5947\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56db\u56de\u3000\u59d0\u59b9\u82b1\u5e76\u5f00\u3000\u5f20\u51a0\u674e\u6234\u3000\u6069\u4ec7\u7ed3\u96be\u89e3\u3000\u82cd\u7a79\u767d\u4e91 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u56db\u56de \u59d0\u59b9\u82b1\u5e76\u5f00\u3000\u5f20\u51a0\u674e\u6234 \u6069\u4ec7\u7ed3\u96be\u89e3\u3000\u82cd\u7a79\u767d\u4e91 \u3000\u3000\u672c\u6765\u8bba\u8d77\u6b66\u529f\uff0c\u8f66\u8f9f\u90aa\u548c\u90a3\u5c11\u5973\u5404\u6709\u64c5\u957f\uff0c\u82e5\u7136\u5404\u4ed7\u5b9d\u5251\u53ae\u62fc\uff0c\u90a3\u662f\u534a\u65a4\u516b\u4e24\u3002\u65e0\u5948\u8f66 \u8f9f\u90aa\u548c\u65b9\u4eca\u660e\u5510\u6653\u6f9c\u62fc\u6597\u534a\u65e5\uff0c\u6c14\u529b\u6d88\u8017\u8bb8\u591a\uff1b\u53c8\u5403\u4e86\u65b9\u4eca\u660e\u4e00\u817f\uff0c\u95ea\u5c55\u817e\u632a\uff0c\u53d7\u4e86\u5f71\u54cd\uff1b \u800c\u4e14\u662f\u7a7a\u624b\u640f\u6597\uff0c\u5904\u5904\u90fd\u53d7\u90a3\u5c11\u5973\u5b9d\u5251\u7684\u5a01\u80c1\u3002\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u90a3\u5c11\u5973\u559d\u58f0\uff1a\u201c\u7740\uff01\u201d\u5530\u7684 \u4e00\u5251\uff0c\u5c06\u8f66\u8f9f\u90aa\u80a9\u808b\u9aa8\u523a\u7a7f\uff01\u8f66\u8f9f\u90aa\u4e5f\u771f\u4e86\u5f97\uff0c\u4f0f\u5730\u4e00\u6eda\uff0c\u817e\u8eab\u8dc3\u51fa\u6570\u4e08\u4ee5\u5916\uff0c\u65b9\u4eca\u660e\u770b\u5f97 \u5446\u4e86\uff0c\u7adf\u7136\u5fd8\u4e86\u534f\u52a9\u5e2e\u5979\u64d2\u4e4b\u4eba\u3002 \u3000\u3000\u90a3\u5c11\u5973\u4e00\u5251\u5c06\u8f66\u8f9f\u90aa\u523a\u4f24\uff0c\u53cd\u800c\u5403\u4e86\u4e00\u60ca\uff0c\u539f\u6765\u8fd9\u8fd8\u662f\u5979\u7b2c\u4e00\u6b21\u51fa\u624b\u4f24\u4eba\uff0c\u9661\u89c1\u5251\u5c16\u5e26 \u8840\uff0c\u624b\u53cd\u8f6f\u4e86\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u731b\u542c\u5f97\u6709\u4eba\u5927\u58f0\u53eb\u9053\uff1a\u201c\u7433\u4e2b\u5934\uff0c\u4f60\u8dd1\u5230\u8fd9\u91cc\u5e72\u561b\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u6115\u7136\u4e0d\u89e3\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u6c99\u5c18\u6eda\u6eda\uff0c\u4e00\u5927\u5806\u4eba\u6740\u5954\u8fc7\u6765\uff0c\u4e3a\u9996\u7684\u672c\u6765\u662f\u4e2a\u548c\u5c1a\uff0c\u624b \u63d0\u7985\u6756\uff0c\u5341\u5206\u51f6\u6076\uff0c\u558a\u58f0\u5c31\u662f\u4ed6\u53d1\u51fa\u6765\u7684\uff1b\u53ef\u662f\u8f6c\u773c\u4e4b\u95f4\uff0c\u4ed6\u80cc\u540e\u7684\u4eba\uff0c\u5df1\u7eb7\u7eb7\u51b2\u4e0a\u3002\u90a3\u4e9b \u8ffd\u8d76\u548c\u5c1a\u7684\u4eba\uff0c\u6709\u9ad8\u6709\u77ee\uff0c\u6709\u9053\u58eb\u4e5f\u6709\u548c\u5c1a\u3002\u4e00\u4e2a\u7626\u957f\u6c49\u5b50\uff0c\u8ffd\u4e24\u6b65\u4fbf\u98de\u8d77\u8eab\u6765\uff0c\u5c48\u7740\u5341 \u6307\uff0c\u5411\u90a3\u548c\u5c1a\u5149\u5934\u731b\u51ff\uff1b\u6709\u4e00\u540d\u9053\u58eb\uff0c\u5411\u90a3\u548c\u5c1a\u5f6a\u523a\u4e24\u5251\uff0c\u4fbf\u62a2\u7740\u51b2\u4e86\u51fa\u6765\uff0c\u6076\u72e0\u72e0\u7684\u5411\u81ea \u5df1\u77aa\u773c\u3002\u8fd9\u5c11\u5973\u770b\u5f97\u5341\u5206\u7eb3\u7f55\uff0c\u53ea\u542c\u5f97\u8fd9\u540d\u9053\u58eb\u5927\u58f0\u559d\u9053\uff1a\u201c\u558f\uff0c\u4f60\u8fd9\u4e2a\u91ce\u4e2b\u5934\uff0c\u5feb\u968f\u6211\u56de \u53bb\uff01\u201d \u3000\u3000\u8fd9\u5c11\u5973\u6b63\u662f\u51af\u745b\u3002\u5979\u968f\u6613\u5170\u73e0\u5b66\u4e86\u5c06\u8fd1\u5341\u5e74\u6b66\u529f\uff0c\u5df2\u5c3d\u5f97\u5929\u5c71\u5251\u6cd5\u7cbe\u9ad3\uff0c\u52a0\u4ee5\u5979\u53c8\u662f\u5e7c \u5e74\u4fbf\u4e60\u6b63\u5b97\u5185\u529f\uff0c\u6839\u5e95\u8981\u6bd4\u5510\u6653\u6f9c\u597d\u5f97\u591a\u3002\u8f7b\u8eab\u672c\u9886\uff0c\u66f4\u975e\u5510\u6653\u6f9c\u53ef\u6bd4\u3002\u6613\u5170\u73e0\u665a\u5e74\u6536\u5f92\uff0c \u628a\u5979\u89c6\u4e3a\u552f\u4e00\u4f20\u4eba\uff08\u5510\u6653\u6f9c\u4e0d\u8fc7\u662f\u6302\u540d\u5f1f\u5b50\uff09\uff0c\u4e3a\u4e86\u6015\u5f71\u54cd\u5979\u5b66\u6b66\u7684\u5fc3\u60c5\uff0c\u5e76\u6ca1\u6709\u5c06\u5979\u60e8\u75db \u7684\u8eab\u4e16\u544a\u8bc9\u7ed9\u5979\u3002\u5979\u53ea\u4f9d\u7a00\u8bb0\u5f97\u5e7c\u5e74\u65f6\u66fe\u5728\u4e00\u4e2a\u6781\u5927\u7684\u5bab\u6bbf\u4e2d\u4f4f\u8fc7\uff0c\u90a3\u91cc\u9762\u6709\u8bb8\u8bb8\u591a\u591a\u7684 \u4eba\uff0c\u6613\u5170\u73e0\u4fbf\u544a\u8bc9\u5979\u90a3\u662f\u4e00\u4e2a\u5f3a\u76d7\u7a9d\uff0c\u5979\u4fbf\u662f\u5728\u90a3\u91cc\u957f\u5927\u540e\u6765\u7ed9\u6551\u51fa\u6765\u7684\u3002\u53e6\u5916\u5979\u8fd8\u8bb0\u5f97\u4e00 \u4e2a\u201c\u5510\u53d4\u53d4\u201d\uff0c\u90a3\u4e2a\u4eba\u4e5f\u66fe\u8ddf\u8fc7\u5e08\u5085\u5b66\u5251\uff0c\u53ef\u662f\u5e08\u5085\u8bf4\u8fd9\u4e2a\u4eba\u4e0d\u7b97\u5979\u7684\u5f92\u5f1f\uff0c\u53eb\u4ed6\u4eec\u4ee5\u53d4\u4f84 \u76f8\u79f0\uff0c\u8fd9\u4e2a\u201c\u5510\u53d4\u53d4\u201d\u5728\u5979\u5341\u51e0\u5c81\u90a3\u5e74\u4e0b\u5c71\u4e86\u3002\u6b64\u5916\u6700\u719f\u7684\u4eba\u4fbf\u662f\u5929\u5c71\u5357\u9ad8\u5cf0\u7684\u674e\u4f2f\u6bcd\u548c \u201c\u674e\u54e5\u54e5\u201d\uff0c\u5979\u5728\u6b66\u743c\u7476\u90a3\u91cc\u4f4f\u8fc7\u4e00\u5e74\uff0c\u90a3\u4e2a\u201c\u674e\u54e5\u54e5\u201d\u6bd4\u201c\u5510\u53d4\u53d4\u201d\u4e0d\u8fc7\u5c0f\u51e0\u5e74\uff0c\u4e0d\u8fc7\u6bd4 \u201c\u5510\u53d4\u53d4\u201d\u597d\u73a9\u5f97\u591a\u3002 \u3000\u3000\u51af\u745b\u8fd9\u65f6\u5df2\u5341\u516d\u5c81\u4e86\uff0c\u4e0b\u5c71\u8fd8\u4e0d\u5230\u4e00\u6708\uff0c\u6613\u5170\u73e0\u53eb\u4ed6\u5148\u53bb\u627e\u5510\u53d4\u53d4\uff0c\u53ef\u662f\u6613\u5170\u73e0\u4e5f\u4e0d\u77e5 \u5510\u6653\u6f9c\u5728\u54ea\u91cc\uff0c\u56e0\u6b64\u53c8\u544a\u8bc9\u5979\u8bf4\u6709\u4e00\u4e2a\u201c\u5415\u56db\u5a18\u201d\u548c\u5510\u53d4\u53d4\u5f88\u8981\u597d\uff0c\u53eb\u5979\u5230\u9099\u5c71\u72ec\u81c2\u795e\u5c3c\u7684 \u5893\u65c1\u5927\u6811\u4e0a\u7559\u4e0b\u5b57\u8ff9\uff0c\u8bf4\u662f\u81ea\u5df1\u5df2\u7ecf\u4e0b\u5c71\uff0c\u53eb\u5415\u56db\u5a18\u89c1\u5b57\u540e\u5e26\u5510\u6653\u6f9c\u6765\u770b\u5979\u3002\u6613\u5170\u73e0\u5fc3\u60f3\u6c5f \u6e56\u4e03\u4fa0\u5728\u6c5f\u6e56\u4e0a\u4ea4\u6e38\u5e7f\u9614\uff0c\u800c\u6c5f\u5357\u4e03\u4fa0\uff0c\u6bcf\u5e74\u603b\u4f1a\u6709\u4eba\u4e0a\u9099\u5c71\u626b\u5893\uff0c\u4e0d\u7ba1\u662f\u54ea\u4e00\u4f4d\u89c1\u4e86\u51af\u745b \u7684\u7559\u5b57\uff0c\u90fd\u4f1a\u901a\u77e5\u5415\u56db\u5a18\uff0c\u800c\u5415\u56db\u5a18\u4e5f\u5fc5\u7136\u80fd\u627e\u5230\u5979\u7684\u3002 \u3000\u3000\u6613\u5170\u73e0\u53c8\u56e0\u4e3a\u9099\u5c71\u8ddd\u79bb\u9648\u7559\u826f\u4e0d\u8fdc\uff0c\u53c8\u5429\u5490\u5979\u5230\u4e86\u6cb3\u5357\u4e4b\u540e\uff0c\u53ef\u4ee5\u5230\u9648\u7559\u826f\u5e74\u5bb6\u7684\u540e\u82b1 \u56ed\u4e2d\u4e00\u770b\uff0c\u5e76\u544a\u8bc9\u5979\u8bf4\u540e\u82b1\u56ed\u6b63\u4e2d\u6709\u4e00\u95f4\u4e66\u623f\uff0c\u53eb\u5979\u5230\u90a3\u95f4\u4e66\u623f\u53bb\u7ec6\u7ec6\u641c\u67e5\uff0c\u770b\u6709\u4ec0\u4e48\u9057\u4e66 \u6ca1\u6709\u3002\u539f\u6765\u90a3\u5e74\u949f\u4e07\u5802\u88ab\u53cc\u9b54\u6240\u6740\uff0c\u6613\u5170\u73e0\u6070\u5de7\u7ecf\u8fc7\u90a3\u91cc\uff0c\u548c\u5415\u56db\u5a18\u7b49\u5c06\u53cc\u9b54\u53ca\u5176\u540c\u515a\u9010 \u9000\uff0c\u6613\u5170\u73e0\u5c06\u949f\u4e07\u5802\u9057\u4e0b\u7684\u533b\u4e66\u5251\u8bc0\u6536\u5165\u56ca\u4e2d\uff0c\u51c6\u5907\u5c06\u6765\u4ea4\u7ed9\u65e0\u6781\u6d3e\u7684\u4f20\u4eba\uff0c\u5f53\u65f6\u56e0\u592a\u8fc7\u5306 \u5fd9\uff0c\u6ca1\u6709\u8be6\u7ec6\u68c0\u89c6\uff0c\u540e\u6765\u624d\u53d1\u73b0\u5085\u9752\u4e3b\u7684\u9057\u8457\u300a\u91d1\u9488\u5ea6\u4e16\u300b\u4e00\u4e66\u6ca1\u6709\u5e26\u51fa\uff0c\u8fd9\u672c\u4e66\u4e43\u662f\u5085\u9752 \u4e3b\u4e00\u751f\u5fc3\u8840\uff0c\u4e0d\u4f46\u6709\u7cbe\u5999\u7684\u533b\u7406\uff0c\u8fd8\u6709\u65e0\u6781\u6d3e\u7684\u5185\u529f\u79d8\u5965\u3002\u6613\u5170\u73e0\u662f\u5085\u9752\u4e3b\u540e\u8f88\uff0c\u5085\u9752\u4e3b\u751f \u524d\u5bf9\u5979\u5f88\u4e3a\u7231\u62a4\uff0c\u6240\u4ee5\u6613\u5170\u73e0\u60f3\u53eb\u51af\u745b\u628a\u90a3\u672c\u4e66\u641c\u67e5\u51fa\u6765\u3002\u8fd9\u4e5f\u662f\u4e0d\u8ba9\u524d\u8f88\u5fc3\u8840\u6e6e\u5f79\u7684\u4e00\u70b9 \u5fc3\u610f\u3002 \u3000\u3000\u5c31\u662f\u8fd9\u6837\uff0c\u51af\u745b\u5728\u6731\u4ed9\u9547\u5230\u9648\u7559\u826f\u8def\u4e0a\u78b0\u5230\u4e86\u8fd9\u4e00\u6279\u4eba\u3002\u9996\u5148\u78b0\u5230\u7684\u662f\u8f66\u8f9f\u90aa\uff0c\u8f66\u8f9f\u90aa \u624b\u4e0a\u62ff\u7740\u7684\u6b63\u662f\u5510\u6653\u6f9c\u90a3\u628a\u6e38\u9f99\u5b9d\u523d\u3002\u8981\u77e5\u6e38\u9f99\u65ad\u7389\u4e8c\u5251\u4e43\u662f\u5929\u5c71\u4e4b\u5b9d\uff0c\u5510\u6653\u6f9c\u5f97\u4e86\u6e38\u9f99 \u5251\uff0c\u51af\u745b\u5f97\u7684\u662f\u65ad\u7389\u5251\uff0c\u4e24\u67c4\u5251\u5747\u662f\u767e\u4f59\u5e74\u524d\u6666\u660e\u7985\u5e08\u6240\u70bc\uff0c\u5f62\u5f0f\u76f8\u4f3c\uff0c\u53ea\u662f\u957f\u77ed\u4e0d\u540c\uff0c\u51af \u745b\u4e00\u89c1\u4fbf\u8ba4\u5f97\uff0c\u6240\u4ee5\u624d\u4f1a\u786c\u62a2\u4e86\u8f66\u8f9f\u90aa\u7684\u5251\uff0c\u5e76\u628a\u4ed6\u523a\u4f24\u3002 \u3000\u3000\u7384\u98ce\u9053\u957f\u89c1\u4e86\u51af\u745b\uff0c\u8bef\u4ee5\u4e3a\u5979\u5c31\u662f\u53bb\u5e74\u6240\u89c1\u7684\u51af\u7433\uff0c\u60f3\u8d77\u56db\u5f1f\u53d7\u5979\u6bd2\u5200\u6240\u4f24\uff0c\u60f3\u628a\u5979\u64d2 \u4f4f\uff0c\u4ea4\u7ed9\u5510\u6653\u6f9c\u5904\u7f6e\u3002\u9648\u5143\u9738\u4e5f\u968f\u540e\u8d76\u5230\u3002\u51af\u745b\u89c1\u7384\u98ce\u6076\u72e0\u72e0\u7684\u77aa\u7740\u5979\uff0c\u5fc3\u60f3\uff1a\u6c5f\u6e56\u4e0a\u6076\u4eba \u771f\u591a\uff0c\u8fd9\u73ed\u4eba\u6ca1\u6765\u7531\u7684\u6765\u6b3a\u8d1f\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u771f\u662f\u53ef\u6076\uff01\u53ea\u60f3\u5e08\u5085\u53eb\u6211\u4e0b\u5c71\u4e4b\u540e\u8981\u884c\u4fa0\u4ed7\u4e49\uff0c \u9504\u6076\u52a9\u5584\u3002\u8fd9\u4e2a\u6076\u9053\u58eb\uff0c\u975e\u7ed9\u4ed6\u4e00\u70b9\u5389\u5bb3\u5c1d\u5c1d\u4e0d\u53ef\u3002\u7384\u98ce\u4e00\u62d0\u6253\u6765\uff0c\u672c\u60f3\u6253\u6389\u5979\u624b\u4e2d\u7684\u6e38\u9f99 \u5b9d\u5251\uff0c\u7136\u540e\u518d\u6349\u5979\uff0c\u90a3\u6599\u4e00\u62d0\u5954\u524d\uff0c\u5bd2\u5149\u9661\u8d77\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u84ec\u98de\u4e2d\uff0c\u7384\u98ce\u5de6\u624b\u94c1\u62d0\u5df2\u7ecf \u65ad\u4e86\u4e00\u622a\uff01\u7384\u98ce\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u51af\u745b\u53cd\u8eab\u4e00\u5251\uff0c\u659c\u523a\u8fc7\u6765\uff0c\u7384\u98ce\u8fd8\u4e86\u4e00\u5251\uff0c\u521a\u521a\u51fa\u624b\uff0c\u51af\u745b \u5df2\u500f\u7684\u53d8\u62db\uff0c\u7b2c\u4e8c\u5251\u7b2c\u4e09\u5251\u63a5\u8fde\u800c\u81f3\uff01\u7384\u98ce\u7684\u201c\u4e71\u62ab\u98ce\u201d\u5251\u6cd5\u5df2\u4ee5\u8fc5\u6377\u7740\u79f0\uff0c\u90a3\u6599\u51af\u745b\u7684\u5251 \u62db\u6bd4\u4ed6\u8fd8\u8981\u5feb\u901f\uff0c\u7384\u98ce\u4ed7\u7740\u706b\u5019\u8001\u5230\uff0c\u89e3\u4e86\u4e09\u62db\uff0c\u7b2c\u56db\u62db\u51af\u745b\u4f7f\u51fa\u5929\u5c71\u5251\u6cd5\u7684\u7edd\u62db\uff0c\u4e00\u62db \u201c\u6d41\u661f\u7a7f\u6708\u201d\uff0c\u5251\u5c16\u7535\u4e5f\u4f3c\u7684\u5954\u5411\u7384\u98ce\u54bd\u5589\uff0c\u7384\u98ce\u4e3a\u4e86\u89e3\u8fd9\u4e00\u62db\uff0c\u903c\u5f97\u5c06\u5251\u6a2a\u5c01\u4e0a\u53bb\uff0c\u53ea\u542c \u5f97\u53c8\u662f\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u7384\u98ce\u624b\u4e2d\u7684\u957f\u5251\u53c8\u7ed9\u622a\u65ad\u4e86\uff01\u7384\u98ce\u5927\u5403\u4e00\u60ca\uff0c\u53cd\u8eab\u8dc3\u51fa\uff01\u4e86\u56e0\u51b2\u5230\uff0c \u4e00\u6756\u5411\u7384\u98ce\u5f53\u5934\u538b\u4e0b\uff0c\u7384\u98ce\u6025\u5fd9\u8df3\u5f00\uff0c\u67f3\u5148\u5f00\u9648\u5fb7\u6cf0\u7b49\u4eba\u7eb7\u7eb7\u6d8c\u4e0a\u3002 \u3000\u3000\u51af\u745b\u89c1\u548c\u5c1a\u6253\u9053\u58eb\uff0c\u5fc3\u60f3\u9053\u58eb\u662f\u6076\u4eba\uff0c\u8fd9\u548c\u5c1a\u5e94\u8be5\u662f\u597d\u4eba\u4e86\u3002\u90a3\u77e5\u4e86\u56e0\u89c1\u4e86\u51af\u745b\uff0c\u4e5f\u8bef \u4f1a\u5979\u662f\u51af\u7433\uff0c\u5fc3\u60f3\u8fd9\u51af\u7433\u4e43\u662f\u5f53\u4eca\u7687\u4e0a\u6240\u8981\u7684\u4eba\uff0c\u4e0d\u80fd\u8ba9\u5979\u518d\u8dd1\u4e86\uff0c\u7985\u6756\u56db\u56f4\u4e00\u8361\uff0c\u628a\u9648\u5fb7 \u6cf0\u67f3\u5148\u5f00\u903c\u5f00\uff0c\u8fd9\u5e2e\u4eba\u539f\u4ee5\u7384\u98ce\u4e3a\u9996\uff0c\u7384\u98ce\u62d0\u5251\u5747\u65ad\uff0c\u8fd9\u73ed\u4eba\u4e0d\u6562\u518d\u903c\u8fd1\u4e86\u56e0\u3002\u4e86\u56e0\u89c1\u51af\u745b \u624b\u4e0a\u62ff\u7684\u662f\u6e38\u9f99\u5b9d\u5251\uff0c\u5fc3\u60f3\u8fd9\u91ce\u4e2b\u5934\u771f\u662f\u65e0\u6cd5\u65e0\u5929\uff0c\u8fde\u8f66\u8f9f\u90aa\u4e5f\u523a\u4f24\u4e86\uff0c\u7985\u6756\u4e00\u6296\uff0c\u7a81\u7136\u5411 \u5979\u626b\u53bb\u3002 \u3000\u3000\u51af\u745b\u8bef\u4ee5\u4e3a\u4e86\u56e0\u662f\u597d\u4eba\uff0c\u90a3\u6599\u4ed6\u7a81\u7136\u4e00\u6756\u626b\u6765\uff0c\u51af\u745b\u788e\u4e0d\u53ca\u9632\uff0c\u4e3e\u5251\u4e00\u6321\uff0c\u53ee\u5f53\u58f0\u4e2d\uff0c \u4e86\u56e0\u7684\u7985\u6756\u867d\u7136\u7f3a\u4e86\u4e00\u53e3\uff0c\u51af\u745b\u7684\u6e38\u9f99\u5b9d\u5251\u5374\u5df2\u7ed9\u4ed6\u9707\u5f97\u8131\u624b\u98de\u53bb\uff0c\u8463\u5de8\u5ddd\u8dc3\u4e0a\u53bb\u62a2\uff0c\u9648\u5fb7 \u6cf0\u8eab\u5f62\u8fc5\u75be\uff0c\u62a2\u5148\u534a\u6b65\uff0c\u628a\u5b9d\u5251\u6293\u5230\u624b\u4e2d\uff0c\u8463\u5de8\u5ddd\u5927\u6012\uff0c\u6697\u8fd0\u5185\u529b\uff0c\u4e00\u638c\u9707\u53bb\uff0c\u9648\u5fb7\u6cf0\u65e0\u6cd5 \u4e0d\u5e94\u8fd9\u62db\uff0c\u7ffb\u638c\u4e00\u6321\uff0c\u8463\u5de8\u5ddd\u5927\u53eb\u4e00\u58f0\uff0c\u5012\u8dc3\u4e09\u4e08\uff0c\u624b\u638c\u6d41\u8840\uff01\u539f\u6765\u9648\u5fb7\u6cf0\u8d3c\u516c\u5b50\u72b6\u5143\u624d\uff0c \u660e\u77e5\u8f83\u91cf\u5b9e\u529b\uff0c\u4e0d\u662f\u8463\u5de8\u5ddd\u5bf9\u624b\uff0c\u638c\u4e2d\u6697\u631f\u4e86\u8463\u5de8\u5ddd\u7684\u4e00\u679a\u201c\u900f\u9aa8\u9489\u201d\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u9489\u5934\u4e0a \u6233\uff0c\u900f\u9aa8\u9489\u771f\u4e2a\u4ece\u638c\u5fc3\u900f\u8fc7\u638c\u80cc\uff01 \u3000\u3000\u9648\u5fb7\u6cf0\u63a5\u4e86\u8fd9\u638c\uff0c\u987f\u89c9\u5929\u65cb\u5730\u8f6c\uff0c\u6717\u6708\u7985\u5e08\u6025\u5fd9\u628a\u4ed6\u626f\u8fc7\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d\u9648\u5fb7\u6cf0 \u80cc\u8f6c\u4e86\u8138\uff0c\u5411\u8896\u53e3\u4e00\u5410\uff0c\u6717\u6708\u7985\u5e08\u89c1\u8896\u53e3\u6bb7\u7ea2\uff0c\u77e5\u4ed6\u53d7\u4e86\u5185\u4f24\uff0c\u6240\u4ee5\u8981\u80cc\u8f6c\u8138\u5410\u5165\u8896\u4e2d\uff0c\u4e43 \u662f\u6015\u654c\u4eba\u770b\u89c1\uff0c\u6025\u5fd9\u53eb\u7384\u98ce\u9053\uff1a\u201c\u54b1\u4eec\u8d70\uff01\u201d\u6b64\u65f6\u6b64\u9645\uff0c\u7384\u98ce\u65ad\u4e86\u5175\u5668\uff0c\u9648\u5fb7\u6cf0\u53d7\u4e86\u5185\u4f24\uff0c \u65b9\u4eca\u660e\u5df1\u6218\u81f3\u529b\u7aed\u7b4b\u75b2\uff0c\u5176\u4f59\u4e09\u4eba\u4e0d\u662f\u4e86\u56e0\u5bf9\u624b\uff0c\u800c\u4e14\u8fd8\u4e0d\u77e5\u90a3\u5c11\u5973\u662f\u53cb\u662f\u654c\uff0c\u7384\u98ce\u903c\u5f97\u628a \u624b\u4e00\u6325\uff0c\u516d\u4e2a\u4eba\u4e00\u9f50\u64a4\u8d70\u3002 \u3000\u3000\u4e86\u56e0\u4e00\u6756\u628a\u51af\u745b\u624b\u4e0a\u7684\u5b9d\u5251\u6253\u98de\uff0c\u4e00\u6293\u6293\u53bb\uff0c\u90a3\u77e5\u51af\u745b\u5df2\u62d4\u51fa\u4e86\u65ad\u7389\u5251\uff0c\u5251\u8bc0\u4e00\u9886\uff0c\u4e00 \u5251\u6a2a\u524a\uff0c\u4e86\u56e0\u8fde\u5fd9\u7f29\u624b\uff0c\u8863\u8896\u5df2\u7ed9\u5272\u53bb\u4e00\u622a\u3002\u4e86\u56e0\u5927\u6012\uff0c\u518d\u8d77\u4e00\u6756\uff0c\u60f3\u628a\u5979\u7684\u77ed\u5251\u7167\u6837\u9707 \u5f00\uff0c\u51af\u745b\u8fd9\u6b21\u5df2\u9886\u4e86\u4e56\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u5251\u5c16\u5728\u7985\u6756\u5934\u4e0a\u4e00\u70b9\uff0c\u8eab\u5b50\u5f39\u5230\u534a\u7a7a\uff0c\u5c31\u5728\u534a\u7a7a\u4e2d\u821e\u8d77 \u4e00\u6735\u5251\u82b1\uff0c\u51cc\u7a7a\u4e0b\u51fb\uff01\u4e86\u56e0\u7985\u6756\u4e00\u7acb\uff0c\u8fc5\u5f80\u4e0a\u6233\uff0c\u51af\u745b\u4fa7\u8eab\u4e00\u95ea\uff0c\u8fde\u62a2\u4e09\u62db\uff0c\u4e86\u56e0\u7985\u6756\u821e\u4e86 \u4e00\u4e2a\u5706\u5708\uff0c\u628a\u51af\u745b\u903c\u51fa\u4e00\u4e08\u5f00\u5916\u3002\u4e86\u56e0\u624b\u821e\u7985\u6756\uff0c\u8d76\u4e0a\u524d\u53bb\uff0c\u4f38\u624b\u53c8\u6293\uff0c\u90a3\u77e5\u51af\u745b\u5251\u6cd5\u7cbe\u5999 \u7edd\u4f26\uff0c\u4e86\u56e0\u6756\u52bf\u4e00\u7f13\uff0c\u5979\u9aa4\u7136\u4ece\u6756\u5e95\u94bb\u4e86\u8fc7\u6765\uff0c\u4e00\u5251\u523a\u5230\u4e86\u56e0\u808b\u4e0b\uff0c\u4e86\u56e0\u5927\u60ca\uff0c\u4e3a\u4e86\u8981\u89e3\u5371 \u62db\uff0c\u62fc\u7740\u628a\u5979\u6253\u6b7b\uff0c\u6756\u8eab\u5f80\u5916\u4e00\u6302\uff0c\u8fd9\u4e00\u62db\u4e43\u662f\u8361\u9b54\u6756\u6cd5\u4e2d\u7684\u201c\u5d29\u201d\u5b57\u8bc0\uff0c\u8d25\u4e2d\u6c42\u80dc\uff0c\u52b2\u9053 \u5947\u5927\uff0c\u51af\u745b\u89c1\u52bf\u4e0d\u597d\uff0c\u5251\u5c16\u548c\u6756\u5c16\u4e00\u63a5\uff0c\u8eab\u5b50\u53c8\u5f39\u5230\u534a\u7a7a\u3002\u5fc3\u60f3\uff1a\u8fd9\u548c\u5c1a\u597d\u51f6\uff0c\u6211\u4e0d\u662f\u4ed6\u7684 \u5bf9\u624b\uff0c\u518d\u843d\u4e0b\u65f6\uff0c\u4e0d\u5411\u4e86\u56e0\u8fdb\u51fb\uff0c\u8ff3\u81ea\u5c55\u5f00\u7edd\u9876\u8f7b\u529f\uff0c\u5411\u5357\u9003\u8d70\uff01 \u3000\u3000\u4e86\u56e0\u76ee\u77aa\u53e3\u5446\uff0c\u5fc3\u9053\uff1a\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u8fd9\u91ce\u4e2b\u5934\u4ece\u90a3\u91cc\u5b66\u6765\u7684\u5251\u6cd5\uff0c\u6c5f\u6e56\u4e0a\u53c8\u4e00\u4e2a\u201c\u5415\u56db \u5a18\u201d\u4e86\u3002\u8463\u5de8\u5ddd\u6b63\u5728\u62d4\u9489\u7597\u6bd2\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u54b1\u4eec\u683d\u4e86\u3002\u201d\u4e86\u56e0\u89c1\u51af\u745b\u8eab\u5f62\uff0c\u500f\u5ffd\u4e0d\u89c1\uff0c \u9053\uff1a\u8fd9\u91ce\u4e2b\u5934\u8f7b\u529f\u4e5f\u6bd4\u4ee5\u524d\u9ad8\u5f97\u591a\u3002\u6211\u4eec\u544a\u8bc9\u5e74\u7fb9\u5c27\u53bb\u3002 \u3000\u3000\u4e86\u56e0\u548c\u8463\u5de8\u5ddd\u56de\u5230\u6731\u4ed9\u9547\uff0c\u8f66\u8f9f\u90aa\u4e5f\u5df2\u9003\u56de\uff0c\u4e09\u4eba\u8fdb\u5e10\uff0c\u624d\u77e5\u5e74\u7fb9\u5c27\u7b49\u5f97\u4e0d\u8010\u70e6\uff0c\u5e26\u4e86 \u51e0\u540d\u4eb2\u5175\uff0c\u5df2\u56de\u5bb6\u53bb\u4e86\u3002\u4e2d\u519b\u8fd8\u8bf4\uff0c\u5927\u5e05\u8981\u8fc7\u4e24\u5929\u624d\u56de\u6765\u548c\u5927\u519b\u4e00\u9f50\u897f\u5f81\uff0c\u8f6c\u544a\u5b9d\u56fd\u7985\u5e08\uff0c \u4ed6\u628a\u65b9\u4eca\u660e\u4ea4\u7ed9\u5cb3\u949f\u742a\u5904\u7f6e\u3002\u4e86\u56e0\u548c\u8463\u5de8\u5ddd\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u593a\u4e86\u5141\u5824\u7684\u5175\u6743\uff0c\u53c8\u6536\u670d\u4e86\u8f66\u8f9f\u90aa\u4e4b\u540e\uff0c\u5341\u5206\u5f97\u610f\u3002\u9065\u671b\u5bb6\u4e61\uff0c\u8111\u6d77\u4e2d\u7a81\u7136\u98d8\u8d77 \u51af\u7433\u7684\u5f71\u5b50\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u5c48\u6307\u4e00\u7b97\uff0c\u51af\u7433\u4eca\u5e74\u5df2\u7ecf\u5341\u516d\u5c81\u4e86\uff0c\u5fc3\u60f3\u5979\u8be5\u957f\u5f97\u6bd4\u4ee5\u524d\u9ad8\u591a\u4e86\u3002\u60f3\u8d77\u81ea\u5df1\u5c11\u5e74 \u5f97\u5fd7\uff0c\u624b\u63e1\u5175\u6743\uff0c\u813e\u8155\u738b\u4faf\uff0c\u505a\u89c2\u537f\u76f8\uff0c\u4eba\u751f\u81f3\u6b64\uff0c\u53ef\u7b97\u5f97\u662f\u5f97\u610f\u6781\u4e86\uff01\u552f\u4e00\u7f3a\u9677\u7684\u662f\uff0c\u8fd8 \u6ca1\u6709\u4e00\u4e2a\u79f0\u5fc3\u6ee1\u610f\u7684\u592b\u4eba\u3002\u53c8\u60f3\u7687\u4e0a\u867d\u7136\u5c5e\u610f\u51af\u7433\uff0c\u4f46\u4ed6\u4e09\u5bab\u516d\u9662\uff0c\u4f73\u4e3d\u6b63\u591a\uff0c\u6211\u82e5\u6377\u8db3\u5148 \u767b\uff0c\u5148\u628a\u51af\u7433\u5f04\u5230\u624b\u4e2d\uff0c\u4ed6\u603b\u4e0d\u597d\u610f\u601d\u548c\u6211\u4e89\u593a\u3002\u8981\u77e5\u5e74\u7fb9\u5c27\u6b64\u9645\uff0c\u8eab\u4efd\u4e0e\u524d\u5df2\u5927\u4e0d\u76f8\u540c\uff0c \u5bf9\u5141\u796f\u4e5f\u4e0d\u4f3c\u4ee5\u524d\u90a3\u6837\u5fcc\u60ee\u4e86\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u547d\u4ee4\u5927\u519b\u5728\u6731\u4ed9\u9547\u5916\u9a7b\u624e\u4e09\u5929\uff0c\u9664\u4e86\u8981\u89e3\u51b3\u5141\u5824\u4e4b\u5916\uff0c\u8fd8\u60f3\u56de\u5bb6\u4e00\u8f6c\uff0c\u63a2\u95ee\u51af\u7433 \u662f\u5426\u5728\u4ed6\u7684\u5bb6\u4e2d\u3002\u8fd9\u65e5\u4e00\u65e9\u5c31\u5199\u4e86\u4e00\u5c01\u4fe1\uff0c\u903c\u53cc\u9b54\u5e26\u56de\u5bb6\u53bb\u7ed9\u4ed6\u7236\u4eb2\uff0c\u514d\u5f97\u9aa4\u7136\u56de\u53bb\uff0c\u8fc7\u4e8e \u7a81\u5140\uff0c\u8fd9\u4e5f\u662f\u5e74\u7fb9\u5c27\u8eab\u4e3a\u5927\u5c06\u519b\u4e4b\u540e\u5e94\u6709\u7684\u201c\u6d3e\u5934\u201d\u3002\u53cc\u9b54\u6027\u5b50\u867d\u91ce\uff0c\u4f46\u5374\u4e0d\u4f3c\u4e86\u56e0\u4e4b\u50b2\uff0c \u8fd8\u670d\u5e74\u7fb9\u5c27\u7ba1\u675f\uff0c\u6240\u4ee5\u5e74\u7fb9\u5c27\u6697\u4e2d\u53eb\u4ed6\u66ff\u81ea\u5df1\u76d1\u89c6\u4e86\u56e0\uff0c\u4eca\u65e5\u53c8\u6d3e\u9063\u4ed6\u4eec\u9001\u4fe1\u3002\u53cc\u9b54\u53bb\u540e\uff0c \u5e74\u7fb9\u5c27\u7b49\u5230\u65e5\u4e0a\u4e09\u7aff\uff0c\u8fd8\u672a\u89c1\u4e86\u56e0\u4ed6\u4eec\u56de\u6765\uff0c\u7b49\u5f97\u4e0d\u8010\u70e6\uff0c\u5fc3\u60f3\uff1a\u8fd9\u4e9b\u5c0f\u4e8b\u4ea4\u7ed9\u5cb3\u949f\u6dc7\u53bb\u529e \u597d\u4e86\uff0c\u5e26\u4e86\u51e0\u4e2a\u4eb2\u5175\uff0c\u8de8\u4e0a\u9a8f\u9a6c\uff0c\u8ff3\u81ea\u56de\u5bb6\u3002 \u3000\u3000\u51af\u745b\u843d\u8352\u800c\u9003\uff0c\u89c1\u90a3\u51f6\u548c\u5c1a\u5e76\u4e0d\u8ffd\u6765\uff0c\u53cd\u5411\u56de\u7a0b\u8d70\u4e86\uff0c\u677e\u4e86\u53e3\u6c14\uff0c\u8d70\u4e0a\u9a7f\u9053\uff0c\u4ecd\u7136\u8ff3\u53bb \u9648\u7559\u3002\u8d70\u5230\u4e2d\u5348\u65f6\u5206\uff0c\u542c\u5f97\u80cc\u540e\u9a6c\u94c3\u53ee\u5f53\uff0c\u56de\u5934\u4e00\u770b\uff0c\u5ffd\u89c1\u4e24\u4e2a\u5f62\u5bb9\u602a\u5f02\u7684\u8001\u5934\uff0c\u9f88\u7259\u54a7 \u5634\uff0c\u51b2\u7740\u81ea\u5df1\u76f4\u7b11\u3002\u51af\u745b\u731b\u7136\u60f3\u8d77\uff0c\u8fd9\u4e24\u4e2a\u4eba\u597d\u50cf\u5728\u90a3\u513f\u89c1\u8fc7\u4f3c\u7684\u3002\u518d\u4e00\u7ec6\u60f3\uff0c\u8fd9\u4e24\u4eba\u4e43\u662f \u201c\u5f3a\u76d7\u7a9d\u201d\u91cc\u7684\uff0c\u81ea\u5df1\u5e7c\u65f6\u4ed6\u4eec\u5e38\u5e38\u62b1\u81ea\u5df1\u5728\u90a3\u4e9b\u5927\u5c4b\u4e2d\u95f4\u6e38\u73a9\uff0c\u90a3\u4e9b\u5c4b\u5b50\u91cc\u6709\u5f88\u591a\u5de6\u53f3\u602a \u602a\u7684\u5927\u4f5b\u50cf\u7684\u3002\u51af\u745b\u8bb0\u5f97\u8d77\uff0c\u53cc\u9b54\u5374\u8bb0\u4e0d\u8d77\u4e86\uff0c\u8fd9\u4e43\u662f\u56e0\u4e3a\u53cc\u9b54\u76f8\u8c8c\u5947\u7279\uff0c\u51af\u745b\u53c8\u662f\u7531\u4ed6\u4eec \u62b1\u56de\u7687\u5e9c\uff0c\u7279\u522b\u4eb2\u8fd1\u7684\u539f\u6545\u3002 \u3000\u3000\u53cc\u9b54\u4e00\u89c1\u51af\u745b\uff0c\u559c\u51fa\u671b\u5916\uff0c\u8428\u5929\u523a\u5728\u9a6c\u80cc\u4e0a\u4e00\u63a0\u800c\u8d77\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u7433\u513f\uff0c\u4f60\u8fd9\u4e24\u5929\u8dd1 \u5230\u90a3\u91cc\u53bb\u554a\uff1f\u968f\u6211\u56de\u53bb\u5427\uff01\u201d\u5ffd\u89c1\u51af\u745b\u773c\u5149\u602a\u5f02\uff0c\u5706\u9f13\u9f13\u7684\u671b\u7740\u81ea\u5df1\uff0c\u60ca\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u4e0d \u8ba4\u8bc6\u6211\u4e48\uff1f\u201d\u51af\u745b\u9a82\u9053\uff1a\u201c\u6211\u8ba4\u8bc6\u4f60\u662f\u5f3a\u76d7\uff01\u201d\u8428\u5929\u523a\u5927\u4e3a\u751f\u6c14\uff0c\u65a5\u9053\uff1a\u201c\u6211\u4eec\u7eb5\u662f\u5f3a\u76d7\u6076 \u9b54\uff0c\u5bf9\u4f60\u4e5f\u6709\u517b\u80b2\u4e4b\u6069\uff01\u201d\u53cc\u9b54\u65e9\u5e74\u7684\u786e\u505a\u8fc7\u5f3a\u76d7\uff0c\u800c\u4e14\u4e5f\u77e5\u81ea\u5df1\u5728\u6c5f\u6e56\u4e0a\u6709\u201c\u9b54\u5934\u201d\u4e4b \u79f0\uff0c\u56e0\u6b64\u6700\u6068\u4eba\u9a82\u4ed6\u4eec\u5f3a\u76d7\u6076\u9b54\uff0c\u82e5\u975e\u8bef\u4f1a\u5979\u662f\u51af\u7433\uff0c\u4ed6\u4eec\u65e9\u5df2\u51fa\u624b\u4e86\u3002 \u3000\u3000\u90a3\u77e5\u8428\u5929\u523a\u672a\u66fe\u51fa\u624b\uff0c\u51af\u745b\u5df2\u5148\u52a8\u624b\uff0c\u8eab\u5f62\u98de\u8d77\uff0c\u5237\u7684\u4e00\u5251\uff0c\u8fce\u9762\u523a\u53bb\uff01\u8428\u5929\u523a\u8eab\u624b\u8fc5 \u75be\uff0c\u98d8\u8eab\u4e00\u95ea\uff0c\u521a\u521a\u907f\u5f00\uff0c\u51af\u745b\u7b2c\u4e8c\u5251\u7b2c\u4e09\u5251\u8ddf\u8e2a\u6025\u523a\uff0c\u8428\u5929\u523a\u8fde\u6362\u51e0\u79cd\u8eab\u6cd5\uff0c\u59cb\u7ec8\u907f\u4e0d\u5f00 \u5979\uff0c\u53ea\u89c9\u51af\u745b\u7684\u5251\u5c16\u5728\u8eab\u524d\u8eab\u540e\u6643\u52a8\u3002\u8fd9\u8fd8\u662f\u56e0\u4e3a\u8428\u5929\u523a\u7ec3\u8fc7\u6251\u51fb\u4e4b\u6280\uff0c\u8eab\u624b\u6e9c\u6ed1\uff0c\u8981\u4e0d\u7136 \u65e9\u5c31\u88ab\u51af\u745b\u523a\u4e2d\u4e86\uff01 \u3000\u3000\u8428\u5929\u523a\u6c14\u5f80\u4e0a\u51b2\uff0c\u5927\u53eb\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff01\u201d\u8428\u5929\u90fd\u53eb\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u65e2\u5982\u6b64\u7edd\u60c5\uff0c\u54b1\u4eec\u8fd8 \u60dc\u5979\u4f5c\u751a\uff1f\u201d\u659c\u523a\u51b2\u4e0a\uff0c\u4e00\u638c\u5288\u4e0b\uff01 \u3000\u3000\u51af\u745b\u89c1\u53cc\u9b54\u76f8\u8c8c\u51f6\u6076\uff0c\u5939\u653b\u81ea\u5df1\uff0c\u5927\u6012\u9053\uff1a\u201c\u597d\u54c7\uff0c\u4f60\u4eec\u8fd9\u4e9b\u72d7\u5f3a\u76d7\u771f\u53ef\u6076\uff01\u201d\u5251\u950b\u4e00 \u8f6c\uff0c\u8428\u5929\u90fd\u86ee\u51b2\u6076\u6253\uff0c\u90a3\u6599\u5230\u51af\u745b\u5251\u6cd5\u5982\u6b64\u7cbe\u5999\uff0c\u6025\u5fd9\u7f29\u624b\uff0c\u80a9\u5934\u5df2\u4e2d\u4e86\u4e00\u5251\uff0c\u8428\u5929\u523a\u8fd9\u65f6 \u518d\u4e5f\u4e0d\u80fd\u5bb9\u5fcd\uff0c\u8eab\u5f62\u7a81\u8d77\uff0c\u5341\u6307\u5ffd\u5764\uff0c\u5341\u6307\u957f\u7532\u5411\u51af\u745b\u84e6\u5730\u523a\u6765\uff0c\u51af\u745b\u89c1\u6b64\u602a\u72b6\uff0c\u4e5f\u5403\u4e00 \u60ca\uff0c\u56de\u8eab\u75be\u523a\uff0c\u8428\u5929\u523a\u5c55\u5f00\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u6293\u3001\u70b9\u3001\u52fe\u3001\u6495\uff0c\u548c\u51af\u745b\u5927\u6218\u8d77\u6765\uff01 \u3000\u3000\u8428\u5929\u90fd\u94dc\u76ae\u94c1\u9aa8\uff0c\u4e2d\u4e86\u4e00\u5251\uff0c\u4e0d\u4ee5\u4e3a\u610f\uff0c\u968f\u624b\u5728\u8def\u65c1\u62d4\u8d77\u4e00\u682a\u5927\u6811\uff0c\u5411\u51af\u745b\u6a2a\u626b\u8fc7\u53bb\uff0c \u51af\u745b\u8eab\u6cd5\u8f7b\u7075\uff0c\u4e00\u95ea\u95ea\u5f00\u3002\u8428\u5929\u523a\u53f3\u624b\u6293\u5979\u624b\u8155\uff0c\u5de6\u624b\u523a\u5979\u9762\u95e8\uff0c\u51af\u745b\u89c1\u4ed6\u6765\u52bf\u51f6\u731b\uff0c\u8fde\u8d70 \u5de7\u62db\uff0c\u8428\u5929\u90fd\u6253\u5f97\u6027\u8d77\uff0c\u628a\u6811\u5e72\u8d34\u5730\u6253\u6765\uff0c\u626b\u5979\u53cc\u817f\u3002\u8fd9\u4e00\u6765\u51af\u745b\u4e3a\u4e86\u9632\u5fa1\u4e0b\u76d8\uff0c\u8f7b\u529f\u6253\u4e86 \u6298\u6263\uff0c\u8428\u5929\u523a\u7684\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u975e\u6bd4\u5bfb\u5e38\uff0c\u800c\u4e14\u4e24\u5144\u5f1f\u914d\u5408\u5f97\u5f53\uff0c\u51af\u745b\u5251\u6cd5\u867d\u5999\uff0c\u4ea6\u662f\u96be\u5948 \u4ed6\u4f55\u3002 \u3000\u3000\u51af\u745b\u73b0\u5728\u7684\u6b66\u529f\u672c\u9886\u5927\u7ea6\u76f8\u5f53\u4e8e\u5415\u56db\u5a18\u5f53\u5e74\u4e0b\u9099\u5c71\u4e4b\u65f6\uff0c\u5f53\u5e74\u5415\u56db\u5a18\u53ef\u80dc\u516b\u81c2\u795e\u9b54\uff0c\u4f46 \u5374\u6321\u4e0d\u4f4f\u53cc\u9b54\u7684\u5408\u51fb\uff01\u51af\u745b\u5982\u4eca\u4e5f\u662f\u5982\u6b64\uff0c\u800c\u4e14\u5979\u5e74\u7eaa\u6bd4\u5f53\u5e74\u5415\u56db\u5a18\u4e0b\u5c71\u4e4b\u65f6\u8fd8\u5c0f\uff0c\u6c14\u529b\u66f4 \u4e0d\u5982\u53cc\u9b54\u60a0\u957f\u3002\u6253\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u6e10\u89c9\u6c14\u5598\u5fc3\u8df3\uff0c\u6c14\u529b\u4e0d\u6d4e\uff0c\u60f3\u7a81\u56f4\u9003\u8d70\uff0c\u53cc\u9b54\u7f20\u5f97\u53c8\u7d27\uff0c\u8428 \u5929\u523a\u53eb\u9053\uff1a\u201c\u597d\u4e2a\u5fd8\u6069\u8d1f\u4e49\u7684\u4e2b\u5934\uff0c\u4f60\u8d76\u5feb\u8dea\u4e0b\u53e9\u5934\u8ba4\u9519\uff0c\u6211\u4eec\u6216\u53ef\u9976\u4f60\uff01\u201d\u51af\u745b\u95f7\u58f0\u4e0d\u54cd \u7a81\u7136\u5251\u628a\u4e00\u7ffb\uff0c\u4e00\u5251\u523a\u5230\u8428\u5929\u523a\u8155\u8109\uff0c\u8428\u5929\u523a\u731b\u7136\u7f29\u624b\uff0c\u8428\u5929\u90fd\u6811\u5e72\u8d34\u5730\u626b\u6765\uff0c\u51af\u745b\u811a\u5c16\u4e00 \u70b9\u6811\u8eab\uff0c\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u5012\u98de\u51fa\u53bb\u3002\u8428\u5929\u523a\u53eb\u58f0\uff1a\u201c\u54ea\u91cc\u8d70\uff01\u201d\u8eab\u5f62\u98de\u8d77\uff0c\u4e00\u6293\u6293\u4e0b\uff0c\u90a3\u6599 \u51af\u745b\u8f7b\u52b2\u5353\u7edd\uff0c\u5728\u534a\u7a7a\u4e2d\u8eab\u8eaf\u4e00\u5c48\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u8428\u5929\u523a\u731d\u4e0d\u53ca\u9632\uff0c\u8154\u9aa8\u4e2d\u5251\uff0c\u624b\u6293\u5411\u524d\u4e00\u63d2 \u4e00\u63a8\uff0c\u51af\u745b\u4e5f\u88ab\u4ed6\u6307\u7532\u523a\u4f24\uff0c\u4e58\u4ed6\u4e00\u63a8\u4e4b\u52bf\uff0c\u98d8\u51fa\u4e94\u516d\u4e08\u5916\u3002 \u3000\u3000\u8428\u5929\u90fd\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u597d\u54c7\uff01\u4f60\u8fd9\u91ce\u4e2b\u5934\u80c6\u6562\u4f24\u5bb3\u5c0a\u957f\uff01\u201d\u821e\u52a8\u5927\u6811\uff0c\u6025\u6025\u8d76\u4e0a\uff0c\u8428\u5929\u523a \u4e2d\u4e86\u4e00\u5251\uff0c\u5e78\u5728\u51af\u745b\u7a7a\u4e2d\u8fdb\u62db\uff0c\u65e0\u4ece\u4f7f\u529b\uff0c\u8fd9\u4e00\u5251\u4f24\u5f97\u4e0d\u91cd\uff0c\u626f\u4e0b\u519c\u886b\uff0c\u5305\u4e86\u4f24\u53e3\uff0c\u5fcd\u75db\u8ffd \u8d76\u3002 \u3000\u3000\u51af\u745b\u8dd1\u4e86\u51e0\u6b65\uff0c\u5ffd\u89c9\u5934\u6655\u76ee\u7729\uff0c\u53e3\u4e2d\u7126\u6e34\u3002\u8981\u77e5\u8428\u5929\u523a\u5341\u6307\u6307\u7532\u90fd\u5728\u6bd2\u86c7\u7684\u6bd2\u6db2\u4e2d\u6d78 \u8fc7\uff0c\u82e5\u65e0\u89e3\u836f\uff0c\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u5fc5\u6b7b\u65e0\u7591\uff0c\u800c\u4e14\u4e00\u7528\u529b\u6bd2\u53d1\u5f97\u8d8a\u5feb\u3002\u51af\u745b\u4e0d\u77e5\u6240\u4ee5\uff0c\u62d4\u6b65 \u98de\u5954\uff0c\u51e0\u4e4e\u6655\u5012\uff0c\u8bd5\u4e00\u6b62\u6b65\uff0c\u8fd0\u6c14\u62b5\u5fa1\uff0c\u8fd9\u624d\u7a0d\u597d\u4e00\u70b9\u3002\u53ef\u662f\u8fd9\u4e48\u4e00\u6765\uff0c\u53cc\u9b54\u90fd\u5df2\u8d76\u4e0a\uff0c\u8428 \u5929\u523a\u72e0\u72e0\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u7edd\u60c5\u7edd\u4e49\u7684\u8d31\u4e2b\u5934\uff0c\u73b0\u5728\u6b7b\u5230\u4e34\u5934\uff0c\u4f60\u8fd8\u4e0d\u8ba4\u9519\u4e48\uff1f\u201d\u51af\u745b\u201c\u5478\u201d\u4e86 \u4e00\u58f0\uff0c\u8bb0\u8d77\u5e08\u5085\u7684\u6559\u8bad\uff1a\u201c\u7eb5\u6559\u8eaf\u4f53\u6210\u7070\uff0c\u4e0d\u53ef\u6c42\u9976\u5c48\u670d\u3002\u201d\u7acb\u5b9a\u811a\u6b65\uff0c\u65ad\u7389\u5251\u626c\u7a7a\u4e00\u95ea\uff0c \u9a82\u9053\uff1a\u201c\u6076\u5f3a\u76d7\uff0c\u4f60\u518d\u8fc7\u6765\uff01\u201d\u8428\u5929\u523a\u6012\u9053\uff1a\u201c\u4f60\u4e2d\u4e86\u6211\u7684\u6bd2\u722a\uff0c\u82e6\u4e0d\u6c42\u9976\uff0c\u5fc5\u6b7b\u65e0\u7591\uff0c\u96be \u9053\u4f60\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u4f60\u518d\u53d1\u6a2a\uff0c\u6211\u4eec\u5c31\u5750\u5728\u8fd9\u91cc\u770b\u4f60\u6bd2\u53d1\u8eab\u4ea1\uff0c\u4f55\u5fc5\u548c\u4f60\u518d\u6253\u3002\u201d\u51af\u745b\u5927\u6012\uff0c \u51b2\u4e0a\u4e24\u6b65\uff0c\u5237\u5237\u4e24\u5251\uff0c\u5148\u53d1\u653b\u52bf\uff01 \u3000\u3000\u8428\u5929\u523a\u51b7\u7b11\u4e00\u58f0\uff0c\u62c9\u8428\u5929\u90fd\u9000\u540e\uff0c\u51af\u745b\u51b2\u524d\u51e0\u6b65\uff0c\u53c8\u611f\u76ee\u7729\u5fc3\u8df3\uff0c\u6025\u5fd9\u5b9a\u4e86\u5b9a\u795e\uff0c \u201c\u5478\u201d\u4e86\u4e00\u53e3\uff0c\u9a82\u9053\uff1a\u201c\u4e0d\u6562\u660e\u5200\u660e\u67aa\uff0c\u6697\u5730\u5077\u65bd\u6bd2\u722a\uff0c\u8fd9\u53ea\u80fd\u7b97\u4e0b\u4e09\u6d41\u7684\u5f3a\u76d7\uff01\u201d\u8428\u5929\u523a \u7f6e\u4e4b\u4e0d\u7406\uff0c\u8428\u5929\u90fd\u5374\u5fcd\u8010\u4e0d\u4f4f\uff0c\u6012\u559d\u4e00\u58f0\uff0c\u6251\u4e0a\u524d\u53bb\uff0c\u8428\u5929\u523a\u53eb\u9053\uff1a\u201c\u9632\u5979\u5b9d\u5251\uff01\u201d\u51af\u745b\u84e6 \u7136\u8dc3\u8d77\u523a\u4e0b\uff0c\u8428\u5929\u90fd\u4e00\u638c\u51fb\u53bb\uff0c\u5288\u5979\u4e0d\u4e2d\uff0c\u51af\u745b\u624b\u8155\u4e00\u7ffb\uff0c\u8428\u5929\u90fd\u80a9\u5934\u53c8\u4e2d\u4e86\u4e00\u5251\uff0c\u8428\u5929\u523a \u5978\u6ed1\u5f97\u591a\uff0c\u5728\u65c1\u89d1\u4e2a\u6b63\u7740\uff0c\u5de6\u624b\u4e00\u52fe\uff0c\u628a\u51af\u745b\u5b9d\u5251\u62a2\u8fc7\uff0c\u987a\u624b\u4e00\u63a8\uff0c\u5c06\u5979\u63a8\u8dcc\u5730\u4e0a\u3002 \u3000\u3000\u8428\u5929\u90fd\u8fde\u4e2d\u4e24\u5251\uff0c\u6c14\u6781\u6068\u6781\uff0c\u7ffb\u8eab\u8dc3\u8d77\uff0c\u8e0f\u6b65\u4e0a\u524d\uff0c\u8428\u5929\u523a\u9053\uff1a\u201c\u8ba9\u5979\u81ea\u5df1\u6c42\u9976\uff01\u201d\u8428 \u5929\u90fd\u6012\u9053\uff1a\u201c\u4f60\u9976\u5979\u6211\u4e0d\u9976\u5979\uff01\u201d\u63e1\u62f3\u51b2\u4e0a\uff0c\u8428\u5929\u523a\u77e5\u9053\u4ed6\u7684\u813e\u6c14\u4e00\u53d1\u4e0d\u53ef\u6536\u62fe\uff0c\u4f38\u624b\u4e00 \u62e6\uff0c\u6b63\u60f3\u8bbe\u6cd5\u529d\u963b\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u9a6c\u94c3\u53ee\u5f53\uff0c\u5c18\u5934\u5927\u8d77\uff0c\u5e74\u7fb9\u5c27\u5e26\u9886\u51e0\u540d\u4eb2\u5175\u7b56\u9a6c\u98de\u6765\uff0c\u9ad8\u58f0 \u53eb\u9053\uff1a\u201c\u4f60\u4eec\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8428\u5929\u90fd\u6124\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u91ce\u4e2b\u5934\u4e0d\u77e5\u597d\u73af\uff0c\u5c06\u6211\u523a\u4e86\u4e24\u5251\uff01\u201d\u51af\u745b\u8fd9\u65f6\u5df2\u6655\u5728\u5730\u4e0a\uff0c\u5e74\u7fb9 \u5c27\u5fc3\u5185\u6697\u60ca\uff0c\u4f46\u5374\u4e0d\u9732\u5f62\u8272\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u78b0\u5230\u5979\u7684\uff1f\u201d\u8428\u5929\u523a\u5c06\u60c5\u5f62\u8bf4\u4e86\uff0c\u5e74\u7fb9\u5c27\u6697 \u9053\uff1a\u8fd8\u597d\uff0c\u4e0d\u662f\u5728\u6211\u5bb6\u4e2d\u78b0\u7740\u7684\uff0c\u4f46\u51af\u7433\u8fd9\u4e2b\u5934\u6700\u9b3c\u7075\u7cbe\uff0c\u4e3a\u4f55\u78b0\u89c1\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u4e5f\u4e0d\u907f\uff1f\u8428 \u5929\u90fd\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u65e0\u60c5\u65e0\u4e49\uff0c\u7980\u5927\u5e05\uff0c\u6211\u53ef\u8981\u628a\u5979\u6bd9\u4e86\uff01\u201d\u5e74\u7fb9\u5c27\u53cc\u773c\u4e00\u7ffb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u5c06\u6765\u7687\u4e0a\u8981\u4eba\uff0c\u8c01\u6562\u62c5\u8fd9\u5173\u7cfb\uff1f\u201d\u8428\u5929\u90fd\u4e43\u662f\u9c81\u83bd\u5339\u592b\uff0c\u95fb\u8a00\u4e00\u7a92\uff0c\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u5929\u523a\uff0c \u4f60\u5c06\u89e3\u836f\u7ed9\u6211\u3002\u201d\u8428\u5929\u523a\u6478\u51fa\u89e3\u836f\uff0c\u9053\uff1a\u201c\u5927\u5e05\uff0c\u6211\u4eec\u5144\u5f1f\u613f\u8d1f\u8d23\u62bc\u5979\u4e0a\u4eac\u3002\u201d\u5e74\u7fb9\u5c27\u4e0d \u7b54\uff0c\u63a5\u8fc7\u89e3\u836f\uff0c\u6025\u5fd9\u7ed9\u51af\u745b\u5185\u670d\u5916\u6577\uff0c\u89e3\u836f\u6781\u5176\u7075\u9a8c\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u51af\u745b\u60a0\u7136\u9192\u8f6c\uff0c\u89c1\u53cc\u9b54\u65c1 \u7acb\uff0c\u4e00\u4e2a\u5e74\u5c11\u5c06\u519b\u8e72\u5728\u81ea\u5df1\u8eab\u65c1\u3002\u51af\u745b\u5927\u4e3a\u60ca\u8be7\uff0c\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u7433\u59b9\uff0c\u4f60\u517b\u517b\u795e\u3002\u201d\u51af\u745b\u5fc3 \u9053\uff1a\u201c\u4eca\u5929\u4e0d\u77e5\u649e\u4e86\u4ec0\u4e48\uff1f\u6709\u4eba\u9a82\u6211\u91ce\u4e2b\u5934\uff0c\u53c8\u6709\u4eba\u53eb\u6211\u7433\u59b9\u59b9\u3002\u201d\u89c1\u8fd9\u5c11\u5e74\u5c06\u519b\u9887\u4e3a\u548c \u5584\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u7d22\u6027\u8dcc\u57ab\u5730\u4e0a\uff0c\u6697\u8fd0\u5185\u529b\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u6c14\u8fbe\u91cd\u68a2\uff0c\u4e00\u8dc3\u800c\u8d77\u3002\u8428\u5929\u90fd\u77aa\u773c\u9a82 \u9053\uff1a\u201c\u91ce\u4e2b\u5934\uff0c\u4f60\u8fd9\u56de\u670d\u4e86\u5427\uff1f\u201d\u8428\u5929\u523a\u9053\uff1a\u201c\u5f1f\u5f1f\uff0c\u4e0d\u8981\u5413\u5979\uff01\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u62fe\u8d77\u4f60\u7684 \u5b9d\u5251\uff0c\u8ddf\u6211\u56de\u53bb\u5427\uff01\u201d\u51af\u745b\u5c06\u65ad\u7389\u5251\u63d2\u56de\uff0c\u5411\u5e74\u7fb9\u5c27\u65bd\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u591a\u8c22\u4f60\u6551\u547d\u4e4b\u6069\u3002\u201d \u8eab\u5f62\u7a81\u8d77\uff0c\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u8df3\u4e0b\u9053\u65c1\u7530\u91ce\uff0c\u7edd\u5c18\u800c\u53bb\uff01\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u5feb\u8ffd\uff01\u201d\u7387\u4eb2\u5175\u8ffd\u53bb\u3002 \u53cc\u9b54\u4e5f\u968f\u7740\u8ffd\u8d76\uff0c\u4f46\u8f6c\u773c\u4e4b\u95f4\uff0c\u51af\u745b\u5df2\u8dd1\u8fc7\u51e0\u5341\u7247\u65f1\u7530\uff0c\u8df3\u4e0a\u5c71\u4e0a\uff0c\u9a6c\u513f\u4e0a\u5c71\u4e0d\u4fbf\uff0c\u53cc\u9b54\u8f7b \u529f\u53c8\u8fdc\u4e0d\u53ca\u5979\uff0c\u773c\u5149\u5149\u7684\u770b\u5979\u9690\u6ca1\u5728\u5c71\u6797\u4e4b\u5185\uff0c\u6ca1\u4e86\u8e2a\u8ff9\u3002 \u3000\u3000\u8428\u5929\u90fd\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u7edd\u60c5\u7edd\u4e49\uff0c\u8fde\u6211\u4eec\u90fd\u4e0d\u8ba4\u4e86\uff0c\u4e0d\u8981\u671b\u5979\u518d\u56de\u6765\u4e86\u3002\u201d\u5e74\u7fb9\u5c27\u9ed8\u7136\u4e0d \u8bed\uff0c\u8fc7\u4e86\u8bb8\u4e45\uff0c\u624d\u9053\uff1a\u201c\u4ee5\u540e\u518d\u7406\u5979\u5427\u3002\u201d\u5e26\u9886\u4eb2\u5175\uff0c\u91cd\u56de\u9a7f\u9053\uff0c\u508d\u665a\u65f6\u5206\uff0c\u5230\u4e86\u5bb6\u4e2d\u3002 \u3000\u3000\u5e74\u9050\u9f84\u89c1\u513f\u5b50\u56de\u5bb6\uff0c\u5341\u5206\u9ad8\u5174\uff0c\u62c9\u624b\u95ee\u957f\u95ee\u77ed\uff0c\u5e74\u7fb9\u5c27\u5c4f\u9000\u5de6\u53f3\uff0c\u6084\u58f0\u95ee\u9053\uff1a\u201c\u6709\u8fd9\u6837 \u7684\u4e00\u4e2a\u5b69\u5b50\uff0c\u6765\u4e86\u6ca1\u6709\uff1f\u201d\u5c06\u51af\u7433\u76f8\u8c8c\u8bf4\u4e86\u3002\u5e74\u9050\u9f84\u9053\uff1a\u201c\u6765\u4e86\u5927\u534a\u5e74\u5566\uff0c\u62ff\u7740\u4f60\u7684\u4fe1\u7269\uff0c \u634e\u4e86\u4f60\u7684\u53e3\u4fe1\u6765\u7684\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u51e0\u65f6\u8d70\u7684\uff1f\u201d\u5e74\u9050\u9f84\u9053\uff1a\u201c\u8c01\u8bf4\u5979\u8d70\u4e86\uff1f\u5348\u95f4\u6211\u8fd8\u6d3e\u4eba \u9001\u71d5\u7a9d\u7ed9\u5979\u5462\u3002\u4f60\u5988\u5988\u5f88\u559c\u6b22\u5979\u3002\u53ef\u662f\u8fd9\u4e2a\u5c0f\u59d1\u5a18\u813e\u6c14\u5f88\u602a\uff0c\u4e0d\u80af\u548c\u6211\u4eec\u540c\u4f4f\uff0c\u8981\u72ec\u81ea\u4f4f\u90a3 \u4e2a\u5927\u82b1\u56ed\u91cc\uff0c\u5c31\u4f4f\u5728\u4f60\u5e08\u5085\u4ee5\u524d\u4f4f\u7684\u90a3\u95f4\u623f\u95f4\u5462\uff01\u5979\u53c8\u4e0d\u80af\u8981\u4eba\u670d\u4f8d\uff0c\u6211\u4eec\u53ea\u597d\u6bcf\u5929\u9001\u98df\u7269 \u7ed9\u5979\uff0c\u5c31\u50cf\u4ee5\u524d\u5bf9\u4f60\u5e08\u5085\u4e00\u6837\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u662f\u6211\u8981\u5979\u8fd9\u6837\u7684\u3002\u201d\u5e74\u9050\u9f84\u9053\uff1a\u201c\u5979\u662f\u4f60\u770b \u4e2d\u7684\u5ab3\u5987\u513f\u5417\uff1f\u201d\u5e74\u7fb9\u5c27\u6781\u4e4b\u5947\u602a\uff0c\u5fc3\u60f3\uff1a\u5348\u95f4\u65f6\u5206\uff0c\u5979\u6b63\u5728\u548c\u53cc\u9b54\u5927\u6253\uff0c\u600e\u80fd\u53c8\u5728\u5bb6\u5185\u5403 \u71d5\u7a9d\uff1f\u4e0d\u7b54\u7236\u4eb2\u7684\u8bdd\uff0c\u5306\u5306\u8d76\u53bb\u82b1\u56ed\uff0c\u5e74\u9050\u9f84\u4ee5\u4e3a\u513f\u5b50\u5bb3\u8e81\uff0c\u4e00\u7b11\u56de\u623f\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u8fdb\u5165\u82b1\u56ed\uff0c\u4f46\u89c1\u6ee1\u56ed\u82b1\u8349\uff0c\u58c1\u5012\u5899\u574d\uff0c\u60f3\u662f\u5e08\u5085\u6b7b\u540e\uff0c\u7236\u4eb2\u5fc3\u4f24\uff0c\u4e5f\u5c31\u4e0d\u613f\u6253\u7406 \u8fd9\u4e2a\u56ed\u5b50\u4e86\u3002\u884c\u4e86\u4e00\u9635\uff0c\u5ffd\u95fb\u814a\u6885\u98d8\u9999\uff0c\u6570\u767e\u6811\u6885\u82b1\u628a\u81ea\u5df1\u4ee5\u524d\u548c\u5e08\u5085\u4f4f\u7684\u90a3\u95f4\u623f\u5b50\u56f4\u5f97\u82b1 \u56e2\u9526\u7ee3\uff0c\u8d70\u53bb\u4e00\u770b\uff0c\u6bcf\u73e0\u6885\u82b1\u90fd\u7ecf\u8fc7\u7ec6\u5fc3\u683d\u526a\uff0c\u60f3\u662f\u51af\u7433\u7684\u5fc3\u8840\u65e0\u7591\u3002\u8d70\u5165\u82b1\u5f84\uff0c\u4f46\u89c1\u77f3\u9636 \u5149\u6d01\uff0c\u53a6\u5b87\u65e0\u5c18\u3002\u548c\u5916\u9762\u8352\u5e9f\u7684\u666f\u8c61\u5927\u4e0d\u76f8\u540c\uff0c\u5e74\u7fb9\u5c27\u8f7b\u8f7b\u63a8\u95e8\u8fdb\u53bb\uff0c\u89c1\u4e66\u623f\u5c1a\u900f\u51fa\u706f\u5149\uff0c \u5e74\u7fb9\u5c27\u8f7b\u6572\u4e24\u6572\uff0c\u91cc\u9762\u51af\u7433\u95ee\u9053\uff1a\u201c\u8c01\u5440\uff1f\u8fd9\u4e48\u665a\u6211\u4e0d\u8981\u4e1c\u897f\u5403\u4e86\uff01\u201d\u5e74\u7fb9\u5c27\u7b11\u4e86\u4e00\u7b11\uff0c\u7a81 \u7136\u63a8\u95e8\u8fdb\u53bb\u3002\u5ffd\u89c1\u51af\u7433\u5750\u5728\u623f\u4e2d\uff0c\u5e26\u7740\u60ca\u614c\u7684\u795e\u60c5\uff0c\u6b63\u5728\u63a8\u5f00\u4e00\u672c\u4e66\u672c\uff01 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u6251\u6714\u8ff7\u79bb\u751a\uff0c\u59d0\u59b9\u8d39\u7591\u731c\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u65e0\u610f\u53d1\u85cf\u4e66\u3000\u5750\u884c\u68a6\u68a6\u3000\u6709\u5fc3\u6c42\u4f34\u4fa3\u3000\u8bef\u4f1a\u91cd\u91cd \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u4e94\u56de \u65e0\u610f\u53d1\u85cf\u4e66\u3000\u5750\u884c\u68a6\u68a6 \u6709\u5fc3\u6c42\u4f34\u4fa3\u3000\u8bef\u4f1a\u91cd\u91cd \u3000\u3000\u51af\u7433\u81ea\u90a3\u65e5\u6487\u5f00\u4e86\u674e\u6cbb\u4e4b\u540e\uff0c\u6765\u5230\u5e74\u5bb6\uff0c\u4ea4\u51fa\u4e86\u5e74\u7fb9\u5c27\u7684\u4fe1\u7269\u4e4b\u540e\uff0c\u4f4f\u5230\u90a3\u4e2a\u5927\u56ed\u5b50 \u91cc\u3002\u56ed\u5b50\u5df2\u7ecf\u8352\u5e9f\u591a\u5e74\uff0c\u5e74\u9050\u9f84\u672c\u4e0d\u6562\u8ba9\u5979\u53bb\u4f4f\uff0c\u51af\u7433\u5fae\u7b11\u663e\u4e86\u4e00\u624b\u6b66\u529f\uff0c\u968f\u624b\u62fe\u8d77\u4e00\u9897\u5c0f \u77f3\uff0c\u5c06\u5ead\u9662\u4e2d\u4e00\u68f5\u69d0\u6811\u4e0a\u7684\u9e1f\u513f\u6253\u843d\uff0c\u5e74\u9050\u9f84\u60f3\u8d77\u81ea\u5df1\u513f\u5b50\u7684\u8bb8\u591a\u5f02\u4e8b\uff0c\u53c8\u89c1\u4e86\u5979\u7684\u672c\u9886\uff0c \u5fc3\u60f3\u8fd9\u5c0f\u59d1\u5a18\u65e2\u7136\u662f\u513f\u5b50\u53eb\u6765\u7684\uff0c\u5fc5\u7136\u5927\u6709\u6765\u5386\uff0c\u7a0d\u4e8b\u6253\u626b\u4e4b\u540e\uff0c\u5c31\u7531\u5979\u642c\u8fdb\u4ee5\u524d\u949f\u4e07\u5802\u4f4f \u8fc7\u7684\u90a3\u95f4\u4e66\u623f\u3002 \u3000\u3000\u51af\u7433\u8d70\u8fdb\u8352\u56ed\uff0c\u53ea\u89c9\u5fc3\u795e\u52a8\u8361\uff0c\u5230\u4e86\u4e66\u623f\u4e4b\u540e\uff0c\u66f4\u89c9\u8fd9\u4e2a\u5730\u65b9\u597d\u50cf\u6765\u8fc7\u4e00\u822c\u3002\u82e6\u82e6\u601d \u7d22\uff0c\u5374\u662f\u60f3\u4e0d\u8d77\u6765\u3002\u5e74\u7fb9\u5c27\u8981\u5979\u4f4f\u5230\u82b1\u56ed\u91cc\u53bb\uff0c\u539f\u6709\u6839\u6df1\u7684\u7528\u610f\u3002\u56e0\u4e3a\u51af\u7433\u5403\u4e86\u5141\u796f\u7684\u8ff7 \u836f\uff0c\u5931\u6389\u8bb0\u5fc6\uff0c\u5bf9\u8fdb\u7687\u5e9c\u4e4b\u524d\u7684\u65e7\u4e8b\uff0c\u518d\u4e5f\u8bb0\u4e0d\u8d77\u6765\u3002\u56e0\u6b64\u5e74\u7fb9\u5c27\u60f3\u8ba9\u5979\u4f4f\u5728\u65e7\u65f6\u4f4f\u8fc7\u7684\u5730 \u65b9\uff0c\u597d\u89e6\u53d1\u5979\u7684\u601d\u60f3\uff0c\u6062\u590d\u5979\u7684\u8bb0\u5fc6\u3002 \u3000\u3000\u53ef\u662f\u51af\u7433\u5931\u6389\u8bb0\u5fc6\u5df2\u6709\u591a\u5e74\uff0c\u867d\u89c9\u8fd9\u56ed\u5b50\u5730\u65b9\u597d\u719f\uff0c\u4ecd\u7136\u60f3\u4e0d\u8d77\u6765\uff0c\u4f4f\u4e86\u51e0\u5929\uff0c\u56ed\u5b50\u7684 \u6bcf\u4e2a\u89d2\u843d\u90fd\u8d70\u5230\u4e86\uff0c\u604d\u60da\u8bb0\u8d77\uff0c\u8fd9\u4e9b\u5730\u65b9\uff0c\u90fd\u662f\u81ea\u5df1\u65e7\u6e38\u4e4b\u5730\u3002\u53ef\u662f\u81ea\u5df1\u4ec0\u4e48\u65f6\u5019\u5230\u8fc7\u8fd9\u4e2a \u56ed\u5b50\uff0c\u5374\u8bb0\u4e0d\u8d77\u4e86\u3002\u518d\u8f6c\u5ff5\u4e00\u60f3\uff1a\u81ea\u5df1\u548c\u5e74\u7fb9\u5c27\u662f\u6700\u8fd1\u624d\u8ba4\u8bc6\u7684\uff0c\u600e\u4e48\u4f1a\u5230\u8fc7\u4ed6\u7684\u5bb6\u4e2d\uff1f\u5fc3 \u4e2d\u7591\u8651\u4e0d\u5b9a\uff0c\u8fc7\u4e86\u534a\u4e2a\u591a\u6708\uff0c\u5fc3\u4e2d\u624d\u6e10\u6e10\u5b89\u9759\u4e0b\u6765\u3002 \u3000\u3000\u4e00\u665a\uff0c\u51af\u7433\u5728\u56ed\u5b50\u91cc\u5f98\u5f8a\uff0c\u82e6\u82e6\u601d\u7d22\uff0c\u5ffd\u89c1\u5899\u5934\u4e0a\u9ed1\u5f71\u4e00\u6643\uff0c\u5148\u540e\u6709\u4e24\u4e2a\u4eba\u8df3\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u51af\u7433\u4e00\u770b\uff0c\u89c1\u8fd9\u4e24\u4eba\u8eab\u6cd5\u867d\u9887\u77eb\u6377\uff0c\u8f7b\u529f\u5374\u975e\u4e0a\u4e58\uff0c\u5979\u638c\u5fc3\u672c\u5df1\u6697\u6263\u4e24\u67c4\u5582\u6bd2\u98de\u5200\uff0c\u5f85 \u8981\u53d1\u51fa\uff0c\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5374\u53c8\u6b62\u4f4f\u3002 \u3000\u3000\u53ea\u542c\u5f97\u524d\u5934\u90a3\u4eba\u9053\uff1a\u201c\u8fd9\u91cc\u5c31\u662f\u949f\u4e07\u5802\u751f\u524d\u7684\u5730\u65b9\u4e86\uff0c\u54a6\uff0c\u600e\u4e48\u623f\u95f4\u91cc\u6709\u706f\u5149\uff1f\u201d \u3000\u3000\u4ed6\u7684\u540c\u4f34\u9053\uff1a\u201c\u96be\u9053\u90a3\u5973\u5a03\u5b50\u8fd8\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u524d\u9762\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u7edd\u5bf9\u4e0d\u4f1a\u3002\u6211\u786e\u5b9e\u6253\u63a2\u6e05\u695a\uff0c\u5c31\u5728\u949f\u4e07\u5802\u6b7b\u7684\u90a3\u5929\uff0c\u4ed6\u5df2\u88ab\u53cc\u9b54\u62b1\u8fdb \u7687\u5e9c\u53bb\u4e86\u3002\u201d \u3000\u3000\u51af\u7433\u542c\u5f97\u201c\u949f\u4e07\u5802\u201d\u4e09\u5b57\uff0c\u5fc3\u5934\u53c8\u662f\u4e00\u9707\uff0c\u5fc3\u60f3\u600e\u4e48\u8fd9\u4e2a\u540d\u5b57\u597d\u719f\uff01 \u3000\u3000\u540e\u9762\u90a3\u4e2a\u591c\u884c\u4eba\u53c8\u9053\uff1a\u201c\u83ab\u975e\u5e74\u7fb9\u5c27\u7559\u6709\u4eba\u770b\u5b88\uff1f\u201d \u3000\u3000\u4ed6\u540c\u4f34\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u73b0\u5728\u5317\u4eac\uff0c\u5fd9\u7740\u66ff\u5141\u796f\u593a\u4f4d\uff0c\u4ed6\u54ea\u91cc\u8fd8\u6709\u95f2\u5fc3\u987e\u7740\u8fd9\u4e2a\u56ed\u5b50\u3002\u201d \u3000\u3000\u540e\u9762\u90a3\u4eba\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u8be1\u8ba1\u591a\u7aef\uff0c\u4e0d\u53ef\u4e0d\u9632\u3002\u201d\u4e24\u4eba\u6084\u6084\u5546\u8bae\u4e00\u9635\uff0c\u5404\u81ea\u53d6\u51fa\u4e00\u4e2a\u5f62\u5982 \u9e64\u5634\u4f3c\u7684\u4e1c\u897f\uff0c\u8d70\u5230\u7a97\u5b50\u4e0b\u9762\uff0c\u51af\u7433\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u5e72\u4e9b\u4ec0\u4e48\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4e24\u4eba\u63a8\u95e8\u8fdb\u53bb\u3002\u51af \u7433\u6084\u6084\u7684\u4ece\u82b1\u6811\u4e1b\u4e2d\u94bb\u4e86\u51fa\u6765\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u98de\u4e0a\u5c4b\u6a90\uff0c\u7528\u4e2a\u201c\u73cd\u73e0\u5012\u5377\u5e18\u201d\u7684\u59ff\u52bf\uff0c\u53cc\u8db3\u52fe \u7740\u5c4b\u6a90\uff0c\u6084\u6084\u5411\u4e0b\u5f20\u671b\u3002 \u3000\u3000\u5c4b\u5185\u7684\u90a3\u4e24\u4e2a\u591c\u884c\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u597d\u50cf\u751a\u4e3a\u60f6\u60d1\u3002\u539f\u6765\u4ed6\u4eec\u4ee5\u4e3a\u5c4b\u5185\u6709\u4eba\uff0c\u6240\u4ee5\u7528\u90a3\u5f62\u5982 \u9e64\u5634\u4f3c\u7684\u4e1c\u897f\uff0c\u628a\u201c\u9e21\u9e23\u4e94\u9f13\u8fd4\u9b42\u9999\u201d\u55b7\u5165\u91cc\u9762\uff0c\u4e0d\u6599\u4e00\u5230\u5c4b\u5185\uff0c\u5374\u53d1\u73b0\u6773\u65e0\u4e00\u4eba\uff0c\u8fd9\u4e43\u662f \u591c\u884c\u4eba\u7684\u5927\u5fcc\uff0c\u4e0d\u7981\u614c\u4e86\u3002\u4e00\u4eba\u518d\u8dc3\u51fa\u5916\u9762\uff0c\u56db\u9762\u5f20\u770b\uff0c\u51af\u7433\u7f29\u5728\u6a90\u810a\uff0c\u90a3\u4eba\u5f20\u671b\u4e00\u56de\uff0c\u56de \u5230\u5c4b\u5185\uff0c\u9053\uff1a\u201c\u5947\u4e86\uff0c\u771f\u6ca1\u6709\u4eba\u3002\u201d\u4ed6\u7684\u540c\u4f34\u9053\uff1a\u201c\u4e0d\u7ba1\u5b83\u6709\u6ca1\u6709\u4eba\uff0c\u54b1\u4eec\u5feb\u641c\u3002\u201d\u4e24\u4eba\u7ffb \u7bb1\u5012\u7b50\uff0c\u770b\u89c1\u51af\u7433\u7684\u8863\u88f3\uff0c\u5341\u5206\u5947\u602a\u3002 \u3000\u3000\u4e00\u4eba\u9053\uff1a\u201c\u96be\u9053\u909d\u5e08\u53d4\u90a3\u4e2a\u5916\u5b59\u5973\u513f\u53c8\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u5979\u5728\u7687\u5e9c\u91cc\u4f4f\u5f97\u597d\u597d\u7684\uff0c\u600e\u4f1a\u56de\u6765\uff0c\u522b\u80e1\u731c\u5427\u3002\u201d\u7ee7\u7eed\u641c\u67e5\uff0c\u7528\u5200\u5251\u94c1 \u51ff\uff0c\u5728\u5899\u58c1\u5730\u4e0a\u4e71\u523a\u4e71\u63d2\u3002\u51af\u7433\u770b\u5f97\u597d\u751f\u7eb3\u7f55\u3002\u5fc3\u9053\uff1a\u96be\u9053\u8fd9\u91cc\u57cb\u6709\u4ec0\u4e48\u91cd\u5b9d\u4e0d\u6210\uff1f\u8fc7\u4e86\u4e00 \u4f1a\uff0c\u4e24\u4eba\u5c06\u5979\u7761\u7684\u90a3\u5f20\u5927\u5e8a\u4e5f\u642c\u8fc7\u4e00\u8fb9\uff0c\u5728\u5e8a\u5e95\u641c\u63a2\uff0c\u53c8\u7528\u94c1\u51ff\u642d\u571f\uff0c\u5ffd\u95fb\u5f97\u91d1\u5c5e\u76f8\u89e6\u4e4b \u58f0\uff0c\u4e00\u4eba\u9053\uff1a\u201c\u627e\u7740\u4e86\uff01\u201d\u6316\u51fa\u4e00\u4e2a\u94c1\u5323\uff0c\u5de6\u5f04\u53f3\u5f04\uff0c\u5374\u5f04\u4e0d\u5f00\u3002 \u3000\u3000\u540c\u4f34\u9053\uff1a\u201c\u62ff\u56de\u53bb\u518d\u8bbe\u6cd5\u5427\u3002\u201d \u3000\u3000\u5148\u524d\u90a3\u4eba\u9053\uff1a\u201c\u4e0d\u77e5\u91cc\u9762\u85cf\u7684\u662f\u4e0d\u662f\u90a3\u672c\u4e66\uff0c\u82e5\u7136\u4e0d\u662f\uff0c\u5c82\u4e0d\u767d\u767d\u8f9b\u82e6\u4e00\u8d9f\u3002\u201d \u3000\u3000\u6478\u51fa\u4e00\u67c4\u7f05\u5200\uff0c\u9053\uff1a\u201c\u5f85\u6211\u628a\u8fd9\u94c1\u5323\u65a9\u5f00\u3002\u201d \u3000\u3000\u4ed6\u7684\u540c\u4f34\u9053\uff1a\u201c\u5f53\u5fc3\u70b9\uff0c\u53ef\u4e0d\u8981\u5f04\u574f\u5323\u4e2d\u85cf\u4e66\u3002\u201d\u8bdd\u58f0\u672a\u505c\uff0c\u5148\u524d\u90a3\u4eba\u5df2\u4e00\u5200\u5288\u4e0b\uff0c\u84e6 \u5730\u91cc\u706b\u661f\u84ec\u98de\uff0c\u94c1\u5323\u4e00\u5f00\uff0c\u4e24\u67c4\u98de\u5200\u7535\u5c04\u800c\u51fa\uff0c\u90a3\u4eba\u731d\u4e0d\u53ca\u9632\uff0c\u7ed9\u98de\u5200\u5c04\u4e2d\u5fc3\u7a9d\uff0c\u60e8\u53eb\u4e00 \u58f0\uff0c\u5f53\u573a\u5012\u5730\u3002\u53e6\u4e00\u4eba\u95ea\u8fc7\u4e00\u8fb9\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u89c1\u65e0\u5f02\u72b6\uff0c\u518d\u4e0a\u524d\u53bb\uff0c\u5c06\u5323\u91cc\u7684\u4e66\u62ff\u4e86\u51fa\u6765\uff0c \u770b\u4e86\u4e00\u770b\u653e\u5165\u6000\u5185\uff0c\u5927\u559c\u7b11\u9053\uff1a\u201c\u7ec8\u7b97\u627e\u7740\u4e86\u3002\u201d\u628a\u5c38\u9996\u8e22\u8fc7\u4e00\u8fb9\uff0c\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u660e\u5e74\u4eca \u65e5\uff0c\u6211\u66ff\u4f60\u505a\u5468\u5e74\u796d\u3002\u4e66\u5df2\u627e\u5230\uff0c\u4f60\u5728\u4e5d\u6cc9\u4e4b\u4e0b\uff0c\u4e5f\u5f53\u7791\u76ee\u4e86\u3002\u201d\u53cd\u8eab\u8d70\u51fa\u5c4b\u5b50\u3002\u51af\u7433\u5fc3 \u9053\uff1a\u8fd9\u4eba\u597d\u574f\uff0c\u53eb\u4ed6\u4e5f\u5403\u4e00\u5200\u3002\u90a3\u4eba\u521a\u521a\u8d70\u51fa\u5c4b\u5b50\uff0c\u7ed9\u51af\u7433\u4e00\u53e3\u98de\u5200\uff0c\u4e5f\u6b63\u6b63\u63d2\u4e2d\u5fc3\u7a9d\uff0c\u5012 \u5730\u60e8\u53eb\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u4e5f\u968f\u7740\u4ed6\u7684\u5e08\u5144\u5230\u9ec4\u6cc9\u8def\u53bb\u3002\u51af\u7433\u8df3\u4e86\u4e0b\u6765\uff0c\u5148\u9676\u51fa\u90a3\u4e00\u672c\u4e66\uff0c\u53ea\u89c1\u5c01 \u9762\u5199\u7740\u201c\u91d1\u9488\u5ea6\u4e16\u201d\u56db\u5b57\u3002\u53c8\u8fdb\u5c4b\u5185\u62fe\u8d77\u90a3\u94c1\u5323\u4e2d\u5c04\u51fa\u7684\u4e24\u67c4\u98de\u5200\uff0c\u770b\u4e86\u4e00\u770b\uff0c\u4e0d\u89c9\u5927\u5403\u4e00 \u60ca\uff01 \u3000\u3000\u90a3\u4e24\u67c4\u98de\u5200\u7684\u5f62\u5f0f\u548c\u81ea\u5df1\u7684\u5b8c\u5168\u4e00\u6837\uff0c\u51af\u7433\u518d\u5bdf\u770b\u90a3\u4e24\u4eba\u7684\u4f24\u53e3\uff0c\u4e2d\u6bd2\u7684\u5fae\u8c61\u4e5f\u76f8\u540c\uff0c \u53c8\u60ca\u53c8\u7591\uff0c\u5fc3\u60f3\uff1a\u97e9\u4f2f\u4f2f\u8bf4\u6211\u6240\u7528\u7684\u5582\u6bd2\u98de\u5200\u4e43\u662f\u72ec\u95e8\u6697\u5668\uff0c\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u65e0\u4eba\u4f1a\u4f7f\uff0c\u4f55\u4ee5\u8fd9 \u94c1\u5323\u4e2d\u6240\u5c04\u51fa\u7684\u98de\u5200\uff0c\u548c\u6211\u7684\u5b8c\u5168\u540c\u4e00\u5bb6\u6570\uff1f \u3000\u3000\u539f\u6765\u51af\u7433\u81ea\u5c0f\u8ddf\u968f\u949f\u4e07\u5802\uff0c\u5b66\u6210\u4e86\u593a\u547d\u795e\u5200\u7684\u7edd\u6280\uff0c\u88ab\u53cc\u9b54\u62b1\u8fdb\u7687\u5e9c\u4e4b\u540e\uff0c\u672c\u6027\u867d\u7136\u8ff7 \u5931\uff0c\u5c0f\u5bf9\u6240\u5b66\u8fc7\u7684\u6b66\u827a\uff0c\u5374\u672a\u5fd8\u8bb0\uff0c\u5979\u90a3\u4e00\u5323\u4e8c\u5341\u56db\u628a\u6bd2\u5200\uff0c\u4e5f\u4ecd\u5e26\u5728\u8eab\u8fb9\u3002\u56db\u7687\u5e9c\u4e2d\u9ad8\u624b \u5982\u4e91\uff0c\u51af\u7433\u56e0\u5f97\u4ed6\u4eec\u559c\u7231\uff0c\u6bcf\u4eba\u90fd\u4f20\u5979\u6b66\u827a\uff1b\u5176\u4e2d\u97e9\u91cd\u5c71\u4e43\u662f\u4f7f\u6697\u5668\u7684\u9ad8\u624b\uff0c\u89c1\u4e86\u5979\u7684\u98de \u5200\uff0c\u4fbf\u77e5\u662f\u5085\u9752\u4e3b\u8fd9\u4e00\u6d3e\u7684\u771f\u4f20\uff0c\u97e9\u91cd\u5c71\u62ff\u4e86\u5979\u7684\u98de\u5200\uff0c\u7ec6\u5fc3\u7814\u7a76\uff0c\u4e0d\u6d88\u51e0\u5929\uff0c\u8fde\u6dec\u70bc\u98de\u5200 \u7684\u6bd2\u836f\uff0c\u4e5f\u7814\u7a76\u51fa\u4e86\u3002\u4f46\u97e9\u91cd\u5c71\u81ea\u5df1\u662f\u4e00\u6d3e\u5b97\u7956\uff0c\u4e0d\u613f\u4f7f\u7528\u522b\u6d3e\u7684\u6697\u5668\uff0c\u6240\u4ee5\u53ea\u4f20\u4e86\u51af\u7433\u6dec \u70bc\u98de\u5200\u7684\u65b9\u6cd5\u548c\u914d\u5236\u89e3\u836f\uff0c\u5e76\u6307\u70b9\u5979\u7684\u624b\u6cd5\uff0c\u81ea\u5df1\u5374\u4e0d\u4f7f\u7528\u3002\u949f\u4e07\u5802\u6b7b\u540e\uff0c\u65e0\u6781\u6d3e\u6ca1\u6709\u4f20 \u4eba\uff0c\u5e74\u7fb9\u5c27\u867d\u4e5f\u5b66\u5f97\u51e0\u6210\uff0c\u4f46\u4ee5\u5e74\u7fb9\u5c27\u7684\u8eab\u4efd\uff0c\u81ea\u7136\u4e0d\u4f1a\u518d\u5728\u6c5f\u6e56\u884c\u8d70\uff0c\u6240\u4ee5\u97e9\u91cd\u5c71\u624d\u4f1a\u5bf9 \u5979\u8bf4\u90a3\u4e00\u756a\u8bdd\u3002\u51af\u7433\u4e5f\u4ee5\u4e3a\u98de\u5200\u4e4b\u6280\u662f\u51fa\u4e8e\u97e9\u91cd\u5c71\u6240\u4f20\uff0c\u6839\u672c\u8bb0\u4e0d\u8d77\u6709\u4e2a\u201c\u949f\u4e07\u5802\u201d\u4e86\u3002 \u3000\u3000\u51af\u7433\u60f3\u6765\u60f3\u53bb\uff0c\u60f3\u4e0d\u660e\u767d\uff0c\u53ea\u597d\u628a\u90a3\u4e24\u4e2a\u5c38\u9996\u6084\u6084\u57cb\u4e86\u3002\u6536\u62fe\u597d\u623f\u4e4b\u540e\uff0c\u5c55\u5f00\u90a3\u94c1\u5323\u4e2d \u7684\u85cf\u4e66\u4e00\u770b\uff0c\u53c8\u5403\u4e00\u60ca\uff0c\u5403\u60ca\u4e4b\u540e\uff0c\u53c8\u4e0d\u7981\u559c\u51fa\u671b\u5916\uff01 \u3000\u3000\u8fd9\u672c\u4e66\u5206\u4e0a\u4e0b\u4e8c\u5377\uff0c\u4e0a\u5377\u5171\u5341\u4e09\u7bc7\uff0c\u524d\u4e09\u7bc7\u662f\u5185\u529f\u7a8d\u8981\uff0c\u540e\u5341\u7bc7\u5219\u662f\u62f3\u7ecf\u5251\u8bc0\u3002\u4e0b\u5377\u5341 \u4e8c\u7bc7\u5168\u662f\u533b\u4e66\u3002\u51af\u7433\u5148\u770b\u62f3\u7ecf\u5251\u8bc0\uff0c\u89c9\u5f97\u6709\u4e9b\u624b\u6cd5\u8fd8\u4e0d\u53ca\u81ea\u5df1\u6240\u5b66\u7684\u5389\u5bb3\uff0c\u4f46\u518d\u770b\u524d\u4e09\u7bc7 \u65f6\uff0c\u5219\u89c9\u5176\u4e2d\u6df1\u85cf\u5965\u4e49\uff0c\u7cbe\u5999\u65e0\u7a77\u3002\u51af\u7433\u672c\u6765\u4ece\u674e\u6cbb\u90a3\u513f\uff0c\u5b66\u4e86\u4e00\u4e9b\u4fee\u7ec3\u5185\u529f\u4e4b\u6cd5\uff0c\u53ef\u662f\u56e0 \u4e3a\u674e\u6cbb\u672c\u4eba\u5c1a\u672a\u8fbe\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u6709\u8bb8\u591a\u90fd\u662f\u77e5\u5176\u7136\u800c\u4e0d\u77e5\u5176\u6240\u4ee5\u7136\uff1b\u800c\u4e14\u53c8\u662f\u51fa\u4e8e\u53e3 \u6388\uff0c\u4e1c\u9cde\u897f\u722a\uff0c\u6709\u5982\u4e00\u76d8\u6563\u73e0\uff0c\u4e32\u4e0d\u8d77\u6765\u3002\u770b\u4e86\u4e66\u540e\uff0c\u987f\u89c9\u8109\u7edc\u5206\u660e\uff0c\u4ee5\u524d\u6240\u5b66\u7684\uff0c\u975e\u4f46\u80fd \u7528\u4e66\u4e2d\u4e4b\u7406\u4e00\u4ee5\u8d2f\u4e4b\uff0c\u800c\u4e14\u609f\u4e86\u8bb8\u591a\u7cbe\u4e49\u3002\u8981\u77e5\u5085\u9752\u4e3b\u4e43\u662f\u5185\u5bb6\u6b63\u5b97\uff0c\u6b66\u529f\u867d\u7136\u4e0d\u53ca\u767d\u53d1\u9b54 \u5973\u7684\u8f9b\u8fa3\uff0c\u4f46\u7167\u4ed6\u7684\u65b9\u6cd5\u4fee\u4e60\u5185\u529f\uff0c\u6bd4\u8d77\u767d\u53d1\u9b54\u5973\u8fd9\u4e00\u6d3e\u6765\uff0c\u5374\u8981\u4e8b\u534a\u529f\u500d\u3002 \u3000\u3000\u51af\u7433\u5929\u8d44\u9896\u609f\uff0c\u81ea\u6b64\u5728\u82b1\u56ed\u7684\u9759\u5ba4\u91cc\u4f9d\u4e66\u4fee\u7ec3\uff0c\u8fc7\u4e86\u534a\u5e74\uff0c\u8fdb\u5883\u795e\u901f\u3002\u8fde\u4e66\u4e2d\u7684\u62f3\u7ecf\u5251 \u8bc0\uff0c\u4e5f\u5168\u5b66\u4e86\u3002\u8fd9\u4e00\u65e5\u5076\u800c\u7ffb\u9605\u4e0b\u5377\uff0c\u51af\u7433\u672c\u4e0d\u8010\u70e6\u9605\u8bfb\u533b\u4e66\uff0c\u7ffb\u4e86\u5341\u591a\u5eff\u9875\uff0c\u5ffd\u89c1\u5176\u4e2d\u8f7d \u6709\u533b\u6cbb\u8ff7\u5931\u8bb0\u5fc6\u7684\u65b9\u6cd5\uff0c\u770b\u4e86\u534a\u5929\uff0c\u770b\u4e0d\u660e\u767d\u3002\u539f\u6765\u8fd9\u4e43\u662f\u5085\u9752\u4e3b\u5f53\u5e74\u4e3a\u6842\u4ef2\u660e\u7684\u201c\u79bb\u9b42 \u75c7\u201d\u6240\u4f5c\u7684\u4e00\u4e2a\u533b\u6848\uff0c\u5085\u9752\u4e3b\u7cbe\u5fc3\u7814\u7a76\u533b\u5b66\uff0c\u5f53\u5e74\u6842\u4ef2\u660e\u4e4b\u75c5\uff0c\u867d\u7136\u662f\u9760\u5192\u70f7\u83b2\u4e4b\u529b\u6cbb\u597d\uff0c \u5176\u4e2d\u7ecf\u8fc7\uff0c\u5f97\u610f\u5916\u4e4b\u52a9\u9887\u591a\uff08\u6842\u4ef2\u660e\u4e4b\u602a\u75c5\uff0c\u4e8b\u89c1\u62d9\u8457\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\uff09\u3002\u5085\u9752\u4e3b\u5fc3\u60f3\u6cbb\u75c5 \u4e0d\u80fd\u5168\u51ed\u5de7\u5408\uff0c\u56e0\u6b64\u795e\u7cbe\u7aed\u667a\uff0c\u60f3\u51fa\u4e86\u533b\u6cbb\u8fd9\u4e00\u7c7b\u201c\u79bb\u9b42\u201d\u602a\u75c7\u7684\u65b9\u6cd5\uff0c\u6240\u7528\u7684\u65b9\u6cd5\u5927\u81f4\u76f8 \u5f53\u4e8e\u8fd1\u4ee3\u7684\u50ac\u7720\u672f\uff0c\u51af\u7433\u6beb\u65e0\u533b\u5b66\u57fa\u7840\uff0c\u6240\u4ee5\u770b\u4e0d\u660e\u767d\u3002 \u3000\u3000\u51af\u7433\u867d\u7136\u770b\u4e0d\u660e\u767d\uff0c\u4f46\u5374\u56e0\u6b64\u6709\u6240\u89e6\u53d1\u3002\u5fc3\u60f3\uff0c\u81ea\u5df1\u5bf9\u5e7c\u5e74\u4e4b\u4e8b\uff0c\u6bcf\u6bcf\u60f3\u4e0d\u8d77\u6765\uff0c\u95ee\u90a3 \u4e9b\u53d4\u4f2f\uff0c\u4e5f\u662f\u8bed\u7109\u4e0d\u8be6\uff0c\u800c\u4e14\u5404\u4eba\u6240\u8a00\uff0c\u9887\u6709\u5dee\u5f02\uff0c\u83ab\u975e\u81ea\u5df1\u4e5f\u60a3\u6709\u8fd9\u79cd\u201c\u79bb\u9b42\u201d\u7684\u602a\u75c7\u3002 \u8fd9\u6837\u770b\u6765\uff0c\u8fd9\u672c\u4e66\u5012\u5927\u6709\u7528\u5904\u5462\uff01\u968f\u624b\u7ffb\u9605\u4e0b\u53bb\uff0c\u7ffb\u5230\u6700\u672b\u4e00\u9875\uff0c\u5ffd\u89c1\u6709\u51e0\u884c\u5199\u9053\uff1a\u201c\u5b57\u8c15 \u7fb9\u5c27\u5f92\u513f\uff1a\u6b64\u4e66\u4e43\u4f60\u5085\u7956\u5e08\u4e00\u751f\u5fc3\u8840\uff0c\u4f60\u5f53\u73cd\u800c\u85cf\u4e4b\uff0c\u5e76\u51ed\u6b64\u4e66\u4e3a\u636e\uff0c\u63a5\u638c\u65e0\u6781\u6d3e\u95e8\u6237\uff0c\u949f \u4e07\u5802\u3002\u201d\u539f\u6765\u949f\u4e07\u5802\u751f\u524d\uff0c\u8017\u5c3d\u5fc3\u8840\uff0c\u628a\u5e74\u7fb9\u5c27\u8bad\u7ec3\u6210\u6587\u6b66\u5168\u6750\uff0c\u672c\u610f\u5c31\u662f\u60f3\u4ed6\u7ee7\u627f\u65e0\u6781\u6d3e \u7684\u8863\u94b5\uff0c\u4e0d\u8fc7\u56e0\u4e3a\u5e74\u7fb9\u5c27\u8fd8\u672a\u957f\u5927\u6210\u4eba\uff08\u949f\u6b7b\u65f6\u5e74\u7fb9\u5c27\u65b9\u5341\u56db\u5c81\uff09\uff0c\u6240\u4ee5\u6ca1\u6709\u5c06\u8fd9\u672c\u4e66\u4f20\u7ed9 \u4ed6\u3002\u949f\u4e07\u5802\u4e3a\u4e86\u9884\u9632\u7ed9\u4ec7\u5bb6\u7a81\u7136\u6240\u5bb3\uff0c\u5728\u4e66\u540e\u7559\u4e0b\u90a3\u51e0\u884c\u5b57\uff0c\u539f\u6709\u5f53\u4f5c\u9057\u5631\u7684\u7528\u610f\u3002\u540e\u6765\u949f \u4e07\u5802\u679c\u771f\u88ab\u53cc\u9b54\u6240\u5bb3\uff0c\u4e34\u6b7b\u4e4b\u65f6\uff0c\u53d1\u73b0\u5e74\u7fb9\u5c27\u7adf\u662f\u4e2a\u5fc3\u672f\u6781\u574f\uff0c\u80cc\u5e08\u53db\u9053\u4e4b\u4eba\uff0c\u8fd9\u672c\u4e66\u7684\u85cf \u5904\uff0c\u4e5f\u5c31\u6ca1\u6709\u544a\u8bc9\u4ed6\u4e86\u3002 \u3000\u3000\u51af\u7433\u770b\u4e86\u8fd9\u51e0\u884c\u5b57\u540e\uff0c\u624d\u77e5\u9053\u949f\u4e07\u5802\u662f\u5e74\u7fb9\u5c27\u7684\u5e08\u5085\uff0c\u4e0d\u89c9\u5c06\u4e00\u4e9b\u4e8b\u60c5\u8054\u60f3\u8d77\u6765\uff0c\u60f3 \u9053\uff1a\u8fd9\u949f\u4e07\u5802\u4e0d\u77e5\u662f\u4ec0\u4e48\u4eba\uff0c\u4f46\u4ed6\u94c1\u5323\u4e2d\u7684\u98de\u5200\u548c\u6211\u6240\u7528\u7684\u4e00\u6a21\u4e00\u6837\uff0c\u60f3\u5fc5\u548c\u6211\u6709\u70b9\u5173\u8054\u7684 \u4e86\u3002\u4f46\u542c\u90a3\u4e24\u4e2a\u5077\u4e66\u8d3c\u6240\u8a00\uff0c\u949f\u4e07\u5802\u6b64\u4eba\u5df2\u7ecf\u6b7b\u53bb\uff0c\u770b\u6765\u53ea\u597d\u95ee\u5e74\u7fb9\u5c27\u4e86\u3002\u8fd9\u4e00\u665a\uff0c\u51af\u7433\u5bf9 \u5377\u51dd\u601d\uff0c\u60f3\u5230\u6df1\u591c\uff0c\u76f4\u5230\u5e74\u7fb9\u5c27\u6765\u4e86\uff0c\u5979\u624d\u60ca\u8d77\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u63a8\u95e8\u8fdb\u53bb\uff0c\u5ffd\u89c1\u51af\u7433\u5750\u5728\u623f\u4e2d\uff0c\u6b63\u5728\u63a8\u5f00\u4e00\u672c\u4e66\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u665a \u4e86\uff0c\u4f60\u8fd8\u672a\u7761\u5417\uff1f\u770b\u7684\u662f\u4ec0\u4e48\u4e66\u5440\uff1f\u201d\u51af\u7433\u7a81\u7136\u60f3\u8d77\uff0c\u8fd9\u672c\u4e66\u662f\u949f\u4e07\u5802\u7559\u7ed9\u5e74\u7fb9\u5c27\u7684\uff0c\u82e5\u7136 \u7ed9\u4ed6\u770b\u89c1\uff0c\u5c82\u4e0d\u662f\u8981\u9001\u56de\u7ed9\u4ed6\uff1f\u51af\u7433\u5fc3\u4e2d\u4e0d\u820d\uff0c\u5e74\u7fb9\u5c27\u4f38\u8fc7\u5934\u6765\uff0c\u51af\u7433\u5c06\u4e66\u4e00\u5377\uff0c\u653e\u5165\u6000 \u4e2d\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u771f\u662f\uff0c\u8fdb\u6765\u4e5f\u4e0d\u5148\u53eb\u4e00\u58f0\u3002\u201d\u51af\u7433\u6d45\u7b11\u8f7b\u98a6\uff0c\u5e74\u7fb9\u5c27\u770b\u5f97\u75f4\u4e86\u3002\u95ee\u9053\uff1a \u201c\u4f60\u7684\u4f24\u5168\u597d\u4e86\u5417\uff1f\u201d\u51af\u7433\u6115\u7136\u4e0d\u8bed\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u7684\u529f\u592b\u4fca\u6781\u4e86\uff0c \u6bd4\u4ee5\u524d\u9ad8\u660e\u5f97\u591a\u5566\uff01\u2026\u2026\u201d\u51af\u7433\u51db\u7136\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u83ab\u975e\u4ed6\u5df2\u77e5\u6211\u5f97\u4e86\u8fd9\u672c\u5b9d\u4e66\u3002\u5e74\u7fb9\u5c27\u7ee7 \u9053\uff1a\u201c\u516b\u81c2\u795e\u9b54\u7684\u6bd2\u722a\u5389\u5bb3\u975e\u5e38\uff0c\u4f60\u670d\u4e86\u89e3\u836f\u4e4b\u540e\u534a\u4e2a\u65f6\u8fb0\u5c31\u884c\u52a8\u5982\u5e38\uff0c\u771f\u771f\u96be\u5f97\uff01\u770b\u4f60\u73b0 \u5728\u7684\u9762\u8272\uff0c\u7b80\u76f4\u50cf\u6ca1\u4e8b\u4eba\u4e00\u6837\u3002\u201d\u51af\u7433\u5947\u9053\uff1a\u201c\u6211\u51e0\u65f6\u53d7\u4f24\u4e86\uff1f\u201d\u5e74\u7fb9\u5c27\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u6dd8 \u6c14\uff0c\u548c\u6211\u5f00\u4ec0\u4e48\u73a9\u7b11\u3002\u4f60\u4e0d\u60f3\u89c1\u53cc\u9b54\uff0c\u6211\u5df2\u628a\u4ed6\u4eec\u652f\u5f00\u4e86\u3002\u201d\u51af\u7433\u83ab\u540d\u5176\u5999\uff0c\u9053\uff1a\u201c\u4f60\u5230\u5e95 \u8bf4\u4e9b\u4ec0\u4e48\uff0c\u6211\u4e00\u70b9\u4e5f\u4e0d\u660e\u767d\uff01\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u4eca\u65e5\u5348\u95f4\u548c\u8c01\u6253\u67b6\u6765\u4e86\uff1f\u201d\u51af\u7433\u5947\u9053\uff1a\u201c\u6211 \u6765\u4e86\u5927\u534a\u5e74\uff0c\u672a\u66fe\u51fa\u8fc7\u56ed\u5b50\uff0c\u51e0\u65f6\u548c\u4eba\u6253\u67b6\u6765\u4e86\uff1f\u201d\u5e74\u7fb9\u5c27\u89c1\u5979\u795e\u60c5\uff0c\u4e0d\u4f3c\u8bf4\u7b11\uff0c\u4e0d\u7981\u5927 \u5947\uff0c\u518d\u8ffd\u95ee\u9053\uff1a\u201c\u4f60\u4eca\u65e5\u6574\u6574\u4e00\u65e5\u90fd\u5728\u8fd9\u623f\u5b50\u91cc\u5417\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u9a97\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u5e74\u7fb9\u5c27 \u7741\u5927\u4e86\u773c\uff0c\u9053\uff1a\u201c\u6211\u4eca\u65e5\u5206\u660e\u89c1\u7740\u4f60\u561b\uff01\u201d\u51af\u7433\u89c1\u4ed6\u602a\u6837\uff0c\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u89c1\u7740\u9b3c \u5566\uff01\u201d\u505c\u4e86\u4e00\u505c\uff0c\u5ffd\u9053\uff1a\u201c\u522b\u8bf4\u8fd9\u4e9b\u9b3c\u8bdd\u5566\uff0c\u6211\u95ee\u4f60\uff0c\u949f\u4e07\u5802\u662f\u4f60\u7684\u5e08\u5085\uff0c\u662f\u5417\uff1f\u201d\u5e74\u7fb9\u5c27 \u53cc\u773c\u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u5514\uff0c\u600e\u4e48\u6837\uff1f\u201d\u51af\u7433\u7a81\u7136\u628a\u624b\u4e00\u626c\uff0c\u4e00\u67c4\u98de\u5200\u5411\u5e74\u7fb9\u5c27\u8fce\u9762\u98de\u53bb\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u5927\u5403\u4e00\u60ca\uff0c\u4f38\u624b\u628a\u98de\u5200\u63a5\u7740\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u51af\u7433\u563b\u563b\u7b11\u9053\uff1a\u201c\u4f60\u63a5\u98de\u5200 \u7684\u624b\u6cd5\u548c\u6211\u4e00\u6837\u3002\u201d\u5e74\u7fb9\u5c27\u5fc3\u9053\uff1a\u201c\u6211\u548c\u4f60\u540c\u51fa\u4e00\u5e08\u4f20\u6388\uff0c\u600e\u4f1a\u4e0d\u4e00\u6837\u3002\u201d\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60 \u662f\u8bd5\u6211\u6765\u4e86\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u90a3\u65e5\u6211\u77a7\u4e0d\u6e05\u695a\u4f60\u7684\u624b\u6cd5\uff0c\u6240\u4ee5\u518d\u8bd5\u4e00\u6b21\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u8bd5 \u8fd9\u4e2a\u5e72\u561b\uff1f\u201d\u51af\u7433\u624b\u6258\u9999\u816e\uff0c\u76ef\u7740\u5e74\u7fb9\u5c27\u773c\u775b\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u56ed\u5b50\u6211\u597d\u50cf\u6765\u8fc7\u4f3c\u7684\uff1f\u201d \u5e74\u7fb9\u5c27\u5fc3\u4e2d\u4e00\u8df3\uff0c\u51af\u7433\u53c8\u9053\uff1a\u201c\u6211\u548c\u4f60\u597d\u50cf\u4e5f\u662f\u4ee5\u524d\u76f8\u8bc6\u4f3c\u7684\uff1f\u600e\u4e48\u4ed6\u4eec\u4e00\u70b9\u4e5f\u4e0d\u544a\u8bc9\u6211 \u5462\uff1f\u201d\u5e74\u7fb9\u5c27\u672c\u6027\u591a\u7591\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u91ce\u4e2b\u5934\u7cbe\u7075\u6781\u4e86\uff0c\u5979\u4ee5\u524d\u7684\u8bf4\u8bdd\u4e0d\u53ef\u5168 \u4fe1\u3002\u82e5\u7136\u5979\u4e0d\u662f\u548c\u7687\u4e0a\u95f9\u7ffb\uff0c\u800c\u662f\u81ea\u4e0a\u6d3e\u6765\u8bd5\u63a2\u6211\u7684\uff0c\u90a3\u53ef\u7cdf\u4e86\u3002\u201d\u51af\u7433\u89c1\u5e74\u7fb9\u5c27\u773c\u73e0\u8f6c\u6765 \u8f6c\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u8bf4\u8bdd\u554a\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u600e\u4f1a\u77e5\u9053\u6211\u5e08\u5085\u7684\u540d\u5b57\uff1f\u201d\u8fd9\u56de\u8f6c\u5230\u51af \u7433\u4e00\u6014\uff0c\u4e00\u65f6\u95f4\u7b54\u4e0d\u51fa\u8bdd\u6765\u3002\u51af\u7433\u820d\u4e0d\u5f97\u90a3\u672c\u4e66\uff0c\u52bf\u4e0d\u80fd\u8bf4\u662f\u4ece\u90a3\u672c\u4e66\u4e0a\u949f\u4e07\u5802\u6240\u7559\u7684\u9057\u8a00 \u77e5\u9053\u7684\u3002\u5e74\u7fb9\u5c27\u628a\u5979\u5c34\u5c2c\u7684\u795e\u60c5\u770b\u5728\u773c\u5185\uff0c\u8d8a\u53d1\u8d77\u7591\u3002\u60f3\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u7687\u5e9c\u7684\u4eba\u544a\u8bc9\u5979\uff0c\u5c31 \u662f\u6c5f\u6e56\u4e0a\u7684\u4eba\u7269\u544a\u8bc9\u5979\u4e86\u3002\u5979\u9003\u4e86\u51fa\u6765\u5df2\u8fd1\u4e00\u5e74\uff0c\u4e0d\u77e5\u548c\u4ec0\u4e48\u4eba\u7269\u6765\u5f80\uff0c\u8fd9\u5c42\u4e5f\u4e0d\u53ef\u4e0d \u9632\u3002\u201d\u51af\u7433\u7709\u5934\u4e00\u76b1\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u600e\u4e48\u5982\u6b64\u591a\u7591\uff0c\u4f60\u5e74\u5927\u5c06\u519b\u58f0\u540d\u663e\u8d6b\uff0c\u4f60\u7684\u4e8b\u60c5\uff0c\u81ea \u7136\u5f88\u591a\u4eba\u77e5\u9053\u7684\u4e86\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u5230\u5e95\u662f\u8c01\u544a\u8bc9\u4f60\u7684\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u662f\u542c\u8def\u4eba\u95f2\u8c08\u77e5\u9053 \u7684\u3002\u600e\u77e5\u90a3\u4e9b\u4eba\u540d\u5b57\uff1f\u201d\u51af\u7433\u81ea\u4ee5\u4e3a\u8fd9\u756a\u8bdd\u8bf4\u5f97\u5f88\u4e3a\u5f97\u4f53\uff0c\u6b8a\u4e0d\u77e5\u53c8\u89e6\u4e86\u5e74\u7fb9\u5c27\u4e4b\u5fcc\uff0c\u5e74\u7fb9 \u5c27\u4ece\u949f\u4e07\u5802\u4e60\u6280\uff0c\u5176\u4e8b\u751a\u79d8\uff0c\u8def\u4eba\u600e\u4f1a\u77e5\u9053\uff0c\u5e74\u7fb9\u5c27\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u5b66\u4f1a\u9a97\u4eba \u7684\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u8c01\u9a97\u4f60\u554a\uff1f\u6211\u95ee\u4f60\u7684\u8bdd\u4f60\u7406\u4e5f\u4e0d\u7406\uff0c\u5374\u53cd\u8fc7\u6765\u76d8\u95ee\u6211\uff0c\u54fc\uff0c\u6211\u4e0d\u548c\u4f60\u597d \u4e86\uff01\u201d\u5e74\u7fb9\u5c27\u7ed9\u5979\u4e00\u9017\uff0c\u5fc3\u75d2\u75d2\u7684\uff0c\u62ff\u4e0d\u51c6\u5979\u8fd9\u5929\u771f\u65e0\u90aa\u7684\u795e\u60c5\u662f\u5426\u5047\u88c5\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5750 \u4e0b\u6765\uff0c\u6211\u95ee\u4f60\uff0c\u4f60\u7684\u98de\u5200\u662f\u8c01\u6559\u7684\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u662f\u97e9\u91cd\u5c71\u4f2f\u4f2f\u6559\u7684\u3002\u201d\u5e74\u7fb9\u5c27\u6447\u6447\u9053\uff1a \u201c\u4e0d\u662f\uff01\u201d\u51af\u7433\u9053\uff1a\u201c\u90a3\u4e48\u662f\u8c01\u6559\u7684\uff1f\u201d\u5e74\u7fb9\u5c27\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u4f3c\u6709\u58f0\u54cd\uff0c\u6025\u547c\u58f0\u8bf4 \u9053\uff1a\u201c\u5feb\u8eb2\u8d77\u6765\uff0c\u5f85\u6211\u770b\u662f\u8c01\u6765\u4e86\uff1f\u201d\u51af\u7433\u62a2\u4e00\u4e2a\u9b3c\u8138\uff0c\u8eb2\u5230\u5e8a\u540e\uff0c\u5e74\u7fb9\u5c27\u6253\u5f00\u4e66\u623f\u95e8\u4e00 \u77a7\uff0c\u5ffd\u89c1\u4e86\u56e0\u624b\u63d0\u7985\u6756\uff0c\u5927\u8e0f\u6b65\u8d70\u6765\u3002 \u3000\u3000\u539f\u6765\u4e86\u56e0\u548c\u8463\u5de8\u5ddd\u5f11\u7fbd\u800c\u5f52\u4e4b\u540e\uff0c\u542c\u5f97\u5e74\u7fb9\u5c27\u56de\u4e86\u8001\u5bb6\uff0c\u4e86\u56e0\u7b49\u4e94\u4eba\u51fa\u5bab\u4e4b\u65f6\uff0c\u5141\u796f\u66fe \u6709\u5bc6\u65e8\uff0c\u8981\u4ed6\u4eec\u6697\u4e2d\u76d1\u89c6\u5e74\u7fb9\u5c27\u3002\u4e86\u56e0\u501a\u8001\u5356\u8001\uff0c\u7d20\u6765\u4e0d\u628a\u5e74\u7fb9\u5c27\u770b\u5728\u773c\u5185\uff0c\u8fd9\u65f6\u53c8\u6068\u5e74\u7fb9 \u5c27\u4e0d\u7b49\u4ed6\u4eec\uff0c\u548c\u8463\u5de8\u5ddd\u5546\u91cf\u9053\uff1a\u201c\u5c0f\u5e74\u8981\u6211\u4eec\u66ff\u4ed6\u5356\u547d\uff0c\u4ed6\u5374\u56de\u5bb6\u4eab\u798f\uff0c\u54b1\u4eec\u627e\u4ed6\u53bb\u3002\u201d\u8463 \u5de8\u5ddd\u57ce\u5e9c\u751a\u6df1\uff0c\u7b11\u9053\uff1a\u201c\u4ed6\u662f\u4e09\u519b\u4e3b\u5e05\uff0c\u6211\u4eec\u6ca1\u6709\u4ed6\u7684\u53ec\u5524\uff0c\u53c8\u65e0\u7d27\u8981\u4e8b\u60c5\uff0c\u600e\u597d\u627e\u4ed6\uff1f\u201d \u4e86\u56e0\u9053\uff1a\u201c\u544a\u8bc9\u4ed6\uff0c\u6211\u4eec\u6349\u4e0d\u5230\u90a3\u4e2a\u65b9\u4eca\u660e\uff0c\u5374\u78b0\u7740\u4e86\u90a3\u4e2a\u91ce\u4e2b\u5934\uff0c\u8fd9\u4e9b\u4e8b\u60c5\u4e0d\u7d27\u8981\u5417\uff1f\u201d \u8463\u5de8\u5ddd\u6447\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e9b\u90fd\u662f\u5c0f\u4e8b\u3002\u201d\u4e86\u56e0\u53cc\u773c\u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u53bb\u6211\u53bb\uff01\u6211\u4eec\u9886\u6709\u7687 \u4e0a\u5bc6\u65e8\uff0c\u53c8\u975e\u4ed6\u7684\u4e0b\u5c5e\uff0c\u6015\u4ed6\u4ec0\u4e48\uff1f\u201d\u63d0\u4e86\u7985\u6756\uff0c\u8fde\u591c\u95ef\u5230\u9648\u7559\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u89c1\u662f\u4e86\u56e0\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\uff0c\u4f55\u4ee5\u6df1\u591c\u5230\u6765\uff1f\u201d\u4e86\u56e0\u5f20\u773c\u56db\u671b\uff0c \u9053\uff1a\u201c\u5c0f\u5e74\uff0c\u4f60\u548c\u8c01\u8bf4\u8bdd\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6211\u5728\u623f\u91cc\u8bfb\u4e66\uff0c\u4f60\u542c\u9519\u4e86\u3002\u201d\u4e86\u56e0\u9053\uff1a\u201c\u8fd9\u95f4\u623f \u5c31\u662f\u949f\u4e07\u5802\u4ee5\u524d\u4f4f\u7684\u5417\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u4e86\u56e0\u9053\uff1a\u201c\u96c5\u81f4\u6781\u4e86\uff0c\u597d\uff0c\u6211\u4eec\u8fdb\u53bb\u5750 \u5750\u3002\u201d\u4e0d\u5f85\u5e74\u7fb9\u5c27\u7b54\u8bdd\uff0c\u8ff3\u81ea\u63a8\u95e8\u8fdb\u53bb\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u5fc3\u4e2d\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u79c3\u9a74\uff0c\u6162\u6162\u53eb\u4f60\u77e5\u9053\u6211\u7684\u5389\u5bb3\u3002\u201d\u5fc3\u4e2d\u6120\u6012\uff0c\u9762\u4e0a\u5374\u4e1d\u6beb\u4e0d \u9732\uff0c\u966a\u7b11\u8bf4\u9053\uff1a\u201c\u7985\u5e08\u8d4f\u5149\uff0c\u90a3\u597d\u6781\u4e86\u3002\u201d\u8ddf\u7740\u4e86\u56e0\u8fdb\u53bb\u3002\u4e86\u56e0\u5750\u4e0b\u6765\u6df1\u6df1\u547c\u5438\uff0c\u7b11\u9053\uff1a \u201c\u597d\u9999\uff0c\u50cf\u662f\u5c0f\u59d0\u7684\u95fa\u623f\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u5927\u5e08\u8bf4\u7b11\u4e86\uff01\u201d\u4e86\u56e0\u8d3c\u5fd2\u5fd2\u7684\u56db\u9762\u5f20\u770b\uff0c\u9053\uff1a \u201c\u7ee3\u82b1\u7684\u6795\u5934\uff0c\u5927\u7ea2\u7684\u88ab\u8925\uff0c\u54e6\uff0c\u4f60\u56de\u5bb6\u79d8\u5bc6\u5b8c\u5a5a\uff0c\u8fde\u6211\u4eec\u4e5f\u4e0d\u544a\u8bc9\uff0c\u8be5\u5f53\u4f55\u7f6a\uff1f\u201d\u5e74\u7fb9\u5c27 \u9053\uff1a\u201c\u5bb6\u6bcd\u5bf9\u6211\u81ea\u5c0f\u6eba\u7231\uff0c\u6211\u662f\u72ec\u5b50\uff0c\u4ed6\u6015\u6211\u957f\u4e0d\u5927\uff0c\u542c\u4e00\u4e9b\u4e09\u59d1\u516d\u5a46\u7684\u8bf4\u8bdd\uff0c\u628a\u6211\u5f53\u5973\u513f \u770b\u5f85\uff0c\u5e8a\u94fa\u88ab\u8925\uff0c\u90fd\u662f\u5973\u5b69\u513f\u5bb6\u7528\u7684\uff0c\u8bf4\u8fd9\u6837\u53ef\u4ee5\u5316\u89e3\u707e\u6b83\uff0c\u771f\u771f\u53ef\u7b11\u6781\u4e86\u3002\u201d\u90a3\u65f6\u6c11\u95f4\u98ce \u4fd7\uff0c\u628a\u72ec\u5b50\u5f53\u5973\u513f\u517b\u5927\u7684\u975e\u5e38\u4e4b\u591a\u3002\u4e86\u56e0\u5c06\u4fe1\u5c06\u7591\uff0c\u7a81\u7136\u8d70\u5230\u5e8a\u524d\uff0c\u8f7b\u6447\u5e8a\u67f1\uff0c\u5e74\u7fb9\u5c27\u5927\u5403 \u4e00\u60ca\uff0c\u4e86\u56e0\u7b11\u9053\uff1a\u201c\u8fd9\u5f20\u5e8a\u4e5f\u9020\u5f97\u597d\u7cbe\u5de7\u3002\u6211\u771f\u60f3\u5728\u8fd9\u91cc\u7761\u4e00\u89c9\u3002\u201d\u5e74\u7fb9\u5c27\u672c\u4ee5\u4e3a\u51af\u7433\u5b9a\u88ab \u53d1\u73b0\uff0c\u90a3\u77e5\u4e86\u56e0\u6447\u4e86\u51e0\u6447\uff0c\u5e10\u540e\u4e00\u70b9\u58f0\u606f\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u597d\u751f\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u9b3c\u4e2b\u5934\u600e\u4e48\u8eb2\u85cf\u5f97\u8fd9\u6837\u597d\uff1f\u201d\u4e86\u56e0\u628a\u7985\u6756\u4e00\u987f\uff0c\u5750\u5728\u5e8a \u4e0a\u3002\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6211\u5bb6\u4e2d\u53e6\u6709\u5ba2\u623f\uff0c\u4e0d\u6562\u59d4\u5c48\u5927\u5e08\u4f4f\u5728\u8fd9\u91cc\u3002\u201d\u8bf4\u65f6\u9762\u8272\u5df2\u53d8\u3002\u4e86\u56e0\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u6211\u662f\u4e2a\u91ce\u548c\u5c1a\uff0c\u548c\u4f60\u8bf4\u8bf4\u7b11\u8bdd\uff0c\u4f11\u602a\u4f11\u602a\uff01\u201d\u5e74\u7fb9\u5c27\u5230\u5e95\u662f\u4e09\u519b\u4e3b\u5e05\uff0c\u4e86\u56e0\u4e0d\u6562\u8fc7\u4e8e \u653e\u8086\u3002\u642d\u8baa\u7b11\u9053\uff1a\u201c\u6211\u78b0\u7740\u90a3\u4e2a\u91ce\u4e2b\u5934\u4e86\u3002\u5979\u7684\u6b66\u529f\u6bd4\u524d\u9ad8\u660e\u5f97\u591a\uff0c\u4f60\u8bf4\u5947\u4e5f\u4e0d\u5947\uff01\u201d\u5e74\u7fb9 \u5c27\u53c8\u662f\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u78b0\u5230\u5979\u4e86\uff1f\u201d\u4e86\u56e0\u9053\uff1a\u201c\u8fd8\u6709\u8c01\u78b0\u5230\u5979\u5417\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u8428\u5bb6\u5144 \u5f1f\u5403\u4e86\u5979\u7684\u5927\u4e8f\u3002\u201d\u5f53\u4e0b\u4e24\u4eba\u5404\u628a\u65e5\u95f4\u6240\u9047\u7684\u4e8b\u8bf4\u4e86\u3002\u5e74\u7fb9\u5c27\u9762\u8272\u4e00\u7aef\uff0c\u9053\uff1a\u201c\u65b9\u4eca\u660e\u6349\u4e0d \u5230\uff0c\u8fd9\u4e8b\u53ef\u8981\u544a\u8bc9\u7687\u4e0a\u3002\u201d\u5c55\u5f00\u7eb8\u7b14\uff01\u4f5c\u52bf\u8981\u5199\u594f\u6298\uff0c\u4e86\u56e0\u4e0d\u901a\u6587\u58a8\uff0c\u6700\u4e0d\u8010\u70e6\u770b\u4eba\u8bfb\u4e66\u5199 \u5b57\uff0c\u9053\uff1a\u201c\u597d\u54c7\uff0c\u5c0f\u5e74\uff0c\u4f60\u5199\u594f\u6298\uff0c\u8981\u6211\u5728\u8fd9\u91cc\u4f8d\u5019\u4f60\u5417\uff1f\u201d\u5e74\u7fb9\u5c27\u5df4\u4e0d\u5f97\u4ed6\u8fd9\u6837\u8bf4\uff0c\u5fd9 \u9053\uff1a\u201c\u6211\u5e26\u4f60\u5230\u90a3\u91cc\u5927\u5c4b\u53bb\u3002\u53eb\u4e24\u4e2a\u6b4c\u5993\u597d\u597d\u966a\u4f60\u3002\u201d\u5e74\u5bb6\u662f\u6cb3\u5357\u9996\u5bcc\uff0c\u5bb6\u4e2d\u6b4c\u5993\uff0c\u9887\u6709\u540d \u6c14\uff0c\u4e86\u56e0\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u8fd8\u591f\u670b\u53cb\u3002\u201d\u5e74\u7fb9\u5c27\u5c06\u4e86\u56e0\u5e26\u51fa\u8352\u56ed\uff0c\u628a\u7ba1\u5bb6\u53eb\u6765\uff0c\u5429\u5490\u4ed6\u597d\u597d \u62db\u5f85\u4e86\u56e0\u3002\u7136\u540e\uff0c\u53c8\u4e00\u4e2a\u4eba\u56de\u5230\u4e66\u623f\u3002 \u3000\u3000\u4e66\u623f\u4e2d\u706f\u5149\u6447\u66f3\u4e0d\u5b9a\uff0c\u5e74\u7fb9\u5c27\u6084\u6084\u63a8\u5f00\u623f\u95e8\uff0c\u6881\u4e0a\u7a81\u7136\u8df3\u4e0b\u4e00\u4eba\uff0c\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f \u6dd8\u6c14\u548c\u6211\u5f00\u4ec0\u4e48\u73a9\u7b11\u3002\u201d\u8df3\u4e0b\u6765\u7684\u5c11\u5973\u62d4\u5251\u5f85\u523a\uff0c\u89c1\u6765\u7684\u662f\u5e74\u7fb9\u5c27\uff0c\u5ffd\u53c8\u7f29\u624b\u3002\u5e74\u7fb9\u5c27\u9053\uff1a \u201c\u521a\u624d\u6211\u548c\u4e86\u56e0\u8bf4\u7684\u8bdd\u4f60\u90fd\u542c\u89c1\u4e86\u5417\uff1f\u4e86\u56e0\u4e5f\u78b0\u5230\u4f60\u4e86\uff0c\u4f60\u8fd8\u8bf4\u4f60\u4eca\u5929\u672a\u51fa\u8fc7\u8fd9\u4e2a\u623f\u5b50\uff01\u201d \u90a3\u5c11\u5973\u9053\uff1a\u201c\u8c01\u662f\u4e86\u56e0\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u88c5\u4ec0\u4e48\u849c\uff0c\u5b9d\u56fd\u7985\u5e08\u4f60\u90fd\u4e0d\u8ba4\u5f97\u5417\uff1f\u201d\u90a3\u5c11\u5973\u82e5 \u6709\u6240\u609f\uff0c\u9053\uff1a\u201c\u662f\u90a3\u4e2a\u624b\u63d0\u7985\u6756\u53c8\u80d6\u53c8\u51f6\u7684\u548c\u5c1a\u5417\uff1f\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u771f\u4f1a\u8bf4\u7b11\uff0c\u4f60\u8fd9\u8bdd\u82e5\u6559\u4e86\u56e0\u95fb\u77e5\uff0c\u6015\u4e0d\u628a\u4ed6\u6c14\u6b7b\uff01\u73b0\u5728\u4f60\u8ba4\u4e86 \u5427\uff1f\u4f60\u662f\u4e0d\u662f\u4eca\u65e5\u5148\u78b0\u89c1\u4e86\u56e0\uff0c\u540e\u6765\u624d\u78b0\u5230\u53cc\u9b54\u7684\uff1f\u201d\u5e74\u7fb9\u5c27\u521a\u624d\u5728\u623f\u4e2d\u548c\u4e86\u56e0\u5404\u8bf4\u4eca\u65e5\u4e4b \u4e8b\uff0c\u539f\u610f\u5c31\u662f\u60f3\u53eb\u51af\u7433\u542c\u89c1\uff0c\u770b\u770b\u5979\u7684\u53cd\u5e94\u5982\u4f55\uff1f\u90a3\u5c11\u5973\u4f3c\u4e4e\u9887\u4e3a\u7591\u60d1\uff0c\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u90a3 \u4e2a\u80d6\u548c\u5c1a\u5c31\u662f\u4e86\u56e0\uff1f\u6211\u975e\u4f46\u78b0\u89c1\u4ed6\uff0c\u8fd8\u548c\u4ed6\u4ea4\u624b\u6765\u4e86\uff0c\u600e\u4e48\u6837\uff1f\u201d\u5e74\u7fb9\u5c27\u5927\u559c\u9053\uff1a\u201c\u4f60\u5230\u5e95 \u8bf4\u771f\u8bdd\u4e86\u3002\u90a3\u4e48\u6211\u4e5f\u544a\u8bc9\u4f60\u771f\u8bdd\u5427\uff0c\u4f60\u7684\u98de\u5200\u4e0d\u662f\u97e9\u91cd\u5c71\u6559\u7684\uff0c\u662f\u949f\u4e07\u5802\u6559\u7684\uff0c\u6211\u548c\u4f60\u867d\u975e \u5144\u59b9\uff0c\u548c\u5144\u59b9\u4e5f\u5dee\u4e0d\u591a\u3002\u201d\u8bf4\u7740\uff0c\u7528\u624b\u62c9\u90a3\u5c11\u5973\uff0c\u4e0d\u6599\u90a3\u5c11\u5973\u8863\u8896\u4e00\u6325\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u6253 \u5728\u5e74\u7fb9\u5c27\u8138\u4e0a\u3002\u65a5\u9053\uff1a\u201c\u563b\u76ae\u7b11\u8138\uff0c\u52a8\u624b\u52a8\u811a\uff0c\u60f3\u627e\u6b7b\u5417\uff1f\u201d\u5e74\u7fb9\u5c27\u6115\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48 \u5566\uff1f\u88c5\u51fa\u8fd9\u4e2a\u51f6\u6837\u7ed9\u8c01\u770b\u5440\uff1f\u201d\u90a3\u5c11\u5973\u5b9d\u5251\u4e00\u6643\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u662f\u5e74\u7fb9\u5c27\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u5e74\u7fb9\u5c27 \u9053\uff1a\u201c\u6211\u7684\u597d\u59d1\u5a18\uff0c\u8fd9\u91cc\u53ef\u4e0d\u662f\u620f\u53f0\u5440\uff01\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u4eca\u65e5\u66fe\u903c\u90a3\u4e2a\u9b54\u5934\u7ed9\u6211\u89e3\u836f\uff0c\u5ff5 \u5728\u8fd9\u70b9\u60c5\u9762\uff0c\u53ef\u4ee5\u9976\u4f60\u4e0d\u6b7b\uff01\u5085\u9752\u4e3b\u7684\u4e66\u5462\uff1f\u5feb\u62ff\u6765\u7ed9\u6211\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4ec0\u4e48\u4e66\u5440\uff1f\u201d\u90a3 \u5c11\u5973\u9053\uff1a\u201c\u4f60\u88c5\u4ec0\u4e48\u849c\uff1f\u4e0d\u662f\u4f60\u628a\u4e66\u641c\u53bb\u4e86\uff0c\u4e3a\u4ec0\u4e48\u6211\u904d\u627e\u4e0d\u89c1\u3002\u201d\u5e74\u7fb9\u5c27\u5fc3\u5ff5\u4e00\u52a8\uff0c\u9053\uff1a \u201c\u5c31\u662f\u4f60\u521a\u624d\u770b\u7684\u90a3\u672c\u4e66\u5417\uff1f\u54c8\uff0c\u54c8\uff0c\u54fc\uff01\u201d\u5fc3\u9053\uff1a\u201c\u539f\u6765\u6211\u5e08\u7956\u7684\u62f3\u7ecf\u5251\u8bc0\u662f\u4f60\u62ff\u53bb\u4e86\uff0c \u4f60\u7684\u6765\u5386\u6211\u4e5f\u5168\u660e\u767d\u4e86\uff0c\u5374\u5c06\u6211\u620f\u800d\u3002\u201d\u4e00\u624b\u6293\u53bb\uff0c\u90a3\u5c11\u5973\u6a2a\u8098\u4e00\u649e\uff0c\u4e00\u4e2a\u8098\u6376\uff0c\u628a\u5e74\u7fb9\u5c27 \u649e\u5f97\u5012\u9000\u4e24\u6b65\uff0c\u5237\u7684\u4e00\u5251\u523a\u53bb\uff0c\u5e74\u7fb9\u5c27\u864e\u543c\u4e00\u58f0\uff0c\u987a\u624b\u628a\u4e00\u5f20\u6905\u5b50\u7aef\u8d77\uff0c\u8fce\u7740\u5b9d\u5251\u4e00\u6321\uff0c\u90a3 \u5c11\u5973\u5251\u62db\u4e00\u53d1\u5ffd\u6536\uff0c\u9053\uff1a\u201c\u6069\u6028\u5206\u660e\uff0c\u6211\u4e0d\u6740\u4f60\uff01\u201d\u811a\u5c16\u4e00\u70b9\uff0c\u7a7f\u7a97\u98de\u51fa\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u5927\u6012\uff0c\u51b2\u51fa\u4e66\u623f\uff0c\u5c06\u5979\u62e6\u622a\uff0c\u90a3\u5c11\u5973\u5bf9\u56ed\u4e2d\u8def\u5f84\u4e0d\u719f\uff0c\u7ed9\u4ed6\u6284\u5c0f\u8def\u622a\u7740\u3002\u5e74\u7fb9\u5c27 \u62d4\u51fa\u5141\u796f\u6240\u8d50\u7684\u5c1a\u65b9\u5b9d\u5251\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4e2b\u5934\u65e0\u60c5\u65e0\u4e49\uff0c\u4f60\u662f\u542c\u8c01\u7684\u5dee\u9063\uff0c\u5230\u8fd9\u91cc\u5367\u5e95\u6765 \u4e86\u3002\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u54c8\uff0c\u6211\u9976\u4e86\u4f60\uff0c\u4f60\u8fd8\u4e0d\u8ba9\u6211\u8d70\uff01\u201d\u5251\u8bc0\u4e00\u9886\uff0c\u4e00\u62db\u201c\u6625\u98ce\u62c2\u67f3\u201d\uff0c\u5411\u5e74 \u7fb9\u5c27\u80f8\u53e3\u4fbf\u523a\uff01 \u3000\u3000\u8fd9\u5c11\u5973\u4e0d\u662f\u51af\u7433\uff0c\u5374\u662f\u51af\u745b\uff0c\u5979\u4e0b\u5c71\u4e4b\u65f6\uff0c\u6613\u5170\u73e0\u5429\u5490\u5979\u8d81\u4e0a\u9099\u5c71\u4e4b\u4fbf\uff0c\u53ef\u5230\u9648\u7559\u5e74\u5bb6 \u641c\u5bfb\u5085\u9752\u4e3b\u7684\u9057\u4e66\u3002\u5979\u4e0d\u77e5\u65e5\u95f4\u78b0\u5230\u7684\u5c11\u5e74\u5c06\u519b\u4fbf\u662f\u5e74\u7fb9\u5c27\uff0c\u9003\u8131\u4e4b\u540e\uff0c\u517b\u597d\u7cbe\u795e\uff0c\u5230\u4e86\u665a \u95f4\uff0c\u4fbf\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u5077\u5077\u6765\u5230\u5e74\u5bb6\uff0c\u6478\u8fdb\u8352\u56ed\u3002\u5979\u8fdb\u6765\u65f6\uff0c\u6070\u503c\u5e74\u7fb9\u5c27\u9001\u4e86\u56e0\u51fa\u6765\uff0c\u6240\u4ee5 \u53cc\u65b9\u90fd\u6ca1\u53d1\u73b0\u3002 \u3000\u3000\u51af\u745b\u8fdb\u4e86\u623f\u5b50\uff0c\u89c1\u706f\u82b1\u5410\u8273\uff0c\u9526\u5e44\u72b9\u6e29\uff0c\u5206\u660e\u662f\u5973\u5b69\u5b50\u7684\u95fa\u623f\uff0c\u800c\u4e14\u623f\u95f4\u7684\u4e3b\u4eba\u597d\u50cf\u662f \u521a\u8d70\u672a\u4e45\u3002\u5927\u4e3a\u5947\u602a\u3002\u56db\u5904\u641c\u7d22\uff0c\u90fd\u627e\u4e0d\u5230\u5e08\u5085\u6240\u8bf4\u7684\u90a3\u4e00\u672c\u4e66\uff0c\u641c\u6765\u641c\u53bb\uff0c\u641c\u5230\u5e10\u540e\uff0c\u5076 \u7136\u89e6\u52a8\u673a\u5173\uff0c\u5899\u4e0a\u4e00\u9053\u6697\u95e8\u500f\u7136\u6253\u5f00\u3002\u51af\u745b\u5fc3\u60f3\uff1a\u8fd9\u623f\u95f4\u91cc\u53e4\u602a\u771f\u591a\u3002\u8d70\u8fdb\u6697\u95e8\uff0c\u4f46\u89c9\u7f15\u7f15 \u5e7d\u9999\uff0c\u76f4\u6251\u9f3b\u7aef\uff0c\u597d\u4f3c\u6709\u4eba\u5728\u8fd9\u91cc\u85cf\u8fc7\uff0c\u51af\u745b\u6253\u4eae\u706b\u77f3\uff0c\u5728\u5939\u5899\u91cc\u7ec6\u7ec6\u641c\u7d22\uff0c\u4ecd\u662f\u627e\u4e0d\u5230\u90a3 \u672c\u4e66\uff0c\u4f46\u5374\u53d1\u73b0\u90a3\u53e6\u4e00\u7aef\u7684\u6697\u95e8\uff0c\u53ef\u4ee5\u901a\u5230\u5916\u9762\u3002 \u3000\u3000\u539f\u6765\u8fd9\u590d\u58c1\u6697\u95e8\uff0c\u4e43\u662f\u949f\u4e07\u5802\u5f53\u65e5\u4e3a\u8eb2\u907f\u4ec7\u5bb6\uff0c\u6697\u4e2d\u8425\u9020\u7684\u3002\u540e\u6765\u88ab\u51af\u7433\u53d1\u73b0\uff0c\u5e74\u7fb9\u5c27 \u5374\u4e0d\u77e5\u9053\uff0c\u5f53\u4e86\u56e0\u8fdb\u6765\u4e4b\u65f6\uff0c\u51af\u7433\u5df2\u6084\u6084\u7684\u4ece\u6697\u95e8\u6e9c\u8d70\u3002 \u3000\u3000\u51af\u745b\u5728\u590d\u58c1\u4e2d\u641c\u4e0d\u5230\u5085\u9752\u4e3b\u9057\u4e66\uff0c\u56de\u5230\u623f\u95f4\uff0c\u6070\u5de7\u78b0\u7740\u5e74\u7fb9\u5c27\u56de\u6765\u3002\u51af\u745b\u65e9\u542c\u5f97\u5e08\u5085\u8bf4 \u8fc7\u5e74\u7fb9\u5c27\u662f\u4e2a\u53db\u56fd\u67ad\u96c4\uff0c\u672c\u5f85\u5c06\u4ed6\u4e00\u5251\u523a\u6b7b\uff0c\u65e0\u5948\u4ed6\u65e5\u95f4\u6551\u8fc7\u81ea\u5df1\u6027\u547d\uff0c\u6240\u4ee5\u624b\u4e0b\u7559\u60c5\uff0c\u53ea \u56fe\u9003\u8131\u4fbf\u7b97\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u5374\u8bef\u4f1a\u5979\u662f\u51af\u7433\uff0c\u4e0d\u77e5\u597d\u6b79\uff0c\u4ecd\u7136\u7d27\u7d27\u8ffd\u6765\u3002\u51af\u745b\u7ed9\u4ed6\u9017\u5f97\u53d1\u4e86\u813e\u6c14\uff0c\u5c55\u51fa\u7edd\u5999 \u7684\u5929\u5c71\u5251\u6cd5\uff0c\u628a\u5e74\u7fb9\u5c27\u6740\u5f97\u624b\u5fd9\u811a\u4e71\uff01 \u3000\u3000\u4f46\u5e74\u7fb9\u5c27\u81ea\u5e7c\u5f97\u949f\u4e07\u5802\u4f20\u6388\uff0c\u53c8\u5f97\u5c11\u6797\u4e09\u8001\u7684\u6307\u70b9\uff0c\u6b66\u529f\u4ea6\u975e\u6cdb\u6cdb\uff0c\u867d\u7136\u4e0d\u662f\u51af\u745b\u5bf9 \u624b\uff0c\u4f46\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u4ecd\u7136\u672a\u66fe\u843d\u8d25\u3002 \u3000\u3000\u51af\u745b\u4e0d\u613f\u4e45\u6218\uff0c\u8fde\u8fdb\u51e0\u62db\uff0c\u628a\u5e74\u7fb9\u5c27\u903c\u9000\u4e4b\u540e\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002\u5e74\u7fb9\u5c27\u5bf9\u51af\u7433\u867d\u7136\u6709\u610f\uff0c\u4f46 \u6b64\u65f6\u6b64\u9645\uff0c\u89c1\u51af\u745b\u5bf9\u4ed6\u8fde\u4e0b\u6740\u624b\uff0c\u5df2\u8d77\u4e86\u7edd\u5927\u7684\u7591\u5fc3\uff0c\u6000\u7591\u5979\u82e5\u4e0d\u662f\u5141\u796f\u6d3e\u6765\u8bd5\u63a2\u81ea\u5df1\uff0c\u4fbf \u662f\u5df2\u548c\u81ea\u5df1\u7684\u4ec7\u5bb6\u7ed3\u6210\u4e00\u8def\uff0c\u4e8c\u8005\u5fc5\u5c45\u5176\u4e00\u3002\u56e0\u6b64\u5fc5\u6b32\u5f97\u800c\u7518\u5fc3\uff0c\u6043\u7740\u81ea\u5df1\u719f\u6089\u56ed\u4e2d\u9053\u8def\uff0c \u4e00\u8def\u62e6\u622a\u548c\u5979\u6e38\u6597\u3002\u62bd\u7a7a\u8fd8\u53d1\u51fa\u4e24\u679d\u544a\u6025\u7684\u54cd\u7bad\uff0c\u60f3\u628a\u53cc\u9b54\u62db\u6765\uff01 \u3000\u3000\u82b1\u56ed\u751a\u4e3a\u5e7f\u9614\uff0c\u4e24\u4eba\u4e00\u8def\u6e38\u6597\uff0c\u7ffb\u8fc7\u4e86\u51e0\u5ea7\u5047\u5c71\uff0c\u76f4\u6253\u5230\u4e86\u82b1\u56ed\u7684\u897f\u5317\u89d2\u4e0a\u3002\u5c31\u5728\u8fd9 \u65f6\uff0c\u5ffd\u542c\u5f97\u4e1c\u8fb9\u4e5f\u4f20\u51fa\u4e86\u53f1\u54a4\u53ae\u6740\u4e4b\u58f0\uff0c\u5e74\u7fb9\u5c27\u4e00\u6014\uff0c\u7ee7\u800c\u4e00\u60f3\uff0c\u83ab\u975e\u662f\u81ea\u5df1\u624b\u4e0b\u7684\u6b66\u58eb\uff0c \u53d1\u73b0\u4e86\u5979\u7684\u540c\u515a\uff0c\u4e5f\u6253\u8d77\u6765\u4e86\u3002\u4e8e\u662f\u8d8a\u53d1\u4e0d\u80af\u7f62\u624b\uff0c\u4e00\u9762\u7d27\u7d27\u7f20\u6597\uff0c\u4e00\u9762\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u6765\u4eba \u5440\uff01\u201d \u3000\u3000\u51af\u745b\u5927\u6012\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e0d\u77e5\u597d\u6b79\uff01\u201d\u65ad\u7389\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u4e00\u62db\u201c\u5927\u6f20\u6d41\u6c99\u201d\uff0c\u9aa4\u4e0b\u6740 \u624b\u3002\u5251\u5149\u95ea\u70c1\uff0c\u6c99\u98de\u98ce\u8d77\uff0c\u5e74\u7fb9\u5c27\u594b\u529b\u6321\u4e86\u4e00\u5251\uff0c\u53ea\u542c\u5f97\u201c\u548b\u5693\u201d\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u81ea\u5df1\u7684 \u5c1a\u65b9\u5b9d\u5251\uff0c\u7adf\u7136\u7f3a\u4e86\u4e00\u53e3\u3002\u51af\u745b\u8d81\u4ed6\u5251\u6cd5\u614c\u4e71\uff0c\u53c8\u662f\u4e00\u62db\u201c\u5927\u6d77\u626c\u6ce2\u201d\uff0c\u5251\u5149\u98de\u6d12\u4e2d\uff0c\u5e74\u7fb9 \u5c27\u80a9\u5934\u4e2d\u4e86\u4e00\u5251\uff0c\u5ffd\u89c1\u4e00\u6761\u9ed1\u5f71\u5982\u98de\u6251\u6765\uff0c\u5e74\u7fb9\u5c27\u53eb\u9053\uff1a\u201c\u5feb\u628a\u90a3\u91ce\u4e2b\u5934\u62ff\u4f4f\uff01\u201d\u51af\u745b\u5df2\u8dc3 \u4e0a\u5899\u5934\uff0c\u8df3\u51fa\u56ed\u5916\u3002 \u3000\u3000\u6765\u7684\u662f\u4e2a\u957f\u8eab\u7389\u7acb\u7684\u5c11\u5e74\uff0c\u51b7\u7b11\u95ee\u9053\uff1a\u201c\u90a3\u4e2a\u91ce\u4e2b\u5934\u5462\uff1f\u201d\u5e74\u7fb9\u5c27\u628a\u624b\u671d\u5916\u4e00\u6307\uff0c\u6de1\u6708 \u758f\u661f\u4e4b\u4e0b\uff0c\u84e6\u7136\u53d1\u73b0\u8fd9\u4eba\u4e0d\u662f\u81ea\u5df1\u5e10\u4e0b\u7684\u6b66\u58eb\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u4eba\u5df2\u5530\u7684\u4e00\u5251\u523a\u6765\uff0c\u5e74 \u7fb9\u5c27\u5927\u5403\u4e00\u60ca\uff0c\u80a9\u5934\u4e00\u7f29\uff0c\u4f7f\u51fa\u65e0\u6781\u5251\u6cd5\u4e2d\u7684\u201c\u4e00\u7fbd\u5343\u94a9\u201d\u62db\u6570\uff0c\u610f\u56fe\u4ee5\u67d4\u514b\u521a\uff0c\u8fde\u6d88\u5e26 \u6253\uff0c\u90a3\u6599\u654c\u4eba\u5251\u6cd5\u5947\u8be1\u8f9b\u8fa3\uff0c\u5251\u52bf\u660e\u660e\u5411\u5de6\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5374\u500f\u7136\u5411\u53f3\uff0c\u5e74\u7fb9\u5c27\u98de\u8eab\u95ea\u8dc3\u4e2d\uff0c \u9661\u89c9\u9876\u5fc3\u4e00\u60ca\uff0c\u5934\u53d1\u5df2\u88ab\u5272\u65ad\u4e00\u675f\uff01 \u3000\u3000\u8fd9\u4eba\u6b63\u662f\u674e\u6cbb\u3002\u4ed6\u81ea\u4ece\u90a3\u65e5\u5f97\u6b66\u6210\u5316\u6551\u9192\u4e4b\u540e\uff0c\u5bf9\u51af\u7433\u5728\u5371\u6025\u4e4b\u65f6\u820d\u4ed6\u800c\u53bb\uff0c\u751a\u4e3a\u4e0d \u89e3\u3002\u89c1\u5979\u7559\u4e0b\u89e3\u836f\uff0c\u5fc3\u4e2d\u59cb\u7a0d\u7a0d\u5bbd\u6170\u3002\u60f3\u9053\uff1a\u201c\u745b\u59b9\u201d\u4e0b\u5c71\u4e4b\u540e\uff0c\u6027\u60c5\u867d\u7136\u5927\u53d8\uff0c\u5374\u8fd8\u4e0d\u662f \u5be1\u60c5\u7edd\u4e49\u4e4b\u4eba\uff0c\u770b\u5979\u7559\u4e0b\u89e3\u836f\uff0c\u4fbf\u53ef\u77e5\u9053\u3002\u56e0\u6b64\uff0c\u5bfb\u5979\u4e4b\u5ff5\u8d8a\u6025\u3002 \u3000\u3000\u6b66\u6210\u5316\u867d\u7136\u662f\u4ed6\u8205\u8205\uff0c\u4f46\u56e0\u5e76\u4e0d\u4f4f\u5728\u5929\u5c71\uff0c\u4e0e\u51af\u745b\u89c1\u9762\u4e0d\u591a\uff0c\u56e0\u6b64\u5bf9\u5979\u7684\u6027\u683c\uff0c\u4e5f\u5c31\u4e0d \u5f88\u719f\u6089\u3002\u542c\u7525\u513f\u8bed\u6c14\uff0c\u663e\u7136\u5bf9\u51af\u745b\u60c5\u6709\u6240\u949f\uff0c\u53f9\u6c14\u529d\u674e\u6cbb\u9053\uff1a\u201c\u90a3\u5973\u5a03\u513f\u867d\u7136\u662f\u6613\u5973\u4fa0\u7684\u5fc3 \u7231\u5f92\u513f\uff0c\u9762\u8c8c\u6b66\u529f\u90fd\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u4f46\u5bdf\u5979\u4eba\u54c1\uff0c\u5374\u4e0d\u6562\u606d\u7ef4\u3002\u5979\u867d\u7136\u7ed9\u4f60\u7559\u4e0b\u89e3\u836f\uff0c\u4f46\u5728\u5371 \u9669\u4e4b\u4e2d\uff0c\u820d\u5f03\u81f3\u4ea4\u597d\u53cb\uff0c\u90a3\u5374\u662f\u6b66\u6797\u4e2d\u6700\u4e0d\u9f7f\u4e4b\u4e8b\u3002\u201d\u60f3\u4e86\u4e00\u60f3\u53c8\u9053\uff1a\u201c\u4f55\u51b5\u548c\u4f60\u540c\u884c\u7684\u90a3 \u4e2a\u5973\u5b69\u5b50\uff0c\u662f\u5426\u51af\u745b\uff0c\u5c1a\u672a\u53ef\u77e5\u3002\u770b\u5979\u7559\u4e0b\u7684\u89e3\u836f\uff0c\u540d\u8d35\u4e4b\u6781\uff0c\u5374\u975e\u5929\u5c71\u6240\u4ea7\u3002\u201d\u674e\u6cbb\u7b11 \u9053\uff1a\u201c\u9664\u975e\u4e16\u4e0a\u6709\u4e24\u4e2a\u51af\u745b\uff0c\u8981\u4e0d\u7136\u5c31\u5b9a\u4e0d\u4f1a\u9519\uff01\u201d\u53c8\u9053\uff1a\u201c\u8205\u8205\u4ee5\u5927\u4e49\u8d23\u5979\uff0c\u9053\u7406\u5f53\u7136\u4e0d \u9519\u3002\u53ef\u662f\u5979\u5230\u5e95\u8fd8\u662f\u4e2a\u5b69\u5b50\u5462\uff01\u5fc3\u6027\u672a\u5b9a\uff0c\u6613\u4e3a\u5916\u7269\u6240\u8bf1\uff0c\u8205\u8205\u4f60\u662f\u5979\u7684\u957f\u8f88\uff0c\u6559\u8bf2\u4e4b\u8d23\uff0c \u4f60\u4e5f\u5e94\u5f53\u62c5\u8d1f\u3002\u201d\u6b66\u6210\u5316\u89c1\u7525\u513f\u6781\u529b\u4e3a\u5979\u8fa9\u89e3\uff0c\u4e5f\u5c31\u4e0d\u518d\u63d0\u4e86\u3002 \u3000\u3000\u8fd9\u65e5\u4ed6\u4eec\u9002\u5de7\u6765\u5230\u9648\u7559\u3002\u6b66\u6210\u5316\u60f3\u8d77\uff1a\u65e0\u6781\u6d3e\u7684\u5ae1\u7cfb\u4f20\u4eba\uff0c\u5085\u9752\u4e3b\u7684\u5f92\u5b59\u949f\u4e07\u5802\u66fe\u5728\u5e74 \u5bb6\u6559\u4e66\uff0c\u542c\u6613\u5170\u73e0\u8bf4\u949f\u4e07\u5802\u7684\u9057\u9aa8\u4fbf\u57cb\u5728\u56ed\u4e2d\u3002\u6211\u548c\u949f\u4e07\u5802\u867d\u7136\u672a\u89c1\u8fc7\u9762\uff0c\u4f46\u6211\u66fe\u5f97\u5085\u9752\u4e3b \u4f20\u8fc7\u51e0\u624b\u7edd\u6280\uff0c\u548c\u65e0\u6781\u6d3e\u751a\u6709\u6e0a\u6e90\uff0c\u65e2\u5230\u6b64\u5730\uff0c\u7406\u5e94\u5f53\u796d\u626b\u4ed6\u7684\u575f\u5893\u3002\u4e8e\u662f\u548c\u674e\u6cbb\u8d81\u7740\u591c\u6df1 \u65f6\u5206\uff0c\u4e5f\u6084\u6084\u6e9c\u5165\u8352\u56ed\u3002 \u3000\u3000\u82b1\u56ed\u5f53\u4e2d\uff0c\u4e66\u623f\u91cc\u7684\u706f\u5149\u900f\u51fa\u78a7\u7eb1\u7a97\u5916\uff0c\u6b66\u6210\u5316\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u58c1\u6709\u4eba\uff01\u201d\u674e\u6cbb\u9053\uff1a\u201c\u8fd9 \u4e2a\u82b1\u56ed\u4e5f\u9020\u5f97\u602a\uff0c\u5b64\u96f6\u96f6\u7684\u53ea\u6709\u8fd9\u4e48\u4e00\u95f4\u623f\u5b50\u3002\u201d\u6b66\u6210\u5316\u9053\uff1a\u201c\u6211\u542c\u5f97\u6613\u5973\u4fa0\u8bf4\u8fc7\uff0c\u949f\u4e07\u5802 \u6b7b\u540e\uff0c\u5979\u6253\u542c\u5f97\u5e74\u5bb6\u5df2\u628a\u8fd9\u82b1\u56ed\u5c01\u4e86\u3002\u600e\u4e48\u53c8\u6709\u4eba\u4f4f\u5728\u8fd9\u91cc\uff1f\u201d\u4e24\u8205\u7525\u8e51\u624b\u8e51\u811a\uff0c\u8f7b\u8f7b\u7684\u8dd1 \u8fc7\u53bb\u770b\u3002\u5ffd\u542c\u5f97\u4e66\u623f\u4e2d\u54c8\u54c8\u72c2\u7b11\uff0c\u6709\u58f0\u559d\u9053\uff1a\u54c8\u54c8\uff0c\u5c0f\u5e74\uff0c\u4f60\u56de\u6765\u4e86\u5417\uff1f\u4f60\u505a\u5f97\u597d\u4e8b\u5440\uff01\u201d \u95e8\u5f00\u5904\u4e00\u4e2a\u80d6\u548c\u5c1a\u8df3\u4e86\u51fa\u6765\uff0c\u5374\u662f\u4e86\u56e0\u3002 \u3000\u3000\u539f\u6765\u4e86\u56e0\u6b64\u4eba\uff0c\u867d\u7136\u4e00\u5411\u83bd\u649e\uff0c\u6709\u52c7\u65e0\u8c0b\uff0c\u8fd9\u56de\u5374\u662f\u7c97\u4e2d\u6709\u7ec6\u3002\u5e74\u7fb9\u5c27\u628a\u4ed6\u5e26\u51fa\u56ed\u5b50\uff0c \u4ea4\u7ed9\u7ba1\u5bb6\u62db\u5f85\u4e4b\u540e\u3002\u4ed6\u5ffd\u7136\u60f3\u8d77\u4eca\u665a\u4e4b\u4e8b\uff0c\u7591\u70b9\u751a\u591a\u3002\u6309\u8bf4\u82e5\u7136\u662f\u81ea\u5c0f\u7ed9\u4eba\u5f53\u5973\u513f\u517b\u5927\uff0c\u6210 \u4eba\u4e4b\u540e\uff0c\u5fc5\u7136\u591a\u5c11\u5e26\u70b9\u5973\u5b69\u513f\u5bb6\u7684\u6c14\u5473\u3002\u4f46\u5e74\u7fb9\u5c27\u6c14\u5b87\u8f69\u6602\uff0c\u505a\u4e8b\u51b3\u65ad\u3002\u4f55\u66fe\u6709\u534a\u70b9\u5973\u5b69\u5b50 \u6c14\uff1f \u3000\u3000\u4e86\u56e0\u5fc3\u6709\u6240\u7591\uff0c\u987e\u4e0d\u5f97\u8d2a\u6b22\u6155\u4e50\u3002\u7ba1\u5bb6\u5c06\u4ed6\u5e26\u8fdb\u4e00\u95f4\u534e\u4e3d\u7684\u5ba2\u623f\uff0c\u7b11\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4f60\u7a0d \u6b47\u4f1a\u513f\uff0c\u6211\u53eb\u4e24\u4e2a\u6b4c\u5993\u966a\u4f60\u3002\u201d\u4e86\u56e0\u5ffd\u9053\uff1a\u201c\u4e14\u6162\u3002\u201d\u7ba1\u5bb6\u505c\u4e86\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u5927\u5e08\u6709\u4f55\u5429 \u5490\uff1f\u201d\u4e86\u56e0\u9053\uff1a\u201c\u4f60\u5bb6\u7684\u5c0f\u4e3b\u4eba\u662f\u72ec\u5b50\uff0c\u81ea\u5c0f\u4e00\u5b9a\u5f88\u53d7\u53cc\u4eb2\u5ba0\u7231\u7684\u4e86\uff1f\u201d\u7ba1\u5bb6\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a \u81ea\u7136\u3002\u201d\u5fc3\u4e2d\u6697\u9053\uff1a\u8fd9\u548c\u5c1a\u4f55\u4ee5\u4f1a\u95ee\u8fd9\u4e9b\u8bdd\uff1f\u4e86\u56e0\u9053\uff1a\u201c\u4f60\u4eec\u4e61\u4e0b\u7684\u98ce\u4fd7\uff0c\u72ec\u751f\u513f\u5b50\uff0c\u591a\u534a \u88ab\u7236\u6bcd\u4f5c\u4e3a\u5973\u513f\u987e\u517b\uff0c\u662f\u5417\uff1f\u201d\u7ba1\u5bb6\u7b11\u9053\uff1a\u201c\u662f\u6709\u8fd9\u6837\u7684\u98ce\u4fd7\uff0c\u53ef\u662f\u6211\u5bb6\u7684\u5c0f\u4e3b\u4eba\u5374\u4e0e\u4f17\u4e0d \u540c\uff0c\u4ed6\u81ea\u5c0f\u987d\u53cd\u5230\u6781\uff0c\u6700\u7231\u548c\u4eba\u6253\u67b6\u3002\u4f60\u60f3\uff0c\u4ed6\u600e\u80af\u7ed9\u7236\u6bcd\u5f53\u5973\u513f\u6253\u626e\uff1f\u201d\u4e86\u56e0\u542c\u4e86\uff0c\u534a\u664c \u4e0d\u8bed\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u8981\u6b4c\u5993\u6765\u966a\u4e86\uff0c\u65e5\u95f4\u6765\u7684\u90a3\u4e24\u4f4d\u5f62\u5bb9\u53e4\u602a\u7684\u8001\u4eba\uff0c\u8fd8\u5728\u5e9c \u4e0a\u5417\uff1f\u201d\u7ba1\u5bb6\u56de\u9053\uff1a\u201c\u5728\u3002\u201d\u4e86\u56e0\u9053\uff1a\u201c\u6211\u60f3\u548c\u4ed6\u4eec\u4e00\u89c1\u3002\u70e6\u4f60\u5bf9\u4ed6\u4eec\u8bf4\uff0c\u5b9d\u56fd\u7985\u5e08\u6709\u8bf7\u3002\u201d \u3000\u3000\u7ba1\u5bb6\u77e5\u9053\u4ed6\u4eec\u4e43\u662f\u540c\u4f34\uff0c\u6beb\u4e0d\u7591\u5fc3\uff0c\u679c\u7136\u53bb\u8bf7\u53cc\u9b54\u3002\u5fc3\u4e2d\u8fd8\u60f3\uff1a\u4e3b\u4eba\u8bf4\u8fd9\u4e2a\u548c\u5c1a\u8d2a\u9152\u597d \u8272\uff0c\u60f3\u6765\u662f\u5938\u5927\u5176\u8f9e\u7684\u4e86\u3002 \u3000\u3000\u53cc\u9b54\u672a\u7761\uff0c\u542c\u8bf4\u4e86\u56e0\u5728\u6b64\uff0c\u6025\u5fd9\u6765\u89c1\u3002\u4e86\u56e0\u5c4f\u9000\u7ba1\u5bb6\uff0c\u5bf9\u53cc\u9b54\u9053\uff1a\u201c\u4e3b\u516c\uff08\u6307\u5141\u796f\uff09\u53eb \u6211\u4eec\u76d1\u89c6\u5c0f\u5e74\uff0c\u6211\u4eec\u90fd\u4ee5\u4e3a\u5c0f\u5e74\u5fe0\u5fc3\u53ef\u9760\u3002\u6b8a\u4e0d\u77e5\u6211\u4eca\u665a\u5374\u53d1\u73b0\u4ed6\u6709\u6b3a\u541b\u7f54\u4e0a\u4e4b\u4e8b\u3002\u201d\u53cc\u9b54 \u60ca\u95ee\u6240\u4ee5\uff0c\u4e86\u56e0\u5c06\u5e74\u7fb9\u5c27\u9a97\u4ed6\u7684\u4e8b\u8bf4\u4e86\u3002\u8bf4\u9053\uff1a\u201c\u4ed6\u7684\u623f\u4e2d\u5b9a\u6709\u53e4\u602a\u3002\u6211\u4eec\u518d\u53bb\u641c\u4ed6\u4e00\u641c\u5982 \u4f55\uff1f\u201d\u4e86\u56e0\u4ee5\u4e3a\u53cc\u9b54\u5fc5\u7136\u7b54\u5e94\uff0c\u5374\u4e0d\u6599\u53cc\u9b54\u4e24\u4f4d\u5728\u4e86\u56e0\u54c8\u5e03\u9640\u8bf8\u4eba\u4e4b\u4e0b\uff0c\u4e86\u56f0\u53c8\u6781\u50b2\u6162\uff0c\u4ed6 \u4eec\u4e45\u5df2\u4e0d\u7518\u3002\u5e74\u7fb9\u5c27\u770b\u51fa\u4ed6\u4eec\u548c\u4e86\u56e0\u4e4b\u95f4\u9887\u6709\u82a5\u8482\uff0c\u65e9\u5df2\u7528\u6069\u7ed3\u4e86\u4ed6\u4eec\u3002\u53cc\u9b54\u542c\u4e86\u4e86\u56e0\u7684 \u8bdd\uff0c\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u70b9\u5c0f\u4e8b\uff0c\u4e5f\u7b97\u4e0d\u5f97\u662f\u6b3a\u541b\u5440\uff01\u4ed6\u5230\u5e95\u662f\u4e09\u519b\u4e3b\u5e05\uff0c\u5c82\u53ef\u5bf9\u4ed6\u65e0\u793c\u3002\u201d\u4e86 \u56e0\u5927\u6012\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u4eec\u4e0d\u53bb\uff0c\u6211\u82e5\u641c\u51fa\u4ec0\u4e48\u8bc1\u636e\uff0c\u7980\u544a\u7687\u4e0a\u4e4b\u65f6\uff0c\u4f60\u4eec\u4e5f\u6709\u5e72\u8fde\u3002\u201d\u516b \u81c2\u795e\u9b54\u8428\u5929\u523a\u6bd4\u8f83\u8001\u6210\u7ec3\u8fbe\uff0c\u62c9\u4e86\u5f1f\u5f1f\u4e00\u628a\uff0c\u9053\uff1a\u201c\u65e2\u7136\u5b9d\u56fd\u7985\u5e08\u6709\u547d\uff0c\u6211\u4eec\u5c82\u6562\u4e0d\u9075\uff0c\u8bf7 \u7985\u5e08\u5148\u53bb\uff0c\u6211\u4eec\u5144\u5f1f\uff0c\u968f\u540e\u5c31\u6765\u3002\u201d\u8428\u5929\u523a\u7528\u5fc3\u662f\u60f3\u770b\u98ce\u4f7f\u8235\uff0c\u4e86\u56e0\u89c1\u4ed6\u4eec\u65e2\u80af\u7b54\u5e94\uff0c\u4e5f\u5c31 \u4e0d\u4e3a\u5df2\u751a\uff0c\u7531\u5f97\u8ba9\u4ed6\u4eec\u5728\u540e\u9762\u628a\u98ce\u3002 \u3000\u3000\u4e86\u56e0\u518d\u5230\u4e66\u623f\u4e4b\u65f6\uff0c\u6b63\u662f\u5e74\u7fb9\u5c27\u5c06\u51af\u745b\u8ffd\u5230\u56ed\u5b50\u897f\u89d2\u7684\u65f6\u5019\u3002\u82b1\u56ed\u5e7f\u9614\uff0c\u4e09\u4eba\u5e76\u672a\u78b0 \u5934\u3002\u4e86\u56e0\u867d\u9690\u9690\u542c\u5f97\u897f\u5317\u89d2\u4e0a\u6709\u5175\u5200\u78b0\u51fb\u4e4b\u58f0\uff0c\u4f46\u60f3\u8d77\u673a\u4f1a\u7a0d\u7eb5\u5373\u901d\uff0c\u4fbf\u4e00\u5fc3\u641c\u7d22\u3002\u4e00\u641c\u4fbf \u641c\u51fa\u4e86\u51af\u7433\u65e5\u5e38\u6240\u7a7f\u7684\u8863\u670d\uff0c\u5fc3\u4e2d\u51b7\u7b11\uff1a\u539f\u6765\u662f\u5c0f\u5e74\u628a\u8fd9\u91ce\u4e2b\u5934\u6536\u5728\u8fd9\u91cc\u4e0e\u6211\u4eec\u4f5c\u5bf9\uff01\u518d\u7528 \u7985\u6756\u6572\u51fb\u5899\u58c1\uff0c\u53c8\u53d1\u73b0\u4e86\u590d\u58c1\u6697\u95e8\uff0c\u66f4\u662f\u751f\u6c14\u3002\u6b63\u60f3\u51fa\u623f\u53bb\u627e\u5e74\u7fb9\u5c27\uff0c\u5374\u4e0d\u6599\u78b0\u5230\u4e86\u6b66\u6210\u5316\u3002 \u3000\u3000\u4e86\u56e0\u5728\u6b66\u6210\u5316\u624b\u4e0b\u5403\u8fc7\u8d25\u4ed7\uff0c\u4ec7\u4eba\u76f8\u89c1\uff0c\u5206\u5916\u773c\u7ea2\u3002\u63d0\u8d77\u7985\u6756\uff0c\u6402\u5934\u4fbf\u6253\u3002\u6b66\u6210\u5316\u5c55\u5f00 \u864e\u6491\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u8fde\u8fdb\u6570\u62db\uff0c\u548c\u4e86\u56e0\u6253\u5f97\u96be\u5206\u96be\u89e3\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u674e\u6cbb\u542c\u5f97\u5e74\u7fb9\u5c27\u7684\u54cd\u7bad\u4f20 \u58f0\uff0c\u5bfb\u58f0\u89c5\u8ff9\uff0c\u8ffd\u5230\u897f\u5317\u89d2\u4e0a\uff0c\u9065\u89c1\u5e74\u7fb9\u5c27\u548c\u4e00\u4e2a\u5c11\u5973\u52a8\u624b\uff0c\u8d76\u5230\u4e4b\u65f6\u3002\u90a3\u5c11\u5973\u5df2\u8dc3\u8fc7\u5899 \u5934\u3002\u674e\u6cbb\u4f7f\u51fa\u767d\u53d1\u9b54\u5973\u7684\u72ec\u95e8\u5251\u6cd5\uff0c\u4e00\u5251\u5272\u65ad\u5e74\u7fb9\u5c27\u7684\u4e00\u675f\u5934\u53d1\uff0c\u5fd9\u53bb\u8ffd\u8d76\u90a3\u4e2a\u5c11\u5973\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u88ab\u674e\u6cbb\u89c1\u9762\u4e00\u62db\uff0c\u51e0\u4e4e\u4e27\u547d\uff0c\u5927\u4e3a\u5403\u60ca\uff0c\u6309\u8bf4\u674e\u6cbb\u6b66\u529f\u867d\u7136\u5389\u5bb3\uff0c\u4f46\u6bd4\u5e74\u7fb9\u5c27\u4e5f \u9ad8\u4e0d\u4e86\u591a\u5c11\u3002\u4e0d\u8fc7\u767d\u53d1\u9b54\u5973\u6240\u4f20\u7684\u5251\u6cd5\uff0c\u4e0e\u5404\u5bb6\u5251\u6cd5\u76f8\u53cd\uff0c\u5947\u8be1\u51f6\u8fa3\uff0c\u5929\u4e0b\u65e0\u5339\uff0c\u5e74\u7fb9\u5c27\u731d \u4e0d\u53ca\u9632\uff0c\u4ee5\u81f4\u5403\u4e86\u5927\u4e8f\u3002\u5fc3\u4e2d\u51db\u7136\uff0c\u731c\u7591\u662f\u51af\u7433\u9080\u4e86\u9ad8\u624b\u6765\u88ad\u51fb\u81ea\u5df1\uff0c\u6025\u6025\u8d76\u56de\u4e66\u623f\u53bb\u770b\u3002 \u3000\u3000\u8fd9\u65f6\u4e86\u56e0\u6263\u6b66\u6210\u5316\u6253\u5f97\u6b63\u9163\uff0c\u4e24\u4eba\u90fd\u662f\u5185\u529f\u6df1\u539a\uff0c\u795e\u529b\u60ca\u4eba\u3002\u7985\u6756\u4e0e\u864e\u6491\u6bcf\u4e00\u76f8\u4ea4\uff0c\u4fbf \u53d1\u51fa\u4e00\u58f0\u5de8\u54cd\uff01\u6253\u5230\u6025\u65f6\uff0c\u4f46\u542c\u5f97\u547c\u547c\u8f70\u8f70\u4e4b\u58f0\uff0c\u56ed\u4e2d\u82b1\u6728\uff0c\u7ed9\u4e24\u4eba\u5175\u5668\u6253\u5f97\u7a00\u70c2\uff0c\u82b1\u98de\u53f6 \u821e\uff0c\u679d\u65ad\u5e72\u88c2\uff0c\u65b9\u5706\u6570\u4e08\u4e4b\u5185\uff0c\u6c99\u98de\u77f3\u8d70\u3002\u4e24\u4eba\u90fd\u7ed9\u5bf9\u65b9\u7684\u5175\u5668\u903c\u51fa\u4e00\u4e08\u5f00\u5916\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u89c1\u6b64\u5a01\u52bf\uff0c\u4e0d\u6562\u4e0a\u524d\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u4e86\u56e0\u6e10\u5904\u4e0b\u98ce\uff0c\u6b66\u6210\u5316\u8fd0\u8d77\u5185\u5bb6\u771f\u529b\uff0c\u864e\u6491\u8361 \u98ce\uff0c\u54d7\u5577\u5577\u4e00\u54cd\uff0c\u5728\u7985\u6756\u4e0a\u9762\uff0c\u55b7\u51fa\u4e00\u6e9c\u706b\u5149\uff0c\u4e86\u56e0\u864e\u53e3\u53d1\u9ebb\uff0c\u6025\u9000\u4e24\u6b65\u3002\u6b66\u6210\u5316\u65e0\u5fc3\u604b \u6218\uff0c\u903c\u9000\u4e86\u56e0\u4e4b\u540e\u4e00\u8f6c\u8eab\u4fbf\u8d70\u3002\u4e86\u56e0\u6c14\u7ea2\u4e86\u773c\uff0c\u4e00\u9000\u590d\u4e0a\uff0c\u7985\u6756\u4e00\u62db\u201c\u82cd\u9f99\u6446\u5c3e\u201d\uff0c\u53c8\u662f\u5377 \u5730\u626b\u6765\u3002\u6b66\u6210\u5316\u7684\u864e\u6491\u4e00\u6a2a\uff0c\u53cd\u624b\u4e00\u62db\u201c\u661f\u6a2a\u6597\u8f6c\u201d\uff0c\u5c06\u4e86\u56e0\u7684\u7985\u6756\u6a2a\u5c01\u51fa\u53bb\uff0c\u559d\u9053\uff1a\u201c\u51f6 \u50e7\uff0c\u4f60\u771f\u4e2a\u4e0d\u77e5\u8fdb\u9000\uff0c\u4ffa\u53ef\u8981\u66ff\u4f60\u8d85\u5ea6\u4e86\uff01\u201d\u53e3\u4e2d\u5ff5\u5ff5\u6709\u8f9e\uff0c\u5de6\u624b\u638f\u51fa\u4e00\u4e2a\u94dc\u94c3\uff0c\u94c3\u94c3\u7684\u6447 \u4e2a\u4e0d\u505c\uff0c\u94c3\u58f0\u566a\u8033\u3002\u628a\u4e86\u56e0\u542c\u5f97\u5fc3\u70e6\u610f\u4e71\uff0c\u6756\u6cd5\u4f7f\u5f00\uff0c\u5df2\u4e0d\u4f3c\u5148\u524d\u7cbe\u5999\uff0c\u6b66\u6210\u5316\u7684\u864e\u6491\u6a2a\u5c04 \u76f4\u51fb\uff0c\u8d8a\u53d1\u51cc\u5389\uff01\u4e86\u56e0\u559d\u9053\uff1a\u201c\u5c0f\u5e74\uff0c\u4f60\u5728\u65c1\u8fb9\u5e72\u77aa\u773c\u5417\uff1f\u201d\u5e74\u7fb9\u5c27\u662f\u5927\u5c06\u8eab\u4efd\uff0c\u672c\u5c31\u4e0d\u80af \u548c\u4e86\u56e0\u4e00\u9f50\u52a8\u624b\uff0c\u4f55\u51b5\u654c\u4eba\u592a\u5f3a\uff0c\u4ed6\u66f4\u4e0d\u613f\u5192\u6b64\u5371\u9669\u4e86\u3002\u4e86\u56e0\u4e00\u559d\uff0c\u5e74\u7fb9\u5c27\u5fc3\u4e2d\u53d1\u6c14\uff0c\u53e3\u4e2d \u5374\u51b7\u7b11\u9053\uff1a\u201c\u53cc\u9b54\u5c31\u5feb\u6765\u5230\u3002\u4f55\u5fc5\u6211\u6765\u52a8\u624b\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e1c\u5357\u89d2\u4e0a\u4e24\u6761\u9ed1\u5f71\u7531\u9690\u800c\u73b0\uff0c\u679c \u7136\u662f\u53cc\u9b54\u5230\u4e86\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u6765\u52a9\u6218\uff01\u201d\u8428\u5929\u523a\u9887\u5de5\u5fc3\u8ba1\uff0c\u8eab\u5f62\u98de\u8d77\uff0c\u5728\u63a0\u8fc7\u5e74\u7fb9\u5c27\u8eab\u8fb9\u4e4b \u65f6\uff0c\u5728\u4ed6\u8033\u8fb9\u8f7b\u8f7b\u8bf4\u4e86\u4e00\u58f0\uff1a\u201c\u4e86\u56e0\u66fe\u641c\u4f60\u7684\u623f\u95f4\uff01\u201d\u4e00\u63a0\u5373\u8fc7\uff0c\u5f85\u5e74\u7fb9\u5c27\u970d\u7136\u9192\u8d77\u4e4b\u65f6\uff0c \u4ed6\u5df2\u548c\u8428\u5929\u90fd\u5de6\u53f3\u5206\u8fdb\uff0c\u4e09\u4e2a\u4eba\u6392\u6210\u54c1\u5b57\u5f62\uff0c\u5c06\u6b66\u6210\u5316\u56f4\u5728\u5f53\u4e2d\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u542c\u4e86\u8428\u5929\u523a\u7684\u8bdd\uff0c\u65e0\u5fc3\u89c2\u6218\u3002\u8dd1\u8fdb\u4e66\u623f\uff0c\u53ea\u89c1\u623f\u4e2d\u8863\u7269\u51cc\u4e71\u3002\u5fc3\u9053\uff1a\u201c\u8fd9\u79c3\u9a74\u8d8a \u6765\u8d8a\u65e0\u793c\u4e86\uff01\u7ee7\u800c\u4e00\u60f3\uff1a\u81ea\u5df1\u662f\u4e09\u519b\u4e3b\u5e05\uff0c\u5f53\u4eca\u7687\u4e0a\u6700\u5ba0\u4fe1\u7684\u5fc3\u8179\u5927\u5c06\uff0c\u4e86\u56e0\u7adf\u6562\u5077\u5077\u6478\u6478 \u641c\u7d22\u6211\u7684\u623f\u95f4\uff0c\u83ab\u975e\u8fd8\u6709\u6240\u6043\uff1f\u5141\u796f\u624b\u6bb5\u6bd2\u8fa3\uff0c\u5e74\u7fb9\u5c27\u7d20\u6765\u6df1\u77e5\uff0c\u5982\u6b64\u4e00\u5c42\u4e00\u5c42\u7684\u63a8\u60f3\u4e0b \u53bb\uff0c\u4e0d\u89c9\u51b7\u6c57\u76f4\u6d41\uff0c\u7259\u6839\u4e00\u54ac\uff0c\u5fc3\u9053\uff1a\u201c\u4e86\u56e0\u6b64\u4eba\u662f\u4e0d\u80fd\u518d\u7559\u5728\u8eab\u8fb9\u4e86\uff0c\u5141\u796f\u66fe\u6709\u5bc6\u4ee4\uff0c\u53eb \u6211\u897f\u5f81\u4e4b\u540e\uff0c\u53ef\u76f8\u673a\u9664\u6389\u4ed6\uff0c\u6211\u63d0\u524d\u52a8\u624b\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u53ef\uff01\u6211\u73b0\u5728\u624b\u63e1\u5175\u6743\uff0c\u4e0e\u4e86\u56e0\u76f8 \u6bd4\uff0c\u5730\u4f4d\u91cd\u8981\u5f97\u591a\uff0c\u5141\u796f\u7edd\u4e0d\u81f3\u4e8e\u56e0\u6211\u63d0\u65e9\u6740\u4e86\u4e86\u56e0\uff0c\u800c\u548c\u6211\u7ffb\u9762\u3002\u5e74\u7fb9\u5c27\u719f\u8bfb\u5175\u5c06\uff0c\u7cbe\u901a \u8c0b\u7565\uff0c\u5230\u4e86\u6b64\u9645\uff0c\u5df2\u731c\u51fa\u662f\u5141\u796f\u6697\u4e2d\u6446\u5e03\uff0c\u53eb\u4ed6\u548c\u4e86\u56e0\u4e92\u76f8\u76d1\u89c6\uff0c\u56e0\u6b64\u9664\u4e86\u56e0\u4e4b\u5ff5\u6108\u6025\u3002\u4e00 \u4e2a\u4eba\u5728\u623f\u4e2d\u76d8\u7b97\u6bd2\u8ba1\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u5728\u623f\u4e2d\u7b97\u8ba1\uff0c\u4e86\u56e0\u5728\u5916\u9762\u6253\u5f97\u66f4\u70c8\u3002\u4e86\u56e0\u529f\u529b\u7565\u5728\u6b66\u6210\u5316\u4e4b\u4e0b\uff0c\u5f97\u53cc\u9b54\u6765\u52a9\uff0c\u7acb \u5373\u5360\u4e86\u4e0a\u98ce\uff01\u8428\u5929\u523a\u5f20\u7259\u821e\u722a\uff0c\u8fde\u4f7f\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u817e\u8dc3\u95ea\u6251\uff0c\u4e13\u653b\u654c\u624b\u7a7a\u95e8\uff0c\u8428\u5929\u90fd\u4f8d\u7740 \u4e00\u8eab\u86ee\u529b\uff0c\u786c\u51b2\u786c\u6253\uff0c\u4e86\u56e0\u7684\u7985\u6756\u5219\u628a\u864e\u6491\u6321\u4f4f\uff0c\u4e0d\u8ba9\u5b83\u6253\u4e2d\u53cc\u9b54\u3002 \u3000\u3000\u6b66\u6210\u5316\u53c8\u6012\u53c8\u6025\uff0c\u6012\u8005\u662f\u4ee5\u4e86\u56e0\u90a3\u6837\u7684\u540d\u5934\uff0c\u7adf\u7136\u4ee5\u591a\u4e3a\u80dc\uff0c\u5408\u51e0\u540d\u9ad8\u624b\u4e4b\u529b\uff0c\u56f4\u653b\u81ea \u5df1\uff1b\u6025\u8005\u662f\u674e\u6cbb\u4e00\u53bb\uff0c\u4e0d\u95fb\u58f0\u606f\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f\u88ab\u654c\u4eba\u64d2\u4e86\uff0c\u8fd8\u662f\u8ffd\u51fa\u56ed\u5b50\u53bb\u4e86\u3002\u6025\u6012\u4e4b\u4e2d\uff0c \u65e0\u5fc3\u604b\u6218\uff0c\u4e3a\u4e86\u8981\u51b2\u51fa\u5305\u56f4\uff0c\u624b\u6cd5\u4e00\u7d27\uff0c\u864e\u6491\u8361\u98ce\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u53cc\u9b54\u7ed9\u4ed6\u8ffd\u5f97\u56e2\u56e2\u4e71\u8f6c\uff0c\u867d \u6709\u4e86\u56e0\u654c\u4f4f\u4ed6\u7684\u864e\u6491\uff0c\u53cc\u9b54\u4e5f\u5df2\u4e0d\u80fd\u6b3a\u8eab\u8fdb\u903c\uff01 \u3000\u3000\u6fc0\u6597\u4e4b\u4e2d\uff0c\u8428\u5929\u90fd\u7ed9\u4ed6\u7684\u94c3\u58f0\u6447\u5f97\u5fc3\u70e6\u610f\u4e71\uff0c\u6012\u9a82\u9053\uff1a\u201c\u88c5\u795e\u5f04\u9b3c\uff0c\u4e0d\u662f\u4e1c\u897f\uff01\u201d\u7528\u94c3 \u58f0\u6270\u654c\u4e43\u662f\u6b66\u6210\u5316\u7684\u72ec\u95e8\u79d8\u6cd5\u3002\u539f\u6765\u9ad8\u624b\u640f\u6597\uff0c\u6700\u5fcc\u5206\u795e\uff0c\u6b66\u6210\u5316\u7528\u94c3\u58f0\u6270\u654c\uff0c\u800c\u81ea\u5df1\u5374\u4e0d \u4e3a\u6240\u6270\uff0c\u5a01\u529b\u65e0\u5f62\u4e2d\u5c31\u589e\u957f\u51e0\u5206\u3002\u53ef\u662f\u8fd9\u79cd\u94c3\u58f0\u6270\u654c\u7684\u6218\u6cd5\uff0c\u53ea\u80fd\u6536\u8f85\u52a9\u4e4b\u6548\uff0c\u80dc\u8d1f\u4e4b\u673a\uff0c \u4e3b\u8981\u8fd8\u662f\u51b3\u4e8e\u53cc\u65b9\u5b9e\u529b\uff0c\u4e86\u56e0\u4e0e\u53cc\u9b54\u4e09\u4eba\u8054\u624b\u5408\u6597\uff0c\u5b9e\u529b\u6bd4\u6b66\u6210\u5316\u5f3a\u5f97\u591a\uff0c\u6240\u4ee5\u867d\u7136\u5728\u767e\u62db \u4e4b\u5185\uff0c\u672a\u89c1\u8f93\u8d62\uff0c\u4f46\u5374\u662f\u7a33\u6301\u5148\u624b\uff0c\u663e\u5904\u4e0a\u51e4\uff01 \u3000\u3000\u4e09\u4eba\u4e4b\u4e2d\uff0c\u8428\u5929\u90fd\u7684\u529f\u529b\u6700\u4f4e\uff0c\u800c\u4e14\u6bd4\u4e86\u56e0\u66f4\u4e3a\u83bd\u649e\uff0c\u4ed6\u88ab\u94c3\u58f0\u6270\u5f97\u5fc3\u795e\u4e0d\u5b9a\uff0c\u53d1\u706b\u6012 \u9a82\uff0c\u51b2\u4e0a\u6765\u786c\u62a2\u6b66\u6210\u5316\u7684\u94dc\u94c3\u3002\u6b66\u6210\u5316\u4e00\u58f0\u51b7\u7b11\uff0c\u864e\u6491\u8d77\u5904\uff0c\u4e00\u62db\u201c\u5de8\u8230\u6a2a\u6c5f\u201d\u628a\u4e86\u56e0\u7684\u7985 \u6756\u5c01\u7740\uff0c\u5de6\u624b\u94dc\u94c3\u5728\u8428\u5929\u90fd\u8033\u8fb9\u7528\u529b\u4e00\u6447\uff0c\u8428\u5929\u90fd\u9661\u7136\u8df3\u8d77\uff0c\u6b66\u6210\u5316\u957f\u8896\u4e00\u6325\uff0c\u4f7f\u51fa\u5085\u9752\u4e3b \u6240\u4f20\u7684\u201c\u6d41\u661f\u98de\u8896\u201d\u7edd\u62db\uff0c\u628a\u8428\u5929\u90fd\u86ee\u725b\u4e5f\u4f3c\u7684\u8eab\u8eaf\u6454\u51fa\u4e09\u4e08\u5f00\u5916\uff01\u7f3a\u53e3\u4e00\u7834\uff0c\u6b66\u6210\u5316\u7acb\u5373 \u7a9c\u51fa\uff0c\u8428\u5929\u523a\u5fd9\u7740\u6551\u5f1f\u5f1f\uff0c\u4e86\u56e0\u4e00\u4eba\u4e0d\u6562\u524d\u8ffd\u3002 \u3000\u3000\u4e86\u56e0\u6536\u56de\u7985\u6756\uff0c\u6c14\u5598\u5401\u5401\u3002\u5e74\u7fb9\u5c27\u7acb\u5728\u95e8\u524d\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u7985\u5e08\u8f9b\u82e6\u4e86\uff01\u201d\u4e86\u56e0\u89c1\u5e74\u7fb9 \u5c27\u795e\u6001\u5b89\u8be6\uff0c\u82e5\u65e0\u5176\u4e8b\uff0c\u5fc3\u4e2d\u5947\u9053\uff1a\u201c\u4ed6\u65e2\u8fdb\u4e86\u4e66\u623f\uff0c\u96be\u9053\u8fd8\u770b\u4e0d\u51fa\u6211\u66fe\u641c\u8fc7\u4ed6\u7684\u623f\u95f4 \u5417\uff1f\u201d\u63d0\u8d77\u7985\u6756\uff0c\u8d70\u4e86\u8fc7\u53bb\u3002\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\uff0c\u8fdb\u6765\u5750\u5440\uff01\u201d\u4e86\u56e0\u5927\u6b65\u8de8\u8fdb\u4e66\u623f\uff0c\u7985 \u6756\u987f\u5730\uff0c\u6026\u7136\u6709\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5e74\uff0c\u4f60\u6536\u85cf\u7684\u90a3\u4e2a\u5c0f\u59d1\u5a18\u5462\uff1f\u54b1\u4eec\u90fd\u662f\u719f\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u53eb\u5979 \u51fa\u6765\u89c1\u89c1\uff1f\u201d\u5e74\u7fb9\u5c27\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\uff0c\u4f60\u8ddf\u968f\u7687\u4e0a\u591a\u5e74\uff0c\u8fd8\u4e0d\u77e5\u9053\u4ed6\u7684\u5fc3\u610f\u3002\u201d \u4e86\u56e0\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4ed6\u8fd9\u4eba\u6700\u4e3a\u591a\u7591\uff0c\u51fa\u4eac\u4e4b\u65f6\uff0c\u4ed6\u66fe\u53eb\u4f60\u6697\u4e2d\u5bf9\u6211\u76d1\u89c6\uff0c\u662f \u4e48\uff1f\u201d\u4e86\u56e0\u7741\u5927\u4e86\u773c\uff0c\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u4e86\uff0c\u662f\u7687\u4e0a\u4eb2\u81ea\u544a\u8bc9\u6211\u7684\u3002\u4ed6\u8bf4\u4ed6\u4e4b\u6240\u4ee5\u8fd9 \u6837\u5429\u5490\u4f60\uff0c\u662f\u8981\u8003\u9a8c\u4f60\u662f\u5426\u5fe0\u8bda\uff1f\u201d\u4e86\u56e0\u201c\u5514\u201d\u4e86\u4e00\u58f0\uff0c\u4f3c\u4fe1\u975e\u4fe1\u3002\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u5e76 \u4e0d\u662f\u63d0\u9632\u6211\uff0c\u800c\u662f\u63d0\u9632\u4f60\u554a\uff01\u201d\u4e86\u56e0\u5fc3\u5934\u4e00\u9707\uff0c\u53e3\u4e2d\u8bf4\u9053\uff1a\u201c\u5c0f\u5e74\uff0c\u4f60\u60f3\u79bb\u95f4\u6211\u548c\u7687\u4e0a \u5417\uff1f\u201d\u5e74\u7fb9\u5c27\u8bf4\u9053\uff1a\u201c\u5c82\u6562\u3002\u4e0d\u8fc7\u7985\u5e08\u4e5f\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u6211\u65ad\u4e0d\u4f1a\u5bf9\u7687\u4e0a\u8bf4\u4f60\u574f\u8bdd\u3002\u201d\u6b64\u8bdd\u7528 \u610f\uff0c\u5176\u5b9e\u662f\u60f3\u903c\u4e86\u56e0\u548c\u4ed6\u4e92\u76f8\u63a9\u62a4\u3002\u4e86\u56e0\u4f4e\u5934\u4e0d\u8bed\uff0c\u5bf9\u5e74\u7fb9\u5c27\u7684\u8bdd\u8fd8\u672a\u5168\u4fe1\uff0c\u5fc3\u60f3\uff1a\u6b64\u4e8b\u751a \u5927\uff0c\u5e94\u8be5\u548c\u8463\u5de8\u5ddd\u5546\u91cf\u3002\u5e74\u7fb9\u5c27\u89c1\u8bf4\u4ed6\u4e0d\u670d\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u90a3\u4e2a\u5973\u5a03\u5b50\u662f\u5728\u6211\u8fd9\u91cc\uff0c\u800c\u4e14\u5df2\u6765 \u4e86\u534a\u5e74\u6709\u591a\u4e86\u3002\u201d\u4e86\u56e0\u89c1\u4ed6\u4f46\u7136\u76f4\u627f\uff0c\u9887\u51fa\u610f\u5916\uff0c\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u662f\u7687\u4e0a\u7684\u610f\u601d\u554a\uff01\u201d \u4e86\u56e0\u9053\uff1a\u201c\u5c0f\u5e74\uff0c\u4f60\u80e1\u8bf4\uff01\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\uff0c\u4f60\u867d\u7136\u6b66\u529f\u7edd\u9876\uff0c\u53ef\u662f\u5bf9\u513f\u5973\u4e4b\u4e8b\uff0c \u5374\u975e\u6df1\u77e5\u3002\u201d\u4e86\u56e0\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u7687\u4e0a\u60f3\u628a\u8fd9\u5973\u5a03\u5b50\u6536\u5165\u540e\u5bab\uff0c\u5c01\u4e3a\u8d35\u5983\uff0c\u4f60 \u77e5\u9053\u4e48\uff1f\u201d\u4e86\u56e0\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e3b\u516c\u60f3\u8981\u5979\u3002\u81f3\u4e8e\u662f\u5426\u8981\u5c01\u5979\u4f5c\u8d35\u5983\uff0c\u90a3\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u53ea\u8981 \u4e3b\u516c\u6b22\u559c\uff0c\u5c01\u5979\u4f5c\u7687\u540e\u4e5f\u4e0d\u51fa\u5947\u3002\u201d\u5e74\u7fb9\u5c27\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u5b9d\u56fd\u7985\u5e08\u6b64\u8a00\u5dee\u77e3\uff01\u8fd9\u6837\u770b \u6765\uff0c\u4f60\u4e0d\u77e5\u9053\u7684\u4e8b\u60c5\u8fd8\u591a\u5462\uff01\u201d \u3000\u3000\u4e86\u56e0\u6115\u7136\uff0c\u9053\uff1a\u201c\u6211\u600e\u4e48\u8bf4\u8bdd\u5dee\u4e86\u3002\u201d\u5e74\u7fb9\u5c27\u7b11\u9053\uff1a\u201c\u672c\u671d\u5bab\u7981\uff0c\u6c49\u5973\u4e0d\u51c6\u5165\u5bab\uff0c\u4f60\u77e5 \u9053\u4e48\uff1f\u201d\u4e86\u56e0\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u8fc7\u662f\u8bf4\u8bf4\u7f62\u4e86\uff0c\u524d\u671d\u7684\u8463\u5c0f\u5b9b\u4e0d\u662f\u6c49\u5973\u4e48\uff1f\u201d\u5e74\u7fb9\u5c27\u7b11\u9053\uff1a\u201c\u540e \u6765\u662f\u653e\u5bbd\u4e86\uff0c\u53ef\u662f\u6c49\u5973\u7edd\u4e0d\u80fd\u7acb\u4e3a\u7687\u540e\uff0c\u96be\u9053\u4f60\u4e0d\u77e5\u9053\u4e48\uff1f\u201d\u4e86\u56e0\u9053\uff1a\u201c\u6664\uff0c\u90a3\u4e48\u7b97\u6211\u8bf4\u9519 \u4e86\u3002\u5176\u5b9e\u90a3\u5973\u5a03\u5b50\u88ab\u5c01\u505a\u4ec0\u4e48\uff0c\u548c\u6211\u90fd\u4e0d\u76f8\u5e72\u3002\u6211\u53ea\u77e5\u9053\u4e3b\u516c\u8981\u5979\uff0c\u800c\u4f60\u5374\u628a\u5979\u85cf\u4e86\u5927\u534a \u5e74\uff0c\u8fd9\u4e2a\u600e\u4e48\u8bf4\u6cd5\u3002\u201d\u5e74\u7fb9\u5c27\u7b11\u9053\uff1a\u201c\u82e5\u53ea\u662f\u7687\u4e0a\u8981\u5979\uff0c\u90a3\u4e48\u4e8b\u60c5\u7b80\u5355\u5f97\u5f88\uff0c\u968f\u4fbf\u628a\u5979\u7cdf \u584c\uff0c\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u53ef\u662f\u7687\u4e0a\u662f\u771f\u7684\u559c\u6b22\u5979\u5440\uff01\u201d\u4e86\u56e0\u9053\uff1a\u201c\u5c0f\u5e74\uff0c\u4f60\u8bf4\u8bdd\u522b\u515c\u5708\u5b50\uff0c\u8fd9\u6709 \u4ec0\u4e48\u4e0d\u540c\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u54c8\uff0c\u8fd9\u5c31\u5927\u5927\u4e0d\u540c\u4e86\u3002\u7687\u4e0a\u559c\u6b22\u5979\uff0c\u8981\u7ed9\u5979\u4e00\u4e2a\u5c01\u53f7\uff0c\u90a3\u4e48\u5979\u5c31 \u5f97\u662f\u9ad8\u8d35\u4eba\u5bb6\u51fa\u8eab\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u4e86\u56e0\u201c\u5514\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u8fd8\u662f\u4e0d\u5927\u660e\u767d\u3002\u201d\u5e74\u7fb9\u5c27 \u9053\uff1a\u201c\u666e\u901a\u4eba\u90fd\u8981\u8bb2\u95e8\u5f53\u6237\u5bf9\uff0c\u8fd9\uff0c\u4f60\u662f\u77e5\u9053\u7684\u4e86\uff1f\u7687\u5e1d\u662f\u5929\u4e0b\u6700\u8d35\u6700\u5bcc\u7684\u4eba\u5bb6\uff0c\u6240\u4ee5\u7acb\u7687 \u540e\u9009\u8d35\u5983\u90fd\u5e94\u662f\u540d\u95e8\u4e4b\u5973\u3002\u666e\u901a\u4eba\u5bb6\u7684\uff0c\u53ea\u80fd\u88ab\u9009\u4f5c\u5bab\u5a25\uff0c\u8981\u6709\u4e86\u513f\u5b50\u4e4b\u540e\uff0c\u624d\u80fd\u88ab\u7834\u4f8b\u5347 \u4e3a\u8d35\u5983\u3002\u90a3\u5973\u5a03\u5b50\u6765\u5386\u4e0d\u660e\uff0c\u7687\u4e0a\u53c8\u4e0d\u613f\u59d4\u5c48\u5979\u505a\u5bab\u5a25\uff0c\u6240\u4ee5\u53ea\u6709\u53e6\u60f3\u529e\u6cd5\u4e86\u3002\u201d\u4e86\u56e0\u9053\uff1a \u201c\u609f\uff0c\u6211\u660e\u767d\u5566\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6240\u4ee5\u7687\u4e0a\u9053\u5979\u51fa\u5bab\uff0c\u53eb\u5979\u4f4f\u5230\u6211\u5bb6\uff0c\u8ba4\u6211\u7236\u4eb2\u505a\u4e49\u7236\uff0c\u4e00 \u4e24\u5e74\u540e\uff0c\u518d\u732e\u5165\u5bab\u3002\u201d\u4e86\u56e0\u9053\uff1a\u201c\u90a3\u4e48\u4e3b\u516c\u4f55\u4ee5\u53c8\u5bf9\u6211\u8bf4\u5979\u662f\u79c1\u81ea\u9003\u8dd1\u7684\uff0c\u8fd8\u53eb\u6211\u7559\u5fc3\u5bfb\u627e \u5979\u5462\uff01\u201d\u5e74\u7fb9\u5c27\u7b11\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4f60\u4eec\u548c\u5979\u5f88\u719f\uff0c\u5979\u7a81\u7136\u4e0d\u89c1\uff0c\u7687\u4e0a\u9632\u5907\u4f60\u4eec\u7591\u5fc3\uff0c\u6240\u4ee5\u6545\u610f \u8bf4\u5979\u8dd1\u4e86\u3002\u5c06\u6765\u5979\u8fdb\u5bab\u4e4b\u65f6\uff0c\u4f60\u4eec\u53ea\u4ee5\u4e3a\u5979\u662f\u6211\u7684\u59b9\u5b50\uff0c\u600e\u77e5\u5979\u5c31\u662f\u90a3\u4e2a\u91ce\u4e2b\u5934\uff01\u201d\u4e86\u56e0\u5df2 \u4fe1\u4e86\u4e03\u516b\u6210\uff0c\u9053\uff1a\u201c\u4f46\u5979\u600e\u4e48\u4f4f\u5728\u4f60\u7684\u623f\u95f4\uff1f\u201d\u5e74\u7fb9\u5c27\u201c\u5478\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u600e\u4e48\u8fd9\u6837 \u5fc3\u90aa\uff01\u8fd9\u56ed\u5b50\u5c01\u95ed\u5df2\u4e45\uff0c\u5979\u4f4f\u5728\u8fd9\u91cc\u6700\u597d\u6ca1\u6709\u3002\u201d\u4e86\u56f0\u9053\uff1a\u201c\u5514\uff0c\u90a3\u662f\u514d\u5f97\u7ed9\u522b\u4eba\u77e5\u9053\u3002\u53ef \u662f\u2014\u2014\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6211\u56de\u6765\u662f\u63a2\u671b\u5979\u5440\uff0c\u4f60\u4ee5\u4e3a\u6211\u4f4f\u5728\u8fd9\u91cc\u4e48\uff1f\u201d\u4e86\u56e0\u7b11\u4e86\u8d77\u6765\u3002\u5e74\u7fb9\u5c27 \u9053\uff1a\u201c\u6240\u4ee5\u4f60\u7edd\u4e0d\u80fd\u544a\u8bc9\u7687\u4e0a\uff0c\u8981\u4e0d\u7136\u7687\u4e0a\u77e5\u9053\u4f60\u8bc6\u7834\u4ed6\u7684\u79d8\u5bc6\uff0c\u4f60\u975e\u4f46\u65e0\u529f\u52b3\u53ef\u8a00\uff0c\u800c\u4e14 \u6709\u4e0d\u6d4b\u4e4b\u7978\uff01\u201d\u4e86\u56e0\u51c9\u4e86\u534a\u622a\uff0c\u63d0\u8d77\u7985\u6756\uff0c\u5ffd\u9053\uff1a\u201c\u90a3\u4e48\u90a3\u4f4d\u672a\u6765\u7684\u8d35\u5983\u5a18\u5a18\u53c8\u8eb2\u5230\u54ea\u513f\u53bb \u4e86\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u5979\u89c1\u4f60\u6765\uff0c\u65e9\u5c31\u5413\u5f97\u8dd1\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u643a\u7740\u4e86\u56e0\u7684\u624b\uff0c\u51fa\u5230\u5916\u9762\uff0c\u62db\u547c\u4e86 \u53cc\u9b54\uff0c\u56db\u4eba\u4e00\u540c\u56de\u5230\u6b63\u5c4b\u5b89\u6b47\u3002\u5e74\u7fb9\u5c27\u77e5\u9053\u4e86\u56e0\u6027\u60c5\u66b4\u8e81\uff0c\u6240\u4ee5\u5148\u662f\u8bbe\u6cd5\u62c9\u62e2\uff0c\u62c9\u62e2\u4e0d\u6210\uff0c \u5c31\u7528\u8bf4\u8bdd\u628a\u4ed6\u5413\u4f4f\uff0c\u514d\u5f97\u4ed6\u5f53\u573a\u53d1\u4f5c\u3002\u56de\u5e9c\u4e4b\u540e\uff0c\u5f85\u4e86\u56e0\u7761\u4e86\uff0c\u6084\u6084\u628a\u8428\u5929\u523a\u53eb\u6765\uff0c\u8bf4\u9053\uff1a \u201c\u7687\u4e0a\u5f53\u5e74\u8058\u8bf7\u4f60\u4eec\u51fa\u5c71\uff0c\u66fe\u8bb8\u593a\u4f4d\u4e4b\u540e\uff0c\u628a\u4f60\u5c01\u4e3a\u56fd\u5e08\uff0c\u4e0d\u60f3\u4f60\u4eec\u73b0\u5728\u5374\u8eab\u5c45\u4eba\u4e0b\u3002\u201d\u8428 \u5929\u523a\u9053\uff1a\u201c\u6211\u4eec\u6280\u4e1a\u5e73\u5eb8\uff0c\u90a3\u6709\u4ec0\u4e48\u8bf4\u7684\u3002\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u5e74\u7fb9\u5c27\u4e00\u542c\u5c31\u77e5\u662f\u610f\u6c14\u4e4b\u8a00\u3002\u7b11 \u9053\uff1a\u201d\u8428\u8001\u524d\u8f88\u4e0d\u5fc5\u81ea\u8c26\uff0c\u8bba\u6b66\u529f\u8bba\u8d44\u5386\uff0c\u4f60\u6700\u5c11\u90fd\u8be5\u548c\u4e86\u56e0\u5e76\u5217\u3002\u201d\u8428\u5929\u523a\u9ed8\u7136\u4e0d\u8bed\u3002\u5e74 \u7fb9\u5c27\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u7687\u4e0a\u7684\u610f\u601d\uff0c\u662f\u8981\u4f60\u66ff\u4ee3\u4e86\u56e0\u3002\u201d \u3000\u3000\u8428\u5929\u523a\u8fde\u8fde\u6447\u9996\uff0c\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u9694\u5899\u65e0\u8033\uff0c\u4f60\u6015\u4ec0\u4e48\uff1f\u5b9e\u5bf9\u4f60\u8bf4\u5427\uff0c\u7687\u4e0a\u56e0\u4ed6\u6043\u529f\u50b2 \u6162\uff0c\u8981\u6211\u628a\u4ed6\u9664\u4e86\u3002\u201d\u5141\u796f\u6240\u6d3e\u7684\u4e94\u4e2a\u4eba\uff1a\u4e86\u56e0\u3001\u8463\u5de8\u5ddd\u3001\u53cc\u9b54\u548c\u7518\u5929\u9f99\uff0c\u4e86\u56e0\u662f\u4ee5\u5141\u796f\u5fc3 \u8179\u81ea\u5c45\uff0c\u5e73\u65f6\u4e0d\u53d7\u5e74\u7fb9\u5c27\u62e2\u7f09\uff0c\u8463\u5de8\u5ddd\u6700\u4e3a\u5978\u6ed1\uff0c\u77e5\u9053\u5141\u796f\u65e2\u5fcc\u5e74\u7fb9\u5c27\u4ea6\u5fcc\u4e86\u56e0\uff0c\u4f46\u5f81\u6218\u4e8b \u4e8b\u8981\u9760\u5e74\u7fb9\u5c27\uff0c\u6240\u4ee5\u4ed6\u5bf9\u5e74\u7fb9\u5c27\u4e5f\u751a\u4e3a\u5df4\u7ed3\uff0c\u7edd\u4e0d\u6562\u4ee5\u524d\u8f88\u81ea\u5c45\u3002\u7518\u5929\u9f99\u4ee5\u8463\u5de8\u5ddd\u9a6c\u9996\u662f \u77bb\uff0c\u81ea\u5df1\u5e76\u65e0\u4e3b\u610f\u3002\u53cc\u9b54\u5219\u65e9\u53d7\u5e74\u7fb9\u8367\u7b3c\u7edc\uff0c\u6240\u4ee5\u5bc6\u8c08\u7247\u523b\u4e4b\u540e\uff0c\u4fbf\u548c\u5e74\u7fb9\u5c27\u5546\u91cf\u9664\u6389\u4e86\u56e0 \u4e4b\u7b56\u3002 \u3000\u3000\u4e86\u56e0\u61f5\u7136\u4e0d\u77e5\u3002\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u53cc\u9b54\u62cd\u95e8\u628a\u4e86\u56e0\u53eb\u8d77\uff0c\u9053\uff1a\u201c\u6211\u4eec\u5144\u5f1f\u4eca\u65e5\u5148\u56de\u519b\u4e2d\uff0c\u5b9d \u56fd\u7985\u5e08\u8fd8\u5728\u5e74\u5e9c\u9017\u7559\uff1f\u201d\u4e86\u56e0\u6025\u7740\u8981\u56de\u53bb\u548c\u8463\u5de8\u5ddd\u5546\u91cf\uff0c\u9053\uff1a\u201c\u6211\u540c\u4f60\u4eec\u4e00\u9f50\u56de\u53bb\u597d\u4e86\u3002\u201d \u5e74\u7fb9\u5c27\u65e9\u5907\u597d\u9152\u83dc\uff0c\u7ed9\u4e86\u56e0\u996f\u884c\uff0c\u53cc\u9b54\u659f\u6ee1\u4e86\u9152\uff0c\u5148\u81ea\u996e\u4e86\uff0c\u4e86\u56e0\u55dc\u9152\u5982\u547d\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u996e \u4e86\u4e09\u676f\uff0c\u5ffd\u89c1\u53cc\u9b54\u9762\u8272\u6709\u5f02\uff0c\u5e74\u7fb9\u5c27\u53c8\u4e0d\u51fa\u6765\uff0c\u4e86\u56e0\u8bd5\u8fd0\u5185\u529f\uff0c\u7528\u4e39\u7530\u4e4b\u6c14\uff0c\u5c06\u9152\u903c\u51fa\uff0c\u5ffd \u89c9\u4e0d\u5982\u5e73\u65e5\u901a\u7545\uff0c\u53cc\u773c\u751f\u8f89\uff0c\u8428\u5929\u90fd\u5413\u5f97\u9762\u5982\u571f\u8272\uff0c\u4e86\u56e0\u5927\u559d\u4e00\u58f0\uff0c\u9661\u7684\u628a\u8428\u5929\u523a\u6293\u4e86\u8d77\u6765\uff01 \u3000\u3000\u6309\u8bf4\u53cc\u9b54\u6b66\u529f\u867d\u8f83\u4e86\u56e0\u4e3a\u5f31\uff0c\u4f46\u5f1f\u5144\u5408\u529b\uff0c\u4e5f\u53ef\u4ee5\u548c\u4e86\u56e0\u6253\u4e2a\u5e73\u624b\uff0c\u82e5\u518d\u52a0\u4e0a\u5e74\u7fb9\u5c27\uff0c \u90a3\u5c31\u53ef\u4ee5\u7a33\u64cd\u80dc\u5238\u3002\u4f46\u5e74\u7fb9\u5c27\u6015\u4e86\u56e0\u52c7\u731b\uff0c\u4e0d\u6562\u51fa\u6765\uff1b\u800c\u53cc\u9b54\u957f\u4e45\u4ee5\u6765\uff0c\u5728\u4e86\u56e0\u79ef\u5a01\u4e4b\u4e0b\uff0c \u5bf9\u4ed6\u751a\u4e3a\u60e7\u6015\u3002\u5c24\u5176\u662f\u8428\u5929\u90fd\uff0c\u56e0\u4e3a\u81ea\u6043\u5929\u751f\u795e\u529b\uff0c\u94dc\u76ae\u94c1\u9aa8\uff0c\u66fe\u548c\u4e86\u56e0\u8f83\u529b\uff0c\u88ab\u4e86\u56e0\u6298 \u670d\uff0c\u51e0\u4e4e\u626d\u65ad\u624b\u8155\u3002\u8428\u5929\u90fd\u662f\u6027\u76f4\u7684\u4eba\uff0c\u81ea\u6b64\u5bf9\u4e86\u56e0\u751f\u754f\uff0c\u5728\u9152\u4e2d\u6709\u8428\u5929\u523a\u91c7\u81ea\u201c\u86c7\u5c9b\u201d\u7684 \u6bd2\u836f\uff0c\u4ed6\u4eec\u5144\u5f1f\u4e8c\u4eba\u670d\u4e86\u89e3\u836f\uff0c\u81ea\u996e\u65e0\u7eba\uff0c\u5e74\u7fb9\u5c27\u56e0\u6b64\u5b9a\u4e0b\u6bd2\u8ba1\uff0c\u53eb\u53cc\u9b54\u529d\u4ed6\u996e\u9152\uff0c\u672c\u4ee5\u4e3a \u4e86\u56e0\u5fc5\u6b7b\u65e0\u7591\uff0c\u4e0d\u6599\u4e86\u56e0\u5185\u529f\u6df1\u539a\uff0c\u8fde\u996e\u4e09\u676f\uff0c\u9762\u4e0d\u6539\u5bb9\uff0c\u53cc\u9b54\u5df2\u81ea\u614c\u4e86\u3002\u56e0\u6b64\u4ee5\u8428\u5929\u523a\u7684 \u6b66\u529f\uff0c\u7adf\u7136\u88ab\u4e86\u56e0\u4e3e\u624b\u4e4b\u95f4\uff0c\u64d2\u4e86\u8fc7\u53bb\uff01 \u3000\u3000\u4e86\u56e0\u4e09\u6307\u5728\u8428\u5929\u523a\u8109\u95e8\u4e00\u6263\uff0c\u559d\u9053\uff1a\u201c\u5feb\u628a\u89e3\u836f\u62ff\u6765\uff01\u201d\u8428\u5929\u523a\u9053\uff1a\u201c\u4e0d\u5173\u54b1\u4eec\u7684 \u4e8b\u3002\u201d\u4e86\u56e0\u624b\u6307\u7528\u529b\uff0c\u8428\u5929\u523a\u9053\uff1a\u201c\u89e3\u836f\u5728\u6211\u888b\u4e2d\u3002\u201d\u4e86\u56e0\u6478\u4e86\u51fa\u6765\uff0c\u4e22\u8fdb\u53e3\u4e2d\uff0c\u5c4f\u98ce\u540e\u4e00 \u58f0\u5450\u558a\uff0c\u5e74\u5bb6\u7684\u6b66\u58eb\u6740\u4e86\u51fa\u6765\uff01 \u3000\u3000\u4e86\u56e0\u628a\u8428\u5929\u523a\u4e00\u63b7\uff0c\u62a2\u4e86\u7985\u6756\uff0c\u62a1\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u628a\u5c4f\u98ce\u684c\u6905\uff0c\u6253 \u5f97\u7a00\u70c2\uff0c\u5e74\u7fb9\u5c27\u5728\u70ae\u697c\u4e0a\u4f38\u51fa\u5934\u6765\uff0c\u559d\u9053\uff1a\u201c\u8428\u5929\u523a\u5feb\u5feb\u4e0a\u524d\uff01\u201d\u8428\u5929\u523a\u8df3\u4e86\u8d77\u6765\uff0c\u548c\u8428\u5929 \u90fd\u786c\u7740\u5934\u76ae\u8fdb\u51fb\uff0c\u4e86\u56e0\u7985\u6756\u4e00\u6296\uff0c\u8f70\u7684\u4e00\u58f0\uff0c\u6253\u5728\u77f3\u67f1\u4e4b\u4e0a\uff0c\u987f\u65f6\u628a\u5927\u53ef\u5408\u62b1\u7684\u77f3\u67f1\u6253\u65ad\uff0c \u67f1\u6298\u6881\u6467\uff0c\u5899\u584c\u5c4b\u5012\uff0c\u53cc\u9b54\u5728\u74e6\u77f3\u98de\u626c\u3001\u6c99\u96fe\u8ff7\u6f2b\u4e2d\u6025\u9000\uff0c\u6b66\u58eb\u4eec\u88ab\u538b\u5f97\u624b\u8db3\u6298\u65ad\uff0c\u6ee1\u5730\u4e71 \u6eda\uff0c\u4e86\u56e0\u62a1\u52a8\u7985\u6756\uff0c\u76f4\u6253\u51fa\u6765\uff0c\u8981\u6253\u8fdb\u540e\u5802\uff0c\u51b2\u4e0a\u70ae\u697c\u548c\u5e74\u7fb9\u5c27\u62fc\u547d\uff01\u53cc\u9b54\u6025\u5fd9\u548c\u4ed6\u7f20\u6597\u3002 \u5e74\u7fb9\u5c27\u57cb\u4f0f\u597d\u7684\u5f13\u7bad\u624b\uff0c\u5f20\u5f13\u642d\u7bad\uff0c\u5f15\u5f26\u5f85\u5c04\u3002\u4e86\u56e0\u6253\u4e86\u4e00\u9635\u5ffd\u89c9\u6c14\u4fc3\u5fc3\u8df3\uff0c\u53e3\u4e2d\u7126\u6e34\uff01 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5154\u6b7b\u72d7\u70f9\uff0c\u9e1f\u5c3d\u5f13\u85cf\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516d\u56de\u3000\u5fc3\u8179\u4e92\u731c\u7591\u3000\u53cc\u9b54\u8fdb\u9152\u3000\u540c\u95e8\u9f50\u805a\u96c6\u3000\u8054\u5251\u8bdb\u51f6 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u516d\u56de \u5fc3\u8179\u4e92\u731c\u7591\u3000\u53cc\u9b54\u8fdb\u9152 \u540c\u95e8\u9f50\u805a\u96c6\u3000\u8054\u5251\u8bdb\u51f6 \u3000\u3000\u4e86\u56e0\u867d\u670d\u4e86\u89e3\u836f\uff0c\u4f46\u672a\u53ca\u8fd0\u6c14\u8c03\u5143\uff0c\u4fbf\u662f\u4e00\u573a\u6076\u6218\uff0c\u89e3\u836f\u538b\u5236\u4e0d\u4f4f\uff0c\u6bd2\u836f\u6162\u6162\u53d1\u4f5c\uff0c\u77e5 \u9053\u4e0d\u80fd\u4e45\u6218\uff0c\u4e00\u4e2a\u7ffb\u8eab\uff0c\u62a1\u5f00\u7985\u6756\uff0c\u76f4\u6253\u51fa\u53bb\u3002\u5f13\u7bad\u624b\u53d1\u4e00\u58f0\u558a\uff0c\u7fbd\u7bad\u7eb7\u98de\uff0c\u90a3\u91cc\u5c04\u5f97\u4ed6 \u4e2d\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u7ed9\u4e86\u56e0\u6253\u51fa\u5927\u95e8\uff0c\u5927\u58f0\u9a82\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u4f60\u8fd9\u4eba\u5a18\u8d3c\uff0c\u649e\u5728\u6d12\u5bb6\u624b\u91cc\uff0c\u4e00\u6756\u6253 \u65ad\u4f60\u7684\u72d7\u817f\uff01\u201d\u4e00\u811a\u628a\u5e74\u5bb6\u5927\u95e8\u8e22\u70c2\uff0c\u547c\u547c\u4e24\u6756\uff0c\u628a\u5927\u95e8\u5de6\u53f3\u7684\u77f3\u72ee\u5b50\u4e5f\u6253\u788e\uff0c\u8fd9\u624d\u626c\u957f\u800c \u53bb\u3002 \u3000\u3000\u4e86\u56e0\u8d70\u540e\uff0c\u5e74\u7fb9\u5c27\u4e0b\u6765\u4e00\u770b\uff0c\u53ea\u89c1\u6ee1\u5730\u65ad\u7bad\uff0c\u4e0d\u7981\u5fc3\u60ca\u3002\u53cc\u9b54\u9053\uff1a\u201c\u7ed9\u4ed6\u8d70\u8131\uff0c\u5982\u4f55\u662f \u597d\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6211\u5373\u523b\u4e0a\u647a\u594f\u4ed6\uff0c\u7687\u4e0a\u4e0d\u4f1a\u4fe1\u4ed6\u7684\u8bdd\u3002\u6211\u518d\u53eb\u8f66\u8f9f\u90aa\u548c\u8463\u5de8\u5ddd\u62ff\u4ed6\uff0c\u8c05 \u4ed6\u9003\u4e0d\u51fa\u6211\u7684\u638c\u5fc3\u3002\u201d \u3000\u3000\u4e86\u56e0\u6253\u51fa\u5e74\u5bb6\uff0c\u8d8a\u60f3\u8d8a\u6c14\u3002\u8eb2\u4e0a\u9644\u8fd1\u5c71\u5934\uff0c\u6253\u5750\u4e00\u4f1a\uff0c\u628a\u6bd2\u90fd\u903c\u53d1\u51fa\u6765\uff0c\u5fc3\u9053\uff1a\u201c\u81ea\u5df1 \u5b64\u638c\u96be\u9e23\uff0c\u518d\u627e\u8fd9\u5c0f\u5b50\u62a5\u4ec7\uff0c\u4e5f\u6253\u4ed6\u4eec\u4e0d\u8fc7\u3002\u4e0d\u5982\u56de\u4eac\u53bb\u7980\u544a\u7687\u4e0a\uff0c\u628a\u4ed6\u8fd9\u5927\u5c06\u519b\u64a4\u4e86\uff0c\u7136 \u540e\u518d\u627e\u4ed6\u7b97\u5e10\u3002\u201d\u4e86\u56e0\u4e00\u5fc3\u8fd8\u4ee5\u4e3a\u5141\u796f\u80fd\u66ff\u4ed6\u4e3b\u6301\u516c\u9053\uff0c\u6c14\u6124\u6124\u7684\u72ec\u5f80\u5317\u884c\u3002 \u3000\u3000\u51af\u7433\u90a3\u665a\u4ece\u590d\u58c1\u6697\u95e8\u4e2d\u9003\u8d70\u4e4b\u540e\uff0c\u4e5f\u5411\u5317\u884c\uff0c\u7b2c\u4e8c\u65e5\u5230\u4e86\u65b0\u5b89\uff0c\u6cbf\u9014\u89c1\u8def\u4eba\u6765\u5f80\uff0c\u65e0\u4e0d \u5bf9\u81ea\u5df1\u6ce8\u89c6\uff0c\u5fc3\u60f3\uff1a\u6211\u4e00\u4e2a\u5355\u8eab\u5973\u5b50\uff0c\u96be\u602a\u53d7\u4eba\u6ce8\u610f\u3002\u6b8a\u4e0d\u77e5\u5979\u4e4b\u6240\u4ee5\u53d7\u4eba\u6ce8\u89c6\uff0c\u4e43\u662f\u751f\u5f97 \u592a\u7f8e\u4e4b\u6545\u3002\u51af\u7433\u53c8\u60f3\uff1a\u4e86\u56e0\u8fd9\u4e00\u5e2e\u4eba\u90fd\u51fa\u6765\u4e86\uff0c\u60f3\u5fc5\u653e\u6211\u4e0d\u8fc7\u3002\u6211\u8fd8\u662f\u6539\u88c5\u4e86\u5427\u3002\u8fd9\u65f6\u5df2\u5c06 \u5230\u9547\u4e0a\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u9a6c\u94c3\u4e4b\u58f0\uff0c\u51af\u7433\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u4e2d\u5e74\u4e66\u751f\uff0c\u9762\u767d\u65e0\u987b\uff0c\u4e94\u77ed\u8eab\u6750\uff0c\u7a7f\u5f97 \u9887\u4e3a\u534e\u4e3d\uff0c\u4e00\u8eab\u9526\u8863\uff0c\u751a\u662f\u593a\u76ee\u3002\u770b\u6765\u50cf\u662f\u4e2a\u516c\u5b50\u54e5\u513f\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u8863\u670d\u771f\u597d\u770b\uff0c\u6211\u4eca \u665a\u5077\u4ed6\u7684\u6539\u88c5\u4fbf\u662f\u3002\u770b\u4ed6\u8d70\u8fdb\u4e00\u95f4\u5ba2\u6808\uff0c\u4e5f\u8ddf\u7740\u8fdb\u53bb\u3002\u638c\u67dc\u7684\u6025\u5fd9\u62db\u5f85\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u5927 \u7237\uff0c\u4f60\u4eec\u8981\u4e00\u95f4\u623f\u8fd8\u662f\u8981\u4e24\u95f4\u623f\u3002\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u56de\u5934\u4e00\u770b\uff0c\u89c1\u4e00\u4e2a\u7edd\u8273\u5c11\u5973\u8ddf\u5728 \u540e\u8fb9\uff0c\u59cb\u77e5\u5e97\u4e3b\u8bef\u4f1a\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4e00\u4e2a\u4eba\u5462\uff0c\u600e\u4f4f\u5f97\u4e24\u95f4\u623f\u3002\u201d\u5e97\u4e3b\u4e5f\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u8fd9 \u4f4d\u5c0f\u59d1\u5a18\u662f\u540c\u4f60\u4e00\u8def\u6765\u7684\u3002\u201d\u51af\u7433\u5550\u4e86\u4e00\u53e3\uff0c\u5e97\u4e3b\u9053\uff1a\u201c\u59d1\u5a18\u83ab\u602a\uff0c\u5e74\u65f6\u4e0d\u597d\uff0c\u5f88\u5c11\u5355\u8eab\u7684 \u5973\u5b50\u6295\u5bbf\u3002\u201d\u90a3\u4e66\u751f\u8981\u4e86\u4e00\u95f4\u4e0a\u623f\uff0c\u51af\u7433\u4e5f\u8981\u4e00\u95f4\uff0c\u5e97\u4e3b\u4eba\u76b1\u8d77\u7709\u5934\uff0c\u82e5\u7136\u662f\u5356\u827a\u8dd1\u6c5f\u6e56\u7684 \u5355\u8eab\u5973\u5b50\u4e5f\u8fd8\u7f62\u4e86\uff0c\u51af\u7433\u7a7f\u7684\u5374\u662f\u5bcc\u5bb6\u5c0f\u59d0\u7684\u8863\u88f3\uff0c\u5e97\u4e3b\u8d77\u4e86\u731c\u7591\uff0c\u4e0d\u6562\u8ba9\u5979\u4f4f\u5bbf\u3002\u51af\u7433 \u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u6b3a\u8d1f\u6211\u6ca1\u94b1\u5417\uff1f\u201d\u638f\u51fa\u4e24\u7c92\u91d1\u8c46\uff0c\u6454\u5728\u67dc\u4e0a\uff0c\u5e97\u4e3b\u4eba\u5fd9\u7b11\u9053\uff1a\u201c\u5c82\u6562\u5c82\u6562\uff0c \u5c0f\u5e97\u7b80\u964b\uff0c\u6211\u662f\u6015\u4f60\u59d1\u5a18\u8d5a\u5f03\u3002\u201d\u6025\u5fd9\u4e5f\u66ff\u5979\u5f00\u4e86\u4e00\u95f4\u4e0a\u623f\u3002 \u3000\u3000\u7761\u5230\u534a\u591c\uff0c\u51af\u7433\u6084\u6084\u8d77\u6765\uff0c\u8dc3\u4e0a\u74e6\u9762\uff0c\u8df3\u8fc7\u90bb\u623f\uff0c\u4f0f\u8033\u4e00\u542c\uff0c\u91cc\u9762\u5bc2\u65e0\u58f0\u606f\uff0c\u54d1\u7136\u6697\u7b11 \u9053\uff1a\u201c\u8fd9\u4e66\u751f\u53c8\u4e0d\u662f\u6c5f\u6e56\u4e0a\u7684\u884c\u5bb6\uff0c\u4f55\u5fc5\u8fd9\u6837\u5c0f\u5fc3\u8c28\u614e\uff1f\u201d\u634f\u788e\u7a97\u683c\uff0c\u98de\u8eab\u8fdb\u53bb\uff0c\u6478\u5230\u5e8a \u8fb9\uff0c\u62ff\u8d77\u9526\u8863\u3002\u5e8a\u4e0a\u7684\u4eba\u5ffd\u7136\u4e00\u58f0\u51b7\u7b11\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u53cd\u624b\u4e00\u52fe\uff0c\u5c31\u628a\u51af\u7433\u7684\u624b\u8155\u52fe\u4f4f\uff01\u51af\u7433 \u6c89\u80a9\u7f29\u8098\uff0c\u8fc5\u901f\u7528\u4e86\u4e00\u62db\u201c\u6e14\u592b\u89e3\u7eb2\u201d\uff0c\u628a\u624b\u8131\u4e86\u51fa\u6765\uff0c\u90a3\u4e66\u751f\u566b\u4e86\u4e00\u58f0\uff0c\u9a88\u6307\u4e00\u6233\uff0c\u597d\u50cf \u957f\u7740\u591c\u773c\u4f3c\u7684\uff0c\u6233\u7684\u7adf\u662f\u540e\u9888\u201c\u5929\u67f1\u201d\u8981\u7a74\u3002 \u3000\u3000\u660f\u9ed1\u4e4b\u4e2d\uff0c\u90a3\u4e66\u751f\u8ba4\u7a74\u4e0d\u5dee\u6beb\u5398\uff0c\u5f53\u7136\u662f\u9ad8\u624b\u65e0\u7591\uff0c\u82e5\u5728\u534a\u5e74\u4e4b\u524d\uff0c\u51af\u7433\u5b9a\u7136\u88ab\u4ed6\u70b9 \u5012\u3002\u4e66\u751f\u51fa\u624b\u5982\u7535\uff0c\u70b9\u5230\u4e4b\u65f6\uff0c\u5ffd\u89c9\u8f6f\u7ef5\u7ef5\u7684\uff0c\u624b\u6307\u9661\u7136\u4e00\u6ed1\uff0c\u6b6a\u8fc7\u4e00\u65c1\uff0c\u51af\u7433\u53cd\u624b\u4e00\u638c\uff0c \u548c\u90a3\u4eba\u53f3\u638c\u63a5\u4e2a\u6b63\u7740\uff0c\u51af\u7433\u7ed9\u4ed6\u638c\u529b\u4e00\u5e26\uff0c\u51e0\u4e4e\u8dcc\u5012\uff0c\u800c\u90a3\u4eba\u9887\u4f3c\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\uff0c\u7ed9\u51af\u7433\u7684 \u638c\u529b\u4e00\u9707\uff0c\u6025\u5fd9\u60dc\u529b\u98d8\u8eab\uff0c\u98de\u8fc7\u623f\u4e2d\u90a3\u5f20\u684c\u5b50\uff0c\u9760\u95e8\u4e00\u7ad9\uff0c\u201c\u54b3\u201d\u7684\u4e00\u58f0\uff0c\u71c3\u4e86\u706b\u77f3\uff0c\u5fae\u5fae \u7b11\u9053\uff1a\u201c\u6211\u65e9\u6599\u4f60\u4f1a\u6765\u7684\u4e86\uff0c\u5750\u4e0b\u6765\uff0c\u5750\u4e0b\u6765\uff0c\u54b1\u4eec\u597d\u597d\u8c08\u8c08\uff0c\u522b\u60ca\u52a8\u4e86\u5e97\u4e2d\u7684\u4eba\u3002\u201d\u51af\u7433 \u884c\u85cf\u8d25\u9732\uff0c\u751a\u4e3a\u5c34\u5c2c\uff0c\u53ea\u597d\u4f9d\u8a00\u5750\u4e0b\u3002 \u3000\u3000\u90a3\u4e66\u751f\u5c06\u706b\u77f3\u70b9\u71c3\u4e86\u684c\u4e0a\u6cb9\u706f\uff0c\u7b11\u9053\uff1a\u201c\u4ee5\u4f60\u7684\u76f8\u8c8c\u6b66\u529f\u4e3a\u4f55\u8981\u505a\u5c0f\u8d3c\uff1f\u201d\u51af\u7433\u9053\uff1a \u201c\u4f60\u600e\u4e48\u4f1a\u6599\u5230\u6211\u6765\u5077\u4f60\uff1f\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u81ea\u78b0\u89c1\u4f60\u540e\uff0c\u4f60\u5c31\u7d27\u8ddf\u7740\u6211\u3002\u8fd9\u70b9\u90fd\u6599\u4e0d\u5230\uff0c\u6211 \u8fd8\u80fd\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\u5417\uff1f\u54c8\uff0c\u4e0d\u8fc7\uff0c\u4f60\u53ef\u8d70\u4e86\u773c\u4e86\uff0c\u6211\u867d\u7136\u8863\u7740\u534e\u7f8e\uff0c\u90a3\u662f\u6211\u7d20\u6027\u5982\u6b64\uff0c\u6211\u56ca \u4e2d\u6240\u6709\uff0c\u5176\u5b9e\u4e0d\u503c\u4f60\u4e00\u5077\u3002\u4f60\u82e5\u7f3a\u94b1\u4f7f\u7528\uff0c\u6211\u53ef\u9001\u4f60\u4e00\u952d\u9ec4\u91d1\uff0c\u518d\u591a\u5c31\u4e0d\u884c\u4e86\u3002\u201d\u51af\u7433\u7b11 \u9053\uff1a\u201c\u8c01\u8981\u4f60\u7684\u9ec4\u91d1\uff1f\u201d\u89e3\u5f00\u5916\u8863\u4e0a\u9762\u7684\u4e24\u7c92\u7ebd\u6263\uff0c\u9732\u51fa\u4e00\u5708\u73cd\u73e0\u9888\u7ec3\uff0c\u70df\u50e7\u751f\u5149\uff0c\u90a3\u662f\u7687 \u5e9c\u73cd\u54c1\uff0c\u6bcf\u7c92\u73cd\u73e0\uff0c\u90fd\u662f\u4e00\u5f0f\u5927\u5c0f\uff0c\u53c8\u5706\u53c8\u5927\u3002\u53ea\u8fd9\u4e32\u73cd\u73e0\uff0c\u4fbf\u8db3\u503c\u767e\u4e24\u9ec4\u91d1\u3002\u90a3\u4e66\u751f\u5403\u4e86 \u4e00\u60ca\uff0c\u9976\u662f\u4ed6\u89c1\u591a\u8bc6\u5e7f\uff0c\u4e5f\u6478\u4e0d\u900f\u51af\u7433\u6765\u8def\u3002 \u3000\u3000\u90a3\u4e66\u751f\u60f3\u4e86\u4e00\u60f3\uff0c\u84e6\u7136\u624b\u6478\u5251\u67c4\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u9ed1\u9053\u4e0a\u7684\u5973\u82f1\u96c4\uff0c\u60f3\u5fc5\u662f\u516c\u95e8\u4e2d \u7684\u5973\u9ad8\u624b\u4e86\uff1f\u5931\u656c\uff0c\u5931\u656c\uff01\u201d\u51af\u7433\u5657\u55e4\u4e00\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u9ed1\u9053\u767d\u9053\uff0c\u6211\u662f\u4e00\u6982\u4e0d\u77e5\uff01\u201d\u90a3\u4e66\u751f \u9053\uff1a\u201c\u90a3\u4f60\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u8fd9\u5957\u8863\u88f3\u503c\u591a\u5c11\u94b1\uff1f\u5356\u7ed9\u6211\u5427\uff01\u201d\u4e66\u751f\u4e00\u6115\uff0c\u731c\u4e0d\u900f \u5979\u7684\u7528\u610f\uff0c\u4e0d\u77e5\u5979\u662f\u73a9\u7b11\u8fd8\u662f\u6b63\u7ecf\u3002\u51af\u7433\u9053\uff1a\u201c\u6211\u5c31\u7528\u8fd9\u4e32\u73cd\u73e0\u4e70\u4f60\u7684\u8863\u670d\uff0c\u4f60\u603b\u4e0d\u5403\u4e8f\u4e86 \u5427\uff1f\u201d\u4e66\u751f\u6012\u9053\uff1a\u201c\u4f60\u662f\u8bda\u5fc3\u6765\u80e1\u95f9\u4e86\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u8c01\u6709\u5de5\u592b\u548c\u4f60\u80e1\u95f9\uff1f\u201d\u4e66\u751f\u770b\u5979\u8bf4\u8bdd \u795e\u6c14\uff0c\u4e0d\u50cf\u662f\u5f00\u73a9\u7b11\uff0c\u4fbf\u9053\uff1a\u201c\u9001\u4f60\u4e00\u5957\u8863\u88f3\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u3002\u8bf7\u95ee\u5c0a\u5e08\u662f\u54ea\u4e00\u4f4d\uff1f\u59d1\u5a18\u5927\u540d \u53ef\u80af\u89c1\u544a\u4e48\uff1f\u201d\u51af\u7433\u53c8\u662f\u55e4\u7684\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u840d\u6c34\u76f8\u9022\uff0c\u4f55\u5fc5\u67e5\u6839\u95ee\u5e95\uff0c\u518d\u8bf4\u6211\u7684\u5e08\u5c0a\u591a \u7740\u5462\uff0c\u600e\u80fd\u4e00\u4e00\u8bf4\u7ed9\u4f60\u542c\u3002\u201d\u4e66\u751f\u9053\uff1a\u201c\u4f60\u8981\u7537\u5b50\u7684\u8863\u88f3\u505a\u4ec0\u4e48\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u7ed9\u4e0d\u7ed9\uff0c \u4e0d\u7ed9\u6211\u4fbf\u8d70\u4e86\u3002\u201d\u90a3\u4e66\u751f\u5728\u6b66\u6797\u4e2d\u751a\u6709\u540d\u6c14\uff0c\u6478\u4e0d\u6e05\u51af\u7433\u6765\u5386\uff0c\u5fc3\u6709\u4e0d\u7518\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u7ad9\u8d77 \u6765\u9053\uff1a\u201c\u4f60\u6709\u672c\u4e8b\uff0c\u5c31\u8d70\u51fa\u53bb\u597d\u4e86\u3002\u201d\u51af\u7433\u5fc3\u60f3\uff1a\u201c\u8981\u6253\u67b6\u6211\u53ef\u4e0d\u6015\uff0c\u53ef\u662f\u60ca\u52a8\u4e86\u4eba\uff0c\u5374\u4e0d \u5927\u597d\u201d\u3002\u4fbf\u9053\uff1a\u201c\u8bf4\u7ed9\u4f60\u542c\u4e5f\u4e0d\u6253\u7d27\uff0c\u4f60\u53ef\u4e0d\u8bb8\u4e71\u8bf4\u51fa\u53bb\u3002\u201d\u4e66\u751f\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u201d\u51af\u7433 \u9053\uff1a\u201c\u6211\u7684\u7236\u4eb2\u662f\u4e2a\u5927\u5f3a\u76d7\uff0c\u4ed6\u903c\u6211\u5ac1\u7ed9\u4e00\u4e2a\u6211\u4e0d\u559c\u6b22\u7684\u4eba\uff0c\u6240\u4ee5\u6211\u4fbf\u9003\u8dd1\u51fa\u6765\uff0c\u4f46\u4ed6\u8033\u76ee \u4f17\u591a\uff0c\u6211\u6015\u88ab\u4eba\u770b\u7834\uff0c\u53ea\u80fd\u6539\u88c5\u9003\u907f\u3002\u201d \u3000\u3000\u51af\u7433\u8bf4\u7684\u5f53\u7136\u662f\u8c0e\u8bdd\uff0c\u53ef\u662f\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5374\u6070\u5de7\u548c\u9c7c\u5a18\u4e4b\u4e8b\u6697\u5408\u3002\u90a3\u4e66\u751f\u51fa\u795e\u7684\u770b\u7740\u5979\uff0c \u5ffd\u9053\uff1a\u201c\u4f60\u4eca\u5e74\u51e0\u5c81\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u771f\u6ca1\u793c\u8c8c\uff0c\u95ee\u4eba\u5e74\u5c81\u5e72\u561b\uff1f\u201d\u90a3\u4e66\u751f\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u597d\uff0c\u4e0d\u95ee\uff0c\u4e0d\u95ee\u3002\u4f60\u7684\u6765\u5386\u4e5f\u4e0d\u5fc5\u544a\u8bc9\u6211\u4e86\u3002\u54b1\u4eec\u5fc3\u7167\u4e0d\u5ba3\u3002\u201d \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u4f60\u613f\u4e0d\u613f\u5356\u4e00\u5957\u8863\u88f3\u7ed9\u6211\uff1f\u201d\u4e66\u751f\u9053\uff1a\u201c\u6211\u9001\u7ed9\u4f60\u3002\u201d\u51af\u7433\u5927\u559c\u9053\u8c22\u3002\u90a3\u4e66 \u751f\u53c8\u9053\uff1a\u201c\u6211\u7684\u6280\u4e1a\u867d\u7136\u7c97\u758f\uff0c\u5728\u6c5f\u6e56\u4e0a\u8fd8\u6709\u4e9b\u670b\u53cb\u3002\u4f60\u660e\u65e5\u548c\u6211\u540c\u884c\u597d\u4e86\u3002\u4fdd\u7ba1\u5927\u6c5f\u5357 \u5317\uff0c\u9ed1\u9053\u4e0a\u7684\u4eba\uff0c\u8c01\u4e5f\u4e0d\u6562\u5bf9\u4f60\u52a8\u624b\u3002\u201d\u51af\u7433\u5fc3\u60f3\uff1a\u8fd9\u4e66\u751f\u53e3\u6c14\u597d\u5927\uff0c\u6211\u5012\u8981\u770b\u770b\u4ed6\u662f\u4f55\u7b49 \u4eba\u7269\uff0c\u4fbf\u4e5f\u7b11\u7740\u7b54\u5e94\u3002 \u3000\u3000\u8fd9\u4e66\u751f\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u767d\u6cf0\u5b98\u7684\u5e08\u5f1f\uff0c\u5728\u72ec\u81c2\u795e\u5c3c\u95e8\u4e0b\u6392\u884c\u7b2c\u516d\u7684\u674e\u6e90\u3002\u4ed6\u662f\u6e56\u5357\u7684\u4e16 \u5bb6\u516c\u5b50\uff0c\u5341\u5e74\u6765\u8db3\u8ff9\u4e0d\u51fa\u4e24\u6e56\u3002\u6700\u8fd1\u5f97\u5230\u7518\u51e4\u6c60\u7684\u4f20\u4e66\uff0c\u8981\u4ed6\u5728\u6e05\u660e\u4e4b\u65e5\uff0c\u5230\u9099\u5c71\u796d\u626b\u5e08\u5085 \u575f\u5893\u3002\u5e76\u8bf4\u660e\u8fd9\u662f\u540c\u95e8\u7684\u4e00\u6b21\u5927\u805a\u96c6\uff0c\u4e0d\u80fd\u4e0d\u6765\u3002\u56e0\u6b64\u674e\u6e90\u624d\u5355\u8eab\u5317\u4e0a\u3002 \u3000\u3000\u674e\u6e90\u867d\u7136\u591a\u5e74\u6765\u8db3\u8ff9\u4e0d\u51fa\u4e24\u6e56\uff0c\u540c\u95e8\u4e4b\u95f4\uff0c\u5374\u662f\u4e92\u901a\u58f0\u6c14\uff0c\u5bf9\u767d\u6cf0\u5b98\u8fd9\u4e8b\uff0c\u9690\u6709\u6240\u95fb\uff0c \u542c\u4e86\u51af\u7433\u7684\u8bdd\uff0c\u7591\u5979\u5c31\u662f\u9c7c\u5a18\u3002\u53ef\u662f\u770b\u5979\u53ea\u662f\u5341\u51e0\u5c81\u7684\u5e7c\u5973\uff0c\u800c\u767d\u6cf0\u5b98\u4e4b\u4e8b\uff0c\u4e94\u516d\u5e74\u524d\u5df2\u70e9 \u7099\u4eba\u53e3\uff0c\u4f20\u904d\u6c5f\u6e56\u3002\u636e\u6b64\u770b\u6765\uff0c\u9c7c\u5a18\u4e0d\u5e94\u5982\u6b64\u5e74\u8f7b\u3002\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u5973\u5b50\u9a7b\u989c\u6709\u672f\uff0c\u542c\u8bf4\u516b\u59b9 \u5415\u56db\u5a18\u5c31\u4e00\u76f4\u50cf\u4e2a\u5eff\u4e00\u5c81\u5de6\u53f3\u7684\u5c11\u5973\u3002\u90a3\u4e48\u9c7c\u5a18\u82e5\u5f97\u5f02\u4eba\u4f20\u6388\uff0c\u4fdd\u7740\u9752\u6625\u9762\u8c8c\uff0c\u4e5f\u4e0d\u8db3\u4e3a \u5947\u3002\u5fc3\u9053\uff1a\u201c\u4e0d\u7ba1\u5979\u662f\u4e0d\u662f\u9c7c\u5a18\uff0c\u6211\u4e14\u548c\u5979\u540c\u8d70\u4e00\u7a0b\uff0c\u540e\u65e5\u53ef\u5230\u9099\u5c71\uff0c\u53ea\u8981\u78b0\u7740\u5468\u4e8c\u54e5\u6216\u7518 \u4e03\u5f1f\u4e00\u95ee\uff0c\u5b9a\u53ef\u77e5\u9053\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u51af\u7433\u6362\u4e86\u7537\u5b50\u670d\u9970\uff0c\u4e70\u4e86\u4e00\u9a91\u5065\u9a6c\uff0c\u679c\u7136\u968f\u7740\u674e\u6e90\u540c\u884c\u3002\u8def\u4e0a\u4e24\u4eba\u5404\u81ea\u51fa\u8a00\u8bd5 \u63a2\uff0c\u51af\u7433\u4e56\u5de7\u4e4b\u6781\uff0c\u674e\u6e90\u54ea\u91cc\u8bd5\u5f97\u51fa\u6765\u3002\u8d70\u4e86\u4e00\u7a0b\uff0c\u4e24\u4eba\u4e0b\u9a6c\u4f11\u606f\uff0c\u5728\u6811\u836b\u4e0b\u804a\u5929\u3002\u674e\u6e90 \u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u6b66\u529f\u597d\u7684\u5973\u5b50\u5c48\u6307\u53ef\u6570\uff0c\u9664\u4e86\u5415\u56db\u5a18\u5916\uff0c\u5c31\u8be5\u6570\u5230\u9c7c\u5a18\u4e86\u3002\u201d\u51af\u7433\u5fae\u5fae\u4e00\u7b11\uff0c \u5fc3\u4e2d\u5927\u4e0d\u4ee5\u4e3a\u7136\u3002\u674e\u6e90\u53c8\u9053\uff1a\u201c\u4f60\u7684\u6b66\u529f\u4e5f\u7b97\u5f97\u662f\u4e0a\u4e0a\u7684\u4e86\u3002\u6211\u770b\u4f60\u7eb5\u7b97\u6bd4\u4e0d\u4e0a\u5415\u56db\u5a18\uff0c\u4e5f \u603b\u53ef\u6bd4\u5f97\u4e0a\u9c7c\u5a18\u3002\u201d\u51af\u7433\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u5979\u4eec\u4e24\u4f4d\u5417\uff1f\u201d\u674e\u6e90\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6ca1 \u6709\u89c1\u8fc7\u3002\u201d\u8fd9\u8bdd\u786e\u662f\u5b9e\u8a00\u3002\u51af\u7433\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u672a\u89c1\u8fc7\u5979\u4eec\u4e8c\u4eba\uff0c\u53c8\u600e\u77e5\u5979\u4eec\u6b66\u529f\u6df1\u6d45\uff0c\u80e1\u4e71 \u6765\u6bd4\u5462\u3002\u201d\u674e\u6e90\u672c\u662f\u51fa\u8a00\u8bd5\u5979\uff0c\u4e0d\u6599\u7ed9\u5979\u95ee\u5012\u3002\u5f3a\u7b11\u9053\uff1a\u201c\u867d\u7136\u6ca1\u6709\u89c1\u8fc7\uff0c\u53ef\u662f\u542c\u6c5f\u6e56\u4e0a\u670b \u53cb\u6240\u8c08\uff0c\u4e5f\u603b\u53ef\u77e5\u4e2a\u5927\u6982\u3002\u4f60\u89c1\u8fc7\u5979\u4eec\u5417\uff1f\u201d\u51af\u7433\u7b11\u9053\uff1a\u201c\u6211\u5012\u771f\u7684\u89c1\u8fc7\uff0c\u5415\u56db\u5a18\u7684\u6b66\u529f\uff0c \u90a3\u662f\u4eba\u4e2d\u5c11\u6709\u3002\u9c7c\u5a18\u867d\u4f1a\u6b66\u827a\uff0c\u5374\u4e0d\u89c1\u5f97\u5982\u4f55\u3002\u201d\u51af\u7433\u7684\u8bdd\u4e5f\u662f\u5b9e\u8a00\u3002\u5979\u524d\u5e74\u5728\u676d\u5dde\u201c\u4e09\u6f6d \u5370\u6708\u201d\u4e4b\u65f6\uff0c\u66fe\u89c1\u8fc7\u5415\u56db\u5a18\u4e0e\u9c7c\u5a18\u548c\u4e86\u56e0\u52a8\u624b\u3002\u674e\u6e90\u542c\u5979\u5927\u8d5e\u5415\u56db\u5a18\u800c\u8d2c\u4f4e\u9c7c\u5a18\uff0c\u8d8a\u53d1\u6000\u7591 \u5979\u5c31\u662f\u9c7c\u5a18\u672c\u4eba\u3002\u6b63\u60f3\u51fa\u8a00\u518d\u8bd5\uff0c\u51af\u7433\u5ffd\u9053\uff0c\u201c\u5feb\u8d70\uff0c\u5feb\u8d70\uff01\u201d\u674e\u6e90\u62ac\u5934\u4e00\u770b\uff0c\u89c1\u4e00\u4e2a\u80d6\u548c \u5c1a\u63d0\u7740\u7985\u6756\u5927\u6b65\u8d70\u6765\uff0c\u6b63\u662f\u81ea\u5df1\u7684\u5927\u5e08\u5144\u4e86\u56e0\u3002\u674e\u6e90\u5403\u4e86\u4e00\u60ca\uff0c\u4e86\u56e0\u5df2\u5927\u58f0\u558a\u9053\uff1a\u201c\u516d\u5f1f\u6162 \u8d70\uff0c\u54b1\u4eec\u5feb\u6709\u5341\u5e74\u4e0d\u89c1\u4e86\u5566\uff01\u201d\u51af\u7433\u89c1\u674e\u6e90\u4e0d\u8d70\uff0c\u5fc3\u60f3\u6211\u82e5\u4e00\u8d70\uff0c\u9a6c\u4e0a\u5c31\u8981\u7ed9\u4e86\u56e0\u770b\u7834\u3002\u4ed6 \u6b66\u529f\u6781\u5f3a\uff0c\u593a\u9a6c\u8ffd\u6765\uff0c\u6211\u5b9a\u9003\u8d70\u4e0d\u4e86\uff0c\u4e5f\u4fbf\u6545\u4f5c\u9547\u5b9a\uff0c\u95f2\u95f2\u7684\u501a\u5728\u6811\u65c1\u3002\u5fc3\u4e2d\u76d8\u7b97\u8131\u8eab\u4e4b\u8ba1\u3002 \u3000\u3000\u674e\u6e90\u5341\u5e74\u6765\u672a\u89c1\u8fc7\u4e86\u56e0\uff0c\u4f46\u4ece\u540c\u95e8\u53e3\u4e2d\uff0c\u5bf9\u4e86\u56e0\u8fd1\u5e74\u7684\u884c\u4e8b\uff0c\u5374\u662f\u4e86\u5982\u6307\u638c\u3002\u5fc3\u4e2d\u6697\u6697 \u53eb\u82e6\u3002\u4e86\u56e0\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u597d\uff1f\u8fd9\u4f4d\u670b\u53cb\u662f\u8c01\uff1f\u201d\u674e\u6e90\u6b66\u529f\uff0c\u540c\u5176\u4ed6\u516d\u4f4d\u540c\u95e8\u4e00\u6837\uff0c\u5927\u534a\u51fa \u4e8e\u4e86\u56e0\u6240\u6388\uff0c\u867d\u7136\u77e5\u4ed6\u5df2\u662f\u80cc\u5e08\u53db\u9053\uff0c\u8fd8\u662f\u606d\u656c\u7684\u95ee\u597d\u3002\u7b54\u9053\uff1a\u201c\u6258\u5e87\u5e73\u5b89\u3002\u8fd9\u4f4d\u670b\u53cb\u662f\u8def \u4e0a\u78b0\u5230\u7684\u3002\u201d\u4e86\u56e0\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u5b9a\u775b\u6ce8\u89c6\u51af\u7433\u3002\u674e\u6e90\u9053\uff1a\u201c\u542c\u8bf4\u5e08\u5144\u8fd1\u5e74\u5f97\u610f\u3002\u201d\u4e86\u56e0 \u9053\uff1a\u201c\u5514\uff0c\u4f60\u4eec\u505a\u5e08\u5f1f\u7684\u5f88\u4e0d\u9ad8\u5174\u662f\u5417\uff1f\u201d\u674e\u6e90\u4e0d\u6562\u7b54\u8bdd\uff0c\u4e86\u56e0\u4ecd\u7136\u671b\u7740\u51af\u7433\uff0c\u674e\u6e90\u5fc3\u4e2d\u9887 \u602a\u51af\u7433\u4e0d\u61c2\u6760\u6e56\u793c\u8282\uff0c\u8d70\u8fc7\u53bb\u5bf9\u5979\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6211\u7684\u5e08\u5144\u4e86\u56e0\uff0c\u4f60\u8fc7\u6765\u89c1\u89c1\u3002\u201d\u51af\u7433\u628a\u624b\u4e00 \u626c\uff0c\u84e6\u7136\u98de\u51fa\u4e09\u67c4\u5315\u9996\uff0c\u4e00\u67c4\u5c04\u674e\u6e90\u5750\u9a6c\uff0c\u4e24\u67c4\u5374\u5c04\u674e\u6e90\uff0c\u674e\u6e90\u5927\u5403\u4e00\u60ca\uff0c\u731d\u4e0d\u53ca\u9632\uff0c\u95ea\u5f00 \u4e00\u628a\uff0c\u5374\u7ed9\u53e6\u4e00\u628a\u5c04\u4e2d\u5de6\u80a9\uff0c\u6251\u901a\u5012\u4e0b\uff0c\u51af\u7433\u98de\u8eab\u8df3\u4e0a\u9a6c\u80cc\uff0c\u7528\u5315\u9996\u5728\u9a6c\u81c0\u4e00\u63d2\uff0c\u90a3\u9a6c\u8d1f\u75db \u72c2\u5636\uff0c\u98de\u5954\u800c\u53bb\uff01 \u3000\u3000\u539f\u6765\u51af\u7433\u542c\u5f97\u674e\u6e90\u4e0e\u4e86\u56e0\u5144\u5f1f\u76f8\u79f0\uff0c\u5fc3\u4e2d\u6697\u60f3\uff1a\u82e5\u4e0d\u5feb\u9003\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u4ed6\u4eec\u5e08\u5144\u5f1f\u4e00\u8bf4 \u660e\u767d\uff0c\u6211\u5c31\u7cdf\u4e86\u3002\u5979\u5e74\u7eaa\u867d\u5c0f\uff0c\u8ba1\u8c0b\u5374\u591a\uff0c\u77e5\u9053\u4e86\u56e0\u6b66\u529f\u6781\u9ad8\uff0c\u98de\u5200\u5b9a\u7136\u5c04\u4ed6\u4e0d\u4e2d\uff0c\u6240\u4ee5\u6539 \u5c04\u674e\u6e90\u3002\u5fc3\u60f3\uff1a\u4ed6\u5e08\u5f1f\u53d7\u4e86\u5200\u4f24\uff0c\u4ed6\u5b9a\u7136\u65e0\u6687\u8ffd\u6211\u3002\u51af\u7433\u60f3\u5f97\u4e0d\u9519\uff0c\u53ef\u662f\u5374\u65e0\u8f9c\u4f24\u4e86\u674e\u6e90\u3002 \u3000\u3000\u4e86\u56e0\u89c1\u51af\u7433\u4e0a\u9a6c\u98de\u5954\uff0c\u8ffd\u4e4b\u4e0d\u53ca\u3002\u679c\u7136\u5148\u6551\u6cbb\u674e\u6e90\u3002\u4e86\u56e0\u5bf9\u51af\u7433\u7684\u6bd2\u5200\u6765\u5386\uff0c\u751a\u4e3a\u6e05 \u695a\uff0c\u56ca\u4e2d\u867d\u65e0\u5bf9\u75c7\u7684\u89e3\u836f\uff0c\u4f46\u7acb\u523b\u66ff\u674e\u6e90\u525c\u8089\u7597\u6bd2\uff0c\u7528\u5927\u5185\u7684\u91d1\u521b\u5723\u836f\u6577\u4e0a\uff0c\u4e5f\u53ef\u4fdd\u65e0\u4e8b\u3002 \u4e86\u56e0\u5012\u4e0d\u662f\u7279\u522b\u75db\u7231\u8fd9\u4e2a\u5e08\u5f1f\uff0c\u800c\u662f\u60f3\u628a\u4ed6\u533b\u597d\u4e4b\u540e\uff0c\u903c\u4ed6\u542c\u81ea\u5df1\u7684\u8bdd\uff0c\u968f\u81ea\u5df1\u5165\u4eac\u3002\u4e86\u56e0 \u6295\u4e86\u5141\u796f\u4e4b\u540e\uff0c\u516d\u4e2a\u5e08\u5f1f\uff0c\u65e0\u4e00\u4ece\u4ed6\uff0c\u4ed6\u6df1\u89c9\u989c\u9762\u65e0\u5149\u3002\u674e\u6e90\u5728\u6c5f\u5357\u4e03\u4fa0\u4e2d\u7684\u540d\u6c14\uff0c\u867d\u4e0d\u5982 \u7518\u51e4\u6c60\u4e0e\u767d\u6cf0\u5b98\u4e4b\u5927\uff0c\u4f46\u903c\u5f97\u4ed6\u4ece\u987a\uff0c\u603b\u53ef\u633d\u56de\u4e00\u70b9\u9762\u5b50\u3002\u514d\u5f97\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u5632\u7b11\uff0c\u8bf4\u4ed6\u81ea \u547d\u662f\u201c\u6c5f\u5357\u4e03\u4fa0\u201d\u4e4b\u9996\uff0c\u8fde\u81ea\u5df1\u4eb2\u624b\u6559\u8fc7\u7684\u5e08\u5f1f\u90fd\u4e0d\u8ddf\u4ed6\u3002 \u3000\u3000\u4e86\u56e0\u66ff\u674e\u6e90\u89e3\u4e86\u5916\u8863\uff0c\u525c\u8089\u7597\u6bd2\uff0c\u624b\u6709\u6240\u89e6\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u641c\u7d22\u674e\u6e90\u6000\u4e2d\u7269\u4ef6\uff0c\u5728\u8d34\u8eab\u5185 \u8863\u4e4b\u5185\uff0c\u641c\u51fa\u4e86\u4e00\u4e2a\u814a\u4e38\uff0c\u4e86\u56e0\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u628a\u814a\u4e38\u6390\u788e\uff0c\u91cc\u9762\u85cf\u6709\u4e00\u5f20\u5b57\u6761\uff0c\u53d6\u51fa \u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u6e56\u5357\u66fe\u9759\uff0c\u5199\u7ed9\u5cb3\u949f\u6dc7\u7684\u5bc6\u51fd\u3002\u66fe\u9759\u548c\u5cb3\u949f\u6dc7\u7684\u7236\u4eb2\u4e43\u662f\u670b\u53cb\uff0c\u8fd9\u5c01\u4fe1\u662f\u529d \u5cb3\u949f\u6dc7\u5728\u53d6\u5f97\u5175\u6743\u4e4b\u540e\uff0c\u4e3e\u5175\u6297\u6e05\u7684\u3002\u4fe1\u4e2d\u8fd8\u62ac\u51fa\u5cb3\u59d3\u7684\u5148\u8d24\u5cb3\u98de\uff0c\u529d\u5cb3\u949f\u6dc7\u5b66\u5cb3\u98de\u7684\u6a21 \u6837\uff0c\u62b5\u6297\u5f02\u65cf\u4fb5\u7565\u3002\u4e86\u56e0\u867d\u7136\u8bc6\u5b57\u65e0\u591a\uff0c\u610f\u601d\u5374\u8fd8\u770b\u5f97\u660e\u767d\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u77a7\u4e0d\u51fa\u8fd9\u4f4d\u516c\u5b50 \u54e5\u513f\u8fd8\u4f1a\u6765\u8fd9\u4e00\u624b\u3002\u201d\u7ee7\u800c\u4e00\u60f3\uff0c\u5cb3\u949f\u6dc7\u4e43\u662f\u5e74\u7fb9\u5c27\u7684\u526f\u624b\uff0c\u6709\u4e86\u8fd9\u5c01\u5bc6\u4ef6\uff0c\u4fbf\u53ef\u4f5c\u4e3a\u51ed \u8bc1\uff0c\u8fde\u5e74\u7fb9\u5c27\u4e5f\u6273\u5012\u4ed6\u3002\u5fc3\u5ff5\u6b64\u4ec7\u53ef\u62a5\uff0c\u4e0d\u89c9\u5927\u559c\u3002\u8fd9\u65f6\u4e86\u56e0\u5fc3\u601d\u5df2\u53d8\uff0c\u53ea\u60f3\u628a\u674e\u6e90\u62bc\u5230\u5317 \u4eac\u9886\u8d4f\uff0c\u4ed6\u987a\u4e0d\u987a\u4ece\uff0c\u5012\u65e0\u5173\u7d27\u8981\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\u3002\u674e\u6e90\u60a0\u60a0\u9192\u8f6c\uff0c\u53ea\u89c1\u4e86\u56e0\u624b\u4e2d\u62ff\u7740\u90a3\u5f20\u5b57\u6761\uff0c\u563b\u563b\u51b7\u7b11\uff0c\u5f97\u610f\u4e4b\u6781\u3002\u674e\u6e90\u51b7 \u6c57\u76f4\u6d41\uff0c\u77e5\u9053\u4e8b\u60c5\u5df2\u8d25\u9732\uff0c\u62fc\u547d\u8df3\u4e86\u8d77\u6765\uff0c\u4e86\u56e0\u4e00\u58f0\u51b7\u7b11\uff0c\u8f7b\u8f7b\u4e00\u63a8\uff0c\u4fbf\u5c06\u674e\u6e90\u63a8\u8dcc\u5730\u4e0a\u3002 \u3000\u3000\u4e86\u56e0\u559d\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u674e\u6e90\u56b7\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u662f\u4e0d\u662f\u6c49\u4eba\uff1f\u201d\u4e86\u56e0\u9053\uff1a\u201c\u662f\u6c49\u4eba\u53c8 \u600e\u6837\uff1f\u201d\u674e\u6e90\u9053\uff1a\u201c\u662f\u6c49\u4eba\u5c31\u8be5\u628a\u8fd9\u5f20\u5b57\u6761\u8fd8\u6211\u3002\u201d\u4e86\u56e0\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4e56\u4e56\u7684\u968f \u6211\u5230\u5317\u4eac\u53bb\u3002\u201d\u674e\u6e90\u53c8\u9053\uff1a\u201c\u4f60\u5b8c\u5168\u5fd8\u8bb0\u4e86\u5e08\u7236\u7684\u5429\u5490\u4e48\uff1f\u201d\u4e86\u56e0\u72c2\u7b11\u9053\uff1a\u201c\u5e08\u5085\u65e2\u6b7b\uff0c\u6211 \u5c31\u662f\u4f60\u4eec\u7684\u5e08\u5085\u3002\u201d\u674e\u6e90\u6012\u9053\uff1a\u201c\u597d\u54c7\uff0c\u4e86\u56e0\uff01\u4f60\u628a\u6211\u6740\u4e86\u5427\uff01\u201d\u4e86\u56e0\u9053\uff1a\u201c\u4f60\u8981\u6b7b\u8fd8\u4e0d\u5bb9 \u6613\uff0c\u53ea\u662f\u4f60\u4e5f\u77e5\u9053\u6211\u7684\u624b\u6bb5\uff0c\u4f60\u60f3\u6211\u7528\u5206\u7b4b\u9519\u9aa8\u7684\u624b\u6cd5\uff0c\u628a\u4f60\u714e\u76ae\u62c6\u9aa8\uff1b\u8fd8\u662f\u60f3\u6211\u7528\u70b9\u7a74\u7684 \u624b\u6cd5\uff0c\u8ba9\u4f60\u843d\u4e2a\u5168\u5c38\uff0c\u800c\u4e14\u8fd8\u53ef\u4ee5\u518d\u6d3b\u4e09\u65e5\uff1f\u201d\u8fd9\u4e24\u79cd\u624b\u6cd5\u90fd\u662f\u6781\u5389\u5bb3\u7684\u5211\u7f5a\uff0c\u5206\u7b4b\u9519\u9aa8\u60e8 \u4e8e\u788e\u5250\u51cc\u8fdf\uff0c\u4f46\u82e5\u88ab\u4ed6\u7528\u6bd2\u8fa3\u7684\u624b\u6cd5\u70b9\u7a74\uff0c\u5219\u6c42\u751f\u4e0d\u5f97\uff0c\u6c42\u6b7b\u4e0d\u80fd\uff0c\u6bd4\u788e\u5250\u51cc\u8fdf\u8fd8\u8981\u75db\u82e6\u3002 \u674e\u6e90\u51fa\u8eab\u5bcc\u5bb6\uff0c\u4e0d\u89c9\u5fc3\u60b8\u3002\u4e86\u56e0\u89c1\u4ed6\u9762\u8272\u60e8\u767d\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u6e05\u695a\u6ca1\u6709\uff1f\u201d\u674e\u6e90\u66fe\u8bfb\u8bd7\u4e66\uff0c \u60f3\u8d77\u201c\u4eba\u751f\u81ea\u53e4\u8c01\u65e0\u6b7b\uff0c\u7559\u53d6\u4e39\u5fc3\u7167\u6c57\u9752\u201d\u4e24\u53e5\uff0c\u80c6\u6c14\u987f\u58ee\uff0c\u65a5\u9053\uff1a\u201c\u4e0d\u5fc5\u591a\u8a00\uff0c\u6211\u82e5\u6015 \u6b7b\uff0c\u4e5f\u7b97\u4e0d\u5f97\u662f\u6c5f\u5357\u4e03\u4fa0\u4e86\uff01\u201d \u3000\u3000\u4e86\u56e0\u4e00\u58f0\u72de\u7b11\uff0c\u628a\u674e\u6e90\u62c9\u8fd1\u8eab\u8fb9\uff0c\u62ac\u8d77\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u7167\u674e\u6e90\u540e\u9888\u634f\u4e0b\u3002\u674e\u6e90\u7791\u76ee\u5f85 \u6b7b\uff0c\u4f46\u542c\u5f97\u4e86\u56e0\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u4eca\u65e5\u4ee5\u540e\uff0c\u6c5f\u5357\u4e03\u4fa0\uff0c\u5c31\u53ea\u5269\u4e0b\u516d\u4eba\u3002\u4f60\u53e3\u53e3\u58f0\u58f0\u8981\u9075\u5e08\u5085\u7684 \u9057\u8bad\uff0c\u5c31\u8ba9\u4f60\u53bb\u89c1\u5e08\u5085\u5427\uff01\u54c8\uff0c\u6b64\u5730\u79bb\u9099\u5c71\u5df2\u8fd1\uff0c\u4f60\u8981\u4e0d\u8981\u6211\u5c06\u4f60\u846c\u5728\u5e08\u5085\u5893\u65c1\uff1f\u201d\u674e\u6e90\u5fc3 \u5ff5\u4e00\u52a8\uff0c\u60f3\u8d77\u660e\u65e5\u53ef\u5230\u9099\u5c71\uff0c\u53c8\u60f3\u8d77\u4e00\u4f17\u540c\u95e8\uff0c\u90fd\u5728\u9099\u5c71\u805a\u4f1a\u3002\u518d\u60f3\u8d77\u60e8\u6b7b\u867d\u65e0\u8db3\u4ff1\uff0c\u4f46\u82e5 \u6709\u4e00\u7ebf\u751f\u673a\uff0c\u4e5f\u4e0d\u8be5\u653e\u8fc7\u3002\u4e86\u56e0\u624b\u6307\u5df2\u89e6\u5230\u4ed6\u7684\u9888\u9aa8\uff0c\u505c\u4e86\u4e00\u505c\uff0c\u674e\u6e90\u5927\u58f0\u56b7\u9053\uff1a\u201c\u5e08\u5144\uff0c \u6211\u8fd8\u662f\u542c\u4f60\u5429\u5490\u3002\u201d\u4e86\u56e0\u54c8\u54c8\u4e00\u7b11\uff0c\u677e\u5f00\u4e86\u624b\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4e2a\u516c\u5b50\u54e5\u513f\uff0c\u679c\u7136\u7ecf\u4e0d\u8d77\u6211\u7684\u4e00 \u5413\uff01\u201d\u674e\u6e90\u9053\uff1a\u201c\u6211\u968f\u4f60\u5230\u5317\u4eac\u53bb\uff0c\u4f46\u8bf7\u4f60\u4e0d\u8981\u7275\u7d2f\u66fe\u8001\u5148\u751f\u3002\u201d\u4e86\u56e0\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a \u201c\u5514\uff0c\u8fd9\u4e2a\u4ee5\u540e\u8bf4\u5427\u3002\u53ea\u8981\u4f60\u4e56\u4e56\u542c\u8bdd\u3002\u6211\u4e5f\u8bb8\u53ef\u4ee5\u624b\u4e0b\u7559\u60c5\u3002\u201d\u4e86\u56e0\u53c8\u95ee\u8d77\u51af\u7433\u4f55\u4ee5\u4f1a\u548c \u4ed6\u4e00\u9053\uff0c\u674e\u6e90\u4f9d\u76f4\u8bf4\u4e86\uff0c\u4e86\u56e0\u77e5\u4ed6\u5341\u5e74\u6765\u8db3\u8ff9\u4e0d\u51fa\u4e24\u6e56\uff0c\u6599\u4ed6\u6240\u8a00\u975e\u5047\u3002\u4e0d\u518d\u591a\u95ee\uff0c\u62bc\u4ed6\u4e0a \u8def\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e2d\u5348\u65f6\u5206\uff0c\u7ecf\u8fc7\u9099\u5c71\u4e0b\uff0c\u4e86\u56e0\u8033\u542c\u5c71\u4e0b\u7684\u9ec4\u6cb3\u6c34\u58f0\u8f70\u9e23\uff0c\u773c\u770b\u5d24\u5c71\u9099\u5c71\uff0c\u8fce\u9762\u77d7 \u7acb\uff0c\u60f3\u8d77\u5f53\u5e74\u5728\u6b64\u5c71\u4e60\u6280\u7684\u5f80\u4e8b\uff0c\u4e0d\u89c9\u6e38\u76ee\u56db\u987e\uff0c\u56de\u5fc6\u65e7\u65e5\u6e38\u8e2a\u3002\u674e\u6e90\u5ffd\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4eca\u65e5 \u662f\u4ec0\u4e48\u65e5\u5b50\uff1f\u201d\u4e86\u56e0\u9053\uff1a\u201c\u8c01\u8010\u70e6\u53bb\u8bb0\u3002\u201d\u674e\u6e90\u9053\uff1a\u201c\u4eca\u65e5\u6b63\u662f\u6e05\u660e\u8282\u5440\uff01\u201d\u4e86\u56e0\u4e00\u7b97\uff0c\u679c \u7136\u4e0d\u9519\uff0c\u9053\uff1a\u201c\u662f\u6e05\u660e\u53c8\u600e\u4e48\u6837\uff1f\u201d\u674e\u6e90\u9053\uff1a\u201c\u5e08\u5085\u6b7b\u540e\uff0c\u5c0f\u5f1f\u5c61\u6b21\u60f3\u6765\u626b\u5893\uff0c\u90fd\u56e0\u5bb6\u4e8b\u7f81 \u7eca\uff0c\u672a\u80fd\u524d\u6765\u3002\u5fc3\u4e2d\u60ed\u6127\u5df2\u6781\u3002\u4eca\u65e5\u6e05\u660e\u4f73\u8282\uff0c\u6070\u6070\u7ecf\u8fc7\u6b64\u5c71\u3002\u5c0f\u5f1f\u6c42\u5e08\u5144\u5f00\u6069\uff0c\u51c6\u6211\u4e0a\u5c71 \u4e00\u62dc\u3002\u201d\u4e86\u56e0\u9762\u4e0a\u4e00\u7ea2\uff0c\u60f3\u8d77\u5e08\u5085\u6b7b\u540e\uff0c\u81ea\u5df1\u4e5f\u4ece\u672a\u6765\u796d\u626b\u8fc7\u3002\u4e86\u56e0\u5bf9\u72ec\u81c2\u795e\u5c3c\u4e4b\u6b7b\u3001\u867d\u7136 \u6f20\u4e0d\u5173\u5fc3\uff0c\u4f46\u72ec\u81c2\u795e\u5c3c\u5230\u5e95\u662f\u4ed6\u7684\u6069\u5e08\uff0c\u65e2\u7136\u7ecf\u8fc7\u6b64\u5c71\uff0c\u53c8\u6070\u503c\u6e05\u660e\u8282\u65e5\uff0c\u4e5f\u6cb9\u7136\u8d77\u4e86\u626b\u5893 \u4e4b\u5fc3\uff0c\u6168\u7136\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u54b1\u4eec\u5c31\u4e0a\u5c71\u4e00\u62dc\u3002\u201d\u7275\u7740\u674e\u6e90\u7684\u624b\uff0c\u76f4\u4e0a\u9099\u5c71\u3002 \u3000\u3000\u9099\u5c71\u6625\u65e5\uff0c\u98ce\u666f\u7edd\u4f73\uff0c\u5c71\u82b1\u904d\u5730\uff0c\u6709\u7ea2\u91cc\u53c2\u767d\u50cf\u5927\u7ea2\u739b\u7459\u7684\u8336\u82b1\uff1b\u6709\u6843\u7ea2\u82b1\u74e3\u5305\u7740\u91d1\u4e1d \u82b1\u854a\u7684\u675c\u9e43\u82b1\uff1b\u6709\u9752\u4e1d\u82b1\u854a\u9576\u7740\u4e73\u767d\u82b1\u74e3\u7684\u62a5\u6625\u82b1\u3002\u5bc6\u5bc6\u4e1b\u4e1b\uff0c\u6ee1\u773c\u90fd\u662f\u3002\u8d70\u4e86\u4e00\u9635\uff0c\u5df2\u5230 \u4e3b\u5cf0\uff0c\u5c71\u5cf0\u4e0a\u6302\u4e0b\u7684\u7011\u5e03\uff0c\u5728\u4e3d\u65e5\u4e0b\u6d12\u8d77\u91d1\u8272\u73cd\u73e0\u7684\u6ce1\u6cab\u3002\u4e86\u56e0\u867d\u7136\u8d2a\u604b\u7e41\u534e\uff0c\u5230\u6b64\u4e5f\u4e0d\u7981 \u7cbe\u795e\u4e00\u723d\u3002\u62ac\u5934\u4e00\u671b\uff0c\u8fd8\u8bb0\u5f97\u524d\u9762\u90a3\u5ea7\u7a81\u51fa\u7684\u5c71\u5cf0\uff0c\u5c31\u662f\u5f53\u5e74\u5e08\u5085\u6559\u81ea\u5df1\u8f7b\u529f\u7684\u5730\u65b9\uff0c\u53ea\u56e0 \u4e3a\u81ea\u5df1\u5e7c\u5e74\u65f6\u6839\u57fa\u624e\u5f97\u4e0d\u597d\uff0c\u8f7b\u529f\u603b\u7ec3\u4e0d\u5230\u767b\u5cf0\u9020\u6781\u3002\u90a3\u8fb9\u6d41\u6cc9\u98de\u7011\u4e4b\u65c1\u7684\u5927\u77f3\uff0c\u5374\u662f\u81ea\u5df1 \u8bd5\u6756\u4e4b\u5730\uff0c\u77f3\u4e0a\u7559\u4e0b\u7684\u6756\u75d5\u60f3\u5fc5\u8fd8\u5728\u3002\u4e86\u56e0\u8fb9\u60f3\u8fb9\u8d70\uff0c\u4e0d\u591a\u4e00\u4f1a\uff0c\u548c\u674e\u6e90\u6500\u5230\u5cf0\u9876\u3002 \u3000\u3000\u5c71\u9876\u4e0a\uff0c\u72ec\u81c2\u795e\u5c3c\u6240\u517b\u7684\u9ed1\u767d\u4e8c\u96d5\u5728\u7a7a\u4e2d\u76d8\u65cb\u98de\u9e23\uff0c\u4f3c\u4e4e\u662f\u51fa\u6765\u63a5\u5ba2\u3002\u4e86\u56e0\u53eb\u4e86\u58f0\uff1a \u201c\u5c0f\u9ed1\u5c0f\u767d\u201d\uff0c\u9ed1\u767d\u4e8c\u96d5\u547c\u7684\u4e00\u58f0\u4ece\u4e86\u56e0\u5934\u9876\u63a0\u8fc7\uff0c\u4e86\u56e0\u9053\uff1a\u201c\u54a6\uff0c\u5c0f\u9ed1\u5c0f\u767d\u4e5f\u4e0d\u7406\u6211 \u4e86\uff01\u201d\u84e6\u7136\u60f3\u8d77\u81ea\u5df1\u4eca\u5e74\u5df2\u8fd1\u516d\u5341\uff0c\u79bb\u5f00\u9099\u5c71\u4e5f\u6709\u4e8c\u5341\u591a\u5e74\u4e86\uff0c\u201c\u5c0f\u9ed1\u5c0f\u767d\u201d\u4e5f\u5e94\u5f53\u6539\u79f0 \u201c\u8001\u9ed1\u8001\u767d\u201d\u4e86\u3002 \u3000\u3000\u4e8c\u96d5\u51fa\u73b0\uff0c\u674e\u6e90\u7cbe\u795e\u4e00\u632f\uff0c\u8d70\u8fc7\u4e24\u884c\u69d0\u6811\u5939\u7740\u7684\u5c71\u9053\uff0c\u5893\u56ed\u9065\u9065\u5728\u671b\u3002\u4e86\u56e0\u5ffd\u9053\uff1a \u201c\u54a6\uff0c\u90a3\u8fb9\u6709\u4eba\u3002\u60f3\u662f\u90a3\u4f4d\u540c\u95e8\u4e5f\u4e0a\u6765\u796d\u626b\u4e86\u3002\u201d\u8bdd\u58f0\u672a\u505c\uff0c\u5415\u56db\u5a18\u548c\u7518\u51e4\u6c60\u7a81\u7136\u73b0\u51fa\u8eab \u5f62\uff0c\u4e86\u56e0\u9aa4\u5403\u4e00\u60ca\uff0c\u7ffb\u8eab\u4fbf\u6293\u674e\u6e90\uff0c\u674e\u6e90\u65e9\u5df2\u8df3\u5f00\uff0c\u5415\u56db\u5a18\u64ae\u5507\u4e00\u5578\uff0c\u9ed1\u767d\u4e8c\u96d5\u51cc\u7a7a\u51fb\u4e0b\uff0c \u4e86\u56e0\u5927\u6012\uff0c\u8fd9\u9ed1\u767d\u4e8c\u96d5\u7adf\u7136\u542c\u5415\u56db\u5a18\u7684\u8bdd\u6765\u653b\u51fb\u81ea\u5df1\uff1b\u7985\u6756\u76d8\u5934\u4e00\u626b\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u6241 \u6bdb\u755c\u751f\u4e5f\u6765\u6b3a\u6211\uff01\u201d\u4e8c\u96d5\u9707\u4e8e\u4ed6\u7684\u5a01\u52bf\uff0c\u4e00\u63a0\u4e0b\u53c8\u98de\u8d77\uff0c\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u5415\u56db\u5a18\u5df2\u4e00\u5251\u98de \u524d\uff0c\u9661\u7136\u628a\u674e\u6e90\u62a2\u8d70\u3002 \u3000\u3000\u4e86\u56e0\u6c14\u7ea2\u4e86\u773c\uff0c\u5415\u56db\u5a18\u5fae\u5fae\u4e00\u7b11\uff0c\u5c71\u5934\u4e0a\u9ad8\u9ad8\u77ee\u77ee\uff0c\u4e09\u4e94\u6210\u7fa4\uff0c\u4e0d\u4f46\u516d\u4e2a\u5e08\u5f1f\u4e00\u4e2a\u4e0d \u7f3a\uff0c\u8fde\u5173\u4e1c\u56db\u4fa0\u548c\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\u7b49\u4eba\u4e5f\u90fd\u6765\u4e86\u3002\u4e8c\u5e08\u5f1f\u5468\u6e05\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e86\u56e0\uff0c\u4f60\u4e5f\u6765 \u4e48\uff1f\u201d \u3000\u3000\u674e\u6e90\u548c\u540c\u95e8\u89c1\u8fc7\uff0c\u59cb\u77e5\u521a\u624d\u62a2\u6551\u81ea\u5df1\u7684\u5c31\u662f\u5e08\u5085\u7684\u5173\u95e8\u5f92\u5f1f\u5415\u56db\u5a18\uff0c\u5341\u5206\u60ca\u8bb6\u3002\u7518\u51e4\u6c60 \u770b\u51fa\u4ed6\u80a9\u81c2\u53d7\u4f24\uff0c\u95ee\u4ed6\u6240\u4ee5\uff0c\u674e\u6e90\u4e00\u4e00\u8bf4\u4e86\uff0c\u7518\u51e4\u6c60\u6781\u4e3a\u6124\u6012\uff0c\u5bf9\u5468\u6e05\u9053\uff1a\u201c\u4e8c\u54e5\uff0c\u4ed6\u8d35\u4eba \u4e8b\u5fd9\uff0c\u90a3\u8fd8\u6709\u95f2\u5fc3\u5230\u6b64\u626b\u5893\uff0c\u4ed6\u662f\u62bc\u516d\u54e5\u5230\u4eac\u8bf7\u8d4f\uff0c\u8def\u7ecf\u6b64\u5730\uff0c\u516d\u54e5\u6c42\u4ed6\u2018\u6069\u51c6\u2019\u4e0a\u5c71\uff0c\u4ed6 \u6015\u2018\u72af\u4eba\u2019\u8131\u9003\uff0c\u6240\u4ee5\u624d\u8ddf\u4e0a\u6765\u7684\u3002\u201d\u4e86\u56e0\u9762\u8272\u7ea2\u91cc\u6cdb\u9752\uff0c\u7985\u6756\u4e00\u6325\uff0c\u628a\u4e00\u5757\u77f3\u5934\u6253\u5f97\u7c89 \u788e\uff0c\u6012\u9053\uff1a\u201c\u51e4\u6c60\uff0c\u4f60\u73b0\u5728\u5bf9\u8c01\u8bf4\u8bdd\uff01\u201d\u7518\u51e4\u6c60\u6273\u8138\u4e0d\u7b54\u3002\u5468\u6e05\u9053\uff1a\u201c\u96be\u5f97\u4f60\u4eca\u65e5\u5230\u6b64\uff0c\u8bf7 \u5230\u5e08\u5085\u5893\u524d\u8bf4\u5427\u3002\u201d \u3000\u3000\u4e86\u56e0\u5fc3\u4e2d\u76d8\u7b97\u8131\u9003\u4e4b\u8ba1\uff0c\u63d0\u8d77\u7985\u6756\uff0c\u50b2\u7136\u524d\u884c\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u5230\u4e86\u5893\u56ed\uff0c\u53ea\u89c1\u5893\u56ed\u7891\u4e0a\u5199 \u7740\u201c\u524d\u660e\u516c\u4e3b\u6b66\u6797\u4fa0\u5c3c\u4e4b\u5893\u201d\u5341\u4e2a\u5927\u5b57\uff0c\u5de6\u4e0b\u89d2\u5199\u7740\u95e8\u4eba\uff1a\u5468\u6e05\u3001\u8def\u6c11\u77bb\u3001\u66f9\u4ec1\u7236\u3001\u767d\u6cf0 \u5b98\u3001\u674e\u6e90\u3001\u7518\u51e4\u6c60\u3001\u5415\u56db\u5a18\u7acb\u3002\u5374\u65e0\u4e86\u56e0\u7684\u540d\u5b57\u3002\u4e86\u56e0\u7985\u6756\u987f\u5730\uff0c\u6012\u9053\uff1a\u201c\u7acb\u8fd9\u5893\u7891\u662f\u8c01\u7684 \u4e3b\u610f\uff1f\u4e0a\u9762\u7684\u5b57\u662f\u8c01\u5199\u7684\uff1f\u201d \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u662f\u6211\u6d3e\u4eba\u5f81\u6c42\u4e86\u4e00\u4f17\u540c\u95e8\u7684\u610f\u89c1\uff0c\u9e20\u5de5\u5efa\u7acb\u7684\u3002\u5893\u7891\u4e0a\u7684\u5b57\u662f\u5927\u5bb6\u516c\u51b3\u63a8 \u8def\u4e09\u54e5\u5199\u7684\uff0c\u600e\u4e48\u6837\uff1f\u201d\u4e86\u56e0\u9053\uff1a\u201c\u4e3a\u4f55\u4f60\u4e0d\u5411\u6211\u8bf7\u95ee\uff1f\u5893\u7891\u4e0a\u53c8\u4e3a\u4f55\u6ca1\u6709\u6211\u7684\u540d\u5b57\uff1f\u201d\u7518 \u51e4\u6c60\u95ed\u53e3\u4e0d\u7b54\u3002\u5468\u6e05\u9053\uff1a\u201c\u54b1\u4eec\u4e00\u4f17\u5c71\u95e8\u90fd\u5df2\u9f50\u96c6\uff0c\u73b0\u5728\u4f9d\u6b21\u884c\u793c\u3002\u201d\u4e03\u4e2a\u4eba\u6392\u6210\u4e00\u961f\uff0c\u7531 \u5468\u6e05\u9886\u524d\uff0c\u6b63\u62df\u884c\u793c\u3002\u4e86\u56e0\u4e00\u8dc3\u800c\u524d\uff0c\u547c\u7684\u4e00\u638c\u628a\u5468\u6e05\u63a8\u5f00\uff0c\u62a2\u5728\u524d\u5934\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c \u5415\u56db\u5a18\u51cc\u7a7a\u8dc3\u8d77\uff0c\u62a2\u5728\u5893\u524d\uff0c\u6a2a\u5251\u4e00\u5c01\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u540c\u95e8\u796d\u626b\uff0c\u656c\u8f9e\u5916\u4eba\u53c2\u9884\u3002\u4f60\u8981 \u8dea\u62dc\uff0c\u5e94\u7b49\u6211\u4eec\u796d\u8fc7\u4e4b\u540e\u3002\u8fd9\u70b9\u89c4\u77e9\uff0c\u4f60\u90fd\u4e0d\u61c2\u4e48\uff1f\u201d \u3000\u3000\u4e86\u56e0\u5927\u53eb\u9053\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff01\u6211\u5165\u5e08\u95e8\u4e4b\u65f6\u4f60\u8fd8\u672a\u51fa\u4e16\uff0c\u4f60\u5012\u5e72\u6d89\u8d77\u6211\u6765\u4e86\uff01\u201d\u5468\u6e05\u8dc3 \u4e0a\u4e00\u5757\u5ca9\u77f3\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u72ec\u81c2\u795e\u5c3c\u95e8\u4e0b\u5f1f\u5b50\u516c\u51b3\uff0c\u5728\u5e08\u5085\u7075\u524d\u796d\u544a\uff0c\u9010\u51fa\u53db\u5f92\u4e86\u56e0\uff0c\u8d81\u5404\u4f4d \u6b66\u6797\u524d\u8f88\u5728\u6b64\uff0c\u4eca\u65e5\u662d\u544a\u5929\u4e0b\uff01\u201d\u4e86\u56e0\u9661\u7684\u8df3\u4e0a\u77f3\u5ca9\uff0c\u5415\u56db\u5a18\u5df2\u62a2\u5148\u4e00\u6b65\uff0c\u7acb\u5728\u5468\u6e05\u8eab\u8fb9\u3002 \u5468\u6e05\u7eed\u9053\uff1a\u201c\u53db\u5f92\u4e86\u56e0\u80cc\u5e08\u53db\u9053\uff0c\u5f1f\u5b50\u7b49\u4eca\u65e5\u7980\u627f\u5e08\u5c0a\u9050\u8bad\uff0c\u6e05\u7406\u95e8\u6237\u3002\u613f\u5404\u4f4d\u6b66\u6797\u524d\u8f88\u4f5c \u8bc1\u3002\u201d\u5468\u6e05\u8bf4\u7f62\uff0c\u7518\u51e4\u6c60\u559d\u9053\uff1a\u201c\u4e86\u56e0\uff0c\u4f60\u8fd8\u8981\u6211\u4eec\u52a8\u624b\u5417\uff1f\u201d\u6c5f\u6e56\u4e0a\u66ff\u6b7b\u53bb\u7684\u5e08\u5085\u6e05\u7406\u95e8 \u6237\uff0c\u7b2c\u4e00\u6b65\u662f\u5148\u7531\u540c\u95e8\u516c\u51b3\uff0c\u628a\u53db\u5f92\u9010\u51fa\u95e8\u5899\uff1b\u7136\u540e\u624d\u9e23\u9f13\u800c\u653b\uff0c\u903c\u4ed6\u5728\u5e08\u5085\u7075\u524d\u81ea\u51b3\uff0c\u81ea \u5df1\u7ed9\u81ea\u5df1\u5b9a\u7f6a\uff0c\u81ea\u5df1\u7ed9\u81ea\u5df1\u6267\u6cd5\uff0c\u7f6a\u60c5\u91cd\u5927\u7684\uff0c\u5c31\u5f97\u81ea\u88c1\u3002\u82e5\u4ed6\u81ea\u5df1\u6240\u5b9a\u4e4b\u7f6a\u8fc7\u8f7b\uff0c\u540c\u95e8\u53ef \u4ee5\u7ea0\u6b63\u3002\u82e5\u4ed6\u4e0d\u670d\uff0c\u5219\u540c\u95e8\u4e2d\u4e0d\u8bba\u54ea\u4e2a\u90fd\u53ef\u6740\u4ed6\u3002\u4eca\u65e5\u4e86\u56e0\u81ea\u6295\u7f57\u7f51\uff0c\u6c5f\u5357\u4e03\u4fa0\u4fbf\u5f97\u5728\u4e00\u65e5 \u4e4b\u5185\uff0c\u5c06\u4e24\u4ef6\u4e8b\u90fd\u5e76\u5728\u4e00\u8d77\u529e\u4e86\u3002 \u3000\u3000\u4e86\u56e0\u563f\u563f\u51b7\u7b11\uff0c\u7a81\u7136\u53cd\u8eab\u4e00\u8dc3\uff0c\u7985\u6756\u5728\u534a\u7a7a\u821e\u4e86\u4e00\u9053\u5706\u5708\uff0c\u8def\u6c11\u77bb\u767d\u6cf0\u5b98\u6025\u5fd9\u95ea\u907f\uff0c\u4e86 \u56e0\u4e00\u63a0\u51b2\u51fa\uff0c\u8dd1\u4e0a\u5de6\u8fb9\u5c71\u5cf0\u3002\u90a3\u8fb9\u6b63\u662f\u5173\u4e1c\u56db\u4fa0\u7b49\u5916\u5bbe\u89c2\u672d\u4e4b\u5904\uff0c\u66f9\u4ec1\u7236\u7684\u5973\u513f\u66f9\u9526\u513f\u4e0e\u9c7c \u5a18\u4e00\u9053\uff0c\u6b63\u4f0f\u5728\u77f3\u4e0a\uff0c\u4f4e\u58f0\u8c08\u8bba\uff0c\u4e86\u56e0\u7a81\u7136\u51b2\u5230\uff0c\u9c7c\u5a18\u60ca\u53eb\u4e00\u58f0\uff0c\u62bd\u5200\u65c1\u8dc3\uff0c\u66f9\u9526\u513f\u9996\u5f53\u5176 \u51b2\uff0c\u7ed9\u4e86\u56e0\u4e00\u624b\u6293\u7740\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u8c01\u6562\u4e0a\u6765\uff01\u201d \u3000\u3000\u7518\u51e4\u6c60\u53cc\u77b3\u55b7\u706b\uff0c\u9ad8\u53eb\u9053\uff1a\u201c\u4e86\u56e0\uff0c\u4f60\u8981\u4e0d\u8981\u8138\uff1f\u4e34\u6b7b\u8fd8\u6b3a\u4fae\u5c0f\u8f88\uff01\u201d\u4e86\u56e0\u54c8\u54c8\u5927\u7b11\uff0c \u628a\u66f9\u9526\u513f\u65cb\u7a7a\u4e00\u821e\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u66f9\u4ec1\u7236\uff0c\u4f60\u4e00\u5927\u628a\u5e74\u7eaa\uff0c\u4e0d\u5e94\u4e0e\u4ed6\u4eec\u4e00\u822c\u89c1\u8bc6\uff0c\u4f60\u51fa\u6765\u8bf4 \u53e5\u516c\u9053\u8bdd\uff01\u201d \u3000\u3000\u66f9\u4ec1\u7236\u5728\u540c\u95e8\u4e4b\u4e2d\u540d\u5217\u7b2c\u56db\uff0c\u5e74\u7eaa\u5374\u662f\u6700\u957f\uff0c\u6bd4\u4e86\u56e0\u5927\u4e24\u5c81\uff0c\u66f9\u9526\u513f\u4e43\u662f\u4ed6\u72ec\u751f\u5973\u513f\uff0c \u75db\u7231\u5f02\u5e38\u3002\u4ed6\u66fe\u4e3a\u4e86\u5973\u513f\u8fdc\u6e38\u56de\u7586\uff0c\u6c42\u6613\u5170\u73e0\u6536\u9526\u513f\u4e3a\u5f92\u3002\u6613\u5170\u73e0\u4e0d\u80af\u7b54\u5e94\uff0c\u4f46\u540e\u6765\u4ecd\u7136\u6559 \u4e86\u5979\u4e00\u8def\u5251\u6cd5\u548c\u51e0\u624b\u7edd\u62db\u3002\u8fd9\u65f6\u66f9\u4ec1\u7236\u89c1\u5973\u513f\u5728\u4e86\u56e0\u624b\u4e2d\uff0c\u5fc3\u75db\u4e4b\u6781\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4e86\u56e0\uff0c \u4f60\u8fd8\u662f\u4eba\u5417\uff1f\u201d\u4e86\u56e0\u563f\u563f\u51b7\u7b11\uff0c\u66f9\u4ec1\u7236\u987b\u7709\u6380\u52a8\uff0c\u9053\uff1a\u201c\u540c\u95e8\u7684\u516c\u51b3\uff0c\u7edd\u65e0\u5dee\u9519\uff0c\u4f60\u5c31\u662f\u6740 \u4e86\u9526\u513f\uff0c\u6211\u4e5f\u8981\u8bf4\u4f60\u662f\u53db\u5f92\uff01\u53db\u5f92\uff01\u201d\u9a82\u58f0\u4e2d\u4e86\u56e0\u7a81\u7136\u5c16\u53eb\u4e00\u58f0\uff0c\u4e00\u624b\u5c06\u66f9\u9526\u513f\u63b7\u4e0b\u6df1\u8c37\uff01 \u7518\u51e4\u6c60\u7684\u5315\u9996\u548c\u767d\u6cf0\u5b98\u7684\u6885\u82b1\u9488\u90fd\u5411\u4e86\u56e0\u98de\u53bb\uff0c\u4f46\u54ea\u91cc\u6253\u5f97\u4e2d\u4ed6\uff01 \u3000\u3000\u4f17\u4eba\u9f50\u58f0\u60ca\u53eb\uff0c\u66f9\u4ec1\u7236\u51e0\u4e4e\u6655\u5012\u3002\u5415\u56db\u5a18\u500f\u7136\u8dc3\u4e0b\u5c71\u8c37\uff0c\u4e86\u56e0\u9762\u8272\u9752\u767d\uff0c\u53eb\u9053\uff1a\u201c\u597d \u54c7\uff0c\u4f60\u4eec\u8fd9\u5e2e\u5c0f\u8f88\u90fd\u6765\u6697\u7b97\u6211\u4e86\u3002\u201d\u7518\u51e4\u6c60\u6012\u4e0d\u53ef\u904f\uff0c\u62d4\u5200\u51b2\u4e0a\uff0c\u770b\u770b\u5c31\u8981\u548c\u4e86\u56e0\u76f8\u6597\u3002\u534a \u5c71\u8170\u91cc\u8863\u8882\u98ce\u98d8\uff0c\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u54a6\uff0c\u516b\u59b9\u4e0a\u6765\u4e86\uff0c\u5979\u62b1\u7740\u4e00\u4e2a\u4eba\u3002\u201d\u8f6c\u773c\u4e4b\u95f4\uff0c\u5415\u56db\u5a18\u62b1\u7740 \u66f9\u9526\u513f\u5189\u5189\u5347\u4e0a\uff0c\u66f9\u4ec1\u7236\u8d76\u4e0a\u53bb\u63a5\uff0c\u5415\u56db\u5a18\u9053\uff1a\u201c\u6ca1\u4e8b\u513f\uff01\u201d\u66f9\u4ec1\u7236\u628a\u5973\u513f\u62b1\u5728\u6000\u4e2d\uff0c\u89c1\u5979 \u624b\u8db3\u64e6\u4f24\uff0c\u7f57\u88f3\u67d3\u8840\uff0c\u4f46\u8fd8\u6d3b\u7740\u3002\u4e0d\u7981\u559c\u6781\u800c\u6ce3\u3002 \u3000\u3000\u539f\u6765\u66f9\u9526\u513f\u6027\u9887\u521a\u70c8\uff0c\u4e0d\u7518\u53d7\u4e86\u56e0\u631f\u6301\uff0c\u5979\u6b66\u529f\u867d\u7136\u8fdc\u8fdc\u4e0d\u5982\u4e86\u56e0\uff0c\u4f46\u5728\u5371\u6025\u4e4b\u65f6\uff0c\u5374 \u8bb0\u8d77\u6613\u5170\u73e0\u6559\u7684\u4e00\u624b\u6551\u547d\u7edd\u62db\uff0c\u7ea4\u8db3\u5411\u4e0a\u4e00\u52fe\uff0c\u8e22\u4e2d\u4e86\u56e0\u7684\u5bf8\u5173\u5c3a\u8109\u95e8\u8981\u5bb3\uff0c\u4e86\u56e0\u903c\u5f97\u5411\u5916 \u4e00\u63b7\uff0c\u5e78\u5f97\u5415\u56db\u5a18\u8f7b\u529f\u5353\u7edd\uff0c\u8df3\u4e0b\u53bb\u628a\u5979\u811a\u540e\u8ddf\u635e\u7740\uff0c\u6551\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e86\u56e0\u7684\u6b8b\u66b4\u66f4\u6fc0\u8d77\u516c\u6124\uff0c\u7518\u51e4\u6c60\u9996\u5148\u559d\u9053\uff1a\u201c\u4e86\u56e0\uff0c\u4f60\u8fd9\u61e6\u592b\uff0c\u5c45\u7136\u8fd8\u6709\u9762\u76ee\u7ad9 \u5728\u8fd9\u91cc\u5417\uff1f\u201d\u4e86\u56e0\u4ee5\u82f1\u96c4\u81ea\u5c45\uff0c\u65a5\u9053\uff1a\u201c\u6211\u662f\u61e6\u592b\uff1f\u4f60\u6562\u4e0d\u6562\u4e0a\u6765\u548c\u6211\u51b3\u4e00\u6b7b\u6218\uff1f\u201d\u7518\u51e4\u6c60 \u9053\uff1a\u201c\u4e00\u4eba\u505a\u4e8b\u4e00\u4eba\u5f53\uff0c\u4f60\u4e3a\u975e\u4f5c\u6076\uff0c\u5374\u4e0d\u6562\u81ea\u884c\u5b9a\u7f6a\uff0c\u53c8\u4e0d\u6562\u9886\u53d7\u5211\u7f5a\uff0c\u4e0d\u662f\u61e6\u592b\u662f\u4ec0 \u4e48\uff1f\u201d\u4e86\u56e0\u5927\u6012\uff0c\u9053\uff1a\u201c\u597d\uff0c\u5927\u5bb6\u90fd\u5230\u5e08\u5085\u5893\u524d\uff0c\u6211\u5012\u8981\u770b\u770b\u4f60\u4eec\u6709\u4ec0\u4e48\u80fd\u4e3a\uff1f\u201d\u4e86\u56e0\u867d\u7136 \u5927\u8a00\u708e\u708e\uff0c\u5176\u5b9e\u5374\u662f\u8272\u5386\u5185\u832c\uff0c\u4ed6\u6b63\u5728\u60f3\u597d\u4e00\u5957\u8bf4\u8f9e\uff0c\u8981\u5728\u6781\u9669\u4e4b\u4e2d\u627e\u51fa\u751f\u8def\u3002 \u3000\u3000\u4e86\u56e0\u63d0\u7740\u7985\u6756\uff0c\u8d70\u5230\u5e08\u5085\u5893\u524d\uff0c\u62a2\u5360\u4e86\u4e00\u5757\u9ad8\u5730\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u9080\u4e86\u8fd9\u4e48\u591a\u6b66\u6797\u670b \u53cb\u6765\u6b64\uff0c\u6211\u867d\u6b7b\u4f55\u60e7\uff01\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u6211\u4eec\u6e05\u7406\u95e8\u6237\uff0c\u7edd\u4e0d\u5047\u624b\u5916\u4eba\uff01\u201d\u4e86\u56e0\u5fc3\u4e2d\u7a0d \u5b89\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u4e03\u4eba\uff0c\u9664\u4e86\u5415\u56db\u5a18\u5916\uff0c\u5176\u4f59\u516d\u4eba\uff0c\u53ef\u5168\u7ecf\u6211\u4eb2\u624b\u6559\u8fc7\uff0c\u4f60\u4eec\u8bf4\u8981\u6e05\u7406\u95e8 \u6237\uff0c\u6211\u4e5f\u6e05\u7406\u95e8\u6237\uff0c\u6211\u597d\u574f\u90fd\u662f\u4f60\u4eec\u7684\u5e08\u5144\uff0c\u7b97\u5f97\u662f\u4f60\u4eec\u7684\u4e00\u534a\u5e08\u5085\uff0c\u4f60\u4eec\u4eca\u65e5\u805a\u4f17\u72af\u4e0a\uff0c \u60c5\u540c\u5f11\u5e08\uff0c\u6211\u5148\u8981\u60e9\u7f5a\u4f60\u4eec\uff0c\u770b\u770b\u4f60\u4eec\u4ece\u6211\u8fd9\u91cc\u5b66\u5230\u7684\u4e00\u70b9\u6280\u4e1a\uff0c\u80fd\u4e0d\u80fd\u5c06\u6211\u6253\u6b7b\uff1f\u201d\u6b66\u6797 \u4e2d\u6700\u4e3a\u8bb2\u7a76\u5c0a\u5e08\u91cd\u9053\uff0c\u6709\u201c\u4e00\u65e5\u4e3a\u5e08\uff0c\u767e\u5e74\u4e3a\u7236\u201d\u4e4b\u8bed\uff0c\u4e86\u56e0\u4ee5\u201c\u534a\u5e08\u201d\u81ea\u5c45\uff0c\u8981\u6c42\u5148\u548c\u4ed6 \u6559\u8fc7\u7684\u5e08\u5f1f\u8f83\u91cf\uff0c\u6b66\u6797\u4e2d\u786e\u662f\u6709\u8fd9\u89c4\u77e9\u3002\u4e0d\u8fc7\u4e86\u56e0\u7684\u60c5\u5f62\u4e0e\u4e00\u822c\u4e0d\u540c\uff0c\u4ed6\u662f\u80cc\u4e86\u5e08\u5085\u9057\u8bad\uff0c \u80cc\u56fd\u53db\u5e08\uff0c\u8fd9\u662f\u6700\u5927\u7684\u7f6a\u540d\uff0c\u5c31\u662f\u5e08\u4f2f\u5e08\u53d4\u7b49\u957f\u8f88\u72af\u4e86\uff0c\u5e08\u4f84\u4e5f\u53ef\u4eb2\u624b\u6740\u4ed6\u3002\u7518\u51e4\u6c60\u7b49\u672c\u53ef \u4e0d\u7406\uff0c\u540c\u5415\u56db\u5a18\u56f4\u653b\uff0c\u53ef\u662f\u542c\u4ed6\u7684\u53e3\u6c14\uff0c\u660e\u660e\u662f\u8bf4\u4ed6\u4eec\u516d\u4eba\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u5468\u6e05\u9996\u5148\u8bf4\u9053\uff1a \u201c\u597d\u54c7\uff0c\u8ba9\u4f60\u6b7b\u5f97\u7791\u76ee\uff0c\u5c31\u4f9d\u4f60\u5212\u51fa\u7684\u9053\u513f\u529e\u5427\uff01\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u53ef\u65e0\u540c\u95e8\u60c5\u4efd\uff0c\u4eca \u65e5\u4f60\u8981\u9003\u751f\uff0c\u4e07\u4e07\u4e0d\u80fd\uff01\u201d\u4e86\u56e0\u6012\u9053\uff1a\u201c\u6211\u82e5\u7b2c\u4e00\u573a\u5c31\u8f93\u4e86\uff0c\u4efb\u7531\u4f60\u4eec\u5b9a\u7f6a\uff0c\u6211\u82e5\u8d62\u4e86\uff0c\u518d \u548c\u4f60\u8fd9\u8d31\u5a62\u8f83\u91cf\u3002\u201d\u5728\u540c\u95e8\u4e4b\u4e2d\uff0c\u4ed6\u6700\u5fcc\u60ee\u5415\u56db\u5a18\uff0c\u53ef\u662f\u5fc3\u60f3\uff1a\u82e5\u7136\u5355\u6253\u72ec\u6597\uff0c\u5374\u51c6\u80fd\u8d62 \u5979\uff0c\u6240\u4ee5\u7528\u8bf4\u8bdd\u628a\u5979\u6324\u51fa\u4e4b\u540e\uff0c\u9a6c\u4e0a\u7985\u6756\u4e00\u62a1\uff0c\u9ad8\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u6765\u5427\uff01\u201d\u547c\u7684\u4e00\u58f0\uff0c\u9aa4 \u5411\u5468\u6e05\u6253\u53bb\uff0c\u5148\u4e0b\u6740\u624b\uff01 \u3000\u3000\u5468\u6e05\u5728\u540c\u95e8\u4e2d\u867d\u7136\u4f4d\u5217\u7b2c\u4e8c\uff0c\u6b66\u529f\u5374\u4e0d\u7cbe\u5f3a\uff0c\u62bd\u621f\u4e00\u67b6\uff0c\u4e24\u53ea\u81c2\u818a\u5168\u90fd\u9ebb\u4e86\uff0c\u7518\u51e4\u6c60\u9f99 \u817e\u864e\u8dc3\uff0c\u53f3\u624b\u5355\u5200\u4e00\u62db\u201c\u62e8\u4e91\u89c1\u65e5\u201d\uff0c\u6a2a\u65ab\u8fc7\u53bb\uff0c\u5de6\u624b\u4e00\u52fe\uff0c\u5c55\u5f00\u64d2\u62ff\u624b\u6cd5\u52fe\u4e86\u56e0\u8109\u95e8\uff0c\u5de6 \u865a\u53f3\u5b9e\uff0c\u660e\u77e5\u514b\u4ed6\u4e0d\u4f4f\uff0c\u7528\u610f\u4e0d\u8fc7\u662f\u5728\u63a9\u62a4\u5468\u6e05\u3002\u4e86\u56e0\u6e9c\u6ed1\u5f02\u5e38\uff0c\u907f\u5f3a\u653b\u5f31\uff0c\u7985\u6756\u4e00\u7acb\uff0c\u628a \u7518\u51e4\u6c60\u7684\u5355\u5200\u9707\u5f00\uff0c\u547c\u7684\u4e00\u638c\u51fb\u51fa\uff0c\u660e\u53d6\u6b63\u9762\u7684\u7518\u51e4\u6c60\uff0c\u6697\u51fb\u5de6\u7ffc\u7684\u8def\u6c11\u77bb\uff0c\u5468\u6e05\u6ed1\u6b65\u626c \u621f\uff0c\u621f\u5c16\u5df2\u582a\u582a\u523a\u5230\u4e86\u56e0\u540e\u8111\uff0c\u4e86\u56e0\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u8eab\u8eaf\u4e00\u77ee\uff0c\u7985\u6756\u5411\u540e\u4e00\u63a8\uff0c\u5f53\u7684 \u4e00\u58f0\uff0c\u628a\u5468\u6e05\u7684\u5929\u65b9\u753b\u621f\u9707\u5f97\u8131\u624b\u98de\u53bb\uff0c\u5de6\u638c\u4e00\u6309\uff0c\u62cd\u5230\u8def\u6c11\u77bb\u524d\u5fc3\uff0c\u767d\u6cf0\u5b98\u4e00\u8dc3\u800c\u4e0a\uff0c\u864e \u5934\u5200\u8fce\u9762\u5288\u4e0b\uff0c\u767d\u6cf0\u5b98\u5728\u540c\u95e8\u4e4b\u4e2d\uff0c\u9664\u4e86\u4e86\u56e0\u4e0e\u5415\u56db\u5a18\u5916\uff0c\u6b66\u529f\u4ec5\u6b21\u4e8e\u7518\u51e4\u6c60\uff0c\u5b9d\u5200\u4e00\u5288\u4e00 \u6643\uff0c\u4f7f\u51fa\u76d8\u9f99\u5200\u6cd5\uff0c\u987f\u65f6\u5728\u4e86\u56e0\u9762\u524d\u6cdb\u8d77\u4e00\u56e2\u94f6\u5149\uff0c\u4e86\u56e0\u903c\u5f97\u64a4\u638c\u9632\u8eab\uff0c\u65bd\u5c55\u7a7a\u624b\u5165\u767d\u5203\u7684 \u624b\u6bb5\u4e0e\u767d\u6cf0\u5b98\u5468\u65cb\uff0c\u5de6\u624b\u7985\u6756\u4e00\u8361\uff0c\u626b\u5f00\u4e86\u540c\u65f6\u653b\u5230\u7684\u51e0\u6761\u5175\u5668\u3002\u674e\u6e90\u81c2\u4f24\u672a\u6108\uff0c\u6325\u4e03\u8282\u97ad \u5411\u4e86\u56e0\u4e0b\u76d8\u7f20\u6253\uff0c\u4e86\u56e0\u7985\u6756\u4e00\u7ede\uff0c\u4e03\u8282\u97ad\u7f20\u5728\u6756\u4e0a\uff0c\u4e86\u56e0\u5927\u559d\u58f0\uff1a\u201c\u53bb\uff01\u201d\u6697\u8fd0\u5185\u529b\u4e00\u9707\uff0c \u674e\u6e90\u7684\u4e03\u8282\u97ad\u7adf\u7136\u88ab\u9707\u5f97\u65ad\u4e3a\u4e09\u622a\u3002\u674e\u6e90\u5927\u60ca\uff0c\u8dc3\u51fa\u5708\u5b50\uff0c\u5468\u6e05\u5df2\u628a\u5175\u5668\u62fe\u8d77\uff0c\u6325\u6208\u518d\u6218\uff0c \u66f9\u4ec1\u7236\u9053\uff1a\u201c\u516d\u5f1f\uff0c\u4f60\u6b47\u6b47\u5427\u3002\u201d\u674e\u6e90\u7259\u6839\u4e00\u54ac\uff0c\u9053\uff1a\u201c\u540c\u8fdb\u540c\u9000\uff0c\u4e0d\u6740\u6b64\u8d3c\uff0c\u8a93\u4e0d\u7f62 \u4f11\uff01\u201d\u89e3\u4e0b\u8170\u5e26\uff0c\u821e\u5f97\u77eb\u5982\u6e38\u9f99\uff0c\u7f20\u8155\u62c2\u9762\uff0c\u5c55\u5f00\u7684\u4ecd\u662f\u8f6f\u97ad\u62db\u6570\u3002\u674e\u6e90\u5728\u540c\u95e8\u4e4b\u4e2d\uff0c\u6b66\u529f \u4e0e\u767d\u6cf0\u5b98\u4e0d\u76f8\u4f2f\u4ef2\uff0c\u53ea\u662f\u6c14\u529b\u8f83\u5dee\uff0c\u66f4\u517c\u53d7\u4f24\u4e4b\u540e\uff0c\u6240\u4ee5\u521a\u624d\u5403\u4e86\u5927\u4e8f\u3002\u73b0\u5728\u6539\u7528\u8170\u5e26\u4f5c\u4e3a \u8f6f\u97ad\uff0c\u8170\u5e26\u5168\u4e0d\u53d7\u529b\uff0c\u4e0d\u6015\u9707\u65ad\uff0c\u5728\u5175\u5668\u4e0a\u5148\u4e0d\u5403\u4e8f\uff0c\u76d8\u65cb\u7f20\u6253\uff0c\u867d\u7136\u4e0d\u80fd\u81f4\u4e86\u56e0\u6b7b\u547d\uff0c\u4e5f \u6536\u4e86\u7275\u5236\u4e4b\u529f\u3002\u66f9\u4ec1\u7236\u75db\u6068\u4e86\u56e0\u5165\u9aa8\uff0c\u4ed6\u4f7f\u7684\u662f\u72ec\u95e8\u5175\u5668\u94c1\u7435\u7436\uff0c\u53ef\u4ee5\u9501\u62ff\u5175\u5203\u624b\u8155\uff0c\u5185\u4e2d \u53c8\u85cf\u6697\u5668\uff0c\u8fd9\u65f6\u62fc\u4e86\u8001\u547d\uff0c\u4e86\u56e0\u4e5f\u4e0d\u6562\u4e0d\u9632\u3002 \u3000\u3000\u8fd9\u4e00\u6218\u76f4\u6253\u5f97\u5929\u660f\u5730\u6697\uff0c\u65e5\u6708\u65e0\u5149\u3002\u4e86\u56e0\u628a\u4e00\u8eab\u7edd\u6280\uff0c\u5168\u90fd\u65bd\u5c55\u51fa\u6765\uff0c\u53ea\u542c\u5f97\u547c\u547c\u8f70 \u8f70\uff0c\u5468\u56f4\u6570\u4e08\u4e4b\u5185\uff0c\u90fd\u662f\u98ce\u58f0\uff0c\u529f\u529b\u7a0d\u4f4e\u7684\uff0c\u5175\u5668\u90e8\u7ed9\u6756\u98ce\u9707\u6b6a\uff0c\u5931\u4e86\u51c6\u5934\u3002\u4f46\u5468\u6e05\u7b49\u4ee5\u516d \u654c\u4e00\uff0c\u6b64\u547c\u5f7c\u5e94\uff0c\u4e86\u56e0\u4e5f\u4e0d\u5bb9\u6613\u5404\u4e2a\u51fb\u7834\u3002\u52a0\u4e0a\u7518\u51e4\u6c60\u5929\u8d4b\u5f02\u7980\uff0c\u81c2\u529b\u6c89\u96c4\uff0c\u4ec5\u4ec5\u7565\u5728\u4e86\u56e0 \u4e4b\u4e0b\uff0c\u8bba\u6b66\u529f\u4e5f\u5dee\u4e0d\u4e86\u8bb8\u591a\uff0c\u4ed6\u548c\u767d\u6cf0\u5b98\u529b\u653b\u4e2d\u8def\uff0c\u654c\u7740\u4e86\u56e0\u7684\u7985\u6756\uff0c\u706d\u715e\u4ed6\u7684\u950b\u8292\u3002\u66f9\u4ec1 \u7236\u4e0e\u8def\u6c11\u77bb\u529b\u653b\u5de6\u7ffc\uff0c\u6bcf\u5230\u5371\u6025\u4e4b\u65f6\uff0c\u66f9\u4ec1\u7236\u5c31\u6309\u94c1\u7435\u7436\u653e\u51fa\u91cc\u9762\u7684\u6697\u7bad\uff1b\u674e\u6e90\u4e0e\u5468\u6e05\u5219\u529b \u653b\u53f3\u7ffc\uff0c\u914d\u5408\u7275\u5236\u3002\u516d\u4eba\u5206\u6210\u4e09\u7ec4\uff0c\u628a\u4e86\u56e0\u56f4\u5f97\u98ce\u96e8\u4e0d\u900f\u3002 \u3000\u3000\u5c71\u4e0a\u7fa4\u96c4\u770b\u5f97\u6035\u76ee\u60ca\u5fc3\uff0c\u770b\u5230\u7d27\u5f20\u4e4b\u65f6\uff0c\u771f\u6068\u4e0d\u5f97\u62d4\u5200\u52a9\u6218\u3002\u53ef\u662f\u8fd9\u4e43\u662f\u522b\u4eba\u6e05\u7406\u95e8\u6237 \u7684\u5185\u90e8\u4e4b\u4e8b\uff0c\u5916\u6d3e\u7684\u4eba\u53ef\u4e0d\u80fd\u63d2\u624b\u3002\u5415\u56db\u5a18\u6c14\u5b9a\u795e\u95f2\uff0c\u501a\u5728\u6811\u8fb9\u89c2\u6218\uff0c\u65f6\u4e0d\u65f6\u53d1\u51fa\u5fae\u7b11\u3002\u7384 \u98ce\u9053\uff1a\u201c\u770b\u6765\u6c5f\u5357\u516d\u4fa0\uff0c\u80dc\u4e0d\u4e86\u4ed6\u4eec\u5e08\u5144\uff0c\u7b2c\u4e8c\u573a\u53ea\u5415\u56db\u5a18\u4e00\u4eba\uff0c\u66f4\u96be\u53d6\u80dc\uff0c\u8fd9\u51f6\u50e7\u53ef\u80fd\u6b7b \u91cc\u9003\u751f\u4e86\uff01\u201d \u3000\u3000\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u4ed6\u82e5\u8fde\u80dc\u4e24\u573a\uff0c\u54b1\u4eec\u7b49\u4ed6\u4e0b\u5c71\u4e4b\u65f6\uff0c\u518d\u884c\u515c\u622a\u3002\u54b1\u4eec\u662f\u4e3a\u6b66\u6797\u9664\u5bb3\uff0c\u4e0e\u4ed6 \u4eec\u6e05\u7406\u95e8\u6237\u65e0\u6d89\u3002\u201d\u67f3\u5148\u5f00\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u7b49\u4e0b\u8bf7\u770b\u5415\u56db\u5a18\u51fa\u624b\u3002\u201d\u5173\u4e1c\u56db \u4fa0\u4e4b\u4e2d\uff0c\u67f3\u5148\u5f00\u9886\u6559\u8fc7\u5415\u56db\u5a18\u7684\u672c\u9886\uff0c\u5bf9\u5979\u751a\u6709\u4fe1\u5fc3\u3002 \u3000\u3000\u8fd9\u4e00\u6218\u6253\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5468\u6e05\u7b49\u516d\u4eba\u90fd\u89c9\u4e86\u56e0\u529f\u529b\u6bd4\u524d\u72b9\u80dc\uff0c\u4e86\u56e0\u4e5f\u611f\u5230\u516d\u4e2a\u5e08\u5f1f\u4eca\u975e\u6614 \u6bd4\u3002\u6fc0\u6218\u4e2d\u4e86\u56e0\u6e10\u89c9\u989d\u4e0a\u89c1\u6c57\uff0c\u6c14\u5598\u5fc3\u4fc3\uff0c\u4e24\u8fb9\u4ecd\u662f\u4e0d\u5206\u80dc\u8d1f\u3002\u4e86\u56e0\u6697\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u81ea\u5df1\u672c \u6765\u4ee5\u8010\u6218\u89c1\u957f\uff0c\u4f46\u6b64\u65f6\u6b64\u9645\uff0c\u5374\u4e0d\u5e94\u4e0e\u4ed6\u4eec\u4e45\u6218\u4e86\u3002\u6756\u6cd5\u4e00\u53d8\uff0c\u8fde\u4e0b\u6740\u624b\u3002\u7518\u51e4\u6c60\u4e0e\u767d\u6cf0\u5b98 \u62fc\u4e86\u5168\u529b\uff0c\u5c1a\u53ef\u62b5\u6321\u5f97\u4f4f\uff0c\u5176\u4ed6\u56db\u4eba\u5df2\u4e0d\u6562\u6b3a\u8eab\u8fdb\u903c\uff0c\u4e86\u56e0\u547c\u7684\u4e00\u6756\uff0c\u628a\u7518\u767d\u4e8c\u4eba\u7684\u5175\u5668\uff0c \u5168\u90fd\u8361\u5f00\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u8fd9\u4e00\u573a\u7b97\u662f\u626f\u5e73\uff0c\u4e24\u65e0\u80dc\u8d1f\uff0c\u6211\u8981\u89c1\u89c1\u90a3\u8d31\u5a62\u7684\u529f\u592b\uff0c\u770b\u5979 \u8fd9\u4e9b\u5e74\u6765\u6709\u591a\u5c11\u8fdb\u5883\uff1f\u201d\u4ed6\u8fd9\u8bf4\u8bdd\u4ecd\u7136\u662f\u4ee5\u638c\u95e8\u5e08\u5144\u81ea\u5c45\uff0c\u8bf4\u5b8c\u8bdd\u540e\uff0c\u62d4\u8eab\u8df3\u51fa\u5708\u5b50\uff0c\u6a2a\u6756 \u5f53\u80f8\uff0c\u53cc\u76ee\u76ef\u7740\u5415\u56db\u5a18\u3002 \u3000\u3000\u8fd9\u4e00\u6218\u4e86\u56e0\u56fa\u7136\u8017\u4e86\u4e0d\u5c11\u6c14\u529b\uff0c\u5468\u6e05\u674e\u6e90\u7b49\u4e5f\u5df2\u75b2\u5026\u4e0d\u582a\u3002\u7518\u51e4\u6c60\u5fc3\u60f3\uff0c\u7f20\u6597\u4e0b\u53bb\uff0c\u5341 \u4e5d\u53ef\u64cd\u80dc\u5238\uff0c\u4f46\u540c\u95e8\u4e4b\u4e2d\uff0c\u5fc5\u6709\u4e00\u4e8c\u4eba\u56e0\u8fc7\u52b3\u800c\u53d7\u5185\u4f24\uff0c\u7b97\u4f5c\u626f\u5e73\u4e5f\u7f62\uff0c\u62b1\u5200\u9000\u4e0b\uff0c\u8868\u793a\u540c \u610f\u3002\u5468\u6e05\u767d\u6cf0\u5b98\u7b49\u4e5f\u76f8\u7ee7\u9000\u4e0b\u3002\u5415\u56db\u5a18\u76c8\u76c8\u4e00\u7b11\uff0c\u98de\u8eab\u8dc3\u8fdb\u573a\u5fc3\u3002 \u3000\u3000\u4e86\u56e0\u5b9a\u4e86\u5b9a\u795e\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u4e00\u4ed7\u6211\u82e5\u80dc\u4e86\uff0c\u5f7c\u6b64\u4e0d\u8bb8\u5bfb\u4ec7\u3002\u201d\u8fd9\u8bdd\u5176\u5b9e\u5df2\u6709\u4e00\u70b9\u6c14\u9981\u3002 \u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u656c\u4f9d\u5c0a\u547d\uff01\u201d\u5237\u7684\u4e00\u5251\u523a\u53bb\u3002\u4e86\u56e0\u7985\u6756\u4e00\u7ffb\uff0c\u4e00\u62db\u201c\u7ffb\u6c5f\u5012\u6d77\u201d\uff0c\u5982\u86df\u9f99\u822c \u76f4\u5377\u8fc7\u6765\u3002\u5468\u6e05\u4e0e\u66f9\u4ec1\u7236\u674e\u6e90\u4e09\u4eba\u90fd\u672a\u89c1\u8fc7\u8fd9\u4f4d\u5c0f\u5e08\u59b9\u7684\u672c\u9886\uff0c\u89c1\u4e86\u56e0\u89c1\u9762\u4fbf\u4e0b\u6bd2\u62db\uff0c\u90fd\u5403 \u4e86\u4e00\u60ca\uff01\u7518\u51e4\u6c60\u9053\uff1a\u201c\u65e0\u59a8\uff01\u201d\u4f46\u542c\u5f97\u5415\u56db\u5a18\u4e00\u58f0\u957f\u5578\uff0c\u5de6\u8db3\u671d\u6756\u5934\u4e00\u8e0f\uff0c\u501f\u7740\u8fd9\u4e00\u8e0f\u4e4b \u52bf\uff0c\u6574\u4e2a\u8eab\u5b50\u7ffb\u817e\u8d77\u6765\uff0c\u75be\u5982\u98de\u9e1f\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u63a0\u8fc7\u4e86\u56e0\u5934\u9876\uff0c\u8eab\u5f62\u672a\u843d\uff0c\u971c\u534e\u5251\u5728\u7a7a\u4e2d\u4e00 \u65cb\uff0c\u5df2\u4f7f\u51fa\u201c\u9e4f\u640f\u4e5d\u9704\u201d\u7684\u62db\u6570\uff0c\u7167\u4e86\u56e0\u7684\u79c3\u5934\u731b\u523a\u4e0b\u6765\uff01\u5c71\u4e0a\u7fa4\u96c4\u5927\u58f0\u53eb\u597d\uff0c\u4e86\u56e0\u7985\u6756\u4e00 \u6296\uff0c\u63a0\u7a7a\u800c\u4e0a\uff0c\u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u628a\u5415\u56db\u5a18\u7684\u5b9d\u5251\u903c\u5f00\uff0c\u6697\u6697\u5403\u60ca\uff1a\u8fd9\u8d31\u5a62\u7684\u529f\u592b\u6bd4\u4ee5\u524d \u53c8\u9ad8\u8bb8\u591a\u4e86\u3002 \u3000\u3000\u5415\u56db\u5a18\u89c1\u4e86\u56e0\u4e45\u6218\u4e4b\u540e\uff0c\u4f59\u52bf\u672a\u8870\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u628a\u7384\u5973\u5251\u6cd5\u4f7f\u5f97\u795e\u5999\u65e0\u65b9\uff0c\u5251\u52bf\u5982\u8679\uff0c \u7740\u7740\u8fdb\u51fb\u3002\u4e86\u56e0\u62a1\u52a8\u7985\u6756\uff0c\u89c1\u62db\u62c6\u62db\uff0c\u89c1\u5f0f\u62c6\u5f0f\uff0c\u4e5f\u662f\u6709\u653b\u6709\u5b88\u3002 \u3000\u3000\u6253\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u672a\u5206\u80dc\u8d1f\u3002\u4e86\u56e0\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u540e\u6094\uff1a\u54ce\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u9002\u624d\u6597\u90a3\u516d\u4eba\uff0c \u5c31\u4e0d\u8be5\u591a\u8017\u6c14\u529b\u3002\u5415\u56db\u5a18\u770b\u4ed6\u5df2\u6709\u602f\u6218\u4e4b\u610f\uff0c\u5251\u62db\u8d8a\u53d1\u50ac\u7d27\uff0c\u771f\u7684\u6709\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6eda\u6eda\u800c \u4e0a\uff0c\u4e86\u56e0\u54ac\u5b9e\u7259\u6839\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u4f7f\u5c3d\u5185\u5bb6\u771f\u529b\uff0c\u6756\u5f71\u5982\u5c71\uff0c\u628a\u5415\u56db\u5a18\u7684\u5251\u5149\u88f9\u4f4f\uff01 \u3000\u3000\u5468\u6e05\u7b49\u53c8\u662f\u5927\u5403\u4e00\u60ca\uff0c\u770b\u4e86\u4e00\u4f1a\uff0c\u4f46\u89c1\u4e00\u7f15\u94f6\u5149\uff0c\u597d\u50cf\u5728\u5343\u4e07\u6839\u6756\u6797\u7684\u5305\u56f4\u4e4b\u4e0b\u7a7f\u6765\u63d2 \u53bb\u3002\u5df2\u770b\u4e0d\u6e05\u90a3\u4e2a\u662f\u4e86\u56e0\uff0c\u90a3\u4e2a\u662f\u5415\u56db\u5a18\u4e86\u3002 \u3000\u3000\u539f\u6765\u5415\u56db\u5a18\u65e9\u6599\u5230\u7ec8\u987b\u8981\u4e0e\u4e86\u56e0\u51b3\u6218\uff0c\u6240\u4ee5\u9664\u4e86\u5251\u6cd5\u66f4\u52a0\u7cbe\u7814\u4e4b\u5916\uff0c\u5728\u4ed9\u971e\u5cad\u4e94\u5e74\uff0c\u53c8 \u6f5c\u4fee\u5185\u529f\uff0c\u628a\u5e08\u5085\u4f20\u7684\u4e0e\u5f97\u81ea\u6613\u5170\u73e0\u6240\u6307\u70b9\u7684\u5185\u529f\u7a8d\u8981\uff0c\u6089\u5fc3\u7814\u4e60\uff0c\u7ecf\u8fc7\u4e94\u5e74\uff0c\u5185\u529f\u5927\u6709\u8fdb \u5883\uff0c\u548c\u4e86\u56e0\u76f8\u6bd4\uff0c\u5df2\u5dee\u4e0d\u4e86\u591a\u5c11\u3002\u800c\u4e86\u56e0\u7ecf\u8fc7\u521a\u624d\u4e00\u6218\uff0c\u6c14\u529b\u8017\u4e86\u4e00\u534a\uff0c\u6b64\u6d88\u5f7c\u957f\uff0c\u5415\u56db\u5a18 \u4e0d\u4f46\u5728\u8f7b\u529f\u5251\u6cd5\u4e0a\u5360\u4e86\u4e0a\u98ce\uff0c\u5373\u5728\u5185\u5bb6\u529f\u529b\u7684\u8f83\u91cf\u4e0a\uff0c\u4e5f\u5c3d\u53ef\u548c\u4e86\u56e0\u6253\u6210\u5e73\u624b\u4e86\uff01\u5415\u56db\u5a18\u4ee5 \u524d\u8f93\u7ed9\u4e86\u56e0\u7684\u5730\u65b9\uff0c\u5c31\u5728\u529f\u529b\u4e0d\u5982\u4ed6\u7684\u6df1\u539a\uff0c\u73b0\u5728\u529f\u529b\u65b9\u9762\u62c9\u6210\u5e73\u624b\uff0c\u5415\u56db\u5a18\u5df2\u662f\u653e\u80c6\u8fdb \u653b\uff0c\u6beb\u65e0\u987e\u8651\u3002 \u3000\u3000\u672c\u6765\u4ee5\u4e86\u56e0\u7684\u672c\u9886\uff0c\u5373\u5904\u4e0b\u98ce\uff0c\u4e5f\u8fd8\u53ef\u6218\u534a\u65e5\uff0c\u504f\u504f\u4ed6\u5fc3\u60c5\u7126\u8e81\uff0c\u529b\u6c42\u901f\u6218\u901f\u51b3\uff0c\u4f7f\u51fa \u5168\u529b\uff0c\u62fc\u547d\u8fdb\u51fb\uff0c\u5415\u56db\u5a18\u4ee5\u9038\u5f85\u52b3\uff0c\u4e58\u6687\u62b5\u9699\uff0c\u4e00\u5473\u6e38\u6597\uff0c\u7ea6\u83ab\u53c8\u6253\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u4e86\u56e0\u6e10\u9732 \u75b2\u6001\uff0c\u9163\u6218\u4e2d\u7fa4\u96c4\u542c\u5f97\u4e00\u58f0\u957f\u7b11\uff0c\u4e86\u56e0\u62d6\u6756\u8d25\u9003\uff0c\u539f\u6765\u5df2\u88ab\u5415\u56db\u5a18\u523a\u778e\u4e00\u773c\u3002 \u3000\u3000\u5415\u56db\u5a18\u8f7b\u529f\u5353\u7edd\uff0c\u90a3\u5bb9\u5f97\u4ed6\u8d25\u91cc\u9003\u751f\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u540e\u53d1\u5148\u81f3\uff0c\u62e6\u5728\u4e86\u56e0\u524d\u9762\uff0c\u5251\u5149\u970d \u970d\uff0c\u628a\u4e86\u56e0\u53c8\u903c\u5f97\u5012\u9000\u56de\u6765\uff01 \u3000\u3000\u4e86\u56e0\u8fd9\u65f6\u4fe8\u5982\u4e00\u5934\u8d1f\u4f24\u7684\u731b\u864e\uff0c\u7985\u6756\u547c\u547c\u62a1\u5f00\uff0c\u72c2\u51b2\u731b\u6253\u3002\u7518\u51e4\u6c60\u53eb\u9053\uff1a\u201c\u516b\u59b9\u5c0f \u5fc3\uff01\u201d\u5415\u56db\u5a18\u5b9a\u795e\u5e94\u4ed8\uff0c\u5251\u4f3c\u5f69\u8776\u7a7f\u82b1\uff0c\u6b65\u82e5\u7075\u732b\u6251\u9f20\uff0c\u8eab\u5982\u67f3\u7d6e\u8f7b\u626c\uff0c\u4e86\u56e0\u7985\u6756\u5230\u4e1c\uff0c\u5979 \u907f\u5230\u897f\uff0c\u4e86\u56e0\u7985\u6756\u5230\u5357\uff0c\u5979\u907f\u5230\u5317\uff1b\u53ef\u53c8\u4e0d\u662f\u4e00\u5473\u95ea\u907f\uff0c\u5c31\u5728\u95ea\u907f\u4e4b\u65f6\uff0c\u4e5f\u4ed7\u7740\u7edd\u5999\u7684\u8eab \u6cd5\uff0c\u4e00\u53e3\u5251\u8fde\u8fde\u95ea\u51fb\u3002\u53c8\u6fc0\u6218\u4e86\u597d\u4e45\uff0c\u7fa4\u96c4\u6b63\u770b\u5f97\u773c\u82b1\u64a9\u4e71\uff0c\u4f46\u542c\u5f97\u4e86\u56e0\u60e8\u53eb\u4e00\u58f0\uff0c\u4ec5\u5269\u7684 \u4e00\u53ea\u773c\uff0c\u4e5f\u7ed9\u5415\u56db\u5a18\u523a\u5272\u4e86\u3002\u5415\u56db\u5a18\u8de8\u6b65\u63c9\u8eab\uff0c\u6a2a\u5251\u4e00\u62b9\uff0c\u5f85\u628a\u4e86\u56e0\u4eba\u5934\u5272\u4e0b\uff0c\u731b\u7136\u4e00\u60f3\uff0c \u4ed6\u5230\u5e95\u66fe\u662f\u5e08\u5085\u9996\u5f92\uff0c\u6307\u70b9\u8fc7\u81ea\u5df1\u516d\u4e2a\u5e08\u5144\u7684\u6b66\u827a\uff0c\u5e94\u8be5\u843d\u4e2a\u5168\u5c38\uff0c\u5251\u5230\u4e2d\u9014\uff0c\u7a81\u7136\u6539\u53d8\u624b \u6cd5\uff0c\u5251\u5c16\u5728\u4e86\u56e0\u80c1\u4e0b\u7684\u8981\u5bb3\u7a74\u9053\u8f7b\u8f7b\u4e00\u70b9\uff0c\u4e86\u56e0\u5927\u53eb\u4e00\u58f0\uff0c\u7985\u6756\u547c\u7684\u63b7\u51fa\uff0c\u4e34\u6b7b\u4e00\u63b7\uff0c\u52b2\u9053 \u4ecd\u662f\u5927\u5f97\u51fa\u5947\uff0c\u5415\u56db\u5a18\u626d\u8eab\u4e00\u95ea\uff0c\u90a3\u7985\u6756\u63d2\u5165\u5bf9\u9762\u77f3\u58c1\uff0c\u53ea\u9732\u51fa\u77ed\u77ed\u7684\u4e00\u622a\u6756\u5934\u3002 \u3000\u3000\u4e86\u56e0\u7edd\u9876\u6b66\u529f\uff0c\u53ea\u56e0\u4e00\u5ff5\u4e4b\u5dee\uff0c\u8d70\u5165\u6b67\u9014\uff0c\u4ee5\u81f4\u843d\u5f97\u5728\u5e08\u5085\u575f\u524d\u60e8\u6b7b\uff0c\u7fa4\u96c4\u65e0\u4e0d\u5401\u55df\u53f9 \u606f\uff0c\u5f15\u4e3a\u9274\u6212\u3002\u7518\u51e4\u6c60\u60f3\u8d77\u4ed6\u5f53\u5e74\u66ff\u5e08\u4f20\u827a\uff0c\u5728\u72ec\u81c2\u795e\u5c3c\u5893\u524d\u53f9\u9053\uff1a\u201c\u82e5\u4f60\u80af\u9075\u5e08\u5085\u9057\u8bad\uff0c \u4f55\u81f3\u843d\u5f97\u4eca\u65e5\u4e0b\u573a\u3002\u201d\u4f17\u4eba\u5546\u8bae\uff0c\u628a\u4ed6\u57cb\u5728\u5e08\u5085\u5893\u65c1\uff0c\u7acb\u7891\u8bf4\u660e\u6b64\u4e8b\uff0c\u4f7f\u540e\u4e16\u4e4b\u4eba\uff0c\u6c38\u4ee5\u4e3a \u6212\u3002\u4f17\u4eba\u63a8\u5b9a\u7531\u5415\u56db\u5a18\u6267\u7b14\uff0c\u5415\u56db\u5a18\u8d70\u8fdb\u72ec\u81c2\u795e\u5c3c\u751f\u524d\u6240\u4f4f\u7684\u77f3\u5ba4\uff0c\u548c\u8def\u6c11\u77bb\u5171\u540c\u659f\u914c\u7891 \u6587\u3002\u7518\u51e4\u6c60\u72ec\u5750\u4e00\u65c1\uff0c\u9ed8\u7136\u4e0d\u8bed\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u4f3c\u6709\u4eba\u58f0\uff0c\u5415\u56db\u5a18\u4e5f\u60ca\u89c9\uff0c\u6025\u5fd9\u63a8\u95e8\u51fa\u53bb\u3002\u8d70 \u5230\u5893\u5730\uff0c\u6e3a\u65e0\u4eba\u8e2a\uff0c\u4f46\u4e86\u56e0\u7684\u9996\u7ea7\uff0c\u5df2\u7ecf\u4e0d\u89c1\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u6b63\u559c\u9099\u5c71\u8bdb\u53db\u8d3c\uff0c\u53c8\u60ca\u767d\u53e3\u5931\u5934\u9885\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u767d\u65e5\u98de\u5934\u3000\u540c\u95e8\u589e\u654c\u5ffe\u3000\u6e05\u6d41\u7167\u5f71\u3000\u7389\u5973\u8d39\u7591\u731c \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u4e03\u56de \u767d\u65e5\u98de\u5934\u3000\u540c\u95e8\u589e\u654c\u5ffe \u6e05\u6d41\u7167\u5f71\u3000\u7389\u5973\u8d39\u7591\u731c \u3000\u3000\u5415\u56db\u5a18\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u67f3\u7709\u5012\u7ad6\uff0c\u9053\uff1a\u201c\u662f\u8c01\u8fd9\u6837\u5927\u80c6\uff0c\u5c45\u7136\u6562\u4e0a\u9099\u5c71\u5077\u5934\uff1f\u201d\u7518\u51e4\u6c60 \u4f0f\u5730\u542c\u58f0\uff0c\u9053\uff1a\u201c\u8fd8\u672a\u53bb\u8fdc\uff0c\u5feb\u5411\u5317\u8ffd\uff01\u201d\u767d\u6cf0\u5b98\u3001\u8def\u6c11\u77bb\u7b49\u540c\u95e8\u5144\u5f1f\u4e0e\u5173\u4e1c\u56db\u4fa0\u7eb7\u7eb7\u8ffd \u51fa\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u70e6\u6717\u6708\u7985\u5e08\u4e0e\u5143\u9738\u5927\u54e5\u5728\u6b64\u9547\u5b88\uff0c\u4e09\u54e5\u4e94\u54e5\u548c\u6768\u8001\u82f1\u96c4\u5230\u5357\u8fb9\u51fa\u53e3\u5de1\u89c6\u3002 \u4e8c\u54e5\u56db\u54e5\u5728\u5eb5\u5185\u5b88\u7075\uff0c\u5176\u4f59\u7684\u4eba\u968f\u6211\u8ffd\u53bb\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u4f0f\u5730\u542c\u58f0\u4e4b\u6280\u767e\u4e0d\u5931\u4e00\uff0c\u8ffd\u4e86\u4e00\u9635\uff0c\u679c\u7136\u53d1\u73b0\u6709\u4e00\u884c\u56db\u4eba\uff0c \u6b63\u81ea\u98de\u5954\u4e0b\u5c71\u3002\u5415\u56db\u5a18\u8f7b\u529f\u4e00\u5c55\uff0c\u6d41\u661f\u9668\u77f3\u822c\u76f4\u51b2\u4e0b\u53bb\uff0c\u500f\u5ffd\u5230\u4e86\u5c71\u8170\u3002\u5b9a\u773c\u4e00\u770b\uff0c\u90a3\u56db\u4eba \u539f\u6765\u662f\u5929\u53f6\u6563\u4eba\u3001\u8f66\u8f9f\u90aa\u3001\u8463\u5de8\u5ddd\u548c\u7518\u5929\u9f99\u3002 \u3000\u3000\u539f\u6765\u5e74\u7fb9\u5c27\u81ea\u903c\u53cd\u4e86\u56e0\u4e4b\u540e\uff0c\u5fc3\u4e2d\u60f4\u60f4\uff0c\u56de\u5230\u519b\u8425\uff0c\u53ea\u89c1\u519b\u4e2d\u6c14\u6c1b\u8083\u7a46\uff0c\u60f3\u4e0d\u5230\u7adf\u662f\u5141 \u796f\u5fa1\u9a7e\u4eb2\u4e34\uff0c\u5e74\u7fb9\u5c27\u6025\u5fd9\u8fdb\u8425\u53e9\u89c1\u3002\u5141\u796f\u5bf9\u4ed6\u89e3\u51b3\u5141\u5824\u4e4b\u4e8b\uff0c\u5927\u52a0\u8d5e\u8d4f\u3002\u5e74\u7fb9\u5c27\u5fc3\u4e2d\u7a0d\u5b89\uff0c \u4e58\u673a\u7980\u9053\uff1a\u201c\u4e86\u56e0\u6043\u529f\u50b2\u4e3b\uff0c\u975e\u4f46\u4e0d\u542c\u6211\u7684\u5c06\u4ee4\uff0c\u800c\u4e14\u5728\u80cc\u540e\u8bfd\u8c24\u5723\u4e0a\uff0c\u8bf4\u5723\u4e0a\u80fd\u6709\u4eca\u65e5\uff0c \u5168\u662f\u4ed6\u7684\u529f\u52b3\u3002\u6211\u8bf4\u4e86\u4ed6\u51e0\u53e5\uff0c\u4ed6\u8d1f\u6c14\u8d70\u4e86\u3002\u60f3\u662f\u8981\u56de\u4eac\u9762\u5723\uff0c\u5148\u544a\u6211\u7684\u72b6\u5462\uff01\u201d \u3000\u3000\u5141\u796f\u773c\u73e0\u4e00\u8f6c\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u5012\u60f3\u89c1\u4ed6\uff01\u201d\u5e74\u7fb9\u5c27\u4e00\u9707\uff0c\u9762\u8272\u9752\u767d\u3002\u5141\u796f\u8f7b\u629a\u5e74\u7fb9\u5c27 \u80a9\u5934\uff0c\u9053\uff1a\u201c\u5e74\u5927\u5c06\u519b\uff0c\u4f60\u662f\u6715\u64ce\u5929\u67f1\u77f3\uff0c\u7edf\u7387\u5927\u519b\uff0c\u663c\u591c\u8f9b\u52b3\uff0c\u4f55\u82e6\u4e0e\u8fd9\u4e2a\u5c71\u91ce\u5339\u592b\u751f \u6c14\u3002\u201d\u53c8\u7b11\u9053\uff1a\u201c\u4ed6\u60f3\u89c1\u6211\uff0c\u53ea\u6050\u4ed6\u89c1\u7740\u6211\u65f6\uff0c\u5df2\u8bf4\u4e0d\u51fa\u8bdd\u4e86\uff01\u201d\u7acb\u523b\u628a\u5929\u53f6\u6563\u4eba\u3001\u8f66\u8f9f \u90aa\u3001\u8463\u5de8\u5ddd\u3001\u7518\u5929\u9f99\u56db\u4eba\u53ec\u6765\uff0c\u964d\u65e8\u9053\uff1a\u201c\u9650\u4f60\u4eec\u5728\u5341\u5929\u4e4b\u5185\u5c06\u4e86\u56e0\u9996\u7ea7\u5e26\u6765\u89c1\u6211\uff01\u201d\u5141\u796f \u6b63\u8981\u501a\u91cd\u5e74\u7fb9\u5c27\uff0c\u8d81\u673a\u727a\u7272\u4e86\u56e0\uff0c\u4ee5\u6536\u6000\u67d4\u4e4b\u6548\u3002\u5929\u53f6\u7b49\u56db\u4eba\u51fa\u5e10\u4e4b\u540e\uff0c\u5e74\u7fb9\u5c27\u8dea\u4e0b\u53e9\u5934\uff0c \u9053\uff1a\u201c\u4e3b\u4e0a\u6d2a\u6069\uff0c\u5c0f\u5c06\u809d\u8111\u6d82\u5730\uff0c\u4e0d\u8db3\u56fe\u62a5\u3002\u201d\u5141\u796f\u4e00\u7b11\u5c06\u4ed6\u62c9\u8d77\uff0c\u9053\uff1a\u201c\u6715\u8fd8\u6709\u5927\u4e8b\u4e0e\u4f60 \u5546\u91cf\u3002\u201d\u548c\u5e74\u7fb9\u5c27\u8fdb\u5e10\u540e\u5bc6\u8c08\u3002 \u3000\u3000\u5929\u53f6\u6563\u4eba\u7b49\u6cbf\u9014\u67e5\u63a2\u4e86\u56e0\u8e2a\u8ff9\uff0c\u4e86\u56e0\u76f8\u8c8c\u51f6\u6076\uff0c\u53c8\u662f\u4e2a\u548c\u5c1a\uff0c\u67e5\u8bbf\u5e76\u4e0d\u56f0\u96be\u3002\u5929\u53f6\u7b49\u867d \u77e5\u4e86\u56e0\u5389\u5bb3\uff0c\u4f46\u5fc3\u60f3\u4ee5\u56db\u4eba\u4e4b\u529b\uff0c\u4ea6\u8db3\u4ee5\u5236\u4ed6\u6b7b\u547d\uff0c\u653e\u80c6\u8ffd\u4e0a\u9099\u5c71\u3002\u60f3\u4e0d\u5230\u4e0a\u5230\u9099\u5c71\u4e4b\u65f6\uff0c \u4e86\u56e0\u5df2\u7ecf\u6b7b\u4e86\uff0c\u5929\u53f6\u6563\u4eba\u7b49\u5927\u5403\u4e00\u60ca\uff0c\u614c\u5fd9\u628a\u4e86\u56e0\u9996\u7ea7\u5272\u4e0b\uff0c\u98de\u9003\u4e0b\u5c71\u3002 \u3000\u3000\u5415\u56db\u5a18\u8ffd\u5230\u5c71\u8170\uff0c\u51b7\u7b11\u65a5\u9053\uff1a\u201c\u4f60\u4eec\u597d\u5927\u7684\u80c6\u5b50\uff0c\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u5bb9\u5f97\u4f60\u4eec\u4e71\u95ef \u4e48\uff1f\u201d\u8f66\u8f9f\u90aa\u89c1\u53ea\u662f\u5415\u56db\u5a18\u4e00\u4eba\uff01\u5fc3\u60f3\uff1a\u201c\u4eba\u4eba\u90fd\u8bf4\u8fd9\u8d31\u5a62\u5251\u6cd5\u5389\u5bb3\uff0c\u6211\u4e14\u8bd5\u5979\u4e00\u8bd5\u3002\u201d\u957f \u5251\u4e00\u6307\uff0c\u4e00\u62db\u201c\u6597\u8f6c\u661f\u6a2a\u201d\uff0c\u659c\u523a\u6a2a\u62b9\uff0c\u5415\u56db\u5a18\u4e00\u58f0\u51b7\u7b11\uff0c\u971c\u534e\u5251\u5411\u5916\u4e00\u5c55\uff0c\u970d\u5730\u98de\u8d77\u4e00\u56e2 \u94f6\u5149\uff0c\u53ea\u8f7b\u8f7b\u4e00\u7ede\uff0c\u5c31\u628a\u654c\u62db\u7834\u4e86\u3002\u8f66\u8f9f\u90aa\u6b63\u60f3\u8fdb\u62db\uff0c\u5415\u56db\u5a18\u5251\u5c16\u4e00\u6307\uff0c\u75be\u5982\u7535\u95ea\uff0c\u5237\u7684\u7adf \u5411\u54bd\u5589\u523a\u5230\uff0c\u8f66\u8f9f\u90aa\u6a2a\u5251\u4e00\u6321\uff0c\u5415\u56db\u5a18\u7684\u5251\u4f3c\u94f6\u86c7\u822c\u9661\u6ed1\u4e0b\u6765\uff0c\u8f66\u8f9f\u90aa\u6025\u9000\u4e09\u6b65\uff0c\u8fd8\u662f\u8eb2\u95ea \u4e0d\u4e86\uff0c\u624b\u8155\u88ab\u5251\u5c16\u5212\u4e86\u4e00\u9053\u4f24\u53e3\uff0c\u957f\u5251\u51e0\u4e4e\u628a\u63e1\u4e0d\u7262\u3002\u5929\u53f6\u6563\u4eba\u89c1\u72b6\u5371\u6025\uff0c\u8d76\u5fd9\u6a2a\u653b\u4e00\u638c\uff0c \u8fd9\u624d\u89e3\u4e86\u8f66\u8f9f\u90aa\u4e4b\u5371\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u8d31\u813e\uff0c\u4f60\u628a\u5e08\u5144\u5bb3\u4e86\uff0c\u8be5\u5f53\u4f55\u7f6a\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u6012\u9053\uff1a\u201c\u5e72\u4f60\u4f55\u4e8b\uff1f\u5feb\u628a\u4e86\u56e0\u9996\u7ea7\u8fd8\u6765\uff01\u201d\u5237\u5237\u4e24\u5251\uff0c\u4e0e\u5929\u53f6\u6563\u4eba\u6597\u5728\u4e00\u8d77\uff0c\u8f66 \u8f9f\u90aa\u7ffb\u8eab\u8fdb\u5251\uff0c\u4fa7\u9762\u52a9\u653b\u3002\u8463\u5de8\u5ddd\u53eb\u9053\uff1a\u201c\u70b9\u5b50\u786c\uff0c\u7802\u77f3\u591a\uff0c\u98ce\u7d27\uff0c\u626f\u547c\uff01\u201d\u610f\u601d\u662f\u8bf4\uff1a\u5415 \u56db\u5a18\u4e0d\u597d\u5bf9\u4ed8\uff0c\u800c\u4e14\u5979\u7684\u515a\u7fbd\u53c8\u591a\uff0c\u5df2\u7ecf\u6765\u4e86\uff0c\u98ce\u5934\u4e0d\u5bf9\uff0c\u8fd8\u662f\u8d70\u5427\uff01\u5929\u53f6\u6563\u4eba\u4e00\u8bb0\u201c\u624b\u6325 \u7435\u7436\u201d\uff0c\u9634\u638c\u53cd\u51fb\uff0c\u5415\u56db\u5a18\u8fd8\u4e86\u4e00\u5251\uff0c\u5929\u53f6\u6563\u4eba\u6b66\u529f\u4e0d\u5728\u4e86\u56e0\u4e4b\u4e0b\uff0c\u5de6\u638c\u6539\u62f3\uff0c\u4e00\u62db\u201c\u5378\u6b65 \u642c\u62e6\u649e\u201d\u62a2\u653b\u8fdb\u6765\uff0c\u5415\u56db\u5a18\u56de\u5251\u53cd\u51fb\uff0c\u8f66\u8f9f\u90aa\u865a\u51fb\u4e00\u62db\uff0c\u8d81\u52bf\u5954\u51fa\uff0c\u8fd9\u65f6\u7518\u51e4\u6c60\u3001\u7384\u98ce\u9053\u957f \u548c\u67f3\u5148\u5f00\u5df2\u7ecf\u8d76\u5230\uff0c\u4e0e\u8463\u5de8\u5ddd\u3001\u7518\u5929\u9f99\u4e8c\u4eba\u4ea4\u4e0a\u4e86\u624b\u3002\u8f66\u8f9f\u90aa\u633a\u5251\u51b2\u51fa\uff0c\u51b7\u4e0d\u9632\u5403\u4e86\u67f3\u5148\u5f00 \u4e00\u8bb0\u94a2\u73af\uff0c\u5934\u75db\u6b32\u88c2\uff0c\u903c\u5f97\u548c\u67f3\u5148\u5f00\u6e38\u6597\u3002 \u3000\u3000\u518d\u8fc7\u4e9b\u65f6\uff0c\u7fa4\u96c4\u4e00\u9f50\u6765\u5230\uff0c\u5929\u53f6\u6563\u4eba\u65e0\u5fc3\u604b\u6218\uff0c\u53cc\u81c2\u4e00\u5206\uff0c\u5de6\u638c\u4e00\u642d\uff0c\u8f7b\u62d4\u5415\u56db\u5a18\u5251 \u628a\uff0c\u53f3\u638c\u53cd\u6765\u622a\u51fb\u5415\u56db\u5a18\u7693\u8155\uff0c\u8fd9\u4e00\u62db\u4ee5\u653b\u4e3a\u5b88\uff0c\u751a\u4e3a\u5389\u5bb3\uff0c\u5415\u56db\u5a18\u4e00\u8f6c\u5251\u950b\uff0c\u8eab\u79fb\u6b65\u6362\uff0c \u53d8\u62db\u659c\u6233\uff0c\u5929\u53f6\u6563\u4eba\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u8131\u51fa\u4e86\u5415\u56db\u5a18\u7684\u5251\u5149\u5708\u5916\u3002 \u3000\u3000\u7384\u51e4\u9053\u957f\u548c\u767d\u6cf0\u5b98\u53cc\u53cc\u62a2\u4e0a\uff0c\u5929\u53f6\u6563\u4eba\u5927\u559d\u4e00\u58f0\uff0c\u5de6\u624b\u4e00\u62db\u201c\u795e\u9e70\u652b\u5154\u201d\uff0c\u5411\u767d\u6cf0\u5b98\u5f53 \u5934\u6293\u4e0b\uff0c\u53f3\u624b\u4e00\u62db\u91dc\u98ce\u94c1\u62d0\uff0c\u8d81\u52bf\u53c8\u907f\u5f00\u4ed6\u53f3\u624b\u5251\u62db\u3002\u5929\u53f6\u6563\u4eba\u5728\u4e00\u4e2a\u7167\u9762\u4e4b\u95f4\uff0c\u63a5\u8fde\u907f\u5f00 \u4e09\u822c\u5175\u5668\u7684\u5939\u51fb\uff0c\u8fd8\u80fd\u8d81\u52bf\u53cd\u51fb\uff0c\u7384\u98ce\u5927\u6012\uff0c\u5251\u62d0\u517c\u65bd\uff0c\u4e0e\u767d\u6cf0\u5b98\u5de6\u53f3\u622a\u51fb\u3002\u5415\u56db\u5a18\u53eb\u9053\uff1a \u201c\u4e0d\u628a\u4e86\u56e0\u9996\u7ea7\u8fd8\u6765\uff0c\u4f11\u60f3\u4e0b\u5f97\u5c71\u53bb\uff01\u201d\u5929\u53f6\u6563\u4eba\u95f7\u58f0\u4e0d\u54cd\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u628a\u767d\u6cf0 \u5b98\u7684\u94a2\u5200\u9707\u5f00\uff0c\u8f6c\u8eab\u53c8\u95ea\u8fc7\u7384\u51e4\u9053\u4eba\u4e00\u5251\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u98d5\u7684\u7a9c\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u5728\u7384\u98ce\u9053\u4eba\u8eab\u65c1 \u98de\u63a0\u51fa\u53bb\uff01 \u3000\u3000\u5415\u56db\u5a18\u7d20\u77e5\u7384\u98ce\u597d\u80dc\uff0c\u6240\u4ee5\u4e0d\u613f\u4ee5\u4e09\u6253\u4e00\uff0c\u5408\u51fb\u654c\u4eba\u3002\u8fd9\u65f6\u89c1\u5929\u53f6\u6563\u4eba\u4ee5\u5927\u6454\u7891\u624b\u9707\u9000 \u5e08\u5144\uff0c\u6025\u5fd9\u633a\u5251\u8ffd\u4e0a\uff0c\u5929\u53f6\u6563\u4eba\u8fc5\u82e5\u72c2\u98da\uff0c\u8863\u8896\u4e00\u62c2\uff0c\u5c06\u67f3\u5148\u5f00\u4e94\u6307\u94a2\u73af\u4e00\u5957\uff0c\u8f66\u8f9f\u90aa\u8131\u51fa \u8eab\u6765\uff0c\u98de\u5954\u4e0b\u5c71\u3002\u5c31\u5728\u8fd9\u970e\u90a3\u95f4\uff0c\u5415\u56db\u5a18\u5df1\u5982\u5f71\u9644\u5f62\uff0c\u4e00\u7f15\u9752\u5149\uff0c\u5251\u950b\u6025\u6307\u5230\u5929\u53f6\u6563\u4eba\u8eab \u540e\uff0c\u5929\u53f6\u6563\u4eba\u4e00\u58f0\u957f\u7b11\uff0c\u53f3\u624b\u4e00\u626c\uff0c\u559d\u9053\uff1a\u201c\u597d\u4e1c\u897f\u6765\u4e86\uff01\u201d\u4e00\u4e2a\u7ea2\u5e03\u5305\u88b1\uff0c\u5411\u5415\u56db\u5a18\u5288\u9762 \u63b7\u6765\uff0c\u5415\u56db\u5a18\u4f4e\u5934\u4f38\u624b\uff0c\u4e00\u628a\u63a5\u7740\uff0c\u5929\u53f6\u6563\u4eba\u53eb\u9053\uff1a\u201c\u8d31\u4e2b\u5934\uff0c\u54ed\u4f60\u7684\u5e08\u5144\u53bb\u5427\uff01\u201d\u5415\u56db\u5a18 \u624b\u6307\u89e6\u5904\uff0c\u5706\u5ffd\u5ffd\u7684\uff0c\u6599\u5305\u88b1\u91cc\u662f\u4e86\u56e0\u7684\u79c3\u5934\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5929\u53f6\u6563\u4eba\u76f4\u51b2\u4e0b\u5c71\u3002 \u3000\u3000\u8463\u5de8\u5ddd\u548c\u7518\u5929\u9f99\u53cc\u6218\u7518\u51e4\u6c60\uff0c\u89c1\u52bf\u4e0d\u597d\uff0c\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u98ce\u8f66\u822c\u7684\u4e00\u8f6c\u4e00\u7ed5\uff0c\u4e5f\u8131\u51fa\u8eab \u6765\uff0c\u8fde\u8dcc\u5e26\u6eda\uff0c\u81ea\u5c71\u5761\u4e0a\u76f4\u6eda\u4e0b\u53bb\u3002\u4ed6\u7ec3\u7684\u662f\u516b\u5366\u6e38\u8eab\u638c\u7684\u529f\u592b\uff0c\u5168\u8eab\u9aa8\u8282\uff0c\u6d3b\u52a8\u5982\u610f\uff0c\u6eda \u4e0b\u5c71\u5761\uff0c\u4e0d\u6015\u53d7\u4f24\u3002\u6765\u9099\u5c71\u7684\u56db\u4eba\u4e2d\uff0c\u7518\u5929\u9f99\u6b66\u529f\u6700\u4f4e\uff0c\u6b63\u60f3\u8ddf\u7740\u9003\u547d\uff0c\u7518\u51e4\u6c60\u5927\u559d\u4e00\u58f0\uff0c \u53cd\u624b\u4e00\u638c\uff0c\u8fc5\u82e5\u5954\u96f7\uff0c\u7518\u5929\u9f99\u65b9\u5954\u51fa\u4e24\u6b65\uff0c\u7a81\u89c9\u80cc\u5fc3\u5982\u4e2d\u94c1\u9524\uff0c\u60e8\u53eb\u4e00\u58f0\uff0c\u53e3\u5410\u9c9c\u8840\u3001\u5728\u5730 \u4e0a\u6253\u6eda\u3002\u7518\u51e4\u6c60\u8fd8\u60f3\u4e0b\u5c71\u8ffd\u654c\uff0c\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4e86\u56e0\u7684\u9996\u7ea7\u5df2\u7ecf\u53d6\u56de\uff0c\u7531\u4ed6\u53bb\u5427\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u89e3\u5f00\u4e8c\u5305\u88b1\uff0c\u53ea\u89c1\u4e86\u56e0\u9996\u7ea7\u9888\u8840\u672a\u5e72\uff0c\u9762\u76ee\u72f0\u72de\u53ef\u6076\uff0c\u9661\u7136\u60f3\u8d77\u4ed6\u6614\u5e74\u66ff\u5e08\u4f20 \u827a\uff0c\u540c\u95e8\u5207\u78cb\u7b49\u7b49\u60c5\u4e8b\uff0c\u4e0d\u89c9\u6ef4\u51fa\u6cea\u6765\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4e5f\u7f62\uff0c\u54b1\u4eec\u66ff\u4ed6\u628a\u9996\u7ea7\u7f1d\u4e0a\uff0c\u4e5f\u7b97\u53eb \u4ed6\u843d\u4e2a\u5168\u5c38\u3002\u201d\u631f\u8d77\u7518\u5929\u9f99\u56de\u5230\u5e08\u5085\u5893\u65c1\u3002 \u3000\u3000\u674e\u6e90\u7b49\u5230\u5357\u8fb9\u5c71\u53e3\u5de1\u89c6\u7684\u4eba\u5c1a\u672a\u56de\u6765\uff0c\u8fd9\u65f6\u5929\u5df2\u9ec4\u660f\uff0c\u665a\u971e\u6210\u7eea\uff0c\u9c7c\u5a18\u5ffd\u9053\uff1a\u201c\u77a7\uff0c\u5929 \u8fb9\u90a3\u4e00\u7247\u7ea2\u4e91\uff01\u201d\u90a3\u7247\u4e91\u7ea2\u5f97\u51fa\u5947\uff0c\u5c31\u50cf\u5929\u8fb9\u5f20\u4e86\u4e00\u5f20\u7ea2\u5e55\uff0c\u7384\u98ce\u9053\uff1a\u201c\u7ea2\u4e91\u51fa\u73b0\uff0c\u5b9a\u6709\u8840 \u5149\u4e4b\u707e\uff01\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff01\u201d\u8fc7\u4e86\u4e00\u9635\uff0c\u7ea2\u4e91\u8d8a\u6269\u8d8a\u5927\uff0c\u5357\u8fb9\u5929\u9645\uff0c\u7ea2\u4e86\u534a\u5929\uff0c \u7518\u51e4\u6c60\u7a81\u7136\u53eb\u9053\uff1a\u201c\u5d69\u5c71\u706b\u8d77\uff01\u201d\u4f17\u4eba\u767b\u9ad8\u4e00\u671b\uff0c\u679c\u89c1\u5d69\u5c71\u90a3\u8fb9\u9690\u73b0\u706b\u5149\u3002 \u3000\u3000\u9099\u5c71\u4e0e\u5d69\u5c71\u9065\u9065\u76f8\u5bf9\uff0c\u76f8\u8ddd\u4e09\u767e\u4f59\u91cc\uff0c\u5728\u9099\u5c71\u8fd9\u8fb9\u770b\u5f97\u89c1\u5d69\u5c71\u706b\u8d77\uff0c\u706b\u52bf\u53ef\u77e5\uff0c\u7518\u51e4\u6c60 \u548c\u5415\u56db\u5a18\u9762\u9762\u76f8\u89d1\uff0c\u767d\u6cf0\u5b98\u5947\u9053\uff1a\u201c\u5d69\u5c71\u5c11\u6797\u5bfa\u4e94\u767e\u50e7\u4f17\uff0c\u4e2a\u4e2a\u8eab\u6000\u7edd\u6280\uff0c\u4e3a\u4f55\u6251\u706b\u4e0d\u4e86\u91ce \u706b\uff0c\u7adf\u8ba9\u706b\u52bf\u71ce\u539f\uff0c\u771f\u771f\u5947\u602a\uff01\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u53ea\u6050\u8fd9\u628a\u706b\u662f\u6e05\u5ef7\u653e\u7684\uff01\u201d\u7518\u51e4\u6c60\u4e00\u9635\u98a4 \u6817\uff0c\u9661\u7136\u5c06\u7518\u5929\u9f99\u4e00\u628a\u6293\u8d77\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u6e05\u5bab\u4f8d\u536b\uff0c\u5b9a\u77e5\u8fd9\u628a\u706b\u662f\u8c01\u653e\u7684\uff01\u201d\u7518\u5929\u9f99\u72e9\u7b11 \u9053\uff1a\u201c\u4f60\u4eec\u5df2\u81ea\u731c\u4e2d\uff0c\u4f55\u5fc5\u95ee\u6211\uff01\u4ee5\u5c11\u6797\u5bfa\u7684\u5a01\u52bf\uff0c\u4e0e\u671d\u5ef7\u76f8\u6297\uff0c\u5c1a\u81ea\u7070\u98de\u70df\u706d\uff01\u4f60\u4eec\u51e0\u4e2a \u4eba\u505a\u5f97\u4e86\u4ec0\u4e48\u5927\u4e8b\uff1f\u8bc6\u76f8\u7684\u5feb\u628a\u8001\u5b50\u653e\u4e86\uff01\u201d\u7518\u51e4\u6c60\u6012\u6781\u51b7\u7b11\uff0c\u9a88\u6307\u671d\u4ed6\u80c1\u4e0b\u4e00\u6233\uff0c\u7518\u5929\u9f99 \u987f\u89c9\u4f53\u5185\u5982\u6709\u65e0\u6570\u5c0f\u86c7\uff0c\u4e71\u7a9c\u4e71\u54ac\uff0c\u75db\u5f97\u8fde\u8fde\u54c0\u53f7\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4f55\u8981\u4e0a\u9099\u5c71\u5077\u5934\uff0c \u5feb\u5feb\u4ece\u5b9e\u62db\u6765\uff01\u4e0d\u7136\u8fd8\u6709\u82e6\u5934\u4f60\u53d7\uff01\u201d\u7518\u5929\u9f99\u903c\u5f97\u5982\u5b9e\u62db\u4e86\u3002\u7518\u51e4\u6c60\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5141 \u796f\u4e0e\u5e74\u7fb9\u5c27\u8fd9\u6837\u72e0\u5fc3\u3002\u53ef\u60dc\u8fd9\u53ae\u6765\u8fdf\u534a\u65e5\uff0c\u5426\u5219\u4e5f\u53ef\u8ba9\u4e86\u56e0\u77e5\u9053\uff0c\u77e5\u9053\u4ed6\u6240\u5356\u8eab\u6295\u9760\u7684\u7687 \u5e1d\uff0c\u662f\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff01\u201d\u8981\u77e5\u9053\u6c5f\u5357\u4e03\u4fa0\u867d\u7136\u8054\u5251\u8bdb\u51f6\uff0c\u5e08\u5144\u5f1f\u4e4b\u60c5\uff0c\u5230\u5e95\u8fd8\u6709\uff0c\u4ee5\u5415\u56db\u5a18\u90a3 \u6837\u75db\u6068\u4e86\u56e0\uff0c\u6fc0\u6597\u4e4b\u65f6\uff0c\u5c1a\u81ea\u624b\u4e0b\u7559\u60c5\uff0c\u7559\u4ed6\u5168\u5c38\uff0c\u800c\u5141\u796f\u53cd\u800c\u8981\u53d6\u4ed6\u9996\u7ea7\uff0c\u8fd9\u600e\u4e0d\u4ee4\u7fa4\u96c4 \u607c\u6012\u3002 \u3000\u3000\u7518\u5929\u9f99\u4f9b\u5b8c\uff0c\u7518\u51e4\u6c60\u4e00\u628a\u5c06\u4ed6\u6293\u8d77\uff0c\u6454\u4e0b\u5c71\u8c37\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e86\u56e0\u6076\u8d2f\u6ee1\u76c8\uff0c\u6b7b\u65e0\u5b8c \u5c38\uff0c\u4e5f\u662f\u5f97\u4e4b\u62a5\u3002\u201d\u5415\u56db\u5a18\u77e5\u9053\u7518\u51e4\u6c60\u5fc3\u4e8b\uff0c\u77e5\u4ed6\u5bf9\u4e86\u56e0\u4e4b\u6b7b\uff0c\u9887\u6709\u60cb\u60dc\u4e4b\u60c5\uff0c\u6240\u4ee5\u51fa\u8bed\u5f00 \u89e3\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u53ea\u662f\u8ba9\u5141\u796f\u90a3\u53ae\uff0c\u53d6\u53bb\u4e86\u56e0\u9996\u7ea7\uff0c\u6211\u786e\u662f\u5fc3\u6709\u4e0d\u7518\u3002\u201d\u5415\u56db\u5a18 \u8f7b\u8f7b\u7b11\u9053\uff1a\u201c\u4e03\u54e5\u4e0d\u5fc5\u607c\u6012\uff0c\u4ed6\u65e5\u6211\u8a93\u5c06\u5141\u796f\u4e4b\u5934\u53d6\u6765\uff0c\u5728\u5e08\u5085\u7075\u524d\u796d\u626b\u4fbf\u662f\u3002\u201d\u7518\u51e4\u6c60\u89c1 \u5415\u56db\u5a18\u5982\u6b64\u8c6a\u8fc8\uff0c\u6697\u6697\u4f69\u670d\u3002\u8fd9\u65f6\u5d69\u5c71\u90a3\u8fb9\u706b\u52bf\u8d8a\u6765\u8d8a\u5927\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u5c31\u5982\u4e00\u6761\u8d64\u8272\u7684\u957f \u86c7\uff0c\u628a\u5d69\u5c71\u56f4\u4e86\u51e0\u531d\uff0c\u767d\u6cf0\u5b98\u62c5\u5fc3\u9053\uff1a\u201c\u4e09\u54e5\u516d\u5f1f\u600e\u4e48\u8fd8\u4e0d\u56de\u6765\uff1f\u201d\u767d\u6cf0\u5b98\u4e0e\u516d\u5e08\u5f1f\u674e\u6e90\u4ea4 \u60c5\u751a\u597d\uff0c\u77e5\u4ed6\u80a9\u4e0a\u6bd2\u5200\u4e4b\u4f24\u672a\u6108\uff0c\u53c8\u5728\u6076\u6597\u4e86\u56e0\u4e4b\u540e\uff0c\u62c5\u5fc3\u4ed6\u4f1a\u51fa\u4e8b\u3002\u5415\u56db\u5a18\u62ac\u5934\u4e00\u671b\uff0c\u7b11 \u9053\uff1a\u201c\u4e94\u54e5\u4e0d\u5fc5\u6302\u5fc3\uff0c\u8fd9\u4e0d\u662f\u4ed6\u4eec\u56de\u6765\u4e86\u5417\uff1f\u54a6\uff0c\u600e\u4e48\u591a\u51fa\u4e86\u4e00\u4e2a\u4eba\uff1f\u201d\u5230\u5357\u8fb9\u5c71\u53e3\u5de1\u89c6\u7684 \u662f\u6768\u4ef2\u82f1\u8def\u6c11\u77bb\u548c\u674e\u6e90\u4e09\u4eba\uff0c\u8fd9\u65f6\u5374\u89c1\u5de6\u4fa7\u5c71\u5cf0\u73b0\u51fa\u56db\u6761\u4eba\u5f71\uff0c\u7518\u51e4\u6c60\u5947\u9053\uff1a\u201c\u8fd9\u53c8\u662f\u90a3\u4f4d \u9ad8\u8d24\u6765\u4e86\uff01\u201d \u3000\u3000\u7728\u773c\u4e4b\u95f4\uff0c\u56db\u4eba\u6765\u5230\u5c71\u4e0a\uff0c\u5415\u56db\u5a18\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u4e00\u74e2\u5927\u5e08\u4f60\u4e5f\u6765\u4e86\uff1f\u5728\u5bbd\u5462\uff1f\u201d\u4e00\u74e2\u548c \u5c1a\u9893\u7136\u5750\u5728\u5730\u4e0a\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u5415\u56db\u5a18\u671d\u4ed6\u9762\u4e0a\u4e00\u770b\uff0c\u4e0d\u89c9\u60ca\u547c\uff1a\u201c\u600e\u4e48\u4f60\u53d7\u4e86\u4f24\u4e86\uff0c \u5e78\u597d\u8fd9\u4f24\u4e0d\u91cd\uff0c\u4e03\u54e5\uff0c\u4f60\u7ed9\u4ed6\u6d3b\u6d3b\u8840\u8def\u3002\u201d \u3000\u3000\u539f\u6765\u90a3\u65e5\u51af\u745b\u79bb\u5f00\u4e86\u5e74\u5bb6\u4e4b\u540e\uff0c\u95ee\u5f97\u9099\u5c71\u79bb\u9648\u7559\u53ea\u662f\u51e0\u767e\u91cc\u8def\u7a0b\uff0c\u8bb0\u5f97\u5e08\u5085\u7684\u5429\u5490\uff0c\u60f3 \u5728\u72ec\u81c2\u795e\u5c3c\u5893\u8fb9\u7684\u6811\u4e0a\u7559\u5b57\u7ed9\u5415\u56db\u5a18\uff0c\u4fbf\u4e5f\u8d76\u5f80\u9099\u5c71\u3002\u4e00\u8def\u60f3\u9053\uff1a\u201c\u8fd9\u5415\u56db\u5a18\u540d\u95fb\u5929\u4e0b\uff0c\u8fde \u5e08\u5085\u4e5f\u79f0\u8d5e\u5979\u7684\u5251\u672f\uff0c\u82e5\u89c1\u7740\u5979\uff0c\u5f97\u597d\u597d\u5411\u5979\u8bf7\u6559\u4e00\u756a\u3002\u201d\u8fd9\u65e5\u5230\u4e86\u9099\u5c71\uff0c\u5df2\u662f\u9ec4\u660f\u65f6\u4efd\uff0c \u5979\u4ece\u5357\u9762\u767b\u5c71\uff0c\u521a\u5165\u5c71\u53e3\uff0c\u5c71\u5d16\u4e0a\u7a81\u7136\u6251\u4e0b\u4e00\u4eba\uff0c\u6076\u58f0\u9a82\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u8fd9\u5973\u8d3c\u5c45\u7136\u4e5f\u6562\u5230\u9099 \u5c71\u6765\u4e86\uff01\u4f60\u7684\u98de\u5200\u5462\uff1f\u518d\u6296\u4e00\u624b\u5427\u3002\u201d\u90a3\u4eba\u53e3\u51fa\u9a82\u8a00\uff0c\u624b\u6325\u8170\u5e26\uff0c\u8eab\u624b\u751a\u662f\u77eb\u6377\uff0c\u8eab\u540e\u8fd8\u8ddf \u7740\u4e00\u4e2a\u8001\u5934\u548c\u4e00\u4e2a\u5c11\u5e74\uff0c\u8fd9\u4e09\u4eba\u6b63\u662f\u5230\u5357\u8fb9\u5c71\u53e3\u5de1\u89c6\u7684\u674e\u6e90\uff0c\u8def\u6c11\u77bb\u548c\u6768\u4ef2\u82f1\u3002\u987b\u77e5\u674e\u6e90\u8eab \u4e3a\u6c5f\u5357\u4e03\u4fa0\u4e4b\u4e00\uff0c\u5728\u6c5f\u6e56\u4e0a\u672a\u66fe\u53d7\u8fc7\u4ec0\u4e48\u632b\u6298\uff0c\u90a3\u65e5\u4e2d\u4e86\u51af\u7433\u4e00\u5200\uff0c\u5f15\u4e3a\u5947\u803b\u5927\u8fb1\uff0c\u6b63\u60f3\u5728 \u9099\u5c71\u4e8b\u4e86\u4e4b\u540e\uff0c\u4fbf\u53bb\u67e5\u8bbf\u51af\u7433\u6765\u5386\uff0c\u62a5\u8fd9\u4e00\u5200\u4e4b\u4ec7\u3002\u800c\u4eca\u9047\u89c1\u51af\u745b\uff0c\u53ea\u9053\u662f\u51af\u7433\u81ea\u6295\u7f57\u7f51\uff0c \u5f53\u7136\u4e0d\u80af\u653e\u8fc7\u3002 \u3000\u3000\u51af\u745b\u542c\u4e86\u8fd9\u51e0\u53e5\u6ca1\u5934\u6ca1\u8111\u7684\u8bdd\uff0c\u6b63\u81ea\u83ab\u540d\u5947\u5999\uff0c\u674e\u6e90\u8170\u5e26\u4e00\u6325\uff0c\u500f\u7684\u5411\u5979\u624b\u8155\u7f20\u6765\uff0c\u51af \u745b\u4e00\u95ea\u95ea\u5f00\uff0c\u95ee\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u4f60\u662f\u8c01\u5440\uff1f\u201d\u674e\u6e90\u6251\u4e0a\u4e00\u6b65\uff0c\u628a\u8170\u5e26\u5f53\u4e3a\u8f6f\u97ad\u65bd\u5c55\uff0c \u53c8\u662f\u4e00\u62db\u201c\u957f\u86c7\u7ed5\u6811\u201d\uff0c\u5411\u51af\u745b\u7ea4\u8170\u75be\u626b\uff0c\u65a5\u9053\uff1a\u201c\u597d\u6b79\u6bd2\u7684\u5973\u8d3c\uff0c\u70e7\u4e86\u7070\u6211\u4e5f\u8ba4\u5f97\u4f60\uff01\u201d \u674e\u6e90\u6b66\u529f\u4e0d\u5728\u767d\u6cf0\u5b98\u4e4b\u4e0b\uff0c\u66f4\u517c\u5728\u6012\u706b\u5934\u4e0a\uff0c\u51fa\u624b\u53c8\u72e0\u53c8\u5feb\uff0c\u51af\u745b\u95ea\u4e86\u51e0\u4e0b\uff0c\u51e0\u4e4e\u7ed9\u4ed6\u8170\u5e26 \u626b\u7740\uff0c\u6012\u9053\uff1a\u201c\u4f60\u4e0d\u8bb2\u7406\uff0c\u4f11\u602a\u6211\u624b\u4e0b\u65e0\u60c5\u3002\u201d\u55d6\u7684\u4e00\u58f0\uff0c\u62d4\u51fa\u5b9d\u5251\uff0c\u674e\u6e90\u4e00\u62db\u843d\u7a7a\uff0c\u987f\u89c9 \u8111\u540e\u751f\u98ce\uff0c\u654c\u4eba\u5df2\u7ed5\u5230\u81ea\u5df1\u8eab\u540e\u3002\u674e\u6e90\u6025\u5fd9\u79fb\u8eab\u8f6c\u6b65\uff0c\u53ea\u89c1\u6643\u6643\u7684\u5251\u5c16\uff0c\u6b63\u5411\u81ea\u5df1\u7684\u54bd\u5589\u6307 \u6765\uff0c\u674e\u6e90\u8170\u5e26\u4e00\u6296\uff0c\u63c9\u8eab\u8fdb\u62db\uff0c\u60f3\u593a\u51af\u745b\u624b\u4e2d\u5b9d\u5251\uff0c\u54ea\u77e5\u51af\u745b\u5251\u6cd5\uff0c\u4e43\u662f\u5929\u5c71\u771f\u4f20\uff0c\u6307\u4e1c\u6253 \u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u5728\u674e\u6e90\u8170\u5e26\u821e\u6210\u7684\u5f27\u5708\u4e2d\u7a7f\u6765\u63d2\u53bb\uff0c\u5251\u5251\u4e0d\u79bb\u674e\u6e90\u8981\u5bb3\u3002\u674e\u6e90\u7eb5\u4e0d\u53d7\u4f24\uff0c\u4e5f \u975e\u51af\u745b\u5bf9\u624b\uff0c\u4f55\u51b5\u53c8\u662f\u5200\u4f24\u672a\u6108\uff0c\u800c\u4e14\u5728\u6fc0\u6597\u4e86\u56e0\u4e4b\u540e\uff01 \u3000\u3000\u8def\u6c11\u77bb\u89c1\u540c\u95e8\u53d7\u56f0\uff0c\u62d4\u5200\u52a9\u6218\uff0c\u6768\u4ef2\u82f1\u770b\u5f97\u751a\u4e3a\u8be7\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u5b50\u7684\u5251\u6cd5\u600e\u4e48\u5982 \u6b64\u5389\u5bb3\uff0c\u6bd4\u6211\u90a3\u4e2a\u4e2b\u5934\u5f3a\u5f97\u591a\u4e86\uff01\u201d\u674e\u8def\u4e8c\u4eba\u5e76\u80a9\u5408\u6218\uff0c\u5140\u662f\u5b88\u591a\u653b\u5c11\uff0c\u5904\u5728\u4e0b\u98ce\uff0c\u6fc0\u6218\u591a \u65f6\uff0c\u8def\u6c11\u77bb\u5200\u6cd5\u4e00\u53d8\uff0c\u811a\u5c16\u4f9d\u7740\u516b\u5366\u638c\u7684\u6b65\u9aa4\uff0c\u8d70\u79bb\u5b98\uff0c\u5954\u574e\u4f4d\uff0c\u4e00\u62db\u201c\u795e\u9f99\u6296\u7532\u201d\uff0c\u5200\u950b \u53cd\u65ab\u654c\u4eba\u80a9\u80db\uff0c\u51af\u745b\u6362\u4e86\u4e24\u62db\uff0c\u6ca1\u80fd\u78b0\u4ed6\u5175\u5203\uff0c\u9887\u4e3a\u5947\u602a\u3002\u539f\u6765\u72ec\u81c2\u795e\u5c3c\u6bd5\u751f\u6c89\u6d78\u6b66\u5b66\uff0c\u6b63 \u6240\u8c13\u5341\u516b\u822c\u6b66\u827a\uff0c\u6837\u6837\u7cbe\u901a\u3002\u6240\u4ee5\u6bcf\u4e2a\u95e8\u5f92\u6240\u4f7f\u7528\u7684\u5175\u5668\uff0c\u90fd\u6709\u6240\u4e0d\u540c\uff0c\u800c\u4e14\u90fd\u6709\u72ec\u7279\u7684\u6280 \u827a\uff0c\u8def\u6c11\u77bb\u4f20\u7684\u662f\u516b\u5366\u7d2b\u91d1\u5200\u6cd5\uff0c\u6309\u7740\u4e94\u884c\u516b\u5366\u65b9\u4f4d\u8fdb\u62db\uff0c\u5b88\u4e2d\u5e26\u653b\uff0c\u4e43\u662f\u4e13\u4e3a\u78b0\u7740\u5f3a\u654c\u65f6 \u7528\u7684\u3002\u51af\u745b\u8fdb\u4e86\u51e0\u62db\uff0c\u6ca1\u6709\u5360\u5f97\u4fbf\u5b9c\uff0c\u5fc3\u4e2d\u4e00\u6025\uff0c\u5251\u62db\u4e00\u53d8\u4e09\uff0c\u7adf\u628a\u5929\u5c71\u5251\u6cd5\u4e2d\u6700\u7cbe\u5999\u7684 \u201c\u987b\u5f25\u5251\u5f0f\u201d\u65bd\u5c55\u51fa\u6765\uff01 \u3000\u3000\u8def\u6c11\u77bb\u5200\u6cd5\u867d\u6709\u4e13\u957f\uff0c\u4f46\u5230\u5e95\u4e0d\u5982\u5929\u5c71\u5251\u6cd5\u7684\u795e\u5999\uff0c\u5c24\u5176\u662f\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u201c\u5927\u987b\u5f25\u201d\u5251 \u5f0f\uff0c\u53d8\u5316\u5965\u701a\uff0c\u4e0d\u53ef\u7a76\u6d01\uff0c\u6bcf\u4e00\u62db\u4e2d\u90fd\u85cf\u6709\u8bb8\u591a\u53d8\u5316\uff0c\u5f53\u4e16\u53ef\u4e0e\u6297\u8861\u7684\u53ea\u6709\u72ec\u81c2\u795e\u5c3c\u4f20\u4e0b\u7684 \u201c\u7384\u5973\u5251\u6cd5\u201d\uff0c\u4f46\u9664\u4e86\u5415\u56db\u5a18\u4e4b\u5916\uff0c\u5176\u4ed6\u4e03\u4eba\u4e60\u827a\u4e4b\u65f6\uff0c\u72ec\u81c2\u795e\u5c3c\u7684\u201c\u7384\u5973\u5251\u6cd5\u201d\u5c1a\u672a\u7ec3 \u6210\uff0c\u5f53\u7136\u4e5f\u65e0\u4ece\u4f20\u6388\u3002\u8def\u6c11\u77bb\u4e0e\u674e\u6e90\u88ab\u51af\u745b\u5251\u6cd5\u4e00\u903c\uff0c\u987f\u65f6\u624b\u5fd9\u811a\u4e71\u3002\u6768\u4ef2\u82f1\u89c1\u52bf\u5934\u4e0d\u597d\uff0c \u53d6\u4e0b\u5f39\u5f13\uff0c\u55d6\u55d6\u6570\u5f39\uff0c\u8fde\u73e0\u75be\u53d1\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u53f7\u79f0\u94c1\u638c\u795e\u5f39\uff0c\u5f39\u5f13\u4e0a\u7684\u529f\u592b\u81ea\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u51af\u745b\u7684\u5251\u6b63\u4f7f\u5230\u75be\u5904\uff0c\u89c1\u6768\u4ef2\u82f1\u53d1 \u51fa\u6697\u5668\uff0c\u5fc3\u4e2d\u7b11\u9053\uff1a\u6211\u7684\u5927\u987b\u5f25\u5251\u5f0f\u6cfc\u6c34\u4e0d\u8fdb\uff0c\u4f60\u8fd9\u5c0f\u5c0f\u5f39\u5b50\u4f55\u8db3\u9053\u54c9\uff01\u5251\u62db\u50ac\u7d27\uff0c\u4ecd\u7136\u76f4 \u53d6\u674e\u8def\u4e8c\u4eba\uff0c\u4e0d\u628a\u6768\u4ef2\u82f1\u7684\u5f39\u5f13\u653e\u5728\u5fc3\u6b62\u3002\u51b7\u4e0d\u9632\u53ee\u5f53\u6570\u58f0\uff0c\u5251\u5c16\u98a4\u52a8\uff0c\u51af\u745b\u864e\u53e3\u9690\u9690\u751f \u75db\uff0c\u5251\u62db\u5168\u5931\u4e86\u51c6\u5934\uff0c\u8def\u6c11\u77bb\u7684\u7d2b\u91d1\u5200\u4e0e\u674e\u6e90\u7684\u8170\u5e26\u4e58\u673a\u5e76\u88ad\uff01 \u3000\u3000\u539f\u6765\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u867d\u662f\u795e\u5999\u4e4b\u6781\uff0c\u6768\u4ef2\u82f1\u7684\u795e\u5f39\u4e5f\u7684\u786e\u6253\u4e0d\u8fdb\u53bb\uff0c\u53ef\u662f\u51af\u745b\u529f\u529b\u5230\u5e95\u7a0d \u5dee\uff0c\u6768\u4ef2\u82f1\u7684\u795e\u5f39\u975e\u4f46\u51c6\u5934\u6781\u597d\uff0c\u800c\u4e14\u52b2\u529b\u5947\u5927\uff0c\u8981\u4e0d\u7136\u4ed6\u7684\u5f39\u5f13\u600e\u80fd\u79f0\u4e3a\u5929\u4e0b\u7b2c\u4e00\uff1f\u8fd8\u5e78 \u51af\u745b\u7684\u5251\u4e43\u662f\u5b9d\u5251\uff0c\u8fd9\u624d\u4e0d\u81f4\u88ab\u5f39\u5f13\u6253\u5d29\u7f3a\u53e3\u3002 \u3000\u3000\u51af\u745b\u5927\u5403\u4e00\u60ca\uff0c\u5e78\u5728\u6768\u4ef2\u82f1\u7684\u795e\u5f39\u4e00\u9635\u5bc6\u88ad\uff0c\u8fc5\u5373\u8fc7\u53bb\uff0c\u5230\u674e\u8def\u4e8c\u4eba\u8fdb\u62db\u4e4b\u65f6\uff0c\u5979\u5df2\u80fd \u7f13\u4e86\u53e3\u6c14\uff0c\u8fd0\u5251\u62b5\u654c\u3002 \u3000\u3000\u6b8a\u4e0d\u77e5\u51af\u745b\u56fa\u7136\u5403\u60ca\uff0c\u800c\u6768\u4ef2\u82f1\u5403\u60ca\u66f4\u751a\uff0c\u4ed6\u7684\u795e\u5f39\u5929\u4e0b\u65e0\u654c\uff0c\u7adf\u7136\u4e00\u9897\u6253\u4e0d\u8fdb\u53bb\uff0c\u542c \u90a3\u567c\u556a\u4e4b\u58f0\uff0c\u5bc6\u5982\u7092\u8c46\uff0c\u94c1\u5f39\u5b50\u60f3\u662f\u5168\u88ab\u654c\u4eba\u7684\u5b9d\u5251\u5288\u88c2\u4e86\uff01\u82e5\u7136\u654c\u4eba\u662f\u4eab\u6709\u76db\u540d\u7684\u4eba\u7269\uff0c \u5982\u4e86\u56e0\u3001\u7518\u5929\u9f99\u3001\u5929\u53f6\u6563\u4eba\u6709\u6b64\u529f\u529b\u5c1a\u4e0d\u51fa\u5947\uff0c\u4f46\u654c\u4eba\u5374\u4ec5\u4ec5\u662f\u4e2a\u5341\u591a\u5c81\u7684\u5973\u5a03\u5b50\uff01\u6768\u4ef2\u82f1 \u4e0d\u77e5\uff0c\u51af\u745b\u6240\u4f7f\u7684\u65ad\u7389\u5251\u662f\u5b9d\u5251\uff0c\u52a0\u4e0a\u5979\u6ef4\u6c34\u4e0d\u5165\u7684\u5251\u6cd5\uff0c\u8fd9\u624d\u80fd\u591f\u62b5\u5fa1\u3002 \u3000\u3000\u5f39\u5f13\u4e00\u505c\uff0c\u674e\u6e90\u4e0e\u8def\u6c11\u77bb\u53c8\u7ed9\u903c\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u674e\u6e90\u53eb\u9053\uff1a\u201c\u6768\u8001\u524d\u8f88\uff0c\u5feb\u53d1\u5f39\u5b50\uff01\u201d\u6768 \u4ef2\u82f1\u62c9\u5f13\u518d\u5c04\uff0c\u5982\u6b64\u4e00\u677e\u4e00\u7d27\uff0c\u674e\u8def\u4e8c\u4eba\u624d\u582a\u582a\u4e0e\u51af\u745b\u6253\u4e2a\u5e73\u624b\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u6768\u4ef2\u82f1\u4e00\u5323\u516d\u5341\u56db\u7c92\u94c1\u5f39\u5df2\u5feb\u53d1\u5b8c\uff0c\u51af\u745b\u5140\u662f\u672a\u9000\uff0c\u6768\u4ef2\u82f1\u4e43\u662f\u5317\u4e94\u7701\u7684\u6b66 \u6797\u9886\u8896\uff0c\u6b8a\u4e0d\u60f3\u5f03\u5f39\u8fd0\u638c\uff0c\u52a9\u674e\u8def\u4e8c\u4eba\u5408\u653b\u4e00\u4e2a\u5973\u5b69\u3002\u800c\u4e14\u6768\u4ef2\u82f1\u770b\u4e86\u51af\u745b\u7684\u5251\u6cd5\uff0c\u867d\u4e0d\u8bc6 \u5176\u540d\uff0c\u4f46\u4e5f\u6697\u6697\u4f69\u670d\uff0c\u601c\u624d\u4e4b\u5ff5\u4e00\u8d77\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6b64\u5730\u4e43\u662f\u72ec\u81c2\u795e\u5c3c\u9675\u5893\u6240\u5728\uff0c\u4eca\u65e5\u6b66\u6797 \u7fa4\u96c4\u5728\u6b64\u516c\u796d\uff0c\u600e\u53ef\u643a\u5251\u4e0a\u5c71\uff1f\u5feb\u7ed9\u6211\u6eda\u4e0b\u5c71\u53bb\uff01\u201d\u6b64\u8bdd\u660e\u662f\u793a\u610f\u53eb\u5979\u9003\u8d70\uff0c\u53ef\u662f\u51af\u745b\u6b63\u5728 \u4e0a\u98ce\uff0c\u542c\u4e86\u6b64\u8a00\uff0c\u8d8a\u53d1\u706b\u8d77\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u7684\u5e08\u5085\u90fd\u6ca1\u6709\u8fd9\u4e48\u5927\u7684\u81ed\u67b6\u5b50\uff0c\u4f60\u8fd9\u8001\u5934\u6562\u559d\u6211\u4e0b \u5c71\uff1f\u201d\u5251\u62db\u8d8a\u53d1\u50ac\u7d27\u3002\u6fc0\u6218\u4e2d\u53ea\u542c\u5f97\u4e00\u9635\u65ad\u91d1\u5207\u7389\u4e4b\u58f0\uff0c\u8def\u6c11\u77bb\u7684\u516b\u5366\u7d2b\u91d1\u5200\u7ed9\u51af\u745b\u7684\u5b9d\u5251 \u524a\u6210\u4e24\u6bb5\u3002\u6768\u4ef2\u82f1\u5927\u60ca\uff0c\u987e\u4e0d\u4e86\u8eab\u4efd\uff0c\u63d0\u8d77\u94c1\u5f39\u5f13\uff0c\u4e0a\u524d\u906e\u6321\uff0c\u8fd8\u672a\u5954\u5230\u573a\u5fc3\uff0c\u53ea\u89c1\u8def\u6c11\u77bb \u5df2\u88ab\u51af\u745b\u8feb\u5230\u5d16\u8fb9\uff0c\u8def\u6c11\u77bb\u817e\u632a\u8eb2\u95ea\uff0c\u8fde\u8fd0\u51e0\u79cd\u8eab\u6cd5\uff0c\u90fd\u8131\u4e0d\u4e86\u5979\u7684\u5251\u5149\u7b3c\u7f69\uff01 \u3000\u3000\u51af\u745b\u7684\u5fc3\u610f\u5176\u5b9e\u5e76\u4e0d\u662f\u60f3\u5c06\u8def\u6c11\u77bb\u7f6e\u4e8e\u6b7b\u5730\uff0c\u5979\u53ea\u662f\u6068\u4ed6\u4eec\u65e0\u6545\u6b3a\u8d1f\uff1b\u6240\u4ee5\u60f3\u60e9\u6212\u4e00\u4e0b \u4ed6\u4eec\u3002\u6768\u4ef2\u82f1\u4e00\u6025\uff0c\u628a\u6700\u540e\u4e09\u7c92\u94c1\u5f39\u6253\u51fa\uff0c\u51af\u745b\u7f13\u4e86\u4e00\u7f13\uff0c\u5c71\u5d16\u4e0b\u7a81\u7136\u7a9c\u4e0a\u4e00\u4eba\uff0c\u8def\u6c11\u77bb\u53eb \u9053\uff1a\u201c\u4e00\u74e2\u5927\u5e08\u6551\u6211\uff01\u201d\u90a3\u548c\u5c1a\u4e00\u62f3\u6363\u51fa\uff0c\u84e6\u7136\u5927\u53eb\u4e00\u58f0\uff0c\u98de\u63a0\u4e08\u8bb8\uff0c\u51af\u745b\u4e5f\u53eb\u4e86\u4e00\u58f0\uff0c\u50cf \u65ad\u7ebf\u98ce\u7b5d\u98d8\u4e0b\u5c71\u8c37\u3002 \u3000\u3000\u539f\u6765\u4e00\u74e2\u548c\u5c1a\u4e0e\u6c5f\u5357\u4e03\u4fa0\u4e43\u662f\u81f3\u4ea4\uff0c\u542c\u5f97\u8def\u6c11\u77bb\u53eb\u58f0\uff0c\u8d76\u7740\u6551\u4eba\uff0c\u53c8\u89c1\u51af\u745b\u53ea\u662f\u4e2a\u5e74\u7eaa \u8f7b\u8f7b\u7684\u5973\u5b69\u5b50\uff0c\u672a\u9632\u654c\uff0c\u5148\u6551\u53cb\uff0c\u4e00\u62db\u201c\u6392\u5c71\u8fd0\u638c\u201d\uff0c\u7a81\u63a8\u51fa\u53bb\uff0c\u638c\u529b\u867d\u7136\u96c4\u52b2\uff0c\u4f46\u4e2d\u9632\u95e8 \u6237\u5927\u5f00\uff0c\u51af\u745b\u5251\u6cd5\u901a\u7384\uff0c\u5251\u5c16\u4e00\u98a4\uff0c\u5df2\u523a\u5230\u4ed6\u54bd\u5589\u4e0b\u7684\u201c\u6108\u6c14\u7a74\u201d\uff0c\u8fd8\u5e78\u5728\u51af\u745b\u5e76\u4e0d\u84c4\u610f\u4f24 \u4eba\uff0c\u89c1\u4e00\u74e2\u95ea\u8eab\u9000\u907f\uff0c\u5251\u5c16\u8d81\u52bf\u4e00\u504f\uff0c\u5728\u4e00\u74e2\u80a9\u5934\u523a\u4e86\u4e00\u5251\uff0c\u4f46\u81ea\u5df1\u4e5f\u7ed9\u654c\u4eba\u638c\u529b\u9707\u5f97\u7acb\u8db3 \u4e0d\u7a33\uff0c\u98d8\u4e0b\u5c71\u8c37\u3002 \u3000\u3000\u4e00\u74e2\u548c\u5c1a\u6b66\u529f\u7cbe\u6e5b\uff0c\u5415\u56db\u5a18\u89c1\u4ed6\u53d7\u4f24\uff0c\u751a\u4e3a\u8be7\u5f02\uff0c\u95ee\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u5f3a\u654c\u6765\u4e86\uff1f\u201d \u8def\u6c11\u77bb\u6068\u6068\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u5728\u676d\u5dde\u9047\u5230\u7684\u90a3\u4e2a\u91ce\u4e2b\u5934\uff0c\u8fd9\u4e2b\u5934\u597d\u4e0d\u72e0\u6bd2\uff0c\u201d\u674e\u6e90\u4e5f\u9053\uff1a\u201c\u6211\u6240 \u4e2d\u7684\u6bd2\u5200\uff0c\u4e5f\u662f\u8fd9\u4e2b\u5934\u653e\u7684\u3002\u6211\u4e0e\u5979\u7d20\u4e0d\u76f8\u8bc6\uff0c\u5979\u7adf\u7136\u4e0b\u6b64\u6bd2\u624b\uff0c\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5c31\u8fd9\u6837\u90aa\u6076\uff0c \u957f\u5927\u4e86\u5b9a\u662f\u6b66\u6797\u4e00\u60a3\uff0c\u516b\u59b9\uff0c\u54b1\u4eec\u8d81\u5979\u7fbd\u6bdb\u672a\u4e30\uff0c\u628a\u5979\u9664\u4e86\u5427\u3002\u201d\u674e\u6e90\u4e00\u5230\u9099\u5c71\uff0c\u5c31\u4e0e\u4f17\u540c \u95e8\u5408\u6597\u4e86\u56e0\uff0c\u6240\u4ee5\u672a\u6709\u6687\u8bf4\u53ca\u53d7\u4f24\u4e4b\u4e8b\u3002\u5415\u56db\u5a18\u542c\u4e86\uff0c\u9887\u4e3a\u60ca\u8be7\uff0c\u5fc3\u60f3\uff0c\u51af\u7433\u6b66\u529f\u867d\u7136\u4e0d \u9519\uff0c\u4f46\u8981\u8bf4\u5979\u4f24\u5f97\u4e86\u4e00\u74e2\u5927\u5e08\uff0c\u5b9e\u96be\u4ee4\u4eba\u7f6e\u4fe1\u3002\u5728\u676d\u5dde\u89c1\u5979\u8ddd\u4eca\u4e0d\u8fc7\u4e00\u5e74\uff0c\u96be\u9053\u5979\u7684\u6b66\u529f\u5c31 \u7cbe\u8fdb\u81f3\u6b64\uff1f\u6b63\u60f3\u8bf4\u8bdd\uff0c\u4e00\u74e2\u548c\u5c1a\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u5973\u5b69\u5b50\u5e76\u4e0d\u72e0\u6bd2\uff0c\u8def\u5144\u674e\u5144\u602a \u9519\u4eba\u4e86\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u65e0\u4e0d\u60ca\u5947\uff1a\u600e\u4e48\u4e00\u74e2\u81ea\u5df1\u53d7\u4e86\u5251\u4f24\uff0c\u8fd8\u66ff\u654c\u4eba\u8fa9\u89e3\u3002\u7518\u51e4\u6c60\u4e5f\u9053\uff1a\u201c\u4f9d \u4e00\u74e2\u5927\u80ba\u7684\u4f24\u52bf\u6765\u770b\uff0c\u8fd9\u5973\u5b69\u5b50\u786e\u975e\u72e0\u6bd2\u4e4b\u4eba\u3002\u201d\u674e\u6e90\u8be7\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bf4\uff1f\u201d\u4e00\u74e2\u89e3\u5f00\u5916 \u8863\uff0c\u9732\u51fa\u80a9\u4e0a\u4f24\u75d5\uff0c\u53ea\u89c1\u5728\u7435\u7436\u9aa8\u8fb9\u6709\u4e00\u9053\u6d45\u6d45\u7684\u4f24\u75d5\uff0c\u5251\u5c16\u6240\u53ca\uff0c\u5728\u201c\u901a\u6d77\u7a74\u201d\u4e0a\u7559\u4e0b\u9489 \u5934\u5927\u7684\u4e00\u4e2a\u7ea2\u70b9\uff0c\u201c\u901a\u6d77\u7a74\u201d\u662f\u4e0a\u8eab\u8840\u8109\u67a2\u7ebd\u4e4b\u4e00\uff0c\u6240\u4ee5\u88ab\u5251\u5c16\u4e00\u70b9\uff0c\u6c14\u8840\u4e0d\u80fd\u6d41\u7545\uff0c\u8981\u7518 \u51e4\u6c60\u66ff\u4ed6\u63a8\u8840\u8fc7\u5bab\u3002 \u3000\u3000\u674e\u6e90\u9053\uff1a\u201c\u51fa\u624b\u4fbf\u523a\u7a74\u9053\u8981\u5bb3\uff0c\u8fd8\u4e0d\u662f\u72e0\u6bd2\u4e48\uff1f\u201d\u4e00\u74e2\u9053\uff1a\u201c\u8fd9\u5973\u5b69\u5b50\u5251\u6cd5\u4e4b\u5999\uff0c\u8001\u7eb3 \u751f\u5e73\u4ec5\u89c1\u3002\u60f3\u662f\u5979\u5251\u4e0e\u5fc3\u5408\uff0c\u6240\u4ee5\u968f\u610f\u6240\u6307\uff0c\u90fd\u662f\u7a74\u9053\u8981\u5bb3\uff0c\u5e76\u4e0d\u662f\u5979\u6709\u610f\u8981\u5c06\u8001\u7eb3\u7f6e\u4e4b\u6b7b \u5730\u3002\u8bd5\u60f3\u5979\u7684\u5251\u5c16\u5df2\u523a\u5230\u7435\u7436\u9aa8\u4e0a\uff0c\u53ea\u8981\u7a0d\u4e3a\u7528\u529b\uff0c\u8001\u7eb3\u7684\u7435\u7436\u9aa8\u8fd8\u4e0d\u88ab\u5979\u6311\u65ad\uff1f\u54c8\uff0c\u90a3\u8001 \u7eb3\u53ef\u53d8\u6210\u4e86\u5e9f\u4eba\uff0c\u518d\u4e0d\u80fd\u7ec3\u6b66\u5566\uff01\u201d\u8def\u6c11\u77bb\u4e00\u60f3\uff1a\u4ee5\u90a3\u5973\u5b69\u5b50\u7684\u5251\u6cd5\uff0c\u679c\u7136\u53ef\u4ee5\u628a\u4e00\u74e2\u5927\u5e08 \u7684\u7435\u7436\u9aa8\u6311\u65ad\u3002\u800c\u4e14\u4e0d\u4f46\u5982\u6b64\uff0c\u4e8b\u540e\u7ec6\u60f3\uff0c\u5979\u5bf9\u81ea\u5df1\u4e5f\u4f3c\u4e4e\u624b\u4e0b\u7559\u60c5\uff0c\u8981\u4e0d\u7136\u6709\u597d\u51e0\u6b21\u5979\u90fd \u53ef\u4ee5\u628a\u81ea\u5df1\u523a\u4f24\u3002\u4e0d\u7981\u5927\u60d1\u4e0d\u89e3\u3002 \u3000\u3000\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4e0d\u7ba1\u5979\u662f\u5426\u90aa\u6076\uff0c\u6211\u4eec\u90fd\u4e0d\u80fd\u9664\u5979\u3002\u201d\u674e\u6e90\u9053\uff1a\u201c\u600e\u4e48\u516b\u59b9\u8ba4\u5f97\u5979\u5417\uff1f \u5979\u662f\u8c01\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u53ef\u80fd\u662f\u5929\u5c71\u6613\u8001\u524d\u8f88\u7684\u4f20\u4eba\uff0c\u4e5f\u53ef\u80fd\u662f\u53cc\u9b54\u7684\u5f92\u5f1f\u3002\u201d\u8def\u6c11\u77bb\u5947 \u9053\uff1a\u201c\u8fd9\u600e\u4e48\u8bf4\uff1f\u201d\u5415\u56db\u5a18\u6b63\u60f3\u628a\u5510\u6653\u6f9c\u5bf9\u5979\u6240\u8bb2\u7684\u6545\u4e8b\u8bf4\u51fa\uff0c\u770b\u4e86\u4e00\u74e2\u4e00\u773c\uff0c\u5ffd\u53c8\u505c\u4f4f\u3002 \u3000\u3000\u8def\u6c11\u77bb\u9053\uff1a\u201c\u516b\u59b9\u600e\u4e48\u4e0d\u8bf4\u4e0b\u53bb\uff1f\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u8fd9\u6545\u4e8b\u592a\u957f\uff0c\u4e00\u74e2\u5927\u5e08\u8fdc\u6765\uff0c\u5fc5\u6709 \u8981\u4e8b\uff0c\u54b1\u4eec\u5148\u542c\u4e00\u74e2\u5927\u5e08\u8bf4\u5427\uff0c\u201d\u4e00\u74e2\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u8981\u4e8b\uff0c\u5728\u5bbd\u89c1\u4f60\u4e45\u4e0d\u56de\u6765\uff0c\u53eb\u6211\u643a \u4e66\u627e\u4f60\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u90a3\u4e48\u53ea\u7559\u4ed6\u4e00\u4eba\u5728\u4ed9\u971e\u5cad\u5417\uff1f\u201d\u4e00\u74e2\u9053\uff1a\u201c\u5728\u5bbd\u8fdb\u955c\u771f\u5feb\uff0c\u73b0\u5728\u975e \u4f46\u5df2\u80fd\u884c\u8d70\u81ea\u5982\uff0c\u800c\u4e14\u5185\u529f\u4e5f\u6709\u4e86\u70b9\u6839\u62b5\u4e86\u3002\u4ed6\u5df2\u7ecf\u4e0d\u7528\u522b\u4eba\u7167\u987e\u5566\uff01\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4ed6\u672a \u4e0b\u8fc7\u5c71\u5427\uff1f\u201d\u5415\u56db\u5a18\u77e5\u9053\u5728\u5bbd\u6027\u60c5\uff0c\u4ed6\u82e5\u65e0\u7279\u522b\u4e8b\u60c5\uff0c\u5fc5\u4e0d\u81f3\u8bf7\u4e00\u74e2\u6765\u627e\u81ea\u5df1\uff0c\u56e0\u6b64\u6000\u7591\u4ed6 \u66fe\u4e0b\u5c71\u89c1\u8fc7\u4ec0\u4e48\u4eba\u7269\u3002 \u3000\u3000\u4e00\u74e2\u4ece\u6000\u4e2d\u6478\u51fa\u4e66\u4fe1\uff0c\u9012\u7ed9\u5415\u56db\u5a18\uff1b\u8fb9\u9053\uff1a\u201c\u4ed6\u672a\u4e0b\u8fc7\u5c71\uff0c\u53ea\u4e0a\u4e2a\u6708\u4f60\u7684\u4e00\u4f4d\u5802\u4f84\u53eb\u505a \u5415\u5143\u7684\u66fe\u627e\u8fc7\u4ed6\u3002\u201d\u5415\u56db\u5a18\u672c\u6765\u9762\u6709\u7b11\u5bb9\uff0c\u62c6\u4fe1\u4e00\u770b\uff0c\u767b\u65f6\u8f6c\u4e3a\u60e8\u767d\u3002\u539f\u6765\u4fe1\u4e2d\u62a5\u544a\u7684\u662f\uff1a \u65b0\u7687\u5e1d\u5373\u4f4d\u4e4b\u540e\uff0c\u7acb\u5373\u6d3e\u5175\u5c06\u5415\u7559\u826f\u7684\u65cf\u4eba\u5168\u6349\u4e86\u53bb\uff0c\u53ea\u9003\u51fa\u4e86\u4e00\u4e2a\u5415\u5143\u3002\u800c\u4e14\u9664\u4e86\u628a\u5415\u7559 \u826f\u7684\u8457\u4f5c\u6bc1\u7248\u711a\u4e66\u4e4b\u5916\uff0c\u53c8\u5c06\u5415\u7559\u826f\u5415\u8446\u4e2d\u7684\u575f\u5893\u6316\u4e86\uff0c\u6398\u51fa\u5c38\u4f53\uff0c\u6233\u5c38\u67ad\u793a\u3002\u5415\u5143\u95f4\u5173\u9003 \u4e0a\u4ed9\u971e\uff0c\u4e0d\u89c1\u5415\u56db\u5a18\uff0c\u53ea\u597d\u5bf9\u6c88\u5728\u5bbd\u8bf4\u77e5\u6b64\u4e8b\u3002\u6c88\u5728\u5bbd\u77e5\u9053\u4e00\u74e2\u548c\u5c1a\u53e4\u9053\u70ed\u80a0\uff0c\u82e5\u7136\u7ed9\u4ed6\u77e5 \u9053\u6b64\u4e8b\uff0c\u5fc5\u7136\u4e0d\u987e\u6027\u547d\uff0c\u95ef\u7262\u52ab\u72f1\uff0c\u975e\u552f\u65e0\u52a9\uff0c\u800c\u4e14\u6709\u5bb3\uff0c\u601d\u4e4b\u518d\u4e09\uff0c\u51b3\u5b9a\u7792\u7740\u4e00\u74e2\uff0c\u53ea\u8bf7 \u4ed6\u5e26\u4fe1\u7ed9\u5415\u56db\u5a18\u3002 \u3000\u3000\u4e00\u74e2\u89c1\u5415\u56db\u5a18\u9762\u8272\u7070\u767d\uff0c\u95ee\u9053\uff1a\u201c\u5728\u5bbd\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d\u5415\u56db\u5a18\u628a\u4fe1\u7ed9\u4ed6\uff0c\u62d4\u51fa\u5b9d\u5251\uff0c\u4e00\u5251 \u5c06\u8eab\u65c1\u4e00\u5757\u5927\u77f3\uff0c\u5288\u4e3a\u4e24\u534a\uff0c\u542b\u6cea\u6012\u9053\uff1a\u201c\u4e0d\u62a5\u6b64\u4ec7\uff0c\u8a93\u4e0d\u4e3a\u4eba\u3002\u201d\u7518\u51e4\u6c60\u7b49\u628a\u5728\u5bbd\u7684\u4fe1\u4f20 \u770b\u4e86\uff0c\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u54b1\u4eec\u90fd\u540c\u53bb\u5427\uff01\u201d \u3000\u3000\u7b2c\u4e8c\u65e5\u7fa4\u96c4\u540c\u4e0b\u9099\u5c71\uff0c\u5415\u56db\u5a18\u60b2\u75db\u7a0d\u51cf\uff0c\u624d\u628a\u5510\u6653\u6f9c\u7684\u6545\u4e8b\u8bf4\u4e86\uff0c\u674e\u6e90\u9053\uff1a\u201c\u5982\u6b64\u8bf4 \u6765\uff0c\u51af\u745b\u51af\u7433\u4e43\u662f\u76f8\u8c8c\u6781\u4e3a\u76f8\u4f3c\u7684\u5b6a\u751f\u59d0\u59b9\uff0c\u90a3\u4e48\u6211\u6240\u78b0\u5230\u7684\u4e5f\u4e0d\u77e5\u662f\u59d0\u59d0\u8fd8\u662f\u59b9\u59b9\u4e86\uff1f\u201d \u5415\u56db\u5a18\u9053\uff1a\u201c\u8fde\u5510\u6653\u6f9c\u90fd\u5206\u8fa8\u4e0d\u51fa\u5979\u4eec\uff0c\u66f4\u4e0d\u8981\u8bf4\u6211\u4eec\u4e86\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8981\u5206\u8fa8\u4ed6\u4eec\uff0c\u53ea \u6709\u4e00\u6cd5\uff0c\u59d0\u59d0\u7684\u6b66\u529f\u8f83\u9ad8\u3002\u201d\u4e00\u74e2\u9053\uff1a\u201c\u5047\u5982\u59b9\u59b9\u7684\u6b66\u529f\u4e5f\u7cbe\u8fdb\u4e86\u5462\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u90a3\u5c31 \u65e0\u6cd5\u8fa8\u8ba4\u4e86\u3002\u201d\u4e00\u74e2\u9053\uff1a\u201c\u5510\u6653\u6f9c\u5462\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4ed6\u548c\u5341\u4e8c\u6307\u795e\u5077\u9648\u5fb7\u6cf0\u5728\u96ea\u9b42\u8c37\u517b \u4f24\u3002\u201d \u3000\u3000\u4e00\u74e2\u53c8\u9053\uff1a\u201c\u5141\u796f\u8fd9\u53ae\u771f\u72e0\uff0c\u4ed6\u767b\u4f4d\u4e0d\u8fc7\u6570\u6708\uff0c\u5c31\u5174\u5927\u72f1\uff0c\u800c\u4e14\u8fd8\u4e00\u628a\u706b\u628a\u5c11\u6797\u5bfa\u70e7 \u4e86\uff01\u201d\u7518\u51e4\u6c60\u95ee\u9053\uff1a\u201c\u4f60\u4ece\u5d69\u5c71\u7ecf\u8fc7\u5417\uff1f\u201d\u4e00\u74e2\u9053\uff1a\u201c\u6211\u5728\u8def\u4e0a\u89c1\u5e74\u7fb9\u5c27\u7684\u5927\u519b\u5f00\u5f80\u5d69\u5c71\uff0c \u53ea\u597d\u7ed5\u9053\u907f\u8fc7\uff0c\u4e0d\u4e45\u5c31\u89c1\u5d69\u5c71\u706b\u8d77\uff0c\u5c11\u6797\u4e94\u767e\u5bfa\u50e7\uff0c\u4e2a\u4e2a\u8eab\u6000\u7edd\u6280\uff0c\u82e5\u7136\u88ab\u4ed6\u4eec\u4e00\u7f51\u6253\u5c3d\uff0c \u8fd9\u5c31\u771f\u662f\u6b66\u6797\u65f7\u53e4\u672a\u6709\u7684\u6d69\u52ab\u4e86\uff01\u201d\u7518\u51e4\u6c60\u60f3\u8d77\u5f53\u5e74\u62a4\u9001\u5370\u5b8f\u4e0a\u5d69\u5c71\uff0c\u66fe\u542c\u65e0\u4f4f\u7985\u5e08\u9884\u6599\u5230 \u5c11\u6797\u5c06\u6709\u5927\u52ab\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u65e0\u4f4f\u7985\u5e08\u9ad8\u77bb\u8fdc\u77a9\uff0c\u4f46\u613f\u4ed6\u80fd\u9003\u8fc7\u8fd9\u573a\u6d69\u52ab\u3002\u201d\u7fa4\u96c4\u9065\u770b\u4e09\u767e\u91cc \u5916\u7684\u5d69\u5c71\uff0c\u706b\u52bf\u672a\u706d\uff0c\u90fd\u4e0d\u7981\u70ef\u5618\u53f9\u606f\u3002 \u3000\u3000\u6309\u4e0b\u5415\u56db\u5a18\u7b49\u4eba\u4e0d\u8868\uff0c\u4e14\u8bf4\u51af\u745b\u9003\u51fa\u9099\u5c71\u4e4b\u540e\uff0c\u601d\u524d\u60f3\u540e\uff0c\u89c9\u5f97\u4e0b\u5c71\u4ee5\u6765\uff0c\u79cd\u79cd\u7ecf\u5386\uff0c \u90fd\u975e\u5e38\u5947\u602a\u3002\u8def\u4e0a\u65e0\u7aef\u78b0\u5230\u4e00\u4e9b\u4eba\u8981\u8ffd\u5979\uff0c\u5e74\u7fb9\u5c27\u53c8\u628a\u5979\u5f53\u6210\u53e6\u4e00\u4e2a\u5973\u5b50\uff0c\u800c\u4eca\u5728\u9099\u5c71\u78b0\u5230 \u7684\u90a3\u4e2a\u624b\u6325\u8170\u5e26\u7684\u5c11\u5e74\uff0c\u53c8\u786c\u8bf4\u81ea\u5df1\u66fe\u7528\u6bd2\u5200\u4f24\u4ed6\uff0c\u771f\u4e0d\u77e5\u662f\u4f55\u9053\u7406\uff01 \u3000\u3000\u51af\u745b\u60f3\u6765\u60f3\u53bb\uff0c\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u4e16\u4e0a\u6709\u4e00\u4e2a\u76f8\u8c8c\u548c\u6211\u6781\u4e3a\u76f8\u4f3c\u7684\u4eba\uff1f\u201d\u8d70\u8fc7\u5c0f\u6cb3\uff0c\u4e34\u6d41 \u7167\u5f71\uff0c\u7edd\u4e16\u4e30\u59ff\uff0c\u8fde\u5979\u81ea\u5df1\u4e5f\u770b\u5f97\u5446\u4e86\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u4e16\u95f4\u4e0a\u8fd8\u6709\u4e00\u4e2a\u8fd9\u6837\u7684\u4eba\uff01\u201d \u5fc3\u4e2d\u72d0\u7591\u5df2\u6781\uff0c\u7a81\u7136\u8f6c\u4e86\u5ff5\u5934\uff0c\u901e\u5411\u9648\u7559\u884c\u53bb\uff0c\u60f3\u518d\u5230\u5e74\u5bb6\uff0c\u52a1\u5fc5\u63a2\u4e2a\u6c34\u843d\u77f3\u51fa\u3002 \u3000\u3000\u201c\u83ab\u975e\u4e16\u95f4\u4e0a\u6709\u4e00\u4e2a\u76f8\u8c8c\u548c\u6211\u6781\u4e3a\u76f8\u4f3c\u7684\u4eba\uff1f\u201d\u201c\u6211\u4e0d\u4fe1\u4e16\u95f4\u4e0a\u8fd8\u6709\u4e00\u4e2a\u8fd9\u6837\u7684\u4eba\uff01\u201d \u51af\u7433\u8fd9\u65f6\uff0c\u4e5f\u662f\u5982\u6b64\u5446\u5446\u7684\u60f3\u3002 \u3000\u3000\u5979\u90a3\u665a\u4ece\u5e74\u7fb9\u5c27\u5367\u5ba4\u4e2d\u7684\u590d\u58c1\u6697\u95e8\u9003\u51fa\u4e4b\u540e\uff0c\u5077\u4e86\u4e00\u9a91\u9a6c\uff0c\u8fde\u591c\u8dd1\u51fa\u9648\u7559\uff0c\u60f3\u8d77\u674e\u6cbb\u628a \u5979\u5f53\u6210\u6613\u5170\u73e0\u7684\u5f92\u5f1f\uff0c\u53e3\u53e3\u58f0\u58f0\u53eb\u5979\u201c\u745b\u59b9\u201d\uff0c\u5e74\u7fb9\u5c27\u4e00\u56de\u6765\u5c31\u95ee\u5979\u6709\u5426\u51fa\u8fc7\u56ed\u5b50\uff0c\u8fd8\u8bf4\u5728 \u8def\u4e0a\u89c1\u5979\u548c\u53cc\u9b54\u6253\u67b6\u3002\u5982\u6b64\u770b\u6765\uff0c\u4e00\u5b9a\u6709\u4e00\u4e2a\u4eba\u6781\u4f3c\u81ea\u5df1\u4e86\u3002\u51af\u7433\u4e5f\u4e0d\u81ea\u89c9\u7684\u4e34\u6d41\u7167\u5f71\uff0c\u987e \u5f71\u81ea\u601c\uff0c\u53eb\u8d77\u6765\u9053\uff1a\u201c\u6211\u4fbf\u662f\u6211\uff0c\u4e16\u95f4\u53ea\u5e94\u6709\u4e00\u4e2a\u6211\uff0c\u90a3\u80fd\u6709\u4e24\u4e2a\u51af\u7433\uff01\u201d\u5979\u672c\u6765\u4e5f\u60f3\u518d\u56de \u5e74\u5bb6\u63a2\u4e2a\u4e0b\u843d\uff0c\u4f46\u4e3a\u4e86\u4ff1\u6015\u4e86\u56e0\uff0c\u53ea\u597d\u832b\u7136\u7684\u5411\u524d\u4e71\u8d70\u3002 \u3000\u3000\u4e00\u65e5\uff0c\u51af\u7433\u6b63\u5728\u8def\u4e0a\u884c\u8d70\uff0c\u5ffd\u89c1\u524d\u9762\u4e00\u9a91\u767d\u9a6c\uff0c\u9a6c\u4e0a\u5750\u7740\u4e00\u4e2a\u5973\u5b50\uff0c\u62ab\u7740\u5927\u7ea2\u6597\u7bf7\uff0c\u767d \u9a6c\u7ea2\u88f3\uff0c\u5341\u5206\u62a2\u773c\uff0c\u770b\u5979\u8170\u60ac\u4f69\u5251\uff0c\u8fd8\u6709\u6697\u5668\u76ae\u56ca\uff0c\u60f3\u5fc5\u4e5f\u662f\u7ec3\u6b66\u7684\u4eba\u3002\u51af\u7433\u5fc3\u9053\uff1a\u6211\u51fa\u9053 \u4ee5\u6765\uff0c\u8fd8\u672a\u78b0\u8fc7\u51e0\u4e2a\u4f1a\u6b66\u827a\u7684\u5973\u5b50\uff0c\u4e0d\u77e5\u6b64\u5973\u6b66\u529f\u5982\u4f55\uff1f\u597d\u5947\u5fc3\u8d77\uff0c\u653e\u9a6c\u5954\u9a70\uff0c\u8ddf\u5728\u5979\u7684\u540e \u9762\u3002\u90a3\u5973\u5b50\u542c\u5f97\u80cc\u540e\u6709\u5f97\u5f97\u8e44\u58f0\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u76ef\u4e86\u51af\u7433\u4e00\u773c\uff0c\u51af\u7433\u89c1\u5979\u7709\u5982\u5f2f\u6708\uff0c\u773c\u82e5\u6668 \u661f\uff0c\u53e3\u5507\u5fae\u5fae\u7fd8\u8d77\uff0c\u7f8e\u662f\u7f8e\u4e86\uff0c\u4f46\u5374\u5e26\u7740\u4e00\u526f\u50b2\u5cb8\u7684\u795e\u60c5\uff0c\u50cf\u662f\u6709\u523a\u7684\u73ab\u7470\u4e1b\u4e2d\u653e\u7740\u4e00\u9897\u6c34 \u5bc6\u6843\uff0c\u7f8e\u8273\u4e4b\u4e2d\u5374\u53c8\u4f3c\u6709\u4e0d\u8c03\u548c\u3001\u548c\u4ee4\u4eba\u4e0d\u8212\u670d\u7684\u611f\u89c9\u3002\u51af\u7433\u8ba8\u538c\u5979\u8fd9\u4e00\u79cd\u795e\u60c5\uff0c\u89c1\u5979\u7741\u5927 \u773c\u775b\u76ef\u7740\u81ea\u5df1\uff0c\u5979\u6a2a\u4e86\u5979\u4e00\u773c\u3002\u90a3\u5973\u4e8e\u5ffd\u7136\u201c\u5550\u201d\u4e86\u4e00\u53e3\uff0c\u4f5c\u4e86\u4e2a\u9119\u5c51\u7684\u795e\u60c5\uff0c\u53cc\u819d\u4e00\u5939\uff0c \u5237\u7684\u4e00\u97ad\uff0c\u628a\u9a6c\u513f\u6253\u5f97\u653e\u5f00\u56db\u8e44\uff0c\u7edd\u5c18\u800c\u53bb\u3002 \u3000\u3000\u51af\u7433\u81ea\u5c0f\u5728\u7687\u5e9c\u957f\u5927\uff0c\u4f17\u4eba\u628a\u5979\u6367\u5f97\u50cf\u51e4\u51f0\u4f3c\u7684\uff0c\u51e0\u66fe\u53d7\u8fc7\u8fd9\u6837\u95f2\u6c14\uff1f\u4e0d\u89c9\u5fc3\u4e2d\u614c\u6012\uff0c \u6697\u9053\uff1a\u8fd9\u5973\u5b50\u597d\u6ca1\u793c\u8c8c\uff0c\u597d\uff0c\u6211\u504f\u8981\u8ddf\u7740\u5979\u8d70\uff0c\u6c14\u5979\u4e00\u6c14\uff0c\u51af\u7433\u5fd8\u8bb0\u81ea\u5df1\u8eab\u4e0a\u7a7f\u7684\u4e43\u662f\u7537\u5b50 \u670d\u88c5\uff0c\u90a3\u5973\u5b50\u89c1\u5979\u7d27\u8ddf\u540e\u9762\uff0c\u4ee5\u4e3a\u5979\u610f\u5b58\u8f7b\u8584\uff0c\u6240\u4ee5\u53d1\u6012\u3002 \u3000\u3000\u82e5\u7136\u8fd9\u5973\u5b50\u662f\u666e\u901a\u7684\u5973\u5b50\u4e5f\u8fd8\u7f62\u4e86\uff0c\u8fd9\u5973\u5b50\u5374\u6b63\u662f\u4ee5\u9a84\u7eb5\u8457\u540d\u6c5f\u6e56\uff0c\u65f6\u65f6\u95f9\u5c0f\u59d0\u813e\u6c14\u7684 \u6768\u67f3\u9752\u3002\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u51fa\u4e8e\u5c0a\u656c\u5979\u7684\u7236\u4eb2\uff0c\u5317\u4e94\u7701\u7684\u6b66\u6797\u9886\u8896\u201c\u94c1\u638c\u795e\u5f39\u201d\u6768\u4ef2\u82f1\uff0c\u6240\u4ee5\u4e2a \u4e2a\u8ba9\u5979\u51e0\u5206\u3002\u5979\u5374\u4ee5\u4e3a\u662f\u81ea\u5df1\u6280\u827a\u51fa\u4f17\uff0c\u4fe8\u7136\u4ee5\u201c\u5973\u4fa0\u201d\u81ea\u5c45\uff0c\u5c31\u76ca\u53d1\u9a84\u7eb5\uff0c\u4efb\u6027\u800c\u884c\u3002\u8fd9 \u6b21\u5979\u7236\u4eb2\u548c\u5510\u6653\u6f9c\u8fdc\u8d74\u4eac\u534e\uff0c\u672c\u6765\u53eb\u5979\u7559\u5b88\u5bb6\u4e2d\uff0c\u4e34\u884c\u8fd8\u90d1\u91cd\u5429\u5490\uff0c\u4e0d\u51c6\u5979\u51fa\u95e8\u4e71\u95ef\u3002\u5979\u7b49 \u4e86\u51e0\u6708\uff0c\u4e0d\u89c1\u7236\u4eb2\u56de\u6765\uff0c\u53c8\u601d\u5ff5\u5510\u6653\u6f9c\u5f97\u7d27\u2014\u2014\u5510\u6653\u6f9c\u867d\u548c\u5979\u8ba2\u4e86\u5a5a\uff0c\u4f46\u5374\u8fdf\u8fdf\u4e0d\u80af\u7ed3\u5a5a\uff0c \u4ee4\u5979\u9887\u4e3a\u4e0d\u6ee1\uff0c\u4f46\u8fd9\u79cd\u4e8b\u60c5\uff0c\u5973\u5b69\u513f\u5bb6\uff0c\u5374\u4e0d\u4fbf\u8bf4\u51fa\u53e3\u6765\uff0c\u2014\u2014\u7adf\u7136\u4e0d\u7406\u7236\u4eb2\u5429\u5490\uff0c\u5355\u4eba\u5339 \u9a6c\uff0c\u72ec\u4e0a\u4eac\u534e\u3002 \u3000\u3000\u51af\u7433\u5374\u662f\u5c0f\u5b69\u5fc3\u6027\uff0c\u89c1\u6768\u67f3\u9752\u795e\u60c5\u50b2\u5cb8\uff0c\u5c61\u5c61\u5728\u9a6c\u80cc\u4e0a\u56de\u7738\u76ef\u5979\uff0c\u8d8a\u53d1\u8981\u8ddf\u7740\u5979\u8d70\u3002\u6768 \u67f3\u9752\u5feb\u65f6\uff0c\u5979\u4e5f\u5feb\u9a6c\u52a0\u97ad\uff0c\u6768\u67f3\u9752\u6162\u65f6\uff0c\u5979\u4e5f\u653e\u677e\u50f5\u7ef3\uff0c\u4e0d\u5373\u4e0d\u79bb\uff0c\u603b\u4fdd\u6301\u5728\u4e09\u5341\u6b65\u8ddd\u79bb\u4e4b \u5185\u3002\u51af\u7433\u5728\u7687\u5e9c\u957f\u5927\uff0c\u4e0d\u77e5\u9a91\u8fc7\u591a\u5c11\u540d\u9a6c\uff0c\u9a91\u672f\u6bd4\u6768\u67f3\u9752\u9ad8\u660e\u5f97\u591a\u3002\u6768\u67f3\u9752\u7ed9\u5979\u903c\u5f97\u6012\u4ece\u5fc3 \u8d77\uff0c\u4e03\u7a83\u751f\u70df\uff0c\u770b\u770b\u5df2\u8d70\u5230\u6797\u8fb9\u8352\u50fb\u4e4b\u5730\uff0c\u84e6\u7136\u52d2\u9a6c\u6a2a\u97ad\uff0c\u6458\u4e0b\u5f39\u5f13\uff0c\u56de\u8116\u53f1\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f \u4e8e\u662f\u5e72\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u51af\u7433\u201c\u54e7\u201d\u7684\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u59d1\u5a18\uff0c\u4f60\u8d70\u4f60\u7684\u8def\uff0c\u6211\u8d70\u6211\u7684\u8def\uff0c\u4f60\u7ba1\u6211\u5e72\u4ec0\u4e48\uff1f\u201d\u6768\u67f3 \u9752\u67f3\u7709\u5012\u7ad6\uff0c\u53f1\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u662f\u90a3\u6761\u7ebf\u4e0a\u7684\uff1f\u80c6\u6562\u8ddf\u8d77\u4f60\u7684\u59d1\u5976\u5976\u6765 \u5566\uff01\u201d\u51af\u7433\u4f38\u51fa\u624b\u6307\u5728\u8138\u4e0a\u4e00\u522e\uff0c\u7b11\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u548c\u4f60\u5bf9\u4eb2\u5bb6\uff0c\u597d\u4e2a\u5927\u59d1\u5a18\uff0c\u4f60\u67e5\u6839\u95ee\u5e95\u505a \u4ec0\u4e48\uff1f\u8fd9\u6761\u8def\u53ef\u53c8\u4e0d\u662f\u4f60\u7684\u3002\u4f60\u80fd\u8d70\u6211\u4e0d\u80fd\u8d70\uff1f\u201d \u3000\u3000\u6768\u67f3\u9752\u563f\u563f\u51b7\u7b11\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d\u51af\u7433\u63a5\u53e3\u7b54\u9053\uff1a\u201c\u77e5\u9053\u4f60\u662f\u51fa\u6765\u627e\u6c49 \u5b50\u7684\u5927\u59d1\u5a18\uff01\u201d\u6768\u67f3\u9752\u674f\u8138\u6da8\u7ea2\uff0c\u6012\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u53e3\u820c\u8f7b\u8584\uff0c\u9976\u4f60\u4e0d\u5f97\uff01\u201d \u4e00\u6280\u5f39\u5f13\uff0c\u6d41\u661f\u822c\u6253\u51fa\u4e09\u7c92\u94c1\u5f39\uff0c\u51af\u7433\u4e00\u4e2a\u201c\u956b\u91cc\u85cf\u8eab\u201d\u8eb2\u8fc7\u7b2c\u4e00\u7c92\uff0c\u53cc\u6307\u4e00\u4f38\u631f\u7740\u4e86\u7b2c\u4e8c \u7c92\uff0c\u8f7b\u8f7b\u4e00\u5f39\uff0c\u53cc\u5f39\u5bf9\u649e\uff0c\u5c06\u6768\u67f3\u9752\u7684\u7b2c\u4e09\u7c92\u94c1\u5f39\u4e5f\u6253\u843d\u4e86\u3002\u4e00\u7ffb\u8eab\u5750\u4e0a\u9a6c\u80cc\uff0c\u7b11\u9053\uff1a \u201c\u54c8\uff0c\u6ca1\u6253\u7740\uff01\u201d \u3000\u3000\u6768\u67f3\u9752\u5927\u6012\uff0c\u5f39\u5f13\u518d\u66f3\uff0c\u5f13\u5982\u6ee1\u6708\uff0c\u5f39\u4f3c\u6d41\u661f\uff0c\u51b0\u96f9\u822c\u98de\u5c04\u8fc7\u53bb\uff0c\u51af\u7433\u53eb\u9053\uff1a\u201c\u54ce\u5440\uff0c \u4e0d\u597d\uff01\u201d\u7a81\u7136\u4e00\u4e2a\u5012\u683d\u8471\u8dcc\u4e0b\u9a6c\u80cc\uff0c\u4f46\u5374\u5e76\u4e0d\u8dcc\u5012\u5730\u4e0a\uff0c\u800c\u662f\u7ea4\u8db3\u8f7b\u52fe\u9a6c\u80cc\uff0c\u5012\u6302\u8d77\u6765\uff0c\u90a3 \u5339\u9a6c\u659c\u523a\u91cc\u51b2\u51fa\u5341\u4f59\u4e08\u5916\uff0c\u6768\u67f3\u9752\u7684\u5f39\u5b50\u55d6\u55d6\u7684\u5728\u9a6c\u80cc\u4e0a\u98de\u8fc7\uff0c\u4ecd\u7136\u6ca1\u6253\u7740\u5979\uff01 \u3000\u3000\u51af\u7433\u6296\u9732\u4e86\u8fd9\u4e00\u624b\u7edd\u5999\u7684\u9a91\u672f\uff0c\u6768\u67f3\u9752\u4e0d\u7981\u6697\u6697\u60ca\u5fc3\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u51af\u7433\u9661\u7136\u53c8\u7ffb \u8eab\u4e0a\u9a6c\uff0c\u53eb\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u63a5\u5200\uff01\u201d\u62cd\u9a6c\u4e00\u51b2\uff0c\u4e24\u67c4\u98de\u5200\u7535\u5c04\u800c\u51fa\uff01\u51af\u7433\u7684\u98de\u5200\u4e4b \u6280\uff0c\u5f97\u8fc7\u949f\u4e07\u5802\u7684\u771f\u4f20\uff0c\u53c8\u5f97\u8fc7\u97e9\u91cd\u5c71\u7684\u6307\u70b9\uff0c\u786e\u662f\u975e\u540c\u5c0f\u53ef\u3002\u6768\u67f3\u9752\u89d1\u51c6\u6765\u52bf\uff0c\u67f3\u8170\u4e00 \u626d\uff0c\u5f85\u95ea\u8fc7\u5979\u7b2c\u4e00\u628a\u98de\u5200\u7136\u540e\u518d\u628a\u7b2c\u4e8c\u628a\u98de\u5200\u6253\u843d\uff0c\u90a3\u6599\u51af\u7433\u7684\u7b2c\u4e8c\u628a\u98de\u5200\u540e\u53d1\u5148\u81f3\uff0c\u6768\u67f3 \u9752\u521a\u5411\u5de6\u8fb9\u4e00\u95ea\uff0c\u4e00\u9053\u5bd2\u5149\u5df2\u7ecf\u75be\u98de\u5230\u9762\u524d\uff0c\u6768\u67f3\u9752\u614c\u5fd9\u628a\u5f39\u5f13\u4e00\u6321\uff0c\u53ea\u542c\u5f97\u201c\u548b\u5693\u201d\u4e00 \u58f0\uff0c\u5f39\u5f13\u7adf\u81ea\u5f53\u4e2d\u65ad\u4e86\u3002\u9888\u8fb9\u4e00\u9635\u51b0\u51c9\uff0c\u7b2c\u4e00\u53e3\u98de\u5200\u53c8\u8d34\u8089\u98de\u8fc7\u3002\u8fd9\u8fd8\u53ea\u662f\u51af\u7433\u610f\u5b58\u620f\u800d\uff0c \u5e76\u4e0d\u7acb\u5fc3\u4f24\u5979\u6027\u547d\uff0c\u8981\u4e0d\u7136\u8fd9\u4e00\u67c4\u98de\u5200\u65e9\u5df2\u7a7f\u5589\u800c\u8fc7\u3002 \u3000\u3000\u6768\u67f3\u9752\u53c8\u60ca\u53c8\u6012\uff0c\u62bd\u51fa\u4f69\u5251\uff0c\u62cd\u9a6c\u4e0a\u524d\uff0c\u8981\u548c\u51af\u7433\u62fc\u4e2a\u6b7b\u6d3b\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u53c8\u4f20\u6765\u4e86\u9a6c\u8e44 \u4e4b\u58f0\uff0c\u51af\u7433\u773c\u5229\uff0c\u5df2\u9690\u9690\u77a7\u89c1\uff0c\u4e8c\u4e09\u91cc\u5916\uff0c\u6765\u4e86\u6570\u9a91\uff0c\u9a6c\u4e0a\u7684\u4eba\u4f3c\u662f\u201c\u516c\u5dee\u201d\u3002\u5fc3\u9053\uff1a\u82e5\u662f \u5141\u796f\u6d3e\u6765\u7684\u4eba\u90a3\u53ef\u4e0d\u597d\u3002\u4e14\u653e\u8fc7\u8fd9\u6cfc\u5a46\u5a18\u5427\u3002\u5de6\u624b\u4e00\u62cd\u9a6c\u9888\uff0c\u90a3\u5339\u9a6c\u4e43\u662f\u5e74\u7fb9\u5c27\u6240\u517b\u7684\u6218 \u9a6c\uff0c\u7ed9\u5979\u4e00\u62cd\uff0c\u84e6\u5730\u957f\u5636\u3002\u6768\u67f3\u9752\u5ffd\u89c1\u51af\u7433\u7684\u9a6c\u75be\u98ce\u822c\u98de\u9a70\u8fc7\u6765\uff0c\u773c\u795e\u4e00\u4e71\uff0c\u5c06\u5251\u671d\u9a6c\u80cc\u4e0a \u51af\u7433\u7684\u8eab\u5f71\u75be\u523a\u4e24\u5251\uff0c\u90a3\u523a\u5f97\u7740\uff0c\u5ffd\u89c9\u5934\u9876\u7ed9\u4eba\u8f7b\u8f7b\u4e00\u62cd\uff0c\u98ce\u58f0\u547c\u547c\uff0c\u90a3\u5339\u9a6c\u5df2\u4ece\u8eab\u8fb9\u98de \u8fc7\uff0c\u6768\u67f3\u9752\u4e00\u6478\u5934\u9876\uff0c\u5934\u4e0a\u6240\u63d2\u7684\u4e24\u6839\u7389\u9497\u5df2\u88ab\u51af\u7433\u53d6\u53bb\u4e86\uff01 \u3000\u3000\u6768\u67f3\u9752\u5927\u6012\uff0c\u62cd\u9a6c\u8ffd\u8d76\uff0c\u8d8a\u8ffd\u8d8a\u8fdc\uff0c\u8fc7\u4e86\u4e9b\u65f6\uff0c\u8fde\u51af\u7433\u7684\u80cc\u5f71\u4e5f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u51af\u7433\u9a6c\u5feb\uff0c\u534a\u4e2a\u65f6\u8fb0\uff0c\u8dd1\u51fa\u4e86\u5eff\u4f59\u91cc\u5916\uff0c\u9065\u89c1\u8fdc\u5904\u5d69\u5c71\u77d7\u7acb\uff0c\u677e\u4e86\u53e3\u6c14\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u542c \u8bf4\u5d69\u5c71\u4e43\u662f\u5c11\u6797\u5bfa\u6240\u5728\uff0c\u5c11\u6797\u5bfa\u6709\u5929\u4e0b\u6b66\u672f\u603b\u6c47\u4e4b\u79f0\uff0c\u6211\u5b66\u4e86\u8fd9\u4e48\u591a\u5404\u5bb6\u5404\u6d3e\u7684\u6b66\u529f\uff0c\u4e0d\u77e5 \u80fd\u5426\u4e0e\u5c11\u6797\u5bfa\u7684\u50e7\u4eba\u6297\u624b\uff0c\u6cb9\u7136\u8d77\u4e86\u5230\u5d69\u5c71\u793c\u62dc\u4e4b\u5fc3\uff0c\u508d\u665a\u65f6\u5206\uff0c\u8d70\u5230\u4e86\u4e00\u4e2a\u5c0f\u9547\uff0c\u540d\u53eb\u90ed \u5bb6\u9547\uff0c\u95ee\u8d77\u9547\u4e0a\u5c45\u6c11\uff0c\u77e5\u6b64\u5730\u79bb\u5d69\u5c71\u53ea\u6709\u4e24\u65e5\u8def\u7a0b\u3001\u9a6c\u5feb\u7684\u5927\u534a\u5929\u5c31\u5230\u4e86\u3002 \u3000\u3000\u51af\u7433\u5728\u9547\u4e0a\u627e\u4e86\u4e00\u95f4\u6700\u597d\u7684\u5ba2\u5e97\u4f4f\u4e0b\uff0c\u5403\u8fc7\u665a\u6273\uff0c\u6b63\u60f3\u5b89\u6b47\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u6709\u5973\u5b50\u7684\u58f0\u97f3 \u8bf4\u9053\uff1a\u201c\u638c\u67dc\u7684\uff0c\u7ed9\u6211\u4e00\u95f4\u6700\u597d\u7684\u4e0a\u623f\uff0c\u6709\u6ca1\u6709\uff1f\u201d\u638c\u67dc\u7684\u606d\u58f0\u7b54\u9053\uff1a\u201c\u6768\u5973\u4fa0\uff0c\u4f60\u8001\u6765 \u8981\uff0c\u6ca1\u6709\u4e5f\u5f97\u6709\u3002\u5c0f\u4e8c\uff0c\u6765\u5440\uff0c\u628a\u8d35\u5ba2\u5e26\u5230\u4e1c\u53a2\u4e0a\u623f\u53bb\u3002\u201d\u51af\u7433\u542c\u5f97\u58f0\u97f3\u597d\u719f\uff0c\u5728\u95e8\u7f1d\u91cc\u5f20 \u671b\u51fa\u6765\uff0c\u89c1\u679c\u7136\u662f\u8def\u4e0a\u78b0\u5230\u7684\u90a3\u4e2a\u5973\u5b50\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u672c\u9886\uff0c\u5c31\u79f0\u8d77\u5973\u4fa0 \u6765\u5566\uff01\u201d\u6768\u67f3\u9752\u8981\u7684\u623f\u95f4\u6b63\u5de7\u5c31\u5728\u5979\u7684\u9694\u90bb\u3002 \u3000\u3000\u6768\u67f3\u9752\u8fdb\u4e86\u5ba2\u623f\uff0c\u638c\u67dc\u7684\u4eb2\u6765\u4f8d\u5019\uff0c\u9001\u8336\u9001\u6c34\u9001\u996d\u5fd9\u4e86\u534a\u5929\uff0c\u6768\u67f3\u9752\u9053\uff1a\u201c\u8f9b\u82e6\u4f60\u5566\uff0c \u738b\u8001\u5934\u3002\u201d\u51af\u7433\u5728\u9694\u5ba4\u542c\u5f97\u94ff\u7136\u6709\u58f0\uff0c\u6768\u67f3\u9752\u9053\uff1a\u201c\u8fd9\u952d\u94f6\u5b50\u8d4f\u7ed9\u4f60\u5566\uff01\u201d\u638c\u67dc\u7684\u9053\uff1a\u201c\u54ce \u5440\uff0c\u5c0f\u7684\u53ef\u4e0d\u6562\u8981\u4f60\u8001\u7684\u94f6\u5b50\uff0c\u6768\u8001\u7237\u5b50\u524d\u5e74\u66ff\u6211\u4eec\u9664\u4e86\u9547\u4e0a\u7684\u6076\u9738\uff0c\u5c0f\u5e97\u4e00\u5e74\u4e5f\u7701\u4e86\u5341\u4e24 \u89c4\u94f6\uff0c\u611f\u6fc0\u8fd8\u6765\u4e0d\u53ca\uff0c\u600e\u597d\u610f\u601d\u8981\u4f60\u7684\u94f6\u5b50\u3002\u201d\u6768\u67f3\u9752\u9053\uff1a\u2018\u90a3\u662f\u6211\u7239\u7684\u4e8b\uff0c\u603b\u4e0d\u80fd\u767d\u4f4f\u767d \u559d\u4f60\u7684\u3002\u201d\u638c\u67dc\u7684\u63a8\u8f9e\u518d\u4e09\uff0c\u8fd9\u624d\u63a5\u4e86\u3002\u9053\uff1a\u201c\u4f60\u8001\u65e9\u5b89\u6b47\u5427\u3002\u201d\u8bf4\u7740\u9000\u51fa\u623f\u95e8\u3002\u51af\u7433\u6697 \u9053\uff1a\u542c\u8fd9\u6837\u8bf4\u6765\uff0c\u8fd9\u6cfc\u5a46\u5a18\u7684\u7236\u4eb2\u5012\u50cf\u662f\u4e2a\u4fa0\u4e49\u4e2d\u4eba\u3002\u63a5\u7740\u542c\u5f97\u9694\u623f\u6089\u6089\u7d22\u7d22\u4e4b\u58f0\u3002\u51af\u7433\u60f3 \u9053\uff1a\u5f85\u5979\u719f\u7761\u4e4b\u540e\uff0c\u6211\u4e14\u641c\u5979\u4e00\u641c\uff0c\u770b\u5979\u662f\u8c01\u7684\u5973\u513f\u3002 \u3000\u3000\u518d\u8fc7\u4e00\u9635\uff0c\u51af\u7433\u542c\u5f97\u9694\u5ba4\u5fae\u6709\u9f3e\u58f0\uff0c\u6b63\u60f3\u8d77\u8eab\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u4eba\u58f0\u6b65\u58f0\uff0c\u5e97\u95e8\u6253\u5f00\uff0c\u4f4f\u5ba2 \u4eec\u60ca\u9192\u9a9a\u52a8\uff0c\u638c\u67dc\u7684\u5927\u58f0\u53eb\u9053\uff1a\u201c\u516c\u5dee\u6765\u67e5\u591c\u5566\uff01\u5ba2\u5b98\u4eec\u90fd\u5b88\u5728\u623f\u91cc\uff0c\u4e0d\u8981\u51fa\u6765\u3002\u201d\u51af\u7433\u5fc3 \u4e2d\u67af\u6151\uff0c\u62ab\u4e86\u8863\u88f3\uff0c\u5750\u5728\u5e8a\u4e0a\u3002\u542c\u90a3\u4e9b\u516c\u5dee\u7684\u811a\u6b65\u58f0\uff0c\u4f3c\u662f\u5206\u6210\u4e24\u8def\uff0c\u5206\u5934\u641c\u67e5\u4e24\u8fb9\u7684\u5ba2\u623f\u3002 \u3000\u3000\u51af\u7433\u60f4\u7136\u6212\u5907\uff0c\u9694\u623f\u9661\u7136\u4f20\u51fa\u5435\u95f9\u4e4b\u58f0\u3002\u5b98\u5dee\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6c5f\u6e56\u7684\u5356\u89e3\u5973\u5b50\u5417\uff0c\u4f60 \u7684\u6c49\u5b50\u5462\uff1f\u201d\u4e00\u4e2a\u5b98\u5dee\u9053\uff1a\u201c\u4eba\u5bb6\u662f\u5927\u95fa\u5973\u5462\uff01\u201d\u6768\u67f3\u9752\u53f1\u9053\uff1a\u201c\u641c\u5b8c\u4e86\u5c31\u7ed9\u6211\u6eda\u51fa\u53bb\u3002\u201d \u5148\u5934\u90a3\u5b98\u5dee\u563b\u563b\u7b11\u9053\uff1a\u201c\u6ca1\u641c\u5b8c\u5462\uff01\u623f\u95f4\u91cc\u641c\u5b8c\u4e86\uff0c\u4f60\u7684\u8eab\u4e0a\u8fd8\u6ca1\u641c\uff01\u201d\u8bed\u97f3\u672a\u843d\uff0c\u53ea\u542c\u5f97 \u556a\u7684\u4e00\u58f0\uff0c\u90a3\u5b98\u5dee\u7684\u8138\u4e0a\u4f3c\u4e4e\u4e2d\u4e86\u4e00\u638c\uff0c\u6740\u732a\u822c\u53eb\u5c06\u8d77\u6765\u3002\u6768\u67f3\u9752\u559d\u9053\uff1a\u201c\u7ed9\u4f60\u59d1\u5976\u5976\u6eda\u51fa \u53bb\uff01\u201d\u8fd9\u4e48\u4e00\u95f9\uff0c\u641c\u4e1c\u8fb9\u5ba2\u623f\u7684\u5b98\u5dee\u987f\u65f6\u8702\u6d8c\u800c\u6765\uff0c\u6768\u67f3\u9752\u5a07\u53f1\u4e00\u58f0\uff1a\u201c\u6eda\uff01\u201d\u5de6\u53f3\u5f00\u5f13\uff0c \u62cd\u62cd\u4e24\u638c\uff0c\u7528\u5bb6\u4f20\u94c1\u638c\u7684\u201c\u529b\u8dcc\u5343\u65a4\u201d\u62db\u6570\uff0c\u628a\u4e24\u540d\u5b98\u5dee\u6454\u51fa\u95e8\u5916\uff0c\u62d4\u51fa\u4f69\u5251\uff0c\u4e00\u8dc3\u800c\u51fa\uff0c \u5b98\u5dee\u53eb\u9053\uff1a\u201c\u597d\u51f6\u7684\u8d3c\u5a46\u5a18\u554a\uff01\u201d \u3000\u3000\u7eb7\u4e71\u4e2d\u6709\u4eba\u559d\u9053\uff1a\u201c\u5148\u628a\u8fd9\u8d3c\u5a46\u5a18\u7f1a\u4e86\uff01\u201d\u987f\u65f6\u5200\u5251\u76f8\u4ea4\uff0c\u5288\u5288\u62cd\u62cd\u7684\u5728\u5916\u95f4\u5929\u4e95\u6253\u5c06 \u8d77\u6765\u3002\u51af\u7433\u542c\u90a3\u5175\u5203\u76f8\u89e6\u4e4b\u58f0\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u54a6\uff0c\u5b98\u5dee\u4e4b\u4e2d\u5c45\u7136\u6709\u5982\u6b64\u9ad8\u624b\uff01\u201d\u63a2\u5934\u7aa5 \u671b\uff0c\u53ea\u89c1\u56db\u540d\u5b98\u5dee\u5c06\u6768\u67f3\u9752\u56f4\u5728\u6838\u5fc3\uff0c\u4e3a\u9996\u7684\u4f7f\u4e00\u67c4\u94a9\u9570\u67aa\uff0c\u541e\u9000\u62bd\u64a4\uff0c\u62db\u62db\u5389\u5bb3\uff0c\u6768\u67f3\u9752 \u7ed9\u903c\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u5f62\u52bf\u5341\u5206\u5371\u6025\u3002\u51af\u7433\u5fc3\u9053\uff1a\u201c\u597d\uff0c\u770b\u5979\u540c\u662f\u5973\u5b50\u4efd\u4e0a\uff0c\u6551\u5979\u4e00\u6551\u3002\u201d \u53cc\u6307\u8fde\u5f39\uff0c\u628a\u53d6\u81ea\u626c\u67f3\u9752\u5934\u4e0a\u7684\u4e24\u652f\u7389\u7c2a\u5f39\u5c06\u51fa\u53bb\uff0c\u6768\u67f3\u9752\u9661\u89c1\u51af\u7433\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a \u201c\u539f\u6765\u8fd9\u574f\u86cb\u548c\u5b98\u5bb6\u7684\u9e70\u72ac\u4e43\u662f\u4e00\u4f19\uff01\u8fd9\u56de\u7cdf\u4e86\uff01\u201d \u3000\u3000\u4f7f\u94a9\u9570\u67aa\u7684\u90a3\u4eba\u8eab\u5f62\u4e00\u95ea\uff0c\u53eb\u9053\uff1a\u201c\u7559\u795e\uff01\u201d\u56f4\u7740\u6768\u67f3\u9752\u7684\u4e24\u540d\u5b98\u5dee\u7a81\u7136\u5012\u5728\u5730\u4e0a\uff0c\u539f \u6765\u662f\u4e2d\u4e86\u51af\u7433\u6697\u5668\u6253\u7a74\u7684\u9053\u513f\uff0c\u6768\u67f3\u9752\u8dc3\u51fa\u5708\u5b50\uff0c\u51af\u7433\u563b\u563b\u7b11\u9053\uff1a\u201c\u5949\u8fd8\u4f60\u7684\u7389\u7c2a\uff0c\u4f60\u600e\u4e48 \u4e0d\u63a5\u5440\uff01\u201d\u6768\u67f3\u9752\u75be\u98ce\u822c\u7684\u51b2\u51fa\u5ba2\u5e97\u5927\u95e8\uff0c\u6280\u8d77\u5f39\u5f13\uff0c\u5288\u5288\u62cd\u62cd\u7684\u5411\u91cc\u9762\u4e71\u6253\u3002\u4f7f\u94a9\u9570\u67aa\u7684 \u6012\u543c\u4e00\u58f0\uff0c\u62d4\u5f00\u8fd1\u8eab\u7684\u94c1\u5f39\uff0c\u6b63\u8981\u8ffd\u51fa\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u5973\u5b50\u9634\u607b\u607b\u7684\u8bf4\u9053\uff1a\u201c\u628a\u8fd9\u5c0f\u5b50\u62ff\u4e0b\uff0c \u54ce\uff0c\u2018\u4ed6\u2019\u4e0d\u662f\u81ed\u5c0f\u5b50\uff0c\u4ed6\u662f\u5973\u626e\u7537\u88c5\u7684\u91ce\u4e2b\u5934\uff0c\u7687\u4e0a\u6240\u8981\u7684\u94a6\u72af\uff01\u201d \u3000\u3000\u51af\u7433\u4e00\u542c\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u5973\u5b50\u7adf\u662f\u97e9\u91cd\u5c71\u7684\u59bb\u5b50\u53f6\u6a2a\u6ce2\uff01\u51af\u7433\u5b9d\u5251\u4e00\u6325\uff0c\u593a\u8def\u800c\u8d70\uff0c\u5de6 \u624b\u4e00\u626c\uff0c\u4e09\u67c4\u98de\u5200\uff0c\u4e24\u67c4\u53d6\u90a3\u4f7f\u94a9\u9570\u67aa\u7684\u6c49\u5b50\uff0c\u4e00\u67c4\u53d6\u53f6\u6a2a\u6ce2\u3002\u53f6\u6a2a\u6ce2\u51b7\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u6559\u4f1a \u5f92\u5f1f\u6253\u5e08\u5085\u5566\uff01\u201d\u4e00\u638c\u5288\u7a7a\uff0c\u51af\u7433\u90a3\u53e3\u98de\u5200\u7ed9\u638c\u98ce\u9707\u5f97\u5012\u98de\u56de\u6765\uff0c\u4e0e\u540e\u53d1\u90a3\u4e24\u67c4\u98de\u5200\u76f8\u649e\uff0c \u4e00\u9f50\u8dcc\u843d\u77f3\u9636\u3002\u4f7f\u94a9\u9570\u67aa\u7684\u90a3\u6761\u6c49\u5b50\u5df2\u98de\u6b65\u4e0a\u524d\uff0c\u4e00\u62db\u201c\u9f9f\u86c7\u9501\u6c5f\u201d\uff0c\u94a9\u9570\u67aa\u4e00\u4f38\uff0c\u9501\u62ff\u51af \u7433\u7684\u5b9d\u5251\uff0c\u51af\u7433\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\u5237\u5237\u4e24\u5251\uff0c\u5148\u628a\u65c1\u8fb9\u7684\u4e24\u540d\u516c\u5dee\u523a\u4f24\uff0c\u56de\u8eab\u4e0e\u90a3\u4f7f\u94a9\u9570\u67aa\u7684\u6597 \u5728\u4e00\u8d77\u3002 \u3000\u3000\u53f6\u6a2a\u6ce2\u78d4\u78d4\u602a\u7b11\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u81ed\u6c49\u5b50\uff0c\u6b63\u70b9\u5728\u8fd9\u513f\uff0c\u5feb\u6765\u77a7\u4f60\u7684\u4e56\u5f92\u5f1f\u5440\uff01\u201d\u84e6\u5730\u91cc \u9707\u5927\u4ef7\u4e00\u58f0\u5de8\u54cd\uff0c\u897f\u8fb9\u4e00\u95f4\u5ba2\u623f\u677f\u58c1\u5012\u584c\uff0c\u6728\u7247\u7eb7\u98de\u4e2d\u60e8\u53eb\u4e4b\u58f0\u5927\u8d77\u6765\uff0c\u53ea\u542c\u5f97\u97e9\u91cd\u5c71\u9ad8\u58f0 \u53eb\u9053\uff1a\u201c\u81ed\u5a46\u5a18\uff0c\u6b63\u70b9\u5728\u8fd9\u513f\uff01\u201d \u3000\u3000\u51af\u7433\u653e\u773c\u4e00\u770b\uff0c\u5728\u706b\u628a\u5149\u4e2d\uff0c\u53ea\u89c1\u4e00\u4e2a\u67af\u9ec4\u7126\u7626\u7684\u548c\u5c1a\uff0c\u53cc\u76ee\u70af\u70af\u6709\u5a01\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u5de6 \u624b\u6293\u8d77\u4e00\u6761\u5927\u6c49\u5411\u77f3\u9636\u6454\u53bb\uff0c\u53f3\u624b\u4e00\u638c\u5c06\u97e9\u91cd\u5c71\u7684\u8f9f\u4e91\u9504\u9707\u5f00\u3002\u97e9\u91cd\u5c71\u5728\u6b66\u6797\u4e2d\u5df2\u7b97\u662f\u4e00\u7b49 \u4e00\u7684\u597d\u624b\uff0c\u7adf\u7136\u7ed9\u8fd9\u8001\u548c\u5c1a\u7684\u638c\u529b\u9707\u5f97\u8fde\u8fde\u540e\u9000\u3002\u51af\u7433\u5206\u5fc3\u4e00\u770b\uff0c\u90a3\u94a9\u9570\u67aa\u7684\u4e00\u62a2\u63b7\u6765\uff0c\u9669 \u9669\u7ed9\u4ed6\u523a\u4e2d\uff0c\u51af\u7433\u8feb\u5f97\u51dd\u795e\u4e00\u5fd7\uff0c\u62b5\u5fa1\u654c\u4eba\u3002\u8033\u8fb9\u4f46\u542c\u5f97\u97e9\u91cd\u5c71\u53eb\u9053\uff1a\u201c\u81ed\u5a46\u5a18\uff0c\u5feb\u8fc7\u6765\uff0c \u738b\u6556\uff0c\u4f60\u7559\u5fc3\u90a3\u91ce\u4e2b\u5934\u7684\u98de\u5200\uff0c\u53ea\u51c6\u6349\u6d3b\u7684\uff0c\u4f60\u5c0f\u5fc3\u4e9b\u513f\uff01\u201d\u53f6\u6a2a\u6ce2\u76ef\u4e86\u51af\u7433\u4e00\u773c\uff0c\u62bd\u5251\u52a9 \u4ed6\u4e08\u592b\uff0c\u6240\u6709\u536b\u58eb\uff0c\u4e5f\u90fd\u62e5\u53bb\u56f4\u653b\u90a3\u8001\u548c\u5c1a\uff0c\u53ea\u5269\u4e0b\u90a3\u4e2a\u4f7f\u94a9\u9570\u67aa\u7684\u6c49\u5b50\u7f20\u7740\u51af\u7433\u3002 \u3000\u3000\u9163\u6218\u4e2d\u53ea\u542c\u5f97\u8fde\u8fde\u60e8\u53eb\u4e4b\u58f0\uff0c\u60f3\u662f\u90a3\u4e9b\u536b\u58eb\u7ed9\u8001\u548c\u5c1a\u4e00\u4e2a\u4e2a\u6253\u8dcc\u4e86\u3002\u4f7f\u94a9\u9570\u67aa\u7684\u90a3\u6c49\u5b50 \u6b66\u529f\u751a\u5f3a\uff0c\u51af\u7433\u53cc\u773c\u6ce8\u5b9a\u4ed6\u7684\u5175\u5668\uff0c\u4e0d\u6562\u65c1\u89c2\uff0c\u5fc3\u4e2d\u6697\u6570\u90a3\u4e0d\u540c\u97f3\u8c03\u7684\u60e8\u53eb\u4e4b\u58f0\uff0c\u970e\u90a3\u4e4b \u95f4\uff0c\u4f3c\u5df2\u6709\u516b\u4eba\u91cd\u4f24\u5012\u5730\uff01 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u548c\u5c1a\u4e43\u5c11\u6797\u5bfa\u4ee5\u524d\u638c\u7ecf\u5802\u7684\u9996\u5e2d\u9ad8\u50e7\uff0c\u5982\u4eca\u8eab\u4e3a\u5c11\u6797\u5bfa\u76d1\u5bfa\u7684\u5f18\u6cd5\u5927\u5e08\u3002\u5f18\u6cd5 \u5927\u5e08\u4e43\u5c11\u6797\u4e09\u8001\u4e4b\u4e0b\u7684\u7b2c\u4e00\u4eba\uff0c\u81ea\u524d\u4efb\u7684\u76d1\u5bfa\u672c\u65e0\u5927\u5e08\u88ab\u5e74\u7fb9\u5c27\u5bb3\u6b7b\u540e\u5c31\u7531\u4ed6\u5347\u4efb\u76d1\u5bfa\uff0c\u96cd \u6b63\uff08\u5141\u796f\u5e74\u53f7\uff09\u767b\u6781\u4e4b\u540e\uff0c\u5c11\u6797\u5bfa\u4e3b\u6301\u65e0\u4f4f\u7985\u5e08\u6599\u77e5\u6d69\u52ab\u5c06\u4e34\uff0c\u597d\u5728\u4ed6\u65e9\u4e24\u5e74\u5df1\u547d\u5c11\u6797\u5bfa\u7684 \u50e7\u4fa3\u643a\u5e26\u6cd5\u7269\u7ecf\u5377\uff0c\u6697\u4e2d\u758f\u6563\uff0c\u4e00\u652f\u8d70\u798f\u5efa\u8386\u7530\uff0c\u4e00\u652f\u8d70\u5e7f\u4e1c\u5357\u6d77\uff0c\u5728\u5c11\u6797\u5bfa\u88ab\u706b\u711a\u7684\u524d \u5915\uff0c\u5bfa\u4e2d\u50e7\u4f17\u5df2\u5341\u4e5d\u758f\u6563\u3002\u90a3\u65e5\uff0c\u542c\u5f97\u5e74\u7fb9\u5c27\u9a7b\u519b\u6731\u4ed9\u9547\u5916\uff0c\u98ce\u58f0\u751a\u7d27\uff0c\u5f18\u6cd5\u4e0e\u8fbe\u6469\u9662\u4e2d\u7684 \u957f\u8001\u4fbf\u8bf7\u4e3b\u6301\u8d70\u907f\uff0c\u5c82\u6599\u65e0\u4f4f\u7985\u5e08\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8d76\u5feb\u8d70\u5427\uff0c\u6211\u4e00\u4e2a\u4eba\u7559\u5728\u8fd9 \u91cc\uff01\u201d\u5f18\u6cd5\u7b49\u5f53\u7136\u4e0d\u80af\uff0c\u65e0\u4f4f\u7985\u5e08\u8bf4\u9053\uff1a\u201c\u5141\u796f\u4e0e\u5e74\u7fb9\u5c27\u82e5\u5230\u5d69\u5c71\uff0c\u4e0d\u89c1\u8001\u8872\uff0c\u600e\u80af\u5e72\u4f11\uff1f \u6211\u7eb5\u82df\u6d3b\u4e00\u65f6\uff0c\u4e5f\u5fc5\u7d2f\u5c11\u6797\u5bfa\u50e7\u88ab\u671d\u5ef7\u8ffd\u6355\u3002\u4f5b\u7956\u4e91\uff1a\u6211\u4e0d\u5165\u5730\u72f1\u8c01\u5165\u5730\u72f1\uff1f\u8001\u8872\u4e0d\u5fb7\uff0c\u4ea6 \u613f\u8eab\u4f53\u529b\u884c\u3002\u201d\u5f18\u6cd5\u7b49\u4ecd\u7136\u4e0d\u80af\uff0c\u65e0\u4f4f\u5e84\u4e25\u5408\u4ec0\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4eca\u4ee5\u5c11\u6797\u5bfa\u4e3b\u6301\u8eab\u4efd\uff0c\u4ee4 \u4f60\u7b49\u901f\u79bb\uff01\u201d\u5c11\u6797\u5bfa\u5bfa\u89c4\u6700\u4e25\uff0c\u5f18\u6cd5\u7b49\u4eba\u53ea\u597d\u6700\u540e\u4e00\u6279\u79bb\u5c71\uff0c\u7b2c\u4e8c\u65e5\u5e74\u7fb9\u5c27\u7684\u5927\u519b\u5c31\u628a\u5d69\u5c71 \u56f4\u5f97\u6c34\u6cc4\u4e0d\u901a\u3002 \u3000\u3000\u96cd\u6b63\u548c\u5e74\u7fb9\u5c27\u90fd\u66fe\u51fa\u8eab\u5c11\u6797\uff0c\u6df1\u77e5\u5bfa\u4e2d\u50e7\u4fa3\u5389\u5bb3\uff0c\u65e9\u51c6\u5907\u4e86\u6700\u5389\u5bb3\u7684\u706b\u5668\uff0c\u4ee5\u4e09\u767e\u540d\u914d \u5907\u6709\u786b\u78fa\u706b\u7b52\u7684\u5fa1\u6797\u519b\uff0c\u5728\u5c11\u6797\u5bfa\u5468\u56f4\u4e71\u626b\uff0c\u90a3\u55b7\u706b\u7b52\u4e2d\u95f4\u8d2e\u6709\u786b\u78fa\u706b\u785d\u767d\u78f7\u7b49\u5f15\u706b\u4e4b\u7269\uff0c \u4e00\u63ed\u7b52\u76d6\uff0c\u767d\u78f7\u70b9\u71c3\uff0c\u70c8\u7130\u7acb\u5373\u55b7\u51fa\uff0c\u4e00\u626b\u7740\u6bbf\u74e6\u5899\u58c1\uff0c\u4fbf\u662f\u4e00\u5927\u7247\u4e00\u5927\u7247\u7684\u706b\u5149\u5f25\u6f2b\u5f00 \u6765\uff0c\u5728\u6570\u767e\u5e74\u524d\uff0c\u8fd9\u5df2\u662f\u65e0\u53ef\u62b5\u5fa1\u7684\u706b\u5668\uff0c\u5141\u796f\u72b9\u6050\u6709\u5931\uff0c\u5728\u55b7\u706b\u961f\u4e4b\u540e\u914d\u4ee5\u6570\u5341\u540d\u5927\u5185\u9ad8 \u624b\uff0c\u518d\u5728\u5916\u56f4\u914d\u4e86\u5343\u540d\u795e\u7bad\u624b\uff0c\u51c6\u5907\u5bfa\u4e2d\u50e7\u4fa3\u7a81\u56f4\u3002\u8c01\u77e5\u706b\u52bf\u718a\u718a\uff0c\u5c11\u6797\u5927\u95e8\u7d27\u95ed\uff0c\u7adf\u6ca1\u4e00 \u4eba\u9003\u51fa\u3002 \u3000\u3000\u5141\u796f\u5927\u611f\u610f\u5916\uff0c\u5bf9\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u96be\u9053\u5bfa\u4e2d\u50e7\u4fa3\u7518\u53d7\u711a\u6bd9\uff1f\u201d\u8f6c\u773c\u4e4b\u95f4\uff0c\u5c11\u6797\u5bfa\u5df2\u6210\u706b \u6d77\u3002\u4e00\u7247\u5899\u6467\u680b\u6298\u4e4b\u58f0\uff0c\u70e7\u4e86\u534a\u5929\uff0c\u53ea\u6709\u5f53\u4e2d\u90a3\u5ea7\u77f3\u5854\u8fd8\u672a\u5012\u584c\uff0c\u5ffd\u89c1\u77f3\u5854\u4e0a\u7aef\u5750\u4e00\u4eba\uff0c\u5e74 \u7fb9\u5c27\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u7684\u4e3b\u6301\u8010\u4e0d\u4f4f\u706b\u70e7\uff0c\u8dd1\u4e0a\u5854\u9876\u6765\u4e86\uff1f\u5f13\u7bad\u624b\u5c0f\u5fc3\uff01\u201d\u8bdd\u58f0\u672a\u505c\uff0c\u65e0\u4f4f\u7985\u5e08 \u53e3\u5ba3\u4f5b\u53f7\uff0c\u7a81\u7136\u4ece\u5854\u9876\u4e00\u8dc3\u800c\u4e0b\uff0c\u8df3\u5165\u706b\u6d77\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u90a3\u77f3\u5854\u8f70\u9686\u5012\u4e0b\uff0c\u5c11\u6797\u5bfa\u4e09\u5341\u516d\u5927 \u6bbf\uff0c\u4e0e\u534e\u4e25\u5b9d\u5854\uff0c\u5c3d\u90fd\u7070\u98de\u70df\u706d\u3002\u5e74\u7fb9\u5c27\u677e\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u7684\u4e3b\u6301\u5df2\u6295\u706b\u81ea\u711a\uff0c\u50e7\u4f17 \u60f3\u5fc5\u90fd\u5df2\u5728\u706b\u6d77\u4e2d\u5316\u4e3a\u7070\u70ec\uff01\u201d\u5141\u796f\u5fc3\u60f3\uff0c\u65e0\u4f4f\u7985\u5e08\u4e43\u662f\u4e94\u767e\u50e7\u4f17\u4e4b\u9996\uff0c\u8fde\u4ed6\u4e5f\u672a\u9003\u8131\uff0c\u5176 \u4ed6\u50e7\u4f17\u60f3\u5fc5\u6ca1\u6709\u5148\u4ed6\u800c\u9003\u4e4b\u7406\uff0c\u53ea\u662f\u8fd8\u6709\u4e00\u4e8b\u672a\u660e\uff0c\u90a3\u77f3\u5854\u9ad8\u5341\u4f59\u4e08\uff0c\u867d\u5728\u706b\u6d77\u4e4b\u4e2d\uff0c\u4e5f\u5e94 \u53ef\u591a\u5ef6\u65f6\u523b\uff0c\u4f55\u4ee5\u8fd9\u6837\u6613\u4e8e\u5012\u584c\uff1f \u3000\u3000\u5141\u796f\u4e0d\u77e5\uff0c\u534e\u4e25\u5b9d\u5854\u7684\u5012\u584c\uff0c\u534a\u662f\u7531\u4e8e\u706b\u529b\u6240\u653b\uff0c\u534a\u662f\u65e0\u4f4f\u7985\u5e08\u81ea\u6bc1\u3002\u65e0\u4f4f\u4e00\u7ea7\u7ea7\u7684\u8d70 \u4e0a\u5854\u9876\uff0c\u5728\u6bcf\u4e00\u5c42\u5854\u57fa\uff0c\u90fd\u7528\u638c\u529b\u9707\u677e\u7816\u77f3\uff0c\u7136\u540e\u518d\u5728\u5854\u9876\u73b0\u8eab\uff0c\u8ba9\u96cd\u6b63\u7687\u5e1d\u4eb2\u81ea\u89c1\u4ed6\u6295\u706b \u81ea\u711a\uff0c\u4ee4\u96cd\u6b63\u5b89\u5fc3\uff0c\u8fd9\u624d\u4fdd\u5168\u4e86\u5c11\u6797\u5bfa\u7684\u8bb8\u591a\u50e7\u4fa3\u3002\u56e0\u4e3a\u96cd\u6b63\u89c1\u201c\u9996\u8981\u201d\u5df2\u9664\uff0c\u7eb5\u6000\u7591\u6709\u5c11 \u6570\u50e7\u4eba\u9003\u8131\uff0c\u8ffd\u7a76\u4e5f\u4e0d\u4e25\u7d27\u4e86\u3002 \u3000\u3000\u96cd\u6b63\u4e0e\u5e74\u7fb9\u5c27\u653e\u706b\u70e7\u4e86\u5c11\u6797\u5bfa\u4e4b\u540e\uff0c\u7559\u4e0b\u4e00\u6279\u4eba\u5728\u5c71\u4e0a\u770b\u5b88\uff0c\u53e6\u5916\u6d3e\u4e00\u6279\u4eba\u5728\u5d69\u5c71\u5468\u56f4 \u4e09\u767e\u91cc\u5185\u641c\u67e5\u5de1\u89c6\u3002\u4e3a\u7684\u662f\u9884\u9632\u6709\u522b\u6d3e\u9ad8\u4eba\u5230\u5d69\u5c71\u95ee\u8baf\uff0c\u6240\u4ee5\u5728\u5d69\u5c71\u5468\u56f4\u5f20\u4e0b\u7f57\u7f51\u3002 \u3000\u3000\u706b\u70e7\u5c11\u6797\u5bfa\u4e4b\u540e\u534a\u4e2a\u6708\uff0c\u641c\u67e5\u6e10\u5bbd\uff0c\u5141\u796f\u5e26\u4e86\u54c8\u5e03\u9640\u7b49\u4eba\uff0c\u53e6\u529e\u4e00\u4ef6\u5927\u4e8b\u53bb\u4e86\u3002\u53ea\u7559\u4e0b \u97e9\u91cd\u5c71\u592b\u59bb\u5728\u6cb3\u5357\u5883\u5185\u4e3b\u6301\u5de1\u89c6\u4e8b\u52a1\u3002\u81f3\u4e8e\u5d69\u5c71\u4e4b\u4e0a\uff0c\u5219\u7559\u4e0b\u6d77\u4e91\u548c\u5c1a\u4e0e\u5fa1\u6797\u519b\u7edf\u9886\u79e6\u4e2d\u8d8a \u9a7b\u5b88\u3002\u5f18\u6cd5\u5927\u5e08\u6253\u542c\u5f97\u6e05\u5ef7\u9632\u8303\u7a0d\u677e\uff0c\u5fc3\u60ac\u65e0\u4f4f\u7985\u5e08\uff0c\u5077\u5077\u56de\u5230\u6cb3\u5357\uff0c\u59cb\u77e5\u4e3b\u6301\u5df2\u58ee\u70c8\u727a \u7272\uff0c\u4e8e\u662f\u660f\u591c\u4e0a\u5c71\uff0c\u5728\u534e\u4e25\u5b9d\u5854\u65e7\u5740\uff0c\u53d6\u4e86\u4e00\u63ac\u6ce5\u571f\uff0c\u4f5c\u4e3a\u5c11\u6797\u5bfa\u7684\u5723\u7269\uff0c\u51c6\u5907\u5e26\u56de\u8386\u7530\uff0c \u6c38\u7559\u540e\u4ee3\u5f1f\u5b50\u77bb\u4ef0\u3002\u5f18\u6cd5\u5927\u5e08\u6b66\u529f\u5353\u7edd\uff0c\u5077\u4e0a\u5d69\u5c71\uff0c\u7559\u5b88\u7684\u6d77\u4e91\u548c\u5c1a\u7b49\u4eba\uff0c\u4e1d\u6beb\u4e0d\u89c9\u3002\u53ef\u60dc \u9003\u4e86\u4e00\u5173\uff0c\u9003\u4e0d\u4e86\u7b2c\u4e8c\u5173\uff0c\u7ec8\u4e8e\u5728\u5c0f\u9547\u7684\u5ba2\u5e97\u4e2d\uff0c\u906d\u9047\u4e86\u97e9\u91cd\u5c71\u592b\u59bb\u7684\u76d8\u67e5\u3002 \u3000\u3000\u4e0e\u51af\u7433\u5bf9\u654c\u7684\u4eba\u540d\u53eb\u738b\u6556\uff0c\u662f\u6cb3\u5357\u629a\u8859\u4e2d\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u6cb3\u5357\u5de1\u629a\u6d3e\u4ed6\u534f\u52a9\u97e9\u91cd\u5c71\u641c\u67e5\u3002 \u6b64\u4eba\u6b66\u529f\u867d\u7136\u4e0d\u82e5\u97e9\u91cd\u5c71\u592b\u59bb\uff0c\u4f46\u5374\u6bd4\u51af\u7433\u7a0d\u9ad8\uff0c\u800c\u4e14\u4ed6\u4e5f\u662f\u6253\u6697\u5668\u7684\u80fd\u624b\uff0c\u7ec3\u6709\u6781\u5176\u6b79\u6bd2 \u7684\u72ec\u95e8\u6697\u5668\u3002 \u3000\u3000\u51af\u7433\u4e0e\u738b\u6556\u6076\u6218\uff0c\u8fde\u7528\u4e86\u597d\u51e0\u6d3e\u6b66\u529f\uff0c\u90fd\u8131\u4e0d\u4e86\u8eab\u3002\u731b\u7136\u60f3\u8d77\u97e9\u91cd\u5c71\u8981\u6349\u6d3b\u7684\u8bf4\u8bdd\uff0c\u5fc3 \u9053\uff1a\u6b64\u4eba\u4e0d\u6562\u4f24\u6211\u7684\u6027\u547d\uff0c\u6015\u4ed6\u4f55\u4e3a\uff1f\u5251\u6cd5\u4e00\u7d27\uff0c\u5168\u53d6\u653b\u52bf\uff0c\u51af\u7433\u6b66\u529f\u4e0e\u738b\u6556\u76f8\u5dee\u6709\u9650\uff0c\u65e2 \u65e0\u6027\u547d\u987e\u8651\uff0c\u5f3a\u653b\u731b\u6253\uff0c\u51cc\u5389\u65e0\u524d\uff0c\u738b\u6556\u7ed9\u5979\u903c\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u770b\u770b\u5c31\u8981\u51b2\u51fa\u95e8\u53bb\u3002\u738b\u6556\u54ac\u4e86 \u54ac\u7259\uff0c\u62a2\u5148\u4e00\u6b65\uff0c\u5835\u4f4f\u5927\u95e8\uff0c\u94a9\u9570\u67aa\u4e00\u4f38\uff0c\u683c\u4f4f\u4e86\u51af\u7433\u5b9d\u5251\uff0c\u5de6\u624b\u4e00\u626c\uff0c\u65e0\u5149\u65e0\u58f0\uff0c\u51af\u7433\u5ffd \u89c9\u8155\u9aa8\u4f3c\u7ed9\u868a\u5b50\u53ee\u4e86\u4e00\u53e3\u4f3c\u7684\uff0c\u5fae\u611f\u9178\u9ebb\uff0c\u4e5f\u4e0d\u600e\u6837\u5728\u610f\uff0c\u7528\u65e0\u6781\u5251\u4e2d\u7684\u201c\u5343\u94a7\u4e00\u7fbd\u201d\u62db \u7b11\u9053\uff1a\u201c\u91ce\u4e2b\u5934\uff0c\u4f60\u4e2d\u4e86\u6211\u7684\u72ec\u95e8\u6697\u5668\u5566\uff01\u8fd8\u4e0d\u4e56\u4e56\u7684\u5f03\u5251\u6295\u964d\uff0c\u8bf7\u6211\u6551\u6cbb\uff1f\u201d\u51af\u7433\u6014\u4e86\u4e00 \u6014\uff0c\u738b\u6556\u8ffd\u5230\u80cc\u540e\uff0c\u51b7\u4e0d\u9632\u51af\u7433\u4e5f\u56de\u624b\u4e00\u626c\uff0c\u4e24\u67c4\u98de\u5200\uff0c\u5de6\u53f3\u5206\u5230\uff0c\u738b\u6556\u65cb\u8eab\u4e00\u683c\uff0c\u5f53\u7684\u4e00 \u58f0\uff0c\u94a9\u9570\u67aa\u6253\u98de\u4e86\u4e00\u67c4\u98de\u5200\uff0c\u53ef\u662f\u7b2c\u4e8c\u53e3\u98de\u5200\u5374\u659c\u523a\u63a0\u80a9\u98de\u8fc7\uff0c\u738b\u6556\u8f6c\u8eab\u7a0d\u7f13\uff0c\u80a9\u4e0a\u4e00\u75db\uff0c \u706b\u8fa3\u8fa3\u7684\u4e5f\u7740\u4e86\u4e00\u4e0b\u3002\u738b\u6556\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u6cfc\u4e2b\u5934\u4f60\u627e\u6b7b\u5566\uff01\u201d\u94a7\u9570\u67aa\u98ce\u72c2\u96e8\u9aa4\uff0c\u518d\u628a\u51af\u7433 \u7684\u5b9d\u5251\u88f9\u4f4f\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u731b\u542c\u5f97\u7830\u7136\u5de8\u54cd\uff0c\u5ba2\u5e97\u7684\u5927\u677f\u95e8\u788e\u6210\u51e0\u5757\uff0c\u98de\u51fa\u95e8\u5916\uff0c\u51e0\u6761\u9ed1\u5f71\uff0c \u500f\u7136\u51b2\u51fa\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u521d\u95ef\u6c5f\u6e56\u906d\u6697\u7b97\uff0c\u9690\u4f0f\u5371\u673a\u5c1a\u4e0d\u77e5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516b\u56de\u3000\u77e2\u5fd7\u6276\u6301\u3000\u771f\u60c5\u840c\u7231\u5ff5\u3000\u91d1\u9488\u5ea6\u52ab\u3000\u602a\u75c7\u89e6\u82b3\u5fc3 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u516b\u56de \u77e2\u5fd7\u6276\u6301\u3000\u771f\u60c5\u840c\u7231\u5ff5 \u91d1\u9488\u5ea6\u52ab\u3000\u602a\u75c7\u89e6\u82b3\u5fc3 \u3000\u3000\u51b2\u51fa\u6765\u7684\u662f\u97e9\u91cd\u5c71\u592b\u59bb\uff0c\u4ed6\u4eec\u4e8c\u4eba\u4e0e\u56db\u540d\u6355\u5feb\u56db\u540d\u536b\u58eb\u56f4\u653b\u5f18\u6cd5\u5927\u5e08\uff0c\u56db\u540d\u6355\u5feb\u6b66\u529f\u751a \u4f4e\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u5168\u7ed9\u5f18\u6cd5\u5927\u5e08\u6253\u5f97\u91cd\u4f24\uff0c\u5176\u4ed6\u56db\u540d\u536b\u58eb\uff0c\u4e0d\u53ca\u534a\u4e2a\u65f6\u8fb0\uff0c\u4e5f\u76f8\u7ee7\u5012\u5730\u3002\u97e9\u91cd \u5c71\u592b\u59bb\u5927\u60ca\uff0c\u62fc\u529b\u62b5\u5fa1\u3002\u5f18\u6cd5\u5927\u5e08\u7684\u6b66\u529f\uff0c\u4ec5\u5728\u5c11\u6797\u4e09\u8001\u4e4b\u4e0b\uff0c\u97e9\u91cd\u5c71\u592b\u59bb\u867d\u7136\u662f\u4e00\u7b49\u4e00\u7684 \u597d\u624b\uff0c\u4e5f\u5360\u4e0d\u4e86\u534a\u70b9\u4fbf\u5b9c\u3002\u4f46\u5f18\u6cd5\u5927\u5e08\u5374\u662f\u7ed9\u4ed6\u4eec\u7f20\u4e0a\u4e86\u3002 \u3000\u3000\u7f20\u6218\u4e00\u4e45\uff0c\u5f18\u6cd5\u5927\u5e08\u5fc3\u5934\u70e6\u8e81\uff0c\u8bda\u6050\u654c\u65b9\u5c1a\u6709\u9ad8\u624b\u3002\u6697\u8fd0\u5185\u5bb6\u771f\u529b\uff0c\u9634\u638c\u5411\u5185\u4e00\u5708\uff0c\u957f \u62f3\u6363\u51fa\u3002\u5c11\u6797\u5bfa\u795e\u62f3\u65e0\u654c\uff0c\u97e9\u91cd\u5c71\u6a2a\u638c\u4e00\u6321\uff0c\u54ea\u6321\u5f97\u4f4f\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u62f3\u98ce\u5df1\u626b\u4e2d\u53f3\u808b\uff0c \u5e9e\u5927\u7684\u8eab\u8eaf\u76f4\u7ed9\u9707\u51fa\u5927\u95e8\u3002 \u3000\u3000\u53f6\u6a2a\u6ce2\u865a\u6643\u4e00\u5251\uff0c\u98de\u8eab\u4fbf\u9003\uff0c\u5f18\u6cd5\u5927\u5e08\u6251\u51fa\u95e8\u5916\uff0c\u731b\u542c\u5f97\u9e23\u9e23\u58f0\u54cd\uff0c\u4e00\u4ef6\u9ed1\u5ffd\u5ffd\u4e1c\u897f\u81ea \u5916\u5c04\u6765\uff0c\u5f18\u6cd5\u542c\u58f0\u8fa8\u5668\uff0c\u5f15\u8eab\u4e00\u95ea\uff0c\u90a3\u6697\u5668\u597d\u602a\uff0c\u7adf\u7136\u8f6c\u6298\u98de\u6765\uff0c\u5f18\u6cd5\u5927\u6012\uff0c\u4e00\u638c\u5288\u53bb\uff0c\u638c \u98ce\u4e00\u8361\uff0c\u6697\u5668\u98de\u5815\u5899\u8fb9\u3002\u5f18\u6cd5\u4e3e\u6b65\u518d\u8ffd\uff0c\u4e0d\u6599\u90a3\u6697\u5668\u5728\u5899\u8fb9\u4e00\u649e\uff0c\u84e6\u7136\u98de\u817e\u8d77\u6765\uff0c\u5f18\u6cd5\u731d\u4e0d \u53ca\u9632\uff0c\u811a\u8e1d\u4e00\u9635\u5267\u75db\uff0c\u6697\u5668\u7684\u5012\u94a7\u5df2\u523a\u8fdb\u9aa8\u5934\u3002\u5f18\u6cd5\u53cc\u6307\u4e00\u94b3\uff0c\u628a\u6697\u5668\u62d4\u4e86\u51fa\u6765\u3002\u8ffd\u51fa\u95e8 \u65f6\uff0c\u97e9\u91cd\u5c71\u592b\u59bb\u5df2\u7ecf\u4e0d\u89c1\u3002\u5f18\u6cd5\u6240\u4e2d\u7684\u6697\u5668\u6b63\u662f\u97e9\u91cd\u5c71\u7cbe\u5fc3\u6240\u70bc\u7684\u201c\u56de\u73af\u94a9\u201d\uff0c\u5f62\u5982\u81a8\u5c3a\uff0c \u4e24\u7aef\u5c16\u5229\uff0c\u5468\u56f4\u8fd8\u88c5\u6709\u660e\u6643\u6643\u7684\u5012\u94a9\uff0c\u53ef\u4ee5\u56de\u73af\u8f6c\u6298\uff0c\u4e0a\u4e0b\u98de\u817e\uff0c\u5f53\u5e74\u6613\u5170\u73e0\u521d\u9047\u56de\u73af\u94a9 \u65f6\uff0c\u8fd8\u51e0\u4e4e\u7740\u4e86\u9053\u513f\uff0c\u5e78\u8d56\u529f\u529b\u6df1\u6e5b\uff0c\u624d\u80fd\u7528\u91d1\u94a2\u6307\u529b\uff0c\u5f3a\u94b3\u4e86\u53bb\u3002\u5f18\u6cd5\u867d\u7136\u6b66\u529f\u5353\u7edd\uff0c\u6bd4 \u6613\u5170\u73e0\u5374\u5dee\u4e00\u7b79\uff0c\u53c8\u4e0d\u660e\u201c\u56de\u73af\u94a9\u201d\u7684\u7279\u6027\uff0c\u56e0\u4e4b\u5403\u4e86\u5927\u4e8f\u3002 \u3000\u3000\u91cc\u9762\u738b\u6556\u4e0e\u51af\u7433\u6076\u6597\uff0c\u731b\u89c1\u97e9\u91cd\u5c71\u592b\u59bb\u547c\u53f7\u9003\u8dd1\uff0c\u5f18\u6cd5\u5927\u5e08\u8ffd\u51fa\u95e8\u6765\uff0c\u628a\u4ed6\u5413\u5f97\u9b42\u4e0d\u9644 \u4f53\uff0c\u98de\u8eab\u4e5f\u9003\u3002\u51af\u7433\u6b63\u60f3\u8ffd\u8d76\uff0c\u5ffd\u542c\u5f97\u8033\u8fb9\u6709\u4eba\u53eb\uff1a\u201c\u5c0f\u59d1\u5a18\u4f60\u4e0d\u8981\u8ffd\u5566\uff01\u8ba9\u8001\u8872\u7ed9\u4f60\u770b \u770b\uff0c\u4f60\u4e2d\u4e86\u4ec0\u4e48\u6697\u5668\uff1f\u201d\u51af\u7433\u4e00\u6014\uff0c\u53ea\u89c1\u90a3\u8001\u548c\u5c1a\u5df2\u7ad9\u5728\u81ea\u5df1\u7684\u9762\u524d\u3002\u51af\u7433\u5fc3\u9053\uff1a\u8fd9\u8001\u548c\u5c1a \u771f\u771f\u5389\u5bb3\uff0c\u4e00\u773c\u5c31\u77a7\u51fa\u6211\u5973\u626e\u7537\u88c5\u3002\u6211\u4e2d\u4e86\u4ec0\u4e48\u6697\u5668\uff1f\u96be\u9053\u521a\u624d\u7ed9\u868a\u5b50\u4f3c\u7684\u53ee\u4e86\u4e00\u53e3\uff0c\u90a3\u5c31 \u662f\u654c\u4eba\u6240\u5938\u53e3\u7684\u72ec\u95e8\u6697\u5668\u5417\uff1f\u5f18\u6cd5\u5927\u5e08\u62ff\u8d77\u51af\u7433\u7684\u624b\uff0c\u8f7b\u8f7b\u6469\u4e86\u4e00\u4e0b\uff0c\u9762\u8272\u500f\u53d8\uff0c\u201c\u5514\u201d\u4e86 \u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u56db\u5ddd\u5510\u5bb6\u7684\u72ec\u95e8\u6697\u5668\u4e03\u715e\u767d\u7709\u9488\u3002\u201d \u3000\u3000\u51af\u7433\u4e0d\u77e5\u4e03\u715e\u767d\u7709\u9488\u662f\u4ec0\u4e48\u540d\u5802\uff0c\u4e5f\u4e0d\u77e5\u9053\u56db\u5ddd\u5510\u5bb6\u662f\u4ec0\u4e48\u6765\u5934\uff0c\u7b11\u7740\u95ee\u9053\uff1a\u201c\u8001\u548c \u5c1a\uff0c\u4f60\u771f\u6709\u672c\u9886\uff0c\u80fd\u628a\u97e9\u91cd\u5c71\u592b\u59bb\u90fd\u6253\u8dd1\u4e86\u3002\u4f60\u4e2d\u4e86\u4ed6\u7684\u56de\u73af\u94a9\u4e00\u5b9a\u5f88\u75db\u4e86\u3002\u6211\u4e00\u70b9\u4e5f\u4e0d\u89c9 \u75db\uff0c\u4f60\u8fd8\u662f\u5148\u533b\u81ea\u5df1\u5427\u3002\u201d\u5f18\u6cd5\u4e00\u542c\uff0c\u8fd9\u5973\u5b69\u5b50\u4e0d\u4f46\u77e5\u9053\u97e9\u91cd\u5c71\u7684\u540d\u5b57\uff0c\u800c\u4e14\u8fd8\u77e5\u4ed6\u7528\u7684\u662f \u4ec0\u4e48\u6697\u5668\uff0c\u4e0d\u7981\u5927\u4e3a\u60ca\u5947\uff0c\u72d0\u7591\u6ee1\u8179\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u7684\u5e08\u5085\u662f\u8c01\uff1f\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u51af\u7433 \u4e00\u6115\uff0c\u672c\u4e0d\u60f3\u8bf4\uff0c\u4f46\u662f\u90a3\u8001\u548c\u5c1a\u6837\u7709\u5584\u76ee\uff0c\u5bf9\u81ea\u5df1\u5341\u5206\u548c\u853c\uff0c\u8131\u53e3\u8bf4\u9053\uff1a\u201c\u6211\u7684\u5e08\u5085\u591a\u7740 \u5462\uff0c\u4f60\u8d76\u8dd1\u7684\u8fd9\u4e00\u5bf9\u97e9\u91cd\u5c71\u592b\u5987\u4e5f\u662f\u6211\u7684\u5e08\u5085\uff0c\u4e0d\u8fc7\u8001\u548c\u5c1a\u4f60\u522b\u5bb3\u6015\uff0c\u6211\u6559\u4f60\u600e\u6837\u6cbb\u4f24\u3002\u201d \u5f18\u6cd5\u542c\u4e86\uff0c\u5927\u611f\u610f\u5916\u3002 \u3000\u3000\u51af\u7433\u68a8\u6da1\u6d45\u7b11\uff0c\u7eed\u9053\uff1a\u201c\u97e9\u91cd\u5c71\u7684\u94a9\u662f\u6ca1\u6bd2\u7684\uff0c\u5b83\u7684\u5012\u94a9\u82e5\u6298\u788e\u4e86\u5d4c\u5165\u8089\u4e2d\uff0c\u4f60\u7528\u78c1\u94c1 \u5438\u51fa\u6765\u4fbf\u884c\u4e86\u3002\u4f60\u77a7\uff0c\u8fd9\u591a\u7b80\u5355\u3002\u4ed6\u7684\u6697\u5668\u662f\u4e13\u4e3a\u64d2\u654c\u7528\u7684\uff0c\u4f60\u7684\u6b66\u529f\u6bd4\u4ed6\u4eec\u9ad8\uff0c\u6ca1\u6709\u7ed9\u4ed6 \u4eec\u64d2\u7740\uff0c\u5c31\u4e0d\u5fc5\u6015\u4ed6\u7684\u6697\u5668\u4e86\u3002\u201d\u51af\u7433\u5fc3\u60f3\uff1a\u6211\u7ed9\u868a\u5b50\u4f3c\u7684\u4e1c\u897f\u53ee\u4e86\u4e00\u4e0b\uff0c\u8fd9\u548c\u5c1a\u90fd\u8fd9\u6837\u7d27 \u5f20\uff0c\u60f3\u6765\u4ed6\u7684\u6b66\u529f\u867d\u9ad8\uff0c\u5bf9\u6697\u5668\u5374\u4e0d\u719f\u4e60\u3002\u51af\u7433\u5fc3\u611f\u8fd9\u548c\u5c1a\u5bf9\u5979\u548c\u5584\uff0c\u53c9\u60f3\u4e58\u673a\u5356\u5f04\u5979\u5bf9\u6697 \u5668\u7684\u77e5\u8bc6\uff0c\u6ed4\u6ed4\u4e0d\u7edd\u7684\u8bf4\u4e86\u4e00\u901a\uff0c\u4e0d\u77e5\u81ea\u5df1\u7adf\u662f\u73ed\u95e8\u5f04\u65a7\u3002 \u3000\u3000\u5f18\u6cd5\u5927\u5e08\u7ed9\u5979\u9017\u5f97\u7b11\u4e86\u8d77\u6765\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u771f\u771f\u53ef\u7231\uff0c\u53ef\u60dc\u592a\u6b22\u559c\u81ea\u6043\u806a\u660e\uff0c\u4e0d\u77e5\u5929 \u9ad8\u5730\u539a\u3002\u201d\u539f\u6765\u51af\u7433\u6240\u4e2d\u7684\u4e03\u715e\u767d\u7709\u9488\u662f\u56db\u5ddd\u5510\u5bb6\u6240\u70bc\u7684\u4e00\u79cd\u6781\u4e3a\u6b79\u6bd2\u7684\u6697\u5668\uff0c\u9488\u7ec6\u5982\u6bdb\uff0c \u6240\u4ee5\u53eb\u505a\u767d\u7709\u9488\u3002\u523a\u5165\u4eba\u4f53\u4e4b\u5185\uff0c\u987a\u7740\u8840\u7ba1\u6df1\u5165\uff0c\u5230\u4e86\u5fc3\u7a9d\uff0c\u795e\u4ed9\u96be\u6551\u3002\u51af\u7433\u624b\u8155\u7a74\u9053\u4e4b\u5904 \u4e2d\u9488\uff0c\u5927\u7ea6\u5728\u4e03\u5929\u4e4b\u540e\uff0c\u767d\u7709\u9488\u4fbf\u5230\u5fc3\u7a9d\u3002\u800c\u4e14\u82e5\u5728\u5934\u4e09\u5929\u4e4b\u5185\uff0c\u4e0d\u80fd\u963b\u4f4f\u6bd2\u9488\u4e0a\u5347\uff0c\u4ee5\u540e \u7eb5\u5f97\u89e3\u836f\u4e5f\u6210\u6b8b\u5e9f\u3002\u8fd9\u89e3\u836f\u53ea\u6709\u5510\u5bb6\u624d\u6709\uff0c\u738b\u6556\u662f\u56db\u5ddd\u5510\u5bb6\u7684\u5973\u5a7f\uff0c\u4ed6\u7528\u767d\u7709\u9488\u4f24\u4e86\u51af\u7433\uff0c \u539f\u610f\u5e76\u4e0d\u662f\u60f3\u53d6\u5979\u6027\u547d\uff0c\u800c\u662f\u60f3\u8981\u631f\u5979\u6295\u964d\u3002\u4e0d\u6599\u4e8b\u51fa\u610f\u5916\uff0c\u738b\u6556\u5fd9\u7740\u9003\u547d\uff0c\u8fd9\u65f6\u5df2\u4e0d\u77e5\u8d70 \u5230\u4f55\u65b9\u3002\u66f4\u65e0\u4ece\u6c42\u5230\u89e3\u836f\u3002 \u3000\u3000\u5f18\u6cd5\u5927\u5e08\u4e00\u7b11\u4e4b\u540e\uff0c\u621a\u7136\u5fc3\u5fe7\uff0c\u770b\u8fd9\u5973\u5b69\u5b50\u96ea\u80a4\u82b1\u8c8c\uff0c\u53ef\u7231\u5f02\u5e38\uff0c\u82e5\u7ed9\u5979\u77e5\u9053\u5728\u4e03\u5929\u4e4b \u5185\uff0c\u4fbf\u8981\u8eab\u4ea1\uff0c\u4e0d\u77e5\u591a\u4f24\u5fc3\u5462\u3002\u5c11\u6797\u5bfa\u7684\u7075\u836f\u867d\u591a\uff0c\u504f\u504f\u5c31\u6ca1\u6709\u4e00\u79cd\u80fd\u89e3\u4e03\u715e\u767d\u7709\u9488\u7684\u3002 \u3000\u3000\u5f18\u6cd5\u5927\u5e08\u6697\u6697\u53f9\u606f\uff0c\u51af\u7433\u89c1\u4ed6\u4e45\u4e45\u4e0d\u8bed\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u884c\u5f97\u52a8\u5417\uff1f\u4f60\u884c\u4e0d\u52a8\uff0c\u6211\u7ed9\u4f60\u627e\u78c1 \u77f3\u53bb\u3002\u201d\u5f18\u6cd5\u5927\u5e08\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\u83e9\u8428\u5fc3\u80a0\uff0c\u8001\u8872\u591a\u8c22\u4f60\u4e86\u3002\u6211\u4e0d\u7528\u78c1\u94c1\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u4e3a \u6211\u62c5\u5fc3\u3002\u5012\u662f\u4f60\u7684\u4f24\uff0c\u4f60\u7684\u4f24\u2026\u2026\u201d\u5f18\u6cd5\u5927\u5e08\u4e0d\u5fcd\u628a\u771f\u76f8\u8bf4\u51fa\uff0c\u8a00\u8f9e\u5450\u5450\uff0c\u51af\u7433\u9053\uff1a\u201c\u600e\u4e48 \u6837\uff1f\u201d\u5f18\u6cd5\u5927\u5e08\u5728\u8eab\u4e0a\u638f\u51fa\u4e09\u7c92\u4e39\u836f\u9053\uff1a\u201c\u4f60\u628a\u8fd9\u836f\u4e38\u541e\u4e0b\u53bb\uff0c\u6bcf\u5929\u541e\u4e00\u7c92\u3002\u7136\u540e\u4f60\u5230\u798f\u5efa \u8386\u7530\u7684\u5c11\u6797\u5bfa\u53bb\uff0c\u5c31\u8bf4\u5728\u8def\u4e0a\u78b0\u5230\u6211\u8fd9\u4e48\u4e00\u4e2a\u8001\u548c\u5c1a\uff0c\u53eb\u4f60\u6765\u7684\uff0c\u4ed6\u4eec\u4fbf\u4f1a\u6536\u7559\u4f60\u4e86\u3002\u201d\u5f18 \u6cd5\u7ed9\u5979\u7684\u4e39\u836f\uff0c\u4e43\u662f\u5c11\u6797\u5bfa\u79d8\u5236\u7684\u201c\u5c11\u8fd8\u4e39\u201d\uff0c\u867d\u7136\u4e0d\u662f\u5bf9\u75c7\u7684\u89e3\u836f\uff0c\u4f46\u5728\u4e09\u4e94\u5929\u5185\uff0c\u5374\u53ef \u5c06\u6bd2\u6c14\u6b62\u7740\u4e0d\u5347\uff0c\u4e00\u6708\u4e4b\u5185\uff0c\u4e0d\u81f3\u8eab\u4ea1\u3002\u5f18\u6cd5\u5927\u5e08\u6253\u7b97\uff0c\u5316\u4e00\u4e24\u5929\u529f\u592b\uff0c\u5230\u6cb3\u5357\u5de1\u629a\u8859\u4e2d\uff0c \u628a\u738b\u6556\u6349\u6765\uff0c\u8feb\u4ed6\u62ff\u51fa\u89e3\u836f\u3002\u4f46\u4e0d\u77e5\u80fd\u5426\u6210\u529f\uff0c\u6240\u4ee5\u4e0d\u60f3\u5148\u8bf4\u3002\u5f18\u6cd5\u8eab\u4e3a\u6e05\u5ef7\u7684\u730e\u7269\uff0c\u81ea\u662f \u4e0d\u6562\u643a\u51af\u7433\u968f\u884c\u3002 \u3000\u3000\u51af\u7433\u63a5\u4e86\u4e39\u836f\uff0c\u7b11\u563b\u563b\u7684\u9053\uff1a\u201c\u8fd9\u836f\u4e38\u82e6\u4e0d\u82e6\uff1f\u201d\u5f18\u6cd5\u6025\u9053\uff1a\u201c\u4e0d\u82e6\uff0c\u4e0d\u82e6\uff0c\u4f60\u5feb\u541e \u5427\u3002\u6709\u4eba\u6765\u5566\uff0c\u8001\u8872\u8981\u8d70\u4e86\uff01\u201d\u50e7\u888d\u4e00\u62c2\uff0c\u4e0a\u5c4b\u4fbf\u9003\uff0c\u51af\u7433\u89c1\u4ed6\u5de6\u8db3\u867d\u7136\u5fae\u8ddb\uff0c\u884c\u52a8\u4ecd\u662f\u5341 \u5206\u77eb\u6377\uff0c\u8fd9\u65f6\u6708\u4eae\u6b63\u5230\u5929\u5fc3\uff0c\u6708\u5149\u4e0b\u5ffd\u89c1\u4e24\u6761\u4eba\u5f71\u5982\u98de\u5954\u6765\uff0c\u5176\u4e2d\u4e00\u4eba\u5c31\u662f\u4ee5\u524d\u5728\u56db\u7687\u5e9c\u8981 \u81ea\u5df1\u8ddf\u4ed6\u51fa\u8d70\u7684\u5510\u6653\u6f9c\u3002\u51af\u7433\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u6050\u5510\u6653\u6f9c\u548c\u5415\u56db\u5a18\u4e43\u662f\u4e00\u9053\uff0c\u8981\u6765\u6349\u62ff\u81ea\u5df1\u3002 \u3000\u3000\u5954\u9003\u4e2d\u5ffd\u542c\u5f97\u90a3\u8001\u548c\u5c1a\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u5510\u6653\u6f9c\uff0c\u662f\u4f60\u548c\u9648\u5927\u54e5\u5417\uff1f\u201d\u51af\u7433\u5fc3\u9053\uff1a\u54e6\uff0c\u539f \u6765\u4ed6\u4eec\u662f\u8ba4\u8bc6\u7684\u3002\u811a\u5e95\u52a0\u5feb\uff0c\u8d8a\u53d1\u8dd1\u5f97\u8fc5\u75be\uff0c\u90a3\u8001\u548c\u5c1a\u548c\u5510\u6653\u6f9c\u5e76\u4e0d\u8ffd\u6765\uff0c\u60f3\u662f\u5510\u6653\u6f9c\u672a\u66fe \u53d1\u73b0\u81ea\u5df1\uff0c\u800c\u8001\u548c\u5c1a\u4e5f\u5fd9\u7740\u548c\u4ed6\u4eec\u53d9\u65e7\u4e86\u3002 \u3000\u3000\u51af\u7433\u8dd1\u4e86\u4e00\u9635\uff0c\u5df2\u51fa\u5c0f\u9547\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4f55\u5fc5\u53bb\u4ec0\u4e48\u798f\u5efa\u8386\u7530\uff0c\u5d69\u5c71\u5c31\u5728\u9644\u8fd1\uff0c\u8981\u5230\u5c11\u6797 \u5bfa\u53bb\uff0c\u4f55\u5fc5\u820d\u8fd1\u56fe\u8fdc\u3002\u800c\u4e14\u5d69\u5c71\u7684\u5c11\u6797\u5bfa\u6bd4\u8386\u7530\u5c11\u6797\u5bfa\u6709\u540d\u5f97\u591a\u3002 \u3000\u3000\u7ea6\u83ab\u8dd1\u4e86\u4e8c\u4e09\u5341\u91cc\uff0c\u5929\u8fd8\u672a\u4eae\uff0c\u51af\u7433\u75b2\u5026\u4e0d\u582a\uff0c\u5728\u5c71\u5761\u7684\u9ad8\u5730\u4e0a\uff0c\u8131\u4e0b\u5916\u8863\uff0c\u8eba\u4e0b\u4f11 \u606f\uff0c\u4e5f\u4e0d\u77e5\u7761\u4e86\u591a\u4e45\uff0c\u6726\u6726\u80e7\u80e7\u4e2d\uff0c\u5ffd\u542c\u5f97\u6709\u811a\u6b65\u4e4b\u58f0\uff0c\u51af\u7433\u8f6c\u4e86\u4e2a\u8eab\uff0c\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a \u201c\u54a6\uff0c\u6709\u4e2a\u5973\u5b69\u5b50\u7761\u5728\u8fd9\u91cc\uff01\u201d\u51af\u7433\u8df3\u8d77\u8eab\u6765\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u5413\u5f97\u7761\u610f\u5168\u6d88\uff0c\u6765\u7684\u4e24\u4eba\uff0c\u4e00\u7537 \u4e00\u5973\uff0c\u6b63\u662f\u738b\u6556\u4e0e\u53f6\u6a2a\u6ce2\u3002 \u3000\u3000\u539f\u6765\u97e9\u91cd\u5c71\u4e0e\u53f6\u6a2a\u6ce2\u9003\u8d70\u4e4b\u540e\uff0c\u4e0d\u4e45\u5c31\u4e0e\u738b\u6556\u4f1a\u5408\uff0c\u97e9\u91cd\u5c71\u53d7\u4e00\u638c\uff0c\u867d\u65e0\u6027\u547d\u4e4b\u5fe7\uff0c\u4f46 \u8981\u517b\u4f24\u51e0\u65e5\uff0c\u738b\u6556\u4e2d\u4e86\u6bd2\u5200\uff0c\u6bd2\u52bf\u4e5f\u6e10\u6e10\u53d1\u4f5c\u3002\u97e9\u91cd\u5c71\u9053\uff1a\u201c\u5f18\u6cd5\u90a3\u79c3\u9a74\u4e2d\u4e86\u6211\u7684\u56de\u73af\u94a9\uff0c \u8f7b\u529f\u5fc5\u53d7\u5f71\u54cd\uff0c\u81ed\u5a46\u5a18\u4f60\u53bb\u7f00\uff08\u8ddf\u8e2a\uff09\u4ed6\u3002\u6211\u53eb\u5929\u53f6\u5e08\u5f1f\u8d76\u53bb\u5e2e\u4f60\u3002\u201d\u53c8\u5bf9\u738b\u6556\u9053\uff1a\u201c\u4f60\u4e2d \u4e86\u90a3\u91ce\u4e2b\u5934\u7684\u6bd2\u5200\uff0c\u53ef\u5f97\u6349\u7740\u5979\u903c\u5979\u62ff\u51fa\u89e3\u836f\u3002\u201d\u5176\u5b9e\u97e9\u91cd\u5c71\u4e5f\u6709\u89e3\u836f\uff0c\u4f46\u6015\u738b\u6556\u4e0d\u80af\u51fa \u529b\uff0c\u6240\u4ee5\u4e0d\u62ff\u7ed9\u4ed6\uff0c\u53ea\u7ed9\u4e86\u4ed6\u4e24\u9897\u666e\u901a\u89e3\u6bd2\u6b62\u75db\u836f\u4e38\uff0c\u5c31\u50ac\u4ed6\u518d\u53bb\u8ffd\u8e2a\u3002 \u3000\u3000\u51af\u7433\u4e00\u8dc3\u800c\u8d77\uff0c\u53cd\u8eab\u6b32\u9003\uff0c\u53f6\u6a2a\u6ce2\u957f\u5251\u4e00\u6307\uff0c\u62e6\u7740\u5979\u7684\u53bb\u8def\u3002\u51af\u7433\u4e00\u62db\u201c\u9965\u9e70\u63a0\u7fbd\u201d\uff0c \u75be\u523a\u8fc7\u53bb\u3002\u53f6\u6a2a\u6ce2\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u6709\u591a\u5927\u80fd\u8010\uff0c\u6562\u4e0e\u5e08\u5a18\u5bf9\u5251\uff01\u201d\u6a2a\u5251\u4e00\u5230\uff0c\u5f80\u5916\u4e00\u5c55\uff0c\u8fd9 \u4e00\u62db\u653b\u5b88\u4ff1\u5907\uff0c\u7aef\u7684\u5389\u5bb3\u3002\u4f46\u53f6\u6a2a\u6ce2\u8bda\u6050\u8bef\u4f24\u4e86\u51af\u7433\uff0c\u4e0d\u6562\u7528\u5c3d\u5168\u529b\uff0c\u53ea\u968f\u610f\u4e00\u6321\uff0c\u4ee5\u4e3a\u51af \u7433\u7684\u5251\uff0c\u5fc5\u7136\u88ab\u5979\u78d5\u98de\uff0c\u90a3\u6599\u51af\u7433\u4eca\u975e\u6614\u6bd4\uff0c\u53cc\u5251\u76f8\u4ea4\uff0c\u53f6\u6a2a\u6ce2\u7684\u5251\u7adf\u7ed9\u8361\u5f00\uff0c\u5403\u4e86\u4e00\u60ca\uff0c \u6697\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u91ce\u4e2b\u5934\u8fdb\u5883\uff0c\u600e\u4e48\u5982\u6b64\u795e\u901f\uff1f\u201d\u4e0d\u6562\u5927\u610f\uff0c\u970d\u970d\u51e0\u5251\uff0c\u5c06\u51af\u7433\u7684\u5251\u52bf\u538b\u4f4f\u3002 \u3000\u3000\u53f6\u6a2a\u6ce2\u8ba4\u771f\u8d77\u6765\uff0c\u51af\u7433\u81ea\u4e0d\u662f\u5979\u7684\u5bf9\u624b\uff0c\u4f46\u51af\u7433\u5f97\u4e86\u5085\u9752\u4e3b\u7684\u9057\u4e66\uff0c\u4e60\u4e86\u7384\u95e8\u7684\u6b63\u5b97\u5185 \u529f\uff0c\u52a0\u4e0a\u672c\u6765\u5b66\u4f1a\u7684\u5404\u6d3e\u6b66\u827a\uff0c\u4e09\u4e94\u5341\u62db\u4e4b\u5185\uff0c\u5c45\u7136\u672a\u9732\u8d25\u8c61\u3002\u738b\u6556\u63d0\u67aa\u6b32\u4e0a\uff0c\u53f6\u6a2a\u6ce2\u6012 \u9053\uff1a\u201c\u4e0d\u5fc5\u4f60\u6765\u5e2e\u5fd9\uff0c\u8001\u5a18\u4e0d\u4fe1\u64d2\u4e0d\u4e86\u5979\uff01\u201d\u5251\u62db\u4e00\u7d27\uff0c\u987f\u65f6\u628a\u51af\u7433\u88f9\u5728\u5251\u5149\u4e4b\u4e2d\u3002\u53ea\u662f\u53f6 \u6a2a\u6ce2\u6709\u6240\u987e\u5fcc\uff0c\u53ea\u60f3\u751f\u64d2\uff0c\u4e0d\u6562\u4f7f\u51fa\u6740\u624b\uff0c\u6240\u4ee5\u51af\u7433\u867d\u7136\u8fed\u9047\u9669\u62db\uff0c\u8fd8\u80fd\u62fc\u547d\u62db\u67b6\u3002 \u3000\u3000\u53c8\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u53f6\u6a2a\u6ce2\u6df1\u89c9\u989c\u9762\u65e0\u5149\uff0c\u62fc\u7740\u628a\u5979\u523a\u4f24\uff0c\u5251\u638c\u517c\u65bd\uff0c\u5251\u523a\u9ebb\u7a74\uff0c\u638c\u593a\u5175 \u5203\uff0c\u51af\u7433\u529b\u6321\u6570\u62db\uff0c\u5341\u5206\u5403\u529b\uff0c\u770b\u770b\u5c31\u8981\u88ab\u5979\u751f\u64d2\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u745b\u59b9\uff0c\u662f\u4f60\u5417\uff1f\u201d \u51af\u7433\u4e0d\u6562\u56de\u5934\uff0c\u529b\u67b6\u4e00\u5251\uff0c\u5927\u58f0\u7b54\u9053\uff1a\u201c\u662f\u5440\uff0c\u674e\u6cbb\u5927\u54e5\uff0c\u5feb\u6765\u5e2e\u6211\u3002\u201d\u53f6\u6a2a\u6ce2\u5251\u8eab\u4e00\u538b\uff0c \u628a\u51af\u7433\u7684\u5b9d\u5251\u538b\u5f97\u62ac\u4e0d\u8d77\u6765\uff0c\u5de6\u624b\u9a88\u6307\u5982\u621f\uff0c\u9aa4\u7136\u5411\u5979\u9ebb\u7a74\u4e00\u70b9\uff01 \u3000\u3000\u5c31\u5728\u6b64\u9645\uff0c\u90a3\u5c11\u5e74\u5df2\u5982\u98de\u8d76\u5230\uff0c\u53f3\u624b\u957f\u5251\u4e00\u683c\uff0c\u628a\u738b\u6556\u7684\u94a9\u9570\u67aa\u8361\u5f00\uff0c\u5de6\u638c\u4e00\u5806\uff0c\u53c8\u5c06 \u53f6\u6a2a\u6ce2\u7684\u70b9\u7a74\u62db\u6570\u5316\u89e3\u3002\u53f6\u6a2a\u6ce2\u4e00\u770b\uff0c\u6b63\u662f\u4ee5\u524d\u5728\u201c\u4e09\u6f6d\u5370\u6708\u201d\u6240\u9047\u7684\u90a3\u4e2a\u5c11\u5e74\uff0c\u540e\u6765\u67e5\u51fa \u662f\u674e\u81ea\u6210\u66fe\u5b59\u7684\u674e\u6cbb\u3002\u563f\u563f\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u53c8\u662f\u4e00\u4e2a\u94a6\u72af\u6765\u4e86\uff01\u201d\u5251\u62db\u66b4\u5c55\uff0c\u9022\u53d6\u674e\u6cbb\uff0c\u674e\u6cbb \u5251\u628a\u4e00\u7ffb\uff0c\u4f3c\u5de6\u5ffd\u53f3\uff0c\u5237\u7684\u4e00\u5251\u523a\u5230\u654c\u4eba\u5de6\u808b\uff0c\u53f6\u6a2a\u6ce2\u4f7f\u4e2a\u201c\u5c01\u4faf\u593a\u5370\u201d\u62db\u6570\uff0c\u60f3\u628a\u674e\u6cbb\u7684 \u5251\u5c01\u4f4f\uff0c\u90a3\u6599\u5251\u62db\u65b9\u5c55\uff0c\u674e\u6cbb\u7684\u5251\u5df2\u5230\u53f3\u65b9\uff0c\u53f6\u6a2a\u6ce2\u7ed9\u4ed6\u4e00\u8fde\u51e0\u8bb0\u602a\u62db\uff0c\u6740\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u51af \u7433\u53c8\u4e58\u9699\u653b\u51fb\uff0c\u53f6\u6a2a\u6ce2\u9669\u8c61\u73af\u751f\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u738b\u6556\uff0c\u4f60\u5bf9\u4ed8\u8fd9\u4e2a\u4e2b\u5934\uff01\u201d\u738b\u6556\u5c55\u5f00\u94a9\u9570\u67aa \u4e0a\u524d\u52a9\u6218\uff0c\u5fc3\u4e2d\u51b7\u7b11\uff1a\u201c\u539f\u6765\u4f60\u8fde\u4e24\u4e2a\u5c0f\u5b69\u5b50\u90fd\u6253\u4e0d\u8fc7\uff0c\u8fd8\u6446\u4ec0\u4e48\u524d\u8f88\u67b6\u5b50\uff1f\u201d \u3000\u3000\u56db\u4eba\u4e00\u5408\u5373\u5206\uff0c\u674e\u6cbb\u7684\u5251\u9489\u7d27\u53f6\u6a2a\u6ce2\uff0c\u6b64\u6765\u5f7c\u5f80\uff0c\u5404\u65bd\u6740\u624b\uff0c\u4ece\u6811\u6797\u4e2d\u6253\u4e0b\u5c71\u5761\u3002\u674e\u6cbb \u662f\u767d\u53d1\u9b54\u5973\u7684\u5ae1\u7cfb\u4f20\u4eba\uff0c\u5251\u6cd5\u5947\u8be1\u8f9b\u8fa3\uff0c\u5929\u4e0b\u65e0\u5339\uff0c\u53f6\u6a2a\u6ce2\u529f\u529b\u867d\u9ad8\uff0c\u4e00\u65f6\u95f4\u5374\u88ab\u538b\u5728\u4e0b\u98ce\u3002 \u3000\u3000\u51af\u7433\u89c1\u674e\u6cbb\u5230\u6765\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u5979\u6709\u4e86\u6628\u665a\u7684\u7ecf\u9a8c\uff0c\u518d\u6218\u738b\u6556\uff0c\u4e0d\u56fe\u6025\u8fdb\uff0c\u53ea\u628a\u65e0\u6781\u5251 \u6cd5\uff0c\u970d\u970d\u5c55\u5f00\uff0c\u628a\u95e8\u6237\u5c01\u5f97\u975e\u5e38\u4e25\u5bc6\uff0c\u738b\u6556\u8fde\u653b\u4e86\u5341\u62db\uff0c\u8eab\u4e0a\u6e10\u6e10\u53d1\u70ed\uff0c\u53e3\u4e2d\u7126\u6e34\uff0c\u77e5\u662f\u6bd2 \u4f24\u53d1\u4f5c\uff0c\u653b\u52bf\u4e0d\u5f97\u4e0d\u7f13\u3002 \u3000\u3000\u51af\u7433\u563b\u563b\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u72d7\u8d3c\uff0c\u6280\u4ea6\u4e0d\u8fc7\u6b62\u6b64\u800c\u5df2\uff0c\u5478\uff0c\u8fd8\u4e0d\u5feb\u5feb\u5f03\u67aa\u6295\u964d\uff01\u201d\u738b\u6556\u6b66\u529f \u5b9e\u9645\u5728\u5979\u4e4b\u4e0a\uff0c\u53ea\u56e0\u6bd2\u5200\u7684\u6bd2\u6027\u53d1\u4f5c\u5f97\u5feb\uff0c\u6240\u4ee5\u4e0d\u652f\u3002\u7ed9\u5979\u4e00\u559d\uff0c\u53c8\u6c14\u53c8\u6012\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u91ce \u4e2b\u5934\uff0c\u4f60\u4e2d\u4e86\u6211\u7684\u6bd2\u9488\uff0c\u4e03\u65e5\u4e4b\u5185\uff0c\u5fc5\u5b9a\u8eab\u4ea1\uff0c\u8fd8\u901e\u4ec0\u4e48\u80fd\uff1f\u201d \u3000\u3000\u51af\u7433\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff01\u201d\u7a81\u7136\u8f6c\u5b88\u4e3a\u653b\uff0c\u4f7f\u5f00\u7684\u5374\u662f\u5141\u5e27\u4eb2\u81ea\u6559\u5979\u7684\u5c11\u6797\u6d3e \u8fbe\u6469\u5251\u6cd5\uff0c\u7740\u7740\u62a2\u653b\uff0c\u4ece\u201c\u4e00\u82c7\u6e21\u6c5f\u201d\u4f7f\u5230\u201c\u6cd5\u8f6e\u4e09\u8f6c\u201d\uff0c\u4e0d\u8fc7\u5341\u62db\uff0c\u5df2\u628a\u738b\u6556\u6740\u5f97\u624b\u5fd9\u811a \u4e71\u3002\u738b\u6556\u53eb\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\u6211\u7684\u8bdd\u5417\uff1f\u4f60\u7684\u66f2\u6c60\u7a74\u73b0\u5728\u662f\u4e0d\u662f\u9690\u9690\u9178\u9ebb\uff1f\u201d\u66f2\u6c60\u7a74\u4f4d\u4e8e\u8098\u90e8\u5173 \u8282\u4e2d\u592e\uff0c\u738b\u6556\u8ba1\u7b97\u767d\u7709\u9488\u4ece\u8840\u7ba1\u4e0a\u884c\uff0c\u73b0\u5728\u8be5\u884c\u81f3\u66f2\u6c60\u7a74\u9644\u8fd1\uff0c\u56e0\u6b64\u51fa\u8a00\u8b66\u544a\uff0c\u4e0d\u6599\u51af\u7433\u5403 \u4e86\u5f18\u6cd5\u5927\u5e08\u6240\u8d60\u7684\u201c\u5c11\u8fd8\u4e39\u201d\uff0c\u767d\u7709\u9488\u5df2\u88ab\u8840\u6db2\u80f6\u4f4f\uff0c\u6682\u4e0d\u4f1a\u4e0a\u884c\uff0c\u56e0\u6b64\u6beb\u65e0\u75db\u695a\u3002\u51af\u7433\u89c1 \u4ed6\u6ee1\u5934\u5927\u6c57\uff0c\u5c1a\u81ea\u865a\u58f0\u606b\u5413\uff0c\u201c\u563f\u563f\u201d\u7b11\u9053\uff1a\u201c\u4e14\u770b\u662f\u8c01\u7684\u6b7b\u671f\u5230\u5427\uff01\u201d\u5de6\u4e00\u5251\u53f3\u4e00\u5251\uff0c\u62db \u62db\u51cc\u5389\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u738b\u6556\u8fde\u4e2d\u4e24\u5251\uff0c\u5077\u773c\u770b\u53f6\u6a2a\u6ce2\u65f6\uff0c\u53f6\u6a2a\u6ce2\u4e0e\u90a3\u5c11\u5e74\u53ae\u6740\u6b63\u9163\uff0c\u4e24\u4eba\u65d7\u9f13 \u76f8\u5f53\uff0c\u4e00\u65f6\u95f4\u5c1a\u5206\u4e0d\u51fa\u80dc\u8d25\uff0c\u60f3\u53f6\u6a2a\u6ce2\u62bd\u51fa\u8eab\u6765\u5e2e\u52a9\u81ea\u5df1\u662f\u4e0d\u80fd\u591f\u7684\u4e86\uff0c\u4e0d\u89c9\u6c14\u9981\uff0c\u58f0\u8c03\u8f6c \u4f4e\uff0c\u4ee5\u6073\u6c42\u7684\u53e3\u543b\u8bf4\u9053\uff1a\u201c\u6211\u6b7b\u4e86\u4f60\u4e5f\u4e0d\u80fd\u6d3b\uff0c\u4e0d\u5982\u54b1\u4eec\u4ea4\u6362\u89e3\u836f\uff0c\u5404\u4e0d\u76f8\u72af\u5982\u4f55\uff1f\u201d\u51af\u7433 \u5927\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u597d\u597d\u6c42\uff0c\u6216\u8bb8\u6211\u80fd\u9976\u4f60\uff0c\u4f60\u610f\u56fe\u8981\u631f\uff0c\u6211\u5c31\u504f\u4e0d\u9976\u4f60\u4e86\uff01\u201d\u5251\u62db\u4e00\u7d27\uff0c\u4e00\u7ffb \u4e00\u7ede\uff0c\u738b\u6556\u7684\u94a9\u9570\u67aa\u628a\u63e1\u4e0d\u4f4f\uff0c\u8131\u624b\u63b7\u4e0b\u5c71\u6da7\uff0c\u7ed9\u5c71\u4e0a\u6cfb\u4e0b\u7684\u7011\u5e03\u4e00\u51b2\uff0c\u5815\u5165\u5c71\u811a\u6cb3\u4e2d\u3002\u738b \u6556\u6a2a\u4e86\u51af\u7433\u4e00\u773c\uff0c\u53eb\u9053\uff1a\u201c\u7389\u8c8c\u82b1\u5bb9\uff0c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u51af\u7433\u6012\u9053\uff1a\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d\u6068\u4ed6\u53e3\u820c \u8f7b\u8584\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u628a\u738b\u6556\u523a\u4e86\u4e2a\u900f\u660e\u7a9f\u7abf\uff0c\u81ea\u524d\u5fc3\u76f4\u900f\u540e\u5fc3\uff0c\u663e\u89c1\u4e0d\u80fd\u6d3b\u4e86\uff01 \u3000\u3000\u674e\u6cbb\u4e0e\u53f6\u6a2a\u6ce2\u5404\u81ea\u4f7f\u51fa\u770b\u5bb6\u672c\u9886\uff0c\u674e\u6cbb\u80dc\u5728\u5251\u6cd5\u5947\u8be1\uff0c\u53f6\u6a2a\u6ce2\u5374\u80dc\u5728\u529f\u529b\u6df1\u6e5b\uff0c\u6fc0\u6597\u4e86 \u4e00\u767e\u6765\u62db\uff0c\u674e\u6cbb\u989d\u5934\u89c1\u6c57\uff0c\u547c\u5438\u7d27\u4fc3\uff0c\u53f6\u6a2a\u6ce2\u6b63\u5728\u53cd\u5b88\u4e3a\u653b\uff0c\u8fde\u62a2\u5148\u624b\uff0c\u5ffd\u542c\u5f97\u738b\u6556\u60e8\u53eb\u4e4b \u4e3e\uff0c\u4e0d\u89c9\u80c6\u5bd2\uff0c\u60f3\u9053\uff1a\u4ed6\u4eec\u4e8c\u4eba\u8054\u624b\u5408\u653b\uff0c\u6211\u53ef\u662f\u6597\u4ed6\u4eec\u4e0d\u8fc7\u3002\u865a\u6643\u4e00\u62db\uff0c\u5982\u98de\u9003\u8dd1\uff0c\u674e\u6cbb \u6c14\u5598\u5401\u5401\uff0c\u505c\u5251\u62db\u547c\u51af\u7433\uff0c\u4e0d\u518d\u8ffd\u8d76\u3002 \u3000\u3000\u51af\u7433\u60f3\u8d77\u4ee5\u524d\u5728\u4ed6\u53d7\u4f24\u4e4b\u9645\u5f03\u4ed6\u800c\u53bb\u7684\u4e8b\uff0c\u5fc3\u4e2d\u6709\u6127\uff0c\u9762\u8272\u5c34\u5c2c\u3002\u674e\u6cbb\u7b11\u9053\uff1a\u201c\u745b\u59b9\uff0c \u6211\u627e\u5f97\u4f60\u597d\u82e6\u3002\u4f60\u548c\u8fd9\u8d3c\u5a46\u5a18\u4e0d\u662f\u5f88\u8981\u597d\u5417\uff1f\u8fd8\u8bb0\u5f97\u524d\u5e74\u5728\u4e09\u6f6d\u5370\u6708\u4e4b\u65f6\uff0c\u4f60\u662f\u548c\u5979\u4e00\u540c\u6765 \u7684\uff0c\u73b0\u5728\u600e\u4e48\u548c\u5979\u6253\u8d77\u67b6\u6765\u4e86\uff1f\u201d\u51af\u7433\u5ff8\u6029\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6b22\u559c\u548c\u8c01\u597d\u4fbf\u548c\u8c01\u597d\uff0c\u8fd9\u6709\u4ec0 \u4e48\u5947\u602a\u5462\uff1f\u8fd9\u4e2a\u8001\u5996\u5987\u6b3a\u8d1f\u6211\uff0c\u6211\u4e0d\u6b22\u559c\u5979\u5566\u3002\u201d \u3000\u3000\u674e\u6cbb\u5fc3\u4e2d\u6697\u559c\uff0c\u60f3\u9053\uff1a\u5230\u5e95\u5979\u7075\u6839\u672a\u706d\uff0c\u77e5\u9053\u5206\u8fa8\u597d\u4eba\u574f\u4eba\u5566\u3002\u5979\u5e74\u5e7c\u65e0\u77e5\uff0c\u6211\u5f97\u597d\u597d \u62db\u6276\u5979\u3002\u5ffd\u89c1\u51af\u7433\u7709\u5934\u4e00\u76b1\uff0c\u95ee\u9053\uff1a\u201c\u745b\u59b9\uff0c\u4f60\u53d7\u4f24\u4e86\u4e48\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6ca1\u6709\u5440\uff0c\u53ea\u662f\u5728\u66f2 \u6c60\u7a74\u9644\u8fd1\uff0c\u751a\u89c9\u9178\u9ebb\uff0c\u4e0d\u77e5\u662f\u4f55\u9053\u7406\uff1f\u201d\u8bf4\u5b8c\u4e4b\u540e\uff0c\u84e6\u7136\u60f3\u8d77\u738b\u6556\u7684\u8bdd\uff0c\u4e0d\u89c9\u4e00\u60ca\u3002\u539f\u6765\u51af \u7433\u5728\u670d\u98df\u4e86\u5c11\u8fd8\u4e39\u540e\uff0c\u767d\u7709\u9488\u672c\u5df2\u7ed9\u8840\u6db2\u80f6\u4f4f\uff0c\u53ef\u662f\u5728\u4e00\u573a\u6fc0\u6597\u4e4b\u540e\uff0c\u8840\u6d41\u5feb\u901f\uff0c\u767d\u7709\u9488\u53c8 \u7f13\u7f13\u4e0a\u5347\u3002\u674e\u6cbb\u7684\u8205\u8205\u6b66\u6210\u5316\u901a\u6653\u533b\u7406\uff0c\u674e\u6cbb\u4e5f\u61c2\u5f97\u4e00\u4e9b\uff0c\u4f38\u624b\u6478\u5979\u7389\u8155\uff0c\u51af\u7433\u9762\u4e0a\u4e00\u7ea2\uff0c \u674e\u6cbb\u56e0\u548c\u51af\u745b\u81ea\u5c0f\u73a9\u5927\uff0c\u5f7c\u6b64\u65e0\u62d8\uff0c\u6240\u4ee5\u6beb\u4e0d\u5728\u610f\uff0c\u6478\u4e86\u4e00\u6478\uff0c\u60ca\u53eb\u9053\uff1a\u201c\u4f60\u662f\u53d7\u4e86\u5582\u6bd2\u6697 \u5668\u6240\u4f24\u4e86\u3002\u5514\uff0c\u6478\u4e0d\u51fa\u6765\uff0c\u6697\u5668\u5fc5\u5b9a\u6781\u4e3a\u7ec6\u5c0f\uff0c\u60f3\u5fc5\u662f\u6885\u82b1\u9488\u5427\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u4e0d\u662f\u6885\u82b1 \u9488\uff0c\u662f\u53eb\u505a\u4ec0\u4e48\u4e03\u715e\u767d\u7709\u9488\u3002\u201d \u3000\u3000\u674e\u6cbb\u5927\u5403\u4e00\u60ca\uff0c\u9ec4\u8c46\u822c\u7684\u6c57\u73e0\u9897\u9897\u8ff8\u51fa\uff0c\u51af\u7433\u89c1\u4ed6\u5982\u6b64\u7d27\u5f20\uff0c\u60ca\u7591\u6e10\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u4e03\u715e \u767d\u7709\u9488\u5f88\u5389\u5bb3\u4e48\uff1f\u6211\u7ed9\u6253\u4e2d\u65f6\u53ea\u4f3c\u7ed9\u868a\u5b50\u53ee\u4e86\u4e00\u53e3\u4f3c\u7684\uff0c\u4e00\u70b9\u4e5f\u4e0d\u89c9\u5f97\u75db\u695a\u3002\u201d\u674e\u6cbb\u6025\u95ee \u9053\uff1a\u201c\u662f\u8c01\u544a\u8bc9\u4f60\u7684\uff1f\u4f60\u4e2d\u7684\u6697\u5668\u771f\u662f\u53eb\u505a\u4e03\u715e\u767d\u7709\u9488\uff1f\u201d\u51af\u7433\u6307\u7740\u5730\u4e0b\u738b\u6556\u7684\u5c38\u9996\uff0c\u8bf4 \u9053\uff1a\u201c\u662f\u4ed6\u544a\u8bc9\u6211\u7684\uff0c\u767d\u7709\u9488\u5c31\u662f\u4ed6\u5c04\u7684\uff0c\u4ed6\u8fd8\u8bf4\uff0c\u6211\u8fc7\u4e0d\u4e86\u4e03\u65e5\u5c31\u8981\u8eab\u4ea1\u54e9\uff01\u201d\u4fa7\u5934\u4e00 \u60f3\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u5478\uff0c\u6211\u53ef\u4e0d\u4fe1\uff0c\u4ed6\u521a\u624d\u8bf4\u8981\u4e0e\u6211\u4e92\u6362\u89e3\u836f\uff0c\u6211\u4e5f\u4e0d\u7406\u4ed6\u3002\u4ed6\u5f53\u6211\u662f\u5c0f\u5b69\u54e9\uff0c \u6211\u53ef\u6ca1\u4e0a\u4ed6\u7684\u5f53\u3002\u201d\u674e\u6cbb\u4e00\u542c\uff0c\u5413\u5f97\u9762\u5982\u571f\u8272\uff0c\u4e0d\u5f85\u5979\u628a\u8bdd\u8bf4\u5b8c\uff0c\u6025\u6025\u4e0a\u524d\u641c\u738b\u6556\u7684\u8eab\uff0c\u51af \u7433\u63a9\u9762\u8bf4\u9053\uff1a\u201c\u5413\uff0c\u4ed6\u6b7b\u7684\u6837\u5b50\u597d\u96be\u770b\uff0c\u628a\u5c38\u9996\u62d6\u8fc7\u4e00\u8fb9\uff0c\u6211\u4e0d\u8981\u770b\u3002\u201d\u51af\u7433\u5230\u5e95\u662f\u5973\u5b69\u513f \u5bb6\uff0c\u51fa\u624b\u867d\u7136\u6bd2\u8fa3\uff0c\u89c1\u4e86\u5c38\u9ab8\uff0c\u95fb\u4e86\u8840\u8165\uff0c\u53ef\u5c31\u4e0d\u7981\u5bb3\u6015\u4e86\u3002 \u3000\u3000\u674e\u6cbb\u4e0d\u7406\u51af\u7433\u53eb\u558a\uff0c\u641c\u4e86\u738b\u6556\u5c38\u8eab\uff0c\u53d6\u51fa\u4e00\u7b52\u6697\u5668\uff0c\u63ed\u5f00\u7b52\u76d6\uff0c\u91cc\u9762\u88c5\u6ee1\u725b\u6bdb\u4e5f\u4f3c\u7684\u5c0f \u82b1\u9488\uff0c\u6bd4\u6885\u82b1\u9488\u8fd8\u8981\u7ec6\u5c0f\uff0c\u674e\u6cbb\u5fc3\u4e2d\u4e00\u75db\uff0c\u6025\u5fd9\u518d\u641c\uff0c\u641c\u904d\u5168\u8eab\uff0c\u5374\u627e\u4e0d\u5230\u89e3\u836f\uff0c\u51af\u7433\u9053\uff1a \u201c\u4f60\u4e0d\u542c\u6211\u7684\u8bdd\uff0c\u6211\u53ef\u8981\u8dd1\u5566\uff01\u201d\u8dd1\u5230\u5c71\u8fb9\uff01\u9762\u5bf9\u6cb3\u6d41\u6df1\u6df1\u547c\u5438\uff0c\u674e\u6cbb\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u542c\u4f60\u7684 \u8bdd\uff0c\u4f60\u4e0d\u8981\u8dd1\uff01\u201d\u628a\u5c38\u9996\u8e22\u8fc7\u4e00\u8fb9\uff0c\u7528\u843d\u53f6\u5c06\u5b83\u63a9\u76d6\u3002\u51af\u7433\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5413\u4f60\u5462\uff0c\u4f60 \u4e5f\u76f8\u4fe1\uff0c\u6211\u73b0\u5728\u4e0d\u8dd1\u5566\uff0c\u5582\uff0c\u767d\u7709\u9488\u771f\u7684\u5f88\u5389\u5bb3\u5417\uff1f\u201d \u3000\u3000\u674e\u6cbb\u89c1\u5979\u6b7b\u5230\u4e34\u5934\uff0c\u5c1a\u81ea\u4e0d\u77e5\uff0c\u8fd8\u4f3c\u5c0f\u5b69\u5b50\u4e00\u822c\u7684\u4e71\u5f00\u73a9\u7b11\uff0c\u53c8\u662f\u60b2\u75db\uff0c\u53c8\u662f\u5fc3\u6025\u3002\u95ee \u9053\uff1a\u201c\u5582\uff0c\u8fd9\u4eba\u4f7f\u7684\u5175\u5668\u5462\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u7ed9\u6211\u6253\u5f97\u4ed6\u63b7\u4e0b\u6cb3\u4e2d\u53bb\u4e86\u3002\u201d\u6c5f\u6d41\u4e1c\u53bb\uff0c\u6cb3\u6c34\u6ed4 \u6ed4\uff0c\u6c34\u6df1\u6d41\u6025\uff0c\u663e\u89c1\u662f\u65e0\u6cd5\u5bfb\u627e\u7684\u4e86\u3002\u738b\u6556\u7684\u89e3\u836f\u5c31\u85cf\u5728\u67aa\u7ba1\u4e4b\u4e2d\uff0c\u674e\u6cbb\u5c31\u7b97\u627e\u7740\uff0c\u4e5f\u8bc6\u4e0d \u7834\u673a\u5173\uff0c\u4f55\u51b5\u6839\u672c\u65e0\u4ece\u5bfb\u627e\u3002 \u3000\u3000\u6b66\u743c\u7476\u719f\u6089\u5404\u5bb6\u6697\u5668\uff0c\u674e\u6cbb\u81ea\u5e7c\u8ddf\u968f\u6bcd\u4eb2\uff0c\u77e5\u9053\u4e03\u715e\u767d\u7709\u9488\u7684\u6765\u5386\uff0c\u5fc3\u4e2d\u76d8\u7b97\u9053\uff1a\u201c\u6b64 \u53bb\u56db\u5ddd\uff0c\u6700\u5c11\u8981\u8d70\u4e00\u4e2a\u591a\u6708\uff0c\u5c31\u7b97\u5510\u5bb6\u80af\u7ed9\u89e3\u836f\uff0c\u4e5f\u662f\u7f13\u4e0d\u6d4e\u6025\u3002\u201d\u5fc3\u4e2d\u4e00\u6025\uff0c\u4e0d\u89c9\u6ef4\u4e0b\u6cea \u6765\uff0c\u500f\u53c8\u60f3\u9053\uff1a\u201c\u53ef\u4e0d\u80fd\u7ed9\u745b\u59b9\u77e5\u9053\uff0c\u5979\u77e5\u9053\u4e86\u4e00\u5b9a\u614c\u6b7b\uff0c\u5c31\u7b97\u4e0d\u80fd\u633d\u6551\uff0c\u4e5f\u8be5\u8ba9\u5979\u6b7b\u524d\u5c3d \u60c5\u5feb\u6d3b\u3002\u201d\u5077\u5077\u8f6c\u8fc7\u4e86\u8eab\uff0c\u62b9\u5e72\u773c\u6cea\u3002\u53ef\u662f\u51af\u7433\u5df2\u5168\u770b\u5728\u773c\u4e2d\uff0c\u5927\u4e3a\u611f\u52a8\u3002\u5fc3\u9053\uff1a\u4ee5\u524d\u4ed6\u53d7 \u4f24\u7684\u65f6\u5019\uff0c\u6211\u629b\u6389\u4ed6\uff0c\u4ed6\u4e00\u70b9\u4e5f\u4e0d\u602a\u6211\uff0c\u73b0\u5728\u6211\u4e2d\u4e86\u6697\u5668\uff0c\u4ed6\u5374\u8fd9\u6837\u5173\u5fc3\uff0c\u6bd4\u6211\u7d27\u5f20\u767e\u500d\uff0c \u54ce\uff0c\u8fd9\u4eba\u867d\u7136\u4e0d\u7b97\u806a\u660e\uff0c\u5374\u662f\u771f\u771f\u96be\u5f97\u3002\u51af\u7433\u5728\u7687\u5e9c\u4e4b\u4e2d\uff0c\u867d\u53d7\u4f17\u4eba\u5ba0\u7231\uff0c\u53ef\u662f\u8fd9\u6837\u81ea\u7136\u6d41 \u9732\u7684\u81f3\u6027\u771f\u60c5\uff0c\u5979\u5374\u662f\u6709\u751f\u4ee5\u6765\u7b2c\u4e00\u6b21\u611f\u53d7\u3002 \u3000\u3000\u674e\u6cbb\u56de\u8fc7\u5934\u6765\uff0c\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u745b\u59b9\uff0c\u4f60\u559c\u6b22\u4ec0\u4e48\uff1f\u6211\u966a\u4f60\u73a9\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u77a7\u4f60\u6025\u6210\u8fd9 \u6837\uff0c\u8fde\u7b11\u58f0\u4e5f\u662f\u98a4\u6296\u7684\uff01\u7b11\u4e0d\u51fa\u5c31\u522b\u7b11\uff0c\u52c9\u5f3a\u88c5\u7b11\u5f88\u662f\u96be\u770b\uff01\u201d\u8bf4\u7740\u626e\u4e86\u4e00\u4e2a\u9b3c\u8138\uff0c\u8fd9\u56de\u674e \u6cbb\u5012\u7ed9\u5979\u771f\u9017\u5f15\u5f97\u7834\u6d95\u4e3a\u7b11\u3002\u51af\u7433\u9053\uff1a\u201c\u6211\u90fd\u4e0d\u6025\u5462\uff01\u4f60\u6025\uff1f\u6211\u4e0d\u4fe1\u767d\u7709\u9488\u8981\u5f97\u4e86\u6211\u7684\u547d\u3002 \u6628\u665a\u6211\u5403\u4e86\u8001\u548c\u5c1a\u4e00\u9897\u4e38\u836f\uff0c\u4ec0\u4e48\u75db\u82e6\u90fd\u4e0d\u89c9\u5f97\u3002\u4ed6\u8fd8\u53eb\u6211\u5230\u798f\u5efa\u8386\u7530\u7684\u5c11\u6797\u5bfa\u53bb\u627e\u4ed6\u3002\u4f60 \u60f3\uff0c\u82e5\u7136\u6211\u6d3b\u4e0d\u4e86\u4e03\u5929\uff0c\u4ed6\u600e\u4e48\u4f1a\u53eb\u6211\u53bb\uff1f\u6211\u5728\u8def\u4e0a\u5df2\u7ecf\u6b7b\u5566\uff01\u201d\u674e\u6cbb\u4e00\u559c\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48 \u8001\u548c\u5c1a\uff1f\u90a3\u836f\u4e38\u8fd8\u6709\u5417\uff1f\u201d\u51af\u7433\u541e\u4e0b\u4e00\u9897\u836f\u4e38\uff0c\u9053\uff1a\u201c\u6b66\u529f\u9876\u597d\u9876\u597d\u7684\u548c\u5c1a\u561b\u3002\u201d\u674e\u6cbb\u7b11 \u9053\uff1a\u201c\u4ed6\u603b\u5f97\u6709\u4e2a\u540d\u5b57\u5427\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u4ed6\u53c8\u672a\u544a\u8bc9\u6211\uff0c\u6211\u600e\u4e48\u77e5\u9053\uff1f\u201d\u674e\u6cbb\u5fc3\u60f3\uff0c\u96be\u9053\u662f \u5c11\u6797\u5bfa\u7684\u9ad8\u50e7\uff1f\u83ab\u975e\u5c31\u7adf\u662f\u8386\u7530\u5c11\u6797\u5bfa\u7684\u4e3b\u6301\u3002\u90a3\u4e48\u53bb\u5d69\u5c71\u4e0d\u662f\u8fd1\u5f97\u591a\u4e86\u54a6\uff0c\u6211\u771f\u7b28\uff01\u7adf\u7136 \u60f3\u4e0d\u8d77\u5c11\u6797\u5bfa\u6765\uff01\u5c11\u6797\u5bfa\u7684\u7075\u4e39\u5999\u836f\u6781\u591a\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u4e0d\u5fc5\u9700\u8981\u5510\u5bb6\u7684\u89e3\u836f\u4e5f\u53ef\u6551\u6cbb\u3002\u674e\u6cbb\u4e0d \u77e5\u5c11\u6797\u5bfa\u5df2\u88ab\u706b\u70e7\uff0c\u66f4\u731c\u60f3\u4e0d\u5230\u51af\u7433\u78b0\u5230\u7684\u5c31\u662f\u5d69\u5c71\u5c11\u6797\u7684\u76d1\u5bfa\uff0c\u800c\u5c11\u6797\u76d1\u5bfa\u4e5f\u65e0\u6cd5\u533b\u6cbb\u3002 \u3000\u3000\u51af\u7433\u89c1\u674e\u6cbb\u5446\u5446\u51fa\u795e\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u4ec0\u4e48\u5440\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u6211\u60f3\u548c\u4f60\u5230\u5d69\u5c71\u53bb\u3002\u54b1\u4eec\u5c31\u662f \u4e0d\u52a0\u5feb\u811a\u7a0b\uff0c\u660e\u5929\u4e5f\u53ef\u5230\u8fbe\u3002\u4e0d\u662f\u6bd4\u53bb\u798f\u5efa\u8386\u7530\u5feb\u5f97\u591a\u5417\uff1f\u201d\u51af\u7433\u62cd\u624b\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u7684\u548c\u6211 \u4e00\u6837\uff0c\u6211\u6b63\u60f3\u5230\u5d69\u5c71\u53bb\u5462\u3002\u5582\uff0c\u54b1\u4eec\u6c42\u5f97\u89e3\u836f\u4e4b\u540e\uff0c\u518d\u7ea6\u5c11\u6797\u5bfa\u50e7\u6bd4\u4e00\u6bd4\u5251\u3002\u201d\u674e\u6cbb\u9053\uff1a \u201c\u5c11\u6797\u5bfa\u662f\u6b66\u6797\u4e2d\u7684\u6cf0\u5c71\u5317\u6597\uff0c\u4f60\u5230\u5c11\u6797\u5bfa\u793c\u62dc\uff0c\u53ef\u4e0d\u80fd\u80e1\u95f9\u3002\u201d\u53c8\u9053\uff1a\u201c\u4f60\u7684\u5e08\u5085\u548c\u5c11\u6797 \u5bfa\u4ee5\u524d\u7684\u4e3b\u6301\u672c\u7a7a\u5927\u5e08\u4e43\u662f\u76f8\u8bc6\u7684\u670b\u53cb\uff0c\u548c\u524d\u4efb\u7684\u76d1\u5bfa\u672c\u65e0\u5927\u5e08\u4e5f\u662f\u77e5\u4ea4\uff0c\u5c11\u6797\u5bfa\u50e7\u4eba\u4e00\u5b9a \u4f1a\u63a5\u5f85\u4f60\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u662f\u4e48\uff1f\u600e\u7684\u6211\u672a\u542c\u5e08\u5085\u63d0\u8fc7\uff1f\u201d\u674e\u6cbb\u7b11\u9053\uff1a\u201c\u6613\u4f2f\u6bcd\u89c1\u4f60\u662f\u4e2a\u5b69 \u5b50\uff0c\u6240\u4ee5\u4ec0\u4e48\u90fd\u4e0d\u544a\u8bc9\u4f60\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u662f\u5440\uff0c\u5979\u5acc\u6211\u591a\u8bdd\uff0c\u6240\u4ee5\u4ec0\u4e48\u4e5f\u4e0d\u544a\u8bc9\u6211\u3002\u201d\u674e\u6cbb\u4e00\u6115\uff0c\u9053\uff1a\u201c\u4f60\u4ee5\u524d\u6587\u9759 \u6781\u5566\uff0c\u73b0\u5728\u5927\u4e86\u624d\u559c\u6b22\u8bf4\u8bdd\u7684\u662f\u4e0d\u662f\uff1f\u4f60\u8fde\u4f60\u5c0f\u65f6\u5019\u662f\u600e\u4e48\u6837\u7684\u90fd\u5fd8\u8bb0\u4e86\uff1f\u201d\u51af\u7433\u4e00\u60ca\uff0c\u5fc3 \u9053\uff1a\u53c8\u78b0\u9489\u4e86\u3002\u53ef\u662f\u5979\u5fc3\u601d\u7075\u654f\uff0c\u9762\u4e0a\u795e\u8272\u81ea\u5982\uff0c\u6beb\u4e0d\u8868\u9732\u51fa\u6765\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u5bf9\u6700\u4eb2\u8fd1 \u7684\u4eba\u5c31\u591a\u8bdd\u5566\uff0c\u5bf9\u4e00\u822c\u7684\u4eba\u8c01\u9ad8\u5174\u591a\u8bf4\uff0c\u201d\u674e\u6cbb\u4e00\u60f3\uff0c\u4e5f\u662f\u9053\u7406\u3002\u51af\u7433\u548c\u6613\u4f2f\u6bcd\u6709\u5982\u6bcd\u5973\uff0c \u5979\u5728\u4f2f\u6bcd\u9762\u524d\u7d6e\u751b\u6492\u5a07\u4e5f\u662f\u5e38\u4e8b\u3002\u51af\u7433\u89c1\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u4e0d\u6562\u591a\u8bf4\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u8bd5\u63a2\u95ee\u9053\uff1a \u201c\u4f60\u8205\u8205\u5462\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4ed6\u6709\u4e8b\u60c5\u56de\u5929\u5c71\u53bb\u4e86\u3002\u201d\u51af\u7433\u5fc3\u4e2d\u4e00\u5bbd\uff0c\u4ed6\u77e5\u9053\u674e\u6cbb\u6027\u60c5\u5fe0\u539a\uff0c \u81ea\u5df1\u5192\u5145\u6613\u5170\u73e0\u7684\u5f92\u5f1f\uff0c\u5b9a\u4e0d\u81f4\u7ed9\u4ed6\u8bc6\u7a7f\u3002 \u3000\u3000\u51af\u7433\u53c8\u518d\u62ab\u4e0a\u7537\u5b50\u8863\u88f3\uff0c\u548c\u674e\u6cbb\u540c\u884c\uff0c\u4e00\u8def\u4e0a\u51af\u7433\u5c3d\u9017\u4ed6\u8bf4\u5929\u5c71\u4e0a\u7684\u4e8b\u60c5\uff0c\u800c\u81ea\u5df1\u5219\u5de7 \u5999\u7684\u907f\u5f00\u4e0d\u77e5\u9053\u7684\u8bdd\u9898\uff0c\u4e0d\u6d88\u534a\u5929\uff0c\u51af\u7433\u5bf9\u5929\u5c71\u4e03\u5251\u7684\u6545\u4e8b\uff0c\u4ed6\u4eec\u4e4b\u95f4\u7684\u5173\u7cfb\u4ee5\u53ca\u6613\u5170\u73e0\u5f92 \u5f1f\u7684\u6027\u683c\u7b49\u7b49\uff0c\u90fd\u5df2\u4e86\u7136\u4e8e\u80f8\u3002 \u3000\u3000\u5d69\u5c71\u662f\u592a\u5ba4\u5c11\u5ba4\u4e24\u5c71\u7684\u603b\u79f0\uff0c\u7b2c\u4e8c\u65e5\u4e24\u4eba\u5230\u4e86\u5c11\u5ba4\u5c71\u5c71\u4e0b\uff0c\u8fd9\u65f6\u8ddd\u706b\u70e7\u5c11\u6797\u5bfa\u5df2\u5c06\u4e00 \u6708\uff0c\u5404\u6d3e\u6b66\u6797\u5b97\u4e3b\u65e0\u4e0d\u77e5\u9053\u6b64\u4e8b\uff0c\u4e25\u7981\u95e8\u5f92\uff0c\u4e0d\u8bb8\u8e0f\u5165\u5d69\u5c71\u5468\u56f4\u4e09\u767e\u91cc\u5185\u3002\u4e5f\u6b63\u662f\u56e0\u6b64\uff0c\u6240 \u4ee5\u5f18\u6cd5\u5927\u5e08\u4e00\u6765\uff0c\u90a3\u5149\u5934\u5c31\u5148\u60f9\u4eba\u6ce8\u610f\uff0c\u9042\u88ab\u97e9\u91cd\u5c71\u63a2\u77e5\uff0c\u800c\u674e\u6cbb\u5219\u56e0\u65e0\u751a\u540d\u6c14\uff0c\u97e9\u91cd\u5c71\u6599 \u522b\u6d3e\u7684\u4eba\u5df2\u4e0d\u6562\u5230\u6765\uff0c\u6240\u4ee5\u5d69\u5c71\u9644\u8fd1\uff0c\u8fd9\u65f6\u5de1\u903b\u53cd\u800c\u8f6c\u677e\uff0c\u624d\u7ed9\u674e\u6cbb\u5077\u5077\u6e9c\u8fdb\u3002\u674e\u6cbb\u4e0e\u51af\u7433 \u9065\u89c1\u6709\u4e00\u5927\u7247\u5c71\u5934\u5149\u79c3\u79c3\u7684\uff0c\u5927\u4e3a\u60ca\u5947\uff0c\u4e24\u4eba\u4e00\u8def\u767b\u5c71\uff0c\u5c71\u4e0b\u9752\u8471\u4e00\u7247\uff0c\u534a\u5c71\u82b1\u6811\u7e41\u8302\uff0c\u4f46 \u4e00\u4e0a\u5230\u5c11\u5ba4\u5c71\u5317\u9e93\u7684\u4e94\u4e73\u5cf0\u4e0b\uff0c\u5374\u53ea\u89c1\u4e00\u7247\u74e6\u783e\uff0c\u548c\u70e7\u7126\u4e86\u7684\u6728\u70ad\u3002 \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u600e\u4e48\u662f\u8fd9\u4e2a\u6837\u5b50\uff0c\u4f60\u4e00\u5b9a\u5e26\u9519\u8def\u4e86\u3002\u8fd9\u91cc\u4e00\u95f4\u623f\u5b50\u90fd\u6ca1\u6709\uff0c\u90a3\u513f\u53bb\u627e\u5c11\u676f \u5bfa\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u5728\u5c11\u5ba4\u5c71\u5317\u9e93\u4e94\u4e73\u5cf0\u4e0b\uff0c\u5929\u4e0b\u8c01\u4eba\u4e0d\u77e5\uff0c\u600e\u4f1a\u8d70\u9519\u3002\u201d\u6307\u7740\u90a3\u5806\u74e6 \u783e\u9053\uff1a\u201c\u4f60\u4e0d\u89c1\u70e7\u7126\u4e86\u7684\u7816\u74e6\uff1f\u5b9a\u662f\u5c71\u4e0a\u5927\u706b\uff0c\u5c11\u6797\u5bfa\u7ed9\u706b\u70e7\u4e86\u3002\u201d\u4e24\u4eba\u751a\u4e3a\u5931\u671b\uff0c\u5c24\u5176\u662f \u674e\u6cbb\u66f4\u89c9\u60b2\u75db\uff0c\u5fc3\u60f3\uff1a\u6551\u6cbb\u745b\u59b9\uff0c\u53ea\u6709\u5bc4\u671b\u4e8e\u5c11\u6797\uff0c\u4e8e\u4eca\u5c11\u6797\u5bfa\u7ed9\u706b\u70e7\u4e86\uff0c\u6559\u6211\u54ea\u91cc\u53bb\u6c42\u7075 \u4e39\u5999\u836f\uff1f \u3000\u3000\u51af\u7433\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u6025\u4e86\uff1f\u5c31\u662f\u6ca1\u6709\u89e3\u836f\uff0c\u6211\u4e5f\u4e0d\u89c1\u5f97\u5c31\u4f1a\u6b7b\u3002\u201d\u51af\u7433\u5df2\u541e\u4e86\u6700\u540e\u4e00\u7c92\u7684 \u5c11\u8fd8\u4e39\uff0c\u53ea\u662f\u5fae\u611f\u624b\u8155\u9178\u9ebb\uff0c\u5176\u4ed6\u5e76\u65e0\u6240\u89c9\u3002\u674e\u6cbb\u8df3\u4e0a\u4e00\u5757\u77f3\u5ca9\uff0c\u7eb5\u6708\u5bdf\u671b\uff0c\u5ffd\u7136\u53eb\u8d77\u6765 \u9053\uff1a\u201c\u90a3\u8fb9\u597d\u50cf\u6709\u4e00\u95f4\u623f\u5b50\uff0c\u54e6\uff0c\u662f\u623f\u5b50\u3002\u54a6\uff0c\u8fd8\u6709\u4e00\u4e2a\u4eba\u3002\u201d\u8dc3\u4e0b\u5ca9\u77f3\uff0c\u8dd1\u524d\u6570\u4e08\uff0c\u6b22\u7136 \u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u4e2a\u548c\u5c1a\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u5c11\u6797\u5bfa\u50e7\uff0c\u54b1\u4eec\u53eb\u4ed6\uff01\u201d\u64ae\u5507\u4e00\u5439\uff0c\u7fa4\u5cf0\u56de \u54cd\uff0c\u90a3\u4eba\u98de\u5954\u800c\u6765\uff0c\u679c\u7136\u662f\u4e2a\u548c\u5c1a\u3002 \u3000\u3000\u674e\u6cbb\u606d\u8eab\u95ee\u9053\uff1a\u201c\u8bf7\u95ee\u5927\u5e08\u6cd5\u8bb3\uff1f\u201d\u90a3\u548c\u5c1a\u9053\uff1a\u201c\u4f60\u4eec\u627e\u8c01\uff1f\u201d\u51af\u7433\u89c1\u8fd9\u548c\u5c1a\u5934\u6234\u7f8a\u89d2 \u5e3d\uff0c\u8eab\u62ab\u9ed1\u8888\u88df\uff0c\u76ee\u5149\u51f6\u6076\uff0c\u624b\u4e2d\u63d0\u7740\u4e00\u628a\u5bd2\u5149\u95ea\u95ea\u7684\u957f\u5251\uff0c\u4e0d\u50cf\u662f\u5c11\u6797\u5bfa\u50e7\u4eba\u670d\u9970\uff0c\u624b\u4e2d \u6697\u6263\u98de\u5200\uff0c\u51b2\u53e3\u7b54\u9053\uff1a\u201c\u4e0d\u627e\u8c01\u3002\u201d\u674e\u6cbb\u5374\u9053\uff1a\u201c\u8bf7\u95ee\u5c11\u6797\u5bfa\u51e0\u65f6\u7ed9\u706b\u70e7\u4e86\uff1f\u65e0\u4f4f\u7985\u5e08\u8fd8\u5728 \u6b64\u4e48\uff1f\u201d\u90a3\u548c\u5c1a\u4e00\u58f0\u72de\u7b11\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1b\u201c\u4f60\u4eec\u627e\u5c11\u6797\u5bfa\u4e3b\u6301\uff1f\u54c8\uff0c\u6765\u5f97\u5de7\u6781\u4e86\uff0c\u6211\u6b63\u8981\u627e\u4f60 \u4eec\uff01\u201d \u3000\u3000\u674e\u6cbb\u6253\u4e86\u4e2a\u7a81\u5140\uff0c\u9053\uff1a\u201c\u5927\u5e08\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u90a3\u548c\u5c1a\u9053\uff1a\u201c\u4ec0\u4e48\u610f\u601d\uff0c\u53eb\u4f60\u53bb\u89c1\u65e0 \u4f4f\u7985\u5e08\uff01\u201d\u957f\u5251\u9aa4\u53d1\uff0c\u547c\u7684\u4e00\u80a1\u52b2\u98ce\u4fbf\u626b\u8fc7\u6765\u3002 \u3000\u3000\u6b64\u4eba\u6b63\u662f\u7559\u5b88\u5d69\u5c71\u7684\u6d77\u4e91\u548c\u5c1a\uff0c\u4ed6\u5728\u5d69\u5c71\u5b88\u4e86\u5c06\u8fd1\u4e00\u6708\uff0c\u4eca\u65e5\u624d\u521d\u89c1\u5916\u4eba\uff0c\u5fc3\u9053\uff1a\u54c8\uff0c \u5230\u5e95\u6709\u4e24\u4e2a\u5c0f\u8d3c\u81ea\u6295\u7f57\u7f51\u3002\u4ed6\u672c\u662f\u5a01\u9707\u5357\u7586\u7684\u5251\u5e08\uff0c\u53ef\u60dc\u8fd0\u9053\u4e0d\u597d\uff0c\u6295\u5230\u5141\u796f\u95e8\u4e0b\u4e4b\u540e\uff0c\u4e00 \u51fa\u5c71\u4fbf\u78b0\u89c1\u5415\u56db\u5a18\uff0c\u88ab\u5415\u56db\u5a18\u5728\u7530\u6a2a\u5c9b\u4e0a\u622a\u65ad\u4ed6\u7684\u5251\uff0c\u81ea\u6b64\u4e0d\u5f97\u91cd\u7528\uff0c\u4e0d\u80fd\u4e0e\u4e86\u56e0\u5929\u53f6\u6563\u4eba \u7b49\u5e76\u5217\uff0c\u53ea\u88ab\u5206\u914d\u5230\u5916\u5e9c\u6b66\u58eb\u4e2d\u53bb\u505a\u4e2a\u6559\u5934\uff0c\u4e5f\u6b63\u56e0\u6b64\uff0c\u4ed6\u672a\u89c1\u8fc7\u51af\u7433\u3002 \u3000\u3000\u674e\u6cbb\u7a81\u906d\u5251\u88ad\uff0c\u51e0\u88ab\u523a\u4f24\uff0c\u5e78\u4ed7\u8f7b\u529f\u7cbe\u5999\uff0c\u5e73\u5730\u62d4\u8d77\u4e08\u4f59\uff0c\u8fd9\u624d\u582a\u582a\u907f\u8fc7\u3002\u6d77\u4e91\u548c\u5c1a\u6325 \u5251\u518d\u523a\uff0c\u51af\u7433\u4e09\u67c4\u98de\u5200\u4e00\u9f50\u51fa\u624b\uff0c\u6d77\u4e91\u548c\u5c1a\u957f\u5251\u4e00\u8f6c\uff0c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u6ef4\u6e9c\u6e9c\u7684\u4e24\u4e2a\u8f6c\u8eab\uff0c \u4e09\u67c4\u98de\u5200\u90fd\u7ed9\u5251\u98ce\u6fc0\u4e0a\u534a\u7a7a\uff0c\u8fdc\u8fdc\u7684\u629b\u4e0b\u5c71\u8c37\u3002\u674e\u6cbb\u53eb\u9053\uff1a\u201c\u745b\u59b9\u4f60\u522b\u52a8\u624b\u3002\u4f60\u518d\u52a8\u624b\uff0c\u6211 \u53ef\u8981\u751f\u6c14\u5566\uff01\u201d\u62d4\u5251\u5728\u624b\uff0c\u4e00\u63d0\u4e00\u7ffb\uff0c\u5c55\u51fa\u767d\u53d1\u9b54\u5973\u7684\u72ec\u95e8\u5251\u6cd5\uff0c\u4e0e\u6d77\u4e91\u548c\u5c1a\u6076\u6597\u8d77\u6765\u3002 \u3000\u3000\u6d77\u4e91\u548c\u5c1a\u672c\u4e0d\u628a\u674e\u6cbb\u653e\u5728\u773c\u4e2d\uff0c\u4e0d\u6599\u5288\u9762\u51e0\u62db\uff0c\u4fbf\u89c9\u6781\u96be\u5316\u89e3\u3002\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u6562\u5927\u610f\uff0c \u957f\u5251\u547c\u547c\u98ce\u54cd\uff0c\u5251\u5149\u7b3c\u7f69\u5168\u8eab\uff0c\u674e\u6cbb\u5251\u6cd5\u867d\u7136\u5947\u8be1\u7edd\u4f26\uff0c\u5374\u4e5f\u653b\u4e0d\u8fdb\u53bb\u3002\u4e24\u4eba\u8f6c\u77ac\u4e4b\u95f4\uff0c\u62fc \u4e86\u4e09\u4e94\u5341\u62db\uff0c\u6d77\u4e91\u548c\u5c1a\u5148\u662f\u4ee5\u5b88\u4e3a\u653b\uff0c\u540e\u662f\u4ee5\u653b\u5bf9\u653b\uff0c\u53cc\u65b9\u65a4\u4e24\u6089\u654c\uff0c\u6740\u5f97\u4e2a\u96be\u5206\u96be\u89e3\u3002\u51af \u7433\u5728\u65c1\u770b\u5f97\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u53ea\u662f\u5bb3\u6015\u674e\u6cbb\u771f\u7684\u751f\u6c14\uff0c\u4e0d\u6562\u4e0a\u524d\u3002\u672c\u6765\u4ee5\u51af\u7433\u7684\u6027\u5b50\uff0c\u5f88\u5c11\u542c\u4eba\u7684 \u8bdd\u3002\u53ea\u56e0\u674e\u6cbb\u5bf9\u5979\u5341\u5206\u7231\u62a4\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u4e2d\uff0c\u4ee4\u5979\u6f5c\u79fb\u9ed8\u5316\uff0c\u4ee4\u5979\u89c9\u5f97\u4e0d\u597d\u610f\u601d\u4e0d\u542c\u674e\u6cbb\u7684 \u8bdd\u3002 \u3000\u3000\u6d77\u4e91\u548c\u5c1a\u62a2\u653b\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u4ecd\u7136\u5360\u4e0d\u4e86\u534a\u70b9\u4fbf\u5b9c\uff0c\u53ea\u89c9\u654c\u4eba\u5251\u6cd5\u5947\u8be1\u4e4b\u6781\uff0c\u65e0\u53ef\u6349\u6478\uff1b \u672c\u6765\u4ed6\u60f3\u72ec\u81ea\u9886\u529f\uff0c\u800c\u4eca\u5374\u60e7\u6015\u51af\u7433\u4e0a\u6765\u5939\u51fb\u4e86\u3002\u674e\u6cbb\u5fc3\u4e2d\u4e5f\u6697\u6697\u5403\u60ca\uff1a\u654c\u4eba\u5251\u6cd5\u75be\u5982\u96f7 \u9706\uff0c\u6bcf\u4e00\u5251\u90fd\u4f3c\u6709\u5343\u94a9\u4e4b\u529b\uff0c\u4e45\u6218\u4e0b\u53bb\uff0c\u53ea\u6015\u5403\u4e8f\u3002\u6b63\u60f3\u53ca\u65f6\u6446\u8131\uff0c\u5ffd\u542c\u5f97\u654c\u4eba\u8fde\u8fde\u957f\u5578\uff0c \u60f3\u662f\u62db\u547c\u540c\u4f34\uff0c\u5fc3\u4e2d\u8d8a\u6025\uff0c\u5237\u7684\u4e00\u5251\uff0c\u9661\u7136\u523a\u53bb\u3002\u6d77\u4e91\u548c\u5c1a\u89c1\u654c\u4eba\u5251\u5c16\u6643\u52a8\uff0c\u4f3c\u662f\u523a\u5411\u4e0a \u76d8\uff0c\u53c8\u4f3c\u4e0b\u523a\u819d\u76d6\uff0c\u9000\u4e86\u4e00\u6b65\uff0c\u5de6\u638c\u6a2a\u62e8\uff0c\u53f3\u5251\u5e73\u80f8\uff0c\u517c\u987e\u4e0a\u4e0b\u5de6\u53f3\uff0c\u4e0d\u8bba\u5bf9\u65b9\u5982\u4f55\u53d8\u62db\uff0c \u90fd\u80fd\u5bf9\u4ed8\u5f97\u4e86\u3002\u54ea\u6599\u674e\u6cbb\u5251\u950b\u4e00\u98a4\uff0c\u6539\u5411\u4e2d\u76d8\uff0c\u75be\u523a\u4ed6\u7684\u201c\u7b11\u8170\u7a74\u201d\u3002\u6d77\u4e91\u548c\u5c1a\u731d\u4e0d\u53ca\u9632\uff0c \u88ab\u5251\u5c16\u70b9\u4e86\u4e00\u4e0b\uff0c\u53c8\u9ebb\u53c8\u75d2\uff0c\u767b\u65f6\u72c2\u7b11\u8d77\u6765\u3002\u674e\u6cbb\u5927\u559c\uff0c\u53cd\u8eab\u62c9\u4e86\u51af\u7433\uff0c\u98de\u9003\u4e0b\u5c71\u3002 \u3000\u3000\u672a\u5230\u5c71\u8170\uff0c\u6d77\u4e91\u548c\u5c1a\u7684\u526f\u624b\uff0c\u5fa1\u6797\u519b\u7edf\u9886\u79e6\u4e2d\u8d8a\u5df2\u7ecf\u8d76\u5230\uff0c\u5224\u5b98\u7b14\u5de6\u53f3\u5206\u5c55\uff0c\u5728\u5c71\u9053\u8981 \u9698\u4e4b\u5904\uff0c\u622a\u4f4f\u53bb\u8def\u3002\u7559\u5b88\u5d69\u5c71\u7684\u536b\u58eb\uff0c\u8d77\u521d\u672c\u6765\u5f88\u591a\uff0c\u540e\u89c1\u65e5\u4e45\u65e0\u4e8b\uff0c\u800c\u5141\u796f\u53c8\u6b63\u6709\u4e8b\u4e8e\u4ed6 \u65b9\uff0c\u6e10\u6e10\u51cf\u7f29\uff0c\u6700\u540e\u53ea\u7559\u4e0b\u4e86\u6d77\u4e91\u548c\u5c1a\u4e0e\u79e6\u4e2d\u8d8a\u4e8c\u4eba\u3002\u79e6\u4e2d\u8d8a\u7684\u6b66\u529f\u867d\u7136\u8fd8\u6bd4\u4e0d\u4e0a\u6d77\u4e91\u548c \u5c1a\uff0c\u4f46\u4ed6\u7684\u5224\u5b98\u7b14\u6253\u7a74\u62db\u6570\uff0c\u4e5f\u9887\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u674e\u6cbb\u60f3\u5728\u7247\u523b\u4e4b\u95f4\u5c06\u4ed6\u6253\u9000\uff0c\u5374\u662f\u4e0d\u80fd\u3002 \u3000\u3000\u6597\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u731b\u542c\u5f97\u6d77\u4e91\u548c\u5c1a\uff0c\u8fde\u58f0\u6012\u543c\uff0c\u5982\u98de\u9000\u4e0b\u3002\u674e\u6cbb\u5fc3\u4e2d\u51db\u7136\uff0c\u7b11\u8170\u7a74\u4e43\u4eba\u8eab \u9ebb\u7a74\u4e4b\u4e00\uff0c\u5728\u8f6f\u8170\u808b\u9aa8\u672b\u7aef\uff0c\u9002\u5f53\u80be\u810f\u4f4d\u7f6e\uff0c\u5982\u88ab\u70b9\u4e2d\uff0c\u4fbf\u4f1a\u72c2\u7b11\u4e0d\u4f11\uff0c\u7ef5\u8f6f\u65e0\u529b\uff0c\u90a3\u77e5\u6d77 \u4e91\u548c\u5c1a\u53ea\u7b11\u4e86\u4e00\u9635\uff0c\u4fbf\u5c45\u7136\u80fd\u4e3e\u6b65\u5982\u98de\uff0c\u8fd9\u529f\u529b\u771f\u662f\u975e\u540c\u5c0f\u53ef\uff01\u51af\u7433\u62d4\u51fa\u77ed\u5251\uff0c\u624b\u6263\u98de\u5200\uff0c \u674e\u6cbb\u9053\uff1a\u201c\u4f60\u7ad9\u5230\u90a3\u4e00\u8fb9\u53bb\uff0c\u6211\u82e5\u4e0d\u654c\uff0c\u4f60\u53ef\u5148\u9003\u4e0b\u5c71\u3002\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u6d77\u4e91\u548c\u5c1a\u5df2\u7136\u8d76\u5230\u3002 \u3000\u3000\u674e\u6cbb\u80cc\u8179\u53d7\u654c\uff0c\u5f62\u52bf\u987f\u53d8\uff0c\u8d70\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u62db\u6570\u6e10\u4e3a\u654c\u56f0\uff0c\u6d77\u4e91\u548c\u5c1a\u957f\u5251\u4e00\u7ede\u4e00\u65cb\uff0c\u5f53 \u7684\u4e00\u58f0\uff0c\u642d\u4f4f\u4e86\u674e\u6cbb\u7684\u5251\u8eab\uff0c\u674e\u6cbb\u51f6\u8fa3\u7684\u5251\u62db\u65bd\u5c55\u4e0d\u5f00\uff0c\u79e6\u4e2d\u8d8a\u7684\u5224\u5b98\u7b14\u5de6\u53f3\u4e00\u5206\uff0c\u201c\u53cc\u98ce \u8d2f\u8033\u201d\uff0c\u5de6\u7b14\u865a\u6307\u9762\u95e8\uff0c\u53f3\u7b14\u76f4\u624e\u80f8\u9645\u7684\u201c\u7384\u673a\u7a74\u201d\uff0c\u8fd9\u4e00\u62db\u674e\u6cbb\u4e07\u96be\u62b5\u6321\uff01 \u3000\u3000\u51af\u7433\u95ea\u8fc7\u4e00\u8fb9\uff0c\u5374\u5e76\u4e0d\u8fdc\uff0c\u8fd9\u65f6\u518d\u4e5f\u4e0d\u7406\u674e\u6cbb\u7684\u8bdd\uff0c\u7389\u624b\u4e00\u626c\uff0c\u98de\u5200\u75be\u5c04\uff0c\u79e6\u4e2d\u8d8a\u626d\u8eab \u95ea\u907f\uff0c\u5931\u4e86\u51c6\u5934\uff0c\u5224\u5b98\u7b14\u5f85\u518d\u70b9\u65f6\uff0c\u5df2\u88ab\u51af\u7433\u4e00\u53e3\u98de\u5200\uff0c\u5c06\u4ed6\u7684\u5224\u5b98\u7b14\u6253\u5f97\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u79e6\u4e2d \u8d8a\u659c\u8dc3\u95ea\u907f\uff0c\u674e\u6cbb\u957f\u5251\u5411\u524d\u4e00\u63a2\uff0c\u89e3\u4e86\u6d77\u4e91\u548c\u5c1a\u7684\u62db\u6570\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u745b\u59b9\uff0c\u5feb\u8d70\u5f00\uff0c\u4e0d\u51c6 \u4e0a\u6765\uff01\u201d\u51af\u7433\u7b11\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u7406\u4f60\u7684\u8bdd\u5566\u3002\u4f60\u522b\u751f\u6c14\uff0c\u4e00\u751f\u6c14\u4f60\u5c31\u4e0d\u591f\u8fd9\u79c3\u9a74\u6253\u4e86\uff0c\u6162\u6162\u751f \u6c14\u4e0d\u8fdf\u3002\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u4e1d\u6beb\u4e0d\u7f13\uff0c\u77ed\u5251\u6a2a\u6325\uff0c\u5df2\u548c\u79e6\u4e2d\u8d8a\u6740\u5728\u4e00\u8d77\u3002 \u3000\u3000\u51af\u7433\u6b66\u529f\u6781\u6742\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u8fde\u6362\u4e86\u4e94\u516d\u79cd\u62db\u6570\uff0c\u79e6\u4e2d\u8d8a\u6b66\u529f\u53ea\u80fd\u7b97\u662f\u4e8c\u6d41\u597d\u624b\uff0c\u7ed9\u5979\u53d8 \u5e7b\u65e0\u5e38\u7684\u62db\u6570\uff0c\u6740\u5f97\u5934\u6655\u76ee\u7729\uff0c\u4e09\u4e94\u5341\u62db\u8fc7\u540e\uff0c\u7adf\u7136\u53ea\u6709\u62db\u67b6\u4e4b\u529f\u3002\u674e\u6cbb\u8fde\u8fde\u53eb\u558a\uff0c\u51af\u7433\u53ea \u662f\u4e0d\u7406\u3002\u6597\u5230\u5206\u9645\uff0c\u79e6\u4e2d\u8d8a\u6b32\u56fe\u8d25\u4e2d\u6c42\u80dc\uff0c\u884c\u9669\u4fa5\u5e78\uff0c\u5237\u7684\u4e00\u4e2a\u7bad\u6b65\uff0c\u98de\u7a9c\u8fc7\u6765\uff0c\u53cc\u7b14\u4e00 \u8d77\uff0c\u53cc\u70b9\u51af\u7433\u80cc\u5fc3\u7684\u547d\u95e8\u8981\u7a74\u3002\u51af\u7433\u8f7b\u529f\u6bd4\u4ed6\u9ad8\u660e\uff0c\u53c8\u7cbe\u542c\u98ce\u8fa8\u5668\u4e4b\u672f\uff0c\u79e6\u4e2d\u8d8a\u98de\u8eab\u7a9c\u8d77\uff0c \u5979\u5df2\u6709\u6240\u51c6\u5907\uff0c\u5bb9\u5f97\u79e6\u4e2d\u8d8a\u7684\u5224\u5b98\u7b14\u582a\u582a\u70b9\u5230\u80cc\u540e\uff0c\u51af\u7433\u8db3\u5c16\u4e00\u70b9\uff0c\u534a\u8eab\u4e00\u8f6c\uff0c\u8eab\u5f62\u659c\u98de\uff0c \u5251\u950b\u5708\u540e\uff0c\u59ff\u52bf\u7f8e\u5999\u4e4b\u6781\uff0c\u79e6\u4e2d\u8d8a\u7684\u53cc\u7b14\u521a\u597d\u51d1\u4e0a\u5979\u7684\u5251\u950b\uff0c\u7ed9\u5979\u4e00\u5708\u4e00\u65cb\uff0c\u501f\u529b\u6253\u529b\uff0c\u53ee \u5f53\u4e24\u58f0\uff0c\u4e24\u679d\u5224\u5b98\u7b14\u90fd\u7ed9\u77ed\u5251\u7ede\u51fa\u624b\u53bb\u3002\u51af\u7433\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5939\u653b\u6211\u7684\u674e\u6cbb\u54e5\u54e5\uff0c\u9976\u4f60\u4e0d \u5f97\uff01\u201d\u56de\u8eab\u4e00\u5251\uff0c\u628a\u79e6\u4e2d\u8d8a\u4e94\u6307\u524a\u65ad\uff0c\u79e6\u4e2d\u8d8a\u60e8\u53eb\u4e00\u58f0\uff0c\u4ece\u534a\u5c71\u8170\u76f4\u6eda\u4e0b\u53bb\u3002 \u3000\u3000\u6d77\u4e91\u548c\u5c1a\u548c\u674e\u6cbb\u5404\u6709\u64c5\u957f\uff0c\u672c\u6765\u8c01\u4e5f\u80dc\u4e0d\u4e86\u8c01\u3002\u53ea\u662f\u6d77\u4e91\u521a\u624d\u88ab\u5251\u5c16\u70b9\u4e86\u7b11\u8170\u7a74\uff0c\u867d\u7136 \u4ed7\u7740\u529f\u529b\u6df1\u539a\uff0c\u8fc5\u5373\u65e0\u4e8b\uff0c\u53ef\u662f\u6c14\u529b\u5230\u5e95\u51cf\u5f31\u4e86\u4e9b\uff0c\u672c\u5c31\u88ab\u674e\u6cbb\u7684\u5251\u6cd5\u538b\u5f97\u5904\u5728\u4e0b\u98ce\uff0c\u800c\u4eca \u4e00\u89c1\u79e6\u4e2d\u8d8a\u4e27\u547d\u8352\u8c37\uff0c\u66f4\u662f\u80c6\u602f\uff0c\u957f\u5251\u4e00\u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\uff0c\u660e\u662f\u62a2\u653b\uff0c\u5b9e\u662f\u9000\u5374\u3002 \u3000\u3000\u51af\u7433\u53eb\u9053\uff1a\u201c\u80d6\u548c\u5c1a\uff0c\u4f60\u8fd8\u7b11\u5f97\u51fa\u5417\uff1f\u201d\u4e00\u6296\u624b\u53c8\u662f\u4e09\u53e3\u98de\u5200\uff0c\u6d77\u4e91\u548c\u5c1a\u5251\u5df2\u653b\u51fa\uff0c\u65e0 \u6cd5\u56de\u9632\uff0c\u5de6\u638c\u8fd0\u638c\u6210\u98ce\uff0c\u9707\u6fc0\u98de\u5200\uff0c\u53ef\u662f\u5982\u6b64\u4e00\u6765\uff0c\u4f7f\u5251\u7684\u529b\u9053\u51cf\u534a\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u674e \u6cbb\u4e00\u5251\u4e0b\u6233\uff0c\u6d77\u4e91\u548c\u5c1a\u817f\u5f2f\u5173\u8282\u4e4b\u5904\uff0c\u5df2\u88ab\u5251\u5c16\u6311\u65ad\u7b4b\u9aa8\uff0c\u6d77\u4e91\u548c\u5c1a\u4e5f\u771f\u4e86\u5f97\uff0c\u5728\u5730\u4e0a\u4e00\u4e2a \u6253\u6eda\uff0c\u7adf\u7136\u548c\u8863\u6eda\u4e0b\u5c71\u5761\u3002 \u3000\u3000\u51af\u7433\u683c\u683c\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u8fd9\u50cf\u4ec0\u4e48\uff1f\u8fd9\u50cf\u997f\u72d7\u6251\u5c4e\u3002\u201d\u7b11\u58f0\u672a\u505c\uff0c\u5ffd\u7136\u4e00\u8de4\u6251\u5730\u3002\u539f\u6765\u5979 \u5728\u5267\u6218\u4e4b\u540e\uff0c\u767d\u7709\u9488\u53c8\u5411\u4e0a\u5347\uff0c\u6bd2\u6027\u53d1\u4f5c\uff0c\u867d\u6709\u5c11\u8fd8\u4e39\uff0c\u4e5f\u538b\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u674e\u6cbb\u5927\u60ca\uff0c\u4fef\u8eab\u4e00\u770b\uff0c\u89c1\u5979\u9762\u5982\u91d1\u7eb8\uff0c\u53eb\u4e86\u51e0\u58f0\uff0c\u4e0d\u89c1\u56de\u5e94\u3002\u674e\u6cbb\u548c\u51af\u745b\u672c\u662f\u9752\u6885\u7af9\u9a6c \u4e4b\u4ea4\uff0c\u8fd9\u65f6\u66f4\u4e0d\u628a\u7537\u5973\u4e4b\u5acc\u653e\u5728\u5fc3\u4e0a\uff0c\u4f38\u624b\u5728\u51af\u7433\u80f8\u53e3\u4e00\u63a2\uff0c\u53ea\u89c9\u6e29\u8f6f\u8f6f\u7684\uff0c\u663e\u7136\u672a\u66fe\u6c14 \u7edd\uff0c\u53ea\u662f\u7528\u529b\u8fc7\u5ea6\uff0c\u4e00\u65f6\u6655\u4e86\u8fc7\u53bb\u3002\u674e\u6cbb\u60f3\u8d77\u8205\u8205\u6559\u4ed6\u7684\u6025\u6551\u65b9\u6cd5\uff0c\u7528\u624b\u6307\u63a7\u5f00\u51af\u7433\u7684\u6a31\u6843 \u5c0f\u53e3\uff0c\u7528\u5634\u5df4\u7ed9\u5979\u63a5\u6c14\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u51af\u7433\u60a0\u60a0\u9192\u8f6c\uff0c\u8fd9\u65f6\u674e\u6cbb\u65b9\u89c9\u8f6f\u7389\u6e29\u9999\uff0c\u4e0d\u89c9\u5fc3\u795e\u4e00 \u8361\uff0c\u6025\u5fd9\u628a\u5634\u79fb\u5f00\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u600e\u4e48\u5566\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u53e3\u6e34\u5f97\u7d27\u3002\u201d\u674e\u6cbb\u77e5\u662f\u6bd2\u6027\u53d1 \u4f5c\uff0c\u9053\uff1a\u201c\u6211\u548c\u4f60\u627e\u4e00\u4e2a\u5730\u65b9\u6b47\u606f\uff01\u62b1\u8d77\u51af\u7433\uff0c\u5728\u5c71\u62d7\u7a81\u51fa\u6765\u7684\u5927\u77f3\u5ca9\u80cc\u540e\uff0c\u627e\u5230\u4e86\u4e00\u4e2a\u5c71 \u6d1e\uff0c\u674e\u6cbb\u5c06\u5979\u62b1\u8fdb\u6d1e\u4e2d\uff0c\u628a\u81ea\u5df1\u7684\u4e0a\u8863\u8131\u4e0b\uff0c\u94fa\u5728\u5730\u4e0a\uff0c\u5c06\u51af\u7433\u8f7b\u8f7b\u653e\u4e0b\uff0c\u6d1e\u4e2d\u7684\u77f3\u949f\u4e73\u6ef4 \u4e0b\u6c34\u73e0\uff0c\u674e\u6cbb\u7528\u624b\u638c\u79ef\u4e86\u4e00\u4e9b\u6c34\uff0c\u704c\u51af\u7433\u996e\u4e86\u3002\u51af\u7433\u795e\u667a\u7a0d\u6e05\uff0c\u9053\uff1a\u201c\u4f60\u8d70\u5427\uff0c\u4e0d\u7528\u7406\u6211 \u4e86\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u745b\u59b9\uff0c\u4f60\u653e\u5fc3\uff0c\u4f60\u4f1a\u597d\u7684\u3002\u6211\u5728\u8fd9\u91cc\u966a\u7740\u4f60\u5462\uff1f\u201d\u51af\u7433\u5583\u5583\u8bf4\u9053\uff1a\u201c\u6211\u4e0d \u597d\uff0c\u6211\u4e0d\u966a\u4f60\u3002\u4f60\u597d\uff0c\u4f60\u966a\u6211\u3002\u201d\u674e\u6cbb\u77e5\u5979\u60f3\u8d77\u4ee5\u524d\u5979\u5728\u81ea\u5df1\u53d7\u4f24\u4e4b\u9645\uff0c\u5f03\u4ed6\u800c\u53bb\u7684\u4e8b\uff1b \u9053\uff1a\u201c\u4f60\u522b\u80e1\u601d\u4e71\u60f3\u5566\uff0c\u6211\u7ed9\u4f60\u627e\u679c\u5b50\u5403\u3002\u201d\u51af\u7433\u53c8\u54fa\u54fa\u8bf4\u9053\uff1a\u201c\u6211\u9a97\u4f60\uff0c\u6211\u6709\u8bdd\u8981\u5bf9\u4f60 \u8bf4\uff01\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5c31\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u674e\u6cbb\u675f\u624b\u65e0\u7b56\uff0c\u8f7b\u8f7b\u7ed9\u5979\u629a\u62cd\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u51af\u7433\u53c8\u9192\u4e86\u8fc7\u6765\uff0c\u5634\u5df4\u5f20\u5f00\uff0c\u5934\u9885\u534a\u4ef0\uff0c\u4f3c \u4e4e\u662f\u60f3\u8bf4\u8bdd\uff0c\u674e\u6cbb\u9053\uff1a\u201c\u745b\u59b9\uff0c\u4f60\u522b\u8bf4\u8bdd\u5566\uff0c\u597d\u597d\u513f\u517b\u795e\u5427\u3002\u201d\u628a\u653e\u5728\u65c1\u8fb9\u7684\u51af\u7433\u80cc\u5305\u53d6 \u6765\uff0c\u60f3\u7ed9\u5979\u5f53\u4f5c\u6795\u5934\uff0c\u597d\u8ba9\u5979\u7761\u5f97\u8212\u670d\u4e00\u70b9\u3002\u5ffd\u7136\u624b\u6709\u6240\u89e6\uff0c\u80cc\u5305\u88f9\u6709\u4e00\u5377\u539a\u539a\u7684\u4e1c\u897f\uff0c\u674e \u51b6\u53d6\u51fa\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u4e00\u672c\u4e66\uff0c\u5c01\u9762\u5199\u7740\u201c\u91d1\u9488\u5ea6\u4e16\u201d\u56db\u5b57\u3002 \u3000\u3000\u674e\u6cbb\u53c8\u60ca\u53c8\u559c\uff0c\u4ed6\u542c\u6bcd\u4eb2\u8bf4\u8fc7\uff0c\u300a\u91d1\u9488\u5ea6\u4e16\u300b\u4e00\u4e66\uff0c\u4e43\u662f\u5085\u9752\u4e3b\u4e00\u751f\u7684\u5fc3\u8840\u4e4b\u4f5c\u3002\u4e0a\u5377 \u7684\u62f3\u7ecf\u5251\u8bc0\u8fd8\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u4e0b\u5377\u7684\u533b\u4e66\u5374\u662f\u7a00\u4e16\u4e4b\u5b9d\u3002\u5374\u4e0d\u77e5\u600e\u7684\u4f1a\u5230\u4e86\u201c\u51af\u745b\u201d\u624b\u4e2d\uff1f\u518d \u60f3\u4e00\u60f3\uff0c\u8bb0\u8d77\u6613\u5170\u73e0\u66fe\u5bf9\u6bcd\u4eb2\u8bf4\u8fc7\u949f\u4e07\u5802\u60e8\u6b7b\u4e4b\u4e8b\uff0c\u8bf4\u662f\u8fd9\u672c\u4e66\u4e00\u5b9a\u5728\u949f\u4e07\u5802\u4e4b\u624b\uff0c\u53ef\u60dc\u5f53 \u65f6\u5fd8\u8bb0\u641c\u5bfb\u3002\u674e\u6cbb\u5fc3\u60f3\uff1a\u53ef\u80fd\u662f\u6613\u4f2f\u6bcd\u53eb\u51af\u745b\u53bb\u53d6\u7684\u4e86\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u6211\u672c\u4e0d\u5e94\u5077\u770b\u6b64\u4e66\uff0c\u4f46 \u4e66\u4e2d\u53ef\u80fd\u6709\u89e3\u4e03\u715e\u767d\u7709\u9488\u7684\u533b\u6cd5\uff0c\u5982\u4eca\u5904\u5728\u7edd\u5883\uff0c\u65e0\u6cd5\u53ef\u65bd\uff0c\u53ea\u597d\u5148\u770b\u4e86\u5b83\uff0c\u4e07\u4e00\u4e0a\u5929\u4e4b \u5e78\uff0c\u5f85\u745b\u59b9\u597d\u8f6c\u4e4b\u540e\uff0c\u518d\u5411\u5979\u544a\u7f6a\u3002 \u3000\u3000\u674e\u6cbb\u628a\u4e0b\u5377\u533b\u4e66\u90e8\u5206\uff0c\u4e00\u9875\u9875\u7684\u7ffb\u9605\u4e0b\u53bb\uff0c\u770b\u4e86\u5341\u591a\u5eff\u9875\uff0c\u5ffd\u89c1\u6709\u4e00\u9875\u5199\u7740\u201c\u79bb\u9b42\u75c7\u533b \u6848\u201d\u3002\u674e\u6cbb\u5fc3\u9053\uff1a\u201c\u8fd9\u4e2a\u540d\u79f0\u597d\u602a\uff01\u201d\u4f46\u4e3a\u4e86\u6025\u4e8e\u627e\u5bfb\u6240\u9700\u7684\u836f\u65b9\uff0c\u8df3\u8fc7\u4e0d\u770b\uff0c\u53c8\u7ffb\u4e86\u5341\u591a \u9875\uff0c\u5ffd\u89c1\u5176\u4e2d\u4e00\u9875\u5199\u9053\uff1a\u201c\u89e3\u6cbb\u98de\u9488\u788e\u7247\u7b49\u90aa\u6bd2\u4e13\u7f16\u3002\u201d\u674e\u6cbb\u5927\u559c\uff0c\u51d1\u5230\u6d1e\u53e3\u4eae\u5149\u4e4b\u5904\uff0c\u7ec6 \u7ec6\u9605\u8bfb\u3002\u51af\u7433\u5ffd\u7136\u4e00\u4e2a\u7ffb\u8eab\uff0c\u9053\uff1a\u201c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u6211\u770b\u4f60\u90a3\u672c\u4e66\u3002\u201d\u51af\u7433\u5ffd\u53c8\u5583 \u5583\u8bf4\u9053\uff1a\u201c\u79bb\u9b42\u75c7\uff0c\u79bb\u9b42\u75c7\uff0c\u4f60\u770b\u5f97\u61c2\u5417\uff1f\u201d \u3000\u3000\u674e\u6cbb\u5fc3\u4e2d\u4e00\u52a8\uff0c\u770b\u51af\u7433\u65f6\uff0c\u51af\u7433\u5df2\u778c\u4e86\u53cc\u773c\uff0c\u8f6c\u4e86\u4e2a\u8eab\uff0c\u53c8\u7761\u53bb\u4e86\u3002\u674e\u6cbb\u5fc3\u9053\uff1a\u4e3a\u4ec0\u4e48 \u5979\u5c3d\u6302\u7740\u79bb\u9b42\u75c7\uff0c\u54a6\uff0c\u5979\u7684\u6027\u60c5\u548c\u5728\u5929\u5c71\u4e4b\u65f6\u5f88\u4e0d\u76f8\u540c\uff0c\u8bb8\u591a\u513f\u65f6\u7684\u4e8b\u4e5f\u4e0d\u8bb0\u5f97\uff0c\u83ab\u975e\u4e5f\u60a3 \u4e86\u79bb\u9b42\u75c7\u4e86\uff1f\u4e14\u522b\u7406\u5979\uff0c\u5148\u628a\u8fd9\u89e3\u6bd2\u4e13\u7f16\u770b\u4e86\u518d\u8bf4\u3002 \u3000\u3000\u674e\u6cbb\u56e0\u4e3a\u8205\u8205\u6b66\u6210\u5316\u901a\u6653\u533b\u7406\uff0c\u4ed6\u4e5f\u9887\u6709\u70b9\u533b\u5b66\u5e38\u8bc6\uff0c\u8bfb\u8d77\u6765\u6bd4\u51af\u7433\u5bb9\u6613\u5f97\u591a\u3002\u770b\u4e86\u4e00 \u904d\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u539f\u6765\u89e3\u4e03\u715e\u767d\u7709\u9488\u4e4b\u6cd5\uff0c\u548c\u89e3\u5176\u4ed6\u9488\u6bd2\u4e4b\u6cd5\u4e00\u6837\uff0c\u5e76\u4e0d\u9700\u8981\u7279\u522b\u89e3\u836f\uff0c\u53ef\u4ee5 \u7528\u5085\u9752\u4e3b\u72ec\u521b\u7684\u9488\u7078\u4e4b\u6cd5\u6cbb\u7597\u3002\u5148\u7528\u94f6\u9488\u523a\u6709\u5173\u7a74\u9053\uff0c\u4eba\u4f53\u672c\u8eab\u7684\u62b5\u6297\u529b\u4f1a\u56e0\u6b64\u800c\u53d7\u6fc0\u53d1\uff0c \u53ef\u4eca\u6bd2\u6027\u6e10\u6e10\u51cf\u5f31\uff0c\u7136\u540e\u7528\u5df4\u8c46\u4ee4\u75c5\u4eba\u5927\u6cfb\uff0c\u4f59\u6bd2\u4ece\u7caa\u4fbf\u4e2d\u6392\u51fa\uff0c\u94f6\u9488\u7ec6\u5c0f\uff0c\u7eb5\u7559\u5728\u4f53\u5185\uff0c \u53d6\u5b83\u4e0d\u51fa\uff0c\u4e5f\u65e0\u8db3\u4e3a\u5bb3\u4e86\u3002 \u3000\u3000\u674e\u6cbb\u559c\u5b5c\u5b5c\u7684\u5c06\u4e66\u653e\u597d\uff0c\u84e6\u7136\u60f3\u8d77\u5730\u5904\u8352\u6548\uff0c\u65e2\u65e0\u53ef\u4f9b\u9488\u7078\u4e4b\u7528\u7684\u94f6\u9488\uff0c\u53c8\u65e0\u5df4\u8c46\u53ef \u4e70\uff0c\u5982\u4f55\u662f\u597d\u3002\u60f3\u4e86\u4e00\u60f3\uff0c\u4ece\u6d1e\u53e3\u671b\u53bb\uff0c\u6ee1\u5c71\u4fee\u7af9\uff0c\u60f3\u9053\uff1a\u201c\u6211\u524a\u7af9\u4e3a\u9488\uff0c\u5927\u7ea6\u4e5f\u4f1a\u6709\u6548\u3002 \u81f3\u4e8e\u5df4\u8c46\uff0c\u867d\u7136\u65e0\u5904\u53ef\u4e70\uff0c\u4f46\u5374\u53ef\u7528\u522b\u7269\u66ff\u4ee3\uff0c\u603b\u4e4b\u80fd\u4ee4\u75c5\u4eba\u5927\u6cfb\u4e00\u573a\u4fbf\u884c\u3002\u8bb0\u8d77\u6709\u4e00\u79cd\u5c71 \u679c\uff0c\u5728\u534a\u751f\u672a\u719f\u4e4b\u65f6\u91c7\u4e0b\uff0c\u4e00\u5403\u4e4b\u540e\uff0c\u4fbf\u4f1a\u8179\u6cfb\uff0c\u60f3\u5fc5\u4e5f\u53ef\u66ff\u4ee3\u5df4\u8c46\uff1f\u4ed6\u5728\u65e0\u671b\u4e4b\u9645\uff0c\u627e\u5230 \u5e0c\u671b\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u90fd\u8981\u4e00\u8bd5\u4e86\u3002 \u3000\u3000\u674e\u6cbb\u770b\u51af\u7433\u5df2\u7ecf\u719f\u7761\uff0c\u518d\u8131\u4e0b\u4e00\u4ef6\u8863\u88f3\uff0c\u7ed9\u5979\u76d6\u4e0a\uff0c\u4fbf\u51fa\u6d1e\u53bb\u627e\u5c71\u679c\uff0c\u8d70\u51fa\u5c71\u53e3\uff0c\u4fef\u89c6 \u5c71\u8c37\uff0c\u5ffd\u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u4e00\u62d0\u4e00\u62d0\u7684\u8d70\u5f97\u975e\u5e38\u7f13\u6162\uff0c\u8d70\u4e24\u6b65\u505c\u4e00\u6b65\uff0c\u8fd8\u4e0d\u65f6\u53d1\u51fa\u547b\u541f\u4e4b\u58f0\uff0c\u7ec6 \u770b\u4e4b\u4e0b\uff0c\u6b63\u662f\u88ab\u81ea\u5df1\u523a\u4f24\u7684\u90a3\u4e2a\u548c\u5c1a\u3002\u5fc3\u9053\uff1a\u6664\uff0c\u539f\u6765\u8fd9\u53ae\u5c45\u7136\u672a\u6b7b\u3002\u674e\u6cbb\u65e0\u6687\u7406\u4ed6\uff0c\u81ea\u53bb \u627e\u5bfb\u90a3\u79cd\u5c71\u679c\uff0c\u627e\u4e86\u534a\u5929\uff0c\u5c45\u7136\u7ed9\u4ed6\u627e\u5230\u3002\u674e\u6cbb\u62e3\u90a3\u534a\u751f\u672a\u719f\u7684\u6458\u4e86\u4e94\u516d\u4e2a\uff0c\u7136\u540e\u65a9\u4e0b\u7af9 \u679d\uff0c\u524a\u6210\u5229\u9488\uff0c\u56de\u8f6c\u5c71\u6d1e\u3002 \u3000\u3000\u5c71\u6d1e\u4e2d\u51af\u7433\u5df2\u9192\uff0c\u4e0d\u89c1\u674e\u6cbb\uff0c\u6b63\u60f3\u9053\uff1a\u6664\uff0c\u4ed6\u5f03\u6211\u800c\u53bb\uff0c\u4e5f\u662f\u5e94\u8be5\u3002\u4e00\u4f1a\u513f\uff0c\u674e\u6cbb\u5df2\u7ecf \u56de\u6765\u3002\u51af\u7433\u9053\uff1a\u201c\u4f60\u8fd8\u672a\u8d70\u5417\uff1f\u6211\u56db\u80a2\u65e0\u529b\uff0c\u80c3\u95f7\u6b32\u5455\uff0c\u5934\u75db\u53e3\u7126\uff0c\u5728\u8fd9\u8352\u5c71\u4e4b\u4e2d\uff0c\u6599\u662f\u65e0 \u6551\u7684\u4e86\u3002\u4f60\u56de\u5929\u5c71\u53bb\u5427\uff0c\u56de\u5929\u5c71\u53bb\u627e\u4f60\u7684\u745b\u59b9\u3002\u201d\u674e\u6cbb\u4ee5\u4e3a\u5979\u795e\u667a\u4e0d\u6e05\uff0c\u4e71\u8bf4\u80e1\u8bed\uff0c\u7b11\u9053\uff1a \u201c\u745b\u59b9\u4e0d\u5c31\u5728\u8fd9\u91cc\u5417\uff1f\u4f60\u653e\u5fc3\u5566\uff01\u6211\u7ed9\u4f60\u6cbb\uff0c\u5305\u4fdd\u4f60\u80fd\u6cbb\u597d\u3002\u201d\u5c06\u51af\u7433\u8f7b\u8f7b\u6276\u8d77\uff0c\u9053\uff1a\u201c\u745b \u59b9\u6055\u7f6a\uff0c\u4f11\u602a\u65e0\u793c\uff01\u201d\u51af\u7433\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u8bf7\u628a\u5916\u88f3\u892a\u4e0b\uff0c\u6211\u7ed9\u4f60\u523a\u6709\u5173\u7a74 \u9053\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8d39\u5fc3\u5566\uff01\u201d\u75c5\u773c\u6726\u80e7\u4e2d\u89c1\u674e\u6cbb\u7126\u6025\u7684\u6a21\u6837\uff0c\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u7ed9 \u6211\u6cbb\u3002\u4f60\u8fd9\u4eba\u5a46\u5a46\u5988\u5988\uff0c\u8bf4\u4ec0\u4e48\u6055\u7f6a\uff0c\u65e0\u793c\u5440\uff0c\u6211\u8fd8\u4e0d\u77e5\u4f60\u5e72\u4ec0\u4e48\u5462\uff01\u201d \u3000\u3000\u51af\u7433\u7761\u4e86\u4e00\u89c9\uff0c\u8bf4\u8bdd\u6e05\u723d\uff0c\u674e\u6cbb\u4e00\u559c\uff0c\u5fc3\u9053\uff1a\u201c\u5979\u521a\u624d\u8fd8\u8bf4\u80e1\u8bed\uff0c\u73b0\u5728\u5374\u8fd9\u6837\u6e05\u9192 \u4e86\u3002\u201d\u7528\u7af9\u9488\u66ff\u5979\u523a\u4e86\u6709\u5173\u7a74\u9053\uff0c\u53c8\u8ba9\u5979\u7761\u4e86\u4e00\u89c9\uff0c\u508d\u665a\u65f6\u5206\uff0c\u51af\u7433\u9192\u89c9\uff0c\u4e00\u5f00\u53e3\u5c31\u56b7\u809a \u997f\u3002\u674e\u6cbb\u5927\u559c\u9053\uff1a\u201c\u8c22\u5929\u8c22\u5730\uff0c\u8fd9\u53ef\u597d\u4e86\uff01\u597d\uff0c\u4f60\u8981\u679c\u5b50\u5417\uff1f\u8fd9\u91cc\u6709\u751c\u5c71\u679c\u3002\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u4e00\u53cc\u5c0f\u513f\u5973\uff0c\u60a3\u96be\u89c1\u771f\u60c5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u7389\u5973\u9732\u673a\u5fc3\u3000\u7591\u56e2\u83ab\u91ca\u3000\u541b\u738b\u8d2a\u7edd\u8272\u3000\u9669\u8c61\u73af\u751f \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e8c\u5341\u4e5d\u56de \u7389\u5973\u9732\u673a\u5fc3\u3000\u7591\u56e2\u83ab\u91ca \u541b\u738b\u8d2a\u7edd\u8272\u3000\u9669\u8c61\u73af\u751f \u3000\u3000\u51af\u7433\u8f97\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u771f\u597d\u3002\u201d\u628a\u5c71\u679c\u54ac\u4e86\u4e00\u53e3\uff0c\u76b1\u7709\u9053\uff1a\u201c\u4f60\u9a97\u6211\uff0c\u8fd9\u679c\u5b50\u662f\u9178 \u7684\u3002\u201d \u3000\u3000\u674e\u6cbb\u9053\uff1a\u201c\u9178\u7684\u66f4\u597d\uff0c\u4f60\u542c\u6211\u7684\u8bdd\uff0c\u628a\u679c\u5b50\u5403\u4e86\u5427\u3002\u201d \u3000\u3000\u51af\u7433\u53e3\u6e34\u809a\u997f\uff0c\u4e09\u4e24\u53e3\u628a\u679c\u5b50\u54ac\u53bb\u4e00\u534a\uff0c\u7b11\u9053\uff1a\u201c\u6211\u542c\u4f60\u7684\u8bdd\u5566\u3002\u201d\u8fc7\u4e86\u4e00\u9635\uff0c\u809a\u5b50\u4f5c \u54cd\uff0c\u51af\u7433\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4f60\u51fa\u53bb\u4e00\u4f1a\u513f\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4f60\u597d\u70b9\u5417\uff1f\u201d\u5ffd\u542c\u5f97\u201c\u52c3\u201d\u7136\u6709 \u58f0\uff0c\u81ed\u6c14\u56db\u6ea2\uff0c\u51af\u7433\u987f\u8db3\u9053\uff1a\u201c\u4f60\u5feb\u51fa\u53bb\uff0c\u4eba\u5bb6\u8981\u62c9\u5c4e\u5566\u3002\u201d \u3000\u3000\u674e\u51b6\u60f3\u4e0d\u5230\u8fd9\u5c71\u679c\u50ac\u6cfb\u4e4b\u529f\uff0c\u6bd4\u5df4\u8c46\u8fd8\u5feb\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u8fde\u81ed\u6c14\u4e5f\u4e0d\u89c9\u5f97\uff0c\u8f6c\u8fc7\u4e86\u8eab\uff0c\u8d70 \u8fd1\u6d1e\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u4e86\uff0c\u597d\u4e86\uff01\u4f60\u628a\u6bd2\u6c14\u6cfb\u6e05\uff0c\u8fc7\u51e0\u5929\u5c31\u53ef\u4ee5\u548c\u6211\u4e0b\u5c71\u4e86\u3002\u4f60\u80fd\u591f\u79fb\u52a8\u5417\uff1f \u4f60\u628a\u8863\u670d\u6362\u4e86\uff0c\u7b49\u4e0b\u6211\u7ed9\u4f60\u6d17\u3002\u201d \u3000\u3000\u51af\u7433\u63a9\u9f3b\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u5566\uff0c\u4f60\u8fd9\u4eba\u771f\u662f\u5a46\u5a46\u5988\u5988\u3002\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u5fc3\u4e2d\u5374\u662f\u5927\u4e3a\u611f\u52a8\uff0c \u60f3\u9053\uff1a\u201c\u8fde\u6211\u90fd\u89c9\u5f97\u81ed\uff0c\u4ed6\u5374\u6beb\u4e0d\u5728\u4e4e\u3002\u201d\u53c8\u60f3\u9053\uff1a\u201c\u82e5\u7136\u4ed6\u77e5\u9053\u6211\u4e0d\u662f\u4ed6\u7684\u4ec0\u4e48\u2018\u745b \u59b9\u2019\uff0c\u4e0d\u77e5\u8fd8\u4f1a\u4e0d\u4f1a\u5bf9\u6211\u5982\u6b64\uff1f\u201d\u53c8\u60f3\u9053\uff1a\u201c\u4ed6\u5f85\u4eba\u63a5\u7269\uff0c\u51fa\u4e8e\u4e00\u7247\u81f3\u8bda\uff0c\u8fd9\u53ef\u662f\u5047\u88c5\u4e0d \u5f97\u3002\u6211\u4ee5\u524d\u5728\u4ed6\u53d7\u4f24\u4e4b\u9645\u5f03\u4ed6\u800c\u53bb\uff0c\u4ed6\u73b0\u5728\u8fd8\u5bf9\u6211\u8fd9\u6837\u3002\u5373\u7b97\u4ed6\u662f\u628a\u6211\u5f53\u6210\u2018\u745b\u59b9\u2019\u624d\u8fd9\u6837 \u5bf9\u6211\uff0c\u4e5f\u662f\u96be\u5f97\u7684\u4e86\u3002\u201d\u5fc3\u4e2d\u611f\u52a8\uff0c\u4e0d\u89c9\u6ef4\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u51af\u7433\u6362\u4e86\u8863\u670d\uff0c\u674e\u6cbb\u628a\u5730\u4e0a\u79fd\u8ff9\u62b9\u51c0\uff0c\u5377\u8d77\u8863\u670d\uff0c\u7b11\u9053\uff1a\u201c\u679c\u5b50\u867d\u9178\uff0c\u5bf9\u4f60\u5374\u5f88\u6709\u76ca\uff0c \u518d\u5403\u4e24\u4e2a\u3002\u201d\u8d70\u51fa\u6d1e\u5916\uff0c\u5ffd\u89c1\u6d1e\u65c1\u85e4\u841d\u6df1\u5904\u4eba\u5f71\u4e00\u95ea\uff0c\u674e\u6cbb\u559d\u9053\uff1a\u201c\u8c01\uff1f\u201d\u62fe\u8d77\u4e00\u5757\u77f3\u5934\uff0c \u5411\u4eba\u5f71\u8eb2\u85cf\u4e4b\u5904\u63b7\u53bb\u3002\u90a3\u4eba\u6500\u7740\u957f\u85e4\uff0c\u6643\u4e86\u4e24\u6643\uff0c\u50cf\u8361\u79cb\u5343\u4e00\u822c\u98d8\u4e0b\u534a\u5c71\uff0c\u8eb2\u5165\u8346\u68d8\u4e1b\u4e2d\u3002 \u5b6a\u6cbb\u77a7\u6e05\u695a\u4e86\uff0c\u539f\u6765\u6b63\u662f\u88ab\u4ed6\u523a\u4f24\u7684\u6d77\u4e91\u548c\u5c1a\uff0c\u5fc3\u4e2d\u6697\u53eb\u4e0d\u5999\uff0c\u4e0d\u6562\u8fdc\u79bb\u51af\u7433\u53bb\u8ffd\uff0c\u5c31\u5728\u6d1e \u53e3\u9644\u8fd1\uff0c\u5bfb\u89c5\u5c71\u6cc9\u6d17\u6da4\u8863\u670d\u3002\u6d17\u5b8c\u518d\u770b\uff0c\u6d77\u4e91\u548c\u5c1a\u7684\u8eab\u5f71\uff0c\u5df2\u7ecf\u9690\u6ca1\u4e0d\u89c1\u3002 \u3000\u3000\u674e\u6cbb\u5c06\u6d17\u5e72\u51c0\u7684\u8863\u88f3\u6302\u5728\u6811\u4e0a\uff0c\u8ba9\u5b83\u98ce\u5e72\uff0c\u56de\u5230\u6d1e\u4e2d\uff0c\u51af\u7433\u53c8\u5df2\u6cfb\u4e86\u4e00\u6b21\uff0c\u53c8\u6362\u4e86\u4e00\u8eab \u8863\u88f3\uff0c\u89c1\u674e\u6cbb\u56de\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u521a\u624d\u548c\u8c01\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u674e\u6cbb\u9053\uff1a\u201c\u90a3\u51f6\u548c\u5c1a\u8fd8\u6ca1\u6b7b\u3002\u4e0d\u8fc7\uff0c\u4f60\u4e5f\u4e0d\u7528\u614c\uff0c\u6211\u5c31\u5728\u9644\u8fd1\u6d17\u8863\u670d\uff0c\u4f60\u542c\u5230\u4ec0\u4e48\u54cd \u52a8\uff0c\u7acb\u523b\u53eb\u6211\u3002\u201d \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u6211\u77e5\u9053\u5566\uff0c\u90a3\u51f6\u548c\u5c1a\u90a3\u5929\u4e0d\u662f\u4e5f\u4e2d\u4e86\u4f60\u4e00\u5251\u5417\uff1f\u4ed6\u53c8\u4e0d\u77e5\u9053\u6211\u751f\u75c5\uff0c\u7eb5\u7b97\u4ed6 \u4f24\u597d\u4e86\uff0c\u4e5f\u4e0d\u6562\u6765\u3002\u201d \u3000\u3000\u674e\u6cbb\u9053\uff1a\u201c\u5c0f\u5fc3\u7684\u597d\u3002\u201d\u5377\u8d77\u8863\u670d\uff0c\u53c8\u51fa\u5916\u9762\u6d17\u6da4\u3002 \u3000\u3000\u4e00\u5929\u4e00\u591c\uff0c\u51af\u7433\u6cfb\u4e86\u516d\u6b21\uff0c\u674e\u6cbb\u4e00\u70b9\u4e5f\u4e0d\u6015\u6c61\u79fd\u9ebb\u70e6\uff0c\u4e00\u591c\u672a\u7761\uff0c\u7ec6\u5fc3\u7167\u6599\u3002\u7b2c\u4e8c\u5929\u51af \u7433\u8179\u6cfb\u6b62\u4e86\uff0c\u53ef\u4ee5\u6276\u7740\u5899\u58c1\u8d70\u8def\uff0c\u53ea\u662f\u809a\u5b50\u997f\u5f97\u96be\u53d7\u3002\u7b11\u9053\uff1a\u201c\u6211\u60f3\u5403\u70e4\u7f8a\u8089\u3002\u201d\u674e\u6cbb\u542c\u5979 \u4e00\u8bf4\uff0c\u4e5f\u89c9\u809a\u997f\u96be\u582a\uff0c\u628a\u5e72\u7cae\u888b\u6253\u5f00\u4e00\u770b\uff0c\u6240\u5269\u65e0\u51e0\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u91cc\u91ce\u5c71\u7f8a\u6709\u7684\u662f\uff0c\u8981\u5403\u70e4 \u7f8a\u8089\u90a3\u4e5f\u4e0d\u96be\uff0c\u53ea\u662f\u51f6\u50e7\u7aa5\u4f3a\u5728\u4fa7\uff0c\u6211\u90a3\u80fd\u5206\u8eab\u51fa\u53bb\u730e\u7f8a\uff1f\u201d\u7b11\u9053\uff1a\u201c\u4f60\u5c06\u5c31\u70b9\u513f\uff0c\u5148\u5403\u5403 \u5e72\u7cae\u5427\uff0c\u55ef\uff0c\u8fd8\u6709\u51e0\u5757\u8089\u812f\uff0c\u8fd9\u7092\u7c73\u4e5f\u8fd8\u4e0d\u9519\u3002\u201d\u5c06\u5e72\u7cae\u5168\u90e8\u9012\u7ed9\u4e86\u51af\u7433\uff0c\u81ea\u5df1\u51fa\u5916\u91c7\u5c71\u679c \u5403\uff0c\u5e76\u751f\u706b\u70e7\u6c34\uff0c\u524a\u6728\u4e3a\u74e2\uff0c\u76db\u6c34\u7ed9\u51af\u7433\u996e\uff0c\u5e72\u4e86\u534a\u5929\uff0c\u809a\u5b50\u4e5f\u5495\u5495\u7684\u53eb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5c71\u6d1e\u9644\u8fd1\u53ef\u4ee5\u5403\u7684\u91ce\u679c\u4e0d\u591a\uff0c\u679c\u5b50\u4e5f\u62b5\u4e0d\u7740\u809a\u997f\uff0c\u674e\u6cbb\u5c06\u5f00\u6c34\u7ed9\u51af\u7433\u9001\u5e72\u7cae\u5403\uff0c\u89c1\u5979\u5403 \u5f97\u6d25\u6d25\u6709\u5473\uff0c\u9965\u706b\u8d8a\u53d1\u4e0a\u5347\u3002\u51af\u7433\u9053\uff1a\u201c\u4f60\u4e0d\u5403\u4e00\u70b9\uff1f\u201d\u674e\u6cbb\u54bd\u4e86\u53e3\u6c34\uff0c\u9053\uff1a\u201c\u6211\u521a\u624d\u5403\u8fc7 \u4e86\uff0c\u8fd8\u9971\u5462\u3002\u201d\u51af\u7433\u628a\u8089\u812f\u5403\u5b8c\uff0c\u5e72\u7cae\u5403\u4e86\u4e00\u534a\uff0c\u8214\u8214\u820c\u5934\uff0c\u7b11\u9053\uff1a\u201c\u771f\u5947\u602a\uff0c\u6211\u5e73\u65f6\u6700\u8ba8 \u538c\u5403\u5e72\u7cae\uff0c\u90a3\u77e5\u5e72\u7cae\u4e5f\u6709\u8fd9\u6837\u7f8e\u5473\uff01\u7b80\u76f4\u6bd4\u5c71\u73cd\u6d77\u5473\u8fd8\u8981\u597d\u5403\u5f97\u591a\u3002\u201d\u674e\u6cbb\u5fc3\u9053\uff1a\u201c\u809a\u5b50\u4e00 \u997f\uff0c\u518d\u7c97\u8d31\u7684\u4e1c\u897f\u4e5f\u8981\u8bf4\u597d\u5403\u3002\u201d\u51af\u7433\u89c1\u4ed6\u9762\u9752\u5507\u767d\uff0c\u4e0d\u77e5\u8fd9\u4e43\u662f\u56e0\u9965\u997f\u6240\u81f4\uff0c\u597d\u751f\u8fc7\u610f\u4e0d \u53bb\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u5929\u4f60\u4e5f\u591f\u7d2f\u5566\uff0c\u597d\u597d\u7761\u4e00\u4f1a\u5427\u3002\u201d\u674e\u6cbb\u70b9\u4e86\u70b9\u5934\uff0c\u559d\u4e86\u4e00\u74e2\u5f00\u6c34\uff0c\u5750\u5728\u5730 \u4e0a\uff0c\u5c4f\u9664\u6742\u5ff5\uff0c\u8fd0\u6c14\u7ec3\u529f\uff0c\u5927\u7ea6\u662f\u997f\u8fc7\u4e86\u5934\uff0c\u611f\u89c9\u4e0a\u53cd\u800c\u4e0d\u50cf\u5148\u524d\u90a3\u6837\u7684\u997f\u5f97\u96be\u53d7\uff0c\u53ea\u662f\u56db \u80a2\u65e0\u529b\uff0c\u7ec3\u4e86\u4e00\u9635\uff0c\u542c\u5f97\u51af\u7433\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u809a\u5b50\u771f\u5feb\u9971\uff0c\u5e72\u7cae\u53c8\u4e0d\u597d\u5403\u5566\u3002\u6211\u597d\u4e86\u4e4b\u540e\uff0c\u548c \u4f60\u5230\u5317\u4eac\u53bb\uff0c\u54b1\u4eec\u53bb\u5403\u805a\u7fe0\u56ed\u7684\u6e9c\u9e21\u812f\uff0c\u6e05\u771f\u9986\u7684\u70e4\u9e2d\u5b50\uff0c\u4e94\u82b3\u658b\u7684\u7092\u9cdd\u7cca\uff0c\u2018\u90fd\u4e00\u5904\u2019 \uff08\u5e97\u540d\uff09\u7684\u9a6c\u83b2\u8089\uff0c\u7136\u540e\u53bb\u5403\u516d\u5fc5\u5c45\u7684\u9171\u9ec4\u74dc\u2026\u2026\u8fd9\u4e9b\u90fd\u662f\u5317\u4eac\u7684\u8001\u5b57\u53f7\uff0c\u83dc\u505a\u5f97\u5471\u5471\u53eb\uff01\u201d \u3000\u3000\u51af\u7433\u5728\u7687\u5e9c\u957f\u5927\uff0c\u65f6\u65f6\u6e9c\u51fa\u6765\u5403\u4e1c\u897f\uff0c\u5bf9\u5317\u4eac\u7684\u540d\u83dc\u5982\u6570\u5bb6\u73cd\u3002\u674e\u6cbb\u672c\u6765\u9965\u706b\u7a0d\u715e\uff0c\u7ed9 \u5979\u8fd9\u4e48\u4e00\u6570\uff0c\u53c8\u997f\u8d77\u6765\uff0c\u8d8a\u53d1\u96be\u71ac\u3002\u592e\u6c42\u5979\u9053\uff1a\u201c\u597d\u59b9\u5b50\uff0c\u4f60\u522b\u8bf4\u5566\u3002\u201d\u51af\u7433\u4e00\u6014\uff0c\u7b11\u9053\uff1a \u201c\u77a7\u4f60\u7684\u6a21\u6837\uff0c\u6562\u60c5\u4e5f\u662f\u997f\u4e86\uff01\u8fd9\u91cc\u4ec0\u4e48\u4e5f\u6ca1\u6709\uff0c\u4f60\u53c8\u4e0d\u53bb\u6253\u7f8a\u3002\u201d\u674e\u6cbb\u5ffd\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u600e \u4e48\u77e5\u9053\u90a3\u4e48\u591a\u7684\u5e97\u540d\u83dc\u540d\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u4e0b\u5c71\u4e4b\u540e\uff0c\u5728\u5317\u4eac\u57ce\u91cc\u73a9\u4e86\u534a\u5e74\u3002\u201d\u674e\u6cbb\u9053\uff1a \u201c\u4f60\u600e\u4e48\u6709\u8fd9\u6837\u7684\u5fc3\u60c5\uff1f\u201d\u5fc3\u91cc\u6709\u70b9\u4e0d\u4fe1\u3002 \u3000\u3000\u51af\u7433\u6e9c\u4e86\u5634\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u77e5\u9053\u7f8a\u8089\u6709\u591a\u5c11\u79cd\u5403\u6cd5\uff1f\u6211\u544a\u8bc9\u4f60\uff0c\u53ea\u2018\u70e4\u8089\u5b9b\u2019\u4e00\u5bb6\uff0c\u5403\u7f8a \u8089\u5c31\u6709\u5341\u516b\u79cd\u5403\u6cd5\uff01\u201d\u674e\u6cbb\u5fc3\u60f3\uff1a\u5979\u73b0\u5728\u5df2\u5acc\u5e72\u7cae\u4e0d\u597d\u5403\u4e86\uff0c\u90a3\u5979\u4e00\u5b9a\u4e0d\u662f\u5f88\u997f\u4e86\u3002\u5979\u4e2d\u7684 \u6bd2\u5df2\u7ecf\u6cfb\u5c3d\uff0c\u518d\u5403\u4e00\u4e9b\u4e1c\u897f\uff0c\u957f\u957f\u6c14\u529b\uff0c\u5c31\u53ef\u4e0b\u5c71\u5566\uff0c\u5916\u9762\u51e0\u58f0\u7f8a\u53eb\uff0c\u51af\u7433\u9053\uff1a\u201c\u597d\u54e5\u54e5\uff0c \u4f60\u542c\u89c1\u5566\uff1f\u6253\u4e00\u53ea\u7f8a\u6765\u5427\uff0c\u6253\u4e0d\u5230\u7f8a\uff0c\u6253\u53ea\u91ce\u5154\u4e5f\u597d\u3002\u201d\u674e\u6cbb\u8df3\u8d77\u6765\u9053\uff1a\u201c\u597d\uff0c\u501f\u51e0\u628a\u98de\u5200 \u7ed9\u6211\uff01\u201d\u51af\u7433\u5927\u559c\uff0c\u628a\u65e0\u6bd2\u7684\u98de\u5200\u6361\u4e86\u51e0\u628a\u7ed9\u4ed6\u3002\u674e\u6cbb\u9053\uff1a\u201c\u4f60\u8df3\u8df3\u770b\u3002\u201d\u51af\u7433\u8df3\u4e86\u4e24\u8df3\u3002 \u674e\u6cbb\u9053\uff1a\u201c\u597d\uff0c\u5feb\u8981\u590d\u539f\u5566\u3002\u4f60\u628a\u6709\u6bd2\u7684\u98de\u5200\u5e26\u7740\uff0c\u8bb0\u5f97\u82e5\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5c31\u5927\u58f0\u53eb\u6211\u3002\u201d \u3000\u3000\u674e\u6cbb\u5728\u6d1e\u53e3\u88c5\u4e86\u4e24\u5f20\u8e0f\u5f29\uff0c\u7528\u4e24\u5757\u77f3\u5934\u538b\u7740\u673a\u62ec\uff0c\u751f\u4eba\u8fdb\u6765\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u8e22\u7740\u77f3\u5934\uff0c\u5f13\u7bad \u4fbf\u4f1a\u5c04\u51fa\uff0c\u5f04\u597d\u4e4b\u540e\uff0c\u559d\u4e86\u4e00\u74e2\u6c34\uff0c\u8d70\u51fa\u6d1e\u5929\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u679c\u7136\u89c1\u6709\u5c71\u7f8a\uff0c\u53ea\u662f\u5c71\u7f8a\u8dd1\u5f97\u751a \u5feb\uff0c\u674e\u6cbb\u6ca1\u6709\u6c14\u529b\uff0c\u54ea\u8ffd\u5f97\u4e0a\uff1f\u5403\u4e86\u51e0\u679a\u91ce\u679c\uff0c\u5fc3\u60f3\uff1a\u6211\u4e0d\u5982\u62e3\u4e00\u5904\u50fb\u9759\u7684\u5730\u65b9\uff0c\u8eb2\u5728\u5ca9\u77f3 \u80cc\u540e\uff0c\u7b49\u5c71\u7f8a\u8d70\u8fc7\uff0c\u6211\u4e00\u628a\u98de\u5200\u5c31\u628a\u5b83\u6253\u6b7b\u3002\u674e\u6cbb\u4f9d\u8ba1\u884c\u4e8b\uff0c\u53ef\u662f\u8fd9\u6837\u7684\u6253\u730e\u6cd5\uff0c\u6709\u5982\u201c\u5b88 \u682a\u5f85\u5154\u201d\uff0c\u7b49\u4e86\u534a\u5929\uff0c\u8fd8\u6ca1\u6709\u5c71\u7f8a\u8d70\u8fc7\uff0c\u674e\u6cbb\u53c8\u997f\u53c8\u6025\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u542c\u5230\u4e00\u58f0\u7f8a\u53eb\u3002 \u3000\u3000\u674e\u6cbb\u5728\u5ca9\u77f3\u540e\u4e00\u67c4\u98de\u5200\u5c04\u4e86\u51fa\u6765\uff0c\u90a3\u53ea\u5c0f\u7f8a\u5927\u7ea6\u662f\u8d70\u6563\u4e86\u7684\uff0c\u7ed9\u98de\u5200\u6253\u4e2d\uff0c\u8dc3\u8fc7\u5c71\u6da7\uff0c \u5012\u5730\u54c0\u9e23\uff0c\u674e\u6cbb\u95ea\u4e86\u51fa\u6765\uff0c\u89c1\u5c0f\u7f8a\u54a9\u54a9\u54c0\u53eb\uff0c\u5fc3\u4e2d\u4e00\u9635\u96be\u8fc7\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u7f8a\u5b64\u96f6\u96f6\u7684\uff0c\u60f3 \u6765\u6b63\u662f\u53bb\u627e\u5b83\u7684\u5988\u5988\uff0c\u6211\u628a\u5b83\u6253\u6b7b\u4e86\uff0c\u6bcd\u7f8a\u665a\u4e0a\u4e0d\u89c1\u5b83\u7684\u5b9d\u5b9d\u56de\u6765\uff0c\u4e0d\u77e5\u591a\u4f24\u5fc3\u5462\uff01\u201d\u53c8\u60f3 \u9053\uff1a\u201c\u745b\u59b9\u4e5f\u50cf\u8fd9\u53ea\u5c0f\u5c71\u7f8a\u4e00\u6837\uff0c\u5979\u8fde\u8eab\u4e16\u6765\u5386\u90fd\u4e0d\u77e5\u9053\uff0c\u5979\u7684\u6bcd\u4eb2\u4e5f\u8bb8\u662f\u6b63\u7b49\u7740\u5979\u56de\u6765 \u5462\uff01\u6211\u4e00\u5b9a\u5f97\u4fdd\u62a4\u5979\uff0c\u4e0d\u80fd\u8ba9\u5979\u88ab\u574f\u4eba\u4f24\u5bb3\u4e86\u3002\u201d\u674e\u6cbb\u4e45\u997f\u4e4b\u540e\uff0c\u624b\u52b2\u4e0d\u8db3\uff0c\u90a3\u98de\u5200\u780d\u5728\u5c71 \u7f8a\u811a\u4e0a\uff0c\u5d4c\u5728\u8089\u5185\uff0c\u674e\u6cbb\u8d70\u4e86\u8fd1\u524d\uff0c\u5c0f\u5c71\u7f8a\u53c8\u662f\u4e00\u58f0\u54c0\u53eb\uff0c\u674e\u6cbb\u53f9\u4e86\u53e3\u6c14\uff0c\u5c48\u4e86\u534a\u819d\uff0c\u628a\u90a3 \u67c4\u98de\u5200\u62d4\u51fa\uff0c\u5728\u80cc\u56ca\u4e0a\u53d6\u51fa\u91d1\u521b\u836f\u66ff\u5c0f\u5c71\u7f8a\u6577\u4e86\u4f24\u53e3\uff0c\u63a8\u5b83\u8d70\u5f00\uff0c\u5fc3\u9053\uff1a\u201c\u95fb\u5176\u58f0\u4e0d\u5fcd\u98df\u5176 \u8089\uff0c\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u771f\u4e0d\u9519\u3002\u6211\u5b81\u53ef\u997f\u6b7b\u4e5f\u4e0d\u5403\u8fd9\u53ea\u5c0f\u7f8a\u3002\u201d\u8eb2\u5728\u5ca9\u540e\uff0c\u60f3\u518d\u7b49\u91ce\u517d\u7ecf\u8fc7\uff0c\u90a3\u5c0f \u5c71\u7f8a\u7684\u53eb\u58f0\u6e10\u53bb\u6e10\u8fdc\uff0c\u5ffd\u7136\u53c8\u542c\u5f97\u4e00\u58f0\u5c16\u53eb\uff0c\u4ece\u5c71\u98ce\u4e2d\u8fdc\u8fdc\u4f20\u6765\u3002\u674e\u6cbb\u8df3\u4e86\u8d77\u6765\uff0c\u5fc3\u9053\uff1a \u201c\u8fd9\u4e0d\u662f\u5c71\u7f8a\u7684\u53eb\u58f0\u3002\u201d\u4f0f\u5730\u4e00\u542c\uff0c\u53eb\u58f0\u65ad\u65ad\u7eed\u7eed\uff0c\u53ef\u4e0d\u6b63\u662f\u51af\u7433\u7684\u53eb\u58f0\uff01 \u3000\u3000\u674e\u6cbb\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u745b\u59b9\u4e00\u5b9a\u662f\u78b0\u7740\u90a3\u4e2a\u51f6\u548c\u5c1a\u4e86\u3002\u201d \u3000\u3000\u674e\u6cbb\u987e\u4e0d\u5f97\u8eab\u5b50\u865a\u8f6f\uff0c\u5c55\u5f00\u5bb6\u4f20\u7684\u8f7b\u529f\u7edd\u6280\uff0c\u64a4\u817f\u4fbf\u8dd1\u3002\u4ed6\u4e3a\u4e86\u730e\u53d6\u5c71\u7f8a\uff0c\u79bb\u6d1e\u5df2\u8fdc\uff0c \u8dd1\u4e86\u4e00\u9635\uff0c\u6c14\u5598\u5fc3\u8df3\uff0c\u4e24\u53ea\u811a\u5c31\u50cf\u5e26\u4e86\u67b7\u9501\u4e00\u822c\uff0c\u611f\u89c9\u5341\u5206\u6c89\u91cd\u3002\u8fd9\u65f6\u51af\u7433\u7684\u53eb\u58f0\u542c\u5f97\u8d8a\u6e05 \u695a\u4e86\uff0c\u5206\u660e\u662f\u547c\u5524\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u674e\u6cbb\u632f\u8d77\u7cbe\u795e\uff0c\u5411\u5c71\u6d1e\u75be\u5954\uff0c\u6025\u5fd9\u4e2d\u5ffd\u7136\u8e22\u7740\u77f3\u5934\uff0c\u4e00\u8de4\u8dcc \u5012\uff0c\u722c\u8d77\u6765\u65f6\u52b2\u9053\u6d88\u5931\uff0c\u518d\u60f3\u4e3e\u6b65\uff0c\u5df2\u662f\u8f6f\u7ef5\u65e0\u529b\u3002\u5b6a\u6cbb\u5927\u6025\uff0c\u5fc3\u9053\uff1a\u201c\u73b0\u5728\u6211\u6beb\u65e0\u6c14\u529b\uff0c \u5c31\u662f\u8d76\u5f97\u56de\u53bb\uff0c\u4e5f\u6ca1\u7528\u5904\u3002\u201d\u8dc3\u5750\u5730\u4e0a\uff0c\u968f\u624b\u5728\u8eab\u65c1\u5f2f\u4e0b\u6765\u7684\u6811\u679d\u4e0a\uff0c\u6458\u4e86\u4e24\u679a\u5c71\u679c\uff0c\u82e6\u601d \u9000\u654c\u4e4b\u6cd5\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ae\u6740\u4e4b\u58f0\u6e10\u6765\u6e10\u8fd1\uff0c\u539f\u6765\u674e\u6cbb\u8dcc\u5012\u4e4b\u5904\uff0c\u79bb\u6d1e\u5df2\u7ecf\u4e0d\u8fdc\uff0c\u51af\u7433\u4e0e\u654c\u4eba \u4e00\u8e48\u53ae\u6740\uff0c\u7adf\u7136\u5230\u4e86\u674e\u6cbb\u85cf\u8eab\u4e0d\u8fdc\u7684\u5730\u65b9\u3002\u674e\u6cbb\u5728\u5927\u77f3\u540e\u8fb9\u671b\u4e86\u51fa\u6765\uff0c\u548c\u51af\u7433\u53ae\u6740\u7684\u6b63\u662f\u90a3 \u4e2a\u51f6\u548c\u5c1a\uff0c\u53ea\u89c1\u4ed6\u5de6\u817f\u5fae\u8ddb\uff0c\u8eab\u5f62\u8fdf\u6ede\uff0c\u4e5f\u597d\u50cf\u8d1f\u4f24\u7684\u6837\u5b50\u3002\u539f\u6765\u6d77\u4e91\u548c\u5c1a\u5728\u5c71\u8c37\u4e2d\u517b\u4e86\u51e0 \u65e5\uff0c\u5251\u4f24\u6e10\u6108\uff0c\u90a3\u65e5\u53d1\u73b0\u674e\u6cbb\u4e4b\u540e\uff0c\u53c8\u53d1\u73b0\u6d41\u4e0b\u6765\u7684\u5c71\u6c34\uff0c\u6c34\u8272\u6de1\u9ec4\uff0c\u81ed\u6c14\u89e6\u9f3b\u3002\u6d77\u4e91\u548c\u5c1a \u662f\u5357\u7586\u7684\u5251\u5e08\uff0c\u5728\u6d77\u5357\u5c9b\u4e94\u6307\u5c71\u6570\u5341\u5e74\uff0c\u5bf9\u5404\u79cd\u6bd2\u7269\u6df1\u6709\u7814\u7a76\uff0c\u4e00\u770b\u4e4b\u4e0b\uff0c\u4fbf\u77e5\u8fd9\u662f\u4e2d\u4e86\u5267 \u6bd2\u4e4b\u540e\u7684\u6392\u6cc4\u7269\u3002\u6d77\u4e91\u548c\u5c1a\u5fc3\u4e2d\u6697\u559c\uff0c\u60f3\u9053\uff1a\u4e24\u4e2a\u5c0f\u5bb6\u4f19\u4e2d\u4e86\u5267\u6bd2\uff0c\u8fd9\u53ef\u662f\u6211\u7684\u673a\u4f1a\u6765\u4e86\u3002 \u4ed6\u53ef\u4e0d\u77e5\u53ea\u662f\u51af\u7433\u4e00\u4eba\u4e2d\u6bd2\u3002 \u3000\u3000\u6d77\u4e91\u6084\u6084\u7684\u4ece\u5c71\u8c37\u5e95\u722c\u4e86\u4e0a\u6765\uff0c\u5728\u6d1e\u53e3\u7aa5\u63a2\uff0c\u4e00\u4e0d\u5c0f\u5fc3\u89e6\u52a8\u4e86\u674e\u6cbb\u6240\u5b89\u6392\u7684\u4f0f\u5f29\uff0c\u8170\u8179 \u8fde\u4e2d\u4e24\u7bad\uff0c\u75db\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u628a\u8305\u8349\u5806\u5728\u6d1e\u53e3\uff0c\u4fbf\u60f3\u653e\u706b\u3002\u51af\u7433\u63d0\u5251\u51fa\u6765\uff0c\u548c\u6d77\u4e91\u548c\u5c1a\u5728\u5c71\u524d \u53ae\u6740\u3002 \u3000\u3000\u51af\u7433\u4e0a\u5c71\u4e4b\u65f6\uff0c\u4e43\u662f\u7537\u5b50\u6253\u626e\uff0c\u5982\u4ee4\u6362\u4e86\u5973\u5b50\u8863\u88f3\uff0c\u8fd9\u5df2\u4ee4\u6d77\u4e91\u5403\u4e86\u4e00\u60ca\uff0c\u5c24\u5176\u5403\u60ca\u7684 \u662f\uff1a\u6d77\u4e91\u548c\u5c1a\u672c\u4ee5\u4e3a\u4ed6\u4eec\u4e8c\u4eba\u4e2d\u4e86\u5267\u6bd2\uff0c\u8c01\u77e5\u51af\u7433\u5927\u6cfb\u4e4b\u540e\uff0c\u7761\u8db3\u5403\u9971\uff0c\u7cbe\u795e\u5df2\u7ecf\u6062\u590d\uff0c\u9664 \u4e86\u7a0d\u89c1\u6e05\u7626\u4e4b\u5916\uff0c\u4e1d\u6beb\u4e0d\u663e\u75c5\u5bb9\u3002\u6d77\u4e91\u548c\u5c1a\u5fc3\u547c\u4e0d\u5999\uff0c\u6697\u9053\uff1a\u83ab\u975e\u662f\u4ed6\u4eec\u5e03\u6210\u9677\u4e95\uff0c\u5f15\u6211\u4e0a \u94a9\u4e48\uff1f\u865a\u6643\u4e00\u5251\uff0c\u8f6c\u8eab\u4fbf\u9003\u3002 \u3000\u3000\u51af\u7433\u95f7\u5728\u6d1e\u4e2d\u51e0\u65e5\uff0c\u4e4d\u89c1\u9633\u5149\uff0c\u7cbe\u795e\u6296\u64de\uff0c\u89c1\u6d77\u4e91\u548c\u5c1a\u8d1f\u4f24\u9003\u8d70\uff0c\u5fc3\u9053\uff1a\u8fd9\u79c3\u9a74\u7559\u5728\u5c71 \u4e2d\u7ec8\u662f\u4e00\u5bb3\uff0c\u6211\u4f55\u4e0d\u8d81\u4ed6\u53d7\u4f24\u4e4b\u9645\uff0c\u628a\u4ed6\u9664\u53bb\u3002\u51af\u7433\u8f7b\u529f\uff0c\u672c\u5c31\u4e0d\u9519\uff0c\u66f4\u517c\u6d77\u4e91\u5728\u5251\u4f24\u4e4b\u540e \u53c8\u53d7\u7bad\u4f24\uff0c\u66f4\u662f\u6bd4\u5979\u4e0d\u4e0a\uff0c\u8dd1\u51fa\u4e8c\u4e09\u5341\u6b65\uff0c\u4fbf\u88ab\u5979\u8ffd\u4e0a\uff0c\u6d77\u4e91\u548c\u5c1a\u8f6c\u5ff5\u4e00\u60f3\uff1a\u8fd9\u5973\u5b50\u6b66\u529f\u5728 \u6211\u4e4b\u4e0b\uff0c\u5979\u7684\u540c\u4f34\u53c8\u4e0d\u5728\u6b64\uff0c\u6211\u6b63\u597d\u8d81\u5979\u5355\u8eab\u4e00\u4eba\uff0c\u5c06\u5979\u64d2\u4e86\uff0c\u631f\u4e3a\u4eba\u8d28\uff0c\u514d\u5f97\u65e5\u591c\u5fc3\u60ca\u80c6 \u6218\uff0c\u6015\u90a3\u4e2a\u5c11\u5e74\u6765\u641c\u7d22\u81ea\u5df1\u3002 \u3000\u3000\u6d77\u4e91\u548c\u5c1a\u529f\u529b\u6df1\u539a\uff0c\u867d\u7136\u53d7\u4e86\u7bad\u4f24\uff0c\u672c\u9886\u8fd8\u662f\u5728\u51af\u7433\u4e4b\u4e0a\uff0c\u5c06\u5979\u5f15\u5230\u5730\u5f62\u9669\u5cfb\u4e4b\u5904\uff0c\u7a81 \u7136\u56de\u8eab\u53cd\u51fb\uff0c\u62a2\u5148\u5360\u4e86\u53ef\u4ee5\u9003\u8d70\u7684\u8def\u53e3\uff0c\u628a\u51af\u7433\u5835\u5728\u5c71\u77f3\u5806\u4e2d\uff0c\u524d\u9762\u662f\u5ced\u58c1\u5371\u5d16\uff0c\u4e07\u96be\u98de \u8d8a\uff0c\u4e24\u4eba\u5c31\u5728\u5c71\u77f3\u5806\u4e2d\u6076\u6218\u8d77\u6765\u3002\u4e0d\u77e5\u4e0d\u89c9\uff0c\u6765\u5230\u4e86\u674e\u6cbb\u7684\u85cf\u8eab\u4e4b\u5904\u3002 \u3000\u3000\u674e\u6cbb\u5728\u5927\u77f3\u540e\u9762\u671b\u51fa\uff0c\u53ea\u89c1\u51af\u7433\u5251\u5f0f\u592d\u77eb\uff0c\u906e\u62e6\u51fb\u523a\uff0c\u5c45\u7136\u4e0d\u9732\u7834\u7efd\uff0c\u559c\u9053\uff1a\u201c\u5085\u9752\u4e3b \u7684\u91d1\u9488\u795e\u6280\uff0c\u771f\u662f\u7075\u6548\u5f02\u5e38\u3002\u770b\u6837\u5b50\u5979\u662f\u5b8c\u5168\u597d\u4e86\u3002\u201d\u4f46\u6d77\u4e91\u548c\u5c1a\u5185\u529f\u6df1\u539a\uff0c\u5251\u52bf\u96c4\u5947\uff0c\u867d \u7136\u8eab\u6cd5\u4e0d\u53ca\u51af\u7433\u8fc5\u6377\u8f7b\u7075\uff0c\u4e00\u62a2\u4e86\u4e0a\u98ce\uff0c\u51af\u7433\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u674e\u6cbb\u770b\u4e86\u4e00\u9635\uff0c\u4f46\u89c1\u51af\u7433\u5728\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u8fde\u6362\u4e86\u4e94\u516d\u79cd\u5251\u6cd5\uff0c\u6bcf\u6bcf\u5728\u5371\u6025\u5173\u5934\uff0c\u5251\u6cd5\u7a81 \u53d8\uff0c\u907f\u8fc7\u9669\u96be\uff0c\u4ee5\u8fbe\u6469\u5251\u6cd5\u7684\u5f3a\u653b\u6700\u540e\u8f6c\u4e3a\u65e0\u6781\u5251\u6cd5\u7684\u575a\u5b88\uff0c\u5c45\u7136\u4e0e\u6d77\u4e91\u548c\u5c1a\u62fc\u4e86\u4e00\u767e\u591a\u62db\uff01 \u3000\u3000\u51af\u7433\u521a\u521a\u75c5\u6108\uff0c\u6c14\u529b\u5230\u5e95\u8f6f\u5f31\uff0c\u62fc\u4e86\u4e00\u767e\u591a\u62db\uff0c\u5251\u6cd5\u6e10\u89c1\u6563\u4e71\u3002\u6d77\u4e91\u548c\u5c1a\u54c8\u54c8\u5927\u7b11\uff0c\u53f3 \u624b\u8fd0\u5251\u5982\u98ce\uff0c\u5de6\u624b\u4fbf\u5728\u5251\u5149\u98d8\u77a5\u4e4b\u4e2d\uff0c\u5c55\u5f00\u7a7a\u624b\u5165\u767d\u5203\u7684\u529f\u592b\uff0c\u5f3a\u62a2\u51af\u7433\u7684\u5b9d\u5251\u3002 \u3000\u3000\u51af\u7433\u53eb\u9053\uff1a\u201c\u674e\u54e5\u54e5\uff0c\u5feb\u6765\u5440\uff01\u201d \u3000\u3000\u6d77\u4e91\u548c\u5c1a\u5927\u7b11\u9053\uff1a\u201c\u4f60\u53eb\u4e86\u534a\u5929\u4ed6\u4e5f\u4e0d\u7b54\u5e94\u4f60\uff0c\u4f60\u8981\u6211\u544a\u8bc9\u4f60\u4ed6\u5728\u54ea\u91cc\u4e48\uff1f\u201d\u51af\u7433\u529b\u6321 \u4e24\u62db\uff0c\u9669\u9669\u88ab\u4ed6\u6293\u7740\uff0c\u6d77\u4e91\u548c\u5c1a\u4e00\u9762\u8fdb\u62db\uff0c\u4e00\u9762\u51b7\u5632\u70ed\u8bbd\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7684\u674e\u54e5\u54e5\u5728\u9ec4\u6cc9\u8def\u4e0a \u7b49\u7740\u4f60\u53bb\u76f8\u4f1a\uff0c\u53ea\u662f\u6211\u53ef\u820d\u4e0d\u5f97\u628a\u4f60\u4e5f\u9001\u5230\u9ec4\u6cc9\u53bb\u54e9\uff01\u201d \u3000\u3000\u674e\u6cbb\u542c\u5f97\u5fc3\u5934\u706b\u8d77\uff0c\u5438\u4e86\u53e3\u6c14\uff0c\u4f7f\u52b2\u8df3\u4e86\u51fa\u6765\uff0c\u84e6\u7136\u559d\u9053\uff1a\u201c\u8d3c\u79c3\uff0c\u4ffa\u7b49\u4f60\u4e45\u4e86\uff0c\u54c8 \u54c8\uff0c\u4f60\u4eca\u65e5\u4e5f\u4f1a\u81ea\u6295\u7f57\u7f51\uff01\u201d\u6d77\u4e91\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u5728\u5168\u672a\u53d7\u4f24\u4e4b\u65f6\uff0c\u4e5f\u8fd8\u4e0d\u662f\u674e\u6cbb\u5bf9\u624b\uff0c\u89c1\u4ed6 \u7a81\u7136\u51fa\u73b0\uff0c\u53ea\u9053\u679c\u771f\u9677\u5165\u4e86\u654c\u4eba\u5e03\u7f6e\u7684\u9677\u4e95\uff0c\u5f3a\u653b\u4e24\u5251\uff0c\u628a\u51af\u7433\u903c\u5f97\u95ea\u8fc7\u4e00\u8fb9\uff0c\u8d81\u7740\u674e\u6cbb\u5c1a \u672a\u98de\u6251\u8fc7\u6765\uff0c\u56de\u8eab\u6025\u8d70\u3002\u62b1\u5934\u4e00\u6eda\uff0c\u5c31\u5728\u8346\u68d8\u4e1b\u4e2d\u76f4\u6eda\u4e0b\u5c71\u6da7\u3002\u51af\u7433\u62cd\u638c\u5927\u7b11\uff0c\u9053\uff1a\u201c\u674e\u54e5 \u54e5\uff0c\u5feb\u53bb\u6253\u843d\u6c34\u72d7\u5440\uff01\u201d\u5ffd\u89c1\u674e\u6cbb\u9762\u8272\u82cd\u767d\uff0c\u6447\u6447\u6b32\u5760\uff0c\u4f38\u6307\u793a\u610f\uff0c\u51af\u7433\u5403\u4e86\u4e00\u60ca\uff0c\u5ffd\u800c\u8fde \u58f0\u5a07\u7b11\u3002 \u3000\u3000\u51af\u7433\u673a\u8b66\u4e4b\u6781\uff0c\u5a07\u7b11\u53eb\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u6253\u4e86\u4e00\u53ea\u8001\u864e\uff0c\u61d2\u5f97\u6253\u843d\u6c34\u72d7\u4e86\uff1f\u864e\u9aa8\u71ac\u6c64\u5f88\u4e0d\u9519 \u5440\uff01\u201d\u674e\u6cbb\u4e00\u6014\uff0c\u968f\u5373\u660e\u767d\u7528\u610f\uff0c\u632f\u594b\u7cbe\u795e\uff0c\u5927\u58f0\u7b11\u9053\uff1a\u201c\u662f\u5440\uff0c\u4f60\u53bb\u5265\u864e\u76ae\uff0c\u6211\u6253\u6c34\u53bb\u3002 \u54b1\u4eec\u5403\u70e4\u7f8a\u8089\u5403\u817b\u4e86\uff0c\u4e5f\u8be5\u6362\u6362\u53e3\u5473\u5566\u3002\u201d\u8bf4\u5b8c\u4e4b\u540e\uff0c\u4e00\u8de4\u8dcc\u5012\uff0c\u5e78\u5f97\u51af\u7433\u5df2\u5230\u8eab\u8fb9\uff0c\u53cc\u81c2 \u4e00\u4f38\u5c06\u4ed6\u62b1\u4f4f\u3002\u5728\u8033\u8fb9\u6084\u6084\u8bf4\u9053\uff1a\u201c\u4f60\u518d\u5fcd\u4e00\u4f1a\u513f\uff0c\u5f85\u90a3\u79c3\u9a74\u8d70\u8fdc\u4e86\uff0c\u6211\u518d\u62b1\u4f60\u56de\u53bb\u3002\u201d \u3000\u3000\u6d77\u4e91\u548c\u5c1a\u6eda\u4e0b\u5c71\u6da7\uff0c\u95fb\u5f97\u4ed6\u4eec\u8c08\u7b11\u4e4b\u58f0\uff0c\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u679c\u7136\u4e2d\u4e86\u4ed6\u4eec\u7684 \u5708\u5957\uff0c\u597d\u9669\uff0c\u597d\u9669\uff01\u6211\u73b0\u5728\u82e5\u78b0\u5230\u8001\u864e\u5df2\u65e0\u6c14\u529b\u76f8\u6597\uff0c\u4f55\u51b5\u6597\u4ed6\u3002\u201d\u6025\u5fd9\u9644\u845b\u6500\u85e4\uff0c\u6e9c\u4e0b\u5c71 \u53bb\uff0c\u8eb2\u8fdb\u5c71\u8c37\u4e2d\u7684\u79d8\u7a9f\u3002 \u3000\u3000\u51af\u7433\u5728\u5c71\u9876\u773a\u671b\uff0c\u76f4\u81f3\u6d77\u4e91\u548c\u5c1a\u7684\u5f71\u5b50\u6d88\u5931\u4e4b\u540e\uff0c\u624d\u4f38\u4f38\u820c\u5934\uff0c\u7b11\u9053\uff1a\u201c\u597d\u9669\uff0c\u539f\u6765\u4f60 \u4e5f\u662f\u6446\u7a7a\u57ce\u4e4b\u8ba1\u3002\u201d\u7ea4\u624b\u4e00\u5370\u674e\u6cbb\u989d\u5934\uff0c\u6b49\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u662f\u7d2f\u591f\u4e86\uff0c\u4f60\u89c9\u5f97\u600e\u6837\uff1f\u4f60\u53ef \u5343\u4e07\u4e0d\u80fd\u751f\u75c5\u5440\uff01\u201d\u674e\u6cbb\u89c1\u5979\u5fe7\u6025\u4e4b\u60c5\uff0c\u73b0\u4e8e\u8f9e\u8272\uff0c\u5fc3\u4e2d\u6b23\u6170\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u5c0f\u59ae\u5b50\u4e5f\u61c2\u5f97\u8981 \u5173\u5fc3\u522b\u4eba\u4e86\u3002\u201d \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u4f60\u6ca1\u6709\u53d1\u70e7\u3002\u54a6\uff0c\u4f60\u600e\u4e48\u4e0d\u8bf4\u8bdd\u5440\uff1f\u201d\u674e\u6cbb\u5341\u5206\u4e0d\u597d\u610f\u601d\uff0c\u4f4e\u58f0\u8bf4 \u9053\uff1a\u201c\u6211\u809a\u5b50\u997f\uff0c\u4e0d\u662f\u751f\u75c5\u3002\u201d\u51af\u7433\u201c\u54e7\u201d\u7684\u4e00\u7b11\uff0c\u4f38\u51fa\u6307\u5934\u5728\u4ed6\u8138\u4e0a\u4e00\u62ec\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u771f \u50bb\uff0c\u4f60\u809a\u5b50\u997f\u4e3a\u4ec0\u4e48\u4e0d\u51fa\u58f0\uff0c\u5374\u628a\u5e72\u7cae\u90fd\u7ed9\u6211\u5403\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4f60\u75c5\u540e\u4f53\u865a\uff0c\u4e0d\u5403\u4e1c\u897f\uff0c\u600e \u80fd\u957f\u51fa\u6c14\u529b\uff1f\u201d\u51af\u7433\u4e00\u7b11\u5c06\u4ed6\u80cc\u8d77\uff0c\u5fc3\u4e2d\u611f\u52a8\uff0c\u53ea\u89c9\u751c\u4e1d\u4e1d\u7684\uff0c\u6c14\u529b\u4e5f\u7279\u522b\u5927\u4e86\u3002 \u3000\u3000\u56de\u5230\u6d1e\u4e2d\uff0c\u51af\u7433\u9053\uff1a\u201c\u6211\u5403\u5269\u7684\u8fd8\u6709\u4e00\u70b9\u70b9\u5e72\u7cae\uff0c\u4f60\u9001\u5f00\u6c34\u5403\u4e86\u5427\u3002\u6211\u6253\u730e\u53bb\uff01\u201d \u3000\u3000\u674e\u6cbb\u9053\uff1a\u201c\u4f60\u521a\u521a\u75c5\u597d\uff0c\u53c8\u7ecf\u8fc7\u4e00\u573a\u6076\u6597\uff0c\u4e5f\u8be5\u6b47\u5566\uff01\u201d \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u771f\u5947\u602a\uff0c\u6211\u6253\u4e86\u4e00\u573a\uff0c\u51fa\u4e86\u4e00\u8eab\u81ed\u6c57\uff0c\u53cd\u800c\u7cbe\u795e\u597d\u4e86\u3002\u4f60\u8eba\u4e00\u56de\uff0c\u6211\u6253\u4e00\u53ea \u5c71\u7f8a\u7ed9\u4f60\u70e4\u3002\u201d \u3000\u3000\u674e\u62ac\u5ffd\u9053\uff1a\u201c\u6709\u4e00\u53ea\u5c0f\u5c71\u7f8a\uff0c\u811a\u4e0a\u4e2d\u4e86\u6211\u7684\u98de\u5200\uff0c\u8d70\u8def\u4e00\u8ddb\u4e00\u62d0\u7684\uff0c\u4f60\u89c1\u4e86\u5b83\u4e0d\u8981\u6253 \u5b83\u3002\u201d \u3000\u3000\u51af\u7433\u7b11\u9053\uff1a\u201c\u54a6\uff0c\u539f\u6765\u4f60\u5df2\u6253\u4f24\u4e00\u53ea\u5c71\u7f8a\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4f60\u53c8\u4e0d\u628a\u5b83\u62d6\u56de\u6765\uff1f\u201d \u3000\u3000\u674e\u6cbb\u9053\uff1a\u201c\u5b83\u53eb\u5f97\u5f88\u51c4\u60e8\uff0c\u6211\u4e0d\u5fcd\u5fc3\u3002\u201d\u4e00\u7b11\u51fa\u6d1e\u800c\u53bb\u3002 \u3000\u3000\u674e\u6cbb\u5403\u4e86\u4e00\u70b9\u5e72\u7cae\uff0c\u8eba\u5728\u5730\u4e0a\uff0c\u671b\u7740\u6d1e\u53e3\u51fa\u795e\uff0c\u60f3\u9053\uff1a\u201c\u745b\u59b9\u5728\u5929\u5c71\u4e4b\u65f6\uff0c\u5341\u5206\u5584\u826f\uff0c \u4e0b\u5c71\u4e4b\u540e\uff0c\u4e0d\u8fc7\u5e74\u591a\uff0c\u5c31\u53d8\u5f97\u574f\u4e86\u3002\u5e78\u597d\u6211\u518d\u9047\u89c1\u5979\uff0c\u8fd9\u51e0\u5929\u6765\u5979\u867d\u7136\u60a3\u4e86\u4e00\u573a\u5927\u75c5\uff0c\u5fc3\u7075 \u5012\u4f3c\u6e10\u6e10\u6062\u590d\u5065\u5eb7\u4e86\u3002\u201d\u60f3\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u60f3\u8d77\u4e00\u4e8b\uff0c\u5fc3\u9053\uff1a\u201c\u6613\u4f2f\u6bcd\u7684\u5929\u5c71\u5251\u6cd5\u4f55\u7b49\u795e\u5999\uff0c \u4f55\u4ee5\u745b\u59b9\u521a\u624d\u548c\u90a3\u79c3\u9a74\u6076\u6597\uff0c\u8fde\u7528\u4e94\u516d\u79cd\u5251\u6cd5\uff0c\u5374\u603b\u4e0d\u628a\u672c\u95e8\u5251\u6cd5\u65bd\u5c55\u51fa\u6765\uff1f\u201d\u60f3\u6765\u60f3\u53bb\uff0c \u7591\u56e2\u83ab\u89e3\uff0c\u4e0d\u89c9\u628a\u4e0b\u5c71\u4ee5\u6765\uff0c\u51e0\u6b21\u9047\u5230\u51af\u7433\u7684\u4e8b\uff0c\u90fd\u60f3\u8d77\u6765\uff0c\u7ec6\u7ec6\u7422\u78e8\uff0c\u53ea\u89c9\u5979\u7684\u884c\u4e8b\u8bed \u6c14\uff0c\u65e5\u5e38\u4e00\u4e9b\u52a8\u4f5c\u4e0a\u7684\u5c0f\u4e60\u60ef\u7b49\u7b49\uff0c\u90fd\u548c\u51af\u745b\u6709\u5f02\uff0c\u60f3\u5f97\u4e0d\u7981\u5446\u4e86\u3002\u201c\u96be\u9053\u5979\u4e0d\u662f\u745b\u59b9\uff1f\u4e0d \u4f1a\u5440\uff0c\u4e0d\u4f1a\uff01\u4e16\u95f4\u90a3\u80fd\u6709\u4e24\u4e2a\u8fd9\u6837\u76f8\u4f3c\u7684\u4eba\uff1f\u201d\u6e10\u6e10\u65e5\u5f71\u897f\u659c\uff0c\u6d1e\u53e3\u9634\u6697\uff0c\u5374\u8fd8\u4e0d\u89c1\u51af\u7433\u56de \u6765\uff01 \u3000\u3000\u674e\u6cbb\u5fc3\u4e2d\u6697\u60ca\uff1a\u201c\u96be\u9053\u5979\u53c8\u50cf\u4e0a\u6b21\u4e00\u6837\uff0c\u629b\u5f00\u6211\u72ec\u81ea\u4e0b\u5c71\uff1f\u201d\u53c8\u60f3\u9053\uff1a\u201c\u81ea\u4ece\u548c\u5979\u540c\u8def \u5230\u5d69\u5c71\u4ee5\u6765\uff0c\u770b\u5979\u8bf4\u8bdd\u884c\u4e8b\uff0c\u5df2\u4e0d\u4f3c\u4e0b\u5c71\u540e\u521d\u89c1\u5979\u65f6\u90a3\u6837\u4e56\u8c2c\uff0c\u6599\u5979\u4eca\u6b21\u65ad\u4e0d\u4f1a\u5f03\u6211\u800c\u53bb \u4e86\u3002\u201d\u6b63\u81ea\u601d\u7591\u4e0d\u5b9a\uff0c\u5ffd\u89c1\u6d1e\u53e3\u4eba\u5f71\u4e00\u95ea\uff0c\u51af\u7433\u63d0\u7740\u4e00\u53ea\u70e4\u719f\u7684\u7f8a\u817f\u8d70\u4e86\u8fdb\u6765\u3002\u674e\u6cbb\u5fc3\u4e2d\u4e00 \u5bbd\uff0c\u6697\u8d23\u81ea\u5df1\u4e0d\u8be5\u628a\u5979\u60f3\u5f97\u592a\u574f\u3002\u201c\u5979\u5e74\u7eaa\u5c0f\uff0c\u9605\u5386\u5c11\uff0c\u521d\u8d70\u6c5f\u6e56\uff0c\u8bef\u4ea4\u532a\u4eba\uff0c\u96be\u514d\u53d7\u4e86\u5f71 \u54cd\uff0c\u4eca\u540e\u5979\u5728\u6211\u7684\u8eab\u8fb9\uff0c\u5c31\u4e0d\u4f1a\u90a3\u6837\u4e86\u3002\u201d \u3000\u3000\u51af\u7433\u53f3\u624b\u63d0\u7740\u7f8a\u817f\uff0c\u5de6\u624b\u628a\u674e\u6cbb\u62c9\u4e86\u8d77\u6765\uff0c\u5c06\u7f8a\u817f\u51d1\u8fd1\u4ed6\u7684\u9f3b\u7aef\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u95fb\u95fb\uff0c \u9999\u4e0d\u9999\uff1f\u201d\u674e\u6cbb\u5c31\u7740\u5979\u7684\u624b\u5543\u4e86\u4e00\u53e3\uff0c\u8fde\u9053\uff1a\u201c\u597d\u9999\uff0c\u597d\u9999\uff01\u201d\u628a\u7f8a\u817f\u63a5\u4e86\u8fc7\u6765\uff0c\u5927\u5403\u5927 \u56bc\u3002\u51af\u7433\u7b11\u9053\uff1a\u201c\u4e45\u997f\u4e4b\u540e\uff0c\u4e0d\u8be5\u5403\u5f97\u592a\u591a\uff0c\u63d0\u9632\u628a\u4f60\u5403\u574f\u4e86\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u4eec \u7ec3\u8fc7\u5185\u529f\u7684\u4eba\uff0c\u80c3\u810f\u4e0d\u50cf\u5e73\u5e38\u4eba\u90a3\u6837\u5ae9\u5f31\u3002\u201d\u7247\u523b\u4e4b\u95f4\uff0c\u628a\u90a3\u53ea\u719f\u7f8a\u817f\u5403\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u51af\u7433\u770b\u4ed6\u72fc\u541e\u864e\u54bd\uff0c\u5341\u5206\u597d\u7b11\u3002\u5ffd\u800c\u60f3\u8d77\u4ed6\u662f\u4e3a\u7740\u81ea\u5df1\u624d\u6371\u9965\u62b5\u997f\uff0c\u4e0d\u89c9\u7b11\u4e0d\u51fa\u6765\u3002\u674e \u6cbb\u5374\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u62c5\u5fc3\u4f60\u4e0d\u56de\u6765\u4e86\u5462\uff01\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u4e0d\u6562\u5728\u5c71\u6d1e\u9644\u8fd1\u70e4\uff0c\u6015\u70df\u85b0\u574f\u4e86 \u4f60\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u73b0\u5728\u5f88\u61c2\u4e8b\u4e86\u3002\u201d\u51af\u7433\u7b11\u9053\uff1a\u201c\u4f60\u6709\u70e4\u7f8a\u8089\u5403\u624d\u8bf4\u6211\u61c2\u4e8b\uff0c\u5c06\u6765 \u6ca1\u7f8a\u8089\u5403\uff0c\u53c8\u8be5\u9a82\u6211\u5566\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u6211\u51e0\u65f6\u9a82\u8fc7\u4f60\u6765\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u9a82 \u6211\u3002\u201d\u4e00\u7b11\u71c3\u8d77\u677e\u679d\u3002 \u3000\u3000\u706b\u5149\u4e2d\u53ea\u89c1\u674e\u6cbb\u671b\u770b\u81ea\u5df1\u51fa\u795e\uff0c\u51af\u7433\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff1f\u662f\u4e0d\u662f\u6211\u8bf4\u9519\u8bdd\uff0c\u53c8\u60f9\u4f60\u751f\u6c14 \u4e86\uff1f\u201d\u674e\u6cbb\u5ffd\u9053\uff1a\u201c\u6211\u5728\u60f3\u7740\u4e00\u62db\u5251\u62db\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u54ea\u4e00\u62db\u5251\u62db\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u521a\u624d\u4f60\u548c \u90a3\u51f6\u50e7\u6597\u5251\uff0c\u6709\u4e00\u62db\u90a3\u51f6\u50e7\u4f7f\u7684\u662f\u2018\u60ca\u6d9b\u62cd\u5cb8\u2019\uff0c\u4f60\u7528\u5c11\u6797\u5251\u6cd5\u4e2d\u7684\u2018\u4e00\u82c7\u6e21\u6c5f\u2019\u8f7b\u8f7b\u5378\u4e86 \u654c\u4eba\u7684\u653b\u52bf\uff0c\u5de7\u5999\u7684\u907f\u4e86\u8fc7\u53bb\uff0c\u90a3\u56fa\u7136\u4e0d\u9519\uff1b\u53ef\u662f\u6211\u8bb0\u5f97\u6613\u4f2f\u6bcd\u6f14\u8fc7\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u2018\u5bd2\u6d9b\u5251 \u6cd5\u2019\uff0c\u6709\u4e00\u62db\u53eb\u505a\u2018\u6d6a\u6d8c\u91d1\u95e8\u201d\u4e00\u62db\u56db\u5f0f\uff0c\u7d27\u51d1\u5f02\u5e38\uff0c\u6b63\u597d\u53ef\u4ee5\u5bf9\u4ed8\u90a3\u62db\u2018\u60ca\u6d9b\u62cd\u5cb8\u2019\u3002\u4e0d \u77e5\u4f60\u4e3a\u4f55\u4e0d\u7528\uff1f\u4f46\u6211\u5bf9\u5929\u5c71\u5251\u6cd5\u5e76\u4e0d\u719f\u4e60\uff0c\u611a\u89c1\u5982\u6b64\uff0c\u5bf9\u4e0d\u5bf9\u8fd8\u8981\u8bf7\u8d24\u59b9\u6307\u6559\u3002\u8bf7\u4f60\u628a\u90a3\u4e00 \u62db\u2018\u6d6a\u6d8c\u91d1\u95e8\u2019\u6f14\u7ed9\u6211\u770b\uff0c\u5f7c\u6b64\u518d\u7422\u78e8\u7422\u78e8\u3002\u201d \u3000\u3000\u51af\u7433\u4e00\u6115\uff0c\u5f3a\u7b11\u7b54\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u5fd8\u8bb0\u5566\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u5fd8\u8bb0\u4e86\uff1f\u201d\u51af\u7433\u773c\u5708\u4e00 \u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0b\u5c71\u4e4b\u540e\uff0c\u66fe\u751f\u8fc7\u4e00\u6b21\u5927\u75c5\uff0c\u90a3\u65f6\u53c8\u6ca1\u6709\u4f60\u7167\u6599\uff0c\u4e00\u4e2a\u8001\u5a46\u5b50\u71ac\u751f\u8349\u836f\u6211 \u559d\uff0c\u75c5\u597d\u4e86\uff0c\u4ee5\u524d\u5b66\u8fc7\u7684\u5f88\u591a\u4e1c\u897f\u90fd\u5fd8\u8bb0\u4e86\u3002\u201d\u51af\u7433\u8bf4\u7684\u5f53\u7136\u662f\u8c0e\u8a00\uff0c\u674e\u6cbb\u542c\u4e86\uff0c\u4e5f\u89c9\u96be\u4e8e \u7f6e\u4fe1\u3002\u7741\u5927\u4e86\u773c\u775b\uff0c\u5446\u5446\u7684\u671b\u7740\u51af\u7433\u3002 \u3000\u3000\u51af\u7433\u5fc3\u4e2d\u4e00\u9178\uff0c\u60f3\u9053\uff1a\u201c\u8fdf\u65e9\u90fd\u8981\u7ed9\u4ed6\u770b\u7834\uff0c\u6211\u4f55\u4e0d\u5bf9\u4ed6\u8bf4\u4e86\u3002\u201d\u674e\u6cbb\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c \u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u5929\u5c71\u5251\u6cd5\u5965\u5999\u4e4b\u6781\uff0c\u4f60\u5374\u628a\u5b83\u5fd8\u4e86\u3002\u201d\u51af\u7433\u8d70\u8fd1\u4e24\u6b65\uff0c\u62c9\u8d77\u674e\u6cbb\u7684\u624b\uff0c \u5ffd\u7136\u7b11\u9053\uff1a\u201c\u5982\u6211\u5bf9\u4f60\u8bf4\u8c0e\uff0c\u4f60\u607c\u6211\u5417\uff1f\u201d \u3000\u3000\u674e\u6cbb\u9aa4\u95fb\u6b64\u8bed\uff0c\u8c6a\u5403\u4e00\u60ca\uff0c\u6025\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u4e86\u4ec0\u4e48\u8c0e\u8bdd\u5462\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u6839\u672c\u4e0d\u61c2\u5929 \u5c71\u5251\u6cd5\uff01\u201d\u674e\u6cbb\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u624d\u662f\u6700\u5927\u7684\u8c0e\u8a00\uff0c\u4f60\u4e0d\u61c2\u5929\u5c71\u5251\u6cd5\u8fd8\u6709\u8c01\u61c2\uff1f\u201d\u51af\u7433\u6cea\u5149\u83b9 \u7136\uff0c\u5ffd\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u7684\u5fc3\u601d\uff0c\u4f60\u6000\u7591\u6211\u4e0d\u662f\u4f60\u7684\u745b\u59b9\uff0c\u4f60\u5bf9\u6211\u7ec6\u5fc3\u7167\u6599\uff0c\u89c9\u5f97\u4e0d\u503c\u4e86\uff0c\u662f \u4e48\uff1f\u201d\u674e\u6cbb\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u54ea\u91cc\u8bdd\u6765\uff1f\u54b1\u4eec\u4ee5\u4fa0\u4e49\u81ea\u8bb8\uff0c\u5373\u7b97\u89c1\u4e86\u6beb\u4e0d\u76f8\u8bc6\u4e4b\u4eba\uff0c\u60a3\u75c5 \u53d7\u4f24\uff0c\u4e5f\u8be5\u6551\u62a4\u3002\u4f55\u51b5\u6211\u4e0e\u4f60\u5462\uff1f\u4f60\u8fd9\u6837\u8bf4\u6cd5\uff0c\u5f53\u6211\u662f\u4ec0\u4e48\u4eba\u4e86\uff1f\u201d\u51af\u7433\u672c\u60f3\u8bf4\u51fa\u771f\u76f8\uff0c\u89c1 \u4ed6\u5982\u6b64\uff0c\u5ffd\u53c8\u5fcd\u4f4f\uff0c\u5657\u55e4\u7b11\u9053\uff1a\u201c\u6211\u548c\u4f60\u95f9\u7740\u73a9\u513f\uff0c\u4f60\u5c31\u5f53\u771f\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u665a\u4e24\u4eba\u90fd\u8f97\u8f6c\u53cd\u4fa7\uff0c\u4e0d\u80fd\u719f\u7761\u3002\u51af\u7433\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u81f3\u8bda\u7684\u541b\u5b50\uff0c\u5fc3\u4e2d\u6709\u611f\uff0c\u53cd\u590d\u601d \u91cf\uff0c\u4e00\u4f1a\u513f\u60f3\u628a\u771f\u76f8\u5168\u544a\u8bc9\u4ed6\uff0c\u4e00\u4f1a\u513f\u53c8\u60f3\u4ecd\u7136\u7792\u7740\u3002\u4e0d\u77e5\u600e\u7684\uff0c\u7a81\u7136\u60f3\u8d77\u4e86\u5e74\u7fb9\u5c27\u6765\uff0c\u5e74 \u7fb9\u5c27\u96c4\u624d\u9738\u6c14\uff0c\u8a00\u884c\u4e3e\u6b62\uff0c\u81ea\u6709\u4e00\u79cd\u5a01\u4eea\uff0c\u51af\u7433\u5fc3\u5e95\u91cc\u975e\u5e38\u4f69\u670d\uff0c\u5728\u6b64\u6b21\u4e0a\u5d69\u5c71\u4e4b\u524d\uff0c\u603b\u89c9 \u5f97\u674e\u6cbb\u4e0d\u80fd\u548c\u4ed6\u76f8\u6bd4\uff0c\u4f46\u7ecf\u4e86\u8fd9\u573a\u75c5\u540e\uff0c\u5e74\u7fb9\u5c27\u7684\u5f71\u5b50\u5ffd\u7136\u6e10\u6e10\u6de1\u4e86\u3002\u51af\u7433\u53c8\u60f3\u8d77\u5141\u796f\u903c\u5a5a \u4e4b\u4e8b\uff0c\u84e6\u7136\u53d1\u4e86\u4e00\u4e2a\u602a\u60f3\uff0c\u5fc3\u9053\uff1a\u201c\u4eba\u4e3a\u4ec0\u4e48\u8981\u7ed3\u5a5a\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u4f46\u5973\u5b69\u5b50\u603b\u5f97\u6709\u4e00\u4e2a\u4e08 \u592b\uff0c\u5927\u7ea6\u662f\u4e0d\u53ef\u907f\u514d\u7684\u4e86\u3002\u5047\u5982\u8981\u6211\u9009\u62e9\u4e00\u4e2a\u4e08\u592b\uff0c\u9009\u62e9\u8c01\u5462\uff1f\u662f\u5e74\u7fb9\u5c27\u5462\uff1f\u8fd8\u662f\u8fd9\u4e2a\u6709\u70b9 \u50bb\u6c14\u7684\u674e\u6cbb\uff1f\u5514\uff0c\u6700\u597d\u4ed6\u4eec\u80fd\u50cf\u6ce5\u4eba\u4e00\u6837\uff0c\u53ef\u4ee5\u6253\u788e\u4e86\u548c\u6c34\u634f\u6210\u4e00\u4e2a\u3002\u5478\uff0c\u771f\u662f\u50bb\u60f3\u6cd5\uff01\u6211 \u73b0\u5728\u53c8\u4e0d\u8981\u7ed3\u5a5a\uff0c\u60f3\u8fd9\u4e9b\u505a\u4ec0\u4e48\uff1f\u201d\u4e0d\u89c9\u7b11\u4e86\u51fa\u6765\u3002 \u3000\u3000\u674e\u6cbb\u4e5f\u6b63\u5728\u80e1\u601d\u4e71\u60f3\uff0c\u56de\u5473\u51af\u7433\u6240\u8bf4\u8fc7\u7684\u8bdd\uff0c\u7591\u4e91\u7b3c\u7f69\u5fc3\u5934\uff0c\u7a81\u95fb\u51af\u7433\u7b11\u58f0\uff0c\u5750\u8d77\u6765 \u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u8fd8\u672a\u7761\u5417\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u60f3\u8d77\u90a3\u4e2a\u79c3\u9a74\u88ab\u4f60\u5413\u8dd1\uff0c\u73b0\u5728\u8fd8\u89c9\u597d\u7b11\u3002\u201d\u77f3\u58c1 \u4e0a\u63d2\u7740\u7684\u677e\u679d\uff0c\u70e7\u5f97\u567c\u556a\u4f5c\u54cd\uff0c\u674e\u6cbb\u9053\uff1a\u201c\u665a\u95f4\u5f88\u51b7\uff0c\u4f60\u5f53\u5fc3\u70b9\uff0c\u8981\u4e0d\u8981\u70e7\u4e00\u5806\u706b\uff1f\u201d\u51af\u7433 \u9053\uff1a\u201c\u4e0d\u8981\uff0c\u6211\u53c8\u4e0d\u662f\u5f31\u4e0d\u7981\u98ce\u7684\u5c0f\u59d0\u3002\u201d\u5979\u7761\u4e0d\u7740\uff0c\u5750\u8d77\u6765\u60f3\u4e0e\u674e\u6cbb\u804a\u5929\u3002\u63a8\u5f00\u5f53\u4f5c\u6795\u5934 \u7684\u5305\u88f9\uff0c\u624b\u89e6\u4e66\u672c\uff0c\u62bd\u4e86\u51fa\u6765\uff0c\u674e\u6cbb\u7b11\u9053\uff1a\u201c\u5168\u9760\u8fd9\u672c\u4e66\u6551\u4e86\u4f60\u7684\u5c0f\u547d\u3002\u201d \u3000\u3000\u51af\u7433\u60f3\u8d77\u674e\u6cbb\u6069\u5fb7\uff0c\u65e0\u53ef\u8a00\u62a5\uff0c\u628a\u90a3\u672c\u5085\u9752\u4e3b\u7684\u7edd\u4e16\u5947\u4e66\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u4f60\u6b22\u559c\u8fd9\u672c\u4e66\uff0c \u6211\u9001\u7ed9\u4f60\u5427\u3002\u201d\u674e\u6cbb\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5947\u9053\uff1a\u201c\u8fd9\u672c\u4e66\u4e0d\u662f\u6613\u6015\u6bcd\u53eb\u4f60\u53bb\u53d6\u7684\u5417\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u662f \u5440\uff0c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u5979\u8001\u4eba\u5bb6\u8981\u4f60\u53d6\u8fd9\u672c\u4e66\uff0c\u5f53\u7136\u662f\u60f3\u7559\u7ed9\u65e0\u6781\u6d3e\u7684\u4f20\u4eba\u3002\u4f60\u600e \u4e48\u53ef\u4ee5\u79c1\u5c06\u6388\u53d7\uff1f\u201d\u51af\u7433\u4fe1\u53e3\u80e1\u8bf4\uff0c\u4e0d\u60f3\u53c8\u78b0\u4e86\u9489\u3002\u8981\u77e5\u6613\u5170\u73e0\u662f\u4e00\u6d3e\u5b97\u5e08\uff0c\u8f88\u4efd\u6781\u5c0a\uff0c\u5979 \u65ad\u65e0\u8981\u522b\u6d3e\u7684\u4e66\u636e\u4e3a\u5df1\u6709\u4e4b\u7406\u3002\u51af\u7433\u5728\u7687\u5e9c\u957f\u5927\uff0c\u5374\u4e0d\u77e5\u8fd9\u79cd\u6b66\u6797\u4e2d\u6700\u4e3a\u770b\u91cd\u7684\u4e8b\u60c5\uff0c\u8bf4\u8c0e \u4e4b\u65f6\uff0c\u987a\u7740\u674e\u6cbb\u7684\u53e3\u6c14\uff0c\u4e3a\u8fd9\u672c\u4e66\u65e2\u7136\u662f\u6613\u5170\u73e0\u8981\u7684\uff0c\u90a3\u4e48\u81ea\u5df1\u8fd9\u4e2a\u201c\u5192\u540d\u5f1f\u5b50\u201d\u4e5f\u5c31\u6709\u6743 \u5904\u7f6e\u4e86\u3002\u7ed9\u674e\u6cbb\u4e00\u8bf4\uff0c\u6025\u5207\u95f4\u7adf\u60f3\u4e0d\u51fa\u8bdd\u6765\u81ea\u5706\u5176\u8bf4\uff0c\u674e\u6cbb\u77a7\u7740\u90a3\u672c\u4e66\uff0c\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u51af\u7433\u5fc3\u9053\uff1a\u201c\u7cdf\u4e86\uff0c\u8fd9\u56de\u5b9a\u662f\u7ed9\u4ed6\u8bc6\u7834\u8c0e\u8a00\uff0c\u6240\u4ee5\u624d\u8fde\u8fde\u53f9\u6c14\u3002\u201d\u674e\u6cbb\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4 \u9053\uff1a\u201c\u60f3\u5f53\u5e74\u6613\u8001\u524d\u8f88\u548c\u65e0\u6781\u6d3e\u7684\u5085\u7956\u5e08\u4ece\u4e2d\u539f\u4e00\u540c\u6765\u5230\u585e\u5916\uff0c\u4e0e\u65e0\u6781\u6d3e\u8bf4\u5f97\u4e0a\u662f\u6570\u4ee3\u4ea4 \u60c5\uff0c\u800c\u4eca\u5374\u773c\u89c1\u5085\u9752\u4e3b\u8eab\u540e\u51cb\u96f6\uff0c\u8fde\u4f20\u4eba\u4e5f\u6ca1\u4e00\u4e2a\uff0c\u8fd9\u672c\u4e66\u5c06\u6765\u4e5f\u4e0d\u77e5\u8981\u7ed9\u8c01\u624d\u597d\uff0c\u5979\u8001\u4eba \u5bb6\u82e5\u89c1\u6b64\u4e66\uff0c\u4e0d\u77e5\u591a\u4f24\u5fc3\u5462\uff01\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\u4ee5\u5979\u8001\u4eba\u5bb6\u7684\u5730\u4f4d\uff0c\u4e5f\u53ef\u884c\u6b66\u6797\u5927 \u5178\uff0c\u4ee5\u4e00\u4ee3\u5b97\u4e3b\u7684\u8eab\u4efd\uff0c\u66ff\u522b\u6d3e\u7269\u8272\u4f20\u4eba\uff0c\u4f20\u4e66\u8d60\u5251\u3002\u201d\u51af\u7433\u773c\u73e0\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e8b\u4f55\u5fc5 \u4f60\u6765\u64cd\u5fc3\uff0c\u65e0\u6781\u6d3e\u7684\u4f20\u4eba\u65e9\u5c31\u6709\u4e86\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u8c01\uff1f\u4f60\u8bf4\u662f\u5e74\u7fb9\u5c27\u5417\uff1f\u5e74\u7fb9\u5c27\u867d\u7136\u662f\u949f\u4e07 \u5802\u7684\u5f92\u5f1f\uff0c\u4f46\u4ed6\u53db\u5e08\u6c42\u8363\uff0c\u4e3a\u6b66\u6797\u6240\u4e0d\u9f7f\u3002\u6211\u542c\u6bcd\u4eb2\u8bf4\u8fc7\uff0c\u6613\u8001\u524d\u8f88\u6709\u4e00\u6b21\u548c\u5979\u95f2\u8bdd\uff0c\u8fd8\u66fe \u60f3\u63d0\u5979\u4e00\u540c\u5177\u540d\uff0c\u901a\u544a\u6b66\u6797\u540c\u9053\uff0c\u66ff\u949f\u4e07\u5802\u6e05\u7406\u95e8\u6237\uff0c\u628a\u5e74\u7fb9\u5c27\u9010\u51fa\u65e0\u6781\u6d3e\u7684\u95e8\u5899\u5462\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u89c1\u51af\u7433\u53cc\u7709\u7d27\u8e69\uff0c\u9762\u8272\u4e0d\u9884\uff0c\u674e\u6cbb\u6115\u7136\u505c\u53e3\uff0c\u62c9\u8d77\u51af\u7433\u7684\u624b\u95ee\u9053\uff1a\u201c\u600e\u4e48 \u4f60\u4e0d\u8212\u670d\u5417\uff1f\u201d \u3000\u3000\u51af\u7433\u5fc3\u4e2d\u96be\u8fc7\u4e4b\u6781\uff0c\u5979\u7edd\u672a\u6599\u5230\u5979\u6240\u4f69\u773c\u7684\u5e74\u7fb9\u5c27\u539f\u6765\u5728\u6b66\u6797\u4fa0\u4e49\u7684\u773c\u4e2d\uff0c\u5374\u662f\u4e00\u4e2a\u574f \u86cb\u3002\u5979\u60f3\u95ee\u674e\u6cbb\u5e74\u7fb9\u5c27\u600e\u6837\u53db\u5e08\uff0c\u53ef\u662f\u53c8\u6015\u674e\u6cbb\u8bf4\u51fa\u4ee4\u5979\u66f4\u4f24\u5fc3\u7684\u4e8b\u60c5\uff0c\u7ec8\u4e8e\u5fcd\u4f4f\uff0c\u674e\u6cbb\u95ee \u9053\uff1a\u201c\u4f60\u8bf4\u65e0\u6781\u6d3e\u65e9\u6709\u4f20\u4eba\uff0c\u82e5\u4e0d\u662f\u6307\u5e74\u7fb9\u5c27\uff0c\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u51af\u7433\u5df2\u628a\u8c0e\u8bdd\u7f16\u597d\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8ba4\u8bc6\u7684\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u8c01\uff1f\u201d\u51af\u7433\u7b11\u9053\uff1a \u201c\u662f\u6211\uff01\u201d\u674e\u6cbb\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u6613\u4f2f\u6bcd\u600e\u820d\u5f97\u4f60\u6539\u6295\u5206\u6d3e\uff1f\u4f60\u662f\u5979\u552f\u4e00\u4f20\u4eba\uff0c\u4f60\u6539\u6295\u522b \u6d3e\uff0c\u5979\u4e0d\u662f\u767d\u8d39\u5341\u5e74\u5fc3\u8840\u5417\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u7684\u5e08\u5085\u8bf4\u5979\u672c\u60f3\u66ff\u65e0\u6781\u6d3e\u53e6\u627e\u4f20\u4eba\uff0c\u53ef\u662f\u82e5\u627e \u4e0d\u5230\u597d\u7684\u4fbf\u5bf9\u4e0d\u4f4f\u5085\u9752\u4e3b\uff0c\u8981\u627e\u597d\u7684\uff0c\u5979\u5e74\u7eaa\u8001\u8fc8\uff0c\u53c8\u6ca1\u6709\u8fd9\u4efd\u7cbe\u795e\u3002\u6240\u4ee5\u624d\u60f3\u53eb\u6211\u66ff\u65e0\u6781 \u6d3e\u91cd\u5f00\u95e8\u6237\u3002\u81f3\u4e8e\u5929\u5c71\u4e00\u6d3e\u7684\u4f20\u4eba\uff0c\u4e0d\u662f\u8fd8\u6709\u6211\u7684\u5510\u53d4\u53d4\u5417\uff1f\u201d\u51af\u7433\u65e9\u4ece\u674e\u6cbb\u53e3\u4e2d\uff0c\u77e5\u9053\u5929 \u5c71\u5404\u4eba\u7684\u5173\u7cfb\uff0c\u6240\u4ee5\u8bf4\u6765\u5934\u5934\u662f\u9053\u3002 \u3000\u3000\u674e\u6cbb\u4e00\u542c\uff0c\u5fc3\u9053\uff1a\u201c\u6613\u4f2f\u6bcd\u4e00\u4ee3\u5b97\u5e08\uff0c\u884c\u4e8b\u975e\u6211\u8f88\u6240\u80fd\u6599\u53ca\u3002\u5979\u53e4\u9053\u70ed\u80a0\uff0c\u5973\u4e2d\u82f1\u96c4\uff0c \u7adf\u80af\u820d\u5f03\u7231\u5f92\uff0c\u771f\u771f\u96be\u5f97\uff01\u201d\u53c8\u60f3\u8d77\u51af\u7433\u7cbe\u901a\u65e0\u6781\u6d3e\u7684\u5251\u672f\uff0c\u60f3\u662f\u6613\u5170\u73e0\u8981\u5979\u5b66\u7684\uff0c\u56e0\u6b64\u5bf9 \u5979\u7684\u8bdd\uff0c\u7adf\u7136\u6df1\u4fe1\u4e0d\u7591\u3002\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u7adf\u662f\u65e0\u6781\u6d3e\u672a\u6765\u7684\u5b97\u4e3b\u4e86\uff0c\u53ef\u559c\uff0c\u53ef\u8d3a\uff01\u201d \u3000\u3000\u51af\u7433\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u6211\u6709\u6743\u628a\u8fd9\u672c\u4e66\u9001\u7ed9\u4f60\u3002\u201d\u674e\u6cbb\u7b11\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u60f3\u6539\u6295\u522b\u6d3e\uff0c\u4f60\u7ed9\u6211 \u505a\u4ec0\u4e48\uff1f\u201d\u5fc3\u4e2d\u6697\u7b11\u5979\u5c0f\u5b69\u813e\u6c14\u3002 \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u6211\u6ca1\u4e1c\u897f\u9001\u4f60\uff0c\u8fd9\u672c\u4e66\u4f60\u975e\u8981\u4e0d\u53ef\u3002\u4f60\u653e\u5fc3\uff0c\u6211\u4e0d\u8981\u4f60\u6539\u6295\u65e0\u6781\u6d3e\u4fbf\u662f\u3002\u201d \u674e\u6cbb\u83ab\u9053\uff1a\u201c\u4e0d\u8981\u8fd9\u6837\u5b69\u5b50\u6c14\u3002\u8fd9\u662f\u4f60\u95e8\u6237\u4e2d\u7684\u5b9d\u7269\uff0c\u4e0d\u8be5\u968f\u4fbf\u9001\u4eba\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u4e0a\u5377\u7684 \u5251\u8bc0\u62f3\u7ecf\uff0c\u6211\u5df2\u719f\u8bb0\u5fc3\u4e2d\u3002\u4e0b\u518c\u7684\u533b\u4e66\uff0c\u6211\u5374\u662f\u4e00\u70b9\u4e0d\u61c2\uff0c\u770b\u7740\u4e5f\u89c9\u538c\u70e6\uff0c\u8981\u5b83\u4f55\u7528\uff1f\u201d \u3000\u3000\u674e\u6cbb\u542c\u5979\u63d0\u8d77\u533b\u4e66\uff0c\u773c\u775b\u4e00\u4eae\uff0c\u5ffd\u7136\u8d77\u4e86\u4e00\u4e2a\u5ff5\u5934\uff0c\u5728\u5fae\u5f31\u7684\u677e\u679d\u5149\u4e2d\uff0c\u201c\u91d1\u9488\u5ea6\u4e16\u201d \u56db\u4e2a\u91d1\u5b57\u7279\u522b\u89e6\u76ee\uff0c\u674e\u6cbb\u60f3\u8d77\u533b\u4e66\u4e2d\u6240\u8f7d\u7684\u79bb\u9b42\u602a\u75c7\uff0c\u5fc3\u9053\uff1a\u201c\u745b\u59b9\u6240\u8bf4\u7684\u751f\u75c5\u540e\u5fd8\u4e86\u5929\u5c71 \u5251\u6cd5\u4e4b\u4e8b\uff0c\u4e0d\u77e5\u662f\u771f\u662f\u5047\u3002\u201d\u5ff5\u5934\u4e00\u8f6c\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u628a\u4e0b\u5377\u533b\u4e66\u9001\u7ed9\u6211\u5427\u3002\u201d\u51af \u7433\u89c1\u4ed6\u80af\u8981\uff0c\u559c\u5b5c\u5b5c\u7684\u628a\u4e0b\u5377\u9012\u7ed9\u4e86\u4ed6\uff0c\u7b11\u9053\uff1a\u201c\u597d\u5566\uff0c\u4f60\u770b\u901a\u4e86\uff0c\u5c06\u6765\u6211\u6709\u4ec0\u4e48\u75c5\u90fd\u627e\u4f60 \u533b\u3002\u201d\u4e24\u4eba\u76f8\u5bf9\u4e00\u7b11\uff0c\u5404\u81ea\u7761\u7720\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u674e\u6cbb\u9192\u6765\uff0c\u51af\u7433\u5df2\u7ecf\u4e0d\u5728\u3002\u674e\u6cbb\u5fc3\u9053\uff1a\u201c\u4ed6\u4e00\u5b9a\u662f\u6253\u730e\u53bb\u4e86\u3002\u201d\u674e\u6cbb\u6628\u665a \u5403\u4e86\u4e00\u53ea\u7f8a\u817f\uff0c\u53c8\u7761\u4e86\u4e00\u89c9\uff0c\u7cbe\u795e\u5df2\u5b8c\u5168\u6062\u590d\uff0c\u8dd1\u51fa\u6d1e\u53e3\uff0c\u5438\u4e86\u4e00\u53e3\u65b0\u9c9c\u7a7a\u6c14\uff0c\u6625\u9633\u70c2\u707f\uff0c \u9e1f\u8bed\u82b1\u9999\uff0c\u6ee1\u5c71\u90fd\u662f\u751f\u6c14\u3002\u674e\u6cbb\u6253\u4e86\u4e00\u5957\u201c\u9f99\u5f62\u516b\u638c\u201d\uff0c\u8212\u6563\u7b4b\u9aa8\uff0c\u4fbf\u8dd1\u5230\u5c71\u4e2d\u5bfb\u89c5\u51af\u7433\u3002 \u3000\u3000\u5d69\u5c71\u5cf0\u5ce6\uff0c\u5343\u6001\u4e07\u72b6\uff0c\u5982\u4e1b\u7b0b\u63d2\u5929\uff0c\u5217\u6208\u8000\u65e5\uff0c\u674e\u6cbb\u722c\u4e0a\u4e00\u5904\u5c71\u5cf0\uff0c\u7b95\u8e1e\u77f3\u4e0a\uff0c\u811a\u5e95\u7684 \u6d41\u6cc9\u98de\u7011\uff0c\u604d\u5982\u6cfb\u7389\u9e23\u91d1\uff0c\u5934\u9876\u7684\u6de1\u96fe\u8f7b\u4e91\uff0c\u4fe8\u82e5\u7b3c\u7eb1\u98d8\u5e26\uff0c\u5c71\u666f\u96c4\u5947\u79c0\u4e3d\u517c\u800c\u6709\u4e4b\u3002\u53ea\u662f \u8fd9\u5ea7\u5c71\u5cf0\u7684\u5bf9\u9762\uff0c\u4fbf\u662f\u5c11\u5ba4\u5c71\u5317\u9e93\u7684\u4e94\u4e73\u5cf0\u4e0b\uff0c\u5927\u706b\u8fc7\u540e\uff0c\u8349\u6728\u7126\u9ec4\uff0c\u4e00\u7247\u5149\u79c3\u79c3\u7684\uff0c\u5728\u7fa4 \u5cf0\u7fe0\u62e5\u4e4b\u4e2d\uff0c\u663e\u5f97\u975e\u5e38\u4e0d\u8c03\u548c\uff0c\u975e\u5e38\u4e4b\u820d\u4eba\u96be\u53d7\u3002\u674e\u6cbb\u5fc3\u9053\uff1a\u201c\u8fd9\u573a\u706b\u4e0d\u77e5\u662f\u600e\u6837\u70e7\u8d77\u6765 \u7684\uff0c\u771f\u715e\u98ce\u666f\uff01\u201d\u770b\u7740\u788d\u773c\uff0c\u7d22\u6027\u8dd1\u5165\u6797\u4e2d\uff0c\u91c7\u6458\u91ce\u82b1\u3002 \u3000\u3000\u9759\u5bc2\u7a7a\u5c71\uff0c\u7a81\u4f20\u6765\u4eba\u58f0\u7b11\u8bed\u3002\u674e\u6cbb\u4e00\u95ea\uff0c\u95ea\u5230\u5927\u77f3\u540e\u9762\u3002\u4e00\u884c\u4eba\u8fdc\u8fdc\u8d70\u6765\uff0c\u8d70\u5230\u77f3\u524d\uff0c \u5ffd\u7136\u7ad9\u7740\u3002\u674e\u6cbb\u5077\u773c\u4e00\u77a7\uff0c\u53ea\u89c1\u4e00\u4e09\u5341\u6765\u5c81\u7684\u6c49\u5b50\uff0c\u9526\u62b1\u7ee3\u5e26\uff0c\u6c14\u5ea6\u9ad8\u534e\uff0c\u4e00\u53ea\u811a\u8e0f\u5728\u77f3 \u4e0a\uff0c\u813e\u7768\u987e\u76fc\uff0c\u53cc\u76ee\u95ea\u95ea\u6709\u5149\uff0c\u5176\u4ed6\u7684\u4eba\uff0c\u5206\u5217\u5de6\u53f3\uff0c\u4f3c\u4e4e\u662f\u4ed6\u5e26\u6765\u7684\u968f\u4ece\uff0c\u6700\u9760\u8fd1\u4ed6\u7684 \u4eba\uff0c\u957f\u76f8\u5341\u5206\u5a01\u6b66\uff0c\u674e\u6cbb\u4e00\u89c1\uff0c\u51e0\u4e4e\u53eb\u51fa\u58f0\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u6b63\u662f\u65b0\u638c\u5175\u6743\u7684\u5f81\u897f\u5927\u5c06\u519b\u5e74\u7fb9\u5c27\uff0c\u53ea\u89c1\u4ed6\u8c04\u8c00\u7b11\u9053\uff1a\u201c\u7687\u4e0a\u795e\u673a\u5999\u7b97\uff0c\u5a01\u5fb7\u517c \u65bd\uff0c\u767b\u4f4d\u4ee5\u6765\uff0c\u4e0d\u8fc7\u6570\u6708\uff0c\u5373\u56db\u6d77\u7fc1\u4ece\uff0c\u7fa4\u4e11\u602f\u670d\uff0c\u5141\u5824\u7edf\u7387\u5eff\u4e07\u96c4\u5e08\u800c\u675f\u624b\u53d7\u64d2\uff0c\u5c11\u6797\u5bfa \u7a7a\u5938\u6280\u51fb\u65e0\u53cc\uff0c\u4ea6\u7070\u98de\u70df\u706d\uff0c\u5373\u79e6\u7687\u6c49\u6b66\u5510\u5b97\u5b8b\u7956\uff0c\u4e5f\u4e0d\u80fd\u4e0e\u7687\u4e0a\u76f8\u6bd4\u3002\u201d\u674e\u6cbb\u5927\u5403\u4e00\u60ca\uff0c \u60f3\u4e0d\u5230\u8fd9\u4eba\u7adf\u662f\u5f53\u4eca\u7687\u5e1d\uff08\u5141\u796f\uff09\u3002 \u3000\u3000\u5141\u796f\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u5e74\u5927\u5c06\u519b\u4f60\u7684\u529f\u52b3\u3002\u201d\u5e74\u7fb9\u5c27\u6307\u7740\u4e0b\u9762\u4e94\u4e73\u5cf0\u4e0b\u7684\u74e6\u783e \u573a\uff0c\u8bf4\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u7684\u4e94\u767e\u5bfa\u50e7\uff0c\u88ab\u70c8\u706b\u6240\u711a\uff0c\u65e0\u4e00\u4eba\u6562\u51fa\u6765\u62b5\u6297\uff0c\u8db3\u89c1\u7687\u4e0a\u5723\u5a01\u3002\u201d\u5141\u796f \u54c8\u54c8\u5927\u7b11\uff0c\u5ffd\u559f\u7136\u53f9\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u7684\u5343\u5e74\u53e4\u5239\uff0c\u6bc1\u4e8e\u4e00\u65e6\uff0c\u867d\u4e91\u81ea\u53d6\uff0c\u6715\u4ea6\u5fc3\u4f24\u3002\u201d\u5e74\u7fb9\u5c27 \u5fd9\u9053\uff1a\u201c\u7687\u4e0a\u5bbd\u6d2a\u4ec1\u539a\uff0c\u53ea\u53ef\u60dc\u5c11\u6797\u5bfa\u7684\u50e7\u4eba\u4e0d\u61c2\u671d\u5ef7\u793c\u6cd5\uff0c\u4e0d\u80fd\u65e9\u65e9\u4f53\u4f1a\u5723\u601d\u3002\u201d\u674e\u6cbb\u542c \u4e86\uff0c\u53ea\u611f\u5230\u4e00\u9635\u6076\u5fc3\uff0c\u4e0d\u671f\u7136\u6253\u4e86\u4e2a\u5bd2\u5664\u3002 \u3000\u3000\u5141\u796f\u53c8\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u6bc1\u4e86\u4e5f\u9887\u53ef\u60dc\uff0c\u5f85\u6715\u56de\u4eac\u4e4b\u540e\uff0c\u5f53\u518d\u547d\u6cb3\u5357\u5de1\u629a\u91cd\u4fee\u5e99\u5b87\uff0c\u53e6\u62db\u4e00 \u73ed\u6709\u5fb7\u7684\u50e7\u4eba\u4e3b\u6301\u3002\u201d\u674e\u6cbb\u542c\u5230\u8fd9\u91cc\uff0c\u5ffd\u89c9\u524d\u9762\u906e\u7740\u4ed6\u7684\u90a3\u5757\u77f3\u5934\uff0c\u5fae\u5fae\u6447\u6643\u3002 \u3000\u3000\u674e\u6cbb\u8eab\u5b50\u4e00\u7f29\uff0c\u7a81\u7136\u95fb\u5f97\u5de8\u58f0\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u5feb\u6eda\u51fa\u6765\uff01\u201d\u5927\u77f3\u8f70\u7136\u5012\u4e0b\uff0c\u674e\u6cbb\u7eb5\u8eab\u8df3 \u51fa\uff0c\u5df2\u9677\u5728\u5305\u56f4\u4e4b\u4e2d\uff01 \u3000\u3000\u539f\u6765\u5141\u796f\u81ea\u706b\u711a\u5c11\u6797\u4e4b\u540e\uff0c\u7559\u4e0b\u6d77\u4e91\u548c\u5c1a\u770b\u5b88\uff0c\u8fc7\u4e86\u4e00\u6708\uff0c\u4e0d\u89c1\u4ed6\u56de\u62a5\uff0c\u53c8\u60f3\u770b\u5c11\u6797\u5bfa \u706b\u5316\u4e4b\u540e\uff0c\u60c5\u5f62\u5982\u4f55\uff0c\u4e00\u65f6\u5174\u8d77\uff0c\u5e26\u4e86\u5929\u53f6\u6563\u4eba\u3001\u54c8\u5e03\u9640\u7b49\u518d\u4e0a\u5d69\u5c71\u3002\u6b64\u65f6\u7a81\u7136\u53d1\u73b0\u57cb\u4f0f\uff0c \u5141\u796f\u4ecd\u662f\u795e\u8272\u81ea\u5982\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u6325\u624b\u8bf4\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u88ab\u711a\u4e4b\u540e\uff0c\u6b66\u6797\u5404\u6d3e\u5b97\u4e3b\uff0c\u65e0\u4e00\u6562\u6765\uff0c \u6b64\u4eba\u5c45\u7136\u6562\u4e0a\u5d69\u5c71\uff0c\u80c6\u91cf\u4e0d\u5c0f\u3002\u4f60\u7b49\u4e14\u5f85\u9000\u4e0b\uff0c\u5f85\u6715\u95ee\u4ed6\u3002\u201d \u3000\u3000\u674e\u6cbb\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u95ee\u4ec0\u4e48\uff1f\u201d\u5141\u796f\u9053\uff1a\u201c\u4f60\u662f\u5c11\u6797\u5bfa\u7684\u4fd7\u5bb6\u5f1f\u5b50\u5417\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4e0d \u662f\u3002\u201d\u5141\u796f\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u548c\u5c11\u6797\u5bfa\u7684\u957f\u8001\u6709\u4ec0\u4e48\u4ea4\u60c5\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u7684\u957f\u8001\u5fb7\u9ad8\u671b \u91cd\uff0c\u6211\u8fd8\u4e0d\u914d\u548c\u4ed6\u4eec\u62c9\u4e0a\u4ea4\u60c5\u3002\u201d\u5e74\u7fb9\u5c27\u9762\u8272\u4e00\u53d8\uff0c\u5141\u796f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u8981\u4e0a\u7bd9 \u5c71\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4f60\u548c\u5c11\u6797\u5bfa\u6709\u4ec0\u4e48\u7ea0\u845b\uff0c\u4e3a\u4ec0\u4e48\u4f60\u53c8\u8981\u706b\u711a\u53e4\u5239\uff0c\u4e24\u5230\u5d69\u5c71\uff1f\u201d\u968f\u4ece\u559d \u9053\uff1a\u201c\u5927\u80c6\u5339\u592b\uff0c\u9876\u649e\u7687\u4e0a\uff01\u201d\u5141\u796f\u7709\u5934\u4e00\u76b1\u3002\u5fc3\u9053\uff1a\u201c\u597d\u4e2a\u5f3a\u9879\u4e0d\u6015\u6b7b\u7684\u5c0f\u5b50\uff0c\u82e5\u80fd\u5c06\u4ed6 \u6536\u670d\uff0c\u5012\u662f\u4e2a\u53ef\u7528\u4e4b\u624d\u3002\u201d \u3000\u3000\u4e00\u4e2a\u968f\u4ece\u7980\u9053\uff1a\u201c\u5fa1\u6797\u519b\u7edf\u9886\u79e6\u4e2d\u8d8a\u7684\u5c38\u8eab\u5df2\u7ecf\u53d1\u73b0\uff0c\u6d77\u4e91\u548c\u5c1a\u5219\u8fd8\u672a\u627e\u5230\uff0c\u8bf7\u7687\u4e0a\u8d50 \u547d\u5c06\u8fd9\u5c0f\u8d3c\u64d2\u4e0b\uff0c\u4e25\u52a0\u5ba1\u95ee\u3002\u770b\u662f\u4ed6\u4e00\u4eba\u6240\u4e3a\uff0c\u8fd8\u662f\u53e6\u6709\u540c\u515a\uff1f\u201d \u3000\u3000\u674e\u6cbb\u5fc3\u60f3\uff1a\u770b\u6765\u4eca\u65e5\u4e07\u96be\u9003\u8131\uff0c\u53ef\u4e0d\u8981\u7275\u7d2f\u4e86\u745b\u59b9\u3002\u4e8e\u662f\u4e0d\u5f85\u5141\u796f\u95ee\u4ed6\uff0c\u7387\u5148\u7b54\u9053\uff1a \u201c\u5168\u662f\u6211\u4e00\u4eba\u505a\u7684\uff01\u201d\u5141\u796f\u95ee\u9053\uff1a\u201c\u79e6\u4e2d\u8d8a\u88ab\u4f60\u6740\u4e86\uff0c\u90a3\u548c\u5c1a\u5462\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u88ab\u6211\u523a\u4f24\uff0c \u65e0\u4eba\u6551\u6cbb\uff0c\u60f3\u5fc5\u4e5f\u6b7b\u4e86\uff01\u201d\u968f\u4ece\u5747\u6012\uff0c\u4fbf\u60f3\u52a8\u624b\u3002\u5141\u796f\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u4f60\u53ef\u522b\u4e71\u5439 \u725b\uff0c\u51ed\u4f60\u4e00\u4eba\uff0c\u5c31\u80fd\u9003\u5f97\u8fc7\u97e9\u91cd\u5c71\u7684\u641c\u67e5\uff0c\u53c8\u80fd\u6740\u4f24\u6715\u7684\u4e24\u4e2a\u9ad8\u624b\u5417\uff1f\u201d \u3000\u3000\u674e\u6cbb\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u8fd9\u4e2a\u7687\u5e1d\u96be\u9053\u7adf\u662f\u4e2a\u6df1\u901a\u6b66\u827a\u4e4b\u4eba\uff1f\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u7687\u4e0a\u660e\u89c1\uff0c\u8fd9\u5c0f\u5b50 \u4e00\u5b9a\u8fd8\u6709\u515a\u7fbd\u3002\u8bf7\u4f20\u4ee4\u53eb\u5fa1\u6797\u519b\u641c\u5c71\uff01\u201d \u3000\u3000\u674e\u51b6\u4e00\u6025\uff0c\u51b2\u53e3\u8bf4\u9053\uff0c\u201c\u4ec0\u4e48\u9ad8\u624b\uff0c\u4e0d\u8fc7\u662f\u8113\u5305\u7f62\u4e86\uff01\u90a3\u4e24\u4e2a\u8113\u5305\u5c31\u662f\u4f60\u6d3e\u6765\u770b\u5b88\u5d69\u5c71 \u7684\u4eba\u5417\uff1f\u54c8\u54c8\uff01\u201d\u65c1\u8fb9\u7684\u5929\u53f6\u6563\u4eba\u6012\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u82e5\u4e0d\u6559\u8bad\u8fd9\u4e2a\u5c0f\u8d3c\uff0c\u54b1\u4eec\u5bab\u5ef7\u536b\u58eb\u58f0\u540d\u626b \u5730\u3002\u5141\u796f\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u54c8\u5e03\u9640\u62db\u4e86\u8fc7\u6765\uff0c\u4f4e\u58f0\u5490\u5429\u51e0\u53e5\uff0c\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u4f60\u522b\u4ee5\u4e3a\u80fd\u4e0a\u5d69\u5c71 \u4fbf\u6709\u4e86\u4e0d\u8d77\u7684\u80fd\u4e3a\uff0c\u522b\u4ee5\u4e3a\u80fd\u6740\u4f24\u6715\u4e00\u4e24\u4e2a\u4eba\u4fbf\u9a84\u5984\u81ea\u5927\uff0c\u6715\u4eca\u65e5\u5e26\u6765\u7684\u968f\u4ece\uff0c\u968f\u4fbf\u4f60\u9009\u4e00 \u4e2a\u6765\u6597\uff0c\u53ea\u8981\u4f60\u80fd\u6597\u4e2a\u5e73\u624b\uff0c\u6715\u4fbf\u653e\u4f60\u4e0b\u5c71\u3002\u201d\u674e\u6cbb\u81ea\u5ff5\u6a2a\u7ad6\u4e00\u6b7b\uff0c\u4f55\u5fc5\u6015\u4ed6\uff0c\u62d4\u5251\u559d\u9053\uff1a \u201c\u6211\u4e5f\u968f\u4fbf\u4f60\u6d3e\u51fa\u4eba\u6765\uff0c\u4e00\u4e2a\u6765\u4fbf\u6597\u4e00\u4e2a\uff0c\u4e24\u4e2a\u6765\u4fbf\u6597\u4e00\u53cc\uff01\u201d\u58f0\u97f3\u6545\u610f\u8bf4\u5f97\u5341\u5206\u5b8f\u4eae\uff0c\u60f3 \u53eb\u51af\u7433\u542c\u5230\uff0c\u597d\u89c1\u673a\u8eb2\u85cf\u3002 \u3000\u3000\u5141\u796f\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u53e3\u51fa\u5927\u8a00\uff0c\u54c8\u5e03\u9640\u4f60\u6597\u4ed6\u5427\uff01\u201d\u54c8\u5e03\u9640\u5e94\u58f0\u800c\u51fa\uff0c\u5728\u8170\u95f4\u53d6\u51fa\u4e24\u4e2a \u6d41\u661f\u9524\uff0c\u547c\u7684\u4e00\u629b\uff0c\u5411\u674e\u6cbb\u5de6\u53f3\u5939\u51fb\uff01 \u3000\u3000\u674e\u6cbb\u89c1\u4ed6\u6765\u52bf\u51f6\u731b\uff0c\u63c9\u8eab\u4e00\u95ea\uff0c\u7a81\u4f3c\u7075\u732b\u6251\u9f20\uff0c\u4e00\u5251\u6025\u8fdb\uff0c\u5251\u628a\u4e00\u98a4\uff0c\u5251\u5c16\u95ea\u7535\u80a1\u7684\u5411 \u54c8\u5e03\u9640\u4e2d\u8def\u523a\u53bb\uff0c\u54c8\u5e03\u9640\u987a\u7740\u5251\u52bf\uff0c\u811a\u8ddf\u4e00\u65cb\uff0c\u5de6\u624b\u6d41\u661f\u9524\u7838\u5411\u5251\u8eab\u3002\u90a3\u77e5\u674e\u6cbb\u7684\u5251\u6cd5\u4e0e\u4f17 \u4e0d\u540c\uff0c\u770b\u4ed6\u523a\u5411\u4e2d\u8def\uff0c\u5374\u662f\u6233\u5411\u4e0b\u76d8\uff0c\u54c8\u5e03\u9640\u4e00\u9524\u7838\u7a7a\uff0c\u654c\u4eba\u7684\u5251\u5c16\u5df2\u6233\u5230\u819d\u76d6\uff0c\u5927\u5403\u4e00 \u60ca\uff0c\u5de6\u8db3\u817e\u5730\u98de\u8d77\uff0c\u978b\u5e95\u4e00\u6cbe\u5251\u5c16\uff0c\u5012\u9000\u4e09\u6b65\uff0c\u5141\u796f\u559d\u91c7\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff01\u201d \u3000\u3000\u54c8\u5e03\u9640\u4e43\u5b98\u4e2d\u536b\u58eb\u7684\u603b\u7ba1\uff0c\u4e0e\u4e86\u56e0\u5929\u53f6\u6563\u4eba\u7b49\u5e76\u9a7e\u9f50\u9a71\uff0c\u542c\u5f97\u7687\u4e0a\u7ed9\u5bf9\u65b9\u559d\u91c7\uff0c\u6df1\u611f\u989c \u9762\u65e0\u5149\uff0c\u53cc\u9524\u65cb\u98ce\u6025\u821e\uff0c\u4ea4\u53c9\u8fdb\u6251\u3002\u4ed6\u7684\u529f\u529b\u4e4b\u9ad8\uff0c\u4e0e\u4e86\u56e0\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u521a\u624d\u90a3\u4e00\u5251\u4e43\u662f\u8f7b\u654c \u5927\u610f\uff0c\u4e00\u65f6\u758f\u5ffd\uff0c\u5982\u4eca\u8981\u5728\u7687\u4e0a\u9762\u524d\uff0c\u4e89\u56de\u9762\u5b50\uff0c\u53cc\u9524\u6025\u821e\uff0c\u547c\u547c\u631f\u98ce\uff0c\u5a01\u529b\u715e\u662f\u60ca\u4eba\u3002\u674e \u6cbb\u7684\u5251\u6cd5\u867d\u7136\u5947\u8be1\u4e4b\u6781\uff0c\u88ab\u4ed6\u53cc\u9524\u7d27\u8feb\uff0c\u5374\u662f\u65bd\u5c55\u4e0d\u5f97\u3002\u5e78\u559c\u674e\u6cbb\u4e0b\u5c71\u4ee5\u6765\uff0c\u7ecf\u4e86\u51e0\u6b21\u9635 \u4ed7\uff0c\u672c\u9886\u53c8\u6bd4\u5728\u676d\u5dde\u6597\u4e86\u56e0\u4e4b\u65f6\u9ad8\u4e86\u4e0d\u5c11\uff0c\u8981\u4e0d\u7136\u65e9\u5c31\u88ab\u4ed6\u64d2\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u8f97\u8f6c\u653b\u62d2\uff0c\u6597\u4e86\u516d\u4e03\u5341\u62db\uff0c\u5141\u796f\u5bf9\u5929\u53f6\u6563\u4eba\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5c45\u7136\u80fd\u4e0e\u54c8 \u603b\u7ba1\u6597\u8fd9\u4e48\u4e45\uff0c\u4e5f\u7b97\u662f\u96be\u5f97\u7684\u4e86\uff01\u201d\u5929\u53f6\u9053\uff1a\u201c\u4e0d\u8fc7\u4ed6\u6c14\u529b\u6e10\u8870\uff0c\u6700\u591a\u4e5f\u53ea\u80fd\u652f\u6301\u4e94\u5341\u62db \u4e86\u3002\u201d\u5141\u796f\u5ffd\u9053\uff1a\u201c\u4ed6\u7684\u5251\u6cd5\u9887\u4e3a\u602a\u5f02\uff0c\u4f60\u8ba4\u5f97\u5417\uff1f\u201d\u5929\u53f6\u6563\u4eba\u9762\u4e0a\u4e00\u7ea2\uff0c\u4ed6\u4e43\u4e00\u6d3e\u5b97\u5e08\uff0c \u89c1\u591a\u8bc6\u5e7f\uff0c\u5374\u8ba4\u4e0d\u5f97\u674e\u6cbb\u7684\u5251\u6cd5\u3002\u5141\u796f\u5fc3\u601d\u8fc7\u4eba\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u5929\u5c71\u5251\u6cd5\u548c\u7384\u5973\u5251\u6cd5\u6211 \u90fd\u89c1\u8fc7\uff0c\u6b64\u4eba\u5251\u6cd5\u65e0\u5929\u5c71\u5251\u6cd5\u53d8\u5316\u4e4b\u7e41\u590d\uff0c\u4ea6\u65e0\u7384\u5973\u5251\u6cd5\u53d8\u5316\u4e4b\u7cbe\u5fae\u3002\u4f46\u5947\u8be1\u4e4b\u5904\u5374\u53c8\u4f3c\u5728 \u4e24\u5bb6\u4e4b\u4e0a\uff0c\u770b\u6765\u4e0d\u662f\u8fbe\u6469\u5251\u6cd5\u4fbf\u662f\u767d\u53d1\u9b54\u5973\u7684\u72ec\u95e8\u5251\u6cd5\u4e86\u3002\u201d\u5141\u796f\u5728\u5c11\u6797\u51fa\u8eab\uff0c\u5bf9\u5404\u5bb6\u5251 \u6cd5\uff0c\u672a\u66fe\u76ee\u51fb\u4ea6\u6709\u8033\u95fb\uff0c\u6545\u6b64\u8bf4\u6765\u751a\u6709\u89c1\u5730\u3002\u5929\u53f6\u6563\u4eba\u4e00\u60f3\uff0c\u609a\u7136\u6697\u60ca\uff0c\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u6b64\u4eba \u7684\u5251\u6cd5\u5b9a\u662f\u767d\u53d1\u9b54\u5973\u7684\u72ec\u95e8\u5251\u6cd5\uff0c\u53ea\u6015\u4ed6\u4fbf\u662f\u6b66\u743c\u7476\u7684\u513f\u5b50\u3002\u6b66\u743c\u7476\u51fa\u624b\u72e0\u8fa3\uff0c\u6bd4\u6613\u5170\u73e0\u66f4 \u4e3a\u96be\u6597\u3002\u6211\u4eec\u4e0d\u8981\u968f\u4fbf\u4f24\u4ed6\u3002\u201d\u5141\u796f\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5c31\u5429\u5490\u54c8\u603b\u7ba1\u4e86\u3002\u6211\u5012\u4e0d\u7ba1\u4ed6\u662f\u8c01\u7684\u513f \u5b50\uff0c\u53ea\u662f\u6b64\u4eba\u6709\u6b64\u80c6\u91cf\uff0c\u90fd\u662f\u96be\u5f97\u7684\u4eba\u624d\u3002\u201d \u3000\u3000\u53c8\u6597\u4e86\u4e09\u5341\u6765\u62db\uff0c\u674e\u6cbb\u6c14\u529b\u4e0d\u52a0\uff0c\u679c\u7136\u73b0\u51fa\u8d25\u8c61\uff0c\u4f46\u54c8\u5e03\u9640\u8981\u60f3\u628a\u4ed6\u751f\u64d2\uff0c\u5374\u4e5f\u9887\u4e0d\u5bb9 \u6613\u3002\u987b\u77e5\u9ad8\u624b\u8f83\u6280\uff0c\u653b\u62d2\u4e4b\u9645\uff0c\u95f4\u4e0d\u5bb9\u53d1\uff0c\u90a3\u80fd\u4f38\u8fdb\u624b\u53bb\u3002\u54c8\u5e03\u9640\u867d\u6280\u80dc\u4e00\u7b79\uff0c\u4f46\u975e\u628a\u674e\u6cbb \u6253\u4f24\u5c31\u65e0\u6cd5\u5c06\u4ed6\u751f\u64d2\uff0c\u504f\u504f\u5141\u796f\u53c8\u4e0d\u8ba9\u54c8\u5e03\u9640\u4f24\u4ed6\u3002\u674e\u6cbb\u7684\u5251\u4f38\u7f29\u4e0d\u5b9a\uff0c\u53d8\u5316\u83ab\u6d4b\uff0c\u54c8\u5e03\u9640 \u51e0\u6b21\u60f3\u628a\u4ed6\u5b9d\u5251\u6253\u98de\uff0c\u90fd\u88ab\u4ed6\u5de7\u5999\u7684\u907f\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u54c8\u5e03\u9640\u5fc3\u5934\u7126\u8e81\uff0c\u7a81\u7136\u60f3\u51fa\u4e00\u4e2a\u5999\u6cd5\uff0c\u53cc\u9524\u4e00\u7d27\uff0c\u6740\u624b\u8fde\u65bd\uff0c\u770b\u7740\u5c31\u8981\u628a\u674e\u6cbb\u6bd9\u4e8e\u9524 \u4e0b\u3002\u54c8\u5e03\u9640\u6bcf\u5c55\u4e00\u62db\u6740\u624b\uff0c\u5c31\u5927\u58f0\u559d\u4ed6\u6295\u964d\u3002\u5c82\u77e5\u674e\u6cbb\u662f\u62fc\u4e86\u6b7b\u547d\u6765\u6597\uff0c\u4e1d\u6beb\u4e0d\u60e7\uff0c\u6025\u6597 \u4e2d\uff0c\u54c8\u5e03\u9640\u7684\u6d41\u661f\u9524\u6bcf\u6bcf\u53ea\u4ece\u4ed6\u5934\u9876\u4e94\u5bf8\u4e4b\u4e0a\u98de\u8fc7\uff0c\u7aef\u7684\u975e\u5e38\u60ca\u4eba\u3002\u674e\u6cbb\u6012\u9053\uff1a\u201c\u4f60\u60f3\u8981\u6211 \u6295\u964d\uff0c\u90a3\u662f\u505a\u4f60\u5988\u7684\u6625\u79cb\u5927\u68a6\uff01\u201d\u5251\u8bc0\u4e00\u9886\uff0c\u8f6c\u5b88\u4e3a\u653b\uff0c\u62fc\u547d\u53cd\u6251\uff0c\u6b63\u5728\u7d27\u5f20\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97 \u4e00\u58f0\u5c16\u53eb\uff0c\u51af\u7433\u5df2\u51fa\u73b0\u6797\u4e2d\u3002 \u3000\u3000\u674e\u6cbb\u53eb\u9053\uff1a\u201c\u745b\u59b9\u3001\u4f60\u5feb\u9003\uff01\u201d\u5fc3\u795e\u4e00\u5206\uff0c\u5b9d\u5251\u7adf\u7ed9\u654c\u4eba\u4e00\u9524\u6253\u98de\u3002\u8033\u8fb9\u542c\u5f97\u5141\u796f\u559d \u9053\uff1a\u201c\u505c\u624b\uff01\u201d \u3000\u3000\u51af\u7433\u542c\u5f97\u53ae\u6740\u4e4b\u58f0\uff0c\u6478\u5230\u6797\u8fb9\uff0c\u7a81\u89c1\u5141\u796f\u5e26\u9886\u8bb8\u591a\u9ad8\u624b\uff0c\u65c1\u7acb\u89c2\u6218\uff0c\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u672c \u6765\u60f3\u9003\uff0c\u4f46\u4e00\u77a5\u4e4b\u4e0b\uff0c\u89c1\u674e\u6cbb\u7ed9\u54c8\u5e03\u9640\u56f0\u4f4f\uff0c\u5371\u9669\u4e4b\u6781\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4e0d\u6551\u4ed6\uff0c\u4ed6\u6027\u547d\u96be \u4fdd\u3002\u201d\u6b64\u65f6\u6b64\u9645\uff0c\u51af\u7433\u518d\u4e5f\u987e\u4e0d\u5f97\u5141\u796f\u903c\u5a5a\u7684\u5a01\u80c1\uff0c\u62d4\u51fa\u6bd2\u5200\uff0c\u84e6\u7136\u8df3\u4e0a\u524d\u53bb\u3002 \u3000\u3000\u5141\u796f\u53c8\u60ca\u53c8\u559c\uff0c\u7b11\u9053\uff1a\u201c\u7433\u4e2b\u5934\uff0c\u4f60\u73a9\u591f\u4e86\u5427\uff0c\u8fd8\u4e0d\u968f\u6211\u56de\u53bb\uff01\u201d\u51af\u7433\u5c06\u6bd2\u5200\u5bf9\u7740\u5fc3 \u7a9d\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u82e5\u628a\u6211\u7684\u674e\u54e5\u54e5\u4f24\u4e86\uff0c\u6211\u4e5f\u4e0d\u518d\u6d3b\u5566\uff01\u201d\u5141\u796f\u7b11\u9053\uff1a\u201c\u8c01\u8981\u4f24\u4ed6\uff0c\u4f60\u8fc7 \u6765\u5427\uff01\u201d \u3000\u3000\u674e\u6cbb\u521a\u624d\u5728\u654c\u4eba\u5305\u56f4\u4e4b\u4e0b\uff0c\u5140\u7136\u4e0d\u60e7\uff0c\u6b64\u9645\u89c1\u51af\u7433\u5411\u7687\u5e1d\u6c42\u60c5\uff0c\u8fd9\u4e00\u60ca\u5374\u662f\u975e\u540c\u5c0f\u53ef\uff01 \u51e0\u4e4e\u7591\u5fc3\u5728\u6076\u68a6\u4e4b\u4e2d\u3002\u84e6\u89c9\u624b\u8155\u4e00\u9ebb\uff0c\u54c8\u5e03\u9640\u4e09\u6307\u5df2\u6263\u7d27\u4ed6\u7684\u8109\u95e8\u3002 \u3000\u3000\u51af\u7433\u4e00\u6b65\u6b65\u8d70\u5c06\u8fc7\u6765\u3002\u5e74\u7fb9\u5c27\u5fc3\u4e2d\u4e00\u9707\uff0c\u751f\u6015\u51af\u7433\u8bf4\u51fa\u66fe\u8eb2\u5728\u4ed6\u5bb6\uff0c\u5411\u5979\u629b\u4e86\u4e00\u4e2a\u773c \u8272\u3002\u51af\u7433\u597d\u4f3c\u5168\u65e0\u77e5\u89c9\uff0c\u6728\u7136\u7684\u8d70\u5230\u7687\u5e1d\u8eab\u65c1\u3002\u201c\u541b\u4e3b\u65e0\u620f\u8a00\uff0c\u4f60\u6536\u4e86\u6bd2\u5200\uff0c\u6211\u4e0d\u4f24\u4ed6\u4fbf \u662f\u3002\u201d\u51af\u7433\u7709\u6bdb\u4e00\u626c\uff0c\u628a\u5200\u653e\u56de\u6697\u5668\u56ca\u4e2d\uff0c\u9053\uff1a\u201c\u56db\u8d1d\u52d2\uff0c\u6211\u5411\u4f60\u8bf7\u5b89\u6765\u5566\uff01\u201d\u54c8\u5e03\u9640\u9053\uff1a \u201c\u4f60\u89c1\u4e86\u7687\u4e0a\u8fd8\u4e0d\u4e0b\u8dea\uff1f\u201d\u7528\u610f\u662f\u63d0\u9192\u5979\u6539\u6362\u79f0\u547c\u3002\u5141\u796f\u7b11\u9053\uff1a\u201c\u5979\u6dd8\u6c14\u60ef\u4e86\uff0c\u4e0d\u5fc5\u62d8 \u793c\u3002\u201d\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u5230\u5904\u4e71\u95ef\uff0c\u5403\u591f\u4e86\u82e6\u5427\u3002\u54b1\u4eec\u4ee5\u524d\u7684\u7687\u5e9c\uff0c\u6539\u4e86\u96cd\u548c\u5bab\uff0c\u4f60\u7684\u623f\u95f4\u8fd8\u662f \u539f\u6765\u7684\u6837\u5b50\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u628a\u4ed6\u653e\u4e86\uff0c\u6211\u4fbf\u56de\u53bb\u3002\u201d\u5141\u7948\u7b11\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u56de\u5230\u4eac\u540e\uff0c\u6211\u4fbf \u653e\u4ed6\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u541b\u4e3b\u65e0\u620f\u8a00\uff0c\u6211\u8ddf\u4f60\u53e3\u53bb\uff01\u201d \u3000\u3000\u674e\u6cbb\u76ee\u77aa\u53e3\u5446\uff0c\u51af\u7433\u8fde\u53eb\u4e24\u58f0\uff0c\u4ed6\u90fd\u672a\u66fe\u7b54\u5e94\u3002\u5141\u796f\u628a\u624b\u4e00\u6325\uff0c\u54c8\u5e03\u9640\u62d6\u4e86\u674e\u6cbb\uff0c\u5148\u884c \u4e0b\u5c71\u3002\u5141\u796f\u9053\uff1a\u201c\u5979\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u4f60\u548c\u4ed6\u5f88\u8981\u597d\u662f\u4e0d\u662f\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u5979\u662f\u6211\u7684\u7ed3\u4e49\u54e5 \u54e5\uff0c\u5f53\u7136\u662f\u5f88\u597d\u7684\u4e86\uff01\u201d\u4e00\u9762\u56de\u7b54\uff0c\u4e00\u9762\u5728\u5fc3\u4e2d\u76d8\u7b97\u5bf9\u7b56\u3002 \u3000\u3000\u5141\u796f\u9aa4\u7136\u611f\u5230\u4e00\u9635\u9178\u610f\uff0c\u51b7\u7b11\u4e0d\u8bed\u3002\u5e74\u7fb9\u5c27\u5fc3\u4e2d\u60f4\u60f4\uff0c\u5374\u559c\u51af\u7433\u5e76\u4e0d\u8bf4\u4ed6\u3002\u5e74\u7fb9\u5c27\u7980 \u9053\uff1a\u201c\u8bf7\u5723\u4e0a\u5148\u5230\u5c0f\u81e3\u5bb6\u4e2d\u3002\u201d\u5141\u796f\u70b9\u4e86\u70b9\u5934\u3002\u51af\u7433\u6ef4\u6e9c\u7684\u773c\u775b\uff0c\u671d\u4ed6\u9762\u4e0a\u4e00\u626b\uff0c\u5e74\u7fb9\u5c27\u6025 \u5fd9\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u7684\u5bb6\u79bb\u5d69\u5c71\u4e0d\u8fdc\uff0c\u4e0b\u5c71\u4e4b\u540e\uff0c\u6362\u4e58\u5feb\u9a6c\uff0c\u5f53\u665a\u4fbf\u5230\u5e74\u5bb6\u3002\u5141\u796f\u9053\uff1a\u201c\u4f60\u628a\u5979\u597d\u597d \u5b89\u7f6e\u4e86\u3002\u201d\u5e74\u7fb9\u5c27\u9886\u547d\uff0c\u5c06\u5979\u5173\u5728\u4ee5\u524d\u6240\u4f4f\u7684\u4e66\u623f\u3002\u4e66\u623f\u5916\u6709\u54c8\u5e03\u9640\u548c\u5929\u53f6\u6563\u4eba\u628a\u5b88\u3002\u90a3\u662f \u4e07\u9003\u4e0d\u4e86\u3002 \u3000\u3000\u81ea\u5e74\u7fb9\u5c27\u56de\u5bb6\u540e\uff0c\u82b1\u56ed\u5df2\u4fee\u8338\u4e00\u65b0\uff0c\u6dfb\u4e86\u597d\u591a\u95f4\u623f\u5c4b\uff0c\u4e0d\u518d\u50cf\u4ee5\u524d\u90a3\u6837\u8352\u51c9\u4e86\u3002\u96cd\u6b63\u7687 \u5e1d\uff08\u5141\u796f\uff09\u548c\u674e\u6cbb\u4e5f\u90fd\u4f4f\u5728\u82b1\u56ed\u91cc\u9762\u3002\u665a\u996d\u8fc7\u540e\uff0c\u96cd\u6b63\u5ffd\u547d\u4eba\u5c06\u5e74\u7fb9\u5c27\u62db\u6765\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u60ca\u7591\u4e0d\u5b9a\uff0c\u5165\u89c1\u7687\u4e0a\u3002\u96cd\u6b63\u9053\uff1a\u201c\u6211\u60f3\u628a\u7433\u4e2b\u5934\u7acb\u4e3a\u8d35\u5983\uff0c\u4f60\u7740\u600e\u6837\uff1f\u201d\u5e74\u7fb9\u5c27 \u9053\uff1a\u201c\u8fd9\u662f\u7687\u4e0a\u5bb6\u4e8b\uff0c\u5c0f\u81e3\u4e0d\u6562\u8bf4\u8bdd\u3002\u201d\u96cd\u6b63\u9053\uff1a\u201c\u4f60\u77e5\u9053\u7433\u4e2b\u5934\u6765\u5386\u4e0d\u660e\uff0c\u6c49\u5973\u7981\u6b62\u5165\u5b98 \u7684\u89c4\u4f8b\u867d\u7136\u653e\u5bbd\uff0c\u6715\u7684\u592a\u540e\u8fd8\u5728\u2014\u2014\u201d\u6b32\u8a00\u53c8\u6b62\u3002\u5e74\u7fb9\u5c27\u4f55\u7b49\u673a\u7075\uff0c\u6697\u9053\uff1a\u5f53\u5e74\u6211\u731c\u5ea6\u7687\u4e0a \u610f\u601d\uff0c\u679c\u7136\u6ca1\u6709\u731c\u9519\u3002\u594f\u9053\uff1a\u201c\u5c0f\u81e3\u5192\u6627\uff0c\u60f3\u8ba4\u7433\u8d35\u4eba\u505a\u4e49\u59b9\u3002\u201d\u96cd\u6b63\u7b11\u9053\uff1a\u201c\u4f60\u679c\u7136\u806a \u660e\uff0c\u597d\uff0c\u8981\u8ba4\u5e72\u8106\u8ba4\u505a\u5802\u59b9\u597d\u5566\u3002\u4f60\u6d3e\u4eba\u9001\u5979\u5165\u5bab\uff0c\u592a\u540e\u4e00\u5b9a\u6ca1\u6709\u8bdd\u8bf4\u3002\u201d\u5e74\u7fb9\u5c27\u5fc3\u4e2d\u6697 \u559c\u3002\u96cd\u6b63\u5ffd\u9053\uff1a\u201c\u897f\u5f81\u4e4b\u884c\uff0c\u53ef\u4ee5\u518d\u7f13\u3002\u4f60\u540e\u5929\u548c\u6211\u4e00\u540c\u56de\u4eac\u5427\u3002\u201d\u5e74\u7fb9\u5c27\u672c\u60f3\u8d81\u673a\u4f1a\u4f1a\u51af \u7433\uff0c\u95fb\u8a00\u5982\u6d47\u51b7\u6c34\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u9c7c\u4eae\u4e4b\u4e8b\u53c8\u5982\u4f55\uff1f\u201d\u539f\u6765\u5e74\u7fb9\u5c27\u5f53\u65e5\u89e3\u51b3\u5141\u5824\u4e4b\u540e\uff0c\u539f\u5b9a \u56de\u5bb6\u53ea\u4f4f\u4e09\u65e5\uff0c\u540e\u6765\u96cd\u6b63\u4e00\u5230\uff0c\u53eb\u4ed6\u628a\u897f\u5f81\u4e4b\u4e8b\u6682\u7f13\uff0c\u6697\u4e2d\u5e03\u7f6e\u5927\u519b\u5bf9\u4ed8\u9c7c\u4eae\uff0c\u6240\u4ee5\u4f4f\u5728\u5982 \u4eca\u3002 \u3000\u3000\u5141\u796f\u7b11\u9053\uff1a\u201c\u8ba9\u9c7c\u4eae\u591a\u505a\u4e24\u6708\u5927\u738b\u5427\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4ed6\u8981\u6c42\u7687\u4e0a\u8df5\u7ea6\uff0c\u5c06\u5c71\u4e1c\u5f52\u4ed6\u7ba1 \u8f96\uff0c\u8fd9\u53c8\u5982\u4f55\u5bf9\u4ed8\uff1f\u201d\u96cd\u6b63\u7b11\u9053\uff1a\u201c\u5f20\u5ef7\u7389\u5e76\u4e0d\u7cca\u6d82\uff0c\u4ea4\u63a5\u4e4b\u4e8b\uff0c\u6587\u4e66\u5f80\u6765\uff0c\u6700\u5c11\u4e5f\u5f97\u6570 \u6708\uff0c\u90a3\u65f6\u4ed6\u5df2\u662f\u74ee\u4e2d\u4e4b\u9cd6\u3002\u201d\u5e74\u7fb9\u5c27\u53ea\u597d\u552f\u552f\u79f0\u662f\uff0c\u8eac\u8170\u544a\u9000\u3002\u5fc3\u9053\uff1a\u7433\u4e2b\u5934\u82e5\u7136\u8fdb\u5bab\uff0c\u8fdf \u65e9\u90fd\u4f1a\u5c06\u6211\u6536\u85cf\u5979\u4e4b\u4e8b\u6cc4\u9732\u51fa\u6765\uff0c\u8fd9\u5374\u5982\u4f55\u662f\u597d\uff1f\u56de\u5230\u623f\u4e2d\uff0c\u5c4f\u9000\u5de6\u53f3\uff0c\u82e6\u5fc3\u601d\u8651\u5bf9\u7b56\u3002 \u3000\u3000\u51af\u7433\u88ab\u5173\u5728\u4e66\u623f\uff0c\u89c1\u7ef5\u5e10\u5982\u65b0\uff0c\u9e33\u9e2f\u88ab\u6696\uff0c\u4e0d\u89c9\u53c8\u628a\u65e7\u4e8b\u56de\u5fc6\u8d77\u6765\u3002\u5fc3\u9053\uff1a\u5e74\u7fb9\u5c27\u5f85\u6211 \u4e0d\u9519\uff0c\u600e\u80fd\u518d\u4e00\u89c1\u4ed6\uff1f\u53c8\u60f3\u9053\uff1a\u8fd9\u82b1\u56ed\u597d\u50cf\u6211\u8bb8\u591a\u5e74\u524d\u66fe\u4f4f\u8fc7\u7684\uff0c\u4f46\u6211\u600e\u6837\u60f3\u90fd\u60f3\u4e0d\u8d77\u6765\uff0c \u82e5\u80fd\u518d\u4f4f\u534a\u5e74\uff0c\u6216\u8005\u53ef\u4ee5\u5bfb\u51fa\u4e00\u4e9b\u7ebf\u7d22\u3002\u6b63\u601d\u91cf\u95f4\uff0c\u96cd\u6b63\u5df2\u6d3e\u54c8\u5e03\u9640\u53ec\u5979\u5165\u89c1\u3002 \u3000\u3000\u51af\u7433\u4e00\u9762\u884c\u4e00\u9762\u60f3\u6cd5\uff0c\u96cd\u6b63\u4f4f\u5728\u56ed\u5b50\u897f\u9996\u65b0\u5efa\u7684\u5927\u623f\uff0c\u51af\u7433\u8fdb\u6765\uff0c\u96cd\u6b63\u5fae\u5fae\u4e00\u7b11\uff0c\u6325\u624b \u53eb\u54c8\u5e03\u9640\u9000\u4e0b\uff0c\u623f\u4e2d\u53ea\u5269\u4e0b\u4ed6\u548c\u51af\u7433\u4e24\u4eba\u3002 \u3000\u3000\u96cd\u6b63\u9053\uff1a\u201c\u4f60\u5728\u7687\u5e9c\u591a\u5e74\uff0c\u6211\u4e00\u5411\u5f85\u4f60\u4e0d\u9519\uff0c\u4f60\u4e3a\u4f55\u9003\u8dd1\uff1f\u201d \u3000\u3000\u51af\u7433\u5c0f\u5634\u4e00\u5476\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4f4f\u5f97\u817b\u5462\uff0c\u51fa\u6765\u8d70\u8d70\uff0c\u8fd9\u72af\u4e86\u4ec0\u4e48\u5929\u6761\u4e86\uff1f\u201d\u96cd\u6b63\u5fc3\u795e\u4e00 \u8361\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5566\uff0c\u65e2\u5f80\u4e0d\u7a76\uff0c\u4f60\u4ee5\u540e\u53ef\u4e0d\u8981\u8d70\u5566\uff01\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u662f\u4f60\u5bb6\u7684\u4eba\uff0c\u4e3a\u4ec0 \u4e48\u4f60\u8981\u7ba1\u6211\uff1f\u201d\u96cd\u6b63\u7b11\u9053\uff1a\u201c\u4ee5\u540e\u4f60\u5c31\u662f\u6211\u5bb6\u7684\u4eba\u5566\uff01\u201d\u4e0a\u6765\u60f3\u4eb2\u51af\u7433\uff0c\u51af\u7433\u4e00\u95ea\u95ea\u5f00\uff0c \u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f\u4e0b\u8d31\u7684\u5bab\u5973\u5417\uff1f\u201d \u3000\u3000\u96cd\u6b63\u51db\u7136\u4e00\u60ca\uff0c\u966a\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u628a\u4f60\u7b56\u5c01\u4e3a\u8d35\u5983\uff0c\u9664\u4e86\u7687\u540e\u4e4b\u5916\uff0c\u5bab\u4e2d\u5c31\u7b97\u4f60\u6700\u4e3a\u5c0a\u8d35 \u4e86\u3002\u201d\u51af\u7433\u751a\u4e3a\u751f\u6c14\uff0c\u5fcd\u7740\u4e0d\u4fbf\u53d1\u4f5c\u3002\u96cd\u6b63\u9053\uff1a\u201c\u5982\u4f55\uff0c\u4eca\u665a\u4f60\u522b\u8d70\u4e86\u5427\uff01\u201d\u4f38\u624b\u53c8\u6765\u62c9\u5979\u3002 \u3000\u3000\u51af\u7433\u5fc3\u9053\uff1a\u4ed6\u6b66\u529f\u5728\u6211\u4e4b\u4e0a\uff0c\u52a8\u5f3a\u8d77\u6765\uff0c\u53ef\u662f\u96be\u4e8e\u5bf9\u4ed8\u3002\u56de\u7738\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5802\u5802\u7687 \u4e0a\uff0c\u5e72\u5077\u5077\u6478\u6478\u7684\u4e8b\uff0c\u4f60\u4e0d\u7f9e\u4e48\uff1f\u4f60\u5c31\u662f\u8981\u628a\u6211\u7b56\u4e3a\u8d35\u5983\uff0c\u4e5f\u8be5\u5f97\u6211\u5fc3\u7518\u60c5\u613f\u624d\u884c\u3002\u201d\u90a3\u77e5 \u96cd\u6b63\u5bf9\u51af\u7433\u5782\u6d8e\u5df2\u4e45\uff0c\u5982\u4eca\u89c1\u5979\u53c8\u957f\u5927\u4e86\u8bb8\u591a\uff0c\u8d8a\u53d1\u957f\u5f97\u5929\u59ff\u56fd\u8272\uff0c\u5fc3\u75d2\u75d2\u7684\u6309\u5948\u4e0d\u4f4f\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5929\u751f\u4e3d\u8d28\u60f9\u707e\u6b83\uff0c\u6101\u5165\u6df1\u5bab\u5c81\u6708\u957f\u3002 \u3000\u3000\u6b32\u77e5\u51af\u7433\u80fd\u5426\u9003\u8131\u864e\u53e3\uff0c\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u56de\u3000\u3000\u5bc6\u5ba4\u85cf\u5978\u3000\u5c06\u519b\u9732\u771f\u76f8\u3000\u6df1\u5bb5\u558b\u8840\u3000\u541b\u4e3b\u8d39\u5fc3\u673a \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u56de \u5bc6\u5ba4\u85cf\u5978\u3000\u5c06\u519b\u9732\u771f\u76f8 \u6df1\u5bb5\u558b\u8840\u3000\u541b\u4e3b\u8d39\u5fc3\u673a \u3000\u3000\u51af\u7433\u9000\u4e86\u4e24\u6b65\uff0c\u96cd\u6b63\u7b11\u9053\uff1a\u201c\u6715\u505a\u7684\u4e8b\uff0c\u8c01\u6562\u95f2\u8bdd\uff01\u201d\u8e0f\u524d\u4e00\u6b65\uff0c\u4f38\u624b\u53c8\u62c9\u3002\u51af\u7433\u5ffd \u9053\uff1a\u201c\u56db\u8d1d\u52d2\u767b\u4e86\u5927\u5b9d\uff0c\u6211\u8fd8\u672a\u8d3a\u3002\u606d\u559c\u7687\u4e0a\u5440\uff01\u201d\u96cd\u6b63\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u4f55\u5fc5\u6765\u8fd9\u4e00\u5957\uff1f\u201d \u51af\u7433\u9053\uff1a\u201c\u5149\u660e\u5927\u6bbf\u7684\u9057\u8bcf\u6539\u5f97\u771f\u597d\uff01\u201d\u96cd\u6b63\u4e00\u60ca\uff0c\u60f3\u8d77\u9057\u8bcf\u6b63\u662f\u5979\u548c\u5929\u53f6\u6563\u4eba\u53bb\u5077\uff0c\u7531\u9686 \u79d1\u591a\u6539\u7684\u3002\u53d8\u8272\u8bf4\u9053\uff1a\u201c\u4f60\u5728\u5916\u9762\u4e71\u95ef\uff0c\u5bf9\u4ec0\u4e48\u4eba\u8bf4\u4e86\u6ca1\u6709\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u8fd8\u77e5\u9053\u5927\u4f53\uff0c \u600e\u6562\u4e71\u8bf4\u3002\u4e0d\u8fc7\u82e5\u662f\u6709\u4eba\u62db\u607c\u4e86\u6211\uff0c\u90a3\u6211\u4fbf\u8981\u56b7\u51fa\u6765\u4e86\u3002\u201d\u5141\u5824\u867d\u9664\uff0c\u4f17\u7687\u5b50\u7684\u7fbd\u7ffc\u8fd8\u672a\u5b8c \u5168\u526a\u9664\uff0c\u6b64\u4e8b\u82e5\u5ba3\u626c\u51fa\u6765\uff0c\u96cd\u6b63\u7687\u4f4d\u4e0d\u7a33\uff0c\u6b32\u7130\u767b\u65f6\u7184\u4e86\u4e00\u534a\uff0c\u7b11\u9053\uff1a\u201c\u597d\u5566\uff0c\u90a3\u4e48\u6211\u4ee5\u8d35 \u5983\u4e4b\u793c\uff0c\u6b63\u5f0f\u63a5\u4f60\u5165\u5bab\uff0c\u54b1\u4eec\u518d\u4e3a\u592b\u5987\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u90a3\u4ee5\u540e\u518d\u8bf4\u3002\u201d\u96cd\u6b63\u9053\uff1a\u201c\u4f60\u4e0d\u4f9d \u6211\uff0c\u6211\u5c31\u628a\u4f60\u7684\u674e\u54e5\u54e5\u6740\u4e86\uff01\u201d \u3000\u3000\u51af\u7433\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u5165\u5bab\u6700\u5c11\u8fd8\u6709\u534a\u6708\uff0c\u80fd\u907f\u8fc7\u4e00\u65f6\u5c31\u7b97\u4e00\u65f6\uff0c\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u591c\u6df1\u5566\uff0c\u4f60 \u4e0d\u7761\u6211\u4e5f\u8981\u7761\u4e86\uff0c\u6211\u56de\u53bb\u5566\u3002\u201d\u96cd\u6b63\u89c1\u5979\u53e3\u6c14\u5df2\u8f6f\uff0c\u5fc3\u9053\uff1a\u5bcc\u8d35\u7e41\u534e\uff0c\u8c01\u4eba\u4e0d\u60f3\u3002\u8fd9\u4e2b\u5934\u4e0d \u8fc7\u662f\u81ea\u9ad8\u8eab\u4efd\uff0c\u4e00\u5b9a\u8981\u6b63\u5f0f\u53d7\u4e86\u518c\u5c01\u624d\u613f\u610f\u7f62\u4e86\u3002\u5fc3\u4e2d\u4e00\u559c\uff0c\u6325\u624b\u8bf4\u9053\uff1a\u201c\u597d\u5566\uff0c\u4f60\u56de\u53bb \u5427\uff01\u201d \u3000\u3000\u51af\u7433\u8d70\u4e86\u4e24\u6b65\uff0c\u8fd8\u672a\u8de8\u51fa\u623f\u95e8\uff0c\u96cd\u6b63\u5ffd\u53c8\u62db\u624b\u8bf4\u9053\uff1a\u201c\u56de\u6765\uff01\u201d\u51af\u7433\u4e00\u60ca\uff0c\u56de\u5934\u8bf4\u9053\uff1a \u201c\u7687\u4e0a\u771f\u96be\u4f3a\u5019\uff0c\u4f60\u53c8\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u5429\u5490\u6211\uff1f\u201d\u96cd\u6b63\u8fdf\u7591\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5728\u6c5f\u6e56\u95ef\u8361\uff0c\u4e5f \u987e\u4e0d\u5f97\u4fee\u9970\u989c\u5bb9\uff0c\u5934\u53d1\u4e71\u4e86\u4e0d\u7406\uff0c\u9970\u7269\u6389\u4e86\u4e0d\u7ba1\u3002\u6211\u8981\u53eb\u5bab\u5a25\u66ff\u4f60\u4fee\u9970\u4e00\u4e0b\u3002\u201d\u51af\u7433\u751a\u4e3a\u5947 \u602a\uff0c\u4e3a\u4f55\u7687\u4e0a\u8981\u7ba1\u5230\u8fd9\u4e9b\u4e8b\u60c5\uff1f \u3000\u3000\u96cd\u6b63\u8fdb\u5165\u5185\u5ba4\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5e26\u4e86\u4e24\u540d\u5bab\u5a25\u51fa\u6765\uff0c\u4e00\u4e2a\u624b\u6367\u68b3\u5986\u76d2\uff0c\u4e00\u4e2a\u624b\u6367\u8138\u76c6\uff0c\u66ff\u5979 \u68b3\u5934\u6574\u8863\uff0c\u51af\u7433\u751a\u4e0d\u8010\u70e6\uff0c\u5fcd\u7740\u6c14\u7531\u5979\u4eec\u53bb\u505a\u3002\u4e00\u4e2a\u5bab\u5a25\u5728\u68b3\u5986\u76d2\u4e2d\u53d6\u51fa\u4e00\u4e2a\u5c0f\u76d2\uff0c\u91cc\u9762\u88c5 \u7684\u597d\u50cf\u662f\u80ed\u8102\uff0c\u5bab\u5a25\u6311\u4e86\u4e00\u70b9\uff0c\u62b9\u5728\u51af\u7433\u81c2\u4e0a\u3002\u51af\u7433\u9053\uff1a\u201c\u80ed\u8102\u4e3a\u4f55\u6d82\u5728\u81c2\u4e0a\uff0c\u4e00\u56e2\u7ea2\u7684\uff0c \u591a\u4e48\u96be\u770b\uff01\u201d\u4e3e\u8896\u4e00\u64e6\uff0c\u7adf\u7136\u63e9\u62ed\u4e0d\u53bb\u3002\u96cd\u6b63\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u7528\u6c34\u6d17\u5427\uff01\u201d\u51af\u7433\u9887\u4e3a\u8be7\u5f02\uff0c \u5728\u5bab\u5a25\u624b\u4e2d\uff0c\u62a2\u8fc7\u624b\u4e2d\uff0c\u5728\u8138\u76c6\u91cc\u4e00\u6d78\uff0c\u7528\u529b\u63e9\u62b9\uff0c\u4e0d\u6599\u8d8a\u62b9\u989c\u8272\u8d8a\u7ea2\uff0c\u96cd\u6b63\u54c8\u54c8\u5927\u7b11\uff0c\u5f97 \u610f\u4e4b\u6781\u3002 \u3000\u3000\u539f\u6765\u96cd\u6b63\u89c1\u5979\u5982\u6b64\u5173\u5fc3\u674e\u6cbb\uff0c\u918b\u6c14\u653b\u5fc3\uff0c\u751f\u4e86\u597d\u591a\u7591\u8651\u3002\u6240\u4ee5\u53eb\u5bab\u5a25\u7528\u5b88\u5bab\u7802\u8bd5\u5979\u4e00 \u8bd5\u3002\u201c\u5b88\u5bab\u201d\u5c31\u662f\u58c1\u864e\uff0c\u5b88\u5b98\u7802\u7684\u5236\u6cd5\u636e\u8bf4\u662f\u4ee5\u73e0\u7802\u548c\u725b\u7f8a\u8102\u5582\u58c1\u864e\u98df\uff0c\u65e5\u4e45\u4e4b\u540e\uff0c\u58c1\u864e\u8179 \u4f5c\u8d64\u8272\uff0c\u901a\u4f53\u900f\u7ea2\uff0c\u628a\u5b83\u7684\u8840\u53d6\u4e86\u51fa\u6765\uff0c\u6df7\u5165\u80ed\u8102\uff0c\u6d82\u5728\u5973\u5b50\u81c2\u4e0a\uff0c\u82e5\u662f\u5987\u4eba\u7684\u8bdd\u4e00\u62b9\u4fbf \u53bb\uff0c\u82e5\u662f\u5904\u5973\u7684\u8bdd\uff0c\u5219\u65e0\u8bba\u600e\u6837\u6d17\u62b9\uff0c\u989c\u8272\u8d8a\u53d1\u9c9c\u8273\u3002\u6709\u4e00\u9996\u548f\u5b88\u5bab\u7802\u7684\u8bd7\u9053\uff1a\u201c\u8c01\u7528\u79e6\u5bab \u4e00\u7c92\u4e39\uff0c\u8bb0\u65f6\u5bb9\u6613\u5b88\u65f6\u96be\uff0c\u9e33\u9e2f\u68a6\u51b7\u80a0\u582a\u65ad\uff0c\u6670\u874e\u9b42\u6d88\u8840\u672a\u5e72\uff1b\u69b4\u5b50\u8272\u5206\u91d1\u948f\u6653\uff0c\u831c\u82b1\u5149\u6620 \u7389\u73ed\u5bd2\uff1b\u4f55\u65f6\u8bd5\u5377\u9999\u7f57\u8896\uff0c\u7b11\u8bed\u4e1c\u541b\u4ed4\u7ec6\u770b\u3002\u201d\u8fd9\u9996\u8bd7\u8bf4\u5f97\u5f88\u201c\u98ce\u96c5\u201d\uff0c\u4f46\u62c6\u7a7f\u6765\u8bf4\uff0c\u4e0d\u8fc7 \u662f\u4fae\u8fb1\u5973\u6027\u7684\u628a\u620f\u3002 \u3000\u3000\u51af\u7433\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u600e\u77e5\u7687\u5e1d\u662f\u7528\u5b88\u5bab\u7802\u8bd5\u5979\uff0c\u6d17\u62b9\u4e0d\u53bb\uff0c\u6c14\u6124\u6124\u7684\u9053\uff1a\u201c\u4f60\u6363\u4ec0\u4e48\u9b3c\uff1f\u201d \u96cd\u6b63\u4e00\u7b11\u53eb\u5bab\u5a25\u6289\u5979\u51fa\u623f\u3002\u51af\u7433\u4e00\u6c14\uff0c\u6454\u6389\u5bab\u5a25\u7684\u624b\uff0c\u81ea\u5df1\u8dd1\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\uff0c\u5e74\u7fb9\u5c27\u5c06\u7236\u6bcd\u8bf7\u5230\u56ed\u4e2d\uff0c\u4e3e\u884c\u6536\u201c\u5973\u513f\u201d\u7684\u5e86\u5178\uff0c\u51af\u7433\u4efb\u7531\u4ed6\u4eec\u6446\u5e03\uff0c\u5e74\u7fb9\u5c27 \u7684\u6bcd\u4eb2\u75db\u60dc\u51af\u7433\uff0c\u77e5\u9053\u6b64\u756a\u8ba4\u4e86\u201c\u5973\u513f\u201d\u4e4b\u540e\uff0c\u5979\u4fbf\u88ab\u9001\u5165\u5bab\u4e2d\uff0c\u751a\u4e3a\u96be\u8fc7\u3002\u66ff\u51af\u7433\u68b3\u5934 \u65f6\uff0c\u9669\u9669\u6ef4\u51fa\u773c\u6cea\uff0c\u51af\u7433\u77a7\u5728\u773c\u5185\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u59c6\u5988\uff0c\u6211\u5b9e\u5728\u820d\u4e0d\u5f97\u4f60\u3002\u201d\u5e74\u7fb9\u5c27\u7684\u6bcd\u4eb2 \u9053\uff1a\u201c\u5b69\u5b50\uff0c\u6211\u4e5f\u820d\u4e0d\u5f97\u4f60\uff0c\u53ea\u6028\u2014\u2014\u201d\u5979\u672c\u60f3\u8bf4\uff1a\u201c\u53ea\u6028\u5e74\u7fb9\u5c27\u8fd9\u5b69\u5b50\u6ca1\u798f\u3002\u201d\u5e95\u4e0b\u7684 \u8bdd\uff0c\u5230\u4e86\u53e3\u8fb9\u53c8\u54bd\u56de\u53bb\u3002\u51af\u7433\u4f55\u7b49\u806a\u660e\uff0c\u65e9\u77e5\u5176\u610f\uff0c\u9053\uff1a\u201c\u7fb9\u5c27\u54e5\u54e5\uff0c\u4e3a\u4f55\u4e0d\u6765\u770b\u6211\uff1f\u201d\u5e74 \u7fb9\u5c27\u6bcd\u4eb2\u9053\uff1a\u201c\u4f60\u4eca\u540e\u662f\u8d35\u5983\u5566\uff0c\u6ca1\u6709\u7687\u4e0a\u4e4b\u547d\uff0c\u4ed6\u600e\u6562\u79c1\u81ea\u89c1\u4f60\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u544a\u8bc9 \u4ed6\uff0c\u6211\u6302\u5ff5\u4ed6\u3002\u201d\u5e74\u6bcd\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u6211\u77e5\u9053\u5566\uff0c\u4ee5\u540e\u4f60\u5728\u7687\u5e1d\u8ddf\u524d\u591a\u62db\u6276\u4ed6\u3002\u201d\u8f6c\u8fc7\u8eab\uff0c \u5077\u5077\u62b9\u4e86\u4e00\u6ef4\u773c\u6cea\u3002 \u3000\u3000\u665a\u996d\u65f6\u6362\u4e86\u53cc\u9b54\u770b\u5b88\uff0c\u8428\u5929\u523a\u6367\u996d\u9001\u6765\uff0c\u6124\u7136\u8bf4\u9053\uff1a\u201c\u7433\u8d35\u4eba\uff0c\u8bf7\u7528\u81b3\uff01\u201d\u51af\u7433\u9053\uff1a \u201c\u8428\u4f2f\u4f2f\uff0c\u4f60\u4e3a\u4f55\u8fd9\u6837\u53eb\u6211\uff1f\u201d\u8428\u5929\u523a\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u53c8\u53eb\u6211\u505a\u4f2f\u4f2f\u4e86\uff1f\u4f60\u4e0d\u662f\u8981\u628a\u6211\u523a\u4e2a\u900f \u660e\u7a9f\u7abf\u5417\uff1f\u201d\u8428\u5929\u523a\u4ee5\u524d\u5c06\u51af\u745b\u8bef\u8ba4\u51af\u7433\uff0c\u51e0\u4e4e\u53eb\u5979\u523a\u4f24\uff0c\u6b64\u6068\u81f3\u4eca\u672a\u6d88\u3002\u51af\u7433\u8be7\u9053\uff1a\u201c\u6211 \u5c82\u6562\u8ddf\u4f60\u52a8\u624b\uff0c\u4f60\u8fd9\u662f\u90a3\u91cc\u8bdd\u6765\uff1f\u201d\u8428\u5929\u523a\u89c1\u5979\u6001\u5ea6\u81ea\u7136\uff0c\u4e0d\u50cf\u51fa\u4e8e\u5047\u5192\uff0c\u5947\u9053\uff1a\u201c\u5c31\u662f\u4e0a \u4e00\u4e2a\u6708\uff0c\u5c31\u5728\u5e74\u5bb6\u9644\u8fd1\uff0c\u4f60\u4e0d\u662f\u5927\u9a82\u6211\u4eec\uff0c\u8fd8\u5c06\u4e8c\u4f2f\uff08\u8428\u5929\u90fd\uff09\u523a\u4f24\u4e86\u5417\uff1f\u201d \u3000\u3000\u51af\u7433\u8df3\u8d77\u6765\u9053\uff1a\u201c\u771f\u5947\u602a\uff0c\u8fd9\u4e8b\u60c5\u6211\u73b0\u5728\u8fd8\u672a\u5f04\u6e05\uff0c\u90a3\u5929\u6211\u6839\u672c\u672a\u51fa\u8fc7\u623f\u95e8\uff0c\u5e74\u7fb9\u5c27\u4e00 \u56de\u6765\u5c31\u8bf4\u6211\u66fe\u4e0e\u4f60\u4eec\u4ea4\u624b\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u6d3b\u89c1\u9b3c\uff0c\u800c\u4eca\u4f60\u4eec\u53c8\u8fd9\u6837\u8bf4\uff0c\u83ab\u975e\u4e16\u95f4\u4e0a\u771f\u6709\u4e00\u4e2a \u4e0e\u6211\u6781\u4e3a\u76f8\u4f3c\u7684\u4eba\uff0c\u4f60\u4eec\u628a\u5979\u5f53\u6210\u6211\u4e86\uff1f\u201d\u8428\u5929\u523a\u4e00\u6014\uff0c\u4ed6\u4e5f\u4e0d\u77e5\u51af\u7433\u8fd8\u6709\u4e00\u4e2a\u5b6a\u751f\u59d0\u59d0\uff0c \u5341\u516d\u5e74\u524d\uff0c\u4ed6\u5728\u592a\u884c\u5c71\u4e0a\u62a2\u4e86\u51af\u745b\uff0c\u540e\u6765\u88ab\u6613\u5170\u73e0\u5077\u53bb\uff0c\u518d\u540e\u6765\u4ed6\u5728\u949f\u4e07\u5802\u624b\u91cc\u53c8\u62a2\u4e86\u51af \u7433\uff0c\u59cb\u7ec8\u628a\u4e24\u59d0\u59b9\u8bef\u4e3a\u4e00\u4eba\uff0c\u95fb\u8a00\u5947\u9053\uff1a\u201c\u90a3\u5929\u771f\u4e0d\u662f\u4f60\uff1f\u4e16\u95f4\u4e0a\u90a3\u4f1a\u6709\u4e24\u4e2a\u8fd9\u6837\u76f8\u4f3c\u4e4b \u4eba\uff1f\u201d \u3000\u3000\u51af\u7433\u5ffd\u9053\uff1a\u201c\u8428\u4f2f\u4f2f\uff0c\u662f\u4f60\u62b1\u6211\u8fdb\u5bab\u7684\u662f\u4e0d\u662f\uff1f\u201d\u8428\u5929\u523a\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u8bf7 \u4f60\u544a\u8bc9\u6211\uff0c\u6211\u7684\u7236\u6bcd\u5230\u5e95\u662f\u8c01\uff1f\u201d\u8428\u5929\u523a\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u5c31\u662f\u4f60\u5bb6\u4ec7\u4eba\u4e4b\u4e00\uff0c\u600e\u80fd\u8bf4\u7ed9\u4f60 \u77e5\u3002\u201d\u5e72\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5bf9\u4f60\u8bf4\u8fc7\u8bb8\u591a\u6b21\u5417\uff1f\u4f60\u662f\u8def\u65c1\u5f03\u5a74\uff0c\u6211\u5c06\u4f60\u62fe\u56de\u6765\u7684\u3002\u201d \u51af\u7433\u5c0f\u5634\u4e00\u6241\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff01\u201d\u8428\u5929\u523a\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\u6211\u4e5f\u6ca1\u6cd5\u3002\u201d\u51af\u7433\u5ffd\u9053\uff1a\u201c\u82e5\u6211\u662f\u5f03 \u5a74\uff0c\u4f60\u5c06\u6211\u62fe\u4e86\u56de\u6765\uff0c\u90a3\u4e48\u4f60\u5c31\u7b49\u4e8e\u6211\u7684\u91cd\u751f\u7236\u6bcd\uff0c\u6211\u5c31\u7b49\u4e8e\u4f60\u7684\u5973\u513f\u4e86\u3002\u4f60\u6ca1\u6709\u5973\u513f\uff0c\u6211 \u505a\u4f60\u7684\u5973\u513f\u597d\u4e0d\u597d\uff1f\u201d\u8428\u5929\u523a\u5fc3\u4e2d\u4e00\u9178\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8d35\u4eba\u5566\uff0c\u6211\u600e\u6562\u9ad8\u6500\uff01\u201d\u51af\u7433\u5ffd\u9053\uff1a \u201c\u8428\u4f2f\u4f2f\uff0c\u4f60\u5e74\u7eaa\u4e5f\u4e00\u5927\u628a\u4e86\uff0c\u8fd8\u5728\u5bab\u4e2d\u6267\u5f79\uff0c\u662f\u4f55\u82e6\u6765\uff1f\u4e0d\u5982\u4f60\u52a9\u6211\u9003\u51fa\u53bb\uff0c\u6211\u4f8d\u5949\u4f60\u7ec8 \u751f\u3002\u201d\u8428\u5929\u523a\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4ed6\u6295\u9760\u5141\u796f\uff0c\u672c\u6765\u662f\u60f3\u505a\u201c\u56fd\u5e08\u201d\uff0c\u4e0d\u6599\u4e2d\u539f\u80fd\u4eba\u751a\u591a\uff0c\u5728\u5141\u796f\u95e8 \u4e0b\uff0c\u4e00\u76f4\u88ab\u4e86\u56e0\u54c8\u5e03\u9640\u7b49\u4eba\u538b\u7740\uff0c\u51fa\u4e0d\u4e86\u5934\uff0c\u5341\u591a\u5e74\u6765\uff0c\u59cb\u7ec8\u662f\u4e2a\u536b\u58eb\uff0c\u800c\u4eca\u8fd8\u8981\u542c\u5e74\u7fb9\u5c27 \u4f7f\u5524\u3002\u95fb\u8a00\u5fc3\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u4e0e\u5176\u90c1\u90c1\u4e0d\u5f97\u5fd7\uff0c\u5012\u4e0d\u5982\u6709\u4e2a\u5973\u513f\u4f8d\u5949\u5929\u5e74\u3002\u201d\u4f46\u60f3\u8d77\u7687\u4e0a\u4e0e\u5e74 \u7fb9\u5c27\u624b\u6bb5\u4e4b\u8fa3\uff0c\u4ed6\u867d\u6709\u9b54\u5934\u4e4b\u53f7\uff0c\u4e5f\u81ea\u80c6\u5bd2\u3002 \u3000\u3000\u51af\u7433\u773c\u775b\u9732\u51fa\u54c0\u6c42\u7684\u773c\u5149\uff0c\u8428\u5929\u523a\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u6025\u5fd9\u907f\u5f00\uff0c\u9053\uff1a\u201c\u4f60\u522b\u80e1\u601d\u4e71\u60f3\u5566\uff0c \u6211\u51fa\u53bb\u4e86\u3002\u201d\u51af\u7433\u597d\u751f\u5931\u671b\uff0c\u5403\u8fc7\u665a\u996d\uff0c\u8dcc\u5750\u5e8a\u4e0a\uff0c\u597d\u50cf\u8001\u50e7\u5165\u5b9a\uff0c\u9762\u58c1\u51fa\u795e\u3002\u542c\u5f97\u56ed\u5916\u6253 \u4e86\u4e8c\u66f4\uff0c\u5ffd\u7136\u60f3\u9053\uff1a\u201c\u8fd9\u623f\u4e2d\u6709\u590d\u58c1\u6697\u95e8\uff0c\u901a\u5230\u5916\u9762\uff0c\u6211\u4f55\u4e0d\u51fa\u53bb\u770b\u770b\uff1f\u201d\u53c8\u60f3\u9053\uff1a\u201c\u51fa\u53bb \u4e5f\u6ca1\u6709\u7528\uff0c\u5916\u9762\u6709\u4eba\u628a\u5b88\uff0c\u6211\u5982\u4f55\u80fd\u591f\u9003\u8131\u3002\u800c\u4e14\u674e\u6cbb\u54e5\u54e5\u53c8\u8fd8\u5728\u4ed6\u4eec\u7684\u638c\u63e1\u4e2d\uff01\u6b7b\u4e86\u6b64 \u5fc3\uff0c\u53c8\u5750\u4e86\u4e00\u4e2a\u66f4\u6b21\uff0c\u542c\u5f97\u4e09\u66f4\u9f13\u54cd\uff0c\u95e8\u5916\u6709\u811a\u6b65\u4e4b\u58f0\uff0c\u4f3c\u4e4e\u662f\u53cc\u9b54\u6362\u73ed\uff0c\u51af\u7433\u5fc3\u9053\uff1a\u201c\u6211 \u5728\u6b64\u5446\u5750\u4e5f\u662f\u65e0\u6cd5\uff0c\u4e0d\u5982\u51fa\u53bb\u770b\u770b\uff0c\u53cc\u9b54\u5bf9\u6211\u4e0d\u9519\uff0c\u82e5\u7136\u662f\u4ed6\u4eec\u6362\u5728\u56ed\u4e2d\u628a\u5b88\uff0c\u4e5f\u8bb8\u4e0d\u4f1a\u62e6 \u6211\uff0c\u6211\u518d\u89c1\u4e00\u6b21\u674e\u54e5\u54e5\uff0c\u90a3\u65f6\u6b7b\u4e5f\u7518\u5fc3\u3002\u201d\u8d70\u5230\u5e10\u540e\uff0c\u4e00\u6309\u5899\u58c1\uff0c\u5f00\u4e86\u6697\u95e8\uff0c\u8d70\u5165\u590d\u58c1\u3002 \u3000\u3000\u590d\u58c1\u4e2d\u9ed1\u9edd\u9edd\u7684\uff0c\u51af\u7433\u6b63\u6478\u7d22\u95f4\uff0c\u5ffd\u89c9\u6709\u4e00\u56e2\u9ed1\u5f71\u5411\u81ea\u5df1\u79fb\u52a8\uff0c\u51af\u7433\u5927\u5403\u4e00\u60ca\uff0c\u628a\u5582\u6bd2 \u7684\u5315\u9996\u5939\u5728\u638c\u4e2d\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7433\u59b9\uff0c\u5664\u58f0\uff0c\u662f\u6211\uff01\u201d \u3000\u3000\u8fd9\u65f6\u5165\u4e86\u590d\u58c1\u4e00\u9635\uff0c\u773c\u775b\u5df2\u6e10\u4e60\u60ef\uff0c\u9ed1\u6697\u4e2d\u4f9d\u7a00\u5206\u8fa8\u5f97\u51fa\u5e74\u7fb9\u5c27\u7684\u9762\u5f71\uff0c\u51af\u7433\u6536\u4e86\u6bd2 \u5200\uff0c\u8815\u52a8\u8eab\u5b50\uff0c\u6162\u6162\u51d1\u8fd1\uff0c\u53cc\u624b\u76f8\u63e1\uff0c\u9759\u5bc2\u4e2d\u5e74\u7fb9\u5c27\u542c\u5f97\u51af\u7433\u7684\u5fc3\u535c\u535c\u7684\u8df3\u3002 \u3000\u3000\u826f\u4e45\uff0c\u826f\u4e45\uff0c\u51af\u7433\u624d\u8bf4\u51fa\u4e00\u58f0\uff1a\u201c\u4f60\u6765\u4e86\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u6302\u5ff5\u6211\uff0c\u6211\u5192\u6b7b\u4e5f \u6765\u3002\u201d\u9ed1\u6697\u4e2d\u51af\u7433\u9762\u4e0a\u63a0\u8fc7\u4e00\u4e1d\u7b11\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u6211\u77e5\u9053\u4f60\u4f1a\u6765\u7684\uff01\u201d \u3000\u3000\u51af\u7433\u81ea\u4ece\u5728\u676d\u5dde\u89c1\u4e86\u5e74\u7fb9\u5c27\u540e\uff0c\u5c31\u89c9\u5f97\u6b64\u4eba\u4f3c\u66fe\u76f8\u8bc6\uff0c\u540e\u6765\u53c8\u5230\u5e74\u5bb6\u6765\u4f4f\uff0c\u60c5\u82d7\u672c\u5df2\u6697 \u751f\uff0c\u8fd9\u65f6\u88ab\u5e74\u7fb9\u5c27\u5bbd\u5927\u7684\u624b\u638c\u63e1\u4f4f\uff0c\u9762\u70ed\u5fc3\u8df3\uff0c\u60f3\u9053\uff1a\u5e74\u7fb9\u5c27\u4e5f\u672a\u5fc5\u50cf\u4ed6\u4eec\u6240\u8bf4\u7684\u90a3\u6837\u574f\uff0c \u4e5f\u8bb8\u4ed6\u662f\u50cf\u6211\u4e00\u6837\uff0c\u548c\u5e08\u5085\u5408\u4e0d\u6765\u624d\u79bb\u5f00\u5e08\u95e8\u7684\u5462\uff01 \u3000\u3000\u5598\u606f\u4e2d\u4f46\u542c\u5f97\u5e74\u7fb9\u5c27\u95ee\u9053\uff1a\u201c\u4f60\u60f3\u9003\u5417\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fdb\u6765\u7684\uff1f\u201d\u5e74\u7fb9\u5c27\u7b11\u4e86\u4e00 \u7b11\uff0c\u9053\uff1a\u201c\u6211\u529d\u4f60\u4e0d\u8981\u9003\u4e86\uff0c\u4eca\u65f6\u4e0d\u540c\u5f80\u65e5\uff0c\u7687\u4e0a\u4eb2\u5728\u8fd9\u513f\u2026\u2026\u201d \u3000\u3000\u539f\u6765\u81ea\u90a3\u6b21\u51af\u7433\u5728\u590d\u58c1\u9003\u540e\uff0c\u5e74\u7fb9\u5c27\u7ec6\u5fc3\u68c0\u67e5\u95e8\u6237\uff0c\u53d1\u73b0\u79d8\u5bc6\uff0c\u6697\u8d5e\u5e08\u5085\u7528\u5fc3\u4e4b\u5de7\uff0c\u521d \u65f6\u60f3\u628a\u6697\u95e8\u5835\u585e\uff0c\u540e\u6765\u4e00\u60f3\uff0c\u4e5f\u8bb8\u8fd8\u6709\u540e\u7528\uff0c\u7d22\u6027\u589e\u8bbe\u673a\u5173\uff0c\u4ece\u5916\u9762\u6398\u4e86\u4e00\u6761\u5730\u9053\u901a\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u51af\u7433\u542c\u4e86\u5e74\u7fb9\u5c27\u7684\u8bdd\uff0c\u5fc3\u4e2d\u4e00\u51b7\uff0c\u9053\uff1a\u201c\u4f60\u7edf\u7387\u5927\u519b\uff0c\u4e5f\u6015\u4ed6\u5417\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6211\u6109 \u5077\u8fdb\u6765\uff0c\u4e0d\u80fd\u803d\u6401\uff0c\u54b1\u4eec\u5148\u628a\u8981\u7d27\u7684\u8bdd\u8bf4\u4e86\u3002\u201d \u3000\u3000\u51af\u7433\u4e00\u53a2\u60c5\u613f\uff0c\u5c3d\u4ece\u597d\u5904\u7740\u60f3\uff0c\u4ee5\u4e3a\u5e74\u7fb9\u5c27\u65e2\u80af\u5192\u9669\u5230\u6b64\uff0c\u4e00\u5b9a\u662f\u8bbe\u6cd5\u6551\u5979\u6765\u4e86\u3002\u7b11 \u9053\uff1a\u201c\u6709\u4ec0\u4e48\u7d27\u8981\u7684\u8bdd\u5462\uff1f\u201d\u5e74\u7fb9\u5c27\u8fdf\u7591\u534a\u664c\uff0c\u5c1a\u672a\u60f3\u597d\u8bf4\u8f9e\uff0c\u51af\u7433\u6328\u8fd1\u4e86\u4ed6\uff0c\u6084\u6084\u8bf4\u9053\uff1a \u201c\u6211\u4e5f\u6709\u8981\u7d27\u7684\u8bdd\u8981\u95ee\u4f60\uff0c\u6211\u597d\u50cf\u4ee5\u524d\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\u4f60\u7684\uff0c\u4e0d\u77e5\u662f\u4e0d\u662f\u5728\u68a6\u4e2d\uff1f\u201d\u5e74\u7fb9\u5c27 \u9053\uff1a\u201c\u6211\u4e5f\u597d\u50cf\u89c1\u8fc7\u4f60\u7684\uff0c\u90a3\u4e48\u8bf4\u6765\uff0c\u54b1\u4eec\u5f88\u6709\u7f18\u4efd\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u662f\u4e0d\u662f\u7f18 \u4efd\uff0c\u6211\u603b\u89c9\u5f97\u4f60\u662f\u6211\u4e00\u4e2a\u5f88\u4eb2\u8fd1\u7684\u4eba\uff0c\u5c31\u50cf\u5144\u59b9\u90a3\u6837\u7684\u4eb2\u8fd1\u7684\u4eba\u3002\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u5fc3\u4e2d\u6697\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u5c0f\u65f6\u5019\u54b1\u4eec\u4e00\u540c\u73a9\u800d\uff0c\u90a3\u65f6\u5e08\u5085\u9a97\u8bf4\u4f60\u662f\u6211\u7684\u758f\u5802\u59b9\u59b9\uff0c\u6211 \u4e5f\u4fe1\u5462\u3002\u201d\u51af\u7433\u7eed\u9053\uff1a\u201c\u73b0\u5728\u6211\u771f\u662f\u4f60\u7684\u59b9\u59b9\u4e86\uff0c\u6211\u5f88\u9ad8\u5174\u3002\u6211\u771f\u7684\u4e0d\u60f3\u8fdb\u5bab\u91cc\u53bb\uff0c\u55ef\uff0c\u4f60 \u8fd8\u80fd\u50cf\u4ee5\u524d\u5728\u676d\u5dde\u7684\u65f6\u5019\u4e00\u6837\uff0c\u5077\u5077\u653e\u6211\u4eec\u9003\u8d70\u5417\uff1f\u6216\u8005\u4f60\u4e0d\u80fd\u6551\u6211\uff0c\u4e5f\u8bf7\u4f60\u8bbe\u6cd5\u6551\u4ed6\uff0c\u6211 \u8bf4\u7684\u662f\u90a3\u4f4d\u674e\u54e5\u54e5\uff0c\u4e00\u4e2a\u975e\u5e38\u597d\u7684\u597d\u4eba\u3002\u5e74\u54e5\u54e5\uff0c\u6211\u4f1a\u4e00\u751f\u611f\u6fc0\u4f60\u7684\u3002\u5927\u7ea6\u54b1\u4eec\u771f\u662f\u66fe\u5728\u68a6 \u4e2d\u76f8\u9022\uff0c\u6240\u4ee5\u6211\u4e00\u89c1\u4f60\uff0c\u5c31\u4fe1\u6258\u4f60\u3002\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u5fc3\u795e\u52a8\u8361\uff0c\u731b\u7136\u60f3\u9053\uff1a\u201c\u6211\u73b0\u5728\u7fbd\u6bdb\u672a\u4e30\uff0c\u597d\u5bb9\u6613\u624d\u5f04\u5230\u5175\u6743\uff0c\u4e0d\u80fd\u4e3a\u4e86\u4e00\u4e2a\u5973 \u5b50\u574f\u4e86\u5927\u4e8b\u3002\u201d\u51af\u7433\u6ed4\u6ed4\u8bf4\u4e2a\u4e0d\u505c\uff0c\u5e74\u7fb9\u5c27\u6025\u9053\uff1a\u201c\u54b1\u4eec\u4ee5\u540e\u8fd8\u6709\u8bb8\u591a\u673a\u4f1a\uff0c\u4f60\u5148\u542c\u6211 \u8bf4\u3002\u201d\u51af\u7433\u4ef0\u9762\u671b\u4ed6\uff0c\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u4e0d\u4f1a\u5bf9\u7687\u4e0a\u8bf4\u51fa\u6211\u66fe\u6536\u85cf\u4f60\u5427\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u7edd\u5bf9\u4e0d \u4f1a\uff01\u201d\u5e74\u7fb9\u5c27\u5fc3\u4e2d\u4e00\u5bbd\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u8bf4\u8bdd\uff0c\u65e0\u4eba\u6562\u6297\u3002\u4f60\u5165\u5bab\u540e\uff0c\u4ed6\u5bf9\u4f60\u4e00\u5b9a\u5ba0\u7231\u4e07\u5206\uff0c\u90a3 \u65f6\u4f60\u5c31\u6bd4\u7687\u540e\u8fd8\u6709\u6743\u529b\uff0c\u54b1\u4eec\u4e00\u91cc\u4e00\u5916\uff0c\u4e92\u76f8\u7167\u5e94\uff0c\u55ef\uff0c\u4f60\u662f\u806a\u660e\u4eba\uff0c\u6211\u4e0d\u8bf4\u5566\uff01\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u5192\u9669\u8fdb\u6765\uff0c\u771f\u610f\u5728\u6b64\u3002\u51af\u7433\u542c\u4e86\uff0c\u5982\u53d7\u96f7\u7855\uff0c\u987f\u65f6\u8eab\u9a71\u9ebb\u6728\uff0c\u8111\u5b50\u7a7a\u6d1e\u6d1e\u7684\uff0c\u4ec0 \u4e48\u4e5f\u8bf4\u4e0d\u51fa\u6765\uff0c\u5979\u7edd\u672a\u6599\u5230\u5e74\u7fb9\u5c27\u7528\u5fc3\u5982\u6b64\u5351\u52a3\u3002 \u3000\u3000\u66f4\u9f13\u58f0\u58f0\uff0c\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u56de\u623f\u4e2d\u53bb\u5427\uff0c\u4e0d\u8981\u9732\u51fa\u8fd9\u91cc\u7684\u590d\u58c1\u6697\u95e8\u3002\u54b1\u4eec\u5fc3\u5fc3\u76f8\u5370\uff0c\u5f7c \u6b64\u6276\u6301\uff0c\u5927\u5bb6\u90fd\u6709\u597d\u5904\u3002\u59b9\u5b50\uff0c\u4f60\u662f\u806a\u660e\u4eba\uff0c\u6211\u4e0d\u591a\u8bf4\u4e86\u3002\u201d\u5176\u5b9e\u6b63\u662f\u4ed6\u4e0d\u653e\u5fc3\uff0c\u53c8\u591a\u8bf4\u4e86 \u4e00\u904d\u3002\u51af\u7433\u542c\u5230\u201c\u5fc3\u5fc3\u76f8\u5370\u201d\u4e4b\u8bed\uff0c\u7a81\u89c9\u4e00\u9635\u6076\u5fc3\u3002\u5e74\u7fb9\u5c27\u6454\u8131\u4e86\u5979\u7684\u624b\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u8be5\u8d70 \u5566\uff01\u201d \u3000\u3000\u9759\u591c\u4e2d\u5ffd\u95fb\u5f97\u811a\u6b65\u4e4b\u58f0\uff0c\u54c8\u5e03\u9640\u62cd\u95e8\u9053\uff1a\u201c\u7433\u8d35\u4eba\uff0c\u5f00\u95e8\u63a5\u9a7e\uff0c\u7687\u4e0a\u6765\u770b\u4f60\u5566\u3002\u201d\u539f\u6765 \u96cd\u6b63\u51c6\u5907\u660e\u65e5\u542f\u7a0b\u56de\u4eac\uff0c\u5fc3\u75d2\u96be\u71ac\uff0c\u4e34\u7761\u524d\u6765\u770b\u5979\u4e00\u6b21\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u624b\u6309\u5899\u4e0a\u673a\u62ec\uff0c\u4fbf\u5f85\u5f00\u542f\u5730\u9053\u6697\u95e8\uff0c\u51af\u7433\u5ffd\u7136\u4e00\u624b\u5c06\u4ed6\u6293\u7740\uff0c \u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u51c6\u8d70\uff01\u201d\u5916\u9762\u96cd\u6b63\u5927\u58f0\u53eb\u9053\uff1a\u201c\u7433\u513f\uff0c\u4f60\u7761\u4e86\u5417\uff1f\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u51b7\u6c57\u8ff8\u6d41\uff0c\u751f\u6015\u78b0\u649e\u58f0\u54cd\uff0c\u4e0d\u6562\u7528\u529b\u6454\u8131\u51af\u7433\u7684\u624b\uff0c\u53ea\u597d\u4f4e\u58f0\u592e\u544a\uff1a\u201c\u597d\u59b9\u5b50\uff0c \u522b\u5f00\u73a9\u7b11\uff0c\u5feb\u4e9b\u653e\u624b\uff01\u201d\u96cd\u6b63\u5728\u5916\u9762\u7b49\u4e86\u8bb8\u4e45\uff0c\u4e0d\u89c1\u5f00\u95e8\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u5728\u91cc\u9762\u505a\u4ec0\u4e48\uff1f\u201d\u53c8 \u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u54a6\uff0c\u6ca1\u4eba\u7b54\u5e94\uff01\u201d\u201c\u5582\uff0c\u4f60\u518d\u4e0d\u5f00\u95e8\uff0c\u6211\u53ef\u8981\u6253\u95e8\u8fdb\u53bb\u5566\uff01\u201d \u3000\u3000\u51af\u7433\u63e1\u7d27\u5e74\u7fb9\u5c27\u7684\u624b\u8155\uff0c\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u4f60\u5e26\u6211\u51fa\u53bb\uff0c\u5c06\u674e\u6cbb\u6551\u51fa\uff0c\u7136\u540e\u6211\u653e\u4f60 \u8d70\u3002\u201d\u5e74\u7fb9\u5c27\u53c8\u60ca\u53c8\u6012\uff0c\u51af\u7433\u53c8\u9053\uff1a\u201c\u4f60\u4e0d\u7b54\u5e94\uff0c\u6211\u5230\u4e0a\u58f0\u53eb\u56b7\uff01\u201d\u95e8\u5916\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u96cd \u6b63\u4e00\u62f3\u51fb\u5728\u95e8\u4e0a\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u80cc\u810a\u4e00\u649e\uff0c\u5f00\u4e86\u5899\u58c1\u6697\u95e8\uff0c\u5c06\u51af\u7433\u62d6\u8fdb\u5730\u9053\u3002\u6c14\u547c\u547c\u7684\u9053\uff1a\u201c\u4f60\u60f3\u5bb3\u6b7b\u6211\u5417\uff1f\u201d \u51af\u7433\u9053\uff1a\u201c\u6211\u53ea\u6709\u4e00\u53e5\u8bdd\uff0c\u4f60\u4e0d\u7b54\u5e94\u53bb\u6551\u674e\u6cbb\uff0c\u6211\u5c31\u53eb\u56b7\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5173 \u5728\u4f55\u5904\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u597d\uff0c\u6211\u8981\u53eb\u5566\uff01\u201d\u95e8\u5916\u53c8\u662f\u201c\u7830\u201d\u7684\u4e00\u58f0\uff01 \u3000\u3000\u5e74\u7fb9\u5c27\u8dd1\u4e86\u4e24\u6b65\uff0c\u6076\u5ff5\u987f\u751f\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u5982\u628a\u5979\u6740\u4e86\uff01\u201d\u51af\u7433\u7a81\u53eb\u4e86\u4e00\u58f0\uff0c\u96cd\u6b63\u5728\u5916\u9762 \u9053\uff1a\u201c\u554a\uff0c\u6211\u8fd8\u5f53\u4f60\u4e0d\u5728\u91cc\u9762\u5462\uff0c\u5feb\u5f00\u95e8\uff01\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u6025\u9053\uff1a\u201c\u597d\uff0c\u6211\u7b54\u5e94\u4f60\uff0c\u5feb\u522b\u4e71\u56b7\uff01\u201d\u5fc3\u60f3\uff1a\u201c\u5979\u6b66\u529f\u4e0d\u5f31\uff0c\u6740\u5979\u4e0d\u6210\uff0c\u90a3\u53ef\u66f4 \u7cdf\u3002\u800c\u4e14\u5728\u5730\u9053\u4e2d\u5c31\u7b97\u80fd\u5c06\u5979\u6740\u4e86\uff0c\u4e5f\u96be\u9003\u6389\u5173\u7cfb\u3002\u4e00\u88ab\u53d1\u73b0\uff0c\u80fd\u8fdb\u5730\u9053\u7684\u7591\u72af\uff0c\u9664\u4e86\u6211\u5c31 \u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\uff0c\u90a3\u65f6\u975e\u4f46\u5927\u5c06\u519b\u505a\u4e0d\u6210\u3001\u8bf4\u4e0d\u5b9a\u8fd8\u6709\u706d\u95e8\u4e4b\u7978\u3002\u201d \u3000\u3000\u96cd\u6b63\u5728\u5916\u9762\u7b49\u4e86\u4e00\u9635\uff0c\u91cc\u9762\u53c8\u65e0\u58f0\u606f\uff0c\u54c8\u5e03\u9640\u5782\u624b\u65c1\u7acb\uff0c\u773c\u775b\u5077\u770b\u7740\u4ed6\uff0c\u96cd\u6b63\u751a\u89c9\u4e0d\u597d \u610f\u601d\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e2b\u5934\u4e5f\u592a\u9a84\u7eb5\u5566\u3002\u5979\u7406\u4e5f\u4e0d\u7406\uff0c\u6211\u8fd9\u7687\u5e1d\u7684\u9762\u5b50\u6401\u5230\u90a3\u91cc\uff1f\u201d\u53eb\u4e86\u4e24\u58f0\uff0c \u91cc\u9762\u4ecd\u662f\u65e0\u4eba\u7b54\u5e94\u3002\u96cd\u6b63\u4e00\u6c14\uff0c\u6697\u8fd0\u6392\u5c71\u638c\u529b\uff0c\u5c06\u623f\u95e8\u6253\u584c\uff01 \u3000\u3000\u5e74\u7fb9\u5c27\u5728\u5730\u9053\u4e2d\u542c\u5f97\u5916\u9762\u8f70\u7136\u5de8\u54cd\uff0c\u9b42\u4e0d\u9644\u4f53\uff0c\u62c9\u7740\u51af\u7433\u6025\u8dd1\u3002\u51af\u7433\u9053\uff1a\u201c\u628a\u674e\u6cbb\u653e\u4e86 \u51fa\u6765\uff0c\u6211\u9a6c\u4e0a\u5c31\u8d70\uff0c\u8d70\u4e0d\u8131\u6211\u4e5f\u51b3\u4e0d\u4f9b\u4f60\uff01\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u8d70\u51fa\u5730\u9053\uff0c\u548c\u51af\u7433\u9690\u5728\u5047\u5c71\u80cc\u540e\uff0c\u6307\u7740\u897f\u8fb9\u7684\u4e00\u95f4\u5c0f\u5c4b\u9053\uff1a\u201c\u674e\u6cbb\u5173\u5728\u90a3\u513f\u3002\u201d \u51af\u7433\u9053\uff1a\u201c\u4f60\u66ff\u6211\u628a\u770b\u5b88\u7684\u4eba\u652f\u5f00\uff01\u201d\u5e74\u7fb9\u5c27\u7a0d\u73b0\u72b9\u7591\uff0c\u51af\u7433\u7b11\u9053\uff1a\u201c\u4f60\u5e72\u4e0d\u5e72\uff0c\u4f60\u4e0d\u5e72\u6211 \u53ef\u8981\u56de\u53bb\u89c1\u7687\u4e0a\u5566\uff01\u201d \u3000\u3000\u518d\u8bf4\u96cd\u6b63\u8e0f\u8ff8\u623f\u4e2d\uff0c\u6c93\u65e0\u4eba\u5f71\uff0c\u5927\u5403\u4e00\u60ca\u9053\uff1a\u201c\u96be\u9053\u521a\u624d\u90a3\u53eb\u58f0\u4e0d\u662f\u5979\u7684\uff1f\u201d\u54c8\u5e03\u9640\u66f4 \u662f\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u5974\u624d\u63a5\u73ed\u4e4b\u540e\uff0c\u8fd8\u89c1\u7a97\u5e18\u4e0a\u5979\u7684\u4eba\u5f71\uff0c\u59d4\u5b9e\u5e76\u65e0\u9003\u8d70\u3002\u201d\u96cd \u6b63\u806a\u660e\u7edd\u9876\uff0c\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5979\u5fc5\u5b9a\u8fd8\u5728\u5c4b\u4e2d\u3002\u201d \u3000\u3000\u590d\u58c1\u6697\u95e8\u4e4b\u7c7b\u7684\u673a\u5173\uff0c\u5728\u5bab\u4e2d\u662f\u53f8\u7a7a\u89c1\u60ef\uff0c\u96cd\u6b63\u5341\u5206\u5728\u884c\uff0c\u63a8\u5f00\u5927\u5e8a\uff0c\u4ece\u54c8\u5e03\u9640\u624b\u4e2d\u53d6 \u8fc7\u6d41\u661f\u9524\uff0c\u5728\u5899\u58c1\u56db\u56f4\u4e00\u626b\uff0c\u89e6\u52a8\u673a\u7ebd\uff0c\u6697\u95e8\u6253\u5f00\uff0c\u96cd\u6b63\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5c0f\u5e74\u8fd8\u6709\u8fd9\u624b\uff01\u201d\u54c8 \u5e03\u9640\u9053\uff1a\u201c\u56ed\u5185\u904d\u5e03\u7687\u4e0a\u4eb2\u4fe1\uff0c\u5c31\u662f\u5e74\u5c06\u519b\u548c\u5979\u4e00\u8d77\u4e5f\u9003\u4e0d\u6389\u3002\u4ed6\u4eec\u4e00\u5b9a\u8fd8\u5728\u5730\u9053\u4e4b\u4e2d\u3002\u201d \u96cd\u6b63\u9053\uff1a\u201c\u4f60\u5165\u53bb\u770b\uff0c\u82e5\u7136\u53d1\u73b0\uff0c\u5c31\u628a\u4ed6\u4eec\u2018\u8bf7\u2019\u51fa\u6765\uff0c\u4e0d\u51c6\u58f0\u5f20\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u54c8\u5e03\u9640\u9762\u8272\u4ed3\u7687\uff0c\u4ece\u5730\u9053\u4e2d\u8df3\u51fa\uff0c\u62a5\u9053\uff1a\u201c\u91cc\u9762\u6ca1\u4eba\uff01\u201d\u96cd\u6b63\u5ffd\u9053\uff1a\u201c\u4eca\u65e5 \u4e4b\u4e8b\uff0c\u4f60\u77e5\u6211\u77e5\uff0c\u53ef\u4e0d\u8bb8\u5bf9\u7b2c\u4e09\u4eba\u8bf4\uff01\u89c1\u4e86\u5e74\u5927\u5c06\u519b\uff0c\u4e5f\u8981\u606d\u656c\u5982\u5e38\uff0c\u51b3\u4e0d\u80fd\u9732\u51fa\u534a\u70b9\u8f9e \u8272\uff01\u201d\u96cd\u6b63\u57ce\u5e9c\u6781\u6df1\uff0c\u6b64\u65f6\u5fc3\u4e2d\u867d\u5df2\u840c\u4e86\u8981\u6740\u5e74\u7fb9\u5c27\u4e4b\u5ff5\uff0c\u4f46\u897f\u57df\u672a\u5e73\uff0c\u9c7c\u4eae\u672a\u9664\uff0c\u5f02\u5df1\u672a \u9504\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u5c31\u6b64\u548c\u5e74\u7fb9\u5c27\u51b3\u88c2\u3002 \u3000\u3000\u54c8\u5e03\u9640\u4e00\u8eab\u51b7\u6c57\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u5974\u624d\u77e5\u9053\u3002\u201d\u96cd\u6b63\u6325\u624b\u9053\uff1a\u201c\u5feb\u5230\u5916\u9762\u641c\u7d22\u3002\u6211\u6599\u5c0f\u5e74 \u4e5f\u4e0d\u6562\u8fd9\u6837\u5927\u80c6\uff0c\u6562\u548c\u7433\u4e2b\u5934\u4e00\u8d77\u5055\u9003\u3002\u201d\u54c8\u5e03\u9640\u201c\u55b3\u201d\u7684\u4e00\u58f0\uff0c\u7acb\u5373\u51fa\u95e8\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u8b66 \u53f7\u5927\u4f5c\uff01 \u3000\u3000\u5e74\u7fb9\u5c27\u7ed9\u51af\u7433\u903c\u5f97\u6ca1\u6cd5\uff0c\u542c\u5f97\u8b66\u53f7\u58f0\uff0c\u5fc3\u751f\u4e00\u8ba1\uff0c\u500f\u7136\u8dd1\u51fa\uff0c\u770b\u5b88\u674e\u6cbb\u7684\u4e43\u662f\u8f66\u8f9f\u90aa\uff0c \u6b63\u662f\u5e74\u7fb9\u5c27\u6240\u6536\u670d\u7684\u5fc3\u8179\u6b66\u58eb\uff0c\u5e74\u7fb9\u5c27\u671d\u4e1c\u8fb9\u4e00\u6307\uff0c\u9053\uff1a\u201c\u90a3\u8fb9\u62a5\u8b66\uff0c\u4f60\u5feb\u53bb\u5e2e\u624b\uff01\u201d \u3000\u3000\u8f66\u8f9f\u90aa\u4e00\u8d70\uff0c\u51af\u7433\u8df3\u4e86\u51fa\u6765\uff0c\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u6551\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u4e56\u4e56\u7684\u7ed9\u6211\u5750\u5728 \u90a3\u8fb9\u592a\u6e56\u77f3\u4e0a\uff0c\u522b\u60f3\u6253\u574f\u4e3b\u610f\u5566\u3002\u5f85\u627e\u51fa\u6765\u53eb\u4f60\u8d70\u4f60\u624d\u8bb8\u8d70\u3002\u201d\u63d0\u5251\u8df3\u5165\u623f\u4e2d\u3002 \u3000\u3000\u96cd\u6b63\u8bd5\u4e86\u5b88\u5bab\u7802\u540e\uff0c\u5df2\u77e5\u674e\u6cbb\u548c\u51af\u7433\u5e76\u65e0\u5173\u7cfb\uff0c\u800c\u4e14\u53c8\u60f3\u5c06\u4ed6\u6536\u670d\u3002\u6240\u4ee5\u5bf9\u4ed6\u5e76\u65e0\u8650 \u5f85\uff0c\u8fde\u67b7\u9501\u4e5f\u672a\u4e0a\uff0c\u51af\u7433\u8df3\u5165\u623f\u4e2d\uff0c\u53eb\u9053\uff1a\u201c\u673a\u4e0d\u53ef\u5931\uff0c\u5feb\u968f\u6211\u9003\uff01\u201d \u3000\u3000\u674e\u6cbb\u8fd9\u4e24\u5929\u604d\u5982\u53d1\u4e86\u4e00\u573a\u68a6\uff0c\u4e07\u5343\u7591\u95ee\uff0c\u585e\u6ee1\u5fc3\u80f8\uff0c\u4f46\u6b64\u65f6\u6b64\u9645\uff0c\u4e5f\u5bb9\u4e0d\u5f97\u4ed6\u53d1\u95ee\uff0c\u968f \u7740\u51af\u7433\uff0c\u7a7f\u7a97\u8df3\u51fa\u3002 \u3000\u3000\u56ed\u4e2d\u4eba\u5f71\u5e62\u5e62\uff0c\u7eb7\u5411\u51af\u7433\u6240\u4f4f\u7684\u5730\u65b9\u8dd1\u53bb\u3002\u51af\u7433\u8df3\u5230\u5047\u5c71\u80cc\u540e\uff0c\u5bf9\u5e74\u7fb9\u5c27\u7b11\u9053\uff1a\u201c\u4f60\u53ef \u4ee5\u8d70\u5566\uff01\u201d\u5979\u5bf9\u5e74\u7fb9\u5c27\u7684\u7231\u610f\uff0c\u867d\u7136\u5982\u767d\u4e91\u906d\u9047\u72c2\u98ce\uff0c\u88ab\u5e74\u7fb9\u5c27\u7684\u4e00\u5e2d\u8bdd\u626b\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u4f46 \u5230\u5e95\u8fd8\u6709\u4e00\u4e9b\u670b\u53cb\u60c5\u4efd\uff0c\u6240\u4ee5\u4e5f\u4e0d\u60f3\u8fc7\u4efd\u628a\u4ed6\u96be\u4e3a\uff0c\u53ea\u60f3\u51ed\u81ea\u5df1\u7684\u8fd0\u6c14\u9003\u8dd1\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u4f38\u51fa\u624b\u6765\uff0c\u9053\uff1a\u201c\u4f46\u613f\u4f60\u9003\u51fa\u864e\u53e3\uff0c\u54b1\u4eec\u82e5\u662f\u6709\u7f18\uff0c\u6765\u751f\u518d\u76f8\u89c1\u5427\u3002\u201d\u51af\u7433\u5fc3\u4e2d \u4e00\u9178\uff0c\u4f38\u624b\u4e0e\u4ed6\u76f8\u63e1\uff0c\u5e74\u7fb9\u5c27\u53cd\u624b\u4e00\u62ff\u5979\u7684\u8109\u95e8\uff0c\u7a81\u7136\u4e00\u638c\u5411\u5979\u8111\u95e8\u51fb\u4e0b\u3002 \u3000\u3000\u539f\u6765\u5e74\u7fb9\u5c27\u6743\u8861\u5229\u5bb3\uff0c\u5fc3\u60f3\uff1a\u4ed6\u4e8c\u4eba\u4e00\u5b9a\u9003\u4e0d\u8131\uff0c\u5c31\u7b97\u9003\u8131\u3002\u5979\u5bf9\u81ea\u5df1\u60c5\u4e49\u5df2\u7edd\uff0c\u7559\u7740 \u4e5f\u662f\u7978\u6b83\uff0c\u773c\u770b\u5979\u5c31\u8981\u548c\u674e\u6cbb\u5192\u9669\u5055\u9003\u3002\u5fc3\u4e2d\u4e00\u6025\uff0c\u6697\u9053\uff1a\u201c\u4e0e\u5176\u8ba9\u5979\u8d1f\u6211\uff0c\u4e0d\u5982\u7531\u6211\u8d1f \u5979\u3002\u800c\u4eca\u51fa\u4e86\u5730\u9053\u3002\u6211\u4e00\u638c\u628a\u5979\u51fb\u6b7b\uff0c\u8c01\u6562\u7591\u662f\u6211\u6740\uff1f\u201d\u4ed6\u77e5\u51af\u7433\u6b66\u529f\u4e0d\u9519\uff0c\u6240\u4ee5\u6545\u610f\u7528\u8bf4 \u8bdd\u6fc0\u52a8\u5979\u7684\u5fc3\u5f26\uff0c\u8d81\u5979\u5206\u5fc3\u4e4b\u9645\uff0c\u7a81\u65bd\u6740\u624b\u3002 \u3000\u3000\u674e\u6cbb\u8d70\u5728\u524d\u5934\uff0c\u89c1\u5979\u4e0e\u5e74\u7fb9\u5c27\u6709\u8bf4\u6709\u7b11\uff0c\u60ca\u7591\u4e0d\u5df2\uff0c\u5173\u5fc3\u8fc7\u751a\uff1b\u9685\u4e00\u56de\u5934\uff0c\u5927\u60ca\u53eb\u9053\uff1a \u201c\u4f60\u505a\u751a\u4e48\uff1f\u201d\u53cd\u624b\u4e00\u638c\uff0c\u5c06\u5e74\u7fb9\u5c27\u624b\u81c2\u683c\u5f00\uff0c\u53f3\u624b\u5251\u633d\u4e86\u4e00\u4e2a\u9006\u82b1\uff0c\u53cd\u8eab\u75be\u523a\u5e74\u7fb9\u5c27\u80f8 \u80c1\uff0c\u5e74\u7fb9\u5c27\u8eab\u5b50\u4e00\u7f29\uff0c\u62ff\u7740\u51af\u7433\u7684\u624b\u81ea\u7136\u677e\u5f00\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u767b\u65f6\u60ca\u52a8\u4e86\u56ed\u4e2d\u4f8d\u536b\uff0c\u7acb\u523b\u6709\u4eba\u8dd1\u6765\uff0c\u674e\u6cbb\u6c14\u547c\u547c\u7684\u72b9\u60f3\u8ffd\u6740\uff0c\u51af\u7433\u6025\u5fd9\u626f\u4ed6\u8eb2 \u5165\u82b1\u6811\u4e1b\u4e2d\uff0c\u674e\u6cbb\u9053\uff1a\u201c\u8fd9\u5978\u8d3c\u5982\u6b64\u9634\u6bd2\uff0c\u771f\u662f\u4eba\u95f4\u5c11\u89c1\uff01\u201d\u51af\u7433\u907f\u5f00\u674e\u6cbb\u773c\u5149\uff0c\u4f4e\u58f0\u8bf4 \u9053\uff1a\u201c\u4e0d\u8981\u7406\u4ed6\uff0c\u54b1\u4eec\u5feb\u8d70\uff01\u201d\u5fc3\u4e2d\u7f9e\u6127\u4e4b\u6781\uff01 \u3000\u3000\u5e74\u5bb6\u82b1\u56ed\u751a\u5927\uff0c\u5047\u5c71\u6811\u6728\uff0c\u5e03\u5c40\u5947\u5de7\uff0c\u4e0d\u719f\u6089\u9053\u8def\u7684\u4eba\uff0c\u8d70\u534a\u5929\u4e5f\u672a\u5fc5\u8d70\u5f97\u51fa\u53bb\uff0c\u51af\u7433 \u5e26\u7740\u674e\u6cbb\u7a7f\u82b1\u7ed5\u77f3\uff0c\u501f\u884c\u969c\u5f62\uff0c\u4e00\u8def\u6025\u8d70\uff0c\u5ffd\u542c\u5f97\u5929\u53f6\u6563\u4eba\u5927\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u6765\u5440\uff0c\u5b88\u7740\u5de6 \u8fb9\u7684\u4ead\u5b50\u548c\u53f3\u8fb9\u7684\u5047\u5c71\uff0c\u7136\u540e\u5411\u4e2d\u592e\u641c\u7d22\uff01\u201d\u51af\u7433\u5077\u5077\u671b\u51fa\uff0c\u53ea\u89c1\u5e74\u7fb9\u5c27\u4e0e\u5929\u53f6\u6563\u4eba\uff0c\u7ad9\u5728 \u592a\u6e56\u77f3\u4e0a\uff0c\u628a\u624b\u6307\u5411\u81ea\u5df1\u85cf\u8eab\u4e4b\u5730\uff0c\u51af\u7433\u6697\u4e2d\u53eb\u82e6\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u6bd4\u6211\u66f4\u719f\u56ed\u4e2d\u9053\u8def\uff0c\u8fd9\u5374\u5982 \u4f55\u662f\u597d\u3002\u201d\u674e\u6cbb\u4fbf\u60f3\u95ef\u51fa\uff0c\u51af\u7433\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u53ea\u89c1\u5341\u591a\u4eba\u5206\u4e09\u9762\u5305\u6284\u800c\u6765\uff0c\u51af\u7433\u5fc3\u4e2d\u76d8\u7b97 \u9053\uff1a\u201c\u4ed6\u4e0d\u4ec1\u6211\u4e0d\u4e49\uff0c\u8c05\u90a3\u4e9b\u4eba\u4e0d\u6562\u4f24\u6211\u6027\u547d\uff0c\u6211\u89c1\u4e86\u7687\u5e1d\u7acb\u523b\u63ed\u7834\u4ed6\u7684\u5978\u8c0b\uff0c\u7136\u540e\u81ea \u6740\u3002\u201d\u4e3b\u610f\u6253\u5b9a\uff0c\u53cd\u89c9\u80f8\u4e2d\u6cf0\u7136\uff0c\u53ea\u89c1\u90a3\u4e9b\u5f85\u536b\u4e09\u8def\u5305\u6765\uff0c\u8d8a\u6765\u8d8a\u8fd1\u3002\u51af\u7433\u7275\u7740\u674e\u6cbb\u7684\u624b\uff0c \u53ea\u89c9\u4ed6\u7684\u624b\u5fc3\u5df2\u5728\u6dcc\u6c57\u3002 \u3000\u3000\u51af\u7433\u5fc3\u9053\uff1a\u201c\u53ef\u601c\u7684\u674e\u6cbb\u54e5\u54e5\uff0c\u4ed6\u90a3\u65e5\u8eab\u9677\u91cd\u56f4\uff0c\u5c1a\u4e0d\u754f\u6b7b\u3002\u800c\u4ee4\u624b\u98a4\u811a\u9707\uff0c\u90a3\u51b3\u4e0d\u4f1a \u56e0\u4e3a\u81ea\u8eab\u7684\u5b89\u5371\uff0c\u800c\u662f\u4e3a\u6211\u62c5\u5fc3\u7684\u4e86\uff01\u201d\u6a2a\u4e86\u5fc3\u80a0\uff0c\u4fbf\u60f3\u5355\u8eab\u8df3\u51fa\u5f15\u5f00\u654c\u4eba\u3002\u5ffd\u89c1\u90a3\u4e9b\u5f85\u536b \u4e09\u8def\u7a7f\u63d2\uff0c\u4ece\u65c1\u8fb9\u4e0d\u8fdc\u4e4b\u5904\u8d70\u8fc7\uff0c\u5374\u6ca1\u4e00\u4eba\u8d70\u8fd1\u5047\u5c71\uff0c\u4e0d\u89c9\u5927\u5947\uff01 \u3000\u3000\u539f\u6765\u4e0d\u4f46\u51af\u7433\u4e0e\u674e\u6cbb\u5fc3\u60ca\uff0c\u5e74\u7fb9\u5c27\u66f4\u662f\u5fc3\u60ca\u80c6\u6218\uff0c\u4ed6\u4e5f\u6599\u60f3\u5230\u51af\u7433\u82e5\u7136\u88ab\u6355\uff0c\u4e00\u5b9a\u4f1a\u628a \u4ed6\u7684\u4f5c\u4e3a\u6296\u9732\u51fa\u6765\u3002\u8fd9\u65f6\u4ed6\u53ea\u6709\u6781\u529b\u60f3\u6cd5\u6697\u52a9\u4ed6\u4eec\u9003\u8131\uff0c\u867d\u4e0d\u5f97\u5df2\uff0c\u4ea6\u8981\u4e3a\u4e4b\uff01 \u3000\u3000\u5e74\u5bb6\u82b1\u56ed\uff0c\u5e03\u5c40\u672c\u6781\u5947\u5de7\uff0c\u5047\u5c71\u82b1\u6728\u628a\u9053\u8def\u95f4\u5f97\u5f2f\u5f2f\u66f2\u66f2\uff0c\u4e00\u5ea7\u5047\u5c71\uff0c\u660e\u660e\u5c31\u5728\u76ee\u524d\uff0c \u53ef\u662f\u8981\u7ed5\u51e0\u4e2a\u5f2f\u624d\u8d70\u5f97\u5230\uff0c\u53c8\u503c\u65f6\u5f53\u5348\u591c\uff0c\u6708\u6697\u57a6\u7a00\uff0c\u66f4\u96be\u770b\u5f97\u6e05\u695a\u3002\u5e74\u7fb9\u5c27\u6307\u70b9\u9053\u8def\uff0c\u5c06 \u90a3\u4e9b\u4f8d\u536b\u90fd\u5f15\u5230\u56ed\u5b50\u7684\u4e2d\u592e\uff0c\u6070\u6070\u8ba9\u51af\u7433\u907f\u8fc7\u3002 \u3000\u3000\u51af\u7433\u677e\u4e86\u53e3\u6c14\uff0c\u7275\u7740\u674e\u6cbb\u4ece\u5047\u5c71\u6d1e\u53e3\u7a7f\u51fa\uff0c\u86c7\u884c\u5154\u4f0f\uff0c\u4e1c\u95ea\u897f\u8eb2\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u56ed\u95e8\u5df2\u7ecf \u5728\u671b\uff0c\u51af\u7433\u5bf9\u674e\u6cbb\u9053\uff1a\u201c\u524d\u9762\u662f\u4e00\u7247\u8377\u5858\uff0c\u4e00\u5b9a\u8981\u821f\u63d6\u624d\u80fd\u901a\u8fc7\uff0c\u6b64\u5916\u522b\u65e0\u901a\u8def\u3002\u4e0d\u8fc7\u8377\u642a \u4e24\u8fb9\u5404\u7cfb\u6709\u6570\u53ea\u83b2\u8239\uff0c\u5858\u4e2d\u5fc3\u6709\u4e00\u5757\u9732\u51fa\u6765\u7684\u77f3\u5934\uff0c\u8fd9\u8377\u5858\u5bbd\u7ea6\u5341\u4e08\uff0c\u5858\u4e2d\u5fc3\u7684\u77f3\u5934\u79bb\u4e24\u5cb8 \u6700\u8fd1\u7684\u90a3\u53ea\u83b2\u8239\u7ea6\u4e09\u4e08\u591a\uff0c\u4f60\u77a7\u51c6\u4e86\u77f3\u5934\u7684\u4f4d\u7f6e\uff0c\u770b\u6211\u4e00\u6253\u624b\u52bf\uff0c\u4f60\u7acb\u523b\u8df3\u51fa\uff0c\u8dc3\u4e0a\u83b2\u8239\uff0c \u501f\u77f3\u5934\u4e3a\u8e0f\u811a\uff0c\u98de\u8fc7\u5bf9\u5cb8\u3002\u4f60\u77a7\u51c6\u4e86\uff1f\u201d\u674e\u6cbb\u70b9\u4e86\u70b9\u5934\uff0c\u51af\u7433\u4e00\u770b\u5bf9\u5cb8\u65e0\u4eba\uff0c\u6253\u4e86\u4e00\u4e2a\u624b \u52bf\uff0c\u674e\u6cbb\u7acb\u523b\u4f9d\u8a00\u8dc3\u8fc7\u5bf9\u5cb8\u3002 \u3000\u3000\u5c82\u77e5\u5bf9\u5cb8\u6709\u53cc\u9b54\u57cb\u4f0f\uff0c\u674e\u6cbb\u4eba\u5728\u534a\u7a7a\uff0c\u811a\u672a\u843d\u5730\uff0c\u8428\u5929\u523a\u7a81\u4ece\u6697\u9edd\u4e4b\u5904\u8dc3\u51fa\uff0c\u5341\u722a\u9f50 \u4f38\uff0c\u51ed\u7a7a\u63a0\u8d77\uff0c\u5411\u674e\u6cbb\u817f\u5f2f\u75be\u6293\uff1b\u8981\u628a\u4ed6\u786c\u62c9\u4e0b\u6765\uff01 \u3000\u3000\u597d\u4e2a\u674e\u6cbb\uff0c\u4e34\u5371\u4e0d\u4e71\uff0c\u4eba\u5728\u7a7a\u4e2d\uff0c\u4e00\u5251\u523a\u4e0b\uff0c\u8428\u5929\u523a\u624b\u638c\u4e00\u7f29\uff0c\u674e\u6cbb\u8eab\u5b50\u4e0b\u5760\u3002\u5c82\u77e5\u8428 \u5929\u523a\u64c5\u957f\u732b\u9e70\u8865\u51fb\u4e4b\u6280\uff0c\u79bb\u4e86\u5730\u9762\uff0c\u8eab\u5b50\u5c1a\u53ef\u4f38\u5c48\u81ea\u5982\uff0c\u4ed6\u624b\u638c\u4e00\u7f29\u4e00\u7ffb\uff0c\u907f\u8fc7\u5251\u950b\uff0c\u5341\u6307 \u957f\u7532\u4e00\u5f39\uff0c\u84e6\u7136\u642d\u4e0a\u4e86\u674e\u6cbb\u80a9\u5934\uff0c\u4e24\u4eba\u4e00\u9f50\u5760\u5730\u3002 \u3000\u3000\u674e\u6cbb\u9aa4\u7136\u4e00\u75db\uff0c\u5de6\u8098\u4e00\u649e\uff0c\u6446\u8131\u654c\u4eba\uff0c\u5237\u5237\u4e24\u5251\uff0c\u8fd8\u653b\u8fc7\u53bb\u3002\u8428\u5929\u523a\u9053\uff1a\u201c\u54c8\uff0c\u5c0f\u8d3c\uff0c \u4f60\u8fd8\u60f3\u9003\uff1f\u201d\u8fdb\u62db\u518d\u6251\uff0c\u8428\u5929\u90fd\u5927\u543c\u4e00\u58f0\uff0c\u4ece\u65c1\u63a9\u51fa\uff0c\u547c\u547c\u4e24\u638c\uff0c\u628a\u674e\u6cbb\u9707\u5f97\u8eab\u5f62\u4e0d\u5b9a\uff0c\u8428 \u592d\u523a\u53c8\u662f\u4e00\u6293\u6293\u4e0b\uff01\u5ffd\u5730\u773c\u775b\u4e00\u4eae\uff0c\u51af\u7433\u4e5f\u5df2\u8dc3\u8fc7\u5bf9\u5cb8\uff0c\u88d9\u5e26\u98ce\u98d8\uff0c\u7bad\u4e00\u822c\u98de\u5c04\u8fc7\u6765\uff0c\u8428\u5929 \u523a\u5fc3\u4e2d\u4e00\u9707\uff0c\u4e00\u6293\u6293\u7a7a\uff0c\u8033\u8fb9\u53ea\u542c\u5f97\u51af\u7433\u53eb\u9053\uff1a\u201c\u8428\u4f2f\u4f2f\uff0c\u54c8\u5e03\u9640\u4ed6\u4eec\u90fd\u5728\u5bf9\u5cb8\uff0c\u4f60\u4f55\u82e6\u5b9a \u8981\u62e6\u622a\u6211\u4eec\uff1f\u201d\u8428\u5929\u523a\u7565\u4e00\u8fdf\u7591\uff0c\u51af\u7433\u5df2\u62c9\u4e86\u674e\u6cbb\u4ece\u4ed6\u722a\u5e95\u63a0\u51fa\uff0c\u8428\u5929\u90fd\u4e00\u638c\u5288\u53bb\uff0c\u8428\u5929\u523a \u5c0f\u58f0\u9053\uff1a\u201c\u7531\u5979\u53bb\u5427\u3002\u201d\u51af\u7433\u674e\u6cbb\u7684\u8eab\u6cd5\u4f55\u7b49\u5feb\u75be\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u5df2\u8eb2\u8fdb\u82b1\u4e1b\u3002 \u3000\u3000\u53ef\u662f\uff0c\u56ed\u4e2d\u5f85\u536b\u95fb\u5f97\u8428\u5929\u90fd\u90a3\u58f0\u5927\u543c\uff0c\u4e5f\u5df1\u7eb7\u7eb7\u8d76\u672a\uff0c\u54c8\u5e03\u9640\u7684\u526f\u624b\u5f6d\u4e91\u5e94\u628a\u5b88\u56ed\u95e8\uff0c \u9996\u5148\u6765\u5230\u3002\u8428\u5929\u523a\u4efb\u51ed\u662f\u600e\u6837\u60f3\u6697\u62a4\u51af\u7433\uff0c\u4e5f\u4e0d\u6562\u9690\u7792\u4e86\uff01 \u3000\u3000\u5f6d\u4e91\u5e94\u95ee\u9053\uff1a\u201c\u90a3\u4e2b\u5934\u5df2\u9003\u5230\u8fd9\u91cc\u5417\uff1f\u201d\u8428\u5929\u523a\u9053\uff1a\u201c\u6b63\u662f\uff0c\u5979\u548c\u90a3\u5c0f\u8d3c\u4e00\u9f50\u9003\u4e86\u3002\u201d \u5f6d\u4e91\u5e94\u9053\uff1a\u201c\u5728\u54ea\u4e2a\u65b9\u5411\uff1f\u201d\u8428\u5929\u523a\u9053\uff1a\u201c\u5979\u4e24\u4eba\u5341\u5206\u6e9c\u6ed1\uff0c\u6211\u4e0e\u4ed6\u4eec\u4ea4\u624b\u4e00\u62db\uff0c\u5df2\u5c06\u90a3\u5c0f \u8d3c\u6293\u4f24\uff0c\u4f46\u8fd8\u662f\u7ed9\u4ed6\u4eec\u6e9c\u8131\uff0c\u9ed1\u591c\u4e2d\u770b\u4e0d\u6e05\u695a\uff0c\u5927\u7ea6\u662f\u8d70\u5411\u90a3\u8fb9\u3002\u201d\u80e1\u4e71\u4e00\u6307\u3002\u5f6d\u4e91\u5e94\u9053\uff1a \u201c\u597d\uff0c\u5927\u5bb6\u5c0f\u5fc3\u641c\u7d22\uff01\u4ed6\u4eec\u63d2\u7ffc\u96be\u98de\uff01\u201d\u53d1\u4e86\u4e24\u679d\u54cd\u7bad\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u54c8\u5e03\u9640\u4e0e\u5929\u53f6\u6563\u4eba\u9996\u5148 \u8d76\u5230\u3002\u51af\u7433\u5fc3\u4e2d\u4e00\u51c9\uff0c\u8fd9\u4e24\u4eba\u6b66\u529f\u5353\u7edd\uff0c\u800c\u4e14\u8fd9\u534a\u8fb9\u56ed\u5b50\u5730\u65b9\u8fdc\u4e0d\u53ca\u8377\u5858\u90a3\u8fb9\u4e4b\u5e7f\uff0c\u5047\u5c71\u6811 \u6728\u4e5f\u8fdc\u4e0d\u53ca\u90a3\u8fb9\u4e4b\u591a\uff0c\u5305\u56f4\u5708\u6e10\u6e10\u7d27\u7f29\uff0c\u90a3\u662f\u4e07\u96be\u9003\u4e86\u3002 \u3000\u3000\u674e\u6cbb\u9053\uff1a\u201c\u54b1\u4eec\u4e0e\u4ed6\u4eec\u62fc\u4e86\u5427\uff01\u201d\u51af\u7433\u6447\u4e86\u6447\u5934\u3002\u55a7\u95f9\u58f0\u4e2d\uff0c\u5ffd\u542c\u5f97\u54c8\u5e03\u9640\u5927\u58f0\u53eb\u9053\uff1a \u201c\u7433\u8d35\u4eba\uff0c\u7687\u4e0a\u8bf7\u4f60\u56de\u53bb\uff01\u201d\u51af\u7433\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u81ea\u5df1\u4e0e\u54c8\u5e03\u9640\u8ddd\u79bb\u5c1a\u6709\u6570\u4e08\u4e4b\u8fdc\uff0c\u96be\u9053 \u4ed6\u5df2\u770b\u51fa\u4e86\u6211\u85cf\u8eab\u4e4b\u6240\uff1f\u6b63\u60f3\u633a\u8eab\u800c\u51fa\uff0c\u5ffd\u95fb\u5f97\u4f8d\u536b\u4eec\u7eb7\u7eb7\u53eb\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u4e86\uff0c\u5728\u8fd9\u91cc \u4e86\uff01\u201d\u811a\u6b65\u58f0\u5374\u5411\u897f\u5317\u89d2\u5954\u53bb\uff0c\u8fde\u54c8\u5e03\u9640\u548c\u5929\u53f6\u6563\u4eba\u7684\u58f0\u97f3\u4e5f\u4f3c\u5230\u4e86\u90a3\u91cc\uff0c\u51af\u7433\u5947\u9053\uff1a\u201c\u4ee4 \u665a\u4e4b\u4e8b\uff0c\u771f\u771f\u4ee4\u6211\u83ab\u540d\u5176\u5999\uff01\u4ed6\u4eec\u89c1\u9b3c\u4e86\u4e48\uff1f\u201d\u4f0f\u5728\u82b1\u4e2d\uff0c\u4ecd\u7136\u4e0d\u6562\u4e71\u52a8\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u5ffd\u542c \u5f97\u5175\u5203\u78b0\u78d5\uff0c\u547c\u558a\u53ae\u6740\u4e4b\u58f0\uff0c\u81ea\u5df1\u5468\u56f4\u6570\u4e08\u4e4b\u5185\u7a7a\u8361\u8361\u7684\u4ec0\u4e48\u4eba\u4e5f\u6ca1\u6709\u4e86\u3002\u51af\u7433\u9053\uff1a\u201c\u8fd9\u771f \u662f\u5929\u8d50\u826f\u673a\uff0c\u4f60\u968f\u6211\u6765\uff0c\u54b1\u4eec\u5feb\u5feb\u9003\u51fa\u8fd9\u4e2a\u56ed\u5b50\uff01\u201d \u3000\u3000\u53ae\u6740\u58f0\u4e2d\u95fb\u5f97\u6709\u5973\u5b50\u7684\u58f0\u97f3\uff0c\u90a3\u58f0\u97f3\u548c\u51af\u7433\u7684\u5341\u5206\u76f8\u4f3c\uff0c\u4f46\u7fa4\u58f0\u5608\u6742\uff0c\u5973\u58f0\u867d\u7136\u7279\u522b\u523a \u8033\uff0c\u5374\u662f\u542c\u4e0d\u4e0d\u6e05\u695a\u3002\u674e\u6cbb\u5728\u5929\u5c71\u957f\u5927\uff0c\u65f6\u65f6\u767b\u9ad8\u8fdc\u773a\uff0c\u773c\u529b\u6781\u597d\uff0c\u8fdc\u671b\u8fc7\u53bb\uff0c\u5ffd\u7136\u4f4e\u58f0\u53eb \u9053\uff1a\u201c\u54a6\uff0c\u771f\u5947\u602a\uff0c\u90a3\u8fb9\u6709\u4e00\u4e2a\u5973\u5b50\u5341\u5206\u4f3c\u4f60\uff01\u201d \u3000\u3000\u51af\u7433\u5fc3\u4e2d\u4e00\u9707\uff0c\u62ac\u8ddf\u671b\u65f6\uff0c\u90a3\u5973\u5b50\u9677\u5728\u91cd\u56f4\uff0c\u53c8\u5df2\u88ab\u4eba\u906e\u7740\uff0c\u770b\u4e0d\u89c1\u4e86\u3002\u51af\u7433\u65e5\u601d\u591c \u60f3\uff0c\u5e0c\u671b\u63a2\u51fa\u81ea\u5df1\u7684\u8eab\u4e16\u4e4b\u8c1c\uff0c\u800c\u4eca\u5df2\u73b0\u4e86\u66d9\u5149\uff0c\u4e16\u95f4\u771f\u6709\u4e00\u4e2a\u548c\u81ea\u5df1\u5341\u5206\u76f8\u4f3c\u7684\u4eba\uff01\u8fd9\u970e \u90a3\u95f4\u51af\u7433\u60ca\u559c\u9519\u6115\uff0c\u601d\u6f6e\u6f8e\u6e43\uff0c\u4f46\u8eab\u5728\u9f99\u6f6d\u864e\u7a74\u4e4b\u4e2d\uff0c\u867d\u7136\u4f38\u624b\u53ef\u4ee5\u63ed\u53bb\u795e\u79d8\u7684\u5e18\u5e55\uff0c\u51af\u7433 \u4e5f\u903c\u5f97\u653e\u5f03\u4e86\u3002\u4ed6\u4eec\u4e8c\u4eba\u5c31\u5728\u4f17\u4f8d\u536b\u653b\u90a3\u5973\u5b50\u7684\u55a7\u95f9\u58f0\u4e2d\uff0c\u6084\u6084\u9003\u51fa\u56ed\u53bb\u3002 \u3000\u3000\u51af\u745b\u4e5f\u6b63\u662f\u4e3a\u63a2\u7d22\u81ea\u5df1\u8eab\u4e16\u4e4b\u8c1c\u800c\u6765\uff0c\u5979\u5230\u5e74\u5bb6\uff0c\u4e5f\u6b63\u662f\u8981\u627e\u5bfb\u4e16\u754c\u4e0a\u7684\u53e6\u4e00\u4e2a\u201c\u81ea \u5df1\u201d\u3002\u4e0d\u6599\u4e00\u8fdb\u56ed\u5b50\uff0c\u4fbf\u9677\u91cd\u56f4\uff0c\u6709\u4eba\u53eb\u5979\u201c\u7433\u8d35\u4eba\u201d\uff0c\u6709\u4eba\u9a82\u5979\u201c\u91ce\u4e2b\u5934\u201d\uff0c\u6709\u4eba\u529d\u5979\u56de \u53bb\uff0c\u6709\u4eba\u903c\u5979\u5c31\u8303\uff0c\u55a7\u56b7\u7eb7\u95f9\uff0c\u51e0\u4e4e\u4ee4\u5979\u4ee5\u4e3a\u8fdb\u4e86\u75af\u72c2\u4e16\u754c\u3002\u51af\u745b\u4e00\u9635\u8ff7\u832b\uff0c\u5fc3\u60f3\u5927\u7ea6\u6709\u4e00 \u4e2a\u4eba\u540d\u5b57\u4e2d\u6709\u4e00\u4e2a\u201c\u7433\u201d\u5b57\u7684\u548c\u81ea\u5df1\u5341\u5206\u76f8\u4f3c\uff0c\u53ef\u662f\u8fd9\u4eba\u5728\u90a3\u91cc\u5462\uff1f\u5c31\u5728\u8fd9\u56ed\u5b50\u91cc\u5417\uff1f\u5979\u60f3 \u559d\u95ee\uff0c\u53ef\u662f\u5728\u5305\u56f4\u4e4b\u4e2d\uff0c\u5979\u7684\u95ee\u8bdd\u88ab\u6df9\u6ca1\u5728\u58f0\u97f3\u7684\u6d77\u6d0b\uff0c\u6709\u4eba\u8981\u4f38\u624b\u6765\u6349\u5979\u4e86\u3002\u51af\u745b\u903c\u5f97\u628a \u5929\u5c71\u5251\u6cd5\u65bd\u5c55\u5f00\u6765\uff0c\u55d6\u55d6\u8fde\u58f0\uff0c\u51b7\u7535\u7cbe\u8292\uff0c\u903c\u5f97\u4f8d\u536b\u4eec\u7eb7\u7eb7\u9000\u540e\uff0c\u5929\u53f6\u6563\u4eba\u51b2\u5165\u91cd\u56f4\uff0c\u5de6\u624b \u5212\u4e86\u534a\u4e2a\u5706\u5f27\uff0c\u4e00\u62db\u201c\u98ce\u5377\u6b8b\u4e91\u201d\uff0c\u5411\u51af\u745b\u7684\u624b\u8155\u75be\u6293\uff01\u51af\u745b\u5403\u4e86\u4e00\u60ca\uff0c\u6b64\u4eba\u624b\u6cd5\u4e4b\u8fc5\u75be\u5201 \u6bd2\uff0c\u8fdc\u5728\u4e00\u4f17\u5f85\u536b\u4e4b\u4e0a\uff0c\u9999\u80a9\u5fae\u6643\uff0c\u4fa7\u8eab\u8fd8\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u53c8\u51c6\u53c8\u5feb\uff0c\u6240\u6233\u7684\u65b9\u4f4d\u6070\u5230\u597d\u5904\uff0c\u987f\u65f6\u653b\u5b88\u6613\u52bf\uff0c\u5929\u53f6\u6563\u4eba\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u903c\u5f97 \u53d8\u62db\u540e\u9000\u3002\u5bf9\u51af\u745b\u7684\u5251\u6cd5\u4e4b\u7cbe\uff0c\u60ca\u5f02\u4e0d\u5df2\uff01\u5fc3\u9053\uff1a\u8fd9\u201c\u91ce\u4e2b\u5934\u201d\u51fa\u4e86\u7687\u5e9c\u4e4b\u540e\uff0c\u4e0d\u8fc7\u5e74\u591a\uff0c \u54ea\u91cc\u5b66\u6765\u7684\u8fd9\u624b\u5251\u6cd5\uff1f \u3000\u3000\u4f8d\u536b\u867d\u591a\uff0c\u53ef\u662f\u56e0\u4e3a\u5927\u5bb6\u90fd\u628a\u5979\u5f53\u505a\u51af\u7433\uff0c\u51af\u7433\u4e43\u662f\u7687\u5e1d\u6240\u5ba0\u7231\u7684\u672a\u6765\u8d35\u5983\uff0c\u8c01\u6562\u4f24 \u5979\uff1f\u4e00\u6709\u987e\u5fcc\uff0c\u53cd\u800c\u88ab\u51af\u745b\u6233\u4f24\u51e0\u4eba\uff0c\u5929\u53f6\u6563\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u9000\u4e0b\uff0c\u5f85\u6211\u64d2\u5979\uff01\u201d \u3000\u3000\u5929\u53f6\u6563\u4eba\u6b66\u529f\u867d\u6bd4\u51af\u745b\u9ad8\u660e\uff0c\u53ef\u662f\u638c\u529b\u4e0d\u6562\u5c3d\u53d1\uff0c\u88ab\u51af\u745b\u5251\u6cd5\u4e00\u903c\uff0c\u53cd\u800c\u9669\u8c61\u73af\u751f\uff0c\u54c8 \u5e03\u9640\u5f03\u4e86\u94dc\u9524\uff08\u6015\u6d41\u661f\u9524\u52bf\u52b2\uff0c\u4f1a\u6253\u4f24\u5979\u3002\uff09\u4e5f\u6765\u52a9\u6218\uff0c\u4e24\u540d\u5927\u5185\u9ad8\u624b\uff0c\u7528\u5c3d\u7cbe\u795e\uff0c\u5c0f\u5fc3\u7ffc \u7ffc\uff0c\u624d\u80fd\u5728\u65e2\u8981\u907f\u514d\u4f24\u5979\u53c8\u8981\u907f\u514d\u4e3a\u5979\u6240\u4f24\u7684\u60c5\u51b5\u4e0b\uff0c\u5c06\u5979\u56f0\u4f4f\uff0c\u53ef\u662f\u8981\u60f3\u62a2\u53bb\u5979\u624b\u4e2d\u7684\u5b9d \u5251\uff0c\u5374\u8fd8\u662f\u4e0d\u80fd\u3002 \u3000\u3000\u96cd\u6b63\u95fb\u58f0\u6ce8\u89c6\uff0c\u5728\u5047\u5c71\u65c1\u78b0\u5230\u4e86\u5e74\u7fb9\u5c27\uff0c\u5e74\u7fb9\u5c27\u5fc3\u5185\u867d\u614c\uff0c\u795e\u8272\u4e0d\u9732\uff0c\u594f\u9053\uff1a\u201c\u542c\u8bf4\u7433 \u8d35\u4eba\u9003\u8dd1\u4e86\uff0c\u7687\u4e0a\u653e\u5fc3\uff0c\u5929\u53f6\u6563\u4eba\u548c\u54c8\u5e03\u9640\u5df2\u8ffd\u4e0b\u53bb\u4e86\u3002\u201d\u96cd\u6b63\u9053\uff1a\u201c\u537f\u5bb6\u8f9b\u82e6\u4e86\uff01\u201d\u5e74\u7fb9 \u5c27\u5fc3\u4e2d\u4e00\u9707\uff0c\u770b\u96cd\u6b63\u65f6\uff0c\u96cd\u6b63\u82e5\u65e0\u5176\u4e8b\uff0c\u62c9\u8d77\u5e74\u7fb9\u5c27\u7684\u624b\u7b11\u9053\uff1a\u201c\u4f60\u662f\u5927\u5c06\u4e4b\u6750\uff0c\u4eca\u665a\u5374\u8981 \u59d4\u5c48\u4f60\u5728\u8fd9\u5c0f\u56ed\u5b50\u91cc\u505a\u6307\u6325\uff0c\u8ffd\u6355\u4e00\u4e2a\u4e2b\u5934\uff1b\u8bf4\u6765\u4e5f\u662f\u4f73\u8bdd\uff0c\u54c8\u54c8\uff01\u201d\u5e74\u7fb9\u5c27\u89c1\u7687\u4e0a\u5bf9\u4ed6\u5982 \u6b64\u5ba0\u4fe1\uff0c\u5fc3\u4e2d\u5927\u4e3a\u5bbd\u6170\uff0c\u96cd\u6b63\u7eed\u9053\uff1a\u201c\u5973\u5b50\u7389\u5e1b\uff0c\u4e0d\u8fc7\u662f\u9063\u5174\u5bc4\u60c5\u7684\u73a9\u7269\uff0c\u5931\u4e4b\u4e5f\u4e0d\u8db3 \u60dc\u3002\u201d\u5e74\u7fb9\u5c27\u529b\u6444\u5fc3\u795e\uff0c\u5bf9\u9053\uff1a\u201c\u7687\u4e0a\u80f8\u895f\u5bbd\u5e7f\uff0c\u65e0\u4eba\u53ef\u53ca\u3002\u7433\u8d35\u4eba\u5e74\u7eaa\u592a\u8f7b\uff0c\u4e5f\u8bb8\u662f\u4e00\u65f6 \u8d2a\u73a9\u8d70\u4e86\u51fa\u6765\uff0c\u7687\u4e0a\u628a\u5979\u62db\u56de\uff0c\u53ef\u4ee5\u4ea4\u5bb6\u6bcd\u770b\u7ba1\u3002\u201d\u96cd\u6b63\u7b11\u9053\uff1a\u201c\u82e5\u7136\u622a\u5f97\u5979\u56de\u6765\uff0c\u90a3\u65f6\u518d \u9ebb\u70e6\u5e74\u592a\u592b\u4eba\u4f34\u5979\u56de\u4eac\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u7687\u4e0a\u653e\u5fc3\uff0c\u5979\u51b3\u8d70\u4e0d\u51fa\u8fd9\u4e2a\u56ed\u5b50\u3002\u201d\u5176\u5b9e\u4ed6\u5fc3\u4e2d\u5374 \u5728\u8bf7\u7956\u5b97\u4fdd\u4f51\u51af\u7433\u80fd\u9003\u51fa\u8fd9\u4e2a\u56ed\u5b50\u3002 \u3000\u3000\u8d70\u4e86\u51e0\u6b65\uff0c\u8377\u5858\u90a3\u8fb9\u53ae\u6740\u58f0\u8d77\uff0c\u4f8d\u536b\u62a5\u9053\uff1a\u201c\u7433\u8d35\u4eba\u5df2\u627e\u5230\u4e86\uff0c\u54c8\u603b\u7ba1\u548c\u5929\u53f6\u6563\u4eba\u6b63\u5728 \u529d\u5979\u56de\u6765\u3002\u201d\u96cd\u6b63\u9053\uff1a\u201c\u5f88\u597d\uff0c\u6211\u4eb2\u81ea\u53bb\u53eb\u5979\u56de\u6765\u3002\u201d\u7b11\u7740\u5bf9\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u54b1\u4eec\u90fd\u53bb\u77a7 \u77bb\u3002\u201d\u5e74\u7fb9\u5c27\u5fc3\u795e\u624d\u5b9a\uff0c\u53c8\u8d77\u6050\u614c\uff0c\u9976\u4ed6\u662f\u4e00\u4ee3\u67ad\u96c4\uff0c\u4e5f\u5df2\u5fae\u5fae\u53d1\u6296\uff0c\u6781\u529b\u538b\u4f4f\uff0c\u8fd9\u5239\u90a3 \u95f4\uff0c\u96cd\u6b63\u5df2\u89c9\u4ed6\u624b\u6307\u5fae\u52a8\uff0c\u53ea\u5f53\u4e0d\u77e5\uff0c\u4ecd\u7136\u548c\u4ed6\u5e76\u80a9\u643a\u624b\uff0c\u540c\u4e0a\u83b2\u821f\uff0c\u76f4\u653e\u5bf9\u5cb8\u3002 \u3000\u3000\u51af\u745b\u529b\u654c\u54c8\u5e03\u9640\u4e0e\u5929\u53f6\u6563\u4eba\u4e8c\u4eba\uff0c\u624b\u4e2d\u5b9d\u5251\uff0c\u51e0\u6b21\u9669\u88ab\u593a\u53bb\u3002\u6b63\u5728\u7d27\u5f20\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb \u9053\uff1a\u201c\u4f60\u4eec\u90fd\u9000\u4e0b\u6765\uff01\u5f85\u6715\u95ee\u5979\u3002\u201d\u51af\u745b\u7edd\u672a\u6599\u5230\u6765\u4eba\u5c31\u662f\u7687\u5e1d\uff0c\u7d27\u5f20\u4e4b\u9645\uff0c\u66f4\u65e0\u95f2\u5fc3\u53bb\u601d \u7d22\u4ed6\u8bf4\u7684\u90a3\u4e2a\u201c\u6715\u201d\u5b57\u7684\u5b57\u4e49\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u559d\u9000\uff0c\u5468\u56f4\u987f\u65f6\u9e26\u96c0\u65e0\u58f0\uff0c\u4e0e\u81ea\u5df1\u4ea4\u624b\u7684\u4e24\u4e2a\u654c \u4eba\u4e5f\u500f\u7684\u8df3\u51fa\u56ed\u5b50\uff0c\u5fc3\u9053\uff1a\u4ec0\u4e48\u4eba\u6709\u8fd9\u6837\u5927\u7684\u5a01\u52bf\u3002\u628a\u773c\u770b\u65f6\uff0c\u4f46\u89c1\u4e00\u4e2a\u4e09\u5341\u591a\u5c81\u7684\u4e2d\u5e74 \u4eba\uff0c\u6c14\u5ea6\u9ad8\u534e\uff0c\u53cc\u76ee\u6709\u4e00\u80a1\u4ee4\u4eba\u4e0d\u6562\u903c\u89c6\u7684\u5a01\u4eea\uff0c\u5411\u81ea\u5df1\u5fae\u5fae\u53d1\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u963f\u7433\uff0c\u4f60\u4eca\u665a \u8fd9\u6837\u80e1\u95f9\uff0c\u4e0d\u6015\u7b11\u8bdd\u5417\uff1f\u5feb\u5feb\u968f\u6211\u56de\u53bb\uff01\u201d \u3000\u3000\u51af\u745b\u5fc3\u60f3\uff1a\u4e0e\u6211\u5bf9\u654c\u8fd9\u4e24\u4eba\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u4e16\u6240\u7f55\u89c1\uff0c\u5bf9\u4ed6\u5374\u5982\u6b64\u606d\u987a\uff0c\u4ed6\u82e5\u4e0d\u662f\u4e00\u6d3e\u5b97 \u4e3b\u5fc5\u662f\u671d\u5ef7\u8d35\u5b98\uff0c\u6211\u53cd\u6b63\u9003\u4e0d\u8131\u4e86\uff0c\u4e14\u548c\u4ed6\u8bb2\u53bb\uff0c\u770b\u4ed6\u8bb2\u4e0d\u8bb2\u7406\u3002\u4fbf\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u672c\u6765\u4e0d \u60f3\u548c\u4ed6\u4eec\u4e89\u6597\uff0c\u4f60\u6765\u5f97\u5f88\u597d\uff0c\u6211\u6b63\u8981\u95ee\u6e05\u695a\u4f60\u3002\u201d\u96cd\u6b63\u4e0d\u77e5\u5979\u8981\u95ee\u4f55\u4e8b\uff0c\u751f\u6015\u5979\u53e3\u6ca1\u906e\u62e6\uff0c \u80e1\u8bf4\u4e71\u9053\uff0c\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u56de\u53bb\u518d\u8bf4\uff01\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u5fc3\u4e2d\u6253\u9f13\uff0c\u9707\u6050\u96be\u8a00\uff0c\u51af\u745b\u548c\u4ed6\u4eec\u540c\u4e0b\u83b2\u8239\uff0c\u8377\u5858\u6c34\u6cdb\u51fa\u94f6\u5149\uff0c\u51af\u745b\u5ffd\u9053\uff1a \u201c\u54a6\uff0c\u4f60\u662f\u5e74\u7fb9\u5c27\uff0c\u6211\u8ba4\u5f97\u4f60\uff01\u201d\u5e74\u7fb9\u5c27\u4e00\u60ca\uff0c\u51b7\u6c57\u6e7f\u8863\uff0c\u51e0\u4e4e\u628a\u4e0d\u7a33\u8235\u3002 \u3000\u3000\u96cd\u6b63\u7b11\u65a5\u9053\uff1a\u201c\u50bb\u4e2b\u5934\uff0c\u4f60\u75af\u4e86\u5417\uff1f\u5e74\u5927\u5c06\u519b\u4f60\u600e\u4f1a\u4e0d\u8ba4\u5f97\uff1f\u4f60\u4eca\u65e5\u624d\u62dc\u4e86\u4e49\u7236\u4e49 \u6bcd\u3002\u201d\u51af\u745b\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u4e00\u5b9a\u53c8\u662f\u628a\u90a3\u4e2a\u4ec0\u4e48\u201c\u7433\u8d35\u4eba\u201d\u7684\u5e10\u7b97\u5728\u6211\u7684\u5934\u4e0a\u4e86\u3002 \u3000\u3000\u56de\u5230\u56ed\u5b50\u8fd9\u8fb9\uff0c\u96cd\u6b63\u7b11\u9053\uff1a\u201c\u5e74\u5c06\u519b\uff0c\u4f60\u4eca\u665a\u7d2f\u5566\uff0c\u65e9\u70b9\u7761\u5427\u3002\u201d\u5e74\u7fb9\u5c27\u5fc3\u4e2d\u60f4\u60f4\uff0c\u53ea \u597d\u544a\u9000\u3002 \u3000\u3000\u96cd\u6b63\u5c06\u5979\u5e26\u4e0a\u82b1\u5385\uff0c\u5de6\u53f3\u65e0\u4eba\uff0c\u51af\u745b\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u91cc\u662f\u4e0d\u662f\u6709\u4f4d\u2018\u7433\u8d35\u4eba\u2019\uff0c\u5979\u662f\u4f4d\u5c0f \u59d1\u5a18\u5417\uff1f\u5728\u4e0d\u5728\u8fd9\u91cc\uff1f\u201d\u96cd\u6b63\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u9053\uff1a\u201c\u4f60\u4e2d\u90aa\u5566\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u4f60\u4eec\u624d\u662f\u4e2d \u90aa\uff0c\u4e00\u5b9a\u662f\u4f60\u4eec\u628a\u5979\u5f53\u6210\u6211\u4e86\uff0c\u4f60\u53eb\u4eba\u627e\u627e\u770b\uff0c\u628a\u5979\u627e\u4e86\u51fa\u6765\uff0c\u4e8b\u60c5\u5c31\u660e\u767d\u5566\uff01\u201d\u96cd\u6b63\u54c8\u54c8 \u7b11\u9053\uff1a\u201c\u4f60\u771f\u6709\u8da3\uff0c\u628a\u7b11\u8bdd\u8bb2\u5f97\u8fd9\u6837\u8ba4\u771f\uff0c\u50cf\u4f60\u8fd9\u6837\u7edd\u4e16\u7f8e\u4eba\uff0c\u5960\u8bf4\u4eba\u95f4\uff0c\u5929\u4e0a\u4e5f\u96be\u518d\u627e\uff0c \u600e\u80fd\u8fd8\u6709\u4e00\u4e2a\u50cf\u4f60\u7684\u4eba\uff1f\u201d\u4efb\u51af\u745b\u5982\u4f55\u8bf4\u6cd5\uff0c\u96cd\u6b63\u53ea\u662f\u4e0d\u4fe1\uff0c\u51af\u745b\u82e6\u607c\u975e\u5e38\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u7136 \u4e0d\u4fe1\uff0c\u90a3\u6211\u53ea\u597d\u8d70\u4e86\uff01\u201d \u3000\u3000\u96cd\u6b63\u7b11\u9053\uff1a\u201c\u4f60\u5012\u8bf4\u5f97\u5bb9\u6613\uff0c\u4f60\u9003\u4e86\u4e00\u6b21\uff0c\u5df2\u7ecf\u662f\u5927\u5931\u8eab\u4efd\uff0c\u4f60\u8fd8\u60f3\u518d\u9003\uff0c\u628a\u6715\u7684\u9762\u5b50 \u653e\u5230\u54ea\u91cc\uff1f\u201d \u3000\u3000\u51af\u745b\u8fd9\u56de\u542c\u6e05\u695a\u4e86\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u4f60\u8bf4\u7684\u4ec0\u4e48\u8eab\u4efd\u3001\u4ec0\u4e48\u9762\u5b50\uff1f\u662f\u600e \u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u96cd\u6b63\u5927\u7b11\u9053\uff1a\u201c\u88c5\u75af\u4f5c\u50bb\u4e5f\u4e0d\u5e94\u5c3d\u88c5\u4e0b\u53bb\uff01\u4f60\u5df2\u7b54\u5e94\u4e86\u6715\u8981\u968f\u8054\u56de\u4eac\uff0c\u53c8\u60f3\u53cd\u6094\u5417\uff1f\u4f60 \u60f3\u60f3\u8d35\u5983\u7684\u8eab\u4efd\u4f55\u7b49\u5c0a\u8363\uff0c\u600e\u53ef\u968f\u4fbf\u629b\u5934\u9732\u9762\uff0c\u8352\u5510\u80e1\u95f9\uff1f\u201d \u3000\u3000\u51af\u745b\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u662f\u7687\u5e1d\uff1f\u201d\u96cd\u6b63\u7b11\u5bb9\u4e00\u6536\uff0c\u62c9\u957f\u4e86\u8138\u5b54\u9053\uff1a\u201c\u6211\u662f\u7687\u5e1d\uff0c\u4f60\u662f\u8d35\u5983\uff0c \u4f60\u4eca\u5e74\u5341\u516d\u5c81\u4e5f\u4e0d\u7b97\u5c0f\u4e86\uff0c\u5e94\u8be5\u5b66\u70b9\u7687\u5bb6\u7684\u793c\u6cd5\u5566\u3002\u201d \u3000\u3000\u51af\u745b\u9000\u4e86\u4e00\u6b65\uff0c\u5706\u6e9c\u6e9c\u7684\u773c\u775b\u671d\u5730\u9762\u4e0a\u4e00\u626b\uff0c\u8fd9\u65f6\u5979\u518d\u4e0d\u6015\u96cd\u6b63\u90a3\u6151\u4eba\u7684\u76ee\u5149\u4e86\uff0c \u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u4f60\u662f\u4e2a\u8352\u6deb\u65e0\u9053\u7684\u66b4\u541b\u5440\uff01\u201d \u3000\u3000\u96cd\u6b63\u6012\u6781\u5f3a\u7b11\uff0c\u9053\uff1a\u201c\u6211\u600e\u4e48\u8352\u6deb\u65e0\u9053\u5462\uff1f\u201d\u51af\u745b\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u542c\u4f60\u8bf4\u6765\uff0c\u90a3\u4e2a\u4ec0\u4e48 \u2018\u7433\u8d35\u4eba\u2019\u4e00\u5b9a\u662f\u6015\u4e86\u4f60\u7684\u6deb\u5a01\uff0c\u7ed9\u4f60\u903c\u8d70\u4e86\u3002\u4f60\u65e2\u662f\u5f53\u4eca\u5929\u5b50\uff0c\u4e0d\u60f3\u6cd5\u81f4\u5929\u4e0b\u4e8e\u592a\u5e73\uff0c\u5374 \u8981\u903c\u8feb\u5973\u5b50\uff0c\u4e0d\u662f\u8352\u6deb\u65e0\u9053\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u96cd\u6b63\u6012\u6781\u6c14\u6781\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u6211\u8352\u6deb\u65e0\u9053\uff1f\u4eca\u665a\u6211\u4fbf\u8981\u53ec\u5e78\u4f60\uff01\u201d\u4f38\u624b\u6765\u62c9\uff0c\u51af \u745b\u52c3\u7136\u5927\u6012\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u5411\u96cd\u6b63\u9762\u95e8\u75db\u63b4\uff01 \u3000\u3000\u96cd\u6b63\u7684\u6b66\u529f\uff0c\u5f97\u81ea\u5c11\u6797\u5bfa\u524d\u4efb\u4e3b\u6301\u672c\u7a7a\u5927\u5e08\u7684\u82e6\u5fc3\u4f20\u6388\uff0c\u51af\u745b\u51fa\u624b\u867d\u5feb\uff0c\u5374\u88ab\u4ed6\u4e00\u95ea\u95ea \u5f00\uff0c\u4f46\u638c\u98ce\u89e6\u8138\uff0c\u4e5f\u81ea\u8fa3\u8fa3\u4f5c\u75db\u3002 \u3000\u3000\u96cd\u6b63\u6012\u53eb\u9053\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff01\u597d\uff0c\u6211\u5148\u628a\u4f60\u7684\u674e\u51b6\u54e5\u54e5\u6740\u4e86\uff01\u201d\u51af\u745b\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60 \u8bf4\u4ec0\u4e48\uff1f\u8c01\u662f\u674e\u6cbb\u54e5\u54e5\uff1f\u201d\u96cd\u6b63\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u6015\u4e86\u5417\uff1f\u54fc\uff0c\u4f60\u7231\u4e0a\u4e86\u90a3\u81ed\u5c0f\u5b50\u662f\u4e0d\u662f\uff1f\u6211\u504f \u8981\u53eb\u4f60\u65ad\u5ff5\u3002\u6211\u6570\u4e09\u58f0\uff0c\u4f60\u82e5\u4e0d\u4ece\uff0c\u6211\u9a6c\u4e0a\u4f20\u4ee4\u628a\u4ed6\u6740\u6389\u3002\u201d \u3000\u3000\u51af\u745b\u548c\u674e\u6cbb\u4e43\u662f\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\u3002\u95fb\u8a00\u5fc3\u9053\uff1a\u83ab\u975e\u662f\u674e\u6cbb\u54e5\u54e5\u5df2\u843d\u5230\u4e86\u4ed6\u4eec\u7684\u638c\u63e1\u4e4b\u4e2d\uff1f \u96cd\u6b63\u5927\u58f0\u6570\u201c\u4e00\u201d\uff0c\u89c1\u51af\u745b\u51dd\u76ee\u601d\u91cf\uff0c\u5fc3\u4e2d\u53c8\u9178\u53c8\u559c\uff0c\u4ee5\u4e3a\u5a01\u80c1\u751f\u6548\uff0c\u53c8\u5927\u58f0\u6570\u201c\u4e8c\u201d\uff0c \u201c\u4e8c\u201d\u5b57\u672a\u66fe\u51fa\u53e3\uff0c\u51af\u745b\u62d4\u51fa\u77ed\u5251\uff0c\u4e00\u5251\u5411\u4ed6\u523a\u6765\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4e0d\u628a\u6211\u7684\u674e\u6cbb\u54e5\u54e5\u653e\u4e86\uff0c\u6211 \u53eb\u4f60\u4eca\u65e5\u8840\u6e85\u5ead\u9636\uff01\u201d \u3000\u3000\u96cd\u6b63\u5927\u5403\u4e00\u60ca\uff0c\u51af\u745b\u95ea\u7535\u822c\u8fde\u523a\u4e24\u5251\uff0c\u96cd\u6b63\u9669\u9669\u88ab\u5979\u523a\u4e2d\uff0c\u51af\u745b\u7b2c\u4e09\u5251\u8ddf\u7740\u6025\u523a\uff0c\u96cd\u6b63 \u65cb\u98ce\u4e00\u95ea\uff0c\u968f\u624b\u62ff\u8d77\u4e00\u5f20\u6905\u5b50\uff0c\u7528\u529b\u4e00\u8361\uff0c\u90a3\u6905\u5b50\u662f\u6a80\u6728\u6240\u9020\uff0c\u751a\u4e3a\u575a\u5b9e\uff0c\u51af\u745b\u4e00\u5251\u5288\u5b83\u4e0d \u65ad\uff0c\u53ea\u89c9\u864e\u53e3\u751f\u75db\u3002\u96cd\u6b63\u9053\uff1a\u201c\u4f60\u8fd9\u70b9\u6b66\u529f\u5c31\u60f3\u9020\u53cd\u4e86\u5417\uff1f\u201d \u3000\u3000\u51af\u745b\u5927\u6012\uff0c\u5c55\u5f00\u8ffd\u98ce\u5251\u6cd5\uff0c\u5411\u4ed6\u731b\u88ad\uff0c\u96cd\u6b63\u521d\u4ee5\u4e3a\u51af\u7433\u6b66\u529f\uff0c\u7edd\u4e0d\u662f\u4ed6\u5bf9\u624b\uff0c\u90a3\u77e5\u6321\u4e86 \u51e0\u62db\uff0c\u53ea\u89c9\u5979\u7684\u5251\u62db\u53c8\u72e0\u53c8\u5feb\uff0c\u548c\u5510\u6653\u6f9c\u7684\u5982\u51fa\u4e00\u5bb6\uff0c\u5251\u52bf\u5982\u8679\uff0c\u5947\u5e7b\u65e0\u6bd4\uff0c\u62db\u62db\u523a\u5411\u81ea\u5df1 \u8981\u5bb3\u3002\u96cd\u6b63\u628a\u6905\u5b50\u821e\u5f97\u864e\u864e\u751f\u98ce\uff0c\u7adf\u81ea\u78d5\u4e0d\u98de\u5979\u7684\u5b9d\u5251\uff01 \u3000\u3000\u51af\u745b\u4e5f\u662f\u5403\u60ca\u4e0d\u5c0f\uff0c\u5979\u672c\u4ee5\u7687\u5e1d\u4e4b\u5c0a\uff0c\u957f\u5728\u6df1\u5bab\uff0c\u5a07\u751f\u60ef\u517b\u5b9a\u7136\u4e0d\u61c2\u6b66\u529f\u3002\u6599\u60f3\u80fd\u5728\u4e09 \u62db\u4e4b\u5185\u628a\u4ed6\u523a\u4f24\uff0c\u5c06\u4ed6\u64d2\u83b7\uff0c\u4f5c\u4e3a\u4eba\u8d28\uff0c\u90a3\u77e5\u96cd\u6b63\u7528\u6905\u5b50\u4f5c\u4e3a\u6b66\u5668\uff0c\u5c55\u5f00\u7684\u7adf\u662f\u5c11\u6797\u6d3e\u7684 \u201c\u51f3\u62d0\u201d\u62db\u6570\uff0c\u6a2a\u626b\u76f4\u5288\uff0c\u52b2\u529b\u5947\u5927\uff0c\u7adf\u7136\u662f\u4e00\u7b49\u4e00\u7684\u529f\u592b\u3002 \u3000\u3000\u8f6c\u77ac\u4e4b\u95f4\uff0c\u51af\u745b\u5df2\u548c\u4ed6\u6597\u4e86\u4e8c\u4e09\u5341\u62db\u3002\u5e74\u7fb9\u5c27\u65e0\u5fc3\u7761\u89c9\uff0c\u8fd9\u65f6\u6b63\u5728\u5916\u9762\u548c\u54c8\u5e03\u9640\u95f2\u8bdd\uff0c \u54c8\u5e03\u9640\u8c28\u4f9d\u96cd\u6b63\u6240\u5631\uff0c\u5bf9\u4ed6\u606d\u656c\u5982\u5e38\u3002\u6b63\u95f2\u8bdd\u95f4\u5ffd\u95fb\u5f97\u91cc\u9762\u50fb\u50fb\u58f0\u54cd\uff0c\u4e24\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u672a\u95fb \u7687\u4e0a\u53ec\u5524\uff0c\u53c8\u4e0d\u6562\u8fdb\u53bb\u6551\u9a7e\u3002\u54c8\u5e03\u9640\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u597d\u5927\u80c6\u5b50\uff0c\u542c\u58f0\u97f3\uff0c\u5c45\u7136\u662f\u548c\u7687\u4e0a\u52a8\u8d77\u624b \u5566\u3002\u201d\u5e74\u7fb9\u5c27\u5fc3\u4e2d\u6697\u559c\uff0c\u60f3\u9053\uff1a\u5979\u548c\u7687\u4e0a\u95f9\u5230\u5982\u6b64\u5730\u6b65\uff0c\u60f3\u6765\u4e0d\u4f1a\u8bb2\u6211\u7684\u574f\u8bdd\u4e86\uff0c\u8bb2\u4e86\u7687\u4e0a \u4e5f\u672a\u5fc5\u76f8\u4fe1\u3002 \u3000\u3000\u54c8\u5e03\u9640\u5ffd\u53eb\uff1a\u201c\u4e0d\u597d\uff01\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u2026\u600e\u4e48\uff1f\u201d\u54c8\u5e03\u9640\u9053\uff1a\u201c\u90a3\u91ce\u4e2b\u5934\u5251\u6cd5\u7cbe\u5999\uff0c\u53ea \u6050\u7687\u4e0a\u4e0d\u662f\u5979\u7684\u5bf9\u719f\u9170\u7687\u4e0a\u53c8\u4e0d\u53ec\u5524\uff0c\u54b1\u4eec\u8fdb\u53bb\u53c8\u6015\u60f9\u8d77\u5c34\u5c2c\uff0c\u5982\u4f55\u662f\u597d\uff01\u201d\u5e74\u7fb9\u5c27\u6b64\u65f6\u7684 \u5fc3\u53c8\u6068\u4e0d\u5f97\u201c\u51af\u7433\u201d\u4e00\u5251\u628a\u7687\u5e1d\u6740\u6389\uff0c\u987a\u7740\u54c8\u5e03\u9640\u7684\u53e3\u6c14\u9053\uff1a\u201c\u662f\u5440\uff0c\u8fd9\u4e0d\u662f\u666e\u901a\u7684\u523a\u5ba2\uff0c \u5979\u662f\u672a\u6765\u8d35\u5983\uff0c\u7687\u4e0a\u548c\u8d35\u5983\u52a8\u624b\uff0c\u54b1\u4eec\u53ef\u4e0d\u597d\u610f\u601d\u8fdb\u53bb\u3002\u201d \u3000\u3000\u91cc\u9762\u6597\u5f97\u4f3c\u4e4e\u8d8a\u53d1\u6fc0\u70c8\uff0c\u684c\u6905\u5012\u5730\u4e4b\u58f0\uff0c\u54cd\u6210\u4e00\u7247\uff0c\u54c8\u5e03\u9640\u5ffd\u9053\uff1a\u201c\u597d\uff0c\u6211\u5b81\u613f\u53d7\u7687\u4e0a \u602a\u8d23\uff0c\u4e5f\u8981\u8fdb\u53bb\u6551\u9a7e\uff01\u201d\u54c8\u5e03\u9640\u662f\u536b\u58eb\u603b\u7ba1\uff0c\u7687\u5e1d\u6700\u4eb2\u4fe1\u4e4b\u4eba\uff0c\u5230\u4e86\u6b64\u9645\uff0c\u975e\u53bb\u4e0d\u53ef\u3002\u54c8\u5e03 \u9640\u51b2\u5165\u623f\u5185\uff0c\u5e74\u7fb9\u5c27\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4e5f\u62a2\u7740\u8fdb\u53bb\u6551\u9a7e\uff01 \u3000\u3000\u96cd\u6b63\u521d\u4ee5\u4e3a\u53ef\u4ee5\u628a\u51af\u745b\u964d\u4f0f\uff0c\u53c8\u89c9\u82e5\u7136\u53ec\u4e86\u536b\u58eb\u8fdb\u6765\uff0c\u4e8b\u60c5\u66f4\u4e0d\u53ef\u6536\u62fe\u3002\u56e0\u6b64\u4e00\u5473\u548c\u51af \u745b\u6076\u6597\u3002\u90a3\u77e5\u6597\u4e86\u4e94\u516d\u5341\u62db\uff0c\u6905\u5b50\u5230\u5e95\u4e0d\u5982\u5b9d\u5251\u7075\u4fbf\uff0c\u88ab\u51af\u745b\u7d27\u7d27\u8ffd\u903c\uff0c\u51e0\u4e4e\u53d7\u4f24\uff0c\u5fc3\u4e2d\u5927 \u6025\u3002\u6b63\u60f3\u53ec\u5524\u536b\u58eb\uff0c\u54c8\u5e03\u9640\u548c\u5e74\u7fb9\u5c27\u5df2\u7ecf\u51b2\u8fdb\u3002\u96cd\u6b63\u9053\uff1a\u201c\u537f\u5bb6\u6765\u5f97\u6b63\u597d\uff0c\u66ff\u6715\u628a\u8fd9\u8d31\u5a62\u64d2 \u4e86\u3002\u751f\u64d2\u56fa\u4f73\uff0c\u6740\u4f24\u6715\u4ea6\u4e0d\u52a0\u7f6a\u8d23\uff01\u201d \u3000\u3000\u54c8\u5e03\u9640\u5f97\u4ee4\uff0c\u731b\u52c7\u76f4\u640f\uff0c\u5e74\u7fb9\u5c27\u4e0d\u7518\u843d\u540e\uff0c\u4e5f\u53bb\u52a9\u6218\u3002\u51af\u745b\u5251\u6cd5\u867d\u5999\uff0c\u548c\u96cd\u6b63\u6076\u6597\u65f6\u6c14 \u529b\u5df2\u8017\u4e86\u4e00\u534a\uff0c\u90a3\u7981\u5f97\u4f4f\u4e24\u540d\u9ad8\u624b\u7684\u56f4\u653b\uff0c\u8d70\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u88ab\u54c8\u5e03\u9640\u4e00\u638c\u626b\u4e2d\u624b\u8155\uff0c\u5b9d\u5251\u8131 \u624b\uff0c\u5e74\u7fb9\u5c27\u6b3a\u8eab\u76f4\u8fdb\uff0c\u9a88\u6307\u4e00\u6233\uff0c\u70b9\u4e86\u5979\u7684\u5de6\u8170\u201c\u7cbe\u4fc3\u7a74\u201d\uff0c\u7cbe\u4fc3\u7a74\u5728\u7531\u4e0b\u6570\u4e0a\u7684\u80cc\u540e\u808b\u9aa8 \u7b2c\u4e8c\u7b2c\u4e09\u6761\u9aa8\u7f1d\u4e2d\uff0c\u5de6\u7a74\u9002\u5f53\u813e\u4f4d\uff0c\u53f3\u7a74\u9002\u5f53\u809d\u4f4d\uff0c\u4e00\u88ab\u70b9\u4e2d\uff0c\u6d51\u8eab\u762b\u75ea\u3002 \u3000\u3000\u96cd\u6b63\u89c1\u51af\u745b\u5012\u5730\uff0c\u751a\u89c9\u5c34\u5c2c\u3002\u5e74\u7fb9\u5c27\u8bf7\u4e86\u201c\u5723\u5b89\u201d\uff0c\u4f4e\u58f0\u7980\u9000\uff1a\u201c\u8d35\u5983\u5fc3\u6000\u53db\u9006\uff0c\u7559\u5728 \u8eab\u8fb9\uff0c\u6050\u975e\u7687\u4e0a\u4e4b\u798f\u3002\u201d\u96cd\u6b63\u9053\uff1a\u201c\u537f\u5bb6\u62a4\u9a7e\u6709\u529f\uff0c\u76f4\u8a00\u65e0\u5fcc\uff0c\u771f\u662f\u6715\u7684\u5fe0\u81e3\uff0c\u537f\u5bb6\u4e14\u9000\uff0c \u6715\u81ea\u6709\u5904\u7f6e\u3002\u201d\u5e74\u7fb9\u5c27\u5fe7\u559c\u4ea4\u534a\uff0c\u53e9\u5934\u9000\u51fa\u3002 \u3000\u3000\u96cd\u6b63\u62db\u624b\u53eb\u54c8\u5e03\u9640\u9053\uff1a\u201c\u4f60\u628a\u5979\u9ebb\u7a74\u89e3\u4e86\uff0c\u53e6\u6362\u4e00\u95f4\u76d1\u7981\uff0c\u4f60\u4eb2\u81ea\u770b\u5b88\u5979\u3002\u201d\u54c8\u5e03\u9640\u5782 \u624b\u201c\u55b3\u201d\u4e86\u4e00\u58f0\uff0c\u96cd\u6b63\u5524\u51fa\u4e00\u540d\u968f\u884c\u592a\u76d1\uff0c\u53eb\u4ed6\u80cc\u8d77\u51af\u745b\u8ddf\u7740\u54c8\u5e03\u9640\u8d70\u3002 \u3000\u3000\u54c8\u5e03\u9640\u672a\u8d70\u51fa\u623f\u95e8\uff0c\u96cd\u6b63\u60f3\u8d77\u4e00\u4e8b\uff0c\u5ffd\u53c8\u628a\u4ed6\u53eb\u56de\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4e2a\u674e\u6cbb\u5462\uff1f\u6709\u6ca1\u6709\u51fa \u4e8b\uff1f\u201d\u674e\u6cbb\u9003\u8131\uff0c\u54c8\u5e03\u9640\u4e5f\u662f\u9002\u624d\u77e5\u9053\u3002\u53e9\u5934\u7980\u9053\uff1a\u201c\u5974\u624d\u6b7b\u7f6a\uff0c\u7763\u8d23\u4e0d\u4e25\uff0c\u8f66\u8f9f\u90aa\u64c5\u79bb\u804c \u4f4d\uff0c\u7ed9\u4ed6\u9003\u4e86\u3002\u5974\u624d\u4e5f\u662f\u521a\u521a\u77e5\u9053\uff0c\u6015\u7687\u4e0a\u6709\u4e8b\u5fc3\u70e6\uff0c\u4e0d\u6562\u8fdb\u7980\u3002\u201d\u96cd\u6b63\u6325\u624b\u9053\uff1a\u201c\u597d\uff0c\u4f60 \u9000\u4e0b\u53bb\u5427\u3002\u201d \u3000\u3000\u54c8\u5e03\u9640\u53bb\u540e\uff0c\u96cd\u6b63\u72ec\u5750\u623f\u4e2d\uff0c\u53cd\u8986\u601d\u60f3\uff0c\u5fc5\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u8fd9\u8d31\u5a62\u89c1\u5979\u5fc3\u4e0a\u4e4b\u4eba\u5df2\u7ecf\u9003 \u8131\uff0c\u6240\u4ee5\u6562\u653e\u80c6\u548c\u6715\u76f8\u6597\uff0c\u54fc\uff0c\u60f3\u4e0d\u5230\u5979\u548c\u90a3\u5c0f\u5b50\u76f8\u7231\u5982\u6b64\u4e4b\u6df1\u3002\u201d\u5fc3\u4e2d\u9178\u6e9c\u6e9c\u7684\u5341\u5206\u96be \u53d7\u3002\u4e00\u5ffd\u513f\u60f3\uff1a\u201c\u5979\u521a\u624d\u542c\u6715\u8981\u6740\u90a3\u5c0f\u5b50\uff0c\u795e\u8272\u7d27\u5f20\u4e4b\u6781\uff0c\u7edd\u975e\u77eb\u60c5\u53ef\u6bd4\uff0c\u5979\u82e5\u77e5\u9053\u4ed6\u5df2\u9003 \u8131\uff0c\u4e0d\u5e94\u8fd9\u6837\u3002\u201d\u518d\u60f3\u9053\uff1a\u201c\u5979\u65e2\u8fd9\u6837\u5014\u5f3a\uff0c\u4e0d\u80af\u4f9d\u4ece\uff0c\u90a3\u4e48\u5e74\u7fb9\u5c27\u5c31\u8bf4\u5f97\u4e0d\u9519\uff1a\u2018\u7559\u5728\u8eab \u8fb9\uff0c\u7ec8\u987b\u975e\u798f\u2019\u3002\u201d\u6740\u673a\u65b9\u8d77\uff0c\u5ffd\u53c8\u60f3\u9053\uff1a\u201c\u5982\u6b64\u7edd\u4ee3\u7f8e\u4eba\uff0c\u6740\u4e86\u5c82\u4e0d\u53ef\u60dc\uff1f\u201d\u601d\u6765\u60f3\u53bb\uff0c \u5728\u6740\u4e0e\u4e0d\u6740\u4e4b\u95f4\uff0c\u5140\u662f\u51b3\u65ad\u4e0d\u4e0b\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u4e3d\u8d28\u5929\u751f\u96be\u820d\u5f03\uff0c\u541b\u4e3b\u4e5f\u81ea\u76b1\u53cc\u7709\u3002 \u3000\u3000\u6b32\u77e5\u7687\u5e1d\u6740\u4e0d\u6740\u51af\u745b\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e00\u56de\u3000\u5de7\u8ba1\u6551\u4f73\u4eba\u3000\u6df1\u6069\u56fe\u62a5\u3000\u7591\u5fc3\u751f\u6697\u9b3c\u3000\u918b\u6c14\u96be\u6d88 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u4e00\u56de \u5de7\u8ba1\u6551\u4f73\u4eba\u3000\u6df1\u6069\u56fe\u62a5 \u7591\u5fc3\u751f\u6697\u9b3c\u3000\u918b\u6c14\u96be\u6d88 \u3000\u3000\u5510\u6653\u6f9c\u90a3\u65e5\u5728\u96ea\u9b42\u8c37\u4e2d\u548c\u8f66\u8f9f\u90aa\u640f\u6597\uff0c\u4e3a\u4e86\u62a2\u56de\u6e38\u9f99\u5251\uff0c\u626d\u4f24\u80da\u9aa8\uff0c\u56e0\u6b64\u4e0d\u80fd\u548c\u7fa4\u96c4\u540c \u4e0a\u9099\u5c71\u3002\u5341\u4e8c\u6307\u795e\u5077\u9648\u5fb7\u6cf0\u4e5f\u56e0\u53d7\u4e86\u8463\u5de8\u5ddd\u638c\u529b\u6240\u9707\uff0c\u4e0d\u80fd\u8d70\u52a8\u3002\u4e24\u4eba\u540c\u5728\u8c37\u4e2d\u517b\u4f24\uff0c\u517b\u4e86 \u4e00\u4e2a\u6708\uff0c\u65b9\u59cb\u6062\u590d\u3002\u6768\u4ef2\u82f1\u6258\u4eba\u634e\u4e86\u53e3\u4fe1\u6765\uff0c\u53eb\u5510\u6653\u6f9c\u901f\u56de\u5c71\u4e1c\u4e1c\u5e73\u5bb6\u4e2d\u3002\u5510\u6653\u6f9c\u95f7\u95f7\u4e0d \u4e50\uff0c\u5fc3\u60f3\uff1a\u5e08\u5085\u53eb\u6211\u56de\u53bb\uff0c\u4e00\u5b9a\u53c8\u662f\u50ac\u6211\u7ed3\u5a5a\u7684\u4e86\u3002\u53ef\u662f\u6069\u5e08\u6709\u4fde\u4e0d\u80fd\u8fdd\u80cc\uff0c\u53ea\u597d\u548c\u9648\u5fb7\u6cf0 \u4e00\u9053\uff0c\u79bb\u5f00\u96ea\u9b42\u8c37\uff0c\u540c\u5f80\u5c71\u4e1c\u3002\u4e0d\u6599\u8d70\u4e86\u4e24\u5929\uff0c\u4fbf\u5728\u5c0f\u9547\u7684\u5ba2\u5e97\u78b0\u5230\u4e86\u5c11\u6797\u76d1\u5bfa\u5f18\u6cd5\u5927\u5e08\uff0c \u5f18\u6cd5\u8bf4\u8d77\u6709\u90a3\u4e48\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u4e2d\u4e86\u4e03\u715e\u767d\u7709\u9488\uff0c\u521a\u521a\u79bb\u53bb\u3002\u5f18\u6cd5\u867d\u7136\u4e0d\u77e5\u9053\u51af\u7433\u7684\u540d\u5b57\uff0c\u4f46 \u5510\u6653\u6f9c\u542c\u4ed6\u6240\u8ff0\uff0c\u5df2\u77e5\u5fc5\u662f\u4e24\u59d0\u59b9\u4e2d\u4e4b\u4e00\u65e0\u7591\u3002 \u3000\u3000\u5510\u6653\u6f9c\u66fe\u7acb\u8a93\u8981\u5c06\u4e24\u59d0\u59b9\u627e\u56de\uff0c\u542c\u4e86\u6d88\u606f\uff0c\u7acb\u523b\u548c\u9648\u5fb7\u6cf0\u5bfb\u627e\uff0c\u4e00\u8def\u6253\u542c\uff0c\u76f4\u5bfb\u5230\u9648\u7559 \u5e74\u5bb6\u9644\u8fd1\u3002\u5510\u6653\u6f9c\u60f3\u8d77\u51af\u7433\u66fe\u5728\u5e74\u5bb6\u957f\u5927\uff0c\u4fbf\u60f3\u5230\u5e74\u5bb6\u63a2\u8bbf\uff0c\u53ef\u662f\u9648\u5fb7\u6cf0\u6253\u542c\u5f97\u5e74\u7fb9\u5c27\u6b63\u5728 \u5bb6\u4e2d\uff0c\u9ad8\u624b\u751a\u591a\uff0c\u6050\u9632\u6253\u8349\u60ca\u86c7\uff0c\u53cd\u4e3a\u4e0d\u5999\uff0c\u56e0\u6b64\u5c06\u5510\u6653\u6f9c\u529d\u6b62\u3002\u800c\u4eba\u5c31\u5728\u5e74\u5bb6\u9644\u8fd1\u7684\u5c71\u5934 \u57cb\u4f0f\uff0c\u610f\u6b32\u7b49\u5f85\u5e74\u7fb9\u5c27\u79bb\u5bb6\u4e4b\u540e\uff0c\u518d\u884c\u63a2\u8bbf\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u5510\u9648\u4e8c\u4eba\u4f0f\u5728\u9644\u8fd1\u5c71\u5934\uff0c\u89c1\u5e74\u5bb6\u82b1\u56ed\u706b\u5149\u70b9\u70b9\uff0c\u53ae\u6740\u58f0\u58f0\uff0c\u9648\u5fb7\u6cf0\u5077\u5077\u51fa\u53bb\uff0c\u5c06 \u56ed\u5916\u4e00\u4e2a\u770b\u5b88\u70b9\u4e86\u9ebb\u7a74\uff0c\u64d2\u56de\u5c71\u5934\uff0c\u8baf\u95ee\u4e4b\u4e0b\uff0c\u59cb\u77e5\u7687\u5e1d\u4e5f\u5728\u5e74\u5bb6\uff0c\u800c\u4e14\u4eca\u665a\u6349\u62ff\u7684\u6b63\u662f\u51af \u7433\u3002 \u3000\u3000\u5510\u6653\u6f9c\u542c\u4e86\u5927\u5403\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u82e5\u7136\u51af\u7433\u7ed9\u4ed6\u4eec\u6349\u56de\u6df1\u5bab\uff0c\u518d\u60f3\u6551\u5979\u51fa\u6765\uff0c\u53ef\u662f\u5343\u96be\u4e07 \u96be\uff01\u201d\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7a81\u7136\u6709\u4e86\u4e00\u4e2a\u4e3b\u610f\uff0c\u5bf9\u9648\u5fb7\u6cf0\u9053\uff1a\u201c\u9648\u5927\u54e5\uff0c\u4eca\u665a\u6211\u53ef\u8981\u5230\u5e74\u5bb6\u8d70\u8d70 \u4e86\u3002\u201d\u9648\u5fb7\u6cf0\u5403\u4e00\u60ca\u9053\uff1a\u201c\u4f60\u60f3\u53bb\u9001\u6b7b\u4e48\uff0c\u53ea\u5e74\u7fb9\u5c27\u7684\u624b\u4e0b\uff0c\u4fbf\u975e\u6211\u4eec\u4e8c\u4eba\u53ef\u654c\uff0c\u4f55\u51b5\u7687\u5e1d \u4e5f\u5728\u5e74\u5bb6\uff0c\u9ad8\u624b\u5982\u4e91\uff0c\u600e\u5bb9\u4f60\u95ef\u8fdb\u95ef\u51fa\uff1f\u201d\u5510\u6653\u6f9c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6b63\u56e0\u4e3a\u6709\u7687\u5e1d\u5728\u90a3 \u91cc\uff0c\u624d\u662f\u6700\u597d\u7684\u65f6\u673a\u3002\u201d\u4f4e\u58f0\u548c\u9648\u5fb7\u6cf0\u8bf4\u4e86\u51e0\u53e5\uff0c\u9648\u5fb7\u6cf0\u8fde\u8fde\u70b9\u5934\u79f0\u5999\uff0c\u4e8e\u662f\u4e24\u4eba\u4f9d\u8ba1\u884c\u4e8b\u3002 \u3000\u3000\u5510\u6653\u6f9c\u72ec\u81ea\u53bb\u53e9\u95e8\u6c42\u89c1\uff0c\u5176\u65f6\u5df2\u662f\u51af\u745b\u88ab\u64d2\u4e4b\u540e\uff0c\u968f\u4ece\u536b\u58eb\uff0c\u95f9\u4e86\u534a\u591c\uff0c\u5404\u81ea\u6563\u53bb\u6b47 \u606f\u3002\u4e0b\u534a\u591c\u5728\u56ed\u4e2d\u8d1f\u8d23\u8f6e\u503c\u7684\u662f\u5929\u53f6\u6563\u4eba\uff0c\u5929\u53f6\u6563\u4eba\u89c1\u6df1\u591c\u6709\u4eba\u6c42\u89c1\u3002\u5df2\u611f\u5947\u5f02\uff0c\u5f00\u95e8\u89c1\u662f \u5510\u6653\u6f9c\uff0c\u66f4\u662f\u60ca\u8bb6\u3002\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u6709\u7d27\u8981\u4e8b\u60c5\uff0c\u975e\u89c1\u7687\u4e0a\u4e0d\u53ef\uff0c\u70e6\u4f60\u901a\u62a5\u3002\u201d\u5929\u53f6\u6563\u4eba\u60f3 \u8d77\u6b64\u4eba\u4e43\u662f\u5eb7\u7199\u8001\u7687\u5e1d\u7684\u536b\u58eb\uff0c\u4ee5\u524d\u66fe\u5949\u8001\u7687\u5e1d\u4e4b\u547d\u5230\u8fc7\u56db\u7687\u5e9c\uff0c\u548c\u5f53\u4eca\u7687\u4e0a\u4e5f\u662f\u65e7\u4ea4\uff0c\u83ab \u975e\u771f\u6709\u4ec0\u4e48\u673a\u5bc6\u7684\u4e8b\u60c5\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u8d76\u5fd9\u7ed9\u4ed6\u901a\u62a5\u3002 \u3000\u3000\u96cd\u6b63\u5728\u623f\u4e2d\u601d\u6765\u60f3\u53bb\uff0c\u60f3\u6740\u51af\u745b\uff0c\u53c8\u820d\u4e0d\u5f97\uff0c\u5728\u6740\u4e0e\u4e0d\u6740\u4e4b\u95f4\uff0c\u5140\u662f\u51b3\u65ad\u4e0d\u4e0b\u3002\u5ffd\u62a5\u5510 \u6653\u6f9c\u6c42\u89c1\uff0c\u4e0d\u8010\u70e6\u7684\u9053\uff1a\u201c\u53c8\u662f\u8fd9\u4e2a\u5bb6\u4f19\uff0c\u4f60\u628a\u4ed6\u62d6\u53bb\u6253\u4e94\u5341\u5927\u677f\uff0c\u660e\u65e5\u6715\u518d\u95ee\u4ed6\u3002\u201d\u5929\u53f6 \u6563\u4eba\u6b63\u60f3\u9000\u4e0b\uff0c\u54c8\u5e03\u9640\u9053\uff1a\u201c\u6b64\u4eba\u66fe\u53d7\u5148\u7687\u8bcf\u4e66\uff0c\u53c8\u66fe\u968f\u5341\u56db\u8d1d\u52d2\u5230\u8fc7\u7545\u6625\u56ed\u63a2\u5148\u5e1d\u4e4b\u75c5\u3002 \u53ea\u6050\u771f\u6709\u4ec0\u4e48\u673a\u5bc6\u4e8b\u60c5\uff1f\u201d\u96cd\u6b63\u5fc3\u4e2d\u4e00\u51db\uff0c\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u8fd9\u4e94\u5341\u677f\u6743\u4e14\u8bb0\u4e0b\uff0c\u4f60\u5524\u4ed6\u8fdb \u6765\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u89c1\u4e86\u96cd\u6b63\uff0c\u957f\u63d6\u4e0d\u8dea\u3002\u96cd\u6b63\u6012\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u597d\u5927\u80c6\uff0c\u5c45\u7136\u8fd8\u6562\u524d\u6765\u89c1\u6715\uff01\u201d\u5510\u6653 \u6f9c\u5c06\u5eb7\u7199\u7ed9\u4ed6\u7684\u90a3\u5757\u6c49\u7389\uff0c\u653e\u5728\u624b\u4e2d\u629a\u5f04\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u606d\u559c\u7687\u4e0a\u767b\u4e86\u5927\u5b9d\uff0c\u7687\u4e0a\u60f3\u8fd8\u8ba4\u8bc6 \u8fd9\u5757\u7389\u5427\uff1f\u201d \u3000\u3000\u96cd\u6b63\u9762\u8272\u4e00\u53d8\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u673a\u5bc6\u8981\u8bf4\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u8bf7\u7687\u4e0a\u5c4f\u9000\u5de6\u53f3\u3002\u201d\u96cd\u6b63\u5fc3 \u60f3\uff1a\u5510\u6653\u6f9c\u6b66\u529f\u867d\u662f\u4e0d\u51e1\uff0c\u4f46\u4ea6\u4e0d\u80fd\u4f24\u6211\u3002\u4fbf\u9053\uff1a\u201c\u54c8\u603b\u7ba1\u548c\u5929\u53f6\u6563\u4eba\uff0c\u4f60\u4eec\u6682\u4e14\u9000\u4e0b\u3002\u201d \u3000\u3000\u5bbd\u9614\u7684\u5ba2\u5385\u4e2d\uff0c\u96cd\u6b63\u548c\u5510\u6653\u6f9c\u9762\u9762\u76f8\u5bf9\uff0c\u5510\u6653\u6f9c\u4ecd\u7136\u629a\u5f04\u624b\u4e2d\u6c49\u7389\uff0c\u96cd\u6b63\u9053\uff1a\u201c\u5148\u7687\u9057 \u8bcf\uff0c\u66fe\u8981\u8054\u597d\u597d\u5f85\u4f60\uff0c\u4f60\u4e14\u5750\u4e0b\u3002\u201d\u5510\u6d47\u6f9c\u4e5f\u4e0d\u5ba2\u6c14\uff0c\u5750\u4e86\u4e0b\u6765\uff0c\u96cd\u6b63\u53c8\u9053\uff1a\u201c\u90a3\u65e5\u5148\u7687\u9a7e \u5d29\uff0c\u4f60\u968f\u5341\u56db\u8d1d\u52d2\u95ef\u8fdb\u7545\u6625\u56ed\uff0c\u610f\u6b32\u4f55\u4e3a\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u771f\u597d\u624b\u6bb5\u3002\u201d\u96cd\u6b63\u4ee5\u4e3a\u5510\u6653\u6f9c\u662f\u6307\u90a3\u65e5\u88ab\u4ed6\u6240\u64d2\u4e4b\u4e8b\uff0c\u51b7 \u7b11\u9053\uff1a\u201c\u6715\u5728\u5c11\u6797\u5bfa\u51fa\u8eab\uff0c\u4e5f\u4e0d\u6015\u4f60\u77e5\u9053\u3002\u201d\u53c8\u9053\uff1a\u201c\u4f60\u548c\u5415\u56db\u5a18\u90a3\u8d3c\u5a62\u662f\u5426\u540c\u515a\u5408\u8c0b\u5bb3 \u6211\uff1f\u4f60\u4ece\u5b9e\u62db\u6765\uff0c\u6715\u51b3\u4e0d\u8ba1\u8f83\u65e7\u6068\u3002\u201d\u90a3\u4e00\u591c\uff08\u96cd\u6b63\u521d\u767b\u5927\u5b9d\u4e4b\u591c\uff09\uff0c\u96cd\u6b63\u672a\u53ca\u5ba1\u95ee\uff0c\u5510\u6653 \u6f9c\u4fbf\u88ab\u5415\u56db\u5a18\u6551\u8d70\uff0c\u96cd\u6b63\u751a\u591a\u7591\u95ee\uff0c\u76d8\u585e\u5fc3\u4e2d\uff0c\u6240\u4ee5\u60f3\u95ee\u4e2a\u660e\u767d\u3002 \u3000\u3000\u5510\u6653\u6f9c\u957f\u7b11\u4e0d\u7b54\uff0c\u96cd\u6b63\u9762\u8272\u4e00\u6c89\uff0c\u4fbf\u60f3\u53d1\u4f5c\u3002\u5510\u6653\u6f9c\u5ffd\u9053\uff1a\u201c\u5eb7\u7199\u4e94\u5341\u4e5d\u5e74\u4e09\u6708\u5341\u516d\u4e4b \u4e8b\uff0c\u7687\u4e0a\u8fd8\u8bb0\u5f97\u4e48\uff1f\u201d \u3000\u3000\u90a3\u4e00\u65e5\u665a\u4e0a\uff0c\u6b63\u662f\u5510\u6653\u6f9c\u521d\u6b21\u5165\u5bab\uff0c\u78b0\u89c1\u51af\u7433\u5077\u5165\u6b63\u5927\u5149\u660e\u6bbf\u7684\u665a\u4e0a\u3002\u5510\u6653\u6f9c\u5f53\u65f6\u8fd8\u4e0d \u77e5\u9053\u662f\u5141\u796f\u53eb\u5979\u5077\u770b\u9057\u8bcf\uff0c\u76f4\u5230\u5141\u796f\u5373\u4f4d\u4e4b\u540e\uff0c\u5510\u6653\u6f9c\u8bb0\u8d77\u524d\u4e8b\uff0c\u624d\u8d77\u4e86\u7591\u5fc3\u3002\u6545\u6b64\u51fa\u8a00\u76f8 \u8bd5\u3002 \u3000\u3000\u96cd\u6b63\u542c\u4e86\uff0c\u9762\u8272\u4e00\u53d8\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u5bf9\u5341\u56db\u8d1d\u52d2\u5012\u5f88\u5fe0\u5fc3\u3002\u201d\u4ed6\u4e00\u76f4\u4ee5\u4e3a\u5510\u6653 \u6f9c\u662f\u5148\u5e1d\u7684\u536b\u58eb\uff0c\u5141\u5824\u7684\u5fc3\u8179\u3002 \u3000\u3000\u5510\u6653\u6f9c\u542c\u8a00\u5bdf\u8272\uff0c\u5fc3\u9053\uff1a\u201c\u770b\u6765\u6211\u731c\u5f97\u4e0d\u9519\uff01\u201d\u60f3\u8d77\u5728\u7545\u6625\u56ed\u4e2d\u6240\u89c1\u7684\u5148\u5e1d\u6b7b\u65f6\u7684\u60e8 \u72b6\uff0c\u84e6\u5730\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u5141\u796f\uff0c\u4f60\u5e72\u5f97\u597d\u4e8b\uff01\u201d \u3000\u3000\u96cd\u6b63\u5403\u4e86\u4e00\u60ca\uff0c\u970d\u5730\u8d77\u7acb\uff0c\u53f3\u81c2\u4e00\u62ac\uff0c\u5411\u5510\u6653\u6f9c\u54bd\u5589\u6293\u53bb\uff0c\u96cd\u6b63\u7684\u6b66\u529f\u5f97\u81ea\u5c11\u6797\u524d\u4efb\u4e3b \u6301\u672c\u7a7a\u5927\u5e08\u7684\u771f\u4f20\uff0c\u6781\u4e3a\u5389\u5bb3\uff0c\u5510\u6653\u6f9c\u80a9\u5934\u4e00\u7f29\uff0c\u811a\u6b65\u4e0d\u79fb\uff0c\u907f\u4e86\u8fd9\u4e00\u62db\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4f60 \u6740\u6b7b\u6211\u4e5f\u6ca1\u7528\uff01\u201d \u3000\u3000\u96cd\u6b63\u9762\u8272\u9752\u767d\uff0c\u5ffd\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u77a7\u89c1\u4e86\u6211\u4e5f\u4e0d\u6015\uff0c\u4f60\u6709\u4ec0\u4e48\u672c\u4e8b\u80fd\u6447\u52a8\u6211\u7684\u5b9d\u5ea7\uff0c \u62a2\u593a\u6211\u7684\u6c5f\u5c71\uff1f\u4fd7\u8bed\u4e91\uff1a\u8bc6\u65f6\u52a1\u8005\u4e3a\u4fca\u6770\uff0c\u4f60\u4ece\u5b9e\u8bf4\u6765\uff0c\u4f60\u662f\u53d7\u8c01\u7684\u6307\u4f7f\uff1f\u662f\u516b\u8d1d\u52d2\u8fd8\u662f\u4e5d \u8d1d\u52d2\uff1f\u4f60\u82e5\u60f3\u66ff\u4ed6\u4eec\u593a\u4f4d\uff0c\u90a3\u4f60\u7684\u4e3b\u610f\u5c31\u6253\u5f97\u9519\u4e86\uff01\u73b0\u949f\u4e0d\u6253\u4f60\u53cd\u53bb\u7ec3\u94dc\u5417\uff1f\u4f60\u4ece\u5b9e\u8bf4\u6765\uff0c \u81ea\u6709\u4f60\u7684\u597d\u5904\uff0c\u4f60\u81ea\u5df1\u60f3\u60f3\u3002\u201d \u3000\u3000\u5341\u56db\u8d1d\u52d2\u5141\u5824\u88ab\u89e3\u4e86\u5175\u6743\u8f6f\u7981\u4e4b\u540e\uff0c\u5728\u4f17\u7687\u5b50\u4e2d\uff0c\u96cd\u6b63\u6700\u60e7\u7684\u5c31\u662f\u516b\u8d1d\u52d2\u5141\u7940\u5df2\u4e86\u3002\u5141 \u7940\u7cbe\u901a\u6b66\u827a\uff0c\u800c\u4e14\u9887\u5f97\u4eba\u5fc3\u3002\u81f3\u4e8e\u4e5d\u8d1d\u52d2\u5141\u5510\u5219\u662f\u5141\u7940\u7684\u4e00\u515a\u3002\u96cd\u6b63\u8c0b\u593a\u4e86\u7687\u4f4d\u4e4b\u540e\uff0c\u751f\u6015 \u5176\u4ed6\u7687\u5b50\u4e5f\u201c\u4ee5\u5176\u4eba\u4e4b\u9053\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\u201d\uff0c\u8c0b\u593a\u4ed6\u7684\u7687\u4f4d\uff0c\u56e0\u6b64\u5904\u5904\u63d0\u9632\uff0c\u7591\u5fc3\u751a\u91cd\u3002 \u3000\u3000\u90a3\u4e00\u65e5\u5728\u7545\u6625\u56ed\u4e2d\uff0c\u96cd\u6b63\u627c\u6740\u4e86\u5eb7\u7199\u4e4b\u540e\uff0c\u5510\u6653\u6f9c\u624d\u51b2\u8fdb\u6765\uff0c\u867d\u7136\u89c1\u5eb7\u7199\u6b7b\u72b6\u53ef\u7591\uff0c\u8fd8 \u4e0d\u6562\u6599\u5230\u662f\u5141\u796f\u6240\u65bd\u7684\u6bd2\u624b\uff0c\u5982\u4ee4\u89c1\u5141\u796f\u5982\u6b64\u53e3\u6c14\uff0c\u5206\u660e\u5148\u5e1d\u662f\u4ed6\u6240\u5f11\u65e0\u7591\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u5510\u6653\u6f9c\u51e0\u4e4e\u6309\u637a\u4e0d\u4f4f\uff0c\u5c31\u60f3\u62d4\u5251\u548c\u4ed6\u53ae\u62fc\uff0c\u96cd\u6b63\u76ee\u9732\u51f6\u5149\uff0c\u5634\u89d2\u6302\u7740\u51b7\u7b11\uff0c \u5510\u6653\u6f9c\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u5fc3\u5ff5\u8fd9\u4e0d\u662f\u901e\u5339\u592b\u4e4b\u52c7\u7684\u65f6\u5019\u3002\u96cd\u6b63\u903c\u524d\u4e00\u6b65\uff0c\u8ffd\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d \u8bf4\uff1f\u6307\u4f7f\u4f60\u7684\u4eba\u662f\u516b\u8d1d\u52d2\u8fd8\u662f\u4e5d\u8d1d\u52d2\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u5f3a\u6291\u6012\u706b\uff0c\u4ef0\u5929\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u662f\u4f55\u7b49\u6837\u4eba\uff1f\u4f60\u4e5f\u4e0d\u77e5\u9053\uff01\u4ed6\u4eec\u5c82\u914d\u6307\u4f7f\u6211\uff1f\u4f60 \u628a\u7687\u4f4d\u770b\u5f97\u5982\u6b64\u4e4b\u91cd\uff0c\u96be\u9053\u522b\u4eba\u4e5f\u5f97\u50cf\u4f60\u4e48\uff1f\u201d \u3000\u3000\u96cd\u6b63\u6014\u4e86\u4e00\u5f81\uff1b\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u516b\u8d1d\u52d2\u4e0d\u60f3\u7687\u4f4d\u5417\uff1f\u201d\u5510\u6653\u6f9c\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u6211\u662f\u8bf4\u6211\u81ea \u5df1\uff0c\u4e0e\u4ed6\u4f55\u6d89\uff1f\u6211\u4e3a\u81ea\u5df1\u5e86\u5e78\uff0c\u597d\u5728\u6211\u4e0d\u957f\u5728\u7687\u5bb6\uff0c\u54c8\u54c8\uff0c\u54c8\u54c8\uff0c\u54c8\uff01\u201d \u3000\u3000\u96cd\u6b63\u65a5\u9053\uff1a\u201c\u4f60\u75af\u4e86\u5417\uff1f\u201d\u4ed6\u600e\u77e5\u5510\u6653\u6f9c\u4e5f\u662f\u51e4\u5b50\u9f99\u5b59\uff0c\u53ea\u56e0\u76ee\u7779\u7687\u5ba4\u9ed1\u5e55\u4e4b\u591a\uff0c\u9aa8\u8089 \u76f8\u6b8b\u4e4b\u60e8\uff0c\u4e00\u65f6\u63a7\u5236\u4e0d\u4f4f\uff0c\u53d1\u4e3a\u60b2\u6124\u4e4b\u58f0\u3002 \u3000\u3000\u5510\u6653\u6f9c\u72c2\u7b11\u4e00\u9635\uff0c\u96cd\u6b63\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u65e2\u7136\u4e0d\u662f\u60f3\u66ff\u516b\u8d1d\u52d2\u4e89\u593a\u7687\u4f4d\uff0c\u90a3\u4e48\u4f60\u6df1\u591c\u5230\u6b64\uff0c \u610f\u6b32\u4f55\u4e3a\uff1f\u6240\u8bf4\u673a\u5bc6\uff0c\u53c8\u662f\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u6d3e\u4eba\u5077\u5165\u6b63\u5927\u5149\u660e\u6bbf\uff0c\u53c8\u5728\u7545\u6625\u56ed\u903c\u6b7b\u7236\u7687\uff0c\u8fd9\u8fd8\u4e0d\u7b97\u662f\u673a\u5bc6\u5417\uff1f\u54c8\uff0c \u5728\u4f60\uff0c\u8fd9\u4e0d\u7b97\u662f\u673a\u5bc6\uff0c\u4f46\u82e5\u4f17\u7687\u5b50\u77e5\u554a\uff0c\u53ef\u5c31\u662f\u5929\u5927\u7684\u79d8\u5bc6\uff0c\u4ed6\u4eec\u5c82\u80af\u4e0e\u4f60\u5e72\u4f11\uff1f\u201d \u3000\u3000\u96cd\u6b63\u76ee\u9732\u51f6\u5149\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u662f\u501f\u6b64\u8981\u631f\u6211\u4e86\uff1f\u6765\u2014\u2014\u201d\u201c\u6765\u4eba\u54ea\u201d\u4e09\u5b57\u5c1a\u672a \u8bf4\u51fa\uff0c\u5510\u6653\u6f9c\u5ffd\u5730\u4e00\u58f0\u51b7\u7b11\uff0c\u7b11\u58f0\u523a\u8033\u7a7f\u5fc3\uff0c\u9976\u662f\u96cd\u6b63\u7edd\u4ee3\u5978\u7687\uff0c\u4e5f\u89c9\u5bd2\u6817\uff0c\u53ea\u542c\u5f97\u5510\u6653\u6f9c \u9053\uff1a\u201c\u4f60\u4eca\u665a\u82e5\u6740\u4e86\u6211\uff0c\u5341\u65e5\u4e4b\u5185\uff0c\u4f60\u7684\u79d8\u5bc6\uff0c\u5c31\u8981\u4f20\u904d\u4eac\u534e\uff01\u201d \u3000\u3000\u96cd\u6b63\u72de\u7b11\u9053\uff1a\u201c\u4f60\u5355\u8eab\u5230\u6b64\uff0c\u6211\u628a\u4f60\u5316\u9aa8\u626c\u7070\uff0c\u8c01\u80fd\u77e5\u9053\uff1f\u4f60\u597d\u597d\u542c\u8054\u7684\u8bdd\uff0c\u4e0d\u5931\u4f60\u7684 \u529f\u540d\u5bcc\u8d35\uff0c\u5148\u5e1d\u8fd8\u6709\u4ec0\u4e48\u9057\u8bcf\u4ea4\u7ed9\u4f60\u5417\uff1f\u201d\u96cd\u6b63\u60f3\u8f6f\u786c\u517c\u65bd\uff0c\u518d\u52a0\u76d8\u95ee\uff0c\u5510\u6653\u6f9c\u5ffd\u5730\u957f\u5578\u4e00 \u58f0\uff0c\u53cc\u638c\u4e00\u62cd\uff0c\u5c4b\u9876\u7a81\u7136\u6709\u58f0\u53eb\u9053\uff1a\u201c\u5510\u5144\u5f1f\u4f60\u653e\u5fc3\uff0c\u4f60\u4eec\u7684\u8bdd\uff0c\u6211\u90fd\u542c\u5230\u4e86\uff01\u201d\u96cd\u6b63\u5927 \u53eb\uff1a\u201c\u6349\u523a\u5ba2\uff01\u201d\u5c4b\u9876\u4e0a\u7684\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u95e8\u5916\u54c8\u5e03\u9640\u4e0e\u5929\u53f6\u6563\u4eba\u98de\u8eab\u8ffd\u8d76\uff0c\u7b11\u58f0\u6563\u5165\u82b1\u6728\u4e1b \u4e2d\uff0c\u8f6c\u77ac\u4e0d\u89c1\u3002 \u3000\u3000\u8fd9\u4eba\u6b63\u662f\u9648\u5fb7\u6cf0\uff0c\u4ed6\u53f7\u79f0\u201c\u795e\u5077\u201d\uff0c\u81ea\u6709\u65e5\u8d70\u5343\u5bb6\u591c\u52ab\u767e\u6237\u7684\u795e\u51fa\u9b3c\u6ca1\u672c\u9886\u3002\u5510\u6653\u6f9c\u4e0e \u4ed6\u7b97\u51c6\uff0c\u5728\u56ed\u4e2d\u5927\u95f9\u4e4b\u540e\uff0c\u6b66\u58eb\u6b47\u606f\uff0c\u6212\u5907\u5fc5\u677e\uff1b\u800c\u4e14\u5510\u6653\u6f9c\u5355\u8eab\u6c42\u89c1\uff0c\u54c8\u5e03\u9640\u4e0e\u5929\u53f6\u6563\u4eba \u7684\u6ce8\u610f\u5fc5\u7136\u653e\u5728\u5510\u6653\u6f9c\u8eab\u4e0a\u3002\u5b88\u5728\u95e8\u5916\uff0c\u7559\u5fc3\u7684\u662f\u5c4b\u5185\u7684\u58f0\u606f\u3002\u56e0\u6b64\u9648\u5fb7\u6cf0\u5f97\u4ee5\u4ece\u5bb9\u57cb\u4f0f\uff0c \u5927\u80c6\u53d1\u8a00\uff0c\u8fd9\u5728\u6c5f\u6e56\u767e\u8ba1\u4e2d\u5c5e\u4e8e\u201c\u660e\u4fee\u6808\u9053\uff0c\u6697\u6e21\u9648\u4ed3\u201d\uff0c\u8f6c\u79fb\u6ce8\u610f\uff0c\u6df1\u5165\u654c\u4eba\u8179\u5730\u4e4b\u8ba1\uff0c \u672c\u6765\u662f\u6781\u9669\u7684\u4e00\u7740\uff0c\u4fa5\u5e78\u7adf\u5f97\u6210\u529f\u3002 \u3000\u3000\u96cd\u6b63\u9762\u8272\u9752\u767d\uff0c\u9893\u7136\u5750\u5728\u592a\u5e08\u6905\u4e0a\uff0c\u4e0d\u53d1\u4e00\u8a00\u3002\u7247\u523b\u4e4b\u540e\uff0c\u54c8\u5e03\u9640\u4e0e\u5929\u53f6\u6563\u4eba\u56de\u6765\u8bf7 \u7f6a\u3002\u8bf4\u662f\u523a\u5ba2\u5df2\u7ecf\u9003\u9038\u65e0\u8e2a\u3002\u54c8\u5e03\u9640\u6084\u6084\u7980\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u8bf7\u628a\u5510\u6653\u6f9c\u8fd9\u5c0f\u5b50\u4ea4\u7ed9\u6211\uff0c\u6211\u7528\u6bd2 \u5211\u903c\u4f9b\uff0c\u4e0d\u6015\u4ed6\u4e0d\u8bf4\u51fa\u523a\u5ba2\u6765\u5386\u3002\u201d\u96cd\u6b63\u6012\u6781\u3002\u4e00\u638c\u5411\u54c8\u5e03\u9640\u63b4\u53bb\uff0c\u5ffd\u5730\u60f3\u8d77\u54c8\u5e03\u9640\u5fe0\u5fc3\u803f \u803f\uff0c\u4e0d\u5e94\u592a\u4f24\u4ed6\u9762\u5b50\uff0c\u4e00\u638c\u62cd\u51fa\uff0c\u672a\u5230\u9762\u95e8\uff0c\u65b9\u5411\u5ffd\u6539\uff0c\u4e00\u638c\u5c06\u9760\u6905\u7684\u6297\u5b50\u6253\u65ad\uff0c\u9053\uff1a\u201c\u4f60 \u4eec\u51fa\u53bb\uff0c\u6715\u81ea\u6709\u4e3b\u610f\uff0c\u4e0d\u5fc5\u4f60\u4eec\u591a\u8a00\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u795e\u8272\u81ea\u5982\uff0c\u5f85\u54c8\u5e03\u9640\u4e0e\u5929\u53f6\u6563\u4eba\u8d70\u540e\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u8fd9\u813e\u6c14\u53ef\u53d1\u4e0d\u5f97\u54ea\uff01\u201d \u3000\u3000\u96cd\u6b63\u6012\u6781\u6c14\u6781\uff0c\u773c\u73e0\u4e00\u8f6c\uff0c\u53cd\u800c\u9762\u8272\u7f13\u548c\uff0c\u5927\u7b11\u8bf4\u9053\uff1a\u201c\u54c8\uff0c\u6709\u4f60\u4e00\u624b\uff0c\u8fd9\u4ea4\u60c5\u53ef\u5f97\u5356 \u7ed9\u4f60\u4e86\u3002\u4f60\u8bf4\uff0c\u4f60\u65e2\u7136\u5192\u6b7b\u89c1\u6715\uff0c\u800c\u53c8\u4e0d\u662f\u542c\u4eba\u6307\u4f7f\uff0c\u90a3\u5fc5\u5b9a\u662f\u6709\u6240\u6c42\u4e8e\u6715\u4e86\u3002\u4f60\u723d\u76f4\u7684 \u8bf4\uff0c\u4f60\u6240\u6c42\u7684\u7a76\u662f\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u7687\u4e0a\u77e5\u673a\u5584\u65ad\uff0c\u679c\u7136\u6bd4\u5341\u56db\u8d1d\u52d2\u9ad8\u660e\uff0c\u96be\u602a\u4f60\u5f97\u4e86\u7687\u4f4d\u3002\u201d\u4f3c\u8d5e\u4f3c\u8bbd\u7684\u8bf4 \u4e86\u51e0\u53e5\u4e4b\u540e\uff0c\u5ffd\u5730\u9762\u8272\u4e00\u7aef\u8bf4\u9053\uff1a\u201c\u6211\u6597\u80c6\u8bf7\u7687\u4e0a\u5c06\u7433\u59d1\u5a18\u4ea4\u7ed9\u6211\u5e26\u56de\u53bb\uff01\u201d \u3000\u3000\u96cd\u6b63\u6014\u4e86\u6014\uff0c\u4ed6\u7edd\u672a\u6599\u5230\u5510\u6653\u6f9c\u5192\u9669\u72af\u96be\uff0c\u4e3a\u7684\u7adf\u662f\u8fd9\u4e2a\u5973\u5b50\u3002\u60f3\u8d77\u51af\u7433\u6708\u8c8c\u82b1\u5bb9\uff0c\u5341 \u5206\u96be\u820d\u3002\u4f46\u542c\u5f97\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u5e26\u5979\u51fa\u53bb\u4e4b\u540e\uff0c\u53d1\u8a93\u8df3\u51fa\u662f\u975e\u4e4b\u573a\uff0c\u518d\u4e0d\u7ba1\u4f60\u4eec\u7687\u5bb6\u4e4b\u4e8b \u4e86\u3002\u201d\u96cd\u6b63\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u600e\u4e48\u542c\u6b64\u4eba\u53e3\u6c14\uff0c\u7adf\u4f3c\u4e0e\u6211\u4eec\u7687\u5ba4\u5927\u6709\u6e0a\u6e90\uff1f\u4e3a\u4ec0\u4e48\u7236\u7687\u8fd9 \u6837\u5ba0\u4fe1\u4e8e\u4ed6\uff1f\u4ed6\u5230\u5e95\u662f\u4ec0\u4e48\u6765\u5386\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u89c1\u96cd\u6b63\u6c89\u541f\u4e0d\u8bed\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\u3002\u8bf4\u8fc7\u4fbf\u7b97\uff0c\u54b1\u4eec\u4eca\u540e\u4e95\u6c34 \u4e0d\u72af\u6cb3\u6c34\uff0c\u6211\u8a00\u5c3d\u4e8e\u6b64\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u987e\u5fcc\u4e48\uff1f\u201d\u8fd9\u8bdd\u65a9\u9489\u622a\u94c1\u7684\u8bf4\u660e\uff1a\u82e5\u7136\u96cd\u6b63\u5c06\u51af\u7433\u653e \u51fa\uff0c\u4ed6\u5c31\u51b3\u4e0d\u63ed\u7a7f\u96cd\u6b63\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u96cd\u6b63\u54c8\u54c8\u4e00\u7b11\uff0c\u63a9\u9970\u7a98\u6001\uff0c\u4e3e\u624b\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u8981\u5979\uff0c\u6211\u4fbf\u8d50\u7ed9\u4f60\u5427\u3002\u7f8e\u4eba\u513f\u4eba\u89c1\u4eba \u7231\uff0c\u60f3\u4e0d\u5230\u6715\u4ee5\u4e07\u4e58\u4e4b\u5c0a\uff0c\u4e5f\u7adf\u65e0\u798f\u6d88\u53d7\uff0c\u4f60\u4eca\u540e\u53ef\u5f97\u597d\u597d\u770b\u5f85\u4e8e\u5979\uff01\u54c8\uff0c\u6765\u4eba\u54ea\uff01\u201d\u5510\u6653 \u6f9c\u60f3\u4e0d\u5230\u4ed6\u5fc3\u601d\u5982\u6b64\u6deb\u90aa\uff0c\u6ee1\u9762\u901a\u7ea2\uff0c\u201c\u5478\u201d\u4e86\u4e00\u53e3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u672c\u65e0\u5927\u5e08\u9a82\u4f60\u662f\u91c7\u82b1\u6deb \u8d3c\uff01\u4f60\u505a\u4e86\u7687\u5e1d\uff0c\u6211\u771f\u8981\u4e3a\u7231\u65b0\u89c9\u7f57\u6c0f\u7684\u5217\u7956\u5217\u5b97\u53eb\u5c48\uff01\u201d \u3000\u3000\u96cd\u6b63\u9762\u8272\u4e00\u6c89\uff0c\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u7684\u8bb0\u6027\u5012\u4e0d\u574f\uff0c\u8fd8\u8bb0\u8d77\u6715\u5728\u5c71\u4e1c\u65f6\u5019\u505a\u7684\u98ce\u6d41\u97f5\u4e8b\u3002\u5bf9 \u5566\uff0c\u54b1\u4eec\u8fd8\u662f\u8001\u670b\u53cb\u5440\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u54c8\u5e03\u9640\u4e0e\u5929\u53f6\u6563\u4eba\u53c8\u5df2\u53cc\u53cc\u8d70\u8fdb\uff0c\u96cd\u6b63\u6454\u624b\u9053\uff1a\u201c\u54c8\u603b\u7ba1\uff0c\u4f60\u628a\u7433\u59d1\u5a18\u5e26 \u6765\u3002\u201d\u53c8\u5bf9\u5929\u53f6\u6563\u4eba\u9053\uff0c\u201c\u4f60\u5c06\u6715\u7684\u91d1\u6ce2\u7389\u6db2\u743c\u6d46\u9152\u62ff\u6765\u3002\u6715\u8981\u4e0e\u5510\u5144\u75db\u996e\u51e0\u676f\u3002\u201d \u3000\u3000\u4e24\u4eba\u63a5\u65e8\u9000\u4e0b\uff0c\u5510\u6653\u6f9c\u60f3\u8d77\u5e74\u7fb9\u5c27\u6bd2\u6740\u672c\u65e0\u5927\u5e08\u4e4b\u4e8b\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u4e8b\u60c5\u4e00\u5b8c\u4fbf\u8d70\uff0c \u8c01\u8981\u559d\u4f60\u7684\u9152\uff1f\u201d \u3000\u3000\u5929\u53f6\u6563\u4eba\u6367\u58f6\u8d70\u51fa\uff0c\u659f\u4e86\u4e24\u676f\uff0c\u5782\u624b\u9000\u4e0b\u3002\u96cd\u6b63\u4e3e\u676f\u7b11\u9053\uff1a\u201c\u6700\u96be\u76f8\u8bc6\u6545\u4eba\u6765\uff0c\u54b1\u4eec\u5728 \u9752\u5c9b\u7684\u6ee8\u6d77\u697c\u540c\u996e\u4ee5\u6765\uff0c\u970e\u5ffd\u53c8\u8fd1\u5341\u5e74\uff0c\u5149\u9634\u5982\u767d\u9a79\u8fc7\u9699\uff0c\u601d\u4e4b\u4ee4\u4eba\u611f\u53f9\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u4ecd\u7136\u7aef\u5750\u4e0d\u52a8\u3002\u96cd\u6b63\u5ffd\u7b11\u9053\uff1a\u201c\u4f60\u6015\u6715\u6bd2\u6740\u4f60\u5417\uff1f\u6715\u8981\u6740\u4f60\uff0c\u4f55\u5fc5\u5728\u9152\u4e2d\u4e0b \u6bd2\uff1f\u201d\u5c06\u676f\u9152\u4e00\u996e\u800c\u5c3d\uff0c\u63b7\u676f\u7b11\u9053\uff1a\u201c\u4f60\u5982\u6b64\u591a\u60e7\uff0c\u53eb\u6715\u5982\u4f55\u80fd\u4fe1\u6258\u4e8e\u4f60\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u5fc3\u60f3\uff1a\u4e0d\u996e\u6050\u751f\u679d\u8282\u3002\u4ed6\u6709\u79d8\u5bc6\u5728\u6211\u624b\u4e2d\uff0c\u6599\u4ed6\u4e0d\u6562\u6740\u6211\u3002\u8fd9\u676f\u9152\u996e\u53c8\u4f55\u59a8\uff1f\u770b \u4ed6\u53c8\u6709\u4ec0\u4e48\u82b1\u6837\uff1f\u4e5f\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\uff0c\u5c06\u7a7a\u676f\u6454\u4e0b\u5ead\u5fc3\u3002\u96cd\u6b63\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u5510\u6653\u6f9c\u4f46\u89c9\u9152\u9999\u6d53\u51bd\uff0c\u4ea6\u65e0\u5f02\u72b6\u3002\u96cd\u6b63\u5927\u7b11\u58f0\u4e2d\uff0c\u54c8\u5e03\u9640\u5df2\u5c06\u51af\u745b\u5e26\u4e0a\u3002\u51af\u745b\u5927\u58f0\u56b7 \u9053\uff1a\u201c\u4f60\u8981\u6740\u8981\u5250\uff0c\u968f\u4f60\u7684\u4fbf\u3002\u8981\u6211\u542c\u4f60\u6446\u5e03\uff0c\u53ef\u4e07\u4e07\u4e0d\u80fd\u3002\u4f60\u5982\u6b64\u8352\u6deb\u65e0\u9053\uff0c\u6211\u770b\u4f60\u7684\u7687 \u4f4d\u4e5f\u4e0d\u4e45\u957f\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u542c\u51af\u745b\u75db\u9a82\u7687\u5e1d\uff0c\u5fc3\u4e2d\u559c\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u6062\u590d\u4e86\u672c\u6027\u4e86\u3002\u201d\u51af\u745b\u5ffd\u89c1\u5510\u6653\u6f9c\u4e5f\u5728\u5802 \u4e0a\uff0c\u60ca\u559c\u4ea4\u96c6\uff0c\u9a82\u58f0\u987f\u6b62\uff0c\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u5510\u53d4\u53d4\uff0c\u4f60\u4e5f\u5728\u8fd9\u513f\uff01\u201d \u3000\u3000\u96cd\u6b63\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u8fd8\u662f\u53d4\u4f84\uff0c\u597d\u5440\uff0c\u7433\u59d1\u5a18\uff0c\u4f60\u4e0d\u613f\u56de\u4eac\uff0c\u5c31\u968f\u4f60\u7684\u53d4\u53d4\u8d70\u5427\u3002\u201d \u3000\u3000\u51af\u745b\u7741\u5927\u773c\u775b\uff0c\u770b\u7740\u5510\u6653\u6f9c\uff0c\u76ee\u5149\u4e2d\u5e26\u7740\u65e0\u9650\u7591\u8651\u3002\u5510\u6653\u6f9c\u542c\u4e86\u5979\u90a3\u58f0\u201c\u53d4\u53d4\u201d\uff0c\u5df2\u77e5 \u5979\u662f\u51af\u745b\u800c\u4e0d\u662f\u51af\u7433\uff0c\u95ee\u9053\uff1a\u201c\u5e08\u5085\u597d\u5417\uff1f\u4f60\u5230\u4e86\u9099\u5c71\u6ca1\u6709\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u662f\u5e08\u5085\u53eb\u4f60\u6765\u63a5 \u6211\u7684\u5417\uff1f\u201d\u8fd9\u65f6\u5979\u5df2\u786e\u77e5\u4e0d\u662f\u5728\u68a6\u4e2d\uff0c\u795e\u60c5\u987f\u7136\u559c\u60a6\u3002 \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\uff01\u201d\u96cd\u6b63\u5ffd\u7136\u659f\u6ee1\u4e00\u676f\u7f8e\u9152\uff0c\u9053\uff1a\u201c\u7433\u59d1\u5a18\uff0c\u8fd9\u662f\u4f60\u6700\u559c\u6b22\u7684\u91d1 \u6ce2\u7389\u6db2\u743c\u6d46\u9152\uff0c\u4f60\u4e0d\u559d\u4e00\u676f\u4e48\uff1f\u201d\u51af\u745b\u6012\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u7433\u59d1\u5a18\uff1f\u8c01\u5e0c\u7f55\u4f60\u7684\u9152\uff1f\u201d\u4f38\u624b\u4e00 \u626b\uff0c\u628a\u9152\u676f\u626b\u843d\u53f0\u9636\uff0c\u7830\u7136\u4e00\u58f0\uff0c\u9152\u676f\u788e\u88c2\uff0c\u5ffd\u5730\u6cdb\u8d77\u4e00\u56e2\u706b\u5149\uff0c\u5510\u6653\u6f9c\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a \u201c\u8fd9\u9152\u5165\u53e3\u5e76\u4e0d\u545b\u5589\uff0c\u4e3a\u4f55\u5982\u6b64\u5389\u5bb3\uff1f \u3000\u3000\u51af\u745b\u8896\u5b50\u4e00\u62c2\uff0c\u8d70\u4e0b\u53f0\u9636\u3002\u96cd\u6b63\u5ffd\u9053\uff1a\u201c\u5510\u6653\u6f9c\uff0c\u4f60\u4e14\u6162\u8d70\u3002\u201d\u51af\u745b\u970d\u5730\u56de\u8f6c\u5934\u6765\uff0c\u6012 \u9053\uff1a\u201c\u6211\u65e9\u77e5\u4f60\u4e0d\u6000\u597d\u610f\uff0c\u4f60\u60f3\u628a\u6211\u7684\u53d4\u53d4\u7559\u4e0b\u4e48\uff1f\u5510\u53d4\u53d4\uff0c\u8fd9\u4e2a\u7687\u5e1d\u574f\u5f97\u5f88\uff0c\u4ed6\u7684\u8bdd\u4e0d\u80fd \u76f8\u4fe1\uff0c\u4ed6\u54ea\u80af\u5bb9\u6211\u4eec\u597d\u597d\u7684\u8d70\uff0c\u4e00\u5b9a\u662f\u53e6\u6709\u8be1\u8ba1\uff0c\u4f60\u4e0d\u8981\u7ed9\u4ed6\u9a97\u4e86\u3002\u201d\u62d4\u51fa\u77ed\u5251\uff0c\u4fbf\u5f85\u518d\u62fc\u3002 \u3000\u3000\u96cd\u6b63\u628a\u624b\u4e00\u6325\uff0c\u54c8\u5e03\u9640\u5c06\u51af\u745b\u62e6\u4f4f\u3002\u96cd\u6b63\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u5510\u5144\u4e0d\u662f\u6211\u4fe1\u4f60\u4e0d\u8fc7\uff0c\u4e8b\u5173\u91cd \u5927\uff0c\u6211\u603b\u5f97\u5728\u4f60\u8eab\u4e0a\u7559\u4e0b\u4e00\u70b9\u51ed\u8bb0\u3002\u201d\u5510\u6653\u6f9c\u72c2\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u81f3\u5c0a\u7687\u4e0a\uff0c\u4e5f\u8981\u884c\u6c5f\u6e56\u4e0a \u9ed1\u9053\u7684\u89c4\u77e9\u4e48\uff1f\u90a3\u5c31\u6765\u5427\uff0c\u6211\u65e2\u6562\u5230\u6b64\uff0c\u5373\u7b97\u4e09\u5200\u516d\u6d1e\uff0c\u51b3\u4e0d\u76b1\u7709\u3002\u201d \u3000\u3000\u6240\u8c13\u7559\u4e0b\u201c\u6041\u8bb0\u201d\uff0c\u4e43\u662f\u9ed1\u9053\u4e0a\u7684\u672f\u8bed\uff0c\u4f8b\u5982\u524a\u6389\u4e00\u53ea\u8033\u6735\uff0c\u523a\u778e\u4e00\u53ea\u773c\u775b\u4e4b\u7c7b\uff0c\u90fd\u7b97 \u5728\u8eab\u4e0a\u7559\u4e0b\u7684\u201c\u51ed\u8bb0\u201d\uff0c\u8fd9\u4e43\u662f\u8f88\u4efd\u5c0a\u3001\u6b66\u529f\u9ad8\u7684\u4e00\u65b9\uff0c\u8981\u60e9\u6212\u5bf9\u65b9\u65f6\u6240\u4e0b\u7684\u8fa3\u624b\uff0c\u4f46\u8fd9\u79cd \u624b\u6bb5\uff0c\u53ea\u6709\u9ed1\u9053\u4e0a\u7684\u9738\u4e3b\u624d\u4f1a\u4f7f\u7528\uff0c\u4e00\u822c\u6b66\u6797\u7684\u6b63\u6d3e\u4eba\u7269\uff0c\u662f\u51b3\u4e0d\u613f\u65bd\u4e3a\u7684\u3002 \u3000\u3000\u96cd\u6b63\u5f97\u610f\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5728\u4f60\u7684\u8eab\u4e0a\u7559\u4e0b\u6041\u8bb0\u4e86\uff0c\u4f60\u4e0d\u77e5\u9053\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u4f60 \u7684\u6b66\u529f\u4e5f\u4e0d\u89c1\u6bd4\u6211\u9ad8\u660e\uff0c\u600e\u80fd\u5728\u6211\u7684\u8eab\u4e0a\u505a\u4e0b\u624b\u811a\uff1f \u3000\u3000\u96cd\u6b63\u9053\uff1a\u201c\u5510\u5144\uff0c\u4f60\u4f11\u602a\u6211\uff0c\u521a\u624d\u90a3\u4e00\u676f\u9152\u4e43\u662f\u6bd2\u9152\uff01\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u8a00\u800c\u65e0\u4fe1\uff0c\u53ef \u4f11\u602a\u6211\u4e0d\u5b88\u8bfa\u8a00\uff01\u201d\u96cd\u6b63\u7b11\u9053\uff1a\u201c\u867d\u662f\u6bd2\u9152\uff0c\u53ef\u5bf9\u4f60\u5168\u65e0\u4f24\u5bb3\u3002\u8fd9\u6bd2\u9152\u8981\u4e00\u5e74\u540e\u624d\u53d1\u4f5c\uff0c\u5728 \u672a\u53d1\u4f5c\u65f6\uff0c\u4f60\u4e00\u5207\u90fd\u5982\u5e38\u4eba\u3002\u53d1\u4f5c\u4e4b\u540e\uff0c\u4e09\u65e5\u773c\u76f2\uff0c\u4e03\u65e5\u6b8b\u5e9f\uff0c\u5230\u7b2c\u5341\u65e5\u4fbf\u5455\u8840\u8eab\u4ea1\uff01\u6240\u4ee5 \u4f60\u81f3\u8fdf\u5728\u660e\u5e74\u4eca\u65e5\uff0c\u4fbf\u5f53\u5165\u5bab\u89c1\u6211\u6c42\u8ba8\u89e3\u836f\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u6c14\u5f97\u6d51\u8eab\u6218\u6296\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u96cd\u6b63\u5f97\u610f\u7b11\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u4e00\u5e74\u4e4b\u5185\uff0c\u82e5\u7136\u5b89\u4efd \u5b88\u5df2\uff0c\u660e\u5e74\u4eca\u65e5\uff0c\u4f60\u6765\u6c42\u6211\u4e4b\u65f6\uff0c\u6211\u81ea\u7136\u628a\u89e3\u836f\u7ed9\u4f60\u3002\u82e5\u4f60\u5984\u56fe\u751f\u4e8b\uff0c\u4e71\u9053\u662f\u975e\uff0c\u6311\u62e8\u4f17\u7687 \u5b50\u4e0e\u6211\u4f5c\u5bf9\uff0c\u90a3\u4e48\uff0c\u54fc\uff0c\u54fc\uff01\u4f60\u5c31\u522b\u60f3\u6d3b\u547d\u4e86\uff01\u201d\u96cd\u6b63\u8fd9\u4e00\u624b\u786e\u662f\u975e\u5e38\u6bd2\u8fa3\uff0c\u4ed6\u5fc3\u4e2d\u65e9\u5df2\u7b97 \u5b9a\uff1a\u201c\u5728\u4e00\u5e74\u4e4b\u5185\uff0c\u5fc5\u80fd\u5c06\u5404\u7687\u5b50\u7684\u7fbd\u7ffc\u5168\u90e8\u526a\u9664\uff0c\u90a3\u65f6\u5c31\u7b97\u5510\u6653\u6f9c\u4e0e\u51af\u7433\u6cc4\u9732\u4ed6\u7684\u79d8\u5bc6\uff0c \u4ed6\u4e5f\u4e0d\u6015\u4e86\u3002\u90a3\u65f6\uff0c\u5510\u6653\u6f9c\u6765\u6c42\u89e3\u836f\uff0c\u751f\u6b7b\u4e4b\u6743\uff0c\u53ef\u5c31\u5168\u64cd\u5728\u4ed6\u7684\u624b\u4e0a\u3002 \u3000\u3000\u5510\u6653\u6f9c\u9a82\u9053\uff1a\u201c\u597d\u4e0d\u8981\u8138\u7684\u4e0b\u6d41\u624b\u6bb5\uff01\u201d\u96cd\u6b63\u5927\u7b11\u9053\uff1a\u201c\u82e5\u975e\u5982\u6b64\uff0c\u8054\u600e\u80fd\u5b89\u5fc3\uff01\u201d\u9762 \u8272\u4e00\u6c89\uff0c\u62c2\u624b\u8bf4\u9053\uff1a\u201c\u4e00\u5e74\u540e\u518d\u89c1\uff0c\u5230\u65f6\u4f60\u8fd8\u8981\u628a\u7433\u8d35\u4eba\u4e5f\u5e26\u6765\uff0c\u4f60\u542c\u6e05\u695a\u6ca1\u6709\uff1f\u54c8\u603b\u7ba1\uff0c \u522b\u52a8\u624b\u5566\uff0c\u8ba9\u4ed6\u4eec\u8d70\uff01\u201d \u3000\u3000\u51af\u745b\u7ed9\u54c8\u5e03\u9640\u62e6\u7740\uff0c\u51b2\u4e0d\u4e0a\u53f0\u9636\uff0c\u6b63\u81ea\u53d1\u6025\uff0c\u5ffd\u89c1\u54c8\u5e03\u9640\u9000\u8fc7\u4e00\u8fb9\uff0c\u5510\u6653\u6f9c\u8d70\u4e0b\u5802\u6765\uff0c \u53c8\u559c\u53c8\u7591\uff0c\u95ee\u9053\uff1a\u201c\u5510\u53d4\u53d4\uff0c\u600e\u4e48\u8fd9\u72d7\u7687\u5e1d\u53c8\u80af\u653e\u4f60\u8d70\u4e86\uff1f\u201d\u5510\u6653\u6f9c\u4e00\u8a00\u4e0d\u53d1\uff0c\u5c06\u51af\u745b\u62c9\u4e0b \u53f0\u9636\uff0c\u51fa\u4e86\u5e74\u5bb6\u82b1\u56ed\uff0c\u8fd9\u624d\u5401\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u51af\u745b\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5510\u6653\u6f9c\u6050\u8bf4\u4e86\u51fa\u6765\uff0c\u5f92\u4ee4\u51af\u745b\u4f24\u5fc3\uff0c\u4e8e\u4e8b\u65e0\u8865\u3002\u6de1\u6de1\u8bf4 \u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u4ed6\u6709\u628a\u67c4\u5728\u6211\u624b\u91cc\uff0c\u6240\u4ee5\u624d\u4e0d\u5f97\u4e0d\u653e\u54b1\u4eec\u51fa\u6765\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u8f6c\u95ee\u51af\u745b\u4e0b\u5c71\u4e4b\u540e\u7684\u4e8b\u60c5\uff0c\u51af\u745b\u7ea6\u7565\u8bf4\u4e86\u4e00\u904d\uff0c\u5ffd\u9053\uff1a\u201c\u5510\u53d4\u53d4\uff0c\u4e16\u754c\u4e0a\u4f1a\u4e0d\u4f1a \u6709\u4e24\u4e2a\u76f8\u8c8c\u5b8c\u5168\u76f8\u4f3c\u7684\u4eba\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u82e5\u7136\u662f\u5b6a\u751f\u7684\u5144\u5f1f\u59d0\u59b9\uff0c\u76f8\u8c8c\u5b8c\u5168\u76f8\u540c\uff0c\u90a3\u4e5f\u662f \u6709\u7684\u3002\u201d\u51af\u745b\u4f4e\u5934\u6c89\u601d\uff0c\u826f\u4e45\uff0c\u826f\u4e45\uff0c\u62ac\u5934\u95ee\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u60f3\u5fc5\u6211\u8fd8\u6709\u4e2a\u5b6a\u751f\u7684\u59d0\u59b9\u4e86\u3002\u5e08 \u5085\u4e0d\u80af\u5c06\u6211\u7684\u6765\u5386\u8eab\u4e16\u8bf4\u51fa\u6765\uff0c\u5510\u53d4\u53d4\uff0c\u4f60\u53ef\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u4e0b\u5c71\u4e4b\u65f6\uff0c\u6613\u5170\u73e0\u66fe\u6709\u5429\u5490\uff0c\u8981\u4ed6\u5728\u627e\u5230\u5e08\u5ac2\u909d\u7ec3\u971e\u4e0e\u51af\u7433\u4e4b\u540e\uff0c\u624d\u80fd\u5c06\u51af\u745b\u7684 \u8eab\u4e16\u4e4b\u8c1c\u8bf4\u7834\u3002\u56e0\u4e3a\u51af\u745b\u81ea\u5c0f\u6027\u60c5\u521a\u70c8\uff0c\u672a\u5230\u5176\u65f6\uff0c\u8ba9\u5979\u77e5\u9053\uff0c\u4e0d\u4f46\u59a8\u788d\u4e86\u5979\u7684\u7ec3\u529f\uff0c\u8fd8\u5f97 \u6050\u9632\u5979\u95ef\u51fa\u4e8b\u6765\u3002 \u3000\u3000\u5510\u6653\u6f9c\u542c\u4e86\u51af\u745b\u6240\u8ff0\uff0c\u60f3\u9053\uff1a\u201c\u542c\u5979\u6240\u8bf4\u4eca\u665a\u7684\u906d\u9047\uff0c\u51af\u7433\u60f3\u5fc5\u5148\u5df2\u9003\u51fa\u8fd9\u4e2a\u56ed\u5b50\u3002\u5979 \u4e0d\u80af\u505a\u7687\u5e1d\u7684\u8d35\u5983\uff0c\u53ef\u89c1\u5979\u672c\u6027\u672a\u6df7\uff0c\u8fd8\u6709\u5fd7\u6c14\u3002\u4ee5\u524d\u5e08\u5085\u4e0d\u8ba9\u6211\u544a\u8bc9\u963f\u745b\uff0c\u9664\u4e86\u6015\u6270\u4e71\u5979 \u7684\u5fc3\u601d\uff0c\u59a8\u788d\u5979\u7684\u7ec3\u529f\u4e4b\u5916\uff0c\u8fd8\u6015\u5979\u5192\u9669\u95ef\u5165\u5bab\u5ef7\uff0c\u6216\u8005\u5f15\u8d77\u9aa8\u8089\u76f8\u6b8b\u3002\u73b0\u5728\u5979\u7684\u529f\u592b\u5df2\u7ecf \u7ec3\u597d\uff0c\u51af\u7433\u53c8\u51fa\u4e86\u5bab\u95e8\uff0c\u544a\u8bc9\u5979\u60f3\u4ea6\u65e0\u59a8\u3002\u51af\u745b\u89c1\u5510\u6653\u6f9c\u4e45\u4e45\u4e0d\u8bed\uff0c\u53c8\u8ffd\u95ee\u9053\uff1a\u201c\u5510\u53d4\u53d4\uff0c \u81ea\u4ece\u6211\u77e5\u9053\u8fd9\u4e16\u754c\u4e0a\u6709\u4e00\u4e2a\u4eba\u548c\u6211\u6781\u4e3a\u76f8\u4f3c\u4e4b\u540e\uff0c\u6211\u7684\u5fc3\u603b\u662f\u4e0d\u5b89\u3002\u4e0d\u8bba\u5979\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u6211 \u603b\u8981\u63a2\u51fa\u5979\u7684\u4e0b\u843d\u3002\u5510\u53d4\u53d4\uff0c\u4f60\u5148\u628a\u4f60\u77e5\u9053\u7684\u544a\u8bc9\u6211\uff0c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u8fdf\u7591\u4e0d\u51b3\uff0c\u89c1\u5979\u7126\u6025\u4e4b\u60c5\uff0c\u73b0\u4e8e\u8f9e\u8272\uff0c\u5fc3\u9053\uff1a\u201c\u6682\u65f6\u8fd8\u662f\u4e0d\u8981\u544a\u8bc9\u5979\u597d\uff0c\u5f85\u5979\u518d \u957f\u5927\u4e00\u4e24\u5e74\uff0c\u6c5f\u6e56\u9605\u5386\u66f4\u591a\u7684\u65f6\u5019\uff0c\u90a3\u65f6\u518d\u8bf4\u4e5f\u4e0d\u8fdf\u3002\u201d\u56e0\u6b64\u6b32\u8bf4\u8fd8\u4f11\uff0c\u52c9\u5f3a\u7684\u7b11\u4e86\u4e00\u7b11\u3002 \u3000\u3000\u51af\u745b\u6025\u9053\uff1a\u201c\u5510\u53d4\u53d4\uff0c\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5510\u6653\u6f9c\u7b11\u9053\uff1a\u201c\u4f60\u7684\u6025\u6027\u5b50\u8fd8\u6ca1\u6539\u60bc\u3002\u6211\u4e5f \u4e0d\u77e5\u9053\u90a3\u4e2a\u7433\u59d1\u5a18\u662f\u4e0d\u662f\u4f60\u7684\u59d0\u59b9\u3002\u65e2\u7136\u8fd9\u6837\u76f8\u4f3c\uff0c\u662f\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u65e2\u7136\u5979\u9003\u8d70\u672a\u4e45\uff0c\u54b1\u4eec\u5c31 \u5728\u9644\u8fd1\u627e\u4e00\u627e\u5979\u3002\u201d\u51af\u745b\u597d\u751f\u5931\u671b\uff0c\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u4e5f\u4e0d\u77e5\u9053\u6211\u7684\u6765\u5386\u5417\uff1f\u201d\u5510\u6653\u6f9c\u542b\u7cca\u5e94 \u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5c06\u6765\u603b\u6709\u6c34\u843d\u77f3\u51fa\u4e4b\u65e5\uff0c\u4f60\u653e\u5fc3\u597d\u4e86\u3002\u201d \u3000\u3000\u9648\u5fb7\u6cf0\u5728\u9644\u8fd1\u7b49\u5019\uff0c\u89c1\u5510\u6653\u6f9c\u5e26\u4e86\u4e00\u4e2a\u5973\u5b69\u5b50\u540c\u6765\uff0c\u5927\u559c\u76f8\u4f1a\u3002\u5510\u6653\u6f9c\u9053\uff1a\u201c\u8fd9\u5c0f\u59d1\u5a18 \u662f\u6211\u7684\u4f84\u5973\uff0c\u4f46\u53c8\u662f\u6211\u7684\u5e08\u59b9\uff0c\u8fd8\u6709\u4e00\u4e2a\u76f8\u8c8c\u548c\u5979\u6781\u4f3c\u7684\u59d1\u5a18\uff0c\u521a\u4ece\u5e74\u5bb6\u9003\u51fa\uff0c\u54b1\u4eec\u5728\u9644\u8fd1 \u5730\u65b9\u518d\u627e\u4e00\u627e\u3002\u201d \u3000\u3000\u8fd9\u4e00\u627e\u5c31\u627e\u4e86\u4e09\u5929\uff0c\u4e09\u4eba\u628a\u9648\u7559\u901a\u5f80\u90bb\u53bf\u7684\u51e0\u6761\u5927\u8def\u90fd\u8e0f\u904d\u4e86\uff0c\u5140\u662f\u5f97\u4e0d\u5230\u534a\u70b9\u6d88\u606f\u3002 \u9648\u5fb7\u6cf0\u9053\uff1a\u201c\u6211\u529d\u4f60\u8fd8\u662f\u56de\u5230\u4f60\u4e08\u4eba\u7684\u5bb6\u4e2d\u53bb\u5427\u3002\u4ed6\u4eba\u9762\u6781\u719f\uff0c\u6258\u4ed6\u6253\u542c\uff0c\u90a3\u8981\u6bd4\u54b1\u4eec\u778e\u627e \u53ef\u5f3a\u5f97\u591a\uff01\u201d \u3000\u3000\u51af\u745b\u5657\u55e4\u4e00\u7b11\uff0c\u5c0f\u6307\u5934\u5728\u8138\u4e0a\u4e00\u522e\uff0c\u7f9e\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5510\u53d4\u53d4\uff0c\u4f60\u51e0\u65f6\u8ba2\u4e86\u4eb2\u4e5f\u4e0d\u544a\u8bc9\u6211 \u77e5\u9053\u3002\u5a76\u5a76\u59d3\u4ec0\u4e48\uff1f\u4e00\u5b9a\u662f\u53c8\u6f02\u4eae\u53c8\u4f1a\u6b66\u827a\u7684\u5973\u82f1\u96c4\u4e86\uff1f\u201d\u5510\u6653\u6f9c\u9762\u4e0a\u4e00\u7ea2\uff0c\u8fd9\u95e8\u4eb2\u4e8b\u4ed6\u5b9e \u5728\u5f88\u4e0d\u613f\u610f\uff0c\u542c\u4eba\u63d0\u8d77\u4e5f\u4e0d\u8212\u670d\u3002 \u3000\u3000\u9648\u5f97\u6cf0\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u770b\u4f60\u8fd9\u4e2a\u53d4\u53d4\uff0c\u8fd8\u50cf\u5c0f\u5b69\u5b50\u4e00\u6837\uff0c\u63d0\u8d77\u65b0\u5a18\u5b50\u5c31\u8138\u7ea2\u3002\u4ed6\u4e0d\u544a\u8bc9 \u4f60\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u94c1\u638c\u795e\u5f39\u6768\u4ef2\u82f1\u7684\u540d\u5b57\u4f60\u542c\u8bf4\u8fc7\u6ca1\u6709\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u6211\u5728\u8def\u4e0a\u66fe\u542c\u4eba\u8bf4\u8fc7\u3002 \u8bf4\u662f\u5357\u6709\u7518\u51e4\u6c60\uff0c\u5317\u6709\u6768\u4ef2\u82f1\u3002\u5357\u7518\u5317\u6768\uff0c\u90fd\u662f\u9886\u8896\u6b66\u6797\u7684\u4eba\u7269\u3002\u201d\u9648\u5fb7\u6cf0\u9053\uff1a\u201c\u5bf9\u5475\uff0c\u4f60 \u53d4\u53d4\u7684\u65b0\u5a18\u5b50\u5c31\u662f\u6768\u4ef2\u82f1\u7684\u5973\u513f\u3002\u8fd9\u6837\u7684\u597d\u4eb2\u4e8b\u4ed6\u90fd\u4e0d\u80af\u8bf4\uff0c\u771f\u8be5\u7f5a\u3002\u745b\u59d1\u5a18\uff0c\u4f60\u548c\u4ed6\u540c\u5230 \u6768\u5bb6\uff0c\u53ef\u5f97\u597d\u597d\u5df4\u7ed3\u4f60\u8fd9\u672a\u6765\u5a76\u5a76\u3002\u201d\u51af\u745b\u7b11\u9053\uff1a\u201c\u5979\u662f\u6211\u7684\u5a76\u5a76\uff0c\u6211\u5f53\u7136\u8be5\u5c0a\u656c\u5979\u3002\u4f46\u4e5f \u4e0d\u5fc5\u7279\u522b\u5df4\u7ed3\u554a\uff01\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u522b\u542c\u8fd9\u9648\u4f2f\u4f2f\u7684\u80e1\u8bf4\u3002\u201d\u9648\u5fb7\u6cf0\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u53d4\u53d4 \u6700\u6015\u5979\uff0c\u4f60\u4e0d\u5df4\u7ed3\u5979\uff0c\u90a3\u600e\u4e48\u884c\u554a\uff01\u6211\u5077\u5077\u544a\u8bc9\u4f60\uff0c\u4f60\u7684\u5a76\u5a76\u813e\u6c14\u53ef\u4e0d\u5927\u597d\uff0c\u4f46\u53ea\u8981\u4f60\u6653\u5f97 \u5949\u627f\u5979\uff0c\u90a3\u4e48\u5979\u4e5f\u4e00\u5b9a\u5f88\u75bc\u4f60\u3002\u201d\u9648\u5fb7\u6cf0\u4e3a\u4eba\u559c\u6b22\u8bf4\u7b11\uff0c\u5374\u5f88\u723d\u76f4\uff1b\u6768\u67f3\u9752\u7684\u574f\u813e\u6c14\u540d\u95fb\u6c5f \u6e56\uff0c\u9648\u5fb7\u6cf0\u751a\u4e3a\u6b22\u559c\u51af\u745b\uff0c\u6015\u5979\u5c06\u6765\u548c\u6768\u67f3\u9752\u76f8\u5904\u4e0d\u597d\uff0c\u6240\u4ee5\u9884\u5148\u5c06\u6768\u67f3\u9752\u7684\u813e\u6c14\u544a\u8bc9\u5979 \u542c\uff0c\u6559\u5979\u5e94\u4ed8\u3002\u4ed6\u662f\u5510\u6653\u6f9c\u7684\u5144\u957f\u4e4b\u8f88\uff0c\u76f4\u8a00\u65e0\u9690\uff0c\u4e5f\u4e0d\u6015\u5510\u6653\u6f9c\u602a\u4ed6\u3002 \u3000\u3000\u51af\u745b\u5927\u7b11\u9053\uff1a\u201c\u9648\u4f2f\u4f2f\uff0c\u6211\u4e0d\u4fe1\u3002\u201d\u9648\u5fb7\u6cf0\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\uff1f\u4f60\u4e0d\u4fe1\u4f60\u95ee\u4f60\u53d4\u53d4\u3002\u201d\u51af\u745b \u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u771f\u7684\u5f88\u6015\u5a76\u5a76\u5417\uff1f\u201d\u5510\u6653\u6f9c\u9762\u7ea2\u76f4\u900f\u8033\u6839\uff0c\u8fde\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u80e1\u8bf4\u3002\u9648\u4f2f\u4f2f\u4e3a \u8001\u4e0d\u5c0a\uff0c\u4f60\u522b\u4fe1\u4ed6\u7684\u8bdd\u3002\u201d \u3000\u3000\u51af\u745b\u89c1\u9648\u5fb7\u6cf0\u6001\u5ea6\u4f3c\u9887\u8ba4\u771f\uff0c\u5c06\u4fe1\u5c06\u7591\uff0c\u5fc3\u9053\uff1a\u201c\u5510\u53d4\u53d4\u4eba\u5f88\u7eaf\u539a\uff0c\u82e5\u7136\u771f\u4e2a\u5a36\u4e86\u4e2a\u96cc \u8001\u864e\uff0c\u90a3\u53ef\u662f\u4ee4\u4eba\u4e3a\u4ed6\u53eb\u5c48\u3002\u54c8\uff0c\u6211\u770b\u6ca1\u6709\u8fd9\u4e2a\u9053\u7406\u3002\u82e5\u7136\u5a76\u5a76\u813e\u6c14\u4e0d\u597d\uff0c\u5510\u53d4\u53d4\u53c8\u600e\u80af\u8981 \u5979\uff1f\u201d\u51af\u745b\u5b69\u5b50\u4e4b\u6c14\u672a\u6cef\uff0c\u867d\u7136\u4e0d\u4fe1\uff0c\u4e5f\u62ff\u5510\u6653\u6f9c\u53d6\u7b11\uff0c\u4e00\u8def\u4e0a\u5f04\u5f97\u5510\u6653\u6f9c\u751a\u4e3a\u4e0d\u597d\u610f\u601d\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u6708\uff0c\u5510\u6653\u6f9c\u7b49\u4e09\u4eba\u4ece\u9648\u7559\u5317\u4e0a\u5546\u90b1\uff0c\u8fdb\u5165\u5c71\u4e1c\u5883\u5185\uff0c\u5230\u4e86\u5b9a\u9676\uff0c\u9648\u5fb7\u6cf0\u9053\uff1a\u201c\u9001 \u541b\u5343\u91cc\uff0c\u7ec8\u987b\u4e00\u522b\u3002\u6b64\u5730\u79bb\u4f60\u5cb3\u5bb6\u4e0d\u8fc7\u6570\u5929\u8def\u7a0b\uff0c\u4f60\u7684\u719f\u4eba\u751a\u591a\uff0c\u6599\u65e0\u610f\u5916\u53d1\u751f\uff0c\u5373\u7b97\u6709 \u4e8b\uff0c\u4e5f\u6709\u4eba\u7167\u5e94\uff0c\u6055\u6211\u4e0d\u8fdc\u9001\u4e86\u3002\u201d\u6298\u5411\u5357\u65b9\uff0c\u81ea\u53bb\u627e\u5bfb\u7518\u51e4\u6c60\u7b49\u6c5f\u5357\u516d\u4fa0\u3002 \u3000\u3000\u5510\u6653\u6f9c\u548c\u51af\u745b\u4e00\u8def\u540c\u884c\uff0c\u672a\u5230\u6768\u5bb6\uff0c\u5df2\u6709\u4eba\u5148\u62a5\u4fe1\u7ed9\u6768\u4ef2\u82f1\u77e5\u9053\u3002\u6768\u4ef2\u82f1\u8fce\u4e86\u51fa\u6765\uff0c\u5510 \u6653\u6f9c\u9053\uff1a\u201c\u9752\u59b9\u59b9\u5462\uff1f\u201d\u4ed6\u5012\u4e0d\u662f\u60e6\u8bb0\u6768\u67f3\u9752\uff0c\u800c\u662f\u4e0d\u89c1\u5979\u51fa\u6765\uff0c\u751a\u4e3a\u5947\u602a\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u77a7\u4e86\u51af\u745b\u4e00\u773c\uff0c\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u54b1\u4eec\u4e0d\u662f\u5728\u9099\u5c71\u4f1a\u8fc7\u5417\uff0c\u59d1\u5a18\uff0c\u4f60\u7684\u5251\u4f7f\u5f97\u771f \u597d\uff01\u201d\u51af\u745b\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u6768\u8001\u524d\u8f88\uff0c\u602a\u4e0d\u5f97\u4f60\u7684\u5f39\u5f13\u6253\u5f97\u8fd9\u6837\u597d\uff0c\u90a3\u5929\u4e0d\u662f\u4f60\u8001\u624b\u4e0b\u7559 \u7cbe\uff0c\u6211\u7684\u5b9d\u5251\u90fd\u51e0\u4e4e\u7ed9\u4f60\u6253\u5d29\u3002\u201d\u5510\u6653\u6f9c\u5947\u9053\uff1a\u201c\u4f60\u4eec\u7adf\u5728\u9099\u5c71\u4f1a\u8fc7\u4e48\uff1f\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u8d24 \u5a7f\uff0c\u4f60\u8fc7\u6765\uff0c\u6211\u6709\u8bdd\u95ee\u4f60\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u4e08\u4eba\u4eca\u65e5\u5982\u6b64\u4e0d\u8fd1\u4eba\u60c5\u3002\u4ed6\u5e73\u65e5\u6781\u4e3a\u597d\u5ba2\uff0c\u4e3a\u4f55\u89c1\u4e86\u6211\u7684\u4f84\u5973\uff0c\u53cd \u800c\u4e0d\u60a6\u3002\u521a\u8c08\u5f97\u51e0\u53e5\uff0c\u5c31\u8981\u6487\u5f00\u5979\u3002\u5e78\u800c\u5979\u4e0d\u662f\u51af\u7433\uff0c\u8981\u4e0d\u7136\u5b9a\u95f9\u6027\u5b50\u3002\u201d \u3000\u3000\u51af\u745b\u4e5f\u77a7\u51fa\u4e86\u51e0\u5206\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u5317\u4e94\u7701\u9f0e\u9f0e\u6709\u540d\u7684\u6b66\u6797\u9886\u8896\uff0c\u6c14\u91cf\u4e5f\u8fd9\u6837\u72ed\u7a84\uff1f\u90a3\u5929 \u548c\u6211\u83ab\u540d\u5176\u5999\u7684\u6253\u4e86\u4e00\u9635\uff0c\u5c31\u6000\u6068\u5728\u5fc3\u4e86\u3002\u201d\u4e0a\u524d\u62e2\u8896\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u5510\u53d4\u53d4\uff0c\u6768\u8001\u524d\u8f88\uff0c \u6211\u4e0d\u6253\u6405\u4f60\u4eec\u4e86\u3002\u201d\u6768\u4ef2\u82f1\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\uff0c\u4f60\u522b\u591a\u5fc3\u3002\u4f60\u6765\u4e86\uff0c\u6b63\u597d\u548c\u4f60\u7684\u59d1\u59d1 \u4f5c\u4f34\u3002\u201d\u53eb\u4e00\u4e2a\u4e2b\u73af\u5e26\u5979\u4eba\u95e8\uff0e\u81ea\u5df1\u5219\u643a\u7740\u5510\u6653\u6f9c\u7684\u624b\uff0c\u5728\u95e8\u5916\u7684\u67f3\u6797\u8c08\u8bdd\u3002 \u3000\u3000\u5510\u6653\u6f9c\u6ee1\u8179\u72d0\u7591\uff0c\u53ea\u542c\u5f97\u6768\u4ef2\u82f1\u95ee\u9053\uff1a\u201c\u8fd9\u4e2a\u59d1\u5a18\u600e\u4e48\u4f1a\u662f\u4f60\u7684\u4f84\u5973\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a \u201c\u6211\u4e0d\u662f\u5bf9\u4f60\u8001\u8bf4\u8fc7\u4e48\uff1f\u6211\u7684\u5e08\u5085\u6536\u7559\u6709\u4e00\u4e2a\u5b64\u5973\uff0c\u8fd9\u5b64\u5973\u5c31\u662f\u5979\u3002\u6211\u4eec\u5728\u5929\u5c71\u4e4b\u65f6\uff0c\u4e00\u5411 \u4ee5\u53d4\u4f84\u76f8\u79f0\u3002\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5979\u65e2\u7136\u662f\u6613\u7956\u5a46\u629a\u517b\u5927\u7684\u5173\u95e8\u5f1f\u5b50\uff0c\u5c31\u5e94\u8be5\u662f\u4e2a\u660e \u7406\u4e4b\u4eba\uff0c\u4e3a\u4f55\u5374\u4e0e\u6c5f\u5357\u4e03\u4fa0\u4f5c\u5bf9\uff1f\u201d\u5510\u6653\u6f9c\u5947\u9053\uff1a\u201c\u5979\u600e\u4e48\u4f1a\u548c\u6c5f\u5357\u4e03\u4fa0\u4f5c\u5bf9\uff1f\u201d \u3000\u3000\u6768\u4ef2\u82f1\u5c06\u90a3\u65e5\u5728\u9099\u5c71\u4e4b\u4e0a\uff0c\u51af\u7433\u628a\u674e\u6e90\u548c\u8def\u6c11\u77bb\u6740\u5f97\u72fc\u72c8\u8d25\u9003\u4e4b\u4e8b\u8bf4\u4e86\u3002\u53c8\u9053\uff1a\u201c\u674e\u6e90 \u5728\u8def\u4e0a\u4e2d\u4e86\u5979\u7684\u98de\u5200\uff0c\u51e0\u4e4e\u6b8b\u5e9f\u3002\u4e3a\u4ec0\u4e48\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u8fd9\u6837\u6b8b\u5fcd\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u800c\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u5b9a\u662f\u8bef\u4f1a\u4e86\u3002\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u8bef\u4f1a\u5462\uff1f\u201d\u5510 \u6653\u6f9c\u9053\uff1a\u201c\u56e0\u4e3a\u8fd8\u6709\u4e00\u4e2a\u548c\u5979\u6781\u4e3a\u76f8\u4f3c\u7684\u59d1\u5a18\u3002\u674e\u6e90\u5927\u54e5\u78b0\u7740\u7684\u60f3\u662f\u53e6\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u6768\u4ef2\u82f1\u5c06\u4fe1\u5c06\u7591\uff0c\u5ffd\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u96be\u9053\u6b3a\u8d1f\u9752\u513f\u7684\u4e5f\u662f\u53e6\u4e00\u4e2a\u4eba\u5417\uff1f\u201d\u6768\u67f3\u9752\u90a3\u65e5\u88ab\u51af \u7433\u620f\u5f04\uff0c\u8fde\u5934\u4e0a\u7684\u7389\u7c2a\u4e5f\u7ed9\u62e8\u53bb\uff0c\u56de\u5bb6\u4e4b\u540e\uff0c\u6c14\u5f97\u4e0d\u5f97\u4e86\uff0c\u51e0\u6b21\u592e\u7236\u4eb2\u7ed9\u5979\u62a5\u4ec7\uff0c\u6768\u4ef2\u82f1\u77e5 \u9053\u5bf9\u65b9\u662f\u4e2a\u5c0f\u59d1\u5a18\u540e\uff0c\u628a\u5973\u513f\u9a82\u4e86\u56de\u53bb\u3002\u4f46\u540e\u6765\u542c\u5f97\u5973\u513f\u63cf\u8ff0\u51af\u7433\u989c\u5bb9\uff0c\u548c\u81ea\u5df1\u5728\u9099\u5c71\u9047\u7684 \u90a3\u4e2a\u5c0f\u59d1\u5a18\u4f3c\u662f\u4e00\u4eba\u3002\u5fc3\u4e2d\u6781\u4e0d\u8212\u670d\u3002\u82e5\u975e\u5e74\u8001\uff0c\u771f\u60f3\u4eb2\u81ea\u51fa\u9a6c\uff0c\u6253\u542c\u5979\u7684\u6765\u5386\u3002 \u3000\u3000\u5510\u6653\u6f9c\u542c\u4e86\u4e08\u4eba\u7684\u8bdd\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4e8b\u4e0d\u80fd\u4e0d\u8bf4\u4e86\u3002\u201d\u5f53\u4e0b\u5c06\u51af\u745b\u51af\u7433\u539f\u662f\u5b6a\u751f\u59d0\u59b9\uff0c\u7236 \u4eb2\u88ab\u8840\u6ef4\u5b50\u6240\u6740\uff0c\u6bcd\u4eb2\u88ab\u64d2\u5165\u7687\u5e9c\uff0c\u540e\u6765\u53c8\u9003\u8d70\u4e86\uff0c\u73b0\u5728\u5c1a\u672a\u77e5\u4e0b\u843d\uff0c\u7b49\u7b49\u4e8b\u60c5\u90fd\u8be6\u7ec6\u8bf4\u4e86\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u542c\u5f97\u6f78\u7136\u6cea\u4e0b\uff0c\u9053\uff1a\u201c\u6211\u51e0\u4e4e\u9519\u602a\u4e86\u8fd9\u4e2a\u5b64\u513f\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5979\u4eec\u8eab\u8d1f\u8840\u6d77\u6df1\u4ec7\uff0c\u53c8\u662f\u5e74\u7fb9\u5c27\u548c\u5b98\u4e2d\u536b\u58eb\u6240\u8981\u641c\u6355\u7684\u4eba\u3002\u5e08\u5085\uff0c\u4f60\u4e0d\u8981 \u8bf4\u7ed9\u9752\u59b9\u77e5\u9053\u3002\u201d\u6768\u4ef2\u82f1\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u77e5\u9053\u9752\u513f\u7684\u53e3\u4e0d\u7262\uff0c\u8fde\u4f60\u7684\u8eab\u4e16\u6211\u4e5f\u4e0d\u6562\u544a\u8bc9\u5979 \u5462\u3002\u8d24\u5a7f\uff0c\u4f60\u653e\u5fc3\u3002\u201d \u3000\u3000\u6768\u4ef2\u82f1\u63e9\u4e86\u773c\u6cea\uff0c\u5ffd\u800c\u7b11\u9053\uff1a\u201c\u9752\u513f\u6c14\u4f60\u548c\u5979\u4e00\u540c\u56de\u6765\u5462\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6709\u4eba\u5148\u544a\u8bc9 \u5979\u4e86\uff1f\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u8fd9\u51e0\u53bf\u7684\u6b66\u6797\u670b\u53cb\uff0c\u90fd\u662f\u6211\u7684\u77e5\u4ea4\uff0c\u6628\u5929\u5df2\u6709\u591a\u4e8b\u7684\u4eba\uff0c\u4ece\u90b9\u53bf\u5230 \u6765\uff0c\u8bf4\u8d77\u4f60\u548c\u4e00\u4e2a\u5c0f\u59d1\u5a18\u4e00\u8def\u540c\u884c\u3002\u5979\u95ee\u6e05\u76f8\u8c8c\uff0c\u6c14\u5f97\u4e0d\u5f97\u4e86\u3002\u201d\u5510\u6653\u6f9c\u604d\u7136\u5927\u609f\uff0c\u7b11\u9053\uff1a \u201c\u6240\u4ee5\u5979\u751f\u4e86\u6c14\uff0c\u4e0d\u80af\u51fa\u6765\u89c1\u6211\u4e86\u3002\u201d \u3000\u3000\u6768\u4ef2\u82f1\u4e5f\u7b11\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u7684\u813e\u6c14\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u624d\u6539\uff0c\u8d24\u5a7f\uff0c\u4f60\u5c06\u6765\u53ef\u5f97\u591a\u5305\u6db5\u5979\u3002\u201d \u5510\u6653\u6f9c\u5c34\u5c2c\u4e00\u7b11\uff0c\u5ffd\u9053\uff1a\u201c\u5e08\u5085\uff0c\u90a3\u4e48\u4f60\u53eb\u745b\u513f\u5148\u8fdb\u5bb6\u95e8\uff0c\u53ea\u6015\u9752\u59b9\u4f1a\u548c\u5979\u52a8\u624b\u3002\u54b1\u4eec\u53ef\u5f97 \u56de\u53bb\u529d\u67b6\u3002\u201d\u6768\u4ef2\u82f1\u7b11\u9053\uff1a\u201c\u90a3\u4e0d\u81f3\u4e8e\u3002\u6211\u5df2\u518d\u4e09\u53ee\u5631\u4e86\u5979\uff0c\u53eb\u5979\u65e0\u8bba\u5982\u4f55\uff0c\u4e0d\u51c6\u52a8\u624b\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u5fc3\u4e2d\u7a0d\u5bbd\uff0c\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u5415\u56db\u5a18\u548c\u7518\u51e4\u6c60\u5f88\u60e6\u8bb0\u4f60\u3002\u5415\u56db\u5a18\u906d\u9022\u60e8\u53d8\uff0c\u8d76\u56de\u6d59 \u6c5f\uff0c\u6211\u672c\u60f3\u548c\u5979\u540c\u884c\u3002\u4f46\u5979\u8bf4\u4e0d\u613f\u56e0\u4e00\u4eba\u4e4b\u4e8b\uff0c\u52b3\u52a8\u5927\u5bb6\u3002\u800c\u4e14\u4eba\u53bb\u591a\u4e86\uff0c\u4e5f\u65e0\u6d4e\u4e8e\u4e8b\uff0c\u6240 \u4ee5\u5728\u9099\u5c71\u796d\u626b\u4e86\u72ec\u81c2\u795e\u5c3c\u7684\u5893\u540e\uff0c\u6211\u4eec\u5c31\u5206\u8def\u4e86\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u542c\u5f97\u4e08\u4eba\u63d0\u8d77\u4e86\u5415\u56db\u5a18\uff0c\u4e0d\u89c9\u9eef\u7136\u795e\u4f24\uff0c\u6768\u4ef2\u82f1\u89c1\u4ed6\u6ca1\u7cbe\u6253\u91c7\uff0c\u53ea\u9053\u4ed6\u65c5\u9014\u52b3 \u987f\uff0c\u9053\uff1a\u201c\u4f60\u957f\u9014\u8dcb\u6d89\uff0c\u4e5f\u8be5\u6b47\u6b47\u4e86\u3002\u201d\u7fc1\u5a7f\u4e24\u4eba\u6b65\u51fa\u67f3\u6797\u3002 \u3000\u3000\u51af\u745b\u95f7\u95f7\u4e0d\u4e50\uff0c\u968f\u4e2b\u73af\u8fdb\u4e86\u6768\u5bb6\uff0c\u65e0\u4eba\u62db\u5f85\uff0c\u66f4\u89c9\u4e0d\u5b89\u3002\u5750\u5b9a\u4e4b\u540e\uff0c\u95ee\u9053\uff1a\u201c\u6768\u59d1\u5a18\u4e0d \u5728\u5bb6\u4e48\uff1f\u201d\u4e2b\u73af\u9053\uff1a\u201c\u5728\u5bb6\u5462\uff01\u201d\u51af\u745b\u9053\uff1a\u201c\u5979\u4e0d\u8212\u670d\u4e48\uff1f\u201d\u4e2b\u73af\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u5979\u4eca\u5929 \u6574\u5929\u8eb2\u5728\u623f\u5185\u3002\u201d\u51af\u745b\u5fc3\u9053\uff1a\u201c\u5979\u662f\u6211\u7684\u5a76\u5a76\uff0c\u6211\u53c8\u5230\u5979\u5bb6\u4f5c\u5ba2\uff0c\u5e94\u8be5\u5148\u53bb\u62dc\u5019\u3002\u201d\u4fbf\u9053\uff1a \u201c\u70e6\u4f60\u5e26\u6211\u5230\u5979\u623f\u4e2d\u3002\u201d\u4e2b\u73af\u5fc3\u9053\uff1a\u5c0f\u59d0\u7684\u813e\u6c14\uff0c\u6211\u53ef\u4e0d\u6562\u62db\u60f9\u3002\u51af\u745b\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u7b49\u5f85\u4e2b \u73af\u5e26\u8def\u3002 \u3000\u3000\u4e2b\u73af\u65e0\u5948\uff0c\u5e26\u5979\u8d70\u5165\u540e\u5802\uff0c\u6307\u7740\u4e00\u6761\u51b7\u5df7\u9053\uff1a\u201c\u4e1c\u9996\u90a3\u4e00\u95f4\u623f\uff0c\u4fbf\u662f\u6211\u4eec\u5c0f\u59d0\u7684\u95fa\u623f\u3002 \u6211\u8fd8\u6709\u70b9\u4e8b\u60c5\uff0c\u6055\u6211\u4e0d\u966a\u4f60\u4e86\u3002\u201d \u3000\u3000\u51af\u745b\u5fc3\u9053\uff1a\u201c\u8fd9\u4e2b\u73af\u4e5f\u4e0d\u61c2\u793c\u8c8c\u3002\u771f\u662f\u95fb\u540d\u4e0d\u5982\u89c1\u9762\uff0c\u89c1\u9762\u80dc\u4f3c\u95fb\u540d\u3002\u6768\u4ef2\u82f1\u548c\u4ed6\u7684\u5bb6 \u4eba\uff0c\u600e\u4e48\u90fd\u662f\u8fd9\u4e2a\u6837\u5b50\uff1f\u201d\u5979\u5230\u5e95\u8fd8\u662f\u5c0f\u5b69\uff0c\u4e0d\u901a\u4e16\u6545\uff0c\u7a7f\u8fc7\u51b7\u5df7\uff0c\u63ed\u5f00\u95e8\u5e18\uff0c\u76f4\u5165\u6768\u67f3\u9752 \u7684\u623f\u95f4\u3002\u53ea\u89c1\u4e00\u4e2a\u5973\u5b50\uff0c\u5750\u5728\u5e8a\u4e0a\uff0c\u6c14\u9f13\u9f13\u7684\u5706\u7741\u53cc\u773c\uff0c\u770b\u7740\u81ea\u5df1\uff01 \u3000\u3000\u51af\u745b\u5413\u4e86\u4e00\u8df3\uff0c\u8d76\u5fd9\u65bd\u793c\uff0e\u53eb\u9053\uff1a\u201c\u5a76\u5a76\u3002\u201d\u6768\u67f3\u9752\u6012\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u5a76\u5a76\uff1f\u201d\u51af\u745b\u5fc3 \u9053\uff1a\u201c\u662f\u554a\uff0c\u5979\u548c\u5510\u53d4\u53d4\u8fd8\u672a\u6210\u5a5a\uff0c\u6240\u4ee5\u4e0d\u9ad8\u5174\u6211\u53eb\u5979\u5a76\u5a76\u3002\u201d\u6539\u53e3\u53eb\u5979\u201c\u59d1\u59d1\u3002\u201d\u6768\u67f3\u9752 \u53c8\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u4f60\u672c\u9886\u9ad8\u5f3a\uff0c\u6211\u90a3\u6709\u798f\u6c14\u6709\u4f60\u8fd9\u6837\u7684\u4f84\u5973\uff01\u201d \u3000\u3000\u51af\u745b\u6115\u5728\u5f53\u573a\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u8bdd\u554a\uff01\u813e\u6c14\u518d\u602a\u4e5f\u6ca1\u6709\u4e00\u89c1\u9762\u4fbf\u602a\u4eba\u7684\u9053\u7406\u3002\u54e6\uff0c\u73b0 \u5728\u662f\u590f\u79cb\u4e4b\u4ea4\uff0c\u5929\u65f6\u4e0d\u6b63\uff0c\u83ab\u975e\u5979\u4e2d\u4e86\u90aa\u4e86\uff1f\u201d\u6ef4\u6e9c\u6e9c\u7684\u773c\u73e0\u5728\u6768\u67f3\u9752\u9762\u626b\u6765\u626b\u53bb\u3002 \u3000\u3000\u6768\u67f3\u9752\u8d8a\u53d1\u6124\u6012\uff0c\u9053\uff1a\u201c\u662f\u8c01\u53eb\u4f60\u8fdb\u6765\u7684\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u6211\u548c\u5510\u53d4\u53d4\u540c\u6765\u7684\u3002\u201d\u6768\u67f3\u9752 \u66f4\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u6653\u6f9c\u5c82\u6709\u6b64\u7406\uff0c\u5e26\u4e86\u8fd9\u4e2a\u91ce\u4e2b\u5934\u56de\u6765\uff0c\u4ed6\u81ea\u5df1\u4e0d\u5148\u6765\u89c1\u6211\uff0c\u5374\u53eb\u5979\u6765\u6c14\u6211\u3002\u201d \u3000\u3000\u51af\u745b\u9053\uff1a\u201c\u59d1\u59d1\u4e0d\u8212\u670d\u4e48\uff1f\u623f\u4e2d\u95f7\u70ed\uff0c\u4e3a\u4f55\u4e0d\u51fa\u53bb\u6563\u6563\u5fc3\u5462\uff1f\u201d\u6768\u67f3\u9752\u4e00\u8dc3\u800c\u8d77\uff0c\u5728\u58c1 \u4e0a\u53d6\u4e0b\u5f39\u5f13\uff0c\u9053\uff1b\u201c\u5f88\u597d\uff0c\u6211\u5c31\u548c\u4f60\u5230\u5916\u9762\u6563\u5fc3\u53bb\u3002 \u3000\u3000\u51af\u745b\u867d\u89c9\u5979\u7684\u884c\u52a8\u5947\u602a\uff0c\u4ecd\u7136\u7b11\u9053\uff1a\u201c\u7ec3\u7ec3\u6b66\u8212\u6563\u7b4b\u9aa8\u4e5f\u597d\u3002\u6768\u516c\u516c\u4ee5\u94c1\u638c\u795e\u5f39\u5a01\u9707\u6cb3 \u6714\uff0c\u59d1\u59d1\u7684\u5f39\u5f13\u4e5f\u4e00\u5b9a\u6253\u5f97\u975e\u5e38\u4e4b\u597d\u4e86\u3002\u201d \u3000\u3000\u6768\u67f3\u9752\u54fc\u4e86\u4e00\u58f0\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u8fd8\u8bf4\u98ce\u51c9\u8bdd\u513f\u3002\u201d\u9762\u8272\u94c1\u9752\uff0c\u63ed\u5e18\u800c\u51fa\uff1b\u4e0d\u4e00\u523b\u5230\u4e86\u5c4b\u540e \u9762\u7684\u7ec3\u6b66\u573a\u4e2d\u3002 \u3000\u3000\u51af\u745b\u9053\uff1a\u201c\u59d1\u59d1\u7684\u5f39\u5f13\u600e\u6837\u6253\u6cd5\uff1f\u7ed9\u6211\u5f00\u5f00\u773c\u754c\u3002\u201d\u6768\u67f3\u9752\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u8d31 \u4eba\uff0c\u4f60\u522b\u7316\u72c2\uff0c\u4f60\u90a3\u5929\u4fa5\u5e78\u9003\u8fc7\uff0c\u5c31\u6562\u8f7b\u89d1\u6211\u6768\u5bb6\u7684\u795e\u5f39\u7edd\u6280\u4e86\u4e48\uff1f\u201d\u51af\u745b\u4e00\u6115\uff0c\u6c14\u5f80\u4e0a \u51b2\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u53eb\u4f60\u5f00\u5f00\u773c\u754c\uff01\u201d\u5f39\u5f13\u4e00\u66f3\uff0c\u75be\u4f3c\u6d41\u661f\uff0c\u55d6\u55d6\u55d6\uff0c\u4e0a\u4e2d \u4e0b\u4e09\u8def\u9f50\u5230\uff0c\u5168\u5954\u51af\u745b\u7684\u7a74\u9053\u6253\u6765\uff01 \u3000\u3000\u6768\u67f3\u9752\u90a3\u65e5\u53d7\u4e86\u51af\u7433\u7684\u6298\u8fb1\u4e4b\u540e\uff0c\u56de\u5bb6\u82e6\u7ec3\u5f39\u5f13\uff0c\u81ea\u4fe1\u5df2\u6709\u5341\u5206\u628a\u63e1\uff0c\u4e00\u52a8\u624b\u4fbf\u7528\u8fde\u73e0 \u6253\u6cd5\uff0c\u6beb\u4e0d\u7559\u60c5\u3002 \u3000\u3000\u51af\u745b\u7ea4\u8170\u4e00\u6446\uff0c\u56e2\u56e2\u4e00\u8f6c\uff0c\u6768\u67f3\u9752\u7684\u5f39\u4e38\u5168\u843d\u4e86\u7a7a\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4e14\u6162\u52a8\u624b\uff0c\u6211\u6709\u8bdd \u8bf4\uff01\u201d\u8fd9\u65f6\uff0c\u5979\u5df2\u60f3\u8d77\u53ef\u80fd\u53c8\u662f\u8bef\u4f1a\uff0c\u662f\u90a3\u4e2a\u4ec0\u4e48\u201c\u7433\u59d1\u5a18\u201d\u6240\u5e72\u7684\u4e8b\uff0c\u6768\u67f3\u9752\u7b97\u5230\u5979\u7684\u5934 \u4e0a\u6765\u4e86\u3002 \u3000\u3000\u6768\u67f3\u9752\u607c\u6012\u5f02\u5e38\uff0c\u6beb\u4e0d\u7406\u4f1a\uff0c\u5f39\u5f13\u518d\u66f3\uff0c\u8fd9\u4e00\u756a\u6765\u5f97\u66f4\u6025\uff0c\u6bcf\u4e09\u7c92\u5e03\u6210\u4e00\u4e2a\u54c1\u5b57\uff0c\u8fce\u9762 \u6253\u6765\uff0c\u51af\u745b\u4e5f\u7ed9\u5979\u5f04\u5f97\u6012\u4e86\u8d77\u6765\uff0c\u5c55\u5f00\u7a7a\u624b\u63a5\u6697\u5668\u7684\u529f\u592b\uff0c\u4f38\u624b\u4e00\u6293\uff0c\u5c06\u5148\u6765\u7684\u4e09\u7c92\u5f39\u5b50\u6293 \u5728\u624b\u5fc3\uff0c\u8fd8\u63b7\u8fc7\u53bb\uff0c\u567c\u567c\u556a\u556a\uff0c\u5c06\u6768\u67f3\u9752\u7684\u7b2c\u4e8c\u7ec4\u5f39\u4e38\u5168\u90e8\u78b0\u843d\uff0c\u5982\u662f\u8005\u8fb9\u6388\u8fb9\u53d1\uff0c\u77ac\u606f\u4e4b \u95f4\uff0c\u6768\u67f3\u9752\u7684\u534a\u888b\u94c1\u5f39\uff0c\u5df2\u5728\u7a7a\u4e2d\u5bf9\u649e\u7c89\u788e\u3002 \u3000\u3000\u6768\u67f3\u9752\u9a91\u864e\u96be\u4e0b\uff0c\u5140\u662f\u53d1\u4e2a\u4e0d\u4f53\uff0c\u6700\u540e\u4e00\u62db\uff0c\u7adf\u7528\u51fa\u201c\u6ee1\u5730\u82b1\u96e8\u201d\u7684\u6253\u6cd5\uff0c\u4e00\u5927\u628a\u4e00\u5927 \u628a\u7684\u8ff3\u5c04\u51fa\u53bb\u3002\u51af\u745b\u5fc3\u4e2d\u6c14\u9053\uff1a\u201c\u4e0d\u7ed9\u4f60\u70b9\u5389\u5bb3\uff0c\u4f60\u4e5f\u4e0d\u77e5\u8fdb\u9000\u3002\u201d\u811a\u5c16\u4e00\u70b9\uff0c\u8eab\u5f62\u51ed\u7a7a\u98de \u63a0\u8d77\u6765\uff0c\u771f\u7684\u8d5b\u4f3c\u5f39\u4e38\uff0c\u626c\u67f3\u9752\u5ffd\u89c1\u4e00\u56e2\u767d\u5f71\u8fce\u9762\u6251\u6765\uff0c\u63aa\u624b\u4e0d\u53ca\uff0c\u5f39\u5f13\u7adf\u7ed9\u51af\u745b\u4e00\u628a\u62a2 \u53bb\uff0c\u6298\u4e3a\u4e24\u6bb5\uff0c\u4e22\u5728\u5730\u4e0a\u3002 \u3000\u3000\u6768\u67f3\u9752\u53c8\u60ca\u53c8\u6012\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u626b\u654c\u4e2d\u76d8\uff0c\u83b2\u7fd8\u4e00\u8d77\uff0c\u53c8\u8e22\u819d\u76d6\u3002\u51af\u745b\u95fb\u5f97\u5916\u9762\u811a\u6b65\u4e4b \u58f0\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8eab\u5f62\u4e00\u4fa7\uff0c\u7528\u4e86\u4e2a\u201c\u71d5\u5b50\u659c\u98de\u201d\u4e4b\u52bf\uff0c\u8ba9\u5f00\u4e86\u6768\u67f3\u9752\u7684\u817f\uff0c\u5374\u907f\u4e0d\u8fc7\u5979\u7684\u94c1 \u638c\uff0c\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u6253\u5728\u51af\u745b\u80f8\u4e0a\u3002 \u3000\u3000\u6768\u67f3\u9752\u5f97\u610f\u5927\u7b11\uff0c\u5ffd\u89c1\u7236\u4eb2\u548c\u672a\u5a5a\u592b\u5a7f\u98de\u5954\u800c\u6765\u3002\u6768\u4ef2\u82f1\u9762\u8272\u94c1\u9752\uff0c\u6c14\u6025\u8d25\u574f\u7684\u9053\uff1a \u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u600e\u4e48\u4e0d\u542c\u6211\u7684\u8bdd\uff01\u201d\u5510\u6653\u6f9c\u5374\u8dd1\u53bb\u62c9\u51af\u745b\u7684\u624b\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u89c9\u5f97\u600e\u4e48\u6837\uff0c\u88ab\u6253 \u4f24\u54ea\u91cc\uff01\u6211\u7ed9\u4f60\u63a8\u8840\u8fc7\u5bab\u3002\u201d \u3000\u3000\u6768\u67f3\u9752\u6c14\u9053\uff1a\u201c\u7239\uff0c\u522b\u4eba\u627e\u4e0a\u95e8\u6765\uff0c\u6b3a\u8d1f\u4f60\u7684\u5973\u513f\uff0c\u4f60\u4e5f\u4e0d\u7406\uff0c\u5374\u53cd\u800c\u602a\u8d77\u6211\u672a\u3002\u6653 \u6f9c\uff0c\u8fc7\u6765\uff01\u8fc7\u6765\u5440\uff01\u54fc\uff0c\u4f60\u5728\u6211\u5bb6\u8fd9\u4e48\u591a\u5e74\uff0c\u5982\u4eca\u5374\u5403\u91cc\u6252\u5916\uff0c\u548c\u8fd9\u5c0f\u8d31\u4eba\u4e00\u9053\u6765\u6b3a\u8d1f\u6211 \u4e86\uff01\u201d\u6768\u4ef2\u82f1\u559d\u9053\uff1a\u201c\u95ed\u53e3\uff0c\u4f60\u518d\u80e1\u9a82\u6211\u5c31\u6253\u4f60\u8033\u5149\uff01\u201d \u3000\u3000\u51af\u745b\u4e00\u7b11\u8fc7\u6765\uff0c\u957f\u63d6\u8bf4\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60\u600e\u4e48\u4e00\u89c1\u9762\u5c31\u9a82\u6211\u6253\u6211\uff1f\u6211\u4e0d\u662f\u4ec0\u4e48\u5c0f\u8d31\u4eba\uff0c\u6211 \u662f\u5929\u5c71\u6613\u5973\u4fa0\u7684\u5f92\u5f1f\uff0c\u51e0\u65f6\u5192\u72af\u4f60\u4e86\uff1f\u201d\u6768\u4ef2\u82f1\u548c\u5510\u6653\u6f9c\u89c1\u51af\u745b\u9762\u8272\u5982\u5e38\uff0c\u4e1d\u6beb\u4e0d\u663e\u53d7\u4f24\u4e4b \u6001\uff0c\u653e\u4e0b\u4e86\u5fc3\uff0c\u6768\u4ef2\u82f1\u66f4\u662f\u5947\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u9752\u513f\u672c\u4e8b\u867d\u662f\u5bfb\u5e38\uff0c\u4f46\u5979\u5f97\u6211\u6240\u4f20\u7684\u94c1\u638c\u529f\u592b\uff0c \u8fd9\u4e00\u638c\u5c11\u8bf4\u4e5f\u6709\u4e09\u4e94\u767e\u65a4\u529b\u91cf\uff0c\u8fd9\u5c0f\u59d1\u5a18\u63a5\u4e86\u8fd9\u638c\uff0c\u82e5\u65e0\u5176\u4e8b\uff0c\u529f\u529b\u4e4b\u6df1\uff0c\u8fde\u6211\u4e5f\u672a\u5fc5\u80fd \u53ca\u3002\u201d \u3000\u3000\u6768\u67f3\u9752\u89c1\u51af\u745b\u53d7\u4e86\u8fd9\u638c\uff0c\u82e5\u65e0\u5176\u4e8b\uff0c\u4e5f\u89c9\u5fc3\u614c\uff0c\u5510\u6653\u6f9c\u9053\uff1a\u201c\u9752\u59b9\uff0c\u4f60\u8ba4\u9519\u4eba\u4e86\u3002\u201d\u6768 \u67f3\u9752\u77aa\u773c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u6211\u53c8\u4e0d\u662f\u5b69\u5b50\uff01\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u4f60\u6bd4\u5c0f\u5b69\u5b50\u8fd8\u80e1\u95f9\uff01\u4f60\u8ddf\u6211\u5b66\u4e86\u8fd9 \u4e48\u591a\u5e74\u6b66\u529f\uff0c\u6c5f\u6e56\u9605\u5386\u4e5f\u4e0d\u5c11\u4e86\u3002\u4f60\u5c31\u7b97\u770b\u4e0d\u51fa\u8fd9\u4f4d\u59d1\u5a18\u7684\u6b66\u529f\u95e8\u6237\uff0c\u4e5f\u8be5\u770b\u51fa\u5979\u7684\u624b\u6cd5\u4e0e \u4f60\u4ee5\u524d\u6240\u78b0\u5230\u7684\u4e0d\u540c\u3002\u201d\u6768\u67f3\u9752\u4e00\u60f3\uff1a\u201c\u51af\u745b\u7684\u6b66\u529f\u7cbe\u7eaf\uff0c\u679c\u7136\u5728\u90a3\u65e5\u78b0\u5230\u7684\u90a3\u5c0f\u59d1\u5a18\u4e4b \u4e0a\u3002\u201d\u5510\u6653\u6f9c\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u76f8\u8c8c\u76f8\u540c\u4e4b\u4eba\uff0c\u5728\u6240\u591a\u6709\u3002\u4e5f\u602a\u4e0d\u5f97\u9752\u59b9\u8ba4\u9519\u3002\u201d\u8fd9\u8bdd\u539f\u662f\u7ed9\u5979 \u5f00\u89e3\uff0c\u4e0d\u60f3\u6768\u67f3\u9752\u63a5\u8fde\u5403\u4e8f\uff0c\u8fd9\u53e3\u6c14\u54bd\u4e0d\u4e0b\u53bb\uff0c\u53c8\u7ed9\u7236\u4eb2\u8bc3\u8d23\uff0c\u7d22\u6027\u653e\u5201\u8bf4\u9053\uff1a\u201c\u5c31\u7b97\u6211\u8ba4 \u9519\u4e86\u4eba\uff0c\u5979\u4e5f\u4e0d\u8be5\u628a\u6211\u7684\u5f39\u5f13\u6298\u65ad\uff0c\u6211\u4eec\u6768\u5bb6\u4ee5\u94c1\u638c\u795e\u5f39\u540d\u95fb\u5929\u4e0b\uff0c\u5979\u6298\u65ad\u4e86\u6211\u4eec\u7684\u5f39\u5f13\uff0c \u5c31\u7b49\u4e8e\u628a\u9556\u5c40\u7684\u9556\u65d7\u6495\u4e86\uff0c\u7239\uff0c\u4f60\u53d7\u5f97\u4e86\u6211\u53d7\u4e0d\u4e86\uff0c\u6765\uff0c\u6765\uff0c\u54b1\u4eec\u518d\u6597\uff01\u201d \u3000\u3000\u6768\u4ef2\u82f1\u6c14\u5f97\u9762\u8272\u7d2b\u9ec4\uff0c\u4e00\u628a\u5c06\u5973\u513f\u62c9\u5f00\u3002\u51af\u745b\u9053\uff1a\u201c\u59d1\u59d1\u638c\u6cd5\u9ad8\u660e\uff0c\u6211\u5df2\u8f93\u62db\uff0c\u4f55\u5fc5\u518d \u6597\u3002\u6211\u4e3a\u4e86\u81ea\u536b\uff0c\u903c\u5f97\u6298\u65ad\u4f60\u7684\u5f39\u5f13\uff0c\u6211\u518d\u7ed9\u4f60\u8d54\u793c\u3002\u201d\u5176\u5b9e\u51af\u745b\u662f\u6545\u610f\u53d7\u5979\u4e00\u638c\uff0c\u597d\u8ba9\u5979 \u4e0b\u53f0\u7684\u3002\u51af\u745b\u8eab\u4e0a\uff0c\u7a7f\u6709\u949f\u4e07\u5802\u5728\u5979\u5468\u5c81\u4e4b\u65f6\u6240\u8d60\u7684\u91d1\u4e1d\u8f6f\u7532\uff0c\u53d7\u4e00\u4e24\u638c\u6ee1\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u65a5\u5973\u513f\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u8fd9\u5c0f\u59d1\u5a18\u6bd4\u4f60\u5e74\u7eaa\u5c0f\u8bb8\u591a\uff0c\u5374\u6bd4\u4f60\u61c2\u4e8b\u3002\u4f60\u4e0d\u5bb3\u81ca\uff0c\u6211\u4e5f\u5bb3 \u81ca\u3002\u5feb\u7ed9\u8fd9\u4f4d\u5c0f\u59d1\u5a18\u8d54\u7f6a\uff0c\u8981\u4e0d\u7136\u6211\u5c31\u4e0d\u8ba4\u4f60\u505a\u5973\u513f\uff01\u201d\u6768\u67f3\u9752\u89c1\u7236\u4eb2\u8116\u5b50\u6da8\u7ea2\uff0c\u80e1\u5b50\u7fc5 \u8d77\uff0c\u77e5\u4ed6\u662f\u771f\u7684\u53d1\u4e86\u813e\u6c14\uff0c\u8d8a\u53d1\u4e0b\u4e0d\u4e86\u53f0\u3002 \u3000\u3000\u5510\u6653\u6f9c\u4e00\u7b11\u89e3\u56f4\uff0c\u5de6\u624b\u62c9\u51af\u745b\uff0c\u53f3\u624b\u62c9\u6768\u67f3\u9752\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\u3002\u745b\u4f84\u5973\uff0c\u4f60\u77a7 \u4f60\u7684\u59d1\u59d1\u662f\u4e0d\u662f\u50cf\u4f60\u4e00\u6837\u5c0f\u5b69\u5b50\u6c14\uff1f\u4f60\u4eec\u5728\u4e00\u8d77\u73a9\uff0c\u4e0d\u6101\u6ca1\u4f34\u5566\uff01\u201d\u51af\u745b\u53c8\u53eb\u4e86\u58f0\u201c\u59d1 \u59d1\u201d\uff0c\u6768\u67f3\u9752\u53ea\u5f97\u5e94\u4e86\u4e00\u58f0\u3002\u6768\u4ef2\u82f1\u8fd9\u624d\u653e\u5bbd\u9762\u8272\u3002 \u3000\u3000\u51af\u745b\u6b63\u60f3\u8bf4\u8bdd\uff0c\u6768\u67f3\u9752\u4fa7\u8138\u4e00\u8fb9\uff0c\u6545\u610f\u907f\u5979\u773c\u5149\uff0c\u5510\u6653\u6f9c\u751a\u89c9\u4e0d\u5b89\uff0c\u53ea\u542c\u5f97\u6768\u67f3\u9752\u9053\uff1a \u201c\u6653\u6f9c\uff0c\u4f60\u6765\uff01\u201d\u4e0d\u7406\u51af\u745b\uff0c\u8ff3\u81ea\u628a\u5510\u6653\u6f9c\u62d6\u56de\u623f\u4e2d\uff0c\u5173\u8d77\u623f\u95e8\uff0c\u5927\u52a0\u76d8\u95ee\u3002 \u3000\u3000\u51af\u745b\u78b0\u4e86\u4e2a\u9489\uff0c\u76ee\u7779\u6768\u67f3\u9752\u771f\u5982\u96cc\u8001\u864e\u4e00\u822c\uff0c\u5c06\u5510\u6653\u6f9c\u62d6\u53bb\uff0c\u60f3\u8d77\u9648\u5fb7\u6cf0\u7684\u8bdd\uff0c\u4e0d\u89c9\u5657 \u55e4\u4e00\u7b11\u3002\u6768\u4ef2\u82f1\u6447\u6447\u5934\u9053\uff1a\u201c\u771f\u6ca1\u529e\u6cd5\u3002\u59d1\u5a18\uff0c\u53eb\u4f60\u89c1\u7b11\u4e86\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u6211\u4e0b\u5c71 \u4e4b\u540e\uff0c\u53eb\u4eba\u8bef\u4f1a\uff0c\u5df2\u4e0d\u6b62\u4e00\u6b21\u5566\u3002\u201d\u6768\u4ef2\u82f1\u6b49\u7136\u8bf4\u9053\uff1a\u201c\u745b\u59d1\u5a18\uff0c\u8bba\u4e16\u4fd7\u7684\u8f88\u4efd\uff0c\u4f60\u662f\u6211\u5b59 \u5973\u4e00\u8f88\uff1b\u8bba\u6b66\u6797\u4e2d\u7684\u8f88\u4efd\uff0c\u4f60\u6211\u5374\u662f\u540c\u8f88\u3002\u54b1\u4eec\u4e0d\u7406\u8fd9\u4e9b\uff0c\u4f60\u65e2\u5230\u4e86\u6211\u5bb6\uff0c\u54b1\u4eec\u5c31\u5982\u81ea\u5df1\u4eba \u4e00\u822c\u3002\u6211\u5973\u513f\u813e\u6c14\u4e0d\u597d\uff0c\u4f60\u4e0d\u8981\u653e\u5728\u5fc3\u4e0a\u3002\u6211\u5df2\u53eb\u4eba\u66ff\u4f60\u6536\u62fe\u597d\u623f\u95f4\uff0c\u4f60\u53bb\u6b47\u6b47\u5427\u3002\u201d\u51af\u745b \u9053\uff1a\u201c\u6768\u516c\u516c\uff0c\u4f60\u662f\u6211\u53d4\u53d4\u7684\u4e08\u4eba\uff0c\u600e\u4e48\u548c\u6211\u5ba2\u6c14\u8d77\u6765\u4e86\u3002\u6211\u600e\u4e48\u6562\u602a\u59d1\u59d1\u5462\u3002\u201d\u968f\u7740\u6768\u4ef2 \u82f1\u56de\u5230\u6b63\u5b85\uff0c\u9690\u9690\u542c\u5f97\u6768\u67f3\u9752\u8d23\u95ee\u5510\u6653\u6f9c\u7684\u58f0\u97f3\uff0c\u4e0d\u89c9\u751a\u4e3a\u66ff\u4ed6\u96be\u8fc7\u3002 \u3000\u3000\u662f\u591c\uff0c\u51af\u745b\u7ffb\u6765\u8986\u53bb\uff0c\u603b\u7761\u4e0d\u7740\u3002\u60f3\u8d77\u65e5\u95f4\u4e4b\u4e8b\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4e2a\u5a76\u5a76\u51f6\u5f97\u4e0d\u8fd1\u60c5\u7406\u3002\u6211 \u4f55\u5fc5\u5728\u8fd9\u91cc\u53d7\u5979\u7684\u6c14\uff1f\u201d\u6084\u6084\u6536\u62fe\u597d\u5305\u88b1\uff0c\u53c8\u60f3\uff1a\u201c\u4e0d\u8f9e\u800c\u884c\u4e5f\u4e0d\u5927\u597d\uff0c\u4f46\u82e5\u7136\u8f9e\u884c\uff0c\u6768\u516c \u516c\u5fc5\u7136\u633d\u7559\u3002\u6211\u53c8\u4e0d\u597d\u8bf4\u602a\u4ed6\u7684\u5973\u513f\uff0c\u4e0d\u5982\u6211\u53bb\u544a\u8bc9\u5510\u53d4\u53d4\u4e00\u58f0\uff0c\u53eb\u4ed6\u4ee3\u6211\u5411\u6768\u516c\u516c\u544a\u7f6a\u4e5f \u4fbf\u7f62\u4e86\u3002\u201d\u5979\u65e5\u95f4\u5df2\u77e5\u5510\u6653\u6f9c\u6240\u5bbf\u7684\u4e66\u623f\u5728\u90a3\u4e00\u8fb9\uff0c\u4e3a\u4e86\u907f\u514d\u60ca\u9192\u6768\u5bb6\u5bb6\u4eba\uff0c\u7d22\u6027\u98de\u8eab\u8e0f\u4e0a \u74e6\u9762\uff0c\u5bab\u5954\u5510\u6653\u6f9c\u7684\u4e66\u623f\u3002 \u3000\u3000\u4e66\u623f\u706f\u5149\u672a\u706d\uff0c\u623f\u4e2d\u6709\u4eba\u8c08\u8bdd\u3002\u5374\u662f\u5510\u6653\u6f9c\u548c\u6768\u4ef2\u82f1\u7684\u58f0\u97f3\u3002\u51af\u745b\u4f0f\u8033\u4e00\u542c\uff0c\u53ea\u542c\u5f97\u5510 \u6653\u6f9c\u9053\uff1a\u201c\u5e08\u5085\uff0c\u4e0d\u662f\u6211\u8981\u6094\u5a5a\uff0c\u5b9e\u662f\u6211\u6015\u803d\u8bef\u4e86\u9752\u59b9\u9752\u6625\u3002\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u6709\u4f55 \u96be\u8a00\u4e4b\u9690\uff1f\u4f60\u662f\u5acc\u5979\u813e\u6c14\u4e0d\u597d\uff0c\u8fd8\u662f\u522b\u6709\u539f\u56e0\uff0c\u5bf9\u6211\u76f4\u8bf4\u4e86\u5427\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u6027\u547d\u53ea\u80fd\u4fdd\u4e00\u5e74\uff0c\u82e5\u7136\u6210\u5a5a\uff0c\u5c82\u4e0d\u7d2f\u9752\u59b9\u5b88\u5be1\u3002\u6240\u4ee5\u4e0d\u5982\u65e9\u65e9\u5c06\u5a5a\u7ea6\u89e3 \u4e86\u3002\u8bf7\u5e08\u5085\u53e6\u9009\u82f1\u624d\u3002\u201d \u3000\u3000\u6768\u4ef2\u82f1\u6025\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u53d7\u4e86\u5185\u4f24\u5417\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u90a3\u662f\u4ec0 \u4e48\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u996e\u4e86\u7687\u5e1d\u7684\u6bd2\u9152\uff0c\u6bd2\u6027\u6f5c\u4f0f\u5728\u8840\u6db2\u4e4b\u4e2d\uff0c\u4e00\u5e74\u4e4b\u540e\u624d\u53d1\u3002\u5230\u65f6\u82e5\u4e0d\u5165 \u5bab\u6c42\u4ed6\u89e3\u836f\uff0c\u5341\u65e5\u4e4b\u540e\uff0c\u4fbf\u6bd2\u53d1\u8eab\u4ea1\u3002\u5e08\u5085\uff0c\u4f60\u77e5\u9053\u6211\u7684\u6027\u60c5\uff0c\u54b1\u4eec\u4fa0\u4e49\u4e2d\u4eba\uff0c\u5934\u53ef\u65ad\u800c\u5fd7 \u4e0d\u53ef\u8fb1\u3002\u6211\u5b81\u6559\u8eab\u6b7b\u540d\u706d\uff0c\u4e5f\u4e0d\u613f\u5411\u7687\u5e1d\u54c0\u6c42\uff01\u201d\u6768\u4ef2\u82f1\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6beb\u65e0\u6212\u5907\u4e4b \u5fc3\uff0c\u559d\u4e86\u4ed6\u7684\u6bd2\u9152\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u82e5\u7136\u4e0d\u559d\uff0c\u4ed6\u4e5f\u4e0d\u653e\u5fc3\uff0c\u8ba9\u6211\u628a\u745b\u4f84\u5973\u5e26\u51fa\u6765\u3002\u201d \u3000\u3000\u51af\u745b\u542c\u5230\u8fd9\u91cc\uff0c\u5fc3\u513f\u535c\u901a\u4e00\u8df3\uff0c\u51e0\u4e4e\u8981\u8dcc\u4e0b\u74e6\u9762\u3002\u6025\u5fd9\u5f3a\u6444\u5fc3\u795e\uff0c\u4f0f\u8033\u518d\u542c\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u53c8\u9053\uff1a\u201c\u96be\u9053\u9664\u4e86\u4ed6\u7684\u89e3\u836f\uff0c\u5c31\u522b\u65e0\u4ed6\u836f\u53ef\u89e3\u5417\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5929\u5c71\u7684\u78a7\u7075\u4e39 \u662f\u89e3\u6bd2\u7684\u5723\u836f\uff0c\u6211\u5c06\u5e08\u5085\u7ed9\u6211\u7684\u51e0\u7c92\uff0c\u5168\u90e8\u5403\u4e86\uff0c\u4ea6\u662f\u65e0\u6548\u3002\u4e0d\u77e5\u9053\u8fd9\u6bd2\u9152\u662f\u4ec0\u4e48\u5236\u70bc\u7684\u3002 \u8fd9\u6837\u5389\u5bb3\u3002\u5e73\u65f6\u4e0d\u89c9\u4ec0\u4e48\uff0c\u8fd0\u6c14\u4e4b\u540e\uff0c\u518d\u63a5\u4e39\u7530\uff0c\u4fbf\u89c9\u9690\u9690\u4f5c\u75db\u3002\u60f3\u6765\u90a3\u7687\u5e1d\u8bf4\u8bdd\uff0c\u7edd\u975e\u865a \u58f0\u606b\u5413\uff01\u201d\u505c\u4e86\u4e00\u505c\uff0c\u53c8\u9053\uff1a\u201c\u6211\u996e\u6bd2\u9152\u81f3\u4eca\uff0c\u5df1\u5c06\u4e00\u6708\uff0c\u660e\u5e74\u8749\u9e23\u8354\u719f\u4e4b\u65f6\uff0c\u4fbf\u662f\u6211\u7684\u6b7b \u671f\u5230\u4e86\u3002\u201d \u3000\u3000\u6768\u4ef2\u82f1\u556a\u7684\u4e00\u638c\u628a\u4e66\u6848\u6253\u5f97\u201c\u7830\u201d\u7136\u4f5c\u54cd\uff0c\u6012\u9053\uff1a\u201c\u597d\u72e0\u6bd2\u7684\u7687\u5e1d\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8 \u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u522b\u65e0\u89e3\u836f\u3002\u8d24\u5a7f\uff0c\u4f60\u5b89\u5fc3\u9759\u517b\uff0c\u6211\u6d3e\u4eba\u53bb\u66ff\u4f60\u8bbf\u95ee\u5929\u4e0b\u540d\u533b\uff0c\u5728\u4e00\u5e74\u4e4b\u5185\uff0c\u603b \u53ef\u4ee5\u8bbe\u6cd5\u66ff\u4f60\u6551\u6cbb\u3002\u201d\u6d3b\u867d\u5982\u6b64\uff0c\u5176\u5b9e\u5374\u662f\u6beb\u65e0\u628a\u63e1\u3002 \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u522b\u8d39\u5fc3\u5566\uff0c\u6211\u6c42\u4f60\u4e0d\u8981\u5c06\u6b64\u4e8b\u8bf4\u7ed9\u9752\u59b9\u548c\u745b\u4f84\u5973\u77e5\u9053\u3002\u514d\u5f97\u5979\u4eec \u4e3a\u6211\u62c5\u5fc3\u3002\u201d \u3000\u3000\u51af\u745b\u5fc3\u75db\u5982\u7ede\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u5510\u53d4\u53d4\u7adf\u4e3a\u6211\u800c\u559d\u4e86\u6bd2\u9152\uff0c\u6211\u5c82\u53ef\u820d\u4ed6\u800c\u53bb\u3002\u201d\u53c8\u60f3\u9053\uff1a \u201c\u6211\u542c\u5e08\u5085\u8bf4\uff0c\u5510\u53d4\u53d4\u5728\u5929\u5c71\u4e09\u5e74\uff0c\u867d\u7136\u5f97\u5979\u7684\u5251\u6cd5\u771f\u4f20\uff0c\u5bf9\u672c\u6d3e\u5185\u529f\u7684\u79d8\u5965\uff0c\u5c1a\u672a\u6df1\u6089\u3002 \u6240\u4ee5\u5510\u53d4\u53d4\u53ea\u662f\u5979\u6302\u540d\u5f1f\u5b50\uff0c\u800c\u6211\u8f88\u4efd\u867d\u5c0f\uff0c\u5374\u53cd\u662f\u5979\u8863\u94b5\u4f20\u4eba\u3002\u6211\u4f55\u4e0d\u5c06\u5185\u529f\u767e\u65e5\u7a8d\uff0c\u90fd \u4f20\u4e86\u7ed9\u4ed6\u3002\u82e5\u4ed6\u529f\u529b\u589e\u5f3a\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u62b5\u5fa1\u6bd2\u529b\u3002\u201d \u3000\u3000\u51af\u745b\u53cd\u8986\u601d\u91cf\uff0c\u51b3\u5b9a\u4e0d\u8d70\uff0c\u5f53\u4e0b\u65e0\u5fc3\u518d\u542c\uff0c\u53c8\u6084\u6084\u6e9c\u56de\u81ea\u5df1\u623f\u5185\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u51af\u745b\u548c\u6768\u67f3\u9752\u89c1\u9762\uff0c\u6768\u67f3\u9752\u5bbf\u6c14\u672a\u6d88\uff0c\u5bf9\u51af\u745b\u6de1\u6de1\u70b9\u5934\uff0c\u7231\u7406\u4e0d\u7406\u3002\u51af\u745b\u4e3a\u4e86\u53d4 \u53d4\uff0c\u5f3a\u81ea\u5fcd\u4f4f\u3002\u5bf9\u5979\u6bb7\u52e4\u62db\u547c\uff0c\u6768\u67f3\u9752\u5fc3\u9053\uff1a\u201c\u5514\uff0c\u4f60\u8fd9\u4e2a\u5c0f\u4e2b\u5934\u4e5f\u77e5\u5411\u6211\u8ba8\u597d\u4e86\u3002\u201d\u706b\u6c14 \u6e10\u6d88\uff0c\u53c8\u89c9\u5f97\u602a\u4e00\u4e2a\u2018\u5c0f\u5b69\u5b50\u2019\u4e5f\u4e0d\u597d\u610f\u601d\uff0c\u4fbf\u4e5f\u548c\u5979\u8bf4\u7b11\u3002 \u3000\u3000\u53ef\u662f\u65e9\u9910\u8fc7\u540e\uff0c\u6768\u67f3\u9752\u53c8\u751f\u6c14\u4e86\u3002\u51af\u745b\u8dd1\u8fdb\u5510\u6653\u6f9c\u623f\u4e2d\uff0c\u5173\u4e86\u623f\u95e8\uff0c\u5927\u534a\u5929\u90fd\u4e0d\u51fa\u6765\u3002 \u6768\u67f3\u9752\u53eb\u4e86\u4e09\u6b21\uff0c\u8981\u4ed6\u51fa\u6765\u966a\u5979\u73a9\uff0c\u5510\u6653\u6f9c\u6bcf\u6b21\u90fd\u8bf4\uff1a\u201c\u5c31\u6765\u5566\uff0c\u5c31\u6765\u5566\uff01\u201d\u5374\u603b\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u6768\u67f3\u9752\u6c14\u5f97\u5c06\u5ba2\u5385\u91cc\u7684\u4e00\u5bf9\u5927\u82b1\u74f6\u6454\u5f97\u7c89\u788e\uff0c\u9a82\u9053\uff1a\u201c\u5341\u516d\u4e03\u5c81\u7684\u59d1\u5a18\uff0c\u4e5f\u4e0d\u5c0f\u5566\uff0c\u4e0d \u662f\u4eb2\u53d4\u53d4\uff0c\u54fc\uff0c\u771f\u4e0d\u8981\u8138\uff0c\u8eb2\u5728\u7537\u4eba\u623f\u4e2d\u4e0d\u80af\u51fa\u6765\u3002\u201d\u6545\u610f\u9a82\u5f97\u8ba9\u51af\u745b\u542c\u89c1\u3002 \u3000\u3000\u51af\u745b\u5728\u623f\u4e2d\u542c\u5f97\u5979\u8fd9\u6837\u7684\u9a82\uff0c\u679c\u7136\u751f\u6c14\uff0c\u5510\u6653\u6f9c\u6025\u9053\uff1a\u201c\u745b\u4f84\u5973\uff0c\u5979\u8bf4\u8bdd\u4e0d\u77e5\u5206\u5bf8\uff0c\u4f60 \u522b\u751f\u6c14\u3002\u201d\u51af\u745b\u60f3\u8d77\u5185\u529f\u7a83\u8981\uff0c\u53d4\u53d4\u5c1a\u672a\u5b8c\u5168\u9886\u4f1a\uff0c\u773c\u6cea\u6ef4\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u90a3\u4e48\u665a\u4e0a \u6211\u6765\u770b\u4f60\u3002\u201d\u6253\u5f00\u623f\u95e8\uff0c\u6c14\u547c\u547c\u7684\u8dd1\u51fa\uff0c\u6768\u67f3\u9752\u89c1\u5979\u5c0f\u5634\u7d27\u7ef7\uff0c\u53cc\u773c\u5706\u7741\uff0c\u6015\u5979\u53d1\u4f5c\uff0c\u53cd\u800c \u4e0d\u6562\u8bf4\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u7b49\u6768\u67f3\u9752\u8fdb\u5165\u623f\u4e2d\uff0c\u9762\u5b54\u4e00\u6273\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fde\u6211\u7684\u4f84\u5973\u4e5f\u4e0d\u80fd\u5bb9\uff0c\u4f60\u8fd8\u6765\u89c1\u6211\u4f5c \u751a\uff1f\u201d\u6768\u67f3\u9752\u4e00\u6014\uff0c\u60f3\u4e0d\u5230\u5510\u6653\u6f9c\u7adf\u7136\u4e5f\u4f1a\u5411\u5979\u53d1\u6c14\u3002\u54ed\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8981\u4f84\u5973\u5c31\u4e0d\u8981\u59bb\u5b50 \u4e86\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u6012\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u4f60\u5f53\u6211\u4eec\u662f\u4f55\u7b49\u6837\u4eba\uff1f\u5979\u662f\u4e2a\u5b64\u5973\uff0c\u4f60\u8fd8\u8981\u6298\u78e8\u5979\u5417\uff1f\u6211\u544a\u8bc9\u7239 \u7239\u77e5\u9053\u3002\u4f60\u5bb9\u4e0d\u5f97\u6211\u4eec\uff0c\u6211\u4eec\u4eca\u5929\u4fbf\u8d70\uff01\u201d\u6768\u67f3\u9752\u867d\u7136\u9a84\u7eb5\u4efb\u6027\uff0c\u5fc3\u5730\u5012\u5e76\u4e0d\u574f\uff0c\u95fb\u8a00\u4e00 \u9707\uff0c\u54ed\u58f0\u987f\u6b62\u3002\u5510\u6653\u6f9c\u534a\u54c4\u534a\u9a97\uff0c\u8f6f\u786c\u517c\u65bd\uff0c\u5c06\u5979\u529d\u4f4f\u3002\u4ee5\u540e\u6768\u67f3\u9752\u679c\u7136\u4e0d\u6562\u5f53\u9762\u53d1\u51af\u745b\u7684 \u813e\u6c14\u4e86\u3002 \u3000\u3000\u51af\u745b\u767d\u5929\u4e5f\u4e0d\u6562\u5230\u5510\u6653\u6f9c\u623f\u95f4\u3002\u4ed7\u7740\u8f7b\u529f\u795e\u5999\uff0c\u6bcf\u665a\u4e09\u66f4\u4e4b\u540e\uff0c\u4fbf\u5077\u5077\u53bb\u548c\u5510\u6653\u6f9c\u76f8 \u4f1a\uff0c\u5c06\u5929\u5c71\u4e00\u6d3e\u7684\u7ec3\u795e\u7ec3\u6c14\u7ec3\u7cbe\u4e4b\u6cd5\uff0c\u7ec6\u5fc3\u4f20\u6388\u7ed9\u5510\u6653\u6f9c\uff0c\u6258\u8a00\u662f\u5e08\u5085\u8981\u5979\u4ee3\u6559\u7684\u3002\u5510\u6653\u6f9c \u4e5f\u60f3\u5230\u5185\u529f\u6cbb\u75c5\u8fd9\u70b9\uff0c\u7528\u529f\u751a\u52e4\u3002\u4f46\u4ed6\u5374\u5e76\u4e0d\u77e5\u9053\u51af\u745b\u5df2\u5c06\u4ed6\u4e0e\u6768\u4ef2\u82f1\u7684\u8bdd\u542c\u53bb\uff0c\u6559\u7684\u5b66\u7684 \u90fd\u6709\u6df1\u5fc3\uff0c\u5927\u5bb6\u90fd\u4e0d\u8bf4\u7a7f\u3002 \u3000\u3000\u5982\u662f\u8005\u8fc7\u4e86\u4e00\u6708\uff0c\u51af\u745b\u4e0e\u6768\u67f3\u9752\u76f8\u5b89\u65e0\u4e8b\uff0c\u5510\u6653\u6f9c\u5185\u529f\u9887\u6709\u8fdb\u5883\uff0c\u4e5f\u751a\u559c\u6b22\uff0c\u4e00\u65e5\u767d \u5929\uff0c\u5510\u6653\u6f9c\u60f3\u4e0e\u51af\u745b\u7814\u8ba8\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u7cbe\u5fae\u4e4b\u5904\uff0c\u4e00\u65e9\u4e0e\u5979\u5f80\u540e\u5c71\uff0c\u6768\u67f3\u9752\u56db\u89c5\u4e0d\u89c1\uff0c\u5728\u5bb6 \u4e2d\u6b63\u81ea\u751f\u6c14\uff0c\u5ffd\u95fb\u5f97\u5916\u9762\u6709\u62cd\u95e8\u4e4b\u58f0\u3002\u6768\u4ef2\u82f1\u4ea4\u6e38\u5e7f\u9614\uff0c\u65f6\u6709\u6c5f\u6e56\u4e0a\u7684\u5947\u4eba\u5f02\u571f\u76f8\u8bbf\uff0c\u6768\u67f3 \u9752\u5fc3\u60f3\uff1a\u201c\u4e0d\u77e5\u662f\u54ea\u4f4d\u5ba2\u4eba\u6765\u4e86\uff1f\u201d\u5077\u5077\u5230\u5385\u540e\u5c4f\u98ce\uff0c\u5411\u5916\u5f20\u671b\u3002\u53ea\u89c1\u7239\u7239\u5df2\u5019\u5728\u5ba2\u5385\uff0c\u4e09 \u4f4d\u5ba2\u4eba\uff0c\u4e00\u7537\u4e8c\u5973\uff0c\u5927\u6b65\u8d70\u4e0a\u53f0\u9636\u3002\u7537\u7684\u662f\u4e2a\u5149\u5934\uff0c\u5934\u53d1\u867d\u767d\uff0c\u9762\u8272\u5374\u662f\u7ea2\u6da6\u6709\u5149\uff0c\u4e24\u4e2a\u5973 \u7684\u4e00\u8001\u4e00\u5c11\uff0c\u8ddf\u5728\u540e\u9762\uff0c\u90a3\u4e2a\u5c11\u5987\u9762\u6709\u60b2\u6124\u4e4b\u5bb9\u3002\u4f3c\u4e4e\u662f\u5bfb\u4ec7\u6765\u7684\u4e00\u822c\u3002 \u3000\u3000\u53ea\u542c\u5f97\u6768\u4ef2\u82f1\u970d\u7136\u8d77\u7acb\uff0c\u6b22\u58f0\u8bf4\u9053\uff1a\u201c\u5510\u4e8c\u5148\u751f\uff0c\u4ec0\u4e48\u98ce\u628a\u4f60\u5439\u5230\u8fd9\u91cc\uff1f\u201d\u90a3\u8001\u5934\u9053\uff1a \u201c\u6211\u5e26\u5c0f\u5973\u6765\u7ed9\u4f60\u8001\u53e9\u5934\uff0c\u5411\u4f60\u6c42\u60c5\u6765\u4e86\u3002\u8d5b\u82b1\u4f60\u8fd8\u4e0d\u7ed9\u6768\u4f2f\u4f2f\u53e9\u5934\u4e48\uff1f\u201d\u90a3\u5c11\u5987\u201c\u54c7\u201d\u7684 \u4e00\u58f0\u54ed\u4e86\u8d77\u6765\uff0c\u679c\u7136\u8dea\u4e0b\u78d5\u5934\u3002\u6768\u67f3\u9752\u770b\u5f97\u6781\u4e3a\u7eb3\u7f55\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u4e8b\u554a\uff1f\u201d \u3000\u3000\u6768\u4ef2\u82f1\u66f4\u4e3a\u7eb3\u7f55\uff0c\u53c8\u4e0d\u597d\u4f38\u624b\u53bb\u6276\uff0c\u53ea\u5f97\u6b20\u8eab\u8fd8\u4e86\u534a\u793c\uff0c\u9053\uff1a\u201c\u6709\u8bdd\u597d\u8bf4\uff0c\u6709\u8bdd\u597d\u8bf4\uff01 \u96be\u9053\u6709\u4ec0\u4e48\u4eba\u8fd8\u6562\u6b3a\u8d1f\u4f60\u4eec\u5417\uff1f\u201d \u3000\u3000\u90a3\u8001\u5934\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u54ed\u54ed\u557c\u557c\u6709\u4ec0\u4e48\u7528\uff1f\u6709\u6768\u8001\u524d\u8f88\u7ed9\u4f60\u4e3b\u6301\u516c\u9053\uff0c\u4f60\u8fd8\u6015 \u4f60\u7684\u5927\u4ec7\u4e0d\u80fd\u62a5\u5417\uff1f\u201d \u3000\u3000\u6768\u4ef2\u82f1\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u5510\u4e8c\u5148\u751f\uff0c\u4f60\u4eec\u5343\u91cc\u9053\u9053\uff0c\u4ece\u56db\u5ddd\u5230\u6b64\uff0c\u4e3a\u7684\u662f\u8981\u6211\u66ff\u4f60\u4eec\u62a5 \u4ec7\u5417\uff1f\u6211\u5e74\u7eaa\u8001\u8fc8\uff0c\u5bf9\u6c5f\u6e56\u4e0a\u5bfb\u4ec7\u6bb4\u6597\u4e4b\u4e8b\uff0c\u5df2\u4e0d\u613f\u63d2\u624b\u5176\u95f4\u3002\u518d\u8bf4\u51ed\u4f60\u4eec\u7684\u672c\u9886\uff0c\u8fd8\u6015\u6709 \u4ec0\u4e48\u4ec7\u4e0d\u80fd\u62a5\u5462\uff1f\u201d \u3000\u3000\u968f\u540c\u6765\u7684\u90a3\u4e2a\u8001\u5987\uff0c\u5ffd\u7136\u4ece\u65c1\u63d2\u5634\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u4ec7\u4eba\u85cf\u5728\u4e00\u4e2a\u6709\u5927\u52bf\u529b\u7684\u4eba\u5bb6 \u91cc\uff0c\u4e0d\u7ecf\u8fc7\u4f60\u8001\u4eba\u5bb6\uff0c\u4ed6\u4eec\u4e0d\u6562\u53bb\u627e\u3002\u201d \u3000\u3000\u6768\u4ef2\u82f1\u5947\u9053\uff1a\u201c\u6709\u4eba\u6562\u4e0e\u4f60\u5bb6\u7ed3\u4ec7\uff1f\u8fd9\u4e8b\u5df2\u7ecf\u5947\u4e86\u3002\u5230\u5e95\u662f\u4ec0\u4e48\u4ec7\u6068\uff1f\u4ec7\u4eba\u662f\u8c01\uff1b\u4ed6\u53c8 \u9760\u8c01\u5305\u5e87\uff1f\u5510\u4e8c\u5148\u751f\uff0c\u4f60\u8bf4\u51fa\u6765\uff0c\u6211\u867d\u7136\u4e0d\u613f\u63d2\u624b\uff0c\u8fd9\u91cc\u7684\u6b66\u6797\u4eba\u7269\uff0c\u90fd\u662f\u670b\u53cb\uff0c\u6709\u4ec0\u4e48\u4e3a \u96be\u4e4b\u5904\uff0c\u4e5f\u597d\u5546\u91cf\u3002\u201d \u3000\u3000\u90a3\u8001\u5934\u773c\u775b\u4e00\u4eae\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4eec\u591a\u8c22\u6768\u8001\u82f1\u96c4\u4e86\u3002\u5c0f\u5973\u8981\u62a5\u7684\u662f\u6740\u592b\u4e4b\u5927\u4ec7\uff0c \u4ec7\u4eba\u5c31\u5728\u4f60\u8001\u5e9c\u4e0a\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u6674\u5929\u6765\u9739\u96f3\uff0c\u5927\u7978\u8d77\u8427\u5899\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e8c\u56de\u3000\u7bad\u53d1\u5f39\u98de\u3000\u5251\u5149\u60ca\u5de8\u5b7d\u3000\u821e\u4f11\u6b4c\u7f62\u3000\u6740\u6c14\u9690\u534e\u5802 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u4e8c\u56de \u7bad\u53d1\u5f39\u98de\u3000\u5251\u5149\u60ca\u5de8\u5b7d \u821e\u4f11\u6b4c\u7f62\u3000\u6740\u6c14\u9690\u534e\u5802 \u3000\u3000\u6768\u4ef2\u82f1\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4e8b\u771f\u771f\u5947\u4e86\uff01\u96be\u9053\u662f\u9752\u513f\u95ef\u4e0b\u7684\u7978\u4e48\uff1f\u201d\u60f3\u5230\u6b64\u5904\uff0c\u4e0d\u7981 \u5bd2\u610f\u76f4\u900f\u5fc3\u5934\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5bb6\u4e2d\u53ea\u662f\u6211\u7236\u5973\u4e8c\u4eba\uff0c\u6211\u5c01\u5200\u5df2\u4e45\uff0c\u5973\u513f\u672c\u4e8b\u5e73\u5e73\uff0c\u6599\u5979\u4e5f \u4e0d\u80fd\u4f24\u4e86\u4ee4\u5a7f\u3002\u4e8c\u5148\u751f\uff0c\u4f60\u6050\u6015\u627e\u9519\u4ec7\u5bb6\u4e86\uff01\u201d \u3000\u3000\u90a3\u8001\u5934\u5e72\u54b3\u4e00\u58f0\uff0c\u5f90\u5f90\u8bf4\u9053\uff1a\u201c\u4ee4\u5a7f\u56de\u6765\u4e86\u6ca1\u6709\uff1f\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u6653\u6f9c\u6240\u505a\u7684\u4e8b\uff0c\u6211\u90fd \u77e5\u9053\uff0c\u4ed6\u4e0a\u6708\u8fd8\u5728\u96ea\u9b42\u8c37\u517b\u4f24\u2026\u2026\u201d\u90a3\u8001\u5934\u4e0d\u5f85\u4ed6\u8bf4\u5b8c\uff0c\u63a5\u7eed\u8bf4\u9053\uff1a\u201c\u4ee4\u5a7f\u548c\u4e00\u4f4d\u59d1\u5a18\u4e0a\u6708 \u4ece\u6cb3\u5357\u540c\u56de\uff0c\u53ef\u6709\u6b64\u4e8b\uff1f\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u6709\u6b64\u4e8b\uff01\u201d\u90a3\u8001\u5934\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u59d1\u5a18\u4fbf\u662f\u6211\u7684\u6740\u5a7f \u4ec7\u4eba\uff0c\u8bf7\u8001\u82f1\u96c4\u5c06\u5979\u4ea4\u51fa\uff01\u201d \u3000\u3000\u539f\u6765\u51af\u7433\u4ee5\u524d\u5728\u9648\u7559\u9644\u8fd1\u6240\u6740\u7684\u738b\u6556\uff0c\u4fbf\u662f\u5510\u4e8c\u5148\u751f\u7684\u5973\u5a7f\u3002\u5510\u5bb6\u8001\u4e00\u8f88\u7684\u5144\u5f1f\u4e09\u4eba\uff0c \u90fd\u4ee5\u6697\u5668\u7684\u6b79\u6bd2\u540d\u95fb\u6c5f\u6e56\u3002\u51af\u7433\u4ee5\u524d\u6240\u4e2d\u7684\u4e03\u715e\u767d\u7709\u9488\uff0c\u4fbf\u662f\u5510\u5bb6\u79d8\u4f20\u7684\u6697\u5668\u4e4b\u4e00\u3002 \u3000\u3000\u8fd9\u5510\u4e8c\u5148\u751f\u540d\u53eb\u5510\u91d1\u5cf0\uff0c\u5728\u4e09\u5144\u5f1f\u4e2d\u6027\u60c5\u6700\u4e3a\u72c2\u50b2\uff0c\u4ed6\u53ea\u751f\u4e00\u5973\uff0c\u540d\u5524\u8d5b\u82b1\uff0c\u5510\u91d1\u5cf0\u5bf9 \u5979\u6eba\u7231\u975e\u5e38\uff0c\u5510\u8d5b\u82b1\u542c\u5f97\u4e08\u592b\u60e8\u6b7b\uff0c\u54ed\u54ed\u56b7\u56b7\uff0c\u5bfb\u6b7b\u89c5\u6d3b\uff0c\u8981\u7236\u4eb2\u66ff\u5979\u62a5\u4ec7\u3002\u5510\u91d1\u5cf0\u4e5f\u89c9\u5f97 \u7231\u5a7f\u88ab\u6740\uff0c\u5927\u6709\u635f\u4e8e\u5510\u5bb6\u5a01\u540d\uff0c\u4fbf\u643a\u4e86\u5973\u513f\uff0c\u79bb\u5f00\u56db\u5ddd\uff0c\u5bfb\u5230\u6cb3\u5357\uff0c\u627e\u5bfb\u6740\u5973\u5a7f\u738b\u6556\u7684\u51f6\u624b\u3002 \u3000\u3000\u6d88\u606f\u5e76\u4e0d\u96be\u5f97\uff0c\u97e9\u91cd\u5c71\u7684\u59bb\u5b50\u53f6\u6a2a\u6ce2\u4fbf\u662f\u5f53\u65e5\u5728\u573a\u4e4b\u4eba\uff0c\u4f46\u5f53\u65f6\u6e05\u5bab\u5df2\u4f20\u4e0b\u5bc6\u4ee4\uff0c\u5728\u4e00 \u5e74\u4e4b\u5185\uff0c\u4e0d\u8bb8\u641c\u6355\u5510\u6653\u6f9c\u548c\u51af\u7433\u3002\u81f3\u4e8e\u539f\u56e0\u5982\u4f55\uff0c\u9664\u4e86\u54c8\u5e03\u9640\u4e00\u4eba\u4e4b\u5916\uff0c\u5176\u4ed6\u4eba\u90fd\u4e0d\u77e5\u9053\u3002 \u53f6\u6a2a\u6ce2\u6068\u6781\u51af\u7433\u4e0e\u5979\u4f5c\u5bf9\uff0c\u89c1\u5510\u5bb6\u7236\u5973\u627e\u6765\uff0c\u6b63\u5408\u5fc3\u610f\u3002\u5979\u6151\u4e8e\u6e05\u5bab\u5bc6\u4ee4\uff0c\u6b63\u597d\u5047\u624b\u5510\u5bb6\u7236 \u5973\u5c06\u5979\u9664\u6389\u3002 \u3000\u3000\u5510\u91d1\u5cf0\u7236\u5973\uff0c\u9760\u4e86\u53f6\u6a2a\u6ce2\u7684\u5e26\u5f15\uff0c\u5bfb\u5230\u5c71\u4e1c\uff0c\u4e00\u63a2\u4fbf\u63a2\u51fa\u5510\u6653\u6f9c\u643a\u540c\u5982\u6b64\u8fd9\u822c\u7684\u4e00\u4e2a\u5c0f \u59d1\u5a18\uff0c\u540c\u4f4f\u6768\u5bb6\u3002\u53f6\u6a2a\u6ce2\u548c\u5510\u5bb6\u7236\u5973\uff0c\u90a3\u77e5\u9053\u8fd9\u662f\u59d0\u59d0\u4e0d\u662f\u59b9\u59b9\uff0c\u56e0\u6b64\u4e0d\u5047\u601d\u7d22\uff0c\u4fbf\u5230\u6768\u5bb6 \u6765\u8981\u51f6\u624b\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u4e5f\u4e0d\u6e05\u695a\u51af\u745b\u662f\u4e0d\u662f\u51f6\u624b\uff0c\u95fb\u8a00\u5403\u4e00\u60ca\u3002\u95ee\u9053\uff1a\u201c\u4ee4\u5a7f\u5728\u4ec0\u4e48\u5730\u65b9\u88ab\u6740\u5bb3\u7684\uff1f\u600e \u7684\u4f1a\u4e0e\u90a3\u5c0f\u59d1\u5a18\u7ed3\u4ec7\uff1f\u4e8c\u5148\u751f\u6015\u542c\u9519\u4e86\u522b\u4eba\u7684\u8bdd\u5427\uff1f\u201d\u5510\u91d1\u5cf0\u6012\u9053\uff1a\u201c\u5728\u6cb3\u5357\u9648\u7559\u88ab\u6740\u7684\uff0c \u8fd9\u4f4d\u97e9\u592a\u592a\u4fbf\u662f\u8bc1\u4eba\u3002\u201d\u6768\u4ef2\u82f1\u6253\u91cf\u4e86\u53f6\u6a2a\u6ce2\u4e00\u773c\uff0c\u89c1\u5979\u5e74\u867d\u534a\u767e\uff0c\u5374\u5996\u91cc\u5996\u6c14\uff0c\u4e0d\u50cf\u6b63 \u4eba\u3002\u9053\uff1a\u201c\u8fd9\u4f4d\u97e9\u592a\u592a\u5f53\u65e5\u5728\u573a\u4e48\uff1f\u53ef\u5426\u5c06\u7ecf\u8fc7\u60c5\u5f62\u8bf4\u7ed9\u8001\u592b\u77e5\u9053\u3002\u54ce\uff0c\u6211\u8001\u7cca\u6d82\u5566\uff0c\u8fd8\u672a \u8bf7\u6559\u5c0a\u592b\u4eba\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u53f6\u6a2a\u6ce2\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u592b\u5987\u8d31\u540d\uff0c\u63d0\u8d77\u6765\u4e5f\u8bb8\u8001\u82f1\u96c4\u542c\u4eba\u8bf4\u8fc7\u3002\u201d\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u7075 \u5c71\u6d3e\u7684\u540d\u5bbf\u97e9\u91cd\u5c71\u592b\u5987\uff0c\u6c5f\u6e56\u4e0a\u77e5\u9053\u7684\u4eba\u4e0d\u5c11\u3002\u51ed\u5979\u7684\u8eab\u4efd\uff0c\u8fd8\u4f1a\u80e1\u8d56\u4f60\u6536\u7559\u7684\u90a3\u4f4d\u5c0f\u59d1\u5a18 \u5417\uff1f\u201d \u3000\u3000\u6768\u4ef2\u82f1\u4ef0\u5929\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8001\u592b\u773c\u62d9\uff0c\u8be5\u8d23\u8be5\u8d23\u3002\u97e9\u91cd\u5c71\u542c\u8bf4\u662f\u5f53\u4ee4\u7687\u4e0a\u5f97\u529b\u4e4b\u4eba\uff0c\u90a3\u4e48 \u4ee4\u5a7f\u60f3\u5fc5\u4e5f\u662f\u5728\u516c\u95e8\u4e2d\u5f53\u5dee\u7684\u4e86\uff1f\u201d \u3000\u3000\u5510\u8d5b\u82b1\u9053\uff1a\u201c\u662f\u5728\u516c\u95e8\u5f53\u5dee\u53c8\u600e\u4e48\u6837\uff1f\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u516c\u5dee\u8ffd\u6355\u72af\u4eba\uff0c\u8fd9\u6837\u7684\u4ec7\u6740\uff0c\u4e8b \u6781\u5bfb\u5e38\u3002\u4e5f\u5f88\u96be\u8bf4\u8c01\u5bf9\u8c01\u4e0d\u5bf9\u3002\u4e8c\u5148\u751f\u662f\u6b66\u6797\u540d\u5bbf\uff0c\u4e5f\u5f53\u77e5\u9053\u5b98\u5dee\u6740\u8d3c\u6216\u8d3c\u6740\u5b98\u5dee\uff0c\u90fd\u4e0d\u80fd \u4e0e\u79c1\u4ec7\u7ed3\u6028\u7b49\u540c\u770b\u5f85\u3002\u6b66\u6797\u4e2d\u4eba\u4e5f\u5f88\u5c11\u4f1a\u63d2\u624b\u5176\u95f4\u3002\u4e8c\u5148\u751f\uff0c\u4e0d\u7ba1\u662f\u4e0d\u662f\u5979\u6740\u7684\uff0c\u6211\u770b\u8fd9\u51a4 \u4ec7\u8fd8\u662f\u89e3\u5f00\u4e3a\u662f\u3002\u201d \u3000\u3000\u6768\u4ef2\u82f1\u4e00\u987f\u8f7b\u63cf\u6de1\u5199\uff0c\u5510\u8d5b\u82b1\u54ed\u56b7\u9053\uff1a\u201c\u96be\u9053\u6211\u7684\u4e08\u592b\u5e73\u767d\u7ed9\u4eba\u6740\u4e86\u4e0d\u6210\uff1f\u8001\u5339\u592b\uff0c\u4f60 \u4eca\u65e5\u4e0d\u5c06\u51f6\u624b\u4ea4\u51fa\uff0c\u90a3\u53ef\u4e07\u4e07\u4e0d\u6210\uff01\u201d \u3000\u3000\u6768\u4ef2\u82f1\u53c8\u662f\u4ef0\u5929\u4e00\u7b11\uff0c\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u8d5b\u82b1\uff0c\u4f60\u522b\u95f9\uff0c\u6211\u81ea\u6709\u5206\u6559\u3002\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u4e8c\u5148 \u751f\uff0c\u6211\u8a00\u5c3d\u4e8e\u6b64\u3002\u4f60\u82e5\u8981\u4e0e\u6211\u53d9\u65e7\u65e5\u4ea4\u60c5\uff0c\u54b1\u4eec\u540c\u996e\u4e00\u676f\u3002\u62a5\u4ec7\u4e4b\u4e8b\uff0c\u8bf7\u4f60\u522b\u63d0\u4e86\uff01\u201d \u3000\u3000\u5510\u91d1\u5cf0\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6768\u8001\u82f1\u96c4\uff0c\u4f60\u5fd8\u4e86\u4e00\u4e8b\u4e86\u3002\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u5510\u91d1\u5cf0 \u9053\uff1a\u201c\u88ab\u6740\u7684\u4eba\u662f\u6211\u7684\u5973\u5a7f\uff0c\u662f\u6211\u5973\u513f\u7684\u4e08\u592b\u3002\u6211\u4eec\u7236\u5973\u548c\u6b7b\u8005\u7684\u5173\u7cfb\uff0c\u53ef\u4e0d\u662f\u666e\u901a\u7684\u6b66\u6797 \u670b\u53cb\u53ef\u6bd4\u3002\u6211\u4eec\u66ff\u4ed6\u62a5\u4ec7\uff0c\u8c01\u4e5f\u62e6\u963b\u4e0d\u4e86\uff01\u6768\u8001\u82f1\u96c4\uff0c\u4f60\u65e2\u7136\u4e0d\u613f\u63d2\u624b\u5176\u95f4\uff0c\u90a3\u6211\u4eec\u4e5f\u4e0d\u80fd \u52c9\u5f3a\u4e8e\u4f60\u3002\u4f46\u6211\u53ef\u8981\u8bf7\u4f60\u6055\u6211\u4eec\u65e0\u793c\uff0c\u6211\u4eec\u53ef\u8981\u81ea\u5df1\u52a8\u624b\u641c\u6355\u4e86\uff01\u201d \u3000\u3000\u6768\u4ef2\u82f1\u52c3\u7136\u4f5c\u8272\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4e8c\u5148\u751f\uff0c\u4f60\u4e5f\u5fd8\u8bb0\u4e00\u4e8b\u4e86\u3002\u201d\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u5fd8\u8bb0\u4ec0 \u4e48\uff1f\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u4f60\u5fd8\u8bb0\u8fd9\u91cc\u662f\u5728\u6211\u6768\u4ef2\u82f1\u7684\u5bb6\u4e2d\uff01\u6211\u7684\u5bb6\u5c82\u5bb9\u4eba\u968f\u4fbf\u641c\u67e5\uff1f\u201d\u5510\u91d1\u5cf0 \u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u662f\u8bda\u5fc3\u8981\u62e6\u963b\u6211\u4eec\u4e86\uff1f\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u6211\u6536\u7559\u7684\u4eba\u4fbf\u662f\u6211\u7684\u5bb6\u4eba\uff0c\u6709\u4ec0\u4e48\u5173 \u7cfb\uff0c\u6211\u4e00\u80a9\u6311\u8d77\uff0c\u4fbf\u662f\u7687\u5e1d\u95ee\u6211\u8981\u4eba\uff0c\u6211\u4e5f\u4e0d\u7ed9\uff01\u201d \u3000\u3000\u5510\u91d1\u5cf0\u78d4\u78d4\u7b11\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u90a3\u4e48\u54b1\u4eec\u662f\u767d\u6765\u4e00\u8d9f\u4e86\uff1f\u8d5b\u82b1\uff0c\u4f60\u8bb0\u5f97\u51fa\u5bb6\u95e8\u65f6\uff0c\u6211\u5bf9\u4f60\u8bf4 \u8fc7\u4ec0\u4e48\u8bdd\uff1f\u201d\u5510\u8d5b\u82b1\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u8bf4\u8fc7\u82e5\u4e0d\u80fd\u4e3a\u5973\u5a7f\u62a5\u4ec7\uff0c\u5c31\u4e0d\u56de\u56db\u5ddd\u3002\u201d\u5510\u91d1\u5cf0\u9053\uff1a \u201c\u662f\u554a\uff01\u4f60\u8bb0\u6027\u4e0d\u574f\uff0c\u6768\u8001\u82f1\u96c4\uff0c\u4f60\u5fcd\u5fc3\u770b\u6211\u8fd9\u628a\u8001\u9aa8\u5934\u57cb\u5728\u5916\u4e61\u5417\uff1f\u201d \u3000\u3000\u6768\u4ef2\u82f1\u9053\uff1b\u201c\u597d\uff0c\u90a3\u5c31\u8bf7\u4f60\u52a8\u624b\uff0c\u8ba9\u6211\u57cb\u9aa8\u5bb6\u4e2d\u5427\u3002\u201d\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u4e0d\u6562\uff0c\u4f46\u6768 \u8001\u82f1\u96c4\u65e2\u7136\u8fd9\u6837\u6267\u62d7\uff0c\u5e87\u62a4\u51f6\u5f92\uff0c\u6211\u4e5f\u53ea\u597d\u8bf7\u4f60\u6055\u6211\u5192\u6627\uff0c\u6211\u53ef\u8981\u8bf7\u6559\u4f60\u7684\u94c1\u638c\u795e\u5f39\u4e86\u3002\u201d \u3000\u3000\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u5f88\u597d\uff0c\u6211\u4e5f\u8981\u89c1\u8bc6\u4f60\u4eec\u5510\u5bb6\u7684\u6697\u5668\u3002\u201d\u80cc\u4e0a\u5f39\uff0c\u8d70\u4e0b\u53f0\u9636\uff0c\u5510\u91d1\u5cf0\u968f\u7740\u8d70 \u4e0b\u5ead\u5fc3\u3002\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u8bf7\uff01\u201d\u5510\u91d1\u5cf0\u8863\u8896\u4e00\u62c2\uff0c\u4e00\u62db\u201c\u8098\u5e95\u770b\u9524\u201d\uff0c\u731b\u7136\u6363\u51fa\uff0c\u6768\u4ef2\u82f1\u53cc\u81c2 \u4e00\u632f\uff0c\u8eab\u968f\u638c\u8d70\uff0c\u8fc5\u82e5\u72c2\u98da\uff0c\u547c\u547c\u4e24\u638c\uff0c\u6a2a\u626b\u51fa\u53bb\u3002\u5510\u91d1\u5cf0\u80a9\u5934\u4e00\u7f29\uff0c\u970d\u5730\u8de8\u8eab\uff0c\u53cc\u62f3\u4ea4 \u9519\uff0c\u4f7f\u7684\u662f\u957f\u62f3\u62db\u6570\uff0c\u62f3\u98ce\u731b\u6251\u9762\u95e8\u3002\u6768\u4ef2\u82f1\u89c1\u4ed6\u529f\u529b\u751a\u6df1\uff0c\u4f4e\u559d\u58f0\uff1a\u201c\u597d\uff01\u201d\u4fa7\u8eab\u5206\u638c\uff0c \u4e00\u4e2a\u864e\u8df3\uff0c\u62a2\u5230\u4e86\u5510\u91d1\u5cf0\u53f3\u4fa7\uff0c\u4e00\u8bb0\u201c\u60ca\u6d9b\u62cd\u5cb8\u201d\uff0c\u638c\u98ce\u98d2\u7136\uff0c\u62c2\u9762\u62cd\u5230\u3002\u5510\u91d1\u5cf0\u75be\u9000\u4e24 \u6b65\uff0c\u5c0f\u81c2\u4e00\u5708\uff0c\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u534a\u5b88\u534a\u653b\uff0c\u6768\u4ef2\u82f1\u7684\u62db\u6570\u89e3\u53bb\u3002\u4e24\u4eba\u8d8a\u6597\u8d8a\u72e0\uff0c\u7a7a\u5ead\u867d \u53ea\u4e24\u4eba\uff0c\u4f46\u542c\u90a3\u62f3\u638c\u5288\u98ce\u4e4b\u58f0\uff0c\u5c31\u5982\u6570\u5341\u4eba\u76f8\u6597\u4e00\u822c\u3002\u6768\u67f3\u9752\u5728\u5c4f\u98ce\u540e\u770b\u5f97\u751a\u4e3a\u5fc3\u6025\uff0c\u6084\u6084 \u7684\u8dd1\u56de\u53bb\uff0c\u628a\u5f39\u5f13\u53d6\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u4e24\u4eba\u6597\u5f97\u66f4\u9163\uff0c\u6e10\u6e10\u4e0d\u95fb\u62f3\u638c\u567c\u556a\u4e4b\u58f0\uff0c\u53ea\u89c1\u4eba\u5f71\u98d8\u98d8\uff0c\u76d8\u65cb\u6765\u5f80\uff0c\u58f0\u52bf\u4f3c\u4e0d\u82e5\u9002 \u624d\u60ca\u4eba\uff0c\u5374\u662f\u751f\u6b7b\u5b58\u4ea1\u4e4b\u6597\uff0c\u4e24\u4eba\u90fd\u7528\u4e0a\u4e86\u5185\u529b\uff0c\u62c6\u62db\u7834\u62db\uff0c\u62f3\u811a\u672a\u6cbe\uff0c\u62db\u5f0f\u5373\u6362\uff0c\u865a\u865a\u5b9e \u5b9e\uff0c\u53d8\u5316\u7e41\u590d\u4e4b\u6781\uff01 \u3000\u3000\u6597\u4e86\u4e00\u9635\uff0c\u4f46\u95fb\u888d\u8896\u62c2\u98ce\u4e4b\u58f0\uff0c\u6768\u4ef2\u82f1\u638c\u6cd5\u500f\u53d8\uff0c\u95ea\u7f29\u4e0d\u5b9a\uff0c\u82e5\u6309\u82e5\u62cd\uff0c\u5728\u5916\u884c\u4eba\u770b \u6765\uff0c\u4f3c\u662f\u8f7b\u98d8\u65e0\u529b\uff0c\u4f46\u5374\u662f\u638c\u542b\u5185\u52b2\uff0c\u67d4\u4e2d\u5e26\u521a\uff0c\u4e00\u6309\u5b9e\u4e86\u4fbf\u521a\u52b2\u975e\u5e38\uff0c\u8fd9\u638c\u6cd5\u517c\u91c7\u94c1\u6c99\u638c \u548c\u64d2\u62ff\u624b\u4e24\u5bb6\u4e4b\u957f\u3002\u5728\u5510\u91d1\u5cf0\u770b\u6765\uff0c\u4e0d\u557b\u662f\u94c1\u9524\u5de8\u65a7\uff0c\u51ff\u77f3\u5f00\u5c71\uff0c\u7adf\u903c\u5f97\u4ed6\u4e0d\u6562\u786c\u63a5\u3002 \u3000\u3000\u6768\u67f3\u9752\u5728\u5c4f\u98ce\u540e\u770b\u5f97\u7709\u98de\u8272\u821e\uff0c\u5f39\u5f13\u4e5f\u5782\u4e86\u4e0b\u6765\u3002\u573a\u4e2d\u4e24\u4eba\u6597\u5230\u5206\u9645\uff0c\u5510\u91d1\u5cf0\u6539\u7528\u78e8\u8eab \u638c\u6e38\u6597\uff0c\u663e\u89c1\u529b\u5df2\u4e0d\u652f\u3002\u5510\u8d5b\u82b1\u53eb\u9053\uff1a\u201c\u7239\uff0c\u6539\u6597\u6697\u5668\u5427\uff01\u201d\u6768\u4ef2\u82f1\u84e6\u5730\u4e00\u58f0\u957f\u5578\uff0c\u53cc\u638c\u4e00 \u5f15\u4e00\u62c2\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u5de6\u638c\u4e00\u62db\u201c\u94c1\u9a91\u7a81\u51fa\u201d\uff0c\u53f3\u638c\u4e00\u62db\u201c\u957f\u9f13\u9f50\u9e23\u201d\uff0c\u5510\u91d1\u5cf0\u4e00\u62f3\u6363\u7a7a\uff0c\u5bf9 \u65b9\u638c\u98ce\u98d2\u7136\uff0c\u5df2\u5230\u80f8\u9645\u3002\u597d\u4e2a\u5510\u91d1\u5cf0\uff0c\u4e34\u5371\u4e0d\u4e71\uff0c\u53f3\u8db3\u4e00\u65cb\uff0c\u501f\u62e7\u4e4b\u52bf\uff0c\u6c89\u8eab\u95ea\u907f\uff0c\u6768\u4ef2\u82f1 \u53cc\u638c\u5728\u4ed6\u9762\u95e8\u5288\u8fc7\uff0c\u638c\u98ce\u52b2\u63a0\uff0c\u5510\u91d1\u5cf0\u7709\u5fc3\u8fa3\u8fa3\u4f5c\u75db\uff0c\u773c\u775b\u90fd\u51e0\u4e4e\u7ed9\u9707\u8361\u5f97\u4e0d\u80fd\u7741\u5f00\uff0c\u4e00\u4e2a \u5012\u7ffb\uff0c\u9000\u51fa\u4e08\u8bb8\uff0c\u53eb\u9053\uff1a\u201c\u6768\u5bb6\u94c1\u638c\uff0c\u540d\u4e0d\u865a\u4f20\uff01\u770b\u6697\u5668\uff01\u201d\u53cd\u624b\u4e00\u626c\uff0c\u53ea\u542c\u5f97\u94ee\u94ee\u4e24\u58f0\uff0c \u4e24\u652f\u94f6\u9556\uff0c\u7834\u7a7a\u98de\u51fa\uff01 \u3000\u3000\u6768\u4ef2\u82f1\u8eab\u56de\u52bf\u8f6c\uff0c\u4f46\u89c1\u9556\u8d34\u808b\u65c1\uff0c\u500f\u7136\u7a7f\u8fc7\u3002\u521a\u8bf4\u5f97\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u84e6\u89c1\u5bd2\u661f\u98de\u6e85\uff0c\u8fce \u9762\u6251\u6765\uff0c\u6768\u4ef2\u82f1\u77e5\u662f\u5510\u5bb6\u7684\u62ff\u624b\u6697\u5668\u4e03\u715e\u767d\u7709\u9488\uff0c\u6025\u8fd0\u5185\u5bb6\u771f\u529b\uff0c\u53cc\u638c\u9f50\u63a8\uff0c\u638c\u631f\u52b2\u98ce\uff0c\u547c \u547c\u4e24\u58f0\uff0c\u767d\u7709\u9488\u672a\u80fd\u8fd1\u8eab\uff0c\u7eb7\u7eb7\u5760\u5730\u3002\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u597d\u5440\uff0c\u518d\u63a5\u8fd9\u4e2a\u3002\u201d\u624b\u626c\u5904\uff0c\u545c\u9e23\u602a \u54cd\uff0c\u4e94\u56e2\u9ed1\u5ffd\u5ffd\u7684\u4e1c\u897f\uff0c\u5f53\u5934\u7f69\u4e0b\uff0c\u6768\u4ef2\u82f1\u5f39\u5f13\u4e00\u66f3\uff0c\u8fde\u73e0\u6253\u51fa\uff0c\u6697\u5668\u5728\u534a\u7a7a\u76f8\u649e\uff0c\u5510\u91d1\u5cf0 \u6240\u53d1\u7684\u4e94\u4e2a\u5706\u7403\uff0c\u4e00\u78b0\u5373\u88c2\uff0c\u5ffd\u7136\u5c04\u51fa\u6570\u5341\u9053\u706b\u5149\uff01 \u3000\u3000\u6768\u4ef2\u82f1\u548c\u8eab\u4e00\u6eda\uff0c\u7ffb\u8d77\u8eab\u65f6\uff0c\u5df2\u626f\u4e0b\u4e86\u8eab\u4e0a\u7684\u957f\u888d\uff0c\u65cb\u98ce\u6025\u821e\uff0c\u706b\u661f\u98de\u6e85\uff0c\u5e78\u672a\u4f24\u8eab\uff0c \u4f46\u5df2\u6cbe\u4e86\u6ee1\u8eab\u6ce5\u571f\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u77e5\u9053\u5510\u5bb6\u6697\u5668\uff0c\u5c42\u51fa\u4e0d\u7a77\uff0c\u53ea\u6709\u4e89\u53d6\u673a\u5148\uff0c\u63a7\u5236\u4e3b\u52a8\uff0c\u624d\u80fd\u5e78\u514d\u3002\u5510\u91d1\u5cf0\u4e09\u6597\u6697 \u5668\uff0c\u672a\u80fd\u4f24\u654c\uff0c\u7a92\u4e86\u4e00\u7a92\u3002\u6768\u4ef2\u82f1\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff01\u201d\u5f39\u5f13\u4e00\u66f3\uff0c\u94c1\u5f39\u5b50\u5df2\u4f3c \u51b0\u96f9\u4e00\u6837\u6253\u6765\u3002 \u3000\u3000\u5510\u91d1\u5cf0\u53eb\u9053\uff1a\u201c\u597d\u5440\uff0c\u4eca\u65e5\u624d\u649e\u5230\u5bf9\u624b\uff01\u201d\u6539\u7528\u91d1\u94b1\u9556\u5e94\u654c\uff0c\u4ee5\u6ee1\u5929\u82b1\u96e8\u6d12\u91d1\u94b1\u7684\u624b \u6cd5\uff0c\u5c06\u94c1\u5f39\u5b50\u4e5f\u78b0\u5f97\u7eb7\u7eb7\u5760\u5730\u3002\u6768\u4ef2\u82f1\u7684\u795e\u5f39\u867d\u6253\u4e0d\u7740\u4ed6\uff0c\u4f46\u5df2\u903c\u5f97\u4ed6\u5fd9\u4e8e\u5e94\u4ed8\uff0c\u81ea\u5df1\u53ef\u4ee5 \u5598\u53e3\u6c14\u4e86\u3002 \u3000\u3000\u6253\u4e86\u4e00\u9635\uff0c\u53cc\u65b9\u4ff1\u65e0\u635f\u4f24\uff0c\u5ffd\u7136\u9759\u6b62\u4e0b\u6765\uff0c\u4e00\u4e2a\u5728\u897f\uff0c\u4e00\u4e2a\u5728\u4e1c\uff0c\u4f3c\u6597\u9e21\u822c\u4e92\u76f8\u6ce8\u89c6\uff0c \u4e00\u4e2a\u662f\u624b\u6263\u5f13\u5f26\uff0c\u6c89\u8170\u4f5c\u52bf\uff0c\u4e00\u4e2a\u638c\u7ffb\u6697\u5668\uff0c\u53cc\u773c\u5706\u7741\u3002\u4e24\u4eba\u6b64\u8fdb\u5f7c\u9000\uff0c\u7ed5\u573a\u4e09\u531d\uff0c\u5140\u662f\u4e0d \u53d1\u4e00\u5f39\u3002\u6768\u67f3\u9752\u770b\u5f97\u6697\u6697\u7eb3\u7f55\uff0c\u5374\u4e0d\u77e5\u8fd9\u5df2\u662f\u4ed6\u4eec\u4e24\u4eba\u51b3\u6218\u7684\u5173\u5934\uff01 \u3000\u3000\u4e24\u4eba\u90fd\u77e5\u96be\u4f24\u5bf9\u65b9\uff0c\u6240\u4ee5\u5927\u5bb6\u90fd\u5bfb\u6687\u62b5\u9699\uff0c\u7b49\u673a\u4f1a\u65bd\u5c55\u6740\u624b\u3002\u7ed5\u573a\u591a\u531d\uff0c\u84e6\u5730\u53d1\u4e00\u58f0 \u558a\uff0c\u4e24\u4eba\u90fd\u8df3\u4e86\u8d77\u6765\uff0c\u6768\u53d1\u5f39\u4e38\uff0c\u5510\u65bd\u6bd2\u7bad\uff0c\u6240\u51fb\u7684\u90fd\u662f\u5bf9\u65b9\u54bd\u5589\u8981\u5bb3\uff0c\u624b\u6cd5\u4e4b\u5feb\u3001\u52b2\u3001 \u51c6\uff0c\u4ee4\u4eba\u53f9\u4e3a\u89c2\u6b62\u3002\u6362\u4e86\u4e00\u62db\uff0c\u4ecd\u7136\u5404\u81ea\u907f\u5f00\uff0c\u53c8\u505c\u4e86\u4e0b\u6765\uff0c\u4e24\u4eba\u90fd\u4fef\u4f0f\u4f5c\u52bf\uff0c\u76ee\u4e0d\u8f6c\u775b\u7684 \u51dd\u89c6\u5bf9\u65b9\u3002 \u3000\u3000\u6768\u67f3\u9752\u89c1\u7236\u4eb2\u7d27\u5f20\u4e4b\u6781\uff0c\u6c57\u73e0\u6cbf\u7740\u9762\u988a\u6ef4\u4e0b\uff0c\u5374\u4ecd\u662f\u624b\u6263\u5f39\u5f13\uff0c\u5b9b\u5982\u77f3\u50cf\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u90a3\u5510\u91d1\u5cf0\u4e5f\u662f\u5982\u6b64\u3002\u6768\u67f3\u9752\u5fc3\u9053\uff1a\u201c\u90a3\u8001\u5934\u76ee\u4e0d\u65c1\u89c6\uff0c\u6211\u7528\u8fde\u73e0\u5f39\u6697\u88ad\uff0c\u53d6\u4ed6\u7a74\u9053\uff0c\u5c82\u4e0d\u751a \u597d\u3002\u201d\u5f39\u5f13\u4e00\u66f3\uff0c\u8fde\u53d1\u4e09\u5f39\uff0c\u4e00\u53d6\u5510\u91d1\u5cf0\u4e0a\u76d8\u7684\u201c\u7709\u5c16\u7a74\u201d\uff0c\u4e00\u53d6\u4e2d\u76d8\u7684\u201c\u7075\u5e9c\u7a74\u201d\uff0c\u4e00\u53d6 \u4e0b\u76d8\u7684\u201c\u7a8d\u9634\u7a74\u201d\uff0c\u4e09\u5f39\u9f50\u53d1\uff0c\u6479\u542c\u5f97\u7236\u4eb2\u53eb\u58f0\u201c\u4e0d\u597d\uff01\u201d\u6768\u67f3\u9752\u5403\u4e86\u4e00\u60ca\uff0c\u5ffd\u89c9\u4e00\u80a1\u52b2\u98ce \u6251\u6765\uff0c\u5c4f\u98ce\u788e\u88c2\uff0c\u5510\u91d1\u5cf0\u53d1\u4e86\u4e00\u679a\u94c1\u5f39\uff0c\u53c8\u5c06\u6768\u67f3\u9752\u6240\u53d1\u7684\u4e09\u679a\u94c1\u5f39\u5b50\u53cd\u9707\u56de\u6765\u3002\u5e78\u5f97\u6768\u67f3 \u9752\u89c1\u673a\uff0c\u5c4f\u98ce\u5012\u65f6\uff0c\u5979\u4e5f\u968f\u5012\u5730\u4e0a\uff0c\u5f39\u4e38\u4ece\u5979\u5934\u9876\u98de\u8fc7\uff0c\u5d4c\u5728\u540e\u9762\u7684\u5899\u58c1\u4e4b\u4e2d\u3002 \u3000\u3000\u5230\u6768\u67f3\u9752\u518d\u7ad9\u8d77\u8eab\u65f6\uff0c\u5ead\u9662\u4e2d\u6697\u5668\u5df2\u662f\u6ee1\u7a7a\u98de\u821e\u3002\u5510\u8d5b\u82b1\u5de6\u624b\u53d1\u767d\u7709\u9488\uff0c\u53f3\u624b\u53d1\u6bd2\u84ba \u85dc\uff0c\u52a9\u7236\u88ad\u654c\u3002\u4ed6\u4eec\u5510\u5bb6\u7684\u89c4\u77e9\uff0c\u4ece\u4e0d\u4ee5\u4e8c\u654c\u4e00\u3002\u53ef\u662f\u5bf9\u65b9\u6709\u4eba\u52a9\u6218\uff0c\u8fd9\u7981\u4f8b\u5c31\u53ef\u89e3\u9664\u3002\u5510 \u8d5b\u82b1\u7684\u529f\u529b\u53ef\u6bd4\u6768\u67f3\u9752\u6df1\u5f97\u591a\uff0c\u7236\u5973\u4fe9\u4e00\u5939\u653b\uff0c\u987f\u65f6\u5982\u864e\u6dfb\u7ffc\u3002\u5510\u91d1\u5cf0\u559d\u9053\uff1a\u201c\u6768\u8001\u513f\uff0c\u8fd8 \u4e0d\u670d\u8f93\u5417\uff1f\u201d\u6768\u4ef2\u82f1\u95f7\u58f0\u4e0d\u54cd\uff0c\u5510\u91d1\u5cf0\u53cc\u624b\u4e00\u626c\uff0c\u8774\u8776\u9556\u5939\u7740\u6bd2\u84ba\u85dc\u56db\u65b9\u98de\u5230\uff0c\u90a3\u8774\u8776\u9556\u4e2d \u85cf\u673a\u62ec\uff0c\u5ffd\u5730\u659c\u98de\uff0c\u5ffd\u8d70\u76f4\u7ebf\uff0c\u9632\u4e0d\u80dc\u9632\uff01\u6768\u4ef2\u82f1\u987e\u4e0d\u5f97\u53d1\u5f39\uff0c\u8fd0\u638c\u6210\u98ce\u3002\u5c06\u8774\u8776\u9556\u51fb\u843d\uff0c \u84e6\u5730\u817f\u5f2f\u4e00\u9ebb\uff0c\u811a\u8ddf\u4e00\u8f6f\uff0c\u6251\u7a7a\u5012\u5730\u3002\u5510\u91d1\u5cf0\u53eb\u9053\uff1a\u201c\u8d5b\u82b1\u4f4f\u624b\uff0c\u4ed6\u4e24\u817f\u90fd\u5df2\u4e2d\u4e86\u6211\u7684\u6bd2\u84ba \u85dc\u4e86\uff01\u201d \u3000\u3000\u6768\u67f3\u9752\u4e00\u8dc3\u6251\u51fa\uff0c\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u662f\u6b64\u4eba\u5417\uff01\u201d\u53f6\u6a2a\u6ce2\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d\u5510\u8d5b\u82b1\uff0c\u4e00\u53e3\u98de \u5200\uff0c\u5c06\u6768\u67f3\u9752\u7684\u5f39\u5f13\u5288\u6210\u4e24\u622a\u3002\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u9752\u513f\uff0c\u4e0d\u51c6\u4e0a\u6765\uff01\u201d\u7ffb\u8eab\u5750\u8d77\uff0c\u9762\u8272\u7070\u767d\u3002 \u3000\u3000\u5510\u91d1\u5cf0\u78d4\u78d4\u7b11\u9053\uff1a\u201c\u6768\u5927\u54e5\uff0c\u8fd9\u756a\u771f\u771f\u5f97\u7f6a\u4f60\u4e86\uff01\u8bf7\u4f60\u5c06\u51f6\u624b\u4ea4\u51fa\u6765\u5427\uff01\u201d\u6768\u4ef2\u82f1\u6297\u58f0 \u8bf4\u9053\uff1a\u201c\u5510\u8001\u4e8c\uff0c\u4f60\u60f3\u6211\u5411\u4f60\u4f4e\u5934\uff0c\u90a3\u662f\u4e07\u4e07\u4e0d\u80fd\uff01\u201d\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u4e0d\u77e5\u6211\u4eec\u5510\u5bb6\u6697 \u5668\u7684\u5389\u5bb3\uff0c\u4f60\u4e2d\u4e86\u6bd2\u84ba\u85dc\uff0c\u6ca1\u6709\u6211\u7684\u89e3\u836f\uff0c\u7eb5\u4e0d\u4ea1\uff0c\u4e5f\u8981\u6b8b\u5e9f\uff01\u201d\u6768\u4ef2\u82f1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u6768 \u67d0\u82e5\u7136\u6015\u6b7b\uff0c\u4e5f\u4e0d\u80fd\u5728\u6b66\u6797\u4e2d\u6df7\u51e0\u5341\u5e74\u4e86\uff01\u201d\u5510\u91d1\u5cf0\u7fd8\u8d77\u62c7\u6307\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6709\u79cd\uff01\u82e5\u4f60\u4e0e \u51f6\u624b\u6709\u8fc7\u547d\u7684\u4ea4\u60c5\uff0c\u4e3a\u670b\u53cb\u4e24\u808b\u63d2\u5200\uff0c\u90a3\u8fd8\u503c\u5f97\uff01\u4f46\u6211\u4eec\u5df2\u67e5\u77e5\u4f60\u4e0e\u90a3\u5c0f\u4e2b\u5934\u4e00\u65e0\u4eb2\uff0c\u4e8c\u65e0 \u6545\u3002\u4f60\u51ed\u4ec0\u4e48\u8981\u5e87\u62a4\u5979\uff1f\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u9053\u4e49\u4e3a\u5148\uff0c\u5979\u662f\u4e00\u540d\u5b64\u5973\uff0c\u65e2\u7136\u5230\u4e86\u6211\u5bb6\uff0c \u90a3\u5c31\u4e0d\u80fd\u5bb9\u5916\u4eba\u6b3a\u8d1f\u3002\u201d\u5510\u91d1\u5cf0\u5927\u7b11\u9053\uff1a\u201c\u4e8b\u5230\u5982\u4eca\uff0c\u4f60\u5c31\u4e0d\u5bb9\u5979\u7ed9\u4eba\u6b3a\u8d1f\u4e5f\u4e0d\u80fd\u4e86\u3002\u4f60\u81ea \u5df1\u6027\u547d\u96be\u4fdd\uff0c\u8fd8\u51ed\u4ec0\u4e48\u5305\u5e87\u51f6\u5f92\uff1f\u54c8\uff01\u5bf9\u4e0d\u4f4f\uff0c\u6211\u4eec\u53ef\u8981\u52a8\u624b\u641c\u4f60\u94c1\u638c\u795e\u5f39\u7684\u8d35\u5e9c\u4e86\uff01\u201d \u3000\u3000\u6768\u4ef2\u82f1\u6c14\u5f97\u773c\u775b\u53d1\u9ed1\uff0c\u5ffd\u542c\u5f97\u53f6\u6a2a\u6ce2\u9053\uff1a\u201c\u4e0d\u5fc5\u641c\u4e86\u3002\u201d\u5927\u95e8\u5916\u8d70\u8fdb\u4e86\u4e24\u4e2a\u4eba\uff1a\u6b63\u662f\u5510 \u6653\u6f9c\u548c\u51af\u745b\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u53eb\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u4f60\u5feb\u5e26\u5979\u9003\u8d70\uff01\u201d\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u662f\u8fd9\u4e2a\u5c0f\u4e2b\u5934\u5417\uff1f\u201d\u53f6\u6a2a\u6ce2\u9053\uff1a \u201c\u6b63\u662f\uff01\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u62a2\u5148\u5374\u5835\u4e86\u5927\u95e8\u3002\u51af\u745b\u5140\u7136\u4e0d\u4ff1\uff0c\u548c\u5510\u6653\u6f9c\u98de\u6b65\u62a2\u8fdb\uff0c\u9f50\u58f0\u53eb\u9053\uff1a \u201c\u516c\u516c\u3001\u5e08\u5085\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u6768\u67f3\u9752\u6a2a\u4e86\u51af\u745b\u4e00\u773c\uff0c\u9053\uff1a\u201c\u90fd\u662f\u4e3a\u4f60\uff0c\u6211\u7684\u7239\u5df2\u4e2d\u522b\u4eba\u7684\u6bd2 \u84ba\u85dc\u5566\u3002\u201d\u51af\u745b\u53cc\u7709\u5012\u7ad6\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff01\u6740\u4eba\u507f\u547d\uff0c\u6b20\u503a\u8fd8\u94b1\uff0c\u6709\u4ec0\u4e48\u4e8b\u6211\u6765\u627f\u5f53\uff01\u5510\u53d4 \u53d4\uff0c\u4f60\u6276\u516c\u516c\u56de\u53bb\u3002\u201d \u3000\u3000\u51af\u745b\u8fd9\u51e0\u53e5\u8bdd\u539f\u662f\u6c5f\u6e56\u4e0a\u7684\u53e3\u5934\u7985\uff0c\u5510\u91d1\u5cf0\u542c\u4e86\uff0c\u5374\u4ee5\u4e3a\u5979\u5c31\u662f\u51f6\u624b\u3002\u6740\u4e86\u4eba\u8fd8\u53e3\u51fa\u5927 \u8a00\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u91ce\u4e2b\u5934\uff0c\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u51fa\u624b\u8fd9\u6837\u6bd2\u8fa3\uff0c\u6740\u4e86\u4eba\u8fd8\u4e0d\u670d\u7f6a\u5417\uff1f\u201d\u5510\u8d5b\u82b1\u5df2\u5fcd \u8010\u4e0d\u4f4f\uff0c\u626c\u624b\u5c31\u662f\u4e09\u67c4\u98de\u5200\uff01 \u3000\u3000\u5510\u6653\u6f9c\u548c\u6768\u67f3\u9752\u5c06\u6768\u4ef2\u82f1\u6276\u8fdb\u9759\u5ba4\uff0c\u6768\u4ef2\u82f1\u8dcc\u5750\u5e8a\u4e0a\uff0c\u6c14\u5598\u5598\u7684\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u6709\u9752\u513f\u670d \u4f8d\u6211\u5c31\u591f\u4e86\u3002\u4f60\u5feb\u51fa\u53bb\uff0c\u7528\u5b9d\u5251\u5f00\u8def\uff0c\u4e00\u5b9a\u8981\u628a\u745b\u59d1\u5a18\u6551\u51fa\u53bb\uff01\u6211\u4eec\u6768\u5bb6\u7684\u4eba\uff0c\u7edd\u4e0d\u80fd\u5bb9\u4eba \u6355\u53bb\uff01\u201d \u3000\u3000\u51af\u745b\u8eab\u5f62\u4e00\u6643\uff0c\u5c0f\u624b\u4e00\u6284\uff0c\u907f\u8fc7\u4e24\u628a\uff0c\u63a5\u4e86\u4e00\u628a\uff0c\u5510\u8d5b\u82b1\u518d\u5ea6\u51fa\u624b\uff0c\u6253\u51fa\u4e09\u628a\u6bd2\u84ba\u85dc\uff0c \u51af\u745b\u628a\u98de\u5200\u8fd8\u63b7\u8fc7\u53bb\uff0c\u6b63\u62df\u518d\u63a5\uff0c\u5ffd\u542c\u5f97\u5510\u6653\u6f9c\u53eb\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u6697\u5668\u6709\u6bd2\uff0c\u4e0d\u80fd\u4e71\u63a5\uff01\u201d\u51af \u745b\u80a9\u5934\u4e00\u7f29\uff0c\u4e00\u4e2a\u956b\u91cc\u85cf\u8eab\uff0c\u4e09\u628a\u6bd2\u84ba\u85dc\u4e5f\u6253\u7a7a\u4e86\u3002 \u3000\u3000\u5510\u91d1\u5cf0\u89c1\u51af\u745b\u8eab\u6cd5\u5947\u5feb\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u738b\u6556\u4f1a\u4e27\u5728\u5979\u7684\u624b\u4e2d\u3002\u201d\u53eb\u9053\uff1a\u201c\u8d5b\u82b1\uff0c\u4f60\u8ba9 \u6211\u6765\uff01\u201d\u53cc\u6307\u8fde\u5f39\uff0c\u7528\u91d1\u94b1\u9556\u6253\u51af\u745b\u7a74\u9053\uff01 \u3000\u3000\u51af\u745b\u4e00\u542c\u98ce\u58f0\uff0c\u5df2\u77e5\u52b2\u75be\uff0c\u55d6\u7684\u4e00\u58f0\u62d4\u51fa\u5b9d\u5251\uff0c\u6a2a\u5251\u4e00\u62ab\uff0c\u53ea\u89c9\u5251\u5c16\u4e71\u98a4\uff0c\u706b\u661f\u98de\u6e85\uff0c \u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u8155\u529b\u4e0d\u5728\u6768\u516c\u516c\u4e4b\u4e0b\uff01\u201d \u3000\u3000\u5510\u91d1\u5cf0\u89c1\u4e09\u679a\u94b1\u9556\u90fd\u7ed9\u5b9d\u5251\u5288\u788e\uff0c\u52c3\u7136\u5927\u6012\uff0c\u5de6\u624b\u91d1\u94b1\u9556\uff0c\u53f3\u624b\u86c7\u7130\u7bad\uff0c\u7eb7\u7eb7\u5c04\u81f3\uff01\u51af \u745b\u5c55\u5251\u78d5\u94b1\u9556\uff0c\u817e\u8eab\u907f\u706b\u7bad\u3002\u86c7\u7130\u7bad\u843d\u5904\u4fbf\u662f\u4e00\u6e9c\u84dd\u8272\u7684\u706b\u7130\uff0c\u84ec\u7136\u70b8\u88c2\uff01 \u3000\u3000\u51af\u745b\u542c\u4e86\u4ed6\u521a\u624d\u7684\u559d\u9a82\uff0c\u660e\u77e5\u53c8\u662f\u4e00\u573a\u8bef\u4f1a\uff0c\u53ef\u662f\u4e00\u6765\u5bf9\u65b9\u7684\u6697\u5668\u5982\u96e8\uff0c\u6b32\u89e3\u91ca\u5df2\u4e0d\u53ef \u80fd\u3002\u4e8c\u6765\u5979\u6068\u5510\u91d1\u5cf0\u4f24\u4e86\u6768\u4ef2\u82f1\uff0c\u8d77\u4e86\u540c\u4ec7\u654c\u5ffe\u4e4b\u5fc3\uff0c\u54ea\u80af\u5411\u654c\u4eba\u4f4e\u58f0\u4e0b\u6c14\uff0c\u6c42\u4ed6\u505c\u624b\u3002 \u3000\u3000\u51af\u745b\u8eab\u6cd5\u8f7b\u7075\uff0c\u5251\u62db\u7d27\u5bc6\uff0c\u5510\u91d1\u5cf0\u6253\u4e86\u4e00\u9635\uff0c\u4f24\u4e0d\u5230\u5979\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u5c55\u51fa\u7edd\u62db\uff0c\u6697\u5668\u6ee1 \u7a7a\u4e71\u53d1\uff0c\u4e92\u76f8\u51fb\u649e\uff0c\u6709\u7684\u659c\u98de\uff0c\u6709\u7684\u76f4\u843d\uff0c\u51af\u7405\u5168\u8eab\u5df1\u5728\u6697\u5668\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u5510\u6653\u6f9c\u53eb\u58f0\uff1a\u201c\u4e0d \u597d\uff01\u201d\u6b63\u5f85\u62d4\u5251\u52a9\u9635\u3002\u5ffd\u89c1\u51af\u745b\u5b9d\u5251\u4e00\u821e\uff0c\u9aa4\u7684\u98de\u8d77\u4e00\u5708\u94f6\u8679\uff0c\u62a4\u7740\u5934\u9762\uff0c\u987f\u65f6\u535c\u535c\u8fde\u58f0\uff0c \u6697\u5668\u7eb7\u7eb7\u6253\u5728\u5979\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u51af\u745b\u8eab\u4e0a\u7a7f\u7684\u662f\u949f\u4e07\u5802\u6240\u9001\u7684\u91d1\u4e1d\u8f6f\u7532\uff0c\u5200\u67aa\u4e0d\u5165\uff0c\u4f55\u60e7\u6697\u5668\uff1f\u51af\u745b\u867d\u7ed9\u6697\u5668\u7684\u529b\u9053\u9707 \u5f97\u8df3\u8361\u4e0d\u4f11\uff0c\u4e00\u9635\u5982\u96e8\u822c\u7684\u6697\u5668\u8fc7\u540e\uff0c\u51af\u745b\u500f\u7136\u63d0\u5251\u51b2\u53bb\uff0c\u8eab\u4e0a\u7adf\u662f\u5168\u65e0\u635f\u4f24\uff01 \u3000\u3000\u5510\u91d1\u5cf0\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u5979\u7684\u8eab\u8eaf\u96be\u9053\u662f\u94c1\u94f8\u7684\u4e0d\u6210\uff1f\u8fd9\u4ec7\u4e07\u4e07\u4e0d\u80fd\u62a5\u4e86\uff01\u201d\u5510\u91d1\u5cf0 \u7684\u6697\u5668\u867d\u591a\uff0c\u7ecf\u4e86\u4e24\u573a\u6076\u6597\uff0c\u4e5f\u5df2\u662f\u6240\u4f59\u65e0\u51e0\uff01 \u3000\u3000\u51af\u745b\u8d81\u7740\u5510\u91d1\u5cf0\u4e00\u7a92\u4e4b\u9645\uff0c\u8eab\u5f62\u9aa4\u8d77\uff0c\u75be\u5982\u98de\u9e1f\uff0c\u5251\u5149\u63a0\u5904\uff0c\u8ff3\u53d6\u5510\u91d1\u5cf0\u5de6\u808b\u7684\u201c\u9b42\u95e8 \u7a74\u201d\uff0c\u5510\u91d1\u5cf0\u98de\u817f\u4e00\u8e22\uff0c\u5de6\u638c\u75be\u65ab\uff0c\u4e24\u4eba\u6362\u4e86\u4e00\u62db\uff0c\u51af\u745b\u559d\u9053\uff1a\u201c\u4f60\u4e5f\u63a5\u6211\u7684\u6697\u5668\u770b\u770b\uff01\u201d \u5de6\u624b\u4e00\u626c\uff0c\u4e00\u628a\u98de\u8292\uff0c\u95ea\u7535\u5c04\u51fa\u3002 \u3000\u3000\u5510\u91d1\u5cf0\u662f\u6697\u5668\u7684\u5927\u540d\u5bb6\uff0c\u63a5\u6697\u5668\u7684\u672c\u9886\u4e5f\u5df2\u5230\u4e86\u51fa\u795e\u5165\u5316\u4e4b\u5883\uff0c\u4f46\u89c1\u4ed6\u53cc\u8896\u4e00\u6325\uff0c\u628a\u98de \u8292\u5377\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002\u4f46\u51af\u745b\u5251\u6cd5\u4f55\u7b49\u5389\u5bb3\uff0c\u5979\u662f\u4ee5\u6697\u5668\u6270\u654c\u5fc3\u795e\uff0c\u5251\u62db\u968f\u81f3\u3002\u5510\u91d1\u5cf0\u624d\u6321\u4e86\u6697 \u5668\uff0c\u4fbf\u89c9\u51b7\u6c14\u68ee\u68ee\uff0c\u5251\u98ce\u523a\u9762\u3002\u9976\u4ed6\u662f\u6b66\u6797\u540d\u5bbf\uff0c\u4e5f\u81ea\u5fc3\u614c\uff01\u75be\u9000\u4e4b\u65f6\uff0c\u9876\u5fc3\u4e00\u51c9\uff0c\u5934\u53d1\u5df2 \u88ab\u524a\u53bb\u4e86\u4e00\u7efa\u3002 \u3000\u3000\u5510\u8d5b\u82b1\u89c1\u7236\u4eb2\u5371\u6025\uff0c\u6025\u53d1\u98de\u5200\u88ad\u654c\uff0c\u51af\u745b\u9053\uff1a\u201c\u4f60\u4e5f\u5c1d\u5c1d\u6211\u7684\u6697\u5668\uff01\u201d\u5510\u8d5b\u82b1\u89c1\u98de\u5200\u5c04 \u4e2d\u5979\u7684\u5fc3\u7a9d\uff0c\u5374\u5ffd\u5730\u53cd\u5f39\u56de\u6765\uff0c\u5df2\u81ea\u76ee\u77aa\u53e3\u5446\uff0c\u98de\u8292\u9aa4\u81f3\uff0c\u5f85\u95ea\u907f\u65f6\uff0c\u53f3\u80a9\u4e00\u9635\u5267\u75db\uff0c\u8f6f\u9aa8 \u5df2\u88ab\u98de\u8292\u900f\u8fc7\uff01 \u3000\u3000\u5510\u91d1\u5cf0\u62c9\u8d77\u5973\u513f\u4fbf\u8d70\uff0c\u51af\u745b\u5982\u98de\u8ffd\u6251\u3002\u53f6\u6a2a\u6ce2\u7edd\u672a\u6599\u5230\u5510\u5bb6\u7236\u5973\u4e5f\u4f1a\u843d\u8d25\uff0c\u5927\u4e3a\u9707\u60ca\uff0c \u51af\u745b\u6251\u6765\uff0c\u53f6\u6a2a\u6ce2\u6a2a\u5251\u4e00\u5c01\uff0c\u9000\u540e\u4e00\u6b65\uff0c\u51af\u745b\u6740\u5f97\u6027\u8d77\uff0c\u5237\uff0c\u5237\uff0c\u5237\uff0c\u8fde\u523a\u4e09\u5251\uff0c\u90fd\u662f\u8ffd\u98ce \u5251\u6cd5\u7684\u7edd\u62db\uff0c\u8fc5\u6377\u65e0\u4f26\uff0c\u53f6\u6a2a\u6ce2\u6b66\u529f\u867d\u9ad8\uff0c\u6321\u4e86\u51e0\u5251\uff0c\u4ea6\u5df2\u65e0\u5fc3\u604b\u6218\uff0c\u62bd\u8eab\u9000\u51fa\u5927\u95e8\u3002\u8fd9\u65f6 \u5ead\u9662\u4e2d\u5df2\u88ab\u86c7\u7130\u7bad\u6240\u5f15\u53d1\u7684\u706b\u70e7\u4e86\u8d77\u6765\u3002\u5510\u6653\u6f9c\u6df1\u77e5\u53f6\u6a2a\u6ce2\u7684\u6b66\u529f\u7edd\u4e0d\u5728\u51af\u745b\u4e4b\u4e0b\uff0c\u800c\u90a3\u5510 \u8001\u5934\u66f4\u96be\u5bf9\u4ed8\uff0c\u51af\u745b\u867d\u7136\u5e78\u80dc\uff0c\u8ffd\u51fa\u53bb\u5fc5\u5b9a\u5403\u4e8f\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u6551\u4eba\u6551\u706b\u8981\u7d27\uff01\u201d\u51af\u745b\u5fc3\u4e2d \u4e00\u51db\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u6279\u8d3c\u7537\u5973\uff0c\u518d\u6562\u6765\u6270\u6211\u6768\u516c\u516c\u4f4f\u5b85\uff0c\u6211\u5251\u4e0b\u51b3\u4e0d\u7559\u60c5\uff01\u201d \u3000\u3000\u5954\u56de\u6765\u65f6\uff0c\u6768\u5bb6\u7684\u4eba\u5df2\u7eb7\u51fa\u6551\u706b\u3002\u51af\u745b\u6025\u5fd9\u8dd1\u8fdb\u540e\u5802\uff0c\u5165\u4e86\u9759\u5ba4\uff0c\u53ea\u89c1\u6768\u4ef2\u82f1\u53cc\u817f\u80bf\u5f97 \u5982\u6c34\u6876\u822c\u5927\uff0c\u6768\u67f3\u9752\u4f4e\u5934\u996e\u6ce3\uff0c\u51af\u745b\u597d\u4e0d\u96be\u8fc7\uff0c\u8bf4\u9053\uff1a\u201c\u6768\u516c\u516c\uff0c\u90fd\u662f\u6211\u7d2f\u4e86\u4f60\u4e86\u3002\u201d\u6768\u4ef2 \u82f1\u6781\u4e3a\u5947\u5f02\uff0c\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u5462\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u7ed9\u6211\u6253\u8dd1\u4e86\uff01\u201d\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u662f\u771f\u7684 \u5417\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u771f\uff01\u201d\u6768\u4ef2\u82f1\u5927\u559c\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u8fd9\u56de\u5168\u9760\u4f60\u4fdd\u5168\u4e86\u6211\u6768\u5bb6\u7684\u5a01\u540d \u4e86\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5510\u6653\u6f9c\u4e5f\u5df2\u8fdb\u6765\u3002\u6768\u4ef2\u82f1\u7b11\u9053\uff1a\u201c\u6768\u5bb6\u603b\u7b97\u672a\u5403\u8fc7\u8d25\u4ed7\uff0c\u6211\u6b7b\u4e5f\u7791\u76ee\u4e86\u3002\u201d \u5510\u6653\u6f9c\u89c1\u4ed6\u53cc\u817f\u53c8\u80bf\u53c8\u9ed1\uff0c\u6025\u9053\uff1a\u201c\u963f\u745b\uff0c\u5feb\u628a\u78a7\u7075\u4e39\u62ff\u51fa\u6765\uff01\u201d \u3000\u3000\u6768\u4ef2\u82f1\u9053\uff1a\u201c\u8fd9\u5510\u5bb6\u7684\u6697\u5668\uff0c\u6700\u4e3a\u6b79\u6bd2\uff0c\u9664\u4e86\u4ed6\u4eec\u7684\u672c\u95e8\u89e3\u836f\uff0c\u4efb\u8c01\u4e5f\u4e0d\u80fd\u6551\u6cbb\u3002\u8fd9\u78a7 \u7075\u4e39\u53ef\u4ee5\u6cbb\u522b\u7684\u5185\u4f24\u6697\u635f\uff0c\u522b\u7cdf\u584c\u4e86\u5427\u3002\u201d\u51af\u745b\u54ea\u91cc\u80af\u4f9d\uff0c\u4ecd\u662f\u8981\u4ed6\u670d\u4e86\uff0c\u670d\u540e\u679c\u7136\u7a0d\u597d\u4e00 \u70b9\uff0c\u6bd2\u6c14\u4e0d\u518d\u4e0a\u5347\uff0c\u4f46\u53cc\u817f\u9ebb\u6728\u4e0d\u7075\uff0c\u6240\u4e2d\u7684\u5267\u6bd2\u4ecd\u662f\u672a\u80fd\u6d88\u89e3\u3002\u6768\u67f3\u9752\u5510\u6653\u6f9c\u51af\u745b\u4e09\u4eba\u6025 \u5f97\u56e2\u56e2\u8f6c\uff0c\u6beb\u65e0\u529e\u6cd5\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u5438\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6b7b\u751f\u6709\u547d\uff0c\u6211\u4e0d\u6025\uff0c\u4f60\u4eec\u66ff\u6211\u6025\u4ec0\u4e48\uff1f\u4f55\u51b5\u5b83\u4e5f\u672a\u5fc5\u80fd\u81f4\u6211\u6b7b \u547d\u3002\u8fd9\u78a7\u7075\u4e39\u867d\u975e\u5bf9\u75c7\u89e3\u836f\uff0c\u4f46\u53ea\u8981\u6bd2\u6c14\u4e0d\u518d\u4e0a\u5347\uff0c\u90a3\u6211\u5c31\u7edd\u6b7b\u4e0d\u4e86\u3002\u201d \u3000\u3000\u51af\u745b\u9053\uff1a\u201c\u65e2\u7136\u53ea\u6709\u4ed6\u672c\u95e8\u89e3\u836f\u624d\u80fd\u6551\u6cbb\uff0c\u90a3\u4e48\u6211\u4e0e\u5510\u53d4\u53d4\u53bb\u8ffd\u4ed6\u4eec\u4fbf\u662f\u3002\u201d\u6768\u4ef2\u82f1 \u9053\uff1a\u201c\u4ed6\u4eec\u65e2\u4e0e\u97e9\u91cd\u5c71\u7684\u5a46\u72fc\u540c\u6765\uff0c\u60f3\u5fc5\u8fd8\u6709\u5927\u5185\u597d\u624b\u5728\u540e\uff0c\u4f60\u4eec\u53ea\u6709\u4e24\u4eba\uff0c\u5982\u4f55\u53ef\u53bb\uff1f\u201d \u51af\u745b\u5fc3\u60f3\uff1a\u6211\u4e0e\u5510\u53d4\u53d4\u62fc\u6b7b\u65e0\u59a8\uff0c\u4f46\u4f4f\u5b85\u7a7a\u865a\uff0c\u82e5\u4ed6\u4eec\u6709\u4eba\u4e58\u865a\u4fb5\u88ad\uff0c\u90a3\u5c82\u4e0d\u662f\u5bb3\u4e86\u516c\u516c\u4e0e \u59d1\u59d1\u6027\u547d\u3002\u56e0\u6b64\u867d\u7136\u7126\u6025\u4e07\u5206\uff0c\u5374\u4e5f\u4e0d\u6562\u79bb\u5f00\u6768\u5b85\u3002 \u3000\u3000\u5176\u5b9e\u6768\u4ef2\u82f1\u4e0e\u51af\u745b\u90fd\u731c\u9519\u4e86\u3002\u5141\u796f\u6709\u628a\u67c4\u5728\u5510\u6653\u6f9c\u624b\u91cc\uff0c\u4e00\u5e74\u4e4b\u5185\uff0c\u7edd\u4e0d\u4f1a\u6d3e\u5927\u5185\u9ad8\u624b \u524d\u6765\u6355\u6349\uff0c\u8fd9\u6b21\u53f6\u6a2a\u6ce2\u5e26\u5510\u5bb6\u7236\u5973\u524d\u6765\uff0c\u4e0d\u8fc7\u662f\u5979\u4e2a\u4eba\u7684\u884c\u52a8\u3002\u800c\u5510\u91d1\u5cf0\u4e5f\u53ea\u662f\u4e3a\u5973\u5a7f\u62a5\u4ec7 \u800c\u6765\uff0c\u5e76\u975e\u6e05\u5ef7\u7684\u8d70\u72d7\u3002 \u3000\u3000\u5510\u91d1\u5cf0\u6570\u5341\u5e74\u5a01\u540d\uff0c\u88ab\u4e00\u4e2a\u5973\u5b69\u5b50\u6740\u5f97\u60e8\u8d25\uff0c\u53c8\u7f9e\u53c8\u6c14\uff0c\u9003\u51fa\u6768\u5bb6\u4e4b\u540e\uff0c\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u53f6 \u6a2a\u6ce2\u4e0e\u5510\u8d5b\u82b1\u90fd\u4e0d\u6562\u9017\u4ed6\u8bf4\u8bdd\u3002\u8d70\u4e86\u5341\u591a\u91cc\uff0c\u5510\u91d1\u5cf0\u7a81\u7136\u95ee\u9053\uff1a\u201c\u771f\u662f\u8fd9\u4e2a\u5973\u5b69\u5b50\u5417\uff1f\u4f60\u6709 \u6ca1\u6709\u770b\u9519\uff1f\u201d\u4ed6\u60f3\u8d77\u53f6\u6a2a\u6ce2\u66fe\u544a\u8bc9\u4ed6\uff1a\u738b\u6556\u5f53\u65e5\u66fe\u5267\u6597\u534a\u5929\uff0c\u800c\u4e14\u662f\u4e2d\u4e86\u90a3\u5973\u5b69\u5b50\u7684\u98de\u5200\u624d \u88ab\u6740\u7684\u3002\u4e0d\u7981\u8d77\u4e86\u7591\u5fc3\uff0c\u60f3\u9053\uff1a\u201c\u738b\u6556\u672c\u4e8b\u8fd8\u5728\u8d5b\u82b1\u4e4b\u4e0b\uff0c\u82e5\u7136\u662f\u4eca\u65e5\u548c\u6211\u6076\u6597\u7684\u90a3\u4e2a\u4e2b \u5934\uff0c\u53ea\u6015\u4e0d\u5230\u4e09\u62db\u5c31\u8981\u9001\u547d\uff0c\u4f55\u987b\u534a\u65e5\u4e4b\u4e45\uff1f\u800c\u4e14\u90a3\u5973\u5b69\u5b50\u6240\u4f7f\u7684\u6697\u5668\u4e5f\u5e76\u4e0d\u662f\u98de\u5200\u3002 \u3000\u3000\u53f6\u6a2a\u6ce2\u4e5f\u8d77\u4e86\u7591\u5fc3\uff0c\u51af\u7433\u5f53\u5e74\u5728\u56db\u7687\u5e9c\u65f6\uff0c\u53f6\u6a2a\u6ce2\u4e5f\u66fe\u4f20\u5979\u6280\u827a\uff0c\u5bf9\u5979\u7684\u672c\u9886\uff0c\u6781\u4e3a\u719f \u6089\u3002\u540e\u6765\u5728\u9648\u7559\u9644\u8fd1\u76f8\u9047\uff0c\u51af\u7433\u7684\u6280\u827a\u867d\u7136\u5927\u8fdb\uff0c\u4e5f\u8fd8\u4e0d\u662f\u5979\u7684\u5bf9\u624b\u3002\u4f46\u4eca\u65e5\u770b\u51af\u745b\u7684\u6b66 \u529f\uff0c\u51b3\u4e0d\u5728\u5979\u4e4b\u4e0b\uff0c\u53f6\u6a2a\u6ce2\u5fc3\u60f3\uff0c\u76f8\u9694\u4e0d\u5230\u4e09\u6708\uff0c\u7eb5\u6709\u795e\u4ed9\u4f20\u6388\uff0c\u8fdb\u6b65\u4e5f\u4e0d\u80fd\u5982\u6b64\u795e\u901f\u3002 \u3000\u3000\u53f6\u6a2a\u6ce2\u6b63\u5728\u7591\u5fc3\uff0c\u88ab\u5510\u91d1\u5cf0\u4e00\u95ee\uff0c\u6c89\u541f\u826f\u4e45\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u76f8\u8c8c\u6781\u4f3c\uff0c\u6b66\u529f\u4e0d\u4f3c\u3002\u6211\u4e5f \u4e0d\u77e5\u662f\u4f55\u9053\u7406\uff1f\u201d\u5510\u91d1\u5cf0\u62cd\u638c\u8bf4\u9053\uff1a\u201c\u7cdf\u4e86\uff0c\u82e5\u7136\u4e0d\u662f\uff0c\u90a3\u5c82\u4e0d\u767d\u767d\u5bb3\u4e86\u6768\u4ef2\u82f1\u7684\u6027\u547d\u3002\u6740 \u738b\u6556\u7684\u4ec7\u4eba\u662f\u8c01\uff0c\u4ee5\u540e\u6211\u4eec\u518d\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\uff0c\u6768\u4ef2\u82f1\u7684\u6027\u547d\uff0c\u6211\u5374\u4e0d\u80fd\u8ba9\u4ed6\u5728\u6b7b\u5728\u6211\u7684\u624b \u4e0a\u3002\u201d \u3000\u3000\u5510\u8d5b\u82b1\u4e00\u6014\uff0c\u9053\uff1a\u201c\u7239\uff0c\u90a3\u4f60\u6b32\u5982\u4f55\uff1f\u201d\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u9001\u89e3\u836f\u7ed9\u4ed6\uff01\u201d\u5510\u8d5b\u82b1\u7ed9\u51af\u745b\u7684 \u98de\u8292\u523a\u7a7f\u8f6f\u9aa8\uff0c\u867d\u65e0\u5927\u788d\uff0c\u6068\u5728\u5fc3\u5934\uff0c\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u7eb5\u7136\u4e0d\u662f\u8fd9\u4e2a\u4eba\u6240\u4e3a\uff0c\u4f46\u6211\u4eec\u90fd\u5403\u4e86\u5979 \u7684\u5927\u4e8f\uff0c\u8fd9\u7cb1\u5b50\u662f\u7ed3\u5b9a\u4e86\u3002\u5982\u4f55\u597d\u7ed9\u4ed6\u518d\u9001\u89e3\u836f\u3002\u201d\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u9001\u7ed9\u5979\uff0c\u662f\u9001\u7ed9\u6768 \u4ef2\u82f1\u3002\u201d\u5510\u8d5b\u82b1\u9053\uff1a\u201c\u6768\u4ef2\u82f1\u548c\u5979\u8fd8\u4e0d\u662f\u4e00\u6837\uff1f\u6211\u4eec\u9001\u89e3\u836f\u7ed9\u4ed6\uff0c\u603b\u662f\u5148\u5411\u4ed6\u4eec\u4f4e\u5934\uff0c\u8fd9\u5c82 \u4e0d\u6298\u4e86\u6211\u4eec\u5510\u5bb6\u7684\u5a01\u98ce\u3002\u201d \u3000\u3000\u5510\u91d1\u5cf0\u53ea\u6709\u8fd9\u4e2a\u5973\u513f\uff0c\u5f88\u542c\u5979\u7684\u8bf4\u8bdd\u3002\u60f3\u60f3\u4e5f\u4e0d\u65e0\u9053\u7406\u3002\u4fbf\u4e0d\u518d\u8a00\u8bed\uff0c\u7ee7\u7eed\u524d\u884c\u3002\u53ef\u662f \u5fc3\u4e2d\u8d8a\u6765\u8d8a\u662f\u4e0d\u5b89\uff0c\u84e6\u7136\u7ad9\u4f4f\u5bf9\u5973\u513f\u9053\uff1a\u201c\u597d\uff0c\u6211\u53e6\u6709\u4e3b\u610f\u4e86\uff01\u201d \u3000\u3000\u5510\u8d5b\u82b1\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e3b\u610f\uff1f\u201d\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u5fc5\u4eb2\u9001\u89e3\u836f\u7ed9\u4ed6\uff0c\u6258\u4eba\u8f6c\u9001\u4fbf\u662f\u3002\u6768 \u8001\u5934\u5728\u6b66\u6797\u4e2d\u4e5f\u7b97\u4e00\u7b49\u4eba\u7269\uff0c\u6211\u4eec\u867d\u4e0d\u6015\u4ed6\uff0c\u4f46\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u82e5\u77e5\u4ed6\u662f\u6b7b\u5728\u6211\u4eec\u624b\u4e0a\uff0c\u9ebb\u70e6 \u5374\u514d\u4e0d\u4e86\uff01\u201d\u5510\u8d5b\u82b1\u4e00\u60f3\uff0c\u7236\u4eb2\u7684\u987e\u8651\u786e\u975e\u865a\u8a00\uff0c\u4e0d\u6562\u62e6\u963b\u3002\u5510\u91d1\u5cf0\u7acb\u8fd1\u53eb\u4f4f\u4e00\u4e2a\u8fc7\u8def\u884c \u4eba\uff0c\u62ff\u51fa\u4e00\u4e24\u94f6\u5b50\uff0c\u8bf7\u4ed6\u4ee3\u9001\u4e1c\u897f\u7ed9\u6768\u4ef2\u82f1\u3002\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u6768\u8001\u7237\u5b50\u8fd9\u51e0\u53bf\u7684\u4eba\u8c01\u4e0d\u94a6\u4f69\u3002 \u94f6\u5b50\u4f60\u6536\u56de\u53bb\u5427\uff0c\u6211\u4ee3\u4f60\u9001\u5230\u4fbf\u662f\u3002\u201d\u53d6\u4e86\u89e3\u836f\u7acb\u5373\u5954\u53bb\u6768\u5bb6\u3002 \u3000\u3000\u504f\u504f\u8fd9\u4eba\u662f\u4e2a\u80d6\u5b50\uff0c\u8dd1\u4e86\u534a\u91cc\uff0c\u4fbf\u89c9\u6c14\u5598\u3002\u4ed6\u53c8\u4e0d\u77e5\u89e3\u836f\u91cd\u8981\uff0c\u4ed6\u60f3\u4eb2\u624b\u4ea4\u7ed9\u4ed6\u6240\u5c0a\u656c \u7684\u4eba\uff0c\u4e0d\u80af\u6258\u5176\u4ed6\u5c0f\u4f19\u5b50\u4ee3\u9001\u3002\u884c\u884c\u6b47\u6b47\uff0c\u8d70\u5230\u6768\u5bb6\uff0c\u5df2\u662f\u638c\u706f\u65f6\u5206\u3002 \u3000\u3000\u8fd9\u65f6\u6768\u4ef2\u82f1\u7684\u53cc\u817f\u5df2\u5b8c\u5168\u9ebb\u6728\uff0c\u7528\u5200\u523a\u817f\uff0c\u653e\u51fa\u6bd2\u8840\uff0c\u4e5f\u4e0d\u89c9\u75db\u3002\u8fd9\u4eba\u6c14\u5598\u5598\u7684\u6572\u95e8\u8dd1 \u8fdb\uff0c\u53eb\u9053\uff1a\u201c\u6768\u8001\u7237\u5b50\uff0c\u6709\u4eba\u9001\u4e1c\u897f\u7ed9\u4f60\u3002\u201d\u6768\u4ef2\u82f1\u4e00\u77a7\uff0c\u662f\u9547\u4e0a\u719f\u4eba\uff0c\u7b11\u9053\uff1a\u201c\u674e\u5927\u80d6\uff0c \u8f9b\u82e6\u4f60\u4e86\u3002\u662f\u8c01\u6258\u4f60\u9001\u6765\u7684\u5440\uff1f\u201d\u90a3\u4eba\u89c1\u6768\u4ef2\u82f1\u8fd9\u4e2a\u6a21\u6837\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u59d3\u5510 \u7684\u5ba2\u4eba\u6258\u6211\u9001\u7684\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u8fd9\u4e00\u559c\u975e\u540c\u5c0f\u53ef\uff0c\u6768\u67f3\u9752\u9053\uff1a\u201c\u90a3\u8001\u5934\u5c45\u7136\u8fd8\u6709\u8fd9\u6837\u597d\u5fc3\uff1f\u201d\u7591\u90a3\u89e3\u836f\u662f\u5047\u3002\u6768 \u4ef2\u82f1\u4e00\u9762\u53eb\u5bb6\u4eba\u5907\u9a6c\u9001\u90a3\u80d6\u5b50\u56de\u5bb6\uff0c\u4e00\u9762\u62c6\u5f00\u89e3\u836f\uff0c\u770b\u4e86\u7528\u6cd5\uff0c\u7acb\u523b\u5185\u670d\u5916\u6577\uff0c\u6b63\u8272\u5bf9\u6768\u67f3 \u9752\u8bf4\u9053\uff1a\u201c\u5510\u8001\u4e8c\u867d\u7136\u6709\u65f6\u884c\u4e8b\u4e56\u8c2c\uff0c\u4f46\u51ed\u4ed6\u8eab\u4efd\uff0c\u5c82\u8096\u9001\u5047\u836f\u5bb3\u4eba\u3002\u201d\u670d\u4e86\u4e4b\u540e\uff0c\u80bf\u6bd2\u679c \u7136\u6e10\u6d88\uff0c\u4f46\u56e0\u65f6\u95f4\u9694\u5f97\u592a\u4e45\uff0c\u80bf\u6d88\u4e4b\u540e\uff0c\u4e24\u817f\u4ecd\u7136\u9ebb\u6728\u4e0d\u7075\u3002 \u3000\u3000\u8fc7\u4e86\u4e09\u65e5\uff0c\u6bd2\u6027\u5316\u51c0\uff0c\u53ef\u662f\u6768\u4ef2\u82f1\u817f\u8840\u7ba1\u5df2\u7ecf\u786c\u5316\uff0c\u8d70\u8def\u4e0d\u80fd\u7528\u529b\uff0c\u4e00\u62d0\u4e00\u62d0\u7684\uff0c\u8fd8\u8981 \u6276\u7740\u5899\u58c1\u800c\u884c\uff0c\u770b\u6765\u5df2\u662f\u6b8b\u5e9f\u5b9a\u4e86\u3002 \u3000\u3000\u5bb6\u4eba\u548c\u51af\u745b\u7b49\u5f53\u7136\u96be\u8fc7\uff0c\u4e0d\u8fc7\u6768\u4ef2\u82f1\u5f97\u4ee5\u62fe\u56de\u4e00\u6761\u6027\u547d\uff0c\u4e5f\u7b97\u4e0d\u5e78\u4e2d\u4e4b\u5e78\uff0c\u6768\u67f3\u9752\u5fc3\u4e2d \u6697\u6697\u57cb\u6028\u51af\u745b\uff0c\u8ba4\u4e3a\u7236\u4eb2\u7684\u6b8b\u5e9f\uff0c\u90fd\u662f\u56e0\u5979\u800c\u8d77\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u51af\u745b\u53c8\u5077\u5230\u5510\u6653\u6f9c\u623f\u4e2d\uff0c\u4ed6\u4eec\u5728\u8fd9\u4e09\u65e5\u4e4b\u4e2d\uff0c\u8863\u4e0d\u89e3\u5e26\uff0c\u5728\u6768\u4ef2\u82f1\u75c5\u584c\u4e4b\u65c1\u770b \u62a4\uff0c\u672a\u66fe\u7814\u4e60\u6b66\u529f\u3002 \u3000\u3000\u4e5f\u662f\u5408\u5f53\u6709\u4e8b\uff0c\u8fd9\u4e00\u665a\u6768\u67f3\u9752\u534a\u591c\u9192\u6765\uff0c\u60f3\u5230\u7236\u4eb2\u623f\u4e2d\u4e00\u770b\uff0c\u8d70\u8fc7\u56de\u5eca\uff0c\u5ffd\u89c1\u5510\u6653\u6f9c\u623f \u4e2d\u5c1a\u6709\u706f\u706b\uff0c\u653e\u8f7b\u811a\u6b65\uff0c\u6084\u6084\u8d70\u8fd1\uff0c\u8d34\u8033\u4e00\u542c\uff0c\u5ffd\u542c\u5f97\u51af\u745b\u548c\u5510\u6653\u6f9c\u4f4e\u4f4e\u8c08\u7b11\u4e4b\u58f0\u3002 \u3000\u3000\u6768\u67f3\u9752\u8fd9\u4e00\u6012\u975e\u540c\u5c0f\u53ef\uff0c\u706b\u6c14\u4e0a\u51b2\uff0c\u54ea\u8fd8\u628a\u51af\u745b\u7684\u672c\u9886\u653e\u5728\u5fc3\u4e0a\u3002\u547c\u7684\u4e00\u638c\uff0c\u51fb\u788e\u7a97 \u95e8\uff0c\u6233\u6307\u9a82\u9053\uff1a\u201c\u8d31\u4e2b\u5934\uff0c\u597d\u4e0d\u8981\u8138\uff01\u201d \u3000\u3000\u51af\u745b\u6115\u7136\u8d77\u7acb\uff0c\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60\u542c\u6211\u8bf4\uff01\u201d\u6768\u67f3\u9752\u8fd9\u65f6\u5df2\u5931\u4e86\u7406\u6027\uff0c\u4e00\u624b\u4fbf\u6293\u51af\u745b\u5934 \u53d1\uff0c\u5927\u58f0\u9a82\u9053\uff1a\u201c\u4f60\u8fd8\u8bf4\u4ec0\u4e48\uff1f\u4e09\u66f4\u534a\u591c\uff0c\u4f60\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f\u54fc\uff0c\u597d\u4e0d\u8981\u8138\uff01\u201d\u51af\u745b\u970d\u5730\u70b9 \u5934\uff0c\u907f\u8fc7\u6768\u67f3\u9752\u4e00\u6293\uff0c\u6768\u67f3\u9752\u5140\u662f\u54ed\u9a82\u4e0d\u4f11\uff0c\u52a8\u624b\u518d\u6293\uff0c\u51af\u745b\u52c3\u7136\u5927\u6012\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f \u4ec0\u4e48\u4eba\uff1f\u201d\u6768\u67f3\u9752\u9a82\u9053\uff1a\u201c\u6211\u5f53\u4f60\u662f\u4e2a\u5077\u6c49\u5b50\u7684\u5c0f\u8d31\u4eba\uff01\u201d\u8bdd\u521a\u51fa\u53e3\uff0c\u51af\u745b\u624b\u638c\u4e00\u626c\uff0c\u62cd\u7684 \u4e00\u58f0\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u6253\u4e86\u5979\u4e00\u4e2a\u8033\u5149\u3002\u6768\u67f3\u9752\u75db\u5f97\u5012\u5730\u6eda\u53eb\uff0c\u51af\u745b\u5df2\u7ecf\u51b2\u51fa\u623f\u95e8\u53bb\u4e86\uff0c\u51af\u745b\u6027\u60c5 \u7eaf\u771f\u521a\u70c8\uff0c\u672c\u6765\u4e0d\u662f\u4e00\u4e2a\u80fd\u53d7\u4eba\u6c14\u7684\u59d1\u5a18\uff0c\u53ea\u56e0\u4e3a\u4e86\u53d4\u53d4\uff0c\u624d\u5fcd\u4e86\u8fd9\u4e48\u4e9b\u65f6\u65e5\u3002\u5979\u6253\u4e86\u672a\u6765 \u7684\u5a76\u5a76\u4e00\u4e2a\u8033\u5149\uff0c\u4ea6\u4e0d\u540e\u6094\u3002\u56de\u5230\u623f\u4e2d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5510\u53d4\u53d4\u5bf9\u672c\u95e8\u7684\u5185\u529f\u7a8d\u8981\uff0c\u5df2\u5168\u9886 \u4f1a\u3002\u4eca\u540e\u53ea\u8981\u80af\u4e0b\u82e6\u529f\u4fbf\u884c\u4e86\u3002\u4f46\u5185\u529f\u662f\u5426\u80fd\u52a9\u4ed6\u89e3\u6d88\u6bd2\u6027\uff0c\u5374\u8fd8\u662f\u672a\u53ef\u77e5\u4e4b\u6570\u3002\u6211\u4f55\u4e0d\u5230 \u4eac\u57ce\u4e00\u8d70\uff0c\u62fc\u4e86\u6027\u547d\uff0c\u4e5f\u5f97\u7ed9\u4ed6\u53d6\u5230\u89e3\u836f\uff0c\u4ee5\u62a5\u4ed6\u76f8\u6551\u4e4b\u6069\u3002\u81f3\u4e8e\u8fd9\u4e2a\u201c\u5a76\u5a76\u201d\uff0c\u4ee5\u540e\u6211\u6c38 \u4e0d\u7406\u5979\uff0c\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u201d\u5979\u60f3\u4e86\u4fbf\u884c\uff0c\u9a6c\u4e0a\u5199\u4e86\u4e00\u5c01\u4e66\u4fe1\uff0c\u53eb\u4ed6\u5728\u4e00\u5e74\u4e4b\u5185\u4e0d\u8981\u79bb\u5f00\u6768 \u5bb6\uff0c\u5f85\u5979\u53d6\u56de\u89e3\u836f\u3002\u5e76\u53eb\u4ed6\u4ee3\u5411\u6768\u516c\u516c\u8d54\u7f6a\uff0c\u5199\u597d\u4e4b\u540e\uff0c\u518d\u5230\u5510\u6653\u6f9c\u623f\u4e2d\uff0c\u5510\u6653\u6f9c\u548c\u6768\u67f3\u9752 \u90fd\u5df2\u4e0d\u5728\u3002\u51af\u745b\u628a\u4fe1\u7528\u7aef\u781a\u538b\u5728\u4ed6\u7684\u4e66\u684c\u4e0a\uff0c\u8ff3\u81ea\u8d70\u4e86\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u542c\u5f97\u5510\u6653\u6f9c\u623f\u4e2d\u5435\u95f9\uff0c\u53eb\u5bb6\u4eba\u628a\u5510\u6653\u6f9c\u548c\u6768\u67f3\u9752\u5524\u6765\uff0c\u95ee\u660e\u539f\u59d4\uff0c\u628a\u6768\u67f3\u9752\u9a82\u5f97 \u72d7\u8840\u6dcb\u5934\uff0c\u6768\u67f3\u9752\u54ed\u9053\uff1a\u201c\u7239\uff0c\u4f60\u603b\u5e2e\u7740\u5916\u4eba\uff0c\u4f60\u4e5f\u4e0d\u77e5\u4ed6\u4eec\u662f\u591a\u4e48\u4eb2\u70ed\uff01\u201d\u6768\u4ef2\u82f1\u62cd\u5e8a\u5927 \u9a82\uff1a\u201c\u4f60\u8fd8\u8bf4\uff0c\u4f60\u8fd8\u8bf4\u3002\u5979\u662f\u4e00\u4e2a\u5c0f\u5b69\u5b50\uff0c\u4f1a\u62a2\u4f60\u7684\u6c49\u5b50\u4e0d\u6210\uff01\u4f60\u4e0d\u8981\u8138\uff0c\u8fd8\u80e1\u9a82\u522b\u4eba\uff01\u201d \u6768\u67f3\u9752\u4ece\u672a\u53d7\u7236\u4eb2\u8fd9\u6837\u9a82\u8fc7\uff0c\u6492\u8d56\u54ed\u9053\uff1a\u201c\u5c0f\u5b69\u5b50\uff1f\u5341\u516d\u4e03\u5c81\u7684\u59d1\u5a18\u8fd8\u662f\u5c0f\u5b69\u5b50\uff1f\u201d\u6768\u4ef2\u82f1 \u6376\u80f8\u53eb\u9053\uff1a\u201c\u90fd\u662f\u6211\u4e0d\u597d\uff0c\u7eb5\u574f\u4e86\u4f60\u8fd9\u4e2a\u4e2b\u5934\uff0c\u6eda\u51fa\u53bb\uff01\u201d\u5510\u6653\u6f9c\u5c34\u5c2c\u4e4b\u6781\uff0c\u4e0a\u524d\u6276\u9053\uff1a \u201c\u7239\uff0c\u4f60\u522b\u751f\u6c14\uff01\u201d\u6768\u67f3\u9752\u9762\u8272\u7070\u767d\uff0c\u54ed\u54ed\u557c\u557c\uff0c\u8dd1\u4e86\u51fa\u53bb\uff0c\u8d8a\u60f3\u8d8a\u6068\uff0c\u8dd1\u5165\u5510\u6653\u6f9c\u623f\u4e2d\uff0c \u5c06\u4e66\u7c4d\u4e71\u6454\uff0c\u53d1\u73b0\u684c\u4e0a\u51af\u745b\u7559\u4e0b\u7684\u4fe1\uff0c\u5fc3\u9053\uff1a\u201c\u54fc\uff0c\u8fd8\u6562\u5077\u5077\u9001\u4fe1\u6765\u54e9\uff01\u201d\u62c6\u5f00\u4e00\u770b\uff0c\u89c1\u4e0a \u9762\u8bf4\u4ec0\u4e48\u89e3\u836f\u4e4b\u4e8b\uff0c\u83ab\u540d\u5176\u5999\uff0c\u4e00\u628a\u6495\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u529d\u4e86\u597d\u4e45\uff0c\u6768\u4ef2\u82f1\u706b\u6c14\u6e10\u6d88\uff0c\u6d41\u6cea\u53f9\u9053\uff1a\u201c\u90fd\u662f\u5979\u6bcd\u4eb2\u6b7b\u5f97\u65e9\uff0c\u8981\u4e0d\u7136\u4e5f\u4e0d\u4f1a\u5982 \u6b64\u3002\u201d\u5510\u6653\u6f9c\u4e00\u9635\u5fc3\u9178\u3002\u6768\u4ef2\u82f1\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u6211\u5e73\u751f\u6700\u91cd\u8a00\u8bfa\uff0c\u6211\u672c\u6765\u7b54\u5e94\u8fc7\u4f60\u4ee3\u627e \u745b\u59d1\u5a18\u7684\u59b9\u59b9\uff0c\u53ea\u662f\u6211\u5982\u4eca\u6b8b\u5e9f\uff0c\u8d70\u4e0d\u52a8\u4e86\u3002\u4f60\u8d70\u4e00\u8d9f\u5427\u3002\u6211\u4e00\u9762\u6258\u4eba\u4ee3\u4e3a\u4eff\u95ee\u540d\u533b\uff0c\u4f60\u5728 \u5916\u9762\u4e5f\u53ef\u81ea\u5bfb\u89e3\u836f\u3002\u4e00\u4e3e\u4e24\u5f97\uff0c\u5c82\u4e0d\u751a\u597d\u3002\u800c\u4e14\u7ecf\u8fc7\u4e86\u8fd9\u573a\u5927\u95f9\uff0c\u4f60\u79bb\u5f00\u4e4b\u540e\uff0c\u6211\u53ef\u4ee5\u597d\u597d \u7ba1\u6559\u9752\u513f\uff0c\u5f85\u4f60\u56de\u672a\u4e4b\u65f6\uff0c\u4e8b\u60c5\u5df2\u6210\u8fc7\u53bb\uff0c\u4fbf\u597d\u8bf4\u8bdd\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u53ea\u662f\u4f60\u8001\u4eba\u5bb6\u2014\u2014\u201d \u6768\u4ef2\u82f1\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u4e3a\u6211\u62c5\u5fc3\uff0c\u6b66\u6797\u4e2d\u7684\u670b\u53cb\u82e5\u7136\u77e5\u6211\u53d7\u4f24\uff0c\u4e00\u5b9a\u4f1a\u6765\u770b\u6211\u3002\u4f60\u8fd8\u6015\u6ca1\u4eba\u5b88 \u62a4\u6211\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u90a3\u4e5f\u8981\u7b49\u6b66\u6797\u7684\u670b\u53cb\u6765\u4e86\u518d\u8bf4\uff1f\u201d \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u5510\u6653\u6f9c\u77e5\u9053\u4e86\u51af\u745b\u51fa\u8d70\u7684\u6d88\u606f\uff0c\u66f4\u662f\u5fc3\u5fe7\uff0c\u6c14\u5728\u5fc3\u5934\uff0c\u548c\u6768\u67f3\u9752\u89c1\u9762\u4e5f\u4e0d \u62db\u547c\u3002\u6768\u67f3\u9752\u672c\u60f3\u95ee\u4ed6\u8981\u53d6\u4ec0\u4e48\u89e3\u836f\uff0c\u89c1\u4ed6\u5982\u6b64\uff0c\u4e5f\u4e0d\u8bf4\u4e86\u3002\u5230\u4e86\u4e2d\u5348\uff0c\u5f97\u4e86\u6d88\u606f\u7684\u6b66\u5e08\u679c \u7136\u9646\u7eed\u90fd\u6765\u95ee\u5019\u3002\u5510\u6653\u6f9c\u653e\u4e0b\u4e86\u5fc3\uff0c\u5f85\u6768\u67f3\u9752\u5165\u5979\u7236\u4eb2\u7684\u623f\u4e2d\u62db\u547c\u5ba2\u4eba\u4e4b\u9645\uff0c\u6084\u6084\u51fa\u8d70\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u6708\uff0c\u5510\u6653\u6f9c\u5df2\u51fa\u73b0\u5728\u6d4e\u5357\u5e02\u4e0a\u3002\u6d4e\u5357\u5e02\u4e0a\u6b63\u4f20\u8bf4\u7eb7\u7eb7\uff0c\u8bf4\u662f\u6709\u4e2a\u7f8e\u82e5\u5929\u4eba\u7684\u5c0f\u59d1 \u5a18\uff0c\u5728\u5e02\u4e2d\u9152\u8086\u4f24\u4e86\u5f20\u5de1\u629a\u7684\u513f\u5b50\u548c\u629a\u8859\u7684\u6559\u5934\uff0c\u516c\u5dee\u6b63\u8981\u6349\u5979\u3002\u5510\u6653\u6f9c\u542c\u4e86\u5927\u5403\u4e00\u60ca\uff0c\u5fc3 \u9053\uff1a\u201c\u8fd9\u5c0f\u59d1\u5a18\u4e0d\u662f\u51af\u745b\u4fbf\u5b9a\u662f\u51af\u7433\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u5728\u6d4e\u5357\u5e02\u8bbf\u5bfb\u51e0\u65e5\uff0c\u6beb\u65e0\u6d88\u606f\u3002\u4e00\u65e5\u5ffd\u89c1\u57ce\u95e8\u5927\u5f00\uff0c\u51e0\u9a7e\u5341\u5206\u534e\u4e3d\u7684\u9a6c\u8f66\uff0c\u4e0a\u9970 \u9ec4\u76d6\uff0c\u524d\u6709\u4eea\u4ed7\uff0c\u540e\u6709\u968f\u4ece\uff0c\u524d\u547c\u540e\u62e5\uff0c\u76f4\u5954\u629a\u8859\u3002\u5510\u6653\u6f9c\u597d\u4e0d\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u662f\u7687\u5ba4 \u4e2d\u4eba\u4e48\uff1f\u6324\u5230\u4eba\u4e1b\u4e2d\u4e00\u671b\uff0c\u5ffd\u89c1\u4e2d\u95f4\u90a3\u8f86\u9a6c\u8f66\uff0c\u6709\u4eba\u63ed\u5f00\u8f66\u5e18\uff0c\u8eab\u62ab\u7ee3\u888d\uff0c\u9876\u6234\u73e0\u8c6b\uff0c\u7f28\u7edc \u7eb7\u5782\uff0c\u6026\u7136\u738b\u8005\u6253\u626e\uff0c\u5f97\u610f\u6d0b\u6d0b\uff0c\u5411\u7740\u70ed\u95f9\u7684\u4eba\u62db\u624b\u3002\u5510\u6653\u6f9c\u89c1\u4e86\uff0c\u53c8\u662f\u4e00\u51c9\uff0c\u6b64\u4eba\u975e\u4ed6\uff0c \u6b63\u662f\u5728\u5c71\u4e1c\u6d77\u5916\u79f0\u738b\u7684\u9c7c\u4eae\uff01 \u3000\u3000\u5510\u6653\u6f9c\u770b\u5f97\u51fa\u795e\uff0c\u76ee\u7779\u90a3\u51e0\u9a7e\u9a6c\u8f66\u5165\u4e86\u629a\u8859\uff0c\u770b\u70ed\u95f9\u7684\u4eba\u6e10\u6e10\u6563\u53bb\uff0c\u5ffd\u7136\u6709\u4eba\u5728\u5510\u6653\u6f9c \u80a9\u4e0a\u4e00\u62cd\uff0c\u5510\u6653\u6f9c\u56de\u8fc7\u5934\u6765\uff0c\u559c\u51fa\u671b\u5916\uff0c\u62cd\u4ed6\u80a9\u5934\u7684\u4eba\u7adf\u662f\u7518\u51e4\u6c60\u3002\u7518\u51e4\u6c60\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6b64 \u5730\u4e0d\u662f\u8c08\u8bdd\u4e4b\u6240\uff0c\u4f60\u968f\u6211\u6765\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u5230\u4e86\u7518\u51e4\u6c60\u6240\u5c45\u7684\u5ba2\u5bd3\uff0c\u7518\u51e4\u6c60\u5173\u4e86\u623f\u95e8\uff0c\u8fd9\u624d\u7b11\u9053\uff1a\u201c\u521a\u624d\u4f60\u4e5f\u77a7\u89c1\u4e86\uff1f\u9c7c\u4eae \u8fd8\u5984\u60f3\u63a5\u6536\u5c71\u4e1c\uff0c\u505a\u4ed6\u7684\u85e9\u738b\u5462\uff01\u636e\u6211\u6240\u77e5\uff0c\u5141\u796f\u6b64\u9645\u5df2\u6d3e\u6c34\u5e08\uff0c\u76f4\u6363\u4ed6\u7684\u5de2\u7a74\u53bb\u4e86\u3002\u201d\u5510 \u6653\u6f9c\u9053\uff1a\u201c\u56db\u5a18\u5462\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u516b\u59b9\u8fd8\u5728\u6d59\u6c5f\u3002\u767d\u4e94\u54e5\u592b\u5987\u524d\u51e0\u5929\u8fd8\u5728\u8fd9\u91cc\uff0c\u73b0\u5728\u5df2\u4e58 \u8239\u51fa\u6d77\uff0c\u8d74\u7530\u6a2a\u5c9b\u4e86\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u9c7c\u4eae\u805a\u6709\u51e0\u4e07\u6c34\u5bc7\uff0c\u7cae\u98df \u8d22\u5b9d\uff0c\u79ef\u805a\u751a\u591a\uff0c\u672a\u5c1d\u4e0d\u53ef\u5229\u7528\u3002\u6240\u4ee5\u6211\u8981\u4ed6\u4eec\u5077\u5077\u56de\u53bb\u3002\u9c7c\u4eae\u4e0d\u5728\uff0c\u4ed6\u5973\u513f\u4e5f\u53ef\u6307\u6325\u90e8 \u4f17\uff0c\u62b5\u5fa1\u654c\u5175\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u90a3\u4e48\u9c7c\u4eae\u5728\u6b64\uff0c\u5c82\u4e0d\u751a\u9669\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8981\u8bf7\u4f60\u5e2e\u5fd9\u4e86\u3002\u4f60\u77e5\u9053 \u6211\u4e0e\u5927\u6c5f\u5357\u5317\u5404\u5904\u7684\u5e2e\u4f1a\u9f99\u5934\u90fd\u719f\uff0c\u629a\u8859\u4e2d\u4e5f\u6709\u6211\u7684\u5f1f\u5144\u3002\u6211\u6b63\u60f3\u6df7\u8fdb\u53bb\u4f3a\u673a\u884c\u4e8b\uff0c\u4f46\u6709\u672c \u9886\u7684\u5e2e\u624b\u4e0d\u591a\uff0c\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u53ef\u613f\u4e0e\u6211\u4e00\u540c\u5192\u9669\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u9664\u4e86\u5415\u56db\u5a18\u5916\uff0c\u6700\u670d\u4f69\u7684\u4fbf\u662f \u7518\u51e4\u6c60\uff0c\u5f53\u4e0b\u4e00\u53e3\u7b54\u5e94\u3002 \u3000\u3000\u9c7c\u4eae\u6ee1\u809a\u5bc6\u5708\uff0c\u5e26\u4e86\u51cc\u4e91\u5c9b\u4e3b\u536b\u626c\u5a01\u3001\u592a\u6e56\u5be8\u4e3b\u5b5f\u6b66\u529f\u7b49\u524d\u6765\u8d74\u4f1a\u3002\u5c71\u4e1c\u5de1\u629a\u5f20\u5ef7\u7389\u8bf7 \u4ed6\u5148\u6b47\u4e09\u65e5\uff0c\u8c08\u4ea4\u63a5\u4e4b\u4e8b\uff0c\u5f53\u665a\u629a\u8859\u7ea2\u70db\u9ad8\u70e7\uff0c\u534e\u5802\u591c\u5bb4\uff0c\u4e00\u961f\u6b4c\u5993\uff0c\u8f7d\u821e\u8f7d\u6b4c\uff0c\u79f0\u89de\u529d \u9152\uff0c\u9c7c\u4eae\u5174\u81f4\u751a\u8c6a\uff0c\u7b11\u9053\uff1a\u201c\u9761\u9761\u4e4b\u97f3\uff0c\u6559\u4eba\u96be\u53d7\uff0c\u6362\u4e00\u4e2a\u8c03\u5b50\u5531\u5531\u3002\u201d\u5f20\u5ef7\u7389\u9053\uff1a\u201c\u8bf7\u5927 \u738b\u70b9\u5531\u3002\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u5c31\u5531\u5f20\u4e8e\u6e56\u7684\u516d\u5dde\u6b4c\u5934\u5427\uff01\u201d\u6b4c\u5993\u5531\u9053\uff1a\u201c\u957f\u6dee\u671b\u65ad\uff0c\u5173\u585e\u83bd\u7136\u5e73\uff0c \u5f81\u5c18\u6697\uff0c\u971c\u98ce\u52b2\uff0c\u6084\u8fb9\u58f0\uff0c\u9eef\u9500\u51dd\u3002\u9065\u60f3\u5f53\u5e74\u4e8b\uff0c\u6b86\u5929\u6570\uff0c\u975e\u4eba\u529b\uff0c\u8bdb\u6cc5\u4e0a\uff0c\u5f26\u6b4c\u5730\u3002\u4ea6\u81bb \u8165\u3002\u9694\u6c34\u6be1\u4e61\uff0c\u843d\u65e5\u725b\u7f8a\u4e0b\uff0c\u533a\u8131\u7eb5\u6a2a\u3002\u770b\u540d\u738b\u5bb5\u730e\uff0c\u9a91\u706b\u4e00\u5ddd\u660e\u3002\u524d\u9f13\u60b2\u9e23\uff0c\u9063\u4eba\u60ca\uff01\u5ff5 \u8170\u95f4\u7bad\uff0c\u5323\u4e2d\u5251\uff0c\u7a7a\u57c3\u8822\uff0c\u7adf\u4f55\u6210\uff01\u65f6\u6613\u5931\uff0c\u5fc3\u5f92\u58ee\uff0c\u5c81\u5c06\u96f6\uff0c\u6e3a\u795e\u4eac\u2026\u2026\u201d\u957f\u6b4c\u672a\u5b8c\uff0c\u9c7c \u4eae\u5df2\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u624d\u542c\u5f97\u5165\u8033\u3002\u201d\u6b64\u8bcd\u662f\u5357\u5b8b\u5f20\u5b5d\u6837\uff08\u4e8e\u6e56\uff09\u60b2\u6124\u795e\u5dde\u5931\u9677\uff0c\u6258\u58ee\u5fd7 \u4e8e\u8bcd\u7ae0\u4e4b\u4f5c\u3002\u9c7c\u4eae\u66fe\u542c\u767d\u6cf0\u5b98\u6b4c\u8fc7\uff0c\u89c9\u5f97\u751a\u597d\uff0c\u6240\u4ee5\u70b9\u5531\uff0c\u5176\u5b9e\u4ed6\u4e5f\u4e0d\u89e3\u5176\u610f\u3002\u5f20\u5ef7\u7389\u542c \u4e86\uff0c\u9762\u8272\u5fae\u53d8\u3002\u9c7c\u4eae\u5927\u7b11\u4e00\u9635\uff0c\u4e3e\u676f\u6b32\u996e\uff0c\u5ffd\u7136\u4e00\u67c4\u5315\u9996\uff0c\u6a2a\u91cc\u98de\u6765\uff0c\u545b\u561f\u561f\u4e00\u58f0\u54cd\uff0c\u5c06\u9c7c \u4eae\u7684\u9152\u676f\u6253\u5f97\u7c89\u788e\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u534e\u5802\u817e\u6740\u6c14\uff0c\u58ee\u58eb\u89c1\u5148\u673a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e09\u56de\u3000\u4e2d\u4f0f\u96be\u9003\u3000\u82f1\u96c4\u5165\u5708\u5957\u3000\u6539\u88c5\u4ee3\u5ac1\u3000\u7389\u5973\u5f04\u7384\u865a \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u4e09\u56de \u4e2d\u4f0f\u96be\u9003\u3000\u82f1\u96c4\u5165\u5708\u5957 \u6539\u88c5\u4ee3\u5ac1\u3000\u7389\u5973\u5f04\u7384\u865a \u3000\u3000\u9c7c\u4eae\u5927\u5403\u4e00\u60ca\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u7559\u5fc3\u6697\u7b97\uff01\u201d\u5f20\u5ef7\u7389\u8eab\u65c1\u7684\u97e9\u91cd\u5c71\u4e0e\u5929\u53f6\u6563\u4eba\u4e0d\u7ea6 \u800c\u540c\uff0c\u98de\u8eab\u63a0\u8d77\uff0c\u4fe8\u5982\u4e24\u5934\u5de8\u9e70\uff0c\u5411\u9636\u4e0b\u7684\u536b\u5352\u4e1b\u4e2d\u6025\u6293\uff01\u5f20\u5ef7\u7389\u559d\u9053\uff1a\u201c\u901f\u95ed\u5927\u95e8\uff0c\u5feb\u6349 \u5978\u7ec6\uff01\u201d\u968f\u5373\u542c\u5f97\u9636\u4e0b\u6b66\u58eb\u7eb7\u7eb7\u53eb\u9053\uff1a\u201c\u54ce\u5440\uff0c\u662f\u6c5f\u5357\u5927\u4fa0\u7518\u51e4\u6c60\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea \u542c\u5f97\u4e00\u9635\u9635\u6697\u5668\u5636\u98ce\u4e4b\u58f0\uff0c\u5802\u4e0a\u9636\u4e0b\uff0c\u70db\u5149\u5168\u706d\uff01\u53ea\u6709\u7b75\u5e2d\u4e0a\u7684\u90a3\u652f\u5de8\u70db\uff0c\u56e0\u6709\u9c7c\u4eae\u7528\u638c\u529b \u9707\u98de\u6697\u5668\u5f97\u4ee5\u4fdd\u5b58\u3002 \u3000\u3000\u5e2d\u4e0a\u70db\u5149\u6447\u66f3\uff0c\u9636\u4e0b\u4eba\u5f71\u51cc\u4e71\uff0c\u9c7c\u4eae\u5b9a\u6674\u770b\u53bb\uff0c\u679c\u7136\u89c1\u662f\u7518\u51e4\u6c60\u548c\u97e9\u91cd\u5c71\u6253\u5728\u4e00\u8d77\uff0c\u53e6 \u5916\u8fd8\u6709\u4e00\u4e2a\u5c11\u5e74\uff0c\u88ab\u5929\u53f6\u6563\u4eba\u8feb\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u770b\u90a3\u80cc\u5f71\uff0c\u4f3c\u4e4e\u662f\u66fe\u4e00\u5ea6\u5230\u8fc7\u7530\u6a2a\u5c9b\u7684\u5510\u6653\u6f9c\u3002 \u3000\u3000\u5f20\u5ef7\u7389\u7b11\u9053\uff1a\u201c\u542c\u8bf4\u7518\u51e4\u6c60\u4e0e\u4ee4\u5a7f\u90fd\u4e0d\u613f\u9c7c\u8001\u79f0\u738b\u3002\u201d\u9c7c\u4eae\u7709\u5934\u4e00\u76b1\uff0c\u592a\u6e56\u5be8\u4e3b\u5b5f\u6b66\u529f \u9053\uff1a\u201c\u6211\u4eec\u52a9\u97e9\u91cd\u5c71\u5e08\u5144\u4e00\u81c0\u4e4b\u529b\u5427\u3002\u201d\u9c7c\u4eae\u6447\u4e86\u6447\u5934\uff0c\u5c06\u5f20\u5ef7\u7389\u7ed9\u4ed6\u6362\u7684\u91d1\u9152\u676f\u6401\u8fc7\u4e00 \u8fb9\uff0c\u659c\u7740\u773c\u775b\uff0c\u770b\u9636\u4e0b\u6df7\u6218\u3002 \u3000\u3000\u7b75\u5e2d\u4e0a\u6709\u70db\u5149\uff0c\u770b\u4e0b\u53f0\u9636\uff0c\u8fd8\u53ef\u4ee5\u7ea6\u7565\u770b\u51fa\u9762\u5f62\u4eba\u5f71\uff0c\u9636\u4e0b\u4e00\u56e2\u6f06\u9ed1\uff0c\u536b\u58eb\u4eec\u90a3\u6562\u63d2 \u624b\u3002\u97e9\u91cd\u5c71\u4e0e\u5929\u53f6\u6563\u4eba\uff0c\u4ed7\u7740\u6b66\u529f\u8d85\u5353\uff0c\u542c\u98ce\u8fa8\u5f71\uff0c\u7d27\u7f20\u7740\u7518\u51e4\u6c60\u4e0e\u5510\u6653\u6f9c\u3002 \u3000\u3000\u7518\u51e4\u6c60\u529b\u654c\u97e9\u91cd\u5c71\u6570\u638c\uff0c\u84e6\u7136\u6253\u4e86\u4e00\u4e2a\u6697\u53f7\uff1a\u4e0e\u5510\u6653\u6f9c\u5f80\u4eba\u5806\u4e2d\u4e00\u94bb\uff0c\u5929\u53f6\u63a2\u8eab\u6293\u62ff\uff0c \u5ffd\u5730\u91cc\u4e0d\u77e5\u4ece\u4ec0\u4e48\u5730\u65b9\u63b7\u6765\u4e00\u6761\u677f\u51f3\uff0c\u51e0\u4e4e\u7838\u4f24\u4ed6\u7684\u811a\u8e1d\u3002\u97e9\u91cd\u5c71\u53cc\u81c2\u4e00\u632f\uff0c\u63a8\u5f00\u4f17\u4eba\uff0c\u5510 \u6653\u6f9c\u53cd\u624b\u4e00\u628a\u98de\u8292\uff0c\u97e9\u91cd\u5c71\u662f\u6697\u5668\u7684\u5927\u540d\u5bb6\uff0c\u8863\u8896\u4e00\u62c2\uff0c\u628a\u98de\u8292\u8361\u5f97\u56db\u5904\u7eb7\u98de\uff0c\u536b\u58eb\u4eec\u7eb7\u7eb7 \u8d70\u907f\u3002\u7518\u51e4\u6c60\u4e0e\u5510\u6653\u6f9c\u8d81\u7740\u8fd9\u4e00\u9635\u54c4\u95f9\uff0c\u6e9c\u8fc7\u89d2\u95e8\uff0c\u65e9\u6709\u5e2e\u4f1a\u4e2d\u7684\u5144\u5f1f\u63a5\u5f15\uff0c\u6084\u6084\u8eb2\u85cf\u8d77 \u6765\u3002\u97e9\u91cd\u5c71\u4e0e\u5929\u53f6\u6563\u4eba\u8ffd\u51fa\u6765\u65f6\uff0c\u8fde\u4ed6\u4eec\u7684\u5f71\u5b50\u4e5f\u4e0d\u89c1\u4e86\u3002\u97e9\u91cd\u5c71\u5fc3\u4e2d\u5927\u6012\uff0c\u60c5\u77e5\u629a\u8859\u4e4b\u5185 \u5fc5\u6709\u5978\u7ec6\uff0c\u53ef\u662f\u5374\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u5802\u4e0a\u9636\u4e0b\u706f\u706b\u91cd\u660e\u3002\u5f20\u5ef7\u7389\u9053\uff1a\u201c\u7ed9\u7518\u51e4\u6c60\u8fd9\u53ae\u8d25\u4e86\u96c5\u5174\uff0c\u771f\u771f\u53ef\u6068\uff01\u54b1\u4eec \u518d\u559d\u9152\u3002\u201d\u9c7c\u4eae\u6309\u676f\u4e0d\u52a8\uff0c\u9053\uff1a\u201c\u5c0f\u738b\u8def\u4e0a\u67d3\u4e86\u4e00\u70b9\u98ce\u5bd2\uff0c\u9152\u662f\u4e0d\u80fd\u559d\u4e86\uff01\u201d\u5f20\u5ef7\u7389\u9053\uff1a \u201c\u65e2\u7136\u5982\u6b64\uff0c\u4e0d\u4fbf\u52c9\u5f3a\u3002\u201d\u81ea\u5df1\u659f\u9152\uff0c\u8fde\u559d\u4e09\u676f\uff0c\u7b11\u9053\uff1a\u201c\u7518\u51e4\u6c60\u8fd9\u53ae\u6b32\u65bd\u79bb\u95f4\u4e4b\u8ba1\uff0c\u5e78\u5927 \u738b\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u5927\u738b\u8fdc\u9053\u800c\u6765\uff0c\u4e0d\u514d\u75b2\u52b3\uff0c\u65e9\u5b89\u6b47\u5427\u3002\u201d \u3000\u3000\u9c7c\u4eae\u4e00\u9897\u5fc3\u4e03\u4e0a\u516b\u843d\uff0c\u4ed6\u5229\u4ee4\u667a\u660f\uff0c\u5bf9\u7518\u51e4\u6c60\u7684\u51fa\u8a00\u793a\u8b66\uff0c\u7adf\u7136\u5224\u65ad\u4e0d\u5b9a\u662f\u597d\u610f\u8fd8\u662f\u574f \u610f\u3002\u4f46\u4ed6\u4e43\u662f\u4e45\u5386\u6c5f\u6e56\u4e4b\u4eba\uff0c\u7ecf\u6b64\u4e00\u6765\uff0c\u81ea\u5df1\u662f\u5c0f\u5fc3\u9632\u5907\u3002\u53ca\u81f3\u89c1\u5f20\u5ef7\u7389\u81ea\u659f\u81ea\u996e\uff0c\u53c8\u5bbd\u4e86 \u5fc3\uff0c\u89c9\u5f97\u81ea\u5df1\u672a\u514d\u592a\u8fc7\u591a\u8651\u3002 \u3000\u3000\u5f20\u5ef7\u7389\u4eb2\u81ea\u5e26\u9c7c\u4eae\u5165\u5185\u5b89\u6b47\uff0c\u9c7c\u4eae\u5ffd\u9053\uff1a\u201c\u4e0e\u6211\u540c\u6765\u7684\u4eba\u90fd\u662f\u6211\u7684\u624b\u8db3\uff0c\u4f60\u4e0d\u5fc5\u4e3a\u6211\u5355 \u72ec\u5e03\u7f6e\u4f4f\u6240\uff0c\u6211\u4eec\u90fd\u4f4f\u5728\u4e00\u8d77\u5427\u3002\u201d\u8981\u77e5\u9c7c\u4eae\u4e5f\u4e0d\u662f\u597d\u76f8\u4e0e\u7684\u4eba\uff0c\u4ed6\u4f55\u5c1d\u4e0d\u63d0\u9632\u5230\u6709\u610f\u5916\u4e4b \u4e8b\u3002\u6240\u4ee5\u5e26\u6765\u7684\u5341\u4f59\u4eba\u5982\u592a\u6e56\u5be8\u4e3b\u5b5f\u6b66\u529f\u3001\u51cc\u4e91\u5c9b\u4e3b\u536b\u626c\u5a01\u7b49\uff0c\u4e2a\u4e2a\u90fd\u662f\u6b66\u529f\u9ad8\u5f3a\u7684\u4eba\u7269\uff0c \u8981\u805a\u5728\u4e00\u5904\uff0c\u7528\u610f\u81ea\u7136\u662f\u9632\u5907\u6697\u7b97\uff0c\u5f20\u5ef7\u7389\u5c82\u6709\u4e0d\u77e5\uff0c\u4f46\u89c1\u4ed6\u773c\u73e0\u4e00\u8f6c\uff0c\u53e3\u91cc\u9891\u9891\u9053\u597d\u3002 \u3000\u3000\u9c7c\u4eae\u4e0e\u4ed6\u7684\u968f\u4ece\u5341\u4f59\u4eba\uff0c\u90fd\u88ab\u5b89\u7f6e\u5728\u5f20\u5ef7\u7389\u65b0\u5efa\u7684\u98de\u7fe0\u697c\u4e2d\uff0c\u98de\u7fe0\u697c\u5728\u629a\u8859\u540e\u56ed\u7684\u5f53 \u4e2d\uff0c\u56db\u5468\u90fd\u6709\u5047\u5c71\u56de\u5eca\uff0c\u524d\u9762\u8fd8\u6709\u4e00\u6240\u6c34\u6a84\uff0c\u6c60\u4e0a\u98d8\u7740\u73bb\u7483\u7f15\u7a7a\u7684\u8377\u82b1\u706f\uff0c\u6811\u4e0a\u6302\u6709\u7ea2\u7eb1\u5bab \u706f\uff0c\u666f\u8272\u751a\u7f8e\u3002\u697c\u9ad8\u5c42\uff0c\u6bcf\u5c42\u90fd\u6709\u4e09\u4e2a\u7cbe\u81f4\u7684\u5c0f\u623f\u95f4\u548c\u4e00\u4e2a\u5927\u5ba2\u5385\uff0c\u5b89\u7f6e\u5341\u591a\u4e2a\u4eba\uff0c\u7ef0\u7ef0\u6709 \u4f59\u3002\u9c7c\u4eae\u548c\u5b5f\u6b66\u529f\u536b\u626c\u5a01\u4e09\u4eba\u8981\u4e86\u4e09\u697c\uff0c\u5f00\u7a97\u773a\u671b\uff0c\u62ab\u895f\u8fce\u98ce\uff0c\u5546\u8c08\u5927\u4e8b\u3002 \u3000\u3000\u536b\u626c\u5a01\u9053\uff1a\u201c\u5927\u738b\uff0c\u4f60\u770b\u7518\u51e4\u6c60\u6765\u610f\u5982\u4f55\uff1f\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u6cf0\u5b98\u4e0d\u613f\u6211\u505a\u85e9\u738b\uff0c\u7518\u51e4\u6c60\u5927 \u7ea6\u662f\u60f3\u65bd\u79bb\u95f4\u4e4b\u8ba1\u3002\u201d\u8fd9\u5176\u5b9e\u4e43\u662f\u5f20\u5ef7\u7389\u7684\u8bf4\u6cd5\u3002\u5b5f\u6b66\u529f\u6c89\u541f\u9053\uff1a\u201c\u7518\u51e4\u6c60\u6709\u6c5f\u5357\u5927\u4fa0\u4e4b \u540d\uff0c\u4ee5\u4ed6\u8eab\u4efd\uff0c\u672a\u5fc5\u80af\u7528\u8c0e\u8a00\u79bb\u95f4\u3002\u201d\u9c7c\u4eae\u62ac\u5934\u671b\u5929\uff0c\u4e45\u4e45\u4e0d\u8bed\u3002\u536b\u626c\u5a01\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c \u4e86\u56e0\u5176\u5b9e\u662f\u7ed9\u5e74\u7fb9\u5c27\u903c\u8d70\uff0c\u4ee5\u81f4\u547d\u4e27\u9099\u5c71\u7684\u3002\u5e74\u7fb9\u5c27\u4e4b\u6562\u903c\u8d70\u4e86\u56e0\uff0c\u5fc5\u5f97\u5141\u796f\u9ed8\u8bb8\u3002\u60f3\u5141\u796f \u4e0e\u5e74\u7fb9\u5c27\u5bf9\u4ed8\u4e86\u56e0\u5c1a\u4e14\u5982\u6b64\uff0c\u4ed6\u4eec\u5c82\u80af\u7518\u5fc3\u88c2\u571f\u5206\u5c01\uff0c\u5c06\u5c71\u4e1c\u9001\u7ed9\u6211\u4eec\u3002\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u4e0d \u7136\uff0c\u6211\u4eec\u4e0e\u4e86\u56e0\u4e0d\u540c\u3002\u4e86\u56e0\u867d\u7136\u662f\u7edd\u4e16\u6b66\u529f\uff0c\u7a76\u7adf\u5b64\u638c\u96be\u9e23\uff0c\u6211\u4eec\u5728\u6d77\u5916\u4e0e\u592a\u6e56\u6d1e\u5ead\u7b49\u5904\uff0c \u90fd\u6709\u90e8\u4f17\uff0c\u5141\u796f\u4e0d\u8df5\u8bfa\u8a00\uff0c\u4ed6\u4e0d\u6015\u6211\u4eec\u6270\u4ed6\u6cbf\u6d77\u4e00\u5e26\u5417\uff1f\u201d\u536b\u626c\u5a01\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u4e0d\u53ef\u4e0d \u9632\u3002\u201d\u9c7c\u4eae\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u60f3\u6211\u4eec\u5341\u591a\u4e2a\u5144\u5f1f\uff0c\u90fd\u662f\u4ee5\u4e00\u654c\u767e\u7684\u597d\u6c49\u3002\u5f20\u5ef7\u7389\u4fbf\u662f\u60f3\u65bd \u6bd2\u624b\uff0c\u6211\u4eec\u4e5f\u4e0d\u6015\u4ed6\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u89c1\u56ed\u4e2d\u4eba\u5f71\u8d70\u52a8\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u6709\u4eba\u4e0a\u697c\u62a5\u9053\uff1a\u201c\u97e9\u91cd\u5c71\u6c42\u89c1\u5927\u738b\u3002\u201d\u9c7c\u4eae \u9053\uff1a\u201c\u8fd9\u6837\u6df1\u591c\uff0c\u4ed6\u8fd8\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u9053\u58f0\uff1a\u201c\u8bf7\u3002\u201d\u97e9\u91cd\u5c71\u683c\u767b\u683c\u767b\u7684\u5927\u8e0f\u6b65\u8d70\u4e0a\u697c\u6765\uff0c\u89c1 \u4e86\u9c7c\u4eae\uff0c\u53cc\u62f3\u4e00\u62f1\uff0c\u72b6\u751a\u5014\u50b2\u3002\u9c7c\u4eae\u4e00\u6014\uff0c\u53ea\u542c\u5f97\u97e9\u91cd\u5c71\u9053\uff1a\u201c\u5e74\u5927\u5c06\u519b\u65e0\u6687\u6765\u89c1\u4f60\u4eec \u4e86\u3002\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u542c\u5f20\u5de1\u629a\u8bf4\uff0c\u7687\u4e0a\u4e0d\u662f\u8981\u6d3e\u4ed6\u6765\u548c\u6211\u8c08\u4ea4\u5272\u5c71\u4e1c\u4e4b\u4e8b\u5417\uff1f\u201d\u97e9\u91cd\u5c71\u9053\uff1a \u201c\u4ed6\u5728\u9752\u5c9b\u7763\u5e08\uff0c\u600e\u6709\u7a7a\u89c1\u4f60\uff1f\u201d\u9c7c\u4eae\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4ed6\u7763\u4ec0\u4e48\u5e08\uff1f\u201d\u97e9\u91cd\u5c71\u9053\uff1a \u201c\u9ec4\u6d77\u6c34\u5e08\uff0c\u73b0\u5728\u4e5f\u5f52\u4ed6\u6307\u6325\u3002\u4ed6\u8981\u6211\u5411\u4f60\u4f20\u8fbe\u5c06\u4ee4\uff01\u201d\u9c7c\u4eae\u9762\u8272\u5927\u53d8\u3002\u97e9\u91cd\u5c71\u51b7\u51b7\u4e00\u7b11\uff0c \u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5e74\u5927\u5c06\u519b\u4e0d\u5fcd\u591a\u6740\u65e0\u8f9c\uff0c\u53eb\u4f60\u901f\u5199\u964d\u8868\uff0c\u547d\u4ee4\u4f60\u7684\u90e8\u5c5e\u6295\u964d\u3002\u6211\u4eec\u5fc5\u5b9a\u597d\u597d\u5b89 \u7f6e\u3002\u8fd9\u662f\u4e00\u3002\u201d \u3000\u3000\u9c7c\u4eae\u6124\u6781\uff0c\u6012\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\uff1f\u201d\u97e9\u91cd\u5c71\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u534a\u5e74\u52ab\u63a0\u641c\u522e\uff01\u85cf\u5b9d\u751a\u591a\u3002\u8fd9\u4e9b \u4e0d\u4e49\u4e4b\u8d22\uff0c\u7406\u5b9c\u89e3\u5f52\u56fd\u5e93\u3002\u4f60\u5c06\u85cf\u5b9d\u4e4b\u5904\u7ec6\u7ec6\u7ed8\u51fa\u56fe\u6765\uff0c\u6d3e\u4e00\u4e2a\u4eba\u9001\u7ed9\u5e74\u5927\u5c06\u519b\uff0c\u514d\u5f97\u4ed6\u8981 \u8d39\u795e\u641c\u7d22\uff01\u4e24\u4ef6\u4e8b\u60c5\uff0c\u4f60\u505a\u4e86\u4e4b\u540e\u3001\u7687\u4e0a\u4f1a\u597d\u597d\u5f85\u4f60\uff0c\u63a5\u4f60\u5230\u5317\u4eac\u53bb\uff0c\u4ecd\u7136\u5c01\u4f60\u4e3a\u738b\u3002\u201d \u3000\u3000\u9c7c\u4eae\u6124\u6781\u72c2\u7b11\uff1a\u201c\u54c8\u54c8\uff01\u5927\u6e05\u541b\u4e3b\u7adf\u662f\u65e0\u4fe1\u65e0\u4e49\u7684\u5c0f\u4eba\uff01\u8fd9\u4e0d\u662f\u8c0b\u8d22\u5bb3\u547d\u7684\u4e0b\u4e09\u6d41\u5c0f\u8d3c \u6240\u4e3a\u5417\uff1f\u201d\u97e9\u91cd\u5c71\u65a5\u9053\uff1a\u201c\u95ed\u5634\uff0c\u4f60\u6562\u8bfd\u8c24\u7687\u4e0a\uff01\u4e0d\u6015\u788e\u5250\u51cc\u8fdf\u5417\uff01\u4f60\u5230\u5e95\u542c\u4e0d\u542c\u5e74\u5927\u5c06\u519b \u7684\u5c06\u4ee4\uff1f\u201d \u3000\u3000\u9c7c\u4eae\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u6562\u5411\u6211\u4e0b\u4ee4\uff01\u597d\uff0c\u54b1\u4eec\u95ef\u51fa\u53bb\u5148\u628a\u8fd9 \u629a\u8859\u70e7\u4e86\uff01\u201d\u628a\u624b\u4e00\u6325\uff0c\u536b\u626c\u5a01\u5b5f\u6b66\u529f\u53cc\u53cc\u6251\u4e0a\uff0c\u97e9\u91cd\u5c71\u632f\u81c2\u4e00\u683c\uff0c\u9000\u540e\u4e09\u6b65\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a \u201c\u4f60\u4eec\u8fd8\u60f3\u95ef\u51fa\u53bb\u5417\uff1f\u53ef\u522b\u505a\u68a6\u5566\uff01\u98de\u7fe0\u697c\u4e0b\u9762\u57cb\u6709\u5343\u4e07\u65a4\u70b8\u836f\uff0c\u4f60\u4eec\u4e4b\u4e2d\uff0c\u53ea\u8981\u6709\u4e00\u4eba\u6562 \u8de8\u51fa\u53bb\u534a\u6b65\uff0c\u4f60\u4eec\u4fbf\u8981\u7acb\u523b\u88ab\u70b8\u6210\u7c89\u788e\uff01\u201d \u3000\u3000\u9c7c\u4eae\u53c8\u60ca\u53c8\u6012\uff0c\u4f5c\u58f0\u4e0d\u5f97\uff0c\u97e9\u91cd\u5c71\u9053\uff1a\u201c\u6211\u8ba9\u4f60\u4eec\u597d\u597d\u5546\u91cf\uff0c\u613f\u4f9d\u4ece\u7684\u8bdd\uff0c\u4fbf\u628a\u767d\u65d7\u6302 \u51fa\u6765\u3002\u8981\u4e0d\u7136\u6027\u547d\u96be\u4fdd\uff01\u54fc\uff0c\u54fc\uff0c\u4f60\u5bf9\u7687\u4e0a\u6709\u4ec0\u4e48\u529f\u52b3\uff1f\u8ba9\u4f60\u5728\u6d77\u5916\u79f0\u738b\uff0c\u5df2\u7ecf\u662f\u5929\u6069\u6d69 \u8361\uff0c\u4f60\u8fd8\u8d2a\u5fc3\u4e0d\u8db3\uff0c\u60f3\u8981\u5c71\u4e1c\uff01\u201d\u51b7\u7b11\u4e00\u9635\uff0c\u547c\u7684\u4e00\u638c\u6253\u5f00\u7a97\u95e8\uff0c\u98de\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u9c7c\u4eae\u9762\u8272\u53d1\u9752\uff0c\u826f\u4e45\uff0c\u826f\u4e45\uff0c\u59cb\u53f9\u6c14\u9053\uff1a\u201c\u97e9\u91cd\u5c71\u867d\u7136\u53ef\u6068\u53ef\u6740\uff0c\u4ed6\u4e5f\u8fd8\u9a82\u5f97\u771f\u5bf9\u3002\u60f3 \u6211\u4eec\u5728\u6d77\u5916\u79f0\u738b\uff0c\u4f55\u7b49\u81ea\u7531\u81ea\u5728\uff0c\u4f55\u5fc5\u53d7\u5141\u796f\u7684\u7b3c\u7edc\uff0c\u771f\u771f\u662f\u4e0e\u864e\u8c0b\u76ae\uff0c\u81ea\u6295\u7f57\u7f51\u3002\u201d\u536b\u626c \u5a01\u9053\uff1a\u201c\u8fc7\u53bb\u4e4b\u4e8b\uff0c\u4e0d\u8981\u8bf4\u4ed6\u4e86\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u5982\u4f55\u5e94\u4ed8\uff1f\u201d \u3000\u3000\u9c7c\u4eae\u9053\uff1a\u201c\u6211\u4e00\u751f\u95ef\u8361\u6c5f\u6e56\uff0c\u4ece\u672a\u5411\u4eba\u4f4e\u5934\u8ba4\u8f93\uff0c\u4ed6\u5c31\u662f\u628a\u6211\u5250\u4e86\uff0c\u6211\u4e5f\u4e0d\u80fd\u5411\u4ed6\u9012\u964d \u8868\uff01\u201d\u536b\u626c\u5a01\u4e0e\u5b5f\u6b66\u529f\u51ed\u7a97\u5916\u773a\uff0c\u53ea\u89c1\u4e00\u6392\u706b\u7bad\u624b\u5f20\u5f13\u642d\u7bad\uff0c\u5bf9\u51c6\u98de\u7fe0\u697c\uff0c\u53ea\u8981\u4e00\u58f0\u4ee4\u4e0b\uff0c \u706b\u7bad\u98de\u6765\uff0c\u98de\u7fe0\u697c\u4fbf\u8981\u70b8\u6210\u7c89\u788e\u3002\u7126\u6025\u6124\u6012\u60ca\u6050\u5f20\u60f6\u7b49\u7b49\u60c5\u7eea\uff0c\u90fd\u5728\u4e24\u4eba\u9762\u4e0a\u8868\u9732\u51fa\u6765\u3002\u9c7c \u4eae\u77a7\u5728\u773c\u5185\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5e74\u5df2\u82b1\u7532\uff0c\u6b7b\u4e0d\u8db3\u60dc\u3002\u53ea\u662f\u7d2f\u4f60\u4eec\u7c89\u9aa8\u788e\u8eab\uff0c\u5374\u662f\u4e8e\u5fc3 \u4e0d\u5fcd\uff01\u201d \u3000\u3000\u5b5f\u6b66\u529f\u9053\uff1a\u201c\u542c\u97e9\u91cd\u5c71\u53e3\u6c14\uff0c\u4ed6\u4eec\u4e00\u662f\u60f3\u4e0d\u6218\u800c\u80dc\uff0c\u4e8c\u662f\u60f3\u5927\u738b\u85cf\u5b9d\uff0c\u770b\u6765\u4e0d\u4f1a\u7acb\u65bd\u8fa3 \u624b\u3002\u54b1\u4eec\u7ed9\u4ed6\u4e00\u4e2a\u2018\u62d6\u2019\u5b57\u3002\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u62d6\uff0c\u80fd\u62d6\u5230\u51e0\u65f6\uff1f\u201d\u5b5f\u6b66\u529f\u9053\uff1a\u201c\u80fd\u62d6\u5230\u51e0\u65f6\u4fbf \u62d6\u5230\u51e0\u65f6\u3002\u201d\u9c7c\u4eae\u5fc3\u60f3\uff1a\u95ef\u51fa\u53bb\u65e2\u4e0d\u53ef\u80fd\uff0c\u626f\u767d\u65d7\u5fc3\u53c8\u4e0d\u613f\u3002\u9664\u4e86\u62d6\u5ef6\u4e4b\u5916\uff0c\u5df2\u65e0\u522b\u6cd5\u3002\u53ea \u597d\u70b9\u5934\u4e0d\u8bed\u3002 \u3000\u3000\u7518\u51e4\u6c60\u4e0e\u5510\u6653\u6f9c\u9760\u629a\u8859\u4e2d\u5e2e\u4f1a\u5144\u5f1f\u7684\u63a9\u62a4\uff0c\u9003\u8fc7\u4e86\u97e9\u91cd\u5c71\u7684\u641c\u67e5\u3002\u5f53\u665a\u4fbf\u77e5\u9053\u4e86\u9c7c\u4eae\u88ab \u56f0\u5728\u98de\u7fe0\u697c\u4e4b\u4e8b\uff0c\u7518\u51e4\u6c60\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4ee5\u9c7c\u4eae\u8fd9\u6837\u7684\u4eba\uff0c\u4e5f\u4f1a\u5229\u4ee4\u667a\u660f\uff0c\u4e2d\u4eba\u6bd2\u8ba1\u3002\u201d\u95ee\u629a \u8859\u4e2d\u90a3\u4e2a\u5e2e\u4f1a\u7684\u5c0f\u5934\u76ee\u9053\uff1a\u201c\u706b\u7bad\u624b\u4e2d\u6709\u5426\u54b1\u4eec\u7684\u4eba\uff1f\u201d\u90a3\u5c0f\u5934\u76ee\u9053\uff1a\u201c\u53ea\u6709\u4e00\u4e24\u4e2a\uff0c\u6d4e\u4e0d \u4e86\u4ec0\u4e48\u4e8b\u3002\u706b\u7bad\u624b\u662f\u97e9\u91cd\u5c71\u4e0e\u5929\u53f6\u6563\u4eba\u8f6e\u73ed\u6307\u6325\uff0c\u53ea\u8981\u6709\u4e00\u679d\u706b\u7bad\u89e6\u53d1\u70b8\u836f\uff0c\u98de\u7fe0\u697c\u4fbf\u8981\u7c89 \u788e\u3002\u201d\u7518\u51e4\u6c60\u867d\u7136\u6709\u52c7\u6709\u8c0b\uff0c\u4e5f\u65e0\u6cd5\u53ef\u60f3\u3002 \u3000\u3000\u9c7c\u4eae\u4e00\u62d6\u4fbf\u62d6\u4e86\u4e03\u5929\uff0c\u5bf9\u97e9\u91cd\u5c71\u7684\u5a01\u5413\u7f6e\u4e4b\u4e0d\u7406\u3002\u7518\u51e4\u6c60\u5f97\u77e5\u6d88\u606f\uff0c\u5bf9\u9c7c\u4eae\u4e4b\u786c\u4e5f\u9887\u4f69 \u670d\u3002\u53ef\u662f\u62d6\u5ef6\u7a76\u975e\u826f\u6cd5\uff0c\u53ea\u8981\u5e74\u7fb9\u5c27\u7684\u6c34\u5e08\u628a\u9c7c\u4eae\u5de2\u7a74\u8361\u5e73\uff0c\u97e9\u91cd\u5c71\u5fc5\u65bd\u6740\u624b\u3002\u53ea\u597d\u5bc4\u671b\u9c7c \u5a18\u4e0e\u767d\u6cf0\u5b98\u80fd\u5e73\u5b89\u5230\u8fbe\u6d77\u5c9b\uff0c\u62b5\u6297\u5b98\u5175\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u629a\u8859\u4e2d\u559c\u6c14\u6d0b\u6d0b\uff0c\u5230\u5904\u6253\u626b\uff0c\u5e76\u5728\u56ed\u4e2d\u642d\u8d77\u620f\u53f0\u3002\u7518\u51e4\u6c60\u5411\u90a3\u5c0f\u5934\u76ee\u6253\u542c\uff0c\u59cb\u77e5 \u8fc7\u51e0\u5929\u4fbf\u662f\u5f20\u5ef7\u7389\u66ff\u513f\u5b50\u5b8c\u5a5a\u7684\u4f73\u671f\u3002\u7518\u51e4\u6c60\u968f\u53e3\u95ee\u9053\uff1a\u201c\u65b0\u5ab3\u5987\u662f\u54ea\u4e00\u5bb6\u7684\uff1f\u201d\u90a3\u5934\u76ee \u9053\uff1a\u201c\u542c\u8bf4\u662f\u6d59\u6c5f\u5de1\u629a\u674e\u536b\u7684\u5343\u91d1\u3002\u201d\u7518\u51e4\u6c60\u5403\u4e86\u4e00\u60ca\u3001\u5fc3\u60f3\uff1a\u674e\u536b\u53ea\u6709\u4e00\u4e2a\u5973\u513f\uff0c\u90a3\u4e48\u5f20 \u5ef7\u7389\u7684\u5ab3\u5987\u4e00\u5b9a\u662f\u674e\u660e\u73e0\u4e86\u3002\u674e\u660e\u73e0\u4e0e\u4e09\u54e5\u8def\u6c11\u77bb\u5931\u5fd7\u76f8\u7231\uff0c\u5982\u4f55\u80af\u5ac1\u5230\u5c71\u4e1c\uff1f\u90a3\u5c0f\u5934\u76ee\u89c1 \u7518\u51e4\u6c60\u9762\u8272\u6709\u5f02\uff0c\u95ee\u9053\uff1a\u201c\u7518\u5927\u4fa0\u6709\u4f55\u5fc3\u4e8b\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u4f60\u7684\u6d88\u606f\u662f\u771f\u7684 \u5417\uff1f\u201d\u90a3\u5c0f\u5934\u76ee\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u771f\uff1f\u542c\u8bf4\u8fd8\u662f\u7687\u5e1d\u505a\u7684\u5a92\u4eba\u5462\uff01\u674e\u536b\u6d3e\u4eba\u628a\u5973\u513f\u9001\u6765\uff0c\u81f3\u8fdf\u5728 \u5927\u540e\u5929\uff0c\u4fbf\u4e00\u5b9a\u53ef\u4ee5\u5230\u4e86\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u4f4e\u9996\u601d\u91cf\uff0c\u5510\u6653\u6f9c\u95ee\u90a3\u5c0f\u5934\u76ee\u9053\uff1a\u201c\u542c\u8bf4\u5f20\u5ef7\u7389\u7684\u513f\u5b50\u66fe\u88ab\u4e00\u4e2a\u5c0f\u59d1\u5a18\u6253\u4e86\u4e00 \u987f\uff0c\u6709\u8fd9\u56de\u4e8b\u5417\uff1f\u201d\u90a3\u5c0f\u5934\u76ee\u9053\uff1a\u201c\u6709\uff0c\u90a3\u5df2\u7ecf\u662f\u5341\u591a\u5929\u4ee5\u524d\u7684\u4e8b\u4e86\u3002\u629a\u8859\u7684\u6559\u5934\u2018\u966a\u592a\u5b50 \u8bfb\u4e66\u2019\uff0c\u4e5f\u6371\u4e86\u4e00\u987f\u597d\u6253\u3002\u201d\u5510\u6653\u6f9c\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u6371\u6253\uff1f\u201d\u90a3\u5c0f\u5934\u76ee\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8fd9 \u4f4d\u5b9d\u8d1d\u5c11\u7237\u6700\u662f\u597d\u8272\uff0c\u5e73\u65e5\u89c1\u59ff\u9996\u5e73\u6574\u7684\u6c11\u5bb6\u5973\u5b50\uff0c\u4e5f\u8981\u5077\u5077\u6478\u6478\u5f04\u5230\u624b\u3002\u542c\u8bf4\u90a3\u65e5\u4ed6\u5728\u9152 \u697c\u78b0\u5230\u4e86\u4e00\u4e2a\u5341\u5206\u7f8e\u8c8c\u7684\u5c0f\u59d1\u5a18\uff0c\u4ed6\u8dd1\u53bb\u8c03\u620f\u4eba\u5bb6\uff0c\u8fd8\u672a\u8bf4\u4e0a\u4e09\u53e5\u8bdd\uff0c\u5c31\u7ed9\u4eba\u5bb6\u6454\u4e0b\u697c\u53bb\u3002 \u629a\u8859\u7684\u6559\u5934\u4e0a\u53bb\uff0c\u4e5f\u7ed9\u6253\u65ad\u4e86\u80da\u9aa8\u3002\u5927\u5c11\u7237\u6084\u6084\u8dd1\u5411\u6765\u517b\u4f24\uff0c\u5e78\u597d\u6240\u4f24\u4e0d\u91cd\uff0c\u8981\u4e0d\u7136\u4ed6\u8fd8\u8981 \u6371\u4e0a\u987f\u6253\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u90a3\u5934\u76ee\u7b11\u9053\uff1a\u201c\u5f20\u5ef7\u7389\u81ea\u53f7\u7406\u5b66\u540d\u5bb6\uff0c\u5e73\u65e5\u9053\u8c8c\u5cb8 \u7136\uff0c\u5bf9\u513f\u5b50\u7684\u7ba1\u675f\u5012\u662f\u5f88\u4e25\u7684\u3002\u201d\u5510\u6653\u6f9c\u60f3\u8d77\u5f20\u5ef7\u7389\u5f53\u5e74\u8ba9\u5141\u796f\u62a2\u52ab\u7f8e\u5973\u53ca\u4ed6\u6697\u7b97\u9c7c\u4eae\u7b49 \u4e8b\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u6837\u7684\u7406\u5b66\u540d\u5bb6\uff0c\u82e5\u7136\u5b54\u5b5f\u6709\u7075\uff0c\u7a0b\u6731\u590d\u751f\uff0c\u4e5f\u8981\u6253\u4ed6\u8033\u5149\u3002\u4ed6\u7ba1\u513f\u5b50\uff0c\u4e0d\u8fc7 \u662f\u505a\u7ed9\u4eba\u5bb6\u770b\u7684\u7f62\u3002\u201d \u3000\u3000\u5f53\u665a\u5510\u6653\u6f9c\u548c\u7518\u51e4\u6c60\u5546\u91cf\uff0c\u60f3\u53bb\u63a2\u5bfb\u90a3\u5c0f\u59d1\u5a18\u7684\u8e2a\u8ff9\u3002\u7518\u51e4\u6c60\u5ffd\u9053\uff1a\u201c\u6211\u8981\u51fa\u53bb\u4e00\u8d9f\uff0c \u4f60\u7684\u4e8b\u6682\u6401\u4e00\u6401\u7f62\u3002\u201d\u5510\u6653\u6f9c\u867d\u7136\u6302\u5fc3\u51af\u745b\u59d0\u59b9\uff0c\u4e5f\u53ea\u597d\u7b54\u5141\u3002 \u3000\u3000\u6253\u4f24\u5f20\u5ef7\u7389\u513f\u5b50\u7684\u6b63\u662f\u51af\u745b\u3002\u5979\u6700\u521d\u52a8\u624b\u4e4b\u65f6\uff0c\u53ea\u9053\u662f\u666e\u901a\u5bcc\u5bb6\u7684\u8f7b\u8584\u5b50\u5f1f\uff0c\u4e0b\u624b\u4e0d \u91cd\u3002\u6253\u4e86\u4e4b\u540e\uff0c\u77e5\u9053\u662f\u5c71\u4e1c\u5de1\u629a\u7684\u513f\u5b50\uff0c\u60f3\u9053\uff1a\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u6211\u8be5\u628a\u4ed6\u7684\u4e24\u53ea\u817f\u90fd\u6253\u6298\u3002\u201d \u5f53\u665a\u4fbf\u79bb\u5f00\u6d4e\u5357\u3002 \u3000\u3000\u8fc7\u4e86\u51e0\u592d\uff0c\u5979\u5728\u8def\u4e0a\u542c\u9014\u4eba\u8c08\u8bb2\uff0c\u77e5\u9053\u9c7c\u4eae\u5230\u6d4e\u5357\u6664\u89c1\u5f20\u5ef7\u7389\u8981\u63a5\u6536\u5c71\u4e1c\uff0c\u51af\u745b\u5fc3\u60f3\uff0c \u7d20\u95fb\u9c7c\u4eae\u85cf\u5b9d\u751a\u591a\uff0c\u4e5f\u8bb8\u4ed6\u4f1a\u6709\u80fd\u89e3\u5510\u53d4\u53d4\u6bd2\u4f24\u4e4b\u836f\u3002\u51af\u745b\u521d\u95ef\u6c40\u6e56\uff0c\u60f3\u6cd5\u5929\u771f\uff0c\u80c6\u5b50\u53c8 \u5927\uff0c\u7adf\u7136\u518d\u6298\u56de\u6d4e\u5357\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u5979\u5728\u5b98\u9053\u4e0a\u8d70\uff0c\u5ffd\u89c1\u524d\u9762\u5c18\u5934\u5927\u8d77\uff0c\u4e00\u5927\u961f\u5b98\u5175\u62a4\u9001\u8bb8\u591a\u8f66\u8f86\uff0c\u8fdc\u8fdc\u8d70\u6765\uff0c\u5b98\u9053 \u501a\u5c71\u9762\u6cb3\uff0c\u51af\u745b\u907f\u4e0a\u5c71\u4e0a\uff0c\u8df3\u4e0a\u4e00\u682a\u5927\u6811\uff0c\u8df3\u671b\u4e0b\u6765\uff1a\u5ffd\u89c1\u4e2d\u95f4\u4e0a\u4e00\u8f86\u5927\u8f66\uff0c\u6302\u7740\u5bf9\u706f\u7b3c\uff0c \u8f66\u4e0a\u7ed3\u7740\u5f69\u7ef8\uff0c\u8f66\u7684\u7b4b\u9762\uff0c\u8fd8\u6709\u4e00\u5bf9\u864e\u5934\u724c\uff0c\u770b\u4e0d\u6e05\u4e0a\u9762\u7684\u5b57\u8ff9\u3002\u90a3\u8f86\u8f66\u5206\u6210\u4e24\u8282\uff0c\u524d\u9762\u8fd9 \u8282\u655e\u5f00\uff0c\u7aef\u5750\u7740\u4e00\u4e2a\u9752\u8863\u5987\u4eba\uff0c\u624b\u4e2d\u63d0\u7740\u4e00\u67c4\u957f\u5251\u3002\u51af\u745b\u8ba4\u51fa\u8fd9\u6b63\u662f\u5728\u6768\u4ef2\u82f1\u5bb6\u4e2d\uff0c\u548c\u81ea\u5df1 \u4ea4\u8fc7\u624b\u7684\u5987\u4eba\uff0c\u540e\u6765\u542c\u6768\u4ef2\u82f1\u8bf4\u5979\u4fbf\u662f\u4ec0\u4e48\u7075\u5c71\u540d\u5bbf\u97e9\u91cd\u5c71\u7684\u59bb\u5b50\uff0c\u540d\u53eb\u53f6\u6a2a\u6ce2\u7684\u3002\u51af\u745b\u4e0d \u7981\u5927\u5947\uff0c\u60f3\u9053\uff1a\u201c\u54a6\uff0c\u5979\u600e\u4e48\u53c8\u5e72\u8d77\u4fdd\u9556\u6765\u4e86\uff1f\u770b\u5979\u8fd9\u4e2a\u6837\u5b50\uff0c\u53ef\u771f\u795e\u6c14\u3002\u201d\u51af\u745b\u4e0d\u77e5\uff0c\u53f6 \u6a2a\u6ce2\u4e43\u662f\u674e\u660e\u73e0\u7684\u5e08\u5085\uff0c\u5979\u8fd9\u56de\u5374\u662f\u62a4\u9001\u5f92\u5f1f\u6765\u6210\u4eb2\u7684\u3002\u4e48\uff1f\u201d\u51af\u745b\u7b11\u9053\uff1a\u201c\u8fd9\u79c3\u9a74\u600e\u4f24\u5f97 \u6211\uff1f\u201d\u8def\u6c11\u77bb\u660e\u660e\u89c1\u5979\u4e2d\u4e86\u4e00\u5251\uff0c\u5982\u4eca\u5374\u662f\u82e5\u65e0\u5176\u4e8b\uff0c\u53ea\u9053\u5979\u7684\u6b66\u529f\u5df2\u7ec3\u5230\u6df1\u4e0d\u53ef\u6d4b\u4e4b\u5883\uff0c \u4e0d\u7981\u5927\u9a87\uff0c\u5fc3\u91cc\u5341\u5206\u4f69\u670d\uff01 \u3000\u3000\u51af\u7410\u9053\uff1a\u201c\u8def\u5927\u4fa0\uff0c\u90a3\u65e5\u5728\u90ca\u5c71\u5192\u72af\u4f60\u4e86\u3002\u201d\u8def\u6c11\u77bb\u60ca\u7591\u4e0d\u8db3\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u548c\u4e86\u56e0\u4e0d\u662f \u4e00\u8def\u7684\u4e48\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u4ec0\u4e48\u4e86\u56e0\uff1f\u6211\u4e0d\u77e5\u9053\u3002\u201d\u8def\u6c11\u77bb\u8be7\u9053\uff1a\u201c\u90a3\u4e48\u7528\u98de\u5200\u4f24\u6211\u7684\u674e\u6e90\u516d \u54e5\u7684\u96be\u9053\u4e0d\u662f\u4f60\u4e48\uff1f\u201d\u51af\u745b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u4ece\u6765\u4e0d\u7528\u98de\u5200\u3002\u4f60\u770b\u9519\u4e86\u3002\u90a3\u662f\u53e6\u4e00\u4e2a\u548c\u6211\u6781\u4e3a \u76f8\u4f3c\u7684\u4eba\u6240\u5e72\u7684\u4e8b\u3002\u6653\u6f9c\u53d4\u53d4\u65e9\u5c31\u5bf9\u6211\u8bf4\u4e86\u3002\u201d\u8def\u6c11\u77bb\u6115\u5728\u5f53\u573a\uff0c\u60f3\u9053\uff1a\u5929\u4e0b\u90a3\u6709\u5982\u6b64\u76f8\u4f3c \u7684\u4eba\uff1f \u3000\u3000\u5ffd\u542c\u5f97\u6797\u5916\u4e00\u58f0\u5927\u7b11\uff0c\u7518\u51e4\u6c60\u8d70\u4e86\u8fdb\u6765\u3002\u8def\u6c11\u77bb\u8df3\u5c06\u8d77\u6765\u9053\uff1a\u201c\u4e03\u54e5\uff0c\u4f60\u4e5f\u6765\u4e86\uff1f\u201d\u7518 \u51e4\u6c60\u9053\uff1a\u201c\u6211\u8ddf\u4e86\u4f60\u534a\u5929\uff0c\u4f60\u4e0d\u77e5\u9053\u4e48\uff1f\u201d\u8def\u6c11\u77bb\u6697\u6697\u53eb\u58f0\uff1a\u201c\u60ed\u6127\u201d\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4eec \u521a\u624d\u548c\u79c3\u9a74\u53ae\u62fc\uff0c\u4f60\u4e5f\u770b\u5230\u4e86\uff1f\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u8fde\u4f60\u4eec\u6240\u8bf4\u7684\u8bdd\uff0c\u6211\u4e5f\u542c\u5230\u4e86\u3002\u745b\u5a18\uff0c\u4f60 \u7684\u5251\u6cd5\u771f\u597d\u554a\uff01\u201d\u7518\u51e4\u6c60\u65e9\u4ece\u5510\u6653\u6f9c\u53e3\u4e2d\u77e5\u9053\u51af\u745b\u6765\u5386\uff0c\u542c\u5979\u8bf4\u8bdd\uff0c\u7acb\u523b\u77e5\u9053\u5979\u7684\u8eab\u4efd\uff0c\u987f \u7136\u6709\u4e86\u4e00\u4e2a\u4e3b\u610f\u3002 \u3000\u3000\u51af\u745b\u6115\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u2014\u2014\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5510\u53d4\u53d4\u6ca1\u6709\u5bf9\u4f60\u8bf4\u8fc7\u4e48\uff1f\u6211\u662f\u7518\u2014 \u2014\u201d\u51af\u745b\u5f79\u5f85\u4ed6\u8bf4\u5b8c\uff0c\u5927\u559c\u53eb\u9053\uff1a\u201c\u4f60\u662f\u6c5f\u5357\u5927\u4fa0\u7518\u51e4\u6c60\uff0c\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u3002\u90a3\u662f\u6c5f \u6e56\u4e0a\u7684\u670b\u53cb\u66ff\u6211\u6367\u573a\u80e1\u4e71\u53eb\u7684\u3002\u201d\u51af\u745b\u60f3\u8d77\u4e00\u4e8b\uff0c\u5ffd\u9053\uff1a\u201c\u521a\u624d\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u51fa\u6765\u6253\u90a3\u79c3 \u9a74\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u8fd8\u8981\u7559\u4ed6\u4e00\u6761\u72d7\u547d\u66ff\u6211\u5e72\u4e00\u6869\u4e8b\u54e9\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u4ed6\u80fd\u66ff\u4f60\u5e72\u4ec0\u4e48\u4e8b \u60c5\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u53eb\u4ed6\u66ff\u6211\u9001\u4e2a\u53e3\u4fe1\u3002\u6211\u521a\u624d\u5b88\u5728\u6797\u5916\uff0c\u4f60\u628a\u4ed6\u6253\u8dd1\u4e4b\u540e\uff0c\u6211\u53c8\u628a\u4ed6\u6253 \u4e86\u4e00\u62f3\u3002\u201d\u51af\u745b\u7b11\u9053\uff1a\u201c\u4f60\u6253\u4e86\u4ed6\u4ed6\u8fd8\u4f1a\u66ff\u4f60\u9001\u4fe1\uff1f\u201d\u7518\u51e4\u6c60\u5ffd\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4e5f\u66ff\u6211\u5343\u4e00\u6869 \u4e8b\u60c5\u597d\u4e0d\u597d\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u53ea\u8981\u6211\u5e72\u5f97\u4e86\uff0c\u4f46\u51ed\u5429\u5490\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u5e72\u5f97\u4e86\uff0c\u4f60\u4e00\u5b9a\u5e72\u5f97 \u4e86\u3002\u8fd9\u662f\u4e00\u6869\u975e\u5e38\u7684\u8da3\u7684\u4e8b\u60c5\uff0c\u4f60\u9644\u8033\u8fc7\u6765\u3002\u201d\u51af\u745b\u597d\u5947\u5fc3\u8d77\uff0c\u679c\u7136\u9644\u8033\u8fc7\u53bb\uff0c\u4e00\u9762\u542c\u4e00\u9762 \u683c\u683c\u7684\u7b11\u3002 \u3000\u3000\u53f6\u6a2a\u6ce2\u6263\u674e\u660e\u73e0\u540c\u4e00\u8f86\u8f66\uff0c\u6d77\u4e91\u548c\u5c1a\u53bb\u627e\u8def\u6c11\u77bb\uff0c\u5979\u4e5f\u662f\u4e8b\u540e\u624d\u77e5\u3002\u77e5\u9053\u4e4b\u540e\uff0c\u9887\u4e3a\u4e0d \u60a6\u3002\u9ec4\u660f\u65f6\u5206\uff0c\u9001\u4eb2\u7684\u8f66\u961f\u5728\u79bb\u6d4e\u5357\u4e94\u5341\u91cc\u5916\u7684\u5c0f\u9547\u9a7b\u624e\u3002\u6d77\u4e91\u548c\u5c1a\u6c14\u6025\u8d25\u574f\uff0c\u4e00\u62d0\u4e00\u62d0\u7684 \u8dd1\u56de\u6765\u89c1\u53f6\u6a2a\u6ce2\u3002\u53f6\u6a2a\u5f7c\u6012\u9053\uff1a\u201c\u9001\u4eb2\u7684\u4e8b\uff0c\u7531\u6211\u4e3b\u6301\uff0c\u4f60\u600e\u4e48\u4e0d\u542c\u53f7\u4ee4\uff0c\u79c1\u81ea\u79bb\u5f00\uff1f\u597d \u5440\uff0c\u4f60\u73b0\u5728\u5403\u4e86\u4e8f\uff0c\u624d\u6765\u627e\u6211\uff01\u201d\u6d77\u4e91\u548c\u5c1a\u4e0e\u53f6\u6a2a\u6ce2\u672c\u6765\u662f\u540c\u8f88\u7684\u4eba\uff0c\u5fcd\u7740\u4e00\u809a\u5b50\u6c14\u56de\u9053\uff1a \u201c\u8def\u6c11\u77bb\u8fd9\u4e0d\u77e5\u6b7b\u6d3b\u7684\u5c0f\u5b50\u8001\u8ddf\u5728\u8f66\u961f\u540e\u9762\uff0c\u4f60\u96be\u9053\u4e0d\u77e5\u9053\u4e48\uff1f\u201d\u53f6\u6a2a\u6ce2\u51b7\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u7528 \u4f60\u63d0\u9192\uff1f\u8def\u6c11\u77bb\u8fd9\u5c0f\u5b50\u6b66\u827a\u5e73\u5e73\uff0c\u5e72\u4e0d\u4e86\u4ec0\u4e48\u5927\u4e8b\uff0c\u4f55\u5fc5\u7406\u4ed6\uff1f\u5343\u91cc\u9001\u4eb2\uff0c\u4fa5\u5e78\u5e73\u5b89\u6e21\u8fc7\uff0c \u4f60\u5374\u8981\u5206\u5fc3\u53bb\u5bf9\u4ed8\u4e00\u4e2a\u50bb\u5c0f\u5b50\uff0c\u4e07\u4e00\u7ed9\u4eba\u4e58\u673a\u6363\u4e71\uff0c\u6709\u6240\u758f\u5931\uff0c\u90a3\u65f6\u6211\u95ee\u4f60\u6709\u4f55\u9762\u76ee\u518d\u89c1\u7687 \u4e0a\uff1f\u4f60\u88ab\u8d2c\u5230\u6d59\u6c5f\uff0c\u8fd8\u4e0d\u597d\u597d\u4e89\u6c14\uff0c\u524d\u7a0b\u574f\u4e86\u4e0d\u6253\u7d27\uff0c\u4f60\u4e0d\u6015\u6c5f\u6e56\u4e0a\u7b11\u8bdd\u5417\uff1f\u54c8\uff0c\u770b\u4f60\u8fd9\u4e2a \u6837\u5b50\uff0c\u4f60\u662f\u4e0d\u662f\u7ed9\u8def\u6c11\u77bb\u8fd9\u5c0f\u5b50\u6253\u4f24\u4e86\uff0c\u8981\u8001\u5a18\u66ff\u4f60\u51fa\u6c14\uff1f\u201d\u6d77\u4e91\u548c\u5c1a\u6012\u9053\uff1a\u201c\u6253\u4f24\u6211\u7684\u4eba \u4e5f\u6b63\u5728\u627e\u4f60\u6666\u6c14\u5462\uff0c\u6211\u770b\u4f60\u4e5f\u672a\u5fc5\u5bf9\u4ed8\u5f97\u4e86\uff01\u201d\u53f6\u6a2a\u6ce2\u6012\u9053\uff1a\u201c\u8c01\uff1f\u201d\u6d77\u4e91\u548c\u5c1a\u9053\uff1a\u201c\u7518\u51e4 \u6c60\uff01\u4ed6\u4eca\u665a\u4fbf\u8981\u6765\u62dc\u8bbf\u4f60\uff0c\u4ed6\u95ee\u4f60\u6562\u4e0d\u6562\u548c\u4ed6\u5355\u6253\u72ec\u6597\uff1f\u201d\u5176\u5b9e\u6d77\u4e91\u548c\u5c1a\u662f\u5148\u7ed9\u51af\u745b\u523a\u4f24\u7136 \u540e\u624d\u7ed9\u7518\u51e4\u6c60\u6253\u4e86\u4e00\u62f3\u7684\u3002\u4ed6\u6015\u8bf4\u51fa\u662f\u7ed9\u4e00\u4e2a\u5c0f\u59d1\u5a18\u6253\u4f24\u66f4\u4f24\u4f53\u9762\uff0c\u6240\u4ee5\u5b8c\u5168\u63a8\u5230\u7518\u51e4\u6c60\u8eab \u4e0a\u3002 \u3000\u3000\u53f6\u6a2a\u6ce2\u51b7\u7b11\u9053\uff1a\u201c\u7518\u51e4\u6c60\u53c8\u600e\u4e48\u6837\uff0c\u8001\u5a18\u8fd8\u80fd\u6015\u4ed6\uff1f\u4e0d\u8fc7\u54b1\u4eec\u73b0\u5728\u9001\u4eb2\u8981\u7d27\uff0c\u7518\u51e4\u6c60\u8be1 \u8ba1\u591a\u7aef\uff0c\u53ef\u4e0d\u8981\u7740\u4e86\u4ed6\u7684\u9053\u513f\u3002\u4f60\u53bb\u53eb\u5404\u8425\u7edf\u9886\u5c0f\u5fc3\u9632\u536b\u3002\u5f85\u6211\u628a\u5c0f\u59d0\u8fed\u5230\u5c71\u4e1c\u629a\u8859\u4e4b\u540e\uff0c \u90a3\u65f6\u7518\u51e4\u6c60\u82e5\u8fd8\u672a\u9001\u547d\uff0c\u6211\u518d\u548c\u4ed6\u5355\u6253\u72ec\u6597\u8ba9\u4f60\u5f00\u5f00\u773c\u754c\u3002\u201d\u6d77\u4e91\u548c\u5c1a\u6068\u5979\u9a84\u50b2\uff0c\u4e0d\u53d1\u4e00 \u8a00\uff0c\u4fbf\u884c\u9000\u51fa\u3002 \u3000\u3000\u662f\u591c\uff0c\u53f6\u6a2a\u6ce2\u7763\u4fc3\u5b98\u519b\uff0c\u5c0f\u5fc3\u9632\u536b\uff0c\u8fc7\u4e86\u4e09\u66f4\uff0c\u5c1a\u65e0\u52a8\u9759\u3002\u53f6\u6a2a\u6ce2\u6697\u7b11\u9053\uff1a\u201c\u7518\u51e4\u6c60\u53c8 \u4e0d\u662f\u4e09\u5934\u516d\u81c2\uff0c\u4ed6\u5355\u8eab\u600e\u6562\u63a2\u8425\uff0c\u60f3\u6765\u53ea\u662f\u6270\u4e71\u519b\u5fc3\u4e4b\u8ba1\u7f62\u4e86\u3002\u201d \u3000\u3000\u6d59\u6c5f\u5de1\u629a\u674e\u536b\u4e3a\u62a4\u9001\u5973\u513f\uff0c\u6d3e\u51fa\u7cbe\u5175\u4e00\u5343\uff0c\u8f66\u8f86\u4e09\u5341\u591a\u4e58\uff0c\u5b89\u8425\u4e4b\u65f6\uff0c\u8f66\u8f86\u56f4\u5728\u56db\u5468\uff0c \u7eb5\u6709\u5927\u80a1\u76d7\u532a\u4e5f\u96be\u8fdb\u653b\u3002\u53f6\u6a2a\u6ce2\u751a\u89c9\u5b89\u5fc3\uff0c\u4e0d\u6599\u8fc7\u4e86\u4e09\u66f4\uff0c\u7a81\u62a5\u7cae\u8f66\u8d77\u706b\uff0c\u53f6\u6a2a\u6ce2\u4e00\u60ca\uff0c\u5fc3 \u4e2d\u72d0\u7591\uff1a\u96be\u9053\u662f\u6709\u4e86\u5978\u7ec6\uff1f\u6025\u5fd9\u4f20\u4ee4\u4e0b\u53bb\uff0c\u53eb\u6d77\u4e91\u548c\u5c1a\u62bd\u8c03\u51fa\u4e00\u5c0f\u961f\u5b98\u5175\u6251\u706d\u706b\u5934\uff0c\u5176\u4ed6\u5404 \u8425\u4e0d\u51c6\u4e71\u52a8\u3002\u504f\u504f\u90a3\u591c\u522e\u897f\u5317\u98ce\uff0c\u7cae\u8349\u6613\u71c3\uff0c\u706b\u52bf\u7adf\u7136\u8d8a\u6765\u8d8a\u5927\u3002 \u3000\u3000\u53f6\u6a2a\u6ce2\u5927\u4e3a\u607c\u6012\uff0c\u6b63\u60f3\u4eb2\u81ea\u67e5\u770b\uff0c\u5ffd\u89c1\u4e00\u4e2a\u5b98\u519b\u7edf\u5e26\u5982\u98de\u8dd1\u6765\uff0c\u53f6\u6a2a\u6ce2\u559d\u9053\uff1a\u201c\u4f60\u4e0d\u5b88 \u5728\u8425\u5730\uff0c\u4e71\u8dd1\u505a\u4ec0\u4e48\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u540d\u7edf\u5e26\u5ffd\u7136\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8d3c\u5a46\u5a18\uff0c\u4f60\u770b\u6211\u662f\u8c01\uff1f\u201d\u547c \u7684\u4e00\u638c\uff0c\u6a2a\u80f8\u5288\u5230\u3002 \u3000\u3000\u53f6\u6a2a\u6ce2\u559d\u9053\uff1a\u201c\u7518\u51e4\u6c60\uff0c\u4f60\u597d\u5927\u80c6\uff01\u201d\u8eab\u5f62\u4e00\u95ea\uff0c\u638c\u98ce\u63a0\u9762\u800c\u8fc7\uff0c\u8fa3\u989d\u4f5c\u75db\u3002\u4f46\u5979\u4e5f\u5728 \u8fd9\u4e00\u95ea\u4e4b\u95f4\uff0c\u62bd\u51fa\u5251\u6765\uff0c\u4e00\u62db\u201c\u795e\u9f99\u6389\u5c3e\u201d\uff0c\u53cd\u624b\u75be\u523a\u3002\u7518\u51e4\u6c60\u6697\u9053\uff1a\u8fd9\u5a46\u5a18\u679c\u7136\u8eab\u624b\u77eb \u6377\uff0c\u540d\u4e0d\u865a\u4f20\uff0c\u602a\u4e0d\u5f97\u674e\u536b\u5c06\u5973\u513f\u4ed8\u6258\u7ed9\u5979\u3002\u8de8\u4e0a\u4e00\u6b65\uff0c\u624b\u6307\u4e00\u62c2\uff0c\u5411\u5979\u53f3\u814b\u51fb\u53bb\uff0c\u53f6\u6a2a\u6ce2 \u4fa7\u8eab\u4e00\u5251\uff0c\u4ecd\u7136\u6ca1\u6709\u523a\u7740\u3002\u7518\u51e4\u6c60\u8eab\u5f62\u4e00\u77ee\uff0c\u5de6\u638c\u4e00\u7a7f\uff0c\u65bd\u5c55\u64d2\u62ff\u624b\u7684\u6076\u62db\uff0c\u786c\u6765\u62a2\u5979\u7684\u5b9d \u5251\uff0c\u53f3\u624b\u4e00\u4e2a\u5370\u638c\uff0c\u638c\u98ce\u98d2\u7136\uff0c\u98d8\u52a8\u80f8\u8863\u3002\u53f6\u6a2a\u6ce2\u5927\u6012\uff0c\u4fa7\u8eab\u659c\u9000\uff0c\u559d\u9053\uff1a\u201c\u7518\u51e4\u6c60\uff0c\u4f60\u597d \u65e0\u793c\uff0c\u80c6\u6562\u620f\u4fae\u8001\u5a18\u3002\u201d\u5237\u5237\u4e24\u5251\uff0c\u8fde\u73af\u53cd\u51fb\uff0c\u7518\u51e4\u6c60\u54c8\u54c8\u5927\u7b11\uff0c\u7eb5\u8eab\u4e00\u8df3\uff0c\u8dc3\u4e0a\u4e00\u8f86\u5927 \u8f66\uff0c\u6a2a\u8098\u4e00\u649e\uff0c\u5c06\u8f66\u9876\u4e86\u671b\u7684\u4e00\u540d\u6e05\u5175\u649e\u4e0b\u8f66\u53bb\uff0c\u5927\u7b11\u9053\uff1a\u201c\u8d3c\u5a46\u5a18\uff0c\u4f60\u6562\u548c\u6211\u89c1\u4e2a\u9ad8\u4e0b \u4e48\uff1f\u201d \u3000\u3000\u4fe9\u4eba\u8fd9\u4e00\u52a8\u624b\uff0c\u5927\u547c\u5c0f\u53eb\uff0c\u5b98\u519b\u5168\u90fd\u60ca\u8d77\uff0c\u53f6\u6a2a\u6ce2\u559d\u9053\uff1a\u201c\u4e71\u7bad\u5c04\u4ed6\uff01\u201d\u5b98\u519b\u539f\u662f\u5404\u4f9d \u8f66\u8f86\uff0c\u7ed3\u6210\u4e09\u5341\u591a\u4e2a\u5c0f\u961f\uff0c\u9635\u5f62\u5e03\u7f6e\u5341\u5206\u4e25\u5bc6\uff0c\u8fd9\u4e00\u6765\u987f\u65f6\u5927\u4e71\uff0c\u77e2\u7bad\u7eb7\u98de\uff0c\u7518\u51e4\u6c60\u8131\u4e0b\u53f7 \u8863\uff0c\u968f\u624b\u4e00\u6325\uff0c\u77e2\u7bad\u56db\u5904\u98de\u5c04\uff0c\u5374\u65e0\u4e00\u7bad\u4f24\u5f97\u4e86\u4ed6\uff0c\u53f6\u6a2a\u6ce2\u5927\u6012\uff0c\u60f3\u9053\uff1a\u82e5\u7136\u53eb\u4ed6\u8fd9\u6837\u5b89\u7136 \u9003\u51fa\uff0c\u6211\u989c\u9762\u4f55\u5b58\uff1f\u63d0\u5251\u8ffd\u53bb\uff0c\u7518\u51e4\u6c60\u4e00\u8df3\uff0c\u53c8\u8df3\u4e0a\u897f\u9996\u4e00\u8f86\u5927\u8f66\uff0c\u597d\u50cf\u6545\u610f\u548c\u5979\u6349\u8ff7\u85cf\u4f3c \u7684\u3002\u53f6\u6a2a\u6ce2\u6012\u706b\u653b\u5fc3\uff0c\u4e00\u9762\u6325\u624b\u53d1\u7bad\uff0c\u4e00\u9762\u6251\u53bb\u8ffd\u8d76\u3002 \u3000\u3000\u674e\u660e\u73e0\u672c\u6765\u4e0d\u613f\u5ac1\u5f20\u5ef7\u7389\u7684\u513f\u5b50\uff0c\u5979\u7239\u5a18\u54c4\u5979\u662f\u8c03\u804c\u5c71\u4e1c\uff0c\u9a97\u5979\u4e0a\u8f66\uff0c\u53eb\u5979\u5148\u884c\u3002\u4e0a\u4e86 \u8f66\u540e\uff0c\u5979\u770b\u51fa\u52bf\u5934\u4e0d\u5bf9\uff0c\u53ef\u662f\u53f6\u6a2a\u6ce2\u770b\u5f97\u751a\u4e25\uff0c\u83ab\u8bf4\u9003\u8dd1\uff0c\u8fde\u5bfb\u6b7b\u4e5f\u4e0d\u53ef\u80fd\u3002\u674e\u660e\u73e0\u4e5f\u662f\u4e2a \u7cbe\u7075\u7684\u59d1\u5a18\uff0c\u5bfb\u601d\uff1a\u6211\u5230\u4e86\u5c71\u4e1c\u629a\u8859\uff0c\u518d\u60f3\u6cd5\u9003\u8131\u4e5f\u4e0d\u8fdf\u3002\u4f46\u5979\u867d\u7136\u5982\u6b64\u6253\u7b97\uff0c\u5fc3\u4e2d\u5230\u5e95\u60f6 \u6050\u4e0d\u5b89\u3002 \u3000\u3000\u662f\u591c\uff0c\u674e\u660e\u73e0\u6b63\u5728\u51dd\u601d\u9ed8\u60f3\uff0c\u5ffd\u95fb\u5f97\u5916\u9762\u53ae\u6740\u4e4b\u58f0\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u5982\u679c\u6211\u80fd\u8d81\u6df7 \u4e71\u4e4b\u4e2d\u9003\u51fa\uff0c\u5c82\u4e0d\u751a\u5999\uff1f\u201d\u63ed\u5f00\u5e10\u5e55\u4e00\u89d2\uff0c\u4f46\u89c1\u5404\u961f\u5b98\u5175\uff0c\u4f9d\u8f66\u96c6\u7ed3\uff0c\u9635\u52bf\u4e0d\u4e71\u3002\u53f6\u6a2a\u6ce2\u547c \u559d\u8ffd\u9010\uff0c\u4f3c\u4e4e\u6b63\u5728\u4e0e\u4eba\u62fc\u6597\u3002\u674e\u660e\u73e0\u60f3\u9053\uff1a\u201c\u5201\u6597\u68ee\u4e25\uff0c\u9635\u5f62\u672a\u4e71\uff0c\u6211\u5982\u4f55\u9003\u5f97\u51fa\u53bb\uff1f\u201d\u9eef \u7136\u53f9\u606f\uff0c\u5bf9\u955c\u4e00\u7167\uff0c\u955c\u4e2d\u5c11\u5973\u5b9d\u6c14\u73e0\u5149\uff0c\u5bb9\u5149\u8273\u53d1\uff0c\u53c8\u4e0d\u7981\u54d1\u7136\u5931\u7b11\uff1a\u5982\u6b64\u8863\u7740\uff0c\u5982\u6b64\u6253 \u626e\uff0c\u53ea\u8981\u4e00\u7a9c\u51fa\u53bb\uff0c\u7acb\u523b\u4fbf\u8981\u53d7\u4eba\u6ce8\u89c6\uff0c\u519b\u4e2d\u5b9a\u4f1a\u54d7\u7136\u60ca\u547c\u3002\u8fd9\u65f6\uff0c\u53f6\u6a2a\u6ce2\u6b63\u88ab\u7518\u51e4\u6c60\u6fc0\u5f97 \u71c3\u8d77\u6012\u706b\uff0c\u6307\u6325\u58eb\u5175\u653e\u7bad\u3002\u674e\u660e\u73e0\u542c\u5916\u9762\u58f0\u54cd\uff0c\u5b98\u519b\u9635\u811a\u5df2\u52a8\uff0c\u5fc3\u4e2d\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u53ef\u662f\u51e0\u6b21\u601d \u91cf\uff0c\u4ecd\u7136\u4e0d\u6562\u9003\u8d70\u3002 \u3000\u3000\u5ffd\u5730\u4e00\u80a1\u98ce\u6765\uff0c\u5e10\u5e18\u4e00\u5377\uff0c\u5916\u9762\u7a81\u7136\u8d70\u8fdb\u4e00\u4e2a\u5c11\u5e74\u5175\u58eb\uff0c\u674e\u660e\u73e0\u5403\u4e86\u4e00\u60ca\uff0c\u6b63\u60f3\u559d\u95ee\uff0c \u90a3\u5c11\u5e74\u5175\u571f\u628a\u53f7\u8863\u4e00\u8131\uff0c\u518d\u626f\u4e0b\u519b\u5e3d\uff0c\u674e\u660e\u73e0\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u4e0d\u662f\u7433\u59d1\u5a18\u5417\uff1f\u201d\u51af\u7433\u4ee5\u524d\u5728 \u6d59\u6c5f\u629a\u8859\u4f4f\u8fc7\uff0c\u5e38\u548c\u674e\u660e\u73e0\u8361\u821f\u897f\u6e56\uff0c\u6240\u4ee5\u674e\u660e\u73e0\u9519\u5c06\u51af\u745b\u5f53\u4f5c\u51af\u7433\u3002 \u3000\u3000\u51af\u745b\u5fae\u5fae\u4e00\u7b11\uff0c\u8fd9\u7b49\u8bef\u4f1a\u4e4b\u4e8b\uff0c\u5982\u4eca\u5979\u5df2\u53f8\u7a7a\u89c1\u60ef\uff0c\u4e5f\u4e0d\u4ee5\u4e3a\u602a\u4e86\u3002\u674e\u660e\u73e0\u9053\uff1a\u201c\u7433\u59b9 \u59b9\u4f60\u600e\u4e48\u6765\u7684\uff1f\u662f\u6211\u7684\u5e08\u5085\u53eb\u4f60\u6765\u7684\u4e48\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u4f60\u4f11\u591a\u8a00\uff0c\u5feb\u6362\u4e0a\u6211\u7684\u8863\u670d\uff0c\u8d81\u5916\u9762 \u6df7\u4e71\uff0c\u79c1\u9003\u51fa\u53bb\u3002\u201d\u5c06\u90a3\u8eab\u53f7\u8863\u5411\u5979\u9762\u524d\u4e00\u63b7\u3002\u674e\u660e\u73e0\u5fc3\u9053\uff1a\u201c\u54a6\uff0c\u5979\u600e\u4e48\u77e5\u9053\u6211\u7684\u5fc3 \u4e8b\uff1f\u201d\u65f6\u673a\u7d27\u8feb\uff0c\u65e0\u6687\u7ec6\u95ee\uff0c\u6025\u6025\u6362\u8863\uff0c\u73e0\u5b9d\u9996\u9970\uff0c\u629b\u5f03\u6ee1\u5730\u3002\u51af\u745b\u4e00\u4e00\u62fe\u8d77\uff0c\u7a7f\u6234\u8d77\u6765\uff0c \u674e\u660e\u73e0\u6539\u4e86\u670d\u88c5\uff0c\u5979\u4e5f\u6539\u4e86\u670d\u88c5\u3002\u674e\u660e\u73e0\u9053\uff1a\u201c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u51af\u745b\u7b11\u9053\uff1a\u201c\u6211\u66ff\u4f60\u51fa\u5ac1\u5440\uff01 \u4f60\u820d\u4e0d\u5f97\u8fd9\u8eab\u534e\u670d\u548c\u73e0\u5b9d\u5417\uff1f\u201d \u3000\u3000\u8fd9\u6b63\u662f\u7518\u51e4\u6c60\u5b9a\u4e0b\u7684\u8ba1\u7b56\uff0c\u4ed6\u5148\u501f\u6d77\u4e91\u548c\u5c1a\u4e4b\u53e3\uff0c\u58f0\u660e\u4eca\u665a\u72ec\u63a2\u519b\u8425\uff0c\u4ee4\u53f6\u6a2a\u6ce2\u5168\u795e\u8d2f \u6ce8\uff0c\u5bf9\u4ed6\u9632\u5907\uff0c\u8fd9\u6837\u5c31\u653e\u677e\u4e86\u5bf9\u674e\u660e\u73e0\u7684\u770b\u7ba1\u3002\u9001\u4eb2\u7684\u5b98\u519b\u4e2d\uff0c\u6709\u6d59\u6c5f\u201c\u6d77\u9633\u5e2e\u201d\u7684\u5f1f\u5144\uff0c \u7518\u51e4\u6c60\u4e0e\u4ed6\u4eec\u76f8\u719f\uff0c\u6084\u6084\u6df7\u5165\u8425\u4e2d\uff0c\u548c\u51af\u745b\u90fd\u6362\u4e86\u5b98\u519b\u7684\u670d\u9970\u3002 \u3000\u3000\u51af\u745b\u89c1\u674e\u660e\u73e0\u6362\u597d\u8863\u88f3\uff0c\u4e00\u9762\u548c\u5979\u5f00\u73a9\u7b11\uff0c\u4e00\u9762\u50ac\u5979\u5feb\u8d70\u3002\u674e\u660e\u73e0\u5411\u5979\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u6211 \u6709\u4e00\u4e2a\u5fc3\u8179\u5a62\u5973\uff0c\u53eb\u505a\u674f\u82b1\uff0c\u660e\u65e5\u4f60\u53ea\u8981\u5979\u670d\u5f85\u4fbf\u662f\uff0c\u591a\u8c22\u4f60\u4e86\u3002\u201d\u63ed\u5f00\u5e10\u5e55\u4fbf\u8d70\u3002\u51af\u745b\u7b11 \u9053\uff1a\u201c\u6b65\u5b50\u8de8\u5927\u4e00\u70b9\u5bf9\u4e86\uff0c\u8fd9\u624d\u50cf\u4e2a\u7537\u513f\u3002\u201d\u51af\u745b\u626e\u8fc7\u7537\u5b50\uff0c\u5bf9\u8fd9\u4e9b\u5fae\u7ec6\u4e4b\u5904\uff0c\u6bd4\u674e\u660e\u73e0\u7cbe \u660e\u5f97\u591a\u3002 \u3000\u3000\u53f6\u6a2a\u5f7c\u8ffd\u9010\u7518\u51e4\u6c60\uff0c\u7518\u51e4\u6c60\u5728\u5927\u8f66\u4e0a\u8df3\u6765\u8df3\u53bb\uff0c\u6325\u8863\u6251\u7bad\uff0c\u5077\u7a7a\u8fd8\u653e\u6697\u5668\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c \u5b98\u519b\u4e2d\u4e0d\u77e5\u662f\u8c01\u5439\u4e86\u51e0\u58f0\u53e3\u54e8\uff0c\u7518\u51e4\u6c60\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u501a\u591a\u4e3a\u80dc\uff0c\u6211\u61d2\u5f97\u548c\u4f60\u7f20\u4e86\u3002\u201d\u8eab\u5f62 \u4e00\u843d\uff0c\u968f\u624b\u6293\u8d77\u4e24\u540d\u7edf\u9886\uff0c\u65cb\u98ce\u6025\u821e\uff0c\u76f4\u51b2\u51fa\u53bb\uff0c\u53f6\u6a2a\u6ce2\u7d27\u8ffd\u4e0d\u820d\uff0c\u5b98\u519b\u4eec\u6015\u6295\u9f20\u5fcc\u5668\uff0c\u4e0d \u6562\u963b\u62e6\uff0c\u970e\u65f6\u51b2\u51fa\u8425\u5730\u3002\u53f6\u6a2a\u6ce2\u7528\u900f\u9aa8\u9489\u6253\u7518\u51e4\u6c60\u811a\u8e1d\uff0c\u8fde\u53d1\u4e09\u679a\u90fd\u6ca1\u6253\u7740\u3002\u7518\u51e4\u6c60\u559d\u9053\uff1a \u201c\u81ed\u5a46\u5a18\uff0c\u4f60\u4e2d\u4e86\u6211\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\uff0c\u4eca\u665a\u6765\u7684\uff0c\u4f60\u4ee5\u4e3a\u53ea\u662f\u6211\u4e00\u4eba\u4e48\uff1f\u201d\u53f6\u6a2a\u6ce2\u4e00\u60ca\uff0c\u5fc3 \u9053\uff1a\u201c\u5bf9\u5440\uff0c\u53ef\u4e0d\u8981\u4e2d\u4e86\u4ed6\u7684\u6697\u7b97\u3002\u201d\u7518\u51e4\u6c60\u8d81\u5979\u4e00\u6014\uff0c\u84e6\u7136\u5927\u559d\u4e00\u58f0\uff0c\u5c06\u4e24\u540d\u4eba\u8d28\u5411\u5979\u629b \u53bb\u3002\u53f6\u6a2a\u6ce2\u95ea\u8eab\u4e00\u8ba9\uff0c\u817f\u5f2f\u7a81\u7136\u4e00\u9635\u5267\u75db\u3002 \u3000\u3000\u53f6\u6a2a\u6ce2\u54ac\u7259\u4e00\u62d4\uff0c\u5374\u662f\u4e00\u67c4\u4e94\u5bf8\u591a\u957f\u7684\u5315\u9996\uff0c\u5e78\u597d\u6240\u4f24\u4e4b\u5904\uff0c\u5e76\u975e\u8981\u5bb3\uff0c\u53f6\u6a2a\u6ce2\u7684\u4e08\u592b \u662f\u6697\u5668\u540d\u5bb6\uff0c\u6cbb\u6697\u5668\u7684\u91d1\u521b\u836f\u5979\u4e5f\u968f\u8eab\u643a\u6709\uff0c\u773c\u770b\u7518\u51e4\u6c60\u8eab\u5f71\u5df2\u6e3a\uff0c\u6068\u6068\u8bf4\u9053\uff1a\u201c\u8001\u5a18\u7ec8\u65e5 \u6253\u96c1\uff0c\u4eca\u4ec0\u53eb\u96c1\u53ee\u4e86\u773c\u775b\u3002\u201d\u90a3\u4e24\u540d\u7edf\u9886\u88ab\u7518\u51e4\u6c60\u63b7\u5f97\u5934\u7834\u8840\u6d41\uff0c\u521a\u521a\u722c\u8d77\uff0c\u53c8\u88ab\u53f6\u6a2a\u6ce2\u5404 \u626b\u4e00\u8bb0\u8033\u5149\uff0c\u9a82\u9053\uff1a\u201c\u90fd\u662f\u4f60\u8fd9\u4e24\u4e2a\u8113\u5305\uff0c\u4e0d\u662f\u4e3a\u4e86\u6015\u8bef\u4f24\u4f60\u4eec\uff0c\u8001\u5a18\u4e5f\u4e0d\u81f3\u4e8e\u4e2d\u4e86\u90a3\u53ae\u6697 \u5668\u3002\u201d\u628a\u91d1\u521b\u836f\u6577\u88f9\u4f24\u53e3\uff0c\u4e00\u62d0\u4e00\u62d0\u7684\u56de\u5230\u8425\u5185\uff0c\u8fd9\u65f6\u7cae\u8f66\u4e4b\u706b\u5df2\u88ab\u6251\u706d\uff0c\u4e5f\u672a\u518d\u53d1\u73b0\u654c \u8e2a\uff0c\u53f6\u6a2a\u6ce2\u62d0\u56de\u674e\u660e\u73e0\u7684\u5e10\u5e55\uff0c\u63ed\u5e18\u4e00\u770b\uff0c\u89c1\u201c\u674e\u660e\u73e0\u201d\u4fa7\u8eab\u5185\u671b\uff0c\u7761\u5f97\u6b63\u9163\u3002\u5fc3\u9053\uff1a\u201c\u8fd9 \u5c0f\u59ae\u5b50\u5012\u4e0d\u7ba1\u5916\u9762\u7ffb\u5929\u8986\u5730\u54e9\u3002\u201d\u7518\u51e4\u6c60\u7684\u5315\u9996\u867d\u7136\u65e0\u6bd2\uff0c\u4f46\u56e0\u52b2\u529b\u751a\u5927\uff0c\u5315\u9996\u51e0\u4e4e\u900f\u8fc7\u817f \u5f2f\u7684\u7b4b\u9aa8\uff0c\u75bc\u75db\u4e0d\u6b62\u3002\u53f6\u6a2a\u6ce2\u5fc3\u9053\uff1a\u201c\u83ab\u4e0d\u8981\u88ab\u5b83\u5f04\u788e\u8e1d\u9aa8\uff0c\u53d8\u6210\u6b8b\u5e9f\uff0c\u5c31\u9ebb\u70e6\u4e86\u3002\u201d\u6025\u5fd9 \u53eb\u4eba\u5f04\u6765\u4e24\u53ea\u751f\u516c\u9e21\uff0c\u51c6\u5907\u7528\u516c\u9e21\u8840\u63a5\u5408\u9aa8\u5934\u7684\u788e\u88c2\u90e8\u5206\uff0c\u81ea\u56de\u5e10\u5e55\u6cbb\u7597\uff0c\u4e5f\u65e0\u5fc3\u518d\u628a\u201c\u674e \u660e\u73e0\u201d\u53eb\u9192\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u5728\u5c71\u4e1c\u629a\u8859\u5185\u8eb2\u85cf\uff0c\u7b49\u4e86\u4e24\u5929\uff0c\u4ecd\u7136\u4e0d\u89c1\u7518\u51e4\u6c60\u56de\u6765\u3002\u9c7c\u4eae\u4e5f\u8fd8\u662f\u88ab\u56f0\u5728\u98de\u7fe0\u697c \u548c\u4ed6\u4eec\u76f8\u6301\u3002\u5510\u6653\u6f9c\u751a\u4e3a\u5fc3\u6025\u3002\u7b2c\u4e09\u65e5\u5ffd\u62a5\u6d59\u629a\u674e\u536b\u5df2\u6d3e\u4eba\u5c06\u5973\u513f\u9001\u5230\uff0c\u629a\u8859\u5185\u5904\u5904\u5f20\u706f\u7eb3 \u5f69\uff0c\u559c\u6c14\u6d0b\u6d0b\u3002 \u3000\u3000\u5f20\u5ef7\u7389\u7ed9\u513f\u5b50\u5b89\u6392\u5a5a\u671f\u4e4b\u65f6\uff0c\u672a\u6599\u5230\u6709\u9c7c\u4eae\u4e4b\u4e8b\uff0c\u4eca\u65e5\u674e\u5bb6\u5c06\u5973\u513f\u9001\u5230\uff0c\u6b22\u559c\u4e4b\u4e2d\u4e5f\u6709 \u51e0\u5206\u6212\u60e7\u3002\u4e2d\u5348\u65f6\u5206\uff0c\u9999\u8f66\u5230\u95e8\u3002\u5f20\u5ef7\u7389\u547d\u4ee4\u6253\u5f00\u4e2d\u5802\uff0c\u53eb\u513f\u5b50\u4eb2\u81ea\u8fce\u63a5\u3002 \u3000\u3000\u793c\u5802\u5185\u5916\uff0c\u4eba\u5934\u7c07\u62e5\uff0c\u96cd\u6b63\u6d3e\u6765\u81f4\u8d3a\u7684\u94a6\u4f7f\u4e5f\u5df2\u5230\u8fbe\uff0c\u771f\u662f\u70ed\u95f9\u975e\u5e38\u3002\u97e9\u91cd\u5c71\u542c\u5f97\u59bb\u5b50 \u5230\u6765\uff0c\u5c06\u6307\u6325\u706b\u7bad\u624b\u4e4b\u8d23\uff0c\u4ea4\u7ed9\u4e86\u5929\u53f6\u6563\u4eba\uff0c\u4e5f\u51fa\u6765\u8fce\u63a5\u3002\u53f6\u6a2a\u6ce2\u9053\uff1a\u201c\u6628\u665a\u6211\u4e2d\u4e86\u7518\u51e4\u6c60 \u7684\u6697\u7b97\uff0c\u4f60\u66ff\u6211\u7528\u6697\u5668\u62a5\u4ec7\u3002\u201d\u97e9\u91cd\u5c71\u8be7\u9053\uff1a\u201c\u7518\u51e4\u6c60\u8fd9\u53ae\u66fe\u5728\u8fd9\u91cc\u5927\u95f9\uff0c\u6211\u6b63\u5bfb\u4ed6\uff0c\u4e0d\u60f3 \u4ed6\u53c8\u53bb\u548c\u4f60\u6363\u4e71\u3002\u5415\u56db\u5a18\u6709\u6ca1\u6709\u51fa\u73b0\uff1f\u201d\u53f6\u6a2a\u6ce2\u9053\uff1a\u201c\u53ea\u4ed6\u4e00\u4eba\u3002\u201d\u97e9\u91cd\u5c71\u9053\uff1a\u201c\u53ea\u4ed6\u4e00\u4eba \u8fd8\u6613\u5bf9\u4ed8\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53ea\u542c\u5f97\u4e09\u58f0\u793c\u70ae\uff0c\u5f20\u5ef7\u7389\u7684\u513f\u5b50\u5df2\u6253\u5f00\u8f66\u95e8\uff0c\u5c06\u65b0\u5a18\u63a5\u51fa\u3002 \u3000\u3000\u51af\u745b\u7684\u8eab\u6750\u548c\u674e\u660e\u73e0\u76f8\u82e5\uff0c\u53c8\u62ab\u7740\u5934\u7eb1\uff0c\u5927\u5bb6\u90fd\u770b\u4e0d\u51fa\u6765\u3002\u5510\u6653\u6f9c\u7528\u4e86\u6613\u5bb9\u4e39\u53d8\u6362\u9762 \u8c8c\uff0c\u4e5f\u6324\u5728\u4eba\u4e1b\u4e4b\u4e2d\u89c2\u793c\uff0c\u5ffd\u89c9\u8fd9\u65b0\u5a18\u5b50\u80cc\u5f71\u597d\u719f\uff0c\u770b\u4e86\u4e00\u9635\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4e00\u5b9a\u662f\u5979\uff0c\u5979\u600e \u4e48\u8fd9\u6837\u6dd8\u6c14\u554a\uff1f\u201d \u3000\u3000\u9664\u4e86\u5929\u53f6\u6563\u4eba\u4e4b\u5916\uff0c\u4eac\u57ce\u6d3e\u6765\u7684\u597d\u624b\u548c\u5c71\u4e1c\u5de1\u629a\u7684\u6559\u5934\uff0c\u90fd\u9f50\u96c6\u8b66\u536b\uff0c\u76ee\u4e0d\u8f6c\u775b\u7684\u770b\u7740 \u8fd9\u5bf9\u65b0\u4eba\u7f13\u7f13\u7684\u8d70\u4e0a\u5802\u6765\u3002\u53f6\u6a2a\u6ce2\u548c\u4e08\u592b\u8bf4\u4e86\u51e0\u53e5\uff0c\u4fbf\u8d70\u8fdb\u53bb\uff0c\u51c6\u5907\u4ee5\u5e08\u5085\u7684\u8d44\u683c\uff0c\u53d7\u65b0\u4eba \u78d5\u5934\u3002 \u3000\u3000\u4e00\u5bf9\u65b0\u4eba\u7f13\u7f13\u8d70\u5165\u793c\u5802\uff0c\u53f6\u6a2a\u6ce2\u5ffd\u5730\u4e00\u60ca\uff1a\u674e\u660e\u73e0\u7684\u8d70\u8def\u59ff\u6001\u548c\u5e73\u65e5\u751a\u4e0d\u76f8\u4f3c\u3002\u5728\u5927\u5802 \u5e7f\u4f17\u4e4b\u4e2d\u4e0d\u6562\u4f5c\u58f0\u3002\u5916\u9762\u53c8\u662f\u4e09\u58f0\u793c\u70ae\u3002\u8d5e\u793c\u5531\u9053\uff1a\u201c\u65b0\u4eba\u4e0a\u5802\uff0c\u4e94\u4e16\u5176\u660c\u3002\u65b0\u4eba\u53e9\u62dc\u7956 \u5148\uff0c\u53e9\u2014\u2014\u201d\u8fd8\u672a\u5531\u5b8c\uff0c\u65b0\u5a18\u5b50\u628a\u5934\u7eb1\u4e00\u626f\uff0c\u55d6\u7684\u4e00\u58f0\u62d4\u51fa\u77ed\u5251\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u8c01\u662f\u4f60\u4eec\u7684\u65b0 \u5a18\uff01\u201d\u5f20\u5ef7\u7389\u7684\u513f\u5b50\u672c\u6765\u6276\u7740\u5979\u7684\u624b\uff0c\u7ed9\u5979\u7528\u529b\u4e00\u634f\uff0c\u987f\u65f6\u6740\u732a\u822c\u7684\u5927\u53eb\u8d77\u6765\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u559c\u7b75\u817e\u6740\u6c14\uff0c\u7389\u5973\u95f9\u534e\u5802\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u56db\u56de\u3000\u7ea2\u70db\u9ad8\u70e7\u3000\u559c\u7b75\u817e\u6740\u6c14\u3000\u5bd2\u6f6e\u4f4e\u62cd\u3000\u6d77\u89d2\u76fc\u5b64\u821f \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u56db\u56de \u7ea2\u70db\u9ad8\u70e7\u3000\u559c\u7b75\u817e\u6740\u6c14 \u5bd2\u6f6e\u4f4e\u62cd\u3000\u6d77\u89d2\u76fc\u5b64\u821f \u3000\u3000\u53d8\u51fa\u610f\u5916\uff0c\u6ee1\u5802\u5bbe\u5ba2\u54d7\u7136\u60ca\u53eb\uff0c\u629a\u8859\u9ad8\u624b\uff0c\u7eb7\u7eb7\u6251\u4e0a\u3002\u51af\u745b\u53eb\u9053\uff1a\u201c\u5f20\u5ef7\u7389\uff0c\u4f60\u60f3\u8981\u4f60 \u513f\u5b50\u4e27\u547d\uff0c\u4fbf\u5c3d\u7ba1\u53eb\u4eba\u4e0a\u6765\u3002\u201d\u77ed\u5251\u62b5\u7740\u5f20\u516c\u5b50\u540e\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u5e26\u6211\u5230\u98de\u7fe0\u697c\u53bb\u3002\u201d \u3000\u3000\u5f20\u5ef7\u7389\u53ea\u6b64\u4e00\u5b50\uff0c\u89c6\u540c\u5b9d\u8d1d\uff0c\u6025\u5fd9\u559d\u6b62\u624b\u4e0b\u3002\u773c\u770b\u51af\u745b\u62bc\u7740\u513f\u5b50\u79bb\u5f00\u793c\u5802\uff0c\u76f4\u8d8b\u540e\u56ed\u3002 \u5510\u6653\u6f9c\u53c8\u60ca\u53c8\u559c\uff0c\u8170\u95f4\u88ab\u4eba\u8f7b\u8f7b\u89e6\u4e86\u4e00\u4e0b\uff0c\u53ea\u542c\u5f97\u7518\u51e4\u6c60\u9053\uff1a\u201c\u54b1\u4eec\u5feb\u5230\u5916\u9762\u63a5\u5e94\u3002\u201d \u3000\u3000\u51af\u745b\u6602\u7136\u4ece\u4eba\u4e1b\u4e2d\u7a7f\u8fc7\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u6765\u5230\u540e\u56ed\u3002\u97e9\u91cd\u5c71\u6c14\u7ea2\u4e86\u773c\uff0c\u6697\u5668\u6263\u5728\u624b\u5fc3\uff0c\u5374\u4e0d \u6562\u52a8\u624b\u3002 \u3000\u3000\u5929\u53f6\u6563\u4eba\u89c1\u51af\u745b\u5230\u6765\uff0c\u5927\u4e3a\u5403\u60ca\uff0c\u5f20\u5ef7\u7389\u54c0\u6c42\u9053\uff1a\u201c\u8ba9\u4ed6\u4eec\u8fdb\u53bb\u5427\u3002\u201d\u5929\u53f6\u6563\u4eba\u6325\u624b\u4ee4 \u706b\u7bad\u624b\u6563\u5f00\uff0c\u53f6\u6a2a\u6ce2\u95ee\u4e08\u592b\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u4e0d\u7528\u6697\u5668\uff1f\u201d\u97e9\u91cd\u5c71\u9053\uff1a\u201c\u4f60\u5fd8\u4e86\u5417\uff1f\u5979\u662f\u7687\u4e0a\u5ba0 \u7231\u7684\u4eba\uff0c\u7687\u4e0a\u66fe\u5429\u5490\u6211\u4eec\u6700\u5c11\u5728\u4e00\u5e74\u4e4b\u5185\uff0c\u4e0d\u8bb8\u78b0\u5979\u3002\u201d\u5929\u53f6\u6563\u4eba\u8fc7\u6765\u5546\u91cf\uff0c\u9053\uff1a\u201c\u8d70\u4e86\u9c7c \u4eae\uff0c\u6b64\u4e8b\u975e\u540c\u5c0f\u53ef\uff0c\u4f60\u770b\u5982\u4f55\uff1f\u201d\u97e9\u91cd\u5c71\u9053\uff1a\u201c\u82e5\u7136\u53ea\u662f\u5f20\u5ef7\u7389\u7684\u513f\u5b50\uff0c\u90a3\u4e48\u54b1\u4eec\u628a\u98de\u7fe0\u697c \u6bc1\u4e86\uff0c\u8ba9\u4ed6\u966a\u4e27\uff0c\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u4f46\uff0c\u4f60\u4e0d\u89c1\u90a3\u91ce\u4e2b\u5934\u5417\uff1f\u201d\u5929\u53f6\u6563\u4eba\u9ed8\u7136\u4e0d\u8bed\u3002\u53f6\u6a2a\u6ce2\u5ffd \u9053\uff1a\u201c\u8fd9\u5973\u5b50\u672a\u5fc5\u662f\u7433\u4e2b\u5934\uff0c\u5f85\u6211\u8bd5\u5979\u4e00\u8bd5\u3002\u201d\u97e9\u91cd\u5c71\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u5979\u4e0d\u662f\u7433\u4e2b\u5934\u662f\u8c01\uff1f\u201d \u53f6\u6a2a\u6ce2\u9053\uff1a\u201c\u6709\u4e00\u4eba\u548c\u5979\u6781\u4e3a\u76f8\u4f3c\uff0c\u4e5f\u8bb8\u8fd9\u5973\u5b50\u662f\u53e6\u4e00\u4e2a\u4eba\u3002\u201d\u97e9\u91cd\u5c71\u9053\uff1a\u201c\u8fd8\u662f\u4e0d\u8981\u5192\u8fd9 \u4e2a\u9669\u5427\u3002\u5f04\u4e0d\u597d\uff0c\u54b1\u4eec\u5403\u4e0d\u4e86\u515c\u7740\u8d70\u3002\u201d\u5929\u53f6\u6563\u4eba\u518d\u4e09\u601d\u60f3\u4e5f\u4e0d\u4e3b\u5f20\u5192\u9669\u3002\u8fd9\u65f6\uff0c\u51af\u745b\u5df2\u8d70 \u4e0a\u98de\u7fe0\u697c\uff0c\u89c1\u5230\u9c7c\u4eae\u4e86\u3002 \u3000\u3000\u9c7c\u4eae\u5927\u4e3a\u60ca\u5947\uff0c\u51af\u745b\u9053\uff1a\u201c\u662f\u7518\u5927\u4fa0\u53eb\u6211\u6765\u8bf7\u4f60\u901f\u56de\u9ec4\u6d77\u7684\u3002\u201d\u9c7c\u4eae\u4e00\u8dc3\u800c\u8d77\uff0c\u9053\uff1a \u201c\u4f60\u8bdd\u53ef\u771f\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u4f60\u4e0d\u89c1\u6211\u62bc\u7740\u5f20\u5ef7\u7389\u7684\u513f\u5b50\u5417\uff1f\u201d\u9c7c\u4eae\u53f9\u9053\uff1a\u201c\u6211\u9c7c\u67d0\u76f8\u8bc6\u6ee1\u5929 \u4e0b\uff0c\u4e0a\u81ea\u7687\u5e1d\uff0c\u4e0b\u81f3\u8d70\u5352\uff0c\u90fd\u6709\u6211\u7684\u719f\u4eba\uff0c\u4eca\u513f\u624d\u4ea4\u4e0a\u4e00\u4e2a\u809d\u80c6\u7167\u4eba\u7684\u670b\u53cb\u3002\u201d\u5bf9\u7518\u51e4\u6c60\u7684 \u4e0d\u8ba1\u65e7\u6068\uff0c\u82e6\u5fc3\u76f8\u6551\uff0c\u751a\u4e3a\u611f\u6fc0\u3002 \u3000\u3000\u51af\u745b\u5ffd\u9053\uff1a\u201c\u9c7c\u5927\u738b\uff0c\u542c\u8bf4\u4f60\u85cf\u5b9d\u751a\u591a\uff0c\u662f\u5417\uff1f\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u95ee\u8fd9\u4e2a\u5e72\u561b\uff1f\u201d \u5fc3\u5185\u72d0\u7591\uff0c\u60f3\u9053\uff1a\u96be\u9053\u7518\u51e4\u6c60\u8fd8\u4f1a\u89ca\u89ce\u6211\u7684\u5b9d\u8d1d\uff1f\u51af\u745b\u5c34\u5c2c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u6709\u4e2a\u53d4\u53d4\uff0c\u88ab\u4eba \u6697\u7b97\uff0c\u5403\u4e0b\u4e86\u5f88\u5389\u5bb3\u7684\u6bd2\u9152\uff0c\u4e0d\u77e5\u4f60\u6709\u5426\u89e3\u836f\uff1f\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u4ec0\u4e48\u6bd2\u9152\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u8fc7\u4e00 \u5e74\u624d\u53d1\u4f5c\u7684\u6bd2\u9152\u3002\u5b9e\u5728\u544a\u8bc9\u4f60\u5427\uff0c\u6697\u7b97\u7684\u4eba\u5c31\u662f\u7687\u5e1d\u3002\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u6211\u53ef\u4ece\u6ca1\u542c\u8fc7\u6709\u8fd9\u6837\u7684 \u6bd2\u9152\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u6697\u7b97\u7684\u4eba\u662f\u7687\u5e1d\u5440\uff01\u4ed6\u4eec\u7684\u53e4\u602a\u73a9\u610f\u513f\u591a\u7740\u5462\uff01\u201d\u9c7c\u4eae\u4fa7\u5934\u4e00\u60f3\uff0c\u9053\uff1a \u201c\u6211\u6709\u5343\u5e74\u829d\u8349\uff0c\u80fd\u4e0d\u80fd\u89e3\uff0c\u53ef\u4e0d\u77e5\u9053\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u597d\u574f\u8ba9\u5b83\u4e00\u8bd5\u3002\u201d\u9c7c\u4eae\u82e5\u5728\u5e73\u65f6\uff0c\u4e00 \u5b9a\u7b11\u5979\u7a1a\u6c14\u53ef\u6652\uff0c\u5343\u5e74\u829d\u8349\uff0c\u54ea\u6709\u968f\u4fbf\u4e71\u8bd5\u4e4b\u7406\uff0c\u4f46\u73b0\u5728\u5374\u6beb\u65e0\u6652\u7b11\u5fc3\u601d\uff0c\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\uff0c \u8499\u4f60\u76f8\u6551\uff0c\u8fd9\u4e9b\u8eab\u5916\u4e4b\u7269\u7b97\u5f97\u4ec0\u4e48\uff1f\u4e0d\u8fc7\u829d\u8349\u4e0d\u5728\u8eab\u8fb9\uff0c\u54b1\u4eec\u5148\u56de\u53bb\u518d\u8bf4\u3002\u201d \u3000\u3000\u51af\u745b\u56e0\u4e3a\u592a\u8fc7\u6302\u5fc3\u5510\u6653\u6f9c\u7684\u6bd2\u4f24\uff0c\u6240\u4ee5\u4e00\u89c1\u9c7c\u4eae\uff0c\u4fbf\u51fa\u8a00\u76f8\u95ee\uff0c\u8fd9\u65f6\u60f3\u8d77\u522b\u4eba\u6b63\u662f\u6b7b\u751f \u9003\u547d\u4e4b\u9645\uff0c\u81ea\u5df1\u62ff\u8fd9\u4e9b\u4e8b\u53bb\u9ebb\u70e6\u4ed6\uff0c\u5c82\u975e\u4e0d\u8bc6\u65f6\u52a1\uff0c\u4e0d\u7981\u54d1\u7136\u5931\u7b11\u3002\u5f53\u4e0b\u548c\u9c7c\u4eae\u7b49\u4e00\u884c\u5341\u4f59 \u4eba\u8d70\u51fa\u98de\u7fe0\u697c\u3002 \u3000\u3000\u51af\u745b\u8d70\u5728\u540e\u5934\uff0c\u5251\u5c16\u4ecd\u7136\u62b5\u7740\u5f20\u5ef7\u7389\u513f\u5b50\u7684\u80cc\u5fc3\uff0c\u8d70\u5230\u56ed\u4e2d\uff0c\u8fc7\u4e86\u70b8\u836f\u57cb\u85cf\u7684\u5371\u9669\u5730 \u5e26\uff0c\u7ecf\u8fc7\u53f6\u6a2a\u6ce2\u9762\u524d\uff0c\u53f6\u6a2a\u6ce2\u7a81\u7136\u628a\u624b\u4e00\u626c\uff0c\u6570\u679a\u4e09\u68f1\u900f\u9aa8\u9489\u9f50\u5411\u51af\u745b\u98de\u6765\uff0c\u53ea\u8981\u51af\u745b\u7528\u5251 \u906e\u62e6\uff0c\u7acb\u523b\u4fbf\u53ef\u77e5\u9053\u5979\u7684\u8eab\u4efd\u3002\u54ea\u77e5\u4eba\u4e1b\u4e2d\u7a81\u7136\u8df3\u51fa\u4e00\u4eba\uff0c\u4e00\u4e3e\u624b\u5c31\u5c06\u53f6\u6a2a\u6ce2\u7684\u900f\u9aa8\u9489\u5168\u6536 \u4e86\u53bb\u3002\u8fd9\u4eba\u5374\u662f\u7518\u51e4\u6c60\u3002 \u3000\u3000\u5f20\u5ef7\u7389\u97e9\u91cd\u5c71\u9f50\u9053\uff1a\u201c\u4f7f\u4e0d\u5f97\uff01\u201d\u6025\u628a\u53f6\u6a2a\u6ce2\u63a8\u5f00\uff0c\u7518\u51e4\u6c60\u559d\u9053\uff1a\u201c\u54fc\uff0c\u73b0\u5728\u4f60\u8fd8\u60f3\u6697 \u7b97\u5417\uff1f\u201d\u5510\u6653\u6f9c\u4e5f\u8df3\u4e86\u51fa\u6765\uff0c\u548c\u9c7c\u4eae\u7b49\u4eba\u56f4\u6210\u4e00\u5708\uff0c\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u670d\uff0c\u54b1\u4eec\u5c31\u5728\u56ed\u4e2d \u518d\u6597\u4e00\u6597\u3002\u201d\u5f20\u5ef7\u7389\u5fd9\u9053\uff1a\u201c\u597d\u6c49\u4eec\u8bf7\u8d70\u3002\u6211\u7684\u513f\u5b50\u4f60\u4eec\u653e\u4e86\u4ed6\u5427\uff01\u201d\u7518\u51e4\u6c60\u51b7\u7b11\u9053\uff1a\u201c\u5230 \u4e86\u6d77\u8fb9\uff0c\u6211\u4eec\u81ea\u7136\u653e\u8d70\u4f60\u7684\u513f\u5b50\u3002\u201d\u53f6\u6a2a\u6ce2\u4f30\u91cf\uff0c\u8fd9\u65f6\u4e24\u8fb9\u62fc\u6597\uff0c\u672a\u5fc5\u80fd\u8d62\uff0c\u4f55\u51b5\u8fd8\u4e0d\u77e5\u9053 \u8fd9\u5973\u5b50\u662f\u4e0d\u662f\u51af\u7433\uff0c\u4e5f\u4fbf\u4e0d\u6562\u4f5c\u58f0\uff0c\u6084\u6084\u6e9c\u5f00\u3002 \u3000\u3000\u8fc7\u4e86\u5341\u5929\uff0c\u9c7c\u4eae\u7b49\u4e00\u884c\u4eba\u5230\u4e86\u6d77\u8fb9\uff0c\u51af\u745b\u5c06\u5f20\u5ef7\u7389\u7684\u513f\u5b50\u6253\u4e86\u4e24\u8bb0\u8033\u5149\uff0c\u7518\u51e4\u6c60\u8bad\u8beb\u4e86 \u4ed6\u4e00\u987f\uff0c\u4f9d\u7ea6\u5c06\u4ed6\u653e\u8d70\u3002\u9c7c\u4eae\u627e\u5230\u4e86\u90e8\u5c5e\uff0c\u4e58\u4e86\u4e00\u6761\u5927\u8239\uff0c\u626c\u5e06\u51fa\u6d77\u3002\u7518\u51e4\u6c60\u7b49\u4eba\u548c\u4ed6\u540c \u5f80\u3002\u5230\u8fd9\u65f6\u624d\u77e5\u9053\u5e74\u7fb9\u5c27\u7684\u6c34\u5e08\uff0c\u56f4\u653b\u6c34\u5be8\u5df2\u4e00\u4e2a\u591a\u6708\uff0c\u60f3\u4ece\u6b63\u9762\u5077\u6e21\uff0c\u5b9e\u4e0d\u53ef\u80fd\uff0c\u9c7c\u4eae\u719f \u6089\u6c34\u9053\uff0c\u53eb\u7ed5\u9053\u65c5\u987a\u53e3\u5916\u7684\u6d77\u9762\u8f6c\u8fc7\u9ec4\u6d77\uff0c\u8054\u7edc\u6d77\u5916\u5404\u5c9b\u6c34\u5bc7\uff0c\u51c6\u5907\u805a\u5175\u89e3\u56f4\u3002 \u3000\u3000\u51af\u745b\u81ea\u5e7c\u5c45\u4f4f\u5929\u5c71\uff0c\u4e4d\u89c1\u5927\u6d77\uff0c\u5341\u5206\u9ad8\u5174\uff0c\u6d77\u6d0b\u4e2d\u7684\u751f\u7269\u4e94\u82b1\u516b\u95e8\uff0c\u65e0\u5947\u4e0d\u6709\u3002\u9c7c\u4eae\u4e00 \u4e00\u66ff\u4ed6\u4eec\u89e3\u91ca\uff1a\u90a3\u50cf\u4f1e\u5b50\u4e00\u6837\uff0c\u5728\u6d77\u9762\u98d8\u6d6e\u7684\u53eb\u505a\u6c34\u6bcd\uff1b\u5c3e\u5df4\u50cf\u4e00\u6761\u7ec6\u957f\u800c\u575a\u97e7\u7684\u5e26\u5b50\uff0c\u7259 \u9f7f\u4f38\u5f00\u50cf\u5c71\u9e21\u5634\u5df4\u7684\u53eb\u505a\u201c\u5858\u9e49\u5927\u5634\u9c7c\u201d\uff0c\u5b83\u6c38\u8fdc\u5f20\u5f00\u5634\u5df4\uff0c\u5c31\u50cf\u4e00\u4e2a\u5929\u7136\u7684\u5927\u9c7c\u7f51\uff0c\u53ef\u4ee5 \u4ee5\u9038\u4ee3\u52b3\u5730\u7b49\u5f85\u4e00\u4e9b\u5c0f\u9c7c\u81ea\u6295\u7f57\u7f51\uff1b\u90a3\u4e00\u5f20\u5634\u4fbf\u5410\u51fa\u4e00\u5927\u56e2\u6f06\u9ed1\u7684\u58a8\u6c34\uff0c\u63a5\u7740\u5c31\u5728\u70df\u5e55\u4e2d\u9003 \u5f97\u65e0\u5f71\u5143\u8e2a\u7684\u53eb\u505a\u58a8\u9c7c\uff1b\u8fd8\u6709\u4e00\u79cd\u5f20\u4e86\u7fc5\u8180\u7684\u98de\u9c7c\uff0c\u5728\u6d77\u9762\u4e0a\u7a7a\u50cf\u4e00\u53ea\u6d77\u71d5\u4f3c\u7684\u654f\u6377\u98de\u821e\uff0c \u4f46\u7728\u773c\u4e4b\u95f4\uff0c\u5b83\u53c8\u5728\u6d77\u6c34\u91cc\u81ea\u7531\u81ea\u5728\u5730\u6e38\u6cf3\u4e86\u3002\u51af\u745b\u76ee\u4e0d\u6687\u63a5\uff0c\u542c\u9c7c\u4eae\u8bf4\u5f97\u6d25\u6d25\u6709\u5473\u3002 \u3000\u3000\u822a\u884c\u4e24\u5929\uff0c\u5230\u4e86\u6e24\u6d77\u4e0e\u9ec4\u6d77\u8fde\u63a5\u4e4b\u5904\uff0c\u8fd9\u65e5\u65e9\u6668\uff0c\u5929\u8272\u751a\u597d\uff0c\u8fdc\u5904\u6d77\u9762\u95ea\u8000\u7740\u4e00\u7247\u84dd\u7eff \u8272\u7684\u78f7\u5149\uff0c\u968f\u6ce2\u8d77\u4f0f\uff0c\u661f\u7fa4\u7a00\u843d\uff0c\u5929\u8272\u5fae\u660e\uff0c\u5929\u7a7a\u521d\u9732\u9c7c\u809a\u767d\u8272\uff0c\u5ffd\u800c\u53d8\u4e3a\u6de1\u7d2b\uff0c\u6162\u6162\u53c8\u653e \u7ea2\u5149\uff1b\u4e91\u5f69\u91d1\u9ec4\uff0c\u6d77\u6ce2\u660e\u4eae\u3002\u51af\u745b\u770b\u5f97\u51fa\u795e\uff0c\u7b11\u5bf9\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u53ea\u9053\u5929\u5c71\u65e5\u51fa\u662f\u4e16\u4e0a\u65e0\u53cc \u7684\u5947\u666f\uff0c\u54ea\u77e5\u5728\u6d77\u4e0a\u770b\u65e5\u51fa\u8fd8\u8981\u7f8e\u4e3d\u5f97\u591a\u3002\u201d\u9c7c\u4eae\u7b11\u9053\uff1a\u201c\u4f60\u770b\u5f97\u591a\u4e86\uff0c\u4f46\u4e0d\u89c9\u5f97\u7a00\u5947\u4e86\u3002 \u6211\u5012\u5f88\u60f3\u5230\u5929\u5c71\u770b\u770b\u65e5\u51fa\u5462\uff01\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u770b\u6765\u4eca\u65e5\u53c8\u662f\u5e73\u9759\u65e0\u6ce2\u4e86\u3002\u6211\u5e38\u542c\u8bf4\u6d77\u4e0a\u98ce\u6d6a \u9669\u6076\uff0c\u539f\u6765\u4e5f\u4e0d\u8fc7\u5982\u6b64\u3002\u201d\u9c7c\u4eae\u76b1\u7709\u4e0d\u8bed\uff0c\u539f\u6765\u8fd9\u65f6\u8282\u6b63\u662f\u6e24\u6d77\u98ce\u66b4\u7684\u5b63\u8282\uff0c\u82e5\u975e\u4e3a\u4e86\u6025 \u4e8b\uff0c\u9c7c\u4eae\u8fd8\u771f\u4e0d\u6562\u626c\u5e06\u51fa\u6d77\u3002\u8fd9\u51e0\u5929\u5929\u6c14\u5f02\u5e38\u6674\u6717\uff0c\u9c7c\u4eae\u9884\u611f\u5230\u8fd9\u6b63\u662f\u6d77\u4e0a\u915d\u917f\u7740\u5927\u98ce\u66b4\u3002 \u679c\u7136\u5230\u4e86\u4e2d\u5348\u65f6\uff0c\u5929\u4e0a\u54cd\u4e86\u51e0\u58f0\u95f7\u96f7\uff0c\u5929\u8272\u7a81\u53d8\uff0c\u65cb\u98ce\u9aa4\u8d77\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u4fbf\u95fb\u5f97\u6d77\u5578\u5982\u96f7\u3002 \u536b\u626c\u5a01\u60ca\u9053\uff1a\u201c\u6d77\u4e0a\u98ce\u66b4\u6765\u4e86\uff01\u201d \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u53f0\u98ce\u626c\u6ce2\uff0c\u6d6a\u6d9b\u50cf\u4e00\u4e2a\u4e2a\u5c71\u5cf0\u822c\u7684\u51b2\u6765\uff0c\u6d6a\u82b1\u98de\u4e0a\u534a\u5929\uff0c\u58f0\u52bf\u60ca\u4eba\u4e4b\u6781\u3002\u9c7c \u4eae\u53f9\u9053\uff1a\u201c\u6211\u7d2f\u4e86\u4f60\u4eec\u4e86\u3002\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u540c\u821f\u5171\u6d4e\uff0c\u6b64\u6b63\u5176\u65f6\u3002\u201d\u52a9\u9c7c\u4eae\u6276\u7740\u8235\u67c4\uff0c\u5f3a \u529b\u628a\u6301\u3002\u90a3\u8239\u4e1c\u5012\u897f\u6b6a\uff0c\u6d77\u6c34\u6e85\u5165\uff0c\u8239\u4e2d\u5404\u4eba\u5373\u523b\u52a8\u624b\uff0c\u628a\u6c34\u8200\u51fa\uff0c\u53c8\u5378\u4e0b\u98ce\u5e06\uff0c\u624b\u5fd9\u811a \u4e71\u3002\u51af\u745b\u5ffd\u7136\u60ca\u53eb\u9053\uff1a\u201c\u554a\uff0c\u5927\u6d77\u602a\u6765\u4e86\uff01\u201d \u3000\u3000\u9c7c\u4eae\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u6761\u5927\u9cb8\u9c7c\u50cf\u4e0a\u4e00\u5ea7\u5c0f\u5c71\u822c\u6d6e\u51fa\u6d77\u9762\uff0c\u55b7\u51fa\u4e00\u6761\u6c34\u67f1\uff0c\u6b63\u5411\u5927\u8239\u6e38 \u6765\u3002\u9c7c\u4eae\u6025\u9053\uff1a\u201c\u51b3\u8f6c\u8235\u907f\u5b83\uff01\u201d\u767e\u5fd9\u4e2d\u8fd8\u5b89\u6170\u51af\u745b\u9053\uff1a\u201c\u8fd9\u662f\u9cb8\u9c7c\uff0c\u4e0d\u662f\u6d77\u602a\u3002\u9cb8\u9c7c\u6027\u5b50 \u548c\u5584\uff0c\u4e0d\u5403\u4eba\u7684\u3002\u201d\u5176\u5b9e\u9cb8\u9c7c\u867d\u4e0d\u5403\u4eba\uff0c\u53ef\u662f\u8239\u53ea\u7ed9\u5b83\u4e00\u78b0\uff0c\u5341\u4e5d\u8986\u6ca1\uff0c\u90a3\u53ef\u8981\u6bd4\u5403\u4eba\u7684\u9ca8 \u9c7c\u8fd8\u53ef\u6015\u5f97\u591a\u3002 \u3000\u3000\u5ffd\u5730\u547c\u5587\u5de8\u54cd\uff0c\u6d2a\u5cf0\u538b\u9876\uff0c\u5927\u8239\u7ed9\u6d6a\u4e00\u629b\uff0c\u649e\u5728\u9cb8\u9c7c\u7684\u5c3e\u90e8\uff0c\u88ab\u9cb8\u9c7c\u4e00\u6446\uff0c\u8239\u4e0a\u8bf8\u4eba\uff0c \u867d\u7136\u90fd\u662f\u6b66\u529f\u7edd\u9879\uff0c\u5168\u90e8\u7ed9\u9707\u5012\u8239\u4e0a\u3002\u5e78\u559c\u4e0d\u662f\u548c\u5de8\u9cb8\u7684\u5934\u8179\u76f8\u649e\uff0c\u8981\u4e0d\u7136\u5b9a\u6c89\u6ca1\u4e86\u3002\u4f46\u867d \u7136\u5982\u6b64\uff0c\u8239\u8231\u5df2\u7ed9\u649e\u7834\u4e00\u4e2a\u5927\u6d1e\uff0c\u6845\u6746\u4e5f\u65ad\u4e86\u3002\u9c7c\u4eae\u53eb\u9053\uff1a\u201c\u5feb\u5835\u7740\u88c2\u53e3\u3002\u201d\u7518\u51e4\u6c60\u7b49\u4eba\u5404 \u62b1\u68c9\u88ab\uff0c\u548c\u8eab\u585e\u7740\u88c2\u53e3\uff0c\u6d77\u6c34\u704c\u9f3b\uff0c\u751a\u4e3a\u96be\u53d7\uff0c\u88c2\u6d1e\u867d\u7ed9\u5835\u4f4f\uff0c\u6d77\u6c34\u8fd8\u662f\u7f13\u7f13\u6f2b\u5165\uff01 \u3000\u3000\u9c7c\u4eae\u53f9\u9053\uff1a\u201c\u6211\u4e00\u751f\u5728\u6d77\u4e0a\u79f0\u738b\uff0c\u96be\u9053\u4e5f\u8981\u6b7b\u5728\u6d77\u4e0a\uff1f\u201d\u53f0\u98ce\u53c8\u8d77\uff0c\u5927\u8239\u867d\u7136\u5378\u4e86\u5e06\uff0c \u4ecd\u7136\u7ed9\u98ce\u522e\u5f97\u5982\u7bad\u98de\u8d70\uff0c\u8fd9\u65f6\u7eb5\u6709\u5343\u94a7\u4e4b\u529b\uff0c\u4e5f\u96be\u5c06\u8235\u628a\u7a33\u3002\u9c7c\u4eae\u9053\uff1a\u201c\u8fd9\u8239\u65e0\u6cd5\u6551\u4e86\uff0c\u7518 \u5927\u4fa0\uff0c\u8bf7\u5728\u4e34\u6b7b\u4e4b\u524d\u53d7\u6211\u4e00\u62dc\u3002\u201d\u7518\u51e4\u6c60\u559d\u9053\uff1a\u201c\u4e00\u606f\u5c1a\u5b58\uff0c\u51b3\u4e0d\u653e\u624b\u3002\u54b1\u4eec\u8981\u6b7b\u91cc\u9003\u751f\u3002\u201d \u4e00\u9762\u6307\u6325\u4eba\u7ee7\u7eed\u5835\u7740\u88c2\u53e3\uff0c\u4e00\u9762\u8fd0\u201c\u5343\u65a4\u5760\u201d\u7684\u5185\u5bb6\u529f\u529b\uff0c\u7a33\u7740\u8239\u8eab\uff0c\u867d\u7136\u822c\u8eab\u4ecd\u662f\u52a8\u8361\u4e0d \u5df1\uff0c\u53ef\u662f\u5374\u6bd4\u524d\u597d\u4e86\u4e00\u4e9b\u3002\u9c7c\u4eae\u6697\u6697\u53eb\u58f0\uff1a\u201c\u60ed\u6127\u201d\u3002\u5fc3\u9053\uff1a\u7518\u51e4\u6c60\u867d\u7136\u4e0d\u4f3c\u81ea\u5df1\u7cbe\u901a\u6c34\u6027\uff0c \u53ef\u662f\u5728\u751f\u6b7b\u4e4b\u9645\uff0c\u5374\u6bd4\u81ea\u5df1\u9547\u5b9a\u5f97\u591a\u3002 \u3000\u3000\u8239\u4e0a\u8bf8\u4eba\u5408\u529b\u575a\u6301\uff0c\u6f06\u9ed1\u4e2d\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5ffd\u5730\u4e00\u58f0\u5de8\u664c\uff0c\u8239\u8eab\u89e6\u7901\uff0c\u7518\u51e4\u6c60\u5927 \u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u5c3d\u4e86\u529b\u8fd8\u4e0d\u80fd\u633d\u56de\u52ab\u96be\uff0c\u4e5f\u53ea\u597d\u7531\u5b83\u4e86\u3002\u9c7c\u4eae\u5374\u5927\u559c\u53eb\u9053\uff1a\u201c\u6709\u6551\u4e86\u3002\u6e24\u6d77 \u8fd9\u6bb5\u6c34\u9762\uff0c\u6ca1\u6709\u5de8\u7901\uff0c\u6211\u4eec\u7684\u8239\u73b0\u5728\u6401\u5e95\uff0c\u60f3\u5fc5\u662f\u98d8\u6d6e\u5230\u4ec0\u4e48\u6d77\u5c9b\u6765\u4e86\u3002\u201d\u8df3\u4e0b\u6c34\u53bb\uff0c\u6c34\u6d78 \u81f3\u5589\uff0c\u5b9a\u775b\u5bdf\u770b\uff0c\u679c\u7136\u662f\u4e2a\u5c0f\u5c9b\u3002 \u3000\u3000\u9c7c\u4eae\u9053\uff1a\u201c\u56f0\u5728\u6d77\u5c9b\uff0c\u8239\u4e0a\u8fd8\u5269\u4e0b\u7684\u4e1c\u897f\uff0c\u90fd\u4e0d\u80fd\u4e22\u4e86\u3002\u4e5f\u8bb8\u54b1\u4eec\u8981\u8fc7\u91ce\u4eba\u7684\u751f\u6d3b\u4e86\u3002\u201d \u548c\u7518\u51e4\u6c60\u7b49\u5408\u529b\u5c06\u8239\u62d6\u8fd1\u9646\u5730\uff0c\u8fd9\u65f6\u6d77\u4e0a\u98ce\u66b4\u5df2\u6b62\uff0c\u4e1c\u65b9\u53c8\u73b0\u51fa\u9c7c\u809a\u767d\u8272\uff0c\u539f\u6765\u4ed6\u4eec\u5728\u6d77\u4e0a \u98d8\u6d6e\uff0c\u5df2\u8fc7\u4e86\u4e00\u5929\u4e00\u591c\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u5728\u6c99\u6ee9\u4e0a\u6b47\u4e86\u4e00\u9635\uff0c\u5403\u4e86\u5e72\u7cae\uff0c\u6062\u590d\u4f53\u529b\uff0c\u6d77\u98ce\u5439\u6765\uff0c\u5f02\u9999\u6251\u9f3b\uff0c\u9999\u6c14\u4e2d\u5374\u53c8\u5e26\u7740 \u8165\u5473\uff0c\u8fdc\u671b\u8fc7\u53bb\uff0c\u53ea\u89c1\u7eff\u836b\u8986\u5168\u5c9b\uff0c\u82b1\u5f00\u6811\u4e0a\uff0c\u707f\u5982\u4e91\u971e\uff0c\u9c7c\u4eae\u5fc3\u9053\uff1a\u8fd9\u662f\u4ec0\u4e48\u82b1\uff1f\u6211\u5728\u5404 \u5904\u6d77\u5c9b\uff0c\u4ece\u672a\u89c1\u8fc7\uff0c\u600e\u4e48\u9999\u6c14\u5982\u6b64\u5947\u602a\u3002 \u3000\u3000\u592a\u9633\u5347\u8d77\u6d77\u9762\uff0c\u4f17\u4eba\u4f53\u529b\u6062\u590d\u3002\u9c7c\u4eae\u80cc\u4e86\u836f\u56ca\uff0c\u5e26\u9886\u4f17\u4eba\u8fdb\u5165\u6d77\u5c9b\u3002\u9c7c\u4eae\u7684\u4e00\u4e2a\u968f\u4ece\u5ffd \u9053\uff1a\u201c\u4f60\u770b\u90a3\u662f\u4e0d\u662f\u6986\u6811\uff0c\u600e\u4e48\u957f\u5f97\u5982\u6b64\u5947\u602a\uff1f\u201d \u3000\u3000\u90a3\u6986\u6811\u679d\u5e72\u5f2f\u5f2f\u66f2\u66f2\uff0c\u604d\u7136\u86c7\u5f62\uff0c\u6709\u4e00\u4e2a\u968f\u4ece\u7981\u4e0d\u4f4f\u7528\u624b\u53bb\u6478\uff0c\u9c7c\u4eae\u5fc3\u4e2d\u4e00\u51db\uff0c\u6025\u53eb \u9053\uff1a\u201c\u5feb\u8d70\uff01\u201d\u90a3\u968f\u4ece\u7a81\u7136\u60e8\u53eb\u4e00\u58f0\uff0c\u8eab\u5b50\u4ec6\u5012\uff0c\u6811\u4e0a\u98de\u8d77\u4e86\u4e00\u6761\u957f\u86c7\uff0c\u5510\u6653\u6f9c\u62d4\u51fa\u6e38\u9f99\u5251 \u5c06\u86c7\u65a9\u65ad\u3002\u540c\u884c\u4e2d\u5ffd\u5730\u53c8\u6709\u4e24\u4eba\u5927\u53eb\uff0c\u539f\u6765\u662f\u8e0f\u7740\u4e86\u76d8\u5728\u8def\u4e0a\u7684\u5927\u86c7\uff0c\u5e78\u559c\u6ca1\u6709\u7ed9\u5b83\u54ac\u7740\u3002 \u3000\u3000\u9c7c\u4eae\u9053\uff1a\u201c\u5feb\u9000\u51fa\u5916\u9762\u6c99\u6ee9\u3002\u201d\u6811\u6797\u4e2d\u6c99\u6c99\u4e4b\u58f0\u5927\u4f5c\uff0c\u65e0\u6570\u957f\u86c7\u7a9c\u4e86\u51fa\u6765\uff0c\u9c7c\u4eae\u53eb\u7518\u51e4 \u6c60\u5510\u6653\u6f9c\u4e0e\u4ed6\u6bbf\u540e\uff0c\u7528\u77f3\u5934\u6253\u6b7b\u51e0\u6761\u8ffd\u51fa\u6765\u7684\u86c7\uff0c\u9000\u5230\u6d77\u6ee9\uff0c\u90a3\u88ab\u86c7\u6240\u54ac\u7684\u4eba\uff0c\u5df2\u662f\u5168\u8eab\u7d2b \u9ed1\uff0c\u4e0d\u80fd\u6551\u6cbb\u3002\u4f17\u4eba\u5927\u9a87\u3002\u9c7c\u4eae\u5728\u888b\u4e2d\u53d6\u51fa\u4e00\u5927\u5757\u96c4\u9ec4\uff0c\u53eb\u6bcf\u4eba\u5206\u4e00\u5757\uff0c\u60ac\u5728\u8eab\u4e0a\uff0c\u5fe7\u5f62\u4e8e \u8272\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5230\u4e86\u5929\u4e0b\u7684\u7b2c\u4e00\u9b54\u5c9b\u4e86\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u9a87\u7136\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u9b54\u5c9b\uff0c\u5c9b\u4e0a\u96be\u9053\u6709\u9b54\u602a\u5417\uff1f\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u7518\u5927\u4fa0\u6709\u6240\u4e0d\u77e5\u3002\u65c5 \u987a\u53e3\u897f\u5317\u6d77\u9762\u6709\u4e2a\u5c0f\u5c9b\uff0c\u53eb\u505a\u86c7\u5c9b\uff0c\u5c9b\u4e0a\u6bd2\u86c7\u65e0\u6570\uff0c\u770b\u6b64\u60c5\u5f62\uff0c\u8fd9\u4e2a\u5c9b\u662f\u86c7\u5c9b\u4e86\u3002\u6bd2\u86c7\u7684\u53e3 \u6d8e\u53ef\u6cbb\u9ebb\u75af\uff0c\u5c9b\u4e0a\u6709\u4e00\u79cd\u6811\u4fbf\u53eb\u9ebb\u75af\u6811\uff0c\u5f00\u7684\u82b1\u7ecf\u971c\u4e0d\u51cb\uff0c\u6240\u4ee5\u53c8\u53eb\u907f\u971c\u82b1\u3002\u6211\u521a\u624d\u95fb\u5f97\u90a3 \u9ebb\u75af\u6811\u4e0a\u7684\u82b1\u9999\uff0c\u4e00\u65f6\u7701\u89c9\u4e0d\u8d77\uff0c\u8981\u4e0d\u7136\u6211\u4e5f\u4e0d\u4f1a\u5192\u6627\u8fdb\u53bb\u4e86\u3002\u86c7\u6027\u559c\u6b22\u6797\u4e2d\u6f6e\u6e7f\u4e4b\u5730\uff0c\u9664 \u975e\u662f\u51fa\u6765\u6652\u592a\u9633\uff0c\u5426\u5219\u5f88\u5c11\u5230\u6d77\u6ee9\u5e72\u71e5\u4e4b\u5730\uff0c\u54b1\u4eec\u53ef\u4ee5\u6682\u5b89\u3002\u201d\u5510\u6653\u6f9c\u7701\u8d77\u4e00\u4e8b\uff0c\u95ee\u9053\uff1a \u201c\u86c7\u5c9b\u9644\u8fd1\u662f\u4e0d\u662f\u6709\u4e2a\u5c0f\u5c9b\u53eb\u505a\u732b\u9e70\u5c9b\uff1f\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u662f\u5440\uff0c\u672c\u6765\u53eb\u505a\u6d77\u732b\u5c9b\u7684\uff0c\u56e0\u4e3a\u5b83\u98de \u65f6\u53d1\u51fa\u4f3c\u732b\u7684\u53eb\u58f0\uff0c\u6240\u4ee5\u88ab\u4eba\u53eb\u505a\u6d77\u732b\uff0c\u5176\u5b9e\u5b83\u5e76\u4e0d\u50cf\u732b\u3002\u8fd9\u4e24\u4e2a\u5c9b\u81ea\u53e4\u4ee5\u6765\uff0c\u65e0\u4eba\u6562\u5230\u3002 \u5c24\u4ee5\u86c7\u5c9b\u66f4\u751a\uff0c\u522b\u4eba\u542c\u5230\u8fd9\u4e2a\u540d\u5b57\u4e5f\u5bb3\u6015\u4e86\uff01\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u90a3\u8428\u6c0f\u53cc\u9b54\u4e0d\u662f\u5728\u732b\u9e70\u5c9b\u51fa\u6765 \u7684\u5417\uff1f\u201d\u9c7c\u4eae\u7b11\u9053\uff1a\u201c\u6211\u8bb0\u8d77\uff01\u90a3\u6b21\u5728\u7530\u6a2a\u5c9b\u805a\u4f1a\u65f6\uff0c\u53cc\u9b54\u8fd8\u60f3\u6536\u4f60\u505a\u5f92\u5f1f\u5462\uff01\u201d\u7a81\u7136\u60f3\u8d77 \u5f53\u5e74\u5f00\u5e9c\u79f0\u738b\u4e4b\u4e8b\uff0c\u4e0d\u7981\u9eef\u7136\u3002\u6b47\u4e86\u4e00\u6b47\uff0c\u624d\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5c9b\u81ea\u53e4\u4ee5\u6765\uff0c\u65e0\u4eba\u6562\u5f80\uff0c \u4f46\u5230\u4e86\u8fd1\u51e0\u5341\u5e74\uff0c\u5374\u7ed9\u4e09\u4e2a\u602a\u4eba\u76d8\u8e1e\uff0c\u4f4f\u5728\u732b\u9e70\u5c9b\u7684\u4e43\u662f\u8428\u6c0f\u53cc\u9b54\uff0c\u4ed6\u4eec\u6b66\u529f\u867d\u9ad8\uff0c\u4e5f\u8fd8\u4e0d \u8db3\u4ee4\u4eba\u9707\u9a87\u3002\u76d8\u8e1e\u5728\u86c7\u5c9b\u7684\u4eba\uff0c\u542c\u8bf4\u6b66\u529f\u5374\u662f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u867d\u7136\u6ca1\u4eba\u89c1\u8fc7\uff0c\u4f46\u4ed6\u80fd\u9a6f\u670d\u4f17\u86c7\uff0c \u53ea\u6b64\u4e00\u70b9\uff0c\u5df2\u662f\u4ee4\u4eba\u9a87\u5f02\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u542c\u6c5f\u6e56\u524d\u8f88\u4f20\u8a00\uff0c\u4f4f\u5728\u86c7\u5c9b\u7684\u5f02\u4eba\u540d\u53eb\u6bd2\u9f99\u5c0a \u8005\uff0c\u771f\u6709\u8fd9\u6837\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u9c7c\u4eae\u9053\uff1a\u201c\u5982\u4f55\u4e0d\u771f\uff01\u5141\u796f\u66fe\u51e0\u6b21\u8bf7\u4ed6\u51fa\u5c71\u4ed6\u90fd\u4e0d\u5141\uff0c\u6211\u4e5f\u66fe\u53eb\u53cc\u9b54\u7ea6\u4ed6\u76f8\u89c1\uff0c\u4ed6\u4e5f\u4e0d \u80af\u3002\u542c\u53cc\u9b54\u8bf4\u6b64\u4eba\u672c\u6765\u662f\u4e00\u4e2a\u60a3\u4e86\u5927\u9ebb\u75af\u7684\u75c5\u4eba\uff0c\u6043\u7740\u4e00\u8eab\u6b66\u529f\uff0c\u5fc3\u60f3\u53cd\u6b63\u4e0d\u80fd\u5bb9\u4e8e\u4eba\u4e16\uff0c \u4f55\u4e0d\u5230\u86c7\u5c9b\u78b0\u78b0\u8fd0\u6c14\uff0c\u53d6\u6bd2\u86c7\u6db2\u548c\u9ebb\u75af\u75c5\u6811\u4e0a\u7684\u82b1\u6765\u6cbb\u3002\u540e\u6765\u4ed6\u5728\u5c9b\u4e0a\u4f4f\u4e86\u51e0\u5e74\uff0c\u9ebb\u75af\u533b\u597d \u4e86\uff0c\u4ed6\u548c\u86c7\u4e5f\u53ae\u6df7\u719f\u4e86\uff0c\u60f3\u8d77\u4ee5\u524d\u60a3\u4e86\u9ebb\u75af\u4e4b\u65f6\uff0c\u4e16\u4eba\u5bf9\u4ed6\u7684\u51b7\u6de1\uff0c\u53cd\u800c\u4e0d\u613f\u51fa\u53bb\u4e86\u3002\u4ed6\u7684 \u6027\u60c5\u4e5f\u8d8a\u6765\u8d8a\u602a\uff0c\u8fde\u53cc\u9b54\u90a3\u6837\u7684\u4e24\u4e2a\u9b54\u5934\uff0c\u867d\u7136\u548c\u4ed6\u6709\u6765\u5f80\uff0c\u5bf9\u4ed6\u4e5f\u975e\u5e38\u5fcc\u60ee\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5c9b\u4e0a\u86c7\u58f0\u53c8\u4f5c\u3002\u536b\u626c\u5a01\u60ca\u53eb\u9053\uff1a\u201c\u6bd2\u9f99\u5c0a\u8005\u6765\u4e86\uff01\u201d\u7518\u51e4\u6c60\u7741\u773c\u4e00\u770b\uff0c\u53ea\u89c1 \u4e00\u4e2a\u91ce\u4eba\u62ab\u5934\u6563\u53d1\uff0c\u80cc\u540e\u8ddf\u7740\u4e00\u5927\u7fa4\u86c7\uff0c\u5c06\u5230\u6d77\u6ee9\uff0c\u5ffd\u628a\u86c7\u7fa4\u559d\u4f4f\uff0c\u5927\u6b65\u8d70\u4e0a\u524d\u6765\u3002 \u3000\u3000\u9c7c\u4eae\u6025\u5fd9\u8fce\u4e0a\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u9ec4\u6d77\u9c7c\u4eae\u4e0e\u6c5f\u5357\u7518\u51e4\u6c60\u5076\u9047\u53f0\u98ce\uff0c\u8bef\u95ef\u5b9d\u5c9b\uff0c\u656c\u4e5e\u5c0a\u8005\u89c1 \u8c05\uff01\u201d\u6bd2\u9f99\u5c0a\u8005\u7ffb\u7740\u4e00\u53cc\u602a\u773c\uff0c\u4e0d\u7406\u4e0d\u776c\u3002\u9c7c\u4eae\u53c8\u9053\uff1a\u201c\u9c7c\u67d0\u4ee5\u524d\u66fe\u6258\u8428\u5bb6\u5144\u5f1f\u4fee\u51fd\u95ee\u597d\uff0c \u8c05\u63d0\u5c0a\u9274\u3002\u201d\u6bd2\u9f99\u5c0a\u8005\u77aa\u773c\u73af\u626b\u4f17\u4eba\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u4eec\u4e4b\u4e2d\u6709\u60a3\u9ebb\u75af\u7684\u5417\uff1f\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u6ca1 \u6709\u3002\u201d\u6bd2\u9f99\u5c0a\u8005\u563f\u563f\u602a\u7b11\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u7136\u4e0d\u60a3\u9ebb\u75af\uff0c\u6765\u8fd9\u5c9b\u505a\u751a\uff1f\u90fd\u7ed9\u6211\u6eda\u4e0b\u6d77\u53bb\uff01\u201d \u9c7c\u4eae\u5403\u4e86\u4e00\u60ca\uff0c\u5fd9\u9053\uff1a\u201c\u6211\u4eec\u7684\u8239\u5df2\u7ed9\u53f0\u98ce\u6bc1\u574f\uff0c\u5f85\u4fee\u597d\u4e4b\u540e\uff0c\u81ea\u5f53\u79bb\u5c9b\u3002\u201d\u6bd2\u9f99\u5c0a\u8005\u602a\u773c \u4e00\u7ffb\uff0c\u53d1\u51fa\u4e00\u79cd\u523a\u8033\u7684\u58f0\u97f3\uff0c\u6479\u7136\u65a5\u9053\uff1a\u201c\u8c01\u7406\u4f60\u4eec\u7684\u4e8b\uff0c\u8fd9\u5c9b\uff0c\u4e0d\u8bb8\u4f60\u4eec\u9017\u7559\uff0c\u4f60\u4eec\u7acb\u523b \u7ed9\u6211\u6eda\u4e0b\u6d77\u53bb\u3002\u201d\u9c7c\u4eae\u4e00\u5411\u5728\u6d77\u4e0a\u79f0\u738b\uff0c\u5bf9\u7687\u5e1d\u4e5f\u672a\u66fe\u5982\u6b64\u4f4e\u58f0\u4e0b\u6c14\uff0c\u4e0d\u89c9\u6012\u9053\uff1a\u201c\u4f60\u600e\u4e48 \u8fd9\u6837\u4e0d\u901a\u4eba\u60c5\uff1f\u6211\u4eec\u6ca1\u6709\u8239\u53ea\uff0c\u5982\u4f55\u53ef\u4ee5\u51fa\u6d77\uff1f\u201d \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u5ffd\u7136\u4ef0\u5929\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u4ec0\u4e48\u53eb\u505a\u4eba\u60c5\uff1f\u6211\u8c62\u517b\u7684\u6bd2\u86c7\u4e5f\u8981\u6bd4\u4f60\u4eec\u4eba\u7c7b \u597d\u5f97\u591a\uff1f\u201d\u6bd2\u9f99\u5c0a\u8005\u4ee5\u524d\u60a3\u9ebb\u75c5\u65f6\uff0c\u53d7\u5c3d\u4e16\u4eba\u767d\u773c\uff0c\u65cf\u4e2d\u7684\u4f2f\u53d4\u5144\u5f1f\u626c\u8a00\u8981\u628a\u4ed6\u4e22\u4e0b\u5927\u6d77\u5582 \u9c7c\uff0c\u4e0d\u662f\u4ed6\u6709\u4e00\u8eab\u6b66\u529f\uff0c\u51e0\u4e4e\u9669\u6b7b\u3002\u540e\u6765\u4ed6\u7684\u5e08\u5085\u4e5f\u4e0d\u4ee5\u4ed6\u4e3a\u5f92\u5f1f\uff0c\u7236\u4eb2\u4e5f\u4e0d\u4ee5\u4ed6\u4e3a\u513f\u5b50\uff0c \u4eb2\u621a\u670b\u53cb\u89c1\u9762\u5373\u907f\uff0c\u540c\u95e8\u5144\u5f1f\u8981\u5c06\u4ed6\u6d3b\u57cb\uff0c\u79cd\u79cd\u51b7\u9177\uff0c\u90fd\u906d\u53d7\u4e86\u3002\u8981\u77e5\u51e0\u767e\u5e74\u524d\uff0c\u9ebb\u75af\u75c5\u65e0 \u836f\u53ef\u533b\uff0c\u4e00\u822c\u4eba\u53c8\u4ee5\u4e3a\u9ebb\u75af\u6613\u4e8e\u4f20\u67d3\uff08\u5176\u5b9e\u9ebb\u75af\u4e0d\u6613\u4f20\u67d3\uff09\uff0c\u89c1\u4e86\u9ebb\u75af\u75c5\u4eba\uff0c\u6bd4\u89c1\u4e86\u9b3c\u602a\u8fd8 \u8981\u6050\u60e7\uff0c\u6240\u4ee5\u5c06\u9ebb\u75af\u75c5\u4eba\u6d3b\u57cb\u3001\u6d78\u6b7b\uff0c\u6216\u70e7\u6210\u7070\u70ec\u4e4b\u4e8b\u5e38\u5e38\u53d1\u751f\uff0c\u4e16\u4eba\u4e5f\u89c6\u4e3a\u5f53\u7136\uff0c\u4e0d\u4ee5\u4e3a \u602a\u3002\u53ef\u662f\uff0c\u6bd2\u9f99\u5c0a\u8005\u8eab\u53d7\u8fd9\u79cd\u79cd\u51b7\u9177\u7684\u5f85\u9047\uff0c\u523b\u9aa8\u94ed\u5fc3\uff0c\u5374\u6c38\u4e0d\u80fd\u78e8\u706d\u3002\u56e0\u6b64\u4ed6\u5192\u9669\u5230\u86c7\u5c9b \u6765\uff0c\u6cbb\u597d\u9ebb\u75af\u4e4b\u540e\uff0c\u5c31\u7adf\u7136\u7518\u613f\u81ea\u7edd\u4e8e\u4eba\u7c7b\uff0c\u6c38\u4e0d\u5230\u5916\u95f4\u53bb\u4e86\u3002\u8fd9\u65f6\u5ffd\u7136\u542c\u9c7c\u4eae\u63d0\u8d77\u201c\u4eba \u60c5\u201d\u4e24\u5b57\uff0c\u51e0\u5341\u5e74\u5728\u4eba\u4e16\u95f4\u6240\u906d\u53d7\u7684\u79cd\u79cd\u4e8b\u60c5\uff0c\u7a81\u7136\u95ea\u7535\u822c\u7684\u4ece\u8111\u6d77\u4e2d\u63a0\u8fc7\uff0c\u767b\u65f6\u66b4\u6012\u8d77\u6765\u3002 \u3000\u3000\u9c7c\u4eae\u4e0e\u7518\u51e4\u6c60\u54ea\u80fd\u4f53\u4f1a\u5230\u6bd2\u9f99\u5c0a\u8005\u8fd9\u79cd\u611f\u53d7\uff0c\u90fd\u89c9\u5f97\u6b64\u4eba\u602a\u50fb\u5f97\u65e0\u53ef\u7406\u55bb\uff0c\u9c7c\u4eae\u5411\u7518\u51e4 \u6c60\u6253\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u5c9b\u53c8\u4e0d\u662f\u4f60\u4e70\u4e0b\u6765\u7684\uff0c\u8c01\u90fd\u53ef\u4ee5\u5c45\u4f4f\uff0c\u4f60\u51ed\u4ec0\u4e48\u8981\u628a\u6211\u4eec \u8d76\u4e0b\u6d77\u53bb\u3002\u201d\u6bd2\u9f99\u5c0a\u8005\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u4f60\u4eec\u4e3a\u4ec0\u4e48\u53c8\u8981\u628a\u6211\u8d76\u4e0b\u6d77\u53bb\uff0c\u5916\u9762\u90a3\u4e48\u591a\u5730\u65b9\u8fd8\u4e0d\u591f \u4f4f\u5417\uff1f\u8fd9\u4e2a\u5c0f\u5c9b\u4f60\u4eec\u4f11\u60f3\u63d2\u8db3\uff01\u201d\u9c7c\u4eae\u5947\u9053\uff1a\u201c\u4f60\u75af\u4e86\u5417\uff1f\u8c01\u8981\u628a\u4f60\u8d76\u4e0b\u6d77\u53bb\u3002\u6211\u4eec\u6700\u591a\u4e5f \u53ea\u662f\u501f\u4f4f\u51e0\u5929\u3002\u201d\u4ed6\u600e\u77e5\u5728\u6bd2\u9f99\u5c0a\u8005\u5fc3\u76ee\u4e4b\u4e2d\uff0c\u628a\u5916\u9762\u5c18\u4e16\u7684\u4eba\uff0c\u90fd\u770b\u6210\u5f53\u5e74\u8feb\u5bb3\u4ed6\u7684\u4eba\u4e00 \u6837\u3002 \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u89c1\u9c7c\u4eae\u6653\u6653\u6297\u8fa9\uff0c\u8d8a\u53d1\u6124\u6012\uff0c\u84e6\u7136\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u4e0d\u6eda\uff0c\u5f85\u6211\u628a\u4f60\u4eec\u4e00\u4e2a\u4e2a \u629b\u4e0b\u6d77\u53bb\uff01\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u957f\u81c2\u66b4\u4f38\uff0c\u4e00\u6293\u7167\u9c7c\u4eae\u9876\u5fc3\u6293\u4e0b\uff0c\u7518\u51e4\u6c60\u65e9\u5df2\u51c6\u5907\u52a8\u624b\uff0c\u6697\u8fd0\u5185\u5bb6 \u771f\u529b\uff0c\u8db3\u5c16\u70b9\u5730\u817e\u7a7a\u4e00\u683c\uff0c\u53cc\u81c2\u76f8\u4ea4\uff0c\u7518\u51e4\u6c60\u7a81\u89c9\u706b\u8fa3\u4e00\u9635\u9ebb\u75db\uff0c\u53c8\u4f3c\u7ed9\u4e00\u80a1\u5927\u529b\u4e00\u63a8\uff0c\u51cc \u7a7a\u8dcc\u4e86\u4e0b\u6765\uff0c\u6bd2\u9f99\u5c0a\u8005\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u4f60\u4eec\u4f1a\u6b66\u529f\uff0c\u602a\u4e0d\u5f97\u5982\u6b64\u5f3a\u9879\u3002\u201d\u66f4 \u4e0d\u6362\u62db\uff0c\u75be\u6293\u5982\u98ce\uff0c\u4ecd\u7136\u63a2\u722a\u76f4\u6293\u9c7c\u4eae\u9876\u5fc3\uff01 \u3000\u3000\u5510\u6653\u6f9c\u4e0e\u51af\u745b\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u6e38\u9f99\u65ad\u7389\u53cc\u5251\u51fa\u9798\uff0c\u4e24\u9053\u5bd2\u5149\uff0c\u9f50\u5377\u8fc7\u53bb\uff0c\u8ffd\u98ce\u5251\u6cd5\uff0c\u5947\u5feb \u65e0\u6bd4\uff0c\u6bd2\u9f99\u5c0a\u8005\u602a\u53eb\u4e00\u58f0\uff0c\u957f\u8896\u4e00\u6325\uff0c\u52b2\u98ce\u75be\u8feb\uff0c\u53cc\u5251\u7a0d\u7a0d\u659c\u6b6a\uff0c\u6bd2\u9f99\u5c0a\u8005\u7adf\u7136\u5c31\u5728\u95f4\u4e0d\u5bb9 \u53d1\u4e4b\u9645\uff0c\u5728\u53cc\u5251\u7f1d\u4e2d\uff0c\u9aa4\u7136\u7a9c\u51fa\uff0c\u5de6\u638c\u53d8\u6293\u4e3a\u62ff\uff0c\u786c\u593a\u5510\u6653\u6f9c\u7684\u6e38\u9f99\u5b9d\u5251\uff0c\u53f3\u638c\u4e00\u7ffb\uff0c\u4fe1\u624b \u5288\u51fa\uff0c\u51af\u745b\u6025\u5fd9\u9000\u95ea\uff0c\u80f8\u53e3\u7adf\u4f3c\u4e24\u65e5\u524d\u5728\u6d77\u4e0a\u9047\u7740\u98ce\u66b4\u4e00\u6837\uff0c\u53d7\u638c\u529b\u6240\u538b\u51e0\u4e4e\u900f\u4e0d\u8fc7\u6c14\u6765\uff01 \u3000\u3000\u7518\u51e4\u6c60\u529f\u529b\u6df1\u6e5b\uff0c\u867d\u7136\u5760\u5730\uff0c\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u7acb\u5373\u8dc3\u8d77\uff0c\u53cc\u638c\u7528\u529b\uff0c\u4e00\u62db\u201c\u5288\u5c71\u65ad \u6d41\u201d\uff0c\u731b\u63a8\u8fc7\u53bb\uff0c\u53cc\u638c\u4e4b\u529b\u4e0e\u6bd2\u9f99\u5c0a\u8005\u903c\u5510\u6653\u6f9c\u7684\u5de6\u638c\u76f8\u78b0\uff0c\u8fd9\u624d\u521a\u521a\u62b5\u6321\u5f97\u4f4f\u3002\u6bd2\u9f99\u5c0a\u8005 \u53c8\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u811a\u8ddf\u4e00\u65cb\uff0c\u53f3\u62f3\u6a2a\u6253\uff0c\u7518\u51e4\u6c60\u6025\u6025\u64a4\u62db\u3002\u5510\u6653\u6f9c\u4e0e\u51af\u745b\u53cc\u5251\u9f50\u5230\uff0c\u6bd2\u9f99\u5c0a \u8005\u54c7\u54c7\u5927\u53eb\uff0c\u4fa7\u8eab\u5206\u638c\uff0c\u540c\u65f6\u8fdb\u51fb\u4e8c\u4eba\uff0c\u9c7c\u4eae\u98de\u8d77\u4e00\u811a\uff0c\u201c\u9b41\u661f\u8e22\u6597\u201d\uff0c\u731b\u8e22\u654c\u4eba\u80ef\u9aa8\uff0c\u6bd2 \u9f99\u5c0a\u8005\u7adf\u4e0d\u56de\u8eab\uff0c\u8170\u6273\u4e00\u633a\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u9c7c\u4eae\u811a\u5c16\u6240\u5230\u5982\u89e6\u94a2\u94c1\uff0c\u53cd\u5f39\u56de\u6765\uff0c\u82e5\u975e\u9c7c\u4eae \u6b66\u529f\u4e5f\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u8fd9\u4e00\u811a\u8e22\u4e0d\u4f24\u654c\u4eba\uff0c\u81ea\u5df1\u5148\u8981\u6298\u65ad\uff01\u9c7c\u4eae\u4e00\u9707\uff0c\u6025\u62d4\u5b9d\u5200\uff0c\u62a4\u7740\u4e0b\u76d8\u3002 \u3000\u3000\u7518\u51e4\u6c60\u5927\u60ca\uff0c\u98de\u62a2\u4e0a\u524d\uff0c\u4ee5\u64d2\u62ff\u624b\u7684\u622a\u624b\u6cd5\u6765\u5207\u6bd2\u9f99\u5c0a\u8005\u624b\u8155\uff0c\u6bd2\u9f99\u5c0a\u8005\u6a2a\u8098\u4e00\u649e\uff0c\u6ca1 \u6709\u649e\u7740\uff0c\u53cc\u65b9\u5404\u6362\u4e00\u62db\u3002\u51af\u745b\u98de\u8eab\u8dc3\u8d77\uff0c\u523a\u4ed6\u5de6\u80a9\uff0c\u5510\u6653\u6f9c\u4fa7\u8eab\u8fdb\u5251\uff0c\u6233\u4ed6\u53f3\u80a9\u3002\u6bd2\u9f99\u5c0a\u8005 \u903c\u5f97\u518d\u653e\u8fc7\u7518\u51e4\u6c60\uff0c\u547c\u547c\u4e24\u638c\uff0c\u5f3a\u529b\u8361\u5f00\u5251\u70b9\uff0c\u4e0e\u56db\u4eba\u5927\u6218\u8d77\u6765\uff01 \u3000\u3000\u7518\u51e4\u6c60\u5185\u529f\u6df1\u6e5b\uff0c\u51e0\u8fbe\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff1b\u51af\u745b\u4e0e\u5510\u6653\u6f9c\u5f97\u5929\u5c71\u5251\u6cd5\u771f\u4f20\uff0c\u5965\u5999\u65e0\u5339\uff1b\u9c7c\u4eae \u7a0d\u4f4e\uff0c\u4f46\u4e5f\u4e0d\u5728\u767d\u6cf0\u5b98\u4e4b\u4e0b\u3002\u56db\u4eba\u5408\u529b\uff0c\u8981\u6bd4\u6c5f\u5357\u4e03\u4fa0\u8054\u624b\u4e4b\u529b\u8fd8\u5f3a\u52b2\u5f97\u591a\u3002\u53ef\u662f\u6bd2\u9f99\u5c0a\u8005 \u5374\u4e5f\u8981\u6bd4\u4e86\u56e0\u9ad8\u51fa\u4e0d\u77e5\u591a\u5c11\u3002\u7518\u51e4\u6c60\u53ea\u89c9\u5f97\u6bd4\u5728\u9099\u5c71\u6076\u6597\u4e86\u56e0\u4e4b\u65f6\uff0c\u8fd8\u8981\u5403\u529b\u3002 \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u8d8a\u6597\u8d8a\u52c7\uff0c\u6597\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4ecd\u662f\u7740\u7740\u62a2\u653b\uff0c\u638c\u98ce\u5230\u5904\uff0c\u5f53\u8005\u8f9f\u6613\u3002\u7518\u51e4\u6c60\u5185\u529f \u6df1\u6709\u706b\u5019\uff0c\u867d\u7136\u5403\u529b\uff0c\u5c1a\u8fd8\u4e0d\u89c9\u600e\u6837\u3002\u51af\u745b\u8f7b\u529f\u8d85\u5999\uff0c\u8d8b\u95ea\u5f97\u5b9c\uff0c\u4e5f\u4e0d\u89c9\u600e\u6837\u3002\u5510\u6653\u6f9c\u5374\u5df2 \u6709\u70b9\u6c14\u5598\u3002\u9c7c\u4eae\u5219\u66f4\u662f\u89c9\u5f97\u5fc3\u5934\u70e6\u8e81\uff0c\u53e3\u4e2d\u7126\u6e34\uff0c\u6e10\u6e10\u62db\u67b6\u4e0d\u4f4f\uff01 \u3000\u3000\u51af\u745b\u5ffd\u9053\uff1a\u201c\u5510\u53d4\u53d4\uff0c\u4f60\u7528\u987b\u5f25\u5251\u5f0f\uff0c\u6211\u7528\u8ffd\u98ce\u5251\u6cd5\uff0c\u5939\u51fb\u8fd9\u4e2a\u91ce\u4eba\uff01\u201d\u987b\u5f25\u5251\u5b88\u591a\u4e8e \u653b\uff0c\u65bd\u5c55\u5f00\u6765\uff0c\u5168\u8eab\u7ed9\u5251\u5149\u62a4\u7740\uff0c\u6e38\u9f99\u5b9d\u5251\u6709\u65ad\u91d1\u5207\u7389\u4e4b\u80fd\uff0c\u6bd2\u9f99\u5c0a\u8005\u4e0d\u6562\u4f38\u624b\u8fdb\u5149\u7f51\u4e4b \u4e2d\uff0c\u51af\u745b\u7684\u8ffd\u98ce\u5251\u6cd5\u72e0\u51c6\u51b3\u6377\uff0c\u4e0e\u6bd2\u9f99\u5c0a\u8005\u5bf9\u653b\uff0c\u5929\u5c71\u53cc\u5251\uff0c\u914d\u5408\u5f97\u5b9c\uff0c\u5a01\u529b\u9661\u7136\u589e\u52a0\u4e00 \u500d\uff0c\u66f4\u52a0\u4e0a\u7518\u51e4\u6c60\u96c4\u52b2\u7684\u638c\u529b\uff0c\u9c7c\u4eae\u8001\u7ec3\u7684\u5200\u6cd5\uff0c\u987f\u65f6\u4e3b\u5ba2\u6613\u52bf\uff0c\u56db\u4eba\u5904\u5883\u4e0d\u4f3c\u5148\u524d\u56f0\u8e69\uff0c \u548c\u6bd2\u9f99\u5c0a\u8005\u5df2\u6709\u5b88\u6709\u653b\u3002 \u3000\u3000\u518d\u6597\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5929\u5c71\u53cc\u5251\u7684\u5a01\u529b\u5c3d\u91cf\u53d1\u6325\u3002\u6bd2\u9f99\u5c0a\u8005\u6e10\u6e10\u88ab\u8feb\u8f6c\u5904\u4e0b\u98ce\u53ef\u662f\u4ecd\u65e0\u75b2 \u6001\u3002\u6597\u5230\u5206\u9645\uff0c\u6bd2\u9f99\u5c0a\u8005\u7a81\u7136\u64ae\u5507\u201c\u5618\u5618\u201d\u4e24\u58f0\uff0c\u6392\u5728\u4ed6\u540e\u9762\u7684\u86c7\u7fa4\uff0c\u7a81\u5982\u4e07\u7bad\u9f50\u53d1\uff0c\u5411\u4f17 \u4eba\u51b2\u6765\uff01 \u3000\u3000\u9c7c\u4eae\u6025\u53eb\u9053\uff1a\u201c\u6392\u6210\u65b9\u9635\uff0c\u4e0d\u8981\u614c\u4e71\uff01\u201d\u6d77\u6ee9\u4e0a\u7684\u5341\u4f59\u4eba\uff0c\u7531\u536b\u626c\u5a01\u3001\u5b5f\u6b66\u529f\u3001\u8def\u6c11 \u77bb\u3001\u674e\u660e\u73e0\u56db\u4e2a\u6b66\u529f\u8f83\u9ad8\u7684\u4eba\u5206\u5360\u56db\u89d2\uff0c\u5404\u5c55\u5175\u5668\uff0c\u5bf9\u6bd2\u86c7\u8fce\u5934\u75db\u51fb\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u6253\u6b7b\u767e\u6570 \u5341\u6761\uff0c\u7fa4\u86c7\u5618\u5618\u5431\u53eb\uff0c\u5728\u65b9\u9635\u5916\u84c4\u52bf\u5f85\u653b\uff0c\u5f62\u52bf\u6781\u9669\u3002\u4f46\u8fd9\u5341\u4f59\u4eba\u53ea\u5bf9\u4ed8\u6bd2\u86c7\uff0c\u5f62\u52bf\u867d\u9669\uff0c \u5374\u5c1a\u672a\u5982\u7518\u51e4\u6c60\u7b49\u56db\u4eba\u4e4b\u751a\uff01 \u3000\u3000\u7518\u51e4\u6c60\u7b49\u56db\u4eba\u5408\u6218\u6bd2\u9f99\u5c0a\u8005\uff0c\u4e5f\u4e0d\u8fc7\u582a\u582a\u6253\u4e2a\u5e73\u624b\uff0c\u800c\u4eca\u53c8\u8981\u5bf9\u4ed8\u6bd2\u86c7\uff0c\u771f\u662f\u6b7b\u751f\u7cfb\u4e8e \u4e00\u53d1\uff0c\u7a0d\u6709\u758f\u5ffd\uff0c\u7acb\u523b\u4e27\u547d\u3002\u5e78\u5728\u6e38\u9f99\u65ad\u7389\u4e8c\u5251\uff0c\u90fd\u662f\u5b9d\u7269\uff0c\u7d27\u6025\u4e4b\u65f6\uff0c\u5251\u5149\u4e00\u64a9\uff0c\u4fbf\u662f\u5341 \u6761\u6bd2\u86c7\u88ab\u65a9\u4e3a\u4e24\u622a\uff0c\u6bd2\u9f99\u5c0a\u8005\u52a0\u7d27\u638c\u529b\uff0c\u529b\u654c\u56db\u4eba\u9632\u5b88\uff0c\u5c24\u5176\u52a0\u7d27\u5bf9\u5510\u6653\u6f9c\u548c\u51af\u745b\u8fdb\u653b\uff0c\u60f3 \u4ee4\u4e8c\u4eba\u4e0d\u80fd\u62bd\u51fa\u5251\u6765\u65a9\u86c7\uff01 \u3000\u3000\u7518\u51e4\u6c60\u5ffd\u5730\u51b7\u7b11\u9053\uff0c\u201c\u54fc\uff0c\u8981\u9760\u6bd2\u86c7\u4e4b\u529b\uff0c\u7b97\u4ec0\u4e48\u82f1\u96c4\uff01\u201d\u6bd2\u9f99\u5c0a\u8005\u7709\u5934\u4e00\u76b1\uff0c\u5ffd\u800c\u4e5f \u51b7\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u4e0d\u662f\u82f1\u96c4\uff0c\u8c01\u8bf4\u6211\u662f\u82f1\u96c4\u6765\u4e86\uff0c\u54fc\uff0c\u54fc\uff0c\u82e5\u4f60\u4eec\u628a\u6211\u5f53\u662f\u82f1\u96c4\uff0c\u4e5f\u4e0d\u81f3 \u4e8e\u628a\u6211\u903c\u5230\u8352\u5c9b\u4e0a\u6765\u3002\u201d\u7518\u51e4\u6c60\u60f3\u7528\u6c5f\u6e56\u4e0a\u7684\u6fc0\u5c06\u4e4b\u6cd5\uff0c\u5c82\u77e5\u6bd2\u9f99\u5c0a\u8005\u4e0e\u6c5f\u6e56\u4e0a\u7684\u4eba\u7269\u5b8c\u5168 \u4e0d\u540c\uff0c\u4e0d\u5355\u5982\u6b64\uff0c\u4ed6\u548c\u4e00\u822c\u4eba\u90fd\u597d\u50cf\u5206\u5904\u4e24\u4e2a\u4e16\u754c\u4e4b\u4e2d\uff0c\u7518\u51e4\u6c60\u7684\u6fc0\u5c06\u6beb\u65e0\u6548\u679c\u3002 \u3000\u3000\u53c8\u652f\u6301\u4e86\u534a\u523b\uff0c\u5f62\u52bf\u66f4\u9669\uff0c\u6797\u4e2d\u6bd2\u86c7\u542c\u5f97\u540c\u4f34\u5636\u53eb\u6765\u5f97\u66f4\u591a\u4e86\u3002\u4f17\u4eba\u60f3\u7247\u523b\u4e4b\u540e\uff0c\u4fbf\u8981 \u53d7\u5343\u4e07\u6bd2\u86c7\u556e\u4f53\u4e4b\u5211\uff0c\u65e0\u4e0d\u5fc3\u60b8\u3002 \u3000\u3000\u5ffd\u5730\u91cc\u6d77\u5916\u9690\u9690\u4f20\u6765\u4e00\u79cd\u566a\u97f3\uff0c\u7fa4\u86c7\u5ffd\u7136\u75be\u9000\uff0c\u9996\u5c3e\u76f8\u8854\uff0c\u6392\u6210\u5706\u9635\u3002\u6bd2\u9f99\u5c0a\u8005\u4ef0\u5929\u4e00 \u671b\uff0c\u4e5f\u7a81\u7136\u8df3\u51fa\u5708\u5b50\uff0c\u8fdc\u8fdc\u79bb\u5f00\u4e86\u86c7\u7fa4\uff0c\u8e72\u5728\u5d16\u77f3\u4e4b\u4e0a\u3002\u4f17\u4eba\u5927\u4e3a\u8c05\u5f02\uff0c\u4e2a\u4e2a\u7fc5\u9996\u957f\u7a7a\uff0c\u4f46 \u89c9\u4e07\u91cc\u65e0\u4e91\uff0c\u6de1\u70df\u7b3c\u78a7\uff0c\u4e5f\u4e0d\u89c1\u4ec0\u4e48\u5f02\u4f6f\u3002 \u3000\u3000\u518d\u8fc7\u7247\u523b\uff0c\u566a\u97f3\u66f4\u5f3a\uff0c\u5934\u9876\u4e0a\u201c\u560e\u560e\u560e\u201d\u7684\u54cd\u6210\u4e00\u7247\uff0c\u542c\u51fa\u662f\u79bd\u9e1f\u9e23\u58f0\uff0c\u51af\u745b\u5ffd\u7136\u53eb \u9053\uff1a\u201c\u554a\u5440\uff01\u4f60\u770b\uff01\u201d\u5929\u8fb9\u4e00\u5927\u5806\u602a\u9e1f\uff0c\u75be\u98de\u800c\u6765\uff0c\u9ed1\u538b\u538b\u7684\u538b\u5728\u6d77\u9762\u4e0a\u6709\u5982\u4e00\u5927\u7247\u9ed1\u4e91\uff0c \u9c7c\u4eae\u53eb\u9053\uff1a\u201c\u732b\u9e70\u6765\u4e86\uff0c\u5feb\u8eb2\uff0c\u5feb\u8eb2\uff01\u201d\u4f46\u6d77\u6ee9\u5149\u79c3\u79c3\u7684\u65e0\u5904\u53ef\u8eb2\uff0c\u4f17\u4eba\u53ea\u597d\u805a\u5728\u4e00\u5904\uff0c\u62d4 \u51fa\u5200\u5251\u7b49\u5175\u5668\u81ea\u536b\uff0c\u5fc3\u4e2d\u63e3\u63e3\uff0c\u60f3\u90a3\u732b\u9e70\u51cc\u7a7a\u4e0b\u51fb\uff0c\u4e00\u5b9a\u6bd4\u6bd2\u86c7\u8fd8\u96be\u5e94\u4ed8\u3002 \u3000\u3000\u8fd9\u65f6\u7fa4\u86c7\u76d8\u6210\u5706\u9635\uff0c\u6602\u5934\u7a7a\u9645\uff0c\u8eab\u4f53\u626d\u66f2\u6210\u5f39\u7c27\u4e4b\u72b6\uff0c\u597d\u50cf\u662f\u51c6\u5907\u968f\u65f6\u7a9c\u51fa\u8fce\u51fb\u3002 \u3000\u3000\u53c8\u5b88\u7247\u523b\uff0c\u90a3\u4e00\u5927\u7fa4\u732b\u9e70\u98de\u5230\u6d77\u5c9b\u4e0a\u7a7a\uff0c\u76d8\u65cb\u4e00\u9635\uff0c\u7a81\u7136\u6251\u4e0b\u3002\u6bd2\u86c7\u7eb7\u7eb7\u7a9c\u8d77\uff0c\u987f\u65f6\u851a \u6210\u5947\u89c2\u3002\u53ea\u89c1\u65e0\u6570\u4f4e\u98de\u7684\u732b\u9e70\uff0c\u7ed9\u6bd2\u86c7\u4e00\u53e3\u54ac\u7740\uff0c\u8dcc\u4e86\u4e0b\u6765\uff1b\u4f46\u4e5f\u6709\u65e0\u6570\u6bd2\u86c7\uff0c\u7ed9\u732b\u9e70\u4e00\u5544 \u5544\u7740\u86c7\u5934\uff0c\u628a\u5b83\u540a\u5230\u534a\u7a7a\uff0c\u7136\u540e\u6254\u4e0b\uff0c\u6454\u6b7b\u6d77\u4e2d\uff0c\u957f\u86c7\u6ee1\u7a7a\u98de\u5815\uff0c\u4f17\u4eba\u60ca\u5fc3\u9a87\u76ee\uff0c\u751a\u6015\u732b\u9e70 \u4f1a\u628a\u86c7\u6454\u5230\u81ea\u5df1\u5934\u4e0a\uff0c\u5374\u559c\u90a3\u4e9b\u732b\u9e70\u6254\u5f97\u6781\u51c6\uff0c\u7adf\u65e0\u4e00\u6761\u6389\u5728\u5730\u4e0a\u3002\u539f\u6765\u732b\u9e70\u4e0e\u6bd2\u86c7\u5e38\u5e38\u6076 \u6597\uff0c\u5728\u53f0\u98ce\u4e4b\u540e\uff0c\u56f0\u4e86\u6570\u65e5\uff0c\u51fa\u52a8\u4e4b\u65f6\uff0c\u89c4\u6a21\u66f4\u5927\u3002\u800c\u8fd9\u6b21\u5c24\u5176\u662f\u6570\u5341\u5e74\u6765\u6700\u5927\u89c4\u6a21\u7684\u4e00 \u6b21\uff0c\u4f17\u4eba\u9002\u9022\u5176\u4f1a\uff0c\u770b\u5230\u8fd9\u573a\u6076\u6218\u3002\u732b\u9e70\u77e5\u9053\u9646\u5730\u4e0a\u7684\u86c7\u6454\u5230\u6d77\u4e2d\u5fc5\u6b7b\u65e0\u7591\uff0c\u6240\u4ee5\u5544\u7740\u4e86\u86c7 \u5934\u4e4b\u540e\uff0c\u4e00\u5b9a\u98de\u5230\u534a\u7a7a\uff0c\u7136\u540e\u628a\u5b83\u6254\u5230\u6d77\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u732b\u9e70\u4e0e\u6bd2\u86c7\u7684\u6076\u6218\uff0c\u6253\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u6e10\u6e10\u5206\u51fa\u80dc\u8d1f\uff0c\u732b\u9e70\u867d\u7136\u6b7b\u4ea1\u751a\u591a\uff0c\u4f46\u56e0\u5b83 \u80fd\u51cc\u7a7a\u98de\u7fd4\uff0c\u5230\u5e95\u5360\u4e86\u4fbf\u5b9c\uff0c\u86c7\u7fa4\u5636\u5636\u4e71\u53eb\uff0c\u7eb7\u7eb7\u7a9c\u56de\u5c9b\u4e0a\u5bc6\u6797\u6df1\u5904\uff0c\u8eb2\u8fdb\u77f3\u9699\u3002\u8fd9\u4e00\u5927\u7fa4 \u732b\u9e70\u4f4e\u98de\u76d8\u65cb\u51e0\u531d\uff0c\u53c8\u201c\u560e\u560e\u560e\u201d\u7684\u53eb\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u597d\u50cf\u5e86\u795d\u80dc\u5229\uff0c\u7136\u540e\u6a2a\u7a7a\u98de\u8fc7\u6d77\u9762\uff0c\u6709 \u597d\u4e9b\u732b\u9e70\u56e0\u4e3a\u75b2\u5377\u4e0d\u582a\uff0c\u98de\u5230\u4e2d\u9014\u6389\u4e0b\u6d77\u6765\u3002 \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u9762\u8272\u7070\u767d\uff0c\u628a\u5ca9\u77f3\u634f\u788e\u4e86\u597d\u5927\u4e00\u628a\uff0c\u63e1\u5728\u624b\u4e2d\uff0c\u5374\u4e0d\u6562\u5411\u732b\u9e70\u5c04\u51fb\u3002\u56e0\u4e3a\u82e5\u7136 \u6709\u4eba\u52a8\u624b\uff0c\u732b\u9e70\u4e00\u5b9a\u8f6c\u79fb\u76ee\u6807\uff0c\u5411\u4eba\u653b\u51fb\uff0c\u90a3\u65f6\u5c31\u7b97\u7edd\u4e16\u6b66\u529f\uff0c\u4e5f\u96be\u5e78\u514d\u3002 \u3000\u3000\u732b\u9e70\u7fa4\u53bb\u540e\uff0c\u6bd2\u9f99\u5c0a\u8005\u4e00\u8a00\u4e0d\u53d1\uff0c\u9ed8\u9ed8\u8d70\u56de\u6797\u4e2d\uff0c\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u771f\u662f\u4e00\u7269\u6cbb\u4e00\u7269\uff0c\u60f3 \u4e0d\u5230\u8fd9\u4e9b\u6241\u6bdb\u755c\u751f\uff0c\u6551\u4e86\u54b1\u4eec\u6027\u547d\u3002\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u732b\u9e70\u4e0e\u6bd2\u86c7\u5927\u6253\u4e00\u573a\u4e4b\u540e\uff0c\u6700\u5c11\u4e5f\u8981\u4f11\u6218 \u534a\u6708\uff0c\u6bd2\u86c7\u518d\u6765\uff0c\u54b1\u4eec\u53ef\u8981\u8bbe\u6cd5\u9632\u5fa1\u4e86\u3002\u201d\u4f46\u5982\u4f55\u9632\u5fa1\uff0c\u53ef\u662f\u60f3\u4e0d\u51fa\u6cd5\u513f\u3002\u9c7c\u4eae\u82e6\u82e6\u601d\u7d22\uff0c \u51af\u745b\u8131\u9669\u4e4b\u540e\uff0c\u53c8\u5230\u6d77\u6ee9\u53bb\u73a9\uff0c\u6d77\u6ee9\u4e0a\u65e0\u6570\u8d1d\u58f3\uff0c\u5341\u5206\u7f8e\u4e3d\uff0c\u51af\u745b\u89c1\u4e00\u4e2a\u7231\u4e00\u4e2a\uff0c\u62fe\u6ee1\u4e86\u4e00 \u4e2a\u8863\u515c\uff0c\u89c1\u5230\u518d\u597d\u7684\u65f6\u5019\uff0c\u624d\u604b\u604b\u4e0d\u820d\u7684\u4e22\u4e86\u515c\u4e2d\u539f\u6709\u7684\u8d1d\u58f3\uff0c\u5c06\u597d\u7684\u8865\u8fdb\u3002\u6709\u4e00\u79cd\u8d1d\u58f3\uff0c \u53eb\u505a\u201c\u864e\u6591\u5b9d\u8d1d\u201d\uff0c\u5149\u6ed1\u6676\u4eae\uff0c\u58f3\u9762\u7684\u82b1\u7eb9\u5982\u864e\u8c79\u76ae\u4e00\u6837\uff0c\u5341\u5206\u9c9c\u8273\uff0c\u51af\u745b\u4e0d\u77e5\u5176\u540d\uff0c\u6781\u4e3a \u559c\u7231\uff0c\u4e3a\u4e86\u627e\u5bfb\u8fd9\u79cd\u8d1d\u58f3\uff0c\u6e10\u8d70\u6e10\u8fdc\u3002 \u3000\u3000\u5510\u6653\u6f9c\u53eb\u9053\uff1a\u201c\u963f\u745b\uff0c\u56de\u6765\uff0c\u5f53\u5fc3\u6f6e\u6c34\u628a\u4f60\u5377\u53bb\u3002\u201d\u51af\u745b\u56de\u9996\u4f5c\u4e2a\u602a\u8138\uff0c\u5ffd\u7136\u201c\u5618\u201d\u4e86 \u4e00\u58f0\uff0c\u8dd1\u56de\u6765\u9053\uff1a\u201c\u9c7c\u8001\u516c\u516c\uff0c\u4f60\u5feb\u53bb\u770b\uff0c\u597d\u5927\u7684\u6d77\u9f9f\uff01\u201d\u9c7c\u4eae\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u60ca \u5b83\uff01\u201d\u8fc7\u4e86\u4e00\u9635\uff0c\u90a3\u5927\u6d77\u9f9f\u7f13\u7f13\u722c\u4e0a\u6c99\u6ee9\uff0c\u597d\u50cf\u4e00\u628a\u5de8\u4f1e\uff0c\u8986\u5728\u6c99\u4e0a\uff0c\u9c7c\u4eae\u7b11\u9053\uff1a\u201c\u5927\u6d77\u9f9f \u6700\u6613\u6349\uff0c\u4f60\u53ea\u8981\u628a\u5b83\u63a8\u7ffb\u4e2a\u8eab\uff0c\u8ba9\u5b83\u56db\u811a\u671d\u5929\uff0c\u5b83\u5c31\u4e56\u4e56\u7684\u542c\u4f60\u6349\u4e86\u3002\u4e0d\u8fc7\uff0c\u8c61\u8fd9\u6837\u7684\u5927\u6d77 \u9f9f\uff0c\u82e5\u662f\u666e\u901a\u7684\u4eba\uff0c\u4e5f\u5f97\u6709\u56db\u4e94\u4e2a\u4eba\u5408\u529b\uff0c\u624d\u80fd\u628a\u5b83\u63a8\u7ffb\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u4e5f\u672a\u66fe\u89c1\u8fc7\u8fd9\u4f6f\u7684\u5927\u6d77\u9f9f\uff0c\u95fb\u8a00\u5174\u8d77\uff0c\u8dd1\u4e0a\u524d\u53bb\uff0c\u4e00\u638c\u5c06\u5b83\u63a8\u7ffb\u3002\u9c7c\u4eae\u9053\uff1a\u201c\u5c06 \u5b83\u62ff\u5230\u8fd9\u91cc\u6765\u3002\u201d\u7518\u51e4\u6c60\u4f9d\u8a00\u5c06\u5927\u6d77\u9f9f\u6349\u56de\u3002\u9c7c\u4eae\u9053\uff1a\u201c\u4f60\u634f\u5b83\u9888\u8116\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u5e72\u561b \u8981\u8650\u5f85\u5b83\uff1f\u201d\u9c7c\u4eae\u7b11\u9053\uff1a\u201c\u4f60\u634f\u5b83\u4e00\u634f\uff0c\u54b1\u4eec\u4eca\u665a\u5c11\u53d7\u597d\u591a\u60ca\u6050\u3002\u201d\u7518\u51e4\u6c60\u77e5\u9c7c\u4eae\u5bf9\u6d77\u4e0a\u4e8b \u60c5\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u4fbf\u4f9d\u8a00\u634f\u4e86\u4e00\u4e0b\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u9c7c\u4eae\u5c06\u6d77\u9f9f\u4e00\u811a\u8e22\u5f00\uff0c\u5fae\u95fb\u81ca\u5473\uff0c\u539f\u6765\u662f\u6d77\u9f9f\u64a4 \u4e86\u4e00\u5708\u9ec4\u5c3f\u3002\u9c7c\u4eae\u9053\uff1a\u201c\u86c7\u6015\u9f9f\u5c3f\uff0c\u8fd9\u79cd\u5927\u6d77\u9f9f\u7684\u5c3f\uff0c\u5c24\u5176\u6709\u6548\u3002\u54b1\u4eec\u4eca\u665a\u6324\u5728\u9f9f\u5c3f\u5708\u4e2d\uff0c \u5c31\u4e0d\u6015\u86c7\u4e86\u3002\u201d\u51af\u745b\u62bf\u5634\u7b11\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u5e72\u3002\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u4f60\u6015\u81ca\uff0c\u4e0d\u5728\u5708\u5b50\u91cc\u4e5f\u884c\uff0c\u4e0d\u8fc7 \u4e0d\u80fd\u79bb\u5f00\u4e09\u4e08\uff0c\u4e09\u4e08\u4e4b\u5916\uff0c\u86c7\u95fb\u4e0d\u5230\u81ca\u5473\uff0c\u5b83\u5c31\u53ef\u80fd\u6765\u9a9a\u6270\u4f60\u4e86\u3002\u201d \u3000\u3000\u9c7c\u4eae\u53c8\u9053\uff1a\u201c\u54b1\u4eec\u4eca\u665a\u53ef\u4ee5\u9971\u9910\u4e00\u987f\u4e86\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8fd9\u6837\u5927\u7684\u6d77\u9f9f\uff0c\u4e09\u65e5\u90fd\u5403\u4e0d \u5b8c\u3002\u201d\u6d77\u9f9f\u7684\u8089\u5473\u6781\u4f3c\u725b\u8089\uff0c\u4f17\u4eba\u5403\u4e86\uff0c\u90fd\u5f88\u559c\u6b22\u3002 \u3000\u3000\u662f\u591c\uff0c\u679c\u65e0\u6bd2\u86c7\u6765\u72af\uff0c\u6bd2\u9f99\u5c0a\u8005\u4e5f\u4e0d\u6765\u6270\u3002\u7b2c\u4e8c\u65e5\u53c8\u662f\u4e2a\u5927\u597d\u6674\u5929\u3002\u9c7c\u4eae\u9053\uff1a\u201c\u54b1\u4eec\u4eca \u65e5\u628a\u8239\u4fee\u597d\uff0c\u901f\u79bb\u6b64\u5730\u3002\u201d\u5927\u8239\u7834\u70c2\u4e0d\u582a\uff0c\u4f17\u4eba\u5e72\u4e86\u4e00\u5929\uff0c\u5c1a\u672a\u5b8c\u5de5\u3002 \u3000\u3000\u665a\u4e0a\uff0c\u7518\u51e4\u6c60\u4e0e\u9c7c\u4eae\u3001\u536b\u626c\u5a01\u7b49\u8f6e\u6d41\u5b88\u591c\uff0c\u7ea6\u83ab\u4e09\u66f4\u65f6\u5206\uff0c\u5ffd\u542c\u5f97\u6d77\u6ee9\u4e0a\u6728\u5934\u788e\u88c2\u4e4b \u58f0\uff0c\u7518\u51e4\u6c60\u6025\u5c06\u4f17\u4eba\u5524\u9192\uff0c\u70b9\u71c3\u677e\u8102\uff01\u8d76\u8d74\u6d77\u6ee9\uff0c\u5374\u89c1\u6bd2\u9f99\u5c0a\u8005\u63d0\u7740\u4e00\u6839\u94c1\u62d0\uff0c\u5728\u8239\u8eab\u4e0a\u4e71 \u626b\uff0c\u4e00\u62d0\u51fb\u4e2d\uff0c\u6728\u7247\u7eb7\u98de\uff0c\u4f17\u4eba\u8d76\u5230\u6765\u65f6\uff0c\u90a3\u6761\u8239\u65e9\u56db\u5206\u4e94\u88c2\uff0c\u7834\u788e\u5f97\u4e0d\u6210\u6837\u5b50\uff0c\u60f3\u91cd\u4fee\u4e5f \u65e0\u4ece\u4fee\u8d77\u4e86\u3002 \u3000\u3000\u7518\u51e4\u6c60\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4e0d\u8bb8\u6211\u4eec\u7559\u5728\u6b64\u5c9b\uff0c\u6211\u4eec\u8d70\u4fbf\u662f\u3002\u4f60\u4e3a\u4f55\u5c06\u6211\u4eec\u7684\u8239\u53ea\u7834 \u574f\uff1f\u201d\u6bd2\u9f99\u5c0a\u8005\u563f\u563f\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8981\u8d70\u4e5f\u4e0d\u6210\u4e86\u3002\u6211\u8fd8\u6ca1\u6709\u73a9\u591f\u5462\uff01\u54b1\u4eec\u518d\u6765\u677e\u6563\u677e \u6563\u7b4b\u9aa8\uff01\u201d\u539f\u6765\u6bd2\u9f99\u5c0a\u8005\u5728\u86c7\u5c9b\u51e0\u5341\u5e74\uff0c\u5e38\u7779\u7fa4\u86c7\u4e0e\u732b\u9e70\u3001\u5de8\u9f20\u4e09\u7c7b\u640f\u6597\uff0c\u81ea\u521b\u4e86\u8bb8\u591a\u5947\u7279 \u7684\u6b66\u529f\uff0c\u4f46\u4ed6\u51e0\u5341\u5e74\u8db3\u4e0d\u51fa\u5c9b\uff0c\u81ea\u5df1\u4e5f\u4e0d\u77e5\u6b66\u529f\u5230\u4e86\u4f55\u7b49\u5730\u6b65\u3002\u5341\u4f59\u5e74\u524d\uff0c\u53cc\u9b54\u5230\u732b\u9e70\u5c9b\u9690 \u5c45\uff0c\u5351\u8f9e\u7ed3\u4ea4\uff0c\u8bf4\u4ed6\u6b66\u529f\u76d6\u4e16\u65e0\u654c\uff0c\u4ed6\u4ea6\u662f\u534a\u4fe1\u534a\u7591\u3002 \u3000\u3000\u8fd9\u6b21\uff0c\u4ed6\u9aa4\u9047\u7518\u51e4\u6c60\u4e00\u73ed\u9ad8\u624b\uff0c\u6253\u4e86\u534a\u5929\uff0c\u5341\u5206\u7545\u5feb\u3002\u56de\u53bb\u4e00\u60f3\uff0c\u5ffd\u7136\u6539\u4e86\u4e3b\u610f\uff0c\u5fc3 \u9053\uff1a\u201c\u6211\u4f55\u5fc5\u5c06\u4ed6\u4eec\u7acb\u523b\u8d76\u5c3d\u6740\u7edd\uff1f\u96be\u5f97\u4ed6\u4eec\u90fd\u6653\u6b66\u529f\uff0c\u4e0d\u5982\u5c31\u7559\u4e0b\u4ed6\u4eec\u7ed9\u81ea\u5df1\u8bd5\u62db\uff0c\u73a9\u5f97 \u591f\u4e86\uff0c\u90a3\u65f6\u518d\u6254\u4ed6\u4eec\u4e0b\u6d77\uff0c\u6216\u4efb\u7531\u6bd2\u86c7\u5c06\u4ed6\u4eec\u54ac\u6b7b\uff0c\u4e5f\u8fd8\u672a\u8fdf\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u542c\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u559d\u9053\uff1a\u201c\u5996\u4eba\u65e0\u7406\u53ef\u55bb\uff0c\u5408\u529b\u5c06\u4ed6\u6bd9\u4e86\uff01\u201d\u6bd2\u9f99\u5c0a\u8005\u54c8\u54c8\u5927\u7b11\uff0c \u9053\uff1a\u201c\u6211\u8fd8\u4e0d\u60f3\u6740\u4f60\u4eec\u5462\uff0c\u4f60\u4eec\u5c31\u60f3\u6740\u6211\u4e86\uff1f\u54c8\u54c8\uff0c\u8fd9\u5c31\u662f\u4f60\u4eec\u6240\u8bf4\u7684\u9053\u7406\u4eba\u60c5\uff1f\u201d\u94c1\u62d0\u4e00 \u62a1\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u7518\u51e4\u6c60\u4e0d\u6562\u7a7a\u624b\u63a5\u62db\uff0c\u62d4\u51fa\u539a\u80cc\u65ab\u5c71\u5200\uff0c\u659c\u65a9\u8fc7\u53bb\uff0c\u5582\u7684\u4e00\u58f0\uff0c\u78b0\u4e2a\u6b63\u7740\uff0c \u864e\u53e3\u53d1\u9ebb\u3002\u51af\u745b\u3001\u5510\u6653\u6f9c\u53cc\u5251\u9f50\u5c55\uff0c\u6bd2\u9f99\u5c0a\u8005\u8eab\u5f62\u5fae\u52a8\uff0c\u94c1\u62d0\u5411\u5510\u6653\u6f9c\u4e00\u70b9\uff0c\u5510\u6653\u6f9c\u8bc6\u5f97\u5389 \u5bb3\uff0c\u9000\u540e\u4e00\u6b65\uff0c\u6a2a\u5251\u4e00\u5c01\uff0c\u4e0d\u6599\u6bd2\u9f99\u5c0a\u8005\u6b66\u529f\u5947\u7279\uff0c\u8155\u52b2\u4e00\u53d1\uff0c\u94a2\u94c1\u94f8\u6210\u7684\u62d0\u6756\u7a81\u7136\u50cf\u8170\u5e26 \u822c\u4e00\u5f2f\uff0c\u5237\u7684\u4e00\u4e0b\uff0c\u6253\u65c1\u8fb9\u7684\u51af\u745b\u3002\u51af\u745b\u4e07\u6599\u4e0d\u5230\u4ed6\u7684\u94c1\u62d0\u6756\u4e5f\u4f1a\u8f6c\u5f2f\uff0c\u51e0\u4e4e\u7ed9\u4ed6\u626b\u7740\u3002\u5e78 \u5728\u51af\u745b\u8f7b\u529f\u5353\u7edd\uff0c\u7edd\u9669\u4e4b\u65f6\uff0c\u5251\u5c16\u5411\u4ed6\u62d0\u6756\u4e00\u70b9\uff0c\u8eab\u5b50\u7a81\u7136\u53cd\u5f39\u5f00\u53bb\uff0c\u5510\u6653\u6f9c\u548c\u7518\u51e4\u6c60\u5413\u51fa \u4e00\u8eab\u51b7\u6c57\uff0c\u6025\u5fd9\u5de6\u53f3\u5939\u653b\uff0c\u89e3\u4e86\u51af\u745b\u4e4b\u56f0\u3002 \u3000\u3000\u8fd9\u4e00\u756a\u6bd2\u9f99\u5c0a\u8005\u6709\u5175\u5668\u5728\u624b\uff0c\u5982\u864e\u6dfb\u7ffc\uff0c\u7518\u51e4\u6c60\u3001\u5510\u6653\u6f9c\u3001\u51af\u745b\u3001\u9c7c\u4eae\u56db\u4eba\uff0c\u4f7f\u51fa\u6d51\u8eab \u672c\u9886\uff0c\u5140\u662f\u5904\u5728\u4e0b\u98ce\uff01\u9c7c\u4eae\u4e00\u58f0\u547c\u5578\uff0c\u536b\u626c\u5a01\u3001\u5b5f\u6b66\u529f\u4e24\u4eba\u4e5f\u52a0\u5165\u6218\u56e2\u3002\u8fd9\u4e24\u4eba\u7684\u6b66\u529f\u4e0e\u9c7c \u4eae\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u4ee5\u516d\u654c\u4e00\uff0c\u9996\u5c3e\u547c\u5e94\uff0c\u624d\u6e10\u6e10\u7c91\u4e3b\u5ba2\u4e4b\u52bf\u626d\u8f6c\u8fc7\u6765\u3002 \u3000\u3000\u6597\u5230\u5929\u8272\u5fae\u660e\uff0c\u53cc\u65b9\u90fd\u5df2\u7b4b\u75b2\u529b\u7aed\uff0c\u6bd2\u9f99\u5c0a\u8005\u54c8\u54c8\u5927\u7b11\uff0c\u53c8\u8dd1\u56de\u6797\u4e2d\u3002\u7518\u51e4\u6c60\u6101\u9053\uff1a \u201c\u6253\u4ed6\u4e0d\u6b7b\uff0c\u5929\u5929\u7ed9\u4ed6\u6363\u4e71\uff0c\u54b1\u4eec\u600e\u80fd\u751f\u8fd8\u9646\u5730\uff1f\u9c7c\u8001\u524d\u8f88\u7684\u6c34\u5be8\u4e4b\u56f4\u53c8\u6709\u4f55\u4eba\u89e3\u6551\uff1f\u201d \u3000\u3000\u9c7c\u4eae\u53f9\u9053\uff1a\u201c\u6211\u90a3\u6c34\u5be8\u7531\u5b83\u53bb\u5427\uff0c\u53ea\u662f\u7d2f\u4e86\u8bf8\u541b\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u770b\u8fd8\u662f\u5192\u9669\u518d\u9020\u8239\u53ea\uff0c\u65b9\u6709\u51fa\u8def\u3002\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u8fd8\u672a\u9020\u597d\uff0c\u4ed6\u5c31\u7ed9\u4f60\u6bc1 \u4e86\uff0c\u9020\u53c8\u4f55\u7528\u3002\u201d\u4ed8\u51e4\u6c60\u9053\uff1a\u201c\u4ee5\u6211\u4eec\u4f17\u4eba\u4e4b\u529b\uff0c\u6253\u4ed6\u4e0d\u6b7b\uff0c\u80dc\u4ed6\u5374\u65e0\u95ee\u9898\u3002\u54b1\u4eec\u767d\u5929\u9020 \u8239\uff0c\u665a\u4e0a\u6d3e\u4eba\u8f6e\u503c\u5b88\u591c\uff0c\u9020\u8239\u7684\u5de5\u573a\u5c31\u5728\u6211\u4eec\u8425\u5730\u4e4b\u65c1\uff0c\u95fb\u8b66\u5373\u547c\uff0c\u5408\u529b\u6597\u4ed6\uff0c\u4e0d\u8ba9\u4ed6\u7834 \u574f\uff0c\u603b\u6709\u9020\u6210\u4e4b\u65e5\u3002\u53ea\u662f\u90a3\u4e9b\u6bd2\u86c7\u5374\u65e0\u6cd5\u5bf9\u4ed8\u3002\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u5c9b\u4e0a\u6bd2\u86c7\uff0c\u7ecf\u4e86\u524d\u65e5\u90a3\u573a\u60e8 \u8d25\uff0c\u6599\u4e0d\u6562\u5927\u4e3e\u7a9c\u51fa\u6d77\u6ee9\u3002\u5176\u5b9e\u6bd2\u86c7\u5927\u534a\u80c6\u5c0f\u6015\u4eba\uff0c\u6700\u6bd2\u7684\u8179\u86c7\u80c6\u5b50\u6700\u5c0f\u3002\u5b83\u4eec\u9664\u4e86\u6015\u9f9f\u5c3f \u96c4\u9ec4\u4e4b\u5916\uff0c\u8fd8\u6015\u54cd\u5668\uff0c\u70c2\u8239\u4e0a\u8fd8\u6709\u51e0\u4e2a\u9762\u76c6\uff0c\u53ef\u4ee5\u62ff\u6765\u4e71\u6253\u5413\u86c7\uff0c\u53ea\u8981\u5b83\u4eec\u4e0d\u662f\u6210\u7fa4\u800c\u6765\uff0c \u51c6\u53ef\u5c06\u5b83\u5413\u9000\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u60f3\uff0c\u9020\u8239\u867d\u7136\u672a\u5fc5\u80fd\u6210\uff0c\u603b\u80dc\u4e8e\u5728\u7edd\u671b\u4e2d\u7b49\u6b7b\uff0c\u4e8e\u662f\u542c\u4ece\u7518\u51e4\u6c60\u4e4b\u8ba1\uff0c\u767d\u5929\u4f10\u6728 \u9020\u8239\uff0c\u665a\u4e0a\u6d3e\u4eba\u5b88\u591c\uff0c\u5982\u662f\u8005\u8fc7\u4e86\u4e09\u5929\uff0c\u9664\u4e86\u5c11\u6570\u6bd2\u86c7\u7a9c\u6765\u9a9a\u6270\uff0c\u7acb\u523b\u7ed9\u6253\u6b7b\u4e4b\u5916\uff0c\u6bd2\u9f99\u5c0a \u8005\u5374\u662f\u5f62\u5f71\u4e0d\u89c1\u3002 \u3000\u3000\u8fd9\u665a\u4e0b\u534a\u591c\u8f6e\u5230\u5510\u6653\u6f9c\u548c\u51af\u745b\u770b\u5b88\uff0c\u4e24\u4eba\u81ea\u4ece\u5728\u5c71\u4e1c\u629a\u8859\u76f8\u89c1\u4e4b\u540e\uff0c\u867d\u7136\u4e00\u8def\u540c\u884c\uff0c\u5374 \u672a\u5c1d\u5355\u72ec\u6664\u5bf9\u3002\u8fd9\u665a\u4e24\u4eba\u503c\u591c\uff0c\u5c06\u8fd1\u5929\u660e\uff0c\u4ecd\u65e0\u54cd\u52a8\u3002\u51af\u745b\u7b11\u9053\uff1a\u201c\u90a3\u4e2a\u91ce\u4eba\u4eca\u665a\u5927\u7ea6\u4e0d\u4f1a \u6765\u4e86\u3002\u5510\u53d4\u53d4\uff0c\u6211\u95ee\u4f60\uff0c\u4f60\u5077\u8dd1\u51fa\u6765\uff0c\u4e0d\u6015\u5a76\u5a76\u751f\u6c14\u5417\uff1f\u201d\u5510\u6653\u6f9c\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u662f \u5077\u8dd1\u51fa\u6765\u7684\u3002\u201d\u51af\u745b\u7b11\u9053\uff1a\u201c\u96be\u9053\u662f\u5a76\u5a76\u51c6\u4f60\u51fa\u6765\u7684\uff1f\u6211\u624d\u4e0d\u4fe1\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u662f\u6211\u7684\u5e08 \u5085\u53eb\u6211\u51fa\u6765\u627e\u4f60\u7684\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u6768\u516c\u4f1a\u771f\u597d\uff0c\u600e\u4e48\u5a76\u5a76\u5374\u8fd9\u6837\u51f6\uff1f\u201d\u5510\u6653\u6f9c\u5c34\u5c2c\u4e4b\u6781\uff0c\u76ee \u5149\u79fb\u5f00\uff0c\u907f\u800c\u4e0d\u7b54\u3002\u51af\u745b\u53c8\u9053\uff1a\u201c\u6211\u90a3\u5929\u6253\u4e86\u5a76\u5a76\u4e00\u4e2a\u8033\u5149\uff0c\u53d4\u53d4\u4e0d\u607c\u6211\u5417\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a \u201c\u90a3\u662f\u8be5\u6253\u7684\u3002\u201d\u51af\u745b\u5ffd\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u4e5f\u771f\u53ef\u601c\u2026\u2026\u201d \u3000\u3000\u51af\u745b\u5fc3\u76f4\u53e3\u5feb\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u5c06\u5185\u5fc3\u7684\u8bdd\u8bf4\u4e86\u51fa\u6765\uff0c\u5728\u677e\u679d\u7684\u5fae\u5149\u4e0b\u89c1\u5510\u6653\u6f9c\u7a98\u6001\u6bd5\u5448\uff0c \u8d76\u5fd9\u6536\u53e3\uff0c\u5510\u6653\u6f9c\u5fc3\u60c5\u6fc0\u52a8\uff0c\u4f38\u624b\u63e1\u4f4f\u51af\u745b\u7684\u624b\uff0c\u9053\uff1a\u201c\u963f\u745b\uff0c\u6211\u7684\u547d\u82e6\u662f\u94f8\u5b9a\u4e86\u7684\u3002\u4f60\u65e5 \u540e\u53ef\u4e0d\u8981\u4f3c\u6211\uff0c\u4e3a\u4e86\u62a5\u7b54\u4ec0\u4e48\u6069\u60c5\uff0c\u5c31\u5c06\u81ea\u5df1\u8bb8\u7ed9\u4eba\u5bb6\u3002\u201d\u8fd9\u4e0b\uff0c\u8f6e\u5230\u51af\u745b\u9762\u4e0a\u98de\u7ea2\uff0c\u9053\uff1a \u201c\u53d4\u53d4\u80e1\u8bf4\u3002\u6211\u538b\u6839\u513f\u8fd8\u672a\u60f3\u5230\u8fd9\u6869\u4e8b\u60c5\u3002\u55ef\uff0c\u5176\u5b9e\u561b\uff0c\u82e5\u662f\u522b\u4eba\u5bf9\u4f60\u6709\u6069\uff0c\u90a3\u5c31\u662f\u522b\u4eba\u5bf9 \u4f60\u5173\u5fc3\uff0c\u53ea\u8981\u4e0d\u5355\u662f\u4e3a\u4e86\u62a5\u7b54\uff0c\u90a3\u53c8\u4f55\u5c1d\u4e0d\u53ef\u76f8\u914d\u3002\u201d\u51af\u745b\u5c1a\u662f\u5c0f\u5b69\u813e\u6c14\uff0c\u60f3\u5230\u4ec0\u4e48\u5c31\u8bf4\u4ec0 \u4e48\uff0c\u5510\u6653\u6f9c\u5fc3\u4e2d\u4e00\u8361\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5c31\u7231\u8c08\u8bba\u8fd9\u4e9b\uff01\u201d\u51af\u745b\u622a\u7740\u8bf4\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u80e1 \u8d56\uff0c\u662f\u4f60\u5148\u5f15\u6211\u8c08\u8bba\u7684\u561b\uff01\u201d \u3000\u3000\u6b63\u8c08\u5f97\u5165\u795e\uff0c\u5ffd\u5730\u91cc\u4e00\u58f0\u602a\u7b11\uff0c\u6bd2\u9f99\u5c0a\u8005\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u5230\u4e86\u8eab\u8fb9\u3002\u94c1\u62d0\u4e00\u62a1\uff0c\u5f53\u5934\u5288 \u4e0b\uff0c\u5510\u6653\u6f9c\u548c\u51af\u745b\u5de6\u53f3\u8dc3\u5f00\uff0c\u53cc\u5251\u4ea4\u51fb\uff0c\u76f8\u8ddd\u5df2\u8fd1\uff0c\u8eb2\u95ea\u8270\u96be\uff0c\u53ee\u5f53\u4e24\u58f0\uff0c\u53cc\u5251\u90fd\u65a9\u5728\u62d0\u6756 \u4e0a\uff0c\u706b\u661f\u98de\u8d77\uff0c\u51af\u5510\u4e8c\u4eba\u7ed9\u9707\u5f97\u51e0\u4e4e\u8dcc\u5012\uff0c\u4f46\u6bd2\u9f99\u5c0a\u8005\u7684\u62d0\u6756\u4e5f\u7ed9\u65ab\u4e86\u4e24\u4e2a\u7f3a\u53e3\u3002 \u3000\u3000\u5510\u6653\u6f9c\u5927\u53eb\uff1a\u201c\u5996\u4eba\u6765\u5566\uff01\u201d\u6bd2\u9f99\u5c0a\u8005\u78d4\u78d4\u602a\u7b11\uff0c\u7a81\u7136\u4f38\u5f00\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u4e00\u6293\u5411\u5510\u6653 \u6f9c\u624b\u8155\u6293\u5230\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u628a\u5251\u5012\u662f\u5b9d\u7269\uff0c\u501f\u7ed9\u6211\u770b\u770b\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u624b\u8155\u4e00\u632f\uff0c\u5251\u950b\u5012\u6302\uff0c\u6bd2\u9f99\u5c0a\u8005\u672c\u4e0d\u628a\u5510\u6653\u6f9c\u770b\u5728\u773c\u5185\uff0c\u81ea\u6043\u8fc7\u751a\uff0c\u60f3\u4e0d\u5230\u5929\u5c71 \u5251\u6cd5\u795e\u5999\u65e0\u5339\uff0c\u8d25\u91cc\u6551\u62db\uff0c\u5251\u950b\u4e00\u51fb\uff0c\u987f\u65f6\u5728\u6bd2\u9f99\u5c0a\u8005\u624b\u81c2\u4e0a\u5212\u4e00\u9053\u4f24\u53e3\u3002\u8fd9\u4e00\u6765\u6bd2\u9f99\u5c0a\u8005 \u771f\u4e2a\u53d1\u4e86\u91ce\u6027\uff0c\u4e94\u6307\u4e00\u7d27\uff0c\u6293\u7740\u5510\u6653\u6f9c\u624b\u8155\u8109\u95e8\uff0c\u5510\u6653\u6f9c\u5168\u8eab\u8f6f\u9ebb\uff0c\u65e0\u529b\u518d\u523a\u3002 \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u75be\u8dd1\u5982\u98de\uff0c\u53eb\u9053\uff1a\u201c\u5b9d\u5251\u6211\u4e5f\u4e0d\u8981\u4e86\uff0c\u6211\u8981\u5c06\u4f60\u4e22\u5230\u5927\u6d77\u5582\u9c7c\uff01\u201d\u51af\u745b\u98de\u8eab\u6025 \u8d76\uff0c\u7518\u51e4\u6c60\u7b49\u4e5f\u95fb\u58f0\u6765\u6551\u3002 \u3000\u3000\u51af\u745b\u8f7b\u529f\u8f83\u597d\uff0c\u4e09\u8d77\u4f0f\uff0c\u7bad\u4e00\u822c\u7a9c\u5230\u6bd2\u9f99\u5c0a\u8005\u8eab\u540e\uff0c\u4e3e\u5251\u4fbf\u523a\uff0c\u6bd2\u9f99\u5c0a\u8005\u53cd\u624b\u4e00\u62d0\uff0c\u51af \u745b\u51b2\u4e0a\u4e09\u4e08\u9000\u540e\u4e24\u4e08\uff0c\u5c61\u5c61\u7ed9\u4ed6\u9707\u9000\uff0c\u4ecd\u662f\u7d27\u8ffd\u4e0d\u820d\u3002 \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u745b\u59d1\u5a18\uff0c\u4e0d\u53ef\u5192\u9669\u3002\u201d\u51af\u745b\u90a3\u91cc\u80af\u542c\uff0c\u8ffd\u5230\u6d77\u8fb9\uff0c\u53ea\u89c1\u6bd2\u9f99\u5c0a\u8005\u5de6\u624b\u4e00 \u629b\uff0c\u628a\u5510\u6653\u6f9c\u8fde\u4eba\u5e26\u5251\u629b\u843d\u6d77\u4e2d\uff0c\u51af\u745b\u54ed\u9053\uff1a\u201c\u5996\u4eba\uff0c\u6211\u4e0e\u4f60\u62fc\u4e86\u3002\u201d\u8fd0\u5251\u5982\u98ce\uff0c\u5237\uff0c\u5237\uff0c \u5237\uff0c\u8fde\u523a\u4e09\u5251\uff0c\u5251\u5251\u51f6\u8fa3\uff0c\u6bd2\u9f99\u5c0a\u8005\u547c\u547c\u4e24\u62d0\uff0c\u6a2a\u91cc\u626b\u6765\u3002\u51af\u745b\u4ecd\u662f\u633a\u8eab\u76f4\u4e0a\uff0c\u6bd2\u9f99\u5c0a\u8005\u5fc3 \u9053\uff1a\u201c\u8fd9\u5973\u5a03\u5251\u6cd5\u7cbe\u7edd\uff0c\u53ef\u4e0d\u8981\u5c06\u5979\u51fb\u6bd9\uff0c\u52b2\u529b\u5df2\u53d1\uff0c\u5f3a\u81ea\u7f29\u56de\u4e00\u534a\uff0c\u4f46\u867d\u7136\u5982\u6b64\uff0c\u51af\u745b\u4e5f \u5df2\u7981\u53d7\u4e0d\u4f4f\uff0c\u7ed9\u4ed6\u62d0\u6756\u4e00\u6311\uff0c\u98de\u5230\u534a\u7a7a\uff0c\u4e5f\u843d\u4e0b\u5927\u6d77\u3002\u6bd2\u9f99\u5c0a\u8005\u6447\u9996\u53eb\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u65e9\u6f6e\u4e4d\u53d1\uff0c\u6d6a\u6d9b\u6c79\u6d8c\uff0c\u51af\u745b\u867d\u901a\u6c34\u6027\uff0c\u5374\u975e\u6781\u7cbe\uff0c\u7ed9\u4e00\u4e2a\u65cb\u8f6c\u7684\u6c34\u6da1\u4e00\u5377\uff0c\u987f\u65f6 \u5377\u5230\u6d77\u5fc3\uff0c\u53ea\u89c1\u5510\u6653\u6f9c\u8f7d\u6d6e\u8f7d\u6c89\uff0c\u6b63\u5728\u81ea\u5df1\u9762\u524d\u4e0d\u8fdc\u3002\u51af\u745b\u63d2\u597d\u5b9d\u5251\uff0c\u529b\u5212\u8fc7\u53bb\uff0c\u4e00\u4e2a\u6d6a\u5934 \u7a81\u4ece\u4fa7\u9762\u629b\u6765\uff0c\u4e24\u4eba\u7684\u8eab\u5b50\u5c31\u50cf\u817e\u4e91\u9a7e\u96fe\u4e00\u822c\uff0c\u7ed9\u6d6a\u5934\u629b\u4e0a\u51e0\u5341\u4e08\u9ad8\u7a7a\uff0c\u843d\u4e0b\u6765\u65f6\uff0c\u79bb\u5cb8\u8d8a \u53d1\u8fdc\u4e86\u3002 \u3000\u3000\u7518\u51e4\u6c60\u53c8\u60ca\u53c8\u6012\uff0c\u8d76\u5230\u6d77\u8fb9\uff0c\u4e24\u4eba\u7684\u8eab\u5f71\u5df2\u5377\u6ca1\u5728\u6d2a\u6d9b\u9a87\u6d6a\u4e4b\u4e2d\u3002\u6bd2\u9f99\u5c0a\u8005\u559d\u9053\uff1a\u201c\u4f60 \u4e5f\u60f3\u5230\u5927\u6d77\u5582\u9c7c\u5417\uff1f\u201d\u7518\u51e4\u6c60\u4e3e\u5200\u786c\u65ab\uff0c\u5200\u62d0\u4e00\u4ea4\uff0c\u7acb\u88ab\u9707\u9000\uff0c\u7518\u51e4\u6c60\u5f3a\u6291\u6012\u706b\uff0c\u65bd\u5c55\u516b\u5366 \u6e38\u8eab\u6cd5\uff0c\u907f\u5b9e\u51fb\u865a\u3002\u4e0e\u4ed6\u6e38\u6597\u3002\u7247\u523b\u4e4b\u540e\uff0c\u9c7c\u4eae\u7b49\u4eba\u90fd\u5230\uff0c\u96c6\u5341\u4f59\u4eba\u4e4b\u529b\uff0c\u5c06\u6bd2\u9f99\u5c0a\u8005\u56f4\u5728 \u6838\u5fc3\uff0c\u4ece\u65e9\u81f3\u5348\uff0c\u62fc\u6597\u4f55\u6b62\u5343\u62db\uff0c\u4ecd\u662f\u4ec5\u4ec5\u6253\u4e2a\u5e73\u624b\uff0c\u6bd2\u9f99\u5c0a\u8005\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u8981\u7761\u5348\u89c9\uff0c \u660e\u65e5\u518d\u4f1a\u3002\u201d\u62d0\u6756\u4e00\u52a8\uff0c\u6740\u51fa\u91cd\u56f4\uff0c\u75be\u8dd1\u56de\u53bb\uff0c\u7518\u51e4\u6c60\u8ffd\u4e0d\u4e0a\u4ed6\uff0c\u53ea\u89c1\u4ed6\u8dd1\u5230\u8fd8\u672a\u9020\u597d\u7684\u8239 \u8fb9\uff0c\u547c\u547c\u4e24\u62d0\uff0c\u53c8\u5c06\u8239\u8eab\u7684\u9f99\u9aa8\u6253\u65ad\u4e86\uff0c\u9c7c\u4eae\u4e0e\u7518\u51e4\u6c60\u9762\u9762\u76f8\u89d1\uff0c\u76ee\u9001\u4ed6\u8dd1\u56de\u6797\u4e2d\uff0c\u6bd2\u86c7\u5939 \u9053\u76f8\u8fce\uff0c\u8c01\u6562\u8ffd\u8d76\uff1f \u3000\u3000\u7518\u51e4\u6c60\u5927\u75db\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u5982\u6b64\u4e27\u547d\uff0c\u5b9e\u5728\u4e0d\u503c\uff0c\u53eb\u6211\u5982\u4f55\u5fc3\u5b89\uff1f\u201d\u9c7c\u4eae\u4e3e\u5200\u8bf4\u9053\uff1a\u201c\u7f62 \u4e86\uff0c\u7f62\u4e86\uff0c\u4e0e\u5176\u53d7\u4ed6\u6298\u78e8\uff0c\u4e0d\u5982\u6b7b\u4e86\u7b97\u4e86\u5427\u3002\u201d\u7518\u51e4\u6c60\u5fcd\u7740\u773c\u6cea\uff0c\u6025\u5fd9\u5c06\u4ed6\u529d\u6b62\u3002\u8fd9\u65f6\u4f17\u4eba \u4e2d\u5df2\u6709\u56e0\u75b2\u7d2f\u8fc7\u751a\uff0c\u5012\u5730\u4fbf\u5373\u547c\u547c\u7761\u719f\u7684\uff0c\u9c7c\u4eae\u672c\u975e\u6709\u5fc3\u81ea\u6740\uff0c\u53ea\u56e0\u542c\u4e86\u7518\u51e4\u6c60\u521a\u624d\u90a3\u756a\u8bf4 \u8bdd\uff0c\u5185\u759a\u8fc7\u751a\uff0c\u6240\u4ee5\u4e00\u65f6\u836b\u4e86\u77ed\u89c1\uff0c\u5982\u4eca\u89c1\u90e8\u5c5e\u5982\u6b64\uff0c\u51c4\u7136\u4e0b\u6cea\u3002\u7518\u51e4\u6c60\u5fc3\u4e2d\u76d8\u7b97\u9053\uff1a\u201c\u5c11 \u4e86\u5510\u6653\u6f9c\u51af\u745b\u4e24\u4eba\uff0c\u5b9e\u529b\u5927\u51cf\uff0c\u4ee5\u540e\u66f4\u4e0d\u5bb9\u635f\u5931\u4e00\u4eba\u4e86\u3002\u6211\u4eec\u8fd9\u5341\u4f59\u4eba\u7684\u653b\u5b88\u914d\u5408\u8fd8\u672a\u5f97 \u5b9c\uff0c\u8fd8\u8be5\u518d\u7ec6\u5fc3\u7814\u7a76\u5bf9\u654c\u4e4b\u6cd5\u3002\u5440\uff0c\u53ef\u60dc\uff0c\u4ed6\u4eec\u771f\u662f\u6b7b\u5f97\u592a\u4e0d\u503c\u4e86\uff01 \u3000\u3000\u7518\u51e4\u6c60\u548c\u9c7c\u4eae\u7b49\u4eba\u90fd\u4ee5\u4e3a\u51af\u5510\u4e8c\u4eba\u5df2\u6b7b\uff0c\u5176\u5b9e\u6b64\u65f6\u4e8c\u4eba\u6b63\u5728\u6d77\u4e0a\u6f02\u6d41\u3002\u4e24\u4eba\u5185\u529f\u867d\u975e\u6781 \u597d\uff0c\u4f46\u4e5f\u5df2\u9887\u6709\u706b\u5019\u3002\u88ab\u6d6a\u6d9b\u4e00\u5377\uff0c\u95ed\u6c14\u4e0d\u547c\u5438\uff0c\u4e5f\u6371\u5f97\u4e00\u4e9b\u65f6\u5019\uff0c\u51af\u745b\u8ffd\u4e0a\u4e86\u5510\u6653\u6f9c\uff0c\u62c9 \u4ed6\u6d6e\u51fa\u6c34\u9762\uff0c\u95ee\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u6709\u6ca1\u6709\u53d7\u4f24\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6ca1\u6709\uff1f\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e00\u4e2a\u5927\u6d6a \u5934\u53c8\u629b\u8fc7\u6765\uff0c\u4e24\u4eba\u6025\u5fd9\u6f5c\u5728\u6c34\u5e95\uff0c\u8ba9\u6d77\u5e95\u7684\u6f5c\u6d41\u5c06\u4ed6\u4eec\u5377\u8d70\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u5f85\u6ce2\u6d6a\u8fc7\u540e\uff0c\u518d\u9732 \u51fa\u5934\u6765\u6362\u6c14\u3002\u8fd9\u65f6\u5df2\u5230\u6d77\u6d0b\u4e2d\u592e\uff0c\u56db\u987e\u832b\u832b\uff0c\u4e0d\u89c1\u9646\u5730\uff0c\u86c7\u5c9b\u5ea7\u843d\u4f55\u65b9\uff0c\u4e5f\u4e0d\u77e5\u4e86\u3002 \u3000\u3000\u6f02\u6d6e\u534a\u5929\uff0c\u98ce\u6d6a\u6e10\u6b62\uff0c\u4e24\u4eba\u56e0\u5212\u6c34\u5403\u529b\uff0c\u4ecd\u7528\u524d\u6cd5\uff0c\u6f5c\u6c34\u4efb\u5b83\u6f02\u6d6e\u3002\u4e0a\u9762\u6d77\u6ce2\u4e0d\u5174\uff0c\u4e24 \u4eba\u6240\u53d7\u7684\u538b\u529b\u5df2\u51cf\uff0c\u5fc3\u60c5\u7a0d\u677e\uff0c\u8fd9\u65f6\u624d\u53d1\u73b0\u6d77\u5e95\u771f\u662f\u4e00\u4e2a\u5947\u5999\u7684\u4e16\u754c\u3002\u900f\u8fc7\u6d45\u84dd\u8272\u7684\u6d77\u6c34\uff0c \u53ea\u89c1\u6d77\u5e95\u957f\u7740\u5404\u5f0f\u5404\u6837\u7684\u73ca\u745a\uff0c\u6709\u83ca\u82b1\u578b\uff0c\u6709\u7261\u4e39\u578b\uff0c\u6709\u9e7f\u9f9f\u578b\uff0c\u6709\u7684\u751a\u81f3\u5982\u677e\u5982\u67cf\uff0c\u679d\u53f6 \u7e41\u8302\uff0c\u8d64\u6a59\u9ec4\u7eff\u9752\u84dd\u7d2b\uff0c\u5404\u79cd\u989c\u8272\u90fd\u6709\uff0c\u5343\u53d8\u4e07\u5316\uff0c\u5728\u6c34\u5e95\u5e7b\u6210\u82b1\u7684\u4e16\u754c\u3002 \u3000\u3000\u51af\u745b\u5927\u60a6\uff0c\u4ec0\u4e48\u5371\u9669\u5168\u90fd\u5fd8\u6389\uff0c\u8bf4\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u6211\u4e0b\u53bb\u91c7\u73ca\u745a\u3002\u201d\u6c34\u5e95\u8bf4\u8bdd\uff0c\u5510\u6653\u6f9c\u4f46 \u89c1\u5979\u5634\u76ae\u5f00\u5408\uff0c\u542c\u800c\u4e0d\u95fb\u3002\u89c1\u5979\u6f5c\u4e0b\uff0c\u53ea\u597d\u8ddf\u8e2a\u3002\u6d77\u5e95\u7684\u73ca\u6e56\u770b\u4f3c\u89e6\u624b\u53ef\u53ca\uff0c\u5176\u5b9e\u6781\u6df1\uff0c\u6f5c \u4e86\u4e00\u9635\uff0c\u592a\u9633\u7684\u7ea2\u8272\u5149\u5f69\u5df2\u65e0\u6cd5\u770b\u89c1\uff0c\u53ea\u6709\u4e00\u7247\u9ee0\u9eef\u7684\u6df1\u9ed1\u8272\uff0c\u4ec0\u4e48\u4e5f\u770b\u4e0d\u89c1\uff0c\u66f4\u4e0d\u8981\u8bf4\u4ec0 \u4e48\u73ca\u745a\u4e86\u3002 \u3000\u3000\u51af\u745b\u5fc3\u9053\uff1a\u539f\u6765\u8d8a\u6f5c\u8d8a\u6df1\uff0c\u53cd\u800c\u65e0\u8da3\uff0c\u6b63\u60f3\u4e0a\u6d6e\uff0c\u5ffd\u89c1\u6d77\u5e95\u8424\u5149\u95ea\u8000\uff0c\u539f\u6765\u6df1\u6d77\u6c34\u65cf\uff0c \u4f53\u4e0a\u90fd\u80fd\u53d1\u5149\uff0c\u5982\u540c\u8424\u706b\u4e00\u822c\uff0c\u4f8b\u5982\u201c\u7435\u7436\u9c7c\u201d\u7528\u653e\u5149\u6765\u5f15\u8bf1\u5f02\u6027\uff0c\u201c\u5927\u98df\u9cd7\u201d\u5728\u5c3e\u5df4\u672a\u7aef \u653e\u5149\u8bf1\u53e6\u4e00\u79cd\u9c7c\u505a\u5b83\u4fd8\u864f\uff0c\u8fd8\u6709\u4e00\u79cd\u201c\u9f99\u9c7c\u201d\uff0c\u8eab\u957f\u4e0d\u53ca\u4e94\u5bf8\uff0c\u4f53\u4e0a\u5374\u6709\u4e8c\u767e\u9897\u5de6\u53f3\u80fd\u653e\u5149 \u7684\u9aa8\u73e0\uff0c\u5c31\u5982\u904d\u5e03\u660e\u706f\u4e00\u822c\uff0c\u8fd9\u4e9b\u9c7c\u7c7b\u51af\u745b\u89c1\u6240\u672a\u89c1\uff0c\u53c8\u6d41\u8fde\u5fd8\u8fd4\u3002\u5ffd\u5730\u8db3\u8e1d\u4f3c\u7ed9\u4ec0\u4e48\u4e1c\u897f \u7f20\u7740\uff0c\u51af\u745b\u75db\u6781\u65e2\u8dc3\uff0c\u5510\u6653\u6f9c\u53d1\u73b0\u4e86\uff0c\u539f\u6765\u7f20\u5979\u7684\u662f\u4e00\u6761\u516b\u722a\u7ae0\u9c7c\uff0c\u6025\u5fd9\u62d4\u5251\u5c06\u7ae0\u9c7c\u7684\u722a\u65a9 \u65ad\uff0c\u5c06\u5979\u62c9\u51fa\u6c34\u9762\u3002\u51af\u745b\u53d7\u4e86\u8fd9\u6b21\u6559\u8bad\uff0c\u624d\u4e0d\u6562\u518d\u6f5c\u4e0b\u6df1\u6d77\u3002 \u3000\u3000\u6f02\u6d6e\u4e86\u5927\u534a\u5929\uff0c\u4e24\u4eba\u4f53\u529b\u6d88\u8017\u592a\u751a\uff0c\u6e10\u89c9\u75b2\u52b3\uff0c\u95ed\u6c14\u4e5f\u4e0d\u80fd\u652f\u6301\u4e86\u3002\u5510\u6653\u6f9c\u6697\u9053\u4e0d\u597d\uff0c \u5982\u6b64\u4e0b\u53bb\uff0c\u7eb5\u4e0d\u4e27\u8eab\u9c7c\u8179\uff0c\u4e5f\u4f1a\u7d2f\u6b7b\u997f\u6b7b\u6e34\u6b7b\uff08\u6d77\u6c34\u4e0d\u80fd\u6b62\u6e34\uff09\u3002\u6b63\u81ea\u7126\u6025\uff0c\u5ffd\u89c1\u76f8\u8ddd\u4e0d\u8fdc \u7684\u6d77\u9762\uff0c\u6709\u4e00\u6761\u6c34\u67f1\u55b7\u4e0a\u534a\u7a7a\uff0c\u4e00\u4e2a\u5c0f\u5c71\u5cf0\u822c\u7684\u4e1c\u897f\uff0c\u9732\u51fa\u6c34\u9762\u3002\u51af\u745b\u5df2\u77e5\u8fd9\u662f\u9cb8\u9c7c\uff0c\u5ffd\u7136 \u5f97\u4e86\u4e00\u4e2a\u4e3b\u610f\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u9a91\u4e0a\u5b83\u7684\u80cc\u9762\u3002\u201d\u5510\u6653\u6f9c\u4e5f\u89c9\u8fd9\u662f\u6b7b\u91cc\u6c42\u751f\u4e4b\u6cd5\uff0c\u7aed\u529b\u62b5\u53d7\u9cb8\u9c7c \u9f13\u6d6a\u7684\u51b2\u6fc0\uff0c\u6f5c\u5230\u5b83\u7684\u8eab\u8fb9\uff0c\u722c\u4e0a\u9cb8\u80cc\uff0c\u90a3\u5de8\u9cb8\u6709\u5982\u4e00\u5ea7\u5c0f\u5c71\uff0c\u6709\u4e24\u4e2a\u4eba\u722c\u4e0a\uff0c\u5b83\u4e1d\u6beb\u672a\u89c9\u3002 \u3000\u3000\u5de8\u9cb8\u6e38\u4e86\u4e00\u9635\uff0c\u5510\u6653\u6f9c\u53d1\u73b0\u6709\u4e00\u4e2a\u5c0f\u5c9b\uff0c\u6025\u5fd9\u62db\u547c\u51af\u745b\uff0c\u5f85\u9cb8\u9c7c\u6e38\u8fc7\u4e4b\u65f6\uff0c\u6025\u5fd9\u8dc3\u4e0b\uff0c \u6e38\u5230\u5c9b\u4e0a\u3002\u4e24\u4eba\u6709\u8fc7\u86c7\u5c9b\u7684\u7ecf\u5386\uff0c\u90fd\u5c0f\u5fc3\u7ffc\u7ffc\u3002\u8fd9\u5c0f\u5c9b\u7eff\u836b\u8986\u76d6\uff0c\u79bd\u9e1f\u751a\u591a\uff0c\u66f4\u53ef\u559c\u7684\u662f\u4e00 \u6761\u86c7\u90fd\u6ca1\u53d1\u73b0\u3002\u539f\u6765\u8fd9\u5c0f\u5c9b\u4e43\u662f\u73ca\u745a\u7901\u4e0a\u7ed3\u4e86\u8bb8\u591a\u5c42\u9e1f\u7caa\u5c42\u6240\u5f62\u6210\uff0c\u5e74\u6df1\u6708\u4e45\uff0c\u5c9b\u7caa\u6709\u5982\u6ce5 \u571f\uff0c\u56e0\u5176\u80a5\u6c83\uff0c\u6240\u4ee5\u5c9b\u4e0a\u6742\u82b1\u4e1b\u751f\u3002 \u3000\u3000\u51af\u745b\u722c\u4e0a\u5c0f\u5c9b\uff0c\u8fd9\u624d\u677e\u4e86\u53e3\u6c14\uff0c\u770b\u81ea\u5df1\u6e7f\u6dcb\u6dcb\u7684\u8863\u670d\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u597d\uff1f\u201d\u73af\u5c9b\u5de1 \u89c6\uff0c\u5c9b\u7684\u897f\u9762\u6709\u4e00\u5904\u51f9\u4e0b\u53bb\u7684\u6c60\u6cbc\uff0c\u51af\u745b\u63ac\u6c34\u4e00\u5c1d\uff0c\u6e05\u51c9\u4e4b\u6781\uff0c\u559c\u9053\uff1a\u201c\u8fd9\u662f\u6de1\u6c34\u3002\u53d4\u53d4\uff0c \u6211\u8981\u5728\u8fd9\u91cc\u6d17\u6fa1\uff0c\u5f85\u8863\u670d\u6652\u5e72\u4e86\u518d\u8d77\u6765\u3002\u201d\u5510\u6653\u6f9c\u8f6c\u8fc7\u4e86\u8eab\uff0c\u8d70\u51fa\u5916\u9762\u770b\u6d77\u3002\u8fc7\u4e86\u8bb8\u4e45\uff0c\u51af \u745b\u6362\u597d\u6652\u5e72\u7684\u8863\u670d\uff0c\u53eb\u4ed6\u4e5f\u53bb\u6d17\u6fa1\u3002\u8fd9\u4e00\u665a\uff0c\u4e24\u4eba\u6253\u4e86\u51e0\u53ea\u6d77\u9e1f\uff0c\u64e6\u77f3\u751f\u706b\uff0c\u70e4\u719f\u6765\u5403\uff0c\u5473 \u9053\u751a\u597d\u3002 \u3000\u3000\u4e24\u4eba\u5728\u5c0f\u5c9b\u4e0a\u65e5\u65e5\u76fc\u671b\u6709\u8fc7\u5f80\u8239\u53ea\uff0c\u603b\u76fc\u4e0d\u7740\uff0c\u6e10\u6e10\u7531\u79cb\u81f3\u51ac\uff0c\u6c14\u5019\u5bd2\u51b7\uff0c\u51af\u745b\u5c06\u5c9b\u4e0a \u7684\u91ce\u9ebb\uff0c\u7f16\u6210\u8863\u670d\u5fa1\u5bd2\u3002\u770b\u7740\u767d\u663c\u65e5\u77ed\uff0c\u9ed1\u591c\u6e10\u957f\uff0c\u4e24\u4eba\u90fd\u975e\u5e38\u7126\u6025\u3002\u53c8\u4e0d\u77e5\u86c7\u5c9b\u5ea7\u843d\u6cb3 \u65b9\uff0c\u7eb5\u6562\u5192\u9669\uff0c\u4e5f\u65e0\u4ece\u5bfb\u627e\u3002 \u3000\u3000\u4e24\u4eba\u65e5\u5915\u76f8\u5bf9\uff0c\u4fee\u7ec3\u5185\u529f\u5251\u6cd5\uff0c\u65e5\u5b50\u5341\u5206\u6613\u8fc7\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5c9b\u4e0a\u7684\u82b1\u6811\u53c8\u7efd\u84d3\u857e\uff0c\u4f3c\u4e4e \u662f\u6625\u5929\u53c8\u5230\u4e86\u3002\u51af\u745b\u89c1\u5510\u6653\u6f9c\u4e00\u65e5\u5fe7\u4f3c\u4e00\u65e5\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e00\u5e74\u6613\u8fc7\uff0c\u4e94\u6708\u4fbf\u662f\u4ed6\u6bd2\u9152\u53d1\u4f5c \u7684\u671f\u9650\u3002\u73b0\u5728\u867d\u7136\u4e0d\u77e5\u65e5\u5b50\uff0c\u4f46\u5927\u7ea6\u603b\u662f\u6625\u5929\u4e86\uff0c\u8d8a\u60f3\u8d8a\u5fe7\u3002\u4e00\u65e5\uff0c\u5bf9\u5510\u6653\u6f9c\u9053\uff1a\u201c\u54b1\u4eec\u5192 \u9669\u9020\u8239\u51fa\u6d77\u5427\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u6447\u6447\u5934\u9053\uff1a\u201c\u4f60\u6211\u90fd\u4e0d\u4f1a\u4f7f\u8239\uff0c\u53c8\u4e0d\u662f\u7cbe\u901a\u6c34\u6027\uff0c\u5982\u4f55\u80fd\u5728\u5927\u6d77\u822a\u884c\uff1f\u4f60\u8fd8\u60f3\u518d \u78b0\u5230\u4e00\u6761\u5de8\u9cb8\uff0c\u5c06\u4f60\u5b89\u5168\u8f7d\u56de\u9646\u5730\u5417\uff1f\u201d\u51af\u745b\u9eef\u7136\u4e0d\u8bed\uff0c\u5ffd\u95ee\u9053\uff1a\u201c\u5510\u53d4\u53d4\uff0c\u4f60\u7684\u5185\u529f\u7cbe\u8fdb \u751a\u901f\uff0c\u8fd1\u6765\u5410\u7eb3\u4e4b\u9645\uff0c\u80f8\u81c6\u5982\u4f55\uff1f\u201d\u5510\u6653\u6f9c\u95fb\u8a00\u77e5\u610f\uff0c\u5f3a\u7b11\u9053\uff1a\u201c\u751f\u6b7b\u6709\u547d\uff0c\u4f60\u66ff\u6211\u62c5\u5fe7\u505a \u4ec0\u4e48\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u96be\u9053\u54b1\u4eec\u5c31\u5728\u8fd9\u5c0f\u5c9b\u675f\u624b\u5f85\u6bd9\uff1f\u8fd8\u662f\u5192\u9669\u51fa\u6d77\u5427\uff01\u201d\u5510\u6653\u6f9c\u6bc5\u7136\u8bf4\u9053\uff1a \u201c\u6211\u4fbf\u662f\u6b7b\u5728\u6b64\u5730\uff0c\u4e5f\u4e0d\u7d2f\u4f60\u5192\u9669\u3002\u5f85\u5b83\u4e00\u4e24\u5e74\uff0c\u603b\u4f1a\u6709\u8239\u53ea\u7ecf\u8fc7\uff0c\u5c9b\u4e0a\u53c8\u4e0d\u6101\u98df\uff0c\u4f60\u6015\u4ec0 \u4e48\u3002\u201d \u3000\u3000\u51af\u745b\u5fc3\u4e2d\u611f\u52a8\uff0c\u773c\u5708\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u6211\u6015\u81ea\u5df1\uff0c\u662f\u6211\u6015\u4f60\u2026\u2026\u54ce\u5440\uff0c\u5510\u53d4\u53d4\uff0c\u4e3a\u4ec0\u4e48 \u4f60\u5904\u5904\u66ff\u6211\u8bbe\u60f3\uff1f\u662f\u6211\u7d2f\u4f60\u996e\u4e86\u6bd2\u9152\uff0c\u73b0\u5728\u53c8\u8981\u7d2f\u4f60\u4e27\u8eab\u8352\u5c9b\u3002\u201d\u8bf4\u7740\uff0c\u5ffd\u7136\u5927\u54ed\u8d77\u6765\uff0c\u53cc \u81c2\u73af\u62b1\u6653\u6f9c\u9888\u9879\u3002\u5510\u6653\u6f9c\u9ed8\u7136\u4e0d\u8bed\uff0c\u826f\u4e45\uff0c\u624d\u628a\u5979\u53cc\u81c2\u62c9\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u50bb\u5b69\u5b50\uff0c\u5c31\u7b97\u6211\u4e94\u6708 \u8eab\u6b7b\uff0c\u6765\u65e5\u65e0\u591a\uff0c\u54b1\u4eec\u4e5f\u8be5\u5feb\u4e50\u5440\uff0c\u54ed\u7740\u7b49\u6b7b\uff0c\u591a\u4e0d\u503c\u5f97\uff01\u201d \u3000\u3000\u51af\u745b\u773c\u6cea\u4e00\u6536\uff0c\u5ffd\u7136\u8df3\u8d77\u6765\u9053\uff1a\u201c\u662f\u5440\uff0c\u53d4\u53d4\uff0c\u54b1\u4eec\u5e94\u8be5\u5feb\u6d3b\uff01\u53d4\u53d4\uff0c\u4f60\u544a\u8bc9\u6211\uff0c\u4f60\u6709 \u4ec0\u4e48\u4e0d\u79f0\u5fc3\u987a\u610f\u4e4b\u4e8b\uff0c\u54b1\u4eec\u8bbe\u6cd5\u8865\u507f\u3002\u201d\u5510\u6653\u6f9c\u60f3\u8d77\u81ea\u5df1\u7684\u51c4\u51c9\u8eab\u4e16\u548c\u4e0d\u5982\u610f\u7684\u5a5a\u4e8b\uff0c\u60b2\u4ece \u4e2d\u6765\uff0c\u4e0d\u53ef\u65ad\u7edd\uff0c\u5374\u5f3a\u7b11\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\uff01\u201d \u3000\u3000\u51af\u745b\u9053\uff1a\u201c\u77a7\u4f60\u7684\u773c\u775b\uff0c\u6211\u77e5\u9053\u4f60\u662f\u9a97\u6211\uff01\u201d\u8fc7\u4e86\u4e00\u9635\uff0c\u5ffd\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u5fc3\u5730\u5584\u826f\uff0c \u5904\u5904\u66ff\u4eba\u7740\u60f3\uff0c\u4e3a\u4f55\u5a76\u5a76\u8fd8\u8981\u65f6\u65f6\u53d1\u4f60\u813e\u6c14\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u600e\u77e5\u9053\uff1f\u522b\u63d0\u5979\u597d\u5417\uff1f\u63d0\u8d77 \u5979\u6211\u5c31\u5fc3\u70e6\u3002\u201d\u51af\u745b\u5782\u9996\u4e0d\u8bed\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u5ffd\u7136\u62cd\u624b\u7b11\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u5c0f\u65f6\u5019\u4f60\u6559\u8fc7\u6211\u4e0d\u8981\u8bf4 \u8c0e\uff0c\u662f\u5417\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u600e\u4e48\u5566\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u90a3\u4f60\u81ea\u5df1\u5c31\u4e0d\u8be5\u8bf4\u8c0e\u3002\u4f60\u521a\u624d\u8bf4\u6ca1\u6709\u4ec0\u4e48 \u4e0d\u79f0\u5fc3\u5982\u610f\u4e4b\u4e8b\uff0c\u73b0\u5728\u53c8\u8bf4\u63d0\u8d77\u5a76\u5a76\u5c31\u5fc3\u70e6\uff0c\u90a3\u5c82\u4e0d\u662f\u4f60\u5b9e\u5728\u6709\u7740\u4e0d\u79f0\u5fc3\u4e0d\u5982\u610f\u4e4b\u4e8b\uff1f\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u5fc3\u60c5\u9707\u8361\uff0c\u8111\u6d77\u91cc\u5148\u98d8\u8fc7\u5415\u56db\u5a18\uff0c\u5fc3\u9053\uff1a\u201c\u5904\u5904\u66ff\u4eba\u8bbe\u60f3\uff0c\u8fd9\u662f\u5415\u56db\u5a18\u6559\u6211\u7684\u3002 \u6211\u73b0\u5728\u53ea\u628a\u5979\u5f53\u6210\u5e08\u957f\uff0c\u4ee5\u524d\u7684\u75f4\u604b\uff0c\u5df2\u8fc7\u53bb\u4e86\u3002\u201d\u970e\u5ffd\u4e4b\u95f4\uff0c\u8111\u6d77\u91cc\u53c8\u73b0\u51fa\u4e86\u6768\u67f3\u9752\uff0c\u5f71 \u5b50\u4e00\u63a0\u5373\u8fc7\u3002\u8ddf\u7740\u7741\u773c\u4e00\u770b\uff0c\u5374\u89c1\u7740\u8fd9\u4e2a\u201c\u5c0f\u4f84\u5973\u201d\u5de7\u7b11\u987e\u76fc\uff0c\u8bf4\u8bdd\u5374\u50cf\u4e00\u9897\u9897\u5f39\u5b50\u4f3c\u7684\uff0c \u6253\u52a8\u7740\u4ed6\u7684\u5fc3\u5f26\uff0c\u4ed6\u8138\u4e0a\u7a81\u7136\u53d1\u70e7\uff0c\u4e0d\u6562\u518d\u60f3\u4e0b\u53bb\u3002 \u3000\u3000\u51af\u745b\u53c8\u62cd\u624b\u7b11\u9053\uff1a\u201c\u770b\u5566\uff0c\u53d4\u53d4\u8138\u7ea2\u54e9\uff01\u201d\u8ffd\u95ee\u9053\uff1a\u201c\u4f60\u5f53\u521d\u4e3a\u4f55\u548c\u5a76\u5a76\u8ba2\u4eb2\uff1f\u201d\u5510\u6653 \u6f9c\u4f4e\u5934\u9053\uff1a\u201c\u5979\u7239\u7239\u5bf9\u6211\u597d\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u662f\u8feb\u4e8e\u65e0\u5948\u7684\u4e86\u3002\u5047\u5982\u4f60\u4e0d\u4f1a\u6b7b\uff0c\u4f60\u4f4f \u4e00\u4e16\u8352\u5c9b\uff0c\u5979\u53c8\u4e0d\u8ddf\u7740\u4f60\uff0c\u4f60\u5fc3\u4e2d\u8fd8\u628a\u5979\u5f53\u662f\u59bb\u5b50\uff0c\u5b88\u5019\u5979\u5417\uff1f\u201d\u5510\u6653\u6f9c\u5fc3\u9b42\u52a8\u8361\uff0c\u6447\u624b\u8bf4 \u9053\uff1a\u201c\u53eb\u4f60\u522b\u63d0\u8fd9\u4e2a\uff0c\u4f60\u63d0\u8fd9\u4e2a\uff0c\u6211\u53c8\u4e0d\u5feb\u4e50\u5566\uff01\u201d \u3000\u3000\u51af\u745b\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u63d0\uff0c\u4e0d\u63d0\u3002\u53d4\u53d4\uff0c\u6211\u8981\u8bbe\u6cd5\u4f7f\u4f60\u5feb\u4e50\uff01\u201d\u8981\u77e5\u51af\u745b\u5df2\u662f\u5341\u4e03\u516b\u5c81\u7684 \u5c11\u5973\uff0c\u6b63\u662f\u60c5\u82d7\u521d\u8301\u4e4b\u65f6\uff0c\u8fd9\u51e0\u4e2a\u6708\u6765\uff0c\u5979\u548c\u5510\u6653\u6f9c\u671d\u5915\u76f8\u5bf9\uff0c\u5fc3\u4e2d\u53c8\u611f\u6fc0\u4ed6\u76f8\u6551\u4e4b\u6069\uff0c\u7adf \u7136\u4e0d\u77e5\u4e0d\u89c9\u7231\u4e0a\u4e86\u4ed6\u3002\u5979\u81ea\u5df1\u4e5f\u672a\u6709\u610f\u8bc6\u7684\u60f3\u8fc7\u8981\u505a\u4ed6\u7684\u59bb\u5b50\uff0c\u53ea\u662f\u89c9\u5f97\u201c\u53d4\u53d4\u201d\u82e5\u5a36\u90a3\u4e2a \u201c\u5a76\u5a76\u201d\uff0c\u5b9e\u5728\u4e0d\u503c\u3002\u5979\u5728\u5929\u5c71\u957f\u5927\uff0c\u6027\u5b50\u7eaf\u771f\uff0c\u80f8\u4e2d\u5e76\u65e0\u4e16\u4fd7\u4e4b\u89c1\uff0c\u5bf9\u5f02\u59d3\u201c\u53d4\u4f84\u201d\u7684\u540d \u4efd\uff0c\u6839\u672c\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u81ea\u8fd9\u65e5\u8d77\uff0c\u4e00\u79cd\u5947\u5999\u7684\u611f\u60c5\u5728\u4e24\u4eba\u4e4b\u95f4\u6ecb\u957f\u8d77\u6765\u3002\u5510\u6653\u6f9c\u548c\u5979\u4e00\u540c\u73a9\uff0c\u597d\u50cf\u662f\u548c\u540c\u4e00\u8f88 \u7684\u670b\u53cb\u73a9\u4f3c\u7684\uff0c\u201c\u53d4\u4f84\u201d\u4e4b\u95f4\u7684\u62d8\u675f\u5df2\u60ed\u6e10\u6d88\u706d\u4e86\u3002\u4ed6\u4eec\u4e00\u540c\u91c7\u6458\u91ce\u82b1\uff0c\u6349\u6811\u4e0a\u7684\u5c0f\u9e1f\uff0c\u5728 \u6d77\u8fb9\u9493\u9c7c\uff0c\u65e9\u4e0a\u770b\u65e5\u51fa\uff0c\u665a\u4e0a\u770b\u661f\u661f\uff0c\u65e5\u5b50\u679c\u7136\u8fc7\u5f97\u975e\u5e38\u5feb\u6d3b\u3002 \u3000\u3000\u4ed6\u4eec\u4e5f\u65e5\u65e5\u76fc\u671b\u6709\u8239\u53ea\u7ecf\u8fc7\u3002\u4f46\u6709\u65f6\u5510\u6653\u6f9c\u4e5f\u4f1a\u6709\u4e00\u79cd\u5947\u602a\u7684\u611f\u60f3\uff1a\u5982\u679c\u771f\u6709\u8239\u53ea\u628a\u6211 \u8f7d\u56de\u9646\u5730\uff0c\u800c\u6211\u53c8\u4e0d\u4f1a\u6b7b\uff0c\u90a3\u65f6\u600e\u6837\uff1f\u601d\u5ff5\u81f3\u6b64\uff0c\u5ffd\u53c8\u89c9\u5f97\u4f3c\u4e4e\u5728\u8fd9\u4e2a\u5c0f\u5c9b\u4e0a\u548c\u51af\u745b\u5c3d\u60c5\u73a9 \u4e50\uff0c\u8fd8\u6709\u610f\u601d\u5f97\u591a\u3002 \u3000\u3000\u4e00\u65e5\uff0c\u4ed6\u4eec\u53c8\u5982\u5e38\u7684\u5728\u6d77\u8fb9\u773a\u671b\uff0c\u8fd9\u5929\uff0c\u6625\u5bd2\u6599\u5c96\uff0c\u5929\u8272\u9634\u973e\uff0c\u4ed6\u4eec\u5fc3\u60f3\u4e00\u5b9a\u4e0d\u4f1a\u6709\u8239 \u53ea\u7ecf\u8fc7\u4e86\u3002\u8c01\u77e5\u770b\u4e86\u4e00\u9635\uff0c\u5ffd\u7136\u53d1\u73b0\u6d77\u7684\u4e0a\u7a7a\u6709\u5341\u591a\u53ea\u602a\u9e1f\u98de\u6765\uff0c\u51af\u745b\u9053\uff1a\u201c\u54a6\uff0c\u53c8\u4e0d\u662f\u6674 \u6717\u7684\u5929\u6c14\uff0c\u9ebb\u9e70\u4e5f\u51fa\u6765\u89c5\u98df\u3002\u201d\u8fc7\u4e86\u4e00\u9635\uff0c\u6d77\u9762\u9732\u51fa\u6845\u6746\uff0c\u51af\u745b\u559c\u9053\uff1a\u201c\u554a\uff0c\u65e5\u76fc\u591c\u76fc\uff0c\u7ec8 \u4e8e\u76fc\u5230\u8239\u6765\u4e86\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u522b\u9ad8\u5174\uff0c\u4f60\u770b\u90a3\u4e9b\u98de\u5728\u8239\u524d\u9762\u7684\u9e1f\uff0c\u4f60\u8ba4\u5f97\u5417\uff1f\u201d\u51af\u745b\u770b\u4e86\u4e00\u770b\uff0c\u4e5f\u8be7 \u5f02\u9053\uff1a\u201c\u8fd9\u662f\u732b\u9e70\u5440\uff01\u201d\u5510\u6653\u6f9c\uff1a\u201c\u662f\u5440\uff0c\u732b\u9e70\u4e3a\u4ec0\u4e48\u98de\u5230\u8fd9\u91cc\u6765\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u4e5f\u8bb8\u732b\u9e70 \u5c9b\u548c\u86c7\u5c9b\u90fd\u79bb\u6211\u4eec\u8fd9\u91cc\u4e0d\u8fdc\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u732b\u9e70\u4e00\u51fa\u4fbf\u662f\u4e00\u5927\u7fa4\u3002\u8fd9\u5c0f\u7fa4\u732b\u9e70\uff0c\u968f\u7740\u8239 \u53ea\uff0c\u597d\u50cf\u5e26\u8def\u4e00\u822c\uff0c\u6211\u6015\u8fd9\u4e9b\u732b\u9e70\uff0c\u662f\u8239\u4e0a\u7684\u4eba\u517b\u719f\u7684\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u90a3\u53c8\u600e\u4e48\u6837\uff1f\u201d\u5510\u6653 \u6f9c\u9053\uff1a\u201c\u4f60\u60f3\u80fd\u517b\u719f\u732b\u9e70\u7684\u8fd8\u6709\u4f55\u4eba\uff1f\u9664\u4e86\u53cc\u9b54\u5916\uff0c\u5c31\u662f\u6bd2\u9f99\u5c0a\u8005\u4e86\u3002\u86c7\u5c9b\u79bb\u732b\u9e70\u5c9b\u5f88\u8fd1\uff0c \u4ed6\u80fd\u517b\u86c7\uff0c\u4e5f\u80fd\u517b\u732b\u9e70\u5440\uff01\u53cc\u9b54\u5728\u5e74\u7fb9\u5c27\u5e10\u4e0b\uff0c\u6211\u770b\u8239\u4e0a\u7684\u4eba\u591a\u534a\u662f\u6bd2\u9f99\u5c0a\u8005\u4e86\uff0c\u82e5\u662f\u4ed6 \u6765\uff0c\u6211\u4eec\u8fd8\u6709\u547d\u4e48\uff1f\u201d\u51af\u745b\u60f3\u8d77\u6bd2\u9f99\u5c0a\u8005\u90a3\u65e5\u6068\u732b\u9e70\u6740\u4ed6\u6bd2\u86c7\u7684\u795e\u60c5\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u65e2\u6068\u732b \u9e70\uff0c\u8239\u4e2d\u7684\u4eba\u5e94\u8be5\u4e0d\u662f\u4ed6\u5427\uff1f\u201d\u867d\u7136\u5982\u6b64\u5fc3\u60f3\uff0c\u5fc3\u4e2d\u5230\u5e95\u60f4\u60f4\u4e0d\u5b89\uff0c\u770b\u7740\u90a3\u53ea\u5c0f\u822c\u8d8a\u6765\u8d8a \u8fd1\u3002\u60f3\u8d77\u6bd2\u9f99\u5c0a\u8005\u7684\u51f6\u76f8\uff0c\u66f4\u662f\u5fc3\u614c\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u4ed9\u5c9b\u5c3d\u6e05\u6d88\u5c81\u6708\uff0c\u53ea\u9632\u9b54\u624b\u53c8\u4f38\u6765\u3002 \u3000\u3000\u6b32\u77e5\u6765\u8005\u662f\u8c01\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e94\u56de\u3000\u4e00\u6b7b\u89e3\u51a4\u4ec7\u3000\u9b54\u5934\u9001\u836f\u3000\u7247\u8a00\u5f00\u6897\u585e\u3000\u826f\u53cb\u8c08\u5fc3 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u4e94\u56de \u4e00\u6b7b\u89e3\u51a4\u4ec7\u3000\u9b54\u5934\u9001\u836f \u7247\u8a00\u5f00\u6897\u585e\u3000\u826f\u53cb\u8c08\u5fc3 \u3000\u3000\u5c0f\u821f\u5982\u7bad\uff0c\u8d8a\u6765\u8d8a\u8fd1\u3002\u53ea\u89c1\u4e24\u4e2a\u9762\u8272\u7126\u9ec4\u7684\u5e72\u7626\u8001\u5934\uff0c\u7a7f\u7740\u4e00\u8eab\u9ec4\u9ebb\u8863\u88f3\uff0c\u7acb\u5728\u8239\u5934\uff0c \u8d6b\u7136\u7adf\u662f\u53cc\u9b54\u3002\u51af\u745b\u8df3\u8d77\u6765\u9053\uff1a\u201c\u53c8\u662f\u8fd9\u4e24\u4e2a\u8001\u8d3c\uff0c\u5510\u53d4\u53d4\uff0c\u4ed6\u4eec\u7684\u6307\u7532\u6709\u6bd2\uff0c\u7b49\u4e0b\u4f60\u8981\u5f53 \u5fc3\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u4ed6\u4eec\u5417\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u4ed6\u4eec\u5728\u5e74\u7fb9\u5c27\u7684\u5bb6\u95e8\u524d\u66fe\u548c\u6211\u6253\u4e86\u4e00\u67b6\u3002 \u6211\u5403\u4e86\u4ed6\u4eec\u7684\u5927\u4e8f\uff0c\u8fd9\u56de\u54b1\u4eec\u6709\u4e24\u4e2a\u4eba\uff0c\u53ea\u8981\u4e0d\u7ed9\u4ed6\u4eec\u6293\u7740\uff0c\u54b1\u4eec\u662f\u7a33\u64cd\u80dc\u7b97\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6b63\u60f3\u95ee\u51af\u745b\uff0c\u53cc\u9b54\u66fe\u5bf9\u5979\u8bf4\u8fc7\u4ec0\u4e48\u8bdd\uff0c\u5c0f\u821f\u62cd\u5cb8\uff0c\u53cc\u9b54\u8df3\u4e86\u4e0b\u6765\u3002\u516b \u81c2\u795e\u9b54\u8428\u5929\u523a\u53eb\u9053\uff1a\u201c\u7433\u4e2b\u5934\uff0c\u4f60\u522b\u614c\uff0c\u6211\u6551\u4f60\u51fa\u53bb\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u8c01\u8981\u4f60 \u6551\uff1f\u201d\u8428\u5929\u523a\u4e00\u773c\u77a5\u89c1\u5510\u6653\u6f9c\u4e5f\u5728\u65c1\u8fb9\uff0c\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u4e5f\u5728\u4e4b\u513f\uff0c\u5927\u6d77\u832b\u832b\uff0c\u4f60\u51fa\u4e0d\u53bb \u4e86\uff0c\u4e0d\u5982\u8ddf\u6211\u56de\u732b\u9e70\u5c9b\uff0c\u505a\u6211\u7684\u5f92\u5f1f\u5427\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u84e6\u7136\u53eb\u9053\uff1a\u201c\u51af\u745b\uff0c\u8fd9\u662f\u4f60\u7684\u4ec7\u4eba\uff0c\u65e0\u53ef\u601c\u89c1\uff0c\u5728\u6211\u4e34\u6b7b\u4e4b\u524d\uff0c\u6559\u4ed6\u4eec\u649e\u5230\u8fd9\u5c9b \u4e0a\u6765\u3002\u622a\u7740\u4ed6\u4eec\u7684\u53bb\u8def\uff0c\u4e0d\u8981\u653e\u4ed6\u4eec\u8d70\u4e86\uff01\u201d\u5510\u6653\u6f9c\u4f30\u8ba1\u81ea\u5df1\u6b7b\u671f\u4e0d\u51fa\u767e\u65e5\uff0c\u65e9\u5c31\u60f3\u5728\u4e34\u6b7b \u4e4b\u524d\uff0c\u628a\u51af\u745b\u7684\u8eab\u4e16\u5411\u5979\u7ec6\u8bf4\uff0c\u5982\u4eca\u89c1\u4e86\u4ec7\u4eba\uff0c\u4e0d\u6687\u7ec6\u8bf4\uff0c\u7acb\u523b\u62d4\u5251\u4e0a\u524d\u3002 \u3000\u3000\u5927\u529b\u795e\u9b54\u8428\u5929\u90fd\u53eb\u9053\uff1a\u201c\u597d\u54c7\uff0c\u54e5\u54e5\uff0c\u6211\u53eb\u4f60\u4e0d\u5fc5\u627e\u8fd9\u91ce\u4e2b\u5934\uff0c\u4f60\u4e0d\u542c\u6211\u7684\u8bdd\uff0c\u4f60\u770b\uff0c \u4ed6\u4eec\u73b0\u5728\u628a\u597d\u5fc3\u5f53\u4f5c\u6076\u610f\u4e86\uff01\u201d\u8428\u5929\u523a\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u5341\u51e0\u5e74\u524d\u4e4b\u4e8b\uff0c\u63d0\u5b83\u4f5c\u751a\uff1f\u518d\u8bf4\u5979\u7684\u5bb6 \u4eba\u4e5f\u4e0d\u662f\u6211\u4eec\u5144\u5f1f\u6740\u7684\uff01\u201d \u3000\u3000\u51af\u745b\u4e00\u542c\uff0c\u604d\u5982\u6674\u5929\u6253\u4e86\u4e00\u4e2a\u9739\u96f7\uff0c\u53eb\u9053\uff1a\u201c\u5510\u53d4\u53d4\uff0c\u6211\u7684\u5bb6\u4eba\u662f\u60e8\u6b7b\u7684\u4e48\uff1f\u201d\u5510\u6653\u6f9c \u9053\uff1a\u201c\u55ef\uff0c\u4f60\u7684\u7237\u7237\u548c\u7236\u4eb2\u662f\u8840\u6ef4\u5b50\u6740\u7684\uff0c\u4f60\u7684\u6bcd\u4eb2\u662f\u4ed6\u4eec\u52ab\u53bb\u7684\u3002\u4f60\u4e5f\u66fe\u88ab\u4ed6\u4eec\u52ab\u5230\u5f3a\u76d7 \u7a9d\u91cc\u4f4f\u4e86\u51e0\u5e74\uff01\u201d\u51af\u745b\u5927\u53eb\u4e00\u58f0\uff0c\u98de\u8eab\u4e00\u8dc3\uff0c\u5b9d\u5251\u4e00\u6325\uff0c\u5411\u8428\u5929\u523a\u6a2a\u524a\u8fc7\u53bb\uff0c\u5251\u5c16\u98a4\u52a8\uff0c\u5bd2 \u5149\u70b9\u70b9\uff0c\u987f\u5982\u6d6a\u82b1\u98de\u6d12\uff0c\u76f4\u6251\u8fc7\u6765\uff01\u8428\u5929\u523a\u6a2a\u8eab\u4e00\u8dc3\uff0c\u7bad\u4e00\u822c\u7684\u98de\u5c04\u51fa\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u7433\u4e2b \u5934\uff0c\u4e0d\u8bba\u597d\u574f\uff0c\u6211\u4e5f\u66fe\u5bf9\u4f60\u6709\u8fc7\u517b\u80b2\u4e4b\u6069\u3002\u4f60\u7684\u6bcd\u4eb2\u867d\u7136\u662f\u6211\u6240\u52ab\uff0c\u4f46\u5176\u540e\u5374\u662f\u738b\u9675\u903c\u6b7b \u7684\uff0c\u4e0e\u6211\u4eec\u65e0\u5e72\uff01\u201d\u5176\u5b9e\u51af\u745b\u7684\u6bcd\u4eb2\u909d\u7ec3\u971e\u662f\u5230\u4e86\u5317\u4eac\u4e4b\u540e\uff0c\u79c1\u81ea\u9003\u8d70\u7684\u3002\u539f\u6765\u738b\u9675\u56e0\u804c\u4f4d \u5351\u5fae\uff0c\u4e0d\u80fd\u4e0e\u53cc\u9b54\u540c\u4f4f\uff0c\u4ed6\u60f3\u903c\u5e08\u5ac2\u5728\u5916\u9762\u8d41\u5c4b\u6210\u5a5a\uff0c\u5c82\u77e5\u909d\u7ec3\u971e\u5728\u8def\u4e0a\u4e0d\u6562\u9003\u8d70\uff0c\u4e43\u662f\u5fcc \u60ee\u53cc\u9b54\uff0c\u53cc\u9b54\u4e0d\u5728\uff0c\u738b\u9675\u4e00\u4eba\uff0c\u54ea\u662f\u5979\u7684\u5bf9\u624b\uff1f\u7ed9\u5979\u75db\u6253\u4e00\u987f\uff0c\u4fbf\u81ea\u9003\u4e86\u3002\u738b\u9675\u662f\u4e2a\u7231\u9762\u5b50 \u7684\u5c0f\u4eba\uff0c\u5f53\u65f6\u6b63\u60f3\u5df4\u7ed3\u7687\u5e9c\u7684\u6b66\u58eb\uff0c\u529b\u56fe\u201c\u4e0a\u8fdb\u201d\uff0c\u81ea\u5df1\u7ed9\u4e00\u4e2a\u5973\u5b50\u75db\u6253\uff0c\u8bf4\u51fa\u6765\u60f9\u4eba\u7b11 \u8bdd\uff0c\u6240\u4ee5\u5403\u4e86\u5927\u4e8f\uff0c\u4e5f\u53ea\u597d\u54d1\u5fcd\uff0c\u5411\u5916\u53ea\u662f\u8bf4\u90a3\u4eba\u4e0d\u670d\u6c34\u571f\u6b7b\u4e86\u3002\u53cc\u9b54\u4e0d\u77e5\u909d\u7ec3\u971e\u9003\u8d70\u4e4b \u4e8b\uff0c\u53ea\u9053\u5979\u662f\u88ab\u738b\u9675\u903c\u6b7b\u7684\u3002 \u3000\u3000\u51af\u745b\u8fd9\u65f6\u6025\u75db\u653b\u5fc3\uff0c\u4e5f\u65e0\u6687\u518d\u95ee\u738b\u9675\u662f\u8c01\uff0c\u6325\u5251\u53c8\u5411\u8428\u5929\u523a\u6025\u523a\uff01 \u3000\u3000\u8428\u5929\u523a\u6025\u5c55\u732b\u9e70\u6251\u51fb\u7684\u7edd\u6280\uff0c\u9661\u7136\u8dc3\u8d77\u4e09\u4e08\u591a\u9ad8\uff0c\u51af\u745b\u8ddf\u8e2a\u7a9c\u4e0a\uff0c\u7ed9\u4ed6\u4e00\u4e2a\u56de\u65cb\uff0c\u53c8\u907f \u4e86\u5f00\u53bb\u3002\u51af\u745b\u518d\u523a\uff0c\u53ea\u542c\u5f97\u8428\u5929\u523a\u53c8\u53eb\u9053\uff1a\u201c\u7433\u4e2b\u5934\uff0c\u4f60\u4e0d\u662f\u8bf4\u8fc7\u8981\u505a\u6211\u7684\u5973\u513f\u5417\uff1f\u201d\u8428\u5929 \u523a\u4ecd\u662f\u628a\u51af\u745b\u5f53\u505a\u51af\u7433\uff0c\u5c06\u51af\u7433\u5728\u5e74\u5bb6\u5411\u4ed6\u6c42\u60c5\u7684\u8bdd\u63d0\u51fa\u8d28\u8be2\uff0c\u51af\u745b\u6012\u4e0d\u53ef\u904f\uff0c\u559d\u9053\uff1a\u201c\u8c01 \u505a\u4f60\u7684\u5973\u513f\uff1f\u6211\u4e43\u5929\u5c71\u95e8\u4e0b\uff0c\u5c82\u662f\u8ba4\u8d3c\u4f5c\u7236\u4e4b\u4eba\uff1f\u201d\u8428\u5929\u523a\u4e00\u6014\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u662f\u5929\u5c71 \u95e8\u4e0b\uff1f\u201d\u51af\u745b\u7684\u8ffd\u98ce\u5251\u6cd5\u4f55\u7b49\u8fc5\u6377\uff0c\u5237\u5237\u4e24\u5251\uff0c\u9e70\u7fd4\u96bc\u523a\uff0c\u6709\u5982\u72c2\u98ce\u9aa4\u8d77\uff0c\u66b4\u96e8\u7a81\u6765\uff0c\u8428\u5929 \u523a\u5927\u5403\u4e00\u60ca\uff0c\u6012\u9053\uff1a\u201c\u8fd9\u91ce\u4e2b\u5934\u7684\u5251\u6cd5\u8d8a\u53d1\u5389\u5bb3\u4e86\uff01\u201d\u8fde\u95ea\u4e09\u5251\u3002\u8428\u5929\u90fd\u84e6\u5730\u4e00\u58f0\u5927\u559d\uff0c\u5728 \u5947\u5ca9\u602a\u77f3\u4e1b\u4e2d\uff0c\u62d4\u4e0b\u4e00\u6839\u77f3\u7b0b\uff0c\u5f53\u6210\u5175\u5668\uff0c\u7eb5\u8eab\u98de\u8d77\uff0c\u62e6\u8170\u4e00\u62c8\uff0c\u51af\u745b\u77ed\u5251\u4e00\u62ab\uff0c\u88ab\u77f3\u7b0b\u5c16 \u7aef\u78b0\u7740\u950b\u5203\uff0c\u53ea\u89c1\u77f3\u5c51\u7eb7\u98de\uff0c\u706b\u661f\u6e85\u8d77\uff0c\u51af\u745b\u864e\u53e3\u6d41\u8840\uff0c\u5b9d\u5251\u5374\u5e78\u65e0\u635f\uff0c\u8428\u5929\u90fd\u5927\u53eb\u9053\uff1a \u201c\u54e5\u54e5\uff0c\u4f60\u4e0d\u6740\u5979\uff0c\u5979\u4fbf\u6740\u4f60\uff0c\u4e8b\u5230\u5982\u4eca\uff0c\u4f60\u8fd8\u624b\u4e0b\u7559\u60c5\u5417\uff1f\u201d \u3000\u3000\u51af\u745b\u4e0e\u8428\u5929\u90fd\u6362\u4e86\u4e00\u62db\uff0c\u77e5\u4ed6\u529b\u5927\u65e0\u7a77\uff0c\u4e0d\u80fd\u786c\u63a5\u3002\u5251\u8bc0\u4e00\u9886\uff0c\u7a81\u6251\u7a7a\u95e8\uff0c\u8428\u5929\u90fd\u53cd\u624b \u4e00\u626b\uff0c\u53ea\u89c1\u5251\u5149\u6f66\u7ed5\uff0c\u51af\u745b\u5df2\u81ea\u53d8\u62db\u6613\u4f4d\uff0c\u5251\u5c16\u5728\u5de6\u4fa7\u6643\u52a8\uff0c\u8428\u5929\u523a\u56de\u638c\u4e00\u9707\uff0c\u51af\u745b\u53c8\u5230\u4e86 \u53f3\u65b9\uff0c\u4e00\u7f15\u9752\u5149\uff0c\u53c8\u6307\u5411\u4e86\u4ed6\u7684\u53f3\u808b\u8981\u7a74\u3002\u8428\u5929\u90fd\u867d\u7136\u529b\u4f3c\u91d1\u521a\uff0c\u8f7b\u529f\u5374\u900a\u51af\u745b\u8fdc\u751a\uff0c\u7ed9\u5979 \u4e00\u8fde\u4e09\u8bb0\u5feb\u72e0\u4e4b\u62db\uff0c\u903c\u5f97\u624b\u5fd9\u811a\u4e71\u3002\u8428\u5929\u523a\u4ef0\u5929\u4e00\u58f0\u957f\u53f9\uff0c\u5341\u6307\u4e00\u4f38\uff0c\u957f\u7532\u4e00\u5f39\uff0c\u5c55\u5f00\u732b\u9e70 \u6251\u51fb\u4e4b\u6280\uff0c\u987f\u5982\u5de8\u9e70\u76d8\u7a7a\uff0c\u9f99\u86c7\u75be\u8d70\uff0c\u72c2\u98ce\u66b4\u96e8\u822c\u7684\u5411\u51af\u745b\u6495\u6293\uff0c\u89e3\u5f00\u4e86\u8428\u5929\u90fd\u4e4b\u5371\u3002 \u3000\u3000\u4f60\u9053\u53cc\u9b54\u4f55\u4ee5\u4f1a\u5230\u8fd9\u4e2a\u8352\u5c9b\uff1f\u539f\u6765\u96cd\u6b63\u7687\u5e1d\u81ea\u4e86\u56e0\u6b7b\u540e\uff0c\u6025\u601d\u62db\u89c5\u80fd\u4eba\u8865\u4e86\u56e0\u4e4b\u7f3a\u3002\u8fd9 \u6b21\u4ed6\u547d\u5e74\u7fb9\u5c27\u51fa\u5175\u9ec4\u6d77\uff0c\u5ffd\u7136\u60f3\u8d77\u4e86\u53cc\u9b54\u4ee5\u524d\u8bf4\u8fc7\uff0c\u5728\u9ec4\u6d77\u6e24\u6d77\u4ea4\u754c\u4e4b\u5904\uff0c\u6709\u5929\u4e0b\u7684\u7b2c\u4e00\u9b54 \u5c9b\u2014\u2014\u86c7\u5c9b\uff0c\u86c7\u5c9b\u4e0a\u5c45\u4f4f\u6709\u5929\u4e0b\u7b2c\u4e00\u7684\u5947\u4eba\u6bd2\u9f99\u5c0a\u8005\u6765\u3002\u96cd\u6b63\u5fc3\u60f3\uff0c\u53cc\u9b54\u6b66\u529f\u6b8a\u975e\u6cdb\u6cdb\uff0c\u65e2 \u7136\u662f\u4ed6\u4eec\u529b\u8d5e\u4e4b\u4eba\uff0c\u7eb5\u7b97\u8a00\u8fc7\u5176\u5b9e\uff0c\u4ea6\u5f53\u4e0d\u5728\u4e86\u56e0\u4e4b\u4e0b\u3002\u5341\u591a\u5e74\u524d\uff0c\u5f53\u4ed6\u8fd8\u662f\u201c\u56db\u8d1d\u52d2\u201d\u4e4b \u65f6\uff0c\u4ea6\u66fe\u8bf7\u53cc\u9b54\u5230\u86c7\u5c9b\u793c\u8058\uff0c\u5f53\u65f6\u6bd2\u9f99\u5c0a\u8005\u4e0d\u5141\u51fa\u5c71\u3002\u6b64\u65f6\u4ed6\u5df2\u4f4d\u767b\u5927\u5b9d\uff0c\u505a\u4e86\u7687\u5e1d\uff0c\u81ea\u601d \u4ee5\u7687\u5e1d\u4e4b\u5c0a\uff0c\u4f55\u6c42\u4e0d\u5f97\uff0c\u56e0\u6b64\u53c8\u547d\u53cc\u9b54\u524d\u5f80\u3002 \u3000\u3000\u5c82\u77e5\u53cc\u9b54\u56e0\u4e45\u53d7\u51b7\u6de1\uff0c\u53c8\u4e0d\u613f\u5c48\u5c45\u4eba\u4e0b\u3002\u7adf\u7136\u8d77\u4e86\u5f02\u5fc3\u3002\u4ed6\u4eec\u660e\u77e5\u6bd2\u9f99\u5c0a\u8005\u4e0d\u4f1a\u7b54\u5141\uff0c \u4e5f\u613f\u8854\u547d\u524d\u5f80\uff0c\u4e43\u662f\u60f3\u85c9\u6b64\u79bb\u5f00\u96cd\u6b63\uff0c\u91cd\u5f52\u732b\u9e70\u5c9b\u79f0\u9738\u3002\u4ed6\u4eec\u5148\u5230\u86c7\u5c9b\uff0c\u86c7\u5c9b\u4e0a\u6bd2\u9f99\u5c0a\u8005\u6b63 \u5728\u5929\u5929\u548c\u7518\u51e4\u6c60\u4ed6\u4eec\u6bd4\u6b66\uff0c\u6bd2\u9f99\u5c0a\u8005\u4e50\u6b64\u4e0d\u75b2\uff0c\u90a3\u80af\u51fa\u5c71\u3002\u53cc\u9b54\u544a\u8bc9\u6bd2\u9f99\u5c0a\u8005\uff0c\u8bf4\u4ed6\u7684\u5bf9\u5934 \u662f\u6c5f\u5357\u4e03\u4fa0\u4e2d\u6700\u8d1f\u76db\u540d\u7684\u7518\u51e4\u6c60\u3002\u6bd2\u9f99\u5c0a\u8005\u7b11\u9053\uff1a\u201c\u6211\u7ba1\u4ed6\u4ec0\u4e48\u4e03\u4fa0\u516b\u4fa0\uff0c\u6211\u72ec\u5c45\u8352\u5c9b\u5341\u51e0 \u5e74\uff0c\u96be\u5f97\u6709\u61c2\u6b66\u529f\u7684\u4eba\u5230\u6765\uff0c\u6211\u8981\u6d88\u9063\u4ed6\u4eec\u4e00\u5e74\u534a\u8f7d\uff0c\u7136\u540e\u518d\u9a71\u4f7f\u6bd2\u86c7\u54ac\u4ed6\u4eec\uff01\u201d\u6bd2\u9f99\u5c0a\u8005 \u53c8\u8c08\u8d77\u66fe\u628a\u4e00\u4e2a\u7f8e\u8273\u5982\u82b1\u7684\u5c11\u5973\u6454\u5165\u6d77\u4e2d\u3002\u6bd2\u9f99\u5c0a\u8005\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u4eec\u6ca1\u89c1\u7740\u5979\uff0c\u8fd9\u5973\u5a03\u513f\u771f \u9017\u4eba\u7231\uff0c\u6211\u672c\u4e0d\u60f3\u6740\u5979\uff0c\u53ef\u60dc\u5979\u4e0d\u77e5\u8fdb\u9000\u3002\u201d \u3000\u3000\u8428\u5929\u523a\u7ec6\u95ee\u90a3\u5c11\u5973\u5bb9\u8c8c\uff0c\u6599\u60f3\u5b9a\u662f\u51af\u7433\u65e0\u7591\u3002\u8428\u5929\u523a\u867d\u662f\u6740\u4eba\u4e0d\u7728\u773c\u7684\u9b54\u5934\uff0c\u4f46\u5bf9\u51af\u7433 \u5374\u4f3c\u522b\u6709\u7f18\u4efd\uff0c\u52a0\u4f3c\u4ed6\u5e74\u7eaa\u5df2\u8001\uff0c\u65e0\u513f\u65e0\u5973\uff0c\u56e0\u4e4b\u5bf9\u51af\u7433\u66f4\u4e3a\u601d\u5ff5\uff0c\u95fb\u8a00\u5403\u4e86\u4e00\u60ca\uff0c\u5f53\u4e0b\u544a \u522b\u6bd2\u9f99\u5c0a\u8005\uff0c\u548c\u5f1f\u5f1f\u56de\u5230\u732b\u9e70\u5c9b\u540e\uff0c\u4fbf\u9a76\u821f\u51fa\u6d77\uff0c\u5230\u9644\u8fd1\u5404\u5c0f\u5c9b\u627e\u5bfb\uff0c\u5e0c\u671b\u51af\u7433\u672a\u6b7b\u3002\u8c01\u77e5 \u89c1\u4e86\u51af\u745b\u4e4b\u540e\uff0c\u5374\u5f15\u8d77\u4e00\u573a\u5267\u6597\u3002 \u3000\u3000\u8428\u5929\u523a\u89c1\u51af\u745b\u5251\u5251\u8f9b\u8fa3\uff0c\u4f24\u5fc3\u4e0d\u5df2\uff0c\u6697\u9053\uff1a\u201c\u5979\u5df2\u77e5\u6211\u662f\u5979\u5bb6\u7684\u4ec7\u4eba\uff0c\u8fd9\u7ed3\u662f\u4e07\u4e07\u4e0d\u80fd \u89e3\u5f00\u7684\u4e86\u3002\u201d\u51af\u745b\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u5929\u5c71\u5251\u6cd5\uff0c\u7cbe\u5999\u5f02\u5e38\uff0c\u5251\u5251\u6307\u5411\u53cc\u9b54\u8981\u5bb3\uff0c\u8428\u5929\u523a\u5fc3\u5ff5\uff1a \u6b64\u4ec7\u65e2\u4e0d\u53ef\u89e3\uff0c\u6211\u4e0d\u6740\u5979\uff0c\u5979\u5fc5\u6740\u6211\u3002\u4ed6\u672c\u6765\u662f\u4e2a\u6740\u4eba\u4e0d\u7728\u773c\u7684\u9b54\u5934\uff0c\u88ab\u51af\u745b\u5251\u5251\u7d27\u903c\uff0c\u6076 \u5ff5\u987f\u751f\uff0c\u6293\u3001\u70b9\u3001\u52fe\u3001\u6495\uff0c\u7528\u795e\u5bf9\u4ed8\uff0c\u8eab\u6cd5\u638c\u6cd5\uff0c\u4e00\u4f7f\u5f00\u6765\uff0c\u56db\u9762\u516b\u65b9\uff0c\u90fd\u662f\u8eab\u5f71\u3002\u51af\u745b\u5251 \u6cd5\u867d\u7136\u795e\u5999\uff0c\u96be\u654c\u53cc\u9b54\u8fdb\u653b\u3002 \u3000\u3000\u5510\u6653\u6f9c\u542c\u4e86\u53cc\u9b54\u4e4b\u8a00\uff0c\u4e00\u9635\u60ca\u6115\uff0c\u8fd9\u65f6\u89c1\u51af\u745b\u60c5\u52bf\u4e0d\u5999\uff0c\u62d4\u5251\u76f8\u52a9\u3002\u5510\u6653\u6f9c\u6b64\u65f6\u5df2\u5f97\u5929 \u5c71\u5251\u6cd5\u7684\u771f\u4f20\uff0c\u6b66\u529f\u975e\u590d\u5f53\u5e74\u53ef\u6bd4\uff0c\u53cc\u5251\u4e00\u5408\uff0c\u53ea\u89c1\u4e24\u9053\u5251\u5149\uff0c\u76d8\u7a7a\u98de\u821e\uff0c\u500f\u5408\u500f\u5206\uff0c\u4e4d\u8fdb \u4e4d\u9000\uff0c\u653b\u4f3c\u96f7\u9706\uff0c\u5b88\u5982\u5c71\u5cb3\uff0c\u6076\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u8428\u5929\u90fd\u5927\u543c\u4e00\u58f0\uff0c\u98de\u63a0\u6570\u4e08\uff0c\u9c9c\u8840\u6cbe\u8863\uff0c\u53d1 \u58f0\u5618\u53eb\uff0c\u5176\u58f0\u6025\u4fc3\uff0c\u51c4\u5386\u65e0\u4f26\u3002\u539f\u6765\u662f\u80a9\u5934\u4e0a\u4e2d\u4e86\u51af\u745b\u4e00\u5251\u3002 \u3000\u3000\u51af\u745b\u4ece\u672a\u95fb\u8fc7\u6b64\u7b49\u602a\u53eb\u4e4b\u58f0\uff0c\u4e0d\u89c9\u4e00\u9635\u5fc3\u60b8\uff0c\u731b\u7136\u95f4\uff0c\u5934\u9876\u4e0a\u602a\u58f0\u5927\u4f5c\uff0c\u5341\u51e0\u53ea\u732b\u9e70\u53d1 \u51fa\u5431\u5431\u602a\u53eb\u4e4b\u58f0\uff0c\u548c\u8428\u5929\u90fd\u7684\u53eb\u58f0\u547c\u5e94\uff0c\u8fde\u7fe9\u4e0b\u6251\uff0c\u90a3\u732b\u9e70\u7684\u5229\u74dc\uff0c\u5b9e\u4f3c\u94f6\u94a9\uff0c\u51af\u745b\u66fe\u89c1\u8fc7 \u5b83\u4eec\u6293\u88c2\u6bd2\u86c7\uff0c\u4e0d\u89c9\u80c6\u5bd2\u3002\u5e78\u559c\u6e38\u9f99\u65ad\u7389\u4e8c\u5251\uff0c\u4e43\u662f\u6666\u660e\u7985\u5e08\u82e6\u5fc3\u6240\u7ec3\u7684\u5b9d\u5251\uff0c\u53cc\u5251\u5c55\u5f00\uff0c \u5149\u8292\u56db\u5c04\uff0c\u5b9b\u5982\u5728\u5934\u9876\u4e0a\u5e03\u4e86\u4e00\u5c42\u5149\u7f51\u3002\u90a3\u4e9b\u732b\u9e70\u4e5f\u4f3c\u9887\u754f\u5251\u5149\uff0c\u4e0d\u6562\u6cbe\u8fd1\uff0c\u53ea\u662f\u5728\u5934\u9876\u4e0a \u7a7a\uff0c\u76d8\u65cb\u98de\u53eb\uff0c\u60f3\u8d81\u7740\u5251\u5149\u9732\u51fa\u7a7a\u9699\u4e4b\u65f6\uff0c\u624d\u98de\u6251\u4e0b\u6293\uff0c\u4f46\u5510\u51af\u4e8c\u4eba\uff0c\u628a\u5251\u4f7f\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c \u732b\u9e70\u867d\u7136\u5389\u5bb3\uff0c\u5374\u662f\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u72b9\u5e78\u53cc\u9b54\u79bb\u5f00\u732b\u9e70\u5c9b\u5df2\u5341\u591a\u5e74\uff0c\u4ee5\u524d\u7ecf\u4ed6\u4eec\u8bad\u7ec3\u597d\u7684\u732b\u9e70\u5c31\u53ea\u5269\u4e0b\u8fd9\u5341\u591a\u53ea\uff0c\u8981\u4e0d\u7136\u4ed6 \u4eec\u4e07\u96be\u62b5\u654c\u3002 \u3000\u3000\u50f5\u6301\u4e86\u4e00\u9635\uff0c\u51af\u745b\u6e10\u6e10\u5fc3\u5b89\u3002\u8428\u5929\u90fd\u89c1\u732b\u9e70\u4e45\u653b\u4e0d\u4e0b\uff0c\u5fc3\u4e2d\u7126\u8e81\uff0c\u53c8\u53d1\u51fa\u5618\u5618\u7684\u602a\u53eb \u58f0\uff0c\u4f3c\u4e4e\u662f\u6307\u6325\u732b\u9e70\u5f3a\u6251\u3002\u90a3\u4e9b\u732b\u9e70\u679c\u7136\u8d8a\u98de\u8d8a\u4f4e\uff0c\u5229\u722a\u51e0\u4e4e\u89e6\u7740\u5b9d\u5251\u7684\u5149\u8292\uff0c\u51af\u745b\u7a81\u7136\u4e00 \u8dc3\uff0c\u5251\u5149\u63a0\u8d77\uff0c\u77eb\u5982\u6e38\u9f99\uff0c\u628a\u4e24\u53ea\u4f4e\u98de\u7684\u732b\u9e70\u7684\u5229\u722a\u65a9\u65ad\uff0c\u90a3\u7fa4\u732b\u9e70\u867d\u53d7\u4e86\u60ca\uff0c\u89c1\u4e3b\u4eba\u547c \u5524\uff0c\u4ecd\u6251\u4e0b\u6765\u52a9\u6218\u3002 \u3000\u3000\u51af\u745b\u5510\u6653\u6f9c\u9053\u58f0\u82e6\u4e5f\uff0c\u4e0e\u90a3\u5341\u51e0\u53ea\u732b\u9e70\u7f20\u6597\uff0c\u5df2\u662f\u5403\u529b\uff0c\u4f55\u51b5\u53c8\u52a0\u4e0a\u8fd9\u4e24\u4e2a\u9b54\u5934\u3002\u4e24\u4eba \u6253\u4e86\u4e00\u4e2a\u62db\u547c\uff0c\u5408\u5c55\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u628a\u95e8\u6237\u7d27\u7d27\u5c01\u95ed\uff0c\u771f\u5982\u6c5f\u6d77\u51dd\u5149\u3002\u4e24\u4eba\u5728\u5251 \u5149\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u53ea\u5b88\u4e0d\u653b\uff0c\u53c8\u50f5\u5f85\u4e86\u534a\u4e2a\u65f6\u8fb0\u3002\u5e78\u559c\u90a3\u4e9b\u732b\u9e70\u6015\u8bef\u4f24\u4e3b\u4eba\uff0c\u6251\u51fb\u4e4b\u65f6\uff0c\u4e0d\u80fd\u65bd \u5c55\u5168\u529b\uff0c\u4e24\u4eba\u867d\u5904\u4e0b\u98ce\uff0c\u5c1a\u80fd\u652f\u6301\u4e0d\u8d25\u3002 \u3000\u3000\u4e24\u65b9\u82e6\u6597\u4e86\u767e\u62db\uff0c\u8428\u5929\u90fd\u5927\u547c\u5c0f\u53eb\uff0c\u8fde\u756a\u731b\u6251\uff0c\u4f46\u51af\u5510\u4e24\u4eba\uff0c\u5b88\u5f97\u6781\u7a33\uff0c\u53cc\u9b54\u4e0e\u9e70\u7fa4\u7684 \u8054\u5408\u653b\u52bf\uff0c\u867d\u5982\u72c2\u98ce\u9a87\u6d6a\uff0c\u5374\u51b2\u4e0d\u7834\u4ed6\u4eec\u5927\u987b\u5f25\u5251\u5f0f\u6240\u5e03\u6210\u7684\u94c1\u58c1\u94dc\u5899\u3002\u8428\u5929\u90fd\u8017\u529b\u8fc7\u591a\uff0c \u540e\u52b2\u4e0d\u7ee7\uff0c\u4ed6\u80a9\u5934\u4e2d\u4e86\u4e00\u5251\uff0c\u8df3\u8dc3\u4e0d\u7075\uff0c\u5510\u6653\u6f9c\u89d1\u4e2a\u7834\u7efd\uff0c\u5f85\u4ed6\u8fd1\u8eab\u4e4b\u9645\uff0c\u7a81\u7136\u8fc8\u524d\u534a\u6b65\uff0c \u53cd\u624b\u4e00\u5251\uff0c\u53ea\u542c\u5f97\u8428\u5929\u90fd\u53c8\u662f\u4e00\u58f0\u5927\u53eb\uff0c\u5de6\u80a9\u7684\u80a9\u9aa8\u7ed9\u6e38\u9f99\u5251\u7a7f\u8fc7\uff0c\u4f24\u5f97\u6bd4\u524d\u66f4\u91cd\u3002\u8428\u5929\u523a \u633d\u6276\u7740\u4ed6\uff0c\u5e26\u7740\u732b\u9e70\u64a4\u9000\uff0c\u8eb2\u5230\u5c0f\u5c9b\u7684\u4e1c\u8fb9\u517b\u4f24\u3002\u732b\u9e70\u5c31\u5728\u4ed6\u4eec\u8eab\u8fb9\u76d8\u65cb\uff0c\u62c5\u4efb\u8b66\u536b\u3002 \u3000\u3000\u5510\u6653\u6f9c\u5401\u4e86\u53e3\u6c14\uff0c\u5ffd\u89c1\u51af\u745b\u76ee\u4e2d\u8574\u6cea\uff0c\u63d2\u5251\u5f52\u9798\uff0c\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u4e0d\u8be5\u7792\u6211\u3002\u201d \u5510\u6653\u6f9c\u9053\uff1a\u201c\u745b\u59b9\uff0c\u4f60\u522b\u602a\u6211\uff0c\u6211\u53e6\u6709\u82e6\u8877\u3002\u201d\u8fd9\u51e0\u4e2a\u6708\u6765\uff0c\u4e24\u4eba\u671d\u5915\u76f8\u5bf9\uff0c\u5c24\u5176\u5728\u51af\u745b\u8868 \u8fbe\u4e86\u7231\u610f\u4e4b\u540e\uff0c\u5510\u6653\u6f9c\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u5df2\u89e3\u9664\u4e86\u90a3\u79cd\u201c\u53d4\u4f84\u201d\u7684\u62d8\u675f\uff0c\u4e5f\u4e0d\u77e5\u4ece\u4ec0\u4e48\u65f6\u5019 \u8d77\uff0c\u6539\u53e3\u53eb\u5979\u201c\u745b\u59b9\u201d\u4e86\u3002\u53ef\u662f\u51af\u745b\u4e60\u60ef\u5df2\u4e45\uff0c\u4e00\u65f6\u6539\u4e0d\u8f6c\u53e3\uff0c\u4ecd\u7136\u79f0\u4ed6\u201c\u53d4\u53d4\u201d\u3002\u79f0\u547c\u4e0d \u540c\uff0c\u672c\u6765\u53ef\u7b11\uff0c\u4f46\u5728\u8352\u5c9b\u4e4b\u4e2d\uff0c\u66f4\u65e0\u7b2c\u4e09\u8005\u5728\u65c1\uff0c\u4e24\u4eba\u4e5f\u5c31\u542c\u5176\u81ea\u7136\uff0c\u5404\u53eb\u5404\u7684\u3002 \u3000\u3000\u5267\u6218\u591a\u65f6\uff0c\u5929\u8272\u5df2\u66ae\u3002\u5510\u6653\u6f9c\u9053\uff1a\u201c\u54b1\u4eec\u5148\u5f04\u70b9\u4e1c\u897f\u5403\uff0c\u4eca\u665a\u5927\u5bb6\u90fd\u4e0d\u8981\u7761\u4e86\u3002\u5341\u51e0\u5e74 \u6765\u7684\u4e8b\u60c5\uff0c\u4e00\u591c\u4e4b\u95f4\uff0c\u4e5f\u4e0d\u77e5\u80fd\u4e0d\u80fd\u628a\u5b83\u8bf4\u5b8c\u3002\u201d \u3000\u3000\u51af\u745b\u6349\u4e86\u4e24\u5c3e\u9ccd\u9c7c\uff0c\u70e4\u719f\u4e86\u5403\uff0c\u8349\u8349\u5403\u8fc7\uff0c\u66ae\u8272\u5df2\u5408\u3002\u6d77\u98ce\u5439\u6765\uff0c\u9976\u6709\u5bd2\u610f\u3002\u5510\u6653\u6f9c \u9053\uff1a\u201c\u55ef\uff0c\u5929\u8272\u53c8\u53d8\u4e86\u3002\u770b\u8fd9\u5929\u8272\uff0c\u4f3c\u4e4e\u6d77\u4e0a\u53c8\u5728\u8574\u917f\u98ce\u66b4\u3002\u201d\u4e24\u4eba\u5728\u5c0f\u5c9b\u6570\u6708\uff0c\u5bf9\u6d77\u4e0a\u6c14 \u5019\uff0c\u6e10\u6e10\u719f\u6089\u3002\u51af\u745b\u542c\u90a3\u98ce\u58f0\u547c\u5578\uff0c\u63a0\u8fc7\u6d77\u6d0b\uff0c\u6d77\u6d9b\u62cd\u5cb8\uff0c\u6d6a\u82b1\u95ea\u70c1\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u5c06\u8981\u522e\u98ce \u4e86\uff0c\u53ef\u662f\u4eca\u665a\u5927\u53f0\u98ce\u8fd8\u4e0d\u4f1a\u6765\uff0c\u6211\u4eec\u4e0d\u5fc5\u8eb2\u5230\u5ca9\u6d1e\u91cc\u53bb\u3002\u201d\u5510\u6653\u6f9c\u5ffd\u7136\u53f9\u9053\uff1a\u201c\u5929\u8272\u53d8\u5e7b\u6b63 \u5982\u4eba\u4e8b\u65e0\u5e38\u3002\u60f3\u4e0d\u5230\u51e0\u4e2a\u6708\u524d\u6211\u4eec\u8fd8\u5728\u9646\u5730\uff0c\u73b0\u4eca\u5374\u56f0\u5728\u8352\u5c9b\u3002\u66f4\u60f3\u4e0d\u5230\u6211\u4f1a\u548c\u4f60\u5728\u8fd9\u6837\u7684 \u6df1\u591c\uff0c\u76f8\u5bf9\u542c\u6d77\u6d0b\u547c\u5578\u3002\u201d\u773c\u795e\u5947\u7279\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f\u6b22\u559c\u8fd8\u662f\u611f\u4f24\u3002 \u3000\u3000\u51af\u745b\u4f3c\u61c2\u975e\u61c2\uff0c\u6162\u6162\u9760\u8fd1\u5510\u6653\u6f9c\u7684\u8eab\u65c1\uff0c\u62c9\u7740\u4ed6\u7684\u624b\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u8bf4\u3002\u6211\u5f88\u5c0f\u7684\u65f6 \u5019\uff0c\u4f60\u5c31\u8ba4\u8bc6\u6211\u4e48\uff1f\u6211\u7684\u7238\u7238\u5988\u5988\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u4ed6\u4eec\u90fd\u548c\u4f60\u5f88\u8981\u597d\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u55ef\uff0c \u4f60\u5468\u5c81\u4e4b\u524d\uff0c\u6211\u51e0\u4e4e\u5929\u5929\u62b1\u4f60\u3002\u4f60\u7684\u7238\u7238\u5988\u5988\u662f\u6211\u7684\u5e08\u54e5\u5e08\u5ac2\u3002\u4f60\u522b\u5fc3\u7126\uff0c\u4f60\u522b\u9707\u6296\uff0c\u4f60\u5b9a \u4e00\u5b9a\u795e\uff0c\u4f60\u542c\u6211\u8bf4\u5440\uff01\u662f\u7684\uff0c\u4f60\u7684\u7237\u7237\u548c\u7236\u4eb2\u90fd\u662f\u60e8\u6b7b\u7684\uff0c\u4f60\u7684\u5988\u5988\uff0c\u751f\u6b7b\u5374\u8fd8\u672a\u77e5\u3002\u50bb\u5b69 \u5b50\uff0c\u54ed\u4ec0\u4e48\u5440\uff1f\u4f60\u8981\u62a5\u4ec7\u3002\u597d\u5427\uff0c\u54ed\u5c31\u54ed\u5427\uff0c\u54ed\u4e86\u4f1a\u8212\u7545\u4e00\u70b9\u3002\u8fd9\u5341\u51e0\u5e74\u6765\uff0c\u6211\u60f3\u8d77\u4f60\u4eec\u7684 \u4e00\u5bb6\u548c\u6211\u81ea\u5df1\u7684\u65f6\u5019\uff0c\u6211\u4e5f\u6709\u65f6\u54ed\u7684\u3002\u201d\u8bf4\u7740\uff0c\u8bf4\u7740\uff0c\u5510\u6653\u6f9c\u4e5f\u6ef4\u51fa\u773c\u6cea\u6765\u4e86\u3002 \u3000\u3000\u51af\u745b\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u8bf4\uff0c\u4f60\u8bf4\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff0c\u6211\u542c\u4e0d\u6e05\u695a\u3002\u597d\uff0c\u6b47\u4e00\u4f1a\u513f\uff0c\u54b1\u4eec\u90fd \u522b\u54ed\u4e86\u3002\u4f60\u8bf4\u7ed9\u6211\u542c\u3002\u6211\u542c\u4f60\u7684\u8bdd\u3002\u5e08\u5085\u4e5f\u6559\u8fc7\u6211\uff0c\u53eb\u6211\u8981\u50cf\u4e2a\u5973\u4e2d\u4e08\u592b\uff0c\u5b81\u53ef\u6d41\u8840\uff0c\u4e0d\u53ef \u6d41\u6cea\u7684\uff0c\u6211\u73b0\u5728\u4e0d\u54ed\u4e86\uff0c\u53d4\u53d4\uff0c\u4f60\u8bf4\u5427\u3002\u201d\u9ed1\u591c\u4e2d\uff0c\u51af\u745b\u53cc\u773c\u95ea\u7740\u6cea\u5149\uff0c\u4f3c\u91d1\u94a2\u77f3\u822c\u7684\u653e\u7740 \u5149\u8292\u3002\u51dd\u89c6\u7740\u5979\u7684\u201c\u53d4\u53d4\u201d\u3002 \u3000\u3000\u5510\u6653\u6f9c\u63a5\u89e6\u7740\u5979\u7684\u76ee\u5149\uff0c\u6025\u907f\u5f00\u53bb\uff0c\u5fc3\u5934\u8d5e\u53f9\u9053\uff1a\u201c\u771f\u662f\u4e2a\u597d\u6837\u7684\u59d1\u5a18\u554a\uff01\u53c8\u82e6\u547d\u53c8\u5014 \u5f3a\u7684\u59d1\u5a18\u554a\uff01\u201d\u63a5\u7740\u51af\u745b\u98a4\u6296\u7684\u58f0\u97f3\u4e4b\u540e\uff0c\u5510\u6653\u6f9c\u628a\u5979\u7684\u624b\u63e1\u5f97\u66f4\u7d27\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u542c\u6211 \u8bf4\u3002\u90a3\u662f\u5341\u516d\u5e74\u524d\u7684\u4e00\u4e2a\u591c\u665a\uff0c\u8fd8\u6709\u4e94\u5929\u4fbf\u662f\u4e2d\u79cb\u4f73\u8282\u3002\u90a3\u4e00\u5929\u6b63\u662f\u4f60\u4eec\u59d0\u59b9\u7684\u5468\u5c81\u2026\u2026\u201d \u51af\u745b\u53eb\u8d77\u6765\u9053\uff1a\u201c\u55ef\uff0c\u6211\u8fd8\u6709\u4e00\u4e2a\u59d0\u59b9\uff0c\u5c31\u662f\u90a3\u4e2a\u6837\u8c8c\u548c\u6211\u6781\u4e3a\u76f8\u4f3c\u4eba\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a \u201c\u662f\u7684\u3002\u4f60\u522b\u6253\u5c94\uff0c\u4f60\u542c\u6211\u8bf4\uff0c\u90a3\u4e00\u5929\u662f\u4f60\u4eec\u59d0\u59b9\u7684\u5468\u5c81\uff0c\u4f60\u7684\u7237\u7237\u548c\u7238\u5988\u90fd\u975e\u5e38\u6b22\u559c\uff0c\u7a81 \u7136\u6765\u4e86\u4e00\u4e2a\u5947\u602a\u7684\u5ba2\u4eba\u2026\u2026\u201d \u3000\u3000\u98ce\u5728\u547c\u53f7\uff0c\u6d77\u5728\u53eb\u5524\uff0c\u661f\u661f\u6e10\u6e10\u897f\u79fb\uff0c\u51af\u745b\u5728\u51dd\u795e\u542c\u7740\u5510\u6653\u6f9c\u7684\u8bf4\u8bdd\u3002\u5510\u6653\u6f9c\u6709\u65f6\u8bf4\u5f97 \u5f88\u6162\uff0c\u6709\u65f6\u8bf4\u5f97\u5f88\u5feb\u3002\u8bf4\u4e86\u4ed6\u4eec\u4e00\u5bb6\u7684\u60b2\u5267\uff0c\u4e5f\u8bf4\u5230\u4e86\u81ea\u5df1\u7684\u8eab\u4e16\u3002\u8bf4\u5230\u4f24\u5fc3\u4e4b\u5904\uff0c\u6709\u65f6\u5c31 \u505c\u987f\u7740\u8bf4\u4e0d\u4e0b\u53bb\uff0c\u6162\u6162\u63e9\u5e72\u4e86\u773c\u6cea\uff0c\u53c8\u518d\u7eed\u8bf4\u3002\u8bf4\u5440\u8bf4\u7684\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u597d\u8270\u96be\u7684 \u7ec8\u4e8e\u8bf4\u5b8c\u4e86\u3002 \u3000\u3000\u201c\u597d\u82e6\u547d\u7684\u7237\u7237\u548c\u7238\u5988\u554a\uff01\u554a\uff0c\u53d4\u53d4\uff0c\u4f60\u4e5f\u597d\u82e6\u547d\u554a\uff01\u201d\u51af\u745b\u53eb\u9053\u3002\u5979\u7d27\u7d27\u501a\u504e\u7740\u5510\u6653 \u6f9c\uff0c\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6211\u603b\u89c9\u5f97\u4f60\u662f\u6211\u7684\u4eb2\u4eba\uff0c\u539f\u6765\u6211\u4eec\u771f\u662f\u8fd9\u6837\u4eb2\u5207\u3002\u201d\u5510\u6653\u6f9c\u8f7b\u8f7b\u7528\u8863\u8896\u66ff \u5979\u62b9\u4e86\u773c\u6cea\uff0c\u51af\u745b\u4fbf\u54bd\u9053\uff1a\u201c\u6211\u8981\u627e\u6211\u7684\u5988\u5988\uff0c\u6211\u8981\u627e\u6211\u7684\u59b9\u59b9\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u662f\u554a\uff0c\u4f60 \u662f\u8be5\u627e\u5979\u4eec\u4e86\uff0c\u8fd9\u5c0f\u5c9b\u867d\u7136\u8352\u50fb\uff0c\u603b\u4f1a\u6709\u8239\u7ecf\u8fc7\uff0c\u4f60\u4e00\u5b9a\u80fd\u56de\u5230\u9646\u5730\uff0c\u627e\u7740\u5979\u4eec\u7684\u3002\u745b\u59b9\uff0c \u4f60\u6027\u60c5\u521a\u70c8\uff0c\u5c06\u6765\u72ec\u8d70\u6c5f\u6e56\uff0c\u53ef\u5f97\u8981\u81ea\u5df1\u5f53\u5fc3\u554a\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u4e0d\u548c\u6211\u4e00\u540c\u56de\u53bb \u4e48\uff1f\u201d\u5510\u6653\u6f9c\u82e6\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u80fd\u56de\u53bb\u4e48\uff1f\u201d\u51af\u745b\u4e00\u60f3\u8d77\u5510\u6653\u6f9c\u6b7b\u671f\u4e0d\u8fdc\uff0c\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u53ef\u65ad \u7edd\uff0c\u7a81\u7136\u75db\u54ed\u5931\u58f0\uff0c\u63fd\u7740\u5510\u6653\u6f9c\u7684\u80a9\u5934\uff0c\u4f0f\u5728\u4ed6\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u8f6f\u7389\u6e29\u9999\uff0c\u54c0\u4e50\u4ea4\u7ec7\uff0c\u5510\u6653\u6f9c\u53ea\u89c9\u4e00\u9635\u8ff7\u832b\uff0c\u63a8\u5f00\u5979\u53c8\u4e0d\u662f\uff0c\u4e0d\u63a8\u5f00\u5979\u53c8\u4e0d\u662f\uff0c\u9762\u7ea2\u8033 \u70ed\uff0c\u8eab\u5fc3\u9707\u64bc\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u53ea\u542c\u5f97\u51af\u745b\u62bd\u564e\u8bf4\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u8fd9\u5341\u51e0\u5e74\u6765\u4f60\u4e3a\u6211\u64cd\u5fc3\uff0c\u4e3a \u6211\u5954\u8dd1\uff0c\u5728\u832b\u832b\u7684\u4eba\u6d77\u91cc\uff0c\u4f60\u5bfb\u89c5\u6211\u4eec\u59d0\u59b9\uff0c\u751a\u81f3\u820d\u51fa\u6027\u547d\u6551\u6211\u51fa\u6765\uff0c\u8fd9\u6069\u60c5\u6211\u8be5\u600e\u6837\u62a5\u7b54 \u4f60\u5462\uff1f\u201d\u5510\u6653\u6f9c\u8f7b\u8f7b\u63a8\u5f00\u5979\u7684\u8eab\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u50bb\u5b69\u5b50\uff0c\u4f60\u6211\u4e4b\u95f4\uff0c\u4e5f\u7528\u5f97\u7740\u2018\u62a5\u7b54\u2019\u8fd9\u4e24\u4e2a \u5b57\u4e48\uff1f\u201d\u51af\u745b\u4ef0\u7740\u8138\u5e9e\uff0c\u75f4\u75f4\u7684\u671b\u7740\u5510\u6653\u6f9c\uff0c\u5ffd\u7136\u8bf4\u5230\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u6b22\u559c\u6211\u5417\uff1f\u201d\u5510\u6653\u6f9c\u5fc3 \u5934\u4e00\u9707\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u4ed6\u4eec\u867d\u7136\u6709\u7231\u610f\uff0c\u53ef\u662f\u4ee5\u5f80\u90fd\u662f\u8bf4\u5f97\u975e\u5e38\u542b\u84c4\uff0c\u5f7c\u6b64\u53ea\u662f\u5fc3\u7167\u4e0d \u5ba3\uff0c\u5510\u6653\u6f9c\u5c24\u5176\u907f\u514d\u660e\u8bf4\u51fa\u6765\uff0c\u9664\u4e86\u4e16\u4fd7\u4e4b\u89c1\u5bf9\u4ed6\u5fc3\u7075\u7684\u675f\u7f1a\u4e4b\u5916\uff0c\u4ed6\u4e5f\u4e0d\u613f\u5728\u4e34\u6b7b\u4e4b\u524d\uff0c \u5728\u4e00\u4e2a\u5c11\u5973\u5fc3\u4e0a\u6295\u4e0b\u9634\u5f71\u3002 \u3000\u3000\u51af\u745b\u4ef0\u9762\u53c8\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u4e0d\u6b22\u559c\u6211\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u96be\u9053\u4f60\u81ea\u5df1\u4e0d\u77e5\u9053 \u5417\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u6211\u8981\u6c38\u8fdc\u548c\u4f60\u5728\u4e00\u8d77\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5b69\u5b50\u6c14\u7684\u7cca\u6d82\u8bdd\u3002\u201d\u51af\u745b \u9053\uff1a\u201c\u8c01\u8bf4\u8fd9\u662f\u5b69\u5b50\u6c14\u7684\u7cca\u6d82\u8bdd\uff1f\u6d77\u67af\u77f3\u70c2\u5c1a\u4e0d\u53ef\u79fb\uff0c\u751f\u79bb\u6b7b\u522b\u53c8\u7109\u80fd\u963b\u9694\uff1f\u201d\u5510\u6653\u6f9c\u5fc3\u5f26 \u98a4\u6296\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u548c\u5979\u76f8\u62e5\u5728\u4e00\u8d77\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u5982\u75f4\u4f3c\u9189\uff0c\u4e5f\u4e0d\u77e5\u76f8\u62e5\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5ffd\u95fb\u5f97\u5934\u9876\u4e0a\u7a7a\u201c\u560e\u560e\u560e\u3001\u5431\u5431\u5431\u201d\u7684\u4e00\u7247\u566a \u97f3\uff0c\u51af\u745b\u9053\uff1a\u201c\u771f\u8ba8\u538c\uff0c\u90a3\u4e9b\u732b\u9e70\u53c8\u6765\u4e86\u3002\u201d\u5510\u6653\u6f9c\u62ac\u5934\u4e00\u770b\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u597d\u4e86\uff0c\u706b\uff0c\u706b\uff01\u201d \u3000\u3000\u51af\u745b\u6b63\u5782\u9996\u95ed\u76ee\uff0c\u5728\u5510\u6653\u6f9c\u7684\u6000\u91cc\uff0c\u9676\u9189\u5728\u5c11\u5973\u521d\u604b\u4e4b\u4e2d\uff0c\u5ffd\u88ab\u63a8\u5f00\uff0c\u7741\u5f00\u773c\u76ae\u4e00\u770b\uff0c \u53ea\u89c1\u56db\u9762\u7684\u6811\u6797\u90fd\u5c04\u51fa\u706b\u5149\u3002\u5510\u6653\u6f9c\u9053\uff1a\u201c\u8fd9\u4e00\u5b9a\u662f\u53cc\u9b54\u5e72\u7684\u597d\u4e8b\u3002\u5feb\u628a\u4ed6\u4eec\u622a\u4f4f\uff0c\u62a2\u4ed6\u4eec \u7684\u5c0f\u8239\uff01\u201d\u4e24\u4eba\u63d0\u5251\u5f80\u4e1c\u9762\u6811\u6797\u4e00\u95ef\uff0c\u6797\u4e2d\u4f20\u51fa\u4e86\u8428\u5929\u90fd\u54c8\u54c8\u5927\u7b11\u4e4b\u58f0\u3002 \u3000\u3000\u539f\u6765\u8428\u5929\u90fd\u5403\u4e86\u51af\u745b\u4e00\u5251\u6740\u673a\u9661\u8d77\uff0c\u89c1\u6d77\u4e0a\u98ce\u8d77\uff0c\u751f\u4e86\u4e00\u8ba1\uff0c\u4ed6\u4eec\u7cbe\u901a\u6c34\u6027\uff0c\u5f85\u653e\u706b\u4e4b \u540e\uff0c\u5c31\u626c\u5e06\u9003\u8d70\uff0c\u8ba9\u5510\u51af\u57cb\u846c\u5728\u706b\u6d77\u4e4b\u4e2d\u3002\u8428\u5929\u523a\u672c\u6765\u6709\u70b9\u4e0d\u5fcd\uff0c\u4f46\u89c1\u6b64\u4ec7\u65e2\u4e0d\u53ef\u89e3\uff0c\u4e5f\u53ea \u597d\u7531\u4ed6\u3002\u4e24\u5144\u5f1f\u5728\u56db\u8fb9\u70b9\u8d77\u706b\u5934\uff0c\u5510\u6653\u6f9c\u4e0e\u51af\u745b\u5df2\u63d0\u5251\u95ef\u5230\u3002 \u3000\u3000\u706b\u5149\u4e2d\u6620\u51fa\u51af\u745b\u7ea2\u6edf\u6edf\u7684\u8138\u5b54\uff0c\u8428\u5929\u523a\u53eb\u9053\uff1a\u201c\u7433\u4e2b\u5934\uff0c\u4f60\u968f\u6211\u4eec\u8d70\u5427\uff01\u201d\u51af\u745b\u6068\u6781\uff0c \u4e00\u5251\u6eaf\u53bb\uff0c\u8428\u5929\u523a\u95ea\u8eab\u6025\u907f\uff0c\u51af\u745b\u632f\u5251\u75be\u523a\uff0c\u52bf\u5982\u62bd\u4e1d\uff0c\u7ef5\u7ef5\u4e0d\u65ad\u3002\u8428\u5929\u90fd\u53eb\u9053\uff1a\u201c\u54e5\u54e5\uff0c \u5feb\u8d70\uff01\u201d\u53ef\u662f\u51af\u745b\u7684\u8f7b\u529f\u4e0d\u5728\u8428\u5929\u523a\u4e4b\u4e0b\uff0c\u5251\u6cd5\u7d27\u6781\uff0c\u8428\u5929\u523a\u4e00\u65f6\u4e4b\u95f4\u54ea\u8131\u5f97\u51fa\u8eab\u3002 \u3000\u3000\u8428\u5929\u90fd\u5fc3\u4e2d\u7126\u8e81\uff0c\u9661\u7136\u5927\u559d\u4e00\u58f0\uff0c\u6298\u4e86\u4e00\u679d\u70e7\u7740\u7684\u6811\u679d\uff0c\u5288\u9762\u5411\u51af\u745b\u63b7\u53bb\uff0c\u5510\u6653\u6f9c\u98de\u8eab \u7eb5\u8d77\uff0c\u957f\u5251\u4e00\u62d4\uff0c\u628a\u5e26\u706b\u7684\u6811\u679d\u64a9\u5f00\uff0c\u633d\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u51cc\u7a7a\u4e0b\u523a\uff0c\u8428\u5929\u90fd\u5de6\u638c\u4e00\u63a8\uff0c\u52b2\u98ce\u8d2f \u80f8\uff0c\u53f3\u638c\u4e00\u52fe\uff0c\u4fbf\u65bd\u5c55\u64d2\u62ff\u624b\u6cd5\u6765\u626d\u5510\u6653\u6f9c\u7684\u81c2\u5f2f\u5173\u8282\uff0c\u8428\u5929\u90fd\u53f7\u79f0\u201c\u5927\u529b\u795e\u9b54\u201d\u94c1\u9aa8\u94dc \u76ae\uff0c\u5728\u53d7\u4f24\u4e4b\u540e\uff0c\u4ecd\u7136\u52c7\u731b\u975e\u5e38\uff0c\u9510\u4e0d\u53ef\u5f53\uff01 \u3000\u3000\u5510\u6653\u6f9c\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u907f\u8fc7\u51f6\u950b\uff0c\u624b\u8d77\u5904\uff0c\u5251\u5149\u66b4\u957f\uff0c\u201c\u91d1\u95e8\u9f13\u6d6a\u201d\u3001\u201c\u767d\u8679\u8d2f\u65e5\u201d\u3001 \u201c\u98de\u6e21\u9634\u5c71\u201d\uff0c\u4e00\u8fde\u51e0\u8bb0\u8ffd\u98ce\u5251\u6cd5\u7684\u7edd\u62db\uff0c\u6709\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6eda\u6eda\u800c\u4e0a\u3002\u8428\u5929\u523a\u6015\u4ed6\u7684\u6e38\u9f99\u5b9d \u5251\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u8eab\u624b\u4e0d\u5982\u4ed6\u77eb\u6377\uff0c\u9976\u662f\u5916\u5bb6\u529f\u592b\u5df2\u7ec3\u5f97\u767b\u5cf0\u9020\u6781\uff0c\u4e5f\u53ea\u5f97\u6b65\u6b65\u540e\u9000\u3002 \u3000\u3000\u8fd9\u5c0f\u5c9b\u65b9\u5706\u4ec5\u662f\u6570\u91cc\uff0c\u5730\u65b9\u751a\u5c0f\uff0c\u6d77\u9762\u7684\u5927\u98ce\u6025\u52b2\u5439\u6765\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u5df2\u6210\u71ce\u539f\u706b\u52bf\u3002\u6811 \u6728\u88ab\u70e7\u5f97\u567c\u556a\u4f5c\u54cd\uff0c\u6d53\u70df\u545b\u5589\uff0c\u773c\u775b\u88ab\u70df\u6240\u523a\uff0c\u51e0\u4e4e\u7741\u4e0d\u5f00\u6765\u3002\u5510\u6653\u6f9c\u5411\u51af\u745b\u6253\u4e86\u4e00\u4e2a\u62db \u547c\uff0c\u593a\u8def\u5954\u5411\u6d77\u8fb9\u3002 \u3000\u3000\u8428\u5929\u90fd\u53d1\u4e00\u58f0\u558a\uff0c\u64ae\u5507\u4e00\u5578\uff0c\u602a\u58f0\u9661\u8d77\uff0c\u90a3\u4e9b\u732b\u9e70\u53c8\u51b2\u4e0b\u6765\uff0c\u5510\u6653\u6f9c\u4e0e\u51af\u745b\u82e5\u907f\u732b\u9e70\uff0c \u52bf\u4fbf\u4e0d\u80fd\u593a\u8def\u3002\u4ed6\u4eec\u4e24\u4eba\u867d\u901a\u6c34\u6027\uff0c\u5374\u4e0d\u751a\u7cbe\uff0c\u82e5\u7136\u593a\u4e0d\u5230\u5c0f\u8239\uff0c\u5fc5\u5b9a\u70e7\u6b7b\u3002 \u3000\u3000\u8428\u5929\u90fd\u53c8\u662f\u4e00\u9635\u54c8\u54c8\u5927\u7b11\uff0c\u4e00\u9762\u6307\u6325\u732b\u9e70\u7ea0\u7f20\u654c\u4eba\uff0c\u4e00\u9762\u593a\u8def\u5954\u8dd1\u3002\u51af\u745b\u6c14\u7ea2\u4e86\u773c\uff0c\u4e00 \u58f0\u53f1\u54a4\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u98de\u7eb5\u8d77\u6765\uff0c\u4ece\u4e00\u4e1b\u201c\u706b\u6811\u201d\u65c1\u8fb9\u98de\u7a9c\u800c\u8fc7\u3002\u90a3\u4e9b\u732b\u9e70\u754f\u4ff1\u706b\u52bf\u5f3a\u70c8\uff0c\u4e0d \u6562\u6251\u4e0b\uff0c\u51af\u745b\u4f55\u7b49\u5feb\u6377\uff0c\u5f97\u6b64\u7a7a\u9699\uff0c\u4e00\u4e0b\u5b50\u4fbf\u5230\u4e86\u8428\u5929\u90fd\u8eab\u540e\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u5feb\u5982\u7535\u63a3\uff0c\u8428\u5929 \u90fd\u60e8\u53eb\u4e00\u58f0\uff0c\u80cc\u540e\u5fc3\u53c8\u4e2d\u4e86\u4e00\u5251\uff01 \u3000\u3000\u8428\u5929\u523a\u6012\u53eb\u9053\uff1a\u201c\u7433\u4e2b\u5934\uff0c\u4f60\u597d\u72e0\uff01\u201d\u4f7f\u51fa\u732b\u9e70\u6251\u51fb\u7684\u51cc\u7a7a\u7edd\u6280\uff0c\u9661\u7136\u8dc3\u8d77\u4e09\u4e08\u591a\u9ad8\uff0c \u4f38\u51fa\u5341\u6307\u957f\u7532\uff0c\u515c\u5934\u6293\u4e0b\uff01\u51af\u745b\u98de\u8eab\u8dc3\u8d77\uff0c\u5251\u52bf\u4e00\u8361\uff0c\u6a2a\u7a7a\u4fbf\u524a\uff0c\u90a3\u77e5\u8428\u5929\u523a\u8eab\u5b50\u60ac\u7a7a\uff0c\u4ecd \u53ef\u5c48\u4f38\u5982\u610f\uff0c\u4e00\u4e2a\u56de\u65cb\uff0c\u5341\u6307\u53c8\u63d2\uff0c\u51af\u745b\u731d\u4e0d\u53ca\u9632\uff0c\u80a9\u5934\u7ed9\u4ed6\u6307\u7532\u78b0\u7740\uff0c\u5e78\u4ed7\u8f7b\u529f\u8d85\u5353\uff0c\u5f3a \u529b\u4e00\u626d\uff0c\u907f\u4e86\u81f4\u547d\u4e4b\u4f24\uff0c\u98de\u8eab\u5815\u5730\uff0c\u732b\u9e70\u53c8\u8ddf\u8e2a\u6251\u6765\u3002 \u3000\u3000\u8428\u5929\u90fd\u80cc\u5fc3\u4e2d\u4e86\u4e00\u5251\uff0c\u82e5\u662f\u5e38\u4eba\uff0c\u5fc5\u6b7b\u65e0\u51dd\uff0c\u4f46\u4ed6\u94dc\u76ae\u94c1\u9aa8\uff0c\u867d\u88ab\u5b9d\u5251\u63d2\u4f24\uff0c\u72c2\u53eb\u51e0 \u58f0\uff0c\u5c45\u7136\u53c8\u8dc3\u8d77\u5e94\u654c\u3002\u5510\u6653\u6f9c\u89c1\u4e86\uff0c\u4e5f\u4e0d\u7981\u5fc3\u60ca\u3002\u8fd9\u65f6\u706b\u52bf\u8d8a\u6765\u8d8a\u5927\u4e86\uff01 \u3000\u3000\u8428\u5929\u523a\u53eb\u9053\uff1a\u201c\u5f1f\u5f1f\uff0c\u4e0d\u8981\u86ee\u6253\uff0c\u8ddf\u6211\u51fa\u6765\uff01\u201d\u62e9\u706b\u52bf\u5c0f\u7684\u5730\u65b9\u6025\u95ef\u3002\u9760\u7740\u732b\u9e70\u536b\u62a4\uff0c \u5c45\u7136\u51b2\u51fa\u4e86\u6570\u5341\u4e08\u5730\uff0c\u9065\u9065\u671b\u89c1\u6d77\u8fb9\u3002\u53ea\u89c1\u72c2\u98ce\u6012\u53f7\uff0c\u6d77\u6d6a\u6ed4\u5929\uff0c\u731b\u542c\u5f97\u8f70\u5566\u4e00\u58f0\u5de8\u54cd\uff0c\u90a3 \u6cca\u5728\u5cb8\u8fb9\u7684\u5c0f\u821f\uff0c\u7cfb\u8239\u7684\u7ef3\u7f06\u88ab\u98ce\u522e\u65ad\uff0c\u7ed9\u5de8\u6d6a\u4e00\u51b2\uff0c\u629b\u4e0a\u5cb8\u6765\uff0c\u649e\u7740\u5de8\u77f3\uff0c\u987f\u6210\u7c89\u788e\uff01 \u3000\u3000\u5510\u6653\u6f9c\u4e00\u9635\u5fc3\u51c9\uff0c\u4ee5\u4ed6\u4eec\u7684\u6c34\u6027\uff0c\u7edd\u4e0d\u80fd\u5728\u65e0\u6240\u51ed\u4f9d\u7684\u60c5\u51b5\u4e4b\u4e0b\u6e38\u51fa\u6d77\u53bb\u3002\u51af\u745b\u53eb\u9053\uff1a \u201c\u5510\u53d4\u53d4\uff0c\u6211\u4eec\u4e0d\u80fd\u540c\u5e74\u540c\u6708\u540c\u65e5\u751f\uff0c\u5374\u53ef\u540c\u5e74\u540c\u6708\u540c\u65e5\u6b7b\u4e86\uff0c\u6211\u4eec\u7eb5\u6b7b\uff0c\u4e5f\u4e0d\u80fd\u53eb\u8fd9\u4e24\u4e2a \u8001\u8d3c\u9003\u751f\uff01\u201d\u4e0d\u7406\u732b\u9e70\u6251\u51fb\uff0c\u5c06\u5b9d\u5251\u821e\u8d77\u4e00\u5708\u94f6\u8679\uff0c\u500f\u5ffd\u8ffd\u8fc7\u4e86\u5934\uff0c\u56de\u8eab\u622a\u51fb\uff01\u5510\u6653\u6f9c\u4e5f\u8ffd \u5230\u53cc\u9b54\u8eab\u540e\uff0c\u524d\u540e\u5939\u653b\u3002\u8fd9\u65f6\u706b\u52bf\u66f4\u5927\uff0c\u5c0f\u5c9b\u4e0a\u7684\u6811\u6728\u682a\u682a\u7740\u706b\uff0c\u6709\u4e9b\u5c0f\u6811\uff0c\u7740\u706b\u4e4b\u540e\uff0c\u88ab \u72c2\u98ce\u4e00\u522e\uff0c\u6574\u682a\u98de\u8d77\uff0c\u597d\u50cf\u706b\u9f99\u4e00\u822c\uff0c\u63a0\u8fc7\u5934\u9876\uff0c\u66f4\u52a0\u4e0a\u98ce\u58f0\u6d6a\u58f0\u5927\u706b\u70e7\u88c2\u6811\u6728\u4e4b\u58f0\u96c6\u6210\u4e00 \u7247\uff0c\u5c31\u597d\u50cf\u6b7b\u4ea1\u7684\u4ea4\u54cd\u4e50\u3002\u5c0f\u5c9b\u4e0a\u6816\u606f\u7684\u6d77\u9e1f\uff0c\u5168\u90e8\u7ed9\u706b\u52bf\u60ca\u5f97\u632f\u7fc5\u4e71\u98de\uff0c\u5b81\u53ef\u98de\u5230\u6d77\u9762\u53bb \u53d7\u72c2\u98ce\u5439\u6253\u3002\u90a3\u4e9b\u732b\u9e70\u867d\u7136\u51f6\u72e0\uff0c\u88ab\u70df\u706b\u6240\u718f\uff0c\u4e5f\u4e0d\u6562\u98de\u4e0b\u6765\u4e86\uff0c\u201c\u560e\u560e\u560e\u201d\u7684\u72c2\u53eb\u4e00\u9635\uff0c \u7eb7\u7eb7\u98de\u5f00\u3002\u5c0f\u5c9b\u4e0a\u53ea\u5269\u4e0b\u56db\u4e2a\u4eba\u5728\u62fc\u547d\u53ae\u6740\uff01 \u3000\u3000\u53cc\u9b54\u6043\u7740\u7cbe\u901a\u6c34\u6027\uff0c\u8fd8\u60f3\u593a\u8def\u5954\u9003\uff0c\u53ef\u662f\u51af\u5510\u4e8c\u4eba\u4ee5\u6b7b\u76f8\u62fc\uff0c\u5251\u5251\u8f9b\u8fa3\uff0c\u90a3\u80fd\u5bb9\u4ed6\u9003 \u8131\uff1f\u8428\u5929\u523a\u6025\u7ea2\u4e86\u773c\uff0c\u5c55\u51fa\u5e73\u751f\u7edd\u6280\uff0c\u6293\u3001\u70b9\u3001\u52fe\uff0c\u6495\uff0c\u72e0\u653b\u72e0\u6251\uff0c\u65f6\u800c\u51cc\u7a7a\u4e0b\u51fb\uff0c\u65f6\u800c\u8d34 \u5730\u64d2\u62ff\uff0c\u6027\u547d\u547c\u5438\uff0c\u6b7b\u751f\u4fc4\u503e\uff0c\u5927\u5bb6\u6240\u60f3\u7684\u90fd\u662f\u600e\u6837\u51fb\u5012\u5bf9\u65b9\uff0c\u5bfb\u6c42\u751f\u8def\uff0c\u4e00\u5207\u7231\u50e7\u6069\u6028\uff0c \u90fd\u6401\u5728\u8111\u540e\u3002 \u3000\u3000\u4e24\u65b9\u5c31\u5728\u706b\u5149\u7684\u7a7a\u9699\u4e2d\u820d\u547d\u6076\u6597\u3002\u5e26\u7740\u706b\u7130\u7684\u679d\u53f6\uff0c\u65f6\u4e0d\u65f6\u98de\u5760\u4e0b\u6765\u3002\u8428\u5929\u90fd\u8fde\u4e2d\u4e09\u5904 \u5251\u4f24\uff0c\u8df3\u8dc3\u4e0d\u7075\uff0c\u51af\u745b\u4e5f\u89c9\u80a9\u5934\u6709\u70b9\u9ebb\u75db\uff0c\u6599\u60f3\u662f\u88ab\u6bd2\u722a\u6293\u4f24\u4e4b\u5904\u5df2\u7ecf\u53d1\u4f5c\uff0c\u5b58\u7740\u5fc5\u6b7b\u4e4b \u5fc3\uff0c\u653b\u5f97\u66f4\u72e0\u3002\u8428\u5929\u90fd\u72c2\u547c\u66b4\u53eb\uff0c\u62f3\u6253\u811a\u8e22\uff0c\u5168\u5e26\u52b2\u98ce\uff0c\u628a\u70df\u7130\u717d\u5411\u51af\u745b\u8fd9\u8fb9\u3002\u51af\u745b\u5fcd\u7740\u773c \u775b\u75bc\u75db\uff0c\u9aa4\u7136\u7a7f\u8fc7\u70df\u96fe\uff0c\u5530\u7684\u4e00\u5251\uff0c\u53c8\u5728\u8428\u5929\u90fd\u80f8\u819b\u5f00\u4e86\u4e00\u9053\u53e3\u5b50\uff0c\u8428\u5929\u523a\u98de\u8eab\u6765\u6551\uff0c\u51af\u745b \u53cd\u624b\u4e00\u5251\uff0c\u559d\u58f0\u201c\u7740\uff01\u201d\u8428\u5929\u523a\u88ab\u70df\u96fe\u906e\u773c\uff0c\u9aa4\u89c1\u5251\u5149\u95ea\u8000\uff0c\u6025\u95ea\u907f\u65f6\uff0c\u5341\u6307\u957f\u7532\u5168\u88ab\u524a \u65ad\u3002\u53cc\u9b54\u54c7\u54c7\u6012\u53eb\uff0c\u5ffd\u542c\u5f97\u8fc5\u96f7\u7a81\u53d1\uff0c\u5929\u7a7a\u4e2d\u54cd\u8d77\u4e86\u8f70\u8f70\u7684\u5de8\u9e23\uff01 \u3000\u3000\u9739\u96f3\u4e00\u58f0\uff0c\u7535\u5149\u75be\u95ea\uff0c\u6012\u96f7\u4e0b\u51fb\uff0c\u5c06\u4e00\u68f5\u5927\u6811\u5288\u65ad\u3002\u8428\u5929\u90fd\u88ab\u96f7\u58f0\u4e00\u9707\uff0c\u8df3\u8d77\u6765\u65f6\u3002\u88ab \u5de8\u96f7\u5288\u65ad\u98de\u8d77\u7684\u5927\u6811\u6b63\u6b63\u538b\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u8428\u5929\u90fd\u5927\u543c\u4e00\u58f0\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u5c06\u71c3\u70e7\u7740\u7684\u5927\u6811\u629b\u8fc7 \u4e00\u8fb9\uff0c\u53ef\u662f\u4ed6\u8eab\u4e0a\u7684\u8863\u88f3\u6bdb\u53d1\u5df1\u5168\u7740\u706b\u711a\u70e7\u3002\u8428\u5929\u90fd\u75db\u6781\u72c2\u6012\uff0c\u5e26\u7740\u718a\u718a\u7684\u706b\u7130\uff0c\u7a81\u7136\u8dc3 \u8d77\uff0c\u5411\u5510\u6653\u6f9c\u4e00\u5934\u649e\u6765\uff0c\u5510\u6653\u6f9c\u98de\u8eab\u6025\u95ea\uff0c\u53ea\u542c\u5f97\u9707\u5929\u4ef7\u4e00\u58f0\u5de8\u54cd\uff0c\u6811\u6728\u6467\u88c2\uff0c\u706b\u7130\u98de\u821e\uff0c \u8428\u5929\u90fd\u8fd9\u4e00\u649e\u6b63\u5de7\u649e\u5728\u4e00\u68f5\u5343\u5e74\u8001\u6811\u8eab\u4e0a\uff0c\u6811\u5012\u4eba\u4ea1\uff0c\u706b\u820c\u4e00\u5377\uff0c\u987f\u65f6\u70e7\u6210\u9ed1\u70ad\uff01 \u3000\u3000\u8428\u5929\u523a\u5927\u53eb\u9053\uff1a\u201c\u5929\u90fd\uff0c\u5929\u90fd\uff01\u201d\u4e0d\u89c1\u56de\u54cd\uff0c\u7741\u5706\u4e86\u773c\uff0c\u5728\u70df\u96fe\u5f25\u6f2b\u4e2d\u770b\u89c1\u60e8\u76f8\uff0c\u4e00\u58f0 \u72c2\u53eb\uff0c\u5341\u722a\u9f50\u626c\uff0c\u5411\u5510\u6653\u6f9c\u75be\u6495\u75be\u6293\uff0c\u8981\u77e5\u53cc\u9b54\u5982\u540c\u4e00\u4f53\uff0c\u51e0\u5341\u5e74\u6765\u4ece\u4e0d\u5206\u79bb\uff0c\u800c\u4eca\u624b\u8db3\u4f24 \u4ea1\uff0c\u75db\u6781\u5982\u72c2\uff0c\u51b3\u5fc3\u6b7b\u62fc\u3002\u5510\u6653\u6f9c\u51e0\u4e4e\u7ed9\u4ed6\u6293\u7740\uff0c\u8fde\u8fde\u540e\u9000\uff0c\u51af\u745b\u633a\u5251\u8fce\u51fb\uff0c\u8428\u5929\u523a\u660e\u77e5\u4e0d \u654c\uff0c\u4ecd\u662f\u72e0\u653b\u731b\u6253\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8fd8\u6211\u5f1f\u5f1f\u547d\u6765\uff01\u201d \u3000\u3000\u51af\u745b\u51b7\u7b11\u4e00\u58f0\uff0c\u77ed\u5251\u62ab\u8361\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u75be\u8fdb\u6570\u62db\uff0c\u51b7\u7b11\u9a82\u9053\uff1b\u201c\u6211\u5bb6\u4eba\u7684\u6027\u547d\u53c8\u5411\u8c01\u53bb \u8ba8\uff1f\u4f60\u4eec\u5144\u5f1f\u6740\u6b7b\u4e86\u591a\u5c11\u4eba\uff0c\u90a3\u4e9b\u51a4\u9b42\u53c8\u5411\u8c01\u8ba8\u503a\uff1f\u201d \u3000\u3000\u8428\u5929\u523a\u84e6\u5403\u4e00\u60ca\uff0c\u51af\u745b\u8fd9\u51e0\u53e5\u8bdd\u5982\u5de8\u96f7\u8f70\u545c\uff0c\u51fb\u5728\u4ed6\u7684\u5fc3\u4e0a\u3002\u6014\u4e86\u4e00\u6014\uff0c\u624b\u811a\u7565\u7f13\uff0c\u51af \u745b\u5251\u6cd5\u4f55\u7b49\u5feb\u6377\uff0c\u5587\u7684\u4e00\u5251\uff0c\u63d2\u5165\u5fc3\u623f\uff0c\u8428\u5929\u523a\u602a\u53eb\u4e00\u58f0\uff0c\u53cc\u773c\u7ffb\u767d\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5f62\u5982\u50f5 \u5c38\uff0c\u51af\u745b\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u6025\u5fd9\u628a\u5251\u62d4\u51fa\uff0c\u53ea\u542c\u5f97\u8428\u5929\u523a\u53eb\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e0d\u602a\u4f60\uff01\u201d\u7ffb\u8eab\u4fbf\u5012\u3002 \u3000\u3000\u5510\u6653\u6f9c\u53eb\u9053\uff1a\u201c\u745b\u59b9\u624b\u4e0b\u7559\u60c5\u3002\u201d\u53ef\u662f\u5df2\u7ecf\u8fdf\u4e86\u3002\u5510\u6653\u6f9c\u9053\uff1a\u201c\u8fd9\u4eba\u4f3c\u6709\u6094\u609f\u4e4b\u5fc3\uff0c\u53ef \u60dc\u53ef\u60dc\uff01\u201d\u4e0a\u524d\u5bdf\u770b\uff0c\u5929\u7a7a\u7535\u5149\u75be\u95ea\uff0c\u96f7\u58f0\u5927\u4f5c\uff0c\u503e\u76c6\u5927\u96e8\uff0c\u4ece\u5929\u800c\u964d\uff0c\u5510\u6653\u6f9c\u677e\u4e86\u53e3\u6c14\uff0c \u8dd1\u5230\u8428\u5929\u523a\u8eab\u8fb9\uff0c\u53ea\u542c\u5f97\u8428\u5929\u523a\u9053\uff1a\u201c\u53eb\u7433\u4e2b\u5934\u6765\uff0c\u6211\u6709\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u5411\u51af\u745b\u62db\u4e86\u62db\u624b\uff1b\u9053\uff1a\u201c\u4eba\u4e4b\u5c06\u6b7b\uff0c\u5176\u8a00\u4e5f\u5584\u3002\u4e14\u542c\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d\u51af\u745b\u6015\u89c1\u8428 \u5929\u523a\u7684\u60e8\u76f8\uff0c\u628a\u8138\u626d\u8fc7\u4e00\u8fb9\uff0c\u7f13\u7f13\u884c\u8fd1\u3002\u53ea\u542c\u5f97\u8428\u5929\u523a\u9053\uff1a\u201c\u7433\u4e2b\u5934\uff0c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u6211\uff0c \u6211\u2026\u2026\u201d\u58f0\u97f3\u65ad\u7eed\u5fae\u5f31\uff0c\u542c\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u5510\u6653\u6f9c\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5c06\u4ed6\u597d\u597d\u57cb\u4e86\u5427\u3002\u201d\u8428\u5929\u523a\u5ffd\u7136\u7ffb\u4e86\u4e2a\u8eab\uff0c\u6323\u624e\u8bf4\u9053\uff1a\u201c\u6211\u8eab \u4e0a\u6709\u4e2a\u836f\u7403\uff0c\u4f60\u62ff\u51fa\u6765\uff0c\u5728\u6211\u6293\u4f24\u4f60\u7684\u4f24\u53e3\u4e0a\u4e00\u6eda\uff0c\u4fbf\u53ef\u65e0\u4e8b\u3002\u8fd9\u836f\u7403\u8fd8\u6709\u5f88\u5927\u7528\u5904\uff0c\u5f88\u5927 \u7528\u5904\u2026\u2026\u201d\u6c14\u529b\u5fae\u5f31\uff0c\u53c8\u8bf4\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u51af\u745b\u5fc3\u4e2d\u4e00\u9178\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u6709\u201c\u9b54\u5934\u201d\u79f0\u53f7\u7684\u8001\u602a\u7269\uff0c\u5728\u4e34\u6b7b\u4e4b\u524d\uff0c\u5c45\u7136\u8fd8\u6709\u5584\u826f\u4e4b \u5fc3\u3002\u4e0d\u89c9\u56de\u8fc7\u8138\u6765\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u8428\u5929\u523a\u6b7b\u524d\u7684\u5f62\u8c8c\u672c\u6781\u53ef\u6015\uff0c\u4f46\u6b64\u65f6\u5728\u51af\u745b\u773c\u4e2d\uff0c\u5374\u5df2\u4f3c\u4e00 \u4e2a\u6148\u6837\u7684\u8001\u4eba\u3002\u51af\u745b\u9053\uff1a\u201c\u4f60\u662f\u6211\u5bb6\u7684\u4ec7\u4eba\u3002\u4f46\u4f60\u53c8\u5bf9\u6211\u6709\u8fc7\u6570\u5e74\u517b\u80b2\u4e4b\u6069\u3002\u6069\u6028\u62b5\u9500\uff0c\u6211 \u4e5f\u4e0d\u602a\u4f60\u4e86\u3002\u201d\u8428\u5929\u523a\u6de1\u6de1\u4e00\u7b11\uff0c\u5510\u6653\u6f9c\u4f38\u624b\u6478\u4ed6\u80f8\u53e3\uff0c\u5df2\u662f\u6c14\u7edd\u3002 \u3000\u3000\u51af\u745b\u9053\uff1a\u201c\u4eba\u771f\u5947\u602a\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6bd4\u8d77\u5e74\u7fb9\u5c27\u8fd9\u4e9b\u4eba\u6765\uff0c\u4ed6\u8981\u597d\u5f97\u591a\u4e86\u3002\u201d\u4f38\u624b\u5230 \u4ed6\u7684\u6000\u4e2d\u6478\u7d22\uff0c\u679c\u7136\u53d6\u51fa\u4e00\u9897\u9ed1\u9edd\u9edd\u7684\u836f\u4e38\u3002 \u3000\u3000\u8fd9\u836f\u4e38\u6709\u9152\u676f\u90a3\u4e48\u5927\u5c0f\uff0c\u53d1\u51fa\u4e00\u79cd\u5f3a\u70c8\u7684\u5f02\u81ed\uff0c\u5510\u6653\u6f9c\u892a\u4e86\u51af\u745b\u7684\u5916\u88f3\uff0c\u5c06\u836f\u4e38\u5728\u5979\u4f24 \u53e3\u4e0a\u6eda\u4e86\u4e24\u6eda\uff0c\u51af\u745b\u89c9\u5f97\u597d\u4f3c\u7ed9\u71a8\u6597\u70eb\u8fc7\u4e00\u6837\uff0c\u70ed\u6c14\u76f4\u900f\u5fc3\u623f\uff0c\u90a3\u6761\u80f3\u818a\u987f\u65f6\u6325\u52a8\u81ea\u5982\uff0c\u9ebb \u75d2\u4e5f\u6b62\u4e86\u3002\u5510\u6653\u6f9c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u836f\u7403\u5982\u6b64\u7075\u6548\uff0c\u60f3\u5fc5\u662f\u53cc\u9b54\u7684\u72ec\u95e8\u89e3\u836f\u4e86\uff0c\u4ed6\u8bf4\u8fd9\u4e1c\u897f\u8fd8 \u6709\u5927\u7528\uff0c\u4f60\u7559\u7740\u5427\u3002\u201d\u51af\u745b\u63a9\u9f3b\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8981\u3002\u201d\u5510\u6653\u6f9c\u7b11\u4e86\u4e00\u7b11\uff0c\u77e5\u5979\u6015\u81ed\uff0c\u4fbf\u628a\u836f\u4e38 \u653e\u5165\u81ea\u5df1\u7684\u56ca\u4e2d\u3002 \u3000\u3000\u66b4\u98ce\u96e8\u6765\u5f97\u5feb\uff0c\u53bb\u5f97\u4e5f\u5feb\uff0c\u8fd9\u65f6\u98ce\u58f0\u672a\u505c\uff0c\u96e8\u58f0\u5df2\u5c0f\uff0c\u5c0f\u5c9b\u7684\u5927\u706b\uff0c\u5e78\u597d\u7ed9\u8fd9\u573a\u5927\u96e8\u4e00 \u538b\uff0c\u5dee\u4e0d\u591a\u5168\u7184\u706d\u4e86\u3002\u5c0f\u5c9b\u4e0a\u79ef\u6c34\u76c8\u5c3a\uff0c\u6742\u82b1\u5f02\u8349\u90fd\u5df2\u70e7\u5149\uff0c\u53ea\u5269\u4e0b\u8bb8\u591a\u70e7\u7126\u4e86\u7684\u5149\u79c3\u79c3\u7684 \u6811\u6728\u3002\u51af\u745b\u7b11\u9053\uff1a\u201c\u771f\u715e\u98ce\u666f\uff0c\u7ed9\u8fd9\u573a\u5927\u706b\u4e00\u70e7\uff0c\u4e0d\u597d\u73a9\u4e86\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u53cc\u9b54\u7684\u5c0f\u8239\u5df2 \u6210\u7c89\u788e\uff0c\u54b1\u4eec\u53ea\u597d\u518d\u803d\u6401\u4e0b\u53bb\u4e86\u3002\u201d\u60f3\u8d77\u81ea\u5df1\u6b7b\u671f\u5927\u7ea6\u4e0d\u8fc7\u767e\u65e5\uff0c\u4e0d\u89c9\u9eef\u7136\u3002 \u3000\u3000\u4e24\u4eba\u9ed8\u9ed8\u65e0\u8a00\uff0c\u4ee5\u5251\u6316\u571f\uff0c\u5c06\u53cc\u9b54\u63a9\u57cb\u3002\u5927\u96e8\u8fc7\u540e\uff0c\u5bd2\u6f6e\u6d8c\u81f3\uff0c\u6d77\u98ce\u900f\u9aa8\uff0c\u751a\u89c9\u5bd2\u51b7\uff0c \u51af\u745b\u501a\u5728\u5510\u6653\u6f9c\u8eab\u4e0a\uff0c\u4e0d\u89c9\u7761\u53bb\u3002\u5510\u6653\u6f9c\u8131\u4e0b\u4e00\u4ef6\u8863\u670d\uff0c\u8986\u5728\u5979\u7684\u8eab\u4e0a\u3002\u5fc3\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u4e8f \u5979\u4e5f\u7761\u5f97\u7740\u3002\u201d\u5510\u6653\u6f9c\u867d\u7136\u75b2\u5026\uff0c\u53ef\u662f\u8fd9\u4e24\u65e5\u6765\u7684\u53d8\u5316\uff0c\u4ee4\u4ed6\u5927\u53d7\u523a\u6fc0\uff0c\u4e00\u5ffd\u513f\u60f3\u5230\u81ea\u5df1\u7684 \u6b7b\u671f\uff0c\u4e00\u5ffd\u513f\u60f3\u5230\u51af\u745b\u5bf9\u4ed6\u7684\u7231\u610f\uff0c\u4e00\u5ffd\u513f\u53c8\u60f3\u5230\u8428\u5929\u523a\u4e34\u6b7b\u7684\u60c5\u666f\uff0c\u601d\u6f6e\u6c79\u6d8c\uff0c\u778c\u4e86\u773c\u775b \u4e5f\u7761\u4e0d\u7740\u3002\u98ce\u58f0\u6e10\u6b62\uff0c\u6d77\u9762\u4e0a\u5ffd\u4f3c\u4f20\u6765\u547c\u558a\u4e4b\u58f0\u3002\u5510\u6653\u6f9c\u4fa7\u8033\u4e00\u542c\uff0c\u5c06\u51af\u745b\u4e00\u63a8\uff0c\u8df3\u4e86\u8d77 \u6765\uff0c\u53eb\u9053\uff1a\u201c\u745b\u59b9\uff0c\u597d\u50cf\u662f\u53c8\u6709\u8239\u6765\u4e86\uff01\u201d \u3000\u3000\u51af\u745b\u62b9\u62b9\u773c\u775b\uff0c\u559c\u9053\uff1a\u201c\u597d\u5440\uff0c\u54b1\u4eec\u53ef\u4ee5\u8131\u9669\u4e86\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u522b\u6b22\u559c\uff0c\u8fd8\u672a\u77e5\u6765 \u7684\u662f\u8c01\u5462\uff01\u201d\u51af\u745b\u9053\uff1a\u201c\u4f46\u613f\u4e0d\u662f\u6bd2\u9f99\u5c0a\u8005\uff01\u201d\u4e24\u4eba\u8dd1\u5230\u6d77\u6ee8\uff0c\u9ed1\u591c\u6c89\u6c89\uff0c\u6d77\u9762\u6d6a\u82b1\u95ea\u8000\uff0c \u5374\u4e0d\u89c1\u8239\u53ea\u5f71\u5b50\u3002\u51af\u745b\u9053\uff1a\u201c\u4f60\u662f\u505a\u68a6\u5427\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u54a6\uff0c\u5947\u602a\uff0c\u6211\u660e\u660e\u542c\u5230\u662f\u4eba\u7684\u53eb \u58f0\u3002\u201d \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u5ffd\u7136\u53c8\u6709\u5578\u58f0\u8fdc\u8fdc\u4f20\u6765\uff0c\u97f3\u7ec6\u800c\u6e05\uff0c\u5b9b\u82e5\u6e38\u4e1d\u8885\u7a7a\uff0c\u81ea\u9065\u8fdc\u7684\u6d77\u8fb9\uff0c\u9690\u9690\u4f20 \u5230\u3002\u5510\u6653\u6f9c\u548c\u51af\u745b\u90fd\u5927\u5403\u4e00\u60ca\u3002\u51af\u745b\u9053\uff1a\u201c\u8fd9\u662f\u4f20\u97f3\u5165\u5bc6\u7684\u529f\u592b\uff01\u201d\u6781\u76ee\u8fdc\u773a\uff0c\u770b\u4e86\u4e00\u9635\uff0c \u624d\u53d1\u73b0\u6d77\u9762\u8fdc\u5904\u6709\u4e00\u4e2a\u9ed1\u70b9\u98d8\u52a8\u3002\u51af\u745b\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u662f\u6709\u5c0f\u8239\u6765\u4e86\uff01\u201d\u5510\u6653\u6f9c\u9053\uff1a \u201c\u5728\u8fd9\u6837\u8fdc\u7684\u5730\u65b9\uff0c\u5578\u58f0\u5c45\u7136\u80fd\u4f20\u5230\u8fd9\u91cc\uff0c\u53d1\u58f0\u7684\u4eba\u5185\u529f\u6df1\u4e0d\u53ef\u6d4b\uff01\u82e5\u7136\u6765\u7684\u53c8\u662f\u654c\u4eba\uff0c\u4f60 \u6211\u4e8c\u4eba\u90fd\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff01\u201d \u3000\u3000\u51af\u745b\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u4e0d\u53bb\u7406\u4ed6\u3002\u201d\u6d77\u9762\u6ce2\u6d9b\u6c79\u6d8c\uff0c\u9ed1\u70b9\u8d8a\u6765\u8d8a\u5927\uff0c\u770b\u6e05\u695a\u662f\u53ea\u5c0f\u8239\u4e86\uff0c \u5c0f\u8239\u98d8\u6447\u5728\u98ce\u6d6a\u4e4b\u4e2d\uff0c\u51af\u745b\u60f3\u8d77\u81ea\u5df1\u6240\u66fe\u53d7\u7684\u98ce\u6d6a\u4e4b\u82e6\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u662f\u8c01\uff0c\u6551 \u4ed6\uff01\u201d\u5510\u6653\u6f9c\u7b11\u9053\uff1a\u201c\u4f60\u6211\u60f3\u7684\u6b63\u597d\u76f8\u540c\uff0c\u4ed6\u4e00\u5b9a\u662f\u89c1\u7740\u54b1\u4eec\u8fd9\u91cc\u7684\u706b\u5149\uff01\u6025\u4e8e\u7740\u9646\uff0c\u6240\u4ee5 \u5411\u8fd9\u91cc\u9a76\u6765\u3002\u73b0\u5728\u5927\u706b\u5df2\u7ed9\u66b4\u96e8\u6240\u706d\uff0c\u4ed6\u627e\u4e0d\u5230\u65b9\u5411\uff0c\u6240\u4ee5\u53eb\u558a\u3002\u54b1\u4eec\u628a\u706b\u751f\u8d77\u6765\u5427\u3002\u201d\u51af \u745b\u62fe\u4e86\u4e00\u5806\u70e7\u6b8b\u7684\u679d\u53f6\uff0c\u4f9d\u8a00\u628a\u706b\u751f\u8d77\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u5c0f\u8239\u679c\u7136\u4f3c\u7bad\u98de\u6765\u3002\u5510\u6653\u6f9c\u51fa\u58f0\u547c \u5524\uff0c\u6d77\u9762\u4e0a\u4f20\u6765\u6781\u6e05\u8106\u7684\u5973\u58f0\uff1a\u201c\u662f\u6653\u6f9c\u5417\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u8fd9\u58f0\u97f3\u662f\u5982\u6b64\u719f\u6089\uff0c\u5982\u6b64\u6e29\u67d4\uff0c\u8fd9\u58f0\u97f3\u5728\u8fc7\u53bb\u66fe\u7ecf\u7ed9 \u4ed6\u4ee5\u529b\u91cf\uff0c\u4ee4\u5979\u5728\u9893\u5510\u7edd\u671b\u4e4b\u9645\u632f\u4f5c\u8d77\u6765\uff0c\u800c\u4eca\u4ed6\u53c8\u9762\u4e34\u751f\u547d\u4e2d\u7684\u7b2c\u4e8c\u6b21\u96be\u9898\uff0c\u60f3\u4e0d\u5230\u53c8\u542c \u5230\u8fd9\u58f0\u97f3\u4e86\u3002\u51af\u745b\u89c1\u4ed6\u5446\u5446\u7684\u51dd\u671b\uff0c\u773c\u4e2d\u6d41\u9732\u51fa\u53c8\u60ca\u53c8\u559c\u7684\u5149\u5f69\uff0c\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u8fd9\u8239\u4e0a\u7684\u5973 \u5b50\u662f\u4f60\u8ba4\u8bc6\u7684\u5417\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u55ef\uff0c\u8ba4\u8bc6\u7684\uff01\u201d\u90a3\u5973\u5b50\u53c8\u53eb\u9053\uff1a\u201c\u662f\u6653\u6f9c\u5417\uff1f\u201d\u5510\u6653\u6f9c\u5927 \u58f0\u5e94\u9053\uff1a\u201c\u662f\u6211\uff0c\u5415\u59d0\u59d0\uff01\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u5c0f\u821f\u6cca\u5cb8\u3002\u8239\u4e0a\u8df3\u4e0b\u4e00\u7537\u4e8c\u5973\uff0c\u7537\u7684\u662f\u767d\u6cf0\u5b98\uff0c\u5973\u7684\u662f\u5415\u56db\u5a18\u548c\u9c7c\u5a18\u3002\u767d\u6cf0 \u5b98\u548c\u9c7c\u5a18\u5026\u5bb9\u6ee1\u9762\uff0c\u8863\u88f3\u6e7f\u900f\uff0c\u663e\u7136\u662f\u66fe\u7ecf\u548c\u66b4\u98ce\u6d6a\u640f\u6597\u8fc7\u3002\u5415\u56db\u5a18\u867d\u7136\u4e5f\u9732\u75b2\u6001\uff0c\u4f46\u6001\u5ea6 \u5374\u751a\u5b89\u8be6\uff0c\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u4e0d\u662f\u505a\u68a6\u5427\uff0c\u4ec0\u4e48\u98ce\u628a\u4f60\u5439\u5230\u8fd9\u5c0f\u5c9b\u6765\u4e86\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u98ce\uff1f\u8fd8\u4e0d\u5c31\u662f\u8fd9\u573a\u53f0\u98ce\uff01\u8981\u4e0d\u662f\u77a7\u89c1\u8fd9\u91cc\u6709\u706b\u5149\uff0c\u6211\u4eec\u51e0\u4e4e\u4ee5\u4e3a\u5b9a \u8981\u7ed9\u9cb8\u9c7c\u541e\u6389\u4e86\u3002\u201d\u9c7c\u5a18\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4e09\u4eba\u4e2d\u5415\u59d0\u59d0\u6c34\u6027\u6700\u5dee\uff0c\u4f46\u5374\u6570\u5979\u6700\u9547\u5b9a\uff0c\u6700\u7ecf\u5f97\u98ce \u6d6a\u3002\u5440\uff0c\u4f60\u4eec\u53c8\u600e\u4e48\u4f1a\u5728\u8fd9\u4e2a\u5c0f\u5c9b\uff1f\u54a6\uff0c\u8fd9\u4e2a\u5c0f\u59d1\u5a18\uff0c\u8fd9\u4e2a\u5c0f\u59d1\u5a18\u4e0d\u662f\u4ee5\u524d\u548c\u4e86\u56e0\u5728\u4e00\u8d77\u7684 \u90a3\u4f4d\u5c0f\u59d1\u5a18\u5417\uff1f\u201d\u9c7c\u5a18\u548c\u767d\u6cf0\u5b98\u4ee5\u524d\u66fe\u5728\u676d\u5dde\u6e56\u5fc3\u4ead\u78b0\u8fc7\u51af\u7433\uff0c\u8fd9\u65f6\u4e0d\u89c9\u53d8\u4e86\u9762\u8272\u3002 \u3000\u3000\u5510\u6653\u6f9c\u7b11\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u90a3\u4f4d\u662f\u5979\u7684\u59b9\u59b9\u3002\u201d\u62db\u624b\u53eb\u51af\u745b\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u4f60\u60f3\u89c1 \u7684\u5415\u56db\u5a18\u59d0\u59d0\u4e86\u3002\u201d\u51af\u745b\u5411\u5415\u56db\u5a18\u671b\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u5979\u771f\u662f\u5415\u59d0\u59d0\u5417\uff1f\u201d\u5510\u6653\u6f9c \u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u662f\uff1f\u201d\u51af\u745b\u4ece\u5e08\u5085\u53e3\u4e2d\u77e5\u9053\uff0c\u5415\u56db\u5a18\u5df2\u6210\u540d\u591a\u5e74\uff0c\u53c8\u542c\u5510\u6653\u6f9c\u8bf4\u8fc7\uff0c\u5415\u56db\u5a18 \u6bd4\u4ed6\u8fd8\u5927\u51e0\u5c81\uff0c\u5728\u51af\u745b\u60f3\u50cf\u4e2d\uff0c\u5415\u56db\u5a18\u4e00\u5b9a\u662f\u4e2a\u4e2d\u5e74\u5987\u5973\uff0c\u4f46\u73b0\u5728\u4e00\u770b\uff0c\u5374\u662f\u4e2a\u7f8e\u8273\u5982\u82b1\u7684 \u5c11\u5973\uff0c\u770b\u6765\u7adf\u548c\u81ea\u5df1\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u4e0d\u89c9\u5446\u4e86\u3002 \u3000\u3000\u5415\u56db\u5a18\u7b11\u4e86\u4e00\u7b11\uff0c\u62c9\u8d77\u51af\u745b\u7684\u624b\u9053\uff1a\u201c\u5c0f\u59b9\u59b9\uff0c\u6211\u5c31\u662f\u5415\u56db\u5a18\u3002\u6211\u66fe\u7ecf\u5f97\u8fc7\u4f60\u7684\u5e08\u5085\u4f20 \u8fc7\u655b\u7cbe\u5185\u89c6\u7684\u5185\u529f\uff0c\u6240\u4ee5\u4e5f\u7b97\u5f97\u4f60\u7684\u4e00\u534a\u5e08\u59d0\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u5415\u59d0\u59d0\uff0c\u4f60\u771f\u5e74\u8f7b\u3002\u201d\u5415\u56db\u5a18 \u7b11\u9053\uff1a\u201c\u662f\u5417\uff1f\u90a3\u5f97\u591a\u8c22\u4f60\u7684\u5e08\u5085\u3002\u201d\u201c\u655b\u7cbe\u5185\u89c6\u201d\u7684\u529f\u592b\u8981\u5185\u529f\u5f88\u6709\u6839\u5e95\u4e4b\u540e\u624d\u80fd\u4fee\u4e60\uff0c \u6240\u4ee5\u6613\u73e0\u5170\u8fd8\u672a\u66fe\u4f20\u7ed9\u51af\u745b\uff0c\u4e0d\u8fc7\u51af\u745b\u4e5f\u542c\u5e08\u5085\u8bf4\u8fc7\u6709\u8fd9\u79cd\u529f\u592b\uff0c\u89c1\u5415\u56db\u5a18\u8bf4\u51fa\u6b64\u4e8b\uff0c\u8fd9\u624d \u76f8\u4fe1\u3002 \u3000\u3000\u5510\u6653\u6f9c\u95ee\u9053\uff1a\u201c\u767d\u5144\u592b\u5987\u4e0d\u662f\u5230\u7530\u6a2a\u5c9b\u53bb\u62b5\u5fa1\u6e05\u5175\u5417\uff1f\u73b0\u5728\u600e\u4e48\u6837\u4e86\uff1f\u5415\u59d0\u59d0\u53c8\u600e\u4f1a\u548c \u4f60\u4eec\u4e00\u9053\uff1f\u201d\u767d\u6cf0\u5b98\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6211\u5cb3\u4e08\u51e0\u5341\u5e74\u7684\u57fa\u4e1a\u5df2\u5168\u6bc1\u4e86\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u65cf\u4eba\u88ab \u6e05\u5ef7\u641c\u6355\u6740\u5bb3\uff0c\u53ea\u9003\u51fa\u4e00\u4e2a\u5802\u4f84\u5415\u5143\u3002\u5927\u534a\u5e74\u524d\uff0c\u6211\u5c06\u4ed6\u9001\u7ed9\u7384\u98ce\u9053\u957f\u4e3a\u5f92\uff0c\u4ece\u8fbd\u4e1c\u534a\u5c9b\u56de \u6765\uff0c\u6070\u5de7\u78b0\u7740\u767d\u5e08\u5144\u592b\u5987\uff0c\u662f\u4ed6\u8bf7\u6211\u540c\u5f80\u7530\u6a2a\u5c9b\u7684\uff0c\u53ef\u60dc\u53bb\u8fdf\u4e86\u4e00\u6b65\uff0c\u6e05\u519b\u6c34\u5e08\u5df2\u628a\u5404\u5904\u5c9b \u5c7f\u6c34\u5be8\u5305\u56f4\uff0c\u6211\u4eec\u5343\u8f9b\u4e07\u82e6\u6df7\u4e86\u8fdb\u53bb\uff0c\u6c34\u5be8\u7684\u5f1f\u5144\u5df2\u6b7b\u4ea1\u8fc7\u534a\uff0c\u6211\u4eec\u867d\u7136\u6781\u529b\u62b5\u654c\uff0c\u4f46\u5df2\u662f \u56de\u5929\u4e4f\u672f\uff0c\u4e0d\u8fc7\uff0c\u6e05\u519b\u6c34\u5e08\u4e5f\u7ed9\u6211\u4eec\u6740\u4f24\u51e0\u4e07\uff0c\u5c0f\u5c0f\u7684\u7530\u6a2a\u5c9b\u4fbf\u5b88\u536b\u4e86\u4e09\u4e2a\u591a\u6708\u3002\u5be8\u7834\u4e4b \u540e\uff0c\u6211\u4eec\u593a\u4e86\u8239\u53ea\uff0c\u9760\u7740\u4e94\u5ac2\uff08\u9c7c\u5a18\uff09\u7cbe\u901a\u6c34\u6027\uff0c\u5728\u9ec4\u6d77\u6e24\u6d77\u7ed5\u4e86\u597d\u5927\u4e00\u4e2a\u5708\u5b50\uff0c\u624d\u907f\u5f00\u6e05 \u519b\u6c34\u5e08\uff0c\u9003\u5230\u8fd9\u91cc\u3002\u201d \u3000\u3000\u9c7c\u5a18\u5728\u65c1\u9759\u542c\uff0c\u95ee\u9053\uff1a\u201c\u5510\u5144\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u4eec\u5728\u7530\u6a2a\u5c9b\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u5728\u6d4e\u5357\u78b0 \u7740\u4e86\u7518\u5927\u54e5\u3002\u201d\u9c7c\u5a18\u6025\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u89c1\u7740\u4e86\u6211\u7684\u7236\u4eb2\u6ca1\u6709\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u548c\u4ed6\u540c\u5904\u4e86 \u6570\u6708\u3002\u201d\u767d\u6cf0\u5b98\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u5728\u54ea\u91cc\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u60f3\u5fc5\u8fd8\u5728\u86c7\u5c9b\u3002\u201d\u9c7c\u5a18\u8df3\u4e86\u8d77\u6765\uff0c\u53eb \u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u86c7\u5c9b\uff1f\u600e\u4e48\u4f1a\u6f02\u6d41\u5230\u90a3\u4e2a\u9b54\u5c9b\u4e0a\u53bb\uff1f\u201d\u6000\u5ff5\u8001\u7236\uff0c\u5fc3\u60ca\u80c6\u6218\uff0c\u9762\u5982\u6b7b\u7070\u3002\u767d\u6cf0 \u5b98\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u51e4\u6c60\u5462\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u7518\u5927\u54e5\u4e5f\u5728\u86c7\u5c9b\u3002\u8fd8\u4e0d\u6b62\u4ed6\u4e24\u4eba\uff0c\u5b5f\u5be8\u738b\uff0c\u536b\u5c9b\u4e3b \u7b49\u4e00\u5e2e\u4eba\u90fd\u5728\u90a3\u91cc\u3002\u201d\u9c7c\u5a18\u542c\u4e86\u5fc3\u4e2d\u7a0d\u5bbd\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u53c8\u4f1a\u79bb\u7fa4\u81f3\u6b64\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a \u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u4f60\u4eec\u6362\u4e86\u8863\u670d\uff0c\u5403\u8fc7\u4e1c\u897f\uff0c\u4f11\u606f\u4e00\u4f1a\uff0c\u6211\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u770b\u7740\u51af\u5510\u4e8c\u4eba\u6240\u7a7f\u7684\u7528\u91ce\u9ebb\u7f1d\u6210\u7684\u8863\u88f3\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u88ab\u56f0\u5728\u8fd9\u91cc\u4e00\u5b9a\u5f88\u4e45 \u4e86\u3002\u201d\u5510\u6653\u6f9c\u9762\u4e0a\u4e00\u7ea2\uff0c\u5415\u56db\u5a18\u7eed\u9053\uff1a\u201c\u4f60\u4eec\u7684\u8863\u88f3\u4e5f\u8be5\u6362\u4e86\u3002\u201d\u51af\u745b\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u4e0d\u4f1a \u7f1d\u8863\uff0c\u662f\u8fd9\u51e0\u4e2a\u6708\u81ea\u5df1\u6478\u7d22\u5b66\u7684\u3002\u6211\u7528\u9c7c\u9aa8\u4f5c\u9488\uff0c\u6413\u9ebb\u4e1d\u4f5c\u7ebf\uff0c\u80e1\u4e71\u7f1d\u8865\uff0c\u62c8\u9488\u5f04\u7ebf\uff0c\u6bd4\u5f04 \u5200\u4f7f\u5251\u8fd8\u96be\u5f97\u591a\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u7f1d\u5f97\u5f88\u597d\uff0c\u771f\u80fd\u5e72\u3002\u4e0d\u8fc7\u6709\u73b0\u6210\u7684\u8863\u670d\uff0c\u62ff\u6765\u66ff \u6362\uff0c\u5c31\u4e0d\u987b\u4f60\u8f9b\u82e6\u4e86\u3002\u201d\u51af\u745b\u7f1d\u7684\u9ebb\u8863\uff0c\u5176\u5b9e\u4e0d\u6210\u6837\u5b50\uff0c\u8863\u88f3\u4e0a\u53c8\u6cbe\u6ee1\u6ce5\u6cde\u8840\u6c61\uff0c\u81ea\u5df1\u770b\u6c34 \u4e2d\u7684\u5f71\u5b50\uff0c\u4e5f\u89c9\u597d\u7b11\u3002 \u3000\u3000\u5415\u56db\u5a18\u5230\u5c0f\u8239\u4e0a\u53d6\u4e86\u8863\u88f3\uff0c\u751f\u706b\u716e\u98df\u3002\u5510\u6653\u6f9c\u548c\u51af\u745b\u6362\u597d\u8863\u88f3\uff0c\u5415\u56db\u5a18\u7b49\u4e5f\u5403\u8fc7\u4e86\u4e1c \u897f\uff0c\u5510\u6653\u6f9c\u5c06\u8fd9\u51e0\u4e2a\u6708\u7684\u906d\u9047\u8bf4\u4e86\u51fa\u6765\u3002\u9c7c\u5a18\u542c\u5f97\u7236\u4eb2\u5df2\u7ecf\u6094\u609f\uff0c\u975e\u5e38\u9ad8\u5174\uff0c\u542c\u5230\u4ed6\u4eec\u88ab\u56f0 \u5728\u9b54\u5c9b\uff0c\u65e5\u65e5\u88ab\u6bd2\u9f99\u5c0a\u8005\u6298\u78e8\uff0c\u975e\u5e38\u7126\u6025\u62c5\u5fc3\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e94\u5ac2\uff0c\u660e\u5929\u6211\u4eec\u5230\u86c7\u5c9b\u53bb\u6597\u6597 \u90a3\u4e2a\u6bd2\u9f99\u5c0a\u8005\u3002\u542c\u6653\u6f9c\u8bf4\uff0c\u8fd9\u4eba\u539f\u662f\u4e2a\u5927\u9ebb\u75af\uff0c\u602a\u4e0d\u5f97\u4ed6\u6124\u4e16\u5ac9\u4fd7\uff0c\u636e\u6211\u770b\uff0c\u4ed6\u867d\u7136\u8868\u9762\u51f6 \u6076\uff0c\u4e5f\u8bb8\u8fd8\u4e0d\u50cf\u53cc\u9b54\u90a3\u6837\u574f\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u53cc\u9b54\u4e5f\u4e0d\u662f\u9876\u574f\u4e4b\u4eba\u3002\u201d\u53c8\u5c06\u8428\u5929\u523a\u4e34\u6b7b\u4e4b\u524d \u9001\u836f\u4e38\u7597\u4f24\u7b49\u4e8b\u8bf4\u51fa\uff0c\u4f17\u4eba\u65e0\u4e0d\u55df\u53f9\u3002 \u3000\u3000\u8fd9\u665a\u51af\u745b\u548c\u5415\u56db\u5a18\u540c\u7761\uff0c\u51af\u745b\u5f88\u559c\u6b22\u5415\u56db\u5a18\uff0c\u95ee\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u548c\u5510\u53d4\u53d4\u5f88\u65e9\u8ba4\u8bc6\u7684 \u5417\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u662f\u5440\uff0c\u4f60\u5468\u5c81\u521a\u8fc7\uff0c\u6211\u5c31\u548c\u4ed6\u8ba4\u8bc6\u4e86\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6211\u8be5\u53eb\u4f60\u59d1 \u59d1\u624d\u662f\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u6211\u4eec\u7684\u5e08\u5085\u662f\u540c\u8f88\uff0c\u4f60\u4e0d\u5fc5\u5ba2\u6c14\u4e86\u3002\u201d\u51af\u745b\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u548c\u6211 \u7684\u5a76\u5a76\u719f\u5417\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u6768\u4ef2\u82f1\u7684\u5973\u513f\u5417\uff1f\u8ba4\u5f97\u7684\uff0c\u4f46\u4e0d\u5f88\u719f\u3002\u201d\u51af\u745b\u82e5\u6709\u6240 \u601d\uff0c\u5ffd\u9053\uff1a\u201c\u5982\u679c\u6211\u7684\u5a76\u5a76\u50cf\u4f60\u4e00\u6837\uff0c\u6211\u5c31\u6b22\u559c\u4e86\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u6b22\u559c\u90a3\u4e2a\u5a76\u5a76 \u5417\uff1f\u201d\u51af\u745b\u70b9\u70b9\u5934\u9053\uff1a\u201c\u55ef\uff0c\u662f\u4e0d\u6b22\u559c\uff01\u6211\u89c9\u5f97\u5979\u548c\u5510\u53d4\u53d4\u4e0d\u914d\u3002\u201d\u51af\u745b\u8bf4\u8fd9\u4e24\u53e5\u8bdd\u65f6\u5145\u6ee1 \u60c5\u611f\uff0c\u773c\u775b\u95ea\u95ea\u53d1\u5149\uff0c\u4f3c\u4e4e\u662f\u5728\u671f\u5f85\u7740\u5415\u56db\u5a18\u7684\u540c\u610f\u3002\u5415\u56db\u5a18\u5fc3\u5ff5\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u55ef\uff0c\u6211\u4e5f\u4e0d \u559c\u6b22\u5979\u3002\u201d\u51af\u745b\u5927\u559c\uff0c\u5415\u56db\u5a18\u5ffd\u95ee\u9053\uff1a\u201c\u4f60\u4e0d\u559c\u6b22\u90a3\u4e2a\u5a76\u5a76\uff0c\u6709\u6ca1\u6709\u548c\u4f60\u7684\u5510\u53d4\u53d4\u8bf4\u5462\uff1f\u201d \u51af\u745b\u5c0f\u8138\u6655\u7ea2\uff0c\u671f\u671f\u827e\u827e\u7b54\u9053\uff1a\u201c\u8bf4\u8fc7\u7684\u3002\u4f46\u6211\u4e0d\u77e5\u8be5\u4e0d\u8be5\u8bf4\u3002\u201d\u5415\u56db\u5a18\u4e00\u7b11\u5c06\u8bdd\u9898\u62c9\u5f00\uff0c \u548c\u5979\u8c08\u8bba\u5251\u6cd5\uff0c\u51af\u745b\u66f4\u662f\u9ad8\u5174\uff0c\u548c\u5979\u8c08\u4e86\u534a\u591c\uff0c\u8fd9\u624d\u7761\u89c9\uff0c\u7b2c\u4e8c\u65e5\u4e00\u65e9\u9192\u6765\uff0c\u5374\u4e0d\u89c1\u4e86\u5415\u56db \u5a18\u3002 \u3000\u3000\u5415\u56db\u5a18\u8fd9\u65f6\u6b63\u548c\u5510\u6653\u6f9c\u5750\u5728\u5ca9\u77f3\u4e0a\u770b\u6d77\u4e0a\u65e5\u51fa\u3002\u98ce\u66b4\u4e4b\u540e\uff0c\u5929\u6717\u6c14\u6e05\uff0c\u53ea\u89c1\u6d77\u6ce2\u6d69\u68ee\uff0c \u5929\u8fde\u6c34\u6c34\u8fde\u5929\uff0c\u6c34\u5e73\u7ebf\u4e0a\u95ea\u8000\u7740\u4e00\u7247\u5f3a\u70c8\u7684\u6a59\u8272\u5149\u8292\uff0c\u4e91\u971e\u4e5f\u53d8\u5f97\u8273\u7ea2\u4e86\u3002\u4e00\u77ac\u95f4\uff0c\u7ea2\u8272\u7684 \u592a\u9633\u8df3\u51fa\u6c34\u5e73\u7ebf\u4e0a\uff0c\u968f\u7740\u6d77\u6d6a\u6ce2\u52a8\uff0c\u5ffd\u4e0a\u5ffd\u4e0b\uff0c\u63a5\u7740\u5c31\u6e10\u6e10\u5347\u51fa\u6d77\u9762\uff0c\u5149\u8272\u9c9c\u7ea2\u4f46\u5e76\u4e0d\u523a \u773c\uff0c\u6709\u65f6\u5b83\u56e0\u6c34\u6c14\u7684\u6298\u5c04\u4f1a\u6210\u4e3a\u6241\u5706\u5f62\uff0c\u6709\u65f6\u53c8\u90a3\u4e48\u6d51\u5706\u5f97\u4ee4\u4eba\u559c\u7231\u3002\u6d77\u9762\u4e0a\u4e07\u9053\u91d1\u5149\uff0c\u53d8 \u5316\u591a\u5f69\uff0c\u4ee4\u4eba\u76ee\u4e0d\u6687\u63a5\u3002\u4e24\u4eba\u770b\u5f97\u51fa\u795e\uff0c\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5415\u59d0\u59d0\uff0c\u8fd8\u8bb0\u5f97\u6570\u5e74\u524d\u6211\u4eec\u5728\u4ed9\u971e\u5cad \u540c\u770b\u65e5\u51fa\u5417\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u8bb0\u5f97\u90a3\u65f6\u4f60\u6b63\u56e0\u8eab\u4e16\u95ee\u9898\u800c\u82e6\u607c\uff0c\u8fe2\u8fe2\u4e07\u91cc\u8d76\u6765\uff0c\u548c\u6211\u8c08\u4e86\u534a\u5929\u3002 \u73b0\u5728\u6ca1\u4e8b\u4e86\u5427\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u90a3\u6b21\u591a\u8c22\u4f60\u7684\u6559\u8bf2\uff0c\u8eab\u4e16\u7684\u82e6\u607c\u65e9\u5df2\u6d88\u6563\uff0c\u4f46\u73b0\u5728\u5374\u53c8\u53e6\u6709 \u4e00\u79cd\u82e6\u607c\u4e86\u3002\u201d\u5415\u56db\u5a18\u6599\u5230\u4e86\u4e09\u56db\u5206\uff0c\u7b11\u95ee\u9053\uff1a\u201c\u53c8\u6709\u4ec0\u4e48\u82e6\u607c\u5462\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u9762\u4e0a\u4e00\u7ea2\uff0c\u671f\u671f\u827e\u827e\uff0c\u8bf4\u4e0d\u51fa\u53e3\u3002\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u4e8b\u60c5\u4e0d\u80fd\u5bf9\u6211\u8bf4\u7684\uff1f \u54ce\uff0c\u4e14\u5f85\u6211\u731c\u731c\u3002\u5514\uff0c\u4f60\u548c\u6768\u67f3\u9752\u5435\u67b6\u4e86\u662f\u4e0d\u662f\uff1f\u201d\u5510\u6653\u6f9c\u8001\u5927\u4e0d\u597d\u610f\u601d\uff0c\u70b9\u4e86\u70b9\u5934\u3002\u5415\u56db \u5a18\u9053\uff1a\u201c\u672a\u5a5a\u592b\u59bb\u5435\u5435\u67b6\u4e5f\u5f88\u7a00\u677e\u5e73\u5e38\uff0c\u4e3a\u4f55\u4f60\u8001\u8bb0\u5728\u5fc3\u5934\u5462\uff1f\u201d\u5510\u6653\u6f9c\u4f4e\u5934\u4e0d\u8bed\uff0c\u8fc7\u4e86\u597d \u4e00\u4f1a\u513f\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u603b\u89c9\u5f97\u6211\u548c\u5979\u7684\u6027\u60c5\u5408\u4e0d\u6765\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u53c8\u7b11\u4e86\u4e00\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u7684\u90a3\u4f4d\u4f84\u5973\uff0c\u6211\u662f\u8bf4\u51af\u745b\u8fd9\u5c0f\u59d1\u5a18\uff0c\u5979\u4e5f\u5f88\u8ba8\u538c\u67f3\u9752\uff0c \u662f\u4e0d\u662f\uff1f\u201d\u5510\u6653\u6f9c\u7684\u5fc3\u6026\u7136\u4e00\u9707\uff0c\u70b9\u5934\u9053\uff1a\u201c\u662f\u7684\uff01\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5047\u5982\u6211\u731c\u5f97\u4e0d\u9519\uff0c\u4f60\u7684 \u82e6\u607c\u4fbf\u5728\u6b64\u4e86\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u53c8\u9ed8\u7136\u4e0d\u8bed\uff0c\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u505a\u4e8b\u4f46\u6c42\u5fc3\u4e4b\u6240\u5b89\u3002\u4f60\u6ca1\u6709\u4ec0\u4e48\u5bf9\u4e0d\u4f4f\u6768\u59d1\u5a18 \u7684\u5730\u65b9\u5427\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5979\u7236\u4eb2\u4e8e\u6211\u6709\u6069\u3002\u201c\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u90a3\u662f\u53e6\u4e00\u56de\u4e8b\u3002\u4f60\u4eec\u65e2\u7136\u5408 \u4e0d\u6765\uff0c\u5c06\u6765\u5f7c\u6b64\u82e6\u607c\uff0c\u6768\u8001\u524d\u8f88\u6050\u4e5f\u4e0d\u4f1a\u5fc3\u5b89\u3002\u201d\u5510\u6653\u6f9c\u5fc3\u5f26\u8df3\u52a8\uff0c\u9053\uff1a\u201c\u59d0\u59d0\u8bf4\u7684\u662f\uff0c\u4f46 \u6211\u548c\u5979\u8ba2\u5a5a\u5df2\u591a\u5e74\u4e86\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u8ba2\u5a5a\u4e0d\u6bd4\u6210\u4eb2\u3002\u6210\u4eb2\u4e4b\u540e\uff0c\u82e5\u975e\u59bb\u5b50\u72af\u4e86\u4e03\u51fa\u4e4b\u6761\uff0c \u4e0d\u80fd\u4ee4\u4e4b\u4e0b\u5802\u3002\u4f46\u8ba2\u5a5a\u4e4b\u540e\u9000\u8058\uff0c\u53e4\u793c\u4ea6\u4e0d\u7981\u6b62\u3002\u54ce\uff0c\u6211\u53c8\u548c\u4f60\u8bf4\u8d77\u5112\u5bb6\u7684\u793c\u6cd5\u6765\u4e86\u3002\u5176\u5b9e \u7537\u5973\u7231\u5e55\uff0c\u53d1\u4e4e\u60c5\uff0c\u6b62\u4e4e\u793c\uff0c\u987a\u5176\u81ea\u7136\uff0c\u8c01\u4eba\u4e5f\u4e0d\u5e94\u8d23\u602a\u3002\u201d\u5510\u6653\u6f9c\u559c\u9053\uff1a\u201c\u59d0\u59d0\u771f\u662f\u901a\u60c5 \u8fbe\u7406\u4e4b\u4eba\u3002\u201d\u8fd9\u6570\u6708\u6765\u6240\u60f3\u4e0d\u901a\u4e4b\u4e8b\uff0c\u7ed9\u5415\u56db\u5a18\u4e00\u8a00\u70b9\u9192\uff0c\u4f46\u89c9\u5fc3\u80f8\u8212\u7545\uff0c\u559c\u60a6\u83ab\u540d\u3002 \u3000\u3000\u5415\u56db\u5a18\u53c8\u9053\uff1a\u201c\u4f60\u65e2\u7136\u8ba4\u5b9a\u548c\u6768\u59d1\u5a18\u5408\u4e0d\u6765\uff0c\u90a3\u4e48\u5c31\u4e0d\u5b9c\u62d6\u5ef6\u4e0b\u53bb\u3002\u201d\u5510\u6653\u6f9c\u7709\u5934\u4e00 \u76b1\uff0c\u60f3\u5230\u4e0d\u77e5\u8be5\u5982\u4f55\u5411\u6768\u4ef2\u82f1\u5f00\u53e3\uff0c\u53c8\u89c9\u5fc3\u70e6\u3002\u5415\u56db\u5a18\u5fae\u7b11\u9053\uff1a\u201c\u5f85\u86c7\u5c9b\u4e4b\u884c\u8fc7\u540e\uff0c\u4f60\u4eec\u56de \u5230\u5927\u9646\uff0c\u6211\u8bd5\u8bd5\u66ff\u4f60\u8bf4\u9879\u5427\u3002\u201d\u5510\u6653\u6f9c\u4f4e\u4f4e\u8bf4\u4e86\u58f0\uff1a\u201c\u8c22\u8c22\u3002\u201d\u5ffd\u7136\u60f3\u8d77\u4e86\u53e6\u4e00\u4ef6\u4e8b\uff0c\u7709\u5934 \u53c8\u76b1\u8d77\u6765\u3002\u5415\u56db\u5a18\u77a7\u4e86\u4ed6\u4e00\u773c\uff0c\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u60f3\u4e0d\u901a\u7684\u5462\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u95fb\u5f97\u5934\u9876\u4e0a\u7a7a\uff0c\u201c\u5431\u5431\u5431\uff0c\u6696\u560e\u560e\u201d\u4e00\u9635\u566a\u97f3\uff0c\u5341\u51e0\u53ea\u732b\u9e70\u5728\u6d77\u5c9b\u4e0a\u76d8\u65cb \u4f4e\u98de\uff0c\u500f\u5ffd\u98de\u53bb\u3002 \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u8fd9\u662f\u53cc\u9b54\u5e26\u6765\u7684\u732b\u9e70\uff0c\u60f3\u5fc5\u662f\u5bfb\u89c5\u5b83\u4eec\u7684\u4e3b\u4eba\u6765\u4e86\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u53c8\u4f4e\u58f0 \u95ee\u9053\uff1a\u201c\u957f\u5e7c\u4e0d\u540c\uff0c\u5c0a\u5351\u6709\u522b\uff0c\u53e4\u793c\u4e4b\u4e2d\u4e5f\u53ef\u901a\u878d\u4e48\uff1f\u201d\u5415\u56db\u5a18\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8bfb\u4e86\u51e0\u5e74 \u4e66\uff0c\u5012\u60f3\u505a\u5b54\u592b\u5b50\u7684\u95e8\u5f92\u4e86\u3002\u4f46\u5b54\u592b\u5b50\u4e5f\u672a\u8bf4\u8fc7\u5f02\u6027\u53d4\u4f84\u4e0d\u80fd\u8054\u5a5a\u7684\u5440\uff01\u73b0\u5728\u7684\u4e60\u4fd7\u4e16\u6cd5\u662f \u540c\u59d3\u4e0d\u5a5a\uff0c\u4f60\u548c\u5979\u53c8\u4e0d\u662f\u4ec0\u4e48\u771f\u6b63\u7684\u53d4\u4f84\uff0c\u6709\u4f55\u4e0d\u53ef\uff1f\u8bf4\u5230\u5e74\u9f84\u76f8\u5dee\uff0c\u90a3\u66f4\u4e0d\u6210\u95ee\u9898\u4e86\u3002\u4f60 \u548c\u5979\u76f8\u5dee\u591a\u5c11\uff1f\u54e6\uff0c\u662f\u5341\u4e94\u5e74\u5427\u3002\u53e4\u4eba\u4e91\uff1a\u7537\u5b50\u4e09\u5341\u800c\u5a36\uff0c\u5973\u5b50\u4e8c\u5341\u800c\u5ac1\uff0c\u53ef\u89c1\u76f8\u5dee\u5341\u5e74\u662f \u5f88\u5e73\u5e38\u7684\u4e8b\u3002\u90a3\u4e48\u5c31\u518d\u591a\u4e94\u5e74\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u6211\u4eec\u53e4\u8001\u7684\u533b\u4e66\u8bf4\u8fc7\uff1a\u5973\u5b50\u4e03\u4e03\u4e0d\u5b9c\u518d\u5a5a\uff0c\u7537\u5b50 \u516b\u516b\u4e0d\u5b9c\u518d\u5a36\uff0c\u90a3\u662f\u6839\u636e\u7537\u5973\u4f53\u8d28\u7684\u4e0d\u540c\u800c\u7acb\u8bba\u7684\u3002\u4e03\u4e03\u662f\u56db\u5341\u4e5d\u5c81\uff0c\u516b\u516b\u662f\u516d\u5341\u56db\u5c81\uff0c\u5176 \u95f4\u76f8\u5dee\u6b63\u597d\u662f\u5341\u4e94\u5e74\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u8305\u585e\u987f\u5f00\uff0c\u4f46\u201c\u9053\u7406\u201d\u867d\u8bf4\u5f97\u901a\u4e86\uff0c\u60f3\u8d77\u4eba\u60c5\u9762\u5b50\uff0c\u5fc3\u4e2d\u4ecd\u662f\u70e6\u4e71\u3002\u60f3\u4e86\u4e00\u60f3\uff0c \u5ffd\u7136\u53f9\u9053\uff1a\u201c\u53ea\u8981\u9053\u7406\u8bf4\u5f97\u8fc7\u53bb\uff0c\u6211\u505a\u7684\u4e0d\u662f\u9519\u4e8b\uff0c\u90a3\u4e48\u6211\u6b7b\u4e86\u4e5f\u53ef\u4ee5\u5b89\u5fc3\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u6b63\u5f53\u76db\u5e74\uff0c\u4e3a\u4f55\u8a00\u6b7b\uff01\u201d\u5ca9\u4e0b\u4eba\u5f71\u4e00\u95ea\uff0c\u51af\u745b\u94bb\u4e86\u51fa \u6765\uff0c\u539f\u6765\u5979\u8eb2\u5728\u4e0b\u9762\uff0c\u5df1\u5077\u542c\u591a\u65f6\uff0c\u5415\u56db\u5a18\u65e9\u5df2\u5bdf\u89c9\uff0c\u6545\u4f5c\u4e0d\u77e5\u3002\u89c1\u5979\u51fa\u6765\uff0c\u62db\u624b\u7b11\u9053\uff1a \u201c\u4e0a\u6765\u5440\uff01\u8fd9\u91cc\u770b\u6d77\u666f\u597d\u6781\u4e86\uff01\u4f60\u77a7\uff0c\u6d77\u666f\u591a\u7f8e\uff0c\u9e1f\u98de\u9c7c\u8dc3\uff0c\u751f\u610f\u76ce\u7136\u3002\u4f60\u7684\u53d4\u53d4\u548c\u6211\u8c08\u5230 \u6b7b\u4ea1\uff0c\u771f\u662f\u5927\u715e\u98ce\u666f\uff01\u201d \u3000\u3000\u51af\u745b\u9762\u86cb\u7ea2\u6251\u6251\u7684\uff0c\u773c\u89d2\u95ea\u6709\u6cea\u5149\uff0c\u62c9\u7740\u5415\u56db\u5a18\u7684\u624b\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u771f\u597d\uff01\u201d\u5415\u56db\u5a18\u5947 \u5f02\u7684\u770b\u7740\u5979\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u59ae\u5b50\u662f\u56e0\u559c\u6781\u800c\u6ce3\u5462\uff0c\u8fd8\u662f\u6709\u4ec0\u4e48\u611f\u89e6\u60b2\u4f24\uff1f\u201d\u53ea\u542c\u5f97\u51af\u745b\u98a4\u58f0 \u8bf4\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u89c1\u591a\u8bc6\u5e7f\uff0c\u6709\u4ec0\u4e48\u53ef\u6551\u5510\u53d4\u53d4\u7684\u5417\uff1f\u4ed6\u88ab\u7687\u5e1d\u6240\u9a97\uff0c\u5403\u4e86\u6bd2\u9152\uff0c\u6b7b\u671f\u771f\u7684 \u4e0d\u6ee1\u767e\u65e5\u4e86\uff01\u5415\u56db\u5a18\u60ca\u9053\uff1a\u201c\u771f\u6709\u6b64\u7b49\u4e8b\uff1f\u201d\u5510\u6653\u6f9c\u5c06\u60c5\u5f62\u8bf4\u4e86\u3002\u5415\u56db\u5a18\u6c89\u541f\u9053\uff1a\u201c\u66fe\u95fb\u70df \u7634\u5357\u8352\u4e4b\u5730\uff0c\u6709\u653e\u86ca\u4e4b\u4e8b\uff0c\u4ee5\u6bd2\u866b\u4e3a\u86ca\uff0c\u4e0b\u4e8e\u996e\u98df\u4e4b\u4e2d\uff0c\u4e2d\u86ca\u8005\u671f\u6ee1\u5373\u6b7b\uff0c\u671f\u9650\u6216\u957f\u6216\u77ed\uff0c \u81ea\u767e\u65e5\u4ee5\u81f3\u6570\u5e74\u5747\u6709\u3002\u4f46\u4ea6\u4ec5\u89c1\u8bf8\u4f20\u8bf4\u800c\u5df2\uff0c\u662f\u5426\u771f\u786e\uff0c\u5c1a\u672a\u53ef\u77e5\uff0c\u96be\u9053\u7687\u5e1d\u4e5f\u4f1a\u653e\u86ca \u5417\uff1f\u201d\u5979\u4e0d\u6562\u8f7b\u4fe1\uff0c\u4f46\u4ee5\u5141\u796f\u624b\u6bb5\u4e4b\u6bd2\uff0c\u53c8\u4e0d\u6562\u4e0d\u4fe1\u3002\u95ee\u9053\uff1a\u201c\u4f60\u5e73\u65e5\u547c\u5438\u4e4b\u95f4\uff0c\u53ef\u89c9\u6709\u4ec0 \u4e48\u5f02\u6837\u5417\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4e5f\u4e0d\u89c9\u6709\u4ec0\u4e48\u5f02\u6837\u3002\u6b7b\u751f\u7531\u547d\uff0c\u6211\u53ea\u6c42\u65e0\u6127\u4e8e\u5fc3\uff0c\u62cd\u624b\u800c\u53bb\uff0c\u53c8 \u6709\u4f55\u60e7\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u767e\u65e5\u4e4b\u5185\uff0c\u6211\u4eec\u5b9a\u53ef\u8d76\u5230\u4eac\u5e08\uff0c\u90a3\u65f6\u6211\u81ea\u6709\u529e\u6cd5\u3002\u201d\u8bf4\u5f97 \u751a\u4e3a\u7b03\u5b9a\u3002\u51af\u745b\u867d\u4e0d\u77e5\u5979\u7684\u529e\u6cd5\u4e3a\u4f55\uff0c\u4e5f\u662f\u5927\u4e3a\u9ad8\u5174\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u523b\uff0c\u767d\u6cf0\u5b98\u592b\u5987\u5bfb\u6765\uff0c\u53eb\u4ed6\u4eec\u540c\u8fdb\u65e9\u9910\uff0c\u51c6\u5907\u51fa\u6d77\u5bfb\u89c5\u86c7\u5c9b\u3002\u5927\u5bb6\u8c08\u8d77\u6bd2\u9f99\u5c0a \u8005\uff0c\u90fd\u89c9\u96be\u6597\u3002\u5415\u56db\u5a18\u7ec6\u7ec6\u67e5\u95ee\u4e86\u6bd2\u9f99\u5c0a\u8005\u7684\u6b66\u529f\uff0c\u6c89\u541f\u6709\u9877\uff0c\u5ffd\u9053\uff1a\u201c\u745b\u59b9\uff0c\u4f60\u6f14\u4e00\u8def\u5929 \u5c71\u5251\u6cd5\u6211\u770b\uff01\u201d\u51af\u745b\u628a\u5929\u5c71\u5251\u6cd5\u653b\u5b88\u5404\u8def\u4e09\u767e\u516d\u5341\u4e00\u79cd\u5251\u5f0f\u5168\u90fd\u6f14\u4e86\u51fa\u6765\uff0c\u5415\u56db\u5a18\u51dd\u795e\u89c2 \u770b\uff0c\u5f85\u5979\u6f14\u5b8c\u4e4b\u540e\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u53ef\u4ee5\u53bb\u4e86\uff01\u201d\u6b64\u4e00\u53bb\u4e5f\uff0c\u6709\u5206\u6559\uff1a \u3000\u3000\u5171\u65bd\u4f0f\u864e\u64d2\u9f99\u624b\uff0c\u70b9\u5316\u5929\u6daf\u6d77\u89d2\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u516d\u56de\u3000\u4ee5\u7231\u6d88\u4ec7\u3000\u9b54\u5934\u590d\u4eba\u6027\u3000\u4e3a\u670b\u5192\u9669\u3000\u4fa0\u5973\u5165\u4eac\u534e \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u516d\u56de \u4ee5\u7231\u6d88\u4ec7\u3000\u9b54\u5934\u590d\u4eba\u6027 \u4e3a\u670b\u5192\u9669\u3000\u4fa0\u5973\u5165\u4eac\u534e \u3000\u3000\u7518\u51e4\u6c60\u4e00\u5e2e\u4eba\u88ab\u56f0\u5728\u86c7\u5c9b\u4e0a\uff0c\u4e0d\u89c9\u6570\u6708\u3002\u8fd9\u6570\u6708\u6765\uff0c\u6bcf\u65e5\u65e9\u6f6e\u9000\u540e\uff0c\u6bd2\u9f99\u5c0a\u8005\u5c31\u6765\u548c\u4ed6 \u4eec\u53ae\u6740\u201c\u6e38\u620f\u201d\uff0c\u51e0\u4e4e\u6210\u4e86\u201c\u529f\u8bfe\u201d\u3002\u9c7c\u4eae\u770b\u7740\u79cb\u5c3d\u51ac\u6765\uff0c\u53c8\u770b\u7740\u96ea\u878d\u82b1\u5f00\uff0c\u60f3\u8d77\u81ea\u5df1\u7684\u6c34 \u5be8\u88ab\u6e05\u519b\u56f4\u653b\uff0c\u4e0d\u77e5\u5982\u4f55\uff1f\u66f4\u60f3\u8d77\u4e86\u5973\u513f\u5973\u5a7f\uff0c\u547d\u8fd0\u96be\u6d4b\uff1b\u5ea6\u65e5\u5982\u5e74\uff0c\u5341\u5206\u7126\u8e81\u3002 \u3000\u3000\u53ef\u559c\u7684\u662f\uff0c\u7ecf\u8fc7\u4e86\u8fd9\u6570\u6708\u53ae\u6740\uff0c\u5927\u5bb6\u7684\u6b66\u529f\u90fd\u63d0\u9ad8\u4e86\u4e0d\u5c11\uff0c\u6bcf\u65e5\u6218\u540e\uff0c\u7518\u51e4\u6c60\u90fd\u68c0\u8ba8\u5f97 \u5931\uff0c\u5e2e\u52a9\u5927\u5bb6\u7ec3\u6280\uff0c\u5e76\u6559\u4ee5\u653b\u5b88\u914d\u5408\u4e4b\u9053\uff0c\u5929\u5929\u7ec3\u4e60\u3002\u8fd9\u5341\u591a\u4eba\u4e2d\uff0c\u672c\u6765\u53ea\u6709\u7518\u51e4\u6c60\u80fd\u786c\u63a5 \u6bd2\u9f99\u5c0a\u8005\u638c\u529b\uff0c\u9c7c\u4eae\u3001\u536b\u626c\u5a01\u3001\u5b5f\u6b66\u529f\u80fd\u7528\u5175\u5668\u786c\u6321\u4e09\u4e24\u62db\uff0c\u6570\u6708\u540e\u5927\u5bb6\u90fd\u53ef\u7a0d\u4e3a\u62db\u67b6\u4e86\u3002 \u66f4\u52a0\u4e0a\u653b\u5b88\u914d\u5408\u5f97\u5b9c\uff0c\u6e10\u6e10\u6bcf\u6218\u90fd\u5360\u4e0a\u98ce\uff0c\u53ef\u662f\u4ecd\u4e0d\u80fd\u5236\u6bd2\u9f99\u5c0a\u8005\u6b7b\u547d\uff0c\u6bcf\u5230\u4ed6\u5c06\u9732\u8d25\u8c61\u4e4b \u9645\uff0c\u5c31\u7ed9\u4ed6\u5f3a\u529b\u51b2\u51fa\u3002\u4f17\u4eba\u4e5f\u66fe\u8bd5\u8fc7\u5206\u51fa\u4e00\u90e8\u5206\u4eba\u529b\u9020\u8239\uff0c\u53e6\u4e00\u90e8\u4efd\u62c5\u4efb\u8b66\u536b\uff0c\u53ef\u662f\u529b\u91cf\u4e00 \u5206\u4e4b\u540e\uff0c\u53c8\u4e0d\u80fd\u62b5\u5fa1\uff0c\u7ed3\u679c\u6240\u9020\u7684\u8239\u4ecd\u662f\u7ed9\u4ed6\u6253\u6210\u7c89\u788e\uff0c\u6beb\u65e0\u529e\u6cd5\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u65e9\u6f6e\u8fc7\u540e\uff0c\u6bd2\u9f99\u5c0a\u8005\u53c8\u6765\u6311\u6218\uff0c\u6fc0\u6218\u4e09\u767e\u56de\u5408\uff0c\u672a\u9732\u75b2\u6001\uff0c\u5929\u7a7a\u4e2d\u4f20\u6765\u4e86\u201c\u560e\u560e \u560e\u201d\u7684\u566a\u97f3\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u5341\u4f59\u53ea\u732b\u9e70\u6a2a\u6d77\u98de\u6765\uff0c\u7518\u51e4\u6c60\u9887\u4e3a\u8be7\u5f02\uff0c\u5fc3\u60f3\uff1a\u96be\u9053\u8fd9\u4e9b\u732b\u9e70\u53c8\u6765 \u548c\u7fa4\u86c7\u4f5c\u6218\uff1f\u732b\u9e70\u4e00\u51fa\u5fc5\u662f\u4e00\u5927\u7fa4\uff0c\u4e3a\u4ec0\u4e48\u8fd9\u6b21\u6765\u7684\u5374\u662f\u8fd9\u6837\u5c11\u4e86\u3002 \u3000\u3000\u732b\u9e70\u98de\u5230\u86c7\u5c9b\u4e0a\u7a7a\uff0c\u76d8\u65cb\u4e24\u531d\uff0c\u6709\u4e24\u53ea\u7279\u522b\u5927\u7684\u732b\u9e70\uff0c\u50cf\u662f\u5b83\u4eec\u7684\u5934\u9886\uff0c\u4f4e\u98de\u54c0\u9e23\uff0c\u5728 \u4f17\u4eba\u5934\u4e0a\u76d8\u65cb\u4e0d\u5df2\u3002\u6bd2\u9f99\u5c0a\u8005\u5ffd\u7136\u5927\u53eb\u4e00\u58f0\uff0c\u94c1\u62d0\u62a1\u56de\uff0c\u547c\u547c\u6570\u62d0\uff0c\u8361\u5f00\u4f17\u4eba\u5175\u5668\uff0c\u75be\u51b2\u51fa \u53bb\u3002\u4e00\u62db\u624b\uff0c\u90a3\u4e24\u53ea\u732b\u9e70\u505c\u5728\u4ed6\u7684\u80a9\u4e0a\uff0c\u9e70\u722a\u4e0a\u4f3c\u4e4e\u6293\u6709\u4e1c\u897f\u3002 \u3000\u3000\u8fd9\u5341\u51e0\u53ea\u732b\u9e70\u6b63\u662f\u53cc\u9b54\u5e26\u51fa\u6d77\u7684\u732b\u9e70\uff0c\u6700\u5927\u7684\u90a3\u4e24\u53ea\u66f4\u662f\u8428\u5929\u523a\u7684\u8001\u4f34\u3002\u8428\u5929\u523a\u4ee5\u524d\u5e38 \u5e38\u5e26\u5b83\u6765\u86c7\u5c9b\u627e\u5bfb\u6bd2\u9f99\u5c0a\u8005\uff0c\u6240\u4ee5\u6bd2\u9f99\u5c0a\u8005\u4e00\u89c1\u4fbf\u80fd\u8ba4\u5f97\u3002\u770b\u90a3\u732b\u9e70\u722a\u4e0a\uff0c\u6293\u7740\u4e00\u63e1\u6307\u7532\uff0c \u8fd8\u6293\u7740\u4e00\u7247\u8840\u6c61\u9ebb\u8863\u3002\u6bd2\u9f99\u5c0a\u8005\u89c1\u4e86\uff0c\u9762\u8272\u500f\u53d8\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u7684\u4e3b\u4eba\u88ab\u5bb3\u6b7b\u4e86\u5417\uff1f\u201d\u732b\u9e70\u4e0d \u61c2\u56de\u7b54\uff0c\u53ea\u662f\u201c\u560e\u560e\u560e\uff0c\u5431\u5431\u5431\u201d\u7684\u4e71\u53eb\u3002 \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u5728\u6d77\u6ee8\u89d2\u9685\u5f04\u9e70\uff0c\u4f17\u4eba\u8fdc\u671b\uff0c\u770b\u4e0d\u6e05\u732b\u9e70\u6293\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\u3002\u4f46\u89c1\u6bd2\u9f99\u5c0a\u8005\u5495\u565c \u7684\u8bf4\u4e86\u51e0\u58f0\uff0c\u53cc\u624b\u4e00\u653e\uff0c\u5927\u732b\u9e70\u4fbf\u5e26\u7740\u5c0f\u732b\u9e70\u79bb\u5c9b\u98de\u53bb\u3002\u6bd2\u9f99\u5c0a\u8005\u5ffd\u7136\u66b4\u6012\u8df3\u8d77\uff0c\u547c\u7684\u4e00 \u62d0\uff0c\u628a\u4e00\u5757\u5ca9\u77f3\u6253\u584c\u534a\u8fb9\uff0c\u5927\u53eb\u9053\uff1a\u201c\u597d\uff0c\u8ba9\u4f60\u4eec\u518d\u6d3b\u591a\u4e00\u65e5\uff0c\u660e\u65e5\u4e0d\u628a\u4f60\u4eec\u6740\u7edd\uff0c\u96be\u6d88\u6211 \u5fc3\u5934\u4e4b\u6068\uff01\u201d\u4e00\u8def\u6325\u821e\u94c1\u62d0\uff0c\u4e71\u6253\u6811\u6728\uff0c\u9000\u5165\u6797\u4e2d\u3002\u4f17\u4eba\u770b\u4e86\u4ed6\u90a3\u51f6\u795e\u6076\u715e\u7684\u6837\u513f\uff0c\u65e0\u4e0d\u5fc3 \u60ca\u80c6\u6218\u3002 \u3000\u3000\u536b\u626c\u5a01\u9053\uff1a\u201c\u771f\u662f\u90aa\u95e8\uff0c\u8fd9\u4e9b\u732b\u9e70\u4e0e\u6211\u4eec\u4f55\u5e72\uff1f\u4f55\u4ee5\u4ed6\u89c1\u4e86\u732b\u9e70\uff0c\u5bf9\u6211\u4eec\u53d1\u8fd9\u4e48\u5927\u7684\u813e \u6c14\uff1f\u201d\u5b5f\u6b66\u529f\u9053\uff1a\u201c\u6bd2\u9f99\u5c0a\u8005\u672c\u6765\u5c31\u4e0d\u662f\u4eba\uff0c\u4ed6\u5c31\u50cf\u6bd2\u86c7\u4e00\u6837\uff0c\u9022\u4eba\u4fbf\u556e\uff0c\u54b1\u4eec\u4e0d\u5fc5\u767d\u8d39\u5fc3 \u601d\u731c\u5ea6\u4ed6\u4e86\u201d\u3002\u60f3\u60f3\u660e\u65e5\u600e\u6837\u5e94\u4ed8\u4ed6\u5427\uff01\u201d\u7518\u51e4\u6c60\u4f4e\u9996\u6c89\u601d\uff0c\u60f3\u4ee5\u6b66\u529f\u5236\u80dc\uff0c\u5b9e\u4e0d\u53ef\u80fd\uff0c\u82e5 \u7136\u6bd2\u9f99\u5c0a\u8005\u771f\u4e0b\u6740\u624b\u6bd2\u62db\uff0c\u8fd9\u5341\u591a\u4eba\u4e2d\u96be\u4fdd\u65e0\u4eba\u4f24\u4ea1\u3002\u82e5\u4ed6\u66f4\u9a71\u4f7f\u86c7\u7fa4\u52a9\u6218\uff0c\u5c31\u8fde\u9003\u751f\u4e5f\u4e0d \u80fd\u591f\u4e86\u3002\u6414\u5934\u65e0\u8ba1\uff0c\u5ffd\u89c1\u9c7c\u4eae\u5728\u6d77\u6ee9\u4e0a\u8d70\u6765\u8d70\u53bb\uff0c\u671b\u7740\u6f6e\u6c34\u51fa\u795e\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u9c7c\u8001\u524d\u8f88\u53ef\u6709\u6cd5\u5b50\u53ef\u60f3\u4e48\uff1f\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u6211\u60f3\u90a3\u6bd2\u9f99\u5c0a\u8005\u81ea\u6043\u6b66\u529f\uff0c\u4e0d\u662f \u6597\u5230\u7b4b\u75b2\u529b\u7aed\u4e4b\u65f6\uff0c\u672a\u5fc5\u80af\u9a71\u7fa4\u86c7\u52a9\u6218\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u54b1\u4eec\u5728\u8fd9\u6d77\u5c9b\u4e0a\u63d2\u7ffc\u96be\u98de\uff0c\u4ed6\u4ec0\u4e48 \u65f6\u5019\u9a71\u51fa\u7fa4\u86c7\uff0c\u54b1\u4eec\u90fd\u662f\u6b7b\u8def\u4e00\u6761\u3002\u4f55\u51b5\u53ea\u4ed6\u4e00\u4eba\uff0c\u5df2\u96be\u5bf9\u4ed8\u3002\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u82e5\u5728\u4ed6 \u9a71\u51fa\u7fa4\u86c7\u4e4b\u524d\uff0c\u5c06\u4ed6\u6253\u5012\uff0c\u518d\u5bf9\u4ed8\u86c7\uff0c\u90a3\u5c31\u5bb9\u6613\u4e86\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6bd2\u9f99\u5c0a\u8005\u6b66\u529f\u8d85\u5353\uff0c\u9664 \u975e\u662f\u5929\u5c71\u7684\u6613\u5170\u73e0\u548c\u6b66\u743c\u7476\u4e24\u4f4d\u524d\u8f88\uff0c\u968f\u4fbf\u4e00\u4f4d\u5230\u6765\uff0c\u624d\u53ef\u5c06\u4ed6\u6536\u62fe\u3002\u9664\u4e86\u8fd9\u4e24\u4eba\uff0c\u5f53\u4eca\u4e4b \u4e16\uff0c\u8c01\u80fd\u662f\u4ed6\u654c\u624b\uff1f\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u4f60\u542c\u90a3\u6d77\u6d9b\u62cd\u5cb8\u4e4b\u58f0\uff0c\u6d77\u6c34\u4e4b\u529b\u603b\u6bd4\u4ed6\u5927\u5427\u3002\u201d\u7518\u51e4\u6c60 \u9053\uff1a\u201c\u6d77\u6c34\u4e4b\u529b\u5982\u4f55\u53ef\u7528\uff0c\u613f\u95fb\u826f\u7b56\u3002\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u4ed6\u5728\u4e2d\u5348\u65f6\u5206\uff0c\u4ece\u672a\u51fa\u73b0\u8fc7\uff0c\u54b1\u4eec\u5c31\u5229 \u7528\u8fd9\u70b9\u7a7a\u9699\uff0c\u505a\u4e00\u4e9b\u673a\u5173\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4ec0\u4e48\u673a\u5173\uff1f\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u5bb9\u6613\u5f97\u5f88\uff01\u8fd9\u5c0f\u5c9b\u4e0a\u6709 \u8bb8\u591a\u5de8\u7af9\uff0c\u54b1\u4eec\u65a9\u4e0b\u5341\u6761\u516b\u6761\uff0c\u6316\u901a\u5b54\u8282\uff0c\u88c5\u4e86\u5f00\u5173\uff0c\u704c\u6ee1\u6d77\u6c34\u2026\u2026\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u90a3\u4e0d \u50cf\u5c0f\u5b69\u5b50\u73a9\u7684\u6c34\u67aa\u4e00\u6837\u5417\uff1f\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u662f\u5440\u3002\u6211\u60f3\u8fd9\u6837\u7684\u6c34\u67aa\uff0c\u82e5\u662f\u51fa\u5176\u4e0d\u610f\uff0c\u9aa4\u7136\u53d1 \u5c04\uff0c\u4e00\u6761\u58ee\u6c49\uff0c\u90fd\u4f1a\u7ed9\u6c34\u529b\u649e\u5012\u3002\u5341\u6761\u516b\u6761\u4e00\u9f50\u55b7\u5c04\uff0c\u6bd2\u9f99\u5c0a\u8005\u4e5f\u4f1a\u683d\u4e2a\u7b4b\u6597\u3002\u54b1\u4eec\u5c06\u90a3\u4e2d \u7a7a\u7684\u5de8\u7af9\u704c\u6ee1\u6d77\u6c34\u4e4b\u540e\uff0c\u7528\u6d6e\u6c99\u6df9\u76d6\uff0c\u8fd9\u91cc\u7684\u6c99\u6ee9\u5f62\u5982\u659c\u5761\uff0c\u54b1\u4eec\u5f15\u4ed6\u5230\u4e2d\u592e\u51f9\u9677\u4e4b\u5730\uff0c\u7a81 \u7136\u53d1\u52a8\uff0c\u5229\u7528\u6c34\u9f99\u4e4b\u529b\u51b2\u626b\uff0c\u53ea\u8981\u4ed6\u4e00\u622e\u7b4b\u6597\uff0c\u9a6c\u4e0a\u7528\u91cd\u624b\u6cd5\u4f24\u4ed6\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4ed6\u5185\u5916 \u529f\u592b\u90fd\u767b\u5cf0\u9020\u6781\uff0c\u80fd\u4e0d\u80fd\u6210\uff0c\u5b9e\u672a\u53ef\u6599\uff0c\u4e0d\u8fc7\uff0c\u4e8b\u5230\u5982\u4eca\uff0c\u522b\u65e0\u4ed6\u6cd5\uff0c\u59d1\u4e14\u8bd5\u4ed6\u4e00\u8bd5\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u65e5\u6f6e\u6c34\u4e00\u9000\uff0c\u6bd2\u9f99\u5c0a\u8005\u53c8\u4ece\u6811\u6797\u4e2d\u8d70\u51fa\uff0c\u80cc\u540e\u8ddf\u7740\u4e00\u5927\u7fa4\u6bd2\u86c7\uff0c\u9ed1\u538b\u538b\u7684\u4e00\u5927\u7247\uff0c \u6015\u4e0d\u6709\u5343\u6761\u4e07\u6761\u3002\u7518\u51e4\u6c60\u53eb\u9053\uff1a\u201c\u7cdf\u4e86\uff0c\u54b1\u4eec\u8fd9\u6b21\u662f\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u4e86\u3002\u201d\u6bd2\u9f99\u5c0a\u8005\u64ae\u5507\u4e00 \u5578\uff0c\u7fa4\u86c7\u6e38\u5230\u6d77\u6ee9\uff0c\u7a81\u7136\u505c\u6b62\u524d\u8fdb\uff0c\u9996\u5c3e\u76f8\u8fde\uff0c\u6392\u6210\u5706\u9635\u3002\u5c31\u50cf\u521d\u6765\u4e4b\u65e5\u6240\u89c1\u7684\u90a3\u822c\u3002\u6bd2\u9f99 \u5c0a\u8005\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u4eca\u65e5\u6253\u6700\u540e\u4e00\u573a\uff0c\u6211\u8981\u6559\u4f60\u4eec\u6b7b\u5f97\u5fc3\u670d\u3002\u86c7\u513f\u5440\u86c7\u513f\uff0c\u5f85\u6211\u6253\u5b8c\u4e4b \u540e\uff0c\u518d\u8bf7\u4f60\u4eec\u5403\u65e9\u70b9\u3002\u201d\u94c1\u62d0\u4e00\u62a1\uff0c\u547c\u7684\u8dc3\u8d77\uff0c\u5411\u7518\u51e4\u6c60\u5f53\u5934\u4fbf\u626b\u3002 \u3000\u3000\u7518\u51e4\u6c60\u865a\u6321\u4e00\u62db\uff0c\u5411\u6d77\u8fb9\u75be\u8dd1\u3002\u6bd2\u9f99\u5c0a\u8005\u9053\uff1a\u201c\u5582\uff0c\u4f60\u6015\u4e86\u4e48\uff1f\u966a\u6211\u597d\u597d\u7684\u518d\u6253\u4e00\u67b6\uff0c \u7b49\u4e0b\u6211\u53ef\u53eb\u4f60\u6b7b\u5f97\u597d\u53d7\u4e00\u70b9\u3002\u201d\u7518\u51e4\u6c60\u628a\u624b\u4e00\u626c\uff0c\u4e09\u67c4\u5315\u9996\u5728\u4ed6\u8eab\u8fb9\u98de\u8fc7\uff0c\u6bd2\u9f99\u5c0a\u8005\u5927\u7b11 \u9053\uff1a\u201c\u8fd9\u79cd\u7834\u94dc\u70c2\u94c1\u5c82\u80fd\u4f24\u6211\uff1f\u201d\u62d0\u6756\u4e00\u632f\uff0c\u4e09\u67c4\u5315\u9996\u65ad\u4e3a\u516d\u622a\uff0c\u5de6\u638c\u547c\u7684\u5411\u9c7c\u4eae\u62cd\u53bb\uff0c\u9c7c \u4eae\u4e5f\u4e0d\u63a5\u62db\uff0c\u626d\u5934\u4fbf\u8d70\uff0c\u8dd1\u5230\u7518\u51e4\u6c60\u7684\u897f\u8fb9\uff0c\u8ddd\u79bb\u9887\u8fdc\uff0c\u6bd2\u9f99\u5c0a\u8005\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5206\u5f00\u66f4\u4e0d\u80fd \u62b5\u654c\u6211\u4e86\u3002\u54fc\uff0c\u54fc\uff0c\u4f60\u4eec\u5c45\u7136\u4e0d\u613f\u966a\u6211\u73a9\u6700\u540e\u4e00\u573a\uff0c\u771f\u771f\u53ef\u6076\uff01\u6211\u8981\u628a\u4f60\u4eec\u4e00\u4e2a\u4e2a\u6495\u88c2\u4e86\u5582 \u86c7\uff01\u201d\u94c1\u62d0\u62ab\u98ce\uff0c\u98de\u5954\u8ffd\u9010\uff0c\u9c7c\u4eae\u7b49\u5341\u591a\u4eba\u5728\u6c99\u6ee9\u4e0a\u4e71\u7a9c\u4e71\u8d70\uff0c\u5f85\u5f15\u5f97\u6bd2\u9f99\u5c0a\u8005\u5230\u4e86\u4e2d\u592e\u51f9 \u9677\u4e4b\u5730\uff0c\u7a81\u7136\u4e00\u58f0\u53f7\u4ee4\uff0c\u4f17\u4eba\u65e9\u8ba4\u6e05\u4e86\u505a\u597d\u8bb0\u53f7\u4e4b\u5904\uff0c\u7528\u811a\u4e00\u62e8\u6d6e\u6c99\uff0c\u5f00\u4e86\u201c\u6c34\u67aa\u201d\uff0c\u5341\u51e0 \u6761\u6c34\u67f1\u9f50\u5411\u6bd2\u9f99\u5c0a\u8005\u51b2\u53bb\u3002\u6bd2\u9f99\u5c0a\u8005\u731d\u4e0d\u53ca\u9632\uff0c\u88ab\u6c34\u529b\u4e00\u649e\uff0c\u4e00\u9635\u6655\u7729\uff0c\u773c\u775b\u7741\u4e0d\u5f00\uff0c\u6447\u6447 \u6b32\u5012\u3002\u7518\u51e4\u6c60\u4e58\u6b64\u65f6\u673a\uff0c\u98de\u8eab\u6025\u8fdb\uff0c\u65bd\u5c55\u5168\u529b\uff0c\u556a\u7684\u4e00\u638c\u51fb\u4e0b\uff0c\u6bd2\u9f99\u5c0a\u8005\u80a9\u5934\u4e00\u7f29\uff0c\u8fd9\u4e00\u638c \u7ed3\u7ed3\u5b9e\u5b9e\u7684\u6253\u4e2d\u4e86\u4ed6\u7684\u540e\u5fc3\u8981\u5bb3\u3002\u6bd2\u9f99\u5c0a\u8005\u5927\u53eb\u4e00\u58f0\uff0c\u7ffb\u8eab\u4fbf\u5012\uff01 \u3000\u3000\u7518\u51e4\u6c60\u638c\u529b\u6709\u6d1e\u7a7f\u725b\u8179\u3001\u788e\u88c2\u5c71\u77f3\u4e4b\u80fd\uff0c\u53f3\u638c\u51fb\u4e2d\uff0c\u5de6\u638c\u53c8\u8d77\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u9c7c\u4eae \u536b\u626c\u5a01\u7b49\u4eba\u4e5f\u5230\uff0c\u9c7c\u4eae\u4e00\u5200\u65a9\u4e0b\uff0c\u6bd2\u9f99\u5c0a\u8005\u7a81\u7136\u5927\u543c\u4e00\u58f0\uff0c\u633a\u80a9\u4e00\u649e\uff0c\u7518\u51e4\u6c60\u5de6\u638c\u672a\u843d\uff0c\u5df2 \u7ed9\u4ed6\u51cc\u7a7a\u629b\u8d77\uff0c\u4f17\u4eba\u7eb7\u7eb7\u8d70\u907f\uff0c\u9c7c\u4eae\u90a3\u5200\u65ab\u4e2d\u4ed6\u7684\u811a\u8e1d\uff0c\u548b\u5693\u4e00\u58f0\uff0c\u5200\u950b\u5012\u5377\uff0c\u4e5f\u7ed9\u4ed6\u7684\u53cd \u529b\u9707\u5012\u3002\u6bd2\u9f99\u5c0a\u8005\u8df3\u4e86\u8d77\u6765\uff0c\u5927\u559d\u9053\uff1a\u201c\u9f20\u8f88\u6562\u65bd\u6697\u7b97\uff01\u201d\u64ae\u5507\u4e00\u5578\uff0c\u540e\u9762\u86c7\u7fa4\u7a81\u5982\u4e07\u7bad\u9f50 \u53d1\uff0c\u51b2\u4e86\u4e0a\u6765\uff01\u6bd2\u9f99\u5c0a\u8005\u6447\u6447\u6643\u6643\u9000\u4e86\u51fa\u53bb\uff0c\u76d8\u819d\u5750\u5728\u4e00\u5757\u5927\u5ca9\u77f3\u4e0a\uff0c\u4e0d\u65f6\u53d1\u51fa\u4f4e\u4f4e\u5618\u53eb\u4e4b \u58f0\u3002 \u3000\u3000\u7518\u51e4\u6c60\u7ed9\u4ed6\u4e00\u6454\uff0c\u8fd0\u6c14\u62a4\u8eab\uff0c\u5728\u534a\u7a7a\u4e00\u4e2a\u7b4b\u6597\uff0c\u6d88\u4e86\u6076\u52b2\uff0c\u9976\u662f\u5982\u6b64\uff0c\u8dcc\u4e0b\u5730\u65f6\uff0c\u4ecd\u662f \u5934\u6655\u773c\u82b1\uff0c\u547c\u5438\u51e0\u4e4e\u7a92\u606f\uff0c\u5e78\u4ed6\u529f\u529b\u6df1\u6e5b\uff0c\u53ea\u662f\u7ed9\u6bd2\u9f99\u5c0a\u8005\u731d\u52b2\u6240\u629b\uff0c\u672a\u53d7\u5185\u4f24\u3002\u7fa4\u86c7\u6251 \u81f3\uff0c\u4ed6\u5df2\u548c\u9c7c\u4eae\u9000\u5b88\u5185\u7ebf\uff0c\u548c\u4f17\u4eba\u4e00\u5904\u8054\u9632\u3002 \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u76d8\u819d\u8fd0\u6c14\uff0c\u80f8\u53e3\u4f5c\u95f7\uff0c\u4ea6\u662f\u4e0d\u7981\u9a87\u7136\u3002\u8fd0\u6c14\u6570\u8f6c\uff0c\u7741\u773c\u4e00\u77a7\uff0c\u53ea\u89c1\u7fa4\u86c7\u867d\u5982\u6f6e \u6c34\u80a1\u6d8c\u53bb\uff0c\u4f46\u654c\u4eba\u4e2a\u4e2a\u90fd\u662f\u9ad8\u624b\uff0c\u6392\u6210\u65b9\u9635\uff0c\u5200\u5288\u6756\u6253\uff0c\u5251\u65a9\u624b\u6495\uff0c\u6bd2\u86c7\u6b7b\u4ea1\u65e0\u6570\uff0c\u6709\u4e9b\u5c0f \u86c7\u4e14\u5df2\u9000\u51fa\u86c7\u9635\u9003\u8d70\u3002\u6bd2\u9f99\u5c0a\u8005\u7a81\u7136\u7ad9\u8d77\uff0c\u5618\u58f0\u602a\u53eb\uff0c\u624b\u821e\u94c1\u62d0\uff0c\u51b2\u5165\u9635\u4e2d\u3002\u7fa4\u86c7\u88ab\u4ed6\u4e00 \u903c\uff0c\u53c8\u518d\u7fa4\u96c6\u5411\u524d\uff0c\u7518\u51e4\u6c60\u5927\u5403\u4e00\u60ca\uff0c\u60f3\u4e0d\u5230\u6bd2\u9f99\u5c0a\u8005\u5728\u53d7\u4e86\u81ea\u5df1\u4e00\u638c\u4e4b\u540e\uff0c\u5c45\u7136\u8fd8\u52c7\u731b\u5982 \u65af\uff01 \u3000\u3000\u8fd9\u4e00\u6765\u5f62\u52bf\u5927\u53d8\uff0c\u4f17\u4eba\u65e2\u8981\u9632\u5907\u6bd2\u86c7\uff0c\u53c8\u8981\u62b5\u5fa1\u5f3a\u654c\uff0c\u9635\u52bf\u5927\u4e71\u3002\u201d\u6b63\u5f53\u7d27\u6025\u4e4b\u9645\uff0c\u6d77 \u9762\u5ffd\u4f20\u6765\u6e05\u8106\u7684\u5578\u58f0\u3002\u7518\u51e4\u6c60\u4fa7\u8033\u4e00\u542c\uff0c\u9762\u6709\u559c\u8272\uff0c\u53d1\u58f0\u76f8\u548c\u3002\u6bd2\u9f99\u5c0a\u8005\u9a82\u9053\uff1a\u201c\u4f60\u6363\u4ec0\u4e48 \u9b3c\uff1f\u201d\u4e00\u62d0\u51fb\u53bb\uff0c\u7518\u51e4\u6c60\u6325\u52a8\u5b9d\u5200\uff0c\u4fa7\u8eab\u4e00\u6321\uff0c\u9c7c\u4eae\u4e5f\u4e00\u8dc3\u800c\u524d\uff0c\u6a2a\u5200\u75be\u5288\uff0c\u6bd2\u9f99\u5c0a\u8005\u6012 \u9053\uff1a\u201c\u4f60\u8fd9\u8001\u513f\u4e5f\u53ef\u6076\u5f97\u7d27\uff01\u201d\u53f3\u62d0\u4e00\u632f\uff0c\u5c06\u7518\u51e4\u6c60\u683c\u9000\u6570\u6b65\uff0c\u5de6\u638c\u4e00\u524a\uff0c\u540c\u65f6\u8fdb\u62db\uff0c\u638c\u98ce \u5982\u5200\uff0c\u6a2a\u5288\u9c7c\u4eae\u624b\u8155\u3002\u9c7c\u4eae\u53eb\u58f0\u201c\u4e0d\u597d\uff01\u201d\u6025\u95ea\u907f\u65f6\uff0c\u624b\u8155\u4e00\u75db\uff0c\u8170\u5200\u98de\u4e0a\u534a\u7a7a\uff0c\u536b\u626c\u5a01\u5b5f \u6b66\u529f\u53cc\u53cc\u62a2\u6551\uff0c\u6bd2\u9f99\u5c0a\u8005\u94c1\u62d0\u4e00\u5c01\uff0c\u62e6\u7740\u53bb\u8def\uff0c\u5618\u53eb\u4e00\u58f0\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e24\u6761\u5927\u86c7\uff0c\u84e6 \u7136\u7a9c\u8d77\uff0c\u7f20\u7740\u9c7c\u4eae\u8eab\u5b50\u3002\u7518\u51e4\u6c60\u6d51\u5200\u6025\u65ab\u6bd2\u86c7\uff0c\u6bd2\u9f99\u5c0a\u8005\u94c1\u62d0\u5c55\u5f00\uff0c\u5c06\u7518\u51e4\u6c60\u7684\u5b9d\u5200\u4e5f\u88f9\u5728 \u6756\u529b\u5708\u5185\uff0c\u51b2\u4e0d\u8fc7\u53bb\u3002\u9c7c\u4eae\u53cc\u624b\u5404\u53c9\u86c7\u9888\uff0c\u62fc\u547d\u5f80\u5916\u6495\u62c9\uff0c\u5f62\u52bf\u9669\u6781\uff01\u6bd2\u9f99\u5c0a\u8005\u78d4\u78d4\u602a\u7b11\uff0c \u62db\u6570\u50ac\u7d27\uff0c\u731b\u7136\u4e00\u559d\u9053\uff1a\u201c\u4eca\u65e5\u53eb\u4f60\u4eec\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff01\u201d\u62d0\u631f\u52b2\u98ce\uff0c\u547c\u547c\u4e24\u62d0\uff0c\u5b5f\u6b66\u529f\u7684\u5355 \u5200\u548c\u536b\u626c\u5a01\u7684\u53cc\u94a9\u5168\u7ed9\u9707\u98de\uff0c\u5927\u7b11\u58f0\u4e2d\uff0c\u6bd2\u9f99\u5c0a\u8005\u53c8\u662f\u4e00\u62d0\u5411\u7518\u51e4\u6c60\u51fb\u4e0b\uff01 \u3000\u3000\u6d77\u4e0a\u5578\u58f0\u53c8\u8d77\uff0c\u7518\u51e4\u6c60\u529b\u654c\u4e09\u62db\uff0c\u53ea\u542c\u5f97\u6bd2\u9f99\u5c0a\u8005\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u4e0d\u77e5\u6b7b\u6d3b\uff0c\u5c45\u7136\u8fd8\u6562 \u6765\u95ef\u6211\u7684\u5b9d\u5c9b\uff1f\u201d\u5c0f\u821f\u6cca\u5ca9\u4e4b\u58f0\u672a\u505c\uff0c\u821f\u4e2d\u5df2\u7a9c\u8d77\u4e94\u6761\u4eba\u5f71\uff01 \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u9886\u5148\u7684\u5c11\u5973\u5feb\u5982\u75be\u98ce\uff0c\u970e\u773c\u4e4b\u95f4\u6765\u5230\u8ddf\u524d\uff0c\u6bd2\u9f99\u5c0a\u8005\u820d\u4e86\u7518\u51e4 \u6c60\uff0c\u4e00\u62d0\u51fb\u53bb\uff0c\u7a81\u89c9\u51b7\u6c14\u68ee\u68ee\uff0c\u76f4\u6251\u5934\u9762\uff0c\u64a4\u638c\u4e0d\u53ca\uff0c\u53cd\u624b\u4e00\u63a8\uff0c\u9000\u540e\u6570\u6b65\uff0c\u5934\u9876\u7684\u84ec\u84ec\u4e71 \u53d1\u5df2\u7ed9\u5251\u5149\u524a\u5f97\u7eb7\u98de\uff0c\u523a\u773c\u6cbe\u88f3\uff0c\u597d\u4e0d\u96be\u53d7\u3002\u53ea\u542c\u5f97\u7518\u51e4\u6c60\u53eb\u9053\uff1a\u201c\u516b\u59b9\uff0c\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u628a \u8fd9\u5996\u4eba\u5148\u6740\u6389\u518d\u8bf4\uff01\u201d\u63a5\u7740\u5ffd\u53c8\u542c\u5f97\u7fa4\u86c7\u5618\u53eb\u9003\u8dd1\u4e4b\u58f0\uff01 \u3000\u3000\u6765\u7684\u6b63\u662f\u5415\u56db\u5a18\u3001\u767d\u6cf0\u5bab\u3001\u9c7c\u5a18\u3001\u5510\u6653\u6f9c\u3001\u51af\u745b\u7b49\u4e94\u4eba\u3002\u5415\u56db\u5a18\u6765\u5f97\u6700\u5feb\uff0c\u89c1\u9762\u4e00\u62db\uff0c \u4fbf\u89e3\u4e86\u7518\u51e4\u6c60\u4e4b\u5371\u3002\u767d\u6cf0\u5b98\u548c\u9c7c\u5a18\u53bb\u63f4\u6551\u9c7c\u4eae\uff0c\u9c7c\u4eae\u6b63\u5728\u5403\u7d27\uff0c\u5ffd\u89c9\u538b\u529b\u4e00\u677e\uff0c\u53ea\u542c\u5f97\u5973\u513f \u5728\u8033\u8fb9\u53eb\u9053\uff1a\u201c\u7239\uff0c\u86c7\u5df2\u7ed9\u65a9\u6389\u4e86\uff01\u201d\u9c7c\u4eae\u559c\u6781\u53eb\u9053\uff1a\u201c\u9c7c\u5a18\uff0c\u662f\u4f60\u6765\u4e86\uff01\u201d\u628a\u5973\u513f\u63fd\u5165\u6000 \u4e2d\u3002\u767d\u6cf0\u5b98\u53eb\u9053\uff1a\u201c\u5cb3\u4e08\u5c0f\u5fc3\uff0c\u8fd8\u6709\u86c7\u6765\uff01\u201d\u63d0\u5200\u5288\u4e86\u4e24\u6761\uff0c\u9c7c\u4eae\u795e\u667a\u4e00\u6e05\uff0c\u6025\u677e\u5f00\u624b\uff0c\u5ffd \u89c1\u7fa4\u86c7\u5618\u5618\u602a\u53eb\uff0c\u5411\u540e\u5954\u9003\u3002 \u3000\u3000\u4f60\u9053\u7fa4\u86c7\u4f55\u4ee5\u9003\u8dd1\uff1f\u539f\u6765\u5c31\u5728\u767d\u6cf0\u5b98\u592b\u5987\u53bb\u63f4\u6551\u9c7c\u4eae\u4e4b\u65f6\uff0c\u5510\u6653\u6f9c\u548c\u51af\u745b\u4e5f\u53bb\u63f4\u6551\u88ab\u7fa4 \u86c7\u6251\u653b\u7684\u536b\u6768\u5a01\u7b49\u4eba\uff0c\u7fa4\u86c7\u89c1\u6709\u751f\u4eba\u5230\u6765\uff0c\u7eb7\u7eb7\u6251\u4e0a\u53bb\u54ac\uff0c\u521a\u521a\u63a5\u8fd1\u5510\u6653\u6f9c\u8eab\u8fb9\uff0c\u5ffd\u7136\u4f3c\u78b0 \u5230\u4ec0\u4e48\u53ef\u6015\u7684\u602a\u7269\u4f3c\u7684\uff0c\u6389\u5934\u4fbf\u9003\uff0c\u5f04\u5f97\u5510\u6653\u6f9c\u4e5f\u83ab\u540d\u5947\u5999\uff0c\u539f\u6765\u516b\u81c2\u795e\u9b54\u8428\u5929\u523a\u4e34\u6b7b\u524d\u9001 \u7ed9\u51af\u745b\u7684\u90a3\u9897\u836f\u7403\uff0c\u4e43\u662f\u7528\u732b\u9e70\u7684\u53e3\u6d8e\u6df7\u4e86\u8708\u86a3\u672b\u548c\u96c4\u9ec4\u7cbe\u7b49\u5236\u70bc\u800c\u6210\uff0c\u732b\u9e70\u6d8e\u662f\u514b\u6bd2\u86c7\u81f3 \u5b9d\uff0c\u66f4\u52a0\u4e0a\u8708\u86a3\u672b\u548c\u96c4\u9ec4\u7cbe\uff0c\u5389\u5bb3\u975e\u51e1\uff0c\u6bd2\u86c7\u53ea\u8981\u95fb\u5230\u90a3\u80a1\u81ed\u5473\uff0c\u4fbf\u7acb\u523b\u6d88\u5931\u6597\u5fd7\uff0c\u95fb\u98ce\u8fdc \u907f\u3002\u5f53\u65e5\u53cc\u9b54\u4e4b\u6562\u5230\u86c7\u5c9b\u62dc\u8bbf\u6bd2\u9f99\u5c0a\u8005\uff0c\u4fbf\u662f\u4ed7\u7740\u8eab\u4e0a\u6709\u8fd9\u79cd\u514b\u5236\u6bd2\u86c7\u7684\u731b\u836f\u3002 \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u89c1\u7fa4\u86c7\u9003\u8dd1\uff0c\u9aa4\u7136\u95f4\u8fd8\u60f3\u4e0d\u8d77\u6765\uff0c\u5927\u58f0\u547c\u559d\uff0c\u7fa4\u86c7\u65e0\u4e00\u542c\u4ee4\u3002\u6bd2\u9f99\u5c0a\u8005\u5fc3\u4e2d\u4e00 \u9707\uff0c\u987e\u4e0d\u5f97\u518d\u548c\u5415\u56db\u5a18\u7f20\u6597\uff0c\u63d0\u6756\u51b2\u51fa\u3002\u5510\u6653\u6f9c\u7eb5\u8eab\u4e0a\u6765\uff0c\u6bd2\u9f99\u5c0a\u8005\u7ad9\u5728\u4e0b\u98ce\uff0c\u98ce\u4e2d\u9001\u6765\u836f \u4e38\u7684\u81ed\u5473\uff0c\u6bd2\u9f99\u5c0a\u8005\u5927\u5403\u4e00\u60ca\uff0c\u5f53\u5934\u4e00\u62d0\uff0c\u51af\u745b\u98de\u8eab\u8dc3\u8d77\uff0c\u51cc\u7a7a\u4e0b\u523a\uff0c\u5929\u5c71\u53cc\u5251\uff0c\u653b\u5b88\u9f50 \u65bd\uff0c\u6bd2\u9f99\u5c0a\u8005\u672a\u80fd\u5f97\u624b\u3002\u5415\u56db\u5a18\u53eb\u9053\uff1a\u201c\u6bd2\u9f99\u524d\u8f88\uff0c\u4f60\u5df2\u7d2f\u4e86\uff0c\u8ba9\u4f60\u6b47\u606f\uff0c\u518d\u6765\u6597\u5427\uff01\u201d\u6bd2 \u9f99\u5c0a\u8005\u602a\u53eb\u4e00\u58f0\uff0c\u594b\u62d0\u628a\u53cc\u5251\u8361\u5f00\uff0c\u8dd1\u5230\u6d77\u89d2\u7684\u5c0f\u5c71\u4e0a\uff0c\u84e6\u7136\u6293\u7740\u4e86\u4e00\u6761\u9003\u8dd1\u7684\u8179\u86c7\uff0c\u6495\u88c2 \u86c7\u8179\uff0c\u53e3\u5438\u86c7\u8840\u3002\u88ab\u524a\u65ad\u4e00\u534a\u7684\u77ed\u53d1\uff0c\u6839\u6839\u7ad6\u7acb\u3002\u4ed6\u867d\u7136\u8d25\u9635\u800c\u9003\uff0c\u4f46\u90a3\u80a1\u51f6\u795e\u6076\u715e\u7684\u6a21 \u6837\uff0c\u4f17\u4eba\u770b\u4e86\uff0c\u65e0\u4e0d\u5bd2\u5fc3\u3002 \u3000\u3000\u5415\u56db\u5a18\u521a\u624d\u90a3\u5251\uff0c\u4e43\u662f\u4e58\u5176\u4e0d\u610f\uff0c\u9aa4\u7136\u53d1\u96be\uff0c\u4ee5\u7edd\u9876\u7684\u8f7b\u529f\uff0c\u914d\u4e0a\u6700\u4e0a\u4e58\u7684\u5251\u6cd5\uff0c\u8fd9\u624d \u80fd\u5f97\u624b\u3002\u53ef\u662f\u5415\u56db\u5a18\u53d7\u4ed6\u638c\u529b\u6240\u63a8\uff0c\u5982\u53d7\u5de8\u538b\uff0c\u4e5f\u77e5\u4ed6\u529f\u529b\u786e\u6bd4\u81ea\u5df1\u9ad8\u5f97\u591a\u3002 \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u516b\u59b9\uff0c\u5e78\u5f97\u4f60\u6765\uff0c\u8981\u4e0d\u7136\u6211\u4eec\u4eca\u65e5\u90fd\u846c\u8eab\u86c7\u8179\u3002\u90a3\u4e9b\u6bd2\u86c7\u4e5f\u602a\uff0c\u89c1\u4e86\u4f60\u4eec \u4fbf\u9003\uff0c\u4e0d\u77e5\u662f\u4f55\u7f18\u6545\uff1f\u83ab\u975e\u662f\u6b64\u602a\u6c14\u6570\u5f53\u5c3d\uff0c\u4e0a\u5929\u4fdd\u4f30\u6211\u4eec\u4e48\uff1f\u4e58\u4ed6\u75b2\u5026\uff0c\u54b1\u4eec\u5408\u529b\u6740\u4e86\u4ed6 \u5427\uff01\u201d\u5415\u56db\u5a18\u5fae\u7b11\u9053\uff1a\u201c\u4e03\u54e5\uff0c\u4f60\u662f\u6c5f\u5357\u7684\u6b66\u6797\u9886\u8896\uff0c\u5c82\u4e0d\u95fb\u4e58\u654c\u4e4b\u5f0a\uff0c\u867d\u80dc\u4e0d\u6b66\u4e48\uff1f\u201d\u7518 \u51e4\u6c60\u4e00\u6115\u8bf4\u9053\uff1a\u201c\u6b64\u4eba\u81ea\u7edd\u4e8e\u4eba\u7c7b\uff0c\u548c\u4ed6\u8fd8\u8c08\u4ec0\u4e48\u6b66\u6797\u89c4\u77e9\uff1f\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u65e0\u81ea\u7edd \u4e8e\u4eba\u7c7b\u4e4b\u4eba\uff0c\u54b1\u4eec\u8981\u53eb\u4ed6\u8d25\u4e5f\u8d25\u5f97\u5fc3\u670d\u3002\u201d\u7518\u51e4\u6c60\u5bf9\u8fd9\u4f4d\u5e08\u59b9\u7d20\u6765\u656c\u4f69\uff0c\u95fb\u8a00\u867d\u5c1a\u4e0d\u4ee5\u4e3a \u7136\uff0c\u5374\u4e5f\u4e0d\u518d\u53cd\u5bf9\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u6bd2\u9f99\u5c0a\u8005\u8fde\u6495\u4e86\u4e09\u6761\u876e\u86c7\uff0c\u9971\u559d\u86c7\u8840\uff0c\u5728\u5ca9\u77f3\u4e0a\u76d8\u819d\u9759\u5750\uff0c\u4f3c\u4e4e\u662f\u9ed8\u8fd0\u5185\u52a8\uff0c\u57f9 \u517b\u6c14\u529b\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6b64\u65f6\u4e0d\u9664\u4ed6\uff0c\u7b49\u4e0b\u5b9a\u6709\u9ebb\u70e6\u3002\u201d\u5415\u56db\u5a18\u628a\u51af\u745b\u62c9\u5728\u8eab\u8fb9\uff0c\u548c\u5979\u4f4e\u58f0\u8c08 \u8bba\u3002\u5176\u4f59\u7684\u4eba\uff0c\u90fd\u5c4f\u4e86\u547c\u5438\uff0c\u6ce8\u89c6\u7740\u6bd2\u9f99\u5c0a\u8005\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6bd2\u9f99\u5c0a\u8005\u53c8\u78d4\u78d4\u602a\u7b11\uff0c\u62fe\u4e86\u94c1\u62d0\uff0c\u98de\u8eab\u7eb5\u8d77\uff0c\u8df3\u5230\u6d77\u6ee9\uff0c\u626c\u58f0\u53eb\u9053\uff1a\u201c\u662f\u8c01 \u6740\u4e86\u53cc\u9b54\uff0c\u62a2\u4e86\u4ed6\u7684\u5b9d\u7269\uff1f\u201d\u51af\u745b\u548c\u5510\u6653\u6f9c\u5e94\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u6211\u4eec\u6740\u7684\uff01\u81f3\u4e8e\u4ec0\u4e48\u5b9d\u7269\uff0c\u6211\u4eec \u53ef\u6ca1\u89c1\u8fc7\uff01\u201d \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u7a81\u7136\u7eb5\u58f0\u957f\u53f9\uff0c\u94c1\u62d0\u987f\u5730\uff0c\u94ee\u94ee\u6709\u58f0\uff0c\u6447\u9996\u6307\u7740\u51af\u745b\u9a82\u5230\uff1a\u201c\u770b\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c \u8c8c\u7f8e\u5982\u82b1\uff0c\u5374\u5fc3\u5982\u86c7\u874e\uff01\u53cc\u9b54\u6015\u4f60\u4eec\u846c\u58f0\u9c7c\u8179\uff0c\u51fa\u6d77\u5bfb\u4f60\uff0c\u4f60\u5374\u5c06\u4ed6\u6740\u4e86\uff01\u54fc\uff0c\u54fc\uff0c\u4f60\u4eec\u8fd9 \u4e9b\u4eba\u54ea\uff0c\u771f\u662f\u6bd4\u6211\u7684\u6bd2\u86c7\u8fd8\u51f6\u3002\u53d7\u6211\u4e00\u6756\uff01\u201d\u5415\u56db\u5a18\u53eb\u9053\uff1a\u201c\u4f60\u60f3\u548c\u8fd9\u4f4d\u5c0f\u59d1\u5a18\u5355\u6253\u72ec\u6597 \u5417\uff1f\u201d\u6bd2\u9f99\u5c0a\u8005\u6012\u9053\uff1a\u201c\u4f60\u4eec\u4e00\u9f50\u4e0a\u6765\uff0c\u6211\u4e0d\u7528\u6bd2\u86c7\uff0c\u4e5f\u80fd\u6740\u5f97\u4f60\u4eec\uff01\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u8fd9 \u4f4d\u59d1\u5a18\u662f\u6211\u7684\u5c0f\u59b9\u59b9\uff0c\u6211\u53ef\u4e0d\u80fd\u8ba9\u5979\u53d7\u4f60\u6b3a\u8d1f\uff0c\u597d\u5427\uff0c\u6211\u5c31\u966a\u5979\u548c\u4f60\u8d70\u4e0a\u51e0\u62db\uff01\u201d \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u516b\u59b9\uff0c\u53ef\u5f97\u5c0f\u5fc3\u3002\u201d\u5510\u6653\u6f9c\u4e5f\u9053\uff1a\u201c\u745b\u59b9\uff0c\u4f60\u82e5\u5403\u7d27\uff0c\u4fbf\u4f11\u604b\u6218\u3002\u201d\u5415\u56db \u5a18\u548c\u51af\u745b\u90fd\u9053\uff1a\u201c\u6211\u6653\u5f97\u3002\u201d\u5404\u4eae\u5b9d\u5251\uff0c\u5e76\u80a9\u4e00\u7ad9\uff0c\u7acb\u597d\u95e8\u6237\uff0c\u7b49\u5019\u6bd2\u9f99\u5c0a\u8005\u6765\u653b\u3002 \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u770b\u4e86\u4e24\u4eba\u4e00\u773c\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5927\u7684\u6b66\u529f\u6700\u9ad8\uff0c\u6211\u5148\u628a\u5979\u6740\u4e86\u3002\u90a3\u5c0f\u7684\u81ea\u9003\u4e0d \u6389\u3002\u201d\u62d0\u6756\u4e00\u8d77\uff0c\u4e00\u62db\u201c\u5927\u9e4f\u5c55\u7fc5\u201d\uff0c\u62d0\u6756\u547c\u631f\u52b2\u98ce\uff0c\u5411\u5415\u56db\u5a18\u62e6\u8170\u6025\u626b\u3002 \u3000\u3000\u5415\u56db\u5a18\u9053\uff1a\u201c\u597d\uff01\u201d\u970d\u5730\u6643\u8eab\u4e0a\u8df3\uff0c\u94c1\u62d0\u5728\u5979\u811a\u4e0b\u4e00\u63a0\u800c\u8fc7\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u51af\u745b\u7684 \u77ed\u5251\u4e00\u6307\uff0c\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u75be\u5982\u7535\u95ea\uff0c\u731b\u70b9\u654c\u4eba\u547d\u95e8\u8981\u7a74\uff0c\u5415\u56db\u5a18\u8eab\u5b50\u60ac\u7a7a\uff0c\u62db\u6570\u4e5f\u6025\uff0c\u4e00 \u62db\u201c\u9e4f\u640f\u4e5d\u9704\u201d\uff0c\u51cc\u7a7a\u4e0b\u523a\u3002\u53cc\u5251\u4e00\u4e0a\u4e00\u4e0b\uff0c\u540c\u65f6\u523a\u5230\uff0c\u597d\u4e0d\u5389\u5bb3\u3002\u6bd2\u9f99\u5c0a\u8005\u5927\u543c\u4e00\u58f0\uff0c\u4e0d \u5f85\u5c06\u6756\u62bd\u56de\uff0c\u53ea\u662f\u968f\u624b\u4e00\u6296\uff0c\u90a3\u94c1\u62d0\u7adf\u7136\u76f4\u5f39\u8d77\u6765\uff0c\u6539\u6a2a\u626b\u548c\u4e0a\u6233\uff0c\u6756\u5c16\u6307\u5411\u5415\u56db\u5a18\u7684\u4e39 \u7530\uff0c\u6756\u8eab\u53c8\u6a2a\u622a\u51af\u745b\u7684\u5b9d\u5251\uff0c\u4e00\u62db\u4e24\u5f0f\uff0c\u5206\u654c\u4eba\uff0c\u5415\u56db\u5a18\u548c\u51af\u745b\u90fd\u907f\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u4e00\u9000\u590d\u5408\uff0c\u5415\u56db\u5a18\u5251\u8d70\u504f\u950b\uff0c\u51af\u745b\u62a2\u653b\u4e2d\u8def\uff0c\u53cc\u5251\u4ea4\u52ff\u526a\u4e0b\uff0c\u6bd2\u9f99\u5c0a\u8005\u53c8\u662f\u4e00\u58f0\u5927\u543c\uff0c \u62d0\u6756\u5f80\u4e0b\u4e00\u6c89\uff0c\u659c\u62cd\u51af\u745b\u8109\u95e8\uff0c\u4e0b\u51fb\u5415\u56db\u5a18\u53cc\u80eb\uff0c\u51af\u745b\u8eab\u8eaf\u7626\u5c0f\uff0c\u8eab\u5f62\u4e00\u7f29\uff0c\u6e38\u9c7c\u822c\u7684\u6ed1\u4e86 \u5f00\u53bb\uff0c\u5415\u56db\u5a18\u65cb\u8eab\u4e00\u8f6c\uff0c\u957f\u5251\u70b9\u5230\u6bd2\u9f99\u5c0a\u8005\u7684\u5de6\u201c\u80a9\u4e95\u7a74\u201d\uff0c\u6bd2\u9f99\u5c0a\u8005\u5927\u559d\u4e00\u58f0\u201c\u7740\uff01\u201d\u4e00 \u4e2a\u201c\u56de\u8eab\u62d7\u6b65\u201d\uff0c\u9f99\u5934\u94c1\u62d0\u5f80\u4e0a\u4e00\u62bd\uff0c\u987a\u52bf\u53cd\u5c55\uff0c\u75be\u5982\u9a87\u7535\u5954\u96ea\uff0c\u6756\u8eab\u5d29\u7838\u5415\u56db\u5a18\u7684\u5b9d\u5251\uff0c \u6756\u5c16\u5374\u70b9\u5230\u9762\u95e8\uff01\u65c1\u89c2\u8bf8\u4eba\uff0c\u5fc3\u60ca\u80c6\u6218\u3002\u54ea\u77e5\u6bd2\u9f99\u5c0a\u8005\u5df2\u5feb\uff0c\u5415\u56db\u5a18\u66f4\u5feb\uff0c\u6bd2\u9f99\u5c0a\u8005\u4e00\u6756\u6253 \u51fa\uff0c\u53ea\u542c\u5f97\u53ee\u5f53\u4e00\u58f0\uff0c\u5415\u56db\u5a18\u957f\u5251\u5728\u62d0\u6756\u4e0a\u4e00\u6309\uff0c\u8eab\u5b50\u5df2\u5f39\u5230\u534a\u7a7a\u3002\u51af\u745b\u5530\u7684\u4e00\u5251\uff0c\u8fb9\u950b\u6025 \u8fdb\uff0c\u4e00\u62db\u201c\u767d\u9e64\u68b3\u7fce\u201d\uff0c\u659c\u5207\u51fa\u53bb\u3002\u6bd2\u9f99\u5c0a\u8005\u5175\u5668\u6765\u4e0d\u53ca\u6536\u56de\uff0c\u5de6\u638c\u62cd\u51fa\uff0c\u8fd0\u638c\u6210\u98ce\uff0c\u4ee5\u653b \u4e3a\u5b88\uff0c\u89e3\u4e86\u51af\u745b\u62db\u6570\u3002 \u3000\u3000\u6597\u4e86\u7247\u523b\uff0c\u6bd2\u9f99\u5c0a\u8005\u5360\u4e0d\u4e86\u4e0a\u98ce\uff0c\u52c3\u7136\u5927\u6012\uff01\u8fd0\u4e86\u5185\u5bb6\u771f\u529b\uff0c\u6756\u6cd5\u4e00\u53d8\uff0c\u547c\u547c\u8f70\u8f70\uff0c\u5de6 \u653b\u53f3\u62d2\uff0c\u5de6\u4e00\u638c\u53f3\u4e00\u6756\uff0c\u7740\u7740\u62a2\u653b\uff0c\u4f46\u89c1\u6756\u5f71\u5982\u5c71\uff0c\u5251\u5149\u4f3c\u7ec3\u3002\u8d70\u9a6c\u706f\u4f3c\u7684\u98ce\u8f66\u65cb\u8f6c\uff0c\u6d77\u6ee9 \u90a3\u8fb9\uff0c\u7fa4\u96c4\u89c2\u6218\uff0c\u4f46\u89c9\u5251\u5149\u6756\u5f71\uff0c\u8000\u4eba\u773c\u76ee\uff0c\u91d1\u94c1\u4ea4\u9e23\uff0c\u9707\u8033\u6b32\u804b\uff0c\u7aef\u7684\u662f\u767e\u5e74\u96be\u9047\u7684\u4e00\u573a \u6076\u6597\u3002\u9976\u662f\u7518\u51e4\u6c60\u90a3\u6837\u9547\u5b9a\u7684\u4eba\uff0c\u624b\u5fc3\u4e5f\u89c9\u6dcc\u6c57\u3002 \u3000\u3000\u8d8a\u6597\u8d8a\u70c8\uff0c\u4f17\u4eba\u8fd8\u671b\u8fc7\u53bb\uff0c\u53ea\u89c1\u4e09\u6761\u8ff7\u7cca\u7684\u4eba\u5f71\u5728\u94f6\u5149\u6ce2\u6d9b\u4e4b\u4e2d\u4e0a\u4e0b\u5f80\u6765\uff0c\u518d\u770b\u4e0b\u53bb \u65f6\uff0c\u8fde\u4eba\u5f71\u4e5f\u9690\u6ca1\u5728\u201c\u6ce2\u6d9b\u201d\u4e4b\u4e2d\u4e86\u3002\u4f17\u4eba\u4e2d\u7518\u51e4\u6c60\u548c\u5510\u6653\u6f9c\u6b66\u529f\u6700\u9ad8\uff0c\u51dd\u795e\u6ce8\u89c6\uff0c\u8fd8\u5206\u8fa8 \u5f97\u51fa\u4f18\u52a3\u5f62\u52bf\uff0c\u5176\u4ed6\u5404\u4eba\u8fde\u4eba\u5f71\u4e5f\u8fa8\u4e0d\u6e05\u3002 \u3000\u3000\u5510\u6653\u6f9c\u770b\u4e86\u4e00\u4f1a\uff0c\u9053\uff1a\u201c\u7518\u5927\u4fa0\uff0c\u5979\u4eec\u8d70\u4e86\u4e0b\u98ce\u4e86\uff0c\u54b1\u4eec\u51fa\u624b\u5427\uff01\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u518d\u7b49 \u4e9b\u65f6\u201d\uff0c\u84e6\u7136\u5f62\u52bf\u53c8\u53d8\uff0c\u6bd2\u9f99\u5c0a\u8005\u7684\u6756\u52bf\u6e10\u7f13\u4e0b\u6765\uff0c\u5415\u56db\u5a18\u5251\u52bf\u5982\u8679\uff0c\u5947\u5e7b\u65e0\u5339\uff0c\u8fde\u8fde\u53cd \u653b\u3002\u51af\u745b\u4f7f\u51fa\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u4e00\u56e2\u5251\u5149\uff0c\u7b3c\u7f69\u8eab\u8eaf\uff0c\u5728\u6bd2\u9f99\u5c0a\u8005\u8eab\u524d\uff0c\u6eda\u6765\u6eda\u53bb\u3002\u7518\u51e4\u6c60\u5438\u4e86 \u4e00\u53e3\u51c9\u6c14\uff0c\u9053\uff1a\u201c\u516b\u59b9\u5251\u6cd5\u53c8\u7cbe\u8fdb\u4e86\uff0c\u5e08\u5085\u590d\u751f\uff0c\u4e5f\u4e0d\u8fc7\u5982\u6b64\uff01\u201d\u5510\u6653\u6f9c\u8fde\u58f0\u8d5e\u53f9\uff0c\u4e5f\u9053\uff1a \u201c\u51af\u745b\u8fd9\u5c0f\u4e2b\u5934\uff0c\u5251\u6cd5\u5728\u6570\u65e5\u4e4b\u95f4\uff0c\u7adf\u7136\u4e5f\u7cbe\u8fdb\u5982\u65af\uff01\u54b1\u4eec\u4e0d\u7528\u51fa\u624b\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u5415\u56db\u5a18\u5728\u5148\u51e0\u5929\u67e5\u95ee\u4e86\u6bd2\u9f99\u5c0a\u8005\u7684\u6b66\u529f\u540e\uff0c\u5fc3\u4e2d\u4fbf\u5df2\u76d8\u7b97\u5bf9\u7b56\u3002\u5fc3\u60f3\uff1a\u5929\u5c71\u5251\u6cd5\u535a \u5927\u7cbe\u6df1\uff0c\u7384\u5973\u5251\u6cd5\u7cbe\u5947\u5965\u5999\uff0c\u6211\u4eec\u4e24\u5bb6\u5251\u6cd5\u4e43\u662f\u6b66\u6797\u53cc\u7edd\uff0c\u914d\u5408\u8d77\u6765\uff0c\u5a01\u529b\u65e0\u6bd4\uff0c\u4ee5\u6b64\u62d2 \u654c\uff0c\u4f55\u654c\u4e0d\u6467\uff1f\u6bd2\u9f99\u5c0a\u8005\u6b66\u529f\u867d\u9ad8\uff0c\u4e5f\u672a\u5fc5\u6321\u5f97\u4e86\u53cc\u5251\u7684\u5a01\u529b\u3002\u56e0\u6b64\u51e0\u65e5\u6765\u90fd\u5728\u6307\u70b9\u51af\u745b\u5e94 \u654c\u4e4b\u65f6\uff0c\u653b\u5b88\u914d\u5408\u7684\u7a8d\u8981\u3002\u51af\u745b\u806a\u660e\u7edd\u9876\uff0c\u4e00\u70b9\u5373\u900f\u3002 \u3000\u3000\u4f46\u51af\u745b\u5251\u6cd5\u867d\u9ad8\uff0c\u7ecf\u9a8c\u5c1a\u6d45\u3002\u52a0\u4ee5\u5415\u56db\u5a18\u53ea\u662f\u53e3\u6388\u673a\u5b9c\uff0c\u5728\u8239\u4e2d\u672a\u80fd\u7ec3\u4e60\u3002\u6240\u4ee5\u4e34\u9635\u521d \u65f6\uff0c\u53cc\u5251\u5408\u653b\uff0c\u4e0d\u591f\u7d27\u51d1\uff0c\u51e0\u4e4e\u7ed9\u6bd2\u9f99\u5c0a\u8005\u6253\u8d25\uff0c\u5230\u8d70\u4e86\u4e09\u4e94\u767e\u62db\u4e4b\u540e\uff0c\u51af\u745b\u6e10\u6e10\u9547\u5b9a\uff0c\u610f \u4e0e\u5251\u4f1a\uff0c\u51dd\u795e\u4f7f\u5251\uff0c\u4e0d\u8e81\u4e0d\u60e7\uff0c\u679c\u7136\u628a\u5f62\u52bf\u626d\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u5c61\u653b\u4e0d\u901e\uff0c\u6e10\u6e10\u88ab\u8feb\u8f6c\u5904\u4e0b\u98ce\uff0c\u4e0d\u6562\u8f7b\u654c\uff0c\u6756\u6cd5\u53c8\u53d8\u3002\u52bf\u4f3c\u6bd4\u524d\u7f13\u4e86\uff0c\u52b2\u9053\u5374 \u662f\u52a0\u5f3a\uff0c\u529b\u900f\u6756\u7aef\uff0c\u6756\u98ce\u6fc0\u8361\uff0c\u5415\u56db\u5a18\u548c\u51af\u745b\u53cc\u5251\u8054\u653b\uff0c\u53c8\u8d70\u4e86\u4e00\u767e\u591a\u62db\u3002\u5140\u662f\u653b\u4e0d\u8fdb\u53bb\u3002 \u51af\u745b\u529f\u529b\u8f83\u6d45\uff0c\u6597\u4e86\u534a\u5929\uff0c\u6c57\u6e7f\u8863\u88f3\uff0c\u7ed9\u6756\u98ce\u9707\u8361\u3002\u80f8\u53e3\u5982\u53d7\u5343\u65a4\u5de8\u77f3\u6240\u538b\uff0c\u6781\u4e0d\u597d\u53d7\u3002\u5415 \u56db\u5a18\u65e0\u8ba1\u53ef\u65bd\uff0c\u77a5\u89c1\u51af\u745b\u8f9b\u82e6\u795e\u60c5\uff0c\u6b63\u60f3\u6492\u9000\u3002\u6025\u653b\u6570\u5251\uff0c\u60f3\u628a\u6bd2\u9f99\u5c0a\u8005\u903c\u5f00\uff0c\u7136\u540e\u53eb\u51af\u745b \u5148\u8d70\u3002 \u3000\u3000\u54ea\u77e5\u4e8b\u60c5\u9887\u51fa\u610f\u5916\uff0c\u5415\u56db\u5a18\u6025\u653b\u6570\u5251\uff0c\u5ffd\u89c9\u6bd2\u9f99\u5c0a\u8005\u94c1\u62d0\u7684\u529b\u9053\u5df2\u4e0d\u82e5\u5148\u524d\u5f3a\u52b2\uff0c\u51af\u745b \u4e5f\u4f3c\u89c9\u5bdf\u5230\u4e86\uff0c\u632f\u8d77\u7cbe\u795e\uff0c\u914d\u5408\u5415\u56db\u5a18\u6025\u653b\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u4f46\u89c1\u6bd2\u9f99\u5c0a\u8005\u989d\u73b0\u7ea2\u7b4b\uff0c\u6c57\u4e0b\u5982 \u96e8\uff0c\u9762\u8272\u7070\u767d\uff0c\u8fde\u8d70\u52a3\u62db\uff01\u5415\u56db\u5a18\u548c\u51af\u745b\u5927\u559c\uff0c\u5de6\u4e00\u5251\u53f3\u4e00\u5251\uff0c\u524d\u4e00\u5251\u540e\u4e00\u5251\uff0c\u8d8a\u653b\u8d8a\u7d27\uff0c \u8d8a\u6253\u8d8a\u5feb\uff0c\u628a\u6bd2\u9f99\u5c0a\u8005\u7684\u62d0\u6756\u76f4\u538b\u4e0b\u53bb\u3002 \u3000\u3000\u4f60\u9053\u6bd2\u9f99\u5c0a\u8005\u4f55\u6545\u7a81\u7136\u4e0d\u652f\uff1f\u539f\u6765\u4ed6\u53d7\u4e86\u7518\u51e4\u6c60\u90a3\u638c\uff0c\u5185\u810f\u5df2\u4f24\u3002\u82e5\u7136\u5f53\u65f6\u5c31\u6025\u9000\u56de \u53bb\uff0c\u9759\u5750\u6570\u5929\uff0c\u5018\u53ef\u81ea\u7597\u3002\u504f\u4ed6\u597d\u5f3a\u6210\u6027\uff0c\u4ed7\u7740\u6df1\u539a\u7684\u5185\u529f\uff0c\u8fd0\u6c14\u5f3a\u7981\u3002\u518d\u5438\u4e86\u86c7\u8840\uff0c\u632f\u8d77 \u7cbe\u795e\u3002\u8868\u9762\u867d\u770b\u4e0d\u51fa\u6765\uff0c\u5176\u5b9e\u5143\u6c14\u5df2\u7ecf\u4f24\u635f\uff0c\u548c\u5415\u51af\u4e8c\u4eba\u62fc\u6597\u5343\u62db\u4e4b\u540e\uff0c\u5185\u4f24\u53d1\u4f5c\uff0c\u5fc3\u75db\u5982 \u7ede\uff0c\u6bd2\u9f99\u5c0a\u8005\u672c\u6765\u662f\u4ed7\u7740\u5185\u529f\u6df1\u539a\uff0c\u5f3a\u529b\u652f\u6301\uff0c\u81f3\u6b64\u529f\u529b\u6e10\u6d88\uff0c\u6709\u5982\u575a\u56fa\u7684\u57ce\u5899\uff0c\u5899\u811a\u5df2\u7ed9 \u767d\u8681\u635f\u574f\uff0c\u54ea\u8fd8\u7981\u53d7\u5f97\u8d77\u98ce\u5439\u96e8\u6dcb\uff1f \u3000\u3000\u7518\u51e4\u6c60\u548c\u5510\u6653\u6f9c\u89c1\u72b6\u5927\u559c\uff0c\u4e0d\u7ea6\u800c\u540c\uff0c\u90fd\u5401\u4e86\u53e3\u6c14\u3002\u4f46\u89c1\u5415\u56db\u5a18\u5251\u8d70\u8fde\u73af\uff0c\u626c\u58f0\u559d\u9053\uff1a \u201c\u6bd2\u9f99\u524d\u8f88\uff0c\u4f60\u8fd8\u8981\u518d\u6253\u5417\uff1f\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u6211\u4eec\u7684\u516b\u59b9\u771f\u662f\u4fa0\u9aa8\u67d4\u80a0\uff0c\u5bf9\u8fd9\u6837\u7684\u5996\u4eba\u4e5f \u62db\u964d\u8d77\u6765\u4e86\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5ffd\u89c1\u51af\u745b\u60e8\u53eb\u4e00\u58f0\uff0c\u7ed9\u6bd2\u9f99\u5c0a\u8005\u4e00\u638c\u6253\u7ffb\u5730\u4e0a\uff01\u8fd9\u4e00\u4e0b\uff0c\u53d8\u5316\u592a \u8fc7\u7a81\u7136\uff0c\u975e\u4f46\u662f\u65c1\u89c2\u8bf8\u4eba\u610f\u6599\u4e0d\u53ca\uff0c\u5373\u5415\u56db\u5a18\u4e5f\u4e07\u60f3\u4e0d\u5230\uff0c\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u539f\u6765\u6bd2\u9f99\u5c0a\u8005\u6124\u4e16\u5ac9\u4fd7\uff0c\u5bf9\u4e00\u5207\u4eba\u7b49\uff0c\u90fd\u89c6\u540c\u86c7\u874e\uff0c\u5fc3\u5ff5\u82e5\u7ed9\u5415\u56db\u5a18\u7b49\u64d2\u83b7\uff0c\u4e0d\u77e5\u8981\u53d7 \u4f55\u7b49\u82e6\u5211\uff0c\u56e0\u6b64\u62fc\u4e86\u4e00\u6b7b\uff0c\u7adf\u7136\u52a8\u7528\u4e86\u4ece\u672a\u7ed9\u4eba\u89c1\u8fc7\u7684\u201c\u91d1\u89d2\u795e\u86c7\u201d\u52a9\u6218\u3002\u8fd9\u91d1\u89d2\u86c7\u4e43\u662f\u86c7 \u5c9b\u7684\u7279\u4ea7\uff0c\u86c7\u5934\u5fae\u51f8\u82e5\u89d2\uff0c\u6bd2\u6027\u6700\u5927\u3002\u6bd2\u9f99\u5c0a\u8005\u9009\u6700\u6bd2\u7684\u6bd2\u86c7\u4ea4\u914d\uff0c\u4e00\u8fde\u57f9\u517b\u4e86\u5341\u51e0\u4ee3\uff0c\u57f9 \u517b\u51fa\u4e00\u6761\u5934\u6709\u6731\u7802\u8272\u7684\u5c16\u89d2\u86c7\u6765\uff0c\u5176\u4ed6\u6bd2\u86c7\u7ed9\u5b83\u4e00\u54ac\u4fbf\u6b7b\u3002\u6bd2\u9f99\u5c0a\u8005\u5b9d\u8d1d\u975e\u5e38\uff0c\u5c06\u5b83\u547d\u540d\u4e3a \u201c\u91d1\u89d2\u795e\u86c7\u201d\uff0c\u7ecf\u5e38\u643a\u5e26\u5728\u8eab\u3002\u6bd2\u9f99\u5c0a\u8005\u4e4b\u517b\u6bd2\u86c7\uff0c\u7b49\u4e8e\u5e73\u5e38\u4eba\u4e4b\u517b\u732b\u72d7\uff0c\u539f\u610f\u672c\u662f\u517b\u4f5c\u73a9 \u7269\uff0c\u5e76\u672a\u60f3\u5230\u8981\u7528\u4f5c\u514b\u654c\u5236\u80dc\u7684\uff0c\u76f4\u5230\u4ed6\u88ab\u903c\u5f97\u65e0\u6cd5\u53ef\u65bd\u4e4b\u9645\uff0c\u59cb\u60f3\u8d77\u6b64\u86c7\u4e4b\u6bd2\uff0c\u7528\u6765\u4e00 \u8bd5\uff0c\u7adf\u544a\u6210\u529f\uff0c\u51af\u745b\u7ed9\u86c7\u4e00\u54ac\uff0c\u590d\u53d7\u4e86\u4e00\u638c\uff0c\u767b\u65f6\u5012\u5728\u5730\u4e0a\uff0c\u53e3\u89d2\u6d41\u51fa\u9ed1\u6d8e\uff01 \u3000\u3000\u5415\u56db\u5a18\u5927\u5403\u4e00\u60ca\uff0c\u77aa\u773c\u4e00\u770b\uff0c\u6bd2\u9f99\u5c0a\u8005\u6325\u6756\u518d\u653b\uff0c\u5415\u56db\u5a18\u63a5\u4e86\u4e00\u62db\uff0c\u731b\u89c1\u5730\u4e0a\u4e00\u6761\u91d1\u5149 \u707f\u70c2\u7684\u5c0f\u86c7\u5a49\u8712\u800c\u6765\uff0c\u5730\u4e0b\u51af\u745b\u547b\u541f\u53eb\u9053\uff1a\u201c\u86c7\uff0c\u86c7\uff01\u6bd2\u86c7\uff01\u201d\u5415\u56db\u5a18\u8eab\u5f62\u6025\u95ea\uff0c\u5728\u8fd9\u77ac\u606f \u4e4b\u95f4\uff0c\u5510\u6653\u6f9c\u4e0e\u7518\u51e4\u6c60\u53cc\u53cc\u62a2\u5230\uff0c\u5510\u6653\u6f9c\u6765\u6551\u51af\u745b\uff0c\u90a3\u6761\u91d1\u8272\u5c0f\u86c7\u6602\u9996\u4eba\u7acb\uff0c\u86c7\u5934\u6446\u52a8\uff0c\u539f \u6765\u86c7\u6108\u6bd2\u4fbf\u6108\u754f\u53cc\u9b54\u6240\u7ec3\u7684\u836f\u4e38\uff0c\u8fd9\u6761\u5c0f\u86c7\u6765\u4e0d\u53ca\u9003\u8d70\uff0c\u5df2\u7ed9\u90a3\u80a1\u6c14\u5473\u718f\u5f97\u762b\u8f6f\uff0c\u53ea\u80fd\u5728\u539f \u5730\u4e0a\u62fc\u547d\u6297\u62d2\uff0c\u65e0\u529b\u6e38\u8d70\u3002\u5510\u6653\u6f9c\u4e00\u5251\u64a9\u53bb\uff0c\u628a\u5b83\u6240\u4e3a\u4e24\u6bb5\u3002 \u3000\u3000\u5415\u56db\u5a18\u6321\u4e86\u4e24\u62db\uff0c\u7518\u51e4\u6c60\u5df2\u81f3\uff0c\u524d\u540e\u5939\u653b\uff0c\u6bd2\u9f99\u5c0a\u8005\u6b64\u65f6\u5df2\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u4e0d\u80fd\u518d\u6218\uff0c\u54bd \u4e86\u53e3\u6c14\uff0c\u62fc\u5c3d\u5168\u529b\uff0c\u4e00\u58f0\u5927\u543c\uff0c\u53f3\u624b\u4e00\u7529\uff0c\u94c1\u62d0\u7b14\u76f4\u5411\u5415\u56db\u5a18\u80f8\u53e3\u63b7\u53bb\uff0c\u5de6\u624b\u53cd\u8eab\u4e00\u638c\uff0c\u4e0e \u7518\u51e4\u6c60\u8fce\u4e2a\u6b63\u7740\u3002\u5415\u56db\u5a18\u8f7b\u529f\u5353\u7edd\uff0c\u7109\u80fd\u7ed9\u4ed6\u63b7\u4e2d\uff0c\u659c\u8eab\u4e00\u8dc3\uff0c\u4fbf\u5df2\u907f\u5f00\u3002\u7518\u51e4\u6c60\u5219\u8fd0\u53cc\u638c \u4e4b\u529b\uff0c\u4e0e\u4ed6\u76f8\u62b5\uff0c\u6bd2\u9f99\u5c0a\u8005\u6c14\u529b\u5df2\u5c3d\uff0c\u88ab\u7518\u51e4\u6c60\u795e\u529b\u4e00\u8feb\uff0c\u72c2\u53eb\u4e00\u58f0\uff0c\u5410\u51fa\u4e00\u5927\u6ee9\u9c9c\u8840\uff0c\u4ec6 \u5012\u5730\u4e0a\u3002 \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u516b\u59b9\u5982\u4f55\uff1f\u201d\u53ea\u89c1\u5415\u56db\u5a18\u4e91\u53d1\u84ec\u4e71\uff0c\u9762\u6709\u6c57\u73e0\uff0c\u663e\u89c1\u6bd4\u5927\u6218\u4e86\u56e0\u4e4b\u5f79\uff0c\u66f4 \u4e3a\u5403\u529b\u3002\u5415\u56db\u5a18\u6c14\u5598\u5401\u5401\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u65e0\u59a8\u788d\uff0c\u4f60\u53bb\u770b\u770b\u51af\u5bb6\u59b9\u5b50\u5427\uff01\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u597d\uff0c \u4f60\u6b47\u6b47\u8fd0\u529f\uff0c\u6211\u5c31\u53bb\u77a7\u51af\u5bb6\u59b9\u5b50\u3002\u201d\u5415\u56db\u5a18\u76d8\u819d\u8fd0\u529f\uff0c\u6d41\u901a\u6c14\u8840\uff0c\u5ffd\u542c\u5f97\u5510\u6653\u6f9c\u653e\u58f0\u75db\u54ed\uff0c \u7518\u51e4\u6c60\u53eb\u9053\uff1a\u201c\u597d\u72e0\u7684\u5996\u4eba\uff0c\u597d\u6bd2\u7684\u6076\u86c7\uff01\u9c7c\u8001\uff0c\u4f60\u770b\u4ed6\u6b7b\u4e86\u6ca1\u6709\uff1f\u597d\uff0c\u4e0d\u8bba\u4ed6\u8fd8\u6709\u65e0\u6c14 \u606f\uff0c\u6211\u90fd\u8981\u5c06\u4ed6\u5316\u9aa8\u626c\u7070\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u4e00\u8dc3\u800c\u8d77\uff0c\u53eb\u9053\uff0c\u201c\u4e14\u6162\uff01\u201d\u9c7c\u4eae\u8e22\u4e86\u6bd2\u9f99\u5c0a\u8005\u4e24\u4e0b\uff0c\u89c1\u4ed6\u5bc2\u7136\u4e0d\u52a8\uff0c\u6478\u4ed6\u5fc3 \u53e3\uff0c\u5c1a\u6709\u5fae\u6e29\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u516b\u59b9\u6709\u4f55\u9ad8\u89c1\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6682\u65f6\u4e0d\u8981\u52a8\u4ed6\u3002\u6653\u6f9c\uff0c\u51af\u745b\u600e \u4e48\u5566\uff1f\u201d\u7518\u51e4\u6c60\u6068\u6068\u8bf4\u9053\uff1a\u201c\u5979\u5df2\u65e0\u6cd5\u6551\u4e86\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u62b1\u8d77\u51af\u745b\uff0c\u54c0\u54c0\u75db\u54ed\u3002\u5415\u56db\u5a18\u4e0a\u524d\u4e00\u770b\uff0c\u53ea\u89c1\u5979\u9762\u76ee\u7d2b\u9ed1\uff0c\u53e3\u89d2\u6d41\u51fa\u8165\u6d8e\uff0c\u80f8\u524d \u8863\u88f3\u788e\u88c2\uff0c\u5415\u56db\u5a18\u8d34\u8033\u5728\u5979\u80f8\u53e3\u4e00\u542c\uff0c\u9053\uff1a\u201c\u8fd8\u672a\u6c14\u7edd\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u5979\u53d7\u4e86\u4e24\u79cd\u5267\u4f24\uff0c \u4e00\u662f\u6bd2\u86c7\u6240\u54ac\uff0c\u6b64\u86c7\u4e4b\u6bd2\uff0c\u6211\u5e73\u751f\u672a\u89c1\uff1b\u4e00\u662f\u53d7\u6bd2\u9f99\u8001\u5996\u638c\u529b\u6240\u4f24\uff0c\u5df2\u53ca\u5185\u810f\uff0c\u7eb5\u6709\u534e\u9640\u518d \u4e16\uff0c\u6241\u9e4a\u91cd\u751f\u4ea6\u65e0\u80fd\u4e3a\u529b\uff01\u201d \u3000\u3000\u9c7c\u4eae\u7b49\u4eba\u90fd\u56f4\u4e0a\u6765\u770b\uff0c\u9c7c\u4eae\u88ab\u56f4\u5728\u5c71\u4e1c\u629a\u8859\u4e4b\u65f6\uff0c\u4e43\u662f\u51af\u745b\u5192\u9669\u6765\u5f15\u4ed6\u4eec\u51fa\u53bb\u7684\uff0c\u6545\u6b64 \u9c7c\u4eae\u5bf9\u51af\u745b\u751a\u4e3a\u7231\u60dc\uff0c\u542c\u7518\u51e4\u6c60\u65ad\u5979\u5fc5\u6b7b\uff0c\u4e0d\u7981\u6d41\u4e0b\u773c\u6cea\u3002 \u3000\u3000\u5415\u56db\u5a18\u5ffd\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u516b\u81c2\u795e\u9b54\u6240\u9001\u7684\u836f\u4e38\uff0c\u5728\u4f60\u8eab\u4e0a\u5417\uff1f\u201d\u5510\u6653\u6f9c\u970d\u7136\u9192\u8d77\uff0c\u9053\uff1a \u201c\u6b64\u836f\u53ef\u6cbb\u86c7\u4f24\uff0c\u521a\u624d\u7fa4\u86c7\u5954\u9003\uff0c\u83ab\u975e\u5c31\u662f\u56e0\u5b83\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u770b\u5b9a\u662f\u3002\u201d\u5510\u6653\u6f9c\u5c06\u836f \u4e38\u53d6\u51fa\uff0c\u5728\u6bd2\u86c7\u54ac\u4f24\u4e4b\u5904\u6eda\u64e6\u51e0\u904d\uff0c\u6bd2\u6c14\u679c\u7136\u6e10\u9000\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u6b64\u86c7\u592a\u6bd2\uff0c\u628a\u8fd9\u836f\u4e38\u5288\u4e0b \u4e00\u534a\uff0c\u7ed9\u5979\u541e\u4e0b\uff01\u201d\u5510\u6653\u6f9c\u4f9d\u8a00\u5c06\u836f\u4e38\u653e\u5165\u5979\u7684\u53e3\u4e2d\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u53ea\u542c\u5f97\u5979\u809a\u5b50\u5495\u5495\u4f5c\u54cd\uff0c \u9762\u4e0a\u7684\u9ed1\u8272\u4e5f\u6e10\u6e10\u892a\u4e86\uff0c\u53ef\u662f\u4ecd\u7136\u660f\u8ff7\u4e0d\u9192\uff0c\u8109\u818a\u5fae\u5f31\uff0c\u6c14\u82e5\u6e38\u4e1d\uff01 \u3000\u3000\u9c7c\u4eae\u5b9a\u4e86\u5b9a\u795e\uff0c\u53eb\u9053\uff1a\u201c\u9c7c\u5a18\uff01\u201d\u9c7c\u5a18\u9053\uff1a\u201c\u7239\uff0c\u6211\u5728\u8fd9\u513f\uff1f\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u6211\u7684\u85cf\u5b9d\u4f60 \u5e26\u4e86\u51fa\u6765\u6ca1\u6709\uff1f\u201d\u7518\u51e4\u6c60\u542c\u4e86\u5927\u4e3a\u4e0d\u6ee1\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u7ecf\u6b64\u98ce\u6ce2\uff0c\u9c7c\u4eae\u8d2a\u6027\u4ecd\u662f\u672a\u6539\uff01\u522b\u4eba \u6027\u547d\u547c\u5438\uff0c\u6b7b\u751f\u83ab\u6d4b\uff0c\u4ed6\u5374\u95ee\u8d77\u85cf\u5b9d\u6765\uff01\u201d \u3000\u3000\u9c7c\u5a18\u9053\uff1a\u201c\u7239\uff0c\u5e26\u4e86\u4e00\u4e9b\u3002\u5927\u5be8\u591c\u95f4\u88ab\u7834\uff0c\u4ed3\u7687\u9003\u8d70\uff0c\u53ea\u5e26\u4e86\u5341\u9897\u591c\u660e\u73e0\uff0c\u4e00\u682a\u5288\u6c34\u7280 \u89d2\uff0c\u4e00\u4e2a\u5546\u4ee3\u4e09\u811a\u9999\u7089\u548c\u4e00\u682a\u5343\u5e74\u829d\u8349\u3002\u5176\u5b83\u90fd\u6765\u4e0d\u53ca\u5e26\u4e86\uff0c\u57cb\u5728\u7530\u6a2a\u5c9b\u5b64\u5cf0\u4e4b\u4e0a\uff0c\u4f46\u613f\u4ed6 \u4eec\u6ca1\u6709\u53d1\u73b0\u3002\u201d\u9c7c\u4eae\u559c\u9053\uff1a\u201c\u884c\u4e86\uff0c\u628a\u90a3\u682a\u5343\u5e74\u829d\u8349\u62ff\u51fa\u6765\u3002\u201d\u9c7c\u5a18\u6b63\u60f3\u5411\u7236\u4eb2\u63d0\u51fa\uff0c\u8bd5\u7528 \u829d\u8349\u7597\u4f24\uff0c\u4e0d\u610f\u7236\u4eb2\u5df2\u5148\u8bf4\u51fa\u3002 \u3000\u3000\u9c7c\u4eae\u63a5\u8fc7\u829d\u8349\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u8349\u529f\u80fd\u8d77\u6b7b\u56de\u751f\uff0c\u8bd5\u5b83\u4e00\u8bd5\u3002\u201d\u770b\u4e86\u4f24\u52bf\uff0c\u53eb\u5510\u6653\u6f9c\u5c06\u534a\u682a \u829d\u8349\u78be\u788e\uff0c\u7eb3\u5165\u51af\u745b\u53e3\u4e2d\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u51af\u745b\u9762\u8272\u6e10\u89c1\u7ea2\u6da6\uff0c\u54c7\u7684\u4e00\u58f0\u5410\u51fa\u4e00\u53e3\u7600\u8840\uff01\u9c7c\u4eae\u53eb \u9c7c\u5a18\u652f\u8d77\u5e10\u84ec\uff0c\u5c06\u51af\u745b\u62b1\u8ff8\u5e10\u7bf7\u5c06\u606f\u3002\u53c8\u5bf9\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5c1a\u6709\u534a\u682a\u829d\u8349\uff0c\u4f60\u7559\u7740\u5427\u3002\u51af\u59d1\u5a18 \u66fe\u95ee\u6211\u8981\u8fc7\u3002\u8bf4\u662f\u53ef\u6d3e\u7528\u573a\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u5ffd\u9053\uff1a\u201c\u628a\u90a3\u534a\u682a\u829d\u8349\u7ed9\u6211\uff01\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f60\u8981\u5b83\u4f5c\u751a\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6551\u6bd2 \u9f99\u5c0a\u8005\uff01\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u2026\u2026\u201d\u60f3\u8bf4\u7684\u662f\uff1a\u201c\u4f60\u75af\u4e86\u5417\uff1f\u201d\u51e0\u5b57\uff0c\u56e0\u4ed6\u5bf9\u5c0f\u5e08\u59b9\u4e00\u5411 \u5c0a\u91cd\uff0c\u6ca1\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u59d0\u59d0\u62ff\u53bb\uff01\u201d\u4ed6\u5bf9\u5415\u56db\u5a18\u4f69\u670d\u975e\u5e38\uff0c\u53ea\u8981\u662f\u5979\u8bf4\u7684\uff0c\u83ab\u8bf4\u534a\u682a\u829d\u8349\uff0c\u5373\u8d74 \u6c64\u8e0f\u706b\uff0c\u4e5f\u6240\u4e0d\u8f9e\u3002\u9c7c\u4eae\u9053\uff1a\u201c\u5510\u5144\u5f1f\uff0c\u4f60\u4e0d\u8981\u7528\u5417\uff1f\u201d\u5510\u6653\u6f9c\u628a\u829d\u8349\u4ea4\u5230\u5415\u56db\u5a18\u624b\u4e0a\uff0c\u6447 \u4e86\u6447\u5934\uff0c\u7b11\u9053\uff1a\u201c\u6211\u7528\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u5415\u56db\u5a18\u77a5\u4e86\u5510\u6653\u6f9c\u4e00\u773c\uff0c\u6b32\u8a00\u53c8\u6b62\u3002 \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u516b\u59b9\u771f\u8981\u6551\u4ed6\u5417\uff1f\u5c06\u4ed6\u6cbb\u597d\u4e4b\u540e\uff0c\u8c01\u4eba\u80fd\u518d\u5c06\u4ed6\u6536\u670d\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4f60 \u6211\u90fd\u80fd\u5c06\u4ed6\u6536\u670d\uff01\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u4e03\u54e5\uff0c\u64ac\u5f00\u4ed6\u7684\u7259\u9f7f\uff01\u201d \u3000\u3000\u7518\u51e4\u6c60\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u6709\u8fd9\u5927\u80fd\u8010\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e03\u54e5\u9886\u8896\u6b66\u6797\uff0c\u96be\u9053\u4e0d\u77e5\u4ee5\u5fb7 \u670d\u4eba\u4e4b\u7406\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f46\u6b64\u4eba\u4e43\u5316\u5916\u5996\u90aa\uff0c\u5c82\u901a\u4eba\u6027\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4ed6\u6bd4\u5e74\u7fb9\u5c27\u5982 \u4f55\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u4e43\u4eba\u9762\u517d\u5fc3\uff0c\u5982\u4f55\u53ef\u6bd4\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4ed6\u51fa\u8fc7\u86c7\u5c9b\u5bb3\u8fc7\u4eba\u6ca1 \u6709\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6ca1\u542c\u8bf4\u8fc7\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u4e48\uff1f\u662f\u4f60\u4eec\u5230\u4e86\u86c7\u5c9b\u4e4b\u540e\uff0c\u4ed6\u624d\u548c\u4f60 \u4eec\u6253\u7684\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u522b\u4eba\u53d7\u53f0\u98ce\u707e\u5bb3\uff0c\u6d41\u843d\u8352\u5c9b\uff0c\u7a0d\u6709\u4eba\u6027\u7684\u90fd\u8be5\u76f8\u6551\uff0c\u4ed6\u53cd\u800c\u9a71\u4f7f\u6bd2 \u86c7\uff0c\u8981\u541e\u98df\u6211\u4eec\uff0c\u96be\u9053\u8fd8\u4e0d\u8be5\u6b7b\u5417\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u662f\u554a\uff0c\u4f60\u8bf4\u7684\u8bdd\uff0c\u6b63\u597d\u66ff\u4ed6\u8fa9\u62a4\u4e86\u3002\u201d \u7518\u51e4\u6c60\u8be7\u9053\uff1a\u201c\u8fd9\u8bdd\u600e\u8bf4\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5f53\u5e74\u4ed6\u60a3\u4e86\u5927\u9ebb\u75af\uff0c\u5c31\u5982\u4f60\u4eec\u9047\u4e0a\u53f0\u98ce\u4e00\u6837\uff0c\u662f \u53d7\u4e86\u4e00\u79cd\u707e\u5bb3\uff0c\u5e76\u975e\u81ea\u5df1\u505a\u9519\u4e86\u4e8b\u60c5\u800c\u53d7\u7684\u707e\u5bb3\u3002\u4f46\u65c1\u4eba\u975e\u4f46\u4e0d\u6551\u6cbb\u4ed6\uff0c\u53cd\u800c\u8981\u5c06\u4ed6\u9a71\u9010\uff0c \u5c06\u4ed6\u6d3b\u57cb\uff0c\u8fd9\u53c8\u600e\u80fd\u4e0d\u4ee4\u4ed6\u6124\u6068\uff1f\u4ed6\u89c1\u4eba\u4fbf\u6253\uff0c\u6b63\u5982\u4f60\u4eec\u56e0\u53d7\u4e86\u4ed6\u7684\u8feb\u5bb3\uff0c\u56e0\u800c\u8981\u5c06\u4ed6\u5316\u9aa8 \u626c\u7070\u4e00\u6837\uff01\u201d \u3000\u3000\u7518\u51e4\u6c60\u672c\u4ff1\u6709\u4ec1\u5fc3\u4fa0\u9aa8\u4e4b\u4eba\uff0c\u542c\u4e86\u8fd9\u756a\u8bdd\u540e\uff0c\u7ec6\u60f3\u4e00\u60f3\uff0c\u62cd\u624b\u8bf4\u9053\uff1a\u201c\u516b\u59b9\u5230\u5e95\u662f\u8bfb\u4e66 \u660e\u57cb\u4e4b\u4eba\uff0c\u89c1\u8bc6\u8fdc\u5728\u6211\u8f88\u4e4b\u4e0a\uff0c\u82e5\u975e\u4f60\u4eca\u65e5\u5f00\u5bfc\uff0c\u6211\u51e0\u4e4e\u505a\u9519\u4e8b\u60c5\u2026\u2026\u201d\u6124\u6c14\u5168\u6d88\uff0c\u53cd\u89c9\u7f9e \u60ed\uff0c\u4e0a\u524d\u64ac\u5f00\u6bd2\u9f99\u5c0a\u8005\u7684\u7259\u9f7f\uff0c\u5c06\u829d\u8349\u5582\u7ed9\u4ed6\u98df\uff0c\u65f6\u95f4\u8fc7\u8fdf\uff0c\u829d\u8349\u867d\u7136\u7075\u9a8c\uff0c\u4e0d\u8fc7\u4f7f\u4ed6\u5fc3\u8d43 \u6062\u590d\u8df3\u52a8\uff0c\u6c14\u606f\u4ecd\u662f\u5fae\u5f31\uff0c\u7518\u51e4\u6c60\u64ac\u5f00\u4ed6\u7684\u5634\u5df4\uff0c\u4e0d\u7406\u4ed6\u53e3\u6c14\u8165\u81ed\uff0c\u5ea6\u6c14\u7ed9\u4ed6\uff0c\u8fc7\u4e86\u597d\u4e45\uff0c \u6bd2\u9f99\u5c0a\u8005\u60a0\u60a0\u9192\u8f6c\uff0c\u773c\u4e2d\u9732\u51fa\u5947\u5f02\u7684\u5149\u5f69\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f60\u73b0\u5728\u8fd8\u4e0d\u80fd\u8fd0\u6c14\uff0c\u8eba\u4e24\u5929\u5427\uff01\u201d \u4ecd\u7136\u5ea6\u6c14\u7ed9\u5730\u3002\u5415\u56db\u5a18\u5206\u51fa\u4eba\u6765\uff0c\u4e00\u6279\u670d\u4f8d\u51af\u745b\uff0c\u4e00\u6279\u670d\u4f8d\u6bd2\u9f99\u5c0a\u8005\uff0c\u5341\u5206\u5468\u5230\uff0c\u8fc7\u4e86\u4e09 \u65e5\uff0c\u51af\u745b\u5df2\u80fd\u8d77\u5e8a\uff0c\u6bd2\u9f99\u5c0a\u8005\u4e5f\u80fd\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u5415\u56db\u5a18\u548c\u7518\u51e4\u6c60\u6781\u529b\u529d\u4ed6\u5b89\u5fc3\u4f11\u606f\u3002\u6bd2\u9f99\u5c0a\u8005\u5fc3\u4e2d\u5145\u6ee1\u7591\u60d1\uff0c\u95ee\u9053\uff1a\u201c\u6211\u672c\u6765\u8981\u6b7b\uff0c\u600e \u4e48\u4f60\u4eec\u53cd\u800c\u6551\u6211\uff1f\u4f60\u4eec\u4e0d\u662f\u4eba\u4e48\uff1f\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4eba\u4e5f\u6709\u8bb8\u591a\u79cd\uff0c\u6709\u4e9b\u4eba\u5e78\u707e\u4e50\u7978\uff0c\u6295\u4e95 \u4e0b\u77f3\uff0c\u6709\u4e9b\u4eba\u5374\u662f\u4ee5\u6551\u5929\u4e0b\u4e4b\u4eba\u4e3a\u5df1\u4efb\uff0c\u600e\u80fd\u4e00\u6982\u800c\u8bba\u5462\uff1f\u201d\u6bd2\u9f99\u5c0a\u8005\u4f3c\u61c2\u975e\u61c2\uff0c\u5367\u5e8a\u51e0 \u65e5\uff0c\u4e0d\u7981\u7ec6\u60f3\u524d\u4e8b\uff0c\u60f3\u8d77\u81ea\u5df1\u5728\u672a\u60a3\u9ebb\u75af\u7684\u5c11\u5e74\u4e4b\u65f6\uff0c\u679c\u7136\u662f\u89c1\u8fc7\u6709\u4e9b\u4eba\u5f88\u597d\uff0c\u6709\u4e9b\u4eba\u5f88 \u574f\uff0c\u4f46\u5728\u81ea\u5df1\u60a3\u4e86\u9ebb\u75af\u4e4b\u540e\uff0c\u4fbf\u4efb\u4f55\u4eba\u90fd\u5bf9\u81ea\u5df1\u51b7\u6de1\u4e86\uff0c\u751a\u81f3\u4ec7\u89c6\u3002\u4e00\u65e5\u53c8\u95ee\u9053\uff1a\u201c\u5728\u5916\u9762 \u8fd8\u6709\u4eba\u60a3\u9ebb\u75af\u5417\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6709\u7684\u3002\u201d\u6bd2\u9f99\u5c0a\u8005\u9053\uff1a\u201c\u8fd8\u672a\u6709\u836f\u533b\u4e48\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a \u201c\u672a\u6709\u3002\u201d\u6bd2\u9f99\u5c0a\u8005\u5ffd\u9053\uff1a\u201c\u5047\u5982\u6211\u4eca\u65e5\u4ecd\u60a3\u9ebb\u75af\u672a\u6108\uff0c\u4f60\u4eec\u4ecd\u4f1a\u5bf9\u6211\u597d\u4e48\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a \u201c\u4e00\u6837\u3002\u201d\u6bd2\u9f99\u5c0a\u8005\u6447\u9996\u4e0d\u4fe1\u3002\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4f60\u8bd5\u60f3\u60f3\uff0c\u4f60\u66fe\u9a71\u4f7f\u6bd2\u86c7\u556e\u54ac\u6211\u4eec\uff0c\u4f60\u53c8\u8981 \u4ed7\u6b66\u529f\u6253\u6b7b\u6211\u4eec\u3002\u4f60\u5728\u6211\u4eec\u773c\u4e2d\u662f\u4e0d\u662f\u6bd4\u9ebb\u75af\u66f4\u53ef\u6015\uff0c\u9ebb\u75af\u672a\u5fc5\u80fd\u4ee4\u4eba\u6b7b\uff0c\u800c\u4f60\u4e0e\u6bd2\u86c7\u5374\u80fd \u81f4\u4eba\u6b7b\u5730\u3002\u6211\u4eec\u65e2\u7136\u613f\u6551\u4eca\u65e5\u4e4b\u4f60\uff0c\u53c8\u4f55\u81f3\u6068\u6614\u65e5\u4e4b\u4f60\uff1f\u201d\u6bd2\u9f99\u5c0a\u8005\u95fb\u8a00\u7ec6\u60f3\uff0c\u5ffd\u7136\u75db\u54ed\u8d77 \u6765\u3002\u5415\u56db\u5a18\u4e0e\u7518\u51e4\u6c60\u9000\u51fa\u5e10\u5916\uff0c\u8ba9\u4ed6\u54ed\u4e2a\u75db\u5feb\u3002\u5f85\u4ed6\u54ed\u5b8c\u4e4b\u540e\uff0c\u518d\u56de\u6765\u7ed9\u4ed6\u6362\u8863\u88f3\u3002 \u3000\u3000\u5982\u662f\u8005\u53c8\u8fc7\u6570\u65e5\uff0c\u6bd2\u9f99\u5c0a\u8005\u4e5f\u65f6\u4e0d\u65f6\u95ee\u8d77\u5916\u9762\u4eba\u4e16\u95f4\u7684\u4e8b\u60c5\uff0c\u6e10\u6e10\u77e5\u9053\u5206\u8fa8\u5584\u6076\uff0c\u91ce\u6027 \u65e5\u6d88\uff0c\u4eba\u6027\u65e5\u957f\u3002\u53c8\u8fc7\u4e86\u51e0\u5929\uff0c\u51af\u745b\u5df2\u5148\u75ca\u6108\uff0c\u4e0e\u5510\u6653\u6f9c\u8fc7\u6765\u63a2\u671b\uff0c\u6bd2\u9f99\u5c0a\u8005\u4e00\u89c1\u4ed6\u4eec\uff0c\u9762 \u8272\u53c8\u53d8\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u53cc\u9b54\u662f\u597d\u4eba\u8fd8\u662f\u574f\u4eba\uff1f\u201d\u6bd2\u9f99\u5c0a\u8005\u9053\uff1a\u201c\u542c\u4f60\u4eec\u8bf4\uff0c\u5f53\u4eca\u7684\u7687\u5e1d\u4e43\u662f \u574f\u4eba\uff0c\u4ed6\u4eec\u5e2e\u52a9\u7687\u5e1d\uff0c\u60f3\u6765\u4e0d\u662f\u597d\u4eba\uff0c\u53ef\u662f\u5bf9\u8fd9\u4f4d\u5c0f\u59d1\u5a18\uff0c\u603b\u4e0d\u80fd\u8bf4\u4e0d\u597d\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a \u201c\u8fd9\u4e0d\u662f\u4e86\uff1f\u4ed6\u4eec\u5e2e\u52a9\u7687\u5e1d\u66fe\u6740\u4e86\u8bb8\u591a\u5584\u826f\u7684\u4eba\u3002\u201d\u5c06\u6614\u5e74\u53cc\u9b54\u5728\u592a\u884c\u5c71\u4e0a\u6740\u5bb3\u6297\u6e05\u4e49\u58eb\uff0c \u6446\u4eba\u5934\u5bb4\u4e4b\u4e8b\u8bf4\u4e86\u3002\u6bd2\u9f99\u5c0a\u8005\u4eba\u6027\u5df2\u590d\uff0c\u542c\u6765\u4e5f\u4e0d\u89c9\u6bdb\u9aa8\u609a\u7136\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u6240\u4ee5\u4e0d\u80fd\u56e0\u4e00 \u70b9\u5c0f\u5584\u800c\u63a9\u5927\u597d\uff0c\u4e5f\u4e0d\u80fd\u56e0\u4e00\u70b9\u5c0f\u4e0d\u662f\u800c\u6bc1\u5927\u8d24\u3002\u201d\u6bd2\u9f99\u5c0a\u8005\u867d\u7136\u4e0d\u80fd\u5b8c\u5168\u9886\u609f\uff0c\u4ea6\u89c9\u5176\u4e2d \u9887\u6709\u9053\u7406\u3002\u5415\u56db\u5a18\u53c8\u9053\uff1a\u201c\u5c1a\u6709\u4e00\u4e8b\u4f60\u8fd8\u672a\u77e5\uff0c\u8fd9\u5c0f\u59d1\u5a18\u7684\u5bb6\u4eba\uff0c\u662f\u53cc\u9b54\u9053\u4eba\u5bb3\u6b7b\u7684\u3002\u8fd9\u5c0f \u59d1\u5a18\u7684\u6bcd\u4eb2\u662f\u53cc\u9b54\u52ab\u53bb\u7684\u3002\u201d\u6bd2\u9f99\u5c0a\u8005\u6376\u5e8a\u8bf4\u9053\uff1a\u201c\u8be5\u6b7b\uff0c\u8be5\u6b7b\uff01\u201d\u51af\u745b\u9053\uff1a\u201c\u6211\u4e00\u70b9\u4e5f\u4e0d \u77e5\u9053\u516b\u81c2\u795e\u9b54\u5c1a\u6709\u4e00\u70b9\u5584\u826f\u4e4b\u5fc3\uff0c\u82e5\u7136\u77e5\u9053\uff0c\u6211\u4e5f\u4e0d\u6740\u5bb3\u4ed6\u4e86\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u70b9\uff0c \u4f60\u4ea6\u4e0d\u77e5\uff01\u201d\u6bd2\u9f99\u5c0a\u8005\u9053\uff1a\u201c\u4f55\u4e8b\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6551\u4f60\u6027\u547d\u7684\u90a3\u534a\u682a\u829d\u8349\uff0c\u672c\u6765\u662f\u9c7c\u4eae\u7559 \u7ed9\u8fd9\u5c0f\u59d1\u5a18\u7684\u3002\u8fd9\u5c0f\u59d1\u5a18\u8981\u7528\u6765\u6551\u5979\u4e00\u4e2a\u4eb2\u4eba\u3002\u5979\u7684\u4eb2\u4eba\u88ab\u7687\u5e1d\u6240\u9a97\uff0c\u8bef\u670d\u6bd2\u9152\uff0c\u65e0\u836f\u53ef \u533b\uff0c\u6307\u671b\u8bd5\u7528\u829d\u8349\u6765\u6551\u3002\u8fd9\u4eb2\u4eba\u4fbf\u662f\u4ed6\u3002\u201d\u6307\u4e86\u6307\u5510\u6653\u6f9c\uff0c\u9053\uff1a\u201c\u662f\u4ed6\u5b81\u613f\u7ed9\u4f60\uff0c\u8fd9\u5c0f\u59d1\u5a18 \u77e5\u9053\u4e86\u4e5f\u6beb\u4e0d\u6028\u8d23\u3002\u201d\u5415\u56db\u5a18\u6545\u610f\u6f0f\u4e86\u4e00\u70b9\uff1a\u8fd9\u4e3b\u5f20\u662f\u5979\u51fa\u7684\u3002\u6bd2\u9f99\u5c0a\u8005\u5446\u4e86\u4e00\u5446\uff0c\u4e0d\u89c9\u53c8 \u63a9\u9762\u6d41\u6cea\u3002 \u3000\u3000\u5728\u8fd9\u5341\u591a\u5929\u4e4b\u4e2d\uff0c\u9c7c\u4eae\u5df2\u7763\u5de5\u5c06\u5927\u8239\u9020\u597d\uff0c\u5230\u6bd2\u9f99\u5c0a\u8005\u590d\u5143\u4e4b\u540e\uff0c\u5415\u56db\u5a18\u7b49\u4eba\u548c\u4ed6\u544a \u522b\u3002\u6bd2\u9f99\u5c0a\u8005\u604b\u604b\u4e0d\u820d\uff0c\u5ffd\u7136\u8dea\u4e86\u4e0b\u6765\uff0c\u5411\u5415\u56db\u5a18\u3001\u7518\u51e4\u6c60\u3001\u5510\u6653\u6f9c\u3001\u51af\u745b\u7b49\u56db\u4eba\u884c\u4e86\u5927 \u793c\uff0c\u4f17\u4eba\u6025\u5fd9\u6276\u8d77\u3002\u6bd2\u9f99\u5c0a\u8005\u6307\u5929\u8a93\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u5df2\u7ecf\u4f4f\u60ef\uff0c\u5230\u5916\u9762\u53bb\u6df7\u6211\u662f\u4e0d\u613f\u4e86\u3002\u4f46 \u6211\u53d1\u8a93\u6bcf\u5e74\u5fc5\u8981\u6551\u6cbb\u9ebb\u75af\u8005\u5341\u4eba\uff0c\u6bcf\u5e74\u5916\u51fa\u4e09\u6708\uff0c\u5c06\u9ebb\u75af\u75c5\u8005\u625b\u56de\u6b64\u5c9b\u3002\u6cbb\u597d\u4e4b\u540e\uff0c\u518d\u9001\u51fa \u53bb\u3002\u4f60\u4eec\u522b\u77a7\u6211\u7684\u6bd2\u86c7\u51f6\u6076\uff0c\u4ed6\u4eec\u5374\u662f\u533b\u6cbb\u9ebb\u75af\u7684\u5723\u836f\u3002\u201d\u5415\u56db\u5a18\u5408\u4ec0\u9053\uff1a\u201c\u5584\u54c9\uff0c\u5584\u54c9\uff01 \u5c0a\u8005\u65e2\u6709\u6b64\u5ff5\uff0c\u4e5f\u5c31\u4e0d\u5fc5\u9650\u5b9a\u5341\u4eba\uff0c\u5c3d\u529b\u800c\u4e3a\u4fbf\u662f\u4e86\uff01\u201d \u3000\u3000\u4f17\u4eba\u79bb\u5f00\u86c7\u5c9b\uff0c\u626c\u5e06\u5f52\u56fd\uff0c\u821f\u4e2d\u8bf4\u8d77\u6bd2\u9f99\u5c0a\u8005\u4e4b\u4e8b\uff0c\u4f17\u4eba\u65e0\u4e0d\u4f69\u670d\u5415\u56db\u5a18\u89c1\u8bc6\u6df1\u8fdc\uff0c\u540c \u8868\u8d5e\u53f9\u3002 \u3000\u3000\u821f\u884c\u4e24\u65e5\uff0c\u7ed5\u8fc7\u5510\u51af\u4e8c\u4eba\u4ee5\u524d\u5f80\u8fc7\u7684\u5c0f\u5c9b\uff0c\u5c9b\u4e0a\u6811\u6728\u5149\u79c3\u79c3\u7684\uff0c\u53ea\u6709\u65b0\u957f\u7684\u91ce\u8349\u4e00\u7247\u9752 \u7eff\u3002\u5510\u6653\u6f9c\u501a\u680f\u9065\u671b\uff0c\u89e6\u666f\u4f24\u5fc3\uff0c\u60f3\u8d77\u5728\u6b64\u5c0f\u5c9b\u6570\u6708\uff0c\u4e43\u662f\u5e73\u751f\u6700\u6b22\u4e50\u7684\u65e5\u5b50\uff0c\u800c\u4eca\u91cd\u5f52\u5927 \u9646\uff0c\u5fe7\u8651\u590d\u751f\uff0c\u4e0d\u89c9\u767e\u611f\u4ea4\u96c6\u3002\u5415\u56db\u5a18\u7aa5\u89c1\u4ed6\u4e34\u98ce\u6d12\u6cea\uff0c\u4e0a\u524d\u7b11\u9053\uff1a\u201c\u4e16\u5916\u6843\u6e90\uff0c\u7a76\u662f\u5e7b \u5883\u3002\u55a7\u56a3\u5c18\u4e16\uff0c\u5374\u662f\u5bb6\u4e61\u3002\u5510\u5144\u5f1f\u4f55\u6545\u604b\u604b\u4e0d\u820d\uff1f\u201d\u5510\u6653\u6f9c\u5f3a\u7b11\u4e86\u7b11\uff0c\u6101\u6000\u4ecd\u672a\u89e3\u5f00\u3002\u53ea\u6709 \u51af\u745b\u5fc3\u65e0\u6742\u5ff5\uff0c\u4f46\u89c9\u80fd\u5728\u201c\u53d4\u53d4\u201d\u8eab\u8fb9\uff0c\u4fbf\u5df2\u5fc3\u6ee1\u610f\u8db3\uff0c\u4e0d\u7ba1\u5b83\u662f\u4e16\u5916\u6843\u6e90\u8fd8\u662f\u55a7\u56a3\u5c18\u4e16\uff01 \u3000\u3000\u8fc7\u4e86\u6b64\u5c9b\uff0c\u518d\u884c\u6570\u65e5\uff0c\u5df2\u5230\u4e86\u9ec4\u6d77\u6d77\u9762\uff0c\u9c7c\u4eae\u9065\u671b\u6d77\u5916\u9690\u9690\u7565\u73b0\u7684\u51e0\u4e2a\u5c9b\u5c7f\uff0c\u90fd\u662f\u4ee5\u524d \u81ea\u5df1\u6240\u521b\u7684\u57fa\u4e1a\uff0c\u800c\u4eca\u5374\u5982\u6d77\u5916\u795e\u5c71\uff0c\u53ef\u671b\u800c\u4e0d\u53ef\u5373\u3002\u60f3\u8d77\u4ee5\u524d\u6d77\u5916\u79f0\u738b\uff0c\u6709\u5982\u6076\u68a6\uff0c\u4e0d\u7981 \u8001\u6cea\u7eb5\u6a2a\u3002\u6b64\u65f6\u4ed6\u4eec\u7fc1\u5a7f\u5df2\u548c\u597d\u5982\u521d\u3002\u767d\u6cf0\u5b98\u4e0a\u524d\u529d\u6170\uff0c\u9c7c\u4eae\u6536\u6cea\u7b11\u9053\uff1a\u201c\u6211\u66ae\u5e74\u665a\u65e5\uff0c\u5f97 \u4f73\u513f\u4f73\u5a7f\uff0c\u4f8d\u5949\u8eab\u8fb9\uff0c\u80dc\u4e8e\u6d77\u5916\u79f0\u738b\u591a\u77e3\uff01\u6211\u6709\u4f55\u5fe7\uff1f\u53ea\u662f\u9002\u624d\u9065\u671b\u5404\u5be8\uff0c\u4e3a\u4f24\u96be\u7684\u5144\u5f1f\u60b2 \u4f24\uff0c\u4ea6\u4e3a\u81ea\u5df1\u9192\u609f\u592a\u8fdf\u75db\u6094\u800c\u5df2\u3002\u201d \u3000\u3000\u51fa\u4e86\u9ec4\u6d77\uff0c\u4e00\u8def\u987a\u98ce\u7740\u9646\u4e4b\u540e\uff0c\u4f17\u4eba\u6539\u88c5\u6613\u5bb9\uff0c\u5077\u5165\u4eac\u5e08\uff0c\u5230\u4e86\u4eac\u5e08\uff0c\u5df2\u662f\u7aef\u9633\u8282\u540e\uff0c \u7518\u51e4\u6c60\u5e26\u4f17\u4eba\u5230\u4e00\u4e2a\u5e2e\u4f1a\u9f99\u5934\u7684\u5bb6\u4e2d\u4f4f\u5bbf\uff0c\u95ee\u8d77\u8fd1\u4e8b\uff0c\u59cb\u77e5\u5e74\u7fb9\u5c27\u5728\u5c71\u4e1c\u73ed\u5e08\u4e4b\u540e\uff0c\u5df2\u8fdc\u5f81 \u9752\u6d77\u53bb\u4e86\uff0c\u4eac\u4e2d\u5404\u7687\u5b50\uff0c\u6216\u88ab\u6740\u5bb3\uff0c\u6216\u88ab\u8d2c\u9edc\uff0c\u6216\u88ab\u526a\u8d4a\u7fbd\u7ffc\uff0c\u5747\u5df2\u65e0\u80fd\u4e3a\u529b\u3002\u96cd\u6b63\u5e7f\u62db\u6b66 \u58eb\uff0c\u5927\u4fee\u5bab\u6bbf\uff0c\u6311\u9009\u79c0\u5973\uff0c\u70b9\u7f00\u5f97\u4e00\u7247\u201c\u5347\u5e73\u201d\u6c14\u8c61\u3002 \u3000\u3000\u5415\u56db\u5a18\u5230\u4eac\u5e08\u540e\uff0c\u7b2c\u4e8c\u65e5\u4fbf\u53bb\u62dc\u8bbf\u4e00\u4e2a\u540d\u533b\uff0c\u8fd9\u533b\u751f\u59d3\u53f6\uff0c\u4e43\u662f\u5415\u7559\u826f\u7684\u6545\u4ea4\uff0c\u533b\u6c42\u7cbe \u6e5b\uff0c\u5f53\u4e16\u65e0\u53cc\uff0c\u53ea\u662f\u751f\u6027\u602a\u50fb\uff0c\u5e73\u65e5\u8bfb\u4e66\u81ea\u5a31\uff0c\u4e0d\u8f7b\u6613\u7ed9\u4eba\u6cbb\u75c5\uff0c\u5415\u56db\u5a18\u4ee5\u524d\u529d\u6170\u5510\u6653\u6487 \u65f6\uff0c\u529b\u8bf4\u6709\u6cd5\u53ef\u60f3\uff0c\u539f\u662f\u5bc4\u671b\u4e8e\u4ed6\u3002\u4e0d\u6599\u5230\u4e86\u4eac\u5e08\u8bbf\u95ee\uff0c\u8fd9\u4e2a\u59d3\u53f6\u7684\u533b\u751f\u5374\u56e0\u4e0d\u80af\u533b\u6cbb\u8d35 \u4eba\uff0c\u51e0\u4e4e\u88ab\u6295\u4e0b\u72f1\uff0c\u5e78\u6709\u4ed6\u4ee5\u524d\u6cbb\u597d\u7684\u75c5\u4eba\u62a5\u8baf\uff0c\u661f\u591c\u5f03\u5bb6\u51fa\u8d70\uff0c\u800c\u4eca\u5df2\u4e0d\u77e5\u9041\u8ff9\u4f55\u65b9\u4e86\u3002 \u3000\u3000\u51af\u745b\u95fb\u8baf\u5927\u4e3a\u6cae\u4e27\u3002\u5415\u56db\u5a18\u6168\u7136\u8bf4\u9053\uff1a\u201c\u6211\u5165\u5bab\u4e00\u63a2\u5982\u4f55\uff1f\u201d\u5510\u6653\u6f9c\u529b\u529d\u4e0d\u53ef\uff0c\u5415\u56db\u5a18 \u9053\uff1a\u201c\u5141\u796f\u8fd9\u53ae\uff0c\u4e43\u662f\u6211\u5bb6\u7684\u5927\u4ec7\u3002\u6211\u6b64\u756a\u5165\u5bab\uff0c\u5e76\u975e\u4e13\u4e3a\u4f60\u53bb\u3002\u82e5\u6709\u673a\u53ef\u4e58\uff0c\u6211\u5c31\u5c06\u4ed6\u6740 \u4e86\u3002\u201d\u7518\u51e4\u6c60\u529b\u529d\u614e\u91cd\uff0c\u5415\u56db\u5a18\u4ee5\u8eab\u8d1f\u5bb6\u56fd\u6df1\u4ec7\uff0c\u5e76\u8981\u4e3a\u826f\u53cb\u7a83\u53d6\u89e3\u836f\uff0c\u827a\u9ad8\u80c6\u5927\uff0c\u575a\u6301\u8981 \u53bb\u3002\u51af\u745b\u521d\u751f\u4e4b\u728a\uff0c\u4e5f\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u8981\u4e0e\u5415\u56db\u5a18\u540c\u884c\u3002\u5415\u56db\u5a18\u77e5\u5979\u8f7b\u529f\u8d85\u5999\uff0c\u70b9\u5934\u5e94\u5141\u3002\u7518\u51e4 \u6c60\u6258\u4eba\u63a5\u5e94\uff0c\u7ea6\u597d\u4e8c\u4eba\u82e5\u4e94\u9f13\u4e0d\u5f52\uff0c\u7518\u51e4\u6c60\u4fbf\u8981\u7387\u4f17\u5927\u95f9\u7687\u5bab\u3002 \u3000\u3000\u662f\u591c\uff0c\u7518\u51e4\u6c60\u7b49\u5728\u5bb6\u5b88\u5019\uff0c\u7126\u6025\u5f02\u5e38\uff0c\u8fc7\u4e86\u56db\u66f4\uff0c\u5415\u56db\u5a18\u4e0e\u51af\u745b\u5ffd\u5982\u4e00\u53f6\u8f7b\u5815\uff0c\u98d8\u843d\u5ead \u9636\uff0c\u6de1\u6708\u758f\u91cc\u4e4b\u4e0b\uff0c\u53ea\u89c1\u4e24\u4eba\u6ee1\u8eab\u8840\u6c61\u3002\u7518\u51e4\u6c60\u95ee\u8d77\u7ecf\u8fc7\u3002\u5415\u56db\u5a18\u53f9\u9053\uff1a\u201c\u8fd9\u72d7\u7687\u5e1d\u9632\u5907\u771f \u4e2a\u4e25\u5bc6\u5f02\u5e38\uff0c\u6b66\u58eb\u904d\u5e03\u3002\u6bd4\u8d77\u4ed6\u53bb\u5e74\u7a83\u4f4d\u4e4b\u521d\uff0c\u5df2\u662f\u5927\u4e0d\u76f8\u540c\uff01\u6211\u4eec\u5165\u5bab\u672a\u4e45\uff0c\u4fbf\u88ab\u53d1\u73b0\u3002 \u8981\u4e0d\u662f\u4ed7\u7740\u5e08\u95e8\u7edd\u6280\uff0c\u51e0\u4e4e\u9003\u4e0d\u51fa\u6765\uff01\u201d\u51af\u745b\u5174\u594b\u8bf4\u9053\uff1a\u201c\u5415\u59d0\u59d0\u80c6\u5b50\u771f\u5927\uff0c\u6211\u4eec\u770b\u770b\u5df2\u7ed9 \u5305\u56f4\u4e86\uff0c\u5168\u4ed7\u7740\u5979\u5f00\u8def\u3002\u4ed6\u6740\u4e86\u4e09\u540d\u6b66\u58eb\uff0c\u6211\u4e5f\u6740\u4e86\u4e24\u540d\uff0c\u5f04\u5230\u6ee1\u8eab\u8840\u6c61\u3002\u6211\u8fd8\u662f\u7b2c\u4e00\u6b21\u89c1 \u5230\u7687\u5bab\uff0c\u5662\uff0c\u7687\u5bab\u771f\u662f\u5927\u6781\u4e86\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u6709\u89c1\u7740\u5141\u796f\u4e48\uff1f\u201d\u5415\u56db\u5a18\u6124\u7136\u4e0d\u8bed\uff0c \u51af\u745b\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u90a3\u72d7\u7687\u5e1d\u4e48\uff1f\u89c1\u7740\u4e86\uff01\u6211\u5728\u5e74\u7fb9\u5c27\u5bb6\u4e2d\u89c1\u8fc7\uff0c\u6211\u8ba4\u5f97\u4ed6\uff01\u4ed6\u8fdc\u8fdc\u7684\u5411\u6211\u4eec \u558a\u8bdd\uff0c\u4ed6\u8bf4\u4ed6\u6599\u5230\u6211\u4eec\u4f1a\u6765\u3002\u4ed6\u8bf4\u5510\u53d4\u53d4\u8fd8\u6709\u4e03\u5929\u4fbf\u662f\u6b7b\u671f\u3002\u8bf4\u6211\u4eec\u8981\u60f3\u7a8d\u53d6\u89e3\u836f\u90a3\u662f\u4f11 \u60f3\u3002\u53eb\u6211\u5feb\u548c\u5510\u53d4\u53d4\u5165\u5bab\u6c42\u4ed6\u3002\u201d\u7518\u51e4\u6c60\u5583\u5583\u8bf4\u9053\uff1a\u201c\u4e03\u5929\uff0c\u53ea\u6709\u4e03\u5929\u3002\u201d\u51af\u745b\u54bd\u6cea\u8bf4\u9053\uff1a \u201c\u662f\u554a\uff0c\u53ea\u6709\u4e03\u5929\uff0c\u600e\u4e48\u597d\u5462\uff1f\u201d\u51af\u745b\u5728\u5ead\u9636\u8c08\u8bdd\uff0c\u8d8a\u8bf4\u8d8a\u5927\u58f0\u3002\u5510\u6653\u6f9c\u5ffd\u7136\u4ece\u5185\u623f\u8d70\u51fa\u3002 \u60e8\u7b11\u9053\uff0c\u201c\u83b9\u59d0\u745b\u59b9\uff0c\u4f60\u4eec\u4e0d\u5fc5\u4e3a\u6211\u8d39\u5fc3\u4e86\u3002\u7518\u5927\u4fa0\uff0c\u6211\u8981\u6c42\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u516c\u6068\u79c1\u60c5\u4e24\u6101\u7edd\uff0c\u54ea\u7518\u9ec4\u571f\u846c\u82f1\u96c4\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e03\u56de\u3000\u5bc4\u8bed\u9001\u9057\u4e66\u3000\u60c5\u6000\u60c6\u6005\u3000\u820d\u8eab\u56fe\u6551\u96be\u3000\u5fc3\u529b\u7a7a\u629b \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u4e03\u56de \u5bc4\u8bed\u9001\u9057\u4e66\u3000\u60c5\u6000\u60c6\u6005 \u820d\u8eab\u56fe\u6551\u96be\u3000\u5fc3\u529b\u7a7a\u629b \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u5510\u5144\u8bf7\u8bf4\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u6709\u4e00\u5c01\u9057\u4e66\u8bf7\u7518\u5927\u4fa0\u9001\u4e0e\u6211\u7684\u6069\u5e08\u6768\u4ef2 \u82f1\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8fd8\u6709\u4e03\u5929\uff0c\u5927\u53ef\u8bbe\u6cd5\uff0c\u5510\u5144\u5b89\u5fc3\uff0c\u5207\u52ff\u80e1\u601d\u4e71\u60f3\u3002\u201d\u5510\u6653\u6f9c\u82e6\u7b11\u9053\uff1a \u201c\u6b7b\u751f\u6709\u547d\uff0c\u4eba\u529b\u5df2\u4e0d\u53ef\u4e3a\uff0c\u8fd8\u662f\u65e9\u65e9\u5b89\u6392\u540e\u4e8b\uff0c\u514d\u5f97\u8bef\u4e86\u4eba\u5bb6\u3002\u201d\u7518\u51e4\u6c60\u4e0d\u77e5\u5510\u6653\u6f9c\u4e0e\u6768 \u67f3\u9752\u4e4b\u95f4\uff0c\u5df2\u95f9\u81f3\u4e0d\u53ef\u6536\u62fe\u3002\u9053\uff1a\u201c\u4f60\u4e0e\u6768\u8001\u4e43\u662f\u81f3\u4eb2\u7fc1\u5a7f\uff0c\u4f46\u53ea\u6015\u4e03\u65e5\u4e4b\u671f\u592a\u901f\uff0c\u4e0d\u80fd\u8bf7 \u4ed6\u8d76\u5230\u4eac\u5e08\u3002\u201d\u7518\u51e4\u6c60\u8fd8\u4ee5\u4e3a\u5510\u6653\u6f9c\u662f\u60f3\u5728\u4e34\u6b7b\u4e4b\u524d\uff0c\u89c1\u6768\u4ef2\u82f1\u7236\u5973\u4e00\u9762\u3002\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6768 \u6069\u5e08\u4e2d\u4e86\u5510\u5bb6\u7684\u6697\u5668\uff0c\u5df2\u6210\u6b8b\u5e9f\uff0c\u4e0d\u5fc5\u8bf7\u4ed6\u6765\u4e86\u3002\u6211\u53ea\u662f\u60f3\u5728\u4e34\u6b7b\u4e4b\u524d\uff0c\u89e3\u9664\u5a5a\u7ea6\uff0c\u514d\u5f97\u8bef \u4e86\u4ed6\u5973\u513f\u7684\u9752\u6625\u3002\u201d\u8981\u77e5\u65e7\u65e5\u5b88\u793c\u4e4b\u5bb6\uff0c\u82e5\u7136\u5a5a\u7ea6\u672a\u9664\uff0c\u5373\u7b97\u672a\u5a5a\u592b\u6b7b\u540e\uff0c\u8fd8\u662f\u4e0d\u597d\u6539\u5ac1 \u7684\uff0c\u6545\u6b64\u5510\u6653\u6f9c\u6709\u6b64\u4e00\u8a00\u3002 \u3000\u3000\u7518\u51e4\u6c60\u8fd8\u5728\u529d\u6170\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u5c31\u8ba9\u4ed6\u5199\u5427\u3002\u4ed6\u65e2\u6709\u8fd9\u756a\u610f\u601d\uff0c\u4e0d\u8ba9\u4ed6\u529e\uff0c\u53cd\u4ee4\u4ed6\u5fc3\u4e2d \u4e0d\u5b89\u3002\u201d\u7518\u51e4\u6c60\u542c\u8bf4\uff0c\u4e5f\u4fbf\u7f62\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u544a\u4e86\u4e2a\u7f6a\uff0c\u56de\u623f\u53bb\u5199\u9057\u4e66\uff0c\u5415\u56db\u5a18\u4e00\u671b\uff0c\u53ea\u89c1\u51af\u745b\u7d27\u8e59\u53cc\u7709\uff0c\u6ceb\u7136\u6b32\u6ce3\u3002 \u3000\u3000\u5415\u56db\u5a18\u8f7b\u643a\u51af\u745b\u7389\u624b\uff0c\u6b65\u81f3\u5ead\u5fc3\uff0c\u51af\u745b\u5ffd\u9053\uff1a\u201c\u4e3a\u62a5\u5927\u6069\uff0c\u820d\u8eab\u4e8b\u4ec7\uff0c\u7b97\u4e0d\u7b97\u5931\u8282\uff1f\u201d \u5415\u56db\u5a18\u6014\u4e86\u4e00\u5f81\uff0c\u9053\uff1a\u201c\u4e0d\u7b97\u5931\u8282\uff0c\u4f46\u4f55\u987b\u5982\u6b64\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u73b0\u5728\u5df2\u662f\u5c71\u7a77\u6c34\u5c3d\u2026\u2026\u201d\u5415 \u56db\u5a18\u622a\u7740\u9053\uff1a\u201c\u7109\u77e5\u4e0d\u4f1a\u67f3\u6697\u82b1\u660e\u2026\u2026\u5484\uff0c\u4ec0\u4e48\u4eba\uff1f\u201d\u5415\u56db\u5a18\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5c4b\u9876\u4e0a\u5ffd\u7136\u4e00\u9635\u54c8 \u54c8\u5927\u7b11\uff0c\u54c8\u5e03\u9640\u548c\u4e00\u4e2a\u8eab\u7a7f\u5927\u7ea2\u50e7\u888d\u7684\u5587\u561b\u9661\u7136\u73b0\u8eab\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u5fa1\u65e8\u4fc3\u9a7e\uff0c\u8bf7\u5510\u4fa0 \u58eb\u548c\u7433\u8d35\u4eba\u5feb\u5feb\u5165\u5bab\uff01\u201d\u8bf4\u5b8c\u4e4b\u540e\uff0c\u53cc\u53cc\u8df3\u4e0b\u5ead\u5fc3\uff0c\u813e\u7768\u56db\u987e\u3002 \u3000\u3000\u8fd9\u7ea2\u8863\u5587\u561b\u540d\u5524\u989d\u97f3\u548c\u5e03\uff0c\u4e43\u7ea2\u6559\u7684\u5927\u5587\u561b\uff0c\u96cd\u6b63\u5949\u5587\u561b\u4e3a\u56fd\u6559\uff0c\u81ea\u4e86\u56e0\u6b7b\u540e\uff0c\u96cd\u6b63\u6025 \u987b\u4e00\u4eba\u8865\u7f3a\uff0c\u989d\u97f3\u548c\u5e03\u6b66\u529f\u5728\u7ea2\u6559\u5587\u561b\u4e2d\u9996\u5c48\u4e00\u6307\uff0c\u96cd\u6b63\u4e43\u662f\u8bc6\u8d27\u4e4b\u4eba\uff0c\u53ec\u4ed6\u4e00\u8bd5\uff0c\u89c1\u4ed6\u6b66 \u529f\u4e0d\u5728\u4e86\u56e0\u4e4b\u4e0b\uff0c\u8f7b\u529f\u5c1a\u5728\u4e86\u56e0\u4e4b\u4e0a\u3002\u7acb\u5373\u5c01\u4ed6\u4e3a\u5927\u56fd\u5e08\uff0c\u5e76\u5c06\u4ee5\u524d\u7684\u56db\u7687\u5e9c\u6539\u4e3a\u96cd\u548c\u5bab\uff0c \u7ed9\u5728\u4eac\u7684\u5587\u561b\u5c45\u4f4f\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u5415\u56db\u5a18\u548c\u51af\u745b\u5927\u95f9\u7687\u5bab\uff0c\u989d\u97f3\u548c\u5e03\u8d76\u6765\u65f6\uff0c\u5979\u4eec\u5df2\u6740\u51fa\u5bab\u5916\uff0c\u989d\u97f3\u548c\u5e03\u4e0e\u54c8\u5e03\u9640 \u6025\u6025\u8ffd\u8e2a\uff0c\u867d\u7136\u8ffd\u8d76\u4e0d\u4e0a\uff0c\u4f46\u5c1a\u4e0d\u81f3\u76f8\u5dee\u592a\u8fdc\uff0c\u5415\u56db\u5a18\u7684\u8eab\u5f62\u9690\u5728\u4e1c\u534e\u95e8\u5916\u7684\u4e00\u6761\u80e1\u540c\uff0c\u5374 \u5df2\u88ab\u4ed6\u4eec\u53d1\u89c9\u3002\u4ed6\u4eec\u4fbf\u9010\u5c4b\u7aa5\u67e5\uff0c\u7ec8\u4e8e\u53d1\u73b0\u3002 \u3000\u3000\u5510\u6653\u6f9c\u6b63\u5728\u5c4b\u5185\u5199\u597d\u9057\u4e66\uff0c\u5ffd\u542c\u5f97\u54c8\u5e03\u9640\u5927\u58f0\u201c\u5ba3\u8bcf\u201d\uff0c\u52c3\u7136\u5927\u6012\uff0c\u4e00\u8dc3\u800c\u51fa\uff0c\u6717\u58f0\u65a5 \u9053\uff1a\u201c\u6211\u5b81\u6b7b\u4e0d\u8fb1\uff0c\u4f60\u60f3\u6211\u5165\u5bab\u54c0\u6c42\uff0c\u4e43\u662f\u5984\u60f3\uff0c\u7ed9\u6211\u6eda\u5f00\uff01\u201d\u5415\u56db\u5a18\u5ffd\u9053\uff1a\u201c\u53eb\u4ed6\u6eda\u5f00\uff0c \u90a3\u592a\u4fbf\u5b9c\u4ed6\u4e86\uff01\u96be\u5f97\u54c8\u5927\u603b\u7ba1\u5230\u6b64\uff0c\u54b1\u4eec\u53ef\u8981\u8bf7\u4ed6\u5c48\u9a7e\u6682\u7559\u4e86\uff01\u201d\u7518\u51e4\u6c60\u4e00\u542c\uff0c\u4fbf\u77e5\u5415\u56db\u5a18 \u7684\u7528\u610f\uff0c\u4e43\u662f\u60f3\u628a\u54c8\u5e03\u9640\u64d2\u7740\uff0c\u6362\u53d6\u89e3\u836f\u3002\u5f53\u4e0b\u9996\u5148\u53d1\u96be\uff0c\u53cc\u81c2\u4e00\u5708\uff0c\u547c\u547c\u53d1\u638c\u3002\u54c8\u5e03\u9640\u63a5 \u4e86\u4e00\u62db\uff0c\u5404\u9000\u4e09\u6b65\u3002\u989d\u97f3\u548c\u5e03\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u6b7b\u6d3b\u7684\u5bb6\u4f19\uff0c\u4f60\u4eec\u51ed\u4ec0\u4e48\u6562\u7559\u4eba\uff1f\u201d\u5415\u56db\u5a18\u8eab \u5f62\u5fae\u52a8\uff0c\u5530\u7684\u4e00\u5251\uff0c\u75be\u5982\u7535\u95ea\uff0c\u76f4\u6307\u54bd\u5589\uff0c\u9053\uff1a\u201c\u51ed\u8fd9\u53e3\u5251\u5c31\u8981\u53eb\u4f60\u7559\u4e0b\uff01\u201d \u3000\u3000\u5d16\u77e5\u989d\u97f3\u548c\u5e03\u6b66\u529f\u786e\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u5415\u56db\u5a18\u7684\u5251\u582a\u582a\u523a\u5230\uff0c\u5ffd\u89c9\u5251\u5c16\u4e00\u79fb\uff0c\u6ed1\u8fc7\u4e00\u8fb9\uff0c\u53ea \u89c1\u989d\u9752\u548c\u5e03\uff0c\u624b\u6325\u62c2\u5c16\uff0c\u4e00\u6325\u4e00\u7ed5\uff0c\u7adf\u7136\u4f7f\u51fa\u501f\u529b\u6253\u529b\u7684\u4e0a\u4e58\u6b66\u529f\uff0c\u5c06\u81ea\u5df1\u5b9d\u5251\u7f20\u7740\u3002\u5415\u56db \u5a18\u5fae\u5403\u4e00\u60ca\uff0c\u970d\u5730\u4e00\u4e2a\u6643\u8eab\uff0c\u501f\u52bf\u4e00\u62e7\uff0c\u5251\u53d1\u5982\u98ce\uff0c\u5f39\u6307\u4e4b\u95f4\uff0c\u8fde\u53d1\u4e09\u5251\uff0c\u989d\u97f3\u548c\u5e03\u51dd\u8eab\u4e0d \u52a8\uff0c\u62c2\u5c18\u5de6\u53f3\u6446\u52a8\uff0c\u4e5f\u8fde\u63a5\u4e09\u62db\u3002\u4e24\u4eba\u4ee5\u4e0a\u4e58\u6b66\u529f\u76f8\u640f\uff0c\u5404\u4e0d\u76f8\u6bd4\u3002\u989d\u97f3\u548c\u5e03\u60f3\u628a\u5415\u56db\u5a18\u7684 \u5b9d\u5251\u593a\u51fa\u624b\u53bb\uff0c\u56fa\u662f\u4e0d\u80fd\uff0c\u5415\u56db\u5a18\u60f3\u628a\u4ed6\u6740\u4f24\uff0c\u5374\u4e5f\u4e0d\u5f97\uff01 \u3000\u3000\u54c8\u5e03\u9640\u548c\u7518\u51e4\u6c60\u4e5f\u662f\u529f\u529b\u6089\u654c\uff0c\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u62c6\u4e86\u5341\u4f59\u5eff\u62db\u3002\u5510\u6653\u6f9c\u62d4\u51fa\u6e38 \u9f99\u5b9d\u5251\uff0c\u4e0a\u6765\u52a9\u6218\u3002\u54c8\u5e03\u9640\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u7684\u6bd2\u4f24\u5df2\u5f00\u59cb\u53d1\u4f5c\uff0c\u4f60\u60f3\u65e9\u70b9\u6b7b\u5417\uff1f\u201d\u51af\u745b\u4e00\u628a \u5c06\u5c06\u5510\u6653\u6f9c\u8863\u8896\u62c9\u7740\uff0c\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u4e14\u9000\u4e0b\uff0c\u6211\u6709\u4e3b\u610f\u3002\u201d\u5510\u6653\u6f9c\u6447\u4e86\u6447\u5934\uff0c\u51af\u745b\u9053\uff1a \u201c\u4f60\u4e0d\u542c\u8bdd\uff0c\u6211\u5c31\u5148\u6b7b\u7ed9\u4f60\u770b\uff0c\u5feb\u5feb\u56de\u53bb\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u53f9\u4e86\u53e3\u6c14\uff0c\u8179\u4e2d\u5ffd\u89c9\u4e00\u9635\u7ede\u75db\uff0c\u53ea\u597d\u9000\u56de\u5c4b\u5185\u3002\u989d\u97f3\u548c\u5e03\u4e0e\u5415\u56db\u5a18\u5404\u4ee5\u4e0a\u4e58\u6b66\u529f \u76f8\u640f\uff0c\u6597\u4e86\u4e94\u4e03\u5341\u62db\uff0c\u5140\u81ea\u4e0d\u5206\u80dc\u8d1f\uff0c\u51af\u745b\u62d4\u51fa\u77ed\u5251\uff0c\u6b63\u62df\u76f8\u52a9\uff0c\u5ffd\u542c\u5f97\u989d\u97f3\u548c\u5e03\u4e00\u58f0\u957f \u5578\uff0c\u5899\u5934\u4e0a\u53c8\u73b0\u51fa\u4e86\u56db\u540d\u7ea2\u6559\u5587\u561b\uff0c\u4e00\u5f0f\u5927\u7ea2\u50e7\u888d\uff0c\u9ed1\u725b\u89d2\u5e3d\uff0c\u5341\u5206\u523a\u76ee\u3002\u54c8\u5e03\u9640\u53c8\u559d\u9053\uff1a \u201c\u656c\u9152\u4e0d\u5403\u4f60\u4eec\u8981\u5403\u7f5a\u9152\u5417\uff1f\u7433\u8d35\u4eba\uff0c\u4f60\u542c\u4e0d\u542c\u7687\u4e0a\u5ba3\u8bcf\uff1f\u201d \u3000\u3000\u51af\u745b\u628a\u5251\u4e00\u63d2\uff0c\u8fce\u4e0a\u524d\u53bb\uff0c\u56db\u540d\u5587\u561b\uff0c\u4e00\u9f50\u8dc3\u4e0b\uff0c\u51af\u745b\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4f11\u5f97\u65e0\u793c\uff0c\u6211\u548c\u4f60 \u4eec\u8fdb\u5bab\u9762\u5723\uff01\u201d\u7518\u51e4\u6c60\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u51af\u59d1\u5a18\u4f60\u5c82\u53ef\u8f7b\u8eab\u524d\u5f80\uff1f\u201d\u54c8\u5e03\u9640\u9053\uff1a \u201c\u5510\u6653\u6f9c\u5462\uff1f\u7687\u4e0a\u8981\u7684\u662f\u4f60\u4eec\u4e24\u4eba\u4e00\u9053\u8fdb\u5bab\u3002\u201d\u51af\u745b\u5df2\u8dd1\u5230\u5587\u561b\u961f\u91cc\uff0c\u626c\u58f0\u7b54\u9053\uff1a\u201c\u6211\u81ea\u548c \u7687\u4e0a\u8bf4\u4e2a\u6e05\u695a\u3002\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u8981\u4f60\u63d2\u53e3\uff1f\u4f60\u518d\u7f57\u5506\uff0c\u8fde\u6211\u4e5f\u4e0d\u53bb\u4e86\u3002\u201d\u54c8\u5e03\u9640\u5fd9\u9053\uff1a\u201c\u662f \u662f\uff0c\u5974\u624d\u966a\u7433\u8d35\u4eba\u56de\u5bab\u3002\u201d\u7518\u51e4\u6c60\u4e0e\u5415\u56db\u5a18\u5f85\u8981\u62e6\u963b\uff0c\u65e0\u5948\u654c\u624b\u592a\u5f3a\uff0c\u90fd\u88ab\u7eca\u4f4f\uff0c\u51af\u745b\u5df2\u968f \u56db\u4e2a\u5587\u561b\u7ffb\u8fc7\u5899\u5934\u3002 \u3000\u3000\u5510\u6653\u6f9c\u5728\u5c4b\u4e2d\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5fc3\u4e2d\u5927\u75db\uff0c\u53c8\u518d\u8dd1\u51fa\uff0c\u9ad8\u53eb\u9053\uff1a\u201c\u745b\u59b9\uff01\u745b\u59b9\uff01\u201d\u51af\u745b\u5728 \u5899\u5916\u5e94\u9053\uff1a\u201c\u5510\u53d4\u53d4\uff0c\u4f60\u5feb\u56de\u53bb\u3002\u6211\u66ff\u4f60\u53bb\u62ff\u89e3\u836f\u4e86\uff01\u201d\u58f0\u97f3\u4e0e\u811a\u6b65\u58f0\u6e10\u8fdc\u6e10\u6c93\uff0c\u5510\u6653\u6f9c\u5fcd \u7740\u75bc\u75db\uff0c\u8dc3\u4e0a\u5899\u5934\uff0c\u989d\u97f3\u548c\u5e03\u559d\u9053\uff1a\u201c\u4f60\u6765\u5f97\u597d\uff01\u4f60\u4e5f\u968f\u6211\u56de\u53bb\uff01\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u624b\u4e2d\u62c2\u5c18\u5f53 \u7a7a\u5377\u4e0b\uff0c\u5510\u6653\u6f9c\u6a2a\u5251\u659c\u524a\uff0c\u53ea\u89c9\u4e00\u80a1\u52b2\u98ce\uff0c\u62c2\u8155\u5982\u5200\uff0c\u5b9d\u5251\u51e0\u4e4e\u7ed9\u593a\u51fa\u624b\uff0c\u989d\u97f3\u548c\u5e03\u5de6\u624b\u4e00 \u626c\uff0c\u4e94\u722a\u9f50\u4e0b\uff0c\u5510\u6653\u6f9c\u62db\u67b6\u4e0d\u4f4f\uff0c\u7ffb\u8eab\u8dcc\u4e0b\u5899\u5934\u3002\u989d\u4e86\u97f3\u548c\u5e03\u4e00\u4e2a\u201c\u731b\u9e70\u6251\u5154\u201d\uff0c\u8ddf\u8e2a\u4e0b \u51fb\uff0c\u5415\u56db\u5a18\u5c55\u5251\u6321\u4f4f\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u53c8\u5bf9\u653b\u4e86\u5341\u6765\u62db\u3002\u54c8\u5e03\u9640\u9053\uff1a\u201c\u7433\u8d35\u4eba\u5df2\u56de\u5bab\uff0c\u8fd8\u548c\u4ed6\u4eec \u6b6a\u7f20\u4f5c\u751a\uff1f\u201d\u6d41\u661f\u53cc\u9524\u5377\u5730\u4e00\u6536\uff0c\u98de\u51fa\u5899\u5916\uff0c\u989d\u97f3\u548c\u5e03\u5fc3\u60f3\u3002\u518d\u6253\u4e0b\u53bb\u4e5f\u672a\u5fc5\u8ba8\u5f97\u4e86\u4fbf\u5b9c\uff0c \u5bab\u4e2d\u9ad8\u624b\u672a\u96c6\uff0c\u521a\u624d\u6765\u7684\u53ea\u662f\u4ed6\u7684\u56db\u540d\u5f92\u5f1f\uff0c\u672c\u4e8b\u6709\u9650\uff0c\u4e5f\u4fbf\u89c1\u597d\u5373\u6536\uff0c\u8df3\u51fa\u5899\u5916\u3002 \u3000\u3000\u7518\u51e4\u6c60\u6c14\u547c\u547c\u7684\u9053\uff1a\u201c\u51af\u59d1\u5a18\u600e\u7684\u8fd9\u4e48\u5b69\u5b50\u6c14\uff1f\u6211\u5c31\u4e0d\u4fe1\u7687\u5e1d\u4f1a\u7ed9\u5979\u89e3\u836f\uff01\u201d\u5415\u56db\u5a18\u53f9 \u53e3\u6c14\u9053\uff1a\u201c\u5979\u672c\u6765\u8fd8\u662f\u4e2a\u5b69\u5b50\u561b\uff0c\u8fd9\u53eb\u505a\u75c5\u6025\u4e71\u6295\u533b\uff0c\u5979\u6ca1\u6cd5\u53ef\u60f3\uff0c\u53ea\u597d\u5982\u6b64\u3002\u5979\u4e5f\u662f\u4e00\u7247 \u4fa0\u9aa8\u67d4\u80a0\uff0c\u54b1\u4eec\u5c82\u53ef\u602a\u5979\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u53ea\u6050\u5979\u6b64\u53bb\u53ea\u662f\u9001\u7f8a\u5165\u864e\u53e3\uff0c\u975e\u552f\u65e0 \u8865\u4e8e\u4e8b\uff0c\u4e14\u8981\u8eab\u53d7\u5176\u5bb3\uff01\u201d\u5510\u6653\u6f9c\u5fc3\u4e2d\u767e\u611f\u4ea4\u96c6\uff0c\u9053\uff1a\u201c\u53cd\u6b63\u6211\u662f\u8981\u6b7b\u7684\u4e86\uff0c\u5f85\u6211\u4e5f\u8fdb\u5bab \u5427\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4e00\u4e2a\u9001\u6b7b\u8fd8\u4e0d\u591f\u5417\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5979\u82e5\u820d\u8eab\u4e3a\u6211\uff0c\u6211\u53c8\u5c82\u80fd\u817c\u989c\u5077 \u6d3b\uff1f\u201d\u7518\u51e4\u6c60\u542c\u4e86\u6b64\u8a00\uff0c\u4e0d\u89c9\u4e00\u6115\uff0c\u8fd9\u624d\u89c9\u51fa\u5176\u4e2d\u5c1a\u6709\u522b\u60c5\u3002\u5415\u56db\u5a18\u6e29\u67d4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6653 \u6f9c\uff0c\u4f60\u4e0d\u8981\u80e1\u601d\u4e71\u60f3\u4e86\u3002\u4e03\u54e5\uff0c\u54b1\u4eec\u4e14\u518d\u8bbe\u6cd5\u3002\u8fd9\u91cc\u662f\u4e0d\u80fd\u518d\u4f4f\u7684\u4e86\uff01\u201d \u3000\u3000\u7518\u51e4\u6c60\u4e0e\u5415\u56db\u5a18\u5546\u91cf\u4ec0\u4e48\u529e\u6cd5\uff0c\u6682\u4e14\u6309\u4e0b\u4e0d\u63d0\u3002\u4e14\u8bf4\u54c8\u5e03\u9640\u4e0e\u989d\u97f3\u548c\u5e03\u5e26\u4e86\u51af\u745b\u56de\u5bab\uff0c \u5df2\u662f\u9ece\u660e\u65f6\u5206\uff0c\u7687\u5e1d\u5750\u671d\u672a\u56de\u3002\u54c8\u5e03\u9640\u5c06\u51af\u745b\u4ea4\u4e0e\u5bab\u5a25\u6253\u626e\uff0c\u81ea\u5df1\u5728\u5916\u76d1\u5b88\u3002\u51af\u745b\u6309\u4e0b\u706b \u6c14\uff0c\u4efb\u7531\u5bab\u5a25\u66ff\u5979\u6253\u626e\uff0c\u8f7b\u5300\u7c89\u8138\uff0c\u7ec6\u70b9\u94c5\u534e\uff0c\u66f4\u6362\u5bab\u88c5\uff0c\u4f69\u5e26\u9970\u7269\uff0c\u6253\u626e\u5f97\u660e\u8273\u7167\u4eba\uff0c\u5343 \u5996\u767e\u5a9a\uff0c\u51af\u745b\u4e00\u58f0\u4e0d\u54cd\uff0c\u53ea\u662f\u90a3\u53e3\u77ed\u5251\uff0c\u5374\u4e0d\u51c6\u5bab\u5a25\u62ff\u8d70\uff0c\u4ecd\u662f\u7d27\u85cf\u6000\u4e2d\u3002 \u3000\u3000\u8fd9\u65e5\u653f\u4e8b\u751a\u591a\uff0c\u96cd\u6b63\u4e00\u4e00\u5904\u7406\u5b8c\u6bd5\uff0c\u53c8\u53ec\u89c1\u4e86\u4e24\u4e2a\u5916\u653e\u7684\u5927\u81e3\uff0c\u56de\u5230\u5185\u82d1\uff0c\u5df2\u662f\u54cd\u5348\u65f6 \u5206\u3002\u542c\u5f97\u989d\u97f3\u548c\u5e03\u548c\u54c8\u5e03\u9640\u8fdb\u7980\uff0c\u8bf4\u662f\u7433\u8d35\u4eba\u81ea\u613f\u56de\u5bab\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u7acb\u523b\u5728\u7fca\u5764\u5bab\u53ec\u89c1\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u56db\u540d\u5bab\u5a25\u5c06\u51af\u745b\u5f15\u8fdb\u3002\u96cd\u6b63\u4e00\u89c1\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e00\u5e74\u4e0d\u89c1\uff0c\u4f60\u51fa\u843d\u5f97\u8d8a\u53d1\u6807 \u81f4\u4e86\uff01\u201d\u51af\u745b\u6012\u4e0a\u7709\u68a2\uff0c\u5374\u4e0d\u53d1\u4f5c\u3002\u96cd\u6b63\u53c8\u7b11\u9053\uff1a\u201c\u7f8e\u4eba\u513f\uff0c\u4f60\u600e\u4e48\u4e0d\u5f00\u53e3\u5440\uff1f\u201d\u5bf9\u5bab\u5a25 \u9053\uff1a\u201c\u5c06\u5979\u7684\u8863\u8896\u5377\u8d77\u6765\uff0c\u5f85\u6715\u9a8c\u770b\u5979\u7684\u5b88\u5bab\u7802\u8fd8\u5728\u4e0d\u5728\uff1f\u201d\u4e24\u540d\u5bab\u5a25\u4e0a\u524d\u52a8\u624b\uff0c\u51af\u745b\u53cc\u81c2 \u4e00\u632f\uff0c\u4e24\u540d\u5bab\u5a25\u201c\u54ce\u54df\u201d\u5927\u53eb\uff0c\u7ed9\u5f39\u51fa\u4e00\u4e08\u5f00\u5916\u3002\u51af\u745b\u6012\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u96cd\u6b63\u9053\uff1a\u201c\u9a8c \u4e86\u4e4b\u540e\uff0c\u6715\u624d\u597d\u518c\u5c01\u4f60\u505a\u8d35\u5983\u5440\uff01\u201d\u51af\u745b\u9053\uff1a\u201c\u4f60\u4e0d\u5148\u628a\u89e3\u836f\u7ed9\u6211\uff0c\u4f11\u60f3\u5f97\u6211\u4f9d\u4ece\uff01\u201d\u96cd\u6b63 \u9053\uff1a\u201c\u55ef\uff0c\u662f\u4e86\u3002\u4f60\u8ba4\u7684\u90a3\u4e2a\u5510\u53d4\u53d4\u5462\uff1f\u4e3a\u4ec0\u4e48\u4ed6\u4e0d\u6765\u6c42\u6211\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u4ed6\u662f\u94c1\u94ee\u94ee\u7684\u6c49 \u5b50\uff0c\u5c82\u80fd\u6c42\u4f60\uff01\u89e3\u836f\u4f60\u613f\u7ed9\u5c31\u7ed9\uff0c\u4e0d\u613f\u7ed9\u4e5f\u7531\u4f60\u3002\u201d\u96cd\u6b63\u9053\uff1a\u201c\u7ed9\u4e86\u5982\u4f55\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u7ed9 \u4e86\uff0c\u6211\u5c31\u5728\u5bab\u4e2d\u505a\u4f60\u7684\u5974\u5a62\u3002\u201d\u96cd\u6b63\u7709\u5f00\u773c\u7b11\uff0c\u9053\uff1a\u201c\u5c82\u6562\u59d4\u5c48\u4f60\u505a\u5974\u5a62\uff0c\u4f60\u5c31\u8981\u662f\u7687\u540e\u4e4b \u4e0b\u7684\u7b2c\u4e00\u4eba\u4e86\u3002\u201d\u500f\u53c8\u53d8\u8272\u95ee\u9053\uff1a\u201c\u4e0d\u7ed9\u53c8\u5982\u4f55\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u62fc\u7740\u4e0e\u4f60\u8840\u6e85\u5ead\u9636\uff0c\u7edd\u4e0d\u4e3a \u4f60\u6240\u8fb1\uff01\u201d \u3000\u3000\u96cd\u6b63\u773c\u73e0\u6eda\u8f6c\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u77a7\u5728\u4f60\u7684\u4efd\u4e0a\uff0c\u8fd9\u89e3\u836f\u6211\u8fd8\u80fd\u4e0d\u7ed9\u5417\uff1f\u54c8\u5e03\u9640\u2014 \u2014\u201d\u54c8\u5e03\u9640\u4e0e\u989d\u97f3\u548c\u5e03\u5728\u7fca\u5764\u5bab\u5916\u9762\u4f3a\u5019\uff0c\u542c\u5f97\u7687\u5e1d\u53eb\u5524\uff0c\u201c\u55b3\u201d\u7684\u5e94\u4e86\u4e00\u58f0\uff0c\u96cd\u6b63\u5927\u58f0\u5429 \u5490\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8fdb\u6765\u3002\u4f60\u901f\u5c06\u89e3\u836f\u9001\u7ed9\u5510\u6653\u6f9c\u5427\u3002\u53eb\u4ed6\u5feb\u5feb\u51fa\u4eac\uff0c\u4e0d\u51c6\u5bf9\u4ed6\u7559\u96be\u3002\u201d\u54c8\u5e03\u9640 \u5e94\u9053\uff1a\u201c\u5974\u624d\u9075\u547d\uff01\u201d\u683c\u767b\u683c\u767b\u8d70\u51fa\u7fca\u5764\u5bab\u5916\u7684\u957f\u5eca\uff0c\u811a\u6b65\u58f0\u6545\u610f\u653e\u5f97\u975e\u5e38\u4e4b\u54cd\u3002 \u3000\u3000\u96cd\u6b63\u6ee1\u9762\u5806\u6b22\uff0c\u5978\u7b11\u9053\uff1a\u201c\u5982\u4f55\uff1f\u5929\u5b50\u65e0\u620f\u8a00\uff0c\u4f60\u8bf4\u8bdd\u53ef\u4e5f\u5f97\u7b97\u6570\u554a\uff01\u201d\u4f38\u624b\u6765\u62c9\u51af \u745b\uff0c\u51af\u745b\u67f3\u7709\u4e00\u7ad6\uff0c\u8863\u8896\u4e00\u62c2\uff0c\u556a\u7684\u4e00\u54cd\uff0c\u62c2\u5230\u96cd\u6b63\u80f8\u524d\uff0c\u96cd\u6b63\u90a3\u4e48\u5f3a\u7684\u6b66\u529f\uff0c\u4e5f\u611f\u5230\u8fa3\u8fa3 \u4f5c\u75db\uff0c\u6025\u5fd9\u95ea\u5f00\uff0c\u559d\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u8981\u53cd\u6094\u4e86\u5417\uff1f\u54c8\u5e03\u9640\u8fd8\u672a\u51fa\u5bab\uff0c\u4f60\u53cd\u6094\u5f97\u672a\u514d\u592a\u65e9\u4e86\uff0c \u6211\u7acb\u523b\u4fbf\u6d3e\u4eba\u8ffd\u4ed6\u56de\u6765\u3002\u201d \u3000\u3000\u51af\u745b\u9053\uff1a\u201c\u54b1\u4eec\u8bf4\u4e00\u53e5\u7b97\u4e00\u53e5\uff0c\u53ef\u4e0d\u8bb8\u4f60\u73a9\u82b1\u62db\uff01\u201d\u96cd\u6b63\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u8fde\u8054\u4e5f\u4e0d \u4fe1\u5417\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u5c31\u662f\u4e0d\u4fe1\u3002\u8c01\u77e5\u4f60\u9001\u7684\u662f\u4e0d\u662f\u89e3\u836f\uff1f\u6211\u8981\u7b49\u5f97\u5230\u4e86\u5510\u53d4\u53d4\u7684\u4eb2\u7b14\u4fe1\u4ef6\uff0c \u8bf4\u786e\u5b9e\u662f\u75ca\u6108\u4e4b\u540e\uff0c\u624d\u80fd\u4f9d\u4f60\u3002\u4f60\u73b0\u5728\u9a97\u6211\uff0c\u90a3\u53ef\u4e0d\u6210\uff01\u4f60\u5f53\u6211\u8fd8\u662f\u5c0f\u5b69\u5b50\u5417\uff1f\u201d \u3000\u3000\u96cd\u6b63\u4ff1\u5979\u6b66\u529f\u5389\u5bb3\uff0c\u4e0d\u6562\u786c\u6765\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u53c8\u751f\u8be1\u8ba1\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5b9a\u8981\u5510\u6653\u6f9c\u7684\u4e66 \u4fe1\uff0c\u6715\u7ed9\u4f60\u6562\u6765\u4fbf\u662f\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u90a3\u4f60\u8fd8\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u5f97\u4e86\u4e66\u4fe1\uff0c\u4f60\u518d\u89c1\u6211\u3002\u201d\u96cd\u6b63 \u9053\uff1a\u201c\u554a\uff0c\u597d\u5927\u7684\u67b6\u5b50\u3002\u201d\u51af\u745b\u9762\u631f\u5bd2\u971c\uff0c\u76ee\u5149\u4e2d\u81ea\u6709\u4e00\u80a1\u51db\u4e0d\u53ef\u72af\u7684\u795e\u60c5\uff0c\u96cd\u6b63\u6253\u4e86\u4e00\u4e2a \u5bd2\u5664\uff0c\u9053\uff1a\u201c\u597d\uff0c\u90fd\u4f9d\u4f60\uff0c\u8c05\u4f60\u4e5f\u9003\u4e0d\u4e86\u6211\u7684\u638c\u5fc3\u3002\u201d\u60bb\u60bb\u7136\u9000\u51fa\u5bab\u5916\u3002 \u3000\u3000\u51af\u745b\u867d\u7136\u9605\u5386\u65e0\u591a\uff0c\u4f46\u5bf9\u7687\u5e1d\u5374\u662f\u4e45\u5177\u6212\u5fc3\uff0c\u7cbe\u7ec6\u5f97\u5f88\u3002\u5bab\u5a25\u9001\u6765\u7684\u996e\u98df\uff0c\u5979\u90fd\u8981\u522b\u4eba \u8bd5\u8fc7\uff0c\u7136\u540e\u518d\u5c1d\uff0c\u96cd\u6b63\u53e6\u6709\u6253\u7b97\uff0c\u996d\u83dc\u4e2d\u5012\u6ca1\u6709\u653e\u4e0b\u8ff7\u836f\u3002 \u3000\u3000\u4e0d\u89c9\u53c8\u5230\u665a\u95f4\uff0c\u5bab\u4e2d\u7ea2\u70db\u9ad8\u70e7\uff0c\u5e7d\u9999\u6ee1\u5802\uff0c\u96cd\u6b63\u53c8\u8fdb\u6765\u4e86\u3002\u51af\u745b\u6273\u8138\u95ee\u9053\uff1a\u201c\u4f60\u5c06\u89e3\u836f \u9001\u5230\u6ca1\u6709\uff1f\u6211\u5510\u53d4\u53d4\u7684\u4fe1\u4ef6\u5462\uff1f\u201d\u96cd\u6b63\u7b11\u9053\uff1a\u201c\u53d6\u6765\u4e86\uff01\u201d\u51af\u745b\u5fc3\u4e2d\u5fd0\u5fd1\uff0c\u65e2\u559c\u4e14\u5fe7\u3002\u9053\uff1a \u201c\u62ff\u6765\u6211\u770b\uff01\u201d\u96cd\u6b63\u9053\uff1a\u201c\u6765\u4eba\u54ea\uff01\u201d\u95e8\u5916\u201c\u55b3\u201d\u7684\u4e00\u58f0\uff0c\u989d\u97f3\u548c\u5e03\u63a8\u95e8\u8d70\u8fdb\uff0c\u624b\u4e2d\u62ff\u7684\u679c \u7136\u662f\u4e00\u5c01\u4fe1\u3002 \u3000\u3000\u51af\u745b\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\u3002\u770b\u5b8c\u4fe1\u540e\uff0c\u4fbf\u662f\u6211\u8840\u6e85\u4e4b\u65f6\uff01\u201d\u5979\u65e9\u51b3\u5b9a\u81ea\u6740\u4ee5 \u62a5\u6653\u6f9c\uff0c\u53ea\u5f85\u770b\u5b8c\u4fe1\u540e\uff0c\u4fbf\u8981\u62d4\u5251\u81ea\u520e\u3002 \u3000\u3000\u96cd\u6b63\u9053\uff1a\u201c\u628a\u4fe1\u4ea4\u7ed9\u7433\u8d35\u4eba\u4eb2\u9605\u3002\u201d\u989d\u97f3\u548c\u5e03\u7f13\u7f13\u8d70\u8fd1\uff0c\u51af\u745b\u5168\u8eab\u98a4\u6296\uff0c\u4f38\u624b\u53bb\u63a5\u3002\u5ffd \u542c\u5f97\u96cd\u6b63\u5927\u559d\u9053\uff1a\u201c\u628a\u5979\u7684\u6b66\u529f\u5e9f\u4e86\uff01\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u989d\u97f3\u548c\u5e03\u624b\u638c\u4e00\u7ffb\uff0c\u53cc\u6307\u4e00\u5939\uff0c\u4fe1\u5c01\u91cc\u85cf\u7684\u4e43\u662f\u4e00\u53e3\u94f6\u9488\uff0c\u8fd9\u65f6\u7a7f\u4e86 \u51fa\u6765\uff0c\u94f6\u5149\u95ea\u95ea\uff0c\u5411\u51af\u745b\u75be\u523a\u3002\u8fd9\u4e00\u4e0b\u53d8\u51fa\u610f\u5916\uff0c\u731d\u4e0d\u53ca\u9632\uff0c\u51af\u745b\u62fc\u4e86\u6027\u547d\uff0c\u53cc\u638c\u6025\u51fb\uff0c\u547c \u547c\u4e24\u638c\uff0c\u90fd\u6253\u5230\u989d\u97f3\u548c\u5e03\u8eab\u4e0a\uff0c\u4f46\u5979\u8eab\u4e0a\u4e5f\u88ab\u989d\u97f3\u548c\u5e03\u4e00\u8fde\u523a\u4e86\u51e0\u9488\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u96cd\u6b63\u7687\u5e1d\u5e03\u597d\u7684\u5708\u5957\uff0c\u539f\u6765\u989d\u97f3\u548c\u5e03\u6709\u4e00\u79cd\u72ec\u95e8\u7684\u6b66\u529f\uff0c\u80fd\u7528\u94f6\u9488\u9694\u8863\u523a\u7a74\uff0c\u5c06 \u654c\u4eba\u7684\u771f\u5143\u4e4b\u6c14\u6cc4\u6389\uff0c\u591a\u597d\u6b66\u529f\uff0c\u4e5f\u4f1a\u6d88\u5931\u3002\u975e\u91cd\u7ec3\u4e09\u5e74\u4e94\u8f7d\uff0c\u4e0d\u80fd\u6062\u590d\u3002\u4f46\u8fd9\u79cd\u6b66\u529f\uff0c\u5728 \u548c\u9ad8\u624b\u5bf9\u654c\u4e4b\u9645\uff0c\u5374\u96be\u8fd0\u7528\uff0c\u53ea\u80fd\u7528\u4e4b\u4e8e\u6697\u7b97\uff0c\u6216\u5bf9\u4fd8\u864f\u65bd\u5211\u3002\u96cd\u6b63\u5fc3\u77e5\u51af\u745b\uff08\u5728\u4ed6\u773c\u4e2d\uff0c \u5219\u4ee5\u4e3a\u662f\u51af\u7433\uff09\u4e0d\u613f\u4ece\u4ed6\uff0c\u56e0\u6b64\u60f3\u51fa\u8fd9\u6bd2\u8ba1\uff01 \u3000\u3000\u989d\u97f3\u548c\u5e03\u7a81\u88ad\u867d\u544a\u6210\u529f\uff0c\u4e5f\u6371\u4e86\u51af\u745b\u4e24\u638c\uff0c\u5929\u5c71\u638c\u6cd5\uff0c\u5389\u5bb3\u975e\u98ce\uff0c\u800c\u4e14\u8ddd\u79bb\u53c8\u8fd1\uff0c\u4e24\u638c \u90fd\u6b63\u4e2d\u8981\u5bb3\uff0c\u9976\u662f\u989d\u97f3\u548c\u5e03\u90a3\u6837\u7cbe\u5f3a\u7684\u6b66\u529f\uff0c\u4e5f\u62b5\u53d7\u4e0d\u4f4f\uff0c\u53ea\u89c9\u80f8\u53e3\u5267\u75db\uff0c\u614c\u5fd9\u8fd0\u6c14\u4fdd\u62a4\uff0c \u4e0d\u8ba9\u7600\u8840\u5f53\u573a\u5455\u51fa\u3002\u96cd\u6b63\u9053\uff1a\u201c\u597d\uff0c\u6ca1\u4f60\u7684\u4e8b\u4e86\uff0c\u653e\u4f60\u4e09\u65e5\u5047\u671f\uff0c\u4f60\u81ea\u5df1\u9759\u517b\u53bb\u5427\uff01\u201d \u3000\u3000\u51af\u745b\u88ab\u523a\u4e86\u51e0\u9488\uff0c\u6709\u5982\u7ed9\u5927\u8682\u8681\u54ac\u4e86\u51e0\u53e3\u4f3c\u7684\uff0c\u4e5f\u4e0d\u89c9\u600e\u6837\u75bc\u75db\u3002\u53ea\u542c\u5f97\u96cd\u6b63\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u7433\u4e2b\u5934\uff0c\u4f60\u4ee5\u540e\u5728\u5bab\u4e2d\u5750\u4eab\u8363\u534e\uff0c\u4e0d\u5fc5\u518d\u61c2\u6b66\u529f\u4e86\u3002\u6765\uff0c\u6765\uff0c\u54b1\u4eec\u4eb2\u8fd1\u4eb2\u8fd1\u5440\u3002\u201d \u3000\u3000\u51af\u745b\u53cc\u7709\u5012\u7ad6\uff0c\u96cd\u6b63\u72de\u7b11\u9053\uff1a\u201c\u4f60\u7684\u6b66\u529f\u5df2\u5168\u6d88\u5931\u4e86\uff0c\u4f60\u8fd8\u4f5c\u8fd9\u4e2a\u6076\u6837\u5b50\u7ed9\u8c01\u770b\uff1f\u6765\uff0c \u6765\uff0c\u6211\u770b\u4f60\u7684\u5b88\u5bab\u7802\u8fd8\u5728\u4e0d\u5728\uff1f\u201d\u52a8\u624b\u6765\u6478\u51af\u745b\u81c2\u818a\u3002 \u3000\u3000\u51af\u745b\u609a\u7136\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u4ed6\u8fd9\u6837\u4e71\u523a\u51e0\u9488\uff0c\u6211\u7684\u6b66\u529f\u4fbf\u6d88\u5931\u4e86\uff1f\u201d\u5979\u6027\u60c5\u521a\u6bc5\uff0c\u672c \u5df2\u62fc\u4e86\u4e00\u6b7b\uff0c\u4e5f\u4e0d\u7ba1\u5b83\u6b66\u529f\u662f\u5426\u8fd8\u5728\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u6a2a\u63b4\u8fc7\u53bb\uff0c\u96cd\u6b63\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u7adf\u6765\u4e0d\u53ca \u907f\u5f00\uff0c\u53ea\u89c9\u8fd9\u4e00\u638c\u529b\u9053\u5947\u5927\uff0c\u7ed9\u5979\u6253\u4e86\u4e00\u8bb0\u8033\u5149\uff0c\u4e24\u9897\u5927\u7259\uff0c\u767b\u65f6\u7529\u6389\uff0c\u534a\u8fb9\u9762\u5b54\uff0c\u7ea2\u80bf\u8d77 \u6765\u3002 \u3000\u3000\u96cd\u6b63\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u54ea\u91cc\u50cf\u662f\u6b66\u529f\u6d88\u5931\u7684\u6a21\u6837\uff1f\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u51af\u745b\u55d6\u7684\u62d4\u51fa\u77ed\u5251\uff0c\u5206 \u5fc3\u4fbf\u523a\uff0c\u96cd\u6b63\u8863\u8896\u4e00\u62c2\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u8863\u8896\u53c8\u7ed9\u5272\u53bb\u4e00\u6bb5\uff0c\u96cd\u6b63\u62d4\u5251\u4e00\u6321\uff0c\u53ee\u5f53\u4e00\u58f0\uff0c\u53cc\u5251\u76f8 \u4ea4\uff0c\u5404\u9000\u51e0\u6b65\uff0c\u4e24\u53e3\u5b9d\u5251\u90fd\u7f3a\u4e86\u4e00\u4e2a\u53e3\u3002\u5988\u73bb\u7684\u6b66\u529f\uff0c\u7adf\u7136\u4e1d\u6beb\u6ca1\u6709\u6d88\u5931\uff0c\u96cd\u6b63\u5fc3\u4e2d\u9a82\u9053\uff1a \u201c\u8be5\u6b7b\u7684\u989d\u97f3\u548c\u5e03\uff0c\u600e\u4e48\u641e\u7684\uff1f\u201d\u51af\u745b\u8fde\u8fde\u6570\u5251\uff0c\u5929\u5c71\u5251\u6cd5\uff0c\u7cbe\u5999\u7edd\u4f26\uff0c\u96cd\u6b63\u5fc3\u4e2d\u53c8\u614c\uff0c\u7ed9 \u5979\u6740\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u6025\u5fd9\u5927\u53eb\u9053\uff1a\u201c\u6765\u4eba\u54ea\uff01\u201d \u3000\u3000\u539f\u6765\u5e76\u4e0d\u662f\u989d\u97f3\u548c\u5e03\u624b\u6cd5\u5931\u7075\uff0c\u800c\u662f\u51af\u745b\u8eab\u4e0a\u7a7f\u7684\u6709\u949f\u4e07\u5802\u6240\u9001\u7684\u91d1\u4e1d\u8f6f\u7532\uff0c\u8fd9\u8f6f\u7532\u4e43 \u662f\u5085\u9752\u4e3b\u7559\u4e0b\u6765\u7684\u5f02\u5b9d\uff0c\u5200\u67aa\u4e0d\u5165\uff0c\u4f55\u51b5\u94f6\u9488\uff0c\u51af\u745b\u6240\u5b66\u7684\u53c8\u662f\u6b63\u6d3e\u5185\u529f\uff0c\u4e00\u9047\u88ad\u51fb\uff0c\u808c\u8089 \u672c\u80fd\u5185\u9677\uff0c\u989d\u97f3\u548c\u5e03\u523a\u65f6\u53c8\u4e0d\u6562\u7528\u529b\uff0c\u53ea\u6c42\u6d88\u4e86\u5979\u7684\u6b66\u529f\u4fbf\u7b97\uff0c\u4e0d\u6562\u5c06\u5979\u523a\u4f24\uff0c\u6545\u6b64\u8fde\u8eab\u4e0a \u6240\u53d7\u7684\u9707\u529b\u4e5f\u5e76\u4e0d\u5927\uff0c\u53ef\u8bf4\u662f\u6beb\u65e0\u635f\u4f24\u3002 \u3000\u3000\u96cd\u6b63\u53eb\u4e86\u51e0\u58f0\uff0c\u5bab\u5916\u4e24\u540d\u503c\u73ed\u7684\u6b66\u58eb\u9065\u9065\u7b54\u5e94\u3002\u96cd\u6b63\u8fd9\u624d\u60f3\u8d77\u989d\u97f3\u548c\u5e03\u5df2\u56de\u53bb\u9759\u517b\uff0c\u54c8 \u5e03\u9640\u51fa\u5dee\u672a\u56de\uff0c\u5916\u9762\u503c\u6bbf\u7684\u536b\u58eb\u4e0d\u662f\u201c\u51af\u7433\u201d\u5bf9\u624b\uff0c\u66f4\u89c9\u5fc3\u614c\u3002 \u3000\u3000\u96cd\u6b63\u5fc3\u614c\uff0c\u51af\u745b\u4e5f\u6709\u987e\u5fcc\uff0c\u5979\u8fde\u8fdb\u5341\u4f59\u62db\uff0c\u672a\u80fd\u5f97\u624b\uff0c\u5fc3\u9053\uff1a\u5510\u53d4\u53d4\u65e2\u672a\u5f97\u89e3\u836f\uff0c\u6211\u767d \u767d\u9001\u6b7b\u4fbf\u6beb\u65e0\u610f\u601d\uff0c\u6211\u603b\u5f97\u5728\u4ed6\u4e34\u6b7b\u4e4b\u524d\uff0c\u518d\u89c1\u4ed6\u4e00\u9762\u3002\u542c\u5f97\u5916\u9762\u811a\u6b65\u58f0\u6e10\u8fd1\uff0c\u9661\u7136\u4e00\u62db \u201c\u60ca\u96f7\u95ea\u7535\u201d\uff0c\u5c06\u96cd\u6b63\u683c\u9000\uff0c\u7a7f\u7a97\u98de\u51fa\uff01 \u3000\u3000\u4e24\u540d\u536b\u58eb\u521a\u521a\u8d76\u5230\uff0c\u51af\u745b\u4fe1\u624b\u4e24\u5251\uff0c\u6740\u4f24\u4e00\u4e2a\uff0c\u98de\u5954\u51fa\u5916\uff0c\u96cd\u6b63\u53eb\u9053\uff1a\u201c\u5feb\u5439\u8b66\u53f7\uff0c\u52a1 \u5fc5\u8981\u628a\u8fd9\u4e2b\u5934\u64d2\u4f4f\uff01\u201d\u51af\u745b\u8df3\u5230\u4e86\u5fa1\u82b1\u56ed\uff0c\u542c\u5f97\u5185\u82d1\u54e8\u58f0\u56db\u8d77\uff0c\u9ed1\u5e62\u5e62\u7684\u5f71\u5b50\u56db\u8fb9\u5954\u6765\uff0c\u5979 \u53c8\u4e0d\u719f\u6089\u5bab\u4e2d\u9053\u8def\uff0c\u53ea\u597d\u62e3\u82b1\u6728\u6df1\u5904\u4e71\u8d70\u3002 \u3000\u3000\u9661\u7136\u773c\u524d\u4e00\u4eae\uff0c\u524d\u9762\u4e00\u7247\u8377\u5858\uff0c\u65c1\u8fb9\u4e00\u5835\u56f4\u5899\uff0c\u9ad8\u53ef\u6570\u4e08\uff0c\u56f4\u5899\u5916\u6709\u4e00\u6247\u94c1\u95e8\uff0c\u95e8\u4e0a\u6709 \u4e00\u4e2a\u5c0f\u7a97\uff0c\u4e00\u540d\u592a\u76d1\uff0c\u6b63\u5728\u5c06\u98df\u7269\u585e\u8fdb\u7a97\u53e3\uff0c\u5e76\u5bf9\u7740\u7a97\u53e3\u53eb\u9053\uff1a\u201c\u963f\u5176\u90a3\uff0c\u5feb\u4e9b\u585e\u9971\u809a\u5b50\uff0c \u8001\u5b50\u4e0d\u8010\u70e6\u4e45\u5019\uff01\u201d\u51af\u745b\u5fc3\u9053\uff1a\u201c\u8fd9\u91cc\u9762\u5173\u7684\u5b9a\u662f\u72af\u4eba\u3002\u963f\u5176\u90a3\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u662f\u90a3\u72af\u4eba\u7684\u540d \u5b57\u5417\uff1f\u201d\u80cc\u540e\u811a\u6b65\u58f0\u6e10\u6765\u6e10\u8fd1\u3002\u4e00\u4e2a\u5ff5\u5934\u7a81\u7136\u4ece\u51af\u745b\u8111\u6d77\u4e2d\u5347\u8d77\uff0c\u8fc5\u5373\u7a9c\u51fa\uff0c\u624b\u8d77\u4e00\u5251\uff0c\u5c06 \u90a3\u4e2a\u592a\u76d1\u523a\u4e86\u4e2a\u900f\u660e\u7a9f\u7abf\uff0c\u5c06\u4ed6\u7684\u5c38\u4f53\u63b7\u4e0b\u8377\u5858\uff0c\u4e00\u5251\u5c06\u90a3\u94c1\u95e8\u7684\u5927\u94c1\u9501\u65a9\u65ad\uff0c\u7adf\u81ea\u63a8\u95e8\u8fdb \u53bb\u3002 \u3000\u3000\u9ed1\u7262\u4e2d\u5ffd\u542c\u5f97\u4e00\u4eba\u5389\u58f0\u53eb\u9053\uff1a\u201c\u8fc7\u6765\uff01\u4f60\u662f\u54ea\u4e00\u4e2a\u5bab\u7684\u5bab\u5973\uff1f\u201d\u90a3\u4eba\u4e45\u5728\u9ed1\u7262\uff0c\u773c\u775b\u4e60 \u60ef\uff0c\u85c9\u7740\u7262\u5916\u6e56\u5149\u7684\u53cd\u5c04\uff0c\u5df2\u770b\u51fa\u51af\u745b\u9762\u8c8c\uff0c\u51af\u745b\u5374\u770b\u4e0d\u89c1\u4ed6\u3002\u5fc3\u60f3\uff1a\u4ed6\u65e2\u7136\u88ab\u7687\u5e1d\u7981\u5728\u9ad8 \u5899\u4e4b\u5185\uff0c\u5b9a\u662f\u597d\u4eba\u3002\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u614c\uff0c\u6211\u6765\u6551\u4f60\uff01\u201d\u9ed1\u6697\u4e2d\u84e6\u5730\u4e00\u58f0\u602a\u7b11\uff0c\u4e00\u80a1\u52b2\u98ce \u6025\u6251\u800c\u6765\uff0c\u51af\u745b\u80a9\u5934\u4e00\u75db\uff0c\u5df2\u88ab\u90a3\u4eba\u6293\u7740\uff0c\u51af\u745b\u81ea\u5e7c\u7ec3\u4e60\u5185\u529f\uff0c\u9047\u654c\u4fbf\u5373\u53cd\u51fb\uff0c\u5df2\u6210\u4e60\u60ef\uff0c \u5f53\u4e0b\u6c89\u80a9\u4e00\u63a8\uff0c\u5012\u9000\u6570\u6b65\uff0c\u90a3\u4eba\u54a6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5bab\u5973\u5417\uff1f\u201d\u968f\u7740\u542c\u5f97\u811a\u9563\u624b\u94d0\u78b0\u649e \u4e4b\u58f0\uff0c\u539f\u6765\u90a3\u4eba\u88ab\u9501\u5728\u5899\u89d2\uff0c\u4e0d\u80fd\u79fb\u52a8\u3002 \u3000\u3000\u51af\u745b\u5fc3\u9053\uff1a\u600e\u4e48\u8fd9\u4eba\u5982\u6b64\u51f6\u6076\uff0c\u90a3\u4eba\u53c8\u559d\u9053\uff1a\u201c\u4f60\u65e2\u8bf4\u6551\u6211\uff0c\u4e3a\u4f55\u8fd8\u4e0d\u8fc7\u6765\uff1f\u201d\u51af\u745b\u5c06 \u5b9d\u5251\u6643\u52a8\uff0c\u85c9\u7740\u5251\u5c16\u5410\u51fa\u7684\u78a7\u83b9\u83b9\u7684\u5bd2\u5149\uff0c\u51dd\u795e\u4e00\u770b\uff0c\u8d1d\u89c1\u90a3\u4eba\u7bf7\u5934\u6563\u53d1\uff0c\u7a81\u51fa\u4e24\u9897\u91d1\u9c7c\u822c \u7684\u773c\u775b\uff0c\u5982\u82e5\u4e0d\u662f\u51af\u745b\u95ef\u8361\u8fc7\u51e0\u5e74\u6c5f\u6e56\uff0c\u771f\u4f1a\u88ab\u4ed6\u5413\u6b7b\uff0c\u90a3\u4eba\u53c8\u53eb\u9053\uff1a\u201c\u4f60\u624b\u4e0a\u62ff\u7684\u662f\u5b9d\u5251 \u5417\uff1f\u5feb\uff0c\u5feb\uff0c\u5feb\u5c06\u6211\u8eab\u4e0a\u7684\u94d0\u9563\u65a9\u65ad\uff01\u201d\u51af\u745b\u7565\u4e00\u8fdf\u7591\uff0c\u542c\u5f97\u7262\u5916\u53c8\u6709\u811a\u6b65\u4e4b\u58f0\uff0c\u90a3\u4eba\u6012 \u9053\uff1a\u201c\u4f60\u6765\u4e0d\u6765\uff1f\u4e0d\u6765\u6211\u5c31\u5c06\u4f60\u6253\u6b7b\uff0c\u4f60\u522b\u77a7\u6211\u4e0d\u80fd\u79fb\u52a8\uff0c\u54b3\uff0c\u4f60\u77a7\u2026\u2026\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u6307\u5c16 \u5df2\u5728\u5730\u4e0a\u6316\u51fa\u4e24\u9897\u788e\u77f3\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u94ee\u7136\u58f0\u54cd\uff0c\u4e24\u9897\u788e\u77f3\u6253\u5230\u94c1\u95e8\u4e4b\u4e0a\uff0c\u6fc0\u51fa\u4e00\u84ec\u706b\u82b1\uff0c\u90a3 \u4eba\u78d4\u78d4\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u6562\u9003\u8dd1\uff0c\u6211\u5c31\u5728\u4f60\u7684\u80cc\u5fc3\u6253\u4e24\u4e2a\u900f\u660e\u7a9f\u7abf\uff01\u201d \u3000\u3000\u51af\u745b\u5fc3\u4e2d\u5927\u8d77\u53cd\u611f\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6015\u4f60\u624d\u6765\u6551\u4f60\uff0c\u6211\u662f\u77a7\u5728\u4f60\u88ab\u72d7\u7687\u5e1d\u5e7d\u7981\u7684\u4efd \u4e0a\uff0c\u624d\u6765\u6551\u4f60\uff01\u201d\u90a3\u4eba\u53c8\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u968f\u5373\u53eb\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u90a3\u4e48\u4f60\u5feb\u6551\u5427\uff01\u201d\u51af\u745b\u4e00\u8dc3 \u800c\u524d\uff0c\u5b9d\u5251\u4e0a\u4e0b\u6325\u52a8\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5c06\u90a3\u4eba\u7684\u811a\u9563\u624b\u94d0\u5168\u90e8\u65a9\u6389\uff0c\u90a3\u4eba\u8d5e\u9053\uff1a\u201c\u597d\u4e00\u628a\u5b9d \u5251\uff01\u201d\u5916\u9762\u811a\u6b65\u4e4b\u58f0\uff0c\u5df2\u5230\u7262\u524d\u3002\u90a3\u4eba\u5ffd\u9053\uff1a\u201c\u5582\uff01\u4f60\u77e5\u9053\u989d\u97f3\u548c\u5e03\u5728\u5bab\u4e2d\u5417\uff1f\u201d\u51af\u745b\u9053\uff1a \u201c\u5728\u7684\uff01\u201d\u90a3\u4eba\u9053\uff1a\u201c\u770b\u4f60\u6837\u5b50\uff0c\u6b66\u529f\u4e0d\u5f31\uff0c\u4f60\u8bb0\u7740\uff0c\u989d\u97f3\u548c\u5e03\u7684\u547d\u95e8\u662f\u574e\u706b\u79bb\u6c34\u4e4b\u7a74\uff0c\u4f60 \u7528\u5b9d\u5251\u523a\u4ed6\uff01\u201d\u51af\u745b\u6b63\u60f3\u95ee\u574e\u706b\u79bb\u6c34\u4e4b\u7a74\u5728\u4eba\u8eab\u90a3\u4e2a\u90e8\u4f4d\uff0c\u53ea\u542c\u5f97\u5916\u9762\u4eba\u58f0\u5608\u6742\uff0c\u60ca\u53eb\u9053\uff1a \u201c\u662f\u8c01\u6253\u5f00\u4e86\u7262\u95e8\uff1f\u201d\u53c8\u6709\u4eba\u6025\u58f0\u53eb\u9053\uff1a\u201c\u6765\uff0c\u4f60\u77a7\u90a3\u8377\u5858\u4e0a\u7684\u6d6e\u5c38\uff0c\u54a6\uff0c\u5440\u2026\u2026\u90a3\u4e0d\u662f\u9001 \u996d\u7684\u592a\u76d1\u4e48\uff1f\u201d\u90a3\u4eba\u5bf9\u5916\u9762\u7684\u5608\u6742\uff0c\u5168\u4e0d\u5728\u610f\uff0c\u81ea\u5df1\u4f38\u62f3\u8e22\u817f\uff0c\u8212\u5c55\u7b4b\u9aa8\uff0c\u51af\u745b\u6240\u5f97\u4ed6\u7684\u9aa8 \u8282\u683c\u683c\u4f5c\u54cd\uff0c\u77e5\u4ed6\u7684\u5916\u529f\u5df2\u5230\u767b\u5cf0\u9020\u6781\u4e4b\u5883\uff0c\u5fc3\u4e2d\u867d\u7136\u5bf9\u4ed6\u538c\u6076\uff0c\u4f46\u60f3\u5230\u201c\u540c\u821f\u5171\u6d4e\u201d\u4e4b \u8bed\uff0c\u4e5f\u559c\u5f97\u4e00\u9ad8\u624b\u76f8\u52a9\uff0c\u6709\u671b\u9003\u8131\u3002\u6b63\u60f3\u8bf4\u8bdd\uff0c\u90a3\u4eba\u5df2\u81ea\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u7528\u5b9d\u5251\u66ff\u6211\u5f00\u8def\uff0c \u4f60\u542c\u4e0d\u542c\u8bdd\uff1f\u201d\u4f38\u624b\u63a8\u5979\uff0c\u51af\u745b\u6b63\u60f3\u9a82\u4ed6\uff1a\u60a3\u96be\u76f8\u52a9\uff0c\u4f55\u5fc5\u5982\u6b64\uff1f\u8bdd\u672a\u51fa\u53e3\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u6709 \u4eba\u53eb\u9053\uff1a\u201c\u516b\u8d1d\u52d2\uff0c\u516b\u8d1d\u52d2\uff1f\u600e\u4e48\uff0c\u96be\u9053\u9003\u8d70\u4e86\u5417\uff1f\u201d\u66f4\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u516b\u8d1d\u52d2\uff0c\u516b\u8d1d\u52d2\uff01\u201d \u51af\u745b\u609a\u7136\u4e00\u60ca\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u8eb2\u8fc7\u4e00\u8fb9\u3002\u4f46\u542c\u5f97\u90a3\u4eba\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u5df2\u77e5\u9053\u6211\u7684\u8eab\u4efd\u4e86\uff0c\u4f60\u52a9 \u6211\u9003\u96be\uff0c\u4ed6\u65e5\u6211\u82e5\u767b\u5927\u5b9d\uff0c\u5c01\u4f60\u505a\u6b63\u5bab\u5a18\u5a18\uff01\u201d \u3000\u3000\u539f\u6765\u6b64\u4eba\u4e43\u662f\u5f53\u4eca\u7687\u4e0a\u7684\u5f1f\u5f1f\uff0c\u5eb7\u7199\u7684\u7b2c\u516b\u5b50\u5141\u7940\uff0c\u5728\u593a\u4f4d\u7684\u8bf8\u7687\u5b50\u4e2d\uff0c\u5141\u7940\u4e5f\u662f\u56fe\u8c0b \u6781\u529b\u8005\u4e4b\u4e00\u3002\u4ed6\u867d\u7136\u4e0d\u4f3c\u5341\u56db\u7687\u5b50\u5141\u63d0\u624b\u63e1\u5175\u6743\uff0c\u4f46\u4ed6\u81ea\u5e7c\u5b66\u897f\u85cf\u7ea2\u6559\u5587\u561b\u7684\u6b66\u529f\uff0c\u53c8\u662f\u5929 \u751f\u795e\u529b\uff0c\u6240\u4ee5\u96cd\u6b63\u5bf9\u4ed6\u4e5f\u751a\u4e3a\u987e\u5fcc\u3002\u96cd\u6b63\u767b\u4f4d\u4e4b\u540e\u4e00\u5e74\uff0c\u6839\u57fa\u5df2\u56fa\uff0c\u624d\u6562\u5bf9\u4ed6\u52a8\u624b\u3002\u4ed6\u548c\u7ea2 \u6559\u5587\u561b\u672c\u6709\u540c\u95e8\u4e4b\u8c0a\uff0c\u5f53\u4ed6\u8fd8\u662f\u7687\u5b50\u4e4b\u65f6\uff0c\u989d\u97f3\u548c\u5e03\u8fd8\u662f\u4ed6\u7684\u5fc3\u8179\u3002\u5230\u96cd\u6b63\u593a\u5ae1\u4e4b\u540e\uff0c\u6697\u4e2d \u6536\u4e70\u4e86\u989d\u97f3\u548c\u5e03\uff0c\u624d\u5229\u7528\u989d\u97f3\u548c\u5e03\u4e4b\u529b\uff0c\u51fa\u5176\u4e0d\u610f\uff0c\u5c06\u4ed6\u64d2\u83b7\u3002\u5176\u65f6\u96cd\u6b63\u8fd8\u672a\u5c06\u8bf8\u7687\u5b50\u7684\u7fbd \u7ffc\u5b8c\u5168\u7fe6\u9664\uff0c\u6740\u4e4b\u6050\u751f\u53d8\u4e71\uff0c\u6545\u6b64\u53ea\u524a\u6389\u4ed6\u7684\u4eb2\u738b\u5c01\u53f7\uff0c\u7981\u4e8e\u9ad8\u5899\uff0c\u5c06\u4ed6\u6539\u540d\u4e3a\u201c\u963f\u5176 \u90a3\u201d\uff0c\u5373\u662f\u6ee1\u8bed\u4e2d\u201c\u72d7\u201d\u7684\u610f\u601d\u3002\u6bcf\u65e5\u5582\u4ee5\u72d7\u98df\uff0c\u5bf9\u4ed6\u767e\u822c\u51cc\u8fb1\u3002 \u3000\u3000\u51af\u745b\u88ab\u5141\u7940\u5a01\u80c1\u5229\u8bf1\uff0c\u903c\u5979\u76f8\u6551\uff0c\u4e0d\u89c9\u5927\u6012\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u72d7\u54ac\u72d7\uff0c\u9aa8\u8089\u76f8 \u6b8b\uff0c\u5173\u6211\u4ec0\u4e48\u4e8b\uff0c\u4f60\u8d70\u4f60\u7684\uff0c\u6211\u8d70\u6211\u7684\uff01\u5f53\u4eca\u7687\u4e0a\u6211\u5c1a\u4e14\u4e0d\u653e\u5728\u773c\u5185\uff0c\u8c01\u7a00\u7f55\u505a\u4f60\u7684\u6b63\u5bab\u5a18 \u5a18\uff01\u201d\u5141\u7940\u9a82\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u4e0d\u53d7\u62ac\u4e3e\u7684\u5c0f\u8d31\u4eba\uff01\u201d\u7262\u95e8\u5916\u7684\u4eba\u60ca\u4ff1\u5141\u7940\u795e\u52c7\uff0c\u4e0d\u6562\u95ef\u8fdb\uff0c\u4f46 \u542c\u90a3\u811a\u6b65\u4e4b\u58f0\uff0c\u5374\u662f\u6108\u6765\u6108\u591a\u3002\u5141\u7940\u5ffd\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u540c\u95ef\u51fa\u53bb\uff0c\u5f7c\u6b64\u76f8\u52a9\uff0c\u9003\u96be\u4e4b\u540e\uff0c\u5404 \u8d70\u5404\u7684\uff01\u201d\u51af\u745b\u9053\uff1a\u201c\u8fd9\u8fd8\u50cf\u8bdd\u3002\u201d \u3000\u3000\u7262\u95e8\u5916\u706b\u5149\u4e00\u95ea\uff0c\u4f3c\u662f\u5df2\u6709\u9ad8\u624b\u8d76\u6765\uff0c\u5f00\u59cb\u8fdb\u5165\u9ed1\u7262\u3002\u5141\u7940\u76ee\u9732\u51f6\u5149\uff0c\u7a81\u7136\u5411\u524d\u4e00\u6251\uff0c \u559d\u9053\uff1a\u201c\u501f\u4f60\u7684\u5b9d\u5251\u7ed9\u6211\uff01\u201d\u51af\u745b\u8f7b\u529f\u5353\u7edd\uff0c\u4e14\u6709\u9632\u5907\uff0c\u7109\u80fd\u53d7\u4ed6\u6697\u7b97\uff0c\u53cd\u8eab\u4e00\u8dc3\uff0c\u63a0\u8fc7\u4ed6 \u7684\u5934\u9876\uff0c\u5141\u7940\u56de\u8eab\u53c8\u6251\u3001\u8fc5\u5982\u75be\u98ce\uff0c\u51af\u745b\u8eab\u5f62\u98d8\u52a8\uff0c\u9661\u7136\u53cc\u638c\u4e00\u5e26\uff0c\u4f7f\u51fa\u501f\u529b\u6253\u529b\u7684\u5185\u5bb6\u67d4 \u52b2\uff0c\u5c31\u7528\u5141\u7940\u731b\u6251\u4e4b\u529b\uff0c\u5c06\u4ed6\u7684\u8eab\u5b50\u8d81\u52bf\u4e00\u629b\uff0c\u629b\u51fa\u7262\u95e8\uff01\u5141\u7940\u867d\u662f\u6b66\u529f\u7cbe\u5f3a\uff0c\u4f46\u5374\u4e07\u4e07\u6599 \u4e0d\u5230\u51af\u745b\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5374\u4f1a\u8fd9\u6837\u4e0a\u4e58\u7684\u5185\u5bb6\u529f\u592b\uff0c\u987f\u65f6\u5934\u4e0b\u811a\u4e0a\uff0c\u7ffb\u5230\u5730\u4e0a\uff01 \u3000\u3000\u51af\u745b\u5fc3\u9053\uff1a\u201c\u6211\u672c\u6709\u610f\u76f8\u52a9\uff0c\u4f60\u5374\u8fd9\u6837\u81ea\u79c1\u3002\u201d\u62d4\u51fa\u77ed\u5251\uff0c\u9690\u5728\u5899\u89d2\u3002\u4f46\u542c\u5f97\u7262\u95e8\u5916\u53eb \u58f0\u56db\u8d77\uff0c\u539f\u6765\u5141\u7940\u7ec3\u6709\u91d1\u94a2\u624b\u94c1\u5e03\u886b\u7684\u529f\u592b\uff0c\u4f17\u6b66\u58eb\u6b63\u60f3\u5408\u529b\u5c06\u4ed6\u64d2\u62ff\uff0c\u88ab\u4ed6\u731b\u7136\u4e00\u4e2a\u7ffb \u8eab\uff0c\u968f\u624b\u4e00\u6293\uff0c\u4fbf\u5c06\u4e24\u4eba\u7529\u4e0b\u8377\u5858\uff01\u968f\u7740\u542c\u5f97\u54c8\u5e03\u9640\u5927\u58f0\u559d\u9053\uff1a\u201c\u963f\u5176\u90a3\uff0c\u4f60\u80c6\u5927\u5305\u5929\uff0c\u7687 \u4e0a\u5b8f\u6069\uff0c\u8d66\u4f60\u4e0d\u6b7b\uff0c\u4f60\u8fd8\u5984\u60f3\u9003\u8d70\u5417\uff1f\u201d\u5141\u7940\u56de\u9a82\u9053\uff1a\u201c\u4f60\u624d\u662f\u963f\u5176\u90a3\uff01\u5403\u6211\u4e00\u638c\uff01\u201d\u968f\u7740 \u567c\u567c\u556a\u556a\u7684\u6253\u6597\u4e4b\u58f0\uff0c\u811a\u6b65\u8ffd\u8d76\u4e4b\u58f0\uff0c\u4f3c\u4e4e\u662f\u5141\u7940\u5df2\u51b2\u51fa\u91cd\u56f4\uff0c\u54c8\u5e03\u9640\u6b63\u5728\u7387\u4f17\u8ffd\u8d76\uff01 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u4eba\u58f0\u6e10\u8fdc\uff0c\u51af\u745b\u677e\u4e86\u53e3\u6c14\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u9aa8\u8089\u76f8\u6b8b\uff0c\u6b63\u662f\u6211\u8131\u9669\u7684\u673a\u4f1a\u3002\u201d \u6cbf\u7740\u5899\u58c1\u6478\u7d22\uff0c\u5411\u5916\u9762\u7aa5\u63a2\u3002\u95e8\u5916\u4eba\u5f71\u4e00\u95ea\uff0c\u6479\u5730\u91cc\u53c8\u6709\u4e00\u4eba\u95ef\u8fdb\u7262\u6765\uff0c\u51af\u745b\u8eab\u5f62\u4e00\u5f13\uff0c\u5530 \u7684\u4e00\u5251\u5411\u4eba\u5f71\u523a\u53bb\uff0c\u51af\u745b\u8fd9\u5251\uff0c\u53c8\u72e0\u53c8\u5feb\uff0c\u4e0d\u6599\u5374\u63b7\u4e86\u4e2a\u7a7a\uff0c\u201c\u9535\u201d\u7136\u58f0\u54cd\uff0c\u5251\u5c16\u523a\u5165\u4e00\u77f3 \u58c1\uff0c\u6025\u5207\u95f4\u7adf\u62d4\u4e0d\u51fa\u6765\uff01 \u3000\u3000\u51af\u745b\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5ffd\u542c\u5f97\u90a3\u4eba\u4f4e\u58f0\u9053\uff1a\u201c\u51af\u59d1\u5a18\uff0c\u4f60\u4e0d\u8981\u614c\uff0c\u5feb\u8ddf\u6211\u6765\u3002\u201d\u51af\u745b\u6307 \u5c16\u8fd0\u52b2\uff0c\u62d4\u51fa\u5b9d\u5251\uff0c\u51dd\u795e\u4e00\u770b\uff0c\u53ea\u89c1\u5bf9\u65b9\u767d\u53d1\u82cd\u82cd\uff0c\u624b\u6301\u62d0\u6756\uff0c\u4e00\u6d3e\u8001\u8fc8\u9f99\u949f\u4e4b\u6001\uff0c\u8eab\u4e0a\u7a7f \u7684\u5374\u662f\u536b\u58eb\u670d\u9970\u3002\u51af\u745b\u53f3\u5251\u6301\u654c\uff0c\u5de6\u638c\u62a4\u80f8\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u4e00\u5927\u628a\u5e74\u7eaa\u4e86\uff0c\u8fd8\u8981\u66ff\u72d7\u7687\u5e1d \u5356\u547d\u5417\uff1f\u201d\u51af\u745b\u4ee5\u4e3a\u8fd9\u8001\u536b\u58eb\u662f\u8981\u5e26\u5979\u53bb\u89c1\u96cd\u6b63\uff0c\u5de6\u638c\u53f3\u5251\uff0c\u90fd\u5df2\u84c4\u52b2\u5f85\u53d1\u3002\u90a3\u8001\u8005\u5fae\u5fae\u7b11 \u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5012\u5f88\u7cbe\u7ec6\u3002\u53ef\u662f\u8c01\u8bf4\u6211\u8981\u66ff\u72d7\u7687\u5e1d\u5356\u547d\u5475\uff1f\u201d\u51af\u745b\u89c1\u4ed6\u4e5f\u9a82\u7687 \u5e1d\uff0c\u6115\u7136\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u8001\u8005\u9053\uff1a\u201c\u6211\u662f\u5949\u7518\u5927\u4fa0\u4e4b\u547d\uff0c\u63a5\u4f60\u51fa\u53bb\u7684\uff01\u201d\u51af\u745b\u672a\u6562\u76f8 \u4fe1\uff0c\u4ecd\u7136\u634f\u7740\u5251\u8bc0\u3002\u90a3\u8001\u8005\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u542c\u8fc7\u4faf\u4e09\u53d8\u7684\u540d\u5b57\u4e48\uff1f\u6211\u5c31\u662f\u4ed6\uff01\u201d\u4faf\u4e09\u53d8\u4e43\u662f\u4ee5 \u524d\u6551\u8fc7\u5510\u6653\u6f9c\u7684\u8001\u536b\u571f\uff0c\u540e\u6765\u53db\u53d8\u51fa\u5bab\uff0c\u968f\u51b7\u7985\u548c\u5c1a\u9690\u5c45\u7684\u3002\u8fd9\u6bb5\u6545\u4e8b\uff0c\u51af\u745b\u65e9\u542c\u5510\u6653\u6f9c\u8bf4 \u8fc7\uff0c\u4e0d\u7981\u53c8\u60ca\u53c8\u559c\uff0c\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u4f60\u5c31\u662f\u4faf\u8001\u4f2f\u4f2f\u3002\u201d\u63d2\u5251\u5f52\u9798\uff0c\u4e0a\u524d\u65bd\u793c\uff0c\u51b7\u4e0d\u9632\uff0c\u90a3 \u8001\u8005\u5374\u62cd\u7684\u4e00\u638c\u6253\u5230\u5979\u7684\u8138\u4e0a\uff0c\u987a\u624b\u4e00\u62b9\uff0c\u51af\u745b\u53ea\u89c9\u6e7f\u817b\u817b\u7684\uff0c\u6709\u4e00\u80a1\u81ed\u5473\uff0c\u773c\u775b\u90fd\u51e0\u4e4e\u7741 \u4e0d\u5f00\u6765\u3002 \u3000\u3000\u4f46\u8fd9\u4e00\u638c\u8f7b\u98d8\u98d8\u7684\uff0c\u6253\u5728\u51af\u745b\u8138\u4e0a\uff0c\u4e1d\u6beb\u4e5f\u4e0d\u89c1\u75db\uff0c\u53ea\u662f\u628a\u51af\u745b\u5413\u4e86\u4e00\u5927\u8df3\uff0c\u5b9d\u5251\u8fd8\u672a \u62d4\u51fa\uff0c\u90a3\u8001\u8005\u5df2\u5148\u8bf4\u9053\uff1a\u201c\u51af\u59d1\u5a18\uff0c\u4f60\u522b\u89c1\u602a\uff0c\u4e0d\u7ed9\u4f60\u6d82\u4e00\u9762\u6c61\u6ce5\uff0c\u4f60\u600e\u4e48\u8d70\u5f97\u51fa\u53bb\uff1f\u8377\u5858 \u91cc\u7684\u70c2\u6ce5\u662f\u6709\u70b9\u81ed\uff0c\u4f60\u5fcd\u7740\u70b9\u513f\u3002\u201d\u51af\u745b\u4e00\u60f3\uff0c\u8fd9\u8001\u8005\u6b66\u529f\u751a\u5f3a\uff0c\u82e5\u7136\u771f\u4e2a\u6697\u7b97\uff0c\u90a3\u4e00\u638c\u4fbf \u53ef\u628a\u81ea\u5df1\u7684\u5934\u9885\u6253\u788e\u3002\u4fe1\u4ed6\u6ca1\u6709\u6076\u610f\uff0c\u90a3\u8001\u8005\u5c06\u624b\u63e9\u62b9\u5e72\u51c0\uff0c\u53c8\u628a\u4e00\u4e2a\u5c0f\u5e03\u5305\u63b7\u5230\u51af\u745b\u773c \u524d\uff0c\u9053\uff1a\u201c\u5feb\u6362\u4e0a\u8fd9\u5957\u8863\u88f3\u3002\u201d\u8bf4\u7f62\u80cc\u8f6c\u8eab\u5b50\u3002 \u3000\u3000\u51af\u745b\u5c55\u5f00\u4e00\u770b\uff0c\u5374\u662f\u4e00\u5957\u592a\u76d1\u7684\u670d\u9970\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u771f\u60f3\u5f97\u5468\u5230\u3002\u201d\u8fb9\u6362\u8863\u8fb9\u8c08\u8bdd\uff0c\u8fd9\u624d \u77e5\u9053\uff0c\u539f\u6765\u7518\u51e4\u6c60\u548c\u5415\u56db\u5a18\u5728\u5979\u8fdb\u5bab\u4e4b\u540e\uff0c\u7126\u6025\u5f02\u5e38\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u624d\u60f3\u5230\u4faf\u4e09\u53d8\u548c\u51b7\u7985\u9690\u5c45 \u897f\u5c71\uff0c\u4ed6\u4eec\u548c\u5bab\u4e2d\u7684\u4e00\u4e9b\u536b\u58eb\u751a\u719f\uff0c\u6216\u6709\u529e\u6cd5\u3002\u540c\u65f6\uff0c\u4ee5\u524d\u843d\u811a\u4e4b\u70b9\uff0c\u5df2\u88ab\u8bc6\u7834\uff0c\u4ea6\u4e0d\u53ef\u518d \u5c45\uff0c\u56e0\u6b64\u7518\u51e4\u6c60\u7b49\u4e00\u73ed\u4eba\u4fbf\u8fde\u591c\u642c\u5f80\u897f\u5c71\uff0c\u627e\u7740\u4e86\u51b7\u7985\u548c\u4faf\u4e09\u53d8\u60f3\u6cd5\u3002 \u3000\u3000\u4faf\u4e09\u53d8\u867d\u7136\u5e74\u8001\uff0c\u4ecd\u6781\u70ed\u5fc3\u3002\u672c\u6765\u4ed6\u5df2\u53db\u53d8\u51fa\u5bab\uff0c\u82e5\u88ab\u6349\u7740\uff0c\u4fbf\u662f\u6b7b\u7f6a\uff0c\u4ed6\u6043\u7740\u719f\u6089\u5bab \u4e2d\u60c5\u51b5\uff0c\u6709\u51e0\u4e2a\u8001\u536b\u58eb\u53c8\u662f\u4ed6\u7684\u5fc3\u8179\u4e4b\u4ea4\uff0c\u53d7\u4e86\u7518\u51e4\u6c60\u7684\u8bf7\u6258\uff0c\u4e0d\u8f9e\u5192\u9669\uff0c\u5c45\u7136\u5728\u7b2c\u4e8c\u5929\u4fbf \u6df7\u5165\u5bab\u4e2d\u3002\u53ef\u662f\u5bab\u4e2d\u4e0a\u4e00\u8f88\u7684\u8001\u536b\u58eb\u6240\u5269\u65e0\u51e0\uff0c\u800c\u4e14\u52bf\u529b\u5df2\u5fae\uff0c\u6839\u672c\u4e0d\u53d7\u91cd\u7528\uff0c\u65e0\u6cd5\u63a5\u8fd1\u7687 \u5e1d\u3002\u8981\u4e0d\u662f\u8fd9\u665a\u95f9\u51fa\u5141\u7940\u7834\u7262\u4e4b\u4e8b\uff0c\u4faf\u4e09\u53d8\u4f11\u60f3\u63a2\u51fa\u51af\u745b\u4e0b\u843d\u3002 \u3000\u3000\u51af\u745b\u6362\u4e86\u8863\u88f3\uff0c\u6b63\u62df\u968f\u5019\u4e09\u53d8\u51fa\u53bb\uff0c\u5ffd\u542c\u5f97\u7262\u95e8\u5916\u53c8\u6709\u4eba\u58f0\uff0c\u51af\u745b\u6325\u52a8\u5b9d\u5251\uff0c\u4fbf\u60f3\u51b2 \u51fa\uff0c\u5374\u88ab\u4faf\u4e09\u53d8\u4e00\u628a\u62c9\u4f4f\u3002 \u3000\u3000\u95e8\u5916\u7684\u4eba\u56b7\u9053\uff1a\u201c\u8001\u4faf\uff0c\u4f60\u597d\u5927\u80c6\u3002\u201d\u51af\u745b\u634f\u4e86\u4e00\u628a\u51b7\u6c57\uff0c\u53ea\u542c\u5f97\u4faf\u4e09\u53d8\u7b11\u9053\uff1a\u201c\u96f7\u8001 \u4e8c\uff0c\u8fdb\u6765\u5427\uff0c\u5916\u9762\u600e\u6837\u4e86\uff1f\u201d\u7247\u523b\u4e4b\u540e\uff0c\u7262\u95e8\u5916\u53c8\u8d70\u8fdb\u4e86\u4e00\u4e2a\u8001\u536b\u58eb\uff0c\u71c3\u7740\u677e\u679d\uff0c\u7167\u89c1\u51af \u745b\uff0c\u60ca\u6115\u4e0d\u5df2\u3002\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u6211\u8981\u62a4\u8fd9\u5c0f\u54e5\u51fa\u53bb\uff0c\u4f60\u6709\u6cd5\u5b50\u5417\uff1f\u201d\u90a3\u96f7\u59d3\u7684\u8001\u536b\u58eb\u6b63\u662f\u6536\u85cf \u4faf\u4e09\u53d8\u7684\u4eba\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u4e3a\u4e86\u4ed6\u5192\u9669\u8fdb\u5bab\u7684\u5417\uff1f\u201d\u5fc3\u4e2d\u5947\u602a\u4e3a\u4f55\u4faf\u4e09\u53d8\u4f1a\u4e3a\u4e00\u4e2a\u5c0f\u592a\u76d1 \u7518\u5192\u6027\u547d\u4e4b\u9669\u3002\u4e0a\u524d\u6765\u62c9\u51af\u745b\uff0c\u51af\u745b\u8eab\u5b50\u4e00\u7f29\uff0c\u90a3\u8001\u536b\u58eb\u4f55\u7b49\u7cbe\u7ec6\uff0c\u5df2\u770b\u51fa\u5979\u8138\u4e0a\u6ce5\u6c34\u6dcb \u6f13\uff0c\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4e2a\u599e\u599e\u3002\u5916\u9762\u867d\u7136\u5929\u9ed1\uff0c\u4f60\u7684\u8138\u53ef\u8fd8\u8be5\u6d82\u5f97\u5747\u5300\u4e00\u70b9\uff0c\u8fd9\u6837\u5728\u970e\u773c\u4e4b \u95f4\uff0c\u8fd8\u53ef\u9a97\u8fc7\u3002\u5582\uff0c\u8001\u5019\uff0c\u5979\u5230\u5e95\u662f\u8c01\uff1f\u201d\u5019\u4e09\u53d8\u9053\uff1a\u201c\u5979\u662f\u5f53\u4eca\u7687\u4e0a\u65b0\u518c\u5c01\u7684\u8d35\u5983\uff01\u201d\u90a3 \u8001\u536b\u58eb\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u77eb\u820c\u96be\u4e0b\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u8fd9\u4e0d\u662f\u8981\u95f9\u51fa\u5927\u4e8b\u5417\uff1f\u201d\u4faf\u4e09\u53d8 \u9053\uff1a\u201c\u4ed6\u4e0d\u6b62\u662f\u7687\u4e0a\u65b0\u518c\u5c01\u7684\u8d35\u5983\uff0c\u53c8\u662f\u5929\u5c71\u5251\u6d3e\u7684\u552f\u4e00\u4f20\u4eba\uff0c\u6613\u8001\u5148\u8f88\u7684\u5173\u95e8\u5f1f\u5b50\uff01\u201d\u90a3 \u8001\u536b\u58eb\u6014\u4e86\u4e00\u6014\uff0c\u604d\u7136\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u554a\uff01\u602a\u4e0d\u5f97\u4f60\u8fd9\u8001\u5934\u513f\u5982\u6b64\u5356\u529b\u3002\u539f\u6765\u662f\u4e3a\u4e86\u6551\u5929\u5c71\u5973 \u5251\u4ed9\u7684\u5f1f\u5b50\uff0c\u5929\u5c71\u5251\u6d3e\uff0c\u6211\u5fc3\u5411\u5f80\u4e4b\uff0c\u5df2\u6570\u5341\u5e74\u77e3\uff0c\u96be\u5f97\u6709\u6b64\u673a\u4f1a\uff0c\u6211\u4e5f\u5f53\u4e3a\u6613\u8001\u5251\u4ed9\u5c3d\u4e00 \u70b9\u529b\u3002\u201d\u8981\u77e5\u6613\u5170\u73e0\u8f88\u4efd\u4e4b\u5c0a\uff0c\u5e76\u4e16\u65e0\u4e8c\uff0c\u5251\u6cd5\u4e4b\u5999\uff0c\u5929\u4e0b\u77e5\u540d\u3002\u6b66\u6797\u4e2d\u4eba\u4ef0\u4e4b\u5982\u6cf0\u5c71\u5317 \u6597\uff0c\u6240\u4ee5\u5c0a\u79f0\u5979\u4e3a\u201c\u5973\u5251\u4ed9\u201d\uff0c\u4ee5\u6709\u673a\u4f1a\u6548\u52b3\u4e3a\u8363\u3002 \u3000\u3000\u4faf\u4e09\u53d8\u7b11\u9053\uff1a\u201c\u96f7\u8001\u4e8c\uff0c\u4f60\u4e5f\u8981\u8d70\u4e86\u5417\uff1f\u201d\u90a3\u8001\u536b\u58eb\u9053\uff1a\u201c\u5728\u5bab\u4e2d\u5403\u996d\u7b49\u6b7b\uff0c\u4e5f\u6ca1\u6709\u4ec0 \u4e48\u610f\u601d\uff0c\u4e0d\u5982\u968f\u4f60\u8d70\u4e86\u3002\u201d\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u90a3\u5141\u7940\u5982\u4f55\u4e86\uff1f\u201d\u8001\u536b\u58eb\u9053\uff1a\u201c\u6b63\u5728\u5916\u9762\u4e0e\u54c8\u5e03\u9640 \u7b49\u6076\u6218\u3002\u897f\u534e\u95e8\u5916\uff0c\u536b\u58eb\u6700\u758f\uff0c\u8981\u9003\u8d70\u6b63\u662f\u673a\u4f1a\uff0c\u5582\uff0c\u4f60\u600e\u4e48\u5982\u6b64\u7cbe\u7075\uff0c\u4f1a\u77e5\u9053\u5979\u85cf\u5728\u7262 \u91cc\uff1f\u201d\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u5141\u7940\u7ec3\u7684\u662f\u7ea2\u6559\u6b66\u529f\uff0c\u90a3\u8377\u5858\u4e2d\u7684\u6d6e\u5c38\uff0c\u9888\u6709\u6307\u75d5\uff0c\u9aa8\u5374\u672a\u788e\uff0c\u663e\u7136\u4e0d \u662f\u4ed6\u5f04\u6b7b\u7684\u3002\u9664\u4e86\u4ed6\u8fd8\u6709\u8c01\uff1f\u201d\u51af\u745b\u4e5f\u6b63\u6709\u6b64\u7591\u95ee\uff0c\u542c\u4e86\u7591\u56e2\u987f\u91ca\u3002\u5f53\u4e0b\u653e\u5fc3\u968f\u4faf\u4e09\u53d8\u8d70\u51fa \u7262\u95e8\u3002 \u3000\u3000\u7687\u5bab\u6bbf\u5b87\u8fde\u4e91\uff0c\u5730\u65b9\u5e7f\u9614\uff0c\u4f17\u4eba\u90fd\u88ab\u5141\u7940\u4e4b\u53d8\u5438\u5f15\u53bb\u4e86\uff0c\u5bf9\u641c\u7d22\u51af\u745b\u4e4b\u4e8b\uff0c\u53cd\u800c\u653e\u677e\uff0c \u4faf\u4e09\u53d8\u5e26\u4e86\u51af\u745b\u4e13\u62e3\u50fb\u8def\u8d70\u51fa\u897f\u95e8\uff0c\u6708\u660e\u661f\u7a00\uff0c\u4ed6\u4eec\u7a7f\u7684\u53c8\u662f\u536b\u58eb\u548c\u592a\u76d1\u7684\u670d\u9970\uff0c\u52a0\u4e0a\u6709\u90a3 \u96f7\u59d3\u7684\u8001\u536b\u58eb\u5728\u524d\u63a2\u8def\uff0c\u7adf\u7136\u5bb9\u5bb9\u6613\u6613\u7684\u8d70\u5230\u4e86\u897f\u534e\u95e8\u3002 \u3000\u3000\u897f\u534e\u95e8\u5b88\u95e8\u7684\u536b\u58eb\u540d\u53eb\u96f7\u6d77\u97f3\uff0c\u662f\u7ba1\u8840\u6ef4\u5b50\u4e00\u4e2a\u5927\u5934\u76ee\uff0c\u9065\u89c1\u4faf\u4e09\u53d8\u8d70\u6765\uff0c\u4ee5\u4e3a\u4ed6\u662f\u5bab \u4e2d\u536b\u58eb\uff0c\u95ee\u9053\uff1a\u201c\u5582\uff0c\u542c\u8bf4\u5141\u7940\u5df2\u88ab\u54c8\u5e03\u9640\u751f\u64d2\uff0c\u91cc\u9762\u6b63\u95f9\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u77a7 \u70ed\u95f9\uff1f\u201d\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u6211\u4eec\u6b63\u662f\u5949\u547d\u53bb\u641c\u6355\u4ed6\u7684\u515a\u7fbd\uff0c\u4f60\u5feb\u5f00\u95e8\u3002\u201d\u96f7\u6d77\u97f3\u9053\uff1a\u201c\u6709\u6587\u4e66 \u5417\uff1f\u201d\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u7ed9\u4ed6\uff01\u201d\u51af\u745b\u4e00\u8dc3\u800c\u524d\uff0c\u500f\u7136\u4e00\u5251\u523a\u53bb\uff0c\u90a3\u96f7\u6d77\u97f3\u5c31\u662f\u5f53\u5e74\u6355\u62ff\u5468\u9752\u4e4b \u4eba\uff0c\u6b66\u529f\u9887\u662f\u4e86\u5f97\uff0c\u51af\u745b\u4e00\u5251\u523a\u53bb\uff0c\u5c45\u7136\u7ed9\u4ed6\u907f\u5f00\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5feb\u6349\u53cd\u8d3c\uff01\u201d\u51af\u745b\u8fde\u73af\u75be \u523a\uff0c\u5530\uff0c\u5530\uff0c\u5530\uff0c\u4e00\u8fde\u4e09\u5251\uff0c\u96f7\u6d77\u97f3\u65bd\u5c55\u5168\u8eab\u672c\u9886\uff0c\u4ec5\u4ec5\u907f\u5f97\u4e24\u62db\u3002 \u3000\u3000\u7b2c\u4e09\u62db\u51af\u745b\u4f7f\u51fa\u5929\u5c71\u5251\u6cd5\u7684\u7edd\u62db\u201c\u660e\u9a7c\u5343\u91cc\u201d\uff0c\u5251\u950b\u4e00\u65cb\uff0c\u5411\u4e0b\u53cd\u523a\uff0c\u96f7\u6d77\u97f3\u5411\u4e0a\u4e00 \u8dc3\uff0c\u811a\u8ddf\u6b63\u597d\u88ab\u5251\u5c16\u523a\u7740\uff0c\u767b\u65f6\u4e00\u4e2a\u5012\u683d\u8471\u8dcc\u7ffb\u5730\u4e0a\uff0c\u5019\u4e09\u53d8\u7acb\u523b\u626d\u5f00\u94c1\u9501\uff0c\u51b7\u4e0d\u9632\u57ce\u5899\u4e0a \u53c8\u6709\u4e24\u4eba\u8dc3\u4e0b\uff0c\u4eba\u672a\u5230\uff0c\u5251\u5148\u5230\uff0c\u53cc\u5251\u9f50\u523a\u4faf\u4e09\u53d8\u9888\u9879\uff0c\u8fd9\u4e24\u4eba\u5374\u662f\u6d77\u4e91\u548c\u5c1a\u548c\u4ed6\u7684\u5f92\u5f1f\u9ece \u65cf\u914b\u957f\u706b\u4e91\u70af\u4e3b\u9f99\u6728\u516c\u3002 \u3000\u3000\u6d77\u4e91\u548c\u5c1a\u672c\u6765\u662f\u5a01\u9707\u6d77\u5357\u7684\u5251\u5e08\uff0c\u53ef\u60dc\u4ed6\u65f6\u8fd0\u4e0d\u6d4e\uff0c\u81ea\u5e94\u5141\u796f\u4e4b\u8058\uff0c\u51fa\u5c71\u4e4b\u540e\uff0c\u8fde\u5403\u4e86 \u51e0\u6b21\u8d25\u4ed7\uff0c\u964d\u5230\u53ea\u505a\u4e00\u4e2a\u666e\u901a\u7684\u536b\u58eb\u7edf\u9886\u3002\u5fc3\u4e2d\u6124\u6124\u4e0d\u5e73\uff0c\u4e45\u56fe\u7acb\u529f\u81ea\u663e\uff0c\u8fd9\u4e00\u5251\u4e43\u4ed6\u5e73\u751f \u529f\u529b\u6240\u805a\uff0c\u51cc\u5389\u975e\u51e1\uff0c\u6ee1\u4ee5\u4e3a\u4e00\u5251\u4fbf\u80fd\u5c06\u654c\u4eba\u4e86\u7ed3\u3002\u90a3\u77e5\u4faf\u4e09\u53d8\u529f\u529b\u4ea6\u6781\u7cbe\u7eaf\u3002\u89c1\u4ed6\u5251\u52bf\u65e2 \u51f6\u4e14\u52b2\uff0c\u7adf\u4e0d\u6551\u654c\u62db\uff0c\u5148\u653b\u654c\u624b\uff0c\u8eab\u8eaf\u4e00\u77ee\uff0c\u53f3\u62f3\u6363\u654c\u5c0f\u8179\uff0c\u5de6\u811a\u53c8\u5411\u4e0a\u4e00\u6311\uff0c\u8e22\u4ed6\u80be\u95e8\u547d \u7a74\u3002\u8fd9\u4e24\u62db\u90fd\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u6d77\u4e91\u548c\u5c1a\u903c\u5f97\u8eab\u5f62\u4e00\u95ea\uff0c\u5251\u950b\u659c\u504f\uff0c\u8d34\u7740\u4faf\u4e09\u53d8\u9888\u9879\u523a\u51fa\uff0c \u867d\u7136\u662f\u53ea\u5dee\u6beb\u5398\uff0c\u5374\u5df2\u7ed9\u4faf\u4e09\u53d8\u5e73\u5b89\u5ea6\u8fc7\u3002 \u3000\u3000\u9f99\u6728\u516c\u7684\u5251\u52bf\u6765\u5f97\u8f83\u6162\uff0c\u4e00\u5251\u523a\u4e0b\uff0c\u6251\u4e86\u4e2a\u7a7a\uff0c\u6b63\u5f85\u6362\u62db\u518d\u523a\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u51af\u745b \u7684\u5251\u77eb\u82e5\u6e38\u9f99\uff0c\u5df2\u4ece\u65c1\u6740\u5230\uff0c\u5251\u5149\u98d8\u77a5\uff0c\u5f39\u6307\u4e4b\u95f4\uff0c\u5df2\u8fde\u4e0b\u51e0\u6b21\u6740\u624b\uff0c\u9f99\u6728\u516c\u867d\u975e\u5eb8\u624b\uff0c\u5374 \u54ea\u80fd\u6321\u5f97\u4e86\u8fd9\u5999\u7edd\u5929\u4e0b\u7684\u5929\u5c71\u5251\u6cd5\uff0c\u4e0d\u5230\u4e94\u62db\uff0c\u624b\u8155\u4fbf\u88ab\u523a\u4f24\uff0c\u957f\u5251\u53ee\u5f53\u5815\u5730\uff01\u8fd9\u65f6\u4faf\u4e09\u53d8 \u548c\u6d77\u4e91\u548c\u5c1a\u6b63\u6253\u5f97\u96be\u5206\u96be\u89e3\u3002\u51af\u745b\u8fd0\u5251\u5982\u98ce\uff0c\u9e70\u7fd4\u96bc\u523a\uff0c\u6d77\u4e91\u548c\u5c1a\u89c1\u4e0d\u662f\u8def\uff0c\u8d8a\u5899\u4fbf\u8dd1\u3002\u4faf \u4e09\u53d8\u4e0e\u51af\u745b\u6025\u6025\u5f00\u4e86\u57ce\u95e8\uff0c\u4ece\u7687\u5bab\u540e\u9762\u7684\u666f\u5c71\u9003\u8dd1\u3002\u5230\u536b\u58eb\u4eec\u8ffd\u51fa\u6765\u65f6\uff0c\u4ed6\u4eec\u5df2\u8d8a\u8fc7\u666f\u5c71\uff0c \u4e0d\u77e5\u53bb\u5411\u4e86\u3002 \u3000\u3000\u7518\u51e4\u6c60\u3001\u5510\u6d47\u6f9c\u7b49\u5728\u897f\u5c71\u7b49\u5f97\u6b63\u6025\uff0c\u76f4\u81f3\u7b2c\u4e8c\u65e5\u65e9\u6668\uff0c\u624d\u89c1\u4faf\u4e09\u53d8\u548c\u51af\u745b\u56de\u6765\u3002\u95ee\u8d77\u7ecf \u8fc7\uff0c\u5510\u6653\u6f9c\u4e0d\u7981\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\u3002\u5415\u56db\u5a18\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4ee5\u540e\u4f60\u53ef\u522b\u518d\u80e1\u95ef\uff01\u8981\u505a\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5927 \u5bb6\u5148\u5546\u91cf\u4e86\u518d\u505a\u4e0d\u597d\u5417\uff1f\u201d\u51af\u745b\u597d\u4e0d\u60ed\u6127\uff0c\u4f4e\u5934\u8bf4\u9053\uff1a\u201c\u7d2f\u4f60\u4eec\u62c5\u5fc3\u4e86\u3002\u201d\u5415\u56db\u5a18\u4e00\u7b11\u5c06\u5979 \u62c9\u8fd1\u8eab\u65c1\uff0c\u66ff\u5979\u6574\u7406\u84ec\u677e\u7684\u4e91\u9b13\u3002\u81f3\u4e8e\u8138\u4e0a\u7684\u6c61\u6ce5\uff0c\u5979\u65e9\u5df2\u5728\u9014\u4e2d\u63e9\u62b9\u5e72\u51c0\u4e86\u3002 \u3000\u3000\u51af\u745b\u5728\u5bab\u4e2d\u4e00\u5929\u4e00\u591c\uff0c\u65f6\u95f4\u5982\u6b64\u4e4b\u77ed\uff0c\u4fbf\u80fd\u8131\u9669\uff0c\u8bf4\u6765\u5b9e\u5c5e\u4e07\u5e78\u3002\u53ef\u662f\u7ecf\u6b64\u4e00\u6765\uff0c\u5510\u6653 \u6f9c\u4e03\u65e5\u4e4b\u671f\uff0c\u53ea\u5269\u4e0b\u4e94\u5929\u4e86\u3002\u51af\u745b\u4e00\u60f3\u8d77\u6765\uff0c\u4e0d\u7531\u5fc3\u4e2d\u5927\u6025\uff0c\u95ee\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u89c9\u5f97\u600e\u4e48 \u6837\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u53ea\u662f\u6c14\u529b\u597d\u50cf\u4e00\u5929\u4e0d\u5982\u4e00\u5929\u3002\u201d\u51af\u745b\u76ee\u8574\u6cea\u5149\uff0c\u6ceb\u7136\u6b32\u6ef4\u3002 \u5510\u6653\u6f9c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u8fd9\u6837\u6b7b\u6cd5\uff0c\u4e5f\u662f\u4f73\u4e8b\u3002\u5929\u4e0b\u80fd\u6709\u51e0\u4eba\u9884\u77e5\u6b7b\u671f\u3002\u53c8\u5f97\u826f\u53cb\u5728\u65c1\uff0c\u4ece \u5bb9\u8bdd\u522b\uff01\u201d\u5510\u6653\u6f9c\u6545\u4f5c\u65f7\u8fbe\u4e4b\u8a00\uff0c\u51af\u745b\u542c\u4e86\uff0c\u8d8a\u53d1\u4f24\u5fc3\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u745b\u59b9\uff0c\u4e8b\u60c5\u8fd8\u672a\u7edd \u671b\uff0c\u4f60\u968f\u6211\u8d70\u4e00\u8d9f\u5427\u3002\u201d\u51af\u745b\u4e00\u8df3\u800c\u8d77\uff0c\u9053\uff1a\u201c\u6c34\u91cc\u706b\u91cc\uff0c\u6211\u90fd\u968f\u53bb\u3002\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5c71\u91cd\u6c34\u590d\u7591\u65e0\u8def\uff0c\u67f3\u6697\u82b1\u660e\u53c8\u4e00\u6751\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u516b\u56de\u3000\u8d39\u5c3d\u5fc3\u673a\u3000\u540d\u533b\u89e3\u5947\u75c7\u3000\u6d6a\u629b\u7cbe\u529b\u3000\u5999\u836f\u4ed8\u4e1c\u6d41 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u516b\u56de \u8d39\u5c3d\u5fc3\u673a\u3000\u540d\u533b\u89e3\u5947\u75c7 \u6d6a\u629b\u7cbe\u529b\u3000\u5999\u836f\u4ed8\u4e1c\u6d41 \u3000\u3000\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u7528\u4e0d\u7740\u5982\u6b64\u7d27\u5f20\uff0c\u6211\u53ea\u662f\u8981\u4f60\u966a\u6211\u53bb\u8bf7\u533b\u751f\u7f62\u4e86\u3002\u201d\u51af\u745b\u5947\u9053\uff1a\u201c\u8bf7\u533b \u751f\uff1f\u662f\u4e0d\u662f\u90a3\u4e2a\u59d3\u53f6\u7684\u533b\u751f\uff1f\u4f60\u4e0d\u662f\u8bf4\u4ed6\u5f97\u7f6a\u6743\u8d35\uff0c\u5df2\u5f03\u5bb6\u8fdc\u8d70\u4e86\u5417\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5f03\u5bb6 \u5219\u6709\u4e4b\uff0c\u8fdc\u8d70\u5374\u672a\u5fc5\u3002\u4e03\u54e5\u5df2\u67e5\u51fa\u7ebf\u7d22\u6765\u4e86\u3002\u4f60\u4f11\u606f\u4e00\u4f1a\uff0c\u5c31\u968f\u6211\u8d70\u5427\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u533b\u751f\u540d\u53eb\u53f6\u5bff\u5e38\uff0c\u522b\u53f7\u5e9f\u56ed\uff0c\u4eca\u5e74\u5df1\u8fd1\u516b\u5341\u3002\u4ed6\u662f\u516d\u4e03\u5341\u5e74\u524d\u4eac\u90fd\u540d\u5251\u5ba2\u77f3\u632f\u98de \u7684\u5916\u7525\uff0c\u77f3\u632f\u98de\u548c\u65e0\u6781\u6d3e\u4e0a\u4e24\u4ee3\u7684\u5b97\u7956\u5085\u9752\u4e3b\u4e43\u662f\u81f3\u4ea4\u3002\u53f6\u5bff\u5e38\u4e8c\u5341\u6765\u5c81\u4e4b\u65f6\uff0c\u5085\u9752\u4e3b\u5c1a\u5065 \u5728\uff0c\u53f6\u5bff\u5e38\u9177\u559c\u533b\u672f\uff0c\u66fe\u5f97\u5085\u9752\u4e3b\u6307\u70b9\uff0c\u56e0\u4e4b\u4e43\u6210\u4e00\u4ee3\u540d\u533b\u3002\u4ed6\u5c11\u65f6\u6587\u6b66\u5168\u624d\uff0c\u672c\u6765\u9887\u6709\u5fd7 \u4e8e\u529f\u540d\uff0c\u5f97\u5085\u9752\u4e3b\u6307\u70b9\u4e4b\u540e\uff0c\u53c8\u660e\u5937\u590f\u4e4b\u8fa9\uff0c\u81ea\u6b64\u7518\u5fc3\u77bb\u6cca\uff0c\u9042\u53f7\u201c\u5e9f\u56ed\u201d\u3002\u5230\u4ed6\u516d\u5341\u4e4b \u540e\uff0c\u4eba\u90fd\u5c0a\u4ed6\u4e3a\u201c\u5e9f\u56ed\u8001\u4eba\u201d\u800c\u4e0d\u540d\u3002\u6708\u524d\u7687\u5e1d\u7684\u4e00\u4e2a\u8d1d\u52d2\u903c\u4ed6\u6cbb\u75c5\uff0c\u4ed6\u4e0d\u613f\u53bb\uff0c\u661f\u591c\u5f03\u5bb6 \u51fa\u8d70\uff0c\u5411\u5916\u626c\u8a00\u662f\u5230\u6c5f\u5357\u6295\u4eb2\uff0c\u4ee5\u606f\u90a3\u8d1d\u52d2\u4e4b\u6012\u3002\u5176\u5b9e\u662f\u907f\u5230\u6000\u67d4\u53bf\u4e00\u4e2a\u670b\u53cb\u7684\u5bb6\u4e2d\u3002\u7518\u51e4 \u6c60\u6258\u5728\u4eac\u7684\u4e00\u4e2a\u5e2e\u4f1a\u9f99\u5934\u67e5\u63a2\uff0c\u5df2\u67e5\u77e5\u4ed6\u90a3\u4e2a\u670b\u53cb\u662f\u6000\u67d4\u53bf\u4e00\u4e2a\u5c0f\u58eb\u7ec5\uff0c\u540d\u53eb\u9646\u5eb7\uff0c\u5e73\u751f\u8bfb \u4e66\u660e\u5fd7\uff0c\u4e0d\u6c42\u95fb\u8fbe\uff0c\u5584\u5f39\u53e4\u7434\uff0c\u5e9f\u56ed\u8001\u4eba\u6bcf\u5e74\u603b\u6709\u4e00\u4e24\u6b21\u8981\u5230\u4ed6\u5bb6\u542c\u7434\u7684\u3002 \u3000\u3000\u51af\u745b\u95ee\u9053\uff1a\u201c\u6000\u67d4\u53bf\u79bb\u8fd9\u91cc\u591a\u8fdc\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u7ea6\u83ab\u4e8c\u767e\u91cc\u5427\u3002\u4ee5\u6211\u4eec\u7684\u811a\u7a0b\uff0c\u4e00\u65e5 \u53ef\u5230\uff0c\u4e24\u65e5\u6216\u81f3\u8fdf\u4e09\u65e5\u4fbf\u53ef\u6765\u56de\u3002\u7edd\u5bf9\u4e0d\u4f1a\u8bef\u4e86\u671f\u9650\u3002\u201d\u51af\u745b\u5927\u559c\uff0c\u653e\u5fc3\u7761\u4e86\u4e00\u89c9\uff0c\u5403\u8fc7\u4e86 \u5348\u996d\uff0c\u4fbf\u548c\u5415\u56db\u5a18\u52a8\u8eab\u3002 \u3000\u3000\u508d\u665a\u65f6\u5206\uff0c\u5230\u4e86\u660c\u5e73\uff0c\u79bb\u6000\u67d4\u53bf\u4ec5\u6709\u4e94\u516d\u5341\u91cc\uff0c\u4f9d\u51af\u745b\u4e4b\u89c1\uff0c\u8fde\u591c\u4fbf\u8981\u8d76\u53bb\u3002\u5415\u56db\u5a18\u7b11 \u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4f4f\u5728\u6000\u67d4\u7684\u4e00\u4e2a\u4e61\u4e0b\u3002\u4e61\u4eba\u4e60\u60ef\u65e9\u5bdd\uff0c\u6211\u4eec\u53c8\u672a\u77e5\u4ed6\u7684\u5bb6\u95e8\u3002\u534a\u591c\u8981\u627e\u4e61\u4eba\u6253 \u63a2\uff0c\u751a\u662f\u4e0d\u4fbf\u3002\u800c\u4e14\u90a3\u8001\u5934\u5b50\u5df2\u8fd1\u516b\u5341\uff0c\u5c31\u662f\u627e\u5230\u4e86\u4ed6\uff0c\u4e5f\u4e0d\u597d\u610f\u601d\u8981\u4ed6\u534a\u591c\u52a8\u8eab\u5440\uff0c\u6025\u4e5f \u4e0d\u6025\u5728\u8fd9\u665a\uff0c\u660e\u5929\u4e00\u65e9\u518d\u53bb\u5427\u3002\u201d\u51af\u745b\u60f3\u60f3\u4e5f\u662f\u9053\u7406\uff0c\u4fbf\u548c\u5415\u56db\u5a18\u540c\u5728\u660c\u5e73\u6295\u5bbf\u3002 \u3000\u3000\u4e00\u5bbf\u65e0\u8bdd\uff0c\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u5929\u8272\u5fae\u660e\uff0c\u5415\u56db\u5a18\u4fbf\u548c\u51af\u745b\u65bd\u5c55\u8f7b\u529f\uff0c\u4e00\u53e3\u6c14\u5954\u4e86\u4e09\u5341\u591a\u91cc\uff0c \u5929\u8272\u5927\u767d\uff0c\u5df2\u5165\u6000\u67d4\u53bf\u5883\u3002\u51af\u745b\u547c\u5438\u6653\u98ce\uff0c\u8eab\u5fc3\u8212\u7545\u3002\u5979\u4eec\u4e24\u4eba\u56e0\u4e0d\u4fbf\u5728\u5927\u8def\u4e0a\u65bd\u5c55\u8f7b\u529f\uff0c \u8d70\u7684\u4e43\u662f\u5c71\u8def\u6377\u5f84\uff0c\u5415\u56db\u5a18\u9065\u6307\u7740\u5c71\u5916\u4e00\u7247\u5e73\u91ce\uff0c\u8bf4\uff1a\u201c\u5728\u90a3\u5e73\u91ce\u5c3d\u5934\uff0c\u4e0d\u662f\u6709\u4e00\u5ea7\u5c71\u5417\uff0c \u5728\u5c71\u4e0b\u7684\u5c0f\u6751\uff0c\u4fbf\u662f\u4ed6\u4eec\u9690\u5c45\u7684\u9ec4\u7af9\u6751\u4e86\u3002\u5927\u7ea6\u8fd8\u8981\u518d\u8d70\u4e09\u5341\u591a\u91cc\uff0c\u4ee5\u6211\u4eec\u7684\u811a\u7a0b\uff0c\u5230\u8fbe\u4e4b \u65f6\uff0c\u4ed6\u4eec\u8fd8\u672a\u5403\u65e9\u996d\u5462\u3002\u201d\u51af\u745b\u5ffd\u62c5\u5fc3\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u90a3\u5e9f\u56ed\u8001\u4eba\u813e\u6c14\u5f88\u602a\u50fb\u5417\uff1f\u5047\u5982\u4ed6\u4e0d \u80af\u533b\uff0c\u90a3\u53ef\u600e\u529e\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u4ed6\u548c\u6211\u7684\u7956\u7236\u4e43\u662f\u6587\u5b57\u4e4b\u4ea4\u3002\u6211\u4eec\u8bf4\u51fa\u6765\u5386\uff0c\u4ed6 \u6ca1\u6709\u4e0d\u6765\u4e4b\u7406\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u89c1\u5c71\u4e0b\u7530\u4ea9\u4e4b\u95f4\uff0c\u4eba\u5f71\u8ffd\u9010\uff0c\u6e05\u6668\u4eba\u9759\uff0c\u9065\u95fb\u53f1\u54a4\u53ae\u6740\u4e4b \u58f0\u3002\u5415\u56db\u5a18\u5927\u5947\uff0c\u767b\u9ad8\u8fdc\u671b\uff0c\u5ffd\u7136\u60ca\u6115\u53eb\u9053\uff1a\u201c\u745b\u59b9\uff0c\u4f60\u5feb\u6765\u770b\uff01\u201d \u3000\u3000\u51af\u745b\u968f\u7740\u5415\u56db\u5a18\u6307\u70b9\u671b\u53bb\uff0c\u53ea\u89c1\u5c71\u4e0b\u8fdc\u5904\uff0c\u8ffd\u9010\u7684\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u6709\u4e00\u4e2a\u5973\u5b50\uff0c\u76f8\u8c8c\u867d\u7136\u770b \u4e0d\u6e05\u695a\uff0c\u80cc\u5f71\u5374\u751a\u719f\u6089\u3002\u51af\u745b\u5fc3\u9b42\u52a8\u8361\uff0c\u7a81\u7136\u5982\u53d7\u5de8\u96f7\u6240\u51fb\u4f3c\u7684\uff0c\u5446\u5728\u5c71\u5934\u3002\u5415\u56db\u5a18\u9053\uff1a \u201c\u4f60\u770b\u5979\u662f\u4e0d\u662f\u6781\u4e3a\u4f3c\u4f60\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u5440\uff0c\u5979\u4e00\u5b9a\u662f\u6211\u90a3\u5931\u6563\u7684\u59b9\u59b9\u4e86\uff01\u54b1\u4eec\u5feb\u53bb\u8ffd \u5979\uff01\u201d\u53ef\u662f\u90a3\u5c71\u4e0b\u7530\u91ce\uff0c\u8ddd\u79bb\u5979\u4eec\u6240\u5728\u7684\u5c71\u5934\uff0c\u5c11\u8bf4\u4e5f\u6709\u5341\u591a\u5eff\u91cc\uff0c\u90a3\u7fa4\u4eba\u8ffd\u9010\u53ae\u6740\uff0c\u500f\u5ffd \u6563\u5165\u5c71\u8c37\uff0c\u770b\u4e0d\u89c1\u4e86\uff0c\u51af\u745b\u5b9a\u4e86\u5b9a\u795e\uff0c\u5fc3\u60f3\uff1a\u6551\u5510\u53d4\u53d4\u7d27\u8981\uff0c\u53ef\u4e0d\u80fd\u5206\u51fa\u8eab\u6765\uff0c\u8ffd\u8e2a\u90a3\u4e2a\u5973 \u5b50\u3002\u53ea\u597d\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u53c8\u9519\u8fc7\u4e00\u6b21\u4e86\u3002\u201d\u5415\u56db\u5a18\u5b89\u6170\u5979\u9053\uff1a\u201c\u65e2\u7136\u77e5\u9053\u5979\u5728\u6b64\u95f4 \u51fa\u6ca1\uff0c\u54b1\u4eec\u8bf7\u4e86\u533b\u751f\uff0c\u6551\u597d\u6653\u6f9c\u4e4b\u540e\uff0c\u518d\u6765\u67e5\u8bbf\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u4e24\u4eba\u8d70\u4e0b\u5c71\u5761\uff0c\u7ecf\u8fc7\u5e73\u91ce\uff0c\u5230\u8fbe\u9ec4\u7af9\u6751\u7684\u65f6\u5019\uff0c\u679c\u7136\u5c1a\u672a\u8fc7\u5348\u3002\u4e24\u4eba\u5411\u6751\u6c11\u6253\u63a2\u9646\u5bb6\uff0c \u4e00\u63a2\u4fbf\u77e5\u3002\u90a3\u9646\u5bb6\u5c31\u5728\u6751\u5b50\u897f\u8fb9\uff0c\u95e8\u53e3\u6709\u4e00\u9053\u5c0f\u6eaa\u6d41\u8fc7\uff0c\u5c4b\u540e\u662f\u4e00\u5927\u7247\u7af9\u6797\uff0c\u5341\u5206\u5e7d\u96c5\u3002\u4e24 \u4eba\u8d70\u8fd1\u95e8\u524d\uff0c\u53ea\u89c1\u5927\u95e8\u655e\u5f00\uff0c\u91cc\u9762\u4eba\u58f0\u5608\u6742\u3002 \u3000\u3000\u5415\u56db\u5a18\u4f9d\u665a\u8f88\u4e4b\u793c\u8c12\u89c1\uff0c\u5728\u5927\u95e8\u4e0a\u62cd\u4e86\u51e0\u4e0b\uff0c\u65e0\u4eba\u51fa\u6765\uff0c\u53ea\u542c\u5f97\u91cc\u9762\u597d\u4f3c\u5435\u67b6\u4f3c\u7684\uff0c\u6709 \u4eba\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u597d\u610f\u76f8\u8bf7\uff0c\u4f60\u53bb\u4e0d\u53bb\uff1f\u201d\u6709\u4eba\u53eb\u9053\uff1a\u201c\u4e0d\u53bb\u5c31\u7ed1\u4ed6\u53bb\uff01\u201d\u6709\u4eba\u53eb\u9053\uff1a\u201c\u51ed\u4f60 \u548c\u65e0\u6781\u6d3e\u7684\u6e0a\u6e90\uff0c\u4f60\u4e0d\u53bb\u5bf9\u5f97\u4f4f\u4eba\u5417\uff1f\u201d\u90a3\u4e9b\u58f0\u97f3\u5608\u6210\u4e00\u7247\uff0c\u5176\u4e2d\u6742\u6709\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\uff0c\u88ab \u5176\u4ed6\u7684\u58f0\u97f3\u76d6\u8fc7\uff0c\u542c\u4e0d\u6e05\u695a\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u4e00\u5b9a\u662f\u6709\u4eba\u8feb\u53f6\u8001\u5934\u5b50\u533b\u4ed6\u6240\u4e0d\u613f\u533b\u7684\u4eba \u4e86\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u54b1\u4eec\u8fdb\u53bb\uff0c\u5c06\u8fd9\u7fa4\u6076\u5ba2\u8d76\u8dd1\u3002\u201d\u91cc\u9762\u53c8\u4f20\u51fa\u4eba\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8c01\u662f\uff1f\u4f60\u522b \u9a97\u6211\u4eec\u5566\uff01\u6211\u4eec\u65e9\u77e5\u4f60\u8eb2\u5728\u8fd9\u91cc\u3002\u8d1d\u52d2\u8d35\u4eba\u4f60\u53ef\u4ee5\u4e0d\u533b\uff0c\u6211\u4eec\u4f60\u5374\u4e0d\u80fd\u4e0d\u533b\uff01\u201d\u53c8\u6709\u4eba\u9053\uff1a \u201c\u533b\u8005\u7236\u6bcd\u5fc3\uff0c\u4f60\u5fcd\u5fc3\u53eb\u6211\u4eec\u7684\u5f1f\u5144\u6b8b\u5e9f\u5417\uff1f\u201d\u51af\u745b\u5fc3\u6025\u5982\u711a\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u7fa4\u51f6\u5f92\uff0c\u5c82 \u6709\u5982\u6b64\u8bf7\u533b\u4e4b\u7406\uff1f\u201d\u62d4\u51fa\u77ed\u5251\uff0c\u65cb\u98ce\u822c\u7684\u76f4\u95ef\u5165\u4e2d\u5802\u3002 \u3000\u3000\u5ba2\u5385\u4e0a\u6709\u56db\u4e2a\u4eba\u6b63\u56f4\u7740\u4e00\u4e2a\u8001\u8005\u3002\u51af\u745b\u4e00\u5230\uff0c\u90a3\u56db\u4e2a\u4eba\u5ffd\u7136\u90fd\u653e\u5f00\u90a3\u4e2a\u8001\u5934\uff0c\u8fce\u4e86\u51fa \u6765\u3002\u8fd9\u56db\u4e2a\u4eba\u4e4b\u4e2d\uff0c\u6709\u4e09\u4e2a\u662f\u9b41\u68a7\u5927\u6c49\uff0c\u751a\u662f\u7c97\u91ce\u3002\u53e6\u6709\u4e00\u4e2a\u5374\u4e5f\u662f\u8001\u8005\uff0c\u5374\u662f\u6837\u7709\u5584\u76ee\uff0c \u4e0d\u7c7b\u51f6\u5f92\u3002\u90a3\u4e09\u4e2a\u9b41\u68a7\u5927\u6c49\u540c\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5973\u5f3a\u76d7\u4f24\u4e86\u6211\u4eec\u7684\u5f1f\u5144\u8fd8\u8981\u8d76\u5c3d\u6740\u7edd\u5417\uff1f\u201d\u51af \u745b\u83ab\u540d\u5176\u5999\uff0c\u90a3\u4e09\u4eba\u5df2\u62d4\u51fa\u5175\u5668\uff0c\u4e00\u54c4\u800c\u4e0a\u3002\u90a3\u8001\u8005\u5374\u53eb\u9053\uff1a\u201c\u4e14\u4f4f\uff0c\u4f60\u662f\u5e74\u7fb9\u5c27\u7684\u4ec0\u4e48 \u4eba\uff1f\u201d\u90a3\u4e09\u4e2a\u7c97\u9c81\u6c49\u5b50\u6765\u52bf\u751a\u51f6\uff0c\u51af\u745b\u4e5f\u6b63\u662f\u5fc3\u6025\u5982\u711a\uff0c\u6ee1\u6000\u6c14\u6124\uff0c\u4e24\u8fb9\u90fd\u5982\u7bad\u79bb\u5f26\uff0c\u90a3\u559d \u5f97\u4f4f\uff1f\u53ea\u542c\u5f97\u4e00\u9635\u65ad\u91d1\u621b\u7389\u4e4b\u58f0\uff0c\u51af\u745b\u7684\u5b9d\u5251\u5de6\u53f3\u62ab\u8361\uff0c\u5c06\u90a3\u4e09\u4eba\u7684\u5175\u5668\uff0c\u5168\u90e8\u524a\u6389\uff0c\u51fa\u624b \u592a\u5feb\uff0c\u63a7\u5236\u4e0d\u4f4f\uff0c\u5176\u4e2d\u4e00\u4eba\u8fd8\u88ab\u523a\u4f24\u4e86\u808b\u9aa8\u3002\u90a3\u8001\u8005\u52c3\u7136\u6012\u9053\uff1a\u201c\u5c0f\u5c0f\u5a03\u513f\uff0c\u5982\u6b64\u72e0\u8fa3\uff01\u201d \u63d0\u7740\u4e00\u6839\u94c1\u70df\u888b\uff0c\u84e6\u7136\u5411\u51af\u745b\u8fce\u5934\u4e00\u7838\uff0c\u53cd\u624b\u4e00\u6ed1\uff0c\u53c8\u659c\u70b9\u5979\u7684\u201c\u80a9\u4e95\u7a74\u201d\uff0c\u51af\u745b\u5fc3\u9053\uff1a \u201c\u770b\u4f60\u8fd9\u8001\u513f\u76f8\u8c8c\u548c\u5584\uff0c\u539f\u6765\u4e5f\u662f\u4e00\u4e18\u4e4b\u8c89\uff0c\u5c45\u7136\u4e00\u51fa\u624b\u5c31\u6253\u6211\u7684\u4e09\u5341\u516d\u9053\u5927\u7a74\u54e9\uff01\u201d\u77ed\u5251 \u4e00\u65bd\uff0c\u66f4\u4e0d\u6253\u8bdd\uff0c\u4ee5\u7259\u8fd8\u7259\uff0c\u7acb\u523b\u4fbf\u53cd\u523a\u4ed6\u7684\u9b42\u95e8\u8981\u7a74\uff01 \u3000\u3000\u90a3\u8001\u8005\u4e00\u4e2a\u65cb\u8eab\u6ed1\u6b65\uff0c\u94c1\u70df\u888b\u5f80\u4e0a\u4e00\u8fce\uff0c\u5de6\u53f3\u4e00\u78d5\uff0c\u201c\u4e91\u9ebe\u4e09\u821e\u201d\u5c45\u7136\u662f\u4e00\u62db\u4e09\u5f0f\uff0c\u529f \u529b\u975e\u51e1\u3002\u51af\u745b\u4e0d\u6562\u8f7b\u654c\uff0c\u624b\u4e2d\u5251\u4e00\u63d0\u4e00\u7ffb\uff0c\u731b\u5c55\u8ffd\u98ce\u5251\u6cd5\u7684\u7edd\u62db\u201c\u6d41\u661f\u98de\u9a76\u201d\u3001\u201c\u91ce\u9a6c\u64cd \u7530\u201d\uff0c\u4e0a\u4e0b\u4e24\u5251\uff0c\u4e0a\u523a\u53cc\u76ee\uff0c\u4e0b\u523a\u4e39\u7530\uff0c\u5251\u52bf\u51cc\u5389\u3002\u90a3\u8001\u8005\u70df\u888b\u4e00\u6a2a\uff0c\u6539\u653b\u4e3a\u5b88\uff0c\u51af\u745b\u7684\u5251 \u88ab\u4ed6\u4e00\u78d5\uff0c\u53ea\u9707\u5f97\u624b\u81c2\u9178\u9ebb\u3002\u90a3\u5802\u4e0a\u7684\u8001\u4eba\u6c14\u547c\u547c\u7684\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u6211\u8fd9\u91cc\u53c8\u4e0d\u662f\u6218\u573a\uff0c \u4f60\u4eec\u5230\u8fd9\u91cc\u6765\u6492\u91ce\uff01\u201d \u3000\u3000\u51af\u745b\u4e00\u70b9\u4e0d\u77e5\uff0c\u8fd9\u548c\u5979\u5bf9\u654c\u7684\u8001\u4eba\uff0c\u5374\u6b63\u662f\u5979\u7684\u5916\u7956\u7236\u909d\u740f\u3002\u539f\u6765\u5728\u5979\u5468\u5c81\u4e4b\u591c\uff0c\u909d\u740f \u5230\u5979\u5bb6\u4e2d\u5403\u9152\uff0c\u591c\u9047\u8840\u6ef4\u5b50\u641c\u6355\u5468\u9752\uff0c\u6b83\u53ca\u6c60\u9c7c\uff0c\u5c06\u5979\u7684\u7956\u7236\u3001\u7236\u4eb2\u90fd\u6740\u6b7b\u4e86\u3002\u949f\u4e07\u5802\u62b1\u4e86 \u51af\u7433\uff0c\u548c\u909d\u740f\u4e00\u8d77\u9003\u8131\uff0c\u949f\u4e07\u5802\u56e0\u5728\u5e74\u5bb6\u6559\u9986\u907f\u4ec7\uff0c\u4e0d\u4fbf\u548c\u909d\u740f\u540c\u4f4f\uff0c\u4fbf\u5c06\u4ed6\u4ecb\u7ecd\u5230\u5929\u53f0\u5c71 \u5f20\u7075\u98ce\u5be8\u4e3b\u90a3\u91cc\u53bb\uff0c\u5f20\u7075\u98ce\u6bd4\u949f\u4e07\u5802\u5c1a\u9ad8\u4e00\u8f88\uff0c\u72ec\u521b\u5929\u53f0\u5c71\u6d3e\u6b66\u529f\uff0c\u662f\u7eff\u6797\u4e2d\u8457\u540d\u5927\u76d7\uff0c\u909d \u740f\u662f\u4e2a\u8001\u5b9e\u7684\u4e61\u4e0b\u6b66\u5e08\uff0c\u672c\u6765\u4e0d\u613f\u843d\u8349\uff0c\u53ef\u662f\u4e8b\u5230\u5176\u95f4\uff0c\u88ab\u903c\u4e0a\u6881\u5c71\uff0c\u4e5f\u65e0\u53ef\u5982\u4f55\u4e86\u3002 \u3000\u3000\u5f20\u7075\u98ce\u6027\u60c5\u8c6a\u723d\uff0c\u751a\u559c\u909d\u740f\u7684\u6734\u5b9e\uff0c\u909d\u740f\u65e2\u6765\u4e4b\u5219\u5b89\u4e4b\uff0c\u4e24\u4eba\u5012\u5f88\u6295\u673a\u3002\u5f20\u7075\u98ce\u95f2\u65f6\u4fbf \u6307\u70b9\u4ed6\u7684\u6b66\u529f\uff0c\u540e\u6765\u8fd8\u8ba9\u4ed6\u505a\u526f\u5be8\u4e3b\u3002\u909d\u740f\u548c\u5f20\u7075\u98ce\u5e74\u7eaa\u76f8\u5dee\u53ea\u4e03\u516b\u5c81\uff0c\u5f20\u7075\u98ce\u672c\u4e0d\u597d\u610f\u601d \u6536\u4ed6\u4e3a\u5f92\uff0c\u5374\u662f\u909d\u740f\u611f\u77e5\u9047\u4e4b\u6069\uff0c\u575a\u8981\u884c\u62dc\u5e08\u4e4b\u793c\uff0c\u7ec8\u4e8e\u5728\u5f20\u7075\u98ce\u4e34\u6b7b\u4e4b\u524d\uff0c\u884c\u4e86\u62dc\u5e08\u4e4b\u793c\u3002 \u3000\u3000\u5f20\u7075\u98ce\u6b7b\u540e\uff0c\u7531\u4ed6\u7684\u513f\u5b50\u5f20\u5929\u6c60\u7ee7\u4e3a\u5be8\u4e3b\uff0c\u909d\u740f\u4ecd\u5728\u5929\u53f0\u5c71\u8f85\u4f50\u4ed6\u3002\u5f20\u5929\u6c60\u624d\u5177\u4e0d\u53ca\u7236 \u4eb2\uff0c\u5c61\u6b21\u88ab\u5b98\u519b\u653b\u51fb\uff0c\u52bf\u529b\u65e5\u8e59\u3002\u5176\u65f6\u949f\u4e07\u5802\u5df2\u6b7b\uff0c\u6d88\u606f\u4f20\u6765\uff0c\u909d\u740f\u6781\u4e3a\u4f24\u60bc\u3002\u6d3e\u4eba\u6253\u542c\uff0c \u624d\u77e5\u51af\u7433\u4e5f\u65e9\u5df2\u5931\u8e2a\u3002\u4e00\u65e5\u909d\u740f\u548c\u5f20\u5929\u6c60\u95f2\u8bdd\u8bf4\u8d77\uff0c\u8bf4\u949f\u4e07\u5802\u6b7b\u540e\uff0c\u65e0\u6781\u6d3e\u6b66\u529f\u5931\u4f20\uff0c\u5085\u9752 \u4e3b\u7684\u5251\u8c31\u533b\u4e66\u4e0d\u77e5\u843d\u5728\u4f55\u4eba\u4e4b\u624b\u4e86\u3002\u5f20\u5929\u6c60\u8d2a\u5ff5\u987f\u8d77\uff0c\u6d3e\u4e86\u4e24\u4e2a\u5f92\u5f1f\uff0c\u5077\u5077\u5230\u5e74\u5bb6\u641c\u67e5\u9057 \u4e66\uff0c\u5374\u4e0d\u6599\u88ab\u51af\u7433\u6740\u6b7b\uff0c\u4e8b\u8fc7\u534a\u5e74\uff0c\u5f20\u5929\u6c60\u624d\u77e5\u6d88\u606f\uff0c\u4e0d\u6562\u518d\u6d3e\u4eba\u53bb\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u597d\u51e0\u5e74\uff0c\u5f20\u5929\u6c60\u88ab\u5b98\u519b\u56f4\u88ad\uff0c\u5c71\u5be8\u88ab\u711a\uff0c\u53ea\u5e26\u5f97\u5341\u4f59\u540d\u624b\u4e0b\u548c\u909d\u740f\u9003\u51fa\u6765\u3002\u81ea\u6b64 \u5728\u6c5f\u6e56\u6d41\u7a9c\uff0c\u5883\u51b5\u66f4\u5dee\u3002\u8fd8\u5e78\u4ed6\u867d\u5931\u4e86\u5c71\u5be8\uff0c\u5c1a\u662f\u5929\u53f0\u6d3e\u7684\u638c\u95e8\uff0c\u6b66\u6797\u4e2d\u4eba\u5bf9\u4ed6\u5c1a\u7b97\u5c0a\u91cd\u3002 \u5b98\u519b\u641c\u6355\u4ed6\u65f6\uff0c\u5f80\u5f80\u6709\u4eba\u5148\u7ed9\u4ed6\u901a\u98ce\u62a5\u8baf\uff0c\u5c31\u8fd9\u6837\u7684\u5728\u6c5f\u6e56\u4e0a\u6df7\u65e5\u5b50\u8fc7\u6d3b\u3002 \u3000\u3000\u8fd9\u5e74\u5f20\u5929\u6c60\u53c8\u60f3\u8d77\u4e86\u5085\u9752\u4e3b\u7684\u9057\u4e66\uff0c\u518d\u5230\u6cb3\u5357\u9648\u7559\u67e5\u63a2\uff0c\u9002\u503c\u674e\u6cbb\u548c\u51af\u7433\u4ece\u5e74\u5bb6\u9003\u51fa\uff0c \u5f20\u5929\u6c60\u65e9\u5df2\u67e5\u77e5\u51af\u7433\u9762\u8c8c\uff0c\u77e5\u5979\u4fbf\u662f\u6740\u81ea\u5df1\u5f92\u5f1f\u7684\u4ec7\u4eba\uff0c\u4fbf\u6d3e\u4eba\u4e00\u8def\u8ffd\u8e2a\uff0c\u76f4\u8ffd\u5230\u5317\u4eac\u9644 \u8fd1\u3002\u8fd9\u65e5\u909d\u740f\u548c\u5f20\u5929\u6c60\u7684\u51e0\u4e2a\u5f92\u513f\u8d70\u5728\u524d\u5934\uff0c\u5728\u6000\u67d4\u7684\u5e73\u91ce\u548c\u51af\u7433\u674e\u6cbb\u76f8\u9047\uff0c\u5f20\u5929\u6c60\u7684\u51e0\u4e2a \u5f92\u5f1f\u4e0a\u524d\u62fc\u6597\uff0c\u88ab\u51af\u7433\u6bd2\u5200\u8fde\u4f24\u4e09\u4eba\uff0c\u5e78\u6709\u909d\u740f\u63a9\u62a4\uff0c\u624d\u4e0d\u81f3\u5168\u519b\u8986\u6ca1\u3002\u51af\u7433\u548c\u674e\u6cbb\u4e00\u6765\u4e0d \u77e5\u4ed6\u4eec\u7684\u6765\u5386\uff0c\u4e8c\u6765\u4ea6\u6015\u95f9\u51fa\u4e8b\u60c5\uff0c\u60f9\u52a8\u5bab\u4e2d\u536b\u58eb\u6ce8\u610f\uff0c\u5306\u5306\u52a8\u624b\u4e4b\u540e\uff0c\u4e5f\u4fbf\u8d70\u4e0a\u9644\u8fd1\u5c71\u5934 \u8eb2\u907f\u4e86\u3002 \u3000\u3000\u51af\u7433\u51fa\u624b\u6781\u72e0\uff0c\u88ab\u4f24\u7684\u4e09\u4eba\u4e0d\u4f46\u4e2d\u6bd2\u660f\u8ff7\uff0c\u800c\u4e14\u9aa8\u81fc\u6298\u788e\uff0c\u6709\u6b8b\u5e9f\u4e4b\u865e\u3002\u5f20\u5929\u6c60\u968f\u540e\u5230 \u6765\uff0c\u89c1\u72b6\u5927\u6012\u3002\u53ef\u662f\u6551\u4eba\u7d27\u8981\uff0c\u65e0\u6687\u641c\u654c\u3002\u5f20\u5929\u6c60\u6d41\u7a9c\u5404\u5730\uff0c\u4f9d\u7167\u7eff\u6797\u4e60\u60ef\uff0c\u5fc5\u5b9a\u8981\u628a\u5f53\u5730 \u540d\u4eba\uff08\u5305\u62ec\u6b66\u5e08\u3001\u8c6a\u7ec5\u4ee5\u53ca\u5176\u4ed6\u5947\u624d\u5f02\u80fd\u4e4b\u58eb\uff09\u8c03\u67e5\u6e05\u695a\u3002\u5f20\u5929\u6c60\u6240\u5e26\u7684\u91d1\u521b\u836f\u65e0\u6cd5\u6cbb\u4f24\uff0c \u60f3\u5230\u90a3\u540d\u533b\u5e9f\u56ed\u8001\u4eba\u6b63\u5728\u9ec4\u7af9\u6751\u9646\u5bb6\u9690\u5c45\uff0c\u4fbf\u8981\u909d\u740f\u5e26\u4eba\u53bb\u628a\u4ed6\u8bf7\u6765\u3002\u5f20\u5929\u6c60\u7d20\u77e5\u5e9f\u56ed\u8001\u4eba \u548c\u65e0\u6781\u6d3e\u6709\u6e0a\u6e90\uff0c\u800c\u909d\u740f\u5219\u662f\u65e0\u6781\u6d3e\u524d\u4efb\u638c\u95e8\u949f\u4e07\u5802\u7684\u597d\u53cb\uff0c\u56e0\u6b64\u6d3e\u909d\u740f\u524d\u53bb\uff0c\u4e5f\u6709\u5957\u4ea4\u60c5 \u4e4b\u610f\u3002\u4e0d\u6599\u909d\u740f\u5374\u5728\u9646\u5bb6\u9047\u5230\u4e86\u81ea\u5df1\u7684\u5916\u5b59\u5973\u51af\u745b\u3002 \u3000\u3000\u909d\u740f\u5b66\u4e86\u5929\u53f0\u6d3e\u7684\u6b66\u529f\uff0c\u52a0\u4e0a\u5341\u516b\u5e74\u6765\u7684\u953b\u70bc\uff0c\u6280\u4e1a\u81ea\u662f\u6bd4\u524d\u5927\u8fdb\uff0c\u4e0d\u540c\u51e1\u4fd7\u3002\u51af\u745b\u8fde \u8fdb\u5341\u4f59\u5eff\u62db\uff0c\u672a\u80fd\u5f97\u624b\uff0c\u5251\u6cd5\u4e00\u53d8\uff0c\u8fde\u7528\u8ffd\u98ce\u5251\u6cd5\u7684\u7cbe\u5999\u62db\u6570\uff0c\u914d\u4ee5\u8f7b\u529f\uff0c\u4e58\u6687\u62b5\u9699\uff0c\u4e00\u67c4 \u5251\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u6070\u5982\u7d2b\u7535\u9752\u971c\uff0c\u7ed5\u7740\u909d\u740f\u98de\u821e\u3002\u909d\u740f\u5e74\u5df2\u8001\u8fc8\uff0c\u8eab\u6cd5\u8fdc\u4e0d\u53ca\u51af\u745b\u7075 \u654f\uff0c\u88ab\u5979\u7684\u8ffd\u98ce\u5251\u6cd5\u6740\u5f97\u624b\u5fd9\u811a\u4e71\u3002\u51af\u745b\u8ffd\u8feb\u8d8a\u7d27\uff0c\u770b\u770b\u5c31\u8981\u628a\u909d\u740f\u523a\u4f24\u3002\u5415\u56db\u5a18\u5728\u65c1\u89c2 \u6218\uff0c\u5ffd\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u63d2\u8fdb\u4e24\u4eba\u5f53\u4e2d\uff0c\u5de6\u624b\u4e00\u62c9\uff0c\u5c06\u51af\u745b\u62c9\u9000\u3002\u53f3\u624b\u4e00\u4f38\uff0c\u5c06\u909d\u740f\u7684\u94c1\u70df\u888b\u62ff \u5230\u624b\u4e2d\uff0c\u53c8\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u8001\u4eba\u5bb6\u6b47\u6b47\u5427\u3002\u8bf7\u533b\u751f\u4e5f\u5f97\u4e24\u76f8\u60c5\u613f\uff0c\u4e0d\u80fd\u786c\u6765\uff0c\u6211\u8fd9\u5c0f\u59b9 \u5b50\u813e\u6c14\u4e0d\u597d\uff0c\u4f60\u5feb\u8d70\u5427\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u8fd9\u624b\u6b66\u529f\uff0c\u8d85\u51e1\u4eba\u5723\uff0c\u909d\u740f\u6d3b\u4e86\u516d\u5341\u591a\u5c81\uff0c\u89c1\u6240\u672a\u89c1\u3002\u5f53\u4e0b\u4e0d\u6562\u518d\u6253\uff0c\u63a5\u8fc7\u70df \u888b\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002\u540c\u6765\u7684\u4eba\uff0c\u80cc\u8d77\u53d7\u4f24\u7684\u540c\u4f34\uff0c\u4e5f\u8ddf\u7740\u8d70\u4e86\u3002 \u3000\u3000\u5415\u56db\u5a18\u4e0a\u524d\u65bd\u4e86\u4e00\u793c\uff0c\u5802\u4e0a\u7684\u8001\u4eba\u6012\u9053\uff1a\u201c\u4f60\u4eec\u95f9\u591f\u4e86\u6ca1\u6709\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u53f6\u516c \u516c\u2026\u2026\u201d\u6b63\u60f3\u8bf4\u51fa\u8eab\u4efd\uff0c\u8bf7\u4ed6\u884c\u533b\u3002\u90a3\u8001\u4eba\u53cc\u773c\u4e00\u7ffb\uff0c\u84e6\u7136\u8d77\u7acb\uff0c\u62cd\u6848\u6012\u9053\uff1a\u201c\u6211\u5df2\u518d\u4e09\u8bf4 \u6211\u4e0d\u61c2\u884c\u533b\uff0c\u6211\u4e5f\u4e0d\u662f\u4f60\u4ec0\u4e48\u53f6\u516c\u516c\uff0c\u4f60\u4eec\u5728\u8fd9\u91cc\u7f57\u5506\u4ec0\u4e48\uff1f\u4f60\u4eec\u5e72\u8106\u628a\u6211\u6740\u4e86\u5427\uff0c\u7701\u5f97\u6211 \u53d7\u8052\u566a\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u9a87\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u53f6\u516c\u516c\uff1f\u201d\u90a3\u8001\u4eba\u9053\uff1a\u201c\u8bf4\u4e0d\u662f\u5c31\u4e0d\u662f\uff0c\u6211\u5750\u4e0d\u6539\u540d\uff0c\u884c\u4e0d\u6539 \u59d3\uff0c\u59d3\u9646\u540d\u5eb7\uff0c\u751f\u5e73\u53ea\u4f1a\u5f39\u7434\uff0c\u4f46\u4e0d\u5f39\u7ed9\u4f60\u4eec\u8fd9\u4e9b\u4eba\u542c\uff01\u600e\u4e48\uff0c\u4f60\u8981\u6740\u4fbf\u6740\uff0c\u4e0d\u6740\u6211\u4fbf\u8981\u56de \u53bb\u7761\u89c9\u4e86\u3002\u201d\u957f\u8896\u4e00\u62c2\uff0c\u6c14\u547c\u547c\u7684\u4fbf\u8981\u8fdb\u5165\u5185\u5802\u3002 \u3000\u3000\u5415\u56db\u5ab3\u548c\u51af\u745b\u90fd\u4e0d\u7981\u51b7\u4e86\u534a\u622a\uff0c\u60f3\u4e0d\u5230\u95f9\u4e86\u534a\u5929\uff0c\u5374\u4e0d\u662f\u5e9f\u56ed\u8001\u4eba\u3002\u51af\u745b\u8df3\u5230\u95e8\u53e3\uff0c\u62e6 \u4f4f\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u8bf7\u95ee\u53f6\u8001\u5148\u751f\u5462\uff1f\u201d\u9646\u5eb7\u7ffb\u773c\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u77e5\u9053\u4e5f\u4e0d\u544a\u8bc9\u4f60\u542c\uff01\u6211\u7ed9\u4f60 \u4eec\u9ebb\u70e6\u5f97\u5df2\u7ecf\u591f\u4e86\uff0c\u8fd8\u8981\u53eb\u4f60\u4eec\u518d\u53bb\u9ebb\u70e6\u4ed6\u5417\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u614c\u5fd9\u8bf4\u9053\uff1a\u201c\u6d59\u4e1c\u5415\u7559\u826f\u7684\u5b59\u5973\u513f\u5411\u4f60\u8001\u8bf7\u5b89\uff01\u201d\u9646\u5eb7\u5413\u4e86\u4e00\u8df3\uff0c\u56de\u8f6c\u8eab\u6765\uff0c\u95ee \u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u662f\u5415\u7559\u826f\u7684\u5b59\u5973\u513f\u5417\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5148\u7956\u751f\u524d\uff0c\u5e38\u9053\u53ca\u53f6\u9646\u4e24\u4f4d\u524d\u8f88\uff0c\u53eb \u6211\u82e5\u5230\u4eac\u90fd\uff0c\u5fc5\u5b9a\u8981\u53bb\u62dc\u5019\u3002\u201d\u9646\u5eb7\u9762\u8272\u767b\u65f6\u4e0d\u540c\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u7956\u7236\u4e5f\u77e5\u9053\u6709\u6211\u8fd9\u4e2a \u4eba\u5417\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u9053\uff1a\u201c\u8001\u4e08\u53e4\u7434\u5999\u7edd\u5929\u4e0b\uff0c\u8c01\u4eba\u4e0d\u77e5\uff01\u201d\u9646\u5eb7\u5ffd\u9053\uff1a\u201c\u9ad8\u5c71\u6d41\u6c34\uff0c\u771f\u610f\u5982\u4f55\uff1f\u201d \u5415\u56db\u5a18\u9053\uff1a\u201c\u9664\u4e86\u548f\u53f9\u4e4b\u97f3\u4e4b\u5916\uff0c\u949f\u5b50\u671f\u8fd8\u6709\u85c9\u6b64\u4ee5\u529d\u4f2f\u7259\u4e4b\u610f\u3002\u201d\u9646\u5eb7\u9053\uff1a\u201c\u529d\u4ec0\u4e48\uff1f\u201d \u5415\u56db\u5a18\u9053\uff1a\u201c\u529d\u4ed6\u629b\u4e86\u529f\u540d\uff0c\u6021\u60c5\u5c71\u6c34\u3002\u53ea\u6709\u6545\u4e61\u5c71\u6c34\uff0c\u624d\u80fd\u6fc0\u53d1\u7434\u97f3\u3002\u201d\u9646\u5eb7\u201c\u6664\u201d\u4e86\u4e00 \u58f0\uff0c\u53d6\u51fa\u4e00\u5177\u53e4\u7434\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u914d\u542c\u6211\u5f39\u7434\u3002\u201d\u95ed\u76ee\u7aef\u5750\uff0c\u5f39\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u4f60 \u542c\u5f97\u51fa\u4ec0\u4e48\u5417\uff1f\u201d\u5415\u56db\u5a18\u6d41\u6cea\u9053\uff1a\u201c\u591a\u8c22\u8001\u4e08\u540a\u5501\uff0c\u4e5f\u591a\u8c22\u8001\u4e08\u6fc0\u52b1\u3002\u201d\u539f\u6765\u9646\u5eb7\u5f39\u7684\u7b2c\u4e00 \u9996\u4e43\u662f\u60bc\u5ff5\u8d24\u4eba\u7684\u201c\u9ec4\u9e1f\u4e4b\u6b4c\u201d\u3002\u662f\u5c06\u8bd7\u7ecf\u300a\u79e6\u98ce\u300b\u4e2d\u4e00\u9996\u6362\u6b4c\u6539\u6210\u7684\uff0c\u5176\u4e2d\u6709\u201c\u5982\u53ef\u8d4e \u516e\uff0c\u4eba\u767e\u5176\u8eab\u201d\u4e4b\u8bed\uff08\u5373\uff1a\u5982\u679c\u51c6\u6211\u4eec\u8d4e\u4ed6\u7684\u547d\uff0c\u6211\u4eec\u613f\u610f\u62ff\u4e00\u767e\u4e2a\u6362\u4ed6\u4e00\u4e2a\u3002\uff09\u7b2c\u4e8c\u9996\u662f \u201c\u4e8e\u7530\u4e4b\u6b4c\u201d\uff0c\u662f\u7528\u8bd7\u7ecf\u300a\u90d1\u98ce\u300b\u4e2d\u4e00\u9996\u6b4c\u9882\u6b66\u58eb\u7684\u8d5e\u6b4c\u6539\u6210\u7684\uff0c\u7528\u610f\u662f\u9f13\u52b1\u5415\u56db\u5a18\u5b66\u90a3\u6b66 \u58eb\u7684\u8fdb\u53d6\u7cbe\u795e\u3002\u770b\u6765\u5415\u56db\u5a18\u7684\u4fa0\u540d\uff0c\u4ed6\u4e5f\u662f\u65e9\u6709\u8033\u95fb\u7684\u4e86\u3002 \u3000\u3000\u5415\u56db\u5a18\u5999\u89e3\u7434\u97f3\uff0c\u4e00\u8bf4\u5373\u4e2d\u3002\u9646\u5eb7\u7741\u773c\u8bf4\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u5192\u540d\u9a97\u6211\uff0c\u4f60\u7684\u786e\u662f\u5415\u7559\u826f\u7684\u5b59 \u5973\u513f\u4e86\uff01\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u6709\u4e00\u4f4d\u81f3\u4ea4\u597d\u53cb\uff0c\u5371\u5728\u65e6\u5915\u3002\u6025\u7740\u8981\u8bf7\u5e9f\u56ed\u8001\u4eba\u8bca\u6cbb\u3002\u201d\u9646\u5eb7 \u9053\uff1a\u201c\u4ed6\u5728\u534a\u6708\u4e4b\u524d\uff0c\u5df2\u79bb\u5f00\u6211\u8fd9\u91cc\u4e86\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u53bb\u4e86\u54ea\u91cc\uff1f\u8001\u4e08\u53ef\u613f\u89c1\u544a\u4e48\uff1f\u201d\u9646 \u5eb7\u7b11\u9053\uff1a\u201c\u770b\u5728\u4f60\u7956\u7236\u7684\u4efd\u4e0a\uff0c\u6211\u53ea\u597d\u8ba9\u4f60\u4eec\u53bb\u9ebb\u70e6\u53f6\u8001\u5934\u4e86\u3002\u53f6\u8001\u5934\u8fd8\u6709\u4e00\u4e2a\u597d\u53cb\u9648\u753b\u5e08 \u5728\u516b\u8fbe\u5cad\u4e1c\u9762\u7684\u5eb7\u5e84\uff0c\u53e6\u6709\u4e00\u4e2a\u59d3\u6768\u7684\u5f92\u5f1f\u5728\u516b\u8fbe\u5cad\u897f\u9762\u7684\u5357\u53e3\u3002\u90a3\u4e24\u4eba\u8bf7\u4ed6\u8f6e\u6d41\u53bb\u4f4f\u3002\u6211 \u4e5f\u4e0d\u77e5\u4ed6\u73b0\u5728\u8c01\u5bb6\u3002\u53cd\u6b63\u662f\u5728\u8fd9\u4e24\u5bb6\u4e4b\u4e2d\u4fbf\u4e86\u3002\u5eb7\u5e84\u548c\u5357\u53e3\u8ddd\u6b64\u5730\u90fd\u6709\u4e00\u767e\u591a\u91cc\uff0c\u4f60\u4eec\u5728\u6b64 \u6b47\u4e00\u665a\u5427\uff0c\u660e\u65e5\u518d\u53bb\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u5f85\u6211\u4eec\u89c1\u4e86\u53f6\u516c\u516c\u4e4b\u540e\uff0c\u518d\u56de\u6765\u542c\u4f60\u8001\u5f39 \u7434\u3002\u201d\u9646\u5eb7\u9053\uff1a\u201c\u4e5f\u597d\uff01\u201d\u7ee7\u800c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u73b0\u5728\u80fd\u542c\u5f97\u61c2\u6211\u7434\u58f0\u7684\u4e5f\u4e0d\u591a\u4e86\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u544a\u8f9e\u51fa\u95e8\uff0c\u5df2\u662f\u5348\u95f4\u65f6\u5206\uff0c\u4fbf\u548c\u51af\u745b\u5546\u91cf\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6709\u6b64\u6ce2\u6298\uff0c\u4e8b\u60c5\u7d27\u6025\u3002\u4f60 \u6211\u5206\u9014\u53bb\u5427\u3002\u6211\u5230\u5eb7\u5e84\u53bb\u627e\u90a3\u59d3\u9648\u7684\u753b\u5e08\u3002\u4f60\u5230\u5357\u53e3\u53bb\u627e\u90a3\u59d3\u6768\u7684\u5f92\u5f1f\u3002\u8bb0\u7740\uff0c\u4f60\u5bf9\u524d\u8f88\u4e00 \u5b9a\u8981\u975e\u5e38\u606d\u656c\uff0c\u5fc3\u4e2d\u518d\u6025\uff0c\u4e5f\u4e0d\u80fd\u706b\u71e5\u3002\u201d\u51af\u745b\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\u3002\u201d\u5f53\u4e0b\u4e24\u4eba\u5206 \u9053\u524d\u5f80\u3002 \u3000\u3000\u51af\u745b\u4e00\u7b97\uff0c\u5047\u5982\u5230\u4e86\u5357\u53e3\uff0c\u80fd\u627e\u5f97\u5230\uff0c\u7acb\u523b\u96c7\u8f66\u8bf7\u4ed6\u56de\u6765\uff0c\u56db\u5929\u521a\u53ef\u8d76\u5230\u3002\u90a3\u5c82\u4e0d\u6b63\u662f \u5510\u6653\u6f9c\u6700\u540e\u7684\u671f\u9650\u3002\u5fc3\u4e2d\u751a\u6025\uff0c\u5fd9\u4e2d\u6709\u9519\uff0c\u504f\u504f\u53c8\u8d70\u9519\u8def\uff0c\u5e78\u5f97\u4e00\u53d1\u89c9\u4fbf\u7acb\u523b\u95ee\u4eba\uff0c\u76f4\u5230\u5348 \u591c\u65f6\u5206\uff0c\u59cb\u6478\u5230\u5357\u53e3\u3002\u51af\u745b\u60f3\u5415\u56db\u5a18\u544a\u8beb\u5979\u7684\u8bf4\u8bdd\uff0c\u53eb\u5979\u4e0d\u8981\u6df1\u591c\u6270\u4eba\uff0c\u4f46\u5374\u53c8\u5fcd\u8010\u4e0d\u4f4f\uff0c \u5fc3\u9053\uff1a\u201c\u6211\u4e14\u5230\u90a3\u59d3\u6768\u7684\u5bb6\u4e2d\u63a2\u63a2\u770b\u3002\u770b\u5e9f\u56ed\u8001\u4eba\u5728\u4e5f\u4e0d\u5728\uff0c\u4e5f\u597d\u5b89\u5fc3\u3002\u201d\u4fbf\u53bb\u62cd\u4e00\u5bb6\u519c\u5bb6 \u7684\u95e8\uff0c\u95ee\u6768\u5bb6\u5730\u5740\uff0c\u4e61\u4e0b\u7684\u4eba\u751a\u4e3a\u8bda\u6734\uff0c\u542c\u8bf4\u5979\u662f\u6025\u75c5\u5ef6\u533b\uff0c\u4fbf\u544a\u8bc9\u5979\u9053\uff1a\u201c\u5728\u6751\u4e1c\u5934\u90a3\u5bb6 \u9752\u7816\u5c4b\u4fbf\u662f\u4e86\u3002\u6768\u5927\u592b\u7684\u533b\u9053\u53ef\u9ad8\u660e\u54e9\uff0c\u4f60\u8bf7\u5f97\u4ed6\u52a8\uff0c\u591a\u91cd\u7684\u75c5\u4e5f\u80fd\u533b\u597d\u3002\u201d\u51af\u745b\u9053\u8c22\u4e00 \u58f0\uff0c\u7acb\u523b\u4fbf\u8d70\u3002 \u3000\u3000\u51af\u745b\u8df3\u4e0a\u74e6\u9762\uff0c\u5ffd\u89c1\u5c4b\u4e2d\u9732\u51fa\u706f\u706b\uff0c\u51af\u745b\u5fc3\u9053\uff1a\u201c\u8fd9\u8001\u5934\u513f\u7cbe\u795e\u771f\u597d\uff0c\u73b0\u5728\u90fd\u8fd8\u672a \u7761\u3002\u201d\u60f3\u4e0b\u53bb\u8c12\u89c1\uff0c\u53c8\u6015\u5413\u4e86\u4ed6\u4eec\u3002\u4fbf\u4f0f\u5728\u74e6\u9762\u4e0a\u5411\u4e0b\u7aa5\u671b\u3002 \u3000\u3000\u5c4b\u5b50\u4e0b\u9762\u70b9\u7740\u4e24\u76cf\u7409\u7483\u706f\uff0c\u684c\u5b50\u4e0a\u653e\u7740\u4e00\u4e2a\u6a80\u9999\u7089\uff0c\u7089\u9999\u64a9\u7ed5\uff0c\u53ea\u89c1\u4e00\u4e2a\u8001\u5934\u7aef\u5750\u684c\u5b50 \u524d\u9762\u7684\u592a\u5e08\u6905\u4e0a\uff0c\u53e6\u4e00\u4e2a\u8001\u5934\u4f8d\u7acb\u5728\u65c1\u3002\u51af\u745b\u5fc3\u60f3\uff1a\u90a3\u7aef\u5750\u7684\u8001\u5934\u60f3\u5fc5\u662f\u5e9f\u56ed\u8001\u4eba\u4e86\u3002 \u3000\u3000\u5e9f\u56ed\u8001\u4eba\u53cc\u76ee\u7d27\u95ed\uff0c\u6447\u5934\u6643\u8111\uff0c\u8bf4\u9053\uff1a\u201c\u533b\u8005\u610f\u4e5f\uff0c\u610f\u5230\u75c5\u9664\uff0c\u8fd0\u7528\u4e4b\u5999\uff0c\u5b58\u4e4e\u4e00\u5fc3\uff0c \u91c7\u53e4\u4eba\u4e4b\u957f\uff0c\u63a2\u75c5\u4eba\u4e4b\u77ed\uff0c\u8fd0\u672c\u8eab\u4e4b\u667a\uff0c\u4e0d\u5fc5\u4e3a\u53e4\u4eba\u6240\u56f4\uff0c\u4ea6\u4e0d\u5fc5\u4e3a\u75c5\u5bb6\u6240\u853d\u3002\u987b\u77e5\u75c5\u75c7\u65e5 \u589e\uff0c\u6709\u4e3a\u53e4\u4ee3\u6240\u65e0\u8005\uff0c\u6545\u65e5\u4e0d\u5fc5\u4e00\u5207\u7686\u4ece\u533b\u6848\u4e2d\u5bfb\uff1b\u75c5\u5bb6\u9648\u8ff0\u75c5\u60c5\uff0c\u6216\u5931\u4e8e\u5938\u5f20\uff0c\u6216\u56e0\u5e76\u53d1 \u4e4b\u75c7\u800c\u8f6c\u79fb\u91cd\u70b9\uff0c\u6545\u65e5\u4e0d\u5fc5\u4e3a\u75c5\u5bb6\u6240\u853d\u3002\u8001\u5f1f\uff0c\u4f60\u5bf9\u6c64\u5934\u53e3\u8bc0\u90fd\u80fd\u80cc\u8bf5\u5982\u6d41\uff0c\u4eca\u540e\u5e94\u5bf9\u533b\u7406 \u66f4\u4e0b\u82e6\u529f\u3002\u201d\u90a3\u4f8d\u7acb\u7684\u8001\u5934\u8fde\u58f0\u5e94\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u5e9f\u56ed\u8001\u4eba\u53c8\u9053\uff1a\u201c\u65f6\u95f4\u65e0\u591a\uff0c\u6211\u4eca\u4f20\u4f60 \u5fc3\u6cd5\u3002\u201d\u63d0\u8d77\u72fc\u6beb\uff0c\u5728\u4e66\u684c\u4e0a\u8fb9\u8bb2\u8fb9\u5199\uff0c\u51af\u745b\u5bf9\u533b\u5b66\u4e00\u65e0\u6240\u77e5\uff0c\u542c\u5f97\u5341\u5206\u70e6\u95f7\uff0c\u6b63\u60f3\u8d70\u5f00\uff0c \u5e9f\u56ed\u8001\u4eba\u7a81\u7136\u6602\u9996\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u5df2\u5077\u542c\u591a\u65f6\uff0c\u8fd8\u4e0d\u4e0b\u6765\u5417\uff1f\u201d \u3000\u3000\u51af\u745b\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u7cdf\u4e86\uff0c\u8fd9\u56de\u5b9a\u7ed9\u4ed6\u89c1\u602a\u4e86\u3002\u53ea\u597d\u98d8\u7136\u5815\u5730\uff0c\u4e0a\u524d\u65bd\u4e86\u4e2a\u793c\uff0c\u9053\uff1a \u201c\u8bf7\u8001\u524d\u8f88\u5bbd\u6055\uff0c\u6211\u672c\u60f3\u660e\u671d\u6765\u7684\uff0c\u4f46\uff0c\u4f46\u2026\u2026\u201d\u6b63\u5728\u63e9\u8f9e\u89e3\u8bf4\uff0c\u5e9f\u56ed\u8001\u4eba\u5ffd\u9053\uff1a\u201c\u62ff\u624b\u8fc7 \u6765\uff0c\u6211\u7ed9\u4f60\u628a\u8109\u3002\u201d\u51af\u745b\u6115\u7136\u4f38\u624b\uff0c\u5e9f\u56ed\u8001\u4eba\u4e09\u6307\u6309\u5979\u8109\u95e8\uff0c\u8fc7\u4e86\u534a\u76cf\u8336\u7684\u65f6\u5206\uff0c\u5ffd\u7136\u677e\u624b \u8bf4\u9053\uff1a\u201c\u602a\uff0c\u602a\u3002\u4f60\u7684\u4eb2\u4eba\u4e4b\u75c5\uff0c\u6ca1\u6709\u4e00\u5e74\uff0c\u4e5f\u6709\u534a\u622a\uff0c\u4e3a\u4f55\u4f60\u4e0d\u6c42\u533b\uff1f\u201d\u51af\u745b\u5947\u9053\uff1a\u201c\u53f6 \u516c\u516c\uff0c\u4f60\u5982\u4f55\u77e5\u9053\uff1f\u201d\u5e9f\u56ed\u8001\u4eba\u53c8\u9053\uff1a\u201c\u4f60\u7684\u5185\u529f\u6839\u57fa\u751a\u539a\uff0c\u8db3\u5f53\u5f97\u522b\u4eba\u5341\u5e74\u7684\u529f\u529b\uff0c\u4f60\u7684 \u5e08\u5085\u662f\u8c01\uff1f\u201d\u51af\u745b\u4e0d\u6562\u9690\u7792\uff0c\u7b54\u9053\uff1a\u201c\u6211\u7684\u5e08\u5085\u662f\u5929\u5c71\u6613\u8001\u4ed9\u5a46\u3002\u201d\u5e9f\u56ed\u8001\u4eba\u9053\uff1a\u201c\u5514\uff0c\u90a3 \u602a\u4e0d\u5f97\uff0c\u539f\u6765\u4f60\u662f\u6613\u5170\u73e0\u7684\u5f92\u513f\u3002\u201d\u95ed\u76ee\u534a\u664c\uff0c\u7136\u540e\u8bf4\u9053\uff1a\u201c\u4f60\u80f8\u4e2d\u6709\u4e00\u80a1\u90c1\u79ef\u4e4b\u6c14\uff0c\u7531\u6765 \u5df2\u4e45\uff0c\u800c\u809d\u706b\u53c8\u71e5\uff0c\u5b9a\u5f53\u662f\u6709\u6781\u91cd\u5927\u7591\u96be\u4e4b\u4e8b\uff0c\u4e45\u672a\u80fd\u91ca\u3002\u4f60\u65e2\u6df1\u591c\u8bbf\u6211\uff0c\u60f3\u6765\u5b9a\u662f\u5ef6\u6211\u6cbb \u75c5\u3002\u82e5\u975e\u4eb2\u4eba\uff0c\u4f60\u4e0d\u4f1a\u5982\u6b64\u7740\u6025\uff1b\u82e5\u975e\u602a\u75c7\uff0c\u4f60\u4e0d\u4f1a\u7591\u56e2\u585e\u80f8\uff0c\u4f60\u8bf4\u8bf4\u770b\uff0c\u4f60\u7684\u4eb2\u4eba\u662f\u4ec0\u4e48 \u75c5\uff1f\u201d\u51af\u745b\u559c\u9053\uff1a\u201c\u53f6\u8001\u516c\u516c\uff0c\u4f60\u771f\u662f\u533b\u9053\u901a\u7384\uff0c\u6599\u4e8b\u5982\u795e\u3002\u6211\u6b63\u662f\u60f3\u5ef6\u4f60\u51b6\u75c5\uff0c\u6211\u7684\u4eb2 \u4eba\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u90a3\u5728\u65c1\u4f8d\u7acb\u7684\u8001\u5934\u5fd9\u622a\u7740\u9053\uff1a\u201c\u5e08\u5085\uff0c\u4f60\u5982\u4f55\u8fd8\u53ef\u52b3\u5fc3\uff1f\u201d\u51af\u745b\u5fd9\u9053\uff1a \u201c\u6211\u662f\u5415\u59d0\u59d0\u53eb\u6211\u6765\u7684\u3002\u5979\u53eb\u6211\u66ff\u5979\u7684\u7237\u7237\u95ee\u5019\u4f60\u8001\u4eba\u5bb6\u3002\u201d\u5e9f\u56ed\u8001\u4eba\u89c1\u5979\u7a81\u7136\u63d2\u8fd9\u51e0\u53e5\u95f2 \u8bdd\uff0c\u4e0d\u89c9\u8be7\u9053\uff1a\u201c\u4f60\u54ea\u4f4d\u59d0\u59d0\uff1f\u5979\u7684\u7237\u7237\u662f\u8c01\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u6211\u7684\u59d0\u59d0\u53eb\u5415\u56db\u5a18\uff0c\u5979\u7684\u7237\u7237 \u662f\u5415\u7559\u826f\u3002\u201d\u5e9f\u56ed\u8001\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u7a81\u7136\u9762\u5448\u4e0d\u60a6\u4e4b\u5bb9\uff0c\u9053\uff1a\u201c\u5415\u7559\u826f\u7684\u5b59\u5973\u513f\u600e\u4e48\u4e5f\u662f\u8fd9\u822c \u4fd7\u4eba\u89c1\u8bc6\u3002\u5979\u5c82\u4e0d\u77e5\u533b\u5bb6\u82e5\u9022\u5947\u75c7\uff0c\u9664\u975e\u4e07\u4e0d\u5f97\u5df2\uff0c\u5fc5\u5b9a\u4f1a\u53bb\u8bca\u6cbb\u7684\u4e48\uff1f\u4f55\u5fc5\u7528\u5979\u7237\u7237\u7684\u60c5 \u9762\u8bf7\u6258\uff1f\u201d\u51af\u745b\u4e00\u559c\uff0c\u8fde\u9053\uff1a\u201c\u662f\u662f\uff01\u201d\u4e0d\u6599\u5e9f\u56ed\u8001\u4eba\u53cc\u773c\u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u53ef\u60dc\u6211\u4e0d\u80fd\u53bb\uff01\u201d \u3000\u3000\u51af\u745b\u6025\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u975e\u4e07\u4e0d\u5f97\u5df2\u624d\u4e0d\u53bb\u7684\u5417\uff1f\u201d\u5e9f\u56ed\u8001\u4eba\u9053\uff1a\u201c\u6211\u6b63\u662f\u4e07\u4e0d\u5f97\u5df2\uff01\u201d \u51af\u745b\u6025\u5f97\u6d41\u6cea\u9053\uff1a\u201c\u4ed6\u8fd8\u6709\u4e09\u5929\u96f6\u534a\u65e5\uff0c\u4fbf\u662f\u6b7b\u671f\uff0c\u4f60\u82e5\u4e0d\u6551\uff0c\u5c31\u6ca1\u6709\u8c01\u80fd\u6551\u4ed6\u4e86\u3002\u201d\u5e9f\u56ed \u8001\u4eba\u5fae\u5fae\u4e00\u6115\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u4ed6\u4e5f\u80fd\u81ea\u77e5\u6b7b\u671f\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u80fd\u81ea\u77e5\uff0c\u662f\u522b\u4eba\u903c\u5f97 \u4ed6\u81ea\u77e5\u7684\u3002\u201d\u5e9f\u56ed\u8001\u4eba\u66f4\u89c9\u5947\u602a\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u4e8b\uff0c\u6211\u8fd8\u672a\u542c\u8bf4\u8fc7\uff0c\u4f60\u8bf4\u903c\u4ed6\u7684\u90a3\u4eba\u662f \u8c01\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u662f\u5f53\u4eca\u7687\u5e1d\u3002\u201d\u5e9f\u56ed\u8001\u4eba\u9053\uff1a\u201c\u54e6\uff0c\u90a3\u6211\u4e00\u5b9a\u8981\u533b\u4ed6\u4e86\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u90a3 \u4e48\u6211\u80cc\u4f60\u8001\u4eba\u5bb6\u8d70\uff0c\u5230\u5929\u4eae\u4e86\u54b1\u4eec\u518d\u96c7\u9a6c\u8f66\u3002\u201d\u5e9f\u56ed\u8001\u4eba\u53c8\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e0d\u80fd\u53bb\uff01\u4f60 \u628a\u4ed6\u5f97\u75c5\u7684\u7ecf\u8fc7\u548c\u75c7\u72b6\u8be6\u7ec6\u8bf4\u7ed9\u6211\u542c\u3002\u201d\u90a3\u4f8d\u7acb\u7684\u8001\u5934\u53c8\u9053\uff1a\u201c\u5e08\u5085\uff0c\u4f60\u516d\u5341\u5e74\u6765\u884c\u533b\u5982\u4e00 \u65e5\uff0c\u4eca\u665a\u53ef\u4e0d\u8981\u518d\u64cd\u5fc3\u4e86\u3002\u201d\u5e9f\u56ed\u8001\u4eba\u55d4\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u6211\u542c\u4e86\u5947\u96be\u6742\u75c7\uff0c\u82e5\u4e0d\u60f3\u6cd5\u533b\u6cbb\uff0c\u6b7b \u4e86\u4e5f\u4e0d\u80fd\u5b89\u5fc3\u3002\u201d\u90a3\u4f8d\u7acb\u7684\u8001\u5934\u65e0\u6cd5\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u4e48\u6211\u66ff\u4f60\u7eaa\u5f55\u533b\u6848\u3002\u201d \u3000\u3000\u51af\u745b\u5c06\u5510\u6653\u8c30\u4e00\u5e74\u524d\u88ab\u96cd\u6b63\u9a97\u996e\u6bd2\u9152\u548c\u8fd1\u65e5\u7684\u75c7\u72b6\uff08\u8eab\u5b50\u53d1\u8f6f\uff0c\u6c14\u529b\u6e10\u6d88\uff0c\u89c6\u7269\u6e10\u89c9\u6a21 \u7cca\u2026\u2026\u7b49\u7b49\u75c7\u72b6\uff09\u90fd\u8be6\u8bf4\u4e86\u3002\u5e9f\u56ed\u8001\u4eba\u9053\uff1a\u201c\u5c45\u7136\u6709\u8fd9\u6837\u7684\u6bd2\u9152\uff1f\u53e4\u4eca\u533b\u6848\u53ef\u90fd\u6ca1\u6709\u8bb0\u8f7d\u3002 \u8fd9\u662f\u4ec0\u4e48\u6bd2\u9152\u5462\uff1f\u201d\u53c8\u95ed\u76ee\u60f3\u4e86\u534a\u664c\uff0c\u4f3c\u4e4e\u4ecd\u662f\u60f3\u4e0d\u51fa\u6765\uff0c\u7741\u5f00\u773c\u775b\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u53ef\u60dc\u6211 \u4e0d\u80fd\u4eb2\u53bb\u671b\u95fb\u95ee\u5207\u3002\u201d\u51af\u745b\u6025\u6781\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u5c31\u65e0\u6cd5\u53ef\u60f3\u4e86\u5417\uff1f\u201d\u5e9f\u56ed\u8001\u4eba\u9053\uff1a\u201c\u522b \u5fd9\uff0c\u4f60\u8ba9\u6211\u518d\u60f3\u3002\u201d\u53c8\u95ed\u76ee\u9759\u5750\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u51af\u745b\u548c\u90a3\u8001\u5934\u90fd\u751a\u7740\u6025\uff0c\u4f8d\u7acb\u5728\u65c1\uff0c\u542c\u7740\u9e21\u557c \u4e86\u4e00\u904d\u53c8\u4e00\u904d\uff0c\u4ed6\u7adf\u7136\u5750\u4e86\u4e00\u4e2a\u66f4\u6b21\uff0c\u624d\u54b3\u55fd\u4e00\u58f0\uff0c\u7741\u773c\u8bf4\u9053\uff1a\u201c\u6768\u8001\u5f1f\uff0c\u4f60\u7ed9\u4ed6\u914d\u836f\u3002\u7528 \u6211\u7684\u516d\u5408\u5b81\u795e\u4e38\u6363\u788e\u914d\u4e0a\u5176\u4ed6\u4e03\u5473\u836f\u3002\u7528\u79cb\u5929\u7684\u6850\u53f6\u548c\u4e00\u5bf9\u96cc\u96c4\u87cb\u87c0\u505a\u5f15\u5b50\u3002\u201d\u90a3\u4f8d\u7acb\u7684\u8001 \u5934\u662f\u4ed6\u7684\u9ad8\u8db3\u5f1f\u5b50\uff0c\u5bb6\u4e2d\u85cf\u6709\u8bb8\u591a\u73cd\u8d35\u836f\u54c1\uff0c\u4f9d\u65b9\u914d\u4e86\uff0c\u5305\u6210\u4e00\u5305\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u9669\uff0c\u8fd9\u4e03\u5473 \u836f\u4e2d\u6709\u4e24\u5473\u521a\u521a\u591f\u7528\u3002\u79cb\u6850\u53f6\u53ea\u5269\u4e00\u7247\uff0c\u96cc\u96c4\u87cb\u87c0\u4e5f\u53ea\u5269\u6b64\u4e00\u5bf9\uff0c\u521a\u914d\u5f97\u8fd9\u4e00\u5242\uff0c\u518d\u914d\u5c31\u6ca1 \u6709\u4e86\u3002\u201d\u5e9f\u56ed\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u836f\u4e5f\u53ea\u80fd\u5403\u4e00\u5242\u8bd5\u8bd5\u3002\u201d\u53c8\u63d0\u8d77\u7b14\u6765\u5f00\u4e86\u4e00\u5f20\u65b9\u5b50\uff0c\u9053\uff1a\u201c\u5403\u4e86 \u90a3\u836f\uff0c\u82e5\u89c1\u6548\u7684\u8bdd\uff0c\u518d\u914d\u8fd9\u65b9\u5b50\u8fde\u5403\u4e09\u5242\u3002\u8fd9\u65b9\u5b50\u4e0a\u7684\u836f\u90fd\u662f\u666e\u901a\u7684\u5b81\u795e\u5b89\u7720\u4e4b\u836f\uff0c\u5bb9\u6613\u914d \u7684\u3002\u201d \u3000\u3000\u51af\u745b\u5927\u559c\uff0c\u63a5\u8fc7\u90a3\u5305\u836f\u548c\u836f\u65b9\uff0c\u6b63\u60f3\u9053\u8c22\u544a\u8f9e\uff0c\u5e9f\u56ed\u8001\u4eba\u5ffd\u9053\uff1a\u201c\u5582\uff0c\u7ed9\u4f60\u770b\u4e86\u75c5\uff0c\u4f60 \u4e0d\u4ea4\u8bca\u8d39\u5417\uff1f\u201d\u51af\u745b\u6599\u4e0d\u5230\u4ed6\u6709\u6b64\u4e00\u7740\uff0c\u8138\u7ea2\u8bf4\u9053\uff1a\u201c\u6211\u8eab\u4e0a\u6ca1\u6709\u5e26\u94b1\uff0c\u6211\uff0c\u6211\u7ed9\u4f60\u8fd9\u73e0\u9970 \u5427\u3002\u201d\u5e9f\u56ed\u8001\u4eba\u9053\uff1a\u201c\u6211\u5e74\u7eaa\u8fd9\u4e48\u5927\u4e86\uff0c\u8c01\u8fd8\u8981\u4f60\u8fd9\u5973\u5b69\u513f\u5bb6\u7684\u4e1c\u897f\uff1f\u4f46\u4f60\u8981\u66ff\u6211\u505a\u4e00\u4ef6 \u4e8b\uff0c\u7b97\u4f5c\u8bca\u8d39\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u8bf7\u516c\u516c\u5429\u5490\u3002\u201d\u5e9f\u56ed\u8001\u4eba\u9053\uff1a\u201c\u6211\u7684\u533b\u672f\u662f\u5085\u9752\u4e3b\u6307\u70b9\u7684\uff0c\u8fd9 \u51e0\u5341\u5e74\u6765\uff0c\u6211\u603b\u7b97\u4e0d\u8f9c\u8d1f\u4ed6\u8001\u4eba\u5bb6\u7684\u671f\u671b\uff0c\u4e5f\u533b\u597d\u4e86\u4e0d\u5c11\u75c5\u4eba\uff0c\u79ef\u4e0b\u4e86\u4e0d\u5c11\u533b\u6848\uff0c\u53ef\u60dc\u4e0d\u80fd \u8ba9\u8001\u4eba\u5bb6\u8fc7\u76ee\u3002\u5509\uff0c\u5509\u3002\u201d\u90a3\u4f8d\u7acb\u7684\u8001\u5934\u9053\uff1a\u201c\u5e08\u5085\u4f60\u4e0d\u8981\u4f24\u5fc3\uff0c\u5085\u8001\u5b97\u5e08\u77e5\u9053\u54b1\u4eec\u80fd\u7ee7\u627f \u4ed6\u7684\u8863\u94b5\uff0c\u5728\u5929\u4e4b\u7075\uff0c\u4e5f\u4e00\u5b9a\u6b23\u6170\u3002\u201d \u3000\u3000\u5e9f\u56ed\u8001\u4eba\u5ffd\u51b7\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u5c45\u7136\u6562\u8bf4\u54b1\u4eec\u80fd\u7ee7\u627f\u5085\u8001\u5148\u5e08\u7684\u8863\u94b5\uff1f\u201d\u90a3\u4f8d\u7acb\u7684\u8001\u5934 \u60f6\u6050\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u611a\u9c81\uff0c\u533b\u9053\u6d45\u8584\uff0c\u6bd4\u8d77\u5148\u8f88\u81ea\u7136\u662f\u76f8\u5dee\u751a\u8fdc\uff0c\u4f46\u5e08\u5085\u4e00\u751f\u5fc3\u529b\u6240\u5949\uff0c\u5728\u533b \u9053\u4e0a\u627f\u5148\u542f\u540e\uff0c\u4e5f\u53ef\u4ee5\u6bd4\u5f97\u4e0a\u5f53\u5e74\u7684\u5085\u8001\u5b97\u5e08\u4e86\u3002\u201d\u5e9f\u56ed\u8001\u4eba\u6447\u5934\u9053\uff1a\u201c\u8fd8\u5dee\u5f97\u8fdc\u5462\uff01\u5728\u533b \u7406\u4e0a\u6211\u8fd8\u6709\u751a\u591a\u672a\u660e\u4e4b\u5904\uff0c\u50cf\u521a\u624d\u8fd9\u4e00\u6869\u5c31\u662f\u5982\u6b64\u3002\u6bcf\u5f53\u6211\u5728\u60f3\u4e0d\u901a\u4e4b\u65f6\u5c31\u6068\u4e0d\u5f97\u8d77\u5085\u8001\u5b97 \u5e08\u4e8e\u5730\u4e0b\u800c\u95ee\u4e4b\u3002\u4e0d\u8fc7\uff0c\u6211\u6240\u79ef\u5b58\u7684\u533b\u6848\uff0c\u5374\u81ea\u4fe1\u80fd\u8d85\u8fc7\u524d\u4eba\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u77e5 \u9053\u5085\u9752\u4e3b\u7684\u6b66\u529f\u533b\u672f\uff0c\u4f20\u7ed9\u8c01\u5417\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u7684\u5f92\u5b59\u949f\u4e07\u5802\uff0c\u6b66\u529f\u533b\u672f\uff0c\u5747\u5f97\u5176 \u4f20\u3002\u949f\u4e07\u5802\u5c06\u6b66\u529f\u4f20\u4e8e\u5e74\u7fb9\u5c27\uff0c\u533b\u672f\u6709\u6ca1\u6709\u4f20\u4ed6\uff0c\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d\u5e9f\u56ed\u8001\u4eba\u53f9\u606f\u9053\uff1a\u201c\u4f20\u975e \u5176\u4eba\uff0c\u4f20\u975e\u5176\u4eba\uff01\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u662f\u6613\u5170\u73e0\u7684\u5f92\u5f1f\uff0c\u4ee5\u6613\u5170\u73e0\u7684\u5f92\u5f1f\uff0c\u4ee5\u6613\u5170\u73e0\u7684 \u8eab\u4efd\uff0c\u53ca\u5979\u5f53\u5e74\u4e0e\u5085\u8001\u5b97\u5e08\u7684\u6e0a\u6e90\uff0c\u5979\u5927\u53ef\u4ee5\u66ff\u65e0\u6781\u6d3e\u89c5\u8863\u94b5\u4f20\u4eba\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u6211\u4e5f\u542c\u5e08 \u5085\u95f2\u8bdd\u8bf4\u8fc7\uff0c\u662f\u6709\u8fd9\u4e2a\u5fc3\u613f\u3002\u201d \u3000\u3000\u51af\u745b\u5fc3\u4e2d\u9887\u4e3a\u5947\u602a\uff0c\u5e9f\u56ed\u8001\u4eba\u65e2\u8bf4\u6709\u4e8b\u8981\u5979\u4ee3\u529e\uff0c\u4f55\u4ee5\u5374\u5c3d\u8c08\u8fd9\u4e9b\u6b66\u6797\u4e2d\u5e9f\u7acb\u4e4b\u4e8b\u3002\u5e9f \u56ed\u8001\u4eba\u53c8\u54b3\u4e86\u4e00\u58f0\uff0c\u9762\u5bb9\u7aef\u8083\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u5085\u8001\u5b97\u5e08\u6709\u4e00\u672c\u9057\u4e66\u540d\u4e3a\u300a\u91d1\u9488\u5ea6\u4e16\u300b\uff0c\u4e43\u662f \u533b\u5b66\u7684\u5b9d\u85cf\u3002\u5c06\u6765\u82e5\u4f60\u5e08\u5085\u4ee3\u65e0\u6781\u6d3e\u7acb\u4e86\u4f20\u4eba\uff0c\u6216\u6709\u4eba\u5df2\u5f97\u4e86\u8fd9\u672c\u9057\u4e66\uff0c\u800c\u884c\u4e3a\u53c8\u5c5e\u6b63\u6d3e \u7684\uff0c\u4f60\u5c31\u5e26\u4ed6\u5230\u8fd9\u513f\u6765\uff0c\u53eb\u4ed6\u627f\u53d7\u6211\u7684\u533b\u6848\u3002\u5085\u8001\u5b97\u5e08\u5f53\u5e74\u5954\u6ce2\u56fd\u4e8b\uff0c\u6d6a\u8ff9\u6c5f\u6e56\uff0c\u533b\u6848\u79ef\u5b58 \u65e0\u591a\u3002\u5f97\u4e86\u4ed6\u7684\u9057\u4e66\uff0c\u518d\u53c2\u770b\u6211\u7684\u533b\u6848\uff0c\u624d\u80fd\u628a\u533b\u5b66\u53d1\u626c\u5149\u5927\u3002\u6211\u4eca\u751f\u5df1\u77e3\uff0c\u4f46\u613f\u6709\u4eba\u80fd\u8d85 \u8fc8\u524d\u8d24\u3002\u8fd9\u4e8b\u5341\u5206\u91cd\u8981\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d\u51af\u745b\u8eac\u8170\u7b54\u9053\uff1a\u201c\u77e5\u9053\uff01\u201d\u5e9f\u56ed\u8001\u4eba\u9053\uff1a\u201c\u6211\u56e0\u4f60\u662f \u6b66\u6797\u4fa0\u5973\uff0c\u6240\u4ee5\u624d\u5c06\u8fd9\u4e8b\u91cd\u6258\u4e8e\u4f60\u3002\u6211\u5c06\u5728\u4e34\u6b7b\u4e4b\u524d\uff0c\u4e86\u6b64\u5fc3\u613f\uff0c\u771f\u662f\u5927\u6170\u751f\u5e73\u3002\u201d \u3000\u3000\u51af\u745b\u5fae\u5fae\u4e00\u6115\uff0c\u9053\uff1a\u201c\u665a\u8f88\u5b9a\u5f53\u505a\u5230\u3002\u201d\u5e9f\u56ed\u8001\u4eba\u5ffd\u53c8\u7791\u76ee\u4e0d\u52a8\uff0c\u6e10\u6e10\u5782\u9996\u51e0\u53ca\u80f8\u819b\u3002 \u90a3\u5728\u65c1\u4f8d\u7acb\u7684\u8001\u5934\u4e0a\u524d\u66ff\u4ed6\u628a\u8109\uff0c\u5ffd\u7136\u8dea\u5012\u5730\u4e0a\uff0c\u606d\u606d\u656c\u656c\u78d5\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u9053\uff1a\u201c\u5148\u5e08\u9057 \u5fd7\uff0c\u5f1f\u5b50\u5b9a\u5f53\u7ee7\u627f\u3002\u4f60\u7684\u533b\u6848\u6211\u66ff\u4f60\u597d\u597d\u4fdd\u85cf\uff0c\u4ee5\u5f85\u8d24\u8005\uff0c\u4f60\u653e\u5fc3\u53bb\u5427\uff01\u201d \u3000\u3000\u51af\u745b\u5927\u9a87\uff0c\u9053\uff1a\u201c\u53f6\u8001\u516c\u516c\u600e\u4e48\u5566\uff1f\u201d\u90a3\u8001\u5934\u9053\uff1a\u201c\u4ed6\u5df2\u6b7b\u4e86\uff01\u201d\u51af\u745b\u5782\u6cea\u9053\uff1a\u201c\u662f\u6211 \u7d2f\u4ed6\u52b3\u5fc3\u8fc7\u5ea6\u4e48\uff1f\u201d\u90a3\u8001\u5934\u9053\uff1a\u201c\u4e0d\u5173\u59d1\u5a18\u4f60\u7684\u4e8b\uff0c\u5148\u5e08\u7cbe\u592a\u7d20\u7ecf\u3002\u4ed6\u65e9\u5df2\u6599\u5230\u4eca\u5915\u5bff\u5143\u5373 \u7ec8\u3002\u6240\u4ee5\u8fde\u591c\u4f20\u6211\u5fc3\u6cd5\u3002\u4e0d\u8fc7\uff0c\u5728\u4ed6\u4e34\u7ec8\u4e4b\u524d\uff0c\u8fd8\u66ff\u4f60\u7684\u4eb2\u4eba\u5f00\u65b9\u6cbb\u75c5\uff0c\u5374\u662f\u610f\u60f3\u4e0d\u5230\u3002\u201d \u51af\u745b\u611f\u6fc0\u4e4b\u6781\uff0c\u4e5f\u8dea\u5012\u5730\u4e0a\u778c\u4e86\u4e09\u4e2a\u54cd\u5934\u3002 \u3000\u3000\u90a3\u8001\u5934\u9001\u51af\u745b\u51fa\u95e8\uff0c\u90d1\u91cd\u8bf4\u9053\uff1a\u201c\u8fd9\u5305\u836f\u4f60\u5343\u4e07\u4e0d\u53ef\u9057\u5931\u4e86\u3002\u5931\u4e86\u65e0\u53ef\u518d\u914d\u3002\u4f46\u613f\u4f60\u7684 \u4eb2\u4eba\u80fd\u836f\u5230\u75c5\u9664\u3002\u201d\u51af\u745b\u62dc\u8c22\uff0c\u4e00\u770b\u5929\u8272\u5df2\u767d\uff0c\u6025\u6025\u544a\u8f9e\u3002\u5fc3\u60f3\u4f3c\u81ea\u5df1\u7684\u8f7b\u529f\uff0c\u5c3d\u53ef\u5728\u671f\u9650 \u4e4b\u524d\u5927\u534a\u5929\u8d76\u5230\uff0c\u5fc3\u4e2d\u5927\u4e3a\u6b23\u60a6\uff0c\u4e00\u8def\u4e0a\u6478\u90a3\u5305\u836f\uff0c\u751f\u6015\u9057\u5931\uff0c\u540e\u6765\u7d22\u6027\u628a\u836f\u634f\u5728\u624b\u5fc3\u3002 \u3000\u3000\u4e0d\u8bf4\u51af\u745b\u4e00\u8def\u7d27\u5f20\u3002\u4e14\u8bf4\u909d\u740f\u88ab\u5415\u56db\u5a18\u4e0e\u51af\u745b\u4ece\u9646\u5bb6\u64b5\u8d70\u4e4b\u540e\uff0c\u5fc3\u4e2d\u5927\u6124\u3002\u90a3\u51e0\u4e2a\u5934\u76ee \u9053\uff1a\u201c\u8fd9\u5973\u5a03\u5b50\u597d\u72e0\u8fa3\uff0c\u54b1\u4eec\u8bf7\u5be8\u4e3b\u6765\uff0c\u7edd\u4e0d\u80fd\u653e\u8fc7\u5979\u3002\u201d\u909d\u740f\u9ed8\u7136\u4e0d\u8bed\uff0c\u5ffd\u800c\u60f3\u9053\uff1a\u201c\u8fd9 \u5973\u5a03\u5b50\u5148\u524d\u5728\u7530\u91ce\u4e4b\u4e2d\u4e0e\u6211\u4eec\u53ae\u6740\u65f6\uff0c\u51fa\u624b\u66f4\u6bd2\uff0c\u6beb\u4e0d\u6253\u8bdd\uff0c\u5c31\u7528\u98de\u5200\u4f24\u4e86\u4e09\u4eba\u3002\u540e\u6765\u5728\u9646 \u5bb6\u4e4b\u65f6\uff0c\u51fa\u624b\u867d\u72e0\uff0c\u4f46\u5374\u53ea\u662f\u524a\u6389\u4ed6\u4eec\u5175\u5668\uff0c\u8f7b\u4f24\u4e00\u4eba\uff0c\u6bd4\u8d77\u5148\u65f6\uff0c\u4f3c\u4e4e\u5df1\u662f\u624b\u4e0b\u7559\u60c5\u4e86\uff0c \u4e0d\u77e5\u662f\u4f55\u539f\u6545\u3002\u54b3\uff0c\u770b\u5979\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5927\u7ea6\u53ea\u6709\u5341\u4e03\u516b\u5c81\uff0c\u6b66\u529f\u5374\u5982\u6b64\u9ad8\u5f3a\uff0c\u6211\u7684\u4e24\u4e2a\u5916\u5b59\u5973 \u513f\u82e5\u7136\u8fd8\u5728\u4e16\u7684\u8bdd\uff0c\u5e74\u7eaa\u5927\u7ea6\u4e5f\u548c\u5979\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u5f20\u5929\u6c60\u7b49\u4eba\u5728\u516b\u8fbe\u5cad\u9644\u8fd1\u7684\u4e00\u4e2a\u5c71\u5934\u7b49\u4ed6\uff0c\u909d\u740f\u8bf7\u4e0d\u5230\u533b\u751f\uff0c\u53c8\u88ab\u4f24\u4e86\u4e00\u4eba\uff0c\u5f88\u662f\u7f9e \u6127\uff0c\u4e00\u8def\u884c\u8d70\uff0c\u4e00\u8def\u601d\u91cf\u8ba9\u4e0d\u8ba9\u5f20\u5929\u6c60\u7387\u4f17\u62a5\u4ec7\u3002\u5f20\u5929\u6c60\u6b66\u529f\u6bd4\u909d\u740f\u9ad8\uff0c\u4f46\u909d\u740f\u5374\u6bd4\u4ed6\u8001\u6210 \u6301\u91cd\u3002\u909d\u740f\u5fc3\u77e5\u4ee5\u5f20\u5929\u6c60\u6027\u5b50\u4e4b\u8e81\uff0c\u4eca\u6b21\u624b\u4e0b\u88ab\u4f24\u4e86\u56db\u4eba\uff0c\u5b9a\u7136\u8981\u627e\u90a3\u5973\u5a03\u5b50\u62fc\u547d\uff0c\u4f46\u90a3\u5973 \u5a03\u5b50\u672c\u4e8b\u751a\u9ad8\uff0c\u800c\u4e14\u548c\u5979\u540c\u884c\u7684\u5c11\u5973\uff0c\u6b66\u529f\u795e\u5947\uff0c\u66f4\u662f\u6df1\u4e0d\u53ef\u6d4b\u3002\u5f20\u5929\u6c60\u591a\u534a\u4e0d\u662f\u5979\u4eec\u5bf9\u719f \u9170\u909d\u740f\u60f3\u9053\uff1a\u73b0\u5728\u5df2\u662f\u52bf\u7a77\u65e5\u8e59\uff0c\u5982\u4f55\u8fd8\u53ef\u62db\u60f9\u5f3a\u654c\uff1f\u6211\u53d7\u5f20\u7075\u98ce\u5927\u6069\uff0c\u53c8\u600e\u80fd\u8ba9\u4ed6\u7684\u513f\u5b50 \u7cca\u91cc\u7cca\u6d82\u53bb\u9001\u6b7b\u3002\u5fc3\u4e2d\u76d8\u7b97\u4e0d\u5b9a\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u624d\u80fd\u62e6\u4f4f\u4ed6\u3002\u9ec4\u660f\u65f6\u5206\uff0c\u9065\u89c1\u516b\u8fbe\u5cad\u7ef5\u4e98\u76ee \u524d\uff0c\u5f20\u5929\u6c60\u85cf\u533f\u7684\u5c71\u5934\uff0c\u4fbf\u5728\u9644\u8fd1\u3002\u6b63\u884c\u8d70\u95f4\uff0c\u5c71\u5773\u5904\u5ffd\u7136\u95ea\u51fa\u4e00\u4eba\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u662f \u4ec0\u4e48\u4eba\uff1f\u7ed9\u6211\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u909d\u740f\u4e00\u770b\uff0c\u53ea\u89c1\u6765\u4eba\u9e70\u9f3b\u72ee\u53e3\uff0c\u76f8\u8c8c\u72f0\u72de\uff0c\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u5341\u516d\u5e74\u524d\u7387\u4f17\u9053\u8ffd\u5468\u9752\uff0c\u6740 \u4e86\u4ed6\u7684\u4eb2\u5bb6\u51af\u5e7f\u6f6e\u7684\u9f99\u6728\u516c\u3002\u9f99\u6728\u516c\u662f\u9ece\u65cf\u914b\u957f\uff0c\u76f8\u8c8c\u5947\u7279\uff0c\u909d\u740f\u4e00\u89c1\uff0c\u5fc3\u4e2d\u706b\u8d77\uff0c\u4ef0\u5929\u6253 \u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\uff01\u9f99\u5927\u536b\u58eb\uff0c\u5e78\u4f1a\uff0c\u5e78\u4f1a\uff01\u201d \u3000\u3000\u539f\u6765\u5728\u4faf\u4e09\u53d8\u5e26\u8d70\u51af\u745b\u4e4b\u540e\uff0c\u5bab\u5ef7\u9707\u6012\uff0c\u751f\u6015\u4faf\u4e09\u53d8\u719f\u6089\u5bab\u4e2d\u9053\u8def\uff0c\u518d\u5f15\u4eba\u6765\uff0c\u800c\u4e14\u6015 \u4ed6\u5728\u5bab\u4e2d\u5c1a\u85cf\u6709\u5185\u7ebf\u3002\u4e8e\u662f\u4e00\u9762\u6574\u8083\u536b\u58eb\uff0c\u5e78\u559c\u5269\u4e0b\u51e0\u4e2a\u8001\u536b\u58eb\uff0c\u7ecf\u6b64\u4e00\u95f9\u90fd\u5df2\u9003\u4e86\uff1b\u53e6\u4e00 \u65b9\u9762\u54c8\u5e03\u9640\u53c8\u5e7f\u6d3e\u5fc3\u8179\u6b66\u58eb\uff0c\u5230\u5904\u641c\u67e5\u4faf\u4e09\u53d8\u548c\u51af\u745b\u4e0b\u843d\u3002\u4eac\u57ce\u4e00\u5e26\uff0c\u7531\u54c8\u5e03\u9640\u4eb2\u81ea\u7387\u9ad8\u624b \u641c\u67e5\uff0c\u90bb\u8fd1\u53bf\u4efd\uff0c\u5219\u6d3e\u6d77\u4e91\u548c\u5c1a\u4e0e\u4ed6\u7684\u5f92\u5f1f\u9f99\u6728\u516c\u53bb\u67e5\u63a2\u3002\u8fd9\u65e5\u4ed6\u4eec\u7a7f\u8fc7\u516b\u8fbe\u5cad\uff0c\u6d77\u4e91\u548c\u5c1a \u5148\u4e0a\u5cad\u4e86\u671b\uff0c\u8ba9\u9f99\u6728\u516c\u5728\u4e0b\u63a5\u5e94\u3002 \u3000\u3000\u9f99\u6728\u516c\u8d77\u521d\u4ee5\u4e3a\u909d\u740f\u7b49\u53ea\u662f\u9ed1\u9053\u4e2d\u7684\u65e0\u540d\u4e4b\u8f88\uff0c\u60f3\u987a\u624b\u64d2\u6765\uff0c\u7acb\u4e00\u5c0f\u529f\u3002\u4e0d\u6599\u7ed9\u909d\u740f\u4e00 \u53e3\u9053\u7834\u6765\u5386\uff0c\u4e0d\u89c9\u6115\u7136\u3002\u7741\u773c\u4e00\u626b\uff0c\u4f9d\u7a00\u8ba4\u5f97\u3002\u909d\u740f\u559d\u9053\uff1a\u201c\u4f60\u72d7\u773c\u77a7\u6e05\u695a\u6ca1\u6709\uff1f\u6cb3\u5357\u6c5d\u5dde \u51af\u6b66\u5e08\u4e00\u5bb6\uff0c\u88ab\u4f60\u4eec\u5f04\u5f97\u6b7b\u7684\u6b7b\uff0c\u9003\u7684\u9003\uff0c\u8fd9\u7b14\u8840\u5e10\uff0c\u4f60\u8fd8\u8bb0\u5f97\u4e48\uff1f\u201d\u9f99\u6728\u516c\u602a\u773c\u4e00\u7ffb\u8bf4 \u9053\uff1a\u201c\u54c8\uff0c\u6211\u9053\u662f\u8c01\uff1f\u539f\u6765\u662f\u4f60\u8fd9\u6f0f\u7f51\u7684\u8001\u5339\u592b\u3002\u8001\u5b50\u751f\u5e73\u6740\u4eba\u4e0d\u8ba1\u5176\u6570\uff0c\u54ea\u8bb0\u5f97\u8bb8\u591a\uff01\u4f60 \u6709\u4ec0\u4e48\u80fd\u529b\uff0c\u8981\u66ff\u51af\u5e7f\u6f6e\u62a5\u4ec7\uff1f\u201d\u957f\u5251\u4e00\u7ffb\uff0c\u4fbf\u5148\u52a8\u624b\u3002 \u3000\u3000\u5341\u4e03\u5e74\u524d\uff0c\u909d\u740f\u88ab\u9f99\u6728\u516c\u6740\u5f97\u72fc\u72c8\u9003\u751f\uff0c\u4e24\u4eba\u6b66\u529f\u53ef\u8bf4\u76f8\u5dee\u6781\u8fdc\u3002\u9f99\u6728\u516c\u54ea\u91cc\u628a\u4ed6\u653e\u5728 \u5fc3\u4e0a\uff0c\u4e00\u52a8\u624b\uff0c\u4fbf\u811a\u8e0f\u4e2d\u5bab\uff0c\u6b3a\u8eab\u8fdb\u5251\u3002\u90a3\u77e5\u5341\u516d\u5e74\u95f4\uff0c\u53d8\u5316\u6781\u5927\uff0c\u4eca\u65e5\u7684\u909d\u740f\uff0c\u5df2\u8fdc\u975e\u6614 \u65e5\u53ef\u6bd4\uff0c\u65f1\u70df\u888b\u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\u4fbf\u7acb\u523b\u628a\u9f99\u6728\u516c\u7684\u957f\u5251\u5c01\u4e86\u51fa\u53bb\u3002\u9f99\u6728\u516c\u5403\u4e86\u4e00\u60ca\uff0c\u909d\u740f \u7684\u70df\u888b\u5f80\u4e0b\u4e00\u6ed1\uff0c\u75be\u70b9\u4ed6\u7684\u201c\u5929\u67a2\u7a74\u201d\uff0c\u9f99\u6728\u516c\u88ab\u903c\u5f97\u8fde\u9000\u4e09\u6b65\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u5085\u5feb\u6765\uff01\u201d \u3000\u3000\u909d\u740f\u5927\u7b11\u9053\uff1a\u201c\u4e3a\u4f55\u4e0d\u53eb\u5e08\u5a18\u6551\u547d\uff1f\u201d\u8ddf\u8e2a\u6025\u8fdb\uff0c\u94c1\u70df\u888b\u5f80\u5916\u4e00\u7529\uff0c\u70b9\u6253\u4ed6\u7684\u540e\u5fc3\u3002\u9f99 \u6728\u516c\u53cd\u624b\u4e00\u5251\uff0c\u8eab\u8eaf\u534a\u8f6c\uff0c\u659c\u950b\u8fdb\u5251\u3002\u909d\u740f\u70df\u88f3\u5f80\u4e0b\u4e00\u538b\uff0c\u5c06\u9f99\u6728\u516c\u957f\u5251\u538b\u7740\uff0c\u559d\u58f0\uff1a \u201c\u53bb\uff01\u201d\u70df\u6746\u4e00\u62ac\uff0c\u5c06\u9f99\u6728\u516c\u9707\u51fa\u4e00\u4e08\u5f00\u5916\u3002\u9f99\u6728\u516c\u672c\u9886\u4e5f\u7b97\u4e0d\u5f31\uff0c\u5c45\u7136\u5e76\u672a\u8dcc\u5012\u3002\u53c8\u9ad8\u53eb \u9053\uff1a\u201c\u5e08\u5085\u5feb\u6765\uff01\u201d \u3000\u3000\u909d\u740f\u6362\u62db\u518d\u6253\uff0c\u9f99\u6728\u516c\u529b\u654c\u6570\u62db\uff0c\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c\u909d\u740f\u8d8a\u6253\u8d8a\u72e0\uff0c\u62fc\u6597 \u4e86\u7ea6\u4e09\u4e94\u5341\u62db\uff0c\u65c1\u89c2\u7684\u4eba\u53eb\u9053\uff1a\u201c\u526f\u5be8\u4e3b\uff0c\u6709\u4e00\u4e2a\u548c\u5c1a\u6765\u4e86\uff01\u201d\u909d\u740f\u9053\uff1a\u201c\u597d\uff0c\u8ba9\u4ed6\u7684\u5e08\u5085 \u66ff\u4ed6\u9001\u4e27\uff01\u201d\u9f99\u6728\u516c\u7cbe\u795e\u9661\u632f\uff0c\u594b\u529b\u4e00\u5251\uff0c\u53cd\u523a\u909d\u740f\u8170\u80c1\uff0c\u909d\u740f\u65e9\u6599\u4ed6\u6709\u6b64\u4e00\u62db\uff0c\u70df\u6746\u4e00 \u62bd\uff0c\u9f99\u6728\u516c\u4e00\u5251\u6eaf\u7a7a\uff0c\u91cd\u5fc3\u4e0d\u7a33\uff0c\u8eab\u5b50\u524d\u503e\uff0c\u909d\u740f\u4e00\u58f0\u957f\u7b11\uff0c\u94c1\u70df\u888b\u4e00\u62db\u201c\u5012\u6253\u91d1\u949f\u201d\uff0c\u535c \u7684\u4e00\u58f0\uff0c\u5c06\u9f99\u6728\u516c\u9888\u9aa8\u6572\u788e\uff0c\u72c2\u7b11\u9053\uff1a\u201c\u51af\u4eb2\u5bb6\uff0c\u5c0f\u5f1f\u4eca\u65e5\u66ff\u4f60\u62a5\u4e86\u4ec7\u4e86\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u6d77\u4e91\u548c\u5c1a\u5df2\u5982\u98de\u800c\u81f3\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u8c01\u6562\u4f24\u6211\u5f92\u5f1f\uff1f\u201d\u909d\u740f\u7684\u56db\u4e2a\u624b\u4e0b \uff08\u5176\u4e2d\u4e00\u4eba\u8f7b\u4f24\uff09\uff0c\u54ea\u77e5\u5389\u5bb3\uff0c\u8fce\u4e0a\u524d\u53bb\u3002\u909d\u740f\u521a\u6536\u62fe\u4e86\u9f99\u6728\u516c\uff0c\u7acb\u5373\u4fbf\u542c\u89c1\u60e8\u53eb\u4e4b\u58f0\uff0c\u8fde \u7eed\u4e0d\u65ad\uff0c\u53ea\u89c1\u90a3\u548c\u5c1a\u5251\u5149\u75be\u5377\uff0c\u8840\u96e8\u817e\u7a7a\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u56db\u540d\u5927\u6c49\u90fd\u6bd9\u5728\u4ed6\u7684\u5251\u4e0b\u3002 \u3000\u3000\u909d\u740f\u5927\u6012\uff0c\u94c1\u70df\u6746\u5f80\u524d\u75be\u70b9\uff0c\u6d77\u4e91\u548c\u5c1a\u4e5f\u5411\u524d\u75be\u8fdb\uff0c\u5251\u5149\u75be\u5c55\uff0c\u5212\u4ed6\u624b\u8155\uff0c\u909d\u740f\u5f80\u5916\u4e00 \u683c\uff0c\u6d77\u4e91\u548c\u5c1a\u8eab\u5f62\u5feb\u6781\uff0c\u5251\u62db\u5982\u7535\uff0c\u55d6\u7684\u6a2a\u622a\u8fc7\u53bb\uff0c\u909d\u740f\u4e00\u7f29\u80a9\u5934\uff0c\u53cd\u6253\u4ed6\u7684\u201c\u80cc\u6881\u7a74\u201d\uff0c \u6d77\u4e91\u548c\u5c1a\u8eab\u5f62\u4e00\u95ea\uff0c\u5251\u52bf\u7565\u504f\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5251\u98ce\u63a0\u80a9\u800c\u8fc7\uff0c\u909d\u740f\u6697\u53eb\u4e00\u58f0\u201c\u597d\u9669\u201d\uff01\u659c\u8dc3\u4e09 \u6b65\uff0c\u56de\u8eab\u518d\u6218\u3002 \u3000\u3000\u909d\u740f\u867d\u7136\u82e6\u7ec3\u4e86\u5341\u516b\u5e74\uff0c\u6bd4\u6d77\u4e91\u548c\u5c1a\uff0c\u5230\u5e95\u8fd8\u76f8\u5dee\u4e00\u7b79\u3002\u5e78\u5728\u5929\u53f0\u6d3e\u7684\u6b66\u529f\uff0c\u9887\u591a\u65b0\u5947 \u62db\u6570\uff0c\u90a3\u6746\u94c1\u70df\u888b\u65e2\u53ef\u5f53\u4e94\u884c\u5251\u7528\uff0c\u4ea6\u53ef\u4f5c\u70b9\u7a74\u6485\u4f7f\uff0c\u534a\u5b88\u534a\u653b\uff0c\u5c45\u7136\u4e5f\u62fc\u6597\u4e86\u4e00\u767e\u6765\u62db\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u5c06\u5165\u9ed1\uff0c\u66ae\u8272\u9634\u973e\uff0c\u9e26\u58f0\u566a\u6797\uff0c\u91ce\u98ce\u64bc\u6811\uff0c\u909d\u740f\u652f\u6301\u4e0d\u4f4f\uff0c\u6e10\u89c9\u5fc3\u5bd2\u3002\u62fc\u4e86\u6027 \u547d\uff0c\u84e6\u7136\u53cd\u51fb\uff0c\u6d77\u4e91\u548c\u5c1a\u6b63\u4f7f\u51fa\u4e00\u62db\u201c\u4ed9\u4eba\u6362\u5f71\u201d\uff0c\u4e00\u62db\u4e24\u5f0f\uff0c\u4e00\u865a\u4e00\u5b9e\uff0c\u865a\u523a\u9762\u95e8\uff0c\u5b9e\u524a \u80f8\u80c1\uff0c\u4ee5\u4e3a\u909d\u740f\u4e0d\u662f\u4e0a\u683c\u4fbf\u662f\u4e0b\u6321\uff0c\u90a3\u65f6\u865a\u5b9e\u5e76\u7528\uff0c\u4e92\u76f8\u8f6c\u6362\uff0c\u654c\u4eba\u7edd\u9003\u4e0d\u4e86\u3002\u90a3\u77e5\u909d\u740f\u62fc \u4e86\u6027\u547d\uff0c\u7a81\u7136\u6251\u8eab\u51fb\u4ed6\u4e2d\u76d8\uff0c\u53ea\u542c\u5f97\u548b\u5587\u4e00\u58f0\uff0c\u909d\u740f\u7684\u80f8\u9aa8\u88ab\u4ed6\u5251\u950b\u524a\u65ad\u4e24\u6839\uff0c\u6d77\u4e91\u548c\u5c1a\u7684 \u524d\u5fc3\u4e5f\u88ab\u4ed6\u7684\u94c1\u70df\u888b\u91cd\u91cd\u51fb\u4e86\u4e00\u8bb0\uff01 \u3000\u3000\u6d77\u4e91\u548c\u5c1a\u5185\u529f\u6df1\u539a\uff0c\u5403\u4e86\u4e00\u8bb0\uff0c\u5c1a\u652f\u6301\u5f97\u4f4f\uff0c\u4e0d\u8fc7\u80f8\u53e3\u4ea6\u5df2\u5267\u75db\u5982\u5272\uff0c\u4e0d\u7531\u5927\u6012\uff0c\u817e\u7684 \u98de\u8d77\u4e00\u811a\uff0c\u5c06\u909d\u740f\u8e22\u7ffb\uff0c\u909d\u740f\u80f8\u53e3\u6240\u53d7\u5251\u4f24\uff0c\u672c\u5df2\u751a\u91cd\uff0c\u52a0\u4e0a\u8fd9\u4e00\u811a\uff0c\u767b\u65f6\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6d77\u4e91\u548c\u5c1a\u53d1\u51fa\u72de\u7b11\uff0c\u6367\u7740\u80f8\u53e3\uff0c\u6b63\u60f3\u53bb\u5272\u654c\u4eba\u9996\u7ea7\uff0c\u5ffd\u542c\u5f97\u5c71\u4e0a\u4e00\u4eba\u558a\u9053\uff1a\u201c\u6d77\u4e91\u79c3 \u8d3c\uff0c\u5f80\u90a3\u91cc\u8dd1\uff1f\u201d\u6d77\u4e91\u4e00\u542c\uff0c\u5413\u5f97\u9b42\u9500\u9b44\u6563\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u53ae\u6599\u4e0d\u80fd\u518d\u6d3b\u4e86\uff0c\u5bf9\u5934\u592a\u5f3a\uff0c\u8fd8\u662f \u9003\u547d\u8981\u7d27\u3002\u201d\u5fcd\u7740\u80f8\u53e3\u5267\u75db\uff0c\u6025\u6025\u9041\u9003\u3002\u6765\u4eba\u4e43\u662f\u674e\u6cbb\u3002 \u3000\u3000\u674e\u6cbb\u548c\u51af\u7433\u81ea\u90a3\u665a\u4ece\u5e74\u5bb6\u9003\u51fa\u4e4b\u540e\uff0c\u674e\u6cbb\u5df2\u77e5\u5979\u4e0d\u662f\u51af\u745b\uff0c\u4f46\u76f8\u5904\u591a\u65f6\uff0c\u60c5\u6839\u65e9\u79cd\uff0c\u867d \u77e5\u5979\u4e0d\u662f\u51af\u745b\uff0c\u4e5f\u820d\u4e0d\u5f97\u79bb\u5f00\u5979\u4e86\u3002 \u3000\u3000\u51af\u7433\u9003\u51fa\u5e74\u5bb6\u4e4b\u9645\uff0c\u6b63\u662f\u51af\u745b\u649e\u5165\u5e74\u5bb6\u4e4b\u65f6\uff0c\u867d\u662f\u60ca\u9e3f\u4e00\u77a5\uff0c\u4f46\u5df2\u89e6\u76ee\u96be\u5fd8\uff0c\u51af\u7433\u8fd9\u624d \u76f8\u4fe1\u4e16\u754c\u4e0a\u771f\u6709\u4e00\u4e2a\u548c\u81ea\u5df1\u76f8\u50cf\u4e4b\u4eba\uff01\u53ef\u662f\u5979\u8fd8\u4e0d\u77e5\u9053\u8fd9\u4eba\u4fbf\u662f\u81ea\u5df1\u7684\u59d0\u59d0\uff01 \u3000\u3000\u51af\u7433\u5bf9\u81ea\u5df1\u5e7c\u65f6\u4e4b\u4e8b\uff0c\u5168\u8bb0\u4e0d\u5f97\uff0c\u674e\u6cbb\u518d\u4e09\u8bf1\u53d1\u5979\u7684\u8bb0\u5fc6\uff0c\u90fd\u5c5e\u5f92\u7136\u3002\u4f46\u51af\u7433\u5374\u8bb0\u5f97\u5230 \u4e86\u56db\u7687\u5e9c\u4ee5\u540e\u7684\u4e8b\u3002\u674e\u6cbb\u867d\u7136\u4e5f\u4e0d\u77e5\u9053\u51af\u745b\u4fbf\u662f\u5979\u7684\u59d0\u59d0\uff0c\u4f46\u5e7c\u65f6\u5374\u542c\u5f97\u6bcd\u4eb2\u8bf4\u8fc7\uff0c\u51af\u745b\u662f \u6613\u5170\u73e0\u4ece\u56db\u7687\u5e9c\u4e2d\u62b1\u56de\u6765\u7684\u3002\u4e0d\u514d\u60f3\u9053\uff1a\u201c\u4e16\u754c\u4e0a\u54ea\u6709\u5982\u6b64\u5de7\u5408\u4e4b\u4e8b\uff1f\u4e24\u4eba\u9762\u8c8c\u5982\u6b64\u76f8\u4f3c\uff0c \u800c\u4e14\u53c8\u90fd\u66fe\u5728\u56db\u7687\u5b50\u7684\u5e9c\u90b8\u6e21\u8fc7\u7ae5\u5e74\uff1f\u8fd9\u79cd\u5947\u4e8b\uff0c\u5012\u4e0d\u80fd\u4e0d\u63a2\u4e2a\u6c34\u843d\u77f3\u51fa\u3002 \u3000\u3000\u674e\u6cbb\u4e3a\u4eba\u6734\u539a\uff0c\u6700\u91cd\u53cb\u60c5\u3002\u4ed6\u4e0e\u51af\u745b\u4e43\u662f\u9752\u6885\u7af9\u9a6c\u4ea4\uff0c\u867d\u7136\u5e7c\u65f6\u4e0d\u89e3\u7537\u5973\u4e4b\u60c5\uff0c\u4f46\u4e24\u5c0f \u65e0\u731c\uff0c\u5fc3\u4e2d\u65e9\u5df2\u628a\u5bf9\u65b9\u5f53\u6210\u6700\u597d\u7684\u4f34\u4fa3\uff0c\u8fd9\u65f6\u674e\u6cbb\u867d\u5df2\u7231\u4e0a\u51af\u7433\uff0c\u4f46\u5bf9\u51af\u745b\u7a76\u662f\u5fd8\u6000\u4e0d\u4e86\u3002 \u5fc3\u60f3\uff1a\u745b\u59b9\u65e2\u7136\u4e0b\u5c71\uff0c\u6211\u600e\u4e48\u6837\u4e5f\u5f97\u627e\u7740\u5979\uff0c\u4e00\u6765\u6211\u8981\u5bf9\u5979\u8bf4\u660e\u4e0b\u5c71\u4e4b\u540e\u7684\u7ecf\u8fc7\uff0c\u8ba9\u5979\u4e5f\u4e3a \u6211\u6b22\u559c\uff1b\u4e8c\u6765\u6211\u4e5f\u8be5\u8ba9\u5979\u89c1\u89c1\u7433\u513f\uff0c\u597d\u53eb\u5979\u77e5\u9053\u4e16\u754c\u4e0a\u6709\u4e00\u4e2a\u4eba\u548c\u5979\u8fd9\u822c\u76f8\u4f3c\u3002\u5979\u4eec\u4e24\u4eba\u5b9e \u5728\u5e94\u8be5\u7ed3\u62dc\u6210\u4e3a\u59d0\u59b9\u3002 \u3000\u3000\u56e0\u6b64\u674e\u6cbb\u6e34\u671b\u627e\u89c1\u51af\u745b\u4e4b\u5fc3\u5c31\u5982\u51af\u7433\u4e00\u6837\uff0c\u4e24\u4eba\u90fd\u4ee5\u4e3a\u51af\u745b\u4e00\u5b9a\u88ab\u7687\u5e1d\u6349\u5165\u5bab\u4e2d\u53bb\u4e86\uff0c \u51af\u7433\u5fc3\u60f3\u51af\u745b\u662f\u4ee3\u81ea\u5df1\u53d7\u96be\uff0c\u751a\u662f\u4e0d\u5b89\uff0c\u56e0\u6b64\u613f\u5192\u5927\u9669\uff0c\u5077\u8fdb\u4eac\u57ce\uff0c\u5e0c\u671b\u80fd\u6709\u673a\u4f1a\u627e\u5230\u4e00\u4e9b \u7ebf\u7d22\u3002 \u3000\u3000\u4f46\u51af\u7433\u53c8\u662f\u7687\u5e1d\u6240\u8981\u6355\u6349\u7684\u4eba\uff0c\u4e24\u4eba\u90fd\u4e0d\u6562\u629b\u5934\u9732\u9762\u5728\u5927\u8def\u4e0a\u8d70\uff0c\u53ea\u662f\u9009\u62e9\u4e61\u6751\u50fb\u5f84\uff0c \u4e1c\u7ed5\u897f\u7ed5\uff0c\u8f6c\u6765\u8f6c\u53bb\uff0c\u8d70\u4e86\u4e00\u5e74\u6709\u591a\uff0c\u624d\u6765\u5230\u5317\u4eac\u57ce\u5916\u7684\u6000\u67d4\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u5e74\u5f53\u4e2d\uff0c\u674e\u6cbb\u4e00\u6709\u7a7a\u5c31\u770b\u5085\u9752\u4e3b\u9057\u4e0b\u7684\u533b\u4e66\uff0c\u5c06\u533b\u7406\u80cc\u5f97\u6eda\u74dc\u70c2\u7ff1\u3002\u5bf9\u6cbb\u7597\u79bb\u9b42 \u75c7\u7684\u75c5\u6848\uff0c\u66f4\u662f\u6f5c\u5fc3\u7814\u8ba8\u3002\u53ea\u662f\u4ed6\u5728\u672a\u6709\u5341\u5206\u628a\u63e1\u4e4b\u524d\uff0c\u53ef\u4e0d\u6562\u8f7b\u6613\u62ff\u51af\u7433\u6765\u8bd5\u9a8c\u3002 \u3000\u3000\u51af\u7433\u5728\u8fd9\u4e00\u5e74\u5f53\u4e2d\uff0c\u4e5f\u5c06\u5085\u9752\u4e3b\u9057\u4e0b\u7684\u62f3\u7ecf\u5251\u8bc0\u7814\u4e60\u4e86\u51e0\u904d\u3002\u51af\u7433\u672c\u5c31\u7cbe\u901a\u597d\u51e0\u6d3e\u6b66 \u529f\uff0c\u800c\u4eca\u5f97\u4e86\u5185\u5bb6\u771f\u4f20\uff0c\u878d\u4f1a\u8d2f\u901a\uff0c\u6b66\u529f\u66f4\u662f\u5927\u975e\u6614\u65e5\u53ef\u6bd4\uff01 \u3000\u3000\u8fd9\u4e00\u65e5\u4ed6\u4eec\u5728\u6000\u67d4\u53bf\u4e61\u4e0b\u7684\u7530\u91ce\uff0c\u9047\u89c1\u4e86\u909d\u740f\u8fd9\u4e00\u5e2e\u4eba\uff0c\u6765\u5411\u51af\u7433\u8ba8\u53d6\u5085\u9752\u4e3b\u7684\u9057\u4e66\u3002 \u51af\u7433\u51fa\u624b\u4f24\u4e86\u4e09\u4eba\uff0c\u4e0e\u674e\u6cbb\u9003\u4e0a\u5c71\u5934\u3002\u674e\u6cbb\u60f3\u8d77\u4e00\u4e8b\uff0c\u5ffd\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d \u3000\u3000\u51af\u7433\u7b11\u9053\uff0c\u201c\u50bb\u54e5\u54e5\uff0c\u6253\u4e86\u80dc\u4ed7\uff0c\u6709\u4ec0\u4e48\u4e0d\u597d\u5440\uff1f\u201d\u674e\u6cbb\u8e59\u7709\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u8d77\u6765\u4e86\uff0c\u539f \u6765\u4f60\u5e76\u4e0d\u662f\u65e0\u6781\u6d3e\u7684\u4f20\u4eba\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u672c\u6765\u662f\u9a97\u4f60\u7684\u561b\uff0c\u4f60\u65e9\u5c31\u5e94\u5f53\u77e5\u9053\u4e86\uff0c\u4e3a\u4ec0\u4e48\u73b0 \u5728\u624d\u60f3\u8d77\uff1f\u201d\u674e\u6cbb\u82e6\u7b11\u9053\uff1a\u201c\u6211\u5b66\u533b\u5b66\u5f97\u5165\u4e86\u8ff7\uff0c\u4f60\u4ee5\u524d\u8bf4\u8fc7\u7684\u8bdd\u53c8\u591a\uff0c\u5c31\u65e0\u6687\u7ec6\u60f3\u4f60\u90a3\u4e00 \u6869\u662f\u9a97\u6211\uff0c\u90a3\u4e00\u6869\u4e0d\u662f\u9a97\u6211\u7684\u4e86\uff0c\u5085\u9752\u4e3b\u7684\u9057\u4e66\u9664\u4e86\u65e0\u6781\u6d3e\u7684\u8863\u94b5\u4f20\u4eba\u4e4b\u5916\uff0c\u522b\u4eba\u5b9e\u5728\u4e0d\u5e94 \u7a83\u53d6\u3002\u201d\u51af\u7433\u6014\u4e86\u4e00\u6014\uff0c\u7b11\u9053\uff1a\u201c\u96be\u9053\u4f60\u8981\u5c06\u4ed6\u7684\u4e66\u4ea4\u56de\u5e74\u7fb9\u5c27\u5417\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u56fa \u7136\u4e0d\u914d\u636e\u6709\u6b64\u4e66\uff0c\u4f46\u6211\u4eec\u4e5f\u4e0d\u5e94\u636e\u4e3a\u5df2\u6709\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u53cd\u6b63\u8fd9\u662f\u65e0\u4e3b\u4e4b\u7269\uff0c\u6211\u4eec\u8981\u4e86\u53c8\u6709 \u4f55\u59a8\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u975e\u4efd\u800c\u5f97\uff0c\u541b\u4e2d\u4e0d\u53d6\u3002\u201d\u51af\u7433\u607c\u9053\uff1a\u201c\u4f60\u5df2\u628a\u533b\u4e66\u719f\u8bb0\u5fc3\u4e2d\uff0c\u6211\u4e5f\u628a\u65b0 \u7ecf\u5251\u8bc0\u90fd\u7814\u4e60\u4e86\uff0c\u96be\u9053\u8fd8\u80fd\u628a\u5b83\u4ece\u5fc3\u4e2d\u6316\u51fa\u53bb\u5417\uff1f\u201d\u674e\u6cbb\u5341\u5206\u82e6\u607c\uff0c\u9053\uff1a\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u6211\u4e5f \u4e0d\u8be5\u53bb\u8bfb\u5b83\u4e86\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u4f60\u7684\u6613\u4f2f\u6bcd\u53ef\u4ee5\u4e3a\u65e0\u6781\u6d3e\u4ee3\u7acb\u4f20\u4eba\u4e48\uff1f\u5c31\u53eb\u5979\u7acb\u6211\u597d \u4e86\u3002\u201d\u674e\u6cbb\u5550\u4e86\u4e00\u53e3\u9053\uff1a\u201c\u4f60\u548c\u65e0\u6781\u6d3e\u6709\u4ec0\u4e48\u6e0a\u6e90\uff0c\u4f60\u53c8\u4e0d\u662f\u949f\u4e07\u5802\u7684\u5f1f\u5b50\u3002\u201d\u51af\u7433\u5ffd\u9053\uff1a \u201c\u6211\u7b2c\u4e00\u6b21\u542c\u5230\u949f\u4e07\u5802\u7684\u540d\u5b57\u65f6\uff0c\u5df2\u7ecf\u89c9\u5f97\u751a\u719f\uff0c\u4e0d\u77e5\u4ec0\u4e48\u7f18\u6545\uff1f\u6216\u8bb8\u6211\u548c\u65e0\u6781\u6d3e\u6709\u6e0a\u6e90\u4e5f \u672a\u53ef\u77e5\u3002\u201d\u674e\u6cbb\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u6765\u9a97\u6211\u4e86\uff01\u201d \u3000\u3000\u51af\u7433\u867d\u662f\u767e\u7aef\u5f00\u89e3\uff0c\u674e\u6cbb\u5fc3\u4e2d\u603b\u89c9\u4e0d\u5b89\uff0c\u51af\u7433\u540e\u6765\u4e5f\u5c31\u4e0d\u7406\u776c\u4ed6\u4e86\u3002\u8fd1\u9ec4\u660f\u65f6\u5206\uff0c\u4e24\u4eba \u6765\u5230\u4e86\u516b\u8fbe\u5cad\u3002\u5ffd\u542c\u5f97\u6df1\u5c71\u5bc6\u6797\u4e4b\u4e2d\uff0c\u4f20\u6765\u5bfa\u9662\u665a\u949f\u3002\u674e\u6cbb\u9053\uff1a\u201c\u54b1\u4eec\u4e14\u53bb\u6295\u5bbf\u3002\u201d\u51af\u7433\u7b11 \u9053\uff1a\u201c\u53c8\u53ef\u53bb\u6c42\u4f5b\u7956\u5bbd\u6055\uff0c\u5c31\u8bf4\u4fe1\u5973\u51af\u7433\u7d2f\u5584\u7537\u674e\u6cbb\u72af\u4e86\u7f6a\uff0c\u8bf7\u6c42\u6211\u4f5b\u6148\u60b2\uff0c\u66ff\u4ed6\u89e3\u8131\u3002\u201d \u674e\u6cbb\u88ab\u4ed6\u9017\u5f97\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u4ec0\u4e48\u65f6\u5019\u624d\u6539\u5f97\u6389\u8fd9\u6cb9\u5634\u554a\uff01\u201d \u3000\u3000\u4e24\u4eba\u5faa\u7740\u949f\u58f0\u5bfb\u53bb\uff0c\u5bfb\u5230\u4e86\u4e00\u5ea7\u8352\u51c9\u7684\u53e4\u5239\uff0c\u665a\u949f\u68b5\u5457\uff0c\u5c31\u4ece\u53e4\u5239\u4e4b\u4e2d\u4f20\u51fa\u3002\u674e\u6cbb\u4e0a\u524d \u8f7b\u6572\u5bfa\u95e8\uff0c\u91cc\u9762\u5ff5\u7ecf\u4e4b\u58f0\u5373\u6b62\uff0c\u95e8\u5f00\u5904\u53ea\u89c1\u4e00\u4e2a\u4e2d\u5e74\u5c3c\u59d1\uff0c\u6301\u7740\u5ff5\u73e0\u9053\uff1a\u201c\u5c71\u4e0b\u4e0d\u8fdc\u5c1a\u6709\u519c \u5bb6\uff0c\u6211\u5355\u8eab\u5c3c\u59d1\uff0c\u4e0d\u4fbf\u7559\u5ba2\u4eba\u4f4f\u5bbf\u3002\u201d \u3000\u3000\u53e4\u5239\u91cc\u900f\u51fa\u706f\u5149\uff0c\u51af\u7433\u62ac\u5934\u4e00\u671b\uff0c\u5ffd\u89c9\u8fd9\u5c3c\u59d1\u597d\u50cf\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\u4f3c\u7684\uff0c\u5fc3\u4e2d\u4e00\u9707\uff0c\u4e0d \u89c9\u5b9a\u4e86\u773c\u795e\uff0c\u90a3\u5c3c\u59d1\u89c1\u4e86\u51af\u7433\uff0c\u9762\u8272\u500f\u7136\u4e00\u8f6c\uff0c\u8eab\u8eaf\u5fae\u5fae\u98a4\u6296\uff0c\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u8fd8\u6709\u4e00\u4f4d\u5973 \u5c45\u58eb\u540c\u6765\uff0c\u8bf7\u8fdb\uff0c\u8bf7\u8fdb\uff01\u201d \u3000\u3000\u674e\u6cbb\u4e0d\u77e5\u5979\u4f55\u4ee5\u8f6c\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u53ea\u89c1\u51af\u7433\u5df2\u8ddf\u7740\u5979\u8d70\u8fdb\u5bfa\u9662\uff0c\u4fbf\u9053\u4e86\u58f0\u8c22\uff0c\u4e5f\u8ddf\u7740\u8fdb \u53bb\u3002\u53e4\u5239\u867d\u7136\u8352\u829c\uff0c\u5bfa\u4e2d\u5374\u6536\u62fe\u5f97\u975e\u5e38\u5e72\u51c0\uff0c\u90a3\u5c3c\u59d1\u5ffd\u5401\u4e86\u53e3\u6c14\uff0c\u56de\u5934\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u53ef\u80af\u5c06 \u540d\u5b57\u89c1\u544a\u5417\uff1f\u201d \u3000\u3000\u674e\u6cbb\u548c\u51af\u7433\u4e00\u8def\u4e0a\u7528\u7684\u90fd\u662f\u5047\u540d\uff0c\u5c3c\u59d1\u4e00\u95ee\uff0c\u674e\u6cbb\u5c31\u5c06\u4e24\u4eba\u7684\u5047\u540d\u8bf4\u4e86\uff0c\u5c3c\u59d1\u9762\u4e0a\u597d\u50cf \u9732\u51fa\u4e00\u4e1d\u5931\u671b\u7684\u795e\u8272\u3002 \u3000\u3000\u674e\u6cbb\u597d\u751f\u5947\u602a\uff0c\u51af\u7433\u5219\u53ea\u89c9\u8ff7\u8ff7\u60af\u60af\uff0c\u5fc3\u4e2d\u6240\u89e6\uff0c\u5c31\u5982\u521d\u5230\u5e74\u7fb9\u5c27\u5bb6\u4e2d\u4e00\u6837\uff0c\u603b\u50cf\u6709\u4ec0 \u4e48\u4e8b\u60c5\u548c\u81ea\u5df1\u6781\u6709\u5173\u8054\uff0c\u81ea\u5df1\u4e0d\u77e5\u5728\u54ea\u4e00\u4e2a\u68a6\u4e2d\u66fe\u89c1\u8fc7\u8fd9\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4e00\u7247\u5730\u3002\u90a3\u4e2d\u5e74\u5c3c\u59d1\u62db \u547c\u4e24\u5750\u4e0b\u4e4b\u540e\uff0c\u9053\uff1a\u201c\u8bf7\u95ee\u8fd9\u4f4d\u5973\u5c45\u571f\uff0c\u4eca\u5e74\u591a\u5927\u5e74\u7eaa\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u5341\u516b\u5c81\u4e86\uff01\u201d\u674e\u6cbb\u5fc3 \u9053\uff1a\u201c\u8fd9\u5c3c\u59d1\u597d\u65e0\u793c\uff0c\u53c8\u4e0d\u662f\u66ff\u4eba\u505a\u5a92\uff0c\u4e00\u89c1\u9762\u5c31\u95ee\u522b\u4eba\u7684\u5e74\u7eaa\u5e72\u561b\uff1f\u201d \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u4ee5\u51af\u7433\u90a3\u6837\u7684\u987d\u76ae\u4efb\u6027\uff0c\u5bf9\u8fd9\u4e2d\u5e74\u5c3c\u59d1\u5374\u4f3c\u751a\u4e3a\u987a\u4ece\uff0c\u5979\u95ee\u4ec0\u4e48\u5c31\u7b54\u4ec0\u4e48\uff0c \u6beb\u4e0d\u607c\u6012\uff0c\u4e5f\u4e0d\u4e71\u5f00\u73a9\u7b11\u3002\u674e\u6cbb\u5012\u6015\u5979\u8bf4\u51fa\u5b98\u5ef7\u79d8\u4e8b\uff0c\u6cc4\u9732\u4e86\u94a6\u72af\u8eab\u4efd\uff0c\u4e0d\u65f6\u7528\u8bf4\u8bdd\u6253\u65ad\u5979 \u4eec\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u90a3\u5c3c\u59d1\u4ecd\u7136\u5728\u9017\u51af\u7433\u8bf4\u8bdd\uff0c\u7d6e\u7d6e\u4e0d\u4f11\u3002\u674e\u6cbb\u4e0d\u5ba2\u6c14\u7684\u9053\uff1a\u201c\u6211\u4eec\u8d70\u4e86\u4e00\u5929\uff0c \u8179\u4e2d\u9965\u6e34\uff0c\u53ef\u80af\u89c1\u8d50\u4e00\u4e9b\u658b\u996d\u5417\uff1f\u201d\u90a3\u5c3c\u59d1\u970d\u7136\u9192\u8d77\uff0c\u9053\uff1a\u201c\u8bf7\u5c45\u58eb\u6055\u7f6a\uff0c\u6211\u6020\u6162\u8d35\u5ba2 \u4e86\u3002\u201d\u8fdb\u5165\u9999\u79ef\u53a8\u4e2d\u3002 \u3000\u3000\u5c3c\u59d1\u8d70\u5f00\uff0c\u674e\u6cbb\u8d76\u5fd9\u5728\u51af\u7433\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u4f60\u53ef\u4e0d\u80fd\u4e71\u8bf4\u8bdd\u5440\uff0c\u8bb0\u7740\uff0c\u7edd\u4e0d\u53ef\u5c06\u4f60\u5728\u56db\u7687 \u5e9c\u4e2d\u4f4f\u8fc7\u4e4b\u4e8b\u8bf4\u51fa\u3002\u6b64\u5730\u4e34\u8fd1\u4eac\u57ce\uff0c\u8c01\u77e5\u9053\u8fd9\u5c3c\u59d1\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u51af\u7433\u597d\u50cf\u9887\u4e3a\u53cd\u611f\u3002\u9053\uff1a \u201c\u8fd9\u5c3c\u59d1\u975e\u5e38\u548c\u5584\uff0c\u53c8\u4eb2\u5207\u53c8\u6148\u6837\uff0c\u5c31\u50cf\u6211\u7684\u4eb2\u4eba\u4f3c\u7684\u3002\u201d\u4f46\u89c1\u674e\u6cbb\u9762\u8272\u4e0d\u8c6b\uff0c\u53ea\u597d\u7b11\u9053\uff1a \u201c\u4f60\u653e\u5fc3\uff0c\u6211\u4e0d\u4e71\u8bf4\u4fbf\u662f\u3002\u201d \u3000\u3000\u90a3\u5c3c\u59d1\u53c8\u51fa\u6765\u4e86\uff0c\u624b\u4e0a\u6301\u7740\u534a\u94b5\u658b\u996d\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u5de7\u5f97\u5f88\uff0c\u53ea\u5269\u8fd9\u4e00\u70b9\u513f\u3002\u7c73\u548c\u83dc\u852c\u90fd \u6ca1\u6709\u4e86\uff0c\u8d81\u7740\u5929\u8fd8\u672a\u9ed1\uff0c\u4f60\u80af\u4e3a\u6211\u4e0b\u5c71\u5316\u4e00\u70b9\u7c73\u5417\uff1f\u201d\u8fd9\u771f\u662f\u4e0d\u60c5\u4e4b\u8bf7\uff0c\u4f46\u674e\u6cbb\u4e00\u5411\u8001\u5b9e\uff0c \u5374\u53c8\u60f3\u4e0d\u51fa\u8bdd\u6765\u63a8\u8f9e\uff0c\u51af\u7433\u9053\uff1a\u201c\u4f60\u5feb\u53bb\u5427\uff0c\u4f60\u4e0d\u662f\u548c\u5c1a\uff0c\u4e0d\u5fc5\u5316\u7f18\uff0c\u7528\u94b1\u53bb\u4e70\u597d\u4e86\u3002\u201d\u674e \u6cbb\u9053\uff1a\u201c\u4e0d\u5982\u6211\u4eec\u5230\u5c71\u4e0b\u6295\u5bbf\uff0c\u514d\u5f97\u6253\u6270\u5e08\u592a\u3002\u201d\u90a3\u5c3c\u59d1\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u6211\u559c\u6b22\u4f60\u4eec\u5728\u8fd9\u91cc \u4f4f\u5bbf\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4e5f\u559c\u6b22\u5728\u8fd9\u91cc\u4f4f\u5bbf\u3002\u4f60\u5feb\u53bb\u5427\uff01\u201d \u3000\u3000\u674e\u6cbb\u6ca1\u6cd5\uff0c\u53ea\u597d\u6367\u4e86\u658b\u94b5\u51fa\u95e8\uff0c\u5230\u4e86\u5916\u9762\u5c71\u5934\uff0c\u66ae\u8272\u5df2\u5408\u3002\u674e\u6cbb\u5fc3\u4e2d\u6697\u6697\u57cb\u6028\uff0c\u5929\u5e95\u4e0b \u5c45\u7136\u6709\u8fd9\u79cd\u4e0d\u8fd1\u60c5\u7406\u7684\u5c3c\u59d1\u3002\u6b63\u4e0d\u77e5\u5230\u54ea\u91cc\u8ba8\u7c73\uff0c\u5ffd\u95fb\u5f97\u5c71\u4e0b\u53ae\u6740\u58f0\uff0c\u5176\u4e2d\u4e00\u4eba\u7684\u58f0\u97f3\uff0c\u542c \u5f97\u51fa\u4e43\u662f\u6d77\u4e91\u548c\u5c1a\uff0c\u674e\u6cbb\u53f1\u54a4\u4e00\u58f0\uff0c\u7acb\u523b\u5954\u4e0b\u5c71\u53bb\u3002 \u3000\u3000\u5230\u4e86\u5c71\u4e0b\uff0c\u6d77\u4e91\u548c\u5c1a\u5df2\u7ecf\u9003\u8dd1\uff0c\u53ea\u89c1\u5730\u4e0b\u5c38\u6a2a\u904d\u5730\uff0c\u53ea\u6709\u4e00\u4eba\u8fd8\u5728\u6323\u624e\u8f6c\u52a8\u3002\u674e\u6cbb\u614c\u5fd9 \u8fc7\u53bb\u5c06\u4ed6\u7ffb\u8f6c\uff0c\u90a3\u4eba\u6ee1\u8138\u8840\u6c61\uff0c\u7a81\u7136\u7741\u5927\u4e24\u53ea\u773c\u775b\uff0c\u53eb\u9053\uff1a\u201c\u5440\uff0c\u539f\u6765\u662f\u4f60\uff01\u4f60\u75db\u75db\u5feb\u5feb\u7ed9 \u6211\u4e00\u5200\u5427\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u674e\u6cbb\u5148\u662f\u6115\u7136\uff0c\u518d\u4e00\u60f3\uff0c\u624d\u542c\u51fa\u8fd9\u6b63\u662f\u4eca\u65e5\u8981\u6765\u52ab\u4e66\u4e4b\u4eba\uff0c\u9887\u4e3a\u5185\u759a\uff0c\u9053\uff1a \u201c\u6211\u4e0e\u4f60\u65e0\u51a4\u65e0\u4ec7\uff0c\u6740\u4f60\u505a\u751a\uff01\u201d\u909d\u740f\u9053\uff1a\u201c\u4f60\u4e0d\u6740\u6211\u4e5f\u4e0d\u80fd\u6d3b\u4e86\uff0c\u4e0d\u5982\u4f60\u7ed9\u6211\u4e00\u5200\uff0c\u6211\u8fd8 \u9886\u4f60\u7684\u60c5\u3002\u201d\u674e\u6cbb\u8f7b\u8f7b\u66ff\u4ed6\u63c9\u4e86\u4e24\u628a\uff0c\u9053\uff1a\u201c\u4f60\u522b\u614c\uff0c\u6211\u66ff\u4f60\u6cbb\u3002\u201d\u909d\u740f\u4f3c\u4e4e\u8212\u670d\u4e86\u4e9b\uff0c\u53c8 \u9053\uff1a\u201c\u6211\u5e08\u5f1f\u60f3\u62a2\u4f60\u4eec\u7684\u4e66\uff0c\u4f60\u4eec\u4e0d\u8981\u548c\u4ed6\u4f5c\u5bf9\uff0c\u89c1\u4e86\u4ed6\u65f6\uff0c\u907f\u5f00\u4ed6\u5427\uff01\u201d \u3000\u3000\u674e\u6cbb\u5fc3\u4e2d\u6b63\u5728\u4e3a\u5085\u9752\u4e3b\u9057\u4e66\u4e4b\u4e8b\u4e0d\u5b89\uff0c\u95ee\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u5e08\u5f1f\uff1f\u201d\u909d\u740f\u9053\uff1a\u201c\u5929\u53f0\u6d3e\u7684 \u638c\u95e8\u5f20\u5929\u6c60\u3002\u201d\u8bf4\u8bdd\u592a\u591a\uff0c\u6c14\u529b\u4e0d\u52a0\uff0c\u58f0\u606f\u6e10\u5f31\u3002\u674e\u6cbb\u64e6\u71c3\u706b\u77f3\uff0c\u66ff\u4ed6\u68c0\u89c6\uff0c\u89c1\u53d7\u4f24\u867d\u91cd\uff0c \u4f30\u91cf\u81ea\u5df1\u8fd8\u80fd\u533b\u6cbb\u3002\u4fbf\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u8bf4\u8bdd\u4e86\uff0c\u6211\u80cc\u4f60\u5230\u9644\u8fd1\u5bfa\u9662\u53bb\uff0c\u66ff\u4f60\u533b\u6cbb\u3002\u90a3\u4e66\u6211\u4eec\u90fd \u4e0d\u8be5\u6709\uff0c\u6211\u548c\u4f60\u5e08\u5f1f\u548c\u89e3\u4e86\u5427\u3002\u201d\u909d\u740f\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u5b89\u6170\u6211\u4e86\uff0c\u6211\u80f8\u9aa8\u5df2 \u65ad\uff0c\u53c8\u53d7\u5185\u4f24\uff0c\u7eb5\u6709\u540d\u533b\uff0c\u4e5f\u96be\u533b\u6cbb\u3002\u4f60\u4ee5\u5fb7\u62a5\u6028\uff0c\u786e\u662f\u541b\u5b50\u3002\u4e34\u6b7b\u4e4b\u524d\uff0c\u6211\u8981\u6c42\u4f60\u4e24 \u4e8b\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4f60\u6b7b\u4e0d\u4e86\uff01\u201d\u909d\u740f\u4ecd\u9053\uff1a\u201c\u4f60\u4e0d\u7b54\u5e94\uff0c\u6211\u6b7b\u4e0d\u7791\u76ee\u3002\u201d\u674e\u6cbb\u719f\u8bfb\u533b\u7406\uff0c\u77e5 \u9053\u75c5\u4eba\u82e5\u6709\u4e8b\u90c1\u7ed3\u5728\u5fc3\uff0c\u5c31\u8be5\u8ba9\u4ed6\u8bf4\u51fa\uff0c\u4fbf\u9053\uff1a\u201c\u4f60\u8bf4\u5427\u3002\u201d\u909d\u740f\u9053\uff1a\u201c\u6211\u6b7b\u4e4b\u540e\uff0c\u4f60\u5c06\u6211 \u9057\u4f53\u4ea4\u7ed9\u6211\u7684\u5e08\u5f1f\u3002\u4ed6\u4eca\u665a\u4e0d\u89c1\u6211\u56de\u5c71\uff0c\u5b9a\u4ece\u5c71\u4e0b\u7ecf\u8fc7\uff0c\u4f60\u89c1\u4e86\u4ed6\uff0c\u53eb\u4ed6\u4ece\u901f\u9063\u6563\u4f17\u4eba\uff0c\u9690 \u5c45\u4e86\u5427\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4f60\u53c8\u8bf4\u8981\u6211\u907f\u4ed6\u3002\u201d\u909d\u740f\u9053\uff1a\u201c\u597d\uff0c\u6211\u7ed9\u4f60\u7559\u4e0b\u4e66\u4fe1\u3002\u201d\u4ee5\u6307\u8638\u8840\uff0c \u626f\u4e0b\u8863\u895f\uff0c\u5199\u4e86\u51e0\u5341\u4e2a\u5b57\u8840\u4e66\uff0c\u5199\u5b8c\u4e4b\u540e\uff0c\u6c14\u529b\u5df2\u5c3d\uff0c\u53ea\u8bf4\u4e86\u53e5\uff1a\u201c\u6211\u8fd8\u6709\u4e24\u4e2a\u5916\u5b59\u5973 \u513f\u2026\u2026\u201d\u5c31\u6655\u6b7b\u8fc7\u53bb\u3002 \u3000\u3000\u674e\u6cbb\u614c\u5fd9\u7ed9\u4ed6\u628a\u8109\uff0c\u53ea\u89c1\u8109\u606f\u5448\u5fae\uff0c\u5374\u8fd8\u4e0d\u662f\u6b7b\u8109\uff0c\u4fbf\u6298\u4e86\u677e\u9488\uff0c\u66ff\u4ed6\u523a\u7a74\uff0c\u8ba9\u4ed6\u8840\u6db2 \u6d41\u901a\uff0c\u518d\u53d6\u51fa\u968f\u8eab\u643a\u5e26\u7684\u91d1\u521b\u836f\uff0c\u66ff\u4ed6\u6b62\u8840\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u4f24\u52bf\u751a\u91cd\uff0c\u4e0d\u80fd\u642c\u52a8\u3002\u53d7 \u4e86\u5185\u4f24\uff0c\u6700\u597d\u7684\u6cbb\u7597\u4e43\u662f\u9759\u517b\uff0c\u4ed6\u82e5\u80fd\u5b89\u7720\uff0c\u5bf9\u4ed6\u7684\u75c5\u52bf\u5927\u6709\u5e2e\u52a9\u3002\u201d\u4fbf\u8e72\u4e0b\u6765\u66ff\u4ed6\u63a8\u62ff\uff0c \u4ee4\u4ed6\u795e\u7ecf\u5b81\u9759\uff0c\u6c89\u6c89\u719f\u7761\u3002\u8fc7\u4e86\u597d\u4e45\uff0c\u674e\u6cbb\u677e\u4e86\u53e3\u6c14\uff0c\u624d\u89c9\u81ea\u5df1\u9965\u997f\u5df2\u6781\uff0c\u597d\u5728\u909d\u740f\u7b49\u4eba\u90fd \u5e26\u6709\u7cae\u56ca\u3002\u674e\u6cbb\u80e1\u4e71\u5bfb\u51e0\u4e2a\u80e1\u9ebb\u997c\u56bc\u4e86\u3002\u5403\u9971\u4e86\u809a\u5b50\uff0c\u4e5f\u501a\u6811\u5047\u5bd0\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u7adf\u7761\u7740 \u4e86\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u674e\u6cbb\u5ffd\u88ab\u4eba\u63a8\u9192\uff0c\u7741\u773c\u4e00\u770b\uff0c\u53ea\u542c\u5f97\u51af\u7433\u57cb\u6028\u9053\uff1a\u201c\u5413\u6b7b\u4eba\u4e86\uff0c \u4f60\u600e\u4e48\u4f34\u7740\u6b7b\u4eba\u7761\u89c9\uff1f\u201d\u674e\u6cbb\u8df3\u4e86\u8d77\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u5566\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u5566\uff1f \u5929\u90fd\u5feb\u4eae\u5566\uff01\u6211\u6025\u5f97\u4e0d\u5f97\u4e86\uff0c\u4ee5\u4e3a\u51fa\u4e86\u4ec0\u4e48\u4e8b\u4e86\u3002\u90a3\u4f4d\u5e08\u592a\u4e5f\u5f88\u4e0d\u5b89\uff0c\u672c\u6765\u8981\u966a\u6211\u627e\u4f60\uff0c\u662f \u6211\u89c1\u5979\u4e0d\u4f1a\u6b66\u529f\uff0c\u6015\u53cd\u800c\u4e0d\u4fbf\uff0c\u6240\u4f3c\u5355\u72ec\u4e0b\u5c71\u4e71\u627e\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u6211\u8d70\u4e4b\u540e\uff0c\u5979\u53c8\u548c\u4f60\u8bf4\u4e86 \u4ec0\u4e48\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u5979\u95ee\u6211\u5c0f\u65f6\u4e4b\u4e8b\uff0c\u6211\u5168\u8bb0\u4e0d\u5f97\uff0c\u80fd\u8bf4\u4ec0\u4e48\uff1f\u4e0d\u8fc7\uff0c\u6211\u544a\u8bc9\u5979\u6211\u4f1a\u6b66\u529f\uff0c \u5979\u5f88\u9ad8\u5174\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4f60\u544a\u8bc9\u5979\u8fd9\u4e9b\u505a\u4ec0\u4e48\uff1f\u201d\u51af\u7433\u5658\u7740\u5634\u513f\u9053\uff1a\u201c\u8fd9\u4e5f\u4e0d\u8bb8\u8bf4\u90a3\u4e5f\u4e0d\u8bb8 \u8bf4\uff0c\u672a\u514d\u592a\u6ca1\u9053\u7406\uff0c\u90a3\u5c3c\u59d1\u53c8\u4e0d\u662f\u574f\u4eba\u3002\u201d \u3000\u3000\u674e\u6cbb\u4e0d\u548c\u5979\u4e89\u8fa9\uff0c\u8d77\u8eab\u66ff\u909d\u740f\u628a\u8109\uff0c\u89c1\u4ed6\u8109\u8c61\u9887\u597d\uff0c\u53ef\u4ee5\u80cc\u4ed6\u5230\u5bfa\u9662\u53bb\u6cbb\u4e86\u3002\u7565\u4e00\u8e0c \u8e87\uff0c\u5bf9\u51af\u7433\u9053\uff1a\u201c\u4f60\u66ff\u6211\u5728\u8fd9\u91cc\u529e\u4e00\u4ef6\u4e8b\u3002\u201d\u51af\u7433\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u5c31\u662f \u4eca\u671d\u62a2\u52ab\u6211\u4eec\u7684\u4eba\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u90a3\u4f60\u8fd9\u4e48\u8d39\u5fc3\u66ff\u4ed6\u533b\u6cbb\u505a\u751a\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u6162\u6162\u518d\u8bf4\u7ed9\u4f60 \u542c\u3002\u73b0\u5728\u6211\u8981\u4f60\u542c\u6211\u7684\u8bdd\u3002\u201d\u51af\u7433\u8d4c\u6c14\u9053\uff1a\u201c\u597d\uff0c\u8bf7\u8bf4\uff01\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4f60\u4e0d\u51c6\u80e1\u95f9\uff0c\u53ef\u4e00\u5b9a \u8981\u542c\u6211\u7684\u8bdd\u554a\uff01\u201d\u51af\u7433\u9053\uff1a\u201c\u597d\u5566\uff0c\u4f9d\u4f60\u4fbf\u662f\u3002\u4f60\u8981\u6211\u628a\u5f3a\u76d7\u63a5\u56de\u6765\u5f53\u7239\u7239\u4f9b\u517b\u90fd\u884c\uff01\u201d \u3000\u3000\u674e\u6cbb\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u8d4c\u6c14\u3002\u4e0d\u8fc7\u6211\u5374\u662f\u771f\u8981\u4f60\u5728\u6b64\u7b49\u5019\u4e00\u4e2a\u5927\u5f3a\u76d7\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u5728 \u5f3a\u76d7\u7a9d\u91cc\u957f\u5927\u7684\uff0c\u7b49\u5c31\u7b49\uff0c\u6015\u4ed6\u5403\u4e86\u6211\u4e0d\u6210\u3002\u201d\u674e\u6cbb\u8ba9\u51af\u7433\u770b\u90a3\u8840\u4e66\uff0c\u9053\uff1a\u201c\u90a3\u5f3a\u76d7\u53eb\u5f20\u5929 \u6c60\uff0c\u662f\u8fd9\u4eba\u5e08\u5f1f\uff0c\u4f60\u89c1\u4e86\u4ed6\uff0c\u5e26\u4ed6\u6765\u5bfa\u9662\u89c1\u6211\u3002\u8fd8\u6709\uff0c\u4ed6\u82e5\u5148\u548c\u4f60\u52a8\u624b\uff0c\u4f60\u4e0d\u51c6\u4f24\u4ed6\u3002\u8d76\u5feb \u5bf9\u4ed6\u8bf4\u660e\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u597d\u5566\uff0c\u53c8\u662f\u4f60\u90a3\u5957\u5316\u654c\u4e3a\u53cb\u7684\u9053\u7406\u5566\u3002\u90a3\u5f20\u5929\u6c60\u662f\u4e0d\u662f\u597d\u4eba\u8fd8\u4e0d\u77e5 \u9053\u5462\uff01\u4e0d\u8fc7\uff0c\u4f60\u65e2\u7136\u8981\u4e0e\u4ed6\u4eec\u548c\u89e3\uff0c\u6211\u5e2e\u4f60\u4fbf\u662f\u4e86\u3002\u201d\u674e\u6cbb\u4e00\u7b11\u3002\u80cc\u8d77\u909d\u740f\u4e0a\u5c71\uff0c\u5929\u8272\u5df2\u7ecf \u5927\u767d\u4e86\u3002 \u3000\u3000\u51af\u745b\u53d6\u5f97\u4e86\u90a3\u5305\u836f\uff0c\u4e00\u8def\u5fc3\u60c5\u7d27\u5f20\uff0c\u5c06\u836f\u634f\u5728\u624b\u5fc3\uff0c\u751f\u6015\u9057\u5931\u3002\u5929\u4eae\u4e4b\u65f6\uff0c\u4ece\u516b\u8fbe\u5cad\u4e0b \u7ecf\u8fc7\uff0c\u524d\u9762\u5ffd\u5730\u51b2\u6765\u5341\u4f59\u9a91\u5feb\u9a6c\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u4f24\u6211\u4eec\u5144\u5f1f\u7684\uff0c\u5c31\u662f\u8fd9\u91ce\u4e2b\u5934\uff01\u201d \u3000\u3000\u8fd9\u6279\u4eba\u6b63\u662f\u5f20\u5929\u6c60\u548c\u4ed6\u7684\u515a\u7fbd\uff0c\u5f20\u5929\u6c60\u4e0d\u89c1\u909d\u740f\u56de\u6765\uff0c\u60c5\u77e5\u5fc5\u6709\u610f\u5916\uff0c\u90a3\u53d7\u4f24\u7684\u4e09\u4eba\u53c8 \u6bd2\u53d1\u5c06\u6b7b\uff0c\u53ea\u597d\u5c06\u4f24\u8005\u9a6e\u5728\u9a6c\u80cc\uff0c\u51fa\u6765\u627e\u5bfb\u3002\u521a\u51fa\u5c71\u53e3\uff0c\u5c31\u9047\u89c1\u51af\u745b\u3002\u5f20\u5929\u6c60\u542c\u8bf4\u5979\u5c31\u662f\u51f6 \u624b\uff0c\u4e0d\u89c9\u6012\u4ece\u5fc3\u8d77\uff0c\u628a\u5224\u5b98\u7b14\u4e00\u4eae\uff0c\u7acb\u523b\u51b2\u4e0a\u53bb\u75db\u4e0b\u6740\u624b\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u5927\u51fa\u51af\u745b\u610f\u5916\uff0c\u4e0d\u53ca\u8fa9\u89e3\uff0c\u654c\u4eba\u5df2\u6740\u5230\u8ddf\u524d\uff0c\u51af\u745b\u628a\u5251\u4e00\u64a9\uff0c\u5f20\u5929\u6c60\u6b66\u529f\u751a\u5f3a\uff0c \u6b22\u7b14\u659c\u98de\uff0c\u5de6\u4e00\u7b14\u70b9\u5979\u7684\u201c\u66f2\u6c60\u7a74\u201d\uff0c\u53f3\u4e00\u7b14\u70b9\u5979\u7684\u201c\u7384\u673a\u7a74\u201d\uff0c\u51af\u745b\u8feb\u7740\u8981\u63a5\u654c\u62db\uff0c\u767e\u5fd9 \u4e2d\u7adf\u8bb0\u4e0d\u8d77\u81ea\u5df1\u5de6\u638c\u638c\u5fc3\u634f\u7740\u90a3\u5305\u836f\u7269\uff0c\u53f3\u624b\u77ed\u5251\u4e00\u5c01\uff0c\u62b5\u5fa1\u654c\u4eba\u5175\u5668\uff0c\u5de6\u624b\u4e00\u5f20\uff0c\u9a88\u6307\u8fd8 \u70b9\u654c\u4eba\u7a74\u9053\uff0c\u8fd9\u4e24\u62db\u662f\u62b5\u654c\u4f7f\u5224\u5b98\u7b14\u4e4b\u7c7b\u70b9\u7a74\u5175\u5668\u7684\u8981\u7740\uff0c\u51af\u745b\u4e0d\u7528\u8003\u8651\uff0c\u500f\u5ffd\u4fbf\u8fde\u53d1\u4e24 \u62db\uff0c\u5f20\u5929\u6c60\u51e0\u4e4e\u7ed9\u5979\u70b9\u7740\uff0c\u8fde\u8fde\u540e\u9000\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u51af\u745b\u4e00\u58f0\u9a87\u53eb\uff0c\u90a3\u5305\u836f\u5df2\u6389\u5230\u5730\u4e0a\uff0c\u614c\u5fd9 \u53bb\u62fe\uff0c\u9ad8\u624b\u5bf9\u654c\uff0c\u53ea\u4e89\u77ac\u606f\u4e4b\u95f4\uff0c\u54ea\u5bb9\u5f97\u51af\u745b\u817e\u51fa\u624b\u6765\u3002\u5f20\u5929\u6c60\u6b63\u5728\u5fc3\u5bd2\uff0c\u5ffd\u542c\u51af\u745b\u9a87\u53eb\uff0c \u8fd8\u4ee5\u4e3a\u5979\u4e2d\u4e86\u540c\u4f19\u7684\u6697\u5668\uff0c\u673a\u4e0d\u53ef\u5931\uff0c\u7acb\u523b\u5c55\u7b14\u70b9\u6253\u51af\u745b\u80cc\u5fc3\u3002 \u3000\u3000\u51af\u745b\u53cd\u624b\u4e00\u5251\uff0c\u5954\u4ed6\u53f3\u80a9\uff0c\u60c5\u6025\u53eb\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u4eec\u65e0\u51a4\u65e0\u4ec7\uff0c\u4f60\u8ba9\u6211\u53d6\u836f\u5373\u8d70\uff0c\u6211\u4e0d\u4f24 \u4f60\uff01\u201d\u5f20\u5929\u6c60\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u8fd8\u60f3\u8d70\u5417\uff1f\u201d\u53cc\u7b14\u75be\u70b9\uff0c\u628a\u51af\u745b\u7f20\u5f97\u8131\u4e0d\u4e86\u8eab\u3002\u4ed6\u7684\u624b\u4e0b\u89c1\u51af\u745b \u8fd9\u4e48\u4e00\u56b7\uff0c\u7acb\u523b\u6709\u4eba\u5c06\u90a3\u5305\u836f\u62fe\u8d77\uff0c\u7b11\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u5b9d\u8d35\u7684\u836f\uff1f\u201d\u8fb9\u8bf4\u8fb9\u6495\u7834\u7eb8\u5305\uff0c\u5c06\u90a3\u51e0 \u5473\u836f\u644a\u5728\u624b\u5fc3\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u8fde\u6811\u53f6\u548c\u87cb\u87c0\u90fd\u62ff\u6765\u4f5c\u836f\uff0c\u5403\u8fd9\u836f\u5b9a\u662f\u5973\u5996\uff01\u201d\u968f\u624b\u4e00\u6454\uff0c \u628a\u51af\u745b\u90a3\u5305\u5e72\u8f9b\u4e07\u82e6\u8ba8\u5f97\u6765\u7684\u836f\uff0c\u4e22\u4e0b\u5c71\u6da7\uff0c\u968f\u7740\u6d41\u6c34\u51b2\u4e0b\u5c71\u53bb\uff0c\u65e0\u5f71\u65e0\u8e2a\uff01 \u3000\u3000\u51af\u745b\u5fc3\u75db\u4e4b\u6781\uff0c\u60f3\u8d77\u5510\u6653\u6f9c\u751f\u547d\u7684\u671f\u9650\u5df2\u4e0d\u6ee1\u4e09\u65e5\uff0c\u8fd9\u5305\u836f\u4e0d\u80fd\u518d\u914d\uff0c\u5e9f\u56ed\u8001\u4eba\u53c8\u5df2\u6b7b \u4e86\uff0c\u8fde\u6c42\u4ed6\u518d\u8bbe\u6cd5\u90fd\u4e0d\u53ef\u80fd\uff0c\u771f\u662f\u5df2\u5230\u5b8c\u5168\u7edd\u671b\u4e4b\u5883\uff01\u53ea\u89c9\u773c\u524d\u4e00\u9635\u53d1\u9ed1\uff0c\u51e0\u4e4e\u7ed9\u5f20\u5929\u6c60\u53cc \u7b14\u6240\u4f24\u3002\u65c1\u8fb9\u7684\u55bd\u7f57\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u8fd9\u5996\u5973\u4e0d\u662f\u6211\u4eec\u5be8\u4e3b\u5bf9\u624b\uff0c\u54b1\u4eec\u7b49\u4f1a\u513f\u4e00\u4eba\u65ab\u5979\u4e00\u5200\uff0c \u66ff\u4e09\u4f4d\u5144\u5f1f\u62a5\u4ec7\uff01\u201d \u3000\u3000\u51af\u745b\u5927\u75db\u4e4b\u540e\uff0c\u7ee7\u4ee5\u5927\u6068\uff0c\u53eb\u9053\uff1a\u201c\u4eca\u65e5\u6211\u4e0d\u6740\u4f60\uff0c\u8a93\u4e0d\u4e3a\u4eba\uff01\u201d\u5251\u6cd5\u500f\u53d8\uff0c\u51cc\u5389\u65e0 \u524d\uff0c\u5f20\u5929\u6c60\u6b66\u529f\u867d\u9ad8\uff0c\u600e\u6321\u5f97\u5999\u7ec8\u5929\u4e0b\u7684\u5929\u5c71\u5251\u6cd5\u3002\u4f55\u51b5\u51af\u745b\u53c8\u662f\u8c41\u51fa\u6027\u547d\uff0c\u6240\u4f7f\u7684\u90fd\u662f\u731b \u70c8\u6740\u7740\u3002\u4e09\u4e94\u5341\u62db\u4e00\u8fc7\uff0c\u9669\u8c61\u73af\u751f\u3002\u5f20\u5929\u6c60\u7684\u515a\u7fbd\u89c1\u5251\u5149\u98de\u821e\uff0c\u5bd2\u6c14\u6c81\u808c\uff0c\u4eba\u5f71\u4e0d\u8fa8\uff0c\u90a3\u6562 \u4e0a\u524d\u63d2\u624b\u3002 \u3000\u3000\u5f20\u5929\u6c60\u7edd\u6599\u4e0d\u5230\u51af\u745b\u5982\u6b64\u5389\u5bb3\uff0c\u88ab\u5979\u6740\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u5fc3\u80c6\u7686\u5bd2\u3002\u5ffd\u7136\u542c\u5f97\u624b\u4e0b\u558a\u9053\uff1a \u201c\u53c8\u4e00\u4e2a\u5996\u5973\u6765\u4e86\u3002\u5440\uff0c\u767d\u65e5\u89c1\u9b3c\uff0c\u5feb\u9003\uff0c\u5feb\u9003\uff01\u201d\u5f20\u5929\u6c60\u62fc\u529b\u62db\u67b6\uff0c\u4e0d\u6562\u659c\u89c6\uff0c\u4f46\u542c\u5f97\u4e00 \u4e2a\u5a07\u6ef4\u6ef4\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u4f60\u662f\u5929\u53f0\u6d3e\u7684\u638c\u95e8\u5f20\u5929\u6c60\u5f20\u5be8\u4e3b\u5417\uff1f\u201d\u5f20\u5929\u6c60\u9053\uff1a\u201c\u662f\u5440\uff01\u662f\u90a3\u6761 \u7ebf\u4e0a\u7684\u5973\u82f1\u96c4\u6765\u4e86\uff01\u201d \u3000\u3000\u53ea\u542c\u5f97\u90a3\u58f0\u97f3\u53c8\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u614c\uff0c\u6211\u6765\u5e2e\u4f60\u3002\u201d\u58f0\u5230\u4eba\u5230\uff0c\u4e00\u56e2\u9752\u5149\uff0c\u500f\u7136\u6eda\u5230\u9762\u524d\uff0c \u51af\u745b\u5927\u51c9\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u75db\u4e0b\u6740\u624b\uff0c\u5530\u7684\u4e00\u5251\uff0c\u5c06\u5f20\u5929\u6c60\u7435\u7436\u9aa8\u523a\u7a7f\uff0c\u56de\u5251\u4e00\u6321\uff0c\u4e0d\u89c9\u5446\u4e86\uff0c \u53cc\u5251\u4e00\u4ea4\uff0c\u4e24\u4eba\u90fd\u540c\u58f0\u558a\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u5f20\u5929\u6c60\u75db\u5f7b\u5fc3\u80ba\uff0c\u53f3\u81c2\u5782\u4e0b\uff0c\u4e3e\u4e0d\u8d77\u6765\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u4e24\u4e2a\u5c11\u5973\u9762\u8c8c\u4e00\u6a21\u4e00\u6837\uff0c\u53cc\u5251 \u76f8\u4ea4\uff0c\u5404\u81ea\u51dd\u671b\uff0c\u5413\u5f97\u9b42\u9500\u9b44\u6563\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u89c1\u9b3c\uff0c\u771f\u662f\u89c1\u9b3c\uff01\u201d \u3000\u3000\u540e\u6765\u7684\u4eba\u6b63\u662f\u51af\u7433\uff0c\u5979\u4e5f\u6599\u4e0d\u5230\u65e0\u610f\u4e4b\u95f4\uff0c\u7adf\u7136\u5728\u6b64\u5730\u9047\u7740\u81ea\u5df1\u6240\u8981\u82e6\u5fc3\u5bfb\u89c5\u4e4b\u4eba\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u76f8\u89c1\u8fd8\u7591\u5728\u68a6\u4e2d\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e5d\u56de\u3000\u5386\u52ab\u559c\u56e2\u5706\u3000\u5bb6\u4eba\u805a\u9996\u3000\u5954\u9a70\u56fe\u4e00\u9762\u3000\u7389\u5973\u60ca\u5fc3 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u4e09\u5341\u4e5d\u56de \u5386\u52ab\u559c\u56e2\u5706\u3000\u5bb6\u4eba\u805a\u9996 \u5954\u9a70\u56fe\u4e00\u9762\u3000\u7389\u5973\u60ca\u5fc3 \u3000\u3000\u5f20\u5929\u6c60\u6ca1\u547d\u5954\u9003\uff0c\u51af\u745b\u6068\u4ed6\u6bc1\u4e86\u5510\u6653\u6f9c\u7684\u89e3\u836f\uff0c\u6b63\u662f\u6012\u4e0a\u5fc3\u5934\uff0c\u89c1\u4ed6\u9003\u8d70\uff0c\u987e\u4e0d\u5f97\u5148\u8ba4 \u59d0\u59b9\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u576a\u5982\u98de\u9e70\u6251\u5154\uff0c\u633d\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u5411\u5f20\u5929\u6c60\u80cc\u5fc3\u4fbf\u523a\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u51af\u7433\u53eb\u9053\uff1a\u201c\u5251\u4e0b\u7559\u4eba\uff01\u201d\u51af\u745b\u6014\u4e86\u4e00\u6014\uff0c\u4f46\u89c1\u51af\u7433\u4ea6\u662f\u98de\u63a0\u800c\u6765\uff0c\u51cc\u7a7a\u4e0b\u51fb\u3002 \u51af\u745b\u7684\u5251\u5c16\u521a\u521a\u5410\u51fa\uff0c\u88ab\u5979\u5f80\u4e0b\u4e00\u683c\uff0c\u53ee\u5f53\u4e00\u58f0\uff0c\u53cc\u5251\u8361\u5f00\u3002\u4e24\u59d0\u59b9\u6a2a\u8dc3\u4e09\u6b65\uff0c\u5f20\u5929\u6c60\u53c8\u5f80 \u524d\u8dd1\u3002 \u3000\u3000\u51af\u7433\u56e0\u674e\u6cbb\u8981\u5979\u6551\u4eba\uff0c\u89c1\u51af\u745b\u8eab\u6cd5\u592a\u5feb\uff0c\u4e00\u65f6\u5fc3\u6025\uff0c\u7adf\u7136\u65bd\u5c55\u4ece\u516b\u81c2\u795e\u9b54\u90a3\u91cc\u5b66\u6765\u7684\u730e \u9e70\u6251\u51fb\u7edd\u6280\uff0c\u8fd9\u4e00\u4e0b\uff0c\u867d\u7136\u6551\u51fa\u4e86\u5f20\u5929\u6c60\uff0c\u5374\u4ee4\u51af\u745b\u7591\u5fc3\u5927\u8d77\u3002 \u3000\u3000\u51af\u745b\u66fe\u4e0e\u516b\u81c2\u795e\u9b54\u5728\u6d77\u5c9b\u6570\u5ea6\u6076\u6597\uff0c\u5bf9\u4ed6\u7684\u730e\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u5370\u8c61\u6700\u6df1\u3002\u4e00\u89c1\u51af\u7433\u7684\u8eab\u6cd5 \u6b63\u662f\u90a3\u9b54\u5934\u7684\u5bb6\u6570\uff0c\u4e0d\u89c9\u5446\u4e86\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u5979\u51fa\u624b\u6551\u8fd9\u6076\u8d3c\uff0c\u7528\u7684\u662f\u516b\u81c2\u795e\u9b54\u7684\u6b79\u6bd2\u62db\u6570\uff0c \u96be\u9053\u5979\u662f\u574f\u4eba\u4e00\u515a\uff1f\u4e0d\u89c9\u5fc3\u75db\u5982\u5272\u3002\u7741\u5927\u773c\u775b\uff0c\u77aa\u671b\u51af\u7433\u3002\u8981\u77e5\u51af\u745b\u81ea\u5e7c\u53d7\u6613\u5170\u73e0\u6559\u8bf2\uff0c\u5bf9 \u662f\u975e\u6b63\u90aa\u4e4b\u8fa8\uff0c\u6781\u4e3a\u8ba4\u771f\uff0c\u8fd9\u65f6\u5ffd\u53d1\u89c9\u81ea\u5df1\u82e6\u82e6\u5bfb\u89c5\u7684\u59b9\u59b9\uff0c\u5374\u662f\u574f\u4eba\uff0c\u4e00\u65f6\u95f4\uff0c\u60f6\u60d1\u3001\u60b2 \u75db\u3001\u607c\u6012\u7b49\u7b49\u60c5\u7eea\uff0c\u4ea4\u96c6\u5fc3\u5934\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff01 \u3000\u3000\u504f\u504f\u51af\u7433\u53c8\u662f\u975e\u5e38\u6dd8\u6c14\uff0c\u89c1\u51af\u745b\u6a2a\u7709\u6012\u76ee\u7684\u602a\u6a21\u6837\uff0c\u4e0d\u89c9\u5657\u55e4\u4e00\u7b11\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u5979\u76f8\u8c8c \u548c\u6211\u5982\u6b64\u76f8\u50cf\uff0c\u5374\u4e0d\u77e5\u6b66\u529f\u6bd4\u6211\u5982\u4f55\uff1f\u5b58\u5fc3\u8bd5\u62db\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u662f\u54ea\u91cc\u8dd1\u51fa\u6765\u7684\u91ce\u5973\u90ce\uff0c\u77aa\u773c \u671b\u6211\u505a\u4ec0\u4e48\uff1f\u201d\u53cd\u624b\u4e00\u5251\uff0c\u75be\u523a\u51af\u745b\u7a74\u9053\uff0c\u540c\u65f6\u8db3\u5c16\u4e00\u8d77\uff0c\u8e22\u5979\u817f\u5f2f\u5173\u8282\u3002\u8fd9\u4e24\u62db\uff0c\u4e00\u62db\u662f \u91c7\u81ea\u6d77\u4e91\u548c\u5c1a\u7684\u5357\u5929\u5251\u6cd5\uff0c\u4e00\u62db\u662f\u8463\u5de8\u5ddd\u7684\u7075\u5c71\u6d3e\u5bb6\u6570\uff0c\u7528\u5f97\u5341\u5206\u6b79\u6bd2\u3002\u51af\u745b\u54ea\u77e5\u5979\u662f\u8bd5 \u62db\uff0c\u903c\u5f97\u4ee5\u653b\u4e3a\u5b88\uff0c\u4e00\u4e2a\u201c\u602a\u87d2\u7ffb\u8eab\u201d\uff0c\u5530\u5530\u4e24\u5251\uff0c\u89e3\u4e86\u51af\u7433\u62db\u6570\u3002 \u3000\u3000\u51af\u7433\u7b11\u9053\uff0c\u201c\u597d\u5251\u6cd5\uff01\u201d\u624b\u634f\u5251\u8bc0\uff0c\u5de6\u4e00\u62db\u201c\u5f69\u51e4\u65cb\u7a9d\u201d\uff0c\u53f3\u4e00\u62db\u201c\u4e91\u9f99\u6389\u9996\u201d\u6b3a\u8eab\u76f4 \u8fdb\u3002\u51af\u745b\u559d\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u5982\u6b64\u4e0d\u77e5\u81ea\u7231\uff0c\u4e0e\u5978\u4eba\u4e3a\u4f0d\uff0c\u4e0d\u6015\u8fb1\u6ca1\u4f60\u7684\u7236\u6bcd\u4e48\uff1f\u201d\u51af\u7433\u62db\u6570\u5341 \u5206\u6e9c\u6ed1\uff0c\u6321\u4e86\u51e0\u62db\uff0c\u5f20\u5929\u6c60\u5df2\u8dd1\u51fa\u534a\u91cc\u4e4b\u5730\u4e86\u3002 \u3000\u3000\u51af\u745b\u5927\u4e3a\u751f\u6c14\uff0c\u559d\u9053\uff1a\u201c\u4f60\u518d\u62e6\u6211\uff0c\u6211\u5c31\u8981\u63b4\u4f60\u4e86\uff01\u201d\u51af\u7433\u7b11\u9053\uff1a\u201c\u4f60\u6709\u672c\u4e8b\uff0c\u5c31\u8bd5\u8bd5 \u770b\uff01\u201d\u51af\u745b\u8eab\u5f62\u4e00\u8d77\uff0c\u5251\u950b\u4e00\u98a4\uff0c\u53ea\u89c1\u94f6\u5149\u98de\u6d12\uff0c\u8000\u773c\u751f\u82b1\uff0c\u51af\u7433\u53eb\u58f0\u4e0d\u597d\uff0c\u5251\u5149\u4eba\u5f71\u4e2d\uff0c \u51af\u745b\u4e00\u638c\u63b4\u5230\uff0c\u89c1\u51af\u7433\u95ea\u7f29\u60ca\u53eb\uff0c\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u638c\u950b\u659c\u659c\u63a0\u8fc7\u5979\u7684\u9762\u95e8\uff0c\u51af\u7433\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c \u907f\u4e86\u5f00\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u6253\u4e0d\u7740\u5c31\u6253\u4e0d\u7740\uff01\u201d\u51af\u745b\u9762\u631f\u5bd2\u971c\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8eab\u5f62\u4e00\u4f0f\u5373 \u8d77\uff0c\u5982\u7bad\u79bb\u5f26\uff0c\u53c8\u5411\u5f20\u5929\u6c60\u8ffd\u53bb\uff01 \u3000\u3000\u51af\u7433\u53eb\u9053\uff1a\u201c\u4ed6\u5df2\u4e2d\u5251\u53d7\u4f24\uff0c\u4f60\u4e3a\u4f55\u8fd8\u8981\u6b3a\u8d1f\u4ed6\uff1f\u4f60\u4e0d\u61c2\u6c5f\u6e56\u89c4\u77e9\u5417\uff1f\u201d\u53c8\u518d\u65bd\u5c55\u732b\u9e70 \u6251\u51fb\u4e4b\u6280\uff0c\u51cc\u7a7a\u4e0b\u51fb\uff0c\u4e0e\u51af\u745b\u7ea0\u7f20\uff0c\u4e00\u9762\u53eb\u9053\uff1a\u201c\u5f20\u5be8\u4e3b\uff0c\u4f60\u5f80\u5c71\u4e0a\u9003\uff0c\u5c71\u4e0a\u6709\u4e2a\u5c0f\u5e99\uff0c\u5e99 \u4e2d\u6709\u4eba\u6551\u4f60\u3002\u201d\u5f20\u5929\u6c60\u60ca\u9b42\u7a0d\u5b9a\uff0c\u56de\u5934\u9053\u4e86\u4e00\u58f0\u201c\u591a\u8c22\u3002\u201d\u5fcd\u7740\u75bc\u75db\uff0c\u75be\u8dd1\u4e0a\u5c71\u3002 \u3000\u3000\u51af\u745b\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u91ce\u4e2b\u5934\uff0c\u6211\u975e\u597d\u597d\u6559\u8bad\u4f60\u4e00\u4e0b\u4e0d\u53ef\uff01\u201d\u5c55\u5f00\u5929\u5c71\u5251\u6cd5\uff0c\u8fde\u73af\u75be\u8fdb\uff0c \u4e13\u523a\u51af\u7433\u624b\u8155\uff0c\u60f3\u8981\u903c\u5979\u5f03\u5251\u6c42\u9976\uff0c\u51af\u7433\u817e\u632a\u95ea\u5c55\uff0c\u8fde\u7528\u597d\u51e0\u6d3e\u6b66\u529f\uff0c\u90fd\u53ea\u6709\u62db\u67b6\u7684\u4efd\u513f\u3002 \u3000\u3000\u9163\u6597\u4e2d\u51af\u745b\u559d\u58f0\u201c\u64a4\u5251\uff01\u201d\u5251\u5c16\u4e00\u6311\uff0c\u53c8\u51c6\u53c8\u75be\uff0c\u51af\u7433\u5ffd\u7b11\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\uff01\u201d\u624b\u4e2d\u5251\u5f80 \u5916\u4e00\u5c01\uff0c\u5251\u52bf\u751a\u7f13\uff0c\u51af\u745b\u5374\u89c9\u5f97\u6709\u4e00\u80a1\u52b2\u529b\u53cd\u63a8\u56de\u6765\uff0c\u4e0d\u89c9\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u53ea\u89c1\u51af\u7433\u5251\u6cd5\u53c8 \u53d8\uff0c\u8eab\u8eaf\u5982\u82b1\u679d\u4e71\u98a4\uff0c\u5251\u52bf\u67d4\u4e2d\u5e26\u521a\uff0c\u7adf\u662f\u5185\u5bb6\u7684\u4e0a\u4e58\u5251\u6cd5\u3002 \u3000\u3000\u539f\u6765\u51af\u7433\u7cbe\u7814\u4e86\u5085\u9752\u4e3b\u7684\u65e0\u6781\u5251\u6cd5\uff0c\u5982\u4eca\u521d\u6b21\u62ff\u6765\u4f7f\u7528\uff0c\u65e0\u6781\u5251\u6cd5\u867d\u4ecd\u4e0d\u53ca\u5929\u5c71\u5251\u6cd5\u7684 \u7cbe\u5999\uff0c\u5374\u64c5\u4e8e\u4ee5\u67d4\u514b\u521a\uff0c\u51af\u745b\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u5948\u4f55\u5979\u4e0d\u5f97\u3002 \u3000\u3000\u53c8\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u51af\u745b\u5fc3\u4e2d\u4e00\u6c14\uff0c\u628a\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u5927\u987b\u5f25\u5251\u5f0f\u5c55\u5f00\uff0c\u53ea\u89c1\u4e00\u56e2\u5251\u5149\uff0c\u538b \u5728\u51af\u7433\u5934\u4e0a\uff0c\u6709\u5982\u6cf0\u5c71\u538b\u9876\uff0c\u597d\u4e0d\u96be\u53d7\uff0c\u51af\u7433\u7684\u529f\u529b\u7a76\u6bd4\u51af\u745b\u7a0d\u900a\uff0c\u5251\u52bf\u6e10\u6e10\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u51af\u745b\u6697\u4e2d\u8fd0\u52b2\uff0c\u559d\u9053\uff1a\u201c\u8fd8\u4e0d\u64a4\u5251\u4e48\uff1f\u201d\u5251\u950b\u81ea\u4e0a\u800c\u4e0b\u5c04\uff0c\u5b9d\u5251\u5411\u540e\u4e00\u5f15\uff0c\u53cc\u5251\u76f8\u4ea4\uff0c \u53ee\u5f53\u4e00\u4e0b\uff0c\u51af\u7433\u7a81\u7136\u5411\u540e\u4e00\u9000\uff0c\u53eb\u9053\uff1a\u201c\u597d\u9669\uff01\u201d\u56de\u5934\u626e\u4e86\u4e00\u4e2a\u9b3c\u8138\uff0c\u5411\u5c71\u4e0a\u75be\u8dd1\u3002 \u3000\u3000\u51af\u7433\u8fd9\u4e00\u62db\u4e43\u662f\u65e0\u6781\u5251\u6cd5\u4e2d\u7684\u7cbe\u534e\u6240\u5728\uff0c\u5148\u7528\u67d4\u529b\u6d88\u89e3\u5f3a\u654c\u7684\u6025\u52b2\uff0c\u7136\u540e\u53cd\u653b\uff0c\u4f46\u51af\u7433 \u89c1\u51af\u745b\u5251\u6cd5\u5965\u5999\u65e0\u6bd4\uff0c\u77e5\u9053\u518d\u6253\u4e0b\u53bb\u5fc5\u7136\u843d\u8d25\uff0c\u6240\u4ee5\u4e0d\u6c42\u53cd\u653b\uff0c\u8d81\u52bf\u540e\u9000\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u81ea\u7136 \u66f4\u5bb9\u6613\u8131\u51fa\u51af\u745b\u5251\u5149\u7b3c\u7f69\u7684\u8303\u56f4\u3002 \u3000\u3000\u51af\u745b\u89c1\u7528\u4e86\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u4e5f\u4e0d\u80fd\u593a\u5979\u624b\u4e2d\u5175\u5668\uff0c\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u5979\u6b66\u529f\u5982\u6b64\u4e86 \u5f97\uff0c\u6211\u66f4\u4e0d\u80fd\u8ba9\u5979\u8bef\u5165\u6b67\u9014\uff0c\u52a9\u7ea3\u4e3a\u8650\u3002\u201d\u63d0\u5251\u4fbf\u8ffd\u3002\u51af\u7433\u8f7b\u529f\u867d\u7136\u4e0d\u53ca\u51af\u745b\uff0c\u4f46\u51af\u745b\u8ffd\u5f97 \u8fd1\u65f6\uff0c\u5979\u4fbf\u53cd\u8eab\u4e00\u5251\uff0c\u7528\u65e0\u6781\u5251\u6cd5\u4e2d\u7cbe\u5999\u7684\u9632\u8eab\u62db\u6570\u62b5\u6321\uff0c\u51af\u745b\u5728\u6570\u62db\u4e4b\u5185\uff0c\u65e0\u6cd5\u5c06\u5979\u6253 \u8d25\uff0c\u53ea\u597d\u8854\u5c3e\u7d27\u8ffd\u3002\u8ffd\u4e86\u4e00\u9635\uff0c\u5c71\u4e0a\u7684\u5c0f\u5c3c\u5eb5\u5df2\u7ecf\u5728\u671b\uff0c\u5f53\u5f53\u7684\u949f\u58f0\u968f\u98ce\u98d8\u6765\uff0c\u51af\u7433\u64ae\u5507\u957f \u5578\uff0c\u7528\u610f\u662f\u60f3\u628a\u674e\u6cbb\u5f15\u51fa\uff0c\u53eb\u4ed6\u60ca\u559c\uff0c\u51af\u745b\u5219\u4ee5\u4e3a\u5979\u662f\u62db\u5524\u540c\u515a\uff0c\u66f4\u662f\u7d27\u8ffd\u4e0d\u820d\u3002 \u3000\u3000\u674e\u6cbb\u5c06\u909d\u740f\u80cc\u56de\u5c71\u4e0a\u5c3c\u5eb5\u4e4b\u65f6\uff0c\u5929\u8272\u5df2\u7ecf\u5927\u767d\uff0c\u53ea\u89c1\u90a3\u4e2d\u5e74\u5c3c\u59d1\uff0c\u76d8\u819d\u5750\u5728\u5927\u6bbf\u7684\u84b2\u56e2 \u4e0a\u3002\u674e\u6cbb\u56e0\u6628\u665a\u4e4b\u4e8b\uff0c\u9887\u4e0d\u9ad8\u5174\uff0c\u8bf4\u9053\uff1a\u201c\u6551\u4eba\u4e00\u547d\u80dc\u9020\u4e03\u7ea7\u6d6e\u5c60\uff0c\u5e08\u592a\uff0c\u6055\u6211\u53c8\u6765\u6253\u6405\u4f60 \u4e86\u3002\u201d\u90a3\u5c3c\u59d1\u8d77\u7acb\u8bf4\u9053\uff1a\u201c\u6551\u4eba\u6027\u547d\uff0c\u751a\u662f\u5e94\u8be5\u3002\u90a3\u4f4d\u5c0f\u59d1\u5a18\u5462\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u5979\u7b49\u4e00\u4f4d\u670b \u53cb\uff0c\u8981\u8fdf\u4e9b\u65f6\u5019\u624d\u80fd\u56de\u6765\u3002\u201d \u3000\u3000\u909d\u740f\u8fd9\u65f6\u5df2\u7ecf\u9192\u4e86\uff0c\u5ffd\u542c\u5728\u674e\u6cbb\u80cc\u4e0a\u53eb\u4e86\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u662f\u8c01\u5728\u8bf4\u8bdd\uff1f\u662f\u7ec3\u971e\u5417\uff1f\u201d\u909d \u740f\u7684\u58f0\u97f3\u867d\u7136\u5fae\u5f31\uff0c\u5728\u90a3\u5c3c\u59d1\u542c\u6765\u5374\u5982\u6674\u5929\u9739\u96f3\uff0c\u6025\u5fd9\u8dd1\u4e0a\u524d\u53bb\uff0c\u6276\u4f4f\u909d\u740f\uff0c\u773c\u6cea\u76c8\u7736\uff0c\u597d \u534a\u5929\u624d\u53eb\u51fa\u58f0\u9053\uff1a\u201c\u554a\uff0c\u7239\uff0c\u771f\u7684\u662f\u4f60\u5417\uff1f\u201d\u674e\u6cbb\u6115\u7136\uff0c\u653e\u4e0b\u909d\u740f\uff0c\u6b63\u5f85\u8be2\u95ee\uff0c\u53ea\u89c1\u4e24\u4eba\u5df1 \u62b1\u5728\u4e00\u8d77\uff0c\u909d\u740f\u8eab\u8eaf\u98a4\u6296\uff0c\u5ffd\u7136\u53eb\u4e86\u4e00\u58f0\uff0c\u6655\u5012\u5730\u4e0a\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u5c3c\u59d1\u54ed\u9053\uff1a\u201c\u7239\uff0c\u4f60\u4e0d\u8981\u8d70\u5440\uff01\u201d\u674e\u6cbb\u4e0a\u524d\u66ff\u909d\u740f\u628a\u8109\uff0c\u9053\uff1a\u201c\u4ed6\u662f\u6b22\u559c\u8fc7\u5ea6\uff0c \u4e00\u65f6\u6fc0\u52a8\uff0c\u6240\u4ee5\u6655\u5012\uff0c\u8fd9\u5e76\u4e0d\u788d\u4e8b\u3002\u201d\u90a3\u5c3c\u59d1\u89c1\u909d\u740f\u8863\u88f3\u67d3\u8840\uff0c\u9762\u5982\u91d1\u7eb8\uff0c\u751a\u662f\u62c5\u5fe7\uff0c\u674e\u6cbb \u9053\uff1a\u201c\u5979\u53d7\u4f24\u867d\u91cd\uff0c\u5374\u975e\u6b7b\u75c7\u3002\u6211\u62c5\u4fdd\u4ed6\u4e09\u5929\u4e4b\u540e\uff0c\u4fbf\u80fd\u8d77\u5e8a\uff0c\u4e00\u6708\u4e4b\u540e\uff0c\u5373\u53ef\u5eb7\u590d\uff01\u201d\u90a3 \u5c3c\u59d1\u6b62\u4e86\u54ed\u58f0\uff0c\u5e2e\u674e\u6cbb\u5c06\u909d\u740f\u62ac\u5165\u9759\u5ba4\uff0c\u674e\u6cbb\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u66ff\u4ed6\u63a8\u8840\u8fc7\u5bab\uff0c\u8ba9\u4ed6\u518d\u9759\u9759\u7761 \u4e00\u4e2a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u5c3c\u59d1\u5728\u65c1\u60d9\u6ce3\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u909d\u740f\u9f3e\u58f0\u5927\u8d77\uff0c\u674e\u6cbb\u9053\uff1a\u201c\u54b1\u4eec\u51fa\u53bb\u5427\u3002\u201d\u90a3\u4e2d\u5e74\u5c3c \u59d1\u76ee\u4e2d\u542b\u6cea\uff0c\u5954\u51fa\u5927\u6bbf\uff0c\u5ffd\u7136\u71c3\u70b9\u9999\u70db\uff0c\u5728\u83e9\u8428\u50cf\u524d\uff0c\u5583\u5583\u7977\u544a\u3002\u674e\u6cbb\u7ad9\u5728\u4e00\u65c1\uff0c\u9690\u7ea6\u542c\u5f97 \u5979\u9053\uff1a\u201c\u4fe1\u5973\u909d\u7ec3\u971e\u591a\u8c22\u83e9\u8428\u4fdd\u4f51\uff0c\u8d50\u6211\u7236\u5973\u56e2\u5706\u3002\u6562\u6c42\u83e9\u8428\u518d\u65bd\u4f5b\u529b\uff0c\u4fdd\u4f51\u745b\u513f\u7433\u513f\u4e5f\u5e73 \u5b89\u65e0\u4e8b\uff0c\u65e9\u65e9\u56de\u5230\u6211\u7684\u8eab\u8fb9\u3002\u201d\u674e\u6cbb\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6025\u95ee\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4e24\u4e2a\u5973\u513f\u5417\uff1f\u201d\u8fd9\u65f6\u5c3c\u5eb5 \u5916\u5df2\u4f20\u6765\u53ae\u6740\u4e4b\u58f0\uff0c\u90a3\u5c3c\u59d1\u7f13\u7f13\u8d77\u7acb\uff0c\u649e\u4e86\u51e0\u4e0b\u94dc\u949f\uff0c\u4e00\u6b65\u4e00\u6b65\u8d70\u51fa\u5bfa\u95e8\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u674e\u6cbb \u53ea\u89c9\u5979\u773c\u5149\u4e2d\u5145\u6ee1\u65e0\u9650\u6148\u7231\uff0c\u5c31\u50cf\u81ea\u5df1\u7684\u6bcd\u4eb2\u4e00\u6837\u3002 \u3000\u3000\u674e\u6cbb\u4e5f\u9ed8\u9ed8\u7684\u8ddf\u4e86\u51fa\u53bb\uff0c\u53ae\u6740\u8ffd\u9010\u4e4b\u58f0\uff0c\u9690\u9690\u4ece\u5c71\u8c37\u5916\u9762\u4f20\u6765\uff0c\u674e\u6cbb\u60f3\u9053\uff1a\u201c\u83ab\u975e\u662f\u7433 \u59b9\u9047\u7740\u5f3a\u654c\u4e86\uff1f\u201d\u5f80\u4e0b\u773a\u671b\uff0c\u5ffd\u89c1\u4e00\u4e2a\u8eab\u6750\u9b41\u4f1f\u7684\u6c49\u5b50\uff0c\u80a9\u8863\u67d3\u8840\uff0c\u795e\u60c5\u840e\u987f\uff0c\u8e09\u8e09\u8dc4\u8dc4\u7684 \u5954\u6765\u3002\u674e\u6cbb\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u4eba\u7b54\u9053\uff1a\u201c\u5929\u53f0\u6d3e\u638c\u95e8\u5f20\u5929\u6c60\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4f60\u7684\u8001\u670b\u53cb \u5728\u91cc\u9762\u7b49\u4f60\u3002\u201d\u5c06\u909d\u740f\u6240\u5199\u7684\u8840\u4e66\u9012\u8fc7\uff0c\u5f20\u5929\u6c60\u9762\u8272\u5927\u53d8\uff0c\u95ee\u9053\uff1a\u201c\u909d\u740f\u9047\u96be\u4e86\u5417\uff1f\u4f60\u662f \u8c01\uff1f\u4f60\u4ece\u90a3\u91cc\u5f97\u7684\u8fd9\u5c01\u8840\u4e66\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u909d\u8001\u5148\u751f\u53d7\u4e86\u70b9\u4f24\uff0c\u5e76\u4e0d\u788d\u4e8b\u3002\u6211\u662f\u4ed6\u5429\u5490\u6765\u6551 \u4f60\u7684\u3002\u4f60\u89c1\u7740\u4e00\u4f4d\u5c0f\u59d1\u5a18\u5427\uff1f\u201d\u5f20\u5929\u6c60\u9053\uff1a\u201c\u4e0d\u6b62\u4e00\u4f4d\uff0c\u4e00\u4e2a\u8981\u6551\u6211\uff0c\u4e00\u4e2a\u8981\u6740\u6211\uff0c\u5979\u4eec\u90fd \u662f\u4e00\u6a21\u4e00\u6837\uff01\u201d\u521a\u4e00\u8bf4\u5b8c\uff0c\u5495\u549a\u4e00\u58f0\uff0c\u5c31\u5012\u4e86\u4e0b\u53bb\u3002\u4ed6\u53d7\u4f24\u4e4b\u540e\uff0c\u62fc\u547d\u5954\u9003\uff0c\u5df2\u7ecf\u652f\u6301\u4e0d\u4f4f \u4e86\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u5c3c\u59d1\u4e00\u76f4\u6ca1\u6709\u8bf4\u8bdd\uff0c\u8fd9\u65f6\u5ffd\u5730\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u55ef\uff0c\u4e00\u6a21\u4e00\u6837\uff0c\u5929\u4e0b\u6709\u8fd9\u6837\u5de7\u7684\u4e8b \u60c5\uff01\u201d\u674e\u6cbb\u5fc3\u795e\u52a8\u8361\uff0c\u90a3\u5c3c\u59d1\u53c8\u9053\uff1a\u201c\u55ef\uff0c\u4ed6\u7684\u7435\u7436\u9aa8\u7ed9\u4eba\u523a\u7a7f\u4e86\uff0c\u4f60\u61c2\u5f97\u533b\u9053\uff0c\u5feb\u6551\u6551\u4ed6 \u5427\uff0c\u4ed6\u662f\u6211\u7239\u7239\u7684\u670b\u53cb\uff0c\u4e00\u5b9a\u4e0d\u662f\u574f\u4eba\u3002\u201d\u674e\u6cbb\u53c8\u662f\u4e00\u60ca\uff1a\u54a6\uff0c\u8fd9\u5c3c\u59d1\u4e5f\u4f1a\u6b66\u529f\uff1f\u201d\u8981\u77e5\u7435 \u7436\u9aa8\u4e43\u624b\u81c2\u4e0e\u80a9\u818a\u76f8\u8fde\u7684\u8106\u9aa8\uff0c\u82e5\u7136\u6298\u65ad\uff0c\u4e0d\u65e9\u6551\u6cbb\uff0c\u90a3\u5c31\u591a\u597d\u6b66\u529f\uff0c\u4e5f\u4f1a\u6b8b\u5e9f\u3002\u674e\u6cbb\u9053\uff1a \u201c\u90a3\u4e48\u8bf7\u5e08\u592a\u5728\u8fd9\u91cc\u7b49\u6211\u7684\u90a3\u4f4d\u670b\u53cb\uff0c\u6211\u7ed9\u4ed6\u6025\u6551\u4e4b\u540e\u5c31\u51fa\u6765\u3002\u201d\u90a3\u4e2d\u5e74\u5c45\u59d1\u4ecd\u7136\u773a\u671b\u524d \u65b9\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u5e94\u58f0\u7b54\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u6211\u4f1a\u7b49\u7684\uff0c\u6211\u5df2\u7ecf\u7b49\u4e86\u5341\u516d\u5e74\u5566\uff01\u201d\u90a3\u58f0\u97f3\u5145\u6ee1\u65e0\u9650 \u5e7d\u6028\uff0c\u674e\u6cbb\u609a\u7136\u4e00\u9707\uff0c\u80cc\u5f20\u5929\u6c60\u56de\u5eb5\u5185\u9759\u5ba4\uff0c\u65e2\u611f\u5947\u5f02\uff0c\u4ea6\u611f\u60f6\u60d1\uff0c\u6599\u77e5\u5fc5\u6709\u975e\u5e38\u610f\u5916\u4e4b \u4e8b\uff0c\u4fbf\u5c06\u53d1\u751f\u3002 \u3000\u3000\u51af\u745b\u4e00\u8def\u8ffd\u9010\u51af\u7433\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u5df2\u8ffd\u5230\u5c3c\u5eb5\u5916\u9762\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u5341\u5206\u4e25\u5389\u5374\u53c8\u4f3c\u5341\u5206 \u6148\u7231\u7684\u58f0\u97f3\u65a5\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u8fd9\u58f0\u9752\u4f3c\u4e4e\u6709\u4e0d\u53ef\u6297\u62d2\u7684\u529b\u91cf\uff0c\u4e24\u59d0\u59b9\u90fd\u4e0d\u7ea6\u800c\u540c\u7684\u505c\u4e0b\u624b\u6765\uff0c\u5446\u7136\u6ce8\u89c6\uff0c\u53ea\u89c1\u5c3c\u5eb5\u5916 \u7acb\u7740\u90a3\u4e2d\u5e74\u5c3c\u59d1\uff0c\u76ee\u4e2d\u8574\u7740\u6cea\u5149\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u6447\u5934\u8bf4\u9053\uff1a\u201c\u9aa8\u8089\u76f8\u9022\uff0c\u4e5f\u4e0d\u77e5\u9053\uff0c\u81ea\u76f8\u6b8b \u6740\uff0c\u5c82\u4e0d\u53ef\u601c\uff01\u201d \u3000\u3000\u8fd9\u4e2d\u5e74\u5c3c\u59d1\u6b63\u662f\u4e24\u59d0\u59b9\u7684\u751f\u8eab\u4e4b\u6bcd\u909d\u7ec3\u971e\uff0c\u5979\u521d\u89c1\u51af\u7433\u4e4b\u65f6\uff0c\u5df2\u7591\u5fc3\u5979\u662f\u81ea\u5df1\u7684\u5973\u513f\uff0c \u4f46\u89c1\u59d3\u540d\u4e0d\u540c\uff0c\u4e0d\u6562\u76f8\u8ba4\u3002\u5982\u4eca\u89c1\u5979\u4eec\u4e00\u6a21\u4e00\u6837\uff0c\u6599\u60f3\u4eba\u95f4\u4e0a\u9664\u4e86\u81ea\u5df1\u8fd9\u5bf9\u5b6a\u751f\u5973\u513f\uff0c\u518d\u65e0 \u5982\u6b64\u76f8\u4f3c\u4e4b\u4eba\u3002 \u3000\u3000\u51af\u745b\u51af\u7433\u90fd\u89c9\u5fc3\u7075\u9707\u8361\uff0c\u51af\u745b\u62fe\u5934\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u600e\u4e48\u77e5\u9053\u5979\u662f\u6211\u7684\u59b9\u59b9\uff1f\u201d\u51af\u7433 \u4e5f\u53eb\u9053\uff1a\u201c\u5e08\u592a\uff0c\u4f60\u77e5\u9053\u6211\u7684\u6765\u5386\u5417\uff1f\u6628\u665a\u4f60\u4e3a\u6cb3\u4e0d\u8bf4\uff1f\u5979\u771f\u7684\u662f\u6211\u7684\u59d0\u59d0\uff1f\u201d\u909d\u7ec3\u971e\u53c8\u662f \u6b22\u559c\uff0c\u53c8\u662f\u8f9b\u9178\uff0c\u5ffd\u5730\u8dd1\u4e0a\u524d\u53bb\uff0c\u5de6\u624b\u62c9\u7740\u51af\u745b\uff0c\u53f3\u624b\u62c9\u7740\u51af\u7433\uff0c\u7aef\u8be6\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u542b\u7b11 \u9053\uff1a\u201c\u4f60\u4eec\u4e24\u90fd\u7b11\u4e00\u7b11\u7ed9\u6211\u770b\uff0c\u8ba9\u6211\u770b\u4f60\u4eec\u8c01\u662f\u59d0\u59d0\uff0c\u8c01\u662f\u59b9\u59b9\u3002\u201d \u3000\u3000\u51af\u745b\u5446\u5446\u671b\u7740\u6bcd\u4eb2\uff0c\u6025\u5207\u95f4\u7b11\u4e0d\u51fa\u6765\uff0c\u51af\u7433\u5374\u5657\u55e4\u7b11\u4e86\u4e00\u58f0\uff0c\u53c8\u7a81\u7136\u4f38\u624b\u5728\u51af\u745b\u814b\u7a9d\u4e00 \u6293\uff0c\u9053\uff1a\u201c\u5e08\u592a\u53eb\u4f60\u7b11\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7b11\uff1f\u201d\u51af\u745b\u9178\u75d2\u96be\u5f53\uff0c\u4e0d\u89c9\u683c\u683c\u5931\u7b11\u3002\u53ea\u542c\u5f97\u90a3\u4e2d\u5e74\u5c3c \u59d1\u9053\uff1a\u201c\u7433\u513f\uff0c\u4e0d\u8bb8\u987d\u76ae\uff0c\u4f60\u662f\u59b9\u59b9\uff0c\u4ee5\u540e\u5e94\u8be5\u542c\u4f60\u59d0\u59d0\u7684\u6559\u5bfc\uff01\u201d\u51af\u745b\u51af\u7433\u90fd\u662f\u806a\u660e\u900f\u9876 \u7684\u59d1\u5a18\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5988\u5988\uff01\u201d\u4e09\u4e2a\u4eba\u62e5\u4f5c\u4e00\u56e2\uff0c\u516d\u884c\u6cea\u73e0\u5728\u7b11\u58f0\u4e2d \u7c41\u7c41\u843d\u4e0b\u3002 \u3000\u3000\u6bcd\u5973\u76f8\u9022\uff0c\u604d\u5982\u9694\u4e16\uff0c\u909d\u7ec3\u971e\u53c8\u54ed\u53c8\u7b3a\uff0c\u6402\u7740\u4e24\u4e2a\u5973\u513f\uff0c\u7d27\u8d34\u80f8\u524d\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6 \u5019\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\uff1a\u201c\u745b\u59b9\uff01\u201d\u674e\u6cbb\u4ece\u5c3c\u5eb5\u91cc\u8d70\u51fa\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u53c8\u662f\u6b22\u559c\u53c8\u662f\u9519\u6115\u3002\u909d\u7ec3\u971e\u7b11 \u9053\uff1a\u201c\u4f60\u6765\u89c1\u89c1\u6211\u8fd9\u4e24\u4f4d\u5973\u513f\uff0c\u591a\u8c22\u4f60\u6551\u4e86\u6211\u7684\u7236\u4eb2\uff0c\u53c8\u5c06\u7433\u513f\u5e26\u6765\uff0c\u8ba9\u6211\u4eec\u4e00\u5bb6\u56e2\u5706\u3002\u201d \u3000\u3000\u51af\u745b\u51af\u7433\u62ed\u4e86\u773c\u6cea\uff0c\u5404\u81ea\u53eb\u9053\uff1a\u201c\u674e\u54e5\u54e5\uff01\u201d\u62a2\u4e0a\u51e0\u6b65\uff0c\u53c8\u662f\u4e0d\u7ea6\u800c\u540c\u7684\u53cc\u53cc\u7ad9\u4f4f\u3002\u674e \u6cbb\u773c\u82b1\u7f2d\u4e71\uff0c\u4e00\u65f6\u95f4\u5206\u4e0d\u51fa\u8c01\u662f\u59d0\u59d0\uff0c\u8c01\u662f\u59b9\u59b9\uff0c\u6b63\u5728\u601d\u7d22\u51af\u7433\u4eca\u65e9\u7a7f\u7684\u662f\u4ec0\u4e48\u8863\u88f3\u3002\u909d\u7ec3 \u971e\u9053\uff1a\u201c\u745b\u513f\u7433\u513f\uff0c\u4f60\u4eec\u518d\u7b11\u4e00\u7b11\u3002\u201d\u8fd9\u56de\u51af\u745b\u51af\u7433\u90fd\u7b11\u4e86\uff0c\u909d\u7ec3\u971e\u6307\u7740\u5979\u4eec\u9053\uff1a\u201c\u4f60\u77a7\uff0c \u5979\u4eec\u957f\u5f97\u4e00\u6a21\u4e00\u6837\uff0c\u5c0f\u65f6\u5019\u6709\u65f6\u8fde\u6211\u4e5f\u5206\u4e0d\u51fa\u6765\u3002\u4e0d\u8fc7\u5979\u4eec\u7b11\u65f6\u90fd\u6709\u4e00\u4e2a\u9152\u6da1\uff0c\u59d0\u59d0\u7684\u9152\u6da1 \u5728\u5de6\u8fb9\u8138\u4e0a\uff0c\u59b9\u59b9\u7684\u9152\u6da1\u5728\u53f3\u8fb9\u8138\u4e0a\uff0c\u4f60\u77a7\u6e05\u695a\uff0c\u4ee5\u540e\u5c31\u4e0d\u4f1a\u8ba4\u9519\u4eba\u4e86\u3002\u201d \u3000\u3000\u51af\u7433\u53c8\u662f\u683c\u683c\u8f7b\u7b11\uff0c\u62a2\u4e0a\u524d\u53bb\uff0c\u62c9\u674e\u6cbb\u7684\u624b\uff0c\u9053\uff1a\u201c\u5988\uff0c\u518d\u8fc7\u4e9b\u65f6\u5019\uff0c\u4f60\u5c31\u5206\u522b\u5f97\u51fa\u6765 \u4e86\u3002\u6211\u6bd4\u59d0\u59d0\u987d\u76ae\u6dd8\u6c14\u5f97\u591a\u5462\uff01\u201d\u909d\u7ec3\u971e\u60f3\u8d77\u5979\u4eec\u5c0f\u65f6\u201c\u6293\u5468\u201d\u4e4b\u4e8b\uff0c\u6027\u683c\u4e4b\u522b\u5728\u90a3\u65f6\u5df2\u6709 \u7aef\u502a\uff0c\u5374\u7b11\u9053\u201c\u4f60\u4eec\u4ee5\u524d\u89c1\u8fc7\u9762\u5417\uff1f\u4f60\u600e\u4e48\u77e5\u9053\u59d0\u59d0\u7684\u6027\u60c5\uff1f\u201d\u51af\u7433\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u505a\u4e2a\u9b3c\u8138 \u9053\uff1a\u201c\u5988\uff0c\u4f60\u4e0d\u77e5\u59d0\u59d0\u591a\u51f6\uff0c\u5979\u4eca\u65e5\u7b2c\u4e00\u6b21\u89c1\u6211\u5c31\u8981\u6559\u8bad\u6211\u5462\uff01\u674e\u54e5\u54e5\uff0c\u4f60\u8d76\u5feb\u5bf9\u59d0\u59d0\u8bf4\uff0c \u90a3\u4e2a\u5f20\u5be8\u4e3b\u662f\u4f60\u53eb\u6211\u6551\u7684\uff0c\u5979\u9a82\u6211\u7ed3\u4ea4\u5978\u4eba\uff0c\u8981\u6253\u6211\u5462\uff01\u201d \u3000\u3000\u51af\u745b\u89c1\u59b9\u59b9\u548c\u674e\u6cbb\u4eb2\u70ed\u7684\u6837\u513f\uff0c\u5fc3\u6709\u6240\u89e6\uff0c\u4e0d\u89c9\u60f3\u9053\uff1a\u201c\u770b\u8fd9\u6837\u5b50\uff0c\u4ed6\u4eec\u5b9a\u662f\u7231\u4fa3\u65e0 \u7591\u3002\u674e\u6cbb\u5929\u6027\u7eaf\u539a\uff0c\u59b9\u59b9\u7ec8\u8eab\u6709\u6258\u3002\u53ef\u662f\u6211\u5374\u4e0d\u77e5\u4eca\u540e\u5982\u4f55\uff1f\u201d\u53c8\u542c\u51af\u7433\u63d0\u8d77\u90a3\u4e2a\u4e2a\u4ec0\u4e48\u5f20 \u5be8\u4e3b\uff0c\u6b63\u662f\u90a3\u4eba\u5c06\u81ea\u5df1\u4e07\u82e6\u5343\u8f9b\u6c42\u5f97\u7684\u89e3\u836f\u5f04\u6bc1\uff0c\u60f3\u8d77\u5510\u6653\u6f9c\u547d\u5728\u987b\u81fe\uff0c\u8d8a\u53d1\u611f\u4f24\uff0c\u7981\u4e0d\u4f4f \u6cea\u5982\u96e8\u4e0b\u3002 \u3000\u3000\u674e\u6cbb\u89c1\u6b64\u60c5\u666f\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u745b\u59b9\u548c\u6211\u662f\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\uff0c\u867d\u65e0\u76df\u8a93\uff0c\u4f46\u5973\u513f\u5bb6\u7684 \u5fc3\u4e8b\u5374\u662f\u96be\u6599\u3002\u6211\u4e0b\u5c71\u4e4b\u540e\uff0c\u4e0d\u5230\u4e09\u5e74\uff0c\u4fbf\u7231\u4e0a\u4e86\u522b\u4eba\u3002\u83ab\u975e\u5979\u56e0\u6b64\u800c\u602a\u6211\u4e48\uff1f\u201d\u601d\u5982\u6f6e \u6d8c\uff0c\u6014\u6014\u5730\u5446\u671b\u51af\u745b\u3002 \u3000\u3000\u909d\u7ec3\u971e\u548c\u51af\u7433\u4e5f\u662f\u60ca\u6115\u4e0d\u5df2\uff0c\u51af\u7433\u5fc3\u9053\uff1a\u201c\u59d0\u59d0\u5440\uff0c\u4f60\u82e5\u662f\u60f3\u8981\u4ed6\uff0c\u5c31\u660e\u8bf4\u4e86\u5427\uff0c\u54ed\u4ec0 \u4e48\u5462\uff1f\u201d\u5fc3\u4e2d\u76d8\u7b97\uff0c\u82e5\u7136\u4ed6\u4eec\u4e8c\u4eba\u771f\u662f\u53e6\u6709\u513f\u5973\u4e4b\u60c5\uff0c\u5c31\u5c06\u674e\u6cbb\u8ba9\u4e0e\u59d0\u59d0\uff0c\u60f3\u662f\u8fd9\u6837\u60f3\u4e86\uff0c \u5fc3\u4e2d\u9690\u9690\u60b2\u9178\u3002 \u3000\u3000\u51af\u745b\u4e00\u8bd5\u773c\u6cea\uff0c\u9053\uff1a\u201c\u674e\u54e5\u54e5\uff0c\u90a3\u4e2a\u5f20\u5be8\u4e3b\u662f\u4ec0\u4e48\u4eba\uff1f\u4f60\u4e3a\u4f55\u8981\u5e87\u62a4\u4e8e\u4ed6\uff1f\u53eb\u4ed6\u51fa\u6765\uff0c \u6211\u4e0d\u628a\u4ed6\u53cc\u624b\u65a9\u6389\uff0c\u96be\u6d88\u5fc3\u5934\u4e4b\u6068\uff01\u201d \u3000\u3000\u674e\u6cbb\u9a87\u9053\uff1a\u201c\u4f60\u548c\u5f20\u5929\u6c60\u6709\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\uff0c\u5982\u6b64\u6068\u4ed6\uff1f\u4ed6\u662f\u5929\u53f0\u6d3e\u7684\u638c\u95e8\uff0c\u867d\u65e0\u5927\u5584\uff0c \u4ea6\u65e0\u5927\u6076\uff0c\u800c\u4e14\u4ed6\u53c8\u662f\u4f60\u5916\u7956\u7236\u7684\u597d\u670b\u53cb\uff0c\u6709\u4ec0\u4e48\u4ec7\u6068\uff0c\u4e5f\u8be5\u770b\u5728\u4f60\u5916\u7956\u7236\u7684\u4efd\u4e0a\uff0c\u9976\u6055\u4e86 \u4ed6\uff01\u201d \u3000\u3000\u51af\u745b\u53c8\u662f\u4e00\u5f81\uff0c\u909d\u7ec3\u971e\u9053\uff1a\u201c\u513f\u5440\uff0c\u4ed6\u8bf4\u5f97\u4e0d\u9519\u3002\u4f60\u4eec\u7684\u5916\u7956\u7236\u4e5f\u5728\u91cc\u9762\u517b\u4f24\uff0c\u7b49\u4f1a\u513f \u4f60\u4eec\u90fd\u53bb\u62dc\u89c1\u4ed6\u5427\u3002\u201d\u5979\u5374\u6ca1\u6709\u60f3\u5230\uff0c\u4e24\u4e2a\u5973\u513f\u4e0d\u4f46\u90fd\u89c1\u8fc7\u5916\u7956\u7236\uff0c\u800c\u4e14\u8fd8\u90fd\u4e0e\u5916\u7956\u7236\u4ea4\u8fc7 \u624b\u4e86\u3002 \u3000\u3000\u51af\u745b\u542c\u4e86\u6b64\u8a00\uff0c\u53c8\u662f\u6cea\u5982\u96e8\u4e0b\uff0c\u909d\u7ec3\u971e\u9053\uff1a\u201c\u745b\u513f\uff0c\u4f60\u5230\u5e95\u6709\u4ec0\u4e48\u51a4\u5c48\u4e4b\u4e8b\uff1f\u201d\u51af\u745b \u9053\uff1a\u201c\u8fd9\u4eba\u4e0d\u662f\u597d\u4eba\uff0c\u4ed6\u628a\u6211\u7684\u89e3\u836f\u6bc1\u4e86\u3002\u201d\u909d\u7ec3\u971e\u9053\uff1a\u201c\u4ec0\u4e48\u89e3\u836f\u5440\uff1f\u201d\u51af\u745b\u54fd\u54bd\u9053\uff1a \u201c\u6211\u8981\u53bb\u6551\u4e00\u4f4d\u597d\u670b\u53cb\u7684\uff0c\u90a3\u4e2a\u4ec0\u4e48\u5f20\u5929\u6c60\u5374\u6ca1\u6765\u7531\u7684\u548c\u6211\u52a8\u624b\uff0c\u5c06\u89e3\u836f\u629b\u4e0b\u5c71\u6da7\uff0c\u6c38\u8fdc\u4e5f \u627e\u4e0d\u56de\u6765\u4e86\u3002\u201d\u51af\u7433\u5374\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u90a3\u597d\u670b\u53cb\u662f\u7537\u7684\u8fd8\u662f\u5973\u7684\uff1f\u201d\u51af\u745b\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a \u201c\u674e\u6cbb\u54e5\u54e5\uff0c\u8fd9\u4eba\u548c\u4f60\u4e5f\u5f88\u719f\u7684\u3002\u4f60\u8fd8\u8bb0\u5f97\u6211\u7684\u5510\u53d4\u53d4\u5417\uff1f\u4ed6\u5728\u5929\u5c71\u4e0a\u4f4f\u4e86\u4e09\u5e74\u3002\u201d\u674e\u6cbb \u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u662f\u5510\u6653\u6f9c\uff01\u201d\u89c1\u51af\u745b\u7740\u6025\u7684\u60c5\u5f62\uff0c\u4e0d\u4f3c\u4ec5\u4ec5\u662f\u53d4\u4f84\u4e4b\u95f4\u7684\u5173\u6000\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c \u53c8\u6697\u6697\u8d23\u5907\u81ea\u5df1\u80e1\u4e71\u731c\u7591\uff0c\u751a\u662f\u60ed\u6127\u3002 \u3000\u3000\u51af\u7433\u4e5f\u4e0d\u89c9\u7b11\u51fa\u58f0\u6765\uff0c\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u53c8\u7d2f\u4f60\u66ff\u6211\u53d7\u8fc7\u4e86\uff0c\u90a3\u5f20\u5929\u6c60\u672c\u6765\u662f\u8981\u627e\u6211\u52a8\u624b\uff0c \u56e0\u4e3a\u4ed6\u7684\u624b\u4e0b\u60f3\u62a2\u6211\u4eec\u7684\u4e00\u672c\u4e66\uff0c\u88ab\u6211\u7528\u98de\u5200\u4f24\u4e86\u4e09\u4eba\uff0c\u6240\u4ee5\u4ed6\u8981\u627e\u6211\u6666\u6c14\u3002\u201d\u51af\u745b\u8be7\u9053\uff1a \u201c\u90a3\u4e48\u4f60\u4eec\u5374\u53c8\u6551\u4ed6\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u51a4\u5bb6\u5b9c\u89e3\u4e0d\u5b9c\u7ed3\uff0c\u5176\u5b9e\u90a3\u4e66\u4e5f\u4e0d\u662f\u6211\u4eec\u7684\uff0c\u65e0\u4e3b\u4e4b\u7269\uff0c \u4e5f\u602a\u4e0d\u5f97\u4ed6\u4eec\u51ef\u89ce\u3002\u201d \u3000\u3000\u51af\u745b\u8fd9\u65f6\u706b\u6c14\u5df2\u6e10\u6e10\u6d88\u4e0b\uff0c\u60f3\u8d77\u5510\u6653\u6f9c\u6027\u547d\u96be\u4fdd\uff0c\u8d8a\u53d1\u4f24\u5fc3\u3002\u674e\u6cbb\u9053\uff1a\u201c\u5510\u5927\u54e5\u6709\u4ec0\u4e48 \u75c5\uff0c\u662f\u4e2d\u4e86\u4ec0\u4e48\u6709\u6bd2\u7684\u6697\u5668\u5417\uff1f\u4f60\u8bf4\u7ed9\u6211\u542c\uff0c\u8bf4\u4e0d\u5b9a\u6211\u80fd\u533b\u6cbb\u3002\u201d\u674e\u6cbb\u548c\u5510\u6653\u6f9c\u3001\u51af\u745b\u90fd\u662f \u5e73\u8f88\uff0c\u4f46\u51af\u745b\u56e0\u5510\u6653\u6f9c\u66fe\u8ddf\u5979\u7956\u7236\u4e60\u6280\uff0c\u6240\u4ee5\u79f0\u4ed6\u201c\u53d4\u53d4\u201d\uff0c\u800c\u674e\u6cbb\u5219\u79f0\u4ed6\u201c\u5927\u54e5\u201d\u3002 \u3000\u3000\u51af\u7433\u63d2\u53e3\u9053\uff1a\u201c\u521a\u624d\u6211\u8bf4\u7684\u90a3\u672c\u4e66\uff0c\u5c31\u662f\u5085\u9752\u4e3b\u7684\u9057\u4e66\uff0c\u542c\u8bf4\u662f\u533b\u5b66\u7684\u4e0d\u4f20\u4e4b\u79d8\u3002\u201d\u51af \u745b\u4e00\u542c\uff0c\u5e0c\u671b\u53c8\u751f\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5feb\u53bb\u770b\u770b\u4ed6\u5427\uff0c\u54ce\uff0c\u53ea\u662f\u90a3\u79cb\u5929\u7684\u68a7\u6850\u53f6\u548c\u96cc\u96c4\u87cb\u8749\u5374\u5230 \u54ea\u91cc\u53bb\u627e\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4e3a\u4f55\u5b9a\u8981\u90a3\u4e24\u5473\u836f\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u90a3\u662f\u5e9f\u56ed\u8001\u4eba\u5f00\u7684\u65b9\u5b50\uff0c\u5e9f\u56ed\u8001 \u4eba\u662f\u5085\u9752\u4e3b\u7684\u5f92\u5f1f\uff0c\u4ed6\u5c31\u8981\u7528\u8fd9\u4e9b\u836f\u624d\u80fd\u89c1\u6548\u3002\u201d \u3000\u3000\u674e\u6cbb\u9053\uff1a\u201c\u54e6\uff0c\u5e9f\u56ed\u8001\u4eba\u3002\u662f\u4e0d\u662f\u53eb\u505a\u53f6\u5bff\u5e38\u7684\uff1f\u5085\u9752\u4e3b\u7684\u4e66\u4e0a\u66fe\u63d0\u8fc7\u4ed6\u7684\u540d\u5b57\uff0c\u8bf4\u53f6 \u5bff\u5e38\u522b\u53f7\u5e9f\u56ed\uff0c\u4e43\u4ed6\u5bc4\u540d\u5f1f\u5b50\uff0c\u4e66\u91cc\u8fd8\u6709\u4e00\u5b97\u4ed6\u4eec\u4e8c\u4eba\u5408\u8bca\u7684\u533b\u6848\uff0c\u60f3\u6765\u5df2\u662f\u4e94\u5341\u5e74\u524d\u4e4b\u4e8b \u4e86\u3002\u745b\u59b9\uff0c\u533b\u9053\u4e0d\u62d8\u4e00\u683c\uff0c\u4f60\u8fd8\u662f\u628a\u5510\u5927\u54e5\u7684\u75c7\u72b6\u5bf9\u6211\u8bf4\u4e00\u8bf4\u5427\u3002\u201d \u3000\u3000\u51af\u745b\u5c06\u5510\u6653\u6f9c\u8bef\u996e\u6bd2\u9152\u548c\u8fd1\u65e5\u7684\u75c7\u72b6\u8be6\u7ec6\u8bf4\u4e86\uff0c\u674e\u6cbb\u7709\u5934\u4e00\u76b1\uff0c\u81ea\u89c9\u6beb\u65e0\u628a\u63e1\u3002 \u3000\u3000\u8981\u77e5\u674e\u6cbb\u53ea\u662f\u719f\u4e60\u533b\u4e66\uff0c\u7cbe\u901a\u533b\u7406\uff0c\u5374\u6beb\u65e0\u4e34\u5e8a\u7ecf\u9a8c\uff0c\u50cf\u5510\u6653\u6f9c\u8fd9\u79cd\u602a\u75c7\uff0c\u975e\u4f46\u533b\u4e66\u4e0a \u4ece\u65e0\u8bb0\u8f7d\uff0c\u533b\u7406\u4e0a\u4ea6\u60f3\u4e0d\u901a\u3002\u4f46\u4e3a\u4e86\u5b89\u6170\u51af\u745b\uff0c\u4ecd\u5f3a\u7b11\u9053\uff1a\u201c\u6211\u660e\u65e9\u5c31\u548c\u4f60\u540c\u53bb\u66ff\u4ed6\u8bca\u6cbb\u4e86 \u4fbf\u662f\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u80fd\u53bb\u5417\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4f55\u987b\u5982\u6b64\u4e4b\u6025\uff1f\u201d\u51af\u745b\u5782\u6cea\u9053\uff1a\u201c\u4f60\u4e0d\u77e5 \u9053\uff0c\u660e\u65e5\u5348\u65f6\uff0c\u518d\u4e0d\u6551\u6cbb\uff0c\u4fbf\u662f\u51c6\u6b7b\u65e0\u7591\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4f60\u4eec\u4f4f\u7684\u5730\u65b9\u79bb\u8fd9\u91cc\u591a\u8fdc\uff1f\u201d\u51af\u745b \u9053\uff1a\u201c\u7ea6\u83ab\u6709\u4e8c\u767e\u91cc\u5427\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u90a3\u4e48\u4eca\u665a\u4e09\u66f4\u6211\u4fbf\u548c\u4f60\u52a8\u8eab\uff0c\u60f3\u6765\u660e\u65e5\u5348\u65f6\u4e4b\u524d\u5b9a\u80fd \u8d76\u5230\u3002\u90a3\u5f20\u5929\u6c60\u7435\u7436\u9aa8\u788e\u4e86\uff0c\u82e5\u7136\u4e0d\u53ca\u65e9\u7ed9\u4ed6\u6551\u6cbb\uff0c\u4ed6\u7684\u6b66\u529f\u4fbf\u8981\u5e9f\u4e86\u3002\u4ed6\u597d\u574f\u4e5f\u662f\u4e00\u6d3e\u638c \u95e8\uff0c\u6211\u4eec\u4e0d\u80fd\u4ee4\u5929\u53f0\u6d3e\u7684\u6b66\u529f\u56e0\u6b64\u800c\u65ad\u5440\uff01\u201d\u51af\u745b\u4e00\u60f3\uff0c\u5f20\u5929\u6c60\u7684\u7435\u7436\u9aa8\u4e43\u662f\u81ea\u5df1\u523a\u7a7f\uff0c\u53c8 \u60f3\u8d77\u5415\u56db\u5a18\u4ee5\u524d\u6551\u6bd2\u9f99\u5c0a\u8005\u4e4b\u4e8b\uff0c\u518d\u60f3\u8d77\u6bcd\u59b9\u521d\u89c1\uff0c\u8fd8\u6709\u597d\u4e9b\u8bdd\u8981\u8bf4\uff0c\u5916\u7956\u7236\u4e5f\u8be5\u95ee\u5019\u3002\u4fbf \u6168\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4e5f\u53ea\u597d\u5982\u6b64\u4e86\u3002\u5510\u53d4\u53d4\u8bf4\u6b7b\u751f\u6709\u5b9a\uff0c\u6211\u4eec\u5df2\u662f\u5c3d\u529b\u800c\u4e3a\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u89c1\u53c8\u6709\u5341\u591a\u4eba\u722c\u4e0a\u5c71\u6765\uff0c\u674e\u6cbb\u4e00\u770b\uff0c\u7b11\u9053\uff1a\u201c\u7433\u59b9\uff0c\u4f60\u7684\u987e\u5ba2\u4e0a\u95e8\u4e86\u3002\u8fd9 \u662f\u4f60\u7528\u6bd2\u5200\u6240\u4f24\u7684\u4eba\uff0c\u4f60\u66ff\u4ed6\u4eec\u533b\u6cbb\u3002\u201d\u51af\u7433\u8fce\u4e0a\u524d\u53bb\uff0c\u90a3\u4e9b\u4eba\u53d1\u4e00\u58f0\u558a\uff0c\u53c8\u60f3\u9003\u8d70\u3002\u51af\u7433 \u9053\uff1a\u201c\u4f60\u4eec\u7684\u5be8\u4e3b\u5728\u8fd9\u513f\uff0c\u6765\uff0c\u6765\uff0c\u6211\u7ed9\u4f60\u4eec\u89e3\u836f\u3002\u201d\u5f20\u5929\u6c60\u7684\u624b\u4e0b\u66fe\u89c1\u5979\u6551\u8fc7\u5be8\u4e3b\uff0c\u53c8\u5206 \u8fa8\u4e0d\u51fa\u5979\u4eec\u8c01\u662f\u59d0\u59d0\uff0c\u8c01\u662f\u59b9\u59b9\uff0c\u662f\u53cb\u662f\u654c\uff0c\u5747\u6240\u4e0d\u77e5\uff0c\u4f46\u6c5f\u6e56\u4e0a\u5316\u654c\u4e3a\u53cb\u4e4b\u4e8b\uff0c\u4ea6\u5c5e\u5e38 \u89c1\uff0c\u4fbf\u5c06\u4f24\u8005\u62ac\u8fdb\u5c3c\u5eb5\u3002\u51af\u7433\u7ed9\u4e86\u4ed6\u4eec\u89e3\u836f\uff0c\u7b11\u9053\uff1a\u201c\u5988\uff0c\u4f60\u8fd9\u5c0f\u5c3c\u5eb5\u6210\u4e86\u533b\u5c40\u4e86\u3002\u54b1\u4eec\u51fa \u5916\u9762\u8c08\u53bb\u3002\u201d\u909d\u7ec3\u971e\u5230\u9759\u5ba4\u53bb\u770b\u770b\u909d\u740f\uff0c\u909d\u740f\u4ecd\u7136\u672a\u9192\uff0c\u4fbf\u548c\u4e24\u4e2a\u5973\u513f\u5230\u5c3c\u5eb5\u7684\u540e\u9762\u8c08\u8bdd\u3002 \u674e\u6cbb\u5219\u4e0a\u5c71\u53bb\u66ff\u5f20\u5929\u6c60\u5f20\u7f57\u7eed\u9aa8\u7684\u4e1c\u897f\u3002 \u3000\u3000\u909d\u7ec3\u971e\u4e00\u624b\u62c9\u7740\u4e00\u4e2a\u5973\u513f\uff0c\u5728\u9633\u5149\u666e\u7167\u4e4b\u4e0b\uff0c\u542c\u5979\u4eec\u6ed4\u6ed4\u4e0d\u7edd\u7684\u8bc9\u8bf4\uff0c\u9664\u4e86\u51af\u7433\u5fd8\u8bb0\u7ae5 \u5e74\u7684\u4e8b\u4e4b\u5916\uff0c\u4e24\u59d0\u59b9\u5c06\u5341\u51e0\u5e74\u6765\u7684\u60c5\u4e8b\u90fd\u4e00\u4e00\u8bf4\u4e86\uff0c\u909d\u7ec3\u971e\u77e5\u9053\u51af\u745b\u7adf\u662f\u5929\u5c71\u5973\u5251\u5ba2\u6613\u5170\u73e0 \u7684\u5f92\u513f\uff0c\u975e\u5e38\u6b22\u559c\uff1b\u51af\u7433\u867d\u7136\u547d\u9014\u591a\u821b\uff0c\u5728\u56db\u7687\u5e9c\u56f0\u4e86\u5c06\u8fd1\u5341\u5e74\uff0c\u4f46\u5374\u4e5f\u56e0\u6b64\u56e0\u7978\u5f97\u798f\uff0c\u5b66 \u4e86\u5404\u6d3e\u6b66\u529f\uff0c\u800c\u4e14\u6700\u8fd1\u53c8\u5f97\u4e86\u65e0\u6781\u6d3e\u7684\u771f\u4f20\u7edd\u6280\uff0c\u4e5f\u8db3\u4ee5\u5927\u6170\u6148\u6bcd\u4e4b\u5fc3\u3002 \u3000\u3000\u4e24\u59d0\u59b9\u8bf4\u4e86\u4e4b\u540e\uff0c\u909d\u7ec3\u971e\u4e5f\u5c06\u5979\u7684\u906d\u9047\uff0c\u544a\u8bc9\u5973\u513f\u77e5\u9053\uff0c\u51af\u745b\u5bf9\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u7531\u5510\u6653\u6f9c \u53e3\u4e2d\u5df1\u7565\u6709\u6240\u77e5\uff0c\u51af\u7433\u5374\u8fd8\u662f\u7b2c\u4e00\u6b21\u77e5\u9053\uff0c\u542c\u4e86\u4e4b\u540e\uff0c\u628a\u96cd\u6b63\u7687\u5e1d\u66f4\u6068\u5f97\u5165\u9aa8\uff0c\u9053\uff1a\u201c\u539f\u6765 \u4ed6\u624d\u662f\u5dee\u9063\u8840\u6ef4\u5b50\u6740\u5bb3\u6211\u4eec\u7239\u7239\uff0c\u903c\u6211\u4eec\u6bcd\u5973\u5206\u5f00\u7684\u4eba\uff0c\u6211\u975e\u4eb2\u624b\u6740\u4e86\u4ed6\uff0c\u96be\u6d88\u5fc3\u5934\u4e4b\u6068\u3002\u201d \u3000\u3000\u909d\u7ec3\u971e\u53c8\u9053\uff1a\u201c\u6211\u81ea\u4ece\u9003\u5230\u8fd9\u91cc\u4e4b\u540e\uff0c\u5341\u51e0\u5e74\u6765\u4e0d\u6562\u4e0b\u5c71\uff0c\u5929\u5e78\u80fd\u9047\u89c1\u4f60\u4eec\u3002\u5c06\u6765\u6211\u5e26 \u4f60\u4eec\u56de\u6545\u4e61\u770b\u770b\u3002\u201d\u505c\u4e86\u4e00\u505c\uff0c\u5ffd\u9053\uff1a\u201c\u7433\u513f\uff0c\u548c\u4f60\u540c\u6765\u7684\u90a3\u5c0f\u4f19\u5b50\u5f88\u4e0d\u9519\u5440\uff0c\u4ed6\u53eb\u4ec0\u4e48\u540d \u5b57\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u4ed6\u53eb\u674e\u6cbb\uff0c\u662f\u5929\u5c71\u4e03\u5251\u4e2d\u6b66\u743c\u7476\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u909d\u7ec3\u971e\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7433\u513f\uff0c\u4f60\u9009\u5f97\u4e0d\u9519\uff0c\u60f3\u4e0d\u5230\u6211\u7684\u4e24\u4e2a\u5973\u513f\u90fd\u548c\u5f53\u4e16\u6b66\u529f\u6700\u9ad8\u7684 \u4e24\u4f4d\u5973\u5251\u5ba2\u6500\u4e0a\u5173\u7cfb\u4e86\u3002\u201d\u51af\u7433\u561f\u7740\u5c0f\u5634\u513f\u9053\uff1a\u201c\u5988\uff0c\u4ed6\u8fd8\u6ca1\u6709\u5411\u6211\u6c42\u5a5a\u5462\uff01\u201d\u909d\u7ec3\u971e\u54c8\u54c8 \u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u59ae\u5b50\u771f\u4e0d\u5bb3\u8e81\uff0c\u4f60\u6025\u4ec0\u4e48\uff1f\u8fdf\u65e9\u4ed6\u603b\u4f1a\u5411\u6211\u63d0\u7684\u3002\u201d\u53c8\u7b11\u9053\uff1a\u201c\u745b\u513f\uff0c\u4f60 \u5462\uff1f\u201d\u51af\u745b\u5782\u9996\u80f8\u524d\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002\u51af\u7433\u7a81\u7136\u4f38\u51fa\u4e00\u53ea\u6307\u5934\uff0c\u522e\u5979\u7684\u8138\u76ae\uff0c\u9053\uff1a\u201c\u59d0\u59d0\u5bb3\u8e81 \u5566\uff01\u4f60\u90a3\u4f4d\u5510\u53d4\u53d4\u5462\uff1f\u201d\u909d\u7ec3\u971e\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u662f\u5510\u6653\u6f9c\u5417\uff1f\u6211\u4ee5\u524d\u53eb\u4ed6\u505a\u5c0f\u5f1f\u5f1f\u7684\uff0c\u6211\u4eec\u5bb6 \u9047\u96be\u4e4b\u65e5\uff0c\u4ed6\u8fd8\u820d\u547d\u4fdd\u62a4\u8fc7\u6211\u548c\u4f60\u5462\uff0c\u540e\u6765\u5230\u4e86\u592a\u884c\u5c71\u4e0a\u624d\u62c6\u6563\u4e86\u3002\u4ed6\u867d\u6bd4\u4f60\u5927\u5341\u591a\u5e74\uff0c\u4f46 \u4eba\u5374\u975e\u5e38\u4e4b\u597d\uff0c\u771f\u5f53\u5f97\u4e0a\u4fa0\u9aa8\u67d4\u80a0\u56db\u5b57\u3002\u8fd9\u5341\u591a\u5e74\uff0c\u6211\u4e5f\u5f88\u60e6\u8bb0\u4ed6\u3002\u5f02\u59d3\u53d4\u53d4\uff0c\u6ca1\u4ec0\u4e48\u5173 \u7cfb\u3002\u201d\u51af\u745b\u6ef4\u4e86\u4e24\u9897\u773c\u6cea\uff0c\u9053\uff1a\u201c\u5988\uff0c\u4e0d\u8981\u8bf4\u5566\uff01\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u522b\u62c5\u5fc3\uff01\u5510\u53d4\u53d4\u7684\u75c5\u75c7\uff0c \u674e\u6cbb\u53bb\u533b\uff0c\u4e00\u5b9a\u80fd\u591f\u533b\u597d\u3002\u201d\u51af\u745b\u628a\u5934\u522b\u8fc7\u4e00\u8fb9\uff0c\u53c8\u6ef4\u4e86\u4e24\u9897\u773c\u6cea\uff0c\u909d\u7ec3\u971e\u5728\u6b22\u559c\u4e0a\u5934\uff0c\u8fd8 \u4ee5\u4e3a\u5973\u513f\u662f\u4e3a\u5510\u6653\u6f9c\u7684\u5b89\u5371\u62c5\u5fc3\uff0c\u4fbf\u4e5f\u8bf4\u9053\uff1a\u201c\u674e\u6cbb\u7684\u533b\u9053\u786e\u5c5e\u9ad8\u660e\uff0c\u4f60\u5916\u7956\u7236\u53d7\u90a3\u6837\u91cd\u7684 \u4f24\uff0c\u4ed6\u4e5f\u80fd\u6551\u6cbb\uff0c\u6211\u60f3\u4ed6\u4e5f\u5b9a\u80fd\u533b\u597d\u6653\u6f9c\u3002\u201d\u51af\u7433\u6697\u6697\u5077\u7aa5\uff0c\u4f46\u89c1\u51af\u745b\u76ee\u8574\u6cea\u5149\uff0c\u773c\u89d2\u7709 \u68a2\uff0c\u9690\u85cf\u65e0\u9650\u5e7d\u6028\u3002\u51af\u7433\u662f\u4e2a\u9b3c\u7075\u7cbe\uff0c\u800c\u4e14\u5979\u4e5f\u66fe\u507f\u8fc7\u7231\u7684\u82e6\u5473\uff0c\u89c1\u6b64\u60c5\u666f\uff0c\u6599\u77e5\u59d0\u59d0\u5fc5\u7136 \u8fd8\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u5374\u4e5f\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u4e09\u6bcd\u5973\u5404\u8bc9\u5e73\u751f\u906d\u9047\uff0c\u4e0d\u77e5\u65e5\u5f71\u897f\u79fb\uff0c\u4e5f\u4e0d\u89c9\u8179\u4e2d\u9965\u997f\uff0c\u4e09\u4eba\u90fd\u9676\u9189\u5728\u5feb\u4e50\u4e0e\u60b2\u4f24\u4ea4\u7ec7 \u7684\u201c\u5e78\u798f\u201d\u4e2d\uff0c\u800c\u4e24\u59d0\u59b9\u53c8\u5404\u6709\u4e0d\u540c\u7684\u5fc3\u5883\u3002\u6b63\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5ffd\u89c1\u674e\u6cbb\u8fdc\u8fdc\u8dd1\u6765\uff0c\u542b \u7b11\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u6ca1\u6709\u8c08\u5b8c\u5417\uff1f\u201d \u3000\u3000\u51af\u7433\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u674e\u6cbb\u53f3\u624b\u63d0\u7740\u4e00\u53ea\u5c71\u9e21\uff0c\u5de6\u624b\u62ff\u7740\u4e00\u6839\u67f3\u679d\uff0c\u7b11\u559c\u559c\u7684\u8d70\u6765\uff0c\u51af\u7433 \u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u771f\u662f\uff0c\u4e0d\u548c\u6211\u4eec\u4e00\u8d77\uff0c\u53c8\u4e0d\u53bb\u505a\u533b\u751f\uff0c\u5374\u6709\u95f2\u5fc3\u60c5\u53bb\u6253\u5c71\u9e21\uff01\u201d\u674e\u6cbb\u9053\uff1a\u201c\u6551 \u5f20\u5929\u6c60\u5c31\u5168\u8981\u9760\u8fd9\u9e21\u5462\uff01\u5e08\u592a\uff0c\u6211\u53ef\u8981\u72af\u4f60\u7684\u5fcc\u4e86\u3002\u201d\u51af\u7433\u622a\u7740\u8bf4\u9053\uff1a\u201c\u8fd8\u53eb\u4ec0\u4e48\u5e08\u592a\uff0c\u4f2f \u6bcd\u4e5f\u4e0d\u53eb\u4e00\u58f0\uff0c\u72af\u4ec0\u4e48\u5fcc\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4f2f\u6bcd\uff0c\u6211\u8981\u5728\u4f60\u7684\u5c3c\u5eb5\u4e2d\u6740\u751f\uff0c\u8981\u7eed\u9aa8\u6ca1\u6709\u751f\u9e21\u7684 \u8840\u53ef\u4e0d\u80fd\u591f\u3002\u201d\u51af\u745b\u7b11\u9053\uff1a\u201c\u5988\u4ee5\u524d\u4f24\u5fc3\u624d\u505a\u5c3c\u59d1\uff0c\u73b0\u5728\u4e00\u5bb6\u56e2\u805a\uff0c\u8fd8\u505a\u4ec0\u4e48\u5c3c\u59d1\u5462\uff1f\u5988\uff0c \u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d\u909d\u7ec3\u971e\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u6dd8\u6c14\uff0c\u5012\u5f88\u77e5\u9053\u5988\u7684\u5fc3\u4e8b\uff0c\u5988\u4f9d\u4f60\u8bf4\uff0c\u660e\u5929\u4fbf\u8fd8\u4fd7\u3002\u201d \u3000\u3000\u674e\u6cbb\u884c\u5165\u5c3c\u5eb5\uff0c\u909d\u7ec3\u971e\u4e5f\u5165\u5185\u53bb\u770b\u7236\u4eb2\uff0c\u51af\u7433\u5374\u9053\uff1a\u201c\u5988\uff0c\u6211\u518d\u548c\u59d0\u59d0\u8c08\u4e00\u4f1a\uff0c\u4f60\u770b\u5916 \u516c\u9192\u4e86\uff0c\u5c31\u53eb\u6211\u4eec\u3002\u201d \u3000\u3000\u51af\u7433\u62c9\u7740\u59d0\u59d0\u8f7b\u8f7b\u8c08\u8bdd\uff0c\u909d\u7ec3\u971e\u89c1\u5979\u4eec\u59d0\u59b9\u4eb2\u70ed\uff0c\u5f88\u662f\u6b22\u559c\u3002\u884c\u5165\u5eb5\u5802\uff0c\u8fd8\u542c\u89c1\u5979\u4eec\u4fe9 \u59d0\u59b9\u683c\u683c\u7684\u7b11\u58f0\u3002 \u3000\u3000\u51af\u745b\u77e5\u9053\u59b9\u59b9\u6bd4\u81ea\u5df1\u53d7\u8fc7\u66f4\u591a\u7684\u82e6\u96be\uff0c\u9002\u624d\u53c8\u662f\u9519\u602a\u4e86\u5979\uff0c\u5bf9\u5979\u975e\u5e38\u75bc\u7231\u3002\u51af\u7433\u62c9\u5979\u5230 \u6811\u836b\u5e95\u5750\u4e0b\uff0c\u5c0f\u58f0\u95ee\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u6709\u4ec0\u4e48\u5fc3\u4e8b\u53ef\u4ee5\u5bf9\u6211\u8bf4\u5417\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u6211\u7684\u5fc3\u4e8b\uff0c\u5c31 \u662f\u8981\u627e\u4f60\u3002\u201d\u51af\u7433\u7b11\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u8fd8\u6709\u7684\uff01\u201d \u3000\u3000\u51af\u745b\u9ed8\u7136\u4e0d\u8bed\uff0c\u51af\u7433\u9053\uff1a\u201c\u6211\u5c0f\u65f6\u4e5f\u4ee5\u4e3a\u81ea\u5df1\u662f\u65e0\u7236\u65e0\u6bcd\u7684\u91ce\u5b69\u5b50\uff0c\u5728\u7687\u5e9c\u91cc\uff0c\u6709\u7684\u4eba \u8ba8\u6211\u559c\u6b22\uff0c\u53eb\u6211\u505a\u5c0f\u2018\u683c\u683c\u2019\uff08\u6ee1\u6d32\u8bed\uff0c\u5bf9\u4eb2\u738b\u5973\u513f\u7684\u5c0a\u8db4\uff0c\u6709\u7684\u4eba\u8ba8\u538c\u6211\uff0c\u9a82\u6211\u505a\u2018\u91ce\u4e2b \u5934\u2019\uff0c\u6211\u4e5f\u4e0d\u7ba1\u522b\u4eba\u6b22\u559c\u6216\u662f\u8ba8\u538c\uff0c\u6211\u5c31\u662f\u8fd9\u4e48\u957f\u5927\u4e86\u3002\u6211\u4e0d\u9ad8\u5174\u7684\uff0c\u5c31\u662f\u7687\u5e1d\u8001\u5b50\u6211\u4e5f\u4e0d \u5356\u5e10\uff1b\u4f46\u6211\u60f3\u8981\u7684\uff0c\u90a3\u5c31\u4e0d\u7ba1\u5b83\u662f\u5929\u8fb9\u62ff\u4e0d\u5230\u7684\u660e\u6708\uff0c\u6211\u4e5f\u8981\u8bbe\u6cd5\u67b6\u8d77\u5929\u68af\u628a\u5b83\u62ff\u4e0b\u6765\u3002\u201d \u3000\u3000\u4e00\u58f0\u8f7b\u8f7b\u7684\u53f9\u606f\u968f\u98ce\u98d8\u8d77\uff0c\u51af\u745b\u9053\uff1a\u201c\u6211\u6b22\u559c\u7684\u4e1c\u897f\u6211\u4e5f\u60f3\u62ff\u5230\u624b\u7684\uff0c\u4f46\u6211\u5374\u4e0d\u613f\u635f\u5bb3 \u4e86\u522b\u4eba\u6765\u53d6\u5f97\u6240\u6b32\u3002\u201d\u51af\u7433\u5ffd\u9053\uff1a\u201c\u4f60\u548c\u90a3\u4f4d\u2018\u5510\u53d4\u53d4\u2019\u5f88\u8981\u597d\u5417\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u55ef\uff0c\u4ed6\u5f88 \u559c\u6b22\u6211\uff0c\u6211\u4eec\u5728\u4e00\u8d77\u7684\u65f6\u5019\u73a9\u5f97\u5f88\u597d\u3002\u201d\u51af\u7433\u60f3\u7b11\u5374\u53c8\u5fcd\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u5f88\u597d\u5417\uff1f\u4f60\u4eec \u76f8\u597d\uff0c\u5173\u522b\u4eba\u751a\u4e48\u4e8b\uff1f\u201d\u51af\u745b\u9762\u4e0a\u4e00\u7ea2\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff0c\u4ed6\u6709\u4e86\u672a\u5a5a\u7684\u59bb\u5b50\uff1f\u201d\u51af \u7433\u6014\u4e86\u4e00\u6014\uff0c\u51af\u745b\u7eed\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u4eec\u4e8c\u4eba\u813e\u6c14\u5f88\u4e0d\u76f8\u6295\u3002\u201d\u51af\u7433\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u90a3\u4f4d \u5510\u53d4\u53d4\u505a\u4e8b\u4e5f\u771f\u4e0d\u723d\u5feb\uff0c\u65e2\u4e0d\u76f8\u6295\uff0c\u4e3a\u4f55\u4e0d\u5e72\u8106\u5206\u4e86\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u90a3\u4f4d\u5a76\u5a76\u5f88\u51f6\uff0c\u7f20\u7740\u4ed6 \u4e0d\u80af\u653e\u3002\u800c\u4e14\u5979\u7684\u7236\u4eb2\u5bf9\u4ed6\u66fe\u6709\u5927\u6069\u3002\u201d\u51af\u7433\u4e00\u542c\uff0c\u7b11\u9053\uff1a\u201c\u51f6\u5973\u4eba\u6211\u4e5f\u89c1\u8fc7\u5f88\u591a\uff0c\u4f60\u8bf4\u8bf4 \u770b\u5979\u600e\u4e48\u51f6\u6cd5\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u4f60\u5728\u6c5f\u6e56\u4e0a\u4e5f\u95ef\u4e86\u51e0\u5e74\uff0c\u6ca1\u542c\u8fc7\u6768\u4ef2\u82f1\u5973\u513f\u7684\u540d\u5b57\u5417\uff1f\u6b66\u6797\u540c \u9053\u4e5f\u5f88\u6015\u5979\uff0c\u4e5f\u4e0d\u5355\u662f\u6015\u5979\uff0c\u800c\u662f\u788d\u7740\u5979\u7236\u4eb2\u7684\u9762\u5b50\u3002\u201d\u51af\u7433\u51e0\u4e4e\u7b11\u51fa\u58f0\u6765\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u9053 \u662f\u8c01\uff1f\u539f\u6765\u662f\u6768\u67f3\u9752\u3002\u201d\u5374\u4e0d\u628a\u81ea\u5df1\u66fe\u548c\u6768\u67f3\u9752\u52a8\u624b\u7684\u4e8b\u8bf4\u51fa\u6765\uff0c\u5fc3\u4e2d\u53e6\u6253\u4e3b\u610f\u3002 \u3000\u3000\u51af\u745b\u628a\u81ea\u5df1\u600e\u6837\u53d7\u6768\u67f3\u9752\u7684\u6c14\uff0c\u540e\u6765\u53c8\u600e\u6837\u548c\u5979\u51b2\u7a81\u7684\u4e8b\u8bf4\u4e86\uff0c\u51af\u7433\u8fb9\u542c\u8fb9\u7b11\uff0c\u5374\u4e0d\u4f5c \u58f0\u3002\u51af\u745b\u6c14\u9053\uff1a\u201c\u522b\u4eba\u548c\u4f60\u8bf4\u6b63\u7ecf\u4e8b\u513f\uff0c\u4f60\u5374\u5c3d\u7b11\uff0c\u6211\u4e0d\u8bf4\u4e86\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u4e0d\u6b63\u7ecf \u5462\uff1f\u6211\u662f\u5728\u7528\u5fc3\u542c\u5440\uff01\u7ba1\u5979\u4ec0\u4e48\u6768\u67f3\u9752\u4e0d\u9752\uff0c\u5510\u53d4\u53d4\u662f\u4f60\u7684\u603b\u662f\u4f60\u7684\uff01\u201d\u51af\u745b\u6c14\u5f97\u8981\u5475\u59b9\u59b9 \u7684\u814b\u7a9d\uff0c\u51af\u7433\u7b11\u9053\uff1a\u201c\u54ce\u5440\uff0c\u4f60\u62a5\u590d\u5566\uff0c\u6211\u6700\u6015\u75d2\uff0c\u4f60\u662f\u59d0\u59d0\u5440\uff0c\u59d0\u59d0\u4e5f\u4e0d\u6b63\u7ecf\uff0c\u96be\u602a\u59b9\u59b9 \u6dd8\u6c14\u5566\uff01\u201d \u3000\u3000\u674e\u6cbb\u548c\u909d\u7ec3\u971e\u8fdb\u5165\u5c3c\u5eb5\uff0c\u5148\u66ff\u5f20\u5929\u6c60\u6cbb\u4f24\uff0c\u628a\u5265\u5269\u4e86\u76ae\u7684\u67f3\u679d\u6574\u6210\u9aa8\u5f62\uff0c\u67f3\u679d\u4e2d\u95f4\u6253\u901a \u6210\u9aa8\u8154\u72b6\uff0c\u7136\u540e\u5b89\u653e\u5728\u4e24\u6bb5\u788e\u9aa8\u5934\u7684\u5207\u9762\u4e2d\u95f4\uff0c\u4ee3\u66ff\u88ab\u5207\u9664\u7684\u9aa8\u5934\uff0c\u5728\u5b89\u653e\u65f6\uff0c\u6728\u68d2\u7684\u4e24\u7aef \u548c\u9aa8\u5934\u7684\u4e24\u4e2a\u5207\u9762\u90fd\u6d82\u4e0a\u70ed\u7684\u751f\u9e21\u8840\uff0c\u518d\u628a\u4e00\u79cd\u80fd\u751f\u957f\u808c\u8089\u7684\u201c\u77f3\u9752\u6563\u201d\u6492\u5728\u808c\u8089\u4e0a\uff0c\u628a\u808c \u8089\u7f1d\u597d\uff0c\u7136\u540e\u53c8\u5728\u63a5\u5408\u90e8\u4efd\u5916\u9762\u6577\u4e0a\u63a5\u8840\u818f\uff0c\u5939\u7740\u6728\u677f\u4ee5\u56fa\u5b9a\u9aa8\u4f4d\u3002\u8fd9\u79cd\u65b9\u6cd5\u53eb\u505a\u201c\u67f3\u679d\u63a5 \u9aa8\u6cd5\u201d\uff0c\u4e43\u4e2d\u56fd\u53e4\u4ee3\u533b\u5b66\u4e2d\u7684\u4e0d\u4f20\u4e4b\u79d8\uff0c\u53ea\u987b\u4e03\u65e5\u9aa8\u6728\u5c31\u53ef\u4ee5\u63a5\u5728\u4e00\u8d77\u3002\u5f20\u5929\u6c60\u5341\u5206\u611f\u6fc0\uff0c \u5bf9\u674e\u6cbb\u4e00\u518d\u9053\u6b49\u3002 \u3000\u3000\u66ff\u5f20\u5929\u6c60\u52a8\u4e86\u624b\u672f\u4e4b\u540e\uff0c\u4ed6\u4eec\u518d\u53bb\u770b\u909d\u740f\uff0c\u909d\u740f\u5df2\u7ecf\u9192\u4e86\uff0c\u7ecf\u8fc7\u4e86\u4e00\u5929\u4e00\u591c\u7684\u4f11\u606f\u6cbb \u7597\uff0c\u751f\u673a\u6062\u590d\uff0c\u7cbe\u795e\u8f6c\u597d\u3002\u909d\u7ec3\u971e\u628a\u4e24\u4e2a\u5973\u513f\u90fd\u56de\u6765\u4e86\u7684\u559c\u8baf\u544a\u77e5\u7236\u4eb2\uff0c\u909d\u740f\u66f4\u662f\u6b22\u559c\u3002 \u3000\u3000\u66ae\u972d\u542b\u5c71\uff0c\u5c71\u4e0b\u519c\u5bb6\uff0c\u708a\u70df\u56db\u8d77\u3002\u51af\u745b\u51af\u7433\u643a\u624b\u540c\u56de\uff0c\u53ea\u89c1\u6bcd\u4eb2\u6b63\u5728\u5eb5\u524d\u547c\u5524\u3002\u5417\u73bb\u95ee \u9053\uff1a\u201c\u5916\u516c\u9192\u4e86\u5417\uff1f\u201d\u909d\u7ec3\u971e\u9053\uff1a\u201c\u6b63\u7b49\u7740\u4f60\u4eec\u5462\u3002\u201d \u3000\u3000\u909d\u7ec3\u971e\u5c06\u5973\u513f\u5e26\u8fdb\u9759\u5ba4\uff0c\u909d\u740f\u4e00\u89c1\uff0c\u4e0d\u89c9\u53eb\u51fa\u58f0\u6765\uff0c\u4e24\u59d0\u59b9\u90fd\u987f\u65f6\u5446\u4e86\u3002\u909d\u7ec3\u971e\u9053\uff1a \u201c\u7239\uff0c\u4f60\u77a7\u5979\u4eec\u957f\u5f97\u8fd9\u4e48\u9ad8\u4e86\uff01\u53f3\u8fb9\u7684\u662f\u745b\u513f\uff0c\u5de6\u8fb9\u7684\u662f\u7433\u513f\u3002\u6211\u4e0d\u8bf4\u4f60\u4e00\u5b9a\u5206\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u51af\u745b\u9053\uff1a\u201c\u5916\u516c\u6055\u7f6a\u3002\u201d\u51af\u7433\u5c34\u5c2c\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5e78\u597d\u6211\u6ca1\u6709\u7528\u98de\u5200\u4f24\u4f60\u3002\u201d\u909d\u740f\u4e00\u6115 \u4e4b\u540e\uff0c\u54c8\u54c8\u5927\u7b11\u3002\u909d\u7ec3\u971e\u8be7\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u548c\u5916\u516c\u4ea4\u8fc7\u624b\u4e86\u3002\u201d\u909d\u740f\u9053\uff1a\u201c\u4e0d\u77e5\u4e0d\u7f6a\u3002\u4f60\u4eec\u7684 \u672c\u4e8b\u90fd\u5f88\u4e86\u5f97\uff0c\u6bd4\u6211\u4eec\u8001\u4e00\u8f88\u7684\u5f3a\u5f97\u591a\u4e86\uff01\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u503c\uff1a\u201c\u6211\u4e5f\u8001\u7cca\u6d82\u4e86\uff0c\u4ed6\u4eec\u4e2d\u4e86 \u949f\u5200\u5802\u72ec\u5bb6\u6240\u6709\u7684\u593a\u547d\u795e\u5200\uff0c\u6211\u5341\u5206\u5947\u602a\uff0c\u5374\u60f3\u4e0d\u8d77\u4f60\u6765\uff01\u201d \u3000\u3000\u51af\u7433\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6025\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u60f3\u8d77\u6211\u6765\uff1f\u201d\u909d\u740f\u9053\uff1a\u201c\u4f60\u7684\u593a\u547d\u795e\u5200\u4e0d\u662f\u949f\u4e07\u5802 \u6240\u4f20\u7684\u4e48\uff1f\u201d\u909d\u7ec3\u9707\u9053\uff1a\u201c\u4f60\u5468\u5c81\u4e4b\u65f6\uff0c\u5c31\u770b\u4e0a\u4e86\u4ed6\u7684\u593a\u547d\u795e\u5200\uff0c\u7237\u7237\u8fd8\u4e0d\u5f88\u9ad8\u5174\u5462\u3002\u201d\u51af \u7433\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6211\u7b2c\u4e00\u6b21\u542c\u89c1\u949f\u4e07\u5802\u7684\u540d\u5b57\uff0c\u5c31\u89c9\u5f97\u975e\u5e38\u4e4b\u719f\uff0c\u8fd9\u6837\u8bf4\u6765\uff0c \u83ab\u975e\u6211\u771f\u662f\u4ed6\u7684\u5ae1\u4f20\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u909d\u740f\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u5417\uff1f\u949f\u4e07\u5802\u6ca1\u6709\u5c06\u4ed6\u7684\u771f\u59d3\u540d\u544a\u8bc9\u4f60\u5417\uff1f\u201d\u909d\u7ec3\u971e \u53f9\u53e3\u6c14\u9053\uff1a\u201c\u7433\u513f\u8bf4\uff0c\u5979\u5bf9\u5c0f\u65f6\u5019\u7684\u4e8b\u60c5\u5df2\u5168\u5fd8\u4e86\u3002\u201d\u909d\u740f\u5947\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\uff1f\u201d\u4e8e\u662f\u5c06 \u9047\u96be\u4e4b\u665a\uff0c\u949f\u4e07\u5802\u5982\u4f55\u62b1\u5979\u51b2\u51fa\u91cd\u56f4\uff0c\u53c8\u600e\u6837\u5c06\u5979\u5e26\u5230\u5e74\u5bb6\u7b49\u4e8b\u8bf4\u4e86\u3002\u8fd9\u4e9b\u4e8b\u909d\u7ec3\u971e\u4e5f\u4e0d\u77e5 \u9053\uff0c\u542c\u5f97\u6014\u6014\u51fa\u795e\u3002 \u3000\u3000\u909d\u740f\u7eed\u9053\uff1a\u201c\u540e\u6765\u6211\u6d3e\u4eba\u63a2\u542c\u4f60\u7684\u6d88\u606f\uff0c\u59cb\u77e5\u4f60\u65e9\u5df2\u4e0d\u5728\u5e74\u5bb6\uff0c\u949f\u4e07\u5802\u4e5f\u83ab\u540d\u5176\u5999\u7684\u6b7b \u4e86\u3002\u4ece\u6b64\u6ca1\u6709\u5f97\u5230\u4f60\u7684\u97f3\u8baf\uff0c\u60f3\u4e0d\u5230\u73b0\u5728\u624d\u4f1a\u9762\u3002\u201d \u3000\u3000\u51af\u7433\u542c\u5f97\u5446\u4e86\uff0c\u4f17\u4eba\u53ea\u89c1\u5979\u4ee5\u624b\u6276\u5899\uff0c\u773c\u73e0\u597d\u50cf\u5b9a\u4f4f\u4e00\u822c\uff0c\u909d\u7ec3\u971e\u8f7b\u8f7b\u629a\u6478\u5979\u7684\u5934\u53d1\uff0c \u5979\u4e5f\u6beb\u65e0\u53cd\u5e94\uff0c\u5c31\u50cf\u9760\u7740\u5899\u58c1\u7684\u4e00\u5c0a\u77f3\u50cf\u3002 \u3000\u3000\u909d\u740f\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u505c\u53e3\u4e0d\u8bf4\u3002\u909d\u7ec3\u971e\u5728\u5979\u8033\u8fb9\u5524\u9053\uff1a\u201c\u7433\u513f\uff0c\u7433\u513f\uff01\u201d\u51af\u7433\u52a8\u4e5f\u4e0d \u52a8\uff0c\u4e5f\u4e0d\u56de\u7b54\u3002\u674e\u6cbb\u6025\u5fd9\u8d70\u8fc7\u53bb\uff0c\u6084\u6084\u5bf9\u909d\u7ec3\u971e\u8bf4\u9053\uff1a\u201c\u4f2f\u6bcd\uff0c\u5979\u6b63\u5728\u601d\u7d22\u5f80\u4e8b\uff0c\u4f60\u522b\u95ee \u5979\uff0c\u6211\u5e26\u5979\u51fa\u53bb\u4e00\u4f1a\u513f\u3002\u201d \u3000\u3000\u674e\u6cbb\u8f7b\u8f7b\u6276\u7740\u51af\u7433\uff0c\u8d70\u51fa\u5eb5\u5916\uff0c\u51af\u7433\u5446\u5446\u7684\u8ddf\u7740\u4ed6\u8d70\uff0c\u8d70\u5230\u4e00\u68f5\u67f3\u6811\u4e0b\uff0c\u674e\u6cbb\u62c9\u5979\u5750 \u4e0b\uff0c\u770b\u7740\u51af\u7433\u7684\u773c\u775b\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u51af\u7433\u5782\u9996\u80f8\u524d\uff0c\u674e\u6cbb\u5728\u5979\u8033\u8fb9\u9053\uff1a\u201c\u6211\u5e26\u4f60\u5230\u5e74\u5bb6\u53bb\u3002\u201d \u51af\u7433\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u53bb\uff0c\u6211\u4e0d\u53bb\uff01\u201d\u674e\u6cbb\u9053\uff1a\u201c\u5230\u4e86\uff0c\u5230\u4e86\uff0c\u554a\uff0c\u8fd9\u5ea7\u82b1\u56ed\u597d\u5927\uff0c\u600e\u4e45\u6ca1\u4eba\u5f80 \u7684\uff1f\u5c0f\u59d1\u5a18\uff0c\u4f60\u4eca\u5e74\u662f\u516b\u5c81\u8fd8\u662f\u4e03\u5c81\uff0c\u8ba4\u5b57\u4e86\u5417\uff1f\u201d\u51af\u7433\u7a81\u7136\u7528\u4e00\u4e2a\u5b69\u5b50\u7684\u58f0\u97f3\u7b54\u9053\uff1a\u201c\u6211 \u4e03\u5c81\u5566\uff0c\u949f\u8001\u5e08\u524d\u4e24\u5e74\u5df2\u7ecf\u6559\u6211\u8bc6\u5b57\u5566\u3002\u201d \u3000\u3000\u674e\u6cbb\u7528\u7684\u6b63\u662f\u4ece\u5085\u9752\u4e3b\u533b\u4e66\u4e2d\u5b66\u6765\u7684\u201c\u8fd4\u5657\u672f\u201d\uff0c\u201c\u8fd4\u5657\u672f\u201d\u662f\u539f\u59cb\u7684\u50ac\u7720\u672f\u4e4b\u4e00\uff0c\u867d \u4e0d\u80fd\u5982\u73b0\u4ee3\u50ac\u7720\u672f\u90a3\u6837\u7075\u6548\uff0c\u53ef\u4ee5\u63a7\u5236\u53d7\u672f\u8005\u7684\u7cbe\u795e\uff0c\u4f46\u50cf\u51af\u7433\u8fd9\u6837\u7684\u60c5\u5f62\uff0c\u5bf9\u81ea\u5df1\u8eab\u4e16\u6765 \u5386\u5df2\u660e\u767d\u4e4b\u540e\uff0c\u518d\u65bd\u7528\u6b64\u672f\uff0c\u90a3\u5c31\u5f88\u5bb9\u6613\u5e2e\u52a9\u5979\u5c06\u9057\u5931\u7684\u8bb0\u5fc6\uff0c\u50cf\u7f1d\u8865\u4e00\u7247\u7247\u788e\u5e03\u4e00\u6837\uff0c\u8fde \u7f00\u8d77\u6765\u3002 \u3000\u3000\u674e\u6cbb\u89c1\u5f00\u59cb\u751f\u6548\uff0c\u505c\u4e86\u4e00\u505c\uff0c\u8ba9\u5979\u7cbe\u795e\u96c6\u4e2d\uff0c\u8f7b\u8f7b\u7684\u4ece\u5979\u8170\u95f4\u62bd\u51fa\u90a3\u5323\u6bd2\u5200\uff0c\u95ee\u9053\uff1a \u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d\u51af\u7433\u4ecd\u7136\u7528\u5b69\u5b50\u7684\u58f0\u8c03\u7b54\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4e0d\u8981\u4e71\u52a8\u6211\u7684\u98de\u5200\uff0c\u8fd9\u662f\u949f\u8001\u5e08\u9001\u7ed9 \u6211\u7684\u593a\u547d\u795e\u5200\uff0c\u5200\u5c16\u6709\u6bd2\u7684\uff01\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5e38\u5e38\u548c\u5e74\u7fb9\u5c27\u540c\u73a9\u98de\u5200\u5417\uff1f\u201d\u51af\u7433\u9053\uff1a \u201c\u5e74\u54e5\u54e5\u4e5f\u6709\u4e00\u5323\u98de\u5200\uff0c\u4ed6\u6628\u5929\u8fd8\u6307\u70b9\u6211\u624b\u6cd5\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u5e74\u54e5\u54e5\u5bf9\u4f60\u597d\u5417\uff1f\u201d\u51af\u7433\u9053\uff1a \u201c\u597d\uff0c\u5f88\u597d\uff01\u201d\u674e\u6cbb\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u55ef\uff0c\u4f60\u73b0\u5728\u662f\u5341\u516d\u5c81\u7684\u5927\u59d1\u5a18\u4e86\uff0c\u7687\u5e1d\u8981\u903c\u4f60\u505a\u8d35\u5983\uff0c \u5e74\u7fb9\u5c27\u6765\u4e86\uff0c\u4ed6\u662f\u4e0d\u662f\u6765\u6551\u4f60\u7684\uff1f\u201d \u3000\u3000\u674e\u6cbb\u63d0\u8d77\u7684\u5df2\u662f\u8fd9\u4e24\u5e74\u7684\u4e8b\u60c5\uff0c\u51af\u7433\u4e00\u4e0b\u5b50\u5c31\u8bb0\u8d77\u6765\u4e86\uff0c\u5ffd\u7136\u201c\u54c7\u201d\u7684\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\uff0c \u53eb\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u4e0d\u662f\u597d\u4eba\uff0c\u4ed6\u5e2e\u5fd9\u7687\u5e1d\u903c\u6211\u9a97\u6211\u3002\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u58f0\u97f3\u5df2\u53d8\u6210\u5c11\u5973\u4e86\u3002 \u3000\u3000\u674e\u6cbb\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u8ba4\u8bc6\u7687\u5e1d\u7684\uff1f\u7687\u5e1d\u5c31\u662f\u56db\u8d1d\u52d2\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d\u51af\u7433\u70b9\u4e86\u70b9\u5934\uff0c\u674e\u6cbb \u9053\uff1a\u201c\u949f\u8001\u5e08\u4e3a\u4ec0\u4e48\u80af\u8ba9\u4f60\u7ed9\u7687\u5e1d\u620f\u4fae\uff1f\u201d\u51af\u7433\u5ffd\u7136\u9759\u9ed8\u4e0b\u6765\uff0c\u9732\u51fa\u4e00\u7247\u832b\u7136\u795e\u6001\u3002\u674e\u6cbb\u903c \u89c6\u5979\u7684\u773c\u775b\uff0c\u8f7b\u8f7b\u7684\u9053\uff1a\u201c\u5514\uff0c\u6709\u4e00\u5929\uff0c\u4f60\u6b63\u548c\u949f\u8001\u5e08\u4e00\u8d77\uff0c\u6709\u4ec0\u4e48\u4eba\u51b2\u8fdb\u6765\u4e86\uff1f\u201d\u674e\u6cbb\u731c \u60f3\u4e00\u5b9a\u662f\u6709\u4eba\u5230\u5e74\u5bb6\u52ab\u5979\u51fa\u6765\uff0c\u56e0\u6b64\u7528\u672f\u8bd5\u63c9\uff0c\u51af\u7433\u679c\u7136\u53c8\u201c\u54c7\u201d\u7684\u54ed\u4e86\u8d77\u6765\uff0c\u7a81\u7136\u53c8\u53d8\u56de \u4e86\u5b69\u5b50\u7684\u58f0\u8c03\uff0c\u53eb\u9053\uff1a\u201c\u6211\u6015\uff0c\u6211\u6015\uff01\u90a3\u4e24\u4e2a\u4eba\u7a7f\u7740\u9ebb\u8863\uff0c\u4e11\u602a\uff0c\u4e11\u602a\uff01\u4ed6\u4eec\u628a\u949f\u8001\u5e08\u6253\u6b7b \u4e86\uff0c\u628a\u6211\u62b1\u8d70\u4e86\u3002\u201d\u674e\u6cbb\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u91cc\u662f\u56db\u7687\u5e9c\uff0c\u55ef\uff0c\u90a3\u4e24\u4e2a\u4eba\u4e5f\u5728\u8fd9\u91cc\u3002\u201d\u674e\u6cbb\u95ee\u9053\uff1a \u201c\u4ed6\u4eec\u662f\u8c01\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u8428\u4f2f\u4f2f\uff01\u522b\u4eba\u53eb\u4ed6\u4eec\u505a\u53cc\u9b54\u3002\u55ef\uff0c\u6211\u4e0d\u559c\u6b22\u3002\u4ed6\u4eec\u90fd\u4e0d\u662f\u771f\u5fc3\u5bf9 \u6211\u597d\u7684\u3002\u56db\u8d1d\u52d2\u903c\u6211\uff0c\u4ed6\u4eec\u4e5f\u90fd\u5e2e\u4ed6\u903c\u6211\u3002\u201d \u3000\u3000\u674e\u6cbb\u5fc3\u4e2d\u6697\u559c\uff0c\u51af\u7433\u5df2\u628a\u5f80\u4e8b\u5168\u8bb0\u5fc6\u8d77\u6765\u4e86\u3002\u60f3\u4e86\u4e00\u60f3\uff0c\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u5bf9\u4f60\u4e0d \u597d\uff0c\u90a3\u4e48\u8c01\u5bf9\u4f60\u597d\uff1f\u201d\u51af\u7433\u9762\u4e0a\u9732\u51fa\u559c\u60a6\u7684\u5149\u5f69\uff0c\u53eb\u9053\uff1a\u201c\u674e\u6cbb\u54e5\u54e5\uff01\u674e\u54e5\u54e5\uff01\u201d\u674e\u6cbb\u7b11\u4e86 \u4e00\u7b11\uff0c\u6e29\u67d4\u7684\u8d34\u7740\u5979\u8033\u8fb9\u9053\uff1a\u201c\u7433\u59b9\uff0c\u4f60\u7741\u773c\u77a7\u77a7\uff0c\u4f60\u770b\u8c01\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u51af\u7433\u6709\u5982\u5927\u68a6\u521d\u9192\uff0c\u5f90\u5f90\u5f20\u5f00\u773c\u775b\uff0c\u5915\u7167\u7a7a\u5c71\uff0c\u665a\u971e\u6295\u5f71\uff0c\u6240\u60f3\u5ff5\u7684\u4eba\u5c31\u5728\u8eab\u8fb9\uff01\u51af\u7433 \u5b9a\u4e86\u5b9a\u795e\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4f5c\u68a6\u5427\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4f60\u7684\u68a6\u5df2\u7ecf\u9192\u4e86\uff01\u4f60\u518d\u60f3\u60f3\u4f60\u5c0f\u65f6\u5019\u7684\u4e8b\uff1f\u201d \u3000\u3000\u51af\u7433\u5b9a\u4e86\u5b9a\u795e\uff0c\u5c0f\u65f6\u5019\u7684\u4e8b\u60c5\u970e\u90a3\u95f4\u90fd\u6d8c\u4e0a\u5fc3\u5934\uff0c\u4ee5\u524d\u79cd\u79cd\uff0c\u5386\u5386\u5982\u5728\u76ee\u524d\u3002\u4e0d\u7981\u542b\u6cea \u7b11\u9053\uff1a\u201c\u55ef\uff0c\u6211\u90fd\u660e\u767d\u4e86\uff01\u201d\u674e\u6cbb\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u4e5f\u8be5\u56de\u53bb\u4e86\uff0c\u4f60\u7684\u5988\u5988\u548c\u59d0\u59d0\u4e00\u5b9a\u7b49\u5f97\u5fc3 \u6025\u4e86\u3002\u201d \u3000\u3000\u51af\u745b\u7684\u786e\u7b49\u5f97\u975e\u5e38\u5fc3\u6025\uff0c\u5979\u8033\u542c\u5bbf\u9e1f\u5f52\u6797\uff0c\u76ee\u9001\u665a\u971e\u6d88\u901d\uff0c\u60f3\u8d77\u660e\u65e5\u5348\u65f6\uff0c\u4fbf\u662f\u5510\u6653\u6f9c \u6700\u540e\u7684\u671f\u9650\uff0c\u6b63\u662f\u6781\u76ee\u5fc3\u7126\uff0c\u67d4\u80a0\u6b32\u65ad\u3002\u6068\u4e0d\u5f97\u548c\u674e\u6cbb\u65e9\u65e9\u52a8\u8eab\u3002 \u3000\u3000\u674e\u6cbb\u4e5f\u77e5\u5979\u7b49\u5f97\u5fc3\u6025\uff0c\u548c\u51af\u7433\u56de\u6765\u4e4b\u540e\uff0c\u8349\u8349\u5403\u8fc7\u665a\u996d\uff0c\u6253\u4e86\u4e2a\u76f9\uff0c\u8fd8\u672a\u5230\u4e09\u66f4\uff0c\u5c31\u548c \u51af\u745b\u52a8\u8eab\uff0c\u51af\u7433\u5219\u7559\u5728\u5eb5\u4e2d\u5e2e\u6bcd\u4eb2\u7167\u6599\u5916\u516c\u3002\u5979\u4eec\u9001\u51af\u745b\u4e0b\u5230\u534a\u5c71\uff0c\u909d\u7ec3\u971e\u4e00\u518d\u53ee\u5631\u9053\uff1a \u201c\u6653\u6f9c\u597d\u4e86\u4e4b\u540e\uff0c\u4f60\u9a6c\u4e0a\u5e26\u4ed6\u6765\u89c1\u6211\u5440\uff01\u201d\u51af\u7433\u5728\u65c1\u7b11\u9053\uff1a\u201c\u5988\uff0c\u8fd9\u4e2a\u8fd8\u7528\u4f60\u5631\u5490\u5417\uff1f\u201d \u3000\u3000\u51af\u745b\u5c55\u989c\u4e00\u7b11\uff0c\u6025\u6025\u548c\u674e\u6cbb\u4e0b\u5c71\uff0c\u8d81\u7740\u6de1\u6708\u758f\u661f\uff0c\u5404\u65bd\u7edd\u9876\u8f7b\u529f\uff0c\u5929\u8272\u8fd8\u672a\u5927\u4eae\uff0c\u4ed6\u4eec \u5df2\u4e0b\u4e86\u516b\u8fbe\u5cad\uff0c\u5230\u4e86\u5c45\u5eb8\u5173\u5916\u3002\u674e\u6cbb\u5ffd\u7136\u653e\u7f13\u811a\u6b65\uff0c\u7ec6\u7ec6\u95ee\u5979\u5e9f\u56ed\u8001\u4eba\u5982\u4f55\u5224\u65ad\u5510\u6653\u6f9c\u7684\u75c5 \u60c5\uff0c\u51af\u745b\u4e00\u4e00\u8bf4\u4e86\u3002\u53c8\u5c06\u5e9f\u56ed\u8001\u4eba\u7684\u53e6\u4e00\u5f20\u65b9\u5b50\u4ea4\u7ed9\u4ed6\u770b\u3002\u674e\u6cbb\u5fc3\u9053\uff1a\u201c\u8427\u745f\u79cb\u98ce\uff0c\u68a7\u6850\u53f6 \u843d\u3002\u7528\u68a7\u6850\u53f6\u4f5c\u836f\u5f15\uff0c\u60f3\u662f\u8981\u75c5\u4eba\u7684\u71e5\u6c14\u4e0b\u6c89\uff0c\u5f52\u795e\u5b81\u9759\u3002\u90a3\u51e0\u5473\u836f\u4e5f\u662f\u5b81\u795e\u4e4b\u836f\uff0c\u800c\u975e\u89e3 \u6bd2\u4e4b\u65b9\uff0c\u4e0d\u77e5\u662f\u4f55\u9053\u7406\uff1f\u201d \u3000\u3000\u674e\u6cbb\u82e6\u82e6\u601d\u7d22\uff0c\u811a\u6b65\u8d8a\u6765\u8d8a\u6162\uff0c\u51af\u745b\u5927\u4e3a\u5fc3\u6025\uff0c\u77e5\u4ed6\u7528\u795e\uff0c\u53c8\u4e0d\u597d\u50ac\u4ed6\u3002\u674e\u6cbb\u60f3\u4e86\u597d \u4e45\uff0c\u4ecd\u662f\u60f3\u4e0d\u51fa\u6240\u4ee5\u7136\u6765\u3002\u5076\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u671d\u9633\u5df2\u4ece\u90a3\u8fb9\u5c71\u95f4\u5189\u5189\u5347\u8d77\u3002\u51af\u745b\u9053\uff1a\u201c\u60f3\u901a\u4e86 \u5417\uff1f\u5feb\u70b9\u8d70\u5440\uff0c\u8981\u4e0d\u7136\u5348\u95f4\u5c31\u4e0d\u80fd\u8d76\u5230\u4e86\u3002\u201d \u3000\u3000\u5c71\u5761\u4e0a\u5ffd\u7136\u6709\u4eba\u63a5\u58f0\u5e94\u9053\uff1a\u201c\u54c8\uff0c\u7433\u8d35\u4eba\uff0c\u4f60\u6025\u4ec0\u4e48\uff1f\u7687\u4e0a\u7b49\u7740\u4f60\u5462\uff01\u201d\u53c8\u4e00\u4eba\u7b11\u9053\uff1a \u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u62d0\u5e26\u8d35\u5983\uff0c\u8fd8\u6562\u523a\u4f24\u4f5b\u7237\uff0c\u5e78\u597d\u5929\u7f51\u6062\u6062\uff0c\u758f\u800c\u4e0d\u6f0f\uff0c\u5982\u4eca\u53c8\u78b0\u89c1\u4f60\u5566\uff01\u597d\u5c0f \u5b50\uff0c\u4f60\u8fd8\u4e0d\u62d4\u5251\uff1f\u201d\u674e\u6cbb\u51af\u745b\u5927\u5403\u4e00\u60ca\uff0c\u7741\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u8fd9\u4e24\u4eba\u4e00\u662f\u97e9\u91cd\u5c71\uff0c\u4e00\u662f\u6d77\u4e91\u548c\u5c1a\u3002 \u3000\u3000\u6d77\u4e91\u548c\u5c1a\u524d\u665a\u53d7\u909d\u740f\u94c1\u70df\u888b\u6253\u4f24\uff0c\u53c8\u88ab\u674e\u6cbb\u7684\u58f0\u97f3\u5413\u8d70\u3002\u4ed6\u4ed7\u7740\u5185\u529f\u6df1\u6e5b\uff0c\u8c03\u6cbb\u4e4b\u540e\uff0c \u5e76\u65e0\u5927\u788d\uff0c\u8d76\u56de\u6765\u642c\u8bf7\u6551\u5175\uff0c\u6b63\u5de7\u5e74\u7fb9\u5c27\u6d3e\u5929\u53f6\u6563\u4eba\u548c\u97e9\u91cd\u5c71\u4e24\u5e08\u5144\u5f1f\u4ece\u9752\u6d77\u56de\u6765\u62a5\u544a\u519b \u60c5\uff0c\u96cd\u6b63\u542c\u8bf4\u5728\u516b\u8fbe\u5cad\u4e0a\u53d1\u73b0\u4ee5\u524d\u548c\u51af\u7433\u5728\u5d69\u5c71\u7684\u90a3\u4e2a\u674e\u6cbb\uff0c\u56e0\u989d\u97f3\u548c\u5e03\u53d7\u4e86\u51af\u745b\u638c\u4f24\uff0c\u6b63 \u5728\u7ec3\u529f\uff0c\u8fd8\u987b\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u624d\u80fd\u590d\u539f\uff0c\u4fbf\u53eb\u97e9\u91cd\u5c71\u548c\u6d77\u4e91\u548c\u5c1a\u540c\u53bb\u3002 \u3000\u3000\u6d77\u4e91\u548c\u5c1a\u4ee5\u524d\u5728\u5d69\u5c71\u4e2d\u8fc7\u674e\u6cbb\u4e00\u5251\uff0c\u6b64\u4ec7\u4e45\u5df2\u601d\u62a5\uff0c\u800c\u4eca\u6709\u97e9\u91cd\u5c71\u5728\u65c1\uff0c\u6709\u4f8d\u65e0\u6050\uff0c\u6599 \u60f3\u4e8c\u4eba\u5df2\u662f\u7f51\u4e2d\u4e4b\u9c7c\uff0c\u795e\u6001\u56a3\u5f20\u4e4b\u6781\uff01 \u3000\u3000\u54ea\u77e5\u51af\u745b\u6b63\u662f\u5fc3\u6025\u5982\u711a\u4e4b\u9645\uff0c\u54ea\u5bb9\u522b\u4eba\u963b\u8def\uff0c\u6d77\u4e91\u548c\u5c1a\u8bdd\u672a\u8bf4\u5b8c\uff0c\u51af\u745b\u5df2\u95ea\u7535\u822c\u7684\u62d4\u51fa \u5251\u6765\uff0c\u53f1\u54a4\u4e00\u58f0\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u5c31\u5982\u4e00\u9053\u7535\u5149\uff0c\u5411\u6d77\u4e91\u548c\u5c1a\u54bd\u5589\u75be\u523a\uff01 \u3000\u3000\u6d77\u4e91\u548c\u5c1a\u5c06\u5979\u5f53\u4f5c\u4ee5\u524d\u7684\u51af\u7433\uff0c\u5e76\u4e0d\u600e\u6837\u5728\u610f\uff0c\u5c82\u77e5\u9053\u4e00\u5251\u8fc5\u75be\u5f02\u5e38\uff0c\u6d77\u4e91\u548c\u5c1a\u957f\u5251\u4e00 \u7ffb\uff0c\u7adf\u7136\u683c\u5b83\u4e0d\u9000\uff0c\u6025\u95ea\u8eab\u65f6\uff0c\u80a9\u5934\u5df2\u4e2d\u4e86\u4e00\u5251\uff0c\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\u3002\u90a3\u4e00\u8fb9\u674e\u6cbb\u548c\u97e9\u91cd\u5c71\u4e5f\u5404 \u4eae\u5175\u5668\uff0c\u4ea4\u4e0a\u4e86\u624b\u3002 \u3000\u3000\u6d77\u4e91\u548c\u5c1a\u6c14\u6781\u72c2\u653b\uff0c\u4f46\u51af\u745b\u4e3a\u4e86\u6551\u4eba\uff0c\u6bd4\u4ed6\u66f4\u4e3a\u62fc\u547d\uff0c\u4ee5\u653b\u5bf9\u653b\uff0c\u6beb\u4e0d\u9000\u8ba9\uff01 \u3000\u3000\u51af\u745b\u7684\u5185\u529b\u867d\u7136\u7a0d\u900a\u4e8e\u6d77\u4e91\u548c\u5c1a\uff0c\u4f46\u5251\u6cd5\u5374\u6bd4\u4ed6\u7cbe\u5999\u5f97\u591a\uff0c\u60c5\u6025\u62fc\u547d\uff0c\u771f\u5982\u96e8\u9aa4\u98ce\u72c2\uff0c \u9f99\u86c7\u98de\u821e\uff0c\u6d77\u4e91\u548c\u5c1a\u5728\u672a\u591f\u4e24\u65e5\u4e4b\u95f4\uff0c\u8fde\u53d7\u4e86\u4e24\u6b21\u4f24\uff0c\u867d\u975e\u81f4\u547d\uff0c\u5143\u6c14\u4ea6\u4f24\u3002\u521d\u65f6\u8fd8\u80fd\u4ee5\u653b \u5bf9\u653b\uff0c\u6e10\u6e10\u4fbf\u53ea\u6709\u62db\u67b6\u7684\u4efd\u513f\u3002 \u3000\u3000\u90a3\u4e00\u8fb9\uff0c\u674e\u6cbb\u5374\u4e0d\u662f\u97e9\u91cd\u5c71\u7684\u5bf9\u719f\u9170\u97e9\u91cd\u5c71\u7684\u529f\u529b\u4e0e\u4e86\u56e0\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u4e00\u67c4\u8f9f\u4e91\u9504\u7eb5\u6a2a \u98de\u821e\uff0c\u6709\u5982\u5343\u94a7\u538b\u9876\uff0c\u4e07\u9a6c\u5954\u817e\u3002\u674e\u6cbb\u4ed7\u7740\u81ea\u53d1\u9b54\u5973\u7684\u72ec\u95e8\u5251\u6cd5\uff0c\u8fde\u8d70\u9669\u62db\uff0c\u4f46\u5374\u82e6\u4e8e\u65e0\u6cd5 \u8fd1\u8eab\uff0c\u97e9\u91cd\u5c71\u89c1\u4ed6\u5251\u6cd5\u5947\u8be1\uff0c\u4e5f\u4e0d\u6562\u8f7b\u654c\uff0c\u4ed6\u6bd4\u8f83\u7a33\u91cd\uff0c\u80dc\u5238\u65e2\u7136\u5728\u63e1\uff0c\u4fbf\u4e0d\u6025\u653b\u6740\uff0c\u60f3\u628a \u674e\u6cbb\u5f04\u5f97\u529b\u7aed\u7b4b\u75b2\uff0c\u7136\u540e\u65b9\u65bd\u6740\u624b\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u5982\u610f\u7b97\u76d8\u5374\u56e0\u6d77\u4e91\u548c\u5c1a\u654c\u4e0d\u4f4f\u51af\u745b\u800c\u88ab\u6253\u7834\uff0c\u9163\u6597\u4e2d\u6d77\u4e91\u548c\u5c1a\u53c8\u4e2d\u4e86\u4e00\u5251\uff0c\u8fd9\u4e00 \u5251\u4f24\u5f97\u66f4\u91cd\uff0c\u80f8\u53e3\u5904\u88ab\u5251\u950b\u63a0\u4e86\u4e00\u9053\u957f\u957f\u7684\u4f24\u53e3\uff0c\u6d77\u4e91\u548c\u5c1a\u6025\u5fd9\u632a\u8fd1\u97e9\u91cd\u5c71\u8eab\u8fb9\uff0c\u97e9\u91cd\u5c71\u6c14 \u9053\uff1a\u201c\u4f60\u5148\u56de\u53bb\uff01\u201d\u8f9f\u4e91\u9504\u4e00\u5c55\uff0c\u5c06\u51af\u745b\u674e\u6cbb\u53cc\u5251\u654c\u4f4f\u3002\u6d77\u4e91\u548c\u5c1a\u75bc\u75db\u96be\u5f53\uff0c\u6025\u6025\u9003\u547d\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u5f62\u52bf\u53c8\u53d8\uff0c\u51af\u745b\u674e\u6cbb\u53cc\u5251\u8054\u653b\uff0c\u9510\u4e0d\u53ef\u5f53\uff0c\u97e9\u91cd\u5c71\u529f\u529b\u867d\u9ad8\uff0c\u5374\u987e\u6b64\u5931\u5f7c\uff0c\u8fed\u9047 \u9669\u62db\uff0c\u4e0d\u89c9\u6697\u6697\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\u3002\u51af\u745b\u559d\u9053\uff1a\u201c\u4f60\u8ba9\u4e0d\u8ba9\u8def\uff1f\u201d\u97e9\u91cd\u5c71\u4fa7\u8eab\u6a2a\u9504\uff0c\u4e00\u62db\u201c\u6a2a\u4e91 \u65ad\u5cf0\u201d\uff0c\u683c\u5251\u9504\u8170\uff0c\u4e0d\u6599\u674e\u6cbb\u5251\u950b\u4e00\u98a4\uff0c\u4f3c\u865a\u4f3c\u5b9e\uff0c\u97e9\u91cd\u5c71\u51e0\u4e4e\u4e2d\u5251\uff0c\u9000\u907f\u65f6\u888d\u8896\u7adf\u88ab\u524a\u53bb \u4e00\u622a\u3002\u51af\u745b\u674e\u6cbb\u53cc\u5251\u75be\u8fdb\uff0c\u97e9\u91cd\u5c71\u594b\u529b\u4e00\u67b6\uff0c\u731b\u5730\u8df3\u51fa\u6838\u5fc3\uff0c\u628a\u624b\u4e00\u626c\uff0c\u4e24\u4ef6\u5947\u5f62\u6697\u5668\uff0c\u7834 \u7a7a\u5c04\u51fa\uff0c\u5206\u53d6\u51af\u745b\u674e\u6cbb\u3002 \u3000\u3000\u8fd9\u6697\u5668\u6b63\u662f\u4ed6\u72ec\u95e8\u79d8\u5236\u7684\u201c\u56de\u73af\u94a7\u201d\uff0c\u53ef\u4ee5\u56de\u73af\u8f6c\u6298\uff0c\u4e0a\u4e0b\u98de\u817e\uff0c\u597d\u4e0d\u5389\u5bb3\uff0c\u674e\u6cbb\u542c\u6613 \u5170\u73e0\u8bf4\u8fc7\u8fd9\u79cd\u6697\u5668\uff0c\u4e0d\u6562\u7528\u5251\u53bb\u6321\uff0c\u8fde\u7528\u51e0\u79cd\u8eab\u6cd5\uff0c\u582a\u582a\u907f\u5f00\uff0c\u51af\u745b\u89c1\u97e9\u91cd\u5c71\u6b66\u529f\u9ad8\u5f3a\uff0c\u6697 \u5668\u5389\u5bb3\uff0c\u5fc3\u9053\uff1a\u201c\u4ee5\u6211\u4e8c\u4eba\u4e4b\u529b\u867d\u53ef\u5c06\u4ed6\u6253\u8d25\uff0c\u4f46\u4e00\u5b9a\u8981\u8017\u4e0d\u5c11\u65f6\u5019\uff0c\u4e0d\u5982\u5413\u4ed6\u4e00\u5413\u3002\u201d\u56de \u73af\u94a9\u545c\u545c\u53d1\u54cd\uff0c\u659c\u91cc\u5c04\u6765\uff0c\u51af\u745b\u7528\u5251\u4e00\u64a9\uff0c\u90a3\u94a9\u88ab\u5916\u529b\u4e00\u649e\uff0c\u5ffd\u7136\u5760\u4e0b\uff0c\u4e00\u4e2a\u7ffb\u817e\uff0c\u5c04\u5230\u51af \u745b\u80f8\u53e3\uff0c\u97e9\u91cd\u5c71\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u201d\u4ed6\u672c\u610f\u4ec5\u662f\u60f3\u628a\u51af\u745b\u5f04\u4f24\uff0c\u4ee5\u4fbf\u64d2\u62ff\uff0c\u5c82\u77e5\u51af\u745b\u8fd9\u4e00 \u64a9\uff0c\u6070\u6070\u5c06\u56de\u73af\u94a9\u903c\u5c04\u5230\u5979\u80f8\u53e3\u81f4\u547d\u4e4b\u5904\u3002\u8981\u77e5\u97e9\u91cd\u5c71\u4ee5\u4e3a\u5979\u662f\u51af\u7433\uff0c\u800c\u51af\u7433\u6b63\u662f\u7687\u4e0a\u6240\u8981 \u4e4b\u4eba\uff0c\u97e9\u91cd\u5c71\u8feb\u4e8e\u65e0\u6cd5\uff0c\u624d\u6562\u7528\u8fd9\u79cd\u6b79\u6bd2\u6697\u5668\uff0c\u81ea\u5ff5\u5c06\u5979\u6253\u4f24\u72b9\u53ef\uff0c\u82e5\u7136\u5c06\u5979\u6bd9\u547d\uff0c\u90a3\u53ef\u662f \u5927\u7f6a\u4e00\u6869\u3002 \u3000\u3000\u6697\u5668\u98de\u5feb\uff0c\u97e9\u91cd\u5c71\u60f3\u8d76\u4e0a\u524d\u6536\u56de\u4ea6\u5df2\u65e0\u53ca\uff0c\u53ea\u542c\u5f97\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u56de\u73af\u94a9\u5c04\u6b63\u51af\u745b\u80f8 \u53e3\uff0c\u94a9\u7740\u8863\u88f3\uff0c\u7adf\u7136\u6302\u5728\u51af\u745b\u80f8\u524d\u3002\u51af\u745b\u53cc\u6307\u4e00\u7b9d\uff0c\u5c06\u56de\u73af\u94a7\u53d6\u4e86\u4e0b\u6765\uff0c\u795e\u8272\u81ea\u82e5\uff0c\u51b7\u51b7\u7b11 \u9053\uff1a\u201c\u8fd9\u79cd\u6697\u5668\u4e5f\u80fd\u4f24\u4eba\u5417\uff1f\u201d\u968f\u624b\u4e00\u629b\uff0c\u5c06\u56de\u73af\u94a9\u629b\u5230\u97e9\u91cd\u5c71\u811a\u4e0b\u3002 \u3000\u3000\u97e9\u91cd\u5c71\u54ea\u77e5\u5979\u8d34\u8eab\u7a7f\u7684\uff0c\u4e43\u662f\u949f\u4e07\u5802\u6240\u8d60\u7684\u5f02\u5b9d\u91d1\u4e1d\u8f6f\u7532\uff0c\u5200\u67aa\u4e0d\u5165\uff0c\u4f55\u51b5\u6697\u5668\uff1f\u4e0d\u7531 \u5f97\u5927\u60ca\u5931\u8272\u3002\u51af\u745b\u674e\u6cbb\u4e00\u4e2a\u51b7\u7b11\u65a5\u9a82\uff0c\u53cc\u5251\u53c8\u4e0a\u3002 \u3000\u3000\u97e9\u91cd\u5c71\u662f\u4e00\u6d3e\u5b97\u7956\uff0c\u5fc3\u5ff5\u6697\u5668\u4f24\u4ed6\u4eec\u4e0d\u5f97\uff0c\u518d\u6597\u4e5f\u654c\u4e0d\u4f4f\u4ed6\u4eec\u53cc\u5251\u8054\u653b\uff0c\u82e5\u7136\u8d25\u5728\u4e24\u4e2a \u5c0f\u8f88\u624b\u4e0b\uff0c\u6b8a\u4e0d\u503c\u5f97\u3002\u865a\u67b6\u4e00\u9504\uff0c\u6025\u6025\u5fd9\u5fd9\u9003\u8d70\u3002 \u3000\u3000\u51af\u745b\u62b9\u4e86\u989d\u4e0a\u51b7\u6c57\uff0c\u53eb\u58f0\u201c\u597d\u9669\u201d\u3002\u674e\u6cbb\u770b\u97e9\u91cd\u5c71\u9003\u7684\u65b9\u5411\uff0c\u6b63\u662f\u5165\u4eac\u57ce\u7684\u5927\u8def\uff0c\u5bf9\u51af \u745b\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u80fd\u8d70\u5927\u8def\u4e86\uff0c\u82e5\u7136\u8ffd\u4e0a\u4e86\u4ed6\uff0c\u53ea\u6050\u53c8\u6709\u4e00\u7ffb\u7f20\u6597\u3002\u7ed5\u5c71\u8def\u8d70\u5427\u3002\u201d \u3000\u3000\u51af\u745b\u4e00\u770b\u65e5\u5f71\uff0c\u8e59\u7709\u8bf4\u9053\uff1a\u201c\u5c0f\u8def\u8fdc\u4e9b\u8fd8\u662f\u8fd1\u4e9b\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u5927\u7ea6\u4e5f\u5dee\u4e0d\u591a\uff0c\u4e0d\u8fc7\u8f83 \u4e3a\u96be\u8d70\u3002\u4f46\u603b\u80dc\u4e8e\u7ed9\u4ed6\u53ae\u7f20\u3002\u201d\u51af\u745b\u4e00\u60f3\uff0c\u786e\u662f\u65e0\u6cd5\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u8ddf\u7740\u674e\u6cbb\u4fbf\u8dd1\u3002 \u3000\u3000\u4ee5\u4e24\u4eba\u8f7b\u529f\uff0c\u82e5\u7136\u4e00\u8def\u5e73\u5b89\uff0c\u672c\u53ef\u5927\u5348\u524d\u534a\u4e2a\u65f6\u8fb0\u8d76\u5230\uff0c\u504f\u504f\u7ed9\u97e9\u91cd\u5c71\u8fd9\u4e48\u4e00\u963b\uff0c\u65e5\u5934 \u5df2\u4e0a\u5df2\u7aff\uff0c\u51af\u745b\u5fc3\u4e2d\u6025\u6781\uff0c\u4e5f\u4e0d\u987e\u5ddd\u8def\u5d0e\u5c96\uff0c\u4e00\u8def\u7eb5\u9ad8\u7a9c\u4f4e\uff0c\u8d5b\u4f3c\u98ce\u9a70\u7535\u64ce\u3002\u674e\u6cbb\u7684\u8f7b\u529f\u672c \u6765\u5df2\u5f97\u5bb6\u4f20\u5fc3\u6cd5\uff0c\u4e16\u95f4\u7f55\u6709\uff0c\u4e5f\u51e0\u4e4e\u8ffd\u5979\u4e0d\u4e0a\u3002 \u3000\u3000\u8dd1\u4e86\u4e2a\u591a\u65f6\u8fb0\uff0c\u65e5\u5934\u5df2\u6e10\u81f3\u5929\u5fc3\uff01\u51af\u745b\u9053\uff1a\u201c\u8fd8\u6709\u591a\u5c11\u8def\u7a0b\uff1f\u201d\u674e\u6cbb\u5598\u6c14\u9053\uff1a\u201c\u56db\u5341 \u91cc\uff01\u201d\u51af\u745b\u4e94\u5185\u5982\u711a\uff0c\u770b\u65e5\u5f71\u5348\u95f4\u4fbf\u5230\uff0c\u56db\u5341\u91cc\u6700\u5c11\u8fd8\u8981\u8dd1\u534a\u4e2a\u65f6\u8fb0\u3002\u8111\u6d77\u4e2d\u5e7b\u51fa\u5510\u6653\u6f9c\u4e34 \u6b7b\u7684\u5f71\u8c61\uff0c\u5fc3\u75db\u5982\u7ede\uff0c\u5ffd\u800c\u60f3\u9053\uff1a\u201c\u4ed6\u4e0d\u89c1\u6211\uff0c\u53ea\u6050\u6b7b\u4e0d\u7791\u76ee\uff01\u201d\u8fd9\u65f6\u5979\u5df2\u4e0d\u6562\u518d\u5e0c\u671b\u5c06\u4ed6 \u6551\u6d3b\uff0c\u800c\u662f\u60f3\u5728\u4ed6\u4e34\u6b7b\u4e4b\u524d\uff0c\u8d76\u53bb\u548c\u4ed6\u89c1\u6700\u540e\u4e00\u9762\u4e86\u3002 \u3000\u3000\u51af\u745b\u62fc\u547d\u8d76\u8def\uff0c\u5c31\u5982\u4e00\u56e2\u767d\u5f71\uff0c\u631f\u7740\u98ce\u58f0\uff0c\u5728\u5c71\u91ce\u4e4b\u95f4\u98de\u8fc7\u3002\u674e\u6cbb\u4e5f\u6025\u4e86\uff0c\u7d27\u7d27\u8ddf\u5728\u51af \u745b\u540e\u9762\uff0c\u4e00\u9762\u7ed9\u5979\u6307\u8def\uff0c\u4e00\u9762\u8fd0\u6c14\u652f\u6301\uff0c\u56db\u5341\u91cc\u8def\uff0c\u7adf\u4f3c\u8f6c\u77ac\u4e4b\u95f4\u4fbf\u5728\u811a\u5e95\u98de\u8fc7\uff0c\u4e24\u4eba\u5230\u4e86 \u897f\u5c71\uff0c\u5510\u6653\u6f9c\u6240\u4f4f\u7684\u3001\u51b7\u7985\u9690\u5c45\u7684\u90a3\u95f4\u5bfa\u9662\u5df2\u7136\u53ef\u4ee5\u671b\u89c1\u4e86\u3002 \u3000\u3000\u51af\u745b\u5ffd\u7136\u53eb\u4e86\u4e00\u58f0\uff0c\u674e\u6cbb\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u65e5\u5934\u6b63\u6b63\u60ac\u6302\u5929\u4e2d\uff0c\u968f\u7740\u542c\u5f97\u8f70\u7136\u4e00\u58f0\u5de8\u54cd\uff0c \u90a3\u662f\u6bcf\u65e5\u5348\u95f4\uff0c\u957f\u9675\uff08\u660e\u6210\u7956\u9675\u56ed\uff09\u6240\u653e\u7684\u5348\u70ae\uff0c\u70ae\u58f0\u4f20\u5230\u897f\u5c71\u3002 \u3000\u3000\u674e\u6cbb\u9053\uff1a\u201c\u5230\u4e86\uff0c\u5230\u4e86\uff01\u201d\u51af\u745b\u9762\u8272\u60e8\u767d\uff0c\u52a0\u7d27\u75be\u8dd1\uff0c\u5fc3\u9053\uff1a\u201c\u5230\u4e86\u53c8\u6709\u4f55\u7528\uff1f\u8fdf\u4e86\uff0c \u8fdf\u4e86\uff01\u201d\u5fc3\u513f\u535c\u535c\u7684\u8df3\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5df2\u5230\u5bfa\u9662\u9762\u524d\uff0c\u53ea\u89c1\u51b7\u7985\u548c\u5c1a\u6b63\u5728\u5bfa\u524d\u773a\u671b\u3002 \u3000\u3000\u51af\u745b\u5fd9\u95ee\u9053\uff1a\u201c\u6211\u7684\u5510\u53d4\u53d4\u600e\u4e48\u6837\u4e86\uff1f\u201d\u51b7\u7985\u773c\u6709\u6cea\u73e0\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5728\u91cc\u9762\u3002\u201d\u51af\u745b \u4e00\u770b\u4ed6\u7684\u8138\u8272\uff0c\u5fc3\u4e2d\u51b7\u4e86\u534a\u622a\u3002\u6c57\u4e0b\u5982\u96e8\uff0c\u8eab\u5b50\u5982\u53d1\u51b7\u822c\u7684\u98a4\u6296\u4e0d\u4f11\u3002 \u3000\u3000\u674e\u6cbb\u9053\uff1a\u201c\u745b\u59b9\uff0c\u522b\u6015\uff0c\u8fd8\u672a\u65ad\u6c14\uff0c\u5c1a\u53ef\u6025\u6551\u3002\u201d\u51af\u745b\u4e00\u8a00\u4e0d\u53d1\uff0c\u5e26\u674e\u6cbb\u8fdb\u5165\u5185\u95f4\uff0c\u53ea \u89c1\u7518\u51e4\u6c60\u8fce\u9762\u8d70\u6765\uff0c\u9053\uff1a\u201c\u4f60\u6765\u8fdf\u4e86\uff0c\u4e0d\u7528\u8fdb\u53bb\u4e86\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u9739\u96f3\u4e00\u58f0\u4f20\u5669\u8017\uff0c\u53ea\u6101\u788e\u4e86\u5973\u513f\u5fc3\u3002 \u3000\u3000\u6b32\u77e5\u5510\u6653\u6f9c\u6027\u547d\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u56db\u5341\u56de\u3000\u3000\u5b09\u7b11\u901e\u5947\u80fd\u3000\u98de\u5200\u524a\u53d1\u3000\u4e34\u5371\u65bd\u5999\u624b\u3000\u8054\u5251\u60e9\u51f6 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u56db\u5341\u56de \u5b09\u7b11\u901e\u5947\u80fd\u3000\u98de\u5200\u524a\u53d1 \u4e34\u5371\u65bd\u5999\u624b\u3000\u8054\u5251\u60e9\u51f6 \u3000\u3000\u51af\u745b\u7a81\u95fb\u6b64\u8bed\uff0c\u6709\u5982\u5de8\u96f7\u8f70\u9876\uff0c\u54ed\u4e0d\u51fa\u58f0\uff0c\u98a4\u5dcd\u5dcd\u7684\uff0c\u51b2\u5165\u95e8\u53bb\uff0c\u7518\u51e4\u6c60\u6015\u5979\u4f24\u5fc3\uff0c\u672c \u60f3\u62e6\u963b\uff0c\u89c1\u5979\u5982\u6b64\uff0c\u53ea\u597d\u957f\u53f9\u4e00\u58f0\uff0c\u8ba9\u5f00\u4e86\u8def\u3002 \u3000\u3000\u51af\u745b\u51b2\u5165\u5185\u5ba4\uff0c\u53ea\u89c1\u9c7c\u4eae\u3001\u767d\u6cf0\u5b98\u3001\u9c7c\u5a18\u3001\u8def\u6c11\u77bb\u7b49\u9ed8\u9ed8\u73af\u7ed5\u5728\u75c5\u69bb\u4e4b\u524d\uff0c\u4e00\u89c1\u51af\u745b\uff0c \u90fd\u632a\u5f00\u8eab\u5b50\u3002\u51af\u745b\u8fd9\u65f6\u624d\u54ed\u5f97\u51fa\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u5510\u53d4\u53d4\uff0c\u662f\u6211\u6765\u4e86\uff0c\u4f60\u542c\u5f97\u89c1\u5417\uff1f\u201d\u75c5\u584c\u4e0a\u7684 \u5510\u6653\u6f9c\u53cc\u76ee\u5fae\u5f00\uff0c\u8eab\u5b50\u597d\u50cf\u8f7b\u8f7b\u62bd\u6410\u4e86\u4e00\u4e0b\uff0c\u5374\u65e0\u8a00\u8bed\uff0c\u674e\u6cbb\u968f\u540e\u8fdb\u6765\uff0c\u53ea\u542c\u5f97\u767d\u6cf0\u5b98\u9053\uff1a \u201c\u4f60\u4eec\u6765\u8fdf\u4e86\uff0c\u4ed6\u521a\u521a\u54bd\u6c14\uff01\u201d \u3000\u3000\u674e\u6cbb\u8fd9\u65f6\u4e5f\u4e0d\u7531\u5f97\u5927\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u7edd\u65e0\u6b64\u7406\uff01\u201d\u6392\u5f00\u4f17\u4eba\uff0c\u53ea\u89c1\u51af\u745b\u54ed\u5f97\u6cea\u4eba\u513f\u4f3c\u7684\uff0c \u9c7c\u5a18\u548c\u674e\u660e\u73e0\u4e24\u4eba\u63fd\u7740\u5979\uff0c\u4e0d\u8bb8\u5979\u6251\u5230\u5510\u6653\u6f9c\u8eab\u4e0a\u3002 \u3000\u3000\u674e\u6cbb\u4f38\u624b\u628a\u8109\uff0c\u53ea\u89c1\u8109\u606f\u5f31\u5982\u6e38\u4e1d\uff0c\u53c8\u4f0f\u5728\u4ed6\u80f8\u53e3\u4e0a\u4e00\u542c\uff0c\u80f8\u53e3\u5c1a\u6709\u5fae\u6e29\uff0c\u5fc3\u810f\u4e5f\u8fd8\u5fae \u5fae\u8df3\u52a8\u3002\u518d\u4ed4\u7ec6\u542c\u8109\uff0c\u8109\u606f\u6beb\u65e0\u534a\u70b9\u75c5\u8c61\uff0c\u53ea\u662f\u5fae\u5f31\u5982\u65af\uff0c\u9f3b\u606f\u4ea6\u51e0\u4e4e\u4e0d\u80fd\u5206\u8fa8\uff0c\u786e\u662f\u65e0\u53ef \u7406\u89e3\u3002\u8fd9\u5239\u90a3\u95f4\u5ffd\u7136\u60f3\u8d77\u4e86\u5e9f\u56ed\u8001\u4eba\u7684\u65ad\u75c7\u7ecf\u8fc7\uff0c\u548c\u4ed6\u6240\u8981\u7528\u79cb\u6850\u53f6\u3001\u79cb\u87cb\u87c0\u3001\u5b81\u795e\u836f\u7b49\u7b49 \u7406\u7531\uff0c\u5ffd\u7136\u8df3\u8d77\uff0c\u5bf9\u51af\u745b\u9053\uff1a\u201c\u5feb\u4e9b\u6b62\u6cea\uff0c\u4f60\u4e00\u54ed\u4ed6\u5c31\u6ca1\u6551\u4e86\u3002\u201d \u3000\u3000\u51af\u745b\u9053\uff1a\u201c\u8fd8\u6709\u6551\u5417\uff1f\u201d\u674e\u6cbb\u70b9\u4e86\u70b9\u5934\uff0c\u51af\u745b\u987f\u65f6\u6b62\u6cea\u3002\u4f17\u4eba\u90fd\u6781\u8be7\u5f02\uff0c\u660e\u660e\u5df2\u7ecf\u65ad \u6c14\uff0c\u4f55\u4ee5\u5c1a\u8bf4\u6709\u6551\uff1f\u800c\u4e14\u8feb\u5207\u4e4b\u95f4\uff0c\u53c8\u54ea\u6765\u7684\u836f\uff1f \u3000\u3000\u674e\u6cbb\u62c9\u51af\u745b\u884c\u5f00\u4e00\u8fb9\uff0c\u4f4e\u58f0\u5bf9\u5979\u9053\uff1a\u201c\u4f60\u7528\u624b\u6307\u6233\u4ed6\u4eba\u4e2d\uff0c\u5728\u4ed6\u8033\u8fb9\u53eb\u9053\uff1a\u201c\u6211\u6c42\u5f97\u7075 \u4e39\u6765\u4e86\uff01\u201d \u3000\u3000\u51af\u745b\u6ee1\u8179\u7591\u60d1\uff0c\u9053\uff1a\u201c\u54ea\u6765\u7684\u7075\u4e39\uff1f\u201d\u674e\u6cbb\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u4f60\u4e00\u5207\u90fd\u8981\u542c\u6211\u6240\u8bf4\uff0c\u5305 \u4f60\u7acb\u89c1\u529f\u6548\u3002\u201d \u3000\u3000\u5988\u73bb\u5c06\u4fe1\u5c06\u7591\uff0c\u4f9d\u674e\u6cbb\u7684\u8bdd\u8bf4\u4e86\uff0c\u53ea\u89c1\u674e\u6cbb\u5012\u4e86\u4e00\u676f\u5f00\u6c34\uff0c\u968f\u624b\u5728\u9999\u7089\u91cc\u53d6\u4e86\u4e00\u70b9\u70b9\u9999 \u7070\uff0c\u5f39\u5165\u676f\u5185\uff0c\u9053\uff1a\u201c\u7ed9\u4ed6\u559d\uff01\u201d \u3000\u3000\u51af\u745b\u9762\u8272\u5927\u53d8\uff0c\u6b63\u60f3\u9a82\u9053\uff1a\u201c\u8fd9\u4e2a\u65f6\u5019\uff0c\u4f60\u8fd8\u9a97\u6211\u3002\u201d\u674e\u6cbb\u53cc\u7738\u70af\u70af\uff0c\u9053\uff1a\u201c\u5feb\u7ed9\u4ed6 \u559d\uff0c\u8fd9\u5c31\u662f\u7075\u4e39\uff01\u201d\u9762\u5bb9\u8083\u7a46\uff0c\u8bf4\u5f97\u6781\u4e3a\u8ba4\u771f\u3002\u51af\u745b\u4e0d\u7531\u81ea\u4e3b\u7684\u63a5\u8fc7\u4e86\u90a3\u676f\u6e05\u6c34\uff0c\u674e\u6cbb\u53c8 \u9053\uff1a\u201c\u704c\u7ed9\u4ed6\u559d\uff0c\u8bf4\u7075\u4e39\u6765\u4e86\uff01\u201d \u3000\u3000\u51af\u745b\u4f9d\u8a00\u5728\u5510\u6653\u6f9c\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u7075\u4e39\u6765\u4e86\uff01\u201d\u5510\u6653\u6f9c\u8eab\u5b50\u53c8\u62bd\u6410\u4e86\u4e00\u4e0b\uff0c\u674e\u6cbb\u63a5\u53e3\u9053\uff1a \u201c\u9a6c\u4e0a\u5c31\u597d\uff01\u201d\u51af\u745b\u5c06\u6df7\u7740\u4e00\u70b9\u70b9\u9999\u7070\u7684\u5f00\u6c34\u704c\u4e0b\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u53ea\u89c1\u5510\u6653\u6f9c\u9f3b\u606f\u6e10\u7c97\uff0c\u8138\u8272 \u4e5f\u6e10\u89c1\u7ea2\u6da6\uff0c\u5f90\u5f90\u5f20\u5f00\u773c\u9053\uff1a\u201c\u54a6\uff0c\u6211\u662f\u4f5c\u68a6\u5417\uff1f\u6211\u660e\u660e\u89c1\u7740\u4e24\u4e2a\u9b3c\u5352\u5c06\u6211\u62c9\u53bb\uff0c\u600e\u4e48\u53c8\u56de \u6765\u4e86\uff1f\u201d \u3000\u3000\u4f17\u4eba\u89c1\u6b64\u60c5\u5f62\uff0c\u4e2a\u4e2a\u5947\u602a\uff0c\u53ea\u89c1\u674e\u6cbb\u5fae\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5510\u5927\u54e5\uff0c\u4f60\u8ba4\u5f97\u5c0f\u5f1f\u5417\uff1f\u201d\u5510\u6653\u6f9c \u671b\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u554a\uff0c\u8d24\u5f1f\uff0c\u4f60\u4e5f\u6765\u4e86\uff01\u201d\u58f0\u97f3\u4ecd\u5f88\u5fae\u5f31\u3002\u674e\u6cbb\u5ffd\u9053\uff1a\u201c\u5404\u4f4d\u542c\u6211\u8bf4\u4e00\u4e2a\u6545 \u4e8b\u3002\u201d \u3000\u3000\u4f17\u4eba\u66f4\u662f\u5947\u602a\uff0c\u51af\u745b\u53c8\u559c\u53c8\u5947\uff0c\u4ed6\u5c45\u7136\u8fd8\u6709\u95f2\u5fc3\u60c5\u8bf4\u6545\u4e8b\uff1f \u3000\u3000\u674e\u6cbb\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u6709\u4e00\u4e2a\u5927\u5c06\uff0c\u5c61\u4e3a\u56fd\u5bb6\u7acb\u529f\uff0c\u671d\u5ef7\u501a\u4e3a\u67f1\u77f3\uff0c\u7687\u5e1d\u89c6 \u5982\u624b\u8db3\u3002\u6211\u5df2\u8bb0\u4e0d\u6e05\u662f\u54ea\u4e2a\u671d\u4ee3\uff0c\u4e5f\u8bb0\u4e0d\u8d77\u5927\u5c06\u548c\u7687\u5e1d\u7684\u540d\u5b57\u4e86\uff0c\u603b\u4e4b\u662f\u6709\u90a3\u4e48\u4e00\u56de\u4e8b\uff0c\u4e14 \u662f\u4e66\u672c\u4e0a\u8bb0\u8f7d\u7684\u3002 \u3000\u3000\u201c\u90a3\u4f4d\u5927\u5c06\u519b\u529b\u654c\u4e07\u4eba\uff0c\u6b66\u529f\u76d6\u4e16\uff0c\u53ef\u662f\u5c31\u6015\u8001\u5a46\uff01\u201d \u3000\u3000\u8def\u6c11\u77bb\u548c\u767d\u6cf0\u5b98\u90fd\u7b11\u4e86\u8d77\u6765\u3002\u51af\u745b\u5fc3\u9053\uff1a\u201c\u674e\u6cbb\u4e00\u5411\u6734\u5b9e\uff0c\u4e0d\u82df\u8a00\u7b11\uff0c\u600e\u4e48\u4eca\u5929\u8bf4\u8d77\u6015 \u8001\u5a46\u7684\u7b11\u8bdd\u6765\uff1f\u96be\u9053\u4ed6\u662f\u8ba5\u523a\u5510\u53d4\u53d4\u6015\u6768\u67f3\u9752\u5417\uff1f\u4e0d\uff0c\u4ed6\u662f\u5fe0\u539a\u4e4b\u4eba\uff0c\u4e0d\u4f1a\u5982\u6b64\u53d6\u7b11\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u674e\u6cbb\u7ee7\u9053\uff1a\u201c\u90a3\u4f4d\u5927\u5c06\u519b\u5e74\u5c06\u534a\u767e\uff0c\u819d\u4e0b\u65e0\u513f\uff0c\u7687\u5e1d\u529d\u4ed6\u7acb\u59be\uff0c\u4ed6\u6015\u8001\u5a46\uff0c\u8fde\u8fd9 \u4e2a\u5ff5\u5934\u90fd\u4e0d\u6562\u6709\u3002\u201d \u3000\u3000\u9c7c\u5a18\u63d2\u53e3\u9053\uff1a\u201c\u6015\u4e0d\u6015\u8001\u5a46\uff0c\u6709\u6ca1\u6709\u513f\u5973\uff0c\u603b\u4e4b\u90fd\u4e0d\u5e94\u8be5\u7acb\u59be\u3002\u201d \u3000\u3000\u767d\u6cf0\u5b98\u66fe\u5728\u524d\u4eba\u7b14\u8bb0\u4e0a\u8bfb\u8fc7\u8fd9\u6bb5\u6545\u4e8b\uff0c\u7b11\u9053\uff1a\u201c\u4ed6\u662f\u8bf4\u6545\u4e8b\u5440\uff0c\u4f60\u522b\u6253\u5c94\uff0c\u8fd9\u6545\u4e8b\u548c\u5510 \u5144\u5927\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u51af\u745b\u9762\u4e0a\u4e00\u7ea2\uff0c\u53c8\u4ee5\u4e3a\u4ed6\u662f\u53d6\u7b11\u3002\u674e\u6cbb\u7eed\u9053\uff1a\u201c\u6709\u4e00\u5929\uff0c\u7687\u5e1d\u628a\u90a3\u4e2a\u5927\u5c06\u519b\u7684\u8001\u5a46\u4f20\u8fdb \u5bab\u6765\uff0c\u628a\u4e00\u676f\u4e1c\u897f\u6446\u5728\u5979\u7684\u9762\u524d\uff0c\u5bf9\u5979\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u676f\u6bd2\u9152\uff0c\u5403\u4e86\u4e4b\u540e\uff0c\u5341\u4e8c\u65f6\u8fb0\u4e4b\u5185\uff0c \u4e03\u7a8d\u6d41\u8840\u800c\u6b7b\uff0c\u65e0\u836f\u53ef\u6551\uff01\u4f60\u82e5\u8bb8\u4e08\u592b\u7acb\u59be\uff0c\u6211\u5c31\u5c06\u4e00\u540d\u5bab\u5973\u8d50\u7ed9\u4ed6\uff0c\u8981\u4f60\u4eb2\u81ea\u5c06\u5bab\u5973\u5e26\u56de \u5bb6\u53bb\u3002\u4f60\u82e5\u4e0d\u8bb8\u4e08\u592b\u7acb\u59be\uff0c\u6715\u4fbf\u5c06\u8fd9\u676f\u6bd2\u9152\u8d50\u4f60\u81ea\u5c3d\uff01 \u3000\u3000\u201d\u90a3\u4f4d\u5927\u5c06\u519b\u7684\u59bb\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u2018\u5b81\u6b7b\u4e0d\u8ba9\u4e08\u592b\u7acb\u59be\uff01\u2019\u676f\u4e00\u996e\u800c\u5c3d\u3002\u8bdd\u867d\u5982\u6b64\uff0c\u5230\u5e95 \u5fc3\u614c\uff0c\u5f53\u65f6\u6562\u996e\u6bd2\u9152\uff0c\u4e43\u662f\u4e00\u65f6\u6c14\u6d8c\uff0c\u56de\u5bb6\u4e4b\u540e\uff0c\u60f3\u8d77\u5982\u6b64\u53bb\u6b7b\uff0c\u5341\u5206\u4f24\u5fc3\u3002\u4e8e\u662f\u4e00\u9762\u5367\u5e8a \u7b49\u6b7b\uff0c\u4e00\u9762\u8981\u4e08\u592b\u66ff\u5979\u5e26\u5b5d\u5ff5\u7ecf\uff0c\u5c82\u77e5\u5341\u4e8c\u65f6\u8fb0\u8fc7\u540e\uff0c\u4e1d\u6beb\u65e0\u4e8b\u3002\u7b2c\u4e8c\u5929\uff0c\u5927\u5c06\u519b\u4e0a\u671d\uff0c\u7687 \u5e1d\u7b11\u9053\uff1a\u2018\u537f\u59bb\u5982\u6b64\uff0c\u8054\u4ea6\u65e0\u6cd5\uff01\u7acb\u59be\u4e4b\u4e8b\uff0c\u53ea\u597d\u4f11\u63d0\u3002\u2019\u5927\u5c06\u519b\u4ecd\u662f\u5fe7\u5fc3\u51b2\u51b2\uff0c\u95ee\u9053\uff1a \u2018\u7687\u4e0a\u4e0d\u662f\u8981\u8d50\u81e3\u59bb\u81ea\u5c3d\u5417\uff1f\u2019\u7687\u5e1d\u7b11\u9053\uff1a\u2018\u8054\u867d\u65e0\u9053\uff0c\u600e\u80fd\u64c5\u6740\u529f\u81e3\u4e4b\u59bb\uff1f\u6628\u5929\u5979\u5403\u7684\u4e43 \u662f\u918b\u5440\uff01\u2019\u201d \u3000\u3000\u674e\u6cbb\u8bf4\u5b8c\u4e4b\u540e\uff0c\u4f17\u4eba\u54c8\u54c8\u5927\u7b11\u3002\u5510\u6653\u6f9c\u7a81\u7136\u5750\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u83ab\u975e\u96cd\u6b63\u5f53\u65e5\u7ed9\u6211\u996e\u7684\uff0c\u4e5f \u4e0d\u662f\u6bd2\u9152\u4e48\uff1f\u201d \u3000\u3000\u674e\u6cbb\u7b11\u9053\uff1a\u201c\u4ee5\u524d\u6211\u4e0d\u6562\u65ad\u5b9a\uff0c\u73b0\u5728\u8bd5\u4e86\uff0c\u6211\u6562\u8bf4\u90a3\u7edd\u4e0d\u662f\u6bd2\u9152\uff01\u6211\u521a\u624d\u7ed9\u4f60\u5403\u7684\u4e5f\u5e76 \u4e0d\u662f\u7075\u4e39\u3002\u201d \u3000\u3000\u51af\u745b\u5947\u9053\uff1a\u201c\u4f60\u600e\u4e48\u63a8\u65ad\u51fa\u6765\uff1f\u201d \u3000\u3000\u674e\u6cbb\u9053\uff1a\u201c\u96cd\u6b63\u8fd9\u4e2a\u72d7\u7687\u5e1d\uff0c\u5176\u5b9e\u662f\u4e2a\u6700\u9634\u9669\u7684\u5c0f\u4eba\uff0c\u4ed6\u5f53\u521d\u6015\u6653\u6f9c\u548c\u4f60\u5c06\u4ed6\u9634\u8c0b\u593a\u5ae1 \u4e4b\u4e8b\u6cc4\u9732\u51fa\u53bb\uff0c\u6240\u4ee5\u65bd\u7528\u8fd9\u4e2a\u9b3c\u8ba1\u3002\u8bd5\u60f3\u5bab\u4e2d\u7eb5\u6709\u8fd9\u79cd\u602a\u836f\uff0c\u4ed6\u53c8\u5c82\u4f1a\u968f\u8eab\u643a\u5e26\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u4e3a\u4ec0\u4e48\u8fd9\u51e0\u5929\u6211\u53c8\u786e\u5982\u91cd\u75c5\uff1f\u201d \u3000\u3000\u674e\u6cbb\u9053\uff1a\u201c\u8bd5\u60f3\u90a3\u4f4d\u5927\u5c06\u519b\u7684\u59bb\u5b50\uff0c\u53d7\u5413\u4e0d\u8fc7\u4e00\u5929\uff0c\u56de\u5bb6\u4e4b\u540e\uff0c\u5c1a\u81ea\u5fc3\u60ca\u80c6\u6218\u3002\u4f55\u51b5\u4f60 \u5728\u8fd9\u4e00\u5e74\u4e4b\u4e2d\uff0c\u65e0\u65f6\u65e0\u523b\uff0c\u7cbe\u795e\u4e0d\u53d7\u5a01\u534f\uff0c\u5fc3\u4e2d\u65e2\u4e0d\u6562\u6000\u7591\u6240\u996e\u7684\u4e0d\u662f\u6bd2\u9152\uff0c\u81ea\u7136\u76f8\u4fe1\u4ed6\u7684 \u6050\u5413\u4e4b\u8a00\uff0c\u4f60\u867d\u7136\u4e0d\u6015\u6b7b\uff0c\u4f46\u5fc3\u4e2d\u5df2\u5b58\u4e86\u4e2a\u67d0\u65e5\u67d0\u65f6\u5fc5\u6b7b\u7684\u5ff5\u5934\uff0c\u56e0\u6b64\u4e34\u5230\u4e86\u8fd9\u4e2a\u671f\u9650\uff0c\u5fc3 \u7075\u81ea\u7136\u53d7\u4e86\u4ed6\u7684\u63a7\u5236\uff0c\u751f\u673a\u505c\u987f\uff0c\u53c8\u600e\u80fd\u4e0d\u5982\u91cd\u75c5\uff1f\u5e78\u800c\u4f60\u4e0d\u6bd4\u5e38\u4eba\uff0c\u8981\u4e0d\u7136\u53ea\u6050\u672a\u5230\u671f \u9650\uff0c\u5c31\u88ab\u4ed6\u5413\u6b7b\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u6697\u6697\u9053\u58f0\u60ed\u6127\uff01\u7518\u51e4\u6c60\u65e9\u5df2\u8d70\u4e86\u8fdb\u6765\uff0c\u542c\u4e86\u674e\u6cbb\u8fd9\u756a\u8bdd\u540e\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u501f\u751f\u754f\u6b7b \u4e43\u4eba\u4e4b\u5e38\u60c5\u3002\u6211\u8f88\u4fa0\u58eb\u4e4b\u4e0d\u754f\u6b7b\u8005\uff0c\u4e43\u662f\u56e0\u4e49\u4e4b\u6240\u5728\uff0c\u6545\u613f\u820d\u8eab\u4ee5\u8d74\u3002\u5fc3\u4e2d\u81ea\u6709\u4e00\u76ee\u7684\u5728\u3002 \u90a3\u4f4d\u5c06\u519b\u7684\u59bb\u5b50\uff0c\u56e0\u5992\u800c\u4e0d\u754f\u6b7b\uff0c\u4e0e\u6211\u8f88\u867d\u4e0d\u80fd\u6bd4\uff0c\u4f46\u7a76\u5176\u6839\u6e90\uff0c\u4ea6\u662f\u6709\u4e00\u76ee\u7684\u5728\uff0c\u82e5\u65e0\u56e0 \u800c\u6b7b\uff0c\u51a4\u5c48\u800c\u6b7b\uff0c\u82e5\u8bf4\u5c1a\u80fd\u5766\u7136\u89c6\u4e4b\uff0c\u90a3\u5c31\u975e\u4eba\u4e4b\u5e38\u60c5\u4e86\u3002\u201d \u3000\u3000\u674e\u6cbb\u53c8\u9053\uff1a\u201c\u6211\u6700\u521d\u4e5f\u60f3\u4e0d\u51fa\u6240\u4ee5\u7136\u6765\uff0c\u540e\u6765\u63a8\u6572\u5e9f\u56ed\u8001\u4eba\u6240\u7528\u7684\u836f\uff0c\u53ea\u662f\u5b81\u795e\u9759\u6c14\uff0c \u5e76\u975e\u89e3\u6bd2\u7597\u4f24\uff0c\u8fd9\u624d\u6562\u5927\u80c6\u6000\u7591\uff0c\u7136\u540e\u5c0f\u5fc3\u6c42\u8bc1\u3002\u5e9f\u56ed\u8001\u4eba\u56e0\u4e3a\u6ca1\u6709\u4eb2\u81ea\u628a\u8109\uff0c\u6240\u4ee5\u5c1a\u4e0d\u6562 \u786e\u5207\u5224\u65ad\uff0c\u53ea\u5728\u5b81\u795e\u89e3\u8e81\u4e0a\u5934\u53bb\u60f3\uff1b\u800c\u6211\u5219\u5728\u89e3\u9664\u5510\u5144\u7684\u7cbe\u795e\u6050\u60e7\u4e0a\u53bb\u60f3\u3002\u8fd9\u4e5f\u6b63\u662f\u53e4\u4eba\u6240 \u8bf4\u5fc3\u75c5\u8fd8\u987b\u5fc3\u836f\u533b\u7684\u9053\u7406\u3002\u51af\u745b\u662f\u4f60\u4eb2\u8fd1\u76f8\u4fe1\u4e4b\u4eba\uff0c\u5979\u8bf4\u90a3\u9999\u7070\u4e4b\u6c34\u4e43\u662f\u7075\u4e39\uff0c\u4f60\u4e5f\u4fbf\u4f1a\u5f53 \u5b83\u662f\u7075\u4e39\u4e86\u3002\u201d \u3000\u3000\u767d\u6cf0\u5b98\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6709\u4e9b\u795e\u68cd\uff0c\u5229\u7528\u9999\u7070\u6c34\u9a97\u4eba\u3002\u4e00\u5b9a\u662f\u6709\u4e9b\u8f7b\u75c5\u7684\u4eba\uff0c\u672c \u6765\u4e0d\u7528\u836f\u5c31\u53ef\u4ee5\u597d\u7684\uff0c\u5403\u4e86\u4ed6\u7684\u9999\u7070\u6c34\u540e\uff0c\u56e0\u4e3a\u4fe1\u5b83\u80fd\u591f\u6cbb\u75c5\u66f4\u6613\u597d\u4e86\uff0c\u4e8e\u662f\u4fbf\u4e3a\u4ed6\u9882\u626c\u3002 \u53ea\u53ef\u60dc\u90a3\u4e9b\u91cd\u75c5\u7684\uff0c\u5403\u4e86\u9999\u7070\u6c34\u6b7b\u53bb\uff0c\u522b\u4eba\u53cd\u4ee5\u4e3a\u4ed6\u547d\u8be5\u5982\u6b64\uff0c\u4e0d\u53bb\u602a\u90a3\u795e\u68cd\u3002\u201d\u4f17\u4eba\u90fd\u7b11 \u8d77\u6765\u3002 \u3000\u3000\u5510\u6653\u6f9c\u5374\u5728\u7b11\u58f0\u4e2d\u6c89\u601d\u9ed8\u60f3\uff0c\u5f85\u4f17\u4eba\u7b11\u8fc7\u4e4b\u540e\uff0c\u6602\u5934\u8bf4\u9053\uff1a\u201c\u96cd\u6b63\u72d7\u7687\u5e1d\u8fd9\u4e00\u624b\u786e\u662f\u72e0 \u8fa3\uff0c\u6211\u4e5f\u731c\u5230\u4ed6\u7684\u7528\u5fc3\u4e86\u3002\u4ed6\u7ed9\u6211\u89c4\u5b9a\u671f\u9650\u4e00\u5e74\uff0c\u8981\u6211\u5230\u65f6\u81f3\u5bab\u4e2d\u6c42\u4ed6\u89e3\u836f\uff0c\u4ed6\u5219\u5728\u8fd9\u4e00\u5e74 \u4e2d\u5e03\u7f6e\uff0c\u526a\u9664\u4f17\u7687\u5b50\u7684\u7fbd\u7ffc\u3002\u82e5\u5230\u65f6\u4ed6\u5b9d\u5ea7\u5df2\u56fa\uff0c\u4e0d\u518d\u6015\u6211\u6cc4\u9732\u79d8\u5bc6\uff0c\u5c31\u53ef\u5c06\u6211\u6740\u6389\u3002\u82e5\u8fd8 \u672a\u56fa\uff0c\u90a3\u5c31\u80e1\u4e71\u7ed9\u6211\u98df\u201c\u89e3\u836f\u2019\uff0c\u518d\u65bd\u6545\u6280\uff0c\u5c06\u2018\u6b7b\u671f\u2019\u5ef6\u957f\u4e00\u5e74\uff0c\u8fd9\u6837\u6211\u5c31\u4e0d\u80fd\u8131\u4ed6\u638c\u63e1 \u4e86\u3002\u800c\u4e14\u4ed6\u53c8\u53ef\u85c9\u6b64\u9a97\u745b\u59b9\u5165\u5bab\uff0c\u6b63\u662f\u4e00\u77f3\u4e24\u9e1f\u4e4b\u8ba1\u3002\u201d\u51af\u745b\u60f3\u8d77\u524d\u51e0\u65e5\u81ea\u5df1\u5192\u9669\u5165\u5bab\uff0c\u51c6 \u5907\u727a\u7272\u81ea\u5df1\u4ee5\u6551\u6653\u6f9c\u771f\u662f\u5e7c\u7a1a\u611a\u6627\uff0c\u5fc3\u4e2d\u6697\u53eb\u597d\u9669\u3002 \u3000\u3000\u5510\u6653\u6f9c\u867d\u544a\u65e0\u4e8b\uff0c\u53ef\u662f\u8fd9\u4e03\u5929\u6765\u7720\u98df\u4e0d\u5b89\uff0c\u8eab\u5b50\u4ecd\u662f\u8106\u5f31\u3002\u674e\u6cbb\u4fbf\u7528\u5e9f\u56ed\u8001\u4eba\u65b9\u5b50\u4e2d\u6240 \u5f00\u7684\u90a3\u4e9b\u666e\u901a\u7684\u5b81\u795e\u4e4b\u836f\uff0c\u53eb\u4eba\u5230\u9644\u8fd1\u5c0f\u57ce\u9547\u4e2d\u91c7\u529e\uff0c\u714e\u7ed9\u5510\u6653\u6f9c\u670d\u3002\u5510\u6653\u6f9c\u8fd9\u4e00\u5e74\u6765\u5185\u529f \u5927\u6709\u8fdb\u5883\uff0c\u836f\u7597\u81ea\u7597\u53cc\u7ba1\u9f50\u4e0b\uff0c\u6599\u60f3\u5728\u4e00\u4e8c\u65e5\u95f4\uff0c\u4fbf\u53ef\u6062\u590d\u5982\u521d\u3002 \u3000\u3000\u5230\u4e86\u786e\u77e5\u5510\u6653\u6f9c\u5e73\u5b89\u65e0\u4e8b\u4e4b\u540e\uff0c\u51af\u745b\u8fd9\u624d\u89c9\u5f97\u81ea\u5df1\u5934\u6655\u76ee\u7729\uff0c\u75b2\u7d2f\u6b32\u6b7b\uff0c\u6b63\u60f3\u53bb\u7761\uff0c\u5ffd \u7136\u53c8\u60f3\u8d77\u4e00\u4e8b\uff0c\u4e3e\u76ee\u56db\u671b\uff0c\u5ea7\u4e2d\u4e0d\u89c1\u4e00\u4eba\u3002 \u3000\u3000\u51af\u745b\u60f3\u8d77\u56db\u65e5\u4e4b\u524d\uff0c\u4ed6\u548c\u5415\u56db\u5a18\u5206\u9053\u6c42\u533b\uff0c\u5f53\u65f6\u66fe\u7ea6\u597d\u4e0d\u8bba\u6c42\u5f97\u4e0e\u5426\uff0c\u90fd\u56de\u897f\u5c71\u76f8\u89c1\uff0c \u5415\u56db\u5a18\u8f7b\u529f\u6bd4\u5979\u9ad8\u660e\u8bb8\u591a\uff0c\u7167\u7406\u65e9\u5e94\u56de\u6765\uff0c\u4f46\u5374\u4e0d\u89c1\u5979\u5728\u6b64\uff0c\u83ab\u975e\u53c8\u51fa\u4e86\u4ec0\u4e48\u4e8b\u4e86\uff1f\u4e0d\u7981\u95ee \u9053\uff1a\u201c\u5415\u59d0\u59d0\u5462\uff1f\u201d\u7518\u51e4\u6c60\u8be7\u9053\uff1a\u201c\u6211\u6b63\u8981\u95ee\u4f60\u5462\uff01\u201d\u51af\u745b\u9053\uff1a\u201c\u5979\u672a\u56de\u6765\u4e48\uff1f\u201d\u7518\u51e4\u6c60 \u9053\uff1a\u201c\u6211\u6b63\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u4f60\u548c\u5979\u540c\u53bb\uff0c\u5374\u548c\u8fd9\u4f4d\u5927\u54e5\u4e00\u540c\u56de\u6765\u3002\u201d \u3000\u3000\u674e\u6cbb\u8fd9\u65f6\u624d\u548c\u4f17\u4eba\u4e92\u901a\u59d3\u540d\uff0c\u4e00\u8bf4\u51fa\u6765\uff0c\u5f7c\u6b64\u90fd\u8bc6\u3002\u51af\u745b\u4e5f\u5c06\u5415\u56db\u5a18\u548c\u5979\u5206\u9053\u6c42\u533b\u4e4b\u4e8b \u8bf4\u4e86\uff0c\u7518\u51e4\u6c60\u5927\u4e3a\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u4ee5\u516b\u59b9\u548c\u5510\u6653\u6f9c\u7684\u4ea4\u60c5\uff0c\u7eb5\u5979\u4e2d\u9014\u53e6\u6709\u4ed6\u4e8b\u7f20\u7eca\uff0c\u4e5f\u4f1a\u6446\u8131 \u8d76\u56de\u6765\u7684\uff0c\u800c\u4eca\u4e0d\u89c1\u56de\u6765\uff0c\u96be\u9053\u662f\u9047\u4e0a\u66f4\u7d27\u8981\u4e4b\u4e8b\uff1f\u6216\u8005\u662f\u78b0\u4e0a\u5f3a\u654c\u8131\u8eab\u4e0d\u5f97\u4e48\uff1f\u4f46\u516b\u59b9\u8f7b \u529f\u7edd\u9876\uff0c\u5251\u672f\u65e0\u53cc\uff0c\u7167\u7406\u4ea6\u4e0d\u4f1a\u9047\u96be\u3002\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002 \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u591a\u65e5\u6765\u5415\u59d0\u59d0\u4e3a\u6211\u5fe7\u52b3\u5954\u8dd1\uff0c\u6211\u8fd8\u672a\u5f97\u5411\u5979\u9053\u8c22\u3002\u4eca\u65e5\u5979\u82e5\u4e0d\u56de\u6765\uff0c\u660e\u65e5 \u6211\u5c31\u548c\u4f60\u4e00\u540c\u53bb\u627e\u5979\u5427\uff01\u201d \u3000\u3000\u51af\u745b\u6b47\u4e86\u4e00\u665a\uff0c\u7b2c\u4e8c\u65e5\u5c06\u5728\u516b\u8fbe\u5cad\u4e0a\u5de7\u9047\u5916\u516c\u6bcd\u59b9\uff0c\u4e00\u5bb6\u56e2\u805a\u7b49\u60c5\u4e8b\u8bf4\u4e0e\u5510\u6653\u6f9c\u542c\uff0c\u5510 \u6653\u6f9c\u542c\u5f97\u7709\u5f00\u773c\u7b11\uff0c\u9053\uff1a\u201c\u771f\u662f\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\uff0c\u4eca\u5929\u6211\u624d\u628a\u5341\u591a\u5e74\u6765\u7684 \u5fc3\u4e8b\u5168\u653e\u4e0b\u4e86\u3002\u201d\u60f3\u8d77\u5e08\u5ac2\u909d\u7ec3\u971e\uff0c\u66f4\u6068\u4e0d\u5f97\u7acb\u65f6\u76f8\u89c1\u3002\u51af\u745b\u9053\uff1a\u201c\u5988\u4e5f\u5f88\u6302\u5ff5\u4f60\u3002\u5979\u6b22\u559c \u4f60\u5462\uff01\u201d\u8bf4\u7f62\u4f4e\u7709\u4e00\u7b11\u3002 \u3000\u3000\u5510\u6653\u6f9c\u95ee\u9053\uff1a\u201c\u5415\u56db\u5a18\u53bb\u7684\u5730\u65b9\u79bb\u516b\u8fbe\u5cad\u8fdc\u5417\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u5979\u53bb\u627e\u5e9f\u56ed\u8001\u4eba\u7684\u597d\u53cb\u9648 \u753b\u5e08\uff0c\u5c31\u5728\u516b\u8fbe\u5cad\u4e1c\u9762\u7684\u5eb7\u5e84\u3002\u5988\u4f4f\u7684\u5c3c\u5eb5\u5728\u516b\u8fbe\u5cad\u7684\u5317\u5cf0\u3002\u5f53\u65e5\u6211\u53bb\u627e\u5e9f\u56ed\u8001\u4eba\u6240\u4f4f\u7684\u5357 \u53e3\uff0c\u5219\u5728\u516b\u8fbe\u5cad\u897f\u9762\u3002\u8ddd\u79bb\u90fd\u4e0d\u8fdc\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u90a3\u4e48\u6211\u548c\u7518\u5927\u4fa0\u627e\u5230\u4e86\u5415\u56db\u5a18\u540e\uff0c\u518d\u53bb \u770b\u671b\u4f60\u4eec\u3002\u201d\u5ffd\u7136\u60f3\u8d77\u5e08\u5ac2\u5f53\u5e74\u53eb\u4ed6\u505a\u5c0f\u5f1f\u5f1f\u7684\u60c5\u666f\uff0c\u4e0d\u89c9\u4e00\u9635\u9762\u7ea2\u3002\u51af\u745b\u731c\u5230\u4ed6\u5fc3\u4e2d\u6240 \u60f3\uff0c\u53c8\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5148\u548c\u674e\u54e5\u54e5\u8d76\u56de\u5c3c\u5eb5\uff0c\u544a\u8bc9\u5988\u5988\u77e5\u9053\uff0c\u597d\u8ba9\u5979\u63a5\u4f60\u3002\u201d \u3000\u3000\u51af\u7433\u6062\u590d\u4e86\u8bb0\u5fc6\u4e4b\u540e\uff0c\u5fc3\u7075\u8d8a\u53d1\u5f00\u6717\uff0c\u6d3b\u6cfc\u66f4\u80dc\u4ece\u524d\u3002\u5979\u548c\u6bcd\u4eb2\u5bf9\u674e\u6cbb\u7684\u533b\u9053\u90fd\u575a\u4fe1\u65e0 \u7591\uff0c\u56e0\u6b64\u4e5f\u4e0d\u4f3c\u51af\u745b\u670d\u4e48\u4e3a\u5510\u6653\u6f9c\u4e4b\u75c5\u800c\u70e6\u607c\u3002 \u3000\u3000\u909d\u740f\u548c\u5f20\u5929\u6c60\u5012\u662f\u6709\u4e9b\u70e6\u607c\uff0c\u4ed6\u4eec\u5341\u51e0\u4e2a\u4eba\u6324\u5728\u5c3c\u5eb5\u4e4b\u5185\uff0c\u5176\u4e2d\u53c8\u534a\u6570\u53d7\u4f24\u672a\u6108\uff0c\u751a\u6015 \u5b98\u519b\u6765\u88ad\uff0c\u56e0\u6b64\u4fbf\u53eb\u51af\u7433\u65f6\u4e0d\u65f6\u5230\u5c71\u53e3\u4e86\u671b\u3002 \u3000\u3000\u8fc7\u4e86\u4e24\u4e2a\u767d\u5929\uff0c\u5e78\u544a\u65e0\u4e8b\u3002\u7b2c\u4e09\u65e5\u65e9\u996d\u65f6\u5206\uff0c\u4f17\u4eba\u6b63\u5728\u5f04\u996d\u3001\u5f20\u5929\u6c60\u7684\u7435\u7436\u9aa8\u867d\u672a\u63a5 \u5408\uff0c\u4ea6\u53ef\u8d70\u52a8\u3002\u5403\u8fc8\u4e4b\u65f6\uff0c\u5ffd\u7136\u629b\u4e0b\u7897\u7b77\uff0c\u4f0f\u5728\u5730\u4e0a\u3002\u51af\u7433\u5947\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u5929\u4ed6\u5728\u5730\u4e0a\u4f0f\u4e86\u597d\u4e45\uff0c\u7ad9\u8d77\u6765\u9053\uff1a\u2018\u6709\u597d\u51e0\u7c07\u4eba\u9a6c\uff0c\u4f3c\u662f\u5206\u6279\u5728\u5cad\u7684\u4e1c\u9762\u75be\u9a70\u800c\u8fc7\uff0c \u597d\u5728\u4ed6\u4eec\u4e0d\u662f\u4e0a\u5c71\uff0c\u4f46\u4ea6\u4e0d\u53ef\u4e0d\u9632\u3002\u7433\u59d1\u5a18\uff0c\u53ea\u597d\u53c8\u9ebb\u70e6\u4f60\u4e00\u6b21\uff0c\u4f60\u5230\u4e1c\u9762\u5c71\u53e3\u770b\u770b\uff0c\u770b\u5916 \u9762\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u90a3\u4e9b\u4eba\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5f20\u5929\u6c60\u662f\u4e2a\u6c5f\u6e56\u5927\u76d7\uff0c\u4f0f\u5730\u542c\u58f0\u4e4b\u6280\u767e\u4e0d\u5931\u4e00\u3002\u51af\u7433\u5230\u4e1c\u9762\u5c71\u53e3\u4e86\u671b\uff0c\u679c\u7136\u89c1\u5c71\u811a\u5e95 \u4e0b\uff0c\u65f6\u4e0d\u65f6\u6709\u4e09\u4e94\u9a91\u9a6c\uff0c\u98de\u9a70\u800c\u8fc7\uff0c\u770b\u6837\u5b50\u4f3c\u662f\u516c\u5dee\u3002\u8fc7\u4e86\u597d\u4e45\uff0c\u516c\u5dee\u8fc7\u5c3d\uff0c\u51af\u7433\u6b63\u60f3\u56de \u53bb\uff0c\u5ffd\u89c1\u53c8\u6709\u51e0\u9a91\u9a6c\u5982\u98de\u800c\u6765\u3001\u770b\u6e05\u695a\u65f6\uff0c\u524d\u9762\u4e00\u9a91\uff0c\u7adf\u7136\u662f\u4e2a\u5973\u5b50\uff0c\u767d\u9a6c\u7ea2\u88f3\uff0c\u5341\u5206\u62a2 \u773c\u3002\u540e\u9762\u6709\u4e09\u9a91\u516c\u5dee\u7d27\u8ffd\uff0c\u90a3\u5973\u5b50\u9a91\u672f\u751a\u7cbe\uff0c\u53ef\u540e\u9762\u90a3\u4e09\u4eba\u4ea6\u662f\u4e0d\u5f31\uff0c\u8ffd\u5230\u5c71\u4e0b\uff0c\u90a3\u7ea2\u8863\u5973 \u9a91\u58eb\u7eb5\u9a6c\u7a9c\u5165\u5c71\u8c37\uff0c\u80ef\u4e0b\u5750\u9a91\u5ffd\u7136\u60e8\u5389\u5636\u9e23\uff0c\u56db\u8e44\u5c48\u5730\uff0c\u7ea2\u8863\u5973\u5b50\u4e00\u4e2a\u7ffb\u8eab\uff0c\u8df3\u4e0b\u9a6c\u80cc\uff0c\u62c9 \u5f00\u5f39\u5f13\uff0c\u671d\u8c37\u53e3\u8ffd\u5175\u4e71\u6253\u3002\u90a3\u4e09\u540d\u516c\u5dee\u4e5f\u90fd\u8df3\u4e0b\u4e86\u9a6c\uff0c\u62d4\u51fa\u5175\u5668\u62e8\u6253\u5f39\u4e38\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5c31\u5c06 \u90a3\u5973\u5b50\u56f4\u4f4f\u4e86\u3002 \u3000\u3000\u51af\u7433\u4e0d\u89c9\u7b11\u51fa\u58f0\u6765\uff0c\u5fc3\u9053\uff1a\u539f\u6765\u53c8\u662f\u8fd9\u4e2a\u5a46\u5a18\uff0c\u524d\u6b21\u5728\u5ba2\u5e97\u4e2d\u78b0\u5230\u5979\u548c\u516c\u5dee\u6253\u67b6\uff0c\u73b0\u5728 \u53c8\u91cd\u6f14\u4e86\u3002\u6211\u6b63\u8981\u627e\u5979\uff0c\u8fd9\u5c82\u4e0d\u662f\u9001\u4e0a\u95e8\u5417\uff1f\u5514\uff0c\u5979\u7684\u529f\u592b\u4f3c\u4e4e\u6bd4\u4ee5\u524d\u9ad8\u660e\u4e00\u4e9b\u4e86\uff1b\u4f46\u8fd9\u4e09 \u4e2a\u516c\u5dee\u5374\u4e5f\u4e0d\u4f3c\u666e\u901a\u516c\u5dee\uff01 \u3000\u3000\u90a3\u5973\u5b50\u4e00\u5251\u529b\u654c\u4e09\u4eba\uff0c\u6e10\u9732\u8d25\u8c61\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u597d\u5927\u80c6\u5b50\uff0c\u4f60\u4eec\u672a\u542c\u8fc7\u94c1\u638c\u795e\u5f39\u6768 \u4ef2\u82f1\u7684\u540d\u5b57\u5417\uff1f\u6211\u5c31\u662f\u4ed6\u7684\u5973\u513f\uff01\u201d\u6768\u4ef2\u82f1\u662f\u5317\u4e94\u7701\u6b66\u6797\u9886\u8896\uff0c\u4e0d\u8bba\u9ed1\u9053\u767d\u9053\uff0c\u5168\u90e8\u7ed9\u4ed6\u51e0 \u5206\u60c5\u9762\uff0c\u58f0\u540d\u4e4b\u54cd\uff0c\u5c31\u5982\u7518\u51e4\u6c60\u4e4b\u5728\u5357\u65b9\u4e00\u6837\u3002\u4e0d\u6599\u8fd9\u51e0\u4e2a\u4eba\u542c\u4e86\uff0c\u5374\u662f\u4e00\u9635\u54c8\u54c8\u5927\u7b11\uff0c\u5176 \u4e2d\u4e00\u4eba\u5927\u7b11\u9053\uff1a\u201c\u6768\u8001\u5934\u513f\u5417\uff0c\u54b1\u4eec\u6b63\u60f3\u548c\u4ed6\u5bf9\u4eb2\u5bb6\uff0c\u5582\uff0c\u5c0f\u5a18\u5b50\uff0c\u6211\u770b\u4f60\u4e00\u8def\u5728\u9a6c\u4e0a\u5782 \u6cea\uff0c\u5341\u4e5d\u662f\u6b7b\u4e86\u5973\u5a7f\uff0c\u672a\u5f97\u65b0\u4eba\uff0c\u4f60\u81ea\u5df1\u6311\u9009\u5427\uff0c\u6211\u4eec\u4e09\u5144\u5f1f\u4f60\u559c\u6b22\u54ea\u4e2a\uff1f\u201d\u6768\u67f3\u9752\u5927\u6012\uff0c \u624b\u4e2d\u5251\u4e00\u9635\u6cfc\u98ce\u523a\u6740\uff0c\u90a3\u4e09\u4eba\u56f4\u7740\u5979\u5b09\u7b11\u620f\u5f04\uff0c\u7f20\u6597\u751a\u7d27\u3002 \u3000\u3000\u539f\u6765\u5728\u5341\u591a\u5929\u524d\uff0c\u7518\u51e4\u6c60\u53d7\u5510\u6653\u6f9c\u4e4b\u6258\uff0c\u6d3e\u9063\u5feb\u9a6c\u4f20\u4e66\uff0c\u5411\u6768\u4ef2\u82f1\u62a5\u544a\u6076\u8017\uff0c\u5510\u6653\u6f9c \u201c\u9057\u4e66\u201d\u4e2d\u5148\u8bf4\u81ea\u5df1\u8eab\u53d7\u5927\u6069\uff0c\u65e0\u53ef\u62a5\u7b54\uff0c\u7ee7\u9053\u73b0\u5728\u906d\u9047\u5947\u7978\uff0c\u5fc5\u6b7b\u65e0\u7591\uff0c\u7136\u540e\u5411\u4ed6\u4eec\u7236\u5973 \u81f4\u6b49\uff0c\u8bf7\u6c42\u89e3\u9664\u5a5a\u7ea6\uff0c\u514d\u8bef\u6768\u67f3\u9752\u9752\u6625\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u8bfb\u4e86\u6b64\u4fe1\uff0c\u5927\u60ca\u5931\u8272\uff0c\u53ef\u662f\u4ed6\u5df2\u6b8b\u5e9f\uff0c\u672a\u80fd\u8d70\u52a8\uff0c\u53ea\u597d\u53eb\u6768\u67f3\u9752\u98de\u9a91\u5165\u4eac\uff0c\u63a2\u542c \u6d88\u606f\uff0c\u4e95\u4ea4\u4ee3\u5979\u9053\uff1a\u201c\u82e5\u4ed6\u672a\u6b7b\uff0c\u4f60\u53ef\u5728\u4eac\u4e2d\u5e7f\u5ef6\u540d\u533b\uff0c\u66ff\u4ed6\u8bca\u6cbb\uff1b\u82e5\u7136\u5df2\u6b7b\uff0c\u4e5f\u8981\u5c06\u4ed6\u7684 \u9ab8\u9aa8\u5e26\u56de\u6765\u3002\u201d\u56e0\u6b64\u53c8\u7ed9\u4e86\u5979\u51e0\u5341\u4e2a\u91d1\u5143\u5b9d\uff0c\u51c6\u5907\u4f5c\u5ef6\u8bf7\u540d\u533b\u4e4b\u7528\u3002\u5e76\u5199\u4e86\u597d\u51e0\u5c01\u4fe1\uff0c\u4ecb\u7ecd \u4ed6\u89c1\u4eac\u57ce\u7684\u6b66\u6797\u540d\u5bbf\u3002 \u3000\u3000\u5c71\u4e1c\u5411\u51fa\u597d\u9a6c\uff0c\u6768\u67f3\u9752\u90a3\u5339\uff0c\u5c24\u5176\u662f\u5343\u4e2d\u9009\u4e00\u7684\u597d\u9a6c\uff0c\u65e5\u884c\u4e94\u767e\u4f59\u91cc\u3002\u6768\u67f3\u9752\u9a6c\u4e0d\u505c \u8e44\uff0c\u98de\u9a70\u5165\u4eac\uff0c\u53ef\u662f\u56e0\u63a5\u4fe1\u8fc7\u8fdf\uff0c\u5230\u4e86\u6000\u67d4\uff0c\u5df2\u8fc7\u671f\u4e09\u65e5\uff0c\u6768\u67f3\u9752\u4e00\u60f3\u81ea\u5df1\u5230\u65f6\uff0c\u5510\u6653\u6f9c\u4e5f \u8bb8\u5df2\u7ecf\u5165\u6b93\uff0c\u4ece\u6b64\u4e0d\u80fd\u4e0e\u4ed6\u76f8\u89c1\uff0c\u6781\u662f\u4f24\u5fc3\uff0c\u653e\u9a6c\u98de\u9a70\uff0c\u6f78\u7136\u6ce3\u4e0b\u3002 \u3000\u3000\u50cf\u5979\u90a3\u6837\u7684\u5355\u8eab\u5973\u5b50\uff0c\u7eb5\u9a6c\u98de\u5954\uff0c\u5df2\u662f\u60f9\u4eba\u6ce8\u89c6\uff0c\u4f55\u51b5\u5979\u53c8\u5728\u9a6c\u4e0a\u7559\u6cea\uff0c\u795e\u6e05\u5f02\u5e38\uff0c\u66f4 \u5f15\u8d77\u4eba\u4eec\u63e3\u6d4b\u3002 \u3000\u3000\u8fd9\u65e5\uff0c\u6b63\u5de7\u6709\u4e00\u5e2e\u8840\u6ef4\u5b50\u548c\u7981\u536b\u519b\u5934\u76ee\u5230\u5357\u53e3\u5916\u9762\u529e\u6848\uff0c\u4ed6\u4eec\u8981\u8ffd\u6355\u4e00\u4e2a\u6781\u91cd\u8981\u7684\u94a6 \u72af\uff0c\u672c\u6765\u65e0\u6687\u7406\u4f1a\u95f2\u4eba\u3002\u4f46\u5176\u4e2d\u6709\u4e09\u4eba\u662f\u91c7\u82b1\u8d3c\u51fa\u8eab\uff0c\u89c1\u6768\u67f3\u9752\u7f8e\u8c8c\uff0c\u800c\u4e14\u9a6c\u4e0a\u9a6e\u7684\u53c8\u770b\u5f97 \u51fa\u662f\u91d1\u94f6\u5b9d\u8d1d\uff0c\u9042\u52a8\u4e86\u52ab\u8d22\u52ab\u8272\u4e4b\u5ff5\uff0c\u56e0\u6b64\u64c5\u79bb\u5927\u961f\uff0c\u7d27\u7d27\u8ffd\u8e2a\uff0c\u5148\u7528\u6697\u5668\u5c06\u5979\u7684\u767d\u9a6c\u5c04 \u6740\uff0c\u7136\u540e\u56f4\u653b\u3002 \u3000\u3000\u6768\u67f3\u9752\u6b63\u5728\u5403\u7d27\uff0c\u5ffd\u542c\u5f97\u4e00\u9635\u201c\u683c\u683c\u201d\u7684\u7b11\u58f0\uff0c\u51af\u7433\u4f3c\u4e00\u56e2\u98ce\u7684\u4ece\u5c71\u4e0a\u98de\u63a0\u4e0b\u6765\uff0c\u624b\u573a \u5904\uff0c\u4e09\u67c4\u593a\u547d\u795e\u5200\u7834\u7a7a\u5c04\u51fa\uff0c\u8fd9\u4e09\u4e2a\u8840\u6ef4\u5b50\u5c0f\u5934\u76ee\u867d\u975e\u5eb8\u624b\uff0c\u4f46\u5374\u600e\u80fd\u9632\u8fd9\u79cd\u72ec\u95e8\u7684\u6b79\u6bd2\u6697 \u5668\uff0c\u98de\u5200\u75be\u52b2\uff0c\u8fdc\u975e\u5c0f\u5c0f\u7684\u5f39\u4e38\u53ef\u6bd4\uff0c\u5176\u4e2d\u4e00\u4eba\u6b66\u529f\u8f83\u4f4e\uff0c\u98de\u5200\u6765\u65f6\uff0c\u4f0f\u8eab\u4e00\u95ea\uff0c\u88ab\u98de\u5200\u4ece \u9888\u9879\u7a7f\u51fa\uff0c\u767b\u65f6\u6bd9\u547d\uff01\u53e6\u4e00\u4eba\u7528\u5200\u78b0\u78d5\uff0c\u98de\u5200\u4ece\u65c1\u6fc0\u51fa\uff0c\u4f24\u4e86\u4ed6\u7684\u80a9\u81c2\uff1b\u5269\u4e0b\u7684\u90a3\u4eba\u6b66\u529f\u8f83 \u5f3a\uff0c\u4e00\u78d5\u4e4b\u540e\uff0c\u5373\u7528\u5317\u6d3e\u201c\u71d5\u9752\u5341\u516b\u7ffb\u201d\u7684\u529f\u592b\uff0c\u5728\u5730\u4e0a\u8fde\u6253\u4e86\u51e0\u4e2a\u7b4b\u6597\uff0c\u582a\u582a\u907f\u8fc7\uff0c\u4f46\u5df2 \u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u6025\u6025\u5954\u9003\u3002 \u3000\u3000\u6768\u67f3\u9752\u597d\u751f\u60ca\u8bb6\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u51af\u7433\u7b11\u563b\u563b\u7684\u7ad9\u5728\u5979\u7684\u9762\u524d\uff0c\u773c\u89d2\u7709\u68a2\uff0c\u9732\u51fa\u4e00\u6d3e\u8f7b \u89c6\u7684\u795e\u6c14\u3002 \u3000\u3000\u6768\u67f3\u9752\u53ea\u9053\u5979\u662f\u51af\u745b\uff0c\u53bb\u5e74\u5979\u88ab\u51af\u745b\u6253\u4e86\u4e00\u4e2a\u8033\u5149\uff0c\u6068\u5728\u5fc3\u5934\uff0c\u8fc4\u672a\u7a0d\u51cf\uff0c\u800c\u4eca\u867d\u5f97\u5979 \u6551\u4e86\u6027\u547d\uff0c\u4f46\u5374\u53c8\u8981\u53d7\u5979\u8f7b\u89c6\uff0c\u6c14\u4e0a\u52a0\u6c14\u3002\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u5979\u603b\u662f\u6551\u4e86\u81ea\u5df1\uff0c\u53ef\u53c8\u4e0d\u597d\u53d1\u4f5c\u3002 \u3000\u3000\u51af\u7433\u7785\u4e86\u6768\u67f3\u9752\u4e00\u9635\uff0c\u7b11\u95ee\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60\u8fd9\u4e48\u6025\u8d76\u8def\u5e72\u561b\uff1f\u516c\u516c\u7684\u75c5\u53ef\u597d\u4e00\u70b9\u4e48\uff1f\u59d1 \u59d1\uff0c\u4f60\u7684\u5f39\u5b50\u53c8\u6bd4\u524d\u6253\u5f97\u9ad8\u660e\u4e86\u3002\u201d\u51af\u7433\u4e24\u5929\u5df2\u4ece\u59d0\u59d0\u53e3\u4e2d\u77e5\u9053\u4e86\u4e00\u5207\u60c5\u5f62\uff0c\u4e0d\u4f8d\u5979\u5148\u51fa \u58f0\uff0c\u4fbf\u5192\u59d0\u59d0\u7684\u8eab\u4efd\u548c\u5979\u8bf4\u8bdd\u3002 \u3000\u3000\u6768\u67f3\u9752\u6c14\u5f80\u4e0a\u51b2\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u660e\u77e5\u6545\u95ee\u3002\u8fd9\u4e00\u5e74\u6765\uff0c\u4f60\u4e0d\u662f\u548c\u4f60\u7684\u53d4 \u53d4\u5728\u4e00\u8d77\u5417\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u662f\u5440\uff01\u6211\u4eec\u671d\u671d\u665a\u665a\u90fd\u5728\u4e00\u5904\uff0c\u5feb\u6d3b\u6781\u4e86\uff01\u201d\u8fd9\u4e00\u4e0b\uff0c\u987f\u4ee4\u6768\u67f3 \u9752\u918b\u6c14\u51b2\u5929\uff0c\u4e0d\u89c9\u4e00\u8fde\u51b7\u7b11\u4e86\u51e0\u58f0\uff0c\u51af\u7433\u7741\u5927\u773c\u775b\u77aa\u5979\uff0c\u6768\u67f3\u9752\u5ba4\u4e86\u4e00\u7a92\uff0c\u4e0d\u6562\u53d1\u4f5c\uff0c\u53c8\u6025 \u4e8e\u77e5\u9053\u5510\u6653\u6f9c\u7684\u6d88\u606f\uff0c\u53ea\u597d\u5fcd\u6c14\u95ee\u9053\uff1a\u201c\u6653\u6f9c\u600e\u4e48\u5566\uff1f\u4f60\u548c\u4ed6\u65e2\u662f\u8fd9\u6837\u8981\u597d\uff0c\u4e3a\u4f55\u5728\u4ed6\u75c5\u5f97 \u8981\u6b7b\u4e4b\u9645\uff0c\u8fd8\u5230\u8fd9\u91cc\u95f2\u901b\uff1f\u201d \u3000\u3000\u51af\u7433\u683c\u683c\u7684\u7b11\u4e2a\u4e0d\u4f11\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u542c\u8c01\u8bf4\u7684\uff1f\u5510\u53d4\u53d4\u6839\u672c\u5c31\u6ca1\u6709\u75c5\uff01\u201d\u51af\u7433\u5fc3\u4e2d\u65e9\u60f3\u597d \u4e86\u4e00\u5957\u8bf4\u8bdd\uff0c\u80e1\u8bf4\u4e00\u901a\uff0c\u5979\u81ea\u5df1\u4e5f\u6599\u4e0d\u5230\u5979\u6240\u8bf4\u7684\u7adf\u662f\u4e8b\u5b9e\u3002 \u3000\u3000\u6768\u67f3\u9752\u60ca\u5947\u4e0d\u5c0f\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u6653\u6f9c\u6839\u672c\u6ca1\u75c5\uff1f\u201d\u51af\u7433\u5e94\u9053\uff1a\u201c\u662f\u5440\uff01\u201d\u6768\u67f3\u9752\u9053\uff1a \u201c\u90a3\u4e48\u4ed6\u53c8\u5199\u4fe1\u7ed9\u6211\u7239\u7239\u8bf4\u662f\u5728\u5927\u524d\u5929\u5c31\u662f\u4ed6\u7684\u6b7b\u671f\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u5df2\u6b7b\u4e86\u5462\uff01\u201d\u51af\u7433\u6545\u4f5c\u60ca \u8bb6\u4e4b\u72b6\uff0c\u9053\uff1a\u201c\u662f\u5417\uff1f\u6211\u6628\u665a\u8fd8\u505a\u6a31\u6843\u871c\u996f\u7ed9\u4ed6\u98df\u5462\uff0c\u54a6\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5199\u90a3\u6837\u7684\u4e00\u5c01\u4fe1\u7ed9 \u4f60\uff1f\u201d\u88c5\u4f5c\u8be7\u5f02\u5bfb\u601d\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u5ffd\u7136\u62cd\u624b\u7b11\u9053\uff1a\u201c\u5475\uff0c\u6211\u77e5\u9053\u4e86\uff0c\u6653\u6f9c\u771f\u574f\uff0c\u4e5f\u4e0d\u544a\u8bc9\u6211 \u4e00\u58f0\u3002\u201d \u3000\u3000\u6768\u67f3\u9752\u542c\u5979\u8bf4\u5f97\u8fd9\u6837\u4eb2\u70ed\uff0c\u201c\u53d4\u53d4\u201d\u4e5f\u4e0d\u53eb\u4e86\uff0c\u6539\u53eb\u201c\u6653\u6f9c\u201d\uff0c\u800c\u4e14\u542c\u5979\u53e3\u6c14\uff0c\u4f3c\u4e4e\u5510 \u6653\u6f9c\u4ec0\u4e48\u4e8b\u60c5\u90fd\u548c\u5979\u5546\u91cf\uff0c\u4e0d\u89c9\u9762\u8272\u5927\u53d8\u3002\u4f46\u4e3a\u4e86\u60f3\u77e5\u9053\u5510\u6653\u6f9c\u4f55\u6545\u8981\u5199\u90a3\u6837\u7684\u4fe1\uff0c\u53ea\u597d\u54ac \u7259\u5fcd\u7740\uff0c\u54bd\u4e86\u597d\u51e0\u53e3\u6c14\uff0c\u6c89\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u51af\u7433\u95ee\u9053\uff1a\u201c\u4ed6\u4fe1\u4e2d\u662f\u4e0d\u662f\u63d0\u5230\u8981\u548c\u4f60\u89e3\u9664\u5a5a\u7ea6\uff1f\u4ed6\u65e9\u5c31\u5bf9\u6211\u8bf4\u8fc7\uff0c\u53eb\u6211\u5e2e\u4ed6\u60f3\uff0c\u770b\u6709 \u4ec0\u4e48\u85c9\u53e3\u53ef\u4ee5\u907f\u514d\u548c\u4f60\u6210\u5a5a\uff01\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u6768\u67f3\u9752\u5df2\u6c14\u5f97\u6012\u53eb\u51fa\u58f0\uff0c\u9a82\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u5fd8\u6069\u8d1f\u4e49\u7684\u5c0f\u755c\u7272\uff01\u201d\u51af\u7433\u9053\uff1a \u201c\u5582\uff0c\u4f60\u518d\u9a82\u6211\u53d4\u53d4\uff0c\u6211\u53ef\u4e0d\u548c\u4f60\u5ba2\u6c14\uff01\u201d\u6768\u67f3\u9752\u8fd9\u65f6\u5df2\u662f\u4e0d\u987e\u4e00\u5207\uff0c\u62cd\u7684\u4e00\u638c\uff0c\u5c31\u5411\u51af\u7433 \u63b4\u6765\uff0c\u51af\u7433\u4e00\u8df3\u8df3\u5f00\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u662f\u59d1\u59d1\uff0c\u6211\u8ba9\u4f60\u4e00\u638c\uff01\u201d\u8fd9\u79cd\u795e\u60c5\u884c\u52a8\uff0c\u548c\u51af\u745b\u4ee5\u524d\u8ba9\u6768 \u67f3\u9752\u7684\u60c5\u5f62\u4e00\u6a21\u4e00\u6837\u3002\u539f\u6765\u662f\u51af\u7433\u6545\u610f\u6a21\u4eff\uff0c\u8fde\u59d0\u59d0\u7684\u6027\u683c\u795e\u6c14\u4e5f\u5b66\u5f97\u5341\u8db3\u3002 \u3000\u3000\u6768\u67f3\u9752\u5927\u53eb\u9053\uff1a\u201c\u6211\u548c\u4f60\u62fc\u4e86\uff0c\u4f60\u6709\u672c\u4e8b\uff0c\u5c31\u628a\u6211\u6253\u6b7b\u5427\uff01\u201d\u4e00\u5934\u649e\u53bb\uff0c\u51af\u7433\u53c8\u4e00\u8df3\u8df3 \u5f00\uff0c\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u65e0\u51a4\u65e0\u4ec7\uff0c\u4e3a\u4ec0\u4e48\u8981\u6253\u6b7b\u4f60\uff1f\u201d\u6768\u67f3\u9752\u4e00\u5934\u649e\u7a7a\uff0c\u51e0\u4e4e\u8dcc\u5012\uff0c\u62c9\u5f00\u5f39\u5f13\u4fbf \u6253\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u4e0d\u8981\u8138\u7684\u5c0f\u8d31\u4eba\uff0c\u5929\u4e0b\u591a\u5c11\u7537\u4eba\uff0c\u4f60\u4e3a\u4f55\u504f\u7f20\u4e0a\u53d4\u53d4\uff1f\u201d\u5f39\u5f13\u4f3c\u51b0\u96f9\u4e71 \u5c04\uff0c\u51af\u7433\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5f39\u5f13\u4e5f\u6253\u5f97\u4e86\u4eba\u5417\uff1f\u524d\u4e24\u6b21\u6211\u90fd\u6ca1\u6709\u7ed9\u4f60\u6253\u7740\uff0c\u4f60\u8fd8\u8981\u5728\u5b54\u592b\u5b50\u95e8\u524d \u5356\u767e\u5bb6\u59d3\uff01\u201d\u4e00\u9762\u95ea\u5c55\u817e\u632a\uff0c\u4e00\u9762\u65bd\u5c55\u97e9\u91cd\u5c71\u4ee5\u524d\u6240\u6559\u7684\u63a5\u6697\u5668\u624b\u6cd5\uff0c\u628a\u6768\u67f3\u9752\u6240\u53d1\u7684\u94c1\u5f39 \u5b50\u968f\u63a5\u968f\u629b\u6492\u6ee1\u4e00\u5730\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u6768\u67f3\u9752\u7684\u5f39\u5b50\u7adf\u6253\u5b8c\u4e86\u3002 \u3000\u3000\u51af\u7433\u53cc\u7709\u5012\u7ad6\uff0c\u8fd9\u624d\u56de\u9a82\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u624d\u662f\u4e0d\u8981\u8138\u7684\u5c0f\u8d31\u4eba\uff0c\u5929\u4e0b\u591a\u5c11\u7537\u4eba\uff0c\u4f60\u4e3a\u4f55\u504f \u8981\u7f20\u4e0a\u6653\u6f9c\uff1f\u4ed6\u4e0d\u6b22\u559c\u4f60\uff0c\u4f60\u8fd8\u8981\u7f20\uff0c\u8fd9\u624d\u662f\u4e0d\u8981\u8138\uff01\u201d\u6768\u67f3\u9752\u9762\u8272\u7070\u767d\uff0c\u75db\u6781\u6068\u6781\uff0c\u62d4\u5251 \u4e71\u523a\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4eca\u65e5\u4e0d\u662f\u4f60\u6b7b\u4fbf\u662f\u6211\u4ea1\uff01\u201d\u5979\u5fc3\u4e2d\u4e5f\u6599\u5b9a\u51af\u745b\u4e0d\u6562\u6740\u6b7b\u81ea\u5df1\uff0c\u56e0\u6b64\u751a\u4e3a \u64a4\u6cfc\u3002 \u3000\u3000\u51af\u7433\u54c8\u54c8\u4e00\u7b11\uff0c\u9661\u7136\u8eab\u5f62\u4e00\u8d77\uff0c\u65bd\u5c55\u732b\u9e70\u6251\u51fb\u7684\u7edd\u6280\uff0c\u51b7\u4e0d\u9632\u628a\u6768\u67f3\u9752\u624b\u4e2d\u7684\u5229\u5251\u593a \u53bb\uff0c\u7528\u91cd\u624b\u6cd5\u62d7\u6298\u4e3a\u4e24\u6bb5\uff0c\u629b\u4e0b\u5c71\u8c37\u3002\u6717\u58f0\u53d1\u8bdd\u9053\uff1a\u201c\u5582\uff0c\u4f60\u542c\u7740\u3002\u51ed\u4f60\u7684\u672c\u4e8b\uff0c\u4f60\u60f3\u6740 \u6211\uff0c\u90a3\u662f\u4e07\u4e0d\u80fd\u591f\uff1b\u6211\u4e5f\u4e0d\u60f3\u6740\u4f60\uff0c\u4f46\u4f60\u518d\u64a4\u6cfc\uff0c\u6211\u5c31\u7528\u98de\u5200\u523a\u7834\u4f60\u7684\u8138\u76ae\uff0c\u5c04\u778e\u4f60\u7684\u53cc \u773c\uff0c\u53eb\u4f60\u6c38\u8fdc\u505a\u4e2a\u89c1\u4e0d\u5f97\u4eba\u7684\u4e11\u5987\u3002\u6211\u8bf4\u5f97\u5230\u505a\u5f97\u5230\uff0c\u5148\u7ed9\u4f60\u77a7\uff01\u77a7\u7740\uff01\u7b2c\u4e00\u5200\uff0c\u6211\u8981\u628a\u4f60 \u7684\u5f39\u5f13\u524a\u65ad\uff01\u201d\u628a\u624b\u4e00\u626c\uff0c\u6768\u67f3\u9752\u7684\u5f39\u5f13\u5e94\u58f0\u6298\u65ad\uff0c\u51af\u7433\u53c8\u559d\u9053\uff1a\u201c\u7b2c\u4e8c\u5200\uff0c\u6211\u8981\u628a\u4f60\u7684\u5934 \u53d1\u524a\u6389\uff01\u201d\u6768\u67f3\u9752\u614c\u5fd9\u8eb2\u95ea\uff0c\u53ea\u89c9\u51b7\u98ce\u75be\u81f3\uff0c\u5200\u5149\u95ea\u5f71\uff0c\u5934\u4e0a\u4e00\u7247\u6c81\u51c9\uff0c\u4e00\u6478\u5934\u9876\uff0c\u5934\u9876\u7684 \u9752\u4e1d\u5df2\u88ab\u524a\u53bb\u4e86\u4e00\u5927\u7247\uff0c\u9732\u51fa\u4e86\u5934\u76d6\uff0c\u56db\u8fb9\u5934\u53d1\u7a00\u758f\uff0c\u4e2d\u95f4\u4e00\u7247\u5149\u5934\uff0c\u4e0d\u4f26\u4e0d\u7c7b\u3002\u51af\u7433\u53c8\u559d \u9053\uff1a\u201c\u4f60\u518d\u4e0d\u8d70\uff0c\u7b2c\u4e09\u5200\u6211\u4fbf\u753b\u7834\u4f60\u7684\u8138\u76ae\uff01\u201d\u6768\u67f3\u9752\u4e00\u65f6\u6c14\u6025\uff0c\u867d\u4e0d\u754f\u6b7b\uff0c\u4f46\u5374\u771f\u6015\u51af\u7433 \u6bd2\u624b\u6bc1\u5bb9\uff0c\u6559\u5979\u6c38\u8fdc\u89c1\u4e0d\u5f97\u4eba\uff0c\u4e0d\u89c9\u60ca\u558a\u4e00\u58f0\uff0c\u56de\u5934\u75be\u8dd1\u3002 \u3000\u3000\u51af\u7433\u628a\u6768\u67f3\u9752\u6c14\u8d70\u5413\u8d70\u4e4b\u540e\uff0c\u5f97\u610f\u4e4b\u6781\uff0c\u653e\u58f0\u5927\u7b11\uff0c\u5728\u5c71\u6da7\u65c1\u4e34\u6d41\u81ea\u7167\uff0c\u7b11\u9053\uff1a\u201c\u5929\u751f \u6211\u4e24\u59d0\u59b9\u5982\u6b64\u76f8\u4f3c\uff0c\u867d\u7136\u6709\u8bb8\u591a\u9ebb\u70e6\uff0c\u4f46\u5374\u4e0a\u6709\u8bb8\u591a\u597d\u5904\uff01\u201d\u51af\u7433\u597d\u6d01\uff0c\u6253\u4e86\u4e00\u67b6\uff0c\u8138\u4e0a\u6cbe \u4e86\u6ce5\u7802\uff0c\u5934\u53d1\u4e5f\u6709\u70b9\u4e71\u4e86\uff0c\u4e8e\u662f\u4fbf\u7528\u6da7\u6c34\u62b9\u4e00\u62b9\u8138\uff0c\u53c8\u7406\u4e86\u7406\u84ec\u4e71\u7684\u5934\u53d1\uff0c\u5ffd\u7136\u60f3\u8d77\u4e86\u6768\u67f3 \u9752\u90a3\u7247\u4e0d\u4f26\u4e0d\u7c7b\u7684\u5149\u5934\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u7b11\uff0c\u7b11\u4e86\u4e00\u9635\uff0c\u5ffd\u542c\u5f97\u8033\u8fb9\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u54c8\uff0c\u6211\u9053\u662f\u8c01\uff1f \u539f\u6765\u662f\u4f60\uff01\u8fd9\u6837\u597d\u7b11\uff0c\u56de\u5bab\u53bb\u7b11\u7ed9\u7687\u4e0a\u770b\u5427\uff01\u201d\u51af\u7433\u609a\u7136\u4e00\u60ca\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u7ea2\u8863\u5587 \u561b\uff0c\u6bd7\u7259\u88c2\u5634\u7684\u5411\u81ea\u5df1\u6076\u7b11\u3002\u8981\u77e5\u51af\u7433\u8fd9\u65f6\u7684\u6b66\u529f\u9020\u8be3\u5df2\u662f\u4e0d\u51e1\uff0c\u800c\u8fd9\u4e2a\u5587\u561b\u5c45\u7136\u80fd\u6084\u65e0\u58f0 \u606f\u7684\u8d70\u5230\u5979\u7684\u8eab\u8fb9\uff0c\u82e5\u975e\u6709\u60ca\u4eba\u7684\u672c\u9886\uff0c\u600e\u751f\u80fd\u591f\uff1f \u3000\u3000\u51af\u7433\u9887\u662f\u673a\u7075\uff0c\u60c5\u77e5\u9047\u4e86\u5f3a\u654c\uff0c\u9547\u5b9a\u7b11\u9053\uff1a\u201c\u4f60\u662f\u7687\u5bab\u91cc\u7684\u5927\u6cd5\u5e08\u4e48\uff1f\u201d\u90a3\u5587\u561b\u53cc\u773c\u4e00 \u7ffb\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u771f\u662f\u8d35\u4eba\u5584\u5fd8\uff0c\u5e78\u597d\u6211\u8fd8\u6709\u70b9\u513f\u80fd\u8010\uff0c\u8981\u4e0d\u7136\u5c31\u7ed9\u4f60\u7684\u5929\u5c71\u638c\u529b\u5e9f\u4e86\u3002\u201d \u3000\u3000\u51af\u7433\u5fc3\u9053\uff1a\u201c\u6211\u5e38\u5e38\u7ed9\u59d0\u59d0\u60f9\u9ebb\u70e6\uff0c\u8fd9\u56de\u662f\u59d0\u59d0\u7ed9\u6211\u60f9\u7684\u9ebb\u70e6\u4e86\uff01\u201d\u90a3\u5587\u561b\u4f38\u624b\u4fbf\u60f3\u6293 \u5979\uff0c\u51af\u7433\u4e00\u8df3\u8df3\u5f00\uff0c\u9053\uff1a\u201c\u6211\u6b63\u60f3\u56de\u5bab\u53bb\u89c1\u7687\u4e0a\uff0c\u4e0d\u5fc5\u4f60\u6765\u529d\u9a7e\uff0c\u4f60\u6562\u6293\u6211\uff1f\u6211\u5c31\u5bf9\u7687\u4e0a\u8bf4 \u4f60\u8c03\u620f\u6211\u3002\u201d\u90a3\u5587\u561b\u628a\u624b\u7f29\u56de\uff0c\u9053\uff1a\u201c\u597d\u5440\uff0c\u7433\u8d35\u4eba\uff0c\u4f60\u8fd8\u672a\u5f97\u5ba0\uff0c\u5c31\u60f3\u54ac\u6211\u4e00\u53e3\u4e48\uff1f\u8fd9\u56de \u9976\u4f60\u733e\u4f3c\u72d0\u72f8\uff0c\u4e5f\u4e0d\u80fd\u9003\u8131\u6211\u7684\u638c\u5fc3\u3002\u4f60\u65e2\u7136\u8981\u89c1\u7687\u4e0a\uff0c\u90a3\u5c31\u5feb\u8d70\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u4e0d\u89c1\u6211 \u5934\u53d1\u8fd8\u672a\u7406\u597d\u4e48\uff1f\u201d\u8e72\u4e0b\u53bb\u7528\u5c71\u6cc9\u6d17\u53d1\uff0c\u90a3\u7ea2\u8863\u5587\u561b\u7ad9\u5728\u5979\u7684\u8eab\u540e\uff0c\u6b63\u81ea\u76d8\u7b97\u8981\u4e0d\u8981\u7528\u786c\u529f \u592b\u64d2\u5979\u3002 \u3000\u3000\u51af\u7433\u6d17\u4e86\u5934\u53d1\uff0c\u53c8\u6574\u6574\u8863\u88f3\uff0c\u9053\uff1a\u201c\u597d\u5566\uff0c\u6211\u968f\u4f60\u8d70\u3002\u201d\u53cd\u624b\u4e00\u626c\uff0c\u4e09\u67c4\u593a\u547d\u795e\u5200\u9aa4\u7136 \u5c04\u51fa\uff0c\u76f8\u8ddd\u751a\u8fd1\uff0c\u53c8\u662f\u51fa\u5176\u4e0d\u610f\uff0c\u90a3\u7ea2\u8863\u5587\u561b\u6b66\u529f\u6781\u9ad8\uff0c\u4e5f\u53ea\u95ea\u5f00\u4e86\u4e00\u628a\uff0c\u5176\u4f59\u4e24\u628a\uff0c\u90fd\u5c04 \u4e2d\u4e86\u4ed6\u7684\u80f8\u524d\u8981\u5bb3\u3002 \u3000\u3000\u51af\u7433\u62cd\u624b\u7b11\u9053\uff1a\u201c\u5012\u4e5f\uff0c\u5012\u4e5f\uff01\u201d\u4e0d\u6599\u90a3\u4e24\u67c4\u98de\u5200\u89e6\u53ca\u4ed6\u7684\u8eab\u4f53\uff0c\u7adf\u7136\u53d1\u51fa\u94ff\u9535\u4e4b\u58f0\uff0c \u5982\u540c\u6253\u7740\u77f3\u5934\u94c1\u677f\u4e00\u822c\uff0c\u968f\u5373\u6389\u843d\u3002\u51af\u7433\u5927\u5403\u4e00\u60ca\uff0c\u53cc\u624b\u9f50\u53d1\uff0c\u8fde\u5c04\u51fa\u516d\u67c4\u98de\u5200\uff0c\u90a3\u7ea2\u8863\u5587 \u561b\u53ea\u662f\u62a4\u7740\u773c\u775b\uff0c\u63a5\u4e86\u5979\u5c04\u5411\u5934\u9762\u7684\u4e24\u628a\uff0c\u5176\u4f59\u56db\u628a\uff0c\u90fd\u7ed9\u4ed6\u7684\u8eab\u4f53\u9707\u843d\u3002\u7ea2\u8863\u5587\u561b\u5927\u7b11 \u9053\uff1a\u201c\u4f5b\u7237\u4e43\u91d1\u521a\u4e0d\u574f\u4e4b\u8eaf\uff0c\u5c82\u60e7\u4f60\u8fd9\u4e9b\u7834\u94dc\u70c2\u94c1\uff01\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u4fe8\u5982\u5de8\u9e70\u6251\u5154\uff0c\u4f38\u5f00\u84b2\u6247 \u822c\u7684\u5927\u624b\uff0c\u5411\u51af\u7433\u5934\u9876\u4fbf\u6293\u3002 \u3000\u3000\u8fd9\u5587\u561b\u6b63\u662f\u989d\u97f3\u548c\u5e03\uff0c\u4ed6\u4e5f\u662f\u5949\u547d\u53bb\u6349\u62ff\u90a3\u4e2a\u6781\u91cd\u8981\u7684\u94a6\u72af\u7684\uff0c\u9014\u4e2d\u9047\u5230\u90a3\u9003\u8131\u6027\u547d\u7684 \u8840\u6ef4\u5b50\uff0c\u544a\u77e5\u4ed6\u5c71\u8c37\u91cc\u6709\u8fd9\u4e48\u4e00\u4e2a\u5389\u5bb3\u7684\u5c0f\u59d1\u5a18\uff0c\u4ed6\u4e00\u542c\u4fbf\u6025\u6025\u8d76\u6765\uff0c\u5fc3\u60f3\uff1a\u82e5\u80fd\u628a\u7433\u8d35\u4eba \u6349\u56de\uff0c\u53ea\u6015\u6bd4\u6349\u4e86\u94a6\u72af\uff0c\u66f4\u80fd\u4ee4\u7687\u4e0a\u5f00\u5fc3\u3002 \u3000\u3000\u51af\u7433\u7528\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u907f\u4ed6\u4e24\u6293\uff0c\u989d\u97f3\u548c\u5e03\u7b2c\u4e09\u6293\u53c8\u5230\uff0c\u51af\u7433\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u5c48\u8eab\uff0c\u633d\u4e86 \u4e00\u6735\u5251\u82b1\uff0c\u5411\u4ed6\u773c\u775b\u6025\u523a\uff01 \u3000\u3000\u989d\u97f3\u548c\u5e03\u4e00\u4f4e\u5934\uff0c\u53cc\u638c\u659c\u51fa\uff0c\u626d\u5979\u624b\u8155\uff0c\u51af\u7433\u4e00\u5251\u5e73\u633d\uff0c\u559d\u58f0\uff1a\u201c\u53bb\uff01\u201d\u5979\u8fd9\u4e00\u5e74\u6765\u7cbe \u4fee\u65e0\u6781\u6d3e\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u8fd9\u4e00\u5251\u52b2\u9053\u5947\u5927\uff0c\u989d\u97f3\u548c\u5e03\u867d\u7136\u7ec3\u6709\u91d1\u949f\u7f69\u94c1\u5e03\u886b\u7684\u7edd\u9876\u786c\u529f\uff0c\u4e5f\u4e0d \u6562\u7ed9\u5979\u7684\u5251\u622a\u7740\u8109\u95e8\uff0c\u53cc\u638c\u4e00\u53d8\uff0c\u659c\u62a2\u4e24\u6b65\uff0c\u731b\u5730\u53cd\u624b\u4e00\u638c\uff0c\u559d\u9053\uff1a\u201c\u64a4\u5251\uff01\u201d\u8fd9\u4e00\u638c\u626b\u5f97 \u52b2\u98ce\u75be\u8d77\uff0c\u51af\u7433\u864e\u53e3\u75bc\u75db\uff0c\u5b9d\u5251\u51e0\u4e4e\u5760\u5730\uff0c\u6025\u5fd9\u817e\u8eab\u98de\u8dd1\u3002 \u3000\u3000\u989d\u97f3\u548c\u5e03\u89c1\u8fd9\u4e00\u638c\u6253\u4e0d\u6389\u5979\u7684\u5175\u5668\uff0c\u4e5f\u9887\u4e3a\u60ca\u8be7\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u91ce\u4e2b\u5934\u662f\u6709\u70b9\u80fd\u8010\uff0c \u53ef\u662f\u8981\u60f3\u9003\u8131\u4f5b\u7237\u638c\u5fc3\uff0c\u90a3\u8fd8\u662f\u96be\u4e8e\u767b\u5929\uff01\u201d\u98de\u8eab\u4e00\u8dc3\uff0c\u53cc\u638c\u5e73\u63a8\uff0c\u8fd9\u4e00\u4e0b\u52b2\u9053\u52a0\u5927\uff0c\u638c\u98ce \u66f4\u5f3a\uff0c\u51af\u7433\u9aa4\u7136\u5982\u53d7\u731b\u529b\u6240\u649e\uff0c\u6025\u5fd9\u5411\u524d\u4e00\u4ec6\uff0c\u987a\u7740\u4ed6\u7684\u638c\u98ce\uff0c\u98d8\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u4e00\u8de4\u8dcc\u5012\uff01 \u3000\u3000\u989d\u97f3\u548c\u5e03\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u7f8e\u4eba\u513f\u6ca1\u8dcc\u4f24\u5427\uff0c\u6211\u7ed9\u4f60\u533b\uff01\u201d\u51af\u7433\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u8df3\u4e86\u8d77 \u6765\uff0c\u626c\u624b\u53c8\u662f\u4e24\u628a\u98de\u5200\uff0c\u5c04\u4ed6\u53cc\u76ee\uff0c\u989d\u97f3\u548c\u5e03\u5927\u6012\uff0c\u4e00\u4e3e\u624b\u5c06\u4e24\u628a\u98de\u5200\u6253\u843d\uff0c\u6b63\u62df\u4e09\u5ea6\u53d1 \u638c\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u8c01\u6562\u6b3a\u4fae\u6211\u7684\u59b9\u59b9\uff01\u201d \u3000\u3000\u989d\u97f3\u548c\u5e03\u4e00\u770b\uff0c\u53ea\u89c1\u5c71\u5761\u4e0a\u4e00\u56e2\u767d\u5f71\uff0c\u75be\u82e5\u98d8\u98ce\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u4e00\u4e2a\u5973\u90ce\u5982\u98de\u8d76\u5230\uff0c\u6765\u7684 \u6b63\u662f\u51af\u745b\u3002\u989d\u97f3\u548c\u5e03\u89c1\u5979\u4eec\u4e00\u6a21\u4e00\u6837\uff0c\u4e0d\u89c9\u5446\u4e86\u3002 \u3000\u3000\u51af\u745b\u58f0\u5230\u4eba\u5230\uff0c\u5237\u7684\u4e00\u5251\uff0c\u5411\u989d\u97f3\u548c\u5e03\u54bd\u5589\u75be\u523a\uff0c\u989d\u97f3\u548c\u5e03\u95ea\u8eab\u53cd\u638c\u671d\u5979\u8109\u95e8\u4e00\u6263\uff0c\u51af \u745b\u7684\u5251\u6cd5\u4f55\u7b49\u795e\u5999\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u5df2\u523a\u5411\u4ed6\u80ef\u9aa8\u201c\u4e09\u5143\u7a74\u201d\uff0c\u989d\u97f3\u548c\u5e03\u5927\u6012\uff0c\u81ea\u8d1f\u662f\u94a2\u94c1\u4e4b \u8eaf\uff0c\u4e0d\u754f\u5200\u5251\uff0c\u53cc\u638c\u4e00\u5408\uff0c\u6293\u7740\u51af\u745b\u80a9\u5934\u3002\u5374\u4e0d\u6599\u51af\u745b\u624b\u4e2d\u7684\u77ed\u5251\uff0c\u4e43\u662f\u6666\u660e\u7985\u5e08\u5f53\u5e74\u91c7\u4e94 \u91d1\u4e4b\u7cbe\uff0c\u6240\u70bc\u7684\u9547\u5c71\u4e4b\u5b9d\uff0c\u4e00\u5251\u523a\u53bb\uff0c\u5165\u8089\u6570\u5bf8\uff0c\u989d\u97f3\u548c\u5e03\u53ea\u89c9\u4e00\u9635\u5267\u75db\uff0c\u6025\u8fd0\u5185\u529f\uff0c\u4f7f\u808c \u8089\u5185\u9677\uff0c\u8feb\u5251\u5c16\u9000\u4e86\u51fa\u6765\uff0c\u4ed6\u817f\u4e0a\u8fd0\u52b2\uff0c\u53cc\u638c\u529b\u9053\u81ea\u51cf\uff0c\u51af\u745b\u4e5f\u8d81\u673a\u6323\u8131\uff0c\u53ea\u89c9\u80a9\u5934\u706b\u8fa3\u4f5c \u75db\u3002\u5e78\u559c\u989d\u97f3\u548c\u5e03\u62ff\u4e0d\u51c6\u5979\u662f\u5426\u7687\u4e0a\u6240\u8981\u4e4b\u4eba\uff0c\u4e0d\u6562\u5341\u5206\u7528\u529b\uff0c\u8981\u4e0d\u7136\u80a9\u4e0a\u7684\u7435\u7436\u9aa8\u4e5f\u4f1a\u88ab \u4ed6\u634f\u788e\u3002 \u3000\u3000\u201c\u4e09\u5143\u7a74\u201d\u4e43\u4eba\u8eab\u6b7b\u7a74\u4e4b\u4e00\uff0c\u989d\u97f3\u548c\u5e03\u88ab\u5b9d\u5251\u523a\u4e2a\u6b63\u7740\uff0c\u5c45\u7136\u4ecd\u662f\u7eb5\u8df3\u5982\u98de\uff0c\u51af\u745b\u4e5f\u4e0d \u7531\u5f97\u5927\u9a87\uff01\u989d\u97f3\u548c\u5e03\u8fd0\u6c14\u95ed\u7a74\uff0c\u75db\u4ecd\u672a\u6b62\uff0c\u52a8\u4e86\u771f\u6012\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u7ba1\u4f60\u662f\u8d35\u4eba\u8d31\u4eba\uff0c\u4f5b \u7237\u90fd\u8981\u9001\u4f60\u5f52\u897f\u3002\u201d\u51dd\u8eab\u7acb\u5b9a\uff0c\u731b\u7136\u4e00\u559d\uff0c\u53cc\u638c\u5e73\u63a8\uff0c\u51af\u7433\u9053\uff1a\u201c\u59d0\u59d0\u5c0f\u5fc3\uff01\u201d\u8fd9\u4e00\u638c\uff0c\u989d \u97f3\u548c\u5e03\u7528\u4e86\u5341\u6210\u529b\u91cf\uff0c\u771f\u5982\u638c\u631f\u98ce\u96f7\uff0c\u9aa4\u7136\u5288\u5230\u3002\u51af\u745b\u559d\u58f0\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u8eab\u5b50\u5e73\u7a7a\u63a0\u8d77\u4e09 \u4e08\uff0c\u52b2\u98ce\u4ece\u5979\u811a\u5e95\u626b\u8fc7\uff0c\u6beb\u53d1\u65e0\u4f24\uff0c\u5979\u5728\u534a\u7a7a\u4e2d\u4f7f\u51fa\u5929\u5c71\u5251\u6cd5\u7edd\u62db\uff0c\u4e00\u62db\u201c\u51b0\u6cb3\u5012\u6302\u201d\uff0c\u94f6 \u5149\u98de\u6d12\uff0c\u6025\u5954\u800c\u4e0b\u3002 \u3000\u3000\u989d\u97f3\u548c\u5e03\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u6562\u518d\u7a7a\u624b\u5bf9\u654c\uff0c\u53d6\u4e86\u62c2\u5c18\uff0c\u626c\u7a7a\u4e00\u6321\u3002\u51af\u745b\u8fd9\u4e00\u5251\u51cc\u5389\u975e\u5e38\uff0c\u5374 \u4e0d\u6599\u5ffd\u7136\u88ab\u62c2\u5c18\u7f20\u7740\uff0c\u7528\u4e0d\u51fa\u529b\u6765\uff0c\u53ea\u89c1\u989d\u97f3\u548c\u5e03\u53c8\u662f\u54c8\u54c8\u5927\u7b11\uff0c\u9a88\u6307\u70b9\u5979\u7684\u201c\u80a9\u4e95\u7a74\u201d\uff01 \u3000\u3000\u51af\u7433\u89c1\u59d0\u59d0\u5371\u6025\uff0c\u65e9\u5df2\u62a2\u4e86\u4e0a\u524d\uff0c\u5237\u7684\u4e00\u5251\uff01\u523a\u4ed6\u80cc\u5fc3\uff0c\u989d\u97f3\u548c\u5e03\u53cd\u638c\u4e00\u51fb\uff0c\u51af\u7433\u9999\u80a9 \u4e00\u7f29\uff0c\u907f\u5f00\u4ed6\u70b9\u7a74\u7684\u53cc\u6307\uff0c\u5de6\u624b\u4e94\u6307\u4e00\u62e2\uff0c\u5411\u4ed6\u80f8\u53e3\u75be\u626b\uff0c\u989d\u97f3\u548c\u5e03\u5403\u8fc7\u82e6\u5934\uff0c\u6025\u6025\u95ea\u8eab\u907f \u5f00\uff0c\u8ba9\u51af\u745b\u628a\u5251\u62bd\u51fa\u3002 \u3000\u3000\u51af\u745b\u51af\u7433\u53cc\u5251\u75be\u8fdb\uff0c\u7d27\u7d27\u7f20\u903c\u3002\u989d\u97f3\u548c\u5e03\u7684\u638c\u529b\u867d\u7136\u5389\u5bb3\u4e4b\u6781\uff0c\u4f46\u6bcf\u53d1\u4e00\u638c\uff0c\u90fd\u8981\u5148\u884c \u8fd0\u6c14\uff0c\u88ab\u53cc\u5251\u8054\u653b\uff0c\u65e0\u6687\u53d1\u638c\u3002\u53ef\u662f\u4ed6\u7684\u90a3\u67c4\u62c2\u5c18\uff0c\u66f4\u662f\u6b66\u6797\u7f55\u89c1\u7684\u5175\u5668\uff0c\u53ef\u8f6f\u53ef\u786c\uff0c\u53ef\u593a \u5200\u5251\uff0c\u53ef\u62c2\u7a74\u9053\uff0c\u51af\u745b\u51af\u7433\u90fd\u4e0d\u6562\u7ed9\u4ed6\u7f20\u7740\u3002\u989d\u97f3\u548c\u5e03\u6b3a\u8eab\u8fdb\u903c\uff0c\u4e24\u59d0\u59b9\u867d\u4e92\u4e3a\u547c\u5e94\uff0c\u4e5f\u662f \u9669\u8c61\u73af\u751f\u3002\u8fd8\u5e78\u6253\u4e86\u4e00\u9635\uff0c\u4e24\u59d0\u59b9\u8eab\u5f62\u75be\u8f6c\u3002\u989d\u97f3\u548c\u5e03\u5df2\u5206\u8fa8\u4e0d\u51fa\u8c01\u662f\u51af\u745b\uff0c\u8c01\u662f\u51af\u7433\u3001\u81ea \u7136\u4e5f\u5c31\u8fa8\u4e0d\u51fa\u8c01\u4eba\u624b\u4e0a\u62ff\u7684\u4e43\u662f\u5b9d\u5251\uff0c\u5fc3\u6709\u987e\u5fcc\uff0c\u4e0d\u6562\u786c\u62a2\u3002\u53ea\u7528\u4e00\u67c4\u62c2\u5c18\uff0c\u903c\u7740\u4e8c\u4eba\u7684 \u5251\uff0c\u4f3a\u673a\u624d\u65bd\u6740\u624b\u3002 \u3000\u3000\u6253\u4e86\u4e00\u9635\uff0c\u5c71\u4e0a\u53c8\u4e0b\u6765\u4e00\u4eba\uff0c\u8fd9\u4eba\u4e43\u662f\u548c\u51af\u745b\u540c\u6765\u7684\u674e\u6cbb\uff0c\u4ed6\u672c\u4ee5\u4e3a\u6709\u51af\u745b\u51fa\u624b\uff0c\u59d0\u59b9 \u8054\u653b\uff0c\u4ec0\u4e48\u5f3a\u654c\uff0c\u4e5f\u53ef\u4ee5\u62b5\u6321\uff0c\u4e0d\u6599\u770b\u4e86\u4e00\u9635\uff0c\u53ea\u89c1\u989d\u97f3\u548c\u5e03\u90a3\u67c4\u62c2\u5c18\u5929\u77eb\u5982\u9f99\uff0c\u5389\u5bb3\u4e4b \u6781\uff0c\u4ee5\u51af\u745b\u5929\u5c71\u5251\u6cd5\u7684\u795e\u5999\uff0c\u51af\u7433\u65e0\u6781\u5251\u6cd5\u7684\u6c89\u7a33\uff0c\u4e5f\u53ea\u6709\u62db\u67b6\u7684\u4efd\u513f\u3002 \u3000\u3000\u989d\u97f3\u548c\u5e03\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u72de\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u518d\u591a\u6765\u51e0\u4e2a\uff0c\u4f5b\u7237\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u201d\u62c2 \u5c18\u6a2a\u626b\uff0c\u903c\u5f00\u4e24\u59d0\u59b9\u7684\u5251\uff0c\u5c18\u6746\u76f4\u6307\uff0c\u8d81\u7740\u674e\u6cbb\u521a\u5230\uff0c\u4fbf\u9aa4\u7136\u70b9\u4ed6\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\u3002\u5c82\u77e5 \u674e\u6cbb\u7684\u5251\u6cd5\uff0c\u5947\u8be1\u4e4b\u5904\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u660e\u660e\u89c1\u4ed6\u5251\u52bf\u5954\u5de6\uff0c\u4e2d\u8def\u95e8\u6237\u655e\u5f00\uff0c\u4e0d\u6599\u500f\u7136\u4e00\u53d8\uff0c\u5251 \u950b\u5df2\u6233\u5411\u53f3\u9996\uff0c\u5251\u52bf\u53d8\uff0c\u6b65\u6cd5\u53d8\uff0c\u865a\u8005\u53d8\u5b9e\uff0c\u5b9e\u82e5\u53d8\u865a\uff0c\u989d\u97f3\u548c\u5e03\u975e\u4f46\u70b9\u4e0d\u4e2d\u4ed6\u7684\u7a74\u9053\uff0c\u8fd8 \u51e0\u4e4e\u7ed9\u4ed6\u523a\u4e86\u4e00\u5251\u3002\u5e78\u5728\u989d\u97f3\u548c\u5e03\u6b66\u529f\u786e\u5c5e\u9ad8\u5f3a\u4e4b\u6781\uff0c\u4e00\u62db\u6251\u7a7a\uff0c\u65b9\u4f4d\u7acb\u53d8\uff0c\u624d\u582a\u582a\u907f\u5f00\u4e86 \u674e\u6cbb\u7684\u7edd\u62db\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u5f62\u52bf\u53c8\u53d8\uff0c\u674e\u6cbb\u548c\u51af\u745b\u51af\u7433\uff0c\u4e09\u4e2a\u4eba\u4e09\u79cd\u5251\u6cd5\uff0c\u90fd\u662f\u5f53\u4eca\u4e4b\u4e16\u6700\u4e0a\u4e58\u7684\u5251\u6cd5\uff0c\u989d \u97f3\u548c\u5e03\u987e\u6b64\u5931\u5f7c\uff0c\u518d\u4e0d\u6562\u4f3c\u4ee5\u524d\u90a3\u6837\u8086\u65e0\u5fcc\u60ee\u3002\u4e24\u65b9\u6709\u653b\u6709\u5b88\uff0c\u53c8\u62fc\u6597\u4e86\u4e00\u767e\u6765\u62db\u3002 \u3000\u3000\u4f46\u674e\u6cbb\u52a0\u5165\u4e4b\u540e\uff0c\u4e5f\u662f\u6709\u5229\u6709\u5f0a\uff0c\u5229\u8005\u4e43\u662f\u4e09\u5251\u8054\u653b\uff0c\u6b64\u547c\u5f7c\u5e94\uff0c\u4e0d\u4e45\u5c31\u5360\u4e86\u4e0a\u98ce\u3002\u5f0a \u8005\u5374\u662f\u989d\u97f3\u548c\u5e03\u4e45\u6218\u4e4b\u540e\uff0c\u8bd5\u51fa\u674e\u6cbb\u7684\u5251\u4e0d\u662f\u5b9d\u5251\uff0c\u7adf\u7528\u91d1\u521a\u6307\u529b\uff0c\u62fc\u53d7\u4e00\u65f6\u4e4b\u75db\uff0c\u786c\u62a2\u674e \u6cbb\u7684\u5175\u5668\u3002\u4ed6\u53f3\u624b\u62c2\u5c18\uff0c\u529b\u654c\u4e24\u59d0\u59b9\u7684\u4e24\u53e3\u5229\u5251\uff0c\u5de6\u624b\u5374\u4ee5\u64d2\u62ff\u624b\u6cd5\uff0c\u6b3a\u8eab\u8fdb\u903c\u674e\u6cbb\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u53cc\u65b9\u9669\u62db\u8fed\u51fa\uff0c\u6740\u5f97\u96be\u89e3\u96be\u5206\u3002\u51af\u745b\u5fc3\u5934\u7126\u8e81\uff0c\u5ffd\u7136\u60f3\u8d77\u5728\u5bab\u4e2d\u9ed1\u56da\u7262\u4e2d\u6240\u78b0\u89c1 \u7684\u90a3\u4e2a\u5141\u7940\uff0c\u5141\u7940\u66fe\u8bf4\u989d\u97f3\u548c\u5e03\u7684\u547d\u95e8\u662f\u574e\u706b\u79bb\u6c34\u4e4b\u7a74\uff0c\u4f46\u5979\u5374\u4e0d\u77e5\u574e\u706b\u79bb\u6c34\u4e4b\u7a74\uff0c\u5728\u4eba\u8eab \u90a3\u4e2a\u90e8\u4f4d\u3002\u5ffd\u60f3\u8d77\u59b9\u59b9\u6b66\u529f\u751a\u6742\uff0c\u6b63\u90aa\u5404\u6d3e\uff0c\u90fd\u66fe\u5b66\u8fc7\uff0c\u4e8e\u662f\u5728\u6fc0\u6597\u4e4b\u4e2d\uff0c\u7a81\u7136\u95ee\u9053\uff1a\u201c\u59b9 \u59b9\uff0c\u574e\u706b\u79bb\u6c34\u4e4b\u7a74\uff0c\u4f60\u61c2\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u51af\u7433\u5e94\u9053\uff1a\u201c\u574e\u706b\u4e4b\u7a74\u5728\u9f99\u5c3e\u9aa8\u4e09\u5bf8\uff0c\u79bb\u6c34\u4e4b\u7a74\uff0c\u54ce\uff0c\u79bb\u6c34\u4e4b\u7a74\uff0c\u5c31\u662f\u4ed6\u7684\u547d\u6839\u5b50 \u5440\uff01\u201d\u539f\u6765\u79bb\u6c34\u4e4b\u7a74\u4e43\u662f\u7537\u5b50\u80be\u56ca\u4e4b\u4e0b\u7684\u90e8\u4f4d\uff0c\u51af\u7433\u4e0d\u597d\u610f\u601d\u8bf4\u51fa\u3002 \u3000\u3000\u989d\u97f3\u548c\u5e03\u5927\u5403\u4e00\u60ca\uff0c\u4e0d\u77e5\u8fd9\u4e24\u59d0\u59b9\u4f55\u4ee5\u4f1a\u61c2\u5f97\u7ea2\u6559\u5587\u561b\u7684\u7a74\u9053\u7528\u8bed\uff1b\u66f4\u4e0d\u77e5\u5979\u4eec\u4f55\u4ee5\u4f1a \u77e5\u9053\u81ea\u5df1\u7684\u547d\u95e8\u8981\u7a74\u3002\u51af\u745b\u8fd9\u65f6\u8fd8\u4e0d\u77e5\u8ffd\u7a76\u7adf\u5728\u90a3\u4e2a\u65b9\u4f4d\uff0c\u5374\u88c5\u4f5c\u61c2\u5f97\u7684\u6837\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u597d \u5440\uff0c\u54b1\u4eec\u5c31\u523a\u4ed6\u7684\u547d\u95e8\u8981\u7a74\u3002\u201d \u3000\u3000\u989d\u97f3\u548c\u5e03\u5fc3\u80c6\u4ff1\u5bd2\u3002\u672c\u6765\u7167\u4ed6\u7684\u6b66\u529f\uff0c\u82e5\u7136\u4ee5\u4e00\u5bf9\u4e00\uff0c\u7eb5\u9047\u4e0a\u4e86\u4e00\u7b49\u4e00\u7684\u597d\u624b\uff0c\u53c8\u8ba9\u5bf9 \u65b9\u77e5\u9053\u4e86\u4ed6\u7684\u547d\u95e8\u8981\u7a74\uff0c\u4ed6\u4e5f\u53ef\u4ee5\u9632\u62a4\u5468\u5168\uff0c\u6709\u6043\u65e0\u6050\u3002\u4f46\u73b0\u5728\u662f\u4ee5\u4e00\u654c\u4e09\uff0c\u4e09\u4eba\u5251\u6cd5\u53c8\u90fd \u662f\u795e\u5999\u51cc\u5389\uff0c\u53ea\u6050\u96be\u4ee5\u517c\u987e\uff0c\u4e07\u4e00\u9632\u5fa1\u4e0d\u5468\uff0c\u90a3\u5c31\u8981\u4e00\u547d\u545c\u547c\uff0c\u65e0\u6cd5\u53ef\u6551\u4e86\u3002 \u3000\u3000\u51af\u745b\u8fd0\u5251\u6025\u523a\uff0c\u53ea\u89c1\u989d\u97f3\u548c\u5e03\u529b\u62a2\u4e24\u62db\uff0c\u4f5c\u52bf\u6251\u653b\uff0c\u8eab\u8d8b\u8d70\u52bf\u3002\u51af\u745b\u6545\u610f\u8ba9\u4ed6\u9003\u8d70\uff0c\u8eab \u5f62\u4e00\u95ea\uff0c\u989d\u97f3\u548c\u5e03\u8fde\u5fd9\u9003\u8d70\u3002\u51af\u745b\u677e\u4e86\u53e3\u6c14\uff0c\u6309\u5251\u4e0d\u8ffd\u3002 \u3000\u3000\u51af\u7433\u7b11\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u600e\u4e48\u4e5f\u61c2\u5f97\u90aa\u6d3e\u7684\u6b66\u529f\uff1f\u201d\u51af\u745b\u5c06\u5141\u7940\u4e4b\u8a00\u8bf4\u4e86\u3002\u51af\u7433\u9053\uff1a\u201c\u5141 \u796f\u505a\u7687\u5b50\u4e4b\u65f6\uff0c\u5c31\u559c\u4e0e\u7ea2\u6559\u5587\u561b\u6765\u5f80\u3002\u6240\u4ee5\u73b0\u5728\u4ed6\u628a\u4ee5\u524d\u6240\u4f4f\u7684\u7687\u5e9c\uff0c\u4e5f\u6539\u4f5c\u4e86\u96cd\u548c\u5bab\uff0c\u5f53 \u4f5c\u7ea2\u6559\u5587\u561b\u7684\u4e0a\u9662\u3002\u6211\u8fd8\u662f\u5728\u4ed6\u7684\u56db\u7687\u5b50\u5e9c\u4e2d\uff0c\u61c2\u5f97\u7ea2\u6559\u5587\u561b\u7684\u70b9\u7a74\u7528\u8bed\u7684\uff0c\u4ed6\u4eec\u7684\u70b9\u7a74\u624b \u6cd5\uff0c\u4e0e\u4e2d\u571f\u751a\u662f\u4e0d\u540c\uff0c\u6781\u4e3a\u6b8b\u9177\uff0c\u54b1\u4eec\u65e5\u540e\u4e0e\u4ed6\u4eec\u5bf9\u654c\uff0c\u4e5f\u5f97\u5c0f\u5fc3\u3002\u4e0d\u8fc7\u989d\u97f3\u548c\u5e03\u5374\u4e0d\u662f\u4ed6 \u4eec\u6559\u4e2d\u7684\u70b9\u7a74\u540d\u624b\u3002\u6211\u4e5f\u542c\u8fc7\u4ed6\u7684\u540d\u5b57\uff0c\u636e\u8bf4\u5185\u5916\u529f\u592b\uff0c\u5728\u7ea2\u6559\u4e4b\u4e2d\uff0c\u90fd\u662f\u7b2c\u4e8c\u628a\u597d\u624b\u3002\u201d \u674e\u6cbb\u9053\uff1a\u201c\u8c01\u662f\u4ed6\u4eec\u7684\u7b2c\u4e00\u9ad8\u624b\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u638c\u6559\u6606\u7538\u4e0a\u4eba\u3002\u201d\u63a5\u7740\u53c8\u5728\u59d0\u59d0\u8033\u8fb9 \u5c06\u574e\u706b\u79bb\u6c34\u4e4b\u7a74\u7684\u65b9\u4f4d\u7ec6\u7ec6\u8bf4\u4e86\u3002 \u3000\u3000\u6fc0\u6218\u4e4b\u540e\uff0c\u4e09\u4eba\u5750\u5728\u8c37\u4e2d\u6b47\u606f\u3002\u51af\u7433\u95ee\u9053\uff1a\u201c\u4f60\u7684\u5510\u53d4\u53d4\u5462\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u5e78\u5f97\u4f60\u7684\u674e \u6cbb\u54e5\u54e5\u533b\u597d\u4e86\u3002\u201d\u51af\u7433\u4e00\u7b11\u9053\uff1a\u201c\u59d0\u59d0\u4f60\u4e5f\u5b66\u5f97\u4f36\u7259\u4fd0\u9f7f\u4e86\u3002\u201d\u5ffd\u7136\u60f3\u8d77\u4e86\u6768\u67f3\u9752\uff0c\u4e0d\u7981\u53c8 \u683c\u683c\u4e71\u7b11\uff0c\u51af\u745b\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd9\u6837\u6b22\u559c\u7b11\u554a\uff1f\u201d\u51af\u7433\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u5728\u8def\u4e0a\u6709\u6ca1\u6709\u78b0\u5230\u4e00\u4e2a\u5149 \u5934\u7684\u5973\u4eba\uff0c\u4e0d\u662f\u5c3c\u59d1\uff0c\u53ea\u662f\u5934\u9876\u4e2d\u95f4\u6ca1\u6709\u5934\u53d1\u7684\u3002\u201d\u51af\u745b\u83ab\u540d\u5176\u5999\uff0c\u9053\uff1a\u201c\u6211\u548c\u674e\u6cbb\u4e0a\u5c71\u5148 \u89c1\u4e86\u5988\uff0c\u5988\u8bf4\u4f60\u5728\u8fd9\u91cc\u4e86\u671b\uff0c\u6240\u4ee5\u6211\u4eec\u627e\u6765\uff0c\u90a3\u91cc\u4f1a\u89c1\u8fd9\u6837\u7684\u602a\u5973\u4eba\uff1f\u201d \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u4f60\u4eec\u89c1\u8fc7\u5988\u4e86\u3002\u4f60\u4eec\u4ece\u90a3\u8fb9\u4e0a\u5c71\uff0c\u602a\u4e0d\u5f97\u78b0\u4e0d\u7740\u5979\u4e86\u3002\u201d\u51af\u745b\u9053\uff1a \u201c\u4f60\u7684\u95f7\u846b\u82a6\u91cc\u5356\u4ec0\u4e48\u836f\uff0c\u5979\u662f\u8c01\u5440\uff1f\u201d\u51af\u7433\u7b11\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u6211\u5e2e\u4e86\u4f60\u4e00\u4e2a\u5927\u5fd9\uff0c\u4f60\u5982\u4f55\u8c22 \u6211\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u4f60\u8bf4\u8bf4\u770b\uff0c\u5230\u5e95\u662f\u5e2e\u4e86\u4ec0\u4e48\u5fd9\uff1f\u201d\u51af\u7433\u5c06\u6c14\u8d70\u6768\u67f3\u9752\u4e4b\u4e8b\u8bf4\u51fa\uff0c\u4e00\u9762\u8bf4\u4e00 \u9762\u7b11\uff0c\u5ffd\u89c1\u59d0\u59d0\u9762\u8272\u5927\u53d8\uff0c\u51af\u7433\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u6562\u518d\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u96be\u9053\u6211\u53c8\u505a\u9519\u4e86\u4e8b\u5417\uff1f\u201d\u51af \u745b\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60\u4e5f\u592a\u6dd8\u6c14\u4e86\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u53ef\u8981\u7cdf\u5566\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5c0f\u5973\u513f\u5bb6\u4e0d\u89e3\u4e8b\uff0c\u98de\u5200\u524a\u53d1\u60f9\u9ebb\u70e6\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e00\u56de\u3000\u5669\u8017\u4f20\u6765\u3000\u60ac\u5934\u60ca\u4fa0\u5973\u3000\u5947\u4eba\u5ffd\u73b0\u3000\u94c1\u638c\u8d25\u5996\u50e7 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u56db\u5341\u4e00\u56de \u5669\u8017\u4f20\u6765\u3000\u60ac\u5934\u60ca\u4fa0\u5973 \u5947\u4eba\u5ffd\u73b0\u3000\u94c1\u638c\u8d25\u5996\u50e7 \u3000\u3000\u51af\u7433\u6115\u7136\u4e0d\u77e5\u6240\u4ee5\uff0c\u674e\u6cbb\u9053\uff1a\u201c\u963f\u7433\uff0c\u4f60\u505a\u4e8b\u53ea\u56fe\u4e00\u65f6\u75db\u5feb\uff0c\u53ef\u4e0d\u60f3\u60f3\u90a3\u6768\u67f3\u9752\u7684\u7236\u4eb2 \u66fe\u662f\u5510\u5927\u54e5\u7684\u6069\u5e08\u3002\u4fd7\u8bed\u8bf4\u5f97\u597d\uff1a\u4e0d\u770b\u50e7\u9762\u770b\u4f5b\u9762\uff0c\u4f60\u600e\u4e48\u4e00\u52a8\u624b\u5c31\u7528\u98de\u5200\u524a\u6389\u4e86\u4eba\u5bb6\u7684\u5934 \u53d1\u3002\u8fd9\u4e00\u6765\uff0c\u4e8b\u60c5\u53ef\u4e0d\u66f4\u9ebb\u70e6\u5417\uff1f\u201d\u51af\u7433\u6c14\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u9ebb\u70e6\uff0c\u662f\u6211\u7ed3\u7684\u7cb1\u5b50\uff0c\u5f85\u6211\u53bb\u89e3\u5f00 \u4fbf\u662f\u3002\u201d\u51af\u745b\u5fd9\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60\u53ef\u522b\u518d\u751f\u4e8b\u4e86\u3002\u201d\u51af\u7433\u4e0d\u518d\u8a00\u8bed\uff0c\u6697\u4e2d\u5374\u53c8\u76d8\u7b97\u4e3b\u610f\u3002 \u3000\u3000\u51af\u745b\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u54b1\u4eec\u5148\u56de\u53bb\u5427\u3002\u201d\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u89c1\u897f\u5357\u89d2\u5929\u8fb9\u7a81\u7136\u5347\u8d77\u4e00\u6735\u7130\u706b\uff0c\u63a5 \u7740\u53c8\u662f\u51e0\u6735\u3002\u51af\u7433\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u662f\u97e9\u91cd\u5c71\u7684\u86c7\u7130\u7bad\uff0c\u4ed6\u53c8\u548c\u8c01\u4ea4\u4e0a\u624b\u4e86\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u53c8 \u542c\u5f97\u201c\u545c\u545c\u201d\u7684\u54cd\u7bad\u4e4b\u58f0\uff0c\u968f\u7740\u5929\u98ce\u9690\u9690\u4f20\u6765\uff0c\u4e00\u957f\u4e8c\u77ed\uff0c\u54cd\u4e86\u4e24\u6b21\u3002\u674e\u51b6\u51dd\u795e\u7ec6\u542c\uff0c\u9053\uff1a \u201c\u4e0d\u597d\uff0c\u8fd9\u662f\u7518\u5927\u4fa0\u547c\u63f4\u7684\u8baf\u53f7\uff0c\u6211\u5728\u676d\u5dde\u542c\u8fc7\u3002\u201d\u51af\u7433\u9065\u671b\u706b\u7130\u5347\u5904\uff0c\u9053\uff1a\u201c\u5c31\u5728\u5916\u9762\u90a3 \u4e00\u5ea7\u5c71\u5cf0\uff0c\u770b\u6765\u4e0d\u8fdc\u3002\u201d\u674e\u6cbb\u5728\u5929\u5c71\u957f\u5927\uff0c\u5bf9\u5c71\u8def\u7d20\u6240\u719f\u6089\uff0c\u671b\u4e86\u4e00\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u770b\u6765\u4e0d \u8fdc\uff0c\u8d70\u8d77\u6765\u53ef\u5f97\u534a\u5929\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u65e2\u7136\u662f\u7518\u5927\u4fa0\u7684\u547c\u63f4\u8baf\u53f7\uff0c\u54b1\u4eec\u7406\u5e94\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u90a3\u65e5\u4e0e\u51af\u745b\u5206\u624b\uff0c\u5206\u9053\u6c42\u533b\uff0c\u5230\u516b\u8fbe\u5cad\u4e1c\u9762\u7684\u5eb7\u5e84\uff0c\u6c42\u89c1\u5e9f\u56ed\u8001\u4eba\u7684\u597d\u53cb\u9648\u753b \u5e08\uff0c\u90a3\u753b\u5e08\u4e00\u6d3e\u540d\u58eb\u6d3e\u5934\uff0c\u7af9\u95e8\u534a\u63a9\uff0c\u5728\u91cc\u9762\u996e\u9152\u4f5c\u753b\u3002\u5415\u56db\u5a18\u6572\u95e8\u6572\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u4f46\u89c1\u4ed6 \u5728\u91cc\u9762\u7684\u7af9\u6797\u4e0b\u996e\u4e00\u53e3\u9152\u753b\u4e24\u7b14\u753b\uff0c\u805a\u7cbe\u4f1a\u795e\uff0c\u597d\u50cf\u4e0d\u77e5\u95e8\u5916\u6709\u4eba\u4f3c\u7684\u3002 \u3000\u3000\u5415\u56db\u5a18\u6084\u6084\u7684\u63a8\u5f00\u7af9\u95e8\uff0c\u8d70\u8fdb\u56ed\u4e2d\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u4e0d\u89c9\u5446\u4e86\u3002\u90a3\u9648\u753b\u5e08\u753b\u7684\u6b63\u662f\u5979\u7956\u7236\u5415 \u7559\u826f\u7684\u8096\u50cf\uff0c\u753b\u4e2d\u5415\u7559\u826f\u7aef\u5750\u6267\u7740\u300a\u6625\u79cb\u300b\u53cc\u76ee\u70af\u70af\u6709\u795e\uff0c\u65c1\u8fb9\u4f8d\u7acb\u4e8c\u4eba\uff0c\u4e00\u4e2a\u662f\u4e25\u6d2a\u9035\uff0c \u4e00\u4e2a\u662f\u6c88\u5728\u5bbd\u3002\u6c88\u5728\u5bbd\u7684\u50cf\u5c1a\u672a\u753b\u597d\uff0c\u4f46\u4ece\u7740\u58a8\u7684\u8f6e\u5eca\u5df2\u5206\u8fa8\u51fa\u6765\u3002 \u3000\u3000\u5415\u56db\u5a18\u54b3\u4e86\u4e00\u58f0\uff0c\u90a3\u753b\u5e08\u7adf\u662f\u201c\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\u201d\uff0c\u4ecd\u7136\u5728\u805a\u7cbe\u4f1a\u795e\u4f5c\u753b\u3002\u5415\u56db\u5a18 \u4e00\u76b1\u7709\u5934\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5582\uff0c\u4f60\u753b\u5f97\u4e0d\u50cf\u5475\uff01\u201d \u3000\u3000\u90a3\u753b\u5e08\u72b9\u5982\u88ab\u4eba\u7a81\u7136\u6253\u4e86\u4e00\u62f3\u4f3c\u7684\uff0c\u8df3\u5c06\u8d77\u6765\uff0c\u7741\u773c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u6211\u753b\u7684\u6709\u54ea\u70b9\u4e0d \u50cf\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u5148\u544a\u8bc9\u6211\uff0c\u5e9f\u56ed\u8001\u4eba\u73b0\u5728\u4f55\u5904\uff1f\u6211\u518d\u544a\u8bc9\u4f60\u54ea\u70b9\u753b\u5f97\u4e0d\u50cf\u3002\u201d\u5415 \u56db\u5a18\u5df2\u6478\u5230\u4ed6\u827a\u4eba\u4e56\u50fb\u7684\u813e\u6027\uff0c\u56e0\u6b64\u4e5f\u5c31\u4e0d\u4ee5\u666e\u901a\u7684\u5ba2\u5957\u8bf4\u8bdd\u548c\u4ed6\u4ea4\u8c08\u3002 \u3000\u3000\u90a3\u753b\u5e08\u53c8\u77aa\u4e86\u5979\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u8981\u627e\u5e9f\u56ed\u8001\u4eba\u5462\uff0c\u4f60\u627e\u4ed6\u505a\u4ec0\u4e48\uff1f\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a \u201c\u81ea\u7136\u662f\u627e\u4ed6\u6cbb\u75c5\u4e86\uff0c\u8fd8\u7528\u8bf4\u5417\uff1f\u201d \u3000\u3000\u90a3\u753b\u5e08\u9053\uff1a\u201c\u6cbb\u4e00\u4e2a\u4eba\u7684\u75c5\u6709\u4ec0\u4e48\u7d27\u8981\uff1f\u800c\u4e14\u4ed6\u4e5f\u4e0d\u80af\u968f\u4fbf\u533b\u4eba\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4ed6\u662f \u4e00\u4ee3\u540d\u533b\uff0c\u82e5\u7136\u78b0\u5230\u7591\u96be\u602a\u75c7\uff0c\u5c31\u5982\u4f60\u78b0\u5230\u7edd\u5999\u5c71\u6c34\u4e00\u6837\uff0c\u5c82\u80af\u4e0d\u65bd\u5c55\u8eab\u624b\u3002\u201d\u90a3\u753b\u5e08\u54c8\u54c8 \u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u5f88\u6709\u9053\u7406\u3002\u5e9f\u56ed\u8001\u4eba\u82e5\u78b0\u5230\u7591\u96be\u6742\u75c7\uff0c\u90a3\u7684\u786e\u662f\u4f60\u4e0d\u8bf7\u4ed6\u4e5f\u8981\u53bb\u7684\u3002\u4f60 \u8bf4\u7684\u662f\u4ec0\u4e48\u75c5\u75c7\uff0c\u662f\u9ebb\u75af\u5417\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u4e0d\u89c9\u4e00\u6115\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u9ebb\u75af\uff1f\u201d\u90a3\u753b\u5e08\u9053\uff1a\u201c\u5e9f\u56ed\u8001\u4eba\u5f88\u4e45\u4ee5\u6765\u5df2\u5728\u601d\u7d22\u533b\u6cbb \u9ebb\u75af\u4e4b\u6cd5\uff0c\u4ed6\u5e38\u8bf4\u4e16\u4eba\u90fd\u628a\u9ebb\u75af\u5f53\u4f5c\u7edd\u75c7\uff0c\u6211\u504f\u8981\u60f3\u51fa\u533b\u7edd\u75c7\u4e4b\u65b9\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4ed6\u60f3 \u51fa\u4e86\u6ca1\u6709\uff1f\u201d\u90a3\u753b\u5e08\u9053\uff1a\u201c\u6ca1\u6709\u5440\uff01\u6240\u4ee5\u6211\u6628\u5929\u624d\u6d3e\u4eba\u53bb\u8bf7\u4ed6\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u542c\u4ed6\u8bdd\u91cc\u6709\u56e0\uff0c\u597d\u5947\u4e4b\u5fc3\u5927\u8d77\uff0c\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u96be\u9053\u662f\u4f60\u60f3\u51fa\u4e48\uff1f\u201d\u90a3\u753b\u5e08\u9053\uff1a \u201c\u6211\u5bf9\u533b\u4e8b\u4e00\u7a8d\u4e0d\u901a\uff0c\u4e0d\u8fc7\u6211\u5374\u77e5\u9053\u9ebb\u75af\u5e76\u975e\u7edd\u75c7\u4e86\u3002\u201d \u3000\u3000\u90a3\u753b\u5e08\u8bf4\u5f97\u5174\u8d77\uff0c\u653e\u4e0b\u753b\u7b14\uff0c\u7eed\u9053\uff1a\u201c\u524d\u51e0\u5929\u6211\u4eec\u8fd9\u91cc\u6765\u4e86\u4e00\u4e2a\u50bb\u4eba\uff0c\u628a\u6751\u5b50\u91cc\u7684\u51e0\u4e2a \u9ebb\u75af\u75c5\u4eba\u62a2\u53bb\uff0c\u6709\u4eba\u8ffd\u4ed6\uff0c\u5c82\u77e5\u4ed6\u529b\u5927\u65e0\u7a77\uff0c\u968f\u624b\u5288\u65ad\u51e0\u68f5\u5927\u6811\uff0c\u628a\u90a3\u4e9b\u4eba\u5413\u4e86\u56de\u6765\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5fc5\u662f\u6bd2\u9f99\u5c0a\u8005\u65e0\u7591\u3002\u201d\u90a3\u753b\u5e08\u7eed\u9053\uff1a\u201c\u4f60\u731c\u8fd9\u602a\u4eba\u628a\u60a3\u9ebb \u75af\u75c5\u7684\u62a2\u53bb\u505a\u4ec0\u4e48\uff1f\u4f60\u4e00\u5b9a\u731c\u4e0d\u5230\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4ed6\u8981\u5c06\u8fd9\u4e9b\u9ebb\u75af\u75c5\u4eba\u533b\u597d\uff0c\u8fd9\u6709\u4ec0\u4e48\u731c\u60f3\u4e0d\u5230\u3002\u201d\u90a3\u753b\u5e08\u62cd\u624b\u9053\uff1a\u201c\u5bf9 \u5475\uff01\u4f60\u731c\u7740\u4e86\uff0c\u6211\u5f53\u65f6\u5374\u731c\u4e0d\u5230\u3002\u4ed6\u628a\u90a3\u51e0\u4e2a\u9ebb\u75af\u75c5\u4eba\u5b89\u653e\u5728\u4e00\u4e2a\u5c71\u6d1e\u91cc\uff0c\u5c31\u662f\u516b\u8fbe\u5cad\u897f\u5357 \u9762\u79ef\u77f3\u5c71\u7684\u90a3\u4e2a\u5927\u5ca9\u6d1e\uff0c\u4f60\u53bb\u8fc7\uff1f\u201d\u5415\u56db\u5a18\u6447\u4e86\u6447\u5934\uff0c\u5fc3\u9053\uff1a\u201c\u6bd2\u9f99\u5c0a\u8005\u6765\u5230\u6b64\u95f4\uff0c\u5c11\u4e0d\u5f97 \u6211\u8981\u53bb\u63a2\u4ed6\u3002\u201d\u90a3\u753b\u5e08\u7eed\u9053\uff1a\u201c\u6211\u53c8\u626f\u8fdc\u4e86\uff0c\u4f60\u542c\u6211\u8bf4\uff0c\u8fd9\u602a\u4eba\u957f\u76f8\u51f6\u6076\uff0c\u5fc3\u5730\u5374\u597d\u3002\u4e0d\u8fc7 \u4e24\u5929\uff0c\u5c31\u628a\u4e09\u4e2a\u75c5\u60a3\u8f83\u8f7b\u7684\u9ebb\u75af\u75c5\u8005\u533b\u597d\u4e86\u653e\u56de\u6765\uff0c\u76ae\u5149\u8089\u6d01\uff0c\u5b8c\u5168\u548c\u5e73\u5e38\u4eba\u4e00\u6837\u3002\u8fd8\u6709\u4e00 \u4e2a\u8f83\u91cd\u7684\uff0c\u73b0\u5728\u8fd8\u7ed9\u4ed6\u7559\u5728\u5c71\u6d1e\u91cc\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u53c8\u9053\uff1a\u201c\u53f6\u8001\u5934\u513f\uff08\u5e9f\u56ed\u8001\u4eba\uff09\u672c\u6765\u662f\u8981 \u5230\u6211\u8fd9\u91cc\u4f4f\u7684\uff0c\u4f46\u5374\u88ab\u4ed6\u7684\u90a3\u4e2a\u59d3\u6768\u7684\u5f92\u5f1f\u8bf7\u53bb\u4e86\uff0c\u4f4f\u5728\u5357\u53e3\u3002\u4ed6\u82e5\u4e0d\u8d76\u5feb\u6765\uff0c\u53c8\u8981\u9519\u8fc7\u673a \u4f1a\u4e86\u3002\u201d\u4ed6\u54ea\u91cc\u6599\u60f3\u5f97\u5230\uff0c\u4ed6\u548c\u5415\u56db\u5a18\u8bf4\u8bdd\u4e4b\u9645\uff0c\u6b63\u662f\u5e9f\u56ed\u8001\u4eba\u6bd5\u547d\u4e4b\u65f6\u3002 \u3000\u3000\u90a3\u753b\u5e08\u8bf4\u5b8c\u4e4b\u540e\uff0c\u89c1\u5415\u56db\u5a18\u9732\u51fa\u7b11\u5bb9\uff0c\u62cd\u817f\u8bf4\u9053\uff1a\u201c\u6211\u5c3d\u8bf4\u9ebb\u75af\uff0c\u5fd8\u8bb0\u95ee\u4f60\u4e86\uff0c\u4f60\u8981\u4ed6 \u533b\u7684\u662f\u4ec0\u4e48\u75c5\uff1f\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u6253\u6270\u4e86\u4f60\u8bb8\u4e45\uff0c\u6211\u544a\u8f9e\u4e86\uff01\u201d\u5415\u56db\u5a18\u5fc3\u60f3\uff1a\u5e9f\u56ed \u8001\u4eba\u65e2\u5728\u5357\u53e3\u6768\u5bb6\uff0c\u90a3\u4e48\u51af\u745b\u4e00\u5b9a\u5df2\u627e\u5230\u4ed6\u4e86\u3002\u8fd9\u753b\u5e08\u6628\u5929\u624d\u6d3e\u4eba\u53bb\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u8d76\u8fc7\u51af\u745b\u7684 \u524d\u5934\u3002 \u3000\u3000\u5415\u56db\u5a18\u6ee1\u6000\u9ad8\u5174\uff0c\u8131\u53e3\u8bf4\u51fa\u544a\u8f9e\u7684\u8bdd\uff0c\u4e0d\u6599\u90a3\u753b\u5e08\u4e00\u628a\u5c06\u5979\u62c9\u7740\uff0c\u6012\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8bf4\u7684 \u8bdd\u4e0d\u7b97\u6570\u4e48\uff1f\u4f60\u8bf4\uff01\u6211\u54ea\u70b9\u753b\u5f97\u4e0d\u50cf\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u62fe\u8d77\u753b\u7b14\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u5e45\u753b\u6709\u4e2a\u6781\u5927\u7684\u6f0f\u6d1e\u3002\u4e25\u6d2a\u9035\u662f\u5415\u7559\u826f\u5148\u751f\u7684\u5f97\u610f\u5f1f\u5b50\uff0c \u4f60\u753b\u4e25\u6d2a\u9035\u4f8d\u7acb\u5728\u4ed6\u7684\u8eab\u8fb9\uff0c\u90a3\u81ea\u7136\u53ef\u4ee5\uff0c\u53ef\u662f\u6c88\u5728\u5bbd\u4e43\u662f\u5415\u8446\u4e2d\u7684\u5b66\u751f\uff0c\u5415\u8446\u4e2d\u662f\u5415\u7559\u826f \u7684\u513f\u5b50\uff0c\u6c88\u5728\u5bbd\u8fde\u5415\u7559\u826f\u5148\u751f\u7684\u9762\u90fd\u6ca1\u89c1\u8fc7\uff0c\u4ed6\u53c8\u600e\u80fd\u4f8d\u7acb\u5728\u4ed6\u7684\u8eab\u8fb9\uff01\u201d\u90a3\u753b\u5e08\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u4f60\u61c2\u4ec0\u4e48\uff1f\u8fd9\u771f\u662f\u5987\u4eba\u5b7a\u5b50\u4e4b\u89c1\uff01\u201d\u5415\u56db\u5a18\u751f\u5e73\u6700\u6068\u522b\u4eba\u8f7b\u89c6\u5973\u5b50\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4e0d \u6b62\u662f\u8fd9\u4e2a\u6f0f\u6d1e\uff0c\u4f60\u753b\u4e5f\u753b\u5f97\u4e0d\u50cf\uff01\u201d \u3000\u3000\u90a3\u9648\u753b\u5e08\u540d\u6ee1\u5929\u4e0b\uff0c\u542c\u5415\u56db\u5a18\u77e2\u53e3\u8bf4\u4ed6\u753b\u5f97\u4e0d\u4f3c\uff0c\u751a\u4e3a\u607c\u6012\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u6709\u591a\u5927\u5e74 \u7eaa\uff1f\u8c05\u4f60\u4e5f\u672a\u89c1\u8fc7\u665a\u6751\uff08\u5415\u7559\u826f\u5b57\uff09\u524d\u8f88\uff0c\u4f60\u600e\u77e5\u6211\u753b\u5f97\u50cf\u4e0e\u4e0d\u50cf\uff1f\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4f60\u753b \u7684\u5415\u8001\u5148\u751f\uff0c\u5f62\u662f\u6781\u4f3c\u4e86\uff0c\u795e\u5374\u4e0d\u4f3c\uff0c\u4f60\u753b\u5f97\u4ed6\u9053\u8c8c\u5cb8\u7136\uff0c\u4e00\u8138\u8083\u6740\u4e4b\u6c14\uff0c\u4f46\u5b9e\u5728\u4ed6\u5374\u662f\u5e73 \u6613\u8fd1\u4eba\uff0c\u8138\u4e0a\u5e38\u5e26\u7b11\u5bb9\u7684\u3002\u6211\u77e5\u9053\u4f60\u662f\u60f3\u63cf\u7ed8\u4ed6\u51db\u7136\u4e0d\u53ef\u72af\u7684\u6b63\u6c14\uff0c\u4f46\u5374\u753b\u4e0d\u51fa\u4ed6\u7684\u795e\u60c5\u7b11 \u8c8c\u3002\u201d\u90a3\u753b\u5e08\u5c11\u65f6\u66fe\u968f\u7236\u6267\u542c\u8fc7\u5415\u7559\u826f\u8bb2\u5b66\uff0c\u95fb\u8a00\u4f9d\u7136\u4e00\u60ca\uff0c\u5fc3\u5ff5\u679c\u662f\u5982\u6b64\u3002\u5415\u56db\u5a18\u7eed\u9053\uff1a \u201c\u5415\u8001\u5148\u751f\u8fd8\u662f\u5f62\u4f3c\u795e\u4e0d\u4f3c\uff0c\u90a3\u4f8d\u7acb\u7684\u6c88\u5728\u5bbd\u5374\u662f\u5f62\u795e\u90fd\u4e0d\u76f8\u4f3c\u3002\u201d\u90a3\u753b\u5e08\u6012\u9053\uff1a\u201c\u5c82\u6709\u5f62 \u4e5f\u4e0d\u4f3c\u7684\u9053\u7406\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6c88\u5728\u5bbd\u7684\u8138\u578b\u4e0d\u662f\u5982\u6b64\uff0c\u4f60\u753b\u7684\u53ea\u6709\u4e24\u4e09\u5206\u76f8\u4f3c\uff0c\u6211\u4ee3\u4f60\u753b \u5427\u3002\u201d\u62fe\u8d77\u753b\u7b14\uff0c\u51e0\u7b14\u63cf\u6210\u3002 \u3000\u3000\u90a3\u753b\u5e08\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u753b\u7684\u662f\u8c01\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5415\u7559\u826f\u7b2c\u4e8c\u4ee3\u4f20\u4eba\u6c88\u5728 \u5bbd\u3002\u201d\u90a3\u753b\u5e08\u9053\uff1a\u201c\u4f60\u662f\u8c01\u4eba\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4f60\u753b\u7684\u5415\u7559\u826f\u4fbf\u662f\u6211\u7956\u7236\uff0c\u6c88\u5728\u5bbd\u5728\u6211\u5bb6\u4e2d \u957f\u5927\u3002\u201d\u90a3\u753b\u5e08\u9053\uff1a\u201c\u4f60\u9a97\u8c01\uff1f\u4f60\u753b\u7684\u6839\u672c\u5c31\u4e0d\u662f\u6c88\u5728\u5bbd\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u6211\u753b\u7684\u82e5\u8fd8\u4e0d\u4f3c\uff0c\u5929\u4e0b\u4e5f\u65e0\u7b2c\u4e8c\u4e2a\u753b\u5f97\u76f8\u4f3c\u4e86\uff01\u201d\u90a3\u753b\u5e08\u51b7\u7b11\u9053\uff1a\u201c\u4f60 \u82e5\u771f\u662f\u5415\u7559\u826f\u7684\u5b59\u5973\u513f\uff0c\u6c88\u5728\u5bbd\u7684\u597d\u670b\u53cb\uff0c\u4eca\u65e5\u4e5f\u4e0d\u8c05\u6709\u6b64\u95f2\u5fc3\u4e0e\u6211\u8bba\u753b\uff01\u201d\u5ffd\u7136\u53d6\u51fa\u4e00\u5377 \u5ba3\u7eb8\uff0c\u4e0a\u9762\u5199\u6ee1\u8747\u5934\u5c0f\u6977\uff0c\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u63a5\u8fc7\u4e00\u770b\uff0c\u5374\u662f\u4e00\u4efd\u4f20\u6284\u7684\u201c\u8c15\u65e8\u201d\uff0c\u8c15\u65e8\u9053\uff1a\u201c\u4e3a\u5415\u7559\u826f\u6848\u5e76\u520a\u523b\u5927\u4e49\u89c9\u8ff7\u5f55 \u9881\u8c15\u5929\u4e0b\u3002\u201d\u5415\u56db\u5a18\u4e00\u770b\uff0c\u53d8\u4e86\u989c\u8272\uff0c\u53ea\u89c1\u90a3\u201c\u8c15\u65e8\u201d\u5199\u7684\u662f\uff1a \u3000\u3000\u201c\u81ea\u53e4\u5e1d\u738b\u4e4b\u6709\u5929\u4e0b\uff0c\u83ab\u4e0d\u7531\u6000\u4fdd\u4e07\u6c11\uff0c\u6069\u52a0\u56db\u6d77\uff0c\u81ba\u4e0a\u5929\u4e4b\u7737\u547d\uff0c\u534f\u4ebf\u5146\u4e4b\u6b22\u5fc3\uff0c\u7528 \u80fd\u7edf\u4e00\u5bf0\u533a\uff0c\u5782\u5bd0\u5955\u4e16\u3002\u76d6\u751f\u6c11\u4e4b\u9053\uff0c\u6068\u6709\u5fb7\u8005\u53ef\u4e3a\u5929\u4e0b\u541b\u3002\u2026\u2026\u592b\u6211\u671d\u65e2\u4ef0\u627f\u5929\u547d\uff0c\u4e3a\u4e2d \u5916\u5168\u6c11\u4e4b\u4e3b\uff0c\u5219\u6240\u4ee5\u8499\u629a\u7ee5\u7231\u80b2\u8005\uff0c\u4f55\u5f97\u4ee5\u9635\u5937\u800c\u6709\u6b8a\u89c6\uff1f\u2026\u2026\u4e43\u9006\u8d3c\u5415\u7559\u826f\u597d\u4e71\u4e50\u7978\uff0c\u79c1 \u4e3a\u8457\u8ff0\uff0c\u5984\u8c13\u5fb7\u4f51\u4ee5\u540e\uff0c\u5929\u5730\u5927\u53d8\uff0c\u67e5\u53e4\u672a\u7ecf\uff0c\u4e8e\u4eca\u590d\u89c1\u3002\u800c\u9006\u5f92\u4e25\u6d2a\u9035\u7b49\uff0c\u8f6c\u76f8\u9644\u548c\uff0c\u5907 \u6781\u7316\u72c2\u2026\u2026\u201d\u4ee5\u4e0b\u4fbf\u662f\u8fde\u7bc7\u7d2f\u8d58\u9a73\u5415\u7559\u826f\u7684\u8bdd\uff0c\u6240\u4ee5\u540d\u4e3a\u201c\u5927\u4e49\u89c9\u8ff7\u3002\u201d\u6ee1\u7eb8\u80e1\u8a00\uff0c\u5415\u56db\u5a18 \u4e5f\u65e0\u5fc3\u7ec6\u9605\u3002\u90a3\u201c\u8c15\u65e8\u201d\u6700\u540e\u5199\u9053\uff1a\u201c\u671d\u8bae\u5415\u7559\u826f\u5415\u8446\u4e2d\u4ff1\u622e\u5c38\u67d0\u793a\uff0c\u4e25\u6d2a\u9035\u6c88\u5728\u5bbd\u7686\u65a9 \u51b3\uff0c\u65cf\u4eba\u4ff1\u8bdb\u6b9b\uff0c\u5b59\u8f88\u53d1\u5f80\u5b81\u53e4\u5854\u7ed9\u62ab\u7532\u4eba\u4e3a\u5974\u3002\u4ef0\u5929\u4e0b\u4ebf\u4e07\u81e3\u6c11\uff0c\u51db\u5782\u4e3a\u6212\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u770b\u4e86\uff0c\u6709\u5982\u5de8\u96f7\u8f70\u9876\uff0c\u98a4\u58f0\u8bf4\u9053\uff1b\u201c\u6c88\u5728\u5bbd\u88ab\u6740\u4e86\u4e48\uff1f\u201d\u90a3\u753b\u5e08\u9053\uff1a\u201c\u5927\u524d\u5929\u4e25 \u6c88\u4e24\u4f4d\u4e49\u58eb\u88ab\u5904\u65a9\u4e4b\u65f6\uff0c\u6211\u521a\u597d\u5728\u4eac\uff0c\u968f\u4f17\u5728\u6cd5\u573a\u77bb\u4ef0\u9057\u5bb9\uff0c\u5f97\u89c1\u4e00\u9762\uff0c\u54fc\uff0c\u4f60\u8fd8\u6709\u5fc3\u809d\u8bf4 \u6211\u753b\u5f97\u4e0d\u50cf\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u9762\u8272\u82cd\u767d\uff0c\u6447\u6447\u6b32\u5012\uff0c\u90a3\u753b\u5e08\u7eed\u9053\uff1a\u201c\u6211\u5c82\u4e0d\u77e5\u6c88\u5728\u5bbd\u4e43\u5415\u8446\u4e2d\u5b66\u751f\uff0c\u975e\u665a\u6751\u524d \u8f88\u4eb2\u6388\uff1f\u4f46\u4ed6\u65e2\u4e3a\u6b64\u6848\u6210\u4ec1\uff0c\u800c\u4ed6\u53c8\u5e7f\u4f20\u665a\u6751\u5b66\u8bf4\u7cbe\u4e49\uff0c\u5219\u6211\u5199\u4ed6\u4f8d\u7acb\u5728\u665a\u6751\u5148\u751f\u4e4b\u65c1\uff0c\u53c8 \u6709\u4f55\u4e0d\u53ef\uff1f\u201d\u6b63\u5728\u7d6e\u7d6e\u53e8\u53e8\u4e4b\u9645\uff0c\u5415\u56db\u5a18\u5ffd\u7136\u4e00\u624b\u5c06\u4ed6\u62c9\u7740\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u771f\u4e2a\u77a7\u6e05\u695a\u4e86\uff1f\u6c88 \u5728\u5bbd\u7684\u6837\u5b50\u5c31\u50cf\u4f60\u6240\u753b\u7684\u90a3\u4eba\u4e48\uff1f\u201d\u90a3\u753b\u5e08\u6447\u5934\u6643\u8111\u8bf4\u9053\uff1a\u201c\u8001\u592b\u522b\u7684\u4e0d\u6562\u5938\u53e3\uff0c\u8fd9\u753b\u7b14\u4f20 \u771f\u7684\u96d5\u866b\u5c0f\u6280\uff0c\u96be\u9053\u8fd8\u6709\u4e0d\u4f3c\u4e48\uff1f\u4e25\u6c88\u4e24\u4f4d\u4e49\u58eb\u4e4b\u5934\uff0c\u73b0\u5728\u8fd8\u60ac\u5728\u57ce\u95e8\u4e4b\u4e0a\uff0c\u4f60\u82e5\u8fd8\u4e0d\u4fe1\uff0c \u53ef\u4ee5\u53bb\u770b\uff01\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5415\u56db\u5a18\u731b\u7136\u628a\u4ed6\u4e00\u63a8\uff0c\u7eb5\u6b65\u5954\u51fa\u95e8\u5916\u3002\u90a3\u753b\u5e08\u722c\u4e86\u8d77\u6765\uff0c\u8fde\u9053\uff1a \u201c\u5c82\u6709\u6b64\u7406\uff0c\u5c82\u6709\u6b64\u7406\uff01\u5192\u5145\u665a\u6751\u5148\u751f\u7684\u5b59\u5973\u4e5f\u8fd8\u7f62\u4e86\uff0c\u8fd8\u6562\u5984\u81ea\u8ba5\u8bc4\u6211\u7684\u4e39\u9752\u5999\u54c1\uff0c\u4fe1\u53e3 \u96cc\u9ec4\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u7d20\u6027\u5a34\u96c5\uff0c\u5e94\u4ed8\u5927\u4e8b\uff0c\u4ece\u4e0d\u5fc3\u614c\u3002\u8fd9\u6b21\u95fb\u8baf\u5fc3\u60ca\uff0c\u6025\u4e71\u5931\u6001\uff0c\u8fd8\u662f\u751f\u5e73\u7684\u7b2c\u4e00 \u6b21\u3002\u8dd1\u51fa\u4e86\u5927\u95e8\u4e4b\u540e\uff0c\u624d\u731b\u7136\u9192\u8d77\uff0c\u5bf9\u90a3\u753b\u5e08\u751a\u4e3a\u62b1\u6b49\uff0c\u5fc3\u9053\uff1a\u201c\u5230\u5e95\u662f\u6211\u4fee\u517b\u529f\u592b\uff0c\u8fd8\u672a \u5230\u5bb6\u3002\u201d\u53ef\u662f\u5979\u6015\u90a3\u753b\u5e08\u4e66\u5446\u5b50\u7684\u813e\u6c14\uff0c\u6015\u88ab\u4ed6\u7f20\u4e2a\u4e0d\u4f11\uff0c\u56e0\u6b64\u4e5f\u5c31\u987e\u4e0d\u53ca\u518d\u56de\u53bb\u9053\u6b49\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u5415\u56db\u5a18\u6e10\u6e10\u6291\u6b62\u6fc0\u52a8\u4e4b\u60c5\uff0c\u9759\u5fc3\u7ec6\u60f3\uff0c\u60f3\u6c88\u5728\u5bbd\u9690\u5c3e\u4ed9\u971e\u5cad\uff0c\u4e03\u516b\u5e74\u6765\u8db3 \u4e0d\u51fa\u6237\uff0c\u5916\u4eba\u5982\u4f55\u77e5\u9053\uff1f\u53c8\u60f3\u8d77\u90a3\u753b\u5e08\u6240\u753b\u4e4b\u50cf\uff0c\u53ea\u6709\u4e09\u5206\u76f8\u4f3c\uff0c\u4e5f\u5927\u662f\u53ef\u7591\uff0c\u5fc3\u9053\uff1a\u201c\u6211 \u4f55\u4e0d\u5c31\u8fdb\u4eac\u57ce\u770b\u770b\uff1f\u51af\u745b\u5fc5\u80fd\u8bf7\u5230\u5e9f\u56ed\u8001\u4eba\uff0c\u6653\u6f9c\u4e4b\u4e8b\uff0c\u6211\u6682\u53ef\u4e0d\u5fc5\u6302\u5fc3\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u811a\u7a0b\u6781\u5feb\uff0c\u508d\u665a\u65f6\u5206\uff0c\u5df2\u5230\u4eac\u57ce\u3002\u5317\u4eac\u57ce\u6709\u4e5d\u4e2a\u57ce\u95e8\uff0c\u6848\u60c5\u91cd\u5927\u7684\u53db\u9006\uff0c\u88ab\u671d\u5ef7 \u8885\u9996\u4e4b\u540e\uff0c\u4fbf\u6392\u65e5\u5c06\u9996\u7ea7\u5728\u5404\u4e2a\u57ce\u95e8\u60ac\u6302\uff0c\u540d\u4e3a\u201c\u60ac\u9996\u4e5d\u95e8\u201d\u3002\u5415\u56db\u5a18\u8f6c\u4e86\u51e0\u4e2a\u57ce\u95e8\uff0c\u8d70\u5230 \u897f\u534e\u95e8\u5916\uff0c\u679c\u7136\u89c1\u7740\u57ce\u95e8\u4e4b\u4e0a\uff0c\u7ad6\u7740\u4e24\u679d\u9ad8\u9ad8\u7684\u6728\u7aff\uff0c\u6bcf\u679d\u6728\u7aff\u4e0a\u60ac\u7740\u4e00\u4e2a\u4eba\u5934\u3002\u66ae\u8272\u82cd\u832b \u4e2d\u770b\u4e0d\u6e05\u695a\uff0c\u5415\u56db\u5a18\u5fc3\u5934\u535c\u535c\u4e71\u8df3\uff0c\u770b\u4e86\u4e00\u9635\uff0c\u89c1\u57ce\u95e8\u4e0a\u53ea\u6709\u56db\u540d\u666e\u901a\u7684\u536b\u58eb\u770b\u5b88\uff0c\u5fc3\u4e2d\u66f4 \u662f\u6000\u7591\u3002 \u3000\u3000\u5415\u56db\u5a18\u54ea\u628a\u8fd9\u51e0\u540d\u536b\u571f\u653e\u5728\u5fc3\u4e0a\uff0c\u770b\u4e86\u4e00\u9635\uff0c\u89d1\u51c6\u65f6\u673a\uff0c\u8db3\u5c16\u4e00\u70b9\uff0c\u5355\u638c\u5728\u57ce\u5899\u4e0a\u4e00 \u6309\uff0c\u75be\u5982\u9e70\u96bc\u7684\u5e73\u7a7a\u63a0\u4e0a\uff0c\u56db\u540d\u536b\u58eb\u53d1\u4e00\u58f0\u558a\uff0c\u8fd8\u672a\u770b\u5f97\u6e05\u695a\uff0c\u5df2\u88ab\u5415\u56db\u5a18\u65cb\u98ce\u822c\u7684\u4e00\u4e2a\u76d8 \u65cb\uff0c\u9010\u4e00\u70b9\u4e86\u7a74\u9053\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u5415\u56db\u5a18\u5df2\u63c9\u5347\u5230\u53f3\u8fb9\u90a3\u679d\u6728\u7aff\u4e4b\u4e0a\uff0c\u5c06\u5934\u53d6\u4e0b\u4e00\u770b\uff0c\u867d\u7136\u5934\u9885\u5df2\u88ab\u5272\u4e0b\u51e0 \u65e5\uff0c\u8fd8\u4f9d\u7a00\u770b\u5f97\u51fa\u662f\u4e25\u6d2a\u9035\uff01\u8fd9\u5239\u90a3\u95f4\uff0c\u5415\u56db\u5a18\u5982\u53d7\u96f7\u51fb\uff0c\u6025\u75db\u653b\u5fc3\uff0c\u9669\u9669\u8dcc\u4e0b\uff0c\u6025\u5fd9\u5b9a\u4e86 \u5b9a\u795e\uff0c\u65bd\u5c55\u8f7b\u529f\u7edd\u6280\uff0c\u5728\u53f3\u8fb9\u6728\u7aff\u4e0a\u4e00\u8361\uff0c\u98de\u5230\u5de6\u8fb9\u6728\u7aff\u4e4b\u4e0a\uff0c\u4f38\u624b\u53d6\u90a3\u60ac\u6302\u7684\u5934\u9885\u3002\u5374\u4e0d \u6599\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u6728\u7aff\u7a81\u7136\u4ece\u4e2d\u65ad\u4e3a\u4e24\u622a\uff0c\u5415\u56db\u5a18\u5934\u4e0b\u811a\u4e0a\uff0c\u51b2\u8dcc\u4e0b\u6765\uff0c\u6728\u7aff\u4e2d\u7a7a\uff0c\u5185\u91cc \u7adf\u7136\u85cf\u7740\u673a\u5173\uff0c\u65ad\u4e3a\u4e24\u622a\u4e4b\u65f6\uff0c\u4e2d\u95f4\u7a81\u7136\u5c04\u51fa\u65e0\u6570\u98de\u7bad\uff01 \u3000\u3000\u5415\u56db\u5a18\u633d\u7740\u4e24\u9897\u5934\u9885\uff0c\u65e0\u53ef\u62b5\u5fa1\uff0c\u5c31\u5728\u90a3\u6728\u7aff\u5012\u62c6\uff0c\u77ed\u7bad\u7eb7\u98de\u4e4b\u4e2d\uff0c\u83b2\u7fd8\u5411\u4e0b\u4e00\u52fe\uff0c\u8f7b \u8f7b\u4e00\u70b9\u90a3\u4e0a\u622a\u6b63\u5728\u5411\u4e0b\u98de\u5815\u7684\u6728\u7aff\u7aff\u5c16\uff0c\u51ed\u7740\u8fd9\u4e00\u70b9\u4e4b\u529b\uff0c\u8eab\u5982\u71d5\u5b50\u659c\u98de\uff0c\u9661\u7136\u95f4\u53c8\u817e\u7a7a\u800c \u8d77\u3002\u53ea\u542c\u5f97\u545c\u545c\u58f0\u54cd\uff0c\u4e00\u4ef6\u5947\u5f62\u6697\u5668\uff0c\u7adf\u5728\u4e0b\u9762\u76d8\u65cb\u98de\u4e0a\uff0c\u5415\u56db\u5a18\u4e00\u542c\uff0c\u77e5\u662f\u97e9\u91cd\u5c71\u7684\u72ec\u95e8 \u6697\u5668\u201c\u56de\u73af\u94a9\u201d\uff0c\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u5979\u5df2\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u53cc\u5934\u5e76\u4ea4\u4e00\u624b\uff0c\u62d4\u51fa\u4e86\u971c\u534e\u5b9d \u5251\uff0c\u8fce\u7740\u90a3\u56de\u73af\u94a9\u76f4\u51b2\u4e0b\u53bb\uff01 \u3000\u3000\u56de\u73af\u94a9\u8d70\u7684\u662f\u4e4b\u5b57\u8def\uff0c\u5415\u56db\u5a18\u4e00\u51b2\u4e0d\u6765\uff0c\u5251\u5c16\u8f7b\u8f7b\u4e00\u64a9\uff0c\u90a3\u56de\u73af\u94a9\u545c\u7684\u4e00\u58f0\u4ece\u65c1\u98de\u51fa\u53c8 \u63a0\u56de\u6765\uff0c\u4f46\u5415\u56db\u5a18\u8fd9\u4e00\u51b2\u4e4b\u52bf\uff0c\u4f55\u7b49\u5feb\u6377\uff0c\u5979\u843d\u4e0b\u57ce\u5899\uff0c\u56de\u73af\u94a9\u5c1a\u5728\u534a\u7a7a\u4e2d\u76d8\u65cb\uff0c\u8ffd\u5979\u4e0d\u53ca\u3002 \u3000\u3000\u8fd9\u665a\u53ea\u6709\u4e00\u5f26\u65b0\u6708\uff0c\u51e0\u70b9\u758f\u661f\uff0c\u5415\u56db\u5a18\u5728\u767e\u5fd9\u4e4b\u4e2d\uff0c\u4e00\u77a5\u624b\u4e0a\u6240\u6362\u7684\u5934\u9885\uff0c\u53ea\u89c1\u4e0e\u6c88\u5728 \u5bbd\u679c\u6709\u51e0\u5206\u76f8\u4f3c\uff0c\u4f46\u56e0\u5272\u4e0b\u591a\u65e5\uff0c\u5934\u9885\u5e72\u7f29\uff0c\u9762\u5f62\u5df2\u6539\uff0c\u4e00\u65f6\u95f4\u77a7\u4e0d\u6e05\u695a\u3002\u5415\u56db\u5a18\u6b63\u5f85\u4ed4\u7ec6 \u8fa8\u8ba4\uff0c\u5ffd\u542c\u5f97\u4e0b\u9762\u54c8\u54c8\u5927\u7b11\uff0c\u57ce\u5899\u5357\u9762\u7684\u7f3a\u53e3\u51f9\u5904\uff0c\u7a81\u7136\u7a9c\u8d77\u4e09\u4eba\uff0c\u5206\u5e03\u5728\u4e1c\u897f\u5317\u4e09\u4e2a\u65b9 \u4f4d\uff0c\u8fd9\u4e09\u4eba\u4e43\u662f\u5929\u53f6\u6563\u4eba\u3001\u97e9\u91cd\u5c71\u548c\u54c8\u5e03\u9640\u3002 \u3000\u3000\u8981\u8bf4\u5415\u56db\u5a18\u7684\u672c\u9886\uff0c\u5982\u4eca\u5df2\u662f\u6bd4\u8fd9\u4e09\u4eba\u90fd\u8981\u9ad8\u51fa\u5c11\u8bb8\uff0c\u53ef\u662f\u65e0\u8bba\u5982\u4f55\uff0c\u5c1a\u4e0d\u80fd\u4ee5\u4e00\u654c \u4e09\uff0c\u53ef\u662f\u8fd9\u4e09\u4eba\u4e5f\u5fd2\u5947\u602a\uff0c\u7a9c\u51fa\u4e4b\u540e\uff0c\u90fd\u662f\u5404\u5b88\u65b9\u4f4d\uff0c\u5e76\u4e0d\u5305\u56f4\u5408\u51fb\u3002\u5415\u56db\u5a18\u65e0\u6687\u8003\u8651\uff0c\u5411 \u6ca1\u4eba\u628a\u5b88\u7684\u5357\u9762\u7f3a\u53e3\u4fbf\u8dd1\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u4fbf\u5f85\u8dc3\u4e0b\uff0c\u9661\u7136\u95f4\u4e00\u80a1\u5927\u529b\u8fce\u9762\u63a8\u6765\uff0c\u5415\u56db\u5a18\u5728\u534a\u7a7a \u4e2d\u7ffb\u4e86\u4e00\u4e2a\u65a4\u6597\uff0c\u56de\u73af\u94a9\u631f\u7740\u545c\u545c\u602a\u5578\u4e4b\u58f0\uff0c\u5411\u5979\u80cc\u5fc3\u6025\u88ad\uff0c\u540c\u65f6\u6709\u4e00\u56e2\u7ea2\u5f71\uff0c\u4e5f\u75be\u5982\u95ea\u7535 \u7684\u5f53\u5934\u7f69\u4e0b\uff0c\u5415\u56db\u5a18\u4e00\u4e2a\u56de\u65cb\uff0c\u5148\u907f\u654c\u653b\uff0c\u518d\u6321\u6697\u5668\uff0c\u5ffd\u89c9\u52b2\u98ce\u8d2f\u80f8\uff0c\u624b\u4e0a\u4e00\u677e\uff0c\u4e24\u9897\u5934\u9885 \u90fd\u7ed9\u6765\u4eba\u7684\u638c\u98ce\u626b\u843d\u57ce\u5899\uff0c\u90a3\u56de\u73af\u94a9\u7684\u545c\u545c\u602a\u54cd\u4e5f\u5df2\u505c\u6b62\u3002\u5415\u56db\u5a18\u811a\u5c16\u70b9\u5730\uff0c\u65cb\u8fc7\u8eab\u6765\uff0c\u53ea \u542c\u5f97\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u513f\u957f\u5f97\u597d\u4fca\uff0c\u7528\u6697\u5668\u6bc1\u4e86\u5979\u5c82\u4e0d\u53ef\u60dc\uff0c\u5f85\u4f5b\u7237\u5c06\u5979\u751f\u64d2\u732e\u4e0e\u7687\u4e0a\u7acb \u4e2a\u529f\u52b3\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u5927\u6012\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u7ea2\u8863\u5587\u561b\uff0c\u9f84\u7259\u88c2\u5634\u7684\u5411\u5979\u602a\u7b11\uff0c\u5374\u4e0d\u662f\u989d\u97f3\u548c\u5e03\u3002 \u5415\u56db\u5a18\u53eb\u9053\uff1a\u201c\u8fd8\u6211\u5934\u6765\uff01\u201d\u5530\u7684\u4e00\u5251\uff0c\u5e73\u80f8\u523a\u53bb\u3002\u90a3\u5587\u561b\u53cc\u638c\u4e00\u4f38\uff0c\u4f5c\u52bf\u7275\u5f15\uff0c\u5415\u56db\u5a18\u8fd9 \u5251\u672c\u6765\u5feb\u6377\u5982\u7535\uff0c\u88ab\u4ed6\u638c\u52bf\u4e00\u5f15\uff0c\u7adf\u7136\u4f3c\u88ab\u4ec0\u4e48\u5438\u7740\u4f3c\u7684\uff0c\u5f80\u65c1\u4e00\u5e26\uff0c\u5251\u52bf\u5931\u4e86\u51c6\u5934\uff0c\u5415\u56db \u5a18\u5927\u5403\u4e00\u60ca\u3002\u8fd9\u5587\u561b\u7684\u5185\u5bb6\u529f\u529b\u7adf\u7136\u8fdc\u80dc\u989d\u97f3\u548c\u5e03\uff0c\u5728\u5df1\u4e4b\u4e0a\u3002\u90a3\u5587\u561b\u53c8\u7b11\u9053\uff1a\u201c\u4f5b\u7237\u65e0\u6687 \u66ff\u6b7b\u4eba\u5ff5\u7ecf\u6652\u5ea6\uff0c\u53db\u9006\u4e4b\u5934\u6211\u5df2\u6253\u788e\uff0c\u7ecf\u8fc7\u6211\u7684\u6cd5\u638c\uff0c\u4e5f\u662f\u6069\u6cfd\u3002\u4f60\u8fd8\u4e0d\u591a\u8c22\u6211\u4e48\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u67f3\u7709\u5012\u7ad6\uff0c\u6697\u8fd0\u5185\u529b\uff0c\u53c8\u662f\u4e00\u5251\u523a\u53bb\uff01\u90a3\u5587\u561b\u4ecd\u7136\u4f5c\u52bf\u7275\u5f15\uff0c\u5415\u56db\u5a18\u7684\u5251\u5c16\u523a\u4e0d \u8fc7\u53bb\uff0c\u53ef\u662f\u4e5f\u4e0d\u50cf\u5148\u524d\u90a3\u6837\u88ab\u4ed6\u5f15\u5f00\uff0c\u7adf\u7136\u6210\u4e86\u50f5\u6301\u4e4b\u52bf\u3002\u90a3\u5587\u561b\u9aa4\u7684\u4e00\u58f0\u602a\u7b11\uff0c\u53cc\u638c\u4e00 \u677e\uff0c\u5415\u56db\u5a18\u91cd\u5fc3\u4e0d\u7a33\uff0c\u5411\u524d\u503e\u6251\uff0c\u90a3\u5587\u561b\u500f\u7136\u4e00\u638c\u5411\u5979\u80cc\u5fc3\u7684\u201c\u5fd7\u5802\u7a74\u201d\u6309\u4e0b\u3002 \u3000\u3000\u90a3\u5587\u561b\u8fd9\u201c\u5355\u638c\u6309\u7a74\u201d\uff0c\u672c\u6765\u5389\u5bb3\u975e\u51e1\uff0c\u66f4\u517c\u5415\u56db\u5a18\u8eab\u5411\u524d\u503e\uff0c\u4ee5\u4e3a\u7edd\u65e0\u4e0d\u4e2d\u4e4b\u7406\uff0c\u90a3 \u77e5\u5415\u56db\u5a18\u8f7b\u529f\u8d85\u5353\uff0c\u9664\u4e86\u6613\u5170\u73e0\u4e4b\u5916\uff0c\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u5df2\u65e0\u5bf9\u719f\u9170\u90a3\u5587\u561b\u52b2\u529b\u4e00\u677e\uff0c\u5979\u5df2\u8d81\u7740\u524d \u6251\u4e4b\u52bf\uff0c\u8eab\u5b50\u500f\u7136\u53d8\u4e86\u4e00\u4e2a\u65b9\u4f4d\u3002\u53cd\u624b\u4e00\u5251\uff0c\u523a\u4ed6\u80f8\u53e3\u201c\u7487\u7391\u7a74\u201d\uff0c\u90a3\u5587\u561b\u5403\u4e86\u4e00\u60ca\uff0c\u8981\u8fd0 \u638c\u529b\u7275\u5f15\uff0c\u5df2\u6765\u4e0d\u53ca\uff0c\u6025\u6025\u541e\u80f8\u5438\u8179\uff0c\u811a\u6b65\u4e0d\u52a8\uff0c\u8eab\u5b50\u51ed\u7a7a\u632a\u540e\u51e0\u5bf8\uff0c\u5415\u56db\u5a18\u4e00\u5251\u523a\u7a7a\uff0c\u7acb \u523b\u8dc3\u4e0b\u3002\u53ea\u542c\u5f97\u54c8\u5e03\u9640\u5927\u53eb\u9053\uff1a\u201c\u53c8\u7ed9\u5979\u9003\u8d70\u4e86\uff01\u201d\u968f\u5373\u542c\u5f97\u90a3\u5587\u561b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8ba9\u5979\u9003\u8d70 \u6b63\u597d\uff01\u201d\u5415\u56db\u5a18\u5fc3\u60f3\uff1a\u201c\u4f60\u4e0d\u8ba9\u6211\u8d70\u4e5f\u4e0d\u884c\uff0c\u8c05\u4f60\u8ffd\u6211\u4e0d\u4e0a\uff01\u201d\u5c82\u77e5\u80cc\u540e\u547c\u7684\u53e3\u58f0\u98ce\u54cd\uff0c\u90a3 \u5587\u561b\u4e5f\u8df3\u4e86\u4e0b\u6765\uff0c\u72b9\u5982\u4e00\u7247\u7ea2\u4e91\u4ece\u5929\u800c\u964d\uff0c\u4eba\u672a\u5230\u5730\uff0c\u638c\u529b\u5148\u53d1\uff0c\u5415\u56db\u5a18\u811a\u5c16\u70b9\u5730\uff0c\u75be\u63a0\u6570 \u4e08\uff0c\u82e5\u4e0d\u662f\u5979\u5185\u529f\u9020\u8be3\u9ad8\u6df1\uff0c\u51e0\u4e4e\u7ed9\u638c\u98ce\u9707\u5012\u3002\u8fd9\u4e00\u77ac\u95f4\uff0c\u54c8\u5e03\u9640\u5929\u53f6\u656c\u4eba\u97e9\u91cd\u5c71\u7b49\u4e09\u4eba\u4e5f \u90fd\u8df3\u4e86\u4e0b\u6765\u3002\u5415\u56db\u5a18\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u54ea\u91cc\u6765\u7684\u8fd9\u4e2a\u624e\u624b\u5f3a\u654c\uff1f \u3000\u3000\u5415\u56db\u5a18\u4e0d\u77e5\u6b64\u4eba\u4e43\u662f\u7ea2\u6559\u5587\u561b\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u989d\u97f3\u548c\u5e03\u7684\u5e08\u5144\u6606\u7538\u4e0a\u4eba\uff0c\u5185\u5916\u529f\u592b\uff0c\u90fd\u5df2 \u5230\u8fbe\u7089\u706b\u7eaf\u9752\u4e4b\u5883\u3002\u4ed6\u672c\u6765\u662f\u5750\u9547\u96cd\u548c\u5bab\u6267\u884c\u638c\u6559\u4e4b\u804c\u7684\uff0c\u96cd\u6b63\u7687\u5e1d\u88ab\u5415\u56db\u5a18\u51af\u745b\u7b49\u5927\u95f9\u7687 \u5bab\u4e4b\u540e\uff0c\u5fcc\u60ee\u5415\u56db\u5a18\u5230\u6781\uff0c\u6240\u4ee5\u7279\u5730\u8bf7\u4ed6\u51fa\u6765\uff0c\u5e76\u7528\u4e25\u6d2a\u9035\u6c88\u5728\u5bbd\u7684\u5934\u9885\u5e03\u6210\u9677\u4e95\uff0c\u4e13\u7b49\u5979 \u6765\uff0c\u8981\u4e0d\u662f\u56e0\u4e3a\u6606\u7538\u4e0a\u4eba\u6258\u5927\uff0c\u4e0d\u613f\u54c8\u5e03\u9640\u7b49\u76f8\u52a9\u5408\u653b\uff0c\u5415\u56db\u5a18\u65e9\u5df2\u88ab\u4ed6\u4eec\u64d2\u4e86\u3002 \u3000\u3000\u6606\u7538\u4e0a\u4eba\u7387\u54c8\u5e03\u9640\u7b49\u6025\u8ffd\uff0c\u5415\u56db\u5a18\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u6b64\u4eba\u6b66\u529f\u5728\u6211\u4e4b\u4e0a\uff0c\u82e5\u4ed6\u9002\u624d\u4ee5 \u638c\u529b\u76f8\u56f0\uff0c\u53ea\u6015\u6211\u8fd8\u5f53\u771f\u9003\u8d70\u4e0d\u4e86\u3002\u542c\u4ed6\u8bf4\u8ba9\u6211\u9003\u8d70\u6b63\u597d\uff0c\u83ab\u975e\u4ed6\u6709\u610f\u76f8\u8ba9\u4e48\uff1f\u65e2\u7136\u8bf4\u4e86\u90a3 \u8bdd\uff0c\u4f55\u4ee5\u53c8\u7387\u4f17\u8ffd\u6765\uff1f\u201d\u5415\u56db\u5a18\u4e00\u65f6\u95f4\u731c\u60f3\u4e0d\u5230\uff0c\u6606\u7538\u4e0a\u4eba\u4e43\u662f\u60f3\u8d81\u6b64\u673a\u4f1a\uff0c\u8ffd\u8e2a\u5415\u56db\u5a18\u5230 \u5979\u7684\u201c\u5de2\u7a74\u201d\uff0c\u628a\u7518\u51e4\u6c60\u7b49\u4e00\u73ed\u597d\u6c49\uff0c\u5168\u90e8\u201c\u6b7c\u706d\u201d\u3002 \u3000\u3000\u4f46\u6606\u7538\u4e0a\u4eba\u6b66\u529f\u867d\u662f\u767b\u5cf0\u9020\u6781\uff0c\u8f7b\u529f\u5374\u53ea\u662f\u548c\u5929\u53f6\u6563\u4eba\u97e9\u91cd\u5c71\u7b49\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u8ffd\u4e86\u4e00 \u9635\uff0c\u548c\u5415\u56db\u5a18\u7684\u8ddd\u79bb\u5df2\u62c9\u5f00\u4e86\u4e03\u516b\u4e08\u5916\u3002\u6606\u7538\u4e0a\u4eba\u7709\u5934\u4e00\u76b1\uff0c\u97e9\u91cd\u5c71\u7b11\u9053\uff1a\u201c\u77a7\u6211\u7684\uff01\u201d\u4e00 \u6296\u624b\uff0c\u53ea\u89c1\u55e4\u7684\u4e00\u9053\u84dd\u706b\u51b2\u5929\u800c\u8d77\uff0c\u5415\u56db\u5a18\u80a9\u5934\u4e00\u7f29\uff0c\u90a3\u9053\u84dd\u706b\u4ece\u5979\u7684\u5934\u9876\u63a0\u8fc7\uff0c\u5415\u56db\u5a18\u6b63 \u5728\u5947\u602a\uff1a\u4f55\u4ee5\u97e9\u91cd\u5c71\u7684\u6697\u5668\uff0c\u5982\u6b64\u5931\u5374\u51c6\u5934\uff0c\u5ffd\u89c1\u90a3\u9053\u84dd\u706b\u6389\u5934\u98de\u56de\uff0c\u84ec\u7684\u4e00\u58f0\uff0c\u7206\u70b8\u5f00 \u6765\uff0c\u65e0\u6570\u94c1\u7802\uff0c\u4f3c\u51b0\u96f9\u4e71\u843d\u3002\u5415\u56db\u5a18\u6025\u6025\u907f\u5f00\u6b63\u9762\uff0c\u659c\u7a9c\u51fa\u4e09\u56db\u4e08\u5916\uff0c\u5c31\u5728\u8fd9\u95ea\u907f\u4e4b\u95f4\uff0c\u6606 \u7538\u4e0a\u4eba\u53c8\u5df2\u8ffd\u5230\u8eab\u540e\u3002 \u3000\u3000\u539f\u6765\u97e9\u91cd\u5c71\u81ea\u4ece\u5403\u4e86\u5415\u56db\u5a18\u7684\u4e8f\u4e4b\u540e\uff0c\u66f4\u82e6\u5fc3\u4fee\u7ec3\u6697\u5668\uff0c\u5c31\u7528\u201c\u56de\u73af\u94a9\u201d\u7684\u539f\u7406\uff0c\u7528\u7529 \u624b\u6cd5\uff0c\u5728\u86c7\u7130\u7bad\u4e4b\u4e2d\u88c5\u4e86\u65e0\u6570\u94c1\u7802\u8ba9\u5b83\u53ef\u4ee5\u5012\u98de\uff0c\u88ad\u51fb\u654c\u4eba\u3002\u5415\u56db\u5a18\u8f7b\u529f\u867d\u9ad8\uff0c\u4f46\u56e0\u8981\u8d70\u659c \u7ebf\u907f\u5f00\u6b63\u9762\u7684\u6697\u5668\uff0c\u800c\u6606\u7538\u4e0a\u4eba\u5219\u8d70\u76f4\u7ebf\u8ffd\u51fb\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u5f62\u52bf\u7acb\u523b\u6539\u89c2\u3002 \u3000\u3000\u5415\u56db\u5a18\u88ab\u6697\u5668\u6240\u963b\uff0c\u8f7b\u529f\u4f18\u70b9\uff0c\u5df2\u88ab\u62b5\u9500\u3002\u97e9\u91cd\u5c71\u5c61\u7528\u6b64\u6cd5\uff0c\u6bcf\u5f53\u8ffd\u4e0d\u4e0a\u5415\u56db\u5a18\u4e4b\u65f6\uff0c \u4fbf\u7528\u86c7\u7130\u7bad\u94c1\u7802\u5b50\u903c\u5979\u6539\u8d70\u659c\u7ebf\u3002\u90a3\u86c7\u7130\u7bad\u4e00\u8d77\u4fbf\u662f\u4e00\u9053\u84dd\u706b\u6d53\u70df\uff0c\u4e45\u4e45\u4e0d\u6563\uff0c\u53c8\u6b63\u597d\u4f5c\u4e86 \u4e00\u79cd\u4fe1\u53f7\uff0c\u6307\u5f15\u5bab\u4e2d\u7684\u536b\u58eb\u8ddf\u8e2a\u8ffd\u51fb\u3002 \u3000\u3000\u5415\u56db\u5a18\u7edd\u9876\u806a\u660e\uff0c\u9003\u4e86\u4e00\u9635\uff0c\u4e5f\u731c\u5230\u4ed6\u4eec\u7684\u6bd2\u8ba1\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u82e5\u9003\u56de\u897f\u5c71\uff0c\u4ed6\u4eec\u4e5f\u5fc5\u8ddf \u8e2a\u800c\u81f3\uff0c\u8fd9\u56db\u4eba\u6b66\u529f\u90fd\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u90a3\u7ea2\u8863\u5587\u561b\u66f4\u662f\u65e0\u4eba\u80fd\u654c\uff0c\u4f55\u51b5\u4ed6\u4eec\u4e00\u5b9a\u8fd8\u6709\u540e\u63f4\uff0c\u897f \u5c71\u867d\u6709\u7518\u51e4\u6c60\u51b7\u7985\u7b49\u4e00\u4f17\u9ad8\u624b\uff0c\u53ea\u6050\u4e5f\u62b5\u6321\u4e0d\u4f4f\uff0c\u8fd9\u5374\u5982\u4f55\u662f\u597d\uff1f\u201d\u5ffd\u5730\u60f3\u8d77\u4e86\u90a3\u753b\u5e08\u6240\u8bf4 \u7684\u201c\u602a\u4eba\u201d\uff0c\u60f3\u9053\uff1a\u6211\u4e0d\u5982\u5f15\u4ed6\u4eec\u5230\u6bd2\u9f99\u5c0a\u8005\u85cf\u8eab\u4e4b\u6240\uff0c\u6bd2\u9f99\u5c0a\u8005\u7684\u6b66\u529f\u5c3d\u53ef\u62b5\u654c\u5f97\u4f4f\u8fd9\u7ea2 \u8863\u5587\u561b\uff01 \u3000\u3000\u5415\u56db\u5a18\u5728\u524d\uff0c\u6606\u7538\u4e0a\u4eba\u7b49\u56db\u4eba\u5728\u540e\uff0c\u5954\u9003\u8ffd\u9010\uff0c\u90fd\u662f\u5feb\u5982\u7535\u64ce\u98ce\u9a70\uff0c\u5728\u5929\u8272\u5fae\u660e\u4e4b\u524d\uff0c \u5df2\u8dd1\u4e86\u4e8c\u767e\u4f59\u91cc\uff0c\u6765\u5230\u4e86\u516b\u8fbe\u5cad\u897f\u5357\u9762\u7684\u79ef\u77f3\u5c71\uff0c\u8fd9\u65f6\u97e9\u91cd\u5c71\u7684\u706b\u7130\u7bad\u5df2\u7ecf\u7528\u5b8c\uff0c\u4f46\u5415\u56db\u5a18 \u4e5f\u7d2f\u5f97\u9999\u6c57\u6dcb\u6f13\u4e86\u3002 \u3000\u3000\u6606\u7538\u4e0a\u4eba\u89c1\u5415\u56db\u5a18\u9003\u5165\u6797\u4e2d\uff0c\u9053\uff1a\u201c\u53db\u8d3c\u7684\u5de2\u7a74\u539f\u6765\u5c31\u5728\u8fd9\u91cc\u3002\u201d\u4e0e\u54c8\u5e03\u9640\u7b49\u5206\u56db\u9762\u88ad \u5165\u6797\u4e2d\uff0c\u53ea\u89c1\u5415\u56db\u5a18\u8eab\u5f62\u98de\u8d77\uff0c\u75be\u4f3c\u733f\u7334\u7684\u98de\u5347\u4e0a\u4e00\u68f5\u5341\u4f59\u4e08\u9ad8\u7684\u5927\u6811\uff0c\u7adf\u7136\u5750\u5728\u6811\u68a2\u4e4b \u4e0a\uff0c\u5927\u56bc\u5e72\u7cae\u3002\u97e9\u91cd\u5c71\u6296\u624b\u6253\u51fa\u51e0\u679a\u91d1\u94b1\u9556\uff0c\u88ab\u5415\u56db\u5a18\u5f39\u6307\u6253\u843d\uff0c\u6606\u7538\u4e0a\u4eba\u5927\u6012\uff0c\u4e5f\u65bd\u5c55\u8f7b \u529f\uff0c\u6500\u4e0a\u5927\u6811\uff0c\u54c8\u5e03\u9640\u5927\u53eb\u9053\uff1a\u201c\u4e0a\u4eba\u5c0f\u5fc3\uff01\u201d\u5415\u56db\u5a18\u6298\u4e86\u4e24\u679d\u6811\u679d\uff0c\u5411\u6606\u7538\u4e0a\u4eba\u53cc\u773c\u75be \u5c04\uff0c\u6606\u7538\u4e0a\u4eba\u867d\u7136\u7ec3\u6709\u91d1\u949f\u7f69\u94c1\u5e03\u886b\u7684\u529f\u592b\uff0c\u4f46\u53cc\u773c\u5374\u662f\u4e0d\u80fd\u62b5\u6321\uff0c\u6025\u6025\u95ed\u76ee\u4fef\u9996\uff0c\u90a3\u4e24\u679d \u6811\u679d\u500f\u7136\u8d2f\u8033\u800c\u8fc7\uff0c\u628a\u6606\u7538\u4e0a\u4eba\u4e24\u53ea\u8033\u6735\uff0c\u90fd\u7a7f\u4e86\u4e00\u4e2a\u5c0f\u6d1e\u3002\u6606\u7538\u4e0a\u4eba\u5927\u53eb\u4e00\u58f0\uff0c\u677e\u624b\u8dc3 \u4e0b\uff0c\u4ef0\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u5415\u56db\u5a18\u795e\u8272\u81ea\u5982\uff0c\u76d8\u819d\u5750\u5728\u6811\u68a2\uff0c\u4ecd\u662f\u5927\u56bc\u5e72\u7cae\u3002\u6606\u7538\u4e0a\u4eba\u6b66\u529f\u867d\u6781\u9ad8 \u5f3a\uff0c\u4f46\u5415\u56db\u5a18\u5c45\u9ad8\u4e34\u4e0b\uff0c\u9996\u5148\u5360\u4e86\u4fbf\u5b9c\u3002\u6606\u7538\u4e0a\u4eba\u8f7b\u529f\u53c8\u4e0d\u5982\u5979\uff0c\u8bd5\u4e86\u4e00\u6b21\uff0c\u4e0d\u6562\u5f3a\u884c\u6500\u767b \u4e0e\u5979\u4f5c\u5bf9\u3002 \u3000\u3000\u5415\u56db\u5a18\u5403\u9971\u4e4b\u540e\uff0c\u84e6\u5730\u4e00\u58f0\u957f\u5578\uff0c\u58f0\u97f3\u4e0d\u5927\uff0c\u4f46\u5374\u6e05\u8106\u5c16\u9510\u4e4b\u6781\uff0c\u6606\u7538\u4e0a\u4eba\u51b7\u7b11\u9053\uff1a \u201c\u6211\u6b63\u8981\u627e\u4f60\u7684\u515a\u7fbd\uff0c\u518d\u6253\u62db\u547c\u5427\uff01\u201d\u5415\u56db\u5a18\u957f\u5578\u6570\u58f0\uff0c\u8fc7\u4e86\u8bb8\u4e45\uff0c\u7adf\u662f\u8352\u6797\u5bc2\u5bc2\uff0c\u6773\u65e0\u4eba \u58f0\u3002\u5415\u56db\u5a18\u53eb\u9053\uff1a\u201c\u6bd2\u9f99\u5c0a\u8005\uff0c\u6bd2\u9f99\u5c0a\u8005\uff01\u201d\u6606\u7538\u4e0a\u4eba\u5927\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u5c0a\u8005\uff1f\u5c31\u7b97\u771f\u7684\u6765\u4e86 \u4e00\u6761\u6bd2\u9f99\uff0c\u4e0a\u4eba\u4e5f\u6709\u5c60\u9f99\u624b\u6bb5\u3002\u201d\u5415\u56db\u5a18\u53eb\u4e86\u51e0\u58f0\uff0c\u8fc7\u4e86\u8bb8\u4e45\uff0c\u4ecd\u662f\u6773\u65e0\u4eba\u58f0\u3002\u5fc3\u4e2d\u4e0d\u89c9\u53d1 \u614c\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u662f\u90a3\u753b\u5e08\u4f20\u9519\uff0c\u6216\u8005\u662f\u6bd2\u9f99\u5c0a\u8005\u5df2\u8d70\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6606\u7538\u4e0a\u4eba\u517b\u597d\u7cbe\u795e\uff0c\u4e0d\u89c1\u6709\u5415\u56db\u5a18\u7684\u540c\u515a\u51fa\u6765\uff0c\u4e0d\u89c9\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u65bd\u5c55\u7a7a\u57ce\u4e4b \u8ba1\u3002\u201d\u5415\u56db\u5a18\u4e0d\u7406\u4e0d\u776c\uff0c\u4ecd\u7136\u76d8\u819d\u5750\u5728\u6811\u68a2\uff0c\u95ed\u76ee\u8c03\u795e\u3002\u6606\u7538\u4e0a\u4eba\u4e00\u58f0\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a \u5982\u6b64\u4e00\u6765\uff0c\u4f5b\u7237\u5c31\u5948\u4f55\u4e0d\u4e86\u4f60\u4e48\uff1f\u201d\u8131\u4e0b\u5927\u7ea2\u8888\u88df\uff0c\u53cc\u81c2\u8fd0\u52b2\uff0c\u84e6\u7136\u4e00\u58f0\u5927\u559d\uff0c\u53cc\u638c\u6a2a\u51fb\u6811 \u8eab\uff0c\u90a3\u4e00\u68f5\u4e24\u4eba\u5408\u62b1\u4e0d\u8fc7\u7684\u5927\u6811\uff0c\u987f\u65f6\u5982\u53d7\u5200\u65a7\uff01 \u3000\u3000\u5415\u56db\u5a18\u5750\u5728\u4e0a\u9762\uff0c\u5c31\u5982\u4e00\u53f6\u5c0f\u821f\uff0c\u5728\u5927\u6d77\u4e4b\u4e2d\u53d7\u98ce\u6d6a\u88ad\u51fb\u3002\u6025\u5fd9\u6500\u7d27\u6811\u5e72\u3002\u6606\u7538\u4e0a\u4eba\u8fde \u51fb\u6570\u638c\uff0c\u628a\u6811\u6839\u9707\u677e\uff0c\u731b\u5730\u53cc\u638c\u594b\u529b\u4e00\u63a8\uff0c\u559d\u9053\uff1a\u201c\u5012\uff01\u201d\u90a3\u68f5\u5927\u6811\u5982\u53d7\u5de8\u65a7\u6240\u5288\uff0c\u8f70\u7684\u4e00 \u58f0\u5de8\u54cd\uff0c\u6811\u8eab\u5f2f\u5012\uff0c\u53ea\u89c1\u5728\u6ce5\u58eb\u98de\u626c\u3001\u679d\u53f6\u7eb7\u843d\u4e4b\u4e2d\uff0c\u4e00\u6761\u6084\u751f\u751f\u7684\u4eba\u5f71\u817e\u7a7a\u98de\u8d77\uff0c\u53c8\u98de\u5230 \u6570\u4e08\u5916\u53e6\u4e00\u68f5\u66f4\u9ad8\u7684\u6811\u4e0a\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u7b28\u79c3\u9a74\uff0c\u4f60\u6709\u591a\u5927\u6c14\u529b\uff0c\u4f60\u80fd\u628a\u6ee1\u5c71\u5927\u6811\u90fd\u4f10\u4e86 \u5417\uff1f\u201d \u3000\u3000\u6606\u7538\u4e0a\u4eba\u51fb\u5012\u5927\u6811\uff0c\u4e0d\u8fc7\u662f\u60f3\u6cc4\u4e00\u53e3\u6c14\uff0c\u5c82\u77e5\u6012\u6c14\u672a\u6cc4\uff0c\u8fd8\u53d7\u654c\u4eba\u8ba5\u7b11\uff0c\u6c14\u4e0a\u52a0\u6c14\uff0c\u5374 \u662f\u65e0\u53ef\u5982\u4f55\uff0c\u8fd9\u65f6\u5929\u8272\u5927\u660e\uff0c\u5bab\u4e2d\u9ad8\u624b\u9646\u7eed\u8d76\u5230\uff0c\u6570\u5341\u4eba\u642d\u7bad\u4e0a\u5c04\uff0c\u90a3\u5927\u6811\u9ad8\u5341\u4f59\u4e08\uff0c\u529b\u6c14 \u7a0d\u5f31\u7684\u6839\u672c\u5c31\u5c04\u4e0d\u5230\uff0c\u5c04\u5230\u7684\u4e5f\u90fd\u7ed9\u5415\u56db\u5a18\u62c2\u843d\u3002\u6606\u7538\u4e0a\u4eba\u6c14\u9053\uff1a\u201c\u597d\u5427\uff0c\u770b\u4f60\u80fd\u8017\u5230\u51e0 \u65f6\uff1f\u201d\u8fc7\u4e86\u4e00\u9635\uff0c\u989d\u97f3\u548c\u5e03\u4e5f\u8d76\u6765\u4e86\uff0c\u96c6\u4e94\u5927\u9ad8\u624b\u4e0e\u6570\u5341\u540d\u536b\u4e0a\u4e4b\u529b\uff0c\u5374\u4ecd\u7136\u53ea\u80fd\u671b\u6811\u6414 \u5934\uff0c\u9664\u4e86\u548c\u5415\u56db\u5a18\u50f5\u6301\u4e4b\u5916\uff0c\u522b\u65e0\u4ed6\u6cd5\u3002 \u3000\u3000\u6606\u7538\u4e0a\u4eba\u53eb\u536b\u58eb\u628a\u5b88\u6811\u6797\u5468\u56f4\uff0c\u9632\u5907\u5916\u654c\u7a9c\u5165\u3002\u81ea\u5df1\u4ecd\u7136\u5b88\u5728\u6811\u4e0b\uff0c\u70e4\u91ce\u5154\u98df\u3002\u5415\u56db\u5a18 \u6240\u5e26\u7684\u5e72\u7cae\u5df2\u7ecf\u5403\u5b8c\uff0c\u770b\u4ed6\u4eec\u5403\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u9965\u706b\u6e10\u6e10\u4e0a\u5347\u3002\u5fc3\u5ff5\u6bd2\u9f99\u5c0a\u8005\uff0c\u53c8\u53d1\u4e86\u51e0\u58f0\u957f \u5578\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\uff0c\u65e5\u5934\u5df2\u7ecf\u8fc7\u5348\uff0c\u731b\u542c\u5f97\u6811\u6797\u5916\u4e00\u58f0\u5de8\u559d\uff0c\u63a5\u7740\u662f\u545c\u545c\u7684\u54cd\u7bad\u58f0\uff0c\u6709\u4e24\u4eba\u51b2\u4e86 \u8fdb\u6765\uff0c\u5f53\u5148\u7684\u662f\u4e00\u4e2a\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\uff0c\u81c2\u529b\u5927\u5f97\u60ca\u4eba\uff0c\u4f46\u89c1\u4ed6\u4e00\u624b\u4e00\u4e2a\uff0c\u50cf\u634f\u7a3b\u8349\u4eba\u4e00\u6837\uff0c \u5c06\u4e24\u540d\u536b\u58eb\u6454\u5f97\u7b4b\u65ad\u9aa8\u6298\u3002\u97e9\u91cd\u5c71\u53eb\u9053\uff1a\u201c\u662f\u7518\u51e4\u6c60\u548c\u5510\u6653\u6f9c\u6765\u4e86\uff0c\u8fd9\u4e24\u4eba\u90fd\u662f\u91cd\u8981\u7684\u94a6 \u72af\uff01\u201d\u6606\u7538\u4e0a\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u4e0d\u8981\u52a8\uff0c\u5f85\u6211\u770b\u770b\u8fd9\u4e24\u4eba\u6709\u4ec0\u4e48\u672c\u9886\uff1f\u201d \u3000\u3000\u6606\u7538\u4e0a\u4eba\u5927\u6447\u5927\u6446\u8d70\u4e86\u51fa\u6765\uff0c\u7518\u51e4\u6c60\u5927\u559d\u4e00\u58f0\uff0c\u4ecd\u7528\u6454\u5012\u536b\u58eb\u7684\u5927\u529b\u91d1\u521a\u624b\u6cd5\uff0c\u53f3\u638c\u671d \u4ed6\u9888\u9879\u4e00\u52fe\uff0c\u90a3\u6599\u5374\u6251\u4e86\u4e2a\u7a7a\uff0c\u731b\u7136\u95f4\u4e00\u80a1\u5927\u529b\u53cd\u63a8\u8fc7\u6765\uff0c\u7518\u51e4\u6c60\u6025\u5fd9\u53d8\u62db\uff0c\u8fd0\u638c\u4e00\u62b5\uff0c\u817e \u7684\u4e00\u58f0\uff0c\u7ed9\u9707\u5f97\u5012\u9000\u6570\u6b65\uff0c\u864e\u53e3\u6d41\u8840\uff0c\u6606\u7538\u4e0a\u4eba\u4e5f\u7ed9\u7518\u51e4\u6c60\u7684\u638c\u529b\u9707\u5f97\u6447\u6447\u6643\u6643\u3002\u7518\u51e4\u6c60\u5185 \u529f\u6df1\u539a\uff0c\u5728\u540c\u95e8\u4e2d\u6570\u4e00\u6570\u4e8c\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u3002\u53ea\u542c\u5f97\u6606\u7538\u4e0a\u4eba\u53eb\u9053\uff1a\u201c\u4f60\u80fd\u6321\u6211\u4e00\u638c\uff0c\u4e0d\u6127 \u6c5f\u5357\u5927\u4fa0\uff01\u201d\u6b63\u8981\u6a2a\u638c\u518d\u5288\uff0c\u5ffd\u89c1\u4e00\u9053\u5bd2\u5149\uff0c\u659c\u950b\u6025\u523a\uff0c\u5feb\u75be\u7edd\u4f26\uff0c\u6797\u5b50\u91cc\u989d\u97f3\u548c\u5e03\u53eb\u9053\uff1a \u201c\u5e08\u5144\u5c0f\u5fc3\uff0c\u8fd9\u662f\u6e38\u9f99\u5b9d\u5251\uff01\u201d \u3000\u3000\u5929\u5c71\u7684\u6e38\u9f99\u5b9d\u5251\uff0c\u5929\u4e0b\u95fb\u540d\uff0c\u6606\u7538\u4e0a\u4eba\u672c\u6765\u662f\u60f3\u786c\u62a2\u5510\u6653\u6f9c\u7684\u5b9d\u5251\uff0c\u95fb\u8a00\u4f9d\u7136\u4e00\u60ca\uff0c\u6765 \u4e0d\u53ca\u6697\u8fd0\u5185\u52b2\u7275\u5f15\uff0c\u6025\u5fd9\u95ea\u907f\u3002\u5510\u6653\u6f9c\u4f7f\u51fa\u9053\u98ce\u5251\u6cd5\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u5251\u5c16\u4e0a\u7684\u5149\u8292\uff0c\u66b4\u957f\u51e0 \u5bf8\uff0c\u5251\u5149\u7ed5\u5904\uff0c\u6606\u7538\u4e0a\u4eba\u5934\u9876\u4e00\u7247\u6c81\u51c9\uff0c\u6240\u6234\u7684\u725b\u89d2\u5e3d\u7adf\u88ab\u524a\u4e3a\u4e24\u7247\u3002\u7518\u51e4\u6c60\u8d81\u673a\u4e00\u8dc3\uff0c\u53cc \u62f3\u75be\u626b\u3002\u6606\u7538\u4e0a\u4eba\u5927\u6012\uff0c\u888d\u8896\u4e00\u6325\uff0c\u628a\u5510\u6653\u6f9c\u7684\u5b9d\u5251\u88f9\u7740\uff0c\u5de6\u638c\u5f80\u5916\u4e00\u767b\uff0c\u53c8\u628a\u7518\u51e4\u6c60\u9707 \u9000\u3002\u5510\u6653\u6f9c\u5251\u6cd5\u867d\u7136\u7cbe\u5999\uff0c\u529f\u529b\u5230\u5e95\u4e0e\u6606\u7538\u4e0a\u4eba\u76f8\u5dee\u5c1a\u8fdc\uff0c\u88ab\u4ed6\u957f\u8896\u88f9\u4f4f\uff0c\u7adf\u7136\u8fdb\u9000\u4e0d\u5f97\u3002 \u6606\u7538\u4e0a\u4eba\u4e00\u58f0\u51b7\u7b11\uff0c\u957f\u8896\u8d8a\u5377\u8d8a\u7d27\uff0c\u5de6\u624b\u4e00\u4f38\uff0c\u53cc\u6307\u5fae\u5c48\uff0c\u4e00\u62db\u201c\u6e38\u9f99\u63c9\u722a\u201d\uff0c\u5411\u5510\u6653\u6f9c\u624b \u8155\u5bf8\u5173\u5c3a\u5904\u4e00\u52fe\uff0c\u786c\u62a2\u6e38\u9f99\u5b9d\u5251\u3002 \u3000\u3000\u7518\u51e4\u6c60\u89c1\u72b6\u5927\u60ca\uff0c\u594b\u8d77\u795e\u529b\uff0c\u53cc\u62f3\u4e00\u51b2\uff0c\u6606\u7538\u4e0a\u4eba\u903c\u5f97\u4f38\u5f00\u624b\u6307\uff0c\u5e73\u638c\u5e94\u654c\uff0c\u7518\u51e4\u6c60\u5185 \u52b2\u6c89\u96c4\uff0c\u53cc\u62f3\u4e00\u51b2\u4e4b\u529b\uff0c\u4f55\u6b62\u5343\u65a4\uff0c\u6606\u7538\u4e0a\u4eba\u867d\u7136\u5389\u5bb3\u4e4b\u6781\uff0c\u4ee5\u5355\u638c\u5e73\u6321\u4e4b\u529b\u786c\u63a5\u4ed6\u7684\u53cc \u62f3\uff0c\u5374\u662f\u6709\u4e9b\u62b5\u6321\u4e0d\u4f4f\uff0c\u62f3\u638c\u76f8\u4ea4\uff0c\u6606\u7538\u4e0a\u4eba\u6643\u4e86\u4e24\u6643\uff0c\u5012\u9000\u4e00\u6b65\uff0c\u5510\u6653\u6f9c\u5ffd\u611f\u538b\u529b\u4e00\u677e\uff0c \u5251\u950b\u5fae\u98a4\uff0c\u55e4\u55e4\u58f0\u54cd\uff0c\u53c8\u628a\u6606\u7538\u4e0a\u4eba\u7684\u957f\u8896\u5272\u65ad\u3002\u6606\u7538\u4e0a\u4eba\u5927\u6012\uff0c\u5408\u638c\u8fd0\u52b2\uff0c\u5f80\u5916\u4e00\u63a8\uff0c\u7518 \u51e4\u6c60\u4e45\u7ecf\u5927\u654c\uff0c\u4ea4\u624b\u4e09\u5408\uff0c\u5df2\u662f\u77e5\u5df2\u77e5\u5f7c\uff0c\u77e5\u9053\u82e5\u7136\u8f83\u91cf\u5185\u529f\uff0c\u81ea\u5df1\u5c1a\u975e\u4ed6\u7684\u654c\u624b\uff0c\u65e9\u5df2\u6599 \u4ed6\u6709\u6b64\u4e00\u7740\uff0c\u6606\u7538\u4e0a\u4eba\u521a\u521a\u53d8\u62db\u5408\u638c\uff0c\u4ed6\u5df2\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u7ed5\u8fc7\u4e00\u65c1\u3002\u6606\u7538\u4e0a\u4eba\u53cc\u638c\u63a8\u51fa\uff0c \u6c99\u98de\u98ce\u8d77\uff0c\u679d\u53f6\u7eb7\u98de\uff0c\u58f0\u52bf\u731b\u70c8\u975e\u5e38\uff0c\u5374\u662f\u4f24\u4ed6\u4e0d\u7740\u3002 \u3000\u3000\u638c\u98ce\u672a\u606f\uff0c\u5251\u5f71\u659c\u98de\uff0c\u5510\u6653\u6f9c\u8d81\u4ed6\u4e00\u62db\u65e2\u51fa\uff0c\u672a\u53ca\u518d\u8fd0\u5185\u5bb6\u771f\u529b\u4e4b\u9645\uff0c\u500f\u7136\u4e00\u5251\u6a2a\u98de\uff0c \u5251\u950b\u659c\u62b9\uff0c\u7518\u51e4\u6c60\u4e5f\u5feb\u901f\u62a2\u653b\uff0c\u715e\u4ed6\u5a01\u52bf\u3002\u6606\u7538\u4e0a\u4eba\u8eab\u5b50\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u5728\u5251\u5f71\u62f3\u98ce\u4e4b\u4e0b\uff0c\u8f6c \u4e86\u4e24\u5708\uff0c\u84e6\u7136\u95f4\u53c8\u662f\u4e00\u58f0\u5927\u559d\uff0c\u53cc\u638c\u5de6\u53f3\u4e00\u5206\uff0c\u5206\u51fb\u7518\u5510\u4e8c\u4eba\uff0c\u638c\u529b\u4e00\u53d1\uff0c\u6709\u5982\u6392\u5c71\u5012\u6d77\uff01 \u7518\u51e4\u6c60\u8fd0\u638c\u4e00\u6321\uff0c\u7eb5\u6b65\u6025\u9000\uff0c\u5510\u6653\u6f9c\u9996\u5f53\u5176\u51b2\uff0c\u51e0\u4e4e\u7ed9\u4ed6\u9707\u5012\u3002\u6606\u7538\u4e0a\u4eba\u624b\u4e0d\u7559\u60c5\uff0c\u5c06\u4e24\u4eba \u5408\u653b\u4e4b\u52bf\u62c6\u5f00\u4e4b\u540e\uff0c\u7acb\u523b\u8fde\u8fde\u53cd\u51fb\u3002 \u3000\u3000\u7518\u51e4\u6c60\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u5fc3\u9053\uff1a\u6b64\u4eba\u529f\u529b\u4e0d\u5728\u6bd2\u9f99\u5c0a\u8005\u4e4b\u4e0b\u3002\u4e0e\u5510\u6653\u6f9c\u6253\u4e86\u4e00\u4e2a\u62db\u547c\uff0c\u5206 \u8fdb\u5408\u51fb\u3002\u7531\u7518\u51e4\u6c60\u62b5\u6321\u4ed6\u7684\u638c\u529b\uff0c\u5510\u6653\u6f9c\u5219\u4ee5\u8fc5\u6377\u7684\u5251\u672f\u6e38\u6597\u7275\u5236\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u4ed6\u65e2\u4e0d\u80fd\u4ee5 \u5168\u529b\u538b\u5236\u7518\u51e4\u6c60\uff0c\u4e5f\u4e0d\u80fd\u786c\u62a2\u5510\u6653\u6f9c\u7684\u5b9d\u5251\uff0c\u8fd8\u8981\u9632\u5907\u7ed9\u5b9d\u5251\u523a\u4f24\uff0c\u53ea\u80fd\u7528\u91d1\u521a\u624b\u8ff7\u8e2a\u638c\u534a \u653b\u534a\u5b88\uff0c\u53ae\u6597\u4e86\u4e00\u767e\u4f59\u62db\uff0c\u5140\u662f\u4e0d\u5206\u80dc\u8d1f\u3002 \u3000\u3000\u53ef\u662f\u6606\u7538\u4e0a\u4eba\u5230\u5e95\u80dc\u5728\u5185\u529f\u6df1\u539a\uff0c\u5185\u52b2\u60a0\u957f\uff0c\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u95f2\u82e5\u65e0\u4e8b\u3002\u5510\u6653\u6f9c\u5374\u5df2\u6709 \u4e9b\u8eab\u70ed\u5fc3\u8df3\u3002\u8fd8\u5e78\u4ed6\u5728\u8fd9\u4e00\u5e74\u4e2d\uff0c\u82e6\u4e60\u5929\u5c71\u6b63\u5b97\u5185\u529f\uff0c\u5927\u6709\u8fdb\u5883\uff0c\u8981\u4e0d\u7136\u66f4\u96be\u652f\u6301\u3002 \u3000\u3000\u53c8\u6597\u4e86\u6570\u5341\u62db\uff0c\u6606\u7538\u4e0a\u4eba\u84e6\u5730\u53d1\u58f0\u602a\u5578\uff0c\u5de6\u638c\u8fde\u626c\uff0c\u628a\u5510\u6653\u6f9c\u7684\u5b9d\u5251\u9707\u5f97\u56db\u8fb9\u6446\u52a8\uff0c\u653b \u4e0d\u8fdb\u6765\uff0c\u53f3\u638c\u4e00\u4f38\uff0c\u638c\u62cd\u6307\u6233\uff0c\u4e13\u5411\u7518\u51e4\u6c60\u7684\u4e09\u5341\u516d\u9053\u5927\u7a74\u8fde\u4e0b\u6740\u624b\u3002 \u3000\u3000\u666e\u901a\u7684\u70b9\u7a74\u591a\u662f\u8fd0\u52b2\u529b\u900f\u6307\u5934\uff0c\u731d\u7136\u88ad\u51fb\u3002\u6307\u52b2\u6709\u9650\uff0c\u82e5\u662f\u9047\u7740\u7518\u51e4\u6c60\u8fd9\u6837\u7684\u9ad8\u624b\uff0c\u8fd0 \u6c14\u62b5\u7981\uff0c\u7eb5\u88ab\u70b9\u4e2d\uff0c\u4ea6\u53ef\u65e0\u4f24\uff0c\u800c\u4e14\u7518\u51e4\u6c60\u638c\u529b\u6c89\u96c4\uff0c\u5bf9\u65b9\u4f38\u6307\u70b9\u4ed6\uff0c\u82e5\u7136\u78b0\u7740\u4ed6\u7684\u94c1\u638c\uff0c \u66f4\u6709\u65ad\u6307\u88c2\u8155\u4e4b\u5371\uff0c\u662f\u4ee5\u7518\u51e4\u6c60\u5e73\u751f\u5bf9\u654c\uff0c\u4ece\u6765\u4e0d\u754f\u70b9\u7a74\u3002\u4f46\u4e0d\u6599\u6606\u7538\u4e0a\u4eba\u7684\u201c\u62cd\u7a74\u201d\u53e6\u6709 \u4e00\u529f\uff0c\u4ed6\u80fd\u4ee5\u638c\u529b\u9707\u8361\u7a74\u9053\uff0c\u7518\u51e4\u6c60\u51fa\u638c\u76f8\u62b5\uff0c\u4e0d\u8fc7\u7b49\u4e8e\u4e92\u8f83\u5185\u52b2\uff0c\u4f46\u82e5\u7a0d\u6709\u758f\u5ffd\uff0c\u4ed6\u5c31\u80fd \u4e58\u865a\u800c\u5165\uff0c\u6216\u62cd\u6216\u6233\uff0c\u4ee4\u4eba\u9632\u4e0d\u80dc\u9632\u3002\u66f4\u517c\u6606\u7538\u4e0a\u4eba\u201c\u62cd\u7a74\u201d\u7684\u624b\u6cd5\u602a\u5f02\u975e\u5e38\uff0c\u7518\u51e4\u6c60\u89c1\u6240 \u672a\u89c1\uff0c\u4e00\u65f6\u95f4\u7adf\u4e0d\u77e5\u5982\u4f55\u5e94\u4ed8\u3002 \u3000\u3000\u5510\u6653\u6f9c\u6b63\u4f8d\u53bb\u5e2e\u7518\u51e4\u6c60\uff0c\u6811\u6797\u4e2d\u5929\u53f6\u6563\u4eba\u4e0e\u54c8\u5e03\u9640\u4ea6\u5df2\u53cc\u53cc\u7eb5\u51fa\uff0c\u5929\u53f6\u6563\u4eba\u8eab\u6cd5\u8f7b\u5feb\uff0c \u62a2\u5728\u524d\u9762\uff0c\u770b\u770b\u5c31\u8981\u8fce\u4e0a\u5510\u6653\u6f9c\uff0c\u5374\u5ffd\u7136\u811a\u6b65\u4e00\u505c\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u7433\u8d35\u4eba\uff0c\u4f60\u4e5f\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u671d\u6797\u5916\u4e00\u77a5\uff0c\u53ea\u89c1\u51af\u745b\u75be\u8d70\u5982\u98ce\uff0c\u5251\u5149\u4f3c\u7ec3\uff0c\u5929\u53f6\u6563\u4eba\u7b11\u58f0\u672a\u5df2\uff0c\u5979\u5df1\u95ef\u4e86\u8fdb \u6765\u3002\u5510\u6653\u6f9c\u5927\u53eb\u9053\uff1a\u201c\u745b\u59b9\uff0c\u5415\u59d0\u59d0\u5728\u6797\u5b50\u91cc\u5934\uff01\u201d\u6606\u7538\u4e0a\u4eba\u53f3\u638c\u62e9\u4e86\u534a\u4e2a\u5706\u5f27\uff0c\u9aa4\u7136\u5411\u5510 \u6653\u6f9c\u4e00\u5f15\uff0c\u5510\u6653\u6f9c\u7acb\u811a\u4e0d\u7a33\uff0c\u4e00\u8de4\u8dcc\u4e0b\u3002 \u3000\u3000\u6606\u7538\u4e0a\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u4f38\u624b\u4fbf\u6293\uff0c\u731b\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u53c8\u662f\u4e00\u4e2a\u5c11\u5e74\u51b2\u4e86\u8fdb\u6765\uff0c\u6606\u7538\u4e0a\u4eba\u4e0d\u4ee5 \u4e3a\u610f\uff0c\u53f3\u624b\u4ecd\u7136\u524d\u6293\uff0c\u5de6\u638c\u4e00\u6325\uff0c\u8fce\u7740\u6765\u4eba\u4e4b\u52bf\uff0c\u626d\u4ed6\u624b\u8155\u3002\u90a3\u77e5\u8fd9\u4eba\u4e43\u662f\u674e\u6cbb\uff0c\u4ed6\u7684\u72ec\u95e8 \u5251\u6cd5\u4e43\u662f\u767d\u53d1\u9b54\u5973\u8fd9\u4e00\u6d3e\u7684\u5ae1\u4f20\uff0c\u5947\u8be1\u4e4b\u5904\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u5251\u52bf\u4e00\u504f\uff0c\u4f3c\u5de6\u5ffd\u53f3\uff0c\u9661\u7136\u5954\u5411\u6606 \u7538\u4e0a\u4eba\u6b63\u5728\u6293\u5510\u6653\u6f9c\u7684\u53f3\u8155\uff0c\u6606\u7538\u4e0a\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u7f29\u624b\u3002\u5510\u6653\u6f9c\u7ffb\u8eab\u8df3\u8d77\uff0c\u4e0e\u674e\u6cbb\u53cc\u5251 \u8054\u653b\u3002 \u3000\u3000\u53e6\u4e00\u8fb9\u5929\u53f6\u6563\u4eba\u6321\u4e86\u51af\u745b\u51e0\u62db\uff0c\u5927\u4e3a\u60ca\u8be7\uff0c\u60f3\u9053\uff1a\u60f3\u4e0d\u5230\u8fd9\u91ce\u4e2b\u5934\u7684\u5251\u6cd5\uff0c\u7adf\u7136\u7cbe\u8fdb\u5982 \u65af\uff01\u4ed6\u628a\u51af\u745b\u8bef\u4f5c\u51af\u7433\u3002\u54c8\u5e03\u9640\u8df3\u4e0a\u4e24\u6b65\uff0c\u6b63\u60f3\u5e2e\u5fd9\u5929\u53f6\u6563\u4eba\u5c06\u51af\u745b\u751f\u64d2\uff0c\u5ffd\u7136\u6797\u5b50\u53c8\u8df3\u8fdb \u4e00\u4e2a\u5c11\u5973\uff0c\u54c8\u5e03\u9640\u5927\u5403\u4e00\u60ca\uff0c\u5148\u540e\u4e24\u4e0a\u5c11\u5973\u7adf\u662f\u4e00\u6a21\u4e00\u6837\uff01\u5929\u53f6\u6563\u4eba\u4e5f\u53d1\u89c9\u4e86\uff0c\u4e24\u4eba\u9762\u9762\u76f8 \u89d1\uff0c\u4e0d\u77e5\u54ea\u4e00\u4e2a\u624d\u662f\u771f\u7684\u201c\u7433\u8d35\u4eba\u201d\uff01 \u3000\u3000\u539f\u6765\u51af\u745b\u51af\u7433\u674e\u6cbb\u4e09\u4eba\u542c\u89c1\u4e86\u7518\u51e4\u6c60\u7684\u54cd\u7bad\uff0c\u6025\u5fd9\u8d76\u6765\uff0c\u51af\u745b\u8f7b\u529f\u6700\u597d\uff0c\u8d76\u5728\u524d\u9762\uff0c\u548c \u5929\u53f6\u6563\u4eba\u4ea4\u4e0a\u4e86\u624b\uff0c\u674e\u6cbb\u5176\u6b21\uff0c\u4e5f\u53ca\u65f6\u8d76\u5230\uff0c\u6070\u6070\u89e3\u4e86\u5510\u6653\u6f9c\u4e4b\u5371\uff0c\u51af\u7433\u6700\u540e\uff0c\u4f46\u5374\u673a\u8b66\u5f02 \u5e38\uff0c\u4e00\u773c\u4fbf\u77a5\u89c1\u4e86\u7518\u51e4\u6c60\uff0c\u53eb\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u518d\u62b5\u6321\u4e00\u9635\uff0c\u6211\u548c\u7518\u5927\u4fa0\u4e00\u540c\u5e2e\u4f60\u3002\u201d\u51af\u7433\u901a \u6653\u897f\u85cf\u7ea2\u6559\u7684\u201c\u62cd\u7a74\u201d\uff0c\u8dd1\u5230\u7518\u51e4\u6c60\u8eab\u8fb9\uff0c\u556a\u556a\u4e24\u638c\uff0c\u6253\u5728\u201c\u4f0f\u5154\u201d\u201c\u7389\u5c71\u201d\u4e24\u4e2a\u7a74\u9053\u65b9 \u4f4d\uff0c\u7518\u51e4\u6c60\u6c14\u8840\u6d41\u901a\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u6325\u638c\u4fbf\u5411\u6606\u7538\u4e0a\u4eba\u8fdb\u62db\uff0c\u674e\u6cbb\u4e0e\u5510\u6653\u6f9c\u6b63\u5728\u5403\u7d27\uff0c\u7518\u51e4\u6c60 \u8fd9\u4e00\u52a0\u5165\uff0c\u4ee5\u4e09\u654c\u4e00\uff0c\u7acb\u523b\u8f6c\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u54c8\u5e03\u9640\u4e00\u89c1\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u7433\u8d35\u4eba\u3002\u201d\u7eb5\u8eab\u6765\u6349\u51af\u7433\uff0c\u5929\u53f6\u6563\u4eba\u8fd9\u65f6\u4e5f\u77e5 \u9053\u4e86\u8c01\u662f\u51af\u7433\uff0c\u8c01\u662f\u51af\u745b\uff0c\u987e\u5fcc\u4e00\u6d88\uff0c\u7acb\u523b\u53cd\u51fb\uff0c\u548c\u51af\u745b\u6253\u5f97\u96be\u89e3\u96be\u5206\u3002 \u3000\u3000\u54c8\u5e03\u9640\u6b66\u529f\u5f53\u5e74\u4e0e\u4e86\u56e0\u5e76\u79f0\u53cc\u7edd\uff0c\u821e\u52a8\u6d41\u661f\u9524\u4e00\u9635\u76d8\u6253\uff0c\u628a\u51af\u7433\u903c\u5f97\u6025\u8d70\u3002\u8fd9\u65f6\u6797\u5b50\u91cc \u5415\u56db\u5a18\u7684\u5578\u58f0\u53c8\u8d77\uff0c\u7518\u51e4\u6c60\u53d1\u5578\u76f8\u5e94\u3002\u51af\u7433\u542c\u5f97\u5415\u56db\u5a18\u5578\u58f0\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4f55\u4e0d\u5148\u8fdb\u6797\u4e2d\u4e0e\u56db \u5a18\u59d0\u59d0\u4f1a\u5408\uff1f\u201d\u5c55\u5f00\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u4e09\u4f0f\u4e09\u8d77\uff0c\u51b2\u8fdb\u6797\u4e2d\u3002 \u3000\u3000\u5415\u56db\u5a18\u542c\u5f97\u7518\u51e4\u6c60\u7684\u5578\u58f0\uff0c\u731b\u7136\u8df3\u4e0b\uff0c\u5411\u97e9\u91cd\u5c71\u75be\u523a\uff0c\u97e9\u91cd\u5c71\u4e3e\u8d77\u8f9f\u4e91\u9504\u6a2a\u626b\uff0c\u5415\u56db\u5a18 \u4eba\u5728\u534a\u7a7a\uff0c\u5251\u62db\u5df2\u53d8\uff0c\u5251\u950b\u4e00\u504f\uff0c\u51b2\u523a\u4ed6\u7684\u624b\u8155\u3002\u97e9\u91cd\u5c71\u6025\u5fd9\u7f29\u624b\uff0c\u80a9\u5934\u7a81\u7136\u4e00\u9635\u5267\u75db\uff0c\u539f \u6765\u662f\u88ab\u5415\u56db\u5a18\u7684\u5f13\u978b\u63e3\u4e2a\u6b63\u7740\uff0c\u5415\u56db\u5a18\u85c9\u7740\u8fd9\u4e00\u63e3\u4e4b\u52bf\uff0c\u98de\u63a0\u51fa\u5341\u4f59\u4e08\u5916\u3002 \u3000\u3000\u989d\u97f3\u548c\u5e03\u53eb\u9053\uff1a\u201c\u5916\u9762\u6709\u654c\u4eba\u6765\u4e86\uff0c\u4f60\u51fa\u53bb\u770b\u5427\uff0c\u8ba9\u6211\u6765\u5bf9\u4ed8\u8fd9\u4e2a\u5973\u8d3c\uff01\u201d\u62c2\u5c18\u98de\u5c55\uff0c \u8fde\u62a2\u4e09\u62db\uff0c\u5404\u4e0d\u76f8\u8ba9\u3002\u989d\u97f3\u548c\u5e03\u6b66\u529f\u7a0d\u900a\u4e8e\u5e08\u5144\u6606\u7538\u4e0a\u4eba\uff0c\u4e0e\u5415\u56db\u5a18\u5219\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u5415\u56db\u5a18 \u88ab\u4ed6\u7f20\u7740\uff0c\u6025\u5207\u4e4b\u95f4\u51b2\u4e0d\u51fa\u53bb\u3002 \u3000\u3000\u51af\u7433\u5954\u5165\u6797\u4e2d\uff0c\u5ffd\u89c1\u97e9\u91cd\u5c71\u51b2\u51fa\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u98de\u8eab\u659c\u95ea\uff0c\u54c8\u5e03\u9640\u72e0\u72e0\u8ffd\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a \u201c\u91ce\u4e2b\u5934\u4f60\u8fd8\u60f3\u8d70\u5417\uff1f\u201d\u97e9\u91cd\u5c71\u6296\u624b\u53d1\u51fa\u4e09\u652f\u7529\u5934\u98de\u7bad\uff0c\u5c06\u51af\u7433\u903c\u5230\u6797\u4e2d\u8f83\u4e3a\u7a7a\u65f7\u4e4b\u5730\uff0c\u514d \u5f97\u88ab\u5979\u5229\u7528\u6797\u6df1\u6811\u5bc6\u6765\u6349\u8ff7\u85cf\u3002\u54c8\u5e03\u9640\u626c\u9524\u6025\u4e0a\uff0c\u51af\u7433\u4e00\u8f6c\u8eab\u4fbf\u662f\u4e09\u628a\u593a\u547d\u98de\u5200\uff0c\u54c8\u5e03\u9640\u5927 \u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u70b9\u5fae\u672a\u4e4b\u6280\uff0c\u8fd8\u6562\u5728\u6211\u7684\u9762\u524d\u65bd\u5c55\u4e48\uff1f\u201d\u5de6\u624b\u4e00\u6284\uff0c\u4fbf\u60f3\u5c06\u5979\u8fd9\u4e09\u628a\u98de\u5200\u5168\u63a5 \u8fc7\u6765\uff0c\u54ea\u77e5\u51af\u7433\u8fd9\u4e00\u5e74\u591a\u6765\u4e60\u4e86\u65e0\u6781\u6d3e\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u5df2\u662f\u5927\u975e\u6614\u6bd4\uff0c\u867d\u7136\u8fd8\u8981\u7565\u900a\u4e8e\u54c8\u5e03\u9640 \u97e9\u91cd\u5c71\u7b49\u4eba\uff0c\u4f46\u4ea6\u5df2\u76f8\u5dee\u4e0d\u8fdc\u3002\u54c8\u5e03\u9640\u521a\u4e00\u4f38\u624b\uff0c\u5ffd\u89c1\u98de\u5200\u5578\u98ce\uff0c\u6765\u52bf\u731b\u6781\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u6025 \u6025\u7f29\u624b\u8eb2\u907f\uff0c\u4e09\u628a\u98de\u5200\uff0c\u4e00\u53e3\u4ece\u4ed6\u5934\u9876\u63a0\u8fc7\uff0c\u4e24\u53e3\u4ece\u8033\u8fb9\u7a7f\u8fc7\uff0c\u5200\u98ce\u522e\u8033\uff0c\u867d\u672a\u53d7\u4f24\uff0c\u4ea6\u89c9 \u75bc\u75db\u3002\u51af\u7433\u683c\u683c\u4e00\u7b11\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u4ecd\u5411\u524d\u5954\u3002\u54c8\u5e03\u9640\u5927\u6012\uff0c\u6d41\u661f\u9524\u500f\u7136\u98de\u51fa\uff0c\u7adf\u7136\u98de\u5230\u51af\u7433 \u524d\u9762\uff0c\u5c06\u4e00\u5757\u62e6\u8def\u7684\u5927\u77f3\u5934\u6253\u5f97\u7c89\u788e\uff01 \u3000\u3000\u54c8\u5e03\u9640\u7528\u610f\u662f\u60f3\u628a\u5927\u77f3\u6253\u788e\uff0c\u903c\u5f97\u51af\u7433\u4e0d\u6562\u524d\u7a9c\uff0c\u975e\u8d70\u56de\u5934\u675f\u624b\u5c31\u64d2\u4e0d\u53ef\u3002\u90a3\u77e5\u8fd9\u98de\u9524 \u4e00\u51fb\uff0c\u5927\u77f3\u662f\u6253\u788e\u4e86\uff0c\u5374\u51fa\u73b0\u4e00\u4e2a\u8c01\u4e5f\u60f3\u4e0d\u5230\u7684\u4e8b\uff0c\u5728\u90a3\u5927\u77f3\u7684\u540e\u9762\uff0c\u7adf\u7136\u662f\u4e2a\u6781\u6df1\u7684\u6d1e \u7a74\uff0c\u5927\u77f3\u6253\u788e\u4e4b\u540e\uff0c\u9732\u51fa\u9ed1\u9edd\u9edd\u7684\u6d1e\u53e3\uff0c\u91cc\u9762\u7adf\u7136\u4f3c\u6709\u547b\u541f\u4e4b\u58f0\u3002 \u3000\u3000\u51af\u7433\u88ab\u54c8\u5e03\u9640\u903c\u5f97\u6025\u4e86\uff0c\u4e0d\u613f\u540e\u9000\u5c31\u64d2\uff0c\u5728\u90a3\u77f3\u5757\u7eb7\u98de\u4e4b\u65f6\uff0c\u4f7f\u4e86\u4e00\u62db\u65e0\u6781\u5251\u7684\u7cbe\u5999\u62db \u6570\u201c\u516b\u65b9\u98ce\u96e8\u201d\uff0c\u5251\u52bf\u56db\u9762\u5c55\u5f00\uff0c\u5728\u6c99\u96e8\u77f3\u5c51\u4e4b\u4e2d\u5f80\u524d\u4e00\u7a9c\uff0c\u5c06\u7802\u77f3\u8361\u5f97\u56db\u8fb9\u6fc0\u5c04\uff0c\u4eba\u4e5f\u7a9c \u5165\u4e86\u6d1e\u5185\u3002 \u3000\u3000\u6d1e\u4e2d\u51b7\u6c14\u68ee\u68ee\uff0c\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\uff0c\u51af\u7433\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u542c\u5f97\u6d1e\u53e3\u54c8\u5e03\u9640\u5567\u5567\u79f0\u602a\u4e4b\u58f0\uff0c\u8fc7 \u4e86\u4e00\u9635\uff0c\u53c8\u542c\u5f97\u54c8\u5e03\u9640\u53eb\u9053\uff1a\u201c\u4f60\u5c31\u662f\u9003\u5165\u4e86\u864e\u7a9d\u86c7\u7a74\uff0c\u6211\u4e5f\u8981\u628a\u4f60\u638f\u51fa\u6765\u3002\u201d\u51af\u7433\u751f\u6765\u6015 \u86c7\uff0c\u542c\u4ed6\u8bf4\u51fa\u201c\u86c7\u7a74\u201d\u4e8c\u5b57\uff0c\u53c8\u662f\u4e00\u60ca\uff0c\u53ef\u54c8\u5e03\u9640\u5df2\u7ecf\u6478\u7d22\u8d70\u8fdb\uff0c\u5bb9\u4e0d\u5f97\u5979\u518d\u8fdf\u7591\uff0c\u53ea\u597d\u4ee5 \u5251\u9632\u8eab\uff0c\u4ecd\u7136\u5411\u6d1e\u53e3\u8d70\u8fdb\u3002 \u3000\u3000\u8fd9\u6d1e\u5f88\u6df1\uff0c\u8d70\u4e86\u4e00\u9635\uff0c\u53c8\u6e05\u6e05\u695a\u695a\u7684\u542c\u5f97\u4e24\u58f0\u547b\u541f\uff0c\u63a5\u7740\u53c8\u6709\u5636\u5636\u602a\u54cd\uff0c\u51af\u7433\u6bdb\u9aa8\u609a \u7136\uff0c\u8d77\u4e86\u9e21\u76ae\u7599\u7629\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u8fd9\u91cc\u771f\u662f\u86c7\u7a74\uff0c\u90a3\u547b\u541f\u4e4b\u58f0\uff0c\u662f\u4eba\u88ab\u86c7\u541e\u556e\u65f6\u6240\u53d1\u51fa\u7684\u58f0 \u97f3\u5417\uff1f\u201d\u62ac\u5934\u4e00\u770b\uff0c\u5ffd\u89c1\u9ed1\u6697\u4e2d\u95ea\u7740\u4e24\u70b9\u78a7\u7eff\u7684\u5149\uff0c\u7eff\u5149\u95ea\u95ea\u70c1\u70c1\uff0c\u5636\u5636\u4e4b\u58f0\u5c31\u5728\u7eff\u5149\u4e2d\u53d1 \u51fa\uff0c\u8fd9\u5206\u660e\u662f\u6bd2\u86c7\u7684\u773c\u775b\uff01 \u3000\u3000\u51af\u7433\u5927\u53eb\u4e00\u58f0\uff0c\u4e00\u5251\u5411\u524d\u65a9\u53bb\uff0c\u731b\u7136\u95f4\u624b\u8155\u4e00\u75db\uff0c\u5b9d\u5251\u5f53\u5f53\u5815\u5730\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u4f4e\u58f0\u559d \u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u4f24\u5bb3\u6211\u6551\u4eba\u7684\u86c7\u513f\uff1f\u201d\u51af\u7433\u8fd0\u52b2\u6323\u624e\uff0c\u5374\u662f\u6d51\u8eab\u8f6f\u7ef5\u7ef5\u7684\u53d1\u4e0d\u51fa\u52b2\u6765\uff0c\u5ffd\u89c9\u6709 \u53c8\u8f6f\u53c8\u6ed1\u7684\u4e1c\u897f\u5a49\u8712\u4e0a\u8eab\uff0c\u5413\u5f97\u5979\u9b42\u98de\u9b44\u6563\u3002 \u3000\u3000\u660f\u6697\u4e2d\u51af\u7433\u5ffd\u53c8\u542c\u5f97\u90a3\u4eba\u54a6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u554a\uff0c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u662f\u6069\u4eba\u6765\u4e86\uff0c\u6069\u4eba\u522b \u614c\uff0c\u6211\u8fd9\u86c7\u662f\u518d\u4e5f\u4e0d\u4f1a\u7528\u6765\u4f24\u4eba\u7684\u4e86\u3002\u91d1\u513f\uff0c\u56de\u6765\uff01\u201d\u4f4e\u53f1\u4e00\u58f0\uff0c\u90a3\u6761\u86c7\u4ece\u51af\u7433\u7684\u8eab\u4e0a\u5a49\u8712 \u800c\u4e0b\uff0c\u90a3\u4eba\u53c8\u95ee\u9053\uff1a\u201c\u51af\u59d1\u5a18\uff0c\u4f60\u4f55\u4ee5\u77e5\u9053\u6211\u5728\u8fd9\u513f\uff1f\u201d\u51af\u7433\u5728\u5916\u9762\u5149\u5904\u8d70\u5165\u6d1e\u4e2d\uff0c\u773c\u775b\u4ecd \u7136\u4e0d\u80fd\u89c1\u7269\uff0c\u4e0d\u77e5\u524d\u9762\u7684\u7a76\u662f\u4f55\u4eba\uff0c\u60ca\u9b42\u672a\u5b9a\uff0c\u90a3\u4eba\u53c8\u9053\uff1a\u201c\u53c8\u6709\u4e00\u4e2a\u4eba\u6765\uff0c\u51af\u59d1\u5a18\uff0c\u4ed6\u662f \u4f60\u7684\u540c\u4f34\u5417\uff1f\u201d\u51af\u7433\u7a0d\u6444\u5fc3\u795e\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u65e2\u7136\u79f0\u6211\u505a\u6069\u4eba\uff0c\u53c8\u6709\u8fd9\u4e48\u9ad8\u7684\u672c\u9886\uff0c\u6211\u4e0d\u5982\u8bf7 \u4ed6\u4e00\u6551\u3002\u201d\u5916\u9762\u54c8\u5e03\u9640\u7684\u811a\u6b65\u58f0\u8d8a\u6765\u8d8a\u8fd1\uff0c\u5e76\u53d1\u58f0\u606b\u5413\u9053\uff1a\u201c\u91ce\u4e2b\u5934\uff0c\u4f60\u518d\u4e0d\u6eda\u51fa\u6765\uff0c\u6211\u5c31 \u7528\u6d41\u661f\u9524\u98de\u8fdb\u53bb\u6253\u788e\u4f60\u7684\u5934\u9885\u3002\u201d\u90a3\u4eba\u53c8\u54a6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u539f\u6765\u4ed6\u662f\u4f60\u7684\u654c\u4eba\uff01\u201d\u51af\u7433\u6025\u5fd9 \u8bf4\u9053\uff1a\u201c\u662f\u6211\u7684\u4ec7\u4eba\uff0c\u4ed6\u6076\u5f97\u5f88\uff01\u201d\u90a3\u4eba\u4e00\u58f0\u51b7\u7b11\u9053\uff1a\u201c\u6069\u4eba\u522b\u614c\uff0c\u6211\u4e13\u6536\u670d\u6076\u4eba\uff01\u201d \u3000\u3000\u54c8\u5e03\u9640\u8d70\u5165\u8fd9\u6837\u9634\u6c89\u7684\u602a\u6d1e\uff0c\u53c8\u542c\u5f97\u5636\u5636\u4e4b\u58f0\uff0c\u4ea6\u662f\u4e0d\u89c9\u5fc3\u5934\u98a4\u7c9f\u3002\u4f46\u4ed6\u81ea\u5f85\u827a\u9ad8\u80c6 \u5927\uff0c\u5c06\u6d41\u661f\u9524\u821e\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u62a4\u4f4f\u8eab\u8eaf\uff0c\u60f3\u9053\uff1a\u201c\u5c31\u662f\u6709\u6bd2\u86c7\u7a81\u7136\u6251\u6765\uff0c\u4e5f\u4f1a\u7ed9\u6211\u7684\u9524\u98ce\u9707 \u6b7b\uff01\u201d\u8fb9\u821e\u6376\uff0c\u8fb9\u6478\u7d22\uff0c\u8d70\u5230\u6d1e\u4e2d\u6df1\u5904\uff0c\u5ffd\u542c\u5f97\u51af\u7433\u548c\u4eba\u4f4e\u58f0\u8c08\u8bdd\uff0c\u4e0d\u89c9\u5927\u5947\u3002 \u3000\u3000\u54c8\u5e03\u9640\u5c06\u6d41\u661f\u9524\u76d8\u5934\u4e00\u821e\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u602a\u7269\uff0c\u8eb2\u5728\u8fd9\u91cc\u5413\u4eba\uff1f\u201d\u9ed1\u6697\u4e2d\u53d1\u51fa\u4e00\u9635 \u9634\u6c89\u6c89\u7684\u51b7\u7b11\uff0c\u90a3\u4eba\u5e94\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u6bd4\u4f60\u4eec\u5728\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\u5e72\u51fa\u90aa\u6076\u52fe\u5f53\u7684\u8981\u597d\u5f97\u591a\uff01\u201d \u54c8\u5e03\u9640\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u6d41\u661f\u9524\u5411\u524d\u4e00\u6446\uff0c\u53ea\u542c\u5f97\u90a3\u4eba\u53c8\u9053\uff1a\u201c\u5514\uff0c\u662f\u6709\u4e00\u70b9\u95e8\u9053\uff0c\u602a\u4e0d \u5f97\u6562\u4f8d\u5f3a\u6b3a\u8d1f\u6211\u7684\u6069\u4eba\uff0c\u53ea\u53ef\u60dc\u4f60\u8fd9\u9a6c\u56de\u56de\u7684\u9524\u6cd5\u8fd8\u672a\u5b66\u5168\uff01\u201d\u54c8\u5e03\u9640\u5927\u5403\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u5728 \u9ed1\u6697\u4e2d\u4e00\u4e2a\u7167\u9762\uff0c\u4ed6\u5c31\u77a7\u6e05\u695a\u4e86\u6211\u7684\u5bb6\u6570\uff0c\u5012\u662f\u4e0d\u53ef\u8f7b\u654c\u3002\u8fd0\u8db3\u5185\u52b2\uff0c\u6d41\u661f\u9524\u7a81\u7136\u5411\u524d\u4e00 \u626b\uff0c\u731b\u7136\u95f4\uff0c\u53ea\u542c\u5f97\u90a3\u4eba\u4e00\u58f0\u5927\u559d\uff1a\u201c\u4f60\u6562\u4f24\u5bb3\u6211\u7684\u75c5\u4eba\u3002\u201d\u54c8\u5e03\u9640\u529f\u529b\u6df1\u539a\uff0c\u8fd9\u6d41\u661f\u9524\u4e00 \u626b\u4e4b\u529b\u4f55\u6b62\u5343\u65a4\uff0c\u4e0d\u6599\u626b\u51fa\u4e4b\u540e\uff0c\u5ffd\u7136\u88ab\u62ff\u7740\u9524\u5934\uff0c\u4e0d\u80fd\u79fb\u52a8\uff0c\u54c8\u5e03\u9640\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u53cc \u624b\u4e00\u677e\uff0c\u626d\u5934\u4fbf\u8dd1\uff0c\u90a3\u4eba\u4e00\u58f0\u51b7\u7b11\uff0c\u58f0\u5230\u4eba\u5230\uff0c\u5939\u624b\u4e00\u4e0b\u626d\u7740\u54c8\u5e03\u9640\u7684\u9888\u8116\uff0c\u5411\u524d\u4e00\u6325\uff0c\u559d \u58f0\uff1a\u201c\u53bb\uff01\u201d\u54c8\u5e03\u9640\u90a3\u6837\u9ad8\u7684\u6b66\u529f\uff0c\u7adf\u7136\u6beb\u65e0\u529e\u6cd5\u52a8\u5f39\uff0c\u88ab\u4ed6\u6454\u51fa\u4e86\u6d1e\u5916\u3002 \u3000\u3000\u51af\u7433\u53c8\u60ca\u53c8\u559c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u672a\u770b\u6e05\u695a\u5417\uff1f\u4f60\u77a7\u4e0d\u89c1\u6211\u7684\u9762\u4e5f \u8be5\u542c\u5f97\u51fa\u6211\u7684\u58f0\u97f3\u3002\u4f60\u6599\u4e0d\u5230\u6211\u4f1a\u5230\u6b64\u5427\uff1f\u201d\u51af\u7433\u83ab\u540d\u5176\u5999\uff0c\u53ea\u542c\u5f97\u90a3\u4eba\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u7684\u5415 \u59d0\u59d0\u5462\uff1f\u201d\u51af\u7433\u5fd9\u9053\uff1a\u201c\u5979\u5728\u5916\u9762\uff0c\u88ab\u6076\u4eba\u56f0\u4f4f\u4e86\u3002\u4f60\u53bb\u6551\u5979\u5427\uff01\u201d\u90a3\u4eba\u9053\uff1a\u201c\u54e6\uff0c\u602a\u4e0d\u5f97 \u6211\u4f3c\u4e4e\u542c\u89c1\u5979\u7684\u5578\u58f0\u3002\u4ec0\u4e48\u4eba\uff0c\u6709\u672c\u9886\u5c06\u5979\u56f0\u4f4f\uff0c\u6211\u5012\u8981\u53bb\u770b\u4e00\u770b\uff01\u201d\u65c1\u8fb9\u53c8\u6709\u4eba\u547b\u541f\u4e86\u4e24 \u58f0\uff0c\u90a3\u4eba\u6478\u4e86\u4e00\u6478\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5df2\u7ecf\u597d\u4e86\uff0c\u518d\u8eba\u534a\u5929\uff0c\u6211\u5c31\u9001\u4f60\u4eec\u56de\u5bb6\uff01\u201d \u3000\u3000\u8fd9\u4eba\u6b63\u662f\u6bd2\u9f99\u5c0a\u8005\uff0c\u8fd9\u4e00\u5e74\u6765\u4ed6\u5230\u5904\u6551\u6cbb\u9ebb\u75af\u75c5\u4eba\uff0c\u5341\u65e5\u4e4b\u524d\uff0c\u521a\u5de7\u6765\u5230\u6b64\u5730\u3002\u4ed6\u6015\u60ca \u4e16\u9a87\u4fd7\uff0c\u56e0\u6b64\u643a\u5e26\u9ebb\u75af\u75c5\u4eba\u8eb2\u5165\u4e86\u8fd9\u4e2a\u6d1e\u4e2d\uff0c\u53c8\u7528\u5927\u77f3\u585e\u4f4f\u6d1e\u53e3\u3002\u5176\u4e2d\u4e24\u4e2a\u60a3\u5f97\u6700\u8f7b\u7684\u5df2\u88ab \u4ed6\u533b\u597d\u9001\u56de\u5bb6\u4e2d\uff0c\u7559\u4e0b\u4e24\u4e2a\u8f83\u91cd\u7684\u8fd8\u5728\u6d1e\u4e2d\u3002\u8fd9\u4e24\u4eba\u8eab\u4f53\u865a\u5f31\uff0c\u6bd2\u9f99\u5c0a\u8005\u523a\u86c7\u8840\u7528\u836f\u7269\u66ff\u4ed6 \u4eec\u533b\u6cbb\uff0c\u9ebb\u75af\u6e10\u597d\uff0c\u53ef\u662f\u5374\u56e0\u8010\u4e0d\u4f4f\u5730\u6d1e\u6f6e\u6e7f\u5bd2\u51b7\uff0c\u53d1\u4e86\u865a\u635f\u4e4b\u75c7\uff0c\u4e24\u65e5\u4e2d\u4e0d\u7701\u4eba\u4e8b\u3002\u6bd2\u9f99 \u5c0a\u8005\u9664\u4e86\u61c2\u5f97\u533b\u9ebb\u75af\u4e4b\u5916\uff0c\u5bf9\u4e8e\u5176\u4ed6\u533b\u9053\uff0c\u5374\u662f\u4e00\u7a8d\u4e0d\u901a\uff0c\u800c\u4e14\u4e5f\u65e0\u4ece\u89c5\u836f\u3002\u53ea\u5f97\u7528\u81ea\u5df1\u7684 \u771f\u5143\u4e4b\u6c14\uff0c\u5ea6\u5165\u75c5\u4eba\u53e3\u4e2d\uff0c\u5e2e\u4ed6\u52a0\u5f3a\u62b5\u6297\uff0c\u8fd9\u9053\u7406\u5c31\u9a8b\u4e8e\u8f93\u8840\u6551\u4eba\u4e00\u6837\u3002\u5728\u8fd9\u4e00\u5929\u4e00\u591c\u4e4b \u4e2d\uff0c\u6bd2\u9f99\u5c0a\u8005\u51dd\u795e\u9759\u6c14\u533b\u80f3\u75c5\u4eba\uff0c\u6240\u4ee5\u542c\u4e0d\u5230\u5415\u56db\u5a18\u5148\u524d\u7684\u5578\u58f0\u3002\u81f3\u51af\u7433\u6765\u65f6\uff0c\u90a3\u4e24\u4e2a\u75c5\u4eba \u5df2\u80fd\u51fa\u58f0\u547b\u541f\uff0c\u8131\u4e86\u9669\u5883\uff0c\u6bd2\u9f99\u5c0a\u8005\u66f4\u65e0\u6302\u8651\uff0c\u7acb\u523b\u4fbf\u548c\u51af\u7433\u51fa\u6d1e\u3002 \u3000\u3000\u54c8\u5e03\u9640\u88ab\u6bd2\u9f99\u5c0a\u8005\u5f3a\u6454\u51fa\u6d1e\uff0c\u97e9\u91cd\u5c71\u5728\u6d1e\u53e3\u5b88\u5019\uff0c\u6b63\u597d\u4e00\u628a\u63a5\u7740\uff0c\u89c1\u4ed6\u9888\u8116\u6307\u75d5\u6df1\u9677\uff0c \u9762\u8272\u7070\u767d\uff0c\u72fc\u72c8\u975e\u5e38\uff0c\u8fde\u6d41\u661f\u9524\u4e5f\u4e0d\u89c1\u4e86\uff0c\u9a87\u7136\u95ee\u9053\uff1a\u201c\u6d1e\u4e2d\u85cf\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u54c8\u5e03\u9640\u6478\u6478 \u9888\u9879\uff0c\u6c14\u5401\u5401\u7684\u9053\uff1a\u201c\u602a\u7269\uff0c\u602a\u7269\uff01\u5feb\u8bf7\u6606\u7538\u4e0a\u4eba\u6765\uff01\u201d\u97e9\u91cd\u5c71\u77e5\u4ed6\u5403\u4e86\u5927\u4e8f\uff0c\u6b64\u60ca\u975e\u5c0f\uff0c \u64ae\u5507\u4e00\u5578\uff0c\u6025\u53eb\u6606\u7538\u4e0a\u4eba\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6d1e\u4e2d\u7a81\u7136\u7a9c\u51fa\u4e00\u4eba\uff0c\u62ab\u5934\u6563\u53d1\uff0c\u76f8\u8c8c\u602a\u5f02\uff0c\u97e9 \u91cd\u5c71\u5b88\u5728\u6d1e\u8fb9\uff0c\u6b63\u662f\u8981\u7b49\u4ed6\u51fa\u6765\uff0c\u7a81\u65bd\u6740\u624b\uff0c\u4e00\u89c1\u4eba\u5f71\uff0c\u7acb\u523b\u4e00\u9504\u5288\u4e0b\uff01 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u6d77\u6daf\u6765\u5f02\u58eb\uff0c\u53cc\u638c\u6597\u7fa4\u51f6\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e8c\u56de\u3000\u6258\u5b50\u62dc\u5978\u5112\u3000\u5c06\u519b\u8fdc\u8651\u3000\u501f\u5200\u8bdb\u6076\u8d3c\u3000\u4fa0\u58eb\u89c1\u673a \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u56db\u5341\u4e8c\u56de \u6258\u5b50\u62dc\u5978\u5112\u3000\u5c06\u519b\u8fdc\u8651 \u501f\u5200\u8bdb\u6076\u8d3c\u3000\u4fa0\u58eb\u89c1\u673a \u3000\u3000\u97e9\u91cd\u5c71\u4e00\u9504\u5288\u4e0b\uff0c\u6bd2\u9f99\u5c0a\u8005\u9ebb\u8863\u5927\u8896\u4e00\u7529\uff0c\u559d\u9053\uff1a\u201c\u597d\u4e0d\u8981\u8138\uff01\u201d\u97e9\u91cd\u5c71\u7684\u8f9f\u4e91\u9504\u88ab\u4ed6 \u4e00\u7529\uff0c\u9707\u5f97\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u602a\u4e0d\u5f97\u54c8\u5e03\u9640\u5403\u4ed6\u7684\u5927\u4e8f\uff0c\u6b64\u4eba\u529f\u529b\u679c\u7136\u662f\u6bd4\u6211\u8f88 \u9ad8\u51fa\u8bb8\u591a\uff0c\u6bd2\u9f99\u5c0a\u8005\u5927\u8896\u4e00\u7529\uff0c\u6ca1\u80fd\u5c06\u4ed6\u7684\u8f9f\u4e91\u9504\u7529\u98de\uff0c\u4e5f\u5fae\u5fae\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u539f\u6765\u5415\u56db \u5a18\u62db\u6765\u8fd9\u4e48\u591a\u5f3a\u654c\uff0c\u602a\u4e0d\u5f97\u88ab\u56f0\u4f4f\u4e86\u3002 \u3000\u3000\u97e9\u91cd\u5c71\u7684\u529f\u529b\u4e0e\u54c8\u5e03\u9640\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u4f55\u4ee5\u54c8\u5e03\u9640\u4e00\u89c1\u9762\u4fbf\u5403\u5927\u4e8f\uff0c\u800c\u97e9\u91cd\u5c71\u867d\u5403\u4e86\u4e8f\u5374 \u5c1a\u4e0d\u81f3\u8dcc\u5012\uff1f\u5176\u4e2d\u6709\u4e2a\u9053\u7406\u3002\u9002\u624d\u54c8\u5e03\u9640\u4ece\u5916\u9762\u5149\u5904\u8d70\u5165\u9ed1\u6d1e\uff0c\u7b49\u4e8e\u662f\u6bd2\u9f99\u5c0a\u8005\u5728\u660e\u5904\u4ed6\u5728 \u6697\u5904\uff0c\u800c\u4e14\u54c8\u5e03\u9640\u672a\u89c1\u8bc6\u8fc7\u6bd2\u9f99\u5c0a\u8005\u7684\u672c\u9886\uff0c\u9632\u5907\u4e5f\u4e0d\u5468\u5168\uff0c\u56e0\u6b64\u5403\u6bd2\u9f99\u5c0a\u8005\u51b7\u4e0d\u9632\u7684\u4e00\u8bb0 \u64d2\u62ff\uff0c\u4fbf\u5c06\u4ed6\u6454\u51fa\u3002\u800c\u4eca\u97e9\u91cd\u5c71\u8eb2\u5728\u6d1e\u53e3\u6697\u88ad\uff0c\u4e43\u662f\u4ed6\u5728\u660e\u6bd2\u9f99\u5c0a\u8005\u5728\u6697\uff0c\u4ed6\u5df2\u77e5\u6765\u4eba\u672c\u9886 \u6781\u4e3a\u9ad8\u5f3a\uff0c\u672a\u6599\u80dc\u5148\u8651\u8d25\uff0c\u4e00\u9504\u5288\u4e0b\uff0c\u8eab\u5f62\u7acb\u9000\uff0c\u6240\u4ee5\u4e0d\u81f3\u4e8e\u50cf\u54c8\u5e03\u9640\u7684\u90a3\u6837\u5403\u4e86\u5927\u4e8f\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e00\u4ea4\u624b\uff0c\u5f3a\u5f31\u5df2\u5224\uff0c\u97e9\u91cd\u5c71\u4e0d\u6562\u62b5\u654c\uff0c\u62d6\u9504\u6025\u8d70\u3002\u6bd2\u9f99\u5c0a\u8005\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a \u201c\u5415\u5973\u4fa0\uff0c\u5415\u5973\u4fa0\uff01\u201d\u5415\u56db\u5a18\u6b63\u5728\u6797\u4e2d\u548c\u989d\u9752\u548c\u5e03\u76f8\u6301\u4e0d\u4e0b\uff0c\u542c\u5f97\u6bd2\u9f99\u5c0a\u8005\u7684\u53eb\u58f0\uff0c\u7cbe\u795e\u5927 \u632f\uff0c\u5237\u5237\u51e0\u5251\uff0c\u8fde\u523a\u989d\u97f3\u548c\u5e03\u8981\u5bb3\uff0c\u989d\u97f3\u548c\u5e03\u632a\u8fc7\u4e00\u8fb9\uff0c\u5415\u56db\u5a18\u7acb\u523b\u5982\u98de\u51b2\u51fa\uff0c\u989d\u97f3\u548c\u5e03\u6025 \u6025\u8ffd\u8d76\uff0c\u8ffd\u5230\u5916\u9762\uff0c\u53ea\u89c1\u4ed6\u7684\u5e08\u5144\u6606\u7538\u4e0a\u4eba\u548c\u6bd2\u9f99\u5c0a\u8005\u5df2\u662f\u4e24\u9635\u5bf9\u5706\uff0c\u5373\u5c06\u52a8\u624b\u3002 \u3000\u3000\u539f\u6765\u6606\u7538\u4e0a\u4eba\u529b\u654c\u5510\u6653\u6f9c\u7518\u51e4\u6c60\u674e\u6cbb\u4e09\u4eba\uff0c\u4e45\u6218\u4e0d\u4e0b\uff0c\u54c8\u5e03\u9640\u4e0e\u97e9\u91cd\u5c71\u53cc\u53cc\u8dd1\u6765\uff0c\u5c06\u4ed6 \u66ff\u51fa\uff0c\u53eb\u4ed6\u53bb\u5bf9\u4ed8\u5f3a\u654c\u3002\u6606\u7538\u4e0a\u4eba\u7acb\u523b\u64a4\u51fa\uff0c\u8fce\u5934\u515c\u622a\uff0c\u53ea\u89c1\u6bd2\u9f99\u5c0a\u8005\u62ab\u5934\u6563\u53d1\uff0c\u72b6\u82e5\u91ce \u4eba\uff0c\u6606\u7538\u4e0a\u4eba\u9887\u611f\u8be7\u5f02\uff0c\u5fc3\u9053\uff1a\u54ea\u91cc\u94bb\u51fa\u8fd9\u4e48\u4e00\u4e2a\u91ce\u4eba\uff0c\u53eb\u54c8\u5e03\u9640\u4e0e\u97e9\u91cd\u5c71\u5413\u6210\u8fd9\u6837\uff1f\u201d \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u89c1\u5415\u56db\u5a18\u8dd1\u51fa\uff0c\u4e00\u58f0\u6b22\u547c\uff0c\u7518\u51e4\u6c60\u4e0e\u5415\u56db\u5a18\u9f50\u58f0\u53eb\u9053\uff1a\u201c\u6bd2\u9f99\u5c0a\u8005\uff01\u201d\u51af\u7433\u8fd9 \u624d\u604d\u7136\u5927\u609f\uff0c\u5979\u542c\u59d0\u59d0\u8bf4\u8fc7\u86c7\u5c9b\u4e4b\u4e8b\uff0c\u6599\u60f3\u662f\u6bd2\u9f99\u5c0a\u8005\u628a\u5979\u5f53\u4f5c\u51af\u745b\uff0c\u6240\u4ee5\u624d\u4f1a\u51fa\u624b\u76f8\u6551\uff01 \u3000\u3000\u989d\u97f3\u548c\u5e03\u8ffd\u4e86\u51fa\u6765\uff0c\u542c\u4e86\u6bd2\u9f99\u5c0a\u8005\u4e4b\u540d\uff0c\u4e0d\u89c9\u4e00\u6115\u3002\u4ed6\u6bd4\u5e08\u5144\u5148\u5165\u7687\u5bab\uff0c\u4e00\u65e5\u5728\u5076\u7136\u8c08 \u8bdd\u4e4b\u95f4\uff0c\u542c\u96cd\u6b63\u7687\u5e1d\u8bf4\u8fc7\u53eb\u53cc\u9b54\u8058\u8bf7\u6bd2\u9f99\u5c0a\u8005\uff0c\u8bf7\u4ed6\u4e0d\u52a8\u4e4b\u4e8b\uff0c\u4e0d\u7981\u53eb\u9053\uff1a\u201c\u7687\u4e0a\u8bf7\u4f60\uff0c\u4f60 \u4e0d\u6765\u4e5f\u8fd8\u7f62\u4e86\uff0c\u5374\u6765\u52a9\u9006\u72af\u4e0a\u5417\uff1f\u201d \u3000\u3000\u4e0d\u8bf4\u72b9\u53ef\uff0c\u4e00\u8bf4\u4e4b\u540e\uff0c\u6bd2\u9f99\u5c0a\u8005\u8d6b\u7136\u9707\u6012\uff0c\u8fd9\u4e00\u5e74\u6765\u4ed6\u91cd\u5c65\u4e2d\u571f\uff0c\u542c\u5230\u4e86\u8bb8\u591a\u96cd\u6b63\u66b4\u8650 \u4e4b\u4e8b\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u4f60\u4eec\u90fd\u662f\u7687\u5bb6\u9e70\u72ac\uff01\u201d\u4f38\u624b\u4e00\u6298\uff0c\u6298\u4e86\u4e00\u679d\u6811\u679d\uff0c\u53cc\u6307\u4e00\u5f39\uff0c \u4fbf\u5411\u989d\u97f3\u548c\u5e03\u98de\u53bb\uff0c\u6606\u7538\u4e0a\u4eba\u6a2a\u91cc\u8dc3\u51fa\uff0c\u62db\u624b\u4e00\u6284\uff0c\u5c06\u90a3\u679d\u6811\u679d\u6284\u5728\u624b\u4e2d\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u539f \u6765\u4f60\u662f\u6bd2\u9f99\u5c0a\u8005\uff0c\u4f60\u8fd9\u624b\u529f\u592b\u4e0d\u4fd7\uff0c\u6765\u6765\uff0c\u54b1\u4eec\u89c1\u4e2a\u771f\u7ae0\uff01\u201d\u53cc\u638c\u4e00\u62b1\uff0c\u4ee5\u6cf0\u5c71\u538b\u9876\u4e4b\u52bf\uff0c \u5411\u6bd2\u9f99\u5c0a\u8005\u63a8\u53bb\u3002 \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u53cc\u638c\u4e00\u6321\uff0c\u53cc\u65b9\u90fd\u611f\u5230\u4e00\u80a1\u5f3a\u52b2\u4e4b\u6781\u7684\u529b\u9053\u5411\u81ea\u5df1\u8fdb\u903c\uff0c\u8c01\u4e5f\u4e0d\u6562\u53d8\u62db\u64a4\u624b\u3002 \u4e24\u4eba\u56db\u638c\u76f8\u4ea4\uff0c\u9489\u5728\u573a\u4e2d\uff0c\u56db\u76ee\u5bf9\u89c6\uff0c\u72b6\u82e5\u6597\u9e21\uff0c\u7adf\u662f\u4e0d\u76f8\u4e0a\u4e0b\u3002 \u3000\u3000\u7518\u51e4\u6c60\u7b49\u4eba\u90fd\u8df3\u51fa\u4e86\u5708\u5b50\uff0c\u770b\u6bd2\u9f99\u5c0a\u8005\u4e0e\u6606\u7538\u4e0a\u4eba\u6076\u6597\u3002\u5510\u6653\u6f9c\u8dd1\u5230\u5415\u56db\u5a18\u8eab\u8fb9\uff0c\u4f4e\u58f0 \u9053\u8c22\u3002\u5415\u56db\u5a18\u89c1\u4ed6\u5b89\u7136\u65e0\u4e8b\uff0c\u5341\u5206\u6b22\u559c\uff0c\u9053\uff1a\u201c\u4f60\u597d\u4e86\uff0c\u6211\u4fbf\u5b89\u5fc3\u4e86\u3002\u201d\u51af\u745b\u4e5f\u8dd1\u4e86\u8fc7\u6765\uff0c \u6328\u7740\u5510\u6653\u6f9c\u8eab\u5b50\u5411\u5415\u56db\u5a18\u4f4e\u58f0\u9053\u8c22\uff0c\u5415\u56db\u5a18\u89c1\u4ed6\u4eec\u4e8c\u4eba\u4eb2\u70ed\u795e\u6c14\uff0c\u60f3\u8d77\u6c88\u5728\u5bbd\u4e0d\u77e5\u751f\u6b7b\u5982 \u4f55\uff0c\u5fc3\u4e2d\u4e00\u9178\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u8f88\u76f8\u4ea4\u5728\u5fc3\uff0c\u4f55\u5fc5\u9053\u8c22\uff1f\u770b\u6bd2\u9f99\u5c0a\u8005\u524d\u8f88\u4e0e\u6606\u7538\u76f8\u6597\u5427\uff01\u201d \u3000\u3000\u8fd9\u65f6\u6797\u5b50\u5468\u56f4\u628a\u5b88\u7684\u4f8d\u536b\u5927\u90fd\u8d70\u4e86\u56de\u6765\uff0c\u7ad9\u5728\u6606\u7538\u4e0a\u4eba\u8eab\u540e\uff0c\u6bd2\u9f99\u5c0a\u8005\u4e0e\u6606\u7538\u4e0a\u4eba\u4ecd\u5728 \u82e6\u82e6\u76f8\u6301\uff0c\u53cc\u65b9\u6c57\u5982\u51fa\u96e8\uff0c\u5374\u662f\u52a8\u4e5f\u4e0d\u52a8\u3002\u5728\u573a\u9ad8\u624b\uff0c\u65e0\u4e0d\u89e6\u76ee\u51c9\u5fc3\uff0c\u77e5\u9053\u8fd9\u79cd\u5185\u5bb6\u771f\u529b\u7684 \u62fc\u6597\uff0c\u7a76\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u8c01\u82e5\u7a0d\u6709\u758f\u5ffd\uff0c\u4fbf\u662f\u4e0d\u6b7b\u5373\u4f24\uff0c\u6bd4\u660e\u5200\u660e\u67aa\u7684\u6076\u6218\uff0c\u8fd8\u8981\u5371\u9669\u5f97\u591a\u3002 \u3000\u3000\u4e24\u4eba\u76f8\u6301\u4e86\u7ea6\u534a\u4e2a\u65f6\u8fb0\uff0c\u6606\u7538\u4e0a\u4eba\u6e10\u89c9\u5bf9\u65b9\u538b\u529b\u52a0\u5f3a\uff0c\u5916\u4eba\u8fd8\u672a\u770b\u5f97\u51fa\u6765\uff0c\u4ed6\u5374\u77e5\u9053\u82e5 \u7136\u518d\u4ee5\u771f\u529b\u62fc\u6597\u4e0b\u53bb\uff0c\u5fc5\u65e0\u5e78\u7406\u3002\u6606\u7538\u4e0a\u4eba\u4e34\u654c\u7684\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u5fc3\u601d\u4e5f\u8f83\u7075\u654f\uff0c\u77e5\u9053\u4e0d\u80fd\u53d8\u62db \u6492\u638c\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u8ba1\u4e0a\u5fc3\u6765\uff0c\u7a81\u7136\u4e00\u53e3\u553e\u6d8e\uff0c\u5411\u6bd2\u9f99\u5c0a\u8005\u773c\u775b\u5410\u53bb\u3002\u6bd2\u9f99\u5c0a\u8005\u4e0d\u9632\u6709\u6b64\u4e00 \u7740\uff0c\u4e0d\u89c9\u4f4e\u5934\u4e00\u95ea\uff0c\u6606\u7538\u4e0a\u4eba\u4e58\u4ed6\u5fc3\u795e\u5206\u6563\uff0c\u53cc\u638c\u4e00\u63a8\uff0c\u6ed1\u4e86\u51fa\u53bb\uff0c\u7bf7\u7684\u4e00\u62f3\uff0c\u51fb\u5728\u6bd2\u9f99\u5c0a \u8005\u80a9\u4e0a\u3002\u6bd2\u9f99\u5c0a\u8005\u5927\u6012\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u4e5f\u51fb\u4e2d\u6606\u7538\u4e0a\u4eba\u5c0f\u8179\uff0c\u4e24\u4eba\u90fd\u5927\u53eb\u4e00\u58f0\uff0c\u5012\u7eb5\u4e08\u8bb8\uff0c\u5728 \u53eb\u558a\u58f0\u4e2d\uff0c\u53ea\u89c1\u4e00\u68f5\u5927\u6811\u7bf7\u7136\u5012\u4e0b\uff0c\u80cc\u540e\u7684\u536b\u58eb\u7eb7\u7eb7\u8eb2\u907f\u3002\u539f\u6765\u6606\u7538\u4e0a\u4eba\u7528\u5de7\u8ba1\u907f\u5f00\u4e86\u6bd2\u9f99 \u5c0a\u8005\u7684\u5185\u5bb6\u771f\u529b\uff0c\u90a3\u80a1\u529b\u91cf\u5374\u628a\u4e00\u68f5\u5927\u6811\u6298\u65ad\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u90fd\u662f\u4fee\u7ec3\u5f97\u94a2\u94c1\u822c\u7684\u8eab\u8eaf\uff0c\u5f7c\u6b64\u6371\u4e86\u5bf9\u65b9\u4e00\u4e0b\uff0c\u867d\u7136\u75bc\u75db\uff0c\u5374\u662f\u65e0\u4e8b\u3002\u6bd2\u9f99\u5c0a\u8005\u6124 \u6012\u4e4b\u6781\uff0c\u4e00\u8dc3\u5411\u524d\uff0c\u6402\u5934\u4fbf\u6293\u3002\u6606\u7538\u4e0d\u6562\u518d\u4ee5\u771f\u529b\u548c\u4ed6\u76f8\u62fc\uff0c\u4f4e\u5934\u4e00\u95ea\uff0c\u7a81\u7136\u56f4\u7740\u6bd2\u9f99\u5c0a\u8005 \u7684\u8eab\u8eaf\uff0c\u5927\u8f6c\u5708\u5708\u3002\u970e\u5ffd\u4e4b\u95f4\uff0c\u53ea\u89c1\u6ee1\u573a\u90fd\u662f\u6606\u7538\u4e0a\u4eba\u7684\u5f71\u5b50\u3002 \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u4e0d\u66fe\u89c1\u8fc7\u8fd9\u6837\u7684\u6218\u6cd5\uff0c\u7ed9\u4ed6\u8f6c\u5f97\u773c\u82b1\u7f2d\u4e71\u3002\u6606\u7538\u4e0a\u4eba\u8fd9\u4e00\u624b\u4e43\u662f\u897f\u85cf\u7ea2\u6559\u7684 \u201c\u8ff7\u8e2a\u638c\u6cd5\u201d\uff0c\u77bb\u4e4b\u5728\u524d\uff0c\u5ffd\u7109\u5728\u540e\uff0c\u6559\u654c\u4eba\u6478\u4e0d\u7740\u81ea\u5df1\u7684\u8e2a\u8ff9\uff0c\u6240\u4ee5\u540d\u4e3a\u201c\u8ff7\u8e2a\u201d\u3002\u8fd9\u79cd \u638c\u6cd5\uff0c\u82e5\u7136\u78b0\u7740\u5415\u56db\u5a18\u7b49\u8f7b\u529f\u7edd\u9876\u4e4b\u4eba\uff0c\u90a3\u662f\u65e0\u6240\u65bd\u5176\u4f0e\u4fe9\u3002\u504f\u504f\u6bd2\u9f99\u5c0a\u8005\uff0c\u8f7b\u529f\u5e76\u975e\u7279\u522b \u64c5\u957f\uff0c\u52a0\u4ee5\u957f\u5904\u6d77\u5c9b\uff0c\u5bf9\u654c\u7684\u7ecf\u9a8c\u4ea6\u4e0d\u4e30\u5bcc\uff0c\u53ea\u80fd\u9760\u7740\u529f\u529b\u6df1\u539a\uff0c\u786c\u6253\u786c\u62fc\u3002\u8fd9\u4e00\u6765\u5374\u7740\u4e86 \u6606\u7538\u4e0a\u4eba\u7684\u9053\u513f\uff0c\u6bd2\u9f99\u5c0a\u8005\u7684\u8eab\u5f62\u8ddf\u4ed6\u8f6c\u4e86\u51e0\u8f6c\uff0c\u4e0d\u89c9\u6709\u4e86\u660f\u7729\u4e4b\u611f\uff0c\u7adf\u7136\u53c8\u6371\u4e86\u6606\u7538\u4e0a\u4eba \u4e24\u638c\uff0c\u867d\u7136\u80fd\u591f\u62b5\u6321\uff0c\u5374\u4e5f\u75db\u5f97\u54c7\u54c7\u5927\u53eb\u3002 \u3000\u3000\u989d\u97f3\u548c\u5e03\u7b49\u9f50\u58f0\u559d\u91c7\uff0c\u9f13\u638c\u52a9\u5a01\uff0c\u5415\u56db\u5a18\u9f13\u8d77\u7709\u5934\uff0c\u770b\u4e86\u4e00\u9635\uff0c\u7a81\u7136\u53eb\u9053\uff1a\u201c\u4ee5\u9759\u5236 \u52a8\uff0c\u53cd\u4e3b\u4e3a\u5974\uff01\u201d \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u6b63\u88ab\u6606\u7538\u4e0a\u4eba\u6253\u5f97\u660f\u5934\u660f\u8111\uff0c\u5f97\u5415\u56db\u5a18\u4e00\u8a00\u70b9\u9192\uff0c\u5982\u62e8\u4e91\u96fe\u3002\u5fc3\u9053\uff1a\u201c\u662f\u5440\uff0c \u6211\u4f55\u5fc5\u8ddf\u4ed6\u56e2\u56e2\u4e71\u8f6e\uff1f\u201d\u6218\u6cd5\u4e00\u53d8\uff0c\u5140\u7acb\u5982\u5c71\uff0c\u575a\u5b88\u4e0d\u52a8\uff0c\u4e00\u638c\u5728\u524d\uff0c\u4e00\u638c\u5728\u540e\uff0c\u6606\u7538\u4e0a\u4eba \u8eab\u5f62\u4e00\u8fd1\uff0c\u6bd2\u9f99\u5c0a\u8005\u4fbf\u8fd0\u5185\u529b\u7ed9\u4ed6\u4e00\u638c\u3002\u6bd2\u9f99\u5c0a\u8005\u6b66\u529f\u5df2\u5230\u5316\u5883\uff0c\u51dd\u795e\u5f85\u654c\uff0c\u7acb\u523b\u6062\u590d\u4e86\u8033 \u806a\u76ee\u660e\uff0c\u4efb\u6606\u7538\u4e0a\u4eba\u7684\u8ff7\u8e2a\u62f3\u6cd5\u5982\u4f55\u6e9c\u6ed1\uff0c\u4ed6\u90fd\u80fd\u6ce8\u5b9a\u4e86\u654c\u4eba\u7684\u8eab\u5f62\uff0c\u89c1\u673a\u53d1\u638c\u3002\u4e0e\u5148\u524d\u7684 \u8ff7\u4e71\u5927\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u6606\u7538\u4e0a\u4eba\u5185\u529b\u672c\u6765\u5c31\u4e0d\u53ca\u6bd2\u9f99\u5c0a\u8005\uff0c\u8ff7\u8e2a\u638c\u6cd5\u65e0\u6548\uff0c\u987f\u65f6\u81ea\u5df1\u53cd\u611f\u5403\u529b\u8d77\u6765\u3002\u6bd2\u9f99\u5c0a\u8005 \u4e00\u638c\u7d27\u4f3c\u4e00\u638c\uff0c\u6bcf\u4e00\u638c\u90fd\u5939\u7740\u6781\u5927\u7684\u5a01\u529b\uff0c\u6606\u7538\u4e0a\u4eba\u903c\u4e0d\u8fdb\u53bb\uff0c\u88ab\u4ed6\u638c\u529b\u6240\u538b\uff0c\u547c\u5438\u6e10\u4fc3\uff0c \u6697\u6697\u53eb\u58f0\u4e0d\u597d\uff0c\u5982\u6b64\u5bf9\u8017\u4e0b\u53bb\uff0c\u654c\u672a\u8d25\u5df1\u5148\u8d25\uff0c\u7edd\u5bf9\u5360\u4e0d\u4e86\u4fbf\u5b9c\u3002 \u3000\u3000\u6606\u7538\u4e0a\u4eba\u7709\u5934\u4e00\u76b1\uff0c\u638c\u6cd5\u53c8\u53d8\uff0c\u5de6\u638c\u64d2\u62ff\uff0c\u53f3\u638c\u201c\u62cd\u7a74\u201d\uff0c\u8eab\u5b50\u4ecd\u662f\u56e2\u56e2\u4e71\u8f6c\uff0c\u53ef\u662f\u5374 \u5728\u4e58\u6687\u8e48\u9699\uff0c\u4ee5\u64d2\u62ff\u624b\u6765\u5316\u89e3\u654c\u4eba\u7684\u653b\u52bf\uff0c\u4ee5\u201c\u62cd\u7a74\u201d\u6cd5\u6765\u5a01\u80c1\u5f3a\u654c\u3002\u6606\u7538\u4e0a\u4eba\u4e43\u897f\u85cf\u7ea2\u6559 \u4e2d\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u786e\u6709\u51e0\u79cd\u72ec\u95e8\u6b66\u529f\uff0c\u6bd2\u9f99\u5c0a\u8005\u867d\u7136\u5185\u5916\u529f\u592b\u90fd\u767b\u5cf0\u9053\u6781\uff0c\u4f46\u51fa\u5947\u5236\u80dc\u7684\u65c1\u95e8 \u5de6\u9053\u529f\u592b\u5374\u662f\u8fdc\u8fdc\u4e0d\u5982\u6606\u7538\u4e0a\u4eba\uff0c\u4ed6\u89c1\u6606\u7538\u4e0a\u4eba\u6309\u6253\u62cd\u6324\uff0c\u638c\u638c\u4e0d\u79bb\u81ea\u5df1\u7a74\u9053\u8981\u5bb3\uff0c\u4e5f\u5403\u4e86 \u4e00\u60ca\uff0c\u4e00\u9762\u8fd0\u6c14\u95ed\u7a74\uff0c\u4e00\u9762\u575a\u5b88\u62d2\u654c\uff0c\u4e24\u65b9\u517c\u987e\uff0c\u638c\u529b\u5a01\u52bf\u6e10\u5f31\u4e0b\u6765\u3002\u6606\u7538\u4e0a\u4eba\u7740\u7740\u62a2\u653b\uff0c \u4e00\u5b88\u4e00\u653b\uff0c\u53c8\u6210\u4e86\u65d7\u9f13\u76f8\u5f53\u4e4b\u5c40\u3002 \u3000\u3000\u5510\u6653\u6f9c\u770b\u5f97\u5fc3\u6025\uff0c\u4e0e\u5415\u56db\u5a18\u8c08\u8bba\u9053\uff1a\u201c\u6bd2\u9f99\u5c0a\u8005\u7684\u771f\u529f\u592b\u80dc\u8fc7\u8fd9\u4e2a\u5996\u50e7\uff0c\u5374\u53cd\u800c\u4e3a\u5996\u50e7 \u6240\u5236\uff0c\u771f\u662f\u83ab\u540d\u5176\u5999\u3002\u201d\u51af\u7433\u63d2\u53e3\u9053\uff1a\u201c\u90a3\u5996\u50e7\u7528\u7684\u662f\u62cd\u7a74\u7684\u529f\u592b\uff0c\u4e5f\u5f88\u5389\u5bb3\u5440\u3002\u6bd2\u9f99\u5c0a\u8005 \u4e0d\u77e5\u5e94\u4ed8\u3002\u4e5f\u80fd\u548c\u4ed6\u6253\u4e2a\u5e73\u624b\uff0c\u5df2\u5f88\u4e0d\u9519\u4e86\u3002\u201d\u5510\u6653\u6f9c\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u89c1\u4ed6\u7684\u638c\u529b\u5df2 \u6e10\u6e10\u51cf\u5f31\u4e86\u4e48\uff1f\u4e45\u6218\u4e0b\u53bb\uff0c\u53ea\u6050\u8981\u7cdf\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u89c1\u5f97\u3002\u6bd2\u9f99\u5c0a\u8005\u867d\u56e0\u5206\u795e\u517c \u987e\uff0c\u638c\u529b\u51cf\u5f31\uff1b\u4f46\u90a3\u5996\u50e7\u4e5f\u56e0\u540c\u65f6\u8981\u65bd\u5c55\u4e09\u79cd\u6b66\u529f\uff0c\u4ed6\u7684\u8ff7\u8e2a\u638c\u6cd5\u4e5f\u5df2\u4e2a\u5927\u5982\u5148\u524d\u7684\u6e9c\u6ed1 \u4e86\u3002\u5f7c\u6b64\u76f8\u6d88\uff0c\u5c0a\u8005\u4e5f\u4e0d\u89c1\u5f97\u4f1a\u8f93\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u7b49\u8c08\u8bdd\u58f0\u97f3\u867d\u7136\u4e0d\u5927\uff0c\u6bd2\u9f99\u5c0a\u8005\u5374\u662f\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u6697\u91cc\u7559\u795e\uff0c\u5fc3\u91cc\u4e00\u4eae\u3002\u638c\u6cd5 \u4e00\u53d8\uff0c\u811a\u8e0f\u597d\u884c\u516b\u5366\u7684\u65b9\u4f4d\uff0c\u7a81\u7136\u7740\u7740\u62a2\u653b\u3002\u6606\u7538\u4e0a\u4eba\u672c\u6765\u662f\u4ee5\u201c\u64d2\u62ff\u624b\u201d\u3001\u201c\u62cd\u7a74\u6cd5\u201d\u914d \u4e0a\u201c\u8ff7\u8e2a\u638c\u201d\u7684\u98d8\u5ffd\u8eab\u6e4d\uff0c\u4e09\u8005\u5408\u7528\u6765\u514b\u5236\u6bd2\u9f99\u5c0a\u8005\uff0c\u4f46\u6bd2\u9f99\u5c0a\u8005\u5185\u529f\u6bd4\u4ed6\u4e3a\u9ad8\uff0c\u201c\u62cd\u7a74 \u6cd5\u201d\u53ea\u80fd\u6536\u5a01\u80c1\u4e4b\u529f\uff0c\u800c\u4e0d\u80fd\u81f4\u654c\u6b7b\u547d\uff1b\u64d2\u62ff\u624b\u4ec5\u53ef\u5e94\u4ed8\u6bd2\u9f99\u5c0a\u8005\u7684\u6bd2\u9f99\u638c\uff0c\u4e5f\u4e0d\u80fd\u53d6\u80dc\u3002 \u5982\u4eca\u201c\u8ff7\u8e2a\u638c\u201d\u7684\u8eab\u6cd5\u53c8\u88ab\u6bd2\u9f99\u5c0a\u8005\u770b\u7834\uff0c\u6bd2\u9f99\u5c0a\u8005\u65e2\u4e0d\u575a\u5b88\uff0c\u4ea6\u4e0d\u4e71\u653b\uff0c\u8e0f\u7a33\u4e94\u884c\u516b\u5366\u65b9 \u4f4d\uff0c\u5fc3\u795e\u81ea\u4e0d\u8ff7\u4e71\u3002\u8fd9\u4e00\u6765\u6bd2\u9f99\u5c0a\u8005\u5a01\u529b\u5927\u589e\uff0c\u6253\u6765\u5934\u5934\u662f\u9053\u3002\u53ea\u89c1\u4ed6\u8e0f\u201c\u574e\u201d\u4f4d\uff0c\u8f6c \u201c\u79bb\u201d\u65b9\uff0c\u547c\u7684\u4e00\u638c\u6a2a\u626b\uff0c\u6606\u7538\u4e0a\u4eba\u6643\u4e86\u4e24\u6643\uff0c\u8f6c\u4e86\u534a\u5708\uff0c\u53cd\u624b\u62cd\u654c\u4eba\u7684\u201c\u671f\u95e8\u7a74\u201d\uff0c\u6bd2\u9f99 \u5c0a\u8005\u5411\u201c\u79bb\u201d\u65b9\u4e00\u8dc3\u800c\u81f3\u201c\u826e\u201d\u4f4d\uff0c\u53cc\u638c\u5408\u62e2\uff0c\u5de6\u53f3\u4e00\u5206\uff0c\u559d\u58f0\u201c\u7740\uff01\u201d\u8fd9\u4e00\u62db\u540d\u4e3a\u201c\u53cc\u9f99 \u5165\u6d77\u201d\uff0c\u6b63\u662f\u6bd2\u9f99\u5c0a\u8005\u7684\u6740\u624b\u7edd\u62db\uff01 \u3000\u3000\u62f3\u98ce\u638c\u5f71\u4e4b\u4e2d\u6606\u7538\u4e0a\u4eba\u5927\u53eb\u4e00\u58f0\uff0c\u5510\u6653\u6f9c\u6b63\u6b32\u559d\u91c7\uff0c\u5374\u4e0d\u6599\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u4e4b\u95f4\uff0c\u7d27\u63a5 \u7740\u53c8\u662f\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u6bd2\u9f99\u5c0a\u8005\u6367\u8179\u5f2f\u8170\uff0c\u6447\u6447\u6b32\u5012\u3002 \u3000\u3000\u5415\u56db\u5a18\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d \u3000\u3000\u51af\u7433\u5c16\u53eb\u9053\uff1a\u201c\u86c7\uff0c\u86c7\uff01\u201d\u731b\u7136\u95f4\u6606\u7538\u4e0a\u4eba\u53c8\u5927\u53eb\u4e00\u58f0\uff0c\u6bd2\u9f99\u5c0a\u8005\u7a81\u7136\u633a\u8170\u524d\u6251\uff0c\u4e00\u62f3 \u5c06\u4ed6\u6253\u5012\uff01 \u3000\u3000\u539f\u6765\u6606\u7538\u4e0a\u4eba\u60c5\u77e5\u65e0\u6cd5\u907f\u5f00\u654c\u4eba\u8fd9\u53cc\u638c\u7684\u731b\u51fb\uff0c\u5fc3\u5934\u4e00\u6a2a\uff0c\u62fc\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u4e00\u8f6c\u8eab\u4ee5\u80a9 \u80cc\u786c\u63a5\u4e86\u4e24\u638c\uff0c\u53cd\u624b\u4e00\u62cd\uff0c\u5374\u4e5f\u62cd\u4e2d\u4e86\u6bd2\u9f99\u5c0a\u8005\u524d\u80f8\u7684\u201c\u5f53\u95e8\u7a74\u201d\u3002\u8fd9\u201c\u5f53\u95e8\u7a74\u201d\u4e43\u662f\u4eba\u8eab \u6b7b\u7a74\u4e4b\u4e00\uff0c\u6606\u7538\u4e0a\u4eba\u6599\u4ed6\u4e0d\u6b7b\u4ea6\u5fc5\u91cd\u4f24\u3002 \u3000\u3000\u54ea\u77e5\u6bd2\u9f99\u5c0a\u8005\u6000\u4e2d\u85cf\u6709\u4e00\u4e2a\u94c1\u7ba1\uff0c\u94c1\u7ba1\u4e2d\u517b\u7740\u4e24\u6761\u6700\u6bd2\u7684\u86c7\uff0c\u8fd9\u4e24\u6761\u86c7\u662f\u6bd2\u9f99\u5c0a\u8005\u7528\u6765 \u533b\u6cbb\u9ebb\u75af\u75c5\u4eba\u7684\u3002\u6606\u7538\u4e0a\u4eba\u4e00\u638c\u62cd\u4e0b\uff0c\u94c1\u7ba1\u788e\u88c2\uff0c\u6bd2\u86c7\u98de\u51fa\uff0c\u7adf\u7136\u5728\u6606\u7538\u4e0a\u4eba\u5de6\u53f3\u7709\u5c16\u54ac\u4e86 \u4e00\u53e3\u3002\u6606\u7538\u4e0a\u4eba\u867d\u7136\u7acb\u523b\u5c06\u4e24\u6761\u6bd2\u86c7\u634f\u6b7b\uff0c\u53ef\u662f\u5728\u8eab\u53d7\u5185\u529b\u9707\u4f24\u4e4b\u540e\uff0c\u590d\u53d7\u86c7\u54ac\uff0c\u4efb\u662f\u94c1\u94f8 \u8eab\u8eaf\u4e5f\u96be\u62b5\u654c\uff0c\u987f\u65f6\u95f4\u773c\u524d\u91d1\u661f\u4e71\u5192\uff0c\u4e0d\u8fa8\u4e1c\u897f\u3002\u6bd2\u9f99\u5c0a\u8005\u65e9\u5df2\u8fd0\u6c14\u62a4\u7a74\uff0c\u53c8\u5f97\u94c1\u7ba1\u66ff\u4ed6\u4e00 \u6863\uff0c\u53d7\u4f24\u4e0d\u91cd\u3002\u4ed6\u63d0\u4e86\u4e00\u53e3\u6c14\uff0c\u594b\u529b\u4e00\u62f3\uff0c\u7acb\u523b\u5c06\u6606\u7538\u4e0a\u4eba\u989a\u9aa8\u6253\u788e\uff0c\u5012\u6bd9\u5730\u4e0b\u3002 \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u4fef\u8eab\u4e00\u770b\uff0c\u89c1\u4e24\u6761\u6bd2\u86c7\u5df2\u6b7b\uff0c\u52c3\u7136\u5927\u6012\uff0c\u968f\u624b\u4e00\u62d4\uff0c\u62d4\u8d77\u4e00\u6839\u5927\u6811\uff0c\u4fbf\u5411\u97e9\u91cd \u5c71\u7b49\u4eba\u731b\u626b\u3002\u97e9\u91cd\u5c71\u7b49\u4eba\u89c1\u6606\u7538\u4e0a\u4eba\u88ab\u4ed6\u51fb\u6bd9\uff0c\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u6025\u6025\u5954\u9003\u3002\u53ea\u82e6 \u5728\u90a3\u4e9b\u672c\u4e8b\u4f4e\u5fae\u7684\u536b\u58eb\uff0c\u88ab\u6bd2\u9f99\u5c0a\u8005\u4e00\u9635\u731b\u626b\uff0c\u7ffb\u7ffb\u6eda\u6eda\uff0c\u6eda\u4e0b\u5c71\u5761\uff0c\u60e8\u53f7\u72c2\u55e5\uff0c\u58f0\u9707\u5c71 \u8c37\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u5c0a\u8005\uff0c\u9976\u4e86\u4ed6\u4eec\u5427\uff01\u201d\u6bd2\u9f99\u5c0a\u8005\u5bf9\u5415\u56db\u5a18\u6700\u4e3a\u656c\u670d\uff0c\u95fb\u8a00\u7acb\u523b\u6b62\u624b\uff0c\u6454\u4e86 \u5927\u6811\uff0c\u5f2f\u8170\u5411\u5979\u81f4\u656c\u3002 \u3000\u3000\u5415\u56db\u5a18\u8eac\u8eab\u9053\uff1a\u201c\u5c0a\u8005\u4e0d\u5fc5\u591a\u793c\u3002\u201d\u6bd2\u9f99\u5c0a\u8005\u6ef4\u6cea\u9053\uff1a\u201c\u6211\u4ee5\u524d\u72ec\u5c45\u86c7\u5c9b\uff0c\u4ec7\u6068\u4e16\u4eba\uff0c \u82e5\u4e0d\u662f\u5f97\u4f60\u548c\u7518\u5927\u4fa0\u70b9\u9192\uff0c\u5c1a\u4e0d\u77e5\u8981\u9020\u591a\u5c11\u51a4\u5b7d\u3002\u751f\u6211\u8005\u7236\u6bcd\uff0c\u77e5\u6211\u8005\u826f\u53cb\uff0c\u6211\u5bf9\u4f60\u4eec\u7684\u6069 \u60c5\u5b9e\u662f\u65e0\u53ef\u62a5\u7b54\u3002\u201d\u51af\u745b\u7b11\u9053\uff1a\u201c\u4f60\u66ff\u6211\u4eec\u6253\u6b7b\u4e86\u8fd9\u4e2a\u5996\u50e7\uff0c\u6211\u4eec\u4e5f\u662f\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d\u51af\u745b\u51af \u7433\u5e76\u6392\u7ad9\u7acb\uff0c\u6bd2\u9f99\u5c0a\u8005\u53d1\u73b0\u5979\u4eec\u76f8\u8c8c\u4e00\u6837\uff0c\u9887\u4e3a\u8be7\u5f02\u3002\u51af\u745b\u7b11\u7b11\u9053\uff1a\u201c\u5979\u662f\u6211\u7684\u59b9\u59b9\u3002\u201d\u51af \u7433\u597d\u5947\u95ee\u9053\uff1a\u201c\u5c0a\u8005\uff0c\u4f60\u90a3\u4e24\u6761\u6bd2\u86c7\u5982\u6b64\u5389\u5bb3\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u4e9b\u653e\u5b83\u4eec\u51fa\u6765\uff0c\u4e0d\u662f\u53ef\u4ee5\u7701\u8bb8\u591a \u624b\u811a\u5417\uff1f\u201d \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u9762\u8272\u500f\u53d8\uff0c\u5ffd\u800c\u53f9\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u6211\u5f97\u4e86\u5415\u5973\u4fa0\u7684\u6559\u8bad\uff0c\u53d1\u8a93\u4e0d\u518d\u5c06\u6240\u517b\u7684\u6bd2\u86c7 \u5bb3\u4eba\uff0c\u53ea\u7528\u5b83\u4eec\u6551\u4eba\uff0c\u90a3\u53ae\u6253\u788e\u94c1\u7ba1\uff0c\u6fc0\u6012\u5b83\u4eec\uff0c\u4e43\u662f\u81ea\u5bfb\u6b7b\u8def\uff0c\u90a3\u53ae\u6b7b\u4e0d\u8db3\u60dc\uff0c\u53ea\u53ef\u60dc\u6211 \u8fd9\u4e24\u4e2a\u6700\u597d\u7684\u5e2e\u624b\uff0c\u7adf\u4e5f\u6b7b\u4e8e\u975e\u547d\u3002\u201d\u51af\u7433\u53c8\u597d\u5947\u95ee\u9053\uff1a\u201c\u600e\u4e48\u5b83\u4eec\u4f1a\u662f\u4f60\u6700\u597d\u7684\u5e2e\u624b\uff1f\u201d \u6bd2\u9f99\u5c0a\u8005\u7eed\u9053\uff1a\u201c\u6211\u5f97\u4e86\u5415\u4fa0\u5973\u7684\u6559\u5b66\uff0c\u8fd9\u4e00\u5e74\u6765\u4e5f\u6551\u4e86\u4e0d\u5c11\u9ebb\u75af\u75c5\u4eba\u3002\u8d77\u521d\u6211\u672c\u60f3\u628a\u75c5\u4eba \u625b\u56de\u86c7\u5c9b\u7684\uff0c\u540e\u6765\u60f3\u8d77\u8fd9\u592a\u8d39\u4e8b\u3002\u56e0\u6b64\u5e26\u4e86\u8fd9\u4e24\u4e2a\u52a9\u624b\u51fa\u6765\uff0c\u6bcf\u5f53\u533b\u6cbb\u75c5\u4eba\uff0c\u5c31\u5c06\u5b83\u4eec\u7684\u8840 \u523a\u51fa\u6765\u5e94\u7528\uff0c\u7136\u540e\u7528\u836f\u5582\u5b83\uff0c\u5728\u51e0\u5929\u4e4b\u5185\uff0c\u4fbf\u80fd\u6062\u590d\u3002\u201d\u51af\u745b\u542c\u4e86\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u8d77\u4e86\u611f\u6168\u3002 \u3000\u3000\u51af\u745b\u60f3\u8d77\u5f53\u65e5\u5728\u86c7\u5c9b\u4e4b\u65f6\uff0c\u5415\u56db\u5a18\u7528\u9c7c\u4eae\u7684\u5343\u5e74\u829d\u8349\u6551\u6bd2\u9f99\u5c0a\u8005\uff0c\u800c\u90a3\u829d\u8349\u5374\u662f\u81ea\u5df1\u60f3 \u6c42\u6765\u6551\u5510\u6653\u6f9c\u7684\uff0c\u5f53\u65f6\u5f97\u77e5\u4e4b\u540e\uff0c\u5fc3\u4e2d\u9887\u4e0d\u8212\u670d\u3002\u5982\u4eca\u770b\u6765\uff0c\u5415\u59d0\u59d0\u786e\u662f\u6551\u5f97\u5bf9\u4e86\u3002\u6551\u4ed6\u4e00 \u4eba\uff0c\u4e5f\u5c31\u6551\u4e86\u5343\u767e\u7684\u9ebb\u75af\u75c5\u4eba\u3002\u81ea\u6b64\u5bf9\u4e8e\u201c\u4fa0\u4e49\u201d\u4e24\u5b57\uff0c\u8ba4\u8bc6\u53c8\u6df1\u5165\u4e00\u5c42\u3002 \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u53c8\u9053\uff1a\u201c\u4e16\u4e0a\u7684\u9ebb\u75af\u75c5\u867d\u7136\u4e0d\u5c11\uff0c\u4f46\u4e5f\u5e76\u4e0d\u5f88\u591a\uff0c\u5927\u7ea6\u6bcf\u53bf\u4e5f\u4e0d\u8fc7\u5341\u4e2a\u516b\u4e2a\u3002 \u6211\u5e26\u8fd9\u4e24\u4e2a\u52a9\u624b\u968f\u884c\uff0c\u5c3d\u53ef\u591f\u7528\u3002\u53ef\u60dc\u5b83\u4eec\u5374\u6b7b\u5728\u90a3\u53ae\u624b\u4e0b\u3002\u201d\u674e\u6cbb\u95ee\u9053\uff1a\u201c\u666e\u901a\u7684\u6bd2\u86c7\u53ef \u7528\u5417\uff1f\u9664\u4e86\u86c7\u8840\u4e4b\u5916\u8fd8\u8981\u4ec0\u4e48\u836f\uff0c\u600e\u4e48\u7528\u6cd5\uff1f\u201d \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u9053\uff1a\u201c\u666e\u901a\u6bd2\u86c7\u867d\u7136\u53ef\u7528\uff0c\u529f\u6548\u5374\u8fdc\u4e0d\u53ca\u6211\u8fd9\u4e24\u6761\u6bd2\u86c7\uff0c\u800c\u4e14\u6211\u4e5f\u4e0d\u8010\u70e6\u904d\u5c71 \u904d\u91ce\u5bfb\u627e\u6bd2\u86c7\u3002\u201d\u5f53\u4e0b\u53c8\u5c06\u5176\u4ed6\u7684\u836f\u7269\u4e0e\u7528\u6cd5\u8bf4\u4e86\uff0c\u674e\u6cbb\u7d27\u8bb0\u5728\u5fc3\u3002\u9664\u4e86\u201c\u907f\u971c\u82b1\u201d\u4e00\u5473\u53ea \u4ea7\u4e8e\u86c7\u5c9b\u4e4b\u5916\uff0c\u5176\u4ed6\u836f\u7269\uff0c\u5374\u4e5f\u5e76\u975e\u96be\u5f97\uff0c\u800c\u8fd9\u4e00\u5473\u836f\u60f3\u6765\u8fd8\u53ef\u7528\u5176\u4ed6\u4e1c\u897f\u4ee3\u66ff\u3002 \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u53c8\u9053\uff1a\u201c\u6211\u7684\u52a9\u624b\u6b7b\u4e86\uff0c\u6211\u8fd8\u8981\u56de\u86c7\u5c9b\u4e00\u6b21\uff0c\u5e26\u66f4\u591a\u7684\u52a9\u624b\u4e0e\u836f\u7269\u6765\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u5408\u4ec0\u9053\uff1a\u201c\u5584\u54c9\uff0c\u5584\u54c9\uff01\u5c0a\u8005\u4e00\u5ff5\u4e4b\u8f6c\uff0c\u5927\u76ca\u4e16\u4eba\u3002\u201d \u3000\u3000\u6bd2\u9f99\u5c0a\u8005\u9053\uff1a\u201c\u90a3\u90fd\u662f\u4f60\u6307\u70b9\u4e4b\u529f\u3002\u201d\u60f3\u8d77\u8fd8\u8981\u9001\u4e24\u4e2a\u9ebb\u75af\u75c5\u4eba\u56de\u5bb6\uff0c\u4fbf\u5411\u5415\u56db\u5a18\u544a\u8f9e \u9053\uff1a\u201c\u6211\u4e0d\u61c2\u8bf4\u5ba2\u5957\u8bdd\uff0c\u6211\u53ea\u80fd\u5c3d\u91cf\u6cbb\u75c5\u4eba\u6765\u62a5\u7b54\u4f60\u4eec\u5f53\u65e5\u4e0d\u6740\u4e4b\u6069\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u70b9\u5934\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u6700\u597d\uff01\u201d\u4f17\u4eba\u76ee\u9001\u6bd2\u9f99\u5c0a\u8005\u80cc\u8d77\u4e24\u4e2a\u9ebb\u75af\u60a3\u8005\u4e0b\u5c71\uff0c\u65e0\u4e0d\u8d5e \u53f9\u3002 \u3000\u3000\u7518\u51e4\u6c60\u95ee\u8d77\u5415\u56db\u5a18\u4f55\u6545\u4f1a\u53d7\u56f4\u653b\uff1f \u3000\u3000\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u6b63\u6709\u4e8b\u8bf7\u4e03\u54e5\u66ff\u6211\u4e00\u65ad\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u8be7\u9053\uff1a\u201c\u516b\u59b9\u806a\u660e\u8fc7\u4eba\uff0c\u611a\u5144\u8fdc\u8fdc\u4e0d\u53ca\uff0c\u4e0d\u77e5\u516b\u59b9\u6709\u4f55\u4e8b\u4e0d\u80fd\u51b3\u65ad\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u5c06\u51e0\u65e5\u6765\u7684\u4e8b\u8bf4\u4e86\uff0c\u9053\uff1a\u201c\u6211\u5c31\u65ad\u4e0d\u5b9a\u90a3\u6c88\u5728\u5bbd\u7684\u5934\u9885\u5230\u5e95\u662f\u771f\u662f\u5047\uff1f\u4f60\u8bf4\u6211\u597d \u4e0d\u597d\u8d76\u56de\u4ed9\u971e\u5cad\u53bb\u770b\u770b\uff1f\u201d\u8fd9\u6b63\u662f\u4efd\u5c5e\u81f3\u4eb2\uff0c\u5173\u5fc3\u8005\u4e71\u3002\u4ee5\u5415\u56db\u5a18\u90a3\u6837\u7684\u6c89\u7740\u51b7\u9759\uff0c\u4e5f\u7ed9\u8fd9 \u4ef6\u4e8b\u5f04\u5f97\u5fc3\u795e\u4e0d\u5b81\u3002 \u3000\u3000\u7518\u51e4\u6c60\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u516b\u59b9\u4e5f\u4e0d\u80fd\u5224\u65ad\u771f\u5047\uff0c\u6211\u66f4\u4e0d\u80fd\u5224\u65ad\u4e86\u3002\u8fd9\u51e0\u65e5\u6211\u4eec\u5fd9\u4e8e\u6551\u6653 \u6f9c\uff0c\u60f3\u4e0d\u5230\u4eac\u4e2d\u53d1\u751f\u5982\u6b64\u5927\u4e8b\u3002\u4e0d\u8fc7\u8fd9\u4ef6\u4e8b\u65e2\u8f70\u52a8\u56db\u65b9\uff0c\u5fc5\u6709\u7ebf\u7d22\uff0c\u4e0d\u5982\u54b1\u4eec\u56de\u5230\u897f\u5c71\u4e4b \u540e\uff0c\u518d\u884c\u67e5\u95ee\u3002\u4f60\u73b0\u5728\u56de\u8f6c\u4ed9\u971e\uff0c\u4e5f\u6ca1\u6709\u7528\uff01\u201d\u5415\u56db\u5a18\u4e00\u60f3\uff1a\u82e5\u7136\u5728\u5bbd\u771f\u4e2a\u6b7b\u4e86\uff0c\u56de\u8f6c\u4ed9 \u971e\uff0c\u5f92\u589e\u5fc3\u4f24\uff1b\u82e5\u7136\u672a\u6b7b\uff0c\u90a3\u5c31\u8fdf\u51e0\u65e5\u56de\u53bb\uff0c\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u4fbf\u70b9\u5934\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u7518\u51e4\u6c60\u53c8\u5bf9\u51af\u745b\u59d0\u59b9\u9053\uff1a\u201c\u7ecf\u6b64\u4e00\u6218\uff0c\u516b\u8fbe\u5cad\u5fc5\u4e3a\u671d\u5ef7\u6ce8\u76ee\uff0c\u53ef\u80fd\u6000\u7591\u6211\u4eec\u805a\u96c6\u6b64\u95f4\u3002 \u4ee4\u5802\u4e0e\u4ee4\u5916\u79df\u5c1a\u5728\u5c71\u4e2d\uff0c\u4e0d\u5982\u90fd\u8bf7\u4ed6\u4eec\u5230\u897f\u5c71\u53bb\u3002\u201d\u5510\u6653\u6f9c\u4e5f\u6b32\u4e00\u89c1\u5e08\u5ac2\uff0c\u4fbf\u548c\u51af\u745b\u59d0\u59b9\u540c \u884c\u3002\u51af\u745b\u53c8\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u53ef\u987a\u9053\u81f3\u5357\u53e3\u53bb\u8981\u90a3\u5e9f\u56ed\u8001\u4eba\u7559\u4e0b\u7684\u533b\u6848\uff0c\u5982\u4eca\u65e2\u77e5\u59b9\u59b9\u662f\u5085\u9752\u4e3b \u4e00\u6d3e\u5ae1\u4f20\uff0c\u65e0\u6781\u6d3e\u7684\u4f20\u4eba\u975e\u5979\u83ab\u5c5e\uff0c\u90a3\u533b\u6848\u7406\u5e94\u5f52\u5979\u6240\u6709\u3002\u201d\u51af\u7433\u5411\u674e\u6cbb\u8f97\u7136\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u50bb\u54e5\u54e5\uff0c\u4f60\u73b0\u5728\u4e0d\u4f1a\u56e0\u770b\u4e86\u5085\u9752\u4e3b\u7684\u9057\u4e66\uff0c\u800c\u5fc3\u611f\u4e0d\u5b89\u4e86\u5427\uff01\u5e9f\u56ed\u8001\u4eba\u7684\u533b\u6848\u6211\u4e5f\u4e00\u5e76\u9001 \u7ed9\u4f60\uff0c\u4f60\u62ff\u4ec0\u4e48\u8c22\u6211\uff1f\u201d\u674e\u6cbb\u563b\u563b\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u533b\u6848\u4e0a\u53c8\u589e\u6dfb\u4e00\u4e2a\u533b\u9ebb\u75af\u7684\u65b9\u6848\u4e86\u3002\u6211\u8981\u50cf \u6bd2\u9f99\u5c0a\u8005\u4e00\u6837\uff0c\u5c06\u6765\u591a\u533b\u75c5\u4eba\u8c22\u4f60\u3002\u201d\u8bdd\u521a\u8bf4\u5b8c\uff0c\u5ffd\u89c1\u7518\u51e4\u6c60\u9762\u8272\u4e00\u53d8\u3002 \u3000\u3000\u5415\u56db\u5a18\u51dd\u795e\u4e00\u542c\uff0c\u7a81\u7136\u8df3\u4e0a\u9ad8\u5904\u5f20\u671b\uff0c\u4f17\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8fdc\u5904\u6709\u519b\u9a6c\u884c\u7f8c\uff0c \u96be\u9053\u96cd\u6b63\u8fd9\u5c0f\u5b50\u7adf\u4e3a\u6211\u4eec\u5174\u5e08\u52a8\u4f17\uff1f\u201d \u3000\u3000\u4f17\u4eba\u8dd1\u4e0a\u5c71\u5934\u9065\u671b\uff0c\u53ea\u89c1\u5c71\u811a\u7684\u5b98\u9053\uff0c\u519b\u9a6c\u6392\u6210\u4e24\u5217\uff0c\u671b\u4e0d\u5c3d\u5934\u3002\u65cc\u65d7\u853d\u7a7a\uff0c\u519b\u5bb9\u751a \u76db\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8fd9\u662f\u5927\u519b\uff0c\u671b\u4e4b\u4e0d\u5c3d\uff0c\u60f3\u6765\u4e0d\u662f\u5bf9\u4ed8\u6211\u4eec\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5c71\u8def\u56de \u53bb\u5427\uff0c\u907f\u514d\u548c\u5927\u519b\u78b0\u5934\u3002\u201d\u53c8\u5631\u5490\u51af\u7433\u7b49\u5c0f\u5fc3\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u770b\u6765\u8fd9\u652f\u5927\u519b\u603b\u6709\u51e0\u4e07\uff0c\u6240\u8fc7 \u4e4b\u5904\uff0c\u9644\u8fd1\u5dde\u53bf\u5fc5\u7136\u60ca\u6050\u3002\u4e0d\u8fc7\u5982\u6b64\u4e00\u6765\uff0c\u5730\u65b9\u5b98\u540f\u4e5f\u5fc5\u7136\u90fd\u53bb\u8fce\u63a5\u519b\u961f\uff0c\u4f60\u4eec\u7ed5\u5c71\u8def\u8d70\uff0c \u53cd\u800c\u4e0d\u4f1a\u6709\u4eba\u6ce8\u610f\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u7518\u51e4\u6c60\u4e00\u8def\uff0c\u5510\u6653\u6f9c\u548c\u51af\u745b\u59d0\u59b9\u4e00\u8def\uff0c\u5415\u7518\u4e8c\u4eba\u56de\u5230\u51b7\u7985\u6240\u5c45\u7684\u5bfa\u9662\uff0c\u53ea\u89c1\u5019\u4e09 \u53d8\u9c7c\u4eae\u8bf8\u4eba\u90fd\u5728\u7d27\u5f20\u7b49\u5019\u3002\u4faf\u4e09\u53d8\u4e00\u89c1\u7518\u51e4\u6c60\u56de\u6765\uff0c\u7acb\u523b\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u78b0\u5230\u4e86\u5e74\u7fb9\u5c27\u7684\u5927\u8f66 \u4e48\uff1f\u201d\u9c7c\u4eae\u4e5f\u95ee\u9053\uff1a\u201c\u51af\u745b\u59d0\u59b9\u5462\uff1f\u88ab\u5e74\u7fb9\u5c27\u6349\u53bb\u4e86\u4e48\uff1f\u201d\u9c7c\u4eae\u751a\u6b22\u559c\u8fd9\u4e24\u59d0\u59b9\uff0c\u4e0d\u89c1\u5979\u4eec \u540c\u56de\uff0c\u751a\u662f\u5fe7\u8651\u3002\u5415\u56db\u5a18\u9053\uff1a\u201c\u5979\u4eec\u53bb\u63a5\u6bcd\u4eb2\u4e86\u3002\u201d\u7518\u51e4\u6c60\u542c\u8bf4\u662f\u5e74\u7fb9\u5c27\u7684\u5927\u519b\uff0c\u6115\u7136\u8bf4 \u9053\uff1a\u201c\u539f\u6765\u662f\u4ed6\uff0c\u4ed6\u5e26\u8fd9\u4e48\u591a\u519b\u9a6c\u56de\u6765\u4f5c\u751a\uff1f\u201d \u3000\u3000\u4faf\u4e09\u53d8\u662f\u524d\u671d\u7684\u8001\u536b\u58eb\uff0c\u6d88\u606f\u751a\u7075\uff0c\u7b54\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u8fd9\u5c0f\u5b50\u5b98\u8fd0\u81ea\u901a\uff0c\u5c45\u7136\u5728\u4e00\u5e74\u4e4b \u95f4\uff0c\u5c06\u9752\u6d77\u4e4b\u4e71\u5e73\u4e86\u3002\u7687\u5e1d\u5c01\u4ed6\u4e3a\u2018\u4e00\u7b49\u516c\u2019\uff0c\u9664\u4e86\u5e1d\u738b\u82d7\u88d4\u529b\u5916\uff0c\u5f02\u59d3\u4e0d\u80fd\u5c01\u738b\uff0c\u56e0\u6b64 \u2018\u4e00\u7b49\u516c\u2019\u8fd9\u5c01\u53f7\u5bf9\u6c49\u4eba\u6765\u8bf4\uff0c\u662f\u6700\u5c0a\u8363\u7684\u4e86\u3002\u7687\u5e1d\u53c8\u8d50\u4e86\u4ed6\u4e00\u6240\u5e9c\u90b8\uff0c\u53eb\u4ed6\u73ed\u5e08\u56de\u671d\uff0c\u63a5 \u53d7\u5c01\u7235\u3002\u542c\u8bf4\u5e74\u7fb9\u5c27\u4e0d\u5927\u613f\u610f\u56de\u6765\uff0c\u5374\u8981\u6c42\u505a\u9655\u7518\u603b\u7763\u3002\u9655\u7518\u603b\u7763\u600e\u5f53\u5f97\u4e0a\u516c\u7235\u4e4b\u8363\uff0c\u4e5f\u4e0d \u77e5\u4ed6\u6253\u4ec0\u4e48\u4e3b\u610f\uff1f\u201d\u7518\u51e4\u6c60\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u5e74\u7fb9\u5c27\u7684\u806a\u660e\uff01\u60f3\u6765\u4ed6\u662f\u4e0d\u613f\u653e\u5f03\u5175\u6743\u3002\u201d\u4faf \u4e09\u53d8\u9053\uff1a\u201c\u4e5f\u6ca1\u542c\u8bf4\u7687\u4e0a\u8981\u524a\u4ed6\u7684\u5175\u6743\u3002\u201d\u7518\u51e4\u6c60\u5fc3\u9053\uff1a\u201c\u6789\u4f60\u505a\u4e86\u8fd9\u4e48\u591a\u5e74\u7684\u536b\u58eb\uff0c\u8fd8\u4e0d \u77e5\u9053\u7687\u5e1d\u7684\u72e0\u8fa3\uff0c\u73b0\u5728\u6b63\u662f\u5e74\u7fb9\u5c27\u5f97\u52bf\u4e4b\u9645\uff0c\u5982\u4f55\u53ef\u4ee5\u9aa4\u7136\u524a\u4ed6\u5175\u6743\u3002\u201d\u5019\u4e09\u53d8\u53c8\u9053\uff1a\u201c\u542c \u8bf4\u7687\u4e0a\u53c8\u51c6\u4ed6\u517c\u9886\u9655\u7518\u603b\u7763\uff0c\u8fd8\u628a\u4ed6\u7684\u7236\u4eb2\u5e74\u9050\u9f84\u4e5f\u5c01\u505a\u2018\u4e00\u7b49\u516c\u2019\uff0c\u53c8\u52a0\u2018\u592a\u5085\u2019\u8854\u3002\u53eb \u4ed6\u5148\u73ed\u5e08\u56de\u4eac\uff0c\u7136\u540e\u518d\u53bb\u8d74\u603b\u7763\u4e4b\u4efb\u3002\u5e74\u9050\u9f84\u90a3\u8001\u5934\u513f\u5e73\u767d\u505a\u4e86\u2018\u592a\u5085\u2019\uff0c\u6b22\u559c\u5f97\u4e0d\u5f97\u4e86\uff0c \u5df2\u5148\u5230\u4eac\u5e08\uff0c\u4f4f\u8fdb\u516c\u5e9c\u3002\u56e0\u6b64\u5e74\u7fb9\u5c27\u4e5f\u53ea\u597d\u73ed\u5e08\u56de\u671d\u4e86\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u5fc3\u62f4\u6c88\u5728\u5bbd\u4e4b\u4e8b\uff0c\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u8fd9\u53ae\u72fc\u5b50\u91ce\u5fc3\uff0c\u591a\u884c\u4e0d\u4e49\u5fc5\u81ea\u6bd9\uff0c\u54b1\u4eec\u4e14\u83ab\u8bf4 \u4ed6\u3002\u4faf\u8001\u5148\u751f\u53ef\u77e5\u4e25\u6d2a\u9035\u6c88\u5728\u5bbd\u88ab\u5bb3\u4e4b\u4e8b\u4e48\uff1f\u201d\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u6b64\u4e8b\u4e5f\u4e0e\u5e74\u7fb9\u5c27\u6709\u5173\u3002\u201d\u5415\u56db \u5a18\u8be7\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u9886\u5175\u5728\u5916\uff0c\u4f55\u4ee5\u4e0e\u4ed6\u6709\u5173\uff1f\u201d \u3000\u3000\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u5973\u4fa0\u6709\u6240\u4e0d\u77e5\uff0c\u8fd9\u591a\u56e0\u66fe\u9759\u800c\u8d77\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u66fe\u9759\uff1f\u8fd9\u4f4d\u8001\u5148\u751f\u4e5f\u88ab \u5bb3\u5417\uff1f\u201d\u66fe\u9759\u5e73\u751f\u6700\u4f69\u670d\u5415\u7559\u826f\uff0c\u4ed6\u672a\u53ca\u5f97\u89c1\u5415\u7559\u826f\u4e8e\u751f\u524d\uff0c\u5374\u4e8e\u5415\u7559\u826f\u6b7b\u540e\u5230\u5415\u5bb6\u8bbf\u6c42\u9057 \u4e66\uff0c\u81ea\u9644\u4e3a\u5415\u7559\u826f\u7684\u5f1f\u5b50\uff0c\u5728\u5f53\u4e16\u9887\u6709\u6587\u540d\uff0c\u4e0e\u4e25\u6d2a\u9035\u6c88\u5728\u5bbd\u7b49\u90e8\u5f88\u719f\u8bc6\u3002\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u66fe \u8001\u5148\u751f\u662f\u88ab\u6355\u4e86\uff0c\u4f46\u5374\u5e76\u672a\u9047\u5bb3\uff0c\u4ed6\u73b0\u5728\u4fbf\u5728\u5e74\u7fb9\u5c27\u519b\u4e2d\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u8be7\u9053\uff1a\u201c\u8fd9\u53c8\u662f\u4f55\u6545\uff1f\u201d\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u8fd9\u4f4d\u66fe\u8001\u5148\u751f\u5999\u60f3\u5929\u5f00\uff0c\u9063\u4ed6\u7684\u5b66\u751f\u5f20\u7199 \u53bb\u6e38\u8bf4\u5e74\u7fb9\u5c27\u7684\u526f\u624b\u5cb3\u949f\u742a\uff0c\u9644\u4f1a\u7a7f\u51ff\uff0c\u529b\u8bc1\u5cb3\u949f\u742a\u662f\u5cb3\u98de\u7684\u540e\u4ee3\u3002\u201d\u7518\u51e4\u6c60\u5657\u55e4\u4e00\u7b11\uff0c\u4faf \u4e09\u53d8\u9053\uff1a\u201c\u56e0\u6b64\u66fe\u9759\u4fbf\u5199\u5bc6\u51fd\u53eb\u5f20\u7199\u5e26\u53bb\uff0c\u8bf4\u5cb3\u6c0f\u548c\u91d1\u4eba\u4e43\u662f\u4e16\u4ec7\uff0c\u5cb3\u5bb6\u5b50\u5f1f\u4e0d\u5e94\u505a\u80e1\u864f\u7684 \u5927\u5c06\uff0c\u5c45\u7136\u529d\u5cb3\u949f\u742a\u548c\u4ed6\u5bc6\u8c0b\u4e3e\u4e8b\u3002\u5cb3\u949f\u6dc7\u4f6f\u4e3a\u7b54\u5e94\uff0c\u5c06\u66fe\u9759\u8bf1\u6765\uff0c\u903c\u4ed6\u4f9b\u51fa\u540c\u515a\u4e4b\u4eba\uff01\u201d \u5415\u56db\u5a18\u6025\u95ee\u9053\uff1a\u201c\u4f9b\u51fa\u4e86\u6ca1\u6709\uff1f\u201d\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u6211\u4e5f\u53ea\u662f\u542c\u8bf4\uff0c\u4ed6\u6709\u6ca1\u6709\u4f9b\u6216\u4f9b\u51fa\u4ec0\u4e48\u5c31\u4e0d \u77e5\u9053\u4e86\u3002\u53ea\u77e5\u9053\u5cb3\u949f\u6dc7\u62ff\u4e86\u66fe\u9759\u4e4b\u540e\uff0c\u4fbf\u7acb\u523b\u98de\u647a\u4e0a\u594f\uff0c\u56e0\u6b64\u6380\u8d77\u8f69\u7136\u5927\u6ce2\uff0c\u671d\u5ef7\u5230\u5904\u641c\u6355 \u5415\u7559\u826f\u9057\u515a\u3002\u5728\u66fe\u9759\u672a\u89e3\u5230\u4eac\u4e4b\u524d\uff0c\u4fbf\u5148\u5c06\u9996\u8981\u4e8c\u4eba\u4e25\u6d2a\u9035\u6c88\u5728\u5bbd\u62ff\u5230\u4eac\u57ce\u5904\u65a9\u4e86\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u5fc3\u5934\u9707\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u6c88\u5728\u5bbd\u662f\u771f\u88ab\u5904\u65a9\u4e86\uff1f\u201d\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u5982\u4f55\u4e0d\u771f\uff0c\u7687\u4e0a \u4e5f\u4e0b\u4e86\u8c15\u65e8\u4e86\uff0c\u4ed6\u4eec\u7684\u5934\u4e5f\u5728\u4e5d\u95e8\u60ac\u6302\u4e86\uff01\u201d\u5415\u56db\u5a18\u542c\u4e86\uff0c\u539f\u6765\u4faf\u4e09\u53d8\u6240\u77e5\u7684\u4ea6\u4e0d\u8fc7\u548c\u81ea\u5df1 \u4e00\u6837\uff0c\u90a3\u4eba\u5934\u662f\u5426\u6c88\u5728\u5bbd\u7684\uff0c\u8fd8\u662f\u672a\u80fd\u786e\u77e5\u3002\u4fbf\u9053\uff1a\u201c\u90a3\u8c15\u65e8\u6211\u4e5f\u89c1\u5230\u4e86\uff0c\u5176\u4e2d\u6ca1\u6709\u63d0\u5230\u66fe \u9759\u3002\u201d\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u8981\u7b49\u5e74\u7fb9\u5c27\u5c06\u4ed6\u89e3\u56de\u4eac\u5e08\u518d\u884c\u5b9a\u5904\u5427\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u4f4e\u5934\u601d\u60f3\uff0c\u81f3\u6b64\u5ffd\u9053\uff1a\u201c\u5f85\u6211\u5165\u5e74\u7fb9\u5c27\u519b\u4e2d\u4e00\u770b\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e03\u54e5\u65e0\u8c13\u5192\u6b64 \u5927\u9669\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f60\u5fcd\u5fc3\u89c1\u4f60\u7237\u7237\u7684\u5f1f\u5b50\u5168\u90fd\u53d7\u5bb3\u5417\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u66fe\u8001\u5148\u751f\u9053\u5fb7\u6587 \u7ae0\u540d\u6ee1\u5929\u4e0b\uff0c\u60f3\u4ed6\u4e0d\u4f1a\u5c48\u670d\uff0c\u62db\u4f9b\u51fa\u6765\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u4e5f\u4e0d\u53ef\u4e0d\u9632\u3002\u6211\u5c31\u662f\u60f3 \u53bb\u63a2\u63a2\uff0c\u770b\u4ed6\u5230\u5e95\u6709\u6ca1\u6709\u62db\u4f9b\uff0c\u82e5\u662f\u62db\u4f9b\uff0c\u90a3\u540d\u5355\u6709\u6ca1\u6709\u5df2\u5448\u7ed9\u5141\u796f\uff1f\u8001\u5b9e\u8bf4\uff0c\u6211\u5c31\u4fe1\u66fe\u9759 \u4e0d\u8fc7\u3002\u5f53\u5e74\u6211\u89c1\u4ed6\u65f6\uff0c\u4ed6\u6781\u529b\u4e3b\u5f20\u8981\u7528\u717d\u52a8\u6e05\u519b\u4e2d\u7684\u6c49\u4eba\u5c06\u9886\u4e4b\u6cd5\uff0c\u4ee5\u8c0b\u590d\u56fd\u3002\u6b64\u7b56\u7565\u867d\u7136 \u672a\u53ef\u539a\u975e\uff0c\u4f46\u628a\u4e00\u5207\u5e0c\u671b\u90fd\u5bc4\u6258\u5176\u95f4\uff0c\u5374\u662f\u56e0\u4eba\u6210\u4e8b\uff0c\u6beb\u65e0\u51fa\u606f\u7684\u60f3\u6cd5\u3002\u8fd9\u6837\u7684\u4eba\u5927\u534a\u4e0d\u662f \u786c\u9aa8\u5934\u3002\u201d\u5415\u56db\u5a18\u609a\u7136\u4e00\u9707\uff0c\u5fc3\u60f3\u81ea\u5df1\u751f\u957f\u5728\u4e66\u9999\u95e8\u7b2c\uff0c\u5bf9\u8bfb\u4e66\u4eba\u53ef\u80fd\u770b\u5f97\u8fc7\u9ad8\uff0c\u800c\u5ffd\u7565\u4e86 \u4ed6\u4eec\u8f6f\u5f31\u7684\u7f3a\u70b9\uff0c\u7518\u51e4\u6c60\u5728\u8fd9\u4e00\u65b9\u9762\uff0c\u5374\u6bd4\u81ea\u5df1\u5f3a\u5f97\u591a\uff0c\u4f46\u4ecd\u4e0d\u53ef\u76f8\u4fe1\u4ee5\u66fe\u9759\u8fd9\u6837\u7684\u4eba\u4ea6\u4f1a \u5c48\u670d\u4e8e\u654c\u4eba\u7684\u6deb\u5a01\u4e4b\u4e0b\uff0c\u53ea\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u53bb\u770b\u770b\u4e5f\u597d\u3002\u53ef\u662f\u5e74\u7fb9\u5c27\u519b\u65c5\u68ee\u4e25\uff0c\u5982\u4f55\u53bb \u5f97\uff1f\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u6211\u81ea\u6709\u529e\u6cd5\u3002\u4e0d\u8fc7\u6211\u8fd8\u8981\u4e00\u4e2a\u5e2e\u624b\uff0c\u4f8d\u4eca\u665a\u5510\u6653\u6f9c\u56de\u6765\u4e4b\u540e\u518d\u8bf4 \u5427\uff01\u201d\u8bf4\u5b8c\u4e4b\u540e\uff0c\u81ea\u53bb\u5e03\u7f6e\u3002\u5415\u56db\u5a18\u77e5\u9053\u7518\u51e4\u6c60\u76f8\u8bc6\u6ee1\u5929\u4e0b\uff0c\u4ed6\u8bf4\u6709\u529e\u6cd5\u5c31\u4e00\u5b9a\u6709\u529e\u6cd5\u3002 \u3000\u3000\u665a\u4e0a\uff0c\u5510\u6653\u6f9c\u548c\u51af\u745b\u59d0\u59b9\u679c\u7136\u63a5\u4e86\u909d\u7ec3\u971e\u7b49\u4e00\u5927\u5e2e\u4eba\u56de\u6765\u3002\u51b7\u7985\u5c06\u4ed6\u4eec\u5b89\u987f\u4e4b\u540e\uff0c\u5415\u56db \u5a18\u4fbf\u53eb\u5510\u6653\u6f9c\u53bb\u548c\u7518\u51e4\u6c60\u5546\u8bae\uff0c\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u4eec\u4eca\u65e5\u5728\u516b\u8fbe\u5cad\u6240\u89c1\u7684\u4e43\u662f\u5e74\u7fb9\u5c27\u7684\u5148\u5934\u90e8 \u961f\uff0c\u73b0\u5728\u5df2\u63a2\u542c\u6e05\u695a\uff0c\u5e74\u7fb9\u5c27\u8fd9\u6b21\u5e26\u56de\u6765\u7684\u5927\u519b\u7adf\u6709\u4e8c\u5341\u4e07\u4e4b\u591a\uff01\u5927\u519b\u884c\u7a0b\u7d20\u6765\u7f13\u6162\uff0c\u6bcf\u5929 \u81f3\u591a\u53ea\u80fd\u8d70\u516d\u5341\u91cc\u3002\u4ed6\u4eec\u8fd8\u8981\u56db\u5929\u624d\u80fd\u5230\u4eac\uff0c\u6653\u6f9c\uff0c\u4f60\u6562\u548c\u6211\u5230\u519b\u8425\u4e2d\u4e00\u63a2\u4e48\uff1f\u201d\u5510\u6653\u6f9c \u9053\uff1a\u201c\u5982\u4f55\u4e0d\u6562\uff01\u6211\u66fe\u5728\u5141\u5824\u519b\u4e2d\u6df7\u8fc7\uff0c\u5bf9\u519b\u8425\u4e2d\u7684\u60c5\u5f62\uff0c\u4e5f\u5f88\u719f\u6089\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6b63\u56e0 \u5982\u6b64\uff0c\u6240\u4ee5\u6211\u8981\u4f60\u53bb\u3002\u6211\u5df2\u7b97\u5b9a\uff0c\u4ed6\u4eec\u660e\u5929\u4f1a\u5230\u623f\u5c71\uff0c\u623f\u5c71\u7684\u5730\u65b9\u5b98\u5fc5\u7136\u6d3e\u9063\u6c11\u592b\uff0c\u7275\u732a\u5bb0 \u7f8a\u4f9b\u5e94\u4ed6\u4eec\uff0c\u6211\u4eec\u6df7\u4f5c\u6c11\u592b\uff0c\u5230\u4e86\u4ed6\u7684\u519b\u524d\uff0c\u6211\u4e00\u5b9a\u53ef\u627e\u5230\u673a\u4f1a\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u4f9d\u8ba1\u800c\u884c\u3002\u7518\u51e4\u6c60\u4f7f\u7528\u6613\u5bb9\u4e39\uff0c\u626e\u6210\u6c11\u592b\uff0c\u7b2c\u4e8c\u65e5\u6df7\u5230\u519b\u524d\uff0c\u679c\u7136\u627e\u5230\u4e86\u4e00\u4e2a\u4ee5 \u524d\u5728\u6c5f\u5357\u5e2e\u4f1a\u4e2d\u7684\u5c0f\u5934\u76ee\uff0c\u540d\u53eb\u97e9\u4e03\uff0c\u73b0\u5728\u519b\u4e2d\u5145\u5f53\u519b\u53a8\u7ba1\u4e8b\u4e4b\u804c\uff0c\u624b\u4e0b\u6709\u5341\u51e0\u540d\u706b\u5934\u519b\u3002 \u5927\u519b\u4e2d\u6709\u51e0\u767e\u4e2a\u519b\u53a8\u7ba1\u4e8b\uff0c\u8d1f\u8d23\u4f9b\u5e94\u4f19\u98df\uff0c\u97e9\u4e03\u4f9b\u5e94\u7684\u6070\u6070\u662f\u5e74\u7fb9\u5c27\u7684\u4eb2\u5175\uff0c\u56e0\u6b64\u6b63\u5728\u5e74\u7fb9 \u5c27\u7684\u201c\u5e05\u8425\u201d\u4e4b\u5185\u3002\u7518\u51e4\u6c60\u8ff3\u9053\u59d3\u540d\uff0c\u76f4\u8bf4\u6765\u610f\u3002\u7518\u51e4\u6c60\u81ea\u5df1\u867d\u7136\u5e76\u4e0d\u5f00\u5c71\u7acb\u8235\uff0c\u5374\u662f\u6c5f\u5357 \u6240\u6709\u5e2e\u4f1a\u90fd\u5949\u4e3a\u9996\u9886\u7684\u4eba\uff0c\u97e9\u4e03\u542c\u4e86\uff0c\u867d\u7136\u4e8b\u60c5\u6781\u9669\uff0c\u4e5f\u613f\u4f9d\u4ece\u3002\u5f53\u4e0b\u4fbf\u53eb\u7518\u5510\u4e8c\u4eba\u5f53\u4f5c\u4ed6 \u65b0\u6536\u7684\u4f19\u5934\u519b\uff0c\u6df7\u5165\u8425\u5185\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u6cbb\u519b\u6781\u4e25\uff0c\u7518\u51e4\u6c60\u867d\u5728\u201c\u5e05\u8425\u201d\uff0c\u5374\u65e0\u6cd5\u6df7\u5165\u5e74\u7fb9\u5c27\u6240\u4f4f\u7684\u201c\u864e\u5e10\u201d\u3002\u662f\u591c\u4e09\u66f4 \u8fc7\u540e\uff0c\u53ea\u542c\u5f97\u519b\u8425\u4e2d\u5201\u6597\u58f0\u58f0\uff0c\u504c\u5927\u7684\u519b\u8425\uff0c\u522b\u65e0\u58f0\u606f\u3002\u7518\u51e4\u6c60\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u4e5f\u7684\u786e \u7b97\u5f97\u4e0a\u5927\u5c06\u4e4b\u624d\uff0c\u53ef\u60dc\u7adf\u7518\u5fc3\u4e3a\u5141\u796f\u6240\u7528\u3002\u201d\u97e9\u4e03\u6084\u6084\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u77e5\u9053\u5728\u5e74\u7fb9\u5c27\u5e10\u5916\u5439 \u89d2\u5b88\u591c\u7684\u4eba\u662f\u8c01\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6253\u66f4\u5b88\u591c\u7684\u96be\u9053\u4f1a\u662f\u4ec0\u4e48\u9ad8\u624b\u5417\uff1f\u201d\u97e9\u4e03\u9053\uff1a\u201c\u8fd9\u4eba\u5012\u4e0d \u662f\u4ee5\u6b66\u529f\u89c1\u957f\u3002\u4f46\u4ed6\u5374\u662f\u4e2a\u5927\u5b98\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4ec0\u4e48\u5927\u5bab\uff1f\u201d\u97e9\u4e03\u9053\uff1a\u201c\u4ed6\u662f\u519b\u95e8\u63d0\u7763\u5bcc \u5c71\u3002\u5e74\u7fb9\u5c27\u8fd9\u6b21\u5e26\u59bb\u5b50\u56de\u6765\uff0c\u4e00\u8def\u4e0a\u4f5c\u5a01\u4f5c\u798f\uff0c\u5938\u8000\u81ea\u5df1\u7684\u6743\u529b\uff0c\u6211\u4eec\u90fd\u7b11\u4ed6\u662f\u505a\u7ed9\u59bb\u5b50\u770b \u7684\u3002\u6bd4\u5982\u5439\u89d2\u5b88\u591c\u7684\u4eba\uff0c\u8d77\u521d\u662f\u7528\u4e2d\u519b\u795e\u5c06\uff0c\u540e\u6765\u7528\u5230\u7edf\u5e26\u526f\u5c06\uff0c\u4eca\u665a\u4e34\u8fd1\u4eac\u57ce\uff0c\u7adf\u7136\u7528\u8d77 \u63d0\u7763\u519b\u95e8\u4e86\u3002\u201d\u5510\u6653\u6f9c\u548b\u820c\u9053\uff1a\u201c\u63d0\u7763\u519b\u95e8\uff0c\u4e0e\u5de1\u629a\uff08\u5341\u7701\u957f\u5b98\uff09\u5e73\u884c\uff0c\u7687\u5e1d\u4e5f\u4e0d\u6562\u7528\u6765\u6253 \u66f4\u5b88\u591c\u3002\u5e74\u7fb9\u5c27\u5982\u6b64\u5f04\u6743\uff0c\u4e0d\u6015\u7687\u5e1d\u77e5\u9053\u4e86\u7f6a\u8d23\u4e48\uff1f\u201d\u97e9\u4e03\u7b11\u9053\uff1a\u201c\u73b0\u5728\u5e74\u5927\u5e05\u529f\u9ad8\u9707\u4e3b\uff0c \u8c01\u6562\u53c2\u4ed6\u3002\u8fd9\u4e2a\u63d0\u7763\u519b\u95e8\u8fd8\u662f\u4e2a\u6ee1\u4eba\u5462\uff01\u201d \u3000\u3000\u5510\u6653\u6f9c\u95ee\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u4ec0\u4e48\u65f6\u5019\u8ba8\u7684\u59bb\u5b50\uff1f\u201d\u97e9\u4e03\u9053\uff1a\u201c\u5728\u897f\u5f81\u81f3\u897f\u5b81\u4e4b\u65f6\u8ba8\u7684\u3002\u5e74\u7fb9 \u5c27\u6709\u4e2a\u6781\u574f\u7684\u813e\u6c14\uff0c\u6bcf\u5230\u4e00\u4e2a\u5730\u65b9\u5b98\u8859\u95e8\u91cc\uff0c\u975e\u4f46\u8981\u5730\u65b9\u5b98\u51fa\u6765\u8fce\u63a5\uff0c\u8fde\u5730\u65b9\u5b98\u7684\u59bb\u5b50\u59d0\u59b9 \u5973\u513f\u90fd\u8981\u51fa\u6765\u8fce\u63a5\u3002\u897f\u5b81\u6709\u4e00\u4e2a\u8499\u53e4\u85e9\u738b\u540d\u53eb\u85cf\u53e4\u4e03\u4fe1\uff0c\u751f\u6709\u4e00\u4e2a\u5973\u513f\u540d\u53eb\u4f73\u7279\u683c\u683c\uff0c\u7f8e\u8c8c \u5982\u82b1\u3002\u8499\u53e4\u85e9\u738b\u5e26\u4e86\u5973\u513f\u62dc\u89c1\uff0c\u5e74\u7fb9\u5c27\u4e00\u6b22\u559c\u4fbf\u628a\u5979\u7559\u4e0b\u4e86\u3002\u85cf\u53e4\u4e03\u4fe1\u6ca1\u6cd5\uff0c\u53ea\u597d\u9001\u7ed9\u4ed6\u505a \u59bb\u5b50\u3002\u671d\u5ef7\u8001\u4f8b\uff0c\u672c\u6765\u4e0d\u8bb8\u5927\u5c06\u5e26\u59bb\u5b50\u51fa\u5f81\uff0c\u4ed6\u4e5f\u4e0d\u7ba1\u3002\u524d\u4e24\u4e2a\u6708\u8fd8\u5728\u519b\u4e2d\u517b\u4e86\u4e2a\u80d6\u5c0f\u5b50 \u5462\uff01\u201d \u3000\u3000\u7518\u51e4\u4ed6\u65e0\u5fc3\u542c\u4ed6\u95f2\u8bdd\uff0c\u8c08\u4e86\u4e00\u4f1a\uff0c\u4fbf\u548c\u5510\u6653\u6f9c\u5192\u5145\u5916\u8425\u7684\u66f4\u592b\uff0c\u5192\u9669\u53bb\u63a2\u5e74\u7fb9\u5c27\u7684\u8425 \u5e10\u3002\u97e9\u4e03\u9053\uff1a\u201c\u7518\u5927\u4fa0\u53ef\u8981\u5c0f\u5fc3\u3002\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u7edd\u4e0d\u8fde\u7d2f\u4f60\u4eec\u4fbf\u662f\u3002\u201d\u5e74\u7fb9\u5c27\u7684\u201c\u864e \u5e10\u201d\u4e4b\u5916\u6709\u4e09\u5c42\u8425\u5e10\u536b\u62a4\uff0c\u7518\u5510\u4e8c\u4eba\u5192\u9669\u5230\u4e86\u7b2c\u4e00\u5c42\u8425\u5e10\u4e4b\u5916\uff0c\u5f80\u6765\u5de1\u903b\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u6cbb\u519b\u6781\u4e25\uff0c\u624b\u4e0b\u4e4b\u4eba\u52a8\u8f84\u5f97\u548e\u3002\u90a3\u5916\u8425\u7684\u66f4\u592b\u7a81\u7136\u89c1\u6dfb\u4e86\u4e24\u4e2a\u65b0\u4eba\uff0c\u867d\u7136\u8be7\u5f02\uff0c \u53ef\u662f\u60f3\u5230\u591a\u4e00\u4e8b\u4e0d\u5982\u5c11\u4e00\u4e8b\uff0c\u90fd\u4e0d\u6562\u58f0\u5f20\u3002\u5510\u6653\u6f9c\u53c8\u77e5\u9053\u519b\u4e2d\u7684\u89c4\u77e9\uff0c\u6545\u610f\u8bf3\u8bf4\u662f\u4e2d\u519b\u56e0\u4e3a \u4e34\u8fd1\u4eac\u8f8e\uff0c\u6240\u4ee5\u589e\u6dfb\u4ed6\u4eec\u5b88\u591c\u3002\u5916\u8425\u7684\u66f4\u592b\u5e73\u7d20\u8fde\u89c1\u4e2d\u519b\u7684\u9762\u4e5f\u4e0d\u6562\uff0c\u66f4\u517c\u53c8\u77e5\u9053\u5e74\u7fb9\u5c27\u813e \u6c14\u53e4\u602a\uff0c\u8fde\u63d0\u7763\u519b\u95e8\u4e5f\u6562\u6d3e\u53bb\u6253\u66f4\uff0c\u8bf4\u4e0d\u5b9a\u8fd9\u4e24\u4eba\u4e5f\u662f\u519b\u5b98\uff0c\u66f4\u4e0d\u6562\u591a\u95ee\u3002 \u3000\u3000\u7518\u51e4\u6c60\u7ed5\u4e86\u4e24\u5708\uff0c\u89d1\u7740\u6ca1\u4eba\uff0c\u5bf9\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u628a\u98ce\uff0c\u6211\u5230\u5e74\u7fb9\u5c27\u5e10\u4e2d\u53bb\u770b\u3002\u201d \u98de\u8eab\u4e00\u8dc3\uff0c\u8df3\u4e0a\u5e10\u5e55\u3002\u7518\u51e4\u6c60\u8f7b\u529f\u867d\u4e0d\u53ca\u5415\u56db\u5a18\uff0c\u5374\u4e5f\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u98de\u8eab\u4e0a\u53bb\uff0c\u6709\u5982\u4e00\u53f6\u8f7b \u5815\uff0c\u5e10\u5e55\u7eb9\u4e1d\u4e0d\u52a8\u3002\u7518\u51e4\u6c60\u4e00\u8def\u86c7\u884c\u514d\u8dc3\uff0c\u4e0d\u4e00\u523b\u4fbf\u5230\u4e86\u5e74\u7fb9\u5c27\u7684\u5e10\u9876\u3002 \u3000\u3000\u5e10\u4e2d\u9759\u6084\u6084\u7684\uff0c\u7518\u51e4\u6c60\u6b63\u81ea\u76d8\u7b97\u5982\u4f55\u53ef\u4ee5\u63a2\u51fa\u66fe\u9759\u4e4b\u4e8b\uff0c\u4e5f\u66fe\u60f3\u5230\u4e0b\u53bb\u8981\u631f\u5e74\u7fb9\u5c27\uff0c\u4f46 \u53c8\u60f3\u5230\u5e74\u7fb9\u5c27\u6b66\u529f\u4ea6\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u4e07\u4e00\u4e0d\u6210\uff0c\u540e\u679c\u66f4\u574f\u3002\u6b63\u76d8\u7b97\u95f4\uff0c\u5ffd\u542c\u5f97\u5e10\u4e0b\u5514\u5514\u58f0\u54cd\uff0c \u6709\u4e2a\u5a07\u6ef4\u6ef4\u7684\u58f0\u97f3\u9053\uff1a\u201c\u51e0\u66f4\u4e86\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u56db\u66f4\u5566\uff01\u201d\u5e10\u95e8\u5916\u545c\u545c\u58f0\u54cd\uff0c\u4e00\u58f0\u9ad8\u4e00\u58f0 \u4f4e\u7684\u5439\u7740\u53f7\u89d2\uff0c\u90a3\u5a07\u6ef4\u6ef4\u7684\u58f0\u97f3\u9053\uff1a\u201c\u4eca\u665a\u662f\u8c01\u5439\u89d2\u5b88\u591c\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6211\u7684\u597d\u683c\u683c\uff0c\u8bf4 \u51fa\u6765\u5413\u4f60\u4e00\u8df3\uff0c\u4eca\u665a\u5b88\u591c\u7684\u662f\u5bcc\u5c71\u63d0\u7763\u5462\uff01\u4f60\u8bf4\uff0c\u4f60\u597d\u4e0d\u597d\u798f\u6c14\uff1f\u6709\u63d0\u7763\u519b\u95e8\u66ff\u4f60\u628a\u95e8\uff01\u201d \u4f73\u7279\u683c\u683c\u5c0f\u5634\u4e00\u55d4\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff0c\u54ea\u6709\u505a\u5230\u63d0\u7763\u8fd8\u8981\u5b88\u591c\u7684\uff1f\u201d\u5e74\u7fb9\u5c27\u7b11\u9053\uff1a\u201c\u662f\u6211\u53eb \u4ed6\u5b88\u591c\uff0c\u4ed6\u5c82\u6562\u4e0d\u4ece\uff1f\u201d\u4f73\u7279\u683c\u683c\u9053\uff1a\u201c\u6211\u8fd8\u662f\u4e0d\u4fe1\uff0c\u4f60\u771f\u6709\u8fd9\u6837\u80c6\u5b50\uff1f\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u4fe1\u6211\u5c31\u628a\u4ed6\u53eb\u8fdb\u6765\u8ba9\u4f60\u770b\u770b\uff01\u201d\u5e10\u4e2d\u4eae\u8d77\u706f\u706b\uff0c\u5429\u5490\u4eb2\u5175\u5c06\u5916\u9762 \u6253\u66f4\u7684\u4eba\u5524\u6765\u3002 \u3000\u3000\u7518\u51e4\u6c60\u614c\u5fd9\u7f29\u5728\u5e10\u89d2\uff0c\u85c9\u5012\u5377\u8d77\u6765\u7684\u5e10\u84ec\u63a9\u655d\u81ea\u5df1\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u542c\u5f97\u65b0\u5175\u9886\u4e86\u4e00\u4e2a\u4eba\u8d70 \u5165\u5e10\u4e2d\u3002\u5e95\u4e0b\u4f73\u7279\u683c\u683c\u563b\u563b\u7b11\u4e86\u4e24\u58f0\uff0c\u5ffd\u542c\u5f97\u5e74\u7fb9\u5c27\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u7518\u51e4\u6c60\u5403\u4e86\u4e00\u60ca\uff0c \u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u51c6\u4e0d\u4f1a\u662f\u5bcc\u5c71\u63d0\u7763\u3002\u201d \u3000\u3000\u90a3\u4eba\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5bcc\u5c71\u519b\u95e8\u5e10\u4e0b\u7684\u5218\u53c2\u5c06\u3002\u201d\u5e74\u7fb9\u5c27\u559d\u9053\uff1a\u201c\u5bcc\u63d0\u7763\u5230\u4ec0\u4e48\u5730\u65b9\u53bb \u4e86\uff1f\u201d\u90a3\u4eba\u77e5\u9053\u4e8b\u60c5\u4e0d\u5999\uff0c\u5fd9\u8dea\u4e0b\u6765\u9053\uff1a\u201c\u5bcc\u63d0\u7763\u56e0\u6709\u8981\u4e8b\uff0c\u56de\u5e10\u53bb\u4e00\u8d9f\uff0c\u53eb\u5351\u804c\u6682\u65f6\u66ff \u4ee3\u3002\u201d\u5b9e\u662f\u5bcc\u5c71\u56e0\u81ea\u5df1\u8eab\u4e3a\u63d0\u7763\u519b\u95e8\uff0c\u5439\u89d2\u5b88\u591c\uff0c\u88ab\u90e8\u4e0b\u770b\u5230\uff0c\u592a\u4e0d\u597d\u770b\uff0c\u56e0\u6b64\u547d\u4ee4\u4e00\u4e2a\u53c2 \u5c06\u66ff\u4ee3\uff0c\u4ee5\u4e3a\u5e74\u7fb9\u5c27\u672a\u5fc5\u4f1a\u4eb2\u8eab\u6765\u67e5\u3002 \u3000\u3000\u90a3\u77e5\u5e74\u7fb9\u5c27\u4eca\u665a\u504f\u504f\u67e5\u95ee\uff0c\u542c\u4e86\u53c2\u5c06\u7684\u8bf4\u8bdd\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u5927\u80c6\u7684\u5bcc\u5c71\uff0c\u4ed6 \u6562\u4e0d\u5b88\u519b\u4ee4\uff0c\u7ed9\u6211\u4e00\u9f50\u65ab\u4e86\uff01\u201d\u8bdd\u4e00\u51fa\u53e3\uff0c\u4fbf\u6709\u5200\u65a7\u624b\u8fdb\u6765\uff0c\u5c06\u8fd9\u4e2a\u53c2\u5c06\u63ea\u51fa\u8425\u53bb\uff0c\u8fc7\u4e86\u4e00 \u4f1a\uff0c\u9001\u8fdb\u4e24\u4e2a\u8840\u6dcb\u6dcb\u7684\u4eba\u5934\uff0c\u4e00\u4e2a\u662f\u63d0\u7763\uff0c\u4e00\u4e2a\u662f\u53c2\u5c06\u3002\u5e74\u7fb9\u5c27\u7b11\u9053\uff1a\u201c\u683c\u683c\uff0c\u4f60\u77a7\u6e05\u695a \u4e86\uff0c\u8fd9\u4e2a\u662f\u4e0d\u662f\u5bcc\u5c71\u63d0\u7763\uff1f\u201d\u4f73\u7279\u683c\u683c\u63a9\u9762\u8bf4\u9053\uff1a\u201c\u5413\u6b7b\u4eba\u4e86\uff0c\u6211\u4e0d\u8981\u770b\uff0c\u5feb\u62ff\u51fa\u53bb\uff01\u201d\u5e74 \u7fb9\u5c27\u628a\u624b\u4e00\u6325\uff0c\u53eb\u4eb2\u5175\u5c06\u9996\u7ed9\u62ff\u53bb\u53f7\u4ee4\u3002 \u3000\u3000\u7518\u51e4\u6c60\u89c1\u5e74\u7fb9\u5c27\u5982\u6b64\u6b8b\u5fcd\uff0c\u751a\u4e3a\u6c14\u6124\u3002\u8425\u5e10\u5916\u53c8\u6709\u4eba\u4f20\u62a5\u9053\uff1a\u201c\u9646\u5c06\u519b\u53c2\u89c1\u3002\u201d\u5e74\u7fb9\u5c27 \u9053\uff1a\u201c\u8fdb\u6765\uff01\u201d\u6765\u7684\u4eba\u540d\u53eb\u9646\u864e\u81e3\uff0c\u662f\u5e74\u7fb9\u5c27\u4e00\u4e2a\u5fc3\u8179\u5927\u5c06\uff0c\u4e5f\u6709\u63d0\u7763\u519b\u8854\uff0c\u8fdb\u5e10\u884c\u793c\u4e4b \u540e\uff0c\u4fbf\u5f00\u58f0\u95ee\u9053\uff1a\u201c\u5bcc\u63d0\u7763\u5462\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6211\u5df2\u628a\u4ed6\u6740\u4e86\uff01\u201d\u9646\u864e\u81e3\u5927\u5403\u4e00\u60ca\uff0c\u8dea\u4e0b\u53bb \u9053\uff1a\u201c\u5927\u5e05\u542c\u7980\uff0c\u6211\u4eec\u4f5c\u6218\uff0c\u5168\u4ed7\u519b\u5fc3\uff0c\u519b\u5fc3\u4e00\u6563\uff0c\u4e07\u5206\u5371\u9669\uff0c\u5982\u4eca\u5927\u5e05\u6740\u4e86\u65e0\u7f6a\u7684\u5bcc\u63d0 \u7763\u3001\u5218\u53c2\u5c06\uff0c\u5c82\u4e0d\u4ee4\u519b\u58eb\u5bd2\u5fc3\u3002\u800c\u4e14\u7687\u4e0a\u5f97\u77e5\uff0c\u4e5f\u6709\u4e0d\u4fbf\u3002\u201d\u9646\u864e\u81e3\u5b9e\u662f\u4e00\u756a\u597d\u610f\uff0c\u72af\u989c\u8fdb \u8c0f\uff0c\u5c82\u77e5\u5e74\u7fb9\u5c27\u542c\u4e86\uff0c\u52c3\u7136\u6012\u9053\uff1a\u201c\u4ffa\u5982\u4eca\u66ff\u7687\u4e0a\u6253\u4e0b\u6c5f\u5c71\uff0c\u4fbf\u662f\u7687\u4e0a\u89c1\u4ffa\uff0c\u4e5f\u8981\u754f\u60e7\u4e09 \u5206\uff0c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u80c6\u6562\u717d\u52a8\u90e8\u4e0b\u6765\u53cd\u5bf9\u6211\u5417\uff1f\u5200\u65a7\u624b\u6765\uff0c\u90fd\u63a8\u51fa\u53bb\u65a9\u4e86\uff01\u201d\u9646\u864e\u81e3\u9b42\u98de\u9b44 \u6563\uff0c\u5927\u53eb\u51a4\u6789\u3002\u4e5f\u662f\u4ed6\u547d\u4e0d\u8be5\u7edd\uff0c\u5cb3\u949f\u742a\u542c\u5f97\u6740\u4e86\u5bcc\u63d0\u7763\u4e5f\u6025\u6025\u8d76\u6765\uff0c\u6070\u6070\u9047\u5230\u9646\u864e\u81e3\u88ab\u63a8 \u51fa\u5e10\u5916\uff0c\u95ee\u4e86\u539f\u6545\uff0c\u6025\u5fd9\u6b62\u4f4f\u5200\u65a7\u624b\uff0c\u8fdb\u5e10\u5411\u5e74\u7fb9\u5c27\u6c42\u60c5\uff0c\u7518\u51e4\u6c60\u4f0f\u5728\u5e10\u4e0a\uff0c\u542c\u5f97\u4ed6\u4eec\u4f4e\u58f0 \u7ec6\u8bed\uff0c\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u6e05\u695a\u3002\u542c\u4e86\u4e00\u4f1a\uff0c\u53ea\u542c\u5f97\u5e74\u7fb9\u5c27\u4f20\u4ee4\u4e0b\u53bb\u9053\uff1a\u201c\u770b\u5728\u5cb3\u5c06\u519b\u9762\u4e0a\uff0c\u9976 \u90a3\u53ae\u4e00\u6b7b\u3002\u4f46\u6b7b\u7f6a\u5154\u4e86\uff0c\u6d3b\u7f6a\u96be\u9976\uff0c\u7740\u4ee4\u6253\u4e94\u5341\u519b\u68cd\uff0c\u7f5a\u4ed6\u66ff\u5bcc\u5c71\u5b88\u591c\u4e09\u665a\uff01\u201d\u4ee4\u4e0b\u4e4b\u540e\uff0c \u8425\u5e10\u5916\u4fbf\u542c\u5f97\u519b\u68cd\u535c\u535c\u4e4b\u58f0\uff0c\u6253\u5f97\u9646\u864e\u81e3\u4e00\u9762\u558a\u75db\uff0c\u4e00\u9762\u8fd8\u8981\u201c\u8c22\u6069\u201d\u3002\u7ecf\u6b64\u4e00\u756a\u55a7\u95f9\uff0c\u5e10 \u5916\u5df2\u6253\u4e94\u66f4\u3002\u5e74\u7fb9\u5c27\u5c06\u5cb3\u949f\u742a\u9001\u51fa\u8425\u5e10\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u66fe\u9759\u8fd8\u5728\u4f60\u5e10\u4e2d\u5417\uff1f\u201d\u5cb3\u949f\u742a\u9053\uff1a \u201c\u662f\uff0c\u5927\u5e05\u3002\u201d\u5e74\u7fb9\u5c27\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u756a\u5e72\u5f97\u5f88\u597d\uff0c\u7687\u4e0a\u5b9a\u7136\u8d4f\u8bc6\u4f60\u4e86\u3002\u201d\u5cb3\u949f\u742a\u6bdb\u9aa8\u609a\u7136\uff0c \u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u5168\u4ed7\u5927\u5e05\u63d0\u643a\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u660e\u65e5\u4f60\u5c06\u4ed6\u9001\u5230\u6211\u8fd9\u91cc\u6765\u3002\u201d\u5cb3\u949f\u742a\u9053\uff1a \u201c\u662f\uff0c\u5927\u5e05\u3002\u201d\u5e74\u7fb9\u5c27\u5c06\u5cb3\u949f\u742a\u8fed\u51fa\uff0c\u56de\u5e10\u518d\u7761\u3002\u7518\u51e4\u6c60\u89c1\u5929\u8272\u5c06\u4eae\uff0c\u6025\u6025\u79bb\u5f00\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u7518\u51e4\u6c60\u867d\u6ca1\u6709\u63a2\u51fa\u4ec0\u4e48\uff0c\u5374\u77e5\u9053\u4e86\u66fe\u9759\u4e0b\u843d\u3002\u4f46\u519b\u4e2d\u9632\u8303\u6781\u4e25\uff0c\u65e5\u95f4\u5b9e\u662f\u65e0\u6cd5\u518d \u63a2\uff0c\u519b\u884c\u4e00\u65e5\uff0c\u665a\u4e0a\u5df2\u5230\u5317\u4eac\u57ce\u5916\u7684\u82a6\u6c9f\u6865\uff0c\u7b2c\u4e8c\u65e5\u4fbf\u53ef\u5165\u4eac\u4e86\u3002\u5927\u519b\u4fbf\u5728\u82a6\u6c9f\u6865\u9644\u8fd1\u9a7b \u624e\u3002\u7518\u51e4\u6c60\u5230\u4e86\u665a\u4e0a\uff0c\u4ecd\u548c\u5510\u6653\u6f9c\u5192\u5145\u5916\u8425\u66f4\u592b\uff0c\u53c8\u5077\u5077\u98de\u4e0a\u4e86\u5e74\u7fb9\u5c27\u7684\u8425\u5e10\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u4e0e\u524d\u4e00\u665a\u53c8\u662f\u4e0d\u540c\uff0c\u53ea\u6709\u9646\u864e\u81e3\u5728\u8425\u5916\u5439\u89d2\u5b88\u591c\uff0c\u5e10\u5916\u8fde\u5341\u540d\u536b\u58eb\u90fd\u6ca1\u6709\u3002\u53ea\u5728 \u5916\u5e10\u4e0e\u201c\u864e\u5e10\u201d\u76f8\u63a5\u4e4b\u95f4\uff0c\u6709\u536b\u58eb\u5de1\u903b\u3002\u5e10\u4e2d\u4eae\u8d77\u706f\u706b\uff0c\u7518\u51e4\u6c60\u4f0f\u5728\u5e10\u4e0a\uff0c\u53ef\u4ee5\u770b\u5230\u5e74\u7fb9\u5c27 \u7684\u5f71\u5b50\u5728\u4e0b\u9762\u8d70\u6765\u8d70\u53bb\uff0c\u5e10\u4e2d\u53ea\u4ed6\u4e00\u4eba\uff0c\u4f73\u7279\u683c\u683c\u4e5f\u4e0d\u5728\u5185\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4eb2\u5175\u5e26\u8fdb\u4e00\u4eba\uff0c\u6b63\u662f\u66fe\u9759\u3002\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u51fa\u53bb\u5429\u9644\uff0c\u4e0d\u51c6\u95f2\u4eba\u8d70\u8fd1\u864e \u5e10\u3002\u201d\u4eb2\u5175\u5e94\u4e86\u4e00\u58f0\uff0c\u6025\u6025\u8d70\u51fa\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u5750\u5728\u864e\u76ae\u6905\u4e0a\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u66fe\u8001\u5934\u513f\uff0c\u4f60\u53ef\u77e5\u9053\u4e25\u6d2a\u9035\u4e0e\u6c88\u5728\u5bbd\u5df2\u88ab\u65a9 \u51b3\uff0c\u60ac\u9996\u4e5d\u95e8\u4e86\u4e48\uff1f\u201d\u66fe\u9759\u745f\u7f29\u4e00\u9685\uff0c\u7b54\u4e0d\u51fa\u8bdd\u3002\u7518\u51e4\u6c60\u6697\u6697\u9a82\u9053\uff1a\u201c\u771f\u662f\u8113\u5305\uff0c\u6bd4\u6c88\u5728\u5bbd \u5dee\u5f97\u592a\u8fdc\u4e86\uff01\u5415\u7559\u826f\u5730\u4e0b\u6709\u77e5\uff0c\u4e00\u5b9a\u9a82\u4ed6\u8c2c\u6258\u95e8\u5899\uff0c\u81ea\u79f0\u5f1f\u5b50\uff01\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u53cc\u773c\u4e00\u626b\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6309\u8bf4\u4f60\u6240\u72af\u7684\u7f6a\u540d\u4e5f\u8be5\u6284\u65a9\u4e5d\u65cf\uff01\u201d\u66fe\u9759\u55eb\u55eb\u8bf4\u9053\uff1a\u201c\u5168 \u4ed7\u5927\u5e05\u5f00\u6069\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u8fd9\u5c31\u5168\u8981\u770b\u4f60\u81ea\u5df1\u4e86\uff01\u201d\u66fe\u9759\u9053\uff1a\u201c\u8bf7\u5927\u5e05\u6307\u793a\u3002\u201d\u5e74\u7fb9\u5c27\u9762 \u5b54\u4e00\u677f\uff0c\u9053\uff1a\u201c\u7687\u4e0a\u8981\u4f60\u5c06\u529f\u8d4e\u7f6a\uff01\u201d\u66fe\u9759\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5df2\u5c06\u4e25\u6d2a\u9035\u548c\u6c88\u5728\u5bbd\u4f9b\u51fa\u6765\u4e86 \u5417\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u662f\u5415\u7559\u826f\u7684\u5f97\u610f\u4f20\u4eba\uff0c\u5929\u4e0b\u7686\u77e5\uff0c\u4f55\u987b\u4f60\u8bf4\uff1f\u201d\u66fe\u9759\u8fa9\u9053\uff1a\u201c\u4f46 \u90a3\u6c88\u5728\u5bbd\u9690\u5c45\u4ed9\u971e\u5c71\u9876\uff0c\u5374\u662f\u65e0\u4eba\u77e5\u9053\u3002\u201d\u7518\u51e4\u6c60\u542c\u4e86\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u6697\u9a82\u201c\u8be5\u6b7b\uff01\u201d\u60ca\u7684 \u662f\uff0c\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u6c88\u5728\u5bbd\u7684\u5934\u9885\u5f53\u4e0d\u662f\u5047\u7684\u4e86\u3002\u6012\u7684\u662f\uff1a\u66fe\u9759\u8fd9\u8001\u513f\u6b3a\u4e16\u76d7\u540d\uff0c\u7adf\u7136\u4e34\u96be\u5c48 \u670d\uff0c\u6beb\u65e0\u6c14\u9aa8\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u7b97\u4f60\u4e00\u6869\u529f\u52b3\uff0c\u4f46\u53ea\u8fd9\u70b9\u529f\u52b3\uff0c\u8fd8\u4e0d\u80fd\u8d4e\u4f60\u7684\u7f6a\u3002\u7687\u4e0a\u8981\u4f60 \u5c06\u5176\u4ed6\u7684\u4eba\u4e5f\u8bf4\u51fa\u6765\u3002\u201d\u66fe\u9759\u9053\uff1a\u201c\u4fe1\u5949\u5415\u7559\u826f\u4e4b\u8bf4\u7684\u4eba\u4e0d\u53ef\u80dc\u8ba1\uff0c\u6211\u4e5f\u4e0d\u5c3d\u77e5\u9053\u3002\u201d\u5e74\u7fb9 \u5c27\u9053\uff1a\u201c\u53ea\u8981\u4f60\u5c06\u5404\u5730\u9996\u8981\u8bf4\u51fa\u6765\u4fbf\u884c\u3002\u201d\u66fe\u9759\u9053\uff1a\u201c\u676d\u5dde\u8f66\u9f0e\u4e30\uff0c\u6e29\u5dde\u5b59\u514b\u7528\uff0c\u9752\u5dde\u5468\u656c \u8206\uff0c\u8944\u57ce\u9ec4\u8865\u5eb5\u7b49\u90fd\u662f\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u8fd8\u6709\u5417\uff1f\u201d\u66fe\u9759\u53c8\u8bf4\u4e86\u51e0\u4e2a\u540d\u5b57\uff0c\u5e74\u7fb9\u5c27\u8fc7\u76ee\u6210 \u8bf5\uff0c\u4e0d\u987b\u7eb8\u7b14\uff0c\u5c06\u51e0\u4e2a\u540d\u5b57\u7d27\u8bb0\u5fc3\u4e2d\u3002 \u3000\u3000\u66fe\u9759\u4f9b\u4e86\u4e4b\u540e\uff0c\u5e74\u7fb9\u5c27\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u66fe\u8001\u5934\u513f\uff0c\u4f60\u60f3\u505a\u4ec0\u4e48\u5b98\uff1f\u201d\u66fe\u9759\u9762\u8272\u7070\u767d\uff0c \u53e9\u5934\u8bf4\u9053\uff1a\u201c\u6c42\u5927\u5e05\u5f00\u6069\uff0c\u5343\u4e07\u4e0d\u53ef\u903c\u6211\u505a\u5b98\uff01\u201d\u5e74\u7fb9\u5c27\u8be7\u9053\uff1a\u201c\u8fd9\u662f\u4e3a\u4f55\uff1f\u201d\u66fe\u9759\u9053\uff1a \u201c\u6211\u4e00\u505a\u5b98\uff0c\u5929\u4e0b\u4e4b\u4eba\uff0c\u5b9a\u77e5\u6211\u5356\u53cb\u6c42\u8363\u3002\u53ef\u601c\u5c0f\u8001\u513f\u6570\u5341\u5e74\u6765\u6240\u79ef\u7684\u58f0\u540d\uff0c\u4fbf\u8981\u4e00\u65e6\u4ed8\u4e4b \u6d41\u6c34\u4e86\uff1f\u201d\u7518\u51e4\u6c60\u6c14\u6124\u4e4b\u6781\uff0c\u53c8\u6697\u9a82\u4e86\u51e0\u58f0\u8be5\u6b7b\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c06\u4f60\u7684\u5fc3\u610f\u544a\u77e5\u7687\u4e0a\uff0c\u4f60\u4e0d\u505a\u5b98\u66f4\u597d\uff01\u201d\u540e\u6765\u96cd\u6b63\u7687\u5e1d\u679c\u7136\u5bf9\u66fe\u9759 \u5f20\u7199\u6beb\u4e0d\u5904\u7f5a\u3002\u5415\u7559\u826f\u4e00\u6848\uff0c\u5728\u6e05\u4ee3\u662f\u6709\u540d\u7684\u5927\u6848\uff0c\u682a\u8fde\u751a\u4f17\uff0c\u8fde\u520a\u523b\u5415\u6c0f\u4e66\u7c4d\u7684\u4eba\u90fd\u88ab\u4e0b \u72f1\u6216\u5904\u6b7b\uff0c\u53cd\u800c\u662f\u53d1\u52a8\u8c0b\u4e71\u7684\u66fe\u9759\u5f20\u7199\u4e8c\u4eba\uff0c\u5374\u5b89\u7136\u65e0\u4e8b\uff0c\u540e\u4e16\u8bfb\u6e05\u53f2\u4e4b\u4eba\u65e0\u4e0d\u5947\u602a\uff0c\u5374\u4e0d \u77e5\u5176\u4e2d\u522b\u6709\u539f\u6545\u3002 \u3000\u3000\u66fe\u9759\u8bf4\u5b8c\u4e4b\u540e\uff0c\u5c0f\u5fc3\u770b\u5e74\u7fb9\u5c27\u7684\u9762\u8272\u3002\u5e74\u7fb9\u5c27\u5ffd\u7136\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u66fe\u8001\u5148\u751f\u771f\u662f\u975e\u540c \u51e1\u4fd7\uff0c\u8bf7\u53d7\u6211\u5e74\u67d0\u4e00\u62dc\uff01\u201d\u66fe\u9759\u5927\u5403\u4e00\u60ca\uff0c\u907f\u5f00\u8bf4\u9053\uff1a\u201c\u8fd9\u5c82\u4e0d\u6298\u6740\u8001\u513f\u3002\u201d\u5e74\u7fb9\u5c27\u628a\u4ed6\u5f3a \u6309\u6905\u4e0a\uff0c\u62dc\u4e86\u4e09\u62dc\uff0c\u9053\uff1a\u201c\u66fe\u8001\u5148\u751f\uff0c\u6211\u8fd9\u4e00\u62dc\u4e43\u662f\u66ff\u5c0f\u513f\u884c\u62dc\u5e08\u4e4b\u793c\u3002\u7687\u4e0a\u5fc5\u7136\u6055\u4f60\u4e4b \u7f6a\uff0c\u4f60\u53ef\u4ee5\u7ec8\u8001\u6797\u6cc9\uff0c\u4f46\u6211\u6c42\u4f60\u5c06\u5c0f\u513f\u5e26\u8d70\u3002\u201d\u66fe\u9759\u6e58\u6cb9\u6b32\u8a00\uff0c\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u5927\u4e08\u592b\u4e00\u8a00\u4e3a \u5b9a\uff0c\u6211\u53ef\u4ee3\u7687\u4e0a\u5148\u4f5c\u4e3b\u5f20\uff0c\u660e\u65e5\u6211\u5c31\u9063\u4eba\u643a\u540c\u5c0f\u513f\uff0c\u968f\u4f60\u540c\u8d70\u3002\u53ef\u662f\u4f60\u5f97\u8bb0\u7740\uff0c\u7edd\u4e0d\u53ef\u4ee5\u5411 \u4eba\u8bf4\u662f\u6211\u7684\u513f\u5b50\uff0c\u5426\u5219\u5c31\u7b97\u6211\u4e0d\u8ffd\u7a76\uff0c\u4e5f\u81ea\u6709\u4eba\u53d6\u4f60\u7684\u9996\u7ea7\uff01\u201d\u66fe\u9759\u5413\u5f97\u51b7\u6c57\u76f4\u6d41\uff0c\u8fde\u9053\uff1a \u201c\u627f\u5927\u5e05\u6df1\u6069\uff0c\u6211\u5c82\u6562\u6cc4\u6f0f\u3002\u201d \u3000\u3000\u539f\u6765\u5e74\u7fb9\u5c27\u6df1\u8c0b\u8fdc\u8651\uff0c\u4ed6\u5c82\u4e0d\u77e5\u96cd\u6b63\u673a\u5fc3\u6781\u91cd\uff0c\u867d\u7136\u81ea\u5df1\u638c\u63e1\u91cd\u5175\uff0c\u8c05\u7687\u5e1d\u4e0d\u6562\u52a0\u5bb3\u3002 \u4f46\u4e5f\u4e0d\u80fd\u4e0d\u9884\u9632\u4e07\u4e00\u3002\u81ea\u4ece\u6709\u4e86\u513f\u5b50\uff0c\u5c31\u5343\u65b9\u767e\u8ba1\uff0c\u8981\u4e3a\u513f\u5b50\u5b89\u6392\u4e00\u6761\u540e\u8def\uff0c\u4f46\u5374\u59cb\u7ec8\u60f3\u4e0d \u51fa\u53ef\u4ee5\u6258\u59bb\u5bc4\u5b50\u7684\u4eba\u9009\u3002\u4eca\u665a\u89c1\u4e86\u66fe\u9759\u7684\u884c\u4e8b\uff0c\u5fc3\u4e2d\u6697\u559c\uff0c\u60f3\u9053\uff1a\u628a\u513f\u5b50\u6258\u4ed6\uff0c\u90a3\u771f\u662f\u6700\u597d \u4e0d\u8fc7\u3002\u7687\u4e0a\u65e2\u4e0d\u4f1a\u7591\u5fc3\u6211\u628a\u513f\u5b50\u6258\u7ed9\u4ed6\uff0c\u6c5f\u6e56\u4e0a\u4e0e\u6211\u5bf9\u5934\u7684\u53cd\u6e05\u4e4b\u58eb\u53c8\u90fd\u4ee5\u4e3a\u66fe\u9759\u662f\u4ed6\u4eec\u4e00 \u8def\u7684\u4eba\uff0c\u66f4\u4e0d\u4f1a\u60f3\u5230\u4ed6\u4f1a\u6536\u7559\u6211\u7684\u513f\u5b50\u3002\u4ed6\u53c8\u6015\u6b7b\uff0c\u6211\u6d3e\u5fc3\u8179\u8ddf\u4ed6\uff0c\u4ed6\u66f4\u7edd\u4e0d\u6562\u5bf9\u6211\u7684\u513f\u5b50 \u4e0d\u5229\u3002\u8fd9\u771f\u662f\u6700\u7406\u60f3\u4e0d\u8fc7\u7684\u4e86\u3002 \u3000\u3000\u7518\u51e4\u6c60\u5374\u731c\u4e0d\u900f\u5e74\u7fb9\u5c27\u7684\u7528\u610f\uff0c\u751a\u4e3a\u8be7\u5f02\u3002\u6b63\u5728\u601d\u7591\uff0c\u5ffd\u89c1\u5916\u8425\u7684\u5e10\u5e55\u4e0a\u4eba\u5f71\u4e00\u95ea\uff0c\u5b9a \u775b\u4e00\u770b\uff0c\u5374\u662f\u8463\u5de8\u5ddd\u3002\u8463\u5de8\u5ddd\u5949\u96cd\u6b63\u4e4b\u547d\u968f\u5e74\u7fb9\u5c27\u51fa\u5f81\uff0c\u6697\u4e2d\u53c8\u5949\u547d\u5c06\u5e74\u7fb9\u5c27\u76d1\u89c6\u3002\u4ed6\u5f97\u77e5 \u5e74\u7fb9\u5c27\u63d0\u8baf\u66fe\u9759\uff0c\u4e5f\u8d77\u4e86\u7591\u5fc3\uff0c\u53ef\u662f\u53c8\u4e0d\u6562\u50cf\u7518\u51e4\u6c60\u4e00\u6837\u5230\u5e74\u7fb9\u5c27\u7684\u8425\u5e10\u4e4b\u4e0a\u63c9\u542c\uff0c\u53ea\u6562\u8fdc \u8fdc\u7684\u5728\u5916\u8425\u5e10\u5e55\u63a2\u671b\uff0c\u4e0d\u6599\u5728\u6de1\u6de1\u7684\u6708\u5149\u4e0b\uff0c\u5374\u53d1\u73b0\u5e74\u7fb9\u5c27\u7684\u864e\u5e10\u4e0a\u4f0f\u6709\u4e00\u4eba\uff0c\u8463\u5de8\u5ddd\u6b66\u529f \u751a\u9ad8\uff0c\u4eba\u53c8\u8001\u5978\u5de8\u6ed1\uff0c\u5fc3\u4e2d\u987f\u65f6\u6709\u4e86\u4e3b\u610f\uff0c\u60f3\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u662f\u5426\u523a\u5ba2\uff0c\u6211\u4e14\u85c9\u6349\u523a\u5ba2\u4e3a\u540d\uff0c \u8fc7\u53bb\u4e00\u770b\u3002\u201d\u5f53\u4e0b\u7acb\u523b\u65bd\u5c55\u8f7b\u529f\u51fa\u6ca1\u58f0\u7684\u98de\u63a0\u8fc7\u53bb\u3002 \u3000\u3000\u5c82\u77e5\u7518\u51e4\u6c60\u4e5f\u662f\u673a\u7075\u5230\u6781\uff0c\u4f0f\u5728\u5e10\u89d2\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5047\u88c5\u6ca1\u6709\u53d1\u73b0\u3002\u5f85\u8463\u5de8\u5ddd\u5230\u4e86\u5e10\u9876\uff0c \u9661\u7136\u5c06\u5e10\u5e55\u4e00\u6380\uff0c\u53eb\uff1a\u201c\u6709\u523a\u5ba2\uff01\u201d\u500f\u7136\u98de\u51fa\uff0c\u6a2a\u638c\u4e00\u626b\uff0c\u8463\u5de8\u5ddd\u9aa4\u7136\u53d7\u60ca\uff0c\u88ab\u5e10\u5e55\u4e00\u5377\uff0c \u53c8\u88ab\u638c\u98ce\u4e00\u903c\uff0c\u7acb\u8db3\u4e0d\u7a33\uff0c\u8dcc\u843d\u5730\u4e0b\u3002\u5916\u9762\u7684\u536b\u58eb\u7eb7\u7eb7\u547c\u558a\u8d76\u5165\uff0c\u5e74\u7fb9\u5c27\u95fb\u58f0\u51fa\u6765\uff0c\u7518\u51e4\u6c60 \u5df2\u63a0\u51fa\u4e24\u91cd\u5e10\u5e55\u4e4b\u5916\uff0c\u548c\u5510\u6653\u6f9c\u6084\u6084\u6e9c\u56de\u4f19\u98df\u623f\u53bb\u4e86\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u4e00\u89c1\u662f\u8463\u5de8\u5ddd\uff0c\u7acb\u523b\u53d8\u4e86\u989c\u8272\u3002\u8463\u5de8\u5ddd\u9053\uff1a\u201c\u5c0f\u4eba\u6765\u62ff\u523a\u5ba2\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u523a \u5ba2\u5462\uff1f\u201d\u8463\u5de8\u5ddd\u9053\uff1a\u201c\u5df2\u7ecf\u8d70\u4e86\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f55\u4ee5\u4f60\u4e0d\u53eb\u558a\uff1f\u201d\u8463\u5de8\u5ddd\u9053\uff1a\u201c\u662f\u523a\u5ba2\u5148 \u53eb\uff01\u201d\u5e74\u7fb9\u5c27\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u519b\u4e2d\u5c3d\u662f\u6211\u7684\u5fc3\u8179\uff0c\u4f55\u6765\u523a\u5ba2\uff1f\u5929\u4e0b\u4e5f\u6ca1\u6709\u5982\u6b64\u5927\u80c6\u7684\u523a \u5ba2\uff0c\u6562\u5148\u53eb\u558a\u7684\u9053\u7406\u3002\u8fd9\u5206\u660e\u662f\u6211\u7684\u536b\u571f\u53d1\u73b0\u4e86\u4f60\uff0c\u7591\u662f\u523a\u5ba2\uff1f\u4ed6\u4eec\u5bf9\u6211\u5fe0\u5fc3\uff0c\u6240\u4ee5\u53eb \u558a\u3002\u201d\u6709\u4e9b\u4e89\u529f\u7684\u536b\u58eb\uff0c\u7eb7\u7eb7\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4eec\u6ca1\u6599\u5230\u662f\u8463\u5927\u7237\u3002\u201d\u8463\u5de8\u5ddd\u9762\u9752\u5507\u767d\uff0c\u6025 \u6025\u5206\u8fa9\uff0c\u5e74\u7fb9\u5c27\u51b7\u7b11\u9053\uff1a\u201c\u6211\u53c8\u6ca1\u6709\u53eb\u4f60\u5b88\u591c\uff0c\u4f55\u4ee5\u4f60\u4f1a\u534a\u591c\u51fa\u6765\u5de1\u8c2d\u3002\u77a7\u4f60\u8863\u5c65\u9f50\u6574\uff0c\u65ad \u65ad\u4e0d\u662f\u77e5\u6709\u523a\u5ba2\uff0c\u624d\u7a81\u7136\u4ece\u5e8a\u4e0a\u8df3\u8d77\u7684\uff0c\u51c6\u9053\u662f\u4f60\u65e9\u5c31\u6599\u5b9a\u6709\u523a\u5ba2\u4e48\uff1f\u201d\u8463\u5de8\u5ddd\u9002\u624d\u4e00\u65f6\u5fc3 \u6025\uff0c\u6b32\u85c9\u53e3\u4fa6\u67e5\uff0c\u6ca1\u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u65e0\u4ece\u5206\u8fa9\uff0c\u53ea\u53eb\u51a4\u6789\u3002\u5e74\u7fb9\u5c27\u51b7\u7b11\u9053\uff1a\u201c\u5373\u7b97\u4f60\u4e0d\u662f\u523a \u5ba2\uff0c\u79c1\u5230\u6211\u7684\u8425\u5e10\uff0c\u4e5f\u662f\u5fc3\u6000\u4e0d\u8f68\u3002\u5200\u65a7\u624b\uff0c\u5c06\u4ed6\u780d\u4e86\uff01\u201d\u8463\u5de8\u5ddd\u65e0\u4ece\u5206\u8fa9\uff0c\u4e5f\u65e0\u6cd5\u62b5\u6297\uff0c \u4efb\u4ed6\u733e\u4f3c\u72d0\u72f8\uff0c\u4e5f\u7ec8\u4e8e\u6b7b\u5728\u5e74\u7fb9\u5c27\u5200\u4e0b\u3002 \u3000\u3000\u7518\u51e4\u6c60\u77e5\u9053\u8463\u5de8\u5ddd\u4e27\u547d\uff0c\u4e0e\u5510\u6653\u6f9c\u629a\u638c\u800c\u7b11\u3002\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5f53\u5e74\u8463\u5de8\u5ddd\u5077\u4e0b\u6bd2\u624b\uff0c\u4ee4\u6c88 \u5728\u5bbd\u6b8b\u5e9f\uff0c\u5982\u4eca\u53d7\u4e86\u6b64\u62a5\u3002\u53eb\u5415\u59d0\u59d0\u77e5\u9053\uff0c\u4e00\u5b9a\u79f0\u5feb\u3002\u201d \u3000\u3000\u7ecf\u6b64\u4e00\u95f9\uff0c\u53c8\u662f\u4e00\u591c\u3002\u7518\u51e4\u6c60\u65e0\u6cd5\u8d70\u51fa\u519b\u8425\uff0c\u53ea\u597d\u968f\u5927\u519b\u8fdb\u57ce\u3002\u5230\u4e86\u5317\u4eac\u57ce\u5916\uff0c\u5ffd\u542c\u5f97 \u8f70\u9686\u9686\u4e09\u58f0\u70ae\u54cd\uff0c\u519b\u4e2d\u6709\u4eba\u62a5\u9053\u201c\u7687\u4e0a\u5fa1\u9a7e\u4eb2\u81ea\u51fa\u57ce\u6765\u4e86\u3002\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5c06\u519b\u6218\u7f62\u73ed\u5e08\u65e5\uff0c\u6b63\u662f\u541b\u738b\u8d77\u5fcc\u65f6\uff01 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e09\u56de\u3000\u6bc5\u529b\u8654\u5fc3\u3000\u5341\u5e74\u5f85\u77e5\u5df1\u3000\u76d7\u540d\u6b3a\u4e16\u3000\u4e00\u65e6\u73b0\u539f\u5f62 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u56db\u5341\u4e09\u56de \u6bc5\u529b\u8654\u5fc3\u3000\u5341\u5e74\u5f85\u77e5\u5df1 \u76d7\u540d\u6b3a\u4e16\u3000\u4e00\u65e6\u73b0\u539f\u5f62 \u3000\u3000\u8fd9\u65f6\u6b63\u662f\u4e09\u4f0f\u5929\u65f6\uff0c\u8d64\u65e5\u708e\u708e\uff0c\u72b9\u5982\u5728\u5929\u4e0a\u5f20\u7740\u4e00\u628a\u5927\u706b\u4f1e\u3002\u96cd\u6b63\u7687\u5e1d\u6446\u52a8\u92ae\u9a7e\uff0c\u8fce\u51fa \u57ce\u6765\uff0c\u5728\u92ae\u8206\u91cc\u70ed\u5f97\u4e00\u628a\u4e00\u628a\u6c57\u6dcc\u4e2a\u4e0d\u505c\uff0c\u51fa\u4e86\u57ce\u95e8\uff0c\u7687\u5e1d\u53c8\u5f03\u8f7f\u4e58\u9a6c\uff0c\u706b\u6bd2\u7684\u65e5\u5934\u76f4\u6652\u4e0b \u6765\uff0c\u70ed\u5f97\u8d8a\u53d1\u5389\u5bb3\u3002\u96cd\u6b63\u662f\u7ec3\u8fc7\u6b66\u529f\u7684\u4eba\uff0c\u4f53\u8d28\u5f3a\u58ee\uff0c\u5728\u6bd2\u65e5\u85b0\u84b8\u4e4b\u4e0b\uff0c\u867d\u662f\u96be\u53d7\uff0c\u4e5f\u8fd8\u4e0d \u89c9\u600e\u4e48\uff0c\u6709\u4e9b\u968f\u884c\u7684\u5c0f\u592a\u76d1\uff0c\u51e0\u4e4e\u5728\u8d64\u65e5\u4e4b\u4e0b\u6655\u5012\u3002 \u3000\u3000\u5e78\u597d\u5728\u5317\u4eac\u57ce\u5916\uff0c\u6709\u4e00\u7247\u5927\u6811\u6797\uff0c\u96cd\u6b63\u56de\u987e\u966a\u540c\u6765\u8fce\u63a5\u5e74\u7fb9\u5c27\u7684\u6587\u6b66\u767e\u5b98\uff0c\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u8d64\u65e5\u708e\u708e\uff0c\u4f60\u4eec\u4e5f\u8f9b\u82e6\u4e86\uff0c\u5c31\u5728\u8fd9\u91cc\u8bbe\u5e10\uff0c\u7b49\u5019\u5e74\u5927\u5c06\u519b\u5427\uff01\u201d\u5927\u81e3\u5f20\u5ef7\u7389\u9053\uff1a\u201c\u7687\u4e0a\u9f99 \u9a6c\u7cbe\u795e\uff0c\u771f\u975e\u5fae\u81e3\u7b49\u6240\u80fd\u53ca\u3002\u201d\u56fd\u8205\u9686\u79d1\u591a\u63a5\u53e3\u9053\uff1a\u201c\u7687\u4e0a\u4e0d\u8f9e\u708e\u70ed\uff0c\u5fa1\u9a7e\u52b3\u519b\uff0c\u8fd9\u771f\u662f\u65f7 \u53e4\u672a\u6709\u7684\u6b8a\u6069\uff0c\u5c06\u58eb\u4eec\u4e3a\u7687\u4e0a\u8d74\u6c64\u8e48\u706b\uff0c\u4e5f\u662f\u5fc3\u7518\u60c5\u613f\u7684\u4e86\u3002\u201d\u96cd\u6b63\u5fae\u5fae\u4e00\u7b11\uff0c\u4ed6\u5fa1\u9a7e\u52b3 \u519b\uff0c\u7528\u610f\u5c31\u6b63\u5728\u7b3c\u7edc\u519b\u5fc3\uff0c\u9686\u79d1\u591a\u8d81\u673a\u5949\u627f\uff0c\u6b63\u5408\u4ed6\u7684\u5fc3\u610f\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u6797\u5b50\u91cc\u5df2\u642d\u8d77\u9ec4\u7f0e\u5b50\u7684\u884c\u5e10\uff0c\u4e2d\u592e\u8bbe\u7740\u7687\u5e1d\u7684\u5b9d\u5ea7\uff0c\u96cd\u6b63\u4e0b\u9a6c\u5c31\u5750\uff0c\u592a\u76d1\u4eec \u5728\u5468\u56f4\u670d\u4f8d\uff0c\u6709\u7684\u6253\u6247\uff0c\u6709\u7684\u9012\u624b\u5dfe\uff0c\u6709\u7684\u732e\u51c9\u8336\uff0c\u8fc7\u4e86\u4e00\u4e9b\u65f6\u5019\uff0c\u542c\u5f97\u8fdc\u8fdc\u7684\u519b\u53f7\u54cd\u58f0\uff0c \u63a5\u7740\u662f\u8f70\u9686\u9686\u4e09\u58f0\u70ae\u54cd\uff0c\u524d\u7ad9\u8fce\u63a5\u7684\u5927\u5458\u98de\u9a6c\u56de\u6765\u62a5\u9053\uff1a\u201c\u5e74\u5927\u5c06\u519b\u73ed\u5e08\u56de\u671d\uff01\u201d \u3000\u3000\u96cd\u6b63\u6574\u4e86\u6574\u9f99\u51a0\u51e4\u5e26\uff0c\u8e31\u51fa\u884c\u5e10\uff0c\u53ea\u89c1\u524d\u9762\u65cc\u65d7\u5bf9\u5bf9\uff0c\u5251\u621f\u68ee\u68ee\uff0c\u4e8c\u5341\u4e07\u5927\u519b\uff0c\u56db\u4eba\u4e00 \u6392\uff0c\u8fe4\u9026\u5341\u4f59\u91cc\uff0c\u671b\u4e0d\u5c3d\u5934\uff01\u90a3\u524d\u950b\u90e8\u961f\uff0c\u5728\u70ed\u65e5\u4e0b\u4e00\u961f\u4e00\u961f\u7684\u8d70\u7740\uff0c\u9664\u4e86\u6574\u9f50\u4e4b\u6781\u7684\u811a\u6b65 \u58f0\u5916\uff0c\u8fde\u4e00\u58f0\u54b3\u55fd\u90fd\u542c\u4e0d\u89c1\u3002\u90a3\u4e9b\u5175\u58eb\u4eec\u8138\u4e0a\u7684\u6c57\u73e0\uff0c\u4e00\u9897\u9897\u50cf\u6c34\u73e0\u4e00\u6837\u6ef4\u4e0b\u6765\uff0c\u5374\u65e0\u4e00\u4eba \u6562\u7528\u624b\u62b9\u3002\u96cd\u6b63\u89c1\u4e86\uff0c\u53c8\u662f\u559c\u6b22\uff0c\u53c8\u662f\u5fc3\u8c0e\u3002\u5e74\u7fb9\u5c27\u6cbb\u519b\u4e4b\u4e25\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01 \u3000\u3000\u4e00\u961f\u961f\u7532\u80c4\u9c9c\u660e\u7684\u524d\u950b\u90e8\u961f\u8d70\u5230\u7687\u5e1d\u8ddf\u524d\uff0c\u884c\u8fc7\u519b\u793c\uff0c\u5de6\u53f3\u5206\u5f00\u3002\u519b\u4e2d\u53c8\u662f\u8f70\u9686\u9686\u4e09\u58f0 \u70ae\u54cd\uff0c\u4e2d\u95f4\u73b0\u51fa\u4e00\u9762\u5927\u65d7\uff0c\u65d7\u4e0a\u7ee3\u7740\u4e00\u4e2a\u7855\u5927\u65e0\u6bd4\u7684\u201c\u5e74\u201d\u5b57\uff0c\u53ea\u89c1\u5e74\u7fb9\u5c27\u9876\u76d4\u8d37\u7532\uff0c\u4e58\u7740 \u7eaf\u767d\u8272\u7684\u9a8f\u9a6c\uff0c\u7acb\u5728\u95e8\u65d7\u4e4b\u4e0b\uff0c\u5cb3\u949f\u742a\u5219\u52d2\u9a6c\u7acb\u5728\u5e74\u7fb9\u5c27\u53f3\u624b\u504f\u65c1\uff0c\u4e24\u4eba\u90fd\u662f\u795e\u91c7\u98de\u626c\uff0c\u4e1d \u6beb\u6ca1\u6709\u75b2\u5026\u7684\u98ce\u5c18\u4e4b\u8272\u3002 \u3000\u3000\u7687\u5e1d\u5fa1\u9a7e\u51fa\u8fce\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u4e24\u65c1\u6587\u6b66\u767e\u5b98\uff0c\u6587\u81ea\u5c1a\u4e66\u4f8d\u90ce\u4ee5\u4e0b\uff0c\u6b66\u81ea\u4e5d\u95e8\u63d0\u7763\u4ee5\u4e0b\uff0c\u90fd \u6309\u54c1\u7ea7\u7a7f\u7740\u87d2\u888d\u7bad\u8863\uff0c\u867d\u7136\u4e2a\u4e2a\u90fd\u70ed\u5f97\u6c57\u900f\u91cd\u8863\uff0c\u5374\u52a8\u4e5f\u4e0d\u6562\u4e00\u52a8\u3002\u7687\u5e1d\u80cc\u540e\u8fd8\u8ddf\u7740\u4e00\u73ed\u738b \u516c\u8d1d\u52d2\u548c\u6bbf\u9601\u5927\u5b66\u58eb\uff08\u6309\uff1a\u6e05\u4ee3\u4e0d\u8bbe\u5bb0\u76f8\uff0c\u51e0\u4e2a\u201c\u5927\u5b66\u58eb\u201d\u5206\u638c\u76f8\u6743\u3002\uff09\u4e5f\u90fd\u662f\u4e00\u4e2a\u4e2a\u9762\u5bb9 \u8083\u7a46\uff0c\u70ed\u5f97\u6697\u6697\u5598\u6c14\uff0c\u5374\u53c8\u4e0d\u6562\u5f04\u51fa\u58f0\u6765\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u4e00\u89c1\u96cd\u6b63\uff0c\u7acb\u5373\u8df3\u4e0b\u9a6c\u6765\u3002\u96cd\u6b63\u62ac\u624b\u8bf4\u9053\uff1a\u201c\u537f\u5bb6\u8fdc\u5f81\u8f9b\u82e6\u4e86\uff0c\u514d\u793c\uff0c\u5e73\u8eab\uff01\u201d \u5e74\u7fb9\u5c27\u8df3\u4e0b\u9a6c\u80cc\uff0c\u672c\u8be5\u530d\u5310\u884c\u793c\uff0c\u542c\u4e86\u96cd\u6b63\u4e4b\u8a00\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u6b20\u4e86\u6b20\u8eab\uff0c\u9053\uff1a\u201c\u5fae\u81e3\u52b3\u52a8\u5723 \u9a7e\uff0c\u809d\u8111\u6d82\u5730\uff0c\u4e0d\u8db3\u8a00\u62a5\uff01\u201d\u5cb3\u949f\u742a\u867d\u7136\u4e5f\u5f97\u96cd\u6b63\u53eb\u4ed6\u201c\u514d\u793c\u201d\uff0c\u5374\u8fd8\u662f\u530d\u5310\u5728\u5730\uff0c\u606d\u606d\u656c \u656c\u7684\u884c\u8fc7\u5927\u793c\u3002 \u3000\u3000\u96cd\u6b63\u53e3\u4e2d\u867d\u53eb\u4ed6\u4eec\u201c\u514d\u793c\u201d\uff0c\u5176\u5b9e\u53ea\u4e0d\u8fc7\u662f\u4e00\u79cd\u5ba2\u5957\u4e4b\u8f9e\uff0c\u4e0d\u610f\u5e74\u7fb9\u5c27\u679c\u7136\u6043\u529f\u800c\u9a84\uff0c \u4e0d\u884c\u5927\u793c\u3002\u96cd\u6b63\u751a\u4e0d\u8212\u670d\uff0c\u4f46\u8868\u9762\u4e0a\u5374\u4e0d\u73b0\u51fa\u534a\u70b9\u8f9e\u8272\uff0c\u53cd\u800c\u8d23\u5907\u5cb3\u949f\u742a\u4e0d\u542c\u4ed6\u7684\u5429\u5490\uff0c\u592a \u8fc7\u591a\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u53c8\u4e0d\u662f\u671d\u5802\u4e4b\u4e0a\uff0c\u4f46\u884c\u519b\u793c\u5df2\u8db3\uff0c\u4f55\u5fc5\u884c\u671d\u5ef7\u4e0a\u7684\u541b\u81e3\u4e4b\u793c\u5462\uff01\u5cb3\u5c06 \u519b\uff0c\u4f60\u8eab\u62ab\u91cd\u7532\uff0c\u530d\u4f0f\u884c\u793c\uff0c\u4e0d\u89c9\u5f97\u4e0d\u4fbf\u4e48\uff1f\u201d\u6253\u4e86\u4e24\u4e2a\u54c8\u54c8\uff0c\u4f3c\u662f\u73a9\u7b11\uff0c\u53c8\u4f3c\u8d23\u5907\uff0c\u5cb3\u949f \u742a\u8fde\u58f0\u544a\u7f6a\uff0c\u5fc3\u4e2d\u5374\u662f\u6697\u6697\u559c\u6b22\u3002\u60f3\u9053\uff1a\u4e0d\u6015\u4f60\u5e74\u7fb9\u5c27\u950b\u8292\u6bd5\u9732\uff0c\u6211\u7ec8\u987b\u4ee5\u201c\u611a\u62d9\u201d\u80dc\u4f60\u7684 \u806a\u660e\uff01 \u3000\u3000\u5e74\u7fb9\u5c27\u5cb3\u949f\u742a\u884c\u8fc7\u793c\u540e\uff0c\u63a5\u7740\u5c31\u662f\u90a3\u4e9b\u603b\u5175\u3001\u63d0\u9547\u3001\u534f\u9547\u3001\u90fd\u7edf\u7b49\u4e00\u73ed\u6b66\u5b98\uff0c\u4e00\u4e2a\u4e2a\u4e0a \u6765\u671d\u89c1\uff0c\u96cd\u6b63\u5429\u5490\u8d50\u5bb4\uff0c\u5e74\u7fb9\u5c27\u8ddf\u7740\u96cd\u6b63\u8d70\u8fdb\u884c\u5e10\uff0c\u4e00\u540c\u5750\u5e2d\uff0c\u90a3\u73ed\u738b\u516c\u5927\u5b66\u58eb\u8d1d\u52d2\u7b49\u5728\u5de6 \u53f3\u966a\u5bb4\u3002\u5cb3\u949f\u742a\u53ca\u4e00\u73ed\u51fa\u5f81\u5c06\u519b\uff0c\u5219\u7531\u4e5d\u95e8\u63d0\u7763\u5175\u90e8\u5c1a\u4e66\u548c\u4e00\u73ed\u5728\u4eac\u7684\u6b66\u5b98\u5728\u5e10\u5916\u5750\u5e2d\u3002\u5e2d \u4e2d\u96cd\u6b63\u95ee\u8d77\u897f\u5f81\u7684\u60c5\u5f62\uff0c\u5e74\u7fb9\u5c27\u6ed4\u6ed4\u4e0d\u7edd\uff0c\u5938\u8000\u6b66\u529f\uff0c\u96cd\u6b63\u542c\u4e86\uff0c\u66f4\u52a0\u4e0d\u60a6\u3002\u5e74\u7fb9\u5c27\u53c8\u594f \u9053\uff1a\u201c\u63d0\u7763\u5bcc\u5c71\u4e0d\u542c\u519b\u4ee4\uff0c\u4f8d\u536b\u8463\u5de8\u5ddd\u5bf9\u81e3\u65e0\u793c\uff0c\u5fae\u81e3\u4e0d\u53ca\u4e0a\u7980\uff0c\u90fd\u5df2\u5148\u884c\u8d50\u6b7b\u4e86\u3002\u201d\u96cd\u6b63 \u5403\u4e86\u4e00\u60ca\uff0c\u5374\u5fae\u7b11\u9053\uff1a\u201c\u519b\u4e2d\u4ee5\u519b\u4ee4\u6700\u5c0a\uff0c\u5927\u5c06\u5728\u5916\uff0c\u53ef\u4ee5\u4e13\u6743\uff0c\u8fd9\u70b9\u5c0f\u4e8b\uff0c\u4e0d\u7980\u62a5\u4e5f\u7f62 \u4e86\u3002\u201d\u5e74\u7fb9\u5c27\u6025\u6025\u8c22\u6069\uff0c\u96cd\u6b63\u53c8\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6715\u5f53\u65e5\u6d3e\u9063\u4e86\u56e0\u3001\u8428\u5929\u523a\u3001\u8428\u5929\u90fd\u3001\u8463\u5de8 \u5ddd\u3001\u7518\u5929\u9f99\u4e94\u4eba\u968f\u4f60\u897f\u5f81\uff0c\u5982\u4eca\u5df2\u5168\u6b7b\u4e86\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u96cd\u6b63\u4e00\u7b11\u9053\uff1a\u201c\u4e5f\u597d\uff0c \u4ed6\u4eec\u90fd\u662f\u91ce\u6027\u96be\u9a6f\uff0c\u9664\u4e86\u4e5f\u597d\u3002\u201d\u5e74\u7fb9\u5c27\u9aa4\u7136\u60f3\u8d77\u51fa\u5f81\u4e4b\u65f6\uff0c\u96cd\u6b63\u4e5f\u66fe\u8bb2\u8fc7\u8fd9\u756a\u8bf4\u8bdd\uff0c\u4f46\u5374 \u7279\u522b\u63d0\u5230\u8463\u5de8\u5ddd\u8f83\u8bc6\u5927\u793c\uff0c\u53eb\u4ed6\u5206\u522b\u5bf9\u5f85\uff0c\u800c\u4eca\u542c\u7687\u4e0a\u53c8\u518d\u63d0\u8d77\uff0c\u5fc3\u77e5\u4e0d\u5999\uff0c\u4f46\u7ec6\u5bdf\u7687\u4e0a\u9762 \u8272\uff0c\u5374\u65e0\u5f02\u5bb9\u3002\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8463\u5de8\u5ddd\u662f\u4f60\u6d3e\u5728\u6211\u519b\u7684\u5750\u63a2\uff0c\u4f60\u5f53\u6211\u4e0d\u77e5\u9053\u4e48\uff1f\u53ea\u8981\u6211\u4e00\u65e5\u5175 \u6743\u5728\u624b\uff0c\u4f60\u7ec8\u4e0d\u6562\u6740\u6211\u3002\u201d \u3000\u3000\u7687\u5e1d\u90ca\u8fce\uff0c\u8d50\u5bb4\u7edf\u5e05\uff0c\u4e0d\u8fc7\u662f\u4e00\u79cd\u4eea\u5f0f\uff0c\u4e09\u676f\u9152\u5403\u5b8c\u4e4b\u540e\uff0c\u4fbf\u544a\u64a4\u5e2d\u3002\u96cd\u6b63\u548c\u5e74\u7fb9\u5c27\u5e76 \u884c\u51fa\u6765\uff0c\u6170\u52b3\u5927\u519b\u3002\u8fd9\u65f6\u65e5\u5f53\u6b63\u5348\uff0c\u70ed\u5f97\u8d8a\u53d1\u5389\u5bb3\uff0c\u6797\u5b50\u5916\u9762\uff0c\u4e8c\u5341\u4e07\u5927\u519b\u5217\u961f\u6574\u9f50\uff0c\u76f4\u633a \u633a\u7684\u7ad9\u5728\u65e5\u5934\u5e95\u4e0b\u3002\u96cd\u6b63\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u5e2e\u5175\u58eb\uff0c\u4e2a\u4e2a\u7532\u80c4\u91cd\u91cd\uff0c\u8138\u4e0a\u88ab\u65e5\u5149\u6652\u5f97\u6cb9\u6ed1\u5149 \u4eae\uff0c\u5374\u52a8\u4e5f\u4e0d\u6562\u4e00\u52a8\u3002\u96cd\u6b63\u9053\uff1a\u201c\u4ed6\u4eec\u4e07\u91cc\u957f\u5f81\uff0c\u6371\u53d7\u96e8\u6dcb\u65e5\u6652\uff0c\u4e5f\u592a\u8f9b\u82e6\u4e86\u3002\u201d\u53eb\u4e00\u540d\u5185 \u76d1\u8fc7\u6765\uff0c\u5429\u5490\u4ed6\u9053\uff1a\u201c\u4f20\u8c15\u4e0b\u53bb\uff0c\u53eb\u4ed6\u4eec\u5feb\u5378\u4e86\u7532\u5427\uff01\u201d\u96cd\u6b63\u5429\u5490\u4e86\u5185\u76d1\u4e4b\u540e\uff0c\u4ecd\u548c\u5e74\u7fb9\u5c27 \u8bf4\u8bdd\u3002\u5e74\u7fb9\u5c27\u867d\u7136\u89c1\u5230\u7687\u5e1d\u5429\u5490\u5185\u76d1\uff0c\u4f46\u4e0d\u6562\u51d1\u8fc7\u53bb\u542c\uff0c\u6240\u4ee5\u4e0d\u77e5\u4ed6\u5429\u5490\u4ec0\u4e48\uff0c\u4ecd\u7136\u5174\u9ad8\u91c7 \u70c8\u7684\u5927\u8c08\u897f\u5f81\u6218\u7ee9\u3002 \u3000\u3000\u90a3\u5185\u76d1\u5f97\u4e86\u5723\u65e8\uff0c\u5fd9\u8d70\u51fa\u53bb\uff0c\u8de8\u4e0a\u9ad8\u5934\u5927\u9a6c\uff0c\u5728\u961f\u4f0d\u9762\u524d\u626c\u58f0\u53eb\u9053\uff1a\u201c\u7687\u4e0a\u6709\u65e8\uff0c\u5175\u58eb \u4eec\u5378\u7532\uff01\u201d\u58f0\u97f3\u98d8\u6563\uff0c\u90a3\u4e9b\u5175\u58eb\u4eec\u5982\u542c\u800c\u4e0d\u95fb\uff0c\u4ecd\u7136\u76f4\u633a\u633a\u7684\u7ad9\u7740\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff01\u90a3\u5185\u76d1\u614c \u4e86\uff0c\u63d0\u9ad8\u58f0\u97f3\u518d\u53eb\u9053\uff1b\u201c\u4e07\u5c81\u4f53\u6064\u4f60\u4eec\uff0c\u53eb\u4f60\u4eec\u5378\u7532\uff01\u201d\u4e8c\u5341\u4e07\u5927\u519b\u9759\u6084\u6084\u7684\uff0c\u6beb\u65e0\u4e00\u70b9\u58f0 \u54cd\uff0c\u53ea\u6709\u5185\u76d1\u7684\u58f0\u97f3\u5728\u7a7a\u6c14\u4e2d\u9707\u8361\u3002 \u3000\u3000\u8fd9\u771f\u662f\u65f7\u53e4\u672a\u6709\u4e4b\u4e8b\uff0c\u7687\u5e1d\u4eb2\u4e0b\u7684\u5723\u65e8\uff0c\u7adf\u7136\u5374\u5931\u6548\u529b\uff0c\u90a3\u540d\u5185\u76d1\u5413\u5f97\u5fc3\u810f\u60e7\u88c2\uff0c\u6da8\u7ea2 \u4e86\u8138\uff0c\u63a3\u5927\u5589\u5499\uff0c\u7b2c\u4e09\u6b21\u53eb\u9053\uff1a\u201c\u7687\u4e0a\u6709\u65e8\uff0c\u5175\u58eb\u4eec\u5378\u7532\uff01\u201d\u5c82\u77e5\u90a3\u73ed\u5175\u58eb\u4e2a\u4e2a\u4f3c\u6728\u5934\u4eba\u4e00 \u6837\uff0c\u5bf9\u4ed6\u6240\u4f20\u7684\u5723\u65e8\uff0c\u4ecd\u7136\u4e0d\u7406\u4e0d\u776c\uff01 \u3000\u3000\u8fd9\u60c5\u5f62\u96cd\u6b63\u4e5f\u6ce8\u610f\u5230\u4e86\uff0c\u9976\u4ed6\u662f\u4e00\u4ee3\u66b4\u541b\uff0c\u6df1\u6c89\u673a\u667a\uff0c\u8fd9\u65f6\u4e5f\u4e0d\u89c9\u5fc3\u614c\uff0c\u53d8\u4e86\u989c\u8272\u3002\u90a3 \u540d\u5185\u76d1\u7eb5\u9a6c\u9a70\u56de\uff0c\u4e00\u5230\u7687\u5e1d\u9762\u524d\uff0c\u7acb\u523b\u6eda\u4e0b\u9a6c\u6765\u8bf7\u7f6a\u3002\u5e74\u7fb9\u5c27\u5728\u65c1\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u5173\u4f60\u7684 \u4e8b\uff0c\u7f6a\u5728\u5c0f\u5c06\u3002\u201d\u96cd\u6b63\u4f55\u7b49\u806a\u660e\uff0c\u7acb\u523b\u4fbf\u77e5\u9053\u4e86\u5175\u58eb\u4eec\u4e0d\u80af\u5949\u547d\u5378\u7532\u7684\u539f\u56e0\uff0c\u5bf9\u5e74\u7fb9\u5c27\u9053\uff1a \u201c\u5929\u6c14\u592a\u70ed\uff0c\u5927\u5c06\u519b\u53ef\u4f20\u4ee4\u5175\u58eb\u4eec\u5378\u4e86\u7532\u5427\uff01\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u542c\u4e86\uff0c\u7b54\u58f0\u201c\u9075\u547d\u3002\u201d\u7f13\u7f13\u8d70\u51fa\uff0c\u5230\u4e86\u961f\u4f0d\u9762\u524d\uff0c\u4ece\u8896\u5b50\u91cc\u638f\u51fa\u4e00\u89d2\u5c0f\u5c0f\u7684\u7ea2 \u65d7\uff0c\u53ea\u8f7b\u8f7b\u4e00\u95ea\uff0c\u4fbf\u542c\u5f97\u534e\u5566\u5566\u4e00\u9635\u54cd\uff0c\u5982\u6ce2\u6d6a\u4e00\u822c\uff0c\u4ece\u524d\u950b\u4f20\u5230\u540e\u961f\uff0c\u4e8c\u5341\u4e07\u5927\u519b\uff0c\u4e00\u9f50 \u5378\u4e0b\u7532\u6765\uff0c\u4e00\u7247\u5e73\u9633\u4e0a\uff0c\u76d4\u7532\u987f\u65f6\u5806\u79ef\u5982\u5c71\uff01 \u3000\u3000\u96cd\u6b63\u770b\u4e86\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u8df3\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u8fd8\u4e86\u5f97\uff1f\u82e5\u7136\u5e74\u7fb9\u5c27\u53d8\u8d77\u5fc3\u6765\uff0c\u6715\u7684\u6027\u547d\u5c82\u4e0d\u662f \u5728\u4ed6\u7684\u638c\u63e1\u4e4b\u4e2d\u4e86\u4e48\uff1f\u201d\u5e74\u7fb9\u5c27\u5374\u662f\u5341\u5206\u5f97\u610f\uff0c\u8d70\u56de\u6765\u5bf9\u96cd\u6b63\u9053\uff1a\u201c\u519b\u4e2d\u53ea\u77e5\u6709\u519b\u4ee4\uff0c\u4e0d\u77e5 \u6709\u7687\u547d\uff0c\u8fd8\u8bf7\u965b\u4e0b\u660e\u9274\uff01\u201d\u968f\u4f8d\u7687\u5e1d\u5de6\u53f3\u7684\u4eb2\u738b\u8d1d\u52d2\u4e0e\u53ca\u5404\u90e8\u5c1a\u4e66\u4e5d\u95e8\u63d0\u7763\uff0c\u65e0\u4e0d\u53d8\u4e86\u989c \u8272\uff0c\u96cd\u6b63\u5374\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6307\u6325\u5927\u519b\uff0c\u5982\u81c2\u4f7f\u6307\uff0c\u5e74\u5927\u5c06\u519b\uff0c\u4f60\u771f\u7b97\u5f97\u662f\u81ea\u53e4\u4ee5\u6765\u7684\u7b2c\u4e00\u540d \u5c06\u4e86\uff0c\u5929\u964d\u5947\u624d\uff0c\u4e3a\u6211\u671d\u4fdd\u62a4\u6c5f\u5c71\uff0c\u771f\u4e43\u6715\u4e4b\u798f\u4e5f\uff01\u201d\u7b11\u58f0\u4e2d\u9690\u853d\u6740\u673a\uff0c\u90a3\u4e9b\u5927\u81e3\u5374\u8fd8\u61f5\u61f5 \u7136\u4e0d\u77e5\u7687\u4e0a\u771f\u610f\uff0c\u7eb7\u7eb7\u5411\u96cd\u6b63\u548c\u5e74\u7fb9\u5c27\u9053\u8d3a\uff0c\u8fde\u56fd\u8205\u9686\u79d1\u591a\u4e5f\u62c9\u7740\u4e86\u5e74\u7fb9\u5c27\u7684\u624b\uff0c\u5bf9\u4ed6\u5927\u62cd \u9a6c\u5c41\uff0c\u96cd\u6b63\u4e00\u4e00\u77a7\u5728\u5fc3\u5185\uff0c\u5374\u4e0d\u4f5c\u58f0\u3002 \u3000\u3000\u7b11\u95f9\u4e86\u4e00\u9635\uff0c\u96cd\u6b63\u53c8\u53eb\u5e74\u7fb9\u5c27\u4f20\u4e0b\u65e8\u53bb\uff0c\u6bcf\u540d\u5175\u58eb\u8d4f\u94f6\u5341\u4e24\uff0c\u897f\u5f81\u6709\u529f\u5c06\u58eb\u5404\u52a0\u4e00\u7ea7\uff0c \u5168\u519b\u653e\u5047\u5341\u5929\u3002\u5e74\u7fb9\u5c27\u8fd9\u56de\u4e0d\u6562\u8fc7\u4efd\u5356\u5f04\uff0c\u5e26\u9886\u5c06\u58eb\u4e09\u547c\u4e07\u5c81\u3002\u8fd9\u5fa1\u9a7e\u4eb2\u8fce\uff0c\u6170\u52b3\u5927\u519b\u7684\u4e00 \u6155\uff0c\u4fbf\u7b97\u7ec8\u4e86\u3002 \u3000\u3000\u7518\u51e4\u6c60\u4e0e\u5510\u6653\u6f9c\u6742\u5728\u540e\u961f\u7684\u4f19\u5934\u519b\u4e2d\uff0c\u5bf9\u8fd9\u5e55\u6d3b\u5267\uff0c\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5927\u519b\u653e\u5047\uff0c\u4ed6\u4eec\u4e5f \u8d81\u673a\u9003\u51fa\u519b\u8425\u3002\u4e24\u4eba\u79bb\u5f00\u5927\u4f19\u5df2\u6709\u4e09\u65e5\uff0c\u6050\u9632\u5415\u56db\u5a18\u8bb0\u6302\uff0c\u6025\u6025\u8d76\u56de\u897f\u5c71\u3002\u201d \u3000\u3000\u5728\u8fd9\u4e09\u5929\u5f53\u4e2d\uff0c\u897f\u5c71\u7684\u51b7\u7985\u50e7\u9662\uff0c\u5e73\u6dfb\u4e86\u8bb8\u591a\u5ba2\u4eba\uff0c\u51af\u745b\u51af\u7433\u5c06\u6bcd\u4eb2\u909d\u7ec3\u971e\u3001\u5916\u7956\u7236\u909d \u740f\u4ee5\u53ca\u5f20\u5929\u6c60\u7b49\u4e00\u5e2e\u4eba\u90fd\u63a5\u4e86\u8fc7\u6765\u3002\u909d\u740f\u3001\u5f20\u5929\u6c60\u7b49\u7ecf\u8fc7\u6570\u65e5\u4f11\u517b\uff0c\u5df2\u53ef\u8d70\u52a8\uff0c\u50e7\u9662\u91cc\u70ed\u95f9 \u975e\u5e38\uff0c\u5927\u5bb6\u90fd\u5728\u7b49\u7518\u51e4\u6c60\u7684\u6d88\u606f\u3002\u7518\u5510\u4e8c\u4eba\u4e00\u56de\uff0c\u4f17\u4eba\u7eb7\u7eb7\u6765\u95ee\uff0c\u7518\u51e4\u6c60\u628a\u63a2\u5e74\u7fb9\u5c27\u519b\u8425\u4e4b \u4e8b\u7ea6\u7565\u8bf4\u4e86\uff0c\u63a5\u7740\u53c8\u8bf4\u96cd\u6b63\u52b3\u519b\u4e4b\u4e8b\uff0c\u9c7c\u4eae\u6447\u5934\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u8fd9\u53ae\u4e5f\u592a\u80c6\u5927\u4e86\uff01\u201d\u5415\u56db\u5a18 \u9053\uff1a\u201c\u8fd9\u4e0d\u6b63\u597d\u5417\uff1f\u54b1\u4eec\u8981\u5bf9\u4ed8\u7684\u4e24\u4e2a\u5927\u4ec7\u4eba\uff0c\u4e00\u4e2a\u662f\u96cd\u6b63\uff0c\u4e00\u4e2a\u662f\u5e74\u7fb9\u5c27\uff0c\u4eca\u540e\u53ea\u987b\u4e13\u5fc3 \u5bf9\u4ed8\u96cd\u6b63\u4fbf\u884c\u4e86\u3002\u5e74\u7fb9\u5c27\u8fd9\u53ae\u81ea\u7136\u6709\u4eba\u6740\u4ed6\u3002\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u4ed6\u62e5\u6709\u5927\u519b\uff0c\u8c01\u80fd\u6740\u4ed6\uff1f\u201d\u767d\u6cf0 \u5b98\u9053\uff1a\u201c\u529f\u9ad8\u9707\u4e3b\uff0c\u5fc5\u7136\u6b7b\u4e8e\u975e\u547d\u3002\u60f3\u90a3\u6c49\u671d\u7684\u97e9\u4fe1\uff0c\u52a9\u5218\u90a6\u5f00\u56fd\u767b\u57fa\uff0c\u529f\u52b3\u6bd4\u5e74\u7fb9\u5c27\u66f4 \u5927\uff0c\u4e5f\u514d\u4e0d\u4e86\u5154\u6b7b\u72d7\u70f9\u4e4b\u96be\uff0c\u4f55\u51b5\u5e74\u7fb9\u5c27\u5462\uff1f\u201d \u3000\u3000\u9c7c\u4eae\u7b11\u9053\uff1a\u201c\u5230\u5e95\u662f\u8d24\u5a7f\u8bfb\u8fc7\u4e66\u7684\u4eba\u6709\u89c1\u8bc6\u3002\u597d\u5728\u6211\u5f97\u8bf8\u4f4d\u4e4b\u52a9\uff0c\u6ca1\u6709\u4e0a\u96cd\u6b63\u8fd9\u5c0f\u5b50\u7684 \u5f53\u3002\u201d \u3000\u3000\u4f17\u4eba\u8bae\u8bba\u7eb7\u7eb7\uff0c\u7518\u51e4\u6c60\u5c06\u5415\u56db\u5a18\u62c9\u8fc7\u4e00\u8fb9\uff0c\u628a\u66fe\u9759\u5982\u4f55\u8d2a\u751f\u6015\u6b7b\uff0c\u5c48\u670d\u62db\u4f9b\u7b49\u7b49\u4e8b\u60c5\u8bf4 \u4e86\uff0c\u5415\u56db\u5a18\u4e00\u542c\uff0c\u987f\u5982\u4e07\u7bad\u7a7f\u5fc3\uff0c\u82b1\u5bb9\u5931\u8272\u3002\u7518\u51e4\u6c60\u9053\uff1a\u201c\u770b\u6765\u6b64\u6848\u5fc5\u5174\u5927\u72f1\uff0c\u66fe\u9759\u5df2\u628a\u9996 \u8981\u8bf8\u4eba\u62db\u4f9b\u51fa\u6765\uff0c\u6211\u4eec\u5fc5\u987b\u53ca\u65e9\u901a\u77e5\u4ed6\u4eec\u9003\u907f\u3002\u201d\u5415\u56db\u5a18\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8fd9 \u4e8b\u7531\u6211\u6765\u529e\u597d\u4e86\uff0c\u516b\u59b9\uff0c\u4f60\u8eab\u8d1f\u56fd\u4ec7\u5bb6\u6068\uff0c\u8fd8\u671b\u8282\u54c0\u4e3a\u597d\u3002\u201d\u5415\u56db\u5a18\u4f4e\u9996\u5982\u75f4\uff0c\u6728\u7136\u4e0d\u8bed\u3002 \u7518\u51e4\u6c60\u9053\uff1a\u201c\u516b\u59b9\uff0c\u4f60\u662f\u5973\u4e2d\u4e08\u592b\uff0c\u4eba\u4e2d\u4fca\u6770\uff0c\u8fd8\u8981\u611a\u5144\u529d\u4e48\uff1f\u201d\u5415\u56db\u5a18\u7a81\u7136\u6602\u5934\u8bf4\u9053\uff1a \u201c\u6211\u82e5\u4e0d\u624b\u5203\u5141\u796f\u8fd9\u72d7\u7687\u5e1d\uff0c\u8a93\u4e0d\u4e3a\u4eba\u3002\u201d\u7518\u51e4\u6c60\u62cd\u624b\u9053\uff1a\u201c\u662f\u554a\uff0c\u8fd9\u624d\u4e0d\u6127\u662f\u5415\u7559\u826f\u7684\u5b59 \u5973\u3002\u53ef\u662f\uff0c\u5bab\u4e2d\u9632\u8303\u6b63\u4e25\uff0c\u5e74\u7fb9\u5c27\u5927\u519b\u53c8\u8fd1\u5728\u4eac\u7530\uff0c\u53ea\u6050\u4e0d\u6613\u884c\u4e8b\uff0c\u62a5\u4ec7\u4e0d\u4e89\u8fdf\u65e9\uff0c\u770b\u8fd9\u60c5 \u5f62\uff0c\u5e74\u7fb9\u5c27\u5fc5\u6709\u4e0e\u5141\u796f\u51b2\u7a81\u4e4b\u4e00\u65e5\uff0c\u7b49\u5230\u90a3\u65f6\uff0c\u624d\u662f\u6211\u4eec\u4e0b\u624b\u7684\u597d\u673a\u4f1a\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e03 \u54e5\u8bf4\u7684\u662f\u3002\u201d\u7518\u51e4\u6c60\u89c1\u5979\u58f0\u8c03\u8f83\u524d\u5e73\u9759\uff0c\u7565\u7565\u5bbd\u5fc3\u3002\u5415\u56db\u5a18\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6599\u4e0d\u5230\u66fe\u8001\u5934\u513f\u7adf \u4f1a\u5982\u6b64\uff01\u53ef\u662f\uff0c\u6211\u8fd8\u672a\u80af\u76f8\u4fe1\u5728\u5bbd\u5df2\u6b7b\u3002\u201d\u7518\u51e4\u6c60\u5fc3\u4e2d\u4e00\u9178\uff0c\u60f3\u9053\uff1a\u201c\u516b\u59b9\uff0c\u4f60\u4e5f\u592a\u75f4\u60c5 \u4e86\uff0c\u5728\u5bbd\u88ab\u66fe\u9759\u51fa\u5356\uff0c\u88ab\u6349\u81f3\u4eac\u5e08\u5904\u51b3\uff0c\u5e03\u544a\u5929\u4e0b\uff0c\u60ac\u9996\u4e5d\u95e8\uff0c\u6b7b\u4e8b\u7109\u80fd\u6709\u5047\uff1f\u201d\u53ef\u662f\u89c1\u5415 \u56db\u5a18\u5e84\u91cd\u7684\u6837\u5b50\uff0c\u4e0d\u5fcd\u4ee4\u5979\u4f24\u5fc3\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u53c8\u6536\u4e86\u56de\u53bb\u3002 \u3000\u3000\u5510\u6653\u6f9c\u77a5\u89c1\u5415\u56db\u5a18\u9762\u8272\u6709\u5f02\uff0c\u8d70\u4e86\u8fc7\u6765\uff0c\u95ee\u9053\uff1a\u201c\u5415\u59d0\u59d0\u4e0d\u8212\u670d\u4e48\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6ca1 \u4ec0\u4e48\u3002\u201d\u968f\u5373\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u8be5\u5230\u5c71\u4e1c\u53bb\u89c1\u6768\u4ef2\u82f1\u4e86\u3002\u201d\u5510\u6653\u6f9c\u9762\u4e0a\u4e00\u7ea2\uff0c\u5415\u56db\u5a18\u7b11\u9053\uff1a \u201c\u4e11\u5ab3\u5987\u7ec8\u987b\u89c1\u7fc1\u59d1\uff0c\u4f60\u8fd9\u50bb\u5973\u5a7f\u5c31\u4e0d\u6562\u89c1\u6cf0\u5c71\u5417\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u59d0\u59d0\u4f11\u5f00\u73a9\u7b11\u3002\u201d\u5415\u56db \u5a18\u77a5\u4e86\u5510\u6653\u6f9c\u4e00\u773c\uff0c\u53c8\u5bf9\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4e03\u54e5\uff0c\u6211\u8fd8\u6709\u51e0\u53e5\u8bdd\u548c\u4f60\u8bf4\u3002\u201d\u5510\u6653\u6f9c\u77e5\u8da3\u544a\u9000\uff0c\u7518 \u51e4\u6c60\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u5c06\u5510\u6653\u6f9c\u5a5a\u59fb\u4e0a\u7ea0\u7eb7\u8bf4\u4e86\uff0c\u7518\u51e4\u6c60\u9053\uff1a\u201c\u5514\uff0c\u539f\u6765\u5982\u6b64\uff01\u6768\u67f3\u9752\u6211\u89c1\u4e86\u4e5f\u8ba8\u538c\uff0c \u53ef\u662f\uff0c\u65e2\u7136\u8ba2\u5a5a\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u73b0\u5728\u624d\u9000\uff0c\u6015\u4e0d\u5927\u597d\u5427\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u9000\uff0c\u5c06\u6765\u540c\u5904 \u4e00\u751f\u5c82\u4e0d\u66f4\u96be\u3002\u201d\u7518\u51e4\u6c60\u5728\u5a5a\u59fb\u95ee\u9898\u4e0a\u6bd4\u8f83\u53e4\u677f\uff0c\u4e00\u5fc3\u4ee5\u4fa0\u4e49\u4e3a\u91cd\uff0c\u5fc3\u60f3\u6768\u4ef2\u82f1\u5bf9\u5510\u6653\u6f9c\u6709 \u6069\u6709\u4e49\uff0c\u8ba2\u5a5a\u8ba2\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u5ffd\u7136\u4e00\u65e6\u8bf4\u4e0d\u8981\u4eba\u5bb6\u7684\u5973\u513f\uff0c\u6b8a\u975e\u539a\u9053\uff0c\u53ef\u662f\u53c8\u60f3\u4e0d\u51fa\u8bdd\u6765\u9a73 \u5415\u56db\u5a18\uff0c\u53ea\u597d\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u5415\u56db\u5a18\u9053\uff1a\u201c\u6211\u660e\u65e5\u8981\u56de\u4ed9\u971e\uff0c\u63a2\u671b\u5728\u5bbd\u3002\u4e0d\u6015\u4f60\u89c1\u7b11\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u4e0d\u76f8\u4fe1\u5728\u5bbd\u5df2 \u6b7b\u3002\u672c\u6765\u6211\u662f\u8981\u5230\u5c71\u4e1c\u4eb2\u81ea\u89c1\u6768\u4ef2\u82f1\uff0c\u66ff\u5510\u6653\u6f9c\u89e3\u4e86\u8fd9\u4e2a\u96be\u9898\u7684\uff0c\u73b0\u5728\u53ea\u6709\u52b3\u70e6\u4f60\u66ff\u6211\u4e00\u8d70 \u4e86\u3002\u201d\u7518\u51e4\u6c60\u4e00\u5411\u656c\u91cd\u8fd9\u4e2a\u5e08\u59b9\uff0c\u5415\u56db\u5a18\u4eb2\u81ea\u6c42\u5230\uff0c\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u5f97\u7b54\u5e94\u3002\u4ff1\u9053\uff1a\u201c\u6211\u4e0d\u61c2 \u8bf4\u8bdd\uff0c\u66f4\u4e0d\u61c2\u66ff\u4eba\u9000\u4eb2\uff0c\u6211\u53ea\u4f9d\u4e8b\u76f4\u8bf4\u3002\u8bf4\u5510\u6653\u6f9c\u4e0e\u4ed6\u7684\u5973\u513f\u6027\u60c5\u4e0d\u5408\uff0c\u73b0\u5728\u5df2\u53e6\u6709\u4e86\u5fc3\u4e0a \u4e4b\u4eba\uff0c\u6768\u4ef2\u82f1\u82e5\u7136\u5927\u53d1\u813e\u6c14\uff0c\u6211\u5c31\u9a6c\u4e0a\u5f00\u6e9c\u3002\u201d\u5415\u56db\u5a18\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u4f60\u5c31\u8fd9\u6837\u8bf4\u5427\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u6c89\u541f\u534a\u664c\uff0c\u53c8\u9053\uff1a\u201c\u4e8b\u6709\u7f13\u6025\u8f7b\u91cd\uff0c\u6211\u5148\u5f97\u8bbe\u6cd5\u901a\u77e5\u5df2\u88ab\u66fe\u9759\u62db\u4f9b\u51fa\u6765\u7684\u8bf8\u4eba\u907f \u7978\uff0c\u7136\u540e\u624d\u80fd\u7ba1\u5230\u6653\u6f9c\u7684\u513f\u5973\u7ea0\u7eb7\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u4e0e\u7518\u51e4\u6c60\u5546\u91cf\u672a\u5df2\uff0c\u4f17\u4eba\u4e5f\u5728\u8bae\u8bba\u7eb7\u7eb7\uff0c\u51b7\u7985\u56b7\u9053\uff1a\u201c\u7518\u5927\u54e5\uff0c\u4f60\u6bc1\u4e86\u6211\u4eec\u7684\u4f5b \u95e8\u5723\u5730\uff0c\u4f60\u987b\u5f97\u8d54\u7ed9\u6211\u5b89\u8eab\u7acb\u547d\u4e4b\u6240\u5475\u3002\u201d\u7518\u51e4\u6c60\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u8fd9\u7834\u7834\u70c2\u70c2\u7684\u5bfa\u9662\uff0c\u672c \u6765\u5c31\u662f\u8fd9\u6837\u5b50\u7684\u561b\u3002\u201d\u51b7\u7985\u7b11\u9053\uff1a\u201c\u4e8f\u4f60\u662f\u8001\u6c5f\u6e56\u4e86\uff0c\u8fde\u8fd9\u70b9\u90fd\u4e0d\u660e\u767d\u5417\uff1f\u4f60\u4eec\u5728\u4eac\u4e2d\u63a5\u4e8c \u8fde\u4e09\u7684\u5927\u95f9\uff0c\u8fd9\u5730\u65b9\u53c8\u4e0d\u662f\u8352\u50fb\u4e4b\u6240\uff0c\u8fd9\u4e48\u591a\u4eba\u805a\u5728\u6b64\u5730\uff1b\u7109\u80fd\u907f\u5f97\u8fc7\u671d\u5ef7\u7684\u8033\u76ee\u3002\u96cd\u6b63\u8fd9 \u5c0f\u5b50\u8fde\u5c11\u6797\u5bfa\u4e5f\u6562\u70e7\uff0c\u4f55\u51b5\u6211\u8fd9\u70c2\u5e99\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u4e0d\u9519\uff0c\u8fd9\u91cc\u4e0d\u80fd\u957f\u4f4f\u4e0b\u53bb\u4e86\uff0c\u54b1\u4eec\u90fd\u53e6\u5916\u627e\u5730\u65b9 \u5427\u3002\u201d\u51b7\u7985\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u73b0\u6210\u7684\u5730\u65b9\uff0c\u53ef\u4ee5\u5bb9\u7eb3\u8fd9\u4e48\u591a\u4eba\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f60\u522b\u6253\u5c94\uff0c\u6211 \u65e9\u5df2\u6709\u4e86\u5b89\u6392\uff0c\u524d\u4e9b\u65e5\u5b50\u6211\u63a5\u5230\u5468\u6e05\u4e8c\u54e5\u7684\u53e3\u4fe1\uff0c\u8bf4\u4ed6\u4e0e\u66f9\u56db\u54e5\u5df2\u5230\u9099\u5c71\u9690\u5c45\uff0c\u770b\u5b88\u5e08\u5085\u7684 \u9675\u5893\u3002\u9099\u5c71\u7ef5\u5ef6\u6570\u767e\u91cc\uff0c\u5c71\u9ad8\u6797\u5bc6\uff0c\u5f62\u52bf\u9669\u5cfb\uff0c\u5c71\u8c37\u571f\u5730\u80a5\u7f8e\uff0c\u53ef\u4ee5\u8015\u79cd\u3002\u6211\u4eec\u90fd\u5230\u9099\u5c71\u53bb \u5982\u4f55\uff1f\u201d\u5f20\u5929\u6c60\u9996\u5148\u8bf4\u9053\uff1a\u201c\u65e2\u6709\u8fd9\u6837\u597d\u7684\u5730\u65b9\uff0c\u5982\u4f55\u4e0d\u53bb\uff1f\u6211\u505a\u4e86\u534a\u751f\u5f3a\u76d7\uff0c\u65e0\u6cd5\u4e0b\u53f0\uff0c \u6b63\u597d\u548c\u5144\u5f1f\u4eec\u9690\u5c45\u9099\u5c71\uff0c\u8015\u79cd\u6e21\u6d3b\u3002\u201d\u9c7c\u4eae\u4e5f\u9053\uff1a\u201c\u6211\u4ee5\u524d\u505a\u6d77\u4e0a\u9738\u738b\uff0c\u5927\u4e0d\u4e86\u5c06\u6765\u518d\u505a\u5c71 \u5927\u738b\uff0c\u6709\u8fd9\u4e48\u591a\u6b66\u827a\u9ad8\u5f3a\u7684\u597d\u6c49\uff0c\u6211\u548c\u5f20\u5be8\u4e3b\u518d\u53ec\u96c6\u4e00\u4e9b\u65e7\u90e8\uff0c\u4fbf\u5728\u9099\u5c71\u5360\u5c71\u4e3a\u738b\uff0c\u8c05\u671d\u5ef7 \u4e5f\u4e0d\u6562\u5c0f\u89d1\u3002\u201d\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u90a3\u662f\u540e\u8bdd\u3002\u73b0\u5728\u4e0d\u5fc5\u6025\u4e8e\u5f00\u5c71\u7acb\u8235\u3002\u90a3\u4e48\uff0c\u5b89\u8eab\u4e4b\u5730\u4fbf\u51b3\u5b9a \u662f\u9099\u5c71\u4e86\u3002\u201d\u51b7\u7985\u7b11\u9053\uff1a\u201c\u597d\u3002\u6211\u4eec\u90fd\u66ff\u4f60\u7684\u5e08\u5085\u5b88\u9675\u53bb\u3002\u5979\u662f\u524d\u8f88\u795e\u5c3c\uff0c\u6211\u4eec\u66ff\u5979\u5b88\u9675\uff0c \u4e5f\u8fd8\u503c\u5f97\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u65e5\uff0c\u5415\u56db\u5a18\u4e00\u4e2a\u4eba\u5148\u56de\u4ed9\u971e\uff0c\u6b63\u4e0e\u4f17\u4eba\u8f9e\u884c\uff0c\u63e1\u624b\u9053\u522b\u4e4b\u9645\uff0c\u5355\u5355\u4e0d\u89c1\u4e86\u51af\u7433\u3002\u51af \u745b\u9053\uff1a\u201c\u59b9\u59b9\u771f\u4e0d\u61c2\u4e8b\uff0c\u660e\u660e\u77e5\u9053\u5415\u59d0\u59d0\u4eca\u65e5\u8fdc\u884c\uff0c\u5374\u4e0d\u77e5\u5230\u54ea\u91cc\u53bb\u73a9\uff1f\u201d\u5415\u56db\u5a18\u5fc3\u5ff5\u4e00 \u52a8\uff0c\u9053\uff1a\u201c\u51af\u7433\u59b9\u59b9\u4e0d\u662f\u4e0d\u61c2\u4e8b\uff0c\u6050\u6015\u662f\u592a\u61c2\u4e8b\u4e86\u3002\u4f60\u4eec\u4e0d\u5fc5\u53bb\u627e\u5979\u4e86\u3002\u6211\u770b\u5979\u4e00\u5b9a\u662f\u5077\u5077 \u6e9c\u8d70\uff0c\u5e72\u5979\u60f3\u5e72\u7684\u4e8b\u53bb\u4e86\u3002\u201d\u51af\u745b\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5979\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u5e72\uff1f\u4e3a\u4f55\u8fde\u6211\u4e5f\u4e0d\u544a \u8bc9\uff1f\u201d\u5415\u56db\u5a18\u5fae\u7b11\u4e0d\u8bed\u3002\u674e\u6cbb\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u5979\u6628\u665a\u95ee\u6211\u662f\u5426\u968f\u5927\u4f19\u5230\u9099\u5c71\uff0c\u6211\u8bf4\u662f\u3002\u5979 \u8bf4\u9099\u5c71\u5f88\u597d\uff0c\u5979\u66fe\u4ece\u5c71\u4e0b\u7ecf\u8fc7\u3002\u6211\u5f53\u65f6\u4e5f\u4e0d\u77e5\u9053\u5979\u662f\u4ec0\u4e48\u610f\u601d\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\u3002 \u5979\u5c06\u6765\u4f1a\u4e0a\u9099\u5c71\u627e\u4f60\u4eec\u7684\uff0c\u4e0d\u5fc5\u5fc3\u7126\u3002\u201d\u51af\u745b\u518d\u4e09\u95ee\u5415\u56db\u5a18\u53ef\u77e5\u51af\u7433\u60f3\u5e72\u4ec0\u4e48\uff1f\u5415\u56db\u5a18\u9053\uff1a \u201c\u6211\u4e5f\u53ea\u662f\u80e1\u731c\u4e00\u6c14\uff0c\u4e0d\u80fd\u4f5c\u51c6\u3002\u5979\u505a\u7684\u4f60\u65e5\u540e\u81ea\u77e5\uff0c\u5148\u8bf4\u51fa\u6765\u53cd\u800c\u4e0d\u597d\u3002\u201d\u51af\u745b\u751a\u662f\u806a \u660e\uff0c\u60f3\u4e86\u7247\u523b\uff0c\u731c\u5230\u51e0\u5206\uff0c\u4e0d\u89c9\u9762\u7ea2\u8033\u8d64\u3002 \u3000\u3000\u5415\u56db\u5a18\u4e00\u7b11\u544a\u8f9e\uff0c\u79bb\u5f00\u4eac\u57ce\uff0c\u8d76\u56de\u4ed9\u971e\uff0c\u5979\u811a\u7a0b\u5feb\u6377\uff0c\u4e00\u4e2a\u6708\u540e\u5df1\u4ece\u6cb3\u5317\u7ecf\u8fc7\u5c71\u4e1c\uff0c\u518d \u7a7f\u8fc7\u6c5f\u82cf\u6d59\u6c5f\u4e8c\u7701\uff0c\u8fdb\u4eba\u798f\u5efa\u5317\u90e8\u3002 \u3000\u3000\u4ed9\u971e\u5cad\u6a2a\u4e98\u6d59\u6c5f\u798f\u5efa\u4e24\u7701\u4ea4\u754c\u4e4b\u5904\uff0c\u633a\u62d4\u79c0\u4e3d\uff0c\u4e00\u5165\u798f\u5efa\u5317\u90e8\uff0c\u5df2\u662f\u9065\u9065\u53ef\u89c1\u3002\u5415\u56db\u5a18 \u867d\u7136\u575a\u4fe1\u6c88\u5728\u5bbd\u6ca1\u6709\u6b7b\uff0c\u53ef\u662f\u884c\u7a0b\u8d8a\u8fd1\uff0c\u5fc3\u60c5\u4e5f\u4e0d\u89c9\u52a8\u8361\u4e0d\u5b89\uff0c\u751f\u6015\u63ed\u5f00\u4e86\u7684\u201c\u8c1c\u201d\u548c\u81ea\u5df1 \u7684\u6599\u60f3\u76f8\u53cd\u3002 \u3000\u3000\u4e00\u65e5\u4e2d\u5348\uff0c\u5415\u56db\u5a18\u6b63\u5728\u8def\u8fb9\u4e00\u95f4\u8336\u4ead\u6b47\u811a\uff0c\u5ffd\u89c1\u5927\u8def\u4e0a\u5c18\u6c99\u98de\u626c\uff0c\u4e00\u5339\u9a6c\u98de\u5954\u800c\u6765\uff0c\u9a6c \u4e0a\u4eba\u662f\u4e2a\u866c\u9aef\u58ee\u6c49\uff0c\u8c8c\u9887\u5a01\u6b66\uff0c\u8d70\u5230\u8336\u4ead\uff0c\u52d2\u9a6c\u56db\u987e\uff0c\u8336\u4eab\u5185\u53ea\u6709\u5be5\u5be5\u51e0\u4e2a\u8336\u5ba2\uff0c\u5415\u56db\u5a18\u643d \u4e86\u6613\u5bb9\u4e39\uff0c\u626e\u6210\u4e61\u4e0b\u7684\u91c7\u8336\u59d1\u5a18\uff0c\u90a3\u4eba\u770b\u4e86\u4e00\u773c\uff0c\u4e5f\u4e0d\u5728\u610f\uff0c\u4fbf\u4e0b\u4e86\u9a6c\u8fdb\u6765\u559d\u8336\u3002\u8fc7\u4e86\u4e00 \u9635\uff0c\u53c8\u6765\u4e86\u4e24\u4e58\u5c0f\u8f7f\uff0c\u90fd\u5230\u8336\u4ead\u9762\u524d\u6b47\u811a\u3002\u8f7f\u95e8\u5f00\u5904\uff0c\u5415\u56db\u5a18\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u4ece\u5148\u5934\u90a3\u4e58 \u8f7f\u8d70\u51fa\u6765\u7684\u7adf\u662f\u66fe\u9759\uff0c\u4ece\u540e\u5934\u90a3\u4e58\u8f7f\u8d70\u51fa\u6765\u7684\u5374\u662f\u4e00\u4e2a\u957f\u968f\u6a21\u6837\u7684\u4eba\uff0c\u80cc\u7740\u4e00\u4e2a\u5c0f\u5b69\u3002 \u3000\u3000\u5415\u56db\u5a18\u80cc\u8fc7\u8138\u53bb\uff0c\u4f4e\u5934\u56bc\u8336\uff0c\u6309\u8bf4\u6b64\u65f6\u5415\u56db\u5a18\u82e5\u8981\u53d6\u66fe\u9759\u6027\u547d\uff0c\u6613\u5982\u53cd\u638c\uff0c\u53ea\u56e0\u4e00\u6765\u987e \u5ff5\u4ed6\u5e74\u5df2\u8001\u8fc8\uff0c\u4e8c\u6765\u5ff5\u53ca\u4ed6\u4e0e\u81ea\u5df1\u7684\u7236\u4eb2\u53d4\u4f2f\u603b\u7b97\u505a\u8fc7\u4e00\u573a\u670b\u53cb\uff0c\u6240\u4ee5\u6012\u4e0a\u5fc3\u5934\uff0c\u4ecd\u7136\u6291 \u4f4f\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u53c8\u6709\u4e00\u9a91\u9a6c\u6765\u8336\u4ead\u6b47\u8db3\uff0c\u9a6c\u4e0a\u4eba\u53c8\u662f\u4e00\u4e2a\u6b66\u58eb\u3002 \u3000\u3000\u66fe\u9759\u662f\u6e56\u5357\u4eba\uff0c\u66fe\u5728\u4e24\u6e56\u6c5f\u897f\u798f\u5efa\u7b49\u7701\u8bb2\u5b66\uff0c\u540d\u58f0\u751a\u5927\uff0c\u77e5\u8005\u9887\u591a\u3002\u5750\u4e86\u4e00\u4f1a\uff0c\u4fbf\u6709\u4e00 \u4e2a\u79c0\u624d\u6a21\u6837\u7684\u8336\u5ba2\uff0c\u606d\u606d\u656c\u656c\u7684\u4e0a\u524d\u884c\u793c\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u53ef\u662f\u66fe\u8001\u5148\u751f\u4e48\uff1f\u201d\u66fe\u9759\u5fae\u5fae\u70b9\u4e86 \u70b9\u5934\uff0c\u90a3\u4eba\u9053\uff1a\u201c\u665a\u751f\u4ee5\u524d\u66fe\u968f\u4e61\u4e2d\u524d\u8f88\u542c\u5148\u751f\u8bb2\u8fc7\u5b66\u3002\u201d\u66fe\u9759\u53c8\u5fae\u5fae\u70b9\u5934\uff0c\u663e\u5f97\u5fc3\u795e\u4e0d\u5fc5 \u7684\u6837\u5b50\uff0c\u90a3\u4eba\u53c8\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u98ce\u628a\u66fe\u8001\u5148\u751f\u9001\u5230\u8fd9\u91cc\uff1f\u53ef\u6709\u518d\u5728\u53bf\u57ce\u91cc\u8bb2\u5b66\u51e0\u5929\u4e4b\u610f\u4e48\uff1f\u201d \u66fe\u9759\u9053\uff1a\u201c\u6211\u5728\u5317\u65b9\u6709\u4f4d\u597d\u53cb\uff0c\u4ed6\u6b7b\u4e86\u9057\u5b64\u6ca1\u4eba\u7167\u7ba1\uff0c\u6211\u6b64\u6b21\u7279\u5730\u5317\u4e0a\u628a\u90a3\u5b69\u5b50\u6536\u517b\uff0c\u8def\u7ecf \u6b64\u5730\uff0c\u5fc3\u6025\u8fd8\u4e61\uff0c\u987e\u4e0d\u5f97\u8bb2\u5b66\u4e86\u3002\u201d\u90a3\u4eba\u8fde\u8fde\u8d5e\u9053\uff1a\u201c\u5148\u751f\u9ad8\u4e49\uff0c\u53ef\u98ce\u53e4\u4eba\uff0c\u665a\u8f88\u4e0d\u80dc\u4f69 \u670d\u3002\u201d\u66fe\u9759\u5fae\u5fae\u4e00\u7b11\u3002\u5415\u56db\u5a18\u4fa7\u8033\u542c\u4ed6\u8bf4\u8bdd\uff0c\u84e6\u7136\u548c\u66fe\u9759\u76ee\u5149\u76f8\u63a5\uff0c\u66fe\u9759\u4e0e\u5415\u56db\u5a18\u751a\u719f\uff0c\u867d \u7136\u5979\u643d\u4e86\u6613\u5bb9\u4e39\uff0c\u795e\u6001\u4e4b\u95f4\uff0c\u5374\u5c1a\u4f9d\u7a00\u53ef\u8ba4\u3002\u66fe\u9759\u4e00\u89c1\uff0c\u7b11\u5bb9\u7acb\u655b\uff0c\u653e\u4e0b\u8336\u676f\u9053\uff1a\u201c\u6211\u8be5\u8d70 \u4e86\u3002\u201d\u5148\u524d\u8fdb\u5e97\u7684\u90a3\u540d\u866c\u9aef\u793e\u6c49\u7acb\u523b\u7b56\u9a6c\u5148\u884c\uff0c\u66fe\u9759\u4e0a\u8f7f\u8d70\u540e\u4e0d\u4e45\uff0c\u540e\u6765\u7684\u90a3\u540d\u6b66\u58eb\u4e5f\u4e0a\u9a6c \u8d70\u4e86\u3002\u66fe\u9759\u4e0e\u8fd9\u4e8c\u4eba\u59cb\u7ec8\u6ca1\u6709\u4ea4\u8c08\uff0c\u88c5\u505a\u4e0d\u76f8\u8bc6\u7684\u6837\u5b50\uff0c\u5415\u56db\u5a18\u5fc3\u4e2d\u6697\u7b11\uff0c\u77e5\u9053\u8fd9\u4e24\u540d\u6b66\u571f \u4e00\u5b9a\u662f\u5e74\u7fb9\u5c27\u6d3e\u6765\u6697\u62a4\u66fe\u9759\uff0c\u517c\u76d1\u89c6\u4ed6\u7684\u3002 \u3000\u3000\u90a3\u79c0\u624d\u6a21\u6837\u7684\u8336\u5ba2\u76ee\u9001\u66fe\u9759\u8d70\u540e\uff0c\u8fd8\u81ea\u4e0d\u65ad\u7684\u548c\u8336\u4ead\u5185\u7684\u51e0\u4e2a\u8336\u5ba2\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u66fe\u8001\u5148 \u751f\uff0c\u9053\u5fb7\u6587\u7ae0\uff0c\u540d\u6ee1\u6d77\u5185\uff0c\u800c\u53c8\u6e05\u9ad8\u6de1\u6cca\uff0c\u4e0d\u6c42\u95fb\u8fbe\uff0c\u771f\u662f\u56fd\u4e2d\u8d24\u4eba\uff0c\u5c71\u4e2d\u9ad8\u58eb\u3002\u201d\u5415\u56db\u5a18 \u5fc3\u4e2d\u8fde\u8fde\u51b7\u7b11\uff0c\u4e0d\u8010\u70e6\u542c\uff0c\u5306\u5306\u4ed8\u4e86\u8336\u94b1\uff0c\u8d70\u51fa\u8336\u4eab\u3002 \u3000\u3000\u5415\u56db\u5a18\u770b\u66fe\u9759\u4ed6\u4eec\u53bb\u7684\u65b9\u5411\u662f\u84b2\u57ce\uff0c\u65b9\u5411\u4e43\u662f\u80cc\u7740\u4ed9\u971e\u5cad\u800c\u884c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u597d\u5728\u6211\u548c \u4ed6\u7684\u65b9\u5411\u4e0d\u540c\uff0c\u8fd9\u8001\u5339\u592b\uff0c\u6211\u5b9e\u5728\u8ba8\u538c\u89c1\u4ed6\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u811a\u7a0b\u751a\u5feb\uff0c\u65e5\u843d\u4e4b\u524d\uff0c\u5df2\u5230\u4ed9\u971e\u5cad\u4e0b\uff0c\u4f46\u89c1\u5cf0\u5ce6\u95f4\u4e91\u96fe\u64a9\u7ed5\uff0c\u5343\u53d8\u4e07\u5316\uff0c\u5e7b\u6210\u5404 \u79cd\u666f\u7269\u3002\u5fc3\u60c5\u987f\u65f6\u7d27\u5f20\uff0c\u60f3\u8d77\u4e86\u6614\u65e5\u548c\u6c88\u5728\u5bbd\u540c\u770b\u4e91\u6d77\u7684\u60c5\u666f\u3002\u5bf9\u4e0d\u77e5\u5982\u4eca\u5728\u5bbd\u505a\u4e9b\u4ec6\u4e48\uff1f \u662f\u72ec\u501a\u4e39\u67ab\uff0c\u8fd8\u662f\u9065\u89c2\u4e91\u6d77\uff1f\u5415\u56db\u5a18\u4e00\u8def\u601d\u91cf\uff0c\u4e0d\u89c9\u5df2\u5230\u534a\u5c71\uff0c\u8fce\u9762\u4e00\u5927\u7247\u5ca9\u77f3\uff0c\u77f3\u7684\u989c\u8272 \u4e00\u7247\u901a\u7ea2\uff0c\u8fd9\u662f\u4ed9\u971e\u5cad\u4e0a\u4e00\u5904\u540d\u80dc\uff0c\u540d\u53eb\u201c\u4e39\u971e\u5d82\u201d\uff0c\u5415\u56db\u5a18\u4ee5\u524d\u5728\u4ed9\u971e\u5cad\u65f6\uff0c\u6700\u559c\u6b22\u5728 \u201c\u5d82\u201d\u4e0b\u6563\u6b65\uff0c\u800c\u4eca\u7ecf\u8fc7\uff0c\u514d\u4e0d\u4e86\u62ac\u5934\u4e00\u671b\uff0c\u5374\u4e0d\u6599\u8fd9\u4e00\u671b\uff0c\u53c8\u53d1\u73b0\u4e86\u60ca\u4eba\u7684\u5947\u8ff9\u3002 \u3000\u3000\u90a3\u7247\u5ca9\u77f3\u603b\u6709\u4e03\u516b\u4e08\u9ad8\uff0c\u672c\u6765\u662f\u5e73\u6ed1\u65e0\u5c18\u7684\uff0c\u800c\u4eca\u5ca9\u77f3\u4e0a\u7aef\u5374\u6709\u4eba\u753b\u4e86\u4e00\u6735\u5170\u82b1\uff0c\u6de1\u6de1 \u51e0\u7b14\uff0c\u7f8e\u5999\u975e\u51e1\u3002\u753b\u5170\u82b1\u7684\u4eba\u4e0d\u4f46\u6709\u7edd\u9876\u8f7b\u529f\uff0c\u800c\u4e14\u6709\u4e39\u9752\u5999\u6280\u3002\u5415\u56db\u5a18\u4e5f\u4e0d\u7981\u5567\u5567\u79f0\u5f02\u3002 \u3000\u3000\u89c1\u4e86\u8fd9\u6735\u5170\u82b1\uff0c\u5415\u56db\u5a18\u6599\u77e5\u5fc5\u6709\u9ad8\u624b\u66fe\u7ecf\u6765\u8fc7\uff0c\u5fc3\u4e2d\u66f4\u6025\uff0c\u770b\u4e86\u4e00\u4e0b\uff0c\u987e\u4e0d\u5f97\u7ec6\u5fc3\u6b23 \u8d4f\uff0c\u4fbf\u5373\u79bb\u5f00\u3002\u201c\u4e39\u971e\u5d82\u201d\u4e0b\u662f\u4e2a\u6c34\u5e18\u6d1e\uff0c\u6c34\u7531\u77f3\u58c1\u5954\u6cfb\u800c\u4e0b\uff0c\u73e0\u6cab\u56db\u6e85\uff0c\u58f0\u5982\u91d1\u77f3\uff0c\u968f\u98ce \u98d8\u5ffd\uff0c\u758f\u5bc6\u4e0d\u5b9a\uff0c\u6c47\u6210\u6c34\u6f6d\uff0c\u5510\u6653\u6f9c\u5f53\u5e74\u66fe\u5728\u6b64\u5904\u5411\u5979\u503e\u5410\u8eab\u4e16\uff0c\u800c\u4eca\u7ecf\u8fc7\uff0c\u56de\u9996\u524d\u5c18\uff0c\u604d \u60da\u5982\u68a6\u3002 \u3000\u3000\u8fc7\u4e86\u5c71\u6cc9\u98de\u7011\uff0c\u4e00\u74e2\u548c\u5c1a\u7684\u7985\u9662\u5df2\u7136\u5728\u671b\u3002\u5415\u56db\u5a18\u5f15\u542d\u957f\u5578\uff0c\u5374\u4e0d\u89c1\u4e00\u74e2\u51fa\u6765\u8fce\u63a5\uff0c\u5415 \u56db\u5a18\u4e0d\u7531\u5f97\u5403\u4e86\u4e00\u60ca\uff0c\u52a0\u5feb\u811a\u6b65\uff0c\u5954\u5165\u7985\u9662\uff0c\u4f46\u89c1\u5bfa\u95e8\u5012\u584c\uff0c\u58c1\u5012\u5899\u574d\uff0c\u82b1\u8c22\u6c34\u5e72\uff0c\u4e00\u7247\u8427 \u7d22\u3002\u65e5\u56db\u5a18\u53eb\u9053\uff1a\u201c\u4e00\u74e2\u5927\u5e08\uff0c\u4e00\u74e2\u5927\u5e08\uff01\u201d\u53ea\u95fb\u8352\u5239\u56de\u58f0\uff0c\u91ce\u9e1f\u60ca\u8d77\u3002\u5415\u56db\u5a18\u53c8\u53eb\u9053\uff1a \u201c\u5728\u5bbd\u54e5\u54e5\uff0c\u5728\u5bbd\u54e5\u54e5\uff01\u201d\u540c\u6837\u4e5f\u542c\u4e0d\u5230\u6709\u4eba\u56de\u7b54\u3002 \u3000\u3000\u5415\u56db\u5a18\u4e0d\u89c9\u5446\u4e86\uff0c\u5979\u672c\u6765\u575a\u4fe1\u5728\u5bbd\u6ca1\u6709\u6b7b\u4ea1\uff0c\u8fd9\u4e00\u4e0b\u5927\u5927\u51fa\u4e4e\u5979\u7684\u610f\u5916\uff0c\u524d\u6b21\u79bb\u5f00\u5728\u5bbd \u4e4b\u65f6\uff0c\u5728\u5bbd\u867d\u8bf4\u5df2\u53ef\u8d70\u52a8\uff0c\u4f46\u5230\u5e95\u4e0d\u5f88\u65b9\u4fbf\uff0c\u800c\u4e14\u4ed6\u53c8\u662f\u907f\u7978\u6b64\u5c71\uff0c\u6309\u8bf4\u65e0\u8bba\u5982\u4f55\u4e0d\u4f1a\u4e0b \u5c71\uff0c\u96be\u9053\uff0c\u96be\u9053\u2014\u2014\u5415\u56db\u5a18\u4e0d\u7531\u5f97\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u4e0d\u6562\u518d\u60f3\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\u5468\u56f4\u7684\u7a7a\u6c14\u90fd\u51b7\u5f97\u597d\u4f3c\u8981\u51dd\u7ed3\u8d77\u6765\uff0c\u5415\u56db\u5a18\u673a\u68b0\u822c\u7684\u79fb\u52a8\u811a\u6b65\uff0c\u6276\u7740\u5899\u58c1\uff0c\u8d70 \u51fa\u7985\u5802\uff0c\u7a7f\u8fc7\u56de\u5eca\uff0c\u8d70\u8fdb\u6c88\u5728\u5bbd\u4ee5\u524d\u5c45\u4f4f\u7684\u9759\u5ba4\u3002\u5ba4\u95e8\u534a\u63a9\uff0c\u4e00\u63a8\u4fbf\u5f00\uff0c\u4e00\u80a1\u4e45\u672a\u6253\u626b\u7684\u8150 \u6c14\u51b2\u9f3b\u800c\u6765\uff0c\u4f46\u89c1\u91cc\u9762\u5e8a\u94fa\u4e66\u684c\uff0c\u6446\u8bbe\u4f9d\u65e7\uff0c\u4f46\u5df2\u79ef\u4e86\u539a\u539a\u7684\u7070\u5c18\u3002\u6709\u51e0\u53ea\u8001\u9f20\u542c\u95fb\u4eba\u58f0\uff0c \u6025\u6025\u9003\u8dd1\u3002 \u3000\u3000\u5415\u56db\u5a18\u9762\u5411\u7a97\u5916\u5438\u4e86\u53e3\u6c14\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u5fc3\u60f3\u8fd9\u4e0d\u662f\u505a\u68a6\u5427\uff1f\u5979\u4ecd\u7136\u4e0d\u613f\u76f8\u4fe1\u5728\u5bbd\u5df2\u6b7b\uff0c \u53c8\u673a\u68b0\u822c\u7684\u79fb\u52a8\u811a\u6b65\uff0c\u8d70\u904d\u4e86\u5bfa\u9662\u7684\u6bcf\u4e2a\u89d2\u843d\uff0c\u771f\u4e2a\u662f\u5bfb\u5bfb\u89c5\u89c5\uff0c\u5bfb\u4e4b\u4e0d\u89c1\uff0c\u89c5\u4e4b\u4e0d\u5f97\uff0c\u8fd9 \u624d\u84e6\u7136\u95f4\u89c9\u5f97\u51b7\u51b7\u6e05\u6e05\uff0c\u51c4\u51c4\u60e8\u60e8\uff0c\u7ec8\u800c\u5ffd\u4f3c\u4e00\u5207\u7a7a\u65e0\u6240\u6709\uff0c\u4e00\u5207\u6e05\u5bc2\u3002 \u3000\u3000\u8fc7\u4e86\u8bb8\u4e45\uff0c\u5415\u56db\u5a18\u624d\u597d\u4f3c\u4ece\u6076\u68a6\u4e2d\u9192\u6765\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u73e0\u6cea\u5df2\u6e7f\u8863\u886b\uff0c\u4f46\u5fc3\u4e2d\u4ecd\u7136\u60f3 \u9053\uff1a\u201c\u90a3\u5934\u9885\u660e\u660e\u4e0d\u50cf\u4ed6\u7684\uff0c\u83ab\u975e\u4ed6\u5728\u9e70\u72ac\u4e0a\u5c71\u4e4b\u65e5\uff0c\u62fc\u547d\u9003\u907f\u5f00\u4e86\uff1f\u201d\u5fc3\u5b58\u4e00\u7ebf\u5e0c\u671b\uff0c\u5728 \u5bfa\u4e2d\u7ec6\u5fc3\u5bdf\u770b\uff0c\u8fd9\u624d\u53d1\u89c9\u5bfa\u4e2d\u5bb6\u5177\u6ca1\u6709\u4e00\u4ef6\u5b8c\u6574\u7684\uff0c\u5206\u660e\u662f\u5728\u8fd9\u5bfa\u4e2d\u6709\u8fc7\u4e00\u573a\u6076\u6597\u3002\u518d\u7ec6\u770b \u65f6\uff0c\u7985\u5802\u7684\u77f3\u9636\u4e4b\u4e0a\u8fd8\u6709\u4e00\u6ee9\u8840\u75d5\uff0c\u65e5\u6652\u98ce\u5e72\uff0c\u4ecd\u662f\u6de1\u7ea2\u4e00\u7247\uff0c\u89e6\u76ee\u60ca\u5fc3\u3002 \u3000\u3000\u8fd9\u65f6\u5415\u56db\u5a18\u7eb5\u6709\u4e07\u5206\u81ea\u4fe1\uff0c\u4e5f\u81ea\u5fc3\u614c\u3002\u5bfa\u9662\u5916\u9e26\u58f0\u566a\u6811\uff0c\u65e5\u5934\u5df2\u843d\u5c71\u4e86\u3002\u5415\u56db\u5a18\u5b9a\u4e86\u5b9a \u795e\uff0c\u53c8\u5f3a\u81ea\u6170\u89e3\u9053\uff1a\u201c\u77e5\u9053\u8fd9\u662f\u8c01\u4eba\u7684\u8840\uff1f\u4e00\u74e2\u548c\u5c1a\u6b66\u529f\u4e0d\u51e1\uff0c\u4e5f\u8bb8\u662f\u4ed6\u6740\u4f24\u9e70\u72ac\u7684\u8840 \u5462\uff01\u201d\u8d81\u7740\u5929\u8fd8\u672a\u9ed1\uff0c\u5415\u56db\u5a18\u8d70\u51fa\u7985\u9662\uff0c\u53c8\u4ece\u5bfa\u9662\u80cc\u540e\u4e0b\u5c71\uff0c\u4e00\u8def\u67e5\u770b\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u9635\uff0c\u5ffd\u5728\u524d\u9762\u4e00\u7247\u5ca9\u77f3\u4e0a\u53c8\u53d1\u73b0\u4e86\u4e00\u6735\u6307\u753b\u7684\u83b2\u82b1\u3002\u4e0e\u5728\u201c\u4e39\u971e\u5d82\u201d\u4e0a\u7684\u90a3\u6735\uff0c \u663e\u51fa\u4e00\u4eba\u4e4b\u624b\u3002\u5415\u56db\u5a18\u5fc3\u5ff5\u4e00\u52a8\u3002\u8d70\u8fc7\u5ced\u58c1\u5e95\u4e0b\uff0c\u4e0d\u4e45\u53c8\u53d1\u73b0\u4e00\u6735\u6307\u753b\u7684\u83b2\u82b1\uff0c\u4ed9\u971e\u5cad\u4e0a\u5c42 \u5ce6\u53e0\u969c\uff01\u5c71\u6da7\u9519\u6742\uff0c\u4e0d\u662f\u4e45\u5c45\u6b64\u5c71\uff0c\u5e38\u4f1a\u8ff7\u8def\uff0c\u770b\u6765\u8fd9\u4e9b\u6307\u753b\u7684\u83b2\u82b1\uff0c\u7adf\u4f3c\u662f\u6c5f\u6e56\u5ba2\u7684\u6807 \u8bb0\uff0c\u62ff\u6765\u5f53\u4f5c\u6307\u8def\u4e4b\u7528\u7684\u3002\u5415\u56db\u5a18\u4e0d\u7981\u7591\u5fc3\u5927\u8d77\uff0c\u5fc3\u9053\uff1a\u201c\u6b64\u5c71\u5e76\u65e0\u5b9d\u7269\uff0c\u753b\u83b2\u82b1\u7684\u4eba\u81ea\u662f \u9ad8\u624b\uff0c\u4ed6\u82e5\u4e0d\u662f\u4e3a\u7740\u518d\u6765\u65f6\u8981\u5230\u67d0\u4e00\u9690\u79d8\u7684\u5904\u6240\uff0c\u5f53\u4e0d\u4f1a\u7559\u4e0b\u6807\u8bb0\u3002\u6211\u5012\u8981\u770b\u770b\u83b2\u82b1\u6307\u5411\u4ec0 \u4e48\u53bb\u5904\uff1f \u3000\u3000\u5415\u56db\u5a18\u811a\u7a0b\u98de\u5feb\uff0c\u7ecf\u8fc7\u4e86\u4e09\u5904\u83b2\u82b1\u6807\u8bb0\uff0c\u53ea\u89c1\u524d\u9762\u5c71\u52bf\u6e10\u8d8b\u5e73\u5766\uff0c\u73b0\u51fa\u4e00\u7247\u659c\u5761\uff0c\u659c\u73bb \u4e0a\u73b0\u51fa\u4e24\u5806\u571f\u4e18\uff0c\u5f62\u5982\u9992\u5934\uff0c\u5415\u56db\u5a18\u4e00\u89c1\uff0c\u5fc3\u513f\u535c\u535c\u7684\u8df3\u4e2a\u4e0d\u505c\uff0c\u770b\u6765\u8fd9\u4e24\u5806\u571f\u4e18\u7adf\u662f\u65b0\u5efa \u7684\u575f\u5893\u3002 \u3000\u3000\u5415\u56db\u5a18\u98de\u8eab\u63a0\u53bb\uff0c\u8d70\u795e\u7ec6\u770b\uff0c\u679c\u7136\u662f\u4e24\u5ea7\u65b0\u575f\uff0c\u6bcf\u5ea7\u575f\u524d\u90fd\u7acb\u7740\u4e00\u5757\u767d\u77f3\u5893\u7891\u3002\u5de6\u8fb9\u90a3 \u5ea7\u5893\u7891\u5199\u7684\u662f\uff1a\u4e00\u74e2\u5927\u5e08\u4e4b\u5893\u3002\u5415\u56db\u5a18\u773c\u524d\u4e00\u9ed1\uff0c\u60f3\u4e0d\u5230\u4ee5\u4e00\u74e2\u5927\u5e08\u90a3\u6837\u7684\u6b66\u529f\u7adf\u4e5f\u906d\u96be\uff0c \u5148\u524d\u7684\u63a8\u65ad\uff0c\u5df2\u662f\u6210\u7a7a\u3002\u518d\u5b9a\u4e00\u5b9a\u795e\uff0c\u770b\u53f3\u8fb9\u90a3\u5757\u5893\u7891\uff0c\u4e0d\u770b\u72b9\u53ef\uff0c\u8fd9\u4e00\u770b\u66f4\u9b42\u98de\u9b44\u6563\uff01\u5893 \u7891\u4e0a\u5199\u7684\u7adf\u662f\u201c\u4ed9\u971e\u5904\u58eb\u6c88\u5728\u5bbd\u8863\u51a0\u4e4b\u51a2\u201d\uff0c\u6c88\u5728\u5bbd\u5230\u4ed9\u971e\u517b\u75c5\u4e4b\u540e\uff0c\u5c1d\u81ea\u53f7\u201c\u4ed9\u971e\u5904 \u58eb\u201d\uff0c\u770b\u6765\u8fd9\u4e00\u5b9a\u662f\u4ed6\u597d\u53cb\u6240\u7acb\u3002\u53f7\u4e3a\u201c\u8863\u51a0\u51a2\u201d\u8005\uff0c\u5fc5\u662f\u56e0\u4e3a\u5efa\u5893\u4e4b\u4eba\u5df2\u77e5\u4ed6\u5728\u4eac\u5e08\u88ab \u65a9\uff0c\u65e0\u6cd5\u6536\u5c38\uff0c\u56e0\u6b64\u53ea\u80fd\u57cb\u846c\u4ed6\u7684\u8863\u51a0\uff0c\u7559\u4e3a\u7eaa\u5ff5\u3002\u53ea\u51ed\u8fd9\u5893\u7891\u4e0a\u7684\u51e0\u4e2a\u5b57\uff0c\u65e2\u5207\u5408\u6c88\u5728\u5bbd \u7684\u8eab\u4efd\uff0c\u53c8\u5207\u5408\u4ed6\u7684\u6b7b\u96be\u60c5\u51b5\uff0c\u4fbf\u53ef\u77e5\u9053\u6c88\u5728\u5bbd\u4e4b\u6b7b\u662f\u4e07\u65e0\u53ef\u7591\u7684\u4e86\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u5415\u56db\u5a18\u5168\u8eab\u9ebb\u6728\uff0c\u6b32\u54ed\u65e0\u6cea\uff0c\u524d\u5c18\u5f80\u4e8b\u90fd\u4e0a\u5fc3\u5934\u3002\u60f3\u4e0d\u5230\u6c88\u5728\u5bbd\u4ee5\u524d\u5927\u96be\u4e0d \u6b7b\uff0c\u800c\u4eca\u5374\u88ab\u540c\u9053\u524d\u8f88\u6240\u5356\uff0c\u6b7b\u5728\u5978\u4eba\u4e4b\u624b\uff0c\u8eab\u9996\u5f02\u5904\uff0c\u5bb6\u4e2d\u53ea\u5269\u8863\u51a0\u3002\u66f4\u60f3\u4e0d\u5230\u4ed6\u4ee5\u5c06\u8fd1 \u5341\u5e74\u7684\u6052\u5fc3\u6bc5\u529b\u3002\u521a\u521a\u6218\u80dc\u75c5\u9b54\uff0c\u514d\u4e86\u6b8b\u5e9f\uff0c\u4e00\u65e6\u4e4b\u95f4\u53c8\u6b7b\u4e8e\u975e\u547d\uff01\u771f\u662f\u5929\u9053\u5b81\u8bba\uff01\u5415\u56db\u5a18 \u6602\u9996\u95ee\u5929\uff0c\u62d4\u5251\u542c\u5730\uff0c\u5929\u65e2\u4e0d\u5e94\uff0c\u5730\u4ea6\u65e0\u58f0\u3002 \u3000\u3000\u5bbf\u9e1f\u6295\u6797\uff0c\u7791\u8272\u56db\u5408\uff0c\u5415\u56db\u5a18\u72ec\u5750\u575f\u524d\uff0c\u5982\u75f4\u4f3c\u9189\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u8fd9\u624d\u6e10\u6e10\u6e05 \u9192\uff0c\u84e6\u7136\u8df3\u8d77\u6765\u9053\uff1a\u201c\u90fd\u662f\u66fe\u9759\u8fd9\u4e2a\u8001\u8d3c\uff0c\u8981\u4e0d\u7136\u8c01\u4f1a\u77e5\u9053\u4ed6\u5728\u4ed9\u971e\uff1f\u8fd9\u6ca1\u9aa8\u5934\u7684\u8001\u8d3c\u4fbf\u662f \u5bb3\u4ed6\u7684\u51f6\u624b\uff0c\u6211\u4e3a\u4ec0\u4e48\u8fd8\u8981\u624b\u4e0b\u7559\u60c5\uff1f\u201d\u5415\u56db\u5a18\u672c\u65e0\u6740\u66fe\u9759\u4e4b\u5fc3\uff0c\u8fd9\u65f6\u4e00\u8154\u6012\u6c14\u90fd\u53d1\u4f5c\u51fa \u6765\uff0c\u6068\u4e0d\u5f97\u4eb2\u81ea\u628a\u66fe\u9759\u62ff\u6765\uff0c\u6740\u4e86\u4e3a\u5728\u5bbd\u62a5\u4ec7\u3002\u5979\u77e5\u9053\u66fe\u9759\u4eca\u665a\u5b9a\u5728\u84b2\u57ce\u6295\u5bbf\uff0c\u84b2\u57ce\u79bb\u4ed9\u971e \u867d\u7136\u7ea6\u6709\u767e\u91cc\uff0c\u5728\u5415\u56db\u5a18\u770b\u6765\uff0c\u53ef\u4e0d\u5f53\u4f5c\u4ec0\u4e48\u4e00\u56de\u4e8b\u3002\u62a5\u4ec7\u4e4b\u5fc3\u4e00\u8d77\uff0c\u7acb\u523b\u4e0b\u5c71\uff0c\u65bd\u5c55\u7edd\u9876 \u8f7b\u529f\uff0c\u76f4\u5954\u84b2\u57ce\uff0c\u4e09\u66f4\u624d\u8fc7\uff0c\u4fbf\u5230\u4e86\u57ce\u5185\u3002\u84b2\u57ce\u662f\u4e2a\u5c0f\u53bf\u57ce\u3002\u4e09\u66f4\u8fc7\u540e\uff0c\u4e07\u7c41\u4ff1\u5bc2\u3002 \u3000\u3000\u66fe\u9759\u6b64\u4eba\uff0c\u672c\u6765\u4e0d\u662f\u7acb\u5fc3\u4f5c\u574f\uff0c\u53ea\u56e0\u8d2a\u751f\u6015\u6b7b\uff0c\u4e00\u65f6\u8f6f\u5f31\uff0c\u901a\u4e0d\u8fc7\u8003\u9a8c\uff0c\u9042\u5c48\u670d\u4e8e\u6deb\u5a01 \u4e4b\u4e0b\uff0c\u4ee5\u81f4\u94f8\u6210\u5927\u9519\u3002\u4e8b\u60c5\u8fc7\u540e\uff0c\u5185\u759a\u795e\u660e\uff0c\u5fc3\u4e2d\u5341\u5206\u4e0d\u5b89\u3002\u8fd9\u65e5\u5728\u8def\u65c1\u7684\u8336\u4ead\u77a5\u89c1\u4e86\u5415\u56db \u5a18\uff0c\u5fc3\u4e2d\u66f4\u662f\u60ca\u6050\u3002\u6240\u5e78\u79bb\u5f00\u4e86\u8336\u4ead\u540e\uff0c\u4e00\u8def\u4e0a\u4e0d\u518d\u89c1\u5415\u56db\u5a18\u8e2a\u8ff9\uff0c\u5fc3\u795e\u65b9\u5f97\u7a0d\u5b9a\u3002\u81ea\u6211\u6170 \u89e3\u9053\uff1a\u56db\u5a18\u600e\u77e5\u6211\u62db\u4f9b\u4e4b\u4e8b\uff0c\u5979\u9002\u624d\u4e0d\u6562\u4e0e\u6211\u62db\u547c\uff0c\u5b9a\u662f\u56e0\u4e3a\u6709\u90a3\u4e24\u540d\u6b66\u58eb\u5728\u65c1\uff0c\u6240\u4ee5\u4e0d\u613f \u9732\u51fa\u8eab\u4efd\u3002\u5012\u5e76\u4e0d\u4e00\u5b9a\u662f\u56e0\u5bf9\u6211\u6709\u654c\u610f\u554a\u3002\u867d\u7136\u5982\u6b64\u6170\u89e3\uff0c\u53ef\u662f\u4e00\u60f3\u5230\u5415\u56db\u5a18\u6b66\u529f\u5353\u7edd\uff0c\u65e2 \u7136\u53d1\u73b0\u4e86\u81ea\u5df1\u8e2a\u8ff9\uff0c\u4e00\u5b9a\u6697\u4e2d\u8ddf\u6765\uff0c\u5c06\u6765\u76f8\u89c1\u4e4b\u65f6\uff0c\u600e\u751f\u548c\u5979\u8c08\u8bdd\uff1f\u601d\u5ff5\u53ca\u6b64\uff0c\u53c8\u4e0d\u7981\u60f4\u60f4 \u4e0d\u5b89\u3002 \u3000\u3000\u8fd9\u665a\uff0c\u5230\u4e86\u84b2\u57ce\uff0c\u4e00\u4ef6\u4ee4\u4ed6\u66f4\u4e0d\u5b89\u7684\u4e8b\u60c5\u53c8\u53d1\u751f\u4e86\u3002\u4e00\u8fdb\u7ad9\u95e8\uff0c\u4fbf\u6709\u4e24\u4eba\u6307\u7740\u4ed6\u7684\u8f7f\u5b50 \u9053\uff1a\u201c\u662f\u66fe\u8001\u5148\u751f\u5417\uff1f\u201d\u90a3\u4e24\u540d\u8f7f\u592b\uff0c\u4e5f\u662f\u5e74\u7fb9\u5c27\u7684\u4eba\uff0c\u4e45\u7ecf\u8bad\u7ec3\uff0c\u4e00\u89c1\u6709\u4eba\u622a\u8f7f\u62db\u547c\uff0c\u7acb \u523b\u505c\u4e0b\u8f7f\u5b50\u3002\u66fe\u9759\u63ed\u5f00\u8f7f\u5e18\uff0c\u53ea\u89c1\u90a3\u4e24\u4eba\u9012\u8fdb\u4e00\u5f20\u62dc\u5e16\uff0c\u9053\uff1a\u201c\u66fe\u8001\u5148\u751f\uff0c\u8bf7\u5230\u5c0f\u5e97\u6b47\u8db3\uff0c \u623f\u95f4\u5df2\u5907\u597d\u4e86\u3002\u201d\u66fe\u9759\u4e00\u770b\u62dc\u5e16\uff0c\u539f\u6765\u662f\u4e00\u4e2a\u540d\u53eb\u201c\u957f\u5b89\u5ba2\u5e97\u201d\u7684\u8fce\u5bbe\u62dc\u5e16\uff0c\u90a3\u65f6\u7684\u98ce\u4fd7\uff0c \u5ba2\u5e97\u82e5\u77e5\u9053\u6709\u8fbe\u5b98\u5bcc\u5546\u8fc7\u5883\uff0c\u5e38\u5e38\u6d3e\u51fa\u5f97\u529b\u4f19\u8ba1\uff0c\u5728\u57ce\u95e8\u63a5\u5f85\uff0c\u8fd9\u4e5f\u662f\u62db\u6765\u751f\u610f\u7684\u4e00\u9053\uff0c\u4e0d \u8db3\u4e3a\u5947\u3002\u53ef\u662f\u4ee5\u66fe\u9759\u4e00\u4ecb\u7a77\u5112\uff0c\u867d\u7136\u540d\u6ee1\u4ed5\u6797\uff0c\u4e00\u751f\u5374\u672a\u66fe\u53d7\u8fc7\u8fd9\u79cd\u62db\u5f85\uff0c\u89c1\u72b6\u5012\u9887\u611f\u610f\u5916 \u4e86\u3002 \u3000\u3000\u66fe\u9759\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u77e5\u9053\u6211\u4eca\u65e5\u5230\u6765\uff1f\u201d\u957f\u5b89\u5ba2\u5e97\u7684\u4f19\u8ba1\u56de\u9053\uff1a\u201c\u66fe\u8001\u5148\u751f\u7684\u670b \u53cb\u4eca\u65e9\u5df2\u901a\u77e5\u4e86\u6211\u4eec\uff0c\u623f\u95f4\u4e5f\u5b9a\u597d\u4e86\u3002\u8bf7\u66fe\u8001\u5148\u751f\u968f\u6211\u4eec\u6765\u5427\u3002\u201d\u66fe\u9759\u6115\u7136\u8bf4\u9053\uff1a\u201c\u6211\u6709\u4ec0 \u4e48\u670b\u53cb\uff1f\u201d\u90a3\u4f19\u8ba1\u966a\u7b11\u9053\uff1a\u201c\u66fe\u5148\u751f\u76f8\u8bc6\u6ee1\u5929\u4e0b\uff0c\u89c1\u4e86\u9762\u81ea\u7136\u77e5\u9053\u4e86\u3002\u201d\u66fe\u9759\u6b63\u5f85\u62d2\u7edd\u4e0d \u53bb\uff0c\u90a3\u4e24\u540d\u6697\u4e2d\u62a4\u9001\u4ed6\u7684\u6b66\u58eb\uff0c\u8fd9\u65f6\u4e5f\u90fd\u5df2\u5165\u4e86\u57ce\u95e8\uff0c\u62a2\u5148\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u7684\u5ba2\u5e97\u4e2d\u8fd8\u6709\u623f \u5417\uff1f\u201d\u5ba2\u5e97\u7684\u4f19\u8ba1\u5fd9\u9053\uff1a\u201c\u6709\uff0c\u6709\uff01\u201d\u90a3\u4e24\u540d\u6b66\u58eb\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e5f\u4f4f\u4f60\u4eec\u7684\u5ba2\u5e97\u3002\u201d\u8fd9\u8bdd\u660e \u660e\u662f\u5bf9\u66fe\u9759\u793a\u610f\uff0c\u975e\u4f4f\u8fd9\u95f4\u5ba2\u5e97\u4e0d\u53ef\u3002\u66fe\u9759\u6ca1\u6cd5\uff0c\u53ea\u597d\u968f\u90a3\u4f19\u8ba1\u884c\u4e86\u3002 \u3000\u3000\u201c\u957f\u5b89\u5ba2\u5e97\u201d\u867d\u7136\u662f\u5c0f\u53bf\u57ce\u4e2d\u7684\u5ba2\u5e97\uff0c\u5e03\u7f6e\u5f97\u5012\u4e5f\u96c5\u81f4\u4e0d\u4fd7\uff0c\u5728\u66fe\u9759\u7684\u623f\u4e2d\uff0c\u8fd8\u6709\u4e66\u53f0 \u7b49\u5bb6\u79c1\u6446\u8bbe\uff0c\u4f19\u8ba1\u9053\uff1a\u201c\u8d35\u53cb\u8bf4\u66fe\u8001\u5148\u751f\u662f\u4e00\u4ee3\u540d\u5112\uff0c\u53eb\u6211\u4eec\u5e03\u7f6e\u5f97\u50cf\u4e66\u623f\u7684\u6837\u5b50\u3002\u201d\u66fe\u9759 \u66f4\u662f\u60f4\u60f4\u4e0d\u5b89\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u5148\u751f\u5462\uff1f\u4e3a\u4f55\u4e0d\u89c1\u9732\u9762\uff1f\u201d\u4f19\u8ba1\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u4e0d\u77e5\u9053\u5440\uff0c\u4ed6\u53eb \u4eba\u6765\u5b9a\u623f\uff0c\u4e22\u4e0b\u94f6\u4e66\u5c31\u8d70\u4e86\u3002\u201d\u66fe\u9759\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u6765\u5b9a\u623f\u3002\u201d\u4e00\u4f19\u8ba1\u9053\uff1a\u201c\u662f\u4e2a\u9ebb\u5b50\u3002\u201d\u66fe \u9759\u4e00\u6115\uff0c\u4f19\u8ba1\u7eed\u9053\uff1a\u201c\u90a3\u9ebb\u5b50\u662f\u4e2a\u957f\u968f\uff0c\u4ed6\u662f\u66ff\u4ed6\u7684\u4e3b\u4eba\u4e3a\u66fe\u8001\u5b9a\u623f\u7684\uff0c\u4ed6\u4e3b\u4eba\u7684\u540d\u5b57\u4ed6\u4e5f \u6ca1\u6709\u7559\u4e0b\u6765\uff0c\u60f3\u6765\u4e00\u5b9a\u662f\u5f85\u4f60\u8001\u6b47\u4e86\u4e00\u665a\u540e\uff0c\u660e\u65e9\u624d\u6765\u62dc\u4f1a\u3002\u201d \u3000\u3000\u66fe\u9759\u89c1\u95ee\u4e0d\u51fa\u6240\u4ee5\u7136\u6765\uff0c\u4e5f\u4fbf\u7f62\u4e86\u3002\u90a3\u4e24\u540d\u6b66\u58eb\u8981\u4e86\u66fe\u9759\u5de6\u53f3\u7684\u4e24\u95f4\u623f\u95f4\uff0c\u5403\u8fc7\u996d\u540e\uff0c \u4e8c\u66f4\u65f6\u5206\uff0c\u88c5\u4f5c\u540c\u8def\u4eba\u6765\u8bbf\uff0c\u8fdb\u5165\u66fe\u9759\u623f\u95f4\uff0c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u66fe\u8001\u5148\u751f\uff0c\u4ee4\u665a\u4f60\u53ef\u5f97\u5c0f\u5fc3\u70b9 \u513f\uff01\u201d\u66fe\u9759\u5413\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u53d1\u73b0\u4e86\u4ec0\u4e48\u4e0d\u59a5\u5417\uff1f\u4f60\u4eec\u53ef\u5f97\u6551\u6551\u6211\u7684\u6027\u547d\uff0c\u6211\u8bf4\uff0c\u4e0d\u5982\u6362\u4e86 \u5ba2\u5e97\u5427\uff01\u201d \u3000\u3000\u90a3\u4e24\u540d\u6b66\u58eb\u4e43\u662f\u5e74\u7fb9\u5c27\u7684\u5fc3\u8179\u6b66\u571f\uff0c\u60e7\u6709\u975e\u5e38\u6b66\u827a\uff0c\u542c\u4e86\u66fe\u9759\u4e4b\u8a00\uff0c\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u66ff\u4f60 \u5b9a\u623f\u7684\u4eba\u4e0d\u95ee\u53ef\u77e5\uff0c\u5b9a\u662f\u5415\u7559\u826f\u7684\u9057\u5b7d\uff0c\u6211\u4eec\u5b9a\u8981\u7b49\u4ed6\u5230\u6765\uff0c\u600e\u597d\u6362\u5e97\uff1f\u201d\u66fe\u9759\u4e0d\u597d\u8a00\u8bed\uff0c \u5fc3\u4e2d\u6697\u6697\u5403\u60ca\u3002\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u4e0d\u77e5\u662f\u4e0d\u662f\u5415\u56db\u5a18\u7684\u5bf9\u624b\uff1f\u54b3\uff0c\u5415\u56db\u5a18\u6740\u4e86\u4ed6\u4eec\u56fa\u7136\u4e0d\u597d\uff0c \u4ed6\u4eec\u6740\u4e86\u5415\u56db\u5a18\u66f4\u4e0d\u597d\u3002\u5415\u5bb6\u5144\u5f1f\u548c\u6211\u5230\u5e95\u662f\u751f\u524d\u77e5\u4ea4\uff0c\u6211\u600e\u5fcd\u89c1\u4ed6\u5bb6\u88ab\u65a9\u8349\u9664\u6839\u3002\u201d\u66fe\u9759 \u8fd9\u65f6\uff0c\u6ee1\u5fc3\u4ee5\u4e3a\u66ff\u4ed6\u5b9a\u623f\u7684\u5fc5\u7136\u662f\u5415\u56db\u5a18\uff0c\u8c01\u77e5\u5374\u6599\u9519\u4e86\u3002 \u3000\u3000\u8fd9\u665a\uff0c\u66fe\u9759\u90a3\u91cc\u8fd8\u7761\u5f97\u7740\uff0c\u4ed6\u770b\u4e86\u4e00\u56de\u201c\u6625\u79cb\u201d\uff0c\u6625\u79cb\u8c34\u8d23\u4e71\u81e3\u8d3c\u5b50\uff0c\u53f2\u7b14\u51db\u7136\uff0c\u53ea\u770b \u4e86\u51e0\u9875\uff0c\u4fbf\u4e0d\u6562\u518d\u770b\u3002\u542c\u542c\u5916\u9762\u5df2\u6253\u4e09\u66f4\uff0c\u5ba2\u5e97\u4e00\u70b9\u58f0\u54cd\u90fd\u6ca1\u6709\uff0c\u66fe\u9759\u5185\u759a\u795e\u660e\uff0c\u5750\u5367\u4e0d \u5b89\uff0c\u6253\u5f00\u7a97\u5b50\uff0c\u7a97\u5b50\u5916\u4e00\u9635\u51b7\u98ce\u5439\u4e86\u8fdb\u6765\uff0c\u591c\u8272\u51a5\u51a5\u4e2d\uff0c\u9690\u9690\u53ef\u4ee5\u89c1\u5230\u4ed9\u971e\u5cad\u4f3c\u9ed1\u718a\u4e00\u6837\u8e72 \u4f0f\u5728\u539f\u91ce\u4e0a\u3002\u66fe\u9759\u4e0d\u7531\u5f97\u60f3\u8d77\u6c88\u5728\u5bbd\u6765\uff0c\u51a5\u51a5\u591c\u8272\u4e2d\uff0c\u7adf\u4f3c\u89c1\u7740\u6c88\u5728\u5bbd\u9888\u8840\u6dcb\u6f13\uff0c\u624b\u4e2d\u63d0\u7740 \u5934\u9885\uff0c\u5934\u9885\u4e0a\u4e24\u53ea\u767d\u6e17\u6e17\u4f3c\u6b7b\u9c7c\u4e00\u6837\u7684\u773c\u73e0\u5411\u4ed6\u6ce8\u89c6\u3002\u66fe\u9759\u60ca\u53eb\u4e00\u58f0\uff0c\u6025\u5fd9\u5173\u4e0a\u7a97\u5b50\uff0c\u773c\u524d \u7684\u5e7b\u8c61\u7acb\u5373\u6d88\u5931\u3002 \u3000\u3000\u66fe\u9759\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u5e73\u751f\u4e0d\u4f5c\u4e8f\u5fc3\u4e8b\uff0c\u534a\u591c\u6572\u95e8\u4e5f\u4e0d\u60ca\uff0c\u8fd9\u8bdd\u771f\u771f\u4e0d\u9519\u3002\u201d\u62b9\u4e86\u62b9 \u989d\u4e0a\u7684\u51b7\u6c57\uff0c\u6f2b\u65e0\u76ee\u7684\u7684\u5728\u623f\u95f4\u5185\u9540\u8d77\u65b9\u6b65\uff0c\u4e0d\u81ea\u89c9\u7684\u5ff5\u8d77\u4e86\u5434\u6885\u6751\u7684\u7edd\u547d\u8bcd\u6765\uff1a\u201c\u2026\u2026\u543e \u75c5\u96be\u5c06\u533b\u836f\u6cbb\uff0c\u803f\u803f\u80f8\u4e2d\u70ed\u8840\u3002\u2026\u2026\u6545\u4eba\u6177\u6168\u591a\u5947\u8282\uff0c\u4e3a\u5f53\u5e74\u6c89\u541f\u4e0d\u65ad\uff0c\u8349\u95f4\u8c15\u6d3b\u3002\u2026\u2026\u8131 \u5c63\u59bb\u5b65\u975e\u6613\u4e8b\uff0c\u7adf\u4e00\u94b1\u4e0d\u503c\u4f55\u987b\u8bf4\uff01\u2026\u2026\u201d\u4e00\u58f0\u9ad8\u4e00\u58f0\u4f4e\uff0c\u65ad\u4ee5\u7eed\u7eed\uff0c\u604d\u5982\u79cb\u866b\u545c\u54bd\u3002\u541f\u58f0 \u4e00\u6b62\uff0c\u5ffd\u53c8\u81ea\u8a00\u81ea\u8bed\u7b11\u9053\uff1a\u201c\u6211\u6bd4\u5434\u6885\u6751\u5230\u5e95\u8fd8\u5f3a\u4e00\u4e9b\uff0c\u4eba\u4eba\u90fd\u77e5\u5434\u68fa\u6751\u665a\u8282\u4e0d\u7ec8\uff0c\u53ef\u662f\u5343 \u53e4\u4e4b\u540e\uff0c\u6709\u8c01\u77e5\u9053\u6211\u66fe\u9759\u5e72\u8fc7\u9519\u4e8b\uff1f\u201d \u3000\u3000\u66fe\u9759\u54ed\u4e00\u4f1a\u7b11\u4e00\u4f1a\uff0c\u5ffd\u542c\u5f97\u623f\u95e8\u5916\u201c\u7b03\u3001\u7b03\u3001\u7b03\uff01\u2026\u2026\u201d\u6572\u95e8\u58f0\u54cd\uff0c\u66fe\u9759\u4ee5\u4e3a\u662f\u90bb\u623f\u6b66 \u58eb\uff0c\u968f\u53e3\u95ee\u4e86\u4e00\u58f0\uff1a\u201c\u8c01\uff1f\u201d\u6ca1\u5f85\u56de\u7b54\uff0c\u4fbf\u62bd\u5f00\u4e86\u95e8\u6805\uff0c\u623f\u95e8\u4e00\u4e0b\u5f00\u4e86\uff0c\u66fe\u9759\u62ac\u5934\u4e00\u671b\uff0c\u5413 \u5f97\u4e09\u9b42\u5931\u4e86\u4e24\u9b42\uff0c\u4e03\u9b44\u4ec5\u4f59\u4e00\u9b44\uff0c\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u662f\u4eba\u8fd8\u662f\u9b3c\uff0c\u4e0d\uff0c\u4e0d\uff0c\u4e0d\u662f\u6211\u5bb3\u4f60 \u7684\uff0c\u4f60\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u4e0d\u4ec5\u66fe\u9759\u5403\u60ca\uff0c\u53e6\u4e00\u4eba\u5403\u60ca\u66f4\u751a\uff0c\u8fd9\u4eba\u4fbf\u662f\u5415\u56db\u5a18\u3002\u5415\u56db\u5a18\u4e09\u66f4\u65f6\u5206\uff0c\u6765\u5230\u84b2\u57ce\uff0c\u84b2\u57ce \u6ca1\u6709\u51e0\u5bb6\u5ba2\u5e97\uff0c\u4e00\u67e5\u4fbf\u77e5\u3002\u5415\u56db\u5a18\u8f7b\u529f\u7edd\u9876\uff0c\u98de\u4e0a\u8fd9\u5bb6\u5ba2\u5e97\u7684\u74e6\u9762\uff0c\u771f\u5982\u4e00\u53f6\u8f7b\u5815\uff0c\u843d\u5904\u65e0 \u58f0\uff0c\u8fde\u90a3\u4e24\u540d\u805a\u7cbe\u4f1a\u795e\u4e00\u5fc3\u7b49\u5f85\u7684\u6b66\u58eb\u4e5f\u6ca1\u6709\u53d1\u73b0\u3002 \u3000\u3000\u5415\u56db\u5a18\u5148\u542c\u5f97\u66fe\u9759\u5ff5\u5434\u6885\u6751\u7684\u201c\u7edd\u547d\u8bcd\u201d\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u8fd8\u77e5\u9053\u81ea\u6028\u81ea \u827e\u3002\u201d\u89c1\u4ed6\u5e74\u8fc8\u82cd\u82cd\uff0c\u4e0d\u5fcd\u4e0b\u624b\uff0c\u540e\u6765\u53c8\u89c1\u4ed6\u81ea\u8a00\u81ea\u7b11\uff0c\u5fcd\u4e0d\u4f4f\u6012\u706b\u71c3\u8d77\uff0c\u6b63\u60f3\u4e0b\u624b\uff0c\u5ffd\u89c1 \u5c3e\u623f\u623f\u95e8\u8f7b\u542f\uff0c\u8d70\u51fa\u4e86\u4e00\u4e2a\u4e66\u751f\u6a21\u6837\u7684\u4eba\uff0c\u9752\u5dfe\u8499\u9762\uff0c\u6765\u5230\u66fe\u9759\u623f\u524d\uff0c\u8f7b\u8f7b\u6572\u95e8\uff0c\u968f\u5373\u628a\u9752 \u5dfe\u9664\u4e0b\uff0c\u8fd9\u4eba\u70e7\u53d8\u4e86\u7070\u5415\u56db\u5a18\u4e5f\u8ba4\u5f97\uff0c\u6b63\u662f\u5415\u56db\u5a18\u4ee5\u4e3a\u5df2\u6b7b\u4e86\u7684\u6c88\u5728\u5bbd\uff01\u521a\u624d\u4ed6\u8d70\u51fa\u623f\u65f6\uff0c \u5415\u56db\u5a18\u5df2\u662f\u7591\u5fc3\uff0c\u5982\u4eca\u9664\u4e86\u9752\u5dfe\uff0c\u66f4\u8bc1\u5b9e\u4e86\uff01 \u3000\u3000\u5415\u56db\u5a18\u8fd9\u4e00\u4e0b\u771f\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u60f3\u4e0d\u5230\u6c88\u5728\u5bbd\u4e0d\u4f46\u6ca1\u6b7b\uff0c\u800c\u4e14\u9762\u8272\u7ea2\u6da6\uff0c\u884c\u52a8\u77eb\u6377\uff0c\u6bd4\u5e73 \u5e38\u4eba\u8fd8\u8981\u5065\u58ee\u5f97\u591a\u3002\u5415\u56db\u5a18\u5fc3\u60c5\u6b22\u60a6\uff0c\u6740\u673a\u53c8\u6cef\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4e14\u770b\u66fe\u8001\u5934\u513f\u6709\u4ec0\u4e48\u8138\u76ae\u89c1 \u4ed6\uff1f\u201d \u3000\u3000\u66fe\u9759\u5413\u5f97\u9b42\u6d88\u9b44\u6563\uff0c\u95ee\u4ed6\u662f\u4eba\u662f\u9b3c\uff0c\u8fd8\u8bf4\uff1a\u201c\u4f60\uff0c\u4f60\u4e0d\u8981\u5411\u6211\u7d22\u547d\uff01\u201d \u3000\u3000\u6c88\u5728\u5bbd\u5fae\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u9b3c\uff01\u90a3\u65e5\u5728\u4ed9\u971e\u5cad\u4e0a\u88ab\u9e70\u72ac\u6355\u53bb\u7684\u662f\u6211\u7684\u5802\u5f1f\u5728\u82f1\u3002\u201d\u9762\u8272\u4e00 \u6c89\uff0c\u4f4e\u58f0\u53c8\u9053\uff1a\u201c\u53ef\u601c\u4ed6\u7b2c\u4e00\u6b21\u4e0a\u5c71\u63a2\u6211\uff0c\u4fbf\u906d\u6bd2\u624b\uff01\u5509\uff0c\u8fd8\u8fde\u7d2f\u4e86\u4e00\u74e2\u548c\u5c1a\u3002\u5728\u82f1\uff0c\u4f60\u4e0d \u662f\u4e5f\u8ba4\u5f97\u7684\u5417\uff1f\u201d \u3000\u3000\u66fe\u9759\u4e00\u542c\uff0c\u6c88\u5728\u5bbd\u4f3c\u4e4e\u8fd8\u672a\u77e5\u9053\u662f\u4ed6\u51fa\u5356\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u4e5f\u4f4e\u5934\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u662f\u5440\uff0c\u5728 \u82f1\u4e0d\u662f\u5f88\u4f3c\u4f60\u5417\uff1f\u5927\u597d\u9752\u5e74\uff0c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d \u3000\u3000\u6c88\u5728\u5bbd\u9762\u8272\u51dd\u91cd\uff0c\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u6b7b\u8005\u5df2\u77e3\uff0c\u751f\u8005\u66f4\u8981\u5c0f\u5fc3\u3002\u66fe\u8001\u4f2f\uff0c\u4f60\u8eab\u5728\u7edd\u9669\u4e4b\u4e2d\uff0c \u8d76\u5feb\u968f\u6211\u9003\u5427\uff01\u201d \u3000\u3000\u66fe\u9759\u521a\u521a\u5bbd\u5fc3\uff0c\u542c\u4e86\u6b64\u8bdd\uff0c\u9762\u8272\u53c8\u53d8\uff0c\u53ea\u542c\u5f97\u6c88\u5728\u5bbd\u7eed\u9053\uff1a\u201c\u90bb\u623f\u7684\u4e24\u4e2a\u6b66\u58eb\u4e00\u8def\u8ddf\u4f60 \u540c\u884c\uff0c\u4ed6\u4eec\u8ba4\u4e0d\u51fa\u4f60\u662f\u8c01\u5417\uff1f\u542c\u8bf4\u671d\u5ef7\u6b63\u5728\u5927\u6355\u6211\u4eec\u8fd9\u4e00\u5e2e\u4eba\uff0c\u4e25\u53d4\u53d4\u4e5f\u5df2\u7ecf\u9047\u5bb3\u4e86\uff0c\u4f60\u662f \u6211\u515a\u4e2d\u7684\u9996\u8111\u4eba\u7269\uff0c\u600e\u4e48\u8fd8\u968f\u4fbf\u5230\u5904\u4e71\u8d70\uff1f\u201d \u3000\u3000\u539f\u6765\u90a3\u65e5\u5e74\u7fb9\u5c27\u6d3e\u9063\u6b66\u58eb\u4e0a\u5c71\uff0c\u6349\u62ff\u6c88\u5728\u5bbd\u65f6\uff0c\u6c88\u5728\u5bbd\u521a\u597d\u56e0\u4e3a\u8eab\u4f53\u5df2\u7ecf\u5eb7\u590d\uff0c\u4e00\u65e9\u5230 \u5c71\u8170\u6563\u6b65\uff0c\u884c\u5f97\u9ad8\u5174\uff0c\u4e0d\u89c9\u79bb\u5bfa\u5e99\u5341\u4f59\u91cc\u8fdc\uff0c\u4ed9\u971e\u5cad\u5c71\u8def\u8fc2\u56de\uff0c\u79bb\u5bfa\u5e99\u5341\u4f59\u91cc\u5df2\u9694\u4e86\u4e24\u4e2a\u5c71 \u5934\uff0c\u5e74\u7fb9\u5c27\u6b66\u58eb\u6765\u65f6\uff0c\u4ed6\u8fde\u77e5\u4e5f\u4e0d\u77e5\u3002\u5230\u4e86\u5174\u5c3d\u56de\u5bfa\uff0c\u624d\u89c1\u4e00\u74e2\u548c\u5c1a\u5c38\u6a2a\u5bfa\u4e2d\uff0c\u6025\u6025\u4e0b\u5c71\u9003 \u907f\uff0c\u5176\u540e\u53c8\u77e5\u4ed6\u7684\u5802\u5f1f\u90a3\u65e5\u6070\u5de7\u4e0a\u5c71\u63a2\u4ed6\uff0c\u7adf\u7136\u505a\u4e86\u66ff\u6b7b\u9b3c\u3002\u6c88\u5728\u5bbd\u60b2\u6124\u83ab\u540d\uff0c\u53ef\u662f\u5415\u56db\u5a18 \u4e0d\u5728\uff0c\u4ed6\u4e00\u4eba\u4e5f\u4e0d\u80fd\u62a5\u4ec7\u3002\u53ea\u597d\u628a\u4e00\u74e2\u548c\u5c1a\u57cb\u4e86\u3002\u540c\u65f6\u53c8\u6545\u610f\u66ff\u81ea\u5df1\u7acb\u4e86\u4e00\u4e2a\u8863\u51a0\u4e4b\u51a2\uff0c\u6545 \u5e03\u7591\u9635\uff0c\u597d\u8ba9\u518d\u6709\u6e05\u5ef7\u7684\u9e70\u72ac\u4e0a\u5c71\u67e5\u63a2\u65f6\uff0c\u53ef\u4ee5\u4e0d\u5fc5\u518d\u6ce8\u610f\u4ed6\u3002 \u3000\u3000\u4e00\u74e2\u548c\u5c1a\u5728\u84b2\u57ce\u76f8\u8bc6\u9887\u591a\uff0c\u5176\u4e2d\u4e5f\u6709\u540c\u9053\u4e2d\u4eba\uff0c\u6c88\u5728\u5bbd\u4fbf\u5728\u4e00\u5bb6\u59d3\u53f6\u7684\u4eba\u5bb6\u5c45\u4f4f\uff0c\u8fd9\u53f6 \u5bb6\u53c8\u662f\u5e2e\u4f1a\u4e2d\u4eba\uff0c\u66fe\u9759\u5750\u7740\u8f7f\u5b50\u4ece\u6d59\u6c5f\u6765\u5230\u798f\u5efa\u7684\u6d88\u606f\uff0c\u5df2\u6709\u4eba\u98de\u9a6c\u544a\u8bc9\u4e8e\u4ed6\uff0c\u540c\u65f6\u4e5f\u628a\u4e24 \u4e2a\u6b66\u58eb\u8ddf\u5728\u8f7f\u524d\u8f7f\u8fb0\u7684\u60c5\u51b5\u8bf4\u4e86\uff0c\u6c88\u5728\u5bbd\u4e00\u542c\uff0c\u6df1\u6050\u66fe\u9759\u4e5f\u906d\u6bd2\u624b\uff0c\u56e0\u6b64\u9884\u65e9\u5e03\u7f6e\uff0c\u5c06\u66fe\u9759 \u5f15\u5230\u957f\u5b89\u5ba2\u5e97\u6765\u3002 \u3000\u3000\u66fe\u9759\u542c\u5f97\u6c88\u5728\u5bbd\u8fde\u58f0\u50ac\u4ed6\u901f\u8d70\uff0c\u771f\u662f\u557c\u7b11\u7686\u975e\u3002\u53c8\u4e0d\u4fbf\u5c06\u771f\u60c5\u5411\u4ed6\u5410\u9732\uff0c\u6b63\u5728\u652f\u652f\u543e \u543e\uff0c\u5c34\u5c2c\u4e07\u5206\u4e4b\u9645\uff0c\u95e8\u5916\u4e00\u58f0\u51b7\u7b11\uff0c\u5de6\u53f3\u4e24\u4e2a\u90bb\u623f\u7684\u6b66\u58eb\u90fd\u51b2\u4e86\u51fa\u6765\u3002\u90a3\u866c\u9aef\u58ee\u6c49\u6a2a\u95e8\u4e00 \u7ad9\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u597d\u5927\u80c6\u7684\u8d3c\u5b50\uff0c\u8001\u5b50\u7b49\u4f60\u5df2\u4e45\u4e86\uff01\u201d\u4f38\u81c2\u4e00\u6293\uff0c\u5411\u6c88\u5728\u5bbd\u7684\u7435\u7436\u9aa8\u4e00\u6263\uff01 \u3000\u3000\u8fd9\u540d\u6b66\u58eb\u957f\u4e8e\u9e70\u722a\u529f\u592b\uff0c\u89c1\u6c88\u5728\u5bbd\u4e00\u6d3e\u6587\u5f31\u4e66\u751f\u7684\u6837\u5b50\uff0c\u4ee5\u4e3a\u8fd8\u4e0d\u662f\u624b\u5230\u64d2\u6765\u3002\u90a3\u77e5\u6c88 \u5728\u5bbd\u5f97\u4e86\u5415\u56db\u5a18\u4f20\u4ed6\u5185\u529f\u6cbb\u75c5\u4e4b\u6cd5\uff0c\u8fd1\u5341\u5e74\u6765\u65e5\u591c\u8654\u5fc3\u4fee\u4e60\uff0c\u5185\u529f\u706b\u5019\u5df2\u5230\uff0c\u6240\u4ee5\u762b\u75ea\u4e4b\u75c7 \u624d\u80fd\u75ca\u6108\u3002\u8fd9\u65f6\uff0c\u4ed6\u867d\u7136\u5bf9\u4e8e\u6280\u51fb\u4e4b\u9053\u4e1d\u6beb\u4e0d\u61c2\uff0c\u53ef\u662f\u5185\u529f\u7684\u7cbe\u7eaf\uff0c\u5df2\u53ef\u6bd4\u5f97\u4e86\u6c5f\u6e56\u4e0a\u7684\u4e00 \u6d41\u597d\u624b\uff01 \u3000\u3000\u90a3\u866c\u9aef\u53cc\u624b\u4e00\u6293\u6293\u53bb\uff0c\u89e6\u7740\u6c88\u5728\u5bbd\u7684\u80a9\u5934\uff0c\u6c88\u5728\u5bbd\u7684\u808c\u8089\u9047\u7740\u5916\u529b\uff0c\u672c\u80fd\u4e00\u7f29\uff0c\u866c\u9aef\u6c49 \u5b50\u53ea\u89c9\u6ed1\u4e0d\u7559\u624b\uff0c\u6709\u5982\u6293\u7740\u4e00\u5c3e\u6ce5\u9cc5\u4e00\u6837\uff0c\u987f\u65f6\u53c8\u7ed9\u4ed6\u6ed1\u8131\u5f00\u53bb\uff0c\u4e0d\u89c9\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a \u201c\u8fd9\u70b9\u5b50\u624e\u624b\uff01\u201d\u6a2a\u638c\u4e00\u62cd\uff0c\u6c88\u5728\u5bbd\u51fa\u638c\u76f8\u62b5\uff0c\u90a3\u866c\u9aef\u6c49\u5b50\u7adf\u7136\u7ed9\u4ed6\u9707\u9000\u4e24\u6b65\uff0c\u8fd9\u5206\u9645\uff0c\u866c \u9aef\u6c49\u5b50\u7684\u540c\u4f34\u5df2\u62d4\u51fa\u5355\u5200\uff0c\u4e00\u62db\u201c\u94c1\u7281\u8015\u5730\u201d\uff0c\u65a9\u4ed6\u53cc\u817f\uff0c\u90a3\u866c\u9aef\u6c49\u5b50\u4e5f\u518d\u6251\u4e0a\u6765\uff0c\u6293\u4ed6\u624b \u8155\uff0c\u8e22\u4ed6\u8170\u80ef\u3002 \u3000\u3000\u6c88\u5728\u5bbd\u5230\u5e95\u662f\u4e0d\u61c2\u6280\u51fb\u4e4b\u4eba\uff0c\u88ab\u4e24\u4eba\u4e00\u903c\uff0c\u624b\u5fd9\u811a\u4e71\uff0c\u5ffd\u95fb\u5f97\u74e6\u6a90\u4e0a\u4e00\u58f0\u51b7\u7b11\uff0c\u6325\u5200\u7684 \u6c49\u5b50\u9996\u5148\u5012\u5730\uff0c\u6c88\u5728\u5bbd\u559c\u9053\uff1a\u201c\u56db\u5a18\u3002\u201d\u866c\u9aef\u6c49\u5b50\u56de\u5934\u4e00\u671b\uff0c\u5415\u56db\u5a18\u51fa\u624b\u5982\u7535\uff0c\u4e00\u5251\u6a2a\u62ab\uff0c \u4e00\u9897\u5934\u9885\u987f\u65f6\u98de\u51fa\u5c4b\u5916\u3002\u8fd9\u65f6\u91cc\u623f\u7684\u5b69\u5b50\u54c7\u7136\u54ed\u4e86\u8d77\u6765\uff0c\u66fe\u9759\u5413\u5f97\u9762\u5982\u571f\u8272\uff0c\u53eb\u9053\uff1a\u201c\u8d24\u4f84 \u5973\uff0c\u8d24\u4f84\u5973\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u9762\u8272\u4e00\u6c89\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u4f84\u5973\uff1f\u201d\u6c88\u5728\u5bbd\u6115\u7136\u9053\uff1a\u201c\u83b9\u59b9\uff0c\u4f60\u600e\u4e48 \u5566\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4f60\u5dee\u70b9\u6b7b\u5728\u4ed6\u7684\u624b\u4e0a\uff0c\u8fd8\u4e0d\u77e5\u9053\u5417\uff01\u66fe\u9759\uff0c\u6211\u95ee\u4f60\uff1a\u5b54\u65e5\u6210\u4ec1\uff0c\u5b5f\u65e5\u53d6 \u4e49\u3002\u4f60\u8bfb\u5723\u8d24\u4e66\uff0c\u6240\u5b66\u4f55\u4e8b\uff1f\u4e3a\u4f55\u4e34\u96be\u6b32\u675f\u82df\u6d3b\uff1f\u6beb\u65e0\u6c14\u9aa8\uff1f\u201d\u66fe\u9759\u9762\u76ae\u901a\u7ea2\uff0c\u7a81\u7136\u5411\u5899\u58c1 \u4e00\u5934\u649e\u53bb\uff0c\u6c88\u5728\u5bbd\u53cc\u81c2\u4e00\u62e6\uff0c\u5c06\u4ed6\u62b1\u7740\u3002\u66fe\u9759\u4e00\u628a\u773c\u6cea\uff0c\u4e00\u628a\u9f3b\u6d95\uff0c\u53c8\u54ed\u53c8\u558a\u9053\uff1a\u201c\u6211\u5e74\u7eaa \u8001\u8fc8\uff0c\u71ac\u4e0d\u7740\u82e6\u5211\uff0c\u4eba\u8c01\u65e0\u9519\uff1f\u54b3\uff0c\u54b3\uff0c\u4f60\uff0c\u4f60\u5c31\u8ba9\u6211\u4e00\u6b7b\uff0c\u4ee5\u8d4e\u7f6a\u5b7d\u4e86\u5427\uff01\u201d \u3000\u3000\u6c88\u5728\u5bbd\u8fd9\u65f6\u9aa4\u7136\u660e\u767d\uff0c\u4f46\u89c1\u7740\u66fe\u9759\u8fd9\u526f\u53ef\u601c\u7684\u6a21\u6837\uff0c\u751a\u4e0d\u5fcd\u5fc3\uff0c\u5ffd\u800c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u767e\u65e0 \u4e00\u7528\u662f\u4e66\u751f\u3002\u83b9\u59b9\uff0c\u6211\u5e78\u8fd8\u6ca1\u6709\u6b7b\uff0c\u9976\u4e86\u4ed6\u5427\u3002\u201d\u5415\u56db\u5a18\u6c14\u6124\u672a\u606f\uff0c\u4f46\u89c1\u6c88\u5728\u5bbd\u66ff\u4ed6\u6c42\u60c5\uff0c \u4e5f\u4fbf\u4e0d\u4e3a\u5df2\u751a\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8d70\u8fdb\u5185\u5ba4\uff0c\u624b\u8d77\u4e00\u5251\uff0c\u53c8\u628a\u90a3\u540d\u201c\u957f\u968f\u201d\u523a\u6b7b\uff0c\u5c06\u5e74\u7fb9\u5c27\u7684 \u5b69\u5b50\u62b1\u4e86\u8d77\u6765\uff0c\u9a82\u58f0\u201c\u5b7d\u79cd\u201d\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u4f46\u89c1\u8fd9\u5b69\u5b50\u5929\u5ead\u9971\u6ee1\uff0c\u6c14\u5b87\u4e0d\u51e1\uff0c\u6c88\u5728\u5bbd\u8fc7\u6765\u95ee \u9053\uff1a\u201c\u8fd9\u662f\u8c01\u5bb6\u5b69\u5b50\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u8fd9\u662f\u5e74\u7fb9\u5c27\u7684\u5b69\u5b50\u3002\u201d\u8bed\u58f0\u5df2\u4e0d\u4f3c\u5148\u524d\u6124\u6068\u3002\u6c88\u5728\u5bbd \u9053\uff1a\u201c\u7236\u6bcd\u4e4b\u7f6a\u4e0d\u53ca\u5b69\u5b50\u3002\u201d\u66fe\u9759\u542c\u4ed6\u4eec\u53e3\u6c14\u5df2\u5c06\u81ea\u5df1\u9976\u6055\uff0c\u8fd9\u65f6\u518d\u4e5f\u4e0d\u60f3\u81ea\u6740\u4e86\uff0c\u98a4\u6296\u8bf4 \u9053\uff1a\u201c\u662f\u5e74\u7fb9\u5c27\u903c\u6211\u8981\u6536\u517b\u4ed6\u7684\u5b69\u5b50\u7684\uff0c\u4e0d\uff0c\u4e0d\u5173\u6211\u7684\u4e8b\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u541b\u5b50\u4e00\u8bfa\u5343\u91d1\u3002 \u5e74\u7fb9\u5c27\u6709\u7f6a\uff0c\u4ed6\u7684\u5b69\u5b50\u6ca1\u7f6a\uff0c\u597d\uff0c\u4f60\u5c0f\u5fc3\u66ff\u4ed6\u629a\u517b\u4e86\u3002\u201d\u61d2\u5f97\u518d\u770b\u66fe\u9759\u90a3\u526f\u53ef\u50e7\u7684\u5634\u8138\uff0c\u8638 \u8840\u5728\u5899\u4e0a\u5927\u4e66\uff0c\u201c\u6740\u4eba\u8005\u5415\u56db\u5a18\u4e5f\uff01\u201d\u5199\u5b8c\u4e4b\u540e\uff0c\u62c9\u8d77\u6c88\u5728\u5bbd\uff0c\u8dd1\u51fa\u5ba2\u5e97\u3002 \u3000\u3000\u51fa\u4e86\u5ba2\u5e97\uff0c\u5415\u56db\u5a18\u9053\uff1a\u201c\u5728\u5bbd\uff0c\u6211\u771f\u6599\u4e0d\u5230\u8fd8\u80fd\u89c1\u4f60\u3002\u201d\u6c88\u5728\u5bbd\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u5df2\u89c1 \u4e0d\u7740\u4e00\u74e2\u5927\u5e08\u4e86\u3002\u201d\u5415\u56db\u5a18\u5ffd\u9053\uff1a\u201c\u5728\u5bbd\uff0c\u54b1\u4eec\u4e0a\u4ed9\u971e\u5cad\u53bb\u796d\u626b\u4e00\u74e2\u5927\u5e08\u4e4b\u5893\uff0c\u5728\u5cad\u4e0a\u76d8\u6853 \u51e0\u5929\uff0c\u4ee5\u524d\u4f60\u4e0d\u80fd\u8d70\u52a8\uff0c\u8bb8\u591a\u5c71\u4e0a\u7684\u7f8e\u666f\uff0c\u54b1\u4eec\u4e0d\u80fd\u4e00\u540c\u8d4f\u73a9\uff0c\u8fd9\u56de\u96be\u5f97\u5077\u5f97\u6d6e\u751f\u51e0\u65e5\u95f2\uff0c \u53ef\u4e0d\u8981\u9519\u8fc7\u540d\u5c71\u80dc\u666f\u4e86\u3002\u201d\u8f97\u7136\u4e00\u7b11\uff0c\u628a\u4e2a\u591a\u6708\u6765\u7684\u62c5\u5fc3\u5bb3\u6015\uff0c\u4ee5\u53ca\u5bf9\u66fe\u9759\u7684\u6c14\u607c\uff0c\u5bf9\u4e00\u74e2 \u7684\u60bc\u5ff5\uff0c\u7b49\u7b49\u4e0d\u6109\u5feb\u7684\u5fc3\u60c5\uff0c\u5168\u90fd\u4e00\u626b\u800c\u7a7a\u3002 \u3000\u3000\u8fd9\u665a\uff0c\u5415\u56db\u5a18\u548c\u6c88\u5728\u5bbd\u5728\u53f6\u5bb6\u4f4f\u4e86\u4e00\u665a\uff0c\u7b2c\u4e8c\u65e5\u4e2d\u5348\uff0c\u4ed6\u4eec\u53c8\u518d\u56de\u4ed9\u971e\u5cad\u4e0a\u3002\u5415\u56db\u5a18\u5fc3 \u60c5\u6109\u5feb\uff0c\u4e00\u8def\u770b\u82b1\u770b\u9e1f\uff0c\u548c\u6c88\u5728\u5bbd\u8c08\u8bba\u522b\u540e\u7684\u60c5\u5f62\uff0c\u53c8\u79f0\u8d5e\u6c88\u5728\u5bbd\u5185\u529f\u8fdb\u5883\u7684\u795e\u901f\u3002\u6c88\u5728\u5bbd \u7b11\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4f60\uff0c\u6211\u8fd9\u751f\u6b8b\u5e9f\u5b9a\u4e86\uff0c\u8fd8\u8c08\u5230\u4ec0\u4e48\u5185\u529f\u5462\uff1f\u83b9\u59b9\uff0c\u4f60\u8fd8\u8bb0\u5f97\u6211\u4ee5\u524d\u90a3\u9996\u96c6\u524d \u4eba\u4e4b\u53e5\u7684\u5c0f\u8bcd\u5417\uff1f\u5415\u56db\u5a18\u9053\uff1a\u201c\u600e\u4e0d\u8bb0\u5f97\uff1f\u201d\u5ff5\u9053\uff0c\u201c\u8c01\u9053\u98d8\u96f6\u4e0d\u53ef\u601c\uff0c\u91d1\u7089\u65ad\u5c3d\u5c0f\u7bc6\u9999\uff0c \u4eba\u751f\u4f55\u5904\u4f3c\u5c0a\u524d\uff1f\u89c1\u4e86\u53c8\u4f11\u8fd8\u4f3c\u68a6\uff0c\u5750\u6765\u867d\u8fd1\u8fdc\u5982\u5929\uff0c\u65ad\u6765\u80fd\u6709\u51e0\u56de\u80a0\uff1f\u201d\u8fd9\u662f\u6c88\u5728\u5bbd\u4ee5\u524d \u81ea\u4f24\u6b8b\u5e9f\uff0c\u81ea\u60ed\u5f62\u79fd\uff0c\u6df1\u89c9\u81ea\u5df1\u914d\u4e0d\u8d77\u5415\u56db\u5a18\uff0c\u6240\u4ee5\u96c6\u524d\u4eba\u4e4b\u53e5\u8868\u8fbe\u81ea\u5df1\u5fc3\u4e2d\u7684\u4f24\u611f\u3002\u5415\u56db \u5a18\u5ff5\u5b8c\u4e4b\u540e\u53c8\u7b11\u9053\uff1a\u201c\u73b0\u5728\uff0c\u4f60\u8be5\u4e0d\u4f1a\u518d\u6709\u8fd9\u79cd\u81ea\u5351\u7684\u5fc3\u7406\u4e86\u5427\uff1f\u201d\u6c88\u5728\u5bbd\u5fae\u5fae\u4e00\u7b11\u9053\uff1a \u201c\u60f3\u4e0d\u5230\u5341\u8f7d\u575a\u6301\uff0c\u7ec8\u507f\u5bbf\u613f\uff0c\u83b9\u59b9\uff0c\u54b1\u4eec\u90fd\u662f\u5bb6\u6563\u4eba\u4ea1\uff0c\u5b64\u96f6\u96f6\u7684\u4eba\u4e86\u3002\u54b1\u4eec\u4ec0\u4e48\u65f6\u5019\u4e86 \u7236\u6bcd\u7684\u5fc3\u4e8b\u5462\uff1f\u201d\u8fd9\u8bdd\u7684\u610f\u601d\uff0c\u5176\u5b9e\u4e43\u662f\u5411\u5415\u56db\u5a18\u8be2\u95ee\u5a5a\u671f\u3002\u5415\u56db\u5a18\u9762\u6cdb\u6843\u7ea2\uff0c\u5ffd\u67d4\u58f0\u8bf4 \u9053\uff1a\u201c\u5f85\u6211\u628a\u96cd\u6b63\u8fd9\u72d7\u7687\u5e1d\u6740\u4e86\uff0c\u54b1\u4eec\u518d\u884c\u5408\u85c9\u53cc\u4fee\uff0c\u4f60\u7b49\u5f97\u5417\uff1f\u201d\u6c88\u5728\u5bbd\u5fc3\u4e2d\u4e00\u51db\uff0c\u6b63\u8272 \u8bf4\u9053\uff1a\u201c\u5927\u4ec7\u672a\u62a5\uff0c\u5c31\u60f3\u5ba4\u5bb6\u4e4b\u597d\uff0c\u90a3\u662f\u6211\u7684\u9519\u4e86\u3002\u83b9\u59b9\uff0c\u62a5\u4ec7\u4e43\u662f\u6b63\u4e8b\uff0c\u6211\u5c82\u6709\u7b49\u4e0d\u5f97\u4e4b \u7406\u3002\u201d \u3000\u3000\u4e24\u4eba\u6de1\u6de1\u8bf4\u8bf4\uff0c\u4e0d\u4e45\u4fbf\u4ece\u201c\u4e39\u971e\u5d82\u201d\u4e0b\u7ecf\u8fc7\uff0c\u5415\u56db\u5a18\u62ac\u5934\u6307\u7740\u90a3\u6735\u6307\u753b\u7684\u83b2\u82b1\u9053\uff1a\u201c\u6b64 \u4eba\u529f\u529b\u4e0d\u5728\u6211\u4e0b\uff0c\u4f60\u53ef\u77e5\u9053\u662f\u8c01\u7559\u4e0b\u7684\u5417\uff1f\u201d\u5728\u5bbd\u770b\u4e86\uff0c\u4e5f\u9887\u60ca\u8be7\uff0c\u9053\uff1a\u201c\u51fa\u4e8b\u4e4b\u540e\uff0c\u6211\u4fbf \u5230\u84b2\u57ce\u9003\u96be\uff0c\u4e0d\u77e5\u6709\u8c01\u4f1a\u5165\u6b64\u5c71\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u643a\u7740\u6c88\u5728\u5bbd\u7684\u624b\uff0c\u8f6c\u8fc7\u51e0\u5904\u5c71\u5773\uff0c\u5faa\u7740\u6307\u753b\u83b2\u82b1\u7684\u6807\u8bb0\uff0c\u6765\u5230\u4e86\u4e00\u74e2\u548c\u5c1a\u7684\u5893 \u5730\uff0c\u5ffd\u542c\u5f97\u9504\u5934\u6398\u5730\u4e4b\u58f0\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u4e09\u5341\u591a\u5c81\u7684\u6c49\u5b50\uff0c\u6b63\u5728\u6398\u4e00\u74e2\u548c\u5c1a\u4e4b\u5893\u3002 \u3000\u3000\u5415\u56db\u5a18\u5927\u6012\uff0c\u53f1\u54a4\u4e00\u58f0\uff0c\u62d4\u51fa\u5b9d\u5251\uff0c\u53cc\u811a\u4e00\u70b9\uff0c\u8eab\u5982\u98de\u9e1f\u3002\u4e00\u63a0\u800c\u4e0b\uff0c\u559d\u9053\uff1a\u201c\u597d\u72e0\u6bd2 \u7684\u9e70\u72ac\uff0c\u6740\u4e86\u4eba\u8fd8\u8981\u6398\u5893\u5077\u5934\u5417\uff1f\u201d\u5415\u56db\u5a18\u8ba4\u5b9a\u4e86\u6b64\u4eba\u82e5\u4e0d\u662f\u5927\u5185\u7684\u536b\u58eb\uff0c\u4fbf\u662f\u5e74\u7fb9\u5c27\u5e10\u4e0b \u7684\u6b66\u58eb\uff0c\u6b64\u6765\u5b9a\u662f\u8981\u628a\u4e00\u74e2\u548c\u5c1a\u7684\u9996\u7ea7\u6398\u53bb\u732e\u529f\u3002 \u3000\u3000\u5415\u56db\u5a18\u7684\u7384\u5973\u5251\u6cd5\u7cbe\u5999\u5f02\u5e38\uff0c\u8fd9\u4e00\u5251\u5c24\u5176\u662f\u5e73\u751f\u7684\u7edd\u5b66\uff0c\u90a3\u6599\u51cc\u7a7a\u4e00\u51fb\uff0c\u90a3\u4eba\u970d\u5730\u907f \u5f00\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u6b32\u8bf4\u53c8\u4f11\u3002\u5415\u56db\u5a18\u4e00\u51fb\u4e0d\u4e2d\uff0c\u5927\u4e3a\u8be7\u5f02\uff0c\u5237\u5237\u5237\u4e00\u8fde\u4e09\u5251\uff0c\u5168\u662f\u7384\u5973\u5251 \u6cd5\u4e2d\u7684\u5389\u5bb3\u6740\u7740\uff0c\u90a3\u4eba\u8db3\u5c16\u4e00\u65cb\uff0c\u56e2\u56e2\u4e71\u8f6c\uff0c\u5415\u56db\u5a18\u4e00\u8fde\u4e09\u5251\uff0c\u90fd\u6251\u4e86\u7a7a\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6 \u5feb\uff0c\u90a3\u4eba\u4e5f\u62d4\u51fa\u5251\u6765\uff0c\u626c\u7a7a\u4e00\u95ea\uff0c\u7adf\u7136\u4ece\u5415\u56db\u5a18\u7edd\u5bf9\u6599\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\uff0c\u653b\u4e86\u8fdb\u6765\u3002\u5415\u56db\u5a18\u5927 \u5403\u4e00\u60ca\uff0c\u5e78\u800c\u4ed7\u7740\u8f7b\u529f\u8d85\u5353\uff0c\u8eab\u5f62\u5fae\u95ea\uff0c\u7acb\u523b\u53cd\u653b\uff0c\u6c89\u5251\u4e00\u5f15\uff0c\u53cd\u5251\u4e00\u6311\uff0c\u4e24\u62db\u6b63\u53cd\u76f8\u6210\uff0c \u653b\u5b88\u4e92\u8f85\uff0c\u7eb5\u662f\u9ad8\u624b\u4e5f\u96be\u9003\u907f\uff0c\u90a3\u4eba\u5374\u4e5f\u602a\uff0c\u5ffd\u7136\u5f80\u5730\u4e0b\u4e00\u5750\uff0c\u95ea\u7535\u822c\u7684\u6253\u4e86\u51e0\u4e2a\u76d8\u65cb\uff0c\u5251 \u52bf\u6709\u5982\u73e0\u6eda\u7389\u76d8\uff0c\u5415\u56db\u5a18\u53cc\u8db3\u51e0\u4e4e\u5403\u4ed6\u65a9\u7740\uff0c\u614c\u5fd9\u8dc3\u4e86\u8d77\u6765\uff0c\u7528\u201c\u9e4f\u640f\u4e5d\u9704\u201d\u7684\u5251\u52bf\u4e00\u5251\u5149 \u970e\u65f6\u8361\u5f00\u4e08\u8bb8\uff0c\u5411\u90a3\u4eba\u5934\u9876\u4e00\u7f69\uff0c\u53ea\u8981\u5251\u5149\u4e00\u5408\uff0c\u4fbf\u662f\u7edd\u9876\u9ad8\u624b\uff0c\u4e5f\u96be\u9003\u98de\u5934\u6ef4\u8840\u4e4b\u707e\uff01 \u3000\u3000\u5251\u5149\u4e0b\u7f69\uff0c\u90a3\u4eba\u8eab\u5f62\u66b4\u957f\uff0c\u7a81\u7136\u7a9c\u51fa\u5251\u5149\u5708\u5916\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u51b3\u4ece\u5415\u56db\u5a18\u6599\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d \u653b\u4e86\u5165\u6765\uff0c\u5415\u56db\u8ddf\u7adf\u672a\u66fe\u89c1\u8fc7\u8fd9\u79cd\u602a\u5f02\u7684\u5251\u6cd5\uff0c\u5927\u4e3a\u5403\u60ca\uff0c\u6025\u6025\u95ea\u907f\u3002\u9000\u4e86\u4e24\u6b65\uff0c\u5251\u6cd5\u4e00 \u53d8\uff0c\u628a\u7384\u5973\u5251\u6cd5\u5c3d\u60c5\u65bd\u5c55\uff0c\u5251\u5149\u62a4\u7740\u5168\u8eab\uff0c\u5251\u52bf\u6eda\u6eda\u800c\u4e0a\u3002\u7384\u5973\u5251\u6cd5\u7684\u5965\u5999\u7cbe\u5947\u4e4b\u5904\uff0c\u4e0e\u5929 \u5c71\u5251\u6cd5\u7684\u535a\u5927\u5b8f\u6df1\uff0c\u540c\u662f\u5929\u4e0b\u65e0\u5339\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u51cc\u5389\u975e\u51e1\uff0c\u5251\u5251\u6307\u5411\u90a3\u4eba\u8981\u5bb3\uff0c\u90a3\u4eba\u811a\u6b65\u8e09 \u8e09\u8dc4\u8dc4\uff0c\u6709\u5982\u9189\u6c49\u4e00\u822c\uff0c\u65f6\u800c\u7eb5\u9ad8\uff0c\u65f6\u800c\u6251\u4f4e\uff0c\u6709\u597d\u51e0\u6b21\u90fd\u4f3c\u4e4e\u8981\u78b0\u7740\u5415\u56db\u5a18\u7684\u5251\u5c16\u4e86\uff0c\u5374 \u4e0d\u77e5\u600e\u7684\u90fd\u5728\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u907f\u4e86\u5f00\u53bb\u3002\u4ed6\u90a3\u53e3\u5251\u4e1c\u6307\u897f\u5212\uff0c\u770b\u6765\u4e0d\u6210\u7ae0\u6cd5\uff0c\u5176\u5b9e\u90fd\u662f\u62db\u91cc \u5957\u62db\uff0c\u5f0f\u4e2d\u6709\u5f0f\uff0c\u53d8\u5316\u7e41\u590d\u4e4b\u6781\u3002\u7adf\u662f\u5415\u56db\u5a18\u81ea\u51fa\u9053\u4ee5\u6765\uff0c\u5728\u5251\u6cd5\u4e0a\u4ece\u6240\u672a\u89c1\u7684\u5f3a\u654c\u3002 \u3000\u3000\u68cb\u9022\u5bf9\u624b\uff0c\u7cbe\u795e\u500d\u632f\uff0c\u5415\u56db\u5a18\u7684\u7384\u5973\u5251\u6cd5\u6e10\u5c55\u6e10\u5feb\uff0c\u66f4\u914d\u4e0a\u7edd\u9876\u8f7b\u529f\uff0c\u4e58\u6687\u8e48\u9699\uff0c\u4e0e\u90a3 \u4eba\u5bf9\u62a2\u653b\u52bf\uff0c\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u5251\u6cd5\u4e0a\u5404\u6709\u6240\u957f\uff0c\u5927\u5bb6\u90fd\u5948\u4f55\u4e0d\u4e86\u5bf9\u65b9\u3002\u53ef\u662f\u5415\u56db\u5a18\u8f7b\u529f\u8f83 \u9ad8\uff0c\u5360\u4e86\u516d\u6210\u653b\u52bf\uff0c\u7a0d\u5360\u4e0a\u98ce\u3002\u4f46\u867d\u7136\u5982\u6b64\uff0c\u8fd8\u662f\u4e0d\u80fd\u5c06\u5bf9\u65b9\u5236\u670d\u3002 \u3000\u3000\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u4e24\u67c4\u5251\u77eb\u82e5\u6e38\u9f99\uff0c\u4e4d\u8fdb\u4e4d\u9000\uff0c\u5ffd\u5206\u5ffd\u5408\uff0c\u65f6\u800c\u53cc\u5251\u76f8\u4ea4\uff0c\u7ea0\u7f20\u4e00\u5904\uff0c\u65f6 \u800c\u5404\u81ea\u6e38\u8d70\uff0c\u4e00\u6cbe\u5373\u79bb\uff0c\u628a\u6c88\u5728\u5bbd\u770b\u5f97\u773c\u82b1\u6f66\u4e71\uff0c\u8fde\u8c01\u662f\u5415\u56db\u5a18\u4e5f\u770b\u4e0d\u6e05\u4e86\u3002 \u3000\u3000\u5415\u56db\u5a18\u5fc3\u5ff5\u4e00\u52a8\uff0c\u90a3\u4eba\u5ffd\u7136\u8df3\u51fa\u5708\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u5fc5\u6597\u4e86\uff0c\u4f60\u7684\u7384\u5973\u5251\u6cd5\u679c\u7136\u7cbe\u5999\uff0c\u4f60 \u6562\u60c5\u662f\u5415\u56db\u5a18\uff1f\u201d\u5415\u56db\u5a18\u4e5f\u9053\uff1a\u201c\u4f60\u4f7f\u7684\u5b9a\u662f\u8fbe\u6469\u5251\u6cd5\u4e86\uff0c\u4f60\u662f\u6b66\u5f53\u6d3e\u7684\u4e48\uff1f\u4e0e\u6842\u4ef2\u660e\u8001\u524d \u8f88\u4ec0\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u6b63\u662f\u5bb6\u7236\u3002\u201d\u5415\u56db\u5a18\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u662f\u6709\u540d\u5251\u5ba2\u4e4b\u540e\uff0c\u5982\u4f55\u7518\u5fc3\u505a\u671d \u5ef7\u9e70\u72ac\uff0c\u8fd9\u5c82\u4e0d\u662f\u5815\u4e86\u5929\u5c71\u4e03\u5251\u7684\u5bb6\u58f0\u4e48\uff1f\u201d\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u5973\u4fa0\u5dee\u77e3\uff0c\u600e\u4e48\u8bf4\u6211\u662f\u671d\u5ef7\u9e70 \u72ac\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u6398\u4e00\u74e2\u5927\u5e08\u4e4b\u5893\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u4e00\u74e2\u5927\u5e08\u662f\u6211\u638c\u95e8\u5e08\u5144\u6b66 \u5f53\u5c71\u5b64\u4e91\u9053\u58eb\u7684\u597d\u53cb\uff0c\u6211\u7684\u5e08\u5144\u5f97\u77e5\u4ed6\u88ab\u5bb3\uff0c\u6050\u9632\u6709\u4eba\u4f24\u6b8b\u4ed6\u7684\u6cd5\u4f53\uff0c\u6545\u6b64\u53eb\u6211\u5c06\u4ed6\u7684\u91d1\u9aa8 \u79fb\u5230\u6b66\u5f53\u8fc1\u846c\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4f60\u4f55\u4e0d\u65e9\u8bf4\uff1f\u6211\u51e0\u4e4e\u4e00\u51fa\u624b\u5c31\u8981\u4f60\u7684\u547d\uff01\u201d\u90a3\u4eba\u4e5f\u7b11\u9053\uff1a \u201c\u6b63\u56e0\u6211\u89c1\u4f60\u7684\u5251\u6cd5\uff0c\u6240\u4ee5\u624d\u591a\u9886\u6559\u51e0\u62db\uff0c\u5f00\u5f00\u773c\u754c\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u8bd5\u62db\u6765 \u4e86\uff01\u8bf7\u6559\u5e08\u5144\u5927\u540d\u3002\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u5c0f\u59d3\u5192\uff0c\u540d\u5e7f\u751f\u3002\u201d\u5415\u56db\u5a18\u4e00\u6115\uff0c\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u6211\u662f\u8ddf\u6bcd\u4eb2\u7684\u59d3\u3002\u6211\u7236\u6240\u751f \u4e09\u5b50\uff0c\u5404\u5404\u59d3\u6c0f\u4e0d\u540c\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u8fd9\u662f\u4e3a\u4f55\uff1f\u201d\u5192\u5e7f\u751f\u9053\uff1a\u201c\u6211\u7236\u672c\u6765\u59d3\u77f3\uff0c\u968f\u4e49\u7236\u59d3 \u6842\u3002\u751f\u4e0b\u6211\u4eec\u4e09\u5144\u5f1f\uff0c\u5927\u54e5\u590d\u59d3\u5f52\u5b97\uff0c\u540d\u77f3\u5ddd\u751f\uff0c\u6211\u968f\u6bcd\u4eb2\u4e4b\u59d3\uff0c\u627f\u7ee7\u6211\u5916\u7956\u7236\u5192\u8f9f\u7586\u7684\u9999 \u70df\u3002\u4e09\u5f1f\u624d\u968f\u7236\u59d3\uff0c\u540d\u6842\u534e\u751f\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u9053\u4e86\u58f0\u5f97\u7f6a\uff0c\u9053\uff1a\u201c\u5c06\u4e00\u74e2\u5927\u5e08\u8fc1\u846c\u4e5f\u597d\uff0c\u514d\u5f97\u65e0\u4eba\u66ff\u4ed6\u5b88\u5893\u3002\u201d\u5192\u5e7f\u751f\u9053\uff1a \u201c\u9664\u4e86\u8fc1\u846c\uff0c\u6211\u5c1a\u6709\u4e00\u4e8b\uff0c\u6b63\u60f3\u8bf7\u6559\u5973\u4fa0\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u5192\u5144\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u5192\u5e7f\u751f\u9053\uff1a\u201c\u4f60\u53ef\u8ba4\u5f97\u5929\u5c71\u6613\u8001\u524d\u8f88\u7684\u5173\u95e8\u5f1f\u5b50\uff0c\u4e00\u4e2a\u540d\u53eb\u51af\u745b\u7684\u5973\u5b50\u4e48\uff1f\u201d\u5415\u51f9\u5a18\u7b11 \u9053\uff1a\u201c\u5c82\u6b62\u8ba4\u5f97\uff0c\u800c\u4e14\u5f88\u719f\u3002\u201d\u5192\u5e7f\u751f\u9053\uff1a\u201c\u90a3\u597d\u6781\u4e86\uff0c\u4f60\u77e5\u9053\u5979\u5728\u54ea\u91cc\uff1f\u201d\u5415\u56db\u5a18\u95ee\u9053\uff1a \u201c\u4f60\u8981\u627e\u5979\uff1f\u201d\u80c3\u5e7f\u751f\u70b9\u4e86\u70b9\u5934\uff0c\u5415\u56db\u5a18\u9887\u4e3a\u5947\u602a\uff0c\u5979\u4ece\u672a\u542c\u51af\u745b\u8bf4\u8fc7\u8ba4\u8bc6\u6b64\u4eba\uff0c\u4fbf\u95ee\u9053\uff1a \u201c\u4f60\u627e\u5979\u505a\u4ec0\u4e48\uff1f\u201d\u5192\u5e7f\u751f\u9053\uff1a\u201c\u6211\u5f1f\u5f1f\u8981\u627e\u5979\u6666\u6c14\uff01\u6211\u6015\u5f1f\u5f1f\u4f1a\u8bef\u4f1a\u4f24\u4e86\u5979\uff0c\u56e0\u6b64\u60f3\u53ca\u65f6 \u8d76\u53bb\u529d\u963b\u3002\u201d\u5415\u56db\u5a18\u5947\u9053\uff1a\u201c\u8fd9\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u4ee4\u5f1f\u548c\u5979\u6709\u4f55\u8fc7\u8282\uff1f\u201d \u3000\u3000\u5192\u5e7f\u751f\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u4eec\u5144\u5f1f\u90fd\u4e0d\u8ba4\u8bc6\u5979\uff0c\u54ea\u80fd\u5b58\u4ec0\u4e48\u8fc7\u8282\u3002\u201d\u5415\u56db\u5a18\u66f4\u5947\uff0c\u7b11 \u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4ee4\u5f1f\u5c82\u4e0d\u662f\u65e0\u7aef\u751f\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5192\u5e7f\u751f\u9053\uff1a\u201c\u5973\u4fa0\u6709\u6240\u4e0d\u77e5\u3002\u6211\u4eec\u4e09\u5144\u5f1f\u5c0f\u65f6\u90fd\u5728\u5929\u5c71\u957f\u5927\uff0c\u90a3\u65f6\u51af\u745b\u8fd8\u672a\u6765\uff0c\u6240\u4ee5\u5f7c \u6b64\u4e0d\u76f8\u8ba4\u8bc6\u3002\u6211\u7236\u4eb2\u6b7b\u540e\uff0c\u6211\u4eec\u4e09\u5144\u5f1f\u5949\u7236\u4eb2\u9057\u547d\uff0c\u79bb\u5f00\u5929\u5c71\uff0c\u5404\u6563\u4e00\u65b9\uff0c\u53d1\u626c\u8fbe\u6469\u5251\u672f\uff0c \u91cd\u6574\u6b66\u5f53\u95e8\u6237\u3002\u6211\u63a5\u4e86\u6b66\u5f53\u5317\u6d3e\u5206\u652f\uff0c\u7ecf\u5e38\u5728\u9655\u7518\u5404\u7701\uff1b\u5927\u54e5\u5728\u6b66\u5f53\u5c71\u534f\u52a9\u672c\u652f\u638c\u95e8\uff0c\u4e09\u5f1f \u5728\u56db\u5ddd\u7167\u7ba1\u8001\u5bb6\u3002\u4e09\u5f1f\u548c\u56db\u5ddd\u4ee5\u6697\u5668\u5f1b\u540d\u7684\u5510\u5bb6\u4ea4\u60c5\u5f88\u597d\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u662f\u4e86\uff0c\u5510\u5bb6\u4e09\u8001 \u4e2d\u7684\u8001\u4e8c\u5510\u91d1\u5cf0\u524d\u4e24\u5e74\u66fe\u5230\u8fc7\u5c71\u4e1c\uff0c\u542c\u8bf4\u662f\u4e3a\u4ed6\u7684\u5973\u5a7f\u62a5\u4ec7\u3002\u201d\u5192\u5e7f\u751f\u9053\uff1a\u201c\u5c31\u662f\u4e3a\u4e86\u6b64 \u4e8b\u3002\u201d\u5415\u56db\u5a18\u63d2\u53e3\u9053\uff1a\u201c\u53ef\u662f\u5510\u91d1\u5cf0\u7684\u5973\u5a7f\u4e0d\u662f\u51af\u745b\u6740\u7684\uff0c\u662f\u5979\u59b9\u59b9\u6740\u7684\u3002\u800c\u4e14\u5510\u91d1\u5cf0\u7684\u5973 \u5a7f\u5728\u516c\u95e8\u5f53\u5dee\uff0c\u516c\u5dee\u6740\u8d3c\u6216\u8d3c\u6740\u516c\u5dee\uff0c\u90fd\u4e0d\u80fd\u4e0e\u79c1\u4ec7\u7ed3\u6028\u7b49\u540c\u770b\u5f85\u3002\u8fd9\u79cd\u5bfb\u5e38\u4e4b\u4e8b\uff0c\u5728\u6b66\u6797 \u4e4b\u4e2d\u662f\u5f88\u5c11\u4f1a\u56e0\u6b64\u5bfb\u4ec7\u4e92\u6597\u7684\uff0c\u66f4\u4e0d\u8981\u8bf4\u8bf7\u4eba\u52a9\u62f3\u4e86\uff0c\u4ee4\u5f1f\u96be\u9053\u8fd8\u4e0d\u77e5\u6b66\u6797\u4e2d\u7684\u89c4\u77e9\u4e48\uff1f\u201d \u3000\u3000\u5192\u5e7f\u751f\u9053\uff1a\u201c\u5510\u4e8c\u5148\u751f\u4e5f\u5f04\u4e0d\u6e05\u695a\u6740\u5979\u5973\u5a7f\u7684\u4eba\u662f\u8c01\uff0c\u53ea\u77e5\u9053\u4e0d\u662f\u51af\u745b\u4fbf\u662f\u51af\u7433\u3002\u8d77\u521d \u4ed6\u8fde\u51af\u745b\u8fd8\u6709\u4e2a\u59b9\u59b9\u4e4b\u4e8b\u4e5f\u4e0d\u77e5\u9053\uff0c\u662f\u540e\u6765\u624d\u8c03\u67e5\u51fa\u6765\u7684\u3002\u5510\u91d1\u5cf0\u6700\u5ba0\u7231\u4ed6\u7684\u72ec\u751f\u5973\u513f\uff0c\u4ed6 \u88ab\u5973\u513f\u6240\u7f20\uff0c\u975e\u66ff\u5973\u5a7f\u62a5\u4ec7\u4e0d\u53ef\u3002\u53ef\u662f\u4ed6\u524d\u4e24\u5e74\u5230\u6768\u4ef2\u82f1\u5bb6\u53bb\u5bfb\u4ec7\u65f6\uff0c\u66fe\u5403\u4e86\u4e00\u6b21\u5927\u4e8f\uff0c\u77e5 \u9053\u81ea\u5df1\u4e0d\u662f\u51af\u745b\u59d0\u59b9\u5bf9\u624b\uff0c\u6240\u4ee5\u5f3a\u9080\u4e86\u6211\u7684\u5f1f\u5f1f\u53bb\u52a9\u62f3\u3002\u4ed6\u628a\u51af\u745b\u59d0\u59b9\u8bf4\u6210\u662f\u81ea\u6043\u5251\u672f\u9ad8 \u5f3a\uff0c\u65e0\u6076\u4e0d\u4f5c\u7684\u5973\u8d3c\uff0c\u6211\u7684\u5f1f\u5f1f\u751f\u6027\u597d\u5f3a\uff0c\u542c\u8bf4\u6709\u5982\u6b64\u5251\u672f\u9ad8\u5f3a\u7684\u5973\u8d3c\uff0c\u7acb\u5fc3\u53bb\u89c1\u8bc6\u89c1\u8bc6\uff0c \u4ed6\u4e0d\u77e5\u9053\u51af\u745b\u7adf\u662f\u6613\u8001\u524d\u8f88\u7684\u7231\u5f92\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u95ee\u9053\uff1a\u201c\u4f60\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d\u5192\u5e7f\u751f\u9053\uff1a\u201c\u6211\u4eca\u5e74\u5e74\u521d\uff0c\u66fe\u5230\u5929\u5c71\u53bb\u796d\u626b\u6211\u7236\u4eb2 \u4e4b\u5893\uff0c\u542c\u6613\u8001\u524d\u8f88\u8bf4\u8d77\u3002\u5e76\u8bf4\u5c06\u6765\u51c6\u5907\u7acb\u51af\u7433\u505a\u65e0\u6781\u6d3e\u7684\u4f20\u4eba\u3002\u8fd9\u4e48\u8bf4\u6765\uff0c\u51af\u5bb6\u59d0\u59b9\u548c\u6211\u4eec \u90fd\u662f\u5929\u5c71\u4e03\u5251\u7684\u540e\u4ee3\u4f20\u4eba\uff0c\u600e\u53ef\u4e92\u76f8\u6b8b\u6740\uff1f\u6211\u4ece\u5929\u5c71\u56de\u6765\u540e\uff0c\u624d\u77e5\u4e09\u5f1f\u521a\u521a\u88ab\u5510\u8001\u4e8c\u63d0\u8bf7\u51fa \u5c71\uff0c\u9002\u9022\u6b66\u5f53\u5c71\u672c\u652f\u638c\u95e8\u53c8\u59d4\u6258\u6211\u6765\u8fc1\u846c\u4e00\u74e2\u5927\u5e08\u4e4b\u9aa8\uff0c\u6240\u4ee5\u6211\u4fbf\u5148\u5230\u6b64\u5730\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u60f3\u4e86\u4e00\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u5728\u5bbd\uff0c\u6211\u4eec\u5728\u4ed9\u971e\u4e4b\u4e8b\u5df2\u4e86\uff0c\u540d\u5c71\u80dc\u666f\u7559\u4f8d\u4ed6\u65e5\u518d\u8d4f\u73a9\u5427\u3002 \u6211\u4eec\u4e5f\u968f\u5192\u5927\u54e5\u8d70\u4e00\u8d9f\uff0c\u505a\u505a\u9c81\u4ef2\u8fde\u3002\u6211\u4eec\u53ef\u4ee5\u5148\u5230\u5c71\u4e1c\u6768\u5bb6\uff0c\u5510\u91d1\u5cf0\u591a\u534a\u4f1a\u5148\u627e\u94c1\u638c\u795e\u5f39 \u6768\u4ef2\u82f1\u3002\u201d\u5192\u5e7f\u751f\u5927\u559c\u9053\uff1a\u201c\u5f97\u5973\u4fa0\u540c\u53bb\uff0c\u90a3\u597d\u6781\u4e86\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u65e0\u7aef\u5377\u8d77\u6ce2\u5343\u5c3a\uff0c\u94f8\u9519\u6210\u4ec7\u4e0d\u5fcd\u770b\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u56db\u5341\u56db\u56de\u3000\u9b42\u65ad\u6d2a\u6ce2\u3000\u751f\u96be\u507f\u5bbf\u613f\u3000\u5fc3\u4f24\u5927\u53d8\u3000\u6b7b\u5374\u60f9\u601d\u91cf \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u56db\u5341\u56db\u56de \u9b42\u65ad\u6d2a\u6ce2\u3000\u751f\u96be\u507f\u5bbf\u613f \u5fc3\u4f24\u5927\u53d8\u3000\u6b7b\u5374\u60f9\u601d\u91cf \u3000\u3000\u51af\u7433\u90a3\u65e5\uff0c\u79bb\u5f00\u4f17\u4eba\u4e4b\u540e\uff0c\u72ec\u81ea\u5230\u5c71\u4e1c\u53bb\u89c1\u6768\u4ef2\u82f1\u3002\u5979\u867d\u5df2\u957f\u5927\uff0c\u5374\u8fd8\u662f\u4e00\u7247\u5b69\u5b50\u5fc3 \u60c5\u3002\u5979\u56e0\u4e3a\u66fe\u7528\u5200\u524a\u4e86\u6768\u67f3\u9752\u7684\u5934\u53d1\uff0c\u9891\u53d7\u59d0\u59d0\u57cb\u6028\uff0c\u4fbf\u8d77\u4e86\u4e00\u4e2a\u5b69\u5b50\u7684\u5ff5\u5934\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a \u59d0\u59d0\u6789\u662f\u5973\u4e2d\u4fa0\u5ba2\uff0c\u5bf9\u81ea\u5df1\u7684\u5a5a\u59fb\u5927\u4e8b\uff0c\u5374\u4e0d\u6562\u723d\u723d\u5feb\u5feb\uff0c\u81ea\u4f5c\u4e3b\u5f20\uff0c\u4e0d\u5982\u6211\u518d\u5192\u5145\u5979\u4e00 \u6b21\uff0c\u627e\u4e0a\u95e8\u53bb\uff0c\u76f4\u622a\u4e86\u5f53\uff0c\u5bf9\u90a3\u6768\u8001\u5934\u513f\u8bf4\u4e86\uff0c\u7701\u5f97\u8bb8\u591a\u9ebb\u70e6\u3002\u6211\u7528\u5feb\u5200\u65a9\u4e71\u9ebb\u7684\u624b\u6bb5\u66ff\u5979 \u64ae\u6210\u597d\u4e8b\uff0c\u770b\u5979\u8fd8\u57cb\u6028\u6211\u4e0d\uff1f \u3000\u3000\u51af\u7433\u5c31\u662f\u62b1\u5b9a\u8fd9\u4e2a\u4e3b\u610f\uff0c\u6765\u5230\u5c71\u4e1c\u4e1c\u5e73\uff0c\u6768\u5bb6\u8fdc\u8fd1\u7686\u77e5\uff0c\u5e76\u4e0d\u96be\u627e\u3002\u590f\u79cb\u4e4b\u4ea4\uff0c\u9887\u591a\u9716 \u96e8\uff0c\u8fd9\u65e5\u96e8\u540e\u5929\u6674\uff0c\u51af\u7433\u6765\u5230\u4e86\u6768\u4ef2\u82f1\u7684\u5c71\u5e84\uff0c\u4f46\u89c1\u6768\u5bb6\u80cc\u5c71\u9762\u6e56\uff0c\u98ce\u666f\u9887\u4e3a\u4f73\u4e3d\uff0c\u53ea\u662f\u90a3 \u6e56\u6c34\u56e0\u53d7\u5c71\u6d2a\u503e\u6ce8\uff0c\u9ec4\u6ce5\u6cdb\u8d77\uff0c\u4e00\u7247\u6df7\u6d4a\uff0c\u6709\u70b9\u513f\u715e\u98ce\u666f\u3002\u6768\u5bb6\u662f\u51e0\u5ea7\u5e73\u623f\uff0c\u4f9d\u5c71\u5efa\u7b51\uff0c\u51af \u7433\u4e5f\u65e0\u5fc3\u8d4f\u73a9\u98ce\u666f\uff0c\u8d70\u4e0a\u5c71\u5761\uff0c\u8ff3\u81ea\u6765\u6263\u6768\u5bb6\u7684\u5927\u95e8\uff0c\u5fc3\u4e2d\u5728\u60f3\uff1a\u7b49\u4e0b\u6211\u89c1\u4e86\u90a3\u6768\u8001\u5934\u513f\uff0c \u7b2c\u4e00\u53e5\u8bdd\u8bf4\u4ec0\u4e48\u597d\u5462\uff1f \u3000\u3000\u4e0d\u6599\u6572\u95e8\u8bb8\u4e45\uff0c\u91cc\u9762\u5374\u65e0\u4eba\u7b54\u5e94\u3002\u51af\u7433\u4e00\u6025\uff0c\u987e\u4e0d\u5f97\u4ec0\u4e48\u793c\u8c8c\uff0c\u4e00\u98de\u8eab\u4fbf\u4ece\u56f4\u5899\u98de\u5165\uff0c \u53ea\u89c1\u91cc\u9762\u5ead\u9662\u6df1\u6df1\uff0c\u4e00\u4e2a\u5c0f\u4e2b\u5934\u5927\u7ea6\u662f\u624d\u542c\u89c1\u6572\u95e8\u4e4b\u58f0\uff0c\u6b63\u5728\u91cc\u9762\u6162\u6162\u7684\u8d70\u51fa\u6765\u3002\u90a3\u4e2b\u5934\u89c1 \u4e86\u51af\u7433\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u56b7\u9053\uff1a\u201c\u54a6\uff0c\u539f\u6765\u662f\u4f60\uff0c\u4f60\u8fd8\u6765\u505a\u4ec0\u4e48\uff01\u201d\u51af\u7433\u9053\uff1a\u201c\u6768\u8001\u7237\u5b50\u5462\uff1f\u4ed6 \u8001\u4eba\u5bb6\u7684\u817f\u53ef\u597d\u70b9\u4e86\uff1f\u201d\u90a3\u4e2b\u5934\u9762\u8272\u4e00\u6c89\uff0c\u7231\u7406\u4e0d\u7406\u3002\u51af\u7433\u5fc3\u9053\uff1a\u201c\u8fd9\u4e2a\u4e00\u5b9a\u662f\u6768\u67f3\u9752\u7684\u8d34 \u8eab\u4e2b\u5934\uff0c\u628a\u6211\u5f53\u6210\u59d0\u59d0\uff0c\u6240\u4ee5\u5bf9\u6211\u607c\u6068\u3002\u201d\u7b11\u9053\uff1a\u201c\u4f60\u5bb6\u5c0f\u59d0\u7684\u5934\u53d1\u957f\u5168\u6ca1\u6709\uff1f\u4f60\u5e26\u6211\u53bb\u89c1 \u5979\u5427\uff0c\u6211\u7ed9\u5979\u8d54\u7f6a\u6765\u4e86\u3002\u201d\u90a3\u4e2b\u5934\u624b\u513f\u4e00\u6454\uff0c\u6447\u5934\u8bf4\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u53bb\u89c1\u5979\uff0c\u54fc\uff0c\u54fc\uff0c\u4f60\u8fd8\u597d \u610f\u601d\u5230\u8fd9\u91cc\u3002\u201d\u8bf4\u5b8c\uff0c\u4e00\u6e9c\u70df\u7684\u8dd1\u4e86\u3002\u51af\u7433\u4e00\u6c14\uff0c\u60f3\u7528\u6ce5\u4e38\u5f39\u5979\u3002\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u5173\u8fd9\u5c0f\u4e2b\u5934 \u4ec0\u4e48\u4e8b\uff1f\u201d\u7f29\u4f4f\u4e86\u624b\uff0c\u81ea\u5df1\u7a7f\u623f\u5165\u5ba4\uff0c\u53bb\u627e\u6768\u67f3\u9752\u3002 \u3000\u3000\u51af\u7433\u4e0d\u719f\u95e8\u6237\uff0c\u8d70\u5165\u5185\u8fdb\u623f\u5c4b\uff0c\u4f46\u89c1\u4e00\u7247\u9eef\u6de1\u6c14\u6c1b\uff0c\u5bb6\u79c1\u6742\u7269\uff0c\u51cc\u4e71\u65e0\u4eba\u6574\u7406\u3002\u51af\u7433\u5fc3 \u9053\uff1a\u201c\u6768\u4ef2\u82f1\u662f\u5317\u4e94\u7701\u7684\u6b66\u6797\u9886\u8896\uff0c\u600e\u4e48\u4e00\u70b9\u4e5f\u4e0d\u61c2\u6301\u5bb6\uff0c\u53eb\u4eba\u770b\u5230\uff0c\u5c82\u4e0d\u7b11\u8bdd\uff1f\u201d\u7ad9\u5728\u5185 \u5802\uff0c\u53eb\u9053\uff1a\u201c\u6768\u516c\u516c\uff0c\u6768\u516c\u516c\uff01\u201d\u5979\u5b8c\u5168\u6a21\u4eff\u5979\u59d0\u59d0\u7684\u79f0\u547c\uff0c\u5fc3\u9053\uff1a\u201c\u4ec5\u6709\u8fd9\u51e0\u95f4\u623f\u5c4b\uff0c\u6768 \u4ef2\u82f1\u4e00\u5b9a\u4f1a\u542c\u5230\u6211\u53eb\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5185\u623f\u9690\u9690\u4f20\u6765\u62bd\u6ce3\u4e4b\u58f0\uff0c\u51af\u7433\u7ad6\u8033\u4e00\u542c\uff0c\u5947\u9053\uff1a\u201c\u54a6\uff0c\u6768\u67f3\u9752\u8fd9\u6cfc\u5a46\u5a18\u542c\u5f97\u6211\u6765\u4fbf\u54ed \u4e86\uff0c\u96be\u9053\u662f\u5411\u5979\u7684\u7236\u4eb2\u6492\u5a07\uff0c\u8981\u5bf9\u4ed8\u6211\u4e48\uff1f\u54fc\uff0c\u597d\u4e0d\u5bb3\u7f9e\uff0c\u6492\u5a07\u4e5f\u4e0d\u8be5\u54ed\u5475\uff01\u201d\u53c8\u53eb\u4e86\u4e24\u58f0 \u201c\u6768\u516c\u516c\u201d\uff0c\u4ecd\u7136\u662f\u53ea\u95fb\u6768\u67f3\u9752\u7684\u62bd\u6ce3\u4e4b\u58f0\uff0c\u5374\u4e0d\u89c1\u6768\u4ef2\u82f1\u56de\u7b54\u3002 \u3000\u3000\u51af\u7433\u5fc3\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u5148\u53bb\u89c1\u89c1\u6768\u67f3\u9752\u3002\u201d\u542c\u5f97\u54ed\u58f0\u53d1\u81ea\u897f\u9996\u7b2c\u4e00\u95f4\u623f\uff0c\u4fbf\u63ed\u4e86\u5e18\u5b50\u81ea \u95ef\u8fdb\u53bb\uff0c\u4f46\u89c1\u6768\u67f3\u9752\u5750\u5728\u623f\u4e2d\uff0c\u773c\u775b\u80bf\u5f97\u50cf\u80e1\u6843\u4e00\u822c\uff0c\u6ca1\u7cbe\u6253\u91c7\u3002\u51af\u7433\u95ef\u8fdb\u6765\uff0c\u5979\u53ea\u51b7\u51b7\u7684 \u77a7\u4e86\u4e00\u4e0b\u3002\u62bd\u6ce3\u58f0\u662f\u505c\u6b62\u4e86\uff0c\u9762\u4e0a\u7684\u8868\u60c5\u5374\u66f4\u53eb\u4eba\u96be\u53d7\u3002\u51af\u7433\u867d\u65e9\u6599\u5230\u5979\u5bf9\u81ea\u5df1\u4e0d\u6ee1\uff0c\u4f46\u5374 \u6599\u4e0d\u5230\u5979\u7adf\u662f\u8fd9\u6837\u4e00\u526f\u597d\u4f3c\u6b7b\u4e86\u4eba\u7684\u795e\u60c5\uff0c\u4e0d\u7981\u6115\u5728\u5f53\u573a\uff0c\u4ed4\u7ec6\u5411\u6768\u67f3\u9752\u6253\u91cf\u3002 \u3000\u3000\u6768\u67f3\u9752\u4e00\u8eab\u767d\u8863\uff0c\u88ab\u98de\u5200\u524a\u8fc7\u7684\u5934\u53d1\u65e9\u5df2\u957f\u4e86\u51fa\u6765\uff0c\u4f46\u56e0\u4e0e\u4e24\u8fb9\u7684\u5934\u53d1\u53c2\u5dee\u4e0d\u9f50\uff0c\u4ecd\u7136 \u96be\u770b\u3002\u51af\u7433\u201c\u5582\uff0c\u5582\uff01\u201d\u4e24\u58f0\uff0c\u6768\u67f3\u9752\u500f\u7136\u62ac\u8d77\u5934\u6765\uff0c\u9762\u4e0a\u5168\u65e0\u8840\u8272\uff0c\u53cc\u773c\u4e00\u7741\uff0c\u5ffd\u53c8\u5782\u4e0b \u4e86\u5934\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u6653\u6f9c\u5462\uff1f\u201d \u3000\u3000\u51af\u7433\u6545\u610f\u6c14\u5979\u9053\uff1a\u201c\u5510\u53d4\u53d4\u4e0d\u613f\u89c1\u4f60\u4e86\uff0c\u4f60\u6709\u4ec0\u4e48\u8bdd\u8981\u5bf9\u4ed6\u8bf4\uff0c\u5bf9\u6211\u8bf4\u4e5f\u662f\u4e00\u6837\u3002\u201d\u5fc3 \u4e2d\u51c6\u5907\u5979\u5927\u53eb\u5927\u56b7\uff0c\u9a6c\u4e0a\u53d1\u4f5c\uff0c\u5374\u4e0d\u60f3\u6768\u67f3\u9752\u5ffd\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6653\u6f9c\u771f\u662f\u8fd9\u6837\u5168\u6ca1\u5fc3\u809d \u5417\uff1f\u6789\u6211\u7238\u7238\u75db\u4ed6\u4e00\u573a\u4e86\u3002\u201d\u8bed\u8c03\u51c4\u51c9\u4e4b\u6781\uff0c\u51af\u7433\u4e5f\u4e0d\u89c9\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u95ee\u9053\uff1a\u201c\u6768\u516c\u516c\u5462\uff0c \u6211\u8981\u66ff\u5510\u53d4\u53d4\u5411\u4ed6\u95ee\u5b89\u3002\u201d \u3000\u3000\u6768\u67f3\u9752\u9661\u7136\u7ad9\u8d77\uff0c\u6068\u6068\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6765\u5427\uff0c\u4f60\u6765\u5411\u4ed6\u8bf7\u5b89\u5427\uff01\u201d\u5e26\u51af\u7433\u7a7f\u623f\u8fc7\u5c4b\uff0c\u6765 \u5230\u540e\u56ed\uff0c\u5728\u56ed\u5b50\u4e1c\u9762\u6709\u4e00\u6240\u516b\u89d2\u4eab\u3002\u4ead\u4e2d\u505c\u7740\u4e00\u526f\u7ea2\u6728\u68fa\u6750\uff0c\u68fa\u6750\u5934\u4e00\u5f20\u767d\u5f20\uff0c\u5199\u7684\u662f\uff1a\u524d \u660e\u4e49\u58eb\u5c71\u4e1c\u4fa0\u5ba2\u6768\u4ef2\u82f1\u4e4b\u7075\u4f4d\u3002 \u3000\u3000\u51af\u7433\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff01\u5979\u4e07\u4e07\u6599\u4e0d\u5230\u6768\u4ef2\u82f1\u5df2\u6b7b\uff0c\u8fd9\u4e2a\u7a81\u7136\u7684\u53d8\u5316\u5b8c\u5168\u6467\u6bc1\u4e86\u5979\u7684\u8ba1 \u5212\uff0c\u770b\u7740\u90a3\u526f\u68fa\u6728\uff0c\u597d\u4e45\u597d\u4e45\u624d\u8bf4\u5f97\u51fa\u58f0\uff1a\u201c\u6768\u516c\u516c\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u6768\u67f3\u9752\u5934\u53d1\u4e00\u62ab\uff0c\u9053\uff1a\u201c\u6653\u6f9c\u771f\u4e2a\u4e0d\u6765\u4e86\u4e48\uff1f\u201d\u51af\u7433\u4e00\u65f6\u95f4\u7b54\u4e0d\u51fa\u8bdd\u6765\uff0c\u6768\u67f3\u9752\u6012\u9053\uff1a \u201c\u597d\uff0c\u6211\u7239\u6b7b\u4e86\uff0c\u4f60\u4eec\u8be5\u5fc3\u6ee1\u610f\u8db3\u4e86\u5427\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff1f\u201d\u5f2f\u8170\u4e0b\u62dc\u3002\u6768\u67f3\u9752 \u9053\uff1a\u201c\u4e0d\u8981\u4f60\u62dc\uff0c\u4f60\u6c14\u6b7b\u4e86\u7239\u8fd8\u4e0d\u591f\uff0c\u53c8\u8981\u6c14\u6b7b\u6211\u5427\uff1f\u201d\u4f38\u624b\u6b32\u6253\u51af\u7433\uff0c\u51af\u7433\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u6768 \u67f3\u9752\u624b\u638c\u4f38\u51fa\uff0c\u5ffd\u53c8\u7f29\u4f4f\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u4f60\u5feb\u8d70\u5427\uff01\u4f60\u4eec\u4ee5\u540e\u522b\u518d\u4e0a\u6211\u6768\u5bb6\u7684\u95e8 \u4e86\uff01\u201d\u58f0\u97f3\u867d\u7136\u6124\u61d1\uff0c\u5374\u4f3c\u7f13\u548c\u8bb8\u591a\u3002\u51af\u7433\u5947\u9053\uff1a\u201c\u54a6\uff0c\u6768\u4ef2\u82f1\u4e00\u6b7b\uff0c\u4ed6\u5973\u513f\u7684\u813e\u6c14\u4e5f\u53d8 \u4e86\uff01\u201d \u3000\u3000\u51af\u7433\u6709\u6240\u4e0d\u77e5\u3002\u539f\u6765\u6768\u4ef2\u82f1\u5e74\u7eaa\u8001\u8fc8\uff0c\u4e2d\u4e86\u5510\u91d1\u5cf0\u7684\u6697\u5668\u540e\uff0c\u867d\u8bf4\u4ed7\u7740\u6570\u5341\u5e74\u7684\u529f\u529b\u4e0e \u5510\u5bb6\u9001\u6765\u7684\u89e3\u836f\uff0c\u5f97\u4ee5\u4e0d\u6b7b\uff0c\u53ef\u662f\u751f\u673a\u5df2\u662f\u6e10\u6e10\u8870\u9000\u3002\u4e94\u6708\u65f6\u5206\uff0c\u63a5\u5230\u5510\u6653\u6f9c\u7684\u4fe1\uff0c\u8bf4\u662f\u6b7b \u671f\u5c06\u81f3\uff0c\u65e0\u53ef\u633d\u6551\uff0c\u53c8\u53d7\u4e86\u4e00\u5413\uff0c\u4ed6\u672c\u6765\u5df1\u98ce\u70db\u6b8b\u5e74\uff0c\u7ecf\u4e86\u8fd9\u4e9b\u53d8\u6545\uff0c\u8eab\u4f53\u66f4\u662f\u8870\u5f31\u3002 \u3000\u3000\u6768\u67f3\u9752\u7c97\u5fc3\u5927\u610f\uff0c\u5bf9\u7236\u4eb2\u7684\u65e5\u8d8b\u8870\u5f31\uff0c\u8fd8\u89c9\u5bdf\u4e0d\u51fa\u6765\u3002\u5979\u88ab\u51af\u7433\u98de\u5200\u524a\u53d1\u4e4b\u540e\uff0c\u8dd1\u56de\u5bb6 \u4e2d\u5411\u7236\u4eb2\u54ed\u56b7\uff0c\u60f3\u6fc0\u52a8\u7236\u4eb2\u51fa\u5934\u4f5c\u4e3b\uff0c\u8c01\u77e5\u6768\u4ef2\u82f1\u6df1\u77e5\u5973\u513f\u813e\u6027\uff0c\u6599\u5979\u5fc5\u662f\u81ea\u53d6\u5176\u548e\uff0c\u7ecf\u6b64 \u4e00\u95f9\uff0c\u53cd\u800c\u4f24\u611f\u4ea4\u96c6\uff0c\u6768\u67f3\u9752\u56de\u5bb6\u7684\u7b2c\u4e8c\u65e5\uff0c\u4ed6\u7acb\u523b\u5bd2\u70ed\u4ea4\u4f5c\uff0c\u7adf\u7136\u4e00\u75c5\u4e0d\u8d77\uff0c\u81f3\u51af\u7433\u5230 \u65f6\uff0c\u4ed6\u6b7b\u4e86\u5df2\u5c06\u8fd1\u4e00\u6708\u4e86\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u662f\u4e2a\u9971\u7ecf\u4e16\u6545\u4e4b\u4eba\uff0c\u4e34\u6b7b\u4e4b\u524d\uff0c\u795e\u667a\u6e05\u660e\uff0c\u56de\u60f3\u81ea\u5df1\u4e00\u751f\u884c\u4e8b\uff0c\u65e0\u751a\u8fc7\u9519\uff0c\u53ea\u662f \u5bf9\u5973\u513f\u592a\u8fc7\u5ba0\u5708\uff0c\u4ee5\u81f4\u517b\u6210\u5979\u90a3\u526f\u9a84\u7eb5\u7684\u813e\u6c14\uff0c\u5374\u662f\u6700\u5927\u7684\u9057\u61be\u3002\u4ed6\u7ec6\u7ec6\u601d\u91cf\uff0c\u89c9\u5f97\u5973\u513f\u548c \u5510\u6653\u6f9c\u7684\u813e\u6c14\uff0c\u7684\u786e\u683c\u683c\u4e0d\u5165\u3002\u53c8\u60f3\u9053\uff1a\u201c\u51af\u745b\u77e5\u4e66\u8bc6\u793c\uff0c\u5e74\u7eaa\u867d\u5c0f\uff0c\u505a\u4e8b\u751a\u6709\u5206\u5bf8\uff0c\u5979\u5fc5 \u4e0d\u4f1a\u65e0\u7f18\u65e0\u6545\u4fae\u8fb1\u6211\u8fd9\u4e2b\u5934\u3002\u201d\u53c8\u60f3\u8d77\u6614\u65e5\u51af\u745b\u5728\u4ed6\u5bb6\u4e2d\u4e4b\u65f6\uff0c\u6768\u67f3\u9752\u79cd\u79cd\u4ee4\u5979\u53d7\u6c14\u4e4b\u4e8b\uff0c \u4e0d\u89c9\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5982\u6b64\u4e00\u6765\uff0c\u903c\u5f97\u4ed6\u4eec\u5f04\u5047\u6210\u771f\uff0c\u4e5f\u5b9e\u5728\u602a\u8d23\u4ed6\u4eec\u4e0d\u5f97\uff01\u201d \u3000\u3000\u4e8e\u662f\u6768\u4ef2\u82f1\u5728\u4e34\u6b7b\u4e4b\u524d\uff0c\u5bf9\u5973\u513f\u75db\u52a0\u529d\u8d23\uff0c\u8bf4\u9053\uff1a\u201c\u5973\u5b69\u513f\u5bb6\uff0c\u5e94\u4ee5\u6027\u60c5\u6e29\u67d4\u4e3a\u4e3b\u3002\u4f60 \u8fd9\u526f\u5201\u86ee\u7684\u6027\u513f\uff0c\u96be\u602a\u6653\u6f9c\u4e0d\u613f\u8981\u4f60\u3002\u4f60\u518d\u4e0d\u6539\u8fc7\uff0c\u6211\u6b7b\u4e0d\u7791\u76ee\u3002\u201d\u58f0\u6cea\u4ff1\u4e0b\uff0c\u6768\u67f3\u9752\u4e0d\u6562 \u8bf4\u8bdd\u3002\u6768\u4ef2\u82f1\u5386\u6570\u5979\u5e73\u65e5\u9a84\u7eb5\u7684\u4e0d\u662f\uff0c\u6768\u67f3\u9752\u53c8\u7f9e\u60ed\u53c8\u60b2\u75db\uff0c\u4f0f\u5728\u75c5\u584c\u4e4b\u65c1\uff0c\u542c\u5979\u7236\u4eb2\u6570 \u8bf4\u3002\u6768\u4ef2\u82f1\u6570\u8bf4\u5b8c\u540e\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u540e\u6094\u4ee5\u524d\u6ca1\u6709\u597d\u597d\u7684\u6559\u8bad\u4f60\uff0c\u8fd9\u6b21\u6050\u6015\u662f\u6700\u540e \u4e00\u6b21\u4e86\u3002\u7238\u603b\u662f\u671b\u4f60\u597d\uff0c\u4f60\u5f97\u8bb0\u7740\u6211\u4eca\u65e5\u7684\u6559\u8bad\u3002\u4f60\u4e0e\u6653\u6f9c\u662f\u5426\u80fd\u591f\u548c\u597d\uff0c\u8fd9\u662f\u672a\u53ef\u77e5\u4e4b \u6570\u3002\u4e0d\u8fc7\uff0c\u4f60\u5e94\u77e5\u9053\uff0c\u4f60\u8d8a\u4efb\u60c5\u4f7f\u6027\uff0c\u4f60\u5c31\u8d8a\u65e0\u6cd5\u4ee4\u4ed6\u4eb2\u8fd1\u3002\u4f60\u653e\u5927\u80f8\u895f\uff0c\u6e29\u67d4\u5bf9\u4ed6\uff0c\u4e5f\u8bb8 \u4e8b\u60c5\u8fd8\u6709\u8f6c\u673a\u3002\u82e5\u7136\u4f60\u4eec\u7ec8\u4e0d\u80fd\u548c\u597d\uff0c\u90a3\u4e5f\u5c31\u7b97\u4e86\u5427\u3002\u4e0d\u8fc7\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4f60\u7684\u6027\u60c5\u603b\u5f97\u6539 \u4e86\uff0c\u9752\u513f\uff0c\u4ee5\u540e\u6ca1\u4eba\u518d\u6559\u8bad\u4f60\u4e86\uff0c\u4f60\u6539\u4e0d\u6539\uff1f\u201d\u6768\u67f3\u9752\u54ed\u5f97\u6b7b\u53bb\u6d3b\u6765\uff0c\u51b3\u5fc3\u6539\u8fc7\uff0c\u6768\u4ef2\u82f1\u5c31 \u5728\u5979\u7684\u54ed\u58f0\u4e4b\u4e2d\u6b7b\u4e86\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u6b7b\u540e\uff0c\u6768\u67f3\u9752\u9075\u4ece\u9057\u5631\uff0c\u505c\u7075\u540e\u56ed\uff0c\u8981\u5f85\u5510\u6653\u6f9c\u548c\u53e6\u5916\u4e00\u4e2a\u4eba\u6765\u8fc7\u4e4b\u540e\u624d\u5b89\u846c\u3002 \u4e0d\u6599\u5510\u6653\u6f9c\u6ca1\u6709\u6765\uff0c\u51af\u7433\u5374\u5148\u6765\u4e86\u3002 \u3000\u3000\u6768\u67f3\u9752\u8bb0\u7740\u7236\u4eb2\u7684\u6559\u8bad\uff0c\u4e0d\u6562\u80e1\u4e71\u53d1\u6c14\uff0c\u53ef\u662f\u6027\u60c5\u7a76\u975e\u65e6\u5915\u4e4b\u95f4\u4fbf\u80fd\u5168\u6539\uff0c\u89c1\u4e86\u51af\u7433\uff0c \u4ecd\u7136\u5fcd\u4e0d\u4f4f\u51e0\u4e4e\u8981\u53d1\u4f5c\u51fa\u6765\uff0c\u4ee5\u81f3\u5728\u6768\u4ef2\u82f1\u4e4b\u7075\u524d\uff0c\u4e24\u4eba\u90fd\u611f\u5230\u5c34\u5c2c\uff0c\u50f5\u5728\u90a3\u513f\uff0c\u60f3\u4e0d\u51fa\u4ec0 \u4e48\u8bdd\u8bf4\u3002 \u3000\u3000\u6768\u4ef2\u82f1\u4e4b\u6b7b\uff0c\u4e43\u662f\u51af\u7433\u59cb\u6599\u6240\u4e0d\u53ca\uff0c\u5fc3\u9053\uff1a\u201c\u59d0\u59d0\u4e4b\u4e8b\u600e\u4e48\u8bf4\u5462\uff0c\uff01\u8fd9\u5c82\u4e0d\u662f\u6108\u5f04\u6108\u9ebb \u70e6\u4e86\uff1f\u6b63\u5728\u4e3a\u96be\uff0c\u5148\u524d\u90a3\u5c0f\u4e2b\u5934\u5ffd\u7136\u6c14\u6025\u8d25\u574f\u7684\u8d70\u4e86\u8fdb\u6765\uff0c\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u5510\u5bb6\u7684\u4eba\u53c8\u6765\u4e86\uff01\u201d \u3000\u3000\u6768\u67f3\u9752\u7709\u6bdb\u4e00\u626c\uff0c\u9053\uff1a\u201c\u6211\u7236\u4eb2\u867d\u6b7b\uff0c\u6211\u4e5f\u4e0d\u80fd\u5815\u4e86\u5bb6\u58f0\u3002\u51af\u745b\uff0c\u4f60\u5feb\u4ece\u540e\u5899\u8131\u8d70\u3002\u6211 \u62fc\u6b7b\u66ff\u4f60\u62c5\u627f\uff01\u201d\u51af\u7433\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u6211\u6709\u4ec0\u4e48\u8981\u4f60\u66ff\u6211\u62c5\u627f\uff01\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u4f60\u8fd8\u88c5\u4ec0\u4e48 \u50bb\uff0c\u4f60\u81ea\u5df1\u6740\u7684\u4eba\u4f60\u4e0d\u77e5\u9053\u5417\uff1f\u4f60\u522b\u4ee5\u4e3a\u4f60\u4e0a\u6b21\u80fd\u5c06\u4ed6\u4eec\u6253\u8dd1\uff0c\u8981\u77e5\u5510\u5bb6\u7684\u4eba\uff0c\u5c82\u662f\u5bb9\u6613\u5c06 \u4e0e\uff1f\u4ed6\u8fd9\u6b21\u82e5\u975e\u7a33\u64cd\u80dc\u5238\uff0c\u4e5f\u4e0d\u4f1a\u518d\u6765\u4e86\u3002\u6211\u7236\u4eb2\u751f\u524d\uff0c\u4e0d\u613f\u4f60\u5728\u6211\u4eec\u8fd9\u91cc\u88ab\u4ed6\u4eec\u8981\u53bb\uff0c\u800c \u4eca\u6211\u662f\u6b64\u5bb6\u4e4b\u4e3b\uff0c\u6211\u4e0d\u80fd\u8ba9\u7236\u4eb2\u5728\u6cc9\u4e0b\u9a82\u6211\u6298\u5815\u4e86\u6768\u5bb6\u7684\u5a01\u540d\uff0c\u4f60\u8fd8\u4e0d\u5feb\u8d70\u5417\uff1f\u201d \u3000\u3000\u51af\u7433\u4e00\u542c\uff0c\u6c14\u5f80\u4e0a\u51b2\uff0c\u6012\u9053\uff1a\u201c\u6211\u4e00\u4eba\u505a\u4e8b\u4e00\u4eba\u5f53\uff0c\u8c01\u8981\u4f60\u6765\u5e87\u62a4\uff1f\u6211\u4e3a\u4ec0\u4e48\u8981\u8dd1\uff1f\u201d \u51b2\u51fa\u4ead\u5b50\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u5916\u9762\u6765\u4e86\u4e09\u4eba\uff0c\u4e00\u4e2a\u8001\u5934\uff0c\u4e00\u4e2a\u5c11\u5987\uff0c\u8fd8\u6709\u4e00\u4e2a\u4e09\u5341\u5c81\u5de6\u53f3\u7684\u6c49 \u5b50\u3002\u8fd9\u4e09\u4eba\u6b63\u662f\u5510\u91d1\u5cf0\u3001\u5510\u8d5b\u82b1\uff0c\u548c\u5510\u91d1\u5cf0\u63d0\u6765\u7684\u5e2e\u624b\u6842\u534e\u751f\u3002 \u3000\u3000\u5510\u91d1\u5cf0\u7236\u5973\u672c\u6765\u60f3\u5411\u6768\u4ef2\u82f1\u8981\u4eba\uff0c\u4e0d\u6599\u4e00\u8fdb\u56ed\u95e8\uff0c\u4fbf\u89c1\u51af\u7433\uff0c\u771f\u662f\u610f\u6599\u4e0d\u5230\u7684\u987a\u5229\u3002\u5510 \u91d1\u5cf0\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u8d3c\u4e5f\u771f\u80c6\u5927\uff0c\u5c45\u7136\u8fd8\u5728\u6768\u5bb6\u6ca1\u6709\u9003\u8d70\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u8d3c\uff0c\u51fa \u53e3\u4f24\u4eba\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u9003\u8d70\uff1f\u201d\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u4f60\u82e5\u4e0d\u60f3\u8fde\u7d2f\u6768\u8001\u5934\u513f\uff0c\u6211\u6709\u4e24\u6761\u8def\u7ed9 \u4f60\u81ea\u5bfb\u4e86\u65ad\uff01\u201d \u3000\u3000\u51af\u7433\u9053\uff1a\u201c\u4ec0\u4e48\u4e24\u6761\u8def\uff1f\u4f60\u8bf4\u8bf4\u770b\u3002\u201d\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u4e00\u6761\u662f\u7acb\u5373\u968f\u6211\u4eec\u8d70\uff0c\u4efb\u7531\u6211\u4eec\u5904 \u7f6e\u3002\u4e00\u6761\u662f\u7acb\u5373\u81ea\u88c1\uff0c\u514d\u5f97\u6211\u4eec\u52a8\u624b\u3002\u201d\u51af\u7433\u521a\u9a82\u5f97\u4e00\u58f0\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff01\u201d\u5510\u8d5b\u82b1\u56b7\u9053\uff1a \u201c\u7239\uff0c\u548c\u8fd9\u4e07\u6076\u7684\u5973\u8d3c\u591a\u8bf4\u505a\u4ec0\u4e48\uff1f\u5feb\u52a8\u624b\u5427\uff01\u201d\u6043\u7740\u6709\u9ad8\u624b\u5728\u65c1\uff0c\u626c\u624b\u4e00\u67c4\u98de\u5200\u4fbf\u5c04\u8fc7\u53bb\u3002 \u3000\u3000\u51af\u7433\u4e00\u95ea\u95ea\u5f00\uff0c\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u4f60\u4e5f\u4f1a\u98de\u5200\uff01\u4f60\u8fd9\u6cfc\u5a46\u5a18\uff0c\u6211\u6740\u4e86\u4f60\u7684\u6c49\u5b50\u5417\uff1f\u4f60\u8fd9\u6837 \u86ee\u4e0d\u8bb2\u7406\uff01\u201d\u51af\u7433\u8fd8\u4e0d\u77e5\u9053\u5510\u8d5b\u82b1\u5c31\u662f\u738b\u6556\u7684\u59bb\u5b50\uff0c\u81ea\u5df1\u6b63\u662f\u6740\u4e86\u5979\u7684\u6c49\u5b50\u3002 \u3000\u3000\u5510\u91d1\u5cf0\u4e00\u542c\uff0c\u4e5f\u52a8\u6012\u4e86\uff0c\u9a82\u9053\uff1a\u201c\u597d\u5973\u8d3c\uff0c\u4f60\u6740\u4e86\u6211\u7684\u5973\u5a7f\uff0c\u8fd8\u8bf4\u98ce\u51c9\u8bdd\u513f\uff1f\u201d\u5fc3\u5ff5\u4e00 \u52a8\uff0c\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8fd8\u6709\u4e00\u4e2a\u59d0\u59b9\uff0c\u4f60\u4eec\u59d0\u59b9\uff0c\u8c01\u662f\u6740\u4eba\u7684\u6b63\u70b9\uff1f\u201d\u51af\u7433\u5403\u4e86\u4e00\u60ca\uff0c \u9053\uff1a\u201c\u4f60\u7684\u5973\u5a7f\u662f\u8c01\uff1f\u201d\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u6cb3\u5357\u94a9\u9570\u67aa\u738b\u6556\u662f\u4e0d\u662f\u4f60\u6740\u7684\uff1f\u201d\u51af\u7433\u201c\u5478\u201d\u7684\u4e00 \u58f0\uff0c\u7b11\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff1f\u539f\u6765\u4f60\u7684\u5973\u5a7f\u662f\u516c\u95e8\u9e70\u72ac\uff0c\u6211\u6740\u7684\u9e70\u72ac\u4e0d\u6b62\u4e00\u4e2a\uff0c\u4f60\u7684\u5973\u5a7f\u5927\u7ea6\u4e5f \u662f\u6211\u5251\u4e0b\u4e4b\u9b3c\u5427\uff01\u201d \u3000\u3000\u5510\u91d1\u5cf0\u52c3\u7136\u5927\u6012\uff0c\u957f\u8896\u4e00\u6325\uff0c\u4fbf\u5f85\u6251\u53bb\uff0c\u5ffd\u89c1\u6768\u67f3\u9752\u5982\u98de\u8dd1\u6765\uff0c\u5510\u91d1\u5cf0\u7f29\u624b\u53eb\u9053\uff1a\u201c\u9752 \u59d1\u5a18\uff0c\u53eb\u4f60\u7684\u8001\u5b50\u51fa\u6765\uff0c\u8fd9\u5973\u8d3c\u6211\u4eec\u8981\u5b9a\u4e86\u3002\u201d\u6768\u67f3\u9752\u53eb\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u7684\u7236\u4eb2\u521a\u6b7b\uff0c\u4f60\u4eec \u5c31\u4e0a\u95e8\u6765\u6b3a\u8d1f\u6211\u4e86\u4e48\uff1f\u201d\u5510\u91d1\u5cf0\u4e0e\u6768\u4ef2\u82f1\u4e0a\u6b21\u867d\u66fe\u52a8\u8fc7\u624b\uff0c\u53ef\u662f\u5f7c\u6b64\u6709\u4e8c\u5341\u5e74\u4ee5\u4e0a\u7684\u4ea4\u60c5\uff0c \u79c1\u5e95\u4e0b\u5510\u91d1\u5cf0\u5bf9\u6768\u4ef2\u82f1\u8fd8\u662f\u5341\u5206\u4f69\u670d\u7684\uff0c\u95fb\u8a00\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u7684\u8001\u5b50\u6b7b \u4e86\u5417\uff1f\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u6211\u7236\u4eb2\u867d\u6b7b\uff0c\u6768\u5bb6\u5a01\u540d\u8fd8\u5728\uff0c\u9664\u975e\u4f60\u628a\u6211\u6740\u6b7b\uff0c\u5426\u5219\u4f11\u60f3\u8981\u4eba\uff01\u201d\u5510 \u8d5b\u82b1\u53eb\u9053\uff1a\u201c\u7239\uff0c\u7ba1\u5b83\u6768\u4ef2\u82f1\u6b7b\u4e0e\u4e0d\u6b7b\u3002\u54b1\u4eec\u52a8\u624b\u3002\u6768\u67f3\u9752\uff0c\u51ed\u4f60\u8fd9\u70b9\u529f\u592b\u60f3\u6765\u62e6\u963b\uff0c\u771f\u771f \u7b11\u8bdd\uff01\u201d\u5de6\u624b\u4e00\u62ac\uff0c\u545c\u545c\u4e24\u58f0\uff0c\u653e\u51fa\u4e24\u679a\u54cd\u7bad\uff0c\u8981\u628a\u6768\u67f3\u9752\u5413\u8d70\uff0c\u5c82\u77e5\u6768\u67f3\u9752\u52a8\u4e86\u86ee\u6027\uff0c\u8fce \u4e0a\u524d\u53bb\uff0c\u4f38\u624b\u4fbf\u63a5\u3002\u5510\u8d5b\u82b1\u6697\u5668\u4e0a\u7684\u529f\u592b\u751a\u4e3a\u4e86\u5f97\uff0c\u54cd\u7bad\u631f\u98ce\uff0c\u53c8\u52b2\u53c8\u75be\uff0c\u51af\u7433\u4e00\u6296\u624b\uff0c\u4e00 \u53e3\u98de\u5200\u6a2a\u622a\u8fc7\u53bb\uff0c\u5c06\u4e24\u679d\u8896\u7bad\uff0c\u4e00\u9f50\u622a\u65ad\uff0c\u53eb\u9053\uff1a\u201c\u6768\u67f3\u9752\uff0c\u6211\u4e0d\u7528\u4f60\u5e2e\uff01\u201d\u968f\u624b\u53c8\u662f\u4e00\u67c4 \u98de\u5200\uff0c\u5411\u5510\u8d5b\u82b1\u8fd8\u656c\u3002 \u3000\u3000\u6842\u534e\u751f\u4e00\u89c1\u98de\u5200\u5e26\u9ed1\u8272\u7684\u5149\u534e\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u62d4\u51fa\u957f\u5251\u4e00\u62cd\uff0c\u53cc\u6307\u4e00\u7b9d\uff0c\u5c06\u98de\u5200\u63a5\u4e0b\uff0c\u770b \u4e86\u4e00\u770b\uff0c\u9053\uff1a\u201c\u679c\u7136\u662f\u4e2a\u72e0\u6bd2\u7684\u5973\u8d3c\uff01\u201d\u51af\u7433\u6325\u5251\u524d\u6251\uff0c\u6842\u534e\u751f\u8f6c\u4e86\u4e24\u8f6c\uff0c\u5148\u4e0d\u53d1\u62db\uff0c\u770b\u5979 \u5251\u6cd5\u3002 \u3000\u3000\u6768\u67f3\u9752\u53eb\u9053\uff1a\u201c\u51af\u745b\uff0c\u6211\u4e0d\u51c6\u4f60\u5728\u6211\u5bb6\u4e2d\u88ab\u4eba\u6349\u53bb\uff0c\u4f60\u9000\u4e0b\uff0c\u5148\u8ba9\u6211\u62fc\u4e86\u518d\u8bf4\u3002\u201d\u5510\u91d1 \u5cf0\u62c8\u987b\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4e24\u4eba\u90fd\u6709\u5fd7\u6c14\u3002\u9752\u59d1\u5a18\uff0c\u4f60\u4e0d\u6127\u662f\u94c1\u638c\u795e\u5f39\u7684\u5973\u513f\uff01\u201d\u7a81\u7136\u4f38\u624b\u5728\u6768\u67f3 \u9752\u7684\u80a9\u5934\u4e00\u6309\uff0c\u9053\uff1a\u201c\u4f60\u7684\u7236\u4eb2\u771f\u7684\u6b7b\u4e86\u5417\uff1f\u5e26\u6211\u53bb\u770b\uff01\u201d\u6768\u67f3\u9752\u88ab\u4ed6\u4e00\u6309\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c\u6012 \u9053\uff1a\u201c\u597d\uff0c\u4f60\u4ee5\u5927\u538b\u5c0f\uff0c\u7f9e\u4e5f\u4e0d\u7f9e\uff1f\u201d\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u5e26\u6211\u53bb\u770b\uff01\u201d\u534a\u62d6\u534a\u62c9\uff0c\u5c06\u6768\u67f3\u9752\u62c9\u5230 \u516b\u89d2\u4ead\u4e2d\u3002 \u3000\u3000\u6842\u534e\u751f\u8f6c\u4e86\u4e24\u8f6c\uff0c\u51af\u7433\u523a\u4ed6\u4e0d\u7740\uff0c\u5251\u6cd5\u4e00\u53d8\uff0c\u4f7f\u51fa\u65e0\u6781\u5251\u4e2d\u7684\u7edd\u62db\u201c\u611a\u516c\u79fb\u5c71\u201d\uff0c\u5251\u52bf \u751a\u7f13\uff0c\u5e73\u5e73\u4e00\u524a\uff0c\u52b2\u529b\u5374\u662f\u8d2f\u6ce8\u5251\u5c16\uff0c\u5de6\u53f3\u517c\u987e\uff0c\u6842\u6bd5\u751f\u53eb\u58f0\uff1a\u201c\u597d\u3001\u503c\u5f97\u4e0e\u4f60\u4e00\u6597\uff01\u201d\u5251 \u67c4\u4e00\u6296\uff0c\u5251\u950b\u5149\u534e\u4e00\u95ea\uff0c\u4e00\u4e0b\u5b50\u4fbf\u4ece\u5934\u9876\u4e0a\u7ed5\u8fc7\u53bb\uff01 \u3000\u3000\u51af\u7433\u5927\u5403\u4e00\u60ca\uff0c\u767e\u5fd9\u4e2d\u65bd\u5c55\u732b\u9e70\u6251\u51fb\u4e4b\u6280\uff0c\u8eab\u5b50\u4e00\u5c48\u4e00\u4f38\uff0c\u7bad\u4e00\u822c\u7684\u98de\u63a0\u51fa\u53bb\u3002\u6842\u534e\u751f \u9053\uff1a\u201c\u54c8\uff0c\u4f60\u8fd8\u6709\u8fd9\u4e00\u624b\uff01\u201d\u98de\u8eab\u6251\u4e0a\uff0c\u8fce\u9762\u4e00\u5251\uff0c\u51af\u7433\u8fde\u7528\u51e0\u79cd\u5251\u5f0f\uff0c\u6321\u4e86\u4e94\u62db\uff0c\u6842\u534e\u751f \u7684\u8fbe\u6469\u5251\u6cd5\u602a\u5f02\u7edd\u4f26\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u51fa\u4eba\u610f\u8868\uff0c\u51af\u7433\u7684\u65e0\u6781\u5251\u6cd5\u867d\u7136\u4e5f\u662f\u5185\u5bb6\u6b63\u5b97\uff0c\u53ef\u662f\u7a76\u56e0 \u6240\u4e60\u65f6\u65e5\u5c1a\u6d45\uff0c\u6321\u4e86\u4e94\u62db\uff0c\u9669\u8c61\u8fed\u89c1\uff0c\u60c5\u77e5\u4e07\u96be\u62b5\u654c\uff0c\u60f3\u8d77\u6768\u67f3\u9752\u4e4b\u8a00\uff0c\u5fc3\u9053\uff1a\u201c\u597d\uff0c\u6211\u7eb5 \u6218\u6b7b\uff0c\u4e5f\u4e0d\u5728\u4f60\u6768\u5bb6\u53d7\u8fb1\u3002\u201d\u6296\u624b\u8fde\u53d1\u4e09\u67c4\u593a\u547d\u795e\u5200\uff0c\u903c\u5f97\u6842\u534e\u751f\u95ea\u907f\uff0c\u7acb\u523b\u65bd\u5c55\u732b\u9e70\u7edd \u6280\uff0c\u98de\u8eab\u8df3\u51fa\u5899\u5916\u3002 \u3000\u3000\u6842\u534e\u751f\u8f7b\u529f\u8d85\u5999\uff0c\u8fc5\u5373\u8ffd\u51fa\uff0c\u5728\u534a\u5c71\u5761\u4e0a\u53c8\u628a\u51af\u7433\u622a\u4f4f\uff0c\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u65e0\u6781\u5251\u6cd5\u662f \u4ece\u90a3\u91cc\u5077\u6765\u7684\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u6211\u5077\u4e0d\u5077\u8981\u4f60\u7ba1\u4e48\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u504f\u8981\u7ba1\uff01\u201d\u811a\u6b65\u8e09\u8e09\u8dc4 \u8dc4\uff0c\u51af\u7433\u8fde\u7528\u51e0\u79cd\u8eab\u6cd5\uff0c\u8dd1\u5230\u90a3\u4e2a\u65b9\u4f4d\uff0c\u90fd\u6070\u6070\u88ab\u4ed6\u622a\u7740\uff01\u4e14\u732b\u9e70\u6251\u51fb\u7684\u7edd\u6280\uff0c\u4e5f\u53ea\u80fd\u8eb2\u95ea \u4e00\u65f6\uff0c\u59cb\u7ec8\u88ab\u4ed6\u8ddf\u5728\u8eab\u540e\u3002 \u3000\u3000\u6842\u534e\u751f\u81ea\u5c0f\u79bb\u5f00\u5929\u5c71\uff0c\u4f0f\u5904\u5ddd\u4e2d\uff0c\u4e0d\u77e5\u51af\u7433\u6765\u5386\uff0c\u89c1\u51af\u7433\u65e2\u4f1a\u5404\u79cd\u90aa\u6d3e\u6b66\u529f\uff0c\u53c8\u901a\u65e0\u6781 \u5251\u6cd5\uff0c\u9887\u4e3a\u60ca\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u770b\u6765\u5979\u4e0d\u5e94\u662f\u5085\u9752\u4e3b\u8fd9\u4e00\u652f\u7684\u5ae1\u4f20\u3002\u5085\u9752\u4e3b\u662f\u5185\u5bb6\u6b63\u5b97\uff0c\u90a3\u80af\u8ba9 \u540e\u4ee3\u5b50\u5f1f\u4e60\u90aa\u6d3e\u6b66\u827a\u3002\u201d\u65bd\u5c55\u8fbe\u6469\u5251\u6cd5\uff0c\u5c06\u51af\u7433\u56f0\u4f4f\uff0c\u5374\u4e0d\u5373\u523b\u65bd\u5c55\u6740\u624b\uff0c\u7acb\u5fc3\u770b\u5979\u5230\u5e95\u61c2 \u5f97\u591a\u5c11\u79cd\u6b66\u529f\u3002 \u3000\u3000\u9716\u96e8\u8fc7\u540e\uff0c\u5c71\u8def\u751a\u6ed1\uff0c\u51af\u7433\u8f7b\u529f\u867d\u7136\u4e0d\u5f31\uff0c\u53ef\u662f\u65e2\u8981\u62b5\u6321\u6842\u534e\u751f\u602a\u5f02\u7edd\u4f26\u7684\u8fbe\u6469\u5251\u6cd5\uff0c \u53c8\u8981\u7559\u5fc3\u811a\u4e0b\uff0c\u62b5\u6321\u66f4\u662f\u8270\u96be\u3002\u6b63\u5728\u5fc3\u614c\uff0c\u5ffd\u7136\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u59b9\u59b9\u4f11\u614c\uff01\u201d\u51af\u7433\u5927\u559c\u53eb \u9053\uff1a\u201c\u59d0\u59d0\u5feb\u6765\uff01\u201d\u539f\u6765\u662f\u5510\u6653\u6f9c\u548c\u51af\u745b\u6765\u4e86\uff01 \u3000\u3000\u51af\u745b\u81ea\u5415\u56db\u5a18\u8d70\u540e\uff0c\u7a0d\u7a0d\u4e00\u60f3\uff0c\u5c31\u731c\u5230\u59b9\u59b9\u5fc5\u7136\u662f\u5c71\u4e1c\u6768\u5bb6\uff0c\u6050\u9632\u5979\u4efb\u6027\u80e1\u95f9\uff0c\u66f4\u51fa\u4e71 \u5b50\uff0c\u4e5f\u987e\u4e0d\u5f97\u5bb3\u81ca\uff0c\u4fbf\u548c\u5510\u6653\u6f9c\u8bf4\u4e86\u3002\u5510\u6653\u6f9c\u9053\uff1a\u201c\u53cd\u6b63\u6211\u4eec\u603b\u4e0d\u80fd\u8eb2\u7740\u4e0d\u89c1\u6768\u6069\u5e08\uff0c\u6211\u65e2 \u65e0\u4e8b\uff0c\u4e8e\u7406\u4e8e\u60c5\uff0c\u90fd\u8be5\u56de\u53bb\u89c1\u4ed6\uff0c\u8ba9\u4ed6\u8001\u4eba\u5bb6\u653e\u5fc3\u3002\u6211\u4e0e\u6768\u67f3\u9752\u7684\u5a5a\u59fb\uff0c\u90a3\u662f\u53e6\u4e00\u56de\u4e8b\u3002\u201d \u51af\u745b\u5929\u771f\u65e0\u90aa\uff0c\u7b11\u9053\uff1a\u201c\u53ea\u8981\u54b1\u4eec\u6c38\u4e0d\u5206\u79bb\uff0c\u4f60\u5c31\u548c\u6768\u67f3\u9752\u7ed3\u5a5a\uff0c\u90a3\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u201d\u5510\u6653 \u6f9c\u82e6\u7b11\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u4e0e\u5979\u7edd\u96be\u7ed3\u5408\uff0c\u6b64\u8bdd\u4f11\u63d0\u3002\u4e0d\u8fc7\uff0c\u54b1\u4eec\u8fd8\u662f\u8981\u53bb\u6768\u5bb6\u3002\u201d\u4e24\u4eba\u548c\u7518\u51e4 \u6c60\u8bf4\u4e86\u3002\u7518\u51e4\u6c60\u6700\u6015\u5904\u7406\u8fd9\u79cd\u7537\u5973\u7ea0\u7eb7\uff0c\u89c1\u4ed6\u4eec\u81ea\u5df1\u8981\u53bb\uff0c\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u5f53\u4e0b\u7ea6\u597d\u4ed6\u4eec\u5728 \u9099\u5c71\u89c1\u9762\uff0c\u4fbf\u7531\u4ed6\u4eec\u53bb\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u4e0e\u51af\u745b\u4e43\u662f\u719f\u8def\uff0c\u811a\u7a0b\u53c8\u5feb\uff0c\u6240\u4ee5\u867d\u7136\u8fdf\u4e86\u4e24\u5929\uff0c\u8fd8\u662f\u53ca\u65f6\u8d76\u5230\u3002\u4e00\u5230\u5c31\u9047\u89c1\u51af \u7433\u5728\u5c71\u5761\u4e0a\u4e0e\u4eba\u6076\u6597\uff0c\u5904\u5883\u751a\u9669\uff0c\u51af\u745b\u6b63\u5f85\u62d4\u5251\u76f8\u52a9\uff0c\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6b64\u4eba\u6562\u5728\u6211\u6069\u5e08\u95e8\u524d\u52a8 \u624b\uff0c\u4e8b\u751a\u53ef\u7591\u3002\u83ab\u975e\u6211\u6069\u5e08\u5bb6\u4e2d\u4e5f\u51fa\u4e86\u4e8b\u4e48\uff1f\u745b\u59b9\u3002\u4f60\u5148\u53bb\u89c1\u6768\u516c\u516c\uff0c\u6211\u5e2e\u7433\u59b9\u3002\u201d\u51af\u745b\u77e5 \u9053\u5510\u6653\u6f9c\u7684\u5185\u529f\u5251\u6cd5\u90fd\u5df2\u5927\u6709\u8ff8\u5883\uff0c\u548c\u81ea\u5df1\u5b9e\u662f\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u4fbf\u9053\uff1a\u201c\u4e5f\u597d\u3002\u6b64\u4eba\u5251\u6cd5\u975e\u6bd4\u5bfb \u5e38\uff0c\u4f60\u5c0f\u5fc3\u4e86\uff01\u201d\u8d70\u4e0b\u5c71\u5761\uff0c\u4ece\u6b63\u95e8\u8fdb\u5165\u6768\u5bb6\u3002\u5728\u5979\u8fdb\u5165\u6768\u5bb6\u4e4b\u65f6\uff0c\u6768\u67f3\u9752\u5374\u6b63\u597d\u4ece\u540e\u56ed\u8df3 \u51fa\u6765\uff0c\u4e24\u4eba\u6ca1\u6709\u78b0\u5934\u3002 \u3000\u3000\u5510\u91d1\u5cf0\u62d6\u4e86\u6768\u67f3\u9752\uff0c\u540c\u4e0a\u56ed\u4e2d\u7684\u516b\u89d2\u4ead\uff0c\u679c\u7136\u89c1\u7740\u4e00\u526f\u7ea2\u6728\u68fa\u6750\uff0c\u5199\u7740\uff1a\u201c\u524d\u660e\u4e49\u58eb\u5c71 \u4e1c\u4fa0\u5ba2\u6768\u4ef2\u82f1\u4e4b\u7075\u4f4d\u3002\u201d\u5510\u91d1\u5cf0\u8001\u773c\u6cea\u6d41\uff0c\u9053\uff1a\u201c\u6768\u5927\u54e5\uff0c\u4f60\u679c\u771f\u6b7b\u4e86\u4e48\uff1f\u201d\u6768\u67f3\u9752\u88ab\u4ed6\u624b \u7b54\u80a9\u5934\uff0c\u73b0\u5728\u624d\u653e\uff0c\u534a\u8eab\u9ebb\u75f9\uff0c\u6781\u4e0d\u8212\u670d\uff0c\u95fb\u8a00\u6012\u9053\uff1a\u201c\u68fa\u6750\u5728\u6b64\uff0c\u96be\u9053\u8fd8\u4f1a\u9a97\u4f60\u4e48\uff1f\u6789\u4f60 \u4e0e\u6211\u7236\u4eb2\u79f0\u5144\u9053\u5f1f\uff0c\u65e2\u6253\u4f24\u4e86\u4ed6\uff0c\u4eca\u65e5\u8d81\u4ed6\u6b7b\u4e86\uff0c\u8fd8\u4e0a\u95e8\u6765\u6b3a\u8d1f\u6211\uff01\u201d\u5510\u91d1\u5cf0\u542c\u4e86\uff0c\u597d\u4e0d\u96be \u53d7\uff0c\u7ffb\u773c\u95ee\u9053\uff1a\u201c\u6211\u4e0d\u662f\u7ed9\u4e86\u4f60\u7236\u4eb2\u89e3\u836f\u4e48\uff1f\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u4f60\u7684\u89e3\u836f\u8fdf\u8fdf\u624d\u6765\uff0c\u9876\u4ec0\u4e48 \u7528\uff1f\u4ed6\u6b8b\u5e9f\u591a\u5e74\uff0c\u800c\u4eca\u6b7b\u4e86\uff0c\u4f60\u624d\u6765\u732b\u54ed\u8001\u9f20\u5047\u6148\u60b2\u3002\u201d\u5510\u91d1\u5cf0\u7709\u5934\u6253\u76b1\uff0c\u9053\uff1a\u201c\u4f60\u7236\u4eb2\u771f \u662f\u56e0\u4f24\u81f3\u6b7b\u7684\u4e48\uff1f\u201d\u6768\u67f3\u9752\u9053\uff1a\u201c\u96be\u9053\u6211\u7236\u4eb2\u8fd8\u4f1a\u81ea\u5df1\u5bfb\u6b7b\u4e0d\u6210\uff01\u201d\u6768\u67f3\u9752\u4f36\u7259\u4fd0\u9f7f\uff0c\u60f3\u628a \u5510\u91d1\u5cf0\u9a82\u8d70\uff0c\u4e0d\u6599\u5510\u91d1\u5cf0\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u6768\u5927\u54e5\u5440\uff0c\u5c0f\u5f1f\u8fd9\u53a2\u6709\u793c\u4e86\uff01\u201d\u4e00\u624b\u5c06\u6768\u67f3 \u9752\u63a8\u51fa\u4ead\u5916\uff0c\u7acb\u523b\u5728\u7075\u524d\u8dea\u4e0b\uff0c\u53cc\u638c\u62cd\u7684\u4e00\u58f0\uff0c\u51fb\u5728\u68fa\u6750\u4e4b\u4e0a\u3002 \u3000\u3000\u539f\u6765\u6c5f\u6e56\u4eba\u7269\uff0c\u4e3a\u4e86\u907f\u514d\u654c\u4eba\u5bfb\u4ec7\uff0c\u5e38\u6709\u8bc8\u6b7b\u4e4b\u4e8b\u3002\u5510\u91d1\u5cf0\u719f\u6089\u6c5f\u6e56\u52fe\u5f53\uff0c\u542c\u4e86\u6768\u67f3\u9752 \u8d1f\u6c14\u4e4b\u8a00\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u60f3\u9053\uff1a\u201c\u83ab\u975e\u6768\u4ef2\u82f1\u6599\u5230\u6211\u4f1a\u518d\u6765\u95ee\u4ed6\u8981\u4eba\uff0c\u6545\u610f\u8bc8\u6b7b\uff0c\u4ee4\u6211\u4e0d\u597d \u610f\u601d\u52a8\u624b\u4e48\uff1f\u201d\u5fc3\u6709\u6000\u7591\uff0c\u6697\u8fd0\u5185\u529b\uff0c\u5728\u68fa\u6750\u4e0a\u8f7b\u8f7b\u4e00\u62cd\uff0c\u68fa\u6750\u677f\u7acb\u523b\u88c2\u5f00\u4e00\u6761\u5927\u7f1d\uff0c\u4e00\u80a1 \u5c38\u81ed\u76f4\u51b2\u51fa\u6765\uff0c\u5510\u91d1\u5cf0\u672c\u4ee5\u4e3a\u68fa\u6750\u5185\u88c5\u7684\u662f\u7802\u77f3\u4e4b\u7c7b\uff0c\u89c1\u72b6\u5fc3\u5934\u4e00\u9707\uff0c\u614c\u5fd9\u63ed\u5f00\u68fa\u76d6\uff0c\u6768\u4ef2 \u82f1\u7684\u5c38\u4f53\u7528\u9999\u6599\u836f\u7269\u62a4\u7740\uff0c\u505c\u68fa\u4ec5\u4ec5\u4e00\u6708\uff0c\u9762\u76ee\u5c1a\u5982\u751f\u524d\uff0c\u53ea\u662f\u63a9\u4e0d\u7740\u5c38\u4f53\u53d1\u6563\u7684\u81ed\u5473\u3002\u518d \u4e00\u770b\u65f6\uff0c\u5c38\u4f53\u80f8\u524d\uff0c\u8fd8\u653e\u7740\u4e00\u5c01\u4fe1\uff0c\u5199\u7684\u7adf\u662f\u201c\u5510\u91d1\u5cf0\u8d24\u5f1f\u4eb2\u62c6\u201d\u51e0\u5b57\u3002\u5510\u91d1\u5cf0\u5403\u4e86\u4e00\u60ca\uff0c \u5fc3\u9053\uff1a\u201c\u539f\u672a\u6768\u4ef2\u82f1\u4e0d\u662f\u8bc8\u6b7b\uff0c\u4f46\u5374\u65e9\u6599\u5230\u6211\u6709\u4eca\u65e5\u4e4b\u4e8b\u3002\u201d\u53d6\u4e86\u4fe1\u5c01\uff0c\u76d6\u597d\u68fa\u6728\uff0c\u62c6\u4fe1\u4e00 \u770b\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u9053\uff1a\u201c\u4ef2\u82f1\u98ce\u70db\u6b8b\u5e74\uff0c\u65e6\u5915\u5c31\u6728\uff0c\u4e0d\u53ca\u4e0e\u8001\u5f1f\u9053\u522b\uff0c\u6168\u4f55\u5982\u4e4b\u3002\u5179\u6709\u6073\u8005\uff0c \u51af\u5bb6\u5b64\u5973\uff0c\u5e7c\u906d\u5b64\u9732\uff0c\u8eab\u4e16\u582a\u601c\uff0c\u5929\u5c71\u6613\u8001\u524d\u8f88\u6536\u4e3a\u7231\u5f92\uff0c\u611a\u5144\u4ea6\u89c6\u540c\u5df1\u5973\u3002\u4fd7\u8bed\u4e91\uff1a\u51a4\u5bb6 \u5b9c\u89e3\u4e0d\u5b9c\u7ed3\uff0c\u4e14\u8d24\u5a7f\u4ea6\u975e\u6b64\u5973\u6240\u6740\uff0c\u613f\u6211\u5f1f\u5ff5\u5728\u6614\u65e5\u4ea4\u60c5\uff0c\u4e0d\u518d\u8ffd\u7a76\uff0c\u5219\u5b58\u6b81\u5747\u611f\u77e3\u3002\u201d\u6768 \u4ef2\u82f1\u4e0d\u64c5\u6587\u58a8\uff0c\u4f46\u5199\u6765\u81ea\u6709\u4e00\u80a1\u771f\u631a\u4e4b\u60c5\u3002\u5510\u91d1\u5cf0\u770b\u4e86\uff0c\u8e0c\u8e87\u4e0d\u51b3\uff0c\u60f3\u9053\uff1a\u201c\u597d\u4e0d\u5bb9\u6613\u624d\u8bf7 \u5f97\u6842\u534e\u751f\u76f8\u52a9\uff0c\u5982\u4f55\u80fd\u8f7b\u6613\u7f62\u624b\uff1f\u4f46\u82e5\u4e0d\u7f62\u624b\u65f6\uff0c\u53c8\u96be\u5374\u6768\u5927\u54e5\u4e4b\u60c5\uff0c\u4f55\u51b5\u5979\u8fd8\u662f\u6613\u8001\u524d\u8f88 \u7684\u7231\u5f92\uff0c\u8fd9\u4e8b\u600e\u751f\u662f\u597d\uff1f\u201d\u53c8\u5ff5\u53ca\u6768\u4ef2\u82f1\u6614\u65e5\u4e3a\u5df1\u6240\u4f24\uff0c\u4ed6\u4eca\u65e5\u4e4b\u6b7b\u672a\u5fc5\u4e0e\u81ea\u5df1\u65e0\u5173\uff0c\u66f4\u662f \u96be\u8fc7\u3002\u5f53\u4e0b\u62dc\u5012\u7075\u524d\uff0c\u75db\u54ed\u4e86\u4e00\u9635\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u6768\u67f3\u9752\u65e9\u5df2\u8d70\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u62d4\u51fa\u6e38\u9f99\u5b9d\u5251\uff0c\u4e0a\u524d\u76f8\u52a9\u51af\u7433\uff0c\u5b9d\u5251\u4e00\u6325\uff0c\u5149\u8292\u7535\u95ea\uff0c\u6842\u6bd5\u751f\u5403\u4e86\u4e00\u60ca\uff0c\u4e00\u8f6c\u624b \u8155\uff0c\u659c\u523a\u51fa\u53bb\uff0c\u5510\u6653\u6f9c\u89c1\u654c\u62db\u602a\u5f02\uff0c\u56de\u5251\u4e00\u5c01\uff0c\u6842\u534e\u751f\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u4f7f\u7684\u83ab\u4e0d\u662f\u6e38\u9f99\u5b9d\u5251 \u4e48\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u65e2\u77e5\u6211\u4f7f\u7684\u662f\u6e38\u9f99\u5b9d\u5251\uff0c\u8fd8\u4e0d\u5feb\u5feb\u64a4\u5251\u3002\u201d\u6842\u534e\u751f\u5927\u6012\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c \u6325\u5251\u731b\u653b\u3002 \u3000\u3000\u539f\u6765\u6842\u534e\u751f\u53ea\u77e5\u6e38\u9f99\u5b9d\u5251\u662f\u51cc\u672a\u98ce\u5f53\u5e74\u4f20\u7ed9\u4e86\u5468\u9752\uff0c\u5374\u4e0d\u77e5\u5468\u9752\u53c8\u4f20\u7ed9\u4e86\u5510\u6653\u6f9c\u7684\u4e8b\u3002 \u540e\u6765\u5468\u9752\u88ab\u5bb3\uff0c\u6613\u5170\u73e0\u66fe\u5230\u4e2d\u539f\u5bfb\u627e\u6b64\u5251\uff0c\u4ed6\u4ea6\u7565\u6709\u77e5\u95fb\uff0c\u800c\u4eca\u89c1\u5510\u6653\u6f9c\u624b\u6301\u6b64\u5251\uff0c\u53ea\u9053\u4ed6 \u662f\u593a\u81ea\u5468\u9752\u4e4b\u624b\u7684\uff0c\u5fc3\u9053\uff1a\u201c\u6b64\u5251\u4e43\u5929\u5c71\u9547\u5c71\u4e4b\u5b9d\uff0c\u6211\u4f55\u4e0d\u66ff\u6613\u8001\u524d\u8f88\u53d6\u56de\u3002\u201d\u4ea4\u624b\u4e09\u4e94\u5341 \u62db\uff0c\u5510\u6653\u6f9c\u5148\u7528\u8ffd\u98ce\u5251\u6cd5\uff0c\u62b5\u654c\u4e0d\u4f4f\uff0c\u518d\u8f6c\u7528\u5929\u5c71\u5251\u6cd5\u4e2d\u6700\u6df1\u5965\u7684\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u653b\u5b88\u517c\u5907\uff0c \u8fd9\u624d\u582a\u582a\u80fd\u591f\u62b5\u6321\u3002 \u3000\u3000\u6842\u534e\u751f\u89c1\u4ed6\u4f7f\u51fa\u5929\u5c71\u5251\u6cd5\uff0c\u4ea6\u5df2\u6697\u6697\u751f\u7591\u3002\u5510\u6653\u6f9c\u867d\u8bf4\u66fe\u5728\u5929\u5c71\u4e09\u5e74\uff0c\u53ea\u56e0\u6613\u5170\u73e0\u4e13\u5fc3 \u6559\u4ed6\u672c\u95e8\u5251\u6cd5\uff0c\u6545\u6b64\u4ed6\u4ea6\u4ec5\u77e5\u6709\u4e00\u79cd\u8fbe\u6469\u5251\u6cd5\uff0c\u5374\u4e0d\u77e5\u8fbe\u6469\u5251\u6cd5\u7a76\u7adf\u5982\u4f55\uff0c\u9aa4\u9047\u5f3a\u654c\uff0c\u4e00\u62db \u4e00\u5f0f\u90fd\u4e0d\u653e\u677e\uff0c\u66f4\u517c\u51af\u7433\u6068\u6842\u534e\u751f\u521a\u624d\u76f8\u903c\uff0c\u51fa\u624b\u66f4\u662f\u6bd2\u8fa3\u3002\u6842\u534e\u751f\u5fc3\u9053\uff1a\u201c\u82e5\u7136\u4ed6\u662f\u5929\u5c71 \u4e00\u6d3e\uff0c\u600e\u4f1a\u4e0d\u77e5\u6211\u7684\u5251\u6cd5\u6765\u5386\uff1f\u82e5\u8bf4\u4ed6\u662f\u5468\u9752\u5f92\u5f1f\uff0c\u5468\u9752\u4e5f\u4ec5\u6653\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u8ffd\u98ce\u5251\u5f0f\uff0c\u4e0d \u80fd\u6559\u51fa\u6b64\u4eba\u3002\u201d\u5510\u6653\u6f9c\u5728\u5929\u5c71\u4ec5\u4ec5\u4e09\u5e74\uff0c\u5916\u4eba\u591a\u4e0d\u77e5\u9053\u3002\u6842\u534e\u751f\u4e00\u65f6\u95f4\u60f3\u4e0d\u5230\u4ed6\u4f1a\u662f\u6613\u5170\u73e0 \u7684\u8bb0\u540d\u5f1f\u5b50\uff0c\u66f4\u517c\u4ed6\u5728\u4e09\u5144\u5f1f\u4e2d\u6700\u4e3a\u597d\u80dc\uff0c\u89c1\u5510\u6653\u6f9c\u4f7f\u51fa\u5929\u5c71\u5251\u6cd5\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e45\u95fb\u5929 \u5c71\u3001\u7384\u5973\u3001\u8fbe\u6469\u4e09\u79cd\u5251\u6cd5\u9f0e\u8db3\u800c\u4e09\uff0c\u5404\u64c5\u80dc\u573a\u3002\u6211\u867d\u66fe\u89c1\u8fc7\u6613\u8001\u524d\u8f88\u7ec3\u4e60\u5251\u6cd5\uff0c\u5374\u4ece\u672a\u6709\u673a \u4f1a\u8bd5\u62db\uff0c\u4eca\u65e5\u4f55\u4e0d\u5c31\u8bd5\u5b83\u4e00\u8bd5\uff0c\u770b\u4e24\u79cd\u5251\u6cd5\uff0c\u5230\u5e95\u4f55\u8005\u8f83\u4f18\u3002\u201d\u5fc3\u840c\u6b64\u5ff5\uff0c\u7acb\u523b\u8f6c\u5b88\u4e3a\u653b\uff0c \u602a\u62db\u53e0\u51fa\uff0c\u5510\u6653\u6f9c\u7684\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u4f7f\u5230\u75be\u5904\uff0c\u5468\u8eab\u4e0a\u4e0b\uff0c\u6709\u5982\u56f4\u5728\u4e00\u5e62\u5149\u73af\u4e4b\u4e2d\uff0c\u800c\u6842\u534e\u751f \u7adf\u7136\u4ece\u5251\u5149\u4e2d\u7a7f\u6765\u63d2\u53bb\u3002 \u3000\u3000\u6309\u8bf4\u5929\u5c71\u5251\u6cd5\u535a\u5927\u7cbe\u6df1\uff0c\u7edd\u4e0d\u5728\u8fbe\u6469\u5251\u6cd5\u4e4b\u4e0b\uff0c\u4f46\u6842\u534e\u751f\u81ea\u5e7c\u5373\u5f97\u7236\u4eb2\u4f20\u6388\uff0c\u6bd4\u5510\u6653\u6f9c \u5374\u8981\u7565\u80dc\u4e00\u7b79\uff0c\u4ed6\u5251\u6cd5\u8eab\u6cd5\uff0c\u65e0\u4e00\u4e0d\u602a\uff0c\u5510\u6653\u6f9c\u4e00\u6025\uff0c\u9632\u4e0d\u80dc\u9632\uff0c\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u5c61\u5c61\u88ab\u4ed6\u7a81 \u7834\uff0c\u5e78\u800c\u6842\u534e\u751f\u987e\u5fcc\u6e38\u9f99\u5b9d\u5251\u7684\u5a01\u529b\uff0c\u8fd8\u4e0d\u6562\u592a\u8fc7\u6b3a\u8eab\u8fdb\u903c\uff0c\u662f\u4ee5\u5510\u6653\u6f9c\u867d\u7136\u843d\u5728\u4e0b\u98ce\uff0c\u4e00 \u65f6\u4e4b\u95f4\uff0c\u5374\u8fd8\u4e0d\u81f4\u843d\u8d25\u3002\u51af\u7433\u65e0\u6781\u5251\u6cd5\u867d\u9ad8\uff0c\u529f\u529b\u672a\u5230\uff0c\u6842\u534e\u751f\u907f\u5f31\u653b\u5f3a\uff0c\u9489\u7740\u5510\u6653\u6f9c\u7edd\u4e0d \u653e\u677e\uff0c\u51af\u7433\u5251\u8d70\u8fde\u73af\uff0c\u5251\u5c16\u4e5f\u672a\u6cbe\u7740\u4ed6\u7684\u8863\u89d2\u3002 \u3000\u3000\u6768\u67f3\u9752\u8dd1\u4e86\u51fa\u6765\uff0c\u89c1\u5510\u51af\u5e76\u80a9\u4f5c\u6218\uff0c\u5510\u6653\u6f9c\u7adf\u4f3c\u8c41\u51fa\u6027\u547d\uff0c\u62fc\u6b7b\u76f8\u62a4\u51af\u7433\uff0c\u5fc3\u4e2d\u9887\u4e3a\u5992 \u6068\u3002\u4f46\u89c1\u5510\u6653\u6f9c\u8fed\u9047\u9669\u62db\uff0c\u53c8\u7981\u4e0d\u4f4f\u5fc3\u60ca\u80c6\u6218\u3002\u5510\u8d5b\u82b1\u89c1\u5979\u51fa\u6765\uff0c\u6015\u5979\u4e0a\u524d\u6270\u4e71\uff0c\u821e\u5200\u76f8 \u8fce\uff0c\u4e0e\u6768\u67f3\u9752\u4e5f\u5728\u5c71\u5761\u4e0a\u6253\u505a\u4e00\u5806\uff0c\u4e24\u4eba\u6b66\u827a\u76f8\u5dee\u4e0d\u8fdc\uff0c\u6768\u67f3\u9752\u51b2\u4e0d\u8fc7\u53bb\uff0c\u5510\u8d5b\u82b1\u4e5f\u6253\u5979\u4e0d \u9000\u3002 \u3000\u3000\u4e24\u8fb9\u6597\u5f97\u6b63\u70c8\uff0c\u5510\u91d1\u5cf0\u4e5f\u4ece\u6768\u5bb6\u8d70\u4e86\u51fa\u6765\uff0c\u5510\u8d5b\u82b1\u6b63\u60f3\u65bd\u7528\u6697\u5668\uff0c\u5510\u91d1\u5cf0\u53eb\u9053\uff1a\u201c\u8d5b \u82b1\uff0c\u4e0d\u51c6\u4f24\u5979\uff01\u201d\u5510\u8d5b\u82b1\u7a92\u4e86\u4e00\u7a92\uff0c\u6768\u67f3\u9752\u4e00\u51b2\u800c\u8fc7\u3002 \u3000\u3000\u8fd9\u65f6\u5510\u6653\u6f9c\u6b63\u5728\u5403\u7d27\uff0c\u5ffd\u95fb\u5f97\u5510\u91d1\u5cf0\u53c8\u626c\u58f0\u53eb\u9053\uff1a\u201c\u6842\u8d24\u5f1f\uff0c\u4e14\u6682\u505c\u624b\uff0c\u6768\u8001\u5934\u513f\u771f\u7684 \u6b7b\u4e86\uff01\u201d\u5510\u6653\u6f9c\u5927\u5403\u4e00\u60ca\uff0c\u6e38\u9f99\u5b9d\u5251\u9669\u9669\u8dcc\u843d\u5c18\u57c3\u3002\u6842\u534e\u751f\u6536\u52bf\u4e0d\u4f4f\uff0c\u4e00\u5251\u521a\u521a\u63b7\u51fa\uff0c\u7565\u7565 \u659c\u504f\uff0c\u628a\u51af\u7433\u7684\u5175\u5203\u64a9\u5f00\uff0c\u6768\u67f3\u9752\u75be\u8d70\u5982\u98ce\uff0c\u521a\u521a\u6251\u5230\uff0c\u6768\u67f3\u9752\u81ea\u77e5\u7edd\u5bf9\u4e0d\u662f\u654c\u4eba\u5bf9\u624b\uff0c\u4f46 \u5fc3\u4e2d\u4f30\u8ba1\u654c\u4eba\u4e0d\u6562\u4f24\u5979\uff0c\u4e00\u6251\u4fbf\u6251\u5230\u5510\u6653\u6f9c\u8eab\u4e0a\uff0c\u4ee5\u8eab\u906e\u63a9\u3002 \u3000\u3000\u672c\u6765\u6842\u534e\u751f\u89c1\u4e86\u5510\u6653\u6f9c\u7684\u5929\u5c71\u5251\u6cd5\uff0c\u5c31\u53ea\u662f\u5fc3\u5b58\u8bd5\u62db\uff0c\u65e0\u610f\u76f8\u5bb3\u3002\u6768\u67f3\u9752\u4e0d\u77e5\u5c31\u91cc\uff0c\u6551 \u4eba\u5fc3\u6025\uff0c\u98de\u8eab\u4ef2\u4e0a\uff0c\u52b2\u9053\u751a\u5927\u3002\u9716\u96e8\u8fc7\u540e\uff0c\u5c71\u8def\u6781\u6ed1\uff0c\u5510\u6653\u6f9c\u51b7\u4e0d\u53ca\u9632\uff0c\u88ab\u6768\u67f3\u9752\u4e00\u649e\uff0c\u53cc \u53cc\u8dcc\u5012\uff0c\u6b66\u529f\u9ad8\u660e\u4e4b\u58eb\u9047\u9669\u81ea\u9632\u51fa\u4e8e\u672c\u80fd\uff0c\u5510\u6653\u6f9c\u4e00\u8de4\u8dcc\u5012\uff0c\u7acb\u5373\u8fd0\u7528\u201c\u5343\u65a4\u5760\u201d\u7684\u529f\u592b\u5c06 \u8eab\u5f62\u7a33\u4f4f\uff0c\u6768\u67f3\u9752\u5374\u4ece\u4ed6\u7684\u8eab\u4e0a\u4e00\u6eda\u800c\u8fc7\uff0c\u4ece\u5c71\u8fb9\u7684\u9661\u5761\u4e0a\u76f4\u6eda\u4e0b\u53bb\uff01 \u3000\u3000\u6842\u534e\u751f\u5927\u53eb\u4e00\u58f0\u201c\u4e0d\u597d\uff01\u201d\u5c71\u5761\u4e0a\u4e00\u80a1\u6025\u6d41\uff0c\u5982\u7011\u5e03\u822c\u51b2\u51fb\u800c\u4e0b\uff0c\u539f\u6765\u6b63\u662f\u5c71\u6d2a\u7a81\u53d1\uff0c \u75be\u5982\u5954\u9a6c\u3002\u6842\u534e\u751f\u51b2\u5929\u800c\u8d77\uff0c\u4f7f\u51fa\u4e94\u79bd\u638c\u6cd5\uff0c\u5f80\u4e0b\u4e00\u6293\uff0c\u770b\u770b\u6293\u7740\u6768\u67f3\u9752\u7684\u5934\u53d1\uff0c\u4e00\u4e2a\u6d2a\u5cf0 \u51b2\u6765\uff0c\u7acb\u523b\u628a\u6768\u67f3\u9752\u51b2\u4e0b\u5c71\u5e95\u7684\u5c0f\u6e56\uff0c\u8eab\u4f53\u6d6e\u6c89\u51e0\u4e0b\uff0c\u4fbf\u88ab\u6d2a\u6ce2\u5377\u6ca1\u3002 \u3000\u3000\u6842\u534e\u751f\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u5c48\u4f38\uff0c\u5012\u7eb5\u56de\u6765\uff0c\u5510\u6653\u6f9c\u5728\u5730\u4e0a\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\u7ad9\u4e86\u8d77\u6765\uff0c\u8033\u542c\u51af\u7433 \u60ca\u53eb\u4e4b\u58f0\uff0c\u76ee\u7779\u6768\u67f3\u9752\u5728\u6e56\u4e2d\u6c89\u6ca1\uff0c\u7acb\u523b\u4e00\u58f0\u6012\u543c\uff0c\u6325\u5251\u731b\u6251\uff0c\u6842\u534e\u751f\u6b32\u60f3\u89e3\u91ca\uff0c\u65e0\u5948\u5510\u6653 \u6f9c\u6709\u5982\u75af\u864e\u4e00\u822c\uff0c\u5251\u5251\u51f6\u731b\uff0c\u6842\u534e\u751f\u8fd0\u5251\u9632\u8eab\uff0c\u7cbe\u795e\u90a3\u5bb9\u5206\u6563\uff0c\u4e24\u4eba\u7728\u773c\u4e4b\u95f4\u4fbf\u6597\u4e86\u4e8c\u4e09\u5341 \u62db\u3002\u51af\u7433\u62e9\u5251\u659c\u653b\uff0c\u4e5f\u62fc\u4e86\u6027\u547d\uff0c\u5510\u8d5b\u82b1\u53eb\u9053\uff1a\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u7239\uff0c\u4f60\u8fd8\u4e0d\u52a8\u624b\u5417\uff1f\u201d\u5510\u91d1\u5cf0 \u6a2a\u4e86\u5fc3\u80a0\uff0c\u957f\u62bd\u4e00\u5377\uff0c\u4e0a\u524d\u4fbf\u62ff\u51af\u7433\u3002 \u3000\u3000\u51af\u745b\u8d70\u5165\u6768\u5bb6\uff0c\u6b63\u662f\u5510\u91d1\u5cf0\u521a\u521a\u8d70\u51fa\u4e4b\u9645\u3002\u51af\u745b\u89c1\u6768\u5bb6\u6742\u7269\u51cc\u4e71\uff0c\u5148\u81ea\u5403\u60ca\uff0c\u8ff3\u81ea\u8d70\u5165 \u6768\u4ef2\u82f1\u5f80\u6614\u517b\u4f24\u7684\u9759\u5ba4\uff0c\u53eb\u4e86\u4e00\u58f0\u201c\u6768\u516c\u516c\uff0c\u6211\u56de\u6765\u4e86\uff01\u201d\u63ed\u5e18\u4e00\u770b\uff0c\u4eba\u5f71\u6beb\u65e0\uff0c\u4f46\u89c1\u4e66\u6848 \u4e0a\u6446\u7740\u4e00\u5c01\u4fe1\uff0c\u5199\u7740\uff1a\u5510\u6653\u6f9c\u4ec1\u68e3\u4eb2\u62c6\u3002\u201d\u51af\u745b\u5403\u60ca\u975e\u5c0f\uff0c\u5c06\u4fe1\u653e\u5165\u6000\u4e2d\uff0c\u51b2\u51fa\u623f\u95e8\u5927\u53eb\uff0c \u6768\u67f3\u9752\u7684\u5c0f\u4e2b\u73af\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u51af\u59d1\u5a18\uff0c\u4f60\u8fd8\u672a\u62dc\u8fc7\u7075\u5417\uff1f\u73b0\u5728\u8fd8\u53eb\u6768\u516c\u516c\u505a\u4ec0\u4e48\uff1f\u201d\u51af\u745b\u4e00 \u624b\u6293\u7740\u5979\u7684\u624b\u8155\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u90a3\u5c0f\u4e2b\u73af\u75bc\u75db\u96be\u5f53\uff0c\u53c8\u60ca\u53c8\u6050\uff0c\u9053\uff1a\u201c\u8001\u7237\u65e9\u5df2\u6b7b \u4e86\uff0c\u4f60\u521a\u624d\u4e0d\u662f\u968f\u5c0f\u59d0\u5230\u540e\u56ed\u8c12\u7075\u5417\uff1f\u201d\u51af\u745b\u628a\u624b\u4e00\u677e\uff0c\u6025\u6025\u8dd1\u5165\u540e\u56ed\uff0c\u521a\u597d\u89c1\u7740\u5510\u91d1\u5cf0\u7684 \u80cc\u5f71\u98de\u51fa\u56f4\u5899\uff0c\u51af\u745b\u65e0\u6687\u8ffd\u8d76\uff0c\u5bfb\u5230\u516b\u89d2\u4ead\u4e0a\uff0c\u53ea\u89c1\u5927\u7ea2\u6728\u68fa\u505c\u5728\u4ead\u4e2d\uff0c\u68fa\u6750\u8fd8\u88c2\u4e86\u4e00\u6761\u5927 \u7f1d\uff0c\u7ec6\u5fc3\u4e00\u770b\uff0c\u663e\u89c1\u662f\u521a\u521a\u7ed9\u4eba\u7528\u638c\u529b\u9707\u88c2\u7684\u3002 \u3000\u3000\u51af\u745b\u53f7\u6dd8\u5927\u54ed\uff0c\u731b\u7136\u60f3\u8d77\uff0c\u9002\u624d\u90a3\u80cc\u5f71\u662f\u53bb\u5e74\u6765\u5bfb\u4ec7\u7684\u5510\u91d1\u5cf0\uff0c\u51af\u745b\u4e0d\u77e5\u6768\u4ef2\u82f1\u4e43\u662f\u75c5 \u6b7b\uff0c\u53ea\u9053\u4ed6\u88ab\u5510\u5bb6\u7684\u4eba\u6240\u5bb3\uff0c\u7acb\u523b\u62d4\u5251\u51fa\u56ed\uff0c\u5954\u4e0a\u5c71\u5761\u3002 \u3000\u3000\u51af\u7433\u672c\u6765\u4e0d\u662f\u5510\u91d1\u5cf0\u7684\u5bf9\u624b\uff0c\u4f46\u5510\u91d1\u5cf0\u56e0\u89c1\u6768\u4ef2\u82f1\u7236\u5973\u5747\u6b7b\uff0c\u5fc3\u4e2d\u6b49\u6127\uff0c\u6597\u610f\u51cf\u5f31\uff0c\u800c \u51af\u7433\u53c8\u662f\u6b66\u529f\u7e41\u6742\uff0c\u5201\u94bb\u5f02\u5e38\uff0c\u5510\u91d1\u5cf0\u4e00\u53cc\u8089\u638c\uff0c\u7adf\u7136\u64d2\u5979\u4e0d\u6062\u3002\u51af\u745b\u5982\u98de\u8d76\u81f3\uff0c\u5927\u58f0\u53eb \u9053\uff1a\u201c\u59b9\u59b9\uff0c\u628a\u8fd9\u8001\u8d3c\u8ba9\u7ed9\u6211\u5427\uff01\u201d\u5510\u6653\u6f9c\u5374\u53eb\u9053\uff1a\u201c\u745b\u59b9\uff0c\u8fd9\u4eba\u624d\u662f\u6b63\u51f6\u3002\u4ed6\u6740\u4e86\u4f60\u7684\u59d1 \u59d1\uff0c\u4f60\u5feb\u6765\u52a9\u6211\uff01\u201d \u3000\u3000\u51af\u745b\u5e94\u4e86\u4e00\u58f0\uff0c\u89c1\u51af\u7433\u5bf9\u4ed8\u5f97\u4e86\u5510\u91d1\u5cf0\uff0c\u7acb\u523b\u6325\u5251\u53bb\u52a9\u5510\u6653\u6f9c\uff0c\u4e00\u51fa\u624b\u4fbf\u662f\u5929\u5c71\u5251\u6cd5\u4e2d \u7684\u7cbe\u5999\u7edd\u62db\uff0c\u659c\u523a\u6842\u6bd5\u751f\u8170\u80c1\u7684\u6b7b\u7a74\uff01 \u3000\u3000\u6842\u534e\u751f\u817e\u5730\u4e00\u4e2a\u7ffb\u8eab\uff0c\u5251\u5982\u98de\u51e4\uff0c\u53cd\u81c2\u523a\u624e\uff0c\u51af\u745b\u8eab\u6cd5\u8f7b\u7075\uff0c\u4e00\u98d8\u4e00\u6643\uff0c\u907f\u62db\u8fdb\u62db\uff0c\u4e00 \u4e0b\u5b50\u4fbf\u62a2\u5230\u6842\u534e\u751f\u53f3\u4fa7\uff0c\u6842\u534e\u751f\u5251\u62db\u867d\u7136\u602a\u5f02\uff0c\u5374\u662f\u88ab\u5979\u8fde\u62a2\u653b\u52bf\uff0c\u53ef\u662f\u51af\u745b\u4e5f\u523a\u4ed6\u4e0d\u7740\u3002 \u4e24\u4eba\u77ac\u606f\u4e4b\u95f4\uff0c\u5404\u62a2\u4e09\u62db\u3002\u51af\u745b\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u6bd4\u5510\u6653\u6f9c\u51fa\u624b\u66f4\u72e0\u3002 \u3000\u3000\u672c\u6765\u82e5\u662f\u4e00\u5bf9\u4e00\uff0c\u6842\u534e\u751f\u6bd4\u51af\u5510\u4e8c\u4eba\u90fd\u8981\u7565\u80dc\u4e00\u7b79\uff0c\u53ef\u662f\u5982\u4eca\u51af\u5510\u53cc\u5251\u8054\u653b\uff0c\u800c\u4e14\u53c8\u90fd \u662f\u5b9d\u5251\uff0c\u9510\u5229\u65e0\u6bd4\u3002\u6842\u534e\u751f\u65bd\u5c55\u5168\u8eab\u672c\u9886\uff0c\u5140\u81ea\u88ab\u4ed6\u4eec\u903c\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u51af\u745b\u5251\u62db\u8d8a\u53d1\u50ac\u7d27\uff0c\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4e0d\u8981\u7528\u5251\u6740\u4ed6\uff0c\u5c06\u4ed6\u4e5f\u903c\u4e0b\u5c71\u6da7\u5e95\u53bb\uff01\u201d\u51af\u745b\u77ed\u5251\u4e00 \u5212\uff0c\u5de6\u4fa7\u523a\u4ed6\u4e91\u53f0\u7a74\uff0c\u53f3\u4fa7\u523a\u4ed6\u7ae0\u95e8\u7a74\uff0c\u6842\u534e\u751f\u903c\u5f97\u8fde\u9000\u4e24\u6b65\uff1b\u5510\u6653\u6f9c\u957f\u5251\u4e00\u6325\uff0c\u5212\u4e86\u4e00\u4e2a \u534a\u5f27\u5f62\uff0c\u4e0a\u523a\u54bd\u5589\uff0c\u4e0b\u524a\u819d\u76d6\uff0c\u53c8\u628a\u6842\u534e\u751f\u903c\u5f97\u9000\u4e86\u4e24\u6b65\uff0c\u5c71\u6d2a\u631f\u7740\u6c99\u77f3\uff0c\u6eda\u6eda\u800c\u4e0b\uff0c\u5c71\u6da7 \u6c34\u6d41\u6e4d\u6025\uff0c\u6c34\u58f0\u8f70\u9e23\uff0c\u6842\u6bd5\u751f\u8fd8\u6709\u51e0\u6b65\uff0c\u4fbf\u8981\u88ab\u903c\u5230\u5c71\u6da7\u60ac\u5d16\uff0c\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff01 \u3000\u3000\u5510\u6653\u6f9c\u62a2\u51fa\u4e00\u6b65\uff0c\u6e38\u9f99\u5251\u518d\u5411\u524d\u538b\uff0c\u6842\u534e\u751f\u54ac\u5b9e\u7259\u6839\uff0c\u6a2a\u5251\u529b\u5c01\uff0c\u51af\u745b\u4e00\u5251\u659c\u523a\uff0c\u53f1 \u9053\uff1a\u201c\u53bb\uff01\u201d\u53cc\u5251\u5408\u529b\uff0c\u6842\u534e\u751f\u53ea\u89c9\u4e00\u80a1\u6781\u5927\u7684\u6f5c\u529b\u63a8\u8feb\u8fc7\u6765\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u53c8\u9000\u4e86\u4e24\u6b65\uff0c\u6b63 \u5728\u6027\u547d\u4fc4\u9877\u4e4b\u9645\uff0c\u5ffd\u89c1\u4e00\u56e2\u767d\u5f71\uff0c\u8d5b\u4f3c\u98ce\u9a70\u7535\u63a3\uff0c\u4ece\u5c71\u811a\u4e0b\u76f4\u6eda\u4e0a\u6765\uff0c\u968f\u5373\u542c\u5f97\u4e00\u58f0\u6e05\u8106\u7684 \u53eb\u58f0\u9053\uff1a\u201c\u6653\u6f9c\u4f4f\u624b\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u51af\u5510\u4e24\u4eba\u6115\u7136\u56de\u987e\uff0c\u4e24\u67c4\u5251\u4ecd\u7136\u672a\u80af\u653e\u677e\uff0c\u5c31\u5728\u8fd9\u5239\u90a3 \u95f4\uff0c\u90a3\u56e2\u767d\u5f71\u5f53\u7684\u4e00\u843d\uff0c\u53ea\u542c\u5f97\u5f53\u7684\u4e00\u58f0\uff0c\u4e09\u53e3\u7ea0\u7ed3\u76f8\u4ea4\u7684\u5b9d\u5251\uff0c\u88ab\u6765\u4eba\u4e00\u4e0b\u6311\u5f00\u3002\u6842\u534e\u751f \u53c8\u559c\u53c8\u60ca\uff0c\u7741\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u9762\u524d\u7ad9\u7740\u4e00\u4e2a\u5c11\u5973\uff0c\u7b11\u541f\u541f\u7684\u5c06\u4ed6\u7684\u4e24\u4e2a\u201c\u654c\u4eba\u201d\u62c9\u8fc7\u4e00\u8fb9\uff0c\u968f \u5373\u53c8\u662f\u4e00\u6761\u4eba\u5f71\u98de\u5954\u800c\u6765\uff0c\u53eb\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u4f60\u8fd8\u4e0d\u591a\u8c22\u5415\u5973\u4fa0\u6551\u547d\u4e4b\u6069\uff1f\u201d \u3000\u3000\u6842\u534e\u751f\u62b1\u5251\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u6765\u7684\u6562\u662f\u6c5f\u5357\u516b\u4fa0\u4e2d\u7684\u5415\u56db\u5a18\u4e48\uff1f\u201d \u3000\u3000\u5192\u5e7f\u751f\u9053\uff1a\u201c\u4e0d\u662f\u5979\u8fd8\u662f\u8c01\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4e45\u4ef0\u5927\u540d\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01\u201d\u5415\u56db\u59d0\u7b11 \u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u4e00\u5bb6\uff0c\u6253\u4e86\u8fd9\u8bb8\u4e45\u8fd8\u4e0d\u77e5\u9053\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u5ffd\u7136\u6d41\u6cea\u53eb\u9053\uff1a\u201c\u5415\u59d0\u59d0\u6055\u6211\u8fd9\u6b21 \u4e0d\u80fd\u542c\u4f60\u7684\u8bdd\uff0c\u6768\u5bb6\u59b9\u5b50\u88ab\u4ed6\u4eec\u6740\u4e86\uff01\u201d\u5415\u56db\u5a18\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u6768\u67f3\u9752\u9047\u5bb3\u4e86 \u4e48\uff1f\u201d\u6842\u534e\u751f\u6025\u6025\u5206\u8fa9\u9053\uff1a\u201c\u6768\u59d1\u5a18\u662f\u81ea\u5df1\u8dcc\u843d\u5c71\u6da7\uff0c\u88ab\u5c71\u6d2a\u51b2\u5230\u6e56\u4e2d\uff0c\u6211\u6551\u5979\u8fd8\u6765\u4e0d\u53ca \u5462\uff01\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u4f60\u5230\u5e95\u770b\u6e05\u695a\u6ca1\u6709\uff1f\u6768\u67f3\u9752\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u5510\u6653\u6f9c\u9002\u624d\u6454\u5012\u5730\u4e0a\uff0c\u7ad9\u8d77\u6765\u65f6\uff0c\u6768\u67f3\u9752\u5df2\u88ab\u5c71\u6d2a\u51b2\u53bb\uff0c\u4ed6\u53ea\u89c1\u6842\u534e\u751f\u4ece\u5c71\u6da7\u4e0a\u7a7a\u5012\u7eb5 \u56de\u6765\uff0c\u6545\u6b64\u7591\u5fc3\u662f\u4ed6\u903c\u6b7b\u4e86\uff0c\u542c\u4ed6\u5982\u6b64\u5206\u8fa8\uff0c\u60c5\u6025\u58f0\u98a4\uff0c\u4e0d\u4f3c\u8bf4\u8c0e\uff0c\u4e0d\u6562\u65ad\u5b9a\u3002\u626c\u58f0\u95ee\u9053\uff1a \u201c\u7433\u59b9\uff01\u9002\u624d\u4e4b\u4e8b\uff0c\u4f60\u53ef\u770b\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u51af\u7433\u867d\u7136\u618e\u538c\u6768\u67f3\u9752\uff0c\u5bf9\u5979\u9002\u624d\u820d\u5df1\u6551\u4eba\uff0c\u4e5f\u9887\u611f\u52a8\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u6768\u5bb6\u59d1\u59d1\u867d\u7136\u4e0d\u662f \u88ab\u4ed6\u6240\u6740\uff0c\u4f46\u5979\u56e0\u820d\u8eab\u6551\u62a4\u53d4\u53d4\uff0c\u88ab\u5c71\u6d2a\u5377\u53bb\uff0c\u63a8\u539f\u7978\u59cb\uff0c\u8bf4\u662f\u88ab\u4ed6\u6240\u6740\u4e5f\u4e0d\u7b97\u51a4\u8d56\u3002\u4ed6\u548c \u8fd9\u4e2a\u8001\u8d3c\uff0c\u90fd\u662f\u903c\u6b7b\u6768\u59d1\u59d1\u7684\u4eba\uff01\u201d\u5510\u91d1\u5cf0\u6012\u9053\uff1a\u201c\u4f60\u4eec\u8981\u8ffd\u7a76\u903c\u6b7b\u6768\u67f3\u9752\u7684\u51f6\u624b\uff0c\u6211\u5374\u5411 \u8c01\u8ffd\u7a76\u6740\u5bb3\u5973\u5a7f\u7684\u51f6\u624b\uff1f\u597d\uff0c\u4f60\u4eec\u4eca\u65e5\u6043\u7740\u4eba\u591a\uff0c\u6211\u5510\u8001\u4e8c\u4e5f\u4e0d\u6253\u7b97\u6d3b\u7740\u56de\u56db\u5ddd\u4e86\uff0c\u6211\u4eec\u5510 \u5bb6\u4e5f\u81ea\u6709\u4eba\u66ff\u6211\u62a5\u4ec7\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u671b\u7740\u6eda\u6eda\u6d2a\u6ce2\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6b7b\u8005\u5df2\u77e3\uff0c\u6d3b\u7740\u7684\u628a\u8fd9\u51a4\u4ec7\u89e3\u5f00\u4e86\u5427\uff01\u53cc\u65b9 \u90fd\u6b7b\u4e86\u4e00\u4eba\uff0c\u4e5f\u4e0d\u5fc5\u95ee\u8c01\u662f\u8c01\u975e\u4e86\uff01\u6653\u6f9c\uff0c\u4f60\u5927\u7ea6\u4e5f\u4e0d\u77e5\u9053\u8fd9\u4f4d\u5144\u53f0\u7684\u6765\u5386\u3002\u201d\u5f53\u4e0b\u5c06\u4e24\u65b9 \u7684\u6765\u5386\u6e0a\u6e90\u90fd\u8be6\u8bf4\u4e86\u3002\u5510\u6653\u6f9c\u89c1\u6768\u67f3\u9752\u59d4\u5b9e\u4e0d\u662f\u6842\u534e\u751f\u6240\u6740\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\u82e6\u547d\u7684\u6069 \u5e08\u54ea\uff01\u597d\u8584\u547d\u7684\u59b9\u5b50\u54ea\uff01\u201d\u6842\u534e\u751f\u6b49\u7136\u8d54\u7f6a\uff0c\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5f7c\u6b64\u4e0d\u77e5\uff0c\u65e0\u5fc3\u4e4b\u9519\uff0c\u5c31\u7b97\u4e86 \u5427\uff01\u201d\u5510\u91d1\u5cf0\u89c1\u5bf9\u65b9\u63a5\u53d7\u548c\u89e3\uff0c\u4e5f\u4fbf\u529d\u6b62\u4e86\u5973\u513f\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e5f\u8ba4\u547d\u4e86\uff01\u201d\u643a\u4e86\u5973\u513f\uff0c\u4e0b \u5c71\u4fbf\u662f\u3002\u5192\u5e7f\u751f\u544a\u4e86\u4e2a\u7f6a\uff0c\u5e26\u4e86\u5f1f\u5f1f\uff0c\u6ee1\u4e0d\u597d\u610f\u601d\u7684\u6025\u6025\u79bb\u5f00\u3002 \u3000\u3000\u5510\u91d1\u5cf0\u7b49\u4eba\u8d70\u540e\uff0c\u5510\u6653\u6f9c\u5982\u9189\u5982\u75f4\uff0c\u76ee\u4e2d\u8574\u6cea\uff0c\u770b\u7740\u6df7\u6d4a\u7ffb\u817e\u7684\u6e56\u6c34\uff0c\u4e45\u4e45\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u4ed6\u867d\u7136\u4e0d\u7231\u6768\u67f3\u9752\uff0c\u53ef\u662f\u5bf9\u5979\u820d\u8eab\u76f8\u6551\uff0c\u4ee5\u81f3\u8eab\u6b7b\uff0c\u5374\u611f\u5230\u5341\u4e8c\u4e07\u5206\u7684\u96be\u8fc7\u3002\u5415\u56db\u5a18\u9053\uff1a \u201c\u6768\u8001\u524d\u8f88\u5df2\u6b7b\uff0c\u4ed6\u5bb6\u65e0\u4eba\uff0c\u4e27\u4e8b\u975e\u4f60\u4e3b\u6301\u4e0d\u53ef\uff0c\u67f3\u9752\u7684\u5c38\u9996\u4f60\u4e5f\u8be5\u6253\u635e\u56de\u6765\u3002\u201d\u5510\u6653\u6f9c\u6cea \u5982\u96e8\u4e0b\uff0c\u70b9\u4e86\u70b9\u5934\u3002\u51af\u745b\u9053\uff1a\u201c\u6768\u516c\u516c\u8fd8\u6709\u4e00\u4fe1\u7ed9\u4f60\u3002\u201d\u5510\u6653\u6f9c\u63e9\u4e86\u773c\u6cea\uff0c\u63a5\u8fc7\u4fe1\u770b\uff0c\u4fe1\u4e2d \u5199\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u76f8\u5904\u5341\u6709\u4f59\u5e74\uff0c\u60c5\u5982\u7236\u5b50\uff0c\u6211\u4eca\u5982\u98ce\u4e2d\u6b8b\u70db\uff0c\u4e0d\u53ca\u76f8\u5f85\uff0c\u5c0f\u5973\u67f3\u9752\uff0c\u5e7c\u5931\u6bcd \u6559\uff0c\u4efb\u6027\u9a84\u7eb5\uff0c\u96be\u914d\u541b\u5b50\u3002\u8d24\u5951\u613f\u76f8\u5fcd\u5219\u5fcd\u4e4b\uff0c\u4e0d\u613f\u76f8\u5fcd\u5219\u53e6\u9009\u8d24\u6dd1\uff0c\u5f85\u4e4b\u5982\u59b9\uff0c\u6211\u5728\u6cc9\u4e0b \u4ea6\u7791\u76ee\u77e3\u3002\u201d\u6768\u4ef2\u82f1\u8fd9\u5c01\u9057\u4fe1\uff0c\u539f\u662f\u4ee5\u9000\u4e3a\u8fdb\u7684\u624b\u6cd5\uff0c\u5510\u6653\u6f9c\u770b\u4e86\uff0c\u66f4\u89c9\u96be\u8fc7\u3002\u60f3\u4e86\u4e00\u60f3\uff0c \u5ffd\u7136\u62c9\u4e86\u51af\u745b\u7684\u624b\uff0c\u8d70\u5230\u5c71\u6da7\u65c1\uff0c\u770b\u7740\u6eda\u6eda\u6d2a\u6ce2\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u745b\u59b9\uff0c\u7ecf\u4e86\u8fd9\u573a\u53d8\u6545\uff0c\u6211\u4eca \u751f\u4eca\u4e16\uff0c\u518d\u4e5f\u6ca1\u5fc3\u60c5\u8c08\u8bba\u5a5a\u4e8b\u4e86\uff0c\u4f60\u80fd\u8c05\u89e3\u6211\u4e48\uff1f\u201d\u51af\u745b\u768e\u5982\u6717\u6708\u7684\u5fc3\u60c5\uff0c\u6709\u5982\u8499\u4e0a\u4e00\u5c42\u9634 \u5f71\uff0c\u867d\u7136\u5979\u4ece\u672a\u66fe\u60f3\u8fc7\u5a5a\u5ac1\uff0c\u542c\u4e86\u4e5f\u89c9\u8f9b\u9178\u3002\u5f53\u4e0b\u542b\u6cea\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u76f8\u4ea4\u4ee5\u5fc3\uff0c\u672c\u5c31\u4e0d \u5fc5\u5982\u4e16\u4fd7\u4e4b\u4eba\uff0c\u8c08\u8bba\u5a5a\u5ac1\u3002\u6768\u5bb6\u59d1\u59d1\u4e3a\u4f60\u800c\u6b7b\uff0c\u4f60\u4eca\u751f\u4e0d\u5a36\uff0c\u5b9e\u662f\u5e94\u8be5\u3002\u6211\u600e\u4f1a\u602a\u4f60\u3002\u4e0d\u4f46 \u662f\u4f60\uff0c\u6211\u4eca\u751f\u4eca\u4e16\u4e5f\u4e0d\u4f1a\u518d\u7ed3\u5a5a\u7684\u4e86\uff01\u201d\u5510\u6653\u6f9c\u770b\u5979\u6cea\u5149\u83b9\u7136\uff0c\u6b32\u529d\u65e0\u4ece\u529d\u8d77\uff0c\u53ea\u662f\u957f\u957f\u7684 \u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u5510\u6653\u6f9c\u548c\u5415\u56db\u5a18\u7b49\u56de\u8f6c\u6768\u5bb6\uff0c\u5c06\u6768\u4ef2\u82f1\u5b89\u846c\u5728\u4e1c\u5e73\u5c71\u9e93\uff0c\u4e27\u4e8b\u5b8c\u540e\uff0c\u5c71\u6d2a\u5df2\u9000\u3002\u5510\u6653\u6f9c \u7b49\u53c8\u5230\u6e56\u4e2d\u6253\u635e\u5c38\u9996\uff0c\u5728\u5c0f\u6e56\u4e2d\u6253\u635e\u4e86\u534a\u65e5\uff0c\u5374\u662f\u6beb\u65e0\u53d1\u73b0\uff0c\u96c7\u7cbe\u901a\u6c34\u6027\u7684\u4eba\u6f5c\u4e0b\u6c34\u5e95\u5bdf \u770b\uff0c\u4e5f\u6253\u4e0d\u7740\u3002\u4f46\u5374\u53d1\u73b0\u8fd9\u4e2a\u5c0f\u6e56\u4e2d\u6709\u4e00\u7f3a\u53e3\u901a\u5411\u5916\u9762\u7684\u6de6\u6cb3\uff0c\u6f5c\u6c34\u7684\u4eba\u731c\u60f3\uff0c\u5c38\u9996\u5927\u6982\u662f \u88ab\u6d2a\u6c34\u51b2\u5230\u5916\u9762\u7684\u6de6\u6cb3\u53bb\u4e86\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u6b7b\u540e\u7ffb\u76f8\u5fc6\uff0c\u751f\u524d\u6068\u4e8b\u591a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e94\u56de\u3000\u4e92\u6597\u6743\u8c0b\u3000\u5c06\u519b\u60b2\u5931\u52bf\u3000\u81ea\u5bfb\u4e86\u65ad\u3000\u957f\u8001\u656c\u51f6\u5f92 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u56db\u5341\u4e94\u56de \u4e92\u6597\u6743\u8c0b\u3000\u5c06\u519b\u60b2\u5931\u52bf \u81ea\u5bfb\u4e86\u65ad\u3000\u957f\u8001\u656c\u51f6\u5f92 \u3000\u3000\u6768\u67f3\u9752\u88ab\u5c71\u6d2a\u5377\u53bb\uff0c\u8fde\u5c38\u4f53\u4e5f\u5bfb\u89c5\u4e0d\u89c1\uff0c\u5510\u6653\u6f9c\u5185\u759a\u4e8e\u5fc3\uff0c\u5b89\u846c\u4e86\u6069\u5e08\u6768\u4ef2\u82f1\u4e4b\u540e\uff0c\u4f7f \u968f\u5415\u56db\u5a18\u7b49\u540c\u5f80\u9099\u5c71\uff0c\u867d\u7136\u6709\u51af\u745b\u671d\u5915\u76f8\u4f34\uff0c\u4ecd\u662f\u6101\u6000\u96be\u91ca\u3002 \u3000\u3000\u5176\u65f6\u96cd\u6b63\u5e1d\u4f4d\u5df1\u56fa\uff0c\u65bd\u7528\u4e25\u5211\u9177\u6cd5\uff0c\u7edf\u6cbb\u5929\u4e0b\uff0c\u6c11\u95f4\u4e49\u58eb\uff0c\u5728\u9ad8\u538b\u4e4b\u4e0b\uff0c\u8d77\u4e8b\u4e0d\u6613\uff0c\u5927 \u90e8\u533f\u8ff9\u9500\u58f0\uff0c\u5448\u73b0\u4e86\u66b4\u98ce\u96e8\u4e4b\u524d\u7684\u5e73\u9759\u3002 \u3000\u3000\u4e0e\u5415\u7559\u826f\u6848\u6709\u5173\u7684\u9996\u8111\u8bf8\u4eba\uff0c\u5e78\u4e8f\u7518\u51e4\u6c60\u6d3e\u4eba\u901a\u77e5\u5f97\u5feb\uff0c\u5927\u534a\u90fd\u80fd\u9003\u8131\uff0c\u53ea\u662f\u9752\u5dde\u5468\u656c \u8206\u3001\u8944\u57ce\u9ec4\u8865\u5eb5\uff0c\u4ee5\u53ca\u8bb8\u591a\u520a\u523b\u5415\u6c0f\u4e66\u7c4d\u4e0e\u53ca\u6536\u85cf\u5415\u6c0f\u4e66\u7c4d\u7684\u4eba\uff0c\u90fd\u88ab\u682a\u8fde\u5750\u7f6a\uff0c\u6216\u88ab\u5904 \u6b7b\uff0c\u6216\u88ab\u5145\u519b\uff0c\u5377\u8d77\u4e86\u8f69\u7136\u5927\u6ce2\uff0c\u8fc7\u4e86\u534a\u5e74\uff0c\u65b9\u624d\u6e10\u544a\u5e73\u606f\u3002 \u3000\u3000\u5415\u56db\u5a18\u751a\u4e3a\u607c\u6012\uff0c\u4e00\u65e5\uff0c\u4e0e\u7518\u51e4\u6c60\u5546\u8bae\uff0c\u6b32\u5230\u4eac\u57ce\u523a\u6740\u96cd\u6b63\uff0c\u7518\u51e4\u6c60\u9053\uff1a\u201c\u516b\u59b9\u662f\u5973\u4e2d \u82f1\u8c6a\uff0c\u4eba\u4e2d\u4fca\u6770\uff0c\u5c82\u4e0d\u95fb\u541b\u5b50\u62a5\u4ec7\uff0c\u5341\u5e74\u672a\u665a\u3002\u76ee\u4e0b\u6b63\u662f\u96cd\u6b63\u52bf\u76db\u4e4b\u65f6\uff0c\u6211\u4eec\u8fd8\u5e94\u518d\u9690\u5fcd\u4e9b \u65f6\uff0c\u9759\u5f85\u673a\u4f1a\u3002\u201d\u5415\u56db\u5a18\u53f9\u9053\uff1a\u201c\u6211\u5c82\u4e0d\u77e5\u8fd9\u4e2a\u9053\u7406\uff0c\u53ea\u662f\u773c\u770b\u96cd\u6b63\u8fd9\u53ae\uff0c\u8086\u884c\u66b4\u8650\uff0c\u6ee5\u6740 \u65e0\u8f9c\uff0c\u5b9e\u5728\u6291\u4e0d\u4f4f\u5fc3\u4e2d\u6c14\u6124\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u6211\u524d\u65e5\u4e0b\u5c71\uff0c\u542c\u5230\u51e0\u6bb5\u5173\u4e8e\u96cd\u6b63\u7684\u6545\u4e8b\u3002\u4e14\u8bf4\u7ed9\u4f60\u542c\uff0c\u4f60\u5c31\u77e5\u9053\u96cd\u6b63\u9632\u8303 \u7684\u4e25\u5bc6\u4e86\u3002\u201d \u3000\u3000\u201c\u7b2c\u4e00\u4ef6\u662f\u65b0\u79d1\u72b6\u5143\u738b\u4e91\u9526\u7684\u6545\u4e8b\u3002\u96cd\u6b63\u56e0\u4e3a\u738b\u4e91\u9526\u662f\u4ed6\u767b\u4f4d\u4e4b\u540e\u7684\u7b2c\u4e00\u4efb\u72b6\u5143\uff0c\u751a\u4e3a \u770b\u91cd\uff0c\u6ee1\u671d\u6587\u6b66\uff0c\u89c1\u7687\u5e1d\u770b\u91cd\u738b\u72b6\u5143\uff0c\u4fbf\u7eb7\u7eb7\u53bb\u8d8b\u5949\u4ed6\uff0c\u771f\u4e2a\u662f\u8f66\u9a6c\u55a7\u56a3\uff0c\u95e8\u5ead\u5982\u5e02\u3002\u8fd9\u4f4d \u738b\u72b6\u5143\u5b98\u5c45\u6043\u8bfb\uff0c\u751a\u662f\u6e05\u95f2\u3002\u5e73\u65e5\u9664\u4e86\u505a\u505a\u8bd7\u5199\u5199\u5b57\u5916\uff0c\u5c31\u662f\u6b22\u559c\u6253\u7eb8\u724c\uff0c\u4e00\u65e5\uff0c\u671d\u7f62\u5f52 \u6765\uff0c\u738b\u72b6\u5143\u548c\u51e0\u4f4d\u540c\u50da\u53c8\u5728\u4e66\u623f\u91cc\u6253\u724c\uff0c\u5ffd\u7136\u4e00\u9635\u98ce\u6765\uff0c\u628a\u7eb8\u724c\u522e\u5728\u5730\u4e0b\uff0c\u62fe\u8d77\u6765\u67e5\u70b9\uff0c\u5374 \u7f3a\u4e86\u4e00\u5f20\uff0c\u738b\u72b6\u5143\u4e5f\u4e0d\u5728\u610f\uff0c\u5429\u5490\u5bb6\u4eba\u6362\u4e86\u4e00\u526f\u724c\u518d\u6253\u3002\u81f3\u4e86\u7b2c\u4e8c\u5929\uff0c\u738b\u4e91\u9526\u4e0a\u671d\uff0c\u96cd\u6b63\u7687 \u5e1d\u5ffd\u95ee\u4ed6\u9053\uff1a\u2018\u4f60\u5728\u5bb6\u4e2d\u5e73\u65f6\u4f5c\u4f55\u6d88\u9063\uff1f\u2019\u738b\u4e91\u9526\u5012\u4e5f\u8001\u5b9e\uff0c\u78d5\u5934\u594f\u9053\uff1a\u2018\u5fae\u81e3\u522b\u65e0\u55dc\u597d\uff0c \u5c31\u662f\u559c\u6b22\u6253\u6253\u7eb8\u724c\u3002\u2019\u96cd\u6b63\u7b11\u9053\uff1a\u2018\u6715\u542c\u8bf4\u4f60\u6628\u65e5\u6210\u4e86\u4e00\u526f\u5927\u724c\uff0c\u5ffd\u7136\u88ab\u98ce\u522e\u53bb\u4e00\u5f20\uff0c\u53ef\u6709 \u8fd9\u4e8b\u4e48\uff1f\u2019\u738b\u4e91\u9526\u5927\u4e3a\u5403\u60ca\uff0c\u530d\u4f0f\u594f\u9053\uff1a\u2018\u5723\u4e0a\u660e\u9274\u4e07\u91cc\uff0c\u662f\u6709\u8fd9\u56de\u4e8b\u60c5\u3002\u2019\u96cd\u6b63\u9053\uff1a\u2018\u8fd9 \u5f20\u724c\u627e\u5230\u4e86\u6ca1\u6709\uff1f\u2019\u738b\u4e91\u9526\u9053\uff1a\u2018\u6ca1\u6709\u627e\u5230\u3002\u2019\u96cd\u6b63\u54c8\u54c8\u5927\u7b11\uff0c\u4ece\u9f99\u6848\u4e0a\u4e22\u4e0b\u4e00\u5f20\u7eb8\u724c\uff0c \u9053\uff1a\u2018\u738b\u4e91\u9526\uff0c\u6055\u4f60\u65e0\u7f6a\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u4f60\u770b\u770b\u8fd9\u5f20\u7eb8\u724c\uff0c\u662f\u4e0d\u662f\u4f60\u4e22\u5931\u7684\u90a3\u5f20\u3002\u2019\u738b\u4e91\u7ef5\u4e00 \u770b\uff0c\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u8fde\u5fd9\u78d5\u5934\u8bf4\u662f\u3002\u96cd\u6b63\u7b11\u9053\uff1a\u2018\u4f60\u5f88\u8001\u5b9e\uff0c\u4e0d\u66fe\u9a97\u6715\u3002\u4e22\u5931\u7684\u724c\uff0c\u6715\u5df2\u66ff \u4f60\u627e\u56de\u6765\u4e86\uff0c\u4f60\u5feb\u56de\u5bb6\u53bb\u6210\u5c40\u5427\u3002\u2019\u8fd9\u4ef6\u4e8b\u60c5\u8fc7\u540e\uff0c\u6ee1\u671d\u6587\u6b66\uff0c\u65e0\u4e0d\u80c6\u6218\u5fc3\u60ca\uff0c\u79c1\u4e0b\u8c08\u8bdd\uff0c \u4e5f\u8c28\u614e\u5c0f\u5fc3\uff0c\u7edd\u4e0d\u6562\u8bae\u8bba\u671d\u653f\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u8bf4\u5b8c\u4e4b\u540e\uff0c\u5415\u56db\u5a18\u9053\uff1a\u201c\u8fd9\u4e00\u5b9a\u662f\u8840\u6ef4\u5b50\u5e72\u7684\u628a\u620f\u3002\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8fd9\u8fd8\u7528\u8bf4\uff1f \u96cd\u6b63\u73b0\u5728\u628a\u8840\u6458\u5b50\u5927\u4e3a\u6269\u5145\uff0c\u4f5c\u4e3a\u4ed6\u7684\u8033\u76ee\u3002\u6211\u4eec\u5165\u4eac\uff0c\u5fc5\u7136\u4e0d\u4f3c\u4ece\u524d\u5bb9\u6613\u4e86\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u542c\u4f60\u8bf4\u6765\uff0c\u8fd9\u5012\u662f\u4e2a\u597d\u6d88\u606f\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u96cd\u6b63\u9632\u8303\u68ee\u4e25\uff0c\u600e\u4e48\u5012\u662f\u4e2a\u597d\u6d88\u606f\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u9053\uff1a\u201c\u4ed6\u8fde\u81ea\u5df1\u7684\u5927\u81e3\u4e5f\u8bf8\u591a\u731c\u7591\uff0c\u4e0d\u6562\u76f8\u4fe1\u4e86\u3002\u8fd9\u5c82\u4e0d\u662f\u771f\u771f\u6b63\u6b63\u7684\u2018\u5b64\u5bb6\u5be1 \u4eba\u2019\u4e86\u4e48\uff1f\u4e00\u4ecb\u72ec\u592b\uff0c\u6709\u4f55\u53ef\u60e7\uff1f\u201d \u3000\u3000\u7518\u51e4\u6c60\u60f3\u4e86\u4e00\u60f3\uff0c\u660e\u767d\u4e86\u5415\u56db\u5a18\u7684\u610f\u601d\uff0c\u9053\uff1a\u201c\u516b\u59b9\u8bf4\u7684\u662f\u3002\u201d\u63a5\u7740\u53c8\u9053\uff1a\u201c\u96cd\u6b63\u5bf9\u4eac \u5b98\u53ea\u662f\u731c\u7591\u9632\u8303\uff0c\u5bf9\u5916\u81e3\u7684\u624b\u6bb5\u66f4\u9177\u3002\u4e00\u4e9b\u524d\u671d\u7684\u5c01\u7586\u5927\u540f\uff0c\u64a4\u7684\u64a4\uff0c\u6362\u7684\u6362\uff0c\u6740\u7684\u6740\uff0c\u8fde \u6587\u5b98\u4e5f\u4e0d\u80fd\u907f\u514d\u3002\u6709\u4e2a\u6b8b\u9177\u7684\u2018\u7b11\u8bdd\u2019\u6211\u518d\u8bf4\u7ed9\u4f60\u542c\u542c\u3002\u67e5\u55e3\u5ead\u7684\u540d\u5b57\u4f60\u542c\u8fc7\u4e48\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u9053\uff1a\u201c\u67e5\u55e3\u5ead\u662f\u6d59\u6c5f\u4eba\uff0c\u4e24\u699c\u51fa\u8eab\u7684\u8fdb\u58eb\uff0c\u6709\u70b9\u6587\u540d\uff0c\u4f46\u5374\u662f\u4e2a\u5229\u7984\u718f\u5fc3\u7684\u5bb6 \u4f19\uff0c\u600e\u4e48\uff0c\u4ed6\u4e5f\u906d\u9047\u4e86\u4e0d\u5e78\u4e4b\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u96cd\u6b63\u8fde\u4ed6\u4e5f\u6740\u4e86\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u8fde\u67e5\u55e3\u5ead\u8fd9\u6837\u7684\u6548\u5fe0\u671d\u5ef7\u7684\u4eba\u4e5f\u4e0d\u80fd\u4fdd\u5168\u9996\u7ea7\u4e48\uff1f\u201d \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8bf4\u6765\u771f\u771f\u7b11\u8bdd\uff0c\u67e5\u55e3\u5ead\u4eca\u6625\u88ab\u547d\u4e3a\u6c5f\u897f\u8003\u5b98\uff0c\u4ed6\u51fa\u4e86\u4e00\u6761\u8003\u4e3e\u5b50\u7684\u9898\u76ee\u53eb \u505a\u2018\u7ef4\u6c11\u6240\u6b62\u2019\u3002\u5b54\u592b\u5b50\u90a3\u5957\u6211\u4e0d\u61c2\uff0c\u542c\u4eba\u8bf4\u8fd9\u662f\u4ece\u56db\u4e66\u4e0a\u6458\u4e0b\u6765\u7684\uff0c\u5f88\u5e73\u5e38\u7684\u4e00\u53e5\u8bdd\u3002\u201d \u5415\u56db\u5a18\u70b9\u4e86\u70b9\u5934\uff0c\u7518\u51e4\u6c60\u9053\uff1a\u201c\u53ef\u662f\u96cd\u6b63\u5374\u8bf4\u2018\u7ef4\u2019\u5b57\u548c\u2018\u6b62\u2019\u5b57\u662f\u2018\u96cd\u6b63\u53bb\u4e86\u5934\u2019\uff0c\u72af\u4e86 \u5927\u9006\u4e0d\u9053\u4e4b\u7f6a\uff0c\u7adf\u4f20\u8c15\u628a\u67e5\u55e3\u5ead\u4ea4\u4e09\u6cd5\u53f8\u5ba1\u5904\uff0c\u67e5\u55e3\u5ead\u5413\u51fa\u75c5\u6765\uff0c\u6b7b\u5728\u72f1\u4e2d\uff0c\u4ecd\u53d7\u6233\u5c38\u8885\u793a \u4e4b\u5211\uff0c\u4f60\u8bf4\u60e8\u4e0d\u60e8\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4ed6\u8d8a\u6b8b\u9177\uff0c\u5c31\u8d8a\u663e\u5f97\u4ed6\u602f\u61e6\uff0c\u6211\u770b\u2018\u96cd\u6b63\u53bb\u4e86\u5934\u2019\u7684\u65e5\u5b50\u4e5f\u4e0d\u8fdc\u4e86\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u56e0\u4e3a\u5916\u5b98\u88ab\u64a4\u88ab\u6362\u88ab\u6740\u7684\u5f88\u591a\uff0c\u56e0\u6b64\u7559\u4e0b\u4e86\u4e0d\u5c11\u7a7a\u7f3a\u3002\u8fd9\u91cc\u53c8\u6709\u4e00\u4e2a\u7b11 \u8bdd\uff0c\u4eca\u5e74\u6b63\u6708\u5341\u4e94\uff0c\u5927\u5c0f\u8859\u95e8\u90fd\u653e\u8282\u5047\uff0c\u5b98\u513f\u4eec\u5404\u81ea\u56de\u5bb6\u5403\u56e2\u5706\u9152\u95f9\u5143\u5bb5\u53bb\u4e86\u3002\u5185\u9601\u8859\u95e8\u91cc \u6709\u4e00\u4e2a\u6587\u4e66\uff0c\u540d\u53eb\u84dd\u7acb\u5fe0\uff0c\u56e0\u4e3a\u5bb6\u4e61\u8fdc\u5728\u6d59\u6c5f\u5bcc\u9633\uff0c\u72ec\u81ea\u7559\u5728\u8859\u4e2d\uff0c\u4e70\u4e86\u534a\u65a4\u9152\uff0c\u5207\u4e86\u4e00\u76d8 \u725b\u8089\uff0c\u5bf9\u7740\u6708\u513f\uff0c\u72ec\u914c\u53f9\u6c14\u3002\u5ffd\u7136\u8d70\u8fdb\u4e00\u4e2a\u5927\u6c49\uff0c\u95ee\u9053\uff0c\u2018\u8fd9\u91cc\u53ea\u5269\u4e0b\u4f60\u4e00\u4e2a\u4eba\u4e48\uff1f\u4e3a\u4f55\u53f9 \u6c14\uff1f\u2019\u8fd9\u59d3\u84dd\u7684\u6587\u4e66\u4ee5\u4e3a\u4ed6\u662f\u672c\u8859\u95e8\u7684\u5b88\u536b\uff0c\u4fbf\u8bf7\u4ed6\u5bf9\u914c\uff0c\u5bf9\u4ed6\u8bf4\u9053\uff1a\u2018\u6211\u5728\u8fd9\u91cc\u5f53\u4e00\u540d\u5c0f \u5c0f\u7684\u5f55\u4e8b\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u516b\u5e74\u4e86\u3002\u8fd9\u4e2a\u7a77\u5dee\u4e8b\u771f\u5982\u9e21\u808b\uff0c\u98df\u4e4b\u65e0\u5473\uff0c\u5f03\u4e4b\u53ef\u60dc\uff0c\u4e0d\u77e5\u4f55\u5e74\u4f55\u6708 \u624d\u80fd\u51fa\u5934\uff1f\u4eca\u665a\u773c\u89c1\u522b\u4eba\u56e2\u5706\u8fc7\u8282\uff0c\u6211\u5374\u8fde\u4e70\u9152\u7684\u94b1\u90fd\u662f\u501f\u6765\u7684\uff0c\u7109\u80fd\u6ca1\u6709\u611f\u89e6\u3002\u2019\u8fd9\u5927\u6c49 \u9053\uff1a\u2018\u4f60\u60f3\u505a\u5b98\u4e48\uff1f\u2019\u84dd\u7acb\u5fe0\u9053\uff1a\u2018\u7109\u6709\u4e0d\u60f3\u4e4b\u7406\uff0c\u53ea\u662f\u6211\u4e00\u65e0\u529f\u540d\u8d44\u683c\uff0c\u4e8c\u65e0\u94b1\u8d22\u6253\u70b9\uff0c \u600e\u8f6e\u5230\u6211\u505a\u5b98\uff1f\u2019\u8fd9\u5927\u6c49\u559d\u4e86\u4e00\u676f\u9152\uff0c\u9053\uff1a\u2018\u4f60\u60f3\u505a\u4ec0\u4e48\u5b98\uff1f\u2019\u84dd\u7acb\u5fe0\u6709\u4e86\u51e0\u5206\u9152\u610f\uff0c\u63b3\u8d77 \u8896\u5b50\uff0c\u4f38\u624b\u5728\u684c\u4e0a\u4e00\u62cd\uff0c\u8bf4\u9053\uff1a\u2018\u5927\u5b98\u6211\u6ca1\u798f\u4efd\u505a\uff0c\u6211\u82e5\u80fd\u505a\u4e00\u4e2a\u5e7f\u4e1c\u7684\u6cb3\u6cca\u6240\u5b98\uff0c\u5fc3\u613f\u5df2 \u8db3\u3002\u6cb3\u6cca\u6240\u5b98\uff0c\u5b98\u804c\u867d\u5c0f\uff0c\u90a3\u51fa\u5165\u53e3\u8239\u7684\u5b5d\u656c\uff0c\u6bcf\u5e74\u5c11\u8bf4\u4e5f\u6709\u4e09\u4e94\u5343\u4e24\u3002\u2019\u90a3\u5927\u6c49\u7b11\u4e86\u4e00 \u7b11\uff0c\u4fbf\u9053\u8c22\u544a\u8f9e\u3002\u7b2c\u4e8c\u5929\uff0c\u96cd\u6b63\u4eb2\u4f20\u2018\u5723\u65e8\u2019\u628a\u8fd9\u540d\u5c0f\u5f55\u4e8b\u8c03\u5230\u5e7f\u4e1c\u53bb\u505a\u6cb3\u6cca\u6240\u5b98\uff0c\u6ee1\u671d\u6587 \u6b66\u65e0\u4e0d\u60ca\u8be7\uff0c\u60f3\u4e0d\u5230\u8fd9\u6837\u829d\u9ebb\u7eff\u8c46\u822c\u7684\u5c0f\u5b98\uff0c\u4e5f\u8981\u52b3\u52a8\u4ed6\u4eec\u7684\u7687\u4e0a\u7279\u964d\u5723\u65e8\u3002\u540e\u6765\uff0c\u4e8b\u60c5\u4f20 \u4e86\u51fa\u6765\uff0c\u6709\u4eba\u4fbf\u731c\u90a3\u5927\u6c49\u82e5\u4e0d\u662f\u96cd\u6b63\u672c\u4eba\u4e5f\u662f\u96cd\u6b63\u7684\u8033\u76ee\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u59d3\u84dd\u7684\u5f55\u4e8b\u4ea4\u4e86\u2018\u597d\u8fd0\u2019\u4e86\uff0c\u4e0d\u8fc7\uff0c\u5c0f\u4eba\u5f97\u5fd7\uff0c\u7ec8\u975e\u597d\u4e8b\u3002\u201d\u7518\u51e4 \u6c60\u9053\uff1a\u201c\u516b\u59b9\u6240\u6599\u4e0d\u5dee\uff0c\u8fd9\u84dd\u7acb\u5fe0\u5230\u4efb\u4e4b\u540e\uff0c\u679c\u7136\u5927\u8086\u8d2a\u6c61\uff0c\u7559\u96be\u8239\u53ea\uff0c\u52d2\u7d22\u6e14\u6c11\uff0c\u65e0\u6240\u4e0d \u4e3a\u3002\u4ed6\u662f\u7279\u5949\u5723\u65e8\u5230\u4efb\u7684\u6cb3\u6cca\u6240\u5b98\uff0c\u4e0a\u53f8\u4e5f\u4e0d\u6562\u7ba1\u4ed6\u3002\u540e\u6765\u6fc0\u8d77\u6e14\u6c11\u516c\u6124\uff0c\u6697\u5730\u91cc\u628a\u4ed6\u6740 \u4e86\uff0c\u6c89\u5c38\u6cb3\u5e95\uff0c\u8ba9\u4ed6\u5582\u9c7c\u3002\u4e0a\u53f8\u56e0\u4ed6\u5e73\u65e5\u4ece\u4e0d\u5356\u5e10\uff0c\u5bf9\u6b64\u4e8b\u4e5f\u4e0d\u67e5\u7a76\uff0c\u53ea\u662f\u5907\u6848\u4e86\u4e8b\u3002\u53ef\u7b11 \u4ed6\u4e00\u5fc3\u6c42\u5b98\uff0c\u5374\u843d\u5f97\u6b7b\u4e8e\u975e\u547d\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u8bf4\u4e86\u8fd9\u51e0\u6bb5\u6545\u4e8b\u4e4b\u540e\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u84dd\u7acb\u5fe0\u4e0d\u8fc7\u662f\u5c0f\u5c0f\u7684\u6cb3\u6cca\u6240\u5b98\u800c\u5df2\uff0c \u6bd4\u4ed6\u8d2a\u6c61\u5f97\u591a\u7684\u5927\u5b98\uff0c\u8fd8\u4e0d\u77e5\u6709\u591a\u5c11\uff0c\u8001\u767e\u59d3\u5374\u662f\u65e0\u53ef\u5948\u4f55\u4e86\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u9053\uff1a\u201c\u8d2a\u5b98\u6c61\u540f\uff0c\u6740\u4e0d\u80dc\u6740\u3002\u9664\u975e\u628a\u7231\u65b0\u89c9\u7f57\u6c0f\u7684\u7687\u671d\u8fde\u6839\u62d4\u6389\u3002\u201d\u7518\u51e4\u6c60\u9053\uff0c \u201c\u96be\uff0c\u96be\u3002\u6ee1\u6d32\u4e4b\u52bf\u6b63\u76db\uff0c\u6211\u4eec\u8fd9\u4e00\u4ee3\u4eba\u6050\u6015\u4e0d\u53ca\u89c1\u5b83\u8986\u706d\u4e86\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u65b9\u8bed\u6709\u4e91\uff1a \u4e08\u592b\u505a\u4e8b\uff0c\u4e0d\u8ba1\u6210\u8d25\uff0c\u6211\u867d\u4e00\u4ecb\u5973\u6d41\uff0c\u8d74\u6c64\u8e48\u706b\uff0c\u5374\u4e5f\u5f53\u4ec1\u4e0d\u8ba9\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u53c8\u9053\uff1a\u201c\u91cd \u5149\u6c49\u5ba4\uff0c\u7ec8\u6211\u4eec\u4e4b\u751f\uff0c\u4e5f\u8bb8\u4e0d\u80fd\u76ee\u7779\uff0c\u4f46\u628a\u96cd\u6b63\u6740\u6389\uff0c\u5374\u672a\u5fc5\u4e0d\u80fd\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u6c89\u601d\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u516b\u59b9\u4e00\u8a00\uff0c\u542f\u6211\u8305\u585e\u3002\u5927\u4e49\u6240\u5728\uff0c\u5f53\u5168\u529b\u4ee5\u8d74\uff0c\u529f\u6210\u4e5f\u4e0d\u5fc5 \u5728\u6211\u3002\u6211\u770b\u8981\u9a71\u9010\u6ee1\u864f\uff0c\u6062\u590d\u6c49\u5ba4\uff0c\u975e\u4e09\u6570\u4eba\u6240\u80fd\u4e3a\u529b\uff0c\u674e\u6cbb\u524d\u6570\u65e5\u8bf4\u8981\u5165\u56db\u5ddd\uff0c\u56e0\u4e3a\u56db\u5ddd \u8fd8\u6709\u4ed6\u7236\u4eb2\u7684\u65e7\u90e8\uff0c\u4e0e\u6211\u5546\u8bae\uff0c\u5f53\u65f6\u6211\u8fd8\u4e0d\u6562\u540c\u610f\u3002\u56e0\u4e3a\u56db\u5ddd\u6b63\u662f\u5e74\u7fb9\u5c27\u7ba1\u8f96\u4e4b\u5730\uff0c\u800c\u674e\u8d64 \u5fc3\u5f53\u5e74\u6b8b\u7559\u7684\u65e7\u90e8\uff0c\u4e3a\u6570\u751a\u5c11\u3002\u674e\u6cbb\u82e5\u5165\u5ddd\u6d3b\u52a8\uff0c\u5371\u9669\u9887\u5927\u3002\u73b0\u5728\u770b\u6765\uff0c\u8fd8\u662f\u8ba9\u4ed6\u53bb\u7684\u597d\u3002 \u661f\u661f\u4e4b\u706b\uff0c\u53ef\u4ee5\u71ce\u539f\uff0c\u5192\u4e9b\u98ce\u9669\uff0c\u4e5f\u8fd8\u503c\u5f97\u3002\u65e5\u5185\u6211\u4e5f\u60f3\u5230\u6c5f\u5357\u8d70\u4e00\u8d9f\uff0c\u62dc\u8bbf\u4e00\u4e9b\u5e2e\u4f1a\u7684\u9996 \u9886\uff0c\u867d\u7136\u8fd8\u8c08\u4e0d\u5230\u805a\u4f17\u4e3e\u4e8b\uff0c\u6700\u5c11\u4e5f\u53ef\u4ee4\u4ed6\u4eec\u4e0d\u4e0e\u5b98\u5e9c\u540c\u6d41\u5408\u6c61\u3002\u201d \u3000\u3000\u8fc7\u4e86\u51e0\u5929\uff0c\u9099\u5c71\u4e0a\u7fa4\u96c4\u8bae\u8ba1\u5df2\u5b9a\uff0c\u674e\u6cbb\u4e0e\u51af\u7433\u76f8\u5055\u5165\u5ddd\uff0c\u7518\u51e4\u6c60\u8d76\u6c5f\u5357\u4e00\u5e26\u3002\u9c7c\u4eae\u7236\u5973 \u4e0e\u767d\u6cf0\u5b98\u4e5f\u91cd\u65b0\u51fa\u6d77\uff0c\u8bbf\u5bfb\u8fd8\u5269\u4e0b\u6765\u7684\u5404\u5c9b\u6d77\u76d7\u3002\u5510\u6653\u6f9c\u4f24\u5fc3\u672a\u8fc7\uff0c\u5374\u60f3\u4e0e\u51af\u745b\u56de\u5929\u5c71\u4e00 \u6b21\uff0c\u5415\u56db\u5a18\u60f3\u60f3\u4e5f\u597d\uff0c\u4fbf\u8ba9\u4ed6\u4eec\u5055\u884c\u3002 \u3000\u3000\u9099\u5c71\u7684\u7fa4\u96c4\u53bb\u4e86\u4e00\u534a\uff0c\u5269\u4e0b\u5415\u56db\u5a18\u5728\u5c71\u4e0a\u5b88\u62a4\u5e08\u5085\u7684\u575f\u5893\uff0c\u6625\u53bb\u79cb\u6765\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e24\u6613\u5bd2 \u6691\uff0c\u5728\u8fd9\u4e24\u5e74\u4e2d\uff0c\u5415\u56db\u5a18\u65e5\u5915\u7ec3\u5251\uff0c\u4e0d\u4f46\u628a\u7384\u5973\u5251\u6cd5\u7ec3\u5f97\u51fa\u795e\u5165\u5316\uff0c\u800c\u4e14\u8fd8\u53c2\u8003\u5929\u5c71\u5251\u6cd5\u4e0e \u8fbe\u6469\u5251\u6cd5\u7684\u53d8\u5316\uff0c\u52a0\u4ee5\u6539\u5584\u589e\u76ca\uff0c\u6bd4\u5979\u5e08\u5085\u5f53\u5e74\uff0c\u8fd8\u8981\u5389\u5bb3\u3002 \u3000\u3000\u4e24\u5e74\u7684\u65f6\u95f4\u4e00\u970e\u5373\u8fc7\uff0c\u4f46\u5916\u9762\u5374\u8d77\u4e86\u6781\u5927\u7684\u53d8\u5316\u3002\u4e00\u65e5\uff0c\u7518\u51e4\u6c60\u56de\u6765\uff0c\u559c\u5b5c\u5b5c\u7684\u544a\u8bc9\u5415 \u56db\u5a18\u9053\uff1a\u201c\u4f60\u4f3c\u524d\u6240\u6599\u4e4b\u4e8b\u5df2\u7ecf\u51fa\u73b0\uff0c\u4eca\u540e\u54b1\u4eec\u53ea\u987b\u5bf9\u4ed8\u96cd\u6b63\u5c31\u884c\u4e86\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5e74\u7fb9 \u5c27\u5df2\u88ab\u96cd\u6b63\u9664\u4e86\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8fd8\u672a\u88ab\u6740\uff0c\u4f46\u4e5f\u591f\u60e8\u7684\u4e86\uff0c\u4ed6\u4ece\u4e00\u7b49\u516c\u517c\u5ddd\u9655\u603b\u7763\u7adf\u7136\u4e00 \u8d2c\u5c31\u8d2c\u53bb\u770b\u5b88\u57ce\u95e8\u3002\u201d\u5415\u56db\u5a18\u867d\u7136\u6599\u5230\u96cd\u6b63\u5bb9\u4e0d\u5f97\u5e74\u7fb9\u5c27\uff0c\u4f46\u5374\u6599\u4e0d\u5230\u53d1\u4f5c\u5982\u6b64\u4e4b\u5feb\uff0c\u800c\u4e14 \u5e74\u7fb9\u5c27\u88ab\u8d2c\u53bb\u770b\u5b88\u57ce\u95e8\uff0c\u66f4\u662f\u4e0d\u80fd\u60f3\u50cf\u7684\u5947\u4e8b\uff01 \u3000\u3000\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u5bf9\u5e74\u7fb9\u5c27\u6765\u8bf4\uff0c\u8d2c\u4ed6\u53bb\u770b\u5b88\u57ce\u95e8\uff0c\u771f\u6bd4\u88ab\u6740\u8fd8\u96be\u53d7\u3002\u53ef\u662f\u4ed6\u73b0\u5728\u8fd8\u6709\u4ec0 \u4e48\u529e\u6cd5\uff0c\u4ed6\u7684\u5175\u6743\u5df2\u88ab\u5265\u593a\u4e86\u3002\u201d\u5f53\u4e0b\u7518\u51e4\u6c60\u4fbf\u4e3a\u5415\u56db\u5a18\u8bf4\u5e74\u7fb9\u5c27\u88ab\u8d2c\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u81ea\u4ece\u897f\u5f81\u9752\u6d77\u56de\u6765\u4e4b\u540e\uff0c\u53d7\u5c01\u4e3a\u201c\u4e00\u7b49\u516c\u201d\uff0c\u4ecd\u517c\u4efb\u5ddd\u9655\u603b\u7763\uff0c\u58f0\u5a01\u4e4b\u76db\uff0c\u4e00\u65f6 \u65e0\u4e24\u3002\u5e74\u7fb9\u5c27\u4e5f\u5fd2\u806a\u660e\uff0c\u81ea\u77e5\u529f\u9ad8\u9707\u4e3b\uff0c\u5175\u6743\u4e07\u4e07\u4e0d\u80fd\u653e\u624b\uff0c\u56e0\u6b64\u4e0d\u613f\u7559\u5728\u5317\u4eac\uff0c\u81ea\u90a3\u6b21\u7687 \u5e1d\u52b3\u519b\u4e4b\u540e\uff0c\u6ca1\u591a\u4e45\uff0c\u4ed6\u4fbf\u5e26\u5175\u56de\u9655\u5ddd\u3002\u5c82\u6599\u96cd\u6b63\u6bd4\u4ed6\u66f4\u806a\u660e\uff0c\u6697\u4e2d\u6276\u690d\u4ed6\u7684\u526f\u624b\u5cb3\u949f\u6dc7\uff0c \u7531\u5cb3\u949f\u6dc7\u7b3c\u7edc\u90e8\u4e0b\uff0c\u6e10\u6e10\u638c\u63e1\u4e86\u519b\u4e2d\u5b9e\u6743\uff0c\u4e0d\u4e45\u53c8\u85c9\u53e3\u897f\u5eb7\u6c11\u53d8\uff0c\u4e0b\u65e8\u53eb\u5cb3\u949f\u6dc7\u5e26\u5175\u5e73\u4e71\uff0c \u4e3a\u4e86\u6015\u5e74\u7fb9\u5c27\u4e0d\u6ee1\uff0c\u8fd8\u7279\u522b\u5bf9\u4ed6\u89e3\u91ca\uff0c\u8bf4\u662f\u201c\u5272\u9e21\u7109\u7528\u725b\u5200\uff0c\u7663\u75a5\u4e4b\u60a3\uff0c\u4e0d\u6562\u6709\u52b3\u5927\u5c06\u3002\u201d \u5e74\u7fb9\u5c27\u56e0\u5cb3\u949f\u6dc7\u4e00\u5411\u5bf9\u4ed6\u5949\u547d\u552f\u8c28\uff0c\u4e07\u4e07\u6599\u4e0d\u5230\u5cb3\u949f\u6dc7\u4f1a\u80cc\u53db\u4ed6\u3002\u800c\u4e14\u4ed6\u5728\u897f\u5b89\u4fee\u4e86\u5bab\u6bbf\u822c \u7684\u5e9c\u90b8\uff0c\u53c8\u6709\u7f8e\u8c8c\u5982\u82b1\u7684\u592b\u4eba\u76f8\u4f34\uff0c\u5723\u65e8\u65e2\u7136\u53eb\u5cb3\u949f\u6dc7\u53bb\uff0c\u4ed6\u4e5f\u4e50\u5f97\u5728\u897f\u5b89\u201c\u4eab\u798f\u201d\u3002 \u3000\u3000\u5cb3\u949f\u6dc7\u5e26\u4e86\u4e00\u90e8\u4efd\u7684\u5175\u529b\u8fdc\u8d74\u897f\u5eb7\uff0c\u8fde\u6253\u8d25\u4ed7\uff0c\u544a\u6025\u6587\u4e66\u96ea\u7247\u98de\u6765\uff0c\u96cd\u6b63\u4fbf\u628a\u5e74\u7fb9\u5c27\u7684 \u519b\u961f\u5206\u6279\u8c03\u53bb\u6551\u63f4\u3002\u524d\u7ebf\u9700\u8981\u589e\u63f4\uff0c\u5e74\u7fb9\u5c27\u5f53\u7136\u4e0d\u6562\u8fdd\u65e8\uff0c\u800c\u4e14\u6240\u6551\u7684\u53c8\u662f\u81ea\u5df1\u7684\u90e8\u4e0b\uff0c\u66f4 \u4e0d\u80fd\u4e0d\u6025\u6025\u53d1\u5175\uff0c\u8fd9\u6837\u7684\u9646\u7eed\u589e\u63f4\uff0c\u7adf\u7136\u628a\u5e74\u7fb9\u5c27\u7684\u5175\u529b\uff0c\u8c03\u53bb\u4e86\u5341\u4e4b\u516b\u4e5d\u3002\u6025\u5f97\u5e74\u7fb9\u5c27\u5728 \u7763\u5e9c\u91cc\u5929\u5929\u9a82\u5cb3\u949f\u6dc7\u8113\u5305\uff0c\u51e0\u4e4e\u60f3\u4e0a\u758f\u8350\uff0c\u4eb2\u5230\u524d\u7ebf\u7763\u5e08\u3002 \u3000\u3000\u8c01\u77e5\u8fd9\u6b63\u662f\u96cd\u6b63\u4e0e\u5cb3\u949f\u6dc7\u6240\u5b9a\u7684\u8ba1\u7b56\uff0c\u8fde\u6253\u8d25\u4ed7\uff0c\u5b8c\u5168\u662f\u6545\u610f\u9020\u6210\u7684\uff0c\u5230\u5e74\u7fb9\u5c27\u7684\u5175\u529b \u5341\u4e4b\u516b\u4e5d\u88ab\u8c03\u5230\u897f\u5eb7\u540e\uff0c\u7acb\u523b\u8f6c\u201c\u8d25\u201d\u4e3a\u80dc\uff0c\u800c\u5723\u65e8\u4e5f\u8981\u5cb3\u949f\u6dc7\u201c\u6682\u65f6\u201d\u7559\u9547\u897f\u5eb7\uff0c\u4e0d\u56de\u6765 \u4e86\u3002 \u3000\u3000\u53ef\u7b11\u6ee1\u671d\u6587\u6b66\uff0c\u90fd\u4e0d\u77e5\u9053\u96cd\u6b63\u7684\u771f\u610f\uff0c\u5728\u8d25\u8baf\u9891\u4f20\u4e4b\u9645\uff0c\u8fd8\u7eb7\u7eb7\u5f39\u52be\u5cb3\u949f\u6dc7\uff0c\u8bf4\u4ed6\u975e\u5927 \u5c06\u4e4b\u6750\uff0c\u8bf7\u96cd\u6b63\u8c03\u5e74\u7fb9\u5c27\u53bb\u3002\u96cd\u6b63\u4e5f\u5c61\u5c61\u4e0b\u65e8\u201c\u7533\u65a5\u201d\u5cb3\u949f\u6dc7\uff0c\u6545\u610f\u505a\u7ed9\u5e74\u7fb9\u5c27\u770b\u3002 \u3000\u3000\u6ee1\u671d\u6587\u6b66\uff0c\u90fd\u4e0d\u77e5\u9053\u5e74\u7fb9\u5c27\u5df2\u662f\u6697\u4e2d\u5931\u52bf\uff0c\u6ca1\u6709\u4eba\u60f3\u5230\u8981\u5f39\u52be\u4ed6\uff0c\u5176\u65f6\u6709\u4e2a\u5927\u81e3\u53eb\u7530\u6587 \u955c\uff0c\u5916\u653e\u505a\u6cb3\u4e1c\u603b\u7763\uff0c\u4ed6\u548c\u96cd\u6b63\u6240\u5ba0\u4fe1\u7684\u5927\u81e3\u9102\u5c14\u6cf0\uff0c\u674e\u654f\u8fbe\u7b49\u4eba\u4e43\u662f\u83ab\u9006\u4e4b\u4ea4\uff0c\u7530\u6587\u955c\u8d74 \u4efb\u65f6\uff0c\u674e\u654f\u8fbe\u8350\u4e00\u4f4d\u90ac\u5e08\u7237\u7ed9\u4ed6\uff0c\u66ff\u4ed6\u529e\u7406\u6587\u4e66\uff0c\u7530\u6587\u955c\u56e0\u4e3a\u90ac\u5e08\u7237\u662f\u674e\u654f\u8fbe\u8350\u7684\uff0c\u5bf9\u4ed6\u751a \u4e3a\u770b\u91cd\u3002\u8bf4\u4e5f\u5947\u602a\uff0c\u51e1\u662f\u90ac\u5e08\u7237\u7ecf\u624b\u7684\u594f\u758f\uff0c\u4ece\u6765\u4e0d\u4f1a\u88ab\u7687\u4e0a\u6279\u9a73\uff0c\u5076\u6709\u4e0d\u662f\u4ed6\u7ecf\u624b\u7684\u3002\u5c31 \u53d7\u6279\u9a73\uff0c\u56e0\u6b64\u7530\u6587\u955c\u5c31\u66f4\u4fe1\u4efb\u90ac\u5e08\u7237\u4e86\u3002 \u3000\u3000\u4e00\u65e5\uff0c\u90ac\u5e08\u7237\u5ffd\u7136\u95ee\u7530\u6587\u955c\u9053\uff1a\u201c\u660e\u516c\u613f\u505a\u4e00\u4e2a\u540d\u81e3\u5417\uff1f\u201d\u7530\u6587\u955c\u751a\u662f\u5947\u602a\uff0c\u7b54\u9053\uff1a \u201c\u8fd9\u8fd8\u7528\u8bf4\u5417\uff1f\u5f53\u7136\u613f\u505a\u540d\u81e3\uff01\u201d\u90ac\u5e08\u7237\u9053\uff1a\u201c\u660e\u516c\u65e2\u613f\u505a\u4e2a\u540d\u81e3\uff0c\u6211\u4e5f\u613f\u505a\u4e2a\u540d\u5e55\u3002\u201d \uff08\u5e55\u50da\uff09\u7530\u6587\u955c\u9053\uff1a\u201c\u4f60\u8981\u600e\u6837\u505a\u540d\u5e55\u5462\uff1f\u201d\u90a3\u5e08\u7237\u9053\uff1a\u201c\u8bf7\u4e3b\u516c\u8ba9\u6211\u505a\u4ef6\u4e8b\u60c5\uff0c\u83ab\u6765\u987e \u95ee\u3002\u201d\u7530\u6587\u955c\u9053\uff1a\u201c\u5148\u751f\u8981\u505a\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u90ac\u5e08\u7237\u9053\uff1a\u201c\u6211\u6253\u7b97\u66ff\u4e3b\u516c\u4e0a\u4e00\u672c\u594f\u7ae0\uff0c\u594f\u7ae0\u91cc \u9762\u6240\u8bf4\u7684\u4f30\uff0c\u5374\u4e00\u4e2a\u5b57\u4e5f\u4e0d\u8bb8\u4e3b\u516c\u77e5\u9053\u3002\u8fd9\u672c\u594f\u7ae0\u4e00\u4e0a\u3002\u4e3b\u516c\u4fbf\u53ef\u505a\u6210\u540d\u81e3\u4e86\uff01\u201d \u3000\u3000\u7530\u6587\u955c\u89c1\u4ed6\u8bf4\u5f97\u5982\u6b64\u80af\u5b9a\uff0c\u53c8\u60f3\u8d77\u4ed6\u6240\u62df\u7684\u594f\u758f\uff0c\u4ece\u6765\u672a\u53d7\u8fc7\u7687\u4e0a\u6279\u6821\uff0c\u4fbf\u5927\u8d77\u80c6\u5b50\uff0c \u8ba9\u4ed6\u4e00\u8bd5\uff0c\u90a3\u665a\uff0c\u90ac\u5e08\u7237\u623f\u4e2d\u7684\u706f\u706b\u4eae\u5230\u5929\u660e\uff0c\u7530\u6587\u955c\u4e5f\u4e00\u591c\u7761\u4e0d\u7740\u89c9\u3002\u7b2c\u4e8c\u65e5\u4e00\u65e9\u90ac\u5e08\u7237 \u628a\u5199\u597d\u7684\u594f\u7ae0\u5c01\u5728\u5927\u4fe1\u5c01\u91cc\uff0c\u7528\u706b\u6f06\u5c01\u4e86\u53e3\uff0c\u62ff\u6765\u53eb\u7530\u6587\u955c\u76d6\u4e0a\u6cb3\u4e1c\u603b\u7763\u7684\u5927\u5370\uff0c\u7530\u6587\u955c \u9053\uff1a\u201c\u594f\u7ae0\u6211\u53ef\u4ee5\u4e00\u4e2a\u5b57\u4e0d\u7740\uff0c\u4f46\u594f\u7684\u662f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5148\u751f\u53ef\u4ee5\u900f\u9732\u4e00\u4e8c\u5417\uff1f\u201d\u90ac\u5e08\u7237\u52c3\u7136\u4f5c \u8272\u9053\uff1a\u201c\u4e3b\u516c\u4e0d\u6562\u76f8\u4fe1\u6211\uff0c\u90a3\u5c31\u7f62\u4e86\uff0c\u6211\u7acb\u523b\u544a\u8f9e\uff01\u201d\u7530\u6587\u955c\u5fd9\u9053\uff1a\u201c\u5148\u751f\u4f11\u8981\u591a\u7591\uff0c\u65e2\u7136 \u4e0d\u80fd\u900f\u9732\uff0c\u6211\u76d6\u4e0a\u5927\u5370\u5c31\u662f\u3002\u201d\u76d6\u5370\u4e4b\u540e\uff0c\u7acb\u523b\u7528\u767e\u91cc\u5feb\u9a6c\u52a0\u7d27\uff0c\u62dc\u647a\u4e0a\u4eac\u3002 \u3000\u3000\u594f\u7ae0\u9001\u51fa\u4e4b\u540e\uff0c\u7530\u6587\u955c\u60a3\u5f97\u60a3\u5931\uff0c\u5c61\u6b21\u60f3\u95ee\u90ac\u5e08\u7237\uff0c\u5374\u53c8\u4e0d\u6562\uff0c\u5fc3\u4e2d\u9887\u4e3a\u540e\u6094\u62ff\u529f\u540d\u7984 \u4f4d\u6765\u4f5c\u8d4c\u6ce8\uff0c\u4f46\u594f\u7ae0\u5feb\u9a6c\u9001\u51fa\uff0c\u5df2\u662f\u65e0\u53ef\u8ffd\u56de\uff0c\u53ea\u597d\u6697\u4e2d\u547d\u536b\u58eb\u76d1\u89c6\u90ac\u5e08\u7237\uff0c\u9632\u5907\u4ed6\u9003\u8d70\uff0c \u5f85\u594f\u7ae0\u6709\u4e86\u7ed3\u679c\u4e4b\u540e\uff0c\u82e5\u8fd8\u65e0\u4e8b\uff0c\u90a3\u4fbf\u7f62\u4e86\uff0c\u82e5\u7136\u6709\u4e8b\uff0c\u90a3\u4fbf\u5148\u628a\u90ac\u5e08\u7237\u6740\u6389\u3002 \u3000\u3000\u90ac\u5e08\u7237\u5374\u662f\u58f0\u8272\u4e0d\u9732\uff0c\u4e00\u5982\u5e73\u5e38\u3002\u8fc7\u4e86\u4e03\u65e5\uff0c\u90b8\u6284\uff08\u5b98\u62a5\uff09\u4ece\u4eac\u4e2d\u5feb\u9a6c\u4f20\u6765\uff0c\u7530\u6587\u955c\u62c6 \u5f00\u4e00\u770b\uff0c\u5413\u5f97\u534a\u6b7b\uff0c\u770b\u5b8c\u4e4b\u540e\uff0c\u53c8\u559c\u51fa\u671b\u5916\uff0c\u51e0\u4e4e\u7591\u5fc3\u81ea\u5df1\u505a\u4e86\u4e00\u573a\u602a\u68a6\uff01 \u3000\u3000\u4f60\u9053\u90ac\u5e08\u7237\u5199\u7684\u90a3\u672c\u594f\u7ae0\u662f\u4ec0\u4e48\uff1f\u539f\u6765\u4ed6\u4e00\u672c\u594f\u7ae0\uff0c\u7adf\u7136\u53c2\u52be\u4e86\u4e24\u4e2a\u6743\u503e\u671d\u91ce\u3001\u58f0\u5a01\u8d6b \u8d6b\u7684\u4eba\u3002\u7b2c\u4e00\u4e2a\u52be\u7684\u662f\u5e74\u7fb9\u5c27\uff0c\u8bf4\u4ed6\u56fe\u8c0b\u4e0d\u8f68\uff0c\u8349\u83c5\u4eba\u547d\uff0c\u514b\u6263\u519b\u664c\uff0c\u64c5\u6740\u63d0\u7763\uff0c\u79cd\u79cd\u7f6a \u540d\uff0c\u4e0d\u80fd\u5217\u4e3e\uff0c\u7b2c\u4e8c\u4e2a\u52be\u7684\u662f\u56fd\u8205\u9686\u79d1\u591a\uff0c\u8bf4\u4ed6\u4e0e\u5e74\u7fb9\u5c27\u72fc\u72c8\u4e3a\u5978\uff0c\u8d2a\u8d43\u6789\u6cd5\uff0c\u79c1\u85cf\u7389\u789f\uff0c \u56fe\u8c0b\u4e0d\u8f68\uff0c\u8be5\u4e0e\u5e74\u7fb9\u5c27\u540c\u7f6a\uff01\u82e5\u7136\u662f\u7ed9\u7530\u6587\u955c\u5148\u77e5\u9053\u7684\u8bdd\uff0c\u4ed6\u7eb5\u6709\u5929\u5927\u7684\u80c6\u5b50\uff0c\u4e5f\u4e0d\u6562\u4e0a\u8fd9 \u672c\u594f\u7ae0\uff01 \u3000\u3000\u7687\u5e1d\u770b\u5b8c\u8fd9\u672c\u594f\u7ae0\uff0c\u6b63\u4e2d\u4e0b\u6000\uff0c\u7acb\u523b\u4e0b\u4ee4\uff0c\u524a\u53bb\u5e74\u7fb9\u5c27\u7684\u4e00\u7b49\u516c\u5c01\u53f7\uff0c\u629a\u8fdc\u5927\u5c06\u519b\u517c\u5ddd \u9655\u603b\u7763\u7684\u804c\u4f4d\u7531\u5cb3\u949f\u6dc7\u4ee3\u66ff\uff0c\u4f46\u7687\u5e1d\u4e5f\u8fd8\u987e\u5fcc\u5230\u5e74\u7fb9\u5c27\u5728\u9655\u897f\u8fd8\u6709\u5c11\u8bb8\u5175\u529b\uff0c\u4e0d\u6562\u5373\u884c\u8bdb \u622e\uff0c\u6240\u4ee5\u5723\u65e8\u53c8\u8bf4\u5ff5\u4ed6\u5c1a\u6709\u5fae\u529f\uff0c\u5bf9\u4e8e\u5175\u4e01\u4ea6\u5c1a\u80fd\u64cd\u7ec3\uff0c\u53eb\u4ed6\u56de\u676d\u5dde\u7ec3\u5175\u3002\u81f3\u4e8e\u9686\u79d1\u591a\uff0c\u5219 \u4ea4\u987a\u627f\u90e1\u738b\u9521\u4fdd\u5ba1\u95ee\uff0c\u5148\u524a\u53bb\u4e00\u5207\u5c01\u53f7\u5b98\u7235\uff0c\u6253\u4e0b\u5929\u7262\u3002\u81f3\u4e8e\u7530\u6587\u955c\uff0c\u5219\u4f20\u65e8\u5609\u5956\uff0c\u8bf4\u4ed6 \u201c\u8d64\u5fc3\u4e3a\u56fd\uff0c\u4e0d\u754f\u6743\u8d35\uff0c\u5927\u80c6\u6562\u8a00\uff0c\u7740\u4ee4\u5347\u4efb\u4e24\u5e7f\u603b\u7763\u3002\u201d\u90b8\u6284\u4e0a\u9762\u5199\u7684\uff0c\u5c31\u662f\u8fd9\u51e0\u6869\u4e8b\u60c5\u3002 \u3000\u3000\u7530\u6587\u955c\u6367\u7740\u8fd9\u5feb\u9a6c\u4f20\u6765\u7684\u90b8\u6284\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u597d\u534a\u5929\u8fd8\u662f\u624b\u98a4\u811a\u9707\uff0c\u4e0d\u80fd\u6062\u590d\u5e38\u6001\u3002\u90a3\u90ac \u5e08\u7237\u8fd9\u65f6\u624d\u7b11\u541f\u541f\u7684\u8d70\u4e86\u8fdb\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u606d\u8d3a\u4e3b\u516c\u5347\u5b98\uff0c\u4e3b\u516c\u505a\u540d\u81e3\u7684\u613f\u671b\u5df2\u8fbe\uff0c\u6211\u4e5f\u5f53\u544a \u8f9e\u4e86\u3002\u201d\u7530\u6587\u955c\u614c\u5fd9\u633d\u7559\uff0c\u90ac\u5e08\u7237\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5e78\u800c\u7687\u4e0a\u89c1\u4e86\u8fd9\u672c\u594f\u7ae0\uff0c\u4e0d\u52a0\u7f6a\u8d23\uff0c\u53cd\u800c\u5956 \u8d4f\uff0c\u5426\u5219\u6211\u4e5f\u4e0d\u80fd\u751f\u51fa\u4f60\u7684\u7763\u629a\u8859\u95e8\u4e86\u3002\u201d\u7530\u6587\u955c\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u6599\u77e5\u90ac\u5e08\u7237\u5df2\u77e5\u9053\u81ea\u5df1\u524d \u4e24\u65e5\u7684\u5e03\u7f6e\uff0c\u53c8\u4e0d\u4fbf\u544a\u7f6a\uff0c\u751a\u662f\u5c34\u5c2c\u3002\u90ac\u5e08\u7237\u53c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u602a\u4e0d\u5f97\u4e3b\u516c\uff0c\u82e5\u975e\u662f\u6211\u64c5\u7528\u4e3b \u516c\u7684\u540d\u4e49\uff0c\u5929\u4e0b\u4e5f\u65e0\u7b2c\u4e8c\u4e2a\u5b98\u5458\uff0c\u6562\u53c2\u52be\u5e74\u7fb9\u5c27\u548c\u9686\u79d1\u591a\uff01\u201d \u3000\u3000\u7530\u6587\u955c\u8fd9\u65f6\u77e5\u9053\u90ac\u5e08\u7237\u5fc5\u662f\u975e\u5e38\u4e4b\u4eba\uff0c\u4e00\u518d\u9053\u8c22\uff0c\u8bd5\u63a2\u95ee\u9053\uff1a\u201c\u5148\u751f\u6709\u8fd9\u6837\u5927\u7684\u9b44\u529b\uff0c \u7530\u67d0\u4f69\u670d\u4e4b\u81f3\u3002\u4e0d\u77e5\u5148\u751f\u4ee5\u524d\u5728\u4ec0\u4e48\u5730\u65b9\u529e\u4e8b\uff0c\u80fd\u591f\u8fd9\u6837\u5584\u4f53\u4e0a\u610f\uff0c\u80fd\u4e3a\u4eba\u4e4b\u6240\u4e0d\u6562\u4e3a\uff1f\u201d \u90ac\u5e08\u7237\u5927\u7b11\u9053\uff1a\u201c\u4f60\u5df2\u8fbe\u5347\u5b98\u4e4b\u613f\uff0c\u4f55\u5fc5\u95ee\u6211\u7684\u6765\u5386\uff1f\u77e5\u9053\u6211\u7684\u6765\u5386\uff0c\u5bf9\u4f60\u6beb\u65e0\u597d\u5904\u3002\u54b1\u4eec \u540e\u4f1a\u6709\u671f\uff0c\u6211\u5148\u544a\u8f9e\u4e86\u3002\u201d\u7530\u6587\u955c\u8001\u4e8e\u5ba6\u9014\uff0c\u8fd9\u65f6\u5fc3\u4e2d\u5df2\u7136\u96ea\u4eae\uff0c\u77e5\u9053\u90ac\u5e08\u7237\u5fc5\u7136\u662f\u7687\u4e0a\u7684 \u4eb2\u4fe1\uff0c\u8fde\u5fd9\u53d6\u51fa\u4e09\u767e\u4e24\u91d1\u5b50\uff0c\u9001\u4ed6\u5f53\u4f5c\uff0c\u201c\u7b14\u916c\u201d\u3002\u4e0d\u6562\u518d\u95ee\uff0c\u8ba9\u4ed6\u8f9e\u53bb\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u88ab\u524a\u53bb\u5ddd\u9655\u603b\u7763\u4e0e\u629a\u8fdc\u5927\u5c06\u519b\u4e4b\u804c\uff0c\u5cb3\u949f\u6dc7\u7acb\u523b\u4ece\u897f\u5eb7\u8d76\u56de\u897f\u5b89\uff0c\u63a5\u6536\u5370\u4fe1\uff0c\u5cb3 \u949f\u6dc7\u4f5c\u51fa\u4e00\u526f\u540c\u60c5\u7684\u6837\u5b50\uff0c\u4e00\u9762\u7528\u597d\u8bdd\u5b89\u6170\uff0c\u613f\u4e3a\u4ed6\u4e0a\u594f\uff0c\u4ee3\u6c42\u4fdd\u5168\uff0c\u5e76\u62d4\u4e00\u767e\u540d\u4eb2\u5175\uff0c\u9001 \u4ed6\u5357\u5f52\u3002\u5e74\u7fb9\u5c27\u6291\u7740\u6012\u706b\uff0c\u5927\u7b11\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u5584\u81ea\u4e3a\u4e4b\uff0c\u4e07\u52ff\u8e48\u6211\u7684\u8986\u8f99\uff01\u6211\u4e00\u751f\u620e \u9a6c\uff0c\u4e3a\u7687\u4e0a\u5357\u5f81\u5317\u4f10\uff0c\u5e78\u514d\u9a6c\u9769\u88f9\u5c38\uff0c\u81f3\u4eca\u5c1a\u6709\u4f55\u8db3\u60e7\uff1f\u6211\u6b64\u6b21\u56de\u53bb\uff0c\u82e5\u6709\u5371\u9669\uff0c\u4e5f\u4e0d\u662f\u4f60 \u4fdd\u62a4\u5f97\u6765\uff0c\u4f60\u7684\u76db\u60c5\uff0c\u6211\u5fc3\u9886\u4e86\uff01\u201d\u53cc\u76ee\u70af\u70af\uff0c\u5c1a\u6709\u6614\u65e5\u5a01\u4e25\uff0c\u5cb3\u949f\u6dc7\u4e0d\u6562\u548c\u4ed6\u518d\u8bf4\uff0c\u614c\u5fd9 \u9000\u51fa\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u6cbb\u519b\u591a\u5e74\uff0c\u81ea\u6709\u4e00\u73ed\u6700\u4eb2\u4fe1\u7684\u5c06\u9886\uff0c\u8fd9\u4e9b\u4eba\u548c\u5e74\u7fb9\u5c27\u7978\u798f\u76f8\u4f9d\uff0c\u800c\u4e14\u62b1\u7740\u201c\u70e7\u51b7 \u7076\u201d\u7684\u5fc3\u60c5\uff0c\u5e0c\u671b\u5e74\u7fb9\u5c27\u4ed6\u65e5\u80fd\u591f\u4e1c\u5c71\u518d\u8d77\uff0c\u4fbf\u7eb7\u7eb7\u8f9e\u804c\uff0c\u968f\u4ed6\u5357\u4e0b\u3002\u5cb3\u949f\u6dc7\u4e5f\u4e0d\u633d\u7559\uff0c\u4e00 \u4e00\u6279\u51c6\u3002\u5e74\u7fb9\u5c27\u5e26\u4e86\u51e0\u5341\u540d\u65e7\u90e8\u4e0b\u548c\u4e8c\u4e09\u767e\u540d\u8001\u5175\uff0c\u524d\u5f80\u676d\u5dde\u3002\u4e0d\u65e5\u5230\u4e86\u957f\u6c5f\u5317\u5cb8\u7684\u4eea\u5fae\uff0c \u4eea\u5fae\u6709\u6c34\u65f1\u4e24\u9014\uff0c\u4ece\u6c34\u9053\u5357\u4e0b\uff0c\u53ef\u8fbe\u676d\u5dde\uff0c\u4ece\u65f1\u9053\u5317\u4e0a\uff0c\u53ef\u8fbe\u5317\u4eac\u3002\u5e74\u7fb9\u5c27\u5fc3\u60f3\u81ea\u5df1\u66fe\u4e3a\u96cd \u6b63\u51fa\u8fc7\u5927\u529b\uff0c\u5982\u4eca\u5df2\u88ab\u524a\u4e86\u5175\u6743\uff0c\u7687\u4e0a\u5f53\u53ef\u5b89\u5fc3\uff0c\u82e5\u80fd\u9762\u89c1\u6c42\u60c5\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u5f97\u4efb\u6e05\u8d35\u4e4b\u804c\uff0c \u4ee5\u4fdd\u5929\u5e74\uff0c\u4fbf\u4e0d\u5373\u5230\u676d\u5dde\u5c31\u4efb\u7ec3\u5175\u4e4b\u804c\uff0c\u5374\u4e0a\u594f\u7ae0\u8981\u6c42\u53ec\u89c1\uff0c\u594f\u7ae0\u91cc\u6709\u4e24\u53e5\u9053\uff1a\u201c\u4eea\u5fae\u6c34\u9646 \u5206\u7a0b\uff0c\u81e3\u5728\u6b64\u9759\u5019\u7eb6\u97f3\u3002\u201d\u8fd9\u4e5f\u4e0d\u8fc7\u60f3\u7687\u5e1d\u56de\u5fc3\u8f6c\u610f\uff0c\u51c6\u4ed6\u8fdb\u4eac\u9762\u9648\u4e4b\u610f\u3002\u5c82\u6599\u96cd\u6b63\u53cd\u8bf4\u4ed6 \u5b58\u5fc3\u53cd\u53db\uff0c\u8981\u5e26\u5175\u8fdb\u4eac\u903c\u5bab\uff0c\u4e00\u9762\u628a\u5e74\u7fb9\u5c27\u7684\u594f\u7ae0\u4ea4\u540f\u90e8\u5ba1\u5904\uff0c\u4e00\u9762\u4eb2\u81ea\u4e0b\u8c15\u516d\u90e8\u5927\u81e3\u9053\uff1a \u3000\u3000\u201c\u6715\u5fa1\u6781\u4e4b\u521d\uff0c\u9686\u79d1\u591a\u5e74\u7fb9\u5c27\u7686\u5bc4\u4ee5\u5fc3\u8182\uff0c\u6beb\u65e0\u731c\u9632\uff0c\u6240\u4ee5\u4f5c\u5176\u516c\u5fe0\uff0c\u671f\u5176\u62a5\u6548\u3002\u5b70\u77e5 \u6715\u89c6\u5982\u4e00\u5fb7\uff0c\u4f0a\u7adf\u6709\u4e8c\u5fc3\uff1b\u8054\u4e88\u4ee5\u5ba0\u8363\uff0c\u4f0a\u5e78\u4e3a\u63d0\u7ed3\u3002\u62db\u6743\u7eb3\u8d3f\uff0c\u64c5\u4f5c\u5a01\u798f\uff0c\u6562\u4e8e\u6b3a\u7f54\uff0c\u5fcd \u4e8e\u6d61\u8d1f\uff0c\u5f7c\u65e2\u89c6\u5178\u5baa\u4e3a\u5e76\u9ae6\uff0c\u8054\u5c82\u80fd\u59d1\u606f\u517b\u5978\u8036\uff1f\u81f3\u5176\u95e8\u4e0b\u8d8b\u8d74\u5954\u8d70\u4e4b\u4eba\uff0c\u6216\u7531\u5e0c\u5176\u8350\u63f4\uff0c \u754f\u5176\u52a0\u5bb3\uff0c\u6025\u5b9c\u6539\u6563\u515a\u4e0e\uff0c\u9769\u9762\u6d17\u5fc3\u3002\u82e5\u4ecd\u65e7\u60c5\uff0c\u60df\u52a1\u9690\u533f\u5de7\u8bc8\uff0c\u4e00\u7ecf\u53d1\u89c9\uff0c\u5b9a\u6cbb\u515a\u9006\u4e4b \u7f6a\u3002\u201d \u3000\u3000\u4e00\u7fa4\u5927\u81e3\uff0c\u89c1\u4e86\u8fd9\u9053\u8c15\u65e8\uff0c\u77e5\u9053\u7687\u5e1d\u7edd\u5bf9\u4e0d\u4f1a\u653e\u8fc7\u5e74\u7fb9\u5c27\u4e86\uff0c\u4fbf\u4f60\u4e5f\u4e00\u672c\uff0c\u6211\u4e5f\u4e00\u672c\uff0c \u4f17\u53e3\u540c\u58f0\uff0c\u8bf4\u5e74\u7fb9\u5c27\u7f6a\u8be5\u4e07\u6b7b\uff0c\u96cd\u6b63\u4e5f\u5999\uff0c\u770b\u4e86\u8bb8\u591a\u594f\u672c\u4e4b\u540e\uff0c\u5f52\u7eb3\u8d77\u6765\uff0c\u8bf4\u6839\u636e\u594f\u7ae0\uff0c\u5e74 \u7fb9\u5c27\u6709\u5341\u516b\u6761\u5927\u7f6a\uff0c\u6715\u4eca\u4ee5\u5bbd\u5927\u4e3a\u6000\uff0c\u6bcf\u6761\u7f6a\u53ea\u964d\u4e00\u7ea7\uff0c\u4e8e\u662f\u4fbf\u8fde\u964d\u5e74\u7fb9\u5c27\u5341\u516b\u7ea7\uff0c\u628a\u4e00\u4e2a \u5927\u5c06\u519b\uff0c\u8d2c\u5230\u676d\u5dde\u53bb\u770b\u5b88\u57ce\u95e8\uff01 \u3000\u3000\u81f3\u4e8e\u9686\u79d1\u591a\uff0c\u5219\u56e0\u6709\u4ed6\u7684\u59b9\u5b50\uff08\u96cd\u6b63\u5eb6\u6bcd\uff0c\u88ab\u5c01\u4e3a\u592a\u5983\u3002\uff09\u6c42\u7687\u4e0a\u9976\u547d\uff0c\u96cd\u6b63\u53ea\u662f\u6068\u4ed6 \u4ee5\u524d\u8d8b\u5949\u5e74\u7fb9\u5c27\uff0c\u53ca\u5fcc\u4ed6\u77e5\u9053\u81ea\u5df1\u7be1\u4f4d\u4e4b\u4e8b\uff0c\u5374\u6599\u4ed6\u4e0d\u80fd\u4f5c\u53cd\uff0c\u4fbf\u5224\u4ed6\u6c38\u8fdc\u76d1\u7981\uff0c\u59bb\u5b50\u5bb6 \u4ea7\uff0c\u5219\u514d\u4e8e\u6284\u6ca1\u3002\u6bd4\u8d77\u5e74\u7fb9\u5c27\u6765\uff0c\u7b97\u4e0d\u5e78\u4e2d\u4e4b\u5e78\u4e86\u3002 \u3000\u3000\u5415\u56db\u5a18\u542c\u7518\u51e4\u6c60\u8bf4\u4e86\u5e74\u7fb9\u5c27\u88ab\u8d2c\u7684\u7ecf\u8fc7\u540e\uff0c\u6c89\u601d\u6709\u9877\uff0c\u8bf4\u9053\uff1a\u201c\u514d\u6b7b\u72d7\u70f9\uff0c\u5e74\u7fb9\u5c27\u6d3b\u8be5 \u6709\u4eca\u65e5\u4e4b\u62a5\uff0c\u6211\u4eec\u4e0d\u5fc5\u53bb\u7406\u4ed6\u4e86\u3002\u4e03\u54e5\uff0c\u6211\u7ec3\u4e86\u4e24\u5e74\u5251\u6cd5\uff0c\u53c8\u609f\u4e86\u8bb8\u591a\u5999\u7406\uff0c\u8fd9\u8d9f\uff0c\u4f60\u8be5\u4e0d \u4f1a\u62e6\u963b\u6211\u5165\u4eac\u4e86\u5427\u3002\u201d\u7518\u51e4\u6c60\u77e5\u5979\u7528\u610f\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u5728\u5c71\u4e0a\u95f7\u4e86\u4e24\u5e74\uff0c\u4e5f\u8be5\u4e0b\u53bb\u8d70\u8d70\u4e86\uff0c\u4e0d \u8fc7\uff0c\u5165\u4eac\u5927\u7ea6\u8fd8\u8981\u518d\u7b49\u4e9b\u65f6\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u65e5\u5415\u56db\u5a18\u548c\u7518\u51e4\u6c60\u6c88\u5728\u5bbd\u8054\u8882\u4e0b\u5c71\u3002\uff08\u6c88\u5728\u5bbd\u6b64\u65f6\u5185\u529f\u5df2\u9887\u4e3a\u4e86\u5f97\uff0c\u53c8\u4ece\u5415\u56db\u5a18\u4e60 \u4e86\u4e00\u4e9b\u6b66\u6280\uff0c\u5df2\u5927\u975e\u6614\u65e5\u53ef\u6bd4\u4e86\uff09\u8fd9\u91cc\u6682\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u4e14\u8bf4\u5e74\u7fb9\u5c27\u88ab\u8d2c\u5230\u676d\u5dde\u5b88\u57ce\u95e8\uff0c\u65e0\u5de7\u4e0d\u5de7\uff0c\u5f53\u676d\u5dde\u5c06\u519b\u7684\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u4ece\u524d\u5728\u5e74\u7fb9 \u5c27\u624b\u4e0b\uff0c\u5f53\u8fc7\u4e2d\u519b\u526f\u5c06\uff0c\u4e3a\u4e86\u529d\u8c0f\u5e74\u7fb9\u5c27\u6740\u63d0\u7763\u5bcc\u5c71\u4e4b\u4e8b\uff0c\u51e0\u4e4e\u4e5f\u88ab\u6740\u6b7b\uff0c\u540e\u6765\u88ab\u7f5a\u5439\u89d2\u5b88 \u591c\uff0c\u5728\u8425\u4e2d\u5f53\u66f4\u592b\u7684\u9646\u864e\u81e3\u3002\u96cd\u6b63\u628a\u5e74\u7fb9\u5c27\u8d2c\u5230\u676d\u5dde\u770b\u5b88\u57ce\u95e8\uff0c\u5b9e\u662f\u6709\u610f\u4ee4\u9646\u864e\u81e3\u5411\u4ed6\u4e3a\u96be\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u77e5\u9053\u9646\u864e\u81e3\u9547\u5b88\u676d\u5dde\uff0c\u5374\u4e5f\u4e0d\u653e\u5728\u5fc3\u5185\u3002\u5230\u7b2c\u4e09\u65e5\uff0c\u5e74\u7fb9\u5c27\u5728\u57ce\u95e8\u4e0b\u76d8\u7740\u817f\u513f\uff0c \u81ea\u7531\u81ea\u5728\u7684\u6652\u592a\u9633\uff0c\u57ce\u95e8\u5185\u5916\uff0c\u9759\u5bc2\u5bc2\u7684\u65e0\u4eba\u51fa\u5165\u3002\u539f\u6765\u676d\u5dde\u4eba\u754f\u5e74\u7fb9\u5c27\u6740\u6c14\uff0c\u77e5\u4ed6\u770b\u5b88\u5317 \u95e8\uff0c\u4e0d\u7ea6\u800c\u540c\uff0c\u90fd\u4e0d\u4ece\u5317\u95e8\u51fa\u5165\u3002\u8fd9\u65f6\u8ddf\u968f\u5e74\u7fb9\u5c27\u7684\u65e7\u90e8\uff0c\u90fd\u5df2\u661f\u6563\uff0c\u53ea\u6709\u4e00\u4e2a\u8001\u5175\uff0c\u8fd8\u8ddf \u5728\u4ed6\u7684\u8eab\u8fb9\u3002\u8fd9\u8001\u5175\u89c1\u4e86\u4e09\u65e5\u6765\u5982\u6b64\u60c5\u5f62\uff0c\u5bf9\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u5c06\u519b\u4eca\u65e5\u867d\u7136\u53d7\u8fb1\uff0c\u5374\u559c\u5a01\u98ce\u5c1a \u5728\uff0c\u5b98\u6c11\u4eba\u7b49\uff0c\u90fd\u4e0d\u6562\u4fae\u6162\u5c06\u519b\u3002\u201d\u5e74\u7fb9\u5c27\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u552f\u5176\u5982\u6b64\uff0c\u96cd\u6b63\u5c31\u66f4\u4e0d\u4f1a\u653e\u8fc7\u6211 \u4e86\u3002\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u542c\u5f97\u9e23\u9523\u5f00\u9053\u4e4b\u58f0\uff0c\u5e74\u7fb9\u5c27\u7b11\u9053\uff1a\u201c\u8981\u4fae\u8fb1\u6211\u7684\u4eba\u6765\u4e86\uff01\u201d\u53eb\u8001\u5175\u8eb2\u8fc7\u4e00 \u8fb9\uff0c\u53ea\u89c1\u9646\u864e\u81e3\u9a91\u7740\u9ad8\u5934\u5927\u9a6c\uff0c\u536b\u5352\u90e8\u4ece\uff0c\u524d\u547c\u540e\u62e5\u7684\u8d70\u51fa\u57ce\u6765\u3002\u5e74\u7fb9\u5c27\u6de1\u6de1\u4e00\u7b11\uff0c\u4ecd\u7136\u76d8 \u817f\u5750\u7740\uff0c\u4f38\u4e86\u4f38\u61d2\u8170\uff0c\u5411\u7740\u9633\u5149\u3002 \u3000\u3000\u9646\u864e\u81e3\u89c1\u5e74\u7fb9\u5c27\u5982\u6b64\u5927\u6a21\u5927\u6837\uff0c\u52c3\u7136\u5927\u6012\uff0c\u6709\u5fc3\u628a\u5e74\u7fb9\u5c27\u7f9e\u8fb1\uff0c\u4fbf\u8d70\u5230\u4ed6\u7684\u8ddf\u524d\uff0c\u51b7\u51b7 \u7b11\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\uff0c\u4f60\u8fd8\u8ba4\u5f97\u4ffa\u5417\uff1f\u201d\u5e74\u7fb9\u5c27\u659c\u77a7\u4e00\u773c\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\uff0c\u505a\u676d\u5dde\u5c06\u519b\u6bd4\u505a\u4ffa \u7684\u66f4\u592b\uff0c\u5927\u7ea6\u8981\u597d\u5f97\u591a\u5427\uff1f\u602a\u4e0d\u5f97\u4f60\u5982\u6b64\u5f97\u610f\u4e86\uff01\u201d\u9646\u864e\u81e3\u88ab\u4ed6\u6311\u8d77\u65e7\u6068\uff0c\u7981\u4e0d\u4f4f\u9a82\u9053\uff1a \u201c\u5e74\u7fb9\u5c27\uff0c\u4f60\u65e2\u8ba4\u5f97\u4ffa\uff0c\u4e3a\u4f55\u4e0d\u7ad9\u8d77\u6765\u8fce\u63a5\uff01\u201d\u5e74\u7fb9\u5c27\u542c\u4e86\uff0c\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9646\u864e \u81e3\uff0c\u4f60\u8981\u54b1\u5bb6\u7ad9\u8d77\u6765\u5417\uff1f\u6211\u7ad9\u8d77\u6765\u4e0d\u96be\uff0c\u4f46\u6211\u7ad9\u8d77\u6765\uff0c\u4f60\u5374\u8981\u8dea\u4e0b\u4e86\uff01\u201d\u9646\u864e\u81e3\u54c8\u54c8\u5927\u7b11 \u9053\uff1a\u201c\u6211\u5802\u5802\u7684\u676d\u5dde\u5c06\u519b\uff0c\u96be\u9053\u8fd8\u8981\u8dea\u4f60\u8fd9\u4e2a\u770b\u5b88\u57ce\u95e8\u7684\u5b98\u513f\u4e0d\u6210\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u8dea\u8fc7 \u6211\u4e5f\u4e0d\u77e5\u591a\u5c11\u6b21\u4e86\uff0c\u73b0\u5728\u6211\u867d\u7136\u4e0d\u80fd\u53eb\u4f60\u518d\u8dea\u6211\uff0c\u4f46\u4f60\u89c1\u4e86\u7687\u4e0a\u6216\u8005\u4ee3\u8868\u7687\u4e0a\u7684\u4e1c\u897f\uff0c\u603b\u8be5 \u8dea\u4e0b\u5427\uff1f\u201d\u9646\u864e\u81e3\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff0c\u53ef\u662f\u4f60\u53c8\u4e0d\u662f\u94a6\u5dee\u5927\u81e3\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u4ee5\u4ee3\u8868\u7687\u4e0a\uff1f\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u4e0d\u614c\u4e0d\u5fd9\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u628a\u53f7\u8863\u89e3\u5f00\uff0c\u53ea\u89c1\u91cc\u9762\u6240\u7a7f\u7684\u5927\u8902\uff0c\u7ee3\u6709\u4e24\u6761\u91d1\u9f99\uff0c\u9646\u864e \u81e3\u6014\u4e86\u4e00\u6014\uff0c\u53ea\u89c1\u5e74\u7fb9\u5c27\u53c8\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u4e2a\u523b\u6709\u4e94\u722a\u91d1\u9f99\u7684\u201c\u4e07\u5c81\u724c\u201d\u6765\uff0c\u5c31\u6446\u5728\u4ed6\u6240\u5750\u7684 \u5c0f\u51f3\u5b50\u4e0a\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u9646\u864e\u5de8\uff0c\u8dea\uff01\u201d\u9646\u864e\u81e3\u8138\u8272\u53d1\u9752\uff0c\u5374\u4e0d\u5f97\u4e0d\u5411\u7740\u201c\u4e07\u5c81\u724c\u201d\u8dea\u4e0b\uff0c \u606d\u606d\u656c\u656c\u7684\u884c\u4e86\u4e09\u8dea\u4e5d\u53e9\u4e4b\u793c\u3002 \u3000\u3000\u539f\u6765\u8fd9\u201c\u76d8\u9f99\u8902\u201d\u548c\u201c\u4e07\u5c81\u724c\u201d\uff0c\u90fd\u662f\u5e74\u7fb9\u5c27\u6614\u65e5\u897f\u5f81\u4e4b\u65f6\uff0c\u96cd\u6b63\u8d50\u4e0e\u4ed6\u7684\u3002\u201c\u76d8\u9f99 \u8902\u201d\u662f\u6709\u6781\u5927\u529f\u52cb\u4e4b\u4eba\u624d\u914d\u7a7f\u7740\uff0c\u4f46\u8fd9\u4e5f\u8fd8\u7f62\u4e86\u3002\u90a3\u201c\u4e07\u5c81\u724c\u201d\u5374\u662f\u4ee3\u8868\u7687\u4e0a\u7684\u4e1c\u897f\uff0c\u89c1\u6b64 \u724c\u8005\u6709\u5982\u89c1\u7687\u4e0a\u4eb2\u4e34\u3002\u4ee5\u524d\u5e74\u7fb9\u5c27\u897f\u5f81\u4e4b\u65f6\uff0c\u96cd\u6b63\u4e3a\u4e86\u8981\u7ed3\u7eb3\u4ed6\uff0c\u6240\u4ee5\u8d50\u4ed6\u6b64\u724c\uff0c\u597d\u8ba9\u4ed6\u80fd \u53f7\u4ee4\u5404\u7701\u7763\u629a\u5927\u5458\uff0c\u4e0d\u5fc5\u8bf7\u793a\uff0c\u5728\u5c01\u5efa\u7687\u671d\u4e2d\uff0c\u8fd9\u662f\u6781\u7f55\u89c1\u7684\u201c\u6b8a\u8363\u201d\u3002\u4e0d\u8fc7\u5e74\u7fb9\u5c27\u4ee5\u524d\u58f0 \u5a01\u8d6b\u8d6b\uff0c\u5404\u7701\u7763\u629a\u867d\u7136\u5728\u5b98\u9636\u54c1\u7ea7\u4e0a\u6709\u4e0e\u4ed6\u5e73\u884c\u7684\uff0c\u4f46\u5374\u65e0\u4e00\u4eba\u6562\u8fdd\u80cc\u4ed6\u7684\u610f\u601d\uff0c\u4ed6\u6240\u5230\u4e4b \u5904\uff0c\u7763\u629a\u5927\u5458\uff0c\u90fd\u6765\u8bf7\u5b89\u5949\u627f\uff0c\u6240\u4ee5\u4ed6\u867d\u6709\u6b64\u724c\uff0c\u5374\u4ece\u672a\u7528\u8fc7\u3002\u96cd\u6b63\u6b64\u6b21\u4e0d\u8bb8\u5e74\u7fb9\u5c27\u5165\u4eac\u8fdb \u89c1\uff0c\u4fbf\u8fde\u8d2c\u4ed6\u4e00\u5341\u516b\u7ea7\uff0c\u4ee5\u524d\u6240\u8d4f\u8d50\u4ed6\u7684\u4e1c\u897f\uff0c\u5305\u62ec\u201c\u4e07\u5c81\u724c\u201d\u5728\u5185\uff0c\u5374\u672a\u6536\u7f34\u56de\u6765\u3002\u5e74\u7fb9 \u5c27\u6b63\u597d\u62ff\u5b83\u6765\u6d3e\u7528\u573a\uff0c\u53cd\u800c\u5927\u5927\u7684\u7f9e\u8fb1\u4e86\u9646\u864e\u81e3\u4e00\u987f\u3002\u9646\u864e\u81e3\u8854\u6068\u56de\u8859\uff0c\u8fde\u591c\u4fee\u8868\u4e0a\u7adf\uff0c\u53c2 \u52be\u5e74\u7fb9\u5c27\u6b3a\u7f54\u50ed\u8d8a\uff0c\u5927\u9006\u4e0d\u9053\uff0c\u8fd9\u4e14\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u5f53\u9646\u864e\u81e3\u6446\u9a7e\u884c\u5230\u5317\u95e8\u4e4b\u65f6\uff0c\u57ce\u5185\u5e02\u6c11\uff0c\u6599\u77e5\u5fc5\u6709\u4e00\u573a\u597d\u620f\uff0c\u867d\u7136\u4e0d\u6562\u884c\u8fd1\uff0c\u5374\u662f\u8fdc\u8fdc \u7684\u9a7b\u8db3\u89c2\u671b\uff0c\u5f85\u9646\u864e\u81e3\u88ab\u7f9e\u8fb1\u4e4b\u540e\uff0c\u6012\u6c14\u51b2\u51b2\u7684\u6446\u9a7e\u56de\u8859\uff0c\u4ed6\u4eec\u53c8\u4e00\u54c4\u800c\u6563\u3002\u5e74\u7fb9\u5c27\u659c\u773c\u4e00 \u770b\uff0c\u6de1\u6de1\u4e00\u7b11\uff0c\u5bf9\u5916\u8fb9\u7684\u55a7\u95f9\uff0c\u4f3c\u4e4e\u6beb\u4e0d\u5173\u5fc3\uff0c\u76ee\u5149\u6240\u5230\uff0c\u5ffd\u89c1\u4e00\u5999\u9f84\u5c11\u5973\u7684\u80cc\u5f71\uff0c\u5728\u4eba\u4e1b \u4e2d\u5189\u5189\u800c\u6ca1\u3002\u8fd9\u80cc\u5f71\u9177\u4f3c\u51af\u7433\uff0c\u5e74\u7fb9\u5c27\u4e0d\u89c9\u5446\u4e86\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u672c\u6765\u6b22\u559c\u51af\u7433\uff0c\u540e\u6765\u56e0\u597d\u4e8b\u96be\u8c10\uff0c\u624d\u5a36\u4e86\u8499\u53e4\u85e9\u738b\u7684\u5973\u513f\u4f73\u7279\u683c\u683c\uff0c\u4f73\u7279\u683c\u683c\u867d \u7136\u7f8e\u8c8c\u5982\u82b1\uff0c\u4f46\u5230\u5e95\u4e0d\u53ca\u51af\u7433\u7684\u6587\u6b66\u53cc\u5168\uff0c\u806a\u660e\u4f36\u4fd0\uff0c\u80fd\u9017\u4eba\u559c\u7231\u3002\u8fd9\u65f6\uff0c\u5e74\u7fb9\u5c27\u76ee\u9001\u8fd9\u5c11 \u5973\u7684\u80cc\u5f71\u5189\u5189\u800c\u6ca1\uff0c\u4e0d\u89c9\u5fc6\u8d77\u4e86\u5c0f\u65f6\u5019\u4e0e\u51af\u7433\u5728\u5927\u82b1\u56ed\u4e2d\u563b\u73a9\u7684\u60c5\u666f\u3002\u7fd8\u9996\u4e91\u5929\uff0c\u6545\u56ed\u671b \u65ad\uff0c\u5fcd\u4e0d\u4f4f\u5fae\u53f9\u4e00\u58f0\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5982\u679c\u5f53\u5e74\u6211\u575a\u4e0d\u8ba9\u4e0e\u5f53\u4eca\u7687\u4e0a\uff0c\u867d\u7136\u6ca1\u6709\u4ee5\u540e\u7684\u529f\u540d\uff0c \u4f46\u8fd9\u5999\u4eba\u513f\u5374\u662f\u6211\u7684\u4e86\uff0c\u4e0e\u5979\u6d6a\u8ff9\u6c5f\u6e56\uff0c\u5c82\u4e0d\u80dc\u4f3c\u516c\u4faf\u76f8\u5c06\uff1f\u201d\u4f46\u8fd9\u5ff5\u5934\u5728\u5fc3\u4e2d\u4e00\u95ea\u5373\u8fc7\uff0c \u968f\u5373\u81ea\u5df1\u7b11\u9053\uff1a\u201c\u5927\u4e08\u592b\u82e5\u4e0d\u80fd\u7559\u82b3\u767e\u4e16\uff0c\u4ea6\u5f53\u9057\u81ed\u4e07\u5e74\u3002\u6211\u80fd\u6709\u4eca\u65e5\uff0c\u4e0d\u8bba\u6210\u8d25\uff0c\u53f2\u518c\u5b9a \u5df2\u7559\u540d\uff0c\u53c8\u5c1a\u6709\u4f55\u6068\uff01\u201d\u6325\u8896\u4e00\u7b11\uff0c\u53c8\u5750\u5728\u90a3\u7834\u65e7\u7684\u5c0f\u51f3\u5b50\u4e0a\u6652\u592a\u9633\u4e86\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5fc3\u6b32\u9759\u6b62\u5374\u4ecd\u4e0d\u80fd\u9759\u6b62\uff0c\u5e74\u66fc\u5c27\u867d\u7136\u81f3\u6b7b\u4e0d\u6094\uff0c\u5374\u53c8\u4e0d\u7531\u5f97\u4e0d\u56e0\u6b64\u800c\u60f3\u8d77\u59bb\u513f\uff0c \u59bb\u5b50\u5012\u8fd8\u7f62\u4e86\uff0c\u5bf9\u5bc4\u6258\u7ed9\u66fe\u9759\u629a\u517b\u7684\u513f\u5b50\u5374\u751a\u662f\u62c5\u5fc3\uff0c\u62c5\u5fc3\u81ea\u5df1\u82e5\u7136\u8eab\u6b7b\u4e4b\u540e\uff0c\u66fe\u9759\u672a\u5fc5\u53ef \u9760\uff0c\u65e7\u90e8\u4e5f\u53ea\u6050\u518d\u96be\u627e\u5f97\u4e00\u4eba\uff0c\u80af\u7167\u987e\u81ea\u5df1\u7684\u9057\u5b64\uff0c\u601d\u5ff5\u53ca\u6b64\uff0c\u4efb\u662f\u4e00\u4e16\u4e4b\u96c4\uff0c\u4e5f\u7981\u4e0d\u4f4f\u9eef \u7136\u795e\u4f24\u3002\u601d\u601d\u60f3\u60f3\uff0c\u4e0d\u89c9\u91d1\u9e1f\u897f\u5815\uff0c\u7389\u5154\u4e1c\u5347\uff0c\u9ed1\u591c\u53c8\u6084\u6084\u7684\u6765\u4e86\u3002 \u3000\u3000\u676d\u5dde\u5317\u95e8\u9762\u5411\u7075\u9690\uff0c\u9065\u5bf9\u94b1\u5858\u3002\u9759\u591c\u6084\u6084\uff0c\u5e74\u7fb9\u5c27\u72b9\u81ea\u72ec\u5750\u57ce\u697c\u4e4b\u4e0a\uff0c\u53ea\u542c\u5f97\u57ce\u5916\u6c5f\u6f6e \u6f8e\u6e43\uff0c\u57ce\u5185\u9690\u9690\u5815\u6b4c\uff0c\u731b\u7136\u60f3\u8d77\uff0c\u518d\u8fc7\u4e24\u65e5\u4fbf\u662f\u4e2d\u79cb\uff0c\u5fc3\u60c5\u66f4\u89c9\u843d\u5bde\u3002\u90a3\u8001\u5175\u539f\u662f\u5e74\u5bb6\u5bb6 \u4e01\uff0c\u5728\u5e74\u7fb9\u5c27\u4f17\u53db\u4eb2\u79bb\u4e4b\u9645\uff0c\u53ea\u4ed6\u5c1a\u672a\u80af\u820d\u53bb\uff0c\u8fd9\u65f6\u5728\u66f4\u697c\u5185\u5524\u9053\uff1a\u201c\u5c06\u519b\u5b89\u5bdd\uff0c\u8001\u5974\u4ee3\u4f60 \u5b88\u591c\u5427\u3002\u201d\u5e74\u7fb9\u5c27\u53f9\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u7ecf\u6211\u63d0\u62d4\u8fc7\u7684\u4eba\u4e0d\u77e5\u591a\u5c11\uff0c\u60f3\u4e0d\u5230\u4eca\u591c\u53ea\u6709\u4f60\u6211\u4e8c\u4eba\u76f8 \u4f34\u3002\u201d\u8bf7\u58f0\u65b9\u6bd5\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\uff0c\u4e0d\u5fc5\u55df\u53f9\uff0c\u8fd8\u6709\u6211\u6765\u63a2\u671b\u4f60\u5462\u3002\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u4e3e\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u5df2\u7ad9\u5728\u81ea\u5df1\u9762\u524d\uff0c\u5374\u662f\u4ee5\u524d\u5341\u56db\u8d1d\u52d2\u7684\u5fc3\u8179\u536b\u58eb\uff0c\u4e0e \u8f66\u8f9f\u90aa\u540c\u79f0\u5141\u5824\u519b\u4e2d\u4e8c\u5b9d\u7684\u65b9\u4eca\u660e\u3002\u53ea\u542c\u5f97\u65b9\u4eca\u660e\u51b7\u7b11\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\uff0c\u60f3\u4e0d\u5230\u4f60\u4e5f\u6709\u4eca\u65e5\uff01 \u60f3\u5f53\u5e74\uff0c\u4f60\u4ee5\u4e0b\u72af\u4e0a\uff0c\u66ff\u5141\u796f\u7be1\u4f4d\uff0c\u8c0b\u5bb3\u5341\u56db\u8d1d\u52d2\uff0c\u6211\u53ea\u4ee5\u4e3a\u4f60\u4ece\u6b64\u9752\u4e91\u76f4\u4e0a\uff0c\u5907\u6781\u5c0a\u8363\uff0c \u96be\u4ee5\u5948\u4f55\u4f60\u4e86\u3002\u5374\u4e0d\u9053\u5141\u796f\u4eca\u65e5\u7167\u6837\u7684\u6765\u6536\u62fe\u4f60\uff0c\u54c8\u54c8\uff0c\u54c8\uff01\u201d\u65b9\u4eca\u660e\u5bf9\u5141\u5824\u611a\u5fe0\u4e00\u7247\uff0c\u4eca \u65e5\u6210\u5fc3\u6765\u7f9e\u8fb1\u5e74\u7fb9\u5c27\uff0c\u51b7\u7b11\u4e4b\u540e\uff0c\u590d\u53c8\u7ee7\u4ee5\u75db\u9a82\uff0c\u5c06\u5e74\u7fb9\u5c27\u7684\u9634\u72e0\u9669\u6bd2\u4e4b\u4e8b\u4e00\u4e00\u6570\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u542c\u4ed6\u6570\u8bf4\uff0c\u5374\u4e5f\u6beb\u4e0d\u52a8\u6012\uff0c\u4f8d\u4ed6\u6570\u8bf4\u5b8c\u540e\uff0c\u53cd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u50bb\u5b50\uff0c\u4f60\u4ee5\u4e3a\u5341 \u56db\u8d1d\u52d2\u5c31\u4e0d\u9634\u9669\u72e0\u6bd2\u4e48\uff1f\u4ed6\u7528\u4e00\u70b9\u5c0f\u6069\u5c0f\u60e0\u6765\u7b3c\u7edc\u4f60\uff0c\u5c31\u503c\u5f97\u4f60\u66ff\u4ed6\u5356\u547d\uff0c\u81f3\u6b7b\u4e0d\u5fd8\uff1f\u54c8\uff0c \u54c8\uff01\u201d\u968f\u53e3\u4e5f\u628a\u5141\u5824\u72e0\u6bd2\u7684\u624b\u6bb5\u8bf4\u4e86\u51e0\u4ef6\uff0c\u4f8b\u5982\u600e\u6837\u5e03\u7f6e\u516b\u65d7\u519b\u76d1\u89c6\u6c49\u519b\uff0c\u600e\u6837\u8054\u7edc\u7687\u5b50\uff0c \u8c0b\u593a\u5e1d\u4f4d\u7b49\u7b49\uff0c\u8bb8\u591a\u5185\u4e2d\u9690\u79d8\uff0c\u90fd\u662f\u65b9\u4eca\u660e\u6240\u4e0d\u77e5\u586b\u7684\uff0c\u65b9\u4eca\u660e\u542c\u5f97\u5446\u4e86\uff0c\u4ecd\u786c\u7740\u53e3\u9a82\u9053\uff1a \u201c\u4ffa\u4e3b\u516c\u4e0d\u8bba\u5982\u4f55\uff0c\u90fd\u8981\u6bd4\u4f60\u597d\u5f97\u591a\u3002\u201d\u5e74\u7fb9\u5c27\u54c8\u54c8\u5927\u7b11\uff0c\u5ffd\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u6ca1\u8bf4 \u9519\uff0c\u5141\u5824\u8fd8\u6709\u4f60\u8fd9\u4e48\u4e00\u4e2a\u9ad8\u660e\u7684\u6b66\u58eb\uff0c\u66ff\u4ed6\u6548\u5fe0\uff0c\u800c\u6211\u53ea\u6709\u4e00\u4e2a\u4e0d\u4e2d\u7528\u7684\u8001\u5175\uff0c\u5c31\u51ed\u8fd9\u4e00 \u70b9\uff0c\u4ed6\u662f\u6bd4\u6211\u5f3a\u5f97\u591a\u4e86\u3002\u597d\uff0c\u628a\u4f60\u7684\u4f69\u5200\u7ed9\u6211\uff01\u201d\u65b9\u4eca\u660e\u9000\u540e\u4e00\u6b65\uff0c\u559d\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u5e74\u7fb9 \u5c27\u9053\uff1a\u201c\u4f60\u6b64\u6765\u4e0d\u662f\u4e3a\u4e86\u8981\u6740\u6211\u5417\uff1f\u6211\u5e74\u67d0\u66fe\u4e3a\u767e\u4e07\u5927\u519b\u7684\u4e3b\u5e05\uff0c\u8fd9\u9897\u5934\u9885\u4e0d\u662f\u4f60\u914d\u65ab\u7684\uff0c \u5ff5\u4f60\u5bf9\u5141\u5824\u4e00\u7247\u611a\u5fe0\uff0c\u5e74\u67d0\u6210\u5168\u4f60\u7684\u5fc3\u613f\uff0c\u5c06\u5934\u9001\u7ed9\u4f60\u5427\uff01\u201d\u65b9\u4eca\u660e\u51b7\u7b11\u4e00\u58f0\uff0c\u7a81\u7136\u7eb5\u8eab\u6251 \u4e0a\uff0c\u6a2a\u638c\u5982\u5200\uff0c\u5411\u5e74\u7fb9\u5c27\u9762\u988a\u4fbf\u63b4\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u5b9e\u662f\u6beb\u65e0\u81ea\u520e\u4e4b\u5fc3\uff0c\u4ed6\u4e0d\u8fc7\u60f3\u7528\u8bc8\u672f\uff0c\u9a97\u53d6\u65b9\u4eca\u660e\u7684\u540c\u60c5\uff0c\u800c\u4e14\u5c31\u7b97\u9a97\u4e0d\u5230\u65f6\uff0c \u6599\u60f3\u65b9\u4eca\u660e\u4e5f\u4e0d\u5bf9\u4ed6\u9632\u5907\uff0c\u771f\u662f\u628a\u4f69\u5200\u9012\u8fc7\u6765\u65f6\uff0c\u4ed6\u5c31\u53ef\u4ee5\u4e00\u62f3\u5c06\u4ed6\u51fb\u5012\uff0c\u53d1\u6cc4\u4e00\u53e3\u6076\u6c14\u3002 \u5c82\u77e5\u65b9\u4eca\u660e\u5e76\u4e0d\u5b58\u5fc3\u6740\u4ed6\uff0c\u53ea\u662f\u8981\u5c06\u4ed6\u7f9e\u8fb1\uff0c\u8fd9\u4e00\u8bb0\u540d\u4e3a\u201c\u9b3c\u738b\u62e8\u638c\u201d\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u53cd\u624b\u6253 \u5e74\u7fb9\u5c27\u7684\u8033\u5149\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u867d\u975e\u5e74\u7fb9\u5c27\u59cb\u6599\u6240\u53ca\uff0c\u4f46\u4ed6\u5230\u5e95\u662f\u540d\u5bb6\u5b50\u5f1f\uff0c\u5c11\u6797\u9ad8\u624b\uff0c\u811a\u6b65\u4e00\u65cb\uff0c\u65e9\u5df2\u8f6c\u51fa\u513f \u6b65\uff0c\u6b63\u60f3\u53cd\u51fb\uff0c\u5ffd\u89c1\u53c8\u662f\u4e00\u6761\u9ed1\u5f71\u5728\u57ce\u5899\u4e0a\u9661\u7136\u51fa\u73b0\uff0c\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u65b9\u4eca\u660e\uff0c\u4f60\u5fd8\u4e86\u4f60\u6211\u6614 \u65e5\u4e4b\u7ea6\u4e48\uff1f\u4f60\u6562\u64c5\u81ea\u52a8\u624b\u4f24\u5bb3\u671d\u5ef7\u5927\u5c06\uff0c\u4f11\u602a\u505a\u5144\u5f1f\u7684\u5251\u4e0b\u65e0\u60c5\uff01\u201d \u3000\u3000\u6765\u7684\u4e43\u662f\u6614\u5e74\u4e0e\u65b9\u4eca\u660e\u540c\u79f0\u5141\u5824\u519b\u4e2d\u4e8c\u5b9d\u7684\u8f66\u8f9f\u90aa\u3002\u4e8c\u4eba\u5f80\u6614\u4ea4\u60c5\u751a\u597d\uff0c\u81f3\u5141\u5824\u88ab\u5e74\u7fb9 \u5c27\u6697\u7b97\u4e4b\u540e\uff0c\u65b9\u4eca\u660e\u5fe0\u5fc3\u6545\u4e3b\uff0c\u8f66\u8f9f\u90aa\u5219\u6295\u987a\u65b0\u541b\uff0c\u5206\u9053\u626c\u9573\uff0c\u5404\u4e3a\u5176\u4e3b\u3002\u65b9\u4eca\u660e\u66fe\u8bf4\u8fc7 \u201c\u53ea\u8981\u4f60\u4e0d\u6765\u6349\u6211\uff0c\u6211\u5c31\u4e0d\u548c\u4f60\u52a8\u624b\u201d\u7684\u8bdd\uff0c\u53ef\u662f\u8f66\u8f9f\u90aa\u4e3a\u4e86\u5356\u53cb\u6c42\u8363\uff0c\u7ec8\u4e8e\u548c\u65b9\u4eca\u660e\u51b3 \u88c2\uff0c\u5728\u96ea\u9b42\u8c37\u4e2d\u7ecf\u8fc7\u4e00\u573a\u6076\u6597\uff0c\u65b9\u4eca\u660e\u5e78\u5f97\u5173\u4e1c\u56db\u4fa0\u76f8\u6551\uff0c\u65b9\u624d\u5f97\u514d\u4e8e\u6b7b\u3002 \u3000\u3000\u4e8b\u9694\u6570\u90b1\uff0c\u4eca\u5bb5\u91cd\u9047\uff0c\u65b9\u4eca\u660e\u542c\u5f97\u8f66\u8f9f\u90aa\u63d0\u8d77\u524d\u8a00\uff0c\u4e0d\u89c9\u52c3\u7136\u5927\u6012\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4e8f\u4f60 \u8fd8\u6709\u8138\u76ae\u63d0\u8d77\u8fd9\u8bdd\uff0c\u4f60\u6211\u5144\u5f1f\u4e4b\u60c5\u65e9\u7edd\uff0c\u4f60\u82e5\u518d\u6765\u62e6\u963b\uff0c\u4f11\u602a\u6211\u624b\u4e0b\u65e0\u60c5\uff01\u201d\u8f66\u8f9f\u90aa\u5530\u7684\u4e00 \u58f0\u62d4\u51fa\u4f69\u5251\uff0c\u906e\u5728\u5e74\u7fb9\u5c27\u524d\u9762\uff0c\u5374\u4e0d\u8a00\u8bed\u3002\u65b9\u4eca\u660e\u53f3\u8db3\u8e0f\u524d\u4e00\u6b65\uff0c\u500f\u5730\u8eab\u5f62\u4e00\u957f\uff0c\u4e00\u62db\u201c\u53cc \u98ce\u8d2f\u8033\u201d\uff0c\u4e24\u62f3\u659c\u51fb\uff0c\u8f66\u8f9f\u90aa\u559d\u9053\uff1a\u201c\u60f3\u627e\u6b7b\u4e48\uff1f\u201d\u5251\u950b\u4e00\u5708\uff0c\u53cd\u624b\u4fbf\u6233\uff0c\u65b9\u4eca\u660e\u659c\u8eab\u5206 \u638c\uff0c\u80a9\u5934\u5f80\u4e0b\u4e00\u6c89\uff0c\u4e00\u4e2a\u201c\u8de8\u864e\u767b\u5c71\u201d\u62db\u5f0f\uff0c\u53f3\u811a\u98de\u51fa\uff0c\u659c\u8e22\u4ed6\u6301\u5251\u7684\u624b\u8155\uff0c\u5de6\u81c2\u4e00\u4f38\uff0c\u53c8 \u7528\u957f\u62f3\u6363\u4ed6\u524d\u80f8\u3002\u8f66\u8f9f\u90aa\u8eab\u624b\u77eb\u6377\u975e\u5e38\uff0c\u970d\u5730\u4e00\u4e2a\u201c\u602a\u87d2\u7ffb\u8eab\u201d\uff0c\u8ba9\u8fc7\u6765\u52bf\uff0c\u633d\u4e86\u4e00\u4e2a\u5251 \u82b1\uff0c\u4fa7\u8eab\u5206\u5251\uff0c\u8f6c\u950b\u518d\u6233\u3002 \u3000\u3000\u8fd9\u4e8c\u4eba\u4e00\u4e2a\u662f\u62f3\u672f\u540d\u5bb6\uff0c\u4e00\u4e2a\u662f\u5251\u672f\u597d\u624b\uff0c\u534a\u65a4\u516b\u4e24\uff0c\u65d7\u9f13\u76f8\u5f53\uff0c\u8f6c\u77ac\u6597\u4e86\u4e8c\u4e09\u5341\u62db\uff0c \u4e0d\u5206\u80dc\u8d1f\u3002\u5e74\u7fb9\u5c27\u7acb\u5728\u4e00\u8fb9\uff0c\u9762\u9732\u7b11\u5bb9\uff0c\u5374\u4e0d\u4e0a\u524d\u52a9\u62f3\u3002\u65b9\u4eca\u660e\u731b\u7136\u60f3\u8d77\u81ea\u5df1\u6b64\u6765\u7684\u76ee\u7684\uff0c \u865a\u663e\u4e00\u62f3\uff0c\u5ced\u58f0\u53eb\u9053\uff1a\u201c\u8f9f\u90aa\uff0c\u4f60\u518d\u542c\u6211\u4e00\u8a00\u3002\u201d\u8f66\u8f9f\u90aa\u5de6\u624b\u634f\u7740\u5251\u8bc0\uff0c\u5251\u52bf\u4f3c\u6536\u4f3c\u53d1\uff0c\u6309 \u5251\u5f53\u80f8\uff0c\u542c\u4ed6\u8a00\u8bed\u3002\u65b9\u4ee4\u660e\u9053\uff1a\u201c\u4f60\u6c42\u529f\u540d\uff0c\u6211\u4e3a\u6545\u4e3b\uff0c\u5f7c\u6b64\u6709\u5fd7\uff0c\u6211\u4e5f\u4e0d\u613f\u5f3a\u4f60\u4ece\u6211\u3002\u4f46 \u65f6\u81f3\u4eca\u65e5\uff0c\u5e74\u7fb9\u5c27\u5df2\u662f\u65e5\u66ae\u9014\u7a77\uff0c\u4f60\u8fd8\u62a4\u7740\u4ed6\u4f5c\u751a\uff1f\u201d\u8f66\u8f9f\u90aa\u51b7\u51b7\u4e00\u7b11\uff0c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u71d5\u96c0 \u5b89\u77e5\u9e3f\u9e44\u4e4b\u5fd7\uff1f\u201d\u65b9\u4eca\u660e\u6012\u706b\u518d\u8d77\uff0c\u6b63\u5f85\u8fdb\u62db\u3002\u5e74\u7fb9\u5c27\u5ffd\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u65b9\u4eca\u660e\uff0c\u4f60\u6548\u5fe0\u5141 \u63d0\uff0c\u4ed6\u6548\u5fe0\u4e8e\u6211\uff0c\u771f\u662f\u65e0\u72ec\u6709\u5076\u3002\u4f60\u95ee\u4ed6\u4e3a\u4f55\u62a4\u6211\uff0c\u4ed6\u82e5\u53cd\u95ee\u4f60\u65f6\uff0c\u4f60\u53c8\u5982\u4f55\uff1f\u201d\u65b9\u4eca\u660e\u6014 \u4e86\u4e00\u6014\uff0c\u500f\u5730\u8df3\u51fa\u5708\u5b50\uff0c\u8f6c\u8eab\u4fbf\u8d70\uff0c\u8f66\u8f9f\u90aa\u5634\u89d2\u5659\u7740\u51b7\u7b11\uff0c\u628a\u5251\u63d2\u56de\u9798\u4e2d\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u5fae\u5fae\u4e00\u7b11\uff0c\u4e0a\u524d\u62cd\u8f66\u8f9f\u90aa\u7684\u80a9\u818a\uff0c\u9053\uff1b\u201c\u60a3\u96be\u89c1\u4eba\u5fc3\uff0c\u5230\u5e95\u662f\u4f60\u8fd8\u6709\u70b9\u60c5\u4efd\uff01\u201d \u4e0d\u6599\u8f66\u8f9f\u90aa\u80a9\u5934\u4e00\u649e\uff0c\u628a\u5e74\u7fb9\u5c27\u649e\u5f97\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u6b3a\u541b\u72af\u4e0a\u7684\u7f6a\u4eba\uff0c\u8c01\u5bf9\u4f60 \u6709\u60c5\u4efd\uff1f\u4f60\u4ee5\u4e3a\u6211\u4eca\u665a\u662f\u6765\u6551\u4f60\u7684\u5417\uff1f\u54c8\u54c8\uff01\u8001\u5b9e\u544a\u8bc9\u4f60\u5427\uff0c\u5f53\u4eca\u7687\u4e0a\u8bf4\u4f60\u592a\u8fc7\u53ef\u6076\uff0c\u8981\u6162 \u6162\u5c06\u4f60\u6298\u78e8\uff0c\u6240\u4ee5\u8d2c\u4f60\u6765\u5b88\u57ce\u95e8\uff0c\u53eb\u54b1\u5bb6\u6765\u77a7\u4f60\u8fd9\u2018\u5927\u5c06\u519b\u2019\u7684\u7a98\u6001\u3002\u5723\u4e0a\u660e\u9274\u4e07\u91cc\uff0c\u4ed6\u65e9 \u5c31\u6599\u5230\u4f60\u6709\u8bb8\u591a\u4ec7\u4eba\uff0c\u6015\u90a3\u4e9b\u4eba\u628a\u4f60\u6740\u6389\uff0c\u5012\u4fbf\u5b9c\u4e86\u4f60\uff0c\u6240\u4ee5\u53c8\u5429\u5490\u6211\u7b49\u6697\u4e2d\u9632\u5907\uff0c\u5230\u7d27\u8981 \u5173\u5934\uff0c\u624d\u5c06\u90a3\u4e9b\u4eba\u9a71\u8d70\uff0c\u5723\u4e0a\u8bf4\uff1a\u5929\u4e0b\u6700\u75db\u5feb\u4e4b\u4e8b\uff0c\u65e0\u8fc7\u4e8e\u770b\u4f60\u6240\u618e\u6076\u4e4b\u4eba\uff0c\u5728\u65e5\u66ae\u9014\u7a77\u4e4b \u9645\uff0c\u6c42\u751f\u4e0d\u5f97\uff0c\u6c42\u6b7b\u4e0d\u80fd\uff0c\u6323\u624e\u65e0\u671b\uff0c\u547c\u6551\u65e0\u95e8\uff0c\u4f60\u4ee5\u4e3a\u5723\u4e0a\u4e0d\u7acb\u5373\u8bdb\u622e\uff0c\u662f\u6709\u6240\u7231\u4e8e\u4f60 \u4e48\uff1f\u4f60\u5f53\u6211\u8f66\u67d0\u4eba\u4eca\u65e5\u8fd8\u8981\u505a\u4f60\u7684\u5974\u4ec6\u4e48\uff1f\u54c8\u54c8\uff01\u4f60\u4e5f\u592a\u4e0d\u81ea\u91cf\u4e86\uff01\u201d\u5e74\u7fb9\u5c27\u542c\u4e86\uff0c\u53ea\u6c14\u5f97 \u4e00\u4f5b\u51fa\u4e16\uff0c\u4e8c\u4f5b\u6d85\u822c\uff01 \u3000\u3000\u8f66\u8f9f\u90aa\u51b7\u5632\u70ed\u8bbd\uff0c\u5c06\u5e74\u7fb9\u5c27\u9a82\u4e86\u4e00\u987f\u3002\u5e74\u7fb9\u5c27\u6291\u7740\u6012\u706b\uff0c\u53cd\u95ee\u4ed6\u9053\uff1a\u201c\u8f9f\u90aa\uff0c\u6211\u5f85\u4f60\u4e0d \u8584\uff0c\u4f60\u5728\u6211\u7684\u5e10\u4e0b\uff0c\u4e0d\u5230\u4e09\u5e74\uff0c\u6211\u5c31\u5c06\u4f60\u4e00\u76f4\u4fdd\u8350\u5230\u56db\u54c1\u536b\u58eb\uff0c\u96be\u9053\u5c31\u6ca1\u6709\u4e00\u70b9\u9999\u706b\u4e4b\u60c5 \u4e48\uff1f\u201d\u8f66\u8f9f\u90aa\u5634\u89d2\u4e00\u7fd8\uff0c\u505a\u4e86\u4e2a\u9119\u5c51\u7684\u795e\u6001\uff0c\u9053\uff1a\u201c\u6211\u505a\u7684\u662f\u7687\u4e0a\u7684\u5b98\uff0c\u53c8\u4e0d\u662f\u505a\u4f60\u7684\u5b98\uff0c \u96be\u9053\u4f60\u8981\u6211\u8c22\u4f60\u7684\u6069\u5178\u4e48\uff1f\u73b0\u5728\u6211\u5df2\u7ecf\u662f\u4e09\u54c1\u536b\u58eb\u5566\uff0c\u6bd4\u4f60\u8fd9\u5b88\u57ce\u95e8\u7684\u5c0f\u5352\uff0c\u6700\u5c11\u8981\u9ad8\u51fa\u5341 \u51e0\u7ea7\uff0c\u6211\u4e0d\u8981\u4f60\u89c1\u9762\u53e9\u5934\uff0c\u5df2\u7ecf\u662f\u5bf9\u4f60\u5f88\u6709\u60c5\u4efd\u4e86\uff0c\u4f60\u8fd8\u80fd\u6709\u4ec0\u4e48\u975e\u4efd\u4e4b\u60f3\uff1f\u201d\u5e74\u7fb9\u5c27\u5ffd\u5730 \u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5bf9\u6781\uff0c\u5bf9\u6781\u3002\u4eba\u5411\u9ad8\u5904\uff0c\u6c34\u5411\u4f4e\u6d41\uff0c\u5f53\u673a\u987b\u7acb\u65ad\uff0c\u65e0\u6bd2\u4e0d\u4e08\u592b\u3002\u662f\u5927\u82f1 \u96c4\uff0c\u4fbf\u5f53\u5982\u6b64\uff0c\u8f9f\u90aa\uff0c\u4e0d\u6789\u4f60\u5728\u6211\u5e10\u4e0b\u591a\u5e74\uff0c\u4f60\u5df2\u7ecf\u5f97\u4e86\u6211\u7684\u5fc3\u6cd5\u5566\u3002\u201d\u8f66\u8f9f\u90aa\u6014\u4e86\u4e00\u6014\uff0c \u6b63\u60f3\u53cd\u5507\u76f8\u8ba5\uff0c\u5ffd\u542c\u5f97\u53c8\u6709\u591c\u884c\u4eba\u7684\u58f0\u54cd\uff0c\u614c\u5fd9\u8df3\u8fc7\u4e00\u8fb9\uff0c\u8eb2\u5165\u57ce\u697c\u6697\u89d2\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53c8\u662f\u54ea\u4f4d\u670b\u53cb\u6765\u4e86\uff1f\u5e74\u67d0\u53ea\u6b64\u4e00\u8eab\uff0c\u8981\u62a5\u4ec7\u5c31\u5feb\u52a8\u624b\uff01\u201d\u8bdd\u72b9 \u672a\u4e86\uff0c\u57ce\u5899\u4e0a\u5df1\u8df3\u4e0a\u4e94\u4eba\uff0c\u4e3a\u9996\u7684\u662f\u5c11\u6797\u5bfa\u7684\u5370\u5b8f\u548c\u5c1a\uff0c\u540e\u9762\u7684\u5374\u662f\u5173\u4e1c\u56db\u4fa0\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u9762\u8272\u5927\u53d8\uff0c\u53ea\u542c\u5f97\u5370\u5b8f\u548c\u5c1a\u621f\u6307\u9a82\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u4f60\u4e5f\u6709\u4eca\u65e5\u4e48\uff1f\u60f3\u6211\u5c11\u6797\u5bfa\u5bf9\u4f60 \u6069\u4e49\u5982\u5c71\uff0c\u4f60\u5374\u6bd2\u624b\u6697\u5bb3\u6211\u7684\u5e08\u5c0a\uff0c\u8fd8\u5e26\u5175\u70e7\u4e86\u5d69\u5c71\u5c11\u6797\u5bfa\u8fd9\u5343\u5e74\u53e4\u5239\uff0c\u6211\u95ee\u4f60\uff0c\u4f60\u7684\u5fc3\u809d \u662f\u4ec0\u4e48\u505a\u7684\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u8981\u6740\u4fbf\u6740\uff0c\u4f55\u5fc5\u591a\u8a00\uff01\u201d\u5370\u5b8f\u7ee7\u7eed\u9a82\u9053\uff1a\u201c\u6211\u7684\u5e08\u5c0a\u672c\u65e0\u5927\u5e08 \u66fe\u4f20\u4f60\u6b66\u529f\uff0c\u4f60\u5c06\u4ed6\u6740\u4e86\uff0c\u6211\u4e5f\u51e0\u4e4e\u906d\u4f60\u5bb3\u6b7b\uff0c\u6309\u8bf4\uff0c\u6211\u5373\u628a\u4f60\u788e\u5c38\u4e07\u6bb5\uff0c\u4e5f\u4e0d\u8db3\u89e3\u6211\u5fc3\u5934 \u4e4b\u6068\uff01\u4f46\u5982\u4eca\u6211\u5374\u4e0d\u60f3\u4f60\u901f\u6b7b\uff0c\u4f60\u7684\u9888\u8840\u4e5f\u4e0d\u503c\u5f97\u6c61\u6211\u6212\u5200\uff0c\u8ba9\u4f60\u6240\u6548\u5fe0\u7684\u7687\u5e1d\uff0c\u5c06\u4f60\u5904 \u6b7b\uff0c\u66f4\u8db3\u4ee4\u5929\u4e0b\u4eba\u79f0\u5feb\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u90a3\u4f60\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u5370\u5b8f\u9053\uff1a\u201c\u4e00\u6765\u8981\u770b\u4f60\u8fd9\u4f4d\u5927\u5c06 \u519b\u4eca\u65e5\u7684\u2018\u5a01\u98ce\u2019\uff1b\u4e8c\u6765\u6211\u8981\u95ee\u4f60\uff0c\u6614\u65e5\u5141\u796f\u6240\u6301\u7684\u8d1d\u53f6\u7b3a\u6587\uff0c\u662f\u4e0d\u662f\u5047\u7684\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a \u201c\u662f\u5047\u7684\uff0c\u600e\u4e48\u6837\uff1f\u90a3\u662f\u6211\u4eff\u672c\u7a7a\u5927\u5e08\u7684\u5b57\u8ff9\u5199\u7684\uff0c\u8ba9\u4f60\u4eec\u5c11\u6797\u5bfa\u6c38\u8fdc\u6709\u4e00\u4e2a\u4e0d\u80fd\u6e05\u6d17\u7684\u53db \u5f92\uff0c\u4e5f\u597d\u7ed9\u6b66\u6797\u7559\u4e2a\u7b11\u67c4\u3002\u201d\u5370\u5b8f\u9053\uff1a\u201c\u597d\u54c7\uff0c\u4f60\u5982\u4eca\u59cb\u5410\u5b9e\u4e86\u3002\u201d\u5e74\u7fb9\u5c27\u8138\u4e0a\u9732\u51fa\u4e00\u4e1d\u5978 \u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5c11\u6797\u5bfa\u77e5\u9053\u4e86\u53c8\u600e\u6837\uff1f\u4f60\u4eec\u5c11\u6797\u5bfa\u8fd8\u80fd\u5948\u4f55\u5f53\u4eca\u7684\u7687\u4e0a\u4e48\uff1f\u201d\u4ed6\u6b64\u9645\u80af\u8bf4\u5b9e \u8bdd\uff0c\u4e43\u662f\u56e0\u4e3a\u5df2\u6068\u6781\u96cd\u6b63\uff0c\u56e0\u6b64\u6545\u610f\u51fa\u8a00\u6311\u62e8\uff0c\u60f3\u5c11\u6797\u5bfa\u7684\u50e7\u4eba\u53bb\u523a\u6740\u96cd\u6b63\u3002 \u3000\u3000\u5370\u5b8f\u9053\uff1a\u201c\u597d\uff0c\u4eca\u65e5\u6211\u4e0d\u6740\u4f60\uff0c\u4f46\u597d\u6b79\u4e5f\u5f97\u5728\u4f60\u8eab\u4e0a\u7559\u4e00\u4e9b\u8bb0\u53f7\u3002\u201d\u7eb5\u6b65\u4e0a\u524d\uff0c\u53cc\u6307\u4e00 \u4f38\uff0c\u70b9\u5411\u5e74\u7fb9\u5c27\u53cc\u76ee\uff0c\u5e74\u7fb9\u5c27\u4e00\u4e2a\u201c\u94c1\u95e8\u95e9\u201d\u3002\u5c06\u4ed6\u6765\u52bf\u5316\u89e3\u3002\u5370\u5b8f\u9053\uff1a\u201c\u4f60\u8fd8\u6562\u7528\u5c11\u6797\u7684 \u624b\u6cd5\u4e0e\u6211\u653e\u5bf9\uff01\u201d\u5173\u4e1c\u56db\u4fa0\u4e2d\u7684\u9648\u5143\u9738\u56b7\u9053\uff1a\u201c\u4f9d\u6211\u8bf4\uff0c\u628a\u4ed6\u6740\u4e86\u75db\u5feb\uff0c\u5370\u5b8f\u5e08\u5144\uff0c\u4f60\u82e5\u8d5a \u4fbf\u5b9c\u4e86\u4ed6\uff0c\u5f85\u6211\u7528\u5206\u7b4b\u9519\u9aa8\u624b\u6536\u62fe\u4e86\u4ed6\u5427\uff01\u201d\u56db\u4fa0\u4e2d\u9648\u5143\u9738\u6700\u4e3a\u9c81\u83bd\uff0c\u4e0d\u5f85\u5206\u8bf4\uff0c\u4e00\u722a\u5982 \u94a9\uff0c\u89d1\u7740\u5e74\u7fb9\u5c27\u7435\u7436\u9aa8\u4fbf\u6293\uff01 \u3000\u3000\u7384\u98ce\u9053\u957f\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u201d\u731b\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u652f\u86c7\u7130\u7bad\u7834\u7a7a\u800c\u6765\uff0c\u5c31\u5728\u9648\u5143 \u9738\u7684\u5934\u4e0a\u70b8\u5f00\uff0c\u9648\u5143\u9738\u4f0f\u5730\u4e00\u6eda\uff0c\u51e0\u4e4e\u8dcc\u4e0b\u57ce\u5899\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5723\u4e0a\u660e\u9274\u4e07\u91cc\uff0c \u679c\u7136\u6709\u5c11\u6797\u5bfa\u7684\u4f59\u5b7d\u548c\u540c\u515a\u6765\u4e86\uff0c\u4f60\u4eec\u5411\u5e74\u7fb9\u5c27\u5bfb\u4ec7\uff0c\u6211\u4eec\u4e5f\u6b63\u597d\u5f20\u4e0b\u7f57\u7f51\u7b49\u541b\u5165\u74ee\u5462\uff01\u201d \u8bf4\u8bdd\u7684\u662f\u97e9\u91cd\u5c71\uff0c\u4ed6\u7684\u5e08\u5f1f\u5929\u53f6\u6563\u4eba\u5219\u5df2\u622a\u7740\u4e86\u7384\u98ce\u7684\u53bb\u8def\uff01 \u3000\u3000\u7384\u98ce\u4e00\u58f0\u5927\u543c\uff0c\u957f\u5251\u4e00\u7ffb\uff0c\u94c1\u62d0\u4e00\u626b\uff0c\u4e24\u624b\u4e24\u822c\u5175\u5668\uff0c\u540c\u65f6\u53d1\u51fa\uff0c\u5929\u53f6\u6563\u4eba\u65cb\u8eab\u4e00\u95ea\uff0c \u547c\u547c\u4e24\u638c\uff0c\u5c06\u7384\u98ce\u9707\u5f97\u8eab\u5f62\u4e0d\u5b9a\uff0c\u6717\u6708\u7985\u5e08\u5728\u846b\u82a6\u91cc\u5438\u4e86\u4e00\u5927\u53e3\u9152\uff0c\u4e00\u53e3\u9152\u6d6a\uff0c\u8fce\u98ce\u55b7\u51fa\uff0c \u5374\u7ed9\u638c\u98ce\u8361\u5f97\u56db\u5904\u98de\u6e85\uff0c\u6709\u5982\u6d12\u4e86\u534a\u5929\u9152\u96e8\u3002\u97e9\u91cd\u5c71\u628a\u624b\u4e00\u626c\uff0c\u53d1\u51fa\u4e24\u822c\u6697\u5668\uff0c\u7528\u56de\u73af\u94a9\u6765 \u53d6\u67f3\u5148\u5f00\uff0c\u7528\u94c1\u83b2\u5b50\u6253\u9648\u5143\u9738\u7684\u7a74\u9053\uff0c\u67f3\u5148\u5f00\u53f7\u79f0\u201c\u4e07\u91cc\u8ffd\u98ce\u201d\uff0c\u7109\u80fd\u7ed9\u4ed6\u6253\u4e2d\uff0c\u95ea\u5c55\u817e \u632a\uff0c\u4e00\u8fde\u907f\u4e86\u51e0\u6b21\uff0c\u53ef\u662f\u90a3\u56de\u73af\u94a7\u8f6c\u6298\u56de\u7fd4\uff0c\u67f3\u5148\u5f00\u4e5f\u7834\u5b83\u4e0d\u5f97\u3002\u9648\u5143\u9738\u8f7b\u529f\u8f83\u900a\uff0c\u7ed9\u94c1\u83b2 \u5b50\u6253\u7740\uff0c\u5e78\u4ed6\u94dc\u76ae\u94c1\u9aa8\uff0c\u867d\u7136\u611f\u5230\u7a74\u9053\u4e0a\u4e00\u9635\u75bc\u75db\uff0c\u5374\u662f\u65e0\u4e8b\u3002 \u3000\u3000\u97e9\u91cd\u5c71\u5e08\u5144\u5f1f\u7684\u6b66\u529f\u6bd4\u5173\u4e1c\u56db\u4fa0\u9ad8\u51fa\u751a\u591a\uff0c\u56db\u4fa0\u4e2d\u53ea\u6709\u7384\u98ce\u6562\u786c\u63a5\u4ed6\u4eec\u7684\u62db\u6570\uff0c\u5176\u4ed6\u4e09 \u4eba\u5374\u8fd1\u4e0d\u4e86\u8eab\u3002\u5370\u5b8f\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u8981\u95ee\u7684\u5df2\u7ecf\u95ee\u4e86\uff0c\u4f55\u82e6\u518d\u5728\u6b64\u5730\u7ea0\u7f20\uff0c\u4e0d\u5982\u8d70\u5427\uff01\u201d\u7384\u98ce \u75be\u523a\u6570\u5251\uff0c\u63a9\u62a4\u64a4\u9000\uff0c\u9648\u5143\u9738\u5148\u8df3\u4e0b\u57ce\u5899\uff0c\u6717\u6708\u7985\u5e08\u55b7\u4e86\u4e24\u53e3\u9152\u6d6a\uff0c\u4e5f\u8ddf\u7740\u5370\u5b8f\u8df3\u4e0b\uff0c\u7384\u98ce \u4e00\u62db\u201c\u4e3e\u706b\u71ce\u5929\u201d\uff0c\u94c1\u62d0\u4e0a\u64a9\uff0c\u6321\u5f00\u4e86\u97e9\u91cd\u5c71\u7684\u8f9f\u4e91\u9504\uff0c\u8ddf\u7740\u7eb5\u8eab\u4e0b\u8df3\u3002\u5929\u53f6\u6563\u4eba\u8eab\u5f62\u98de \u8d77\uff0c\u7528\u201c\u9965\u9e70\u6251\u5154\u201d\u7684\u624b\u6cd5\uff0c\u4f38\u624b\u4fbf\u6293\uff0c\u731b\u542c\u5f97\u5934\u9876\u4e0a\u4e00\u58f0\u547c\u5578\uff0c\u5929\u53f6\u6563\u4eba\u6025\u5fd9\u4e00\u4e2a\u5012\u7ffb\uff0c \u786c\u628a\u7eb5\u51fa\u53bb\u7684\u8eab\u5f62\u64a4\u4e86\u56de\u6765\uff0c\u51b2\u5929\u4e00\u62f3\uff0c\u51fb\u654c\u4e0b\u989a\uff0c\u5374\u542c\u5f97\u54c8\u54c8\u7b11\u58f0\uff0c\u67f3\u5148\u5f00\u5df2\u4ece\u4ed6\u7684\u5934\u9876 \u63a0\u8fc7\uff0c\u98de\u4e0b\u57ce\u5899\u3002\u5173\u4e1c\u56db\u4fa0\uff0c\u867d\u7136\u4e0d\u662f\u9876\u513f\u5c16\u513f\u7684\u89d2\u8272\uff0c\u5374\u662f\u5404\u6709\u72ec\u95e8\u6b66\u529f\uff0c\u97e9\u91cd\u5c71\u5e08\u5144\u5f1f \u7adf\u7136\u622a\u4ed6\u4eec\u4e0d\u4f4f\u3002 \u3000\u3000\u5929\u53f6\u6563\u4eba\u9053\u58f0\uff1a\u201c\u9000\uff01\u201d\u4e0e\u97e9\u91cd\u5c71\u4e00\u540c\u8dc3\u4e0b\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u4eba\u58f0\u5df2\u6773\u3002\u8f66\u8f9f\u90aa\u53c8\u4ece\u57ce\u697c\u6697 \u89d2\u5904\u94bb\u4e86\u51fa\u6765\u3002\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u7687\u4e0a\u75db\u6068\u5c11\u6797\u4f59\u5b7d\uff0c\u4f60\u4e3a\u4f55\u4e0d\u8d81\u6b64\u7acb\u529f\uff1f\u201d\u8f66\u8f9f\u90aa\u51b7\u7b11\u9053\uff1a \u201c\u6211\u8fd8\u8981\u770b\u5b88\u4f60\u5462\uff01\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u7709\u6bdb\u4e00\u626c\uff0c\u9053\uff1a\u201c\u591a\u8c22\u76db\u60c5\u3002\u201d\u5ffd\u7136\u4f5c\u51fa\u6c89\u601d\u4e4b\u72b6\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8f9f \u90aa\uff0c\u6211\u6709\u4e00\u4e8b\u4e0e\u4f60\u5546\u91cf\u3002\u201d\u8f66\u8f9f\u90aa\u9053\uff1a\u201c\u4f60\u60f3\u6211\u653e\u4f60\u4e48\uff1f\u5929\u4e0b\u4e4b\u5927\u5df2\u65e0\u4f60\u5bb9\u8eab\u4e4b\u5904\u4e86\u3002\u4f60\u5e9f \u8bdd\u4f11\u63d0\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6211\u5c82\u80fd\u5f3a\u4f60\u6240\u96be\uff0c\u6211\u5b9e\u544a\u4f60\uff0c\u6211\u6709\u7a00\u4e16\u7684\u73cd\u5b9d\uff0c\u60f3\u8d60\u9001\u4e0e\u4f60\u3002\u201d\u8f66 \u8f9f\u90aa\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u6837\u597d\u5fc3\uff1f\u6211\u5bf9\u4f60\u4f55\u6069\uff1f\u4f60\u80af\u5c06\u7a00\u4e16\u73cd\u5b9d\u9001\u6211\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6211\u4e0d\u662f \u767d\u9001\u4f60\u7684\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u65e9\u6599\u5230\u6709\u4eca\u65e5\u4e4b\u7978\uff0c\u6240\u4ee5\u5c06\u5c0f\u513f\u65e9\u5df2\u5bc4\u6258\u5728\u4e00\u4e2a\u670b\u53cb\u5bb6\u4e2d\uff0c\u6211\u8fdf\u65e9 \u5fc5\u6b7b\uff0c\u5bb6\u4ea7\u5b9a\u7136\u6284\u6ca1\uff0c\u5c0f\u513f\u4ed6\u65e5\u957f\u5927\u4f55\u4ee5\u4e3a\u751f\uff1f\u6240\u4ee5\u60f3\u628a\u4ef7\u503c\u8fde\u57ce\u7684\u73e0\u5b9d\u4e0e\u4f60\uff0c\u51ed\u4f60\u7684\u826f \u5fc3\uff0c\u53d8\u4ef7\u5356\u51fa\u4e4b\u540e\uff0c\u4ea4\u56de\u4e00\u534a\u4e0e\u6211\u90a3\u4f4d\u670b\u53cb\uff0c\u4ee5\u4fbf\u5c0f\u513f\u4ed6\u65e5\u5f97\u4e2a\u6e29\u9971\u3002\u201d \u3000\u3000\u8f66\u8f9f\u90aa\u610f\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u6211\u51fa\u4eac\u4e4b\u65f6\uff0c\u7687\u4e0a\u5df2\u5c06\u5e74\u7fb9\u5c27\u5bb6\u5c5e\u5c3d\u884c\u6536\u7981\uff0c\u72ec\u72ec\u4e0d\u89c1\u4ed6\u7684\u513f \u5b50\uff0c\u7687\u4e0a\u8bf4\u8981\u65a9\u8349\u9664\u6839\uff0c\u8fd8\u53eb\u6211\u4eec\u6697\u4e2d\u67e5\u8bbf\u3002\u5e74\u7fb9\u5c27\u6240\u8bf4\u7684\u6599\u662f\u5b9e\u60c5\u3002\u6211\u4e0d\u5982\u5047\u4f5c\u7b54\u5141\uff0c\u9a97 \u4ed6\u5c06\u85cf\u5b9d\u4e4b\u5904\u8bf4\u4e86\uff0c\u90a3\u5c82\u4e0d\u662f\u65e2\u53ef\u4e3a\u7687\u4e0a\u7acb\u529f\uff0c\u53c8\u53ef\u5f97\u7a00\u4e16\u73cd\u5b9d\u3002\u201d\u4fbf\u9053\uff1a\u201c\u8fd9\u70b9\u5c0f\u4e8b\uff0c\u6211 \u8f66\u67d0\u8fd8\u53ef\u4f5c\u4e3b\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d\u8f66\u8f9f\u90aa\u9053\uff1a\u201c\u4e8e\u4eba\u65e0\u635f\uff0c\u4e8e\u5df1\u6709\u5229\uff0c\u4f55\u4e50\u4e0d\u4e3a\uff1f\u8bf7 \u4f60\u5c06\u4f60\u90a3\u4f4d\u670b\u53cb\u7684\u5730\u5740\u8bf4\u51fa\u6765\u5427\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u8bf4\uff0c\u6211\u4fe1\u4f60\u4e86\uff0c\u4f46\u9694\u5899\u6709\u8033\uff0c\u73e0\u5b9d \u4e5f\u4e0d\u4fbf\u9732\u773c\uff0c\u4f60\u9644\u8033\u8fc7\u6765\u5427\uff01\u201d\u8f66\u8f9f\u90aa\u679c\u7136\u8d70\u5230\u5e74\u7fb9\u5c27\u8eab\u524d\uff0c\u4fa7\u8033\u503e\u542c\u3002\u4e0d\u6599\u5e74\u7fb9\u5c27\u53cd\u624b\u4e00 \u638c\uff0c\u65bd\u5c55\u65e0\u6781\u95e8\u7684\u64d2\u62ff\u7edd\u6280\uff0c\u4e00\u628a\u6263\u7740\u4ed6\u7684\u8109\u95e8\uff0c\u8f66\u8f9f\u90aa\u5168\u8eab\u762b\u75ea\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c\u5e74\u7fb9\u5c27\u9a82 \u9053\uff1a\u201c\u4f60\u8fd9\u72d7\u5a18\u517b\u7684\uff0c\u5c45\u7136\u6562\u6765\u6b3a\u6211\uff01\u6211\u5c82\u80fd\u53d7\u4f60\u4e4b\u6c14\uff01\u6211\u53cd\u6b63\u5df2\u72af\u4e86\u5341\u516b\u6761\u5927\u7f6a\uff0c\u518d\u591a\u72af \u4e00\u6761\uff0c\u4e5f\u4e0d\u600e\u4e48\u3002\u201d\u9a88\u6307\u671d\u8f66\u8f9f\u90aa\u80c1\u4e0b\u4e00\u6233\uff0c\u70b9\u4e86\u4ed6\u7684\u6b7b\u7a74\uff0c\u8f66\u8f9f\u90aa\u60e8\u53eb\u4e00\u58f0\uff0c\u767b\u65f6\u6c14\u7edd\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u51b7\u51b7\u4e00\u7b11\uff0c\u53ea\u542c\u5f97\u66f4\u697c\u9f13\u54cd\uff0c\u5df2\u662f\u56db\u66f4\uff0c\u5468\u56f4\u9759\u5f97\u6015\u4eba\uff0c\u5fc3\u9053\uff0c\u201c\u4eca\u665a\u6765\u4e86\u51e0\u6279 \u4ec7\u4eba\uff0c\u90a3\u8001\u5175\u96be\u9053\u5413\u6b7b\u4e86\u4e48\uff1f\u4e3a\u4f55\u4e0d\u89c1\u4ed6\u7684\u58f0\u664c\uff1f\u55ef\uff0c\u4eca\u65e5\u53ea\u6709\u4e00\u4e2a\u8001\u5f31\u6b8b\u5175\u8fd8\u613f\u610f\u8ddf\u968f \u6211\uff0c\u6211\u4e5f\u7b97\u5012\u9709\u6781\u4e86\uff01\u201d\u6b63\u60f3\u51fa\u58f0\u547c\u5524\uff0c\u89c1\u8f66\u8f9f\u90aa\u7684\u5c38\u4f53\u6a2a\u5728\u57ce\u5899\u4e4b\u4e0a\uff0c\u773c\u775b\u72b9\u81ea\u7741\u5f00\uff0c\u767d \u6e17\u6e17\u7684\u4ee4\u4eba\u6076\u5fc3\uff0c\u5e74\u7fb9\u5c27\u6027\u8d77\uff0c\u4e00\u811a\u5c06\u4ed6\u8e22\u4e0b\u4e86\u57ce\u5899\uff0c\u5ffd\u542c\u5f97\u8033\u8fb9\u4e00\u58f0\u201c\u963f\u5f25\u9640\u4f5b\u201d\uff0c\u5165\u8033 \u523a\u5fc3\uff0c\u5e74\u7fb9\u5c27\u7741\u773c\u4e00\u770b\uff0c\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u6765\u7684\u7adf\u662f\u4ee5\u524d\u5c11\u6797\u5bfa\u7684\u76d1\u5bfa\uff0c\u800c\u4eca\u5c11\u6797\u5bfa\u7684\u4e3b\u6301\u5f18 \u6cd5\u5927\u5e08\uff01\u5f18\u6cd5\u5927\u5e08\u4e0e\u5c11\u6797\u4e09\u8001\u540c\u8f88\uff0c\u59dc\u6842\u4e4b\u6027\uff0c\u5ac9\u6076\u5982\u4ec7\uff0c\u5c31\u4f3c\u4ee5\u524d\u7684\u672c\u65e0\u5927\u5e08\u4e00\u6837\u3002\u5e74\u7fb9 \u5c27\u5fc3\u9053\uff1a\u201c\u5c11\u6797\u4e09\u8001\u5148\u540e\u4ea1\u8fc7\uff0c\u800c\u4eca\u662f\u5f18\u6cd5\u4e3b\u6301\uff0c\u4ed6\u4e00\u5b9a\u662f\u8981\u7528\u5c11\u6797\u5bb6\u6cd5\uff0c\u60e9\u6cbb\u6211\u4e86\u3002\u201d\u60f3 \u8d77\u5c11\u6797\u5bfa\u7684\u5206\u7b4b\u9519\u9aa8\uff0c\u95ed\u7a74\u4f24\u6b8b\u7b49\u7b49\u60e9\u6cbb\u53db\u5f92\u624b\u6cd5\uff0c\u6bd4\u53d7\u51cc\u8fdf\u788e\u5250\u8fd8\u8981\u75db\u82e6\uff0c\u4e0d\u89c9\u80c6\u5bd2\uff01 \u3000\u3000\u5f18\u6cd5\u5927\u5e08\u76ee\u5149\u6709\u5982\u5229\u526a\uff0c\u76ef\u7740\u5e74\u7fb9\u5c27\u95ee\u9053\uff1a\u201c\u5e74\u5927\u5c06\u519b\uff0c\u4f60\u53ef\u8fd8\u8ba4\u5f97\u8001\u8872\u4e48\uff1f\u201d\u5e74\u7fb9\u5c27 \u9053\uff1a\u201c\u5f1f\u5b50\u77e5\u7f6a\u4e86\u3002\u201d\u5f18\u6cd5\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u5e08\u5c0a\uff1f\u4f60\u662f\u8c01\u7684\u5f1f\u5b50\uff1f\u5c11\u6797\u5bfa\u4e0d\u5bb9\u4f60\u6765\u73b7 \u6c61\uff0c\u65e0\u6781\u6d3e\u4e5f\u4e0d\u8ba4\u4f60\u8fd9\u4e2a\u53db\u5f92\u3002\u201d\u5e74\u7fb9\u5c27\u4f4e\u9996\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u8bf7\u5927\u5e08\u6148\u60b2\uff0c\u8d50\u6211\u4e00\u4e2a\u5168\u5c38 \u5427\uff01\u201d\u5f18\u6cd5\u5927\u5e08\u9762\u631f\u5bd2\u971c\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u81ea\u6709\u671d\u5ef7\u660e\u6b63\u5178\u5211\uff0c\u4f55\u7528\u8001\u8872\u52a8\u624b\u3002\u6211\u6765\u89c1\u4f60\uff0c \u4e3a\u7684\u662f\u4e24\u6869\u4e8b\u60c5\uff0c\u4f60\u4e14\u542c\u7740\uff1a\u7b2c\u4e00\u4ef6\u662f\u5c11\u6797\u4e09\u8001\u66fe\u4f20\u8fc7\u4f60\u7684\u6b66\u529f\uff0c\u7b49\u4e8e\u95f4\u63a5\u52a9\u4f60\u4e3a\u6076\uff0c\u8fd9\u662f \u5c11\u6797\u5bfa\u7684\u7f6a\u8fc7\uff0c\u8001\u8872\u8981\u4e3a\u524d\u4efb\u4e3b\u6301\u8d4e\u7f6a\uff0c\u6536\u56de\u4f60\u7684\u6b66\u529f\u3002\u201d\u8bf4\u5230\u6b64\u5904\uff0c\u731b\u7136\u4f38\u624b\u5411\u5e74\u7fb9\u5c27\u8111 \u95e8\u4e00\u62cd\uff0c\u5e74\u7fb9\u5c27\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u96be\u8eb2\u907f\uff0c\u88ab\u4ed6\u4e00\u62cd\uff0c\u53ea\u89c9\u5929\u65cb\u5730\u8f6c\uff0c\u8fc7\u4e86\u597d\u4e45\uff0c\u624d\u6e05\u9192\u8fc7\u6765\uff0c \u56db\u80a2\u5df2\u662f\u7ef5\u8f6f\u65e0\u529b\u3002\u5f18\u6cd5\u5927\u5e08\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5982\u4eca\u624d\u6536\u56de\u4f60\u7684\u6b66\u529f\u5df2\u662f\u8fdf\u4e86\uff0c\u4f46\u4e5f\u7b97\u4e86\u4e00\u5b97\u516c \u6848\uff0c\u5b88\u7740\u4e86\u5c11\u6797\u5386\u4ee3\u76f8\u4f20\u7684\u89c4\u77e9\u3002\u201d \u3000\u3000\u5f18\u6cd5\u5927\u5e08\u7a0d\u505c\u534a\u664c\uff0c\u53c8\u9053\uff1a\u201c\u6211\u9664\u4e86\u8981\u4e3a\u524d\u4efb\u4e3b\u6301\u6536\u56de\u4f60\u7684\u6b66\u529f\uff0c\u8fd8\u8981\u66ff\u65e0\u6781\u6d3e\u6e05\u7406\u95e8 \u6237\u3002\u8fd9\u4e8b\u672c\u8be5\u5929\u5c71\u7684\u6613\u8001\u524d\u8f88\u529e\u7684\uff0c\u5979\u65e0\u6687\u518d\u5230\u4e2d\u539f\uff0c\u6258\u4eba\u544a\u77e5\u8001\u8872\uff0c\u8bf7\u8001\u8872\u4ee3\u529e\uff0c\u5c11\u4e0d\u5f97 \u8981\u591a\u8d39\u4e00\u4e9b\u624b\u811a\u3002\u201d\u8bf4\u5230\u6b64\u5904\uff0c\u4e24\u9053\u5bff\u7709\u4e00\u7ad6\uff0c\u5386\u58f0\u559d\u9053\uff1a\u201c\u949f\u4e07\u5802\u8d39\u5c3d\u5fc3\u8840\uff0c\u5c06\u4f60\u57f9\u517b\u6210 \u6750\uff0c\u4f60\u4e3a\u4f55\u52fe\u5f15\u53cc\u9b54\uff0c\u5c06\u4ed6\u5bb3\u6b7b\uff1f\u50cf\u4f60\u8fd9\u7b49\u884c\u4e3a\uff0c\u8fd8\u80fd\u5bb9\u4e8e\u6b66\u6797\u5417\uff1f\u201d\u5e74\u7fb9\u5c27\u5df2\u77e5\u5f18\u6cd5\u4e0d\u80af \u52a8\u624b\u6740\u4ed6\uff0c\u7d22\u6027\u95ed\u53e3\u4e0d\u7b54\u3002\u5f18\u6cd5\u7eed\u9053\uff1a\u201c\u60f3\u5f53\u5e74\u5085\u9752\u4e3b\u8001\u5148\u751f\u521b\u7acb\u95e8\u6237\uff0c\u4f55\u7b49\u8270\u96be\uff0c\u60f3\u4e0d\u5230 \u51fa\u4e86\u4f60\u8fd9\u4e2a\u4e07\u6076\u53db\u5f92\uff0c\u51e0\u4e4e\u4ee4\u65e0\u6781\u6d3e\u81f3\u4f60\u800c\u65a9\u3002\u5e78\u5f97\u65e0\u6781\u6d3e\u8fd8\u6709\u4e00\u4e2a\u4f20\u4eba\uff0c\u8981\u4e0d\u7136\u5085\u9752\u4e3b\u4e0e \u949f\u4e07\u5802\u90fd\u6b7b\u4e0d\u7791\u76ee\u3002\u201d\u5e74\u7fb9\u5c27\u5ffd\u95ee\u9053\uff1a\u201c\u65e0\u6781\u6d3e\u8fd8\u6709\u4ec0\u4e48\u4f20\u4eba\uff1f\u201d\u5f18\u6cd5\u9053\uff1a\u201c\u4e0d\u7528\u4f60\u7ba1\uff0c\u6211 \u53d7\u6613\u8001\u524d\u8f88\u4e4b\u6258\uff0c\u524d\u6765\u544a\u8bc9\u4e8e\u4f60\uff0c\u6211\u5df2\u4e0e\u6613\u8001\u524d\u8f88\u8054\u540d\uff0c\u901a\u544a\u6b66\u6797\u540c\u9053\uff0c\u4ee3\u65e0\u6781\u6d3e\u6e05\u7406\u95e8 \u6237\uff0c\u53e6\u7acb\u4f20\u4eba\uff0c\u5c06\u4f60\u9a71\u9010\u51fa\u65e0\u6781\u6d3e\u95e8\u5899\u4e4b\u5916\u4e86\uff01\u201d\u5e74\u7fb9\u5c27\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u6027\u547d\u5df2\u662f\u4e0d\u4fdd\uff0c\u8fd8 \u4e89\u6301\u8fd9\u4e2a\u4e48\uff1f\u201d\u5f18\u6cd5\u5927\u5e08\u6447\u4e86\u6447\u5934\uff0c\u6012\u9053\uff1a\u201c\u5b7d\u755c\u5b7d\u755c\uff0c\u81f3\u6b7b\u4e0d\u609f\uff01\u201d\u500f\u7136\u62d4\u51fa\u6212\u5200\uff0c\u5e74\u7fb9 \u5c27\u5403\u4e86\u4e00\u60ca\uff0c\u4f46\u89c9\u9762\u524d\u5bd2\u5149\u7535\u5c04\uff0c\u5200\u98ce\u98d5\u98d5\uff0c\u90a3\u53e3\u5229\u5203\uff0c\u5c31\u597d\u50cf\u5728\u8138\u76ae\u4e0a\u522e\u6765\u522e\u53bb\u4e00\u822c\uff0c\u53ea \u542c\u5f97\u5f18\u6cd5\u5927\u5e08\u5728\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u5168\u65e0\u5ec9\u803b\uff0c\u6127\u4f5c\u987b\u7709\uff0c\u7565\u793a\u8584\u60e9\uff0c\u4ee5\u6212\u8d3c\u5b50\u3002\u201d\u5200\u98ce\u500f\u6b62\uff0c\u5e74 \u7fb9\u5c27\u5f20\u773c\u770b\u65f6\uff0c\u5f18\u6cd5\u5927\u5e08\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u4f38\u624b\u4e00\u6478\uff0c\u9762\u4e0a\u5149\u6ed1\u6ed1\u7684\uff0c\u4e0d\u4f46\u6240\u7559\u7684\u4e24\u6487\u864e\u987b\uff0c\u88ab\u5243\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u8fde\u7709\u6bdb\u4e5f\u522e \u5f97\u4e2a\u4e00\u4e1d\u4e0d\u7559\u3002\u5e74\u7fb9\u5c27\u5e73\u751f\uff0c\u90a3\u66fe\u53d7\u8fc7\u5982\u6b64\u5947\u803b\u5927\u8fb1\uff0c\u4e0d\u89c9\u6124\u7136\u6325\u62f3\uff0c\u6012\u58f0\u9a82\u9053\uff1a\u201c\u5f18\u6cd5\u8d3c \u79c3\uff0c\u8fb1\u6211\u592a\u751a\uff01\u201d\u4f46\u4e00\u62f3\u6325\u51fa\uff0c\u7acb\u523b\u611f\u5230\u6c14\u5598\u65e0\u529b\uff0c\u53c8\u4e0d\u89c9\u53f9\u4e86\u53e3\u6c14\uff0c\u9893\u7136\u5750\u5230\u5730\u4e0a\u3002 \u3000\u3000\u661f\u6a2a\u6597\u8f6c\uff0c\u8fd9\u65f6\u5df2\u6253\u8fc7\u4e94\u66f4\uff0c\u671d\u9732\u6653\u98ce\uff0c\u9976\u6709\u5bd2\u610f\uff0c\u5e74\u7fb9\u5c27\u54b3\u4e86\u4e24\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u738b\u8001 \u4e09\uff0c\u738b\u8001\u4e09\uff01\u201d\u738b\u8001\u4e09\u662f\u90a3\u8001\u5175\u7684\u540d\u5b57\uff0c\u53eb\u4e86\u4e24\u58f0\uff0c\u4e0d\u89c1\u7b54\u5e94\uff0c\u6b63\u5728\u5947\u602a\uff0c\u5ffd\u89c1\u90a3\u540d\u8001\u5175\u98a4 \u5dcd\u5dcd\u7684\u4ece\u57ce\u697c\u5185\u8d70\u4e86\u51fa\u6765\uff0c\u5728\u65d7\u7aff\u7684\u201c\u98ce\u706f\u201d\u6620\u7167\u4e4b\u4e0b\uff0c\u9762\u8272\u663e\u5f97\u4e00\u7247\u7070\u767d\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u738b\u8001\u4e09\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u8fd9\u540d\u8001\u5175\u5411\u5e74\u7fb9\u5c27\u8fce\u5934\u4e00\u63d6\uff0c\u6006\u7136\u8bf4\u9053\uff1a\u201c\u8bf7\u6055\u6211 \u8fd9\u540d\u4e0d\u4e2d\u7528\u7684\u8001\u5175\u96be\u4ee5\u518d\u4f8d\u5019\u4f60\u4e86\uff01\u201d\u5e74\u7fb9\u5c27\u77e5\u9053\u81ea\u5df1\u4e0e\u65b9\u4eca\u660e\u7684\u8c08\u8bdd\u5df2\u88ab\u4ed6\u542c\u5230\uff0c\u5fd9\u9053\uff1a \u201c\u8001\u4e09\uff0c\u4f60\u522b\u591a\u5fc3\u2026\u2026\u201d\u738b\u8001\u4e09\u622a\u7740\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u8bf4\u4e86\uff0c\u4eca\u665a\u6211\u4e00\u5207\u90fd\u660e\u767d\u5566\uff01\u5c0f\u5b98\uff0c\u5728\u6211 \u66fe\u770b\u7740\u4f60\u957f\u5927\uff0c\u5374\u4ece\u4e0d\u77e5\u9053\u4f60\u662f\u4e00\u4e2a\u5982\u6b64\u5fd8\u6069\u8d1f\u5fb7\u3001\u5be1\u60c5\u7edd\u4e49\u7684\u4eba\uff01\u8001\u4e3b\u4eba\u4e00\u751f\u4e5f\u672a\u66fe\u505a\u8fc7 \u4ec0\u4e48\u6076\u4e8b\uff0c\u600e\u4e48\u5374\u4f1a\u5f97\u5230\u8fd9\u6837\u7684\u6076\u62a5\u5e94\uff0c\u751f\u4e0b\u4f60\u8fd9\u4e2a\u8d25\u5bb6\u706d\u65cf\u7684\u9006\u5b50\uff0c\u54b3\uff0c\u6211\u771f\u66ff\u4f60\u5e74\u5bb6\u5386 \u4ee3\u7956\u5148\u4e0d\u503c\uff01\u201d\u8fd9\u540d\u8001\u5175\u8bf4\u5f97\u5341\u5206\u6fc0\u52a8\uff0c\u5e74\u7fb9\u5c27\u6c14\u5f97\u9762\u8272\u9752\u767d\uff0c\u51e0\u4e4e\u60f3\u5c06\u4ed6\u4e00\u62f3\u6253\u6b7b\uff0c\u4f46\u60f3 \u5230\u8fd9\u540d\u8001\u5175\u4e5f\u4f1a\u51e0\u624b\u62f3\u811a\uff0c\u800c\u81ea\u5df1\u6b66\u529f\u5374\u5df2\u6d88\u5931\uff0c\u62f3\u5934\u4e00\u6325\uff0c\u7acb\u5373\u7f29\u56de\u3002 \u3000\u3000\u90a3\u540d\u8001\u5175\u53f9\u4e86\u4e00\u53e3\u957f\u6c14\uff0c\u773c\u6cea\u7c07\u7c41\u4e0b\u843d\uff0c\u53c8\u9053\uff1a\u201c\u6211\u670d\u4f8d\u4e86\u4f60\u7684\u8001\u5b50\u591a\u5e74\uff0c\u53c8\u670d\u4f8d\u4e86\u4f60 \u591a\u5e74\uff0c\u5e76\u66fe\u968f\u4f60\u4e07\u91cc\u957f\u5f81\uff0c\u51fa\u751f\u5165\u6b7b\uff0c\u4e00\u672a\u5347\u5b98\uff0c\u4e8c\u672a\u53d1\u8d22\uff0c\u4e5f\u7b97\u5bf9\u5f97\u4f4f\u4f60\u5e74\u5bb6\u4e86\u3002\u6211\u4eca\u65e5 \u62dc\u8f9e\uff01\u201d\u8bdd\u5b8c\u4e4b\u540e\uff0c\u5411\u5e74\u7fb9\u5c27\u4e00\u63d6\u5230\u5730\uff0c\u4ece\u57ce\u5934\u4e0a\u62fe\u7ea7\u800c\u4e0b\uff0c\u8d70\u4e86\u51e0\u6b65\uff0c\u5ffd\u53c8\u56de\u5934\u8bf4\u9053\uff1a \u201c\u4f60\u6628\u65e5\u6362\u4e0b\u7684\u8863\u670d\uff0c\u6211\u5df2\u6d17\u51c0\u6652\u5e72\uff0c\u4f60\u81ea\u5df1\u6536\u62fe\u5427\uff0c\u4eca\u671d\u7684\u65e9\u996d\u6211\u4e5f\u505a\u597d\u4e86\uff0c\u4ee5\u540e\u4f60\u81ea\u5df1 \u5b66\u7740\u505a\u5427\uff0c\u6211\u8fd9\u6ca1\u4e2d\u7528\u7684\u8001\u4ec6\u4eba\u62dc\u8f9e\u4e86\u3002\u201d\u4e00\u6b65\u4e00\u6b65\u8d70\u4e0b\u57ce\u5899\uff0c\u5fae\u5fae\u663e\u5f97\u6709\u70b9\u6004\u502d\u7684\u80cc\u5f71\uff0c \u4e0d\u4e45\u5c31\u6d88\u5931\u5728\u6668\u5149\u66e6\u5fae\u4e4b\u4e2d\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u5446\u82e5\u6728\u9e21\uff0c\u989d\u5934\u6c81\u6c57\uff0c\u8fd9\u56de\u624d\u771f\u6b63\u5c1d\u5230\u4e86\u4f17\u53db\u4eb2\u79bb\u7684\u6ecb\u5473\uff0c\u53ea\u89c9\u5929\u5730\u4e4b\u5927\uff0c\u5df2\u65e0 \u81ea\u5df1\u53ef\u5bb9\u8eab\u4e4b\u5730\uff0c\u832b\u832b\u4eba\u6d77\uff0c\u5df2\u65e0\u518d\u80af\u4eb2\u8fd1\u81ea\u5df1\u4e4b\u4eba\uff0c\u53c8\u60f3\u8d77\u4ee5\u540e\u6d17\u8863\u505a\u996d\u90fd\u8981\u81ea\u5df1\u5e72\u4e86\uff0c \u66f4\u89c9\u201c\u82f1\u96c4\u201d\u672b\u8def\uff0c\u557c\u7b11\u7686\u975e\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u8d70\u8fdb\u57ce\u697c\uff0c\u679c\u7136\u89c1\u6709\u4e00\u9505\u70ed\u996d\uff0c\u8fd9\u65f6\u624d\u53d1\u89c9\u81ea\u5df1\u4e5f\u997f\u5f97\u8f6f\u4e86\uff0c\u80e1\u4e71\u7684\u628a\u4e00\u9505\u70ed\u996d \u5403\u5b8c\uff0c\u8bd5\u8bd5\u8fd0\u52a8\u56db\u80a2\uff0c\u59cb\u77e5\u81ea\u5df1\u6b66\u529f\u867d\u7136\u6d88\u5931\uff0c\u5374\u8fd8\u6709\u5e73\u5e38\u4eba\u7684\u6c14\u529b\uff0c\u770b\u7740\u90a3\u51e0\u5757\u77f3\u5934\u6ce5\u58eb \u642d\u8d77\u7684\u571f\u708a\uff0c\u82e6\u7b11\u4e00\u58f0\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u82e5\u7136\u8fde\u505a\u996d\u7684\u6c14\u529b\u90fd\u6ca1\u6709\u4e86\uff0c\u5c82\u4e0d\u66f4\u662f\u7cdf \u7cd5\uff1f\u201d\u53ef\u662f\u751f\u7c73\u600e\u6837\u624d\u80fd\u716e\u6210\u4e00\u9505\u719f\u996d\uff0c\u8fd9\u4e2a\u5e74\u7fb9\u5c27\u5374\u4e0d\u77e5\u9053\uff0c\u751a\u662f\u53d1\u6101\u3002 \u3000\u3000\u66d9\u5149\u6e10\u9732\uff0c\u5929\u5df2\u9ece\u660e\uff0c\u53c8\u8be5\u662f\u4e0b\u53bb\u770b\u5b88\u57ce\u95e8\u7684\u65f6\u5019\u4e86\u3002\u5e74\u7fb9\u5c27\u6b65\u51fa\u57ce\u697c\uff0c\u8d70\u4e0b\u57ce\u5899\uff0c\u5f80 \u65e5\u8fd8\u6709\u8001\u5175\u76f8\u4f34\uff0c\u4eca\u671d\u53ea\u6709\u81ea\u5df1\u4e00\u4eba\uff0c\u66f4\u89c9\u5f97\u51c4\u51c9\u5bc2\u5bde\uff0c\u5e73\u751f\u884c\u4e8b\uff0c\u970e\u7136\u95f4\u4e00\u4e00\u4ece\u5fc3\u5934\u7ffb \u8fc7\uff0c\u4e00\u79cd\u6094\u6068\u4e4b\u5ff5\u4e0d\u89c9\u6cb9\u7136\u800c\u751f\uff0c\u4f46\u4e00\u5ffd\u95f4\u53c8\u88ab\u6124\u6068\u7684\u60c5\u7eea\u6240\u66ff\u4ee3\uff0c\u6068\u4e0d\u5f97\u628a\u8fd9\u5b87\u5b99\u8fde\u540c\u81ea \u5df1\u4e00\u9f50\u6bc1\u706d\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u8d70\u4e0b\u57ce\u5899\uff0c\u6253\u5f00\u57ce\u95e8\uff0c\u6653\u98ce\u6251\u9762\uff0c\u968f\u7740\u5439\u8fdb\u6765\u7684\u662f\u4e00\u58f0\u6e05\u8106\u7684\u7b11\u58f0\uff0c\u53ea\u89c1\u4e00\u4e2a\u5c11 \u5973\u7b11\u76c8\u76c8\u7684\u7ad9\u5728\u57ce\u95e8\u4e4b\u5916\uff0c\u5e74\u7fb9\u5c27\u4e00\u6253\u5f00\u57ce\u95e8\uff0c\u5979\u4fbf\u8bf4\u9053\uff0c\u201c\u5e74\u5927\u5c06\u519b\uff0c\u4f60\u597d\u65e9\u554a\uff01\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u5239\u90a3\uff0c\u7adf\u7591\u5fc3\u81ea\u5df1\u662f\u5728\u4f5c\u68a6\uff0c\u63c9\u63c9\u773c\u775b\uff0c\u770b\u6e05\u695a\u4e86\u679c\u7136\u662f\u51af\u7433\u3002\u5e74 \u7fb9\u5c27\u9762\u4e0a\u63a0\u8fc7\u4e00\u4e1d\u7b11\u610f\uff0c\u5ffd\u53c8\u6124\u7136\u8bf4\u9053\uff1a\u201c\u51af\u7433\uff0c\u4f60\u4e5f\u6765\u5632\u5f04\u6211\u4e48\uff1f\u201d \u3000\u3000\u51af\u7433\u548c\u674e\u6cbb\u8fd9\u4e24\u5e74\u6765\u5728\u56db\u5ddd\u5192\u4e86\u8bb8\u591a\u8270\u9669\uff0c\u8054\u7edc\u4e86\u4e00\u4e9b\u4eba\uff0c\u540e\u6765\u542c\u5f97\u5e74\u7fb9\u5c27\u88ab\u64a4\u804c\u67e5 \u529e\uff0c\u4fbf\u628a\u56db\u5ddd\u7684\u57fa\u4e1a\u4ea4\u7ed9\u8f66\u9f0e\u4e30\u7684\u513f\u5b50\u8f66\u54f2\u751f\u4e3b\u7406\uff0c\u4e24\u4eba\u8d76\u56de\u53bb\u60f3\u627e\u5415\u56db\u5a18\u3002\u9014\u4e2d\u53c8\u542c\u5f97\u5e74 \u7fb9\u5c27\u8fde\u964d\u5341\u516b\u7ea7\uff0c\u88ab\u8d2c\u5230\u676d\u5dde\u5b88\u57ce\u95e8\u7684\u6d88\u606f\uff0c\u51af\u7433\u8fd9\u65f6\u867d\u7136\u5df2\u662f\u4e8c\u5341\u5c81\u7684\u5927\u59d1\u5a18\u4e86\uff0c\u5b69\u5b50\u7684 \u813e\u6c14\u4ecd\u7136\u672a\u6539\uff0c\u60f3\u8d77\u5c0f\u65f6\u5019\u66fe\u4e0e\u5e74\u7fb9\u5c27\u540c\u73a9\u7684\u4e8b\uff0c\u53c8\u60f3\u8d77\u5e74\u7fb9\u5c27\u9a97\u5979\u54c4\u5979\uff0c\u60f3\u628a\u5979\u9001\u7ed9\u96cd\u6b63 \u4e4b\u4e8b\uff0c\u4e00\u65f6\u5174\u8d77\uff0c\u8981\u5230\u676d\u5dde\u6765\u770b\u770b\u5e74\u7fb9\u5c27\uff0c\u770b\u770b\u8fd9\u4f4d\u201c\u5927\u5c06\u519b\u201d\u662f\u4e0d\u662f\u771f\u7684\u5728\u628a\u5b88\u57ce\u95e8\uff0c\u674e \u6cbb\u62d7\u5979\u4e0d\u8fc7\uff0c\u4fbf\u66ff\u5979\u5728\u95e8\u5916\u628a\u98ce\uff0c\u8ba9\u51af\u7433\u5355\u72ec\u53bb\u548c\u5e74\u7fb9\u5c27\u4f1a\u9762\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u6069\u6028\u81ea\u968f\u6d41\u6c34\u53bb\uff0c\u76f8\u9022\u4eca\u5df2\u9694\u4e91\u6ce5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u56db\u5341\u516d\u56de\u3000\u672b\u8def\u7a77\u9014\u3000\u529f\u540d\u968f\u901d\u6c34\u3000\u8352\u5c71\u53e4\u5239\u3000\u5251\u6c14\u5c04\u5bd2\u661f \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u56db\u5341\u516d\u56de \u672b\u8def\u7a77\u9014\u3000\u529f\u540d\u968f\u901d\u6c34 \u8352\u5c71\u53e4\u5239\u3000\u5251\u6c14\u5c04\u5bd2\u661f \u3000\u3000\u51af\u7433\u683c\u683c\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u6211\u5632\u5f04\u4f60\uff0c\u662f\u4f60\u81ea\u5df1\u5632\u5f04\u81ea\u5df1\u3002\u4eba\u5fc5\u81ea\u6094\u800c\u540e\u4eba\u6094\u4e4b\uff0c\u8fd9 \u53e5\u8bdd\u96be\u9053\u4f60\u8fd8\u4e0d\u61c2\u5417\uff1f\u4f60\u81ea\u4f5c\u81ea\u53d7\uff0c\u73b0\u5728\u8fd8\u672a\u540e\u6094\u5417\uff1f\u201d\u5e74\u7fb9\u5c27\u9ed8\u7136\u4e0d\u8bed\uff0c\u51af\u7433\u9762\u8272\u4e00\u8f6c\uff0c \u5ffd\u7136\u4e00\u672c\u6b63\u7ecf\u7684\u8bf4\u9053\uff1a\u201c\u82e5\u7136\u4f60\u80fd\u8bb0\u7740\u949f\u6069\u5e08\u7684\u6559\u8bad\uff0c\u4f60\u4e5f\u4e0d\u81f3\u4e8e\u6709\u4eca\u65e5\uff01\u201d\u5e74\u7fb9\u5c27\u4e0d\u89c9\u4e00 \u6014\uff0c\u53ea\u542c\u5f97\u51af\u7433\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4ee5\u524d\u7684\u4e8b\uff0c\u6211\u5168\u90fd\u77e5\u9053\u4e86\uff0c\u4f60\u5bb6\u66fe\u6536\u5bb9\u8fc7\u6211\uff0c\u8fd9\u4e00\u70b9\u6211\u8be5\u611f \u6fc0\u3002\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u4e00\u6014\u9053\uff1a\u201c\u4f60\u90fd\u8bb0\u8d77\u6765\u4e86\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u90fd\u8bb0\u8d77\u6765\u4e86\u3002\u4f60\u5c0f\u65f6\u5019\u4e5f\u5f3a\u6a2a\u9738\u9053\uff0c\u4f46 \u5bf9\u6211\u5c1a\u8fd8\u4e0d\u5dee\u3002\u201d\u5e74\u7fb9\u5c27\u559c\u9053\uff1a\u201c\u662f\u554a\uff01\u6211\u4e00\u5411\u628a\u4f60\u5f53\u4f5c\u4eb2\u59b9\u59b9\u4e00\u6837\uff0c\u5bf9\u4efb\u4f55\u4eba\u90fd\u6ca1\u6709\u5bf9\u4f60 \u90a3\u6837\u597d\uff0c\u4f60\u77e5\u9053\u5c31\u597d\u4e86\u3002\u8c22\u8c22\u4f60\u6765\u770b\u6211\uff0c\u6211\u5e74\u7fb9\u5c27\u4ed6\u65e5\u7eb5\u7136\u788e\u5c38\u4e07\u6bb5\uff0c\u5f97\u4e00\u77e5\u5df1\u4e5f\u53ef\u65e0\u61be \u4e86\u3002\u201d\u51af\u7433\u7a81\u7136\u4e00\u9635\u51b7\u7b11\uff0c\u65cb\u53c8\u6c89\u75db\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u4f60\u8d8a\u5927\u5c31\u8d8a\u574f\uff0c\u574f\u5230\u4e0d\u53ef\u6536\u62fe\uff01\u54fc\uff0c\u4f60\u8fd8 \u8bb0\u4e0d\u8bb0\u5f97\uff0c\u4f60\u8981\u628a\u6211\u9001\u7ed9\u7687\u5e1d\uff0c\u597d\u4fdd\u969c\u4f60\u7684\u529f\u540d\uff1f\u6211\u4e0d\u4f9d\u4ece\uff0c\u4f60\u5c31\u6697\u4e2d\u5077\u4e0b\u6bd2\u624b\u5bb3\u6211\uff0c\u4e0d\u662f \u6211\u7684\u674e\u6cbb\u54e5\u54e5\u6551\u6211\uff0c\u6211\u8fd9\u6761\u5c0f\u547d\u65e9\u5df2\u5b8c\u4e86\u3002\u4ec0\u4e48\u4eb2\u59b9\u59b9\uff1f\u4f60\u4e0d\u6015\u5f15\u8d77\u6211\u7684\u6076\u5fc3\u4e48\uff1f\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u9762\u4e0a\u4e00\u9635\u7ea2\u4e00\u9635\u9752\uff0c\u4f4e\u5934\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u6211\u77e5\u9519\u4e86\u3002\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u5bf9\u6211\u4e0d\u597d\uff0c\u8fd9 \u4e5f\u8fd8\u7f62\u4e86\uff0c\u6700\u4e0d\u8be5\u7684\u662f\u949f\u6069\u5e08\u8d39\u5c3d\u5fc3\u8840\uff0c\u57f9\u4f60\u6210\u6750\uff0c\u4f60\u5374\u5f15\u72fc\u5165\u5ba4\uff0c\u5c06\u4ed6\u5bb3\u6b7b\uff01\u82e5\u975e\u4f60\u5df2\u662f \u96be\u9003\u4e00\u6b7b\uff0c\u6211\u4eca\u65e5\u4fbf\u8981\u4e3a\u672c\u95e8\u60e9\u6cbb\u5978\u5f92\uff01\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u5ffd\u5730\u62ac\u8d77\u773c\u775b\uff0c\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5f18\u6cd5\u5927\u5e08\u6240\u8bf4\u7684\u65e0\u6781\u6d3e\u4f20\u4eba\u4fbf\u662f\u4f60\u8fb9\u4e2a\u5c0f\u4e2b \u5934\u3002\u201d\u51af\u7433\u7709\u6bdb\u4e00\u626c\uff1a\u9053\uff1a\u201c\u600e\u4e48\uff0c\u6211\u4e0d\u914d\u4e48\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u5b89\u5fc3\u505a\u5427\u3002\u6211\u8fd9\u4e48\u5927\u7684\u5bcc \u8d35\u529f\u540d\uff0c\u5168\u90fd\u4e22\u4e86\u3002\u96be\u9053\u8fd8\u4f1a\u4e0e\u4f60\u4e89\u533a\u533a\u4e00\u4e2a\u638c\u95e8\u7684\u4f4d\u7f6e\u4e48\uff1f\u201d\u51af\u7433\u53cc\u7709\u7d27\u76b1\uff0c\u6447\u4e86\u6447\u5934\uff0c \u9053\uff1a\u201c\u6211\u771f\u8fd8\u672a\u89c1\u8fc7\u81f3\u6b7b\u4e0d\u609f\u7684\u4eba\uff0c\u5f00\u53e3\u5bcc\u8d35\uff0c\u95ed\u53e3\u529f\u540d\uff0c\u4f60\u53e3\u8bf4\u4e0d\u5728\u4e4e\uff0c\u5176\u5b9e\u5728\u4e4e\u5f97\u5f88\u3002 \u5415\u59d0\u59d0\u66fe\u5bf9\u6211\u8c08\u8bba\u8fc7\u4f60\uff0c\u8bf4\u4f60\u672c\u6765\u7b97\u5f97\u662f\u4e2a\u4eba\u6750\uff0c\u53ea\u662f\u88ab\u2018\u540d\u5229\u2019\u4e8c\u5b57\u6240\u65ad\u9001\u4e86\u3002\u6211\u4ee5\u524d\u8fd8 \u4e0d\u5927\u61c2\uff0c\u73b0\u5728\u770b\u6765\uff0c\u771f\u771f\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e24\u4eba\u4ea4\u8c08\u7247\u523b\uff0c\u5929\u8272\u5df2\u7ecf\u5927\u767d\uff0c\u897f\u6e56\u4e0a\u6e14\u821f\u6653\u5531\uff0c\u9690\u9690\u4f20\u6765\u4e86\u91c7\u83f1\u7684\u6b4c\u58f0\uff0c\u674e\u6cbb\u8fdc\u8fdc\u7684 \u5439\u4e86\u4e00\u58f0\u53e3\u54e8\uff0c\u51af\u7433\u9053\uff1a\u201c\u55ef\uff0c\u6211\u8be5\u8d70\u4e86\uff01\u201d\u5e74\u7fb9\u5c27\u7ad6\u8d77\u8033\u6735\uff0c\u5ffd\u9053\uff1a\u201c\u8c01\u548c\u4f60\u540c\u6765\uff1f\u201d\u51af \u7433\u9053\uff1a\u201c\u4f60\u7ba1\u8fd9\u4e2a\u5e72\u561b\uff1f\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u662f\u4e0d\u662f\u90a3\u4e2a\u53eb\u505a\u674e\u6cbb\u7684\u5c0f\u5b50\uff01\u201d\u51af\u7433\u6124\u7136\u8bf4\u9053\uff1a \u201c\u4ec0\u4e48\u5c0f\u5b50\uff1f\u4ed6\u6bd4\u4f60\u597d\u5f97\u591a\uff01\u201d\u63d0\u9ad8\u55d3\u5b50\u5e94\u9053\uff1a\u201c\u55ef\uff0c\u674e\u6cbb\u54e5\u54e5\uff0c\u6211\u5c31\u6765\u4e86\uff01\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u9762\u4e0a\u9732\u51fa\u4e00\u79cd\u5947\u5f02\u7684\u795e\u60c5\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u7433\u59d1\u5a18\uff0c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\uff0c\u4ee5\u524d\u54b1\u4eec\u7684\u56ed \u5b50\u91cc\u6709\u4e00\u4e2a\u6c60\u5858\uff0c\u6c60\u5858\u91cc\u517b\u6709\u4e00\u5bf9\u9e33\u9e2f\uff0c\u4f60\u5c0f\u65f6\u5019\uff0c\u4e2a\u5b50\u4e0d\u591f\u9ad8\uff0c\u8981\u6211\u62b1\u8d77\u4f60\u6765\u770b\u6c60\u5858\u91cc\u7684 \u9e33\u9e2f\u620f\u6c34\u3002\u201d\u51af\u7433\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5374\u6c89\u7740\u9762\u9053\uff1a\u201c\u4f60\u5c3d\u8bf4\u8fd9\u4e9b\u65e0\u804a\u7684\u8bdd\u513f\u5e72\u561b\uff1f\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u60f3\u8d77\u8fd9\u4e9b\u5c0f\u65f6\u5019\u7684\u4e8b\u60c5\uff0c\u6211\u771f\u662f\u540e\u6094\u5f97\u5f88\u3002\u201d\u51af\u7433\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u540e\u6094\u5df2\u7ecf \u8fdf\u4e86\uff01\u201d\u5e74\u7fb9\u5c27\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u4f5c\u51fa\u6b32\u8bf4\u8fd8\u4f11\u7684\u6837\u513f\uff0c\u51af\u7433\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\uff0c\u8d76\u5feb\u8bf4\u5427\uff01 \u6211\u771f\u7684\u8981\u8d70\u4e86\u3002\u201d\u8bed\u8c03\u6e10\u8f6c\u67d4\u548c\uff0c\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u6211\u4f46\u613f\u80fd\u518d\u548c\u4f60\u540c\u5728\u4e00\u5904\u3002\u60f3\u6211\u5e7c\u8bfb\u5175\u4e66\uff0c \u591a\u5c11\u61c2\u5f97\u4e9b\u884c\u519b\u7528\u5175\u4e4b\u9053\uff0c\u4f60\u4eec\u4ed6\u65e5\u82e5\u4e3e\u4e49\u5e08\uff0c\u6211\u613f\u4f5c\u6bdb\u9042\u81ea\u8350\u3002\u201d\u51af\u7433\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fc3\u9053\uff1a \u201c\u5e74\u7fb9\u5c27\u81ea\u662f\u4e00\u4e2a\u5c06\u624d\uff0c\u82e5\u4ed6\u662f\u771f\u5fc3\u8bda\u610f\u7684\u8bdd\uff0c\u5012\u4e5f\u672a\u5c1d\u4e0d\u53ef\u8003\u8651\u3002\u4e0d\u5982\u5f85\u6211\u548c\u674e\u6cbb\u54e5\u54e5\u5546 \u8bae\uff0c\u770b\u662f\u5982\u4f55\uff1f\u201d\u51af\u7433\u4f4e\u9996\u6c89\u601d\uff0c\u5e74\u7fb9\u5c27\u53c8\u9053\uff1a\u201c\u4f60\u4e0d\u76f8\u4fe1\u6211\u4e48\uff1f\u201d\u51af\u7433\u62ac\u8d77\u5934\u6765\uff0c\u548c\u5e74\u7fb9 \u5c27\u7684\u773c\u5149\u89e6\u4e2a\u6b63\u7740\uff0c\u5ffd\u800c\u5fc3\u4e2d\u4e00\u51db\uff0c\u53ea\u89c9\u5e74\u7fb9\u5c27\u7684\u773c\u5149\u4e2d\u4f3c\u4e4e\u542b\u7740\u65e0\u9650\u5978\u8bc8\uff0c\u4e1d\u6beb\u4e0d\u80fd\u4ee4\u4eba \u4fe1\u8d56\uff0c\u5e74\u7fb9\u5c27\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u771f\u662f\u4e0d\u4fe1\u6211\u4e86\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u4f60\u80fd\u540e\u6094\u5f88\u597d\uff0c\u4f46\u8fd9 \u4e8b\u6211\u4e0d\u80fd\u4f5c\u4e3b\uff0c\u5f85\u6211\u89c1\u4e86\u5415\u59d0\u540e\u518d\u66ff\u4f60\u8bf4\u3002\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u5fc5\u8bf4\u4e86\u3002\u201d\u51af\u7433\u4e3e\u6b65\u6b32 \u8d70\uff0c\u5e74\u7fb9\u5c27\u53c8\u53eb\u9053\uff1a\u201c\u7433\u59d1\u5a18\uff0c\u8fd8\u6709\u4e00\u4ef6\u5c0f\u5c0f\u7684\u4e8b\u60c5\u3002\u201d\u51af\u7433\u8f6c\u8eab\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5feb \u8bf4\uff01\u201d\u5e74\u7fb9\u5c27\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u505a\u4e86\u65e0\u6781\u6d3e\u7684\u638c\u95e8\u5417\uff1f\u90a3\u4e48\u8fd9\u628a\u5251\u4f60\u5e94\u8be5\u62ff\u53bb\uff0c\u8fd9\u662f\u5085\u5e08\u7956\u5f53\u5e74 \u7528\u7684\u5b9d\u5251\uff01\u201d\u51af\u7433\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u672c\u8be5\u8ffd\u7f34\u56de\u5e08\u5c0a\u7684\u9057\u7269\uff0c\u600e\u4e48\u5012\u53cd\u5fd8\u4e86\uff01\u201d\u8d70\u5230\u5e74\u7fb9\u5c27\u8ddf \u524d\uff0c\u4f38\u624b\u63a5\u5251\u3002\u4e0d\u6599\u5e74\u7fb9\u5c27\u8d81\u5979\u53cc\u624b\u4f38\u51fa\uff0c\u80f8\u524d\u95e8\u6237\u5927\u5f00\u4e4b\u9645\uff0c\u500f\u7136\u9a88\u6307\u4e00\u6233\uff0c\u201c\u5f97\u201d\u7684\u4e00 \u58f0\uff0c\u6b63\u6b63\u70b9\u4e2d\u5979\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\uff0c\u8fd9\u201c\u7487\u7391\u7a74\u201d\u4e43\u662f\u4eba\u8eab\u6b7b\u7a74\u4e4b\u4e00\uff0c\u82e5\u88ab\u70b9\u4e2d\uff0c\u7acb\u523b\u8eab\u4ea1\u3002 \u3000\u3000\u539f\u6765\u5e74\u7fb9\u5c27\u81ea\u77e5\u5fc5\u6b7b\uff0c\u4e00\u5207\u7edd\u671b\uff0c\u5df2\u8fd1\u75af\u72c2\uff0c\u60ef\u4e0d\u5f97\u4e16\u754c\u548c\u4ed6\u4e00\u540c\u6bc1\u706d\uff0c\u5c24\u5176\u542c\u51af\u7433\u4e24 \u6b21\u63d0\u8d77\u201c\u674e\u6cbb\u54e5\u54e5\u201d\uff0c\u66f4\u662f\u53c8\u5992\u53c8\u6068\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u5982\u82b1\u4f3c\u7389\u7684\u5c0f\u59d1\u5a18\uff0c\u65e2\u4e0d\u80fd\u4e3a\u6211\u6240 \u6709\uff0c\u6211\u4e5f\u5c31\u4e0d\u80fd\u8ba9\u5979\u4e3a\u522b\u4eba\u6240\u6709\u3002\u6211\u7684\u6b66\u529f\u867d\u5931\uff0c\u70b9\u7a74\u7684\u65b9\u6cd5\u5374\u8fd8\u8bb0\u5f97\uff0c\u4f55\u4e0d\u5c06\u5979\u6740\u4e86\uff0c\u7136 \u540e\u518d\u884c\u81ea\u520e\u3002\u201d \u3000\u3000\u4e0d\u6599\u51af\u7433\u7684\u7a74\u9053\u867d\u88ab\u70b9\u4e2d\uff0c\u5374\u53ea\u662f\u8eab\u8eaf\u6447\u6643\u4e86\u4e24\u4e0b\uff0c\u5e76\u4e0d\u5982\u5e74\u7fb9\u5c27\u6240\u613f\uff0c\u5012\u5730\u8eab\u4ea1\u3002\u539f \u6765\u70b9\u7a74\u7684\u529f\u592b\uff0c\u5fc5\u987b\u914d\u4ee5\u6307\u5934\u7684\u52b2\u529b\uff0c\u529b\u900f\u6307\u5c16\uff0c\u624d\u80fd\u4f7f\u654c\u4eba\u7684\u8840\u6d41\u7a81\u7136\u505c\u6b62\u3002\u5e74\u7fb9\u5c27\u6b66\u529f \u5df2\u5931\uff0c\u53ea\u6709\u5e73\u5e38\u4eba\u7684\u6c14\u529b\uff0c\u800c\u51af\u7433\u7684\u5185\u529f\u5df2\u6709\u9053\u8be3\uff0c\u82e5\u7136\u9047\u7740\u9ad8\u624b\uff0c\u70b9\u6b63\u7a74\u9053\uff0c\u90a3\u81ea\u662f\u65e0\u6cd5 \u633d\u6551\uff0c\u800c\u4eca\u4e0d\u8fc7\u7b49\u4e8e\u88ab\u666e\u901a\u7684\u4eba\uff0c\u51d1\u5de7\u5728\u7a74\u9053\u4e0a\u6233\u4e86\u4e00\u4e0b\uff0c\u867d\u7136\u4e00\u9635\u75bc\u75db\uff0c\u5374\u662f\u5b89\u5168\u65e0\u4e8b\u3002 \u3000\u3000\u51af\u7433\u88ab\u5e74\u7fb9\u5c27\u51fa\u5176\u4e0d\u610f\u7684\u7528\u529b\u4e00\u6233\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u7acb\u523b\u660e\u767d\u4e86\u8fd9\u662f\u4ec0\u4e48\u4e00\u56de\u4e8b\u60c5\uff0c\u6c14\u5f97\u7389 \u624b\u4e00\u626c\uff0c\u62cd\u62cd\u4e24\u8bb0\u8033\u5149\uff0c\u628a\u5e74\u7fb9\u5c27\u6253\u8dcc\u5730\u4e0a\u3002\u5e74\u7fb9\u5c27\u76ee\u9732\u51f6\u5149\uff0c\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u5410\u51fa\u4e00\u53e3\u8840 \u6c34\uff0c\u4e24\u9897\u5927\u7259\u3002\u51af\u7433\u6c14\u5f97\u8bf4\u4e0d\u51fa\u540e\u6765\uff0c\u8fc7\u4e86\u8bb8\u4e45\uff0c\u624d\u8ff8\u51fa\u4e00\u53e5\u8bdd\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u771f\u662f\u5929\u4e0b \u6700\u72e0\u6bd2\u7684\u4eba\uff01\u201d\u4f38\u624b\u53d6\u4e86\u5e74\u7fb9\u5c27\u7684\u5b9d\u5251\uff0c\u62d4\u51fa\u534a\u622a\uff0c\u5ffd\u53c8\u542c\u5f97\u674e\u6cbb\u50ac\u8d70\u7684\u53e3\u54e8\u4e4b\u58f0\u3002 \u3000\u3000\u51af\u7433\u76ef\u4e86\u5e74\u7fb9\u5c27\u4e00\u773c\uff0c\u6068\u6068\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u6740\u4f60\uff0c\u4f60\u4e5f\u6ca1\u6709\u51e0\u5929\u6d3b\u4e86\uff01\u201d\u98de\u8eab\u8ffd\u4e0a\u674e\u6cbb\uff0c \u674e\u6cbb\u9053\uff1a\u201c\u5929\u8272\u5df2\u7ecf\u5927\u767d\uff0c\u592a\u9633\u4e5f\u5feb\u51fa\u6765\u4e86\uff0c\u4f60\u8fd8\u4e0d\u8d70\uff0c\u4f60\u770b\u90a3\u8fb9\u5df2\u6709\u4eba\u6765\u4e86\uff01\u201d \u3000\u3000\u51af\u7433\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u968f\u7740\u674e\u6cbb\u98de\u5feb\u51fa\u57ce\u3002\u4e00\u53e3\u6c14\u8dd1\u5230\u90ca\u5916\uff0c\u674e\u6cbb\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4e0d\u8ba9\u4f60\u548c\u4ed6\u591a \u8bf4\uff0c\u6211\u60f3\u5e74\u7fb9\u5c27\u65e2\u88ab\u8d2c\u5230\u6b64\u5904\u770b\u5b88\u57ce\u95e8\uff0c\u96cd\u6b63\u8fd9\u53ae\u8bf4\u4e0d\u5b9a\u4f1a\u6d3e\u6709\u9ad8\u624b\u6697\u4e2d\u76d1\u89c6\uff0c\u82e5\u6709\u95ea\u5931\uff0c \u5c82\u975e\u4e0d\u503c\uff1f\u201d\u51af\u7433\u653e\u6162\u811a\u6b65\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u674e\u6cbb\u54e5\u54e5\uff0c\u4f60\u80fd\u539f\u8c05\u6211\u4e48\uff1f\u201d\u674e\u6cbb\u7b11\u9053\uff1a\u201c\u6211\u82e5 \u662f\u80f8\u895f\u72ed\u7a84\u4e4b\u4eba\uff0c\u4e5f\u4e0d\u8ba9\u4f60\u5355\u72ec\u548c\u4ed6\u89c1\u9762\u4e86\u3002\u201d\u51af\u7433\u9762\u6655\u7ea2\u6f6e\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\u3002\u6211 \u662f\u8bf4\uff0c\u662f\u8bf4\u2026\u2026\u55ef\uff0c\u6211\u8001\u5b9e\u5bf9\u4f60\u8bf4\u5427\uff0c\u6211\u4eca\u671d\u5728\u5c06\u89c1\u5e74\u7fb9\u5c27\u4e4b\u65f6\uff0c\u8fd8\u6709\u70b9\u60cb\u60dc\u4e4b\u60c5\u2026\u2026\u201d\u674e \u6cbb\u4e0d\u5f85\u5979\u8bf4\u5b8c\uff0c\u4fbf\u7b11\u7740\u63a5\u9053\uff1a\u201c\u4ed6\u672c\u6765\u662f\u4e2a\u4eba\u6750\uff0c\u5374\u8bef\u5165\u6b67\u9014\uff0c\u81ea\u5bfb\u6bc1\u706d\uff0c\u6211\u4e5f\u4e3a\u4ed6\u60cb\u60dc \u5462\uff0c\u8fd8\u6709\u4ec0\u4e48\u503c\u5f97\u63d0\u7684\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u73b0\u5728\uff0c\u6211\u5374\u4e00\u70b9\u4e5f\u4e0d\u60cb\u60dc\u4ed6\u4e86\uff01\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u9762\u8272\u975e \u5e38\u4e25\u8083\uff0c\u548c\u5979\u5e73\u5e38\u987d\u76ae\u7684\u6837\u5b50\u4e0d\u5927\u76f8\u540c\uff0c\u5979\u50cf\u4e00\u4e0b\u5b50\u957f\u6210\u4e86\u201c\u5927\u4eba\u201d\uff0c\u61c2\u5f97\u4e86\u8bb8\u591a\u4e8b\u60c5\u4f3c \u7684\u3002\u674e\u6cbb\u5947\u5f02\u7684\u770b\u4e86\u5979\u4e00\u773c\uff0c\u5bf9\u5979\u7684\u8bdd\u610f\uff0c\u4f3c\u660e\u767d\u53c8\u4f3c\u4e0d\u5927\u660e\u767d\uff0c\u53ea\u8f7b\u8f7b\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u5374\u4e5f \u4e0d\u518d\u591a\u95ee\u3002 \u3000\u3000\u51af\u7433\u8d70\u540e\uff0c\u5e74\u7fb9\u5c27\u6367\u7740\u88ab\u6253\u80bf\u7684\u534a\u8fb9\u8138\uff0c\u6323\u624e\u7740\u5750\u4e86\u8d77\u6765\uff0c\u8fd9\u65f6\u4ed6\u80f8\u4e2d\u7a7a\u7a7a\u6d1e\u6d1e\u7684\uff0c\u795e \u7ecf\u4e5f\u4f3c\u4e4e\u9ebb\u6728\u4e86\uff0c\u65e9\u6668\u7684\u51b7\u98ce\u522e\u5730\u5439\u6765\uff0c\u5e74\u7fb9\u5c27\u6253\u4e86\u4e00\u4e2a\u5bd2\u98a4\uff0c\u53cc\u624b\u4ff8\u7740\u5934\u9885\u6447\u4e86\u51e0\u6447\uff0c\u5583 \u5583\u8bf4\u9053\uff1a\u201c\u5927\u7ea6\u6211\u771f\u7684\u9519\u4e86\uff1f\u201d\u6478\u6478\u5934\u9885\uff0c\u5411\u5929\u72c2\u7b11\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5927\u597d\u5934\u9885\uff0c\u88ab\u4eba\u53d6\u53bb\uff0c \u5c82\u4e0d\u53ef\u60dc\uff01\u695a\u9738\u738b\u4e4c\u6c5f\u4e13\u5bfb\u520e\uff0c\u72b9\u662f\u82f1\u96c4\uff01\u6211\u5c82\u53ef\u4e0d\u5982\u4ed6\uff1f\u4eca\u65e5\u662f\u5929\u4ea1\u6211\u4e5f\uff0c\u65e2\u662f\u5fc5\u6b7b\uff0c\u6211 \u53c8\u4f55\u5fc5\u518d\u6d3b\u7740\u8ba9\u4eba\u51cc\u8fb1\uff1f\u201d\u53cc\u624b\u4ff8\u7740\u5934\u9885\uff0c\u7a81\u7136\u5411\u57ce\u95e8\u4e00\u649e\u3002 \u3000\u3000\u5934\u9885\u672a\u89e6\u57ce\u95e8\uff0c\u5ffd\u7136\u88ab\u4eba\u62b1\u7740\uff0c\u5e74\u7fb9\u5c27\u6323\u624e\u4e0d\u5f97\uff0c\u7741\u773c\u770b\u65f6\uff0c\u5374\u662f\u97e9\u91cd\u5c71\u548c\u5929\u53f6\u6563\u4eba\uff0c \u53ea\u89c1\u8fd9\u4e24\u4eba\u9762\u9752\u5507\u80bf\uff0c\u6837\u5b50\u5f88\u662f\u96be\u770b\u3002\u539f\u6765\u4ed6\u4eec\u8ffd\u8d76\u5370\u5b8f\u4e0e\u5173\u4e1c\u56db\u4fa0\uff0c\u5374\u9047\u7740\u5f18\u6cd5\u5927\u5e08\uff0c\u4e00 \u987f\u7985\u6756\u5c06\u4ed6\u4eec\u6253\u4e86\u56de\u6765\u3002 \u3000\u3000\u97e9\u91cd\u5c71\u548c\u5929\u53f6\u6563\u4eba\u89c1\u5e74\u7fb9\u5c27\u7684\u6837\u5b50\uff0c\u66f4\u89c9\u96be\u770b\uff0c\u97e9\u91cd\u5c71\u9053\uff1a\u201c\u5582\uff0c\u4f60\u7684\u80e1\u5b50\u548c\u7709\u6bdb\u88ab\u8c01 \u5243\u4e86\uff1f\u6211\u4eec\u8d70\u4e86\u4e4b\u540e\uff0c\u6709\u8c01\u6765\u8fc7\uff1f\u201d\u5929\u53f6\u6563\u4eba\u77a5\u89c1\u8f66\u8f9f\u90aa\u7684\u5c38\u8eab\uff0c\u4e5f\u95ee\u9053\uff1a\u201c\u662f\u8c01\u6740\u7684\uff1f\u662f \u4f60\uff0c\u8fd8\u662f\u654c\u4eba\uff1f\u201d\u5e74\u7fb9\u5c27\u54c8\u54c8\u5927\u7b11\uff0c\u5927\u53eb\u9053\uff1a\u201c\u90fd\u6b7b\u4e86\u5e72\u51c0\uff01\u201d\u97e9\u91cd\u5c71\u51b7\u7b11\u9053\uff1a\u201c\u7687\u4e0a\u8fd8\u4e0d \u8bb8\u4f60\u6b7b\u5462\uff01\u201d\u5e74\u7fb9\u5c27\u5927\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u8bb8\u6211\u505a\u695a\u9738\u738b\uff1f\u5440\uff01\u6211\u8fde\u695a\u9738\u738b\u4e5f\u4e0d\u5982\u4e86\uff01\u201d\u624b\u821e\u8db3 \u8e48\uff0c\u8bed\u65e0\u4f26\u6b21\uff0c\u5929\u53f6\u6563\u4eba\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u75af\u4e86\uff01\u201d\u97e9\u91cd\u5c71\u8f7b\u8f7b\u4e00\u63a8\uff0c\u5e74\u7fb9\u5c27\u6beb\u65e0\u53cd\u6297\u7684\u529b\u91cf\uff0c \u503e\u4ec6\u6b32\u5012\u3002\u97e9\u91cd\u5c71\u5403\u60ca\u9053\uff1a\u201c\u8fde\u6b66\u529f\u4e5f\u6ca1\u6709\u4e86\uff01\u201d\u5929\u53f6\u6563\u4eba\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u65e2\u7136\u6210\u4e86\u8fd9\u4e2a\u6837 \u5b50\uff0c\u54b1\u4eec\u8fd8\u662f\u8d76\u5feb\u628a\u4ed6\u62bc\u56de\u4eac\u5e08\u53bb\u5427\u3002\u201d\u97e9\u91cd\u5c71\u70b9\u4e86\u70b9\u5934\uff0c\u5f53\u65e5\u5c31\u7528\u516b\u767e\u91cc\u5feb\u9a6c\u52a0\u7d27\uff0c\u98de\u62a5 \u7687\u5e1d\uff0c\u7b2c\u4e8c\u65e5\u4fbf\u62bc\u4ed6\u4e0a\u4eac\uff0c\u6709\u4ed6\u4e8c\u4eba\u62bc\u89e3\uff0c\u5e74\u7fb9\u5c27\u5c31\u662f\u60f3\u81ea\u6740\u4e5f\u4e0d\u6210\u4e86\u3002\u53ea\u662f\u4e00\u8def\u4e0a\u80e1\u8a00\u4e71 \u8bed\uff0c\u6709\u65f6\u5019\u547c\u5524\u513f\u5b50\uff0c\u6709\u65f6\u53c8\u5927\u53eb\u51af\u7433\u3002 \u3000\u3000\u5e74\u7fb9\u5c27\u72c2\u6027\u5927\u53d1\u4e4b\u65f6\uff0c\u51af\u7433\u5df2\u79bb\u5f00\u676d\u5dde\u4e94\u516d\u5341\u91cc\uff0c\u51af\u7433\u5e76\u672a\u6599\u5230\u4ed6\u4f1a\u53d1\u75af\uff0c\u60f3\u8d77\u4ed6\u4e11\u6076 \u7684\u6837\u5b50\uff0c\u8fd8\u662f\u89c9\u5f97\u4e00\u9635\u9635\u6076\u5fc3\u3002\u674e\u6cbb\u4e00\u70b9\u4e5f\u4e0d\u95ee\u5979\u89c1\u5e74\u7fb9\u5c27\u7684\u7ecf\u8fc7\uff0c\u53ea\u662f\u4e00\u8def\u7528\u8bf4\u8bdd\u9017\u5979\u5f00 \u5fc3\uff0c\u51af\u7433\u6e10\u6e10\u4e5f\u6709\u8bf4\u6709\u7b11\u4e86\u3002 \u3000\u3000\u674e\u6cbb\u51af\u7433\u6b64\u884c\u7684\u8def\u7ebf\uff0c\u662f\u60f3\u4ece\u6d59\u6c5f\u897f\u5165\u5b89\u5fbd\uff0c\u6298\u5165\u6cb3\u5357\uff0c\u56de\u8f6c\u9099\u5c71\uff0c\u4e24\u4eba\u811a\u7a0b\u751a\u5feb\uff0c\u65e5 \u5934\u672a\u843d\uff0c\u5df2\u5230\u5929\u76ee\u5c71\u533a\uff0c\u6b63\u8f6c\u5165\u5c71\u8def\uff0c\u5ffd\u95fb\u5f97\u5c71\u8c37\u4e0b\u6709\u545c\u545c\u602a\u5578\u3001\u6697\u5668\u5636\u98ce\u7684\u58f0\u97f3\uff0c\u51af\u7433\u53eb \u9053\uff1a\u201c\u8840\u6ef4\u5b50\uff01\u201d\u674e\u6cbb\u767b\u9ad8\u4e00\u671b\uff0c\u9053\uff1a\u201c\u539f\u6765\u662f\u5173\u4e1c\u56db\u4fa0\u88ab\u56f4\u4f4f\u4e86\uff01\u201d\u51af\u7433\u770b\u4e86\u4e00\u770b\uff0c\u9053\uff1a \u201c\u54a6\uff0c\u8fd8\u6709\u65b9\u4eca\u660e\u548c\u9648\u5fb7\u6cf0\u5462\uff0c\u54b1\u4eec\u4e0b\u53bb\u6551\u4ed6\u3002\u201d\u4e24\u4eba\u62d4\u5251\u75be\u5954\u800c\u4e0b\u3002 \u3000\u3000\u539f\u6765\u5f18\u6cd5\u5927\u5e08\u60e9\u6212\u4e86\u5e74\u7fb9\u5c27\u540e\uff0c\u5728\u56de\u7a0b\u4e2d\u53c8\u6253\u8d70\u4e86\u97e9\u91cd\u5c71\u4e0e\u5929\u53f6\u6563\u4eba\uff0c\u5370\u5b8f\u548c\u5c1a\u672c\u6765\u662f \u540c\u5173\u4e1c\u56db\u4fa0\u4e00\u540c\u6765\u7684\uff0c\u800c\u4eca\u4e8b\u60c5\u5df2\u4e86\uff0c\u4fbf\u548c\u4f4f\u6301\u540c\u56de\u798f\u5efa\u5c11\u6797\u5bfa\uff0c\u5173\u4e1c\u56db\u4fa0\u5219\u5f80\u9099\u5c71\u627e\u7518\u51e4 \u6c60\u548c\u5415\u56db\u5a18\u3002 \u3000\u3000\u81f3\u4e8e\u65b9\u4eca\u660e\u548c\u9648\u5fb7\u6cf0\u5219\u662f\u5728\u9014\u4e2d\u76f8\u9047\u7684\uff0c\u65b9\u4eca\u660e\u81ea\u90a3\u6b21\u5728\u96ea\u9b42\u8c37\u6076\u6218\u4e4b\u540e\uff0c\u4e0e\u9648\u5fb7\u6cf0\u4e00 \u9053\u517b\u4f24\uff0c\u6210\u4e3a\u597d\u53cb\uff0c\u8fd9\u6b21\u65b9\u4eca\u660e\u6765\u627e\u5e74\u7fb9\u5c27\uff0c\u8981\u4e3a\u201c\u6545\u4e3b\u201d\u62a5\u4ec7\u51fa\u6c14\uff0c\u9648\u5fb7\u6cf0\u963b\u4ed6\u4e0d\u4f4f\uff0c\u53ea \u5f97\u8d76\u6765\u63a5\u5e94\uff0c\u65b9\u4eca\u660e\u88ab\u8f66\u8f9f\u90aa\u8d76\u8d70\uff0c\u5782\u5934\u4e27\u6c14\uff0c\u591c\u51fa\u676d\u57ce\u3002\u9648\u5fb7\u6cf0\u8fce\u7740\u4ed6\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff0c\u89c1 \u7740\u4e86\u5e74\u7fb9\u5c27\u6ca1\u6709\uff1f\u201d\u65b9\u4eca\u660e\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u89c1\u662f\u89c1\u7740\u4e86\uff0c\u4f46\u8fd9\u4e2a\u4ec7\u6211\u4e5f\u4e0d\u518d\u60f3\u62a5\u4e86\u3002\u201d\u9648\u5fb7 \u6cf0\u4ee5\u4e3a\u4ed6\u662f\u5403\u4e86\u8d25\u4ed7\u4f24\u5fc3\uff0c\u6b63\u60f3\u51fa\u8a00\u76f8\u6170\uff0c\u65b9\u4eca\u660e\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u8bf4\u5f97\u4e0d\u9519\uff0c\u5341\u56db\u8d1d\u52d2\u5e76\u4e0d\u503c \u5f97\u6211\u4e3a\u4ed6\u5356\u547d\uff01\u201d\u9648\u5fb7\u6cf0\u5947\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff1f\u4f60\u76f8\u4fe1\u4ed6\u4e86\uff1f\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u6211\u4e0d\u76f8 \u4fe1\uff0c\u4ed6\u6628\u665a\u8bf4\u7684\u5374\u4e0d\u5bb9\u6211\u4e0d\u76f8\u4fe1\u3002\u201d\u5c06\u5e74\u7fb9\u5c27\u6240\u8bf4\u7684\uff0c\u5173\u4e8e\u5341\u56db\u7687\u5b50\u7684\u9634\u72e0\u624b\u6bb5\uff0c\u4ee5\u201c\u65d7\u201d \u5236\u201c\u6c49\u201d\u7b49\u7b49\u6076\u8ff9\u8f6c\u8ff0\u51fa\u6765\uff0c\u9648\u5fb7\u6cf0\u5927\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4ee5\u524d\u529d\u8fc7\u4f60\uff0c\u4f60\u4e0d\u542c\u3002\u60f3\u4e0d\u5230\u5e74\u7fb9\u5c27\u5012 \u505a\u4e86\u4f60\u7684\u6559\u5e08\u4e86\u3002\u201d\u65b9\u4eca\u660e\u9893\u7136\u4e0d\u8bed\uff0c\u8fd9\u4e5f\u96be\u602a\uff0c\u4ed6\u53d1\u73b0\u4e86\u5341\u591a\u5e74\u6765\uff0c\u4ed6\u8981\u5c3d\u5fe0\u7684\u201c\u4e3b \u5b50\u201d\uff0c\u7ade\u662f\u5168\u4e0d\u503c\u5f97\u5c3d\u5fe0\u7684\u4eba\uff0c\u4e5f\u5c31\u96be\u514d\u4f24\u5fc3\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u5728\u8def\u4e0a\u9047\u5230\u5173\u4e1c\u56db\u4fa0\uff0c\u63d0\u5230\u524d\u5f80\u9099\u5c71\u4e4b\u4e8b\uff0c\u65b9\u4eca\u660e\u6168\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e5f\u53bb\uff01\u201d\u9648 \u5fb7\u6cf0\u7b11\u9053\uff1a\u201c\u4f60\u53bb\u505a\u4ec0\u4e48\uff1f\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u548c\u4f60\u4eec\u4e00\u8d77\u53bb\u62a5\u4ec7\u5440\u3002\u201d\u9648\u5fb7\u6cf0\u9053\uff1a\u201c\u4f60\u53c8\u8bf4\u8fd9 \u4ec7\u4e0d\u518d\u62a5\uff1f\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u8fd9\u56de\u4e0d\u662f\u4e3a\u5341\u56db\u7687\u5b50\u62a5\u4ec7\uff0c\u662f\u4e3a\u6211\u4eec\u6c49\u65cf\u81ea\u5df1\u4eba\u62a5\u4ec7\u5440\uff01\u6211\u4ee5\u524d \u6068\u6781\u96cd\u6b63\u8fd9\u5c0f\u5b50\uff0c\u73b0\u5728\u4e5f\u6068\u6781\u4ed6\uff0c\u4f46\u4ee5\u524d\u4e4b\u6068\u548c\u73b0\u5728\u4e4b\u6068\u53c8\u4e0d\u540c\u4e86\uff01\u201d\u9648\u5fb7\u6cf0\u70b9\u70b9\u5934\u9053\uff1a \u201c\u8fd9\u4e2a\u6211\u660e\u767d\uff0c\u4f60\u4e0d\u7528\u591a\u8bf4\u4e86\u3002\u201d \u3000\u3000\u4e8e\u662f\u65b9\u4eca\u660e\u548c\u5173\u4e1c\u56db\u4fa0\u7b49\u4e00\u884c\u4eba\u540c\u5f80\u9099\u5c71\uff0c\u5374\u4e0d\u6599\u96cd\u6b63\u5e03\u7f6e\u4e25\u5bc6\uff0c\u9664\u4e86\u6d3e\u9053\u97e9\u91cd\u5c71\u3001\u5929 \u53f6\u6563\u4eba\u548c\u8f66\u8f9f\u90aa\u7b49\u4eba\u76d1\u89c6\u5e74\u7fb9\u5c27\u4e4b\u5916\uff0c\u53c8\u6d3e\u6709\u54c8\u5e03\u9640\u7387\u9886\u4e00\u73ed\u8840\u6ef4\u5b50\u5728\u901a\u5f80\u676d\u5dde\u7684\u5404\u5904\u8981\u9053 \u548c\u5c71\u9698\u9669\u8981\u4e4b\u5904\u5de1\u903b\uff0c\u4e24\u4e0b\u76f8\u9047\uff0c\u4f17\u5be1\u4e0d\u654c\uff0c\u5173\u4e1c\u56db\u4fa0\u8fd9\u4e00\u73ed\u4eba\u88ab\u903c\u4e0b\u5c71\u8c37\uff0c\u51ed\u7740\u5730\u5f62\uff0c\u8d1f \u9685\u6076\u6597\u3002 \u3000\u3000\u54c8\u5e03\u9640\u662f\u6e05\u5bab\u7684\u7b2c\u4e00\u6d41\u597d\u624b\uff0c\u5389\u5bb3\u975e\u5e38\uff0c\u66f4\u517c\u90a3\u5341\u591a\u540d\u8840\u6ef4\u5b50\u4e5f\u90fd\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u6240\u7528\u7684 \u6697\u5668\u201c\u8840\u6ef4\u5b50\u201d\uff08\u8840\u6ef4\u5b50\u5373\u56e0\u6240\u7528\u6697\u5668\u55bb\u540d\uff09\u5c24\u5176\u5389\u5bb3\uff0c\u7384\u98ce\u7b49\u4eba\u6b66\u529f\u867d\u9ad8\uff0c\u88ab\u56f0\u5728\u5c71\u8c37\u4e4b \u4e2d\uff0c\u5374\u662f\u7a81\u56f4\u4e0d\u51fa\u3002 \u3000\u3000\u6b63\u5728\u5403\u7d27\uff0c\u5ffd\u95fb\u5f97\u5c71\u4e0a\u4e00\u58f0\u53eb\u558a\uff0c\u674e\u6cbb\u51af\u7433\u53cc\u5251\u9f50\u4e0b\uff0c\u54c8\u5e03\u9640\u53c8\u60ca\u53c8\u559c\uff0c\u53eb\u9053\uff1a\u201c\u662f\u7433 \u8d35\u4eba\uff01\u201d\u5c06\u8840\u6ef4\u5b50\u673a\u62ec\u4e00\u5f00\uff0c\u629b\u51fa\u53bb\u76f4\u53d6\u7384\u98ce\uff0c\u53cd\u8eab\u4e00\u8dc3\uff0c\u821e\u6d41\u661f\u6376\u6765\u6355\u6349\u51af\u7433\u3002\u51af\u7433\u7b11 \u9053\uff1a\u201c\u4f60\u73b0\u5728\u8fd8\u60f3\u6765\u6b3a\u8d1f\u6211\u5417\uff1f\u201d\u628a\u624b\u4e00\u626c\uff0c\u4e00\u67c4\u98de\u5200\uff0c\u95ea\u7535\u98de\u53bb\uff0c\u5728\u534a\u7a7a\u4e2d\u4e0e\u54c8\u5e03\u9640\u6240\u53d1 \u7684\u8840\u6ef4\u5b50\u76f8\u78b0\uff0c\u53cc\u53cc\u843d\u5730\uff0c\u5148\u89e3\u4e86\u7384\u98ce\u4e4b\u5371\uff0c\u518d\u8fce\u6218\u54c8\u5e03\u9640\u3002 \u3000\u3000\u51af\u7433\u5f97\u4e86\u65e0\u6781\u6d3e\u7684\u771f\u4f20\uff0c\u6b66\u529f\u5df2\u662f\u5927\u975e\u6614\u6bd4\uff0c\u53ea\u89c1\u5979\u4e0d\u614c\u4e0d\u5fd9\uff0c\u5b9d\u5251\u4e00\u62db\u201c\u529b\u5212\u9e3f\u6c9f\u201d \u6325\u4e86\u534a\u5f27\u5f62\uff0c\u7adf\u7136\u5c06\u54c8\u5e03\u9640\u7684\u6d41\u661f\u9524\u8361\u8fc7\u4e00\u8fb9\uff0c\u54c8\u5e03\u9640\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u600e\u4e48\u6562\u786c \u63a5\u6211\u7684\u795e\u529b\uff1f\u201d\u632f\u81c2\u4e00\u821e\uff0c\u6d41\u661f\u9524\u547c\u7684\u4e00\u54cd\uff0c\u4ece\u5de6\u5230\u53f3\uff0c\u62e6\u8170\u6a2a\u51fb\uff0c\u51af\u7433\u5b9d\u5251\u4e00\u7f29\u5f80\u91cc\u4e00 \u7c98\uff0c\u53c8\u628a\u54c8\u5e03\u9640\u51f6\u731b\u7684\u653b\u52bf\u89e3\u5f00\uff0c\u54c8\u5e03\u9640\u66f4\u662f\u5947\u5f02\uff0c\u5f53\u4e0b\u4e0d\u6562\u5927\u610f\uff0c\u4ee5\u4e00\u9524\u62a4\u8eab\uff0c\u4e00\u9524\u8fce \u654c\uff0c\u7d27\u8ffd\u51af\u7433\u3002 \u3000\u3000\u5176\u5b9e\u51af\u7433\u7684\u529f\u529b\uff0c\u8fd8\u662f\u6bd4\u4e0d\u4e0a\u54c8\u5e03\u9640\uff0c\u5979\u63a5\u4e86\u4e24\u9524\uff0c\u80f3\u818a\u9178\u75db\uff0c\u5e78\u800c\u6240\u4f7f\u7684\u4e43\u662f\u5085\u9752\u4e3b \u5f53\u5e74\u6240\u7528\u7684\u5b9d\u5251\uff0c\u867d\u6bd4\u4e0d\u4e0a\u6e38\u9f99\u65ad\u7389\uff0c\u4e5f\u662f\u4e94\u91d1\u7684\u7cbe\u534e\u6240\u70bc\uff0c\u624d\u4e0d\u81f3\u88ab\u9524\u5934\u6253\u6298\uff0c\u82e5\u7136\u54c8\u5e03 \u9640\u4e00\u8def\u5f3a\u653b\uff0c\u51af\u7433\u8fd8\u771f\u62b5\u6321\u4e0d\u4f4f\u3002\u800c\u4eca\u54c8\u5e03\u9640\u534a\u653b\u534a\u5b88\uff0c\u6b63\u5408\u51af\u7433\u8def\u6570\uff0c\u51af\u7433\u7684\u65e0\u6781\u5251\u6cd5\u521a \u67d4\u76f8\u6d4e\uff0c\u5b88\u5907\u5f97\u5341\u5206\u4e25\u5bc6\uff0c\u66f4\u517c\u51af\u7433\u901a\u6653\u5404\u79cd\u65c1\u95e8\u7684\u6b66\u529f\uff0c\u62db\u5f0f\u5947\u591a\uff0c\u6e9c\u6ed1\u4e4b\u6781\uff0c\u54c8\u5e03\u9640\u5728 \u4e94\u4e03\u5341\u62db\u4e4b\u5185\uff0c\u7adf\u7136\u5948\u4f55\u5979\u4e0d\u5f97\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u674e\u6cbb\u4e5f\u5df2\u8dc3\u5165\u654c\u4eba\u4e1b\u4e2d\uff0c\u4ed6\u7684\u5251\u6cd5\u4e43\u662f\u767d\u53d1\u9b54\u5973\u8fd9\u4e00\u6d3e\u7684\u5ae1\u4f20\uff0c\u5947\u8be1\u8f9b\u8fa3\uff0c\u5929\u4e0b \u65e0\u53cc\uff0c\u51e0\u4e2a\u7167\u9762\uff0c\u5c31\u7ed9\u4ed6\u523a\u4f24\u4e86\u4e24\u540d\u8840\u6ef4\u5b50\uff0c\u7384\u98ce\u7b49\u4eba\u7cbe\u795e\u5927\u632f\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u540c\u65f6\u53cd\u51fb\uff01 \u3000\u3000\u54c8\u5e03\u9640\u88ab\u51af\u7433\u7eca\u4f4f\uff0c\u8840\u6ef4\u5b50\u5931\u4e86\u4e3b\u8111\uff0c\u62e6\u654c\u4eba\u4e0d\u4f4f\uff0c\u7384\u98ce\u5de6\u5251\u53f3\u62d0\uff0c\u6a2a\u6572\u76f4\u626b\uff0c\u6fc0\u6218\u4e2d \u4e00\u5251\u524a\u6389\u4e86\u4e00\u540d\u8840\u6ef4\u5b50\u7684\u5929\u7075\u76d6\uff0c\u4e00\u62d0\u53c8\u6253\u6298\u4e86\u4e00\u540d\u8840\u6ef4\u5b50\u7684\u80eb\u9aa8\uff0c\u6717\u6708\u7985\u5e08\u4e5f\u7528\u9152\u6d6a\u55b7\u778e \u4e86\u51e0\u540d\u8840\u6ef4\u5b50\u7684\u773c\u775b\uff0c\u8840\u6ef4\u5b50\u7eb7\u7eb7\u5927\u547c\uff0c\u593a\u8def\u5954\u8d70\u3002 \u3000\u3000\u54c8\u5e03\u9640\u89c1\u4e0d\u662f\u8def\uff0c\u6025\u5fd9\u820d\u4e86\u51af\u7433\uff0c\u9547\u4f4f\u9635\u811a\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u653e\u6697\u5668\uff01\u201d\u970e\u65f6\u95f4\u53ea\u89c1\u6ee1\u7a7a \u94c1\u7403\u98de\u821e\uff0c\u53d1\u51fa\u60e8\u5389\u7684\u545c\u545c\u602a\u53eb\u4e4b\u58f0\uff0c\u51af\u7433\u53eb\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u5de6\u53f3\u4e24\u624b\uff0c\u5404\u53d1\u516d\u67c4\u98de\u5200\uff0c \u5c06\u5341\u4e8c\u4e2a\u201c\u8840\u6ef4\u5b50\u201d\u6697\u5668\u649e\u843d\u5730\u4e0a\uff0c\u8fd9\u593a\u547d\u98de\u5200\uff0c\u4ee5\u5c0f\u514b\u5927\uff0c\u501f\u529b\u6253\u529b\uff0c\u5728\u534a\u7a7a\u649e\u6bd4\u81ea\u5df1\u4f53 \u79ef\u5927\u7684\u6697\u5668\u4e4b\u6cd5\uff0c\u4e43\u662f\u65e0\u6781\u6d3e\u7684\u72ec\u95e8\u7edd\u6280\uff0c\u5f53\u5e74\u949f\u4e07\u5802\u5c31\u66fe\u4ed7\u8fc7\u8fd9\u95e8\u7edd\u6280\u8131\u51fa\u8840\u6ef4\u5b50\u7684\u91cd \u56f4\uff0c\u51af\u7433\u65bd\u5c55\u51fa\u6765\uff0c\u5f97\u5fc3\u5e94\u624b\uff0c\u5341\u4e8c\u4e2a\u201c\u8840\u6ef4\u5b50\u201d\u843d\u5730\uff0c\u8fd8\u6709\u51e0\u4e2a\u5219\u5206\u522b\u88ab\u7384\u98ce\u674e\u6cbb\u7b49\u6253 \u843d\uff0c\u53ef\u662f\u8fd9\u6837\u7684\u4e00\u9635\u5fd9\u4e71\uff0c\u54c8\u5e03\u9640\u4e5f\u7387\u9886\u90a3\u73ed\u8840\u6ef4\u5b50\u9000\u51fa\u8c37\u53e3\u4e86\u3002 \u3000\u3000\u7384\u98ce\u8d5e\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u98de\u5200\u7edd\u6280\u3002\u201d\u51af\u7433\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u804a\u4ee5\u8d4e\u5f53\u5e74\u8bef\u4f24\u4e4b\u7f6a\u3002\u201d\u51af\u7433 \u521d\u51fa\u9053\u65f6\uff0c\u66fe\u7528\u98de\u5200\u8bef\u4f24\u8fc7\u201c\u56db\u4fa0\u201d\u4e2d\u7684\u9648\u5143\u9738\uff0c\u6240\u4ee5\u6709\u6b64\u4e00\u8a00\u3002\u7384\u98ce\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u70b9\u5c0f \u4e8b\uff0c\u6211\u4eec\u90fd\u65e9\u5df2\u5fd8\u8bb0\u4e86\uff0c\u4e8f\u4f60\u8fd8\u8bb0\u5f97\uff01\u201d\u9648\u5143\u9738\u4e5f\u7b11\u9053\uff1a\u201c\u771f\u662f\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\uff0c\u540e\u8f88\u7684\u82f1 \u96c4\u513f\u5973\uff0c\u6bd4\u54b1\u4eec\u5f3a\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u4e00\u884c\u4eba\u8c08\u8c08\u7b11\u7b11\uff0c\u540c\u5f80\u9099\u5c71\u3002\u7384\u98ce\u95ee\u9053\uff1a\u201c\u4f60\u7684\u59d0\u59d0\u5462\uff1f\u201d\u51af\u7433\u9053\uff1a\u201c\u5979\u4e24\u5e74\u524d\u548c\u5510\u6653 \u6f9c\u540c\u56de\u5929\u5c71\u53bb\u4e86\u3002\u201d\u6717\u6708\u7985\u5e08\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u76f8\u8c8c\u4e4b\u4f3c\uff0c\u771f\u662f\u4e16\u95f4\u5c11\u6709\u3002\u82e5\u4e0d\u662f\u4f60\u521a\u624d\u5148\u8bf4 \u4e86\u90a3\u4e00\u756a\u8bdd\uff0c\u6211\u4eec\u4e5f\u4e0d\u77e5\u9053\u4f60\u662f\u51af\u745b\u8fd8\u662f\u51af\u7433\u3002\u89c1\u4e86\u4f60\uff0c\u6211\u4eec\u5c31\u60f3\u5230\u4f60\u7684\u59d0\u59d0\uff0c\u53ef\u60dc\u5979\u8fdc\u5728 \u5929\u5c71\u3002\u201d\u51af\u7433\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u5ff5\u5979\u5462\u3002\u53ef\u60dc\u8def\u9014\u8fd9\u4e48\u9065\u8fdc\uff0c\u60f3\u6258\u4eba\u634e\u4e2a\u4fe1\u4e5f\u4e0d\u65b9\u4fbf\u3002\u82e5 \u7136\u5979\u5f97\u77e5\u5e74\u7fb9\u5c27\u5931\u52bf\u5f85\u6bd9\u4e4b\u4e8b\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u6b22\u559c\u5462\uff01\u201d \u3000\u3000\u51af\u7433\u53ef\u6ca1\u6709\u6599\u5230\uff0c\u51af\u745b\u548c\u5510\u6653\u6f9c\u8fd9\u65f6\u4e5f\u6b63\u5728\u8d76\u8fd4\u9099\u5c71\u7684\u9014\u4e2d\uff0c\u4e0d\u8fc7\u4e00\u4e2a\u662f\u4ece\u897f\u5317\u51fa\u6765\uff0c \u4e00\u4e2a\u662f\u4ece\u6d59\u6c5f\u897f\u4e0a\uff0c\u5f7c\u6b64\u7684\u8def\u9014\u4e0d\u540c\u7f62\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u7ecf\u8fc7\u4e86\u90a3\u573a\u5927\u53d8\u4e4b\u540e\uff0c\u5fc3\u4e2d\u751a\u662f\u6291\u90c1\uff0c\u56de\u5230\u5929\u5c71\u4e4b\u540e\uff0c\u6c89\u9ed8\u5be1\u8a00\uff0c\u53ea\u662f\u8654\u5fc3\u7ec3 \u5251\uff0c\u6613\u5170\u73e0\u9887\u611f\u5947\u602a\uff0c\u79c1\u4e0b\u91cc\u4e5f\u95ee\u8fc7\u51af\u745b\uff0c\u51af\u745b\u5e76\u4e0d\u9690\u7792\uff0c\u5c06\u4e00\u5207\u90fd\u544a\u8bc9\u4e86\u6613\u5170\u73e0\u3002\u6613\u5170\u73e0 \u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u4eec\u4e03\u5251\u4e4b\u4e2d\uff0c\u5f53\u5e74\u4e5f\u66fe\u6709\u51e0\u4f4d\u7d2f\u4e8e\u60c5\u5b7d\uff0c\u8fde\u4e00\u4ee3\u5947\u4fa0\u7684\u51cc\u672a\u98ce\u53d4\u53d4\u4e5f\u4e0d\u80fd \u514d\u3002\u4f46\u613f\u4f60\u4eec\u5c06\u6765\u4e5f\u50cf\u6211\u7684\u51cc\u53d4\u53d4\u548c\u5218\u90c1\u82b3\u4e00\u6837\uff0c\u5728\u7ecf\u8fc7\u8bb8\u591a\u52ab\u96be\u4e4b\u540e\uff0c\u5316\u9664\u9b54\u969c\u3002\u4e0d\u8fc7\u8fd9 \u79cd\u4e8b\u4e5f\u52c9\u5f3a\u4e0d\u5f97\uff0c\u8001\u662f\u653e\u5728\u5fc3\u5934\uff0c\u53cd\u800c\u82e6\u4e86\u81ea\u5df1\u3002\u201d\u6613\u5170\u73e0\u662f\u8fc7\u6765\u4eba\uff0c\u4e5f\u4e0d\u7528\u8bf4\u8bdd\u53bb\u529d\u5510\u6653 \u6f9c\uff0c\u53ea\u4e13\u5fc3\u6559\u4ed6\u6b66\u827a\uff0c\u6e10\u6e10\u5c06\u4ed6\u7684\u5fc3\u601d\u5f15\u5f00\uff0c\u5510\u6653\u6f9c\u5728\u5929\u5c71\u4f4f\u4e86\u4e00\u5e74\u591a\uff0c\u8865\u4e60\u672c\u95e8\u7684\u6b66\u529f\uff0c \u5c06\u4ee5\u524d\u672a\u66fe\u5b66\u7684\uff0c\u5168\u90fd\u5b66\u4e86\u3002 \u3000\u3000\u4e00\u65e5\uff0c\u6613\u5170\u73e0\u5c06\u5510\u6653\u6f9c\u53eb\u6765\uff0c\u9053\uff1a\u201c\u4f60\u7684\u6b66\u529f\uff0c\u5982\u4eca\u5df2\u5c3d\u5f97\u5929\u5c71\u5fc3\u6cd5\u4e86\uff0c\u6211\u4eca\u6b63\u5f0f\u51c6\u4f60 \u5217\u5165\u95e8\u5899\uff0c\u4e0d\u518d\u662f\u6302\u540d\u5f1f\u5b50\u4e86\u3002\u201d\u5510\u6653\u6f9c\u5927\u559c\u53e9\u8c22\uff0c\u6613\u5170\u73e0\u9053\uff1a\u201c\u5929\u5c71\u4e00\u6d3e\uff0c\u4ee3\u51fa\u82f1\u8c6a\uff0c\u4f60 \u6b63\u58ee\u5e74\uff0c\u672a\u5b9c\u5f52\u9690\u3002\u660e\u65e5\u518d\u548c\u4f60\u745b\u59b9\u4e0b\u5c71\uff0c\u76f8\u52a9\u5415\u56db\u5a18\u548c\u7518\u51e4\u6c60\u5427\u3002\u201d\u5510\u6653\u6f9c\u867d\u662f\u96be\u820d\uff0c\u4f46 \u60f3\u60f3\u5e08\u5085\u8bf4\u7684\u8bdd\u4e43\u662f\u6b63\u7406\uff0c\u4e8e\u662f\u7b2c\u4e8c\u65e5\u4fbf\u548c\u51af\u745b\u62dc\u8f9e\u5e08\u5085\uff0c\u518d\u4e0b\u5929\u5c71\u3002 \u3000\u3000\u4e24\u4eba\u95f4\u5173\u8dcb\u6d89\uff0c\u91cd\u5165\u4e2d\u539f\u3002\u5510\u6653\u6f9c\u867d\u4e0d\u4f3c\u4e24\u5e74\u524d\u90a3\u4e48\u5fe7\u90c1\uff0c\u5374\u4ecd\u662f\u62d8\u8c28\u81ea\u6043\uff0c\u4e0d\u6562\u4e0e\u51af \u745b\u6d89\u53ca\u513f\u5973\u4e4b\u60c5\u3002 \u3000\u3000\u8d70\u4e86\u4e09\u4e2a\u591a\u6708\uff0c\u7ecf\u8fc7\u5927\u6f20\u6d41\u6c99\uff0c\u7a77\u5c71\u6076\u6c34\uff0c\u4e24\u4eba\u53c8\u56de\u5230\u4e86\u6cb3\u5357\uff0c\u8def\u4e0a\u542c\u4eba\u8c08\u8d77\u5e74\u7fb9\u5c27\u5931 \u52bf\u4e4b\u4e8b\uff0c\u4f20\u8bf4\u7eb7\u7eb7\uff0c\u4e5f\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff0c\u4e24\u4eba\u5fc3\u60c5\u66f4\u6025\uff0c\u6068\u4e0d\u5f97\u7acb\u5373\u89c1\u7740\u5415\u56db\u5a18\u3002 \u3000\u3000\u8fd9\u65e5\u8def\u8fc7\u5d69\u5c71\uff0c\u5d69\u5c71\u4e0a\u4e00\u5927\u7247\u70e7\u7126\u4e86\u7684\u5c71\u5934\uff0c\u65b0\u7684\u6811\u6728\u53c8\u5df1\u7a00\u7a00\u758f\u758f\u7684\u957f\u4e86\u8d77\u6765\uff0c\u62bd\u6761 \u53d1\u53f6\u3002\u5510\u6653\u6f9c\u5341\u5206\u611f\u6168\uff0c\u541f\u9053\uff1a\u201c\u67af\u6811\u9022\u6625\u72b9\u518d\u53d1\uff0c\u6c5f\u5c71\u5386\u52ab\u5269\u65b0\u6101\u3002\u6811\u72b9\u5982\u6b64\uff0c\u4eba\u4f55\u4ee5 \u582a\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u5929\u8272\u665a\u4e86\uff0c\u4e0d\u5982\u5c31\u5728\u5d69\u5c71\u6b47\u4e00\u5bb5\u5427\uff0c\u6211\u4e5f\u60f3\u51ed\u540a\u4e00\u4e0b\u90a3\u70e7\u5269\u7684\u53e4\u5239\u5462\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u548c\u51af\u745b\u6b65\u4e0a\u5d69\u5c71\uff0c\u53ea\u89c1\u4e00\u7247\u74e6\u783e\uff0c\u88ab\u98ce\u96e8\u78e8\u6d17\uff0c\u5df2\u6e10\u6e10\u548c\u5c71\u4e0a\u7684\u6ce5\u571f\u6df7\u505a\u4e00\u56e2\uff0c \u6b8b\u7816\u7834\u74e6\u4e0d\u53ef\u5206\u8fa8\uff0c\u4e0a\u9762\u8fd8\u957f\u8d77\u4e86\u9752\u82d4\u3002\u5510\u6653\u6f9c\u53f9\u9053\uff1a\u201c\u5343\u5e74\u53e4\u5239\uff0c\u4ed8\u4e4b\u52ab\u7070\uff0c\u53ef\u53f9\u53ef \u6068\u3002\u201d\u51af\u745b\u7b11\u6307\u7740\u74e6\u783e\u4e0a\u7684\u9752\u8349\u9053\uff1a\u201c\u91ce\u706b\u70e7\u4e0d\u5c3d\uff0c\u6625\u98ce\u5439\u53c8\u751f\uff0c\u4f55\u987b\u6168\u53f9\u3002\u201d\u4e24\u4eba\u6cbf\u7740\u5c11 \u6797\u5bfa\u7684\u9057\u5740\u4e00\u8def\u8d70\u53bb\uff0c\u5728\u5c71\u5761\u4e0a\u53d1\u73b0\u4e00\u95f4\u70e7\u4e86\u534a\u8fb9\u7684\u7834\u5bfa\uff0c\u5510\u6653\u6f9c\u9053\uff1a\u201c\u54b1\u4eec\u5c31\u5728\u8fd9\u91cc\u6b47\u5bbf \u4e00\u5bb5\u5427\uff0c\u5d69\u5c71\u4e0e\u9099\u5c71\u76f8\u8ddd\u4e0d\u8fc7\u4e09\u767e\u591a\u91cc\uff0c\u518d\u8d70\u4e24\u5929\uff0c\u4fbf\u53ef\u5230\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u4ece\u5df2\u5d29\u584c\u4e86\u5927\u534a\u8fb9\u7684\u540e\u5899\uff0c\u8df3\u5165\u7834\u5bfa\uff0c\u6478\u5165\u6bbf\u4e2d\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u662f \u8c01\uff1f\u201d\u8fd9\u58f0\u97f3\u98a4\u9707\u6025\u4fc3\uff0c\u663e\u5f97\u4e2d\u6c14\u4e0d\u8db3\uff0c\u4f46\u5374\u522b\u5177\u4e00\u79cd\u5a01\u4e25\u3002\u5510\u6653\u6f9c\u63a8\u95e8\u8fdb\u53bb\uff0c\u53ea\u89c1\u5730\u4e0a\u70e7 \u7740\u4e00\u5806\u706b\uff0c\u65c1\u8fb9\u8eba\u7740\u4e00\u4e2a\u4eba\uff0c\u9762\u4e0a\u4f3c\u5e26\u75c5\u5bb9\uff0c\u4f46\u53cc\u773c\u5f20\u5f00\uff0c\u5374\u662f\u5149\u8292\u5916\u9732\uff0c\u70af\u70af\u6709\u795e\u3002 \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u4eec\u662f\u8fc7\u8def\u7684\u5ba2\u4eba\uff0c\u5148\u751f\u8d35\u59d3\uff1f\u201d\u90a3\u4eba\u672c\u5df2\u6b20\u8eab\u534a\u5750\uff0c\u76ef\u4e86\u4e8c\u4eba\u4e00\u773c\uff0c\u53c8 \u7761\u4e0b\u53bb\uff0c\u5bf9\u5510\u6653\u6f9c\u7684\u8bdd\u7adf\u4e0d\u7406\u776c\u3002\u51af\u745b\u597d\u5fc3\u95ee\u9053\uff1a\u201c\u5ba2\u5b98\u53ef\u662f\u6709\u75c5\u4e48\uff1f\u201d\u90a3\u4eba\u7738\u5b50\u534a\u5f20\uff0c \u9053\uff1a\u201c\u6211\u7761\u610f\u6b63\u6d53\uff0c\u8bf7\u4f60\u4eec\u522b\u6253\u6270\u4e86\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u82e5\u7136\u6709\u75c5\uff0c\u6211\u4eec\u968f\u8eab\u8fd8\u5e26\u6709\u4e00\u70b9\u4e38\u6563\uff0c \u4e5f\u8bb8\u5408\u7528\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u53eb\u4f60\u4eec\u522b\u7f57\u5506\uff0c\u4f60\u4eec\u600e\u4e48\u8001\u662f\u7231\u7ba1\u95f2\u4e8b\uff1f\u4f11\u8bf4\u6211\u6ca1\u75c5\uff0c\u6709\u75c5\u4e5f\u4e0d\u8981 \u4f60\u533b\u3002\u201d\u626f\u8fc7\u88ab\u5934\uff0c\u76d6\u8fc7\u5934\u9762\u3002\u51af\u745b\u89c1\u4ed6\u65e0\u53ef\u7406\u55bb\uff0c\u4e0d\u518d\u8a00\u8bed\u3002\u5510\u6653\u6f9c\u5374\u7559\u795e\u5230\u4ed6\u5934\u9876\u4e0a\u6709 \u70ed\u6c14\u6563\u53d1\u51fa\u6765\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u5185\u529f\u6df1\u6e5b\uff0c\u60f3\u5fc5\u662f\u53d7\u4e86\u6697\u4f24\uff0c\u73b0\u5728\u6b63\u7528\u5185\u529f\u81ea\u7597\uff0c \u54b1\u4eec\u771f\u4e0d\u8be5\u53bb\u6253\u6270\u4ed6\u3002\u201d\u626f\u4e86\u51af\u745b\u4e00\u4e0b\uff0c\u4e24\u4eba\u81ea\u5728\u6bbf\u89d2\u9760\u5899\u6b47\u606f\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u90a3\u4eba\u9f3e\u58f0\u5df2\u8d77\uff0c\u5916\u8859\u5ffd\u53c8\u6709\u8c08\u7b11\u4e4b\u58f0\uff0c\u5510\u6653\u6f9c\u4e00\u671b\uff0c\u53ea\u89c1\u6709\u4e24\u4eba\u8de8\u5899\u800c\u5165\uff0c \u4e0d\u89c9\u554a\u5440\u4e00\u58f0\uff0c\u4e0e\u51af\u745b\u540c\u65f6\u7ad9\u4e86\u8d77\u6765\u3002\u6765\u7684\u4e43\u662f\u7236\u5973\u4e8c\u4eba\uff0c\u6b63\u662f\u66fe\u5230\u6768\u4ef2\u82f1\u5bb6\u5bfb\u4ec7\uff0c\u4e0e\u51af\u745b \u66fe\u7ecf\u4e24\u5ea6\u4ea4\u624b\u7684\u5510\u91d1\u5cf0\u4e0e\u5510\u8d5b\u82b1\u3002\u539f\u6765\u81ea\u4e24\u5e74\u524d\u5510\u91d1\u5cf0\u63a5\u53d7\u4e86\u5415\u56db\u5a18\u7684\u8c03\u89e3\u540e\uff0c\u4fbf\u5e26\u5973\u513f\u5230 \u5404\u5904\u6563\u5fc3\uff0c\u6700\u8fd1\u5728\u6731\u4ed9\u9547\u6536\u4e86\u5973\u5a7f\u738b\u6556\u7684\u9057\u9aa8\uff0c\u60f3\u5e26\u56de\u56db\u5ddd\u8fc1\u846c\uff0c\u4eca\u65e5\u7ecf\u8fc7\u5d69\u5c71\uff0c\u8def\u65e0\u5ba2 \u5e97\uff0c\u4e5f\u5bfb\u5230\u8fd9\u4e2a\u7834\u5239\u6765\u6b47\u5bbf\u3002 \u3000\u3000\u5510\u6653\u6f9c\u89c1\u662f\u4ed6\u4eec\u7236\u5973\uff0c\u9887\u611f\u5c34\u5c2c\uff0c\u606d\u606d\u656c\u656c\u7684\u95ee\u5b89\u9053\uff1a\u201c\u5510\u8001\u524d\u8f88\uff0c\u4f60\u597d\uff1f\u201d\u5510\u91d1\u5cf0\u9f3b \u5b50\u91cc\u54fc\u4e86\u4e00\u58f0\uff1a\u201c\u597d\uff01\u201d\u5510\u8d5b\u82b1\u77aa\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c\u624b\u6478\u6697\u5668\u56ca\u5b50\uff0c\u5510\u91d1\u5cf0\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u4e08\u592b\u51fa \u8a00\u5fc5\u5b88\uff0c\u4ed6\u4eec\u4e0d\u662f\u6076\u610f\uff0c\u4e0d\u51c6\u4f60\u518d\u591a\u4e8b\u3002\u201d\u867d\u8bf4\u5982\u6b64\uff0c\u5510\u91d1\u5cf0\u81ea\u5df1\u4e5f\u662f\u5bf9\u51af\u5510\u4e8c\u4eba\u6273\u8d77\u8138 \u5b54\uff0c\u7231\u7406\u4e0d\u7406\uff0c\u4f3c\u4e4e\u6781\u4e0d\u613f\u610f\u548c\u4ed6\u4eec\u6500\u8c08\u3002 \u3000\u3000\u5510\u8d5b\u82b1\u9053\uff1a\u201c\u7239\uff0c\u8fd9\u91cc\u8fd8\u6709\u4e00\u4e2a\u4eba\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u8fd9\u4f4d\u5ba2\u5b98\u6709\u75c5\uff0c\u6b63\u5728\u719f\u7761\uff0c\u522b\u5435\u9192 \u4ed6\u3002\u201d\u5510\u8d5b\u82b1\u6487\u4e86\u6487\u5634\uff0c\u8138\u513f\u626d\u8fc7\u4e00\u8fb9\uff0c\u5634\u91cc\u5495\u561f\u8bf4\u9053\uff1a\u201c\u8c01\u8ddf\u4f60\u8bf4\u8bdd\uff1f\u201d\u51af\u745b\u8ba8\u4e86\u4e2a\u8001\u5927 \u6ca1\u8da3\uff0c\u8d4c\u6c14\u518d\u4e0d\u8bf4\u8bdd\u3002\u5510\u91d1\u5cf0\u5c0f\u58f0\u5bf9\u5973\u513f\u9053\uff1a\u201c\u90a3\u5c0f\u4e2b\u5934\u8bf4\u7684\u4e5f\u6709\u9053\u7406\uff0c\u8fd9\u91cc\u4e0d\u6bd4\u5ba2\u5e97\uff0c\u5435 \u9192\u4e86\u4eba\uff0c\u4e0d\u597d\u610f\u601d\u3002\u201d\u773c\u775b\u76ef\u7740\u90a3\u4e2a\u201c\u75c5\u4eba\u201d\uff0c\u8138\u4e0a\u8d8a\u6765\u8d8a\u9732\u51fa\u60ca\u8be7\u7684\u989c\u8272\u3002 \u3000\u3000\u5510\u8d5b\u82b1\u6084\u58f0\u95ee\u9053\uff1a\u201c\u7239\uff0c\u4f60\u770b\u51fa\u4ec0\u4e48\u95e8\u9053\u6765\u4e86\uff1f\u201d\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u6b64\u4eba\u8eab\u6000\u7edd\u6280\uff0c\u7edd\u4e0d\u662f \u5bfb\u5e38\u4e4b\u8f88\u3002\u201d\u6b63\u60f3\u8bbe\u6cd5\u7ed3\u7eb3\uff0c\u5ffd\u95fb\u5f97\u5bfa\u5916\u53c8\u6709\u811a\u6b65\u4e4b\u58f0\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u5b69\u5b50\u7684\u58f0\u97f3\u5148\u53eb\u9053\uff1a \u201c\u6211\u4e0d\u4f4f\u8fd9\u4e2a\u7834\u5e99\u3002\u201d\u63a5\u7740\u662f\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u597d\u50cf\u662f\u6709\u4eba\u5728\u90a3\u5b69\u5b50\u7684\u5c41\u80a1\u4e0a\u6253\u4e86\u4e00\u4e0b\uff0c\u5927\u58f0 \u9a82\u9053\uff1a\u201c\u4f60\u8fd8\u5145\u4ec0\u4e48\u5c11\u7237\u3002\u6709\u7834\u5e99\u4f60\u4f4f\u5df2\u7ecf\u7b97\u662f\u597d\u4e86\uff0c\u96be\u9053\u4f60\u8fd8\u60f3\u4f4f\u5bab\u6bbf\u5417\uff1f\u201d\u53e6\u4e00\u4eba\u9053\uff1a \u201c\u8981\u4f4f\u5bab\u6bbf\u4e5f\u4e0d\u96be\uff0c\u603b\u6709\u5f97\u4f60\u4f4f\u7684\uff0c\u53ea\u6050\u4f60\u4f4f\u4e0d\u957f\u4e45\u3002\u201d\u8fd9\u4eba\u201c\u562d\u201d\u7684\u4e00\u811a\u8e22\u5f00\u5e99\u95e8\uff0c\u84e6\u7136 \u53d1\u89c9\u91cc\u9762\u6709\u4eba\uff0c\u8bf4\u8bdd\u987f\u7136\u715e\u4f4f\u3002 \u3000\u3000\u5510\u6653\u6f9c\u51af\u745b\u4e0e\u5510\u5bb6\u7236\u5973\u7684\u773c\u5149\u9f50\u5411\u5916\u9762\u6ce8\u89c6\uff0c\u53ea\u89c1\u8fdb\u6765\u4e24\u4e2a\u5927\u4eba\uff0c\u4e00\u8272\u9752\u8863\uff0c\u8170\u630e\u6734 \u5200\uff0c\u4f5c\u6b66\u58eb\u6253\u626e\uff0c\u5e26\u7740\u4e00\u4e2a\u8863\u7740\u534e\u4e3d\u7684\u5b69\u5b50\uff0c\u5b69\u5b50\u7ea6\u83ab\u6709\u56db\u4e94\u5c81\u7684\u6837\u5b50\uff0c\u751f\u5f97\u5934\u89d2\u5ce5\u5d58\uff0c\u7709 \u6e05\u76ee\u79c0\uff0c\u5341\u5206\u53ef\u7231\u3002\u4f46\u5374\u7d27\u95ed\u7740\u5634\uff0c\u597d\u50cf\u53d7\u4e86\u8bb8\u591a\u59d4\u5c48\u7684\u6a21\u6837\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u9752\u8863\u6b66\u58eb\u559d\u9053\uff1a\u201c\u90fd\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d\u5510\u6653\u6f9c\u7b54\u9053\uff1a\u201c\u8fc7\u8def\u7684\u5ba2\u4eba\u3002\u201d\u5510\u91d1\u5cf0\u5374\u51b7 \u51b7\u8bf4\u9053\uff1a\u201c\u8352\u5c71\u53e4\u5239\uff0c\u8c01\u90fd\u53ef\u4ee5\u501f\u4f4f\uff0c\u4f60\u6709\u4f60\u4f4f\uff0c\u6211\u6709\u6211\u4f4f\uff0c\u4f60\u7ba1\u6211\u662f\u4ec0\u4e48\u4eba\uff01\u201d\u90a3\u4e24\u540d\u6b66 \u571f\u5411\u4ed6\u6a2a\u626b\u4e00\u773c\uff0c\u5510\u91d1\u5cf0\u50b2\u7136\u51b7\u7b11\uff0c\u77aa\u773c\u76f8\u5bf9\uff0c\u90a3\u4e24\u540d\u6b66\u58eb\u89c1\u4ed6\u7ae5\u989c\u9e64\u53d1\uff0c\u7cbe\u795e\u5065\u94c4\uff0c\u53cc\u76ee \u70af\u70af\u6709\u5149\uff0c\u663e\u7136\u662f\u5185\u529f\u6781\u6709\u9020\u8be3\uff0c\u76f8\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u8f7b\u8f7b\u9a82\u4e86\u4e00\u58f0\uff1a\u201c\u597d\u4e2a\u5229\u53e3\u7684\u8001\u513f\uff01\u201d\u5374 \u4e5f\u4e0d\u6562\u591a\u4e8b\u3002 \u3000\u3000\u90a3\u201c\u75c5\u4eba\u201d\u542c\u5f97\u5435\u95f9\u4e4b\u58f0\uff0c\u6253\u4e86\u4e00\u4e2a\u5475\u6b20\uff0c\u62ac\u8d77\u534a\u8eab\uff0c\u9732\u51fa\u5934\u6765\uff0c\u770b\u4e86\u90a3\u4e24\u4e2a\u6b66\u571f\u4e00 \u773c\uff0c\u53c8\u7761\u4e0b\u53bb\u3002\u884c\u5728\u524d\u5934\u7684\u90a3\u4e2a\u9752\u8863\u6b66\u58eb\u9053\uff1a\u201c\u8ba9\u5f00\u4e9b\u513f\uff0c\u8001\u7237\u8981\u70e4\u706b\uff01\u201d\u5510\u6653\u6f9c\u770b\u4e0d\u8fc7 \u773c\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4eba\u5bb6\u751f\u7684\u706b\u5462\uff01\u201d\u90a3\u6b66\u58eb\u9053\uff1a\u201c\u8981\u4f60\u591a\u7ba1\u95f2\u4e8b\uff01\u201d\u4f38\u624b\u5411\u90a3\u201c\u75c5\u4eba\u201d\u4e00\u63a8\uff0c \u5ffd\u5730\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u51e0\u4e4e\u8dcc\u5165\u706b\u5806\uff0c\u6124\u7136\u9a82\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4e1c\u897f\u7eca\u4e86\u8001\u5b50\u4e00\u8de4\uff1f\u201d\u5510\u8d5b\u82b1\u683c\u683c \u7684\u7b11\u4e2a\u4e0d\u505c\uff0c\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u5f3a\u6881\u9738\u9053\uff0c\u5fc5\u906d\u5929\u8c34\u3002\u8fd9\u53eb\u505a\u6d3b\u62a5\u5e94\uff0c\u8001\u5929\u7237\u4e5f\u6709\u773c\u775b\u3002\u201d\u90a3\u540d \u6b66\u58eb\u5927\u6012\uff0c\u624b\u6284\u5200\u628a\uff0c\u5510\u91d1\u5cf0\u53c8\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u662f\u6cdb\u8bba\uff0c\u53c8\u4e0d\u662f\u8bf4\u4f60\uff0c\u4f60\u8981\u52a8\u6b66\u4e48\uff1f\u5c0f\u8001\u513f \u4e5f\u613f\u5949\u966a\uff01\u201d\u5510\u6653\u6f9c\u548c\u51af\u745b\u4e5f\u90fd\u7ad9\u4e86\u8d77\u6765\uff0c\u90a3\u4e24\u540d\u6b66\u571f\u89c1\u5510\u5bb6\u7236\u5973\u5e26\u7740\u6697\u5668\u9752\u56ca\uff0c\u5510\u6653\u6f9c\u8170 \u60ac\u7684\u5251\u5323\uff0c\u53c8\u9690\u9690\u900f\u7740\u5b9d\u5149\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u56db\u4eba\u90fd\u662f\u4f1a\u5bb6\uff0c\u770b\u6765\u6b3a\u8d1f\u4e0d\u4e86\u3002\u201d\u987f\u65f6\u8f6f\u4e86\u4e0b\u6765\uff0c \u642d\u8baa\u7b11\u9053\uff1a\u201c\u51fa\u95e8\u4eba\u5230\u5904\u4e0e\u4eba\u65b9\u4fbf\uff0c\u4f55\u5fc5\u751f\u8fd9\u4e48\u5927\u7684\u95f2\u6c14\uff1f\u201d\u5728\u8fd1\u706b\u5806\u7684\u5730\u65b9\u94fa\u4e86\u4e00\u5f20\u6bdb \u6be1\uff0c\u548c\u5b69\u5b50\u4e00\u540c\u8eba\u4e0b\u3002 \u3000\u3000\u90a3\u5b69\u5b50\u89c1\u5510\u5bb6\u7236\u5973\u4e0e\u90a3\u6b66\u58eb\u9488\u950b\u76f8\u5bf9\uff0c\u6beb\u4e0d\u754f\u60e7\uff0c\u751a\u662f\u9ad8\u5174\uff0c\u8eba\u4e0b\u4e00\u9635\uff0c\u5ffd\u5730\u53c8\u8df3\u4e86\u8d77 \u6765\uff0c\u7334\u513f\u4f3c\u7684\u4e00\u4e0b\u5b50\u8df3\u5230\u5510\u8d5b\u82b1\u8eab\u8fb9\uff0c\u6307\u7740\u5979\u7684\u5f39\u5f13\u95ee\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u4f60\u4e5f\u4f1a\u6253\u5f39\u5f13\u5417\uff1f\u524d\u4e24 \u4e2a\u6708\u4ed6\u4eec\u521a\u521a\u6559\u6211\uff0c\u540e\u6765\u53c8\u4e0d\u6559\u4e86\u3002\u59d1\u59d1\u4f60\u6559\u6211\u597d\u5417\uff1f\u201d\u90a3\u4e24\u540d\u6b66\u58eb\u540c\u58f0\u53f1\u9053\uff1a\u201c\u4e0d\u51c6\u591a \u5634\uff0c\u5feb\u56de\u6765\u7761\uff01\u201d\u5510\u8d5b\u82b1\u5bf9\u8fd9\u5b69\u5b50\u5341\u5206\u559c\u7231\uff0c\u56de\u9a82\u9053\uff1a\u201c\u5c0f\u5b69\u5b50\u559c\u6b22\u8bf4\u8bdd\uff0c\u53c8\u4e0d\u4f24\u4e86\u4f60\u7684\u76ae \u6bdb\uff0c\u8fd9\u4e48\u51f6\u505a\u4ec0\u4e48\uff1f\u201d\u90a3\u6b66\u58eb\u9053\uff1a\u201c\u6211\u7ba1\u5b69\u5b50\u5173\u4f60\u4ec0\u4e48\u4e8b\uff1f\u54fc\uff0c\u4f60\u56de\u4e0d\u56de\u6765\uff1f\u201d\u5510\u91d1\u5cf0\u5ffd\u95ee \u9053\uff1a\u201c\u5582\uff0c\u597d\u5b69\u5b50\uff0c\u544a\u8bc9\u516c\u516c\uff0c\u8fd9\u4e24\u4e2a\u4eba\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u90a3\u4e24\u540d\u6b66\u58eb\u773c\u775b\u7741\u5f97\u94dc\u94c3\u4f3c\u7684\uff0c\u4e24\u4eba\u56db\u773c\uff0c\u5706\u9f13\u9f13\u7684\u77aa\u7740\u5b69\u5b50\uff0c\u90a3\u5b69\u5b50\u5f20\u5f00\u4e86\u53e3\uff0c\u521a\u8bf4 \u51fa\u201c\u4ed6\uff0c\u4ed6\u4eec\u2026\u2026\u201d\u51e0\u4e2a\u5b57\uff0c\u4fbf\u7acb\u5373\u6536\u4f4f\uff0c\u5510\u91d1\u5cf0\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u56de\u53bb\u5427\u3002\u201d\u5510\u8d5b \u82b1\u7275\u7740\u5b69\u5b50\u7684\u624b\uff0c\u4ecd\u7136\u820d\u4e0d\u5f97\u653e\uff0c\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u8ba9\u4ed6\u56de\u53bb\uff0c\u4e0d\u8981\u7d2f\u4ed6\u53d7\u8d23\u9a82\u4e86\u3002\u201d\u90a3\u5b69\u5b50\u672c \u6765\u662f\u6d3b\u6cfc\u6cfc\u7684\uff0c\u987f\u7136\u53d8\u5f97\u840e\u7f29\u65e0\u795e\uff0c\u4f4e\u5934\u9f13\u6c14\uff0c\u56de\u5230\u4e86\u6b66\u58eb\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u5510\u91d1\u5cf0\u5341\u5206\u7eb3\u7f55\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u6b66\u58eb\u663e\u7136\u4e0d\u662f\u5b69\u5b50\u7684\u7236\u4eb2\uff0c\u770b\u8fd9\u5b69\u5b50\u8863\u88f3\u534e\u8d35\uff0c \u5012\u50cf\u662f\u5b98\u5ba6\u4eba\u5bb6\u7684\u5b50\u5f1f\uff0c\u83ab\u975e\u8fd9\u4e24\u4e2a\u6b66\u58eb\u4e43\u662f\u4ed6\u5bb6\u7684\u62a4\u9662\u6216\u9556\u5e08\uff0c\u9001\u5b69\u5b50\u5230\u4ed6\u7236\u4eb2\u7684\u8859\u95e8\u53bb \u7684\uff1f\u4f46\u82e5\u662f\u8fd9\u6837\uff0c\u8fd9\u5b69\u5b50\u53c8\u4e0d\u5e94\u5982\u6b64\u754f\u60e7\u4ed6\u4eec\uff0c\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u4e5f\u4e0d\u5e94\u4ed6\u5982\u6b64\u51f6\u6cd5\u3002\u201d\u9976\u662f\u5510\u91d1 \u5cf0\u89c1\u591a\u8bc6\u5e7f\uff0c\u600e\u662f\u731c\u60f3\u4e0d\u900f\u3002 \u3000\u3000\u4e5f\u602a\u4e0d\u5f97\u5510\u91d1\u5cf0\u731c\u60f3\u4e0d\u900f\uff0c\u539f\u6765\u8fd9\u5b69\u5b50\u7adf\u662f\u5e74\u7fb9\u5c27\u7684\u513f\u5b50\u5e74\u5bff\uff08\u5e74\u7fb9\u5c27\u6015\u4ed6\u77ed\u547d\uff0c\u6240\u4ee5 \u7ed9\u4ed6\u53d6\u4e86\u8fd9\u4e48\u4e00\u4e2a\u4fd7\u6c14\u7684\u540d\u5b57\uff09\uff0c\u5e74\u7fb9\u5c27\u6258\u7ed9\u66fe\u9759\uff0c\u53c8\u6d3e\u4e86\u4e24\u540d\u5fc3\u8179\u7684\u6b66\u58eb\u53bb\u76d1\u62a4\uff0c\u7528\u610f\u539f \u662f\u6050\u9632\u81ea\u5df1\u5931\u52bf\u4e4b\u540e\uff0c\u6c5f\u6e56\u4e0a\u7684\u597d\u6c49\u4f1a\u52a0\u5bb3\u4ed6\u7684\u513f\u5b50\uff0c\u90a3\u6599\u6b32\u52a0\u5bb3\u4ed6\u513f\u5b50\u7684\u5374\u4e0d\u662f\u6c5f\u6e56\u4e0a\u7684 \u597d\u6c49\uff0c\u800c\u662f\u81ea\u5df1\u7684\u5fc3\u8179\u3002 \u3000\u3000\u539f\u6765\u66fe\u9759\u81ea\u90a3\u6b21\u5728\u84b2\u57ce\u7ed9\u5415\u56db\u5a18\u5632\u9a82\u4e86\u4e00\u987f\uff0c\u826f\u5fc3\u6709\u6127\uff0c\u56de\u5bb6\u4e4b\u540e\uff0c\u8d8a\u60f3\u8d8a\u89c9\u96be\u8fc7\uff0c\u7adf \u7136\u751f\u8d77\u75c5\u6765\uff0c\u5e74\u8001\u4f53\u8870\uff0c\u7f20\u7ef5\u75c5\u69bb\uff0c\u867d\u7136\u836f\u77f3\u7eb7\u6295\uff0c\u5140\u65e0\u8d77\u8272\u3002\u5306\u5306\u8fc7\u4e86\u4e09\u5e74\uff0c\u5e74\u7fb9\u5c27\u5931\u52bf \u7684\u6d88\u606f\u4f20\u6765\uff0c\u66fe\u9759\u629a\u517b\u7740\u5e74\u7fb9\u5c27\u7684\u5b69\u5b50\u66f4\u662f\u62c5\u5fe7\u3002\u4e0d\u4e45\uff0c\u5173\u4e8e\u5e74\u7fb9\u5c27\u7684\u6d88\u606f\u8d8a\u6765\u8d8a\u574f\uff0c\u6700\u540e \u7adf\u542c\u5230\u4ed6\u8fde\u964d\u5341\u516b\u7ea7\uff0c\u88ab\u8d2c\u5230\u676d\u5dde\u53bb\u770b\u5b88\u57ce\u95e8\uff0c\u800c\u4eac\u4e2d\u7684\u5bb6\u5c5e\u4e5f\u7ed9\u6536\u7981\u4e86\u3002\u8fd9\u65f6\uff0c\u90a3\u4e24\u540d\u5fc3 \u8179\u7684\u6b66\u571f\u4fbf\u751f\u4e86\u5f02\u5fc3\uff0c\u60f3\u628a\u8fd9\u5b69\u5b50\u5e26\u5230\u4eac\u4e2d\u9886\u8d4f\uff0c\u6015\u66fe\u9759\u4e0d\u4ece\uff0c\u5bf9\u4ed6\u5927\u65bd\u6050\u5413\uff0c\u66fe\u9759\u672c\u6765\u662f \u4e2a\u80c6\u5c0f\u7684\u4eba\uff0c\u66f4\u517c\u662f\u4e45\u75c5\u4e4b\u8eab\uff0c\u88ab\u4ed6\u4eec\u4e00\u5413\uff0c\u7adf\u7136\u6d3b\u6d3b\u5413\u6b7b\u3002\u4e24\u4e2a\u6b66\u58eb\u4fbf\u5e26\u4e86\u5e74\u7fb9\u5c27\u7684\u5b69 \u5b50\uff0c\u517c\u7a0b\u8d76\u4eac\u3002\u4f46\u4ed6\u4eec\u65e2\u6015\u6c5f\u6e56\u4e0a\u7684\u597d\u6c49\uff0c\u66f4\u6015\u5bab\u4e2d\u7684\u536b\u58eb\u534a\u9014\u63d0\u622a\uff0c\u62a2\u4e86\u5b69\u5b50\u9886\u529f\uff0c\u53cd\u6cbb \u4ed6\u4eec\u5e74\u7fb9\u5c27\u515a\u7fbd\u4e4b\u7f6a\u3002\u6240\u4ee5\u4e00\u8def\u4e0a\u4e5f\u4e13\u89c5\u5c0f\u8def\u884c\u8d70\uff0c\u5e0c\u671b\u5165\u4eac\u4e4b\u540e\uff0c\u79d8\u5bc6\u51fa\u9996\u3002 \u3000\u3000\u5374\u4e0d\u6599\u8fd9\u665a\u5728\u53e4\u5239\u6295\u5bbf\u9047\u7740\u4e86\u5510\u91d1\u5cf0\u7236\u5973\u3002\u5510\u8d5b\u82b1\u9752\u5e74\u4e27\u592b\uff0c\u819d\u4e0b\u65e0\u513f\uff0c\u4e00\u89c1\u8fd9\u4e2a\u5b69 \u5b50\uff0c\u751a\u662f\u6295\u7f18\uff0c\u6781\u4e3a\u559c\u7231\u3002\u5b69\u5b50\u88ab\u6b66\u58eb\u5524\u56de\u4e4b\u540e\uff0c\u4fbf\u5583\u5583\u5495\u5495\u7684\u548c\u7236\u4eb2\u5546\u8bae\uff0c\u7eb5\u607f\u7236\u4eb2\u628a\u5b69 \u5b50\u62a2\u8fc7\u6765\u3002\u5979\u7684\u7406\u7531\u662f\uff1a\u65e2\u7136\u80fd\u65ad\u5b9a\u8fd9\u4e24\u4e2a\u6b66\u58eb\u4e0d\u662f\u5b69\u5b50\u7684\u4eb2\u4eba\uff0c\u90a3\u4e48\u5c31\u4e0d\u8be5\u8ba9\u5b69\u5b50\u88ab\u4ed6\u4eec \u51cc\u8650\u3002\u5510\u4f1a\u5cf0\u88ab\u5973\u513f\u8bf4\u5f97\u5fc3\u52a8\uff0c\u4fbf\u60f3\u53bb\u5411\u90a3\u4e24\u4e2a\u6b66\u58eb\u6311\u8845\u3002 \u3000\u3000\u5e74\u5bff\u7761\u4e0b\u4e0d\u4e45\uff0c\u5ffd\u7136\u5728\u68a6\u4e2d\u54c7\u7684\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u6253\u6211\uff0c\u4e0d\u8981\u6253\u6211\uff01\u201d\u4f34\u7740 \u4ed6\u7761\u7684\u90a3\u540d\u6b66\u58eb\uff0c\u201c\u556a\u201d\u4e00\u58f0\u53c8\u6253\u5728\u4ed6\u7684\u5c41\u80a1\u4e0a\uff0c\u9a82\u9053\uff1a\u201c\u5c0f\u7334\u513f\uff0c\u7761\u4e5f\u7761\u5f97\u4e0d\u5b89\u9759\uff0c\u8c01\u6253 \u4f60\u4e86\uff1f\u201d\u5510\u91d1\u5cf0\u4e00\u4e0b\u5b50\u8df3\u98de\u8d77\u8eab\u6765\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u8981\u8138\u7684\u4e1c\u897f\uff0c\u6b3a\u4fae\u5b69\u5b50\u3002\u4f60\u8fd8\u95ee\u8c01\u6253\u4ed6\uff0c\u4e0d \u662f\u4f60\u6253\u4ed6\u4e48\uff1f\u201d\u90a3\u6b66\u58eb\u6012\u9053\uff1a\u201c\u597d\u54c7\uff0c\u6211\u89c1\u4f60\u591a\u957f\u51e0\u5c81\uff0c\u5904\u5904\u8ba9\u4f60\uff0c\u4f60\u5012\u7ba1\u8d77\u8001\u5b50\u4eec\u7684\u95f2\u4e8b \u4e86\uff1f\u201d \u3000\u3000\u5510\u91d1\u5cf0\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e13\u7ba1\u95f2\u4e8b\uff0c\u4f60\u600e\u4e48\u6837\uff1f\u597d\u5b69\u5b50\u8fc7\u6765\uff0c\u516c\u516c\u75bc\u4f60\uff01\u201d\u90a3\u540d\u6b66\u58eb\u52c3\u7136\u5927 \u6012\uff0c\u4e00\u638c\u63a8\u53bb\uff0c\u5510\u91d1\u5cf0\u65e9\u6709\u9632\u5907\uff0c\u8863\u8896\u4e00\u7529\uff0c\u547c\u7684\u4e00\u4e0b\uff0c\u626b\u5230\u90a3\u6b66\u58eb\u7684\u9762\u4e0a\uff0c\u70ed\u8fa3\u8fa3\u7684\u4e0d\u557b \u6253\u4e86\u4ed6\u4e00\u8bb0\u8033\u5149\u3002\u75db\u5f97\u4ed6\u54c7\u54c7\u53eb\u9053\uff1a\u201c\u8001\u5339\u592b\uff0c\u4f60\u4f5c\u53cd\u4e86\uff0c\u5403\u6211\u4e00\u5200\uff01\u201d\u62bd\u51fa\u5200\u6765\uff0c\u6402\u5934\u4fbf \u65ab\uff0c\u5510\u91d1\u5cf0\u547c\u547c\u4e24\u638c\uff0c\u5c06\u4e24\u540d\u6b66\u58eb\u4e00\u9f50\u903c\u5f00\uff0c\u6b63\u8981\u51fa\u624b\u62a2\u90a3\u5b69\u5b50\uff0c\u5ffd\u5730\u91cc\u5916\u9762\u54cd\u7bad\u545c\u545c\u4e71 \u54cd\uff0c\u63a5\u7740\u5929\u7a7a\u73b0\u51fa\u51e0\u9053\u84dd\u8272\u7684\u706b\u5149\uff0c\u5510\u91d1\u5cf0\u548c\u90a3\u4e24\u540d\u6b66\u58eb\u4f4f\u624b\u4e0d\u6597\uff0c\u53ea\u542c\u5f97\u54cd\u7bad\u8fc7\u540e\uff0c\u4fbf\u662f \u5608\u6742\u7684\u4eba\u58f0\uff0c\u90a3\u6b66\u58eb\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u54b1\u4eec\u7ed9\u5f3a\u76d7\u5305\u56f4\u4e86\uff01\u201d \u3000\u3000\u5510\u91d1\u5cf0\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6015\u5f3a\u76d7\uff1f\u6211\u4fdd\u62a4\u4f60\uff01\u628a\u5b69\u5b50\u5148\u4ea4\u7ed9\u6211\uff01\u201d\u5510\u91d1\u5cf0\u81ea\u6043\u548c\u9ed1\u767d \u4e24\u9053\u90e8\u6709\u4ea4\u60c5\uff0c\u63d0\u8d77\u56db\u5ddd\u5510\u5bb6\u7684\u540d\u5934\uff0c\u6c5f\u6e56\u4e0a\u6709\u70b9\u540d\u6c14\u7684\u4eba\u65e0\u4eba\u4e0d\u6653\uff0c\u662f\u4ee5\u50b2\u7136\u4e0d\u60e7\uff01 \u3000\u3000\u5916\u9762\u7684\u4eba\u5927\u58f0\u53eb\u9053\uff1a\u201c\u662f\u8fd9\u91cc\u4e86\uff01\u201d\u53ea\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u5bfa\u95e8\u7acb\u523b\u649e\u5f00\uff0c\u5916\u9762\u9ed1\u538b\u538b \u7684\u5806\u6ee1\u4e86\u4eba\uff0c\u5510\u6653\u6f9c\u4e0e\u51af\u745b\u5927\u5403\u4e00\u60ca\uff0c\u4e3a\u9996\u7684\u7adf\u662f\u6e05\u5bab\u7684\u9996\u5e2d\u6b66\u58eb\u3001\u897f\u85cf\u7ea2\u6559\u7684\u7b2c\u4e8c\u9ad8\u624b\u989d \u97f3\u548c\u5e03\u3002 \u3000\u3000\u90a3\u4e24\u540d\u6b66\u58eb\u89c1\u662f\u5b98\u519b\uff0c\u5927\u559c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u662f\u81ea\u5df1\u4eba\uff01\u201d\u989d\u97f3\u548c\u5e03\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u81ea\u5df1\u4eba\uff0c \u62a5\u4e0a\u540d\u6765\uff01\u201d\u989d\u97f3\u548c\u5e03\u7684\u624b\u4e0b\uff0c\u6709\u4eba\u8ba4\u5f97\u4ed6\u4eec\u662f\u5e74\u7fb9\u5c27\u7684\u5fc3\u8179\u6b66\u58eb\uff0c\u5bf9\u989d\u97f3\u548c\u5e03\u8bf4\u4e86\uff0c\u989d\u97f3 \u548c\u5e03\u5706\u7741\u53cc\u773c\uff0c\u4e00\u626b\u6bbf\u5802\uff0c\u5ffd\u51b7\u7b11\u9053\uff1a\u201c\u597d\u54c7\uff0c\u4f60\u4eec\u60f3\u4f5c\u53cd\u4e86\uff0c\u548c\u53db\u9006\u52fe\u7ed3\u4e00\u8d77\uff0c\u662f\u4e0d\u662f\u60f3 \u4e3a\u4f60\u4eec\u7684\u2018\u5927\u5e05\u2019\u62a5\u4ec7\uff1f\u201d\u90a3\u4e24\u540d\u6b66\u58eb\u6025\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff01\u6211\u4eec\u662f\u5e26\u5e74\u2026\u2026\u201d\u201c\u7fb9\u5c27\u7684\u5b69\u5b50\u201d \u51e0\u5b57\u8fd8\u672a\u51fa\u53e3\uff0c\u5df2\u7ed9\u989d\u97f3\u548c\u5e03\u4e00\u624b\u4e00\u4e2a\u63d0\u4e86\u8d77\u6765\uff0c\u63b7\u7ed9\u968f\u4ece\u7f1a\u4e86\uff0c\u5b69\u5b50\u54c7\u54c7\u54ed\u558a\uff0c\u5510\u91d1\u5cf0\u4e00 \u624b\u62a2\u4e86\u8fc7\u6765\uff0c\u62b1\u5728\u6000\u4e2d\u3002 \u3000\u3000\u989d\u97f3\u548c\u5e03\u4e00\u8dc3\u8fdb\u95e8\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u513f\u53c8\u662f\u4f55\u4eba\uff1f\u201d\u5510\u91d1\u5cf0\u7ffb\u51fa\u7ee3\u6709\u5510\u5bb6\u6807\u8bb0\u3001\u9752\u72ee\u4e3a \u53f7\u7684\u6697\u5668\u56ca\u9053\uff1a\u201c\u770b\u4f60\u8eab\u624b\u975e\u51e1\uff0c\u8fde\u6211\u7684\u8bb0\u53f7\u4e5f\u8ba4\u4e0d\u5f97\u4e48\uff1f\u201d\u5510\u91d1\u5cf0\u6b7b\u53bb\u7684\u90a3\u5973\u5a7f\u738b\u6556\u539f\u5728 \u516c\u95e8\u4e2d\u5f53\u5dee\uff0c\u4ed6\u81ea\u5df1\u548c\u5fa1\u6797\u519b\u7684\u7edf\u9886\u5f20\u7ef4\u4e5f\u662f\u670b\u53cb\uff0c\u8bb8\u591a\u6709\u540d\u7684\u6355\u5934\u8fd8\u662f\u4ed6\u7684\u540e\u8f88\uff0c\u4ed6\u4ee5\u4e3a \u6765\u7684\u662f\u6cb3\u5357\u5de1\u629a\u8859\u95e8\u6355\u76d7\u7684\u516c\u5dee\uff0c\u6240\u4ee5\u501a\u8001\u5356\u8001\u3002\u4e0d\u6599\u989d\u9752\u548c\u5e03\u6765\u81ea\u897f\u85cf\uff0c\u8fde\u5510\u5bb6\u7684\u540d\u5934\u4e5f \u672a\u542c\u8fc7\uff0c\u542c\u4e86\u5510\u91d1\u5cf0\u7684\u8bdd\uff0c\u201c\u54fc\u201d\u7684\u4e00\u58f0\uff0c\u53cd\u624b\u4e00\u6293\uff0c\u5411\u5510\u91d1\u5cf0\u4fbf\u4e0b\u6740\u624b\uff01 \u3000\u3000\u5510\u91d1\u5cf0\u5de6\u624b\u62b1\u7740\u5b69\u5b50\uff0c\u53f3\u638c\u5f80\u5916\u4e00\u6325\uff0c\u567c\u556a\u4e00\u58f0\uff0c\u5510\u91d1\u5cf0\u8eab\u5b50\u6447\u6447\u6b32\u5012\uff0c\u989d\u97f3\u548c\u5e03\u5c0f\u81c2 \u4e00\u5708\uff0c\u5de6\u624b\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u76f4\u63d2\u54bd\u5589\uff0c\u53f3\u624b\u5c48\u8d77\u4e94\u6307\uff0c\u4fbf\u51fb\u5929\u7075\u76d6\u8981\u5bb3\u3002\u8fd9\u4e24\u62db\u662f\u7ea2\u6559 \u4e2d\u7684\u53d6\u547d\u7edd\u62db\uff0c\u5341\u5206\u5389\u5bb3\uff0c\u5510\u91d1\u5cf0\u7684\u529f\u529b\u672c\u5c31\u4e0d\u5982\u989d\u97f3\u548c\u5e03\uff0c\u4e14\u53c8\u62b1\u7740\u5b69\u5b50\uff0c\u66f4\u662f\u65e0\u6cd5\u62b5 \u654c\uff0c\u4ed6\u4e00\u4e2a\u201c\u9000\u6b65\u6a2a\u80b1\u201d\uff0c\u52c9\u5f3a\u5316\u89e3\u4e86\u654c\u4eba\u63d2\u5411\u54bd\u5589\u7684\u62db\u5f0f\uff0c\u5934\u9876\u5929\u7075\u76d6\u5374\u66b4\u9732\u5728\u654c\u4eba\u4e94\u6307 \u4e4b\u4e0b\uff0c\u770b\u770b\u5c31\u8981\u7ed9\u989d\u97f3\u548c\u5e03\u51fb\u7a7f\uff01 \u3000\u3000\u5510\u5be8\u82b1\u5927\u53eb\u4e00\u58f0\uff0c\u98de\u8eab\u6251\u4e0a\uff0c\u5ffd\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u4e24\u6761\u4eba\u5f71\u5df2\u5148\u81ea\u4ece\u5979\u8eab\u8fb9\u62a2\u8fc7\uff0c\u8fd8\u672a\u770b \u6e05\uff0c\u53ea\u542c\u5f97\u989d\u97f3\u548c\u5e03\u54fc\u4e86\u4e00\u58f0\uff0c\u5510\u91d1\u5cf0\u8e09\u8e09\u8dc4\u8dc4\u7684\u5954\u51fa\u6570\u6b65\uff0c\u4e00\u8de4\u8dcc\u5012\u5730\u4e0a\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u8352\u5c71\u817e\u6740\u6c14\uff0c\u53e4\u5239\u4f0f\u5371\u673a\u3002 \u3000\u3000\u6b32\u77e5\u5510\u91d1\u5cf0\u6027\u547d\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e03\u56de\u3000\u4f73\u8282\u95f9\u5143\u5bb5\u3000\u5bab\u4e2d\u789f\u8840\u3000\u5fa1\u6cb3\u9003\u5927\u4fa0\u3000\u6c34\u5e95\u6f5c\u8e2a \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u56db\u5341\u4e03\u56de \u4f73\u8282\u95f9\u5143\u5bb5\u3000\u5bab\u4e2d\u789f\u8840 \u5fa1\u6cb3\u9003\u5927\u4fa0\u3000\u6c34\u5e95\u6f5c\u8e2a \u3000\u3000\u5510\u6653\u6f9c\u4e0e\u51af\u745b\u53cc\u5251\u51fa\u9798\uff0c\u4e00\u63a0\u800c\u4e0a\uff0c\u4e8c\u4eba\u8eab\u6cd5\u5feb\u6781\uff0c\u540e\u53d1\u5148\u81f3\uff0c\u53cd\u62a2\u5728\u5510\u8d5b\u82b1\u4e4b\u524d\uff0c\u5de6 \u53f3\u5206\u88ad\u3002\u989d\u97f3\u548c\u5e03\u903c\u5f97\u56de\u8f6c\u8eab\u8eaf\uff0c\u8fde\u73af\u53cc\u638c\uff0c\u89e3\u5f00\u51af\u5510\u4e8c\u4eba\u7684\u653b\u52bf\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u5510\u91d1\u5cf0\u5df2\u8131 \u51fa\u8eab\u6765\uff0c\u4f46\u56e0\u9707\u8361\u8fc7\u751a\uff0c\u4e00\u8de4\u8dcc\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5510\u8d5b\u82b1\u8fd9\u624d\u8d76\u81f3\uff0c\u5931\u60ca\u95ee\u9053\uff1a\u201c\u7239\uff0c\u4f60\u53d7\u4f24\u4e86\uff1f\u201d\u5510\u91d1\u5cf0\u7ffb\u8eab\u5750\u8d77\uff0c\u5de6\u624b\u4ecd\u7136\u7d27\u62b1\u7740\u5e74 \u7fb9\u5c27\u7684\u5b69\u5b50\uff0c\u6025\u58f0\u8bf4\u9053\uff1a\u201c\u6ca1\u4e8b\u3002\u4f60\u5feb\u66ff\u6211\u62b1\u8fd9\u5b69\u5b50\uff0c\u7d27\u9760\u6211\u7684\u8eab\u8fb9\uff0c\u5343\u4e07\u4e0d\u53ef\u4e71\u52a8\u3002\u201d\u5f85 \u5973\u513f\u63a5\u8fc7\u5b69\u5b50\uff0c\u7acb\u523b\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u53cc\u773c\u70af\u70af\uff0c\u4f3c\u6597\u573a\u7684\u516c\u9e21\u4e00\u6837\uff0c\u6ce8\u89c6\u654c\u4eba\u3002 \u3000\u3000\u989d\u97f3\u548c\u5e03\u88ab\u51af\u5510\u903c\u9000\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u5251\u6cd5\u53c8\u9ad8\u591a\u4e86\uff01\u201d\u4e0d\u6562\u7a7a\u624b\u5bf9\u654c\uff0c\u53d6 \u51fa\u62c2\u5c18\uff0c\u4e00\u626c\u4e00\u7ed5\uff0c\u4e24\u67c4\u5b9d\u5251\uff0c\u7adf\u7136\u90fd\u7ed9\u7f20\u7740\uff0c\u903c\u4e0d\u8fd1\u8eab\u3002\u989d\u97f3\u548c\u5e03\u54c8\u54c8\u5927\u7b11\uff0c\u90a3\u6599\u7b11\u58f0\u672a \u6bd5\uff0c\u5ffd\u542c\u5f97\u201c\u535c\u52d2\u201d\u51e0\u58f0\uff0c\u62c2\u5c18\u7adf\u7136\u65ad\u4e86\u51e0\u6839\u3002\u970e\u65f6\u95f4\u5bd2\u5149\u75be\u5c04\uff0c\u4e24\u53e3\u660e\u6643\u6643\u7684\u5229\u5251\uff0c\u540c\u65f6 \u903c\u5230\u9762\u95e8\uff01 \u3000\u3000\u989d\u97f3\u548c\u5e03\u7684\u62c2\u5c18\uff0c\u4e43\u662f\u7528\u897f\u85cf\u7266\u725b\u7684\u5c3e\u7f20\u7ed5\u767d\u91d1\u800c\u6210\uff0c\u575a\u97e7\u5f02\u5e38\uff0c\u672c\u6765\u4e5f\u662f\u4e00\u4ef6\u5b9d\u7269\uff0c \u66f4\u52a0\u4e0a\u989d\u97f3\u548c\u5e03\u5185\u529f\u6df1\u6e5b\uff0c\u529b\u900f\u62c2\u5c18\uff0c\u53ef\u8f6f\u53ef\u786c\uff0c\u5e73\u65f6\u4ed6\u7528\u8fd9\u67c4\u4f5b\u5c18\u593a\u53d6\u654c\u4eba\u5175\u5203\uff0c\u786e\u662f\u5f97 \u5fc3\u5e94\u624b\uff0c\u65e0\u5f80\u4e0d\u5229\u3002\u4f46\u5510\u6653\u5170\u7684\u6e38\u9f99\u5251\u4e0e\u51af\u745b\u7684\u65ad\u7389\u5251\u90fd\u662f\u524a\u94c1\u5982\u6ce5\uff0c\u5439\u6bdb\u7acb\u65ad\u7684\u5b9d\u5251\uff0c\u4e24 \u4eba\u867d\u7136\u4e00\u65f6\u4e4b\u95f4\u88ab\u989d\u97f3\u548c\u5e03\u7684\u5185\u529b\u903c\u7740\uff0c\u4e0d\u80fd\u79fb\u52a8\uff0c\u4f46\u7acb\u5373\u4fbf\u8fd0\u7528\u5929\u5c71\u5251\u6cd5\u7684\u201c\u67d4\u52b2\u201d\uff0c\u5251 \u5c16\u5fae\u98a4\uff0c\u524a\u65ad\u62c2\u5c18\uff0c\u7a81\u56f4\u800c\u51fa\u3002\u82e5\u975e\u8fd9\u62c2\u5c18\u4e5f\u662f\u5b9d\u7269\uff0c\u524a\u65ad\u7684\u8fd8\u4e0d\u6b62\u8fd9\u51e0\u6839\u3002 \u3000\u3000\u989d\u97f3\u548c\u5e03\u867d\u7136\u77e5\u9053\u654c\u4eba\u53cc\u5251\u4e0d\u662f\u51e1\u54c1\uff0c\u4f46\u5374\u6599\u4e0d\u5230\u5982\u6b64\u950b\u5229\uff0c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u53cc\u5251\u5df2\u5230\u9762 \u95e8\u3002\u5e78\u4ed6\u6b66\u529f\u5df2\u8fbe\u767b\u5cf0\u9020\u6781\u4e4b\u5883\uff0c\u80a9\u5934\u5fae\u52a8\uff0c\u5de6\u638c\u4e00\u6325\uff0c\u51af\u5510\u4e8c\u4eba\u88ab\u4ed6\u638c\u529b\u4e00\u9707\uff0c\u8eab\u5f62\u7a0d \u6b6a\uff0c\u5251\u5c16\u843d\u70b9\u659c\u504f\uff0c\u53cc\u5251\u4ea4\u63d2\uff0c\u4ece\u4ed6\u80a9\u5934\u4e24\u8fb9\u7a7f\u8fc7\uff0c\u5374\u6ca1\u6709\u5c06\u4ed6\u523a\u7740\uff01 \u3000\u3000\u989d\u97f3\u548c\u5e03\u5e26\u6765\u7684\u536b\u571f\u53d1\u4e00\u58f0\u558a\uff0c\u7eb7\u7eb7\u56f4\u4e0a\uff0c\u989d\u97f3\u548c\u5e03\u77a7\u4e86\u4e00\u773c\uff0c\u89c1\u62c2\u5c18\u88ab\u524a\u65ad\u7684\u4e0d\u8fc7\u51e0 \u6839\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u968f\u5373\u559d\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5c0f\u8f88\u63d2\u7fc5\u96be\u98de\uff0c\u4f60\u4eec\u5c06\u8fd9\u5e99\u91cc\u7684\u4eba\u90fd\u7ed9\u6211\u6349\u4e86\uff0c\u4ed4\u7ec6\u641c \u7d22\uff0c\u4e00\u4e2a\u4e5f\u4e0d\u8bb8\u6f0f\uff01\u201d\u536b\u58eb\u4eec\u77e5\u4ed6\u4e0d\u7528\u5e2e\u624b\uff0c\u4fbf\u56f4\u4e0a\u53bb\u6349\u5510\u91d1\u5cf0\u7236\u5973\u3002 \u3000\u3000\u5510\u91d1\u5cf0\u4ecd\u7136\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u82e5\u65e0\u5176\u4e8b\uff0c\u5510\u8d5b\u82b1\u5219\u62b1\u7740\u5b69\u5b50\uff0c\u5750\u5728\u7236\u4eb2\u8eab\u540e\uff0c\u5979\u6bd4\u7236\u4eb2\u4f4e \u534a\u4e2a\u5934\uff0c\u8eab\u8eaf\u6070\u6070\u88ab\u7236\u4eb2\u906e\u7740\uff0c\u4e5f\u662f\u52a8\u4e5f\u4e0d\u52a8\uff0c\u8fd8\u4f4e\u58f0\u7684\u54c4\u90a3\u5b69\u5b50\uff0c\u53eb\u4ed6\u4e0d\u8981\u5bb3\u4f2f\u3002 \u3000\u3000\u536b\u58eb\u4eec\u89c1\u6b64\u60c5\u5f62\uff0c\u5012\u4e0d\u6562\u9aa4\u7136\u5192\u8fdb\uff0c\u9886\u5934\u7684\u4eba\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u513f\uff0c\u6363\u4ec0\u4e48\u9b3c\u3002\u201d\u5510\u91d1\u5cf0 \u53cc\u7709\u4e00\u626c\uff0c\u76ee\u5149\u5982\u70ac\u51b7\u51b7\u4e00\u7b11\uff0c\u5374\u4e0d\u7b54\u8bdd\u3002\u8fd9\u65f6\u989d\u97f3\u548c\u5e03\u53c8\u5df2\u548c\u51af\u5510\u4e8c\u4eba\u6597\u4e86\u516d\u4e03\u62db\uff0c\u659c\u773c \u4e00\u77a5\uff0c\u5927\u6012\u9a82\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u5e2e\u8113\u5305\uff0c\u8fd8\u8981\u7b49\u6211\u6765\u52a8\u624b\u4e48\uff1f\u201d\u9886\u5934\u7684\u536b\u58eb\u626c\u5200\u75be\u8fdb\uff0c\u81f3\u8ddd\u79bb\u5510 \u91d1\u5cf0\u4e00\u4e08\u4e4b\u5904\uff0c\u5ffd\u7136\u5927\u53eb\u4e00\u58f0\uff0c\u7ffb\u8eab\u4fbf\u5012\uff0c\u5728\u5730\u4e0a\u60e8\u53eb\u72c2\u55e5\u3002\u5176\u4e2d\u6709\u8bc6\u8d27\u7684\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c \u8fd9\u662f\u5510\u5bb6\u7684\u6b79\u6bd2\u6697\u5668\u4e27\u95e8\u9489\uff01\u201d\u8bdd\u58f0\u672a\u5b8c\uff0c\u53c8\u6709\u51e0\u4eba\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5510\u91d1\u5cf0\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u756a\u72d7\u4e0d\u77e5\u6211\u7684\u6765\u5386\uff0c\u96be\u9053\u4f60\u4eec\u4e5f\u4e0d\u77e5\u4e48\uff1f\u201d\u989d\u97f3\u548c\u5e03\u5e26\u6765\u7684\u536b\u58eb\u6ee1 \u6c49\u53c2\u534a\uff0c\u6c49\u4eba\u536b\u58eb\u4e2d\u5927\u534a\u77e5\u9053\u5510\u5bb6\u7684\u6765\u5386\uff0c\u6709\u4eba\u53eb\u51fa\u58f0\u9053\u201c\u4f60\u662f\u5510\u4e8c\u5148\u751f\u4e48\uff1f\u201d\u5510\u91d1\u5cf0\u50b2\u7136 \u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u77e5\u9053\u6211\u7684\u6765\u5386\uff0c\u8fd8\u4e0d\u4e56\u4e56\u7ed9\u6211\u6eda\u51fa\u53bb\uff01\u201d\u536b\u58eb\u4e2d\u6709\u4e24\u4e2a\u662f\u989d\u97f3\u548c\u5e03\u7684\u5f92\u5f1f\uff0c \u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u6015\u4f60\u7684\u6697\u5668\uff01\u201d\u5404\u628a\u624b\u4e2d\u5175\u5203\uff0c\u6325\u6210\u4e00\u9053\u5706\u5708\uff0c\u8fd9\u4e24\u4eba\u529f\u529b\u751a\u9ad8\uff0c\u7adf\u628a\u5510 \u91d1\u5cf0\u6253\u51fa\u6765\u7684\u4e09\u53e3\u4e27\u95e8\u9489\u9707\u843d\u5728\u5730\uff0c\u6b63\u5728\u6d0b\u6d0b\u5f97\u610f\uff0c\u5ffd\u7136\u7709\u5fc3\u5267\u75db\uff0c\u60e8\u53eb\u4e00\u58f0\uff0c\u4e24\u4eba\u56db\u773c\uff0c \u5168\u7ed9\u6253\u778e\uff0c\u8fd8\u6709\u4e24\u540d\u8ddf\u7740\u903c\u8fdb\u7684\u6ee1\u6d32\u536b\u58eb\uff0c\u624b\u8155\u5173\u8282\u4e4b\u5904\u7a81\u7136\u4f3c\u7ed9\u8708\u86a3\u54ac\u4e86\u4e00\u53e3\uff0c\u53c8\u75db\u53c8\u9178 \u53c8\u9ebb\uff0c\u624b\u4e0a\u7684\u5175\u5668\u7adf\u81ea\u638c\u63e1\u4e0d\u7a33\uff0c\u5f53\u7684\u6389\u5728\u5730\u4e0a\uff0c\u6c49\u4eba\u536b\u58eb\u4e2d\u6709\u8bc6\u8d27\u7684\u53c8\u60ca\u53eb\u9053\uff1a\u201c\u5feb\u9000\uff0c \u8fd9\u662f\u767d\u7709\u9488\uff01\u201d\u56db\u5ddd\u5510\u5bb6\u7684\u6697\u5668\u5929\u4e0b\u65e0\u53cc\uff0c\u5176\u4e2d\u5c24\u4ee5\u4e27\u95e8\u9489\u548c\u767d\u7709\u9488\u6700\u4e3a\u5389\u5bb3\uff0c\u4e27\u95e8\u9489\u4e13\u6253 \u4eba\u8eab\u8981\u5bb3\u7a74\u9053\uff0c\u4e2d\u6697\u5668\u7684\u75db\u695a\u975e\u5e38\uff0c\u4f46\u5374\u65e0\u6bd2\uff0c\u62d4\u51fa\u94c1\u9489\uff0c\u89e3\u5f00\u7a74\u9053\u4e4b\u540e\uff0c\u4ecd\u7136\u53ef\u6551\uff1b\u90a3\u767d \u9aa8\u9488\u5219\u7ec6\u5982\u725b\u6bdb\uff0c\u88ab\u5c04\u4e2d\u7684\u4eba\u5e76\u4e0d\u89c9\u75db\uff0c\u4f46\u5374\u542b\u6709\u5267\u6bd2\uff0c\u5c11\u5219\u4e09\u65e5\uff0c\u591a\u5219\u4e03\u5929\uff0c\u767d\u7709\u9488\u4fbf\u987a \u7740\u8840\u7ba1\u8fdb\u5165\u5fc3\u7a9d\uff0c\u65e0\u6cd5\u53ef\u6551\u3002\u6c49\u4eba\u536b\u58eb\u89c1\u4ed6\u4f7f\u51fa\u8fd9\u4e24\u79cd\u6697\u5668\uff0c\u4e2a\u4e2a\u5fc3\u60ca\uff0c\u4e0d\u7ea6\u800c\u540c\u90fd\u9000\u51fa\u6570 \u4e08\u5f00\u5916\uff01 \u3000\u3000\u6ee1\u4eba\u536b\u58eb\u6b7b\u4f24\u4e86\u51e0\u4eba\u4e4b\u540e\uff0c\u4e5f\u90fd\u7eb7\u7eb7\u9000\u540e\u3002\u989d\u97f3\u548c\u5e03\u53eb\u9053\uff1a\u201c\u4ed6\u7528\u6697\u5668\uff0c\u4f60\u4eec\u4e0d\u77e5\u9053\u7528 \u6697\u5668\u4e48\uff1f\u54ce\u5440\uff0c\u54df\uff01\u201d\u539f\u6765\u4ed6\u6b63\u5728\u5267\u6597\u4e4b\u4e2d\uff0c\u8fd9\u4e00\u5206\u5fc3\uff0c\u88ab\u51af\u745b\u4e00\u5251\u4ece\u4ed6\u80a9\u5934\u524a\u8fc7\uff0c\u524a\u53bb\u4e86 \u4e00\u7247\u76ae\u8089\u3002 \u3000\u3000\u767d\u7709\u9488\u4e0d\u80fd\u53ca\u8fdc\uff0c\u536b\u58eb\u4eec\u9000\u5230\u6570\u4e08\u4e4b\u5730\uff0c\u7eb7\u7eb7\u53d1\u51fa\u6697\u5668\uff0c\u5510\u91d1\u5cf0\u5927\u7b11\u9053\uff1a\u201c\u9c81\u73ed\u95e8\u524d\u5f04 \u5927\u65a7\uff0c\u597d\uff0c\u770b\u6211\u7684\uff01\u201d\u6539\u53d1\u4e27\u95e8\u9489\u6bd2\u84ba\u85dc\u7b49\u4efd\u91cf\u8f83\u91cd\u7684\u6697\u5668\uff0c\u53c8\u628a\u536b\u58eb\u4eec\u518d\u903c\u9000\u4e00\u4e08\u4e4b\u5730\uff0c \u536b\u571f\u4eec\u53d1\u6765\u7684\u6697\u5668\uff0c\u5c04\u5230\u4ed6\u7684\u9762\u524d\u5df2\u662f\u65e0\u529b\uff0c\u88ab\u4ed6\u6216\u95ea\u6216\u63a5\uff0c\u968f\u63a5\u968f\u53d1\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5730\u4e0b\u4fbf \u6492\u6ee1\u6697\u5668\uff0c\u98de\u5200\u3001\u98de\u9556\u3001\u8896\u7bad\u3001\u94c1\u83b2\u5b50\u3001\u4e27\u95e8\u9489\u3001\u6bd2\u84ba\u85dc\uff0c\u4ec0\u4e48\u90fd\u6709\u3002\u536b\u58eb\u4e2d\u53c8\u6709\u51e0\u4eba\u53d7 \u4f24\uff0c\u4f46\u4ecd\u7136\u4e0e\u4ed6\u76f8\u6301\uff0c\u6ee1\u7a7a\u6697\u5668\uff0c\u53d1\u4e2a\u4e0d\u505c\uff01\u8fd9\u6837\u4e00\u6765\uff0c\u867d\u7136\u536b\u58eb\u4eec\u53d7\u4f24\u8f83\u591a\uff0c\u4f46\u5510\u91d1\u5cf0\u4e5f \u5f62\u52bf\u751a\u5371\uff0c\u4ed6\u6697\u5668\u4e0a\u7684\u529f\u592b\u867d\u662f\u5929\u4e0b\u7b2c\u4e8c\uff0c\u4f46\u654c\u4eba\u4f17\u591a\uff0c\u6697\u5668\u5982\u8757\uff0c\u82e5\u7136\u7a0d\u6709\u758f\u795e\uff0c\u90a3\u4fbf\u4e0d \u582a\u60f3\u50cf\uff01 \u3000\u3000\u9163\u6597\u4e2d\uff0c\u51af\u745b\u542c\u98ce\u8fa8\u5668\uff0c\u89c1\u5510\u91d1\u5cf0\u5f62\u52bf\u4e0d\u4f73\uff0c\u75be\u653b\u4e24\u5251\uff0c\u62bd\u8eab\u4fbf\u9000\uff0c\u53eb\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u4f60 \u7528\u987b\u5f25\u5251\u6cd5\uff0c\u7d27\u5b88\u4e9b\u65f6\uff0c\u6211\u53bb\u5e2e\u5fd9\u5510\u8001\u516c\u516c\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5feb\u53bb\uff01\u201d\u5251\u6cd5\u4e00\u53d8\uff0c\u6e38 \u9f99\u76d8\u5934\u76d6\u9876\uff0c\u5de6\u53f3\u98de\u821e\uff0c\u970e\u65f6\u95f4\uff0c\u4f46\u89c1\u94f6\u5149\u531d\u5730\uff0c\u7d2b\u7535\u98de\u7a7a\uff0c\u5510\u6653\u6f9c\u6574\u4e2a\u8eab\u5b50\u5c31\u5982\u85cf\u5728\u4e00\u5e62 \u9752\u8272\u5149\u5e62\u4e4b\u5185\uff0c\u989d\u97f3\u548c\u5e03\u62a2\u653b\u6570\u62db\uff0c\u6709\u5982\u78b0\u5230\u94dc\u5899\u94c1\u58c1\uff0c\u65e0\u6cd5\u653b\u8ff8\u3002\u989d\u97f3\u548c\u5e03\u5927\u6012\uff0c\u8fd0\u5c3d\u5185 \u529b\uff0c\u8fde\u53d1\u6570\u638c\uff0c\u5510\u6653\u6f9c\u4eba\u5728\u5149\u5e62\u4e4b\u5185\uff0c\u7ed9\u9707\u8361\u5f97\u6643\u4e2a\u4e0d\u505c\uff0c\u5251\u6cd5\u5374\u4ecd\u4e1d\u6beb\u4e0d\u677e\u61c8\uff0c\u6025\u5207\u4e4b \u95f4\uff0c\u989d\u97f3\u548c\u5e03\u5140\u662f\u5948\u4f55\u4ed6\u4e0d\u5f97\u3002 \u3000\u3000\u51af\u745b\u62bd\u8eab\u4e00\u9000\uff0c\u811a\u5c16\u70b9\u5730\uff0c\u4f7f\u4e2a\u201c\u7d2b\u71d5\u817e\u7a7a\u201d\u7684\u8eab\u6cd5\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u4ece\u536b\u58eb\u4eec\u5934\u9876\u98de\u8d8a\uff0c \u5979\u8eab\u4e0a\u7a7f\u6709\u949f\u4e07\u5802\u6240\u9001\u7684\u9632\u8eab\u5b9d\u7532\uff0c\u6ee1\u7a7a\u6697\u5668\u78b0\u5230\u5979\u7684\u8eab\u4e0a\uff0c\u7eb7\u7eb7\u843d\u4e0b\uff0c\u4e00\u77ac\u95f4\uff0c\u5979\u5df1\u843d\u5230 \u4e86\u5510\u91d1\u5cf0\u7236\u5973\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u536b\u58eb\u4eec\u89c1\u6697\u5668\u4f24\u5979\u4e0d\u5f97\uff0c\u5927\u662f\u60ca\u5947\uff0c\u5510\u91d1\u5cf0\u770b\u4e86\u5979\u4e00\u773c\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u610f\u4f3c\u751a\u4e3a\u611f\u6fc0\u3002\u51af \u745b\u4e0d\u4ff1\u6697\u5668\uff0c\u628a\u5b9d\u5251\u821e\u6210\u4e00\u9053\u94f6\u8679\uff0c\u62e6\u5728\u5510\u91d1\u5cf0\u7684\u9762\u524d\uff0c\u5c06\u654c\u4eba\u53d1\u6765\u7684\u6697\u5668\u7eb7\u7eb7\u78d5\u843d\u3002\u5510\u91d1 \u5cf0\u5219\u817e\u51fa\u624b\u6765\uff0c\u7528\u4e27\u95e8\u9489\u6253\u654c\u4eba\u7684\u7a74\u9053\uff0c\u8fd9\u4e00\u6765\uff0c\u53c8\u5c06\u536b\u58eb\u4eec\u903c\u9000\u6570\u6b65\uff0c\u53cc\u65b9\u4ecd\u662f\u50f5\u6301\u3002 \u3000\u3000\u5510\u6653\u6f9c\u5355\u72ec\u5bf9\u4ed8\u989d\u97f3\u548c\u5e03\uff0c\u53ea\u6709\u9632\u5b88\u4e4b\u529f\uff0c\u5374\u65e0\u53cd\u653b\u4e4b\u529b\uff0c\u989d\u97f3\u548c\u5e03\u677e\u4e86\u53e3\u6c14\uff0c\u8fde\u53d1\u6570 \u638c\uff0c\u5c06\u5510\u6653\u6f9c\u903c\u5f97\u6b65\u6b65\u540e\u9000\uff0c\u731b\u7136\u7eb5\u8eab\u4e00\u8dc3\uff0c\u53cc\u638c\u659c\u98de\uff0c\u4ece\u6697\u5668\u4e1b\u4e2d\u7a7f\u8fc7\uff0c\u6ee1\u7a7a\u6697\u5668\u7ed9\u4ed6\u7684 \u638c\u529b\u9707\u5f97\u5de6\u53f3\u7eb7\u98de\uff0c\u51af\u745b\u5927\u5403\u4e00\u60ca\uff0c\u77ed\u5251\u53cd\u624b\u4e00\u523a\uff0c\u5510\u91d1\u5cf0\u4e5f\u53d8\u4e86\u9762\u8272\uff0c\u5c06\u4efd\u91cf\u91cd\u7684\u6bd2\u84ba\u85dc \u6253\u53bb\uff0c\u4f46\u89c1\u989d\u97f3\u548c\u5e03\u8eab\u5f62\u4e00\u6b6a\uff0c\u4ece\u4ed6\u4eec\u7684\u4fa7\u8fb9\u7a7f\u51fa\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u9022\u81ea\u6251\u5230\u4f5b\u50cf\u4e0b\u9762\u90a3\u4e2a\u201c\u75c5 \u4eba\u201d\u7684\u8eab\u8fb9\u3002\u539f\u6765\u989d\u97f3\u548c\u5e03\u5e76\u4e0d\u662f\u6765\u6349\u5510\u91d1\u5cf0\u7236\u5973\uff0c\u800c\u662f\u4ece\u4ed6\u4eec\u8eab\u524d\u95ef\u8fc7\uff0c\u8981\u53bb\u6349\u90a3\u4e2a\u201c\u75c5 \u4eba\u201d\u3002 \u3000\u3000\u4f5b\u50cf\u4e0b\u90a3\u5806\u706b\u5df2\u7ecf\u7184\u706d\uff0c\u706b\u70df\u523a\u76ee\u545b\u5589\uff0c\u989d\u97f3\u548c\u5e03\u51b7\u7b11\u9053\uff1a\u201c\u8d1d\u52d2\u7237\uff0c\u4f60\u4f55\u82e6\u5728\u8fd9\u91cc\u53d7 \u7f6a\uff0c\u8fd8\u662f\u968f\u5974\u624d\u56de\u5bab\u53bb\u5427\u3002\u201d\u4f38\u624b\u63ed\u90a3\u75c5\u4eba\u7684\u88ab\u76d6\uff0c\u5ffd\u542c\u5f97\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u989d\u97f3\u548c\u5e03\u9762\u4e0a\u7740 \u4e86\u4e00\u4e0b\uff0c\u9976\u662f\u4ed6\u7ec3\u6709\u91d1\u949f\u7f69\u94c1\u5e03\u886b\u7684\u529f\u592b\uff0c\u9762\u4e0a\u4e5f\u70ed\u8fa3\u8fa3\u4f5c\u75db\u3002\u989d\u97f3\u548c\u5e03\u53cd\u624b\u62ff\u4f4f\u4e86\u90a3\u4eba\u7684 \u624b\u8155\uff0c\u4f7f\u52b2\u4e00\u62d6\uff0c\u90a3\u4eba\u7ffb\u8eab\u5750\u8d77\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u597d\uff0c\u6211\u65e9\u77e5\u5141\u796f\u4e0d\u4f1a\u653e\u8fc7\u6211\u4e86\uff0c\u4f60\u6b64\u6765\u53ea \u662f\u4e3a\u6211\u4e48\uff1f\u201d\u989d\u97f3\u548c\u5e03\u9053\uff1a\u201c\u7687\u4e0a\u4e13\u8bda\u8bf7\u8d1d\u52d2\u56de\u4eac\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u628a\u8fd9\u5bf9\u7236\u5973 \u653e\u8d70\u5427\uff01\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\u5927\u51fa\u4f17\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u4e24\u65b9\u7684\u6697\u5668\u4ea4\u950b\uff0c\u4e5f\u6682\u65f6\u4f11\u6b62\u4e0b\u6765\uff0c\u5510\u6653\u6f9c\u51dd\u795e\u6ce8\u89c6\uff0c\u53ea\u89c1 \u90a3\u201c\u75c5\u4eba\u201d\u867d\u662f\u5f62\u5bb9\u6194\u60b4\uff0c\u5374\u81ea\u6709\u4e00\u79cd\u51db\u7136\u4e0d\u53ef\u4fb5\u72af\u7684\u5a01\u4eea\uff0c\u731b\u7136\u7701\u8d77\uff0c\u8fd9\u4eba\u539f\u6765\u5c31\u662f\u81ea\u5df1 \u4ee5\u524d\u5728\u7687\u5bab\u4e2d\u66fe\u7ecf\u89c1\u8fc7\u9762\u7684\u4e5d\u7687\u5b50\u5141\u642a\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u5361\u55e4\u201d\u4e00\u54cd\uff0c\u989d\u97f3\u548c\u5e03\u5c06\u4e5d\u7687\u5b50\u7684\u8155\u9aa8\u626d\u65ad\uff0c\u5c06\u4ed6\u53cc\u624b\u53cd\u526a\uff0c\u63d0\u4e86\u8d77\u6765\uff0c\u8eac\u8eab \u8bf4\u9053\uff1a\u201c\u53ea\u8981\u4ed6\u4eec\u4e0d\u4e0e\u6211\u4e3a\u96be\uff0c\u5974\u624d\u81ea\u5f53\u9075\u547d\u3002\u201d\u539f\u6765\u96cd\u6b63\u6839\u57fa\u5df1\u56fa\u4e4b\u540e\uff0c\u7b2c\u4e00\u6b65\u5c06\u4f17\u7687\u5b50 \u7684\u7fbd\u7ffc\u526a\u9664\uff0c\u7b2c\u4e8c\u6b65\u4fbf\u5c06\u4ee5\u524d\u6562\u4e8e\u548c\u81ea\u5df1\u4e89\u593a\u7687\u4f4d\u7684\u5144\u5f1f\u4e00\u4e00\u501f\u9898\u6740\u6389\uff0c\u5176\u4e2d\u4e5d\u7687\u5b50\u5141\u642a\u4e0e \u5341\u7687\u5b50\u5141\u4fc4\uff0c\u7cbe\u901a\u6b66\u529f\uff0c\u95fb\u98ce\u5148\u9041\uff0c\u989d\u97f3\u548c\u5e03\u6b64\u756a\u5343\u91cc\u8ffd\u8e2a\uff0c\u6240\u4e3a\u7684\u5c31\u662f\u5141\u642a\u3002\u81f3\u4e8e\u5510\u6653\u6f9c \u8bf8\u4eba\uff0c\u4e0d\u8fc7\u662f\u5076\u7136\u78b0\u7740\u7f62\u4e86\u3002\u989d\u97f3\u548c\u5e03\u5fc3\u60f3\uff1a\u5510\u6653\u6f9c\u4e0e\u51af\u745b\u5251\u6cd5\u7cbe\u5999\u5f02\u5e38\uff0c\u90a3\u8001\u513f\u7684\u6697\u5668\u4e5f \u5341\u5206\u5389\u5bb3\uff0c\u7f20\u6597\u4e0b\u53bb\uff0c\u53ea\u6050\u53cd\u6709\u610f\u5916\uff0c\u6240\u4ee5\u5141\u642a\u63d0\u51fa\uff0c\u53eb\u989d\u97f3\u548c\u5e03\u5c06\u4ed6\u4eec\u653e\u8d70\uff0c\u989d\u97f3\u548c\u5e03\u4e5f \u4fbf\u4e50\u5f97\u505a\u4e2a\u987a\u6c34\u4eba\u60c5\u3002 \u3000\u3000\u5510\u91d1\u5cf0\u5927\u529b\u60ca\u6115\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u539f\u6765\u5f53\u4eca\u7687\u4e0a\u8fd9\u6837\u65e0\u60c5\uff0c\u9aa8\u8089\u5c1a\u4e14\u76f8\u6b8b\uff0c\u4f55\u51b5\u5916\u4eba\u3002\u6211 \u4ee5\u524d\u8ba9\u5973\u5a7f\u5728\u516c\u95e8\u5f53\u5dee\uff0c\u5373\u7b97\u4e0d\u6b7b\u4e8e\u975e\u547d\uff0c\u4e5f\u65ad\u4e0d\u4f1a\u6709\u597d\u7684\u4e0b\u573a\u3002\u6ce8\u76ee\u770b\u65f6\uff0c\u4f46\u89c1\u5141\u642a\u75db\u5f97 \u9ec4\u8c46\u822c\u5927\u7684\u6c57\u73e0\uff0c\u6ef4\u4e86\u4e0b\u6765\uff0c\u5374\u54ac\u7740\u7259\u6839\u62b5\u53d7\uff0c\u4e0d\u54fc\u4e00\u58f0\u3002\u989d\u97f3\u548c\u5e03\u53c8\u8eac\u8eab\u8bf4\u9053\uff1a\u201c\u65f6\u5019\u4e0d \u65e9\uff0c\u8bf7\u8d1d\u52d2\u8d70\u5427\uff01\u201d \u3000\u3000\u5141\u642a\u5ffd\u5730\u4e00\u58f0\u60e8\u7b11\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u770b\u89c1\u4e86\uff0c\u4f46\u613f\u4eca\u540e\u751f\u751f\u4e16\u4e16\uff0c\u5927\u5bb6\u90fd\u4e0d\u8981\u751f\u5728 \u7687\u5bb6\uff01\u201d\u5510\u6653\u6f9c\u4e00\u95fb\u6b64\u8bed\uff0c\u5165\u8033\u94bb\u5fc3\uff0c\u624b\u6309\u5b9d\u5251\uff0c\u4fbf\u60f3\u51b2\u51fa\u3002\u51af\u745b\u5728\u4ed6\u8eab\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60 \u65e9\u5df2\u4e0d\u662f\u7687\u5bb6\u7684\u4eba\u4e86\uff0c\u6211\u4eec\u8c01\u4e5f\u6ca1\u6709\u628a\u4f60\u5f53\u505a\u7687\u5b50\uff0c\u8eab\u4e16\u4e4b\u6068\uff0c\u65e9\u8be5\u5fd8\u6389\u3002\u6b64\u4eba\u4ee5\u524d\u4e0e\u5141\u796f \u4e89\u4f4d\uff0c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u597d\u4e1c\u897f\uff0c\u4f60\u4f55\u5fc5\u4e3a\u4ed6\u62fc\u547d\uff1f\u201d\u5510\u6653\u6f9c\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u662f\u60f3\u6551\u4ed6\uff0c \u6211\uff0c\u6211\u2026\u2026\u201d\u5fc3\u60c5\u590d\u6742\u4e4b\u6781\uff0c\u96be\u4e8e\u89e3\u91ca\uff0c\u8bf4\u4e86\u4e00\u534a\uff0c\u8bf4\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u989d\u97f3\u548c\u5e03\u62c9\u7740\u5141\u642a\uff0c\u7f13\u7f13\u8d70\u51fa\uff0c\u5ffd\u6709\u4e00\u540d\u536b\u58eb\u6307\u7740\u5510\u91d1\u5cf0\u6240\u62b1\u7684\u5e74\u5bff\u8bf4\u9053\uff1a\u201c\u7980\u5927\u4eba\uff0c \u8fd9\u662f\u5e74\u7fb9\u5c27\u7684\u5b69\u5b50\u3002\u521a\u624d\u90a3\u4e24\u4eba\u4fbf\u662f\u5e74\u7fb9\u5c27\u7684\u5fc3\u8179\u6b66\u58eb\u3002\u201d\u5510\u91d1\u5cf0\u8fd9\u4e00\u60ca\u66f4\u751a\uff0c\u671b\u7740\u6000\u4e2d\u7684 \u5b69\u5b50\uff0c\u53ea\u89c1\u8fd9\u5b69\u5b50\u5413\u5f97\u9762\u9752\u5507\u767d\uff0c\u53eb\u9053\uff1a\u201c\u516c\u516c\u6551\u6211\uff0c\u6211\u4e0d\u53bb\uff0c\u6211\u4e0d\u53bb\uff01\u201d \u3000\u3000\u989d\u97f3\u548c\u5e03\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u771f\u662f\u597d\u673a\u7f18\uff01\u201d\u626b\u4e86\u5510\u91d1\u5cf0\u4e00\u773c\uff0c\u559d\u9053\uff1a\u201c\u5140\u90a3\u8001\u513f\uff0c\u4f60\u662f\u5e74 \u7fb9\u5c27\u7684\u4ec0\u4e48\u4eba\uff1f\u201d\u5510\u91d1\u5cf0\u9053\uff1a\u201c\u4ec0\u4e48\u90fd\u4e0d\u662f\u3002\u201d\u989d\u97f3\u548c\u5e03\u559d\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u628a\u90a3\u5b69\u5b50\u7ed9 \u6211\uff0c\u9976\u4f60\u4e0d\u6b7b\uff01\u201d\u5e74\u5bff\u201c\u54c7\u201d\u7684\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\uff0c\u5510\u8d5b\u82b1\u6025\u9053\uff1a\u201c\u7239\uff0c\u4e0d\u8981\u4ea4\u7ed9\u4ed6\u4eec\u3002\u201d \u3000\u3000\u989d\u97f3\u548c\u5e03\u52c3\u7136\u4f5c\u8272\uff0c\u6b63\u8981\u53d1\u4f5c\uff0c\u5ffd\u542c\u5f97\u5bfa\u5916\u4e00\u9635\u53ee\u53ee\u7684\u94c3\u58f0\uff0c\u4f20\u4e86\u8fdb\u6765\uff0c\u63a5\u7740\u8fde\u58f0\u60e8 \u53eb\uff0c\u51af\u745b\u5927\u559c\u53eb\u9053\uff1a\u201c\u6b66\u8001\u524d\u8f88\u6765\u4e86\uff01\u201d\u989d\u97f3\u548c\u5e03\u5927\u6012\uff0c\u8dc3\u51fa\u5bfa\u95e8\uff0c\u5728\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u4e00\u4e2a \u6c5f\u6e56\u90ce\u4e2d\uff0c\u53f3\u624b\u63d0\u7740\u864e\u6491\uff0c\u5de6\u624b\u6447\u7740\u94dc\u94c3\uff0c\u76f4\u5411\u81ea\u5df1\u8d70\u6765\uff0c\u95e8\u5916\u51e0\u4e2a\u628a\u98ce\u7684\u6043\u58eb\u90fd\u5df2\u88ab\u4ed6\u6253 \u5012\u4e86\u3002 \u3000\u3000\u989d\u97f3\u548c\u5e03\u77aa\u76ee\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u7ed9\u6211\u7ad9\u7740\uff01\u201d\u6765\u7684\u6b63\u662f\u6b66\u743c\u7476\u7684\u5f1f\u5f1f\u6b66\u6210\u5316\uff0c\u53ea\u89c1\u4ed6\u51b7 \u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u5927\u7684\u67b6\u5b50\uff01\u4f60\u8981\u95ee\u6211\u662f\u8c01\u5417\uff1f\u6211\u662f\u66ff\u4f60\u62db\u9b42\u7684\u4f7f\u8005\uff01\u201d\u6447\u8d77\u94dc\u94c3\uff0c\u53ee\u53ee\u4f5c \u54cd\u3002\u989d\u97f3\u548c\u5e03\u5927\u6012\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5de6\u638c\u63a8\u51fa\uff0c\u6b66\u6210\u5316\u8eab\u5f62\u6beb\u4e0d\u6643\u52a8\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u63d0\u8d77\u864e\u6491\uff0c\u8fce \u5934\u4fbf\u6253\uff0c\u989d\u97f3\u548c\u5e03\u62c2\u5c18\u4e00\u7ed5\uff0c\u5c06\u4ed6\u864e\u6491\u7f20\u7740\uff0c\u5374\u4ecd\u662f\u7ed9\u4ed6\u903c\u9000\u4e24\u6b65\u3002\u989d\u97f3\u548c\u5e03\u5927\u60ca\uff0c\u6697\u8fd0\u5185 \u529b\u5411\u65c1\u4e00\u626f\uff1b\u6b66\u6210\u5316\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u7684\u864e\u6491\u867d\u6ca1\u8131\u624b\uff0c\u4f46\u4e5f\u4e0d\u80fd\u968f\u5fc3\u6240\u6b32\uff0c\u76f4\u6253\u8fc7\u53bb\u3002\u4e24\u4eba \u5404\u8fd0\u5185\u529b\u76f8\u6597\uff0c\u5927\u5bb6\u90fd\u77e5\u9053\u78b0\u5230\u4e86\u9876\u5c16\u513f\u7684\u9ad8\u624b\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u8c01\u4e5f\u65e0\u6cd5\u5948\u4f55\u3002 \u3000\u3000\u51af\u745b\u98de\u6b65\u62a2\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u6b66\u8001\u524d\u8f88\uff0c\u4f60\u6765\u5f97\u597d\uff01\u201d\u6b66\u6210\u5316\u9053\uff1a\u201c\u8fd9\u53ae\u6043\u7740\u4eba\u591a\uff0c\u65af\u8d1f\u4e86 \u4f60\u4e48\uff1f\u201d\u51af\u745b\u7b11\u9053\uff1a\u201c\u4ed6\u8fd8\u6ca1\u6709\u90a3\u4e48\u5927\u7684\u80fd\u8010\u3002\u201d\u8f6c\u9762\u5bf9\u989d\u97f3\u548c\u5e03\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u518d\u6597\u4e48\uff1f\u201d \u989d\u97f3\u548c\u5e03\u5c06\u62c2\u5c18\u4e00\u6536\uff0c\u6a2a\u8dc3\u4e09\u6b65\uff0c\u9053\uff1a\u201c\u597d\uff0c\u5f7c\u6b64\u7f62\u624b\uff0c\u65e5\u540e\u518d\u89c1\uff0c\u6211\u53ef\u4e0d\u9976\u4f60\u4e86\u3002\u201d\u51af\u745b \u7b11\u9053\uff1a\u201c\u90a3\u5c31\u4ee5\u540e\u518d\u77a7\u5427\u3002\u201d\u6b66\u6210\u5316\u6a2a\u76ee\u626b\u4e86\u989d\u97f3\u548c\u5e03\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5982\u6b64\u6b66\u529f\uff0c\u53ef\u60dc\u53ef \u60dc\u3002\u201d\u989d\u97f3\u548c\u5e03\u6012\u9053\uff1a\u201c\u4ec0\u4e48\u53ef\u60dc\uff1f\u201d\u6b66\u6210\u5316\u9053\uff1a\u201c\u4e86\u56e0\u7684\u4e0b\u573a\uff0c\u96be\u9053\u4f60\u4e0d\u77e5\u9053\u4e48\uff1f\u201d\u989d\u97f3 \u548c\u5e03\u6b63\u662f\u96cd\u6b63\u8058\u6765\u8865\u4e86\u56e0\u4e4b\u7f3a\u7684\uff0c\u95fb\u8a00\u4e00\u51db\uff0c\u63d0\u8d77\u62c2\u5c18\uff0c\u6b66\u6210\u5316\u9053\uff1a\u201c\u662f\u4e0d\u662f\u8fd8\u8981\u4e0e\u6211\u6253\u4e2a \u75db\u5feb\uff1f\u201d\u989d\u97f3\u548c\u5e03\u4e00\u8a00\u4e0d\u53d1\uff0c\u628a\u624b\u4e00\u62db\uff0c\u5e26\u4e86\u536b\u58eb\u4eec\u75be\u4e0b\u5c71\u53bb\u3002\u5141\u642a\u88ab\u626d\u65ad\u8155\u9aa8\uff0c\u652f\u6301\u4e0d \u4f4f\uff0c\u5df2\u75db\u5f97\u6655\u8e76\u8fc7\u53bb\uff0c\u4e5f\u88ab\u536b\u58eb\u80cc\u4e0b\u5c71\u4e86\u3002 \u3000\u3000\u6b66\u6210\u5316\u7b11\u9053\uff1a\u201c\u6211\u542c\u8bf4\u4f60\u4eec\u518d\u4e0b\u5929\u5c71\uff0c\u6599\u4f60\u4eec\u5fc5\u7136\u8981\u53bb\u627e\u5415\u56db\u5a18\uff0c\u5374\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u76f8 \u89c1\u3002\u4f60\u5e08\u7236\u597d\u5417\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u54b1\u4eec\u4e14\u5148\u522b\u8c08\uff0c\u53bb\u77a7\u77a7\u5510\u91d1\u5cf0\u5427\u3002\u201d\u6b66\u6210\u5316\u9053\uff1a\u201c\u5510\u91d1\u5cf0\u662f \u8c01\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u4e00\u4f4d\u6b66\u6797\u524d\u8f88\uff0c\u6697\u5668\u5929\u4e0b\u65e0\u53cc\u3002\u201d\u6b66\u6210\u5316\u9053\uff1a\u201c\u54e6\uff0c\u90a3\u662f\u56db\u5ddd\u5510\u5bb6\u7684\u4eba \u4e86\uff0c\u4f60\u600e\u4e48\u4f1a\u8ba4\u5f97\u4ed6\uff1f\u201d \u3000\u3000\u8fb9\u8bf4\u8fb9\u8d70\u8fdb\u5e99\u4e2d\uff0c\u53ea\u89c1\u5510\u91d1\u5cf0\u4ecd\u7136\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u800c\u8272\u7070\u767d\uff0c\u5510\u8d5b\u82b1\u62b1\u7740\u5e74\u5bff\uff0c\u6101\u5bb9\u6ee1 \u9762\uff0c\u5c0f\u58f0\u95ee\u9053\uff1a\u201c\u7239\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u201d\u5510\u91d1\u5cf0\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\u5728\u4fdd\u5f97\u8fd9\u5b69\u5b50\u7684\u5c0f\u547d\u513f\u3002\u6211 \u4e0d\u4e2d\u7528\u4e86\uff0c\u4f60\u62b1\u4ed6\u56de\u53bb\u5427\u3002\u6211\u8ba4\u4ed6\u505a\u5916\u5b59\u3002\u201d\u5510\u8d5b\u82b1\u6cea\u5982\u96e8\u4e0b\uff0c\u9053\uff1a\u201c\u7239\uff0c\u4f60\u53d7\u4e86\u4ec0\u4e48\u5389\u5bb3 \u7684\u4f24\u5427\uff1f\u54b1\u4eec\u836f\u56ca\u4e2d\u6709\u7684\u662f\u597d\u836f\u3002\u201d\u5510\u91d1\u5cf0\u82e6\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u836f\u90fd\u6ca1\u6709\u7528\uff0c\u6211\u53d7\u4e86\u90a3\u53ae\u638c\u529b\u9707 \u8361\uff0c\u53c8\u82e6\u6218\u4e86\u8fd9\u4e9b\u65f6\u523b\uff0c\u529b\u7aed\u795e\u75b2\uff0c\u6709\u5982\u6cb9\u5c3d\u706f\u67af\uff0c\u54ea\u662f\u836f\u7269\u6240\u80fd\u6551\u6cbb\uff1f\u201d\u51af\u745b\u5ffd\u63a5\u53e3\u9053\uff1a \u201c\u6211\u5c31\u6709\u836f\u7269\u53ef\u4ee5\u6551\u6cbb\u3002\u201d\u5510\u8d5b\u82b1\u5927\u559c\uff0c\u987e\u4e0d\u5f97\u4ee5\u524d\u7684\u4ec7\u6068\uff0c\u5fd9\u9053\uff1a\u201c\u90a3\u4e48\u8bf7\u4f60\u8d76\u5feb\u6551 \u5427\u3002\u201d\u5510\u91d1\u5cf0\u610f\u4f3c\u4e0d\u4fe1\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u836f\u7269\uff1f\u201d\u51af\u745b\u53d6\u51fa\u4e00\u4e2a\u5c0f\u5c0f\u7684\u94f6\u74f6\uff0c\u503e\u51fa\u4e09\u7c92\u78a7\u7eff\u8272 \u7684\u836f\u4e38\uff0c\u987f\u65f6\u6e05\u9999\u6251\u9f3b\uff0c\u7cbe\u795e\u4e3a\u723d\u3002\u51af\u745b\u5c06\u836f\u4e38\u9012\u7ed9\u4e86\u5510\u91d1\u5cf0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u662f\u7528\u5929\u5c71\u96ea\u83b2 \u81ea\u5df1\u5236\u7684\u78a7\u7075\u4e39\uff0c\u8865\u6c14\u65fa\u8840\uff0c\u7597\u6bd2\u89e3\u4f24\uff0c\u6700\u662f\u6709\u6548\u3002\u4ee5\u4f60\u8001\u4eba\u5bb6\u6839\u57fa\u4e4b\u597d\uff0c\u5f97\u5b83\u56fa\u672c\u57f9\u539f\uff0c \u4e0d\u4f46\u6027\u547d\u65e0\u5fe7\uff0c\u6b66\u529f\u4e5f\u4e0d\u81f3\u4e8e\u6709\u6240\u51cf\u635f\u3002\u201d \u3000\u3000\u5510\u91d1\u5cf0\u5c06\u4e09\u7c92\u78a7\u7075\u4e39\u54bd\u4e0b\uff0c\u53ea\u89c9\u4e00\u80a1\u6e05\u51c9\u4e4b\u6c14\uff0c\u76f4\u900f\u4e39\u7530\uff0c\u7b11\u9053\uff1a\u201c\u5929\u5c71\u96ea\u83b2\uff0c\u540d\u4e0d\u865a \u4f20\uff0c\u679c\u7136\u662f\u7075\u4e39\u5999\u836f\u3002\u201d\u5e74\u5bff\u773c\u77aa\u77aa\u7684\u671b\u7740\u5510\u91d1\u5cf0\uff0c\u51af\u745b\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u7684\u5c0f\u547d\u513f\u662f\u62fe\u56de\u6765 \u4e86\uff0c\u4f60\u77e5\u9053\u8be5\u600e\u6837\u9053\u8c22\u5417\uff1f\u201d\u5e74\u5bff\u6781\u4e3a\u4e56\u5de7\uff0c\u8d70\u4e0b\u5730\u6765\uff0c\u5148\u8dea\u5728\u5730\u4e0a\uff0c\u5bf9\u5510\u8d5b\u82b1\u78d5\u4e86\u4e2a\u54cd \u5934\uff0c\u53eb\u4e86\u4e00\u58f0\u201c\u5988\u5988\u201d\u3002\u5510\u8d5b\u82b1\u559c\u5f97\u7709\u5f00\u773c\u7b11\uff0c\u8fde\u9053\uff1a\u201c\u4e56\u513f\u5b50\uff0c\u4e56\u513f\u5b50\u3002\u201d\u5e74\u5bff\u53c8\u5bf9\u5510\u91d1 \u5cf0\u78d5\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u53eb\u9053\uff1a\u201c\u516c\u516c\u3002\u201d\u5510\u8d5b\u82b1\u7b11\u9053\uff1a\u201c\u8be5\u53eb\u5916\u516c\u3002\u201d\u5510\u91d1\u5cf0\u4e00\u7b11\u5c06\u4ed6\u6402\u5165\u6000\u4e2d\u3002 \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u5f88\u50cf\u4ed6\u7684\u7236\u4eb2\uff0c\u806a\u660e\u5f97\u5f88\u3002\u201d\u51af\u745b\u9053\uff1a\u201c\u4f46\u613f\u4ed6\u4e0d\u8981\u5168\u50cf\u7236\u4eb2\u3002\u201d \u6b66\u6210\u5316\u4e0e\u5510\u91d1\u5cf0\u5bd2\u6684\u51e0\u53e5\uff0c\u5404\u9053\u4ef0\u6155\u3002\u6b66\u6210\u5316\u770b\u770b\u5929\u8272\uff0c\u53ea\u89c1\u66d9\u5149\u5fae\u9732\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8981\u5148 \u8d70\u4e00\u6b65\uff0c\u4f60\u518d\u6253\u5750\u4e00\u4e2a\u65f6\u523b\uff0c\u6c14\u529b\u4fbf\u53ef\u6062\u590d\uff0c\u6055\u6211\u4eec\u4e0d\u518d\u966a\u4f60\u4e86\u3002\u201d\u5510\u8d5b\u82b1\u4e00\u518d\u5411\u51af\u745b\u9053 \u8c22\uff0c\u5510\u5bb6\u7236\u5973\u4e0e\u51af\u5bb6\u59d0\u59b9\u4e4b\u95f4\u7684\u4ec7\u6068\uff0c\u8fd9\u624d\u771f\u6b63\u89e3\u6d88\u4e86\u3002 \u3000\u3000\u6b66\u6210\u5316\u548c\u51af\u5510\u4e8c\u4eba\u5728\u6668\u5149\u4e2d\u8d70\u4e0b\u5d69\u5c71\uff0c\u6b66\u6210\u5316\u9053\uff1a\u201c\u4f60\u4eec\u662f\u8d76\u53bb\u9099\u5c71\u5417\uff1f\u201d\u51af\u745b\u9053\uff1a \u201c\u6b63\u662f\u3002\u6211\u7684\u5988\u5988\u4e5f\u5728\u9099\u5c71\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d\u6b66\u6210\u6bd4\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5988\u5988\u5df2\u4e0d\u5728\u9099\u5c71\u4e86\u3002\u4f60\u8981\u89c1 \u5979\u5417\uff1f\u201d\u51af\u745b\u60ca\u9053\uff1a\u201c\u53ef\u662f\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u6b66\u6210\u5316\u7b11\u9053\uff1a\u201c\u672c\u6765\u8981\u51fa\u4e8b\u60c5\u7684\uff0c\u53ef\u662f\u73b0\u5728\u6ca1 \u4e8b\u4e86\u3002\u96cd\u6b63\u8fd9\u5c0f\u5b50\u597d\u72e0\uff0c\u5728\u524a\u4e86\u5e74\u7fb9\u5c27\u7684\u5175\u6743\u4e4b\u540e\uff0c\u5bf9\u5404\u5730\u5fd7\u58eb\u641c\u6355\u66f4\u4e25\uff0c\u4ed6\u63a2\u51fa\u9099\u5c71\u6709\u8c6a \u6770\u5578\u805a\uff0c\u4fbf\u6d3e\u5927\u519b\u56f4\u5c71\uff0c\u5e78\u800c\u5415\u56db\u5a18\u65e9\u5f97\u6d88\u606f\uff0c\u53eb\u6211\u9001\u4f60\u7684\u6bcd\u4eb2\u3001\u5916\u516c\u548c\u5f20\u5929\u6c60\u7b49\u4eba\u5230\u5929\u5c71 \u53bb\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u8fd9\u6837\u5b89\u6392\u5f88\u597d\uff0c\u4ed6\u4eec\u90fd\u662f\u6bd5\u751f\u60a3\u96be\uff0c\u4e5f\u8be5\u5b89\u5ea6\u665a\u5e74\u4e86\u3002\u6b66\u8001\u524d\u8f88\uff0c\u4f60\u5728 \u54ea\u513f\u89c1\u7740\u5415\u56db\u5a18\u7684\uff1f\u201d\u6b66\u6210\u5316\u9053\uff1a\u201c\u5728\u4eac\u57ce\u9644\u8fd1\u3002\u5415\u56db\u5a18\u60f3\u5165\u4eac\u523a\u6740\u96cd\u6b63\uff0c\u4f46\u4eac\u90fd\u9632\u8303\u6781 \u4e25\uff0c\u4e00\u76f4\u6ca1\u673a\u4f1a\u4e0b\u624b\u3002\u6211\u672c\u6765\u662f\u60f3\u5bfb\u627e\u674e\u6cbb\u7684\uff0c\u9047\u89c1\u4e86\u5415\u56db\u5a18\uff0c\u624d\u77e5\u4ed6\u4eec\u5df2\u53bb\u4e86\u56db\u5ddd\u3002\u201d\u51af \u745b\u9053\uff1a\u201c\u90a3\u4e48\u6211\u7684\u5988\u5988\u73b0\u5728\u54ea\u91cc\uff1f\u201d\u6b66\u6210\u5316\u9053\uff1a\u201c\u6211\u4eec\u5728\u8def\u4e0a\u5206\u6279\u884c\u8d70\uff0c\u6211\u66ff\u4ed6\u4eec\u6253\u524d\u7ad9\u63a2 \u8def\uff0c\u6628\u665a\u7ecf\u8fc7\u5d69\u5c71\uff0c\u4ed6\u4eec\u5c31\u5728\u5c71\u4e0b\u4e00\u5bb6\u4eba\u5bb6\u4f4f\u5bbf\u3002\u201d\u51af\u745b\u5927\u559c\uff0c\u5f53\u5373\u548c\u5510\u6653\u6f9c\u53bb\u62dc\u89c1\u6bcd\u4eb2\uff0c \u89c1\u9762\u4e4b\u540e\uff0c\u81ea\u6709\u4e00\u756a\u4eb2\u70ed\uff0c\u4e0d\u5fc5\u7ec6\u8868\u3002 \u3000\u3000\u51af\u7433\u674e\u6cbb\u4e0e\u5173\u4e1c\u56db\u4fa0\u7b49\u4eba\u8d70\u4e86\u4e00\u4e2a\u591a\u6708\uff0c\u5230\u8fbe\u9099\u5c71\uff0c\u4f46\u89c1\u5c71\u4e0a\u4e00\u7247\u74e6\u783e\uff0c\u4e0d\u4f46\u8fd9\u51e0\u5e74\u6765 \u6240\u5efa\u7684\u623f\u5c4b\u90fd\u5168\u70e7\u4e86\uff0c\u8fde\u72ec\u81c2\u795e\u5c3c\u4ee5\u524d\u6240\u4f4f\u7684\u5c3c\u5eb5\u4e5f\u5df2\u8361\u4e3a\u5e73\u5730\uff0c\u4f17\u4eba\u76ee\u77aa\u53e3\u5446\uff0c\u53ea\u9053\u662f\u706b \u70e7\u5d69\u5c71\u5c11\u6797\u5bfa\u7684\u4e00\u5e55\uff0c\u91cd\u6f14\u4e8e\u9099\u5c71\uff0c\u51af\u7433\u674e\u6cbb\u5c24\u5176\u7740\u6025\uff0c\u5ffd\u95fb\u5f97\u4e1b\u6797\u8302\u8349\u4e4b\u95f4\uff0c\u5c71\u79bd\u4e71\u9e23\uff0c \u535c\u535c\u98de\u8d77\uff0c\u8f6c\u77ac\u95f4\u534a\u7a7a\u547c\u547c\u58f0\u54cd\uff0c\u53ea\u89c1\u4e24\u53ea\u5927\u9e4f\u9e1f\u4e00\u9ed1\u4e00\u767d\uff0c\u53cc\u7fc5\u5f20\u5f00\uff0c\u5982\u78e8\u76d8\u5927\u5c0f\uff0c\u98de\u4e86 \u4e0b\u6765\uff0c\u5728\u4f17\u4eba\u5934\u9876\u4e0a\u4f4e\u98de\u76d8\u65cb\uff0c\u7384\u98ce\u9053\u957f\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u72ec\u81c2\u795e\u5c3c\u5f53\u5e74\u6240\u517b\u7684\u4e24\u5934\u5927\u9e4f\uff0c\u60f3\u4e0d \u5230\u5982\u4eca\u8fd8\u5728\u3002\u201d\u90a3\u4e24\u5934\u5927\u9e4f\u76d8\u65cb\u4e00\u9635\uff0c\u53c8\u5411\u5c71\u8170\u98de\u53bb\uff0c\u67f3\u5148\u5f00\u9053\uff1a\u201c\u8fd9\u4e24\u5934\u9e4f\u9e1f\uff0c\u751a\u6709\u7075 \u6027\uff0c\u5b83\u4eec\u53ef\u722a\u88c2\u731b\u864e\uff0c\u4f46\u89c1\u4e86\u719f\u4eba\uff0c\u5374\u4ece\u4e0d\u4fb5\u72af\u3002\u6211\u4eec\u5230\u8fc7\u51e0\u6b21\u9099\u5c71\uff0c\u5b83\u4eec\u5b9a\u7136\u8ba4\u5f97\u3002\u770b\u5b83 \u4eec\u8fd9\u6837\u4f4e\u98de\u9e23\u53eb\uff0c\u597d\u50cf\u662f\u5411\u4e3b\u4eba\u62a5\u8baf\u4e00\u822c\uff0c\u96be\u9053\u8fd8\u6709\u4eba\u5728\u9099\u5c71\u4e4b\u4e0a\u4e48\uff1f\u201d \u3000\u3000\u4f17\u4eba\u968f\u7740\u5927\u9e4f\u6240\u98de\u7684\u65b9\u5411\u8d70\u53bb\uff0c\u8d70\u5230\u5c71\u5773\u5ce1\u8c37\u4e4b\u5904\uff0c\u84e6\u7136\u5f00\u6717\uff0c\u4f46\u89c1\u91ce\u82b1\u904d\u5730\uff0c\u53e4\u69d0\u5939 \u9053\uff0c\u5bf9\u9762\u5c71\u5cf0\u5012\u6302\u4e0b\u6765\u7684\u7011\u5e03\uff0c\u55b7\u73e0\u6e85\u7389\uff0c\u5728\u4e3d\u65e5\u7167\u5c04\u4e4b\u4e0b\uff0c\u5e7b\u6210\u4e03\u5f69\u3002\u7384\u98ce\u9053\uff1a\u201c\u4ece\u8fd9\u91cc \u8fc7\u53bb\uff0c\u5c31\u662f\u72ec\u81c2\u795e\u5c3c\u7684\u5893\u5730\u4e86\u3002\u60f3\u5fc5\u662f\u56e0\u8fd9\u91cc\u5c71\u52bf\u7edd\u9669\uff0c\u5b98\u519b\u7aa5\u671b\u65e0\u4eba\uff0c\u53c8\u65e0\u623f\u5c4b\uff0c\u6240\u4ee5\u5c31 \u4e0d\u4e0b\u6765\u653e\u706b\u4e86\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u7136\u542c\u5f97\u4e00\u58f0\u957f\u7b11\uff0c\u5415\u56db\u5a18\u4e0e\u51af\u745b\u7a81\u7136\u51fa\u73b0\uff0c\u7b11\u9053\uff1a\u201c\u6211\u9053\u662f \u4f55\u4eba\uff0c\u539f\u6765\u662f\u4f60\u4eec\u6765\u4e86\u3002\u201d\u5510\u6653\u6f9c\u4e5f\u8ddf\u7740\u8d70\u4e86\u51fa\u6765\u4e0e\u4f17\u4eba\u76f8\u89c1\uff0c\u4ff1\u90fd\u5927\u559c\u3002 \u3000\u3000\u5415\u56db\u5a18\u9053\uff1a\u201c\u9099\u5c71\u662f\u4e0a\u4e2a\u6708\u88ab\u70e7\u6389\u7684\uff0c\u5b98\u519b\u89c1\u5c71\u4e0a\u7a7a\u65e0\u4e00\u4eba\uff0c\u653e\u706b\u4e4b\u540e\uff0c\u4fbf\u7b97\u4e86\u4e8b\u3002\u6211 \u5e08\u5085\u7684\u9675\u5893\u5f97\u4ee5\u4fdd\u5b58\uff0c\u5b9e\u662f\u666e\u5929\u4e4b\u5e78\u3002\u6211\u6599\u51af\u7433\u5979\u4eec\u5fc5\u8fd8\u8981\u56de\u6765\uff0c\u6240\u4ee5\u72ec\u81ea\u56de\u5c71\uff0c\u4e00\u6765\u770b\u671b \u9675\u5893\uff0c\u4e8c\u6765\u7b49\u5019\u5ba2\u4eba\u3002\u60f3\u4e0d\u5230\u5374\u5148\u7b49\u5230\u6653\u6f9c\u548c\u51af\u745b\uff0c\u7136\u540e\u624d\u662f\u4f60\u4eec\uff01\u201d \u3000\u3000\u5404\u4eba\u5bd2\u55a7\u5df1\u6bd5\uff0c\u7384\u98ce\u95ee\u9053\uff1a\u201c\u5973\u4fa0\u65b0\u81ea\u4eac\u57ce\u56de\u6765\uff0c\u53ef\u6709\u4ec0\u4e48\u6d88\u606f\u4e48\uff1f\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a \u201c\u6d88\u606f\u662f\u6709\uff0c\u4e0d\u8fc7\u8fd9\u6d88\u606f\u60f3\u5fc5\u5927\u5bb6\u90fd\u5df2\u6599\u5230\u7684\u4e86\u3002\u201d\u7384\u98ce\u9053\uff1a\u201c\u53ef\u662f\u5e74\u7fb9\u5c27\u88ab\u5904\u6b7b\u4e86\u4e48\uff1f\u201d \u5415\u56db\u5a18\u9053\uff1a\u201c\u6b63\u662f\u3002\u90a3\u5e74\u7fb9\u5c27\u88ab\u62bc\u56de\u4eac\u5e08\u4e4b\u540e\uff0c\u676d\u5dde\u5c06\u519b\u9646\u864e\u81e3\u7684\u5f39\u7ae0\u4e5f\u8ddf\u7740\u594f\u4e0a\uff0c\u5899\u5012\u4f17 \u4eba\u63a8\uff0c\u671d\u4e2d\u5927\u81e3\u4f60\u4e00\u672c\u6211\u4e00\u672c\uff0c\u90fd\u662f\u5f39\u52be\u5e74\u7fb9\u5c27\u7684\uff0c\u628a\u90a3\u4e9b\u5f39\u7ae0\u6240\u5217\u7684\u7f6a\u72b6\u603b\u8ba1\u8d77\u6765\uff0c\u4f60\u4eec \u731c\u731c\u770b\u6709\u591a\u5c11\uff0c\u7adf\u7136\u6709\u4e5d\u5341\u4e8c\u6761\u4e4b\u591a\uff01\u201d\u51af\u7433\u548b\u820c\u9053\uff1a\u201c\u55ef\uff0c\u8fd9\u4e48\u591a\uff01\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u96cd\u6b63 \u4eb2\u4e0b\u8c15\u65e8\uff0c\u8bf4\u4ed6\u6709\u5927\u9006\u4e4b\u7f6a\u4e94\uff0c\u6b3a\u7f54\u4e4b\u7f6a\u4e5d\uff0c\u50ed\u8d8a\u4e4b\u7f6a\u5341\u516d\uff0c\u72c2\u5984\u4e4b\u7f6a\u5341\u4e09\uff0c\u4e13\u64c5\u4e4b\u7f6a\u516d\uff0c \u8d2a\u8d4e\u4e4b\u7f6a\u5341\u516b\uff0c\u5fcc\u523b\u4e4b\u7f6a\u516d\uff0c\u4fb5\u8680\u4e4b\u7f6a\u5341\u4e94\uff0c\u6b8b\u5fcd\u4e4b\u7f6a\u56db\uff0c\u4f60\u4eec\u7b97\u7b97\u770b\u662f\u4e0d\u662f\u4e5d\u5341\u4e8c\u6761\uff1f\u201d \u5510\u6653\u6f9c\u7b11\u9053\uff1a\u201c\u4f60\u5c45\u7136\u8bb0\u5f97\u8fd9\u6837\u6e05\u695a\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u90a3\u9053\u2018\u4e0a\u8c15\u2019\u4e5f\u662f\u5999\u6587\uff0c\u5176\u5b9e\u5bf9\u96cd\u6b63 \u6765\u8bf4\uff0c\u4f55\u5fc5\u5982\u6b64\u7f57\u5506\uff0c\u8fd9\u4e5d\u5341\u4e8c\u6761\u5927\u7f6a\u5408\u8d77\u6765\u4e0d\u8fc7\u4e00\u6761\uff1a\u529f\u9ad8\u9707\u4e3b\u800c\u5df2\u3002\u53ef\u7b11\u96cd\u6b63\u8fd8\u8981\u5047\u60fa \u60fa\u201c\u5723\u65e8\u2019\u4e0b\u6765\u8bf4\uff0c\u5e74\u7fb9\u5c27\u72af\u4e86\u4e5d\u5341\u4e8c\u6761\u5927\u7f6a\uff0c\u6309\u5f8b\u672c\u8be5\u51cc\u8fdf\u5904\u6b7b\uff0c\u4f46\u59d1\u5ff5\u4ed6\u5e73\u9752\u6d77\u6709\u529f\uff0c \u7740\u4ea4\u6b65\u519b\u7edf\u9886\u76d1\u8e22\u81ea\u88c1\uff0c\u4ed6\u7684\u7236\u4eb2\u5e74\u9050\u9f84\u5219\u88ab\u593a\u7235\u4f4d\uff0c\u514d\u8bae\u5904\u5206\u3002\u53ef\u601c\u90a3\u8001\u5934\u513f\u7236\u51ed\u5b50\u8d35\uff0c \u65e0\u7aef\u7aef\u505a\u4e86\u51e0\u5e74\u4e00\u7b49\u516c\uff0c\u6b63\u5728\u6b22\u559c\uff0c\u5ffd\u800c\u7ecf\u6b64\u4e00\u573a\u6253\u51fb\uff0c\u5728\u5e74\u7fb9\u5c27\u8fd8\u672a\u88ab\u5904\u6b7b\u4e4b\u524d\uff0c\u5df2\u5148\u5413 \u6b7b\u4e86\u3002\u81f3\u4e8e\u5e74\u7fb9\u5c27\u6240\u6709\u7684\u5bb6\u4ea7\uff0c\u5168\u88ab\u67e5\u6284\u5165\u5bab\uff0c\u8fd9\u66f4\u4e0d\u5728\u8bdd\u4e0b\u4e86\u3002\u96cd\u6b63\u8fd8\u8bf4\u4ed6\u8fd9\u6837\u5904\u7f6e\u662f\u5bbd \u5b8f\u5927\u91cf\u5230\u4e86\u6781\u70b9\uff0c\u8981\u5e74\u7fb9\u5c27\u4e34\u6b7b\u4e4b\u524d\uff0c\u5411\u4ed6\u4e0a\u8868\u8c22\u6069\u3002\u201d\u7384\u98ce\u9053\uff1a\u201c\u4e0d\u662f\u542c\u8bf4\u5e74\u7fb9\u5c27\u5df1\u75af\u4e86 \u5417\uff1f\u4ed6\u8fd8\u80fd\u5199\u90a3\u6837\u7684\u8c22\u6069\u8868\uff1f\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u81ea\u7136\u6709\u4eba\u4ee3\u5199\uff0c\u5b98\u4e66\u6587\u732e\uff0c\u5927\u534a\u90fd\u662f\u5982\u6b64\u865a\u5047 \u7684\uff0c\u6709\u4f55\u7a00\u5947\u3002\u201d\u4f17\u4eba\u4e0d\u89c9\u5927\u7b11\u3002 \u3000\u3000\u5415\u56db\u5a18\u53c8\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u521a\u88ab\u5904\u6b7b\uff0c\u96cd\u6b63\u6015\u4eba\u5fc3\u4e0d\u670d\uff0c\u6240\u4ee5\u9632\u8303\u7279\u522b\u4e25\u5bc6\uff0c\u6211\u63a2\u8fc7\u4e24\u6b21\u7687 \u5bab\uff0c\u5bab\u4e2d\u6bbf\u5b87\u5982\u4e91\uff0c\u6839\u672c\u4e0d\u77e5\u4ed6\u8eb2\u5728\u54ea\u91cc\uff0c\u7b2c\u4e8c\u6b21\u63a2\u5bab\u65f6\u88ab\u9ad8\u624b\u53d1\u73b0\uff0c\u7acb\u88ab\u56f4\u653b\uff0c\u8fd8\u51e0\u4e4e\u8131 \u4e0d\u4e86\u8eab\u3002\u201d\u7384\u98ce\u9053\uff1a\u201c\u523a\u6740\u4e07\u4e58\u4e4b\u541b\uff0c\u8c08\u4f55\u5bb9\u6613\uff01\u4f9d\u6211\u7684\u611a\u89c1\uff0c\u6700\u5c11\u8fd8\u8981\u591a\u4e24\u540d\u8f7b\u529f\u7edd\u9876\u3001 \u6b66\u529f\u7cbe\u6e5b\u7684\u9ad8\u624b\u540c\u53bb\uff0c\u4e5f\u8bb8\u8fd8\u6709\u673a\u4f1a\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u662f\u5440\uff0c\u6240\u4ee5\u6211\u624d\u5728\u8fd9\u91cc\u7b49\u5019\u4f60\u4eec\u3002\u51af\u745b\u51af\u7433\u8fd9\u4e24\u5e74\u6b66\u529f\u5927\u8fdb\uff0c\u8f7b\u529f\u5c24 \u9ad8\uff0c\u5c31\u8ba9\u6211\u4eec\u4e09\u59d0\u59b9\u53bb\u597d\u4e86\u3002\u201d\u7384\u98ce\u62cd\u638c\u7b11\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\uff0c\u4e2d\u5e3c\u80dc\u987b\u7709\uff0c\u6211\u4eec\u4e14\u7b49\u7740\u770b \u4e09\u4fa0\u5927\u663e\u795e\u901a\uff0c\u6c38\u7559\u4f73\u8bdd\u3002\u55ef\uff0c\u8fd9\u4e24\u4e2a\u5c0f\u6dd8\u6c14\u5462\uff1f\u201d\u4f17\u4eba\u4e00\u770b\uff0c\u4e0d\u89c1\u51af\u745b\u51af\u7433\uff0c\u5415\u56db\u5a18\u7b11 \u9053\uff1a\u201c\u5979\u4fe9\u59d0\u59b9\u4e00\u5b9a\u662f\u80cc\u7740\u4eba\u53bb\u8bf4\u4f53\u5df1\u8bdd\u513f\u4e86\uff0c\u963f\u745b\u2014\u2014\u963f\u7433\uff01\u201d\u5c71\u8c37\u4f20\u6765\u56de\u58f0\uff0c\u51af\u745b\u8fdc\u8fdc \u7b54\u9053\uff1a\u201c\u5c31\u6765\u4e86\uff01\u201d \u3000\u3000\u5415\u56db\u5a18\u6599\u5f97\u4e0d\u9519\uff0c\u5979\u4eec\u679c\u7136\u662f\u53bb\u8bf4\u4f53\u5df1\u7684\u8bdd\u513f\uff0c\u51af\u7433\u628a\u59d0\u59d0\u62c9\u5230\u7eff\u836b\u6df1\u5904\uff0c\u6084\u6084\u95ee\u5979\u548c \u5510\u6653\u6f9c\u5230\u5e95\u5982\u4f55\uff1f\u51af\u745b\u9ed8\u7136\u4e0d\u8bed\uff0c\u88ab\u59b9\u59b9\u95ee\u5f97\u6025\u4e86\uff0c\u773c\u5708\u4e00\u7ea2\uff0c\u561f\u7740\u5634\u513f\u7b54\u9053\uff1a\u201c\u6211\u8fd8\u662f\u5bf9 \u4ed6\u5982\u540c\u53d4\u53d4\u4e00\u6837\u3002\u201d\u51af\u7433\u7b11\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\u5427\uff0c\u6211\u521a\u624d\u542c\u5f97\u4f60\u4e0d\u662f\u53eb\u4ed6\u53d4\u53d4\uff0c\u800c\u662f\u53eb\u4ed6\u7684\u540d\u5b57 \u5462\uff01\u201d\u51af\u745b\u677f\u8138\u8bf4\u9053\uff1a\u201c\u53eb\u540d\u5b57\u53c8\u600e\u4e48\u6837\uff1f\u53cd\u6b63\u6211\u4eca\u751f\u4eca\u4e16\u4e0d\u518d\u5ac1\u4eba\u3002\u6211\u5c31\u4e0d\u4fe1\uff0c\u5973\u5b69\u5b50\u975e \u5f97\u6709\u4e2a\u7537\u4eba\u4e0d\u6210\u3002\u201d \u3000\u3000\u51af\u7433\u5657\u55e4\u4e00\u7b11\uff0c\u5ffd\u7136\u88c5\u5f97\u5f88\u6b63\u7ecf\u7684\u6837\u5b50\uff0c\u62c9\u7740\u51af\u745b\u7684\u624b\u9053\uff1a\u201c\u53ef\u662f\u5988\u5988\u4e0d\u76f8\u4fe1\u5462\uff01\u5988\u5988 \u968f\u4ed6\u4eec\u53bb\u5929\u5c71\uff0c\u8fd9\u4e2a\u4f60\u5df2\u7ecf\u77e5\u9053\u4e86\u3002\u524d\u51e0\u5929\u6211\u78b0\u89c1\u5979\uff0c\u5979\u8fd8\u95ee\u8d77\u4f60\u4eec\u7684\u4e8b\u60c5\u3002\u5979\u8bf4\u5973\u5b69\u513f\u5bb6 \u603b\u662f\u8981\u5ac1\u4eba\u7684\uff0c\u5979\u53c8\u8bf4\u90a3\u4e2a\u5510\u6653\u6f9c\uff0c\u55ef\uff0c\u4f60\u90a3\u4e2a\u2018\u53d4\u53d4\u2019\u4e3a\u4eba\u5f88\u597d\uff0c\u5979\u8fd8\u6015\u4f60\u5acc\u2018\u53d4\u53d4\u2019\u5e74 \u7eaa\u6bd4\u4f60\u5927\u5341\u591a\u5c81\uff0c\u53eb\u6211\u529d\u4f60\u5462\uff01\u5582\uff0c\u4f60\u4e0d\u542c\u5988\u5988\u7684\u8bdd\u5417\uff1f\u201d\u51af\u7433\u88c5\u4f5c\u5927\u4eba\u7684\u6a21\u6837\uff0c\u8bf4\u5f97\u5341\u5206 \u795e\u6c14\uff0c\u51af\u745b\u4e5f\u7ed9\u5979\u9017\u5f97\u7b11\u4e86\u8d77\u6765\u3002\u4f46\u968f\u5373\u60f3\u5230\u6bcd\u4eb2\u4e5f\u66fe\u8fd9\u6837\u529d\u8fc7\u81ea\u5df1\uff0c\u53ef\u662f\u81ea\u5df1\u548c\u6653\u6f9c\u4e4b\u95f4 \u7684\u4e8b\u60c5\uff0c\u8fde\u6bcd\u4eb2\u4e5f\u4e0d\u4fbf\u544a\u8bc9\uff0c\u6b64\u6bb5\u60c5\u6000\uff0c\u53ea\u80fd\u6df1\u85cf\u5fc3\u5e95\uff0c\u601d\u60f3\u8d77\u6765\u53c8\u4e0d\u7981\u9eef\u7136\u3002 \u3000\u3000\u51af\u745b\u7ed9\u59b9\u59b9\u95ee\u5f97\u5f88\u662f\u5c34\u5c2c\uff0c\u5e78\u5f97\u5415\u56db\u5a18\u547c\u5524\uff0c\u66ff\u5979\u89e3\u56f4\u3002\u4e00\u8c08\u8d77\u8981\u5165\u4eac\u523a\u6740\u96cd\u6b63\uff0c\u5927\u5bb6 \u90fd\u7cbe\u795e\u5955\u5955\u3002\u8fc7\u4e86\u534a\u6708\uff0c\u5415\u56db\u5a18\u548c\u7518\u51e4\u6c60\u8054\u7edc\u4e0a\u4e86\uff0c\u4ed7\u7740\u7518\u51e4\u6c60\u7684\u4eba\u7f18\u6781\u5e7f\uff0c\u5404\u8def\u82f1\u96c4\uff0c\u90fd \u9646\u7eed\u6df7\u5165\u4e86\u5317\u4eac\uff0c\u53ef\u662f\u4eac\u4e2d\u9632\u8303\u6781\u4e25\uff0c\u7687\u5bab\u53c8\u5927\uff0c\u4e5f\u4e0d\u77e5\u96cd\u6b63\u4f4f\u5728\u54ea\u4e2a\u5bab\u6bbf\uff0c\u6df1\u6050\u4e00\u51fb\u4e0d \u4e2d\uff0c\u53cd\u4e3a\u8d3b\u7978\u3002\u6240\u4ee5\u5165\u4eac\u534a\u5e74\uff0c\u8fd8\u662f\u6ca1\u673a\u4f1a\u4e0b\u624b\u3002 \u3000\u3000\u96cd\u6b63\u6740\u4e86\u5e74\u7fb9\u5c27\u540e\uff0c\u4e5f\u9884\u9632\u4f1a\u6709\u523a\u5ba2\u884c\u523a\uff0c\u4e0d\u4f46\u5728\u5bab\u4e2d\u904d\u5e03\u536b\u58eb\uff0c\u800c\u4e14\u8fde\u5bab\u5ef7\u7684\u4e00\u4e9b\u65e7 \u4f8b\uff0c\u5982\u8282\u65e5\u53ef\u4ee5\u6f14\u620f\u4f5c\u4e50\u4e4b\u7c7b\uff0c\u4e5f\u5168\u90fd\u7981\u4e86\uff0c\u81ea\u5df1\u66f4\u662f\u6bcf\u665a\u66f4\u6362\u5bbf\u5904\uff0c\u63d0\u5fc3\u540a\u80c6\uff0c\u5904\u5904\u63d0\u9632\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u6837\u5b50\u505a\u7687\u5e1d\uff0c\u4e5f\u59d4\u5b9e\u6ca1\u6709\u5473\u9053\u3002\u8fc7\u4e86\u534a\u5e74\u6709\u591a\uff0c\u96cd\u6b63\u89c1\u592a\u5e73\u65e0\u4e8b\uff0c\u7981\u4ee4\u6e10\u677e\uff0c\u65f6 \u5149\u8fc5\u901f\uff0c\u51ac\u53bb\u6625\u6765\uff0c\u8fc7\u4e86\u65b0\u5e74\uff0c\u8f6c\u77ac\u53c8\u662f\u5143\u5bb5\u4f73\u8282\uff0c\u96cd\u6b63\u7684\u751f\u6bcd\u672c\u662f\u5eb7\u7199\u7684\u4e00\u4e2a\u5983\u5b50\uff0c\u5982\u4eca \u6bcd\u51ed\u5b50\u8d35\uff0c\u505a\u4e86\u592a\u540e\uff0c\u81ea\u662f\u5c3d\u60c5\u4eab\u53d7\uff0c\u65e0\u987b\u8bf4\u5f97\u3002\u8fd9\u534a\u5e74\u6765\u95f7\u5728\u5bab\u4e2d\uff0c\u5341\u5206\u4e0d\u8010\uff0c\u8d81\u7740\u5143\u5bb5 \u5c06\u5230\uff0c\u4fbf\u8981\u96cd\u6b63\u5f00\u7981\uff0c\u5927\u5927\u4f5c\u4e50\u4e00\u756a\u3002\u9664\u4e86\u539f\u6709\u7684\u5185\u5ead\u4f9b\u5949\uff0c\u6559\u574a\u6b4c\u821e\uff0c\u53ef\u4ee5\u62db\u6765\u6f14\u51fa\u4e4b \u5916\uff0c\u8fd8\u51c6\u8bb8\u8fd1\u4eb2\u8d1d\u52d2\uff0c\u5404\u5404\u4f9b\u732e\u8282\u76ee\u3002\u96cd\u6b63\u4e00\u60f3\uff0c\u4e0e\u81ea\u5df1\u4f5c\u5bf9\u7684\u5144\u5f1f\uff0c\u90fd\u5df2\u9664\u4e86\uff0c\u8c05\u5269\u4e0b\u7684 \u5404\u8d1d\u52d2\u4e0d\u6562\u518d\u6709\u4e8c\u5fc3\uff0c\u4fbf\u987a\u7740\u6bcd\u4eb2\u7684\u610f\u601d\uff0c\u5141\u8bb8\u5f00\u7981\u3002\u8fd9\u6d88\u606f\u5341\u5206\u79d8\u5bc6\uff0c\u76f4\u5230\u5143\u5bb5\u524d\u5915\uff0c\u624d \u8bf4\u7ed9\u5404\u8d1d\u52d2\u542c\u3002\u8fd9\u5374\u4e50\u574f\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4eba\u662f\u4eb2\u738b\u5141\u745b\uff0c\u5eb7\u7199\u7684\u7b2c\u5341\u516d\u5b50\u3002\u4ed6\u662f\u96cd\u6b63\u540c\u6bcd\u6240\u751f\u7684\u4eb2\u5144\u5f1f\uff0c\u5728\u8bf8\u7687\u5b50\u4e4b\u4e2d\uff0c\u5e74\u7eaa \u6700\u5c0f\uff0c\u6240\u4ee5\u5f53\u5eb7\u7199\u5728\u65e5\uff0c\u4ed6\u5e76\u6ca1\u6709\u53c2\u52a0\u593a\u4f4d\u4e4b\u4e89\uff0c\u56e0\u6b64\u5f97\u4ee5\u4fdd\u5168\u9996\u9886\u3002 \u3000\u3000\u5141\u745b\u5e74\u8f7b\u559c\u73a9\uff0c\u4e5f\u559c\u7ec3\u6b66\uff0c\u5927\u7ea6\u5728\u4e00\u6708\u4e4b\u524d\uff0c\u6709\u4eba\u8350\u4e00\u4f4d\u6b66\u5e08\u7ed9\u4ed6\uff0c\u8fd9\u6b66\u5e08\u4e2d\u7b49\u8eab\u6750\uff0c \u8c8c\u4e0d\u60ca\u4eba\uff0c\u5141\u745b\u4e0d\u4fe1\u4ed6\u6709\u672c\u9886\uff0c\u4e00\u65f6\u9ad8\u5174\u548c\u4ed6\u8f83\u91cf\uff0c\u62f3\u811a\u521a\u521a\u6cbe\u8eab\uff0c\u5c31\u4e0d\u77e5\u600e\u7684\u88ab\u4ed6\u6454\u5012\u3002 \u6b64\u4eba\u81ea\u79f0\u5510\u9f99\uff0c\u4e0d\u4f46\u7cbe\u4e8e\u6b66\u529f\uff0c\u800c\u4e14\u957f\u4e8e\u6742\u800d\uff0c\u5141\u745b\u5341\u5206\u6b22\u559c\uff0c\u4fbf\u7559\u4ed6\u5728\u7687\u5e9c\u4e4b\u4e2d\u3002\u8fd9\u6b21\u96cd \u6b63\u5143\u5bb5\u5f00\u7981\uff0c\u5141\u745b\u95ee\u5510\u9f99\u53ef\u6709\u4ec0\u4e48\u620f\u6cd5\u82b1\u6837\u3002\u5510\u9f99\u8fde\u58f0\u7b54\u9053\uff1a\u201c\u6709\uff0c\u6709\uff01\u201d\u9a6c\u4e0a\u6f14\u51fa\u51e0\u5957\uff0c \u5982\u800d\u6c34\u6d41\u661f\u3001\u9876\u575b\u5b50\u3001\u821e\u5f69\u7ef8\u7b49\u7b49\uff0c\u90fd\u6bd4\u5bfb\u5e38\u5356\u827a\u7684\u4eba\u597d\u5f97\u591a\uff0c\u5141\u745b\u5927\u559c\uff0c\u5510\u9f99\u53c8\u8981\u6c42\u591a\u5e26 \u51e0\u540d\u52a9\u624b\uff0c\u5141\u745b\u4e5f\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u5230\u4e86\u5143\u5bb5\u665a\u4e0a\uff0c\u7d2b\u7981\u57ce\u4e2d\u5f20\u706f\u7ed3\u5f69\uff0c\u70ed\u95f9\u975e\u5e38\uff0c\u5fa1\u82b1\u56ed\u4e2d\u642d\u8d77\u620f\u53f0\uff0c\u5468\u56f4\u5750\u7684\u90fd\u662f\u7687\u4eb2 \u56fd\u621a\uff0c\u90a3\u620f\u53f0\u65c1\u8fb9\u642d\u4e86\u4e00\u4e2a\u770b\u53f0\uff0c\u96cd\u6b63\u548c\u4f17\u5983\u5ad4\u966a\u7740\u592a\u540e\u5728\u770b\u53f0\u4e0a\u89c2\u8d4f\u3002 \u3000\u3000\u592a\u540e\u5bf9\u8fd9\u4e9b\u6742\u6280\u767e\u620f\u3002\u5341\u5206\u6b22\u559c\uff0c\u4e00\u770b\u5230\u9ad8\u5174\u4e4b\u5904\uff0c\u4fbf\u53eb\u4eba\u8d4f\u94b1\uff0c\u5230\u4e86\u5141\u745b\u5e26\u6765\u7684\u8fd9\u4e00 \u73ed\u4eba\u4e0a\u53f0\uff0c\u592a\u540e\u66f4\u662f\u9ad8\u5174\uff0c\u7b11\u5bf9\u96cd\u6b63\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5c0f\u5144\u5f1f\u7d20\u6765\u6b22\u559c\u73a9\u800d\uff0c\u770b\u4ed6\u53c8\u6709\u4ec0\u4e48\u597d \u7684\uff1f\u201d \u3000\u3000\u53ea\u89c1\u4e94\u4e2a\u7a7f\u7740\u5f69\u8272\u620f\u670d\u3001\u753b\u4e86\u8138\u8c31\u7684\u4eba\u767b\u4e0a\u620f\u53f0\uff0c\u7687\u592a\u540e\u9053\uff1a\u201c\u6f14\u4ec0\u4e48\u620f\uff0c\u600e\u4e48\u5c3d\u662f\u987b \u751f\u3001\u4e11\u89d2\uff1f\u201d\u5141\u745b\u56de\u9053\uff1a\u201c\u4e0d\u662f\u6f14\u620f\uff0c\u662f\u800d\u6742\u6280\u3002\u201d\u96cd\u6b63\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f46\u89c1\u4e24\u4eba\u7ad9\u5728\u53f0\u4e0a\uff0c\u62c9 \u624b\u5e76\u80a9\uff0c\u4e00\u4eba\u8df3\u4e86\u4e0a\u6765\uff0c\u5206\u8e0f\u8fd9\u4e24\u4eba\u7684\u80a9\u5934\uff0c\u53c8\u4e00\u4eba\u8df3\u5230\u8fd9\u4eba\u7684\u80a9\u5934\uff0c\u96cd\u6b63\u9053\uff1a\u201c\u8fd9\u51e0\u4eba\u529f \u592b\u597d\u4fca\uff0c\u662f\u73a9\u53e0\u7f57\u6c49\u5417\uff1f\u201d\u8bdd\u72b9\u672a\u5b8c\uff0c\u53ea\u89c1\u6700\u540e\u90a3\u540d\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\u4e00\u8dc3\u800c\u4e0a\uff0c\u5934\u4e0a\u9876\u7740\u4e00 \u4e2a\u6c34\u7897\uff0c\u4e24\u624b\u4e5f\u5404\u62ff\u4e00\u4e2a\u6c34\u7897\uff0c\u4ed6\u5fae\u4e00\u503e\u659c\uff0c\u4e09\u4e2a\u6c34\u7897\u90fd\u6709\u6c34\u6e85\u51fa\uff0c\u8bc1\u660e\u7897\u4e2d\u76db\u6ee1\u4e86\u6c34\u3002\u4f46 \u89c1\u4ed6\u53cc\u624b\u4e00\u629b\uff0c\u4e24\u4e2a\u6c34\u7897\u63b7\u5230\u534a\u7a7a\uff0c\u547c\u7684\u4e00\u58f0\u53c8\u629b\u51fa\u4e24\u6761\u5f69\u7ef8\uff0c\u5c31\u5728\u534a\u7a7a\u4e2d\u628a\u6c34\u7897\u7f20\u7740\uff0c\u975e \u4f46\u5934\u9876\u4e0a\u7684\u90a3\u7897\u6c34\u6ef4\u6c34\u4e0d\u6f0f\uff0c\u5728\u534a\u7a7a\u4e2d\u7684\u4e24\u4e2a\u6c34\u7897\uff0c\u4e5f\u7aef\u7aef\u6b63\u6b63\u7684\u843d\u4e86\u4e0b\u6765\uff0c\u4e5f\u4e0d\u89c1\u6709\u4e00\u6ef4 \u6c34\u6f0f\u51fa\u3002 \u3000\u3000\u5c31\u5728\u559d\u5f69\u58f0\u4e2d\uff0c\u90a3\u4eba\u821e\u8d77\u5f69\u7ef8\uff0c\u4e24\u53ea\u6c34\u7897\u76d8\u65cb\u98de\u821e\uff0c\u8d8a\u8f6c\u8d8a\u75be\uff0c\u7687\u592a\u540e\u9ad8\u58f0\u53eb\u597d\uff0c\u90a3\u4eba \u53cc\u624b\u4e00\u677e\u3002\u6c34\u7897\u5728\u4ed6\u5934\u9876\u4e0a\u6253\u4e86\u4e24\u4e2a\u76d8\u65cb\uff0c\u4ed6\u4e00\u4f38\u624b\u53c8\u6293\u4f4f\u4e86\u5f69\u7ef8\u7684\u4e2d\u6bb5\uff0c\u518d\u821e\u8d77\u6765\uff0c\u4e00\u8fb9 \u662f\u5f69\u7ef8\u98de\u821e\uff0c\u4e00\u8fb9\u662f\u6c34\u7897\u76d8\u65cb\uff0c\u7687\u592a\u540e\u8fde\u8fde\u559d\u5f69\uff0c\u9053\uff1a\u201c\u8fd9\u6bd4\u5bfb\u5e38\u7684\u800d\u6c34\u7897\u8981\u597d\u770b\u591a\u4e86\u3002\u201d \u96cd\u6b63\u5374\u51dd\u795e\u7ec6\u770b\uff0c\u5e76\u4e0d\u559d\u5f69\uff0c\u5ffd\u7136\u95ee\u5141\u745b\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u4eba\u539f\u6765\u662f\u4f60\u5e9c\u4e0a\u7684\u5417\uff1f\u201d\u5141\u745b\u6b63\u770b\u5f97 \u5165\u795e\uff0c\u672a\u53ca\u56de\u7b54\uff0c\u53c8\u89c1\u534a\u7a7a\u4e2d\u5f69\u7ef8\u8f6c\u6298\uff0c\u7adf\u7136\u6253\u51fa\u201c\u5929\u4e0b\u592a\u5e73\u201d\u56db\u5b57\uff0c\u7687\u592a\u540e\u559c\u9053\uff1a\u201c\u771f\u662f \u795e\u6280\uff0c\u8d4f\u94b1\uff01\u201d\u201d\u592a\u76d1\u628a\u4e24\u7ba9\u94f6\u5b50\u62ac\u5230\u53f0\u4e0a\uff0c\u96cd\u6b63\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u628a\u8fd9\u51e0\u4e2a\u4eba\u62ff\u4e0b\u6765\u95ee\uff01\u201d \u3000\u3000\u620f\u53f0\u4e0a\u788e\u94f6\u5982\u96e8\uff0c\u4e00\u7247\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\uff0c\u6742\u4ee5\u4f17\u4eba\u559d\u91c7\u4e4b\u58f0\uff0c\u6b63\u81ea\u70ed\u95f9\uff0c\u96cd\u6b63\u8fd9\u4e00\u58f0\u5927 \u559d\uff0c\u5927\u51fa\u4f17\u4eba\u610f\u5916\uff0c\u9aa4\u7136\u95f4\u58f0\u97f3\u9759\u6b62\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u51b3\uff0c\u53ea\u89c1\u90a3\u4e2a\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\uff0c\u5f69\u7ef8\u4e00 \u5c55\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u4e24\u9053\u767d\u5149\uff0c\u75be\u5982\u7535\u5c04\uff0c\u76f4\u5411\u96cd\u6b63\u9762\u95e8\u98de\u53bb\u3002\u539f\u6765\u5728\u5f69\u7ef8\u4e2d\u7adf\u7136\u88f9\u7740\u4e24\u67c4\u950b\u5229 \u7684\u5315\u9996\u3002 \u3000\u3000\u96cd\u6b63\u65e9\u5df2\u7591\u60d1\uff0c\u9884\u6709\u63d0\u9632\uff0c\u4ed6\u4e00\u559d\u4e4b\u540e\uff0c\u8eab\u8eaf\u8fc5\u5373\u95ea\u5165\u5983\u6ba1\u4e1b\u4e2d\uff0c\u4e24\u67c4\u5315\u9996\u5494\u5693\u4e24\u58f0\uff0c \u7a7f\u8fc7\u4e86\u4e24\u540d\u5983\u5ad4\u7684\u80a9\u9aa8\uff0c\u987f\u65f6\u95f4\uff0c\u9a87\u53eb\u4e4b\u58f0\u7eb7\u8d77\uff0c\u7687\u592a\u540e\u6655\u5012\u53f0\u4e0a\uff0c\u4f17\u5983\u5ad4\u4e71\u6210\u4e00\u56e2\u3002\u5c31\u5728 \u8fd9\u970e\u90a3\u95f4\uff0c\u90a3\u6c49\u5b50\u8eab\u5f62\u75be\u8d77\uff0c\u98de\u9e1f\u822c\u7684\u6251\u4e0a\u770b\u53f0\uff0c\u4e09\u7897\u6c34\u4e00\u9f50\u6cfc\u5230\u96cd\u6b63\u8eab\u4e0a\uff0c\u96cd\u6b63\u773c\u775b\u7741\u4e0d \u5f00\u6765\uff0c\u53ea\u89c9\u5bd2\u98ce\u52b2\u5c04\uff0c\u51b7\u6c14\u68ee\u68ee\uff0c\u4e00\u67c4\u950b\u5229\u7684\u77ed\u5251\u5df2\u7ecf\u89e6\u5230\u80cc\u540e\u3002 \u3000\u3000\u96cd\u6b63\u5374\u4e5f\u5341\u5206\u6c89\u7740\u673a\u8b66\uff0c\u5c31\u5728\u8fd9\u5343\u94a9\u4e00\u53d1\u3001\u6b7b\u751f\u4fc4\u9877\u4e4b\u95f4\uff0c\u53cc\u624b\u4e00\u52fe\uff0c\u628a\u4e24\u540d\u5983\u5ad4\u6324\u5230 \u8eab\u540e\uff0c\u90a3\u6c49\u5b50\u6536\u624b\u4e0d\u53ca\uff0c\u773c\u89c1\u4e24\u540d\u5a07\u5f31\u7684\u5973\u4eba\u9c9c\u8840\u98de\u6e85\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u633a\u5251\u518d\u6251\uff0c \u96cd\u6b63\u5df2\u662f\u4e00\u4e2a\u98de\u8eab\uff0c\u8df3\u4e0a\u697c\u680f\u3002 \u3000\u3000\u8fd9\u6c49\u5b50\u54ea\u662f\u4ec0\u4e48\u5bfb\u5e38\u5356\u827a\u4e4b\u8f88\uff0c\u5374\u662f\u6c5f\u5357\u5927\u4fa0\u7518\u51e4\u6c60\u3002\u4ed6\u6df7\u5165\u4e86\u5141\u745b\u7684\u4eb2\u738b\u5e9c\u4e2d\uff0c\u597d\u4e0d \u5bb9\u6613\u624d\u7b49\u5230\u8fd9\u4e2a\u673a\u4f1a\uff0c\u54ea\u80af\u653e\u8fc7\uff0c\u867d\u7136\u660e\u77e5\u6781\u9669\uff0c\u4e5f\u7d27\u8ffd\u4e0d\u820d\uff0c\u968f\u7740\u96cd\u6b63\u8df3\u4e0a\u697c\u680f\u3002 \u3000\u3000\u4e0e\u7518\u51e4\u6c60\u540c\u6765\u7684\u90a3\u56db\u4e2a\u4eba\u4e5f\u4e0d\u662f\u4ec0\u4e48\u800d\u6742\u6280\u7684\u52a9\u624b\uff0c\u800c\u662f\u5510\u6653\u6f9c\u3001\u674e\u6cbb\u3001\u767d\u6cf0\u5b98\u548c\u9c7c \u4eae\u3002\u4ed6\u4eec\u8fdf\u4e86\u4e00\u6b65\uff0c\u5df2\u7ed9\u770b\u53f0\u4e0b\u7684\u536b\u58eb\u622a\u7740\uff0c\u54c8\u5e03\u9640\u821e\u8d77\u6d41\u661f\u9524\uff0c\u97e9\u91cd\u5c71\u4eae\u51fa\u8f9f\u4e91\u9504\uff0c\u52a0\u4e0a \u5176\u4ed6\u536b\u58eb\u7684\u5835\u622a\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u54ea\u80fd\u95ef\u5f97\u51fa\u53bb\u3002 \u3000\u3000\u5510\u6653\u6f9c\u5927\u6025\uff0c\u6e38\u9f99\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u771f\u4e2a\u662f\u77eb\u5982\u6e38\u9f99\uff0c\u731b\u5982\u96c4\u72ee\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u706b\u661f\u98de\u6e85\uff0c\u628a \u97e9\u91cd\u5c71\u7684\u8f9f\u4e91\u9504\u524a\u53bb\u4e00\u7247\uff0c\u97e9\u91cd\u5c71\u53eb\u9053\uff1a\u201c\u54fc\uff0c\u539f\u6765\u662f\u4f60\uff0c\u597d\u5927\u80c6\u7684\u53db\u9006\uff0c\u5c45\u7136\u6562\u5230\u5bab\u4e2d\u6765 \u4e86\uff01\u201d\u5510\u6653\u6f9c\u7684\u5b9d\u5251\u867d\u7136\u950b\u5229\uff0c\u529f\u529b\u5374\u662f\u7a0d\u6709\u4e0d\u53ca\uff0c\u88ab\u4ed6\u903c\u5f97\u9000\u540e\u4e24\u6b65\u3002\u80cc\u540e\u4e00\u540d\u536b\u58eb\uff0c\u821e \u8d77\u6df7\u5143\u94c1\u724c\uff0c\u5f53\u5934\u4fbf\u538b\uff0c\u5ffd\u88ab\u674e\u6cbb\u659c\u523a\u4e00\u5251\uff0c\u523a\u5165\u5173\u8282\uff0c\u94c1\u724c\u8131\u624b\u98de\u51fa\uff0c\u6253\u4e0d\u7740\u654c\u4eba\uff0c\u5374\u53cd \u4f24\u4e86\u81ea\u5df1\u7684\u4e00\u4e2a\u540c\u4f34\u3002 \u3000\u3000\u674e\u6cbb\u7684\u5251\u6cd5\u6700\u4e3a\u5947\u8be1\uff0c\u865a\u5b9e\u76f8\u53cd\uff0c\u4e00\u51fa\u624b\u5c31\u662f\u523a\u5411\u5173\u8282\u8981\u5bb3\uff0c\u5728\u5251\u5149\u98d8\u77a5\u4e2d\uff0c\u53c8\u6709\u4e24\u540d \u536b\u58eb\u4e2d\u5251\u5012\u5730\u3002\u54c8\u5e03\u9640\u98de\u9524\u8fce\u654c\uff0c\u4eba\u5728\u4e00\u4e08\u5f00\u5916\uff0c\u674e\u6cbb\u5251\u77ed\uff0c\u6b3a\u4e0d\u8fdb\u53bb\uff0c\u88ab\u4ed6\u53cc\u9524\u76d8\u65cb\u8feb \u538b\uff0c\u817e\u4e0d\u51fa\u624b\u6765\u3002 \u3000\u3000\u767d\u6cf0\u5b98\u89c1\u7518\u51e4\u6c60\u9063\u5165\u770b\u53f0\u4e0a\u5c42\u7684\u7545\u97f3\u9601\uff0c\u7adf\u5982\u6ce5\u725b\u5165\u6d77\uff0c\u65e2\u4e0d\u95fb\u53ae\u6740\u4e4b\u58f0\uff0c\u53c8\u4e0d\u89c1\u4eba\u5f71 \u51fa\u6765\uff0c\u5927\u4e3a\u7740\u6025\u3002\u4ed6\u53d1\u51fa\u4e86\u4e00\u628a\u6885\u82b1\u9488\uff0c\u867d\u7136\u6253\u4f24\u51e0\u4eba\uff0c\u5374\u53c8\u7ed9\u5929\u53f6\u6563\u4eba\u8d76\u6765\uff0c\u8fd0\u638c\u529b\u5c06\u4ed6 \u8ffd\u7740\u3002\u6b63\u5728\u6781\u5ea6\u7d27\u5f20\u4e4b\u9645\uff0c\u5ffd\u542c\u89c1\u4e00\u58f0\u5927\u53eb\uff0c\u53ea\u89c1\u90a3\u5ea7\u7545\u97f3\u9601\u7a81\u7136\u584c\u4e86\u4e00\u89d2\uff0c\u4e00\u4e2a\u4eba\u6d41\u661f\u822c \u7684\u5c04\u4e86\u51fa\u6765\uff0c\u201c\u5657\u901a\u201d\u4e00\u58f0\uff0c\u8dcc\u5165\u5fa1\u6cb3\u3002 \u3000\u3000\u767d\u6cf0\u5bab\u60ca\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u4e03\u54e5\u9047\u5bb3\u4e86\uff01\u201d\u53ea\u542c\u5f97\u697c\u53f0\u4e0a\u54c8\u54c8\u5927\u7b11\uff0c\u96cd\u6b63\u53c8\u8d70\u4e86\u51fa\u6765\uff0c\u9ad8 \u58f0\u559d\u9053\uff1a\u201c\u7518\u51e4\u6c60\u4eca\u5df2\u4f0f\u8bdb\uff0c\u6715\u5bbd\u5927\u4e3a\u6000\uff0c\u5176\u4f59\u53db\u515a\uff0c\u5f03\u68b0\u6295\u8bda\uff0c\u53ef\u4ee5\u8d66\u7f6a\u3002\u201d\u9c7c\u4eae\u5927\u6012\uff0c \u559d\u9053\uff1a\u201c\u8c01\u4fe1\u4f60\u7684\u9b3c\u8bdd\uff01\u201d\u91d1\u5203\u631f\u98ce\uff0c\u4e00\u5200\u5288\u53bb\uff0c\u5c06\u4e00\u540d\u536b\u58eb\uff0c\u5288\u4e3a\u4e24\u534a\uff0c\u518d\u5288\u7b2c\u4e8c\u5200\u4e4b \u65f6\uff0c\u5374\u88ab\u97e9\u91cd\u5c71\u4e00\u9504\u6321\u4f4f\u3002 \u3000\u3000\u96cd\u6b63\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u989d\u97f3\u548c\u5e03\u51fa\u6765\uff0c\u66ff\u80a4\u5c06\u8fd9\u4e9b\u53db\u9006\u64d2\u4e0b\uff0c\u683c\u6740\u4e0d\u8bba\uff01\u201d\u53ea\u542c\u5f97\u4e00 \u58f0\u201c\u9886\u65e8\uff01\u201d\u7545\u97f3\u9601\u91cc\u98de\u51fa\u4e00\u4eba\uff0c\u624b\u6325\u62c2\u5c18\uff0c\u51cc\u7a7a\u4e0b\u51fb\uff0c\u62c2\u5c18\u4e00\u5c55\uff0c\u7acb\u523b\u5c06\u9c7c\u4eae\u7684\u539a\u80cc\u65ab\u5c71 \u5200\u5377\u4e0a\u534a\u7a7a\uff0c\u5510\u6653\u6f9c\u5927\u5403\u4e00\u60ca\uff0c\u6e38\u9f99\u5b9d\u5251\u98a4\u950b\u4e00\u6296\uff0c\u4f7f\u51fa\u5929\u5c71\u5251\u6cd5\u7684\u7edd\u62db\u201c\u5927\u6f20\u6d41\u6c99\u201d\uff0c\u4f46 \u89c1\u6d51\u8eab\u4e0a\u4e0b\uff0c\u5377\u8d77\u51e0\u9053\u5251\u5149\uff0c\u51b7\u7535\u7cbe\u8292\uff0c\u7f24\u7eb7\u98de\u821e\uff0c\u771f\u5982\u5927\u6f20\u6c99\u626c\uff0c\u75be\u653b\u800c\u4e0a\uff0c\u989d\u97f3\u548c\u5e03\u9886 \u8fc7\u5389\u5bb3\uff0c\u62c2\u5c18\u4e0d\u6562\u4e0e\u5b9d\u5251\u76f8\u89e6\uff0c\u4e00\u6cbe\u5373\u8d70\uff0c\u60ca\u98d8\u95ea\u7535\u822c\u7684\u53c8\u7ed5\u5230\u767d\u6cf0\u5b98\u80cc\u540e\uff0c\u9aa4\u4e0b\u6740\u624b\uff01 \u3000\u3000\u767d\u6cf0\u5b98\u76d8\u9f99\u7ed5\u6b65\uff0c\u6ed1\u8fc7\u4e00\u8fb9\uff0c\u9aa4\u7136\u53cd\u624b\u4e00\u63b7\uff0c\u559d\u58f0\uff1a\u201c\u7740\uff01\u201d\u4e00\u628a\u6885\u82b1\u9488\uff0c\u94f6\u5149\u95ea\u95ea\uff0c \u4e00\u9f50\u98de\u5230\u989d\u97f3\u548c\u5e03\u7684\u9762\u95e8\uff0c\u767d\u6cf0\u5b98\u4ee5\u524d\u4e3a\u4e86\u5bf9\u4ed8\u4e86\u56e0\uff0c\u66fe\u5728\u6885\u82b1\u9488\u4e0a\u4e0b\u8fc7\u82e6\u529f\uff0c\u6885\u82b1\u9488\u4efd\u91cf \u6781\u5c0f\uff0c\u4e0d\u6613\u53d7\u529b\uff0c\u800c\u4ed6\u5374\u53ef\u6253\u5230\u4e94\u4e08\u6709\u4f59\uff0c\u5e76\u53ef\u968f\u610f\u523a\u7a74\u4f24\u76ee\uff0c\u5389\u5bb3\u5f02\u5e38\uff0c\u800c\u4eca\u8ddd\u79bb\u6781\u8fd1\uff0c \u4ee5\u4e3a\u5b9a\u53ef\u594f\u6548\uff0c\u54ea\u6599\u5728\u94f6\u5149\u75be\u95ea\u4e4b\u4e2d\uff0c\u989d\u97f3\u548c\u5e03\u5c06\u62c2\u5c18\u4e00\u6536\u4e00\u5377\uff0c\u53ea\u542c\u5f97\u55e4\u55e4\u4e71\u54cd\uff0c\u6709\u5982\u7092 \u8c46\uff0c\u90a3\u4e48\u4e00\u5927\u628a\u7684\u6885\u82b1\u9488\uff0c\u90fd\u7ed9\u989d\u97f3\u548c\u5e03\u65bd\u5c55\u6700\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u5168\u90e8\u5377\u53bb\uff0c\u9707\u6210\u7c89\u5c51\u3002 \u3000\u3000\u674e\u6cbb\u89c1\u52bf\u4e0d\u597d\uff0c\u957f\u5251\u4e00\u6296\uff0c\u75be\u523a\u989d\u97f3\u548c\u5e03\u9762\u4e0a\u53cc\u775b\uff0c\u989d\u97f3\u548c\u5e03\u62c2\u5c18\u4e00\u7ed5\uff0c\u90a3\u77e5\u674e\u6cbb\u7684\u5251 \u660e\u4f3c\u5411\u5de6\uff0c\u5b9e\u662f\u5411\u53f3\uff0c\u5251\u950b\u9661\u7136\u4e00\u8f6c\uff0c\u524a\u5230\u53f3\u8033\uff0c\u989d\u97f3\u548c\u5e03\u4e00\u4e2a\u5f2f\u8170\uff0c\u582a\u582a\u907f\u8fc7\u3002\u674e\u6cbb\u633a\u5251 \u518d\u523a\uff0c\u731b\u4e0d\u9632\u5929\u53f6\u6563\u4eba\u659c\u523a\u6740\u51fa\uff0c\u53cc\u638c\u4e00\u63a8\uff0c\u8fc5\u5982\u5954\u96f7\uff0c\u674e\u6cbb\u8eab\u4e0d\u7531\u5df1\uff0c\u9000\u4e86\u4e24\u6b65\uff0c\u989d\u97f3\u548c \u5e03\u4e00\u4e2a\u957f\u8eab\uff0c\u62c2\u5c18\u6362\u98ce\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u53c8\u662f\u5f53\u5934\u7f69\u4e0b\u3002 \u3000\u3000\u6b63\u5728\u7d27\u6025\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u6e05\u5578\uff0c\u6070\u4f3c\u957f\u5929\u9e64\u5524\uff0c\u66f3\u7a7a\u800c\u4e0b\uff0c\u5929\u53f6\u6563\u4eba\u53eb\u9053\uff1a\u201c\u4e0a\u4eba\u5c0f\u5fc3\uff0c \u53c8\u662f\u90a3\u8d31\u5a62\u6765\u4e86\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u6ee1\u56ed\u60ca\u53eb\u4e4b\u4e2d\uff0c\u5c31\u5728\u5fa1\u82b1\u56ed\u4e2d\u679d\u67f3\u523a\u7a7a\u7684\u677e\u6811\u68a2\uff0c\u75be \u5982\u98de\u9e1f\u80a1\u7684\u63a0\u4e0b\u51e0\u4eba\uff0c\u770b\u6e05\u695a\u65f6\uff0c\u7adf\u662f\u4e09\u540d\u5c11\u5973\uff0c\u8863\u5e26\u98d8\u98d8\uff0c\u6709\u5982\u59d1\u5c04\u4ed9\u4eba\uff0c\u63a0\u7a7a\u800c\u964d\uff0c\u6df1 \u5bab\u5185\u9662\uff0c\u5979\u4eec\u7adf\u80fd\u6f5c\u4f0f\u8fdb\u6765\uff0c\u800c\u4e14\u5728\u6ee1\u56ed\u4eba\u4f17\uff0c\u5343\u767e\u53cc\u773c\u775b\u4e4b\u4e0b\uff0c\u5c45\u7136\u65e0\u4eba\u53d1\u73b0\uff0c\u53ea\u8fd9\u4e00\u4efd \u8f7b\u8eab\u529f\u592b\uff0c\u5c31\u5df2\u6559\u6240\u6709\u536b\u571f\uff0c\u5fc3\u5bd2\u80c6\u6218\uff01 \u3000\u3000\u8fd9\u4e09\u4eba\u6b63\u662f\u5415\u56db\u5a18\u548c\u51af\u745b\u51af\u7433\uff0c\u5176\u5b9e\uff0c\u5979\u4eec\u7684\u8f7b\u529f\u867d\u7136\u90fd\u5230\u4e86\u8e0f\u96ea\u65e0\u75d5\uff0c\u53bb\u6765\u65e0\u8ff9\u7684\u5730 \u6b65\uff0c\u4f46\u8981\u5077\u8fdb\u9ad8\u624b\u904d\u5e03\u7684\u7687\u5bab\uff0c\u5374\u4e5f\u4e0d\u662f\u6613\u4e8b\uff0c\u53ea\u56e0\u4eca\u65e5\u5143\u5bb5\uff0c\u5fa1\u82b1\u56ed\u4e2d\u6f14\u620f\uff0c\u6240\u6709\u9ad8\u624b\u90fd \u8c03\u5230\u56ed\u4e2d\u62a4\u536b\uff0c\u5979\u4eec\u624d\u80fd\u795e\u4e0d\u77e5\u9b3c\u4e0d\u515a\u7684\u5077\u5077\u6e9c\u4e86\u8fdb\u6765\u3002\u800c\u989d\u97f3\u548c\u5e03\u7b49\u53c8\u6b63\u5728\u548c\u5510\u6653\u6f9c\u8bf8\u4eba \u7f20\u6597\uff0c\u6ee1\u56ed\u4eba\u4f17\uff0c\u6ce8\u610f\u529b\u90fd\u88ab\u5438\u5f15\u8fc7\u53bb\uff0c\u8c01\u4e5f\u6ca1\u53d1\u73b0\u5979\u4eec\u3002 \u3000\u3000\u989d\u97f3\u548c\u5e03\u53eb\u58f0\u201c\u4e0d\u597d\u201d\uff0c\u62a2\u5148\u5835\u622a\uff0c\u5510\u6653\u6f9c\u5927\u53eb\u9053\uff1a\u201c\u7687\u5e1d\u5728\u7545\u97f3\u9601\u91cc\u3002\u201d\u5415\u56db\u5a18\u811a\u65b9 \u70b9\u5730\uff0c\u53c8\u518d\u98de\u8d77\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u53c8\u8df3\u4e0a\u697c\u680f\uff0c\u6251\u5165\u9601\u5185\uff0c\u5f20\u773c\u4e00\u77a7\uff0c\u6e3a\u65e0\u4e00\u4eba\uff0c\u5415\u56db\u5a18\u4f55\u7b49\u673a \u8b66\uff0c\u811a\u4e00\u4f38\u5165\uff0c\u971c\u534e\u5251\u4e00\u62db\u201c\u591c\u6218\u516b\u65b9\u201d\uff0c\u7acb\u5373\u53cd\u8eab\u98de\u51fa\uff0c\u7545\u97f3\u9601\u5185\uff0c\u98de\u7bad\u5982\u8757\uff0c\u90fd\u7ed9\u5979\u7684 \u5251\u5149\u524a\u65ad\u3002 \u3000\u3000\u51af\u745b\u51af\u7433\u7a0d\u540e\u534a\u6b65\uff0c\u521a\u597d\u78b0\u4e0a\u62a2\u6765\u5835\u622a\u7684\u989d\u97f3\u548c\u5e03\uff0c\u51af\u745b\u77ed\u5251\u4e00\u626c\uff0c\u8fc5\u5982\u7535\u63a3\uff0c\u4e00\u62db\u4e24 \u5f0f\uff0c\u65e2\u70b9\u9762\u95e8\uff0c\u53c8\u523a\u80f8\u819b\uff0c\u989d\u97f3\u548c\u5e03\u8eab\u968f\u5251\u8f6c\uff0c\u62c2\u5c18\u53cd\u81c2\u659c\u98de\uff0c\u5c18\u4e1d\u7f15\u7f15\uff0c\u6709\u5982\u5343\u767e\u6761\u516b\u722a \u7ae0\u9c7c\uff0c\u4e00\u9f50\u6293\u4e0b\uff0c\u51fa\u624b\u4e4b\u5feb\uff0c\u65e0\u4ee5\u5f62\u5bb9\uff0c\u5e78\u800c\u51af\u745b\u5728\u5929\u5c71\u53c8\u82e6\u7ec3\u4e86\u4e24\u5e74\uff0c\u6df1\u5f97\u5236\u654c\u673a\u5148\u4e4b \u65e8\uff0c\u53ea\u89c1\u5979\u4e0d\u614c\u4e0d\u5fd9\uff0c\u4ee5\u9759\u5236\u52a8\uff0c\u5fae\u5fae\u4e00\u95ea\uff0c\u5251\u5149\u75be\u5410\uff0c\u8fde\u7528\u201c\u6d41\u661f\u8d76\u6708\u201d\u3001\u201c\u98de\u4e91\u63a3\u7535\u201d \u4e24\u62db\uff0c\u4ece\u62c2\u5c18\u4e4b\u4e0b\uff0c\u9aa4\u7136\u7a7f\u8fc7\uff0c\u523a\u4ed6\u80c1\u4e0b\u7684\u201c\u671f\u95e8\u7a74\u201d\uff0c\u989d\u97f3\u548c\u5e03\u4e0d\u6599\u51af\u745b\u5251\u6cd5\u7cbe\u901a\u5982\u65af\uff0c \u62c2\u5c18\u4e00\u6536\uff0c\u638c\u529b\u5916\u53d1\uff0c\u53ea\u89c1\u94f6\u5149\u7ed5\u5904\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u51af\u745b\u8fde\u7528\u4ee5\u67d4\u5236\u521a\u4e4b\u52b2\uff0c\u5c06\u989d\u97f3\u548c\u5e03\u7684\u62c2 \u5c18\u524a\u53bb\u4e00\u7f15\uff0c\u4f46\u8eab\u5b50\u4e5f\u7ed9\u4ed6\u7684\u638c\u529b\u903c\u5f97\u7acb\u8db3\u4e0d\u7a33\uff0c\u6447\u6447\u6b32\u5815\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u51af\u7433\u5251\u8d70\u659c\u8fb9\uff0c\u4e00\u4e2a\u65cb\u8f6c\uff0c\u5251\u52bf\u5947\u5999\u4e4b\u6781\uff0c\u4f3c\u662f\u5411\u5de6\uff0c\u53c8\u4f3c\u5411\u53f3\uff0c\u523a \u5411\u989d\u97f3\u548c\u5e03\u7684\u547d\u95e8\u8981\u7a74\u3002\u989d\u97f3\u548c\u5e03\u6d51\u8eab\u6a2a\u7ec3\u529f\u592b\uff0c\u53ea\u6709\u574e\u706b\u79bb\u6c34\u4e4b\u7a74\u662f\u4ed6\u81f4\u547d\u6240\u5728\uff0c\u89c1\u72b6\u5403 \u4e86\u4e00\u60ca\uff0c\u6025\u8fd0\u5185\u5bb6\u771f\u529b\uff0c\u5012\u6301\u62c2\u5c18\uff0c\u4e00\u62db\u201c\u6a2a\u9a7e\u91d1\u6881\u201d\uff0c\u8981\u628a\u51af\u7433\u7684\u8155\u9aa8\u51fb\u788e\uff0c\u90a3\u77e5\u51af\u745b\u7a33 \u4e86\u8eab\u5f62\uff0c\u5251\u5149\u53c8\u5230\uff0c\u51af\u745b\u7684\u5185\u529f\u6bd4\u51af\u7433\u5f3a\u5f97\u591a\uff0c\u624b\u4e0a\u62ff\u7684\u53c8\u662f\u5b9d\u5251\uff0c\u989d\u97f3\u548c\u5e03\u4e0d\u6562\u786c\u67b6\uff0c\u518d \u8fd0\u638c\u529b\uff0c\u53cd\u51fb\u51af\u745b\uff0c\u989d\u97f3\u548c\u5e03\u7684\u5185\u529f\u53c8\u6bd4\u51af\u745b\u5f3a\u5f97\u591a\uff0c\u51af\u745b\u4e5f\u4e0d\u6562\u786c\u63a5\uff0c\u659c\u8eab\u6ed1\u6b65\uff0c\u8df3\u8fc7\u4e00 \u8fb9\uff0c\u989d\u97f3\u548c\u5e03\u6b63\u5f85\u8ffd\u654c\uff0c\u51af\u7433\u7684\u6740\u624b\u5251\u6cd5\u53c8\u5954\u5411\u4ed6\u7684\u574e\u706b\u79bb\u6c34\u4e4b\u7a74\uff0c\u989d\u97f3\u548c\u5e03\u6c14\u5f97\u54c7\u54c7\u5927 \u53eb\uff0c\u5374\u662f\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u5f97\u6536\u56de\u62c2\u5c18\uff0c\u64a4\u56de\u638c\u529b\uff0c\u9632\u62a4\u7a74\u9053\u3002 \u3000\u3000\u4f60\u9053\u51af\u7433\u4f55\u4ee5\u8fd9\u6837\u5389\u5bb3\uff1f\u539f\u6765\u5979\u65e9\u5e74\u5728\u56db\u7687\u5b50\u5e9c\u5185\uff0c\u7cbe\u4e60\u5404\u79cd\u90aa\u6d3e\u6b66\u529f\uff0c\u77e5\u9053\u897f\u85cf\u7ea2\u6559 \u7684\u62c2\u7a74\u523a\u7a74\u4e4b\u6cd5\uff0c\u8fd9\u4e24\u5e74\u53c8\u5f97\u4e86\u65e0\u6781\u6d3e\u7684\u771f\u4f20\uff0c\u529f\u529b\u5927\u8fdb\uff0c\u5bf9\u7834\u201c\u6a2a\u7ec3\u529f\u592b\u201d\u7684\u6cd5\u95e8\uff0c\u6781\u4e4b \u7eaf\u719f\u3002\u4e24\u59d0\u59b9\u8054\u624b\u5408\u6597\u5f3a\u654c\uff0c\u6740\u5f97\u989d\u97f3\u548c\u5e03\u4e5f\u5fae\u6709\u602f\u610f\u3002 \u3000\u3000\u6b64\u65f6\u5415\u56db\u5a18\u5df2\u8df3\u51fa\u4e86\u7545\u97f3\u9601\uff0c\u659c\u773c\u4e00\u77a5\uff0c\u89c1\u989d\u97f3\u548c\u5e03\u4e0e\u51af\u5bb6\u59d0\u59b9\u7f20\u6597\u4e0d\u4e0b\uff0c\u4e5f\u4e0d\u7406\u4ed6\uff0c \u5251\u4f3c\u8ffd\u98ce\uff0c\u8ff3\u81ea\u6251\u5165\u536b\u58eb\u7fa4\u4e2d\uff0c\u626c\u58f0\u53eb\u9053\uff1a\u201c\u5feb\u95ef\u51fa\u53bb\uff0c\u96cd\u6b63\u8fd9\u72d7\u7687\u5e1d\u65e9\u5df2\u9003\u4e86\u3002\u201d\u5510\u6653\u6f9c \u9053\uff1a\u201c\u7518\u5927\u4fa0\uff0c\u4ed6\uff0c\u4ed6\u2026\u2026\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e03\u54e5\u4e0d\u89c1\u8e2a\u8ff9\uff0c\u54b1\u4eec\u4eca\u65e5\u7b97\u662f\u6298\u4e86\u3002\u201d\u9c7c\u4eae\u987b\u7709 \u6380\u52a8\uff0c\u8fd9\u65f6\u4ed6\u53c8\u5df2\u62a2\u5230\u4e00\u6761\u82b1\u67aa\uff0c\u5411\u524d\u731b\u51b2\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6740\u5f97\u4e00\u4e2a\u662f\u4e00\u4e2a\uff0c\u54b1\u4eec\u66ff\u7518\u5927\u4fa0\u62a5 \u4ec7\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4e03\u54e5\u6b66\u529f\u65e2\u9ad8\uff0c\u4eba\u53c8\u673a\u667a\uff0c\u672a\u5fc5\u4fbf\u906d\u4e0d\u6d4b\u3002\u54b1\u4eec\u53ef\u4e0d\u80fd\u518d\u786c\u62fc\u4e0b\u53bb\uff0c\u51b2 \u51fa\u53bb\u8981\u7d27\u3002\u201d\u5979\u4e00\u9762\u8bf4\u8bdd\uff0c\u624b\u4e0b\u5374\u6beb\u4e0d\u653e\u677e\uff0c\u5251\u950b\u6240\u6307\uff0c\u5982\u6c64\u6cfc\u96ea\uff0c\u5c06\u8fd1\u8eab\u7684\u4f8d\u536b\u6740\u5f97\u624b\u65ad \u8db3\u6298\uff0c\u5934\u7834\u8840\u6d41\u3002 \u3000\u3000\u97e9\u91cd\u5c71\u5927\u6012\uff0c\u8df3\u4e86\u4e0a\u6765\uff0c\u8f9f\u4e91\u9504\u4e00\u62db\u201c\u6cf0\u5c71\u538b\u9876\u201d\uff0c\u84e6\u5730\u5411\u5415\u56db\u5a18\u5f53\u5934\u5288\u4e0b\uff0c\u5415\u56db\u5a18\u4e00 \u58f0\u51b7\u7b11\uff0c\u4e09\u5c3a\u9752\u950b\uff0c\u659c\u659c\u4e00\u62cd\uff0c\u7adf\u81ea\u5c06\u97e9\u91cd\u5c71\u7684\u8f9f\u4e91\u9504\u5243\u51fa\u5916\u95e8\uff0c\u66f4\u4e0d\u6362\u52bf\uff0c\u624b\u8155\u4e00\u6c89\uff0c\u5251 \u62db\u53c8\u53d1\uff0c\u54c8\u5e03\u9640\u53eb\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u201d\u6025\u6025\u98de\u9524\u659c\u88ad\uff0c\u90a3\u6599\u5415\u56db\u5a18\u8eab\u6cd5\u5feb\u5230\u96be\u4ee5\u5f62\u5bb9\uff0c\u54c8\u5e03\u9640\u7684 \u53cc\u9524\uff0c\u660e\u660e\u5df2\u7838\u5230\u5979\u7684\u5934\u9876\uff0c\u5374\u4e0d\u77e5\u600e\u7684\u4e00\u4e0b\u5b50\u4fbf\u7ed9\u5979\u95ea\u4e86\u8fc7\u53bb\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u5251\u950b\u659c\u5c55\uff0c \u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u5c06\u97e9\u91cd\u5c71\u7684\u53f3\u624b\u624b\u6307\uff0c\u524a\u53bb\u4e24\u6307\uff0c\u8f9f\u4e91\u9504\u8131\u624b\u98de\u51fa\uff0c\u6070\u597d\u78b0\u7740\u4e00\u540d\u4f8d\u536b\uff0c\u7adf \u81ea\u5c06\u4ed6\u62e6\u8170\u65a9\u65ad\uff0c\u5288\u4e3a\u4e24\u622a\u3002 \u3000\u3000\u5415\u56db\u5a18\u8fd9\u51e0\u5e74\u5728\u9099\u5c71\u82e6\u7ec3\uff0c\u5251\u6cd5\u901a\u7384\uff0c\u5185\u529f\u4e5f\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u6bd4\u97e9\u91cd\u5c71\u5df2\u4e0d\u53ea\u9ad8\u51fa \u4e00\u7b79\uff0c\u97e9\u91cd\u5c71\u4e0d\u77e5\uff0c\u8fd8\u4ee5\u4e3a\u5979\u662f\u5f53\u5e74\u7684\u5415\u56db\u5a18\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5415\u56db\u5a18\u5251\u6cd5\u867d\u7136\u7cbe\u5999\uff0c\u529f\u529b\u5374 \u7a0d\u900a\u4e8e\u6211\uff0c\u7eb5\u7b97\u4e0d\u654c\uff0c\u4e5f\u6700\u5c11\u53ef\u6253\u4e2a\u4e00\u4e8c\u767e\u62db\u3002\u6599\u654c\u4e00\u5dee\uff0c\u9632\u654c\u4fbf\u758f\uff0c\u6240\u4ee5\u672c\u6765\u53ef\u6321\u5f97\u4e09\u4e94 \u5341\u62db\u7684\uff0c\u5374\u5728\u4e09\u62db\u4e4b\u5185\uff0c\u4fbf\u7ed9\u5415\u56db\u5a18\u6740\u4f24\u4e86\u3002\u54c8\u5e03\u9640\u89c1\u97e9\u91cd\u5c71\u53d7\u4f24\uff0c\u5927\u5403\u4e00\u63a0\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002 \u5929\u53f6\u6563\u4eba\u4e0d\u6562\u604b\u6218\uff0c\u4e5f\u53ea\u80fd\u63a9\u62a4\u5e08\u5144\u9000\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u54c8\u5e03\u9640\u7b49\u4e09\u5927\u9ad8\u624b\u4e00\u8d70\uff0c\u536b\u58eb\u4eec\u66f4\u662f\u4e0d\u6562\u8ffd\u51fb\uff0c\u51af\u745b\u51af\u7433\u968f\u7740\u95ef\u51fa\uff0c\u989d\u97f3\u548c\u5e03\u5b64\u638c\u96be \u9e23\uff0c\u4e5f\u4e0d\u6562\u8ffd\uff0c\u628a\u624b\u4e00\u6325\uff0c\u53eb\u4f17\u536b\u58eb\u6551\u6b7b\u6276\u4f24\uff0c\u81ea\u5df1\u5219\u6025\u6025\u5165\u5bab\u53bb\u89c1\u7687\u5e1d\u3002 \u3000\u3000\u96cd\u6b63\u8fd9\u65f6\u5df2\u85cf\u5165\u6df1\u5bab\u5bc6\u5ba4\uff0c\u539f\u6765\u4ed6\u9632\u5907\u6781\u4e3a\u5468\u5bc6\uff0c\u65e9\u5728\u7545\u97f3\u9601\u5185\uff0c\u5e03\u597d\u673a\u5173\uff0c\u5e76\u6398\u4e86\u5730 \u9053\uff0c\u901a\u5230\u5bab\u5185\uff0c\u800c\u4e14\u53c8\u5148\u53eb\u989d\u97f3\u548c\u5e03\u57cb\u4f0f\u5728\u91cc\u9762\u3002\u7518\u51e4\u6c60\u4e00\u65f6\u5fc3\u6025\uff0c\u8ffd\u5165\u7545\u97f3\u9601\u5185\uff0c\u5403\u4e86\u6697 \u7b97\uff0c\u800c\u96cd\u6b63\u5374\u4ece\u5730\u9053\u8d70\u4e86\u3002 \u3000\u3000\u989d\u97f3\u548c\u5e03\u8bf7\u5185\u76d1\u5f15\u5165\uff0c\u5728\u5bc6\u5ba4\u4e2d\u8c12\u89c1\u96cd\u6b63\uff0c\u53e9\u5934\u8bf7\u7f6a\u3002\u96cd\u6b63\u9053\uff1a\u201c\u4f60\u6551\u9a7e\u6709\u529f\uff0c\u4f55\u7f6a\u4e4b \u6709\uff1f\u6715\u53ea\u6068\u5141\u745b\u8fd9\u5c0f\u755c\u751f\uff0c\u7adf\u4e5f\u6562\u6765\u6697\u7b97\u4e8e\u6211\u3002\u201d\u989d\u97f3\u548c\u5e03\u9053\uff1a\u201c\u5c0f\u4eb2\u738b\u672a\u5fc5\u662f\u6709\u610f\u7684\u3002\u201d \u96cd\u6b63\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u662f\u6709\u610f\u65e0\u610f\uff0c\u4e0d\u80fd\u4e0d\u7f5a\uff0c\u592a\u540e\u73b0\u5df2\u6551\u56de\uff0c\u73b0\u5728\u5c1a\u5728\u660f\u8ff7\u4e2d\uff0c\u4f60\u5feb\u53bb\u5c06\u8fd9\u5c0f \u755c\u751f\u5524\u6765\uff0c\u8981\u4e0d\u7136\u592a\u540e\u9192\u6765\uff0c\u53c8\u8981\u62e6\u963b\u4e86\u3002\u201d\u989d\u97f3\u548c\u5e03\u5782\u624b\u5e94\u4e86\u4e00\u58f0\uff1a\u201c\u55b3\uff01\u201d\u6b63\u5f85\u8d70\u51fa\uff0c \u96cd\u6b63\u53c8\u9053\uff1a\u201c\u4f60\u5148\u53eb\u7cbe\u901a\u6c34\u6027\u7684\u5230\u5fa1\u6cb3\u641c\u7d22\uff0c\u770b\u7518\u51e4\u6c60\u6b7b\u4e86\u6ca1\u6709\uff1f\u201d\u989d\u97f3\u548c\u5e03\u9053\uff1a\u201c\u4ed6\u8eab\u53d7 \u7bad\u4f24\uff0c\u53c8\u5403\u4e86\u5974\u624d\u4e00\u638c\uff0c\u8dcc\u4e0b\u5fa1\u6cb3\uff0c\u5bd2\u51ac\u814a\u6708\uff0c\u6c34\u51b7\u5982\u51b0\uff0c\u7eb5\u4ed6\u4e0d\u6b7b\uff0c\u4e5f\u96be\u4ee5\u8fd0\u7528\u5185\u529f\uff0c\u51b7 \u4e5f\u51b7\u6b7b\u4ed6\u3002\u201d\u96cd\u6b63\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u8fd8\u662f\u641c\u5230\u4e86\u5c38\u8eab\uff0c\u6715\u624d\u653e\u5fc3\u3002\u201d\u989d\u97f3\u548c\u5e03\u53c8\u5e94\u4e86\u4e00\u58f0\uff1a \u201c\u55b3\u3002\u201d\u95ee\u9053\uff1a\u201c\u7687\u4e0a\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\u5417\uff1f\u201d\u96cd\u6b63\u60f3\u4e86\u4e00\u60f3\uff0c\u5ffd\u9053\uff1a\u201c\u4e0d\u7ba1\u6709\u6ca1\u6709\u641c\u7740\u7518\u51e4\u4ed6 \u7684\u5c38\u8eab\uff0c\u4f60\u90fd\u53eb\u4eba\u5230\u57ce\u5185\u53bb\u653e\u51fa\u8c23\u8a00\uff0c\u8bf4\u662f\u7518\u51e4\u6c60\u5df1\u88ab\u6211\u4eec\u6349\u7740\u4e86\u3002\u79d8\u5bc6\u4e0d\u53ef\u6cc4\u6f0f\uff0c\u81f3\u7d27\u81f3 \u7d27\uff01\u201d \u3000\u3000\u7687\u5bab\u5185\u7eb7\u7eb7\u6270\u6270\uff0c\u8fc7\u4e86\u4e00\u591c\uff0c\u7b2c\u4e8c\u65e5\u96cd\u6b63\u4e0b\u4ee4\u4e5d\u95e8\u5927\u641c\uff0c\u540c\u65f6\u5728\u5bab\u5185\u66f4\u52a0\u7d27\u5e03\u7f6e\uff0c\u5fd9\u4e86 \u4e00\u65e5\uff0c\u771f\u6b63\u7684\u53db\u515a\u4e00\u4e2a\u4e5f\u6349\u4e0d\u5230\uff0c\u53ea\u6349\u5230\u4e86\u4e00\u4e9b\u201c\u53ef\u7591\u7684\u201d\u65e0\u8f9c\u7684\u5e73\u6c11\uff0c\u7518\u51e4\u6c60\u7684\u5c38\u8eab\u4e5f\u641c \u4e0d\u7740\u3002\u96cd\u6b63\u6b63\u5728\u7eb3\u7f55\uff0c\u508d\u665a\u65f6\u5206\uff0c\u5ffd\u89c1\u4e00\u4e2a\u4eb2\u4fe1\u7684\u5185\u76d1\u6765\u89c1\uff0c\u62a5\u9053\uff1a\u201c\u4faf\u4e09\u53d8\u6c42\u89c1\u7687\u4e0a\u3002\u201d \u3000\u3000\u4faf\u4e09\u53d8\u4e43\u662f\u53db\u53d8\u51fa\u5bab\u7684\u8001\u536b\u58eb\uff0c\u989d\u97f3\u548c\u5e03\u5728\u4fa7\uff0c\u542c\u8bf4\u662f\u4ed6\u6c42\u89c1\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u96cd\u6b63\u8bf4\u9053\uff1a \u201c\u53eb\u4ed6\u4eba\u6765\u3002\u201d\u989d\u97f3\u548c\u5e03\u9053\uff1a\u201c\u53ea\u6050\u5176\u4e2d\u6709\u8bc8\u3002\u201d\u96cd\u6b63\u7b11\u9053\uff1a\u201c\u6715\u6b63\u8981\u5c06\u8ba1\u5c31\u8ba1\uff0c\u4f55\u865e\u5176 \u8bc8\u3002\u201d\u95ee\u5185\u76d1\u9053\uff1a\u201c\u4ed6\u662f\u600e\u6837\u6765\u7684\uff1f\u201d\u90a3\u5185\u76d1\u9053\uff1a\u201c\u4ed6\u53cd\u624b\u81ea\u7f1a\uff0c\u6c42\u89c1\u5185\u5ef7\u603b\u7ba1\uff0c\u8bf4\u662f\u6709\u4e00 \u4ef6\u6781\u673a\u5bc6\u4e4b\u4e8b\uff0c\u8981\u8bf4\u4e0e\u7687\u4e0a\u77e5\u9053\u3002\u4ed6\u8fd8\u5e26\u4e86\u4e00\u4e2a\u8499\u9762\u4eba\u6765\uff0c\u79d8\u5bc6\u5c31\u5728\u8499\u9762\u4eba\u8eab\u4e0a\u3002\u603b\u7ba1\u4e0d\u6562 \u4f5c\u4e3b\uff0c\u8bf7\u7687\u4e0a\u660e\u793a\u3002\u201d\u96cd\u6b63\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u90fd\u53eb\u8fdb\u6765\u3002\u201d \u3000\u3000\u7247\u523b\u4e4b\u540e\uff0c\u5185\u76d1\u5c06\u4faf\u4e09\u53d8\u4e0e\u90a3\u8499\u9762\u4eba\u5e26\u8fdb\uff0c\u96cd\u6b63\u5389\u58f0\u559d\u9053\uff1a\u201c\u5019\u4e09\u53d8\uff0c\u4f60\u8fd8\u6709\u80c6\u6765\u89c1\u6211 \u5417\uff1f\u201d\u4faf\u4e09\u53d8\u53e9\u9996\u9053\uff1a\u201c\u5974\u624d\u77e5\u9053\u9519\u4e86\uff0c\u7279\u6765\u5c06\u529f\u8d4e\u7f6a\u3002\u201d\u96cd\u6b63\u201c\u5514\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\uff0c \u5f88\u597d\uff01\u6715\u4e00\u5411\u5bbd\u5927\u5f85\u4eba\uff0c\u4f60\u65e2\u6709\u6094\u8fc7\u4e4b\u5fc3\uff0c\u6715\u5f53\u7ed9\u4f60\u81ea\u65b0\u4e4b\u8def\uff0c\u8bf4\u5427\u3002\u201d\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u8bf7\u7687 \u4e0a\u5c4f\u9000\u5de6\u53f3\u3002\u201d\u96cd\u6b63\u54c8\u54c8\u5927\u7b11\uff0c\u9000\uff1a\u201c\u4f60\u5f53\u6715\u662f\u4e09\u5c3a\u4e4b\u7ae5\u5417\uff1f\u201d\u4faf\u4e09\u53d8\u9053\uff1a\u201c\u82e5\u7136\u7687\u4e0a\u89c1 \u7591\uff0c\u8bf7\u5c06\u5974\u624d\u7684\u7435\u7436\u9aa8\u7a7f\u4e86\u3002\u201d\u96cd\u6b63\u9053\uff1a\u201c\u4f60\u5012\u8fd8\u723d\u5feb\uff0c\u6211\u4e5f\u4e0d\u7a7f\u4f60\u7684\u7435\u7436\u9aa8\uff0c\u514d\u4f60\u6b8b\u5e9f\u3002 \u989d\u97f3\u548c\u5e03\uff0c\u5c06\u8fd9\u4e24\u4eba\u7684\u6b66\u529f\u5e9f\u6389\uff01\u201d\u989d\u97f3\u548c\u5e03\u5e94\u4e86\u4e00\u58f0\uff0c\u5728\u8eab\u4e0a\u53d6\u51fa\u4e00\u6839\u5c3a\u8bb8\u7684\u957f\u9489\uff0c\u94f6\u5149 \u95ea\u95ea\uff0c\u4e0d\u7531\u5206\u8bf4\uff0c\u5728\u5019\u4e09\u53d8\u548c\u90a3\u8499\u9762\u4eba\u7684\u8eab\u4e0a\u5404\u523a\u4e09\u9488\uff0c\u968f\u5373\u53cc\u624b\u4f38\u5f00\uff0c\u5206\u522b\u5728\u4e8c\u4eba\u8170\u4e0a\u4e00 \u634f\uff0c\u4faf\u4e09\u53d8\u4e00\u4e2a\u8e09\u8dc4\uff0c\u51e0\u4e4e\u8dcc\u5012\uff0c\u90a3\u8499\u9762\u4eba\u4e5f\u6b6a\u6b6a\u659c\u659c\uff0c\u4e24\u4eba\u989d\u4e0a\uff0c\u90fd\u8ff8\u51fa\u9ec4\u8c46\u80a1\u7684\u5927\u6c57 \u73e0\uff0c\u96cd\u6b63\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u73b0\u5728\u867d\u7136\u6682\u65f6\u53d7\u82e6\uff0c\u5374\u514d\u4e86\u6b8b\u5e9f\uff0c\u4ee5\u540e\u4f60\u4eec\u5c31\u5982\u5e38\u4eba\u4e00\u822c\uff0c \u53ef\u4ee5\u597d\u597d\u7684\u5b89\u4efd\u8fc7\u65e5\u5b50\u4e86\u3002\u4f60\u4eec\u8bf4\u6715\u662f\u4e0d\u662f\u7279\u522b\u5bbd\u5927\uff0c\u683c\u5916\u5f00\u6069\u3002\u201d\u4faf\u4e09\u53d8\u53e9\u5934\u9053\uff1a\u201c\u5974\u624d \u8c22\u6069\u3002\u201d\u96cd\u6b63\u6325\u6325\u624b\u9053\uff1a\u201c\u989d\u97f3\u548c\u5e03\uff0c\u4f60\u51fa\u53bb\u5427\u3002\u201d \u3000\u3000\u989d\u97f3\u548c\u5e03\u6084\u6084\u9000\u51fa\uff0c\u539f\u6765\u989d\u97f3\u548c\u5e03\u6709\u4e00\u79cd\u72ec\u95e8\u6b66\u529f\uff0c\u80fd\u7528\u94f6\u9488\uff0c\u9694\u8863\u523a\u7a74\uff0c\u5c06\u654c\u4eba\u7684\u771f \u5143\u4e4b\u6c14\u6cc4\u6389\uff0c\u591a\u597d\u6b66\u529f\uff0c\u4e5f\u4f1a\u6d88\u5931\u3002\u989d\u97f3\u548c\u5e03\u53c8\u56e0\u4e0a\u6b21\u53d7\u8fc7\u51af\u745b\u7684\u6559\u8bad\uff0c\uff08\u51af\u745b\u7a7f\u6709\u62a4\u8eab\u5b9d \u7532\uff0c\u88ab\u523a\u4e4b\u540e\uff0c\u6b66\u529f\u4ecd\u80fd\u4fdd\u6301\u3002\uff09\u6240\u4ee5\u523a\u7a74\u4e4b\u540e\uff0c\u518d\u5728\u4ed6\u4eec\u4e8c\u4eba\u8eab\u4e0a\u4e00\u634f\uff0c\u82e5\u662f\u6b66\u529f\u8fd8\u5728\u7684 \u4eba\uff0c\u81ea\u7136\u4f1a\u6709\u53cd\u5e94\uff0c\u4e00\u8bd5\u4e4b\u4e0b\uff0c\u8bd5\u51fa\u4ed6\u4eec\u5185\u52b2\u5168\u65e0\uff0c\u8fd9\u624d\u5b89\u5fc3\u8d70\u51fa\u3002 \u3000\u3000\u96cd\u6b63\u5f85\u989d\u97f3\u548c\u5e03\u4e00\u51fa\u5bc6\u5ba4\uff0c\u7acb\u5373\u95ee\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u673a\u5bc6\u4e4b\u4e8b\u544a\u4e0e\u6715\u542c\uff1f\u201d\u5019\u4e09\u53d8\u9053\uff1a \u201c\u673a\u5bc6\u5c31\u5728\u6b64\u4eba\u8eab\u4e0a\u3002\u201d\u4f38\u624b\u4e00\u63ed\uff0c\u5c06\u90a3\u8499\u9762\u4eba\u7684\u9762\u5dfe\u63ed\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u53ef\u8ba4\u8bc6\u6b64\u4eba \u5417\uff1f\u201d\u96cd\u6b63\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u662f\u5510\u6653\u6f9c\uff0c\u5510\u4fa0\u58eb\uff0c\u54b1\u4eec\u53ef\u662f\u8001\u670b\u53cb\u4e86\uff01\u201d\u4faf\u4e09\u53d8 \u9053\uff1a\u201c\u7687\u4e0a\u53ef\u77e5\u9053\u5510\u6653\u6f9c\u7684\u6765\u5386\u4e48\uff1f\u201d\u96cd\u6b63\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u77e5\u9053\uff1f\u6653\u6f9c\uff0c\u8bf4 \u8d77\u6765\u54b1\u4eec\u8fd8\u4e0d\u6b62\u662f\u8001\u670b\u53cb\uff0c\u800c\u4e14\u662f\u540c\u80de\u5144\u5f1f\u5462\uff01\u4f60\u65e2\u77e5\u81ea\u8eab\u6765\u5386\uff0c\u5c31\u597d\u597d\u7684\u5728\u5bab\u4e2d\u4eab\u798f\uff0c\u4e0d \u8981\u518d\u8ddf\u90a3\u4e9b\u6c49\u4eba\u80e1\u641e\u4e86\u3002\u201d\u5510\u6653\u6f9c\u4e0e\u4faf\u4e09\u53d8\u90fd\u662f\u4e00\u6014\uff0c\u970e\u65f6\u95f4\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u539f\u6765\u96cd\u6b63\u5728\u5eb7\u7199\u6b7b\u540e\uff0c\u641c\u67e5\u4ed6\u7684\u79d8\u5bc6\uff0c\u53d1\u73b0\u4ed6\u75c5\u4e2d\u7684\u65e5\u8bb0\uff0c\u5176\u4e2d\u6709\u53d9\u8ff0\u5510\u6653\u6f9c\u7684\u7ecf\u8fc7\uff0c \u5bf9\u4ed6\u9887\u4e3a\u601d\u5ff5\uff0c\u8ba4\u4e3a\u5728\u5341\u51e0\u4e2a\u513f\u5b50\u4e2d\uff0c\u8fd8\u662f\u8fd9\u4e2a\u4e0d\u80fd\u76f8\u8ba4\u7684\u513f\u5b50\uff0c\u54c1\u683c\u6700\u4f73\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u96cd\u6b63\u5bf9\u5510\u6653\u6f9c\u624d\u7279\u522b\u5fcc\u6068\uff0c\u8fd9\u65f6\u60f3\u7528\u8f6f\u529f\u5957\u51fa\u4ed6\u7684\u53e3\u4f9b\u3002\u5510\u6653\u6f9c\u54ac\u7259\u5207\u9f7f\uff0c\u5927\u58f0 \u9a82\u9053\uff1a\u201c\u4faf\u8001\u8d3c\uff0c\u6211\u88ab\u4f60\u6240\u9a97\uff0c\u88ab\u4f60\u6349\u5230\u5bab\u4e2d\uff0c\u6211\u6068\u4e0d\u5f97\u98df\u4f60\u4e4b\u8089\uff01\u201d\u96cd\u6b63\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4f60 \u771f\u4e2a\u8981\u80f3\u818a\u5411\u5916\u5f2f\u5417\uff1f\u90a3\u4e9b\u80e1\u95f9\u7684\u6c49\u4eba\u85cf\u5728\u54ea\u91cc\uff1f\u4f60\u8bf4\u51fa\u6765\uff0c\u6715\u7acb\u5373\u8ba4\u4f60\u4e3a\u5f1f\uff0c\u8d50\u4f60\u4eb2\u738b\u5c01 \u53f7\u3002\u201d\u5510\u6653\u6f9c\u95ed\u53e3\u4e0d\u8bf4\uff0c\u96cd\u6b63\u9053\uff1a\u201c\u5f53\u4eca\u5929\u4e0b\uff0c\u6709\u8c01\u6562\u6297\u6715\u4e4b\u547d\uff1f\u4f60\u8981\u77e5\u9053\u5141\u5510\u5141\u4fc4\u7684\u4e0b\u573a \u5417\uff1f\u4ed6\u4eec\u9003\u51fa\u5bab\u5ef7\uff0c\u88ab\u6715\u64d2\u56de\uff0c\u5df2\u7ecf\u5316\u9aa8\u626c\u7070\u4e86\uff0c\u5bab\u4e2d\u6709\u7684\u662f\u73b0\u6210\u7684\u70ae\u70d9\u4e4b\u5211\uff0c\u4f60\u662f\u4e0d\u662f\u4e5f \u60f3\u548c\u4ed6\u4eec\u4e00\u6837\uff1f\u201d\u626c\u58f0\u53eb\u9053\uff1a\u201c\u989d\u97f3\u548c\u5e03\uff0c\u9884\u5907\u70ae\u70d9\uff01\u201d\u968f\u5373\u653e\u8f6f\u53e3\u6c14\uff0c\u53c8\u5fae\u7b11\u9053\uff1a\u201c\u4eb2\u738b \u4e4b\u53f7\u4e0e\u70ae\u70d9\u4e4b\u5211\uff0c\u968f\u4f60\u9009\u5427\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u4f4e\u9996\u4f5c\u6c89\u601d\u4e4b\u72b6\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u59cb\u62ac\u5934\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u8bf4\uff0c\u6211\u8bf4\uff0c\u8fd9\u91cc\u6709\u4e00\u5f20\u540d \u5355\uff0c\u6070\u5de7\u6211\u8fd8\u5e26\u5728\u8eab\u4e0a\uff0c\u6211\u4ea4\u7ed9\u4f60\u3002\u201d\u96cd\u6b63\u8fc8\u524d\u51e0\u6b65\uff0c\u4f38\u624b\u8bf4\u9053\uff1a\u201c\u62ff\u6765\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6 \u5feb\uff0c\u5510\u6653\u6f9c\u7a81\u7136\u53cd\u624b\u4e00\u62ff\uff0c\u95ea\u7535\u822c\u7684\u52fe\u7740\u96cd\u6b63\u7684\u624b\u8155\uff0c\u96cd\u6b63\u4e5f\u771f\u5389\u5bb3\uff0c\u53f3\u8db3\u4e00\u80f3\uff0c\u7acb\u523b\u65bd\u5c55 \u5c11\u6797\u771f\u4f20\u7684\u201c\u8fde\u73af\u6b7b\u5f71\u811a\u201d\u8e22\u4ed6\u8170\u80ef\uff0c\u8fd9\u4e00\u811a\u82e5\u88ab\u8e22\u4e2d\uff0c\u5510\u6653\u6f9c\u4e0d\u6b7b\u4e5f\u5f97\u91cd\u4f24\u3002\u5c31\u5728\u5510\u6653\u6f9c \u53d1\u96be\u4e4b\u65f6\uff0c\u4faf\u4e09\u53d8\u4e5f\u548c\u8eab\u6251\u4e0a\uff0c\u8fd9\u4e00\u811a\u6b63\u597d\u8e22\u4e2d\u4faf\u4e09\u53d8\u5934\u9885\uff0c\u767b\u65f6\u8111\u6d46\u8ff8\u6d41\uff0c\u6b7b\u4e8e\u975e\u547d\u3002\u53ef \u662f\u96cd\u6b63\u53d7\u4e86\u4faf\u4e09\u53d8\u4e00\u963b\uff0c\u5510\u6653\u6f9c\u8eab\u624b\u4f55\u7b49\u5feb\u6377\uff0c\u7acb\u523b\u9a88\u6307\u4e00\u6233\uff0c\u70b9\u4e2d\u4ed6\u7684\u7a74\u9053\u3002\u96cd\u6b63\u5de6\u8db3\u521a \u521a\u63d0\u8d77\uff0c\u5374\u5df2\u8f6f\u7ef5\u7ef5\u7684\u8e22\u4e0d\u51fa\u53bb\u3002 \u3000\u3000\u539f\u6765\u4faf\u4e09\u53d8\u4e0e\u5510\u6653\u6f9c\u6240\u4f7f\u7684\u4e43\u662f\u82e6\u8089\u8ba1\u3002\u5728\u5927\u95f9\u5fa1\u82b1\u56ed\u4e4b\u540e\uff0c\u4faf\u4e09\u53d8\u6697\u4e2d\u6253\u63a2\uff0c\u6b63\u597d\u542c \u5230\u4e86\u96cd\u6b63\u6240\u653e\u51fa\u7684\u8c23\u8a00\uff0c\u53ea\u9053\u7518\u51e4\u6c60\u771f\u4e2a\u88ab\u64d2\u3002\u4f17\u4eba\u5546\u8bae\uff0c\u65e0\u6cd5\u65bd\u6551\uff0c\u6240\u4ee5\u624d\u7531\u4faf\u4e09\u53d8\u5b9a\u51fa \u8fd9\u6761\u82e6\u8089\u8ba1\u6765\uff0c\u5192\u9669\u8fdb\u5bab\uff0c\u51c6\u5907\u52ab\u6301\u7687\u5e1d\u3002 \u3000\u3000\u5510\u6653\u6f9c\u8eab\u4e0a\u7a7f\u6709\u4ece\u51af\u745b\u501f\u6765\u7684\u91d1\u4e1d\u8f6f\u7532\uff0c\u88ab\u989d\u97f3\u548c\u5e03\u94f6\u9488\u523a\u540e\uff0c\u7acb\u523b\u8fd0\u7528\u6613\u5170\u73e0\u6240\u4f20\u7684 \u4e0a\u4e58\u5185\u529f\uff0c\u628a\u52b2\u529b\u6536\u655b\uff0c\u53c8\u8ff8\u51fa\u6c57\u73e0\uff0c\u989d\u97f3\u548c\u5e03\u548c\u96cd\u6b63\u90a3\u4e48\u7cbe\u660e\u7684\u4eba\uff0c\u7adf\u7136\u88ab\u4ed6\u9a97\u8fc7\uff0c\u81f3\u4e8e \u5019\u4e09\u53d8\u5219\u771f\u662f\u6b66\u529f\u6d88\u5931\uff0c\u62fc\u6b7b\u66ff\u5510\u6653\u6f9c\u6321\u4e86\u4e00\u811a\uff0c\u4e3a\u53cb\u727a\u7272\u4e86\u3002 \u3000\u3000\u4e24\u4e0b\u52a8\u624b\uff0c\u6709\u5982\u8fc5\u96f7\u75be\u98ce\uff0c\u5f85\u989d\u97f3\u548c\u5e03\u51b2\u5165\u6765\u65f6\uff0c\u96cd\u6b63\u5df2\u88ab\u5510\u6653\u6f9c\u5236\u670d\uff0c\u4e0d\u80fd\u52a8\u5f39\u3002\u989d \u97f3\u548c\u5e03\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u53ea\u89c1\u5510\u6653\u6f9c\u7684\u5b9d\u5251\uff0c\u6b63\u6a2a\u67b6\u5728\u96cd\u6b63\u7684\u9888\u9879\uff0c\u6295\u9f20\u5fcc\u5668\uff0c\u90a3\u6562\u5411\u524d\u3002 \u3000\u3000\u96cd\u6b63\u53cd\u800c\u9547\u9759\u975e\u5e38\uff0c\u5ffd\u7136\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u4f60\u771f\u884c\uff01\u6211\u4e5f\u65e9\u6599\u5230\u4f60\u4eec\u4f7f\u7684\u662f\u82e6\u8089\u4e4b \u8ba1\uff0c\u5374\u6599\u4e0d\u5230\u4f60\u5c45\u7136\u8fd8\u7a7f\u6709\u9632\u8eab\u7684\u5f02\u5b9d\u3002\u6211\u8ba4\u8f93\u4e86\uff0c\u4f60\u8981\u4ec0\u4e48\uff0c\u4f60\u8bf4\u51fa\u6765\u5427\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u628a\u7518\u5927\u4fa0\u4ea4\u51fa\u6765\uff01\u201d\u96cd\u6b63\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u628a\u6715\u7684\u547d\u6362\u7518\u51e4\u6c60\u7684\u547d\uff0c\u4e5f \u8fd8\u503c\u5f97\u3002\u989d\u97f3\u548c\u5e03\uff0c\u5c06\u7518\u51e4\u6c60\u653e\u56de\u7ed9\u4ed6\u3002\u201d\u989d\u97f3\u548c\u5e03\u5e94\u58f0\u9075\u547d\uff0c\u7247\u523b\u4e4b\u540e\u3002\u679c\u7136\u5e26\u8fdb\u6765\u4e00 \u4eba\uff0c\u5934\u624e\u9752\u5dfe\uff0c\u9732\u51fa\u5927\u534a\u8fb9\u9762\u5b54\uff0c\u989d\u97f3\u548c\u5e03\u9053\uff1a\u201c\u7518\u51e4\u6c60\u5934\u53d7\u7bad\u4f24\uff0c\u53c8\u5728\u5fa1\u6cb3\u4e2d\u996e\u4e86\u8bb8\u591a\u51b7 \u6c34\uff0c\u672c\u8be5\u597d\u597d\u5c06\u606f\u3002\u4f60\u8981\u5c06\u4ed6\u5e26\u53bb\uff0c\u82e5\u7136\u6709\u751a\u4e0d\u6d4b\uff0c\u53ef\u4f11\u602a\u6211\u4e0d\u65e9\u8bf4\u77e5\u3002\u201d \u3000\u3000\u5510\u6653\u6f9c\u7559\u795e\u4e00\u77a7\uff0c\u5728\u5bab\u706f\u6620\u7167\u4e4b\u4e0b\uff0c\u53ea\u89c1\u90a3\u4eba\u4e94\u77ed\u8eab\u6750\uff0c\u867d\u7136\u53d7\u4f24\uff0c\u53ef\u662f\u53cc\u76ee\u8fd8\u70af\u70af\u6709 \u795e\uff0c\u679c\u7136\u662f\u7518\u51e4\u6c60\u3002\u5510\u6653\u6f9c\u5524\u9053\uff1a\u201c\u7518\u5927\u54e5\u3002\u201d\u90a3\u4eba\u5e94\u4e86\u4e00\u58f0\u201c\u55ef\uff0c\u5510\u8d24\u5f1f\u3002\u201d\u58f0\u97f3\u5636\u54d1\u5fae \u5f31\uff0c\u5510\u6653\u6f9c\u60f3\u9053\uff1a\u201c\u4ed6\u53d7\u4e86\u91cd\u4f24\uff0c\u53c8\u996e\u4e86\u8bb8\u591a\u51b7\u6c34\uff0c\u602a\u4e0d\u5f97\u5982\u6b64\u6194\u60b4\uff0c\u8fde\u58f0\u97f3\u90fd\u54d1\u4e86\u3002\u201d\u8981 \u5f85\u4e0a\u524d\u68c0\u89c6\u7518\u51e4\u6c60\u6240\u53d7\u7684\u4f24\uff0c\u53c8\u6015\u96cd\u6b63\u4e58\u673a\u9003\u6389\uff0c\u5fc3\u4e2d\u4e00\u60f3\uff0c\u968f\u5373\u8bf4\u9053\uff1a\u201c\u4f60\u628a\u6211\u4eec\u9001\u51fa\u5bab \u53bb\uff0c\u8981\u4ece\u9760\u666f\u5c71\u90a3\u8fb9\u7684\u795e\u6b66\u95e8\u8d70\u51fa\u3002\u6211\u4eec\u4e00\u8d70\u51fa\u95e8\uff0c\u7acb\u523b\u653e\u4f60\u3002\u201d\u96cd\u6b63\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8bdd\u4ed6\u4eec \u53ef\u80af\u4f9d\u4e48\uff1f\u5728\u795e\u6b66\u95e8\u5916\uff0c\u60f3\u5fc5\u6709\u4f60\u4eec\u63a5\u5e94\u7684\u4eba\u4e86\u3002\u4ed6\u4eec\u6b32\u5f97\u6715\u800c\u7518\u5fc3\uff0c\u4f60\u505a\u5f97\u4e86\u4e3b\u4e48\uff1f\u201d\u5510 \u6653\u6f9c\u6012\u9053\uff1a\u201c\u6211\u4eec\u53ef\u4e0d\u50cf\u4f60\uff0c\u4e13\u4f5c\u8bc8\u9a97\u4e4b\u884c\u3002\u541b\u5b50\u4e00\u8a00\u2026\u2026\u201d\u96cd\u6b63\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u5feb\u9a6c\u4e00\u97ad\u3002 \u597d\uff0c\u6715\u4e5f\u66fe\u5728\u6c5f\u6e56\u884c\u8d70\uff0c\u54b1\u4eec\u5c31\u4f9d\u6c5f\u6e56\u4e0a\u7684\u89c4\u77e9\u529e\u4e86\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u4f60\u53eb\u4e00\u4e2a\u5185\u76d1\u6276\u7740\u7518 \u5927\u4fa0\u8d70\u5728\u524d\u5934\uff0c\u4e0d\u51c6\u989d\u97f3\u548c\u5e03\u4ed6\u4eec\u5728\u65c1\u3002\u201d\u96cd\u6b63\u7b11\u9053\uff1a\u201c\u4f60\u771f\u591a\u7591\u3002\u201d\u629b\u4e2a\u773c\u8272\uff0c\u9053\uff1a\u201c\u989d \u97f3\u548c\u5e03\uff0c\u4f60\u8d70\u5f00\u5427\u3002\u201d\u5510\u6653\u6f9c\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u4e0d\u8bb8\u6363\u9b3c\uff0c\u4f60\u82e5\u60f3\u53eb\u989d\u97f3\u548c\u5e03\u5230\u795e\u6b66\u95e8\u5916\u53bb\u641c \u67e5\uff0c\u53bb\u4f24\u5bb3\u6211\u4eec\u7684\u4eba\uff0c\u53ef\u4f11\u602a\u6211\u5251\u4e0b\u65e0\u60c5\u3002\u201d\u96cd\u6b63\u5937\u7136\u81ea\u82e5\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5417\uff1f\u6715\u65e2 \u88ab\u4f60\u631f\u6301\uff0c\u4f60\u672c\u6765\u5c31\u4e0d\u8be5\u518d\u591a\u7591\u4e86\u3002\u201d \u3000\u3000\u5185\u76d1\u6276\u7740\u90a3\u4eba\u8d70\u5728\u524d\u5934\uff0c\u5510\u6653\u6f9c\u5c06\u5b9d\u5251\u67b6\u5728\u96cd\u6b63\u9888\u4e0a\uff0c\u6beb\u4e0d\u653e\u677e\uff0c\u4ece\u5185\u9662\u8d70\u5230\u540e\u9762\u666f\u5c71 \u7684\u795e\u6b66\u95e8\uff0c\u8db3\u8db3\u8d70\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u6cbf\u9014\u679c\u7136\u65e0\u4eba\u641c\u6270\uff0c\u5510\u6653\u6f9c\u60f3\u9053\uff1a\u201c\u5fc5\u662f\u6015\u592a\u76d1\u548c\u536b\u58eb\u89c1 \u7740\uff0c\u4e0d\u597d\u770b\u76f8\uff0c\u6240\u4ee5\u5148\u53eb\u989d\u97f3\u548c\u5e03\u5173\u7167\u4ed6\u4eec\u907f\u5f00\u4e86\u3002\u201d\u63e1\u7d27\u5b9d\u5251\uff0c\u6beb\u65e0\u987e\u8651\u3002 \u3000\u3000\u9014\u4e2d\u7518\u51e4\u6c60\u4e5f\u66fe\u56de\u5934\u77a7\u8fc7\u51e0\u6b21\uff0c\u76ee\u5149\u5145\u6ee1\u611f\u6fc0\uff0c\u5374\u53ea\u662f\u5fae\u5fae\u70b9\u9996\u793a\u610f\uff0c\u5e76\u4e0d\u51fa\u58f0\u3002\u5230\u4e86 \u795e\u6b66\u95e8\u8fb9\uff0c\u5510\u6653\u6f9c\u9053\uff1a\u201c\u7518\u5927\u54e5\uff0c\u4f60\u8fd8\u80fd\u8d70\u8def\u5417\uff1f\u201d\u7518\u51e4\u6c60\u628a\u624b\u4e00\u6325\uff0c\u5c06\u90a3\u4e2a\u5185\u76d1\u6454\u4e86\u4e2a\u7b4b \u6597\uff0c\u5510\u6653\u6f9c\u559c\u9053\uff1a\u201c\u5927\u54e5\u529f\u529b\u771f\u9ad8\uff0c\u53d7\u4e86\u91cd\u4f24\uff0c\u6b66\u529f\u8fd8\u5728\u3002\u201d\u795e\u6b66\u95e8\u6162\u6162\u6253\u5f00\uff0c\u5510\u6653\u6f9c\u9053\uff1a \u201c\u5927\u54e5\u8fc7\u6765\uff0c\u6276\u7740\u6211\u7684\u80a9\u8180\uff0c\u5415\u59d0\u59d0\u5c31\u5728\u666f\u5c71\u4e4b\u4e0a\uff0c\u54b1\u4eec\u51fa\u4e86\u6b64\u95e8\uff0c\u5979\u5c31\u4f1a\u6765\u63a5\u5e94\u4e86\u3002\u201d\u96cd \u6b63\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u8981\u6715\u9001\u4e48\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u51fa\u4e86\u95e8\u518d\u8bf4\u3002\u201d\u7518\u51e4\u6c60\u56de\u8f6c\u4e86\u8eab\uff0c\u6162\u6162\u9760\u8fd1\u5510\u6653 \u6f9c\uff0c\u53cc\u624b\u6276\u7740\u4ed6\u7684\u80a9\u5934\uff0c\u5510\u6653\u6f9c\u5fc3\u4e2d\u9178\u75db\uff0c\u4fa7\u8eab\u5c31\u4ed6\uff0c\u6b63\u60f3\u51fa\u58f0\u6170\u95ee\uff0c\u84e6\u7136\u95f4\u80a9\u5934\u5267\u75db\uff0c\u6301 \u5251\u7684\u624b\u8155\u4e5f\u7ed9\u201c\u7518\u51e4\u6c60\u201d\u4e00\u628a\u62c9\u5f00\uff0c\u5510\u6653\u6f9c\u60ca\u53eb\u9053\uff1a\u201c\u7518\u5927\u54e5\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u8033\u8fb9\u4e00\u58f0\u9739 \u96f3\uff0c\u90a3\u4eba\u559d\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u5927\u54e5\uff01\u201d\u4e00\u624b\u6293\u80a9\uff0c\u4e00\u624b\u6495\u8155\uff0c\u96cd\u6b63\u54c8\u54c8\u5927\u7b11\uff0c\u8131\u51fa\u8eab\u6765\uff0c\u4ed6\u7684 \u7a74\u9053\uff0c\u5c31\u5728\u90a3\u4eba\u9760\u8eab\u4e4b\u9645\uff0c\u7528\u624b\u8098\u4e00\u649e\u65e9\u89e3\u5f00\u4e86\u3002 \u3000\u3000\u8fd9\u4eba\u54ea\u91cc\u662f\u4ec0\u4e48\u7518\u51e4\u6c60\uff0c\u5374\u662f\u5bab\u4e2d\u7684\u4e00\u540d\u536b\u58eb\uff0c\u53ea\u56e0\u8eab\u6750\u957f\u5f97\u9887\u4f3c\u7518\u51e4\u6c60\uff0c\u6240\u4ee5\u96cd\u6b63\u53eb \u4ed6\u4f2a\u88c5\uff0c\u8fd9\u4e43\u662f\u9884\u65e9\u4f0f\u4e0b\u4e4b\u8ba1\uff0c\u5510\u6653\u6f9c\u53ea\u56e0\u4e0d\u80fd\u4ed4\u7ec6\u5bdf\u89c6\uff0c\u6240\u4ee5\u88ab\u4ed6\u9a97\u8fc7\u3002\u201d \u3000\u3000\u5e78\u800c\u8fd9\u540d\u536b\u58eb\u5e76\u975e\u4e00\u6d41\u9ad8\u624b\uff0c\u5510\u6653\u6f9c\u867d\u7136\u88ab\u4ed6\u51fa\u5176\u4e0d\u610f\u6293\u7740\u80a9\u5934\u624b\u8155\uff0c\u5374\u4e5f\u8fd8\u80fd\u6323\u624e\u3002 \u3000\u3000\u96cd\u6b63\u8131\u51fa\u4e86\u8eab\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5feb\u628a\u57ce\u95e8\u5173\u4e0a\uff01\u201d\u989d\u97f3\u548c\u5e03\u3001\u97e9\u91cd\u5c71\u3001\u54c8\u5e03\u9640\u7b49\u4eba\uff0c\u90fd\u4ece \u6697\u9edd\u5904\u8df3\u4e86\u51fa\u6765\uff0c\u539f\u6765\u4ed6\u4eec\u5728\u6b64\u7406\u4f0f\uff0c\u65e9\u5df2\u7b49\u4e86\u591a\u65f6\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u62b1\u7740\u90a3\u4eba\u4f0f\u5730\u4e00\u6eda\uff0c\u7528\u201c\u4e09\u73af\u5957\u6708\u201d\u3001\u201c\u5999\u89e3\u8fde\u73af\u201d\u7684\u62db\u6570\u6323\u8131\u51fa\u6765\uff0c\u5b9d\u5251\u4e00 \u6325\uff0c\u628a\u5047\u7518\u51e4\u6c60\u65a9\u4e3a\u4e24\u6bb5\uff0c\u53ea\u89c1\u54c8\u5e03\u9640\u624b\u821e\u6d41\u661f\u9524\u6253\u5230\uff0c\u800c\u795e\u6b66\u95e8\u7684\u5927\u94c1\u95e8\u53c8\u5df2\u6162\u6162\u5173\u95ed\uff0c \u795e\u6b66\u95e8\u9ad8\u8fbe\u6570\u4e08\uff0c\u5510\u6653\u6f9c\u7684\u7435\u7436\u9aa8\u5df2\u88ab\u90a3\u4eba\u62fc\u6b7b\u529b\u6293\u4f24\uff0c\u8f7b\u529f\u867d\u4ee5\u65bd\u5c55\uff0c\u662f\u518d\u4e5f\u8dc3\u4e0d\u4e0a\u53bb \u3000\u3000\u5510\u6653\u6f9c\u628a\u5fc3\u4e00\u6a2a\uff0c\u8c41\u51fa\u6027\u547d\uff0c\u56de\u5251\u8fce\u654c\uff0c\u5ffd\u542c\u5f97\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u5173\u57ce\u95e8\u7684\u4eba\u4f3c\u662f\u4e2d\u4e86\u6697 \u5668\uff0c\u5012\u4e86\u4e0b\u53bb\uff0c\u989d\u97f3\u548c\u5e03\u4e0e\u97e9\u91cd\u5c71\u6025\u6025\u8dc3\u4e0a\u57ce\u5899\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u6e05\u5578\uff0c\u5415\u56db\u5a18\u4e0e\u51af\u745b\u51af\u7433\u4e5f\u4ece \u5916\u9762\u8df3\u4e0a\u5899\u5934\uff0c\u5173\u4e1c\u56db\u4fa0\u5374\u4ece\u57ce\u95e8\u6740\u5165\u3002\u989d\u97f3\u548c\u5e03\u62c2\u5c18\u4e00\u5c55\uff0c\u6321\u4f4f\u4e86\u5415\u56db\u5a18\u7684\u5b9d\u5251\uff0c\u53eb\u9053\uff1a \u201c\u5feb\u843d\u94c1\u95f8\u3002\u201d\u97e9\u91cd\u5c71\u5de6\u624b\u63d0\u7740\u8f9f\u4e91\u9504\u9632\u8eab\uff0c\u53f3\u624b\u6309\u4e0b\u94c1\u95f8\uff0c\u5173\u4e1c\u56db\u4fa0\u5df2\u62a2\u5165\u5185\u9762\uff0c\u51af\u745b\u51af \u7433\u4e5f\u5954\u5230\u4e86\u5510\u6653\u6f9c\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u5899\u5934\u4e0a\u4e07\u7bad\u9f50\u53d1\uff0c\u56ed\u4e2d\u57cb\u4f0f\u7684\u6b66\u58eb\u7eb7\u7eb7\u6d8c\u4e0a\uff0c\u7bad\u72b9\u7f62\u4e86\uff0c\u6700\u5389\u5bb3\u7684\u662f\u7ea2\u6559\u5587\u561b\u6240\u7528\u7684\u55b7 \u706b\u7b52\uff0c\u7b52\u76d6\u4e00\u542f\uff0c\u4fbf\u662f\u547c\u7684\u4e00\u58f0\uff0c\u4e00\u80a1\u70c8\u7130\u55b7\u5c04\u51fa\u6765\uff0c\u6240\u89e6\u4e4b\u5904\uff0c\u7acb\u5373\u711a\u70e7\uff0c\u8fd9\u4e43\u662f\u96cd\u6b63\u5904 \u5fc3\u79ef\u7d2f\uff0c\u57cb\u4f0f\u4e0b\u7684\uff0c\u4ed6\u7b97\u5b9a\u7fa4\u96c4\u5fc5\u7136\u5192\u9669\u6765\u6551\u7518\u51e4\u6c60\uff0c\u51c6\u5907\u4e00\u7f51\u6210\u64d2\uff0c\u5c06\u4ed6\u4eec\u5168\u90fd\u70e7\u6b7b\u3002 \u3000\u3000\u4f17\u4eba\u5728\u706b\u7130\u4ea4\u53c9\u626b\u5c04\u4e0b\u817e\u632a\u95ea\u8dc3\uff0c\u53c8\u8981\u8eb2\u907f\u5f13\u7bad\uff0c\u5341\u5206\u5371\u9669\u3002\u51af\u745b\u95ee\u9053\uff1a\u201c\u4f60\u8dd1\u5f97\u51fa\u53bb \u5427\u3002\u201d\u5510\u6653\u6f9c\u6447\u4e86\u6447\u5934\u3002\u8fd9\u65f6\u94c1\u95f8\u5c31\u5c06\u843d\u5730\uff0c\u5899\u5934\u4e0a\u53c8\u7ad9\u6ee1\u4e86\u5f13\u7bad\u624b\u548c\u94a9\u9570\u624b\uff0c\u5173\u4e1c\u56db\u4fa0\u4e4b \u4e2d\uff0c\u9664\u4e86\u7384\u98ce\u9053\u4eba\u4e0e\u67f3\u5148\u5f00\u4e4b\u5916\uff0c\u5176\u4ed6\u4e8c\u4eba\u8f7b\u529f\u8f83\u4f4e\uff0c\u4f30\u91cf\u4e5f\u4e0d\u80fd\u8d8a\u5899\u800c\u51fa\u4e86\u3002 \u3000\u3000\u5415\u56db\u5a18\u4e00\u53e3\u5251\u9f99\u86c7\u98de\u821e\uff0c\u628a\u989d\u97f3\u548c\u5e03\u6740\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u51af\u7433\u628a\u624b\u4e00\u626c\uff0c\u4e09\u67c4\u593a\u547d\u795e\u5200 \u9f50\u5411\u97e9\u91cd\u5c71\u5954\u53bb\uff0c\u97e9\u91cd\u5c71\u662f\u63a5\u6697\u5668\u7684\u80fd\u624b\uff0c\u89c1\u4e09\u67c4\u98de\u5200\u7684\u6253\u6cd5\uff0c\u975e\u53cc\u624b\u9f50\u63a5\u4e0d\u884c\uff0c\u53ea\u5f97\u817e\u51fa \u624b\u6765\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u9648\u5143\u9738\u8df3\u5165\u57ce\u95e8\uff0c\u53cc\u81c2\u628a\u94c1\u95e8\u6258\u7740\uff0c\u5411\u4e0a\u529b\u4e3e\uff0c\u9648\u5143\u9738\u6709\u5355\u638c\u5f00\u7891 \u4e4b\u529b\uff0c\u94c1\u95f8\u867d\u7136\u91cd\u903e\u5343\u65a4\uff0c\u7adf\u81ea\u7ed9\u4ed6\u6162\u6162\u7684\u5411\u4e0a\u6258\u8d77\u3002\u7384\u98ce\u4e0e\u51af\u745b\u6717\u6708\u7b49\u4eba\u6740\u9000\u8fd1\u8eab\u536b\u58eb\uff0c \u4e00\u9f50\u6d8c\u51fa\u3002\u51af\u7433\u5219\u4e0d\u505c\u624b\u7684\u53d1\u51fa\u98de\u5200\uff0c\u53eb\u97e9\u91cd\u5c71\u4e0d\u80fd\u518d\u6309\u94c1\u95f8\u3002 \u3000\u3000\u53ef\u662f\u51af\u7433\u968f\u8eab\u6240\u5e26\u7684\u98de\u5200\u53ea\u6709\u4e8c\u5341\u56db\u628a\uff0c\u5510\u6653\u6f9c\u4e0e\u51af\u745b\u8d70\u5728\u6700\u540e\uff0c\u5230\u795e\u6b66\u95e8\u8fb9\uff0c\u51af\u7433\u7684 \u98de\u5200\u5df2\u7ecf\u53d1\u5b8c\u3002\u97e9\u91cd\u5c71\u78d4\u78d4\u602a\u7b11\uff0c\u628a\u624b\u4e00\u626c\uff0c\u201c\u545c\u545c\u201d\u58f0\u54cd\uff0c\u4e24\u67c4\u201c\u56de\u73af\u94a9\u201d\u540c\u65f6\u5c04\u51fa\uff0c\u5206 \u53d6\u51af\u745b\u4e0e\u5510\u6653\u6f9c\uff0c\u56de\u73af\u94a9\u80fd\u56de\u7fd4\u8f6c\u6298\uff0c\u5389\u5bb3\u975e\u5e38\uff0c\u51af\u5510\u4e8c\u4eba\u88ab\u8fd9\u6697\u5668\u903c\u5f97\u9000\u8eab\u95ea\u907f\uff0c\u5c31\u5728\u8fd9 \u4e00\u5239\u90a3\uff0c\u97e9\u91cd\u5c71\u529b\u6309\u94c1\u95f8\uff0c\u9648\u5143\u9738\u989d\u73b0\u7ea2\u7b4b\uff0c\u5927\u53eb\uff1a\u201c\u5feb\u95ef\uff01\u201d\u51af\u745b\u4e0e\u5510\u6653\u6f9c\u53cc\u5251\u4ea4\u53c9\u4e00 \u7ede\uff0c\u90a3\u56de\u73af\u94a9\u98de\u4e0d\u51fa\u53bb\uff0c\u7acb\u88ab\u7ede\u4e3a\u56db\u622a\u3002\u4e24\u4eba\u8eab\u5f62\u75be\u8d77\uff0c\u4ece\u9648\u5143\u9738\u8eab\u8fb9\u63a0\u51fa\uff0c\u53ea\u542c\u5f97\u8f70\u9686\u4e00 \u58f0\uff0c\u7d27\u63a5\u7740\u4e24\u58f0\u60e8\u53eb\uff0c\u5510\u6653\u6f9c\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u5415\u56db\u5a18\u633d\u7740\u4e00\u4e2a\u4eba\u5934\uff0c\u5954\u5230\u8eab\u540e\uff0c\u8fde\u58f0\u53eb\u9053\uff1a \u201c\u5feb\u8d70\uff0c\u5feb\u8d70\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5927\u5185\u98de\u5934\u96be\u89e3\u6068\uff0c\u96c1\u884c\u6298\u7ffc\u6700\u4f24\u5fc3\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b\u2014\u2014\u7b2c\u56db\u5341\u516b\u56de\u3000\u4e09\u5973\u5c60\u9f99\u3000\u7ec8\u987b\u6d88\u5927\u6068\u3000\u4e00\u756a\u9010\u9e7f\u3000\u5404\u81ea\u7f14\u826f\u7f18 \u6881\u7fbd\u751f\u300a\u6c5f\u6e56\u4e09\u5973\u4fa0\u300b \u7b2c\u56db\u5341\u516b\u56de \u4e09\u5973\u5c60\u9f99\u3000\u7ec8\u987b\u6d88\u5927\u6068 \u4e00\u756a\u9010\u9e7f\u3000\u5404\u81ea\u7f14\u826f\u7f18 \u3000\u3000\u4f17\u4eba\u6740\u9000\u536b\u58eb\uff0c\u8d8a\u8fc7\u666f\u5c71\uff0c\u98ce\u9a70\u7535\u64ce\u7684\u5954\u51fa\u5317\u4eac\u90ca\u5916\uff0c\u5728\u6b8b\u661f\u660e\u706d\u3001\u6653\u8272\u6726\u80e7\u4e4b\u9645\uff0c\u5df2 \u5230\u4e86\u897f\u5c71\u9ad8\u5904\uff0c\u6b47\u4e86\u4e0b\u6765\uff0c\u4f17\u4eba\u624d\u770b\u6e05\u695a\u5415\u56db\u5a18\u624b\u4e0a\u63d0\u7684\u5934\u9885\u4e43\u662f\u97e9\u91cd\u5c71\u3002\u7384\u98ce\u4ee5\u62d0\u51fb\u77f3\uff0c \u8001\u6cea\u6f78\u6d41\uff0c\u54ed\u4e0d\u6210\u58f0\uff0c\u5415\u56db\u5a18\u4e5f\u9eef\u7136\u65e0\u8bed\u3002\u67f3\u5148\u5f00\u54ed\u9053\uff1a\u201c\u53ef\u60dc\u4e86\u6211\u90a3\u56db\u5f1f\uff0c\u867d\u7136\u6740\u4e86\u8fd9 \u53ae\uff0c\u4e5f\u4e0d\u8db3\u89e3\u6068\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u6068\u53ea\u6068\u6211\u8fdf\u4e86\u4e00\u6b65\u3002\u201d\u5510\u6653\u6f9c\u66f4\u662f\u6028\u6068\u81ea\u5df1\uff0c\u9053\uff1a\u201c\u82e5\u975e \u6211\u53d7\u4e86\u4f24\uff0c\u9648\u4fa0\u58eb\u4e5f\u4e0d\u4f1a\u4ee5\u8840\u8089\u4e4b\u8eaf\uff0c\u53bb\u6258\u90a3\u5343\u65a4\u94c1\u95f8\u3002\u201d\u6717\u6708\u7985\u5e08\u9053\uff1a\u201c\u5143\u9738\u56db\u5f1f\u820d\u751f\u53d6 \u4e49\uff0c\u4e5f\u4e0d\u6789\u4fa0\u5ba2\u4e4b\u540d\u3002\u54b1\u4eec\u529b\u6297\u6e05\u5ef7\uff0c\u6709\u4eba\u9047\u96be\u5728\u6240\u4e0d\u514d\uff0c\u54b1\u4eec\u8fd8\u662f\u60f3\u6cd5\u66ff\u4ed6\u62a5\u4ec7\u5427\u3002\u201d \u3000\u3000\u539f\u6765\u9648\u5143\u9738\u867d\u7136\u662f\u5929\u751f\u795e\u529b\uff0c\u4f46\u88ab\u97e9\u91cd\u5c71\u529b\u6309\u94c1\u95f8\uff0c\u7ec8\u4e8e\u652f\u6301\u4e0d\u4f4f\uff01\u5c31\u5728\u5510\u6653\u6f9c\u5954\u51fa\u795e \u6b66\u95e8\u4e4b\u9645\uff0c\u7ed9\u94c1\u95f8\u95f8\u4e3a\u4e24\u6bb5\u3002 \u3000\u3000\u5510\u6653\u6f9c\u9053\uff1a\u201c\u96cd\u6b63\u8fd9\u53ae\u771f\u662f\u9634\u9669\u6076\u6bd2\uff0c\u9648\u4fa0\u571f\u906d\u4ed6\u6bd2\u624b\uff0c\u7518\u5927\u4fa0\u53c8\u662f\u751f\u6b7b\u83ab\u6d4b\uff0c\u8fd9\u4e2a\u5927 \u4ec7\u4e0d\u77e5\u4f55\u65e5\u624d\u80fd\u62a5\u3002\u201d\u5415\u56db\u5a18\u6536\u4e86\u773c\u6cea\uff0c\u6479\u5730\u5411\u5929\u957f\u5578\uff0c\u5c71\u4e2d\u6df1\u5904\uff0c\u968f\u5373\u53d1\u51fa\u545c\u545c\u54cd\u7bad\u4e4b \u58f0\uff0c\u4e00\u957f\u4e8c\u77ed\uff0c\u5510\u6653\u6f9c\u8ba4\u5f97\u8fd9\u662f\u5415\u56db\u5a18\u540c\u95e8\u7684\u4fe1\u53f7\uff0c\u95ee\u9053\uff1a\u201c\u767d\u6cf0\u5b98\u5728\u8fd9\u91cc\u4e48\uff1f\u201d\u5415\u56db\u5a18 \u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u5728\u8fd9\u91cc\u3002\u4e03\u54e5\u6628\u65e5\u9ec4\u660f\uff0c\u5df2\u662f\u8131\u9669\u5f52\u6765\uff0c\u867d\u7136\u53d7\u4f24\u4e0d\u8f7b\uff0c\u5374\u65e0\u5927\u788d\u3002\u201d\u5510\u6653\u6f9c \u60b2\u75db\u4e4b\u4e2d\uff0c\u95fb\u6b64\u559c\u8baf\uff0c\u4e0d\u89c9\u8df3\u8d77\u6765\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d\u4ed6\u66fe\u773c\u89c1\u7518\u51e4\u6c60\u6454\u4e0b\u5fa1\u6cb3\uff0c\u53c8\u773c\u89c1\u989d\u97f3\u548c \u5e03\u4ece\u7545\u97f3\u9601\u4e2d\u98de\u8eab\u800c\u51fa\uff0c\u4e0d\u4fe1\u7518\u51e4\u6c60\u80fd\u5728\u4e2d\u4e86\u673a\u5173\u57cb\u4f0f\uff0c\u9047\u5230\u989d\u97f3\u548c\u5e03\u8fd9\u6837\u7684\u5f3a\u654c\u6697\u88ad\u4e4b \u4e0b\uff0c\u5c45\u7136\u8fd8\u80fd\u591f\u9003\u51fa\u6027\u547d\u3002 \u3000\u3000\u5415\u56db\u5a18\u7ea4\u624b\u4e00\u6307\uff0c\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u770b\u3002\u201d\u53ea\u89c1\u5c71\u8170\u8305\u8349\uff0c\u65e0\u98ce\u81ea\u5f00\uff0c\u539f\u6765\u6709\u51e0\u4e2a\u4eba\u85cf\u5728\u91cc \u9762\uff0c\u5982\u4eca\u73b0\u51fa\u8eab\u6765\uff0c\u53ef\u4e0d\u6b63\u662f\u7518\u51e4\u6c60\u3001\u767d\u6cf0\u5b98\u4ed6\u4eec\uff1f \u3000\u3000\u4f17\u4eba\u805a\u4f1a\uff0c\u5510\u6653\u6f9c\u542c\u4ed6\u4eec\u8c08\u8bdd\uff0c\u65b9\u77e5\u7ecf\u8fc7\uff0c\u539f\u6765\u7518\u51e4\u6c60\u8eab\u7ecf\u767e\u6218\uff0c\u673a\u8b66\u975e\u5e38\uff0c\u90a3\u65e5\u4e00\u8e0f \u5165\u7545\u97f3\u9601\u4fbf\u77e5\u6709\u5f02\uff0c\u7acb\u5373\u7528\u638c\u529b\u9707\u584c\u4e00\u89d2\uff0c\u9976\u662f\u5982\u6b64\uff0c\u8eab\u4e0a\u8fd8\u662f\u53d7\u4e86\u51e0\u5904\u7bad\u4f24\uff0c\u540e\u5fc3\u4e5f\u4e2d\u4e86 \u989d\u97f3\u548c\u5e03\u4e00\u638c\u3002 \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u989d\u97f3\u548c\u5e03\u7684\u638c\u529b\u975e\u540c\u5c0f\u53ef\uff0c\u6211\u5403\u4e86\u4e00\u638c\uff0c\u53ea\u89c9\u773c\u524d\u4e00\u7247\u6f06\u9ed1\uff0c\u51e0\u4e4e\u7ed9\u4ed6\u6253 \u6655\uff0c\u6454\u4e0b\u5fa1\u6cb3\u4e4b\u540e\uff0c\u51b7\u6c34\u4e00\u6d78\uff0c\u53cd\u6e05\u9192\u8fc7\u6765\u3002\u5e78\u800c\u6ca1\u6709\u4eba\u4e0b\u6c34\u6765\u8ffd\u3002\u201d\u9c7c\u4eae\u9053\uff1a\u201c\u90a3\u65f6\u6211\u4eec \u5df2\u7ecf\u5728\u56ed\u4e2d\u6df7\u6218\u4e86\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u63a5\u7740\u9053\uff1a\u201c\u6211\u751f\u957f\u6c5f\u5357\u6c34\u4e61\uff0c\u672c\u6765\u901a\u6653\u6c34\u6027\uff0c\u53ef\u662f\u9aa8\u75db\u6b32\u88c2\uff0c\u65e0\u529b\u6e38\u51fa\uff0c\u4e5f\u662f\u547d \u4e0d\u8be5\u7edd\uff0c\u6211\u8eab\u4e0a\u5e26\u6709\u51b7\u7985\u4ee5\u524d\u9001\u7ed9\u6211\u7684\u957f\u767d\u5c71\u8001\u53c2\uff0c\u672c\u662f\u5e26\u5728\u8eab\u8fb9\uff0c\u51c6\u5907\u6551\u4eba\u7684\uff0c\u6070\u597d\u7528\u5f97 \u7740\uff0c\u6211\u56bc\u4e86\u4e00\u679d\u4eba\u53c2\uff0c\u7d22\u6027\u853d\u5728\u82a6\u82c7\u4e1b\u4e2d\u6c34\u6d45\u4e4b\u5904\uff0c\u8fd0\u6c14\u884c\u8840\uff0c\u81ea\u5df1\u7597\u4f24\u3002\u8fc7\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c \u6c14\u529b\u867d\u7136\u672a\u80fd\u5b8c\u5168\u6062\u590d\uff0c\u4f46\u5374\u53ef\u4ee5\u5728\u6c34\u4e2d\u6e38\u52a8\u4e86\u3002\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u5fa1\u6cb3\u6c34\u9053\u901a\u5230\u5916\u9762\u5417\uff1f\u6c34 \u5e95\u4e0b\u96be\u9053\u6ca1\u6709\u963b\u62e6\uff0c\u4f60\u600e\u4e48\u6e38\u5f97\u51fa\u53bb\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u5e78\u4e8f\u4e00\u4e2a\u5bab\u5973\u6307\u70b9\u3002\u201d\u5510\u6653\u6f9c\u8be7\u9053\uff1a \u201c\u5bab\u5973\u6709\u8fd9\u6837\u5927\u7684\u672c\u4e8b\uff0c\u80fd\u591f\u4e0b\u6c34\u6551\u4f60\uff1f\u201d \u3000\u3000\u7518\u51e4\u6c60\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u5979\u6551\u6211\uff0c\u662f\u6211\u6551\u5979\u3002\u5979\u4e00\u70b9\u672c\u9886\u90fd\u6ca1\u6709\uff0c\u800c\u4e14\uff0c\u5f53\u6211\u53d1\u73b0\u5979\u65f6\uff0c\u5979 \u5df2\u7ecf\u662f\u5feb\u8981\u534a\u6b7b\u7684\u4eba\u4e86\u3002\u201d\u5510\u6653\u6f9c\u5947\u9053\uff1a\u201c\u90a3\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u7518\u51e4\u6c60\u9053\uff1a\u201c\u4f60\u522b\u5fc3\u6025\uff0c\u542c\u6211 \u9053\u6765\u3002\u6211\u672c\u60f3\u6f5c\u6c34\u51fa\u53bb\uff0c\u4f46\u6e38\u5230\u5916\u9762\uff0c\u5374\u89c1\u6c34\u5e95\u5e03\u4e86\u5341\u51e0\u91cd\u94c1\u7f51\uff0c\u6211\u77e5\u9053\u5185\u4e2d\u5fc5\u7136\u85cf\u6709\u673a \u5173\uff0c\u89e6\u52a8\u4e0d\u5f97\uff0c\u6b63\u5728\u5fc3\u6025\uff0c\u5ffd\u89c1\u4e00\u6761\u6b7b\u5c38\uff0c\u6f02\u6d41\u8fc7\u6765\uff0c\u6211\u6e38\u8fc7\u53bb\u4e00\u770b\uff0c\u53ea\u89c1\u662f\u4e00\u4e2a\u5e74\u7eaa\u5df2\u8001 \u7684\u5bab\u5973\uff0c\u6211\u4ee5\u4e3a\u5979\u662f\u5931\u8db3\u843d\u6c34\u7684\uff0c\u628a\u5979\u6258\u8d77\uff0c\u5bdf\u89c9\u5979\u5fc3\u5934\u5c1a\u6696\uff0c\u4fbf\u7528\u63a8\u8840\u8fc7\u5bab\u4e4b\u672f\uff0c\u52a9\u5979\u547c \u5438\uff0c\u5979\u82cf\u9192\u8fc7\u6765\uff0c\u521d\u65f6\u8fd8\u4ee5\u4e3a\u6211\u662f\u5bab\u4e2d\u536b\u58eb\uff0c\u60ca\u614c\u4e4b\u6781\uff0c\u6c42\u6211\u8d50\u5979\u2018\u5168\u5c38\u2019\uff0c\u6211\u5c06\u8eab\u4efd\u544a\u8bc9 \u5979\uff0c\u53eb\u5979\u4e0d\u8981\u5bb3\u6015\u3002\u95ee\u5979\u56e0\u4f55\u843d\u6c34\u3002\u539f\u6765\u5979\u5165\u5bab\u5df2\u7ecf\u4e8c\u5341\u591a\u5e74\uff0c\u8fd8\u672a\u66fe\u89c1\u8fc7\u7687\u5e1d\u3002\u201d\u7384\u98ce \u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\uff1f\u201d\u5415\u56db\u5a18\u9053\u3002\u201d\u675c\u7267\u7684\u963f\u623f\u5bab\u8d4b\uff0c\u5199\u79e6\u5bab\u7f8e\u5973\u4e4b\u591a\uff0c\u8bf4\u9053\uff1a\u2018\u6709\u4e0d\u5f97\u89c1 \u8005\uff1a\u4e09\u5341\u516d\u5e74\u3002\u201d\u5979\u4e8c\u5341\u5e74\u89c1\u4e0d\u5230\u7687\u5e1d\uff0c\u8fd8\u7b97\u662f\u597d\u7684\u4e86\u3002\u7687\u5bab\u6bbf\u5b87\u8fde\u4e91\uff0c\u5bab\u5a25\u53c8\u662f\u5982\u6b64\u4e4b \u591a\uff0c\u600e\u80fd\u90fd\u89c1\u5230\u7687\u5e1d\u3002\u201d \u3000\u3000\u7518\u51e4\u6c60\u9053\uff1a\u201c\u8fd9\u4e2a\u5bab\u5a25\u5df2\u56db\u5341\u591a\u5c81\uff0c\u7167\u6e05\u5bab\u65e7\u4f8b\uff0c\u672c\u5c31\u65e9\u8be5\u9063\u6563\u51fa\u53bb\uff0c\u8ba9\u5979\u81ea\u884c\u62e9\u914d\uff0c \u53ef\u662f\u5979\u6ca1\u94b1\u7ed9\u7ba1\u4e8b\u7684\u592a\u76d1\uff0c\u4fbf\u6ca1\u4eba\u7406\u5979\uff0c\u8ba9\u5979\u81ea\u751f\u81ea\u706d\u3002\u5979\u5e74\u7eaa\u5df2\u5927\uff0c\u88ab\u6d3e\u5728\u5bab\u4e2d\u6267\u5f79\uff0c\u65f6 \u5e38\u906d\u53d7\u6253\u9a82\uff0c\u53d7\u82e6\u4e0d\u8fc7\uff0c\u6545\u6b64\u6295\u6c34\u81ea\u6740\u3002\u6211\u6551\u4e86\u5979\u540e\uff0c\u95ee\u5979\u53ef\u6709\u4ec0\u4e48\u529e\u6cd5\u51fa\u53bb\uff0c\u5979\u5ffd\u7136\u60f3\u8d77 \u4e8c\u5341\u5e74\u524d\uff0c\u5f53\u5979\u8fd8\u662f\u5e74\u8f7b\u8c8c\u7f8e\u4e4b\u65f6\uff0c\u66fe\u548c\u4e00\u4e2a\u5c0f\u592a\u76d1\u5f88\u597d\u3002\u5bab\u4e2d\u7ba1\u7406\u5fa1\u6cb3\u7684\u8bbe\u6709\u4e13\u4eba\uff0c\u90a3\u5c0f \u592a\u76d1\u4fbf\u662f\u5728\u6e05\u7406\u5fa1\u6cb3\u9053\u5904\u6267\u5f79\u7684\u3002\u5979\u8fd8\u8bb0\u5f97\u90a3\u5c0f\u592a\u76d1\u66fe\u7ecf\u544a\u8bc9\u5979\u7684\u4e00\u4ef6\u4e8b\uff0c\u8bf4\u662f\u5fa1\u6cb3\u4e2d\u6709\u4e00 \u5904\u5f15\u6d3b\u6c34\u8fdb\u6765\u7684\uff0c\u5e95\u4e0b\u7559\u6709\u4e2a\u7f3a\u53e3\uff0c\u6ca1\u6709\u94c1\u7f51\u62e6\u963b\uff0c\u53ea\u6709\u94c1\u95f8\u5f00\u5173\uff0c\u94c1\u95f8\u6bcf\u65e5\u6e05\u6668\u5f00\u4e00\u6b21\uff0c \u4ed6\u4eec\u66fe\u6109\u5077\u4ece\u90a3\u91cc\u6e9c\u51fa\u5bab\u5916\u6e38\u73a9\uff0c\u53ea\u4e0d\u77e5\u73b0\u5728\u8fd8\u662f\u4e0d\u662f\u8fd9\u6837\u3002\u6211\u4eec\u59d1\u4e14\u4e00\u8bd5\uff0c\u6211\u6258\u7740\u5979\u6e38\u5230 \u90a3\u91cc\uff0c\u6f5c\u4f0f\u7b49\u5019\uff0c\u5230\u4e86\u65f6\u523b\uff0c\u4fbf\u6f5c\u4e0b\u6c34\u5e95\uff0c\u679c\u7136\u94c1\u95f8\u4f9d\u65f6\u5f00\u5173\uff0c\u6211\u4eec\u4fbf\u8f7b\u6613\u9003\u4e86\u51fa\u6765\u3002\u6211\u8d81 \u7740\u5929\u8272\u8fd8\u672a\u5927\u4eae\uff0c\u5230\u4e00\u5bb6\u5bcc\u6237\uff0c\u5077\u4e86\u4e00\u5957\u8863\u670d\uff0c\u53c8\u5077\u4e86\u4e00\u4e9b\u94f6\u5b50\u7ed9\u5979\uff0c\u8ba9\u5979\u81ea\u5df1\u9003\u751f\u3002\u4ee5\u540e \u7684\u4e8b\uff0c\u516b\u59b9\u90fd\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5415\u56db\u5a18\u9053\uff1a\u201c\u540e\u6765\u4e03\u54e5\u627e\u5230\u6211\u4eec\uff0c\u4ed6\u4f24\u52bf\u867d\u65e0\u5927\u788d\uff0c\u4f46\u5143\u6c14\u5927\u4f24\uff0c\u6b66\u529f\u672a\u590d\uff0c\u56e0\u6b64\u6211\u53eb \u4e94\u54e5\u4ed6\u4eec\u5148\u4f34\u4ed6\u5230\u897f\u5c71\uff0c\u7136\u540e\u8d76\u5230\u5bab\u4e2d\u6551\u4f60\u3002\u201d \u3000\u3000\u51af\u7433\u542c\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u5ffd\u7136\u62cd\u624b\u7b11\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6211\u4eec\u4ece\u90a3\u513f\u6f5c\u5165\uff0c\u5c82\u4e0d\u662f\u597d\uff1f\u201d\u5415\u56db\u5a18\u6447 \u6447\u5934\u9053\uff1a\u201c\u96cd\u6b63\u4f55\u7b49\u5389\u5bb3\uff01\u4ed6\u53d1\u73b0\u7518\u4e03\u54e5\u5728\u5fa1\u6cb3\u4e2d\u5931\u8e2a\uff0c\u4e0d\u628a\u5fa1\u6cb3\u7ffb\u4e2a\u5e95\u624d\u602a\uff0c\u8fd9\u4e2a\u6f0f\u606b\u4e00 \u5b9a\u7ed9\u4ed6\u53d1\u89c9\u8865\u597d\u4e86\u3002\u800c\u4e14\u5c31\u7b97\u4eba\u5230\u91cc\u9762\uff0c\u4e5f\u4e0d\u77e5\u96cd\u6b63\u85cf\u5728\u4f55\u5904\u3002\u6211\u4eec\u53c8\u4e0d\u80fd\u957f\u4f4f\u5bab\u4e2d\uff0c\u7b49\u5019 \u673a\u4f1a\uff0c\u53ea\u8fd9\u6837\u5077\u6109\u8fdb\u53bb\u4e00\u4e24\u6b21\uff0c\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u51af\u7433\u5583\u5583\u8bf4\u9053\uff1a\u201c\u4e0d\u80fd\u5728\u5bab\u4e2d\u4e45\u4f4f\u3002\u201d\u53c8\u541f\u9053\uff1a\u201c\u6709\u4e0d\u5f97\u89c1\u8005\u4e09\u5341\u516d\u5e74\u3002\u6709\u4e86\uff0c\u6709 \u4e86\uff01\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u4f60\u8fd9\u9b3c\u7075\u7cbe\uff0c\u53c8\u6709\u4ec0\u4e48\u9b3c\u4e3b\u610f\u4e86\uff1f\u201d\u51af\u7433\u8bf4\u9053\uff1a\u201c\u5929\u673a\u4e0d\u53ef\u6cc4\u9732\uff0c\u6211\u4ece \u90a3\u4e2a\u5bab\u5a25\u7684\u4e8b\uff0c\u60f3\u5230\u4e86\u4e00\u4e2a\u5999\u6cd5\uff0c\u4f60\u9644\u8033\u8fc7\u6765\u3002\u201d\u5415\u56db\u5a18\u542c\u5979\u5728\u8033\u8fb9\u6084\u6084\u7684\u8bf4\uff0c\u5148\u662f\u2018\u5478\u2019 \u4e86\u4e00\u58f0\uff0c\u7ee7\u800c\u53c8\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u5c0f\u9b3c\u5934\u6253\u7684\u9b3c\u4e3b\u610f\u4e5f\u8fd8\u4e0d\u9519\u3002\u201d\u9762\u9732\u7b11\u5bb9\uff0c\u628a\u4f17\u4eba\u5f04\u5f97 \u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u96cd\u6b63\u7ecf\u4e86\u8fd9\u4e00\u58f0\u5927\u95f9\u4e4b\u540e\uff0c\u5fc3\u80c6\u4ff1\u5bd2\uff0c\u540e\u6765\u542c\u5f97\u4e5d\u95e8\u63d0\u7763\u62a5\u9053\uff0c\u8bf4\u662f\u5415\u56db\u5a18\u8fd9\u4e00\u5e2e\u4eba\uff0c \u5df2\u7ecf\u51b2\u51fa\u57ce\u5916\uff0c\u8fd9\u624d\u7a0d\u7a0d\u653e\u5fc3\uff0c\u4f46\u5bab\u4e2d\u4ecd\u662f\u6212\u5907\u4e0d\u61c8\u3002 \u3000\u3000\u5306\u5306\u8fc7\u4e86\u534a\u5e74\uff0c\u5b81\u9759\u65e0\u4e8b\uff0c\u96cd\u6b63\u5fc3\u9053\uff1a\u60f3\u662f\u8fd9\u5e2e\u4eba\u77e5\u9053\u5389\u5bb3\uff0c\u4e0d\u6562\u6765\u4e86\u3002\u6715\u8d35\u4e3a\u5929\u5b50\uff0c \u5bcc\u6709\u56db\u6d77\uff0c\u5374\u56e0\u5bb3\u6015\u523a\u5ba2\uff0c\u4e0d\u6562\u5bfb\u6b22\u4f5c\u4e50\uff0c\u8fde\u5728\u5bab\u4e2d\u4e5f\u4e0d\u6562\u968f\u4fbf\u8d70\u52a8\uff0c\u505a\u8fd9\u7687\u5e1d\uff0c\u4e5f\u6ca1\u6709\u4ec0 \u4e48\u610f\u601d\u3002\u89c1\u65e5\u4e45\u65e0\u4e8b\uff0c\u4fbf\u6e10\u6e10\u6d3b\u52a8\u8d77\u6765\uff0c\u5230\u5404\u5983\u5ad4\u5185\u9662\u8d70\u8d70\u3002 \u3000\u3000\u6e05\u5bab\u65e7\u4f8b\uff0c\u6bcf\u4e09\u5e74\u66f4\u6362\u4e00\u6279\u5bab\u5a25\uff0c\u5c06\u65b0\u7684\u8865\u8fdb\u6765\uff0c\u5c06\u65e7\u7684\u9063\u51fa\u53bb\uff0c\u8fd9\u4fbf\u662f\u4e09\u5e74\u6311\u9009\u4e00\u6b21 \u201c\u79c0\u5973\u201d\u7684\u7531\u6765\u3002\u201c\u79c0\u5973\u201d\u6311\u9009\u8fdb\u5bab\u4e4b\u540e\uff0c\u62d4\u7ed9\u5404\u5ad4\u5983\u4f7f\u7528\uff0c\u79f0\u4e3a\u201c\u5b98\u5a25\u201d\uff0c\u82e5\u7136\u7687\u5e1d\u89c1 \u7740\uff0c\u89c9\u5f97\u5408\u610f\u8fd9\u624d\u8d50\u8d4f\u5c01\u53f7\uff0c\u79f0\u4e3a\u201c\u8d35\u4eba\u201d\uff0c\u201c\u8d35\u4eba\u201d\u5f97\u5ba0\uff0c\u518d\u201c\u5347\u201d\u4e3a\u201c\u8d35\u5983\u201d\uff0c\u4f46\u5bab\u4e2d \u5bab\u5a25\u65e0\u6570\uff0c\u54ea\u91cc\u80fd\u4e00\u4e00\u89c1\u5230\u7687\u5e1d\u3002 \u3000\u3000\u4e00\u65e5\uff0c\u96cd\u6b63\u95f2\u7740\u65e0\u4e8b\uff0c\u60f3\u8d77\u4e09\u6708\u4e4b\u524d\uff0c\u66fe\u4ece\u5404\u5730\u6311\u9009\u4e86\u4e00\u6279\u79c0\u5973\uff0c\u4e0d\u77e5\u5176\u4e2d\u53ef\u6709\u597d\u7684\u6ca1 \u6709\u3002\u4fbf\u53eb\u5185\u76d1\u5c06\u79c0\u5973\u7684\u540d\u518c\u548c\u753b\u56fe\uff08\u6bcf\u4e00\u79c0\u5973\u9644\u6709\u4e00\u5f20\u753b\u56fe\uff0c\u4ee5\u4fbf\u7687\u5e1d\u6309\u56fe\u7d22\u9aa5\uff0c\u6240\u4ee5\u5e38\u6709 \u79c0\u5973\u8d3f\u8d42\u753b\u5de5\uff0c\u5e0c\u671b\u5c06\u5979\u7684\u76f8\u8c8c\u753b\u5f97\u597d\u4e9b\u7684\u4e8b\uff09\u62ff\u6765\uff0c\u968f\u4fbf\u7ffb\u7ffb\uff0c\u5ffd\u89c1\u5176\u4e2d\u4e00\u540d\u79c0\u5973\uff0c\u76f8\u8c8c \u9887\u4f3c\u51af\u7433\uff0c\u5fc3\u4e2d\u4e00\u8df3\uff0c\u518d\u7ec6\u770b\u65f6\uff0c\u89c1\u5217\u6709\u8be6\u7ec6\u7684\u59d3\u540d\u7c4d\u8d2f\uff0c\u4e43\u662f\u5357\u660c\u4e00\u5bb6\u666e\u901a\u4eba\u5bb6\u7684\u5973\u513f\uff0c \u5524\u4f5c\u6797\u82b7\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u6697\u7b11\uff1a\u201c\u79c0\u5973\u201d\u7531\u5dde\u53bf\u9009\u62d4\uff0c\u518d\u7ecf\u94a6\u5dee\u9a8c\u6536\uff0c\u6700\u540e\u8fd8\u8981\u7ecf\u5bab\u4e2d\u7684\u5185\u52a1\u603b \u7ba1\u5904\u6838\u5bf9\u65e0\u8bef\uff0c\u8fd9\u624d\u653e\u8fdb\u5bab\u4e2d\uff0c\u54ea\u80fd\u6709\u5047\uff01\u800c\u4e14\u8fd9\u540d\u79c0\u5973\uff0c\u867d\u7136\u9762\u8c8c\u6709\u4e9b\u76f8\u4f3c\uff0c\u5374\u53c8\u90a3\u80fd\u53ca \u5f97\u51af\u7433\u7684\u56fd\u8272\u5929\u59ff\uff1f\u60f3\u662f\u6715\u5fc3\u6709\u6240\u601d\uff0c\u4ee5\u81f3\u7591\u795e\u7591\u9b3c\u3002\u96cd\u6b63\u5bf9\u753b\u6c89\u541f\uff0c\u89e6\u8d77\u5f53\u5e74\u4e4b\u4e8b\uff0c\u51af\u7433 \u5a07\u61a8\u7684\u6837\u5b50\uff0c\u5982\u5728\u76ee\u524d\uff0c\u4e0d\u89c9\u53f9\u53e3\u6c14\u9053\uff1a\u8fd9\u6837\u7684\u4e00\u4e2a\u4eba\u95f4\u5c11\u6709\u7684\u7f8e\u4eba\u513f\uff0c\u53ef\u60dc\u4e0e\u8054\u4f5c\u5bf9\u3002\u518d \u770b\u4e00\u770b\u90a3\u5524\u4f5c\u2018\u6797\u82b7\u2019\u7684\u753b\u56fe\uff0c\u89c1\u4e0b\u9762\u6ce8\u7740\uff1a\u53d1\u7ed9\u7fe0\u534e\u5bab\u5218\u8d35\u4eba\u4f7f\u7528\u3002\u96cd\u6b63\u6c89\u541f\u534a\u664c\uff0c\u63a9\u4e86 \u753b\u56fe\uff0c\u53eb\u5185\u76d1\u5c06\u54c8\u5e03\u9640\u5524\u6765\uff0c\u5e26\u7740\u4ed6\u4e00\u540c\u8d70\u53bb\u3002 \u3000\u3000\u5983\u5ad4\u6240\u5728\u7684\u5730\u65b9\uff0c\u79f0\u4e3a\u201c\u7981\u82d1\u201d\uff0c\u5bab\u4e2d\u7684\u536b\u58eb\u53ea\u80fd\u5728\u5916\u9762\u5b88\u536b\uff0c\u82e5\u975e\u7279\u522b\u5949\u5230\u7687\u5e1d\u4e4b \u547d\uff0c\u4e0d\u80fd\u5165\u5185\u3002\u96cd\u6b63\u53eb\u54c8\u5e03\u9640\u5728\u7fe0\u534e\u5bab\u5916\u7b49\u5019\uff0c\u81ea\u5df1\u8d70\u8fdb\u5bab\u4e2d\u3002 \u3000\u3000\u7fe0\u534e\u5bab\u662f\u96cd\u6b63\u767b\u4f4d\u4e4b\u540e\u6539\u5efa\u8fc7\u7684\uff0c\u5bab\u5899\u5185\u82b1\u6728\u6276\u758f\uff0c\u8fd8\u6709\u4e00\u5927\u7247\u8377\u5858\u5305\u5728\u5bab\u5899\u4e4b\u5185\uff0c\u4ee5 \u524d\u7684\u201c\u51b7\u5bab\u201d\u65e7\u5740\uff0c\u5c31\u5728\u7fe0\u534e\u5bab\u53f3\u8fb9\uff0c\u6539\u5efa\u4e4b\u540e\uff0c\u4e5f\u88ab\u5708\u8fdb\u5bab\u5899\u4e4b\u5185\u4e86\u3002\u96cd\u6b63\u4fe1\u6b65\u8d70\u53bb\uff0c\u4f46 \u89c1\u6708\u8272\u6eb6\u6eb6\uff0c\u6e05\u8f89\u531d\u5730\uff0c\u98ce\u9001\u8377\u9999\uff0c\u6c81\u4eba\u5fc3\u80ba\uff1b\u5c06\u5230\u8377\u5858\uff0c\u5ffd\u95fb\u5f97\u8f7b\u8f7b\u53f9\u606f\u4e4b\u58f0\uff0c\u8377\u5858\u83b2\u53f6 \u7530\u7530\uff0c\u73b0\u51fa\u4ead\u4ead\u5012\u5f71\uff0c\u96cd\u6b63\u653e\u8f7b\u811a\u6b65\uff0c\u6084\u6084\u8d70\u8fd1\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u65b0\u6765\u7684\u79c0\u5973\uff0c\u4e3a\u4f55 \u53f9\u606f\uff1f\u201d\u90a3\u5bab\u5a25\u56de\u8fc7\u5934\u6765\uff0c\u96cd\u6b63\u5fc3\u5934\u4e00\u9707\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u6797\u82b7\u5417\uff1f\u201d\u89c1\u5979\u9762\u8c8c\u6bd4\u753b\u56fe\u7f8e\u5f97 \u591a\uff0c\u4f46\u4ecd\u7136\u6bd4\u4e0d\u4e0a\u51af\u7433\uff0c\u8138\u4e0a\u8fd8\u6709\u4e00\u9897\u9ed1\u75e3\u3002\u96cd\u6b63\u5fc3\u9053\uff1a\u679c\u7136\u76f8\u4f3c\uff0c\u82e5\u7136\u6ca1\u6709\u8fd9\u9ed1\u75e3\uff0c\u6715\u771f \u4f1a\u5f53\u5979\u662f\u51af\u7433\u4e86\u3002\u90a3\u79c0\u5973\u56de\u7738\u4e00\u76fc\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u5974\u5a62\u6b63\u662f\u6797\u82b7\uff0c\u4e0d\u6562\u6709\u52b3\u7687\u4e0a\u4eb2\u95ee\u3002\u201d\u4e00 \u7b11\u4e4b\u4e0b\uff0c\u5de6\u8fb9\u8138\u4e0a\uff0c\u73b0\u51fa\u4e00\u4e2a\u6d45\u6d45\u7684\u68a8\u6da1\u3002 \u3000\u3000\u96cd\u6b63\u53c8\u662f\u5fc3\u5934\u4e00\u9707\uff0c\u9000\u4e86\u4e24\u6b65\uff0c\u624d\u518d\u8d70\u4e0a\u524d\u6765\uff0c\u4f38\u624b\u62c9\u90a3\u79c0\u5973\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u771f\u50cf\u4e00\u4e2a \u4eba\u3002\u201d\u539f\u6765\u96cd\u6b63\u7cbe\u7ec6\u975e\u5e38\uff0c\u51af\u7433\u81ea\u5c0f\u5728\u4ed6\u7687\u5e9c\u957f\u5927\uff0c\u4ed6\u5df2\u7559\u610f\u5230\u51af\u7433\u7b11\u65f6\uff0c\u662f\u53f3\u8fb9\u8138\u4e0a\u73b0\u51fa \u68a8\u6da1\uff0c\u4e0e\u8fd9\u79c0\u5973\u521a\u597d\u662f\u4e00\u5de6\u4e00\u53f3\u3002 \u3000\u3000\u90a3\u79c0\u5973\u53e3\u4e2d\u7b11\u9053\uff1a\u201c\u50cf\u4ec0\u4e48\u4eba\uff1f\u201d\u5f85\u96cd\u6b63\u4f38\u624b\u62c9\u65f6\uff0c\u7a81\u7136\u53cd\u624b\u4e00\u638c\uff0c\u6263\u4f4f\u4e86\u96cd\u6b63\u7684\u624b \u8155\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53f3\u624b\u53cc\u6307\u4e00\u6233\uff0c\u70b9\u5411\u4ed6\u9762\u4e0a\u53cc\u775b\u3002\u8fd9\u4e00\u62db\u662f\u64d2\u62ff\u624b\u6742\u4ee5\u523a\u6233\u672f\uff0c\u5389\u5bb3 \u975e\u5e38\uff1b\u654c\u4eba\u82e5\u975e\u5f53\u573a\u762b\u75ea\uff0c\u5c31\u5f97\u4e24\u773c\u4ff1\u76f2\u3002 \u3000\u3000\u5e78\u800c\u96cd\u6b63\u6b66\u529f\u66fe\u5f97\u5c11\u6797\u4e09\u8001\u771f\u4f20\uff0c\u505a\u4e86\u7687\u5e1d\u4e4b\u540e\uff0c\u4e5f\u8fd8\u52e4\u4fee\u82e6\u7ec3\uff0c\u5c31\u5728\u8fd9\u53d8\u751f\u4e0d\u6d4b\u3001\u6027 \u547d\u4fc4\u9877\u4e4b\u95f4\uff0c\u4f7f\u51fa\u7f57\u6c49\u62f3\u7684\u6551\u547d\u795e\u62db\uff0c\u624b\u8098\u5411\u540e\u4e00\u649e\uff0c\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\u907f\u4e86\u5f00\u53bb\uff0c\u96cd\u6b63 \u6c14\u529b\u8f83\u5927\uff0c\u53d8\u62db\u8fc5\u901f\uff0c\u90a3\u5c11\u5973\u64d2\u62ff\u4e0d\u7a33\uff0c\u53cd\u88ab\u4ed6\u62d6\u5f97\u5411\u524d\u51b2\u4e86\u4e24\u6b65\uff0c\u96cd\u6b63\u5927\u559d\u4e00\u58f0\uff0c\u5de6\u62f3\u6253 \u51fa\uff0c\u75be\u82e5\u795e\u96f7\uff0c\u5c11\u6797\u795e\u62f3\u975e\u540c\u5c0f\u53ef\uff0c\u83ab\u8bf4\u88ab\u4ed6\u6253\u4e2d\uff0c\u6b66\u529f\u7a0d\u4f4e\u7684\u88ab\u62f3\u98ce\u6fc0\u8361\uff0c\u4e5f\u4f1a\u9707\u4f24\u3002 \u3000\u3000\u5374\u4e0d\u6599\u62f3\u98ce\u8d77\u5904\uff0c\u5029\u5f71\u65e0\u8e2a\u3002\u90a3\u5c11\u5973\u7684\u8f7b\u529f\u7adf\u5df2\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u5979\u5c31\u8d81\u7740\u62f3\u98ce\u6fc0\u8361 \u4e4b\u9645\uff0c\u98d8\u8eab\u98de\u8d77\uff0c\u4eba\u5728\u534a\u7a7a\uff0c\u5251\u5df2\u51fa\u5323\uff0c\u5c31\u5728\u534a\u7a7a\u4e2d\u633d\u4e86\u4e00\u6735\u5251\u82b1\uff0c\u51cc\u7a7a\u4e0b\u523a\u3002\u96cd\u6b63\u5927\u53eb \u9053\uff1a\u201c\u54c8\u5e03\u9640\u5feb\u6765\u6551\u9a7e\uff01\u201d\u65bd\u5c55\u795e\u62f3\u62db\u6570\uff0c\u8fb9\u6253\u8fb9\u9000\uff1b\u970e\u773c\u4e4b\u95f4\uff0c\u907f\u4e86\u4e09\u62db\uff0c\u90a3\u5c11\u5973\u5251\u6cd5\u975e \u5e38\u5389\u5bb3\uff0c\u867d\u7136\u5728\u51e0\u62db\u4e4b\u5185\uff0c\u672a\u80fd\u5f97\u624b\uff0c\u4f46\u5251\u5149\u98d8\u77a5\uff0c\u604d\u5982\u5929\u5973\u6563\u82b1\uff0c\u6c34\u94f6\u6cfb\u5730\uff0c\u628a\u96cd\u6b63\u7684\u9000 \u8def\uff0c\u5b8c\u5168\u5c01\u4e86\u3002 \u3000\u3000\u8fd9\u79c0\u5973\u6b63\u662f\u51af\u745b\uff0c\u5979\u548c\u51af\u7433\u3001\u5415\u56db\u5a18\u90fd\u5192\u5145\u79c0\u5973\uff0c\u8fdb\u5bab\u6765\u4e86\u3002\u539f\u6765\u5f53\u4e0a\u6b21\u5927\u95f9\u7687\u5b98\u4e4b \u540e\uff0c\u51af\u7433\u542c\u5f97\u7518\u51e4\u6c60\u8c08\u8d77\u90a3\u6295\u6c34\u81ea\u5c3d\u7684\u5bab\u5973\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u51fa\u5999\u8ba1\u3002\u79c0\u5973\u4e09\u5e74\u6311\u9009\u4e00\u6b21\uff0c\u4eca \u5e74\u6b63\u662f\u6311\u9009\u4e4b\u671f\uff0c\u6709\u5973\u4eba\u5bb6\uff0c\u4e0d\u8bba\u8d2b\u5bcc\uff0c\u90fd\u7eb7\u7eb7\u8bbe\u6cd5\u9003\u907f\uff0c\u6216\u7acb\u5373\u89c5\u5a7f\u9063\u5ac1\uff0c\u6216\u8d3f\u8d42\u5dde\u53bf\uff0c \u5192\u540d\u9876\u66ff\u3002\u5415\u56db\u5a18\u7b49\u4e09\u4eba\u81ea\u613f\u9876\u66ff\u8d2b\u82e6\u4eba\u5bb6\u7684\u5973\u513f\uff0c\u542c\u5019\u6311\u9009\uff0c\u4ee5\u5979\u4eec\u7684\u59ff\u8272\uff0c\u81ea\u7136\u4e00\u9009\u5c31 \u88ab\u9009\u4e0a\u3002 \u3000\u3000\u5979\u4eec\u9664\u4e86\u7528\u6613\u5bb9\u672f\uff08\u65e9\u671f\u7684\u5316\u88c5\u672f\uff09\uff0c\u529b\u6c42\u53d8\u5316\u9762\u8c8c\u4e4b\u5916\uff0c\u5230\u4e86\u5bab\u4e2d\uff0c\u53c8\u6545\u610f\u8d3f\u8d42\u753b \u5de5\uff0c\u8bf7\u753b\u5de5\u4e0d\u8981\u628a\u5979\u4eec\u753b\u5f97\u592a\u8fc7\u4e0e\u539f\u6765\u7684\u76f8\u8c8c\u76f8\u4f3c\u3002\u800c\u4e14\uff0c\u66f4\u6709\u8da3\u7684\u662f\uff0c\u522b\u7684\u79c0\u5973\u90fd\u8981\u6c42\u753b \u5de5\u753b\u5f97\u7f8e\u4e9b\uff0c\u53ea\u6709\u5979\u4eec\u4e09\u4e2a\uff0c\u5374\u8d3f\u8d42\u753b\u5de5\u4e0d\u8981\u753b\u5f97\u90a3\u6837\u7f8e\u3002\u5979\u4eec\u8fdb\u5bab\u4e4b\u540e\uff0c\u6070\u503c\u96cd\u6b63\u63d0\u5fc3\u540a \u80c6\uff0c\u9632\u5907\u523a\u5ba2\uff0c\u65e0\u6687\u5bfb\u6b22\uff0c\u6240\u4ee5\u4e00\u8fde\u4e09\u6708\uff0c\u5979\u4eec\u90fd\u6ca1\u6709\u78b0\u89c1\u8fc7\u7687\u5e1d\u3002\u5374\u4e0d\u6599\u4eca\u665a\u795e\u5dee\u9b3c\u4f7f\uff0c \u96cd\u6b63\u81ea\u5df1\u6295\u5230\u7fe0\u534e\u5bab\u6765\uff0c\u548c\u51af\u745b\u9047\u4e0a\u4e86\u3002 \u3000\u3000\u54c8\u5e03\u9640\u5728\u5bab\u5899\u5916\u542c\u5f97\u96cd\u6b63\u547c\u5524\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u6025\u6025\u98de\u4e0a\u5899\u5934\uff0c\u5954\u6765\u6551\u9a7e\uff0c\u5ffd\u89c1\u6811\u4e1b \u4e2d\uff0c\u4eba\u5f71\u4e00\u6643\uff0c\u4e00\u540d\u5bab\u5a25\u73b0\u51fa\u8eab\u6765\uff0c\u8eab\u6cd5\u8f7b\u7075\u4e4b\u6781\uff0c\u54c8\u5e03\u9640\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6d41\u661f\u9524\u6b63\u5f85\u629b\u51fa\uff0c\u5ffd \u542c\u5f97\u545c\u545c\u4e4b\u58f0\uff0c\u90a3\u5bab\u5a25\u53cc\u624b\u4e00\u626c\uff0c\u4e24\u9053\u4e4c\u91d1\u5149\u8292\uff0c\u5288\u7a7a\u5c04\u5230\uff0c\u8fd9\u6b63\u662f\u51af\u7433\u7684\u72ec\u95e8\u6697\u5668\u593a\u547d\u795e \u5200\uff0c\u89c1\u8840\u5c01\u5589\uff0c\u5389\u5bb3\u65e0\u6bd4\u3002 \u3000\u3000\u54c8\u5e03\u9640\u662f\u5bab\u4e2d\u4f8d\u536b\u7684\u603b\u7ba1\uff0c\u6b66\u529f\u5353\u7edd\u975e\u51e1\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u53cc\u9524\u4e00\u4e2a\u76d8\u65cb\uff0c\u4e24\u67c4\u98de\u5200\uff0c\u90fd\u7ed9 \u4ed6\u53cd\u51fb\u5f97\u98de\u4e0a\u534a\u7a7a\uff0c\u65ad\u6210\u56db\u622a\u3002\u4f46\u867d\u7136\u5982\u6b64\uff0c\u4ed6\u5df2\u7ecf\u88ab\u963b\u4e86\u4e00\u963b\u3002\u51af\u7433\u8eab\u624b\u4f55\u7b49\u5feb\u6377\uff0c\u7acb\u5373 \u62d4\u5251\u8fdb\u62db\uff0c\u523a\u4ed6\u54bd\u5589\u3002\u54c8\u5e03\u9640\u4e00\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u53cc\u9524\u8fd8\u51fb\uff0c\u5374\u4e0d\u6599\u51af\u7433\u8eab\u6cd5\u5201\u94bb\u5f02\u5e38\uff0c\u4f46\u89c1\u5979 \u5251\u968f\u8eab\u8f6c\uff0c\u81c2\u968f\u5251\u626c\uff0c\u4e00\u4e2a\u77ee\u8eab\uff0c\u5c31\u4ece\u53cc\u9524\u4ea4\u51fb\u4e4b\u4e0b\uff0c\u94bb\u4e86\u8fc7\u53bb\uff0c\u5237\u5237\u4e24\u5251\uff0c\u624e\u8170\u523a\u8179\uff0c\u72e0 \u8fa3\u4e4b\u6781\u3002\u54c8\u5e03\u9640\u5927\u5403\u4e00\u60ca\u3002\u6599\u4e0d\u5230\u51af\u7433\u6b66\u529f\u7cbe\u8fdb\u5982\u65af\uff0c\u6025\u628a\u5de6\u9524\u76d8\u7a7a\u4e00\u821e\uff0c\u4f7f\u4e2a\u201c\u96ea\u82b1\u76d6 \u9876\u201d\uff0c\u53f3\u9524\u531d\u5730\u4e00\u7ed5\uff0c\u4f7f\u4e2a\u201c\u67af\u6811\u76d8\u6839\u201d\uff0c\u62a4\u7740\u5168\u8eab\u3002\u51af\u7433\u5251\u6cd5\u867d\u7136\u7cbe\u8fdb\uff0c\u529f\u529b\u5374\u8fd8\u6bd4\u4e0d\u4e0a \u654c\u4eba\uff0c\u88ab\u54c8\u5e03\u9640\u53cc\u9524\u4e00\u903c\uff0c\u8fd1\u4e0d\u4e86\u8eab\u3002 \u3000\u3000\u4f46\u54c8\u5e03\u9640\u88ab\u5979\u6240\u963b\uff0c\u6025\u5207\u4e4b\u95f4\u4e5f\u95ef\u4e0d\u8fc7\u53bb\u3002\u53ea\u542c\u5f97\u96cd\u6b63\u8fde\u58f0\u547c\u53eb\uff0c\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u4e14\u5df2 \u9690\u7ea6\u53ef\u95fb\u3002\u54c8\u5e03\u9640\u5927\u6025\uff0c\u53cc\u9524\u4e00\u821e\uff0c\u7a81\u7136\u628a\u5de6\u9524\u629b\u51fa\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5f53\u80f8\u51fb\u53bb\uff0c\u51af\u7433\u77e5\u9053\u5389 \u5bb3\uff0c\u95ea\u8eab\u6025\u9000\uff0c\u54c8\u5e03\u9640\u53cc\u9524\u4ea4\u4e8e\u4e00\u624b\uff0c\u53d6\u51fa\u4e24\u4e2a\u9ed1\u5ffd\u5ffd\u7684\u5706\u7403\uff0c\u63b7\u4e0a\u534a\u7a7a\uff0c\u53d1\u51fa\u602a\u5578\uff0c\u51af\u7433 \u77e5\u9053\u8fd9\u662f\u53ec\u5524\u8840\u6ef4\u5b50\u7684\u4fe1\u53f7\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6599\u77e5\u59d0\u59d0\u5fc5\u7136\u5df2\u78b0\u4e0a\u7687\u5e1d\uff0c\u8981\u4e0d\u7136\u54c8\u5e03\u9640\u4e0d\u4f1a\u7740\u6025 \u5982\u65af\uff0c\u4e8e\u662f\u4e0d\u5f85\u54c8\u5e03\u9640\u518d\u4e0a\uff0c\u4fbf\u5bfb\u58f0\u89c5\u8ff9\uff0c\u5411\u96cd\u6b63\u547c\u53eb\u7684\u5730\u65b9\u63a0\u53bb\u3002 \u3000\u3000\u54c8\u5e03\u9640\u7684\u8f7b\u529f\u5374\u6bd4\u4e0d\u4e0a\u51af\u7433\uff0c\u767e\u5fd9\u4e2d\u98de\u51fa\u4e24\u4e2a\u8840\u6ef4\u5b50\uff0c\u51af\u7433\u5934\u4e5f\u4e0d\u56de\uff0c\u53cd\u624b\u4e24\u67c4\u98de\u5200\uff0c \u5c31\u628a\u8840\u6ef4\u5b50\u6253\u843d\u3002\u6b63\u5728\u5f97\u610f\uff0c\u5ffd\u95fb\u5f97\u54c8\u54c8\u602a\u7b11\uff0c\u4e00\u6761\u5e9e\u5927\u7684\u4eba\u5f71\uff0c\u7a81\u7136\u4ece\u8fde\u63a5\u5b98\u5899\u5916\u7684\u67cf\u6811 \u4e0a\u98de\u4e86\u8fdb\u6765\uff0c\u4f46\u89c1\u4e00\u4e2a\u756a\u50e7\uff0c\u62ab\u7740\u5927\u7ea2\u8888\u88df\uff0c\u5b9b\u5982\u4e00\u6735\u706b\u4e91\uff0c\u63a0\u7a7a\u800c\u964d\uff0c\u6765\u7684\u4e0d\u662f\u522b\u4eba\uff0c\u6b63 \u662f\u989d\u97f3\u548c\u5e03\uff0c\u4f46\u89c1\u4ed6\u58f0\u5230\u4eba\u5230\uff0c\u62c2\u5c18\u4e00\u5c55\uff0c\u5c31\u628a\u51af\u7433\u903c\u9000\u4e09\u6b65\uff0c\u54c8\u5e03\u9640\u5927\u559c\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u662f \u7687\u4e0a\u6240\u8981\u7684\u4eba\uff0c\u5343\u4e07\u4e0d\u8981\u653e\u8fc7\u3002\u201d\u4ed6\u77e5\u9053\u4ee5\u989d\u97f3\u548c\u5e03\u7684\u6b66\u529f\uff0c\u51af\u7433\u7edd\u4e0d\u80fd\u9003\u51fa\u4ed6\u638c\u63e1\uff0c\u4fbf\u8ff3 \u81ea\u53bb\u6551\u96cd\u6b63\u3002 \u3000\u3000\u5374\u4e0d\u6599\u51af\u7433\u6b66\u529f\u867d\u7136\u8fdc\u4e0d\u53ca\u989d\u97f3\u548c\u5e03\uff0c\u4f46\u5374\u901a\u6653\u5404\u79cd\u90aa\u6d3e\u6b66\u529f\uff0c\u800c\u4e14\u5979\u53c8\u77e5\u9053\u989d\u97f3\u548c\u5e03 \u547d\u95e8\u8981\u7a74\u6240\u5728\uff0c\u989d\u97f3\u548c\u5e03\u8fde\u8fdb\u4e09\u62db\uff0c\u90fd\u88ab\u5979\u8fd0\u7528\u732b\u9e70\u6251\u51fb\u4e4b\u6280\u907f\u8fc7\uff0c\u5b9d\u5251\u8fde\u73af\u75be\u523a\uff0c\u4e0a\u6307 \u201c\u79bb\u706b\u201d\uff0c\u4e0b\u6307\u201c\u574e\u6c34\u201d\uff0c\u989d\u97f3\u548c\u5e03\u9887\u6709\u987e\u5fcc\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u81ea\u5948\u4f55\u4e0d\u5f97\u3002\u53ef\u662f\u51af\u7433\u6b66\u529f\u5230 \u5e95\u4e0e\u4ed6\u76f8\u53bb\u751a\u8fdc\uff0c\u867d\u7136\u901a\u6653\u897f\u85cf\u7ea2\u6559\u523a\u7a74\u4e4b\u6cd5\uff0c\u4e5f\u662f\u6b3a\u4e0d\u8fd1\u8eab\u3002 \u3000\u3000\u7fe0\u534e\u5bab\u5185\uff0c\u51af\u745b\u5251\u4f3c\u94f6\u86c7\uff0c\u628a\u96cd\u6b63\u56f0\u5728\u4e00\u9685\uff0c\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u770b\u770b\u5c31\u8981\u628a\u96cd\u6b63\u9489\u5728\u5899 \u4e0a\u3002\u54c8\u5e03\u9640\u98de\u5954\u8d76\u5230\uff0c\u9524\u4f3c\u6d41\u661f\uff0c\u53ee\u5f53\u4e00\u58f0\uff0c\u4e0e\u51af\u745b\u7684\u5b9d\u5251\u78b0\u4e2a\u6b63\u7740\uff0c\u53d1\u51fa\u4e00\u7bf7\u706b\u82b1\u3002\u54c8\u5e03 \u9640\u7684\u94dc\u9524\u88ab\u5288\u6210\u4e24\u534a\uff0c\u4f46\u51af\u745b\u4e5f\u7ed9\u9707\u9000\u4e09\u6b65\u3002\u54c8\u5e03\u9640\u594b\u4e0d\u987e\u8eab\uff0c\u6325\u9524\u75be\u8fdb\uff0c\u82e5\u8bba\u51af\u745b\u8fd9\u65f6\u7684 \u6b66\u529f\u4e0e\u54c8\u5e03\u9640\u5df2\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u8f7b\u529f\u5c24\u5728\u54c8\u5e03\u9640\u4e4b\u4e0a\uff0c\u53ef\u662f\u5979\u5fd7\u5728\u96cd\u6b63\uff0c\u65e0\u6687\u4e0e\u54c8\u5e03\u9640\u7ea0\u7f20\uff0c\u5251 \u950b\u4e00\u8f6c\uff0c\u590d\u8fdb\u4e00\u62db\uff0c\u7a81\u7136\u98de\u8eab\u63a0\u8d77\uff0c\u54c8\u5e03\u9640\u4e00\u9524\u51fb\u5230\uff0c\u4f46\u89c1\u5979\u8eab\u5b50\u60ac\u7a7a\uff0c\u5f13\u978b\u4e00\u8e0f\u94dc\u9524\uff0c\u8f7b \u5982\u67f3\u7d6e\uff0c\u7adf\u501f\u7740\u94dc\u9524\u53cd\u51fb\u4e4b\u529b\uff0c\u98d8\u5728\u534a\u7a7a\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5251\u5149\u5982\u7ec3\uff0c\u523a\u5230\u4e86\u96cd\u6b63\u5934\u4e0a\u3002 \u3000\u3000\u96cd\u6b63\u673a\u667a\u4e07\u5206\uff0c\u5c31\u5730\u4e00\u6eda\uff0c\u4e00\u4e2a\u201c\u71d5\u9752\u5341\u516b\u7ffb\u201d\u907f\u5f00\u3002\u51af\u745b\u98de\u8eab\u4e00\u63a0\uff0c\u5237\u5237\u4e24\u5251\uff0c\u8ddf\u8e2a \u8ffd\u523a\u3002\u53ef\u662f\u96cd\u6b63\u6b66\u529f\uff0c\u4ea6\u975e\u5f31\u8005\uff0c\u907f\u5f00\u4e86\u51af\u745b\u51cc\u7a7a\u4e0b\u51fb\u4e4b\u52bf\uff0c\u7acb\u523b\u6325\u62f3\u53cd\u51fb\uff0c\u54c8\u5e03\u9640\u4e5f\u5927\u559d \u4e00\u58f0\uff0c\u821e\u9524\u6025\u4e0a\uff0c\u53cd\u5c01\u4f4f\u4e86\u51af\u745b\u7684\u53bb\u8def\u3002\u51af\u745b\u4ee5\u4e00\u654c\u4e8c\uff0c\u65bd\u5c55\u4e0d\u5f00\uff0c\u950b\u8292\u5927\u51cf\uff0c\u96cd\u6b63\u54c8\u54c8\u5927 \u7b11\uff0c\u6b63\u5f85\u4e58\u673a\u7a9c\u51fa\uff0c\u51af\u745b\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u60f3\u9003\u5417\uff1f\u4f60\u770b\u662f\u8c01\u6765\u4e86\u3002\u201d\u96cd\u6b63\u7ad6\u8033\u4e00\u542c\uff0c\u5bab\u5899\u5916 \u4eba\u58f0\u5608\u6742\uff0c\u81ea\u8fdc\u800c\u8fd1\uff0c\u4eba\u58f0\u4e2d\u5939\u7740\u957f\u5578\uff0c\u90a3\u662f\u5929\u53f6\u6563\u4eba\u7684\u5578\u58f0\uff0c\u96cd\u6b63\u5927\u7b11\u9053\uff1a\u201c\u662f\u6715\u7684\u536b\u58eb \u6765\u4e86\uff0c\u4f60\u5f03\u5251\u5f52\u987a\uff0c\u8054\u8fd8\u53ef\u9976\u4f60\u4e00\u6b7b\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u53ef\u5c01\u4f60\u505a\u8d35\u4eba\u3002\u201d\u51af\u745b\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f \u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u4e0d\u81ea\u77e5\uff0c\u4f60\u770b\u8fd9\u662f\u8c01\u4eba\uff0c\u662f\u4f60\u7684\u536b\u58eb\u5417\uff1f\u201d\u7e41\u679d\u8302\u53f6\u4e4b\u4e2d\uff0c\u5ffd\u5730\u4e00\u58f0\u957f\u5578\uff0c\u4e00 \u4e2a\u767d\u8863\u5c11\u5973\uff0c\u8863\u5e26\u98d8\u98d8\uff0c\u4e25\u82e5\u5fa1\u98ce\u800c\u4e0b\uff0c\u96cd\u6b63\u4e00\u89c1\uff0c\u4ea1\u9b42\u5931\u9b44\uff0c\u7adf\u7136\u662f\u5415\u56db\u5a18\u6765\u4e86\u3002\u5415\u56db\u5a18 \u8f7b\u529f\u5df2\u5230\u51fa\u795e\u5165\u5316\u4e4b\u5883\uff0c\u5728\u573a\u8bf8\u4eba\uff0c\u9664\u4e86\u51af\u745b\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u4eba\uff0c\u8fde\u54c8\u5e03\u9640\u90a3\u6837\u6b66\u529f\u9ad8\u660e\u7684\u4eba \u5728\u5185\uff0c\u4e5f\u90fd\u542c\u4e0d\u5230\u5979\u7684\u58f0\u606f\u3002 \u3000\u3000\u5415\u56db\u5a18\u62d4\u5251\u51fa\u9798\uff0c\u62e6\u4f4f\u4e86\u96cd\u6b63\u7684\u53bb\u8def\uff0c\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u9634\u7075\u4e0d\u8fdc\uff0c\u5973\u513f\u4eca\u65e5\u66ff\u4f60 \u62a5\u4ec7\u4e86\uff01\u201d\u7b11\u58f0\u51c4\u5389\uff0c\u96cd\u6b63\u6bdb\u53d1\u7686\u7ad6\uff0c\u54c8\u5e03\u9640\u4e5f\u5413\u5f97\u8f6f\u4e86\u3002\u5415\u56db\u5a18\u6301\u5251\u5728\u624b\uff0c\u4e00\u6b65\u4e00\u6b65\u903c \u8fd1\uff0c\u54c8\u5e03\u9640\u624b\u63d0\u94dc\u9524\uff0c\u7acb\u5728\u96cd\u6b63\u8eab\u8fb9\uff0c\u8eab\u9a71\u98a4\u6296\uff0c\u96cd\u6b63\u5446\u82e5\u6728\u9e21\uff0c\u76d8\u7b97\u4e0d\u51fa\u8131\u8eab\u4e4b\u8ba1\uff0c\u5415\u56db \u5a18\u8f7b\u529f\u6bd4\u4ed6\u9ad8\u660e\u5f97\u591a\uff0c\u4ed6\u82e5\u5192\u9669\u9003\u547d\uff0c\u7a7a\u95e8\u56db\u9732\uff0c\u6b7b\u5f97\u66f4\u5feb\u3002 \u3000\u3000\u5415\u56db\u5a18\u6301\u5251\u4e00\u6b65\u6b65\u903c\u8fd1\uff0c\u51af\u745b\u4e5f\u63d0\u5251\u51dd\u795e\uff0c\u5e2e\u5415\u56db\u5a18\u5c01\u4f4f\u4e86\u96cd\u6b63\u7684\u540e\u8def\uff0c\u8fd9\u201c\u5185\u82d1\u5c60 \u9f99\u201d\u7684\u4e00\u5e55\u770b\u770b\u5c31\u8981\u4e0a\u6f14\uff0c\u5ffd\u542c\u5f97\u989d\u97f3\u548c\u5e03\u559d\u9053\uff1a\u201c\u5415\u56db\u5a18\u4e14\u6162\u52a8\u624b\uff0c\u4f60\u770b\u8fd9\u662f\u8c01\u4eba\uff1f\u201d\u51af \u745b\u60ca\u53eb\u4e00\u58f0\uff0c\u4f46\u89c1\u989d\u97f3\u548c\u5e03\u5df2\u628a\u51af\u7433\u64d2\u5728\u624b\u4e2d\uff0c\u51af\u7433\u53cc\u624b\u4f4e\u5782\uff0c\u5934\u6401\u5728\u654c\u4eba\u80a9\u4e0a\uff0c\u53cc\u76ee\u7d27 \u95ed\uff0c\u4f3c\u4e4e\u662f\u5df2\u7ed9\u989d\u97f3\u548c\u5e03\u70b9\u4e86\u7a74\u9053\u3002 \u3000\u3000\u5415\u56db\u5a18\u4e00\u58f0\u957f\u53f9\uff0c\u8fd9\u6570\u6708\u6765\uff0c\u5979\u542b\u7f9e\u5fcd\u8fb1\uff0c\u5192\u5145\u5bab\u5a25\u5728\u5bab\u4e2d\u6267\u5f79\uff0c\u6709\u5982\u5a62\u5973\uff0c\u597d\u4e0d\u5bb9\u6613 \u624d\u7b49\u5230\u8fd9\u5927\u597d\u673a\u4f1a\uff0c\u773c\u770b\u5c31\u53ef\u4ee5\u62a5\u56fd\u6068\u5bb6\u4ec7\uff0c\u5374\u6599\u4e0d\u5230\u529f\u4e8f\u4e00\u7bd1\uff0c\u88ab\u989d\u97f3\u548c\u5e03\u5236\u7740\u4e86\u673a\u5148\uff0c \u628a\u81ea\u5df1\u7684\u4eba\u64d2\u4e3a\u4eba\u8d28\u3002 \u3000\u3000\u96cd\u6b63\u80c6\u6c14\u987f\u58ee\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5415\u56db\u5a18\u4f60\u610f\u6b32\u5982\u4f55\uff1f\u662f\u4e0d\u662f\u8fd8\u8981\u4e0e\u6715\u89c1\u4e2a\u9ad8\u4e0b\uff1f\u201d\u5415\u56db\u5a18 \u5251\u5c16\u4e0b\u6307\uff0c\u6124\u7136\u8bf4\u9053\uff1a\u201c\u628a\u6211\u4eec\u7684\u4eba\u8fd8\u6765\uff0c\u9976\u4f60\u4e0d\u6b7b\u3002\u201d\u96cd\u6b63\u9053\uff1a\u201c\u597d\uff0c\u989d\u97f3\u548c\u5e03\uff0c\u4f60\u628a\u5979 \u4eec\u9001\u51fa\u5b98\u53bb\u3002\u54c8\u54c8\uff0c\u5415\u56db\u5a18\u5475\uff0c\u6715\u5c11\u966a\u4e86\uff01\u201d\u5411\u54c8\u5e03\u9640\u6253\u4e86\u4e2a\u773c\u8272\uff0c\u8863\u8896\u4e00\u6446\uff0c\u5c31\u8981\u8fc8\u6b65\u52a8 \u8eab\uff0c\u51af\u745b\u5ffd\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u96cd\u6b63\u77a5\u5979\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u5f85\u5982\u4f55\uff1f\u6715\u5df2\u77e5\u9053\u4f60\u4eec\u662f\u59d0\u59b9\u4e86\uff0c\u4f60\u4e0d\u8981\u4f60\u59b9\u59b9\u7684\u6027\u547d\u4e86 \u5417\uff1f\u201d\u51af\u745b\u9053\uff1a\u201c\u4f60\u4eec\u8be1\u8ba1\u591a\u7aef\uff0c\u6211\u4fe1\u4e0d\u8fc7\uff0c\u6211\u5148\u8981\u770b\u6211\u7684\u59b9\u59b9\u662f\u5426\u5df2\u906d\u6bd2\u624b\uff0c\u5415\u59d0\u59d0\uff0c\u4f60 \u770b\u7740\u8fd9\u72d7\u7687\u5e1d\u3002\u201d\u96cd\u6b63\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53bb\u770b\u5427\u3002\u201d\u51af\u745b\u5411\u989d\u97f3\u548c\u5e03\u7684\u65b9\u5411\u4e00\u6b65\u6b65\u8d70\u8fd1\uff0c\u989d\u97f3\u548c \u5e03\u5927\u7b11\u9053\uff1a\u201c\u4f60\u662f\u5927\u5c71\u6613\u8001\u4e5e\u5a46\u7684\u5f1f\u5b50\uff0c\u96be\u9053\u8fde\u70b9\u7a74\u4e5f\u770b\u4e0d\u51fa\u4e48\uff1f\u4f60\u770b\u5979\u597d\u7aef\u7aef\u7684\u51e0\u66fe\u6709\u534a \u70b9\u4f24\u75d5\uff1f\u201d\u63d0\u8d77\u51af\u7433\u5728\u51af\u745b\u9762\u524d\u6643\u4e86\u4e24\u6643\uff0c\u51af\u745b\u7a81\u7136\u53f1\u54a4\u4e00\u58f0\uff0c\u5251\u638c\u9f50\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5927\u51fa\u4f17\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u5415\u56db\u5a18\u60f3\u98de\u8eab\u62e6\u622a\u4e5f\u6765\u4e0d\u53ca\u3002\u4f46\u89c1\u989d\u97f3\u548c\u5e03\u63d0\u8d77\u51af\u7433\uff0c\u5f80\u524d \u4e00\u6321\uff0c\u4e00\u7f15\u9752\u5149\u4ece\u51af\u7433\u9888\u9879\u65c1\u8fb9\u7a7f\u8fc7\u3002\u63a5\u7740\u662f\u201c\u556a\u201d\u7684\u4e00\u638c\u51fb\u5728\u51af\u7433\u8eab\u4e0a\uff0c\u5415\u56db\u5a18\u5931\u58f0\u60ca \u53eb\uff0c\u5ffd\u542c\u5f97\u989d\u97f3\u548c\u5e03\u5927\u543c\u4e00\u58f0\uff0c\u51af\u7433\u7684\u8eab\u5b50\u5982\u7bad\u79bb\u5f26\uff0c\u98de\u4e0a\u534a\u7a7a\uff0c\u51af\u745b\u5530\u7684\u4e00\u5251\uff0c\u7a7f\u8fc7\u4e86\u989d \u97f3\u548c\u5e03\u7684\u54bd\u5589\uff0c\u987f\u65f6\u8840\u82b1\u56db\u6e85\u3002\u989d\u97f3\u548c\u5e03\u90a3\u5e9e\u5927\u7684\u8eab\u8eaf\u5728\u5730\u4e0a\u6eda\u4e86\u51e0\u6eda\uff0c\u6251\u901a\u8dcc\u4e0b\u8377\u5858\u3002 \u3000\u3000\u539f\u6765\u51af\u7433\u901a\u6653\u897f\u85cf\u7ea2\u6559\u7684\u70b9\u7a74\u523a\u7a74\u62c2\u7a74\u7b49\u624b\u6cd5\uff0c\u4e3a\u4e86\u5bf9\u4ed8\u989d\u97f3\u548c\u5e03\uff0c\u4e24\u59d0\u59b9\u65e9\u7ecf\u7ec3\u4e60\uff0c \u6240\u4ee5\u51af\u745b\u4e00\u773c\u671b\u53bb\uff0c\u5c31\u77e5\u9053\u51af\u7433\u4e0a\u4e09\u8def\u7684\u4e03\u4e2a\u8f6f\u9ebb\u7a74\u90fd\u5df2\u7ed9\u989d\u97f3\u548c\u5e03\u6240\u5c01\uff0c\u89e3\u7a74\u4e0d\u96be\uff0c\u53ef\u662f \u8981\u4ece\u989d\u97f3\u548c\u5e03\u8fd9\u6837\u6b66\u529f\u9ad8\u5f3a\u7684\u4eba\u624b\u4e2d\uff0c\u5c06\u6240\u5c01\u7684\u7a74\u9053\u4e00\u4e00\u89e3\u5f00\uff0c\u5374\u662f\u8c08\u4f55\u5bb9\u6613\u3002\u51af\u745b\u672c\u6765\u4e0d \u6562\u5192\u9669\uff0c\u4f46\u4e00\u60f3\u5230\u56fd\u6068\u5bb6\u4ec7\uff0c\u4e00\u60f3\u5230\u5415\u56db\u5a18\u7b49\u4eba\u591a\u5e74\u6765\u5904\u5fc3\u79ef\u8651\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u7b49\u5230\u8fd9\u4e2a\u597d\u7684 \u673a\u4f1a\uff0c\u82e5\u7136\u5c31\u6b64\u88ab\u4ed6\u631f\u5236\uff0c\u5c82\u975e\u5c3d\u4ed8\u4e1c\u6d41\uff1f\u5929\u5c71\u5251\u8bc0\u4e4b\u4e2d\uff0c\u6709\u4e00\u62db\u53eb\u505a\u201c\u4e03\u661f\u805a\u4f1a\u201d\uff0c\u80fd\u5728 \u5f39\u6307\u4e4b\u95f4\uff0c\u8fde\u523a\u4e03\u5904\u7a74\u9053\uff0c\u90a3\u662f\u987b\u8981\u6709\u6700\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u80fd\u628a\u5185\u5bb6\u771f\u529b\uff0c\u900f\u8fc7\u5251\u5c16\uff0c\u6070\u5230\u597d \u5904\uff0c\u65b9\u80fd\u529e\u5230\u3002\u51af\u745b\u8fd9\u4e24\u5e74\u6765\u5728\u5929\u5c71\u82e6\u5b66\uff0c\u8fd9\u4e00\u62db\u4e5f\u53ea\u4e0d\u8fc7\u6709\u4e03\u6210\u706b\u5019\u3002\u4f46\u5728\u6781\u9669\u4e4b\u4e2d\uff0c\u5df2 \u65e0\u6687\u8003\u8651\uff0c\u7acb\u5373\u628a\u5251\u5c16\u523a\u7a74\u653b\u51fb\u654c\u4eba\u7684\u624b\u6cd5\u5316\u4e3a\u6307\u6233\u89e3\u7a74\u7684\u6025\u6551\u4e4b\u6cd5\uff0c\u5251\u62db\u5219\u4ecd\u662f\u7528\u8ffd\u98ce\u5251 \u6cd5\u4e2d\u7684\u8fc5\u6377\u62db\u6570\uff0c\u51fa\u5176\u4e0d\u610f\uff0c\u5251\u638c\u9f50\u65bd\u3002\u989d\u97f3\u548c\u5e03\u4e07\u4e07\u6599\u4e0d\u5230\u51af\u745b\u6562\u8fd9\u6837\u5192\u9669\uff0c\u767e\u5fd9\u4e2d\u63d0\u8d77 \u51af\u7433\u4e00\u6321\uff0c\u5374\u6b63\u7740\u4e86\u51af\u745b\u7684\u9053\u513f\uff0c\u51af\u745b\u4e00\u5251\u75be\u4f3c\u8ffd\u98ce\uff0c\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u8d34\u7740\u51af\u7433\u7684\u9888\u9879\u7a7f \u8fc7\uff0c\u76f4\u53d6\u989d\u97f3\u548c\u5e03\u9762\u4e0a\u53cc\u775b\uff0c\u989d\u97f3\u548c\u5e03\u6b66\u529f\u4e5f\u771f\u9ad8\u5f3a\uff0c\u5728\u8fd9\u5267\u53d8\u4ed3\u5352\u4e4b\u95f4\uff0c\u5c45\u7136\u4e00\u4e2a\u4f4e\u5934\uff0c \u53cc\u6307\u642d\u7740\u5251\u8eab\u4e00\u5f15\uff0c\u5c31\u628a\u51af\u745b\u7684\u5b9d\u5251\u5f15\u51fa\u5916\u95e8\uff1b\u53ef\u662f\u4e3a\u4e86\u5e94\u4ed8\u51af\u745b\u7684\u7a81\u88ad\uff0c\u989d\u97f3\u548c\u5e03\u7684\u773c\u795e \u5df2\u88ab\u5f15\u5f00\uff0c\u51af\u745b\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u4e4b\u95f4\uff0c\u89e3\u5f00\u4e86\u51af\u7433\u7684\u7a74\u9053\u3002\u51af\u7433\u7a74\u9053\u4e00\u89e3\uff0c\u6b66\u529f\u6062\u590d\u3002\u5979\u672c \u6765\u662f\u88ab\u989d\u97f3\u548c\u5e03\u642d\u5728\u80a9\u5934\u7684\uff0c\u53cc\u624b\u4e0b\u5782\uff0c\u6307\u5c16\u6240\u89e6\uff0c\u6b63\u662f\u989d\u97f3\u548c\u5e03\u7684\u201c\u574e\u6c34\u201d\u201c\u79bb\u706b\u201d\u4e4b \u7a74\uff0c\u7acb\u523b\u4e58\u673a\u4e00\u70b9\uff0c\u7834\u4e86\u989d\u97f3\u548c\u5e03\u7684\u6c14\u529f\uff0c\u8131\u8eab\u98de\u8d77\u3002\u51af\u745b\u518d\u8865\u4e0a\u4e00\u5251\uff0c\u5c31\u6b64\u628a\u8fd9\u897f\u85cf\u7ea2\u6559 \u4e2d\u7684\u7b2c\u4e8c\u540d\u9ad8\u624b\uff0c\u9001\u8fdb\u9634\u95f4\u3002 \u3000\u3000\u96cd\u6b63\u89c1\u51af\u745b\u7a81\u65bd\u731b\u88ad\uff0c\u5415\u56db\u5a18\u5931\u58f0\u60ca\u53eb\uff0c\u6ce8\u610f\u8f6c\u79fb\uff0c\u7acb\u523b\u4e58\u673a\u98de\u8eab\u9003\u8d70\u3002\u5374\u4e0d\u6599\u51af\u7433\u8131 \u8eab\u98de\u51fa\uff0c\u6b63\u5de7\u843d\u5728\u96cd\u6b63\u524d\u9762\uff0c\u8d81\u52bf\u53cc\u638c\u4e00\u6251\uff0c\u75be\u7528\u65e0\u6781\u638c\u6cd5\u4e2d\u7684\u201c\u4e94\u9f99\u6251\u9762\u201d\u62db\u6570\uff0c\u731d\u51fb\u96cd \u6b63\u9762\u95e8\u3002\u96cd\u6b63\u6c89\u80a9\u7f29\u8098\uff0c\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\u95ea\u5230\u51af\u7433\u4fa7\u9762\uff0c\u96cd\u6b63\u5728\u62f3\u811a\u4e0a\u7684\u529f\u592b\uff0c\u5b9e\u5728\u8981\u6bd4 \u51af\u7433\u9ad8\u5f3a\uff0c\u51af\u7433\u7b2c\u4e8c\u62db\u8fd8\u672a\u51fa\u624b\uff0c\u4ed6\u5df2\u8d81\u52bf\u4e00\u626d\uff0c\u626d\u7740\u4e86\u51af\u7433\u7684\u80f3\u818a\uff0c\u6b63\u60f3\u6548\u6cd5\u989d\u97f3\u548c\u5e03\u5c06 \u51af\u7433\u64d2\u4e3a\u4eba\u8d28\uff0c\u7a81\u7136\u542c\u5230\u4e00\u58f0\u60e8\u53eb\uff0c\u60f3\u662f\u54c8\u5e03\u9640\u5df2\u6bd9\u5728\u5415\u56db\u5a18\u5251\u4e0b\u3002\u96cd\u6b63\u5fc3\u98a4\u8eab\u6296\uff0c\u53ea\u89c9\u5bd2 \u98ce\u98d2\u7136\uff0c\u9762\u524d\u94f6\u5149\u75be\u95ea\uff0c\u5415\u56db\u5a18\u4e00\u4e0b\u5b50\u5230\u4e86\u9762\u524d\uff0c\u96cd\u6b63\u653e\u5f00\u51af\u7433\u7684\u624b\uff0c\u5c1a\u5f85\u51fa\u62db\u8fce\u51fb\uff0c\u54ea\u91cc \u8fd8\u6765\u5f97\u53ca\uff1f\u5415\u56db\u5a18\u51fa\u624b\u5982\u7535\uff0c\u4e00\u4e0b\u6263\u7740\u4ed6\u7684\u8109\u95e8\uff0c\u4ee4\u4ed6\u52a8\u5f39\u4e0d\u5f97\uff0c\u6b63\u5728\u6b64\u65f6\uff0c\u7fe0\u534e\u5bab\u5916\u7684\u536b \u58eb\u5df2\u6f6e\u6c34\u822c\u6d8c\u8fdb\uff0c\u4e3a\u9996\u7684\u4e43\u662f\u5929\u53f6\u6563\u4eba\u3002 \u3000\u3000\u5415\u56db\u5a18\u6267\u7740\u7687\u5e1d\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u8fd9\u4e2a\u66b4\u8650\u660f\u541b\u4e5f\u503c\u5f97\u4f60\u4eec\u4e3a\u4ed6\u5356\u547d\u5417\uff1f\u5e74\u7fb9\u5c27\u662f\u4f55\u7b49\u4e0b \u573a\uff1f\u4ed6\u7684\u5fc3\u8179\u536b\u58eb\u53c8\u6709\u51e0\u4eba\u4e0d\u662f\u6b7b\u4e8e\u975e\u547d\uff1f\u8fd9\u4e9b\uff0c\u96be\u9053\u4f60\u4eec\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u4ed6\u5728\u751f\u4e4b\u65e5\uff0c\u4f60\u4eec \u6216\u8005\u8fd8\u8981\u6c42\u4ed6\u3001\u60e7\u4ed6\uff0c\u5982\u4eca\uff0c\u4ed6\u5c31\u8981\u9888\u8840\u6e85\u5730\uff0c\u4e00\u7791\u4e0d\u89c6\uff0c\u518d\u4e5f\u4e0d\u80fd\u4e3a\u798f\u4e3a\u7978\uff0c\u4f60\u4eec\u4f55\u5fc5\u8fd8 \u8981\u4e3a\u4ed6\u9001\u6b7b\uff1f\u201d \u3000\u3000\u5415\u56db\u5a18\u7684\u58f0\u97f3\u5e76\u4e0d\u5b8f\u4eae\uff0c\u4f46\u7528\u7684\u662f\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u6bcf\u5b57\u6bcf\u53e5\uff0c\u90fd\u5982\u91d1\u7389\u9535 \u9e23\uff0c\u523a\u5230\u6bcf\u4eba\u5fc3\u91cc\u3002\u5415\u56db\u5a18\u4f83\u4f83\u800c\u8c08\uff0c\u8bdd\u4e00\u8bf4\u5b8c\uff0c\u63a5\u7740\u4e00\u58f0\u51c4\u7b11\uff0c\u4ef0\u5929\u53eb\u9053\uff1a\u201c\u7237\u7237\uff0c\u7239 \u7239\uff0c\u6240\u6709\u88ab\u8fd9\u660f\u541b\u6b8b\u5bb3\u7684\u5fd7\u58eb\u4ec1\u4eba\uff0c\u4ffa\u5415\u83b9\u4eca\u65e5\u4e3a\u4f60\u4eec\u62a5\u4ec7\u4e86\uff01\u201d\u5251\u5149\u4e00\u7ed5\uff0c\u628a\u96cd\u6b63\u7684\u5934\u9885 \u5272\u4e86\u4e0b\u6765\uff0c\u63d0\u5728\u624b\u4e2d\uff0c\u6a2a\u5251\u56db\u987e\uff0c\u795e\u8272\u51db\u7136\u3002\u5929\u53f6\u6563\u4eba\u53d1\u4e00\u58f0\u558a\uff0c\u5c1a\u5f85\u4e0a\u524d\uff0c\u5415\u56db\u5a18\u5389\u58f0\u65a5 \u9053\uff1a\u201c\u8c01\u8981\u4e3a\u8fd9\u660f\u541b\u966a\u4e27\uff0c\u8bf7\u8bd5\u5251\u950b\uff01\u5478\uff0c\u5929\u53f6\u6563\u4eba\uff0c\u4f60\u4e5f\u662f\u4e00\u6d3e\u5b97\u4e3b\uff0c\u5374\u8d2a\u56fe\u5bcc\u8d35\uff0c\u6548\u547d \u660f\u541b\uff0c\u4e0d\u77e5\u7f9e\u4e48\uff1f\u5ff5\u4f60\u5e73\u751f\uff0c\u5c1a\u65e0\u5927\u6076\uff0c\u5feb\u5feb\u56de\u5c71\uff0c\u9976\u4f60\u4e0d\u6b7b\u3002\u4f60\u82e5\u8fd8\u8981\u52a8\u624b\uff0c\u8bf7\u95ee\u4f60\u7684\u6b66 \u529f\u6bd4\u8d77\u989d\u97f3\u548c\u5e03\u4e0e\u54c8\u5e03\u9640\u5982\u4f55\uff01\u201d \u3000\u3000\u5929\u53f6\u6563\u4eba\u4e00\u7a92\uff0c\u6709\u5341\u591a\u540d\u8840\u6ef4\u5b50\uff0c\u4e0d\u77e5\u6b7b\u6d3b\uff0c\u629b\u51fa\u6697\u5668\uff0c\u5341\u51e0\u4e2a\u9ed1\u5ffd\u5ffd\u7684\u5706\u7403\u5e26\u7740\u9e23\u545c \u602a\u54cd\uff0c\u6a2a\u7a7a\u5bc6\u96c6\u98de\u6765\uff0c\u51af\u7433\u53eb\u58f0\uff1a\u201c\u597d\u800d\u5475\uff01\u201d\u53cc\u624b\u4e00\u626c\uff0c\u8fde\u53d1\u5341\u4e8c\u67c4\u98de\u5200\uff0c\u628a\u98de\u6765\u7684\u8840\u6ef4 \u5b50\u5168\u90e8\u649e\u843d\u3002\u6bcf\u4e2a\u8840\u6ef4\u5b50\u91cc\u90fd\u6709\u5341\u67c4\u5315\u9996\uff0c\u673a\u5173\u6253\u5f00\uff0c\u98de\u5200\u7eb7\u7eb7\u5c04\u51fa\uff0c\u5b9b\u5982\u6563\u4e0b\u6ee1\u5929\u5200\u96e8\u3002 \u5415\u56db\u5a18\u4e00\u58f0\u51b7\u7b11\uff0c\u98de\u8eab\u63a0\u8d77\uff0c\u7a7f\u5165\u6ee1\u5929\u5200\u96e8\u4e4b\u4e2d\uff0c\u5c31\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u8fde\u6349\u4e86\u5341\u51e0\u67c4\u5315\u9996\uff0c\u95ea\u7535 \u822c\u7684\u75be\u5c04\u56de\u53bb\uff0c\u5c31\u5728\u5979\u98de\u8eab\u63a0\u8d77\u81f3\u843d\u4e0b\u5730\u6765\u7684\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u8fde\u53d1\u4e86\u5341\u51e0\u53e3\u98de\u5200\uff0c\u521a\u597d\u628a\u90a3\u4e9b \u6562\u4e8e\u65bd\u653e\u6697\u5668\u7684\u8840\u6ef4\u5b50\u5168\u90fd\u6740\u6389\u3002\u536b\u58eb\u4eec\u53d1\u4e00\u58f0\u558a\uff0c\u7eb7\u7eb7\u8dc3\u51fa\u5bab\u5899\uff0c\u81f3\u4e8e\u5929\u53f6\u6563\u4eba\u5219\u65e9\u5df2\u9003 \u4e86\u3002\u5415\u56db\u5a18\u4e00\u58f0\u957f\u7b11\uff0c\u4e0e\u51af\u745b\u51af\u7433\u8df3\u4e0a\u4e86\u7409\u7483\u74e6\u9762\uff0c\u5982\u98de\u5954\u51fa\u5bab\u5916\uff0c\u8fd9\u65f6\u5df2\u662f\u6668\u9e21\u5531\u6653\uff0c\u5929 \u5c06\u5927\u767d\u4e86\u3002 \u3000\u3000\u5341\u4f59\u65e5\u540e\uff0c\u5c71\u4e1c\u9053\u4e0a\uff0c\u51fa\u73b0\u4e86\u56db\u7537\u4e09\u5973\uff0c\u4e09\u4e2a\u5973\u7684\u5c31\u662f\u540d\u9707\u6c5f\u6e56\u7684\u201c\u4e09\u5973\u4fa0\u201d\uff1a\u5415\u56db \u5a18\u3001\u51af\u745b\u3001\u51af\u7433\u3002\u90a3\u56db\u7537\u7684\u5374\u662f\u7518\u51e4\u6c60\u3001\u6c88\u5728\u5bbd\u3001\u5510\u6653\u6f9c\u548c\u674e\u6cbb\u3002\u539f\u6765\u81ea\u4e09\u5973\u4fa0\u5192\u9669\u5145\u79c0 \u5973\uff0c\u5165\u5bab\u8fdb\u884c\u62a5\u4ec7\u4e4b\u540e\uff0c\u7fa4\u96c4\u90fd\u5bc6\u805a\u5728\u516b\u8fbe\u5cad\u4e0a\u542c\u5019\u6d88\u606f\uff0c\u5f85\u5f97\u5415\u56db\u5a18\u6210\u529f\u5f52\u6765\uff0c\u5c06\u96cd\u6b63\u7684 \u5934\u9885\u796d\u8fc7\u5979\u7684\u7956\u7236\u3001\u7236\u4eb2\u4e4b\u540e\uff0c\u624d\u5404\u81ea\u6563\u53bb\u3002\u5176\u4e2d\u5173\u4e1c\u4e09\u4fa0\u5230\u5173\u5916\u6e38\u4fa0\uff0c\u9c7c\u4eae\u7236\u5973\u4e0e\u767d\u6cf0\u5b98 \u626c\u5e06\u51fa\u6d77\uff0c\u8def\u6c11\u77bb\u5055\u674e\u660e\u73e0\u5f52\u9690\u7530\u56ed\uff0c\u5415\u56db\u5a18\u4e0e\u7518\u51e4\u6c60\u672c\u8981\u5230\u9099\u5c71\u91cd\u4fee\u5e08\u5085\u7684\u9675\u56ed\uff0c\u4f46\u5510\u6653 \u6f9c\u5374\u6709\u5fc3\u4e8b\u672a\u4e86\uff0c\u8bf7\u4ed6\u4eec\u91cd\u5230\u5c71\u4e1c\u6768\u4ef2\u82f1\u7684\u6545\u5c45\uff0c\u60f3\u6700\u540e\u4e00\u6b21\u796d\u626b\u6069\u5e08\u4e4b\u5893\uff0c\u7136\u540e\u56de\u8f6c\u5929 \u5c71\u3002\u5415\u56db\u5a18\u4e0e\u4ed6\u5341\u51e0\u5e74\u77e5\u4ea4\uff0c\u5f62\u540c\u59d0\u5f1f\uff0c\u5206\u522b\u5728\u5373\uff0c\u4e5f\u89c9\u4f9d\u4f9d\u4e0d\u820d\uff0c\u4fbf\u7b54\u5e94\u548c\u4ed6\u540c\u8d70\u4e00\u7a0b\u3002 \u3000\u3000\u5176\u65f6\u6b63\u662f\u51c9\u79cb\u4e5d\u6708\uff0c\u6c14\u723d\u5929\u9ad8\uff0c\u82f1\u96c4\u513f\u5973\uff0c\u6069\u4ec7\u4e8b\u4e86\uff0c\u7545\u8c08\u4fa0\u4e49\uff0c\u5e76\u8f94\u5954\u9a70\uff0c\u771f\u4e2a\u662f\u8c6a \u60c5\u80dc\u6982\uff0c\u610f\u6c14\u5343\u4e91\uff0c\u6d51\u5fd8\u4e86\u4ec6\u4ec6\u98ce\u5c18\uff0c\u65c5\u9014\u8fdc\u8fd1\u3002\u6b63\u5728\u5e76\u8f94\u5954\u9a70\u4e4b\u95f4\uff0c\u5ffd\u7136\u53d1\u73b0\u5415\u56db\u5a18\u4e0e\u6c88 \u5728\u5bbd\uff0c\u4e0d\u77e5\u5728\u4ec0\u4e48\u65f6\u5019\uff0c\u5df2\u7ecf\u843d\u540e\u6570\u91cc\u3002 \u3000\u3000\u5510\u6653\u6f9c\u4e0e\u7518\u51e4\u6c60\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u5415\u56db\u5a18\u4e0e\u6c88\u5728\u5bbd\u4e24\u5339\u9a6c\u513f\u5e76\u5728\u4e00\u8d77\uff0c\u4fa7\u8eab\u8c08\u7b11\uff0c\u7f13\u7f13\u800c \u884c\uff0c\u771f\u4e2a\u662f\u8033\u9b13\u53ae\u78e8\uff0c\u60c5\u6df1\u6b3e\u6b3e\u3002\u7518\u51e4\u6c60\u5fae\u5fae\u4e00\u7b11\uff0c\u53eb\u4f17\u4eba\u52d2\u7d27\u7ef3\u7d22\uff0c\u653e\u6162\u9a6c\u8e44\u3002 \u3000\u3000\u6c88\u5728\u5bbd\u8654\u5fc3\u6bc5\u529b\uff0c\u7b49\u4e86\u5341\u5e74\uff0c\u8fd9\u65f6\u771f\u662f\u5fc3\u82b1\u6012\u653e\uff0c\u559c\u6781\u5fd8\u8a00\u3002\u5415\u56db\u5a18\u5ae3\u7136\u4e00\u7b11\uff0c\u8f7b\u58f0\u8bf4 \u9053\uff1a\u201c\u8bb0\u5f97\u4f60\u4ece\u524d\u66fe\u96c6\u8fc7\u6b27\u9633\u6c38\u53d4\u7684\u4e24\u53e5\u8bcd\uff1a\u89c1\u4e86\u53c8\u4f11\u8fd8\u4f3c\u68a6\uff0c\u5750\u6765\u867d\u8fd1\u8fdc\u5982\u5929\u3002\u73b0\u5728\u53ef\u8fd8 \u8fd9\u6837\u60f3\u4e48\uff1f\u201d\u6c88\u5728\u5bbd\u9053\uff1a\u201c\u6211\u73b0\u5728\u60f3\u5230\u7684\u662f\u8fd9\u9996\u8bcd\u7684\u524d\u4e24\u53e5\uff1a\u695a\u738b\u53f0\u4e0a\u4e00\u795e\u4ed9\uff0c\u773c\u8272\u76f8\u770b\u610f \u5df2\u4f20\u3002\u4e0d\uff0c\u6211\u73b0\u5728\u53ea\u7fa1\u9e33\u9e2f\u4e0d\u7fa1\u4ed9\uff0c\u695a\u738b\u53f0\u4e0a\u7684\u795e\u4ed9\u4e5f\u672a\u5fc5\u6bd4\u5f97\u4e0a\u6211\u5982\u4eca\u7684\u6b22\u4e50\u3002\u201d\u5415\u56db\u5a18 \u5550\u4e86\u4e00\u53e3\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u5b66\u5f97\u8fd9\u6837\u7684\u8f7b\u8584\u4e86\uff1f\u8c01\u548c\u4f60\u2018\u773c\u8272\u76f8\u770b\u610f\u5df2\u4f20\u2019\u5475\uff1f\u201d\u53e3\u89d2\u6625\u98ce\uff0c\u67d4 \u60c5\u4e07\u79cd\uff0c\u6c88\u5728\u5bbd\u5fc3\u90fd\u9189\u4e86\u3002\u826f\u4e45\u826f\u4e45\uff0c\u624d\u5fae\u5fbd\u541f\u9053\uff1a\u201c\u4f46\u5f97\u660e\u73e0\u660e\u53c8\u5b9a\uff0c\u4e00\u751f\u957f\u5bf9\u6c34\u6676 \u76d8\u3002\u201d\u5415\u56db\u5a18\u7b11\u9053\uff1a\u201c\u4e66\u5446\u5b50\uff0c\u4e0d\u8981\u5c3d\u541f\u8bd7\u4e86\uff0c\u4f60\u770b\u4ed6\u4eec\u90fd\u5728\u671b\u6211\u4eec\u5462\uff01\u201d\u50ac\u9a6c\u8d76\u4e0a\uff0c\u4f46\u89c1 \u51af\u7433\u548c\u674e\u6cbb\u4e5f\u662f\u5728\u5e76\u8f94\u8c08\u5fc3\uff0c\u53ea\u6709\u5510\u6653\u6f9c\u9a70\u51fa\u8def\u65c1\uff0c\u795e\u60c5\u60c6\u6005\uff0c\u51af\u745b\u9ed8\u9ed8\u7684\u8ddf\u5728\u540e\u9762\uff0c\u610f\u6001 \u4e5f\u751a\u4f3c\u832b\u7136\u3002 \u3000\u3000\u5510\u6653\u6f9c\u76ee\u7779\u5415\u56db\u5a18\u4e0e\u6c88\u5728\u5bbd\u4eb2\u70ed\u7684\u795e\u60c5\uff0c\u60f3\u60f3\u81ea\u5df1\u7684\u4e00\u751f\u60c5\u5b7d\uff0c\u4e0d\u89c9\u4f24\u5fc3\u3002\u4ed6\u672c\u6765\u7231\u6781 \u51af\u745b\uff0c\u53ef\u662f\u6709\u4e86\u6768\u67f3\u9752\u8fd9\u6bb5\u4e8b\u63d2\u5728\u4e2d\u95f4\uff0c\u4efb\u5b83\u5c81\u6708\u9891\u66f4\uff0c\u7ec8\u662f\u803f\u803f\u4e8e\u5fc3\uff0c\u96be\u4e8e\u78e8\u706d\u3002\u51af\u745b\u5929 \u771f\u672a\u51ff\uff0c\u867d\u7136\u60f3\u4e0d\u5230\u4fd7\u4e16\u7537\u5973\u4e4b\u60c5\uff0c\u4f46\u89c1\u4ed6\u8fd9\u4e2a\u6837\u5b50\uff0c\u4e5f\u89c9\u60c5\u6000\u60d8\u60d8\uff0c\u4e0d\u77e5\u600e\u6837\u548c\u4ed6\u5f00\u89e3\u3002 \u3000\u3000\u5415\u56db\u5a18\u5fc3\u4e2d\u4e00\u9178\uff0c\u50ac\u9a6c\u4e0a\u524d\u5f3a\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u5f1f\uff0c\u4f60\u53c8\u5728\u60f3\u4ec0\u4e48\u4e86\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u771f \u613f\u662f\u5341\u591a\u5e74\u524d\u90a3\u4e0d\u61c2\u4e8b\u7684\u2018\u5c0f\u5f1f\u5f1f\u2019\u5c11\u4e86\u73b0\u5728\u8fd9\u8bb8\u591a\u51a4\u5b7d\u3002\u201d\u5415\u56db\u5a18\u9053\uff1a\u201c\u5f80\u8005\u5df2\u77e3\uff0c\u6765\u8005 \u53ef\u8ffd\u3002\u6b7b\u8005\u4e0d\u80fd\u590d\u751f\uff0c\u4f60\u53c8\u4f55\u5fc5\u8f9c\u8d1f\u773c\u524d\u8fd9\u5982\u82b1\u7f8e\u7737\uff1f\u201d\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6b64\u60c5\u5df2\u4efd\u968f\u6d41\u6c34\uff0c\u5fcd \u5bf9\u65b0\u4eba\u5fc6\u65e7\u4eba\uff1f\u6211\u4e0e\u6768\u67f3\u9752\u867d\u7136\u65e0\u771f\u60c5\uff0c\u4f46\u5979\u4e3a\u6211\u800c\u6b7b\uff0c\u53eb\u6211\u5982\u4f55\u5fd8\u8bb0\u5f97\u5979\uff1f\u8fd9\u5fc3\u4e8b\u6b64\u751f\u662f \u96be\u4e8e\u653e\u4e0b\u7684\u4e86\u3002\u4f60\u82e5\u53eb\u6211\u6000\u7740\u8fd9\u6837\u7684\u5fc3\u60c5\u4e0e\u51af\u745b\u76f8\u597d\uff0c\u6211\u53c8\u600e\u80fd\u5bf9\u5f97\u4f4f\u5979\uff1f\u201d\u5415\u4e5f\u5a18\u53f9\u4e86\u53e3 \u6c14\uff0c\u5fc3\u75c5\u96be\u533b\uff0c\u786e\u662f\u65e0\u8a00\u53ef\u4ee5\u5f00\u89e3\u3002 \u3000\u3000\u7518\u51e4\u6c60\u54b3\u4e86\u4e00\u58f0\uff0c\u626c\u97ad\u6307\u9053\uff1a\u201c\u4f60\u770b\u770b\uff0c\u54b1\u4eec\u8d70\u5f97\u597d\u5feb\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u5df2\u7ecf\u5230\u4e86\u6768\u8001\u82f1\u96c4 \u7684\u95e8\u524d\u4e86\uff01\u201d\u4f17\u4eba\u4e00\u671b\uff0c\u4f46\u89c1\u5c0f\u5761\u4e0a\u904d\u683d\u6768\u67f3\uff0c\u67f3\u6797\u63a9\u6620\u9732\u51fa\u4e00\u89d2\u7ea2\u5899\uff0c\u666f\u7269\u8fd8\u4f3c\u5f53\u5e74\uff0c\u53ea \u662f\u6768\u4ef2\u82f1\u7236\u5973\u5374\u5df2\u7ecf\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u5510\u6653\u6f9c\u5fc3\u9178\u6cea\u6ef4\uff0c\u4e0e\u4f17\u4eba\u7cfb\u597d\u9a6c\u5339\uff0c\u8d70\u4e0a\u5c71\u5761\uff0c\u53ea\u89c1\u90a3\u8fb9\u5c71\u5761\u4e0b\u9762\u7684\u5c0f\u6e56\uff0c\u53c8\u6b63\u662f\u6e56\u5e73 \u6c34\u6ee1\uff0c\u9aa4\u7136\u60f3\u8d77\u5f53\u65e5\u6768\u67f3\u9752\u88ab\u6d2a\u6ce2\u5377\u8d70\u7684\u60c5\u666f\uff0c\u5386\u5386\u5982\u5728\u76ee\u524d\uff0c\u66f4\u662f\u5fc3\u5934\u96be\u8fc7\u3002\u7518\u51e4\u6c60\u5ffd\u7136 \u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u770b\u95e8\u524d\u6253\u626b\u5f97\u597d\u5e72\u51c0\uff0c\u96be\u9053\u91cc\u9762\u8fd8\u4f4f\u6709\u4eba\u4e48\uff1f\u201d\u51af\u745b\u4e5f\u89c9\u5947\u602a\uff0c\u62c9 \u7740\u5510\u6653\u6f9c\u9053\uff1a\u201c\u6211\u548c\u4f60\u8fdb\u53bb\u770b\u770b\uff0c\u770b\u770b\u662f\u8c01\u66ff\u4ed6\u8001\u4eba\u5bb6\u6253\u626b\u95e8\u5ead\uff1f\u201d\u5510\u6653\u6f9c\u62b9\u4e86\u773c\u6cea\uff0c\u9ed8\u9ed8 \u65e0\u8a00\u7684\u63a8\u5f00\u4e86\u95e8\uff0c\u95e8\u5f00\u5904\u5ffd\u89c1\u4e00\u4e2a\u5c11\u5987\u8d70\u4e86\u51fa\u6765\uff0c\u5510\u6653\u6f9c\u4e0d\u89c9\u9762\u8272\u5927\u53d8\u3002 \u3000\u3000\u8fd9\u5c11\u5987\u6b63\u662f\u6768\u67f3\u9752\uff0c\u5979\u9aa4\u7136\u89c1\u4e86\u5510\u6653\u6f9c\uff0c\u4e5f\u4e0d\u89c9\u800c\u8272\u4e00\u53d8\uff0c\u4e24\u4eba\u5446\u82e5\u6728\u9e21\uff0c\u53c8\u60ca\u53c8\u559c\uff0c \u826f\u4e45\u826f\u4e45\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u6768\u67f3\u9752\u5ffd\u7136\u5c55\u7709\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e09\u5e74\u591a\u4e0d\u89c1\u4e86\uff0c\u4f60\u597d\u5475\uff01\u51af\u745b\u4e5f\u957f \u5f97\u8fd9\u4e48\u9ad8\u4e86\uff01\u201d\u62a2\u524d\u6765\u62c9\u51af\u745b\u7684\u624b\uff0c\u795e\u6001\u663e\u5f97\u65e2\u8c6a\u8fc8\uff0c\u53c8\u4eb2\u70ed\uff0c\u5510\u6653\u6f9c\u4e0d\u7981\u5927\u5947\uff0c\u60f3\u4e0d\u5230\u5979 \u5b8c\u5168\u53d8\u4e86\uff01\u51af\u745b\u559c\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u90a3\u65e5\u4f60\u88ab\u5c71\u6d2a\u5377\u53bb\uff0c\u771f\u53eb\u6211\u4eec\u62c5\u5fc3\uff0c\u73b0\u5728\u53ef\u597d\u4e86\uff0c\u4f60\uff0c\u4f60 \u4eec\u2026\u2026\u201d\u51af\u745b\u5f97\u89c1\u6768\u67f3\u9752\u751f\u8fd8\uff0c\u4e43\u662f\u8877\u5fc3\u6b22\u559c\uff0c\u8fd9\u4e2a\u65f6\u95f4\uff0c\u5979\u5168\u7136\u628a\u81ea\u5df1\u7684\u79c1\u60c5\u629b\u5728\u4e00\u8fb9\uff0c \u6b63\u60f3\u4e3a\u4ed6\u4eec\u7684\u91cd\u9022\u800c\u5e86\u8d3a\uff0c\u53ef\u662f\u8bdd\u521a\u51fa\u53e3\uff0c\u53c8\u4e0d\u77e5\u600e\u6837\u63aa\u8bcd\uff0c\u9762\u4e0a\u98de\u8d77\u4e00\u7247\u7ea2\u971e\uff0c\u6768\u67f3\u9752\u5ffd \u7136\u7b11\u9053\uff1a\u201c\u6653\u6f9c\uff0c\u8fd9\u91cc\u8fd8\u6709\u4e00\u4e2a\u4f60\u8ba4\u8bc6\u7684\u8001\u670b\u53cb\u3002\u201d\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u9521\u4e5d\uff0c\u548c\u971e\u513f\u51fa\u6765\uff01\u201d\u91cc \u9762\u5e94\u58f0\u8d70\u51fa\u4e00\u4eba\uff0c\u6b63\u662f\u5f53\u5e74\u5411\u6768\u67f3\u9752\u6c42\u5a5a\u4e0d\u9042\u7684\u90b9\u9521\u4e5d\uff0c\u4ed6\u6000\u4e2d\u8fd8\u62b1\u7740\u4e00\u4e2a\u7ea6\u83ab\u4e24\u5c81\u5927\u7684\u5973 \u5b69\u5b50\uff0c\u821e\u7740\u4e24\u53ea\u5c0f\u624b\uff0c\u5728\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u53d4\u53d4\u201d\u3002 \u3000\u3000\u539f\u6765\u6768\u67f3\u9752\u5c4b\u540e\u7684\u5c0f\u6e56\uff0c\u901a\u5411\u5916\u9762\u6cfa\u6cb3\uff0c\u65e0\u5de7\u4e0d\u5de7\uff0c\u90a3\u65e5\u6768\u67f3\u9752\u88ab\u5c71\u6d2a\u5377\u53bb\uff0c\u51b2\u5230\u6cfa \u6cb3\uff0c\u6b63\u597d\u201c\u63d2\u7ffc\u795e\u72ee\u201d\u90b9\u9e23\u768b\u548c\u4ed6\u7684\u513f\u5b50\u90b9\u9521\u4e5d\uff0c\u56e0\u4e3a\u542c\u5230\u6768\u4ef2\u82f1\u6b8b\u5e9f\u7684\u6d88\u606f\uff0c\u81ea\u6cfa\u6cb3\u4e58\u8239 \u800c\u4e0b\uff0c\u524d\u6765\u63a2\u8bbf\u8001\u53cb\uff0c\u5c06\u5979\u6551\u8d77\uff0c\u8d39\u4e86\u5927\u534a\u5929\u7684\u65f6\u95f4\u6551\u6cbb\uff0c\u6768\u67f3\u9752\u624d\u60a0\u60a0\u9192\u8f6c\uff0c\u53ef\u662f\u56e0\u4e3a\u88ab \u5c71\u6d2a\u51b2\u51fb\uff0c\u53d7\u4e86\u91cd\u4f24\uff0c\u53ea\u5f97\u5728\u90b9\u9521\u4e5d\u7684\u8239\u4e2d\u517b\u75c5\uff0c\u8fd9\u65f6\u6768\u67f3\u9752\u5fc3\u7075\u53d7\u4e86\u6781\u5927\u7684\u521b\u4f24\uff0c\u4e0d\u613f\u518d \u56de\u53bb\u89c1\u5510\u6653\u6f9c\uff0c\u5230\u517b\u597d\u75c5\u65f6\uff0c\u5510\u6653\u6f9c\u5df2\u7ecf\u548c\u51af\u745b\u5230\u5929\u5c71\u53bb\u4e86\u3002 \u3000\u3000\u90b9\u9521\u4e5d\u5bf9\u6768\u67f3\u9752\u8fd8\u6ca1\u6709\u5b8c\u5168\u5fd8\u60c5\uff0c\u5728\u5979\u517b\u75c5\u671f\u95f4\uff0c\u4e3a\u5979\u767e\u822c\u770b\u62a4\uff0c\u6768\u67f3\u9752\u8fd9\u51e0\u5e74\u6765\u89c9\u5bdf \u5230\u5510\u6653\u6f9c\u7231\u7684\u5b9e\u662f\u51af\u745b\uff0c\u5728\u75c5\u4e2d\u601d\u524d\u60f3\u540e\uff0c\u89c9\u5f97\u5510\u6653\u6f9c\u65e2\u65e0\u5fc3\u4e8e\u5df1\uff0c\u8fd9\u75f4\u60c5\u7737\u604b\u4e5f\u5b9e\u5728\u6ca1\u6709 \u4ec0\u4e48\u610f\u601d\uff0c\u52a0\u4e4b\u65e5\u4e45\u60c5\u751f\uff0c\u5728\u75c5\u4e2d\u5c24\u5176\u6613\u5bf9\u7231\u62a4\u81ea\u5df1\u7684\u4eba\u53d1\u751f\u60c5\u610f\uff0c\u4e8e\u662f\u5230\u4e86\u75c5\u597d\u4e4b\u540e\uff0c\u5979 \u548c\u90b9\u9521\u4e5d\u7684\u7231\u82d7\u4e5f\u5df2\u57f9\u517b\u8d77\u6765\u3002\u5510\u6653\u6f9c\u4ee5\u524d\u66fe\u6709\u4fe1\u7ed9\u8fc7\u6768\u4ef2\u82f1\u63d0\u8bae\u89e3\u9664\u5a5a\u7ea6\uff0c\u6768\u4ef2\u82f1\u4e34\u6b7b\u9057 \u8a00\u4e5f\u66fe\u7b54\u5e94\u8ba9\u4ed6\u4eec\u81ea\u884c\u9009\u62e9\uff0c\u56e0\u4e4b\u5979\u6263\u90b9\u9521\u4e5d\u7684\u5a5a\u4e8b\u4fbf\u987a\u7406\u6210\u7ae0\uff0c\u4e0d\u5fc5\u518d\u5f81\u6c42\u5510\u6653\u6f9c\u7684\u540c\u610f \u4e86\u3002 \u3000\u3000\u8fd9\u53d8\u5316\u5927\u51fa\u5510\u6653\u6f9c\u610f\u6599\u4e4b\u5916\uff0c\u60f3\u4e0d\u5230\u591a\u5e74\u6765\u5fc3\u5934\u4e0a\u7684\u4e00\u5757\u5fc3\u75c5\u7adf\u7136\u4e00\u4e0b\u89e3\u5f00\uff0c\u800c\u4e14\u89e3\u51b3 \u5f97\u8fd9\u4e48\u5706\u6ee1\u3002\u4ed6\u60c5\u4e0d\u81ea\u7981\u7684\u63e1\u4f4f\u6768\u67f3\u9752\u7684\u624b\u8877\u8bda\u9053\u8d3a\uff0c\u540c\u65f6\u773c\u89d2\u8198\u7740\u51af\u745b\uff0c\u76f8\u601d\u4e07\u79cd\uff0c\u90fd\u5c3d \u5728\u4e0d\u8a00\u4e4b\u4e2d\u3002 \u3000\u3000\u4f17\u4eba\u5728\u6768\u67f3\u9752\u5bb6\u4e2d\u4f4f\u4e86\u51e0\u65e5\uff0c\u5404\u5404\u6563\u53bb\u3002\u51af\u745b\u51af\u7433\u5510\u6653\u6f9c\u674e\u6cbb\u56de\u8f6c\u5929\u5c71\uff0c\u5415\u56db\u5a18\u548c\u6c88\u5728 \u5bbd\u7ed3\u5a5a\u540e\u9690\u5c45\u9099\u5c71\uff0c\u4e60\u6b66\u4fee\u6587\uff0c\u4eab\u4eba\u95f4\u6e05\u798f\u3002\u7518\u51e4\u6c60\u5219\u6210\u4e3a\u4e00\u4ee3\u7684\u6b66\u5b66\u5927\u5e08\uff0c\u4f20\u6388\u4e86\u8bb8\u591a\u5f1f \u5b50\u3002\u201c\u6c5f\u6e56\u4e09\u5973\u4fa0\u201d\u4e00\u6837\u98d8\u96f6\u8eab\u4e16\uff0c\u5374\u53c8\u4e00\u6837\u5f97\u5230\u6700\u7f8e\u597d\u7684\u6536\u573a\u3002\u8bfb\u8005\u8bf8\u541b\uff0c\u60f3\u5fc5\u4e5f\u4e00\u6837\u7684 \u4e3a\u5979\u4eec\u611f\u5230\u6b23\u6170\u4e86\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u4f3c\u6c34\u67d4\u60c5\uff0c\u5982\u82b1\u7f8e\u7737\uff0c\u5343\u79cb\u4f73\u8bdd\u4eba\u4e89\u7fa1\uff0c \u3000\u3000\u4f9d\u4eba\u71d5\u5b50\u53c8\u5f52\u6765\uff0c\u6ca7\u6851\u53d8\u4e86\u5fc3\u96be\u53d8\u3002 \u3000\u3000\u67f3\u7d6e\u8f7b\u98d8\uff0c\u6625\u98ce\u62c2\u9762\uff0c\u8bcd\u7b3a\u4e0d\u5199\u6587\u541b\u6028\uff0c \u3000\u3000\u6c5f\u5357\u585e\u5916\u4e00\u822c\u540c\uff0c\u78a7\u6ce2\u6df1\u5212\u9e33\u9e2f\u89c1\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u2014\u2014\u8c03\u5bc4\u8e0f\u838e\u884c \u3000\u3000\uff08\u5168\u4e66\u5b8c\uff09"}),showOnHomepage:!0}]}}]);