"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[2925],{16277:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u4e03\u5251\u4e0b\u5929\u5c71",content:(0,t.jsx)(t.Fragment,{children:'\u4e00\u5973\u72ec\u5bfb\u4ec7\u3000\u5341\u516d\u5e74\u95f4\u7ecf\u51e0\u52ab \u7fa4\u96c4\u9f50\u51fa\u624b\u3000\u4e94\u53f0\u5c71\u4e0a\u9707\u4e09\u519b \u3000\u3000\u5c71\u897f\u4e94\u53f0\u5c71\u662f\u8457\u540d\u7684\u4f5b\u6559\u5723\u5730\uff0c\u5176\u4e0a\u7684\u6e05\u51c9\u5bfa\uff0c\u636e\u8bf4\u662f\u4e1c\u6c49\u65f6\u6240\u5efa\uff0c\u5343\u4f59\u5e74\u6765\uff0c\u9999\u706b\u4e0d \u8870\u3002\u81ea\u6e05\u671d\u5eb7\u7199\u7687\u5e1d\u767b\u4f4d\u4ee5\u540e\uff0c\u51e0\u6b21\u4e0a\u4e94\u53f0\u5c71\u793c\u4f5b\uff0c\u91cd\u4fee\u53e4\u5239\uff0c\u518d\u5efa\u91d1\u8eab\uff0c\u66f4\u628a\u4e94\u53f0\u5c71\u7684\u7075 \u6500\u5cf0\u4e0b\uff0c\u53d8\u6210\u4e86\u4f5b\u6559\u6700\u5927\u7684\u4e1b\u6797\u3002 \u3000\u3000\u8fd9\u4e00\u5e74\u662f\u5eb7\u7199\u5341\u4e09\u5e74\uff0c\u6b63\u5de7\u78b0\u4e0a\u6e05\u51c9\u5bfa\u6587\u6b8a\u83e9\u8428\u7684\u5f00\u5149\u5927\u5178\uff0c\u5927\u5178\u5728\u4e09\u6708\u4e8c\u5341\u4e5d\u4e3e\u884c\uff0c \u53ef\u662f\u65b9\u8fc7\u4e86\u5e74\uff0c\u5584\u7537\u4fe1\u5973\u5df2\u81ea\u5404\u5730\u800c\u6765\uff0c\u5c71\u4e0a\u7684\u4e94\u4e2a\u5927\u94dc\u5854\uff0c\u6bcf\u5c42\u90fd\u5d4c\u6ee1\u4f5b\u706f\uff0c\u4ece\u65b0\u6b63\u8d77\u5c31 \u663c\u591c\u901a\u660e\uff0c\u771f\u662f\u6bbf\u5b57\u91d1\u78a7\uff0c\u5999\u76f8\u5e84\u4e25\u3002 \u3000\u3000\u4e34\u5230\u5f00\u5149\u5927\u5178\u8fd9\u5929\uff0c\u8fd9\u4efd\u70ed\u95f9\u66f4\u4e0d\u7528\u63d0\u5566\uff0c\u4e00\u5927\u6e05\u65e9\uff0c\u5c71\u5c97\u3001\u677e\u6797\u3001\u5ce1\u8c37\u3001\u5e7d\u6da7\uff0c\u90fd\u6324 \u6ee1\u4e86\u4eba\uff0c\u6709\u7684\u662f\u4f5b\u6559\u4fe1\u5f92\uff0c\u6709\u7684\u662f\u4e13\u7a0b\u6765\u89c2\u5149\u770b\u70ed\u95f9\u7684\u4eba\u3002 \u3000\u3000\u5728\u8fd9\u4e9b\u4eba\u4e2d\uff0c\u6709\u4e00\u4e2a\u4e09\u7efa\u957f\u987b\u3001\u9762\u8272\u7ea2\u6da6\u3001\u5112\u51a0\u5112\u670d\u7684\u8001\u4eba\uff0c\u548c\u4ed6\u540c\u6765\u7684\u662f\u4e00\u4e2a\u4fca\u4fcf\u7684 \u7f8e\u5c11\u5e74\uff0c\u8bf4\u8bdd\u5374\u5e26\u7740\u5973\u97f3\u3002\u8fd9\u4e24\u4e2a\u4eba\u8bf4\u6765\u5927\u6709\u6765\u5934\u3002\u5112\u51a0\u8001\u83e9\u540d\u53eb\u535a\u9752\u4e3b\uff0c\u4e0d\u4f46\u533b\u672f\u7cbe\u5999\uff0c \u5929\u4e0b\u65e0\u5339\uff0c\u800c\u4e14\u957f\u4e8e\u6b66\u529f\uff0c\u5728\u65e0\u6781\u5251\u6cd5\u4e0a\u6709\u7cbe\u6df1\u9020\u8be3\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6\u8fd8\u662f\u4e66\u753b\u540d\u5bb6\uff0c\u662f\u660e\u672a \u6e05\u521d\u7684\u4e00\u4f4d\u5947\u58eb\u3002 \u3000\u3000\u90a3\u7f8e\u5c11\u5e74\u5374\u662f\u4e00\u4f4d\u5973\u626e\u7537\u88c5\u7684\u5c0f\u59d0\uff0c\u540d\u53eb\u5192\u6d63\u83b2\u3002\u5979\u7684\u7236\u4eb2\u53eb\u5192\u8f9f\u7586\uff0c\u4e5f\u662f\u660e\u672a\u6e05\u521d\u7684 \u4e00\u4f4d\u5927\u540d\u58eb\uff0c\u5f53\u65f6\u7684\u540d\u5993\u8463\u5c0f\u5b9b\u6155\u4ed6\u4e4b\u624d\uff0c\u81ea\u613f\u505a\u4ed6\u7684\u4f8d\u59ec\u3002\u8463\u5c0f\u5b9b\u4e5f\u662f\u8bd7\u8bcd\u523a\u7ee3\u4e24\u4ff1\u7cbe\u5999 \u7684\u624d\u5973\uff0c\u4e24\u4eba\u610f\u6c14\u76f8\u6295\uff0c\u5341\u5206\u4eb2\u7231\u3002\u4e0d\u6599\u540e\u6765\u56e0\u8463\u5c0f\u5b9b\u8273\u540d\u8fdc\u64ad\uff0c\u7adf\u7ed9\u6d2a\u627f\u7574\u62a2\u8ff8\u5bab\u53bb\uff0c\u732e \u7ed9\u987a\u6cbb\u7687\u5e1d\uff0c\u88ab\u5c01\u8d35\u5983\u3002\u5192\u8f9f\u7586\u5931\u53bb\u8463\u5c0f\u5b9b\u4e4b\u540e\uff0c\u7ec8\u65e5\u90c1\u90c1\u5be1\u6b22\uff0c\u7adf\u5c14\u6291\u90c1\u544a\u7ec8\u3002 \u3000\u3000\u5085\u9752\u4e3b\u662f\u5192\u8f9f\u7586\u751f\u5e73\u631a\u53cb\uff0c\u5192\u8f9f\u7586\u6b7b\u65f6\uff0c\u5192\u6d63\u83b2\u4e0d\u8fc7\u4e09\u5c81\uff0c\u56e0\u4e3a\u5979\u7684\u8eab\u4e16\u53e6\u6709\u590d\u6742\u4e4b \u5904\uff0c\u5192\u8f9f\u7586\u6015\u5979\u53d7\u65cf\u4eba\u6b67\u89c6\uff0c\u4fbf\u6258\u5085\u9752\u4e3b\u7167\u6599\u3002\u56e0\u6b64\u5192\u6d63\u83b2\u81ea\u5e7c\u8ddf\u968f\u8fd9\u4f4d\u4e16\u4f2f\uff0c\u5012\u4e5f\u5b66\u4e86\u4e00 \u8eab\u6b66\u827a\u3002 \u3000\u3000\u8fd9\u5929\u6e05\u65e9\uff0c\u4e24\u4eba\u4e5f\u968f\u4f17\u89c2\u5149\u3002\u5085\u9752\u4e3b\u5de6\u987e\u53f3\u76fc\uff0c\u597d\u50cf\u5174\u8da3\u5f88\u9ad8\uff1b\u800c\u5192\u6d63\u83b2\u5219\u9762\u5bb9\u6c89\u90c1\uff0c \u597d\u50cf\u6709\u5f88\u5927\u7684\u5fc3\u4e8b\u3002\u5085\u9752\u4e3b\u5728\u987e\u76fc\u4e4b\u95f4\uff0c\u5ffd\u7136\u5fae\u54a6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u83b2\u513f\uff0c\u4f60\u770b\u90a3\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u62ac\u5934\u4e00\u770b\uff0c\u4e0d\u89c9\u5413\u4e86\u4e00\u8df3\uff0c\u539f\u6765\u524d\u9762\u7684\u4e24\u4eba\uff0c\u4e00\u4e2a\u6d3b\u50cf\u540a\u6b7b\u9b3c\uff01\u8eab\u957f\u4e03\u5c3a\u6765\u9ad8\uff0c \u7626\u524a\u5f97\u50cf\u4e00\u679d\u4fee\u7af9\uff0c\u9762\u8272\u53c8\u662f\u767d\u60e8\u60e8\u7684\uff0c\u602a\u662f\u5413\u4eba\uff1b\u53e6\u4e00\u4e2a\u5374\u80a5\u80a5\u77ee\u77ee\uff0c\u5934\u5927\u5982\u6597\uff0c\u5934\u9876\u5374 \u662f\u5149\u79c3\u79c3\u7684\u3002 \u3000\u3000\u5192\u6d63\u83b2\u672c\u6765\u5f88\u662f\u6c89\u90c1\uff0c\u77a7\u89c1\u8fd9\u4e24\u4e2a\u4eba\u7684\u602a\u76f8\uff0c\u4e00\u60ca\u8fc7\u540e\uff0c\u4e0d\u89c9\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u7b11\u4e86\u51fa \u6765\u3002\u90a3\u4e24\u4eba\u542c\u89c1\u7b11\u58f0\uff0c\u56de\u8fc7\u8eab\u6765\uff0c\u77aa\u773c\u5f85\u627e\uff0c\u5085\u9752\u4e3b\u5fd9\u62c9\u5979\u7684\u8863\u8896\uff0c\u5728\u4eba\u4e1b\u4e2d\u6df7\u8fc7\uff0c\u7136\u540e\u4f4e \u4f4e\u5730\u544a\u8bc9\u5979\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\u4e43\u662f\u6c5f\u6e56\u4e0a\u6709\u540d\u4eba\u7269\uff0c\u9ad8\u7684\u90a3\u4e2a\u53eb\u4e27\u95e8\u795e\u5e38\u82f1\uff0c\u77ee\u7684\u90a3\u4e2a\u53eb\u94c1\u5854 \u7a0b\u901a\u3002\u4f60\u6709\u4e8b\u8981\u529e\uff0c\u4f55\u5fc5\u53bb\u60f9\u8fd9\u4e24\u4e2a\u6d3b\u5b9d\uff1f\u201d \u3000\u3000\u4e24\u4eba\u884c\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u5192\u6d63\u83b2\u53c8\u662f\u8f7b\u8f7b\u5730\u602a\u53eb\u4e00\u58f0\uff0c\u5bf9\u5085\u9752\u4e3b\u8bf4\uff1a\u201c\u4f2f\u4f2f\uff0c\u4f60\u770b\u90a3\u4e2a\u548c \u5c1a\uff01\u201d\u5085\u9752\u4e3b\u4f9d\u7740\u6240\u6307\u65b9\u5411\u7740\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u65b9\u9762\u5927\u8033\u7684\u548c\u5c1a\u7ad9\u5728\u4eba\u4e1b\u4e4b\u4e2d\uff0c\u5468\u56f4\u7684\u4eba\u867d\u7136\u4f60 \u63a8\u6211\u62e5\uff0c\u5374\u603b\u662f\u6328\u4e0d\u8fd1\u90a3\u4e2a\u548c\u5c1a\uff0c\u4ed6\u4e00\u8d70\u52a8\uff0c\u5468\u56f4\u7684\u4eba\u5c31\u4f3c\u4e4e\u81ea\u52a8\u7ed9\u4ed6\u8ba9\u8def\u4e00\u6837\uff0c\u603b\u632a\u51fa\u4e00 \u70b9\u7a7a\u9699\u6765\uff0c\u5085\u9752\u4e3b\u770b\u4e86\uff0c\u4e0d\u7981\u53c8\u662f\u5fae\u201c\u54a6\u201d\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u4e2a\u91ce\u548c\u5c1a\u4e5f\u6765\u4e86\uff0c\u8fd9\u4e2a\u548c \u5c1a\u4ece\u6765\u4e0d\u5ff5\u7ecf\u793c\u4f5b\uff0c\u4e5f\u4e0d\u6212\u8364\u8165\uff0c\u4e13\u95e8\u6b22\u559c\u5728\u6c5f\u6e56\u4e0a\u7ba1\u95f2\u4e8b\uff0c\u4eba\u79f0\u4ed6\u4e3a\u602a\u5934\u9640\u901a\u660e\u548c\u5c1a\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4e1c\u9762\u5c71\u5773\u53c8\u8fc7\u6765\u4e00\u7c07\u4eba\uff0c\u6709\u51e0\u4e2a\u6c49\u5b50\uff0c\u7275\u7740\u7334\u513f\uff0c\u80cc\u7740\u5200\u67aa\uff0c\u6253\u9523\u6253\u9f13\u7684\uff0c\u4f3c\u4e4e\u662f \u5356\u89e3\u827a\u4eba\u3002\u4e3a\u9996\u7684\u4e00\u4e2a\u5987\u4eba\uff0c\u867d\u7136\u8346\u9497\u88d9\u5e03\uff0c\u53ef\u662f\u5374\u4eea\u6001\u4e07\u65b9\uff0c\u5bb9\u5149\u903c\u4eba\uff0c\u5f88\u6709\u70b9\u8d35\u5987\u7684\u98ce \u97f5\u3002\u5085\u9752\u4e3b\u77a7\u4e86\u4e00\u773c\uff0c\u4fcf\u6084\u5730\u5bf9\u5192\u6d63\u83b2\u9053\uff1a\u201c\u8fd9\u4e2a\u5987\u4eba\u4e0d\u662f\u5bfb\u5e38\u7684\u5356\u89e3\u5973\u5b50\uff0c\u77a7\u5979\u7684\u773c\u795e\uff0c \u8db3\u6709\u4e8c\u4e09\u5341\u5e74\u7684\u5185\u5bb6\u529f\u529b\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u548c\u5192\u6d63\u83b2\u4e00\u8def\u8c08\u4e00\u8def\u8d70\uff0c\u4e0d\u89c9\u8d8a\u8fc7\u597d\u51e0\u5806\u4eba\u3002\u524d\u9762\u90a3\u4e2a\u602a\u5934\u9640\u4e5f\u884c\u884c\u4f01\u4f01\uff0c\u4e1c\u5f20 \u897f\u671b\u3002\u5085\u9752\u4e3b\u4e0d\u613f\u548c\u4ed6\u7167\u9762\uff0c\u6b63\u60f3\u62c9\u5192\u6d63\u83b2\u4ece\u65c1\u7684\u8def\u8d70\uff0c\u5ffd\u89c1\u4e00\u4e2a\u5c11\u5e74\uff0c\u597d\u50cf\u662f\u53d1\u73b0\u90a3\u602a\u5934 \u9640\u7684\u8e2a\u8ff9\uff0c\u4e0d\u670d\u6c14\u4f3c\u7684\uff0c\u6545\u610f\u5411\u524d\u649e\u53bb\u3002\u5085\u9752\u4e3b\u6697\u6697\u8bf4\u4e86\u4e00\u58f0\uff1a\u201c\u8981\u7cdf\uff01\u201d\u53ea\u89c1\u901a\u660e\u548c\u5c1a\u53cc \u80a9\u4e00\u8038\uff0c\u90a3\u4e2a\u5c11\u5e74\u8dcc\u8dcc\u649e\u649e\u5730\u6536\u4e0d\u4f4f\u811a\u6b65\u76f4\u649e\u51fa\u6765\uff0c\u4e00\u8fde\u78b0\u5230\u4e86\u51e0\u4e2a\u4eba\uff0c\u76f4\u649e\u5230\u5192\u6d63\u83b2\u8eab \u4e0a\uff0c\u90a3\u4e2a\u5c11\u5e74\u4f3c\u662f\u7ed9\u649e\u5f97\u53d1\u6025\u4e86\uff0c\u4e0d\u5047\u601d\u7d22\u5730\u4e00\u624b\u5411\u5192\u6d63\u83b2\u6293\u6765\uff0c\u60f3\u5c06\u8eab\u5f62\u5b9a\u4f4f\u3002\u4e0d\u6599\u8fd9\u4e00 \u624b\u6293\u53bb\uff0c\u6b63\u662f\u671d\u7740\u5192\u6d63\u83b2\u7684\u80f8\u90e8\uff0c\u5192\u6d63\u83b2\u6ee1\u9762\u901a\u7ea2\uff0c\u4f38\u624b\u5c31\u662f\u4e00\u683c\uff0c\u53cc\u81c2\u76f8\u4ea4\uff0c\u53ea\u89c9\u6765\u4eba\u6c14 \u529b\u751a\u5927\uff0c\u81ea\u4e0a\u672c\u60f3\u7528\u65e0\u6781\u638c\u7684\u64d2\u62ff\u6cd5\u5c06\u4ed6\u6454\u5012\uff0c\u5374\u7ed9\u4ed6\u53cd\u624b\u6293\u4f4f\u624b\u81c2\uff0c\u7f9e\u5f97\u5192\u6d63\u83b2\u53cc\u81c2\u4e00 \u632f\uff0c\u8fd0\u7528\u5185\u529b\uff0c\u5c06\u5c11\u5e74\u76f4\u903c\u51fa\u53bb\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u8d81\u7740\u4e00\u6293\u4e4b\u529b\uff0c\u5df2\u5c06\u8eab\u5f62\u5b9a\u4f4f\uff0c\u867d\u7ed9\u5192\u6d63\u83b2\u903c\u9000\uff0c\u5374\u4e0d\u518d\u8dcc\u8dcc\u649e\u649e\u4e86\u3002\u53ea\u662f\u4ed6\u521a \u624d\u4e00\u624b\u6293\u795d\u558a\u6d63\u83b2\u7684\u81c2\u8180\uff0c\u611f\u89c9\u6ed1\u817b\u817b\u7684\uff0c\u4f3c\u4e4e\u662f\u4e2a\u5973\u5b50\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u5b9a\u4f4f\u8eab\u5f62\u4e4b\u540e\uff0c\u6025\u5fd9 \u56de\u8fc7\u8eab\u6765\u9053\u6b49\uff0c\u89c1\u5192\u6d63\u83b2\u662f\u4e2a\u5c11\u5e74\uff0c\u624d\u653e\u4e86\u5fc3\u3002\u5192\u6d63\u83b2\u8fd9\u65f6\u770b\u6e05\u695a\u8fd9\u4e2a\u5c11\u5e74\uff0c\u89c1\u5730\u5ed3\u5982\u800c \u7389\uff0c\u6e29\u6587\u4e4b\u4e2d\u5e26\u7740\u82f1\u6c14\uff0c\u4e0d\u7531\u5f97\u53c8\u662f\u6ee1\u9762\u98de\u7ea2\uff0c\u89c1\u5c11\u5e74\u8d54\u7f6a\uff0c\u6ca1\u5948\u4f55\u53ea\u5f97\u8fd8\u4e86\u4e00\u63d6\u3002 \u3000\u3000\u90a3\u4e2a\u548c\u5c1a\u8fd9\u65f6\u8f6c\u8fc7\u5934\u6765\uff0c\u5411\u5c11\u5e74\u54c8\u54c8\u7b11\u9053\uff0c\u201c\u649e\u4f60\u4e0d\u5012\uff0c\u7b97\u4f60\u672c\u4e8b\uff0c\u54b1\u4eec\u4ee5\u540e\u518d\u89c1\u3002\u201d \u5085\u9752\u4e3b\u5728\u548c\u5c1a\u8f6c\u5934\u65f6\uff0c\u5df2\u628a\u5934\u522b\u8fc7\u4e00\u8fb9\uff0c\u603b\u7b97\u6ca1\u6709\u4eae\u76f8\u3002 \u3000\u3000\u98ce\u6ce2\u8fc7\u540e\uff0c\u5085\u5192\u4e8c\u4eba\uff0c\u53c8\u662f\u8fb9\u8c08\u8bba\u884c\u3002\u4e0d\u4e45\u5c31\u5230\u4e86\u5c71\u4e0a\u3002\u53ea\u89c1\u5bfa\u524d\u5927\u961f\u65d7\u5175\uff0c\u5206\u5217\u5de6 \u77f3\uff0c\u5bfa\u524d\u4e24\u4e09\u4e08\u65b9\u5706\u4e4b\u5730\uff0c\u5374\u662f\u7a7a\u8361\u8361\u7684\uff0c\u4e00\u4e2a\u4eba\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5192\u6d63\u83b2\u6b63\u89c9\u5f97\u60ca\u5f02\uff0c\u53ea\u542c\u5f97\u65c1\u8fb9\u7684\u4eba\u4e5f\u5728\u5431\u5431\u55b3\u55b3\u7684\u8c08\u8bba\u3002\u4e00\u4e2a\u8001\u8005\u8bf4\uff1a\u201c\u770b\u6765\u8fd9\u6b21\u7687 \u4e0a\u4e0d\u4f1a\u4eb2\u6765\u4e86\uff0c\u65e2\u6ca1\u6709\u9ec4\u7eeb\u94fa\u9053\uff0c\u4e5f\u6ca1\u6709\u4eea\u4ed7\u961f\uff0c\u8fde\u5b88\u536b\u5728\u5bfa\u95e8\u7684\u4e5f\u53ea\u6709\u8fd9\u4e48\u5be5\u5be5\u51e0\u5341\u4e2a \u4eba\u3002\u201d\u53e6\u4e00\u4e2a\u597d\u50cf\u4e61\u7ec5\u6a21\u6837\u7684\u4eba\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u4e8b\u8981\u95ee\u6211\u4eec\u624d\u77e5\u9053\uff0c\u7687\u4e0a\u524d\u51e0\u6b21\u6765\u8fdb\u9999\u90fd\u662f \u6211\u4eec\u7ec5\u887f\u63a5\u9a7e\u3002\u8fd9\u6b21\u662f\u9102\u4eb2\u738b\u591a\u94ce\u4ee3\u8868\u7687\u4e0a\u6765\uff0c\u9102\u4eb2\u738b\u4e00\u5411\u4e0d\u6b22\u559c\u94fa\u5f20\uff0c\u4ed6\u51fa\u5de1\u65f6\uff0c\u6709\u65f6\u53ea \u5e26\u51e0\u4e2a\u4eb2\u5175\u54e9\uff01\u201d\u53c8\u4e00\u4e2a\u5e26\u7740\u6c5f\u6d59\u53e3\u97f3\u7684\u5546\u8d3e\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u9102\u4eb2\u738b\u591a\u94ce\uff0c\u662f\u4e0d\u662f\u5341\u591a\u5e74\u524d \u505a\u8fc7\u4e24\u6c5f\u63d0\u7763\u7684\u591a\u94ce\uff01\u8bb0\u5f97\u4ed6\u90a3\u65f6\u5728\u676d\u5dde\u5927\u5a5a\uff0c\u90a3\u624d\u53eb\u70ed\u95f9\u54e9\u3002\u53ea\u662f\u5728\u5927\u5a5a\u524d\u5915\uff0c\u524d\u671d\u7684\u9c81 \u738b\u4f59\u90e8\u52ab\u72f1\uff0c\u95f9\u5f97\u6ee1\u57ce\u98ce\u96e8\uff0c\u7b2c\u4e8c\u5929\u5927\u5a5a\uff0c\u8001\u767e\u7272\u4eec\u90fd\u4e0d\u6562\u53bb\u770b\u70ed\u95f9\u3002\u201d\u90a3\u4e2a\u4e61\u7ec5\u7b11\u9053\uff1a \u201c\u4f60\u5439\u725b\u5439\u51fa\u7834\u7efd\u6765\u4e86\uff0c\u65e2\u7136\u90fd\u4e0d\u6562\u53bb\u770b\uff0c\u4f60\u53c8\u600e\u77e5\u4ed6\u7684\u5927\u5a5a\u70ed\u95f9\uff1f\u5582\uff0c\u4ed6\u5927\u5a5a\u524d\u5915\u7684\u52ab\u72f1 \u4e8b\u60c5\u662f\u600e\u6837\u7684\uff1f\u4f60\u8bf4\u8bf4\u770b\u3002\u201d\u90a3\u5546\u4eba\u5148\u662f\u9762\u7ea2\u7ea2\u5730\u5e94\u4e86\u4e00\u58f0\uff1a\u201c\u662f\u6211\u80c6\u5927\uff0c\u5728\u95e8\u7f1d\u91cc\u5077\u770b \u54e9\u3002\u201d\u8ddf\u7740\u89c1\u4e61\u7ec5\u5bf9\u52ab\u72f1\u4e8b\u60c5\u5f88\u6709\u5174\u8da3\uff0c\u4e5f\u5c31\u5f97\u610f\u6d0b\u6d0b\u5730\u62c9\u4ed6\u8fc7\u4e00\u65c1\u54c7\u5566\u5431\u5566\u5730\u8c08\u8d77\u6765\u3002 \u3000\u3000\u5192\u6d63\u83b2\u89c1\u4ed6\u4eec\u8c08\u8bba\u4e0d\u76f8\u5e72\u7684\u95f2\u4e8b\uff0c\u61d2\u5f97\u6ce8\u610f\u3002\u8fd9\u65f6\u53c8\u542c\u5f97\u65c1\u8fb9\u6709\u4e24\u4e2a\u79c0\u624d\u6a21\u6837\u7684\u4eba\u8c08\u8bba \u9053\uff1a\u201c\u4e0d\u77e5\u4f55\u6545\u5f53\u4eca\u7687\u4e0a\u5bf9\u4e94\u53f0\u5c71\u7279\u522b\u6709\u5174\u8da3\uff0c\u767b\u4f4d\u4e0d\u4e45\uff0c\u5c31\u63a5\u8fde\u6765\u4e86\u51e0\u6b21\uff0c\u8fd9\u6b21\u5f00\u5149\u5927\u5178 \u5374\u53c8\u4e0d\u6765\u3002\u5582\uff0c\u542c\u8bf4\u5927\u8bd7\u4eba\u5434\u6885\u6751\u6709\u4e00\u9996\u8bd7\u5c31\u662f\u548f\u7687\u4e0a\u6765\u4e94\u53f0\u5c71\u8fdb\u9999\u7684\uff0c\u4f60\u8bb0\u5f97\u4e48\uff1f\u201d\u4ed6\u7684 \u540c\u4f34\u8bf4\uff1a\u201c\u6211\u4ece\u4eac\u4e2d\u6765\uff0c\u600e\u4f1a\u4e0d\u77e5\u9053\u3002\u4eac\u4e2d\u4f20\u904d\u8fd9\u9996\u8bd7\uff0c\u53ea\u662f\u5927\u5bb6\u90fd\u89e3\u4e0d\u901a\uff0c\u89c9\u5f97\u5f88\u5947\u602a\u3002 \u90a3\u9996\u8bd7\u9053\uff1a\u2018\u53cc\u6210\u660e\u9753\u5f71\u5f98\u5f8a\uff0c\u7389\u4f5c\u5c4f\u98ce\u58c1\u4f5c\u53f0\u3002\u5728\u9732\u8c03\u6b8b\u5343\u91cc\u8349\uff0c\u6e05\u51c9\u5c71\u4e0b\u516d\u9f99\u6765\u3002\u2019\u53cc \u6210\u662f\u53e4\u795e\u8bdd\u4e2d\u897f\u738b\u6bcd\u7684\u4f8d\u5973\uff0c\u8fd9\u9996\u8bd7\u548f\u8fdb\u9999\uff0c\u4e0d\u77e5\u600e\u7684\u4f1a\u62c9\u626f\u5230\u7f8e\u4e3d\u7684\u4ed9\u5973\u4e0a\u53bb\uff1f\u4e0d\u8fc7\u5434\u6885 \u6751\u662f\u5148\u5e1d\u6700\u5ba0\u7231\u7684\u6587\u5b66\u4f8d\u4e1b\u4e4b\u81e3\uff0c\u8fd9\u8bd7\u5927\u7ea6\u4f1a\u6709\u70b9\u9053\u7406\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u542c\u4ed6\u4eec\u8fd9\u6837\u8bf4\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4e0d\u89c9\u5446\u5446\u5730\u770b\u4f4f\u4ed6\u4eec\uff0c\u90a3\u4e24\u4e2a\u79c0\u624d\u53d1\u73b0\u4e86\uff0c\u5fae\u5fae\u4e00 \u7b11\u3002\u5192\u6d63\u83b2\u642d\u8baa\u95ee\u9053\uff1a\u201c\u600e\u7684\u90a3\u5bfa\u95e8\u73b0\u5728\u8fd8\u662f\u7d27\u7d27\u5173\u4f4f\uff0c\u800c\u4e14\u95e8\u7684\u51e0\u4e08\u65b9\u5706\u4e4b\u5730\u7a7a\u8361\u8361\u7684\u6ca1 \u4e00\u4e2a\u4eba\uff1f\u201d\u65c1\u8fb9\u4e00\u4e2a\u8001\u8005\u63d2\u5634\u7b54\u9053\uff1a\u201c\u5c0f\u54e5\u5927\u7ea6\u662f\u521d\u6b21\u89c2\u5149\u8fd9\u7c7b\u5927\u5178\uff0c\u4e0d\u77e5\u9053\u89c4\u77e9\u3002\u8fd9\u5e99\u95e8 \u524d\u7684\u7b2c\u4e00\u679d\u9999\u8981\u5f85\u9102\u4eb2\u738b\u6765\u70b9\uff0c\u7136\u540e\u6253\u5f00\u5e99\u95e8\uff0c\u518d\u7531\u9102\u4eb2\u738b\u5728\u6587\u6b8a\u7b54\u8428\u9762\u524d\u4e0a\u7b2c\u4e00\u7089\u9999\uff0c\u7136 \u540e\u624d\u505a\u6cd5\u4e8b,\u62db\u5f85\u5404\u65b9\u5584\u7537\u4fe1\u5973\u8fdb\u53bb\u968f\u559c\u3002\u201d \u3000\u3000\u6b63\u8c08\u8bba\u95f4\uff0c\u5ffd\u542c\u5f97\u5c71\u4e0b\u9e23\u9523\u5f00\u9053\uff0c\u5f69\u65d7\u62db\u5c55\uff0c\u961f\u65d7\u5175\u62e5\u7740\u4e58\u516b\u4eba\u5927\u8f7f\u81ea\u5c71\u4e0b\u4e0a\u6765\uff0c\u4eba\u591a \u65f6\u5df2\u5230\u6e05\u51c9\u7259\u5d29\uff0c\u8f7f\u524d\u4f1a\u4e24\u4e2a\u5927\u706f\u5ba0\uff0c\u5199\u7740\u201c\u9102\u4eb2\u738b\u5e9c\u201d\u56db\u4e2a\u5927\u5b57\u3002 \u3000\u3000\u8fd9\u65f6\u4e2d\u5c71\u8170\u5904\uff0c\u53c8\u662f\u9635\u9635\u4eba\u5352\u8d77\u54c4\uff0c\u5085\u9752\u4e3b\u3001\u5192\u6d63\u83b2\u56de\u5934\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u519b\u5b98\u786c\u4ece\u4eba\u4e1b\u4e2d \u95ef\u8fc7\uff0c\u98de\u6b65\u4e0a\u5c71\uff0c\u80cc\u540e\u8fd8\u8ddf\u7740\u4e00\u4e2a\u62ab\u7740\u5927\u7ea2\u50e7\u888d\u7684\u5587\u561b\u50e7\uff0c\u5085\u9752\u4e3b\u89c1\u4e86\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u81ea\u8a00\u81ea \u8bed\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u4e2a\u9b54\u5934\uff0c\u4e5f\u4ece\u4e07\u91cc\u4e4b\u5916\u8d76\u6765\u89c2\u5149\uff1f\u201d \u3000\u3000\u5192\u6d63\u83b2\u89c1\u5085\u9752\u4e3b\u6ee1\u9762\u60ca\u7591\u4e4b\u8272\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u4eba\uff0c\u96be\u9053\u6bd4\u901a\u660e\u548c\u5c1a\u8fd8\u5389\u5bb3\uff1f\u201d\u5085\u9752 \u4e3b\u6084\u58f0\u9053\uff1a\u201c\u4f60\u73b0\u5728\u522b\u95ee\uff0c\u8fc7\u540e\u518d\u544a\u8bc9\u4f60\uff0c\u4eca\u5929\u51c6\u6709\u70ed\u95f9\u770b\u54e9\uff01\u201d \u3000\u3000\u8fd9\u65f6\u523b\u9633\u521d\u4e0a\uff0c\u4e94\u53f0\u5c71\u4e0a\u7a7a\u7684\u4e91\u96fe\uff0c\u50cf\u7ed9\u4e00\u53ea\u5de8\u624b\u7a81\u7136\u63ed\u53bb\u4e00\u6837\u3002\u6d8c\u51fa\u91d1\u5149\u4e07\u9053\uff0c\u6620\u8d77 \u534a\u5929\u7ea2\u971e\u3002\u5728\u53d8\u5e7b\u83ab\u6d4b\u7684\u4e91\u5f69\u4e4b\u4e2d\uff0c\u73b0\u51fa\u8840\u7ea2\u8272\u7684\u65e5\u8f6e\uff0c\u7167\u5f97\u6ee1\u5c71\u6ee1\u8c37\uff0c\u90fd\u662f\u6625\u610f\u3002\u8fd9\u65f6\u9102 \u4eb2\u738b\u7684\u7eff\u5462\u7fe1\u7fe0\u5927\u8f7f\u5df2\u505c\u653e\u5728\u6e05\u51c9\u5bfa\uff0c\u5728\u7ea2\u65e5\u8feb\u5c04\u4e0b\uff0c\u6cdb\u51fa\u60a6\u65e5\u7684\u4e3d\u5f69\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u4e2a\u4e07\u4eba\u5c4f\u606f\u3001\u9759\u5f85\u9102\u4eb2\u738b\u51fa\u6765\u4e0a\u7b2c\u4e00\u62c4\u9999\u7684\u65f6\u5019\uff0c\u5ffd\u7136\u4ece\u6e05\u51c9\u7089\u4fa7\uff0c\u8f6c\u51fa\u4e00\u4e2a\u5a77 \u5a77\u5c11\u5973\uff0c\u9762\u4e0a\u62ab\u7740\u8f7b\u7eb1\uff0c\u624b\u91cc\u62ff\u7740\u4e00\u9762\u9999\u706b\uff0c\u5728\u5e99\u95e8\u524d\u5c06\u9999\u63d2\u4e0b\uff0c\u65c1\u82e5\u65e0\u4eba\u7684\u8ff3\u81ea\u793c\u62dc\u8d77 \u6765\u3002\u8fd9\u4e00\u4e0b\u7a81\u5982\u5176\u6765\uff0c\u5413\u5f97\u4eb2\u5175\u4eec\u624b\u5fd9\u811a\u4e71\uff0c\u6025\u6025\u5927\u58f0\u547c\u559d\uff0c\u8d76\u4e0a\u524d\u53bb\u5c06\u5c11\u5973\u4e24\u624b\u6349\u7740\uff0c\u5c11 \u5973\u4e5f\u6beb\u4e0d\u53cd\u6297\uff0c\u8ba9\u4ed6\u4eec\u4f3c\u6349\u5c0f\u9e21\u4f3c\u7684\uff0c\u6349\u5230\u9102\u4eb2\u738b\u7684\u5927\u8f7f\u9762\u524d\u3002\u4eb2\u5175\u4eec\u4f3c\u4e4e\u662f\u8981\u8ba9\u9102\u4eb2\u738b\u4eb2 \u81ea\u53d1\u843d\u3002 \u3000\u3000\u8fd9\u7a81\u5982\u5176\u6765\u7684\u602a\u4e8b\uff0c\u8fde\u5085\u9752\u4e3b\u4e5f\u5413\u4e86\u4e00\u8df3\uff0c\u6b63\u51b3\u4e0d\u5b9a\u5e94\u5426\u51fa\u624b\u63f4\u6551\u4e4b\u65f6\uff0c\u7a81\u89c1\u90a3\u5c11\u5973\u4e00 \u53cc\u81c2\u4e00\u632f\uff0c\u4e24\u540d\u4eb2\u5175\uff0c\u76f4\u7ed9\u6454\u51fa\u4e00\u4e08\u5f00\u5916\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u5c11\u5973\u55d6\u7684\u4e00\u58f0\uff0c\u62d4\u51fa\u4e00\u628a\u7cbe \u8292\u8000\u76ee\u7684\u77ed\u5251\uff0c\u5de6\u624b\u4e00\u638c\u628a\u7fe1\u7fe0\u8f7f\u95e8\u9707\u5f97\u788e\u7247\u7eb7\u98de\uff0c\u53f3\u624b\u4e00\u5251\u4fbf\u63d2\u8fdb\u53bb\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u591a \u94ce\uff0c\u4eca\u5929\u662f\u4f60\u7684\u6b7b\u671f\uff01\u201d \u3000\u3000\u8f7f\u5b50\u91cc\u7684\u4eba\u5fae\u5fae\u54fc\u4e86\u4e00\u58f0\uff0c\u4e00\u53cd\u624b\u5c31\u5c06\u5c11\u5973\u7684\u624b\u81c2\u5201\u4f4f\uff0c\u5c11\u5973\u6b63\u5f85\u7528\u529b\u518d\u63d2\u8fdb\u53bb\uff0c\u7741\u76ee \u4e00\u770b\uff0c\u5ffd\u7136\u60ca\u53eb\u4e00\u58f0\uff0c\u614c\u4e0d\u8fed\u5730\u62bd\u51fa\u5251\u6765\u5f80\u540e\u4fbf\u9000\uff0c\u5c31\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u5ffd\u5730\u53c8\u662f\u4e00\u4e2a\u5c11\u5e74\uff0c\u81ea \u4eba\u4e1b\u4e2d\u4e00\u63a0\u6570\u4e08\uff0c\u4e09\u8d77\u4e09\u843d\uff0c\u4f3c\u5927\u9e1f\u822c\u98de\u6251\u800c\u6765\uff0c\u4eba\u672a\u5230\uff0c\u9556\u5148\u53d1\uff0c\u4e00\u51fa\u624b\u5c31\u662f\u4e09\u679d\u8fde\u73e0 \u9556\uff0c\u75c9\u5411\u8f7f\u4e2d\u98de\u53bb\uff01 \u3000\u3000\u90a3\u5c11\u5973\u60ca\u9b42\u672a\u5b9a\uff0c\u89c1\u98de\u9556\u8fde\u7fe9\u800c\u6765\uff0c\u5ffd\u7136\u7eb5\u8d77\u7528\u77ed\u5251\u4fbf\u683c\uff0c\u672c\u6765\u7167\u5979\u7684\u6b66\u529f\uff0c\u8fd9\u51e0\u679d\u98de \u9556\uff0c\u539f\u4e0d\u96be\u5c3d\u6570\u6253\u843d\uff0c\u53ea\u662f\u5979\u5fc3\u7075\u521a\u521a\u53d7\u4e86\u9707\u8361\uff0c\u795e\u5fd7\u672a\u6e05\uff0c\u8fd9\u4e00\u683c\u4e00\u6321\uff0c\u53ea\u6253\u843d\u4e86\u4e24\u679d\u98de \u9556\uff0c\u7b2c\u4e09\u679d\u8fd8\u662f\u5c04\u4eba\u8f7f\u4e2d\u3002 \u3000\u3000\u5728\u573a\u7684\u6c5f\u6e56\u597d\u6c49\u89c1\u5c11\u5973\u7a81\u7136\u53cd\u654c\u4e3a\u53cb\uff0c\u6551\u63f4\u8d77\u591a\u94ce\u6765\uff0c\u90fd\u5927\u60d1\u4e0d\u89e3\u3002\u53c8\u89c1\u7b2c\u4e09\u679d\u9556\u5c04\u5165 \u8f7f\u4e2d\uff0c\u7adf\u662f\u6beb\u65e0\u58f0\u606f\uff0c\u5c31\u4f3c\u6ce5\u725b\u5165\u6d77\u4e00\u6837\u3002\u901a\u660e\u548c\u5c1a\u8fd9\u65f6\u5df2\u6324\u5230\u4eba\u5806\u524d\u9762\uff0c\u7a81\u7136\u632f\u81c2\u5927\u547c\u4e00 \u58f0\uff1a\u201c\u4e0d\u8981\u653e\u8d70\u591a\u94ce\uff01\u201d\u90a3\u4e9b\u5356\u89e3\u827a\u4eba\u548c\u4e27\u95e8\u795e\u5e38\u82f1\u3001\u94c1\u5854\u7a0b\u901a\u7b49\u4e00\u5e72\u4eba\u4f17\uff0c\u4fbf\u7eb7\u7eb7\u81ea\u4eba\u4e1b \u4e2d\u8df3\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u90a3\u53d1\u6697\u5668\u7684\u5c11\u5e74\uff0c\u4e5f\u5feb\u8dd1\u5230\u8f7f\u524d\uff0c\u731b\u7136\u95f4\u8f7f\u5e18\u5f00\u5904\uff0c\u4e00\u6280\u98de\u9556\u4f3c\u6d41\u661f\u95ea\u7535\u822c\u76f4\u5c04\u51fa \u6765\uff0c\u90a3\u5c11\u5e74\u5927\u53eb\u4e00\u58f0\uff0c\u7ed9\u98de\u9556\u6253\u4e2a\u6b63\u7740\uff01\u8fd9\u65f6\uff0c\u51e0\u767e\u540d\u4eb2\u5175\uff0c\u4e00\u534a\u56f4\u7740\u8f7f\u95e8\uff0c\u4e00\u534a\u62d2\u654c\uff0c\u53e6 \u6709\u51e0\u4e2a\u88e8\u5bab\u7259\u5c06\uff0c\u6b66\u529f\u8f83\u597d\u7684\uff0c\u4fbf\u8dd1\u53bb\u8981\u6d3b\u6349\u8fd9\u53d1\u6697\u5668\u7684\u5c11\u5e74\u3002 \u3000\u3000\u5192\u6d63\u83b2\u5728\u65c1\u77a7\u5f97\u6e05\u695a\uff0c\u53d1\u6697\u5668\u7684\u5c11\u5e74\u6b63\u662f\u521a\u624d\u4e0e\u81ea\u5df1\u76f8\u649e\u7684\u90a3\u4e2a\u4eba\u3002\u518d\u4e00\u770b\u65f6\uff0c\u53ea\u89c1\u90a3 \u62ab\u7740\u9762\u7eb1\u7684\u5c11\u5973\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u5df2\u6740\u4eba\u91cd\u56f4\uff0c\u5c06\u5c11\u5e74\u4e00\u628a\u62c9\u51fa\u3002\u90a3\u5c11\u5e74\u5de6\u81c2\u4e2d\u4e86\u4e00\u9556\uff0c\u8840\u6d41\u5982 \u6ce8\uff0c\u5e78\u597d\u4e0d\u662f\u4f24\u7740\u8981\u5bb3\uff0c\u8fd8\u80fd\u52c9\u5f3a\u652f\u6301\u3002 \u3000\u3000\u8fd9\u65f6\u6e05\u51c9\u5bfa\u524d\u5df2\u5f62\u6210\u6df7\u6218\u5c40\u9762\uff0c\u89c2\u5149\u4eba\u4f17\uff0c\u56db\u656c\u5954\u9003\uff0c\u901a\u660e\u548c\u5c1a\u4e00\u628a\u6212\u5200\u821e\u5f97\u547c\u547c\u98ce \u54cd\uff0c\u9510\u4e0d\u53ef\u5f53\uff0c\u53ea\u662f\u90a3\u4e9b\u4eb2\u5175\u4eec\u90fd\u662f\u4e45\u7ecf\u6218\u9635\u7684\u5175\u58eb\uff0c\u867d\u7ed9\u4ed6\u4eec\u6253\u4e86\u8fdb\u6765\uff0c\u5374\u5e76\u4e0d\u663e\u5f97\u614c \u4e71\u3002 \u3000\u3000\u4e27\u95e8\u795e\u5e38\u82f1\u548c\u94c1\u5854\u7a0b\u901a\u4e8c\u4eba\uff0c\u4e00\u4e2a\u4f7f\u4e27\u95e8\u68d2\uff0c\u4e00\u4e2a\u4f7f\u4e94\u82b1\u65a7\uff0c\u4e00\u9762\u6740\uff0c\u4e00\u9762\u558a\uff0c\u201c\u591a\u94ce \u8d3c\u5b50\uff0c\u8fd8\u4e0d\u51fa\u6765\u7eb3\u547d\uff01\u201d\u558a\u58f0\u672a\u4e86\uff0c\u8f7b\u79fb\u83b2\u6b65\uff0c\u5fae\u542f\u6731\u5507\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u627e\u9102\u4eb2\u738b\u6709\u4ec0\u4e48 \u4e8b\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\u5927\u51fa\u610f\u5916\uff0c\u5bfa\u524d\u9a9a\u52a8\u987f\u65f6\u5e73\u606f\u4e0b\u6765\uff0c\u5e38\u82f1\u3001\u7a0b\u901a\u4e0d\u518d\u9669\u559d\uff0c\u901a\u660e\u548c\u5c1a\u5782\u4e0b\u6212\u5200\uff0c \u4eb2\u5175\u4eec\u4e5f\u6a2a\u5200\u51dd\u6b65\u505c\u4e0b\u624b\u6765\uff0c\u901a\u660e\u548c\u5c1a\u7b49\u4e00\u5e72\u4eba\u4f17\u662f\u9c81\u738b\u65e7\u90e8\uff0c\u6b64\u6765\u4e3a\u7684\u662f\u627e\u591a\u94ce\u62a5\u4ec7\u3002\u539f \u6765\u5728\u6ee1\u6e05\u5165\u5173\u4e4b\u540e\uff0c\u5357\u660e\u653f\u6743\uff0c\u8fd8\u7ee7\u7eed\u4e86\u4e00\u4e9b\u65f6\u5019\u3001\u6297\u6e05\u519b\u6c11\u5148\u540e\u62e5\u7acb\u8fc7\u798f\u738b\u3001\u9c81\u738b\u3001\u6842\u738b \u7b49\u660e\u671d\u5b97\u5ba4\uff0c\u9c81\u738b\u5c31\u662f\u4e1c\u5357\u5fd7\u58eb\u5f20\u714c\u8a00\u3001\u5f20\u540d\u632f\u7b49\u62e5\u7acb\u7684\u3002\u9c81\u738b\u5efa\u90fd\u6d59\u6c5f\u7ecd\u5174\uff0c\u81ea\u79f0\u201c\u76d1 \u56fd\u201d\uff0c\u7ef4\u6301\u4e86\u4e94\u516d\u5e74\u5c0f\u671d\u5ef7\u7684\u5c40\u9762\uff0c\u540e\u6765\u7ed9\u591a\u94ce\u9ebe\u4e0b\u5927\u5c06\u9648\u9526\u6240\u5e73\u3002\u9c81\u738b\u4f59\u90e8\u5728\u676d\u5dde\u5bc6\u8c0b\u590d \u56fd\uff0c\u53c8\u56e0\u79d8\u5bc6\u6cc4\u6f0f\uff0c\u6570\u767e\u4eba\u88ab\u64d2\uff0c\u5173\u5728\u676d\u5dde\u603b\u5175\u5927\u7262\uff0c\u540e\u6765\u5728\u591a\u94ce\u5927\u5a5a\u524d\u5915\uff0c\u8d8a\u72f1\u9003\u8d70\uff0c\u4e00 \u573a\u6df7\u6218\uff0c\u53c8\u727a\u7272\u4e86\u8bb8\u591a\u4eba\u3002\u56e0\u6b64\u9c81\u738b\u65e7\u90e8\u548c\u591a\u94ce\u4ec7\u6df1\u5982\u6d77\uff0c\u4e8b\u8fc7\u5341\u516d\u5e74\uff0c\u8fd8\u805a\u96c6\u5230\u4e94\u53f0\u5c71 \u6765\uff0c\u8981\u628a\u591a\u94ce\u751f\u64d2\uff0c\u6d3b\u796d\u6b7b\u8005\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u662f\u54cd\u5f53\u5f53\u7684\u82f1\u96c4\u513f\u5973\uff0c\u51a4\u6709\u5934\uff0c\u503a\u6709\u4e3b\uff0c\u591a\u94ce\u7684\u5bb6\u5c5e\uff0c\u4ed6\u4eec\u662f\u4e0d\u613f\u6b8b\u622e\u7684\u3002\u8fd9\u756a \u7a81\u7136\u89c1\u591a\u94ce\u7684\u5927\u8f7f\uff0c\u8d70\u51fa\u7684\u5374\u662f\u4e2a\u8d35\u5987\uff0c\u867d\u60c5\u77e5\u5fc5\u662f\u591a\u94ce\u7684\u738b\u5983\uff0c\u65f6\u95f4\u4e5f\u7ed9\u505c\u4f4f\u4e86\u3002 \u3000\u3000\u4e24\u8fb9\u50f5\u6301\u4e86\u7247\u523b\uff0c\u60c5\u52bf\u5f88\u662f\u5c34\u5c2c\uff0c\u9102\u738b\u5983\u5fae\u5fae\u4e00\u7b11\u3001\u8bf4\u9053\uff1a\u201c\u82e5\u6ca1\u6709\u4ec0\u4e48\u4e8b\uff0c\u4f60\u4eec\u5c31\u6563 \u53bb\u5427\u3002\u201d\u8bf4\u7f62\u63a8\u5f00\u5bfa\u95e8\uff0c\u4fbf\u5f85\u8fdb\u53bb\u3002\u5e38\u82f1\u62a1\u8d77\u4e27\u95e8\u68d2\uff0c\u5927\u53eb\u4e00\u58f0\u9053\uff1a\u201c\u9556\u4f24\u5f20\u516c\u5b50\u7684\u5c31\u662f\u8fd9 \u4e2a\u8d3c\u5a46\u5a18\uff0c\u5979\u65e2\u4e0e\u6211\u4eec\u4e3a\u654c\uff0c\u4f17\u5144\u5f1f\u4f55\u5fc5\u9976\u5979\uff1f\u201d\u4e00\u6296\u624b\uff0c\u51e0\u679a\u4e27\u95e8\u9489\uff0c\u76f4\u671d\u5979\u80cc\u540e\u6253\u53bb\uff0c \u9102\u738b\u5983\u7406\u4e5f\u4e0d\u7406\uff0c\u542c\u5f97\u8111\u540e\u4e00\u54cd\uff0c\u4e00\u53cd\u624b\u5c31\u628a\u51e0\u679a\u4e27\u95e8\u9489\u5b8c\u5168\u6284\u5728\u624b\u4e2d\uff0c\u5979\u63a5\u6697\u5668\u7684\u624b\u6cd5\uff0c \u7adf\u662f\u975e\u5e38\u7684\u7eaf\u719f\uff0c\u901a\u660e\u548c\u5c1a\u7b49\u5927\u6012\uff0c\u5c55\u5f00\u5175\u5203\u53c8\u51b2\u6740\u8d77\u6765\uff0c\u9102\u738b\u5983\u5728\u9f13\u566a\u58f0\u4e2d\uff0c\u5df2\u8fdb\u5165\u6e05\u51c9 \u5bfa\u53bb\u4e86\uff01 \u3000\u3000\u8fd9\u65f6\u5c71\u4e0b\u53c8\u662f\u91d1\u9f13\u9f50\u9e23\uff0c\u4e00\u5f6a\u519b\u9a6c\uff0c\u6025\u6b65\u8d76\u4e0a\u5c71\u6765\u3002 \u3000\u3000\u9f13\u89d2\u9f50\u9e23\uff0c\u6208\u77db\u6620\u65e5\uff0c\u5728\u6ee1\u5c71\u7eb7\u4e71\u4e4b\u4e2d\uff0c\u8fd9\u5f6a\u4eba\u9a6c\u7684\u5148\u5934\u90e8\u961f\u5df2\u8d76\u5230\u7075\u9e6b\u5cf0\u4e0b\u6e05\u51c9\u5bfa \u524d\u3002\u8fd9\u5f6a\u4eba\u7532\u80c4\u9c9c\u660e\uff0c\u53f3\u624b\u6301\u5200\u77db\uff0c\u5de6\u624b\u640f\u94c1\u76fe\uff0c\u78b0\u5230\u5175\u5203\u6765\u88ad\uff0c\u4fbf\u4e3e\u76fe\u5148\u8fce\uff0c\u5200\u77db\u968f\u51fa\uff0c \u53ea\u542c\u5f97\u201c\u5f53\uff01\u5f53\uff01\u201d\u4e4b\u58f0\uff0c\u9707\u8033\u6b32\u804b\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u4fbf\u628a\u6e05\u51c9\u5bfa\u56e2\u56e2\u5730\u56f4\u4e86\u8d77\u6765\u3002\u8fd9\u5f6a\u4eba\u9a6c\u662f \u6ee1\u6e05\u7684\u7981\u536b\u519b\uff0c\u4e13\u8d1f\u7687\u5bab\u548c\u738b\u5e9c\u7684\u5b88\u536b\u4e4b\u8d23\uff0c\u6bd4\u5fa1\u6797\u519b\u8fd8\u8981\u7cbe\u9009\u5f97\u591a\u3002 \u3000\u3000\u90a3\u62ab\u7740\u9762\u7eb1\u3001\u624b\u6301\u77ed\u5251\u7684\u5c11\u5973\uff0c\u6b63\u63a9\u62a4\u7740\u90a3\u53d7\u4f24\u5c11\u5e74\uff0c\u7a81\u56f4\u800c\u51fa\uff0c\u5979\u5de6\u8fb9\u4e00\u515c\uff0c\u53f3\u8fb9\u4e00 \u7ed5\uff0c\u884c\u524d\u5ffd\u540e\uff0c\u884c\u5de6\u5ffd\u53f3\uff0c\u8fdc\u65bd\u6697\u5668\uff0c\u8fd1\u7528\u5251\u653b\uff0c\u8fc5\u5982\u7075\u733f\uff0c\u6ed1\u5982\u72f8\u732b\uff0c\u4e13\u4ece\u7f1d\u9699\u91cc\u94bb\u51fa \u6765\uff0c\u9752\u6625\u5c31\u8981\u7a81\u56f4\uff0c\u5ffd\u7136\u8fce\u9762\u78b0\u7740\u8fd9\u5f6a\u4eba\u9a6c\uff0c\u6b63\u5f85\u7ed5\u903c\u800c\u884c\uff0c\u7a81\u542c\u5f97\u4e00\u58f0\u731b\u559d\uff1a\u201c\u5f80\u54ea\u91cc \u8d70\uff01\u201d\u4e00\u53e3\u957f\u5251\uff0c\u75be\u5982\u95ea\u7535\u5730\u88ad\u5230\u3002 \u3000\u3000\u62ab\u7eb1\u5c11\u5973\u8eab\u8eaf\u4e00\u4f0f\uff0c\u77f3\u81c2\u659c\u51b5\uff0c\u957f\u5251\u547c\u7684\u4e00\u58f0\u4ece\u5934\u4e0a\u780d\u8fc7\uff0c\u5979\u731b\u7684\u4e00\u957f\u8eab\u8eaf\uff0c\u77ed\u5251\u500f\u7136 \u7ffb\u4e0a\uff0c\u6a2a\u622a\u654c\u4eba\u624b\u8155\u3002\u8fd9\u62db\u4f7f\u5f97\u5341\u5206\u9669\u6076\uff0c\u4e0d\u6599\u654c\u4eba\u6b66\u529f\u4e5f\u6781\u6df1\u6e5b\uff0c\u7adf\u4e0d\u64a4\u5251\u56de\u6551\uff0c\u75c9\u81ea\u624b \u8155\u4e00\u65cb\uff0c\u4e5f\u7528\u5251\u628a\u6572\u51fb\u5c11\u5973\u624b\u8155\uff0c\u4e24\u4eba\u4e00\u6cbe\u5373\u8d70\uff0c\u5404\u81ea\u4ee5\u653b\u4e3a\u5b88\u5730\u907f\u4e86\u9669\u62db\uff0c\u53cc\u65b9\u90fd\u6697\u6697\u60ca \u8be7\u3002 \u3000\u3000\u5c11\u5973\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u548c\u81ea\u5df1\u5bf9\u654c\u7684\u4eba\u6c14\u5b87\u8f69\u6602\uff0c\u8eab\u6750\u9b41\u4f1f\uff0c\u6599\u77e5\u4e0d\u662f\u5bfb\u5e38\u4eba\u7269\uff0c\u6b63\u601d\u7591 \u95f4\uff0c\u731b\u542c\u5f97\u4e00\u58f0\u5927\u559d\uff1a\u201c\u5140\u90a3\u4e0d\u662f\u591a\u94ce\u8d3c\u5b50\uff01\u201d\u5c11\u5973\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u542c\u5f97\u5bf9\u624b\u505a\u89e3\u7b54\u9053\uff1a\u201c\u662f \u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u8bc6\u7834\u591a\u94ce\uff0c\u5927\u58f0\u559d\u95ee\u7684\u6b63\u662f\u4e27\u95e8\u795e\u5e38\u82f1\u548c\u94c1\u5854\u7a0b\u901a\u4e8c\u4eba\uff0c\u4ed6\u4eec\u8ddd\u79bb\u591a\u94ce\u8f83\u8fd1\uff0c\u820d\u547d\u5730\u62a2 \u4e86\u8fc7\u6765\u3002\u8fd9\u65f6\u5c11\u5973\u7684\u9664\u5251\u4e5f\u8d8a\u653b\u8d8a\u7d27\uff0c\u4f46\u591a\u94ce\u8155\u529b\u6c89\u96c4\uff0c\u5c11\u5973\u7684\u5251\u4e00\u7ed9\u78b0\u7740\uff0c\u624b\u4e0a\u5c31\u662f\u4e00\u9635 \u9178\u9ebb\uff0c\u800c\u65c1\u8fb9\u90a3\u4f4d\u53d7\u4f24\u5c11\u5e74\uff0c\u53c8\u56e0\u5931\u4e86\u81ea\u5df1\u63a9\u62a4\uff0c\u7adf\u7ed9\u591a\u94ce\u7684\u7259\u5c06\u51fb\u5012\uff0c\u6a2a\u62d6\u6d3b\u62fd\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u5e38\u82f1\u3001\u7a0b\u901a\u5df2\u7136\u8d76\u5230\uff0c\u53eb\u58f0\uff1a\u201c\u59d1\u5a18\u7a0d\u9000\uff01\u201d\u62ab\u7eb1\u5c11\u5973\u72e0\u72e0\u5730\u76ef\u4e86\u591a\u94ce\u4e00\u773c\uff0c\u81ea\u77e5 \u5728\u5982\u6b64\u5f62\u52bf\u4e0b\uff0c\u96be\u4e8e\u53d6\u80dc\uff0c\u4e5f\u4fbf\u64a4\u5251\u62bd\u8eab\uff0c\u5148\u53bb\u63f4\u6551\u90a3\u5c11\u5e74\u540c\u4f34\u3002 \u3000\u3000\u5e38\u82f1\u7a0b\u901a\u6765\u52bf\u5341\u5206\u51f6\u731b\uff0c\u4e00\u8fde\u51fb\u5012\u4e86\u5341\u51e0\u4e2a\u7981\u536b\u519b\uff0c\u591a\u94ce\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f17\u5c06\u9000\u540e\uff0c\u5f85 \u6211\u72ec\u64d2\u8fd9\u4e24\u4e2a\u8d3c\u4eba\u3002\u201d\u957f\u5251\u4e00\u6321\uff0c\u706b\u661f\u84ec\u98de\u4e2d\uff0c\u628a\u5e38\u82f1\u7684\u4e27\u95e8\u68d2\u524a\u53bb\u4e86\u68d2\u5934\uff0c\u4f46\u591a\u94ce\u7684\u94c1\u76fe \u4e5f\u7ed9\u7a0b\u901a\u4e00\u65a7\u5288\u88c2\uff0c\u591a\u94ce\u7d22\u6027\u628a\u94c1\u76fe\u629b\u6389\uff0c\u5c55\u5f00\u5173\u5916\u957f\u767d\u5c71\u6d3e\u7684\u98ce\u96f7\u5251\u6cd5\u548c\u4e24\u4eba\u5927\u6218\u8d77\u6765\uff01 \u3000\u3000\u591a\u94ce\u51fa\u73b0\u540e\uff0c\u5f62\u52bf\u5927\u53d8\uff0c\u901a\u660e\u548c\u5c1a\u7b49\u4e00\u5e72\u4eba\u4f17\uff0c\u7eb7\u7eb7\u5411\u591a\u94ce\u8fd9\u8fb9\u6740\u6765\uff0c\u7981\u536b\u519b\u867d\u7136\u5389 \u5bb3\uff0c\u53ef\u662f\u5728\u5c71\u5730\u4e0a\u5230\u5e95\u4e0d\u6613\u963b\u62e6\uff0c\u7adf\u7ed9\u4ed6\u4eec\u6e10\u6e10\u6740\u8fd1\u2026\u2026 \u3000\u3000\u7a0b\u901a\u5e38\u82f1\u4e8c\u4eba\u662f\u6c5f\u6e56\u4e0a\u51fa\u540d\u7684\u731b\u6c49\uff0c\u5175\u68b0\u65e2\u96fe\uff0c\u529b\u6c14\u53c8\u5927\uff0c\u548c\u591a\u94ce\u6253\u8d77\u6765\uff0c\u6b63\u662f\u534a\u65a4\u516b \u4e24\uff0c\u9163\u6597\u8d77\u6765\uff0c\u53ea\u89c1\u5e38\u82f1\u7684\u4e27\u95e8\u68d2\u5982\u602a\u87d2\u6bd2\u9f99\uff0c\u6a2a\u51b2\u76f4\u626b\uff1b\u7a0b\u901a\u7684\u4e24\u67c4\u677f\u65a7\u5982\u5c71\u79fb\u5cb3\u52a8\uff0c\u91cd \u91cd\u538b\u6765\uff0c\u800c\u591a\u94ce\u7684\u529f\u529b\u4e5f\u975e\u540c\u5c0f\u53ef\uff0c\u957f\u5251\u5c55\u5f00\uff0c\u631f\u7740\u98ce\u96f7\u4e4b\u58f0\uff0c\u541e\u5410\u62bd\u64a4\uff0c\u65f6\u5982\u9e70\u96bc\u98de\u5929\uff1b \u51fb\u6d4b\u622a\u65a9\uff0c\u65f6\u5982\u731b\u864e\u4f0f\u5730\uff0c\u4e00\u9053\u5251\u5149\uff0c\u88f9\u4f4f\u822c\u5175\u5668\uff0c\u7adf\u662f\u6beb\u4e0d\u9000\u8ba9\u3002 \u3000\u3000\u9163\u6597\u4e2d\u901a\u660e\u548c\u5c1a\u6a2a\u7709\u6012\u76ee\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u4e3e\u5200\u731b\u5288\u3002\u957f\u5251\u6212\u5200\u78b0\u4e2a\u6b63\u7740\uff0c\u4e00\u58f0\u5de8\u54cd\uff0c\u706b\u82b1 \u84ec\u98de\uff0c\u4e24\u4eba\u90fd\u78b0\u5f97\u864e\u53e3\u53d1\u70ed\uff0c\u901a\u660e\u548c\u5c1a\u66f4\u4e0d\u6362\u62db\uff0c\u6b3a\u8eab\u76f4\u8fdb\uff0c\u987a\u624b\u4e00\u5200\uff0c\u4fbf\u5207\u591a\u94ce\u80a0\u95e8\uff0c\u591a \u94ce\u5fae\u5fae\u4e00\u95ea\uff0c\u5251\u62db\u500f\u53d8\uff0c\u53cd\u5708\u5230\u901a\u660e\u548c\u5c1a\u80cc\u540e\uff0c\u4e3e\u5251\u4fbf\u632a\uff0c\u901a\u660e\u548c\u5c1a\u5934\u4e5f\u4e0d\u56de\uff0c\u542c\u98ce\u8fa8\u62db\uff0c \u53cd\u624b\u4e00\u5200\uff0c\u65a9\u654c\u4eba\u624b\u8155\u3002\u591a\u94ce\u82e5\u4e0d\u6536\u62db\uff0c\u5b9a\u5fc5\u4e24\u8d25\u4ff1\u4f24\u3002 \u3000\u3000\u591a\u94ce\u5230\u5e95\u662f\u4e2a\u4eb2\u738b\uff0c\u901a\u660e\u548c\u5c1a\u6562\u62fc\u6027\u547d\u8d70\u51fa\u9669\u62db\uff0c\u4ed6\u5374\u4e0d\u6562\u3002\u4ed6\u6025\u5f97\u201c\u5927\u5f2f\u8170\uff0c\u659c\u63d2 \u67f3\u201d\uff0c\u8eac\u8eab\u6362\u6b65\uff0c\u628a\u63b7\u51fa\u7684\u5251\u786c\u64a4\u56de\u6765\u3002\u4ed6\u4e5f\u5fae\u5fae\u6709\u70b9\u80c6\u60ca\u4e86\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e24\u65c1\u7684\u7981\u536b\u519b\u5df2\u662f\u5982\u6f6e\u6d8c\u6765\uff0c\u66ff\u4ed6\u6321\u4f4f\u90a3\u73ed\u6c5f\u6e56\u597d\u6c49\u3002\u8fd9\u65f6\u591a\u94ce\u5e26\u6765 \u7684\u4eba\u9a6c\uff0c\u9646\u7eed\u4e0a\u5c71\uff0c\u81ea\u5c71\u811a\u5230\u534a\u5c71\uff0c\u5a49\u8712\u5982\u957f\u9f99\uff0c\u5bc6\u5bc6\u9ebb\u9ebb\uff0c\u603b\u6709\u4e8c\u4e09\u5343\u4eba\uff0c\u91d1\u9f13\u9f50\u9e23\uff0c\u6ee1 \u5c71\u5450\u558a\uff0c\u58f0\u52bf\u6781\u76db\uff0c\u7adf\u4f3c\u51b2\u950b\u6253\u4ed7\u4e00\u6837\u3002 \u3000\u3000\u90a3\u5356\u89e3\u5973\u4eba\u7a81\u7136\u6253\u51fa\u4e00\u6280\u8896\u7bad\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u53d1\u51fa\u4e00\u9053\u84dd\u706b\uff0c\u76f4\u4e0a\u9065\u7a7a\u3002\u8fd9\u706b\u7bad\u662f\u4e2a\u8baf \u53f7\uff0c\u4e00\u53d1\u51fa\u540e\uff0c\u9c81\u738b\u4f59\u90e8\u8fde\u547c\u901f\u9000\uff0c\u5206\u5934\u6740\u51fa\uff0c\u722c\u4e0a\u5c71\u53bb\u3002 \u3000\u3000\u591a\u94ce\u626d\u5934\u4e00\u770b\uff0c\u548c\u5356\u89e3\u5973\u4eba\u5bf9\u4e2a\u6b63\u7740\uff0c\u4ed6\u672c\u60f3\u62e6\u622a\u901a\u660e\u548c\u5c1a\u53bb\u8def\u7684\uff0c\u8fd9\u65f6\u4e5f\u6539\u53d8\u4e86\u4e3b \u610f\uff0c\u98de\u6b65\u4fbf\u8ffd\u90a3\u4e2a\u5356\u89e3\u5973\u4eba\u3002 \u3000\u3000\u90a3\u5356\u89e3\u5973\u4eba\u8eab\u6cd5\u597d\u5feb\uff0c\u591a\u94ce\u5927\u6b65\u8ffd\u53bb\uff0c\u7981\u536b\u519b\u4e24\u8fb9\u95ea\u5f00\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7ed9\u5979\u5f15\u4e0a\u4e86\u7075\u9e6b\u5cf0\u9669 \u5cfb\u4e4b\u5904\u3002\u591a\u94ce\u4e00\u770b\uff0c\u53ea\u89c1\u5947\u5ca9\u602a\u77f3\uff0c\u7a81\u5140\u5cf0\u5cfb\uff0c\u5cf0\u56de\u8def\u8f6c\uff0c\u51f9\u51f8\u4e0d\u5e73\uff0c\u7981\u536b\u519b\u5728\u5c71\u8170\u4e0b\u8ffd\u9010 \u9c81\u738b\u7684\u65e7\u90e8\uff0c\u9ad8\u5cf0\u4e0a\u53ea\u6709\u81ea\u5df1\u548c\u90a3\u5356\u89e3\u5973\u4eba\u3002\u5fc3\u5ff5\u4e8c\u52a8\uff0c\u4e0d\u7981\u8e0c\u8e87\uff0c\u90a3\u5356\u89e3\u5973\u4eba\u597d\u50cf\u77e5\u9053\u4ed6 \u7684\u5fc3\u610f\u4e00\u6837\uff0c\u56de\u5934\u4e00\u7b11\uff0c\u626c\u624b\u5c31\u662f\u4e00\u679d\u86c7\u7130\u7bad\u5411\u4ed6\u5c04\u6765\uff0c\u591a\u94ce\u5f15\u8eab\u4e00\u95ea\uff0c\u84ec\u7684\u4e00\u58f0\uff0c\u4e00\u6e9c\u70df \u706b\u5c31\u5728\u4ed6\u8eab\u65c1\u63a0\u8fc7\uff0c\u628a\u9644\u8fd1\u91ce\u8349\u70e7\u5c06\u8d77\u6765\uff0c\u90a3\u5973\u7684\u6b62\u6b65\u51dd\u7738\uff0c\u6a2a\u5251\u77a7\u89c6\uff0c\u597d\u50cf\u5f88\u770b\u4e0d\u8d77\u591a\u94ce \u7684\u795e\u6c14\u3002 \u3000\u3000\u591a\u94ce\u5fc3\u4e2d\u6709\u6c14\uff0c\u5fc3\u60f3\u81ea\u5df1\u5927\u5c0f\u6570\u767e\u6218\uff0c\u6218\u65e0\u4e0d\u80dc\uff0c\u96be\u9053\u6015\u4e00\u4e2a\u5973\u4eba\uff0c\u800c\u4e14\u8fd9\u4e2a\u5973\u4eba\u7684\u76f8 \u8c8c\uff0c\u5f88\u50cf\u6d59\u5357\u201c\u5973\u532a\u9996\u201d\u5218\u90c1\u82b3\u7684\u6a21\u6837\uff0c\u628a\u5979\u9664\u6389\uff0c\u5bf9\u671d\u5ef7\u5927\u6709\u597d\u5904\u3002 \u3000\u3000\u591a\u94ce\u6863\u6848\u4e2d\u7684\u201c\u6d59\u5357\u6b8b\u532a\u201d\u5c31\u662f\u524d\u660e\u9c81\u738b\u7684\u4f59\u90e8\u3002\u56e0\u4e3a\u9c81\u738b\u7684\u5c0f\u671d\u5ef7\u662f\u591a\u94ce\u706d\u6389\u7684\uff0c\u56e0 \u6b64\u4ed6\u540e\u6765\u867d\u7136\u5378\u4e86\u4e24\u6c5f\u63d0\u7763\u4e4b\u804c\uff0c\u6709\u5173\u6c5f\u6d59\u9c81\u738b\u65e7\u90e8\u6d3b\u52a8\u7684\u60c5\u5f62\uff0c\u5730\u65b9\u5b98\u540f\u9001\u6765\u7684\u6587\u4e66\uff0c\u5175 \u90e8\u4e5f\u603b\u5907\u4e00\u4efd\u526f\u672c\u7ed9\u4ed6\uff0c\u5e76\u5f81\u8be2\u4ed6\u7684\u610f\u89c1\u3002\u8fd9\u4e2a\u201c\u5973\u532a\u9996\u201d\u5218\u90c1\u82b3\u662f\u6700\u8fd1\u51e0\u5e74\u624d\u5d1b\u8d77\u7684\uff0c\u4ee5 \u524d\u7684\u201c\u532a\u9996\u201d\u5218\u7cbe\u4e00\u662f\u9c81\u738b\u90e8\u4e0b\u4e00\u5458\u5927\u5c06\uff0c\u5218\u90c1\u82b3\u662f\u4ed6\u7684\u5973\u513f\uff0c\u4f46\u5730\u65b9\u5b98\u9001\u6765\u7684\u6587\u4e66\u62a5\u544a\uff0c \u81ea\u5218\u7cbe\u4e00\u6b7b\u540e\uff0c\u9c81\u738b\u65e7\u90e8\u5c31\u516c\u63a8\u5218\u90c1\u82b3\u505a\u9996\u9886\uff0c\u90a3\u65f6\u5979\u8fd8\u672a\u6ee1\u4e09\u5341\u5c81\uff0c\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u53ef\u662f\u9c81\u738b \u4f59\u90e8\u5bf9\u5979\u90fd\u5f88\u670d\u8d34\u3002\u591a\u94ce\u5728\u6863\u6848\u4e2d\u66fe\u89c1\u8fc7\u5979\u671d\u56fe\u50cf\uff0c\u56e0\u6b64\u4e00\u89c1\u4fbf\u89c9\u597d\u751f\u9762\u719f\u3002 \u3000\u3000\u8fd9\u65f6\u591a\u94ce\u7ed9\u5979\u4e00\u9017\uff0c\u5fcd\u4e0d\u4f4f\u633a\u5251\u4fbf\u52a8\uff0c\u5f85\u5f97\u591a\u94ce\u4e00\u5251\u5288\u6765\uff0c\u5979\u5fae\u4e00\u4fa7\u8eab\uff0c\u9752\u94a2\u5251\u5411\u5de6\u4e00 \u9886\uff0c\u591a\u94ce\u6b3a\u8eab\u76f4\u8fdb\uff0c\u7528\u529b\u4e00\u62cd\uff0c\u60f3\u5c06\u5218\u90c1\u82b3\u7684\u5251\u62cd\u6389\uff0c\u4e0d\u6599\u8fd9\u4e00\u5251\u62cd\u53bb\uff0c\u53cd\u7ed9\u5218\u90c1\u82b3\u7684\u5251\u642d \u4e0a\u5251\u8eab\uff0c\u8f7b\u8f7b\u4e00\u5f15\uff0c\u501f\u529b\u6253\u529b\uff0c\u591a\u94ce\u8eab\u5b50\u7adf\u7ed9\u5e26\u52a8\uff0c\u79fb\u4e86\u4e24\u6b65\u3002\u591a\u94ce\u8d81\u524d\u503e\u4e4b\u52bf\uff0c\u75be\u7684\u7ffb\u5251 \u5012\u7ede\uff0c\u5316\u4e86\u5218\u90a3\u82b3\u7684\u5185\u52b2\uff0c\u4e00\u56e2\u5bd2\u5149\u88f9\u7740\u4e86\u5218\u90c1\u82b3\u7684\u5175\u5203\u3002 \u3000\u3000\u5218\u90c1\u82b3\u7684\u65e0\u6781\u5251\u6cd5\uff0c\u517c\u592a\u6781\u6b66\u5f53\u4e24\u6d3e\u4e4b\u957f\uff0c\u673a\u7075\u5230\u6781\uff0c\u5728\u591a\u94ce\u957f\u5251\u7ffb\u7ede\u65f6\uff0c\u4e5f\u8d81\u52bf\u4e00 \u5377\uff0c\u201c\u56de\u98ce\u620f\u67f3\u201d\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\u5c06\u591a\u94ce\u7684\u957f\u5251\u8361\u5f00\u3002\u5979\u53c8\u662f\u64a4\u5251\u62bd\u8eab\uff0c\u672a\u8d25\u5148\u9000\u3002 \u3000\u3000\u591a\u94ce\u6c14\u5f80\u4e0a\u51b2\uff0c\u5927\u8e0f\u6b65\u8ffd\u53bb\u3002\u5ffd\u7136\u95f4\uff0c\u53ea\u89c1\u5218\u90c1\u82b3\u50cf\u98de\u9e1f\u4e00\u6837\uff0c\u8df3\u5728\u4e24\u7130\u4e4b\u95f4\u76f8\u8fde\u7684\u4e00 \u4e2a\u77f3\u6881\u4e0a\uff0c\u8fd9\u77f3\u6881\u5bbd\u4e0d\u5230\u4e09\u5c3a\uff0c\u7ea6\u6709\u5341\u4f59\u4e08\u957f\uff0c\u897f\u8fb9\u662f\u9669\u5cfb\u5947\u5cf0\uff0c\u5e95\u4e0b\u662f\u4e07\u4e08\u6df1\u8c37\u3002\u591a\u94ce\u8ffd \u5f97\u5f97\u610f\uff0c\u6536\u5178\u4e0d\u4f4f\uff0c\u60f3\u4e5f\u4e0d\u60f3\u4fbf\u98d8\u8eab\u8df3\u4e0a\u65b9\u6881\u3002\u5218\u90c1\u82b3\u79c0\u7709\u5012\u602a\uff0c\u9752\u94a2\u5251\u5982\u94f6\u8679\u75be\u5410\uff0c\u548c\u591a \u94ce\u5c31\u5728\u8fd9\u7edd\u9669\u7684\u77f3\u7cb1\u4e0a\u5927\u6218\u8d77\u6765\u3002 \u3000\u3000\u5218\u90c1\u82b3\u80dc\u5728\u8eab\u6cd5\u8f7b\u7075\uff0c\u591a\u94ce\u80dc\u5728\u529f\u529b\u6df1\u539a\u3002\u8fd9\u4e00\u756a\u4ea4\u624b\uff0c\u53ea\u542c\u5f97\u5251\u98ce\u864e\u864e\uff0c\u4e24\u4eba\u90fd\u7ed9\u7cbe \u5149\u51b7\u7535\u822c\u7684\u5251\u6c14\u7f69\u4f4f\uff0c\u6597\u4e86\u4e00\u767e\u591a\u62db\uff0c\u5140\u662f\u672a\u5206\u80dc\u8d1f\u3002\u8fd9\u65f6\u7981\u536b\u519b\u548c\u901a\u660e\u548c\u5c1a\u7b49\u4e00\u5e72\u4eba\u4f17\uff0c \u4e5f\u5df2\u7ecf\u8ffd\u9010\u5230\u4e86\u7075\u9e6b\u5cf0\u4e0a\uff0c\u4f17\u4eba\u4e00\u89c1\u591a\u94ce\u548c\u4e00\u4e2a\u5973\u4eba\u5728\u7edd\u9669\u4e4b\u5730\u62fc\u547d\u6597\u5251\uff0c\u90fd\u4e0d\u7981\u60ca\u9a87\u8d77 \u6765\uff0c\u4e24\u8fb9\u7684\u4eba\u90fd\u662f\u4e00\u9762\u6df7\u6218\uff0c\u4e00\u9762\u6ce8\u89c6\u7740\u77f3\u6881\u4e0a\u820d\u6b7b\u5fd8\u751f\u7684\u6076\u6218\uff01 \u3000\u3000\u5085\u9752\u4e3b\u3001\u5192\u6d63\u83b2\u4e8c\u4eba\uff0c\u8fd9\u65f6\u4e5f\u7b95\u8e1e\u5728\u4e00\u5757\u5ca9\u77f3\u4e4b\u4e0a\u4f5c\u58c1\u4e0a\u89c2\uff0c\u770b\u4e86\u4e00\u4f1a\uff0c\u5192\u6d63\u83b2\u9053\uff1a \u201c\u5085\u4f2f\u4f2f\uff0c\u4f60\u770b\u90a3\u5356\u89e3\u5973\u4f7f\u7684\u662f\u4e0d\u662f\u6211\u4eec\u672c\u95e8\u7684\u65e0\u6781\u5251\u6cd5\uff1f\u201d \u3000\u3000\u5085\u9752\u4e3b\u82e5\u6709\u6240\u601d\uff0c\u534a\u664c\u7b54\u9053\uff1a\u201c\u6211\u60f3\u8d77\u6765\u4e86\uff0c\u7b97\u8d77\u6765\u5979\u8be5\u662f\u4f60\u7684\u5e08\u59d0\u3002\u4e8c\u5341\u591a\u5e74\u524d\uff0c\u6211 \u7684\u5e08\u5144\u5f1f\u601d\u5357\u548c\u9c81\u738b\u90e8\u4e0b\u7684\u5927\u5c06\u5218\u7cbe\u4e00\u4ea4\u60c5\u5f88\u597d\uff0c\u8ba4\u4e86\u5218\u7cbe\u4e00\u7684\u5c0f\u5973\u513f\u505a\u5e72\u5973\uff0c\u4ece\u516d\u5c81\u8d77\u5c31 \u6559\u5979\u7ec3\u529f\uff0c\u5355\u601d\u5357\u7684\u5251\u6cd5\u81ea\u6210\u4e00\u6d3e\uff0c\u4ee5\u65e0\u6781\u5251\u6cd5\u63c9\u5408\u6b66\u5f53\u5251\u6cd5\uff0c\u521a\u67d4\u517c\u6d4e\uff0c\u548c\u5929\u5c71\u6666\u660e\u7985\u5e08 \u5e76\u79f0\u5f53\u4e16\u4e24\u5927\u5251\u672f\u540d\u5bb6\u3002\u8fd9\u5973\u4eba\u51c6\u662f\u5218\u7cbe\u4e00\u7684\u5973\u513f\u65e0\u7591\u4e86\uff0c\u53ef\u60dc\u5979\u7684\u529f\u529b\u7565\u900a\u2018\u4e8e\u591a\u94ce\uff0c\u8981 \u4e0d\u7136\u53ea\u8bba\u5251\u6cd5\uff0c\u65e9\u5c31\u8be5\u8d62\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e0b\u9762\u4e24\u4eba\u8d8a\u6597\u8d8a\u6025\uff0c\u731b\u7136\u95f4\u5218\u90c1\u82b3\u5251\u4ea4\u5de6\u624b\uff0c\u8179\u6643\u4e00\u62db\uff0c\u591a\u94ce\u4e00\u5251\u5288\u53bb\uff0c\u5218 \u90c1\u82b3\u4e00\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\uff0c\u5012\u7ffb\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u53f3\u624b\u4e00\u626c\uff0c\u4e00\u4ef6\u9ed1\u5ffd\u5ffd\u7684\u4e1c\u897f\u5f53\u5934\u7f69\u4e0b\uff0c\u8fd9\u662f \u5979\u7684\u5947\u95e8\u6697\u5668\u201c\u9526\u4e91\u515c\u201d\uff0c\u7528\u94a2\u4e1d\u7ec7\u7f51\uff0c\u7f51\u7684\u5468\u56f4\u662f\u6708\u7259\u5f62\u7684\u5012\u987b\uff0c\u591a\u94ce\u63e9\u624b\u4e0d\u53ca\uff0c\u80a9\u5934\u7ed9 \u201c\u9526\u4e91\u515c\u201d\u515c\u4e2a\u6b63\u7740\uff0c\u5012\u987b\u6263\u7740\u76ae\u8089\uff0c\u5218\u90c1\u82b3\u7535\u529b\u4e00\u62c9\uff0c\u9c9c\u8840\u7f15\u7f15\u6cae\u6cae\u800c\u51fa\uff0c\u591a\u94ce\u5fae\u5fae\u54fc\u4e86 \u4e00\u58f0\uff0c\u4ecd\u662f\u63a5\u7740\uff0c\u624b\u4e2d\u5251\u4e0a\u906e\u4e0b\u6321\uff0c\u628a\u95e8\u6237\u5c01\u5f97\u5f88\u4e25\u3002 \u3000\u3000\u5218\u90c1\u82b3\u8fd0\u5251\u5982\u98ce\uff0c\u72e0\u72e0\u653b\u4e0a\u3002\u591a\u94ce\u6b63\u5371\u6025\u95f4\uff0c\u731b\u542c\u5f97\u5de6\u4fa7\u7edd\u58c1\u4e4b\u4e0a\u4e00\u58f0\u5927\u53eb\uff1a\u201c\u6211\u6765 \u4e5f\uff01\u201d\u53e6\u6709\u4e00\u58f0\u8d4b\u559d\uff1a\u201c\u695a\u662d\u5357\uff0c\u4f60\u5e72\u4e48\uff1f\u201d\u8bed\u58f0\u672a\u4e86\uff0c\u7a81\u6709\u4e00\u4eba\u4f3c\u6d41\u661f\u98de\u5815\uff0c\u6070\u6070\u843d\u5728\u77f3 \u6881\u4e4b\u4e0a\uff0c\u8eab\u5f62\u672a\u5b9a\uff0c\u4fbf\u662f\u4e00\u5251\u64a9\u53bb\uff0c\u628a\u201c\u7ef5\u4e91\u515c\u201d\u7684\u767e\u70bc\u94a2\u7ef3\u65a9\u65ad\uff0c\u62e6\u5728\u591a\u94ce\u524d\u9762\uff0c\u4fbf\u548c\u5218 \u90c1\u82b3\u4ea4\u8d77\u624b\u6765\u3002\u591a\u94ce\u628a\u5012\u987b\u62d4\u51fa\uff0c\u6b63\u5f85\u540e\u9000\uff0c\u5ffd\u89c1\u77f3\u6881\u90a3\u7aef\u53c8\u662f\u4e00\u4e2a\u548c\u5c1a\u7b11\u563b\u563b\u5730\u62e6\u4f4f\u53bb \u8def\uff0c\u591a\u94ce\u4e00\u770b\uff0c\u6b63\u662f\u90a3\u4e2a\u602a\u5934\u9640\u901a\u660e\u548c\u5c1a\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u6012\uff0c\u957f\u5251\u4e00\u6446\uff0c\u53ea\u5f97\u518d\u5ea6\u548c\u901a\u660e\u62fc\u547d \u6076\u6218\uff01 \u3000\u3000\u695a\u662d\u5357\u7a81\u7136\u73b0\u8eab\uff0c\u628a\u5728\u573a\u7684\u597d\u6c49\u90fd\u5413\u4e86\u4e00\u8df3\u3002\u5085\u9752\u4e3b\u4e5f\u76b1\u8d77\u7709\u5934\uff0c\u5bf9\u5192\u6d63\u83b2\u8bf4\uff1a\u201c\u6211\u4eca \u6668\u8bf4\u7684\u9b54\u5934\u4fbf\u662f\u6b64\u4eba\uff0c\u4ed6\u5728\u6c5f\u6e56\u4e0a\u88ab\u79f0\u4e3a\u2018\u6e38\u9f99\u5251\u2019\u695a\u662d\u5357\uff0c\u4e43\u662f\u6666\u660e\u7985\u5e08\u7684\u5f92\u5f1f\uff0c\u4e8c\u5341\u4e00 \u5e74\u80fd\u548c\u4ed6\u5927\u5e08\u5144\u6768\u4e91\u9a8f\u5e76\u79f0\u5929\u5c71\u5251\uff0c\u53ef\u60dc\u4e24\u4eba\u6027\u683c\u521a\u521a\u76f8\u53cd\uff0c\u6768\u4e91\u9a86\u662f\u8c6a\u6c14\u5343\u4e91\uff0c\u7ec8\u751f\u4e3a\u590d \u56fd\u5954\u8dd1\uff1b\u800c\u695a\u662d\u5357\u5374\u70ed\u4e2d\u5229\u7984\uff0c\u7ec8\u4e8e\u88ab\u5434\u4e09\u6842\u7f51\u7f57\u4e86\u53bb\uff0c\u505a\u4e86\u4ed6\u519b\u4e2d\u7684\u603b\u6559\u5934\uff0c\u6768\u4e91\u9a8f\u79bb\u5947 \u6b7b\u540e\uff0c\u5929\u5c71\u7edd\u827a\uff0c\u53ea\u4ed6\u4e00\u4e2a\u4f20\u4eba\uff0c\u4ed6\u66f4\u662f\u6a2a\u884c\u65e0\u5fcc\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5728\u90a3\u4e24\u5cf0\u4e4b\u95f4\u76f8\u8fde\u7684\u77f3\u6881\u4e0a\uff0c\u4e24\u5bf9\u4eba\u6597\u5251\uff0c\u8fde\u8f6c\u8eab\u4e5f\u4e0d\u53ef\u80fd\uff0c\u5e38\u70d8\u66f4\u662f\u60ca\u9669\u65e0 \u6bd4\uff0c\u90a3\u695a\u662d\u5357\u7684\u5251\u6cd5\u679c\u7136\u795e\u5947\uff0c\u5218\u90c1\u82b3\u7684\u9752\u94a2\u5251\u672c\u6765\u8fc5\u6377\u65e0\u6bd4\uff0c\u65c1\u89c2\u7684\u770b\u6765\uff0c\u597d\u50cf\u660e\u660e\u5c31\u8981 \u523a\u4e2d\u695a\u662d\u5357\u7684\u8981\u5bb3\u4e86\uff0c\u53ef\u4e0d\u77e5\u600e\u7684\uff0c\u603b\u7ed9\u4ed6\u628a\u6765\u52bf\u6d88\u4e8e\u65e0\u5f62\uff0c\u8fde\u770b\u4e5f\u770b\u4e0d\u6e05\u695a\u4ed6\u662f\u600e\u4e48\u907f\u5f00 \u800c\u53c8\u662f\u600e\u6837\u53cd\u653b\u7684\u3002\u5085\u9752\u4e3b\u770b\u4e86\u4e00\u4f1a\uff0c\u5bf9\u5192\u6d63\u83b2\u8bf4\uff1a\u201c\u770b\u6765\u975e\u6211\u51fa\u624b\u4e0d\u884c\u4e86\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c \u53ea\u89c1\u695a\u662d\u5357\u5251\u62db\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6eda\u6eda\u800c\u4e0a\uff0c\u5218\u90c1\u82b3\u62db\u67b6\u5df2\u663e\u5f97\u5f88\u662f\u8270\u96be\u3002\u5085\u9752\u4e3b\u53ee\u5631\u4e86\u5192\u6d63\u83b2 \u4e00\u58f0\uff1a\u201c\u4f60\u522b\u4e71\u8d70\uff01\u201d\u53cc\u81c2\u4e00\u632f\uff0c\u5c31\u5982\u5927\u96c1\u4e00\u822c\uff0c\u5f80\u4e0b\u98de\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u6070\u597d\u695a\u662d\u5357\u7528\u4e86\u4e00\u62db\u201c\u6781\u76ee\u6ca7\u6ce2\u201d\uff0c\u6307\u5411\u5218\u90c1\u82b3\u80f8\u90e8\uff0c\u5218\u90c1\u82b3\u7684\u9752\u94a2\u5251\u7ed9\u4ed6\u8361\u5f00\uff0c \u64a4\u5251\u5df2\u6765\u4e0d\u53ca\u3002\u5085\u9752\u4e3b\u5230\u5f97\u6070\u662f\u65f6\u5019\uff0c\u53f3\u624b\u65e0\u6781\u5251\u51cc\u7a7a\u4e0b\u51fb\uff0c\u5de6\u624b\u4e00\u628a\u6293\u4f4f\u5218\u90c1\u82b3\u81c2\u8180\uff0c\u8fd0 \u5185\u5bb6\u529f\u529b\uff0c\u5411\u540e\u4e00\u629b\uff0c\u5218\u90c1\u82b3\u501f\u7740\u8fd9\u4e00\u629b\u4e4b\u529b\uff0c\u5728\u534a\u7a7a\u4e2d\u7ffb\u4e00\u4e2a\u7b4b\u6597\uff0c\u8f7b\u98d8\u98d8\u7684\u4f3c\u7fbd\u6bdb\u4e00\u6837 \u843d\u5728\u90a3\u8fb9\u7684\u5371\u5d16\u4e4b\u4e0a\u3002 \u3000\u3000\u695a\u662d\u5357\u4e3e\u5251\u4e00\u6321\uff0c\u89c9\u6765\u4eba\u5185\u52b2\u66f4\u5927\u3002\u81ea\u5df1\u672c\u60f3\u8d81\u4ed6\u8eab\u5f62\u672a\u5b9a\uff0c\u5c06\u4ed6\u8feb\u4e0b\u6df1\u8c37\uff0c\u4e0d\u6599\u53cc\u5251 \u76f8\u4ea4\uff0c\u53ea\u89c9\u6709\u4e00\u80a1\u5927\u529b\u63a8\u6765\uff0c\u53cd\u7ed9\u9707\u9000\u4e86\u4e24\u6b65\uff0c\u4e0d\u7981\u5fc3\u5185\u6697\u60ca\u3002\u4f46\u81ea\u601d\u5929\u5c71\u5251\u6cd5\u72ec\u6b65\u6d77\u5185\uff0c \u6765\u4eba\u7eb5\u662f\u529f\u529b\u6df1\u539a\uff0c\u4e5f\u96be\u9003\u5251\u4e0b\u3002\u4e8e\u662f\uff0c\u66f4\u4e0d\u601d\u91cf\uff0c\u4e00\u53e3\u5251\u75be\u7684\u65bd\u5c55\u5f00\u6765\uff0c\u5251\u5251\u72e0\u6df1\uff0c\u5168\u662f \u6307\u5411\u654c\u4eba\u8981\u5bb3\uff01 \u3000\u3000\u5085\u9752\u4e3b\u631f\u6570\u5341\u5e74\u5185\u5bb6\u529f\u529b\uff0c\u51cc\u7a7a\u4e0b\u51fb\uff0c\u4e0d\u80fd\u5c06\u695a\u662d\u5357\u51fb\u5012\uff0c\u5fc3\u4e2d\u4e5f\u662f\u6697\u6697\u5403\u60ca\u3002\u77ac\u606f\u4e4b \u95f4\uff0c\u4e24\u4eba\u5df2\u6597\u4e86\u4e94\u4e03\u5341\u62db\uff0c\u53cc\u65b9\u5168\u662f\u6beb\u4e0d\u9000\u8ba9\u3002\u4e24\u53e3\u5251\u95ea\u7535\u60ca\u98d8\uff0c\u8d8a\u6597\u8d8a\u6025\uff0c\u8fdc\u5904\u671b\u53bb\uff0c\u53ea \u89c1\u94f6\u5149\u6ce2\u6d9b\u4e4b\u4e2d\u88f9\u7740\u4e24\u6761\u9ed1\u5f71\uff0c\u6d6e\u6c89\u8d77\u4f0f\uff0c\u8fde\u901a\u660e\u548c\u5c1a\u7b49\u4e00\u5e72\u597d\u624b\uff0c\u4e5f\u81ea\u9a87\u76ee\u60ca\u5fc3\uff0c\u7d27\u5f20\u5f97 \u8fde\u6c14\u4e5f\u900f\u4e0d\u8fc7\u6765\uff01 \u3000\u3000\u695a\u662d\u5357\u8d8a\u6218\u8d8a\u52c7\uff0c\u5251\u62db\u8d8a\u6765\u8d8a\u5feb\u3002\u5085\u9752\u4e3b\u5982\u5251\u62db\u500f\u53d8\uff0c\u8d8a\u5c55\u8d8a\u6162\uff0c\u4f46\u9976\u662f\u695a\u662d\u5357\u5982\u4f55\u8fc5 \u6377\uff0c\u5374\u603b\u662f\u653b\u4e0d\u8fdb\u53bb\uff0c\u5251\u5c16\u4e0d\u8bba\u6307\u5230\u54ea\u513f\uff0c\u90fd\u78b0\u7740\u4e00\u80a1\u56de\u51fb\u4e4b\u529b\uff0c\u5085\u9752\u4e3b\u624b\u4e0a\u5c31\u50cf\u633d\u7740\u5343\u65a4 \u91cd\u7269\u4e00\u6837\uff0c\u5251\u5c16\u4e1c\u6307\u897f\u5212\uff0c\u4f3c\u4e4e\u751a\u4e3a\u5403\u529b\uff0c\u4f46\u5374\u662f\u5251\u5149\u64a9\u7ed5\uff0c\u597d\u50cf\u5728\u8eab\u5b50\u5468\u56f4\u7b51\u8d77\u4e86\u65e0\u5f62\u7684 \u94c1\u58c1\u94dc\u5899\u3002\u695a\u662d\u5357\u662f\u8bc6\u8d27\u7684\u4eba\uff0c\u77e5\u9053\u8fd9\u662f\u6700\u4e0a\u4e58\u7684\u5185\u5bb6\u5251\u6cd5\uff0c\u4e0d\u7981\u5012\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\u3002 \u3000\u3000\u695a\u662d\u5357\u653b\u4e0d\u8fdb\u53bb\uff0c\u5085\u9752\u4e3b\u4e5f\u6740\u4e0d\u51fa\u6765\u3002\u4e24\u4eba\u90fd\u6709\u70b9\u7740\u6025\u4e86\u3002\u5c31\u5728\u8fd9\u50f5\u5f85\u7684\u65f6\u95f4\uff0c\u731b\u7136\u95f4 \u5085\u9752\u5b9d\u5251\u62db\u4e00\u64a4\uff0c\u95e8\u6237\u5927\u5f00\u3002\u695a\u662d\u5357\u4e00\u5251\u523a\u5c06\u4e0b\u6765\uff0c\u5085\u9752\u4e3b\u5fae\u5fae\u4e00\u95ea\uff0c\u624b\u4e2d\u5251\u7a81\u7136\u4e00\u95e9\uff0c\u5c06 \u695a\u662d\u5357\u7684\u5251\u950b\u9501\u4f4f\uff0c\u5de6\u624b\u95ea\u7535\u822c\u7684\u5f53\u5934\u5288\u53bb\uff0c\u695a\u662d\u5357\u731d\u4e0d\u53ca\u9632\uff0c\u53f3\u624b\u5251\u4e00\u633a\u4e00\u5377\uff0c\u4e5f\u4ee5\u5de6\u638c \u8fce\u51fb\u4e0a\u53bb\uff0c\u53ea\u542c\u5f97\u84ec\u7136\u4e00\u58f0\uff0c\u63a5\u7740\u6ee1\u5c71\u60ca\u547c\uff0c\u4e24\u4eba\u90fd\u4f3c\u65ad\u7ebf\u98ce\u7b5d\u4e00\u822c\uff0c\u5411\u77f3\u6881\u4e0b\u7684\u4e07\u4e08\u6df1\u8c37 \u5815\u53bb\uff01\u5085\u9752\u4e3b\u5815\u5230\u534a\u5c71\uff0c\u89e6\u7740\u4e86\u5d16\u77f3\u65c1\u8fb9\u4f38\u51fa\u7684\u866c\u677e\uff0c\u4e00\u628a\u62c9\u4f4f\uff0c\u5c31\u6b62\u4e86\u4e0b\u5815\u4e4b\u52bf\uff1b\u695a\u662d\u5357 \u5374\u5982\u5f39\u5206\u4e00\u822c\uff0c\u5728\u534a\u7a7a\u4e2d\u7ffb\u4e86\u51e0\u4e2a\u7b4b\u6597\uff0c\u76f4\u843d\u8c37\u5e95\uff01 \u3000\u3000\u8fd9\u65f6\u591a\u94ce\u4e5f\u7ed9\u901a\u660e\u548c\u5c1a\u6b65\u6b65\u8fdb\u8feb\uff0c\u4e00\u76f4\u8feb\u5230\u77f3\u6881\u7684\u4e00\u7aef\uff0c\u518d\u9000\u5c31\u662f\u7edd\u9669\u7684\u5371\u5d16\uff0c\u800c\u5371\u5d16 \u4e0a\u53c8\u6709\u5218\u90c1\u82b3\u6301\u5251\u5b88\u7740\uff01 \u3000\u3000\u8fd9\u65f6\u591a\u94ce\u5e26\u6765\u7684\u7981\u536b\u519b\u5df2\u5168\u6570\u767b\u5c71\uff0c\u89c2\u5149\u7684\u5584\u7537\u4fe1\u5973\u54ed\u53f7\u9739\u5929\uff0c\u9c81\u738b\u7684\u65e7\u90e8\u4e5f\u6709\u8bb8\u591a\u8fd8 \u672a\u7a81\u56f4\u3002\u800c\u7981\u536b\u519b\u7684\u795e\u673a\u8425\u5f13\u7bad\u624b\u4e5f\u5f20\u5f3a\u5f13\uff0c\u98de\u7fbd\u7bad\uff0c\u5411\u5218\u90c1\u82b3\u7b49\u5df2\u7a81\u56f4\u7684\u4eba\u5c04\u53bb\uff0c\u867d\u8bf4\u5371 \u5d16\u7edd\u58c1\uff0c\u5f13\u7bad\u5f88\u96be\u7784\u51c6\uff0c\u53ef\u662f\u5f62\u52bf\u4e5f\u5f88\u5371\u9669\uff0c\u5218\u90c1\u82b3\u76ee\u7779\u6df7\u6218\uff0c\u8033\u542c\u547c\u58f0\uff0c\u7a81\u7136\u53c8\u53d1\u51fa\u4e00\u679d \u706b\u7bad\uff0c\u559d\u4ee4\u901a\u660e\u548c\u5c1a\u505c\u624b\u3002 \u3000\u3000\u901a\u660e\u548c\u5c1a\u6115\u7136\u6b62\u6b65\uff0c\u6b63\u601d\u7591\u95f4\uff0c\u53ea\u542c\u5f97\u5218\u90c1\u82b3\u559d\u95ee\u9053\uff1a\u201c\u591a\u94ce\uff0c\u4f60\u8fd8\u60f3\u4e0d\u60f3\u6d3b\uff1f\u201d\u591a\u94ce \u88c5\u51fa\u6beb\u4e0d\u5728\u4e4e\u7684\u795e\u6c14\u8bf4\u9053\uff1a\u201c\u60f3\u53c8\u600e\u6837\uff1f\u4e0d\u60f3\u53c8\u600e\u6837\uff1f\u201d\u5218\u90c1\u82b3\u9053\uff1a\u201c\u5982\u679c\u4f60\u60f3\u6d3b\u547d\uff0c\u4f60\u5c31 \u53eb\u7981\u536b\u519b\u7f62\u624b\uff0c\u6211\u4eec\u4eca\u65e5\u5f7c\u6b64\u4e0d\u72af\uff0c\u540c\u65f6\u4f60\u4e5f\u4e0d\u51c6\u6ee5\u6355\u4e00\u4e2a\u8001\u767e\u59d3\u3002\u201d\u591a\u94ce\u60f3\u4e86\u4e00\u4e0b\uff0c\u95ee \u9053\uff1a\u201c\u4ee5\u540e\u53c8\u600e\u4e48\u6837\uff1f\u201d\u5218\u90c1\u82b3\u9053\uff1a\u201c\u4ee5\u540e\u662f\u4ee5\u540e\u7684\u4e8b\u3002\u4f60\u5f53\u7136\u4e0d\u4f1a\u653e\u8fc7\u6211\u4eec\uff0c\u6211\u4eec\u4e5f\u4e0d\u4f1a \u653e\u8fc7\u4f60\uff01\u201d\u591a\u94ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u8fd8\u516c\u5e73\uff0c\u5c31\u8fd9\u6837\u529e\u5427\uff01\u201d\u957f\u5251\u4e00\u62ac\uff0c\u53d1\u51fa\u53f7\u4ee4\u3002 \u3000\u3000\u679c\u7136\u519b\u4ee4\u5982\u5c71\uff0c\u4f20\u8fbe\u4e0b\u53bb\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5200\u5251\u5f52\u9798\uff0c\u5f3a\u5f13\u6302\u8d77\uff0c\u88ab\u56f4\u7684\u9c81\u738b\u65e7\u90e8\u8d70\u51fa\u6765\uff0c \u89c2\u5149\u7684\u4eba\u4eec\u4e5f\u9c7c\u8d2f\u4e0b\u5c71\u4e86\u3002 \u3000\u3000\u901a\u660e\u548c\u5c1a\u6a2a\u5200\u51dd\u6b65\uff0c\u76ee\u9001\u591a\u94ce\u5927\u8e0f\u6b65\u8d70\u8fc7\u77f3\u7cb1\uff0c\u6068\u5f97\u75d2\u75d2\u7684\uff0c\u53e6\u4e00\u4e2a\u66f4\u75db\u6068\u591a\u94ce\u7684\u662f\u90a3 \u4e2a\u62ab\u7eb1\u5c11\u5973\uff0c\u5979\u8eab\u501a\u77f3\u5d16\uff0c\u624b\u63a2\u6000\u4e2d\uff0c\u4f3c\u4e4e\u662f\u60f3\u6478\u51fa\u6697\u5668\u3002\u4e27\u95e8\u795e\u5e38\u82f1\u5728\u5979\u80cc\u540e\uff0c\u6025\u5fd9\u62e6\u963b \u9053\u201c\u59d1\u5a18\uff0c\u53ef\u522b\u80e1\u6765\uff01\u6211\u4eec\u9996\u9886\u5df2\u53d1\u4e0b\u547d\u4ee4\uff0c\u4e0d\u80fd\u5931\u4fe1\u4e8e\u4eba\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u8fd9\u65f6\u5df2\u722c\u4e86\u4e0a\u6765\uff0c\u5218\u90c1\u82b3\u91cd\u65b0\u4ee5\u793c\u76f8\u89c1\uff0c\u8c22\u8fc7\u8fd9\u4f4d\u591a\u5e74\u4e0d\u89c1\u7684\u5e08\u53d4\u3002\u5f85\u591a\u94ce\u8d70\u8fc7 \u77f3\u6881\uff0c\u5979\u4e5f\u7387\u9886\u4e00\u5e72\u4eba\u4f17\uff0c\u7ffb\u8fc7\u7075\u9e6b\u5cf0\uff0c\u4ece\u53e6\u4e00\u9762\u4e0b\u5c71\u4e86\u3002\u62ab\u7eb1\u5c11\u5973\u867d\u7136\u4e0d\u662f\u5979\u4eec\u4e00\u8def\uff0c\u4e5f \u7ed9\u9080\u8bf7\u540c\u884c\u3002 \u3000\u3000\u4e00\u8def\u4e0a\u5927\u5bb6\u90fd\u5f88\u5c11\u4f5c\u58f0\u3002\u529f\u8d25\u5782\u6210\uff0c\u514d\u4e0d\u4e86\u6709\u70b9\u4e27\u6c14\u3002\u53ef\u662f\u5927\u5bb6\u4e5f\u8c05\u89e3\u5218\u90c1\u82b3\u7684\u505a\u6cd5\uff0c \u8f7b\u91cd\u6743\u8861\uff0c\u4e5f\u8bb8\u591a\u4eba\u7684\u6027\u547d\u548c\u591a\u94ce\u76f8\u6362\uff0c\u4e5f\u662f\u503c\u5f97\u7684\u3002\u5218\u90c1\u82b3\u7684\u5174\u81f4\u4f3c\u4e4e\u8fd8\u5f88\u4e0d\u9519\uff0c\u5979\u89c1\u5230 \u5192\u6d63\u83b2\u660e\u8273\u7167\u4eba\uff0c\u4e3e\u6b62\u4f63\u96c5\uff0c\u4ece\u5fc3\u5e95\u91cc\u5c31\u6b22\u559c\u5979\uff0c\u4e00\u8def\u9017\u5979\u8bf4\u8bdd\u3002\u53ea\u662f\u5192\u6d63\u83b2\u5374\u4f3c\u4e4e\u90c1\u95f7\u672a \u6d88\uff0c\u8c08\u8bdd\u4e4b\u95f4\uff0c\u663e\u5f97\u6709\u70b9\u513f\u5fc3\u795e\u4e0d\u5c5e\u7684\u6837\u5b50\u3002 \u3000\u3000\u8fd9\u73ed\u4eba\u7684\u811a\u7a0b\u5f88\u5feb\uff0c\u7ffb\u8fc7\u9ad8\u5cf0\uff0c\u7a7f\u8fc7\u5e7d\u8c37\uff0c\u8d70\u4e86\u4e00\u91cc\u7684\u5c71\u5f84\uff0c\u4e5f\u53ea\u4e0d\u8fc7\u82b1\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c \u4e0d\u4e45\u5c31\u5230\u4e86\u4e00\u4e2a\u5c71\u5e84\uff0c\u5e84\u524d\u5df2\u7ecf\u6709\u8bb8\u591a\u4eba\u76f8\u5019\u3002 \u3000\u3000\u5218\u90c1\u82b3\u5bf9\u5085\u9752\u4e3b\u9053\uff1a\u201c\u8fd9\u662f\u6c5f\u6e56\u524d\u8f88\u6b66\u5143\u82f1\u7684\u5e84\u5b50\uff0c\u6211\u4eec\u6b64\u6765\uff0c\u5c31\u662f\u501f\u4ed6\u7684\u5e84\u5b50\u9a7b\u811a \u7684\u3002\u201d\u5085\u9752\u4e3b\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u60f3\u662f\u7ec8\u5357\u6d3e\u7684\u540d\u5bbf\u6b66\u5143\u82f1\uff1f\u6211\u548c\u4ed6\u4e5f\u662f\u591a\u5e74\u7684\u670b\u53cb\u4e86\u3002\u201d\u5218\u90c1 \u82b3\u5e94\u9053\uff1a\u201c\u6b63\u662f\u6b64\u4eba\u3002\u201d\u8bf4\u65f6\uff0c\u5e84\u5b50\u91cc\u5df2\u6709\u4eba\u51fa\u6765\u7980\u62a5\uff0c\u90a3\u4eba\u662f\u7559\u5b88\u7684\u9c81\u738b\u65e7\u90e8\uff0c\u81ea\u5728\u5218\u90c1 \u82b3\u8033\u8fb9\u8bf4\u4e86\u51e0\u53e5\uff0c\u53ea\u89c1\u5218\u90c1\u82b3\u9576\u8d77\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\uff01\u70e6\u4f60\u5148\u8fdb\u53bb\u7980\u544a\u5e84\u4e3b\uff0c\u6211\u4eec\u5728 \u522b\u9662\u7a0d\u6b47\uff0c\u6599\u7406\u4e00\u70b9\u4e8b\u60c5\u3002\u7136\u540e\u518d\u62dc\u89c1\u5e84\u4e3b\u548c\u97e9\u603b\u8235\u4e3b\u3002\u201d\u901a\u660e\u548c\u5c1a\u95ee\u9053\uff1a\u201c\u53ef\u662f\u5929\u5730\u4f1a\u7684 \u97e9\u5fd7\u90a6\u603b\u8235\u4e3b\u6765\u4e86\uff1f\u201d\u5218\u90c1\u82b3\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u4e00\u73ed\u4eba\u90fd\u5f88\u9ad8\u5174\uff0c\u53ef\u662f\u5374\u53c8\u50cf\u6709\u4e9b\u4ec0\u4e48\u987e\u5fcc \u4f3c\u7684\uff0c\u4e0d\u6562\u5728\u5218\u90c1\u82b3\u9762\u524d\u8c08\u8bba\u3002 \u3000\u3000\u5218\u90c1\u82b3\u7387\u9886\u901a\u660e\u548c\u5c1a\u7b49\u4e00\u73ed\u4eba\u4f17\u8fdb\u5165\uff0c\u5085\u9752\u4e3b\u3001\u5192\u6d63\u83b2\u548c\u62ab\u7eb1\u5c11\u5973\u4e5f\u4e00\u540c\u884c\u8fdb\uff0c\u5750\u5b9a\u4e4b \u540e\uff0c\u5218\u90c1\u82b3\u9762\u5bb9\u5e84\u4e25\uff0c\u7a81\u7136\u5bf9\u62ab\u7eb1\u5c11\u5973\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u53ef\u522b\u602a\uff0c\u6211\u4eec\u7d20\u6765\u6069\u6028\u5206\u660e\uff0c\u4eca\u5929\u4f60 \u62a4\u4e86\u591a\u94ce\u738b\u5983\uff0c\u5374\u53c8\u820d\u547d\u6551\u6211\u4eec\u7684\u5f20\u516c\u5b50\uff0c\u6211\u4eec\u5b9e\u5728\u83ab\u6d4b\u9ad8\u6df1\uff0c\u4e0d\u77e5\u59d1\u5a18\u4f60\u80fd\u5426\u8d50\u544a\u6765\u606f\uff1f \u80fd\u5426\u4ee5\u771f\u5bb9\u76f8\u89c1\uff1f\u201d\u62ab\u7eb1\u5c11\u5973\u9ed8\u4e0d\u505a\u58f0\uff0c\u6162\u6162\u9664\u4e0b\u8f7b\u7eb1\uff0c\u5ffd\u7136\u95f4\uff0c\u5168\u573a\u76ee\u5149\u90fd\u6ce8\u610f\u7740\u5979\uff0c\u6709 \u7684\u4eba\u4e14\u53d1\u51fa\u4e86\u602a\u58f0\uff01 \u3000\u3000\u90a3\u62ab\u7eb1\u5c11\u5973\u7f13\u7f13\u9664\u4e0b\u8f7b\u7eb1\u4e4b\u540e\uff0c\u4e00\u970e\u90a3\u95f4\u4f17\u4eba\u90fd\u5446\u4f4f\u4e86\u3002\u5979\u7684\u9762\u8c8c\uff0c\u7adf\u7136\u4e0e\u591a\u94ce\u738b\u5983\u4e00 \u6a21\u4e00\u6837\uff0c\u53ea\u5dee\u8eab\u4e0a\u6ca1\u7a7f\u7740\u65d7\u88c5\u3002\u901a\u660e\u548c\u5c1a\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u662f\u65d7\u4eba\u8fd8\u662f\u6c49\u4eba\uff1f\u201d\u5c11\u5973\u6a2a\u4e86\u901a \u660e\u548c\u5c1a\u4e00\u773c\u9053\uff1a\u201c\u6211\u81ea\u7136\u662f\u6c49\u4eba\u3002\u201d\u7a0b\u901a\u95ee\u9053\uff1a\u201c\u59d1\u5a18\u7684\u82b3\u540d\u3001\u5e08\u95e8\uff0c\u80fd\u5426\u89c1\u544a\uff1f\u201d\u5c11\u5973\u7b11 \u9053\uff1a\u201c\u6bcf\u4e00\u4e2a\u4eba\u90fd\u6709\u4e00\u4e2a\u540d\u5b57\uff0c\u540d\u5b57\u4e0d\u8fc7\u662f\u4e09\u4e2a\u8bb0\u53f7\u7f62\u4e86\uff0c\u4e3a\u4e86\u79f0\u547c\u65b9\u4fbf\u8d77\u89c1\uff0c\u4f60\u4eec\u5c31\u53eb\u6211 \u505a\u6613\u5170\u73e0\u5427\u3002\u81f3\u4e8e\u5e08\u95e8\uff0c\u4ee5\u6211\u8fd9\u6837\u4e00\u4e2a\u4e0d\u6210\u6750\u7684\u5973\u5b50\uff0c\u65f6\u4e0d\u613f\u4eb5\u6e0e\u4ed6\u8001\u4eba\u5bb6\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u6613\u5170\u73e0\u73af\u626b\u4e86\u4f17\u4eba\u4e00\u773c\u3002\u5979\u81ea\u7136\u770b\u5f97\u51fa\u4f17\u4eba\u7591\u60d1\u7684\u795e\u60c5\uff0c\u4e8e\u662f\u63d0\u9ad8\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u81f3\u4e8e\u95ee \u6211\u4e3a\u4ec0\u4e48\u6551\u62a4\u591a\u94ce\u738b\u5983\uff0c\u6211\u60f3\u5404\u4f4d\u90fd\u662f\u82f1\u96c4\u513f\u5973\uff0c\u4e0d\u7528\u6211\u8bf4\uff0c\u4e5f\u77e5\u9053\u8fd9\u4e2a\u9053\u7406\uff0c\u6211\u672c\u610f\u662f\u8981 \u523a\u6740\u591a\u94ce\uff0c\u54ea\u77e5\u5374\u78b0\u4e0a\u738b\u5983\u3002\u6211\u81ea\u7136\u4e0d\u5fcd\u523a\u6740\u4e00\u4e2a\u624b\u65e0\u5bf8\u94c1\u7684\u5973\u4eba\uff01\u800c\u5979\u6253\u4f24\u5f20\u516c\u5b50\uff0c\u5374\u662f \u4ee5\u540e\u7684\u4e8b\u3002\u201d \u3000\u3000\u5728\u5c11\u5973\u65f6\u4f83\u800c\u8c08\u65f6\uff0c\u5085\u9752\u4e3b\u5077\u5077\u5199\u4e86\u4e00\u5f20\u5b57\u6761\uff0c\u53eb\u5192\u6d63\u83b2\u9012\u7ed9\u5218\u90c1\u82b3\u770b\uff0c\u4e0a\u9762\u5199\u9053\uff1a \u201c\u6b64\u5973\u76ee\u5149\u6563\u4e71\uff0c\u795e\u6001\u5f02\u5e38\uff0c\u5b9a\u6709\u975e\u5e38\u4e4b\u75db\u3002\u201d\u5218\u90c1\u82b3\u77e5\u9053\u8fd9\u4f4d\u5e08\u53d4\u533b\u7406\u7cbe\u5999\uff0c\u548c\u81ea\u5df1\u6240\u6d4b \u4e5f\u4e0d\u8c0b\u800c\u5408\u3002\u4e8e\u662f\u4e00\u5f85\u5c11\u5973\u8bf4\u5b8c\uff0c\u4fbf\u6e29\u8a00\u5b89\u6170\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u522b\u591a\u5fc3\uff01\u6211\u4eec\u6240\u95ee\uff0c\u4e5f\u4e0d\u8fc7\u662f \u60f3\u7ed3\u7eb3\u59d1\u5a18\u8fd9\u6837\u4e00\u4f4d\u670b\u53cb\u800c\u5df2\u3002\u59d1\u5a18\uff0c\u4f60\u5982\u4e0d\u5acc\u5f03\uff0c\u6211\u75f4\u957f\u51e0\u5e74\uff0c\u6211\u8981\u53eb\u4f60\u4e00\u58f0\u59b9\u5b50\u3002\u201d\u4e8e \u662f\u4eb2\u81ea\u4e0b\u53bb\uff0c\u5c06\u6613\u5170\u73e0\u62c9\u7740\uff0c\u53eb\u5979\u5750\u5728\u81ea\u5df1\u7684\u8eab\u8fb9\uff0c\u6613\u5170\u73e0\u773c\u89d2\u5fae\u6da6\uff0c\u4f4e\u58f0\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u59d0 \u59d0\uff01\u201d\u901a\u660e\u548c\u5c1a\u7b49\u4eba\u89c1\u5979\u8fd9\u4e2a\u6837\u513f\uff0c\u4e5f\u4e3e\u5f97\u597d\u751f\u7684\u8fc7\u610f\u4e0d\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u6b66\u5e84\u4e3b\u5df2\u77e5\u9053\u5085\u9752\u4e3b\u4e5f\u6765\u4e86\uff0c\u9ad8\u5174\u975e\u5e38\uff0c\u7279\u522b\u6d3e\u4eba\u6765\u8bf7\u5085\u9752\u4e3b\u8fc7\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u5218 \u59d1\u5a18\u6709\u4e8b\u60c5\u6599\u7406\uff0c\u90a3\u5c31\u8bf7\u5085\u5927\u7237\u5148\u89c1\u89c1\u9762\u5427\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u968f\u5e84\u4e01\u8fc7\u4e86\u51e0\u91cd\u9662\u5b50\uff0c\u5230\u4e86\u4e00\u95f4\u7cbe\u81f4\u7684\u4e66\u623f\uff0c\u4f46\u89c1\u53ea\u6709\u6b66\u5143\u82f1\u4e00\u4eba\u6d01\u8c31\u76f8\u5019\uff0c\u4e24 \u4eba\u5df2\u6709\u4e8c\u5341\u591a\u5e74\u6ca1\u89c1\u9762\u4e86\uff0c\u8fd9\u756a\u89c1\u9762\uff0c\u771f\u4e2a\u662f\u611f\u6168\u4e07\u5343\uff0c\u4e24\u4eba\u8c08\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u6b66\u5143\u82f1\u7a81\u7136\u8bf4 \u9053\uff1a\u201c\u5085\u5927\u54e5\uff0c\u6211\u6709\u4e8b\u76f8\u6258\uff0c\u4f60\u53ef\u5f97\u5356\u4e2a\u9762\u5b50\u3002\u201d\u5085\u9752\u4e3b\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d\u6b66\u5143\u82f1\u9053\uff1a \u201c\u60f3\u6258\u4f60\u505a\u5a92\u3002\u201d\u5085\u9752\u4e3b\u7b11\u9053\uff1a\u201c\u6211\u53ef\u7f3a\u4e4f\u8ba4\u8bc6\u4ec0\u4e48\u5973\u5b69\u5b50\u3002\u81f3\u4e8e\u968f\u6211\u6765\u7684\u8fd9\u4f4d\u5192\u5c0f\u59d0\uff0c\u5979 \u5e74\u7eaa\u8fd8\u5c0f\u54e9\u3002\u201d\u6b66\u5143\u82f1\u4e5f\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u60f3\u6253\u4f60\u8fd9\u4f4d\u5192\u5c0f\u59d0\u7684\u4e3b\u610f\u3002\u6211\u8bf4\u7684\u662f\u4f60\u7684\u4f84\u5973\u5218\u90c1\u82b3 \u59d1\u5a18\uff1b\u5979\u7684\u7236\u6bcd\u5e08\u7236\u90fd\u6b7b\u4e86\uff0c\u4f60\u662f\u5979\u7684\u5e08\u53d4\uff0c\u53ef\u62ff\u5f97\u4e00\u534a\u4e3b\u610f\u3002\u201d\u5085\u9752\u4e3b\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u6258 \u4eba\u505a\u5a92\uff1f\u201d \u3000\u3000\u6b66\u5143\u82f1\u91cd\u91cd\u5730\u559d\u4e86\u4e00\u53e3\u9152\uff0c\u634b\u7740\u987b\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u4e2a\u4eba\u8bf4\u8d77\u6765\u4e5f\u4e0d\u8fb1\u6ca1\u5218\u59d1\u5a18\u3002\u4ed6\u5c31 \u662f\u5929\u5730\u4f1a\u7684\u603b\u8235\u4e3b\u97e9\u5fd7\u90a6\u3002\u8fd9\u4eba\u4e0d\u4f46\u662f\u8c6a\u4fa0\u5fc3\u80a0\uff0c\u800c\u4e14\u4eba\u6781\u5fe0\u539a\u3002\u4ed6\u672c\u662f\u4e00\u4e2a\u9a6c\u573a\u573a\u4e3b\uff0c\u6e05 \u5175\u6765\u540e\uff0c\u4ed6\u96c6\u4f17\u521b\u7acb\u4e86\u5929\u5730\u4f1a\uff0c\u53ea\u56e0\u8fde\u5e74\u5954\u8dd1\uff0c\u8fd1\u56db\u5341\u5c81\u8fd8\u6ca1\u6709\u6210\u5bb6\u3002\u201d\u6b66\u5143\u82f1\u8bf4\u7740\u53c8\u53f9\u4e86 \u4e00\u53e3\u6c14\u9053\uff1a\u201c\u6211\u4eec\u8001\u4e86\uff0c\u4e5f\u4e0d\u77e5\u9053\u5e74\u9752\u4eba\u7684\u60f3\u6cd5\u4e86\u3002\u5218\u59d1\u5a18\u6837\u6837\u90fd\u597d\uff0c\u5c31\u53ea\u662f\u813e\u6c14\u53ef\u6709\u70b9\u602a \u50fb\uff0c\u4e00\u548c\u5979\u63d0\u4eb2\uff0c\u5979\u5c31\u4e0d\u9ad8\u5174\u3002\u97e9\u5fd7\u90a6\u4ee5\u524d\u5e2e\u8fc7\u5979\u4e0d\u5c11\u5fd9\uff0c\u4e5f\u66fe\u6258\u6b66\u6797\u540c\u9053\u5411\u5979\u63d0\u8fc7\u5a5a\u4e8b\uff0c \u5979\u53ea\u662f\u4e00\u4e2a\u52b2\u513f\u4e0d\u7406\uff0c\u4ee5\u5979\u8fd9\u6837\u7684\u4eba\u6750\uff0c\u4e5f\u5f04\u5230\u4e09\u5341\u51fa\u5934\u8fd8\u672a\u7ed3\u5a5a\uff0c\u800c\u4e14\u597d\u50cf\u4e0d\u613f\u610f\u7ed3\u5a5a\uff0c \u4f60\u8bf4\uff0c\u8fd9\u53ef\u4e0d\u662f\u602a\u4e8b\uff1f\u201d \u3000\u3000\u5085\u9752\u4e3b\u542c\u4e86\uff0c\u51dd\u601d\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u4ee3\u4f60\u95ee\u95ee\u5218\u59d1\u5a18\u7684\u610f\u601d\uff0c\u4f46\u7b54\u4e0d\u7b54\u5e94\uff0c\u53ef\u662f\u5979 \u81ea\u5df1\u7684\u4e8b\u3002\u201d \u3000\u3000\u4e24\u4f4d\u8001\u670b\u53cb\u53c8\u8c08\u4e86\u4e00\u9635\uff0c\u6b66\u5e84\u4e3b\u9053\uff1a\u201c\u6211\u548c\u4f60\u53bb\u89c1\u89c1\u97e9\u603b\u8235\u4e3b\u5982\u4f55\uff1f\u201d\u5085\u9752\u4e3b\u6b23\u7136\u9053\uff1a \u201c\u597d\u3002\u201d\u4e24\u4eba\u8d70\u51fa\u5ba2\u5385\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u5b69\u5b50\u54d7\u7b11\uff0c\u6709\u4e00\u4e2a\u7a1a\u5ae9\u7684\u58f0\u97f3\u9053\uff1a\u201c\u97e9\u53d4\u53d4\uff0c\u4f60\u8f93\u4e86\uff0c \u53ef\u4e0d\u8bb8\u62b5\u8d56\u5440\uff01\u6211\u8981\u9a91\u9a6c\u3002\u201d\u6b66\u5143\u82f1\u63a8\u95e8\u8fdb\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u5927\u6c49\u722c\u5728\u5730\u4e0a\uff0c\u818a\u5934\u4e0a\u9a91\u7740\u4e00\u4e2a\u5b69 \u5b50\uff0c\u62cd\u624b\u54c8\u54c8\u5927\u7b11\u3002\u6b66\u5143\u82f1\u559d\u9053\uff1a\u201c\u6210\u5316\uff0c\u4e0d\u8bb8\u95f9\uff01\u201d \u3000\u3000\u90a3\u5b69\u5b50\u4e00\u8df3\u843d\u5730\uff0c\u5927\u6c49\u4e5f\u7ad9\u4e86\u8d77\u6765\uff0c\u7d2b\u9762\u6cdb\u7ea2\uff0c\u5ff8\u6029\u5730\u7b11\u7740\uff0c\u7c97\u8c6a\u4e2d\u5e26\u7740\u201c\u59a9\u5a9a\u201d\u3002\u6b66 \u5143\u82f1\u4e0d\u7981\u7b11\u9053\uff1a\u201c\u97e9\u5927\u54e5\u8d8a\u6765\u8d8a\u5b69\u5b50\u6c14\u4e86\uff0c\u53ef\u7eb5\u574f\u4e86\u6210\u5316\u8fd9\u5b69\u5b50\u3002\u201d\u8bf4\u7740\u66ff\u5085\u9752\u4e3b\u4ecb\u7ecd\u9053\uff1a \u201c\u8fd9\u4f4d\u5c31\u662f\u5929\u5730\u4f1a\u7684\u97e9\u603b\u8235\u4e3b\u97e9\u5fd7\u90a6\uff0c\u8fd9\u662f\u6211\u7684\u5c0f\u513f\u5b50\u6210\u5316\uff0c\u5582\uff0c\u6210\u5316\u8fc7\u6765\u62dc\u89c1\u5085\u4f2f\u4f2f\uff0c\u5411 \u4ed6\u8ba8\u89c1\u9762\u793c\u3002\u201d \u3000\u3000\u6b66\u6210\u5316\u4eca\u5e74\u53ea\u6709\u5341\u4e00\u5c81\uff0c\u662f\u6b66\u5143\u82f1\u4e94\u5341\u5927\u5bff\u90a3\u5e74\u751f\u7684\uff0c\u5b9d\u8d1d\u5f97\u4e86\u4e0d\u5f97\u3002\u8fd9\u65f6\u8df3\u8df3\u8e66\u8e66\u5730 \u8fc7\u6765\uff0c\u624b\u91cc\u8fd8\u62ff\u7740\u68cb\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u97e9\u53d4\u53d4\u548c\u6211\u4e0b\u8c61\u68cb\uff0c\u8fde\u8f93\u4e09\u76d8\u7ed9\u6211\u5566\uff01\u201d\u97e9\u5fd7\u90a6\u9053\uff1a\u201c\u6210 \u5316\u8fd9\u5b69\u5b50\u771f\u5389\u5bb3\uff0c\u6211\u521a\u521a\u5b66\u4e86\u6885\u82b1\u8c31\uff0c\u7528\u5c4f\u98ce\u9a6c\u6765\u6321\u4ed6\u7684\u5f53\u5934\u70ae\u8fdb\u4e03\u5175\u5c40\uff0c\u8c01\u77e5\u8fd9\u5b69\u5b50\u6839\u672c \u4e0d\u662f\u7167\u68cb\u4e66\u884c\u7684\uff0c\u8fd9\u4e2a\u6218\u6cd5\u4e0d\u5408\u68cb\u8c31\uff0c\u6211\u53ef\u62b5\u5fa1\u4e0d\u4e86\u5566\uff01\u201d\u8bf4\u7f62\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u5085\u9752\u4e3b\u4e5f\u7b11\u9053\uff1a\u201c\u8fd9\u53eb\u505a\u5c3d\u4fe1\u4e66\u4e0d\u5982\u65e0\u4e66\uff0c\u58a8\u5b88\u6210\u89c4\u53ef\u4e0d\u884c\u7f57\uff01\u8bf4\u7740\uff0c\u7a81\u7136\u53eb\u6210\u5316\u9053\uff1a \u201c\u4f60\u628a\u68cb\u5b50\u5b8c\u5168\u63e1\u5728\u624b\u91cc\uff0c\u5411\u6211\u6253\u6765\uff0c\u4f2f\u4f2f\u6559\u4f60\u53d8\u620f\u6cd5\uff01\u201d\u6210\u5316\u770b\u4e86\u7236\u4eb2\u4e00\u773c\uff0c\u6b66\u5143\u82f1\u7b11 \u9053\uff0c\u201c\u4f2f\u4f2f\u53eb\u4f60\u6253\u4f60\u5c31\u6253\u561b\uff01\u201d\u5085\u9752\u4e3b\u52a0\u4e0a\u4e00\u53e5\u9053\uff1a\u201c\u800c\u4e14\u8981\u7528\u6253\u6697\u5668\u7684\u65b9\u6cd5\uff0c\u5c3d\u91cf\u65bd\u5c55\u51fa \u6765\uff0c\u8ba9\u6211\u770b\u770b\u4f60\u7684\u529f\u592b\u3002\u201d\u6210\u5316\u89c1\u7236\u4eb2\u4e0d\u9a82\u4ed6\u987d\u76ae\uff0c\u8fd8\u9f13\u52b1\u4ed6\u6253\uff0c\u5fc3\u4e2d\u5927\u559c\u3002\u4e8e\u662f\u63e1\u4e00\u5927\u628a \u68cb\u5b50\uff0c\u53cc\u624b\u4e00\u626c\uff0c\u7528\u201c\u6ee1\u5929\u82b1\u96e8\u201d\u7684\u6253\u91d1\u94b1\u9556\u624b\u6cd5\uff0c\u5411\u5085\u9752\u4e3b\u6d12\u53bb\u3002\u5085\u9752\u4e3b\u54c8\u54c8\u4e00\u7b11\uff0c\u5c06\u624b \u81c0\u7f29\u5728\u8896\u91cc\uff0c\u53ea\u89c1\u68cb\u5b50\u7eb7\u98de\uff0c\u843d\u5904\u65e0\u58f0\uff0c\u5085\u9752\u4e3b\u53cc\u8896\u4e00\u5c55\uff0c\u4e00\u679a\u679a\u68cb\u5b50\u76f8\u7ee7\u4ece\u4ed6\u8896\u4e2d\u843d\u4e0b\u3002 \u4f17\u4eba\u4e0d\u7981\u5927\u9a87\uff0c\u4ed6\u7adf\u7528\u4eac\u620f\u4e2d\u6c34\u8896\u7684\u529f\u592b\uff0c\u5c31\u80fd\u628a\u6697\u5668\u5377\u53bb\u3002\u8fd9\u79cd\u63a5\u6697\u5668\u7684\u529f\u592b\uff0c\u771f\u662f\u95fb\u6240 \u672a\u98ce\u89c1\u6240\u672a\u89c1\u3002 \u3000\u3000\u6b66\u6210\u5316\u8fd9\u5b69\u5b50\u53ef\u4e50\u574f\u4e86\uff0c\u8dd1\u8fc7\u6765\u5c31\u78e8\u5085\u9752\u4e3b\u6559\uff0c\u5085\u9752\u4e3b\u7b11\u7740\u5bf9\u6b66\u5143\u82f1\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u5c06\u8fd9 \u4e2a\u2018\u6c34\u8896\u63a5\u6697\u5668\u2019\u7684\u624b\u6cd5\uff0c\u6559\u7ed9\u6210\u5316\u505a\u89c1\u9762\u793c\uff0c\u8fd9\u4efd\u793c\u600e\u4e48\u6837\uff0c\u4f60\u6ee1\u610f\u4e86\u5427\uff1f\u201d\u6b66\u5143\u82f1\u5927 \u559c\uff0c\u8fde\u8bf4\u9053\uff1a\u201c\u6c42\u4e4b\u4e0d\u5f97\uff0c\u6c42\u4e4b\u4e0d\u5f97\uff01\u201d\u8d76\u5fd9\u53eb\u6210\u5316\u78d5\u5934\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e00\u4e2a\u5e84\u4e01\u8fdb\u6765\u5bf9\u6b66\u5e84\u4e3b\u8bf4\u4e86\u51e0\u53e5\uff0c\u6b66\u5e84\u4e3b\u9053\uff1a\u201c\u5218\u59d1\u5a18\u65e2\u7136\u6709\u7a7a\u4e86\uff0c\u5c31\u8bf7\u4ed6\u4eec\u8fdb \u6765\u5427\u3002\u201d\u4e0d\u4e00\u4f1a\uff0c\u5ba2\u5385\u5916\u4eba\u58f0\u5608\u6742\uff0c\u901a\u660e\u548c\u5c1a\u3001\u5e38\u82f1\u3001\u7a0b\u901a\u7b49\u7eb7\u7eb7\u56b7\u9053\uff1a\u201c\u97e9\u5927\u54e5\uff0c\u4f60\u6765\u4e86 \u5417\uff1f\u53ef\u60f3\u6b7b\u6211\u4eec\u4e86\u3002\u201d\u8bf4\u7740\u5c31\u51b2\u8fdb\u6765\uff0c\u5c06\u97e9\u5fd7\u90a6\u4e00\u628a\u62c9\u7740\u3002\u5728\u901a\u660e\u548c\u5c1a\u7b49\u540e\u9762\u7684\uff0c\u5219\u662f\u4ed6\u4eec \u7684\u5973\u9996\u9886\u5218\u90c1\u82b3\uff0c\u5218\u90c1\u82b3\u4e5f\u5fae\u5fae\u7b11\u7740\uff0c\u5728\u843d\u843d\u5927\u65b9\u4e2d\uff0c\u663e\u5f97\u5c0a\u8d35\u77dc\u6301\u3002 \u3000\u3000\u5085\u9752\u4e3b\u5728\u65c1\u770b\u4e86\uff0c\u6697\u6697\u55df\u53f9\u3002\u5fc3\u60f3\uff0c\u7537\u5973\u4e4b\u95f4\u7684\u4e8b\u60c5\uff0c\u771f\u662f\u5947\u5999\u3002\u5728\u81ea\u5df1\u773c\u4e2d\uff0c\u97e9\u5fd7\u90a6 \u786e\u662f\u4e00\u4e2a\u6206\u76f4\u7684\u6c49\u5b50\uff0c\u8fd9\u6b21\u77e5\u9053\u5218\u90c1\u82b3\u6709\u4e8b\u4e8e\u4e94\u53f0\u5c71\uff0c\u53c8\u8fdc\u8fdc\u8fdb\u6765\uff0c\u62d4\u5200\u76f8\u52a9\uff0c\u8fd9\u4efd\u60c5\u8c0a\uff0c \u53c8\u5c82\u662f\u666e\u901a\u53ef\u6bd4\u3002\u4f46\u770b\u5218\u90c1\u82b3\u7684\u795e\u60c5\uff0c\u5728\u5c0a\u91cd\u4e4b\u4e2d\u4fdd\u6301\u7740\u8ddd\u79bb\uff0c\u8fd9\u5934\u5a5a\u4e8b\uff0c\u770b\u6765\u5f88\u96be\u64ae\u5408\u3002 \u3000\u3000\u8fd9\u65f6\u5916\u9762\u53c8\u8fdb\u6765\u4e86\u4e24\u4e2a\u4eba\uff0c\u4e00\u4e2a\u77ed\u5c0f\u7cbe\u608d\uff0c\u4e24\u773c\u5955\u5955\u6709\u795e\uff1b\u4e00\u4e2a\u7d2b\u94dc\u80a4\u8272\uff0c\u957f\u76f8\u5f88\u662f\u5a01 \u6b66\u3002\u7ecf\u97e9\u5fd7\u90a6\u4ecb\u7ecd\uff0c\u59cb\u77e5\u77ed\u5c0f\u7cbe\u608d\u7684\u540d\u6768\u4e00\u7ef4\uff0c\u662f\u5929\u5730\u4f1a\u4e2d\u7684\u667a\u56ca\uff0c\u7d2b\u94dc\u80a4\u8272\u7684\u540d\u534e\u7d2b\u5c71\uff0c \u662f\u5929\u5730\u4f1a\u7684\u526f\u8235\u4e3b\u3002\u4e24\u4eba\u9762\u8272\uff0c\u90fd\u663e\u5f97\u9887\u4e3a\u7d27\u5f20\u3002 \u3000\u3000\u5218\u90c1\u82b3\u5f85\u4e24\u4eba\u5750\u5b9a\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u4ee5\u524d\u97e9\u603b\u8235\u4e3b\u548c\u6211\u8c08\u8fc7\u5f7c\u6b64\u5408\u4f5c\u4e4b\u4e8b\u3002\u6211\u60f3\u53cc\u65b9\u5b97\u65e8\u76f8 \u540c\uff0c\u590d\u56fd\u4e4b\u5fc3\uff0c\u5e76\u65e0\u4e8c\u81f4\uff0c\u6211\u4eec\u9c81\u738b\u65e7\u90e8\uff0c\u5c31\u4e00\u9f50\u52a0\u5165\u4f60\u4eec\u7684\u5929\u5730\u4f1a\u597d\u4e86\u3002\u201d \u3000\u3000\u6768\u4e00\u7ef4\u9053\uff1a\u201c\u90a3\u597d\u6781\u4e86\uff0c\u603b\u8235\u4e3b\u548c\u6211\u4eec\u90fd\u5f88\u6b22\u8fce\u3002\u201d\u97e9\u5fd7\u90a6\u6025\u9053\uff1a\u201c\u6768\u4e00\u7ef4\uff0c\u4e0d\u662f\u8fd9\u4e48 \u8bf4\uff01\u201d\u901a\u660e\u548c\u5c1a\u8bb6\u9053\uff1a\u201c\u603b\u8235\u4e3b\u7684\u610f\u601d\u662f\u2014\u2014\u201d\u97e9\u5fd7\u90a6\u622a\u7740\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4eec\u6b22\u8fce\u4f60\u4eec\u6216\u4f60 \u4eec\u6b22\u8fce\u6211\u4eec\uff0c\u5f7c\u6b64\u5408\u4f5c\uff0c\u5c31\u65e0\u4e3b\u5ba2\u4e4b\u5206\uff0c\u800c\u4e14\u6211\u7684\u610f\u601d\u662f\uff1a\u5e94\u8be5\u7531\u5218\u59d1\u5a18\u505a\u603b\u8235\u4e3b\uff01\u6211\u662f\u4e00 \u4e2a\u7c97\u4eba\uff0c\u563f\uff01\u563f\uff01\u201d\u97e9\u5fd7\u90a6\u7b11\u4e86\u4e24\u58f0\uff0c\u8fd8\u672a\u60f3\u5230\u600e\u6837\u8bf4\u4e0b\u53bb\uff0c\u5218\u90c1\u82b3\u5df2\u63a5\u7740\u8bf4\uff1a\u201c\u8fd8\u662f\u97e9\u8235 \u4e3b\u7ee7\u4efb\u7684\u597d\uff0c\u5929\u5730\u4f1a\u5728\u897f\u5317\u5df2\u6709\u57fa\u7840\uff0c\u6211\u4eec\u7684\u4eba\u6570\u4e5f\u6bd4\u8f83\u5c11\u3002\u201d\u6768\u4e00\u7ef4\u9053\uff1a\u201c\u662f\u5440\uff01\u6211\u4eec\u90fd \u4f69\u670d\u5218\u59d1\u5a18\uff0c\u5218\u59d1\u5a18\u8fd9\u756a\u8bdd\u662f\u6709\u9053\u7406\u3002\u201d\u97e9\u5fd7\u90a6\u77aa\u4e86\u4ed6\u4e00\u773c\u3002\u6768\u5e0c\u671b\u5218\u90c1\u82b3\u63a8\u8ba9\u3002 \u3000\u3000\u54ea\u77e5\u5218\u90c1\u82b3\u81ea\u6709\u6253\u7b97\uff0c\u5374\u4e0d\u63a8\u8ba9\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u97e9\u8235\u4e3b\u5982\u6b64\u63a8\u91cd\uff0c\u6211\u53ea\u597d\u4e0d\u81ea\u91cf\u529b \u4e86\u3002\u201d\u97e9\u5fd7\u90a6\u5927\u559c\uff0c\u901a\u660e\u548c\u5c1a\u4e5f\u5f88\u6b23\u7136\u3002\u53ea\u6709\u6768\u4e00\u7ef4\u6697\u6697\u4e0d\u60a6\u3002\u5f53\u4e0b\u5927\u5bb6\u8bae\u5b9a\uff0c\u62e9\u597d\u5409\u65e5\uff0c \u518d\u884c\u5f00\u5c71\u7acb\u8235\u4e4b\u793c\u3002\u800c\u4e14\u5728\u603b\u8235\u4e4b\u524d\uff0c\u97e9\u5fd7\u90a6\u81ea\u613f\u901a\u4ee4\u5404\u5730\u5929\u5730\u4f1a\u5f92\uff0c\u53d7\u5218\u90c1\u82b3\u7ea6\u675f\u3002 \u3000\u3000\u63a5\u7740\u5927\u5bb6\u8c08\u8d77\u4e94\u53f0\u5c71\u4e0a\u5927\u6218\u591a\u94ce\u548c\u695a\u662d\u5357\u4ece\u6ec7\u8fb9\u8d76\u6765\u7684\u4e8b\u3002\u5218\u90c1\u82b3\u9053\uff1a\u201c\u8fd9\u4e2a\u9b54\u5934\uff0c\u7684 \u786e\u96be\u4e8e\u5bf9\u4ed8\uff0c\u9664\u5085\u5e08\u53d4\u5916\uff0c\u6211\u4eec\u90fd\u4e0d\u662f\u4ed6\u5bf9\u624b\uff01\u8fd9\u6b21\u4ed6\u7ed9\u5085\u5e08\u53d4\u9707\u843d\u6df1\u8c37\uff0c\u6211\u53ea\u671b\u80fd\u5c31\u6b64\u9664 \u6389\u4ed6\u3002\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6211\u4e5f\u5236\u670d\u4e0d\u4e86\u4ed6\uff0c\u6211\u770b\u4f60\u4eec\u522b\u9ad8\u5174\uff0c\u4ee5\u4ed6\u7684\u529f\u529b\uff0c\u672a\u5fc5\u4f1a\u8dcc\u6b7b\u3002\u201d \u3000\u3000\u97e9\u5fd7\u90a6\u51dd\u795e\u9759\u542c\uff0c\u7a81\u7136\u62cd\u638c\u8bf4\u9053\uff1a\u201c\u6211\u5012\u60f3\u8d77\u4e00\u4e2a\u4eba\uff0c\u4e5f\u8bb8\u4ed6\u5236\u670d\u5f97\u4e86\u8fd9\u4e2a\u9b54\u5934\u3002\u201d\u901a \u660e\u548c\u5c1a\u5fd9\u95ee\u662f\u8c01\uff0c\u97e9\u5fd7\u90a6\u9053\uff1a\u201c\u6211\u4e5f\u672a\u89c1\u8fc7\u4ed6\uff0c\u53ea\u77e5\u9053\u4ed6\u53eb\u505a\u5929\u5c71\u795e\u8292\u51cc\u672a\u98ce\u3002\u201d\u5218\u90c1\u82b3 \u9053\uff1a\u201c\u8fd9\u4e2a\u5916\u53f7\u597d\u602a\uff01\u201d\u97e9\u5fd7\u90a6\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u79cd\u5f62\u5982\u77ed\u7bad\u7684\u8292\u523a\uff0c\u53ea\u751f\u957f\u5728\u5929\u5c71\u7684\u3002\u975e\u5e38\u5c16 \u9510\uff0c\u575a\u5982\u91d1\u94c1\uff0c\u523a\u4eba\u5f88\u75db\u3002\u4ed6\u7684\u5251\u6cd5\u8f9b\u8fa3\uff0c\u8bf4\u8bdd\u53c8\u5c16\u523b\u3002\u6240\u4ee5\u5f97\u4e86\u8fd9\u4e2a\u5916\u53f7\u3002\u53ef\u662f\u4ed6\u5728\u897f\u5317 \u7684\u540d\u5934\u53ef\u5927\u54e9\uff03\u8377\u85cf\u56de\u7586\u5404\u5730\u7684\u90e8\u843d\u90fd\u5f88\u4f69\u670d\u4ed6\uff0c\u5c71\u6c11\u7267\u6c11\u548c\u4ed6\u7684\u4ea4\u60c5\u4e5f\u5f88\u597d\uff0c\u53ea\u662f\u4ed6\u603b\u662f \u72ec\u6765\u72ec\u5f80\uff0c\u6bcf\u5230\u4e00\u5904\uff0c\u5c31\u6df7\u5728\u5c71\u6c11\u7267\u6c11\u4e4b\u4e2d\uff0c\u4e0d\u5bb9\u6613\u627e\u3002\u6211\u8fd9\u6b21\u5230\u5c71\u897f\u4e4b\u524d\uff0c\u66fe\u6d3e\u4e86\u597d\u51e0\u4e2a \u8ba4\u8bc6\u4ed6\u7684\u5f1f\u5144\u5230\u5904\u627e\u4ed6\u3002\u201d\u4f17\u4eba\u542c\u8bf4\u6709\u8fd9\u6837\u4e00\u4e2a\u4f20\u5947\u4eba\u7269\uff0c\u90fd\u5f88\u60ca\u8be7\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u53c8\u8c08\u4e86\u4e00\u4e9b\u201c\u5929\u5c71\u795e\u8292\u201d\u7684\u4f20\u5947\u4e8b\u8ff9\uff0c\u4f17\u4eba\u542c\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u5085\u9752\u4e3b\u95ee\u9053\uff1a\u201c\u8fd9\u4eba \u5251\u6cd5\u5982\u6b64\u5389\u5bb3\uff0c\u96be\u9053\u662f\u6666\u660e\u7985\u5e08\u7684\u53e6\u4e00\u4f20\u4eba\uff1f\u600e\u7684\u8001\u673d\u4ece\u672a\u542c\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u5218\u90c1\u82b3\u8f7b\u8f7b\u62cd\u638c\uff0c\u6253\u65ad\u4f17\u4eba\u8bdd\u67c4\uff0c\u8bf4\u9053\uff1a\u201c\u6682\u65f6\u4e0d\u5fc5\u7406\u4ec0\u4e48\u5929\u5c71\u795e\u8292\u5427\uff0c\u6211\u4eec\u5148\u8c08\u8c08\u6b63 \u7ecf\u4e8b\u3002\u7b2c\u4e00\u662f\u5f20\u516c\u5b50\u4eca\u5929\u5931\u9677\u5728\u4e94\u53f0\u5c71\uff0c\u82e5\u6551\u4e0d\u51fa\u6765\uff0c\u5bf9\u4e0d\u4f4f\u4ed6\u7684\u7236\u4eb2\u3002\u7b2c\u4e8c\u662f\u4eca\u5929\u591a\u94ce\u5e26 \u8fd9\u4e48\u591a\u7981\u536b\u519b\u6765\uff0c\u548c\u4ed6\u7684\u5e73\u5e38\u884c\u5f84\u4e0d\u7b26\uff0c\u5176\u4e2d\u5fc5\u6709\u8e51\u8df7\uff0c\u6ee1\u6e05\u5165\u5173\u4e4b\u540e\uff0c\u81f3\u4eca\u4e09\u5341\u4e00\u5e74\uff0c\u4e2d \u539f\u5df2\u5b9a\u3002\u53ea\u7559\u4e0b\u53f0\u6e7e\u4e0e\u56de\u7586\u8499\u85cf\u4e00\u5e26\u5c1a\u672a\u6536\u5165\u7248\u56fe\u3002\u53f0\u6e7e\u5b64\u60ac\u6d77\u5916\uff0c\u4e0d\u6210\u4ec0\u4e48\u6c14\u5019\uff1b\u897f\u5317\u4e0e \u585e\u5916\u5404\u90e8\u843d\uff0c\u82e5\u80fd\u8054\u5408\u6297\u6e05\uff0c\u518d\u4e0e\u53f0\u6e7e\u4f5c\u6388\u9f13\u4e4b\u65f6\uff0c\u6216\u8bb8\u5c1a\u6709\u70b9\u4f5c\u4e3a\u3002\u6211\u98ce\u95fb\u6e05\u5ef7\u6b63\u56fe\u7ecf\u7565 \u897f\u5317\uff0c\u591a\u94ce\u6b64\u6765\uff0c\u6216\u8bb8\u4e0e\u6b64\u6709\u5173\uff0c\u6211\u4eec\u5012\u4e0d\u80fd\u4e0d\u63a2\u63a2\u865a\u5b9e\u3002 \u3000\u3000\u535a\u9752\u4e3b\u95ee\u9053\uff1a\u201c\u5f20\u516c\u5b50\u662f\u2026\u2026\u201d\u5218\u90c1\u82b3\u9053\uff1a\u201c\u662f\u6211\u4eec\u5148\u5927\u5c06\u519b\u5f20\u714c\u5947\u7684\u516c\u5b50\uff0c\u4e5f\u662f\u6b66\u5e86 \u4e3b\u7684\u5e08\u4f84\uff0c\u7ec8\u6d77\u6d3e\u7684\u7b2c\u4e09\u4ee3\u5f1f\u5b50\u3002\u4ed6\u521d\u51fa\u5e08\u95e8\uff0c\u4fbf\u5931\u9677\u5728\u654c\u4eba\u624b\u91cc\uff0c\u975e\u60f3\u6cd5\u6551\u51fa\u6765\u4e0d\u53ef\u3002\u201d \u5f20\u60f6\u5947\u662f\u6297\u6e05\u7684\u540d\u5c06\uff0c\u4e5f\u662f\u4ee5\u524d\u7edf\u7387\u9c81\u738b\u5168\u519b\u7684\u4e3b\u5e05\uff0c\u5927\u5bb6\u542c\u4e86\u90fd\u5f88\u6b49\u7136\u3002 \u3000\u3000\u5085\u9752\u4e3b\u6bc5\u7136\u8d77\u7acb\u9053\uff1a\u201c\u4f17\u82f1\u96c4\u5982\u4e0d\u5acc\u5f03\u8001\u673d\uff0c\u6211\u4eca\u665a\u613f\u4e0e\u5192\u5c0f\u59d0\u63a2\u5c71\uff01\u201d\u5085\u9752\u4e3b\u6b66\u529f\u8d85 \u5353\uff0c\u81ea\u7136\u662f\u9002\u5f53\u4eba\u9009\uff0c\u53ea\u662f\u5927\u5bb6\u4e0d\u77e5\u9053\u5192\u6d63\u83b2\u5982\u4f55\uff0c\u4e00\u65f6\u90fd\u672a\u4f5c\u58f0\uff0c\u901a\u660e\u548c\u5c1a\u56b7\u9053\uff1a\u201c\u4e0d\u5982 \u6211\u968f\u5085\u524d\u8f88\u53bb\uff1f\u201d\u5192\u6d63\u83b2\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u6b66\u529f\u867d\u7136\u4e0d\u6d4e\uff0c\u4e0e\u5085\u4f2f\u4f2f\u540c\u53bb\uff0c\u6216\u5c1a\u4e0d\u4f1a \u5931\u9677\u3002\u201d\u8fd9\u65f6\u9662\u5b50\u5916\u4e00\u9635\u9e26\u566a\uff0c\u5085\u9752\u4e3b\u7b11\u9053\uff1a\u201c\u5916\u9762\u90a3\u68f5\u69d0\u6811\u4e0a\u6709\u4e00\u53ea\u4e4c\u9e26\uff0c\u53eb\u5f97\u4eca\u4eba\u70e6 \u8e81\uff0c\u6d63\u83b2\uff0c\u4f60\u628a\u5b83\u6349\u4e0b\u6765\u5427\uff01\u201d\u5192\u6d63\u83b2\u76c8\u76c8\u8d77\u7acb\uff0c\u5ffd\u5730\u53cc\u81c0\u4e00\u5f20\uff0c\u53ea\u4e00\u8dc3\u4fbf\u5230\u4e86\u5ead\u5fc3\uff0c\u66f4\u4e0d \u4f5c\u52bf\uff0c\u8eab\u5b50\u5e73\u5730\u62d4\u8d77\uff0c\u8f7b\u98d8\u98d8\u5730\u76f4\u7eb5\u4e0a\u69d0\u6811\u6811\u68a2\uff0c\u4e4c\u9e26\u201c\u54d1\u201d\u7684\u4e00\u58f0\uff0c\u632f\u7fc5\u6b32\u98de\uff0c\u5192\u6d63\u83b2\u8db3 \u5c16\u4e00\u70b9\u6811\u68a2\uff0c\u7bad\u4e00\u822c\u5730\u76f4\u51b2\u4e0a\u6570\u4e08\uff0c\u4e4c\u9e26\u521a\u521a\u98de\u8d77\uff0c\u5c31\u7ed9\u5192\u6d63\u83b2\u4e00\u628a\u635e\u7740\uff0c\u8df3\u5c06\u4e0b\u6765\uff0c\u4f17\u4eba \u90fd\u770b\u5f97\u5446\u4e86\uff01\u901a\u660e\u548c\u5c1a\u7fd8\u8d77\u5927\u62c7\u6307\u9053\uff1a\u201c\u8fd9\u6837\u7684\u8f7b\u529f\uff0c\u53bb\u5f97\uff01\u53bb\u5f97\uff01\u201d\u4f17\u4eba\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u5f53\u665a\uff0c\u5085\u9752\u4e3b\u4e0e\u5192\u6d63\u83b2\u6362\u4e86\u591c\u884c\u8863\uff0c\u8d81\u7740\u6708\u6697\u661f\u7a00\uff0c\u4ece\u4e94\u53f0\u5c71\u7684\u5317\u9762\uff0c\u76f4\u4e0a\u5230\u5c71\u9876\uff0c\u4e94 \u53f0\u5c71\u4e94\u5cf0\u5982\u53f0\uff0c\u662f\u6709\u540d\u7684\u5927\u5c71\uff0c\u591a\u94ce\u5e26\u6765\u7684\u51e0\u5343\u7981\u536b\u519b\u53ea\u80fd\u5728\u6e05\u51c9\u5bfa\u5468\u56f4\u5c71\u5c97\u8b66\u536b\uff0c\u54ea\u91cc\u7167 \u987e\u5f97\u5230\u5168\u5c71\uff0c\u5085\u5192\u4e8c\u4eba\uff0c\u8fc5\u5982\u98d8\u98ce\uff0c\u53c8\u662f\u591c\u8272\u5982\u58a8\uff0c\u7adf\u81ea\u6ca1\u4eba\u53d1\u73b0\u3002 \u3000\u3000\u6b63\u5f53\u4ed6\u4eec\u4ece\u5c71\u9876\u6084\u6084\u5730\u964d\u6eb6\u4e0b\u6765\uff0c\u672a\u5230\u534a\u5c71\u3002\u5ffd\u5730\u5085\u9752\u4e3b\u5728\u5192\u6d63\u83b2\u8033\u8fb9\u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u201d \u8eab\u5f62\u4e00\u8d77\uff0c\u659c\u91cc\u7a9c\u51fa\u6570\u4e08\uff0c\u5192\u6d63\u83b2\u4e5f\u8ddf\u7eb5\u800c\u5230\u3002\u53ea\u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u5e26\u7740\u9762\u7f69\uff0c\u84e6\u5730\u626d\u8fc7\u5934\u6765\u3002 \u3000\u3000\u6b32\u77e5\u6765\u8005\u662f\u8c01\uff0c\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u4e8c\u7ae0\u3000\u6d6a\u8ff9\u6c5f\u6e56\u3000\u6c34\u5c3d\u840d\u67af\u98ce\u4e0d\u8bed\u3000\u9690\u8eab\u53e4\u5239\u3000\u7a7a\u7075\u5e7b\u706d\u8272\u96be\u7559 \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u4e8c\u56de \u6d6a\u8ff9\u6c5f\u6e56\u3000\u6c34\u5c3d\u840d\u67af\u98ce\u4e0d\u8bed \u9690\u8eab\u53e4\u5239\u3000\u7a7a\u7075\u5e7b\u706d\u8272\u96be\u7559 \u3000\u3000\u9ed1\u591c\u4e2d\u5192\u6d63\u83b2\u53ea\u89c1\u90a3\u62ab\u7740\u9762\u7f69\u7684\u5c11\u5973\uff0c\u4e00\u53ea\u773c\u775b\u9732\u5728\u5916\u9762\uff0c\u987e\u76fc\u4e4b\u95f4\uff0c\u5149\u91c7\u7167\u4eba\uff0c\u5c31\u5982 \u9ed1\u6f06\u7684\u5929\u7a7a\u5d4c\u7740\u4e00\u9897\u661f\u661f\uff0c\u53c8\u5982\u767d\u6c34\u94f6\u4e2d\u5305\u7740\u9ed1\u6c34\u94f6\u3002\u90a3\u5c11\u5973\u89c1\u5085\u5192\u8ffd\u4e0a\uff0c\u707f\u7136\u4e00\u7b11\uff0c\u8bf4 \u9053\uff1a\u201c\u5404\u8d70\u5404\u7684\u5427\uff01\u201d\u4ece\u522b\u7684\u5c71\u5f84\u8dd1\u4e86\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u7684\u58f0\u97f3\u597d\u719f\uff0c\u5192\u6d63\u83b2\u6b63\u5f85\u8ffd\u53bb\u770b\u770b\u662f\u8c01\uff0c\u5085\u9752\u4e3b\u4e00\u628a\u6298\u7740\u5979\u9053\uff1a\u201c\u522b\u8ffd\u5979\uff0c\u5979\u5c31 \u662f\u4eca\u5929\u51fa\u573a\u7684\u62ab\u7eb1\u5c11\u5973\u6613\u5170\u73e0\uff0c\u5979\u4e00\u5b9a\u53e6\u6709\u4e8b\u60c5\uff0c\u4e0d\u613f\u548c\u6211\u4eec\u4e00\u8def\u3002\u201d\u5192\u6d63\u83b2\u5fc3\u60f3\uff1a\u600e\u7684\u8fd9 \u5c11\u5973\u884c\u5f84\u5982\u6b64\u795e\u79d8\uff1f \u3000\u3000\u5085\u5192\u4e8c\u4eba\u5c55\u5f00\u7edd\u9876\u8f7b\u529f\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u522b\u6e05\u51c9\u9769\u524a\u3002\u867d\u7136\u591c\u8272\u5982\u58a8\uff0c\u53ef\u662f\u73af\u7ed5\u7740\u6e05\u51c9\u5bfa \u7684\u4e94\u4e2a\u5927\u94dc\u5854\uff0c\u6bcf\u4e2a\u9ad8\u5341\u4e09\u5c42\uff0c\u6bcf\u5c42\u5916\u9762\u90fd\u5d4c\u7740\u5341\u516b\u76cf\u786b\u7483\u706f\uff0c\u5c06\u6e05\u51c9\u5bfa\u9644\u8fd1\u7167\u5f97\u901a\u660e\uff0c\u800c \u5bfa\u7684\u7981\u536b\u519b\u5de1\u903b\u6765\u5f80\uff0c\u663e\u89c1\u9632\u5b88\u5f97\u5f88\u662f\u4e25\u5bc6\u3002\u800c\u5f53\u4e2d\u7684\u4e3b\u5854\u524d\u9762\uff0c\u53c8\u6392\u7740\u4e00\u6392\u5f13\u7bad\u624b\uff0c\u800c\u4e14 \u6bcf\u5f20\u5f13\u90fd\u662f\u7bad\u5728\u5f26\u4e0a\uff0c\u6c14\u6c1b\u5f88\u662f\u7d27\u5f20\uff0c\u5085\u5192\u4e8c\u4eba\u4f0f\u5728\u4e00\u5757\u5ca9\u77f3\u540e\u9762\uff0c\u6b63\u60f3\u4e0d\u51fa\u7528\u4ec0\u4e48\u65b9\u6cd5\u6df7 \u8fdb\u53bb\u3002\u6b63\u601d\u91cf\u95f4\uff0c\u5ffd\u7136\u522e\u8fc7\u4e00\u9635\u72c2\u98ce\uff0c\u7802\u77f3\u4e71\u98de\uff0c\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u90a3\u5de6\u9762\u7684\u5927\u94dc\u5854\u7b2c\u4e09\u5c42\u6b63 \u9762\u7684\u4e09\u76cf\u7409\u7483\u706f\uff0c\u731b\u7684\u7184\u706d\uff01\u9ed1\u591c\u4e2d\u597d\u4f3c\u6709\u4e00\u6761\u4eba\u5f71\u51cc\u7a7a\u98de\u4e0a\uff0c\u7981\u536b\u519b\u54d7\u7136\u5927\u547c\uff0c\u5f13\u7bad\u7eb7\u7eb7 \u5411\u7a7a\u5c04\u53bb\u3002\u5fd9\u4e71\u4e2d\u53c8\u662f\u4e00\u9635\u72c2\u98ce\u522e\u8fc7\uff0c\u5f53\u4e2d\u4e3b\u5854\u7b2c\u4e09\u5c42\u6b63\u9762\u7684\u4e09\u76cf\u6d41\u7483\u706f\u53c8\u4e00\u9f50\u7184\u706d\u3002\u5085\u9752 \u4e3b\u6025\u62cd\u5192\u6d63\u83b2\uff0c\u559d\u58f0\uff1a\u201c\u5feb\u8d77\u201d\uff0c\u4e24\u4eba\u8d81\u5fd9\u4e71\u660f\u9ed1\u4e2d\u95ea\u8eab\u76f4\u51fa\uff0c\u8f7b\u8f7b\u4e00\u63a0\uff0c\u8df3\u4e0a\u4e86\u4e3b\u5854\u7684\u7b2c \u4e00\u5c42\u5854\u693d\uff0c\u5c06\u624b\u4e00\u6309\uff0c\u8eab\u5b50\u51ed\u7a7a\u5f39\u8d77\uff0c\u8d8a\u8fc7\u4e86\u7b2c\u4e8c\u5c42\u5c31\u5230\u4e86\u7b2c\u4e09\u5c42\uff0c\u4e24\u4eba\u4e00\u95ea\uff0c\u95ea\u5165\u5854\u5185\u3002 \u5085\u9752\u4e3b\u4fcf\u4fcf\u5bf9\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4eca\u591c\u6709\u7edd\u9876\u529f\u592b\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u90a3\u7409\u7483\u706f\u5b9a\u88ab\u4eba\u4ee5\u98de\u8757\u77f3\u4e4b\u7c7b\u7684\u6697 \u5668\uff0c\u7528\u91cd\u624b\u6cd5\u6253\u706d\u7684\uff01\u201d\u5916\u9762\u7684\u7981\u536b\u519b\uff0c\u95f9\u4e86\u4e00\u4f1a\u513f\uff0c\u4e0d\u89c1\u6709\u4eba\uff0c\u7591\u662f\u9ed1\u591c\u98de\u9e1f\u63a0\u8fc7\uff0c\u53c8\u7591 \u7409\u7483\u706f\u662f\u72c2\u98ce\u5377\u8d77\u7684\u7802\u77f3\u5076\u7136\u6253\u7184\u7684\uff0c\u4ed6\u4eec\u7d22\u6027\u70b9\u8d77\u677e\u679d\u706b\u628a\u5b88\u536b\uff0c\u4e5f\u4e0d\u518d\u67e5\u7a76\u4e86\u3002 \u3000\u3000\u4e3b\u5854\u5185\u6bcf\u4e00\u5c42\u90fd\u5f88\u5e7f\u9614\uff0c\u9664\u6389\u5f53\u4e2d\u7684\u5927\u5385\u5916\uff0c\u8fd8\u95f4\u6709\u51e0\u95f4\u623f\u95f4\u3002\u5085\u5192\u4e8c\u4eba\u4e00\u95ea\u5165\u5185\uff0c\u4e5f \u4ee5\u6697\u5668\u5c06\u5927\u5385\u7684\u51e0\u76cf\u706f\u6253\u706d\u3002\u4e0d\u4e00\u4f1a\uff0c\u6709\u4e24\u4e2a\u4eba\u62ff\u770b\u201c\u6c14\u6b7b\u98ce\u201d\uff08\u4e00\u79cd\u6beb\u4e0d\u900f\u98ce\u7684\u706f\u7b3c\uff09\u51fa \u6765\uff0c\u5600\u5600\u5495\u5495\u9053\uff1a\u201c\u600e\u7684\u4eca\u665a\u5c71\u98ce\u8fd9\u6837\u5389\u5bb3\uff0c\u5916\u9762\u7684\u7409\u7483\u5e7b\u7184\u706d\u4e86\uff0c\u8fde\u91cc\u9762\u7684\u4e5f\u5439\u7184\u4e86\uff0c\u771f \u662f\u90aa\u95e8\uff01\u201d\u5085\u5192\u4e8c\u4eba\u4e0d\u6562\u6020\u6162\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u95ea\u7535\u822c\u5730\u63a0\u5230\u4e24\u4eba\u9762\u524d\uff0c\u9a88\u6307\u4e00\u70b9\uff0c\u4e24\u4eba\u8fd8\u672a\u558a \u5f97\u51fa\u6765\uff0c\u5c31\u88ab\u5085\u5192\u4e8c\u4eba\u70b9\u4e86\u54d1\u7a74\uff0c\u4e00\u628a\u62d6\u51fa\u5916\u9762\uff0c\u7ad9\u5728\u5854\u6a90\u4e4b\u5904\uff0c\u501f\u7b2c\u56db\u5c42\u7409\u7483\u706f\u5c04\u4e0b\u7684\u5149 \u7ebf\u4e00\u770b\uff0c\u51e0\u4e4e\u53eb\u51fa\u58f0\u6765\uff01 \u3000\u3000\u8fd9\u4e24\u4eba\u4e0d\u662f\u7981\u536b\u519b\uff0c\u4e5f\u4e0d\u662f\u666e\u901a\u7684\u4eba\uff0c\u4ece\u670d\u9970\u4e0a\u770b\uff0c\u5206\u660e\u662f\u4e24\u4e2a\u592a\u76d1\u3002\u5085\u9752\u4e3b\u8fd8\u4e0d\u76f8 \u4fe1\uff0c\u4f38\u624b\u5f80\u4e0b\u4e00\u638f\uff0c\u8bf4\u201c\u662f\uff01\u201d\u5192\u6d63\u83b2\u7f9e\u5f97\u628a\u5934\u522b\u8fc7\u9762\u3002\u5085\u9752\u4e3b\u7a81\u7684\u9192\u8d77\u5192\u6d63\u83b2\u4e43\u662f\u5c11\u5973\uff0c \u4e5f\u89c9\u4e0d\u597d\u610f\u601d\u3002\u4f38\u624b\u4e00\u70b9\uff0c\u628a\u4e24\u4eba\u7684\u54d1\u7a74\u89e3\u4e86\u8fc7\u6765\uff0c\u4e00\u624b\u62c9\u7740\u4e00\u4e2a\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5feb \u8bf4\uff0c\u7687\u4e0a\u662f\u4e0d\u662f\u6765\u4e86\uff1f\u5728\u54ea\u4e00\u5c42\uff1f\u82e5\u6562\u4e0d\u8bf4\uff0c\u5c31\u628a\u4f60\u4eec\u63a8\u4e0b\u5854\u53bb\uff01\u201d \u3000\u3000\u94dc\u5854\u5dcd\u5ce8\uff0c\u4e0b\u4e34\u65e0\u5730\uff0c\u4e24\u4e2a\u592a\u76d1\u4e0d\u7531\u5f97\u6218\u6817\u8d77\u6765\uff0c\u7ed3\u7ed3\u5df4\u5df4\u4ed6\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u5728\u7b2c\u516d \u5c42\u3002\u201d\u5085\u9752\u4e3b\u4e00\u628a\u5c06\u4ed6\u4eec\u63a8\u8fdb\u5854\u5185\uff0c\u4e0e\u5192\u6d63\u83b2\u817e\u8eab\u4fbf\u8d77\uff0c\u8fde\u8d8a\u8fc7\u56db\u4e94\u4e24\u5c42\uff0c\u5230\u4e86\u7b2c\u516d\u5c42\u5854 \u5916\uff0c\u5f80\u91cc\u5077\u7aa5\uff0c\u679c\u7136\u89c1\u6709\u51e0\u4e2a\u592a\u76d1\u5728\u91cc\u9762\u6253\u76f9\uff0c\u5ba4\u4e2d\u6709\u4e00\u5f20\u9ec4\u7f15\u5e10\u76d6\u7740\u7684\u5927\u5e8a\u3002\u5085\u5192\u4e8c\u4eba\u5fc3 \u60f3\uff0c\u5e10\u91cc\u7761\u7684\u4e00\u5b9a\u662f\u7687\u5e1d\u3002\u5085\u5192\u4e8c\u4eba\u6258\u5730\u8df3\u5c06\u5165\u53bb\uff0c\u592a\u76d1\u4eec\u54d7\u7684\u60ca\u53eb\u8d77\u6765\uff0c\u5192\u6d63\u83b2\u4e00\u628a\u62c9\u5f00 \u9ec4\u5e10\uff0c\u4f38\u624b\u4fbf\u638f\u3002\u4e0d\u6599\u5e10\u4e2d\u4eba\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u8df3\u5c06\u8d77\u6765\uff0c\u4e00\u628a\u7cbe\u5149\u95ea\u76ee\u7684\u5315\u9996\uff0c\u5411\u5192\u6d63\u83b2\u5fc3 \u7a9d\u731b\u63d2\u3002\u5192\u6d63\u83b2\u8eab\u624b\u77eb\u6377\uff0c\u4e00\u53cd\u624b\u5c31\u5c06\u90a3\u4eba\u624b\u8155\u5201\u4f4f\uff0c\u5315\u9996\u53ea\u5dee\u534a\u5bf8\u6ca1\u6709\u523a\u5230\u3002 \u3000\u3000\u90a3\u4eba\u7684\u6b66\u529f\u7adf\u975e\u6cdb\u6cdb\uff0c\u624b\u8155\u9aa4\u7684\u7528\u529b\u5f80\u4e0b\u4e00\u6c89\uff0c\u5315\u9996\u867d\u6389\u5728\u5730\u4e0a\uff0c\u624b\u8155\u5374\u5df2\u8131\u4e86\u51fa\u6765\uff0c \u5de6\u638c\u201c\u94f6\u8679\u75be\u53f6\u201d\uff0c\u500f\u5730\u4fbf\u6311\u5192\u6d63\u83b2\u53f3\u8098\uff0c\u5192\u6d63\u83b2\u7528\u638c\u4e00\u683c\uff0c\u7adf\u7ed9\u9707\u9000\u6570\u6b65\uff0c\u90a3\u4eba\u5927\u559d\u4e00 \u58f0\uff0c\u62a2\u5c06\u51fa\u6765\uff0c\u4e0d\u6599\u5085\u9752\u4e3b\u8eab\u5f62\u5947\u5feb\uff0c\u98d8\u98ce\u4f3c\u7684\u6b3a\u8eab\u76f4\u8fdb\uff0c\u4fe1\u624b\u7ed9\u4e86\u4ed6\u4e24\u4e2a\u5634\u5df4\uff0c\u90a3\u4eba\u6b63\u5f85 \u8fd8\u51fb\uff0c\u5df2\u7ed9\u4ed6\u7528\u64d2\u62ff\u624b\u62ff\u7740\uff0c\u7529\u529b\u4e00\u634f\uff0c\u5168\u8eab\u8f6f\u9ebb\uff0c\u518d\u4e5f\u52a8\u5f39\u4e0d\u5f97\u3002\u90a3\u4eba\u56b7\u9053\uff1a\u201c\u4f60\u4eec\u80c6\u6562 \u72af\u4e0a\u5417\uff1f\u201d\uff0c\u5192\u6d63\u83b2\u89c1\u90a3\u4eba\u8eab\u4e0a\u7a7f\u7684\u662f\u201c\u9f99\u888d\u201d\uff0c\u5fc3\u60f3\u600e\u7684\u7687\u5e1d\u4e5f\u6709\u8fd9\u4e48\u597d\u7684\u6b66\u529f\u3002\u5085\u9752\u4e3b \u65e9\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u88c5\u4ec0\u4e48\u849c\uff1f\u201d\u4ed6\u5bf9\u5192\u6d63\u83b2\u9053\uff1a\u201c\u8fd9\u4eba\u4e0d\u662f\u7687\u5e1d\uff01\u201d\u539f\u6765\u5eb7\u7199\u7687\u5e1d\u5373\u4f4d\u65f6\uff0c\u4e0d \u8fc7\u516b\u5c81\uff0c\u73b0\u5728\u4e5f\u53ea\u662f\u4e8c\u5341\u591a\u5c81\u7684\u5c11\u5e74\uff0c\u800c\u5e10\u4e2d\u7684\u4eba\uff0c\u5374\u662f\u4e09\u56db\u5341\u5c81\u7684\u6c49\u5b50\u3002 \u3000\u3000\u5f53\u4e0b\u5085\u9752\u4e3b\u624b\u5f85\u5229\u5251\u3001\u5a01\u80c1\u592a\u76d1\u8bf4\u51fa\u7687\u5e1d\u6240\u5728\uff0c\u51e0\u4e2a\u5c0f\u9ec4\u95e8\u773c\u5149\u5149\u671b\u7740\u4e00\u4e2a\u8001\u592a\u76d1\uff0c\u5085 \u9752\u4e3b\u4f38\u624b\u5728\u4ed6\u8eab\u4e0a\u8f7b\u8f7b\u4e00\u62cd\uff0c\u90a3\u8001\u592a\u76d1\u75db\u5f7b\u5fc3\u80ba\uff0c\u5fd9\u9053\uff1a\u201c\u6211\u8bf4\uff0c\u6211\u8bf4\uff01\u201d \u3000\u3000\u8fd9\u8001\u592a\u76d1\u662f\u7687\u5e1d\u7684\u8fd1\u8eab\u5185\u4f8d\u4e4b\u4e00\uff0c\u8bf4\u9053\uff1a\u201c\u7687\u5e1d\u4e0d\u5728\u8fd9\u91cc\uff0c\u4ed6\u867d\u7136\u662f\u9a7b\u5728\u8fd9\u4e00\u5c42\uff0c\u4f46\u8fd9 \u5ea7\u94dc\u5854\u5e95\u4e0b\uff0c\u6709\u5730\u9053\u76f4\u901a\u6e05\u51c9\u5bfa\u8001\u76d1\u5bfa\u548c\u5c1a\u7684\u7985\u623f\uff0c\u4ed6\u4ece\u5730\u9053\u53bb\u770b\u8001\u548c\u5c1a\u53bb\u4e86\uff0c\u5085\u9752\u4e3b\u6307\u7740 \u90a3\u5e10\u4e2d\u4eba\u95ee\u9053\uff1a\u201c\u4ed6\u662f\u8c01\uff1f\u201d\u8001\u592a\u76d1\u9053\uff1a\u201c\u4ed6\u662f\u5bab\u4e2d\u7684\u5df4\u56fe\u9c81\uff08\u52c7\u58eb\u4e4b\u610f\uff0c\u6e05\u671d\u5b98\u8854\uff09\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u60f3\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u82e5\u60f3\u6d3b\u547d\uff0c\u987b\u4f9d\u6211\u7684\u6446\u5e03\u3002\u201d\u8001\u592a\u76d1\u6025\u6025\u70b9\u5934\uff0c\u90a3\u4e2a\u5df4 \u56fe\u9c81\u867d\u7136\u5f3a\u786c\uff0c\u4f46\u7ed9\u5085\u9752\u4e3b\u5236\u4f4f\uff0c\u77e5\u9053\u82e5\u4e0d\u7b54\u5e94\uff0c\u5fc5\u843d\u6b8b\u5e9f\uff0c\u4e5f\u53ea\u597d\u7b54\u5141\u4e86\u3002 \u3000\u3000\u5085\u9752\u4e3b\u968f\u624b\u5265\u4e0b\u4e00\u4e2a\u5c0f\u9ec4\u95e8\u7684\u670d\u9970\uff0c\u53eb\u5192\u6d63\u83b2\u62ab\u4e0a\uff0c\u88c5\u6210\u592a\u76d1\u3002\u592a\u76d1\u8bf4\u8bdd\u884c\u52a8\uff0c\u672c\u6765\u5c31 \u50cf\u5973\u4eba\uff0c\u5192\u6d63\u83b2\u8fd9\u4e00\u4f2a\u88c5\uff0c\u6b63\u597d\u5408\u9002\u3002\u5085\u9752\u4e3b\u9053\uff1a\u201c\u4f60\u5e26\u6211\u4eec\u4ece\u5730\u9053\u8fdb\u53bb\uff0c\u82e5\u5730\u9053\u4e2d\u628a\u5b88\u7684 \u4eba\u95ee\u8d77\uff0c\u4f60\u5c31\u8bf4\u6211\u662f\u7687\u4e0a\u8bf7\u6765\u7684\u592a\u533b\u3002\u201d\u8bf4\u7f62\u5085\u9752\u4e3b\u5c06\u5ba4\u4e2d\u7684\u5c0f\u592a\u76d1\u4e00\u4e00\u70b9\u4e86\u54d1\u7a74\uff0c\u8981\u5f85\u516d \u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u624d\u80fd\u81ea\u89e3\u3002\u6599\u7406\u5b8c\u6bd5\uff0c\u5085\u9752\u4e3b\u508d\u7740\u90a3\u4e2a\u5df4\u56fe\u9c81\uff0c\u5192\u6d63\u83b2\u508d\u7740\u90a3\u4e2a\u8001\u592a\u76d1\uff0c\u4e00\u4eba \u631f\u6301\u4e00\u4e2a\uff0c\u8bf4\u58f0\uff1a\u201c\u8d70\uff01\u201d\u8001\u592a\u76d1\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u4f38\u624b\u5728\u5899\u4e0a\u4e00\u6309\uff0c\u5899\u4e0a\u5f00\u51fa\u4e86\u4e00\u6247\u6d3b\u95e8\uff0c\u590d\u58c1 \u91cc\u5b89\u6709\u767e\u51e0\u7ea7\u68af\u5b50\uff0c\u76f4\u901a\u5230\u5730\u9053\u53e3\u3002 \u3000\u3000\u5730\u9053\u4e2d\u5b88\u536b\u68ee\u4e25\uff0c\u6bcf\u9694\u5341\u4f59\u6b65\u5c31\u6709\u4e00\u4e2a\u6b66\u58eb\u7ad9\u5c97\u3002\u90a3\u4e2a\u8001\u592a\u76d1\u5927\u7ea6\u662f\u66fe\u8ddf\u968f\u7687\u4e0a\u5728\u8fd9\u6761 \u5730\u9053\u8fdb\u51fa\u8fc7\uff0c\u6b66\u58eb\u4eec\u4e00\u70b9\u4e5f\u4e0d\u7591\u5fc3\uff0c\u8fde\u95ee\u4e5f\u4e0d\u95ee\uff0c\u5c31\u8ba9\u4ed6\u4eec\u5f80\u91cc\u9762\u76f4\u95ef\u3002\u4e0d\u4e45\uff0c\u4fbf\u5230\u4e86\u5730\u9053 \u7684\u5c3d\u5934\u3002\u5085\u9752\u4e3b\u5192\u6d63\u83b2\u631f\u6301\u7740\u8001\u592a\u76d1\u548c\u5df4\u56fe\u9c81\uff0c\u51dd\u8eab\u6b62\u6b65\uff0c\u5728\u5730\u9053\u7684\u51fa\u53e3\u5904\u505c\u4e86\u4e0b\u6765\uff0c\u4e0a\u9762 \u4eba\u58f0\uff0c\u900f\u4e0b\u5730\u9053\uff0c\u867d\u7136\u4e0d\u5f88\u6e05\u695a\uff0c\u53ef\u662f\u5374\u5206\u8fa8\u5f97\u51fa\u90a3\u662f\u201c\u6e38\u9f99\u5251\u201d\u695a\u662d\u5357\u7684\u58f0\u97f3\u3002\u5085\u5192\u4e8c\u4eba \u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u5bb6\u4f19\u679c\u7136\u6ca1\u6709\u8dcc\u6b7b\uff01 \u3000\u3000\u4e0a\u9762\u7684\u4eba\u4f3c\u4e4e\u8d8a\u8bf4\u8d8a\u5927\u58f0\uff0c\u5085\u5192\u4e8c\u4eba\u53ea\u542c\u5f97\u4e00\u4e2a\u5c11\u5e74\u7684\u58f0\u97f3\u5f88\u5a01\u4e25\u5730\u559d\u95ee\u9053\uff1a\u201c\u5434\u4e09\u6842 \u8fd9\u53ae\u771f\u6562\u8fd9\u6837\uff1f\u201d\u695a\u662d\u5357\u6218\u6218\u5162\u5162\u7684\u58f0\u97f3\u7b54\u9053\uff1a\u201c\u5974\u813e\u4e0d\u6562\u8bf4\u8c0e\u3002\u201d\u8bf4\u5b8c\u4e4b\u540e\uff0c\u4e0a\u9762\u5ffd\u7136\u9759 \u5bc2\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u5085\u5192\u4e8c\u4eba\u6b63\u60ca\u7591\u95f4\uff0c\u5ffd\u5730\u8f70\u9686\u4e00\u58f0\uff0c\u5730\u9053\u4e24\u58c1\u7a81\u7136\u63a8\u51fa\u4e00\u9053\u94c1\u95f8\uff0c\u5085\u5192\u4e8c\u4eba \u6115\u7136\u56de\u987e\uff0c\u53ea\u89c1\u90a3\u9053\u94c1\u95f8\u5df2\u628a\u81ea\u5df1\u548c\u4e24\u4e2a\u7ad9\u5c97\u6b66\u58eb\u90fd\u5c01\u9501\u5728\u8fd9\u4e00\u6bb5\u5730\u9053\u4e4b\u5185\u3002\u4e0a\u9762\u695a\u662d\u5357\u5927 \u58f0\u9669\u559d\uff1a\u201c\u4ec0\u4e48\u4eba\u6562\u5728\u5e95\u4e0b\u5077\u542c\uff1f\u201d \u3000\u3000\u539f\u6765\u695a\u662d\u5357\u6b66\u529f\u8d85\u5353\uff0c\u8033\u806a\u76ee\u660e\uff0c\u5085\u5192\u4e00\u884c\u4eba\u867d\u7136\u653e\u8f7b\u811a\u6b65\uff0c\u53ef\u662f\u5230\u5e95\u8fd8\u6709\u58f0\u606f\uff0c\u5c24\u5176 \u90a3\u4e2a\u8001\u592a\u76d1\u7684\u811a\u6b65\u66f4\u91cd\u3002\u695a\u662d\u5357\u542c\u5f97\u811a\u6b65\u58f0\u884c\u8fd1\u5374\u7a81\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u4e45\u4e45\u4e0d\u89c1\u58f0\u54cd\uff0c\u4e0d\u7981\u8d77\u4e86 \u7591\u5fc3\uff0c\u6084\u6084\u5730\u7980\u544a\u7687\u5e1d\uff0c\u7687\u5e1d\u4e00\u60f3\uff1a\u4e0b\u9762\u7ad9\u5c97\u7684\u6b66\u58eb\uff0c\u6700\u8fd1\u7684\u8fd9\u5bf9\uff0c\u4e5f\u8ddd\u79bb\u5730\u9053\u53e3\u5341\u4e08\uff0c\u4e0d \u4f1a\u8d70\u8fd1\u524d\u6765\uff0c\u82e5\u662f\u4e3b\u5854\u4e2d\u7684\u592a\u76d1\uff0c\u4ed6\u4eec\u6ca1\u6709\u81ea\u5df1\u5429\u5490\uff0c\u4e5f\u4e0d\u4f1a\u6765\uff0c\u800c\u4e14\u5c31\u662f\u6765\u4e86\uff0c\u4e5f\u4e0d\u4f1a\u505c \u5728\u95e8\u53e3\uff0c\u65e2\u4e0d\u7980\u544a\uff0c\u53c8\u8fdf\u8fdf\u4e0d\u8fdb\uff0c\u5fc3\u4e2d\u5927\u7591\uff0c\u4f38\u624b\u5c31\u6309\u673a\u62ec\uff0c\u628a\u8fd1\u5730\u9053\u4e00\u6bb5\u7684\u94c1\u95f8\u5f00\u4e86\u51fa \u6765\uff0c\u559d\u9053\uff1a\u201c\u66ff\u6211\u8fdb\u53bb\u628a\u5077\u542c\u7684\u4eba\u6349\u51fa\u6765\u3002\u201d \u3000\u3000\u5730\u4e0b\u7684\u5085\u9752\u4e3b\u673a\u4f36\u5230\u6781\uff0c\u94c1\u95f8\u4e00\u5f00\uff0c\u4ed6\u5c31\u5c06\u8001\u592a\u76d1\u548c\u5df4\u56fe\u9c81\u70b9\u5012\uff0c\u55d6\u7684\u4e00\u58f0\uff0c\u62d4\u51fa\u4f69 \u5251\u3002\u8fd9\u65f6\u90a3\u4e24\u4e2a\u7ad9\u5c97\u6b66\u58eb\u4e5f\u5df2\u60ca\u89c9\uff0c\u53cc\u53cc\u6251\u4e0a\u524d\u6765\uff0c\u4f46\u600e\u7981\u5f97\u5085\u9752\u4e3b\u795e\u6280\u60ca\u4eba\uff0c\u53ea\u4e09\u4e24\u4e2a\u7167 \u9762\uff0c\u4fbf\u7ed9\u5085\u9752\u4e3b\u523a\u7740\u7a74\u9053\u3002\u5730\u9053\u53e3\u7684\u94c1\u76d6\u677f\u7a81\u5730\u6380\u8d77\uff0c\u5085\u9752\u4e3b\u559d\u58f0\u201c\u5c0f\u5fc3\uff01\u201d\u5916\u9762\u6697\u5668\u7eb7\u7eb7 \u6253\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5085\u9752\u4e3b\u3001\u5192\u6d63\u83b2\u5c55\u5f00\u5251\u6cd5\uff0c\u6d51\u8eab\u4e0a\u4e0b\uff0c\u5377\u8d77\u5bd2\u5149\uff0c\u6697\u80fd\u6253\u6765\uff0c\u7ed9\u649e\u5f97\u7eb7\u98de\uff0c\u78b0\u5728\u4e24\u8fb9\u77f3 \u58c1\u4e0a\u4e01\u5f53\u4f5c\u54cd\u3002\u5085\u9752\u4e3b\u5927\u53eb\u4e00\u58f0\u201c\u95ef\u51fa\u53bb\uff01\u201d\u5728\u6697\u5668\u5982\u96e8\u4e2d\uff0c\u786c\u94bb\u51fa\u5916\u3002\u65e0\u6781\u5251\u201c\u8fce\u98ce\u626b \u5c18\u201d\uff0c\u8eab\u968f\u5251\u8fdb\uff0c\u4f46\u89c1\u4e00\u5708\u94f6\u5149\uff0c\u84e6\u5730\u6eda\u51fa\uff0c\u5192\u6d63\u83b2\u4e5f\u7d27\u7d27\u8ddf\u7740\u7a9c\u51fa\u4e86\u5730\u9053\u3002 \u3000\u3000\u6e38\u9f99\u5251\u695a\u662d\u5357\u65e9\u5df2\u5b88\u5728\u6d1e\u53e3\uff0c\u4e00\u89c1\u4eba\u51fa\uff0c\u5f53\u5934\u4e00\u5251\u5c31\u5288\u5c06\u4e0b\u6765\uff0c\u5085\u9752\u4e3b\u6a2a\u5251\u4e00\u626b\uff0c\u4f46\u542c \u5f97\u5251\u5c16\u4e0a\u201c\u55e1\u55e1\u201d\u4e00\u9635\u5578\u58f0\uff0c\u4e24\u628a\u5251\u90fd\u7ed9\u5bf9\u65b9\u8361\u4e86\u5f00\u53bb\u3002\u695a\u662d\u5357\u5b9a\u775b\u4e00\u770b\uff0c\u89c1\u6765\u7684\u6b63\u662f\u5bf9\u5934 \u5085\u9752\u4e3b\uff0c\u53c8\u6c14\u53c8\u6012\uff0c\u5927\u559d\u4e00\u58f0\u201c\u8001\u5339\u592b\uff0c\u4eca\u65e5\u4e0e\u4f60\u518d\u51b3\u751f\u6b7b\uff01\u201d\u4e00\u53e3\u5251\u72e0\u72e0\u6740\u6765\u3002\u5085\u9752\u4e3b\u4e5f \u8c41\u51fa\u4e86\u6027\u547d\u4e0e\u4ed6\u6076\u6597\u3002\u8fd9\u65f6\u5192\u6d63\u83b2\u4e5f\u5df2\u7a9c\u4e86\u51fa\u6765\uff0c\u5979\u89c1\u5ba4\u4e2d\u5c11\u5e74\u6b63\u5728\u8d70\u907f\uff0c\u7acb\u5373\u4e00\u8dc3\u800c\u524d\uff0c \u4e00\u628a\u6293\u53bb\u3002 \u3000\u3000\u4f5b\u6bbf\u5916\u7684\u536b\u58eb\u5728\u542c\u5f97\u695a\u662d\u5357\u5406\u559d\u65f6\uff0c\u5df2\u8702\u6d8c\u5165\u5185\uff0c\u4ed6\u4eec\u54ea\u80af\u8ba9\u5192\u6d63\u83b2\u6293\u7740\u7687\u5e1d\uff0c\u970e\u65f6 \u95f4\uff0c\u51e0\u822c\u5175\u5668\uff0c\u6a2a\u91cc\u626b\u6765\uff0c\u5192\u6d63\u83b2\u56de\u5251\u4e00\u6321\uff0c\u7f13\u5f97\u4e00\u7f13\uff0c\u5eb7\u7199\u7687\u5e1d\u5df2\u4ece\u4fa7\u95e8\u8d70\u8fdb\u5185\u5ba4\u53bb\u4e86\u3002 \u3000\u3000\u5085\u9752\u4e3b\u4f7f\u51fa\u6d51\u8eab\u7edd\u6280\uff0c\u5251\u62db\u53d1\u51fa\uff0c\u76f4\u5982\u98ce\u7ffb\u4e91\u6d8c\uff0c\u695a\u662d\u5357\u8fde\u756a\u6251\u51fb\uff0c\u8fde\u8d70\u9669\u62db\uff0c\u90fd\u672a\u5f97 \u624b\u3002\u4f46\u5085\u9752\u4e3b\u867d\u6321\u5f97\u4f4f\u695a\u662d\u5357\uff0c\u5374\u5403\u4e8f\u5728\u5b64\u638c\u96be\u9e23\uff0c\u4ed6\u6025\u4e2d\u751f\u667a\uff0c\u731b\u7684\u89d1\u51c6\u5f53\u524d\u4e00\u4eba\uff0c\u7a81\u5730 \u5251\u950b\u4e00\u8f6c\uff0c\u5251\u62db\u5982\u7535\uff0c\u970e\u7684\u5c31\u5c06\u90a3\u4eba\u624b\u8155\u622a\u65ad\u3002\u90a3\u4eba\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u6eda\u5012\u5730\u4e0a\uff0c\u5085\u9752\u4e3b\u4ece\u7f3a \u53e3\u91cc\u4fbf\u7a9c\u51fa\u53bb\uff0c\u4e00\u8df3\u98de\u4e0a\u4e86\u4f5b\u6bbf\u5f53\u4e2d\u7684\u795e\u575b\u3002 \u3000\u3000\u8fd9\u795e\u575b\u5f88\u662f\u5bbd\u5e7f\uff0c\u4e0a\u9762\u5851\u7740\u516d\u4e2a\u5c0a\u8005\uff0c\u5341\u516b\u7f57\u6c49\u3002\u4e8c\u5341\u56db\u5c0a\u5927\u4f5b\u50cf\u90fd\u662f\u751f\u94c1\u94f8\u6210\uff0c\u6392\u5217 \u53c8\u4e0d\u6574\u9f50\u3002\u5085\u9752\u4e3b\u5728\u795e\u575b\u4e0a\u501f\u4f5b\u50cf\u4f5c\u63a9\u62a4\uff0c\u7a7f\u6765\u63d2\u53bb\u3002\u695a\u662d\u5357\u548c\u5339\u58eb\u4eec\uff0c\u65e0\u6cd5\u56f4\u653b\uff0c\u53ea\u597d\u548c \u4ed6\u4f3c\u6349\u8ff7\u85cf\u822c\u7684\u4e92\u76f8\u8ffd\u9010\u3002 \u3000\u3000\u8fd9\u65f6\u5192\u6d63\u83b2\u4e5f\u7ed9\u536b\u58eb\u4eec\u72e0\u72e0\u8ffd\u9010\uff0c\u5e78\u597d\u536b\u58eb\u4e2d\u7684\u9ad8\u624b\uff0c\u90fd\u534f\u52a9\u695a\u662d\u5357\u5bf9\u4ed8\u5085\u9752\u4e3b\u53bb\u4e86\uff0c \u9762\u5192\u6d63\u83b2\u53c8\u6700\u957f\u4e8e\u8f7b\u8eab\u529f\u592b\uff0c\u5728\u4f5b\u5802\u5185\u7a9c\u6765\u7a9c\u53bb\uff0c\u6ed1\u5982\u6e38\u9c7c\uff0c\u7adf\u7136\u6ca1\u5728\u7ed9\u4ed6\u4eec\u6349\u7740\u3002\u6b63\u5728\u7d27 \u6025\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u5085\u9752\u4e3b\u5728\u795e\u575b\u4e0a\u626c\u58f0\u53eb\u9053\uff1a\u201c\u83b2\u513f\uff0c\u5582\u4ed6\u4eec\u7740\u7802\u5b50\uff01\u201d \u3000\u3000\u539f\u6765\u5085\u9752\u4e3b\u957f\u4e8e\u533b\u672f\uff0c\u4ed6\u81ea\u5df1\u867d\u7136\u4e0d\u559c\u7528\u6697\u5668\uff0c\u4f46\u5374\u7ed9\u5192\u6d63\u83b2\u7ec3\u4e86\u4e00\u79cd\u6697\u5668\uff0c\u593a\u547d\u795e \u7802\u3002\u8fd9\u94c1\u7802\u53c8\u5206\u4e24\u79cd\uff0c\u4e00\u79cd\u662f\u7528\u6bd2\u836f\u6db2\u6d78\u5236\u8fc7\u7684\uff0c\u4e00\u79cd\u662f\u65e0\u6bd2\u7684\uff0c\u5085\u9752\u4e3b\u4f20\u5979\u8fd9\u79cd\u6697\u5668\u65f6\uff0c \u8c06\u8c06\u544a\u8beb\uff0c\u975e\u81f3\u6781\u5371\u9669\u5173\u5934\uff0c\u4e0d\u51c6\u7528\u6bd2\u7684\u90a3\u79cd\uff0c\u8fd9\u6b21\u7531\u5085\u9752\u4e3b\u5148\u53eb\u5979\u7528\uff0c\u7b97\u5f97\u662f\u7834\u5929\u8352\u7684\u7b2c \u4e00\u906d\u3002 \u3000\u3000\u5192\u6d63\u83b2\u4e5f\u662f\u521d\u6b21\u906d\u9022\u8fd9\u6837\u7684\u5927\u5e38\u70d8\uff0c\u5fd9\u4e71\u4e2d\u7adf\u6ca1\u8bb0\u8d77\u81ea\u5df1\u8fd8\u6709\u8fd9\u79cd\u5389\u5bb3\u7684\u6697\u5668\uff0c\u7ed9\u5085\u9752 \u4e3b\u63d0\u8d77\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u5de6\u5b50\u6234\u8d77\u9e7f\u76ae\u624b\u5957\uff0c\u5f80\u6697\u5668\u56ca\u4e2d\u4e00\u63a2\uff0c\u63e1\u4e86\u4e00\u628a\u6709\u6bd2\u7684\u593a\u547d\u795e\u7802\uff0c\u628a\u624b \u4e00\u626c\uff0c\u795e\u7802\u5206\u6210\u51e0\u6761\u76f4\u7ebf\u5411\u8ffd\u6765\u7684\u654c\u4eba\u6253\u53bb\uff0c\u7acb\u5373\u6709\u51e0\u4eba\u7ed9\u6253\u4e2d\u4e86\u5934\u9762\uff0c\u867d\u7136\u5e76\u4e0d\u89c1\u75db\uff0c\u53ef \u662f\u4e0d\u4e45\u5c31\u89c9\u5f97\u5468\u8eab\u9ebb\u75d2\u3002\u8fd9\u4e9b\u536b\u58eb\u90fd\u662f\u8001\u4e8e\u6c5f\u6e56\u7684\u4e86\uff0c\u542c\u5f97\u5085\u9752\u4e3b\u8bf4\u201c\u6bd2\u7802\u5b50\u201d\u65f6\u5df2\u7ecf\u7559 \u5fc3\uff0c\u4e00\u65e6\u611f\u5230\u5f02\u6837\uff0c\u5982\u4f55\u4e0d\u614c\uff1f\u5413\u5f97\u4ed6\u4eec\u90fd\u4e0d\u6562\u8feb\u8fd1\u5192\u6d63\u83b2\uff1f \u3000\u3000\u53ef\u662f\u795e\u7802\u53ea\u80fd\u53ca\u8fd1\uff0c\u4e0d\u80fd\u53ca\u8fdc\uff0c\u654c\u4eba\u8ddd\u79bb\u4e24\u4e09\u4e08\u5916\uff0c\u4fbf\u65e0\u529e\u6cd5\u3002\u90a3\u4e9b\u536b\u58eb\u79bb\u5f00\u4e86\u795e\u7802\u7684 \u6709\u6548\u8303\u56f4\uff0c\u53c8\u7eb7\u7eb7\u5730\u5411\u5192\u6d63\u83b2\u53d1\u5c04\u6697\u5668\u3002\u5192\u6d63\u83b2\u4e2d\u5251\u5355\u8eab\uff0c\u5e94\u4ed8\u5f88\u662f\u6709\u6613\u3002\u5ffd\u542c\u5f97\u5085\u9752\u4e3b\u53c8 \u662f\u4e00\u58f0\u558a\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u987e\u6211\uff0c\u4f60\u5148\u95ef\u51fa\u53bb\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u53c8\u662f\u4e24\u628a\u593a\u547d\u795e\u7802\uff0c\u5728\u4f17\u536b\u58eb\u8d70\u907f\u4e2d\uff0c\u84e6\u5730\u56de\u8eab\u4fbf\u8d70\uff0c\u7bad\u4e00\u822c\u5730\u7a7f\u51fa\u7a97\u6237\uff0c\u968f\u5373 \u65bd\u5c55\u201c\u58c1\u864e\u6e38\u5899\u201d\u4e4b\u6280\uff0c\u95ea\u7535\u822c\u5730\u76f4\u4e0a\u5230\u5927\u4f5b\u5802\u7684\u74e6\u9762\u4e4b\u4e0a\u3002 \u3000\u3000\u6e05\u51c9\u5bfa\u7684\u5927\u4f5b\u6bbf\u662f\u7528\u5317\u4eac\u51fa\u4ea7\u7684\u7409\u7483\u74e6\u76d6\u7684\uff0c\u8fd9\u79cd\u74e6\u5149\u6ed1\u5f02\u5e38\uff0c\u96be\u4e8e\u9a7b\u8db3\uff0c\u5192\u6d63\u83b2\u7d22\u6027 \u5de6\u53f3\u8db3\u4ea4\u66ff\u6ed1\u884c\uff0c\u970e\u65f6\u95f4\u5c31\u6ed1\u5230\u4e86\u5c4b\u9876\u7684\u4e2d\u592e\uff0c\u6e05\u51c9\u5bfa\u5404\u5904\u7684\u4f5b\u706f\u4e0e\u4e94\u4e2a\u94a2\u5854\u4e0a\u6240\u5d4c\u7684\u7409\u7483 \u706f\u4ea4\u76f8\u8f89\u6620\uff0c\u7167\u8000\u5f97\u660e\u5982\u767d\u663c\u3002\u5192\u6d63\u83b2\u4e00\u4eba\u5728\u74e6\u9762\u4e0a\u6ed1\u884c\uff0c\u76ee\u6807\u6781\u663e\uff0c\u5730\u4e0b\u7684\u6697\u5668\u53c8\u7eb7\u7eb7\u6253 \u6765\uff0c\u6bd4\u5728\u4f5b\u5802\u4e2d\u66f4\u96be\u8eb2\u95ea\u3002 \u3000\u3000\u5192\u6d63\u83b2\u817e\u632a\u8d8b\u907f\uff0c\u767e\u5fd9\u4e2d\u7adf\u7ed9\u4e00\u7bad\u6253\u98de\u4e86\u98ce\u5e3d\uff0c\u9732\u51fa\u6ee1\u5934\u79c0\u53d1\uff0c\u5979\u5fc3\u4e2d\u4e00\u614c\uff0c\u731b\u7136\u95f4\u5730 \u4e0b\u53c8\u6253\u4e0a\u4e00\u4e2a\u6697\u5668\uff0c\u5706\u5706\u7684\u5e26\u7740\u5578\u58f0\uff0c\u52b2\u9053\u6781\u5927\uff0c\u5979\u5de6\u8db3\u4e00\u6ed1\uff0c\u524d\u9762\u7409\u7483\u74e6\u7830\u7136\u4e00\u58f0\uff0c\u7adf\u7ed9 \u4e0a\u6765\u7684\u94c1\u7403\u6253\u88c2\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u5192\u6d63\u83b2\u6536\u52bf\u4e0d\u4f4f\uff0c\u6574\u4e2a\u4eba\u4ece\u6d1e\u4e2d\u6389\u4e86\u4e0b\u53bb\uff01 \u3000\u3000\u8fd9\u4e00\u6389\u4e0b\uff0c\u6070\u597d\u6389\u5728\u5341\u738b\u6bbf\u7684\u4e00\u4e2a\u5927\u4f5b\u50cf\u4e0a\uff0c\u5192\u6d47\u83b2\u7528\u529b\u4e00\u6273\u4f5b\u50cf\u7684\u5927\u624b\uff0c\u60f3\u628a\u8eab\u5f62\u5b9a \u4f4f\uff0c\u4e0d\u6599\u90a3\u4f5b\u50cf\u7adf\u662f\u6d3b\u52a8\u7684\uff0c\u5192\u6d63\u83b2\u7528\u529b\u4e00\u6273\uff0c\u90a3\u4f5b\u50cf\u8f67\u8f67\u7684\u8f6c\u4e86\u534a\u4e2a\u5706\u5708\uff0c\u4f5b\u50cf\u80cc\u540e\u73b0\u51fa \u4e86\u4e00\u6247\u6d3b\u95e8\uff0c\u5192\u6d63\u83b2\u4e3a\u907f\u8ffd\u5175\uff0c\u4e0d\u52a0\u601d\u7d22\u7684\u5c31\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u8fdb\u53bb\uff0c\u76f4\u628a\u5192\u6d63\u83b2\u5413\u4e86\u4e00\u8df3\u3002\u90a3\u662f\u4e00\u95f4\u6781\u4e3a\u7cbe\u81f4\u7684\u50e7\u820d\uff0c\u5f53\u4e2d\u5750\u7740\u4e00\u4e2a\u8001\u548c\u5c1a\uff0c\u767d \u987b\u98d8\u62c2\uff0c\u65c1\u8fb9\u5782\u624b\u7acb\u7740\u4e00\u4e2a\u5c11\u5e74\u3002\u6b63\u662f\u521a\u624d\u4f5b\u5802\u81ea\u5df1\u6293\u4e0d\u4f4f\u7684\u5eb7\u7199\u7687\u5e1d\u3002\u90a3\u8001\u548c\u5c1a\u4f4e\u7709\u5408 \u8ba1\uff0c\u9ed8\u4e0d\u4f5c\u58f0\u3002\u5eb7\u7199\u7687\u5e1d\u5219\u5634\u5507\u5fae\u5fbd\u5f00\u5408\uff0c\u4f3c\u4e4e\u5728\u6073\u6c42\u4ec0\u4e48\u4f3c\u7684\u3002 \u3000\u3000\u5192\u6d63\u83b2\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5fc3\u60f3\u83ab\u975e\u81ea\u5df1\u542c\u5230\u7684\u4f20\u8bf4\u7adf\u662f\u771f\u7684\u3002\u5c31\u5728\u8fd9\u4e00\u970e\u90a3\uff0c\u80cc\u540e\u638c\u98ce\u98d2\u7136\uff0c \u8ff7\u832b\u4e2d\uff0c\u5192\u6d63\u83b2\u6b32\u907f\u65e0\u4ece\uff0c\u7adf\u7ed9\u4eba\u4e00\u624b\u6263\u4f4f\u4e86\u81c2\u8180\uff0c\u90a3\u4eba\u7684\u4e94\u53ea\u624b\u6307\u5c31\u50cf\u94c1\u94a9\u4e00\u6837\uff0c\u5192\u6d63\u83b2 \u7ed9\u4ed6\u4e00\u628a\u6293\u7740\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u90a3\u4eba\u628a\u5192\u6d63\u83b2\u62d6\u5230\u4e86\u7687\u5e1d\u8ddf\u524d\uff0c\u5eb7\u7199\u8ba4\u5f97\u8fd9\u4eba\u6b63\u662f\u521a\u624d\u8ffd\u62ff\u81ea\u5df1\u7684\u4eba\uff0c\u5fc3\u4e2d\u5927\u6012\u3002\u4f46\u89c1 \u5979\u5934\u4e0a\u6ee1\u5934\u79c0\u53d1\uff0c\u5206\u660e\u662f\u4e2a\u5c11\u5973\uff0c\u8eab\u4e0a\u7a7f\u7684\u5374\u53c8\u662f\u592a\u76d1\u670d\u88c5\uff0c\u4e0d\u7981\u5927\u4e3a\u60ca\u8bb6\uff0c\u559d\u95ee\uff1a\u201c\u4f60\u5230 \u5e95\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u8001\u548c\u5c1a\u53cc\u7738\u5df2\u8c41\uff0c\u731b\u7136\u95f4\u597d\u50cf\u89e6\u7740\u4ec0\u4e48\u4f3c\u7684\uff0c\u9762\u8272\u5927\u53d8\uff0c\u9732\u51fa\u53c8\u60ca\u53c8\u559c\u7684\u795e\u60c5\uff0c\u53cc \u76ee\u70af\u70af\u653e\u5149\uff0c\u5ffd\u7136\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5973\u5c45\u58eb\u6211\u8ba4\u5f97\uff01\u201d\u63a5\u7740\u6f2b\u58f0\u541f\u9053\uff1a\u201c\u60a0\u60a0\u751f\u6b7b\u522b\u7ecf\u5e74\uff0c \u9b42\u9b44\u4e0d\u66fe\u6765\u5165\u68a6\uff01\u201d\u4ed6\u6ce8\u89c6\u5192\u6d63\u83b2\u8bb8\u4e45\u8bb8\u4e45\uff0c\u53c8\u5583\u5583\u81ea\u8bed\u5730\u4f3c\u95ee\u975e\u95ee\u9053\uff1a\u201c\u4f60\u5230\u662f\u4eba\u8fd8\u662f\u7cbe \u7075\uff1f\u54ce\uff0c\u4f60\u771f\u957f\u5f97\u597d\u50cf\u5979\u5440\uff01\u4f60\u4e0d\u662f\u5979\u7684\u9b42\u9b44\uff0c\u4e5f\u5b9a\u662f\u5979\u7684\u5316\u8eab\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u8fd9\u65f6\u5fc3\u4e2d\u4e86\u4e86\uff0c\u53c8\u662f\u60b2\u75db\uff0c\u53c8\u662f\u6124\u6068\uff0c\u51b2\u53e3\u95ee\u9053\uff1a\u201c\u4f60\u5c31\u662f\u987a\u6cbb\u7687\u5e1d\u8001\u513f\u4e86\u5427\uff0c \u6211\u7684\u6bcd\u4eb2\u5462\uff1f\u5979\u5230\u5e95\u662f\u751f\u662f\u6b7b\uff1f\u662f\u5728\u8fd9\u91cc\u8fd8\u662f\u5728\u5bab\u4e2d\uff1f\u4f60\u8981\u66ff\u6211\u544a\u8bc9\u5979\uff0c\u5979\u7684\u83b2\u513f\u6765\u627e\u5979 \u4e86\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u8fd9\u4e48\u4e00\u95f9\uff0c\u5eb7\u7199\u7687\u5e1d\u9707\u6012\u5df2\u6781\uff0c\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u767d\uff0c\u731b\u7136\u53d1\u4f5c\u9053\uff1a\u201c\u8fd9\u662f\u4e2a\u75af\u5973 \u4eba\uff0c\u960e\u4e2d\u5929\uff0c\u628a\u5979\u62c9\u4e0b\u53bb\uff01\u201d\u960e\u4e2d\u5929\u5c31\u662f\u521a\u624d\u64d2\u795d\u558a\u6d63\u83b2\u7684\u4f8d\u536b\uff0c\u4e5f\u662f\u5eb7\u7199\u7684\u5fc3\u8179\u536b\u58eb\u3002\u4ed6 \u5728\u8001\u548c\u5c1a\u53d1\u8a00\u65f6\uff0c\u5df2\u6084\u6084\u5730\u907f\u8fc7\u4e00\u8fb9\uff0c\u624b\u6263\u6697\u5668\uff0c\u8fdc\u8fdc\u7ad9\u5f00\uff0c\u65e8\u5728\u907f\u5acc\u3002\u8fd9\u65f6\u89c1\u5eb7\u7199\u53d1\u4f5c\uff0c \u745f\u745f\u7f29\u7f29\u5730\u8d70\u4e86\u51fa\u6765\uff0c\u4ed6\u65e0\u610f\u4e4b\u4e2d\u77e5\u9053\u4e86\u8fd9\u79cd\u5bab\u4e2d\u79d8\u5bc6\uff0c\u6b63\u4e0d\u77e5\u662f\u7978\u662f\u798f\u3002 \u3000\u3000\u8001\u548c\u5c1a\u53cc\u7738\u70af\u70af\uff0c\u671d\u7740\u5eb7\u7199\u53d1\u8bdd\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u5413\u552c\u5979\uff0c\u4f60\u5c0f\u65f6\u5019\u5979\u7684\u6bcd\u4eb2\u4e5f\u66fe\u62b1\u8fc7 \u4f60\u3002\u201d\u8bf4\u7f62\uff0c\u7f13\u7f13\u5730\u628a\u5192\u6d63\u83b2\u62c9\u4e86\u8d77\u6765\uff0c\u53f9\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u7684\u7236\u4eb2\u5931\u4e86\u5979\uff0c\u6211\u4e5f\u6ca1\u6709\u5f97\u7740 \u5979\uff1b\u5979\u672c\u6765\u5c31\u4e0d\u662f\u8fd9\u4e2a\u5c18\u4e16\u4e2d\u4eba\uff0c\u4f60\u53eb\u6211\u5230\u54ea\u91cc\u53bb\u66ff\u4f60\u4f20\u8bdd\uff1f\u201d\u5192\u6d63\u83b2\u77aa\u5927\u773c\u775b\u9053\uff1a\u201c\u90a3\u4e48 \u662f\u6211\u7684\u6bcd\u4eb2\u6b7b\u4e86\uff1f\u201d\u8001\u548c\u5c1a\u9053\uff1a\u201c\u68a6\u5e7b\u5c18\u7f18\uff0c\u7535\u5149\u77f3\u706b\uff0c\u5982\u6c34\u4e2d\u6708\uff0c\u5982\u955c\u4e2d\u5f71\uff0c\u5982\u96fe\u4e2d\u82b1\u3002 \u8463\u9102\u5983\u5076\u7136\u7559\u4e0b\u8272\u76f8\uff0c\u5230\u5982\u4eca\u8272\u7a7a\u5e7b\u706d\uff0c\u4eba\u6211\u4ff1\u5fd8\uff0c\u4f60\u53c8\u4f55\u5fc5\u8fd9\u6837\u6267\u7740\uff1f\u201d\u5192\u6d63\u83b2\u6025\u9053\uff1a \u201c\u6211\u4e0d\u6653\u8c08\u7985\uff0c\u4f60\u8d76\u5feb\u544a\u8bc9\u6211\u5979\u5230\u5e95\u600e\u6837\uff1f\u201d\u8001\u548c\u5c1a\u9053\uff1a\u201c\u4e5f\u7f62\uff0c\u4f60\u65e2\u7136\u8fd9\u6837\u601d\u5ff5\u6bcd\u4eb2\uff0c\u6211 \u5c31\u5e26\u4f60\u53bb\u89c1\u5979\u3002\u201d\u8bf4\u7f62\uff0c\u7f13\u7f13\u5730\u7ad9\u8d77\u6765\uff0c\u62cd\u7740\u5192\u6d63\u83b2\u7684\u624b\uff0c\u5f80\u5916\u5c31\u8d70\u3002\u5eb7\u7199\u548c\u960e\u4e2d\u5929\u9ed8\u9ed8\u65e0 \u8a00\u5730\u8ddf\u5728\u540e\u9762\uff0c\u9762\u8272\u5c34\u5c2c\u4e4b\u6781\u3002 \u3000\u3000\u8001\u548c\u5c1a\u62c9\u7740\u5192\u6d63\u83b2\u8d70\u51fa\u89d2\u95e8\uff0c\u7ecf\u8fc7\u5927\u6bbf\uff0c\u53ea\u542c\u5f97\u91cc\u9762\u91d1\u94c1\u4ea4\u9e23\uff0c\u53f1\u54a4\u8ffd\u9010\u3002\u5085\u9752\u4e3b\u5728\u4f5b \u50cf\u4e2d\u95f4\uff0c\u7ed5\u6765\u7ed5\u53bb\uff0c\u5251\u5149\u5982\u7ec3\uff0c\u72ec\u6218\u536b\u58eb\u3002\u8001\u548c\u5c1a\u95ee\u5192\u6d63\u83b2\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff0c\u4ed6\u662f\u548c\u4f60\u4e00\u540c \u6765\u7684\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4ed6\u53eb\u5085\u9752\u4e3b\uff0c\u662f\u548c\u6211\u4e00\u540c\u6765\u7684\u3002\u201d\u8001\u548c\u5c1a\u5bf9\u5eb7\u7199\u9053\uff1a\u201c\u7384\u70e8\uff08\u5eb7\u7199\u540d \u5b57\uff09\uff0c\u4f60\u53eb\u4ed6\u4eec\u90fd\u505c\u624b\u3002\u5085\u9752\u4e3b\u662f\u5192\uff08\u8f9f\u7586\uff09\u5148\u751f\u631a\u53cb\uff0c\u4e5f\u662f\u4e16\u5916\u9ad8\u4eba\u3002\u4e0d\u8981\u4e0e\u4ed6\u4e3a\u96be\u3002\u201d \u5eb7\u7199\u5fc3\u867d\u4e0d\u613f\uff0c\u4f46\u4e0d\u6562\u8fdd\u80cc\uff0c\u53ea\u597d\u4f20\u4ee4\u4e0b\u53bb\u3002\u5085\u9752\u4e3b\u957f\u5251\u5f52\u9798\uff0c\u62c2\u4e00\u62c2\u8eab\u4e0a\u7684\u7070\u5c18\uff0c\u4ece\u795e\u575b \u8df3\u4e0b\u6765\uff0c\u5411\u8001\u548c\u5c1a\u5fae\u4e00\u9894\u9996\uff0c\u65e2\u4e0d\u9053\u8c22\uff0c\u4e5f\u4e0d\u53d1\u8a00\u3002 \u3000\u3000\u8001\u548c\u5c1a\u5de6\u624b\u6298\u7740\u5192\u6d63\u83b2\uff0c\u53f3\u624b\u62c9\u7740\u5eb7\u7199\uff0c\u80cc\u540e\u8ddf\u7740\u5085\u9752\u4e3b\u548c\u960e\u4e2d\u5929\uff0c\u9ed8\u9ed8\u5730\u7f13\u6b65\u524d\u884c\u3002 \u4e00\u4f17\u4f8d\u536b\u8be7\u5f02\u975e\u5e38\uff0c\u5927\u5bb6\u90fd\u4e0d\u6562\u4f5c\u672f\uff0c\u4e5f\u4e0d\u6562\u8ddf\u4e0a\u524d\u53bb\uff0c\u53ea\u6709\u695a\u662d\u5357\u8fdc\u8fdc\u5730\u6301\u5251\u968f\u884c\u3002 \u3000\u3000\u8fd9\u884c\u4eba\u6240\u5230\u4e4b\u5904\uff0c\u536b\u58eb\u9ec4\u95e8\u90fd\u8eac\u8170\u4fef\u80cc\uff0c\u4e24\u9762\u95ea\u5f00\uff0c\u8001\u548c\u5c1a\u7406\u4e5f\u4e0d\u7406\uff0c\u4ecd\u662f\u9ed8\u9ed8\u524d\u884c\uff0c \u4e0d\u4e00\u4f1a\u5c31\u8d70\u5230\u4e86\u6e05\u51c9\u5bfa\u4e2d\u4e00\u4e2a\u53e4\u69d0\u8986\u836b\u7684\u56ed\u5b50\uff0c\u5176\u65f6\u6b8b\u661f\u660e\u706d\uff0c\u66d9\u8272\u5c06\u5f00\uff0c\u4e94\u53f0\u653e\u98ce\u547c\u547c\uff0c \u677e\u6d9b\u5c71\u7011\uff0c\u6c47\u6210\u97f3\u4e50\u3002\u8001\u548c\u5c1a\u6307\u7740\u56ed\u4e2d\u4e00\u4e2a\u4eba\u9752\u8349\u79bb\u7684\u8352\u51a2\u5bf9\u5192\u6d63\u83b2\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u9762\u57cb\u7684\u662f \u4f60\u7684\u6bcd\u4eb2\u7684\u8863\u51a0\uff0c\u81f3\u4e8e\u4f60\u7684\u6bcd\u4eb2\uff0c\u5979\u5df2\u7ecf\u4ed9\u53bb \u3000\u3000\u8fd9\u4e2a\u8001\u548c\u5c1a\u6b63\u662f\u987a\u6cbb\u7687\u5e1d\uff0c\u4ed6\u5f97\u8463\u5c0f\u5b9b\u540e\u5341\u5206\u5ba0\u7231\uff0c\u5c01\u4ed6\u4e3a\u9102\u5983\u3002\u53ea\u662f\u8463\u5c0f\u5b9b\u65e2\u6000\u5ff5\u5192 \u8f9f\u7586\uff0c\u66f4\u6000\u5ff5\u5730\u9057\u4e0b\u7684\u5973\u513f\u6d63\u83b2\uff0c\u5fc3\u4e2d\u90c1\u90c1\uff0c\u6574\u65e5\u65e0\u6b22\uff0c\u987a\u6cbb\u56e0\u6b64\u4e5f\u662f\u610f\u5174\u8427\u7d22\u3002\u592a\u540e\u95fb\u77e5 \u4e00\u4e2a\u6c49\u5973\u53d7\u5ba0\uff0c\u5df2\u662f\u4e0d\u60a6\uff0c\u66f4\u4f55\u51b5\u5982\u6b64\u3002\u5f53\u4e0b\u5927\u6012\uff0c\u547d\u4ee4\u5bab\u5973\u628a\u8463\u5c0f\u5b9b\u4e71\u68cd\u6253\u7684\uff0c\u6c89\u5c38\u5fa1 \u6cb3\u3002\u987a\u6cbb\u77e5\u9053\u540e\uff0c\u4e00\u75db\u65ad\u7edd\u3002\u7adf\u6084\u4fcf\u5730\u8d70\u51fa\u5bab\u95e8\uff0c\u5230\u4e94\u53f0\u5c71\u505a\u4e86\u548c\u5c1a\uff0c\u5728\u6e05\u51c9\u5bfa\u4e2d\u4e3a\u8463\u5c0f\u5b9b \u7acb\u4e86\u4e2a\u8863\u51a0\u51a2\u3002 \u3000\u3000\u8fd9\u65f6\u5192\u6d63\u83b2\u89c1\u4e86\u8352\u51a2\uff0c\u60b2\u75db\u6b32\u7edd\uff0c\u5979\u987e\u4e0d\u5f97\u98ce\u5bd2\u9732\u91cd\uff0c\u5728\u8349\u5730\u4e0a\u5c31\u62dc\u5c06\u4e0b\u53bb\u3002\u575f\u5934\u4e24\u76cf \u957f\u660e\u706f\u53d1\u7740\u60e8\u7eff\u5149\u534e\uff0c\u7167\u6837\u767d\u7389\u5893\u7891\u4e0a\u7684\u51e0\u4e2a\u7bc6\u5b57\uff1a\u201c\u6c5f\u5357\u624d\u5973\u8463\u5c0f\u5b9b\u4e4b\u5893\u201d\u3002\u5192\u6d63\u83b2\u89c1\u4e86 \u4e0a\u9762\u5e76\u6ca1\u6709\u5199\u7740\u201c\u8d35\u5983\u201d\u4e4b\u7c7b\u7684\u5934\u8854\uff0c\u5fc3\u4e2d\u7a0d\u597d\u8fc7\u4e00\u70b9\uff0c\u5979\u56de\u7738\u4e00\u770b\uff0c\u53ea\u89c1\u8001\u548c\u5c1a\u4e5f\u8dcc\u5012\u5728 \u4e71\u8349\u4e1b\u4e2d\uff0c\u9762\u8272\u60e8\u767d\uff0c\u5eb7\u7199\u7687\u5e1d\u9762\u5bb9\u6120\u6012\uff0c\u628a\u5934\u522b\u8fc7\u4e00\u8fb9\u3002\u5085\u9752\u4e3b\u5219\u62ac\u773c\u671b\u7740\u7167\u591c\u7684\u661f\u7a7a\uff0c \u597d\u50cf\u4ee5\u5f80\u601d\u7d22\u533b\u5b66\u96be\u9898\u4e00\u6837\uff0c\u5728\u601d\u7d22\u7740\u4eba\u751f\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u5728\u6e05\u4ee3\u7684\u7687\u5e1d\u4e2d\uff0c\u987a\u6cbb\u867d\u7136\u662f\u201c\u5f00\u56fd\u4e4b\u541b\u201d\uff0c\u4f46\u4e5f\u662f\u51b2\u9f84\uff08\u516d\u5c81\uff09\u5373\u4f4d\uff0c\u5927\u534a\u751f\u53d7\u7740\u53d4 \u7236\u591a\u5c14\u886e\u6bcd\u540e\u7684\u6276\u6301\uff0c\u540e\u6765\u8fd8\u5f04\u51fa\u592a\u540e\u4e0b\u5ac1\u5c0f\u53d4\u7684\u602a\u5267\u3002\u8fd9\u60c5\u5f62\u5c31\u6709\u70b9\u4f3c\u838e\u58eb\u6bd4\u4e9a\u5267\u4e2d\u7684\u54c8 \u59c6\u96f7\u7279\u4e00\u6837\uff0c\u987a\u6cbb\u7cbe\u795e\u4e0a\u4e5f\u662f\u53d7\u7740\u538b\u6291\u800c\u5fe7\u90c1\u7684\uff0c\u4ed6\u5728\u51fa\u5bb6\u4e4b\u540e\uff0c\u81ea\u4edf\u60c5\u7f18\u3002\u60f3\u81ea\u5df1\u541b\u4e34\u5929 \u4e0b\uff0c\u5374\u5f97\u4e0d\u5230\u4e00\u4e2a\u5973\u4eba\u7684\u5fc3\uff0c\u5bf9\u541b\u738b\u6743\u529b\u54d1\u7136\u5931\u77e5\uff0c\u4e5f\u6df1\u6094\u81ea\u5df1\u62c6\u6563\u4e86\u5192\u8f9f\u7586\u7684\u795e\u4ed9\u7737\u5c5e\u3002 \u8fd9\u65f6\u4ed6\u8dcc\u5750\u8352\u51a2\u4e4b\u65c1\uff0c\u5bcc\u8d35\u8363\u534e\uff0c\u6069\u6069\u6028\u6028\uff0c\u7535\u5149\u77f3\u706b\u822c\u7684\u5728\u5fc3\u5934\u63a0\u8fc7\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u62dc\u4e86\u51e0\u62dc\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u629a\u7740\u5251\u9500\uff0c\u770b\u7740\u987a\u6cbb\u3002\u5979\u89c1\u8fd9\u8001\u548c\u5c1a\u4f3c\u6bd4\u77f3\u4e00\u822c\u8dcc\u5750\u5730 \u4e0a\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u4e00\u9635\u98a4\u6144\uff0c\u624b\u4e0d\u89c9\u8f6f\u4e86\u4e0b\u6765\uff0c\u535a\u8005\u4e3b\u957f\u53ee\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6d63\u83b2\uff0c\u6211\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u53f9\u58f0\u672a\u5df2\uff0c\u811a\u6b65\u672a\u79fb\uff0c\u5ffd\u89c1\u4e00\u7fa4\u6b66\u58eb\u8ffd\u7740\u4e00\u4e2a\u62ab\u9762\u7eb1\u7684\u5c11\u5973\uff0c\u8d8a\u8ffd\u8d8a\u8fd1\u3002\u5192\u6d63\u83b2\u4e00\u770b\uff0c \u4e0d\u89c9\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5170\u73e0\u59d0\u59d0\uff01\u201d \u3000\u3000\u539f\u6765\u5728\u5192\u6d63\u83b2\u78b0\u89c1\u8001\u548c\u5c1a\u65f6\uff0c\u6613\u5170\u73e0\u4e5f\u6709\u5947\u9047\u3002\u8fd9\u8981\u4ece\u591a\u94ce\u592b\u59bb\u8bf4\u8d77\u3002 \u3000\u3000\u591a\u94ce\u53d7\u4e86\u5218\u90c1\u82b3\u6697\u5668\u6240\u4f24\uff0c\u867d\u975e\u81f4\u547d\uff0c\u4f46\u4e5f\u6d41\u8840\u8fc7\u591a\uff0c\u56de\u5230\u6e05\u51c9\u5bfa\u5c31\u8eba\u5728\u5e8a\u4e0a\u9759\u517b\u3002\u9102 \u738b\u5983\u7eb3\u5170\u660e\u6167\u89c1\u4e08\u592b\u8fd9\u4e2a\u6837\u4e86\uff0c\u5fc3\u4e2d\u4e2a\u65e0\u6bd4\u601c\u60dc\uff0c\u4eb2\u81ea\u670d\u4f8d\u4ed6\u6c64\u836f\uff0c\u529d\u4ed6\u5b89\u7720\u3002\u591a\u94ce\u7ed3\u5a5a\u540e \u5341\u516d\u5e74\u6765\uff0c\u59bb\u5b50\u5bf9\u4ed6\u90fd\u662f\u51b7\u51b7\u7684\uff0c\u8fd9\u65f6\u89c1\u5979\u4eb2\u81ea\u670d\u4f8d\uff0c\u5fc3\u4e2d\u975e\u5e38\u9163\u7545\uff0c\u4e0d\u4e00\u5168\u5c31\u7761\u7740\u4e86\u3002\u9102 \u738b\u5983\u5f85\u4ed6\u7761\u540e\uff0c\u72ec\u81ea\u501a\u680f\u51dd\u601d\uff0c\u6108\u60f3\u6108\u4e71\u3002\u8fd9\u65f6\u5f85\u5973\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u7eb3\u5170\u516c\u5b50\u7684\u6765\u770b\u4f60\uff01\u201d \u3000\u3000\u9102\u738b\u5983\u9053\uff1a\u201c\u8fd9\u4e48\u591c\u4e86\uff0c\u4ed6\u8fd8\u6ca1\u7761\uff1f\u201d\u8bf4\u7f62\u5429\u5490\u4f8d\u5973\u5f00\u95e8\u3002\u95e8\u5f00\u5904\uff0c\u4e00\u4e2a\u5c11\u5e74\u62ab\u7740\u6597 \u84ec\uff0c\u5174\u51b2\u51b2\u5730\u8d70\u8fdb\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u6bcd\uff0c\u6211\u53c8\u5f97\u4e86\u4e00\u9996\u65b0\u8bcd\u3002\u201d \u3000\u3000\u8fd9\u4f4d\u5c11\u5e74\u662f\u9102\u738b\u5983\u7eb3\u5170\u660e\u6167\u7684\u5802\u4f84\uff0c\u4e5f\u662f\u6709\u6e05\u4e00\u4ee3\u7684\u7b2c\u4e00\u4f4d\u8bcd\u4eba\uff0c\u53eb\u7eb3\u5170\u5bb9\u82e5\uff0c\u4ed6\u7684\u7236 \u4eb2\u7eb3\u5170\u660e\u73e0\uff0c\u6b63\u662f\u5f53\u671d\u7684\u5bb0\u76f8\uff08\u5b98\u53f7\u592a\u5085\uff09\u3002\u7eb3\u5170\u5bb9\u540d\u624d\u534e\u7edd\u4ee3\uff0c\u95fb\u540d\u4e8e\u5168\u56fd\uff0c\u5eb7\u7199\u7687\u5e1d\u975e \u5e38\u5ba0\u7231\u4ed6\uff0c\u4e0d\u8bba\u5230\u4ec0\u4e48\u5730\u65b9\u5de1\u6e38\u90fd\u8854\u4ed6\u968f\u884c\u3002\u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u7eb3\u5170\u5bb9\u82e5\u867d\u7136\u51fa\u8eab\u5728\u8d35\u65cf\u5bb6\u5ead\uff0c \u5374\u662f\u751f\u6027\u4e0d\u559c\u62d8\u675f\uff0c\u7231\u597d\u4ea4\u6e38\uff0c\u4ed6\u6700\u8ba8\u538c\u5bab\u5ef7\u4e2d\u7684\u523b\u677f\u751f\u6d3b\uff0c\u5374\u53c8\u4e0d\u80fd\u6446\u8131\uff0c\u56e0\u6b64\u90c1\u90c1\u4e0d \u6b22\uff0c\u5728\u8d35\u65cf\u7684\u8840\u7ba1\u4e2d\u6d41\u7740\u53db\u9006\u7684\u8840\u6db2\u3002\u540e\u854a\u7814\u7a76\u201c\u7ea2\u5b66\u201d\u7684\u4eba\uff0c\u6709\u7684\u8bf4\u201d\u7ea2\u697c\u68a6\u201d\u4e2d\u7684\u8d3e\u5b9d \u7389\u4fbf\u662f\u7eb3\u5170\u5bb9\u82e5\u7684\u5f71\u5b50\uff0c\u5176\u8a00\u867d\u672a\u514d\u9644\u4f1a\uff0c\u4f46\u4e5f\u4e0d\u65e0\u9053\u7406\u3002 \u3000\u3000\u5728\u5bab\u5ead\u548c\u5bb6\u65cf\u4e2d\uff0c\u7eb3\u5170\u5bb9\u82e5\u548c\u4ed6\u7684\u59d1\u59d1\u6700\u8c08\u5f97\u6765\u3002\u7eb3\u5170\u660e\u6167\u77e5\u9053\u4ed6\u7684\u813e\u6c14\uff0c\u542b\u7b11\u9053\uff1a \u201c\u542c\u8bf4\u4f60\u8fd9\u51e0\u5929\u5199\u4e86\u4e00\u9996\u65b0\u8bcd\uff0c\u5176\u4e2d\u4e24\u53e5\u662f\u2018\u522b\u6709\u6839\u82bd\u3001\u4e0d\u662f\u4eba\u95f4\u5bcc\u8d35\u82b1\u3002\u2019\u8001\u7237\u5b50\uff08\u7687 \u5e1d\uff09\u5f88\u4e0d\u6b22\u559c\uff0c\u4eca\u5929\u53c8\u5199\u4e86\u4ec0\u4e48\u65b0\u8bcd\u4e86\uff01\u201d \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u9053\uff1a\u201c\u6211\u5f39\u7ed9\u59d1\u59d1\u542c\u3002\u201d\u8bf4\u7f62\u4ece\u6597\u7bf7\u91cc\u62ff\u51fa\u4e00\u628a\u201d\u9a6c\u5934\u7434\u201d\uff0c\u8c03\u597d\u5f26\u7d22\uff0c\u94ee\u7eb5 \u5730\u5f39\u594f\u8d77\u6765\uff0c\u5531\u9053\uff1a\u201c\u8f9b\u82e6\u6700\u601c\u5929\u4e0a\u6708\uff0c \u3000\u3000\u4e00\u5915\u5982\u73af\uff0c\u5915\u5915\u957f\u5982\u5757\uff01 \u3000\u3000\u4f46\u4f3c\u6708\u8f6e\u7ec8\u768e\u6d01\uff0c \u3000\u3000\u4e0d\u8f9e\u51b0\u96ea\u4e3a\u537f\u70ed\uff01 \u3000\u3000\u65e0\u5948\u949f\u60c5\u5bb9\u6613\u7edd\uff0c \u3000\u3000\u71d5\u5b50\u4f9d\u7136\uff0c\u8f6f\u8e0f\u5e18\u94a9\u8bf4\u3002 \u3000\u3000\u5531\u7f62\u79cb\u575f\u6101\u672a\u6b47\u3002 \u3000\u3000\u6625\u4e1b\u8ba4\u53d6\u53cc\u6816\u8776\u3002\u201d \u3000\u3000\u7434\u58f0\u5982\u6ce3\u5982\u8bc9\uff0c\u7eb3\u5170\u660e\u6167\u542c\u5f97\u75f4\u4e86\uff0c\u6cea\u73e0\u6cbf\u7740\u9762\u988a\u6d41\u4e86\u4e0b\u6765\uff0c\u6cea\u5149\u4e2d\u6447\u6643\u770b\u6768\u4e91\u9aa2\u7684\u5f71 \u5b50\uff0c\u5979\u60f3\u8d77\u4e86\u5341\u516d\u5e74\u524d\u7684\u5927\u5a5a\u524d\u5915\uff0c\u90a3\u65f6\u5979\u4f55\u5c1d\u4e0d\u60f3\u50cf\u5929\u7a7a\u7684\u9e1f\u513f\u4e00\u6837\u98de\u7fd4\uff0c\u7136\u800c\u73b0\u5728\u8fd8\u4e0d \u662f\u88ab\u5173\u5728\u72ed\u7a84\u7684\u7b3c\u5b50\u51c4\u8ff7\u4e2d\uff0c\u7434\u58f0\u201c\u5212\u201d\u7136\u800c\u6b62\uff0c\u4f59\u97f3\u7f0e\u7ed5\u4e2d\uff0c\u7a81\u6709\u4e00\u4e2a\u5c11\u5973\u7684\u58f0\u97f3\u9053\uff1a \u201c\u597d\u8bcd\uff01\u201d \u3000\u3000\u7eb3\u5170\u59d1\u4f84\u84e6\u7136\u60ca\u8d77\uff0c\u53ea\u89c1\u4e00\u4e2a\u6234\u7740\u9762\u7eb1\u7684\u5c11\u5973\uff0c\u76c8\u76c8\u5730\u7acb\u5728\u5802\u4e2d\u3002\u7eb3\u5170\u660e\u6167\u6b66\u529f\u672c\u6765\u4e0d \u9519\uff0c\u53ea\u56e0\u4e3a\u8ff7\u4e8e\u7434\u58f0\uff0c\u7adf\u81ea\u4e0d\u89c9\u8fd9\u5c11\u5973\u662f\u4ec0\u4e48\u65f6\u5019\u6765\u7684\u3002 \u3000\u3000\u7eb3\u5170\u660e\u6167\u84e6\u7136\u60f3\u8d77\u4eca\u5929\u5728\u4e94\u53f0\u5c71\u884c\u523a\u7684\u5c11\u5973\uff0c\u77bf\u7136\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u90a3\u5c11\u5973\u54ac\u7740 \u7259\u6839\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4e00\u4e2a\u7f6a\u4eba\uff01\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u7adf\u4f3c\u5728\u4ec0\u4e48\u5730\u65b9\u542c\u8fc7\u7684\uff0c\u8fd9\u5c11\u5973\u7684\u4f53\u6001\u4e5f\u597d\u50cf\u662f\u81ea\u5df1\u975e\u5e38\u719f\u6089\u7684\u4eba\uff0c\u7eb3\u5170\u660e\u6167\u7a81 \u7136\u8d77\u4e86\u4e00\u79cd\u5947\u5999\u7684\u611f\u89c9\uff0c\u8bb0\u4e0d\u8d77\u662f\u7684j\u5728\u54ea\u4e00\u4e2a\u68a6\u4e2d\u66fe\u548c\u8fd9\u4f4d\u5c11\u5973\u76f8\u9022\u3002\u5979\u662f\u8fd9\u6837\u7684\u4eb2\u8fd1\u800c \u53c8\u662f\u8fd9\u6837\u7684\u964c\u751f\u2026\u2026\u3002 \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u77a7\u7740\u8fd9\u4f4d\u5c11\u5973\uff0c\u4f53\u6001\u4e3e\u6b62\uff0c\u7adf\u7136\u5f88\u50cf\u59d1\u59d1\uff0c\u4e5f\u4e0d\u89c9\u5947\u602a\u8d77\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u72af\u4e86 \u4ec0\u4e48\u7f6a\u5462\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u6211\u72af\u4e86\u4ec0\u4e48\u7f6a\uff1f\u6211\u7684\u6bcd\u4eb2\u81ea\u5c0f\u5c31\u629b\u5f03\u4e86\u6211\u3002\u6211\u60f3\uff0c\u8fd9\u4e00 \u5b9a\u662f\u524d\u4e16\u7684\u7f6a\u5b7d\uff01\u201d \u3000\u3000\u9102\u738b\u5983\u84e6\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u60f3\u6293\u5c11\u5973\u7684\u624b\uff0c\u5c11\u5973\u8ffd\u4e86\u51e0\u6b65\uff0c\u4e24\u53ea\u773c\u775b\u9732\u51fa\u51db\u7136\u7684\u795e\u60c5\uff0c\u51b7\u51b7 \u5730\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u78b0\u6211\uff0c\u4f60\u662f\u4e00\u4e2a\u9ad8\u8d35\u7684\u738b\u5983\uff0c\u4f60\u53c8\u6ca1\u6709\u629b\u5f03\u8fc7\u4f60\u4eb2\u751f\u7684\u513f\u5973\uff0c\u4f60\u8981\u548c\u6211\u63a5 \u8fd1\uff0c\u4e0d\u6015\u4f1a\u6c61\u4e86\u4f60\u5417\uff1f\u201d \u3000\u3000\u9102\u738b\u5983\u9893\u7136\u5730\u5012\u5728\u9760\u6905\u4e0a\uff0c\u53cc\u624b\u6342\u4f4f\u8138\u5e9e\uff0c\u4e09\u4e2a\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u7a7a\u6c14\u4f3c\u6b7b\u4e00\u6837\u7684\u6c89\u5bc2\uff0c\u826f \u4e45\uff0c\u826f\u4e45\uff0c\u9102\u738b\u5983\u7a81\u7136\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u544a\u8bc9\u6211\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\u5417\uff1f\u201d\u5c11\u5973\u7b54\u9053\uff1a\u201c\u6211\u53eb\u6613\u5170 \u73e0\u3002\u201d\u9102\u738b\u5983\u677e\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u4e0d\u59d3\u6768\uff1f\u201d\u5c11\u5973\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u59d3\u6768\uff1f\u738b\u5983\u5bf9\u59d3\u6768\u7684\u5f88 \u6709\u597d\u611f\u5417\uff1f\u201d \u3000\u3000\u9102\u738b\u5983\u6728\u7136\u4e0d\u7b54\uff0c\u53e3\u4e2d\u5583\u5583\u5730\u5ff5\u9053\uff1a\u201c\u6613\u5170\u73e0\uff0c\u6613\u5170\u73e0\u2026\u2026\u201d\uff0c\u84e6\u7136\u60f3\u8d77\u201c\u6613\u201d\u5b57\u662f \u201c\u6768\u201d\u5b57\u7684\u4e00\u534a\uff0c\u201c\u5170\u201d\u5b57\u662f\u81ea\u5df1\u590d\u59d3\u4e2d\u7684\u7b2c\u4e8c\u4e2a\u5b57\uff0c\u800c\u81ea\u5df1\u5931\u53bb\u7684\u5973\u513f\uff0c\u4e73\u540d\u6b63\u662f\u53eb\u505a \u201c\u5b9d\u73e0\u201d\u3002 \u3000\u3000\u9102\u738b\u5983\u6162\u6162\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u6781\u624b\u6500\u7740\u501a\u5b50\u7684\u9760\u80cc\uff0c\u53ea\u89c9\u8ff7\u8ff7\u832b\u832b\uff0c\u6d51\u8eab\u65e0\u529b\u3002\u8fd9\u65f6\u95e8\u5916\u53c8 \u6709\u4f8d\u5973\u6572\u95e8\uff0c\u8bf4\u9053\uff1a\u201c\u738b\u7237\u9192\u6765\u4e86\uff0c\u60f3\u8bf7\u738b\u5983\u8fdb\u53bb\u3002\u201d\u9102\u738b\u5983\u5982\u68a6\u521d\u9192\uff0c\u8bb0\u8d77\u4e86\u81ea\u5df1\u7684\u8eab \u4efd\uff0c\u9694\u95e8\u5429\u5490\u4f8d\u5973\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\uff0c\u4f60\u5148\u8fdb\u53bb\u670d\u4f8d\u738b\u7237\uff0c\u6211\u968f\u540e\u5c31\u6765\u3002\u201d\u8bf4\u7f62\u53c8\u5750\u4e86\u4e0b\u53bb\uff0c \u95f4\u6613\u5170\u73e0\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u56f0\u96be\u8981\u6211\u5e2e\u5fd9\u5417\uff1f\u201d \u3000\u3000\u6613\u5170\u73e0\u51b7\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6ca1\u6709\u4ec0\u4e48\u56f0\u96be\uff0c\u6240\u6709\u7684\u56f0\u96be\uff0c\u6211\u81ea\u5df1\u4e00\u4e2a\u4eba\u90fd\u786c\u633a\u8fc7\u53bb \u4e86\u3002\u201d\u9102\u738b\u5983\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5230\u6b64\u95ee\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6ca1\u6709\u5417\uff1f\u201d\u6613\u5170\u73e0\u60f3\u4e86\u4e00\u60f3\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u5982 \u679c\u6709\u7684\u8bdd\uff0c\u53c8\u600e\u4e48\u6837\uff1f\u201d\u9102\u738b\u5983\u7b54\u9053\uff1a\u201c\u53ea\u8981\u662f\u4f60\u7684\u4e8b\u60c5\uff0c\u6211\u90fd\u4f1a\u66ff\u4f60\u529e\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u5411\u524d\u8d70\u4e86\u4e24\u6b65\uff0c\u731b\u7136\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6211\u8bf7\u4f60\u628a\u4eca\u65e5\u5728\u6e05\u51c9\u5bfa\u524d\u6349\u5230\u7684\u5c11\u5e74\u653e\u51fa \u6765\uff0c\u4ea4\u7ed9\u6211\u5e26\u8d70\u3002\u201d\u9102\u738b\u5983\u8be7\u7136\u95ee\u9053\uff1a\u201c\u5c31\u662f\u4eca\u65e5\u884c\u523a\u6211\u7684\u90a3\u4f4d\u5c11\u5e74\u5417\uff1f\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u6b63 \u662f\uff0c\u738b\u5983\u4e0d\u613f\u610f\u653e\u4ed6\u5417\uff1f\u6211\u60f3\u544a\u8bc9\u4f60\uff0c\u4ed6\u4e5f\u662f\u6b7b\u4e86\u7236\u4eb2\u7684\u5b64\u513f\u3002\u4eca\u65e5\u4ed6\u4e0d\u77e5\u9053\u8f7f\u4e2d\u662f\u4f60\u3002\u201d \u9102\u738b\u5983\u60f3\u4e86\u534a\u664c\uff0c\u6bc5\u7136\u7b54\u9053\uff1a\u201c\u6211\u653e\u4ed6\u8d70\uff01\u201d\u8bf4\u7f62\uff0c\u7f13\u7f13\u8d77\u6765\uff0c\u8d70\u8fdb\u4e86\u540e\u5802\u3002 \u3000\u3000\u7eb3\u5170\u5bb9\u84e6\u7136\u7741\u5927\u773c\u775b\uff0c\u770b\u7740\u8fd9\u4f4d\u5947\u602a\u7684\u5c11\u5973\uff0c\u53ea\u89c9\u5f97\u5979\u7684\u76ee\u5149\uff0c\u5982\u5229\u5f29\uff1b\u5982\u5bd2\u51b0\uff0c\u4e0d\u89c9 \u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u907f\u5f00\u4e86\u5979\u7684\u773c\u5149\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5982\u679c\u6211\u4eec\u6709\u4ec0\u4e48\u7f6a\u5b7d\u7684\u8bdd\uff0c\u90a3\u4e5f\u662f\u4e0e\u751f\u800c\u4ff1 \u6765\u3002\u6bd4\u5982\u6211\uff0c\u6211\u5c31\u89c9\u5f97\u6211\u5728\u7687\u5bb6\u5c31\u662f\u4e00\u79cd\u7f6a\u5b7d\u3002\u201d \u3000\u3000\u6b63\u8bf4\u7740\u95f4\uff0c\u95e8\u5916\u4e00\u9635\u6b65\u5c65\u58f0\uff0c\u9102\u738b\u5983\u5df2\u628a\u4eca\u65e5\u884c\u523a\u5979\u7684\u5c11\u5e74\u51fa\u6765\u4e86\u3002 \u3000\u3000\u90a3\u88ab\u64d2\u7684\u5c11\u5e74\uff0c\u662f\u524d\u660e\u9c81\u738b\u624b\u4e0b\u5927\u5c06\u5f20\u714c\u8a00\u7684\u513f\u5b50\uff0c\u540d\u53eb\u5f20\u534e\u662d\u3002\u4ed6\u4e2d\u4e86\u9102\u738b\u5983\u9556\uff0c\u867d \u975e\u81f4\u547d\uff0c\u4e5f\u662f\u53d7\u4f24\u9887\u91cd\uff0c\u88ab\u64d2\u540e\uff0c\u591a\u94ce\u672c\u60f3\u5373\u884c\u5ba1\u95ee\uff0c\u65e0\u5948\u591a\u94ce\u7684\u4f24\u6bd4\u4ed6\u66f4\u91cd\uff0c\u56e0\u6b64\u53ea\u597d\u628a \u4ed6\u5173\u5728\u540e\u5802\uff0c\u9102\u738b\u5983\u4eb2\u81ea\u53bb\u63d0\uff0c\u81ea\u7136\u5f88\u5feb\u5c31\u63d0\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5f20\u534e\u662d\u88ab\u4ec7\u4eba\u63d0\u4e86\u51fa\u6765\uff0c\u5fc3\u4e2d\u6b63\u81ea\u60ca\u7591\u4e0d\u5b9a\uff0c\u5ffd\u89c1\u623f\u4e2d\u5750\u7740\u90a3\u4f4d\u62ab\u7740\u9762\u7eb1\u7684\u5c11\u5973\uff0c\u53ea\u662f \u5f53\u65e5\u6bd4\u81ea\u5df1\u8d76\u5148\u4e00\u6b65\uff0c\u60f3\u884c\u523a\u591a\u94ce\u7684\u4eba\u3002\u8fd9\u65f6\u89c1\u5979\u5b89\u7136\u5750\u5728\u5802\u4e0a\uff0c\u8fd8\u548c\u4e00\u534e\u670d\u5c11\u5e74\u5e76\u5750\u95f2 \u8c08\uff0c\u8be7\u5f02\u4e4b\u6781\uff0c\u4e0d\u89c9\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6613\u5170\u73e0\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5f20\u516c\u5b50\uff0c\u4f60\u968f\u6211\u8d70\u5427\uff01\u4f60\u8fd8\u80fd\u591f\u8d70\u52a8\u5417\uff1f\u201d\u5f20\u534e\u662d\u8fdf\u7591\u4e86\u4e00 \u4f1a\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u8fd8\u80fd\u591f\u8d70\u52a8\u3002\u201d\u7eb3\u5170\u5bb9\u540d\u65c1\u5750\uff0c\u89c1\u4ed6\u9762\u5982\u91d1\u7eb8\uff0c\u5374\u8fd8\u6602\u9996\u633a\u80f8\uff0c\u5206\u660e\u662f \u5fcd\u53d7\u7740\u75db\u82e6\u7684\u795e\u60c5\uff0c\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u6837\u8d70\u672a\u4e86\u5c31\u8d70\u5f97\u4e86\uff0c\u6211\u4e0d\u6562\u5192\u6627\uff0c\u6709\u4e2a\u4e0d\u60c5 \u4e4b\u8bf7\uff0c\u60f3\u59d4\u5c48\u8fd9\u4f4d\u5144\u53f0\u6743\u5f53\u6211\u7684\u4e66\u50ee\uff0c\u5f85\u5c06\u606f\u597d\u540e\uff0c\u518d\u8d70\u4e0d\u8fdf\u3002\u201d\u9102\u738b\u5983\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5230\u5e95 \u662f\u4f60\u60f3\u5f97\u5468\u5230\u3002\u201d\u5f20\u534e\u662d\u671b\u4e86\u9102\u738b\u5983\u4e00\u773c\u9053\uff1a\u201c\u6211\u9886\u516c\u5b50\u7684\u60c5\uff0c\u4f60\u4eec\u82e5\u4e0d\u6740\u6211\uff0c\u6211\u81ea\u5df1\u4f1a \u8d70\uff01\u201d\u8bf4\u65f6\u795e\u6001\uff0c\u8868\u73b0\u5f97\u5f88\u662f\u5014\u5f3a\u3002 \u3000\u3000\u9102\u738b\u5983\u60f3\u4e86\u4e00\u4e0b\uff0c\u5bf9\u6613\u5170\u73e0\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4eec\u8981\u8d70\uff0c\u6211\u4e5f\u4e0d\u52c9\u201d\u5f3a\u4f60\u4eec\u3002\u8fd9\u91cc\u6709\u4e00\u53ea\u4ee4 \u7bad\uff0c\u4f60\u62ff\u53bb\u5427\uff0c\u4e5f\u8bb8\u4f1a\u7ed9\u4f60\u51cf\u5c11\u4e00\u4e9b\u9ebb\u70e6\u3002\u201d\u8bf4\u7f62\u62ff\u51fa\u7fe1\u7fe0\u96d5\u6210\u7684\u77ed\u7bad\uff0c\u7bad\u4e0a\u523b\u6709\u201c\u9102\u4eb2\u738b \u591a\u94ce\u201d\u51e0\u4e2a\u5c0f\u5b57\u3002 \u3000\u3000\u6613\u5170\u73e0\u5e76\u4e0d\u63a8\u8f9e\uff0c\u63a5\u8fc7\u4ee4\u7bad\u3002\u5f20\u534e\u662d\u767d\u4e86\u5979\u4e00\u773c\uff0c\u4f3c\u6709\u4e0d\u6ee1\uff0c\u4f46\u8fd8\u662f\u968f\u7740\u5979\u8d70\u4e86\u3002\u9102\u738b \u5983\u626d\u7740\u53cc\u624b\uff0c\u547c\u5438\u8feb\u4fc3\uff0c\u6b63\u5982\u4e00\u4e2a\u4eba\u53d7\u5230\u8089\u4f53\u4e0a\u83ab\u5927\u7684\u75db\u82e6\u4e00\u6837\u3002\u800c\u5176\u5fc3\u7075\u7684\u75db\u82e6\uff0c\u66f4\u8d85\u8fc7 \u8089\u4f53\u7684\u75db\u82e6\u4e07\u500d\u3002\u6613\u5170\u73e0\u8eab\u5b50\u5fae\u5fae\u98a4\u52a8\uff0c\u9732\u5728\u9762\u7eb1\u5916\u7684\u773c\u775b\uff0c\u6709\u6cea\u6c34\u6ef4\u4e0b\u6765\uff0c\u9102\u738b\u5983\u8d70\u4e0a\u524d \u4e24\u6b65\uff0c\u4f38\u51fa\u624b\u6765\uff0c\u5f20\u534e\u662d\u4e0d\u8010\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u8d70\uff1f\u201d\u6613\u5170\u73e0\u5982\u5728\u6076\u68a6\u4e2d\u9192\u6765\uff0c\u770b\u89c1\u5f20\u534e\u662d\u5014\u5f3a \u7684\u795e\u6c14\uff0c\u84e6\u7136\u56de\u590d\u4e86\u81ea\u5236\u7684\u80fd\u529b\u3002\u867d\u7136\u9102\u738b\u5983\u770b\u89c1\u5979\u6240\u4f69\u7684\u7fe0\u73af\uff0c\u95ea\u95ea\u98a4\u52a8\uff0c\u77e5\u9053\u5979\u8fd8\u5728\u53d1 \u6296\uff0c\u4f46\u5979\u5df2\u7ecf\u8f6c\u8fc7\u8eab\u8eaf\uff0c\u62a2\u5728\u5f20\u534e\u662d\u7684\u524d\u9762\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u8d70\u51fa\u53bb\u4e86\u3002\u9102\u738b\u5983\u84e6\u5730\u8f6c\u8fc7\u8eab\u6765\uff0c \u5c31\u5728\u5802\u4e0a\u4f9b\u7740\u7684\u4e00\u5c0a\u4f5b\u50cf\u9762\u524d\uff0c\u8dea\u4e86\u4e0b\u53bb\u3002\u7eb3\u5170\u5bb9\u82e5\u51dd\u7acb\u5728\u5979\u7684\u8eab\u65c1\uff0c\u4f9d\u7a00\u542c\u5230\u5979\u7684\u786c\u54bd\u3002 \u3000\u3000\u6613\u5170\u73e0\u548c\u5f20\u534e\u662d\u8d70\u51fa\u4e86\u9662\u5b50\u5916\uff0c\u53ea\u89c1\u6708\u6697\u661f\u6708\uff0c\u591c\u9e26\u557c\u98de\uff0c\u8fdc\u5904\u94dc\u5854\u4e0a\u7684\u7409\u7483\u706f\uff0c\u9065\u5c04 \u4e0b\u6765\uff0c\u900f\u8fc7\u6276\u758f\u6811\u53f6\uff0c\u5149\u7ebf\u4e5f\u5f88\u5e7d\u6697\u3002\u6cbf\u8def\u65f6\u4e0d\u65f6\u6709\u5de1\u903b\u7684\u7981\u536b\u519b\u8d70\u8fc7\u6765\uff0c\u6613\u5170\u73e0\u5c06\u4ee4\u7bad\u4e00 \u626c\uff0c\u679c\u7136\u536b\u5175\u4eec\u6ca1\u6709\u76d8\u95ee\u3002\u8d70\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u95f4\uff0c\u5f20\u534e\u662d\u8eab\u5b50\u5411\u4fa7\u4e00\u503e\u3002 \u3000\u3000\u6613\u5170\u73e0\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u5fd9\u6276\u4f4f\u3002\u539f\u6765\u77f3\u8def\u82cd\u82d4\uff0c\u5f97\u4e0d\u7559\u8db3\u3002\u5f20\u534e\u662d\u53d7\u4f24\u4e4b\u540e\uff0c\u4e00\u4e0d\u5c0f\u5fc3\uff0c \u5c31\u8dcc\u4e86\u4e0b\u53bb\u3002\u867d\u7136\u6613\u5170\u73e0\u4e00\u628a\u6276\u4f4f\uff0c\u4ed6\u80f8\u53e3\u5df2\u78b0\u5230\u4e00\u682a\u6a2a\u51fa\u6765\u7684\u6811\u6860\uff0c\u4f24\u53e3\u53ea\u662f\u53d1\u75db\uff0c\u4ed6\u5fcd \u4e0d\u4f4f\u201c\u54df\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u6613\u5170\u73e0\u95ee\u9053\uff1a\u201c\u7d27\u8981\u5417\uff1f\u201d\u4ed6\u633a\u7740\u8bf4\u4e86\u4e00\u53e5\u201c\u4e0d\u7d27\u8981\u201d\uff0c\u63a8\u5f00 \u4e86\u6613\u5170\u73e0\u6276\u4ed6\u7684\u624b\uff0c\u5728\u5e7d\u6697\u7684\u706f\u5149\u4e0b\uff0c\u53c8\u6478\u7d22\u524d\u884c\u3002 \u3000\u3000\u9644\u8fd1\u7684\u513f\u4e2a\u4e00\u7981\u536b\u519b\uff0c\u95fb\u58f0\u6765\u5230\u3002\u6613\u5170\u73e0\u5c06\u4ee4\u7bad\u53d6\u51fa\uff0c\u6ee1\u4ee5\u53ef\u4ee5\u987a\u5229\u901a\u8fc7\uff0c\u4e0d\u6599\u5176\u4e2d\u4e00 \u4e2a\u6559\u5934\uff0c\u7cbe\u8b66\u975e\u5e38\u3002\u4ed6\u5728\u6de1\u9ec4\u8272\u7684\u706f\u3002\u5149\u4e0b\uff0c\u77a7\u89c1\u6613\u5170\u73e0\u9762\u8272\u6709\u5f02\uff0c\u518d\u4ed4\u7ec6\u4e00\u770b\uff0c\u53ea\u89c1\u5f20\u534e \u662d\u80f8\u524d\u7684\u8863\u961f\u8840\u67d3\u7ea2\u4e86\u4e00\u5927\u7247\u3002\u4ed6\u84e6\u7136\u559d\u9053\uff1a\u201c\u6293\u8d77\u6765\uff01\u201d\u4e00\u638c\u8bf4\u7740\u5411\u5f20\u534e\u662d\u5288\u6765\u3002\u5f20\u534e\u662d \u4eba\u867d\u53d7\u4f24\uff0c\u4e00\u5230\u5371\u6025\uff0c\u529b\u6c14\u5c31\u7528\u51fa\u6765\u4e86\uff0c\u4ed6\u5411\u540e\u4e00\u7eb5\uff0c\u6a2a\u8dc3\u51fa\u4e00\u4e08\u5de6\u53f3\u3002\u8fd9\u65f6\u6613\u5170\u73e0\u5df2\u662f\u62d4 \u5251\u51fa\u624b\uff0c\u548c\u7981\u536b\u519b\u6559\u5934\u6597\u5728\u4e00\u8d77\u3002\u53e6\u6709\u4e24\u4e09\u4e2a\u7981\u536b\u519b\uff0c\u8dd1\u4e0a\u6765\u6349\u62ff\u5f20\u534e\u662d\uff0c\u5f20\u534e\u662d\u632f\u8155\u6253\u51fa \u51e0\u652f\u74e6\u9762\u900f\u98ce\u9556\uff0c\u867d\u7136\u4f24\u540e\u6c14\u529b\u4e0d\u52a0\uff0c\u51c6\u5934\u8fd8\u5728\uff0c\u5f53\u5802\u6709\u4e24\u4e2a\u7981\u536b\u519b\u7ed9\u6253\u4e2a\u6b63\u7740\uff0c\u8ffd\u4e86\u4e0b \u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u9644\u8fd1\u53f7\u89d2\u545c\u545c\u7684\u5439\u4e86\u8d77\u6765\uff0c\u5047\u5c71\u6811\u6797\u4e4b\u95f4\uff0c\u4eba\u5f71\u7ef0\u7ef0\u3002\u5f20\u534e\u662d\u8ff7\u4e71\u4e2d\u53d1\u6b65\u5954\u8dd1\uff0c\u4e0d \u77e5\u4e0d\u89c9\u79bb\u5f00\u4e86\u6613\u5170\u73e0\uff0c\u8dd1\u8fc7\u51e0\u6761\u5e7d\u6697\u7684\u5c0f\u5f84\uff0c\u80cc\u540e\u9669\u559d\u58f0\u58f0\uff0c\u811a\u6b65\u8feb\u8fd1\u3002\u614c\u4e71\u4e2d\uff0c\u4e0d\u5047\u601d \u7d22\uff0c\u770b\u89c1\u524d\u9762\u7ea2\u5899\u7eff\u74e6\uff0c\u780c\u6210\u4e00\u5ea7\u5c0f\u5c0f\u7684\u7cbe\u820d\uff0c\u4ed6\u4e00\u63a8\u95e8\u5c31\u8d70\u4e86\u8fdb\u53bb\uff0c\u8fd9\u65f6\u6c14\u529b\u7528\u5c3d\uff0c\u5de8\u9ab8 \u6b32\u6563\uff0c\u7adf\u7136\u4e00\u8de4\u8dcc\u5728\u5730\u4e0a\uff0c\u6655\u4e86\u8fc7\u53bb\uff01 \u3000\u3000\u6613\u5170\u73e0\u89c1\u5f20\u534e\u662d\u614c\u5fd9\u4e71\u8dd1\uff0c\u5fc3\u91cc\u53d1\u6025\uff0c\u60f3\u8dd1\u4e0a\u53bb\u6551\u63f4\uff0c\u65e0\u5948\u53c8\u7ed9\u7981\u536b\u519b\u7f20\u7740\uff0c\u5979\u5a07\u53f1\u4e00 \u58f0\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u767b\u65f6\u5377\u8d77\u4e86\u51e0\u9053\u95ea\u7535\u4f3c\u7684\u5149\u5f69\u3002\u7981\u536b\u519b\u6559\u5934\u867d\u7136\u6b66\u529f\u4e0d\u5f31\uff0c\u4e5f\u7ed9\u5979\u7684\u5947\u95e8\u5251 \u6cd5\u903c\u5f97\u8000\u773c\u6b32\u82b1\uff0c\u8fde\u8fde\u540e\u9000\u3002\u6613\u5170\u73e0\u6025\u4f7f\u4e2a\u201c\u4e73\u71d5\u7a7f\u5e18\u201d\uff0c\u98de\u8eab\u4e00\u7eb5\u8df3\u51fa\u4e86\u5708\u5b50\u4e4b\u5916\uff0c\u6025\u6025 \u524d\u5954\u3002\u80cc\u540e\u8ffd\u7740\u56db\u9762\u516b\u65b9\u8d74\u8fc7\u6765\u7684\u7981\u536b\u519b\u3002\u5c31\u5728\u8fd9\u5371\u6025\u4e4b\u9645\uff0c\u5979\u78b0\u89c1\u5085\u9752\u4e3b\u548c\u5192\u6d63\u83b2\uff0c\u6b63\u548c \u987a\u6cbb\u5eb7\u7199\u4e24\u4e2a\u7687\u5e1d\uff0c\u7acb\u5728\u8463\u5c0f\u5b9b\u7684\u8863\u51a0\u5893\u65c1\u3002 \u3000\u3000\u8ffd\u6765\u7684\u7981\u536b\u519b\u5ffd\u7136\u53d1\u73b0\u5eb7\u7199\u7687\u5e1d\u7ad9\u5728\u90a3\u91cc\uff0c\u800c\u7687\u5e1d\u65c1\u8fb9\u7684\u5c11\u5973\uff0c\u53c8\u548c\u4ed6\u4eec\u6240\u8ffd\u7684\u5c11\u5973\u6253 \u8d77\u62db\u547c\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u5782\u4e0b\u624b\u6765\uff0c\u8fdc\u8fdc\u7ad9\u8d70\u3002 \u3000\u3000\u90a3\u8001\u548c\u5c1a\u6162\u6162\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u5bf9\u5eb7\u7199\u7687\u5e1d\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u96be\u4e3a\u4ed6\u4eec\uff0c\u90fd\u653e\u4e0b\u5c71\u53bb\u3002\u201d\u5eb7\u7199\u9ed8 \u7136\u4e0d\u7b54\uff0c\u8001\u548c\u5c1a\u62f1\u624b\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u4e0b\u53bb\u5427\u3002\u201d\u8bf4\u7f62\u4ece\u8863\u8896\u91cc\u6478\u51fa\u4e00\u4e32\u73cd\u73e0\uff0c\u5b9d\u5149\u5916\u6620\uff0c\u9012\u7ed9 \u5192\u6d63\u83b2\u9053\uff1a\u201c\u4f60\u62ff\u53bb\u7f62\uff0c\u8fd9\u662f\u4f60\u4ea1\u6bcd\u7684\u9057\u7269\u3002\u201d \u3000\u3000\u6613\u5170\u73e0\u8fd9\u4e00\u60ca\u8bb6\uff0c\u6bd4\u521a\u624d\u6240\u8c13\u66f4\u751a\u3002\u4eca\u591c\u7684\u4e8b\uff0c\u5c31\u771f\u5982\u68a6\u5883\u4e00\u822c\u3002\u5085\u9752\u4e3b\u548c\u5192\u6d63\u83b2\uff0c\u7adf \u7136\u4f1a\u548c\u7687\u5e1d\u7ad9\u5728\u4e00\u8d77\uff0c\u800c\u6700\u5389\u5bb3\u7684\u6e38\u9f99\u5251\u695a\u662d\u5357\u53c8\u548c\u4e00\u4e2a\u9ed1\u8863\u6b66\u58eb\uff08\u960e\u4e2d\u5929\uff09\u64e6\u5251\u7ad9\u5728\u80cc \u540e\u3002\u5979\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4e00\u4e2a\u540c\u4f34\u5462\u3002\u201d\u8001\u548c\u5c1a\u9053\uff1a\u201c\u4f60\u4eec\u4e00\u8d77\u8d70\u597d\u4e86\u3002\u201d\u5eb7\u7199\u5fcd \u4e0d\u4f4f\u6012\u76ee\u800c\u89c6\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u9053\u8981\u6211\u7ed9\u4f60\u4eec\u627e\u5bfb\u540c\u4f34\u4e0d\u6210\u8001\u548c\u5c1a\u9762\u8272\u5fae\u53d8\uff0c\u5bf9\u5eb7\u7199\u9053\uff1a\u201c\u2018\u4f60 \u8bf4\u4ec0\u4e48\uff1f\u201d\u5eb7\u7199\u7684\u5fc3\u8179\u536b\u58eb\u960e\u4e2d\u5929\u5927\u7740\u80c6\u5b50\u4e0a\u524d\u8bf4\u9053\uff1a\u201c\u5979\u7684\u540c\u4f34\u4e5f\u4e0d\u77e5\u662f\u7ed9\u8c01\u6349\u4e86\uff0c\u8fd9\u95f4 \u6e05\u51c9\u5bfa\u53c8\u5f88\u5927\uff0c\u4e00\u65f6\u95f4\u5f88\u96be\u67e5\u51fa\u3002\u7687\u4e0a\u628a\u8fd9\u4ef6\u4e8b\u4ea4\u7ed9\u5974\u624d\u529e\u5427\uff0c\u67e5\u51fa\u540e\u5974\u624d\u628a\u4ed6\u9001\u4e0b\u5c71 \u53bb\u3002\u201d\u5eb7\u7199\u5411\u960e\u4e2d\u5929\u4f7f\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u5927\u58f0\u5429\u5490\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5c31\u8fd9\u6837\u529e\uff0c\u4f60\u5e26\u4e00\u767e\u540d\u5bab\u5ef7\u4f8d\u536b \u53bb\u641c\u67e5\uff0c\u53ef\u8981\u641c\u5f97\u4ed4\u7ec6\u4e00\u70b9\u3002\u201d\u960e\u4e2d\u5929\u9886\u65e8\u5f85\u8d70\uff0c\u5eb7\u7199\u5ffd\u7136\u53c8\u5c06\u4ed6\u5524\u4f4f\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4f60\u628a\u6715 \u7684\u610f\u601d\u544a\u8bc9\u7981\u536b\u519b\u526f\u7edf\u9886\u5f20\u627f\u658c\u597d\u4e86\uff0c\u4f60\u8fd8\u5f97\u8d76\u6765\u56de\u89c1\u6211\u3002\u201d\u960e\u4e2d\u5929\u201c\u55b3\u201d\u7684\u4e00\u58f0\uff0c\u9886\u65e8\u9000 \u4e0b\uff0c\u5085\u9752\u4e3b\u9a8c\u8c8c\u8fa8\u8272\uff0c\u867d\u7136\u60c5\u77e5\u6709\u8bc8\uff0c\u4f46\u5374\u65e0\u53ef\u5948\u4f55\u3002\u770b\u60c5\u5f62\uff0c\u81ea\u5df1\u4e0d\u8d70\uff0c\u4e5f\u5c06\u751f\u53d8\u3002\u4ed6\u5411 \u8001\u548c\u5c1a\u518d\u5fae\u5fae\u9894\u9996\uff0c\u62db\u547c\u5192\u6d63\u83b2\u548c\u6613\u5170\u73e0\u9053\uff1a\u201c\u6211\u4eec\u8d70\u5427\uff01\u201d\u8001\u548c\u5c1a\u60e8\u7136\u4e00\u7b11\uff1a\u201c\u4f60\u4eec\u4e5f\u8be5 \u8d70\u4e86\u3002\u201d\u3002\u8bf4\u7f62\uff0c\u4e24\u53ea\u773c\u775b\u76ef\u4f4f\u5eb7\u7199\u9053\uff1a\u201c\u4f20\u65e8\u4e0b\u53bb\uff0c\u8ba9\u6765\u4eba\u8d70\uff01\u201d\u5eb7\u7199\u52c9\u52c9\u5f3a\u5f3a\u5730\u8ddf\u7740\u8bf4 \u9053\uff1a\u201c\u8ba9\u6765\u4eba\u8d70\u3002\u201d\u7981\u536b\u519b\u8f70\u7684\u4e00\u58f0\u5e94\u9053\uff1a\u201c\u8ba9\u6765\u4eba\u8d70\uff01\u201d\u58f0\u97f3\u4e00\u4e2a\u63a5\u7740\u4e00\u4e2a\u7684\u4f20\u9012\u4e0b\u53bb\uff0c \u5085\u9752\u4e3b\u7b49\u4e00\u884c\u4e09\u4eba\uff0c\u5c31\u5728\u558a\u58f0\u4e2d\u626c\u957f\u800c\u53bb\u3002\u5eb7\u7199\u7ef7\u7740\u8138\uff0c\u695a\u662d\u5357\u6309\u7740\u5251\uff0c\u671b\u7740\u4ed6\u4eec\u5927\u6447\u5927\u6446 \u5730\u8d70\u51fa\u4e86\u5bfa\u95e8\u3002 \u3000\u3000\u8fd9\u65f6\u523b\u5085\u9752\u4e3b\u7b49\u5e73\u5b89\u4e0b\u5c71\uff0c\u800c\u6e05\u51c9\u5bfa\u5185\u5374\u95f9\u5f97\u5929\u7ffb\u5730\u8986\u3002\u7981\u536b\u519b\u7684\u526f\u7edf\u9886\u5f20\u627f\u658c\uff0c\u5e26\u7740 \u4e00\u767e\u540d\u5bab\u5ef7\u4f8d\u536b\uff0c\u5230\u5904\u4e71\u641c\uff0c\u6349\u62ff\u9690\u5728\u5bfa\u5185\u7684\u5f20\u534e\u662d\u3002 \u3000\u3000\u518d\u8bf4\u5f20\u534e\u662d\u6655\u8fc7\u53bb\u540e\uff0c\u8ff7\u60af\u4e2d\u5ffd\u7136\u4e00\u9635\u51b7\u6c14\u76f4\u900f\u8111\u6d77\u3002\u4ed6\u7741\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u534e\u670d\u5c11 \u5e74\uff0c\u62ff\u7740\u4e00\u676f\u51b7\u6c34\u55b7\u4ed6\uff0c\u8fd9\u5c11\u5e74\u6b63\u662f\u7eb3\u5170\u5bb9\u82e5\uff0c\u518d\u770b\u4e00\u770b\uff0c\u81ea\u5df1\u7adf\u7136\u662f\u5728\u4e00\u95f4\u6781\u96c5\u81f4\u7684\u4e66\u623f \u4e4b\u5185\uff0c\u6c89\u9999\u64a9\u7ed5\uff0c\u56fe\u4e66\u6ee1\u58c1\u3002\u4ed6\u60f3\u6323\u8d77\u8eab\u6765\uff0c\u5374\u662f\u6d51\u8eab\u65e0\u529b\u3002\u7eb3\u5170\u5bb9\u82e5\u7b11\u9053\uff1a\u201c\u597d\u4e86\uff0c\u4f60\u9192 \u8fc7\u6765\u4e86\uff0c\u522b\u4e71\u52a8\uff0c\u4f60\u6d41\u8840\u8fc7\u591a\uff0c\u521a\u521a\u624d\u6b62\u5462\uff1f" \u3000\u3000\u5f20\u534e\u662d\u77a7\u4e86\u4e00\u77a7\u7eb3\u5170\u5bb9\u82e5\uff0c\u5fc3\u5185\u5341\u5206\u5947\u602a\uff0c\u53ea\u5f97\u5411\u4ed6\u9053\u8c22\u3002\u8fd9\u65f6\u95e8\u5916\u5ffd\u7136\u706b\u628a\u901a\u660e\uff0c\u706b \u5149\u76f4\u5c04\u8fdb\u6765\uff0c\u4eba\u58f0\u811a\u6b65\u58f0\uff0c\u5608\u6210\u4e00\u7247\u3002\u7eb3\u5170\u5bb9\u82e5\u628a\u4e00\u5f20\u9e2d\u7ed2\u88ab\uff0c\u5c06\u5f20\u534e\u662d\u8499\u5934\u76d6\u8fc7\uff0c\u500f\u5730\u6253 \u5f00\u623f\u95e8\uff0c\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5f20\u627f\u658c\u4e00\u770b\uff0c\u5728\u8fd9\u4e66\u623f\u4f4f\u7684\uff0c\u7adf\u662f\u76f8\u56fd\u4e4b\u5b50\u7eb3\u5170\u5bb9\u82e5\u3002\u4ed6\u6025\u5fd9\u5782\u4e0b\u624b\u9053\uff1a\u201c\u5974\u624d\u5949\u65e8\u641c \u62ff\u9003\u72af\uff0c\u4e0d\u60f3\u60ca\u52a8\u4e86\u516c\u5b50\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u51b7\u7b11\u4e00\u4e0b\uff0c\u628a\u624b\u644a\u5f00\uff0c\u8fde\u9053\uff1a\u201c\u8bf7\uff0c\u8bf7\uff0c\u6211\u8fd9\u91cc\u4e13\u95e8 \u7a9d\u85cf\u94a6\u72af\uff01\u4f60\u5feb\u8fdb\u6765\u641c\u67e5\u5440\uff01\u201d\u5f20\u534e\u662d\u85cf\u5728\u9e2d\u7ed2\u88ab\u4e4b\u5185\uff0c\u542c\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\u3002\u6b32\u77e5\u5f20\u534e\u662d\u80fd\u5426 \u8131\u9669\uff0c\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u4e09\u7ae0\u3000\u5251\u6c14\u73e0\u5149\u3000\u4e0d\u89c9\u5750\u884c\u7686\u68a6\u68a6\u3000\u7434\u58f0\u7b1b\u97f5\u3000\u65e0\u7aef\u557c\u54ed\u5c3d\u975e\u975e \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u4e09\u56de \u5251\u6c14\u73e0\u5149\u3000\u4e0d\u89c9\u5750\u884c\u7686\u68a6\u68a6 \u7434\u58f0\u7b1b\u97f5\u3000\u65e0\u7aef\u557c\u54ed\u5c3d\u975e\u975e \u3000\u3000\u5f20\u627f\u658c\u4efb\u5bab\u5185\u4f8d\u536b\u591a\u5e74\uff0c\u5982\u4f55\u4e0d\u77e5\u7eb3\u5170\u5bb9\u82e5\u4e43\u662f\u5f53\u4eca\u7687\u4e0a\u6700\u559c\u6b22\u7684\u4eba\uff0c\u542c\u7eb3\u5170\u5bb9\u82e5\u8fd9\u4e48 \u4e00\u8bf4\uff0c\u7eb5\u4f7f\u6709\u5929\u5927\u7684\u80c6\uff0c\u4e5f\u4e0d\u6562\u5192\u6627\u8d70\u8fdb\u3002\u7eb3\u5170\u5bb9\u82e5\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u4e0d\u8fdb\u6765 \u5440\uff1f\u73b0\u5728\u8eba\u5728\u627e\u5e8a\u4e0a\u7684\u5c31\u662f\u94a6\u72af\uff01\u201d\u6709\u4e00\u4e2a\u536b\u58eb\u6123\u5934\u6123\u8111\u5730\u63a2\u9996\u5165\u5185\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u5429\u5490\u6211 \u4eec\u641c\uff0c\u6211\u4eec\u5c31\u641c\u5427\uff0c\u6211\u770b\u5e8a\u4e0a\u8eba\u7684\u597d\u50cf\u771f\u6709\u4e00\u4e2a\u4eba\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u9762\u8272\u4e00\u53d8\uff0c\u5f20\u627f\u658c\u6025\u8d76\u4e0a\u4e00 \u6b65\uff0c\u626c\u624b\u5c31\u662f\u4e00\u5df4\u638c\uff0c\u6253\u5728\u90a3\u4e2a\u50bb\u5934\u50bb\u8111\u7684\u536b\u58eb\u8138\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u4f60\u6562\u5192\u72af\u7eb3\u5170\u516c\u5b50\uff1f\u4f60\u4eec\u901a \u901a\u7ed9\u6211\u6eda\u51fa\u53bb\uff01\u201d\u201c\u90a3\u536b\u58eb\u5600\u5600\u5495\u5495\u7684\u8bf4\u9053\uff1a\u201c\u6eda\u51fa\u53bb\u5c31\u6eda\u51fa\u53bb\u3002\u201d\u53cc\u624b\u6367\u7740\u8138\uff0c\u8e51\u624b\u8e51\u811a \u5730\u8d70\u51fa\u4e66\u623f\uff0c\u7eb3\u5170\u5bb9\u82e5\u7830\u7684\u4e00\u58f0\u628a\u623f\u95e8\u5173\u4e0a\uff0c\u5f20\u627f\u658c\u8fd8\u5728\u95e8\u5916\u8d54\u7f6a\u9053\u6b49\u3002\u7eb3\u5170\u5bb9\u82e5\u7406\u4e5f\u4e0d \u7406\uff0c\u63ed\u5f00\u9e2d\u7ed2\u88ab\u4e00\u770b\uff0c\u53ea\u89c1\u5f20\u534e\u662d\u6ee1\u5934\u5927\u6c57\uff0c\u795e\u6c14\u5374\u50cf\u6e05\u723d\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u5f20\u627f\u658c\u56db\u5904\u4e71\u641c\uff0c\u5747\u65e0\u6240\u83b7\uff0c\u53ea\u597d\u56de\u53bb\u590d\u547d\u3002\u4ed6\u5230\u4e86\u7687\u4e0a\u9a7b\u811a\u7684\u6bbf\u5916\uff0c\u60f3\u627e\u960e\u4e2d\u5929\u4ee3\u4e3a \u7980\u594f\uff0c\u201c\u884c\u5bab\u201d\u5916\u8fb9\uff0c\u4e00\u4e2a\u5b88\u536b\u90fd\u770b\u4e0d\u89c1\uff0c\u4e0d\u89c9\u5927\u4e3a\u8be7\u5f02\u3002 \u3000\u3000\u4e14\u8bf4\u5eb7\u7199\u7687\u5e1d\u548c\u8001\u548c\u5c1a\u56de\u6765\u4e4b\u540e\uff0c\u5fc3\u85cf\u9690\u6012\uff0c\u61ca\u607c\u5f02\u5e38\uff0c\u8001\u548c\u5c1a\u8fdb\u4e86\u7985\u623f\uff0c\u54b3\u58f0\u4e0d\u6b62\uff0c \u5eb7\u7199\u5c48\u819d\u8bf7\u5b89\uff0c\u8001\u548c\u5c1a\u9053\uff1a\u201c\u4e94\u53f0\u5c71\u4e0a\uff0c\u98ce\u5bd2\u9732\u51b7\uff0c\u4f60\u966a\u6211\u6298\u817e\u4e86\u4e00\u4e2a\u665a\u4e0a\uff0c\u4e5f\u8be5\u5b89\u6b47 \u4e86\u3002\u201d\u5eb7\u7199\u88c5\u51fa\u7b11\u5bb9\uff0c\u8bf4\u4e86\u53e5\u201c\u7236\u7687\u4e07\u5b89\u201d\uff0c\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u53ef\u662f\u5eb7\u7199\u7687\u5e1d\u5e76\u6ca1\u6709\u5b89\u6b47\uff0c\u4ed6\u5728\u9694\u5ba4\u8d77\u6765\u8d70\u53bb\uff0c\u7ed5\u5ba4\u5f77\u5fa8\u3002\u4e00\u65f6\u51b7\u7b11\uff0c\u4e00\u65f6\u6447\u5934\uff0c\u4e00\u65f6 \u53f9\u606f\uff0c\u731b\u7136\u95f4\u4e00\u62f3\u6253\u5728\u5899\u58c1\u4e0a\uff0c\u78b0\u5f97\u4ed6\u51e0\u4e4e\u53eb\u8d77\u75db\u6765\u3002\u8fd9\u65f6\uff0c\u95e8\u5916\u6709\u4eba\u8f7b\u8f7b\u6572\u95e8\uff0c\u5eb7\u7199\u95ee \u9053\uff1a\u201c\u662f\u960e\u4e2d\u5929\u5417\u3002\u201d\u95e8\u5916\u5e94\u4e86\u4e00\u58f0\uff0c\u5eb7\u7199\u500f\u5730\u6253\u5f00\u623f\u95e8\uff0c\u5c06\u960e\u4e2d\u5929\u62c9\u4e86\u8fdb\u53bb\u3002\u53c8\u4f38\u9996\u5411\u623f \u5916\u671b\u4e86\u4e00\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u536b\u58eb\u4eec\u5728\u95e8\u5916\u5b88\u536b\u5417\uff1f\u201d\u960e\u4e2d\u5929\u7b54\u9053\uff1a\u201c\u662f\u5974\u5a62\u6597\u80c6\uff0c\u77e5\u9053\u7687\u4e0a\u559c \u6b22\u5b89\u9759\uff0c\u6050\u9632\u4ed6\u4eec\u811a\u6b65\u58f0\u60ca\u52a8\u4e86\u5723\u9a7e\uff0c\u8fdb\u6765\u65f6\u5df2\u5429\u5490\u4ed6\u4eec\u90fd\u5728\u5927\u6bbf\u4e4b\u5916\u9632\u536b\u4e86\u3002\u201d\u5eb7\u7199\u70b9\u4e86 \u70b9\u5934\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u5f88\u806a\u660e\u3002\u201d \u3000\u3000\u5eb7\u7199\u5173\u7d27\u4e86\u623f\u95e8\uff0c\u7ef7\u7d27\u7740\u8138\u4f4e\u58f0\u5bf9\u960e\u4e2d\u5929\u9053\uff1a\u201c\u4f60\u5728\u4ead\u5185\u6709\u591a\u5c11\u5e74\u4e86\uff1f\u201d\u960e\u4e2d\u5929\u5c48\u6307\u7b97 \u9053\uff1a\u201c\u5341\u4e94\u5e74\u4e86\u3002\u201d\u5eb7\u7199\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e5f\u670d\u4f8d\u8fc7\u5148\u7687\u4e8c\u4e09\u5e74\u3002\u201d\u960e\u4e2d\u5929\u9053\uff1a\u201c\u5723\u4e0a\u660e\u5bdf\uff0c\u6b63 \u662f\u4e09\u5e74\u3002\u201d\u5eb7\u7199\u7a81\u7136\u677f\u8d77\u9762\u5b54\uff0c\u6740\u6c14\u9690\u73b0\u3002 \u3000\u3000\u960e\u4e2d\u5929\u4e00\u9897\u5fc3\u7a81\u7a81\u8df3\u52a8\uff0c\u5eb7\u7199\u7687\u5e1d\u9634\u4fa7\u607b\u5730\u95ee\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u8ba4\u8bc6\u8fd9\u4e2a\u6e05\u51c9\u5bfa\u7684\u76d1\u5bfa\u8001 \u548c\u5c1a\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u960e\u4e2d\u5929\u6251\u5730\u8dea\u5728\u5730\u4e0a\uff0c\u56de\u9053\uff1a\u201c\u5974\u5a62\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u5eb7\u7199\u7687\u5e1d\u5389\u58f0\u53f1\u9053\uff1a\u201c\u4f60\u8bf4\u8c0e\uff01\u201d\u960e\u4e2d\u5929\u7565\u7565\u7684\u4e00\u76f4\u53e9\u5934\uff0c\u5927\u7740\u773c\u5b50\u56de\u9053\uff1a\u201c\u7687\u4e0a\u6055\u81e3 \u65e0\u7f6a\uff0c\u8fd9\u8001\u548c\u5c1a\u6709\u70b9\u50cf\u5148\u7687\uff0c\u53ea\u662f\u4ed6\u987b\u7709\u5df1\u767d\uff0c\u5bb9\u989c\u5df2\u653f\uff0c\u4e0d\u662f\u4ed4\u7ec6\u5206\u8fa8\uff0c\u5df2\u7ecf\u770b\u51fa\u6765 \u4e86\u3002\u201d \u3000\u3000\u5eb7\u7199\u7687\u5e1d\u7b11\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8d77\u6765\uff0c\u8fd8\u662f\u4f60\u5bf9\u6715\u5fe0\u76f4\u3002\u2019\u960e\u4e2d\u5929\u745f\u745f\u7f29\u7f29\u5730\u7ad9\u4e86\u8d77\u6765\uff0c \u5eb7\u7199\u7687\u5e1d\u4e24\u9053\u773c\u5149\uff0c\u76f4\u76ef\u5728\u4ed6\u7684\u9762\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u8001\u548c\u5c1a\u5c31\u662f\u524d\u7687\uff0c\u7ecf\u4eca\u665a\u8fd9\u4e48\u4e00\u95f9\uff0c\u8fd8\u7528 \u8ba4\u8bc6\u4ed6\u7684\u8001\u81e3\u5b50\u624d\u770b\u5f97\u51fa\u5417\uff1f\u201d \u3000\u3000\u960e\u4e2d\u5927\u5782\u624b\u54c8\u8170\uff0c\u4e0d\u6562\u7f6e\u7b54\u3002\u5eb7\u7199\u53c8\u9053\uff1a\u201c\u4f60\u62ac\u8d77\u5934\u6765\u3002\u201d\u960e\u4e2d\u5929\u62ac\u8d77\u5934\uff0c\u5eb7\u7199\u731b\u7136\u95ee \u9053\uff1a\u201c\u4f60\u77e5\u9053\u5434\u6885\u6751\u5b66\u58eb\u662f\u600e\u6837\u6b7b\u7684\uff1f\u201d\u960e\u4e2d\u5929\u6d51\u8eab\u98a4\u6296\uff0c\u56de\u9053\uff1a\u201c\u5974\u5a62\u4e0d\u77e5\u3002\u201d\u5eb7\u7199\u51b7\u51b7 \u7684\u7b11\u9053\uff1a\u201c\u662f\u996e\u4e86\u6715\u6240\u8d50\u7684\u6bd2\u9152\u6bd2\u6b7b\u7684\uff0c\u4ed6\u5199\u4e86\u4e00\u9996\u8bd7\uff0c\u6697\u793a\u5148\u7687\u5728\u4e94\u53f0\u5c71\u4e0a\uff0c\u8fd8\u80e1\u626f\u4e00 \u987f\uff0c\u8bf4\u8463\u5c0f\u5b9b\u90a3\u8d31\u5a62\u4e5f\u5728\u5c71\u4e0a\u5462\u3002\u8fd9\u6837\u80c6\u5927\u7684\u5974\u624d\uff0c\u4f60\u8bf4\u8be5\u4e0d\u8be5\u6bd2\u6b7b\uff1f\u201d\u960e\u4e2d\u5929\u5413\u5f97\u4e00\u8eab\u51b7 \u6c57\uff0c\u8fde\u5fd9\u722c\u5728\u5730\u4e0a\uff0c\u53c8\u662f\u8fde\u8fde\u778c\u5934\uff0c\u8fde\u8fde\u8bf4\u9053\uff1a\u201c\u8be5\u6bd2\u6b7b\uff01\u8be5\u6bd2\u6b7b\uff01\u201d\u5eb7\u7199\u7687\u5e1d\u5e72\u7b11\u51e0\u58f0\uff0c \u5c06\u4ed6\u4e00\u628a\u62c9\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5f88\u597d\uff0c\u4f60\u5f88\u673a\u4f36\uff0c\u4f60\u53ef\u77e5\u9053\u8054\u4eca\u665a\u6df1\u591c\u53ec\u89c1\u4f60\u7684\u610f\u601d\u5417\uff1f\u201d \u3000\u3000\u960e\u4e2d\u5929\u901a\u4f53\u6d41\u6c57\uff0c\u5fc3\u60f3\uff0c\u7687\u4e0a\u4eca\u665a\u5c06\u79d8\u5bc6\u7279\u522b\u6cc4\u6f0f\u7ed9\u4ed6\u77e5\u9053\uff0c\u8fd9\u91cc\u9762\u53ef\u542b\u6709\u6df1\u610f\uff0c\u8fd9\u662f \u4e00\u4e2a\u5927\u597d\u65f6\u673a\uff0c\u5f04\u5f97\u597d\uff0c\u529f\u540d\u5229\u7984\u4ec0\u4e48\u90fd\u6709\uff1b\u5f04\u4e0d\u597d\uff0c\u4e5f\u8bb8\u5c31\u50cf\u5434\u6885\u6751\u4e00\u6837\uff0c\u4e0d\u660e\u4e0d\u81ea\u5730\u5c48 \u6b7b\u3002\u201d\u4ed6\u6a2a\u4e86\u5fc3\u5927\u7740\u773c\u56de\u9053\uff1a\u201c\u5974\u5a62\u53ea\u77e5\u9053\u6548\u5fe0\u7687\u4e0a\u4e00\u4eba\uff0c\u7687\u4e0a\u5429\u5490\u7684\uff0c\u5974\u5a62\u4e07\u6b7b\u4e0d\u8f9e\u3002\u201d \u5eb7\u7199\u6740\u6c14\u6ee1\u9762\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u8fd8\u7528\u5f97\u7740\u6715\u5429\u5490\u5417\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u9694\u90bb\u7684\u8001\u548c\u5c1a\u53c8\u662f\u4e00\u9635\u5927\u58f0\u54b3\u55fd\uff0c\u6572\u7740\u5899\u58c1\u95ee\u9053\uff1a\u201c\u7384\u70e8\uff08\u5eb7\u7199\u540d\u5b57\uff09\uff0c\u4f60\u5728\u548c\u8c01 \u8bf4\u8bdd\u5440\uff1f\u8fd9\u4e48\u665a\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u7761\uff1f\u201d\u5eb7\u7199\u67d4\u58f0\u7b54\u9053\uff1a\u201c\u7236\u7687\u4e0d\u8212\u670d\u5417\uff1f\u81e3\u513f\u5c31\u8fc7\u6765\u770b \u4f60\u3002\u201d\u8001\u548c\u5c1a\u5927\u58f0\u9053\uff1a\u201c\u4f60\u5f88\u5b5d\u987a\uff0c\u4f60\u4e0d\u5fc5\u60e6\u8bb0\u6211\uff0c\u4f60\u7761\u5427\uff01\u201d\u5eb7\u7199\u4e0d\u7b54\uff0c\u4e00\u628a\u62c9\u7740\u960e\u4e2d \u5929\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u548c\u4f60\u53bb\u770b\u770b\u4ed6\uff0c\u4f60\u5f97\u597d\u597d\u670d\u4f8d\u4ed6\u3002\u201d \u3000\u3000\u8001\u548c\u5c1a\u89c1\u5eb7\u7199\u540c\u960e\u4e2d\u5929\u8fdb\u6765\uff0c\u9887\u611f\u8bb6\u5f02\u3002\u5eb7\u7199\u867d\u7136\u51e0\u6b21\u6765\u8fc7\u4e94\u53f0\u5c71\u8c12\u89c1\uff0c\u6709\u65f6\u4e5f\u4f1a\u5e26\u5fc3 \u8179\u536b\u58eb\u5728\u65c1\uff0c\u53ef\u662f\u4ece\u6765\u672a\u5728\u4eba\u524d\u8ba4\u8fc7\u81ea\u5df1\u662f\u7236\u7687\uff0c\u4eca\u665a\u4ed6\u7684\u884c\u4e3a\uff0c\u53ef\u6709\u70b9\u5947\u602a\u3002 \u3000\u3000\u960e\u4e2d\u5929\u9762\u8272\u7070\u767d\uff0c\u4e24\u624b\u5fae\u5fae\u98a4\u6296\uff0c\u8001\u548c\u5c1a\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u5eb7\u7199\u9053\uff1a\u201c\u7236\u7687\uff0c\u4ed6\u662f\u4f60\u7684\u8001\u536b \u58eb\uff0c\u81e3\u513f\u7279\u522b\u5e26\u4ed6\u6765\u670d\u4f8d\u4f60\u3002\u201d\u8001\u548c\u5c1a\u4e00\u9635\u54b3\u55fd\uff0c\u4fa7\u8f6c\u8eab\u8eaf\u95ee\u9053\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u960e\u4e2d \u5929\u9053\uff1a\u201c\u5974\u5a62\u53eb\u960e\u4e2d\u5929\uff0c\u670d\u4f8d\u8fc7\u965b\u4e0b\u4e09\u5e74\u3002\u201d\u8001\u548c\u5c1a\u4f9d\u7a00\u8bb0\u5f97\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\uff01\u4f60 \u6276\u6211\u8d77\u6765\u5750\u5750\u5427\uff01\u201d \u3000\u3000\u960e\u4e2d\u5929\u6162\u6162\u8d70\u8fc7\u53bb\uff0c\u4e24\u624b\u5728\u8001\u548c\u5c1a\u80c1\u4e0b\u4e00\u67b6\uff0c\u8001\u548c\u5c1a\u62ac\u8d77\u5934\u6765\uff0c\u5ffd\u89c1\u4ed6\u6ee1\u773c\u7ea2\u4e1d\uff0c\u6ee1\u9762 \u6740\u6c14\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u987a\u6cbb\u5230\u5e95\u662f\u505a\u8fc7\u7687\u5e1d\uff0c\u867d\u7136\u505a\u4e86\u548c\u5c1a\uff0c\u4f59\u5a01\u72b9\u5728\uff0c \u960e\u4e2d\u5929\u7ed9\u4ed6\u4e00\u559d\uff0c\u4e24\u624b\u731b\u7136\u4e00\u677e\uff0c\u5168\u8eab\u4f3c\u60a3\u4e86\u53d1\u51b7\u75c5\u4e00\u822c\uff0c\u6296\u4e2a\u4e0d\u6b62\uff0c\u8001\u548c\u5c1a\u5931\u4e86\u501a\u9760\uff0c\u4e00 \u8de4\u8dcc\u843d\u5e8a\u4e0b\u3002\u5eb7\u7199\u6025\u98a4\u58f0\u5389\u53f1\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u8fd8\u4e0d\u597d\u597d\u3001\u670d\u4f8d\u7236\u7687\uff1f\u201d\u960e\u4e2d\u5929\u5b9a\u4e86\u5b9a\u795e\uff0c \u4e00\u5f2f\u8170\u5c06\u8001\u548c\u5c1a\u631f\u8d77\uff0c\u95ed\u4f4f\u773c\u775b\uff0c\u7528\u529b\u4e00\u631f\uff0c\u53ea\u542c\u5f97\u8001\u548c\u5c1a\u60e8\u53eb\u4e00\u58f0\uff1a\u201c\u7384\u70e8\uff0c\u4f60\u597d\uff01\u201d\u6e05 \u4ee3\u7684\u5f00\u56fd\u541b\u4e3b\uff0c\u7adf\u7136\u4e0d\u6b7b\u5728\u4ec7\u4eba\u5251\u4e0b\u800c\u6b7b\u5728\u513f\u5b50\u624b\u4e0a\u3002 \u3000\u3000\u960e\u4e2d\u5929\u7ad9\u8d77\u8eab\u6765\uff0c\u53ea\u89c9\u808c\u8089\u4e00\u9635\u9635\u75c9\u631b\uff0c\u4ed6\u770b\u5eb7\u7199\u826f\u5e1d\uff0c\u53ea\u89c1\u5eb7\u7199\u4e5f\u4f3c\u5927\u75c5\u521d\u6108\u4e00\u6837\uff0c \u9762\u76ee\u6b7b\u7070\u3002\u826f\u4e45\u826f\u4e45\uff0c\u5eb7\u7199\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u505a\u5f97\u5f88\u597d\uff0c\u4f60\u968f\u6715\u6765\u5427\u3002\u201d \u3000\u3000\u960e\u4e2d\u5929\u968f\u5eb7\u7199\u56de\u5230\u90bb\u5ba4\uff0c\u5eb7\u7199\u968f\u624b\u62ff\u8d77\u4e00\u4e2a\u53e3\u5929\u96d5\u808c\u7684\u9152\u547d\uff0c\u5012\u4e86\u4e00\u676f\u6de1\u7eff\u7684\u9152\uff0c\u9012\u8fc7 \u53bb\u9053\uff1a\u201c\u4f60\u5149\u559d\u676f\u9152\u538b\u538b\u60ca\u3002\u201d\u960e\u4e2d\u5929\u731b\u7684\u8bb0\u8d77\u4e86\u5434\u6885\u6751\uff0c\u51b7\u6c57\u76f4\u6d41\uff0c\u8fdf\u8fdf\u7591\u7591\uff0c\u4e0d\u6562\u9aa4 \u63a5\u3002\u5eb7\u7199\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u5927\u4e8b\u5df2\u4e86\uff0c\u54b1\u4eec\u541b\u81e3\u90fd\u8be5\u5e72\u4e00\u676f\u3002\u201d\u8bf4\u7f62\u5c06\u676f\u4e2d\u7684\u9152\u4e00\u996e\u800c\u5c3d\uff0c\u5c06 \u676f\u7ffb\u8f6c\u6765\u4e00\u7167\uff0c\u968f\u5373\u53c8\u5012\u4e86\u4e00\u676f\uff0c\u7b11\u9053\uff1a\u201c\u81ea\u6b64\u4f60\u4e43\u662f\u6715\u6700\u5fc3\u8179\u4e4b\u4eba\uff0c\u660e\u5929\u8d77\u4f60\u5c31\u505a\u7981\u536b\u519b \u7684\u9996\u9886\u5427\uff0c\u5916\u52a0\u592a\u5b50\u5c11\u4fdd\u8854\uff0c\u4f60\u597d\u597d\u5e72\u5427\uff01\u201d\u960e\u4e2d\u5929\u8fd9\u4e00\u559c\u975e\u540c\u5c0f\u53ef\uff0c\u9a6c\u4e0a\u7cbe\u795e\u5927\u632f\uff0c\u722c\u5728 \u5730\u4e0a\u53e9\u4e86\u51e0\u4e2a\u5934\uff0c\u8d77\u8eab\u63a5\u8fc7\u9152\u676f\uff0c\u4e5f\u662f\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u6697\u5ba4\u4e4b\u4e2d\uff0c\u541b\u81e3\u4fe9\u76f8\u89c6\u800c\u7b11\u3002\u6b63\u5728\u6b64\u65f6\uff0c\u5ffd\u7136\u7a97\u5916\u4e5f\u6709\u4e00\u58f0\u51b7\u7b11\u4f20\u4e86\u8fdb\u6765\uff0c\u5eb7\u7199\u9762\u8272\u5927 \u53d8\uff0c\u960e\u4e2d\u5929\u4e00\u8dc3\u800c\u51fa\uff0c\u53ea\u89c1\u74e6\u80cc\u4e0a\u4e00\u6761\u7070\u8272\u4eba\u5f71\uff0c\u5728\u7409\u7483\u74e6\u4e0a\u75be\u63a0\u8f7b\u9a70\uff0c\u6377\u5982\u98de\u9e1f\u3002\u960e\u4e2d\u5929 \u5728\u5927\u5185\u536b\u58eb\u4e4b\u4e2d\uff0c\u529f\u592b\u6700\u597d\uff0c\u529f\u529b\u4e0d\u884c\u695a\u662d\u5357\u4e4b\u4e0b\uff0c\u4e00\u6396\u8863\u895f\uff0c\u4e5f\u50cf\u71d5\u5b50\u63a0\u6ce2\u4e00\u6837\uff0c\u63a0\u4e0a\u7409 \u7483\u74e6\u9762\u3002\u90a3\u4eba\u811a\u6b65\u7a81\u7136\u653e\u6162\uff0c\u4f3c\u6709\u610f\u7b11\u4ed6\uff0c\u960e\u4e2d\u5929\u6293\u81c2\u76f4\u4e0a\uff0c\u4f38\u624b\u4e00\u6293\uff0c\u52bf\u5982\u98de\u9e70\uff0c\u90a3\u4eba\u624b \u62fe\u4f4f\u4fbf\u626d\uff0c\u960e\u4e2d\u5929\u53ea\u89c9\u4f3c\u7ed9\u94c1\u94b3\u94b3\u4f4f\u4e00\u6837\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u81ea\u5df1\u51e0\u5341\u5e74\u7684\u9e70\u722a\u529f\u592b\uff0c\u7adf\u7136\u65bd\u5c55\u4e0d \u5f97\u3002\u90a3\u4eba\u731b\u7136\u559d\u9053\uff1a\u201c\u960e\u4e2d\u5929\uff0c\u4f60\u6b7b\u5230\u4e34\u5934\u8fd8\u4e0d\u4e0d\u77e5\u9053\uff0c\u8fd8\u548c\u6211\u6253\u4ec0\u4e48\uff1f\u4f60\u559d\u4e86\u6bd2\u9152\u4e86\uff01\u8d76 \u5feb\u505c\u624b\uff0c\u5f85\u6211\u770b\u770b\uff0c\u8fd8\u80fd\u4e0d\u80fd\u89e3\u6551\uff1f\u201d\u960e\u4e2d\u5929\u5fc3\u4e2d\u4e00\u60ca\uff0c\u53ea\u89c9\u773c\u524d\u91d1\u661f\u4e71\u5192\uff0c\u5730\u8f6c\u5929\u65cb\uff0c\u811a \u6b65\u865a\u6d6e\uff0c\u8dcc\u5012\u7409\u7483\u74e6\u9762\uff0c\u76f4\u6eda\u4e0b\u53bb\u3002 \u3000\u3000\u7070\u8863\u4eba\u8eab\u5f62\u5982\u7bad\u5c04\u51fa\uff0c\u4e00\u628a\u6293\u4f4f\u960e\u4e2d\u5929\u7684\u8863\u5e26\uff0c\u5c06\u4ed6\u635e\u4e86\u56de\u6765\uff0c\u6309\u5728\u5730\u9762\uff0c\u968f\u624b\u5728\u6000\u91cc \u63a2\u51fa\u4e00\u652f\u94f6\u9488\uff0c\u5411\u4ed6\u7684\u80cc\u810a\u5929\u67a2\u7a74\u4e00\u624e\u3002\u960e\u4e2d\u5929\u201c\u54ce\u54df\u201d\u4e00\u58f0\u558a\u4e86\u51fa\u6765\uff0c\u7070\u8863\u4eba\u5c06\u4ed6\u7ffb\u8f6c\u8eab \u6765\uff0c\u53c8\u662f\u7528\u529b\u4e00\u634f\uff0c\u960e\u4e2d\u5929\u5634\u5df4\u5f20\u5f00\uff0c\u7070\u8863\u4eba\u672a\u5f85\u4ed6\u51fa\u58f0\uff0c\u5df2\u5c06\u4e09\u7c92\u78a7\u7984\u8272\u7684\u4e39\u4e38\u585e\u4e86\u8fdb \u53bb\uff0c\u5c06\u4ed6\u6447\u4e86\u51e0\u6447\uff0c\u95ee\u9053\uff1a\u201c\u600e\u6837\uff1f\u201d\u960e\u4e2d\u5929\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8c22\u8c22\uff01\u201d\u4ed6\u5168\u8eab\u867d\u89c9\u9ebb \u75d2\uff0c\u795e\u6c14\u5374\u662f\u6e05\u723d\u4e86\u4e9b\u3002\u7070\u8863\u4eba\u7ed9\u4ed6\u7684\u4e39\u4e38\u4e43\u662f\u5929\u5c71\u4e0a\u4e98\u53e4\u4e0d\u5316\u7684\u5bd2\u51b0\u6240\u957f\u51fa\u7684\u96ea\u83b2\uff0c\u914d\u4e0a \u5176\u4ed6\u836f\u7269\u6240\u70bc\u6210\u7684\uff0c\u80fd\u89e3\u767e\u6bd2\u3002\u960e\u4e2d\u5929\u53c8\u4ed7\u7740\u529f\u529b\u6df1\u539a\uff0c\u56e0\u6b64\u867d\u5403\u4e86\u6700\u5389\u5bb3\u7684\u6bd2\u9152\uff0c\u6682\u65f6\u8fd8 \u80fd\u652f\u6301\u3002 \u3000\u3000\u8fd9\u65f6\u9644\u8fd1\u7684\u536b\u58eb\u65e9\u7ed9\u58f0\u54cd\u60ca\u52a8\uff0c\u8d76\u4e86\u8fc7\u6765\u3002\u7070\u8863\u4eba\u5411\u960e\u4e2d\u5929\u9053\uff1a\u201c\u4f60\u8d76\u5feb\u968f\u6211\u4e0b\u5c71\uff0c\u6211 \u518d\u7ed9\u4f60\u533b\u6cbb\uff0c\u4e0d\u7136\u6027\u547d\u4e0d\u4fdd\uff01\u201d\u960e\u4e2d\u5929\u5fd9\u4e0d\u8fed\u5730\u7b54\u5e94\uff0c\u968f\u7740\u7070\u8863\u4eba\u53cc\u53cc\u8dc3\u843d\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec \u95f9\u4ec0\u4e48\uff1f\u8d3c\u4eba\u65e9\u5df2\u8d70\u4e86\u3002\u6211\u73b0\u5728\u5c31\u8981\u4e0b\u5c71\u641c\u67e5\u3002\u201d\u536b\u58eb\u4eec\u90fd\u77e5\u9053\u960e\u4e2d\u5929\u662f\u6700\u5f97\u7687\u4e0a\u5ba0\u4fe1\u7684\u536b \u58eb\uff0c\u5728\u5bab\u4e2d\u7684\u6743\u529b\u6bd4\u7981\u536b\u519b\u526f\u7edf\u9886\u5f20\u627f\u658c\u8fd8\u5927\u3002\u4ed6\u4eec\u89c1\u7740\u4ed6\u548c\u7070\u8863\u4eba\u5728\u4e00\u8d77\uff0c\u867d\u611f\u8be7\u5f02\uff0c\u4f46 \u4e5f\u77e5\u9053\u662f\u4ed6\u8bf7\u6765\u7684\u5947\u624d\u5f02\u80fd\u4e4b\u58eb\uff0c\u8c01\u90fd\u4e0d\u6562\u8bd8\u95ee\uff0c\u8ba9\u4ed6\u4eec\u81ea\u884c\u4e0b\u5c71\uff0c\u960e\u4e2d\u5929\u4e34\u8d70\u524d\u8fd8\u5429\u5490\u4ed6 \u4eec\u4e0d\u8981\u60ca\u52a8\u7687\u4e0a\u3002 \u3000\u3000\u518d\u8bf4\u6b66\u5bb6\u5e84\u4e2d\u4e00\u4f17\u82f1\u96c4\uff0c\u81ea\u5085\u9752\u4e3b\u548c\u5192\u6d63\u83b2\u53bb\u63a2\u5c71\u540e\uff0c\u5fc3\u4e2d\u60ac\u60ac\uff0c\u5927\u5bb6\u90fd\u4e0d\u80af\u53bb\u7761\u3002\u534a \u591c\u65f6\u5206\uff0c\u542c\u8bf4\u6613\u5170\u73e0\u4e5f\u5931\u4e86\u8e2a\uff0c\u66f4\u662f\u6302\u5fc3\u3002\u5927\u5bb6\u7d22\u6027\u5750\u7740\u7b49\u5f85\uff0c\u53ef\u662f\u7b49\u4e86\u4e00\u591c\uff0c\u8fd8\u662f\u4e0d\u89c1\u4ed6 \u4eec\u56de\u6765\u3002\u6b66\u5e84\u4e3b\u53d1\u4e0b\u547d\u4ee4\uff0c\u53eb\u5e84\u53ee\u5475\u5168\u90e8\u51c6\u5907\uff0c\u5e76\u6d3e\u51fa\u51e0\u4e2a\u5e84\u4e01\uff0c\u4e54\u88c5\u519c\u592b\uff0c\u51fa\u53bb\u8015\u4f5c\uff0c\u987a \u4fbf\u5de1\u98ce\u3002 \u3000\u3000\u6b66\u5bb6\u5e84\u4e2d\u4eba\u4eba\u90fd\u5f88\u7126\u6025\uff0c\u53ea\u6709\u6b66\u6210\u5316\u8fd9\u4e2a\u5b69\u5b50\u5374\u8df3\u8df3\u8e66\u8e66\uff0c\u9ad8\u5174\u5f97\u5f88\uff0c\u4ed6\u4e00\u65e9\u5c31\u8d77\u4e86 \u8eab\uff0c\u7f20\u7740\u4ed6\u7684\u59d0\u59d0\u6b66\u743c\u7476\u5230\u540e\u5c71\u53bb\u91c7\u675c\u9e43\u82b1\u3002\u6b66\u743c\u7476\u53ea\u6709\u5341\u516d\u5c81\uff0c\u4e5f\u662f\u4e00\u4e2a\u6dd8\u6c14\u7684\u5c0f\u59d1\u5a18\uff0c \u90a3\u65e5\u5929\u6c14\u6674\u6717\uff0c\u6625\u98ce\u4e2d\u9001\u6765\u65b0\u9c9c\u6ce5\u571f\u7684\u6c14\u606f\uff0c\u8fd8\u5939\u7740\u5b64\u4eba\u7684\u82b1\u9999\uff0c\u662f\u96be\u5f97\u7684\u597d\u5929\u6c14\u3002\u5979\u7ed9\u5f1f \u5f1f\u4e00\u62c9\uff0c\u4e5f\u81ea\u5fc3\u75d2\u96be\u71ac\uff0c\u59d0\u5f1f\u4fe9\u5077\u5077\u5730\u5c31\u4ece\u540e\u95e8\u6e9c\u51fa\uff0c\u8d70\u5230\u5c71\u4e0a\u53bb\u4e86\u3002 \u3000\u3000\u6b66\u5bb6\u5e84\u7684\u540e\u5c71\u5c71\u8c37\uff0c\u56e0\u6709\u4e94\u53f0\u5c71\u6321\u4f4f\u897f\u5317\u7684\u5bd2\u98ce\uff0c\u6c14\u5019\u8f83\u6696\uff0c\u66ae\u6625\u4e09\u6708\uff0c\u675c\u9e43\u82b1\u5df2\u7ea2\u904d \u5c71\u5761\u3002\u6e05\u6668\u65f6\u5206\uff0c\u8349\u6728\u51dd\u7740\u9732\u73e0\uff0c\u767e\u9e1f\u79bb\u5de2\u6b4c\u5531\uff0c\u66f4\u6f4d\u82b1\u5149\u6fc0\u6e58\uff0c\u6eaa\u6c34\u6e05\u6f84\uff0c\u6b66\u743c\u7476\u975e\u5e38\u9ad8 \u5174\uff0c\u4e00\u8fb9\u7ed9\u5f1f\u5f1f\u91c7\u82b1\uff0c\u4e00\u8fb9\u5c31\u5531\u8d77\u4e86\u5c71\u6b4c\uff1a \u3000\u3000\u201c\u6625\u65e5\u91cc\u6765\uff0c\u6ee1\u5c71\u662f\u675c\u9e43\u82b1\u3002 \u3000\u3000\u675c\u9e43\u82b1\u5440\uff0c\u5f00\u5f97\u50cf\u671d\u971e\u3002 \u3000\u3000\u8fdc\u65b9\u7684\u5ba2\u4eba\uff0c\u6b47\u4e00\u6b47\u5427\uff0c \u3000\u3000\u5e26\u4e0a\u4e00\u6735\u82b1\uff0c\u8ba9\u82b1\u9999\u4f34\u4f60\u8f6c\u56de\u5bb6\u2026\u2026\u201d \u3000\u3000\u6b4c\u58f0\u672a\u5b8c\uff0c\u4f59\u97f3\u7f2d\u7ed5\uff0c\u5ffd\u7136\u95f4\u6b66\u6210\u5316\u5927\u58f0\u53eb\u9053\uff1a\u201c\u59d0\u59d0\uff01\u201d \u3000\u3000\u6b66\u743c\u7476\u5faa\u58f0\u671b\u53bb\uff0c\u53ea\u89c1\u5c71\u5773\u90a3\u8fb9\u8d70\u8fc7\u6765\u4e00\u4e2a\u7a7f\u7740\u4ef6\u5927\u7ea2\u50e7\u888d\u7684\u5587\u561b\uff0c\u9762\u5982\u9505\u5e95\uff0c\u9f3b\u5b54\u671d \u5929\uff0c\u76f8\u8c8c\u5341\u5206\u4e11\u602a\u3002\u6b66\u743c\u7476\u9053\uff1a\u201c\u6210\u5316\uff0c\u4e0d\u8981\u7406\u4ed6\u3002\u201d\u5979\u81ea\u5df1\u8fd9\u6837\u8bf4\uff0c\u81ea\u5df1\u5374\u5148\u5657\u54e7\u4e00\u58f0\uff0c \u7b11\u4e86\u8d77\u6765\u3002\u5979\u4ece\u6765\u672a\u89c1\u8fc7\u8fd9\u6837\u4e11\u602a\u7684\u4eba\uff0c\u89c9\u5f97\u4ed6\u7684\u795e\u60c5\u5f88\u662f\u6709\u8da3\u3002 \u3000\u3000\u90a3\u7ea2\u8863\u5587\u561b\u770b\u89c1\u4e00\u4e2a\u6f02\u4eae\u7684\u5c0f\u59d1\u5a18\u770b\u7740\u4ed6\u7b11\uff0c\u5927\u8e0f\u6b65\u8d70\u6765\uff0c\u53fd\u54e9\u5495\u565c\u8bb2\u4e86\u51e0\u53e5\u8bdd\uff0c\u6b66\u743c \u7476\u4e0d\u61c2\u85cf\u8bed\uff0c\u6447\u4e86\u6447\u5934\uff0c\u7ea2\u8863\u5587\u561b\u4f38\u624b\u5411\u524d\u4e00\u6307\uff0c\u6b66\u743c\u7476\u4ee5\u4e3a\u4ed6\u8981\u6253\u5979\uff0c\u5f80\u65c1\u4e00\u7eb5\uff0c\u90a3\u5587\u561b \u54a7\u5f00\u5927\u53e3\uff0c\u563b\u563b\u5730\u7b11\uff0c\u6446\u6446\u624b\uff0c\u53c8\u8d76\u4e0a\u6765\u3002\u6210\u5316\u89c1\u4ed6\u8ffd\u81ea\u5df1\u7684\u59d0\u59d0\uff0c\u5fc3\u4e2d\u6709\u6c14\uff0c\u968f\u624b\u634f\u8d77\u4e00 \u56e2\u6ce5\u571f\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u5c31\u6253\u5728\u4ed6\u7684\u9762\u4e0a\uff0c\u7ea2\u8863\u5587\u561b\u54c7\u54c7\u5927\u53eb\uff0c\u6b66\u6210\u5316\u4e00\u4e0d\u505a\u4e8c\u4e0d\u4f11\uff0c\u4e24\u53ea\u5c0f\u817f \u4e00\u5f2f\uff0c\u731b\u7684\u4f3c\u7ed9\u5f39\u7c27\u5f39\u8d77\u4e00\u6837\uff0c\u5728\u534a\u7a7a\u6253\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u4e00\u8df3\u8df3\u5230\u5587\u561b\u7684\u5934\u4e0a\uff0c\u7528\u624b\u62c9\u7740\u5587\u561b \u7684\u8863\u9886\uff0c\u5f80\u4e0a\u4e00\u626f\uff0c\u90a3\u5587\u561b\u5927\u558a\u4e00\u58f0\uff0c\u5c06\u5934\u5411\u540e\u4e00\u649e\uff0c\u6b66\u6210\u5316\u65e9\u5df2\u677e\u4e86\u624b\u8df3\u843d\u5730\u4e0a\u3002\u7ea2\u8863\u5587 \u561b\u4f38\u5f00\u4e24\u53ea\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u5f2f\u8170\u4e71\u635e\uff0c\u6b66\u6210\u5316\u8e66\u8e66\u8df3\u8df3\uff0c\u6ed1\u4f3c\u6e38\u9c7c\uff0c\u7ea2\u8863\u5587\u561b\u5140\u662f\u635e\u4ed6\u4e0d \u7740\u3002\u6b66\u743c\u7476\u6050\u5f1f\u5f1f\u6709\u5931\uff0c\u4e5f\u8d76\u4e0a\u53bb\u5e2e\u624b\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u5c55\u5f00\u7ec8\u5357\u6d3e\u6e38\u8eab\u638c\u6cd5\uff0c\u7a7f\u82b1\u8774\u8776\u822c\u7684\u5de6 \u4e00\u62f3\u53f3\u4e00\u638c\uff0c\u6253\u5728\u5587\u561b\u8eab\u4e0a\u3002\u90a3\u5587\u561b\u94dc\u7b4b\u94c1\u9aa8\uff0c\u6328\u4e86\u8bb8\u591a\u62f3\u811a\uff0c\u867d\u4e0d\u89c9\u75db\uff0c\u4e5f\u6c14\u5f97\u53fd\u54e9\u5495\u565c \u7684\u4e71\u9a82\u3002 \u3000\u3000\u6b66\u743c\u7476\u59d0\u5f1f\u8d8a\u6253\u8d8a\u7cbe\u795e\uff0c\u6b63\u5728\u95f9\u5f97\u4e0d\u53ef\u5f00\u4ea4\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u82cd\u52b2\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u6210\u5316\uff0c\u4e0d \u8bb8\u95f9\uff01\u201d\u6b66\u6210\u5316\u4e00\u770b\uff0c\u89c1\u662f\u5085\u9752\u4e3b\u548c\u5192\u6d63\u83b2\u3001\u6613\u5170\u73e0\u6b63\u671d\u7740\u81ea\u5df1\u8d70\u6765\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u62db\u547c\u4e86\u59d0 \u59d0\u4e00\u58f0\uff0c\u4e24\u4eba\u6258\u5730\u8df3\u5c06\u51fa\u53bb\u3002\u7ea2\u8863\u5587\u5587\u6ca1\u5934\u6ca1\u8111\u5730\u8ffd\u4e0a\u524d\u6765\uff0c\u7ed9\u5085\u9752\u4e3b\u4e00\u4e2a\u201c\u987a\u624b\u7275\u7f8a\u201d\uff0c \u5c06\u4ed6\u4e24\u624b\u62ff\u7740\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002\u7ea2\u8863\u5587\u561b\u5f20\u53e3\u53c8\u9a82\uff0c\u6613\u5170\u73e0\u8fc7\u6765\uff0c\u4e5f\u53fd\u54e9\u5495\u565c\u5730\u8bb2\u4e86\u51e0\u53e5\u3002\u7ea2\u8863 \u5587\u561b\u9a6c\u4e0a\u6ee1\u9762\u5806\u4e86\u7b11\u5bb9\uff0c\u5085\u9752\u4e3b\u53cc\u624b\u4e00\u677e\uff0c\u4ed6\u7acb\u523b\u6253\u4e86\u4e00\u4e2a\u624b\u52bf\uff0c\u751f\u751f\u786c\u786c\u4ed6\u8bb2\u4e86\u4e00\u53e5\u6c49 \u8bdd\uff1a\u201c\u6211\u627e\u6b66\u5bb6\u5e84\u3002\u201d \u3000\u3000\u539f\u6765\u6613\u5170\u73e0\u5728\u6f20\u5916\u957f\u5927\uff0c\u61c2\u5f97\u85cf\u8bed\u3002\u5979\u89c1\u7ea2\u8863\u5587\u561b\u4e00\u9762\u6253\u4e00\u9762\u9a82\u6b66\u743c\u7476\u59d0\u5f1f\uff1a\u201c\u4f60\u8fd9\u4e24 \u4e2a\u5c0f\u5a03\u5a03\u600e\u7684\u8fd9\u6837\u6ca1\u5bb6\u6559\uff1f\u6211\u597d\u610f\u95ee\u8def\uff0c\u4f60\u4eec\u5374\u6253\u8d77\u6211\u6765\uff0c\u96be\u9053\u6c49\u4eba\u90fd\u662f\u8fd9\u6837\u4e0d\u8bb2\u7406\uff1f\u201d\u5979 \u544a\u8bc9\u5085\u9752\u4e3b\u77e5\u9053\uff0c\u5085\u9752\u4e3b\u5df2\u770b\u51fa\u8fd9\u4e2a\u5587\u561b\uff0c\u6b63\u662f\u6628\u65e5\u548c\u695a\u662d\u5357\u4e00\u8d77\uff0c\u540c\u5230\u4e94\u53f0\u5c71\u89c2\u5149\u7684\u5587\u561b \u50e7\uff0c\u542c\u6613\u5170\u73e0\u8bf4\uff0c\u4ed6\u4f3c\u4e4e\u53c8\u4e0d\u542b\u6076\u610f\uff0c\u4e0d\u77e5\u662f\u654c\u662f\u53cb\uff0c\u5fc3\u4e2d\u9887\u4e3a\u7591\u60d1\uff0c\u56e0\u6b64\u5148\u4e0a\u6765\u5c06\u4ed6\u64d2 \u4e0b\u3002 \u3000\u3000\u8fd9\u65f6\u7531\u6613\u5170\u73e0\u6743\u5145\u901a\u8bd1\uff0c\u53ea\u89c1\u4ed6\u6307\u4e00\u6307\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6628\u5929\u8fd9\u4f4d\u5c45\u58eb\u5c06\u695a\u662d\u5357\u6253\u843d\u5c71\u8c37\uff0c \u6211\u4e0b\u53bb\u627e\u5bfb\uff0c\u51e0\u4e4e\u7ed9\u695a\u662d\u5357\u6253\u6b7b\uff0c\u5e78\u5f97\u4e00\u4f4d\u6c49\u4eba\u642d\u6551\uff0c\u53ea\u51e0\u4e2a\u7167\u9762\uff0c\u5c31\u5c06\u695a\u662d\u5357\u6253\u8dd1\uff0c\u90a3\u4f4d \u6c49\u4eba\u53eb\u6211\u627e\u6b66\u5bb6\u5e84\u3002\u54ea\u77e5\u5374\u78b0\u5230\u8fd9\u4e24\u4e2a\u4e0d\u8bb2\u7406\u7684\u5a03\u5a03\u3002\u201d\u5085\u9752\u4e3b\u542c\u4e86\u5927\u4e3a\u5947\u602a\uff0c\u4e0d\u89e3\u695a\u662d\u5357 \u548c\u4ed6\u4e00\u8def\uff0c\u4e3a\u4f55\u5374\u5c06\u6253\u8d77\u6765\uff1f\u800c\u4e14\u695a\u662d\u5357\u7684\u6b66\u529f\u975e\u540c\u5c0f\u53ef\uff0c\u53c8\u662f\u4f55\u4eba\u6709\u6b64\u529f\u529b\uff0c\u53ea\u51e0\u4e2a\u7167 \u9762\uff0c\u5c31\u6253\u8dd1\u4e86\u4ed6\uff1f \u3000\u3000\u5085\u9752\u4e3b\u6ee1\u6000\u7591\u60d1\uff0c\u53eb\u6613\u5170\u73e0\u95ee\u90a3\u5587\u561b\uff0c\u95f4\u4ed6\u6240\u9047\u5230\u7684\u90a3\u4e2a\u6c49\u4eba\u662f\u4e2a\u600e\u6837\u7684\u4eba\uff0c\u5587\u561b\u7ed3\u7ed3 \u5df4\u5df4\u8bf4\u5f97\u4e0d\u6e05\uff0c\u5ffd\u7136\u95f4\uff0c\u4ed6\u7528\u624b\u4e00\u6307\uff0c\u5bf9\u6613\u5170\u73e0\u9053\uff0c\u201c\u4f60\u4eec\u4e0d\u5fc5\u95ee\u4e86\uff0c\u4f60\u770b\uff0c\u90a3\u4e0d\u662f\u4ed6\u6765 \u4e86\uff01\u201d\u8bdd\u58f0\u672a\u5b8c\uff0c\u5c71\u5773\u5904\u5df2\u8f6c\u51fa\u4e24\u4e2a\u5f02\u6837\u88c5\u675f\u7684\u6c49\u5b50\uff0c\u4e00\u4e2a\u7a7f\u7740\u7070\u6251\u6251\u7684\u591c\u884c\u8863\uff0c\u4e00\u4e2a\u5374\u662f \u6e05\u5bab\u536b\u58eb\u6253\u626e\u3002\u6613\u5170\u73e0\u4e00\u89c1\uff0c\u201c\u54d7\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u6ee1\u9762\u7b11\u5bb9\u98de\u8dd1\u4e0a\u53bb\uff0c\u597d\u50cf\u78b0\u5230\u4e86\u4eb2\u4eba \u4e00\u6837\u3002 \u3000\u3000\u6613\u5170\u73e0\u5feb\uff0c\u5085\u9752\u4e3b\u6bd4\u5979\u66f4\u5feb\uff0c\u4ed6\u888d\u8896\u4e00\u4f5b\uff0c\u5b9b\u5982\u5b64\u9e64\u63a0\u7a7a\uff0c\u98de\u8d8a\u8fc7\u6613\u5170\u73e0\uff0c\u8f7b\u98d8\u98d8\u5730\u5728 \u4e24\u4eba\u9762\u524d\u4e00\u843d\uff0c\u4f38\u624b\u5411\u960e\u4e2d\u5929\u4e00\u6293\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u536b\u58eb\uff0c\u4f60\u4e5f\u6765\u4e86\uff1f\u201d\u7070\u8863\u4eba\u62a2\u5728\u5934\u91cc\uff0c\u4f38\u624b \u4e00\u67b6\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u4e0d\u5fc5\u5ba2\u6c14\uff01\u201d\u5085\u9752\u4e3b\u7684\u624b\uff0c\u5982\u89e6\u67af\u67f4\uff0c\u4ed6\u500f\u5730\u9a78\u6307\u5982\u621f\uff0c\u5411\u7070\u8863 \u4eba\u5de6\u80a9\u4e95\u7a74\u4fbf\u70b9\uff0c\u7070\u8863\u4eba\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u53cd\u8fce\u4e0a\u53bb\uff0c\u5085\u9752\u4e3b\u53cc\u6307\u70b9\u4e2a\u6b63\u7740\uff0c\u7070\u8863\u4eba\u4f3c\u6beb\u65e0\u6240\u89c9\uff0c \u95f2\u95f2\u5730\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88\u4e0d\u8981\u548c\u6211\u5f00\u73a9\u7b11\uff01\u201d\u4ed6\u5fae\u5fae\u540e\u9000\uff0c\u53cc\u638c\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u8fd9\u53a2\u6709\u793c \u4e86\u3002\u201d\u5085\u9752\u4e3b\u54ea\u6562\u6020\u6162\uff0c\u4e5f\u53cc\u638c\u5408\u4ec0\uff0c\u8fd8\u4ed6\u4e00\u63d6\uff0c\u4e24\u8fb9\u90fd\u662f\u638c\u98ce\u98d2\u7136\uff0c\u65e0\u5f62\u4e2d\u5c31\u4f3c\u5bf9\u649e\u4e00 \u6837\uff0c\u5085\u9752\u4e3b\u7ed9\u9707\u9000\u4e09\u56db\u6b65\uff0c\u7070\u8863\u4eba\u4e5f\u6447\u6447\u6643\u6643\uff0c\u51e0\u6b32\u8dcc\u5012\u3002 \u3000\u3000\u8fd9\u65f6\u6613\u5170\u73e0\u5df2\u4e0a\u6765\uff0c\u5f80\u4e24\u4eba\u4e2d\u95f4\u4e00\u7ad9\uff0c\u5bf9\u5085\u9752\u4e3b\u9053\uff1a\u201c\u5085\u4f2f\u4f2f\uff0c\u8fd9\u4f4d\u4fbf\u662f\u5929\u5c71\u795e\u8292\u51cc\u672a \u98ce\uff01\u201d\u53c8\u5411\u51cc\u672a\u98ce\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u4fbf\u662f\u65e0\u6781\u6d3e\u8001\u524d\u8f88\u5085\u9752\u4e3b\u3002\u201d\u51cc\u672a\u98ce\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a \u201c\u539f\u6765\u662f\u795e\u533b\u5085\u8001\u5148\u751f\u5728\u6b64\uff0c\u5931\u656c\uff01\u5931\u656c\uff01\u201d\u6025\u5fd9\u91cd\u65b0\u65bd\u793c\uff0c\u8fd9\u56de\u53ef\u662f\u771f\u7684\u65bd\u793c\uff0c\u6ca1\u6709\u638c\u98ce \u53d1\u51fa\u4e86\u3002 \u3000\u3000\u5085\u9752\u4e3b\u89c1\u4ed6\u79f0\u81ea\u5df1\u4e3a\u201c\u795e\u533b\u201d\uff0c\u60c5\u77e5\u4ed6\u53ea\u662f\u4f69\u670d\u81ea\u5df1\u7684\u533b\u672f\uff0c\u5e76\u4e0d\u662f\u4f69\u670d\u81ea\u5df1\u7684\u6b66\u529f\uff0c \u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u7684\u6b66\u529f\u662f\u6bd4\u6211\u7a0d\u5f3a\u4e00\u70b9\uff0c\u4f46\u82e5\u8bf4\u4e09\u51e0\u4e2a\u7167\u9762\u4fbf\u80fd\u6253\u8d25\u695a\u662d\u5357\uff0c\u5374\u96be\u4ee4 \u4eba\u7f6e\u4fe1\u3002\u201d\u4ed6\u4e0d\u77e5\u51cc\u672a\u98ce\u4e0e\u695a\u662d\u5f88\u6709\u6e0a\u6e90\uff0c\u695a\u662d\u5357\u4e00\u89c1\u4ed6\u51fa\u624b\u7684\u5bb6\u6570\uff0c\u4fbf\u5413\u4e86\u4e00\u8df3\uff0c\u4e00\u7740\u614c \u5c31\u4e2d\u4e86\u4e00\u638c\uff0c\u6025\u6025\u5954\u9003\u3002\u56e0\u6b64\u5085\u9752\u4e3b\u6628\u665a\u591c\u63a2\u4e94\u53f0\u5c71\uff0c\u4e0e\u695a\u662d\u5357\u4ea4\u624b\u65f6\u53d1\u73b0\u695a\u662d\u5357\u7684\u529f\u529b\u4f3c \u4e4e\u51cf\u9000\u4e86\u8bb8\u591a\uff0c\u539f\u56e0\u5c31\u662f\u695a\u662d\u5357\u521a\u521a\u5403\u4e86\u51cc\u672a\u98ce\u4e00\u638c\u3002 \u3000\u3000\u5f53\u4e0b\u5085\u9752\u4e3b\u4e5f\u91cd\u65b0\u65bd\u793c\uff0c\u628a\u51cc\u672a\u98ce\u770b\u4e2a\u6e05\u695a\uff0c\u8fd9\u4e2a\u5927\u6f20\u5916\u7684\u4f20\u5947\u4eba\u7269\uff0c\u5374\u662f\u4e2d\u7b49\u8eab\u6750\uff0c \u5e76\u4e0d\u9b41\u68a7\uff0c\u6700\u7279\u522b\u7684\u662f\uff0c\u9762\u4e0a\u6709\u4e24\u9053\u5200\u75d5\uff0c\u5341\u5206\u96be\u770b\u3002\u51cc\u672a\u98ce\u89c1\u5085\u9752\u4e3b\u6ce8\u89c6\u81ea\u5df1\uff0c\u7b11\u9053\uff1a \u201c\u5085\u8001\u5148\u751f\uff0c\u8fd8\u662f\u5148\u8bf7\u4f60\u770b\u770b\u6211\u8fd9\u4f4d\u670b\u53cb\u5427\uff01\u201d\u5085\u9752\u4e3b\u671d\u960e\u4e2d\u5929\u9762\u4e0a\u4e00\u770b\uff0c\u7981\u4e0d\u4f4f\u5931\u58f0\u53eb\u4e86 \u51fa\u6765\uff0c\u62c9\u7740\u960e\u4e2d\u5929\u4fbf\u8dd1\uff0c\u51cc\u672a\u98ce\u83ab\u540d\u5176\u5999\u5730\u8ddf\u5728\u540e\u9762\u3002\u5085\u9752\u4e3b\u5c06\u960e\u4e2d\u5929\u62c9\u5230\u4e86\u4e00\u4e2a\u5c71\u6eaa\u65c1 \u8fb9\uff0c\u53eb\u960e\u4e2d\u5929\u9053\uff1a\u201c\u4f60\u559d\u51e0\u53e3\u6c34\uff0c\u7136\u540e\u518d\u55b7\u4e00\u53e3\u6c34\u5728\u675c\u9e43\u82b1\u4e0a\u3002\u201d\u960e\u4e2d\u5929\u5982\u8a00\u55b7\u53bb\uff0c\u53ea\u89c1\u4e00 \u4e1b\u751f\u6c14\u52c3\u52c3\u7684\u675c\u9e43\u82b1\uff0c\u7ed9\u6c34\u4e00\u55b7\uff0c\u767b\u65f6\u67af\u840e\u4e0b\u53bb\uff0c\u4e00\u74e3\u74e3\u96f6\u843d\u5730 \u3000\u3000\u51cc\u672a\u98ce\u77eb\u820c\u96be\u4e0b\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u6bd2\u7269\uff1f\u5982\u6b64\u5389\u5bb3\uff1f\u201d\u5085\u9752\u751f\u770b\u4e86\u4e00\u770b\u88ab\u960e\u4e2d\u5929\u55b7\u8fc7 \u7684\u675c\u9e43\u82b1\uff0c\u5df2\u7531\u9c9c\u7ea2\u53d8\u6210\u767d\u8272\uff0c\u8be7\u5f02\u975e\u5e38\uff0c\u8bf4\u9053\uff1a\u201c\u5eb7\u7199\u597d\u6bd2\uff0c\u8fd9\u4e43\u662f\u897f\u85cf\u7684\u5b54\u96c0\u6bd2\u548c\u6ec7\u6c60 \u7684\u9e64\u9876\u7ea2\u5408\u6210\u7684\u6bd2\u836f\u3002\u5403\u4e86\u8fd9\u79cd\u6bd2\u7269\uff0c\u4e0d\u9700\u534a\u4e2a\u65f6\u8fb0\uff0c\u4fbf\u5f62\u9500\u9aa8\u6bc1\uff0c\u4f60\u600e\u4e48\u652f\u6301\u5f97\u8fd9\u4e48\u4e9b\u65f6 \u5019\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u662f\u6211\u7ed9\u4e86\u4ed6\u7528\u5929\u5c71\u96ea\u83b2\u70ae\u5236\u7684\u78a7\u7075\u4e39\u3002\u201d\u5085\u9752\u4e3b\u70b9\u4e86\u70b9\u5934\uff0c\u9ed8\u9ed8\u4e0d\u8bed\uff0c \u62c9\u7740\u960e\u4e2d\u5929\u4fbf\u8d70\uff0c\u53ef\u662f\u5374\u8d70\u5f97\u5f88\u6162\uff0c\u960e\u4e2d\u5929\u60f3\u65bd\u5c55\u8f7b\u529f\uff0c\u4e5f\u7ed9\u4ed6\u6309\u4f4f\u3002\u960e\u4e2d\u5929\u76ee\u7779\u675c\u9e43\u82b1\u53d8 \u8272\uff0c\u5fc3\u4e2d\u60f6\u6050\uff0c\u95ee\u5085\u9752\u4e3b\u9053\uff1a\u201c\u53ef\u6709\u89e3\u6551\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6211\u5c3d\u6211\u7684\u529b\u5c31\u662f\u4e86\u3002\u201d\u51cc\u672a\u98ce \u9053\uff1a\u201c\u8fd9\u6bd2\u9152\u65e2\u7136\u5982\u6b64\u5389\u5bb3\uff0c\u4f55\u4ee5\u5eb7\u7199\u53c8\u5148\u996e\u4e00\u676f\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u89e3\u5b54\u96c0\u7caa\u548c\u9e64\u9876\u7ea2\u7684 \u6bd2\uff0c\u987b\u7528\u4e0a\u597d\u7684\u957f\u767d\u5c71\u4eba\u53c2\u3001\u5929\u5c71\u96ea\u83b2\u3001\u897f\u85cf\u7684\u66fc\u9640\u7f57\u82b1\u8fd9\u51e0\u5473\u836f\uff0c\u518d\u548c\u9617\u7f8e\u7389\u4e00\u540c\u6363\u788e\uff0c \u518d\u7528\u9e64\u6d8e\u6eb6\u5316\uff0c\u70bc\u6210\u89e3\u836f\uff0c\u800c\u4e14\u987b\u7acb\u5373\u670d\u4e0b\uff0c\u4f60\u7ed9\u4ed6\u7684\u5929\u5c71\u96ea\u83b2\uff0c\u53ea\u662f\u5408\u6210\u89e3\u836f\u4e2d\u7684\u4e00\u5473\uff0c \u5eb7\u7199\u6562\u5148\u996e\u6bd2\u9152\uff0c\u5f53\u7136\u662f\u4ed6\u9884\u5148\u670d\u4e0b\u4e86\u89e3\u836f\u3002\u201d\u960e\u4e2d\u5929\u5fe7\u5f62\u4e8e\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0\u5473\u836f\uff0c\u90fd\u662f \u4eba\u4e16\u5947\u73cd\uff0c\u9664\u4e86\u5927\u5185\u5177\u5907\uff0c\u6211\u4eec\u54ea\u91cc\u53bb\u627e\uff1f\u201d\u5085\u9752\u4e3b\u7b11\u9053\uff1a\u201c\u6362\u4e86\u522b\u4eba\uff0c\u559d\u4e0b\u8fd9\u79cd\u6bd2\u9152\uff0c\u5b9a \u7136\u65e0\u6cd5\u89e3\u6551\uff0c\u4f60\u4e5f\u8bb8\u8fd8\u6709\u529e\u6cd5\uff0c\u4f60\u4e0d\u7528\u95ee\uff0c\u968f\u6211\u6765\u5c31\u662f\u3002 \u3000\u3000\u5f53\u4e0b\u4e00\u884c\u4eba\u7f13\u7f13\u8d70\u56de\u6b66\u5bb6\uff0c\u6b66\u743c\u7476\u59d0\u5f1f\uff0c\u77e5\u9053\u7ea2\u8863\u5587\u561b\u5e76\u975e\u6076\u4eba\uff0c\u90fd\u8d70\u4e0a\u524d\u6765\u8d54\u7f6a\uff0c\u6b66 \u6210\u5316\u7b11\u563b\u563b\u5730\u6307\u7740\u5587\u561b\uff0c\u53c8\u6307\u7740\u81ea\u5df1\u7684\u9f3b\u5b50\u505a\u7740\u624b\u52bf\u9053\uff1a\u201c\u8fd9\u6b21\u6211\u6253\u4e86\u4f60\u4e00\u987f\uff0c\u4f60\u522b\u89c1\u602a\uff0c \u4e0b\u6b21\u4f60\u548c\u522b\u4eba\u6253\u67b6\uff0c\u6211\u5fc5\u5b9a\u5e2e\u4f60\uff01\u201d\u7ea2\u8863\u5587\u561b\u867d\u542c\u4e0d\u61c2\uff0c\u4e5f\u731c\u5f97\u5230\u4ed6\u7684\u610f\u601d\uff0c\u5f20\u5f00\u5927\u5634\u5df4\u8d54 \u7b11\u3002 \u3000\u3000\u5085\u9752\u4e3b\u7b49\u4eba\u56de\u6765\uff0c\u65e9\u5df2\u6709\u4eba\u62a5\u8baf\uff0c\u6b66\u5e84\u4e3b\u548c\u97e9\u5fd7\u90a6\u51fa\u6765\u8fce\u63a5\uff0c\u97e9\u5fd7\u90a6\u77a7\u89c1\u51cc\u672a\u98ce\uff0c\u559c\u51fa \u671b\u5916\uff0c\u5927\u53eb\u201c\u7a00\u5ba2\uff01\u7a00\u5ba2\uff01\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u97e9\u603b\u8235\u4e3b\uff0c\u4f60\u6d3e\u4eba\u6765\u627e\u6211\uff0c\u6211\u54ea\u77e5\u9053\uff0c\u4ed6\u4eec\u6ca1\u6211 \u7740\u6211\uff0c\u6211\u5374\u5148\u627e\u5230\u4f60\u4e86\u3002\u201d\u97e9\u5fd7\u90a6\u7b11\u563b\u563b\u5730\u6765\u62c9\u4ed6\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u603b\u8235\u4e3b\u4e86\uff0c\u4f60\u60f3\u89c1 \u89c1\u6211\u4eec\u7684\u65b0\u8235\u4e3b\u3002\u201d\u8bf4\u7740\u62c9\u4ed6\u5f80\u91cc\u6025\u8d70\uff0c\u56b7\u9053\uff1a\u201c\u5218\u5927\u59d0\uff0c\u6211\u628a\u5929\u5c71\u795e\u8292\u4e5f\u8bf7\u6765\u4e86\uff0c\u4f60\u5f97\u51fa \u6765\u89c1\u554a\uff01\u201d\u56b7\u7f62\u53c8\u5bf9\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4f4d\u65b0\u8235\u4e3b\u4e43\u662f\u5973\u4e2d\u8c6a\u6770\uff0c\u4e5f\u662f\u5c0f\u5f1f\u9664\u4e86\u5144\u957f\u4e4b\u5916\uff0c \u751f\u5e73\u6700\u4f69\u670d\u7684\u4e00\u4eba\u3002\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u5218\u90c1\u82b3\u7531\u901a\u660e\u548c\u5c1a\u966a\u7740\uff0c\u4ece\u91cc\u9762\u8d70\u4e86\u51fa\u6765\uff0c\u901a\u660e\u548c\u5c1a\u5927\u6b65\u51b2\u4e0a\uff0c\u56b7\u9053\uff1a\u201c\u54ea \u4f4d\u662f\u5929\u5c71\u795e\u8292\uff1f\u6211\u5148\u89c1\u89c1\u3002\u201d\u51cc\u672a\u98ce\u4e00\u7b11\u4f38\u51fa\u624b\u6765\uff0c\u901a\u660e\u548c\u5c1a\u7528\u529b\u4e00\u63e1\uff0c\u5fc3\u60f3\uff1a\u201c\u4e14\u8bd5\u8bd5\u4f60 \u5929\u5c71\u795e\u8292\u7684\u529f\u529b\u600e\u6837\uff1f\u201d\u51cc\u672a\u98ce\u597d\u50cf\u77e5\u9053\u4ed6\u7684\u610f\u601d\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u522b\u8fd9\u6837\u7528\u529b\u554a\uff01\u201d\u901a\u660e\u548c\u5c1a \u63e1\u7740\u51cc\u672a\u98ce\u7684\u624b\uff0c\u53ea\u89c9\u67d4\u82e5\u65e0\u9aa8\uff0c\u5c31\u50cf\u63e1\u7740\u4e00\u56e2\u68c9\u82b1\u4e00\u6837\uff0c\u65e0\u5904\u4f7f\u52b2\u3002\u6b63\u60ca\u7591\u95ee\uff0c\u201c\u68c9\u82b1\u201d \u5ffd\u7136\u53d8\u6210\u201c\u94c1\u68d2\u201d\uff0c\u901a\u660e\u548c\u5c1a\u5934\u624b\u75bc\u75db\uff0c\u8fde\u5fd9\u653e\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u597d\u529f\u592b\uff0c\u6211\u670d\u4f60\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\u5218\u90c1\u82b3\u5df2\u8d70\u5230\u8ddf\u524d\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u901a\u660e\u522b\u80e1\u95f9\uff01\u201d\uff0c\u58f0\u97f3\u4ecd\u662f\u90a3\u6837\u6e29\u67d4\uff0c\u4f46\u8fd9\u6e29\u67d4\u7684 \u58f0\u97f3\u5374\u597d\u50cf\u6295\u4e0b\u51cc\u672a\u98ce\u5fc3\u6e56\u7684\u77f3\u5b50\u3002 \u3000\u3000\u51cc\u5728\u98ce\u5fc3\u5934\u4e00\u9707\uff0c\u8eab\u8eaf\u5fae\u98a4\uff0c\u6545\u610f\u4f5c\u51fa\u61d2\u6d0b\u6d0b\u7684\u795e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u4fbf\u662f\u6c5f\u6e56\u4e0a\u4eba\u79f0 \u2018\u4e91\u9526\u5251\u2019\u7684\u5218\u90c1\u82b3\u4e86\u5427\uff1f\u606d\u559c\u4f60\u505a\u4e86\u603b\u9640\u4e3b\u3002\u201d\u968f\u5373\u53c8\u7b11\u9053\uff1a\u201c\u66ae\u6625\u4e09\u6708\uff0c\u6b63\u662f\u6c5f\u5357\u6700\u597d \u7684\u5b63\u8282\uff0c\u5218\u603b\u8235\u4e3b\u5374\u4ece\u6cb3\u5357\u6765\u5230\u897f\u5317\uff0c\u96be\u9053\u5c31\u53ea\u4e3a\u4e86\u591a\u94ce\u8fd9\u4e2a\u8d3c\u5b50\u5417\uff1f\u201d\u5218\u90c1\u82b3\u6014\u4e86\u4e00\u5f81\uff0c \u5fc3\u60f3\u8fd9\u4eba\u8bf4\u8bdd\u597d\u6ca1\u793c\u8c8c\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u51cc\u82f1\u96c4\u7684\u610f\u601d\u662f\u6211\u4eec\u4e0d\u8be5\u6765\u5417\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u600e \u6562\u8fd9\u6837\u8bf4\uff0c\u53ea\u662f\u82e5\u4e3a\u4e86\u591a\u94ce\u4e00\u4eba\uff0c\u5174\u5e08\u52a8\u4f17\u5b9e\u72af\u4e0d\u7740\uff0c\u8981\u5149\u590d\u6c49\u65cf\u6cb3\u5c71\uff0c\u4e5f\u4e0d\u662f\u6697\u6740\u4e00\u4e24\u4eba \u6240\u80fd\u6d4e\u4e8b\u3002\u201d\u901a\u660e\u548c\u5c1a\u5927\u4e3a\u4e0d\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5351\u738b\u65e7\u90e8\u5728\u6c5f\u5357\u7ed9\u5b98\u519b\u56f4\u527f\uff0c\u7acb\u8db3\u4e0d\u4f4f\u4e86\uff0c \u6211\u4eec\u8fd9\u51e0\u4e2a\u4eba\u624d\u8d76\u5230\u897f\u5317\u6765\uff0c\u6b32\u5728\u897f\u5317\u518d\u521b\u57fa\u4e1a\uff0c\u591a\u94ce\u4e0d\u8fc7\u662f\u5076\u5c14\u78b0\u7740\u7f62\u4e86\u3002\u51cc\u82f1\u96c4\u56e0\u6b64\u4fbf \u803b\u7b11\u6211\u4eec\u5417\uff1f\u201d\u51cc\u672a\u98ce\u7ede\u626d\u7740\u53cc\u624b\uff0c\u7b11\u9053\uff1a\u201c\u5c82\u6562\uff0c\u5c82\u6562\uff01\u4e0d\u8fc7\uff0c\u6b32\u56fe\u5927\u4e8b\uff0c\u6211\u770b\u8fd8\u662f\u8981\u56de \u5230\u5357\u65b9\u53bb\u3002\u201d\u5085\u9752\u4e3b\u542c\u51fa\u8bdd\u91cc\u6709\u56e0\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u8bf4\uff1f\u201d\u51cc\u672a\u98ce\u6307\u6307\u7ea2\u8863\u5587\u561b\u9053\uff1a\u201c\u4ed6 \u5e26\u6765\u4e86\u7edd\u5927\u7684\u673a\u5bc6\u6d88\u606f\uff0c\u8fdb\u53bb\u518d\u8c08\u5427\u3002\u4e0d\u8fc7\u8fd8\u662f\u5148\u8bf7\u4f60\u6cbb\u6cbb\u8fd9\u4f4d\u670b\u53cb\u3002\u201d\u8bf4\u7f62\u6307\u4e86\u4e00\u6307\u960e\u4e2d \u5929\u3002 \u3000\u3000\u5218\u90c1\u82b3\u89c1\u51cc\u672a\u98ce\u7ede\u626d\u7740\u53cc\u624b\uff0c\u731b\u7136\u89e6\u8d77\u5fc3\u4e8b\uff0c\u8fd9\u4eba\u7684\u795e\u6001\u597d\u611f\u81ea\u5df1\u5c11\u5e74\u65f6\u4ee3\u7684\u670b\u53cb\uff0c\u53ef \u662f\u9762\u8c8c\u5374\u5b8c\u5168\u4e0d\u540c\u3002\u90a3\u4f4d\u670b\u53cb\u662f\u4e2a\u82f1\u4fca\u5c11\u5e74\uff0c\u800c\u51cc\u672a\u98ce\u5374\u8fd9\u6837\u96be\u770b\uff0c\u5979\u4e0d\u7981\u8fde\u8fde\u770b\u4e86\u51cc\u672a\u98ce \u51e0\u773c\u3002 \u3000\u3000\u518d\u8bf4\u4f17\u4eba\u8fdb\u4e86\u5185\u5385\u4e4b\u540e\uff0c\u5085\u9752\u4e3b\u72ec\u81ea\u5e26\u960e\u4e2d\u5929\u5230\u4e86\u4e00\u4e2a\u9759\u5ba4\uff0c\u8bf4\u9053\uff1a\u201c\u522b\u4eba\u996e\u4e86\u8fd9\u79cd\u6bd2 \u9152\uff0c\u7684\u786e\u65e0\u6cd5\u89e3\u6551\u3002\u4f60\u5e78\u5728\u5f97\u4e86\u51cc\u672a\u98ce\u7684\u5929\u5c71\u96ea\u83b2\uff0c\u6682\u65f6\u53ef\u4ee5\u6491\u7740\uff0c\u800c\u4f60\u53c8\u662f\u7ec3\u8fc7\u5185\u529f\u7684 \u4eba\uff0c\u53ef\u4ee5\u8bd5\u7528\u2018\u6c14\u529f\u7597\u6cd5\u2019\u5e73\u5fc3\u9759\u6c14\uff0c\u610f\u5b88\u4e39\u7530\uff0c\u5728\u5ba4\u5185\u6253\u5750\u4e8c\u5341\u56db\u4e2a\u65f6\u8fb0\uff0c\u628a\u6bd2\u6c14\u903c\u5728\u80a0 \u810f\u4e00\u9685\uff0c\u7136\u540e\u6211\u518d\u7ed9\u4f60\u4e00\u5242\u6cfb\u836f\uff0c\u628a\u5b83\u6e32\u6cc4\u51fa\u6765\uff0c\u7136\u540e\u518d\u7528\u836f\u56fa\u672c\u7119\u6e90\uff0c\u5927\u7ea6\u5f53\u53ef\u65e0\u4e8b\u3002\u201d \u960e\u4e2d\u5929\u5927\u559c\u8c22\u8fc7\uff0c\u95ee\u4e86\u5085\u9752\u4e3b\u201c\u6c14\u529f\u7597\u6cd5\u201d\u7684\u6253\u5750\u59ff\u52bf\u548c\u547c\u5438\u65b9\u6cd5\uff0c\u539f\u6765\u548c\u4ed6\u6240\u5b66\u8fc7\u7684\u201c\u5750 \u529f\u201d\u4e5f\u5dee\u4e0d\u591a\uff0c\u7acb\u5373\u95ed\u76ee\u76d8\u819d\uff0c\u5728\u9759\u5ba4\u5185\u6253\u8d77\u5750\u6765\u3002 \u3000\u3000\u5085\u9752\u4e3b\u6599\u7406\u5b8c\u6bd5\uff0c\u8d70\u4e86\u51fa\u6765\uff0c\u53ea\u89c1\u5385\u5185\u7fa4\u96c4\uff0c\u96c5\u96c0\u65e0\u58f0\uff0c\u9762\u8272\u5f88\u662f\u7d27\u5f20\u3002\u51cc\u672a\u98ce\u7b11\u9053\uff1a \u201c\u5085\u8001\u524d\u8f88\u6765\u4e86\uff0c\u53ef\u4ee5\u5546\u91cf\u5546\u91cf\u3002\u201d\u5085\u9752\u4e3b\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u5440\uff1f\u201d\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u5085\u5148\u751f\u6628 \u665a\u548c\u5192\u5c0f\u59d0\u63a2\u5c71\uff0c\u53ef\u542c\u5230\u695a\u662d\u5357\u8fd9\u53ae\u548c\u7687\u5e1d\u8bf4\u4e86\u4e9b\u4ec0\u4e48\u6765\uff1f\u201d \u3000\u3000\u5085\u9752\u4e3b\u60f3\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u50cf\u542c\u5230\u4ed6\u4eec\u8c08\u8d77\u5434\u4e09\u6842\uff0c\u5eb7\u7199\u4f3c\u662f\u5f88\u751f\u6c14\u7684\u6837\u5b50\u3002\u201d\u8bf4 \u7f62\uff0c\u5ffd\u7136\u60f3\u8d77\u4e00\u4e8b\uff0c\u95ee\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6628\u665a\u7528\u98de\u714c\u77f3\u6253\u788e\u94dc\u5854\u4e0a\u7409\u7483\u706f\u7684\uff0c\u60f3\u6765\u5c31\u662f\u4f60\u4e86\u3002\u201d \u51cc\u672a\u98ce\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d\u5085\u9752\u4e3b\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u63d0\u8d77\u5434\u4e09\u6842\uff0c\u5434\u4e09\u6842\u548c\u6211\u4eec\u6709\u4ec0\u4e48\u5173\u7cfb \u5462\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u53e0\u7740\u4e24\u4e2a\u624b\u6307\u7b11\u9053\uff1a\u201c\u5927\u6709\u5173\u7cfb\uff0c\u5434\u4e09\u6842\u5c31\u8981\u53db\u6e05\u4e86\u3002\u201d\u5085\u9752\u4e3b\u5927\u5403\u4e00\u60ca\uff0c\u5c06\u4fe1 \u5c06\u7591\u3002 \u3000\u3000\u5434\u4e09\u6842\u662f\u5f15\u6e05\u5175\u5165\u5173\u7684\u5927\u6c49\u5978\uff0c\u5f53\u65f6\u5b98\u5c01\u201c\u5e73\u897f\u738b\u201d\uff0c\u5f00\u5e9c\u6606\u660e\uff0c\u6709\u4e91\u5357\u3001\u56db\u5ddd\u4e24\u7701\u4e4b \u5730\uff0c\u6b63\u662f\u6e05\u5ef7\u6700\u501a\u91cd\u7684\u85e9\u738b\u3002\u51cc\u672a\u98ce\u8bf4\u4ed6\u8981\u53cd\u53db\u671d\u5ef7\uff0c\u8fd9\u6d88\u606f\u5b9e\u5728\u6765\u5f97\u7a81\u5140\u3002\u3002 \u3000\u3000\u51cc\u672a\u98ce\u89c1\u5085\u9752\u4e3b\u5c06\u4fe1\u5c06\u7591\uff0c\u7b11\u9053\uff1a\u201c\u7ea2\u8863\u5587\u561b\u548c\u960e\u4e2d\u5929\u90fd\u662f\u8bc1\u4eba\uff01\u201d\u539f\u6765\u6e05\u5175\u5165\u5173\uff0c\u5f97 \u660e\u671d\u53db\u81e3\u5434\u4e09\u6842\u3001\u5c1a\u53f8\u559c\u3001\u803f\u4ef2\u660e\u4e09\u4eba\u4e4b\u529b\u751a\u591a\uff0c\u5c24\u4ee5\u5434\u4e09\u6842\u7684\u201c\u529f\u52b3\u201d\u6700\u5927\u3002\u6ee1\u6e05\u5165\u5173 \u540e\uff0c\u9664\u5c06\u5434\u4e09\u6842\u5c01\u4e3a\u201c\u5e73\u897f\u738b\u201d\u5916\uff0c\u5e76\u5c01\u5c1a\u53ef\u559c\u4e3a\u201c\u5e73\u5357\u738b\u201d\uff0c\u9886\u6709\u5e7f\u4e1c\uff0c\u803f\u4ef2\u660e\u4e3a\u201c\u9756\u5357 \u738b\u201d\uff0c\u9886\u6709\u798f\u5efa\uff0c\u79f0\u4e3a\u201c\u4e09\u85e9\u201d\u3002\u5230\u5eb7\u7199\u5373\u4f4d\u4e4b\u540e\uff0c\u4e2d\u539f\u5927\u5b9a\uff0c\u6ee1\u6e05\u7684\u7edf\u6cbb\uff0c\u5df2\u7ecf\u5de9\u56fa\u3002\u5eb7 \u7199\u662f\u4e2a\u96c4\u624d\u5927\u7565\u7684\u541b\u738b\uff0c\u5982\u4f55\u5bb9\u5f97\u201c\u4e09\u85e9\u201d\u62e5\u5175\u81ea\u56fa\uff0c\u88c2\u5730\u4e3a\u738b\uff1f\u56e0\u6b64\u6697\u4e2d\u53eb\u4eba\u793a\u610f\u201c\u4e09 \u85e9\u201d\uff0c\u81ea\u8bf7\u9053\u4f11\uff0c\u5434\u4e09\u6842\u3001\u803f\u7cbe\u5fe0\uff08\u803f\u4ef2\u660e\u4e4b\u5b59\uff0c\u5f53\u65f6\u7ee7\u627f\u2018\u9756\u5357\u738b\u2019\u4f4d\uff09\u4e0d\u7406\u4e0d\u776c\uff0c\u8fd8\u4e0d \u76f8\u4fe1\u8fd9\u662f\u201c\u671d\u5ef7\u201d\u7684\u610f\u601d\u3002\u5c1a\u53ef\u559c\u5374\u6bd4\u8f83\u5978\u6ed1\uff0c\u5728\u5eb7\u7199\u5341\u5e74\uff0c\u594f\u8bf7\u5c06\u201c\u85e9\u738b\u201d\u4e4b\u4f4d\u8ba9\u7ed9\u513f\u5b50 \u5c1a\u4e4b\u4fe1\u3002\u4e0d\u6599\u594f\u6298\u4e0a\u540e\uff0c\u5eb7\u7199\u201c\u5fa1\u6279\u201d\u4e0b\u6765\uff0c\u4e0d\u7279\u201c\u51c6\u4e88\u6240\u8bf7\u201d\uff0c\u800c\u4e14\u53eb\u5c1a\u53ef\u559c\u7387\u9886\u85e9\u5c5e\u90e8 \u5c06\u5230\u8fbd\u4e1c\u53bb\u201c\u517b\u8001\u201d\u3002\u8fd9\u4e2a\u5fa1\u6279\u4e0b\u6765\uff0c\u5434\u4e09\u6842\u5927\u611f\u4e0d\u5b89\uff0c\u6df1\u6015\u201c\u524a\u85e9\u201d\u6210\u4e3a\u4e8b\u5b9e\uff0c\u4e8e\u662f\u9042\u8d77 \u4e86\u53cd\u53db\u6e05\u5ef7\u4e4b\u5fc3\u3002 \u3000\u3000\u5f53\u65f6\u8499\u85cf\u4e00\u5e26\uff0c\u6e05\u5ef7\u5c1a\u6240\u4e0d\u53ca\uff0c\u5434\u4e09\u6842\u9042\u6d3e\u9063\u5fc3\u8179\u695a\u662d\u5357\u6df1\u5165\u897f\u85cf\uff0c\u8c12\u89c1\u6d3b\u4f5b\uff0c\u548c\u4ed6\u76f8 \u7ea6\uff0c\u82e5\u4e3e\u4e8b\u540e\u5434\u4e09\u6842\u5360\u4e0a\u98ce\u65f6\uff0c\u5219\u8499\u85cf\u4e5f\u4e00\u540c\u53d1\u96be\uff1b\u82e5\u5434\u4e09\u6842\u5360\u4e0b\u98ce\u65f6\uff0c\u5219\u8bf7\u8fbe\u8d56\u6d3b\u4f5b\u51fa\u6765 \u201c\u8c03\u505c\u201d\u3002\u8fd9\u4e5f\u662f\u5434\u4e09\u6842\u9884\u7559\u201c\u9000\u6b65\u201d\u7684\u4e00\u6761\u8ba1\u7b56\u3002\u4ed6\u672c\u6765\u4e3a\u7684\u5c31\u4e0d\u662f\u8981\u5149\u590d\u6c49\u65cf\u6cb3\u5c71\uff0c\u800c \u662f\u8981\u4fdd\u5168\u81ea\u5df1\u7684\u5229\u7984\uff0c\u9664\u4e86\u548c\u8fbe\u8d56\u6d3b\u4f5b\u8054\u7edc\u5916\uff0c\u5434\u4e09\u6302\u5e76\u53e6\u6d3e\u6709\u4eba\u548c\u5c1a\u53ef\u559c\u3001\u803f\u7cbe\u5fe0\u8054\u7edc\u3002 \u3000\u3000\u695a\u662d\u5357\u8c12\u89c1\u8fbe\u8d56\u6d3b\u4f5b\u540e\uff0c\u8c08\u5f97\u5f88\u662f\u987a\u5229\u3002\u8fbe\u8d56\u6d3e\u7ea2\u8863\u5587\u561b\u548c\u4ed6\u56de\u6ec7\u590d\u547d\u3002\u9053\u7ecf\u5c71\u897f\uff0c\u987a \u4fbf\u5c31\u4e86\u4e94\u53f0\u5c71\u89c2\u5149\u6587\u6b8a\u83e9\u8428\u7684\u5f00\u5149\u5178\u793c\uff0c\u4e0d\u6599\u695a\u662d\u5357\u6b64\u4eba\uff0c\u4e5f\u662f\u5229\u7984\u718f\u5fc3\u4e4b\u8f88\u3002\u4ed6\u9ed8\u5bdf\u60c5 \u52bf\uff0c\u77e5\u9053\u5434\u4e09\u6842\u4e3e\u4e8b\uff0c\u5b9a\u7136\u5931\u8d25\uff0c\u9042\u8d77\u4e86\u53db\u5434\u6295\u6e05\u4e4b\u5fc3\u3002\u56e0\u6b64\u5728\u4e94\u53f0\u5c71\u4e0a\uff0c\u4ed6\u7adf\u4e0d\u60dc\u548c\u7fa4\u96c4 \u76f8\u6597\uff0c\u62d4\u5251\u6551\u4e86\u591a\u94ce\uff0c\u7ea2\u8863\u5587\u561b\u89c1\u4ed6\u7a81\u7136\u51fa\u624b\uff0c\u5df2\u77a7\u51fa\u4e86\u51e0\u5206\uff0c\u540e\u6765\u695a\u662d\u5357\u4e0e\u5085\u9752\u4e3b\u540c\u5815\u6df1 \u8c37\uff0c\u7ea2\u8863\u5587\u561b\u4e0b\u53bb\u627e\u5bfb\uff0c\u695a\u662d\u5357\u4e00\u89c1\u4ed6\u8a00\u8bed\u4e4b\u95f4\u8d77\u4e86\u731c\u7591\uff0c\u7acb\u523b\u53cd\u989c\u76f8\u5411\uff0c\u7ea2\u8863\u5587\u561b\u867d\u7ec3\u6709 \u94c1\u5e03\u886b\u7684\u529f\u592b\u5374\u6321\u4e0d\u4f4f\u695a\u662d\u5357\u7684\u5185\u529f\u7cbe\u6e5b\uff0c\u82e5\u975e\u521a\u597d\u78b0\u5230\u51cc\u672a\u98ce\uff0c\u4ed6\u51e0\u4e4e\u6b7b\u5728\u695a\u662d\u5357\u638c\u4e0b\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5c06\u6551\u7ea2\u8863\u5587\u561b\u7684\u7ecf\u8fc7\u6e90\u6e90\u672c\u672c\u8bf4\u51fa\uff0c\u4f17\u4eba\u90fd\u505a\u58f0\u4e0d\u5f97\u3002\u5085\u9752\u4e3b\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u6628\u665a \u5eb7\u7199\u548c\u695a\u662d\u5357\u8c08\u8d77\u5434\u4e09\u6842\uff0c\u60f3\u5fc5\u5c31\u662f\u4e3a\u6b64\u4e8b\u4e86\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6b63\u662f\u3002\u6211\u542c\u960e\u4e2d\u5929\u8bf4\uff0c\u5eb7\u7199 \u5df2\u51c6\u5907\u6d3e\u9063\u5fc3\u8179\uff0c\u8d76\u8d74\u5e7f\u4e1c\u548c\u798f\u5efa\u53bb\u76d1\u89c6\u5c1a\u53ef\u559c\u548c\u803f\u7cbe\u5fe0\uff0c\u53e6\u5916\u6d3e\u4eba\u53bb\u56db\u5ddd\uff0c\u53eb\u5ddd\u9655\u603b\u7763\u8d75 \u826f\u680b\u9632\u8303\u5434\u4e09\u6842\u3002\u201d \u3000\u3000\u5218\u90c1\u82b3\u6c89\u601d\u826f\u4e45\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u82e5\u7136\u5982\u6b64\uff0c\u6211\u4eec\u8be5\u6bd4\u5eb7\u7199\u6240\u6d3e\u7684\u5fc3\u8179\u5148\u5230\u4e00\u6b65\u3002\u201d\u6b63\u8bf4 \u8bdd\u95f4\uff0c\u5ffd\u542c\u5f97\u5e84\u5916\u4eba\u58f0\u55a7\u817e\uff0c\u6218\u9a6c\u5636\u9e23\u3002 \u3000\u3000\u5374\u8bf4\u591a\u94ce\u5728\u4e94\u53f0\u5c71\u88ab\u7fa4\u96c4\u6253\u5f97\u5927\u8d25\uff0c\u607c\u6012\u5f02\u5e38\uff0c\u5f53\u665a\u5085\u9752\u4e3b\u548c\u5192\u6d63\u83b2\u63a2\u5c71\uff0c\u53c8\u628a\u6e05\u51c9\u5bfa \u95f9\u5f97\u6cb8\u6cb8\u626c\u626c\u3002\u591a\u94ce\u5348\u591c\u95fb\u62a5\uff0c\u66f4\u662f\u6124\u6012\uff0c\u65e0\u5948\u8eab\u53d7\u91cd\u4f24\uff0c\u4e0d\u80fd\u8d77\u5e8a\uff0c\u53ea\u597d\u5524\u7eb3\u5170\u738b\u5983\u6765 \u95ee\uff0c\u4e0d\u6599\u7b49\u4e86\u8bb8\u4e45\uff0c\u738b\u5983\u624d\u6765\uff0c\u4e00\u6765\u5c31\u62a5\u8bf4\u8fde\u5f53\u65e5\u64d2\u4f4f\u7684\u5f20\u534e\u662d\u4e5f\u88ab\u4eba\u6551\u8d70\u4e86\u3002\u591a\u94ce\u5fc3\u4e2d\u5927 \u7591\uff0c\u5f20\u534e\u662d\u5173\u5728\u540e\u5802\uff0c\u88ab\u4eba\u6551\u8d70\uff0c\u4f55\u4ee5\u81ea\u5df1\u4e00\u70b9\u58f0\u606f\u90fd\u6ca1\u542c\u5230\uff0c\u7eb3\u5170\u738b\u5983\u9274\u8c8c\u8fa8\u8272\uff0c\u77e5\u9053\u4e08 \u592b\u8d77\u4e86\u731c\u7591\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u77a7\u4f60\uff0c\u4e00\u70b9\u70b9\u5c0f\u4e8b\u60c5\u90fd\u8981\u4eb2\u81ea\u64cd\u5fc3\uff0c\u4f60\u73b0\u5728\u5e94\u5f53\u9759\u5fc3\u517b\u75c5\u561b\uff01\u6765 \u4eba\u867d\u662f\u9ad8\u624b\uff0c\u4f46\u5bfa\u4e2d\u536b\u58eb\u5982\u4e91\uff0c\u4e5f\u4e0d\u6015\u4ed6\u4eec\u8d70\u5f97\u4e86\u3002\u4f60\u82e5\u4e3a\u523a\u5ba2\u9003\u6389\u800c\u8981\u8d23\u602a\u4e0b\u4eba\uff0c\u90a3\u5c31\u8d23 \u602a\u6211\u597d\u4e86\uff0c\u523a\u5ba2\u662f\u6211\u7763\u7387\u536b\u58eb\u770b\u7ba1\u7684\uff01\u201d\u591a\u94ce\u4e00\u89c1\u59bb\u5b50\u8f7b\u55a7\u6d45\u7b11\uff0c\u54ea\u91cc\u8fd8\u53d1\u4f5c\u5f97\u6765\u3002\u4ed6\u8fde\u770b \u7ba1\u5f20\u534e\u662d\u7684\u536b\u58eb\u4e5f\u4e0d\u5524\u6765\u95ee\u4e86\uff0c\u5176\u5b9e\u5c31\u662f\u4ed6\u5524\u6765\u95ee\u4e5f\u95ee\u4e0d\u51fa\uff0c\u9102\u738b\u5e9c\u7684\u536b\u58eb\uff0c\u60e7\u6015\u738b\u5983\u66f4\u80dc \u4e8e\u60e7\u6015\u738b\u7237\uff0c\u4eba\u662f\u738b\u5983\u653e\u7684\uff0c\u536b\u58eb\u600e\u6562\u6cc4\u9732\u3002 \u3000\u3000\u53ef\u662f\u591a\u94ce\u4e5f\u53e6\u6709\u6253\u7b97\uff0c\u7b2c\u4e8c\u65e5\u4e00\u65e9\u5c31\u628a\u7981\u536b\u519b\u526f\u7edf\u9886\u5f20\u627f\u658c\u5524\u6765\uff0c\u53eb\u4ed6\u5e26\u4e09\u5343\u7981\u536b\u519b\u5728 \u9644\u8fd1\u6751\u5e84\u5927\u7d22\u3002\u591a\u94ce\u4ee5\u4eb2\u738b\u8eab\u4efd\u8282\u5236\u7981\u536b\u519b\uff0c\u5f20\u627f\u658c\u81ea\u7136\u662f\u5589\u552f\u542c\u547d\u3002 \u3000\u3000\u6b66\u5bb6\u5e84\u662f\u5c71\u4e0b\u7684\u4e00\u4e2a\u5927\u6751\u5e84\uff0c\u6b66\u5e84\u4e3b\u53c8\u662f\u6c5f\u6e56\u4e0a\u95fb\u540d\u7684\u4eba\u7269\uff0c\u5f20\u627f\u658c\u4e5f\u662f\u51fa\u8eab\u6c5f\u6e56\uff0c\u4e0e \u6b66\u5e84\u4e3b\u66fe\u6709\u4e00\u9762\u4e4b\u4ea4\u3002\u5f20\u627f\u658c\u4e00\u4e0b\u5c71\u5c31\u5148\u5230\u4e86\u6b66\u5bb6\u5e84\uff0c\u90a3\u4e9b\u4e54\u88c5\u519c\u592b\u5728\u7530\u95f4\u64cd\u4f5c\u7684\u5e84\u4e01\uff0c\u795e \u8272\u53c8\u614c\u614c\u5f20\u5f20\uff0c\u88ab\u7981\u536b\u519b\u64d2\u4f4f\u76d8\u95ee\uff0c\u6709\u4eba\u71ac\u4e0d\u4f4f\u6253\uff0c\u4fbf\u4f9b\u51fa\u5e84\u5185\u6765\u4e86\u4e0d\u5c11\u5ba2\u4eba\u3002\u5f20\u627f\u658c\u5fc3\u4e2d \u5927\u559c\uff0c\u4e00\u58f0\u53f7\u4ee4\uff0c\u6570\u5343\u7981\u536b\u519b\u7acb\u523b\u6446\u5f00\u9635\u52bf\uff0c\u5c06\u6b66\u5bb6\u5e84\u56f4\u5f97\u5bc6\u4e0d\u901a\u98ce\u3002 \u3000\u3000\u5e84\u5185\u7fa4\u96c4\u95fb\u62a5\uff0c\u8df3\u4e86\u8d77\u6765\u3002\u901a\u660e\u548c\u5c1a\u62d4\u51fa\u6212\u5200\u9053\uff1a\u201c\u54b1\u4eec\u51b2\u51fa\u53bb\uff01\u201d\u6b66\u5143\u82f1\u62c8\u987b\u4e0d\u8bed\uff0c \u5218\u90c1\u82b3\u770b\u4e86\u901a\u660e\u548c\u5c1a\u4e00\u773c\u9053\uff1a\u201c\u5982\u4f55\u5e94\u4ed8\uff0c\u5f53\u8bf7\u6b66\u8001\u82f1\u96c4\u4f5c\u4e3b\u3002\u201d\u5979\u77e5\u4eca\u65e5\u4e4b\u4e8b\uff0c\u4e0d\u6bd4\u6628\u65e5 \u7684\u5927\u95f9\u4e94\u53f0\u5c71\uff0c\u4eca\u65e5\u88ab\u56f4\uff0c\u8fde\u6b66\u5bb6\u5e84\u7684\u5987\u5b7a\u8001\u5f31\u90fd\u7275\u7d2f\u5728\u5185\uff0c\u5982\u4f55\u80fd\u591f\u86ee\u5e72\uff1f\u6b66\u5143\u82f1\u9053\uff1a \u201c\u6211\u4e14\u5230\u56f4\u5899\u4e0a\u53bb\u770b\u770b\uff0c\u4e00\u4f17\u82f1\u96c4\u6682\u65f6\u53ef\u522b\u51fa\u5934\u3002\u201d \u3000\u3000\u6b66\u5143\u82f1\u767b\u4e0a\u56f4\u5899\uff0c\u53ea\u89c1\u5e84\u5916\u6208\u77db\u6620\u65e5\uff0c\u4e09\u5343\u7981\u536b\u519b\u539a\u7532\u88ab\u8eab\u3001\u5f3a\u5f13\u5728\u624b\uff0c\u4f5c\u52bf\u6b32\u5c04\uff0c\u5f20 \u627f\u658c\u4e00\u89c1\u6b66\u5143\u82f1\u51fa\u6765\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u6211\u4eec\u8fdc\u6765\uff1b\u6b66\u5e84\u4e3b\u4f60\u53ef\u8be5\u63a5\u5f85\u6211\u4eec\u8fdb\u53bb\uff1f\u201d\u6b66\u5143\u82f1 \u795e\u8272\u81ea\u5982\uff0c\u6717\u58f0\u7b54\u9053\uff1a\u201c\u5c71\u5e84\u7b80\u964b\uff0c\u96be\u8fce\u5927\u519b\u3002\u5b98\u957f\u9a7e\u5230\uff0c\u6211\u5c31\u8bf7\u51e0\u4f4d\u5b98\u957f\u8fdb\u53bb\u559d\u676f\u8336 \u5427\u3002\u201d\u5f20\u627f\u658c\u7d20\u6765\u6301\u91cd\uff0c\u89c1\u4ed6\u5982\u6b64\u795e\u60c5\uff0c\u5fc3\u4e2d\u72b9\u7591\u4e0d\u51b3\uff0c\u60f3\u9053\uff1a\u201c\u6b66\u5143\u82f1\u603b\u7b97\u662f\u4e2a\u7ec5\u58eb\uff0c\u53c8 \u662f\u6b66\u6797\u524d\u8f88\uff0c\u82e5\u641c\u4e0d\u51fa\uff0c\u81ea\u5df1\u4e5f\u53d7\u6c5f\u6e56\u4eba\u7269\u803b\u7b11\u3002\u201d\u4f46\u5176\u52bf\u53c8\u4e0d\u80fd\u7f62\u4f11\uff0c\u5fc3\u60f3\u8fdb\u53bb\u4e5f\u4e0d\u59a8 \u4e8b\uff0c\u4e8e\u662f\u9ad8\u58f0\u7b54\u9053\uff1a\u201c\u65e2\u7136\u4f60\u6015\u63a5\u5f85\u5927\u519b\uff0c\u6211\u5c31\u9063\u7259\u5c06\u5e26\u4e09\u767e\u540d\u519b\u58eb\u8fdb\u53bb\u597d\u4e86\uff0c\u6b66\u5e84\u4e3b\u662f\u6b66 \u6797\u524d\u8f88\uff0c\u8c05\u4e0d\u4f1a\u4f7f\u51fa\u8be1\u8ba1\u3002\u201d\u4ed6\u4ee4\u65d7\u4e00\u6446\uff0c\u961f\u4f0d\u5ffd\u7684\u88c2\u5f00\uff0c\u5f53\u4e2d\u63a8\u51fa\u5341\u5c0a\u571f\u70ae\u3002 \u3000\u3000\u6b66\u5143\u82f1\u539f\u60f3\u54c4\u5f20\u627f\u658c\u8fdb\u53bb\uff0c\u5c06\u4ed6\u64d2\u4f4f\uff0c\u4f5c\u4e3a\u8981\u631f\u3002\u89c1\u6b64\u60c5\u5f62\uff0c\u77e5\u4ed6\u6709\u6240\u51c6\u5907\uff0c\u4ed6\u53ea\u6d3e\u7259 \u5c06\u8fdb\u6765\uff0c\u5c31\u662f\u5c06\u7259\u5c06\u6349\u4f4f\uff0c\u4e5f\u65e0\u6d4e\u4e8e\u4e8b\uff0c\u800c\u4e14\u8ddf\u7740\u5fc5\u662f\u5c60\u6751\u4e4b\u7978\uff01 \u3000\u3000\u5916\u9762\u6b66\u5e84\u4e3b\u5341\u5206\u7d27\u5f20\uff0c\u5e84\u5185\u7fa4\u96c4\u4e5f\u5f88\u7740\u6025\u3002\u5218\u90c1\u82b3\u9053\uff1a\u201c\u4e8b\u5230\u4e34\u5934\uff0c\u770b\u6765\u662f\u975e\u62fc\u4e0d\u53ef \u4e86\uff01\u201d\u5979\u6bc5\u7136\u8d77\u7acb\uff0c\u6b63\u5f85\u90e8\u7f72\uff0c\u5374\u4e0d\u89c1\u4e86\u97e9\u5fd7\u90a6\u7684\u526f\u624b\u534e\u7d2b\u5c71\u548c\u6768\u4e00\u7ef4\u4e24\u4e2a\u4eba\uff0c\u5979\u7709\u5934\u4e00 \u76b1\uff0c\u95ee\u8d77\u97e9\u5fd7\u90a6\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u53bb\u4e86\u54ea\u91cc\u3002 \u3000\u3000\u518d\u8bf4\u960e\u4e2d\u5929\u5728\u9759\u5ba4\u4e4b\u5185\uff0c\u505a\u8d77\u5085\u9752\u4e3b\u6559\u7ed9\u4ed6\u7684\u201c\u6c14\u529f\u7597\u6cd5\u201d\uff0c\u6253\u5750\u4e0d\u4e45\uff0c\u679c\u89c9\u80f8\u4e2d\u8212\u7545 \u8bb8\u591a\u3002\u960e\u4e2d\u5929\u534a\u751f\u5f13\u9a6c\uff0c\u51fa\u751f\u5165\u6b7b\uff0c\u4e3a\u5229\u7984\u5954\u6ce2\uff0c\u4ece\u672a\u8bd5\u8fc7\u9759\u5750\u4e0b\u6765\uff0c\u597d\u597d\u601d\u60f3\u3002\u6b64\u523b\u9759\u5ba4 \u6253\u5750\uff0c\u8d77\u521d\u50cf\u662f\u8111\u5b50\u7a7a\u8361\u8361\u7684\uff0c\u4ec0\u4e48\u90fd\u6ca1\u6709\u3002\u731b\u7136\u95f4\uff0c\u601d\u6f6e\u7eb7\u8d77\uff0c\u60f3\u7740\u5e1d\u7687\u4eba\u5bb6\u7684\u5be1\u6069\uff0c\u6c5f \u6e56\u4fa0\u58eb\u7684\u4e49\u6c14\uff0c\u518d\u60f3\u60f3\u81ea\u5df1\u6240\u5e72\u8fc7\u7684\u4e8b\u60c5\uff0c\u4e0d\u89c9\u5929\u826f\u8ff8\u53d1\uff0c\u8d8a\u60f3\u8d8a\u89c9\u5f97\u60ed\u6127\uff0c\u81ea\u5df1\u8fd9\u4e00\u751f\u5c31 \u597d\u4f3c\u5e1d\u7687\u9e70\u72ac\uff0c\u4e13\u95e8\u66ff\u4e3b\u4eba\u6355\u6740\u5584\u826f\uff0c\u800c\u73b0\u5728\u522b\u4eba\u5374\u4e0d\u8f9e\u4e07\u6b7b\uff0c\u8981\u628a\u81ea\u5df1\u6551\u6d3b\u3002\u601d\u60f3\u50cf\u4e00\u4e2a \u6ce2\u6d6a\u63a5\u7740\u4e00\u4e2a\u6ce2\u6d6a\uff0c\u5085\u9752\u4e3b\u6559\u4ed6\u9759\u5750\uff0c\u4ed6\u7684\u5185\u5fc3\u5374\u597d\u50cf\u4e00\u4e2a\u6218\u573a\u3002 \u3000\u3000\u6b63\u5f53\u960e\u4e2d\u5929\u9759\u601d\u51a5\u60f3\u4e4b\u9645\uff0c\u9694\u58c1\u5ffd\u7136\u4f20\u6765\u8e3d\u8e3d\u4eba\u8bed\uff0c\u8bdd\u58f0\u867d\u7136\u5f88\u4f4e\uff0c\u5728\u9759\u5ba4\u4e2d\u5374\u542c\u5f97\u975e \u5e38\u6e05\u695a\u3002\u9694\u5ba4\u6709\u4e24\u4e2a\u4eba\u5728\u5bf9\u8bdd\uff0c\u4e00\u4e2a\u8bf4\uff1a\u201c\u5916\u9762\u7684\u7981\u536b\u519b\u5df2\u628a\u5e84\u5b50\u56f4\u5f97\u5bc6\u4e0d\u901a\u98ce\uff0c\u6768\u5927\u54e5\uff0c \u4f60\u600e\u6837\u6253\u7b97\uff1f\u201d\u53e6\u4e00\u4e2a\u4eba\u7b54\u9053\uff1a\u201c\u6211\u4eec\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u8fd8\u4e0d\u662f\u5750\u7740\u7b49\u6b7b\uff01\u534e\u5927\u54e5\uff0c\u6b7b\u5c31\u6b7b\u5427\u3002 \u53ef\u662f\uff0c\u6211\u5374\u8981\u602a\u4f60\uff0c\u600e\u60f3\u7684\u51c0\u662f\u81ea\u5df1\u7684\u4e8b\u60c5\u3002\u6211\u5fe7\u7684\u662f\u6b66\u5bb6\u5e84\u4e00\u5343\u6570\u767e\u8001\u5e7c\u7537\u5973\uff0c\u4eca\u5929\u6050\u6015 \u90fd\u9003\u4e0d\u4e86\u8fd9\u573a\u6d69\u52ab\uff01\u201d\u90a3\u4e2a\u88ab\u5524\u4f5c\u534e\u5927\u54e5\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u6b66\u5e84\u4e3b\u4e00\u4e16\u597d\u4eba\uff0c\u5374\u4e0d\u6599\u843d\u5f97 \u8fd9\u6837\u7ed3\u679c\uff01\u201d \u3000\u3000\u960e\u4e2d\u5929\u4e00\u5b57\u4e00\u53e5\uff0c\u542c\u5f97\u5206\u660e\uff0c\u5c24\u5176\u5728\u542c\u5230\uff1a\u201c\u4e0d\u8981\u51c0\u60f3\u81ea\u5df1\u7684\u4e8b\u60c5\u3002\u201d\u8fd9\u53e5\u8bdd\u65f6\uff0c\u731b\u7136 \u95f4\u5c31\u5982\u4e07\u7bad\u7a7f\u5fc3\uff0c\u5341\u5206\u96be\u8fc7\u3002\u4ed6\u731b\u7684\u54ac\u7740\u7259\u6839\u7ad9\u4e86\u8d77\u6765\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u5085\u9752\u4e3b\u53eb\u4ed6\u4e00\u5b9a\u8981\u9759\u5750 \u4e00\u5929\u4e00\u591c\u7684\u5429\u5490\uff0c\u4ed6\u65cb\u98ce\u4f3c\u7684\u6253\u5f00\u623f\u95e8\uff0c\u8ff3\u81ea\u671d\u5e84\u5916\u8d70\u53bb\uff0c\u8fd9\u65f6\u5e84\u53ee\u5475\u51fa\u51fa\u8fdb\u8fdb\uff0c\u5fd9\u4e71\u4e2d\u8c01 \u4e5f\u6ca1\u6709\u6ce8\u610f\u4ed6\u3002 \u3000\u3000\u5e84\u5916\uff0c\u8fd9\u65f6\u6b66\u5143\u82f1\u6b63\u611f\u4e3a\u96be\uff0c\u4ed6\u65e0\u6cd5\u62d2\u7edd\u5f20\u627f\u658c\u7684\u7259\u5c06\u8fdb\u6765\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u53ea\u597d\u786c\u7740\u5934\u76ae \u6253\u5f00\u5e84\u95e8\u518d\u7b97\u3002 \u3000\u3000\u90a3\u7259\u5c06\u5f97\u610f\u6d0b\u6d0b\uff0c\u9ad8\u89c6\u9614\u6b65\uff0c\u5e26\u4e09\u767e\u7981\u536b\u519b\u4e00\u51b2\u800c\u5165\uff0c\u4e0d\u6599\u521a\u4eba\u4e86\u5e84\u95e8\uff0c\u5ffd\u542c\u5f97\u6709\u4e00\u4e2a \u6d2a\u4eae\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8fdb\u6765\u4f5c\u4ec0\u4e48\uff1f\u5f20\u627f\u658c\u6765\u4e86\u5417\uff1f\u53eb\u4ed6\u89c1\u6211\uff01\u201d\u90a3\u7259\u5c06\u62ac\u5934\u4e00\u770b\uff0c\u6765\u4eba \u6b63\u662f\u7ba1\u8f96\u5bab\u4e2d\u536b\u58eb\u3001\u7687\u5e1d\u6700\u5ba0\u4fe1\u7684\u960e\u4e2d\u5929\uff0c\u4ed6\u8fd9\u4e00\u5413\u975e\u540c\u5c0f\u53ef\uff0c\u6025\u5fd9\u7b54\u9053\uff1a\u201c\u5c0f\u7684\u4e0d\u77e5\u4f60\u8001 \u5728\u8fd9\u91cc\uff0c\u5f20\u627f\u658c\u5c31\u5728\u5916\u9762\u3002\u201d\u960e\u4e2d\u5929\u9053\uff1a\u201c\u4f60\u4eec\u6eda\u51fa\u53bb\uff0c\u53eb\u4ed6\u8fdb\u6765\uff01\u201d\u7259\u5c06\u552f\u552f\u9886\u547d\u3002 \u3000\u3000\u5f20\u627f\u658c\u89c1\u7259\u5c06\u8fdb\u800c\u590d\u51fa\uff0c\u5341\u5206\u60ca\u8bb6\uff0c\u4ed6\u7b56\u9a6c\u4e0a\u524d\uff0c\u5ffd\u89c1\u5899\u5934\u4e0a\u51fa\u73b0\u4e00\u4eba\u5fae\u7b11\u9053\uff1a\u201c\u5f20\u627f \u658c\uff0c\u7687\u4e0a\u6628\u591c\u53eb\u6211\u5429\u5490\u4f60\u7684\u4e8b\u60c5\uff0c\u4f60\u529e\u5f97\u600e\u6837\u4e86\uff1f\u4f60\u8fd8\u672a\u5411\u6211\u590d\u547d\u5462\uff01\u201d \u3000\u3000\u5f20\u627f\u658c\u89c1\u4e86\u960e\u4e2d\u5929\uff0c\u4e5f\u662f\u5341\u5206\u60ca\u8bb6\uff0c\u89c1\u4ed6\u95ee\u8d77\uff0c\u53ea\u5f97\u606d\u987a\u5730\u7b54\u9053\uff1a\u201c\u5351\u804c\u6628\u591c\u641c\u67e5\u9003 \u72af\uff0c\u6ca1\u6709\u641c\u7740\uff0c\u60f3\u8c12\u89c1\u7687\u4e0a\u3002\u7687\u4e0a\u53c8\u6ca1\u6709\u529f\u592b\uff0c\u4eca\u5929\u4e00\u5927\u8bf7\u65e9\uff0c\u9102\u4eb2\u738b\u5c31\u5dee\u9063\u6211\u6765\u4e86\u3002\u201d\u960e \u4e2d\u5929\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u7687\u4e0a\u73b0\u5728\u6b63\u5728\u627e\u4f60\u5462\uff01\u6211\u5728\u8fd9\u91cc\u62dc\u4f1a\u670b\u53cb\uff0c\u4f60\u4e0d\u5fc5\u8fdb\u6765\u4e86\uff0c\u8fd8\u662f\u8d76\u5feb\u56de \u53bb\u5427\uff01\u201d\u5728\u5bab\u5ef7\u4e2d\uff0c\u960e\u4e2d\u5929\u65e0\u5f02\u5f20\u627f\u658c\u7684\u9876\u4e49\u4e0a\u53f8\uff0c\u6240\u4f20\u8fbe\u7684\u53c8\u662f\u7687\u547d\uff0c\u4e00\u6bd4\u8d77\u6765\uff0c\u5f20\u627f\u658c \u53ea\u597d\u628a\u9102\u4eb2\u738b\u7684\u547d\u4ee4\u653e\u5728\u540e\u5934\uff0c\u5782\u624b\u201c\u55b3\u201d\u7684\u5e94\u4e86\u4e00\u58f0\uff0c\u62d4\u8d77\u5927\u519b\uff0c\u4fbf\u5411\u540e\u9000\uff01 \u3000\u3000\u960e\u4e2d\u5929\u5140\u7acb\u5899\u5934\uff0c\u770b\u7740\u7981\u536b\u519b\u9000\u5f97\u5e72\u5e72\u51c0\u51c0\u4e4b\u540e\uff0c\u8fd9\u624d\u7f13\u7f13\u8d70\u4e0b\u56f4\u5899\u3002\u5085\u9752\u4e3b\u8fce\u9762\u8d70 \u6765\uff0c\u671d\u5730\u9762\u4e0a\u4e00\u77a7\uff0c\u6025\u6025\u5c06\u4ed6\u6276\u4f4f\u3002\u960e\u4e2d\u5929\u9762\u8272\u60e8\u767d\u5982\u7eb8\uff0c\u6447\u6447\u6643\u6643\uff0c\u8bf4\u9053\uff1a\u201c\u8c22\u8c22\u4f60\uff0c\u6211 \u4e0d\u884c\u4e86\uff01\u201d\u4ed6\u8fd9\u65f6\u53ea\u89c9\u4f53\u5185\u6709\u5343\u4e07\u6761\u5c0f\u86c7\uff0c\u5230\u5904\u4e71\u54ac\uff0c\u521a\u624d\u4ed6\u7528\u5c3d\u7cbe\u795e\uff0c\u62fc\u547d\u633a\u7740\uff0c\u73b0\u5728\u662f \u518d\u4e5f\u652f\u6491\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u6b66\u5143\u82f1\u89c1\u72b6\u5927\u60ca\uff0c\u8d70\u8fc7\u6765\u62c9\u7740\u960e\u4e2d\u5929\u7684\u624b\uff0c\u542b\u7740\u773c\u6cea\u8bf4\u9053\uff1a\u201c\u960e\u5927\u54e5\uff0c\u6211\u4eec\u90fd\u5f88\u611f\u6fc0 \u4f60\uff01\u201d\u960e\u4e2d\u5929\u9762\u4e0a\u9732\u51fa\u4e00\u4e1d\u5fae\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4e00\u751f\u4e2d\u6240\u505a\u7684\u552f\u4e00\u597d\u4e8b\uff0c\u505a\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u6211 \u6b7b\u4e5f\u6b7b\u5f97\u7791\u76ee\u4e86\uff01\u201d\u8bf4\u7f62\uff0c\u53cc\u76ee\u4e00\u95ea\uff0c\u5085\u9752\u4e3b\u634f\u7740\u4ed6\u7684\u624b\uff0c\u53ea\u89c9\u8109\u606f\u5df2\u65ad\uff0c\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9ed8 \u9ed8\u65e0\u8a00\u5730\u628a\u4ed6\u7684\u5c38\u4f53\u62b1\u4e86\u8d77\u6765\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u8fd8\u4e0d\u77e5\u960e\u4e2d\u5929\u5df2\u7ecf\u65ad\u6c14\uff0c\u8d70\u8fc7\u6765\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u5f97\u6551\u4e48\uff1f\u201d\u5085\u9752\u4e3b\u60e8\u7136\u7b54\u9053\uff1a\u201c\u7eb5 \u6709\u56de\u5929\u4e4b\u672f\uff0c\u4e5f\u6551\u4e0d\u4e86\uff01\u4ed6\u5403\u4e86\u6700\u5389\u5bb3\u7684\u6bd2\u836f\uff0c\u5f53\u665a\u53c8\u5954\u8dd1\u534a\u591c\uff0c\u867d\u6709\u5929\u5c71\u96ea\u83b2\u4fdd\u7740\uff0c\u6bd2\u6c14 \u5df2\u6563\u5e03\u4f53\u5185\uff0c\u6211\u6559\u4ed6\u7684\u6c14\u529f\u7597\u6cd5\u533b\u6cbb\uff0c\u6700\u5c11\u8981\u9759\u5750\u4e00\u5929\u4e00\u591c\uff0c\u4ed6\u8fd9\u4e00\u95f9\uff0c\u7cbe\u795e\u6c14\u529b\u5df1\u5168\u8017\u5c3d \u4e86\uff01\u201d\u97e9\u5fd7\u90a6\u76b1\u7740\u7709\u5934\u9053\uff1a\u201c\u662f\u8c01\u8bf4\u7ed9\u4ed6\u77e5\u9053\u7684\uff1f\u201d\u6768\u4e00\u7ef4\u548c\u534e\u7d2b\u5c71\u5f7c\u6b64\u5bf9\u77a7\uff0c\u4e0d\u6562\u4f5c\u58f0\u3002 \u4ed6\u4eec\u628a\u960e\u4e2d\u5929\u6fc0\u4e86\u51fa\u6765\uff0c\u5374\u6ca1\u6599\u5230\u6bd2\u836f\u8fd9\u6837\u5389\u5bb3\u3002 \u3000\u3000\u5218\u90c1\u82b3\u77a7\u5728\u773c\u5185\uff0c\u5374\u4e0d\u8a00\u8bed\u3002\u5979\u60f3\uff1a\u201c\u8fd9\u4e24\u4eba\u5fc3\u5730\u867d\u6b20\u7eaf\u539a\uff0c\u4f46\u5230\u5e95\u662f\u4e3a\u4e86\u6551\u51fa\u5927 \u5bb6\u3002\u201d\u56e0\u6b64\u4e0d\u613f\u70b9\u7834\uff0c\u7d2f\u4ed6\u4eec\u53d7\u8d23\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u960e\u4e2d\u5929\u8fd9\u6837\u7684\u6b7b\uff0c\u4e5f\u7b97\u503c\u5f97\u4e86\u3002\u53ea\u662f\u7981\u536b \u519b\u867d\u7ed9\u4ed6\u559d\u9000\uff0c\u4e5f\u53ea\u662f\u6682\u65f6\u7f13\u5175\u4e4b\u8ba1\uff0c\u5f85\u4ed6\u4eec\u5f04\u6e05\u695a\u540e\uff0c\u4e00\u5b9a\u66f4\u5927\u4e3e\u800c\u6765\uff0c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u6211\u4eec \u4e5f\u8be5\u65e9\u4f5c\u6253\u7b97\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u4f17\u4eba\u5546\u8bae\u4e86\u4e00\u4f1a\uff0c\u51b3\u5b9a\u5f03\u5e84\u8fdc\u8d70\uff0c\u6b66\u5bb6\u7236\u5973\u548c\u4e00\u4f17\u5e84\u4e01\uff0c\u968f\u534e\u7d2b\u5c71\u3001\u6768\u4e00\u7ef4\u4e8c\u4eba\u7559 \u5728\u5c71\u897f\uff0c\u4e3b\u6301\u897f\u5317\u7684\u5929\u5730\u4f1a\uff1b\u5218\u90c1\u82b3\u548c\u97e9\u5fd7\u90a6\u5165\u4e91\u5357\uff0c\u770b\u5434\u4e09\u6842\u7684\u60c5\u5f62\uff0c\u4ed6\u4eec\u660e\u77e5\u5434\u4e09\u6842\u53ea \u662f\u4e3a\u4e86\u4e2a\u4eba\u5229\u7984\uff0c\u4f46\u5374\u60f3\u5229\u7528\u4ed6\u548c\u6e05\u5ef7\u7684\u51b2\u7a81\uff0c\u56fe\u8c0b\u590d\u56fd\uff1b\u5085\u9752\u4e3b\u548c\u5192\u6d63\u83b2\u5165\u5ddd\uff0c\u53bb\u770b\u56db\u5ddd \u7684\u5f62\u52bf\uff1b\u901a\u660e\u548c\u5c1a\u548c\u5e38\u82f1\u3001\u7a0b\u901a\u8d74\u7ca4\uff0c\u53bb\u622a\u6e05\u5ef7\u7684\u4eba\uff0c\u81f3\u4e8e\u6613\u5170\u73e0\uff0c\u5219\u81ea\u613f\u5b64\u8eab\u8fdb\u6740\uff0c\u8bbe\u6cd5 \u6551\u5f20\u516c\u5b50\uff0c\u4f17\u4eba\u89c9\u5f97\u5371\u9669\uff0c\u6b63\u5f85\u62e6\u963b\uff0c\u5085\u9752\u4e3b\u770b\u4e86\u5979\u4e00\u773c\uff0c\u60f3\u8d77\u6628\u591c\u8bb8\u591a\u79bb\u5947\u4e4b\u4e8b\uff0c\u8bf4\u9053\uff1a \u201c\u8ba9\u5979\u53bb\u5427\uff0c\u5979\u53bb\u6700\u4e3a\u5408\u9002\uff01\u201d\u8fd9\u4e00\u53bb\uff0c\u6709\u5206\u6559\uff1a\u82f1\u96c4\u56db\u6563\u56fe\u8c6a\u4e3e\uff0c\u6c5f\u6e56\u5904\u5904\u8d77\u98ce\u6ce2\u3002\u6b32\u77e5 \u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u56db\u7ae0\u3000\u6bd4\u5251\u538b\u51f6\u4eba\u3000\u540c\u95e8\u51b3\u6218\u3000\u5c55\u56fe\u5bfb\u7f09\u68a6\u3000\u65e7\u4fa3\u91cd\u6765 \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u56db\u56de \u6bd4\u5251\u538b\u51f6\u4eba\u3000\u540c\u95e8\u51b3\u6218 \u5c55\u56fe\u5bfb\u7f09\u68a6\u3000\u65e7\u4fa3\u91cd\u6765 \u3000\u3000\u5728\u5c71\u897f\u5927\u540c\u9644\u8fd1\uff0c\u6851\u5e72\u6cb3\u7d22\u56de\u5982\u5e26\uff0c\u6ed4\u6ed4\u9ec4\u6c34\u4e0d\u7edd\u4e1c\u6d41\uff0c\u6cb3\u7684\u4e24\u5cb8\u5c71\u5ce6\u8d77\u4f0f\uff0c\u66f4\u96c4\u5947\u7684 \u662f\uff0c\u4e34\u6cb3\u662f\u4e00\u7247\u9661\u5c96\u7edd\u58c1\uff0c\u800c\u7edd\u58c1\u4e0a\u5374\u5e03\u6ee1\u4e86\u6d1e\u7a9f\uff0c\u8fd9\u4e9b\u6d1e\u7a9f\u90fd\u662f\u53e4\u4ee3\u4f5b\u6559\u5f92\u6240\u5f00\u8f9f\u7684\u3002\u5927 \u540c\u9644\u8fd1\u7684\u8fd9\u4e9b\u6d1e\u7a9f\uff0c\u6709\u4e00\u4e2a\u603b\u540d\u53eb\u505a\u201c\u4e91\u5c97\u77f3\u7a9f\u201d\uff0c\u5927\u5927\u5c0f\u5c0f\uff0c\u6570\u8fbe\u767e\u4f59\uff0c\u91cc\u9762\u7684\u4f5b\u50cf\u96d5 \u523b\uff0c\u4e16\u754c\u95fb\u540d\u3002 \u3000\u3000\u8fd9\u4e00\u5929\u6b63\u662f\u66ae\u6625\u65f6\u8282\uff0c\u5929\u6c14\u6674\u660e\uff0c\u5728\u5c71\u5ce6\u6b65\uff0c\u6709\u4e24\u7537\u4e00\u5973\uff0c\u9ed8\u9ed8\u524d\u884c\uff0c\u4e24\u4e2a\u7537\u7684\u662f\u201c\u5929 \u5c71\u795e\u8292\u201d\u51cc\u672a\u98ce\u548c\u5929\u5730\u4f1a\u526f\u8235\u4e3b\u97e9\u5fd7\u90a6\uff0c\u5973\u7684\u662f\u5929\u5730\u4f1a\u7684\u603b\u8235\u4e3b\u5218\u90c1\u82b3\uff01 \u3000\u3000\u4ed6\u4eec\u81ea\u4e94\u53f0\u5c71\u4e0b\u4e0e\u7fa4\u96c4\u5206\u624b\u4ee5\u540e\uff0c\u7ed5\u9053\u897f\u884c\u5165\u6ec7\uff0c\u8d70\u4e86\u4e09\u5929\uff0c\u5230\u4e86\u4e91\u5c97\uff0c\u5cfb\u5cad\u8352\u5c71\uff0c\u8fde \u5c45\u6c11\u90fd\u627e\u4e0d\u5230\uff0c\u66f4\u4e0d\u8981\u8bf4\u65c5\u820d\u4e86\u3002\u5218\u90c1\u82b3\u7b11\u9053\uff1a\u201c\u770b\u6765\u4eca\u665a\u6211\u4eec\u53ea\u597d\u4f4f\u77f3\u7a9f\u4e86\uff01\u201d\u51cc\u672a\u98ce \u9053\uff1a\u201c\u4f60\u4e0d\u662f\u6700\u559c\u6b22\u4f4f\u5f00\u6717\u7684\u5730\u65b9\u5417\uff1f\u77f3\u7a9f\u600e\u4f4f\u5f97\u60ef\uff1f\u201d\u5218\u90c1\u82b3\u8be7\u7136\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211 \u7684\u4e60\u60ef\uff1f\u201d\u539f\u6765\u5218\u90c1\u82b3\u5c0f\u65f6\uff0c\u4f4f\u5728\u676d\u5dde\uff0c\u6240\u4f4f\u7684\u5730\u65b9\uff0c\u90fd\u662f\u7a97\u660e\u51e0\u51c0\u3002\u522b\u7684\u5973\u5b69\u513f\u5bb6\uff0c\u90fd\u4e0d \u5927\u6562\u6253\u5f00\u7a97\u5b50\uff0c\u800c\u5979\u7684\u623f\u5b50\uff0c\u7a97\u5e18\u5374\u603b\u662f\u5377\u8d77\u7684\u3002\u56e0\u4e3a\u5979\u559c\u7231\u9633\u5149\uff0c\u618e\u6076\u9634\u6697\u3002 \u3000\u3000\u51cc\u672a\u98ce\u89c1\u5979\u53cd\u95ee\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u6211\u662f\u8fd9\u6837\u731c\u7f62\u4e86\uff0c\u5c0f\u59d0\u4eec\u603b\u662f\u559c\u6b22\u6d01\u51c0\u7684\u3002\u201d\u5218\u90c1\u82b3 \u9053\uff1a\u201c\u6211\u5c0f\u65f6\u5019\u662f\u8fd9\u6837\uff0c\u73b0\u5728\u6d6a\u8ff9\u6c5f\u6e56\uff0c\u4ec0\u4e48\u5730\u65b9\u90fd\u4f4f\u5f97\u60ef\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u6b3e\u6b3e\u800c\u8c08\uff0c\u97e9\u5fd7\u90a6\u77a7\u5728\u773c\u5185\uff0c\u5fc3\u91cc\u4e0d\u89c9\u6cdb\u8d77\u4e00\u79cd\u5f02\u6837\u7684\u611f\u60c5\uff0c\u4ed6\u6709\u5fc3\u4e8e\u5218\u90c1\u82b3\u5df1\u6709 \u5341\u5e74\u4e86\uff0c\u53ef\u662f\u5979\u5374\u6beb\u65e0\u77e5\u89c9\u4f3c\u7684\uff0c\u800c\u5bf9\u4e8e\u51cc\u672a\u98ce\uff0c\u5374\u4f3c\u4e00\u89c1\u5982\u6545\u3002\u867d\u7136\u51cc\u672a\u98ce\u5bf9\u5979\u597d\u50cf\u51b7\u70ed \u5f02\u5e38\uff0c\u800c\u4e14\u6709\u65f6\u8fd8\u6545\u610f\u548c\u5979\u9876\u649e\uff0c\u4f46\u5979\u4e5f\u4e0d\u4ee5\u4e3a\u610f\u3002 \u3000\u3000\u5218\u90c1\u82b3\u4e5f\u770b\u51fa\u4e86\u97e9\u5fd7\u90a6\u7684\u795e\u60c5\uff0c\u7b11\u9053\uff1a\u201c\u97e9\u5927\u54e5\uff0c\u600e\u4e48\u4f60\u51e0\u5929\u6765\u90fd\u5f88\u5c11\u8bf4\u8bdd\u5440\uff1f\u6211\u4eec\u8d76 \u5feb\u53bb\u627e\u4e00\u4e2a\u77f3\u7a9f\u5427\u3002\u201d\u97e9\u5fd7\u90a6\u5e94\u4e86\u4e00\u58f0\uff0c\u968f\u624b\u62fe\u8d77\u5c71\u65c1\u7684\u67af\u679d\uff0c\u7528\u706b\u77f3\u64e6\u71c3\u8d77\u6765\uff0c\u505a\u6210\u706b \u628a\uff0c\u6307\u7740\u7edd\u58c1\u4e0a\u7684\u4e00\u4e2a\u5927\u77f3\u7a9f\u9053\uff1a\u201c\u8fd9\u4e2a\u6700\u597d\uff01\u201d\u5218\u90c1\u82b3\u4e00\u770b\uff0c\u6d1e\u53e3\u51ff\u6709\u201c\u4f5b\u8f6c\u6d1e\u201d\u4e09\u4e2a\u5927 \u5b57\u3002\u97e9\u5fd7\u90a6\u9053\uff1a\u201c\u6211\u5728\u897f\u5317\u591a\u5e74\uff0c\u5e38\u5e38\u542c\u4f5b\u5f92\u8c08\u8d77\u8fd9\u4e2a\u77f3\u7a9f\uff0c\u8bf4\u662f\u91cc\u9762\u7684\u4f5b\u50cf\u96d5\u523b\uff0c\u9b3c\u65a7\u795e \u5de5\uff0c\u53ef\u60dc\u6211\u662f\u4e2a\u8001\u7c97\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u61c2\u3002\u201d \u3000\u3000\u4e09\u4eba\u8fb9\u8c08\u8fb9\u8fdb\u5165\u7a9f\u5185\uff0c\u8fd9\u77f3\u7a9f\u679c\u7136\u6781\u4e3a\u96c4\u4f1f\uff0c\u5f53\u4e2d\u7684\u5927\u5750\u4f5b\u9ad8\u8fbe\u4e09\u4e08\u6709\u591a\uff0c\u5b83\u7684\u4e00\u4e2a\u624b \u6307\u5934\u6bd4\u6210\u4eba\u7684\u8eab\u4f53\u8fd8\u957f\uff0c\u56db\u58c1\u66f4\u523b\u6ee1\u5947\u5947\u602a\u602a\u7684\u58c1\u753b\uff0c\u98ce\u683c\u4e0e\u4e2d\u571f\u5927\u4e0d\u76f8\u6837\u3002\u5218\u90c1\u82b3\u770b\u7740\u58c1 \u4e0a\u6240\u523b\u7684\u201c\u98de\u5929\u201d\uff08\u4ed9\u5973\uff09\uff0c\u8863\u5e26\u98d8\u4e3e\uff0c\u597d\u50cf\u7a7a\u9645\u56de\u7fd4\uff0c\u7834\u58c1\u6b32\u98de\uff0c\u4e0d\u7981\u5927\u4e3a\u8d5e\u8d4f\u3002\u51cc\u672a\u98ce \u4e5f\u5567\u5567\u79f0\u5947\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5728\u897f\u5317\u591a\u5e74\uff0c\u4e5f\u672a\u66fe\u89c1\u8fc7\u8fd9\u6837\u7f8e\u5999\u7684\u58c1\u753b\uff01\u201d \u3000\u3000\u5218\u90c1\u82b3\u82e5\u6709\u6240\u89e6\uff0c\u63a5\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u5230\u897f\u5317\u591a\u5c11\u5e74\u4e86\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u5341\u516d\u5e74\u4e86\uff01\u201d\u5218\u90c1 \u82b3\u9762\u8272\u500f\u53d8\uff0c\u5ffd\u7136\u5728\u884c\u56ca\u4e2d\u53d6\u51fa\u4e00\u5377\u56fe\u753b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e14\u770b\u770b\u8fd9\u4e00\u5e45\u5427\uff01\u201d\u4e00\u6253\u5f00\u6765\uff0c\u53ea\u89c1 \u91cc\u9762\u753b\u7684\u662f\u4e00\u4e2a\u4e30\u795e\u4fca\u79c0\u7684\u5c11\u5e74\u7537\u5b50\u3002 \u3000\u3000\u5728\u51cc\u672a\u98ce\u5c55\u5f00\u753b\u56fe\u65f6\uff0c\u5218\u90c1\u82b3\u53cc\u7738\u95ea\u95ea\u653e\u5149\uff0c\u7d27\u7d27\u5730\u76ef\u7740\u4ed6\uff0c\u51cc\u672a\u98ce\u5f3a\u529b\u6291\u5236\u7740\u5185\u5fc3\u7684 \u6fc0\u52a8\uff0c\u6de1\u6de1\u5730\u7b11\u9053\uff1a\u201c\u753b\u5f97\u771f\u4e0d\u9519\u5440\uff01\u8138\u4e0a\u7684\u7a1a\u6c14\u751f\u52a8\u5730\u8868\u73b0\u51fa\u6765\u4e86\uff01\u753b\u4e2d\u7684\u5c11\u5e74\uff0c\u6050\u6015\u53ea \u6709\u5341\u4e94\u516d\u5c81\u5427\uff1f\u201d\u5218\u90c1\u82b3\u6df1\u6c89\u5730\u671b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u8bc6\u753b\u4e2d\u7684\u4eba\u5417\uff1f\u201d\u51cc\u672a\u98ce\u4f5c\u51fa\u8be7\u5f02\u7684 \u6837\u5b50\u53cd\u95ee\u9053\uff1a\u201c\u6211\u600e\u4e48\u4f1a\u8ba4\u8bc6\u4ed6\uff1f\u201d \u3000\u3000\u97e9\u5fd7\u90a6\u770b\u7740\u5218\u90c1\u82b3\u7684\u795e\u60c5\uff0c\u89c9\u5f97\u975e\u5e38\u5947\u602a\uff0c\u4e5f\u51d1\u4e0a\u6765\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u4eba\uff1f\u5218\u5927\u59d0\u4e3a\u4ec0 \u4e48\u968f\u8eab\u5e26\u7740\u4ed6\u7684\u753b\u50cf\uff1f\u662f\u4f60\u5931\u6563\u4e86\u7684\u5144\u5f1f\u8fd8\u662f\u4eb2\u670b\uff1f\u201d \u3000\u3000\u5218\u90c1\u82b3\u832b\u7136\u8d77\u7acb\uff0c\u97e9\u5fd7\u90a6\u5728\u706b\u628a\u5149\u4e2d\uff0c\u770b\u89c1\u5979\u5fae\u5fae\u98a4\u6296\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u8fd9\u65f6\u5916 \u9762\u6851\u5e72\u6cb3\u591c\u6d9b\u62cd\u5cb8\uff0c\u901a\u8fc7\u5e7d\u6df1\u7684\u77f3\u7a9f\uff0c\u56db\u58c1\u8361\u8d77\u56de\u58f0\uff0c\u5c31\u50cf\u7a7a\u5c71\u4e2d\u54cd\u8d77\u5343\u767e\u9762\u6218\u9f13\u3002\u5218\u90c1\u82b3 \u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u542c\u8fd9\u6d9b\u58f0\u5012\u5f88\u50cf\u5728\u94b1\u5858\u6c5f\u6f6e\u5462\u3002\u201d\u5979\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9760\u7740\u77f3\u58c1\uff0c\u795e\u60c5\u5f88\u662f\u75b2\u5026\u3002 \u97e9\u5fd7\u90a6\u5fc3\u4e2d\u4e00\u9635\u75bc\u75db\uff0c\u8d70\u8fc7\u53bb\u60f3\u6276\u5979\uff0c\u5218\u90c1\u82b3\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u7528\u4f60\u6276\u3002\u97e9\u5927\u54e5\uff0c\u8fd9\u4e8b\u60c5\u6211\u65e9 \u8be5\u5bf9\u4f60\u8bf4\u4e86\u3002\u201d\u5979\u6307\u7740\u753b\u4e2d\u7684\u5c11\u7537\u8bf4\u9053\uff1a\u201c\u8fd9\u5e45\u753b\u662f\u6211\u753b\u7684\u3002\u753b\u4e2d\u7684\u5927\u5b69\u5b50\u662f\u6211\u7684\u7ae5\u5e74\u7684\u597d \u53cb\uff0c\u5728\u94b1\u5858\u6c5f\u5927\u6f6e\u4e4b\u591c\uff0c\u6211\u6253\u4e86\u4ed6\u4e00\u4e2a\u8033\u5149\uff0c\u4ed6\u8df3\u8fdb\u94b1\u5858\u6c5f\u6b7b\u4e86\uff01\u201d\u97e9\u5fd7\u90a6\u95ee\u9053\uff1a\u201c\u65e2\u7136\u662f \u597d\u53cb\uff0c\u4f60\u4e3a\u4ec0\u4e48\u53c8\u6253\u4ed6\u8033\u5149\uff1f\u201d \u3000\u3000\u5218\u90c1\u82b3\u9762\u8272\u60e8\u767d\uff0c\u54d1\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u9519\uff01\u90a3\u65f6\u6211\u4eec\u7684\u7236\u4eb2\u90fd\u662f\u9c81\u738b\u7684\u90e8\u4e0b\uff0c\u6b7b\u5728\u6218 \u573a\uff0c\u6211\u4eec\u548c\u9c81\u738b\u7684\u65e7\u90e8\uff0c\u9690\u5c45\u676d\u5dde\u3002\u6709\u4e00\u5929\uff0c\u6211\u4eec\u7684\u4eba\uff0c\u6709\u51e0\u4e2a\u88ab\u5f53\u65f6\u9547\u5b88\u676d\u5dde\u7684\u7eb3\u5170\u603b\u5175 \u6240\u6355\uff0c\u6211\u7684\u670b\u53cb\u4e5f\u5728\u5185\u3002\u540e\u6765\u542c\u8bf4\u4f9b\u51fa\u9c81\u738b\u5728\u676d\u5dde\u7684\u4eba\uff0c\u4ee5\u81f4\u51e0\u4e4e\u88ab\u4e00\u7f51\u626b\u5c3d\u3002\u201d\u97e9\u5fd7\u90a6\u63e1 \u7740\u62f3\u5934\uff0c\u55b7\u7684\u4e00\u58f0\u6253\u5728\u77f3\u58c1\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u4ed6\u662f\u8fd9\u6837\u7684\u4eba\uff0c\u4e0d\u8981\u8bf4\u6253\u4ed6\u8033\u5149\uff0c\u5c31\u662f\u6740\u4e86\u4e5f \u5e94\u8be5\uff01\u201d\u4ed6\u8bf4\u4e86\u4e4b\u540e\uff0c\u770b\u89c1\u5218\u90c1\u82b3\u53c8\u6447\u4e86\u6447\u5934\uff0c\u518d\u95ee\u9053\uff1a\u201c\u5230\u5e95\u662f\u4e0d\u662f\u771f\u7684\u4ed6\u8bf4\u4e86\uff1f\u201d\u5218\u90c1 \u82b3\u9053\uff1a\u201c\u90a3\u665a\u6211\u4eec\u7684\u4eba\u8d8a\u72f1\u6210\u529f\uff0c\u4ed6\u4e5f\u8dd1\u4e86\u51fa\u6765\uff0c\u6211\u78b0\u5230\u4ed6\uff0c\u95ee\u4ed6\u5230\u5e95\u8bf4\u4e86\u6ca1\u6709\uff1f\u4ed6\u8bf4\uff1a \u2018\u8fd9\u5b8c\u5168\u662f\u771f\u7684\uff01\u2019\u97e9\u5fd7\u90a6\u6012\u9053\uff1a\u201c\u5218\u5927\u59d0\uff0c\u4e8f\u6211\u4e00\u5411\u656c\u4f69\u4f60\uff0c\u8fd9\u6837\u7684\u4eba\uff0c\u4f60\u4e0d\u6740\u4ed6\u5df1\u662f\u5dee \u4e86\uff0c\u8fd8\u8981\u60f3\u5ff5\u4ed6\uff01\u201d \u3000\u3000\u5218\u90c1\u82b3\u77aa\u4e86\u4ed6\u4e00\u773c\u9053\uff1a\u201c\u4e8b\u60c5\u6709\u65f6\u5f88\u590d\u6742\uff0c\u5728\u6ca1\u6709\u5b8c\u5168\u6e05\u695a\u4e4b\u524d\uff0c\u968f\u4fbf\u4e0b\u5224\u8bed\uff0c\u53ef\u80fd\u5c31 \u94f8\u6210\u5927\u9519\u3002\u6211\u90a3\u4f4d\u670b\u53cb\uff0c\u4ece\u5c0f\u5c31\u662f\u975e\u5e38\u575a\u5f3a\u7684\u5c0f\u5b50\u3002\u53ef\u662f\u4ed6\u88ab\u6355\u65f6\u5230\u5e95\u53ea\u662f\u5341\u516d\u5c81\u7684\u5927\u5b69\u5b50 \u54ea\uff01\u201d\u97e9\u5fd7\u90a6\u9053\uff1a\u201c\u662f\u5b69\u5b50\u4e5f\u4e0d\u80fd\u539f\u8c05\uff01\u201d\u5218\u90c1\u82b3\u4e0d\u7406\u4ed6\u63d2\u5634\uff0c\u7ee7\u7eed\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u4ed6\u88ab\u6355 \u540e\uff0c\u53d7\u4e86\u5404\u79cd\u6bd2\u5211\uff0c\u4ed6\u4e00\u53e5\u8bdd\u4e5f\u6ca1\u6709\u8bf4\u3002\u540e\u6765\u654c\u4eba\u4f7f\u7528\u82e6\u8089\u8ba1\uff0c\u53eb\u4e00\u4e2a\u4eba\u4e54\u88c5\u6297\u6e05\u4e49\u58eb\uff0c\u548c \u4ed6\u540c\u5173\u5728\u4e00\u4e2a\u7262\u623f\uff0c\u63d0\u4ed6\u51fa\u53bb\u6253\u65f6\uff0c\u4e5f\u628a\u90a3\u4e2a\u4eba\u62d6\u53bb\u6253\uff0c\u800c\u4e14\u6bd4\u4ed6\u8fd8\u6253\u5f97\u5389\u5bb3\u3002\u4ed6\u5e74\u7eaa\u8f7b\u5c31 \u76f8\u4fe1\u90a3\u4eba\u662f\u81ea\u5df1\u4eba\u3002\u90a3\u4eba\u8bf4\u8981\u8d8a\u72f1\uff0c\u4f46\u6015\u51fa\u72f1\u540e\u65e0\u5904\u8eb2\u85cf\u3002\u4ed6\u5c31\u5c06\u6211\u4eec\u603b\u90e8\u7684\u5730\u5740\u8bf4\u7ed9\u90a3\u4eba \u77e5\u9053\u3002\u8fd9\u4ef6\u4e8b\u662f\u6211\u4eec\u7684\u4eba\u8d8a\u72f1\u540e\uff0c\u64d2\u7740\u72f1\u5352\uff0c\u8be6\u7ec6\u67e5\u95ee\u624d\u67e5\u51fa\u6765\u7684\uff01\u201d \u3000\u3000\u97e9\u5fd7\u90a6\u542c\u4e86\u8fd9\u8bdd\u65f6\u5446\u4f4f\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u5218\u5927\u59d0\uff0c\u6055\u6211\u65e0\u7406\uff0c\u6211\u60f3\u95ee\u4f60\u4e00\u53e5\u8bdd\u2026\u2026\u201d \u3000\u3000\u5218\u90c1\u82b3\u628a\u5934\u53d1\u5411\u540e\u63a0\u4e86\u4e00\u63a0\uff0c\u9762\u5bf9\u7740\u97e9\u5fd7\u90a6\uff0c\u7528\u4e00\u79cd\u6025\u4fc3\u7684\u58f0\u8c03\u6253\u65ad\u4ed6\u7684\u8bdd\u9053\uff1a\u201c\u6211\u77e5 \u9053\u4f60\u60f3\u95ee\u7684\u662f\u4ec0\u4e48\u4e86\u3002\u8fd9\u5341\u591a\u5e74\u6765\uff0c\u6211\u603b\u5e26\u7740\u4ed6\u7684\u753b\u50cf\uff0c\u7ed3\u5a5a\u7684\u4e8b\u60c5\uff0c\u6211\u8fde\u60f3\u4e5f\u6ca1\u6709\u60f3 \u8fc7\uff01\u201d\u97e9\u5fd7\u90a6\u9ed8\u7136\u4e0d\u8bed\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u624d\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u60f3\u6cd5\u771f\u53ef\u6015\uff01\u201d\u5218\u90c1\u82b3\u6447\u6447\u5934\u9053\uff1a \u201c\u5047\u5982\u4f60\u5f53\u65f6\u770b\u89c1\u4ed6\u7ed9\u6211\u6253\u7684\u90a3\u5f20\u8138\uff0c\u4f60\u5c31\u4e0d\u4f1a\u4ee5\u4e3a\u6211\u60f3\u5f97\u53ef\u6015\u4e86\uff01\u6211\u4e00\u95ed\u8d77\u773c\u775b\uff0c\u5c31\u4f1a\u770b \u89c1\u4ed6\u90a3\u53ef\u6016\u7684\uff0c\u7edd\u671b\u7684\uff0c\u5b69\u5b50\u6c14\u7684\u8138\uff01\u6211\u6740\u6b7b\u4e86\u6211\u6700\u597d\u7684\u670b\u53cb\uff0c\u6211\u505a\u9519\u7684\u4e8b\u60c5\u662f\u518d\u4e5f\u4e0d\u80fd\u633d \u56de\u4e86\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u626d\u7ede\u7740\u53cc\u624b\uff0c\u5e26\u7740\u5200\u75d5\u7684\u8138\uff0c\u51b7\u51b0\u51b0\u7684\u4e00\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\u3002\u5218\u90c1\u82b3\u77a5\u4e86\u4e00\u773c\uff0c\u84e6\u5730 \u671b\u60ca\u53eb\u8d77\u6765\u3002\u7528\u624b\u8499\u7740\u773c\u775b\uff0c\u558a\u9053\uff1a\u201c\u5440\uff01\u6211\u597d\u50cf\u53c8\u770b\u5230\u4ed6\u4e86\u2026\u2026\u201d\u97e9\u5fd7\u90a6\u8dd1\u8fc7\u53bb\uff0c\u7528\u624b\u8f7b \u8f7b\u6276\u7740\u5979\uff0c\u8bf4\u9053\uff1a\u201c\u603b\u8235\u4e3b\uff0c\u4f60\u60f3\u5f97\u592a\u591a\u4e86\uff0c\u8fd9\u53ea\u662f\u4e00\u79cd\u5e7b\u89c9\u2026\u2026\u201d\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u773c\u5149\u548c\u51cc \u672a\u98ce\u78b0\u4e2a\u6b63\u7740\uff0c\u51cc\u672a\u98ce\u7684\u773c\u5149\u5c31\u50cf\u523a\u4eba\u7684\u201c\u5929\u5c71\u795e\u8292\u201d\u4e00\u6837\uff0c\u97e9\u5fd7\u90a6\u4e0d\u89c9\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u56b7 \u9053\uff1a\u201c\u51cc\u5927\u54e5\uff0c\u4e0d\u8981\u8fd9\u6837\u770b\u4eba\u884c\u4e0d\u884c\uff1f\u7ed9\u4f60\u5413\u6b7b\u4e86\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u201c\u55e4\u54e7\u4e00\u58f0\u5632\u7b11\u9053\uff1a\u201c\u4e8f\u4f60\u4eec\u8fd8\u662f\u5929\u5730\u4f1a\u7684\u8235\u4e3b\u5462\uff01\u8fd9\u6837\u80c6\u5c0f\u3002\u4f60\u4eec\u522b\u5c3d\u4f5c\u6076\u68a6 \u4e86\uff0c\u4f60\u542c\u542c\uff0c\u5916\u9762\u597d\u50cf\u6709\u4eba\u6765\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u77f3\u7a9f\u91cc\u55e1\u55e1\u7136\u7684\u54cd\u8d77\u56de\u58f0\uff0c\u4e00\u56e2\u706b\u5149\u5728\u9ed1\u6697\u4e2d\u6e10\u6e10\u79fb\u8fd1\u3002\u51cc\u672a\u98ce\u632f\u81c2\u8fce\u4e0a\uff0c\u53ea\u89c1\u5916 \u9762\u6765\u4e86\u56db\u4e2a\u5587\u561b\u548c\u4e00\u4e2a\u519b\u5b98\u88c5\u675f\u7684\u4eba\u3002\u51cc\u672a\u98ce\u548c\u97e9\u5fd7\u90a6\u90fd\u61c2\u5f97\u85cf\u8bed\uff0c\u4e24\u9762\u4ea4\u8c08\uff0c\u77e5\u9053\u4ed6\u4eec\u4e5f \u662f\u9519\u8fc7\u5bbf\u5934\uff0c\u624d\u5230\u77f3\u7a9f\u8fc7\u591c\u7684\u3002 \u3000\u3000\u56db\u4e2a\u5587\u561b\u90fd\u5f88\u548c\u853c\uff0c\u53ea\u89c1\u90a3\u4e2a\u519b\u5b98\u795e\u8272\u5374\u9887\u50b2\u6162\uff0c\u51cc\u672a\u98ce\u77a7\u7740\u4ed6\u7684\u8896\u53e3\u7ee3\u6709\u98de\u9e70\uff0c\u77e5\u9053 \u90a3\u662f\u5434\u4e09\u6842\u738b\u5e9c\u4e2d\u4eba\u7684\u6807\u5fd7\uff0c\u4e0d\u89c9\u591a\u770b\u4e86\u4e24\u773c\uff0c\u90a3\u519b\u5b98\u5600\u5600\u5495\u5495\uff0c\u51cc\u672a\u98ce\u7b49\u4e5f\u4e0d\u7406\u4ed6\uff0c\u81ea\u5728 \u4f5b\u50cf\u4e4b\u540e\u5b89\u6b47\u3002\u90a3\u4f5b\u50cf\u4e00\u4e08\u6765\u9ad8\uff0c\u50cf\u4e00\u4e2a\u5927\u5c4f\u98ce\u4e00\u6837\uff0c\u5c06\u4e24\u8fb9\u7684\u4eba\u963b\u9694\u5f00\u6765\u3002 \u3000\u3000\u90a3\u51e0\u4e2a\u5587\u561b\uff0c\u5174\u81f4\u4f3c\u4e4e\u5f88\u597d\uff0c\u5728\u4f5b\u50cf\u8fb9\u70e7\u8d77\u4e00\u5806\u706b\uff0c\u624b\u821e\u8db3\u8e48\u5730\u5531\u8d77\u6b4c\u6765\u3002\u6b4c\u58f0\u8d77\u521d\u6fc0 \u6602\u6e05\u8d8a\uff0c\u8f83\u540e\u5374\u5f88\u82cd\u51c9\u3002\u5218\u90c1\u82b3\u597d\u5947\u5730\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u5531\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u542c\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u5531\u7684\u662f\u897f\u85cf\u7684\u4e00\u4e2a\u4f20\u5947\u6545\u4e8b\u3002\u6545\u4e8b\u8bf4\u6709\u4e00\u4e2a\u5c11\u5e74\u53eb\u505a\u54c8 \u7684\u5e90\uff0c\u662f\u8349\u539f\u4e0a\u7684\u82f1\u96c4\uff0c\u53c8\u662f\u4e00\u4e2a\u597d\u6b4c\u624b\uff0c\u4ed6\u975e\u5e38\u9a84\u50b2\uff0c\u4ece\u4e0d\u80af\u5411\u4eba\u4f4e\u5934\u3002\u540e\u6765\u4ed6\u7231\u4e0a\u4e00\u4e2a \u7267\u7f8a\u5973\uff0c\u540d\u53eb\u963f\u76d6\uff0c\u963f\u76d6\u6bd4\u4ed6\u66f4\u9a84\u50b2\uff0c\u8981\u4ed6\u5f53\u7740\u4f17\u4eba\u7684\u9762\u8dea\u5728\u5979\u7684\u88d9\u4e0b\uff0c\u5979\u624d\u7b54\u5e94\u5a5a\u4e8b\uff0c\u54c8 \u7684\u5e90\u679c\u771f\u8dea\u4e0b\u6765\u6c42\u5a5a\uff0c\u5e74\u9752\u7684\u59d1\u5a18\u4eec\u90fd\u63a9\u7740\u9762\uff0c\u4e0d\u5fcd\u89c1\u4ed6\u4eec\u5fc3\u76ee\u4e2d\u7684\u82f1\u96c4\uff0c\u8fd9\u6837\u53d7\u51cc\u8fb1\u3002\u73b0 \u5728\u5531\u7684\uff0c\u5c31\u662f\u54c8\u7684\u5e90\u8bf4\u7684\u8bdd\uff0c\u4ed6\u8bf4\uff1a\u201c\u6211\u5b64\u9e64\u91ce\u4e91\u7684\u4ed9\u68a6\uff0c\u5230\u800c\u4eca\u90fd\u5df2\u5e7b\u5165\u7a7a\u51a5\uff0c\u8fd9\u4e8c\u5341\u5e74 \u6765\u7684\u6df1\u5fc3\u9a84\u50b2\uff0c\u90fd\u964d\u4f0f\u4f60\u51b0\u96ea\u7684\u806a\u660e\uff01\u201d\u5218\u90c1\u82b3\u542c\u7740\u51cc\u672a\u98ce\u7684\u8f6c\u8bd1\uff0c\u5fc3\u4e2d\u5982\u9189\uff0c\u5076\u7136\u4e00\u77a5\u3001 \u53ea\u89c1\u51cc\u672a\u98ce\u7684\u773c\u4e2d\uff0c\u4e5f\u95ea\u7740\u5f02\u6837\u7684\u5149\u5f69\u3002 \u3000\u3000\u5218\u90c1\u82b3\u60ca\u5f02\u5730\u671b\u4e86\u671b\u51cc\u672a\u98ce\uff0c\u51cc\u672a\u98ce\u201c\u5618\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u542c\uff0c\u8fd9\u9996\u897f\u85cf\u7684\u4f20\u5947\u8bd7\u7f8e\u6781 \u4e86\uff01\u73b0\u5728\u662f\u7267\u7f8a\u5973\u963f\u76d6\u7684\u503e\u8bc9\u3002\u5979\u66fe\u62d2\u7edd\u8fc7\u4e00\u4e2a\u85e9\u738b\u738b\u5b50\u7684\u6c42\u5a5a\uff0c\u5fc3\u4e2d\u5176\u5b9e\u4e5f\u662f\u7231\u54c8\u7684\u5e90 \u7684\uff0c\u4ed6\u8bf4\uff1a \u3000\u3000\u2018\u4e00\u5207\u7e41\u534e\u5728\u6211\u662f\u6619\u82b1\u8fc7\u773c\uff0c \u3000\u3000\u4f17\u751f\u8272\u76f8\u5230\u660e\u671d\u53c8\u662f\u865a\u65e0\uff0c \u3000\u3000\u6211\u53ea\u89c1\u591c\u7a7a\u4e2d\u7684\u660e\u661f\u4e00\u70b9\uff0c \u3000\u3000\u6c38\u6052\u4e0d\u706d\u76f4\u5230\u77f3\u70c2\u6d77\u67af\uff01 \u3000\u3000\u90a3\u4e0d\u706d\u7684\u661f\u661f\u662f\u4ed6\u6f06\u9ed1\u7684\u660e\u7738\uff0c \u3000\u3000\u5c06\u6307\u793a\u6211\u53bb\u819c\u62dc\uff0c\u53eb\u6211\u53bb\u7948\u6c42\uff0c \u3000\u3000\u8fd9\u5341\u591a\u5e74\u6765\u7684\u75f4\u60c5\u7737\u604b\uff0c \u3000\u3000\u613f\u5316\u4f5c\u4ed6\u5fc3\u574e\u4e2d\u7684\u8109\u8109\u957f\u6d41\u3002\u201d \u3000\u3000\u5218\u90c1\u82b3\u547c\u5438\u7d27\u4fc3\uff0c\u629a\u638c\u8bf4\u9053\uff1a\u201c\u8fd9\u9996\u6b4c\u679c\u7136\u597d\uff0c\u7ed3\u679c\u600e\u6837\uff1f\u8be5\u662f\u4ed6\u4eec\u4e24\u4eba\u7ed3\u4e86\u5a5a\u5427\uff1f\u201d \u51cc\u672a\u98ce\u5fe7\u90c1\u5730\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u7ed3\u5c40\u662f\u8c01\u4e5f\u6599\u4e0d\u5230\u7684\uff0c\u54c8\u7684\u5e90\u662f\u975e\u5e38\u9a84\u50b2\u7684\u4eba\uff0c\u4ed6\u7231\u963f\u76d6\uff0c\u4ed6 \u4e5f\u7231\u81ea\u5df1\u7684\u9a84\u50b2\uff0c\u4ed6\u8dea\u4e0b\u6765\u6c42\u5a5a\uff0c\u963f\u76d6\u7b11\u4e86\uff0c\u6b63\u60f3\u62c9\u4ed6\u8d77\u6765\uff0c\u4e0d\u6599\u4ed6\u4e00\u628a\u5315\u9996\u5c31\u628a\u963f\u76d6\u63d2\u6b7b \u4e86\uff0c\u8ddf\u7740\u4ed6\u81ea\u5df1\u4e5f\u81ea\u6740\u4e86\u3002\u4ed6\u4e34\u6b7b\u524d\u5531\u9053\uff1a \u3000\u3000\u2018\u6b22\u4e50\u7684\u65f6\u95f4\u8fc7\u5f97\u77ed\u4fc3\u800c\u660e\u4eae \u3000\u3000\u50cf\u9ed1\u591c\u7684\u5929\u7a7a\u84e6\u5730\u7535\u5149\u4e00\u95ea\uff0c \u3000\u3000\u867d\u65cb\u5373\u53c8\u6d88\u4e8e\u6f20\u6f20\u957f\u7a7a\uff0c \u3000\u3000\u5df2\u7167\u51fa\u5feb\u4e50\u60b2\u54c0\u4ea4\u7ec7\u7684\u7231\u5ff5\u3002\u2019\u201d \u3000\u3000\u97e9\u5fd7\u90a6\u558a\u8d77\u6765\u9053\uff1a\u201c\u8fd9\u4e0d\u8fd1\u4eba\u60c5\uff0c\u5982\u679c\u6211\u7231\u4e00\u4e2a\u4eba\uff0c\u6211\u7edd\u4e0d\u4f1a\u6740\u5979\uff01\u201d\u51cc\u672a\u98ce\u7b11\u9053\uff1a \u201c\u6211\u4e5f\u4e0d\u4f1a\uff0c\u4f46\u5982\u679c\u6211\u662f\u54c8\u7684\u5e90\uff0c\u90a3\u5973\u4eba\u8981\u6211\u5f53\u4f17\u8868\u793a\u5c48\u670d\uff0c\u6211\u4e5f\u4e00\u5b9a\u4e0d\u4f1a\u5411\u5979\u6c42\u5a5a\u3002\u8fd9\u9996 \u6b4c\u867d\u7136\u4e0d\u8fd1\u4eba\u60c5\uff0c\u4f46\u4e5f\u5531\u51fa\u4e86\u4eba\u7684\u81ea\u5c0a\uff0c\u867d\u7136\u90a3\u81ea\u5c0a\u662f\u8fc7\u4efd\u7684\u3002\u8fd9\u9996\u957f\u6b4c\u7684\u9898\u540d\u662f\uff1a\u5728\u8349\u539f \u4e0a\u8c01\u662f\u6700\u5014\u5f3a\u7684\u4eba\u3002\u201d \u3000\u3000\u90a3\u519b\u5b98\u4f3c\u4e4e\u7ed9\u6b4c\u58f0\u6405\u5f97\u5f88\u4e0d\u8010\u70e6\uff0c\u7528\u85cf\u8bdd\u559d\u9053\uff1a\u201c\u4e0d\u8981\u5531\u4e86\uff0c\u5feb\u53bb\u7761\u5427\uff0c\u660e\u65e9\u8fd8\u8981\u8d76 \u8def\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u89c1\u77f3\u7a9f\u4e2d\u9634\u4fa7\u4fa7\u5730\u6709\u4eba\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u8d76\u8def\u4e86\uff0c\u4f60\u4eec\u6ca1\u6709\u660e\u5929\u4e86\uff01\u201d\u4e0d\u8bf4 \u519b\u5b98\u548c\u5587\u561b\uff0c\u5c31\u662f\u51cc\u672a\u98ce\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u4eba\u597d\u4fca\u7684\u5185\u529f\uff0c\u4eba\u8fd8\u672a\u5230\uff0c\u800c\u58f0\u97f3\u597d\u4f3c\u5c31\u5728\u8033\u8fb9\uff01 \u3000\u3000\u4e24\u4e2a\u5587\u561b\u84e6\u7684\u8df3\u5c06\u8d77\u6765\uff0c\u5411\u5916\u6251\u53bb\uff0c\u5728\u9ed1\u6697\u7684\u77f3\u7a9f\u901a\u9053\u4e2d\uff0c\u53ea\u542c\u5f97\u6687\u6687\u556a\u556a\u7684\u6454\u4ea4\u58f0 \u54cd\uff0c\u51cc\u672a\u98ce\u5728\u4f5b\u50cf\u80cc\u540e\u671b\u53bb\uff0c\u5ffd\u89c1\u4e24\u56e2\u9ed1\u5ffd\u5ffd\u7684\u4e1c\u897f\u63b7\u4e86\u8fdb\u6765\u3002\u4e24\u4e2a\u5587\u561b\u7adf\u7136\u4e0d\u8fc7\u4e09\u4e94\u4e2a\u7167 \u9762\uff0c\u5c31\u7ed9\u6765\u4eba\u6454\u5012\uff0c\u5f53\u4f5c\u76ae\u7403\u4e00\u6837\u5730\u629b\u4e86\u8fdb\u6765\u3002\u90a3\u519b\u5b98\u548c\u53e6\u5916\u4e24\u4e2a\u5587\u561b\u52c3\u7136\u5927\u6012\uff0c\u500f\u5730\u62d4\u51fa \u4e86\u5175\u5668\uff0c\u5c31\u8fce\u4e0a\u53bb\u901a\u9053\u4e2d\uff0c\u51e0\u58f0\u957f\u7b11\uff0c\u98de\u9e1f\u822c\u5730\u63a0\u8fdb\u4e86\u51e0\u4e2a\u9ed1\u8863\u6c49\u5b50\u3002\u97e9\u5fd7\u90a6\u8038\u4e00\u8038\u80a9\uff0c\u5c31 \u5f85\u8df3\u51fa\uff0c\u51cc\u672a\u98ce\u4e00\u628a\u6309\u4f4f\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u522b\u5fd9\uff0c\u770b\u6765\u7684\u662f\u4ec0\u4e48\u4eba\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u6765\u4eba\u5df2\u5230\u4e86 \u4f5b\u50cf\u4e4b\u524d\uff0c\u51cc\u672a\u98ce\u4e00\u89c1\uff0c\u8be7\u5f02\u5f97\u51e0\u4e4e\u558a\u51fa\u58f0\u6765\u3002 \u3000\u3000\u8fdb\u6765\u7684\u662f\u4e09\u4e2a\u9ed1\u8863\u536b\u58eb\uff0c\u4e3a\u9996\u7684\u7adf\u662f\u6e38\u9f99\u5251\u695a\u662d\u5357\u3002\u4e0d\u8bf4\u51cc\u672a\u98ce\u60ca\u8be7\uff0c\u4e0e\u5587\u561b\u50e7\u540c\u6765\u7684 \u519b\u5b98\u4e5f\u558a\u4e86\u8d77\u6765\uff0c\u8fd9\u519b\u5b98\u540d\u53eb\u5f20\u5929\u8499\uff0c\u4e0e\u695a\u662d\u5357\u672c\u6765\u540c\u662f\u5434\u4e09\u6842\u7684\u5fc3\u8179\u3002 \u3000\u3000\u5f20\u5929\u8499\u89c1\u695a\u662d\u5357\u628a\u4e24\u4e2a\u5587\u561b\u6454\u4e86\u8fdb\u6765\uff0c\u6025\u5fd9\u558a\u9053\uff01\u201c\u5927\u54e5\u522b\u52a8\u624b\uff0c\u662f\u81ea\u5df1\u4eba\uff01\u201d\u695a\u662d\u5357 \u8de8\u524d\u4e00\u6b65\u559d\u9053\uff1a\u201c\u5929\u8499\uff0c\u4f60\u53eb\u4ed6\u4eec\u628a\u2018\u820d\u5229\u5b50\u2019\u4ea4\u51fa\u6765\uff0c\u6211\u53ef\u4ee5\u9976\u4ed6\u4eec\u4e0d\u6b7b\uff01\u201d \u3000\u3000\u201c\u820d\u5229\u5b50\u201d\u4e43\u662f\u4f5b\u95e8\u7684\u5b9d\u8d1d\uff0c\u636e\u8bf4\u6709\u9053\u7684\u9ad8\u50e7\u6b7b\u540e\uff0c\u7528\u706b\u711a\u5316\uff0c\u9aa8\u8089\u867d\u70e7\u6210\u7070\uff0c\u4f46\u5374\u6709 \u4e00\u9897\u50cf\u73cd\u73e0\u822c\u7684\u9aa8\u5934\uff0c\u767e\u70bc\u4e0d\u5316\uff0c\u5176\u540d\u4fbf\u662f\u201c\u820d\u5229\u5b50\u201d\u3002\u5434\u4e09\u6842\u9053\u6842\u738b\u5165\u7f05\uff0c\u628a\u7f05\u7538\u7d2b\u5149\u5bfa \u9547\u5bfa\u4e4b\u5b9d\u2014\u2014\u9f99\u6811\u7985\u5e08\u7559\u4e0b\u7684\u201c\u820d\u5229\u5b50\u201d\u52ab\u4e86\u56de\u6765\u3002\u9f99\u6811\u662f\u91ca\u8fe6\u725f\u5c3c\u7684\u5927\u5f1f\u5b50\uff0c\u5927\u4e58\u6559\u6d3e\u7684 \u521b\u59cb\u4eba\uff0c\u4f5b\u6559\u7684\u5723\u7269\uff0c\u7b2c\u4e00\u662f\u91ca\u8fe6\u725f\u5c3c\u7559\u4e0b\u7684\u4f5b\u7259\uff0c\u7b2c\u4e8c\u4fbf\u662f\u9f99\u6811\u7985\u5e08\u7559\u4e0b\u7684\u201c\u820d\u5229\u5b50\u201d\uff0c \u5434\u4e09\u6842\u4e3a\u4e86\u8981\u8054\u7edc\u8fbe\u8d56\u5587\u561b\uff0c\u56e0\u6b64\u53eb\u5f20\u5929\u8499\u62a4\u9001\u201c\u820d\u5229\u5b50\u201d\u5230\u897f\u85cf\uff0c\u90a3\u56db\u4e2a\u5587\u561b\u4e43\u662f\u5165\u6ec7\u8fce \u63a5\u5723\u7269\u7684\u4eba\u3002\u695a\u662d\u5357\u77e5\u9053\u8fd9\u4e8b\uff0c\u548c\u5eb7\u7199\u4e00\u8bf4\uff0c\u5eb7\u7199\u7acb\u523b\u6d3e\u4e24\u4e2a\u6b66\u529f\u8d85\u5353\u7684\u536b\u58eb\u548c\u4ed6\u4e00\u540c\u53bb\u62e6 \u52ab\u3002\u6b63\u56e0\u5eb7\u7199\u5206\u5fc3\u4e8e\u5bf9\u4ed8\u5434\u4e09\u6842\u548c\u62e6\u52ab\u5723\u7269\uff0c\u6b66\u5bb6\u5e84\u7fa4\u96c4\uff0c\u624d\u80fd\u987a\u5229\u5206\u6563\uff0c\u6ca1\u6709\u53d7\u5230\u641c\u6355\u3002 \u3000\u3000\u5f20\u5929\u8499\u89c1\u695a\u662d\u5357\u4e00\u5f00\u53e3\u5c31\u8981\u201c\u820d\u5229\u5b50\u201d\uff0c\u5fc3\u4e2d\u5927\u7591\uff0c\u95ee\u9053\uff1a\u201c\u695a\u5927\u54e5\uff0c\u4f60\u521a\u4ece\u897f\u85cf\u56de\u6765 \u5417\uff0c\u8fd9\u2018\u820d\u5229\u5b50\u2019\u662f\u5e73\u897f\u738b\u53eb\u6211\u62a4\u9001\u7684\uff0c\u4e0d\u6562\u6709\u52b3\u3002\u201d\u695a\u662d\u5357\u51b7\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u5e73\u897f\u738b\uff1f\u8fd9 \u2018\u820d\u5229\u5b50\u2019\u662f\u5f53\u4eca\u7687\u4e0a\u53eb\u6211\u6765\u62ff\u7684\uff01\u201d\u5f20\u5929\u8499\u5927\u5403\u4e00\u60ca\u9053\uff1a\u201c\u4f60\u53cd\u4e86\uff01\u201d\u695a\u662d\u5357\u5927\u7b11\u9053\uff1a \u201c\u5434\u4e09\u6842\u53cd\u5f97\u6211\u53cd\u4e0d\u5f97\uff1f\u6211\u95ee\u4f60\uff0c\u4f60\u5230\u5e95\u662f\u613f\u8ddf\u5434\u4e09\u6842\u8fd8\u662f\u613f\u8ddf\u7687\u5e1d\uff1f\u201d \u3000\u3000\u5f20\u5929\u8499\u5728\u5e73\u897f\u738b\u5e9c\u4e2d\uff0c\u5730\u4f4d\u6bd4\u695a\u662d\u5357\u7a0d\u4f4e\uff0c\u5434\u4e09\u6842\u56fe\u8c0b\u53cd\u53db\u4e4b\u4e8b\uff0c\u4ed6\u6beb\u4e0d\u77e5\u60c5\u3002\u89c1\u695a\u662d \u5357\u8fd9\u6837\u8bf4\uff0c\u5982\u6674\u5929\u8d77\u4e86\u9739\u96f3\uff0c\u987f\u65f6\u505a\u58f0\u4e0d\u5f97\u3002\u695a\u662d\u5357\u8feb\u524d\u4e00\u6b65\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5230\u5e95\u600e\u4e48\u6837\uff1f\u201d \u5f20\u5929\u8499\u5fc3\u4e2d\u4e03\u4e0a\u516b\u843d\uff0c\u72b9\u7591\u4e0d\u8db3\u3002\u53e6\u5916\u4e24\u4e2a\u5587\u561b\uff0c\u89c1\u695a\u662d\u5357\u7528\u6c49\u8bdd\u5927\u58f0\u547c\u559d\uff0c\u867d\u542c\u4e0d\u61c2\u4ed6\u8bf4 \u4ec0\u4e48\uff0c\u4f46\u770b\u6837\u5b50\u4f3c\u662f\u903c\u8feb\u5f20\u5929\u8499\u7684\u6837\u5b50\uff0c\u5fc3\u4e2d\u6709\u6c14\uff0c\u53cc\u53cc\u8dd1\u4e0a\uff0c\u65bd\u5c55\u201c\u5927\u529b\u5343\u65a4\u62f3\u201d\uff0c\u4e00\u5de6 \u4e00\u53f3\uff0c\u54e9\u54e9\u5730\u6253\u51fa\u4e24\u62f3\u3002\u695a\u662d\u5357\u6545\u610f\u5356\u5f04\uff0c\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u8fce\u9762\u5c31\u63a5\u4e86\u4e24\u62f3\u3002\u8fd9\u4e24\u62f3\u51fb\u7740\u80f8\u819b\uff0c \u201c\u84ec\uff01\u84ec\uff01\u201d\u4e24\u58f0\uff0c\u5982\u4e2d\u8d25\u9769\uff01\u4e24\u4e2a\u5587\u561b\u90fd\u7ed9\u5f39\u9000\u51e0\u6b65\uff0c\u53ef\u662f\u695a\u662d\u5357\u4e5f\u89c9\u4e00\u9635\u75bc\u75db\uff0c\u5403\u4e86\u4e00 \u60ca\uff0c\u5fc3\u60f3\u8fd9\u4e24\u4e2a\u5587\u561b\u679c\u7136\u6709\u51e0\u65a4\u6c14\u529b\u3002\u4ed6\u4e0d\u6562\u6020\u6162\uff0c\u6251\u5730\u817e\u8d77\uff0c\u4f3c\u98de\u9e70\u652b\u5154\u4e4b\u52bf\uff0c\u671d\u4e24\u4e2a\u5587 \u561b\u7684\u540e\u5fc3\u4fbf\u6293\uff0c\u770b\u770b\u5f97\u624b\uff0c\u5ffd\u542c\u5f97\u4f5b\u50cf\u540e\u4e00\u58f0\u5de8\u559d\uff0c\u4e00\u9897\u94c1\u84ba\u85dc\u6d41\u661f\u95ea\u7535\u822c\u7684\u88ad\u5230\u3002\u695a\u662d\u5357 \u597d\u4fca\u7684\u529f\u592b\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u7acb\u523b\u5012\u7ffb\u51fa\u53bb\u3002\u90a3\u9897\u94c1\u84ba\u85dc\u7ed9\u4ed6\u5728\u5012\u7ffb\u65f6\u7528\u811a\u540e \u8ddf\u4e00\u8e74\uff0c\u7bad\u4e00\u6837\u5730\u5012\u5c04\u56de\u53bb\u3002\u4f5b\u50cf\u540e\u97e9\u5fd7\u90a6\u521a\u521a\u7eb5\u51fa\uff0c\u5403\u94c1\u84ba\u85dc\u4e00\u5c04\uff0c\u6025\u633a\u624b\u4e2d\u5175\u5200\u516b\u5366\u7d2b \u91d1\u5200\u4e00\u62cd\uff0c\u867d\u7136\u5c06\u94c1\u84ba\u85dc\u62cd\u98de\uff0c\u53ef\u662f\u864e\u53e3\u7adf\u4e00\u9635\u53d1\u9ebb\u3002\u8fd9\u94c1\u76d4\u84ba\u85dc\u695a\u662d\u5357\u5012\u8e66\u56de\u6765\uff0c\u52b2\u5ea6\u8fd8 \u662f\u5982\u6b64\u4e4b\u5f3a\uff0c\u97e9\u5fd7\u90a6\u4e5f\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u97e9\u5fd7\u90a6\u521a\u7ad9\u7a33\u811a\u6b65\uff0c\u695a\u662d\u5357\u5df2\u662f\u518d\u5ea6\u6251\u5230\uff0c\u97e9\u5fd7\u90a6\u8eab\u5f62\u4e00\u77ee\uff0c\u5f80\u524d\u4e00\u4e2a\u7eb5\u6b65\uff0c\u516b\u5366\u7d2b\u91d1 \u5200\u7167\u695a\u662d\u5357\u80f8\u524d\u75be\u5288\uff0c\u695a\u662d\u5357\u5de6\u624b\u8896\u5b50\u5f80\u5916\u4e00\u62c2\uff0c\u4e00\u80a1\u52b2\u98ce\uff0c\u76f4\u6251\u9762\u95e8\uff0c\u97e9\u5fd7\u90a6\u4fa7\u4e00\u4fa7\u5934\uff0c \u5200\u5df2\u63b7\u7a7a\uff0c\u695a\u662d\u5357\u8eab\u5f62\u8fc5\u5982\u98d8\u98ce\uff0c\u7a81\u5730\u7ed5\u5230\u97e9\u5fd7\u90a6\u80cc\u540e\uff0c\u97e9\u5fd7\u90a6\u4e5f\u662f\u865a\u5b9e\u5e76\u7528\uff0c\u62db\u6570\u5e76\u672a\u4f7f \u8001\uff0c\u4ed6\u4e00\u5200\u6eaf\u7a7a\uff0c\u5df2\u75be\u7684\u659c\u584c\u8eab\u5f62\uff0c\u5200\u950b\u5916\u5c55\uff0c\u5237\u5730\u65c1\u626b\u695a\u662d\u5357\u4e0b\u76d8\u3002\u695a\u662d\u5357\u5927\u559d\u4e00\u58f0\u201c\u64a4 \u624b\uff01\u201d\u53f3\u638c\u5288\u9762\u6253\u51fa\uff0c\u5de6\u624b\u5219\u9a88\u6307\u5982\u671d\uff0c\u7167\u97e9\u5fd7\u90a6\u53f3\u81c2\u201c\u4e09\u91cc\u7a74\u201d\u70b9\u53bb\u3002\u97e9\u5fd7\u90a6\u5200\u5df2\u5288\u51fa\uff0c \u89c1\u52bf\u4e0d\u5999\uff0c\u8fde\u5fd9\u53d8\u62db\u5e94\u654c\uff0c\u201c\u4e09\u7f8a\u5f00\u6cf0\u201d\uff0c\u4e00\u62db\u4e09\u5f0f\uff0c\u523a\u80f8\u819b\uff0c\u6302\u4e24\u80a9\uff0c\u72e0\u72e0\u5730\u626b\u6765\u3002\u4f46\u4ed6 \u5feb\uff0c\u695a\u662d\u5357\u66f4\u5feb\u3002\u4ed6\u4e00\u5200\u5288\u51fa\uff0c\u654c\u4eba\u65b9\u4f4d\u5df2\u53d8\uff0c\u4ed6\u53ea\u89c1\u654c\u4eba\u5de6\u62f3\u5728\u9762\u524d\u4e00\u6643\uff0c\u773c\u795e\u4e00\u4e71\uff0c\u53f3 \u81c2\u5df2\u662f\u4e00\u9635\u9178\u9ebb\u3002\u695a\u662d\u5357\u6b66\u529f\u795e\u5947\uff0c\u7adf\u662f\u65b9\u4f4d\u53d8\u800c\u62db\u6570\u672a\u53d8\uff0c\u5de6\u624b\u624b\u6307\uff0c\u4ecd\u7136\u70b9\u7740\u4e86\u97e9\u5fd7\u90a6 \u7684\u7a74\u9053\u3002\u53ea\u542c\u5f97\u201c\u545b\u5577\u201d\u4e00\u58f0\uff0c\u7d2b\u91d1\u5200\u6389\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u51e0\u62db\u5feb\u5982\u7535\u5149\u77f3\u706b\uff01\u4e0e\u695a\u662d\u5357\u540c\u6765\u7684\u4e24\u4e2a\u536b\u58eb\uff0c\u8fd9\u65f6\u624d\u521a\u521a\u770b\u6e05\u97e9\u5fd7\u90a6\u7684\u9762\u5bb9\uff0c\u5927\u58f0 \u558a\u9053\uff1a\u201c\u8fd9\u53ae\u662f\u5929\u5730\u4f1a\u7684\u603b\u8235\u4e3b\uff01\u4e0d\u8981\u653e\u8fc7\u4ed6\uff01\u201d\u695a\u662d\u5357\u72de\u7b11\u4e00\u58f0\uff0c\u6b63\u5f85\u8d76\u4e0a\uff0c\u8c6a\u7136\u4e00\u9053\u4e4c \u91d1\u5149\u8292\uff0c\u81ea\u4f5b\u50cf\u540e\u7535\u5c04\u800c\u51fa\uff0c\u695a\u662d\u5357\u8fd0\u8db3\u5185\u52b2\uff0c\u6a2a\u8896\u4e00\u62cd\uff0c\u7adf\u5f79\u5c06\u6697\u5668\u62cd\u98de\uff0c\u888d\u8896\u7ed9\u523a\u7a7f\u4e86 \u4e00\u4e2a\u5927\u6d1e\uff0c\u6697\u5668\u8d34\u8089\u800c\u8fc7\uff0c\u4f59\u52bf\u4ecd\u7136\u975e\u5e38\u5f3a\u70c8\uff0c\u5c04\u5728\u5bf9\u9762\u77f3\u58c1\u4e0a\uff0c\u94ff\u9535\u6709\u58f0\uff0c\u4e00\u679d\u4f3c\u8896\u7bad\u800c \u975e\u8896\u7bad\u7684\u4e1c\u897f\uff0c\u7adf\u7136\u7a7f\u5165\u4e86\u77f3\u58c1\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4f5b\u50cf\u80cc\u540e\uff0c\u4e00\u7537\u4e00\u5973\u98de\u8eab\u800c\u51fa\uff0c\u53cc\u53cc\u62e6\u5728\u695a\u662d\u5357\u9762\u524d\uff0c\u695a\u662d\u5357\u55d6\u7684\u4e00 \u58f0\uff0c\u62d4\u51fa\u4f69\u5251\uff0c\u5e76\u4e0d\u4e0a\u524d\uff0c\u5374\u53cd\u5012\u7eb5\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u6666\u660e\u7985\u5e08\u7684\u4ec0\u4e48\u4eba\uff0c\u4e09\u756a\u4e24 \u6b21\u548c\u6211\u4f5c\u5bf9\uff0c\u4f60\u5f53\u6211\u771f\u7684\u6015\u4f60\u5417\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u5218\u90c1\u82b3\u5df2\u5c06\u97e9\u5fd7\u90a6\u6551\u8d77\uff0c\u7ed9\u4ed6\u89e3\u4e86\u7a74\u9053\u3002\u51cc\u672a\u98ce\u7b11\u563b\u563b\u5730\u7ad9\u5728\u4f5b\u50cf\u4e4b\u524d\uff0c\u4e0d\u7406\u695a\u662d \u5357\uff0c\u5148\u7528\u85cf\u8bed\u5bf9\u90a3\u51e0\u4e2a\u5587\u561b\u9053\uff1a\u201c\u4f60\u4eec\u7ad9\u8fc7\u8fd9\u4e00\u8fb9\u6765\uff0c\u2018\u820d\u5229\u5b50\u2019\u53ef\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u62a2\u53bb\u3002\u201d\u90a3 \u51e0\u4e2a\u5587\u561b\u4f9d\u8a00\u75be\u9000\uff0c\u548c\u695a\u662d\u5357\u540c\u6765\u7684\u4e24\u4e2a\u536b\u58eb\uff0c\u53cc\u53cc\u8d76\u4e0a\uff0c\u51cc\u672a\u98ce\u628a\u624b\u4e00\u626c\uff0c\u53c8\u662f\u4e24\u9053\u4e4c\u91d1 \u5149\u8292\u7535\u5c04\u800c\u51fa\uff0c\u90a3\u4e24\u4e2a\u536b\u58eb\u4e5f\u975e\u5f31\u8005\uff0c\u4e00\u4e2a\u4e3e\u8d77\u9b3c\u5934\u5200\u7528\u529b\u4e00\u683c\uff0c\u53ea\u542c\u5f97\u84e6\u7136\u4e00\u58f0\uff0c\u706b\u661f\u75be \u98de\uff0c\u9b3c\u5934\u5200\u7adf\u7ed9\u6697\u5668\u5c04\u7f3a\u4e00\u53e3\uff1b\u53e6\u4e00\u4e2a\u7528\u201c\u4e00\u9e64\u51b2\u5929\u201d\u7684\u8f7b\u529f\u7edd\u6280\uff0c\u5e73\u5730\u62d4\u8d77\u4e09\u4e08\u591a\u9ad8\uff0c\u9976 \u662f\u4ed6\u8eb2\u5f97\u8fd9\u6837\u7094\uff0c\u6697\u5668\u8fd8\u662f\u8d34\u7740\u4ed6\u7684\u978b\u5c04\u8fc7\uff0c\u4ed6\u7a7f\u7684\u662f\u94c1\u638c\u978b\uff0c\u540e\u8ddf\u4e5f\u7ed9\u5c04\u6389\u3002\u4e24\u4eba\u5413\u51fa\u4e86 \u4e00\u8eab\u51b7\u6c57\u3002\u695a\u662d\u5357\u559d\u9053\uff1a\u201c\u522b\u5fd9\u6599\u7406\u90a3\u4e9b\u5587\u561b\uff0c\u4ed6\u4eec\u9003\u8dd1\u4e0d\u4e86\uff01\u201d\u4e24\u4e2a\u536b\u58eb\u8d81\u6b64\u4e00\u559d\uff0c\u4e5f\u4e0d \u518d\u8ffd\uff0c\u5206\u7acb\u695a\u662d\u5357\u5de6\u53f3\u3002\u800c\u5f20\u5929\u8499\u5374\u4ecd\u4e0d\u58f0\u4e0d\u54cd\uff0c\u659c\u6328\u5728\u4f5b\u50cf\u4e4b\u65c1\uff0c\u9760\u8fd1\u5587\u561b\u3002 \u3000\u3000\u8fd9\u65f6\u51cc\u672a\u98ce\u624d\u51b7\u51b7\u5730\u5bf9\u695a\u662d\u5357\u7b11\u9053\uff1a\u201c\u8bba\u5e08\u95e8\u6e0a\u6e90\uff0c\u6211\u8981\u5c0a\u4f60\u4e00\u58f0\u5e08\u5144\uff1b\u8bba\u6c5f\u6e56\u9053\u4e49\uff0c \u6211\u8981\u9a82\u4f60\u4e00\u58f0\u8d3c\u5b50\uff01\u4f60\u5230\u5e95\u613f\u6211\u5c0a\u4e3a\u5e08\u5144\uff0c\u8fd8\u662f\u7518\u4e3a\u6211\u9a82\u4f5c\u8d4b\u5b50\uff1f\u4eba\u9b3c\u6b8a\u9014\uff0c\u4f60\u8be5\u65e9\u4f5c\u6289\u62e9 \u4e86\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u81ea\u6c5f\u5357\u8fdc\u5954\u6f20\u5916\uff0c\u5728\u5927\u5c71\u4e4b\u5dc5\uff0c\u8ddf\u968f\u6666\u660e\u7985\u5e08\u4e60\u6280\u5341\u5e74\uff0c\u5176\u4e8b\u751a\u79d8\uff0c\u83ab\u8bf4\u6b66\u6797\u4e2d \u65e0\u4eba\u77e5\u6653\uff0c\u5c31\u662f\u66fe\u5728\u6666\u660e\u7985\u5e08\u95e8\u4e0b\u4e60\u6280\u7684\u695a\u662d\u5357\u4e5f\u4e0d\u77e5\u9053\u3002\u695a\u662d\u5357\u53ea\u9053\u5927\u5e08\u5144\u6768\u4e91\u9a86\u6b7b\u540e\uff0c \u81ea\u5df1\u53ef\u4ee5\u72ec\u9738\u5929\u4e0b\uff0c\u4e0d\u6599\u90a3\u65e5\u5728\u4e94\u53f0\u5c71\u8c37\uff0c\u5ffd\u7136\u94bb\u51fa\u4e86\u4e00\u4e2a\u51cc\u672a\u98ce\uff0c\u4f7f\u51fa\u4e86\u5929\u5c71\u638c\u6cd5\u4e2d\u7684\u7edd \u62db\uff0c\u81ea\u5df1\u9aa4\u5403\u4e00\u60ca\uff0c\u7adf\u7136\u6328\u4e86\u4e00\u638c\u3002\u5982\u4eca\u542c\u4ed6\u516c\u7136\u8868\u767d\u8eab\u4efd\uff0c\u53eb\u81ea\u5df1\u4f5c\u5e08\u5144\uff0c\u5fc3\u4e2d\u4e00\u614c\uff0c\u4f46 \u968f\u5373\u53c8\u60f3\uff1a\u201c\u7eb5\u4f7f\u4ed6\u5c31\u662f\u6666\u660e\u7985\u5e08\u7684\u5173\u95e8\u5f92\u5f1f\uff0c\u4f46\u4ed6\u4e0d\u8fc7\u4e09\u5341\u5c81\u5de6\u53f3\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u6bd4\u4e0d\u4e0a\u81ea \u5df1\u51e0\u5341\u5e74\u529f\u529b\uff0c\u4f55\u5fc5\u6015\u4ed6\uff1f \u3000\u3000\u5f53\u4e0b\u695a\u662d\u5357\u6a2a\u76ee\u77a7\u89c6\uff0c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u5e08\u5144\uff1f\u4f60\u8981\u8ba4\u6211\u505a\u5e08\u5144\uff0c\u53ef\u5f97\u5148\u9732\u51e0\u624b\u51fa \u6765\u77a7\u77a7\uff0c\u6765\uff01\u6765\uff01\u6211\u8ba8\u6559\u4f60\u7684\u638c\u6cd5\uff01\u201d\u4ed6\u6328\u4e86\u4e00\u638c\uff0c\u4f59\u5fff\u672a\u6d88\uff0c\u4e00\u5b9a\u8981\u5728\u638c\u6cd5\u4e0a\u627e\u56de\u9762\u5b50\u3002 \u3000\u3000\u51cc\u672a\u98ce\u51b7\u51b7\u4e00\u7b11\uff0c\u4fbf\u5f85\u4eae\u5f0f\uff0c\u695a\u662d\u5357\u6b63\u5f85\u4e0a\u524d\uff0c\u548c\u4ed6\u540c\u6765\u7684\u4e00\u4e2a\u536b\u58eb\uff0c\u5ffd\u5730\u659c\u523a\u6740\u51fa\uff0c \u8bf4\u9053\uff1a\u201c\u6740\u9e21\u7109\u7528\u725b\u5200\uff0c\u4e14\u5f85\u4ffa\u5148\u4f1a\u4f1a\u8fd9\u53ae\uff01\u201d\u695a\u662d\u5357\u4e00\u770b\uff0c\u8fd9\u536b\u58eb\u540d\u53eb\u53e4\u5143\u4eae\uff0c\u4e43\u662f\u6cb3\u5357 \u70b9\u7a74\u540d\u5bb6\u65b9\u5bb6\u4e4b\u540e\uff0c\u4ed6\u7684\u70b9\u7a74\u6cd5\u6400\u6742\u5728\u638c\u6cd5\u4e4b\u4e2d\uff0c\u5389\u5bb3\u5f02\u5e38\uff0c\u662f\u5927\u5185\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\u3002\u695a\u662d\u5357 \u5fc3\u60f3\uff0c\u8ba9\u4ed6\u5148\u53bb\u8bd5\u62db\uff0c\u5bf9\u81ea\u5df1\u751a\u6709\u597d\u5904\uff0c\u82e5\u4ed6\u80dc\u4e86\uff0c\u81ea\u5df1\u65e0\u987b\u51fa\u624b\uff1b\u82e5\u4ed6\u8f93\u4e86\uff0c\u81ea\u5df1\u4e5f\u53ef\u770b \u6e05\u695a\u51cc\u672a\u98ce\u8def\u9053\u3002\u4e8e\u662f\u5fae\u5fae\u70b9\u9996\uff0c\u8ba9\u53e4\u5143\u4eae\u5148\u4e0a\u3002 \u3000\u3000\u53e4\u5143\u4eae\u521a\u624d\u7ed9\u51cc\u672a\u98ce\u4e00\u679d\u6697\u5668\uff0c\u6253\u65ad\u978b\u8ddf\uff0c\u4e5f\u662f\u6124\u6012\u5f97\u5f88\uff0c\u4ed6\u4e00\u4e0a\u6765\uff0c\u5c31\u5927\u58f0\u559d\u9053\uff1a \u201c\u6211\u4e5f\u662f\u8981\u5148\u8ba8\u6559\u4f60\u7684\u638c\u6cd5\uff0c\u4f60\u82e5\u8981\u6bd4\u6697\u5668\uff0c\u7b49\u4e0b\u6211\u4e5f\u53ef\u5949\u966a\u3002\u54b1\u4eec\u8bf4\u8bdd\u5728\u524d\uff0c\u53ef\u4e0d\u8bb8\u6697\u653e \u51b7\u7bad\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u77e5\u9053\u4ed6\u6015\u81ea\u5df1\u7684\u6697\u5668\u5389\u5bb3\uff0c\u6240\u4ee5\u62ac\u51fa\u6c5f\u6e56\u4e0a\u6bd4\u6b66\u7684\u89c4\u77e9\uff0c\u8a00\u660e\u5728\u524d\uff0c\u8981\u6bd4\u5b8c\u4e00\u6837 \u624d\u6bd4\u4e00\u6837\uff0c\u9042\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u6697\u5668\uff0c\u4e00\u6837\u53ef\u4ee5\u6253\u5f97\u4f60\u4e71\u8df3\uff01\u201d \u3000\u3000\u53e4\u5143\u4eae\u811a\u5c16\u4e00\u70b9\uff0c\u5982\u7bad\u79bb\u5f26\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u548c\u4f60\u6597\u5634\uff0c\u63a5\u62db\uff01\u201d\u8bdd\u58f0\u672a\u5b8c\uff0c\u4e00\u638c\u5df2\u5411\u51cc\u672a \u98ce\u201c\u5929\u62a0\u7a74\u201d\u6309\u53bb\uff0c\u51cc\u672a\u98ce\u89c1\u4ed6\u638c\u98ce\u751a\u52b2\uff0c\u6240\u6309\u90e8\u4f4d\u53c8\u662f\u7a74\u9053\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u4e00\u58f0\u957f\u5578\uff0c\u500f\u5730 \u4e00\u4e2a\u65cb\u8eab\uff0c\u6a2a\u638c\u5982\u5200\uff0c\u731b\u5207\u53e4\u5143\u4eae\u8151\u95e8\uff0c\u53e4\u5143\u4eae\u5927\u543c\u4e00\u58f0\uff0c\u6258\u5730\u8df3\u5c06\u51fa\u53bb\uff0c\u51cc\u672a\u98ce\u53cc\u81c2\u5f29 \u5f20\uff0c\u4e00\u63a0\u4e08\u8bb8\uff0c\u5411\u80cc\u5fc3\u4fbf\u6293\u3002\u90a3\u6599\u53e4\u5143\u4eae\u867d\u5403\u8feb\u9000\uff0c\u5374\u4e0d\u662f\u771f\u8d25\uff0c\u4ed6\u500f\u5730\u8eab\u8eaf\u4e00\u77ee\uff0c\u9640\u87ba\u822c \u7684\u76f4\u62e7\u8f6c\u6765\uff0c\u53cc\u638c\u9aa4\u53d1\uff0c\u4e00\u6253\u51cc\u672a\u98ce\u80c1\u4e0b\u7684\u201c\u4e73\u6cc9\u7a74\u201d\uff0c\u4e00\u626b\u8170\u90e8\u201c\u5173\u5143\u7a74\u201d\uff0c\u7ade\u662f\u8d25\u91cc\u53cd \u653b\uff0c\u72e0\u62db\u786c\u626b\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u770b\u5f97\u201c\u963f\u5440\u201d\u7684\u53eb\u51fa\u58f0\u6765\uff0c\u695a\u662d\u5357\u5374\u4e00\u58f0\u5927\u9053\uff1a\u201c\u8001\u53e4\uff0c\u7559\u795e\uff01\u201d\u97e9\u5fd7\u90a6\u8fd8\u672a\u770b \u6e05\uff0c\u53ea\u89c1\u53e4\u5143\u4eae\u5df2\u8dcc\u8dcc\u649e\u649e\u5012\u9000\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u9762\u8272\u7070\u767d\u3002\u51cc\u672a\u98ce\u559d\u9053\uff1a\u201c\u4f60\u5df2\u8f93\u62db\uff0c\u8fd8\u8d56\u5728 \u8fd9\u91cc\u4f5c\u751a\uff01\u201d\u53e4\u5143\u4eae\u95f7\u58f0\u4e0d\u54cd\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u72e0\u72e0\u5730\u53c8\u653b\u4e0a\u6765\u3002\u8fd9\u4e00\u6765\u53ea\u89c1\u638c\u98ce\u8d8a\u53d1\u51cc\u5389\uff0c\u51cc \u672a\u98ce\u8fdb\u500f\u9000\uff0c\u8eab\u6cd5\u6b65\u6cd5\uff0c\u4e1d\u6beb\u4e0d\u4e71\u3002\u800c\u53e4\u5143\u4eae\u5219\u4f3c\u4e00\u53ea\u53d7\u4f24\u7684\u72ee\u5b50\uff0c\u5f3a\u653b\u731b\u6253\uff0c\u638c\u98ce\u6240\u5230\uff0c \u5168\u662f\u6309\u5411\u51cc\u672a\u98ce\u7684\u4e09\u5341\u516d\u9053\u5927\u7a74\u3002 \u3000\u3000\u53e4\u5143\u4eae\u4e00\u65f6\u758f\u5ffd\uff0c\u5403\u4e86\u4e2a\u4e8f\uff0c\u5fc3\u4e2d\u5927\u6012\uff0c\u518d\u5ea6\u731b\u6251\uff0c\u51cc\u5389\u4e4b\u4e2d\u89c1\u7ef5\u5bc6\uff0c\u6240\u622a\u4e4b\u4e2d\u6742\u70b9 \u7a74\uff0c\u53cc\u638c\u8d77\u5904\uff0c\u5168\u662f\u6309\u5411\u4eba\u8eab\u4e09\u5341\u516d\u9053\u5927\u7a74\uff0c\u51cc\u672a\u98ce\u8eab\u968f\u638c\u8d70\uff0c\u89c1\u62db\u62c6\u62db\uff0c\u53e4\u5143\u4eae\u5140\u662f\u653b\u4e0d \u8fdb\u53bb\u3002\u6218\u4e86\u7247\u523b\uff0c\u51cc\u672a\u98ce\u84e6\u5730\u5927\u559d\u4e00\u58f0\uff0c\u638c\u6cd5\u9aa4\u53d8\uff0c\u53f3\u624b\u6a2a\u638c\u5982\u5203\u3001\u5288\u3001\u6309\u3001\u64d2\u3002\u62ff\uff0c\u5c55\u5f00 \u4e86\u5929\u5c71\u64d2\u62ff\u624b\u4e2d\u6700\u5389\u5bb3\u7684\u622a\u624b\u6cd5\uff1b\u5de6\u624b\u5374\u9a88\u6307\u5982\u621f\uff0c\u7adf\u5728\u53e4\u5143\u4eae\u53cc\u638c\u7ffb\u98de\u4e4b\u4e2d\uff0c\u6b3a\u8eab\u76f4\u8fdb\uff0c \u627e\u5bfb\u7a74\u9053\u3002\u53e4\u5143\u4eae\u7684\u65ad\u638c\u6cd5\u7ed9\u4ed6\u7684\u622a\u624b\u6cd5\u514b\u4f4f\uff0c\u4e1d\u6beb\u65bd\u5c55\u4e0d\u5f97\uff0c\u800c\u51cc\u672a\u98ce\u7684\u5de6\u624b\uff0c\u5374\u5982\u540c\u637b \u7740\u4e00\u6280\u70b9\u7a74\u6485\uff0c\u6307\u5c16\u6240\u5230\uff0c\u4e5f\u5168\u662f\u6307\u5411\u53e4\u5143\u4eae\u7684\u4e09\u5341\u516d\u9053\u5927\u7a74\u3002\u8fd9\u6b63\u662f\u201c\u4ee5\u5176\u4eba\u4e4b\u9053\u8fd8\u6cbb\u5176 \u4eba\u4e4b\u8eab\u201d\uff0c\u53e4\u5143\u4eae\u662f\u70b9\u7a74\u540d\u5bb6\uff0c\u8bc6\u5f97\u5389\u5bb3\uff0c\u5fc3\u4e2d\u8d8a\u53d1\u5403\u60ca\uff0c\u51cc\u672a\u98ce\u4e5f\u771f\u201c\u635f\u201d\uff0c\u6bcf\u5411\u4e00\u5904\u7a74 \u9053\u70b9\u53bb\uff0c\u5c31\u5927\u559d\u4e00\u58f0\uff0c\u201c\u4e09\u91cc\u7a74\u201d\u3001\u201c\u6d8c\u6cc9\u7a74\u201d\u3001\u201c\u5929\u5143\u7a74\u201d\u2026\u2026\u53eb\u4e2a\u4e0d\u505c\uff0c\u597d\u50cf\u6545\u610f\u70b9\u9192 \u5bf9\u65b9\uff0c\u5360\u5143\u4eae\u5de6\u53f3\u8d8b\u907f\uff0c\u5168\u8eab\u90fd\u7ed9\u51b7\u6c57\u6e7f\u900f\uff0c\u65c1\u8fb9\u4eba\u770b\u6765\uff0c\u53ea\u89c1\u4ed6\u8e66\u8e66\u56e0\u8eba\u98de\uff0c\u5f62\u72b6\u5341\u5206\u6ed1 \u7a3d\u3002 \u3000\u3000\u695a\u662d\u5357\u8d8a\u770b\u8d8a\u4e0d\u662f\u5473\u513f\uff0c\u53eb\u9053\uff1a\u201c\u9000\u4e0b\uff01\u9000\u4e0b\u201d\u4ed6\u53cc\u638c\u4e00\u9519\uff0c\u6b63\u5f85\u4e0a\u524d\uff0c\u53ea\u542c\u5f97\u51cc\u672a\u98ce \u53c8\u662f\u4e00\u58f0\u5927\u559d\uff0c\u8eab\u5f62\u8fc5\u82e5\u72c2\u98ce\uff0c\u731b\u7684\u7ed5\u5230\u53e4\u5143\u4eae\u4e09\u80cc\u540e\uff0c\u53ea\u4e00\u6293\uff0c\u4fbf\u6293\u7740\u4e86\u53e4\u5143\u4eae\u53f3\u81c2\uff0c\u5de6 \u624b\u5728\u4ed6\u8170\u540e\u4e00\u6233\uff0c\u53e4\u5143\u4eae\u50cf\u6b7b\u86c7\u4e00\u6837\uff0c\u8f6f\u4f5c\u4e00\u56e2\u3002\u51cc\u672a\u98ce\u5728\u5927\u559d\u58f0\u4e2d\uff0c\u5c06\u53e4\u5143\u4eae\u731b\u63b7\u51fa\u53bb\u3002 \u695a\u662d\u5357\u4e00\u628a\u63a5\u7740\uff0c\u53ea\u89c1\u53e4\u5143\u4eae\u53cc\u7738\u7d27\u95ed\uff0c\u56db\u773c\u50f5\u786c\uff0c\u6025\u5fd9\u4f38\u624b\u5728\u4ed6\u7684\u201c\u4f0f\u5154\u7a74\u201d\u4e00\u62cd\uff0c\u53e4\u5143 \u4eae\u54c7\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u5410\u51fa\u4e00\u53e3\u6de4\u8840\uff0c\u8f6f\u762b\u5012\u5730\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u695a\u662d\u5357\u518d\u4e5f\u6309\u637a\u4e0d\u5728\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u5411\u51cc\u672a\u98ce\u6251\u53bb\u3002\u51cc\u672a\u98ce\u53cc\u80a9\u4e00\u8038\uff0c\u8f7b\u8f7b\u907f\u5f00\uff1b\u695a\u662d\u5357 \u62a2\u6b65\u4e0a\u524d\uff0c\u53cc\u638c\u53c8\u65cb\u98ce\u4e00\u6837\u5288\u53bb\uff0c\u51cc\u672a\u98ce\u4ecd\u7136\u4e0d\u63a5\uff0c\u4fa7\u8eab\u4e00\u51b2\uff0c\u7adf\u7fe9\u5982\u5de8\u9e70\uff0c\u4ece\u695a\u662d\u5357\u638c\u5e95 \u76f4\u94bb\u51fa\u53bb\u3002\u695a\u662d\u5357\u5927\u559d\u4e00\u58f0\uff0c\u7ffb\u8eab\u4e00\u6293\uff0c\u53cc\u638c\u64d2\u62ff\uff1b\u51cc\u672a\u98ce\u8d61\u7684\u7a9c\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u5982\u71d5\u7fc5\u659c \u5c55\uff0c\u4fa7\u8eab\u4e0b\u843d\u3002\u695a\u662d\u5357\u559d\u58f0\uff1a\u201c\u90a3\u91cc\u8d70\u201d\uff1f\u53c8\u8ffd\u4e0a\u6765\u3002\u51cc\u672a\u98ce\u51dd\u8eab\u6b62\u6b65\uff0c\u53cc\u76ee\u864e\u864e\u6709\u5a01\uff0c\u5927 \u58f0\u8bf4\u9053\uff1a\u201c\u4e14\u6162\u52a8\u624b\uff0c\u6211\u5c0a\u4f60\u662f\u5e08\u5144\uff0c\u8ba9\u4f60\u4e09\u62db\uff0c\u4f60\u82e5\u518d\u4e0d\u77e5\u8fdb\u9000\uff0c\u6211\u53ea\u597d\u4e0e\u4f60\u4e00\u51b3\u96cc\u96c4\u3002 \u6211\u82e5\u8f93\u4e86\uff0c\u4ece\u6b64\u56de\u8f6c\u5929\u5c71\uff0c\u4f60\u82e5\u8f93\u4e86\u53c8\u5982\u4f55\uff1f\u201d\u695a\u662d\u5357\u9053\uff1a\u201c\u820d\u5229\u5b50\u968f\u4f60\u62ff\u53bb\uff01\u201d\u51cc\u672a\u98ce \u9053\uff1a\u201c\u597d\uff0c\u53d1\u62db\u5427\uff01\u201d\u695a\u662d\u5357\u811a\u8e0f\u6d2a\u95e8\uff0c\u53cc\u638c\u631f\u7740\u52b2\u98ce\u55d6\u5730\u5411\u51cc\u672a\u51e4\u80f8\u819b\u6253\u53bb\uff01\u51cc\u672a\u98ce\u4e00\u638c \u683c\u5f00\uff0c\u4e24\u4eba\u98ce\u9a70\u7535\u63a3\u822c\u5730\u6253\u5c06\u8d77\u6765\u3002\u53ea\u89c1\u624b\u638c\u8d77\u5904\uff0c\u5168\u5e26\u52b2\u98ce\uff0c\u77f3\u7a9f\u5185\u591a\u5e74\u5806\u79ef\u7684\u5c18\u571f\uff0c\u7ed9 \u638c\u98ce\u9707\u8361\u5f97\u56db\u5904\u98de\u626c\uff0c\u5982\u9ed1\u96fe\u5f25\u6f2b\uff0c\u77f3\u7a9f\u672c\u5c31\u9634\u775b\uff0c\u8fd9\u4e00\u6765\u66f4\u663e\u5f97\u9634\u98ce\u60e8\u60e8\uff0c\u9a87\u76ee\u60ca\u5fc3\u3002\u901a \u9053\u4e0a\u70e7\u7740\u7684\u4e00\u5806\u706b\uff0c\u706b\u5149\u5728\u638c\u98ce\u70df\u96fe\u4e2d\u6447\u66f3\uff0c\u4f3c\u660e\u4f3c\u706d\uff0c\u65c1\u8fb9\u7684\u4eba\u90fd\u5c4f\u7740\u547c\u5438\uff0c\u5fc3\u5934\u4f3c\u7ed9\u91cd \u7269\u538b\u7740\uff0c\u900f\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u4e24\u4eba\u6253\u4e86\u4e00\u4f1a\uff0c\u84e6\u7136\u90fd\u5f80\u540e\u9000\u51fa\u51e0\u6b65\uff0c\u4f17\u4eba\u60ca\u8be7\u770b\u65f6\uff0c\u53ea\u89c1\u4e24\u4eba\u5706\u7741\u53cc\u773c\uff0c\u4f3c\u6597\u9e21\u4e00\u822c \u4e92\u76f8\u77aa\u89c6\u3002\u695a\u662d\u5357\u5927\u559d\u4e00\u58f0\uff0c\u5728\u51e0\u6b65\u4e4b\u5916\uff0c\u4e00\u638c\u5288\u51fa\uff0c\u51cc\u672a\u98ce\u53cc\u638c\u5408\u4ec0\uff0c\u4e5f\u662f\u9065\u9065\u4e00\u653e\uff1b\u4e24 \u4eba\u62f3\u6765\u811a\u5f80\uff0c\u4e2d\u95f4\u603b\u9694\u7740\u51e0\u6b65\u8ddd\u79bb\uff0c\u638c\u950b\u8fde\u8863\u88f3\u4e5f\u6cbe\u4e0d\u7740\uff0c\u800c\u4e14\u8d8a\u6253\u8d8a\u6162\uff0c\u5c31\u771f\u7684\u50cf\u4e24\u5e08\u5144 \u5f1f\u5728\u90a3\u91cc\u62c6\u62db\u7ec3\u5f0f\u4e00\u6837\u3002\u5218\u90c1\u82b3\u548c\u97e9\u5fd7\u90a6\u7b49\u90fd\u662f\u884c\u5bb6\uff0c\u65e9\u770b\u51fa\u4e24\u4eba\u6bcf\u4e00\u4e3e\u624b\u6295\u8db3\uff0c\u5168\u90fd\u6697\u85cf \u7740\u51e0\u4e2a\u53d8\u5316\uff0c\u867d\u7136\u9694\u7740\u51e0\u6b65\uff0c\u6bcf\u4e00\u62db\u6570\uff0c\u4e5f\u90fd\u5168\u662f\u5e26\u5b88\u5e26\u653b\uff0c\u5e94\u4ed8\u5bf9\u65b9\u7684\u3002\u8fd9\u79cd\u6700\u4e0a\u4e58\u7684\u638c \u6cd5\uff0c\u82e5\u662f\u54ea\u4e00\u65b9\u7a0d\u6709\u758f\u6f0f\uff0c\u5bf9\u65b9\u53ea\u8981\u8eab\u5f62\u5fae\u52a8\uff0c\u4fbf\u53ef\u7acb\u65bd\u6740\u624b\u3002 \u3000\u3000\u4e24\u4eba\u62c6\u4e86\u4e00\u767e\u591a\u62db\uff0c\u90fd\u662f\u7a0d\u6cbe\u5373\u8d70\uff0c\u4ecd\u7136\u5206\u4e0d\u51fa\u4e0a\u4e0b\u9ad8\u4f4e\u3002\u65c1\u8fb9\u7684\u4eba\u6b63\u770b\u5f97\u773c\u82b1\u7f2d\u4e71\u4e4b \u9645\uff0c\u84e6\u542c\u5f97\u51cc\u672a\u98ce\u4e5f\u662f\u4e00\u58f0\u5927\u559d\uff0c\u695a\u662d\u5357\u731b\u7684\u5411\u540e\u4fbf\u9000\uff0c\u51cc\u672a\u98ce\u8eab\u5f62\u8fc5\u5982\u72c2\u98d8\uff0c\u6b3a\u8eab\u76f4\u8fdb\uff0c \u53cd\u624b\u4e00\u638c\uff0c\u695a\u662d\u5357\u84e6\u7136\u5982\u5de8\u9e70\u4e0b\u6251\uff0c\u81ea\u4e0a\u4e00\u7eb5\u800c\u4e0b\uff0c\u53cc\u638c\u671d\u51cc\u672a\u98ce\u7684\u5929\u7075\u76d6\u76f4\u6309\u4e0b\u6765\u3002\u51cc\u672a \u98ce\u8feb\u5f97\u53cc\u638c\u5411\u4e0a\u4e00\u62b5\uff0c\u56db\u638c\u76f8\u4ea4\uff0c\u201c\u84ec\uff01\u84ec\uff01\u201d\u4e24\u58f0\uff0c\u4e24\u4eba\u7adf\u7ed9\u78b0\u8dcc\u4e00\u4e08\u5f00\u5916\u3002 \u3000\u3000\u539f\u6765\u695a\u662d\u5357\u4e60\u6b66\u7684\u65f6\u95f4\uff0c\u867d\u6bd4\u51cc\u672a\u98ce\u957f\uff0c\u4f46\u51cc\u672a\u98ce\u7ec3\u7684\u662f\u7ae5\u5b50\u529f\uff0c\u81ea\u5c0f\u5c31\u628a\u6839\u57fa\u624e\u597d\uff0c \u800c\u695a\u662d\u5357\u5c11\u5e74\u65f6\u66fe\u72c2\u5ad6\u7eb5\u996e\uff0c\u529f\u529b\u53cd\u5dee\u4e86\u4e00\u7b79\uff0c\u66f4\u52a0\u4e0a\u695a\u662d\u5357\u8fd1\u5e74\u5fd7\u5f97\u610f\u6ee1\uff0c\u7ec3\u4e60\u9042\u758f\uff0c\u9aa4 \u9047\u5f3a\u654c\uff0c\u867d\u7136\u529f\u529b\u5927\u81f4\u76f8\u5f53\uff0c\u4e5f\u8981\u53d7\u5236\u3002\u521a\u624d\u51cc\u672a\u98ce\u672c\u5df2\u8d62\u4e86\u4e00\u62db\uff0c\u6b63\u8981\u7eed\u65bd\u6740\u624b\uff0c\u4e0d\u6599\u695a \u662d\u5357\u5374\u8df3\u5728\u4f5b\u50cf\u7684\u624b\u6307\u4e0a\uff0c\u82e5\u7136\u8fd9\u4e00\u638c\u6253\u53bb\uff0c\u4f1a\u6bc1\u574f\u4f5b\u50cf\u3002\u51cc\u672a\u98ce\u6295\u9f20\u5fcc\u5668\uff0c\u4e0d\u6562\u635f\u4f24\u4e91\u5c97 \u77f3\u7a9f\u4e2d\u7684\u7470\u5b9d\uff0c\u53ea\u597d\u6025\u6025\u64a4\u638c\uff0c\u695a\u662d\u5357\u4e58\u52bf\u4ece\u4e0a\u538b\u4e0b\uff0c\u5360\u4e86\u4fbf\u5b9c\uff0c\u56e0\u6b64\u4e24\u4eba\u5728\u8868\u9762\u770b\u597d\u50cf\u6253 \u6210\u5e73\u624b\u3002 \u3000\u3000\u695a\u662d\u5357\u5fc3\u91cc\u660e\u767d\uff0c\u8fd9\u4f4d\u672a\u89c1\u8fc7\u9762\u7684\u5e08\u5f1f\uff0c\u529f\u529b\u786e\u6bd4\u81ea\u5df1\u8fd8\u9ad8\uff0c\u53c8\u6025\u53c8\u6012\u3002\u4f46\u5229\u7984\u718f\u5fc3\uff0c \u53c8\u4e0d\u80af\u7f62\u624b\u3002\u4ed6\u4ec6\u5730\u5373\u8d77\uff0c\u201c\u6e38\u9f99\u5251\u201d\u55d6\u7136\u51fa\u624b\uff0c\u5fae\u5e26\u5578\u58f0\u3002\u8fd9\u67c4\u5251\u524a\u94c1\u5982\u6ce5\uff0c\u662f\u5929\u5c71\u6d3e\u6240 \u4f20\u7684\u4e24\u628a\u5b9d\u5251\u4e4b\uff08\u53e6\u4e00\u628a\u662f\u77ed\u5251\uff0c\u4e3a\u6768\u4e91\u9a86\u6240\u5f97\uff0c\u6768\u6b7b\u540e\u5df1\u5f52\u6613\u5170\u73e0\uff09\u3002\u695a\u662d\u5357\u5728\u5251\u6cd5\u4e0a\u9020 \u8be3\u6700\u6df1\uff0c\u53c8\u4f8d\u6709\u5b9d\u5251\u5728\u624b\uff0c\u56e0\u6b64\u867d\u8f93\u4e86\u62db\uff0c\u4ecd\u662f\u4e00\u6d3e\u72c2\u50b2\uff0c\u8981\u548c\u51cc\u672a\u98ce\u6bd4\u5251\u3002 \u3000\u3000\u695a\u662d\u5357\u62d4\u5251\u51fa\u624b\uff0c\u7565\u4e00\u6325\u52a8\uff0c\u53ea\u89c1\u4e00\u7f15\u5bd2\u5149\uff0c\u7535\u95ea\u800c\u51fa\uff0c\u5218\u90c1\u82b3\u9a87\u7136\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u5b9d \u5251\uff01\u201d\u51cc\u672a\u98ce\u5168\u7136\u4e0d\u987e\uff0c\u63d0\u5de6\u811a\uff0c\u5012\u9752\u950b\uff0c\u6b3a\u8eab\u76f4\u8fdb\uff0c\u4e00\u5251\u65a9\u53bb\uff0c\u5251\u950b\u81ea\u4e0b\u5377\u4e0a\uff0c\u5012\u524a\u695a\u662d \u5357\u53f3\u81c2\uff0c\u8fd9\u662f\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u7edd\u9669\u4e4b\u62db\uff0c\u540d\u4e3a\u201c\u6781\u76ee\u6ca7\u6ce2\u201d\u3002\u695a\u662d\u5357\u81ea\u7136\u8bc6\u5f97\uff0c\u4ed7\u7740\u5b9d\u5251\u950b \u5229\uff0c\u4e5f\u4f7f\u51fa\u9669\u62db\uff0c\u970d\u5730\u584c\u8eab\uff0c\u201c\u9a6c\u9f99\u626b\u5730\u201d\uff0c\u5237\uff01\u5237\uff01\u5237\uff01\u4e00\u8fde\u4e09\u5251\uff0c\u5411\u51cc\u672a\u98ce\u4e0b\u76d8\u76f4\u626b\u8fc7 \u53bb\u3002\u51cc\u672a\u98ce\u7075\u5de7\u4e4b\u6781\uff0c\u8eab\u5f62\u5982\u7329\u733f\u8df3\u63b7\uff0c\u4e00\u8d77\u4e00\u843d\uff0c\u695a\u662d\u5357\u5251\u5251\u5728\u4ed6\u7684\u811a\u5e95\u626b\u8fc7\uff0c\u78b0\u4e5f\u6ca1\u6709 \u78b0\u7740\u3002\u695a\u662d\u5357\u521a\u4e00\u957f\u8eab\uff0c\u6b63\u53d8\u62db\uff0c\u51cc\u672a\u98ce\u77ac\u606f\u4e4b\u95f4\uff0c\u5c31\u4e00\u8fde\u653b\u4e86\u4e94\u5251\uff0c\u695a\u662d\u5357\u7ed9\u8feb\u5f97\u63aa\u624b\u4e0d \u53ca\uff0c\u8fde\u8fde\u540e\u9000\uff0c\u7adf\u65e0\u6687\u53bb\u524a\u4ed6\u7684\u5175\u5203\u3002 \u3000\u3000\u4f46\u695a\u662d\u5357\u5728\u5251\u6cd5\u4e0a\u6d78\u6deb\u4e86\u51e0\u5341\u5e74\uff0c\u81ea\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u4e00\u770b\u51cc\u672a\u98ce\u6253\u6cd5\uff0c\u5c31\u77e5\u9053\u4ed6\u662f\u4ee5\u5feb \u5236\u6162\uff0c\u7528\u6700\u8fc5\u6377\u7684\u5251\u6cd5\u6765\u8feb\u81ea\u5df1\u9632\u5b88\uff0c\u4f7f\u81ea\u5df1\u4e0d\u80fd\u5229\u7528\u5b9d\u5251\u7684\u6240\u957f\u3002\u4ed6\u51b7\u7b11\u4e00\u58f0\uff0c\u5ffd\u7136\u51dd\u8eab \u4e0d\u52a8\uff0c\u4e00\u53e3\u5251\u970d\u970d\u5730\u56db\u9762\u5c55\u5f00\uff0c\u5e7d\u6697\u7684\u77f3\u7a9f\u4e2d\uff0c\u767b\u65f6\u6d8c\u51fa\u4e00\u5708\u94f6\u8679\uff0c\u56de\u73af\u98de\u821e\u3002\u51cc\u672a\u98ce\u7684\u5251 \u662f\u666e\u901a\u5175\u5203\uff0c\u4e00\u78b0\u7740\u4fbf\u4f1a\u7ed9\u4ed6\u524a\u65ad\uff0c\u56e0\u6b64\u6839\u672c\u9012\u4e0d\u8fdb\u53bb\u3002\u800c\u4ed6\u5374\u5728\u94f6\u8679\u4e2d\u803f\u803f\u6ce8\u89c6\uff0c\u5bfb\u6687\u62b5 \u9699\u627e\u51cc\u672a\u98ce\u7684\u7834\u7efd\u3002 \u3000\u3000\u9163\u6597\u58f0\u4e2d\uff0c\u51cc\u672a\u98ce\u62bd\u5251\u540e\u9000\uff0c\u695a\u662d\u5357\u5927\u559d\u4e00\u58f0\uff0c\u633a\u5251\u523a\u51fa\uff0c\u5251\u5149\u5982\u7ec3\uff0c\u5411\u51cc\u672a\u98ce\u80cc\u540e\u6233 \u6765\u3002\u51cc\u672a\u98ce\u5ffd\u5730\u56de\u8f6c\u671d\u81e3\uff0c\u95ea\u7535\u822c\u5730\u4e3e\u5251\u4e00\u64a9\uff0c\u53ea\u542c\u5f97\u545b\u5577\u4e00\u58f0\uff0c\u548c\u695a\u662d\u5357\u7684\u5251\u78b0\u4e2a\u6b63\u7740\uff0c \u5218\u90c1\u82b3\u60ca\u53eb\u4e00\u58f0\uff0c\u4ee5\u4e3a\u8fd9\u756a\u51cc\u672a\u98ce\u5b9a\u96be\u5e78\u514d\uff0c\u4e0d\u6599\u54cd\u58f0\u8fc7\u540e\uff0c\u7a81\u7136\u975e\u5e38\u6c89\u5bc2\uff0c\u65e2\u65e0\u91d1\u94c1\u4ea4\u9e23 \u4e4b\u58f0\uff0c\u751a\u81f3\u8fde\u811a\u6b65\u58f0\u4e5f\u542c\u4e0d\u5230\u3002 \u3000\u3000\u539f\u6765\u51cc\u672a\u98ce\u8fd9\u56de\u8eab\u4e00\u5251\uff0c\u4fbf\u642d\u7740\u4e86\u695a\u662d\u5357\u7684\u5251\u810a\uff0c\u950b\u5203\u5e76\u4e0d\u89e6\u53ca\u3002\u695a\u662d\u5357\u7528\u529b\u4e00\u62bd\uff0c\u53ea \u89c9\u81ea\u5df1\u7684\u5251\u7adf\u4f3c\u7ed9\u7c98\u7740\u4e00\u6837\uff0c\u62bd\u4e0d\u51fa\u6765\uff01\u539f\u6765\u6666\u660e\u7985\u5e08\u91c7\u96c6\u5404\u6d3e\u5251\u6cd5\u4e4b\u957f\uff0c\u521b\u7acb\u5929\u5c71\u5251\u6cd5\uff0c \u8fd9\u4e00\u624b\u4fbf\u662f\u592a\u6781\u5251\u6cd5\u4e2d\u7684\u201c\u7c98\u201d\u5b57\u8bc0\u3002 \u3000\u3000\u695a\u662d\u5357\u81ea\u662f\u884c\u5bb6\uff0c\u77e5\u9053\u82e5\u786c\u8981\u62bd\u5251\uff0c\u5fc5\u5b9a\u7ed9\u51cc\u672a\u98ce\u5982\u5f71\u9644\u5f62\uff0c\u8fde\u7ef5\u4e0d\u65ad\u5730\u76f4\u653b\u8fc7\u6765\uff0c\u65e0 \u53ef\u5948\u4f55\uff0c\u53ea\u597d\u548c\u4ed6\u6597\u5185\u529f\uff0c\u82e6\u82e6\u7f20\u8feb\uff01 \u3000\u3000\u8fd9\u79cd\u6597\u5251\uff0c\u771f\u662f\u6b66\u6797\u7f55\u89c1\u3002\u77f3\u7a9f\u91cc\u9759\u5f97\u8fde\u7ee3\u82b1\u9488\u8dcc\u5728\u5730\u4e0a\u90fd\u80fd\u542c\u51fa\u58f0\u6765\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u53ea \u542c\u5f97\u695a\u662d\u5357\u53d1\u51fa\u5fae\u5fae\u7684\u5598\u606f\u4e4b\u58f0\uff0c\u989d\u4e0a\u5f00\u59cb\u6c81\u51fa\u6c57\u73e0\uff0c\u770b\u6765\u4e24\u5e08\u5144\u5f1f\uff0c\u5c31\u8981\u751f\u6b7b\u7acb\u5224\uff0c\u65e0\u6cd5 \u89e3\u6551\u3002 \u3000\u3000\u6b63\u5728\u4f17\u4eba\u5168\u795e\u8d2f\u6ce8\u4e4b\u9645\uff0c\u548c\u5587\u561b\u540c\u6765\u7684\u519b\u5b98\u2014\u2014\u695a\u662d\u5357\u7684\u8001\u642d\u6863\u5f20\u5929\u8499\uff0c\u5ffd\u7136\u6084\u4fcf\u5730\u6cbf \u7740\u77f3\u58c1\uff0c\u79fb\u8eab\u8d70\u8fd1\u4e00\u4e2a\u5587\u561b\uff0c\u84e6\u7136\u4f38\u6307\u4e00\u70b9\uff0c\u90a3\u5587\u561b\u5927\u53eb\u4e00\u58f0\uff0c\u7ffb\u8eab\u4fbf\u5012\u3002\u5f20\u5929\u8499\u4e00\u628a\u6293 \u7740\uff0c\u5728\u4ed6\u6000\u4e2d\u4e00\u638f\uff0c\u638f\u51fa\u4e00\u53ea\u64c5\u9999\u76d2\u5b50\uff0c\u72de\u7b11\u4e00\u58f0\uff0c\u95ea\u7535\u822c\u5730\u5411\u77f3\u7a9f\u5916\u9762\u9003\u53bb\uff01\u51e0\u4e2a\u5587\u561b\u5927 \u58f0\u72c2\u547c\uff1a\u201c\u820d\u5229\u5b50\uff0c\u7ed9\u52ab\u8d70\u4e86\uff01\u820d\u5229\u5b50\u7ed9\u52ab\u8d70\u4e86\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u5927\u53eb\u4e00\u58f0\uff0c\u5c06\u5251\u731b\u7684\u4e00\u62bd\uff0c\u8f6c\u8eab\u4fbf\u8ffd\u3002\u695a\u662d\u5357\u8eab\u5b50\u5411\u524d\u4e00\u503e\uff0c\u968f\u5373\u4e00\u8dc3\u800c\u8d77\uff0c\u5251 \u5149\u5982\u7ec3\uff0c\u4e5f\u72e0\u72e0\u5730\u81ea\u540e\u8d76\u6765\u3002\u8fd9\u65f6\u5f20\u5929\u8499\u5728\u524d\u9762\u72c2\u5954\uff0c\u4f17\u4eba\u5728\u540e\u9762\u7d27\u7d27\u8ffd\u8d76\u3002\u695a\u662d\u5357\u4e00\u9762\u8ffd \u4e00\u9762\u6325\u821e\u5b9d\u5251\uff0c\u97e9\u5fd7\u90a6\u7b49\u897f\u8fb9\u95ea\u907f\uff0c\u970e\u65f6\u5df2\u7ed9\u4ed6\u8d76\u5728\u524d\u5934\uff0c\u53ea\u662f\u603b\u8d8a\u4e0d\u8fc7\u51cc\u672a\u98ce\u3002 \u3000\u3000\u51cc\u672a\u98ce\u8f7b\u529f\u8d85\u5353\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u8d8a\u8fc7\u901a\u9053\uff0c\u51fa\u4e86\u77f3\u7a9f\uff0c\u8fd9\u65f6\u548c\u5f20\u5929\u8499\u8ddd\u79bb\u8d8a\u6765\u8d8a\u8fd1\uff0c\u4ed6 \u594b\u8eab\u4e00\u63a0\uff0c\u633a\u5251\u76f4\u5411\u5f20\u5929\u8499\u540e\u5fc3\u63b7\u53bb\uff0c\u5f20\u5929\u8499\u4e5f\u65e9\u5df2\u89e3\u51fa\u5175\u5203\uff0c\u4ed6\u6240\u7528\u7684\u662f\u4e00\u6761\u9f99\u7ede\u9501\u9aa8 \u97ad\uff0c\u64c5\u4e8e\u9501\u62ff\u5200\u5251\uff0c\u53c8\u53ef\u4f5c\u786c\u5175\u5668\u7528\uff0c\u4ed6\u548c\u695a\u662d\u5357\u5e76\u5217\u5434\u4e09\u6842\u5e10\u4e0b\uff0c\u6b66\u529f\u4e5f\u81ea\u4e0d\u5f31\uff0c\u542c\u5f97\u8111 \u540e\u98ce\u58f0\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u53cd\u624b\u5c31\u662f\u4e00\u97ad\uff0c\u51cc\u672a\u98ce\u7684\u5251\u7adf\u7136\u7ed9\u4ed6\u7f20\u7740\u3002\u5f20\u5929\u8499\u5927\u559c\uff0c\u8f6c\u8eab\u7528\u529b\u4e00 \u62c9\uff0c\u4e0d\u6599\u4e1d\u6beb\u6ca1\u6709\u62c9\u52a8\uff0c\u53cd\u7ed9\u51cc\u672a\u98ce\u5c06\u5251\u4e00\u633a\uff0c\u5251\u5c16\u76f4\u5411\u8109\u95e8\u5212\u6765\u3002\u5f20\u5929\u8499\u5927\u5403\u4e00\u60ca\uff0c\u6025\u6025 \u5c06\u624b\u4e00\u6296\uff0c\u9501\u9aa8\u97ad\u500f\u5730\u89e3\u5f00\uff0c\u51cc\u672a\u98ce\u7684\u5251\u5df2\u5982\u96f7\u9706\u51fb\u5230\u3002 \u3000\u3000\u51cc\u672a\u98ce\u8fd0\u5251\u5982\u51e4\uff0c\u5728\u957f\u97ad\u98de\u821e\u4e2d\u6b3a\u8eab\u76f4\u8fdb\u3002\u5f20\u5929\u8499\u62fc\u547d\u62b5\u6321\uff0c\u7ed9\u4ed6\u8feb\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u9000\u5230 \u4e86\u60ac\u5d16\u8fb9\u6cbf\uff0c\u53ea\u542c\u5f97\u6c34\u58f0\u8f70\u9e23\uff0c\u4e24\u4eba\u8eab\u65c1\uff0c\u4e00\u6761\u7011\u5e03\u51b2\u6cfb\u800c\u4e0b\uff0c\u800c\u4e0b\u9762\u5c31\u662f\u6df1\u4e0d\u53ef\u6d4b\u7684\u6851\u5e72 \u6cb3\u3002 \u3000\u3000\u4e24\u4eba\u52a8\u624b\u4e0d\u8fc7\u7247\u523b\uff0c\u695a\u662d\u5357\u5df2\u81ea\u8d74\u5230\uff0c\u5f20\u5929\u8499\u731b\u7684\u7528\u529b\u6253\u51fa\u51e0\u97ad\uff0c\u5411\u65c1\u4e00\u95ea\uff0c\u51cc\u672a\u98ce\u633a \u5251\u4fbf\u6251\uff0c\u5ffd\u89c1\u5f20\u5929\u8499\u5de6\u624b\u4e00\u626c\uff0c\u4e00\u4ef6\u4e1c\u897f\uff0c\u8d8a\u8fc7\u4e86\u51cc\u672a\u98ce\u76f4\u5411\u695a\u662d\u5357\u98de\u53bb\u3002\u51cc\u672a\u98ce\u8d77\u521d\u4ee5\u4e3a \u662f\u6697\u5668\uff0c\u4f46\u4e00\u542c\u98ce\u58f0\uff0c\u5df2\u77e5\u4e0d\u662f\uff0c\u800c\u4e14\u53c8\u4e0d\u662f\u5411\u81ea\u5df1\u6253\u6765\uff0c\u66f4\u611f\u60ca\u8be7\u3002\u8fd9\u65f6\u53ea\u542c\u5f97\u5f20\u5929\u8499\u4e00 \u58f0\u5927\u559d\uff1a\u201c\u63a5\u4f4f\uff01\u201d\u8ddf\u7740\u5bf9\u51cc\u672a\u98ce\u72de\u7b11\u9053\uff1a\u201c\u4f60\u628a\u6211\u6740\u4e86\u5427\uff01\u2018\u820d\u5229\u5b50\u2019\u4f60\u53ef\u4f11\u60f3\uff01\u201d\u51cc\u672a \u98ce\u970d\u7136\u9192\u8d77\uff0c\u56de\u8eab\u4e00\u8dc3\uff0c\u5411\u695a\u662d\u5357\u5954\u53bb\uff0c\u53ea\u89c1\u695a\u662d\u5357\u521a\u521a\u63a5\u4e86\u4e1c\u897f\uff0c\u6b63\u60f3\u6536\u5165\u6000\u4e2d\uff0c\u51cc\u672a\u98ce \u773c\u529b\u6781\u5f3a\uff0c\u5206\u660e\u770b\u51fa\u662f\u4e2a\u9526\u76d2\uff0c\u4ed6\u6025\u5f97\u5927\u543c\u4e00\u58f0\uff0c\u820d\u4e86\u5f20\u5929\u8499\uff0c\u633a\u5251\u76f4\u903c\u695a\u662d\u5357\uff0c\u5251\u6cd5\u8fc5\u6377 \u4e4b\u6781\uff0c\u970e\u5ffd\u5c31\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u8fd9\u65f6\u4f17\u4eba\u5df2\u9646\u7eed\u8d76\u5230\u3002\u5f20\u5929\u8499\u7eb7\u8dc3\u5982\u98de\uff0c\u767b\u4e0a\u4e00\u4e2a\u7a81\u51fa\u6765\u7684\u5c0f \u5c71\u5cf0\uff0c\u6b63\u597d\u5728\u695a\u662d\u5357\u548c\u51cc\u672a\u98ce\u7684\u5934\u9876\uff0c\u4ed6\u5c45\u9ad8\u4e34\u4e0b\uff0c\u5c06\u5c71\u77f3\u7528\u529b\u63a8\u4e0b\uff0c\u7830\u7830\u5de8\u54cd\uff0c\u6c99\u77f3\u7eb7 \u98de\uff0c\u6ce5\u571f\u98de\u626c\u4e2d\uff0c\u51e0\u5757\u5927\u5982\u78e8\u76d8\u5de8\u77f3\u6eda\u6eda\u800c\u4e0b\u3002\u695a\u662d\u5357\u548c\u51cc\u672a\u98ce\u5728\u7f20\u6597\u4e2d\u90fd\u65e0\u6cd5\u8eb2\u907f\uff0c\u53cc\u53cc \u5411\u524d\uff0c\u6eda\u5730\u846b\u82a6\u822c\u5730\u5411\u6851\u5e72\u6cb3\u9762\u76f4\u8dcc\u4e0b\u53bb\u3002\u51cc\u672a\u98ce\u6124\u6068\u4e4b\u6781\uff0c\u7a7a\u4e2d\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u5c06\u624b\u4e2d\u957f \u5251\u671d\u5c0f\u5c71\u5cf0\u8131\u624b\u63b7\u53bb\uff0c\u53ea\u542c\u5f97\u5f20\u5929\u8499\u54ce\u54df\u4e00\u58f0\uff0c\u7ed9\u51cc\u672a\u98ce\u957f\u5251\u523a\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u51cc\u672a\u98ce\u4f7f\u51fa\u7edd\u9876\u8f7b\u529f\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u5c06\u8fd1\u6cb3\u9762\uff0c\u53c8\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u53cc\u811a\u8f7b\u8f7b\u52fe\u4f4f\u6cb3\u8fb9 \u5ced\u58c1\u4e0a\u7a81\u51fa\u7684\u77f3\u7b0b\uff0c\u653e\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u695a\u662d\u5357\u7ed9\u7011\u5e03\u76f4\u51b2\u4e0b\u53bb\uff0c\u4ed6\u534a\u4e2a\u8eab\u5b50\u5df2\u6d78\u5165\u6c34\u4e2d\uff0c\u7528\u4e00 \u53ea\u624b\u62fc\u547d\u6293\u7740\u6cb3\u5cb8\u7684\u77f3\u5934\uff0c\u6323\u624e\u6b32\u8d77\uff0c\u8fd9\u5f62\u52bf\uff0c\u53cc\u65b9\u90fd\u662f\u5371\u9669\u4e4b\u6781\u3002 \u3000\u3000\u6b32\u77e5\u4e24\u4eba\u6027\u547d\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u4e94\u7ae0\u3000\u96be\u53d7\u6e29\u67d4\u3000\u5c82\u4e3a\u65b0\u77e5\u5fd8\u65e7\u597d\u3000\u60ca\u5fc3\u6076\u6597\u3000\u559c\u4ece\u65b9\u7a9f\u5f97\u771f\u7ecf \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u4e94\u56de \u96be\u53d7\u6e29\u67d4\u3000\u5c82\u4e3a\u65b0\u77e5\u5fd8\u65e7\u597d \u60ca\u5fc3\u6076\u6597\u3000\u559c\u4ece\u65b9\u7a9f\u5f97\u771f\u7ecf \u3000\u3000\u6b63\u5728\u6b64\u6781\u7aef\u7d27\u5f20\u4e4b\u9645\uff0c\u51cc\u672a\u98ce\u53cc\u8db3\u52fe\u7740\u5ced\u58c1\u7684\u77f3\u7b0b\uff0c\u7528\u529b\u4e00\u7ffb\uff0c\u8eab\u5b50\u5012\u6302\uff0c\u4f38\u624b\u4e00\u628a\u6293 \u7740\u695a\u662d\u5357\u9888\u9879\uff0c\u50cf\u6349\u5c0f\u9e21\u4e00\u6837\uff0c\u5c06\u4ed6\u63d0\u51fa\u6c34\u9762\uff0c\u695a\u662d\u5357\u867d\u6709\u5b9d\u5251\u5728\u624b\uff0c\u4f46\u521a\u624d\u7ed9\u767e\u4e08\u7011\u5e03\u51b2 \u51fb\u800c\u4e0b\uff0c\u65e9\u5df2\u4e4f\u529b\uff0c\u66f4\u517c\u534a\u622a\u8eab\u5b50\u6d78\u5728\u6c34\u4e2d\uff0c\u66f4\u662f\u65e0\u4ece\u62b5\u6321\uff0c\u51cc\u672a\u98ce\u4e00\u628a\u6293\u8d77\uff0c\u5288\u624b\u5c31\u593a\u4e86 \u4ed6\u7684\u5b9d\u5251\uff0c\u53cc\u624b\u53c9\u7740\u4ed6\u7684\u5589\u5499\uff0c\u695a\u662d\u5357\u5636\u54d1\u5730\u53eb\u4e86\u4e00\u58f0\uff0c\u65ad\u65ad\u7eed\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u7ed9\u4f60\u2018\u820d\u5229 \u4e8e\u2019\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u53cc\u624b\u677e\u5f00\u9053\uff1a\u201c\u62ff\u6765\u5427\u3002\u201d\u695a\u662d\u5357\u638f\u51fa\u6e7f\u6f09\u6f09\u7684\u6a80\u9999\u76d2\u5b50\uff0c\u51cc\u672a\u98ce \u4f38\u624b\u63a5\u8fc7\uff0c\u695a\u662d\u5357\u9762\u8272\u5341\u5206\u96be\u770b\uff0c\u8fd9\u8fd8\u662f\u4ed6\u6709\u751f\u4ee5\u6765\u7b2c\u4e00\u6b21\u8ba4\u8f93\u3002 \u3000\u3000\u51cc\u672a\u98ce\u6b63\u5f85\u62c9\u4ed6\u540c\u4e0a\u60ac\u5d16\uff0c\u84e6\u7136\u95f4\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u9053\u84dd\u706b\u7adf\u5728\u8eab\u8fb9\u70b8\u88c2\u5f00 \u6765\uff0c\u51cc\u672a\u98ce\u534a\u8eab\u60ac\u7a7a\uff0c\u6302\u5728\u60ac\u5d16\u4e4b\u4e0a\uff0c\u6839\u672c\u65e0\u4ece\u8eb2\u907f\uff0c\u80a9\u80cc\u7ed9\u706b\u7130\u707c\u5f97\u6eda\u70ed\uff0c\u9762\u4e0a\u4e5f\u7740\u4e86\u51e0 \u70b9\u706b\u661f\uff0c\u4ed6\u6025\u5fd9\u4e00\u624b\u6309\u7740\u77f3\u58c1\uff0c\u5c06\u8eab\u5b50\u5728\u77f3\u58c1\u4e0b\u4e00\u6eda\uff0c\u706b\u7130\u867d\u544a\u7184\u706d\uff0c\u4f46\u4ecd\u662f\u611f\u5230\u75bc\u75db\u3002\u695a \u662d\u5357\u8d81\u52bf\u7ffb\u8f6c\u8eab\u6765\uff0c\u4ef0\u671b\u7740\u51cc\u672a\u98ce\uff0c\u51cc\u672a\u98ce\u7741\u76ee\u5927\u559d\u4e00\u58f0\uff0c\u5c06\u62a2\u6765\u7684\u6e38\u9f99\u5251\u62d4\u5728\u624b\u4e2d\uff0c\u695a\u662d \u5357\u4e0d\u6562\u518d\u4e0a\uff0c\u8fd9\u65f6\u53ea\u542c\u5f97\u60ac\u5d16\u4e0a\u5608\u6210\u4e00\u7247\uff0c\u547c\u559d\u58f0\u548c\u5175\u5203\u78b0\u778c\u58f0\u4ea4\u6742\u4f20\u6765\u3002 \u3000\u3000\u8fd9\u679d\u86c7\u7130\u7bad\u662f\u548c\u695a\u662d\u5357\u540c\u6765\u7684\u536b\u58eb\u4e4b\u4e00\u90dd\u5927\u7ef6\u653e\u7684\uff0c\u548c\u695a\u662d\u5357\u540c\u6765\u7684\u4e24\u4e2a\u4eba\uff0c\u70b9\u7a74\u540d\u5bb6 \u53e4\u5143\u4eae\u5df2\u4e3a\u51cc\u672a\u98ce\u70b9\u6210\u6b8b\u5e9f\uff1b\u90dd\u5927\u7ef6\u5374\u6742\u5728\u4f17\u4eba\u4e4b\u4e2d\uff0c\u4e00\u540c\u8dd1\u51fa\u7a9f\u5916\uff0c\u4ed6\u89c1\u51cc\u672a\u98ce\u548c\u695a\u662d\u752b \u540c\u5815\u5d16\u4e0b\uff0c\u7adf\u53d6\u51fa\u6b79\u6bd2\u6697\u5668\u86c7\u7130\u7bad\u5411\u4e0b\u9762\u8098\u53bb\uff0c\u86c7\u7130\u7bad\u53d1\u65f6\u6709\u4e00\u9053\u84dd\u706b\uff0c\u89c1\u7269\u5373\u71c3\uff0c\u4e0d\u80fd\u7528 \u624b\u63a5\uff0c\u4e5f\u4e0d\u80fd\u7528\u5175\u5668\u78b0\u778c\uff0c\u53ea\u80fd\u907f\u5f00\uff0c\u4ed6\u8fd9\u4e00\u7bad\u662f\u7acb\u60f3\u5c06\u51cc\u672a\u98ce\u5c04\u6b7b\uff0c\u7eb5\u4fbf\u695a\u662d\u5357\u4e5f\u8bef\u4f24\u5728 \u5185\uff0c\u4e5f\u5728\u6240\u4e0d\u60dc\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u548c\u5218\u90c1\u82b3\u89c1\u4ed6\u5982\u6b64\u6b79\u6bd2\uff0c\u52c3\u7136\u5927\u6012\uff0c\u97e9\u5fd7\u90a6\u4e00\u6446\u516b\u5366\u7d2b\u91d1\u5200\u9996\u5148\u51b2\u4e0a\uff0c\u624d\u6253\u4e86\u6570 \u62db\uff0c\u5218\u90c1\u82b3\u5c31\u8131\u624b\u98de\u51fa\u72ec\u95e8\u6697\u5668\u9526\u4e91\u515c\uff0c\u5c06\u4ed6\u6293\u4f24\uff0c\u90dd\u5927\u7ef6\u624b\u4e2d\u5175\u5203\uff0c\u4e5f\u7ed9\u97e9\u5fd7\u90a6\u6253\u843d\uff0c\u4ed6 \u6d74\u8840\u62fc\u547d\u51b2\u51fa\uff0c\u624d\u8dd1\u4e86\u51e0\u6b65\uff0c\u5c31\u7ed9\u4e24\u4e2a\u5587\u561b\u8fce\u9762\u622a\u7740\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u53cc\u6251\u8fdb\uff0c\u4e00 \u4e2a\u77ee\u8eab\uff0c\u5404\u626f\u7740\u4ed6\u7684\u4e00\u6761\u817f\uff0c\u4f3c\u8361\u79cb\u5343\u4f3c\u7684\u5c06\u4ed6\u8361\u4e86\u8d77\u6765\uff0c\u8361\u4e86\u51e0\u8361\uff0c\u53c8\u662f\u4e00\u58f0\u5de8\u559d\uff0c\u5c06\u4ed6 \u629b\u843d\u60ac\u5d16\u3002 \u3000\u3000\u695a\u662d\u5357\u6b63\u5728\u60f6\u6025\uff0c\u5ffd\u89c1\u534a\u7a7a\u4e2d\u6389\u4e0b\u4e00\u4e2a\u4eba\u6765\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u4e5f\u4e0d\u7ba1\u662f\u654c\u662f\u53cd\uff0c\u4f38\u624b\u4e00\u628a\u63a5 \u7740\uff0c\u5411\u6c34\u9762\u4e00\u629b\uff0c\u4e58\u7740\u5c38\u4f53\u6d6e\u6c89\u4e4b\u9645\uff0c\u63d0\u4e00\u53e3\u6c14\uff0c\u7528\u8db3\u5185\u52b2\uff0c\u5411\u6c5f\u4e2d\u8dc3\u53bb\uff0c\u5355\u8db3\u4e00\u70b9\u5c38\u4f53\uff0c \u53c8\u662f\u62fc\u547d\u4e00\u8dc3\uff0c\u7adf\u7ed9\u4ed6\u8dc3\u5230\u79bb\u51cc\u672a\u98ce\u5341\u4f59\u4e08\u7684\u53e6\u4e00\u5904\u6cb3\u5d16\uff0c\u4ed6\u624b\u8db3\u5e76\u7528\uff0c\u4f3c\u733f\u7334\u822c\u7684\u722c\u4e0a\u4e86 \u5ced\u58c1\uff0c\u4e00\u6e9c\u70df\u5730\u9003\u4e86\u3002\u97e9\u5fd7\u90a6\u8fde\u53d1\u4e86\u51e0\u7c92\u94c1\u83b2\u5b50\uff0c\u90fd\u56e0\u8ddd\u79bb\u592a\u8fdc\uff0c\u6ca1\u6709\u6253\u7740\u3002 \u3000\u3000\u695a\u662d\u5357\u4e34\u5371\u9003\u8131\uff0c\u97e9\u5fd7\u90a6\u6068\u6781\u9a82\u9053\uff1a\u201c\u53c8\u4fbf\u5b9c\u4e86\u8fd9\u5978\u8d3c\uff01\u201d\u5218\u90c1\u82b3\u9053\uff1a\u201c\u4e0d\u5fc5\u7406\u4ed6\uff0c\u5148 \u770b\u7740\u51cc\u672a\u98ce\u5427\uff0c\u4eca\u665a\u53ef\u7d2f\u4e86\u4ed6\u4e86\uff01\u201d\u97e9\u5fd7\u90a6\u9ed8\u7136\u4e0d\u8bed\uff0c\u8d70\u8fd1\u5d16\u8fb9\uff0c\u53ea\u89c1\u6d6a\u6d9b\u62cd\u5cb8\uff0c\u5ced\u58c1\u4e0a\u6709 \u4e00\u4e2a\u9ed1\u5f71\u5728\u6162\u6162\u79fb\u52a8\u3002\u97e9\u5fd7\u90a6\u5c06\u591c\u884c\u4eba\u968f\u8eab\u643a\u5e26\u7684\u5343\u91cc\u706b\u6253\u5f00\uff0c\u5218\u90c1\u82b3\u5728\u706b\u5149\u4e2d\u770b\u89c1\u51cc\u672a\u98ce \u722c\u884c\u800c\u4e0a\uff0c\u663e\u5f97\u5f88\u662f\u8270\u96be\u3002\u5927\u5403\u4e00\u60ca\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u4ed6\u53d7\u4e86\u4f24\u4e86\uff0c\u7167\u4ed6\u5e73\u65e5\u7684\u529f\u592b\uff0c\u7edd\u4e0d\u4f1a \u8fd9\u4e2a\u6837\u5b50\uff01\u201d\u5979\u89e3\u4e0b\u201c\u9526\u4e91\u515c\u201d\u8f7b\u8f7b\u5730\u629b\u4e0b\u53bb\uff0c\u201c\u9526\u4e91\u515c\u201d\u662f\u6570\u4e08\u957f\u7684\u94a2\u7ef3\uff0c\u5c16\u7aef\u88c5\u7740\u5012\u987b \u94a2\u7f51\uff0c\u4f5c\u6697\u5668\u7528\u65f6\u53ef\u4ee5\u6293\u4eba\uff0c\u800c\u73b0\u5728\u5374\u6070\u597d\u662f\u6551\u4eba\u7684\u5de5\u5177\uff0c\u51cc\u672a\u98ce\u5df2\u722c\u4e0a\u4e00\u534a\uff0c\u5218\u90c1\u82b3\u53cc\u8db3 \u94a9\u7740\u5d16\u8fb9\uff0c\u63a2\u4e0b\u8eab\u5b50\uff0c\u5c06\u94a2\u7ef3\u8f7b\u8f7b\u4e00\u6446\uff0c\u6070\u597d\u89e6\u7740\u4e86\u51cc\u672a\u98ce\u7684\u624b\u6307\u3002\u51cc\u672a\u98ce\u4f38\u624b\u63e1\u7740\u3002\u5218\u90c1 \u82b3\u53eb\u58f0\uff1a\u201c\u5c0f\u5fc3\uff01\u201d\u7528\u529b\u4e00\u8361\uff0c\u94a2\u7ef3\u6296\u5f97\u7b14\u76f4\uff0c\u5c06\u51cc\u672a\u98ce\u5e73\u7a7a\u629b\u4e86\u8d77\u6765\uff0c\u51cc\u672a\u98ce\u50cf\u8361\u79cb\u5343\u4f3c \u7684\uff0c\u63e1\u7740\u94a2\u7ef3\uff0c\u8d8a\u8361\u8d8a\u9ad8\uff0c\u5218\u90c1\u82b3\u4e00\u7f29\u8eab\u8eaf\uff0c\u5c06\u94a2\u7ef3\u4e00\u5377\uff0c\u628a\u51cc\u672a\u98ce\u8f7b\u8f7b\u653e\u5728\u5730\u4e0a\uff0c\u81ea\u5df1\u4e5f \u7ad9\u4e86\u8d77\u6765\u3002\u51e0\u4e2a\u5587\u561b\u9f50\u58f0\u8d5e\u9053\uff1a\u201c\u771f\u597d\u81c2\u529b\u3002\u201d\u4ed6\u4eec\u4e0d\u77e5\u5218\u90c1\u82b3\u4f7f\u7684\u4e43\u662f\u5de7\u52b2\u3002 \u3000\u3000\u5218\u90c1\u82b3\u987e\u4e0d\u5f97\u56de\u7b54\uff0c\u6276\u7740\u51cc\u672a\u98ce\u7ec6\u770b\uff0c\u53ea\u89c1\u4ed6\u80a9\u80cc\u5df2\u7ed9\u70e7\u5f97\u6b8b\u7834\uff0c\u808c\u8089\u53d8\u5f97\u6de4\u7ea2\uff0c\u51cc\u672a \u98ce\u8f6c\u8fc7\u9762\u6765\uff0c\u5587\u561b\u4eec\u5f00\u58f0\u60ca\u53eb\uff0c\u4ed6\u7684\u8138\u672c\u6765\u5c31\u6709\u4e24\u9053\u5200\u75d5\uff0c\u73b0\u5728\u52a0\u4e0a\u786b\u78fa\u706b\u70e7\u5f97\u53c8\u9ed1\u53c8\u80bf\uff0c \u66f4\u663e\u5f97\u5341\u5206\u53ef\u6015\u3002\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u96be\u770b\u4e86\uff0c\u66f4\u4e11\u602a\u4e00\u70b9\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u201d\u5218\u90c1\u82b3\u9053\uff1a \u201c\u4f60\u89c9\u5f97\u600e\u6837\uff1f\u201d\u51cc\u672a\u98ce\u786c\u633a\u7740\u9053\uff1a\u201c\u4e0d\u8fc7\u70e7\u7834\u4e86\u70b9\u76ae\u8089\uff0c\u6ca1\u6709\u4ec0\u4e48\uff1f\u201d\u4ed6\u968f\u8bf4\u968f\u628a\u6a80\u9999\u76d2 \u5b50\u638f\u4e86\u51fa\u6765\uff0c\u9012\u7ed9\u4e00\u4e2a\u5587\u561b\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6253\u4e86\u534a\u591c\uff0c\u8fd8\u5e78\u628a\u4f60\u4eec\u7684\u2018\u820d\u5229\u4e8e\u2019\u593a\u4e86\u56de \u6765\uff01\u201d\u5587\u561b\u4eec\u9f50\u9f50\u62dc\u8c22\u3002\u4e3a\u9996\u7684\u5587\u561b\uff0c\u5f88\u662f\u5c0f\u5fc3\uff0c\u5c06\u64c5\u9999\u76d2\u5b50\u6253\u4e86\u5f00\u6765\uff0c\u53ea\u89c1\u91cc\u9762\u6709\u51e0\u7c92\u73cd \u73e0\u822c\u7684\u4e1c\u897f\uff0c\u5410\u51fa\u5149\u8292\u3002\u5587\u561b\u7ec6\u770b\u4e00\u756a\uff0c\u5ffd\u7136\u5927\u60ca\u5931\u8272\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u820d\u5229\u5b50\uff0c\u7ed9\u4ed6\u4eec\u6389\u6362 \u4e86\uff01\u201d\u51cc\u672a\u98ce\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u8fd9\u4e0d\u662f\u2018\u820d\u5229\u5b50\u2019\uff1f\u201d\u5587\u561b\u9053\uff1a\u201c\u8fd9\u662f\u73cd\u73e0\uff0c \u2018\u820d\u5229\u4e8e\u2019\u6ca1\u6709\u8fd9\u6837\u900f\u660e\u5149\u4eae\uff01\u201d \u3000\u3000\u539f\u6765\u5f20\u5929\u8499\u7d20\u5de5\u5fc3\u8ba1\uff0c\u4ed6\u5728\u5434\u4e09\u6842\u5c06\u793c\u7269\u4ea4\u7ed9\u5587\u561b\u4eec\u65f6\uff0c\u89c1\u8fc7\u201c\u820d\u5229\u4e8e\u201d\u7684\u6a21\u6837\uff0c\u4ed6\u5c31 \u6109\u5077\u9020\u4e86\u4e00\u4e2a\u540c\u6837\u5927\u5c0f\u7684\u6a80\u9999\u76d2\u5b50\uff0c\u91cc\u9762\u653e\u4e0a\u73cd\u73e0\u3002\u4ed6\u672c\u6765\u662f\u51c6\u5907\u5728\u8def\u4e0a\u4e07\u4e00\u6709\u4eba\u52ab\u593a\u65f6\uff0c \u53ef\u4ee5\u62ff\u6765\u9876\u5305\u3002\u5f53\u665a\u4ed6\u542c\u695a\u662d\u5357\u4e00\u8bf4\uff0c\u4e5f\u8d77\u4e86\u80cc\u53db\u5434\u4e09\u6842\u4e4b\u5fc3\uff0c\u56e0\u6b64\u4ed6\u5728\u695a\u662d\u5357\u5371\u6025\u65f6\uff0c\u5148 \u52ab\u4e86\u5587\u561b\u7684\u201c\u820d\u5229\u5b50\u201d\uff0c\u51c6\u5907\u62ff\u53bb\u732e\u7ed9\u7687\u4e0a\u9080\u529f\u3002\u540e\u6765\u4ed6\u88ab\u51cc\u672a\u98ce\u8feb\u5f97\u65e0\u8def\u53ef\u8d70\u65f6\uff0c\u53c8\u5de7\u4f7f \u201c\u91d1\u8749\u8131\u58f3\u201d\u4e4b\u8ba1\uff0c\u5c06\u5047\u7684\u201c\u820d\u5229\u5b50\u201d\u629b\u7ed9\u695a\u662d\u5357\uff0c\u8f6c\u79fb\u4e86\u51cc\u672a\u98ce\u7684\u76ee\u6807\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5f53\u4e0b\u505a\u58f0\u4e0d\u5f97\uff0c\u72e0\u72e0\u8bf4\u9053\uff1a\u201c\u518d\u78b0\u5230\u8fd9\u8d3c\u5b50\u5b9a\u8981\u5265\u4ed6\u7684\u76ae\uff01\u201d\u4ed6\u53c8\u5411\u5587\u561b\u4eec\u81f4 \u6b49\u3002\u5587\u561b\u4eec\u5f88\u4e0d\u597d\u610f\u601d\uff0c\u518d\u4e09\u62dc\u8c22\uff0c\u8bf4\u9053\uff1a\u201c\u867d\u7136\u593a\u56de\u7684\u662f\u5047\u2018\u820d\u5229\u5b50\uff1f\u201d\u4f46\u51cc\u672a\u98ce\u5374\u820d\u4e86 \u6027\u547d\u4e3a\u6211\u4eec\u5c3d\u529b\uff0c\u6b64\u6069\u6b64\u5fb7\uff0c\u6c38\u4e16\u4e0d\u5fd8\uff01\u201d\u4ed6\u4eec\u89c1\u51cc\u672a\u98ce\u4f24\u91cd\uff0c\u53c8\u6025\u4e8e\u8981\u56de\u85cf\u62a5\u544a\uff0c\u4e0d\u613f\u518d \u6270\u51cc\u672a\u98ce\uff0c\u9f50\u9f50\u544a\u8f9e\uff0c\u8d81\u7740\u62c2\u6653\u8d76\u8def\u3002 \u3000\u3000\u5218\u90c1\u82b3\u548c\u97e9\u5fd7\u90a6\u6276\u7740\u51cc\u672a\u51e4\u8d70\u56de\u77f3\u7a9f\uff0c\u4e00\u8fdb\u4e86\u6d1e\uff0c\u51cc\u672a\u98ce\u5c31\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u5750\u5728\u5730\u4e0a\u3002 \u5218\u90c1\u82b3\u6025\u5fd9\u8fc7\u53bb\uff0c\u6276\u7740\u4ed6\u9053\uff1a\u201c\u600e\u4e48\u5566\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4f60\u628a\u6211\u7684\u884c\u56ca\u62ff\u6765\uff01\u201d\u4ed6\u5728\u884c\u56ca\u4e2d \u53d6\u51fa\u4e24\u7c92\u78a7\u7eff\u8272\u7684\u4e39\u4e38\uff0c\u4e00\u53e3\u54bd\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4e8b\u5566\uff0c\u90a3\u5c0f\u5b50\u7684\u86c7\u7130\u7bad\u662f\u786b\u78fa\u706b\uff0c\u706b\u6bd2\u653b \u5fc3\uff0c\u6709\u70b9\u96be\u53d7\uff0c\u8fd9\u4e39\u4e38\u662f\u5929\u5c71\u96ea\u83b2\u914d\u6210\uff0c\u6b63\u597d\u53ef\u89e3\u706b\u6bd2\u3002\u201d\u5218\u90c1\u82b3\u8fd8\u4e0d\u653e\u5fc3\uff0c\u89c1\u4ed6\u9762\u4e0a\u70e7\u8d77 \u8bb8\u591a\u706b\u6ce1\uff0c\u53c8\u5c06\u81ea\u5df1\u968f\u8eab\u643a\u5e26\u7684\u6cbb\u5916\u4f24\u7684\u836f\u818f\u7ed9\u4ed6\u6d82\u62b9\u3002\u51cc\u672a\u98ce\u626d\u8f6c\u4e86\u5934\uff0c\u4f3c\u4e4e\u5f88\u4e0d\u613f\u610f\u3002 \u5218\u90c1\u82b3\u4ee5\u4e3a\u4ed6\u907f\u5acc\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u6c5f\u6e56\u4eba\u7269\uff0c\u4e0d\u8bb2\u8fd9\u5957\u3002\u201d\u5979\u4e00\u624b\u5c06\u51cc\u672a\u98ce\u6309\u7740\uff0c\u67d4\u58f0\u8bf4 \u9053\uff1a\u201c\u4e0d\u8bb8\u52a8\uff0c\u75c5\u4eba\u5e94\u8be5\u542c\u8bdd\uff1b\u4f60\u4e0d\u542c\u8bdd\u6211\u53ef\u751f\u6c14\u5566\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u95ed\u7740\u773c\u775b\uff0c\u8ba9\u5979\u6d82\u62b9\u3002\u5ffd\u7136\u95f4\u5218\u90c1\u82e5\u53cc\u624b\u98a4\u6296\uff0c\u4e00\u74f6\u836f\u818f\uff0c\u535c\u7684\u8dcc\u843d\u5730\u4e0a\uff0c\u97e9\u5fd7 \u90a6\u9053\uff1a\u201c\u4f60\u7d2f\u5566\uff1f\u6211\u66ff\u4f60\u643d\u5427\uff01\u201d\u51cc\u672a\u98ce\u7ffb\u8f6c\u8eab\u5b50\uff0c\u5c06\u5934\u6795\u5728\u81c2\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u90fd\u8bf4\u4e0d\u7528\u7406 \u5b83\u4e86\u3002\u201d\u5218\u90c1\u82b3\u9ed8\u7136\u4e0d\u8bed\uff0c\u51dd\u5750\u5982\u77f3\u50cf\uff0c\u773c\u775b\u5982\u5b9a\u73e0\uff0c\u7d27\u7d27\u76ef\u7740\u51cc\u672a\u98ce\u7684\u9762\u5b54\uff0c\u826f\u4e45\u826f\u4e45\uff0c \u7a81\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u524d\u4e00\u5b9a\u4e0d\u662f\u8fd9\u4e2a\u6837\u5b50\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u81ea\u7136\u4e0d\u662f\uff0c\u6211\u53d7\u4e86\u5200\u4f24\uff0c\u53c8\u53d7\u4e86\u706b\u70e7\uff0c\u672c\u6765\u662f\u4e11\u964b\u4e86\u3002\u201d\u5218\u90c1\u82b3\u6447\u6447\u5934 \u9053\uff1a\u201c\u4e0d\u5bf9\uff01\u8fd9\u56de\u6211\u53ef\u770b\u5f97\u975e\u5e38\u4ed4\u7ec6\uff0c\u4f60\u4ee5\u524d\u4e00\u5b9a\u957f\u5f97\u5f88\u4fca\uff0c\u800c\u4e14\u8fd8\u50cf\u6211\u7684\u4e00\u4f4d\u676d\u5dde\u53cb \u4eba\uff01\u201d\u97e9\u5fd7\u90a6\u51b7\u51b7\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u51cc\u672a\u98ce\u4e00\u9635\u72c2\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6839\u672c\u6ca1\u6709\u5230\u8fc7\u676d\u5dde\uff01\u201d\u8fd9\u7b11\u58f0 \u539f\u5c31\u662f\u63a9\u9970\u4ed6\u5185\u5fc3\u7684\u7a98\u8feb\u3002\u5218\u90c1\u82b3\u5c06\u4fe1\u5c06\u7591\uff0c\u5ffd\u7136\u53d1\u89c9\u97e9\u5fd7\u90a6\u4e5f\u7d27\u7d27\u5730\u76ef\u7740\u5979\uff0c\u795e\u60c5\u4e0d\u60a6\u3002 \u5979\u970d\u7136\u9192\u8d77\uff0c\u5982\u679c\u51cc\u672a\u51e4\u4e0d\u662f\u90a3\u4eba\uff0c\u81ea\u5df1\u8c08\u8bba\u4e00\u4e2a\u7537\u4eba\u7684\u7f8e\u4e11\uff0c\u53ef\u771f\u5931\u6389\u603b\u8235\u4e3b\u7684\u8eab\u4efd\uff0c\u4e5f \u7ed9\u97e9\u5fd7\u90a6\u770b\u8f7b\u4e86\u3002\u5979\u9762\u4e0a\u4e00\u9635\u70ed\uff0c\u4e5f\u5e72\u7b11\u9053\uff1a\u201c\u6211\u662f\u5947\u602a\u4f60\u7684\u6b66\u529f\u8fd9\u6837\u9ad8\u5f3a\uff0c\u600e\u4f1a\u9762\u4e0a\u5e26\u6709 \u5200\u75d5\uff1f\u201d\u5979\u4ed3\u4fc3\u4e4b\u95f4\uff0c\u6324\u51fa\u8bdd\u6765\uff0c\u7adf\u6ca1\u60f3\u5230\u642d\u4e0d\u4e0a\u539f\u5148\u7684\u8bdd\u9898\uff0c\u97e9\u5fd7\u90a6\u53c8\u662f\u51b7\u51b7\u5730\u54fc\u4e86\u4e00 \u58f0\u3002 \u3000\u3000\u51cc\u672a\u98ce\u7b54\u9053\uff1a\u201c\u8fd9\u5200\u75d5\u662f\u6211\u521a\u5230\u56de\u7586\u7684\u65f6\u5019\uff0c\u78b0\u4e0a\u6768\u4e91\u9a86\u5927\u4fa0\u7684\u4e00\u4e2a\u4ec7\u4eba\uff0c\u4ed6\u89c1\u6211\u5e26\u7740 \u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u968f\u624b\u5c31\u7ed9\u6211\u4e00\u5200\uff0c\u8981\u4e0d\u662f\u6709\u4eba\u642d\u6551\uff0c\u51e0\u4e4e\u7ed9\u4ed6\u6bc1\u4e86\uff01\u201d\u5218\u90c1\u82b3\u542c\u5f97\u5341\u5206\u5947\u602a\uff0c \u95ee\u9053\uff1a\u201c\u6768\u5927\u4fa0\u7684\u4ec7\u4eba\u548c\u4f60\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u4f60\u53c8\u4e3a\u4ec0\u4e48\u5e26\u4e00\u4e2a\u5973\u5b69\u5b50\u8fdc\u8fdc\u8dd1\u53bb\u56de\u7586\uff1f\u90a3\u4e2a\u5973\u5b69 \u5b50\u6709\u591a\u5927\u4e86\uff1f\u201d\u51cc\u672a\u98ce\u4e00\u8bf4\u4e4b\u540e\uff0c\u81ea\u77e5\u5931\u8a00\uff0c\u5fd9\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u5c06\u6765\u6211\u518d\u5bf9\u4f60\u8bf4\u3002\u90a3\u4e2a\u5973 \u5b69\u5b50\u53ea\u6709\u4e24\u5c81\u3002\u201d\u97e9\u5fd7\u90a6\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u53ea\u6709\u4e24\u5c81\uff0c\u5218\u8235\u4e3b\uff0c\u4f60\u2026\u2026\u4f60\u53ef\u6ca1\u6709\u4ec0\u4e48\u8bdd\u8bf4\u4e86\uff01\u201d \u4ed6\u672c\u6765\u60f3\u8bf4\uff1a\u201c\u4f60\u53ef\u653e\u5fc3\u4e86\u3002\u201d\u4e00\u5230\u53e3\u8fb9\uff0c\u53ef\u60f3\u8d77\u4e0d\u80fd\u8fd9\u6837\u51b2\u72af\u5218\u90c1\u82b3\uff0c\u8fd9\u624d\u4e34\u65f6\u6539\u4e86\u3002\u9976 \u662f\u8fd9\u6837\uff0c\u5218\u90c1\u82b3\u8fd8\u662f\u767d\u4e86\u4ed6\u4e00\u773c\uff0c\u5979\u5f88\u4e0d\u5f00\u5fc3\uff0c\u4e5f\u5f88\u5947\u602a\u97e9\u5fd7\u90a6\u7684\u795e\u6001\u4f3c\u4e4e\u6709\u70b9\u5931\u5e38\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\uff0c\u51cc\u672a\u98ce\u7684\u4f24\u52bf\uff0c\u679c\u7136\u597d\u5f97\u591a\uff0c\u5df2\u7ecf\u53ef\u4ee5\u8d70\u52a8\u4e86\uff0c\u5218\u90c1\u82b3\u8fd8\u662f\u6bb7\u52e4\u5730\u770b\u62a4\u7740\u4ed6\u3002 \u97e9\u5fd7\u90a6\u5374\u7ec8\u65e5\u5be1\u8a00\u5be1\u7b11\u3002\u7b2c\u4e09\u65e5\u65e9\u6668\uff0c\u5218\u90c1\u82b3\u4e00\u89c9\u9192\u6765\uff0c\u7adf\u7136\u4e0d\u89c1\u4e86\u97e9\u5fd7\u90a6\u7684\u8e2a\u8ff9\uff0c\u53ea\u89c1\u5c18 \u571f\u4e0a\u6709\u4eba\u7528\u624b\u6307\u5199\u7740\u51e0\u884c\u6b6a\u6b6a\u659c\u659c\u7684\u5927\u5b57\u3002 \u3000\u3000\u90a3\u51e0\u884c\u6b6a\u6b6a\u659c\u659c\u7684\u5927\u5b57\u5199\u9053\uff1a\u201c\u54b1\u662f\u4e00\u4e2a\u7c97\u4eba\uff0c\u4e0d\u61c2\u89c4\u77e9\uff1b\u867d\u5c5e\u65e7\u4ea4\uff0c\u4e0d\u5982\u65b0\u77e5\uff1b\u5929\u5730 \u4f1a\u4e4b\u4e8b\uff0c\u6709\u543e\u59d0\u4e3b\u6301\u4e0e\u51cc\u82f1\u96c4\u76f8\u52a9\uff0c\u5927\u6709\u53ef\u4e3a\uff0c\u6210\u529f\u53ef\u671f\u3002\u4ece\u6b64\u544a\u8f9e\uff0c\u76fc\u671b\u73cd\u91cd\u3002\u201d\u62ac\u5934\u4e00 \u884c\u5199\u7740\uff1a\u201c\u62dc\u4e0a\u5218\u603b\u8235\u4e3b\u201d\uff1b\u4e0b\u9762\u7f72\u540d\u201c\u7c97\u4eba\u97e9\u5fd7\u90a6\u201d\u3002\u5218\u90c1\u82b3\u770b\u4e86\uff0c\u9eef\u7136\u4e0d\u8bed\uff0c\u51cc\u672a\u98ce \u9053\uff1a\u201c\u4ed6\u5012\u662f\u4e2a\u8c6a\u723d\u7684\u6c49\u5b50\u3002\u53ea\u662f\u8bef\u4f1a\u592a\u591a\u4e86\u6211\u8fd9\u4e2a\u2018\u65b0\u77e5\u2019\u672c\u5c31\u65e0\u5fc3\u758f\u95f4\u2018\u65e7\u4ea4\u2019\uff01\u201d\u5218 \u90c1\u82b3\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4ed6\u7684\u5fc3\u773c\u513f\u4e5f\u592a\u591a\u4e86\uff0c\u6211\u62c5\u5fc3\u4ed6\u4e00\u4e2a\u4e71\u95ef\uff0c\u96be\u4fdd\u4e0d\u51fa\u5c94\u5b50\u3002\u201d\u662f\u4e0d\u77e5 \u4ed6\u8d70\u5411\u4f55\u65b9\uff0c\u51cc\u672a\u98ce\u53c8\u662f\u4f24\u52bf\u521d\u6108\uff0c\u66f4\u662f\u65e0\u6cd5\u5bfb\u627e\u3002 \u3000\u3000\u518d\u8bf4\u97e9\u5fd7\u90a6\u90a3\u65e5\u53d7\u4e86\u5218\u90c1\u82b3\u767d\u773c\uff0c\u6108\u60f3\u6108\u4e0d\u662f\u5473\u513f\u3002\u5f53\u665a\u7ffb\u6765\u8986\u53bb\uff0c\u6574\u591c\u5143\u7720\uff0c\u60f3\u81ea\u5df1 \u4e00\u4e2a\u201c\u7c97\u4eba\u201d\uff0c\u6b66\u827a\u4e0e\u51cc\u672a\u98ce\u53c8\u662f\u76f8\u53bb\u751a\u8fdc\uff0c\u5982\u4f55\u914d\u5f97\u4e0a\u5979\u3002\u4ed6\u5fc3\u4e2d\u672c\u6765\u6124\u6124\u4e0d\u5e73\uff0c\u57cb\u6028\u5218 \u90c1\u82b3\u521a\u4ea4\u4e0a\u4e00\u4e2a\u201c\u65b0\u670b\u53cb\u201d\uff0c\u5c31\u628a\u591a\u5e74\u7684\u201c\u8001\u670b\u53cb\u201d\u51b7\u6de1\uff1b\u8fd9\u6837\u4e00\u60f3\uff0c\u53cd\u89c9\u5e73\u9759\u4e0b\u6765\u3002\u4ed6\u5fc3 \u4e2d\u6697\u9053\uff1a\u4f55\u5fc5\u5728\u4ed6\u4eec\u4e2d\u95f4\uff0c\u505a\u4e00\u4e2a\u62e6\u8def\u77f3\u5934\uff0c\u4e8e\u662f\u4e0d\u5f97\u5929\u660e\uff0c\u62ab\u8863\u4fbf\u8d77\uff0c\u770b\u7740\u4ed6\u4eec\u7761\u5f97\u6b63 \u9163\uff0c\u6697\u6697\u53f9\u53e3\u6c14\uff0c\u80cc\u597d\u884c\u56ca\uff0c\u6302\u597d\u5175\u5668\uff0c\u72ec\u4e2a\u513f\u8d70\u51fa\u7a9f\u5916\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u8ff7\u8ff7\u832b\u832b\uff0c\u4e5f\u4e0d\u77e5\u8be5\u8d70\u5411\u4f55\u65b9\uff0c\u4ed6\u4fe1\u6b65\u6240\u4e4b\uff0c\u5728\u5c71\u5c97\u6f2b\u65e0\u76ee\u7684\u5730\u4e71\u8dd1\uff0c\u8fd9\u65f6\u6668\u9732 \u672a\u5e72\uff0c\u6653\u98ce\u62c2\u9762\uff0c\u884c\u8d70\u95f4\uff0c\u5ffd\u542c\u5f97\u54df\u54df\u9e7f\u9e23\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u5934\u6885\u82b1\u5c0f\u9e7f\uff0c\u5728\u5c71\u6eaa\u65c1\u8fb9\u996e \u6c34\u3002\u996e\u4e86\u4e00\u4f1a\uff0c\u53c8\u54a9\u54a9\u4e71\u53eb\u3002\u97e9\u5fd7\u90a6\u5fc3\u60f3\uff1a\u8fd9\u5934\u5c0f\u9e7f\uff0c\u5b64\u96f6\u96f6\u7684\u5728\u8fd9\u91cc\u996e\u6c34\uff0c\u4e00\u5b9a\u662f\u5931\u4e86\u6bcd \u4eb2\u7684\u79bb\u7fa4\u5c0f\u9e7f\uff0c\u771f\u662f\u53ef\u601c\u3002\u4ed6\u80e1\u601d\u4e71\u60f3\uff0c\u6162\u6162\u5730\u8d70\u8fc7\u53bb\uff0c\u81ea\u8a00\u81ea\u8bed\u4ed6\u8bf4\u9053\uff1a\u201c\u5c0f\u9e7f\uff0c\u5c0f\u9e7f\uff0c \u6211\u4e5f\u662f\u4e2a\u6ca1\u6709\u670b\u53cb\u7684\u4eba\uff0c\u4f60\u4e0d\u8d5a\u5f03\uff0c\u6211\u548c\u4f60\u505a\u4e2a\u670b\u53cb\u5427\u3002\u201d \u3000\u3000\u80e1\u601d\u4e71\u60f3\u95f4\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u517d\u543c\uff0c\u5728\u6811\u6797\u8349\u83bd\u4e4b\u4e2d\uff0c\u8dd1\u51fa\u4e86\u4e00\u53ea\u91d1\u94b1\u5927\u8c79\uff0c\u4e00\u58f0\u72c2\u543c\uff0c\u817e \u7a7a\u7a9c\u8d77\uff0c\u5411\u90a3\u5934\u5c0f\u9e7f\u6251\u53bb\uff0c\u97e9\u5fd7\u90a6\u5927\u6012\uff0c\u9a82\u9053\uff1a\u201c\u5c0f\u9e7f\u8fd9\u6837\u53ef\u601c\uff0c\u4f60\u8fd8\u53bb\u6b3a\u8d1f\u5b83\uff01\u201d\u4ed6\u4e5f\u4e00 \u8dc3\u6570\u4e08\uff0c\u4e00\u8fde\u53d1\u51fa\u51e0\u652f\u8896\u7bad\uff0c\u7bad\u7bad\u5c04\u4e2d\uff0c\u53ea\u662f\u8ddd\u79bb\u8fc7\u8fdc\uff0c\u90a3\u8c79\u5b50\u76ae\u8089\u53c8\u539a\uff0c\u867d\u7136\u75db\u5f97\u72c2\u55c5\u6012 \u543c\uff0c\u5374\u5e76\u672a\u8dcc\u5012\uff0c\u90a3\u5c0f\u9e7f\u88ab\u5b83\u54ac\u4e2d\u540e\u817f\uff0c\u4e5f\u75db\u5f97\u72c2\u5954\uff0c\u90a3\u91d1\u94b1\u8c79\u8eab\u4e0a\u5e26\u7bad\uff0c\u4ecd\u7136\u4e0d\u820d\uff0c\u7d27\u7d27 \u8ffd\u53bb\u3002\u97e9\u5fd7\u90a6\u7a81\u7136\u4e00\u8154\u6012\u6c14\uff0c\u597d\u50cf\u8981\u5411\u8c79\u5b50\u53d1\u6cc4\u4e00\u6837\uff0c\u4e5f\u65bd\u5c55\u8f7b\u529f\uff0c\u8ffd\u5728\u8c79\u5b50\u4e4b\u540e\u3002 \u3000\u3000\u8ffd\u4e86\u4e00\u56de\uff0c\u90a3\u5c0f\u9e7f\u4f3c\u4e4e\u6025\u4e0d\u62e9\u8def\uff0c\u7adf\u7a9c\u8fdb\u4e86\u4e00\u5ea7\u5c0f\u5c0f\u7684\u77f3\u7a9f\u3002\u90a3\u8c79\u5b50\u4e5f\u8ffd\u5c06\u8fdb\u53bb\uff0c\u97e9\u5fd7 \u90a6\u8d76\u5728\u540e\u9762\uff0c\u8ddd\u79bb\u5df2\u8fd1\uff0c\u53c8\u662f\u4e00\u652f\u8896\u7bad\uff0c\u5c04\u5165\u91d1\u94b1\u8c79\u7684\u809b\u95e8\uff0c\u90a3\u8c79\u5b50\u5927\u53eb\u4e00\u58f0\uff0c\u4ec6\u5728\u5730\u4e0a\uff0c \u5c1a\u672a\u722c\u8d77\uff0c\u5df2\u7ed9\u97e9\u5fd7\u90a6\u5939\u52b2\u6349\u7740\uff0c\u7528\u529b\u4e00\u62d7\uff0c\u628a\u8c79\u5b50\u9888\u9879\u62d7\u65ad\uff0c\u5feb\u610f\u4e4b\u6781\uff0c\u8bf4\u9053\uff1a\u201c\u770b\u4f60\u8fd8 \u6b3a\u8d1f\u5c0f\u9e7f\uff01\u201d\u4ed6\u5c06\u8c79\u5b50\u4e00\u628a\u629b\u8fdb\u6d1e\u5185\uff0c\u7f13\u6b65\u8fdb\u53bb\uff0c\u53ea\u542c\u5f97\u91cc\u9762\u5c0f\u9e7f\u53eb\u58f0\u5f88\u662f\u60e8\u5389\uff0c\u4ed6\u5fc3\u4e2d\u4e00 \u52a8\uff0c\u5ffd\u542c\u5f97\u91cc\u9762\u4eba\u58f0\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u4ed6\u5b9a\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u4eba\u5c06\u5c0f\u9e7f\u6309\u7740\uff0c\u6b63\u5728\u7528\u5200\u4e8e\u952f \u6885\u82b1\u9e7f\u7684\u9e7f\u8338\uff0c\u8fd9\u4eba\u4e00\u89c1\u97e9\u5fd7\u90a6\u8fdb\u6765\uff0c\u84e6\u5730\u8df3\u8d77\u3002\u8131\u624b\u5c31\u662f\u4e00\u53e3\u98de\u5200\uff0c\u5411\u4ed6\u63b7\u53bb\uff0c\u97e9\u5fd7\u90a6\u95ea \u8eab\u907f\u8fc7\uff0c\u7741\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u8fd9\u4eba\u6b63\u662f\u5f20\u5929\u8499\uff01\u539f\u6765\u5f20\u5929\u8499\u90a3\u65e5\u7ed9\u51cc\u672a\u98ce\u4e00\u5251\u63b7\u4e2d\uff0c\u6d41\u8840\u5f88\u591a\uff0c \u56e0\u6b64\u8eb2\u5230\u8fd9\u4e2a\u6d1e\u4e2d\u517b\u4f24\u3002\u201d \u3000\u3000\u97e9\u5fd7\u90a6\u89c1\u662f\u5f20\u5929\u8499\uff0c\u60f3\u8d77\u4ed6\u7684\u72e0\u6bd2\uff0c\u90a3\u65e5\u51e0\u4e4e\u5c06\u51cc\u672a\u98ce\u5f04\u6b7b\uff0c\u52c3\u7136\u5927\u6012\uff0c\u7d2b\u91d1\u5200\u9aa4\u7684\u51fa \u624b\uff0c\u7167\u5fc3\u4fbf\u523a\u3002\u5f20\u5929\u8499\u5237\u7684\u8df3\u524d\u4e24\u6b65\uff0c\u9f99\u7eb9\u97ad\u4e5f\u53d1\u51fa\u62db\u6765\uff0c\u97e9\u5fd7\u90a6\u62a1\u5200\u731b\u780d\uff0c\u5f20\u5929\u8499\u957f\u97ad\u4e00 \u6296\uff0c\u7f20\u5728\u97e9\u5fd7\u90a6\u97ad\u4e0a\uff0c\u7ed9\u4ed6\u7528\u529b\u4e00\u5f39\uff0c\u7d2b\u91d1\u5200\u7adf\u7ed9\u5f39\u4e86\u56de\u53bb\u3002\u97e9\u5fd7\u90a6\u8d8a\u53d1\u5927\u6012\uff0c\u8dc3\u7eb5\u5982\u98ce\uff0c \u4e00\u53e3\u5200\u6eda\u6eda\u800c\u4e0a\uff0c\u5f20\u5929\u8499\u8eab\u5b50\u5374\u4f3c\u8f6c\u52a8\u4e0d\u7075\uff0c\u53ea\u5f97\u62db\u67b6\u3002\u97e9\u5fd7\u90a6\u770b\u770b\u5f97\u624b\uff0c\u731b\u7136\u95f4\u5f20\u5929\u8499\u5927 \u559d\u4e00\u58f0\uff0c\u8eab\u5b50\u5f80\u540e\u4e00\u5750\uff0c\u97e9\u5fd7\u90a6\u7684\u7d2b\u91d1\u5200\u88ab\u957f\u97ad\u7f20\u7740\uff0c\u7ed9\u4ed6\u5f80\u540e\u4e00\u62d6\uff0c\u7d2b\u91d1\u5200\u7adf\u8131\u624b\u98de\u53bb\u3002 \u5f20\u5929\u8499\u66f4\u4e0d\u653e\u677e\uff0c\u75be\u7684\u53c8\u662f\u4e00\u97ad\uff0c\u6253\u4e2d\u97e9\u5fd7\u90a6\u80f8\u90e8\u3002\u97e9\u5fd7\u90a6\u4ec6\u5728\u5730\u4e0a\uff0c\u6eda\u4e86\u6570\u6eda\uff0c\u5bc2\u7136\u4e0d \u52a8\u3002 \u3000\u3000\u5f20\u5929\u8499\u5fc3\u4e2d\u5927\u559c\uff0c\u632a\u6b65\u4e0a\u524d\uff0c\u8fd8\u60f3\u8865\u4ed6\u4e00\u97ad\uff0c\u6b63\u8d70\u8fd1\u97e9\u5fd7\u90a6\u8eab\u8fb9\uff0c\u731b\u7136\u95f4\uff0c\u97e9\u5fd7\u90a6\u5728\u5730 \u4e0a\u5927\u559d\u4e00\u58f0\uff0c\u94c1\u83b2\u5b50\u51b0\u96f9\u822c\u5730\u6253\u51fa\uff0c\u5f20\u5929\u8499\u731d\u4e0d\u53ca\u9632\uff0c\u5934\u9762\u4e24\u80a9\u7ed9\u72e0\u72e0\u6253\u4e2d\u51e0\u9897\u3002\u5f20\u5929\u8499\u5f80 \u65c1\u4e00\u8df3\uff0c\u5ffd\u89c9\u811a\u4e0b\u597d\u50cf\u8e29\u4e86\u68c9\u82b1\u4e00\u6837\uff0c\u8f6f\u5f31\u65e0\u529b\u3002\u7ed9\u51cc\u672a\u98ce\u5251\u4f24\u7684\u521b\u53e3\uff0c\u53c8\u6c69\u6c69\u6d41\u51fa\u8840\u6765\uff01 \u3000\u3000\u97e9\u5fd7\u90a6\u5728\u5730\u4e0a\u4e00\u8dc3\u800c\u8d77\uff0c\u5ffd\u89c1\u5f20\u5929\u8499\u5750\u5728\u5730\u4e0a\uff0c\u957f\u97ad\u653e\u5728\u4e00\u65c1\uff0c\u5341\u5206\u60ca\u5f02\uff0c\u4ed6\u7c97\u4e2d\u6709 \u7ec6\uff0c\u626c\u624b\u53c8\u662f\u51e0\u7c92\u94c1\u83b2\u5b50\uff0c\u5f20\u5929\u8499\u6012\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u5012\u4f1a\u4f7f\u8bc8\uff01\u201d \u3000\u3000\u8fd9\u56de\u4ed6\u6709\u4e86\u9632\u5907\uff0c\u53cc\u624b\u4e0a\u4e0b\u4e00\u6284\uff0c\u628a\u94c1\u83b2\u5b50\u63a5\u5728\u624b\u4e2d\uff0c\u53cd\u6253\u51fa\u53bb\uff1b\u97e9\u5fd7\u90a6\u817e\u632a\u95ea\u907f\uff0c\u65e0 \u5948\u5f20\u5929\u8499\u6253\u5f97\u6bd4\u4ed6\u9ad8\u660e\uff0c\u77f3\u81c2\u8fd8\u662f\u4e2d\u4e86\u4e00\u7c92\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u4e2d\u4e86\u6697\u5668\uff0c\u53cd\u800c\u54c8\u54c8\u5927\u7b11\u3002\u539f\u6765\u4ed6\u521a\u624d\u6328\u4e86\u4e00\u97ad\uff0c\u5f88\u662f\u75bc\u75db\uff0c\u73b0\u5728\u7ed9\u94c1\u83b2\u5b50\u6253 \u4e2d\uff0c\u5374\u53ea\u4f3c\u81ea\u5df1\u4ee5\u524d\u5728\u7530\u95f4\u64cd\u4f5c\uff0c\u548c\u5b69\u5b50\u4eec\u563b\u620f\u65f6\uff0c\u7ed9\u987d\u7ae5\u7528\u5c0f\u77f3\u5b50\u63b7\u4e2d\u4e00\u6837\uff0c\u4e00\u70b9\u4e5f\u4e0d \u75db\u3002\u4ed6\u77e5\u9053\u5f20\u5929\u8499\u6c14\u529b\u5df2\u7aed\uff0c\u7eb5\u8eab\u4e00\u8df3\uff0c\u731b\u6251\u5728\u5f20\u5929\u8499\u8eab\u4e0a\uff0c\u5f53\u7740\u5fc3\u53e3\uff0c\u7528\u529b\u51fb\u4e86\u51e0\u62f3\u3002\u5f20 \u5929\u8499\u53cc\u638c\u4e5f\u62cd\u4e2d\u97e9\u5fd7\u90a6\u8170\u80c1\uff0c\u4e24\u4eba\u626d\u4f5c\u4e00\u56e2\u3002 \u3000\u3000\u8bba\u6b66\u529f\uff0c\u5f20\u5929\u8499\u4ec5\u6bd4\u695a\u662d\u5357\u7565\u900a\u4e00\u7b79\uff0c\u81ea\u7136\u8981\u6bd4\u97e9\u5fd7\u90a6\u9ad8\u8bb8\u591a\uff0c\u65e0\u5948\u4ed6\u53d7\u4e86\u51cc\u672a\u98ce\u7684\u91cd \u521b\uff0c\u4f24\u53e3\u590d\u88c2\uff0c\u7adf\u5f53\u4e0d\u4f4f\u97e9\u5fd7\u90a6\u6c34\u725b\u822c\u7684\u6c14\u529b\uff0c\u626d\u6253\u7247\u523b\uff0c\u4fbf\u7ed9\u97e9\u5fd7\u90a6\u6309\u5728\u5730\u4e0a\u3002\u4ed6\u72c2\u55e5\u4e00 \u58f0\uff0c\u5f20\u53e3\u4fbf\u54ac\uff0c\u97e9\u5fd7\u90a6\u80a9\u5934\u7ed9\u4ed6\u91cd\u91cd\u54ac\u4e86\u4e00\u53e3\uff0c\u75db\u5f97\u53eb\u51fa\u58f0\u6765\u3002\u5f20\u5929\u8499\u501f\u52bf\u62bd\u51fa\u53f3\u624b\u95ea\u7535\u822c \u5730\u62ff\u7740\u4e86\u97e9\u5fd7\u90a6\u53f3\u624b\u624b\u8155\u89d2\u529b\u4e00\u626d\uff0c\u7528\u64d2\u62ff\u624b\u6cd5\uff0c\u5c06\u97e9\u5fd7\u90a6\u624b\u638c\u5c48\u4e86\u8fc7\u6765\uff0c\u97e9\u5fd7\u90a6\u75db\u5f97\u8981 \u547d\uff0c\u5de6\u624b\u4e5f\u653e\u677e\u4e86\u3002\u5f20\u5929\u8499\u673a\u7075\u4e4b\u6781\uff0c\u5de6\u624b\u53c8\u95ea\u7535\u822c\u5730\u634f\u7740\u4e86\u97e9\u5fd7\u90a6\u7684\u8109\u95e8\uff0c\u97e9\u5fd7\u90a6\u624b\u4e0d\u80fd \u7528\u529b\uff0c\u8eab\u5b50\u6253\u6a2a\u6251\u5728\u5f20\u5929\u8499\u8eab\u4e0a\uff0c\u7ade\u54ac\u7740\u4e86\u5f20\u5929\u8499\u7684\u5589\u5499\uff1b\u5f20\u5929\u8499\u4f38\u53e3\u54ac\u65f6\uff0c\u5374\u53ea\u54ac\u7740\u4ed6\u7684 \u80a9\u5934\u3002\u97e9\u5fd7\u90a6\u54ac\u4e86\u51e0\u53e3\uff0c\u53ea\u89c9\u8840\u8165\u5473\u76f4\u51b2\u5165\u5589\u5499\uff0c\u6076\u5fc3\u6b32\u5455\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u54c7\u7684\u4e00\u58f0\u628a\u53e3\u4e2d\u9c9c\u8840\u5410\u4e86\u51fa\u6765\uff0c\u7741\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u5f20\u5929\u8499\u5589\u5499\u5df2\u88c2\u5f00\u4e00\u4e2a\u5927\u6d1e\uff0c\u9c9c \u8840\u50cf\u55b7\u6cc9\u4e00\u6837\u6d8c\u51fa\uff0c\u53ea\u662f\u4ed6\u7684\u4e24\u53ea\u624b\u8fd8\u7d27\u7d27\u63fd\u7740\u81ea\u5df1\u3002\u97e9\u5fd7\u90a6\u9976\u662f\u8eab\u7ecf\u767e\u6218\uff0c\u4e5f\u4e0d\u7981\u5bb3\u6015\u8d77 \u6765\uff0c\u4ed6\u7528\u529b\u4e00\u6323\uff0c\u5206\u5f00\u5f20\u5929\u8499\u53cc\u624b\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u8fd9\u65f6\u53ea\u89c9\u56db\u80a2\u9178\u8f6f,\u4ed6\u884c\u5f00\u51e0\u6b65\uff0c\u652f\u6491\u4e0d \u4f4f\uff0c\u7d22\u6027\u4e5f\u8eba\u5728\u5730\u4e0a\uff0c\u63a9\u7740\u9762\u5b54\uff0c\u95ed\u76ee\u517b\u795e\u3002 \u3000\u3000\u521a\u624d\u7ed9\u8c79\u5b50\u54ac\u4f24\u7684\u90a3\u5934\u5c0f\u9e7f\uff0c\u597d\u50cf\u77e5\u9053\u97e9\u5fd7\u90a6\u662f\u5b83\u7684\u670b\u53cb\u4f3c\u7684\uff0c\u6162\u6162\u5730\u6328\u5c06\u8fd1\u6765\u3002\u97e9\u5fd7 \u90a6\u5728\u660f\u8ff7\u4e2d\u53ea\u89c9\u5c0f\u9e7f\u5728\u81ea\u5df1\u7684\u80f8\u53e3\u8f7b\u8f7b\u6469\u64e6\uff0c\u60a0\u60a0\u9192\u8f6c\uff0c\u4ed6\u4e5f\u8f7b\u8f7b\u5730\u7528\u624b\u629a\u6478\u7740\u5c0f\u9e7f\uff0c\u5583\u5583 \u8bf4\u9053\uff1a\u201c\u8c79\u5b50\u6b7b\u4e86\uff0c\u6076\u4eba\u4e5f\u6b7b\u4e86\uff0c\u5c0f\u9e7f\uff0c\u5c0f\u9e7f\u4e0d\u7528\u5bb3\u6015\u4e86\uff01\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u7136\u53c8\u89c9\u6709\u751c\u751c\u817b \u817b\u7684\u6db2\u4f53\u6ef4\u8fdb\u81ea\u5df1\u7684\u53e3\u91cc\uff0c\u4e00\u76f4\u6ed1\u4e0b\u5589\u5499\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u4e39\u7530\u4f3c\u6709\u4e00\u9635\u6696\u6c14\u5347\u8d77\uff0c\u4eba\u4e5f\u6e05\u723d\u4e86 \u8bb8\u591a\u3002\u90a3\u6db2\u4f53\u6b63\u662f\u9e7f\u8840\uff0c\u5b83\u7ed9\u8c79\u5b50\u54ac\u4f24\uff0c\u53c8\u7ed9\u5f20\u5929\u8499\u5200\u4f24\uff0c\u6d41\u8840\u4e00\u76f4\u672a\u6b62\uff0c\u9e7f\u8840\u662f\u8865\u6c14\u8865\u8840 \u7684\u73cd\u54c1\uff0c\u97e9\u5fd7\u90a6\u7528\u529b\u8fc7\u5ea6\uff0c\u53c8\u53d7\u4e86\u91cd\u4f24\uff0c\u5e78\u5f97\u9e7f\u8840\u7ed9\u4ed6\u7a0d\u7a0d\u56de\u590d\u4e86\u7cbe\u795e\u548c\u4f53\u529b\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u82cf\u9192\u8fc7\u6765\uff0c\u53ea\u89c1\u5730\u4e0a\u4e00\u6ee9\u6ee9\u7684\u9c9c\u8840\uff0c\u8840\u6cca\u4e2d\u6d6e\u7740\u4e00\u53ea\u5c0f\u5c0f\u7684\u76d2\u5b50\uff0c\u4ed6\u731b\u7136\u9192\u8d77\uff0c \u7cbe\u795e\u4e00\u632f\uff0c\u6025\u5fd9\u5728\u8840\u6cca\u4e2d\u628a\u76d2\u5b50\u638f\u4e86\u8d77\u6765\uff0c\u7528\u8863\u895f\u62b9\u51c0\uff0c\u6253\u5f00\u4e00\u770b\uff0c\u53ea\u89c1\u91cc\u9762\u653e\u7740\u51e0\u7c92\u73cd\u73e0 \u4f3c\u7684\u4e1c\u897f\uff0c\u4f46\u5374\u4e0d\u5982\u73cd\u73e0\u900f\u660e\uff0c\u800c\u662f\u7070\u8910\u8272\u7684\uff0c\u76d2\u5b50\u5468\u56f4\u523b\u6709\u4e00\u4e9b\u53e4\u53e4\u602a\u602a\u7684\u6587\u5b57\uff0c\u90a3\u662f\u68b5 \u6587\uff0c\u97e9\u5fd7\u90a6\u867d\u7136\u4e0d\u8bc6\uff0c\u4f46\u770b\u6837\u5b50\uff0c\u4ed6\u5df2\u9192\u609f\u5230\u8fd9\u4e00\u5b9a\u662f\u820d\u5229\u5b50\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u6025\u5fd9\u628a\u76d2\u5b50\u76d6 \u4e0a\uff0c\u6536\u8fdb\u884c\u56ca\u3002 \u3000\u3000\u53ea\u662f\u8fd9\u4e48\u8f7b\u8f7b\u79fb\u52a8\uff0c\u97e9\u5fd7\u90a6\u773c\u524d\u53c8\u662f\u91d1\u661f\u4e71\u5192\uff0c\u8fd9\u624d\u77e5\u9053\u81ea\u5df1\u6bd5\u7adf\u662f\u7528\u529b\u8fc7\u5ea6\uff0c\u4e0d\u80fd\u518d \u884c\u8d70\u4e86\u3002\u4ed6\u6478\u6478\u8eab\u8fb9\u7684\u5c0f\u9e7f\uff0c\u5c0f\u9e7f\u4e5f\u6ca1\u6709\u4e86\u6c14\u606f\uff0c\u6562\u60c5\u4e5f\u662f\u6b7b\u4e86\u3002\u731b\u7136\u95f4\u4ed6\u89c9\u5f97\u975e\u5e38\u5bc2\u5bde\uff0c \u597d\u50cf\u81ea\u5df1\u4ece\u6765\u6ca1\u6709\u8fc7\u4eb2\u4eba\u4e5f\u6ca1\u6709\u8fc7\u670b\u53cb\u4e00\u6837\uff0c\u5fc3\u4e2d\u7a7a\u8361\u8361\u7684\u4ec0\u4e48\u4e5f\u6ca1\u6709\uff0c\u8ff7\u8ff7\u7cca\u7cca\u95f4\uff0c\u4ed6\u8eba \u5728\u5730\u4e0a\u9677\u5165\u4e86\u719f\u7761\u4e4b\u4e2d\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u7761\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u4e00\u89c9\u9192\u6765\uff0c\u53ea\u89c1\u9633\u5149\u4ece\u6d1e\u5916\u900f\u5165\uff0c\u8fd9\u5df2\u7ecf\u662f\u7b2c\u4e8c\u5929\u7684\u4e0a\u5348\u4e86\uff0c\u4ed6 \u7ad9\u4e86\u8d77\u6765\uff0c\u4ecd\u7136\u89c9\u5f97\u8f6f\u8f6f\u7684\uff0c\u809a\u5b50\u4e5f\u997f\u5f97\u53d1\u614c\uff0c\u53ea\u662f\u7cbe\u795e\u5374\u6bd4\u6628\u5929\u597d\u4e86\u8bb8\u591a\u3002\u4ed6\u60f3\uff0c\u73b0\u5728\u8d70 \u51fa\u53bb\uff0c\u81ea\u5df1\u4f53\u529b\u8fd8\u662f\u4e0d\u652f\uff0c\u82e5\u78b0\u5230\u654c\u4eba\uff0c\u90a3\u66f4\u65e0\u4ece\u62b5\u5fa1\uff0c\u770b\u6765\u53ea\u597d\u5728\u8fd9\u77f3\u7a9f\u4e2d\u6b47\u606f\u51e0\u5929\u518d \u8bf4\uff0c\u53ef\u662f\u7cae\u98df\u54ea\u91cc\u627e\u5462\uff1f\u888b\u4e2d\u53ea\u6709\u4e00\u4e9b\u5e72\u7cae\uff0c\u9876\u4e0d\u4e86\u4ec0\u4e48\u7528\uff0c\u81ea\u5df1\u53c8\u4e0d\u5fcd\u98df\u5c0f\u9e7f\u7684\u8089\uff0c\u6b63\u7740 \u6025\u95f4\uff0c\u5ffd\u7136\u773c\u5149\u4e00\u77a5\uff0c\u62cd\u638c\u7b11\u9053\uff1a\u201c\u600e\u7684\u628a\u8fd9\u53ea\u8c79\u5b50\u5fd8\u4e86\uff1f\u201d\u6628\u5929\u90a3\u53ea\u5927\u8c79\uff0c\u7ed9\u81ea\u5df1\u62d7\u65ad\u4e86 \u9888\u9aa8\uff0c\u4e22\u8fdb\u7a9f\u4e2d\uff0c\u73b0\u5728\u4e0d\u6b63\u5c31\u5728\u8eab\u65c1\uff1f\u97e9\u5fd7\u90a6\u628a\u8c79\u5b50\u62d6\u8fdb\u77f3\u7a9f\u6df1\u5904\uff0c\u5728\u884c\u56ca\u4e2d\u53d6\u51fa\u706b\u77f3\uff0c\u628a \u7a9f\u4e2d\u7684\u4e00\u4e9b\u673d\u6728\uff0c\u805a\u96c6\u4e86\u6765\uff0c\u70e7\u8d77\u4e86\u4e00\u5806\u65fa\u706b\uff0c\u7528\u7d2b\u91d1\u5200\u5272\u4e0b\u8c79\u8089\uff0c\u5c31\u5728\u706b\u4e0a\u70e7\u719f\u540e\u5403\u3002 \u3000\u3000\u706b\u5149\u718a\u718a\uff0c\u628a\u77f3\u7a9f\u7167\u5f97\u901a\u660e\uff0c\u97e9\u5fd7\u90a6\u62ac\u5934\u56db\u770b\uff0c\u5ffd\u89c1\u77f3\u58c1\u4e0a\u753b\u7740\u8bb8\u591a\u4eba\u50cf\uff0c\u90a3\u4e9b\u4eba\u50cf\u5404 \u6709\u5404\u7684\u59ff\u52bf\u5341\u5206\u53e4\u602a\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c1\u6709\u7684\u4eba\u50cf\u4f4e\u7709\u5408\u8ba1\uff1b\u6709\u7684\u4eba\u50cf\u6469\u62f3\u64e6\u638c\uff1b\u6709\u7684\u4eba\u50cf\u4f5c\u52bf\u6b32\u6251\uff0c\u5982 \u864e\u5982\u72ee\uff1b\u6709\u7684\u4eba\u50cf\u4f5c\u52bf\u64d2\u62ff\uff0c\u5982\u733f\u5982\u9e70\uff0c\u8fd8\u6709\u624b\u91cc\u62ff\u7740\u5200\u5251\u4f5c\u5288\u523a\u4e4b\u72b6\u7684\uff0c\u5404\u79cd\u59ff\u6001\uff0c\u5343\u5947 \u767e\u602a\u3002\u4f46\u56e0\u5e74\u6df1\u65e5\u8fdc\uff0c\u6709\u7684\u753b\u50cf\u5df2\u6a21\u7cca\u4e0d\u6e05\uff0c\u6709\u7684\u56fe\u50cf\u66f4\u5265\u843d\u6b86\u5c3d\uff0c\u53ea\u4f59\u4e0b\u4e00\u70b9\u70b9\u7684\u75d5\u8ff9\u3002 \u97e9\u5fd7\u90a6\u95f2\u5f97\u65e0\u804a\uff0c\u7d22\u6027\u6cbf\u7740\u77f3\u58c1\uff0c\u7ec6\u7ec6\u4e00\u6570\uff0c\u5176\u4e2d\u6e05\u6670\u53ef\u8fa8\u7684\u6709\u4e09\u5341\u516d\u5e45\uff0c\u6a21\u7cca\u4e0d\u6e05\u548c\u5df2\u7ecf \u5265\u843d\u7684\u5374\u6709\u4e03\u5341\u4e8c\u5e45\u4e4b\u591a\u3002\u5728\u6e05\u6670\u53ef\u8fa8\u7684\u4e09\u5341\u516d\u5e45\u4e4b\u4e2d\uff0c\u6709\u516d\u5e45\u662f\u6253\u5750\u4e4b\u50cf\uff0c\u5176\u4e2d\u4e09\u5e45\u7684\u59ff \u6001\uff0c\u90fd\u662f\u76d8\u819d\u5782\u624b\uff0c\u6b63\u9762\u800c\u5750\uff0c\u597d\u50cf\u5b8c\u5168\u4e00\u6837\uff0c\u53e6\u5916\u4e09\u5e45\u5219\u7a0d\u7a0d\u6539\u4e86\u4e00\u4e9b\uff0c\u6709\u4e00\u5e45\u662f\u4fa7\u9762\u6253 \u5750\u7684\uff0c\u6709\u4e00\u5e45\u662f\u5408\u638c\u80f8\u524d\u7684\uff0c\u6709\u4e00\u5e45\u662f\u6b20\u8eab\u6b32\u8d77\u7684\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u9971\u9910\u8c79\u8089\u4e4b\u540e\uff0c\u6c14\u529b\u7a0d\u589e\uff0c\u53cd\u6b63\u65e0\u4e8b\uff0c\u5c31\u8bd5\u7167\u7740\u58c1\u4e0a\u753b\u50cf\u7684\u59ff\u52bf\u7ec3\u4e60\u3002\u524d\u9762\u516d \u5e45\uff0c\u4ed6\u770b\u5f97\u83ab\u540d\u5176\u5999\uff0c\u61d2\u5f97\u53bb\u7406\uff0c\u53ea\u62e3\u90a3\u4e9b\u81ea\u5df1\u770b\u5f97\u61c2\u7684\u6765\u5b66\uff0c\u8d77\u5148\u662f\u7ec3\u51e0\u4e2a\u638c\u6cd5\uff0c\u8bf4\u4e5f\u5947 \u602a\uff0c\u7167\u6837\u6253\u4e86\u4e00\u904d\u4e4b\u540e\uff0c\u7adf\u7136\u6c14\u8840\u6d41\u901a\uff0c\u8eab\u5fc3\u8212\u9002\uff0c\u7cbe\u795e\u957f\u4e86\u8bb8\u591a\u3002\u4ed6\u8d8a\u7ec3\u8d8a\u9ad8\u5174\uff0c\u53cd\u6b63\u81ea \u5df1\u5c1a\u672a\u5b8c\u5168\u590d\u539f\uff0c\u5c31\u7d22\u6027\u5728\u6d1e\u4e2d\u591a\u7559\u51e0\u65e5\uff0c\u5c06\u4e09\u5341\u5e45\u753b\u7740\u8fd0\u638c\u3001\u4f7f\u5200\u3001\u51fb\u5251\u7684\u5404\u79cd\u59ff\u52bf\uff0c\u7ec3 \u4e86\u53c8\u7ec3\uff0c\u4e0d\u8fc7\u4e09\u5929\uff0c\u5df2\u7ecf\u6eda\u74dc\u70c2\u719f\u3002 \u3000\u3000\u7b2c\u56db\u5929\u65e9\u6668\uff0c\u8c79\u8089\u5df2\u7ecf\u5403\u5b8c\uff0c\u7a9f\u4e2d\u7684\u673d\u6728\u4e5f\u5df2\u70e7\u5c3d\uff0c\u4ed6\u8bd5\u7740\u7ec3\u7ec3\u529b\u6c14\uff0c\u53ea\u89c9\u5df2\u5b8c\u5168\u6062 \u590d\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u6536\u8d77\u884c\u56ca\uff0c\u4fbf\u5f85\u51fa\u6d1e\uff0c\u5ffd\u7136\u542c\u5230\u5916\u9762\u6709\u4eba\u58f0\u548c\u811a\u6b65\u58f0\uff0c\u597d\u50cf\u5411\u77f3\u7a9f\u884c\u6765\uff0c\u8fde \u5fd9\u95ea\u8eab\u8eb2\u5728\u4e00\u5c0a\u4f5b\u50cf\u4e4b\u540e\u3002 \u3000\u3000\u6765\u4eba\u884c\u5230\u6d1e\u53e3\uff0c\u97e9\u5fd7\u90a6\u542c\u5f97\u4e00\u4e2a\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u600e\u7684\u597d\u50cf\u6709\u5c38\u81ed\u5473\u9053\uff01\u201d\u97e9\u5fd7\u90a6\u8fd9\u624d \u60f3\u8d77\u5f20\u5929\u8499\u7684\u5c38\u4f53\u8fd8\u6ca1\u6709\u63a9\u57cb\uff0c\u81ea\u5df1\u5728\u77f3\u7a9f\u4f4f\u4e86\u51e0\u5929\uff0c\u9f3b\u5b50\u5df2\u7ecf\u4e60\u60ef\uff0c\u7a9f\u4e2d\u53c8\u51b7\uff0c\u5e76\u672a\u89c9\u5f97 \u600e\u6837\u3002\u6765\u4eba\u662f\u5916\u9762\u8d70\u8fdb\uff0c\u81ea\u7136\u4e00\u55c5\u5c31\u89c9\u5f97\u523a\u9f3b\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u6709\u4e24\u4e2a\u4eba\u8d70\u8fdb\u6d1e\u5185\uff0c\u624b\u4e2d\u71c3\u7740\u706b\u628a\uff0c\u7167\u89c1\u4e86\u5f20\u5929\u8499\u7684\u5c38\u4f53\uff0c\u54d7\u7136\u60ca\u547c\u3002\u5176\u4e2d \u4e00\u4eba\u6307\u7740\u5f20\u5929\u8499\u7684\u519b\u5b98\u670d\u9970\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u83ab\u975e\u5c31\u662f\u695a\u662d\u5357\u6240\u8bf4\u7684\uff0c\u5434\u4e09\u6842\u624b\u4e0b\u519b\u5b98\uff0c\u636e\u4ed6\u8bf4 \u8fd9\u4eba\u6b66\u529f\u5f88\u9ad8\uff0c\u6050\u6015\u662f\u7ed9\u51cc\u672a\u98ce\u5bb3\u6b7b\u7684\uff01\u201d\u97e9\u5fd7\u90ca\u6697\u6697\u54fc\u4e86\u4e00\u58f0\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u4eec\u5c31\u53ea\u77e5\u9053\u6709 \u4e2a\u51cc\u672a\u98ce\uff01\u201d \u3000\u3000\u8fd9\u65f6\u8fd9\u4e24\u4e2a\u4eba\u53cd\u663e\u5f97\u6709\u70b9\u5bb3\u6015\u4e86\uff0c\u4f60\u63a8\u6211\u6211\u63a8\u4f60\u7684\u4e0d\u6562\u641c\u7d22\u3002\u6709\u4e00\u4e2a\u4eba\u8bf4\uff1a\u201c\u522b\u7684\u4eba\u8fd8 \u597d\uff0c\u53ea\u6015\u51cc\u672a\u98ce\u8eb2\u5728\u91cc\u9762\uff01\u201d\u97e9\u5fd7\u90a6\u5fc3\u4e2d\u6709\u6c14\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u8df3\u4e86\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u662f\u51cc\u672a\u98ce \u4e5f\u6536\u62fe\u5f97\u4f60\u4eec\uff01\u201d\u4e24\u4eba\u5413\u4e86\u4e00\u8df3\uff0c\u5c06\u706b\u628a\u5411\u97e9\u5fd7\u90a6\u4e00\u63b7\uff0c\u97e9\u5fd7\u90a6\u95ea\u8eab\u907f\u8fc7\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u6251\u4e86 \u4e0a\u53bb\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e43\u662f\u7981\u536b\u519b\u6559\u5934\uff0c\u90a3\u65e5\u695a\u662d\u5357\u7ed9\u6253\u5f97\u5927\u8d25\u4e4b\u540e\uff0c\u6025\u5fd9\u8dd1\u56de\u53bb\u627e\u7981\u536b\u519b\u7684\u526f\u603b\u9886\u5f20 \u627f\u658c\uff0c\u53eb\u4ed6\u6d3e\u5f97\u529b\u624b\u4e0b\uff0c\u5206\u5934\u8ffd\u8e2a\u3002\u4e91\u5c97\u9644\u8fd1\u66f4\u662f\u7279\u522b\u7559\u610f\u3002\u8fd9\u4e24\u4e2a\u6559\u5934\uff0c\u6070\u5de7\u548c\u97e9\u5fd7\u90a6\u649e \u4e2a\u6b63\u7740\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u6251\u4e86\u4e0a\u53bb\uff0c\u8fd9\u4e24\u4e2a\u6559\u5934\u5df2\u770b\u6e05\u695a\u97e9\u5fd7\u90a6\u9762\u4e0a\u5e76\u65e0\u5200\u75d5\uff0c\u77e5\u9053\u4e0d\u662f\u51cc\u672a\u98ce\u4e86\uff0c\u52c7\u6c14 \u500d\u589e\uff0c\u9a6c\u4e0a\u8fce\u51fb\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u4ee5\u4e00\u654c\u4e8c\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u9aa4\u53d1\uff0c\u7a7f\u4f69\u76f4\u8fdb\u3002\u654c\u4eba\u500f\u5730\u5de6\u53f3\u4e00\u5206\uff0c\u4e00\u4e2a\u53cc\u62f3\u7d27\u63e1 \u6253\u51fa\u4e09\u5341\u516d\u8def\u957f\u62f3\uff0c\u62f3\u98ce\u98d8\u98d8\uff0c\u76f4\u6363\u9762\u95e8\uff1b\u4e00\u4eba\u53cc\u638c\u5982\u5200\uff0c\u62db\u719f\u52bf\u6025\uff0c\u6253\u7684\u662f\u897f\u85cf\u5929\u9f99\u638c \u6cd5\u3002\u4e00\u62f3\u4e00\u638c\uff0c\u5947\u8bc1\u76f8\u751f\uff01\u5341\u5206\u51cc\u5389\uff0c\u6253\u4e86\u7247\u523b\uff0c\u97e9\u5fd7\u90a6\u7adf\u7ed9\u8feb\u5230\u77f3\u7a9f\u4e00\u9685\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u4e3a\u5929\u5730\u4f1a\u603b\u8235\u4e3b\uff0c\u6b66\u529f\u81ea\u975e\u6cdb\u6cdb\uff0c\u65e0\u5948\u654c\u4eba\u4e5f\u662f\u9ad8\u624b\uff0c\u800c\u4e14\u662f\u5728\u5de6\u53f3\u5939\u51fb\uff0c\u62f3\u638c \u5e76\u7528\uff0c\u914d\u5408\u5f97\u5341\u5206\u7d27\u5bc6\u3002\u97e9\u5fd7\u90a6\u653b\u4e0d\u8fdb\u53bb\uff0c\u6e10\u6e10\u7ed9\u8feb\u5f97\u53ea\u6709\u9000\u5b88\u7684\u4efd\u513f\u3002 \u3000\u3000\u6253\u5230\u5206\u9645\uff0c\u5de6\u9762\u654c\u4eba\u4e00\u62f3\u5411\u97e9\u5fd7\u90a6\u9762\u95e8\u6363\u51fa\uff0c\u97e9\u6210\u90a6\u5de6\u638c\u4e0a\u62ac\uff0c\u6b63\u60f3\u6a2a\u622a\u6765\u52bf\uff0c\u53f3\u9762\u654c \u4eba\u5df2\u6b3a\u8eab\u62a2\u8fdb\uff0c\u5de6\u624b\u731b\u62e8\u97e9\u5fd7\u90a6\u53f3\u638c\uff0c\u53f3\u624b\u4e5f\u6a2a\u638c\u4e0a\u51fb\uff0c\u5411\u97e9\u5fd7\u90a6\u5de6\u81c2\u731b\u88ad\uff0c\u4e24\u4eba\u6765\u52bf\u90fd\u6781 \u51f6\u731b\u3002\u97e9\u5fd7\u90a6\u5371\u6025\u4e4b\u95f4\uff0c\u84e6\u7136\u4e0d\u81ea\u89c9\u5730\u4f7f\u51fa\u5728\u77f3\u58c1\u4e0a\u6240\u753b\u7684\u638c\u6cd5\uff0c\u4e0d\u9000\u53cd\u8fdb\uff0c\u53f3\u817f\u4e03\u6b65\uff0c\u8eab \u5f62\u4e00\u659c\uff0c\u811a\u8ddf\u4e00\u8f6c\uff0c\u884c\u638c\u968f\u7740\u8eab\u5f62\u534a\u8f6c\u4e4b\u52bf\uff0c\u5c06\u53f3\u9762\u654c\u4eba\u7684\u62f3\u5934\u4e00\u628a\u63b3\u7740\uff0c\u5411\u6000\u4e2d\u4e00\u62d6\uff0c \u201c\u987a\u624b\u7275\u7f8a\u201d\uff0c\u5c06\u654c\u4eba\u6a2a\u62fd\u8fc7\u6765\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u8d77\u201d\uff01\u5c06\u654c\u4eba\u6a2a\u4e3e\u8d77\u6765\uff0c\u4e00\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u98de \u63b7\u51fa\u53bb\uff0c\u6b63\u597d\u649e\u7740\u53e6\u4e00\u654c\u4eba\uff0c\u90a3\u4eba\u5927\u53eb\u4e00\u793e\u58f0\uff0c\u5411\u540e\u4fbf\u5012\uff0c\u800c\u7ed9\u97e9\u5fd7\u90a6\u63b7\u51fa\u53bb\u7684\u654c\u4eba\uff0c\u4f59\u52bf \u672a\u8870\uff0c\u4ecd\u4f3c\u7bad\u822c\u5c04\u51fa\uff0c\u5934\u9885\u78b0\u7740\u4e00\u5c0a\u4f5b\u50cf\uff0c\u767b\u65f6\u8111\u6d46\u8ff8\u88c2\uff0c\u6d41\u4e86\u904d\u5730\uff0c\u4f5b\u50cf\u4e5f\u7ed9\u649e\u5f97\u6447\u6447\u6b32 \u5012\uff01 \u3000\u3000\u97e9\u5fd7\u90a6\u4e00\u62db\u5f97\u624b\uff0c\u66f4\u4e0d\u653e\u677e\uff0c\u53cc\u8db3\u4e00\u987f\uff0c\u8eab\u968f\u638c\u8d70\uff0c\u8fc5\u82e5\u72c2\u98d8\uff0c\u90a3\u4ec6\u5012\u7684\u654c\u4eba\u521a\u4ece\u5730\u4e0a \u722c\u8d77\uff0c\u7ed9\u97e9\u5fd7\u90a6\u4e00\u638c\u6253\u4e2a\u6b63\u7740\uff0c\u518d\u5ea6\u8dcc\u5012\uff0c\u8fd8\u6ca1\u558a\u5f97\u51fa\u58f0\uff0c\u5c31\u5df2\u4e86\u7ed3\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u4f7f\u51fa\u65b0\u5b66\u638c\u6cd5\uff0c\u5c45\u7136\u4e09\u62db\u4e24\u5f0f\uff0c\u5c31\u6253\u8d25\u5f3a\u654c\uff0c\u5927\u559c\u82e5\u72c2\u3002\u4ed6\u89c1\u4f5b\u50cf\u6447\u6447\u6b32\u5012\uff0c\u6025 \u5fd9\u62a2\u8fc7\u53bb\u6276\u4f4f\uff0c\u5ffd\u5730\u773c\u775b\u4e00\u4eae\uff0c\u77a5\u89c1\u4f5b\u50cf\u4e0b\u6709\u4e00\u672c\u6b8b\u65e7\u7684\u5c0f\u4e66\uff0c\u4ed6\u8f7b\u8f7b\u62ff\u4e86\u8d77\u6765\uff0c\u5439\u53bb\u4e66\u4e0a \u7684\u5c18\u57c3\uff0c\u63ed\u5f00\u4e00\u770b\uff0c\u53ea\u89c1\u91cc\u9762\u7684\u6587\u5b57\uff0c\u5947\u5f62\u602a\u72b6\uff0c\u548c\u88c5\u820d\u5229\u5b50\u7684\uff1a\u5348\u5185\u6240\u523b\u5b57\u4f53\u4e00\u6837\uff0c\u4ed6\u4e00 \u4e2a\u4e5f\u8ba4\u4e0d\u5f97\u3002\u63ed\u5230\u6700\u540e\uff0c\u624d\u770b\u5230\u4e24\u884c\u6c49\u5b57\uff0c\u8fd9\u4e24\u884c\u5b57\u662f\uff1a\u201c\u8fbe\u6469\u6613\u7b4b\u7ecf\uff0c\u7559\u8d60\u6709\u7f18\u8005\u3002\u201d\u5e95 \u4e0b\u6709\u51e0\u884c\u5c0f\u5b57\u6ce8\u9053\uff1a\u201c\u4e00\u767e\u96f6\u516b\u5f0f\uff0c\u5f0f\u5f0f\u89c1\u795e\u5947\uff0c\u4e5d\u56fe\u516d\u5ea7\u50cf\uff0c\u7b2c\u4e00\u624e\u6839\u57fa\u3002\u201d\u6700\u540e\u4e00\u884c\u5c0f \u5b57\uff0c\u662f\u201c\u540e\u5b66\u65e0\u4f4f\u8c28\u8bc6\uff0c\u5510\u8d1e\u5143\u4e94\u5e74\u4e5d\u6708\u3002\u201d\u97e9\u5fd7\u90a6\u770b\u4e86\uff0c\u4ecd\u662f\u83ab\u540d\u5176\u5999\uff0c\u4f46\u89c1\u6b64\u4e66\u53e4\u96c5\u53ef \u7231\uff0c\u4e5f\u5c31\u968f\u624b\u585e\u5728\u884c\u56ca\u4e2d\u3002\u76f4\u5230\u8bb8\u591a\u5e74\u540e\uff0c\u4ed6\u624d\u77e5\u9053\uff0c\u8fbe\u6469\u7985\u5e08\u662f\u5357\u5317\u671d\u6881\u6b66\u5e1d\u65f6\uff0c\u81ea\u5370\u6765 \u534e\u7684\u9ad8\u50e7\uff0c\u4e5f\u662f\u201c\u7985\u5b97\u201d\u7684\u521b\u7acb\u8005\uff0c\u201c\u6613\u7b4b\u201d\u201c\u6d17\u9ad3\u201d\u4e8c\u7ecf\u662f\u8fbe\u6469\u7985\u5e08\u6b66\u529f\u7684\u7cbe\u534e\uff0c\u58c1\u4e0a\u7684 \u4e00\u767e\u96f6\u516b\u5e45\u753b\u50cf\uff0c\u5c31\u662f\u6b66\u5b66\u4e2d\u8457\u540d\u7684\u201c\u8fbe\u6469\u4e00\u767e\u96f6\u516b\u5f0f\u201d\u771f\u672c\u3002\u53ef\u60dc\u97e9\u5fd7\u90a6\u53ea\u5b66\u4e86\u4e09\u5341\u4e2a\u5f0f \u5b50\uff0c\u800c\u6700\u91cd\u8981\u7684\uff0c\u624e\u6839\u57fa\u7684\u524d\u516d\u4e2a\u5750\u5f0f\uff0c\u4ed6\u5374\u6839\u672c\u4e0d\u5b66\uff0c\u4ee5\u81f4\u867d\u6709\u5947\u9047\uff0c\u540e\u6765\u8fd8\u662f\u5403\u4e86\u5927 \u4e8f\uff0c\u8fd9\u662f\u540e\u8bdd\uff08\u4f5c\u8005\u6309\uff1a\u636e\u8fd1\u4ee3\u53f2\u5b66\u5bb6\u8003\u8bc1\uff0c\u2018\u6613\u7b4b\u2019\u3001\u2018\u6d17\u9ad3\u2019\u4e8c\u7ecf\u4e43\u662f\u660e\u4ee3\u6587\u4eba\u5047\u5192\u8fbe \u6469\u540d\u4e49\u7684\u4f2a\u4f5c\u3002\u4f46\u5c0f\u8bf4\u662f\u65e0\u987b\u8003\u8bc1\u5f97\u90a3\u6837\u4e25\u8c28\u7684\u3002\u8bfb\u8005\u8bf8\u541b\uff0c\u5f53\u201c\u5c0f\u8bf4\u5bb6\u8a00\u201d\u770b\u53ef\u4e5f\uff09\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u7f13\u6b65\u8d70\u51fa\u77f3\u7a9f\uff0c\u53ea\u89c1\u9633\u5149\u904d\u5730\uff0c\u5c71\u8c37\u4e4b\u95f4\uff0c\u7fa4\u82b1\u7ade\u8273\uff0c\u97e9\u5fd7\u90a6\u8eb2\u5728\u77f3\u7a9f\u4e4b\u4e2d\u51e0 \u65e5\uff0c\u4e0d\u89c1\u9633\u5149\u3002\u8fd9\u65f6\u5728\u84dd\u5929\u767d\u4e91\u4e4b\u4e0b\uff0c\u5c71\u82b1\u91ce\u8349\u4e4b\u4e2d\uff0c\u5fc3\u5883\u5927\u4e3a\u5f00\u6717\uff0c\u51e0\u65e5\u6765\u7684\u5fe7\u90c1\uff0c\u50cf\u6de1 \u6de1\u7684\u8f7b\u70df\uff0c\u5728\u767d\u4e91\u95f4\u6d88\u6563\u4e86\u3002\u4ed6\u6cbf\u9014\u7eb5\u76ee\uff0c\u6d4f\u89c8\u5c71\u666f\uff0c\u5ffd\u89c1\u65ad\u5d16\u5c96\u58c1\u4e4b\u4e0a\uff0c\u9694\u4e0d\u4e86\u591a\u8fdc\uff0c\u5c31 \u6709\u4eba\u7528\u5200\u523b\u7740\u4e00\u679d\u7bad\u5934\uff0c\u8fd8\u6709\u4e00\u4e9b\u5de6\u53f3\u602a\u602a\u7684\u6697\u53f7\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u6b63\u60ca\u8be7\u95f4\uff0c\u5ffd\u542c\u5f97\u5c71\u5c97\u4e0a\u4f20\u6765\u53f1\u54a4\u4e4b\u58f0\uff0c\u5e76\u6709\u5c18\u571f\u7802\u77f3\u98de\u6e85\u800c\u4e0b\u3002\u97e9\u5fd7\u90a6\u60c5\u77e5\u4e0a \u9762\u5fc5\u6709\u4eba\u62fc\u6597\uff0c\u597d\u5947\u5fc3\u8d77\uff0c\u6500\u7740\u5c71\u85e4\uff0c\u4e0a\u53bb\u63a2\u671b\uff0c\u4e0a\u5230\u4e0a\u9762\uff0c\u53ea\u89c1\u6709\u56db\u4e2a\u9ed1\u8863\u536b\u58eb\uff0c\u56f4\u7740\u4e09 \u4e2a\u5587\u561b\uff0c\u6253\u5f97\u6b63\u9163\u3002\u97e9\u5fd7\u90a6\u89c1\u4e86\uff0c\u53c8\u662f\u4e00\u8be7\uff0c\u8fd9\u4e09\u4e2a\u5587\u561b\u4e2d\uff0c\u6709\u4e00\u4e2a\u6b63\u662f\u4ee5\u524d\u548c\u5f20\u5929\u8499\u540c \u884c\uff0c\u62a4\u9001\u820d\u5229\u5b50\u7684\u4eba\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u770b\u4e86\u534a\u664c\uff0c\u53ea\u89c1\u90a3\u56db\u4e2a\u536b\u58eb\uff0c\u8d8a\u6253\u8d8a\u51f6\uff0c\u6253\u5f97\u4e09\u4e2a\u5587\u561b\uff0c\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u7adf\u65e0\u8fd8 \u624b\u4e4b\u529b\uff0c\u4ed6\u5fcd\u8010\u4e0d\u4f4f\uff0c\u864e\u543c\u4e00\u58f0\uff0c\u62d4\u5200\u800c\u51fa\u3002\u90a3\u4e2a\u8ba4\u5f97\u7684\u5587\u561b\u5927\u559c\uff0c\u53eb\u4e86\u4e86\u58f0\uff0c\u97e9\u5fd7\u90a6\u6b63\u5f85 \u62db\u547c\uff0c\u53ea\u89c1\u4e24\u4e2a\u536b\u58eb\uff0c\u5df2\u8131\u51fa\u6218\u56f4\uff0c\u62e6\u622a\u81ea\u5df1\uff0c\u9634\u607b\u607b\u5730\u7b11\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff1f\u539f\u6765\u662f\u97e9\u603b\u8235 \u4e3b\uff01\u201d\u4e24\u4eba\u4e00\u4f7f\u5224\u5b98\u7b14\uff0c\u4e00\u4f7f\u952f\u9f7f\u5200\uff0c\u4e00\u7167\u9762\u5c31\u4e0b\u6bd2\u62db\uff0c\u7b14\u70b9\u7a74\u9053\uff0c\u5200\u6302\u4e24\u80a9\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u60f3\u7528\u65b0\u5b66\u6765\u7684\u8fd0\u5200\u51fb\u5251\u4e4b\u6cd5\u5bf9\u4ed6\u4eec\u3002\u4f46\u4e00\u8f6c\u5ff5\u95f4\uff0c\u4ecd\u662f\u4f7f\u51fa\u81ea\u5df1\u672c\u95e8\u7684\u516b\u5366\u7d2b\u91d1 \u5200\u6cd5\u3002\u4ed6\u662f\u60f3\u8bd5\u8bd5\u672c\u95e8\u7684\u5200\u6cd5\u548c\u65b0\u5b66\u7684\u6280\u827a\uff0c\u5dee\u522b\u5982\u4f55\uff0c\u624d\u4f7f\u51fa\u65b0\u5b66\u7684\u62db\u6570\u3002 \u3000\u3000\u516b\u5366\u7d2b\u91d1\u5200\u8fde\u73af\u516d\u5341\u56db\u5f0f\uff0c\u662f\u660e\u4ee3\u6b66\u5e08\u5355\u601d\u5357\u6240\u521b\u7684\u5200\u6cd5\u4e4b\u4e00\uff08\u53e6\u4e00\u4e3a\u94a9\u9570\u5200\uff09\uff0c\u4e00\u4f7f \u5f00\u6765\uff0c\u661f\u6d41\u7535\u63a3\uff0c\u4e0a\u4e0b\u7ffb\u98de\uff0c\u4e5f\u7aef\u7684\u5389\u5bb3\u3002\u53ea\u662f\u90a3\u4e24\u4eba\u7684\u5175\u5668\uff0c\u90fd\u662f\u7f55\u89c1\u7684\u5916\u95e8\u5175\u5203\u3002\u5c24\u5176 \u90a3\u4f7f\u5224\u5b98\u7b14\u7684\uff0c\u4e00\u8eab\u5c0f\u5de7\u529f\u592b\uff0c\u4e13\u95e8\u5bfb\u6687\u62b5\u9699\uff0c\u63a2\u5bfb\u7a74\u9053\u3002\u82e5\u53ea\u662f\u4ee5\u4e00\u5bf9\u4e00\uff0c\u97e9\u5fd7\u90a6\u7684\u672c\u8eab \u529f\u592b\u8fd8\u5c3d\u53ef\u5bf9\u4ed8\u5f97\u4e86\uff0c\u800c\u4eca\u662f\u4ee5\u4e00\u654c\u4e8c\uff0c\u9976\u662f\u97e9\u5fd7\u90a6\u7528\u5c3d\u529f\u592b\uff0c\u4e5f\u53ea\u662f\u582a\u582a\u6253\u4e2a\u5e73\u624b\u3002 \u3000\u3000\u6253\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u97e9\u5fd7\u90a6\u5df2\u611f\u5403\u529b\uff0c\u5077\u773c\u770b\u90a3\u4e09\u4e2a\u5587\u561b\uff0c\u867d\u7136\u51cf\u4e86\u538b\u529b\uff0c\u4e5f\u4e0d\u8fc7\u662f\u521a\u521a\u62b5 \u5fa1\u5f97\u4f4f\u3002\u4ed6\u5fc3\u4e2d\u70e6\u8e81\uff0c\u8d81\u90a3\u4f7f\u952f\u9f7f\u5200\u7684\u4e00\u5200\u5411\u81ea\u5df1\u5288\u6765\u65f6\uff0c\u4fa7\u8eab\u4e00\u95ea\uff0c\u731b\u7684\u8eab\u968f\u5200\u8d70\uff0c\u7d2b\u91d1 \u5200\u626c\u7a7a\u4e00\u95ea\uff0c\u5728\u4f7f\u5224\u5b98\u7b14\u7684\u9762\u95e8\u4e0a\u6643\u4e86\u4e00\u6643\uff0c\u90a3\u4f7f\u5224\u5b98\u7b14\u7684\u4ee5\u4e3a\u4ed6\u4f7f\u7684\u662f\u201c\u6a2a\u65a9\u201d\u62db\u6570\uff0c\u53cc \u80a9\u4e00\u7eb5\uff0c\u6b63\u5f85\u62bd\u7b14\u8fdb\u62db\uff0c\u4e0d\u6599\u97e9\u5fd7\u90a6\u5200\u6cd5\u5341\u5206\u5947\u7279\uff0c\u5200\u5149\u4e00\u95ea\u4e4b\u95f4\uff0c\u5200\u5c16\u4e00\u5d29\uff0c\u7adf\u7136\u7a7f\u7b14\u4e0a \u6311\uff0c\u628a\u90a3\u4eba\u7684\u80a9\u5934\u6233\u4e86\u4e00\u4e2a\u5927\u6d1e\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u66f4\u4e0d\u8f6c\u8eab\uff0c\u542c\u5f97\u80cc\u540e\u98ce\u58f0\uff0c\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u53cd\u624b\u5c31\u662f\u4e00\u5200\uff0c\u90a3\u4f7f\u952f\u9f7f\u5200\u7684\u4e00\u5200\u780d \u7a7a\uff0c\u7ed9\u97e9\u5fd7\u90a6\u53cd\u624b\u51fb\u4e2a\u6b63\u7740\uff0c\u952f\u9f7f\u5200\u545b\u5577\u4e00\u58f0\uff0c\u6389\u5728\u5730\u4e0a\u3002\u97e9\u5fd7\u90a6\u8fd9\u624d\u8f6c\u8fc7\u8eab\u6765\uff0c\u7d2b\u91d1\u5200\u7528 \u529b\u5288\u4e0b\uff0c\u5c06\u90a3\u4eba\u5288\u6210\u4e24\u7247\u3002\u4f7f\u5224\u5b98\u7b14\u7684\u5fcd\u75db\u7eb5\u8d77\uff0c\u6ca1\u547d\u5954\u9003\uff0c\u97e9\u5fd7\u90a6\u4e5f\u4e0d\u7406\u4ed6\uff0c\u5f84\u81ea\u63d0\u5200\uff0c \u52a0\u5165\u6218\u56e2\uff0c\u53bb\u63f4\u52a9\u90a3\u4e09\u4e2a\u5587\u561b\u3002 \u3000\u3000\u90a3\u53e6\u5916\u4e24\u4e2a\u7a7f\u7740\u7981\u536b\u519b\u670d\u9970\u7684\u519b\u5b98\uff0c\u548c\u5587\u561b\u6253\u5f97\u6b63\u9163\u3002\u97e9\u5fd7\u90a6\u9aa4\u5730\u95ef\u4e86\u8fdb\u6765\uff0c\u624b\u8d77\u4e00 \u5200\uff0c\u5206\u5fc3\u523a\u8fdb\uff0c\u8eab\u6cd5\u8fc5\u901f\u4e4b\u6781\uff0c\u767b\u65f6\u628a\u4e00\u4e2a\u654c\u4eba\u523a\u5012\u5730\u4e0a\uff1b\u53e6\u4e00\u4e2a\u654c\u4eba\u89c1\u72b6\u5927\u60ca\uff0c\u624b\u6267\u94f6 \u67aa\uff0c\u5f80\u5916\u4e00\u683c\uff0c\u97e9\u5fd7\u90a6\u970d\u5730\u56de\u8eab\uff0c\u8fde\u4eba\u5e26\u5200\u4e00\u8f6c\uff0c\u706f\u5149\u95ea\u70c1\uff0c\u659c\u63a0\u8fc7\u53bb\uff0c\u5200\u950b\u8d34\u7740\u67aa\u6746\u5411\u4e0a \u4fbf\u524a\u3002\u90a3\u4eba\u6025\u6025\u677e\u624b\uff0c\u94f6\u67aa\u6389\u843d\u5730\u4e0a\uff0c\u97e9\u5fd7\u90a6\u6b3a\u8eab\u6025\u8fdb\uff0c\u5de6\u624b\u4e00\u62ac\uff0c\u4e00\u628a\u6293\u7740\u654c\u4eba\u624b\u8155\u6602\u529b \u4e00\u62d7\uff0c\u90a3\u4eba\u75db\u5f97\u5927\u53eb\u8d77\u6765\uff0c\u670d\u670d\u8d34\u8d34\u5730\u7ed9\u97e9\u5fd7\u90a6\u50cf\u7275\u7f8a\u4e00\u6837\u7275\u7740\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u4eca\u65e5\u8fde\u8d25\u516d\u4e2a\u7981\u536b\u519b\u519b\u5b98\uff0c\u6240\u7528\u7684\u5200\u6cd5\u638c\u6cd5\uff0c\u5168\u662f\u4ece\u77f3\u58c1\u4e0a\u7684\u753b\u50cf\u5b66\u6765\u7684\uff0c\u6bcf\u4e00 \u62db\u4f7f\u51fa\uff0c\u90fd\u6709\u5947\u6548\uff0c\u771f\u662f\u53c8\u60ca\u53c8\u559c\u3002\u8fd9\u65f6\u5fc3\u4e2d\u5feb\u6d3b\u4e4b\u6781\uff0c\u6293\u7740\u90a3\u4e2a\u519b\u5b98\u9053\uff1a\u201c\u4f60\u4eec\u5e73\u65f6\u6b3a\u4fae \u8001\u767e\u59d3\u4e5f\u6b3a\u4fae\u5f97\u591f\u4e86\uff0c\u4eca\u513f\u53ef\u8981\u4f60\u53d7\u4e00\u70b9\u82e6\u3002\u201d\u7528\u529b\u4e00\u626d\uff0c\u90a3\u4eba\u5927\u58f0\u53eb\u9053\uff1a\u201c\u597d\u6c49\u9976\u547d\uff01\u201d \u97e9\u5fd7\u90a6\u7b11\u9053\uff1a\u201c\u4f60\u8981\u9976\u547d\u4e5f\u4e0d\u96be\uff0c\u4f60\u5f97\u544a\u8bc9\u6211\u4eec\uff0c\u4f60\u4eec\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u519b\u5b98\u9053\uff1a\u201c\u6211\u4eec\u5949 \u547d\u5206\u9014\u67e5\u63a2\u51cc\u672a\u98ce\u7684\u8e2a\u8ff9\u3002\u201d\u97e9\u5fd7\u90a6\u5927\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8fde\u6211\u4e5f\u6253\u4e0d\u8fc7\uff0c\u8fd8\u6562\u53bb\u8ffd\u51cc\u672a\u98ce\u3002\u201d\u90a3 \u519b\u5b98\u6390\u5a9a\u966a\u7b11\u9053\uff1a\u201c\u4f60\u8001\u7237\u5b50\u7684\u6b66\u529f\u6bd4\u51cc\u672a\u98ce\u8fd8\u5f3a\uff01\u201d\u97e9\u5fd7\u90a6\u9a82\u9053\uff1a\u201c\u8c01\u8981\u4f60\u4e71\u9001\u9ad8\u5e3d\uff01\u201d \u4ed6\u53e3\u4e2d\u6012\u9a82\uff0c\u5fc3\u4e2d\u5374\u6709\u7740\u4e00\u79cd\u83ab\u540d\u5176\u5999\u7684\u5feb\u610f\uff0c\u5fc3\u9053\uff1a\u201c\u4eba\u4eec\u4e5f\u8bc6\u5f97\u6211\u4e86\uff01\u201d\u5f53\u4e0b\u7528\u529b\u4e00 \u63a8\uff0c\u559d\u9053\uff1a\u201c\u65e2\u7136\u4f60\u8bf4\u5b9e\u8bdd\uff0c\u5c31\u9976\u4e86\u4f60\u5427\uff01\u201d\u90a3\u519b\u5b98\u6025\u6025\u62b1\u5934\u9f20\u7a9c\uff0c\u8fde\u671b\u90fd\u4e0d\u6562\u56de\u671b\u3002 \u3000\u3000\u4e09\u4e2a\u5587\u561b\u9f50\u6765\u9053\u8c22\uff0c\u5c24\u5176\u90a3\u4e2a\u539f\u5148\u8bc6\u5f97\u7684\u5587\u561b\uff0c\u66f4\u662f\u4e00\u628a\u5c06\u4ed6\u62b1\u7740\uff0c\u543b\u4ed6\u7684\u989d\u3002\u97e9\u5fd7\u90a6 \u4e0d\u60ef\u8fd9\u4e2a\u793c\u8282\uff0c\u5ff8\u6029\u7b11\u9053\uff1a\u201c\u7b97\u4e86\u7b97\u4e86\uff0c\u4f60\u4eec\u662f\u6765\u627e\u2018\u820d\u5229\u5b50\u2019\u7684\u5417\uff1f\u201d\u90a3\u719f\u6089\u7684\u5587\u561b\uff0c\u540d \u53eb\u5b97\u8fbe\xb7\u5b8c\u771f\uff0c\u544a\u8bc9\u4ed6\u9053\uff1a\u4ed6\u4eec\u90a3\u5929\u5931\u6389\u4e86\u820d\u5229\u5b50\u540e\uff0c\u672a\u66fe\u56de\u8f6c\u897f\u85cf\uff0c\u5df2\u6309\u8fde\u78b0\u5230\u6765\u8fce\u63a5\u5723 \u7269\u7684\u50e7\u4fa3\uff0c\u4ed6\u4eec\u5929\u5929\u51fa\u6765\u67e5\u63a2\u5f20\u5929\u8499\u7684\u8e2a\u8ff9\u3002\u867d\u7136\u6599\u60f3\u5f20\u5929\u8499\u53ef\u80fd\u5df2\u8fdc\u8d70\u9ad8\u98de\uff0c\u4f46\u4ed6\u4eec\u8fd8\u662f \u672a\u6b7b\u5fc3\u3002\u5c24\u5176\u90a3\u672a\u89c1\u8fc7\u820d\u5229\u5b50\u7684\u5587\u561b\uff0c\u66f4\u662f\u7ecf\u5e38\u8981\u4ed6\u966a\u7740\uff0c\u5728\u4e91\u5c97\u77f3\u7a9f\u9644\u8fd1\u5f98\u5f8a\uff0c\u4e0d\u6599\u5c31\u78b0 \u5230\u8fd9\u6279\u519b\u5b98\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u542c\u540e\uff0c\u5927\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5bfb\u8bbf\u5723\u7269\u4e5f\u771f\u8bda\u5fc3\uff0c\u4f60\u4eec\u770b\u770b\u8fd9\u4e2a\uff01\u201d\u8bf4\u7740\u4ece\u6000\u4e2d\u638f\u51fa \u64c5\u9999\u76d2\u5b50\u6765\uff0c\u6253\u5f00\u7ed9\u4ed6\u4eec\u4e00\u770b\uff0c\u5b97\u8fbe\xb7\u5b8c\u771f\u559c\u6781\u72c2\u547c\uff1a\u201c\u8fd9\u662f\u820d\u5229\u5b50\uff01\u201d\u6251\u7684\u5c31\u8dea\u5728\u5730\u4e0a\u53e9 \u5934\uff0c\u5176\u4ed6\u4e24\u4e2a\u5587\u561b\u5148\u662f\u4e00\u6014\uff0c\u8ddf\u7740\u660e\u767d\u8fc7\u6765\uff0c\u4e5f\u6025\u6025\u53e9\u5934\u793c\u8d5e\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u7ed9\u4ed6\u4eec\u8fd9\u4e48\u4e00\u95f9\uff0c\u4e0d\u77e5\u6240\u63aa\uff0c\u5ffd\u7136\u95f4\uff0c\u90a3\u4e09\u4e2a\u5587\u561b\u9f50\u7ad9\u4e86\u8d77\u6765\uff0c\u4ece\u6000\u91cc\u53d6\u51fa\u4e00\u6761 \u4e1d\u5dfe\uff0c\u53cc\u624b\u6367\u7740\uff0c\u9012\u5230\u97e9\u5fd7\u90a6\u9762\u524d\uff0c\u97e9\u5fd7\u90a6\u77e5\u9053\u8fd9\u662f\u5587\u561b\u6700\u5c0a\u91cd\u7684\u793c\u8282\uff0c\u540d\u53eb\u201c\u732e\u54c8\u8fbe\u201d\u3002 \u6025\u6025\u8bf4\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u6562\u5f53\uff0c\u8fd9\u600e\u4e48\u6562\u5f53\uff01\u201d\u5b97\u8fbe\xb7\u5b8c\u771f\u4ee3\u8868\u5587\u561b\u8bf4\u9053\uff1a\u201c\u4ece\u6b64\u4f60\u4fbf\u662f\u6211\u4eec\u5587 \u561b\u7684\u5927\u6069\u4eba\uff0c\u6211\u4eec\u671b\u4f60\u80fd\u591f\u968f\u6211\u4eec\u5230\u897f\u85cf\u3002\u201d\u97e9\u5fd7\u90a6\u5148\u662f\u8c26\u8ba9\uff0c\u7ee7\u7740\u60f3\u4e86\u4e00\u60f3\uff0c\u542b\u7b11\u70b9\u5934\u7b54 \u5e94\u3002\u8fd9\u4e00\u53bb\uff0c\u8981\u76f4\u5230\u51e0\u5e74\u540e\u4ed6\u624d\u80fd\u518d\u4e0e\u51cc\u672a\u98ce\u3001\u5218\u90c1\u82b3\u89c1\u9762\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u516d\u7ae0\u3000\u96fe\u6c14\u5f25\u6f2b\u3000\u8352\u6751\u6765\u5f02\u58eb\u3000\u6e56\u5149\u6f88\u6e58\u3000\u5e7d\u8c37\u51fa\u5f81\u9a91 \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u516d\u56de \u96fe\u6c14\u5f25\u6f2b\u3000\u8352\u6751\u6765\u5f02\u58eb \u6e56\u5149\u6f88\u6e58\u3000\u5e7d\u8c37\u51fa\u5f81\u9a91 \u3000\u3000\u5f53\u97e9\u5fd7\u90a6\u548c\u5587\u561b\u4eec\u7a7f\u8d8a\u5eb7\u85cf\u9ad8\u539f\u7684\u65f6\u5019\uff0c\u51cc\u672a\u51e4\u548c\u5218\u90c1\u82b3\uff0c\u4e5f\u6b63\u5728\u4e91\u8d35\u9ad8\u539f\u4e0a\u4ec6\u4ec6\u98ce \u5c18\u3002\u5341\u591a\u5929\u6765\u7684\u65c5\u884c\uff0c\u5728\u4ed6\u4eec\u4e24\u4eba\u4e4b\u95f4\uff0c\u6ecb\u957f\u4e86\u4e00\u79cd\u6781\u4e3a\u5947\u5f02\u7684\u611f\u60c5\u3002\u5218\u90c1\u82b3\u611f\u89c9\u5230\uff0c\u51cc\u672a \u98ce\u5bf9\u5979\u6709\u65f6\u597d\u50cf\u662f\u591a\u5e74\u7684\u8001\u53cb\uff0c\u6709\u65f6\u53c8\u597d\u50cf\u662f\u5b8c\u5168\u964c\u751f\u7684\u4eba\u3002\u4ed6\u4e00\u8def\u4e0a\u90fd\u5f88\u77dc\u6301\u3002\u4f46\u5728\u6545\u610f \u7684\u51b7\u6f20\u4e2d\uff0c\u5374\u4e0d\u65f6\u53c8\u81ea\u7136\u6d41\u9732\u51fa\u4e00\u79cd\u5173\u6000\uff0c\u4e00\u4efd\u60c5\u610f\u3002\u5218\u90c1\u82b3\u6709\u751f\u4ee5\u6765\uff0c\u4ece\u672a\u66fe\u53d7\u8fc7\u4eba\u8fd9\u6837 \u51b7\u8c08\uff0c\u4e5f\u4ece\u672a\u66fe\u53d7\u8fc7\u4eba\u8fd9\u6837\u5173\u6000\u3002\u5728\u8fd9\u79cd\u9519\u7efc\u590d\u6742\u7684\u611f\u60c5\u4e2d\uff0c\u663e\u5f97\u662f\u5982\u6b64\u77db\u76fe\uff0c\u53c8\u662f\u5982\u6b64\u79bb \u5947\uff0c\u5979\u867d\u7136\u662f\u4e45\u5386\u6c5f\u6e56\u3001\u60ef\u7ecf\u98ce\u6d6a\u7684\u5973\u4e2d\u8c6a\u6770\uff0c\u5728\u611f\u60c5\u7684\u7f51\u4e2d\uff0c\u4e5f\u6b63\u5982\u8718\u86db\u4e4b\u7518\u4e8e\u81ea\u7f1a\u4e86\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u5979\u66fe\u6000\u7591\u8fc7\u51cc\u672a\u98ce\u5c31\u662f\u5979\u5c11\u5e74\u65f6\u4ee3\u7684\u670b\u53cb\uff0c\u4f46\u8fd9\u600e\u4e48\u53ef\u80fd\u5462\uff1f\u5f53\u5e74\u51fa\u4e8b\u4e4b\u5915\uff0c\u5979 \u660e\u660e\u770b\u5230\u4ed6\u7684\u8863\u5c65\u5728\u94b1\u5858\u6c5f\u4e0a\u6f02\u6d6e\uff0c\u4e5f\u8bb8\u4ed6\u7684\u5c38\u4f53\u5df2\u6f02\u51fa\u5927\u6d77\u4e0e\u957f\u9cb8\u4e3a\u4f0d\u4e86\uff01\u800c\u51cc\u672a\u98ce\u7684\u76f8 \u8c8c\u3001\u58f0\u97f3\uff0c\u4e5f\u90fd\u4e0e\u5979\u5fc3\u4e2d\u591a\u5e74\u6765\u85cf\u7740\u7684\u5f71\u5b50\u4e0d\u540c\u3002\u53ea\u662f\u51cc\u672a\u98ce\u5728\u6c89\u601d\u65f6\u7ede\u626d\u624b\u6307\u7684\u4e60\u60ef\uff0c\u5374 \u4e0e\u201c\u4ed6\u201d\u5b8c\u5168\u4e00\u6837\u3002\u5218\u90c1\u82b3\u5230\u5e95\u662f\u4e2a\u8235\u4e3b\uff0c\u5979\u53c8\u4e0d\u6562\u5766\u767d\u8bf4\u51fa\u5979\u7684\u6000\u7591\uff0c\u53ea\u662f\u7ecf\u5e38\u5728\u65c5\u9014\u4e0a \u9ed8\u9ed8\u5730\u6ce8\u89c6\u7740\u51cc\u672a\u98ce\uff0c\u5e0c\u671b\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u53d1\u73b0\u66f4\u591a\u7684\u76f8\u540c\u4e4b\u70b9\uff0c\u51cc\u672a\u98ce\u4e5f\u597d\u50cf\u53d1\u73b0\u4e86\u5979\u7684\u6ce8 \u610f\uff0c\u65f6\u4e0d\u65f6\u62a5\u4ee5\u6de1\u6de1\u7684\u4e00\u7b11\u3002 \u3000\u3000\u5341\u591a\u5929\u7684\u65c5\u884c\uff0c\u5728\u6fc0\u52a8\u4e0e\u5947\u5f02\u7684\u60c5\u611f\u51b2\u51fb\u4e0b\u8fc7\u53bb\u4e86\u3002\u8fd9\u5929\u4ed6\u4eec\u5df2\u5230\u534e\u5b81\uff0c\u8ddd\u79bb\u6606\u660e\u53ea\u6709 \u4e09\u767e\u591a\u91cc\u4e86\u3002\u4ed6\u4eec\u62c2\u6653\u8d77\u6765\u8d76\u8def\uff0c\u8d70\u4e86\u4e00\u7a0b\uff0c\u51cc\u672a\u98ce\u7b11\u6307\u7740\u8fdc\u65b9\u9053\uff1a\u201c\u4ee5\u6211\u4eec\u7684\u811a\u7a0b\uff0c\u4eca\u5929 \u508d\u665a\uff0c\u5f53\u4f1a\u8d76\u5230\u6606\u660e\u4e86\u3002\u201d\u4ed6\u4eec\u6b63\u884c\u8fdb\u4e00\u4e2a\u5e7d\u8c37\uff0c\u731b\u7136\u95f4\uff0c\u5929\u8272\u9634\u6697\uff0c\u5e7d\u8c37\u4e0a\u9762\u96fe\u6c14\u5f25\u6f2b\uff0c \u8d8a\u6765\u8d8a\u6d53\uff0c\u6e10\u6e10\u5929\u9ed1\u5982\u58a8\uff0c\u773c\u524d\u7684\u9053\u8def\u4e5f\u770b\u4e0d\u6e05\u695a\u4e86\u3002\u51cc\u672a\u98ce\u9a87\u7136\u60ca\u547c\uff1a\u201c\u8fd9\u662f\u4e4c\u8499\u5c71\u7684\u6d53 \u96fe\uff0c\u968f\u7740\u6d53\u96fe\u800c\u6765\u7684\u5e38\u662f\u7634\u6c14\uff0c\u6211\u4eec\u53ef\u8981\u5c0f\u5fc3\uff01\u201d\u4ed6\u4eec\u5c4f\u4f4f\u547c\u5438\uff0c\u6478\u7d22\u524d\u884c\uff0c\u53c8\u8fc7\u4e86\u7247\u523b\uff0c \u5ffd\u7136\u773c\u524d\u4e00\u4eae\uff0c\u524d\u9762\u662f\u4e00\u4e2a\u5927\u6e56\uff0c\u5728\u7fa4\u5cf0\u56f4\u7ed5\u4e4b\u95f4\uff0c\u5e73\u9759\u5730\u8eba\u7740\uff0c\u8fd9\u6e56\u903c\u6eba\u5982\u5e26\uff0c\u6e56\u4e0a\u6709\u6735 \u6735\u767d\u4e91\u5728\u5cf0\u5ce6\u95f4\u98d8\u8361\u3002\u4ece\u5c71\u8170\u5230\u5c71\u811a\uff0c\u6ee1\u5e03\u7740\u82cd\u7eff\u8272\u7684\u6749\u6811\u548c\u67cf\u6811\uff0c\u6709\u4e9b\u6811\u6728\uff0c\u4e00\u76f4\u63d2\u5230\u6e56 \u91cc\u3002\u98ce\u666f\u7aef\u7684\u79c0\u4e3d\u3002\u8fd9\u65f6\u4e0a\u7a7a\u867d\u7136\u6d53\u96fe\u5f25\u6f2b\uff0c\u4e0b\u9762\u6e56\u6c34\u5374\u662f\u78a7\u6ce2\u7ff1\u7ff1\uff0c\u6e56\u9762\u6709\u5982\u4e00\u7247\u767d\u7389\uff0c \u4f46\u6d53\u96fe\u4e0b\u663e\u5f97\u5206\u5916\u6676\u83b9\u3002\u5218\u90c1\u82b3\u6478\u51fa\u5730\u56fe\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u2018\u629a\u4ed9\u6e56\u2019\uff0c\u5728\u8fd9\u91cc\u7634\u6c14\u8f83\u8584\uff0c\u6211\u4eec \u4e0d\u5982\u5728\u8fd9\u91cc\u7a0d\u7a0d\u505c\u7559\u3002\u201d \u3000\u3000\u4e24\u4eba\u8fb9\u8c08\u8fb9\u884c\uff0c\u7634\u6c14\u968f\u6d53\u96fe\u800c\u6765\uff0c\u867d\u8bf4\u6709\u6e56\u4e2d\u6c34\u6c14\u907f\u7634\uff0c\u4e5f\u89c9\u547c\u5438\u4e0d\u8212\u3002\u4e24\u4eba\u6b63\u60f3\u6b47 \u4e0b\uff0c\u5ffd\u89c9\u6709\u4e00\u9635\u9635\u9999\u6c14\uff0c\u8fdc\u8fdc\u88ad\u6765\uff0c\u7634\u6c14\u987f\u89e3\u3002\u4e24\u4eba\u5927\u559c\uff0c\u8fce\u7740\u9999\u6c14\u627e\u5bfb\uff0c\u4e0d\u4e45\u5c31\u53d1\u73b0\u4e00\u5806 \u91ce\u706b\uff0c\u6709\u8bb8\u591a\u5934\u4e0a\u7f20\u7740\u5305\u4e2d\u7684\u7537\u5973\u56f4\u706b\u5750\u7740\u3002\u51cc\u672a\u98ce\u89c1\u591a\u8bc6\u5e7f\uff0c\u77e5\u9053\u8fd9\u662f\u5f5d\u65cf\u5c71\u6c11\u70e7\u8d77\u4e91\u5357 \u7279\u4ea7\u7684\u9999\u8305\u6765\u907f\u7634\uff0c\u6e56\u8fb9\u5927\u7ea6\u6709\u4e2a\u5c71\u6751\uff0c\u6240\u4ee5\u4e00\u9047\u6d53\u96fe\u7634\u6c14\uff0c\u6751\u6c11\u5c31\u5c06\u5e73\u65e5\u805a\u96c6\u7684\u9999\u8305\u70e7\u8d77 \u91ce\u706b\uff0c\u4e00\u540c\u907f\u7634\u3002\u51cc\u672a\u98ce\u6025\u6025\u4e0e\u5218\u90c1\u82b3\u8d76\u4e0a\u524d\u53bb\uff0c\u548c\u6751\u6c11\u4eec\u6253\u62db\u547c\uff0c\u6307\u5929\u7a7a\uff0c\u6253\u624b\u52bf\uff0c\u5477\u5477 \u54d1\u54d1\uff0c\u8868\u8fbe\u6765\u610f\u3002\u5f5d\u6c11\u6c11\u98ce\u7eaf\u6734\uff0c\u4e00\u89c1\u5c31\u77e5\u4ed6\u4eec\u6765\u610f\uff0c\u7acb\u523b\u6709\u4eba\u8ba9\u51fa\u4f4d\u7f6e\u6765\uff0c\u8bf7\u4ed6\u4eec\u5750\u4e0b\u3002 \u51cc\u672a\u98ce\u5750\u4e0b\u65f6\uff0c\u5ffd\u89c9\u4eba\u7fa4\u4e2d\uff0c\u4f3c\u63ba\u6742\u6709\u4e24\u4e2a\u6c49\u4eba\uff0c\u5b9a\u775b\u770b\u7740\u81ea\u5df1\uff0c\u51cc\u672a\u98ce\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5fd9\u7528\u4e24 \u624b\u63cd\u770b\u9762\u5e9e\uff0c\u63a9\u7740\u5200\u75d5\uff0c\u4f4e\u4e0b\u5934\u6765\u70e4\u706b\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5934\u4e0a\u70df\u96fe\u66f4\u6d53\uff0c\u5f5d\u6c11\u4eec\u53c8\u52a0\u8fdb\u8bb8\u591a\u9999 \u8305\uff0c\u628a\u706b\u5f04\u5f97\u66f4\u65fa\uff0c\u8fd9\u65f6\u6e56\u7554\u53c8\u6709\u4e00\u4e2a\u4eba\u5feb\u6b65\u8dd1\u6765\uff0c\u51cc\u672a\u98ce\u770b\u4ed6\u6b65\u5c65\u77eb\u5065\uff0c\u4fbf\u77e5\u662f\u4e2a\u6b66\u6797\u9ad8 \u624b\u3002\u4f46\u5230\u8d70\u8fd1\u4e00\u770b\uff0c\u539f\u662f\u4e66\u751f\u6253\u626e\uff0c\u751f\u5f97\u5f88\u6e05\u79c0\uff0c\u770b\u6837\u5b50\u4e0d\u8fc7\u4e8c\u5341\u6765\u5c81\uff0c\u8fd9\u4eba\u61c2\u5f97\u5f5d\u6c11\u8bed \u8a00\uff0c\u4e00\u5230\u6765\uff0c\u5c31\u548c\u5f5d\u4eba\u5927\u58f0\u8bf4\u7b11\uff0c\u4f3c\u4e4e\u4ed6\u5728\u8fd9\u91cc\u8fd8\u6709\u719f\u4eba\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5728\u5e7d\u8c37\u91cc\u53c8\u51b2\u51fa\u51e0\u4e2a \u9ec4\u8863\u5927\u6c49\uff0c\u51cc\u672a\u98ce\u8fdc\u8fdc\u4e00\u770b\uff0c\u4f4e\u4f4e\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u7528\u624b\u8098\u78b0\u78b0\u5218\u90a3\u82b3\uff0c\u53eb\u5979\u8f6c\u8fc7\u8138\u672a\uff0c\u4e0d\u8981 \u548c\u6765\u4eba\u7167\u9762\u3002\u8fd9\u4e9b\u4eba\u5f88\u662f\u5f3a\u6a2a\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u5148\u548c\u5f5d\u4eba\u62db\u547c\uff0c\u5c31\u6324\u4e86\u8fdb\u6765\uff0c\u6070\u597d\u5750\u5728\u4e24\u4e2a\u6c49\u4eba\u7684 \u65c1\u8fb9\u3002\u7634\u6c14\u9713\u6c14\u5f25\u6f2b\u4e2d\uff0c\u5ffd\u542c\u5f97\u6ee1\u7a7a\u60ca\u79bd\u4e71\u53eb\uff0c\u6709\u4e00\u5927\u7fa4\u9e1f\u51b2\u51fa\u6d53\u96fe\uff0c\u5728\u706b\u5806\u4e0a\u76d8\u65cb\u4f4e\u98de\u3002 \u8fd9\u7fa4\u98de\u9e1f\u5927\u7ea6\u4e5f\u662f\u8010\u4e0d\u4f4f\u7634\u6c14\u98de\u4e0b\u6765\u7684\u3002\u6709\u51e0\u4e2a\u5f5d\u4eba\uff0c\u624b\u91cc\u62ff\u7740\u957f\u957f\u7684\u7af9\u7aff\uff0c\u7b49\u7740\u9e1f\u513f\u98de\u4f4e \u65f6\uff0c\u7a81\u7136\u4e00\u7aff\u63b7\u53bb\uff0c\u5c45\u7136\u7ed9\u4ed6\u4eec\u6253\u4e0b\u5341\u6765\u53ea\u98de\u9e1f\u3002\u4f46\u5230\u4e86\u540e\u6765\uff0c\u9e1f\u513f\u4e5f\u7075\u8b66\u4e86\uff0c\u5b83\u4eec\u867d\u7136\u4e3a \u4e86\u8eb2\u907f\u7634\u6c14\uff0c\u4e0d\u80fd\u4e0d\u4f4e\u98de\u4e0b\u6765\uff0c\u76d8\u65cb\u5728\u706b\u96be\u4e4b\u4e0a\uff0c\u4f46\u5b83\u4eec\u4f4e\u98de\u8f7b\u63a0\uff0c\u4e00\u89c1\u7aff\u5f71\uff0c\u4fbf\u5373\u9ad8\u98de\uff0c \u5f5d\u6c11\u4eec\u5948\u4f55\u5b83\u4eec\u4e0d\u5f97\u3002\u5148\u6765\u7684\u4e24\u4e2a\u6c49\u4eba\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u5404\u81ea\u5411\u5f5d\u6c11\u4eec\u8ba8\u8fc7\u4e86\u679d\u7af9\u7aff\uff0c\u7ad9\u7acb\u8d77 \u6765\uff0c\u53ea\u89c1\u4ed6\u4eec\u7af9\u7aff\u821e\u5904\uff0c\u77eb\u5982\u6e38\u9f99\uff0c\u4f4e\u98de\u7684\u79bd\u9e1f\uff0c\u4e00\u78b0\u7740\u5c31\u843d\u4e0b\u6765\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u5c31\u6253\u4e0b\u4e86\u4e00 \u5927\u5806\u98de\u9e1f\u3002\u9e1f\u7fa4\u5413\u5f97\u632f\u7fc5\u4e71\u98de\uff0c\u98de\u51fa\u4e86\u7af9\u7aff\u6240\u80fd\u5230\u8fbe\u7684\u8303\u56f4\u3002\u540e\u6765\u7684\u90a3\u51e0\u4e2a\u9ec4\u8863\u5927\u6c49\uff0c\u53d1\u51fa \u51b7\u51b7\u7684\u7b11\u58f0\uff0c\u5176\u4e2d\u4e00\u4eba\u84e6\u7136\u5728\u5730\u4e0a\u62e3\u8d77\u4e86\u4e00\u5757\u77f3\u5934\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u53ea\u7b11\u4e86\u58f0\u9053\uff1a\u201c\u4f55\u5fc5\u8fd9\u6837\u8d39 \u4e8b\uff0c\u770b\u6211\u7684\u5427\uff01\u201d\u4ed6\u5c06\u624b\u4e2d\u7684\u77f3\u5934\u7528\u529b\u4e00\u6413\uff0c\u53cc\u624b\u4e00\u626c\uff0c\u53ea\u89c1\u788e\u77f3\u7eb7\u98de\u6253\u51fa\uff0c\u7a7a\u4e2d\u7684\u98de\u9e1f\uff0c \u7eb7\u7eb7\u843d\u4e0b\u3002\u90a3\u4e2a\u6c49\u4eba\u6025\u6025\u653e\u4e0b\u4e86\u7af9\u7aff\uff0c\u62b1\u62f3\u8bf7\u95ee\u3002\u90a3\u9ec4\u8863\u4eba\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c\u5bf9\u5176\u4e2d\u4e00\u4eba\u8bf4 \u9053\uff1a\u201c\u91d1\u5d16\uff0c\u4f60\u4e0d\u8ba4\u5f97\u6211\uff0c\u6211\u53ef\u8fd8\u8ba4\u5f97\u4f60\uff0c\u542c\u8bf4\u4f60\u5728\u5e73\u5357\u738b\u5c1a\u4e4b\u4fe1\u5904\u5f88\u662f\u5f97\u610f\uff0c\u8fd9\u4f4d\u670b\u53cb\uff0c \u60f3\u6765\u4e5f\u662f\u738b\u5e9c\u4e2d\u7684\u5f97\u529b\u4eba\u624b\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e2a\u5524\u4f5c\u91d1\u5d16\u7684\u770b\u4e86\u4ed6\u534a\u664c\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u524d\u8f88\u53ef\u662f\u90b1\u4e1c\u6d1b\u5148\u751f\uff0c\u5341\u5e74\u524d\u4f3c\u5728\u5386\u57ce\u89c1 \u8fc7\uff0c\u524d\u8f88\u5728\u90a3\u91cc\u5f97\u610f\uff1f\u201d\u90b1\u4e1c\u6cbb\u89c1\u4ed6\u53e3\u53e3\u58f0\u58f0\u4ee5\u665a\u8f88\u81ea\u5c45\uff0c\u9762\u8272\u7a0d\u7a0d\u597d\u8f6c\uff0c\u4f46\u4ecd\u662f\u8feb\u8fd1\u4e00 \u6b65\uff0c\u5927\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u4ece\u5c1a\u4e4b\u4fe1\u5904\u6765\uff0c\u5e26\u4ec0\u4e48\u4e1c\u897f\u53bb\u89c1\u5434\u4e09\u6842\uff0c\u7ed9\u6211\u770b\u770b\uff1f\u201d\u91d1\u5d16\u9762\u8272\u5927\u53d8\uff0c \u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u6055\u665a\u8f88\u4e0d\u80fd\u4ece\u547d\uff01\u201d\u90b1\u4e1c\u6d1b\u9634\u4fa7\u607b\u51b7\u7b11\u7740\u5bf9\u540c\u6765\u7684\u4e09\u4e2a\u4eba\u8bf4\u9053\uff1a\u201c\u641c\u4ed6\uff01\u201d \u90a3\u4e09\u4e2a\u9ec4\u8863\u4eba\u9f50\u9f50\u6251\u53bb\uff0c\u91d1\u5d16\u53cc\u638c\u75be\u53d1\uff0c\u89d1\u51c6\u5f53\u524d\u4e00\u4eba\uff0c\u4e00\u8bb0\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u5de6\u53f3\u5f00\u5f13\uff0c\u5c31 \u6253\u8fc7\u53bb\uff0c\u90a3\u5165\u4fa7\u8eab\u4e00\u907f\uff0c\u91d1\u5d16\u54e9\u7684\u5982\u7bad\u51b2\u51fa\uff0c\u90a3\u4e09\u4e2a\u5927\u58f0\u547c\u559d\uff0c\u5305\u6284\u4e0a\u6765\u3002\u91d1\u5d16\u7684\u540c\u4f34\u65b9\u60f3 \u51fa\u624b\u76f8\u52a9\uff0c\u5df2\u7ed9\u90b1\u4e1c\u6d1b\u4e00\u9897\u788e\u77f3\uff0c\u6253\u4e2d\u7a74\u9053\uff0c\u767b\u65f6\u8f6f\u762b\u5730\u4e0a\u3002\u8fd9\u51e0\u4e2a\u4eba\u4e00\u9635\u5927\u95f9\uff0c\u5f5d\u6c11\u4eec\u7eb7 \u7eb7\u8d70\u907f\u3002\u51cc\u672a\u98ce\u968f\u4f17\u7ad9\u4e86\u8d77\u6765\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u90a3\u51e0\u4e2a\u4eba\u5df2\u6253\u8fd1\u4ed6\u7684\u8eab\u8fb9\u3002\u90a3\u4e09\u4e2a\u9ec4\u8863\u5927\u6c49\uff0c\u52c7 \u731b\u975e\u5e38\uff0c\u4e09\u9762\u56f4\u653b\uff0c\u62f3\u843d\u5982\u96e8\u3002\u91d1\u5d16\u7b49\u4e8e\u662f\u6e9c\u6ed1\uff0c\u4e00\u9762\u62db\u67b6\uff0c\u4e00\u9762\u95ea\u907f\uff0c\u6e9c\u5165\u4eba\u4e1b\u4e4b\u4e2d\uff0c\u4e3a \u9996\u7684\u9ec4\u8863\u5927\u6c49\uff0c\u66b4\u559d\u4e00\u58f0\uff0c\u4e00\u638c\u659c\u907f\u8fc7\u53bb\uff0c\u91d1\u5d16\u5f80\u4e0b\u4e00\u584c\u8eab\uff0c\u7f29\u987b\u85cf\u9888\uff0c\u638c\u950b\u500f\u5730\u64e6\u5934\u76ae\u8fc7 \u53bb\uff0c\u5927\u6c49\u90a3\u4e00\u638c\u7adf\u7136\u6253\u5728\u51cc\u672a\u98ce\u8eab\u4e0a\u3002 \u3000\u3000\u51cc\u672a\u98ce\u672c\u6765\u662f\u4e0d\u60f3\u66b4\u9732\u8eab\u4efd\u7684\uff0c\u73b0\u5728\u7a81\u7136\u5403\u4e86\u9ec4\u8863\u5927\u6c49\u4e00\u638c\uff0c\u672c\u80fd\u5730\u8fd0\u51fa\u201c\u5378\u529b\u89e3\u52bf\u201d \u7684\u4e0a\u4e58\u529f\u592b\uff0c\u8eab\u5b50\u4e00\u95ea\uff0c\u90a3\u4eba\u7684\u638c\u4f3c\u6253\u7740\u4e00\u56e2\u68c9\u82b1\uff0c\u65e0\u4ece\u4f7f\u529b\uff0c\u638c\u950b\u64e6\u80f8\u800c\u8fc7\uff0c\u6536\u52bf\u4e0d\u53ca\uff0c \u8eab\u5411\u524d\u503e\uff0c\u91d1\u5d16\u8d81\u52bf\u84e6\u5730\u957f\u8eab\uff0c\u4e00\u811a\u8e22\u53bb\uff0c\u628a\u90a3\u9ec4\u8863\u5927\u6c49\uff0c\u626b\u51fa\u4e24\u4e08\u5f00\u5916\u3002 \u3000\u3000\u548c\u9ec4\u8863\u5927\u6c49\u540c\u6765\u7684\u90b1\u4e1c\u6d1b\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u65f6\u4ed6\u4e0d\u6562\u518d\u6258\u5927\u4e86\uff0c\u6025\u6025\u8d76\u4e0a\u524d\u6765\uff0c\u51dd\u76ee\u4e00\u770b\uff0c \u6070\u6070\u548c\u51cc\u672a\u98ce\u5bf9\u4e2a\u6b63\u7740\u3002\u4ed6\u53cc\u773c\u4e0a\u7ffb\uff0c\u4e00\u58f0\u602a\u53eb\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u662f\u4f60\u8fd9 \u53ae\u3002\u201d\u51cc\u672a\u98ce\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u5e78\u4f1a\uff0c\u5e78\u4f1a\uff0c\u5341\u516d\u5e74\u524d\uff0c\u9886\u4f60\u4e24\u5200\uff0c\u5e78\u597d\u672a\u88ab\u523a\u6b7b\uff01\u201d\u90b1\u4e1c\u6d1b\u5927 \u7b11\u9053\uff1a\u201c\u4f60\u60f3\u7b97\u65e7\u8d26\uff0c\u6211\u53ef\u60f3\u540c\u4f60\u7b97\u65b0\u5e10\u5462\uff01\u597d\uff0c\u597d\uff0c\u54b1\u4eec\u518d\u6765\u4e00\u573a\u5355\u72ec\u4e00\u6597\uff01\u201d\u8fd9\u65f6\u53e6\u4e00 \u4e2a\u9ec4\u8863\u5927\u6c49\uff0c\u4f38\u624b\u4e00\u6307\uff0c\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u90b1\u8001\u524d\u8f88\uff0c\u6d59\u5357\u7684\u5973\u532a\u9996\u4e5f\u5728\u8fd9\u513f\uff0c\u8ba9\u4ed6\u4eec\u4e00\u8d77\u4e0a \u5427\uff01\u201d\u90b1\u4e1c\u6d1b\u602a\u773c\u4e00\u7ffb\uff0c\u53c8\u662f\u8fde\u58f0\u602a\u7b11\uff1a\u201c\u4eca\u65e5\u4f55\u5e78\u8fde\u4f1a\u4e24\u4f4d\u7537\u5973\u82f1\u96c4\uff01\u201d\u4ed6\u4fa7\u8fc7\u9762\uff0c\u5bf9\u90a3 \u51e0\u4e2a\u5927\u6c49\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5bf9\u4ed8\u90a3\u4e2a\u5973\u7684\uff0c\u8fd9\u5c0f\u5b50\u6211\u8981\u548c\u4ed6\u89c1\u4e2a\u771f\u7ae0\uff01\u201d\u91d1\u5d16\u8fd9\u65f6\u4e5f\u770b\u6e05\u695a\u4e86\u51cc \u672a\u98ce\u9762\u5bb9\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u77e5\u9053\u6b64\u4eba\u5c31\u662f\u7eb5\u6a2a\u897f\u5317\uff0c\u6b66\u6797\u4f20\u8bf4\u4e2d\u7684\u795e\u5947\u4eba\u7269\uff1b\u800c\u90b1\u4e1c\u6d1b\u4e5f\u662f\u9752\u5e74 \u6c5f\u6e56\u4e00\u9738\uff0c\u4e8c\u5341\u591a\u5e74\u524d\uff0c\u7a81\u7136\u5728\u6c5f\u5357\u51fa\u73b0\uff0c\u8c01\u90fd\u4e0d\u77e5\u4ed6\u7684\u6765\u5386\uff0c\u540e\u6765\u7a81\u7136\u9690\u53bb\uff0c\u8c01\u4e5f\u4e0d\u77e5\u4ed6 \u7684\u53bb\u5904\u3002\u8fd9\u4e24\u4eba\u90fd\u4e0d\u597d\u60f9\u3002\u4ed6\u89c1\u90b1\u4e50\u6d1b\u7387\u90a3\u51e0\u4e2a\u5927\u6c49\uff0c\u6b63\u53d6\u7740\u5305\u6284\u4e4b\u52bf\uff0c\u6025\u5fd9\u62b1\u62f3\u8bf4\u9053\uff1a \u201c\u90b1\u8001\u524d\u8f88\uff0c\u6211\u548c\u4ed6\u4eec\u53ef\u4e0d\u662f\u4e00\u8def\uff01\u201d\u90b1\u4e1c\u6d1b\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u7684\u4e8b\u505c\u4e0b\u518d\u8bf4\uff0c\u53ea\u8981\u4f60\u4e0d\u7406 \u95f2\u4e8b\uff0c\u54b1\u4eec\u8fd8\u6709\u5546\u91cf\u3002\u201d\u90b1\u4e1c\u6d1b\u81ea\u4fe1\u53ef\u4ee5\u5bf9\u4ed8\u51cc\u672a\u98ce\uff0c\u4f46\u5374\u4e0d\u77e5\u5218\u90c1\u82b3\u7684\u6df1\u6d45\uff0c\u800c\u91d1\u5d16\u4e5f\u662f \u4e00\u540d\u597d\u624b\uff0c\u56e0\u6b64\u4ed6\u5206\u522b\u7f13\u6025\uff0c\u5b58\u5fc3\u5148\u622a\u7740\u51cc\u672a\u98ce\u518d\u8bf4\u3002 \u3000\u3000\u8fd9\u4e2a\u90b1\u4e1c\u6d1b\u8bf4\u8d77\u5927\u6709\u6765\u5934\uff0c\u4ed6\u662f\u9102\u4eb2\u738b\u591a\u94ce\u7684\u5e08\u53d4\uff0c\u548c\u5f53\u5e74\u88ab\u6768\u4e91\u9a86\u6740\u6b7b\u7684\u7ebd\u67af\u5362\u662f\u540c \u95e8\u5e08\u5144\u5f1f\u3002\u662f\u957f\u767d\u5c71\u6d3e\u201c\u98ce\u96f7\u5251\u201d\u9f50\u771f\u541b\u95e8\u4e0b\uff0c\u6392\u884c\u7b2c\u4e09\uff0c\u6b66\u529f\u6700\u5f3a\uff0c\u4ed6\u672c\u662f\u6ee1\u5dde\u5973\u771f\u65cf \u4eba\uff0c\u8ddf\u968f\u6e05\u5175\u5165\u5173\uff0c\u6539\u4e86\u4e2a\u6c49\u4eba\u540d\u5b57\uff0c\u5165\u5173\u540e\uff0c\u4e00\u9762\u6697\u4e2d\u7ed9\u6e05\u5ef7\u62c9\u62e2\u6c5f\u6e56\u597d\u624b\uff0c\u4e00\u9762\u4fa6\u5bdf\u5173 \u5185\u6b66\u6797\u60c5\u5f62\uff0c\u4ed6\u4e0d\u77e5\u9053\u6768\u4e91\u9a86\u5df2\u7ecf\u6b7b\u53bb\uff0c\u8ffd\u8e2a\u800c\u81f3\u5230\u5929\u5c71\uff0c\u60f3\u627e\u6768\u4e91\u806a\u6666\u6c14\uff0c\u51cc\u672a\u98ce\u90a3\u65f6\u521a \u5230\u56de\u7586\uff0c\u6b66\u529f\u4e0d\u5f3a\uff0c\u6328\u4e86\u4ed6\u4e24\u5200\uff0c\u540e\u6765\u8fd8\u662f\u6666\u660e\u7985\u5e08\uff0c\u663e\u4e86\u4e00\u624b\u7ef5\u638c\u51fb\u77f3\u5982\u7c89\u7684\u529f\u592b\uff0c\u624d\u628a \u4ed6\u5413\u8d70\u7684\u3002\u4eca\u756a\u4ed6\u8fdc\u5230\u6ec7\u4e2d\uff0c\u4e3a\u7684\u5c31\u662f\u8ffd\u8e2a\u51cc\u672a\u98ce\uff01\u201d\u548c\u90b1\u4e1c\u6d1b\u540c\u6765\u7684\u4e09\u4e2a\u9ec4\u8863\u5927\u6c49\uff0c\u90fd\u662f \u5927\u5185\u7684\u4e00\u7b49\u536b\u58eb\u3002\u539f\u6765\u695a\u662d\u5357\u4e91\u5c97\u6218\u8d25\u4e4b\u540e\uff0c\u56de\u53bb\u4e00\u62a5\uff0c\u5eb7\u7199\u7687\u5e1d\u4e5f\u8038\u7136\u52a8\u5bb9\uff0c\u5fc3\u5ff5\u6709\u51cc\u672a \u98ce\u8fd9\u6837\u7684\u9ad8\u624b\u7559\u5728\u4e16\u4e0a\uff0c\u7ec8\u662f\u5927\u60a3\uff0c\u56e0\u6b64\u7acb\u547d\u90b1\u4e1c\u6d1b\u5e26\u9886\u4e00\u4e2a\u52a9\u624b\uff0c\u4eb2\u81ea\u51fa\u9a6c\uff0c\u641c\u67e5\u51cc\u672a\u98ce \u4e0b\u843d\u3002\u53e6\u6d3e\u4e24\u4e2a\u536b\u58eb\uff0c\u8d76\u8d74\u6606\u660e\u3002\u90b1\u4e1c\u6d1b\u5e26\u9886\u52a9\u624b\uff0c\u5230\u4e86\u4e91\u5c97\uff0c\u5728\u65ad\u5d16\u5c96\u58c1\u4e4b\u4e0a\uff0c\u770b\u89c1\u5218\u90c1 \u82b3\u7ed9\u97e9\u5fd7\u90a6\u7684\u5b57\u3002\u5176\u4e2d\u6709\u201c\u76fc\u4ecd\u7ee7\u7eed\u897f\u884c\uff0c\u5171\u56fe\u5927\u4e1a\u201d\u4e4b\u53e5\uff0c\u8fd9\u7559\u5b57\u97e9\u5fd7\u90a6\u6ca1\u6709\u89c1\u5230\uff0c\u5374\u7ed9 \u90b1\u4e1c\u6d1b\u770b\u5230\u4e86\uff1b\u90b1\u4e1c\u6d1b\u5fc3\u601d\u9887\u4e3a\u7075\u654f\uff0c\u4e00\u89c1\u4fbf\u731c\u5230\u4ed6\u4eec\u5fc5\u662f\u5165\u6ec7\uff0c\u56e0\u6b64\u6025\u6025\u8d76\u6765\uff0c\u5230\u4e86\u6ec7 \u8fb9\uff0c\u4f1a\u5408\u4e86\u539f\u5148\u6765\u7684\u4e24\u4e2a\u536b\u58eb\uff0c\u4e00\u884c\u56db\u4eba\uff0c\u5728\u6d53\u96fe\u7634\u6c14\u4e4b\u4e0b\uff0c\u6765\u5230\u4e86\u629a\u4ed9\u6e56\u6ee8\uff0c\u6070\u6070\u548c\u51cc\u672a \u98ce\u78b0\u4e0a\uff01 \u3000\u3000\u8fd9\u65f6\u90b1\u4e1c\u6d1b\u516c\u7136\u53eb\u9635\uff0c\u6b63\u662f\u4ec7\u4eba\u89c1\u9762\uff0c\u5206\u5916\u773c\u7ea2\uff0c\u51cc\u672a\u98ce\u62d4\u5251\u4fbf\u8d77\uff0c\u521a\u884c\u4e86\u4e24\u6b65\uff0c\u5ffd\u53c8 \u8f6c\u8eab\uff0c\u5de6\u624b\u5728\u5218\u90c1\u82b3\u8170\u95f4\u4e00\u62bd\uff0c\u5c06\u5979\u7684\u9752\u94a2\u5251\u62d4\u51fa\uff0c\u53f3\u624b\u5c06\u81ea\u5df1\u62a2\u81ea\u695a\u662d\u5357\u624b\u4e2d\u7684\u6e38\u9f99\u5251\u9012 \u8fc7\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4f7f\u8fd9\u4e2a\uff01\u201d\u5218\u90c1\u82b3\u6115\u7136\u5f85\u95ee\uff0c\u51cc\u672a\u98ce\u65e9\u5df2\u98de\u6b65\u800c\u51fa\u3002\u5218\u90c1\u82b3\u731b\u7136\u7701\u8d77\uff0c\u8fd9\u662f \u4ed6\u4e3a\u4e86\u654c\u624b\u592a\u5f3a\uff0c\u6240\u4ee5\u7559\u4e0b\u5b9d\u5251\u7ed9\u81ea\u5df1\u9632\u8eab\uff0c\u5fc3\u4e2d\u611f\u52a8\uff0c\u62ff\u7740\u6e38\u9f99\u5251\u6014\u6014\u5730\u7ad9\u7740\uff0c\u773c\u89d2\u4e0d\u89c9 \u6ef4\u51fa\u4e86\u9897\u6676\u83b9\u7684\u6cea\u73e0\u3002 \u3000\u3000\u8fd9\u65f6\u90b1\u4e1c\u6d1b\u5df2\u7ecf\u548c\u51cc\u672a\u98ce\u52a8\u8d77\u624b\u6765\uff0c\u90b1\u4e1c\u6d1b\u5de6\u624b\u62a1\u5200\uff0c\u53f3\u624b\u5175\u5668\uff0c\u53ef\u662f\u4e24\u624b\u7684\u5175\u5668\u4e0d \u540c\uff0c\u8fd9\u79cd\u529f\u592b\uff0c\u5728\u6b66\u672f\u4e2d\u6700\u662f\u96be\u5b66\u3002\u5c24\u5176\u5200\u4e0e\u5251\u56e0\u4e3a\u5f62\u72b6\u76f8\u4f3c\uff0c\u7528\u6cd5\u53d8\u5316\u4e4b\u95f4\uff0c\u5374\u975e\u5e38\u5965 \u5999\uff0c\u4f3c\u540c\u5b9e\u5f02\u3002\u4fd7\u8bdd\u8bf4\uff1a\u201c\u5fc3\u96be\u4e24\u7528\u201d\uff0c\u53cc\u624b\u4f7f\u4e24\u822c\u5175\u5668\uff0c\u5c31\u7b49\u5982\u53eb\u4eba\u4e00\u624b\u7528\u7b14\u5199\u5b57\uff0c\u4e00\u624b \u7528\u9488\u7f1d\u8863\u4e00\u6837\uff0c\u8be5\u6709\u591a\u96be\uff1f\u53ef\u662f\u90b1\u4e1c\u6d1b\u7684\u5de6\u5200\u53f3\u5251\uff0c\u65bd\u5c55\u5f00\u6765\uff0c\u5374\u5999\u5230\u6beb\u5dc5\uff0c\u4e0d\u4f46\u6ca1\u6709\u9519 \u6f0f\uff0c\u800c\u4e14\u660e\u660e\u770b\u6765\uff0c\u4e24\u624b\u4f7f\u51fa\u7684\u62db\u6570\u76f8\u4f3c\uff0c\u5374\u53c8\u865a\u865a\u5b9e\u5b9e\uff0c\u53d8\u5316\u4e0d\u540c\u3002\u9976\u662f\u51cc\u672a\u98ce\u5929\u5c71\u5251\u6cd5 \u72ec\u6b65\u6d77\u5185\uff0c\u5f00\u5934\u5341\u591a\u62db\uff0c\u4e5f\u611f\u5230\u5e94\u4ed8\u4e3a\u96be\uff0c\u843d\u5728\u4e0b\u98ce\u3002 \u3000\u3000\u4f46\u51cc\u672a\u51e4\u662f\u4f55\u7b49\u4eba\u4e5f\uff0c\u4ed6\u5341\u591a\u62db\u4e00\u8fc7\uff0c\u5df2\u770b\u6e05\u695a\u4e86\u90b1\u4e1c\u6d1b\u7684\u8def\u9053\uff0c\u5251\u62db\u500f\u53d8\uff0c\u5c55\u5f00\u4e86 \u201c\u7ef5\u91cc\u85cf\u9488\u201d\u7684\u7cbe\u5947\u62db\u6570\uff0c\u8eab\u5f62\u98d8\u5ffd\u5982\u98ce\uff0c\u5251\u6cd5\u865a\u5b9e\u5e76\u7528\uff0c\u5251\u5230\u8eab\u5230\uff0c\u6bcf\u4e00\u62db\u90fd\u6697\u85cf\u51e0\u4e2a\u53d8 \u5316\uff0c\u7edd\u4e0d\u628a\u62db\u6570\u4f7f\u8001\u3002\u90b1\u4e1c\u6d1b\u7684\u98ce\u96f7\u5200\u5251\u53d8\u5316\u5df2\u6781\u4e3a\u7e41\u590d\uff0c\u800c\u51cc\u672a\u98ce\u7684\u5251\u6cd5\uff0c\u66f4\u662f\u9b3c\u795e\u83ab \u6d4b\u3002\u4e24\u4eba\u8fd9\u4e00\u573a\u53ae\u62fc\u8d8a\u6253\u8d8a\u6025\uff0c\u8d8a\u6253\u8d8a\u731b\uff0c\u65c1\u4eba\u770b\u53bb\uff0c\u53ea\u89c1\u4e00\u56e2\u5200\u5149\u5251\u6c14\uff0c\u604d\u60da\u89c1\u666f\u800c\u4e0d\u89c1 \u4eba\uff0c\u8fa8\u4e0d\u51fa\u662f\u8c01\u5f3a\u8c01\u5f31\uff0c\u5b70\u4f18\u5b70\u52a3\uff01 \u3000\u3000\u90b1\u4e1c\u6d1b\u662f\u54d1\u5b50\u5403\u9ec4\u8fde\uff0c\u6709\u82e6\u8bf4\u4e0d\u51fa\uff0c\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u51cc\u672a\u98ce\u7684\u5251\u6cd5\u7adf\u662f\u5982\u6b64\u795e\u5947\u3002\u767e\u5fd9 \u4e2d\uff0c\u4ed6\u770b\u5230\u5218\u90c1\u82b3\u4e00\u6b65\u4e00\u6b65\u79fb\u524d\uff0c\u53cc\u76ee\u7d27\u76ef\u6597\u573a\uff0c\u4f3c\u662f\u5341\u5206\u5173\u6ce8\uff0c\u84e6\u5730\u5f97\u4e86\u4e3b\u610f\uff0c\u5927\u58f0\u559d \u9053\uff1a\u201c\u5b69\u5b50\u4eec\uff0c\u628a\u90a3\u8d3c\u5a46\u5a18\u62ff\u4e0b\uff01\u201d \u3000\u3000\u90a3\u56f4\u4e0a\u6765\u7684\u4e09\u4e2a\u536b\u58eb\uff0c\u4e00\u4e2a\u540d\u53eb\u5f20\u9b41\uff0c\u624b\u4f7f\u8d64\u94dc\u5200\uff1b\u4e00\u4e2a\u540d\u53eb\u5f6d\u6606\u6797\uff0c\u624b\u4f7f\u4e00\u679d\u767d\u8721\u7aff \u5b50\uff0c\u5176\u957f\u4e03\u5c3a\u56db\u5bf8\uff0c\u80fd\u5f53\u67aa\u4f7f\uff0c\u4e5f\u53ef\u4f5c\u68cd\u7528\uff1b\u53e6\u4e00\u4e2a\u540d\u53eb\u90dd\u7ee7\u660e\uff0c\u624b\u4f7f\u4e00\u5bf9\u98de\u6293\uff0c\u6700\u662f\u5389 \u5bb3\u3002\u5f6d\u6606\u6797\u7684\u8721\u7aff\u5b50\u5148\u5230\uff0c\u7ed9\u5218\u90c1\u82b3\u4e3e\u5251\u4e00\u6321\uff0c\u767d\u8721\u7aff\u5b50\u7acb\u7ed9\u5207\u65ad\u4e00\u622a\uff0c\u5f6d\u6606\u6797\u6025\u6025\u63a3\u56de\uff0c \u53eb\u9053\uff1a\u201c\u8fd9\u8d3c\u5a46\u5a18\u4f7f\u7684\u662f\u5b9d\u5251\uff01\u201d\u90dd\u7ee7\u660e\u4e0d\u58f0\u4e0d\u54cd\uff0c\u53cc\u624b\u4e00\u626c\uff0c\u4e00\u5bf9\u98de\u6293\u5e26\u7740\u864e\u864e\u98ce\u58f0\uff0c\u5288 \u9762\u6253\u51fa\u3002\u5218\u90c1\u82b3\u628a\u5251\u4e00\u633d\uff0c\u6253\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u60f3\u5c06\u98de\u6293\u65a9\u65ad\uff0c\u54ea\u77e5\u90dd\u7ee7\u660e\u4e5f\u6e9c\u6ed1\u5f97\u5f88\uff0c\u5218\u90c1\u82b3 \u5251\u62db\u65b9\u53d1\uff0c\u4ed6\u7684\u53cc\u6293\u5ffd\u7136\u4e00\u6296\uff0c\u5df1\u662f\u6539\u4ece\u4e0b\u4e09\u8def\u626b\u5230\uff0c\u5f85\u5218\u90c1\u82b3\u4e08\u5251\u4e0b\u622a\u65f6\uff0c\u4ed6\u7684\u98de\u6293\u53c8\u4ece \u4e24\u80c1\u7ed5\u6765\u4e86\u3002\u8fd9\u5bf9\u98de\u6293\u5728\u4ed6\u624b\u4e2d\uff0c\u5982\u540c\u6d3b\u52a8\u7684\u6697\u5668\uff0c\u5218\u90c1\u82b3\u4ed7\u7740\u5b9d\u5251\u5389\u5bb3\uff0c\u5de6\u8fce\u53f3\u62d2\uff0c\u5140\u662f \u7ed9\u4ed6\u95f9\u5f97\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u5f6d\u6606\u6797\u548c\u5f20\u9b41\u89c1\u6709\u4fbf\u5b9c\u53ef\u62e3\uff0c\u4ece\u4e24\u4fa7\u6251\u653b\u4e0a\u6765\u3002\u5f6d\u6606\u6797\u8fd9\u65f6\u4e5f\u5b66\u4e56\u4e86\uff0c\u534a\u622a\u7aff\u5b50\u4f7f\u51fa\u8bb8 \u591a\u82b1\u62db\uff0c\u914d\u5408\u7740\u98de\u6293\u8fdb\u653b\uff0c\u53ea\u662f\u4e0d\u548c\u5979\u7684\u5b9d\u5251\u76f8\u78b0\uff0c\u800c\u5f20\u9b41\u7684\u539a\u80cc\u8d64\u94dc\u5200\uff0c\u5374\u662f\u68b0\u91cd\u529b\u6c89\uff0c \u867d\u7136\u4e00\u7ed9\u5b9d\u5251\u78b0\u7740\uff0c\u5c31\u5212\u4e86\u4e00\u9053\u53e3\u5b50\uff0c\u5b9d\u5251\u5374\u96be\u5c06\u5b83\u524a\u65ad\u3002\u98de\u6293\u8fdc\u653b\uff0c\u8d64\u94dc\u5200\u8fd1\u88ad\uff0c\u767d\u8721\u7aff \u5b50\u4fa7\u6270\uff0c\u4e09\u822c\u5175\u5668\uff0c\u4e09\u79cd\u6253\u6cd5\uff0c\u5218\u90c1\u82b3\u5e94\u4ed8\u5f97\u975e\u5e38\u5403\u529b\uff0c\u5e78\u597d\u6709\u6e38\u9f99\u5251\u5728\u624b\uff0c\u654c\u4eba\u4e5f\u4e0d\u6562\u9aa4 \u7136\u653b\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u6d53\u96fe\u6e10\u6d88\uff0c\u5929\u8272\u590d\u4eae\uff0c\u6210\u7fa4\u98de\u9e1f\uff0c\u7ed9\u8fd9\u4e00\u573a\u6076\u6597\uff0c\u5413\u5f97\u632f\u7fc5\u9ad8\u98de\uff0c\u5728\u534a\u7a7a\u4e2d\u95f4\u65cb\u54c0 \u9e23\uff0c\u4e00\u89c1\u5929\u4eae\uff0c\u7eb7\u7eb7\u51b2\u96fe\u9003\u51fa\u3002\u597d\u50cf\u5e95\u4e0b\u8fd9\u4e00\u573a\u6076\u6597\uff0c\u6bd4\u7634\u6c14\u66f4\u8db3\u4ee4\u98de\u9e1f\u60ca\u5fc3\u3002 \u3000\u3000\u51cc\u672a\u98ce\u521a\u521a\u62a2\u4e86\u5148\u624b\uff0c\u5360\u5f97\u4e0a\u98ce\uff0c\u6b63\u5728\u6b65\u6b65\u8fdb\u903c\u4e4b\u9645\uff0c\u542c\u5f97\u5218\u90c1\u82b3\u5df2\u7ecf\u51fa\u624b\uff0c\u4ed6\u9065\u8fa8\u5175 \u5668\u78b0\u78d5\u4e4b\u58f0\uff0c\u5df2\u77e5\u5218\u90c1\u82b3\u53d7\u4e86\u56f4\u653b\uff0c\u5fc3\u4e2d\u6697\u547c\u4e0d\u5999\u3002\u4ed6\u767e\u5fd9\u4e2d\u4fa7\u76ee\u7aa5\u89c6\uff0c\u53ea\u89c1\u5218\u90c1\u82b3\u4e00\u67c4\u5251 \u821e\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u5df2\u662f\u53ea\u80fd\u62db\u67b6\uff0c\u4e0d\u80fd\u8fd8\u62db\u4e86\u3002\u9ad8\u624b\u6bd4\u5251\uff0c\u5982\u540d\u5bb6\u5bf9\u5f08\uff0c\u5168\u4ed7\u6c14\u6c89\u5fc3\u9759\u7684\u9547\u5b9a \u5de5\u592b\u3002\u51cc\u672a\u98ce\u4e00\u6025\u8e81\uff0c\u7acb\u523b\u7ed9\u90b1\u4e1c\u6d1b\u627e\u7740\u4e86\u6f0f\u6d1e\uff0c\u98ce\u96f7\u5200\u5251\uff0c\u53c8\u7d27\u7d27\u8fdb\u901a\u8fc7\u6765\uff0c\u7adf\u7136\u53cd\u5ba2\u4e3a \u4e3b\uff0c\u53c8\u62a2\u5148\u624b\u8fdb\u653b\u3002\u51cc\u672a\u98ce\u9192\u609f\u901f\u51b3\u4e0d\u662f\u529e\u6cd5\uff0c\u6025\u5fd9\u91cd\u6444\u5fc3\u795e\uff0c\u4e00\u9762\u8fce\u6218\uff0c\u4e00\u9762\u7f13\u7f13\u5411\u5218\u90c1 \u82b3\u8fd9\u8fb9\u79fb\u6765\u3002 \u3000\u3000\u65f6\u95f4\u4e00\u957f\uff0c\u5218\u90c1\u82b3\u8d8a\u611f\u96be\u4ee5\u652f\u6301\uff0c\u5979\u989d\u89d2\u89c1\u6c57\uff0c\u624b\u5fc3\u53d1\u70ed\uff0c\u547c\u5438\u6e10\u4fc3\uff0c\u5fc3\u8df3\u6e10\u5267\uff0c\u5251\u62db \u53d1\u51fa\uff1b\u7adf\u6bcf\u6bcf\u53d7\u4e86\u7275\u5236\uff0c\u4e0d\u80fd\u968f\u610f\u5c48\u4f38\u3002\u6b63\u5371\u6025\u95f4\uff0c\u90dd\u7ee7\u660e\u98de\u6293\u53c8\u6402\u5934\u6492\u4e0b\uff0c\u5218\u90c1\u82b3\u521a\u4f7f\u51fa \u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u5251\u950b\u4e0a\u6307\uff0c\u5f6d\u6606\u6797\u7684\u767d\u8721\u7aff\u5b50\uff0c\u5f53\u80f8\u523a\u5230\uff0c\u5218\u90c1\u82b3\u522b\u62db\u4e0d\u53d8\uff0c\u5251\u8eab\u5916 \u524a\uff0c\u5f6d\u6606\u6797\u500f\u5730\u5c06\u7aff\u5b50\u884c\u540e\u4e00\u63a3\uff0c\u8ba9\u4f4d\u7ed9\u5f20\u9b41\u7684\u8d64\u94dc\u5200\u5f53\u80f8\u523a\u6765\u3002\u5218\u90c1\u82b3\u65e0\u53ef\u5948\u4f55\u594b\u529b\u4e00 \u683c\uff0c\u4e0e\u8d64\u94dc\u5200\u78b0\u4e2a\u6b63\u7740\uff0c\u5251\u950b\u5c06\u8d64\u94a2\u5200\u65ab\u4e86\u4e00\u4e2a\u51f9\u53e3\uff0c\u672a\u53ca\u62bd\u51fa\uff0c\u98de\u6293\u53c8\u5df2\u5f53\u5934\u6293\u4e0b\u3002\u5218\u90c1 \u82b3\u65e0\u6cd5\u62db\u67b6\uff0c\u5c31\u5728\u6b64\u6027\u547d\u4fc4\u9877\u4e4b\u95f4\uff0c\u5ffd\u542c\u5f97\u90dd\u6606\u660e\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u98de\u6293\u5ffd\u7136\u51ed\u7a7a\u8361\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u90dd\u7ee7\u660e\u500f\u5730\u5c06\u98de\u6293\u6536\u56de\uff0c\u5927\u58f0\u6012\u9a82\u9053\uff1a\u201c\u8fd9\u7b97\u662f\u90a3\u8def\u9ad8\u4eba\uff1f\u4f55\u4e0d\u51fa\u6765\u8d50\u6559\uff0c\u5374\u5728\u80cc\u5730\u91cc \u5077\u63b7\u63b7\u4e00\u9556\uff0c\u51b7\u653e\u4e00\u7bad\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u5c11\u5e74\u58f0\u97f3\u51b7\u7136\u5730\u53d1\u8bdd\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4eba\u56f4\u653b \u4e00\u4e2a\u5a18\u513f\uff0c\u8fd9\u53c8\u7b97\u662f\u90a3\u8def\u9ad8\u4eba\u3002\u201d\u90dd\u7ee7\u660e\u770b\u731b\u89d1\u53d1\u58f0\u4e4b\u5904\uff0c\u4e00\u626c\u624b\u5c31\u662f\u4e24\u628a\u98de\u9525\uff0c\u8054\u7fe9\u98de \u53bb\u3002\u90a3\u5c11\u5e74\u53c8\u662f\u51b7\u51b7\u4e00\u7b11\uff0c\u53ea\u542c\u5f97\u534a\u7a7a\u4e2d\u55e4\u55e4\u4e24\u58f0\uff0c\u4e24\u67c4\u98de\u9525\u7adf\u4e92\u76f8\u6fc0\u649e\uff0c\u8dcc\u843d\u6e56\u4e2d\u3002\u5218\u90c1 \u82b3\u8fd9\u65f6\u5df2\u770b\u6e05\u5c11\u5e74\u53d1\u7684\u6697\u5668\uff0c\u5f62\u5982\u4e00\u53ea\u8774\u8776\uff0c\u8fce\u98ce\u6709\u58f0\uff0c\u90dd\u7ee7\u660e\u7684\u7b2c\u4e00\u679a\u98de\u94fe\u7ed9\u6697\u5668\u4e00\u649e\uff0c \u53cd\u6fc0\u56de\u53bb\uff0c\u6070\u6070\u548c\u7b2c\u4e8c\u679a\u98de\u9525\u78b0\u4e2a\u6b63\u7740\u3002\u5218\u90c1\u82b3\u8ba4\u5f97\u8fd9\u662f\u56db\u5ddd\u5510\u5bb6\u72ec\u521b\u7684\u6697\u5668\u8774\u8776\u9556\uff0c\u6697\u6697 \u60ca\u5947\uff0c\u8fd9\u5c11\u5e74\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u7adf\u7136\u4f1a\u7528\u8fd9\u6837\u5947\u5f62\u6697\u5668\u3002 \u3000\u3000\u90dd\u7ee7\u660e\u4ee5\u98de\u6293\u98de\u9525\u4e24\u6837\u7edd\u6280\uff0c\u79f0\u96c4\u6b66\u6797\uff0c\u98de\u9525\u7ed9\u4eba\u8f7b\u8f7b\u6253\u843d\uff0c\u4e0d\u7531\u5f97\u53c8\u60ca\u53c8\u6012\u3002\u987b\u77e5\u4ed6 \u7684\u98de\u9525\u4e43\u662f\u6697\u5668\u4e2d\u6700\u6c89\u91cd\u7684\uff0c\u73b0\u5728\u7adf\u7ed9\u4e00\u679a\u5c0f\u5c0f\u7684\u8774\u8776\u7f25\uff0c\u53cd\u8361\u5f00\u53bb\uff0c\u8fd9\u5c11\u5e74\u7684\u529f\u529b\u53ef\u60f3\u800c \u77e5\uff0c\u4ed6\u867d\u7136\u6124\u6012\u3001\u4e5f\u4e0d\u6562\u6389\u4ee5\u8f7b\u5fc3\u4e86\uff0c\u5f53\u4e0b\uff0c\u628a\u4e24\u67c4\u98de\u6293\uff0c\u4f7f\u5f97\u661f\u6d41\u7535\u63a3\uff0c\u4e00\u67c4\u62a4\u8eab\uff0c\u4e00\u67c4 \u653b\u654c\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u7684\u5175\u5668\u5374\u4e5f\u5947\u602a\uff0c\u4e43\u662f\u4e24\u67c4\u6d41\u5c3e\u9524\uff0c\u957f\u957f\u7684\u94c1\u7d22\uff0c\u9876\u7aef\u7cfb\u7740\u4e00\u4e2a\u94a2\u7403\uff0c\u4e0d\u7528\u65f6\u56f4 \u5728\u8170\u95f4\uff0c\u7528\u65f6\u4e00\u6296\u624b\u4fbf\u98de\u63b7\u800c\u51fa\uff0c\u4e5f\u548c\u98de\u6293\u4e00\u6837\u5982\u540c\u6d3b\u52a8\u7684\u6697\u5668\u3002\u8fd9\u65f6\u4e24\u4eba\u76f8\u9694\u4e94\u516d\u4e08\u8fdc\uff0c \u4ea4\u8d77\u624b\u6765\uff0c\u98de\u6293\u98de\u9524\u5728\u534a\u7a7a\u4e2d\u4e92\u76f8\u78b0\u78d5\uff0c\u56db\u6761\u94fe\u7d22\u5982\u795e\u9f99\u4e71\u821e\uff0c\u5ffd\u524a\u659c\u98de\uff0c\u5ffd\u800c\u76f4\u5c04\uff0c\u597d\u770b \u4e4b\u6781\u3002\u800c\u98de\u9524\u98de\u6293\u4e00\u78b0\u7740\u4fbf\u6e85\u51fa\u706b\u82b1\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u660e\u5373\u706d\u3002 \u3000\u3000\u5218\u90c1\u82b3\u51cf\u5c11\u4e86\u6700\u5f3a\u7684\u654c\u624b\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u4e00\u67c4\u6e38\u9f99\u5251\u5982\u7075\u86c7\u75be\u5410\uff0c\u5bd2\u5149\u70c1\u70c1\uff0c\u51b7\u6c14\u68ee\u68ee\uff0c \u6307\u5357\u6253\u5317\uff0c\u628a\u5f20\u9b41\u548c\u5f6d\u6606\u6797\u8feb\u5f97\u8fde\u8fde\u540e\u9000\u3002\u4e0d\u8fc7\u7247\u523b\uff0c\u53ea\u542c\u5f97\u545b\u5577\u4e00\u58f0\uff0c\u5f6d\u6606\u6797\u7684\u767d\u8721\u7aff \u5b50\uff0c\u53c8\u7ed9\u65a9\u65ad\u3002 \u3000\u3000\u8fd9\u65f6\u51cc\u672a\u98ce\u548c\u90b1\u4e1c\u6d1b\u4e5f\u6253\u5f97\u5341\u5206\u70bd\u70ed\uff0c\u51cc\u672a\u98ce\u89c1\u5218\u90c1\u82b3\u5df2\u7ecf\u8131\u9669\uff0c\u66f4\u65e0\u5fe7\u6302\uff0c\u4e00\u67c4\u9752\u94a2 \u5251\uff0c\u500f\u5730\u5c55\u5f00\uff0c\u65f6\u800c\u67d4\u5982\u67f3\u7d6e\uff0c\u65f6\u800c\u63d2\u82e5\u6d2a\u6d9b\u3002\u90b1\u4e1c\u6d1b\u7684\u98ce\u96f7\u5200\u5251\uff0c\u867d\u7136\u52b2\u5ea6\u5341\u8db3\uff0c\u53d8\u5316\u7e41 \u591a\uff0c\u53ef\u662f\u5728\u653b\u51fb\u65f6\u5374\u7ed9\u51cc\u672a\u98ce\u8f7b\u8f7b\u5316\u53bb\uff0c\u5728\u9632\u5b88\u65f6\u53c8\u7ed9\u51cc\u672a\u98ce\u76f4\u538b\u8fc7\u6765\uff0c\u5de6\u5200\u53f3\u5251\u4e24\u822c\u5175 \u5668\uff0c\u90fd\u7ed9\u51cc\u672a\u51e4\u4e00\u70b3\u5355\u5251\u514b\u4f4f\u3002\u6218\u5230\u5206\u9645\uff0c\u731b\u542c\u5f97\u51cc\u672a\u98ce\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u5251\u64a9\u53bb\uff0c\u90b1\u4e1c\u6d1b\u5de6\u624b \u957f\u5200\uff0c\u767b\u65f6\u8131\u624b\uff0c\u51cc\u672a\u98ce\u75be\u5982\u95ea\u7535\uff0c\u4e3e\u5251\u5728\u90b1\u4e50\u6d1b\u9762\u95e8\u4e00\u5212\uff0c\u518d\u5411\u53f3\u4e00\u65cb\uff0c\u5c06\u90b1\u4e1c\u6d1b\u5de6\u8fb9\u7684 \u8033\u6735\u5272\u4e0b\u6765\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u8fd9\u662f\u7b2c\u4e00\u5200\u7684\u8fd8\u672c\u4ed8\u606f\uff01\u201d\u90b1\u4e1c\u6d1b\u8bf4\u7f62\u54c8\u54c8\u5927\u7b11\uff0c\u5374\u4e0d\u8feb\u8d76\u3002 \u3000\u3000\u90b1\u4e1c\u6d1b\u6ca1\u547d\u5954\u9003\u65f6\u5927\u547c\u201c\u98ce\u7d27\u201d\uff01\u767e\u5fd9\u4e2d\u8fd8\u5411\u90a3\u4e2a\u72ec\u6218\u90dd\u7ee7\u660e\u7684\u5c11\u5e74\u53d1\u51fa\u4e00\u5757\u98de\u8757\u77f3\uff0c \u53eb\u9053\uff1a\u201c\u90dd\u8001\uff0c\u626f\u547c\uff01\u201d\u51cc\u672a\u98ce\u89c1\u4ed6\u5355\u72ec\u62db\u547c\u90dd\u7ee7\u660e\uff0c\u5927\u8d77\u7591\u5fc3\uff0c\u4e00\u633a\u9752\u94a2\u5251\uff0c\u4fbf\u6765\u62e6\u622a\uff0c \u8fd9\u90dd\u7ee7\u660e\u679c\u7136\u865a\u89c1\u4e00\u6643\uff0c\u907f\u8fc7\u4e86\u90a3\u5c11\u5e74\u7684\u6d41\u661f\u9524\uff0c\u62d4\u8db3\u98de\u5954\uff0c\u6070\u6070\u7ed9\u51cc\u672a\u98ce\u622a\u4f4f\u3002\u90dd\u7ee7\u660e\u53cc \u624b\u4e00\u626c\uff0c\u4e24\u67c4\u98de\u6293\uff0c\u76f4\u5411\u51cc\u672a\u98ce\u6253\u6765\uff0c\u51cc\u672a\u98ce\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u5f85\u5f97\u98de\u6293\u547c\u7684\u4e00\u58f0\u5230\u4e86\u5934\u4e0a\u65f6\uff0c\u53f3 \u624b\u9752\u94a2\u5251\u5411\u4e0a\u4e00\u633a\uff0c\u7ed9\u4e00\u67c4\u98de\u6293\u7f20\u4e2a\u6b63\u7740\uff1b\u51cc\u672a\u98ce\u62bd\u540e\u5fae\u4e00\u5750\u8eab\uff0c\u90dd\u7ee7\u660e\u7ed9\u626f\u5f97\u5411\u524d\u79fb\u4e86\u51e0 \u6b65\u3002\u8fd9\u65f6\u7b2c\u4e8c\u67c4\u98de\u6293\u53c8\u5df1\u75be\u5982\u95ea\u7535\u5730\u98de\u5230\uff0c\u51cc\u672a\u98ce\u5934\u9762\u5fae\u4fa7\uff0c\u8ba9\u8fc7\u98de\u6293\u94a2\u950b\uff0c\u5de6\u624b\u500f\u5730\u5411\u4e0a \u4e00\u6293\uff0c\u5c06\u98de\u6293\u7684\u94a2\u7d22\u4e00\u628a\u6293\u4f4f\uff0c\u5927\u559d\u4e00\u58f0\u201c\u8d77\u201d\uff01\u5de6\u624b\u7528\u529b\u4e00\u6325\uff0c\u53f3\u624b\u9752\u94a2\u5251\u5411\u5916\u4e00\u9001\uff0c\u90dd \u7ee7\u660e\u731d\u4e0d\u53ca\u9632\uff0c\u7adf\u7ed9\u51cc\u672a\u98ce\u6325\u52a8\u98de\u6293\u4e3e\u4e86\u8d77\u6765\uff01 \u3000\u3000\u90dd\u7ee7\u660e\u8eab\u4f53\u60ac\u7a7a\uff0c\u5c45\u7136\u867d\u8d25\u4e0d\u4e71\uff0c\u7a7a\u4e2d\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u843d\u5728\u5730\u4e0a\uff0c\u4e00\u626c\u624b\u53c8\u662f\u4e09\u67c4\u98de\u9525 \u5411\u51cc\u672a\u51e4\u6253\u6765\uff0c\u51cc\u672a\u98ce\u5c31\u62ff\u7740\u98de\u6293\u5f53\u5175\u5203\uff0c\u8fce\u7740\u98de\u9525\u6765\u8def\uff0c\u4e00\u9635\u6325\u821e\uff0c\u4e09\u67c4\u98de\u9525\uff0c\u90fd\u88ab\u53cd\u51fb \u9707\u4e0a\u9ad8\u7a7a\uff0c\u8fdc\u8fdc\u5730\u629b\u5411\u6e56\u5fc3\uff0c\u6d6a\u82b1\u98de\u6e85\uff01 \u3000\u3000\u5c31\u5728\u51cc\u672a\u98ce\u6076\u6597\u90dd\u7ee7\u660e\u7684\u5f53\u53e3\uff0c\u5218\u90c1\u82b3\u72ec\u6218\u5f6d\u6606\u6797\u3001\u5f20\u9b41\u4e8c\u4eba\uff0c\u4e5f\u5df2\u5360\u4e86\u4e0a\u98ce\uff0c\u5f20\u9b41\u6043 \u7740\u68b0\u91cd\u529b\u6c89\uff0c\u539a\u80cc\u8d64\u94dc\u5200\u6a2a\u91cc\u4e00\u78d5\uff0c\u5200\u950b\u4e00\u8f6c\uff0c\u4f7f\u4e86\u4e00\u62db\u201c\u94c1\u725b\u8015\u5730\u201d\uff0c\u659c\u65a9\u4e24\u5200\uff0c\u660e\u662f\u8fdb \u653b\uff0c\u5b9e\u662f\u8d70\u52bf\u3002\u5218\u90c1\u82b3\u51b7\u7b11\u4e00\u58f0\uff0c\u6e38\u9f99\u5251\u84e6\u7684\u4e00\u64a4\uff0c\u8ba9\u654c\u4eba\u62a2\u4e86\u8fdb\u6765\uff0c\u5237\u7684\u75be\u5982\u661f\u706b\uff0c\u622a\u65a9 \u654c\u4eba\u624b\u8155\u3002\u5f20\u9b41\u5200\u6570\u5df2\u7ecf\u7528\u8001\uff0c\u6b63\u5f85\u8f6c\u8eab\uff0c\u5200\u8fd8\u672a\u4e3e\uff0c\u4e00\u6761\u53f3\u81c2\uff0c\u5df2\u7ed9\u6e38\u9f99\u5251\u786c\u751f\u751f\u9f50\u6839\u5207 \u65ad\uff0c\u767b\u65f6\u75db\u5f97\u4e00\u58f0\u5389\u53eb\uff0c\u8840\u6e85\u5c18\u57c3\uff0c\u5f6d\u6606\u6797\u62d6\u7740\u534a\u622a\u767d\u8721\u7aff\u5b50\uff0c\u5411\u5916\u5954\u9003\uff0c\u8fce\u9762\u78b0\u7740\u90a3\u4e2a\u5c11 \u5e74\u4e66\u751f\uff0c\u4e24\u67c4\u6d41\u661f\u9524\uff0c\u5f53\u5934\u51fb\u4e0b\uff0c\u53c8\u662f\u767b\u65f6\u4e86\u7ed3\uff01 \u3000\u3000\u90dd\u7ee7\u660e\u7ee7\u7eed\u9003\u8dd1\uff0c\u51cc\u672a\u98ce\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u672d\u4e5f\uff01\u201d\u626c\u624b\u4e00\u9053\u4e4c\u91d1\u5149\u8292\uff0c\u75be\u5c04\u800c \u51fa\uff0c\u90dd\u7ee7\u660e\u542c\u98ce\u8fa8\u5668\u5934\u4e5f\u4e0d\u56de\uff0c\u53cd\u624b\u6253\u51fa\u4e00\u67c4\u98de\u9525\uff0c\u60f3\u5c06\u51cc\u672a\u51e4\u7684\u6697\u5668\u78b0\u843d\u3002\u4e0d\u6599\u51cc\u6731\u98ce\u7684 \u6697\u5668\u52b2\u5ea6\u60ca\u4eba\uff0c\u4e00\u679d\u4f3c\u7bad\u975e\u7bad\u7684\u4e1c\u897f\uff0c\u548c\u98de\u9525\u4e00\u78b0\uff0c\u7adf\u5d4c\u5165\u4e86\u98de\u9525\u4e4b\u4e2d\uff0c\u800c\u4e14\u628a\u98de\u9525\u76f4\u5c04\u5f97 \u53cd\u51fb\u56de\u53bb\uff0c\u90dd\u7ee7\u660e\u542c\u5f97\u80cc\u540e\u5636\u98ce\uff0c\u8eb2\u95ea\u5df2\u6765\u4e0d\u53ca\uff0c\u80a9\u5934\u7adf\u7ed9\u7a7f\u4e86\u4e00\u4e2a\u5927\u6d1e\uff01 \u3000\u3000\u8fd9\u65f6\u5218\u90c1\u82b3\u8ddd\u79bb\u8f83\u8fd1\uff0c\u65e9\u5df2\u6025\u6b65\u8d76\u4e0a\u3002\u90dd\u7ee7\u660e\u6b63\u5f85\u53d6\u51fa\u98de\u9525\u8fce\u654c\u3002\u5218\u90c1\u82b3\u5df2\u662f\u4e00\u58f0\u6e05 \u53f1\uff1a\u201c\u770b\u6697\u5668\uff01\u201d\u4e00\u626c\u624b\uff0c\u4e00\u4ef6\u9ed1\u5ffd\u5ffd\u7684\u7f51\u72b6\u4e1c\u897f\u8fce\u5934\u7f69\u4e0b\uff0c\u628a\u90dd\u7ee7\u660e\u7f69\u4e2a\u6b63\u7740\uff0c\u5218\u90c1\u82b3\u53cc \u624b\u4e00\u633d\uff0c\u628a\u72ec\u95e8\u6697\u5668\u9526\u4e91\u515c\u6536\u7d27\uff0c\u5c06\u90dd\u7ee7\u660e\u6a2a\u62d6\u76f4\u66f3\u7684\u76f4\u626f\u8fc7\u6765\uff0c\u6e38\u9f99\u5251\u4e00\u626c\uff0c\u6b63\u5f85\u65a9\u4e0b\u3002 \u51cc\u672a\u98ce\u4e00\u63a0\u6570\u4e08\uff0c\u5982\u98de\u8d76\u81f3\uff0c\u5c06\u5218\u90c1\u82b3\u624b\u8155\u4e00\u6258\uff0c\u8bf4\u9053\uff1a\u201c\u5251\u4e0b\u7559\u4eba\uff01\u201d\u5218\u90c1\u82b3\u4e00\u6115\uff0c\u5c06\u9526 \u4e91\u515c\u89e3\u5f00\uff0c\u51cc\u672a\u98ce\u4f38\u624b\u4e00\u638f\uff0c\u5f80\u4ed6\u6000\u4e2d\u53d6\u51fa\u4e00\u5c01\u4e66\u4fe1\uff0c\u4e0a\u9762\u5199\u7740\u201c\u5b89\u897f\u5c06\u519b\u674e\u201d\uff0c\u51cc\u672a\u98ce\u62bd \u51fa\u4fe1\u7b3a\u4e00\u770b\uff01\u51b7\u7b11\u4e00\u58f0\uff0c\u6536\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u53ef\u4ee5\u6253\u53d1\u8fd9\u53ae\uff01\u201d\u4ed6\u4e00\u4f38\u624b\uff0c\u5c06\u90dd\u7ee7\u660e\u6293 \u4e86\u8d77\u6765\uff0c\u968f\u624b\u4e00\u6254\uff0c\u5c06\u4ed6\u629b\u4e0b\u4e86\u8fdc\u8fdc\u7684\u6e56\u5fc3\uff01 \u3000\u3000\u6d53\u96fe\u6e10\u6536\uff0c\u7634\u6c14\u5df2\u6563\uff0c\u4e00\u573a\u6076\u6597\u4e4b\u540e\uff0c\u5e7d\u8c37\u6e56\u7554\uff0c\u91cd\u53c8\u5f52\u4e8e\u5bc2\u9759\uff0c\u5f5d\u6c11\u4eec\u7ed9\u8fd9\u4e00\u573a\u6076\u6597 \u5413\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u7ad9\u5f97\u8fdc\u8fdc\u7684\uff0c\u7528\u60ca\u60e7\u7684\u773c\u5149\uff0c\u6253\u91cf\u7740\u8fd9\u7fa4\u964c\u751f\u7684\u6c49\u5ba2\u3002\u90a3\u5c11\u5e74\u4e66\u751f\uff0c\u8de8\u524d\u51e0 \u6b65\uff0c\u7528\u5f5d\u8bed\u53fd\u54e9\u5495\u565c\u5730\u8bb2\u4e86\u51e0\u53e5\uff0c\u544a\u8bc9\u4ed6\u4eec\u88ab\u6253\u7684\u90fd\u662f\u6076\u4eba\uff0c\u53eb\u4ed6\u4eec\u4e0d\u8981\u5bb3\u6015\u3002 \u3000\u3000\u8fd9\u65f6\u91d1\u5d16\u4e5f\u5df2\u6296\u6296\u7d22\u7d22\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u5411\u51cc\u672a\u98ce\u5f53\u5934\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u548c\u4ed6\u4eec\u4e0d\u662f\u4e00\u8def\uff0c \u4f60\u8001\u773c\u89c1\u4ed6\u4eec\u521a\u624d\u60f3\u628a\u6211\u7f6e\u4e8e\u6b7b\u5730\u3002\u201d\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e0d\u662f\u548c\u4ed6\u4eec\u4e00\u8def\uff0c\u4f60\u662f\u5e73\u5357 \u738b\u7684\u4f7f\u8005\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d\u91d1\u5d16\u70b9\u5934\u8bf4\u662f\u3002\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u77e5\u9053\u4f60\u662f\u4e00\u53ea\u8759\u8760\uff01\u201d\u610f\u601d\u662f\u8bf4 \u4ed6\u79bd\u517d\u53cc\u6816\uff0c\u671b\u98ce\u4f7f\u8235\u3002\u91d1\u5d16\u7ed9\u4ed6\u4e00\u8bf4\uff0c\u9762\u8272\u5c34\u5c2c\u4e4b\u6781\u3002\u51cc\u672a\u98ce\u563b\u563b\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u89c1\u8bc6\u4f60 \u4eec\u738b\u7237\u5e26\u6765\u7684\u4e1c\u897f\uff01\u201d\u8bf4\u7740\u7f13\u7f13\u8d70\u53bb\u3002 \u3000\u3000\u91d1\u5d16\u773c\u89c1\u51cc\u672a\u98ce\u7684\u6b66\u529f\u8fd8\u5728\u90b1\u4e1c\u6d1b\u4e4b\u4e0a\uff0c\u77e5\u9053\u8981\u9003\u4e5f\u9003\u4e0d\u8131\uff0c\u5413\u5f97\u9762\u9752\u5507\u767d\uff0c\u6b65\u6b65\u540e \u9000\u3002\u6b63\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u5e7d\u8c37\u4e00\u9635\u6e05\u8106\u7684\u94c3\u58f0\uff0c\u63a5\u7740\u662f\u5f97\u5f97\u8e44\u58f0\uff0c\u81ea\u8fdc\u800c\u8fd1\uff0c\u90a3\u5c11\u5e74\u4e66\u751f\u62db\u547c \u51cc\u672a\u98ce\u9053\uff1a\u201c\u522b\u5fd9\u7406\u4f1a\u8fd9\u53ae\uff0c\u4ed6\u4e0d\u662f\u4ec0\u4e48\u811a\u8272\u3002\u201d\u51cc\u672a\u98ce\u7b11\u4e86\u4e00\u7b11\uff0c\u8f6c\u8fc7\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u770b \u4f60\u7684\u9762\u6211\u4e0d\u4f38\u624b\u7b97\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u4e0a\u524d\u548c\u90a3\u5c11\u5e74\u642d\u8bdd\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5c1a\u672a\u5f00\u58f0\uff0c\u90a3\u5c11\u5e74\u5df2\u5230\u4e86\u8ddf\u524d\uff0c\u53f3\u624b\u4e00\u62ac\uff0c\u5c06\u4e00\u67c4\u98de\u9525\u4e3e\u8d77\uff0c\u90a3\u9525\u5934\u8fd8\u5d4c\u7740\u4e00\u6746 \u7bad\u72b6\u7684\u4e1c\u897f\uff0c\u5c11\u5e74\u4e00\u628a\u62d4\u51fa\uff0c\u9012\u5c06\u8fc7\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u7684\u6697\u5668\uff01\u201d\u63a5\u7740\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u522b \u5fd9\u544a\u8bc9\u6211\u4f60\u7684\u540d\u5b57\uff0c\u8ba9\u6211\u731c\u4e00\u731c\uff0c\u51ed\u7740\u4f60\u8fd9\u679d\u6697\u5668\uff0c\u6211\u731c\u4f60\u662f\u5929\u5c71\u795e\u8292\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u89c1\u4ed6\u4e00\u53e3\u9053\u7834\u6697\u5668\u6765\u5386\uff0c\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u4eba\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u89c1\u95fb\u5012\u662f\u5e7f \u535a\uff01\u201d\u4ed6\u8f6c\u8bf7\u95ee\u5c11\u5e74\u7684\u540d\u5b57\uff0c\u90a3\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u8fdc\u8fdc\u4f3c\u6709\u519b\u9a6c\u8d70\u52a8\uff0c\u5f85\u89c1\u4e86\u4ed6\u4eec\uff0c\u54b1\u4fe9\u518d\u7ec6\u8c08 \u5982\u4f55\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u89c1\u4ed6\u8bf4\u8bdd\u5f88\u662f\u8c6a\u723d\uff0c\u4f46\u5982\u53c8\u4f3c\u6709\u8bb8\u591a\u5fcc\u8bb3\u3002\u51cc\u672a\u98ce\u662f\u8001\u6c5f\u6e56\u4e86\uff0c\u4fbf\u4e0d\u518d\u95ee\uff0c\u6b63\u8bf4 \u8bdd\u95f4\uff0c\u5e7d\u8c37\u5df2\u51b2\u51fa\u4e00\u5f6a\u4eba\u9a6c\uff0c\u4e3a\u9996\u7684\u6267\u7740\u4e00\u6746\u5927\u65d7\uff0c\u5199\u7740\u201c\u5e73\u897f\u738b\u5e9c\u201d\u51e0\u4e2a\u5927\u5b57\uff0c\u9a6c\u4e0a\u9a91 \u5175\uff0c\u90fd\u6234\u7740\u9762\u7f69\uff0c\u60f3\u662f\u9014\u4e2d\u9047\u5230\u6d53\u96fe\uff0c\u6234\u6765\u907f\u7634\u7684\u3002 \u3000\u3000\u91d1\u5d16\u4e00\u89c1\u8fd9\u5f6a\u4eba\u9a6c\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u5fd9\u62db\u547c\u4e0e\u4ed6\u540c\u6765\u7684\u4eba\uff0c\u62a2\u7740\u8fce\u4e0a\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5e73\u5357\u738b \u4f7f\u8005\u62dc\u89c1\u5e73\u897f\u738b\uff01\u201d\u9a6c\u4e0a\u7684\u519b\u5b98\u671b\u4e86\u4e00\u671b\uff0c\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u968f\u4fbf\u5429\u5490\u4e24\u5458\u88e8\u5c06\u53bb\u63a5\u91d1\u5d16\uff0c\u4ed6 \u81ea\u5df1\u5e76\u4e0d\u505c\u7559\uff0c\u7eb5\u9a6c\u7ed5\u6e56\u6ee8\u5954\u8dd1\uff0c\u6e38\u76ee\u56db\u987e\u3002\u731b\u7136\u95f4\uff0c\u4ed6\u55d6\u7684\u4e0b\u9a6c\uff0c\u5411\u7740\u90a3\u5c11\u5e74\u4e66\u751f\uff0c\u6df1\u6df1 \u4e00\u793c\uff0c\u606d\u606d\u656c\u656c\u4ed6\u8bf4\u9053\uff1a\u201c\u5e73\u897f\u738b\u77e5\u9053\u4f60\u5c06\u4eca\u65e5\u5230\u6765\uff0c\u7279\u547d\u5351\u5c06\u4e09\u767e\u91cc\u5916\u606d\u8fce\uff01\u201d\u9a91\u5175\u961f \u4e2d\uff0c\u7acb\u523b\u9f13\u4e50\u9f50\u9e23\uff0c\u8868\u793a\u656c\u610f\uff0c\u6b64\u8a00\u4e00\u51fa\uff0c\u51cc\u672a\u98ce\u4e5f\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e66\u751f\u610f\u6001\u60a0\u95f2\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f55\u5fc5\u8fd9\u6837\u591a\u793c\uff01\u201d\u8fd9\u65f6\u65e9\u6709\u4e24\u4e2a\u7259\u5c06\u7275\u7740\u4e00\u5339\u767d\u9a6c \u8fc7\u6765\uff0c\u5782\u624b\u8bf4\u9053\uff1a\u201c\u8bf7\u674e\u516c\u5b50\u4e0a\u9a6c\u3002\u201d\u5c11\u5e74\u4e66\u751f\u671b\u4e86\u4e00\u671b\u51cc\u672a\u98ce\u548c\u5218\u90c1\u82b3\uff0c\u4e3e\u624b\u8bf4\u9053\uff1a\u201c\u9ebb \u70e6\u4f60\u4eec\u518d\u501f\u4e24\u9a91\uff0c\u4ed6\u4eec\u662f\u6211\u7684\u670b\u53cb\u3002\u201d\u4ed6\u548c\u9a6c\u4e0a\u7684\u519b\u5b98\u8bf4\u8bdd\uff0c\u773c\u775b\u5374\u4e00\u76f4\u671b\u7740\u51cc\u672a\u98ce\uff0c\u773c\u5149 \u4e2d\u663e\u9732\u51fa\u671f\u5f85\u548c\u4fe1\u4efb\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5bf9\u5218\u90c1\u82b3\u4f7f\u4e2a\u773c\u8272\uff0c\u6168\u7136\u9053\uff1a\u201c\u597d\u201d\uff0c\u4e0a\u4e86\u5750\u9a91\uff0c\u7259\u5c06\u66ff\u4ed6\u4eec\u6574\u597d\u50f5\u7ef3\uff0c\u9012\u8fc7\u9a6c \u97ad\uff0c\u4e34\u884c\u8fd8\u656c\u4e86\u4e00\u4e2a\u519b\u793c\u3002\u91d1\u5d16\u4ed6\u4eec\u4e5f\u8ba8\u6765\u4e24\u5339\u9a6c\uff0c\u4f46\u6240\u53d7\u793c\u9047\uff0c\u5374\u8fdc\u4e0d\u5982\u51cc\u672a\u98ce\u4ed6\u4eec\u3002\u91d1 \u5d16\u53c8\u662f\u5c34\u5c2c\uff0c\u53c8\u662f\u7eb3\u7f55\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u662f\u5e73\u5357\u738b\u7684\u4f7f\u8005\uff0c\u5e73\u5357\u738b\u4e0e\u5434\u4e09\u6842\u4e43\u662f\u540c\u7b49\u7684\u85e9\u738b\uff0c\u4ed6 \u53c8\u6709\u6c42\u4e8e\u6211\u4eec\uff0c\u600e\u7684\u770b\u60c5\u5f62\u8fd9\u5f6a\u4eba\u9a6c\uff0c\u5374\u4e0d\u4f3c\u6765\u63a5\u6211\uff0c\u800c\u4f3c\u662f\u4e13\u7a0b\u6765\u63a5\u8fd9\u4e2a\u5c11\u5e74\u4e66\u751f\u3002\u96be\u9053 \u8fd9\u4e2a\u5c11\u5e74\u4e66\u751f\u7684\u8eab\u4efd\u6bd4\u6211\u8fd8\u9ad8\uff1f\u201d\u4ed6\u5fc3\u4e2d\u5341\u5206\u4e0d\u5feb\uff0c\u4e00\u8def\u9ed8\u4e0d\u4f5c\u58f0\u3002 \u3000\u3000\u5feb\u9a6c\u5954\u5f1b\uff0c\u519b\u884c\u8fc5\u901f\uff0c\u65e5\u66ae\u4e4b\u540e\uff0c\u5df2\u8d76\u5230\u6606\u660e\uff0c\u519b\u5b98\u5e26\u4ed6\u4eec\u5230\u5e73\u897f\u738b\u5e9c\u5b89\u6b47\uff0c\u738b\u5e9c\u501a\u5c71 \u5efa\u7b51\uff0c\u53ea\u89c1\u5c42\u697c\u91cd\u53e0\uff0c\u56de\u5eca\u66f2\u6298\uff0c\u7aef\u7684\u662f\u6c14\u8c61\u4e07\u5343\u3002\u7389\u5e9c\u7684\u603b\u7ba1\u5c06\u5c11\u5e74\u4e66\u751f\u548c\u51cc\u672a\u51e4\u5b89\u7f6e\u5728 \u4e00\u5904\uff0c\u5218\u90c1\u82b3\u5219\u53e6\u6709\u738b\u5e9c\u5973\u5b98\u670d\u4f8d\uff0c\u91d1\u5d16\u5374\u88ab\u5b89\u7f6e\u5728\u53e6\u4e00\u6240\u5728\u3002 \u3000\u3000\u90a3\u4e66\u751f\u6df1\u5165\u738b\u5e9c\uff0c\u4f3c\u4e4e\u6beb\u4e0d\u5728\u610f\uff0c\u5403\u9971\u6c90\u6d74\u4e4b\u540e\uff0c\u5012\u5934\u4fbf\u7761\u3002\u51cc\u672a\u98ce\u867d\u7136\u662f\u8001\u6c5f\u6e56\uff0c\u4e5f \u5140\u662f\u731c\u4e0d\u51fa\u4ed6\u7684\u8eab\u4efd\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u52a0\u7b2c\u4e09\u5929\uff0c\u738b\u5e9c\u4e2d\u4eba\u4e0e\u5434\u4e09\u6302\u624b\u4e0b\u5927\u5c06\u90fd\u966a\u4ed6\u4eec\u6e38\u73a9\uff0c\u50cf\u6367\u51e4\u51f0\u4f3c\u7684\uff0c\u56f4\u62e5\u7740\u5c11 \u5e74\u4e66\u751f\uff0c\u767b\u78a7\u9e21\u5c71\uff0c\u4e0a\u5927\u89c2\u697c\uff0c\u8d4f\u6606\u660e\u6e56\uff0c\u6e38\u9ed1\u9f99\u6f6d\uff0c\u904d\u89c8\u6606\u660e\u540d\u80dc\uff0c\u771f\u662f\u5f85\u5982\u4e0a\u5bbe\u3002\u90a3\u5c11 \u5e74\u4e00\u8def\u6e38\u89c8\uff0c\u4e00\u8def\u53e3\u8bb2\u6307\u5212\uff0c\u8c08\u8bba\u5175\u6cd5\uff0c\u6bcf\u5230\u4e00\u5904\uff0c\u5c31\u4f9d\u7740\u5730\u5f62\uff0c\u7eb5\u8c08\u653b\u5b88\u7b56\u7565\uff0c\u542c\u5f97\u90a3\u4e9b \u5c06\u5b98\uff0c\u8fde\u8fde\u70b9\u5934\u3002\u51cc\u672a\u98ce\u5fc3\u60f3\uff0c\u8fd9\u5c11\u5e74\u867d\u662f\u5f02\u4eba\uff0c\u53ef\u662f\u5374\u672a\u514d\u8fc7\u4e8e\u70ab\u9732\uff0c\u4ed6\u5374\u4e0d\u77e5\u8fd9\u5c11\u5e74\u662f \u53e6\u6709\u5fc3\u610f\uff0c\u4ed6\u6df1\u5165\u9669\u5730\uff0c\u6545\u610f\u6307\u638c\u8c08\u5175\uff0c\u4e43\u662f\u6572\u5c71\u9707\u864e\u7684\u8ba1\u7b56\u3002\u4ed6\u672c\u6765\u5c31\u8981\u5413\u4e00\u5413\u5434\u4e09\u6842\u624b \u4e0b\u7684\u5c06\u5b98\u3002 \u3000\u3000\u7b2c\u4e09\u65e5\u9ec4\u660f\u65f6\u5206\uff0c\u738b\u5e9c\u7684\u603b\u7ba1\uff0c\u5ffd\u7136\u6765\u62a5\uff0c\u8bf4\u662f\u5e73\u897f\u738b\u5434\u4e09\u6842\u8bbe\u5bb4\u76f8\u9080\uff0c\u5c11\u5e74\u4e66\u751f\u548c\u51cc \u672a\u98ce\u3001\u5218\u90c1\u82b3\u3001\u91d1\u5d16\u7b49\u90fd\u662f\u88ab\u9080\u8bf7\u7684\u8d35\u5bbe\u3002\u51cc\u672a\u98ce\u7b49\u90fd\u5e26\u597d\u4e86\u968f\u8eab\u5175\u5668\uff0c\u738b\u5e9c\u4e2d\u4eba\u89c1\u4ed6\u4eec\u8eab \u4f69\u5200\u5251\uff0c\u4ea6\u662f\u4e0d\u6562\u5e72\u6d89\u3002 \u3000\u3000\u7b75\u5e2d\u8bbe\u5728\u738b\u5e9c\u7684\u5927\u5802\uff0c\u56db\u9762\u5939\u58c1\u85b0\u7740\u6a80\u9999\uff0c\u5802\u4e0b\u662f\u8eab\u62ab\u7532\u80c4\u7684\u738b\u5e9c\u4eb2\u5175\uff0c\u5802\u4e0a\u662f\u5434\u4e09\u6842 \u624b\u4e0b\u7684\u5927\u5c06\u548c\u8fd1\u81e3\u3002\u8fd8\u6709\u7684\u5c31\u662f\u5728\u7b75\u524d\u64c5\u677f\u8f7b\u6572\u3001\u8f7b\u76c8\u8d77\u821e\u7684\u6b4c\u5993\u548c\u821e\u5a18\u3002\u5c11\u5e74\u4e66\u751f\u6602\u5934\u76f4 \u5165\uff0c\u5374\u4e0d\u89c1\u5434\u4e09\u6842\u5176\u4eba\uff0c\u53ea\u89c1\u4e00\u4e2a\u864e\u80cc\u718a\u8170\u7684\u5c06\u519b\uff0c\u66ff\u5434\u4e09\u6842\u5728\u90a3\u91cc\u6b3e\u5f85\u5bbe\u5ba2\u3002\u5c11\u5e74\u4e66\u751f\u6084 \u4fcf\u5730\u5bf9\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd9\u662f\u5434\u4e09\u6842\u7684\u864e\u5c06\u4fdd\u67f1\uff1f\u201d \u3000\u3000\u4fdd\u67f1\u4e00\u89c1\u4ed6\u4eec\u8fdb\u6765\uff0c\u7acb\u523b\u9080\u8bf7\u4e0a\u5ea7\uff0c\u968f\u5373\u6709\u4e00\u4e2a\u6b66\u58eb\u8fc7\u6765\u659f\u9152\u3002\u8fd9\u4e2a\u6b66\u58eb\u659f\u9152\uff0c\u5374\u6709\u70b9 \u90aa\u95e8\uff0c\u53ea\u89c1\u4ed6\u659f\u6ee1\u4e00\u676f\u4e4b\u540e\uff0c\u968f\u624b\u4e00\u653e\uff0c\u6bcf\u53ea\u9152\u676f\u90fd\u6df1\u6df1\u5730\u9677\u8fdb\u4e86\u684c\u5185\u3002 \u3000\u3000\u4fdd\u67f1\u4e3e\u624b\u9053\uff1a\u201c\u8bf7\uff0c\u201d\u5c06\u4e24\u6307\u6263\u7740\u9152\u676f\u7684\u8fb9\u7f18\uff0c\u8f7b\u8f7b\u4e00\u62d4\uff0c\u5c06\u9677\u5728\u684c\u9762\u7684\u9152\u676f\u6574\u4e2a\u62d4 \u8d77\uff0c\u6ef4\u9152\u4e0d\u6f0f\uff0c\u4e00\u996e\u800c\u5c3d\u3002\u5c11\u5e74\u4e66\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u7528\u4e2d\u6307\u52fe\u7740\u676f\u8fb9\u4e00\u65cb\uff0c\u90a3\u9152\u676f\u731b\u5730\u8df3\u8d77\uff0c\u5c11 \u5e74\u4f38\u53e3\u4e00\u54ac\uff0c\u628a\u9152\u676f\u54ac\u7740\uff0c\u4e5f\u662f\u4e00\u996e\u800c\u5c3d\uff0c\u6ef4\u9152\u4e0d\u6f0f\u3002\u4e24\u8f6e\u4e0b\u53bb\u662f\u51cc\u672a\u98ce\u548c\u5218\u90c1\u82b3\uff0c\u51cc\u672a\u98ce \u773c\u89d2\u6697\u7aa5\uff0c\u89c1\u5218\u90c1\u82b3\u79c0\u7709\u4f3c\u8e59\uff0c\u5fc3\u4e2d\u6697\u5ff5\uff1b\u5218\u90c1\u82b3\u867d\u7136\u64c5\u957f\u5251\u672f\uff0c\u53ea\u6050\u6ca1\u6709\u8fd9\u79cd\u5185\u5bb6\u529f\u529b\uff0c \u6c89\u541f\u4e4b\u95f4\uff0c\u53ea\u89c1\u4fdd\u67f1\u610f\u6001\u9a84\u8c6a\uff0c\u8fde\u58f0\u5411\u51cc\u672a\u98ce\u50ac\u9053\uff1a\u201c\u8fd9\u4f4d\u58ee\u58eb\u4e5f\u8bf7\u5e72\u676f\u5440\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u5251\u7709\u4e0a\u4e00\u626c\uff0c\u53cc\u773c\u73af\u626b\u5168\u5e2d\uff0c\u4e24\u624b\u6309\u5728\u684c\u4e0a\uff0c\u8f7b\u8f7b\u4e00\u62cd\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u8bf7\u5e72 \u676f\uff01\u201d\u731b\u7136\u95f4\uff0c\u90a3\u4e9b\u5d4c\u5728\u684c\u9762\u7684\u9152\u676f\uff0c\u4e00\u4e0b\u5b50\u90fd\u8df3\u8d77\u6765\uff0c\u51cc\u672a\u98ce\u3001\u5218\u90c1\u82b3\u3001\u91d1\u5d16\u7b49\u4f38\u624b\u63a5 \u4f4f\uff0c\u4e00\u996e\u800c\u5c3d\uff0c\u540c\u5e2d\u7684\u53e6\u5916\u51e0\u4eba\uff0c\u5374\u4ee5\u4e8b\u51fa\u610f\u5916\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u6ca1\u6709\u63a5\u4f4f\uff0c\u51e0\u4e2a\u9152\u676f\u8dcc\u5728\u684c \u4e0a\uff0c\u94ff\u9535\u6709\u58f0\uff0c\u676f\u4e2d\u7684\u9152\u5168\u6cfb\u5728\u684c\u4e0a\u3002 \u3000\u3000\u4fdd\u67f1\u9762\u8272\u4e00\u53d8\uff0c\u968f\u5373\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4e14\u6162\uff01\u6362\u8fc7\u53e6\u4e00\u5957\u9152\u676f\u3002\u201d\u4ed6\u628a\u684c\u4e0a\u7684\u9152\u676f\uff0c \u5206\u85cf\u4e24\u8896\u4e4b\u5185\uff0c\u53cc\u8896\u4e00\u626c\uff0c\u4e00\u5957\u5341\u53ea\u9152\u676f\uff0c\u6885\u82b1\u95f4\u7af9\u822c\u6574\u6574\u9f50\u9f50\u5730\u5d4c\u5728\u51e0\u4e08\u5916\u7684\u5899\u58c1\u4e0a\u3002\u8fd9 \u4e9b\u9152\u676f\u90fd\u662f\u7cbe\u94a2\u505a\u7684\uff0c\u4ed6\u8fd9\u4e24\u8896\u98de\u676f\u7684\u624b\u6cd5\uff0c\u6b63\u662f\u6253\u6697\u5668\u7684\u4e0a\u4e58\u529f\u592b\u3002 \u3000\u3000\u5e2d\u4e0a\u6362\u8fc7\u53e6\u4e00\u5957\u9152\u676f\uff0c\u4fdd\u67f1\u4eb2\u81ea\u7ed9\u4f17\u4eba\u659f\u9152\uff0c\u5230\u9012\u7ed9\u51cc\u672a\u98ce\u65f6\uff0c\u7528\u638c\u529b\u4e00\u8feb\uff0c\u676f\u5185\u7684\u9152 \u76f4\u6d8c\u8d77\u6765\uff0c\u51cc\u672a\u98ce\u8fd0\u638c\u529b\u9065\u9065\u4e00\u6309\uff0c\u6d8c\u8d77\u7684\u9152\uff0c\u500f\u5730\u53c8\u9000\u4e86\u4e0b\u53bb\uff0c\u4ed6\u4f38\u624b\u8f7b\u8f7b\u4e00\u63a5\uff0c\u4e00\u996e\u800c \u5c3d\uff0c\u7b11\u9053\uff1a\u201c\u591a\u8c22\u5c06\u519b\u8d50\u9152\uff01\u201d \u3000\u3000\u4fdd\u67f1\u7ed9\u51cc\u672a\u51e4\u8f83\u91cf\u4e0b\u53bb\uff0c\u975e\u5e38\u5c34\u5c2c\uff0c\u5e72\u7b11\u51e0\u58f0\uff0c\u5bf9\u5c11\u5e74\u4e66\u751f\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u8ddf\u968f\u771f\u597d\u529f \u5929\uff01\u201d\u5c11\u5e74\u4e66\u751f\u6115\u4e00\u6115\uff0c\u6b63\u5f85\u8d77\u7acb\u8bf4\u660e\u51cc\u672a\u98ce\u8eab\u4efd\uff0c\u51cc\u672a\u98ce\u5374\u6697\u629b\u773c\u8272\u5236\u6b62\uff0c\u8bf4\uff1a\u201c\u5c71\u91ce\u6821 \u539a\uff0c\u600e\u53ca\u5f97\u5927\u5c06\u519b\u795e\u6280\u3002\u201d \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u4fdd\u67f1\u4e3e\u624b\u8bf4\u9053\uff1a\u201c\u5e73\u897f\u738b\u6709\u4e8b\uff0c\u8981\u8fc7\u4e00\u4f1a\u624d\u6765\uff0c\u5148\u8bf7\u5404\u4f4d\u542c\u6b4c\u770b\u821e\u3002\u201d\u4ed6\u628a \u638c\u4e00\u62cd\uff0c\u5802\u4e0b\u51fa\u6765\u4e24\u7537\u4e24\u5973\uff0c\u5531\u4e86\u4e2a\u558f\uff0c\u968f\u5373\u5206\u6210\u4e24\u5bf9\uff0c\u7ed5\u7740\u5927\u5802\uff0c\u4e14\u821e\u4e14\u6b4c\u3002 \u3000\u3000\u6b4c\u58f0\u54cd\u904f\u884c\u4e91\uff0c\u821e\u59ff\u7fe9\u832c\u60ca\u9e3f\uff1b\u4ed6\u4eec\u8d8a\u821e\u8d8a\u6025\uff0c\u8d8a\u5531\u8d8a\u9ad8\u3002\u6b4c\u7684\u662f\u5357\u5b8b\u8bcd\u5bb6\u8f9b\u5f03\u75be\u7684\u4e00 \u9996\u8bcd\uff0c\u53ea\u542c\u4ed6\u4eec\u5531\u9053\uff1a\u201c\u9189\u91cc\u6311\u706f\u770b\u5251\uff0c\u68a6\u56de\u5439\u7528\u8fde\u8425\uff0c\u516b\u767e\u91cc\u5206\u9ebe\u4e0b\u7099\uff0c\u4e94\u5341\u5f26\u7ffb\u585e\u5916 \u58f0\uff0c\u6c99\u573a\u79cb\u70b9\u5175\u2026\u2026\u201d\u5c11\u5e74\u4e66\u751f\u62cd\u624b\u8bf4\u9053\uff1a\u201c\u58ee\u54c9\uff01\u201d\u8d5e\u58f0\u672a\u4e86\uff0c\u4e24\u5bf9\u7537\u5973\u5df2\u821e\u5230\u5927\u6bbf\u4e4b \u4e2d\uff0c\u8fd9\u65f6\u6b63\u5531\u81f3\u4e0b\u534a\u9619\u201c\u9a6c\u4f5c\u7684\u9a74\u98de\u5feb\uff01\u5f13\u5982\u9739\u96f7\u5f26\u60ca\u201d\u4e8c\u53e5\u3002 \u3000\u3000\u4ed6\u4eec\u75be\u821e\u5982\u98de\uff0c\u53cc\u624b\u4f5c\u51fa\u5f20\u5f13\u4e4b\u72b6\uff0c\u731b\u5411\u5916\u4e00\u653e\uff0c\u51cc\u672a\u98ce\u5de6\u8fb9\u684c\u4e0a\u70b9\u7740\u51e0\u679d\u5927\u725b\u6cb9\u70db\uff0c \u84e6\u7136\u706b\u7130\u7eb7\u98de\uff0c\u9f50\u9f50\u7184\u706d\uff0c\u4ed6\u4eec\u4e00\u4e2a\u65cb\u8eab\uff0c\u53cc\u624b\u5408\u4ec0\uff0c\u53c8\u662f\u9065\u9065\u6325\u638c\uff0c\u5411\u51cc\u672a\u98ce\u53f3\u8fb9\u5e2d\u536b\u626b \u53bb\uff0c\u638c\u98ce\u98d2\u7136\uff0c\u867d\u662f\u9694\u5e2d\uff0c\u4e5f\u81ea\u89c9\u5230\u3002 \u3000\u3000\u51cc\u672a\u98ce\u51dd\u5750\u4e0d\u52a8\uff0c\u4f46\u89c1\u53f3\u8fb9\u5e2d\u4e0a\u7684\u7ea2\u70db\uff0c\u7ed9\u638c\u98ce\u8feb\u5f97\u6447\u6643\u4e0d\u5b9a\uff0c\u4ed6\u5fae\u4e00\u4fa7\u8eab\uff0c\u4e5f\u8fd0\u638c\u9065 \u5411\u53f3\u8fb9\u5e2d\u4e0a\u6253\u53bb\uff0c\u90a3\u70db\u7130\u6b63\u5012\u5411\u51cc\u672a\u98ce\u8fd9\u8fb9\uff0c\u7ed9\u4e24\u9762\u7684\u638c\u98ce\u4e00\u5939\uff0c\u767b\u65f6\u53c8\u76f4\u7acb\u8d77\u6765\u3002\u51cc\u672a\u98ce \u5bf9\u4fdd\u67f1\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u534e\u4e3e\u591c\u5bb4\uff0c\u7ea2\u70db\u9ad8\u70e7\uff0c\u82e5\u4ee4\u70db\u706d\u5be1\u6b22\uff0c\u4f55\u5f02\u711a\u7434\u716e\u9e64\uff1f\u201d\u4fdd\u67f1\u6240\u9009\u7684\u4e24 \u5bf9\u7537\u5973\uff0c\u539f\u662f\u64c5\u6253\u5288\u7a7a\u638c\u7684\u9ad8\u624b\uff0c\u4ee5\u732e\u821e\u4e3a\u540d\uff0c\u6545\u610f\u70ab\u6280\u3002\u73b0\u5728\u6697\u4e2d\u8f83\u91cf\uff0c\u4e43\u662f\u5408\u56db\u4eba\u7684\u638c \u529b\uff0c\u624d\u582a\u582a\u654c\u5f97\u4f4f\u51cc\u672a\u98ce\uff0c\u4ed6\u6df1\u89c9\u989c\u9762\u65e0\u5174\uff0c\u7ed9\u51cc\u672a\u98ce\u4e00\u8bf4\uff0c\u8d81\u52bf\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u58ee\u58eb\u6240\u8a00\uff0c \u751a\u5408\u543e\u610f\uff0c\u53eb\u4ed6\u4eec\u505c\u4e86\u5427\u3002\u201d\u628a\u624b\u4e00\u6325\uff0c\u4e24\u5bf9\u7537\u5973\uff0c\u505c\u6b4c\u8f8d\u821e\uff0c\u6084\u6084\u5730\u6e9c\u4e0b\u5802\u53bb\u3002 \u3000\u3000\u4fdd\u67f1\u8fde\u51fa\u96be\u9898\uff0c\u6697\u4e2d\u8f83\u91cf\uff0c\u90fd\u96be\u5c11\u5e74\u4e66\u751f\u548c\u51cc\u672a\u98ce\u4e0d\u5012\uff0c\u602b\u7136\u4e0d\u4ec5\u3002\u540c\u5e2d\u7684\u4e00\u4f4d\u519b\u5b98\uff0c \u89c1\u72b6\u6602\u7136\u8d77\u7acb\uff0c\u5bf9\u4fdd\u67f1\u8bf4\u9053\uff1a\u201c\u4eca\u5bb5\u76db\u4f1a\uff0c\u4e0d\u53ef\u65e0\u6b22\uff0c\u5351\u804c\u613f\u7b75\u5d29\u821e\u5251\uff0c\u4ee5\u5a31\u8d35\u5bbe\uff0c\u4e45\u95fb\u674e \u516c\u5b50\u5251\u672f\u7cbe\u7edd\uff0c\u613f\u4f5c\u629b\u7816\u5f15\u7389\u4e4b\u8bf7\u3002\u201d\u5c11\u5e74\u4e66\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u5e76\u4e0d\u7b54\u8154\u3002\u4fdd\u67f1\u9053\uff1a\u201c\u4f60\u5148\u821e \u5427\uff0c\u82e5\u7a0d\u6709\u53ef\u89c2\uff0c\u4f55\u6101\u674e\u516c\u5b50\u4e0d\u80af\u8d50\u6559\uff01\u201d\u4fdd\u67f1\u660e\u77e5\u4ee5\u5c11\u5e74\u4e66\u751f\u7684\u8eab\u4efd\uff0c\u4e0d\u80af\u548c\u81ea\u5df1\u5e10\u4e0b\u4e00 \u4e2a\u519b\u5b98\u821e\u5251\uff0c\u56e0\u6b64\u6545\u610f\u4e00\u5531\u4e00\u548c\uff0c\u62ff\u8bdd\u6324\u8feb\u5c11\u5e74\u4e66\u751f\u51fa\u624b\u3002 \u3000\u3000\u8fd9\u519b\u5b98\u540d\u53eb\u8303\u950c\uff0c\u548c\u695a\u662d\u5357\u5f20\u5929\u8499\u5e76\u79f0\u738b\u5e9c\u4e09\u6770\uff0c\u5251\u672f\u6df1\u5f97\u5357\u6d3e\u6469\u4e91\u5251\u771f\u4f20\uff0c\u8fd9\u65f6\u5927\u6b65 \u8d70\u51fa\uff0c\u53cc\u624b\u5411\u5c11\u5e74\u4e66\u751f\u4e00\u62f1\uff0c\u9053\u58f0\u201c\u6055\u7f6a\u201d\uff0c\u4f69\u5251\u51db\u7136\u51fa\u9798\uff0c\u53f3\u624b\u633d\u5251\uff0c\u6253\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u5de6 \u624b\u637b\u7740\u5251\u8bc0\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u8d8a\u821e\u8d8a\u75be\uff0c\u65f6\u800c\u51cc\u7a7a\u9ad8\u8e48\uff0c\u65f6\u800c\u8d34\u5730\u5e73\u94fa\uff0c\u5251\u6c14\u68ee\u68ee\uff0c\u51b7\u5149\u8000\u76ee\uff0c \u8d8a\u821e\u8d8a\u8fd1\u3002\u4fdd\u67f1\u5f97\u610f\u6d0b\u6d0b\uff0c\u5bf9\u5c11\u5e74\u4e66\u751f\u8bf4\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u8fd9\u4eba\u7684\u5251\u672f\u4e0d\u53ef\u4e00\u662f\u4e86\u5417\uff1f\u201d \u3000\u3000\u5c11\u5e74\u4e66\u751f\u6de1\u6de1\u4e00\u7b11\uff0c\u672a\u53ca\u7b54\u8bdd\uff0c\u51cc\u672a\u98ce\u5df2\u84e6\u7136\u8d77\u7acb\uff0c\u622a\u4f4f\u8bf4\u9053\uff1a\u201c\u4e00\u4eba\u72ec\u821e\uff0c\u4f55\u5982\u4e24\u4eba \u5bf9\u821e\uff01\u201d\u4ed6\u5c06\u9519\u5c31\u9519\uff0c\u5c31\u4ee5\u674e\u516c\u5b50\u7684\u8ddf\u968f\u81ea\u5c45\uff0c\u4e0d\u5f85\u4fdd\u67f1\u70b9\u5934\uff0c\u4fbf\u5f84\u81ea\u5927\u6b65\u8d70\u51fa\u3002 \u3000\u3000\u51cc\u672a\u98ce\u8fd9\u4e00\u8d70\u51fa\uff0c\u8303\u950c\u987f\u65f6\u5c06\u5251\u52bf\u4e00\u6536\uff0c\u5706\u7741\u53cc\u773c\uff0c\u76ef\u7740\u51cc\u672a\u98ce\uff0c\u6309\u5251\u8bf4\u9053\uff1a\u201c\u8bf7\uff01\u201d \u51cc\u672a\u98ce\u4e00\u58f0\u4e0d\u54cd\uff0c\u5c06\u6e38\u9f99\u5251\u55d6\u5730\u62d4\u51fa\uff0c\u53ea\u89c1\u4e00\u6cd3\u79cb\u6c34\uff0c\u95ea\u95ea\u5149\u534e\u3002\u8303\u950c\u4e0e\u695a\u662d\u5357\u66fe\u5728\u738b\u5e9c\u65e5 \u5915\u76f8\u5904\uff0c\u4e00\u89c1\u4fbf\u8ba4\u51fa\u8fd9\u662f\u695a\u662d\u5357\u7684\u4f69\u5251\uff0c\u9762\u8272\u5927\u53d8\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u53e3\u5251\u4ece\u90a3\u91cc\u5f97\u6765\u3002\u201d\u51cc\u672a \u98ce\u5c06\u5251\u4e00\u629b\u4e00\u63a5\uff0c\u4f3c\u6f2b\u4e0d\u7ecf\u610f\u5730\u8bf4\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u59d3\u695a\u7684\u5bb6\u4f19\uff0c\u81ea\u4f1a\u5251\u672f\u5929\u4e0b\u65e0\u654c\uff0c\u6211\u548c\u4ed6\u6bd4 \u8bd5\uff0c\u539f\u6765\u7adf\u662f\u4e2a\u94f6\u6837\u8721\u67aa\u5934\uff0c\u4e0d\u8fc7\u4ed6\u8fd9\u53e3\u5251\u5012\u662f\u597d\u5bb6\u4f19\uff0c\u6211\u4e0d\u5ba2\u6c14\uff0c\u5c31\u628a\u5b83\u62ff\u4e0b\uff0c\u770b\u5728\u8fd9\u53e3 \u5251\u9762\u4e0a\uff0c\u6211\u8981\u4e86\u4ed6\u7684\u4e1c\u897f\uff0c\u5c31\u9976\u4e86\u4ed6\u7684\u6027\u547d\uff0c\u4f60\u770b\uff0c\u8fd9\u53e3\u5251\u8fd8\u597d\uff1f\u201d\u8bf4\u7f62\u53c8\u5c06\u5251\u629b\u4e86\u4e00\u629b\uff0c \u597d\u50cf\u5b69\u5b50\u73a9\u5f04\u5fc3\u7231\u7684\u73a9\u5177\u4e00\u6837\u3002 \u3000\u3000\u8303\u950c\u542c\u4e86\u505a\u58f0\u4e0d\u5f97\u3002\u4ed6\u81ea\u77e5\u5251\u672f\u4e0d\u53ca\u695a\u662d\u5357\u7cbe\u5999\uff0c\u695a\u662d\u5357\u7684\u5251\u5c1a\u4e14\u7ed9\u4eba\u593a\u4e86\uff0c\u4ed6\u5982\u4f55\u80fd \u884c\uff1f\u8fd9\u65f6\u6b63\u662f\u8fdb\u9000\u4e24\u96be\uff0c\u4e45\u4e45\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u51cc\u672a\u98ce\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u5251\u63d2\u56de\u9798\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u6211 \u8fd9\u53e3\u5251\u662f\u5b9d\u5251\uff0c\u9760\u5175\u5668\u53d6\u80dc\uff0c\u58ee\u592b\u4e0d\u4e3a\uff0c\u6211\u5c31\u53cc\u638c\u63a5\u9601\u4e0b\u51e0\u62db\u5427\uff01\u201d\u8bf4\u7740\u53cc\u624b\u4e00\u62f1\uff0c\u8fde\u58f0\u9053 \u8bf7\uff01 \u3000\u3000\u8303\u950c\u7ed9\u51cc\u672a\u98ce\u903c\u5f97\u4e0b\u4e0d\u4e86\u53f0\uff0c\u5fc3\u60f3\u5373\u662f\u695a\u662d\u5357\u4e5f\u7edd\u4e0d\u6562\u4ee5\u8089\u638c\u6765\u5bf9\u6211\u7684\u5229\u5251\uff0c\u8fd9\u4eba\u7eb5\u6bd4 \u695a\u662d\u5357\u8fd8\u5f3a\uff0c\u5728\u6469\u4e91\u5251\u6cd5\u4e0b\u4e5f\u987b\u8ba8\u4e0d\u4e86\u597d\u53bb\uff0c\u5fc3\u4e2d\u4e00\u5b9a\uff0c\u5251\u82b1\u4e00\u633d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u7528\u53cc\u638c\u6765 \u8f83\u91cf\u4ffa\u7684\u5251\u6cd5\uff0c\u8db3\u89c1\u9ad8\u660e\u3002\u53ea\u662f\u5229\u5251\u65e0\u60c5\uff0c\u82e5\u662f\u6b7b\u4f24\uff0c\u4f60\u4eec\u662f\u5ba2\uff0c\u8fd9\u5374\u5982\u4f55\u4f7f\u5f97\uff1f\u201d\u4ed6\u8fb9\u8bf4 \u8fb9\u770b\u7740\u4fdd\u67f1\u548c\u5c11\u5e74\u4e66\u751f\u3002 \u3000\u3000\u51cc\u672a\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u82e5\u6709\u6b7b\u4f24\uff0c\u5404\u5b89\u5929\u547d\u3002\u54b1\u4eec\u628a\u8bdd\u8bf4\u5728\u5934\u91cc\uff0c\u8c01\u4e5f\u602a\u4e0d\u4e86\u8c01\uff0c\u4f60\u53ea\u7ba1 \u8fdb\u62db\uff0c\u53ea\u6050\u4f60\u5251\u950b\u867d\u5229\uff0c\u4ffa\u8fd9\u53cc\u8089\u638c\u4e5f\u4e0d\u6613\u53eb\u4f60\u523a\u7740\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53cc\u81c2\u4e00\u5c48\u4e00\u4f38\uff0c\u7738\u773c\u800c \u89c6\u3002 \u3000\u3000\u4fdd\u67f1\u7ed9\u51cc\u672a\u51e4\u6fc0\u5f97\u5fcd\u53d7\u4e0d\u4f4f\uff0c\u5fc3\u60f3\u5c11\u5e74\u4e66\u751f\u867d\u4e0d\u80fd\u8f7b\u6613\u5192\u72af\uff0c\u4f46\u62ff\u4ed6\u7684\u8ddf\u968f\u51fa\u6c14\uff0c\u4e5f\u53ef \u6740\u6740\u4ed6\u4eec\u7684\u6c14\u7130\uff0c\u9042\u5927\u58f0\u5429\u5490\u9053\uff1a\u201c\u8303\u950c\uff0c\u4f60\u65e2\u9047\u9ad8\u660e\uff0c\u5c31\u8be5\u9886\u6559\uff0c\u5b66\u4e2a\u4e09\u62db\u4e24\u5f0f\u3002\u6b66\u6797\u5370 \u8bc1\uff0c\u4e8b\u5c5e\u5bfb\u5e38\uff0c\u7eb5\u6709\u8bef\u4f24\uff0c\u674e\u516c\u5b50\u5c82\u80fd\u602a\u4f60\uff1f\u201d\u8bf4\u7f62\u5411\u5c11\u5e74\u4e66\u751f\u563f\u563f\u7b11\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u6211\u8fd9 \u8bdd\u53ef\u6ca1\u8bf4\u9519\uff1f\u201d\u5c11\u5e74\u4e66\u751f\u89c1\u8303\u950c\u521a\u624d\u51fa\u624b\u4e0d\u51e1\uff0c\u751a\u4e3a\u51cc\u672a\u98ce\u62c5\u5fc3\uff0c\u53ea\u4ee5\u51cc\u672a\u98ce\u628a\u8bdd\u8bf4\u5f97\u592a \u6ee1\uff0c\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u8303\u950c\u89c1\u4fdd\u67f1\u51fa\u5934\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u5251\u8bc0\u4e00\u9886\uff0c\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u75be\u5982\u95ea\u7535\uff0c\u4fbf\u5411\u51cc\u672a\u98ce\u54bd\u5589\u523a \u6765\uff0c\u51cc\u672a\u98ce\u53cc\u638c\u4e00\u62c2\uff0c\u8eab\u968f\u638c\u8d70\uff0c\u53f3\u638c\u4e00\u6309\u5251\u67c4\uff0c\u5de6\u638c\u201c\u659c\u6302\u5355\u97ad\u201d\uff0c\u4fbf\u5411\u8303\u950c\u8109\u95e8\u5207\u3002\u8303 \u950c\u8eab\u624b\u4e5f\u7aef\u7684\u8fc5\u6377\uff0c\u5de6\u811a\u4e00\u6ed1\uff0c\u5251\u950b\u4e00\u4fa7\uff0c\u5bd2\u5149\u95ea\u5904\uff0c\u622a\u638c\u6302\u80a9\uff0c\u5237\u7684\u53c8\u626b\u8fc7\u53bb\u3002\u51cc\u672a\u98ce\u4e00 \u957f\u5578\uff0c\u53cc\u638c\u659c\u5c55\uff0c\u5251\u950b\u5728\u4ed6\u80f8\u524d\u63a0\u8fc7\uff0c\u4ed6\u500f\u5730\u5411\u524d\u4e00\u6251\uff0c\u53cc\u638c\u556a\u7684\u4e00\u4e0b\uff0c\u5728\u8303\u6323\u80a9\u5934\u51fb\u4e86\u4e00 \u638c\u3002 \u3000\u3000\u8fd9\u4e00\u62ff\u53ea\u7528\u4e86\u4e09\u6210\u529b\u91cf\uff0c\u8303\u950c\u5df2\u611f\u4e00\u9635\u5267\u75db\uff01\u6025\u5f80\u540e\u4e00\u7eb5\uff0c\u907f\u5c06\u5f00\u53bb\u3002\u51cc\u672a\u98ce\u7b11\u9053\uff1a \u201c\u627f\u8ba9\uff01\u201d\u8303\u950c\u54ac\u7259\u5fcd\u4f4f\uff0c\u4e00\u58f0\u4e0d\u53d1\uff0c\u5de6\u624b\u4e00\u9886\u5251\u950b\uff0c\u53c8\u72e0\u72e0\u653b\u4e0a\uff0c\u5251\u5251\u76f4\u523a\u8981\u5bb3\u3002\u51cc\u672a\u98ce \u89c1\u4ed6\u5982\u6b64\u65e0\u793c\uff0c\u5fc3\u4e2d\u5927\u6012\uff0c\u5c55\u5f00\u5929\u5c71\u638c\u6cd5\u4e2d\u7684\u622a\u5b57\u8bc0\uff0c\u6311\u7948\u62e6\u5207\uff0c\u5c01\u95ed\u64d2\u62ff\uff0c\u53cc\u638c\u8d77\u5904\uff0c\u5168 \u662f\u8fdb\u624b\u62db\u6570\u3002\u5728\u5251\u5149\u71ce\u7ed5\u4e4b\u4e2d\uff0c\u84e6\u5730\u6b3a\u8eab\u76f4\u8fbe\uff0c\u5de6\u624b\u9a88\u6307\u5982\u9501\uff0c\u5411\u8303\u950c\u5de6\u4e73\u95e8\u7a74\u70b9\u53bb\u3002\u8303\u950c \u4e0d\u6599\u654c\u4eba\u8eab\u6cd5\u5982\u6b64\u5947\u5feb\uff0c\u53ea\u597d\u5f80\u540e\u64a4\u8eab\uff0c\u4ed6\u81ea\u4ee5\u4e3a\u9000\u5f97\u5feb\u3002\u90a3\u77e5\u51cc\u672a\u98ce\u8fdb\u5f97\u66f4\u5feb\uff0c\u5982\u5f71\u968f \u5f62\uff0c\u4e00\u6291\u8eab\uff0c\u53f3\u638c\u5f80\u5de6\u65f6\u4e0b\u4e00\u7a7f\uff0c\u6b63\u6b63\u6309\u5728\u8303\u950c\u7684\u4e39\u7530\u4e0a\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u8303\u950c\u8eab\u9a71\u51cc\u7a7a\u98de\u8d77\uff0c \u624b\u4e2d\u5251\u4e5f\u5815\u4e0b\u6765\u3002\u51cc\u672a\u98ce\u5c06\u5251\u4e00\u628a\u6309\u7740\uff0c\u8303\u950c\u4e5f\u81ea\u6709\u4eba\u51fa\u6765\u6276\u8d77\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5c06\u63a5\u6765\u7684\u5251\uff0c\u7b11\u563b\u563b\u5730\u5f80\u4e0a\u4e00\u629b\uff0c\u5c06\u6e38\u9f99\u5251\u62d4\u51fa\uff0c\u5f80\u4e0a\u4e00\u8fce\uff0c\u628a\u8303\u950c\u7684\u5251\u622a\u4e3a\u4e24 \u6bb5\uff0c\u5927\u6b65\u56de\u8f6c\u5e2d\u4e0a\u3002 \u3000\u3000\u8fd9\u65f6\u5434\u4e09\u6842\u624b\u4e0b\u7684\u6b66\u58eb\u90fd\u52a8\u4e86\u516c\u6124\uff0c\u970e\u65f6\u95f4\u51fa\u6765\u4e86\u4e03\u516b\u4e2a\u4eba\uff0c\u56f4\u5728\u51cc\u672a\u98ce\u9762\u524d\uff0c\u8bf4\u9053\uff1a \u201c\u8fd9\u4f4d\u58ee\u58eb\u8d62\u4e86\u8303\u950c\uff0c\u6211\u4eec\u65e0\u8bdd\u53ef\u8bf4\u3002\u53ea\u662f\u8fd9\u628a\u5251\u4e43\u662f\u6211\u4eec\u7684\u5934\u9886\u695a\u662d\u5357\u7684\uff0c\u4ed6\u76d7\u6765\u6b64\u5251\uff0c \u53c8\u5230\u8fd9\u91cc\u5356\u5f04\uff0c\u65e2\u8d62\u4e86\u4ed6\uff0c\u8fd8\u8981\u524a\u65ad\u522b\u4eba\u5175\u5668\uff0c\u6211\u4eec\u5012\u8981\u8bf7\u6559\u8bf7\u6559\uff0c\u8fd9\u662f\u5982\u4f55\u8bf4\u6cd5\uff1f\u201d\u6b63\u7eb7 \u95f9\u95f4\uff0c\u5ffd\u7136\u540e\u5802\u4e09\u58f0\u9f13\u54cd\uff0c\u4e2d\u519b\u624b\u6267\u9ec4\u65d7\uff0c\u5927\u58f0\u53eb\u559d\u5230\uff1a\u201c\u5e73\u897f\u738b\u9a7e\u5230\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u7b75\u524d\u9f99\u864e\u6597\uff0c\u8c6a\u6c14\u538b\u85e9\u738b\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u4e03\u7ae0\u3000\u5251\u80c6\u7434\u5fc3\u3000\u4f3c\u559c\u4f3c\u55d4\u540c\u547d\u9e1f\u3000\u96ea\u6ce5\u9e3f\u722a\u3000\u4ea6\u771f\u4ea6\u5e7b\u5f02\u4e61\u4eba \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u4e03\u56de \u5251\u80c6\u7434\u5fc3\u3000\u4f3c\u559c\u4f3c\u55d4\u540c\u547d\u9e1f \u96ea\u6ce5\u9e3f\u722a\u3000\u4ea6\u771f\u4ea6\u5e7b\u5f02\u4e61\u4eba \u3000\u3000\u4e09\u58f0\u9f13\u54cd\uff0c\u5434\u4e09\u6842\u7f13\u7f13\u8d70\u8fdb\u6765\uff0c\u5802\u4e0a\u5c06\u9886\u7eb7\u7eb7\u8d77\u7acb\u3002\u5c11\u5e74\u4e66\u751f\u548c\u5218\u90c1\u82b3\u4ecd\u662f\u7aef\u5750\u5e2d\u4e2d\u3002 \u51cc\u672a\u98ce\u672c\u6765\u662f\u7ad9\u7740\u548c\u6b66\u58eb\u7406\u8bba\u7684\uff0c\u8fd9\u65f6\u4e5f\u7d22\u6027\u5927\u9a6c\u91d1\u5200\u5730\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u51cc\u672a\u98ce\u51b7\u773c\u770b\u53bb\uff0c\u53ea\u89c1\u5434\u4e09\u6842\u5e74\u8fc7\u516d\u65ec\uff0c\u5934\u9876\u5df2\u7ecf\u6709\u4e9b\u79c3\u4e86\uff0c\u5bb9\u989c\u7565\u663e\u6194\u60b4\uff0c\u5374\u4e5f\u65e0\u9f99 \u949f\u4e4b\u6001\u3002\u5c11\u5e74\u4e66\u751f\u9762\u4e0a\u51b7\u51b0\u51b0\u7684\uff0c\u53cc\u76ee\u8574\u6012\uff0c\u53cc\u624b\u7d27\u7d27\u6309\u7740\u684c\u5b50\uff0c\u4f3c\u5728\u90a3\u91cc\u5f3a\u81ea\u6291\u5236\u3002 \u3000\u3000\u5434\u4e09\u6302\u89c1\u4e86\u5c11\u5e74\u4e66\u751f\uff0c\u6ee1\u9762\u5806\u6b22\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u516c\u5b50\u771f\u662f\u4fe1\u4eba\uff0c\u679c\u7136\u4e0d\u8fdc\u5343\u91cc\u800c\u6765\uff0c\u5e78 \u4f1a\uff0c\u5e78\u4f1a\uff01\u201d\u5c11\u5e74\u4e66\u751f\u8fd9\u624d\u7f13\u7f13\u8d77\u7acb\uff0c\u5fae\u5fae\u6b20\u8eab\uff0c\u8bf4\u9053\uff1a\u201c\u5e73\u897f\u738b\uff0c\u4f60\u597d\u5440\uff01\u201d\u201c\u5e73\u897f\u738b\u201d \u4e09\u5b57\uff0c\u8bf4\u5f97\u7279\u522b\u5927\u58f0\uff0c\u5434\u4e09\u6842\u9762\u8272\u500f\u53d8\uff0c\u5c34\u5c2c\u4e4b\u6781\uff0c\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u674e\u516c\u5b50\u5feb\u522b\u8fd9\u6837\u79f0\u547c\uff0c\u4eca \u65e5\u54b1\u4eec\u8be5\u4ee5\u81f3\u8bda\u76f8\u89c1\uff01\u201d \u3000\u3000\u90a3\u51e0\u4e2a\u56f4\u5728\u51cc\u672a\u51e4\u65c1\u8fb9\u7684\u6b66\u58eb\uff0c\u8dc3\u8dc3\u6b32\u52a8\u3002\u5434\u4e09\u6842\u89c1\u51cc\u672a\u98ce\u7765\u7768\u4f5c\u6001\uff0c\u65c1\u82e5\u65e0\u4eba\uff0c\u8be7\u5f02 \u95ee\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u8fd9\u4f4d\u670b\u53cb\u53c8\u662f\u4f55\u4eba\uff1f\u201d\u5c11\u5e74\u4e66\u751f\u5fae\u7b11\u9053\uff1a\u201c\u4ed6\u662f\u540d\u6ee1\u897f\u5317\u7684\u5927\u4fa0\u51cc\u672a \u98ce\uff01\u201d\u4fdd\u67f1\u542c\u4e86\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u51cc\u672a\u98ce\u7684\u540d\u5934\u4ed6\u662f\u542c\u8fc7\u7684\uff0c\u53ef\u662f\u5374\u4e07\u60f3\u4e0d\u5230\u4ed6\u4f1a\u8dd1\u5230\u6606\u660e\u6765\uff0c \u800c\u4e14\u662f\u548c\u5c11\u5e74\u4e66\u751f\u5728\u4e00\u9053\u3002 \u3000\u3000\u51cc\u672a\u98ce\u6602\u7136\u8d77\u7acb\uff0c\u5bf9\u5434\u4e09\u6842\u9053\uff1a\u201c\u738b\u7237\u5e10\u4e0b\u4e0d\u5fff\u6211\u62ff\u4e86\u8fd9\u628a\u5251\u2026\u2026\u8bf4\u7740\u6307\u4e00\u6307\u8170\u4e2d\u7684\u6e38 \u9f99\u5251\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u53e3\u5251\u662f\u6211\u81ea\u695a\u662d\u5357\u624b\u4e2d\u53d6\u6765\u7684\uff0c\u4ed6\u73b0\u5728\u662f\u5f53\u4eca\u7687\u4e0a\u7684\u5fc3\u8179\u536b\u58eb\uff0c\u738b\u7237 \u4e5f\u6653\u5f97\u8fd9\u4e2a\u4eba\u5417\uff1f\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u6b66\u58eb\u54d7\u7136\u3002\u51cc\u672a\u98ce\u5728\u6000\u4e2d\u63a2\u51fa\u4e00\u5c01\u4fe1\uff0c\u9012\u7ed9\u4fdd\u67f1\uff0c\u8bf4\u9053\uff1a \u201c\u8bf7\u4f60\u4ea4\u7ed9\u738b\u7237\uff01\u201d \u3000\u3000\u5434\u4e09\u6842\u62c6\u4fe1\u4e00\u770b\uff0c\u51b7\u6c57\u76f4\u6d41\u3002\u8fd9\u4fe1\u7adf\u662f\u6e05\u5ef7\u5bc6\u8bcf\uff0c\u7ed9\u9a7b\u6606\u660e\u7684\u5b89\u897f\u5c06\u519b\u674e\u672c\u6df1\uff0c\u53eb\u4ed6\u4f1a \u540c\u4e91\u5357\u5de1\u629a\u6731\u56fd\u6cbb\u5bc6\u8c0b\u628a\u5434\u4e09\u6842\u9664\u6389\u7684\u3002\u4ed6\u770b\u4e86\uff0c\u5c06\u4fe1\u4e00\u56e2\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u5bf9\u968f\u4ece \u6b66\u58eb\u5429\u5490\u51e0\u53e5\uff0c\u53eb\u4ed6\u4eec\u5148\u9000\u4e0b\u53bb\u3002 \u3000\u3000\u5434\u4e09\u6842\u4ea4\u5f85\u5b8c\u6bd5\uff0c\u9762\u8272\u4e00\u7aef\uff0c\u5bf9\u6b66\u571f\u6b4c\u5973\u7b49\u4e00\u5e72\u4eba\u4f17\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u901a\u901a\u7ed9\u6211\u9000 \u4e0b\u3002\u201d\u7247\u523b\u4e4b\u95f4\uff0c\u5927\u5802\u53c8\u590d\u5e73\u9759\uff0c\u4e00\u4f17\u6b66\u58eb\u90fd\u5728\u95e8\u5916\u4f8d\u5019\uff0c\u5802\u4e0a\u53ea\u7559\u4e0b\u5434\u4e09\u6842\u7684\u51e0\u4e2a\u5fc3\u8179\u5c06 \u9886\u3002 \u3000\u3000\u5434\u4e09\u6842\u5429\u5490\u91cd\u6574\u7b75\u5e2d\uff0c\u4eb2\u81ea\u7aef\u8d77\u9152\u6765\uff0c\u5bf9\u5c11\u5e74\u4e66\u751f\u8bf4\u9053\uff1a\u201c\u4ee4\u53d4\u7956\u76d6\u4e16\u82f1\u8c6a\uff0c\u529f\u8f89\u65e5 \u6708\u3002\u5f53\u5e74\u4ffa\u5e74\u5c11\u6c14\u76db\uff0c\u4e00\u7740\u68cb\u5dee\uff0c\u539f\u610f\u4e5f\u5e76\u975e\u53cd\u5bf9\u4ee4\u53d4\u963b\uff0c\u800c\u662f\u6b32\u4e3a\u4ee4\u53d4\u7956\u6e05\u9664\u2018\u541b\u4fa7\u2019\uff0c \u5c06\u5218\u5b97\u654f\u725b\u91d1\u661f\u7b49\u5978\u8d3c\u626b\u706d\uff0c\u4e0d\u610f\u5f04\u6210\u4eca\u65e5\u4e4b\u5c40\u3002\u4e09\u5341\u4f59\u5e74\u6765\uff0c\u6bcf\u4e00\u5ff5\u53ca\uff0c\u8f84\u5982\u8292\u523a\u5728\u80cc\u3002 \u65e5\u524d\u4e0e\u4ee4\u5144\u4fee\u51fd\u901a\u597d\uff0c\u4eca\u65e5\u53c8\u627f\u516c\u5b50\u4e0d\u5f03\uff0c\u8fdc\u9053\u524d\u6765\uff0c\u8bf7\u5c3d\u6b64\u676f\u8584\u9152\uff0c\u4ee5\u91ca\u4e24\u5bb6\u4e4b\u5acc\uff01\u201d\u51cc \u672a\u98ce\u542c\u4e86\uff0c\u5927\u5403\u4e00\u60ca\u3002\u539f\u6765\u8fd9\u5c11\u5e74\u4e66\u751f\uff0c\u7adf\u662f\u674e\u81ea\u6210\u7684\u4f84\u5b59\u3002\u91d1\u5d16\u542c\u4e86\uff0c\u4e5f\u624d\u604d\u7136\u5927\u609f\uff0c\u81ea \u5df1\u8eab\u4efd\u7684\u786e\u6bd4\u4ed6\u5dee\u5f97\u5f88\u8fdc\u3002\u53ea\u662f\u8c01\u90fd\u77e5\u9053\u674e\u81ea\u6210\u529f\u8d25\u5782\u6210\uff0c\u539f\u56e0\u5c31\u662f\u5728\u4e8e\u5434\u4e09\u6842\u5f15\u6e05\u5175\u5165 \u5173\uff0c\u8fd9\u79cd\u5927\u6068\u6df1\u4ec7\uff0c\u5982\u4f55\u80fd\u591f\u5316\u89e3\uff1f\u4ed6\u4eec\u4e07\u5206\u4e0d\u89e3\u4f55\u4ee5\u674e\u81ea\u6210\u7684\u4f84\u5b59\u5c45\u7136\u6562\u6765\uff0c\u800c\u5434\u4e09\u6842\u53c8 \u4ee5\u4e0a\u5bbe\u76f8\u5f85\uff1f \u3000\u3000\u8bf4\u8d77\u8fd9\u6b21\u79bb\u5947\u7684\u805a\u4f1a\uff0c\u8981\u8ffd\u6eaf\u5230\u4e09\u5341\u4e09\u5e74\u524d\u7684\u62c4\u4e8b\uff0c\u90a3\u65f6\u662f\u660e\u671d\u672a\u4ee3\u7687\u5e1d\u5d07\u5e27\u7684\u672b\u5e74\uff0c \u674e\u81ea\u6210\u7684\u519c\u6c11\u519b\u81ea\u897f\u5b89\u4e00\u76f4\u6253\u5230\u5317\u4eac\uff0c\u5d07\u5e27\u5728\u7164\u5c71\u81ea\u7f22\uff0c\u5434\u4e09\u6842\u90a3\u65f6\u662f\u8fbd\u4e1c\u9547\u7684\u603b\u5175\uff0c\u9a7b\u9632 \u5c71\u6d77\u5173\uff0c\u7edf\u6709\u9a6c\u6b65\u519b\u5341\u4f59\u4e07\uff0c\u5f53\u674e\u81ea\u6210\u5927\u4e3e\u8fdb\u653b\u3001\u4eac\u5e08\u5371\u6025\u4e4b\u65f6\uff0c\u660e\u671d\u5c01\u5434\u4e09\u6842\u4e3a\u201c\u5e73\u897f \u738b\u201d\uff0c\u53eb\u4ed6\u6025\u6025\u5e26\u5175\u56de\u4eac\u3002\u54ea\u77e5\u4ed6\u8d70\u5230\u4e2d\u9014\uff0c\u4eac\u57ce\u5df2\u7834\uff0c\u4ed6\u53c8\u91cd\u56de\u5c71\u6d77\u5173\u89c2\u671b\u3002 \u3000\u3000\u674e\u81ea\u6210\u653b\u7834\u5317\u4eac\u540e\uff0c\u660e\u671d\u7684\u529b\u91cf\u5df2\u7ecf\u74e6\u89e3\uff0c\u53ea\u5269\u4e0b\u5434\u4e09\u6842\u8fd9\u652f\u4eba\u9a6c\u8fd8\u6709\u70b9\u5b9e\u529b\u4e86\u3002\u674e\u81ea \u6210\u4e3a\u4e86\u5c3d\u65e9\u6536\u62fe\u5927\u5c40\uff0c\u9042\u53eb\u5434\u4e09\u6842\u7684\u7236\u4eb2\u5434\u8944\u4f5c\u62e7\u529d\u964d\u3002\u5434\u4e09\u6842\u521d\u65f6\u4ee5\u52bf\u5b64\u529b\u8584\uff0c\u81ea\u5ff5\u8fdc\u975e \u674e\u81ea\u6210\u5bf9\u624b\uff0c\u88ab\u8feb\u7b54\u5e94\u6295\u964d\u3002\u4e0d\u6599\u4ed6\u672a\u5230\u5317\u4eac\uff0c\u5c31\u542c\u5230\u7231\u59be\u9648\u5706\u5706\u88ab\u5218\u5b97\u654f\u6240\u593a\u7684\u6d88\u606f\uff0c\u5218 \u5b97\u654f\u6b63\u662f\u674e\u81ea\u6210\u9ebe\u4e0b\u7b2c\u4e00\u5458\u5927\u5c06\u3002\u4ed6\u5927\u6012\u4e4b\u4e0b\uff0c\u53c8\u60f3\u8d77\u81ea\u5df1\u82e5\u6295\u964d\u674e\u81ea\u6210\uff0c\u4e00\u5b9a\u8981\u5c48\u5c55\u5218\u5b97 \u654f\u725b\u91d1\u661f\uff08\u674e\u81ea\u6210\u7684\u5bb0\u76f8\uff09\u7b49\u4eba\u4e4b\u4e0b\uff0c\u5229\u7984\u672a\u5982\u5df1\u610f\uff0c\u593a\u59be\u4e4b\u6068\u96be\u6d88\uff0c\u4e8e\u662f\u9042\u5e61\u7136\u53d8\u8ba1\uff0c\u7adf \u7136\u52fe\u5f15\u6e05\u5175\u5165\u5173\uff0c\u628a\u674e\u81ea\u6210\u7684\u519b\u961f\u548c\u5357\u660e\u7684\u6b8b\u4f59\u653f\u6743\u90fd\u6d88\u706d\u4e86\uff0c\u5f97\u5230\u9648\u5706\u5706\u7684\u4ee3\u4ef7\u662f\u505a\u4e86\u5934 \u53f7\u6c49\u5978\u3002 \u3000\u3000\u674e\u81ea\u6210\u5728\u6e05\u5175\u548c\u5434\u4e09\u6842\u5939\u51fb\u4e4b\u4e0b\uff0c\u5728\u6e56\u5317\u4e5d\u5bab\u5c71\u6218\u6b7b\u3002\u4f46\u4ed6\u6b7b\u540e\u8fd8\u7559\u4e0b\u5404\u5730\u7684\u519c\u6c11\u519b\u56db \u5341\u4e07\u4e4b\u4f17\uff0c\u7531\u4ed6\u7684\u4f84\u513f\u674e\u9526\u7387\u9886\uff0c\u56e0\u5927\u654c\u5f53\u524d\uff0c\u519c\u6c11\u519b\u51b3\u5b9a\u548c\u5357\u660e\u653f\u5e9c\u5408\u4f5c\uff0c\u5357\u660e\u653f\u5e9c\u8fd8\u66fe \u5c01\u674e\u9526\u7684\u519b\u961f\u4e3a\u201c\u5fe0\u8d1e\u8425\u201d\uff0c\u5c01\u674e\u81ea\u6210\u7684\u59bb\u5b50\u9ad8\u6c0f\u4e3a\u201c\u5fe0\u8d1e\u592b\u4eba\u201d\u3002\u4e0d\u8fc7\u674e\u9526\u867d\u548c\u5357\u660e\u653f\u5e9c \u5408\u4f5c\uff0c\u5374\u4ecd\u662f\u4fdd\u6301\u72ec\u7acb\uff0c\u4ecd\u5949\u5927\u5e05\uff08\u674e\u81ea\u6210\u5efa\u56fd\u7684\u56fd\u53f7\uff09\u6b63\u6714\uff0c\u79f0\u674e\u81ea\u6210\u4e3a\u201c\u5148\u5e1d\u201d\uff0c\u79f0\u9ad8 \u6c0f\u4e3a\u201c\u592a\u540e\u201d\u3002\u540e\u6765\u674e\u9526\u53c8\u5728\u6e56\u5357\u6218\u6b7b\uff0c\u519b\u961f\u7531\u674e\u9526\u7684\u517b\u5b50\u674e\u6765\u4ea8\u7387\u9886\uff0c\u8f6c\u6218\u81f3\u56db\u5ddd\u4e91\u5357\u7684 \u8fb9\u533a\uff0c\u5341\u4f59\u4e07\u519b\u961f\u90fd\u5206\u6563\u85cf\u533f\u5c71\u5cad\u4e4b\u4e2d\u3002\u6e05\u671d\u540e\u6765\u5c01\u5434\u4e09\u6842\u4e3a\u5e73\u897f\u738b\uff0c\u547d\u4ed6\u7ba1\u8f96\u4e91\u5357\u56db\u5ddd\u4e24 \u7701\uff0c\u7528\u610f\u4e4b\u4e00\uff0c\u5c31\u662f\u8981\u4ed6\u5bf9\u4ed8\u674e\u81ea\u6210\u7684\u6b8b\u90e8\u3002 \u3000\u3000\uff08\u7fbd\u751f\u6309\uff1a\u674e\u6765\u4ea8\u636e\u8bf4\u662f\u5728\u5eb7\u7199\u4e09\u5e74\u56e0\u529b\u7aed\u77e2\u5c3d\uff0c\u81ea\u711a\u4e8e\u6e56\u5317\u8305\u9e93\u5c71\u4e5d\u83b2\u576a\u7684\uff0c\u4f46\u5c0f\u8bf4 \u4e0d\u540c\u5386\u53f2\uff0c\u800c\u4e14\u8bf4\u4e0d\u5b9a\u4ed6\u662f\u201c\u5047\u6b7b\u201d\uff0c\u56e0\u6b64\u6211\u5199\u4ed6\u5728\u5eb7\u7199\u5341\u4e8c\u5e74\u4e4b\u540e\u4ecd\u7136\u751f\u5b58\u3002\u4f5c\u8005\u59d1\u5984\u544a \u4e4b\uff0c\u8bfb\u8005\u59d1\u5984\u542c\u4e4b\u53ef\u4e5f\u3002\uff09 \u3000\u3000\u5434\u4e09\u6842\u5f00\u5e9c\u6606\u660e\u4e4b\u540e\uff0c\u4e5f\u66fe\u5c61\u6b21\u6d3e\u519b\u201c\u8fdb\u527f\u201d\uff0c\u53ef\u662f\u5ddd\u6ec7\u8fb9\u5883\uff0c\u6df1\u5c71\u5927\u5ddd\uff0c\u5730\u52bf\u9669\u5cfb\uff0c \u674e\u6765\u4ea8\u90e8\u961f\u53c8\u795e\u51fa\u9b3c\u6ca1\uff0c\u98d8\u5ffd\u5982\u98ce\uff0c\u56e0\u6b64\u5728\u660e\u4ea1\u4e4b\u540e\u4e00\u76f4\u6210\u4e3a\u6e05\u5ef7\u7684\u9690\u60a3\u3002 \u3000\u3000\u8fd9\u6837\u7684\u50f5\u6301\uff0c\u7ee7\u7eed\u4e86\u5341\u4f59\u5e74\u3002\u674e\u6765\u4ea8\u867d\u7136\u9650\u4e8e\u5b9e\u529b\u4e0d\u80fd\u51fa\u51fb\uff0c\u5434\u4e09\u6842\u4e5f\u4e0d\u6562\u6df1\u5165\u201c\u527f \u532a\u201d\u3002\u8fd9\u5c11\u5e74\u4e66\u751f\u540d\u5524\u674e\u601d\u6c38\uff0c\u662f\u674e\u6765\u4ea8\u7684\u5e7c\u5f1f\uff0c\u4e49\u624d\u6b66\u7565\uff0c\u51fa\u8272\u5f53\u884c\uff0c\u867d\u7136\u4ed6\u4e0d\u662f\u4e3b\u5e05\uff0c \u540d\u6c14\u8fd8\u5728\u62c5\u4efb\u4e3b\u5e05\u7684\u54e5\u54e5\u4e4b\u4e0a\u3002 \u3000\u3000\u5230\u4e86\u5eb7\u7199\u5341\u4e09\u5e74\uff0c\u5434\u4e09\u6842\u4e3a\u6e05\u5ef7\u6240\u8feb\uff0c\u6025\u56fe\u8c0b\u53cd\u81ea\u6551\uff0c\u8fd9\u65f6\u60f3\u8d77\u4e86\u674e\u81ea\u6210\u7684\u4f59\u90e8\uff0c\u6b63\u662f \u81ea\u5df1\u80cc\u540e\u7684\u4e00\u628a\u5c16\u5200\uff0c\u82e5\u7136\u5f97\u4e0d\u5230\u4ed6\u4eec\u7684\u8c05\u89e3\u5c31\u5192\u6627\u4e3e\u5175\uff0c\u4ed6\u4eec\u81ea\u5c71\u533a\u4e00\u51fa\uff0c\u81ea\u5df1\u5c31\u5c06\u80cc\u8179 \u53d7\u654c\uff0c\u56e0\u6b64\u6781\u4e3a\u7126\u8651\u3002 \u3000\u3000\u201c\u5c71\u96e8\u6b32\u6765\u98ce\u6ee1\u697c\u201d\uff0c\u8fd9\u65f6\u5149\uff0c\u6606\u660e\u6b63\u5904\u5728\u5927\u98ce\u66b4\u7684\u524d\u5915\uff0c\u6e05\u5ef7\u7684\u4eba\uff0c\u897f\u5357\u5404\u7701\u7763\u629a\u7684 \u4eba\uff0c\u5e73\u5357\u738b\u3001\u9756\u5357\u738b\u7684\u4f7f\u8005\uff0c\u674e\u6765\u4ea8\u7684\u90e8\u5c5e\uff0c\u5404\u65b9\u7684\u4eba\u90fd\u5728\u6606\u660e\u52fe\u5fc3\u6597\u89d2\u5730\u6d3b\u52a8\u3002\u5434\u4e09\u6842\u82e6 \u601d\u65e0\u6c41\uff0c\u6700\u540e\u542c\u4e86\u4e00\u4e2a\u8c0b\u58eb\u4e4b\u8a00\uff0c\u539a\u7740\u9762\u76ae\uff0c\u9063\u4f7f\u8005\u5e26\u4fe1\u5230\u5ddd\u6ec7\u8fb9\u533a\uff0c\u81f4\u51fd\u674e\u6765\u4ea8\uff0c\u8981\u6c42\u5f03 \u5acc\u4fee\u597d\u3002\u674e\u6765\u4ea8\u548c\u624b\u4e0b\u5927\u5c06\uff0c\u5bc6\u8bae\u4e09\u65e5\uff0c\u4f17\u8bba\u7eb7\u7eaa\uff0c\u6709\u7684\u8bf4\u5434\u4e09\u6842\u662f\u903c\u6b7b\u201c\u5148\u5e1d\u201d\uff08\u6307\u674e\u81ea \u6210\uff09\u7684\u5927\u4ec7\u4eba\uff0c\u5982\u4f55\u80fd\u591f\u5408\u4f5c\uff1b\u6709\u7684\u8bf4\u4ed6\u65e2\u51b3\u5fc3\u6297\u6e05\uff0c\u5c31\u5927\u53ef\u8054\u5408\u4e00\u81f4\u3002\u6700\u540e\u674e\u601d\u6c38\u4e00\u8a00\u800c \u51b3\uff0c\u63d0\u51fa\u516b\u4e2a\u5927\u5b57\uff1a\u201c\u4ee5\u6211\u4e3a\u4e3b\uff0c\u5148\u5916\u540e\u5185\u3002\u201d\u4e0a\u53e5\u610f\u601d\u662f\u82e5\u548c\u5434\u4e09\u6842\u8054\u5408\u884c\u52a8\uff0c\u5fc5\u987b\u81ea\u5df1 \u8fd9\u8fb9\u63e1\u7740\u4e3b\u52a8\u7684\u5927\u6743\uff1b\u4e0b\u53e5\u610f\u601d\u662f\uff0c\u4e3a\u4e86\u5148\u5bf9\u4ed8\u6ee1\u8bf7\uff0c\u4e0d\u59a8\u628a\u5434\u4e09\u6842\u7684\u65e7\u4ec7\u6682\u629b\u5f00\u4e00\u8fb9\u3002\u8ba1 \u7b56\u4e00\u5b9a\uff0c\u674e\u601d\u6c38\u4e0d\u60dc\u4eb2\u8eab\u5192\u9669\uff0c\u5355\u67aa\u5339\u9a6c\uff0c\u524d\u5f80\u6606\u660e\u3002 \u3000\u3000\u4e66\u63a5\u524d\u6587\u3002\u8bdd\u8bf4\u5434\u4e09\u6842\u89c1\u4e86\u674e\u601d\u6c34\uff0c\u6ee1\u9762\u5806\u6b22\uff0c\u8fde\u8fde\u89e3\u91ca\uff0c\u674e\u601d\u6c38\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u738b\u7237\u4e0d \u7528\u591a\u8a00\uff0c\u6211\u4eec\u82e5\u662f\u8bb0\u7740\u524d\u4ec7\uff0c\u4eca\u65e5\u4e5f\u4e0d\u4f1a\u5230\u6b64\u3002\u201d \u3000\u3000\u5434\u4e09\u6842\u62cd\u624b\u4f5c\u5ff5\uff0c\u8fde\u58f0\u8d5e\u9053\uff1a\u201c\u662f\u5440\uff01\u6240\u4ee5\u6211\u4eec\u90fd\u4f69\u670d\u674e\u516c\u5b50\u7684\u5ea6\u91cf\uff01\u4eca\u65e5\u4e4b\u4e8b\uff0c\u8be5\u5148 \u9a71\u9010\u80e1\u864f\u51fa\u5173\u3002\u201d\u51cc\u672a\u98ce\u542c\u4e86\uff0c\u5ffd\u7136\u5531\u8d77\u4e00\u6bb5\u620f\u7684\u9053\u66f0\uff1a\u201c\u8fd9\u53eb\u505a\u2014\u2014\u89e3\u94c3\u8fd8\u987b\u7cfb\u94c3\u4eba\uff0c\u6210 \u4e5f\u8427\u4f55\uff0c\u8d25\u4e5f\u8427\u4f55\u3002\u201d\u610f\u601d\u5341\u5206\u660e\u663e\uff0c\u8ba5\u7b11\u65e7\u65e5\u5f15\u6e05\u5175\u5165\u5173\u7684\u662f\u5434\u4e09\u6842\uff0c\u73b0\u5728\u8981\u9a71\u9010\u6e05\u5175\u51fa \u5173\u53c8\u662f\u5434\u4e09\u6302\u3002 \u3000\u3000\u4fdd\u67f1\u53cc\u65e5\u55b7\u706b\uff0c\u6309\u637a\u4e0d\u4f4f\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u8bf4\u4ec0\u4e48\uff1f\u201d\u51cc\u672a\u98ce\u563b\u563b\u7b11\u9053\uff1a\u201c\u65e0\u804a\u5f97 \u7d27\uff0c\u5531\u5531\u66f2\u513f\u3002\u201d\u5434\u4e09\u6842\u6015\u4e8b\u60c5\u5f04\u50f5\uff0c\u5e72\u7b11\u51e0\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u58ee\u58eb\u771f\u597d\u95f2\u60c5\uff0c\u4e0d\u8fc7\u54b1\u4eec\u8fd8\u662f \u5148\u8c08\u8c08\u6b63\u4e8b\u3002\u201d\u63a5\u7740\u4ed6\u5c31\u8bf4\u51fa\u4e00\u5927\u5806\u7763\u629a\u671d\u540d\u5b57\uff0c\u5e76\u9053\uff1a\u201c\u5e73\u5357\u738b\u5c1a\u53ef\u559c\u548c\u9756\u5357\u738b\u803f\u7cbe\u5fe0\u4e5f \u5c06\u5728\u5357\u65b9\u54cd\u5e94\uff0c\u6211\u770b\u9664\u975e\u4e49\u65d7\u4e0d\u4e3e\uff0c\u4e00\u4e3e\u5927\u4e8b\u5fc5\u6210\u3002\u558f\uff0c\u8fd9\u4f4d\u5c31\u662f\u5e73\u5357\u738b\u7684\u4f7f\u8005\u3002\u201d\u8bf4\u7740\u6307 \u4e86\u4e00\u6307\u91d1\u5d16\uff0c\u91d1\u5d16\u53d7\u5ba0\u82e5\u60ca\uff0c\u8eac\u8170\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u90fd\u552f\u5e73\u897f\u738b\u7684\u9a6c\u9996\u662f\u77bb\u3002\u201d\u5434\u4e09\u6842\u77aa\u4e86\u4ed6\u4e00 \u773c\u9053\uff1a\u201c\u4ee5\u540e\u522b\u518d\u79f0\u6211\u5e73\u897f\u738b\u4e86\uff0c\u6211\u73b0\u5728\u7684\u5b98\u8854\u662f\u5929\u4e0b\u6c34\u9646\u5927\u5143\u5e05\uff0c\u5174\u660e\u8ba8\u864f\u5927\u5c06\u519b\uff01\u201d\u8bf4 \u7f62\u53c8\u6362\u8fc7\u7b11\u8138\u5bf9\u674e\u601d\u6c38\u9053\uff1a\u201c\u8d24\u6606\u4ef2\u4e00\u5411\u4ee5\u8ba8\u864f\u4e3a\u5df1\u4efb\uff0c\u8fd9\u56de\u8be5\u6ca1\u7b2c\u4e8c\u53e5\u7f57\uff01\u201d \u3000\u3000\u674e\u601d\u6c38\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u2018\u4e49\u65d7\u2019\u8bf4\u5f97\u5012\u5bb9\u6613\uff0c\u53ea\u662f\u8fd9\u6a84\u6587\u53ef\u5f88\u96be\u4e0b\u7b14\u5440\uff01\u201d\u51cc\u672a\u98ce\u7a81\u7136\u53c8 \u63d2\u53e3\u9053\uff1a\u201c\u6562\u95ee\u8fd9\u2018\u5929\u4e0b\u6c34\u9646\u5927\u5143\u5e05\uff0c\u5174\u660e\u8ba8\u864f\u5927\u5c06\u519b\u2019\uff0c\u662f\u8c01\u5c01\u7684\uff1f\u82e5\u6709\u4eba\u95ee\u8d77\u6c38\u660e\u738b\u7684 \u4e0b\u573a\uff0c\u5927\u5c06\u519b\u53c8\u8be5\u5982\u4f55\u5bf9\u7b54\uff1f\u201d\u6c38\u660e\u738b\u662f\u660e\u671d\u7684\u5b97\u5ba4\uff0c\u4e5f\u662f\u5357\u660e\u6297\u6e05\u7684\u6700\u540e\u4e00\u652f\uff0c\u6c38\u660e\u738b\u662f \u5434\u4e09\u6842\u4eb2\u81ea\u8ffd\u5230\u7f05\u7538\uff0c\u6349\u6765\u7ede\u6740\u7684\u3002\u51cc\u672a\u98ce\u8fd9\u4e00\u5f53\u9762\u5632\u9a82\uff0c\u5434\u4e09\u6842\u5c1a\u672a\u4f5c\u58f0\uff0c\u4fdd\u67f1\u5df2\u500f\u5730\u62d4 \u51fa\u5251\u6765\uff0c\u9694\u5ea7\u523a\u53bb\uff0c\u674e\u601d\u6c38\u7ad9\u8d77\u8896\u5b50\u4e00\u62c2\uff0c\u62e6\u5728\u4e24\u4eba\u4e2d\u95f4\u3002\u5434\u4e09\u6842\u5927\u53eb\u201c\u4f4f\u624b\uff01\u201d\u4fdd\u67f1\u6da8\u7ea2 \u4e86\u9762\uff0c\u786c\u5c06\u523a\u51fa\u7684\u5251\u64a4\u56de\uff0c\u4ecd\u662f\u6012\u76ee\u800c\u89c6\u3002 \u3000\u3000\u674e\u601d\u6c38\u53cc\u624b\u636e\u684c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5927\u5c06\u519b\u6682\u8bf7\u606f\u6012\uff0c\u51cc\u5927\u4fa0\u6240\u8a00\u867d\u7136\u5192\u72af\u864e\u5a01\uff0c\u5374\u4e5f\u4e0d\u65e0 \u9053\u7406\uff01\u201d\u5434\u4e09\u6842\u51dd\u5750\u4e0d\u52a8\uff0c\u9634\u9634\u6c89\u6c89\u5730\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u9053\u7406\uff1f\u613f\u89c1\u6559\u4e8e\u9ad8\u660e\uff01\u201d \u3000\u3000\u674e\u601d\u6c38\u9053\uff1a\u201c\u5927\u5c06\u519b\u65e2\u613f\u5766\u8bda\u76f8\u89c1\uff0c\u5fc5\u4e0d\u4ee5\u76f4\u8a00\u4e3a\u7f6a\uff0c\u4ee5\u5927\u5c06\u519b\u7684\u8eab\u4efd\uff0c\u4eca\u65e5\u82e5\u4ecd\u4ee5\u53cd \u6e05\u590d\u660e\u4e3a\u53f7\u53ec\uff0c\u6050\u5927\u6709\u672a\u4fbf\u3002\u540d\u4e0d\u6b63\u5219\u8a00\u4e0d\u987a\uff0c\u660e\u671d\u65ad\u9001\u5728\u5c06\u519b\u8eab\u4e0a\uff0c\u5929\u4e0b\u5171\u77e5\uff0c\u4eca\u65e5\u5c06\u519b \u81ea\u79f0\u2018\u5174\u660e\u706d\u864f\u2019\u6050\u767e\u59d3\u96be\u4ee5\u4fe1\u670d\uff01\u201d \u3000\u3000\u5434\u4e09\u6842\u5c34\u5c2c\u4e4b\u6781\uff0c\u6ee1\u809a\u6012\u706b\uff0c\u5374\u53c8\u4e0d\u4fbf\u53d1\u4f5c\u51fa\u6765\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u5f3a\u5fcd\u95ee\u9053\uff1a\u201c\u7136\u5219\u516c\u5b50\u53c8 \u6709\u4f55\u9ad8\u89c1\uff1f\u201d\u674e\u601d\u6c38\u5766\u7136\u8bf4\u9053\uff1a\u201c\u4e0e\u5176\u7528\u2018\u53cd\u6e05\u590d\u660e\u2019\uff0c\u4e0d\u5982\u7528\u2018\u9a71\u864f\u5174\u6c49\u2019\uff0c\u800c\u4e14\u4ee5\u5927\u5c06 \u519b\u540d\u4e49\u662d\u544a\u56db\u65b9\uff0c\u4e0d\u5982\u7531\u5bb6\u5144\u51fa\u9762\u3002\u201d\u4fdd\u67f1\u6012\u95ee\u9053\uff1a\u201c\u539f\u6765\u8bf4\u6765\u8bf4\u53bb\uff0c\u5374\u662f\u4f60\u4eec\u60f3\u81ea\u5df1\u4f5c \u4e3b\u3002\u53eb\u6211\u4eec\u66ff\u4f60\u4eec\u6253\u6c5f\u5c71\uff01\u201d\u674e\u601d\u6c38\u6124\u7136\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u77e5\u62e9\u4e8e\u5929\u4e0b\u6709\u5229\u8005\u800c\u4e3a\uff0c\u53ea\u6c42\u80fd\u9a71\u9664 \u80e1\u864f\uff0c\u5e76\u4e0d\u8ba1\u8f83\u5176\u4ed6\uff0c\u4e5f\u4e0d\u907f\u5acc\u9000\u8ba9\uff01\u201d \u3000\u3000\u5434\u4e09\u6842\u62c2\u8896\u800c\u8d77\uff0c\u5e72\u7b11\u51e0\u58f0\u8bf4\u9053\uff1a\u201c\u674e\u516c\u5b50\u786e\u662f\u76f4\u723d\u7537\u513f\uff0c\u4f46\u6b64\u4e8b\u4e00\u65f6\u96be\u51b3\uff0c\u5bb9\u6539\u65e5\u518d \u8bae\u5982\u4f55\uff1f\u4fdd\u67f1\uff0c\u4f60\u66ff\u6211\u9001\u5ba2\uff01\u201d\u7ed9\u4fdd\u67f1\u6253\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u4fbf\u5373\u5e26\u9886\u4e24\u65c1\u6587\u6b66\u79bb\u5f00\u3002 \u3000\u3000\u4fdd\u67f1\u5fc3\u9886\u795e\u4f1a\uff0c\u7aef\u8336\u9001\u5ba2\uff0c\u6b64\u65f6\u5927\u5802\u4e0a\u9664\u674e\u601d\u6c38\u3001\u5218\u90c1\u82b3\u3001\u51cc\u672a\u98ce\u4e09\u4eba\u5916\uff0c\u4fbf\u53ea\u6709\u4fdd\u67f1 \u4e00\u4eba\u3002\u4fdd\u67f1\u7aef\u8d77\u8336\u676f\uff0c\u5374\u53ea\u662f\u4f5c\u51fa\u9001\u5ba2\u7684\u59ff\u6001\uff0c\u5e76\u4e0d\u966a\u4ed6\u4eec\u5916\u51fa\uff0c\u4e5f\u6ca1\u53eb\u4eba\u5e26\u8def\u3002\u674e\u601d\u6c38\u53ea \u9053\u662f\u5f7c\u6b64\u8a00\u8bdd\u51b2\u649e\uff0c\u6240\u4ee5\u4ed6\u4eec\u6545\u610f\u51b7\u6de1\uff0c\u5fc3\u4e2d\u6697\u7b11\u5434\u4e09\u6842\u91cf\u6d45\uff1b\u51cc\u672a\u98ce\u8001\u4e8e\u6c5f\u6e56\uff0c\u5374\u662f\u6ee1\u8179 \u72d0\u7591\u3002\u4ed6\u8d70\u4e86\u5341\u4f59\u6b65\uff0c\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4fdd\u67f1\u4e00\u8138\u72de\u7b11\uff0c\u51cc\u672a\u98ce\u5927\u53eb\uff1a\u201c\u674e\u516c\u5b50\u7559\u795e\uff01\u201d\u4fdd\u67f1 \u5df2\u5728\u5899\u58c1\u4e0a\u4e00\u6309\uff0c\u84e6\u7136\u95f4\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u5927\u5802\u4e2d\u592e\u7684\u5730\u9762\uff0c\u7a81\u7136\u4e0b\u9677\uff0c\u51cc\u672a\u98ce\u65bd\u5c55\u7edd\u9876\u8f7b \u529f\uff0c\u8eab\u5b50\u4e00\u5f13\uff0c\u7bad\u4e00\u822c\u671d\u4fdd\u67f1\u51b2\u53bb\uff0c\u4fdd\u67f1\u53cc\u8896\u4e00\u626c\uff0c\u6253\u51fa\u4e00\u5957\u91d1\u676f\uff0c\u51cc\u672a\u98ce\u534a\u7a7a\u4e2d\u8eab\u5b50\u8737 \u66f2\uff0c\u4e00\u4e2a\u5012\u7ffb\uff0c\u907f\u8fc7\u91d1\u676f\uff0c\u50cf\u5927\u9e70\u6251\u4e0b\uff0c\u671d\u4fdd\u67f1\u4fbf\u6293\u3002\u4ed6\u6765\u5f97\u75be\u5982\u95ea\u7535\uff0c\u4fdd\u67f1\u521a\u81ea\u4e00\u6014\uff0c\u5df2 \u7ed9\u4ed6\u51b2\u5230\u9762\u524d\u3002\u4fdd\u67f1\u6025\u5f97\u53cc\u62f3\u5982\u98ce\u6253\u51fa\u3002\u51cc\u672a\u98ce\u4e0d\u95ea\u4e0d\u8eb2\uff0c\u4e00\u628a\u5c06\u4ed6\u62b1\u4f4f\uff0c\u4e24\u4eba\u4e00\u540c\u8dcc\u4e0b\u5730 \u7262\u3002 \u3000\u3000\u5730\u7262\u91cc\u9ed1\u6c89\u6c89\u7684\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\uff0c\u51cc\u672a\u98ce\u4e00\u5f85\u811a\u8e0f\u5b9e\u5730\uff0c\u7acb\u523b\u56b7\u9053\uff1a\u201c\u5218\u5927\u59d0\uff0c\u4f60\u4eec\u90fd\u5728 \u8fd9\u91cc\u5417\uff1f\u201d\u89d2\u843d\u91cc\u6709\u4e00\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\u7b54\u9053\uff1a\u201c\u662f\u51cc\u5927\u54e5\u5417\uff01\u6211\u4eec\u90fd\u5728\u8fd9\u91cc\u3002\u201d\u51cc\u672a\u98ce\u653e\u5f00\u4fdd \u67f1\uff0c\u5faa\u58f0\u627e\u53bb\u3002\u54ea\u77e5\u4fdd\u67f1\u4e00\u8131\u8eab\uff0c\u5288\u9762\u53c8\u662f\u4e00\u62f3\uff0c\u51cc\u672a\u98ce\u594b\u529b\u683c\u5f00\uff0c\u559d\u9053\uff1a\u201c\u4f60\u60f3\u627e\u6b7b\uff1f\u201d \u4fdd\u67f1\u6c14\u547c\u547c\u7684\u4e00\u8a00\u4e0d\u53d1\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u6253\u51fa\u4e03\u516b\u62f3\u3002 \u3000\u3000\u51cc\u672a\u98ce\u521a\u624d\u53d7\u4e86\u4fdd\u67f1\u51e0\u62f3\u9887\u611f\u75bc\u75db\uff0c\u77e5\u9053\u6b64\u4eba\u529f\u529b\uff0c\u4e0d\u80fd\u5c0f\u89c6\uff0c\u5982\u4f55\u80fd\u8ba9\u4ed6\u518d\u5ea6\u6253\u4e2d\uff0c \u9ed1\u6697\u4e2d\u5c55\u5f00\u516b\u5366\u6e38\u8eab\u638c\u6cd5\uff0c\u7ed5\u7740\u4fdd\u67f1\uff0c\u4e58\u9699\u8fdb\u51fb\uff0c\u90a3\u4fdd\u67f1\u4e5f\u715e\u662f\u4e86\u5f97\uff0c\u542c\u98ce\u8fa8\u5f62\uff0c\u62f3\u52bf\u4e1d\u6beb \u4e0d\u7f13\uff0c\u6bcf\u4e00\u62f3\u90fd\u662f\u6253\u5411\u51cc\u672a\u98ce\u7684\u8981\u5bb3\uff0c\u5c31\u50cf\u5468\u8eab\u957f\u7740\u773c\u775b\u4e00\u6837\u3002 \u3000\u3000\u51cc\u672a\u98ce\u77e5\u9053\u4ed6\u6253\u7684\u662f\u5c11\u6797\u7f57\u6c49\u62f3\uff0c\u8bb2\u7a76\u7684\u662f\u52bf\u52b2\u529b\u8db3\uff0c\u62db\u6570\u8fc5\u6377\uff0c\u4e0d\u80fd\u786c\u63a5\u3002\u4ed6\u53f1\u54a4\u4e00 \u58f0\uff0c\u53cc\u638c\u7ffb\u7ffb\u6eda\u6eda\uff0c\u4e13\u4ece\u201c\u7a7a\u95e8\u201d\u8fdb\u6251\uff0c\u628a\u4e00\u53cc\u8089\u638c\uff0c\u5f53\u6210\u4e09\u822c\u5175\u5668\u4f7f\u7528\uff0c\u77f3\u638c\u5288\u6309\u64d2\u62ff\uff0c \u5982\u540c\u4e00\u679d\u4e94\u884c\u5251\uff0c\u5de6\u638c\u638c\u5288\u6307\u6233\uff0c\u5982\u540c\u5355\u5200\u914d\u4e0a\u70b9\u7a74\u6485\u3002\u4fdd\u675c\u5728\u9ed1\u6697\u4e2d\uff0c\u53ea\u89c9\u638c\u98ce\u547c\u547c\uff0c\u51cc \u5389\u4e4b\u6781\uff0c\u800c\u654c\u4eba\u6bcf\u4e00\u62db\u6570\uff0c\u53c8\u90fd\u662f\u5411\u81ea\u5df1\u7a74\u9053\u6253\u6765\uff0c\u4e0d\u7981\u5927\u9a87\uff0c\u5fc3\u60f3\uff0c\u8fd9\u51cc\u672a\u98ce\u679c\u7136\u540d\u4e0d\u865a \u4f20\uff0c\u5728\u9ed1\u6697\u4e4b\u4e2d\uff0c\u8ba4\u7a74\u8fd8\u662f\u5982\u6b64\u6e05\u695a\uff01 \u3000\u3000\u674e\u601d\u6c38\u3001\u5218\u90c1\u82b3\u5728\u6697\u9edd\u91cc\u542c\u6687\u6687\u556a\u556a\u7684\u62f3\u638c\u58f0\uff0c\u6253\u5f97\u5341\u5206\u70ed\u95f9\uff0c\u4e5f\u4e0d\u77e5\u51cc\u672a\u98ce\u548c\u4ec0\u4e48\u4eba \u6253\uff0c\u53ea\u662f\u542c\u5f97\u4e24\u65b9\u7684\u62f3\u58f0\u638c\u58f0\uff0c\u7adf\u4f3c\u529f\u529b\u6089\u654c\u3002 \u3000\u3000\u674e\u601d\u6c38\u9053\uff1a\u201c\u5218\u59d1\u5a18\uff0c\u4f60\u5e26\u6709\u706b\u71a0\u5b50\u5417\uff1f\u201d\u706b\u71a0\u5b50\u662f\u6c5f\u6e56\u4eba\u968f\u8eab\u643a\u5e26\u7684\u7269\u4ef6\u4e4b\u4e00\u3002\u5218\u90c1 \u82b3\u7ed9\u4ed6\u63d0\u9192\uff0c\u5e94\u4e86\u4e00\u58f0\uff0c\u5c06\u968f\u8eab\u706b\u71a0\u5b50\u4eae\u8d77\uff0c\u8d70\u8fd1\u4e00\u770b\uff0c\u51cc\u672a\u98ce\u89c1\u4e86\u706b\u5149\uff0c\u77a7\u89c1\u5218\u90c1\u82b3\u7f13\u7f13 \u5411\u81ea\u5df1\u8d70\u8fd1\uff0c\u594b\u8d77\u795e\u5a01\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u638c\u6309\u6307\u6233\u4e4b\u4e2d\uff0c\u731b\u7684\u98de\u8d77\u4e00\u817f\uff0c\u628a\u4fdd\u67f1\u8e22\u5012\u5730\u4e0a\u3002\u4fdd\u67f1 \u61d2\u9a74\u6253\u6eda\uff0c\u4e00\u7ffb\u8eab\uff0c\u4eae\u51fa\u6298\u94c1\u5200\u4fbf\u65ab\uff0c\u51cc\u672a\u98ce\u638c\u52bf\u4e00\u5f15\uff0c\u53c8\u518d\u8d77\u4e00\u817f\uff0c\u6b63\u8e22\u4e2d\u4fdd\u67f1\u624b\u8155\uff0c\u6298 \u94c1\u5200\u51cc\u7a7a\u98de\u8d77\uff0c\u51cc\u672a\u98ce\u8d76\u4e0a\u4e00\u6b65\uff0c\u556a\u7684\u4e00\u638c\u6253\u5728\u4fdd\u67f1\u80cc\u4e0a\uff0c\u628a\u4fdd\u67f1\u518d\u5ea6\u6253\u7ffb\uff0c\u53f3\u811a\u7167\u8170\u773c\u4e00 \u8e29\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u8fd8\u60f3\u6253\uff1f\u201d\u4fdd\u67f1\u7ed9\u4ed6\u8e29\u7740\u201c\u6d8c\u6cc9\u7a74\u201d\uff0c\u53ea\u89c9\u767e\u9ab8\u6b32\u6563\uff0c\u75db\u5f7b\u5fc3\u813e\uff0c\u5636\u54d1 \u53eb\u9053\uff1a\u201c\u4f60\u628a\u6211\u6740\u4e86\u5427\uff01\u6211\u6b7b\u4e86\uff0c\u4f60\u4eec\u4e5f\u4e0d\u80fd\u6d3b\u3002\u201d\u51cc\u672a\u98ce\u542c\u4e86\u7709\u5934\u4e00\u76b1\uff0c\u628a\u811a\u62bd\u5f00\uff0c\u89c1\u5200 \u628a\u4ed6\u8e22\u8fc7\u89d2\u843d\uff0c\u559d\u9053\uff1a\u201c\u8c01\u8010\u70e6\u6740\u4f60\uff01\u201d\u51cc\u672a\u98ce\u6b63\u5f85\u548c\u5218\u90c1\u82b3\u76f8\u89c1\uff0c\u5ffd\u542c\u5f97\u5468\u56f4\u6709\u6df7\u6dc6\u7684\u6d41 \u6c34\u4e4b\u58f0\u3002 \u3000\u3000\u51cc\u672a\u98ce\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u6c34\u7262\uff01\u201d\u4fdd\u67f1\u8eb2\u5728\u89d2\u843d\u54c8\u54c8\u5927\u7b11\u3002\u674e\u601d\u6c38\u5fc3\u5934\u706b\u8d77\uff0c\u5c06\u4ed6\u4e00\u628a\u63d0 \u8d77\uff0c\u4f38\u51fa\u7a97\u5916\u5728\u6c34\u4e2d\u4e00\u6d78\uff0c\u4fdd\u67f1\u4e00\u5411\u751f\u957f\u5728\u4e91\u8d35\u9ad8\u539f\uff0c\u4ece\u672a\u4e0b\u8fc7\u6c34\uff0c\u7ed9\u8fd9\u4e48\u4e00\u6d78\uff0c\u767b\u65f6\u6740\u732a \u4f3c\u7684\u60ca\u53eb\u8d77\u6765\u3002\u674e\u601d\u6c38\u6d78\u4e86\u51e0\u6d78\uff0c\u518d\u5c06\u4ed6\u63d0\u8d77\uff0c\u7b11\u9053\uff1a\u201c\u770b\u4f60\u8fd8\u56b7\uff1f\u201d\u8fd9\u65f6\u5916\u9762\u6c34\u58f0\u5ffd\u7136\u505c \u6b62\uff0c\u6709\u4eba\u5927\u53eb\u9053\uff1a\u201c\u8bf7\u674e\u516c\u5b50\u7b54\u8bdd\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u4ece\u5218\u90c1\u82b3\u624b\u4e0a\u706b\u6298\u5b50\u6240\u53d1\u51fa\u7684\u706b\u5149\u4e2d\uff0c\u770b\u51fa\u8fd9\u5ea7\u6c34\u7262\u53ea\u662f\u6728\u677f\u780c\u6210\uff0c\u9020\u5f97\u5e76\u4e0d\u575a \u56fa\uff0c\u7a97\u6237\u867d\u7136\u7528\u7cbe\u5927\u7684\u94c1\u679d\u76f8\u95f4\uff0c\u4e5f\u5bb9\u6613\u62d7\u65ad\uff0c\u53ea\u662f\u5c4b\u5b50\u5916\u5168\u662f\u6c34\uff0c\u53ea\u662f\u6df1\u85cf\u5730\u4e0b\uff0c\u5c31\u662f\u6bc1 \u4e86\u8fd9\u5ea7\u5c4b\u5b50\uff0c\u4e5f\u63d2\u7fc5\u96be\u9003\u3002\u4ed6\u6328\u8fd1\u7a97\u6237\uff0c\u6500\u7740\u94c1\u679d\u5927\u58f0\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u5916\u9762\u7684\u4eba\u5012\u5f88\u80fd \u5206\u8fa8\u53e3\u97f3\uff0c\u53c8\u662f\u5927\u58f0\u559d\u9053\uff1a\u201c\u4e0d\u8981\u4f60\u8fd9\u53ae\u63d2\u5634\uff0c\u53eb\u674e\u516c\u5b50\u51fa\u6765\u3002\u201d \u3000\u3000\u674e\u601d\u6c38\u7f13\u7f13\u8d70\u5230\u7a97\u7684\uff0c\u90ce\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u738b\u7237\u60f3\u7684\u597d\u8ba1\u8c0b\uff0c\u53ea\u53ef\u60dc\u4f60\u4eec\u5c31\u5f04\u5f97\u6b7b\u6211\u4eec\u51e0 \u4e2a\u4eba\uff0c\u4e5f\u5f04\u4e0d\u6b7b\u6211\u4eec\u5341\u4e07\u5144\u5f1f\uff01\u201d\u5916\u9762\u7684\u4eba\u58f0\u8c03\u4e00\u53d8\uff0c\u6e29\u8bed\u529d\u9053\uff1a\u201c\u738b\u7237\u5c82\u6562\u6020\u6162\u516c\u5b50\uff0c\u53ea \u662f\u516c\u5b50\u4e5f\u592a\u6267\u62d7\u4e86\uff0c\u738b\u7237\u7684\u610f\u601d\uff0c\u60f3\u516c\u5b50\u4fee\u51fd\u5408\u5144\uff0c\u8bf7\u4ed6\u51fa\u5175\u6e56\u5317\uff0c\u6211\u4eec\u4e24\u5bb6\u4ecd\u7ed3\u76df\u597d\uff01\u516c \u5b50\u5982\u80af\u7b54\u5141\uff0c\u7acb\u523b\u4fbf\u53ef\u51fa\u6765\uff01\u201d\u674e\u601d\u6c38\u77e5\u9053\u4ed6\u4eec\u60f3\u4ee5\u81ea\u5df1\u4f5c\u4eba\u8d28\uff0c\u8ba9\u81ea\u5df1\u8fd9\u4e00\u652f\u519b\u961f\uff0c\u66ff\u4ed6 \u5148\u6253\u786c\u4ed7\uff0c\u597d\u8ba9\u4ed6\u4ece\u4e2d\u53d6\u5229\u3002\u51b7\u51b7\u4e00\u7b11\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u53ef\u4ee5\u8ba8\u4ef7\u8fd8\u4ef7 \u7684\uff1f\u4f60\u4eec\u82e5\u6709\u8bda\u610f\u6297\u6e05\uff0c\u90a3\u5c31\u5f97\u9a6c\u4e0a\u6539\u756a\u53f7\uff0c\u6613\u670d\u9970\uff0c\u5949\u5927\u987a\u6b63\u6714\uff0c\u81f3\u4e8e\u5434\u4e09\u6842\u8fd9\u53ae\uff0c\u7eb5\u4e0d \u81ea\u6740\u4ee5\u8c22\u56fd\u4eba\uff0c\u4e5f\u5f53\u4ea4\u51fa\u5175\u6743\uff0c\u4ece\u6b64\u9000\u4f11\uff01\u201d\u5916\u9762\u7684\u58f0\u97f3\u5bc2\u7136\u4e0d\u54cd\uff0c\u6c34\u58f0\u53c8\u54d7\u5566\u5566\u7684\u54cd\u8d77 \u6765\uff0c\u5feb\u8981\u6d78\u5230\u7a97\u53e3\u4e86\uff0c\u674e\u601d\u6c38\u6070\u7136\u81ea\u82e5\uff0c\u4e0d\u4f4f\u51b7\u7b11\uff0c\u5ffd\u7136\u95f4\u6c34\u58f0\u53c8\u544a\u505c\u6b62\uff0c\u6c34\u7262\u7262\u9876\u5ffd\u7136\u63ed \u4e00\u4e2a\u5927\u6d1e\uff0c\u6709\u4eba\u628a\u4e00\u7bee\u98df\u7269\u540a\u4e0b\u6765\uff0c\u4f20\u58f0\u8bf4\u9053\uff1a\u201c\u8bf7\u674e\u516c\u5b50\u8fdb\u9910\u3002\u201d \u3000\u3000\u5218\u90c1\u82b3\u5bf9\u98df\u7269\u770b\u4e86\u4e00\u773c\uff0c\u4e0d\u6562\u52a8\u624b\u3002\u51cc\u672a\u98ce\u4e00\u628a\u6309\u4e86\u8fc7\u6765\uff0c\u5927\u5403\u5927\u559d\uff0c\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u6b64 \u523b\u8fd8\u4e0d\u6562\u4e0b\u6bd2\uff01\u201d\u8bf4\u7f62\u770b\u4e86\u4fdd\u67f1\u4e00\u773c\uff0c\u5c06\u4e00\u4efd\u98df\u7269\u629b\u8fc7\u53bb\uff0c\u4fdd\u5728\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7aed\u529b\u558a\u9053\uff1a\u201c\u4e0a \u9762\u4e0d\u8981\u518d\u540a\u98df\u7269\u4e0b\u6765\uff0c\u6211\u997f\u5f97\u8d77\uff01\u201d\u674e\u601d\u6c38\u98de\u8d77\u4e00\u811a\uff0c\u628a\u4ed6\u518d\u8e22\u4e00\u4e2a\u7b4b\u6597\uff0c\u4ed6\u8fd8\u662f\u6076\u6bd2\u5730\u7b11 \u7740\u3002\u4fdd\u67f1\u6599\u5b9a\uff0c\u5728\u8fd9\u79cd\u5f62\u52bf\u4e4b\u4e0b\uff0c\u4ed6\u4eec\u4e92\u76f8\u8981\u631f\uff0c\u5434\u4e09\u6842\u4e0d\u6562\u6740\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u6562\u6740\u81ea\u5df1\uff0c \u4e50\u5f97\u5927\u5bb6\u6328\u997f\uff0c\u5230\u997f\u5f97\u614c\u4e86\uff0c\u4e0d\u6015\u4ed6\u4eec\u4e0d\u5c31\u8303\u3002\u800c\u4e14\u4ed6\u7b97\u5b9a\uff0c\u5982\u679c\u5927\u5bb6\u90fd\u997f\u5f97\u6655\u8f6f\u65e0\u529b\uff0c\u5916 \u9762\u7684\u6b66\u58eb\uff0c\u5c31\u6562\u95ef\u8fdb\u6c34\u7262\uff0c\u90a3\u65f6\u81ea\u5df1\u5f53\u7136\u53ef\u4ee5\u9003\u51fa\u4ed6\u4eec\u7684\u638c\u63e1\u3002 \u3000\u3000\u7ecf\u4fdd\u67f1\u8fd9\u6837\u4e00\u56b7\uff0c\u4e0a\u9762\u679c\u7136\u505c\u6b62\u4f9b\u98df\u4e86\u3002\u4e00\u8fde\u8fc7\u4e86\u56db\u5929\uff0c\u5927\u5bb6\u90fd\u5df2\u997f\u5f97\u53d1\u614c\uff0c\u51cc\u672a\u98ce\u5ffd \u7136\u751f\u8d77\u75c5\u6765\uff0c\u5168\u8eab\u75c9\u5b6a\uff0c\u6296\u4e2a\u4e0d\u4f4f\uff0c\u5218\u90c1\u82b3\u4e5f\u865a\u5f31\u65e0\u529b\uff0c\u6162\u6162\u5730\u632a\u8fd1\u4ed6\u7684\u8eab\u8fb9\uff0c\u6267\u7740\u4ed6\u7684 \u624b\uff0c\u51c4\u7136\u5730\u671b\u7740\u4ed6\uff01\u867d\u7136\u662f\u5728\u9ed1\u6697\u7684\u6c34\u7262\uff0c\u51cc\u672a\u51e4\u4e5f\u80fd\u4ece\u5979\u6676\u83b9\u7684\u7738\u5b50\u4e2d\uff0c\u611f\u5230\u4e00\u4efd\u51c4\u51b7\u3002 \u4ed6\u611f\u5230\u5fc3\u7075\u7684\u98a4\u6144\uff0c\u4e0e\u5fc3\u7075\u7684\u75db\u82e6\u6bd4\u8f83\u8d77\u6765\uff0c\u4ed6\u8eab\u4f53\u7684\u75c9\u631b\u771f\u4e0d\u7b97\u5f97\u4ec0\u4e48\u4e00\u56de\u4e8b\u4e86\uff0c\u867d\u7136\u8eab \u4f53\u7684\u75db\u82e6\u4e5f\u5728\u6298\u78e8\u7740\u4ed6\u3002 \u3000\u3000\u5218\u90c1\u82b3\u632a\u6b63\u8eab\u5b50\uff0c\u6267\u7740\u4ed6\u7684\u624b\u95ee\u9053\uff1a\u201c\u672a\u98ce\uff0c\u6211\u4eec\u90fd\u6050\u6015\u4e0d\u80fd\u6d3b\u7740\u8d70\u51fa\u53bb\u4e86\uff01\u7b54\u5e94\u6211\uff0c \u4f60\u80fd\u591f\u544a\u8bc9\u6211\u5b9e\u8bdd\u5417\uff1f\u201d\u51cc\u672a\u98ce\u5c06\u624b\u6323\u8131\u51fa\u6765\uff0c\u53c8\u4e60\u60ef\u5730\u7ede\u626d\u7740\u624b\u6307\uff0c\u559f\u7136\u53f9\u9053\uff1a\u201c\u5982\u679c\u786e \u77e5\u6211\u5c31\u8981\u6b7b\u7684\u8bdd\uff0c\u5728\u4e34\u6b7b\u7684\u6211\u4f1a\u5c06\u4e00\u5207\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u5218\u90c1\u82b3\u5c4f\u606f\u547c\u5438\uff0c\u4e00\u89c1\u4ed6\u7ede\u626d\u7740\u624b\u6307\uff0c\u7a81\u7136\u53c8\u628a\u4ed6\u7684\u53cc\u624b\u63e1\u7740\uff0c\u7528\u4e00\u79cd\u7a81\u7136\u7206\u53d1\u7684\u3001\u53c8 \u597d\u50cf\u81ea\u8a00\u81ea\u8bed\u7684\u58f0\u8c03\u8bf4\u9053\uff1a\u201c\u4f60\u751f\u5e73\u66fe\u5e72\u8fc7\u4e00\u4e8c\u5b97\u771f\u6b63\u6b8b\u9177\u7684\u4e8b\u60c5\u5417\uff1f\u5982\u679c\u4f60\u5e72\u8fc7\uff0c\u4f60\u5c31\u77e5 \u9053\u8fd9\u8981\u6bd4\u6b7b\u8fd8\u96be\u53d7\uff01\u6211\u6740\u6b7b\u7684\u90a3\u4e2a\u7ae5\u5e74\u670b\u53cb\uff0c\u5982\u679c\u4ed6\u771f\u7684\u6b7b\u4e86\uff0c\u6211\u4f1a\u9057\u61be\u7ec8\u751f\u3002\u4f46\u5982\u679c\u4ed6\u50cf \u4f60\u90a3\u6837\uff0c\u6ca1\u6709\u6b7b\u53bb\uff0c\u53ea\u662f\u8dd1\u5230\u8fdc\u8fdc\u7684\u5730\u65b9\u53bb\uff0c\u800c\u4ed6\u53c8\u4e00\u751f\u6068\u7740\u6211\uff0c\u90a3\u4e48\u6211\u5c31\u4e0d\u6b62\u662f\u9057\u61be\u800c\u5c06 \u662f\u6bcf\u4e00\u4e2a\u767d\u5929\u548c\u6bcf\u4e00\u4e2a\u9ed1\u591c\uff0c\u90fd\u5904\u5728\u6076\u68a6\u4e2d\uff0c\u5728\u68a6\u4e2d\u5468\u56f4\u90fd\u662f\u9ed1\u6f06\u6f06\u7684\uff0c\u5c31\u50cf\u8fd9\u4e2a\u6c34\u7262\u4e00 \u6837\u2026\u2026\u201d \u3000\u3000\u51cc\u672a\u98ce\u75db\u82e6\u5730\u56de\u7b54\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u5df2\u7ecf\u591f\u6b8b\u9177\u4e86\uff01\u6211\u4f46\u613f\u4f60\u90a3\u4f4d\u670b\u53cb\u8fd8\u662f\u6b7b\u53bb\u7684\u597d\uff0c\u6d3b\u7740 \u56de\u6765\uff0c\u6050\u6015\u771f\u662f\u66f4\u6b8b\u9177\u7684\u3002\u554a\uff0c\u6211\u4ece\u6765\u6ca1\u6709\u544a\u8bc9\u8fc7\u4f60\u6211\u7684\u7ae5\u5e74\u662f\u600e\u6837\u7684\uff0c\u662f\u5417\uff1f\u6211\u4eec\u73b0\u5728\u90fd \u662f\u5927\u4eba\u5382\uff0c\u6084\u6709\u65f6\u4e5f\u8fd8\u4f1a\u56de\u5fc6\u8d77\u5c0f\u5b69\u5b50\u65f6\u5019\u662f\u600e\u6837\u7684\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u5218\u90c1\u82b3\u7528\u4e00\u79cd\u671f\u5f85\u7684\u773c\u5149\u6402\u7740\u4ed6\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u5427\uff01\u201d\u51cc\u672a\u98ce\u518d\u5ea6\u5c06\u624b\u6323\u8131\u51fa\u6765\uff0c\u53c8 \u7ede\u6254\u7740\u624b\u6307\u8bf4\u9053\uff1a\u201c\u6211\u7684\u6bcd\u4eb2\u5f88\u7231\u6211\uff0c\u4f46\u6709\u65f6\u5979\u4e5f\u5f88\u4e25\u5389\u3002\u6709\u4e00\u6b21\u6709\u4e2a\u5927\u5b69\u5b50\u6b3a\u4fae\u6211\uff0c\u6211\u628a \u4ed6\u6253\u4e86\u4e00\u987f\u3002\u6211\u7684\u6bcd\u4eb2\u8d23\u5907\u6211\uff0c\u6211\u89c9\u5f97\u5f88\u59d4\u5c48\uff0c\u6211\u7a81\u7136\u5077\u5077\u5730\u79bb\u5f00\u4e86\u5bb6\uff0c\u8eba\u5728\u9644\u8fd1\u7684\u5c71\u9876\uff0c \u5728\u90a3\u91cc\u60f3\uff1a\u6bcd\u4eb2\u4e00\u5b9a\u4ee5\u4e3a\u6211\u6b7b\u4e86\uff0c\u8fd9\u65f6\u5019\u5979\u4e00\u5b9a\u5728\u54ed\u6ce3\u4e86\u3002\u8fd9\u6837\u5730\u60f3\u7740\u60f3\u7740\uff0c\u5b69\u5b50\u7684\u5fc3\u597d\u50cf \u662f\u65e2\u611f\u5230\u5feb\u610f\uff0c\u53c8\u611f\u5230\u51c4\u51c9\u2026\u2026\u554a\uff01\u90c1\u82b3\uff0c\u4f60\u5728\u7b11\u8fd8\u662f\u5728\u54ed\u4e86\uff1f\u4f60\u611f\u5230\u8fd9\u4e2a\u5b69\u5b50\u60f3\u6cd5\u5f88\u53ef\u7b11 \u5417\uff1f\u201d \u3000\u3000\u5218\u90c1\u82b3\u54fd\u54bd\u7740\u8bf4\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6298\u78e8\u4f60\u6240\u7231\u7684\u4eba\u5462\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u81ea\u5df1\u4e5f\u4e0d\u77e5 \u9053\uff0c\u6211\u90a3\u65f6\u5927\u7ea6\u662f\u89c9\u5f97\u6bcd\u4eb2\u8fd9\u6837\u7231\u6211\uff0c\u5c31\u4e0d\u8be5\u4e0d\u95ee\u9752\u7ea2\u7682\u767d\u8d23\u5907\u6211\uff0c\u5b69\u5b50\u6c14\u7684\u60f3\u6cd5\u5e38\u5e38\u662f\u8fd9 \u6837\u7684\uff0c\u662f\u5417\uff1f\u201d\u5218\u90c1\u82b3\u547c\u5438\u8feb\u4fc3\uff0c\u7b2c\u4e09\u6b21\u5c06\u4ed6\u7684\u53cc\u624b\u63e1\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u4f60\u73b0\u5728\u4e0d\u662f\u5b69\u5b50 \u4e86\uff01\u201d\u51cc\u672a\u98ce\u5fcd\u53d7\u7740\u75db\u82e6\uff0c\u6545\u610f\u7b11\u51fa\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8bf4\u6211\u4eec\u7684\u4e8b\u3002\u5f53\u7136\u6211\u4e0d\u662f\u4f60\u90a3\u4e2a\u670b\u53cb\u3002 \u4e0d\u8fc7\u6211\u60f3\u4ed6\u4e5f\u8bb8\u6709\u8fc7\u8fd9\u6837\u5b69\u5b50\u6c14\u7684\u60f3\u6cd5\uff0c\u800c\u4e14\u5982\u679c\u4ed6\u50cf\u6211\u90a3\u6837\uff0c\u5f88\u5c0f\u7684\u65f6\u5019\uff0c\u5c31\u8dd1\u5230\u5bd2\u51b7\u7684 \u5f02\u4e61\uff0c\u554a\uff01\u6211\u5fd8\u8bb0\u544a\u8bc9\u4f60\uff0c\u6211\u5e38\u5e38\u7a81\u7136\u53d1\u751f\u75c9\u631b\u75c7\uff0c\u5c31\u662f\u5c0f\u65f6\u5019\u5728\u5bd2\u51b7\u7684\u5f02\u4e61\u9020\u6210\u7684\u3002\u6211\u60f3 \u4f60\u7684\u670b\u53cb\u5982\u679c\u50cf\u6211\u90a3\u6837\uff0c\u5047\u5982\u4ed6\u662f\u6d3b\u7740\u7684\u8bdd\uff0c\u4ed6\u60f3\u8d77\u6765\u4e5f\u8bb8\u4f1a\u53d1\u72c2\u7684\uff01\u201d \u3000\u3000\u5218\u90c1\u82b3\u7a81\u7136\u7d27\u63e1\u4ed6\u7684\u53cc\u624b\uff0c\u4ee5\u5145\u6ee1\u7edd\u671b\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u771f\u7684\u4e00\u70b9\u4e5f\u4e0d\u80fd\u539f\u8c05\u5417\uff1f\u201d\u51cc\u672a \u98ce\u5ffd\u7136\u4f4e\u4f4e\u5730\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u662f\u53ef\u4ee5\u539f\u8c05\u7684\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5148\uff0c\u5ffd\u7136\u6c34\u7262\u4e0a\u9762\u540a\u4e0b\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u674e\u601d\u6c38\u867d\u7136\u997f\u4e86\u51e0\u5929\uff0c\u8fd8\u80fd\u8d70\uff0c\u8fd9\u65f6\u89c1\u4e0a\u9762\u540a\u4e0b\u4e00\u4e2a\u4eba\u6765\u3002\u5fd9\u8fce\u4e0a\u53bb\u95ee\u9053\uff1a\u201c\u4ec0\u4e48 \u4eba\uff1f\u201d\u90a3\u4eba\u62ab\u7740\u4e00\u4ef6\u6597\u7bf7\uff0c\u906e\u8fc7\u5934\u9762\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u7f13\u7f13\u8d70\u6765\u3002\u674e\u601d\u6c38\u7b49\u4ed6\u8d70\u8fd1\u8eab\u8fb9\uff0c\u731b\u5730\u4f38 \u51fa\u5728\u4e4e\uff0c\u4e00\u628a\u62c9\u7740\u6765\u4eba\u8109\u95e8\uff0c\u62c7\u6307\u98df\u6307\u7d27\u6263\u5728\u201c\u5173\u5143\u7a74\u201d\u3002\u674e\u601d\u6c38\u867d\u7136\u4e45\u997f\u4e4b\u540e\uff0c\u6c14\u529b\u4e0d \u4f73\uff0c\u4f46\u70b9\u7a74\u529f\u592b\u5230\u5e95\u8fd8\u5728\uff0c\u201c\u5173\u5143\u7a74\u201d\u53c8\u662f\u4e09\u5341\u516d\u9053\u5927\u7a74\u4e4b\u4e00\uff0c\u8981\u662f\u5e38\u4eba\u88ab\u8fd9\u6837\u4e00\u6263\uff0c\u9a6c\u4e0a \u5c31\u5f97\u8f6f\u762b\u4e0b\u6765\u3002\u53ef\u662f\u6765\u4eba\u53ea\u8f7b\u8f7b\u201c\u54a6\u201d\u4e00\u58f0\uff0c\u674e\u601d\u6c38\u53ea\u89c9\u634f\u7740\u7684\u662f\u4e00\u5806\u68c9\u82b1\uff0c\u8f6f\u7ef5\u7ef5\u7684\u65e0\u4ece \u4f7f\u5200\uff0c\u5fc3\u4e2d\u4eba\u9aa4\uff0c\u8fd9\u6b63\u662f\u5185\u5bb6\u6700\u4e0a\u4e58\u7684\u95ed\u7a74\u529f\u592b\uff0c\u4fbf\u662f\u674e\u601d\u6c38\u4e5f\u53ea\u4e00\u77e5\u534a\u89e3\u3002\u5fc3\u60f3\uff1a\u5982\u4f55\u5434 \u4e09\u6842\u5e9c\u4e2d\uff0c\u7adf\u6709\u5982\u6b64\u4eba\u7269\uff1f \u3000\u3000\u6765\u4eba\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u4e4b\u540e\uff0c\u5ffd\u7136\u51d1\u8fd1\u674e\u601d\u6c38\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u522b\u614c\uff0c\u6211\u7edd\u4e0d\u4f1a\u52a0\u5bb3\u4e8e\u4f60\u3002 \u4f60\u522b\u53eb\u56b7\uff0c\u53ea\u8bf7\u4f60\u6084\u6084\u544a\u8bc9\u6211\uff0c\u6709\u4f4d\u51cc\u672a\u98ce\u662f\u5728\u8fd9\u91cc\uff1f\u201d\u674e\u601d\u6c38\u9762\u7ea2\u8033\u70ed\uff0c\u5fd9\u628a\u634f\u7740\u4ed6\u7684\u624b \u653e\u5f00\uff0c\u5411\u51cc\u672a\u98ce\u8eba\u5904\u6307\u4e86\u4e00\u6307\uff0c\u6765\u4eba\u53cc\u7738\u4e00\u770b\uff0c\u5c31\u5411\u51cc\u672a\u98ce\u8d70\u53bb\u3002 \u3000\u3000\u5218\u90c1\u82b3\u6b63\u81ea\u5fc3\u5982\u9189\uff0c\u6709\u4eba\u8fdb\u6765\uff0c\u5979\u4e5f\u6d51\u5982\u4e0d\u89c9\uff0c\u4ecd\u662f\u7d27\u7d27\u63e1\u7740\u51cc\u672a\u51e4\u7684\u624b\u95ee\u9053\uff1a\u201c\u4f60\u8bf4 \u4ec0\u4e48\uff1f\u518d\u8bf4\u4e00\u904d\u2026\u2026\u4f60\u662f\u4e0d\u662f\u8bf4\u53ef\u4ee5\u539f\u8c05\uff1f\u90a3\u4e48\u4f60\u662f\u2026\u2026\u4f60\u662f\u90a3\u4e2a\u4eba\u5417\uff1f\u201d\u51cc\u672a\u98ce\u7a81\u7136\u6323\u624e \u7740\u53c8\u628a\u624b\u8131\u4e86\u51fa\u6765\uff0c\u63a8\u5f00\u4e86\u5979\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u6709\u4eba\u6765\u4e86\u3002\u201d\u5218\u90c1\u82b3\u8292\u7136\u5750\u5728\u5730\u4e0a\uff0c\u88ab\u51cc\u672a\u98ce \u8fd9\u4e48\u4e00\u63a8\uff0c\u65b9\u59cb\u5982\u68a6\u521d\u9192\uff0c\u4e5f\u4e0d\u77e5\u54ea\u91cc\u6765\u7684\u529b\u6c14\uff0c\u7a81\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u5411\u6765\u4eba\u4e00\u638c\u6253\u53bb\u3002\u6765\u4eba\u8f7b \u8f7b\u4e00\u95ea\uff0c\u5218\u90c1\u82b3\u6536\u52bf\u4e0d\u4f4f\uff0c\u8eab\u5411\u524d\u503e\uff0c\u6765\u4eba\u5c06\u5979\u6276\u4f4f\uff0c\u5728\u5979\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u4f84\u5973\uff0c\u4f60\u9192\u9192\uff01\u662f \u6211\u6765\u4e86\uff01\u6211\u7ed9\u4f60\u6cbb\u75c5\uff01\u201d\u8bf4\u4e86\u4e24\u904d\uff0c\u5218\u90c1\u82b3\u624d\u542c\u51fa\u90a3\u4eba\u7684\u58f0\u97f3\uff0c\u5ffd\u7136\u201c\u54c7\u201d\u7684\u54ed\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6765\u4eba\u6b66\u529f\u6df1\u6e5b\uff0c\u7ec3\u5c31\u4e00\u53cc\u591c\u773c\uff0c\u4ed6\u671d\u5218\u90c1\u82b3\u9762\u4e0a\u4e00\u770b\uff0c\u53c8\u671d\u8eba\u5728\u5730\u4e0a\u7684\u51cc\u672a\u98ce\u4e00\u770b\uff0c\u8f7b \u8f7b\u5730\u62cd\u7740\u5218\u90c1\u82b3\u80a9\u818a\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u5fc3\u6025\uff0c\u6211\u5148\u7ed9\u51cc\u672a\u98ce\u6cbb\u75c5\u3002\u201d\u4ed6\u53ea\u9053\u5218\u90c1\u82b3\u662f\u53d7\u4e0d\u4f4f\u82e6\u695a \u800c\u54ed\u51fa\u58f0\u6765\uff0c\u5374\u4e0d\u77e5\u5979\u53e6\u6709\u5fc3\u75c5\u3002 \u3000\u3000\u63d0\u5230\u51cc\u672a\u98ce\u7684\u75c5\uff0c\u5218\u90c1\u82b3\u5012\u6e05\u9192\u8fc7\u6765\u4e86\uff0c\u54fd\u54bd\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u6211\u4e0d\u8981\u7d27\uff0c\u4f60\u5148\u770b\u770b\u4ed6\u5427\uff0c \u6211\u5e76\u4e0d\u662f\u5fc3\u6025\u2026\u2026\u201d\u5979\u8bf4\u5230\u8fd9\u91cc\u53c8\u8bf4\u4e0d\u4e0b\u53bb\u4e86\uff0c\u6765\u4eba\u975e\u5e38\u60ca\u5f02\u5730\u770b\u4e86\u5979\u4e00\u773c\uff0c\u6447\u4e86\u6447\u5934\uff0c\u5c31 \u8e72\u5728\u5730\u4e0a\uff0c\u66ff\u51cc\u672a\u98ce\u628a\u8109\u3002 \u3000\u3000\u51cc\u672a\u98ce\u8fd9\u65f6\u4e5f\u770b\u51fa\u6765\u4eba\u662f\u8c01\uff0c\u6b63\u60f3\u5f20\u53e3\u62db\u547c\uff0c\u6765\u4eba\u5374\u6446\u4e86\u6446\u624b\uff0c\u793a\u610f\u53eb\u522b\u56b7\u3002\u628a\u8109\u4e4b \u540e\uff0c\u6765\u4eba\u81ea\u6000\u91cc\u53d6\u51fa\u4e00\u652f\u5c3a\u4f59\u957f\u7684\u94f6\u9488\uff0c\u5728\u9ed1\u6697\u4e2d\u95ea\u95ea\u53d1\u5149\u3002\u4ed6\u628a\u51cc\u672a\u98ce\u7684\u5916\u8863\u8131\u6389\uff0c\u5ffd\u7136 \u7528\u9488\u5728\u51cc\u672a\u98ce\u7684\u8eab\u4e0a\u4e71\u523a\u3002\u674e\u601d\u6c38\u89c1\u72b6\u5927\u60ca\uff0c\u6025\u5fd9\u559d\u9053\uff1a\u201c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u6765\u4eba\u53d6\u51fa\u94f6\u9488\uff0c\u89e3 \u6389\u51cc\u672a\u51e4\u5916\u8863\u65f6\uff0c\u5218\u90c1\u82b3\u5df2\u628a\u5934\u522b\u8fc7\u4e00\u8fb9\uff0c\u8fd9\u65f6\u89c1\u674e\u601d\u6c38\u6b32\u4e0a\u524d\u62e6\u963b\uff0c\u6025\u5fd9\u4f38\u624b\u62e6\u9053\uff1a\u201c\u4ed6 \u662f\u66ff\u51cc\u672a\u98ce\u6cbb\u75c5\uff01\u4ed6\u662f\u795e\u533b\uff01\u201d\u674e\u601d\u6c38\u89c1\u94f6\u9488\u523a\u5165\u51cc\u672a\u98ce\u80cc\u810a\uff0c\u51e0\u6ca1\u5165\u4e00\u534a\uff0c\u51cc\u672a\u98ce\u5374\u82e5\u65e0 \u5176\u4e8b\uff0c\u4e00\u58f0\u4e0d\u56b7\uff0c\u8fd9\u624d\u534a\u4fe1\u534a\u7591\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u51cc\u672a\u98ce\u201c\u54df\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u6765\u4eba\u5c06\u94f6\u9488\u62bd\u51fa\uff0c\u7b11\u9053\uff1a\u201c\u597d\u4e86\uff0c\u597d\u4e86\uff01\u201d \u51cc\u672a\u98ce\u970d\u5730\u7ffb\u8eab\u5750\u8d77\uff0c\u7eb3\u5934\u4fbf\u62dc\uff0c\u8d5e\u9053\uff1a\u201c\u9488\u7597\u795e\u6280\uff0c\u540d\u4e0d\u865a\u4f20\uff01\u201d\u674e\u601d\u6c38\u6115\u7136\u56de\u987e\uff0c\u53ea\u89c1 \u4fdd\u67f1\u4e5f\u884c\u4e86\u8fd1\u6765\u3002 \u3000\u3000\u51cc\u672a\u98ce\u89c1\u4fdd\u67f1\u884c\u8fd1\uff0c\u7a81\u7136\u9a88\u6307\u4e00\u70b9\uff0c\u6b63\u6b63\u6233\u4e2d\u4fdd\u67f1\u8170\u95f4\u7684\u660f\u7729\u7a74\uff0c\u4fdd\u67f1\u672a\u53ca\u51fa\u58f0\uff0c\u5df2\u5012 \u5728\u5730\u4e0a\u3002\u6765\u4eba\u5411\u6c34\u7262\u4e0a\u9762\u4e00\u6307\uff0c\u674e\u601d\u6c38\u62ac\u5934\u4e0a\u671b\uff0c\u9690\u7ea6\u53ef\u89c1\u6c34\u7262\u4e0a\u706b\u5149\u95ea\u6620\uff0c\u4eba\u5f71\u7ef0\u7ef0\u3002\u6765 \u4eba\u5ffd\u7136\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u738b\u7237\u597d\u610f\u547d\u6211\u66ff\u4f60\u4eec\u6cbb\u75c5\uff0c\u4e00\u5fc3\u4ecd\u6b32\u7ed3\u76df\uff0c\u516c\u5b50\u4f55\u5fc5\u5982\u6b64\u5f3a \u786c\uff01\u201d\u8bf4\u7f62\u968f\u5373\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u5feb\u5531\u53cc\u7c27\uff01\u201d\u674e\u601d\u6c38\u806a\u660e\u7edd\u9876\uff0c\u5fc3\u9886\u795e\u4f1a\uff0c\u968f\u5373\u5927\u58f0\u559d \u9053\uff1a\u201c\u533b\u8005\u95ed\u53e3\uff01\u6cbb\u75c5\u4e4b\u52b3\uff0c\u7406\u5f53\u611f\u8c22\uff0c\u82e5\u8c08\u5927\u4e8b\uff0c\u5c82\u662f\u4f60\u53ef\u63d2\u8a00\uff01\u201d\u6765\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u53c8\u6545 \u610f\u5927\u58f0\u5520\u53e8\uff0c\u674e\u601d\u6c38\u58f0\u8c03\u8f6c\u6e29\u548c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u613f\u7ed3\u4ea4\u4f60\u8fd9\u6837\u4e00\u4f4d\u670b\u53cb\u4fbf\u662f\u4e86\uff0c\u4f46\u4f60\u82e5\u66ff\u5434\u4e09 \u6842\u8fd9\u53ae\u8bf4\u5ba2\uff0c\u53ef\u662f\u767d\u8d39\u5fc3\u795e\uff01\u201d\u6765\u4eba\u53c8\u91cd\u91cd\u53f9\u4e86\u53e3\u6c14\uff0c\u7275\u52a8\u7ef3\u7d22\uff0c\u6c34\u7262\u4e0a\u7684\u4eba\u53c8\u628a\u4ed6\u540a\u4e0a\u53bb \u4e86\u3002 \u3000\u3000\u51cc\u672a\u98ce\u4e0e\u674e\u601d\u6c38\u76f8\u89c6\u800c\u7b11\uff0c\u968f\u624b\u89e3\u5f00\u4fdd\u67f1\u7684\u7a74\u9053\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u628a\u6211\u4eec\u997f\u6b7b\uff1f\u4f60\u7684\u738b\u7237 \u504f\u504f\u4e0d\u542c\u4f60\u7684\u8bdd\u3002\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u679c\u7136\u4e0a\u9762\u53c8\u628a\u98df\u7269\u540a\u4e0b\u6765\u4e86\uff0c\u674e\u601d\u6c38\u7b49\u5927\u5403\u5927\u559d\uff0c\u5374\u628a\u9aa8\u5934 \u6b8b\u4f59\uff0c\u4e22\u7ed9\u4fdd\u67f1\uff0c\u628a\u4fdd\u67f1\u6c14\u5f97\u8981\u6b7b\uff0c\u767d\u767d\u966a\u4ed6\u4eec\u997f\u4e86\u51e0\u5929\uff0c\u7ed3\u679c\u4e0a\u9762\u53c8\u4e0d\u4f9d\u81ea\u5df1\u539f\u6765\u7684\u8ba1\u7b56 \u884c\u4e8b\u3002 \u3000\u3000\u81ea\u6b64\u4e4b\u540e\uff0c\u90a3\u533b\u751f\u6bcf\u9694\u4e24\u5929\uff0c\u5c31\u4e0b\u6765\u4e00\u6b21\uff0c\u7ed9\u4ed6\u4eec\u56db\u4eba\u90fd\u98df\u4e86\u4e9b\u8865\u4e2d\u76ca\u6c14\u7684\u836f\u8336\uff0c\u6bcf\u6b21 \u4e0b\u6765\uff0c\u90fd\u6545\u610f\u548c\u674e\u601d\u6c38\u7b49\u5927\u58f0\u8bf4\u7b11\uff0c\u5230\u6700\u540e\u4e24\u5929\uff0c\u4e0a\u9762\u7684\u4eba\u5f71\u5df2\u6ca1\u6709\u6700\u521d\u7684\u591a\u4e86\u3002 \u3000\u3000\u5341\u5929\u4e4b\u540e\u51cc\u672a\u98ce\u7b49\u5df2\u5b8c\u5168\u590d\u539f\u3002\u4e00\u65e5\uff0c\u90a3\u533b\u751f\u5ffd\u7136\u98d8\u7136\u800c\u4e0b\uff0c\u4e00\u89c1\u9762\u5c31\u5927\u58f0\u56b7\u9053\uff1a\u201c\u5feb \u968f\u7740\u6211\u8d70\uff01\u201d\u4fdd\u67f1\u60ca\u8be7\u4e4b\u95f4\uff0c\u5df2\u88ab\u4ed6\u4e00\u638c\u51fb\u5012\uff0c\u4ed6\u4f7f\u7684\u662f\u5206\u7b4b\u9519\u9aa8\u624b\u6cd5\uff0c\u628a\u4fdd\u67f1\u5f04\u5f97\u5168\u8eab\u9ebb \u8f6f\uff0c\u8ddf\u7740\u968f\u624b\u5728\u836f\u56ca\u4e2d\u53d6\u51fa\u4e00\u628a\u5315\u9996\uff0c\u5411\u5218\u90c1\u82b3\u9053\uff1a\u201c\u501f\u4f60\u7684\u9526\u4e91\u515c\u4e00\u7528\uff01\u201d\u674e\u601d\u6c38\u77e5\u9053\u7528 \u6025\uff0c\u5c06\u7f20\u5728\u8170\u95f4\u7684\u6d41\u661f\u9524\u89e3\u4e0b\uff0c\u9012\u7ed9\u4ed6\u9053\uff1a\u201c\u8fd9\u4e2a\u6bd4\u9526\u4e91\u515c\u66f4\u5408\u7528\uff01\u201d\u533b\u751f\u8d5e\u9053\uff1a\u201c\u674e\u516c\u5b50 \u771f\u662f\u80fd\u4eba\uff01\u201d\u624b\u4e2d\u5315\u9996\u5411\u4e0a\u4e00\u63b7\uff0c\u63d2\u5728\u5341\u4f59\u4e08\u9ad8\u7684\u77f3\u58c1\u4e0a\uff0c\u7528\u529b\u4e00\u8dc3\uff0c\u5b9b\u5982\u5927\u96c1\u817e\u7a7a\uff0c\u53f3\u638c \u5728\u5315\u9996\u4e0a\u4e00\u6309\uff0c\u5de6\u624b\u4e00\u64a4\uff0c\u6d41\u661f\u9524\u671d\u4e0b\u9762\u4e00\u6643\uff0c\u5218\u90c1\u82b3\u4e00\u8dc3\u6570\u4e08\uff0c\u521a\u521a\u63e1\u7740\u9524\u5934\uff0c\u90a3\u533b\u751f\u7528 \u529b\u4e00\u6325\uff0c\u5218\u90c1\u82b3\u51cc\u7a7a\u98de\u8d77\uff0c\u501f\u7740\u8fd9\u4e00\u6325\u4e00\u9001\u4e4b\u529b\uff0c\u98de\u8eab\u8131\u51fa\u6c34\u7262 \u3000\u3000\u533b\u751f\u8fd9\u624b\u540d\u53eb\u201c\u91d1\u5200\u6362\u638c\u201d\uff0c\u539f\u6765\u81ea\u7262\u5e95\u81f3\u4e0a\u7a7a\u6709\u4e09\u5341\u4f59\u4e08\u9ad8\uff0c\u4ee5\u4ed6\u7684\u529f\u529b\uff0c\u867d\u7136\u4e0d\u85c9 \u5315\u9996\uff0c\u4e5f\u53ef\u5728\u77f3\u58c1\u4e0a\u6362\u638c\u98de\u51fa\uff0c\u4f46\u4ed6\u6599\u5218\u90c1\u82b3\u672a\u5fc5\u6709\u5982\u6b64\u529f\u529b\uff0c\u56e0\u6b64\u624d\u7528\u5315\u9996\u6765\u652f\u6301\u8eab\u4f53\u7684 \u91cd\u91cf\uff0c\u4ee5\u7edd\u9876\u8f7b\u529f\uff0c\u5c06\u5218\u90c1\u82b3\u9001\u51fa\u6c34\u7262\u3002\u8ddf\u7740\u674e\u601d\u6c38\u4e5f\u4ee5\u540c\u6837\u65b9\u6cd5\u98de\u51fa\u3002\u7b2c\u4e09\u4e2a\u8f6e\u5230\u51cc\u672a \u98ce\uff0c\u4ed6\u628a\u4fdd\u5f80\u5939\u5728\u80c1\u4e0b\uff0c\u4e0d\u63a5\u98de\u9524\uff0c\u5e73\u5730\u62d4\u8d77\uff0c\u8dc3\u5230\u5341\u4f59\u4e08\u9ad8\u4e4b\u5904\uff0c\u7528\u8db3\u5c16\u4e00\u70b9\u77f3\u58c1\uff0c\u6362\u52bf \u518d\u8d77\uff0c\u90a3\u533b\u751f\u8d5e\u9053\uff1a\u201c\u597d\u8f7b\u529f\uff01\u201d\u6536\u8d77\u98de\u9524\uff0c\u968f\u540c\u4ed6\u4e00\u540c\u8dc3\u51fa\uff01 \u3000\u3000\u51fa\u4e86\u6c34\u7262\uff0c\u53ea\u89c1\u5730\u4e0a\u6a2a\u4e03\u7ad6\u516b\u5730\u8eba\u770b\u4e94\u516d\u4e2a\u6b66\u58eb\uff0c\u4e0d\u95ee\u800c\u77e5\u662f\u8fd9\u602a\u533b\u751f\u7528\u91cd\u624b\u6cd5\u70b9\u5012\u7684 \u4e86\u3002\u53ea\u662f\u521a\u624d\u5728\u6c34\u7262\u4e0b\u4e1d\u6beb\u4e0d\u95fb\u6253\u6597\u4e4b\u58f0\uff0c\u53ef\u4ee5\u60f3\u89c1\u4ed6\u52a8\u624b\u7684\u8fc5\u901f\u3002\u7528\u91cd\u624b\u6cd5\u70b9\u7a74\u4e0d\u96be\uff0c\u96be \u5728\u4ed6\u4fc4\u9877\u4e4b\u95f4\uff0c\u5c06\u8fd9\u4e9b\u4eba\u5b8c\u5168\u5236\u670d\u3002 \u3000\u3000\u674e\u601d\u6c38\u597d\u751f\u656c\u4f69\uff0c\u4ee5\u524d\u5728\u6c34\u7262\u4e2d\u770b\u4e0d\u6e05\u695a\uff0c\u73b0\u5728\u5149\u4eae\u4e4b\u5904\uff0c\u53ea\u89c1\u8fd9\u533b\u751f\u7ae5\u989c\u767d\u53d1\uff0c\u957f\u987b \u4e09\u7ecd\uff0c\u98d8\u98d8\u82e5\u4ed9\u3002\u674e\u601d\u6c38\u6b63\u6b32\u8bf7\u95ee\u59d3\u540d\uff0c\u5218\u90c1\u82b3\u5df2\u7b11\u9053\uff1a\u201c\u4ee5\u524d\u5728\u6c34\u7262\u4e2d\u4e0d\u4fbf\u8bf4\u7ed9\u4f60\u77e5\uff0c\u4ed6 \u5c31\u662f\u6211\u7684\u5e08\u53d4\u5085\u9752\u4e3b\u5148\u751f\uff01\u674e\u601d\u6c38\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u6b23\u7136\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u7ec8\u5357\u6d3e\u8001\u524d\u8f88\uff0c\u602a\u4e0d \u5f97\u6b66\u529f\u5982\u6b64\u7cbe\u7eaf\uff01\u201d\u6b63\u5f85\u65bd\u793c\uff0c\u5085\u9752\u4e3b\u4e00\u628a\u5c06\u4ed6\u62c9\u4f4f\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u4e0d\u662f\u53d9\u8bdd\u4e4b\u5730\u3002\u5feb \u968f\u6211\u8d70\uff01\u201d \u3000\u3000\u5085\u9752\u4e3b\u5bf9\u4e8e\u738b\u5e9c\u7684\u9053\u8def\u4f3c\u4e4e\u5f88\u719f\uff0c\u5e26\u9886\u4f17\u4eba\uff0c\u4e0a\u4e86\u74e6\u9762\uff0c\u76f4\u5411\u540e\u56ed\u5954\u53bb\u3002\u6b63\u5954\u8dd1\u95f4\uff0c\u51cc \u672a\u98ce\u631f\u7740\u7684\u4fdd\u67f1\u5ffd\u7136\u5927\u559d\u4e00\u58f0\uff1a\u201c\u5b69\u513f\u4eec\u8fd8\u4e0d\u51fa\u672a\uff01\u201d\u731b\u7136\u95f4\uff0c\u6b63\u9762\u6697\u5668\u5982\u98de\u879f\u822c\u6253\u4e0a\uff0c\u51cc \u672a\u98ce\u6012\u559d\u4e00\u58f0\uff1a\u201c\u4f60\u627e\u6b7b\uff01\u201d\u53f3\u81c2\u7528\u529b\u4e00\u631f\uff0c\u4fdd\u67f1\u767b\u65f6\u75db\u5f97\u6655\u4e86\u8fc7\u53bb\u3002\u4ed6\u6e38\u9f99\u5251\u65e9\u5df2\u51fa\u624b\uff0c \u5de6\u81c2\u4e00\u62a1\uff0c\u821e\u8d77\u4e00\u5708\u6e05\u5149\uff0c\u628a\u90a3\u4e9b\u6697\u5668\u78b0\u5f97\u6ee1\u7a7a\u4e71\u98de\uff0c\u5982\u540c\u6d12\u4e0b\u4e86\u5927\u82b1\u96e8\u3002\u4e0b\u9762\u7684\u6697\u5668\u8fd8\u662f \u4e0d\u65ad\u6253\u672a\uff0c\u8fd9\u65f6\u674e\u601d\u6c38\u5df2\u821e\u8d77\u6d41\u661f\u9524\uff0c\u90a3\u4e9b\u94a2\u9556\u85fb\u8638\u4e4b\u7c7b\u8f83\u6709\u4efd\u91cf\u7684\u6697\u5668\uff0c\u7ed9\u98de\u9524\u78b0\u7740\uff0c\u53d1 \u51fa\u4e00\u6e9c\u6e9c\u706b\u82b1\uff0c\u5728\u9ad8\u7a7a\u6fc0\u8361\uff01\u5341\u5206\u597d\u770b\uff0c\u5085\u9752\u4e3b\u5e94\u4ed8\u6697\u5668\u7684\u65b9\u6cd5\u66f4\u662f\u7279\u522b\uff0c\u53ea\u89c1\u4ed6\u6325\u52a8\u53cc \u8896\uff0c\u6216\u62c2\u6216\u63a5\uff0c\u4efb\u662f\u6697\u5668\u7eb7\u7eb7\u6512\u51fb\uff0c\u4e5f\u5948\u4f55\u4ed6\u4e0d\u5f97\u3002 \u3000\u3000\u51cc\u672a\u98ce\u8d81\u674e\u5085\u4e8c\u4eba\u78b0\u63a5\u6697\u5668\u4e4b\u9645\uff0c\u5b9d\u5251\u5165\u9798\uff0c\u968f\u624b\u63a2\u51fa\u51e0\u679d\u98de\u8292\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6765\u800c\u4e0d \u5f80\u975e\u793c\u4e5f\uff01\u201d\u53cc\u624b\u4e00\u626c\uff0c\u51e0\u9053\u4e4c\u91d1\u5149\u8292\uff0c\u7535\u5c04\u800c\u51fa\uff0c\u4e0b\u9762\u8fde\u58f0\u60e8\u53eb\uff0c\u51e0\u4e2a\u6b66\u571f\u7ed9\u98de\u8292\u5bf9\u80f8\u7a7f \u8fc7\uff0c\u767b\u65f6\u4e86\u7ed3\u3002\u4e00\u9635\u5927\u4e71\uff0c\u5085\u9752\u4e3b\u5df2\u7387\u4f17\u8d8a\u8fc7\u51e0\u91cd\u74e6\u9762\uff0c\u76f4\u5954\u540e\u56ed\u3002 \u3000\u3000\u8fd9\u65f6\u4fdd\u67f1\u5df1\u60a0\u60a0\u9192\u8f6c\uff0c\u674e\u601d\u6c38\u5728\u540e\u9762\uff0c\u89c1\u4ed6\u867d\u7136\u88ab\u51cc\u672a\u98ce\u7528\u529b\u631f\u7740\uff0c\u5374\u662f\u4e00\u9762\u72de\u7b11\u3002\u5fc3 \u5ff5\u4e00\u52a8\uff0c\u5ffd\u89c1\u524d\u9762\u547c\u7684\u4e00\u58f0\uff0c\u4e00\u80a1\u70c8\u7130\uff0c\u8fce\u9762\u55b7\u6765\uff0c\u4f17\u4eba\u77e5\u9053\u8fd9\u79cd\u786b\u78fa\u706b\u7130\u5341\u5206\u5389\u5bb3\uff0c\u6025\u5fd9 \u56db\u4e0b\u8d70\u907f\uff0c\u731b\u7136\u95f4\u524d\u540e\u5de6\u77f3\u90fd\u5c04\u51fa\u8fd9\u79cd\u706b\u7130\uff0c\u800c\u4e14\u90fd\u662f\u5411\u51cc\u672a\u98ce\u626b\u6765\uff0c\u5b9b\u5982\u51e0\u9053\u706b\u9f99\uff0c\u8981\u5c06 \u51cc\u672a\u98ce\u541e\u566c\u3002\u51cc\u672a\u98ce\u6012\u543c\u4e00\u58f0\uff0c\u98de\u8eab\u4e00\u6643\uff1a\u201c\u4e00\u9e64\u51b2\u5929\u201d\uff0c\u5728\u706b\u5149\u4e2d\u51cc\u7a7a\u800c\u8d77\uff0c\u6251\u4e0b\u82b1\u56ed\uff0c \u5728\u5730\u9762\u4e0a\u548c\u8eab\u4e00\u6eda\uff0c\u5c06\u8eab\u4e0a\u706b\u661f\u6251\u706d\uff0c\u800c\u4fdd\u67f1\u4e5f\u7ed9\u6454\u51fa\u51e0\u4e08\u4e4b\u5916\uff0c\u5934\u9762\u90fd\u7ed9\u706b\u7130\u707c\u4f24\u3002\u4ed6\u4e00 \u8131\u51fa\u51cc\u672a\u98ce\u638c\u63e1\uff0c\u7acb\u523b\u4ece\u6b66\u58eb\u624b\u4e2d\uff0c\u593a\u8fc7\u4e00\u6761\u6746\u68d2\uff0c\u50cf\u53d1\u72c2\u7684\u72ee\u5b50\u4e00\u6837\uff0c\u7387\u9886\u6b66\u58eb\u4e0a\u524d\u5305 \u56f4\uff0c\u771f\u662f\u540d\u4e0d\u865a\u4f20\u7684\u4e00\u5458\u608d\u5c06\u3002 \u3000\u3000\u5085\u9752\u4e3b\u7b49\u4eba\u7d27\u8ddf\u7740\u51cc\u672a\u98ce\u8dc3\u4e0b\u82b1\u56ed\uff0c\u53ea\u89c1\u82b1\u56ed\u91cc\u5f71\u5f71\u7ef0\u7ef0\u7684\u56db\u9762\u662f\u4eba\uff0c\u5f53\u524d\u7684\u5341\u51e0\u4e2a\u6b66 \u4e0a\u4e0b\u6301\u55b7\u706b\u7b52\uff0c\u4ea4\u53c9\u626b\u5c04\uff0c\u706b\u7130\u5230\u5904\uff0c\u6811\u6728\u82b1\u8349\uff0c\u90fd\u718a\u718a\u5730\u711a\u70e7\u8d77\u6765\uff0c\u51cc\u672a\u98ce\u7b49\u56db\u4eba\u65bd\u5c55\u7edd \u9876\u8f7b\u529f\uff0c\u5728\u706b\u5149\u4e2d\u7a9c\u6765\u7a9c\u53bb\uff0c\u8fd8\u8981\u5bf9\u4ed8\u968f\u7740\u4eba\u7130\u5c04\u51fa\u7684\u5404\u79cd\u6697\u5668\uff0c\u5f62\u52bf\u786e\u662f\u5341\u5206\u5371\u9669\uff01 \u3000\u3000\u5728\u738b\u5e9c\u6b66\u58eb\u4eec\u786b\u78fa\u55b7\u706b\u7b52\u4e71\u626b\u4e4b\u4e0b\uff0c\u51cc\u672a\u98ce\u7b49\u56db\u4eba\u95f9\u5f97\u4e2a\u9996\u5c3e\u4e0d\u80fd\u517c\u987e\uff0c\u5404\u81ea\u5206\u5f00\uff0c\u4ee5 \u7edd\u9876\u8f7b\u529f\uff0c\u8f7b\u767b\u5de7\u7eb5\u548c\u4ed6\u4eec\u5468\u65cb\uff0c\u4f46\u53ea\u8981\u4ed6\u4eec\u8dd1\u5230\u54ea\u91cc\uff0c\u706b\u7130\u4fbf\u968f\u7740\u55b7\u6765\uff1b\u51cc\u672a\u98ce\u52c3\u7136\u5927 \u6012\uff0c\u8131\u4e0b\u5916\u8863\uff0c\u632f\u81c2\u4e00\u6296\uff0c\u547c\u547c\u5e26\u98ce\u3002\u4e00\u80a1\u70c8\u7130\u5982\u706b\u86c7\u822c\u5c04\u5230\uff0c\u51cc\u672a\u98ce\u5e76\u4e0d\u8eb2\u907f\uff0c\u8fce\u7740\u706b \u5934\uff0c\u5c06\u5e03\u886b\u4e00\u7f69\uff0c\u8eab\u5b50\u51cc\u7a7a\u8dc3\u8d77\uff0c\u5de6\u624b\u624b\u5fc3\u6263\u7740\u7684\u201c\u5929\u5c71\u795e\u8292\u201d\uff0c\u4e5f\u5c31\u5728\u63a0\u8d77\u4e4b\u9645\u98de\u51fa\uff0c\u70c8 \u7130\u7ed9\u5e03\u886b\u4e00\u6251\uff0c\u706b\u5934\u4e5f\u7ed9\u626b\u4e86\u56de\u53bb\u3002\u867d\u7136\u5728\u8fd9\u4e00\u6321\u4e00\u6251\u4e4b\u95f4\uff0c\u5e03\u886b\u5df2\u718a\u718a\u5730\u71c3\u70e7\u8d77\u6765\uff0c\u53ef\u662f \u51cc\u672a\u98ce\u56e0\u6709\u5e03\u886b\u63a9\u853d\uff0c\u7adf\u662f\u6beb\u53d1\u4e0d\u4f24\u3002 \u3000\u3000\u90a3\u4e2a\u6b66\u58eb\u7edd\u672a\u6599\u5230\u51cc\u672a\u98ce\u5982\u6b64\u5389\u5bb3\uff0c\u731b\u7136\u95f4\u89c1\u4ed6\u602a\u9e1f\u4f3c\u7684\u51cc\u7a7a\u63a0\u8d77\uff0c\u76ee\u77aa\u53e3\u5446\uff0c\u8bf4\u65f6\u8fdf \u90a3\u65f6\u5feb\uff0c\u4e00\u9053\u4e4c\u91d1\u5149\u8292\u6742\u5728\u706b\u5149\u4e2d\u7535\u5c04\u800c\u81f3\uff0c\u4ed6\u8eb2\u95ea\u4e0d\u53ca\uff0c\u672c\u80fd\u5730\u5c06\u55b7\u706b\u7b52\u4e00\u6321\uff0c\u53ea\u542c\u5f97 \u201c\u556a\u201d\u7684\u4e00\u58f0\u70b8\u88c2\u5f00\u6765\uff0c\u706b\u661f\u7eb7\u98de\uff0c\u706b\u7130\u5012\u5c04\uff0c\u767b\u65f6\u7ed9\u70c8\u7130\u5305\u56f4\u4e86\u5168\u8eab\uff0c\u50cf\u70e4\u732a\u4e00\u6837\u7684\u70e7\u7126 \u4e86\uff01\u706b\u7130\u98de\u5904\uff0c\u9644\u8fd1\u7684\u6b66\u58eb\u7eb7\u7eb7\u8d70\u907f\uff0c\u51cc\u672a\u98ce\u8fd9\u65f6\u5df2\u51cc\u7a7a\u4e0b\u8d70\uff0c\u5c06\u7740\u706b\u7684\u5e03\u886b\u56db\u4e0b\u4e00\u626b\uff0c\u987a \u624b\u5411\u4eba\u4e1b\u4e2d\u629b\u53bb\uff0c\u53f3\u624b\u62d4\u51fa\u6e38\u9f99\u5251\uff0c\u72c2\u98ce\u66b4\u96e8\u822c\u7684\u76f4\u6740\u8fc7\u6765\uff0c\u55b7\u706b\u7b52\u53ea\u5b9c\u8fdc\u653b\uff0c\u4e0d\u5b9c\u8fd1\u53d6\u3002 \u4eba\u4e1b\u4e2d\u6709\u51e0\u4e2a\u624b\u6301\u55b7\u706b\u7b52\u7684\u6b66\u58eb\uff0c\u4e5f\u53ea\u5f97\u653e\u4e0b\u706b\u5668\uff0c\u62d4\u51fa\u5175\u5203\u5e94\u654c\u3002 \u3000\u3000\u51cc\u672a\u98ce\u8fd9\u4e00\u8def\u51b2\u5f00\u7f3a\u53e3\uff0c\u5085\u9752\u4e3b\u7b49\u6025\u5c55\u5f00\u8eab\u5f62\uff0c\u81ea\u7f3a\u53e3\u6d8c\u8fdb\u3002\u4e09\u7537\u4e00\u5973\u5982\u56db\u5934\u731b\u864e\uff0c\u9510 \u4e0d\u53ef\u5f53\u3002\u53ea\u662f\u82b1\u56ed\u4e2d\u7684\u536b\u58eb\u53ef\u771f\u4e0d\u5c11\uff0c\u4e00\u89c1\u56db\u4eba\u8981\u60f3\u51b2\u51fa\u91cd\u56f4\uff0c\u7acb\u523b\u56db\u9762\u516b\u65b9\u5305\u56f4\u800c\u6765\uff0c\u524d \u540e\u5de6\u53f3\u90fd\u6210\u4e86\u5200\u5c71\u5251\u6d77\u3002\u51cc\u672a\u98ce\u4e00\u9a6c\u5f53\u5148\uff0c\u5085\u9752\u4e3b\u4ed7\u5251\u6bbf\u540e\uff0c\u674e\u601d\u6c38\u548c\u5218\u90c1\u82b3\u5939\u5728\u5f53\u4e2d\uff0c\u674e \u601d\u6c38\u821e\u8d77\u6d41\u661f\u9524\uff0c\u5c06\u8fd1\u8eab\u7684\u654c\u4eba\u8feb\u5f00\uff1b\u5218\u90c1\u82b3\u5219\u5077\u7a7a\u65bd\u653e\u6697\u5668\uff0c\u52a9\u51cc\u672a\u98ce\u95ef\u9053\u3002 \u3000\u3000\u6e38\u9f99\u5251\u867d\u6709\u65ad\u91d1\u622a\u94c1\u4e4b\u80fd\uff0c\u65e0\u5948\u654c\u4eba\u592a\u591a\uff0c\u622a\u4e0d\u80dc\u622a\uff0c\u800c\u4e14\u78b0\u7740\u4e00\u4e9b\u91cd\u5175\u5668\uff0c\u8fd8\u771f\u4e0d\u6562 \u786c\u63a5\uff0c\u867d\u7136\u6253\u5f97\u7ffb\u7ffb\u6eda\u6eda\uff0c\u5730\u8f6c\u5929\u65cb\uff0c\u5374\u7adf\u662f\u51b2\u51fa\u4e09\u6b65\uff0c\u9000\u540e\u4e24\u6b65\uff0c\u65e0\u6cd5\u8131\u8eab\u3002 \u3000\u3000\u6253\u5230\u7d27\u5904\uff0c\u5085\u9752\u4e3b\u5ffd\u7136\u8fde\u8fde\u602a\u5578\uff0c\u968f\u7740\u602a\u5578\u4e4b\u58f0\uff0c\u4e00\u9635\u53f7\u89d2\u545c\u545c\u957f\u9e23\uff0c\u738b\u5e9c\u6b66\u58eb\u6115\u7136\u56db \u987e\uff0c\u731b\u7136\u95f4\uff0c\u8f70\u5929\u9707\u5730\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u82b1\u56ed\u7684\u56db\u9762\u56f4\u5899\u5728\u8f70\u96f7\u58f0\u4e2d\uff0c\u7ed9\u70b8\u5f97\u7816\u77f3\u7eb7\u98de\uff0c\u9644\u8fd1\u7684 \u6b66\u58eb\uff0c\u7eb7\u7eb7\u4f0f\u4e0b\uff0c\u51cc\u672a\u98ce\u8d81\u52bf\u5927\u5c55\u795e\u5a01\uff0c\u6740\u51fa\u4e00\u6761\u8840\u8def\uff01 \u3000\u3000\u5de8\u54cd\u8fc7\u540e\uff0c\u81ea\u56ed\u5916\u95ef\u8fdb\u4e86\u4e8c\u4e09\u5341\u6761\u5927\u6c49\uff0c\u4e3a\u9996\u7684\u7adf\u662f\u4e00\u4e2a\u9752\u8863\u5c11\u5973\u548c\u4e00\u4e2a\u9ec4\u886b\u5c11\u5e74\u3002\u8fd9 \u7fa4\u4eba\u4e00\u95ef\u8fdb\u6765\uff0c\u7acb\u523b\u5f2f\u7bad\u5982\u8fde\u73e0\u75be\u53d1\uff0c\u4e13\u6361\u4eba\u591a\u4e4b\u5904\u5c04\u53bb\uff0c\u9a7d\u7bad\u4e2d\u8fd8\u5939\u6742\u7740\u7070\u74f6\u77f3\u5b50\uff0c\u4e00\u540c \u653e\u5c04\uff0c\u785d\u70df\u6eda\u6eda\uff0c\u706b\u7130\u718a\u718a\uff0c\u738b\u5e9c\u7684\u6b66\u58eb\u4eec\u867d\u7136\u8bad\u7ec3\u6709\u7d20\uff0c\u4e5f\u7ed9\u6740\u5f97\u624b\u5fd9\u811a\u4e71\uff01 \u3000\u3000\u5218\u90c1\u82b3\u8ba4\u5f97\u90a3\u5e26\u5934\u7684\u5c11\u5e74\u6b63\u662f\u4ee5\u524d\u548c\u5085\u9752\u4e3b\u540c\u5230\u6b66\u5bb6\u5e84\uff0c\u540e\u6765\u53c8\u548c\u4ed6\u591c\u63a2\u4e94\u53f0\u5c71\u7684\u5192\u6d63 \u83b2\u3002\u81f3\u4e8e\u548c\u5979\u4e00\u9053\u7684\u9ec4\u886b\u5c11\u5e74\uff0c\u5374\u4e0d\u8bc6\u662f\u4f55\u7b49\u4eba\u7269\u3002 \u3000\u3000\u674e\u601d\u6c38\u5219\u9664\u4e86\u4e3a\u9996\u7684\u90a3\u5bf9\u7537\u5973\u4e0d\u8ba4\u8bc6\u5916\uff0c\u5176\u4f59\u7684\u5168\u90fd\u8ba4\u8bc6\uff0c\u90a3\u4e9b\u4eba\u6b63\u662f\u81ea\u5df1\u7684\u90e8\u4e0b\uff0c\u5728 \u4ed6\u5355\u8eab\u5e94\u7ea6\u6765\u6606\u660e\u4e4b\u524d\uff0c\u5148\u627c\u6765\u5367\u5e95\u7684\u3002\u53ea\u662f\u4ed6\u4e07\u5206\u4e0d\u89e3\uff0c\u4f55\u4ee5\u81ea\u5df1\u7684\u90e8\u4e0b\uff0c\u7adf\u4f1a\u542c\u8fd9\u5bf9\u964c \u751f\u7537\u5973\u7684\u6307\u6325\uff1f \u3000\u3000\u8fd9\u7fa4\u4eba\u8d8a\u6740\u8d8a\u52c7\uff0c\u5c24\u5176\u90a3\u4e2a\u9ec4\u886b\u5c11\u5e74\uff0c\u4f7f\u7740\u4e00\u5bf9\u957f\u5251\uff0c\u94f6\u5149\u8000\u773c\uff0c\u65bd\u5c55\u5f00\u6765\u7adf\u662f\u9690\u9690\u5e26 \u7740\u98ce\u96f7\u4e4b\u58f0\uff0c\u5f53\u9ec4\u8f9f\u6613\uff01\u4fdd\u67f1\u6c14\u7ea2\u4e86\u773c\uff0c\u89d1\u51c6\u674e\u601d\u6c38\u76f4\u6251\u8fc7\u53bb\uff0c\u624b\u4e2d\u6746\u4ff8\u4e00\u4e2a\u76d8\u65cb\uff0c\u76f4\u6296\u5f00 \u6765\uff0c\u821e\u6210\u4e00\u9053\u4e08\u8bb8\u65b9\u5706\u7684\u68d2\u82b1\uff0c\u5f53\u5934\u7f69\u4e0b\u3002\u674e\u5fe0\u6c38\u7684\u6d41\u661f\u9524\u98de\u821e\u8fc7\u53bb\uff0c\u7ed9\u6746\u68d2\u7eca\u4f4f\u9524\u7d22\uff0c\u7528 \u529b\u4e00\u62c9\uff0c\u674e\u601d\u6c38\u7adf\u7ed9\u62c9\u52a8\u4e24\u6b65\u3002\u51cc\u672a\u98ce\u8ddd\u79bb\u7a0d\u8fdc\uff0c\u672a\u53ca\u6765\u6551\uff0c\u53ea\u89c1\u90a3\u4e2a\u9ec4\u886b\u5c11\u5e74\uff0c\u864e\u543c\u4e00 \u58f0\uff0c\u5982\u98de\u6251\u81f3\uff0c\u4e0d\u95ee\u7682\u767d\uff0c\u53cc\u5251\u4ea4\u53c9\u4e00\u5288\uff0c\u6746\u6367\u7ed9\u5288\u53bb\u534a\u622a\uff0c\u6d41\u661f\u9524\u7684\u7684\u9524\u7d22\u4e5f\u7ed9\u65a9\u65ad\u3002\u6376 \u5934\u76f4\u98de\u4e0a\u534a\u7a7a\uff01\u4fdd\u67f1\u3001\u674e\u601d\u6c38\u90fd\u5927\u60ca\u5931\u8272\uff0c\u5404\u767d\u9000\u540e\u51e0\u6b65\u3002\u9752\u8863\u5c11\u5973\u6307\u770b\u674e\u601d\u6c38\u5927\u58f0\u53eb\u9053\uff1a \u201c\u54b1\u4eec\u662f\u81ea\u5df1\u4eba\u3002\u201d\u9ec4\u886b\u5c11\u5e74\u4e00\u58f0\u4e0d\u53d1\uff0c\u626d\u8f6c\u4e86\u8eab\u8ffd\u4e0a\u4fdd\u67f1\uff0c\u53c8\u662f\u4e00\u5251\u5288\u53bb\uff0c\u4fdd\u67f1\u4e00\u4e2a\u7ed5\u6b65 \u4fa7\u8eab\uff0c\u534a\u622a\u6746\u68f1\u4ee5\u201c\u957f\u86c7\u5165\u6d1e\u201d\u4e4b\u52bf\uff0c\u786c\u63d2\u8fdb\u6765\uff0c\u9ec4\u886b\u5c11\u5e74\u53f3\u5251\u5288\u51fa\uff0c\u5de6\u5251\u5374\u63a5\u7740\u4e0d\u52a8\uff0c\u8fd9 \u65f6\u7a81\u7136\u5f80\u4e0a\u4e00\u515c\uff0c\u54ce\u54b3\u4e00\u58f0\uff0c\u53c8\u628a\u4fdd\u67f1\u7684\u6746\u68d2\u65a9\u65ad\u4e00\u622a\uff0c\u53f3\u5251\u6539\u5288\u4e3a\u523a\uff0c\u53c8\u75be\u53c8\u51c6\uff0c\u628a\u4fdd\u67f1 \u7684\u80a9\u5934\u523a\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u4fdd\u67f1\u4e00\u9635\u72c2\u817a\uff0c\u8fde\u8fde\u5012\u7eb5\uff0c\u6309\u7740\u4f24\u53e3\u4fbf\u9003\u3002\u738b\u5e9c\u4e09\u6770\u4e4b\u4e00\u7684\u8303\u94ee\uff0c\u6025 \u5fd9\u8fc7\u6765\u62b5\u6321\uff0c\u4ed6\u7684\u6469\u4e91\u5251\u6cd5\u4ee5\u8f7b\u7075\u8fc5\u6377\u89c1\u957f\uff0c\u8eab\u63a0\u8d77\u4e00\u5251\u5411\u9ec4\u886b\u5c11\u5e74\u5934\u4e0a\u523a\u4e0b\uff0c\u5728\u4e0b\u843d\u4e4b \u9645\uff0c\u4e00\u4e2a\u201c\u8e6c\u811a\u201d\u5411\u9ec4\u886b\u5c11\u5e74\u80f8\u819b\u731b\u8e22\u3002\u9ec4\u886b\u5c11\u5e74\u53cc\u624b\u201c\u4e3e\u706b\u71ce\u5929\u201d\uff0c\u53ea\u4e00\u64a9\u4fbf\u628a\u8303\u94ee\u7684\u5251 \u78d5\u4e0a\u534a\u7a7a\uff0c\u53ef\u662f\u4ed6\u7684\u80f8\u819b\u4e5f\u7ed9\u8303\u6323\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u8e22\u4e86\u4e00\u811a\u3002\u51cc\u672a\u98ce\u8fd9\u65f6\u6b63\u56de\u8eab\u63f4\u52a9\uff0c\u89c1\u4ed6\u7ed9\u8e22 \u4e2a\u6b63\u7740\uff0c\u5927\u4e3a\u7740\u6025\uff0c\u6025\u5fd9\u4e00\u4e2a\u201c\u9f99\u5f62\u98de\u6b65\u201d\u98de\u63a0\u6570\u4e08\uff0c\u54ea\u77e5\u5c1a\u672a\u8d76\u81f3\uff0c\u53ea\u89c1\u8303\u6323\u5df2\u7ed9\u5f39\u51fa\u6570 \u4e08\u5f00\u5916\uff0c\u8dcc\u5f97\u5934\u7834\u8840\u6d41\uff0c\u8fd9\u5c11\u5e74\u7adf\u6709\u4e00\u8eab\u6a2a\u7ec3\u529f\u592b\uff01\u51cc\u672a\u98ce\u4e5f\u4e0d\u7981\u6697\u6697\u5403\u60ca\uff0c\u770b\u90a3\u5c11\u5e74\u4e0d\u8fc7 \u4e8c\u5341\u591a\u5c81\uff0c\u7adf\u662f\u5185\u5916\u517c\u4fee\uff0c\u4e09\u62db\u4e24\u5f0f\u5c31\u5c06\u4fdd\u67f1\u548c\u8303\u6323\u6253\u8d25\uff0c\u6b66\u529f\u4e4b\u5f3a\uff0c\u7adf\u4f3c\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\uff01 \u3000\u3000\u738b\u5e9c\u8fd9\u8fb9\uff0c\u4e24\u5458\u4e3b\u5c06\u4e00\u53bb\uff0c\u4f17\u6b66\u58eb\u7eb7\u7eb7\u9003\u7a9c\uff0c\u5192\u6d63\u83b2\u6253\u4e2a\u80e1\u54e8\uff0c\u5e26\u9886\u4f17\u4eba\u4fbf\u5411\u82b1\u56ed\u7f3a\u53e3 \u9000\u51fa\uff0c\u82b1\u56ed\u5916\u7cfb\u6709\u4e8c\u5341\u591a\u5339\u9a8f\u9a6c\u3001\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4e24\u4eba\u4e00\u9a91\uff0c\u5feb\u5feb\u64a4\u9000\uff01\u201d\u51cc\u672a\u98ce\u5c06\u9ec4\u886b\u5c11\u5e74 \u4e00\u626f\u9053\uff1a\u201c\u6211\u548c\u4f60\u5171\u4e58\u4e00\u5339\u3002\u201d\u626f\u7740\u4ed6\u7684\u624b\u62c9\u4e0a\u9a6c\u80cc\uff0c\u9ec4\u886b\u5c11\u5e74\u4ecd\u662f\u4e00\u58f0\u4e0d\u54cd\uff0c\u4e0a\u4e86\u9a6c\u80cc\u5374 \u7528\u529b\u4e00\u5939\uff0c\u90a3\u5339\u9a6c\u8d1f\u75db\u6012\u5954\uff0c\u5728\u957f\u8857\u72c2\u5636\u800c\u8fc7\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u5c31\u8dd1\u51fa\u90ca\u5916\u7adf\u8fdc\u8fdc\u629b\u5f00\u4e86\u4f17\u4eba\uff0c \u51cc\u672a\u98ce\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c11\u5e74\u597d\u602a\uff01\u201d\u4ed6\u7528\u624b\u8f7b\u8f7b\u4e00\u6309\u5c11\u5e74\u80a9\u5934\u8bf4\u9053\uff1a\u201c\u6162\u4e9b\u597d\u5417\uff1f\u201d\u5c11\u5e74\u5fae\u5fae\u4e00 \u632f\uff0c\u54fc\u9053\uff1a\u201c\u597d\uff01\u201d\u8eab\u5b50\u817e\u7a7a\u8dc3\u8d77\uff0c\u4fbf\u98de\u4e0b\u9a6c\u80cc\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5acc\u5feb\uff0c\u6211\u4e0d\u548c\u4f60\u540c\u9a91\u597d\u4e86\uff01\u201d \u8bf4\u7f62\u53d1\u8db3\u72c2\u5954\uff0c\u5feb\u903e\u5954\u9a6c\uff0c\u51cc\u672a\u98ce\u65e0\u5948\uff0c\u53ea\u5f97\u50ac\u9a6c\u8d76\u4e0a\u3002\u4e0d\u4e00\u4f1a\u8dd1\u5230\u4e00\u5904\u4e1b\u6797\uff0c\u4ed6\u5728\u4e00\u68f5\u67f3 \u6811\u4e0a\u4e00\u7ad9\uff0c\u5ffd\u7136\u81ea\u987e\u81ea\u5730\u8f7b\u8f7b\u54fc\u8d77\u5c0f\u66f2\u6765\uff0c\u51cc\u672a\u98ce\u8d70\u8fd1\u8ddf\u524d\uff0c\u4ed6\u4e5f\u4e0d\u7406\u4e0d\u776c\uff01 \u3000\u3000\u51cc\u672a\u98ce\u542c\u4ed6\u5531\u9053\uff1a \u3000\u3000\u201c\u6cb3\u8fb9\u6709\u4e2a\u9c7c\u513f\u8df3\uff0c\u53ea\u5728\u6c34\u9762\u98d8\uff0c\u5cb8\u4e0a\u7684\u4eba\u513f\uff0c\u4f60\u53ea\u542c\u7740\uff0c\u4e0d\u5fc5\u5f80\u4e0b\u77a7\u3002\u6700\u4e0d\u8be5\u624b\u6301\u957f \u7aff\u5c06\u4ffa\u9493\u3002\u5fc3\u4e0b\u9519\u60f3\u4e86\uff0c\u9c7c\u513f\u867d\u5c0f\uff0c\u4e94\u6e56\u56db\u6d77\u90fd\u6e38\u5230\uff0c\u4e5f\u66fe\u5f04\u6ce2\u6d9b\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u542c\u4ed6\u5531\u8fd9\u652f\u66f2\uff0c\u60c5\u6b4c\u4e0d\u50cf\u60c5\u6b4c\uff0c\u611f\u53f9\u4e0d\u50cf\u611f\u53f9\u3002\u5fc3\u60f3\uff1a\u96be\u9053\u4ed6\u4e5f\u50cf\u81ea\u5df1\u4e00\u6837\uff0c\u5728 \u9752\u6625\u7684\u5c81\u6708\u91cc\uff0c\u7ecf\u5386\u8fc7\u767e\u52ab\u6ca7\u6851\uff1f\u4ed6\u8fc8\u524d\u51e0\u6b65\uff0c\u5bf9\u9ec4\u886b\u5c11\u5e74\u9053\uff1a\u201c\u6211\u53eb\u51cc\u672a\u98ce\uff0c\u662f\u4ece\u56de\u7586\u6765 \u7684\u3002\u6562\u95ee\u5144\u53f0\u5c0a\u59d3\u5927\u540d\uff0c\u4f55\u65b9\u4eba\u6c0f\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u81ea\u62a5\u59d3\u540d\uff0c\u4ee5\u4e3a\u4ed6\u5fc5\u5b9a\u8038\u7136\u52a8\u5bb9\uff0c\u4e0d\u6599\u4ed6\u7adf\u4f3c\u6ca1\u542c\u8fc7\u51cc\u672a\u98ce\u7684\u540d\u5934\u4e00\u6837\uff0c\u5b9a\u7740\u773c \u795e\u51b7\u51b7\u7684\u770b\u4ed6\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u8ddf\u7740\u7b54\u9053\uff1a \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u6211\u59d3\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u77e5\u9053\u6211\u662f\u4ece\u90a3\u91cc\u6765\u7684\uff0c\u6211\u8fd8\u60f3\u627e\u4eba\u544a\u8bc9\u6211\u5462\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u4e0d\u7981\u6115\u7136\uff0c\u53c8\u60f3\uff1a\u83ab\u975e\u4ed6\u662f\u4f24\u5fc3\u4eba\u522b\u6709\u6000\u62b1\uff0c\u4e0d\u80af\u5c06\u59d3\u540d\u76f8\u544a\uff1f\u4e0a\u53bb\u62c9\u4ed6\u624b\u9053\uff1a \u201c\u540c\u662f\u5929\u6daf\u6ca6\u843d\u4eba\uff0c\u76f8\u9022\u4f55\u5fc5\u66fe\u76f8\u8bc6\uff1f\u5144\u53f0\u4e0d\u80af\u89c1\u4e5f\u5c31\u7f62\u4e86\u3002\u53ea\u662f\u4eca\u65e5\u65e2\u627f\u76f8\u6551\uff0c\u5927\u5bb6\u603b\u662f \u670b\u53cb\uff0c\u54b1\u4eec\u8c08\u4e00\u8c08\u5982\u4f55\uff1f\u201d\u9ec4\u886b\u5c11\u5e74\u628a\u624b\u4e00\u7529\u9053\uff1a\u201c\u4f60\u53eb\u6211\u8c08\u4ec0\u4e48\uff1f\u6211\u771f\u50cf\u521a\u521a\u51fa\u751f\u7684\u5a74\u513f \u4e00\u6837\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u77e5\u9053\u5440\uff01\u201d\u4ed6\u89c1\u51cc\u672a\u98ce\u6ee1\u8138\u4e0d\u60a6\u4e4b\u60c5\uff0c\u91cd\u91cd\u5730\u628a\u624b\u4e00\u6454\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8bb2\u7684\u90fd \u662f\u771f\u8bdd\u5440\uff0c\u4f60\u8981\u4e0d\u4fe1\u6211\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u4ece\u672a\u89c1\u8fc7\u8fd9\u6837\u602a\u7684\u4eba\uff0c\u4e0d\u7981\u6709\u70b9\u706b\u6c14\uff0c\u5c11\u5e74\u5c06\u624b\u91cd\u91cd\u4e00\u6454\uff0c\u4ed6\u4e5f\u6697\u8fd0\u5185\u529b\uff0c\u7d27\u7d27 \u4e00\u63e1\uff0c\u5c11\u5e74\u201c\u54df\uff01\u201d\u7684\u4e00\u58f0\uff0c\u7a81\u7136\u624b\u8155\u4e0b\u6c89\uff0c\u8fd0\u7528\u8170\u5200\u5c06\u624b\u6323\u8131\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u597d\u4e0d\u8bb2 \u7406\uff01\u201d\u51cc\u672a\u98ce\u7ed9\u4ed6\u51b5\u8170\u4e00\u987f\uff0c\u628a\u63e1\u4e0d\u4f4f\uff0c\u4e5f\u4e0d\u81ea\u7981\u201c\u54df\u201d\u4e86\u4e00\u58f0\uff0c\u4e24\u4eba\u529f\u529b\uff0c\u7adf\u662f\u534a\u65a4\u516b \u4e24\u3002\u4ed6\u89c1\u5c11\u5e74\u6012\u5bb9\u6ee1\u9762\uff0c\u4ee5\u4e3a\u4ed6\u5fc5\u5b9a\u7ffb\u8138\uff0c\u4e0d\u6599\u4ed6\u53c8\u72ec\u81ea\u884c\u5f00\u4e86\u53bb\uff0c\u501a\u5728\u4e00\u68f5\u6811\u4e0a\uff0c\u53cc\u624b\u62b1 \u5934\uff0c\u4f3c\u5728\u90a3\u91cc\u82e6\u82e6\u601d\u7d22\u3002\u5ffd\u7136\u53d1\u72c2\u822c\u5730\u5524\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u89c1\u6211\u90fd\u8981\u95ee\u6211\u7684\u59d3\u540d\uff0c\u6211\u5374\u53bb\u627e\u8c01\u544a \u8bc9\u6211\uff1a\u6211\u662f\u8c01\uff1f\u201d\u558a\u7f62\u864e\u76ee\u4e2d\u7adf\u7136\u6ef4\u4e0b\u4e86\u773c\u6cea\u6765\uff01 \u3000\u3000\u51cc\u672a\u98ce\u89c1\u4ed6\u8fd9\u6837\uff0c\u4e0d\u77e5\u6240\u63aa\u3002\u9065\u9065\u4e00\u671b\uff0c\u53ea\u89c1\u5c18\u5934\u5927\u8d77\uff0c\u5085\u9752\u4e3b\u3001\u5192\u6d63\u83b2\u3001\u674e\u601d\u6c38\u7b49\u4e00 \u5e72\u4eba\u4f17\uff0c\u98de\u9a91\u8d76\u81f3\u3002\u5192\u6d63\u83b2\u4e00\u4e0b\u4e86\u9a6c\uff0c\u5c31\u7b11\u7740\u5bf9\u5085\u9752\u4e3b\u9053\uff1a\u201c\u5085\u4f2f\u4f2f\uff0c\u6211\u731c\u4ed6\u662f\u5728\u8fd9\u513f\uff0c\u4f60 \u770b\u662f\u4e0d\u662f\uff1f\u4ed6\u8fd8\u8bb0\u5f97\u8d77\u6211\u4eec\u548c\u4ed6\u7ea6\u597d\u7684\u5730\u65b9\uff0c\u600e\u4f1a\u6ca1\u6cd5\u533b\u6cbb\uff1f\u201d\u5085\u9752\u4e3b\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a \u201c\u6211\u770b\u5f88\u96be\uff01\u201d\u5192\u6d63\u83b2\u561f\u7740\u5634\u9053\uff1a\u201c\u96be\u5e76\u4e0d\u7b49\u4e8e\u7edd\u671b\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u4e0a\u53bb\uff0c\u67d4\u58f0\u5bf9\u90a3\u4e2a\u9ec4\u886b\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u968f\u6211\u4eec\u53bb\u5b89\u6b47\uff0c\u6211\u4eec\u6709\u5f88\u591a\u670b\u53cb\uff0c\u8fd9\u4e9b\u670b\u53cb \u4e5f\u662f\u4f60\u7684\u670b\u53cb\uff0c\u670b\u53cb\u7684\u5bb6\u5c31\u662f\u4f60\u7684\u5bb6\uff01\u4f60\u542c\u6211\u8bdd\uff0c\u8fc7\u51e0\u5929\u6211\u5c31\u4f1a\u544a\u8bc9\u4f60\uff1a\u4f60\u662f\u8c01\uff0c\u6211\u4e00\u5b9a\u4f1a \u628a\u2018\u5931\u6389\u2019\u7684\u4f60\u2018\u6211\u2019\u56de\u6765\u3002\u201d\u8bf4\u7f62\u53c8\u66ff\u4ed6\u4ecb\u7ecd\u674e\u601d\u6c38\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4e2d\u95ef\u738b\u7684\u4f84\u5b59\u3002\u201d\u9ec4\u886b \u5c11\u5e74\u5583\u5583\u5730\u9053\uff1a\u201c\u674e\u95ef\u738b\uff0c\u674e\u95ef\u738b\u201d\u5192\u6d63\u83b2\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u542c\u8fc7\u8fd9\u4e2a\u540d\u5b57\u53eb\u4e86\u674e\u95ef\u5de5\u5382\u9ec4\u886b \u5c11\u5e74\u9053\uff1a\u201c\u8bb0\u4e0d\u8d77\u6765\u4e86\uff0c\u4e0d\u77e5\u9053\u6709\u6ca1\u6709\u542c\u8fc7\uff0c\u53ea\u662f\u597d\u50cf\u6bd4\u522b\u7684\u540d\u5b57\u719f\u3002\u201d\u8bf4\u7f62\u53c8\u53cc\u624b\u62b1\u5934\u82e6 \u82e6\u601d\u7d22\u3002 \u3000\u3000\u5192\u6d63\u83b2\u5ae3\u7136\u53e3\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u51fa\u6682\u65f6\u5c31\u4e0d\u8981\u53bb\u60f3\u4ed6\u3002\u597d\uff0c\u54b1\u4eec\u8d70\u3002\u201d\u90a3\u9ec4\u886b\u5c11\u5e74\uff0c \u7adf\u7136\u5f88\u542c\u5979\u7684\u8bdd\uff0c\u63a5\u7740\u51cc\u672a\u51e4\u8de8\u4e0a\u9a6c\u80cc\u9053\uff1a\u201c\u4f60\u662f\u5979\u7684\u670b\u53cb\uff0c\u5c31\u662f\u6211\u7684\u670b\u53cb\uff0c\u6211\u613f\u548c\u4f60\u5171\u4e58 \u8fd9\u5339\u9a6c\u3002\u201d\u5085\u9752\u4e3b\u671d\u5192\u6d63\u83b2\u4e00\u7b11\uff0c\u5192\u6d63\u83b2\u9762\u4e0a\u7eef\u7ea2\uff0c\u508d\u7740\u5218\u90c1\u82b3\u50ac\u9a6c\u4fbf\u8d70\u3002 \u3000\u3000\u4ed6\u4eec\u6295\u5954\u7684\u662f\u674e\u601d\u6c38\u4e00\u4e2a\u7236\u6267\u7684\u5bb6\uff0c\u8fd9\u4eba\u4ee5\u524d\u666f\u674e\u9526\u6c38\u7684\u7259\u5c06\uff0c\u95ef\u738b\u7684\u540e\uff0c\u4ed6\u5949\u674e\u9526\u4e4b \u547d\uff0c\u9690\u5c45\u6606\u660e\u90ca\u5916\uff0c\u4e8c\u5341\u5e74\u6765\u90fd\u548c\u95ef\u738b\u65e7\u90e8\u4fdd\u6301\u8054\u7edc\u3002 \u3000\u3000\u5927\u4f19\u5230\u8fbe\u8fd9\u5bb6\u4eba\u5bb6\u65f6\uff0c\u5df2\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u4e3b\u4eba\u65e9\u5df2\u6709\u4e86\u51c6\u5907\uff0c\u5f53\u5373\u8bbe\u9152\u7f6e\u996d\uff0c\u6b3e\u5f85\u7fa4\u96c4\u3002 \u3000\u3000\u8fd9\u5bb6\u5ead\u9662\u91cc\u6709\u4e24\u6b8a\u4e39\u6842\uff0c\u6606\u660e\u6c14\u5019\u6e29\u548c\uff0c\u521d\u79cb\u65f6\u5206\uff0c\u6842\u82b1\u5df2\u7136\u76db\u5f00\uff0c\u9999\u6c14\u9178\u90c1\uff0c\u4e2d\u4eba\u5982 \u9189\u3002\u9ec4\u886b\u5c11\u5e74\u5728\u7ecf\u8fc7\u5ead\u9662\u65f6\uff0c\u5ffd\u7136\u53cc\u9e70\u7d27\u76b1\uff0c\u663e\u5f97\u5f88\u662f\u7126\u8e81\uff0c\u5192\u6d63\u83b2\u770b\u5728\u773c\u5185\uff0c\u4e5f\u4e0d\u4f5c\u58f0\u3002 \u98df\u5b8c\u996d\u540e\uff0c\u4e3b\u4eba\u53d6\u51fa\u6842\u82b1\u871c\u996f\u5f85\u5ba2\uff0c\u9ec4\u886b\u5c11\u5e74\u5ffd\u7136\u53d1\u8d77\u813e\u6c14\uff0c\u5c06\u5bc6\u996f\u626b\u843d\u5730\u4e0a\uff0c\u4e3b\u4eba\u5927\u4e3a\u60ca \u8be7\uff0c\u5085\u9752\u4e3b\u5728\u4ed6\u8033\u8fb9\u4f4e\u4f4e\u8bf4\u4e86\u51e0\u53e5\uff0c\u9ec4\u886b\u5c11\u5e74\u4fbf\u5373\u5f0a\u8d54\u7f6a\u8bf4\u9053\uff1a\u201c\u89c1\u4e86\u6842\u82b1\uff0c\u6211\u597d\u50cf\u8981\u60f3\u8d77 \u4ec0\u4e48\u4e8b\u4f3c\u7684\uff0c\u53ef\u662f\u60f3\u6765\u60f3\u53bb\u53c8\u60f3\u4e0d\u51fa\uff0c\u4e0d\u77e5\u600e\u7684\u5c31\u70e6\u8e81\u8d77\u6765\uff0c\u4e3b\u4eba\u5bb6\u4f60\u53ef\u522b\u602a\u3002\u201d\u4f17\u4eba\u867d\u89c9 \u9ec4\u886b\u5c11\u5e74\u4e3e\u52a8\u602a\u5f02\uff0c\u4f46\u4ed6\u4eca\u65e5\u95ef\u8fdb\u738b\u5e9c\uff0c\u51fa\u529b\u6700\u591a\uff0c\u8c01\u4e5f\u4e0d\u613f\u5f53\u9762\u602a\u8d23\u4ed6\u3002 \u3000\u3000\u674e\u601d\u6c38\u548c\u51cc\u672a\u51e4\u90fd\u662f\u6ee1\u8179\u7591\u56e2\uff0c\u674e\u601d\u6c38\u60f3\u95ee\u81ea\u5df1\u7684\u90e8\u4e0b\uff0c\u600e\u6837\u4f1a\u548c\u9ec4\u886b\u5c11\u5e74\u4ed6\u4eec\u4f1a\u5408\u4e00 \u5904\uff1b\u51cc\u672a\u98ce\u4e5f\u60f3\u95f4\u535a\u9752\u4e3b\u600e\u4e48\u5ffd\u7136\u5230\u4e86\u6606\u660e\uff0c\u800c\u4e14\u6df7\u8fdb\u4e86\u738b\u5e9c\u5192\u5145\u533b\u751f\uff0c\u5085\u9752\u4e3b\u597d\u50cf\u77e5\u9053\u4ed6 \u4eec\u7684\u5fc3\u4e8b\u4f3c\u7684\uff0c\u9152\u5e2d\u65b9\u6563\uff0c\u5c31\u5bf9\u4ed6\u4eec\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u4eec\u95f9\u4e86\u4e00\u5929\uff0c\u4e5f\u591f\u7d2f\u4e86\u3002\u201d\u8fd8\u662f\u8d81\u65e9\u4f11 \u606f\uff0c\u5f85\u660e\u65e5\u518d\u5c06\u524d\u56e0\u540e\u679c\uff0c\u544a\u8bc9\u4e8c\u4f4d\u5982\u4f55\uff1f\u201d\u5085\u9752\u4e3b\u662f\u8001\u524d\u8f88\uff0c\u51cc\u672a\u98ce\u89c1\u4ed6\u8fd9\u6837\u8bf4\uff0c\u53ea\u5f97\u6ee1 \u809a\u5b50\u7eb3\u95f7\u7740\uff0c\u81ea\u53bb\u6b47\u606f\u3002 \u3000\u3000\u8fd9\u4e00\u665a\uff0c\u51cc\u672a\u98ce\u601d\u6f6e\u8d77\u4f0f\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u7761\u4e0d\u7740\u3002\u4e00\u5ffd\u513f\u60f3\u8d77\u5218\u90c1\u82b3\u5728\u6c34\u7262\u4e2d\u6fc0\u52a8\u7684\u795e \u60c5\uff1b\u4e00\u5ffd\u513f\u53c8\u60f3\u8d77\u9ec4\u886b\u5c11\u5e74\u602a\u5f02\u7684\u884c\u72b6\uff0c\u7761\u4e0d\u7740\u89c9\uff0c\u9042\u62ab\u8863\u8d77\u5e8a\uff0c\u5728\u5ead\u9662\u7684\u6708\u5149\u4e0b\u72ec\u81ea\u5f98 \u5f8a\u3002 \u3000\u3000\u4ed6\u7684\u623f\u95e8\u5916\u5c31\u662f\u5385\u5802\uff0c\u4ed6\u4e00\u51fa\u6765\u53ef\u53c8\u78b0\u5230\u4e86\u4ef6\u5947\u4e8b\uff0c\u5385\u5802\u4e0a\u5085\u9752\u4e3b\u72ec\u81ea\u79c9\u70db\u8bfb\u4e66\uff0c\u4e00\u89c1 \u4ed6\u51fa\u6765\uff0c\u7acb\u523b\u8bf4\u9053\uff1a\u201c\u51cc\u58ee\u58eb\uff0c\u4f60\u8fdb\u53bb\uff0c\u7b49\u4e0b\u4e0d\u8bba\u78b0\u5230\u4ec0\u4e48\u4e8b\u4f60\u90fd\u4e0d\u80fd\u58f0\u5f20\uff0c\u4e5f\u4e0d\u80fd\u52a8 \u624b\uff01\u201d\u51cc\u672a\u98ce\u89c1\u4ed6\u9762\u5bb9\u5e84\u8083\uff0c\u90d1\u91cd\u5176\u8f9e\uff0c\u53ea\u597d\u9000\u56de\u623f\u5185\uff0c\u6ce8\u89c6\u7740\u5916\u8fb9\u7684\u52a8\u9759\u3002 \u3000\u3000\u8fd9\u6837\u7ea6\u6478\u53c8\u8fc7\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5df2\u7ecf\u662f\u4e0b\u534a\u591c\u4e86\uff0c\u51cc\u672a\u98ce\u89c1\u5916\u9762\u6beb\u65e0\u52a8\u9759\uff0c\u5085\u9752\u4e3b\u4ecd\u662f\u7aef\u5750 \u5982\u77f3\u50cf\uff0c\u773c\u775b\u4e0d\u79bb\u4e66\u672c\uff0c\u597d\u751f\u7eb3\u95f7\uff0c\u5026\u75b2\u6b32\u7761\u3002\u5ffd\u7136\u95f4\uff0c\u542c\u6ed1\u697c\u68af\u58f0\u54cd\uff0c\u4e00\u4eba\u8d70\u4e0b\u6765\uff0c\u51cc\u672a \u98ce\u6025\u5fd9\u7738\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u9ec4\u886b\u5c11\u5e74\uff0c\u624b\u63d0\u53cc\u5251\uff0c\u633a\u7acb\u5982\u50f5\u5c38\uff0c\u773c\u775b\u5982\u5b9a\u73e0\uff0c\u9762\u4e0a\u9690\u9690\u542b\u6709\u6740 \u6c14\uff0c\u4e00\u6b65\u4e00\u6b65\u5411\u5085\u9752\u4e3b\u8d70\u6765\u3002\u51cc\u672a\u98ce\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u60f3\u53bb\u62e6\u4f4f\uff0c\u5374\u53c8\u60f3\u8d77\u5085\u9752\u4e3b\u7684\u8bdd\u3002\u653e \u773c\u770b\u65f6\uff0c\u53ea\u89c1\u5085\u9752\u4e3b\u597d\u50cf\u5168\u5140\u77e5\u89c9\u4f3c\u7684\uff0c\u4ecd\u5728\u7aef\u5750\u770b\u4e66\u3002\u6b63\u662f\uff1a \u3000\u3000\u6df1\u5bb5\u9022\u602a\u5f02\uff0c\u8c6a\u4fa0\u4e5f\u5fc3\u60ca\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u516b\u7ae0\u3000\u6069\u6028\u96be\u660e\u3000\u7a7a\u5c71\u60ca\u6076\u6597\u3000\u7075\u6839\u672a\u65ad\u3000\u4e00\u8bed\u9178\u8ff7\u832b \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u516b\u56de \u6069\u6028\u96be\u660e\u3000\u7a7a\u5c71\u60ca\u6076\u6597 \u7075\u6839\u672a\u65ad\u3000\u4e00\u8bed\u9178\u8ff7\u832b \u3000\u3000\u51cc\u672a\u98ce\u95ef\u8361\u6c5f\u6e56\uff0c\u7ecf\u8fc7\u65e0\u6570\u52ab\u96be\uff0c\u771f\u662f\u4ec0\u4e48\u60ca\u9669\u4e4b\u4e8b\u90fd\u66fe\u9047\u8fc7\uff0c\u591a\u51f6\u6076\u7684\u654c\u4eba\uff0c\u4ed6\u4e5f\u662f \u89c6\u82e5\u65e0\u7269\uff0c\u4f46\u770b\u7740\u8fd9\u9ec4\u886b\u5c11\u5e74\u50cf\u50f5\u5c38\u822c\u76f4\u633a\u633a\u8d70\u6765\uff0c\u773c\u73e0\u52a8\u4e5f\u4e0d\u52a8\u5730\u53d1\u51fa\u51b7\u51b7\u7684\u5149\u8292\uff0c\u4e0d\u89c9 \u4e5f\u662f\u6709\u70b9\u6bdb\u9aa8\u8038\u7136\u3002\u773c\u770b\u7740\u4ed6\u8d8a\u884c\u8d8a\u8fd1\uff0c\u5c31\u5feb\u8d70\u5230\u5085\u9752\u4e3b\u8ddf\u524d\u4e86\uff0c\u9762\u4e0a\u7684\u6740\u6c14\u4e5f\u66f4\u663e\u9732\u4e86\uff0c \u4ed6\u51e0\u4e4e\u8981\u558a\u51fa\u58f0\u6765\u3002\u53ef\u662f\u4ed6\u77e5\u9053\u5085\u9752\u4e3b\u65e9\u6709\u51c6\u5907\uff0c\u770b\u4ed6\u8fd9\u6837\u795e\u8272\u81ea\u5982\uff0c\u4e1d\u6beb\u4e0d\u5f53\u505a\u4e00\u56de\u4e8b\u513f \u4f3c\u7684\uff0c\u4ed6\u4e5f\u7a0d\u7a0d\u653e\u4e0b\u5fc3\u6765\u3002\u5fc3\u60f3\uff1a\u867d\u7136\u8fd9\u9ec4\u886b\u5c11\u5e74\u6b66\u529f\u6781\u5f3a\uff0c\u4f46\u5085\u9752\u4e3b\u4e5f\u662f\u6b66\u6797\u4e2d\u9876\u5c16\u513f\u7684 \u4eba\u7269\uff0c\u7edd\u4e0d\u4f1a\u4e00\u4e0b\u5b50\u5c31\u4e3a\u9ec4\u886b\u5c11\u5e74\u6240\u5236\uff0c\u82e5\u7136\u4ed6\u4e00\u52a8\u624b\uff0c\u81ea\u5df1\u4e0a\u53bb\u76f8\u52a9\uff0c\u5408\u4e8c\u4eba\u4e4b\u529b\uff0c\u65e0\u8bba \u5982\u4f55\u4e5f\u5236\u670d\u5f97\u4e86\u4ed6\u3002 \u3000\u3000\u5085\u9752\u4e3b\u4e00\u76f4\u7b49\u5230\u9ec4\u886b\u5c11\u5e74\u8d70\u5230\u4e86\u8eab\u8fb9\uff0c\u8fd9\u624d\u7f13\u7f13\u8d77\u7acb\uff0c\u82e5\u65e0\u5176\u4e8b\u5730\u95ee\u9053\uff1a\u201c\u7761\u5f97\u597d \u5417\uff1f\u201d\u9ec4\u886b\u5c11\u5e74\u76f4\u7740\u773c\u795e\u5446\u5446\u5730\u671b\u7740\u5085\u9752\u4e3b\u3002\u5085\u9752\u4e3b\u5fae\u5fae\u4e00\u7b11\uff0c\u62ff\u8d77\u4e86\u4e00\u676f\u8336\uff0c\u9012\u8fc7\u53bb\u9053\uff1a \u201c\u4f60\u559d\u4e00\u676f\u3002\u201d\u9ec4\u886b\u5c11\u5e74\u53f3\u624b\u4e00\u677e\uff0c\u957f\u5251\u545b\u5577\u5815\u5730\uff0c\u63a5\u8fc7\u4e86\u8336\u4fbf\u559d\uff0c\u5085\u9752\u4e3b\u62cd\u638c\u7b11\u9053\uff1a\u201c\u4f60 \u4e14\u518d\u7761\u4e00\u4f1a\u513f\u3002\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u9ec4\u886b\u5c11\u5e74\u9893\u7136\u5012\u5730\uff0c\u4e0d\u4e00\u523b\u5c31\u53d1\u51fa\u4e86\u9f3e\u58f0\u3002 \u3000\u3000\u51cc\u672a\u98ce\u6b63\u5f85\u7eb5\u51fa\uff0c\u5ffd\u542c\u5f97\u53c8\u662f\u683c\u767b\u683c\u767b\u7684\u4e0b\u697c\u68af\u4e4b\u58f0\uff0c\u5fc3\u60f3\uff0c\u96be\u9053\u53c8\u6709\u4e00\u4e2a\u5931\u9b42\u7684\u5bb6 \u4f19\uff1f\u53ea\u662f\u8fd9\u811a\u6b65\u58f0\u6025\u8feb\u5f97\u591a\uff0c\u89c1\u4e00\u4e2a\u5c11\u5973\u52ff\u5306\u5954\u4e0b\uff0c\u8fd9\u5c11\u5973\u6b63\u662f\u5192\u6d63\u83b2\u3002 \u3000\u3000\u5192\u6d63\u83b2\u4e00\u89c1\u9ec4\u886b\u5c11\u5e74\u7761\u5728\u5730\u4e0a\uff0c\u957f\u5251\u5815\u5728\u8eab\u8fb9\uff0c\u5931\u58f0\u95ee\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u4f24\u7740\u4f60\u5417\uff1f\u201d\u5085\u9752 \u4e3b\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4ed6\u6839\u672c\u6ca1\u6709\u548c\u6211\u52a8\u624b\u3002\u201d\u8bf4\u7f62\u5fae\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u628a\u4ed6\u5e9f\u4e86\uff0c\u4f60\u770b\u597d\u5417\uff1f\u201d \u5192\u6d63\u83b2\u558a\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u6210\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6740\u4ed6\uff0c\u4e5f\u4e0d\u662f\u628a\u4ed6\u5f04\u6b8b\u5e9f\uff0c\u6211\u662f\u8bf4\u628a\u4ed6\u7684 \u6b66\u529f\u5e9f\u4e86\uff0c\u6211\u53ea\u8981\u7565\u65bd\u624b\u672f\uff0c\u5c31\u53ef\u4ee5\u4fbf\u4ed6\u7a7a\u6709\u4e00\u8eab\u6b66\u827a\uff0c\u5374\u6beb\u65e0\u529b\u6c14\u4f7f\u5f97\u51fa\u6765\uff01\u201d\u5192\u6d63\u83b2\u54fd \u54bd\u7740\u9053\uff1a\u201c\u4f60\u600e\u80fd\u8fd9\u6837\u5fcd\u5fc3\uff1f\u4f60\u5e73\u751f\u66ff\u4eba\u6cbb\u75c5\uff0c\u73b0\u5728\u4e0d\u66ff\u4ed6\u6cbb\u4e5f\u7f62\u4e86\uff0c\u8fd8\u8981\u6349\u5f04\u4ed6\u5e72\u561b\uff1f\u201d \u5085\u9752\u4e3b\u9053\uff1a\u201c\u5c31\u662f\u56e0\u4e3a\u6211\u6cbb\u4e0d\u4e86\u4ed6\u7684\u75c5\uff0c\u4ed6\u8fd9\u4e2a\u2018\u79bb\u9b42\u75c7\u2019\uff08\u4f5c\u8005\u6309\uff1a\u8fd9\u662f\u4e2d\u56fd\u4ee5\u524d\u533b\u5b66\u4e0a \u7684\u540d\u8bcd\uff0c\u76f8\u5f53\u4e8e\u8fd1\u4ee3\u533b\u5b66\u7684\u6240\u8c13\u201c\u68a6\u6e38\u75c7\u201d\uff09\uff0c\u4e00\u5b9a\u662f\u53d7\u4e86\u4ec0\u4e48\u523a\u6fc0\uff0c\u6240\u4ee5\u624d\u53d1\u4f5c\u51fa\u6765\uff0c\u504f \u504f\u4ed6\u53c8\u628a\u4ec0\u4e48\u90fd\u5fd8\u8bb0\u4e86\uff0c\u6ca1\u6cd5\u63a2\u51fa\u4ed6\u7684\u75c5\u6e90\uff0c\u8fd9\u53eb\u6211\u5982\u4f55\u80fd\u6cbb\uff1f\u5c24\u5176\u53ef\u6015\u7684\u662f\uff0c\u4ed6\u5728\u53d1\u4f5c\u7684 \u65f6\u5019\uff0c\u6839\u672c\u5c31\u4ec0\u4e48\u4e5f\u4e0d\u77e5\u9053\uff0c\u4ed6\u867d\u7136\u767d\u5929\u91cc\u662f\u4e2a\u597d\u4eba\uff0c\u665a\u4e0a\u53d1\u4f5c\u65f6\uff0c\u5f88\u53ef\u80fd\u6740\u4e86\u4eba\u4e5f\u4e0d\u81ea \u77e5\uff0c\u4ed6\u7684\u6b66\u529f\u53c8\u8fd9\u6837\u5389\u5bb3\uff0c\u6211\u4e0d\u628a\u4ed6\u5e9f\u4e86\u3002\u8c01\u5236\u670d\u5f97\u4e86\u4ed6\uff1f\u201d\u5192\u6d63\u83b2\u95ee\u9053\uff1a\u201c\u4ed6\u521a\u624d\u60f3\u6740\u4f60 \u5417\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6211\u8fd8\u770b\u4e0d\u51fa\u6765\uff0c\u53ea\u662f\u89c1\u4ed6\u9762\u4e0a\u5145\u6ee1\u6740\u6c14\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u6211\u8bb0\u5f97\u4f60\u4ee5\u524d \u548c\u6211\u8c08\u8fc7\u2018\u79bb\u9b42\u75c7\u2019\u7684\u75c7\u72b6\uff0c\u6709\u4e00\u4e9b\u4eba\u5fc3\u91cc\u57cb\u85cf\u7740\u7684\u4e8b\u60c5\uff0c\u5e73\u65f6\u8fde\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\uff0c\u5230\u4e86\u68a6 \u4e2d\uff0c\u4e16\u4fd7\u7684\u675f\u7f1a\u6ca1\u6709\u4e86\uff0c\u4f1a\u7a81\u7136\u5347\u8d77\u6765\uff0c\u5982\u51b0\u5c71\u4e4b\u4e0a\u6d6e\uff0c\u53ef\u662f\u4ed6\u53ea\u662f\u4e3a\u6ee1\u8db3\u81ea\u5df1\u88ab\u538b\u5236\u7684\u6b32 \u671b\uff0c\u5728\u68a6\u4e2d\u6b32\u6c42\u901e\u5feb\u4e8e\u4e00\u65f6\uff0c\u771f\u6b63\u7684\u6076\u4e8b\u8fd8\u662f\u505a\u4e0d\u51fa\u6765\u7684\u3002\u8fd9\u65f6\u4ed6\u867d\u7136\u662f\u53e6\u5916\u4e00\u4e2a\u2018\u4ed6\u2019 \uff08\u4f5c\u8005\u6309\uff1a\u76f8\u5f53\u4e8e\u8fd1\u4ee3\u533b\u5b66\u4e0a\u7684\u201c\u7cbe\u795e\u5206\u88c2\u75c7\u201d\uff09\uff0c\u5374\u5e76\u4e0d\u5371\u5bb3\u4e16\u4eba\uff0c\u8fd9\u53eb\u505a\u5584\u6027\u79bb\u9b42\u75c7\uff0c \u662f\u5417\uff1f\u201d\u5085\u9752\u4e3b\u542c\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u6446\u4e86\u6446\u624b\uff0c\u500f\u5730\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5192\u6d63\u83b2\u60ca\u95ee\u9053\uff1a\u201c\u5085\u4f2f\u4f2f\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u8fd9\u4e2a\u65f6\u5019\uff0c\u4e8f\u4f60\u8fd8\u6709\u8010\u5fc3\u8c08\u533b\u5b66 \u4e0a\u7684\u95ee\u9898\u3002\u4ed6\u7a76\u7adf\u4f1a\u4e0d\u4f1a\u5bb3\u4eba\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\uff0c\u6211\u4e0d\u80fd\u591f\u5192\u8fd9\u4e2a\u9669\uff0c\u8ba9\u4ed6\u7559\u7740\u4e00\u8eab\u6b66\u529f\uff0c\u665a\u95f4 \u4e71\u95ef\u3002\u201d\u8bf4\u7f62\uff0c\u7f13\u7f13\u5411\u9ec4\u886b\u5c11\u5e74\u884c\u53bb\uff0c\u5192\u6d63\u83b2\u6025\u5f97\u4e24\u884c\u773c\u6cea\u593a\u7736\u800c\u51fa\uff0c\u8bf4\u9053\uff1a\u201c\u5085\u4f2f\u4f2f\uff0c\u4f60 \u4e0d\u75bc\u6211\u4e86\u3002\u201d\u5085\u9752\u4e3b\u672a\u53ca\u56de\u7b54\uff0c\u5ffd\u89c1\u4e00\u6761\u9ed1\u5f71\u4f3c\u5927\u96c1\u822c\u7684\u98de\u63a0\u800c\u6765\uff0c\u5085\u9752\u4e3b\u9000\u540e\u4e00\u6b65\uff0c\u54c8\u54c8 \u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5fcd\u4e0d\u4f4f\u8981\u8dd1\u51fa\u6765\u4e86\uff0c\u4f60\u600e\u4e48\u4e0d\u542c\u6211\u7684\u8bdd\uff1f\u201d\u8fd9\u98de\u63a0\u800c\u6765\u7684\u9ed1\u5f71\uff01\u6b63\u662f\u51cc\u672a \u98ce\u3002 \u3000\u3000\u51cc\u672a\u98ce\u547c\u5438\u7d27\u4fc3\uff0c\u6025\u58f0\u8bf4\u9053\uff1a\u201c\u522b\u7684\u4eba\u542c\u4f60\u7684\u8bdd\uff0c\u4f60\u8981\u628a\u4ed6\u6b66\u529f\u5e9f\u6389\uff0c\u6211\u53ef\u4e0d\u7b54\u5e94\u3002\u4f60 \u60f3\u4ed6\u8fd9\u8eab\u529f\u592b\u662f\u5bb9\u6613\u7ec3\u6210\u7684\u4e48\uff1f\u201d\u6b63\u597d\u5bf9\u6211\u4eec\u6709\u591a\u5927\u597d\u5904\uff01\u6211\u5b9e\u5728\u4e0d\u5fcd\u89c1\u8fd9\u6837\u7684\u4eba\u624d\u7ed9\u4f60\u6bc1 \u6389\uff01\u201d\u5192\u6d63\u83b2\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u5085\u4f2f\u4f2f\uff0c\u4f60\u770b\u51cc\u5927\u4fa0\u4e5f\u8fd9\u6837\u8bf4\uff0c\u4f60\u8fd8\u5fcd\u5fc3\u4e0b\u5f97\u4e86\u624b\uff1f\u201d \u3000\u3000\u5085\u9752\u4e3b\u53c8\u662f\u4e00\u9635\u54c8\u54c8\u5927\u7b11\uff0c\u5ffd\u7136\u655b\u624b\u5750\u4e86\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u82e6\u82e6\u601d\u7d22\u600e\u6837\u533b\u6cbb\u8fd9\u4e2a\u5c11 \u5e74\uff0c\u73b0\u5728\u7ec8\u4e8e\u627e\u5230\u529e\u6cd5\u4e86\u3002\u201d\u5192\u6dc0\u83b2\u8be7\u7136\u95ee\u9053\uff1a\u201c\u600e\u4e48\u2026\u2026\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u4f60\u9053\u6211\u771f\u7684\u8981 \u628a\u4ed6\u5e9f\u6389\u5417\uff1f\u6211\u4e0d\u8fc7\u662f\u60f3\u8bd5\u8bd5\u4f60\u5bf9\u4ed6\u5fc3\u610f\u5982\u4f55\uff1f\u73b0\u5728\u53ef\u8bd5\u51fa\u6765\u4e86\u3002\u201d\u5192\u6d63\u83b2\u561f\u7740\u5634\u9053\uff1a\u201c\u4f60 \u662f\u4e0e\u6211\u5f00\u73a9\u7b11\u3002\u201d\u5085\u9752\u4e3b\u4e00\u672c\u6b63\u7ecf\u5730\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u5f00\u73a9\u7b11\uff01\u4f60\u77e5\u9053\u2018\u5fc3\u75c5\u8fd8\u987b\u5fc3\u836f\u533b\u2019\uff0c\u4ed6 \u73b0\u5728\u9700\u8981\u4e00\u4e2a\u6e29\u67d4\u4f53\u8d34\u7684\u5973\u5b69\u5b50\u5728\u4ed6\u8eab\u8fb9\uff0c\u800c\u8fd9\u4e2a\u5973\u5b69\u5b50\uff0c\u662f\u4ed6\u80af\u4fe1\u670d\u7684\u4eba\uff0c\u8fd9\u6837\u4ed6\u624d\u4f1a\u542c \u5979\u7684\u8bdd\uff0c\u4e5f\u53ea\u6709\u8fd9\u6837\u4e00\u4e2a\u8010\u5fc3\u7684\u5973\u5b69\u5b50\uff0c\u624d\u4f1a\u63a2\u51fa\u4ed6\u7684\u75c5\u6e90\u3002\u53ef\u662f\u4ed6\u53c8\u6700\u8fd9\u4e48\u5371\u9669\u7684\u4eba\uff0c\u5982 \u679c\u90a3\u4e2a\u5973\u5b69\u5b50\u4e0d\u662f\u771f\u5fc3\u613f\u4e3a\u4ed6\u727a\u7272\u4e00\u5207\uff0c\u4e0d\u662f\u5bf9\u4ed6\u6781\u597d\u7684\u8bdd\uff0c\u5979\u5c31\u4e0d\u6562\u966a\u4f34\u7740\u8fd9\u6837\u7684\u4e00\u4e2a\u75c5 \u4eba\uff0c\u5c31\u662f\u80af\u966a\u4f34\u4ed6\uff0c\u4e5f\u4e0d\u4f1a\u5f97\u51fa\u4ec0\u4e48\u7ed3\u679c\u3002\u8fd9\u6837\u7684\u75c5\u4eba\uff0c\u4ed6\u7684\u611f\u89c9\u662f\u6700\u654f\u9510\u7684\u3002\u8c01\u5bf9\u4ed6\u662f\u4e0d \u662f\u771f\u6b63\u5173\u5fc3\uff0c\u4ed6\u4f1a\u611f\u89c9\u51fa\u6765\u7684\u3002\u4ed6\u9700\u8981\u4e00\u4e2a\u6bcd\u4eb2\uff0c\u4e00\u4e2a\u59d0\u59b9\uff0c\u4e00\u4e2a\u670b\u53cb\uff0c\u4e00\u4e2a\u53ef\u4ee5\u628a\u4efb\u4f55\u8bdd \u90fd\u544a\u8bc9\u7ed9\u5979\u7684\u4eba\u3002\u800c\u4f60\u5c31\u662f\u6700\u9002\u5408\u53bb\u7167\u987e\u4ed6\u7684\u4eba\u3002\u53ef\u662f\u5728\u6b64\u4e4b\u524d\uff0c\u6211\u8fd8\u4e0d\u77e5\u9053\u4f60\u5bf9\u4ed6\u7684\u5fc3 \u610f\uff0c\u6240\u4ee5\u6545\u610f\u8981\u628a\u4ed6\u5e9f\u6389\u8bd5\u4e00\u8bd5\u4f60\u3002\u201d\u5085\u9752\u4e3b\u8bf4\u4e86\uff0c\u5192\u6d63\u83b2\u9ed8\u7136\u4e0d\u8bed\uff0c\u5085\u9752\u4e3b\u53c8\u7b11\u7740\u8bf4\u9053\uff1a \u201c\u4f60\u770b\u5085\u4f2f\u4f2f\u662f\u75bc\u4f60\u4e0d\u662f\uff1f\u201d\u51cc\u672a\u98ce\u4e5f\u7ed9\u8fd9\u53e5\u8bdd\u5f15\u5f97\u7b11\u8d77\u6765\u4e86\u3002 \u3000\u3000\u5085\u9752\u4e3b\u770b\u4e86\u51cc\u672a\u98ce\u4e00\u773c\uff0c\u53c8\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u6211\u4eca\u665a\u4e0d\u4f46\u8bd5\u4e86\u6d63\u83b2\u59d1\u5a18\uff0c\u8fd8\u8bd5\u4e86\u51cc\u5927\u4fa0\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u8be7\u7136\u95ee\u9053\uff1a\u201c\u4f60\u8bd5\u6211\u5e72\u561b\uff1f\u201d\u5085\u9752\u4e3b\u7b11\u901a\uff1a\u201c\u552f\u82f1\u96c4\u80fd\u91cd\u82f1\u96c4\uff0c\u4f60\u7684\u6b66\u529f\u662f\u9876\u5c16 \u513f\u7684\u4eba\u7269\u4e86\uff0c\u6240\u4ee5\u4e00\u5b9a\u7279\u522b\u601c\u624d\u3002\u4eca\u665a\u4e00\u8bd5\uff0c\u679c\u7136\u4f60\u5bf9\u4ed6\u6781\u4e3a\u7231\u60dc\u3002\u8fd8\u51e0\u4e4e\u8981\u4e0e\u8001\u592b\u7ffb\u8138 \u5462\uff01\u8001\u5b9e\u8bf4\uff0c\u6211\u867d\u7136\u8bd5\u51fa\u6d63\u83b2\u613f\u966a\u4f34\u4ed6\uff0c\u4f46\u8fd8\u62c5\u5fc3\u4ed6\u4e07\u4e00\u53d1\u4f5c\u65f6\uff0c\u771f\u4e2a\u884c\u51f6\u7684\u8bdd\uff0c\u6ca1\u4eba\u80fd\u5236 \u670d\u5f97\u4e86\u4ed6\u3002\u73b0\u6709\u4f60\u548c\u6d63\u83b2\u5728\u4e00\u8d77\u8ddf\u7740\u4ed6\uff0c\u90a3\u5c31\u4e07\u65e0\u4e00\u5931\u3002\u5f53\u8ddf\u7740\u4ed6\u65f6\uff0c\u4f60\u5f97\u8ba9\u6d63\u83b2\u4e0e\u4ed6\u591a\u4eb2 \u8fd1\uff0c\u4f60\u53ea\u80fd\u662f\u5728\u65c1\u8fb9\u4fdd\u62a4\u3002\u201d\u8bf4\u7f62\u53c8\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u51cc\u672a\u98ce\u9053\uff1a\u201c\u5085\u8001\u5148\u751f\u7684\u533b\u672f\uff0c\u6211\u662f\u4f69\u670d\u6781\u4e86\uff0c\u82e5\u6709\u5dee\u9063\uff0c\u5728\u6240\u4e0d\u8f9e\u3002\u53ef\u662f\u5085\u8001\u5148\u751f\u4e5f \u80fd\u5c06\u75c5\u4eba\u7684\u6765\u5386\uff0c\u544a\u8bc9\u6211\u4e00\u70b9\u5417\uff1f\u6bd4\u5982\u8bf4\u4f60\u4eec\u662f\u600e\u6837\u9047\u5230\u7684\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u5728\u70db\u5149\u6447\u66f3\u4e4b\u4e2d\uff0c\u8bf4\u51fa\u4e86\u4e00\u6bb5\u60ca\u5fc3\u52a8\u9b44\u7684\u906d\u9047\u3002 \u3000\u3000\u539f\u6765\u5f53\u65e5\u5085\u9752\u4e3b\u548c\u5192\u6d63\u83b2\uff0c\u5728\u6b66\u5bb6\u5e84\u4e0e\u7fa4\u96c4\u5206\u624b\uff0c\u81ea\u5c71\u897f\u7ecf\u9655\u897f\u53d6\u9646\u8def\u5165\u5ddd\u3002\u884c\u4e86\u591a \u5929\uff0c\u5230\u4e86\u5251\u9601\uff0c\u8fd9\u5251\u9601\u662f\u6709\u540d\u7684\u9669\u5cfb\u5730\u65b9\uff0c\u201c\u8700\u9053\u96be\uff0c\u96be\u4e8e\u4e0a\u9752\u5929\u201d\uff0c\u8fd9\u53e5\u810d\u7099\u4eba\u53e3\u7684\u540d \u53e5\uff0c\u6240\u6307\u7684\u5c31\u662f\u5251\u9601\u8fd9\u4e00\u6bb5\u8def\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\uff0c\u4ed6\u4eec\u901a\u8fc7\u4e1b\u5c71\u4e2d\u77d7\u7acb\u7684\u201c\u5251\u95e8\u5173\u201d\uff0c\u5728\u5386\u53f2\u4e0a\u6709\u540d\u7684\u201c\u6808\u9053\u201d\u4e0a\u884c\u8d70\u3002\u6240\u8c13 \u201c\u6808\u9053\u201d\uff0c\u662f\u5728\u60ac\u5d16\u5c96\u58c1\u4e0a\uff0c\u5f00\u5c71\u51ff\u77f3\u8f9f\u51fa\u6765\u7684\u7f8a\u80a0\u5c0f\u5f84\u3002\u6709\u4e9b\u5730\u65b9\u6839\u672c\u65e0\u8def\u53ef\u901a\uff0c\u4e8e\u662f\u5728 \u5c96\u58c1\u5343\u5904\u51ff\u7a74\u67b6\u6728\uff0c\u5c31\u5728\u8fd9\u4e9b\u6a2a\u67f1\u4e0a\u67b6\u8d77\u51cc\u7a7a\u7684\u9053\u8def\uff1b\u6709\u4e9b\u5730\u65b9\u5219\u6cbf\u7740\u5c71\u58c1\uff0c\u51ff\u6210\u51e0\u5343\u6b65\u7684 \u68af\u7ea7\uff0c\u5085\u5192\u4e8c\u4eba\u5728\u6808\u9053\u4e0a\u884c\u8d70\uff0c\u4ef0\u770b\u662f\u906e\u65e0\u853d\u53e3\u7684\u4e1b\u5c71\uff0c\u770b\u662f\u6d9b\u58f0\u8f70\u9e23\u3001\u6df1\u4e0d\u53ef\u6d4b\u7684\u5c71\u8c37\u3002 \u5085\u9752\u4e3b\u8fd8\u4e0d\u89c9\u600e\u4e48\uff0c\u5192\u6d63\u83b2\u5374\u89c9\u5f97\u6709\u70b9\u6035\u76ee\u60ca\u5fc3\uff0c\u5982\u5c65\u8584\u51b0\u3002\u5176\u65f6\u867d\u662f\u521d\u590f\uff0c\u5728\u6808\u9053\u9ad8\u5904\uff0c \u4e5f\u89c9\u5c71\u98ce\u8feb\u4eba\uff0c\u8863\u4e0d\u80dc\u5bd2\u3002 \u3000\u3000\u5085\u9752\u4e3b\u7684\u6545\u4e8b\uff0c\u5c31\u4ece\u8fd9\u91cc\u8bf4\u8d77\u3002\u4ed6\u5bf9\u51cc\u672a\u51e4\u9053\uff1a\u201c\u90a3\u4e00\u65e5\uff0c\u6211\u4eec\u5728\u6808\u9053\u4e0a\u884c\u8d70\uff0c\u8bf4\u4e5f\u60ed \u6127\uff0c\u6211\u4eec\u90fd\u7b97\u662f\u6709\u70b9\u529f\u592b\u7684\u4eba\uff0c\u884c\u4e86\u4e00\u5929\uff0c\u8fd8\u672a\u66fe\u8d70\u5b8c\u8def\uff0c\u773c\u770b\u66ae\u972d\u82cd\u832b\uff0c\u5c71\u8272\u6b32\u66ae\uff0c\u6211\u7684 \u5fc3\u53ef\u6709\u70b9\u6025\u4e86\uff0c\u82e5\u5728\u6df1\u5c71\u91ce\u5bbf\uff0c\u6211\u81ea\u7136\u6beb\u65e0\u6240\u8c13\uff0c\u53ea\u662f\u6d63\u83b2\u5374\u662f\u4e2a\u5e74\u9752\u7684\u5973\u5b69\u5b50\uff0c\u800c\u4e14\u6211\u770b \u5979\u9762\u4e0a\u4f3c\u6709\u75c5\u5bb9\uff0c\u66f4\u662f\u7126\u8651\u3002 \u3000\u3000\u5192\u6d63\u83b2\u63d2\u53e3\u9053\uff1a\u201c\u4f60\u603b\u662f\u628a\u6211\u5f53\u5c0f\u5b69\u5b50\uff0c\u5176\u5b9e\u90a3\u65f6\u6211\u5e76\u4e0d\u662f\u751f\u75c5\u3002\u800c\u662f\u81ea\u4ece\u591c\u63a2\u4e94\u53f0\u5c71 \u4e4b\u540e\uff0c\u534a\u4e2a\u6708\u6765\uff0c\u603b\u611f\u5230\u5fc3\u91cc\u96be\u53d7\uff01\u201d\u51cc\u672a\u98ce\u542c\u4e86\uff0c\u6697\u6697\u55df\u53f9\u3002\u4e94\u53f0\u5c71\u4e4b\u591c\uff0c\u5192\u6d63\u83b2\u5bfb\u627e\u6bcd \u4eb2\uff0c\u5374\u627e\u5230\u4e86\u4ea1\u6bcd\u7684\u8863\u51a0\u4e4b\u5bb6\u3002\u8fd9\u4e00\u5e55\u60b2\u5267\uff0c\u4ed6\u4e5f\u66fe\u7ecf\u6697\u4e2d\u76ee\u7779\u3002\u4ed6\u81ea\u7136\u61c2\u5f97\u5192\u6d63\u83b2\u4e3a\u4ec0\u4e48 \u5fc3\u91cc\u96be\u53d7\u3002 \u3000\u3000\u5085\u9752\u4e3b\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4f55\u5c1d\u4e0d\u77e5\u9053\u4f60\u5fc3\u91cc\u96be\u53d7\uff0c\u6211\u5c31\u662f\u6015\u4f60\u6291\u90c1\u6210\u75c5\u5440\uff01\u201d\u5192\u6d63\u83b2\u773c\u5708 \u4e00\u7ea2\uff0c\u5ffd\u7136\u671b\u7740\u719f\u7761\u5728\u5730\u4e0a\u7684\u9ec4\u886b\u5c11\u5e74\uff0c\u6ef4\u6cea\u4e0b\u6765\u3002\u51cc\u672a\u98ce\u5fc3\u60f3\uff1a\u602a\u4e0d\u5f97\u4ed6\u4f1a\u7231\u4e0a\u9ec4\u886b\u5c11 \u5e74\uff0c\u8fd9\u4e24\u4eba\u4e00\u4e2a\u662f\u65e0\u7236\u6bcd\u7684\u5b64\u5973\uff0c\u4e00\u4e2a\u662f\u4e0d\u77e5\u81ea\u8eab\u51fa\u5904\u7684\u9752\u5e74\uff0c\u76f8\u540c\u7684\u547d\u8fd0\u50cf\u4e00\u6839\u7ea2\u7ebf\u628a\u4ed6 \u4eec\u8054\u8d77\u6765\u4e86\u3002 \u3000\u3000\u5085\u9752\u4e3b\u7ee7\u7eed\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u6b63\u5728\u7740\u6025\u4e4b\u65f6\uff0c\u5ffd\u7136\u6211\u4eec\u770b\u5230\u5c71\u5773\u5904\u6709\u4e00\u4e2a\u5c11\u5973\u5728\u91c7\u96c6\u5c71\u85e4\uff0c \u5979\u968f\u4fbf\u7528\u624b\u4e00\u626f\uff0c\u5c31\u662f\u4e00\u6761\u3002\u8fd9\u79cd\u5c71\u85e4\u5341\u5206\u575a\u97e7\uff0c\u5bfb\u5e38\u4eba\u7528\u5200\u5272\uff0c\u4e5f\u8fd8\u5f97\u82b1\u4e00\u4e9b\u529f\u592b\uff0c\u5979\u7ade \u662f\u8fd9\u6837\u7684\u6beb\u4e0d\u8d39\u529b\uff0c\u6211\u770b\u7740\u4e5f\u6709\u70b9\u60ca\u5947\u3002\u6d63\u83b2\u53eb\u4e86\u4e00\u58f0\uff0c\u90a3\u4e2a\u59d1\u5a18\u56de\u5934\u6765\uff0c\u89c1\u4e86\u6d63\u83b2\uff0c\u9ad8\u5174 \u5f97\u4ec0\u4e48\u4f3c\u7684\uff0c\u8d70\u8fc7\u6765\u62c9\u6d63\u83b2\u7684\u624b\uff0c\u95ee\u5979\u7a76\u7adf\u662f\u4e0d\u662f\u4ed9\u5973\uff0c\u7a81\u7136\u88ab\u98ce\u5439\u843d\u8352\u5c71\uff1f\u56e0\u4e3a\u5979\u5728\u6df1\u5c71 \u4e2d\u5df2\u7ecf\u5f88\u4e45\u770b\u4e0d\u5230\u5916\u9762\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u63a5\u7740\u9053\uff1a\u201c\u5176\u5b9e\u5979\u624d\u957f\u5f97\u7f8e\u5462\uff01\u90a3\u4e2a\u6837\u513f\u5440\uff01\u5c31\u50cf\u5e7d\u8c37\u4e2d\u7684\u767e\u5408\u82b1\uff01\u6211\u544a\u8bc9\u5979\u6211 \u4eec\u662f\u666e\u901a\u7684\u65c5\u4eba\uff0c\u5979\u6025\u5f97\u4ec0\u4e48\u4f3c\u7684\uff0c\u8d76\u5fd9\u62db\u547c\u6211\u4eec\u5230\u5979\u5bb6\u4e2d\u4f4f\u5bbf\u3002\u6211\u60f3\uff0c\u8fd9\u6837\u7684\u9669\u5cfb\u5cf0\u5dc5\uff0c \u5c45\u7136\u8fd8\u6709\u4eba\u5bb6\uff0c\u90a3\u8fd9\u4eba\u5bb6\u4e5f\u4e00\u5b9a\u4e0d\u662f\u666e\u901a\u4eba\u5bb6\u4e86\uff01\u201d \u3000\u3000\u5085\u9752\u4e3b\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u7684\u5bb6\u5c31\u5728\u9644\u8fd1\uff0c\u53ef\u662f\u6211\u4eec\u8fdc\u770b\u5374\u4e00\u70b9\u770b\u4e0d\u51fa\u6765\u3002\u539f\u6765\u5979\u7684 \u5bb6\u7adf\u7136\u662f\u5efa\u5728\u4e24\u5cf0\u5939\u5cd9\u4e4b\u95f4\u7684\u60ac\u5d16\u5c96\u58c1\u4e0a\uff0c\u5ced\u58c1\u4e0a\u7a81\u51fa\u7684\u4e24\u682a\u866c\u677e\u521a\u597d\u628a\u5c4b\u5b50\u906e\u7740\u3002\u6211\u4eec\u8d70 \u8fdb\u5c4b\u5185\uff0c\u53ea\u89c1\u4e00\u4e2a\u516d\u65ec\u5de6\u53f3\u7684\u8001\u8005\uff0c\u751f\u5f97\u53c8\u9ed1\u53c8\u7626\uff0c\u624b\u6307\u5982\u9e1f\u722a\u4e00\u6837\uff0c\u6307\u7532\u5f88\u957f\uff0c\u7cbe\u795e\u5065 \u94c4\uff0c\u6211\u4eec\u89c1\u5230\u4ed6\u5f88\u60ca\u8be7\u7684\u89c1\u5230\u6211\u4eec\uff0c\u6211\u4eec\u544a\u8bc9\u4ed6\u662f\u8ff7\u4e86\u8def\u7684\u884c\u8005\uff0c\u4ed6\u5c06\u4fe1\u5c06\u7591\uff0c\u4f46\u6bd5\u7adf\u628a\u6211 \u4eec\u62db\u5f85\u4e0b\u6765\uff0c\u6211\u770b\u4ed6\u9762\u4e0a\u5e26\u6709\u6101\u5bb9\uff0c\u548c\u6211\u4eec\u8c08\u8bdd\u65f6\uff0c\u4e5f\u597d\u50cf\u6709\u70b9\u5fc3\u4e0d\u5728\u7109\u7684\u6837\u5b50\u3002\u6211\u4ee5\u4e3a\u4ed6 \u662f\u4e0d\u9ad8\u5174\u6211\u4eec\u6253\u6270\uff0c\u8981\u4e0d\u5c31\u662f\u6000\u7591\u6211\u4eec\u662f\u574f\u4eba\u3002\u53ef\u662f\u4ed6\u62db\u547c\u53c8\u5f88\u5468\u5230\u3002 \u3000\u3000\u201c\u6211\u4eec\u9971\u9910\u4e00\u987f\uff0c\u5165\u591c\u4e4b\u540e\uff0c\u4ed6\u7a81\u7136\u5bf9\u6211\u4eec\u9053\uff1a\u2018\u5ba2\u5b98\uff0c\u6211\u770b\u4f60\u4eec\u4e0d\u662f\u666e\u901a\u7684\u5ba2\u4eba\uff0c\u5927 \u7ea6\u90fd\u4f1a\u70b9\u6b66\u529f\uff0c\u53ea\u662f\u4eca\u665a\u82e5\u6709\u4ec0\u4e48\u4e8b\u53d1\u751f\uff0c\u4f60\u4eec\u90fd\u4e0d\u8bb8\u58f0\u5f20\uff0c\u4e5f\u4e0d\u8bb8\u52a8\u624b\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u542c\u5230\u8fd9\u91cc\uff0c\u63d2\u53e3\u7b11\u9053\uff1a\u201c\u5c31\u50cf\u4f60\u4eca\u665a\u5429\u5490\u6211\u4e00\u6a21\u4e00\u6837\uff1f\u201d\u5085\u9752\u4e3b\u8bf4\u9053\uff1a\u201c\u6211\u548c\u4f60 \u662f\u5f00\u73a9\u7b11\uff0c\u4ed6\u53ef\u4e25\u5389\u5f97\u591a\uff0c\u90a3\u795e\u6c14\u53ef\u6015\u6781\u4e86\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u9053\uff1a\u201c\u5f53\u65f6\u90a3\u4f4d\u59d1\u5a18\u95ee\u9053\uff1a\u2018\u7238\u7238\uff0c\u5988\u5988\u8fd8\u6ca1\u6709\u56de\u6765\u5462\uff01\u662f\u4e0d\u662f\u4e0a\u6b21\u90a3\u4e2a\u574f\u4eba\u53c8 \u6765\u4e86\uff0c\u8fd9\u56de\u6211\u957f\u5927\u4e86\uff0c\u6211\u5e2e\u4f60\u7684\u624b\u3002\u2019\u90a3\u4e2a\u8001\u4eba\u542c\u4e86\uff0c\u9762\u8272\u5927\u53d8\uff0c\u65a5\u8d23\u5979\u9053\uff1a\u2018\u4e0d\u8bb8\u4f60\u52a8 \u624b\uff0c\u4f60\u82e5\u52a8\u624b\uff0c\u6211\u5c31\u4e0d\u8ba4\u4f60\u662f\u5973\u513f\uff0c\u5c31\u7b97\u6211\u7ed9\u4eba\u6253\u6b7b\u4e86\uff0c\u4f60\u4e5f\u4e0d\u51c6\u548c\u6765\u4eba\u52a8\u624b\uff0c\u5373\u4f7f\u4ed6\u8981\u5e26 \u4f60\u8d70\uff0c\u4f60\u4e5f\u5f97\u8ddf\u4ed6\u8d70\uff0c\u7edd\u4e0d\u8bb8\u66ff\u6211\u62a5\u4ec7\uff0c\u4f60\u542c\u89c1\u5417\uff1f\u2019\u90a3\u5c11\u5973\u54ed\u9053\uff1a\u2018\u7238\u7238\uff0c\u4f60\u8bf4\u7684\u662f\u4ec0\u4e48 \u8bdd\uff1f\u2019\u90a3\u8001\u8005\u5389\u58f0\u8bf4\u9053\uff1a\u2018\u4f60\u82eb\u8fdd\u80cc\u6211\u8a00\uff0c\u6211\u6b7b\u4e0d\u7791\u76ee\uff01\u2019\u6211\u542c\u5230\u4e86\uff0c\u89c9\u5f97\u8fd9\u4e2a\u8001\u4eba\u4e0d\u8fd1\u60c5 \u7406\u3002\u6211\u770b\u7740\u5085\u4f2f\u4f2f\uff0c\u4ed6\u5374\u4e00\u53e5\u4e5f\u4e0d\u51fa\u58f0\uff0c\u6211\u60f3\u8bf4\u8981\u62d4\u5200\u76f8\u52a9\uff0c\u4f46\u53c8\u89c9\u5f97\u8fd9\u662f\u4e0d\u81ea\u91cf\u529b\uff0c\u56e0\u4e3a \u90a3\u4e2a\u59d1\u5a18\u6bd4\u6211\u8fd8\u5f3a\u3002\u5c4b\u5b50\u91cc\u4e00\u7247\u6101\u4e91\u60e8\u96fe\uff0c\u6211\u7684\u5fc3\u4e5f\u50cf\u94c5\u4e00\u6837\u53c8\u6c89\u53c8\u5b9e\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6211\u5728\u6c5f\u6e56\u884c\u8d70\uff0c\u4e5f\u6709\u51e0\u5341\u5e74\u4e86\uff0c\u4ece\u672a\u9047\u8fc7\u8fd9\u6837\u7684\u602a\u4e8b\u3002\u8fd9\u4e2a\u8001\u8005\u770b\u6765\u7ec3\u5c31 \u5927\u529b\u9e70\u722a\u7684\u529f\u592b\uff0c\u4e24\u773c\u795e\u5149\u5955\u5955\uff0c\u4e00\u770b\u4fbf\u77e5\u662f\u5185\u5bb6\u9ad8\u624b\uff0c\u53ef\u662f\u6211\u5374\u4e1d\u6beb\u4e0d\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u4eba\u3002 \u6211\u731c\u5927\u7ea6\u662f\u6c5f\u6e56\u4e0a\u7684\u5bfb\u4ec7\u62a5\u590d\uff0c\u521a\u597d\u7ed9\u6211\u4eec\u78b0\u4e0a\u3002\u53ef\u82e5\u662f\u6c5f\u6e56\u5bfb\u4ec7\uff0c\u5f53\u4e8b\u4eba\u7edd\u6ca1\u6709\u4e0d\u6b22\u8fce\u52a9 \u62f3\u4e4b\u7406\uff0c\u8fd9\u8001\u4eba\u8fde\u5973\u513f\u4e5f\u4e0d\u51c6\u5e2e\u5fd9\uff0c\u8fd9\u53ef\u53eb\u6211\u600e\u6837\u4e5f\u731c\u4e0d\u900f\uff01\u201d \u3000\u3000\u8fd9\u65f6\u7a97\u5916\u591c\u51e4\u547c\u547c\uff0c\u9e64\u6851\u5389\u9e23\uff0c\u51cc\u672a\u98ce\u5ffd\u7136\u62cd\u638c\u8bf4\u9053\uff1a\u201c\u6211\u731c\u5f97\u51fa\u8fd9\u4e2a\u8001\u8005\u662f\u4ec0\u4e48 \u4eba\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5ffd\u7136\u7a97\u5916\u6709\u4eba\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u731c\u5f97\u51fa\u8fd9\u8001\u8005\u662f\u4ec0\u4e48\u4eba\uff01\u201d\u51cc\u672a\u98ce\u4e00\u8dc3\u800c \u8d77\uff0c\u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\u84e6\u5730\u7a7f\u7a97\u800c\u5165\u3002 \u3000\u3000\u90a3\u8df3\u8fdb\u6765\u7684\u4eba\u662f\u674e\u601d\u6c38\uff0c\u4ed6\u4e5f\u662f\u5fc3\u6709\u7591\u56e2\uff0c\u7ec8\u5bb5\u672a\u5bd0\uff0c\u4e3a\u5192\u6d63\u83b2\u7a97\u4e0b\u697c\u68af\u4e4b\u58f0\u6240\u60ca\uff0c\u8ddf \u4e86\u4e0b\u6765\u3002\u51cc\u672a\u98ce\u542c\u5f97\u51fa\u795e\uff0c\u7adf\u672a\u53d1\u73b0\u4ed6\u4f0f\u5728\u7a97\u5916\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5085\u9752\u4e3b\u89c1\u51cc\u672a\u98ce\u548c\u674e\u601d\u6c38\u90fd\u8bf4\u77e5\u9053\u8fd9\u8001\u8005\u662f\u8c01\uff0c\u5927\u4e3a\u8be7\u5f02\u3002\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u66fe\u542c \u8fc7\u5e08\u7236\u8c08\u8d77\u5404\u6d3e\u540d\u5bbf\uff0c\u636e\u8bf4\u5728\u5251\u9601\u6808\u9053\u7684\u7edd\u9876\u4e4b\u5904\uff0c\u9690\u5c45\u6709\u4f4d\u8001\u8005\uff0c\u540d\u53eb\u6842\u5929\u6f9c\uff0c\u5728\u5927\u529b\u9e70 \u722a\u529f\u548c\u7ef5\u638c\u4e0a\u6709\u7edd\u9876\u529f\u592b\uff0c\u9e70\u722a\u529f\u662f\u5916\u5bb6\u7edd\u6280\uff0c\u7ef5\u638c\u5219\u662f\u5185\u5bb6\u6700\u96be\u7ec3\u7684\u529f\u592b\uff0c\u8fd9\u4eba\u80fd\u5185\u5916\u517c \u4fee\uff0c\u53ef\u7b97\u662f\u6b66\u6797\u4e2d\u7684\u602a\u6770\u3002\u201d\u5192\u6d63\u83b2\u542c\u4e86\uff0c\u201c\u5618\u201d\u4e86\u4e00\u58f0\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4ed6\u59d3\u6842\uff1f\u201d\u51cc\u672a\u98ce \u70b9\u4e86\u70b9\u5934\uff0c\u5192\u6d63\u83b2\u773c\u6ce2\u6d41\u52a8\uff0c\u624b\u6258\u9999\u816e\uff0c\u4f3c\u5728\u601d\u7d22\u4ec0\u4e48\u4e8b\u60c5\u4e00\u6837\u3002 \u3000\u3000\u674e\u601d\u6c38\u9053\uff1a\u201c\u6211\u4e5f\u542c\u5148\u7236\u8bf4\u9053\uff0c\u6709\u4e00\u4e2a\u540d\u53eb\u6842\u5929\u6f9c\u7684\u4eba\uff0c\u6b66\u529f\u6781\u5f3a\uff0c\u5f53\u5f20\u732e\u5fe0\u4e3b\u5ddd\u65f6\uff0c \u66fe\u6295\u5728\u5f20\u90a3\u5927\u5c06\u674e\u5b9a\u56fd\u5e10\u4e0b\uff0c\u4e0d\u4e45\u5f20\u732e\u5fe0\u674e\u5b9a\u56fd\u76f8\u7ee7\u8d25\u4ea1\uff0c\u6b64\u4eba\u5c31\u4e0d\u77e5\u8e2a\u8ff9\u3002\u540e\u6765\u6709\u4eba\u8bf4\u4ed6 \u9690\u8eab\u5251\u9601\uff0c\u5148\u7236\u6d3e\u4eba\u53bb\u627e\u4e86\u51e0\u6b21\uff0c\u90fd\u6ca1\u6709\u627e\u7740\u3002\u5085\u8001\u524d\u8f88\u8bf4\u6709\u4eba\u627e\u4ed6\u5bfb\u4f19\uff0c\u6211\u60f3\u4e5f\u8bb8\u4e0d\u662f\u79c1 \u4eba\u5bfb\u4ec7\uff0c\u800c\u662f\u6e05\u5ef7\u7684\u9ad8\u624b\u8e29\u5230\u4e86\u4ed6\u7684\u8e2a\u8ff9\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u4f60\u53ea\u731c\u5230\u4e86\u4e00\u534a\uff0c\u6700\u521d\u6765\u5bfb\u4ec7\u7684\u4eba\u4e0d\u662f\u6e05\u5ef7\u7684\u4eba\u3002\u201d\u63a5\u7740\u4ed6\u5f80\u4e0b \u8bf4\u9053\uff1a\u201c\u90a3\u8001\u4eba\u6b63\u5728\u548c\u5973\u513f\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u5c4b\u9876\u4e0a\u7a7a\u7a81\u7136\u63a0\u8fc7\u4e00\u6280\u54cd\u7bad\uff0c\u4e00\u58f0\u63a5\u7740\u4e00\u58f0\uff0c\u602a\u58f0\u6447 \u66f3\uff0c\u751a\u4e3a\u51c4\u5389\u3002\u8fd9\u662f\u6c5f\u6e56\u4e0a\u5bfb\u4ec7\u793a\u8b66\u7684\u8baf\u53f7\uff0c\u800c\u4e14\u82e5\u975e\u81ea\u4fe1\u80fd\u591f\u628a\u5bf9\u65b9\u624b\u5230\u64d2\u6765\uff0c\u51b3\u4e0d\u4f1a\u4f7f \u7528\u8fd9\u79cd\u5148\u884c\u4f20\u58f0\u4e0d\u81c2\u7684\u65b9\u5f0f\u3002\u6211\u6b63\u89c9\u5341\u5206\u8be7\u5f02\uff0c\u8fd9\u5bf9\u7236\u5973\u7684\u6b66\u529f\uff0c\u5df2\u662f\u6b66\u6797\u540c\u9053\u4e2d\u6240\u7f55\u89c1\uff0c \u96be\u9053\u53c8\u6709\u4ec0\u4e48\u9ad8\u4eba\uff0c\u6562\u5982\u6b64\u6258\u5927\uff1f\u54cd\u7bad\u8fc7\u540e\uff0c\u679c\u7136\u5916\u9762\u4f20\u6765\u66b4\u96f7\u4e5f\u4f3c\u7684\u559d\u58f0\uff1a\u201c\u4f60\u8fd8\u4e0d\u51fa\u6765 \u7b54\u8bdd\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u6101\u5bb9\u6ee1\u9762\uff0c\u7f13\u7f13\u8d77\u7acb\uff0c\u5bf9\u5973\u513f\u9053\uff1a\u2018\u4f60\u5343\u4e07\u542c\u6211\u7684\u8bdd\uff01\u2019\u53c8\u5411\u6211\u4eec\u9053\uff1a\u2018\u4f60\u4eec\u4e5f \u5343\u4e07\u522b\u7406\u95f2\u4e8b\uff01\u2019\u8bf4\u5b8c\uff0c\u4fbf\u51b2\u51fa\u5c4b\u5916\uff0c\u6211\u5fcd\u4e0d\u4f4f\u4e5f\u8ddf\u7740\u51fa\u53bb\uff0c\u56de\u5934\u4e00\u770b\uff0c\u90a3\u4e2a\u5c0f\u59d1\u5a18\u548c\u6d63\u83b2 \u4e5f\u51fa\u6765\u5566\uff01 \u3000\u3000\u201c\u5c4b\u5916\u7ad9\u7740\u7684\u662f\u4e00\u4e2a\u7ea2\u9762\u866c\u9aef\u7684\u8001\u8005\uff0c\u4e00\u89c1\u6211\u8ddf\u7740\u51fa\u6765\uff0c\u7ffb\u8d77\u6389\u773c\u77a7\u4e86\u77a7\uff0c\u51b7\u7b11\u9053\uff1a \u2018\u4f60\u5c45\u7136\u8fd9\u6837\u4e0d\u8981\u8138\uff0c\u8fd8\u627e\u4eba\u52a9\u62f3\uff01\u2019\u6211\u6025\u5fd9\u8bf4\u9053\uff1a\u2018\u6211\u53ea\u662f\u8fc7\u8def\u7684\u5ba2\u4eba\uff01\u2019\u6211\u77e5\u9053\u8fd9\u7c7b\u7684 \u6c5f\u6e56\u4ec7\u6597\uff0c\u82e5\u53ea\u662f\u4e00\u4eba\u51fa\u9762\uff0c\u90a3\u5c31\u5fc5\u5b9a\u662f\u7ea6\u597d\u7684\u5355\u6253\u72ec\u6597\u3002\u5916\u4eba\u82e5\u5076\u7136\u649e\u4e0a\uff0c\u4e5f\u5f97\u907f\u5f00\u3002\u9664 \u975e\u81ea\u95ee\u4e0d\u654c\u7684\u4e00\u65b9\uff0c\u9884\u5148\u9080\u597d\u5230\u4eb2\u81f3\u8fd1\u7684\u5e08\u53cb\uff0c\u90a3\u624d\u53e6\u5f53\u522b\u8bba\u3002\u6015\u4e5f\u5f97\u8ba9\u6b63\u70b9\uff08\u4e8b\u4e3b\uff09\u5148\u89c1 \u4e86\u771f\u7ae0\u624d\u80fd\u51fa\u624b\u3002\u6211\u672c\u8be5\u907f\u5f00\uff0c\u4f46\u654c\u4e0d\u4f4f\u597d\u5947\u5fc3\u7684\u5438\u5f15\uff0c\u4ecd\u7136\u5728\u8fdc\u8fdc\u7684\u770b\u4ed6\u4eec\u600e\u6837\u8f83\u91cf\u3002\u8fd9 \u65f6\u6211\u5ffd\u7136\u770b\u89c1\u6808\u9053\u4e0b\u9762\uff0c\u5c71\u8170\u5904\u4f3c\u6709\u9ed1\u5f71\u79fb\u52a8\u3002\u6b63\u6ce8\u89c6\u95f4\uff0c\u90a3\u7ea2\u9762\u8001\u8005\u5927\u559d\u9053\uff1a\u2018\u5c31\u662f\u6709\u4eba \u52a9\u62f3\uff1b\u6211\u4e5f\u4e0d\u6015\uff1a\u2019\u53cc\u638c\u4e00\u9519\uff0c\u66f4\u4e0d\u6253\u8bdd\uff0c\u5c31\u72e0\u72e0\u5730\u5411\u9ed1\u7626\u8001\u4eba\u6253\u53bb\uff0c\u6211\u7ad9\u5728\u5341\u4f59\u4e08\u5916\uff0c\u4e5f \u542c\u89c1\u547c\u547c\u7684\u638c\u58f0\u3002\u201d \u3000\u3000\u68f1\u672a\u98ce\u5bf9\u638c\u6cd5\u5251\u6cd5\u5747\u6709\u6781\u6df1\u7684\u9020\u8be3\uff0c\u542c\u5085\u9752\u4e3b\u8bf4\u5230\u4e24\u4f4d\u8001\u524d\u8f88\u5728\u5251\u9601\u5343\u7ea7\u6808\u9053\u4e4b\u4e0a\u5bf9 \u638c\uff0c\u4e0d\u7981\u5fc3\u5411\u5f80\u4e4b\u3002\u8bf4\u9053\uff1a\u201c\u4ee5\u6842\u5929\u6f9c\u7684\u6b66\u529f\uff0c\u5c45\u7136\u6709\u4eba\u6562\u767b\u95e8\u6311\u6218\uff0c\u53ef\u60dc\u6211\u770b\u4e0d\u5230\u8fd9\u6837\u7684 \u5bf9\u638c\u3002\u201d\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u5bf9\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6211\u770b\u4f60\u5728\u5251\u9601\u78b0\u522b\u7684\u9ed1\u7626\u8001\u4eba\uff0c\u4e5d\u6210\u662f\u6842\u5929\u6f9c\u3002\u4ed6 \u540e\u6765\u51fa\u624b\u662f\u4e0d\u662f\u4ee5\u7ef5\u638c\u4e3a\u4e3b\uff0c\u4fbf\u4ee5\u9e70\u722a\u529f\u592b\uff0c\u662f\u7684\u8bdd\uff0c\u4fbf\u51c6\u662f\u4ed6\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u5f53\u9ed1\u7626\u8001\u4eba\u662f\u6842\u5929\u6f9c\u5427\uff0c\u8bf4\u8d77\u6765\u5bb9\u6613\u8bb0\u4e9b\u3002\u6211\u521a\u624d\u8bf4\u5230 \u90a3\u7ea2\u9762\u866c\u987b\u7684\u8001\u8005\uff0c\u89c1\u4e86\u6842\u5929\u6f9c\u5c31\u5982\u53d1\u72c2\u4e00\u6837\uff0c\u53cc\u638c\u4e00\u9519\u4fbf\u72e0\u72e0\u6251\u4e0a\u3002\u6842\u5929\u6f9c\u5374\u4e0d\u52a8\u624b\uff0c\u53cc \u8db3\u4e00\u53d1\u52b2\uff0c\u4eba\u4fbf\u50cf\u98de\u7bad\u4e00\u6837\uff0c\u5c04\u51fa\u4e24\u4e09\u4e08\u5916\uff0c\u53e3\u91cc\u5c3d\u56b7\uff1a\u2018\u4f60\u6162\u70b9\u52a8\u624b\u884c\u4e0d\u884c\uff1f\u4e5f\u5f97\u8ba9\u4eba\u628a \u8bdd\u8bf4\u4e2a\u6e05\u695a\uff01\u2019\u90a3\u7ea2\u9762\u8001\u8005\u5374\u4e0d\u7406\u4e0d\u776c\uff0c\u7adf\u662f\u5982\u5f71\u964b\u5f62\uff0c\u6b65\u6b65\u8fdb\u8feb\u3002\u6842\u5927\u6f9c\u9000\u5f97\u51e0\u9000\uff0c\u5df2\u5230 \u4e86\u5c96\u58c1\u7684\u8fb9\u7f18\uff0c\u518d\u4e5f\u4e0d\u80fd\u5f80\u540e\u9000\u5566\uff01\u90a3\u7ea2\u9762\u8001\u4eba\u53cc\u638c\u9f50\u53d1\uff0c\u5411\u6842\u5929\u6f9c\u8fce\u9762\u63a8\u6765\u3002\u6842\u5929\u6f9c\u53cc\u638c \u500f\u5730\u4e00\u5206\uff0c\u659c\u8eab\u4e03\u6b65\uff0c\u53f3\u638c\u6a2a\u6321\uff0c\u5de6\u638c\u4e00\u7ffb\uff0c\u5411\u7ea2\u9762\u8001\u4eba\u8155\u4e0b\u4e00\u9563\uff0c\u540c\u65f6\u5e97\u624b\u9a88\u6307\u5982\u671d\uff0c\u4e00 \u63a2\u8eab\uff0c\u52bf\u6377\u5982\u7528\uff0c\u53cc\u6307\u5411\u7ea2\u9762\u8001\u4eba\u8170\u808b\u70b9\u53bb\uff0c\u7ea2\u9762\u8001\u4eba\u53cc\u638c\u4e00\u5c01\uff0c\u6309\u7740\u5de6\u638c\u4e0b\u5288\uff0c\u4e3e\u817f\u6a2a \u626b\u3002\u201d\u51cc\u672a\u98ce\u95ed\u76ee\u9759\u542c\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u7ea2\u9762\u8001\u4eba\u8fd9\u62db\u62c6\u5f97\u4e0d\u884c\u3002\u6842\u5929\u8c30\u7528\u7684\u662f\u7ef5\u638c\u4e2d\u5b54\u96c0\u6296 \u7fce\u7684\u5bb6\u6570\uff0c\u4e2d\u9014\u672a\u5f85\u53d8\u5c3d\uff0c\u53c8\u63ba\u4ee5\u70b9\u7a74\u6cd5\u3002\u7ea2\u9762\u8001\u4eba\u8fd9\u6837\u89e3\u6cd5\uff0c\u53ea\u80fd\u5316\u53bb\u5bf9\u65b9\u638c\u529b\uff0c\u907f\u4e0d\u5f00 \u70b9\u7a74\u3002\u4ed6\u90a3\u4e00\u817f\u53ea\u662f\u865a\u62db\uff0c\u4ee5\u653b\u4e3a\u5b88\u7684\uff0c\u6842\u5929\u6f9c\u53ea\u8981\u5f80\u659c\u8eab\u8fdb\u6b65\uff0c\u7ea2\u9762\u8001\u4eba\u5c31\u5b8c\u4e86\u3002\u770b\u6765\u7ea2 \u9762\u8001\u4eba\u6765\u52bf\u6c79\u6c79\uff0c\u8bf4\u5230\u771f\u529f\u592b\uff0c\u8981\u6bd4\u6842\u5929\u6f9c\u5dee\u4e00\u7b79\u3002 \u3000\u3000\u5085\u9752\u4e3b\u9053\uff1a\u201c\u8001\u5f1f\u638c\u6cd5\u679c\u662f\u9ad8\u660e\uff0c\u6842\u5929\u6f9c\u5f80\u5de6\u659c\u8eab\u9000\u6b65\uff0c\u624b\u6307\u5df2\u7136\u70b9\u5230\u7ea2\u9762\u8001\u4eba\u808b\u4e0b\u3002 \u53ef\u662f\u6842\u5929\u6f9c\u597d\u50cf\u6709\u610f\u8ba9\u4ed6\u4f3c\u7684\uff0c\u865a\u865a\u4e00\u6233\uff0c\u4e58\u7740\u7ea2\u9762\u8001\u4eba\u659c\u95ea\u4e4b\u9645\uff0c\u81ea\u5df1\u5374\u731b\u5730\u5f80\u53f3\u7a9c\u51fa\uff0c \u79bb\u5f00\u4e86\u5ced\u58c1\u8fb9\u7f18\u3002\u201d\u51cc\u672a\u51e4\u9053\uff1a\u201c\u7ea2\u9762\u8001\u4eba\u8f93\u4e86\u4e00\u62db\u5566\uff0c\u8be5\u505c\u624b\u4e86\uff1f\u201d \u3000\u3000\u5085\u9752\u4e3b\u9053\uff1a\u201c\u4ed6\u624d\u4e0d\u505c\u624b\u5462\uff01\u201d\u6211\u5728\u6708\u5149\u4e0b\uff0c\u770b\u5230\u4ed6\u7684\u7ea2\u9762\u53d8\u7d2b\uff0c\u4e00\u4e2a\u7bad\u6b65\u53c8\u6251\u8fc7\u6765\uff0c \u597d\u50cf\u62fc\u547d\u4f3c\u7684\uff0c\u4ed6\u4e5f\u771f\u6709\u70b9\u90aa\u95e8\uff0c\u62f3\u6cd5\u5c55\u5f00\uff0c\u8eab\u4f3c\u98de\u9c7c\uff0c\u6b65\u5982\u6d41\u6c34\uff0c\u7ed5\u7740\u6302\u5929\u6f9c\u8eab\u5b50\u6ef4\u6e9c\u6e9c \u4e71\u8f6c\uff0c\u4e24\u624b\u5ffd\u62f3\u5ffd\u638c\uff0c\u75be\u903e\u98ce\u8f6e\uff0c\u8eab\u6cd5\u624b\u6cd5\u8d8a\u6765\u8d8a\u8bc0\uff0c\u811a\u4e0b\u8d70\u7684\u5374\u662f\u4e5d\u5bab\u516b\u5366\u65b9\u4f4d\uff0c\u4e1d\u6beb\u4e0d \u4e71\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4ed6\u4f7f\u7684\u4e00\u5b9a\u662f\u4e5d\u5bab\u795e\u884c\u638c\uff0c\u8fd9\u79cd\u638c\u6cd5\uff0c\u6697\u85cf\u516b\u4e5d\u4e03\u5341\u4e8c\u624b\u70b9\u5378\u6cd5\uff0c\u70b9\u662f \u70b9\u7a74\uff0c\u5378\u662f\u5378\u9aa8\u3002\u5207\u65ab\u70b9\u62ff\uff0c\u88d4\u6b63\u76f8\u751f\u3002\u6b63\u662f\u540c\u65f6\u5bf9\u4ed8\u5185\u5916\u4e24\u5bb6\u7684\u4e0a\u4e58\u638c\u6cd5\u3002\u54ce\uff01\u8fd9\u7ea2\u9762\u8001 \u4eba\u4e0d\u5f31\uff0c\u4ed6\u521a\u624d\u8f93\u7684\u90a3\u62db\uff0c\u5927\u7ea6\u662f\u6b3a\u654c\u8fc7\u751a\u3002\u4ed6\u7684\u4e5d\u5bab\u795e\u884c\u638c\uff0c\u53ef\u662f\u6b66\u5f53\u6d3e\u9547\u5c71\u7684\u638c\u6cd5 \u5462\uff01\u201d \u3000\u3000\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6842\u5929\u6f9c\u7684\u529f\u592b\u4e5f\u4fca\u6781\u4e86\uff0c\u7ea2\u9762\u8001\u4eba\u8eab\u5b50\u6ef4\u6e9c\u6e9c\u5730\u8f6c\uff0c\u4ed6\u4e5f\u968f\u7740\u7ea2\u9762\u8001\u4eba \u8f6c\uff0c\u4ed6\u53d1\u638c\u597d\u50cf\u8f6f\u7ef5\u7ef5\u7684\uff0c\u53ef\u662f\u5bf9\u65b9\u7684\u51cc\u5389\u638c\u6cd5\uff0c\u90fd\u7ed9\u4ed6\u968f\u52bf\u5316\u89e3\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd9\u573a\u5bf9\u638c\uff0c\u4e00\u5b9a\u597d\u770b\u6781\u4e86\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5417\uff1f\u201d\u8fd9\u4e24\u4eba\u8eab\u6cd5\uff0c\u5c31 \u5b9b\u5982\u8d70\u9a6c\u706f\u4e00\u6837\uff0c\u500f\u5de6\u500f\u53f3\uff0c\u5ffd\u9006\u5ffd\u987a\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u6211\u770b\u5230\u6708\u5149\u5e95\u4e0b\uff0c\u4e24\u6761\u9ed1\u5f71\uff0c\u8054\u6210\u4e00 \u5708\uff0c\u95ea\u7535\u822c\u75be\u4e00\u8f6c\uff0c\u83ab\u8bf4\u5206\u4e0d\u51fa\u62db\u6570\uff0c\u8fde\u54ea\u4e2a\u662f\u7ea2\u9762\u8001\u4eba\uff0c\u54ea\u4e2a\u662f\u6842\u5929\u6f9c\u4e5f\u5206\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u5085\u9752\u4e3b\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u51fa\u624b\u662f\u5feb\u6781\u4e86\uff0c\u4f46\u7ec6\u770b\u4e4b\u4e0b\u8fd8\u5206\u5f97\u51fa\u5f3a\u5f31\uff0c\u7ea2\u9762\u8001\u4eba\u5982\u6012\u72ee\u640f\u51fb\uff0c \u800c\u6842\u5929\u6f9c\u5219\u5982\u7075\u9e64\u56de\u7fd4\u3002\u7ea2\u9762\u8001\u4eba\u7b52\u4e00\u62db\u90fd\u662f\u91cd\u624b\uff0c\u51f6\u72e0\u6781\u4e86\uff0c\u800c\u6842\u5929\u6f9c\u5374\u95ea\u907f\u5f97\u6070\u5230\u597d \u5904\uff0c\u6709\u597d\u51e0\u62db\u8fde\u6211\u90fd\u770b\u4e0d\u6e05\u4ed6\u662f\u600e\u6837\u907f\u5f00\u3002\u6309\u8bf4\uff0c\u4ee5\u4ed6\u90a3\u6837\u7684\u529f\u529b\uff0c\u654c\u4eba\u4e00\u51fb\u4e0d\u4e2d\uff0c\u4ed6\u5c31\u53ef \u4ee5\u4e58\u865a\u53cd\u51fb\uff0c\u4f46\u5947\u602a\u5f97\u5f88\uff0c\u4ed6\u5374\u53c8\u662f\u8001\u5b88\u4e0d\u653b\uff0c\u751a\u81f3\u654c\u4eba\u660e\u660e\u6709\u4e86\u7834\u7efd\uff0c\u4ed6\u4e5f\u662f\u70b9\u5230\u4e3a\u6b62\uff0c \u6211\u660e\u660e\u770b\u5230\u6709\u4e00\u62db\uff0c\u7ea2\u9762\u8001\u8005\u7528\u2018\u7275\u7f18\u624b\u2019\u5de6\u53f3\u5939\u51fb\uff0c\u6842\u5929\u6f9c\u907f\u8fc7\u6b63\u9762\uff0c\u53cd\u62a2\u8fdb\u53bb\uff0c\u53ea\u8981\u4e00 \u638c\u5207\u4e0b\uff0c\u7ea2\u9762\u8001\u4eba\u975e\u53d7\u91cd\u4f24\u4e0d\u53ef\uff0c\u4ed6\u5374\u4f7f\u51fa\u82b1\u62db\uff0c\u4e34\u65f6\u53d8\u5f0f\uff0c\u653e\u8fc7\u4e86\u673a\u4f1a\u3002\u201d\u51cc\u672a\u51e4\u9053\uff1a \u201c\u8fd9\u6837\u975e\u5403\u4e8f\u4e0d\u53ef\uff01\u7ea2\u9762\u8001\u4eba\u7684\u529f\u529b\u3001\u638c\u6cd5\u4ec5\u7a0d\u900a\u4e8e\u6842\u5929\u6f9c\u800c\u5df2\uff0c\u4ed6\u8fd9\u4e00\u653e\u677e\uff0c\u5f88\u5bb9\u6613\u7ed9\u5bf9 \u65b9\u53cd\u4e58\u4e4b\u673a\u3002\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5417\uff1f\u6211\u770b\u5f97\u7d27\u5f20\u6781\u4e86\uff0c\u6068\u4e0d\u5f97\u60f3\u63d0\u9192\u4ed6\u3002\u518d\u6253\u4e86\u4e00\u9635\uff0c \u7ea2\u9762\u8001\u4eba\u5ffd\u7136\u4e00\u817f\u98de\u8d77\uff0c\u8e22\u6842\u5929\u6f9c\u808b\u4e0b\u7684\u7a74\u9053\uff0c\u6842\u5929\u6f9c\u5728\u638c\u4e00\u515c\u5341\u6b63\u6b63\u515c\u4f4f\u5bf9\u65b9\u7684\u5de6\u8db3\u8db3 \u8ddf\uff0c\u53ea\u8981\u7528\u529b\u4e00\u9001\uff0c\u7acb\u523b\u53ef\u4ee5\u5c06\u654c\u4eba\u629b\u843d\u60ac\u5d16\uff0c\u4ed6\u5c06\u624b\u8155\u4e00\u6c89\uff0c\u5927\u7ea6\u662f\u60f3\u5c06\u654c\u4eba\u6309\u843d\u5730\u4e0a\uff0c \u54ea\u79ef\u538b\u7f13\u5f97\u4e00\u7f13\uff0c\u7acb\u523b\u7ed9\u7ea2\u9762\u8001\u4eba\u65bd\u5c55\u9e33\u9e2f\u8fde\u73af\u817f\uff0c\u5de6\u8db3\u731b\u7684\u5411\u6842\u5929\u6f9c\u80f8\u819b\u8e22\u53bb\uff0c\u6842\u5929\u6f9c\u5927 \u53eb\u4e00\u58f0\uff0c\u53cc\u638c\u4e00\u677e\uff0c\u7ea2\u9762\u8001\u4eba\u5df2\u63a0\u51fa\u6570\u4e08\uff0c\u4e00\u53cd\u8eab\u53c8\u662f\u4e09\u679d\u9a7d\u7bad\uff0c\u6842\u5929\u6f9c\u8fd9\u65f6\u9762\u8272\u6e17\u767d\uff0c\u8eab \u6cd5\u8fdf\u6ede\uff0c\u907f\u4e0d\u4e86\u7b2c\u4e09\u679d\uff0c\u7adf\u7ed9\u5f2f\u7bad\u5c04\u4e2d\u4e86\u5c0f\u8179\u3002\u201d \u3000\u3000\u660c\u6d63\u83b2\u7d27\u5f20\u5730\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u90a3\u4e2a\u5c0f\u59d1\u5a18\u672c\u6765\u662f\u7ad9\u5728\u6211\u8eab\u65c1\u7684\uff0c\u8fd9\u65f6\u7a81\u7136\u51b2\u4e86\u51fa\u53bb\uff0c\u53f3\u624b \u4e00\u6296\uff0c\u4e00\u6839\u957f\u957f\u7684\u5c71\u85e4\u5411\u90a3\u4eba\u629b\u53bb\uff0c\u5de6\u624b\u4e5f\u6253\u51fa\u4e09\u679a\u94a2\u9556\u3002\u90a3\u4e2a\u7ea2\u9762\u8001\u4eba\u5947\u602a\u6781\u4e86\uff0c\u4e00\u89c1\u8fd9 \u4e2a\u5c0f\u59d1\u5a18\u51b2\u6765\uff0c\u4e1d\u6beb\u4e0d\u907f\uff0c\u53cd\u8fce\u4e0a\u524d\u53bb\u8bf4\u9053\uff1a\u201c\u574f\u4eba\u6253\u6b7b\u4e86\uff0c\u5b9d\u5b9d\u8ddf\u6211\u8d70\uff01\u201d\u5c0f\u59d1\u5a18\u731b\u7136\u51fa \u624b\uff0c\u4ed6\u4ecd\u50cf\u6beb\u65e0\u6240\u89c9\u4f3c\u7684\u7f13\u7f13\u8d70\u6765\uff0c\u90a3\u53ef\u7cdf\u5566\uff0c\u4ed6\u7684\u53cc\u8db3\u7ed9\u5c71\u85e4\u7eca\u7740\uff0c\u5de6\u80a9\u4e5f\u4e2d\u4e86\u4e00\u7f25\uff01\u6842 \u5929\u6f9c\u5ffd\u7136\u5927\u58f0\u53eb\u9053\uff1a\u2018\u7af9\u541b\uff0c\u522b\u52a8\u624b\uff0c\u4ed6\u662f\u4f60\u7684\u7238\u7238\uff01\u2019\u7ea2\u9762\u8001\u4eba\u8fde\u58f0\u60e8\u7b11\uff0c\u90a3\u4e2a\u5c0f\u59d1\u5a18\uff0c \u5c31\u5982\u53d7\u4e86\u96f7\u51fb\u4e00\u6837\uff0c\u5728\u6708\u5149\u4e0b\u5168\u8eab\u98a4\u6296\uff0c\u8fd9\u65f6\u6211\u5ffd\u89c9\u8111\u540e\u98ce\u58f0\u98d2\u7136\u3001\u84e6\u7136\u95f4\u5085\u4f2f\u4f2f\u4e00\u638c\u5c31\u5c06 \u6211\u63a8\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u6211\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u56db\u4e2a\u7a7f\u9ed1\u8863\u7684\u4eba\uff1b\u4f3c\u98de\u9e1f\u822c\u6251\u4e86\u8fdb\u6765\uff0c\u6709\u4e00\u4e2a\u5df2\u51b2\u8fd1\u90a3 \u4e2a\u5c0f\u59d1\u5a18\u4e86\uff0c\u7ea2\u9762\u8001\u4eba\u6012\u543c\u4e00\u58f0\uff0c\u53cc\u8db3\u4e00\u8df3\uff0c\u5c71\u85e4\u88c2\u6210\u51e0\u6bb5\uff0c\u6a2a\u98de\u51fa\u53bb\uff0c\u90a3\u4e2a\u9ed1\u8863\u6c49\u5b50\u624b\u521a \u6293\u5230\u5c0f\u59d1\u5a18\u7684\u80a9\u5934,\u5c31\u88ab\u7ea2\u9762\u8001\u4eba\u4e00\u628a\u62b1\u4f4f\uff0c\u5012\u5728\u5730\u4e0a\u4e00\u6eda\uff0c\u7adf\u7136\u4e00\u540c\u4ece\u5ced\u58c1\u6eda\u4e0b\u53bb\u4e86\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u542c\u5f97\u8840\u8109\u5076\u5f20\uff0c\u201c\u554a\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u4e2a\u7ea2\u9762\u8001\u4eba\u7adf\u7136\u548c\u654c\u4eba\u540c\u5f52\u4e8e\u5c3d\uff0c\u53ef \u60dc\uff01\u201d\u5192\u6d63\u83b2\u4e0d\u7406\u51cc\u672a\u98ce\u6253\u5c94\uff0c\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u90a3\u4e2a\u5c0f\u59d1\u5a18\u89c1\u7ea2\u9762\u8001\u4eba\u62b1\u7740\u4e00\u4e2a\u9ed1\u8863\u6c49\u5b50\u6eda\u4e0b \u60ac\u5d16\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u84e6\u7136\u53d1\u72c2\u4e00\u6837\uff0c\u98de\u5954\u5411\u524d\uff0c\u5728\u60ac\u5d16\u8fb9\u8e0a\u8eab\u4e00\u8dc3\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u4e5f\u8df3\u4e0b\u53bb\u4e86\uff0c \u6211\u8df3\u51fa\u53bb\u6551\uff0c\u5df2\u6765\u4e0d\u53ca\uff01\u8033\u8fb9\u53ea\u542c\u5f97\u6842\u5929\u6f9c\u7684\u60e8\u53eb\u58f0\uff0c\u63a5\u7740\u662f\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u63a5\u7740\u662f\u5085 \u4f2f\u4f2f\u5927\u58f0\u547c\u5524\uff0c\u53eb\u6211\u56de\u6765\uff01\u54ce\u5440\uff01\u90a3\u5c0f\u59d1\u5a18\u771f\u662f\uff0c\u90a3\u8df3\u4e0b\u60ac\u5d16\u4e4b\u524d\u7684\u795e\u60c5\u53c8\u771f\u53ef\u6015\uff01\u201d\u5192\u6d63 \u83b2\u8bf4\u65f6\uff0c\u9762\u8272\u60e8\u767d\uff0c\u58f0\u97f3\u98a4\u6296\uff0c\u5c4b\u5b50\u91cc\u84e6\u7136\u50cf\u6b7b\u4e00\u6837\u7684\u6c89\u5bc2\uff0c\u9759\u5f97\u542c\u89c1\u5404\u4eba\u7684\u5fc3\u8df3\u58f0\uff01 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5085\u9752\u4e3b\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6765\u7684\u90a3\u56db\u4e2a\u9ed1\u8863\u6c49\u5b50\uff0c\u90fd\u662f\u6e05\u5bab\u5927\u5185\u7684\u9ad8\u624b\u3002\u7ed9\u7ea2\u9762\u8001 \u4eba\u62b1\u7740\u6eda\u4e0b\u60ac\u5d16\u7684\u90a3\u4e2a\u6211\u8ba4\u5f97\uff0c\u7ef0\u53f7\u53eb\u505a\u201c\u516b\u81c2\u54ea\u53f1\u2019\u7126\u9738\uff0c\u4ee5\u524d\u662f\u6a2a\u884c\u6c5f\u6e56\u7684\u5927\u76d7\uff0c\u6e05\u5175 \u5165\u5173\u4e4b\u540e\uff0c\u4ed6\u5e26\u4e00\u5e2e\u6d41\u5bc7\u6295\u6548\u6e05\u519b\uff0c\u540e\u6765\u542c\u8bf4\u505a\u4e86\u5927\u5185\u4f8d\u536b\uff0c\u4ed6\u7684\u529f\u592b\u7edd\u4e0d\u5728\u6211\u4e4b\u4e0b\uff0c\u6211\u6765 \u4e0d\u53ca\u8bf4\u8bdd\uff0c\u53ea\u597d\u4e00\u638c\u5c06\u6d63\u83b2\u63a8\u5f00\u3002\u53e6\u4e09\u4e2a\u9ed1\u8863\u4f8d\u536b\uff0c\u6211\u4e0d\u8ba4\u5f97\uff0c\u4f46\u4e00\u770b\u8eab\u6cd5\uff0c\u90fd\u662f\u4e00\u7b49\u9ad8 \u624b\u3002\u4ed6\u4eec\u5728\u5251\u9601\u4e0a\u4e00\u73b0\u8eab\uff0c\u7acb\u523b\u5c31\u5411\u6842\u5929\u6f9c\u5954\u53bb\uff0c\u6211\u518d\u4e5f\u6309\u637a\u4e0d\u4f4f\uff0c\u6025\u5fd9\u62d4\u5251\u98de\u8eab\uff0c\u62a2\u5728\u5934 \u91cc\uff0c\u66ff\u6842\u5929\u6f9c\u6321\u4e86\u4e00\u9635\u3002\u201d\u4ed6\u505c\u4e86\u4e00\u505c\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5e78\u4e8f\u90a3\u4e2a\u6b66\u529f\u6700\u5f3a\u7684\u7126\u9738\uff0c\u7ed9 \u7ea2\u9762\u8001\u4eba\u62b1\u7740\u6eda\u4e0b\u7edd\u58c1\uff0c\u8981\u4e0d\u7136\uff0c\u6211\u4eec\u90a3\u665a\uff0c\u6050\u6015\u90fd\u4f1a\u8840\u6e85\u8352\u5c71\uff01\u201d\u674e\u601d\u6c38\u6124\u7136\u8bf4\u9053\uff1a\u201c\u6ee1 \u6d32\u9791\u5b50\u4e5f\u771f\u72e0\uff0c\u51e0\u5341\u5e74\u4e86\u90fd\u4e0d\u80af\u653e\u8fc7\u5148\u7956\u548c\u5f20\u732e\u5fe0\u624b\u4e0b\u7684\u77e5\u540d\u4e4b\u58eb\uff0c\u4ed6\u4eec\u8981\u65a9\u8349\u9664\u6839\u3002\u6842\u5929 \u6f9c\u4e5f\u771f\u662f\uff0c\u5148\u7236\u66fe\u51e0\u6b21\u6d3e\u4eba\u627e\u4ed6\uff0c\u5982\u679c\u4ed6\u548c\u6211\u4eec\u5927\u4f19\u5728\u4e00\u8d77\uff0c\u5c31\u6ca1\u6709\u4e8b\u5566\uff0c\u504f\u504f\u4ed6\u5374\u8981\u53bb \u2018\u9690\u5c45\u2019\uff0c\u8fd9\u4e2a\u65f6\u5019\u56fd\u5bb6\u90fd\u5df2\u4e0d\u4fdd\uff0c\u53c8\u600e\u5bb9\u4f60\u505a\u4e16\u5916\u9ad8\u4eba\uff1f\u201d \u3000\u3000\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6211\u5c31\u662f\u89c1\u90a3\u4e9b\u536b\u58eb\u8fd9\u4e48\u72e0\uff0c\u5c31\u8c41\u51fa\u6027\u547d\u548c\u4ed6\u4eec\u62fc\u5566\uff01\u4f46\u90a3\u4e09\u4e2a\u536b\u58eb\uff0c\u6b66\u529f \u5b9e\u5728\u9ad8\u5f3a\uff0c\u6211\u6ca1\u6cd5\u5168\u6570\u62e6\u4f4f\uff0c\u7ed3\u679c\u8fd8\u662f\u7ed9\u4e00\u4e2a\u51b2\u8fc7\u53bb\u6253\u6842\u5929\u6f9c\uff0c\u6211\u7ed9\u4e24\u4e2a\u536b\u58eb\u7eca\u4f4f\uff0c\u8131\u4e0d\u4e86 \u8eab\uff0c\u8fde\u5206\u795e\u770b\u671b\u4e5f\u4e0d\u53ef\u80fd\u3002\u6253\u4e86\u4e00\u4f1a\uff0c\u542c\u89c1\u6d63\u83b2\u9ad8\u58f0\u53eb\u558a\uff0c\u6211\u624d\u77e5\u9053\u90a3\u4e2a\u53bb\u6349\u6842\u5929\u6f9c\u7684\u536b \u58eb\uff0c\u5df2\u7ecf\u7ed9\u9664\u6389\u4e86\u3002 \u3000\u3000\u5192\u6d63\u83b2\u9053\uff1a\u201c\u6211\u8dd1\u8fc7\u53bb\u5e2e\u6842\u5929\u6f9c\uff0c\u5374\u53cd\u662f\u4ed6\u5e2e\u4e86\u6211\uff0c\u90a3\u4e2a\u536b\u58eb\uff0c\u624b\u4f7f\u4e00\u628a\u7ea2\u6bdb\u5200\uff0c\u975e\u5e38 \u5389\u5bb3\u3002\u6211\u7684\u5251\u78b0\u4e0d\u4e0a\u4ed6\uff0c\u53ea\u7ed9\u5200\u98ce\u4e00\u8361\u5c31\u8361\u5f00\u5566\uff01\u6211\u4e5f\u4e0d\u7ba1\uff0c\u5c55\u5f00\u5c0f\u5de7\u529f\u592b\uff0c\u770b\u4ed6\u5feb\u8981\u5f97\u624b \u65f6\u3002\u5c31\u4ece\u65c1\u8fb9\u7ed9\u4ed6\u4e00\u5251\u3002\u90a3\u6842\u5929\u6f9c\u7684\u6b66\u529f\u771f\u662f\u60ca\u4eba\uff0c\u4ed6\u9762\u8272\u5df2\u60e8\u767d\u5982\u7eb8\uff0c\u8eab\u5b50\u4e5f\u6447\u6447\u6643\u6643\uff0c \u4ed6\u8fd8\u662f\u4e00\u624b\u63a9\u8179\uff0c\u5355\u638c\u5e94\u6218\uff0c\u90a3\u4e2a\u536b\u58eb\u5200\u5149\u95ea\u95ea\uff0c\u53ea\u5728\u4ed6\u8eab\u8fb9\u6253\u8f6c\u8f6c\uff0c\u8fd8\u4e0d\u6562\u771f\u4e2a\u903c\u8fd1\u8eab \u53bb\u3002\u5927\u7ea6\u662f\u6015\u4ed6\u7684\u5927\u529b\u9e70\u722a\u7684\u529f\u592b\uff0c\u6253\u4e86\u4e00\u4f1a\uff0c\u90a3\u4e2a\u536b\u58eb\u597d\u50cf\u7126\u8e81\u8d77\u6765\u4e86\uff0c\u731b\u7136\u4e00\u4e2a\u65cb\u8eab\uff0c \u2018\u4e91\u9f99\u4e09\u73b0\u2019\uff0c\u5530\uff01\u5530\uff01\u5530\uff01\u4e00\u8fde\u4e09\u5200\uff0c\u5411\u6211\u523a\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u2018\u5148\u628a\u4f60\u8fd9\u4e2a\u4e2b\u5934\u9664\u53bb\uff01\u2019 \u5728\u4ed6\u53d1\u51fa\u7b2c\u4e8c\u5200\u65f6\uff0c\u6211\u7684\u5251\u5c31\u7ed9\u78d5\u98de\u4e86\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u8bf4\u5230\u624b\u4e2d\u7684\u9752\u94a2\u521b\u7ed9\u9ed1\u8863\u536b\u58eb\u4e00\u5200\u78d5\u98de\u65f6\uff0c\u674e\u601d\u6c38\u4e0d\u7531\u5f97\u558a\u51fa\u58f0\u6765\u3002\u51cc\u672a\u98ce\u5374\u5410 \u4e86\u53e3\u6c14\uff0c\u95f2\u95f2\u5730\u8bf4\u9053\uff1a\u201c\u8fd9\u9ed1\u8863\u536b\u58eb\u8981\u69fd\u4e86\uff01\u201d\u5192\u6d63\u83b2\u60ca\u5947\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u4f60\u600e\u7684\u597d\u50cf\u5f53\u573a \u770b\u89c1\u4e00\u6837\uff01\u90a3\u9ed1\u536b\u58eb\u7b2c\u4e00\u5200\u5c06\u6211\u8feb\u9000\u4e24\u6b65\uff0c\u7b2c\u4e8c\u5200\u5c06\u6211\u7684\u5175\u5203\u78d5\u98de\uff0c\u7b2c\u4e09\u5200\u9a6c\u4e0a\u5f53\u5934\u5288\u4e0b\uff0c \u6211\u6beb\u65e0\u529e\u6cd5\u62b5\u6297\uff0c\u53ea\u6709\u95ed\u76ee\u5f85\u6b7b\u3002\u4e0d\u6599\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u90a3\u536b\u58eb\u60e8\u53eb\u4e00\u58f0\uff0c\u6211\u7741\u773c\u4e00\u770b\uff1a\u53ea \u89c1\u6842\u5929\u6f9c\u5df2\u4e00\u624b\u5c06\u90a3\u4e2a\u536b\u58eb\u6293\u8d77\uff0c\u90a3\u4e2a\u536b\u58eb\u4e5f\u771f\u4e86\u5f97\uff0c\u84e6\u5730\u5934\u5411\u540e\u5f2f\uff0c\u53cd\u624b\u5411\u6813\u5929\u6f9c\u8170\u95f4\u4e00 \u6233\uff0c\u6842\u5929\u6f9c\u6012\u543c\u4e00\u58f0\uff0c\u628a\u63a9\u7740\u5c0f\u8179\u7684\u624b\u4e5f\u4f38\u4e86\u51fa\u6765\uff0c\u4ee5\u624b\u4e00\u6495\uff0c\u7acb\u523b\u628a\u90a3\u4e2a\u536b\u58eb\u6495\u6210\u4e24\u7247\uff0c \u8840\u6dcb\u6dcb\u53ef\u6015\u6781\u4e86\uff0c\u6211\u5413\u5f97\u5168\u8eab\u762b\u8f6f\uff0c\u6842\u5929\u6f9c\u628a\u90a3\u4e24\u7247\u8840\u4eba\u629b\u4e0b\u6df1\u8c37\uff0c\u7528\u624b\u63a8\u4e86\u6211\u4e00\u4e0b\uff0c\u6307\u4e00 \u6307\u5085\u4f2f\u4f2f\u8fd9\u8fb9\uff0c\u597d\u50cf\u53eb\u6211\u53bb\u5e2e\u624b\u4f3c\u7684\u3002\u6211\u4e00\u770b\u4ed6\uff0c\u8179\u90e8\u8840\u5982\u6cc9\u6d8c\uff0c\u5168\u8eab\u7684\u8863\u670d\u90fd\u67d3\u7ea2\u4e86\u3002\u6211 \u6025\u5fd9\u628a\u5934\u5dfe\u6495\u4e0b\uff0c\u7ed9\u4ed6\u5305\u4e0a\uff0c\u4ed6\u5750\u5728\u5730\u4e0a\uff0c\u518d\u4e5f\u8bf4\u4e0d\u51fa\u58f0\u5566\uff01\u4f46\u8fd8\u662f\u8fde\u8fde\u6307\u7740\u5085\u4f2f\u4f2f\uff0c\u597d\u50cf \u5f88\u751f\u6c14\u7684\u6837\u5b50\uff0c\u50ac\u6211\u524d\u53bb\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u8bf4\u5230\u8fd9\u91cc\uff0c\u624d\u677e\u4e86\u53e3\u6c14\uff0c\u51cc\u672a\u98ce\u8d5e\u9053\uff1a\u201c\u597d\u4e2a\u5927\u529b\u9e70\u722a\u795e\u529f\uff01\u654c\u4eba\u53ea\u8981\u4e00\u5206\u795e\uff0c \u7acb\u523b\u5c31\u88ab\u4ed6\u4e58\u865a\u800c\u5165\u4e86\uff0c\u53ef\u60dc\u4ed6\u53d7\u4e86\u91cd\u4f24\u5728\u524d\uff0c\u8f6c\u52a8\u4e0d\u7075\uff0c\u5f97\u624b\u4e4b\u540e\uff0c\u8fd8\u662f\u53d7\u4e86\u654c\u4eba\u6697 \u7b97\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u548c\u53e6\u5916\u4e24\u4e2a\u536b\u58eb\u53ae\u62fc\uff0c\u6b63\u611f\u5403\u529b\uff0c\u5ffd\u542c\u5f97\u6d63\u83b2\u5927\u547c\uff1a\u2018\u6211\u4eec\u5df2\u6253 \u6b7b\u4e00\u4e2a\u4e86\uff0c\u2019\u5979\u4e5f\u771f\u7cbe\u7075\uff0c\u8fdc\u8fdc\u5730\u628a\u94c1\u83b2\u5b50\u62fc\u547d\u6253\u6765\uff0c\u5979\u77e5\u9053\u6211\u6709\u53cc\u8896\u63a5\u6697\u5668\u7684\u73a9\u827a\uff0c\u4e0d\u6015 \u8bef\u4f24\uff0c\u90a3\u4e24\u4e2a\u536b\u58eb\u5374\u7ed9\u94c1\u83b2\u5b50\u6253\u5f97\u4e1c\u8eb2\u897f\u907f\uff0c\u867d\u65e0\u6cd5\u4f24\u7740\u4ed6\u4eec\uff0c\u4e5f\u591f\u4ed6\u4eec\u53d7\u5566\u3002\u90a3\u4e24\u4e2a\u536b\u58eb \u4e00\u56de\u907f\u6697\u5668\uff0c\u4e00\u9762\u626d\u5934\u5f20\u671b\uff0c\u5927\u7ea6\u662f\u679c\u7136\u53d1\u73b0\u540c\u4f34\u4e0d\u89c1\u4e86\uff0c\u9f50\u58f0\u60ca\u547c\uff0c\u8fde\u9053\uff1a\u2018\u98ce\u7d27\uff01\u2019\u6211 \u4e58\u52bf\u98de\u8eab\u6251\u53bb\uff0c\u7528\u65e0\u6781\u5251\u4e2d\u7684\u2018\u5c55\u7ffc\u51cc\u4e91\u2019\u7edd\u62db\uff0c\u4e00\u5251\u4e00\u4e2a\uff0c\u5168\u90e8\u4e86\u7ed3\uff01\u771f\u60f3\u4e0d\u5230\u8fd9\u4e24\u4e2a\u5bf9 \u624b\u5f3a\u654c\uff0c\u88ab\u6211\u5982\u6b64\u5bb9\u6613\u5730\u523a\u6389\uff01\u201d \u3000\u3000\u5085\u9752\u4e3b\u505c\u4e0b\u6765\u559d\u4e86\u4e00\u53e3\u8336\uff0c\u7528\u624b\u6307\u6572\u77f3\u684c\u9762\uff0c\u5f97\u5f97\u6709\u58f0\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u654c\u4eba\u662f\u5168\u6570\u6253\u6b7b \u4e86\uff0c\u53ef\u662f\u6842\u5929\u6f9c\u4e5f\u5df2\u5944\u5944\u4e00\u606f\u3002\u6211\u6025\u5fd9\u8dd1\u8fc7\u53bb\u770b\u4ed6\uff0c\u53ea\u89c1\u4ed6\u5168\u8eab\u6d74\u8840\u3002\u6211\u7528\u91d1\u521b\u836f\u7ed9\u4ed6\u6b62\u4e86 \u8840\uff0c\u518d\u7528\u5c71\u8fb9\u7684\u6cc9\u6c34\u7ed9\u4ed6\u63e9\u62b9\u5e72\u51c0\uff0c\u53ea\u89c1\u80f8\u8863\u5df2\u7834\uff0c\u80f8\u819b\u4e0a\u6709\u4e2a\u978b\u5370\uff0c\u60f3\u6765\u5c31\u662f\u7ed9\u7ea2\u9762\u8001\u4eba \u8fde\u73af\u817f\u8e22\u4f24\u7684\uff0c\u7ea2\u9762\u8001\u4eba\u8fd9\u811a\u771f\u72e0\uff0c\u53ef\u662f\u6842\u5929\u6f9c\u5c45\u7136\u80fd\u633a\u5f97\u8fd9\u4e48\u4e9b\u65f6\u5019\uff0c\u8fd8\u80fd\u91cd\u4f24\u4e4b\u540e\u638c\u6bd9 \u654c\u4eba\uff0c\u529f\u529b\u7684\u6df1\u539a\u771f\u662f\u6211\u5e73\u751f\u4ec5\u89c1\uff01\u9664\u4e86\u80f8\u90e8\u7684\u4f24\u5916\uff0c\u4ed6\u7684\u5c0f\u8179\u4e5f\u7ed9\u9a7d\u7bad\u7a7f\u4e86\u4e00\u4e2a\u6d1e\uff0c\u8fde\u80a0 \u5b50\u4e5f\u770b\u5f97\u89c1\u5566\u3002\u53e6\u5916\u80c1\u4e0b\u8fd8\u7ed9\u9ed1\u8863\u536b\u58eb\u70b9\u4e2d\u4e86\u2018\u6108\u6c14\u7a74\u2019\u3002\u6211\u770b\u4ed6\u7684\u795e\u60c5\uff0c\u77e5\u9053\u4ed6\u6781\u529b\u8fd0\u529f \u95ed\u4f4f\u7a74\u9053\u3002\u6211\u6025\u5fd9\u7ed9\u4ed6\u89e3\u5f00\uff0c\u53ea\u662f\u65f6\u95f4\u8fc7\u4e45\uff0c\u89e3\u5f00\u4e86\u7a74\u9053\uff0c\u4ed6\u4e5f\u53ea\u80fd\u6296\u52a8\uff0c\u8bdd\u5df2\u662f\u8bf4\u4e0d\u51fa \u4e86\uff0c\u6211\u62b1\u4ed6\u56de\u8f6c\u5c4b\u5185\uff0c\u518d\u4ed4\u7ec6\u68c0\u89c6\uff0c\u6211\u7684\u533b\u672f\u867d\u7136\u81ea\u4fe1\u5e76\u975e\u5eb8\u624b\uff0c\u53ef\u662f\u5230\u5e95\u4e0d\u80fd\u771f\u4e2a\u8d77\u6b7b\u56de \u751f\uff0c\u4ed6\u4f24\u5f97\u8fd9\u6837\u91cd\uff0c\u7cbe\u795e\u6c14\u529b\u90fd\u8017\u5c3d\uff0c\u8fd9\u53eb\u6211\u5982\u4f55\u80fd\u6551\u3002\u6211\u671b\u7740\u4ed6\u6d41\u6cea\uff0c\u4ed6\u5374\u5ffd\u7136\u6323\u624e\u7740\u7528 \u624b\u6307\u5728\u5730\u4e0a\u7528\u529b\u5730\u5212\uff01\u6296\u6296\u7d22\u7d22\u5730\u5212\u4e86\u4e00\u884c\u5927\u5b57\uff0c\u90a3\u884c\u5b57\u662f\uff1a\u2018\u8bf7\u5230\u6ec7\u4e1c\u4e94\u9f99\u5e2e\uff0c\u6709\u4e00 \u4e2a\u2026\u2026\u201d\u521d\u5199\u65f6\u6ce5\u571f\u7eb7\u98de\uff0c\u6bcf\u4e2a\u5b57\u90fd\u5165\u571f\u6570\u5206\uff0c\u540e\u6765\u8d8a\u5199\u8d8a\u6162\uff0c\u6ce5\u571f\u4e0a\u53ea\u80fd\u7a00\u7a00\u6d6e\u6d6e\u7684\u770b\u5230 \u4e00\u70b9\u5b57\u8ff9\uff0c\u5c1a\u672a\u5199\u5b8c\uff0c\u4ed6\u5c31\u5ffd\u7136\u65ad\u4e86\u6c14\u5566\uff01\u201d \u3000\u3000\u5085\u9752\u4e3b\u8bb2\u5b8c\u4e4b\u540e\uff0c\u542c\u4f17\u9eef\u7136\u3002\u826f\u4e45\uff0c\u51cc\u672a\u98ce\u62ac\u5934\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u8fd9\u4e2a\u9ec4\u886b\u5c11\u5e74\u53c8\u662f\u600e\u6837\u6765 \u7684\uff1f\u4ed6\u548c\u6842\u5929\u6f9c\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u5440\uff01\u5f53\u65f6\u6211\u8fde\u6842\u5929\u6f9c\u7684\u59d3\u540d\u8fd8\u4e0d\u77e5\u9053\uff0c\u4ed6\u53c8\u5199\u5f97\u6ca1\u5934\u6ca1\u5c3e\uff0c\u4e0d \u8fc7\u6211\u60f3\u8fd9\u4f4d\u6b66\u6797\u4fa0\u9690\uff0c\u4e34\u7ec8\u65f6\u8fd8\u6bb7\u6bb7\u4ee5\u6b64\u4e3a\u5ff5\uff0c\u4ed6\u4eca\u665a\u4e4b\u4e8b\uff0c\u4e00\u5b9a\u662f\u548c\u4e94\u9f99\u5e2e\u6709\u5173\u7cfb\u7684\u4e86\u3002 \u6211\u82e5\u4e0d\u66ff\u4ed6\u529e\u5230\uff0c\u4ed6\u4e00\u5b9a\u6b7b\u4e0d\u7791\u76ee\u3002\u201d\u63a5\u7740\u4ed6\u53c8\u5728\u70db\u5149\u6447\u66f3\u4e2d\u8bf4\u51fa\u7b2c\u4e8c\u4e2a\u52a8\u4eba\u5fc3\u9b44\u7684\u6545\u4e8b\u3002 \u3000\u3000\u539f\u6765\u5085\u9752\u4e3b\u548c\u5192\u6d63\u83b2\u4eba\u5ddd\uff0c\u662f\u5f53\u65e5\u7fa4\u96c4\u5927\u95f9\u4e94\u53f0\u5c71\u4e4b\u540e\uff0c\u5728\u6b66\u5bb6\u5e84\u4e2d\u5206\u6d3e\u7684\uff08\u89c1\u7b2c\u4e09 \u56de\uff09\u3002\u5085\u9752\u4e3b\u5728\u6842\u5929\u6f9c\u6b7b\u540e\u7b2c\u4e8c\u65e5\u8fc7\u4e86\u5251\u9601\uff0c\u4e00\u8def\u5357\u884c\uff0c\u6cbf\u9014\u89c1\u5175\u9a6c\u5f80\u6765\uff0c\u4ed6\u731c\u56db\u5ddd\u5de1\u629a\u7f57 \u68ee\u4e00\u5b9a\u5df2\u548c\u5434\u4e09\u6842\u6709\u4e86\u8054\u7edc\uff0c\u56e0\u6b64\u8c03\u5175\u9063\u5c06\uff0c\u51c6\u5907\u5e94\u53d8\u4e86\u3002\u4ed6\u4f9d\u7740\u97e9\u5fd7\u90a6\u5728\u6b66\u5bb6\u5e84\u7ed9\u4ed6\u7684\u5730 \u5740\uff0c\u627e\u5230\u4e86\u56db\u5ddd\u5929\u5730\u4f1a\u7684\u8235\u4e3b\uff0c\u4ea4\u4ee3\u4e86\u4e00\u4e0b\uff0c\u544a\u8bc9\u4ed6\u4eec\u5434\u4e09\u6842\u56fe\u8c0b\u53cd\u6e05\u7684\u4e8b\u60c5\uff0c\u53eb\u4ed6\u4eec\u4e5f\u51c6 \u5907\u5e94\u53d8\uff0c\u4ea4\u4ee3\u5b8c\u6bd5\uff0c\u5c31\u81ea\u5ddd\u5165\u6ec7\u3002\u884c\u4e86\u4e8c\u5341\u591a\u5929\uff0c\u5230\u4e86\u6ec7\u4e1c\uff0c\u4e00\u8def\u6253\u542c\uff0c\u5374\u63a2\u4e0d\u51fa\u4e94\u9f99\u5e2e\u7684 \u6240\u5728\uff0c\u751a\u81f3\u4e94\u9f99\u5e2e\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u5e2e\u4f1a\u4e5f\u4e0d\u6e05\u695a\u3002\u4e00\u65e5\u5230\u4e86\u6ec7\u4e1c\u7684\u6cbe\u76ca\uff0c\u5728\u79bb\u57ce\u767e\u4f59\u91cc\u7684\u4e00 \u4e2a\u5c0f\u6751\u9547\uff0c\u5ffd\u7136\u89c1\u6709\u5341\u591a\u4e2a\u5927\u6c49\uff0c\u4e00\u4e2a\u8ddf\u7740\u4e00\u4e2a\uff0c\u8d70\u8fdb\u4e00\u95f4\u9152\u5e97\u3002\u8fd9\u5341\u591a\u4e2a\u6c49\u5b50\uff0c\u4e2a\u4e2a\u6b65\u5c65 \u77eb\u5065\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u6c5f\u6e56\u4eba\u7269\u3002\u5085\u9752\u4e3b\u597d\u5947\u5fc3\u8d77\uff0c\u4e5f\u548c\u5192\u6d63\u83b2\u8ddf\u4e86\u8fdb\u53bb\u3002\u5165\u5230\u9152\u5e97\uff0c\u53ea\u89c1\u4e2a\u4eba \u8eba\u5728\u5730\u4e0a\uff0c\u9762\u5982\u91d1\u7eb8\uff0c\u90a3\u4e9b\u5927\u6c49\u56f4\u7740\u4ed6\uff0c\u6709\u4eba\u7ed9\u4ed6\u63a8\u8840\u8fc7\u5bab\uff0c\u53ef\u662f\u8fd9\u4eba\u4ecd\u662f\u660f\u660f\u8ff7\u8ff7\u7684\u7761 \u7740\uff0c\u4e1d\u6beb\u6ca1\u6709\u8d77\u8272\u3002 \u3000\u3000\u5085\u9752\u4e3b\u80cc\u7740\u836f\u7bb1\uff0c\u672c\u6765\u5c31\u662f\u6c5f\u6e56\u90ce\u4e2d\u6253\u626e\uff0c\u4ed6\u5c31\u4e0d\u5ba2\u6c14\u5730\u6324\u5f00\u4e86\u4f17\u4eba\u4e0a\u524d\u770b\u671b\u3002\u6709\u4e00\u4e2a \u6c49\u5b50\u9053\uff1a\u201c\u4f60\u770b\u4ec0\u4e48\uff1f\u4ed6\u7684\u4f24\u4e0d\u662f\u4f60\u80fd\u533b\u7684\uff01\u201d\u5085\u9752\u4e3b\u4e00\u770b\uff0c\u5c31\u77e5\u9053\u8fd9\u4eba\u662f\u53d7\u94c1\u6c99\u638c\u4f24\u4e86\u7a74 \u9053\uff0c\u7684\u786e\u4e0d\u662f\u666e\u901a\u90ce\u4e2d\u6240\u80fd\u533b\u6cbb\uff0c\u5c31\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u4f24\u6211\u8fd8\u80fd\u6cbb\uff0c\u4ed6\u53d7\u4f24\u4e4b\u540e\uff0c\u5230\u73b0\u5728\u8fd8\u672a\u8fc7 \u4e8c\u5341\u56db\u4e2a\u65f6\u8fb0\u561b\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u5468\u56f4\u7684\u6c49\u5b50\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u5fd9\u606d\u606d\u656c\u656c\u5730\u8bf7\u4ed6\u533b\u6cbb\u3002\u4ed6\u8fc7\u53bb \u66ff\u90a3\u4e2a\u53d7\u4f24\u6c49\u5b50\u63a8\u62ff\uff0c\u4e00\u4e0b\u5b50\u5c31\u89e3\u5f00\u4e86\u7a74\u9053\uff0c\u4e09\u4e94\u4e0b\u5c31\u6d3b\u4e86\u8840\u8109\uff0c\u4e0d\u8fc7\u4e00\u4f1a\uff0c\u90a3\u6c49\u5b50\u7a81\u7136\u54c7 \u7684\u4e00\u58f0\u5410\u51fa\u4e86\u4e00\u53e3\u6de4\u8840\uff0c\u5f20\u53e3\u9a82\u9053\uff1a\u201c\u6211\u8981\u8e0f\u5e73\u4f60\u8fd9\u4e94\u9f99\u5e2e\u5c0f\u5c0f\u7684\u5c71\u5be8\uff01\u201d\u5085\u9752\u4e3b\u542c\u4e86\uff0c\u4e0d \u7981\u5927\u559c\uff0c\u6b63\u662f\uff1a\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\u3002\u627e\u4e86\u8fd9\u4e48\u591a\u5929\u7684\u4e94\u9f99\u5e2e\uff0c\u7adf\u7136\u4ece\u8fd9\u4e2a\u6c49 \u5b50\u53e3\u4e2d\uff0c\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4e2a\u53d7\u4f24\u7684\u6c49\u5b50\u60a0\u60a0\u9192\u8f6c\uff0c\u89c1\u4f17\u5f1f\u5144\uff0c\u56f4\u5728\u8eab\u8fb9\uff0c\u53c8\u6709\u4e00\u4e2a\u964c\u751f\u7684\u8001\u8005\u7ed9\u81ea\u5df1\u63a8\u62ff\uff0c\u5341 \u5206\u60ca\u8be7\u3002\u5085\u9752\u4e3b\u7b11\u9053\uff1a\u201c\u4e0d\u59a8\u4e8b\u4e86\uff0c\u518d\u5c06\u606f\u4e24\u5929\uff0c\u5305\u4f60\u884c\u52a8\u5982\u5e38\u3002\u201d\u4f17\u4eba\u89c1\u4ed6\u533b\u672f\u5982\u6b64\u7cbe \u5999\uff0c\u53c8\u662f\u60ca\u5947\uff0c\u53c8\u662f\u4f69\u670d\u3002\u4e00\u4e2a\u77ed\u5c0f\u7cbe\u608d\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u597d\u50cf\u662f\u8fd9\u4f19\u4eba\u7684\u5927\u54e5\uff0c\u8d70\u8fc7\u6765\u5531\u4e86\u4e2a \u80a5\u558f\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5148\u751f\u6551\u4e86\u6211\u7684\u5144\u5f1f\uff01\u6562\u95ee\u5c0a\u59d3\u5927\u540d\uff1f\u201d\u81ea\u6000\u4e2d\u6293\u4e86\u4e00\u628a\u91d1\u74dc\u5b50\uff0c\u9012\u8fc7\u53bb \u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u4e1c\u897f\uff0c\u4e0d\u6562\u8a00\u916c\uff0c\u53ea\u662f\u804a\u8868\u656c\u610f\u800c\u5df2\u3002\u201d\u5085\u9752\u4e3b\u5fae\u5fae\u4e00\u7b11\uff0c\u63a8\u5f00\u4e86\u4ed6\u7684\u624b\u9053\uff1a \u201c\u916c\u52b3\u6211\u662f\u8981\u7684\uff0c\u53ea\u662f\u4e0d\u8981\u91d1\u5b50\uff01\u201d\u90a3\u6c49\u5b50\u6115\u7136\u95ee\u9053\uff1a\u201c\u4f60\u8981\u4ec0\u4e48\uff1f\u201d\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6211\u8981 \u7684\u662f\u2018\u4e94\u9f99\u5e2e\u2019\uff0c\u8bf7\u4f60\u544a\u8bc9\u6211\u4e94\u9f99\u5e2e\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u4f60\u4eec\u548c\u5b83\u6709\u4ec0\u4e48\u8fc7\u8282\uff1f\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u56db\u5468\u7684\u5341\u51e0\u6761\u5927\u6c49\uff0c\u90fd\u54c4\u52a8\u8d77\u6765\uff0c\u4e03\u5634\u516b\u820c\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u95ee\u8fd9\u4e2a\u5e72\u561b\uff1f\u201d \u201c\u4f60\u548c\u4e94\u9f99\u5e2e\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u2026\u2026\u4e3a\u9996\u7684\u6c49\u5b50\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u538b\u7740\u4f17\u4eba \u9053\uff1a\u201c\u6309\u8bf4\u4f60\u6551\u4e86\u6211\u4eec\u7684\u5144\u5f1f\uff0c\u6211\u4eec\u5e94\u5f53\u544a\u8bc9\u4f60\u3002\u53ef\u662f\u8fd9\u4e8b\u5173\u7cfb\u592a\u5927\uff0c\u6211\u4eec\u5f97\u5148\u77e5\u9053\u4f60\u7684\u6765 \u5386\u3002\u201d\u5085\u9752\u4e3b\u7b11\u9053\uff1a\u201c\u6211\u59d3\u5085\uff0c\u8d31\u5b57\u9752\u4e3b\uff0c\u548c\u4e94\u9f99\u5e2e\u4e5f\u6709\u70b9\u5c0f\u5c0f\u7684\u8fc7\u8282\u3002\u201d\u4e3a\u9996\u7684\u6c49\u5b50\u201c\u554a \u5440\u201d\u4e00\u58f0\uff0c\u53eb\u4e86\u8d77\u6765\uff0c\u62dc\u5c06\u4e0b\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4f55\u4e0d\u65e9\u8bf4\uff0c\u539f\u6765\u5927\u6c34\u51b2\u5230\u9f99\u738b\u5e99\uff0c\u90fd\u662f\u4e00\u5bb6 \u4eba\u3002\u201d\u8bf4\u7f62\u53c8\u5bf9\u4f17\u4eba\u8bf4\u9053\uff1a\u201c\u5085\u5148\u751f\u5c31\u662f\u4f60\u4eec\u603b\u5934\u76ee\u5e38\u5e38\u63d0\u5230\u7684\u4eba\uff0c\u4ed6\u662f\u6b66\u6797\u524d\u8f88\uff0c\u53c8\u662f\u5f53 \u4eca\u7684\u795e\u533b\u56fd\u624b\u3002\u6211\u4eec\u603b\u5934\u76ee\u51e0\u6b21\u60f3\u6d3e\u4eba\u5411\u4f60\u95ee\u5019\uff0c\u53ea\u662f\u6211\u4eec\u50fb\u5904\u8fb9\u9672\uff0c\u4f60\u8001\u5374\u8fdc\u5728\u6c5f\u5357\uff0c\u5c71 \u6cb3\u963b\u9694\uff0c\u4e0d\u80fd\u5982\u613f\uff0c\u4e0d\u6599\u4eca\u65e5\u5374\u5728\u6b64\u76f8\u89c1\u3002\u201d \u3000\u3000\u8fd9\u4e3a\u9996\u7684\u6c49\u5b50\u81ea\u62a5\u59d3\u540d\uff0c\u59d3\u5f20\u540d\u9752\u539f\uff0c\u662f\u674e\u6765\u4ea8\u624b\u4e0b\u4e00\u5458\u5c06\u9886\uff0c\u4ed6\u8fd8\u6015\u5085\u9752\u4e3b\u4e0d\u660e\u767d\uff0c \u53c8\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7684\u603b\u5934\u76ee\uff0c\u5c31\u662f\u674e\u9526\u7684\u517b\u5b50\uff0c\u674e\u95ef\u738b\u7684\u5b59\u5b50\u8f88\u3002\u201d\u5085\u9752\u4e3b\u542c\u5f97\u4ed6\u662f\u674e\u6765\u4ea8\u7684 \u90e8\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u548c\u4f60\u4eec\u7684\u5934\u9886\u795e\u4ea4\u5df2\u4e45\uff0c\u65e9\u5c31\u60f3\u62dc\u8c12\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u5f20\u9752\u539f\u8bf4\u51fa\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u548c\u4e94\u9f99\u5e2e\u4f5c\u5bf9\u7684\u4e8b\u6765\uff0c\u539f\u6765\u5728\u674e\u601d\u6c38\u5355\u8eab\u5230\u6606\u660e\u4f1a\u89c1\u5434\u4e09\u6842 \u4e4b\u65f6\uff0c\u5c31\u5e03\u7f6e\u4e86\u4eba\u624b\u3002\u5206\u6279\u4ece\u541d\u8dc4\u6df7\u5165\u6606\u660e\uff0c\u4f5c\u4e3a\u63a5\u5e94\u3002\u4ed6\u4eec\u5c31\u662f\u53d6\u9053\u6ec7\u4e1c\u7684\u4e00\u6279\uff0c\u5171\u6709\u5341 \u516b\u4e2a\u4eba\uff0c\u7531\u5f20\u9752\u539f\u7387\u9886\u3002\u4e0d\u6599\u5230\u4e86\u6b64\u5730\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u7ed9\u4e94\u9f99\u5e2e\u77e5\u9053\u4e86\u98ce\u58f0\uff0c\u51fa\u5934\u963b\u6897\uff0c\u628a\u5f20 \u9752\u539f\u7684\u526f\u624b\u848b\u58ee\u6253\u4f24\uff0c\u53c8\u5c06\u4ed6\u4eec\u4e24\u4e2a\u5144\u5f1f\u64d2\u53bb\u3002 \u3000\u3000\u5f20\u9752\u539f\u9053\uff1a\u201c\u8fd9\u4e94\u9f99\u5e2e\u539f\u662f\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5e2e\u4f1a\uff0c\u5374\u5e76\u4e0d\u2018\u5b89\u7a91\u7acb\u67dc\u2019\uff08\u6ca1\u6709\u56fa\u5b9a\u5730\u5740\uff09\uff0c \u5b9e\u9645\u53ea\u662f\u4e00\u5e2e\u52ab\u63a0\u5546\u65c5\u7684\u6e38\u532a\uff0c\u6700\u8fd1\u4e00\u5e74\uff0c\u59cb\u8eb2\u5230\u6cbe\u76ca\u7684\u516d\u6a1f\u5c71\u4e2d\uff0c\u6211\u4eec\u66fe\u6d3e\u4eba\u53eb\u4ed6\u4eec\u5165 \u4f19\uff0c\u4ed6\u4eec\u4e0d\u613f\uff0c\u6211\u4eec\u4e5f\u4e0d\u52c9\u5f3a\u4ed6\u4eec\uff0c\u4e0d\u6599\u8fd9\u6b21\u4ed6\u4eec\u5982\u6b64\u5927\u80c6\uff0c\u5c45\u7136\u6562\u622a\u52ab\u6211\u4eec\u5144\u5f1f\uff0c\u4e8b\u540e\u6211 \u4eec\u4e5f\u6349\u7740\u4e86\u4ed6\u4eec\u7684\u4e00\u4e2a\u4eba\uff0c\u8ffd\u95ee\u53e3\u4f9b\uff0c\u624d\u77e5\u4e94\u9f99\u5e2e\u4e00\u4e2a\u6708\u624d\u7ed9\u5434\u4e09\u6842\u6536\u4e70\uff0c\u53ea\u662f\u8fd8\u672a\u6b63\u5f0f\u6539 \u7f16\u800c\u5df2\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u95ee\u9053\uff1a\u201c\u4e94\u9f99\u5e2e\u7684\u9996\u9886\u662f\u4ec0\u4e48\u4eba\uff1f\u6709\u591a\u5c11\u5e2e\u532a\uff1f\u201d\u5f20\u9752\u539f\u9053\uff1a\u201c\u4e94\u9f99\u5e2e\u7684\u9996\u9886\u5012 \u6709\u70b9\u2018\u786c\u4efd\u2019\uff08\u672c\u4e8b\u4e4b\u610f\uff09\u4ed6\u4eec\u662f\u6ec7\u5357\u5df1\u6545\u7684\u8001\u6b66\u5e08\u845b\u4e2d\u9f99\u7684\u4e94\u4e2a\u5f92\u5f1f\uff0c\u636e\u8bf4\u845b\u4e2d\u9f99\u6709\u4e94\u79cd \u7edd\u6280\uff0c\u4ed6\u4eec\u5404\u5f97\u4e00\u79cd\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u597d\u5947\u95ee\u9053\uff1a\u201c\u90a3\u4e94\u6837\u7edd\u6280\u3002\u201d\u5f20\u9752\u539f\u9053\uff1a\u201c\u845b\u4e2d\u9f99\u4ee5\u94c1\u6c99\u638c\u8457\u540d\uff0c\u9664\u94c1\u6c99\u638c\u5916\uff0c \u4ed6\u8fd8\u6709\u4e00\u79cd\u81ea\u521b\u7684\u6b66\u529f\uff0c\u53eb\u2018\u5730\u5802\u817f\u2019\u3002\u672c\u6765\u2018\u6eda\u5730\u5802\u2019\u8fd9\u79cd\u529f\u592b\uff0c\u4e00\u5411\u662f\u4ee5\u62f3\u4e3a\u4e3b\uff0c\u6240\u4ee5 \u53ea\u6709\u5730\u5802\u62f3\u800c\u65e0\u5730\u5802\u817f\uff0c\u4f46\u845b\u4e2d\u9f99\u8fd9\u6d3e\u5374\u662f\u4ee5\u817f\u4e3a\u4e3b\uff0c\u53ef\u7b97\u662f\u53e6\u8f9f\u8e4a\u5f84\uff0c\u53e6\u5916\u52a0\u4e0a\u4ed6\u64c5\u957f\u7684 \u5175\u5203\u4e09\u8282\u68cd\uff0c\u6697\u5668\u84ba\u9ece\u548c\u62f3\u6cd5\u4e2d\u7684\u4e94\u884c\u62f3\uff0c\u4fbf\u79f0\u4e3a\u845b\u95e8\u4e94\u7edd\uff01\u5085\u9752\u4e3b\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u4e94\u6837 \u529f\u592b\u5730\u5802\u817f\u8f83\u65b0\u9c9c\u5916\uff0c\u5176\u4ed6\u4e5f\u5f88\u5e73\u5e38\u561b\uff0c\u54ea\u80fd\u5c31\u79f0\u4e3a\u2018\u4e94\u7edd\u2019\uff1f\u201d\u5f20\u9752\u539f\u9053\uff1a\u201c\u4ee5\u524d\u7684\u6b66\u5e08 \u591a\u559c\u6b22\u6807\u699c\uff0c\u4ed6\u4e00\u4e2a\u4eba\u80fd\u61c2\u5f97\u8fd9\u51e0\u6837\u6b66\u529f\uff0c\u4e5f\u7b97\u96be\u5f97\u4e86\u3002\u201d\u5f20\u9752\u539f\u505c\u4e86\u4e00\u505c\uff0c\u53c8\u7ee7\u7eed\u8bf4\u9053\uff1a \u201c\u845b\u4e2d\u9f99\u7684\u4e94\u4e2a\u5f1f\u5b50\u4ee5\u6570\u5b57\u6392\u884c\uff0c\u53eb\u505a\u5f20\u4e00\u864e\u3001\u674e\u4e8c\u8c79\u3001\u8d75\u4e09\u9e92\u3001\u94b1\u56db\u9e92\u548c\u5510\u4e94\u718a\uff0c\u5404\u5f97\u4e00 \u95e8\u529f\u592b\uff0c\u5c31\u4ee5\u5e08\u7236\u7684\u540d\u8457\u6807\u699c\uff0c\u79f0\u4e3a\u4e94\u9f99\u5e2e\uff0c\u540e\u6765\u4ed6\u4eec\u6ca6\u4e3a\u532a\u5e2e\uff0c\u4eba\u6570\u4e5f\u4e0d\u5f88\u591a\uff0c\u5927\u7ea6\u53ea\u6709 \u56db\u4e94\u767e\u4eba\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u770b\u4e86\u770b\u5929\u8272\uff0c\u95ee\u660e\u4e86\u53bb\u516d\u6a1f\u5c71\u7684\u8def\uff0c\u8d77\u7acb\u8bf4\u9053\uff1a\u201c\u5feb\u5929\u9ed1\u4e86\uff0c\u6211\u4eec\u4eca\u591c\u5c31\u63a2\u5b83\u4e00 \u63a2\uff0c\u660e\u5929\u624d\u6b63\u5f0f\u62dc\u5c71\uff0c\u6597\u4e00\u6597\u8fd9\u4e94\u9f99\u3002\u201d\u4e34\u8d70\u53c8\u7559\u4e0b\u4e00\u4e9b\u836f\u7ed9\u53d7\u4f24\u7684\u848b\u58ee\uff0c\u8bf4\u9053\uff1a\u201c\u518d\u98df\u4e0b \u8fd9\u4e9b\u836f\uff0c\u4f60\u660e\u5929\u5c31\u53ef\u4ee5\u8ddf\u6211\u4eec\u53bb\u6597\u4e94\u9f99\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u548c\u5192\u6d63\u83b2\u8f7b\u529f\u7edd\u9876\uff0c\u4ee5\u524d\u591c\u63a2\u4e94\u53f0\u5c71\uff0c\u5728\u5343\u4e07\u7981\u536b\u519b\u7684\u9632\u536b\u4e0b\u4e5f\u6765\u53bb\u81ea\u5982\uff0c\u4f55\u51b5 \u8fd9\u5c0f\u5c0f\u7684\u5c71\u5be8\u3002\u4e09\u66f4\u65f6\u5206\uff0c\u4ed6\u4eec\u6478\u5230\u4e86\u516d\u6a1f\u5c71\u7684\u5927\u5be8\u4e4b\u4e2d\uff0c\u8bf4\u662f\u5927\u5be8\uff0c\u5176\u5b9e\u4e5f\u5f88\u7b80\u964b\uff0c\u8305\u8349 \u6728\u7247\u642d\u6210\u7684\u623f\u5b50\uff0c\u4e1c\u4e00\u6392\u897f\u4e00\u6392\uff0c\u501a\u5c71\u5f62\u5efa\u7b51\uff0c\u65e2\u4e0d\u6574\u9f50\uff0c\u4e5f\u4e0d\u76f8\u8fde\uff0c\u5f53\u4e2d\u6709\u4e00\u5ea7\u9752\u7816\u7684\u5c4b \u5b50\uff0c\u5927\u7ea6\u662f\u5927\u5be8\u7684\u8bae\u4e8b\u5385\u3002\u5085\u5192\u4e8c\u4eba\u8d81\u7740\u6708\u9ed1\u98ce\u9ad8\uff0c\u5c55\u5f00\u8fc5\u6377\u7684\u8eab\u6cd5\uff0c\u5728\u8305\u5c4b\u4e0a\u98de\u63a0\u800c\u8fc7\uff0c \u4e00\u76f4\u6251\u5230\u5f53\u4e2d\u7684\u9752\u7816\u5c4b\u5b50\uff0c\u5c4b\u4e0a\u6709\u4e24\u540d\u5de1\u903b\uff0c\u7ed9\u4ed6\u4eec\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\uff0c\u70b9\u4e86\u54d1\u7a74\u548c\u8f6f \u9ebb\u7a74\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002\u4ed6\u4eec\u63a2\u5934\u4e0b\u671b\uff0c\u53ea\u89c1\u5c4b\u4e2d\u5fc3\u5750\u7740\u4e94\u4e2a\u4eba\uff0c\u60f3\u5fc5\u5c31\u662f\u6240\u8c13\u201c\u4e94\u9f99\u201d\u4e86\u3002\u5176\u4e2d \u4e00\u4eba\u9053\uff1a\u201c\u64d2\u4e86\u674e\u8d3c\u6240\u6d3e\u7684\u4eba\uff0c\u9001\u7ed9\u5e73\u897f\u738b\u662f\u4e00\u9879\u5927\u529f\u54e9\u3002\u201d\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u53c8\u542c\u8bf4\u5e73\u897f\u4e3b\u8981 \u548c\u674e\u6765\u4ea8\u5546\u8c08\u3002\u201d\u539f\u5148\u8bf4\u8bdd\u7684\u4eba\u9053\uff1a\u201c\u4f60\u542c\u8fd9\u4e9b\u8c23\u8a00\uff0c\u5e73\u897f\u738b\u5904\u5904\u9632\u7740\u4ed6\u4eec\uff0c\u5c31\u662f\u5546\u8c08\u4e5f\u6de1 \u4e0d\u51fa\u9053\u7406\u3002\u201d\u53c8\u4e00\u4eba\u9053\uff1a\u201c\u674e\u6765\u4ea8\u624b\u4e0b\uff0c\u5175\u591a\u5c06\u4f17\uff0c\u6211\u4eec\u53ef\u5f97\u65e9\u65e9\u51c6\u5907\u3002\u201d\u6700\u8001\u7684\u4e00\u4e2a\u9053\uff1a \u201c\u4ed6\u4eec\u8fdc\u5728\u8fb9\u533a\uff0c\u6211\u4eec\u660e\u65e5\u62d4\u5be8\u4fbf\u884c\uff0c\u5f84\u6295\u6606\u660e\u738b\u5e9c\uff0c\u4ed6\u4eec\u54ea\u8ffd\u5f97\u53ca\u3002\u201d\u53c8\u4e00\u4eba\u9053\uff1a\u201c\u6211\u5c31 \u62c5\u5fc3\u4ed6\u4eec\u7a81\u6d3e\u9ad8\u624b\u6765\u88ad\u51fb\u3002\u201d\u8001\u8005\u9053\uff1a\u201c\u53cd\u6b63\u662f\u4eca\u665a\u548c\u660e\u65e9\u7684\u4e8b\uff0c\u5c31\u662f\u4ed6\u4eec\u4ea4\u6e38\u5e7f\u9614\uff0c\u4e00\u65f6 \u4e5f\u8bf7\u4e0d\u6765\u8bb8\u591a\u9ad8\u624b\u3002\u800c\u4e14\u6211\u4eec\u4e5f\u6709\u4e00\u4e2a\u529f\u592b\u7edd\u9876\u7684\u9ad8\u624b\uff0c\u6015\u4ec0\u4e48\u54e9\uff1f\u201d\u53e6\u4e00\u4eba\u95ee\u9053\uff1a\u201c\u8fd9\u4e2a \u6d3b\u5b9d\u8d1d\u4f60\u54c4\u5f97\u3002\u6211\u53ea\u8bf4\u8c01\u662f\u574f\u4eba\uff0c\u53eb\u4ed6\u53bb\u6740\uff0c\u4ed6\u5c31\u4f1a\u53bb\u6740\u3002\u201d\u5085\u9752\u4e3b\u5728\u623f\u4e0a\u542c\u4e86\u5927\u4e3a\u60ca\u5947\uff0c \u600e\u7684\u6709\u529f\u592b\u7edd\u9876\u7684\u9ad8\u624b\uff0c\u4f1a\u50cf\u5c0f\u5b69\u5b50\u4e00\u6837\u542c\u4eba\u54c4\u7684\uff1f\u6b63\u601d\u7591\u95f4\uff0c\u5192\u6d63\u83b2\u4e0d\u8010\u4e45\u4f0f\uff0c\u52a8\u4e86\u4e00 \u4e0b\uff0c\u5ffd\u7136\u5c4b\u5185\u6709\u4eba\u559d\u9053\uff1a\u201c\u623f\u4e0a\u6765\u7684\u662f\u54ea\u4e00\u8def\u670b\u53cb\uff0c\u6df1\u591c\u5230\u6765\uff0c\u6709\u4f55\u6307\u6559\uff1f\u201d \u3000\u3000\u5c4b\u5b50\u5185\u7684\u4eba\u51fa\u4e86\u58f0\uff0c\u5085\u9752\u4e3b\u8f7b\u8f7b\u5730\u78b0\u4e86\u5192\u6d63\u83b2\u4e00\u4e0b\uff0c\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u5feb\u53bb\u4e1c\u9762\u653e\u706b\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u4e00\u5c55\u8eab\u5f62\uff0c\u98de\u63a0\u8fc7\u51e0\u95f4\u8305\u5c4b\u3002\u5085\u9752\u4e3b\u827a\u9ad8\u80c6\u5927\uff0c\u5728\u6a90\u5934\u4e00\u7ad9\uff0c\u73b0\u51fa\u8eab\u6765\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u6211\u662f\u4e2a\u8fc7\u8def\u7684\uff0c\u6765\u8bbf\u670b\u53cb\u6765\u4e86\uff01\u201d\u201c\u4e94\u9f99\u201d\u4e2d\u7684\u8001\u5927\u5f20\u4e00\u864e\u6012\u9053\uff1a\u201c\u5988\u5df4\u5b50\u7684\uff0c\u8bbf\u670b \u53cb\u8bbf\u5230\u6211\u7684\u5927\u5be8\u6765\u4e86\uff0c\u4f60\u5f53\u6211\u4e94\u9f99\u5e2e\u662f\u597d\u6b3a\u8d1f\u7684\u5417\uff1f\u201d\u4e94\u4eba\u4e00\u9f50\u62a2\u5c06\u51fa\u6765\uff0c\u5510\u4e94\u718a\u559d\u58f0\uff1a \u201c\u6253\uff01\u201d\u4e24\u624b\u9f50\u53d1\uff0c\u56db\u9897\u6bd2\u84ba\u85dc\u5411\u5085\u9752\u4e3b\u4e24\u8fb9\u5c04\u6765\u3002\u5085\u9752\u4e3b\u53c8\u662f\u54c8\u54c8\u4e00\u7b11\uff0c\u53cc\u8896\u4e00\u5377\uff0c\u628a\u56db \u679a\u6bd2\u84ba\u85dc\u5b8c\u5168\u5377\u53bb\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u5510\u4e94\u718a\u770b\u4e0d\u51fa\u5085\u9752\u4e3b\u5982\u4f55\u6536\u53bb\u4ed6\u7684\u6697\u5668\uff0c\u4ed6\u89c1\u84ba\u85dc\u98de\u53bb\uff0c\u843d \u5904\u65e0\u58f0\uff0c\u5341\u5206\u60ca\u9a87\u3002\u4ed6\u60f3\u5c31\u662f\u654c\u4eba\u53cc\u624b\u4f1a\u6309\u6697\u5668\uff0c\u4e5f\u4e0d\u80fd\u540c\u65f6\u63a5\u53bb\u56db\u679a\u84ba\u85dc\uff0c\u4f55\u51b5\u84ba\u85dc\u6709 \u6bd2\uff0c\u6839\u672c\u5c31\u63a5\u4e0d\u5f97\uff0c\u8fd9\u53ef\u6709\u70b9\u90aa\u95e8\uff0c\u4ed6\u4e0d\u7981\u558a\u51fa\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u4e2a\u786c\u6c49\u5b50\uff01\u201d\u5085\u9752\u4e3b\u5355\u8db3\u70b9\u7740 \u53a6\u8e6d\uff0c\u7528\u4e2a\u201c\u91d1\u9e21\u72ec\u7acb\u201d\u4e4b\u52bf\uff0c\u4f63\u89c6\u4e0b\u6765\uff0c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u662f\u591f\u70b9\u5b50\u53c8\u600e\u4e48\u6837\uff1f\u201d\u674e\u4e8c\u8c79\u5927 \u6012\uff0c\u4e00\u6446\u4e09\u8282\u68cd\uff0c\u98de\u8eab\u4e0a\u5c4b\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u671d\u5085\u9752\u4e3b\u4e0b\u76d8\u6253\u6765\uff0c\u5085\u9752\u4e3b\u77e5\u9053\u4e09\u8282\u68cd\u662f\u201c\u9022\u786c\u5373 \u62d0\u201d\uff0c\u53ea\u8981\u7528\u5175\u5668\u4e00\u9694\uff0c\u7b2c\u4e00\u8282\u5c31\u4f1a\u5782\u4e0b\u6765\uff0c\u62d0\u5f2f\u6253\u5230\u3002\u4ed6\u5251\u4e5f\u4e0d\u62d4\uff0c\u674e\u4e8c\u8c79\u4e00\u68cd\u6253\u6765\uff0c\u4ed6 \u628a\u53cc\u624b\u7f29\u5165\u8896\u5185\uff0c\u5927\u8896\u4e00\u821e\uff0c\u628a\u4e09\u8282\u68cd\u5377\u4e2a\u6b63\u7740\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4e0b\u53bb\uff01\u201d\u628a\u63d0\u7740\u7684\u5de6\u8db3\u7528\u529b \u4e00\u8e6c\uff0c\u674e\u4e8c\u8c79\u7ed9\u8e22\u5f97\u56db\u811a\u671d\u5929\u8dcc\u843d\u5730\u4e0a\uff0c\u51e0\u4e4e\u722c\u4e0d\u8d77\u6765\uff0c\u5085\u9752\u4e3b\u6b63\u5728\u5927\u7b11\uff0c\u5ffd\u5730\u53c8\u662f\u4e00\u6761\u9ed1 \u5f71\u7a9c\u4e86\u4e0a\u6765\uff0c\u638c\u631f\u52b2\u98ce\uff0c\u5288\u9762\u6253\u5230\u3002\u8fd9\u4eba\u6b63\u662f\u8001\u5927\u5f20\u4e00\u864e\u3002\u201d \u3000\u3000\u5f20\u4e00\u864e\u6df1\u5f97\u845b\u4e2d\u9f99\u94c1\u6c99\u638c\u7684\u771f\u4f20\uff0c\u638c\u53ef\u6d1e\u7a7f\u725b\u8179\uff0c\u4ed6\u7528\u8db3\u5341\u6210\u529b\u91cf\uff0c\u5fd7\u5728\u5fc5\u5f97\u3002\u5085\u9752\u4e3b \u7f29\u540e\u534a\u6b65\uff0c\u4e3e\u638c\u76f8\u8fce\uff0c\u5f20\u4e00\u864e\u4e00\u638c\u6253\u53bb\uff0c\u53ea\u89c9\u5982\u6253\u7740\u4e00\u56e2\u68c9\u82b1\uff0c\u65e0\u5904\u4f7f\u529b\uff0c\u5085\u9752\u4e3b\u8f7b\u8f7b\u7528\u4e2a \u201c\u62ff\u201d\u5b57\u3001\u8bc0\uff0c\u65bd\u5c55\u64d2\u62ff\u624b\uff0c\u4e09\u6307\u628a\u4ed6\u7684\u8109\u95e8\u5173\u5bf8\u6263\u4f4f\uff0c\u8fd0\u638c\u4e00\u6325\uff0c\u53c8\u628a\u4ed6\u6454\u5230\u5730\u4e0a\u3002 \u3000\u3000\u8001\u56db\u94b1\u56db\u9e92\u89c1\u51e0\u4e2a\u628a\u5144\uff0c\u90fd\u906d\u632b\u6298\uff0c\u706b\u7206\u7206\u5730\u51b2\u4e86\u4e0a\u6765\uff0c\u4e94\u884c\u62f3\u75be\u5982\u98ce\uff0c\u970e\u5ffd\u5c31\u6253\u51fa\u4e86 \u4e03\u516b\u62f3\uff0c\u5085\u9752\u4e3b\u6697\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5012\u6bd4\u521a\u624d\u90a3\u4e2a\u5f3a\u3002\u201d\u4e94\u884c\u62f3\u5b8c\u5168\u91c7\u53d6\u653b\u52bf\uff0c\u5085\u9752\u4e3b\u53c8\u9000\u4e86\u4e00 \u6b65\uff0c\u7528\u65e0\u6781\u62f3\u968f\u52bf\u5316\u89e3\u3002\u65e0\u6781\u62f3\u5584\u4ee5\u67d4\u514b\u521a\uff0c\u4e0d\u5230\u5341\u62db\uff0c\u94b1\u56db\u9e92\u653b\u52bf\u5df2\u5b8c\u5168\u987f\u632b\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u5be8\u5185\u5e2e\u532a\u5df2\u95fb\u8b66\u4ec6\u5230\u3002\u4f46\u5192\u6d63\u83b2\u6240\u653e\u7684\u706b\u4e5f\u5df2\u718a\u718a\u5730\u71c3\u70e7\u8d77\u6765\u3002\u79cb\u9ad8\u6c14\u723d\uff0c\u5c71\u98ce\u53c8 \u70c8\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u4e00\u6392\u8305\u8349\u6728\u7247\u642d\u6210\u7684\u623f\u5c4b\u5c31\u6ca1\u5728\u706b\u7130\u4e4b\u4e2d\u3002\u5e2e\u532a\u53c8\u6025\u6025\u5206\u4eba\u51fa\u53bb\u6551\u706b\uff0c\u987f\u65f6 \u4e71\u6210\u4e00\u7247\u3002\u5085\u9752\u4e3b\u89c1\u662f\u65f6\u5019\uff0c\u559d\u9053\uff1a\u201c\u4e94\u9f99\u4ea6\u4e0d\u8fc7\u5982\u6b64\uff0c\u9886\u6559\uff01\u9886\u6559\uff01\u201d\u5927\u7b11\u58f0\u4e2d\uff0c\u817e\u8eab\u4fbf \u8d77\uff0c\u8fd9\u65f6\u5192\u6d63\u83b2\u4e5f\u5df2\u5728\u5c4b\u9762\u73b0\u8eab\uff0c\u4e24\u4eba\u6c47\u5408\u4e00\u8d77\uff0c\u5728\u5f13\u7bad\u6512\u5c04\u4e2d\uff0c\u98de\u8eab\u9053\u51fa\u4e86\u5927\u5be8\u3002\u90a3\u4e9b\u8fd1 \u8eab\u7684\u7bad\uff0c\u5168\u7ed9\u5085\u9752\u4e3b\u53cc\u8896\u62cd\u843d\u3002 \u3000\u3000\u5085\u9752\u4e3b\u9000\u51fa\u5927\u5be8\uff0c\u8d70\u4e0b\u5c71\u8c37\uff0c\u4e00\u8def\u7b11\u201c\u4e94\u9f99\u201d\u6d6a\u5f97\u865a\u540d\uff0c\u5ffd\u7136\u4ece\u5c71\u6da7\u5904\u4f20\u6765\u4e00\u58f0\u602a\u7b11\uff0c \u661f\u5149\u4e0b\u5ffd\u89c1\u4e00\u6761\u9ed1\u5f71\u76f4\u633a\u633a\u5730\u5411\u81ea\u5df1\u884c\u6765\uff01 \u3000\u3000\u5085\u9752\u4e3b\u5927\u58f0\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u53ea\u89c1\u90a3\u4eba\u53cc\u624b\u63a9\u9762\uff0c\u50cf\u68a6\u6e38\u4eba\u4e00\u6837\uff0c\u6d51\u7136\u65e0\u89c9\u5730\u4e00\u76f4\u8d70 \u6765\u3002\u5085\u9752\u4e3b\u5f85\u4ed6\u8d70\u8fd1\uff0c\u53c8\u9661\u7136\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u54d1\u7684\u5417\uff1f\u201d\u90a3\u4eba\u64a4\u4e0b\u53cc\u624b\uff0c\u832b\u7136\u53cd\u95ee\u9053\uff1a \u201c\u4f60\u662f\u8c01\uff1f\u4f60\u600e\u4e48\u8fd9\u6837\u51f6\u5440\uff1f\u201d\u5085\u9752\u4e3b\u84e6\u7136\u51fa\u624b\uff0c\u4f7f\u4e2a\u64d2\u62ff\u624b\u6cd5\uff0c\u5de6\u81c2\u4e00\u8d77\uff0c\u5411\u4ed6\u808b\u4e0b\u4e00 \u67b6\uff0c\u53f3\u58c1\u659c\u7a7f\uff0c\u52bf\u5982\u5377\u74e6\uff0c\u634f\u7740\u4ed6\u7684\u624b\u8155\u4fbf\u626d\uff0c\u90a3\u4eba\u5de6\u81c2\u4e00\u6c89\u4e00\u62c2\uff0c\u53f3\u81c2\u5411\u540e\u4e00\u987f\uff0c\u7acb\u523b\u5316 \u89e3\uff0c\u5085\u9752\u4e3b\u4e00\u7ffb\u638c\uff0c\u6539\u4e3a\u201c\u62e8\u4e91\u89c1\u65e5\u201d\uff0c\u4e58\u52bf\u6253\u53bb\uff0c\u90a3\u4eba\u4e3e\u638c\u76f8\u5de1\uff0c\u53cc\u638c\u4e00\u62b5\uff0c\u5085\u9752\u4e3b\u5931\u58f0 \u53eb\u9053\uff1a\u201c\u597d\u529f\u592b\uff01\u201d\u63a5\u8fde\u9000\u51fa\u516b\u4e03\u6b65\u53bb\uff0c\u90a3\u4eba\u4e5f\u7ed9\u5085\u9752\u4e3b\u7684\u638c\u529b\uff0c\u8feb\u5f97\u8e09\u8e09\u8dc4\u8dc4\uff0c\u659c\u7a9c\u51fa\u4e08 \u8bb8\uff0c\u624d\u7a33\u5f97\u4f4f\u8eab\u5f62\u3002 \u3000\u3000\u5085\u9752\u4e3b\u8fd9\u65f6\u5df2\u770b\u6e05\u695a\u6765\u4eba\u662f\u4e2a\u7f8e\u5c11\u5e74\uff0c\u7a7f\u4e00\u4ef6\u674f\u9ec4\u8272\u886b\u5b50\uff0c\u5f88\u662f\u6f47\u6d12\uff0c\u53ea\u662f\u5728\u661f\u5149\u4e0b\u770b \u4ed6\u9762\u5b54\u53d1\u767d\uff0c\u773c\u795e\u6563\u4e71\u3002\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6b63\u5f85\u518d\u95ee\uff0c\u9ec4\u886c\u5c11\u5e74\u5df2\u53d1\u6012\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u574f\u4eba\u5417\uff1f\u4e00\u89c1 \u9762\u5c31\u4e71\u52a8\u624b\u6253\u4eba\u3002\u201d\u5085\u9752\u4e3b\u8fc8\u524d\u4e24\u6b65\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u662f\u574f\u4eba\uff0c\u53ea\u662f\u89c1\u4f60\u5411\u8fd9\u8fb9\u8d70\u6765\uff0c \u4ee5\u4e3a\u4f60\u662f\u4e94\u9f99\u5e2e\u7684\u3002\u4f60\u662f\u4e94\u9f99\u5e2e\u7684\u5417\uff1f\u201d\u5c11\u5e74\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u4e94\u9f99\u5e2e\uff1f\u201d\u5085\u9752\u4e3b\u7528\u624b\u4e00\u6307\uff1a \u201c\u5c31\u662f\u8fd9\u4e2a\u5c71\u5be8\u91cc\u7684\u4eba\u3002\u201d\u5c11\u5e74\u9053\uff1a\u201c\u8fd9\u4e2a\u5c71\u5be8\u5417\uff1f\u554a\uff0c\u6211\u6653\u5f97\uff0c\u6211\u5c31\u662f\u4f4f\u5728\u90a3\u91cc\u7684\u3002\u90a3\u4e9b \u4eba\u96be\u9053\u662f\u574f\u4eba\u5417\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u5f53\u7136\u662f\u574f\u4eba\uff01\u201d\u9ec4\u886b\u5c11\u5e74\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u3002\u201d\u5085\u9752\u4e3b \u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\u53eb\u505a\u574f\u4eba\u5417\uff1f\u201d\u5c11\u5e74\u9053\uff1a\u201c\u4e0d\u5927\u6e05\u695a\uff0c\u5148\u6253\u4eba\u7684\u5927\u7ea6\u5c31\u662f\u574f\u4eba\u3002\u201d\u5085\u9752\u4e3b \u7b11\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u6bd4\u5982\u4f60\u77e5\u9053\u4e00\u4e2a\u4eba\u662f\u5927\u6076\u4eba\uff0c\u4f60\u4f1a\u5148\u6253\u4ed6\u5417\uff1f\u201d\u5c11\u5e74\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4f1a\uff01\u201d\u5085 \u9752\u4e3b\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u8fd9\u4e2a\u5c71\u5be8\u91cc\u7684\u4eba\u548c\u6e05\u5ef7\u52fe\u7ed3\uff0c\u4f60\u77e5\u9053\u4ec0\u4e48\u53eb\u505a\u2018\u6e05\u5ef7\u2019\u5417\uff1f\u2018\u6e05\u5ef7\u2019 \u5c31\u662f\u6ee1\u5dde\u9791\u5b50\u7684\u671d\u5ef7\uff0c\u4e13\u6b3a\u8d1f\u6211\u4eec\u6c49\u4eba\u7684\u3002\u201d\u9ec4\u886b\u5c11\u5e74\u53cc\u7738\u95ea\u95ea\uff0c\u60f3\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u6e05\u5ef7 \u9791\u5b50\uff1f\u554a\uff0c\u597d\u591a\u5e74\u524d\uff0c\u4f3c\u4e4e\u6709\u4eba\u5e38\u5e38\u5bf9\u6211\u8bf4\u8fd9\u4e2a\uff0c\u662f\u4e0d\u9519\uff0c\u9791\u5b50\u662f\u574f\u4eba\uff1f\u201d \u3000\u3000\u5192\u6d63\u83b2\u8fd9\u65f6\u8f7b\u8f7b\u5730\u8d70\u4e86\u4e0a\u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u4f60\u53ef\u4ee5\u544a\u8bc9\u6211\u4eec\u4f60\u662f\u8c01\u4e86\u5427\uff1f\u201d\u9ec4\u886b\u5c11 \u5e74\u9053\uff1a\u201c\u6211\u662f\u8c01\uff1f\u6ca1\u6709\u4eba\u544a\u8bc9\u6211\uff0c\u6211\u4e0d\u77e5\u9053\uff1f\u201d\u58f0\u8c03\u82e6\u607c\u5f02\u5e38\u3002\u5192\u6d63\u83b2\u4e0d\u7981\u9053\uff1a\u201c\u4f60\u7684\u7238\u7238 \u548c\u5988\u5988\u5462\uff1f\u201d\u5c11\u5e74\u4e00\u542c\uff0c\u7a81\u7136\u5168\u8eab\u98a4\u6296\uff0c\u9762\u8272\u8d8a\u53d1\u60e8\u767d\uff0c\u5ffd\u5730\u555c\u6ce3\u8d77\u6765\u3002\u5192\u6d63\u83b2\u89c1\u4ed6\u50cf\u4e2a\u5c0f \u5b69\u5b50\u4f3c\u7684\uff0c\u4e0d\u89c9\u7528\u624b\u629a\u4e00\u4e0b\u4ed6\u7684\u5934\u53d1\uff0c\u629a\u4e86\u4e4b\u540e\uff0c\u624d\u60f3\u8d77\u5bf9\u65b9\u662f\u4e2a\u82f1\u4fca\u5c11\u5e74\uff0c\u9762\u7ea2\u7ea2\u5730\u7f29\u624b \u8bf4\u9053\uff1a\u201c\u662f\u6211\u8bf4\u8bdd\u607c\u4e86\u4f60\u5417\uff1f\u4f60\u522b\u602a\u554a\uff01\u201d\u5c11\u5e74\u6b62\u6cea\u62ac\u5934\uff0c\u671b\u7740\u5192\u6d63\u83b2\u6e29\u67d4\u7684\u8138\uff0c\u5ffd\u7136\u8bf4 \u9053\uff1a\u201c\u4f60\u5f88\u597d\uff0c\u6211\u597d\u50cf\u6709\u4e00\u4e2a\u5f88\u4eb2\u7684\u4eba\uff0c\u4e5f\u50cf\u4f60\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u89c1\u5c71\u4e0a\u8bb8\u591a\u4eba\u4e0b\u6765\uff0c\u624b\u4e3e\u7740\u706b\u628a\uff0c\u5927\u58f0\u547c\u558a\uff1a\u201c\u9ec4\u886b\u513f\uff0c\u9ec4\u886b\u513f\uff0c\u4f60\u5728\u90a3 \u91cc\uff1f\u201d\u5c11\u5e74\u5e94\u4e86\u4e00\u58f0\uff0c\u5bf9\u5085\u9752\u4e3b\u9053\uff1a\u201c\u4ed6\u4eec\u6765\u53eb\u6211\u4e86\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u661f\u7738\u6b32\u6ef4\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u4eec\u8d70\u5427\uff01\u201d\u9ec4\u886b\u5c11\u5e74\u4ece\u5728\u542c\u4eba\u7528\u8fd9\u6837\u5173\u6000\u7684\u58f0\u97f3 \u8bf4\u8bdd\uff0c\u5fc3\u5934\u4e00\u9635\u6696\u70d8\u70d8\u7684\uff0c\u5446\u5446\u5730\u770b\u7740\u5192\u6d63\u83b2\u4e24\u9897\u9ed1\u6e9c\u69b4\u7684\u773c\u73e0\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u884c\u4e86\u4e00\u6b65\uff0c\u5ffd \u7136\u53c8\u505c\u4e0b\u6765\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u6211\u5f97\u5f04\u6e05\u695a\u8fd9\u5c71\u5be8\u4e2d\u7684\u4eba\u786e\u662f\u574f\u4eba\u6211\u624d\u8d70\u3002\u201d\u9ec4\u886b\u5c11\u5e74\u4e3e\u624b\u9053\u522b\uff0c \u626d\u8f6c\u8eab\u8eaf\uff0c\u98de\u9e1f\u822c\u5730\u8dc3\u4e0a\u5c71\u53bb\u3002\u5085\u9752\u4e3b\u8d5e\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u771f\u597d\u6b66\u529f\uff0c\u53ea\u53ef\u60dc\u60a3\u4e86\u5fc3\u75c5\uff01\u201d\u5192\u6d63 \u83b2\u9053\uff1a\u201c\u8fd9\u4e2a\u75c5\u4e5f\u771f\u53e4\u602a\uff0c\u8fde\u81ea\u5df1\u7684\u6765\u5386\u90fd\u5fd8\u8bb0\u4e86\uff01\u4f2f\u4f2f\uff0c\u4f60\u4e3a\u4ec0\u4e48\u53c8\u653e\u4ed6\u56de\u53bb\u5462\uff1f\u201d\u5085\u9752 \u4e3b\u9053\uff1a\u201c\u8fd9\u4eba\u51c6\u662f\u53d7\u4e86\u7edd\u5927\u7684\u523a\u6fc0\uff0c\u6216\u505a\u4e86\u4e0d\u80fd\u633d\u6551\u7684\u9519\u4e8b\uff0c\u56e0\u6b64\u7cbe\u795e\u4e0a\u6709\u4e00\u79cd\u6f5c\u5728\u7684\u529b\u91cf \u538b\u8feb\u4ed6\u5fd8\u8bb0\u8fc7\u53bb\u3002\u8fd9\u79cd\u75c5\u5047\u82e5\u627e\u4e0d\u51fa\u75c5\u6e90\uff0c\u5f88\u96be\u533b\u597d\uff0c\u5c14\u8fc7\u4ed6\u53ea\u662f\u5fd8\u8bb0\u201c\u8fc7\u53bb\u201d\u5374\u6ca1\u6709\u5fd8\u8bb0 \u2018\u73b0\u5728\u201c\u4f60\u4e0d\u6240\u4ed6\u8bf4\uff0c\u4ed6\u8fd8\u8981\u56de\u53bb\u60f3\u4e00\u60f3\uff0c\u4ed6\u8fd8\u80fd\u591f\u60f3\uff0c\u5c31\u8bc1\u660e\u4ed6\u7075\u6839\u672a\u65ad\u3002\u8fd9\u6837\u7684\u4eba\uff0c\u6211 \u4eec\u4e00\u70b9\u4e5f\u4e0d\u80fd\u5f3a\u8feb\u4ed6\uff0c\u53ea\u80fd\u542c\u4ece\u4ed6\u7684\u610f\u613f\u3002\u201d \u3000\u3000\u5085\u5192\u4e8c\u4eba\u5728\u8c08\u8bba\u9ec4\u886c\u5c11\u5e74\uff0c\u9ec4\u886b\u5c11\u5e74\u8fd9\u65f6\u679c\u5982\u5085\u9752\u4e3b\u6240\u6599\uff0c\u5728\u82e6\u82e6\u601d\u7d22\u8fc7\u53bb\u3002\u4ed6\u53ea\u8bb0\u5f97 \u8fd9\u4e09\u5e74\u6765\u8ddf\u8fd9\u5c71\u5be8\u4e2d\u4eba\u5728\u4e00\u8d77\u7684\u4e8b\uff0c\u66f4\u8fdc\u7684\u5c31\u8bb0\u4e0d\u5f97\u4e86\uff0c\u4ed6\u4f9d\u7a00\u8bb0\u5f97\u81ea\u5df1\u597d\u50cf\u662f\u5728\u4e00\u4e2a\u51ac\u5929 \u7684\u65e5\u5b50\uff0c\u8eb2\u5728\u5927\u96ea\u8986\u76d6\u7684\u5c71\u5cad\u4e0a\uff0c\u660f\u660f\u8ff7\u8ff7\uff0c\u5ffd\u7136\u7ed9\u8fd9\u7fa4\u4eba\u53d1\u73b0\uff0c\u5f53\u65f6\u6709\u4e24\u4e2a\u4eba\u6301\u5200\u8981\u6740 \u4ed6\uff0c\u4ed6\u8fd8\u80fd\u52a8\u7985\uff0c\u53ea\u4e00\u6296\u624b\uff0c\u5c31\u7528\u96ea\u5757\u6253\u4e86\u90a3\u4e24\u4e2a\u4eba\u7684\u7a74\u3002\u540e\u6765\u90a3\u4e2a\u53eb\u505a\u5f20\u4e00\u864e\u7684\u4eba\u53eb\u4f4f\u4e86 \u4f17\u4eba\uff0c\u62ff\u4e1c\u897f\u7ed9\u4ed6\u5403\u559d\uff0c\u5c31\u53eb\u4ed6\u8ddf\u968f\u4ed6\u4eec\u8d70\u5566\u3002\u81f3\u4e8e\u4e3a\u4ec0\u4e48\u8eb2\u5728\u96ea\u5730\u4e0a\uff0c\u5374\u53c8\u60f3\u4e0d\u8d77\u6765\u4e86\uff0c \u53ea\u8bb0\u5f97\u81ea\u5df1\u597d\u50cf\u6740\u8fc7\u4e00\u4e2a\u8ddf\u81ea\u5df1\u6700\u4eb2\u5bc6\u7684\u4eba\uff0c\u81f3\u4e8e\u5230\u5e95\u662f\u4ec0\u4e48\u4eba\uff0c\u5374\u8bb0\u4e0d\u8d77\u6765\u4e86\u3002\u800c\u6bcf\u9022\u81ea \u5df1\u601d\u7d22\u8fc7\u53bb\uff0c\u4e00\u60f3\u5230\u8fd9\u4e9b\u65f6\uff0c\u7cbe\u795e\u5c31\u975e\u5e38\u4e0d\u5b89\uff0c\u975e\u5e38\u75db\u82e6\uff0c\u600e\u6837\u4e5f\u6ca1\u6cd5\u60f3\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u53c8\u60f3\u8d77\u8ddf\u968f\u8fd9\u4e9b\u4eba\u5954\u8dd1\uff0c\u8d77\u521d\u8fd9\u4e9b\u4eba\u76d8\u95ee\u4ed6\u7684\u6765\u5386\uff0c\u76d8\u95ee\u4e0d\u51fa\uff0c\u606b\u5413\u4ed6\uff0c\u4ed6\u4e0d\u7406\uff0c\u90a3 \u4e9b\u4eba\u6700\u521d\u5f88\u5931\u671b\uff0c\u540e\u6765\u53c8\u5f88\u9ad8\u5174\uff0c\u5230\u4ec0\u4e48\u5730\u65b9\uff0c\u90fd\u5b89\u987f\u81ea\u5df1\u72ec\u4f4f\u4e00\u95f4\u623f\u5b50\uff0c\u800c\u4e14\u603b\u6709\u4eba\u966a \u7740\uff0c\u53eb\u81ea\u5df2\u4e0d\u8981\u5230\u5904\u4e71\u8d70\uff0c\u53ea\u78b0\u5230\u6709\u6b66\u529f\u5f88\u597d\u7684\u4eba\u548c\u4ed6\u4eec\u4f5c\u5bf9\uff0c\u4ed6\u4eec\u6253\u4e0d\u8fc7\u65f6\uff0c\u624d\u53eb\u81ea\u5df1\u51fa \u6765\u5e2e\u5fd9\u3002\u4f46\u81ea\u5df1\u56e0\u4e3a\u975e\u5e38\u4e0d\u613f\u610f\u6740\u4eba\uff0c\u4e5f\u4ece\u672a\u5e2e\u4ed6\u4eec\u6740\u8fc7\u4eba\uff0c\u53ea\u628a\u6765\u4eba\u6253\u8dd1\u5c31\u7b97\u4e86\u3002 \u3000\u3000\u4ed6\u53c8\u60f3\u8d77\u6700\u8fd1\u8fd9\u4e9b\u4eba\u662f\u5e38\u5e38\u8bb2\u8d77\u4e9b\u4ec0\u4e48\u201c\u6e05\u5ef7\u201d\u548c\u201c\u62db\u5b89\u201d\u4e4b\u7c7b\u7684\u8bf4\u8bdd\uff0c\u4f46\u89c1\u4ed6\u6765\u65f6\u53c8 \u4e0d\u8bb2\u4e86\uff0c\u4ec0\u4e48\u662f\u201c\u6e05\u5ef7\u201d\uff0c\u4ec0\u4e48\u53eb\u201c\u62db\u5b89\u201d\uff0c\u81ea\u5df1\u4e5f\u61d2\u5f97\u53bb\u60f3\u3002\u4eca\u591c\u7ed9\u8fd9\u8001\u4eba\u548c\u5c11\u5973\u70b9\u9192\uff0c \u624d\u4f9d\u7a00\u53c8\u8bb0\u8d77\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u4f3c\u4e4e\u6709\u4eba\u5e38\u5e38\u53ee\u5631\u81ea\u5df1\u8981\u63a8\u7ffb\u6e05\u5ef7\uff0c\u9a71\u9010\u9791\u5b50\u51fa\u53bb\u3002\u90a3\u4e2a\u4eba\u4f3c \u4e4e\u4e5f\u662f\u81ea\u5df1\u4e00\u4e2a\u5f88\u4eb2\u7684\u4eba\u3002\u8fd9\u6837\u4e00\u60f3\uff0c\u201c\u6e05\u5ef7\u201d\u5f53\u7136\u662f\u574f\u4e1c\u897f\u4e86\uff0c\u201c\u62db\u5b89\u201d\u662f\u4ec0\u4e48\uff0c\u81ea\u5df1\u4e0d \u61c2\uff0c\u4f46\u548c\u6e05\u5ef7\u8fde\u5728\u4e00\u8d77\uff0c\u5927\u7ea6\u4e5f\u4e0d\u4f1a\u662f\u4ec0\u4e48\u597d\u5b57\u773c\u3002 \u3000\u3000\u4e0d\u8bf4\u9ec4\u886b\u5c11\u5e74\u8fd9\u665a\u82e6\u601d\u4e0d\u5df2\uff0c\u76f4\u5230\u5929\u660e\u3002\u4e14\u8bf4\u5085\u5192\u4e8c\u4eba\u6df1\u591c\u56de\u5230\u539f\u6765\u7684\u9152\u5e97\uff0c\u53ea\u89c1\u9ed1\u538b \u538b\u7684\u5806\u6ee1\u4e86\u4e00\u5c4b\u5b50\u4eba\uff0c\u6709\u4e9b\u4eba\u6ca1\u5730\u65b9\u7ad9\uff0c\u5c31\u5728\u5c4b\u5b50\u5916\u5e2d\u5730\u800c\u5750\u3002 \u3000\u3000\u5f20\u9752\u539f\u89c1\u5085\u9752\u4e3b\u6709\u70b9\u60ca\u8be7\uff0c\u7b11\u9053\uff1a\u201c\u6765\u7684\u8fd9\u8bb8\u591a\u5144\u5f1f\uff0c\u90fd\u662f\u6211\u4eec\u5728\u8fd9\u91cc\u7684\u4eba\u3002\u201d\u5085\u9752\u4e3b \u5fc3\u60f3\uff1a\u6cbe\u76ca\u662f\u4e00\u4e2a\u8352\u51c9\u7684\u5730\u65b9\uff0c\u4ed6\u4eec\u80fd\u5728\u6307\u987e\u4e4b\u95f4\uff0c\u7ea0\u96c6\u4e86\u8fd9\u8bb8\u591a\u4eba\uff0c\u4e5f\u771f\u662f\u96be\u5f97\u3002 \u3000\u3000\u5f53\u4e0b\u5085\u9752\u4e3b\u5c06\u591c\u63a2\u516d\u6a1f\u5c71\u7684\u60c5\u5f62\uff0c\u7ea6\u7565\u4e00\u8bf4\uff0c\u5927\u961f\u7acb\u523b\u8d77\u7a0b\uff0c\u4e2d\u5348\u4ee5\u524d\uff0c\u4fbf\u5df2\u8d76\u5230\u3002\u53ea \u89c1\u6a1f\u5c71\u9876\uff0c\u5be8\u95e8\u5927\u5f00\uff0c\u201c\u4e94\u9f99\u201d\u5e26\u7740\u6570\u767e\u5e2e\u532a\uff0c\u7adf\u81ea\u8fce\u4e86\u4e0b\u6765\u3002\u5085\u9752\u4e3b\u5f20\u9752\u539f\u5e76\u80a9\u800c\u4e0a\uff0c\u5f20 \u9752\u539f\u5c55\u51fa\u201c\u95ef\u201d\u5b57\u5927\u65d7\uff08\u95ef\u738b\u6b7b\u540e\uff0c\u5176\u90e8\u4e0b\u4ecd\u4ee5\u201c\u95ef\u201d\u5b57\u65d7\u4e3a\u53f7\uff09\uff0c\u4e0a\u524d\u559d\u9053\uff1a\u201c\u6211\u4eec\u4e0e\u4f60 \u4e94\u9f99\u5e2e\u8fdc\u65e5\u65e0\u51a4\uff0c\u8fd1\u65e5\u65e0\u4ec7\uff0c\u4f60\u4f55\u6545\u6263\u7559\u6211\u4eec\u5144\u5f1f\uff1f\u4eca\u65e5\u82e5\u7136\u653e\u51fa\uff0c\u4e07\u4e8b\u7686\u4f11\uff0c\u5426\u5219\u4e0d\u5f85\u5927 \u519b\u5230\u6765\uff0c\u4e5f\u53ef\u5c06\u4f60\u8fd9\u5c0f\u5c0f\u7684\u5c71\u5be8\uff0c\u8e0f\u4e3a\u5e73\u5730\u3002\u201d \u3000\u3000\u201c\u4e94\u9f99\u201d\u4e2d\u7684\u8001\u5927\u5f20\u4e00\u864e\uff0c\u89c1\u5085\u9752\u4e3b\u540c\u6765\uff0c\u500f\u7136\u53d8\u8272\uff0c\u542c\u4e86\u5f20\u9752\u539f\u7684\u8bdd\uff0c\u5706\u7741\u53cc\u773c\uff0c\u5927 \u58f0\u8bf4\u9053\uff1a\u201c\u8c01\u4e0d\u77e5\u9053\u4f60\u4eec\u662f\u95ef\u8d3c\u9057\u5b7d\uff0c\u4f60\u4eec\u5413\u5012\u522b\u4eba\uff0c\u5413\u4e0d\u5012\u6211\uff01\u201d\u8bf4\u7f62\u53c8\u5fff\u5fff\u5730\u6a2a\u7768\u5085\u9752 \u4e3b\u4e00\u773c\uff0c\u72e0\u72e0\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u8d3c\uff0c\u6b3a\u6211\u592a\u751a\uff01\u201d\u628a\u624b\u4e00\u6446\uff0c\u5510\u4e94\u718a\u5728\u80cc\u540e\u4e00\u6296\u624b\u4fbf\u6253\u51fa\u4e86\u4e09 \u9897\u6bd2\u84ba\u85dc\uff0c\u4e24\u9897\u5954\u5085\u9752\u4e3b\uff0c\u4e00\u9897\u5954\u5f20\u9752\u539f\uff0c\u5085\u9752\u4e3b\u6a2a\u64c5\u4e00\u8dc3\uff0c\u5927\u8896\u5c55\u5904\uff0c\u5c06\u5954\u5f20\u9752\u539f\u7684\u4e00\u9897 \u5148\u62cd\u843d\uff0c\u518d\u56de\u8fc7\u8eab\u6765\uff0c\u53cc\u638c\u5411\u5916\u4e00\u9707\uff0c\u628a\u4e24\u9897\u6bd2\u84ba\u85dc\u90fd\u9707\u4e86\u4e0b\u53bb\uff0c\u674e\u4e8c\u8c79\u5927\u53eb\u4e00\u58f0\uff0c\u6025\u6296\u4e09 \u8282\u68cd\u5c06\u53cd\u5c04\u56de\u6765\u7684\u6bd2\u84ba\u85dc\u6253\u843d\u3002\u5085\u9752\u4e3b\u9519\u6b65\u6643\u80a9\uff0c\u7d22\u6027\u51b2\u5165\u5bf9\u65b9\u9635\u4e2d\uff0c\u53cc\u8896\u98de\u821e\uff0c\u8d5b\u5982\u4e24\u6761 \u8f6f\u97ad\uff0c\u628a\u201c\u4e94\u9f99\u201d\u8feb\u5f97\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u8fd9\u65f6\u5f20\u9752\u539f\u5e26\u6765\u7684\u4eba\uff0c\u4e5f\u548c\u4e94\u9f99\u5e2e\u5e2e\u532a\u6df7\u6218\u8d77\u6765\uff0c\u5e2e\u532a\u867d\u4eba\u6570\u8f83\u591a\uff0c\u4f46\u5f20\u9752\u539f\u7684\u4eba\u90fd\u662f \u7cbe\u9009\u7684\u58ee\u58eb\uff0c\u8d8a\u6740\u8d8a\u52c7\uff0c\u4e94\u9f99\u5e2e\u5df2\u9547\u4e0d\u4f4f\u9635\u811a\u773c\u770b\u5c31\u8981\u6e83\u8d25\u3002 \u3000\u3000\u5c31\u5728\u6b64\u9645\uff0c\u5c71\u811a\u4e0b\u53f7\u89d2\u5f00\u9e23\uff0c\u53c8\u4e0a\u6765\u4e86\u4e00\u5f6a\u4eba\u9a6c\u3002\u800c\u201c\u4e94\u9f99\u201d\u4e5f\u8fde\u8fde\u5927\u53eb\u201c\u9ec4\u886b\u513f\uff01\u9ec4 \u886b\u513f\uff01\u201d\u5f20\u9752\u539f\u6b63\u624b\u6267\u5927\u5200\uff0c\u8eab\u5148\u58eb\u5352\uff0c\u51b2\u5165\u9635\u4e2d\uff0c\u5ffd\u89c1\u4e00\u4e2a\u9ec4\u886b\u5c11\u5e74\uff0c\u4e24\u624b\u7a7a\u7a7a\u5782\u7740\u5934\u4e00 \u76f4\u8d70\u51fa\uff0c\u597d\u50cf\u996d\u540e\u6563\u6b65\uff0c\u51dd\u601d\u51a5\u60f3\u4ec0\u4e48\u4e8b\u60c5\u4f3c\u7684\uff0c\u6218\u573a\u4e0a\u5175\u5203\u4ea4\u54cd\uff0c\u4f1a\u9f13\u9f50\u9e23\uff0c\u4ed6\u90fd\u4f3c\u4e1d\u6beb \u672a\u89c9\uff0c\u800c\u4e94\u9f99\u5e2e\u532a\uff0c\u4e00\u89c1\u4ed6\u51fa\u6765\uff0c\u5c31\u4e24\u9762\u5206\u5f00\u3002\u5f20\u9752\u539f\u5927\u4e3a\u8be7\u5f02\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u5927\u65ab\u5200\u626c\u7a7a\u4e00 \u95ea\u5c31\u7167\u9ec4\u886b\u5c11\u5e74\u5934\u9885\u5288\u5c06\u4e0b\u6765\uff0c\u4e0d\u6599\u82f1\u886b\u5c11\u5e74\u5fae\u5fae\u4e00\u95ea\uff0c\u7adf\u4e00\u4e0b\u5b50\u5c31\u62a2\u4e86\u8fdb\u6765\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u7528 \u4ec0\u4e48\u624b\u6cd5\uff0c\u53ea\u4e00\u7167\u9762\u5f20\u9752\u539f\u7684\u5927\u65ab\u5200\u5c31\u7ed9\u4ed6\u62a2\u53bb\uff0c\u9ec4\u886b\u5c11\u5e74\u968f\u624b\u5c06\u5200\u629b\u843d\u5730\u4e0a\uff0c\u53eb\u9053\uff1a\u201c\u4f60 \u4e0d\u8981\u8fd9\u6837\u51f6\u554a\uff01\u201d\u53f3\u624b\u6307\u6263\u4f4f\u5f20\u9752\u539f\u8109\u95e8\uff0c\u5de6\u624b\u63e1\u62f3\uff0c\u4fbf\u5f85\u6253\u4e0b\u3002\u5f20\u9752\u539f\u4e5f\u662f\u674e\u6765\u4ea8\u624b\u4e0b\u4e00 \u5458\u52c7\u58eb\uff0c\u4e0d\u6599\u8f6c\u77ac\u4e4b\u95f4\u5c31\u7ed9\u9ec4\u886b\u5c11\u5e74\u5236\u4f4f\u3002\u5f20\u9752\u539f\u5e26\u6765\u7684\u4eba\uff0c\u90fd\u4e0d\u7981\u60ca\u547c\u8d77\u6765\u3002\u6b63\u662f\uff1a \u3000\u3000\u4e24\u519b\u65b9\u6fc0\u6597\uff0c\u602a\u6770\u663e\u795e\u529f\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u4e5d\u7ae0\u3000\u6251\u6714\u8ff7\u79bb\u3000\u8010\u5fc3\u8be6\u602a\u68a6\u3000\u5bfb\u5e7d\u63a2\u79d8\u3000\u65e0\u610f\u4f1a\u9ad8\u4eba \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u4e5d\u56de \u6251\u6714\u8ff7\u79bb\u3000\u8010\u5fc3\u8be6\u602a\u68a6 \u5bfb\u5e7d\u63a2\u79d8\u3000\u65e0\u610f\u4f1a\u9ad8\u4eba \u3000\u3000\u5f20\u9752\u539f\u6b63\u5728\u60ca\u614c\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u6e05\u8106\u7684\u5973\u5b50\u58f0\u97f3\uff1a\u201c\u4f60\u4e0d\u8981\u6253\uff0c\u4ed6\u662f\u597d\u4eba\uff01\u201d\u9ec4\u886b\u5c11\u5e74\u5fae \u5fae\u4e00\u7b11\uff0c\u653e\u4e0b\u62f3\u5934\uff0c\u9053\u58f0\u201c\u5f97\u7f6a\u201d\u4e0d\u7406\u5f20\u9752\u539f\uff0c\u4fbf\u8fce\u5c06\u4e0a\u53bb\uff0c\u5f20\u9752\u539f\u56de\u5934\u4e00\u770b\uff0c\u89c1\u662f\u5192\u6d63\u83b2 \u6301\u5251\u8d76\u81f3\u3002\u4ed6\u5f04\u5f97\u83ab\u660e\u5176\u5999\uff0c\u5401\u4e86\u53e3\u6c14\uff0c\u968f\u624b\u6253\u7ffb\u4e0a\u6765\u5077\u88ad\u7684\u51e0\u4e2a\u5e2e\u532a\uff0c\u62a2\u8fc7\u4e00\u6746\u5927\u62a2\uff0c\u518d \u6740\u51fa\u6765\uff0c\u770b\u4ed6\u4eec\u4e24\u4eba\u5230\u5e95\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u3002 \u3000\u3000\u8fd9\u65f6\u5c71\u811a\u4e0b\u90a3\u5f6a\u4eba\u9a6c\uff0c\u5927\u7ea6\u6709\u4e09\u4e94\u767e\u4eba\uff0c\u4e5f\u6740\u4e86\u4e0a\u6765\uff0c\u6253\u7740\u201c\u5927\u6e05\u5e73\u897f\u738b\u201d\u65d7\u53f7\uff0c\u539f\u6765 \u9886\u8fd9\u652f\u5175\u9a6c\u7684\u662f\u5434\u4e09\u6842\u624b\u4e0b\u7684\u4e00\u4e2a\u5927\u5c06\uff0c\u539f\u9a7b\u9738\u76ca\u53bf\u57ce\uff0c\u5949\u5434\u4e09\u6842\u547d\uff0c\u4ee3\u8868\u738b\u5e9c\u6765\u6536\u7f16\u4e94\u9f99 \u5e2e\u7684\uff0c\u8fd9\u65f6\u5434\u4e09\u6842\u5c1a\u672a\u6b63\u5f0f\u4e3e\u4e8b\u53cd\u6e05\uff0c\u6240\u4ee5\u65d7\u5e1c\u4e0a\u4ecd\u7136\u6709\u201c\u5927\u6e05\u201d\u5b57\u773c\uff0c\u5192\u6d63\u83b2\u6307\u7740\u90a3\u9762\u65d7 \u8bf4\u9053\uff1a\u201c\u4f60\u770b\u90a3\u4e0a\u9762\u5199\u7684\u662f\u4ec0\u4e48\u5b57\uff1f\u6211\u6ca1\u6709\u9a97\u4f60\u5440\uff01\u201d\u9ec4\u886b\u5c11\u5e74\u77a7\u5f97\u5206\u660e\u3002\u53c8\u89c1\u4e94\u9f99\u5e2e\u5df2\u5206 \u51fa\u4eba\u8fce\u4e0a\u53bb\uff0c\u63a5\u7740\u524d\u9762\u90a3\u4e2a\u5e26\u5175\u9a6c\u7684\u5b98\uff0c\u6253\u8eac\u4f5c\u63d6\uff0c\u90a3\u5e26\u5175\u5b98\u5927\u58f0\u547c\u559d\uff0c\u7acb\u523b\u6307\u6325\u6e05\u5175\uff0c\u515c \u62ff\u5f20\u9752\u539f\u7684\u4eba\u3002\u9ec4\u886b\u5c11\u5e74\u4e0d\u7981\u52c3\u7136\u5927\u6012\u3002\u5ffd\u7136\u98de\u6b65\u51b2\u5165\u9635\u4e2d\uff0c\u4e94\u9f99\u5e2e\u532a\u56db\u6563\u9000\u8ba9\u3002\u7247\u523b\u4e4b \u95f4\uff0c\u4ed6\u5df2\u51b2\u5230\u90a3\u4e2a\u5e26\u5175\u5b98\u7684\u9762\u524d\u3002 \u3000\u3000\u90a3\u5e26\u5175\u5b98\u89c1\u4e94\u9f99\u5e2e\u532a\u56db\u4e0b\u5206\u5f00\uff0c\u4e00\u4e2a\u5c11\u5e74\u6012\u76ee\u63e1\u62f3\uff0c\u81ea\u9635\u4e2d\u51b2\u51fa\uff0c\u5175\u4e01\u7adf\u62e6\u4ed6\u4e0d\u4f4f\uff0c\u7ed9 \u4ed6\u7a7a\u624b\u6251\u5012\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u4e00\u63d0\u9a6c\u7f30\uff0c\u659c\u523a\u51b2\u51fa\uff0c\u9ec4\u886b\u5c11\u5e74\u8fc5\u75be\u5982\u98ce\uff0c\u51e0\u4e2a\u8d77\u843d\uff0c\u5df2\u62e6\u5728\u9a6c \u524d\uff0c\u7741\u76ee\u731b\u559d\uff0c\u5982\u7efd\u6625\u96f7\uff0c\u90a3\u9a6c\u7ed9\u4ed6\u559d\u5f97\u524d\u8e44\u8e22\u8d77\uff0c\u4eba\u7acb\u8d77\u6765\uff0c\u519b\u5b98\u6025\u5fd9\u4e00\u6309\u9a6c\u5934\uff0c\u5c06\u957f\u77db \u4e00\u633a\uff0c\u5728\u9a6c\u80cc\u4e0a\u7528\u529b\u523a\u4e0b\u3002\u9ec4\u886b\u5c11\u5e74\u6beb\u4e0d\u9000\u8ba9\uff0c\u4e00\u4f38\u624b\u5c31\u63a5\u7740\u957f\u77db\uff0c\u559d\u58f0\u201c\u4f60\u4e0b\u6765\uff01\u201d\u7528\u529b \u4e00\u626f\uff0c\u6e05\u519b\u519b\u5b98\u5e94\u58f0\u843d\u9a6c\u3002\u9644\u8fd1\u4e00\u5458\u526f\u5c06\u820d\u547d\u6251\u6765\u3002\u9ec4\u886b\u5c11\u5e74\u53c8\u662f\u4e00\u58f0\u5927\u559d\uff1a\u201c\u4f60\u56de\u53bb\uff01\u201d \u5de6\u638c\u4e00\u626c\uff0c\u5728\u654c\u4eba\u80f8\u53e3\u4e0a\u731b\u529b\u4e00\u51fb\uff0c\u90a3\u5458\u526f\u5c06\u7ed9\u9707\u5f97\u8eaf\u4f53\u817e\u7a7a\uff0c\u624b\u4e2d\u6734\u5200\u4e5f\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u6309\u7740\u6e05\u5175\u7edf\u9886\uff0c\u62a2\u8fc7\u6734\u5200\uff0c\u5580\u5693\u4e00\u58f0\uff0c\u5c06\u5934\u5272\u4e0b\u3002\u6e05\u5175\u548c\u5e2e\u532a\u90fd\u7ed9\u5413\u5446\u4e86\uff0c\u6ca1 \u4eba\u518d\u6562\u62e6\u963b\uff0c\u9ec4\u886b\u5c11\u5e74\u7eb5\u6a2a\u6218\u9635\u4e4b\u4e2d\uff0c\u7adf\u7136\u5982\u5165\u65e0\u4eba\u4e4b\u5883\u3002\u201d \u3000\u3000\u4e94\u9f99\u5e2e\u4e94\u4e2a\u9996\u9886\u8d77\u521d\u542c\u5f97\u9ec4\u886b\u5c11\u5e74\u58f0\u97f3\uff0c\u559c\u5f62\u4e8e\u8272\u3002\u5fc3\u60f3\uff1a\u63f4\u519b\u5df2\u7136\u8d76\u5230\uff0c\u9ec4\u886b\u5c11\u5e74\u53c8 \u6765\uff0c\u654c\u4eba\u518d\u5389\u5bb3\u4e5f\u4e0d\u6015\u4e86\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5728\u540e\u9762\u7528\u6bd2\u84ba\u85dc\u52a9\u9635\u7684\u5510\u4e94\u718a\uff0c\u89c1\u9ec4\u886b\u5c11\u5e74\u63d0\u7740\u4e00\u9897 \u4eba\u5934\uff0c\u6012\u51b2\u51b2\u8dd1\u56de\uff0c\u5927\u559c\u53eb\u9053\uff1a\u201c\u9ec4\u886b\u513f\u6765\u5566\uff01\u201d\u674e\u4e8c\u8c79\u6025\u5fd9\u558a\u9053\uff1a\u201c\u9ec4\u886b\u513f\uff0c\u4f60\u5feb\u8fc7\u6765\uff0c \u5bf9\u9762\u8fd9\u4e2a\u8001\u7684\u662f\u574f\u4eba\uff01\u201d\u9ec4\u886b\u5c11\u5e74\u53f3\u624b\u4e00\u626c\uff0c\u4e00\u9897\u8840\u6dcb\u6dcb\u7684\u4eba\u5934\uff0c\u98de\u5165\u9635\u4e2d\uff0c\u6251\u7684\u4e00\u58f0\uff0c\u6b63 \u6253\u5728\u674e\u4e8c\u8c79\u9762\u4e0a\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u63b7\u51fa\u4eba\u5934\uff0c\u51dd\u8eab\u6012\u9053\uff1a\u201c\u4f60\u624d\u662f\u574f\u4eba\uff01\u201d\u674e\u4e8c\u8c79\u9aa4\u51fa\u4e0d\u610f\uff0c\u7ed9\u4eba\u5934\u63b7\u4e2d\uff0c\u4e09\u8282 \u68cd\u6253\u51fa\u5df2\u4e0d\u6210\u7ae0\u6cd5\u3002\u5085\u9752\u4e3b\u8d81\u52bf\u62a2\u8fdb\u3002\u957f\u8896\u4e00\u5377\uff0c\u4e09\u8282\u68cd\u547c\u7684\u4e00\u58f0\u7ed9\u629b\u4e86\u51fa\u53bb\u3002\u94b1\u56db\u9e92\u4ece\u53f3 \u9762\u4e00\u62f3\u6363\u6765\uff0c\u5085\u9752\u4e3b\u66f4\u4e0d\u56de\u5934\uff0c\u53cc\u8896\u5411\u540e\u4e00\u62cd\uff0c\u4f7f\u51fa\u201c\u6d41\u4e91\u98de\u8896\u201d\u4e2d\u7684\u201c\u53cd\u624b\u64d2\u7f8a\u201d\u7edd\u62db\uff0c \u53ea\u4e00\u62cd\u5c31\u5c06\u94b1\u56db\u9e92\u62cd\u5012\u5730\u4e0a\uff0c\u540c\u65f6\u4ed6\u53f3\u811a\u4e5f\u5df2\u98de\u8e22\u51fa\u53bb\uff0c\u5c06\u674e\u4e8c\u8c79\u8e22\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u767b\u65f6\u6bd9 \u547d\u3002 \u3000\u3000\u201c\u4e94\u9f99\u201d\u5df2\u53bb\u4e8c\u9f99\uff0c\u9635\u52bf\u987f\u65f6\u74e6\u89e3\u3002\u4ee5\u201c\u4e94\u9f99\u201d\u4e4b\u529b\u5c1a\u654c\u4e0d\u4f4f\u5085\u9752\u4e3b\uff0c\u4f55\u51b5\u53ea\u4f59\u201c\u4e09 \u9f99\u201d\uff1f\u8fde\u9003\u4e5f\u9003\u4e0d\u4e86\u3002\u8d75\u4e09\u9e92\u53cc\u624b\u652f\u5730\uff0c\u5168\u9760\u4e24\u817f\u53d1\u62db\uff0c\u65f6\u95f4\u4e00\u4e45\uff0c\u5df2\u81ea\u89c9\u7d2f\uff0c\u8fd9\u65f6\u6b63\u5f85\u7ffb \u8f6c\u8eab\u6765\uff0c\u7ed9\u5085\u9752\u4e3b\u89d1\u4e2a\u6b63\u7740\uff0c\u8d77\u817f\u6a2a\u626b\u8fc7\u53bb\uff0c\u559d\u9053\uff1a\u201c\u53eb\u4f60\u4e5f\u5c1d\u5c1d\u5730\u5802\u817f\u6ecb\u5473\uff01\u201d\u8d75\u4e09\u9e92\u4e24 \u811a\u671d\u5929\uff0c\u5c1a\u672a\u7ffb\u8f6c\uff0c\u7ed9\u5085\u9752\u4e3b\u4e00\u817f\u626b\u53bb\uff0c\u4e24\u811a\u9f50\u6839\u622a\u65ad\uff0c\u987f\u65f6\u53d8\u6210\u4e86\u4e2a\u8840\u846b\u82a6\uff0c\u5728\u5730\u4e0a\u56e2\u56e2 \u4e71\u6eda\u3002 \u3000\u3000\u5510\u4e94\u718a\u53d1\u51fa\u6700\u540e\u4e09\u679a\u84ba\u85dc\uff0c\u63a9\u62a4\u9000\u5374\u3002\u5085\u9752\u4e3b\u628a\u8896\u4e00\u5377\uff0c\u9732\u51fa\u53cc\u624b\uff0c\u4ed6\u7ec3\u8fc7\u201c\u94c1\u63e9\u7985\u201d \u529f\u592b\uff0c\u4e0d\u6015\u84ba\u85dc\u523a\uff0c\u76ae\u80a4\u4e0d\u7834\u635f\uff0c\u6709\u8f70\u4e5f\u65e0\u59a8\u3002\u53ea\u4e00\u6349\uff0c\u4fbf\u6349\u4f4f\u4e86\u4e24\u679a\u84ba\u85dc\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a \u201c\u4f60\u4e5f\u63a5\u63a5\u5b83\u73a9\u73a9\u3002\u201d\u53cc\u624b\u4e00\u629b\uff0c\u5c06\u4e24\u679a\u6bd2\u84ba\u85dc\u53cd\u6253\u51fa\u53bb\u3002\u7b2c\u4e00\u679a\u4e0e\u5510\u4e94\u718a\u6253\u6765\u7684\u7b2c\u4e09\u679a\u649e \u4e2a\u6b63\u7740\uff0c\u53cc\u53cc\u8dcc\u843d\uff0c\u7b2c\u4e8c\u679a\u5f84\u53d6\u5510\u4e94\u718a\u4e0a\u76d8\uff0c\u5176\u75be\u5982\u98de\uff0c\u5510\u4e94\u718a\u867d\u7136\u662f\u4f7f\u6bd2\u84ba\u85dc\u7684\u80fd\u624b\uff0c\u5374 \u8eb2\u4e0d\u5f00\u81ea\u5df1\u6697\u5668\u3002\u7ed9\u84ba\u85dc\u5728\u80a9\u5934\u7a7f\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u60e8\u53eb\u4e00\u58f0\uff0c\u53c8\u662f\u7ffb\u8eab\u5012\u5730\u3002 \u3000\u3000\u5f20\u4e00\u864e\u89c1\u52bf\u5934\u4e0d\u597d\uff0c\u8fde\u5fd9\u9003\u8dd1\u3002\u9ec4\u886b\u5c11\u5e74\u51b7\u51b0\u51b0\u5730\u62e6\u5728\u4ed6\u7684\u9762\u524d\uff0c\u5f20\u4e00\u864e\u6025\u9053\uff1a\u201c\u4f60\u8d76 \u5feb\u5e2e\u6211\u5440\uff0c\u6211\u517b\u4e86\u4f60\u8fd9\u4e48\u591a\u5e74\u3002\u201d\u9ec4\u886b\u5c11\u5e74\u9762\u65e0\u8868\u60c5\uff0c\u6447\u4e86\u6447\u5934\u3002\u5f20\u4e00\u864e\u5f80\u5de6\u4e00\u7a9c\uff0c\u811a\u672a\u843d \u5730\uff0c\u9ec4\u886b\u5c11\u5e74\u8eab\u5f62\u5fae\u52a8\uff0c\u5df2\u81ea\u7ad9\u5728\u4ed6\u7684\u9762\u524d\uff1b\u5f20\u4e00\u864e\u518d\u5411\u53f3\u4e00\u7a9c\uff0c\u4ecd\u662f\u811a\u672a\u843d\u5730\uff0c\u53c8\u89c1\u9ec4\u886b \u5c11\u5e74\u51b7\u51b0\u51b0\u5730\u7ad9\u5728\u4ed6\u7684\u9762\u524d\u3002\u5f20\u4e00\u864e\u53d1\u8d77\u6025\u6765\uff0c\u731b\u7684\u53cc\u638c\u51fb\u51fa\uff0c\u7528\u8db3\u5341\u6210\u529b\u91cf\uff0c\u5411\u9ec4\u886b\u5c11\u5e74 \u6253\u53bb\uff0c\u4ed6\u7ec3\u5c31\u7684\u662f\u94c1\u6c99\u638c\u529f\u592b\uff0c\u8fd9\u4e00\u51fb\u529b\u91cf\u4f55\u6b62\u5343\u65a4\uff0c\u9ec4\u886b\u5c11\u5e74\u4e3e\u81c2\u4e00\u6321\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u771f\u7684 \u8981\u6253\uff1f\u201d\u624b\u81c2\u4e00\u632f\uff0c\u5f20\u4e00\u864e\u5c31\u4f3c\u6253\u5728\u94c1\u77f3\u4e0a\u4e00\u6837\uff0c\u7adf\u7ed9\u53cd\u5f39\u51fa\u53bb\u3002\u5085\u9752\u4e3b\u521a\u597d\u8d76\u4e0a\uff0c\u4e00\u624b\u635e \u7740\uff0c\u987a\u52bf\u5c31\u70b9\u4e86\u4ed6\u7684\u8f6f\u9ebb\u7a74\u3002 \u3000\u3000\u8fd9\u65f6\u201c\u4e94\u9f99\u201d\u5df2\u56db\u6b7b\u4e00\u4f24\uff0c\u6e05\u519b\u519b\u5b98\u4e5f\u7ed9\u9ec4\u886b\u5c11\u5e74\u5bb0\u6389\uff0c\u6e05\u519b\u548c\u5e2e\u532a\u90a3\u91cc\u7981\u5f97\u4f4f\u5f20\u9752\u539f \u7b49\u4e00\u5e2e\u4eba\u51b2\u6740\uff0c\u6ee1\u5c71\u5954\u9003\uff0c\u5f20\u9752\u539f\u7b49\u4e5f\u4e0d\u7a77\u8ffd\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u4ed6\u4eec\u5df2\u9003\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u8fd9\u65f6\u53cc\u624b\u80cc\u5728\u540e\u9762\uff0c\u81ea\u987e\u81ea\u7684\u4f4e\u5934\u6f2b\u6b65\uff0c\u5192\u6d63\u83b2\u4ece\u540e\u8d76\u4e0a\uff0c\u548c\u4ed6\u5e76\u80a9\u800c\u884c\uff0c\u54bd \u559d\u7ec6\u8bed\uff0c\u597d\u50cf\u662f\u5b89\u6170\u4ed6\u4e00\u6837\uff0c\u9ec4\u886b\u5c11\u5e74\u62ac\u8d77\u5934\u6765\uff0c\u773a\u671b\u8fdc\u65b9\uff0c\u864e\u76ee\u8574\u6cea\uff0c\u5ffd\u7136\u53c8\u54a7\u5634\u50bb\u7b11\uff0c \u5bf9\u5192\u6d63\u83b2\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u597d\uff0c\u6211\u542c\u4f60\u7684\u8bdd\uff01\u201d \u3000\u3000\u5085\u9752\u4e3b\u77a7\u4e86\u4e00\u4e0b\uff0c\u82e5\u6709\u6240\u611f\uff0c\u4e0d\u518d\u7406\u4f1a\u4ed6\u4eec\uff0c\u5f84\u81ea\u5c06\u5f20\u4e00\u864e\u653e\u5728\u5730\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\uff0c \u6211\u95ee\u4f60\u8bdd\uff0c\u4f60\u82e5\u636e\u5b9e\u56de\u7b54\uff0c\u6211\u53ef\u4ee5\u9976\u4f60\u4e00\u6b7b\u3002\u201d\u5f20\u4e0a\u864e\u559c\u51fa\u671b\u5916\uff0c\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d\u5085\u9752\u4e3b \u9053\uff1a\u201c\u5728\u5251\u9601\u6808\u9053\u7684\u7edd\u9876\uff0c\u4f4f\u6709\u4e00\u4e2a\u9ed1\u7626\u8001\u4eba\uff0c\u4f60\u53ef\u77e5\u9053\u4ed6\u662f\u8c01\uff1f\u201d\u5f20\u4e00\u864e\u8be7\u7136\u7b54\u9053\uff1a\u201c\u6211 \u8fde\u5251\u9601\u90fd\u6ca1\u6709\u5230\u8fc7\uff01\u201d\u5085\u9752\u4e3b\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u8bf4\u7684\u53ef\u662f\u771f\u8bdd\uff1f\u201d\u5f20\u4e00\u864e\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u9a97 \u4f60\uff1f\u201d\u5085\u9752\u4e3b\u4f38\u624b\u5728\u4ed6\u80cc\u540e\u4e00\u62cd\uff0c\u7528\u5206\u7b4b\u9519\u9aa8\u4e4b\u6d3b\uff0c\u5f04\u5f97\u5f20\u4e00\u864e\u60e8\u53eb\u8d77\u6765\u3002\u8fd9\u5206\u7b4b\u9519\u9aa8\u7684\u624b \u6cd5\uff0c\u6bd4\u4ec0\u4e48\u9177\u5211\u62f7\u6253\u90fd\u5389\u5bb3\uff0c\u53d7\u7684\u4eba\u5168\u8eab\u7b4b\u9aa8\u4f3c\u6b32\u5bf8\u5bf8\u788e\u88c2\uff0c\u715e\u662f\u96be\u6328\u3002\u5f20\u4e00\u864e\u53eb\u9053\uff1a\u201c\u4f60 \u53eb\u6211\u8bf4\u4ec0\u4e48\uff1f\u6211\u5b9e\u5728\u4e0d\u77e5\u9053\u3002\u201d\u5085\u9752\u4e3b\u89c1\u4ed6\u8eab\u53d7\u5267\u75db\uff0c\u5c1a\u8bf4\u4e0d\u77e5\uff0c\u53c8\u60f3\u4ee5\u4ed6\u7684\u672c\u4e8b\uff0c\u5c31\u662f\u8d70 \u4e0a\u9ed1\u7626\u8001\u4eba\u4f4f\u5904\uff0c\u6050\u6015\u4e5f\u96be\u529e\u5230\u3002\u770b\u6765\u4ed6\u786e\u5b9e\u4e0d\u77e5\u9ed1\u7626\u8001\u4eba\u5176\u4eba\u3002\u4f46\u4f55\u4ee5\u9ed1\u7626\u8001\u4eba\u4e34\u6b7b\uff0c\u5374 \u6bb7\u6bb7\u4ee5\u4e94\u9f99\u5e2e\u4e3a\u5ff5\uff0c\u53eb\u81ea\u5df1\u66ff\u4ed6\u5728\u4e94\u9f99\u5e2e\u5185\u627e\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4eba\u53c8\u7a76\u7adf\u662f\u8c01\uff1f\u83ab\u975e\u5c31\u662f\u9ec4\u886b\u5c11 \u5e74\u3002\u4ed6\u53c8\u4e00\u638c\u6253\u5728\u5f20\u4e00\u864e\u80a9\u5934\u4e0a\uff0c\u518d\u559d\u95ee\u9053\uff1a\u201c\u8fd9\u9ec4\u886b\u5c11\u5e74\u53c8\u662f\u54ea\u91cc\u6765\u7684\uff1f\u201d\u4e00\u638c\u6253\u4e0b\uff0c\u5f20 \u4e00\u864e\u5ffd\u7136\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u5f20\u53e3\u55b7\u51fa\u4e00\u5927\u53e3\u9c9c\u8840\uff0c\u4ed6\u4e3a\u4e86\u6015\u53d7\u6298\u78e8\uff0c\u7adf\u81ea\u54ac\u65ad\u820c\u5c16\u6b7b\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u5f20\u9752\u539f\u7b49\u5df2\u805a\u62e2\u4e86\u6765\uff0c\u5411\u5085\u9752\u4e3b\u9053\u8c22\u3002\u95ee\u9053\uff1a\u201c\u5085\u8001\u524d\u8f88\u53ef\u613f\u548c\u6211\u4eec\u5230\u6606\u660e\u53bb\u3002\u201d \u5085\u9752\u4e3b\u60f3\u4e94\u9f99\u5e2e\u4e4b\u4e8b\u65e2\u67e5\u4e0d\u51fa\u6765\u3002\u5230\u6606\u660e\u53bb\u4e5f\u53ef\u987a\u4fbf\u8bbf\u8bbf\u51cc\u672a\u98ce\u548c\u5218\u90c1\u82b3\uff0c\u800c\u4e14\u8fd8\u53ef\u4ee5\u6709\u52a9 \u4e8e\u674e\u6765\u4ea8\uff0c\u5f53\u4e0b\u6168\u7136\u7b54\u5e94\u3002 \u3000\u3000\u5c31\u8fd9\u6837\uff0c\u5085\u9752\u4e3b\u3001\u5192\u6d63\u83b2\u548c\u9ec4\u886b\u5c11\u5e74\u90fd\u548c\u5f20\u9752\u539f\u7b49\u4e00\u73ed\u4eba\u5230\u4e86\u6606\u660e\uff0c\u4e00\u5230\u8fbe\uff0c\u7acb\u523b\u5c31\u7ed9 \u4e00\u4ef6\u610f\u5916\u7684\u4e8b\u60c5\u60ca\u9a87\u4f4f\u4e86\u3002 \u3000\u3000\u5f20\u9752\u539f\u7b49\u4e00\u5230\u6606\u660e\uff0c\u627e\u7740\u4e86\u674e\u601d\u6c38\u9884\u5148\u57cb\u4f0f\u5728\u6606\u660e\u7684\u4eba\uff0c\u8fd9\u624d\u77e5\u9053\u4e8b\u60c5\u5df2\u53d1\u751f\u4e86\u53d8\u5316\u3002 \u3000\u3000\u674e\u601d\u6c38\u521d\u5230\u6606\u660e\u90a3\u51e0\u5929\uff0c\u6e38\u5c71\u73a9\u6c34\uff0c\u548c\u4ed6\u4eec\u6697\u4e2d\u8fd8\u4fdd\u6301\u7740\u8054\u7edc\u3002\u81ea\u7b2c\u56db\u5929\u8d77\uff0c\u4fbf\u97f3\u8baf\u6c93 \u7136\u3002\u5341\u591a\u5929\u540e\u5728\u738b\u5e9c\u4e2d\u201c\u5367\u5e95\u201d\u7684\u4eba\u624d\u63a2\u51fa\uff0c\u674e\u601d\u6c38\u548c\u53e6\u5916\u4e00\u4e2a\u9762\u5e26\u5200\u75d5\u7684\u7537\u5b50\uff0c\u5df2\u7ecf\u88ab\u56f0 \u5728\u738b\u5e9c\u4e4b\u4e2d\u4e86\uff0c\u5f20\u9752\u539f\u7b49\u6025\u5f97\u5982\u540c\u70ed\u9505\u4e0a\u7684\u8682\u8681\uff0c\u6b32\u5077\u88ad\u738b\u5e9c\uff0c\u52bf\u6240\u4e0d\u80fd\uff1b\u6b32\u98de\u9a91\u8c03\u5175\uff0c\u53c8 \u662f\u5173\u5c71\u963b\u9694\u3002 \u3000\u3000\u5e78\u597d\u5929\u65e0\u7edd\u4eba\u4e4b\u8def\uff0c\u53c8\u8fc7\u4e86\u51e0\u5929\uff0c\u738b\u5e9c\u4e2d\u4eba\u4f20\u51fa\u6d88\u606f\uff0c\u5434\u4e09\u6842\u6700\u5ba0\u7231\u7684\u5b59\u5b50\u5434\u4e16\u64ad\u5f97\u4e86 \u602a\u75c7\uff0c\u534a\u8eab\u9ebb\u75f9\uff0c\u4e0d\u80fd\u8d77\u5e8a\uff0c\u5f81\u8058\u5404\u5730\u540d\u533b\uff0c\u90fd\u675f\u624b\u65e0\u7b56\u3002\u5085\u9752\u4e3b\u4e00\u542c\uff0c\u5c31\u80cc\u8d77\u836f\u56ca\uff0c\u5f84\u81ea \u6295\u5230\u5e73\u897f\u738b\u5e9c\u5e94\u8058\u3002 \u3000\u3000\u738b\u5e9c\u7684\u7ba1\u95e8\uff0c\u8d77\u5148\u8fd8\u4e0d\u8bb8\u4ed6\u8fdb\u5185\uff0c\u5085\u9752\u4e3b\u7d22\u6027\u81ea\u62a5\u59d3\u540d\uff0c\u628a\u4ed6\u5413\u4e86\u4e00\u8df3\u3002\u5085\u9752\u4e3b\u533b\u540d\u6ee1 \u5168\u56fd\uff0c\u771f\u662f\u8c01\u4e2a\u4e0d\u5982\uff0c\u54ea\u4e2a\u4e0d\u6653\uff0c\u5434\u4e09\u6842\u4e5f\u4e45\u95fb\u5176\u540d\uff0c\u53ea\u662f\u4e0d\u77e5\u4ed6\u9664\u4e86\u662f\u4e2a\u540d\u533b\uff0c\u8fd8\u662f\u4e2a\u6b66 \u6797\u4fa0\u9690\u3002\u5f53\u4e0b\u5373\u523b\u5ef6\u89c1\uff0c\u5f85\u4e3a\u4e0a\u5bbe\uff0c\u5085\u9752\u4e3b\u81ea\u79f0\u662f\u4ef0\u6155\u6ec7\u4e2d\u5c71\u6c34\uff0c\u6240\u4ee5\u4e0d\u8fdc\u5e72\u91cc\u6765\u4f5c\u58ee\u6e38\u3002 \u9002\u9022\u738b\u5e9c\u5f81\u8058\u540d\u533b\uff0c\u7279\u6765\u5e94\u8bd5\u3002 \u3000\u3000\u4ee5\u5085\u9752\u4e3b\u7684\u795e\u533b\u5999\u6280\uff0c\u81ea\u7136\u662f\u836f\u5230\u75c5\u9664\uff0c\u670d\u4e86\u4e00\u5242\uff0c\u5434\u4e16\u64ad\u8eab\u5b50\u5c31\u80fd\u8f6c\u52a8\uff0c\u4e94\u5929\u4e4b\u540e\uff0c \u4fbf\u5982\u5e38\u4eba\uff0c\u5434\u4e09\u6842\u656c\u5982\u5929\u4eba\uff0c\u800c\u5085\u9752\u4e3b\u53c8\u66f2\u610f\u5949\u627f\uff0c\u56e0\u6b64\u4e0d\u4e45\u5c31\u53ef\u4ee5\u5728\u738b\u5e9c\u81ea\u7531\u8d70\u52a8\u3002\u8fd9\u65f6 \u9002\u9022\u4fdd\u67f1\u88ab\u51cc\u672a\u98ce\u631f\u7740\uff0c\u540c\u9677\u6c34\u7262\uff0c\u8fc7\u4e86\u591a\u5929\uff0c\u770b\u5b88\u7684\u4eba\u62a5\u8bf4\uff0c\u6c34\u7262\u91cc\u7684\u4eba\u4f3c\u4e4e\u5df2\u75c5\u4e86\u3002\u5434 \u4e09\u6842\u60f3\u8981\u631f\u674e\u601d\u6c38\u7ed3\u76df\uff0c\u81ea\u7136\u4e0d\u60f3\u4ed6\u6b7b\uff0c\u4f55\u51b5\u8fd8\u6709\u81ea\u5df1\u7684\u7231\u5c06\u4fdd\u67f1\u5728\u5185\u3002\u82e5\u8bf7\u7b2c\u4e8c\u4f4d\u540d\u533b\u53bb \u770b\uff0c\u53c8\u6050\u9632\u6cc4\u6f0f\u673a\u5bc6\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u53ea\u6709\u5085\u9752\u4e3b\u9002\u5408\uff0c\u4ed6\u65e2\u662f\u56fd\u624b\uff0c\u53c8\u662f\u5f02\u4e61\u4eba\uff0c\u5373\u7b97\u77e5\u9053\u673a \u5173\uff0c\u4e5f\u65e0\u5927\u788d\u3002 \u3000\u3000\u5c31\u8fd9\u6837\uff0c\u5085\u9752\u4e3b\u85c9\u884c\u533b\u4e3a\u540d\uff0c\u6551\u51fa\u4e86\u674e\u601d\u6c38\u548c\u51cc\u672a\u98ce\u7b49\u4eba\uff0c\u800c\u4e14\u900f\u8fc7\u738b\u5e9c\u4e2d\u5367\u5e95\u7684\u4eba\uff0c \u9884\u5148\u7ea6\u597d\u9ec4\u886b\u5c11\u5e74\u548c\u5192\u6d63\u83b2\u63a5\u5e94\uff0c\u628a\u5e73\u897f\u738b\u5e9c\u95f9\u5f97\u4e0d\u4ea6\u4e50\u4e4e\u3002 \u3000\u3000\u4e66\u63a5\u524d\u6587\uff0c\u5085\u9752\u4e3b\u548c\u5192\u6d63\u83b2\u5c06\u524d\u56e0\u540e\u679c\uff0c\u7ec6\u7ec6\u9053\u6765\uff0c\u526a\u70db\u6e05\u8c08\uff0c\u66d9\u5149\u6b32\u9732\uff0c\u8c08\u5b8c\u4e4b\u540e\uff0c \u9ec4\u886b\u5c11\u5e74\u8fd8\u662f\u719f\u7761\u672a\u9192\u3002\u674e\u601d\u6c38\u5148\u8c22\u8fc7\u5085\u9752\u4e3b\u76f8\u6551\u4e4b\u6069\uff0c\u518d\u6307\u7740\u9ec4\u886b\u5c11\u5e74\u9053\uff1a\u201c\u6b64\u4eba\u8eab\u4e16\uff0c \u5fc5\u6709\u9690\u79d8\uff0c\u53ef\u60dc\u4ed6\u4e00\u8eab\u6b66\u529f\uff0c\u5374\u5f97\u4e86\u5982\u6b64\u602a\u7634\u3002\u5f53\u4eca\u7528\u4eba\u4e4b\u9645\uff0c\u5085\u8001\u524d\u8f88\u548c\u5192\u59d1\u5a18\u53ef\u5f97\u628a\u4ed6 \u533b\u597d\u624d\u884c\u3002\u201d\u5085\u9752\u4e3b\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u591a\u8c22\u674e\u516c\u5b50\uff0c\u674e\u516c\u5b50\u548c\u51cc\u5927\u4fa0\u90fd\u5df2\u8bc1\u5b9e\u90a3\u9ed1\u7626\u8001\u4eba\u540d\u53eb\u6842 \u5929\u6f9c\uff0c\u53ea\u8981\u77e5\u9053\u8fd9\u4e2a\u8001\u4eba\u59d3\u6842\uff0c\u9ec4\u886b\u5c11\u5e74\u4fbf\u6709\u6cd5\u5b50\u533b\u4e86\uff01\u201d\u674e\u601d\u6c38\u8be7\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u4e2a \u8bf4\u6cd5\uff1f\u201d\u5192\u6d63\u83b2\u76c8\u76c8\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u89c1\u4ed6\u6628\u665a\u7ecf\u8fc7\u6842\u82b1\u6811\u4e0b\uff0c\u795e\u60c5\u7a81\u611f\u4e0d\u5b89\u5417\uff1f\u540e\u6765\u5403\u6842\u82b1 \u505a\u7684\u871c\u996f\uff0c\u53c8\u7a81\u7136\u53d1\u6012\uff0c\u5c06\u871c\u996f\u626b\u843d\u5730\u4e0a\u5417\uff1f\u201d \u3000\u3000\u5085\u9752\u4e3b\u62cd\u638c\u7b11\u9053\uff1a\u201c\u597d\u59d1\u5a18\uff0c\u4f60\u8d8a\u6765\u8d8a\u884c\u4e86\uff0c\u6211\u8fd9\u70b9\u672c\u9886\u90fd\u5feb\u8981\u7ed9\u4f60\u638f\u53bb\u4e86\uff01\u201d\u8bf4\u7f62\u7ad9 \u4e86\u8d77\u6765\uff0c\u637b\u4e86\u4e00\u5f20\u7eb8\u6761\uff0c\u5728\u9ec4\u886b\u5c11\u5e74\u9f3b\u5b54\uff0c\u64a9\u4e86\u4e24\u64a9\u3002 \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u8f7b\u8f7b\u5730\u201c\u6664\u201d\u4e86\u4e00\u58f0\uff0c\u624b\u811a\u98a4\u52a8\uff0c\u5085\u9752\u4e3b\u5bf9\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u6211\u4eec\u90fd\u51fa\u53bb\uff0c\u73b0\u5728 \u8981\u770b\u770b\u59d1\u5a18\u7684\u533b\u672f\u4e86\uff01\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u52a8\u4e86\u51e0\u4e0b\uff0c\u5ffd\u7136\u76f4\u8df3\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u8001\u864e\uff01\u8001\u864e\uff01\u201d\u5192\u6d63\u83b2\u76c8\u76c8\u8d70\u8fc7\uff0c\u67d4\u58f0\u53eb \u9053\uff1a\u201c\u522b\u6015\uff0c\u6211\u5728\u8fd9\u513f\u3002\u4f60\u53d1\u4e86\u4ec0\u4e48\u6076\u68a6\uff1f\u201d\u9ec4\u886b\u5c11\u5e74\u7528\u624b\u8f7b\u62cd\u5934\u9885\uff0c\u7741\u5927\u773c\u775b\uff0c\u56db\u56f4\u4e00 \u770b\uff0c\u770b\u89c1\u81ea\u5df1\u7684\u4e24\u628a\u957f\u5251\uff0c\u5815\u5728\u5730\u4e0a\uff0c\u60ca\u9a87\u5730\u95ee\u9053\uff1a\u201c\u6211\u771f\u7684\u548c\u4eba\u6253\u67b6\u4e86\u5417\uff1f\u6211\u6740\u4e86\u4eba\u6ca1 \u6709\uff1f\u201d\u5192\u6d63\u83b2\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\uff01\u4f60\u4ece\u697c\u4e0a\u8d70\u4e0b\u6765\uff0c\u5728\u8fd9\u91cc\u7761\u4e86\u4e00\u89c9\u3002\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u5b9a\u4e86\u5b9a\u795e\uff0c\u5c4b\u5185\u706f\u5149\u6447\u66f3\uff0c\u5c4b\u5916\u591c\u98ce\u4f4e\u5578\uff0c\u5192\u6d63\u83b2\u76c8\u76c8\u5730\u7ad9\u5728\u70db\u65c1\uff0c\u4e00\u53cc\u5982\u79cb \u6c34\u7684\u773c\u775b\u76ef\u7740\u81ea\u5df1\u3002\u4ed6\u53c8\u56f0\u60d1\u5730\u7528\u624b\u6414\u4e86\u6414\u5934\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4e0d\u662f\u68a6\uff1f\u201d\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u5f53 \u7136\u4e0d\u662f\uff0c\u4e0d\u4fe1\u4f60\u54ac\u54ac\u624b\u6307\u3002\u201d\u9ec4\u886b\u5c11\u5e74\u9053\uff1a\u201c\u90a3\u4f60\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u6211\u6765\u544a\u8bc9 \u4f60\u4f60\u662f\u8c01\uff01\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u9aa4\u5403\u4e00\u60ca\uff0c\u644a\u5f00\u4e24\u624b\u53eb\u9053\uff1a\u201c\u8bf7\u8bf4\uff01\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4f60\u5148\u628a\u4f60\u505a\u7684\u6076\u68a6\u544a\u8bc9 \u6211\uff0c\u7136\u540e\u6211\u624d\u544a\u8bc9\u4f60\uff01\u201d\u9ec4\u886b\u5c11\u5e74\u60f3\u4e86\u4e00\u60f3\u9053\uff1a\u201c\u597d\uff0c\u6211\u5148\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u4ed6\u8bf4\uff1a\u201c\u68a6\u4e2d\u6211\u5728\u4e00\u4e2a\u5927\u5c71\u4e2d\uff0c\u5c71\u4e2d\u6709\u4e00\u68f5\u6842\u6811\u3002\u201d\u8bf4\u5230\u6842\u6811\uff0c\u4ed6\u9762\u8272\u82cd\u767d\uff0c\u6b47\u4e86\u4e00 \u4e0b\uff0c\u518d\u5f80\u4e0b\u9053\uff1a\u201c\u6811\u4e0b\u6709\u4e24\u53ea\u7ef5\u7f8a\uff0c\u4e00\u8001\u4e00\u5e7c\u3002\u7a81\u7136\u95f4\u7a7a\u4e2d\u98de\u6765\u4e86\u4e00\u53ea\u8001\u864e\uff0c\u8fd9\u8001\u864e\u6709\u7fd8\u8180 \u7684\u3002\u8fd9\u8001\u864e\u5f88\u548c\u5584\uff0c\u548c\u6821\u4ea8\u7f8a\u73a9\u8d77\u6765\u5566\u3002\u540e\u6765\u4e0d\u77e5\u600e\u7684\uff0c\u90a3\u8001\u7ef5\u7f8a\u548c\u5b83\u6253\u67b6\uff0c\u8001\u7ef5\u7f8a\u7684\u89d2\u628a \u8001\u864e\u89e6\u5f97\u76f4\u9000\uff0c\u90a3\u8001\u864e\u98de\u4e86\u8d77\u6765\uff0c\u5f20\u5f00\u5927\u53e3\u5c31\u54ac\uff0c\u6837\u5b50\u975e\u5e38\u53ef\u6015\u3002\u6211\u4e00\u9897\u77f3\u5934\u6253\u8fc7\u53bb\uff0c\u628a\u8001 \u864e\u7684\u7fc5\u8180\u6253\u65ad\uff0c\u4e24\u53ea\u7ef5\u7f8a\u54d7\u6656\u5927\u53eb\u3002\u540e\u6765\u4e00\u9635\u72c2\u98ce\u5439\u8fc7\uff0c\u628a\u6842\u6811\u5439\u6298\uff0c\u6811\u5e72\u6b63\u6b63\u6253\u4e2d\u6211\u7684\u9f3b \u6881\uff0c\u6211\u5c31\u9192\u4e86\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u4e00\u9762\u542c\u4e00\u9762\u60f3\uff0c\u542c\u5b8c\u4e4b\u540e\uff0c\u773c\u775b\u4e00\u4eae\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u7740\uff0c\u6211\u73b0\u5728\u544a\u8bc9\u4f60\uff0c\u4f60\u662f\u4e0d\u662f \u6000\u7591\u81ea\u5df1\u4ee5\u524d\u6740\u8fc7\u4e00\u4e2a\u5f88\u4eb2\u7684\u4eba\uff0c\u4f46\u5374\u60f3\u4e0d\u8d77\u8fd9\u4eba\u662f\u8c01\uff1f\u201d\u9ec4\u886b\u5c11\u5e74\u5168\u8eab\u6218\u6296\uff0c\u70b9\u4e86\u70b9\u5934\u3002 \u5192\u6d63\u83b2\u9053\uff1a\u201c\u4f60\u4e0d\u6562\u60f3\uff0c\u56e0\u4e3a\u8fd9\u4eba\u662f\u4f60\u7684\u7236\u4eb2\uff0c\u4f60\u4ee5\u4e3a\u4f60\u81ea\u5df1\u6740\u4e86\u7236\u4eb2\u3002\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u4e00\u542c\u4e4b\u540e\uff0c\u9762\u8272\u5927\u53d8\uff0c\u4f38\u5f00\u5927\u624b\uff0c\u671d\u5192\u6d63\u83b2\u5f53\u5934\u6293\u4e0b\uff0c\u5192\u6d63\u83b2\u51dd\u7acb\u4e0d\u52a8\uff0c\u9547\u5b9a \u5730\u770b\u7740\u4ed6\uff0c\u9ec4\u886b\u5c11\u5e74\u7684\u624b\u5df2\u89e6\u7740\u5192\u6d63\u83b2\u5934\u4e0a\u79c0\u53d1\uff0c\u4ee5\u4ed6\u7684\u529f\u592b\uff0c\u53ea\u8981\u5f80\u4e0b\u4e00\u6293\uff0c\u5341\u4e2a\u5192\u6d63\u83b2 \u4e5f\u4e0d\u80fd\u518d\u6d3b\u3002 \u3000\u3000\u5192\u6d63\u83b2\u5fae\u5fae\u7b11\u7740\uff0c\u5b9a\u7740\u773c\u775b\u770b\u4ed6\uff0c\u9ec4\u886b\u5c11\u5e74\u8e0c\u8e87\u4e00\u4e0b\u3002\u5192\u6d63\u83b2\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f46\u4f60\u5e76\u6ca1\u6709 \u6740\u6b7b\u81ea\u5df1\u7684\u7236\u4eb2\uff01\u4f60\u8d76\u5feb\u653e\u624b\uff0c\u522b\u5f04\u4e71\u4e86\u6211\u7684\u5934\u53d1\uff0c\u4f60\u518d\u4e0d\u653e\uff0c\u6211\u8981\u751f\u6c14\u4e86\u3002\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u5401\u4e86\u53e3\u6c14\uff0c\u7a81\u7136\u50cf\u6597\u8d25\u7684\u516c\u9e21\u4f3c\u7684\uff0c\u9893\u7136\u5012\u5728\u5730\u4e0a\uff0c\u63a9\u9762\u555c\u6ce3\u3002\u5192\u6d63\u83b2\u7406\u597d\u79c0 \u53d1\uff0c\u8ba9\u4ed6\u54ed\u4e86\u4e00\u4f1a\uff0c\u8fd9\u624d\u8fc7\u53bb\u5c06\u624b\u642d\u5728\u4ed6\u80a9\u4e0a\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u4f60\u8d77\u6765\uff0c\u4f60\u60f3\u8d77\u4e86\u81ea\u5df1\u662f\u8c01 \u5417\uff1f\u201d\u9ec4\u886b\u5c11\u5e74\u968f\u7740\u5192\u6d63\u83b2\u7684\u58f0\u97f3\u7ad9\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u662f\u60f3\u4e0d\u8d77\uff01\u6211\u53ea\u662f\u8bb0\u8d77\u4e86\u6211\u771f\u7684\u6740\u6b7b\u4e86 \u7236\u4eb2\u5440\uff01\u201d\u5192\u6d63\u83b2\u60a6\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u6ca1\u6740\u6b7b\u5c31\u662f\u6ca1\u6740\u6b7b\uff0c\u4f60\u4e0d\u4fe1\u6211\u7684\u8bdd\uff1f\u597d\uff0c\u6211\u7ed9\u4f60\u770b\u4e00\u6837\u4e1c \u897f\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u5750\u4e86\u4e0b\u6765\uff0c\u5728\u684c\u4e0a\u53d6\u8fc7\u7eb8\u7b14\uff0c\u542e\u58a8\u6325\u6beb\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u4fbf\u753b\u6210\u4e86\u4e00\u5e45\u7edd\u5999\u7684\u5c71\u6c34 \u753b\u3002\u753b\u7684\u662f\u5251\u9601\u6808\u9053\u7edd\u9876\u5904\u7684\u666f\u8c61\uff0c\u6808\u9053\u4e4b\u65c1\uff0c\u6709\u4e00\u5947\u5cf0\u7a81\u51fa\uff0c\u5e95\u4e0b\u662f\u4e24\u5cf0\u5939\u5cd9\u7684\u5e7d\u8c37\uff0c\u753b \u5b8c\u4e4b\u540e\uff0c\u63b7\u7b14\u4e00\u7b11\uff0c\u5bf9\u9ec4\u886b\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u770b\u770b\uff0c\u8fd9\u5730\u65b9\u4f60\u53ef\u719f\u6089\uff1f\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u51dd\u795e\u8bf4\u9053\uff1a\u201c\u7740\u5730\u65b9\u771f\u719f\uff0c\u6211\u597d\u50cf\u5728\u8fd9\u5c4b\u9760\u8fd1\u53f3\u8fb9\u7684\u677e\u6811\uff0c\u4e0d \u662f\u5728\u4e24\u9897\u677e\u6811\u7684\u4e2d\u95f4\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4f60\u5bf9\u4e86\uff0c\u8fd9\u5730\u65b9\u4f60\u6bd4\u6211\u719f\uff0c\u6211\u6545\u610f\u753b\u9519\u4e00\u70b9\u70b9\uff0c\u4f60\u90fd \u770b\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u8fd9\u65f6\u4e5f\u5750\u4e86\u4e0b\u6765\uff0c\u652f\u5934\u9ed8\u5750\u3002\u5192\u6d63\u83b2\u4e5f\u4e0d\u7406\u4ed6\uff0c\u518d\u5728\u8305\u5c4b\u524d\u9762\u753b\u4e86\u4e00\u4e2a\u9ed1\u7626\u8001 \u4eba\u548c\u4e00\u4e2a\u7ea2\u9762\u8001\u4eba\uff0c\u5192\u6d63\u83b2\u662f\u4e00\u4ee3\u624d\u5b50\u5192\u8f9f\u7586\u4e4b\u5973\uff0c\u4e39\u9752\u5999\u7b14\uff0c\u5f97\u81ea\u5bb6\u4f20\uff0c\u753b\u8d77\u6765\u795e\u4f3c\u5f97 \u5f88\u3002\u753b\u6210\u4e4b\u540e\uff0c\u63a8\u4e86\u9ec4\u886b\u5c11\u5e74\u4e00\u628a\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u518d\u7741\u5f00\u773c\u775b\u770b\u770b\uff0c\u54ea\u4e00\u4e2a\u662f\u4f60\u7684\u7236\u4eb2\uff1f\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u7741\u5927\u773c\u775b\uff0c\u53ea\u4e00\u770b\u4fbf\u8df3\u4e86\u8d77\u6765\uff0c\u5192\u6d63\u83b2\u53eb\u9053\uff1a\u201c\u4f60\u9759\u9759\uff0c\u4e0d\u8981\u53d1\u614c\uff01\u201d\u9ec4\u886b\u5c11 \u5e74\u9762\u8272\u5927\u53d8\uff0c\u5728\u8fd9\u5e45\u753b\u4fa7\u7ad9\u7740\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4ed6\u4eec\u662f\u5728\u5927\u95f9\u5e73\u897f\u738b\u5e9c\u4e4b\u540e\uff0c\u548c\u674e\u601d\u6c38\u7b49\u4eba\u5206\u624b \u7684\u3002\u674e\u601d\u6c38\u4f30\u8ba1\u5434\u4e09\u6842\u7684\u53cd\u6e05\uff0c\u5c31\u5c06\u53d1\u52a8\uff0c\u56e0\u6b64\u5728\u8131\u9669\u4e4b\u540e\u7684\u7b2c\u4e8c\u5929\uff0c\u5c31\u7387\u4f17\u8fd4\u56de\u9632\u5730\u3002\u5085 \u9752\u4e3b\u3001\u5218\u90c1\u82b3\u7b49\u4e5f\u63a5\u53d7\u4e86\u674e\u601d\u6c38\u7684\u9080\u8bf7\uff0c\u5230\u4ed6\u519b\u4e2d\u6682\u4f4f\u3002\u5085\u9752\u4e3b\u4e34\u884c\u524d\uff0c\u6084\u6084\u5c06\u5192\u6d63\u83b2\u62c9\u8fc7 \u4e00\u8fb9\uff0c\u5bf9\u5979\u8bf4\u9053\uff1a\u201c\u81ea\u4f60\u7236\u4eb2\u6b7b\u540e\uff01\u591a\u5e74\u6765\u6211\u548c\u4f60\u76f8\u4f9d\u4e3a\u547d\uff0c\u60c5\u5982\u7236\u5973\uff0c\u4f46\u7236\u5973\u4e5f\u4e0d\u80fd\u4e00\u4e16 \u76f8\u4f9d\u3002\u9ec4\u886b\u5c11\u5e74\u5982\u672a\u96d5\u7684\u749e\u7389\uff0c\u4e00\u65e6\u6062\u590d\u7075\u667a\uff0c\u5fc5\u5c06\u5927\u9732\u5149\u8292\u3002\u800c\u4e14\u8fd9\u4eba\u867d\u7136\u5728\u8ff7\u5931\u8bb0\u5fc6\u4e4b \u4e2d\uff0c\u5fc3\u5730\u4e5f\u8868\u73b0\u5f97\u6781\u4e3a\u7eaf\u539a\u3002\u4f60\u597d\u751f\u7167\u987e\u4ed6\u5427\uff01\u201d\u4ed6\u8fd8\u6307\u70b9\u4e86\u5192\u6d63\u83b2\u51e0\u4e2a\u5173\u4e8e\u533b\u6cbb\u7cbe\u795e\u5931\u5e38 \u7684\u6cd5\u5b50\uff0c\u4e24\u4eba\u8fd9\u624d\u70ef\u5618\u9053\u522b\u3002\u5218\u90c1\u82b3\u4e5f\u6084\u6084\u5730\u548c\u51cc\u672a\u98ce\u9053\u522b\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u679c\u4f60\u5e2e\u52a9\u6d63\u83b2\u59d1 \u5a18\uff0c\u533b\u597d\u4e86\u9ec4\u886b\u5c11\u5e74\u4e4b\u540e\uff0c\u5c31\u8d76\u5feb\u56de\u6765\u3002\u6211\u4f46\u613f\u6709\u4e00\u5929\u80fd\u548c\u4f60\u5230\u94b1\u5858\u6c5f\u770b\u6f6e\uff01\u4e5f\u770b\u770b\u6ce2\u6d9b\u51b2 \u53bb\u7684\u5f80\u4e8b\u3002\u201d\u51cc\u672a\u98ce\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u8bf4\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u50cf\u9ec4\u886b\u5c11\u5e74\u90a3\u6837\u5931\u6389\u8bb0\u5fc6\uff0c\u6709\u4e00\u5929\u6211 \u4f1a\u544a\u8bc9\u4f60\u7684\u3002\u201d\u5218\u90c1\u82b3\u4e24\u773c\u6f6e\u6e7f\uff0c\u4e0d\u518d\u8a00\u8bed\uff0c\u4fbf\u5373\u9053\u522b\u3002 \u3000\u3000\u51cc\u672a\u98ce\u548c\u5192\u6d47\u83b2\u90fd\u662f\u4e00\u6837\u7684\u548c\u81ea\u5df1\u5e73\u751f\u6700\u4eb2\u7231\u7684\u4eba\u5c0f\u522b\u3002\u53ef\u662f\u5192\u6d63\u83b2\u79bb\u5f00\u4e86\u5085\u9752\u4e3b\u4e4b \u540e\uff0c\u548c\u9ec4\u886b\u5c11\u5e74\u4e00\u9053\uff0c\u5374\u662f\u795e\u6765\u98de\u626c\uff0c\u8d8a\u6765\u8d8a\u50cf\u4e2a\u6210\u719f\u7684\u5c11\u5973\u4e86\u3002\u7231\u60c5\u7684\u5149\u8f89\uff0c\u6d88\u706d\u4e86\u5979\u8eab \u4e16\u7684\u9634\u5f71\u3002\u51cc\u672a\u98ce\u5185\u5fc3\u5374\u4ecd\u662f\u975e\u5e38\u6c89\u90c1\uff0c\u4ee5\u524d\u5728\u738b\u5e9c\u6c34\u7262\u4e4b\u4e2d\uff0c\u4ed6\u51e0\u4e4e\u5c31\u8981\u8bf4\u51fa\u4ed6\u662f\u8c01\uff0c\u5728 \u6b64\u6b21\u4e34\u522b\u4e4b\u65f6\uff0c\u4e5f\u51e0\u4e4e\u8981\u5bf9\u5218\u90c1\u82b3\u627f\u8ba4\u5f80\u4e8b\u3002\u7136\u800c\u4ed6\u6309\u637a\u4f4f\u4e86\uff0c\u4ed6\u559c\u7231\u81ea\u5df1\u5014\u5f3a\u7684\u6027\u683c\uff0c\u800c \u6b64\u523b\uff0c\u5374\u53c8\u6709\u70b9\u618e\u6068\u81ea\u5df1\u5014\u5f3a\u7684\u6027\u683c\u4e86\u3002 \u3000\u3000\u4e00\u8def\u4e0a\uff0c\u4ed6\u603b\u662f\u8ddf\u5728\u5192\u6d63\u83b2\u548c\u9ec4\u886b\u5c11\u5e74\u540e\u9762\uff0c\u770b\u4ed6\u4fe9\u5e76\u80a9\u800c\u884c\uff0c\u5fc3\u4e2d\u6697\u7b11\uff0c\u81ea\u5df1\u6240\u62c5\u5f53 \u7684\u771f\u662f\u4e2a\u6700\u5947\u602a\u7684\u5dee\u4f7f\u3002\u5085\u9752\u4e3b\u548c\u674e\u601d\u6c38\u662f\u6050\u6015\u9ec4\u886b\u5c11\u5e74\u8ff7\u5931\u7406\u6027\uff0c\u6216\u8005\u7a81\u7136\u534a\u591c\u68a6\u6e38\uff0c\u4f1a \u4f24\u5bb3\u4e86\u5192\u6d63\u83b2\u3002\u6240\u4ee5\u8981\u501f\u91cd\u4ed6\u7684\u6b66\u529f\uff0c\u4ee5\u9632\u4e07\u4e00\u3002\u4f46\u73b0\u5728\u770b\u4ed6\u4eec\u4e24\u4eba\u4eb2\u70ed\u7684\u6837\u5b50\uff0c\u51cc\u672a\u98ce\u5fc3 \u60f3\uff0c\u5c31\u662f\u9ec4\u886b\u5c11\u5e74\u518d\u8ff7\u5931\u7406\u6027\uff0c\u5168\u4e16\u754c\u7684\u4eba\u90fd\u4e0d\u8ba4\u8bc6\u4e86\uff0c\u4ed6\u8fd8\u662f\u4f1a\u542c\u5192\u6d63\u83b2\u7684\u6d3b\u7684\u3002\u800c\u4e8b\u5b9e \u4e0a\uff0c\u4e00\u8def\u884c\u6765\u9ec4\u886b\u5c11\u5e74\u4e5f\u662f\u4e00\u5929\u6bd4\u4e00\u5929\u6e05\u9192\uff0c\u5e76\u6ca1\u6709\u95f9\u8fc7\u4ec0\u4e48\u610f\u5916\u3002 \u3000\u3000\u8fd9\u5929\u9ec4\u660f\u65f6\u5206\uff0c\u4ed6\u4eec\u5230\u4e86\u5251\u9601\u4e4b\u98a0\u3002\u9ec4\u886b\u5c11\u5e74\u53cc\u76ee\u70af\u70af\u53d1\u5149\uff0c\u62ab\u8346\u89c5\u8def\uff0c\u5f88\u5feb\u5c31\u627e\u5230\u4e86 \u90a3\u4e24\u682a\u866c\u677e\u4ea4\u8986\u4e0b\u7684\u8305\u5c4b\uff0c\u4ed6\u51b2\u8fdb\u5c4b\u5185\uff0c\u5c4b\u5185\u5df2\u7a7a\u65e0\u4e00\u4eba\uff0c\u4ed6\u629a\u5f04\u7740\u5c4b\u5185\u5269\u4e0b\u7684\u4e1c\u897f\uff0c\u4e00\u51e0 \u4e00\u51f3\uff0c\u4e00\u5f13\u4e00\u7bad\uff0c\u597d\u50cf\u5bf9\u8fd9\u4e9b\u4e1c\u897f\u90fd\u5145\u6ee1\u4e86\u611f\u60c5\u3002\u5ffd\u7136\u95f4\u4ed6\u568e\u9676\u5927\u54ed\u8d77\u6765\uff0c\u8dd1\u51fa\u5c4b\u5916\uff0c\u6307\u7740 \u4e0b\u9762\u7684\u5e7d\u8c37\u9053\uff1a\u201c\u6211\u5c31\u662f\u5728\u8fd9\u91cc\u6740\u6b7b\u627e\u7684\u4eb2\u4eba\u7684\u3002\u6211\u5728\u8fd9\u95f4\u8305\u5c4b\u91cc\u957f\u5927\uff0c\u90a3\u4e2a\u9ed1\u7626\u8001\u4eba\u6559\u6211 \u6b66\u529f\uff0c\u4ed6\u8d77\u521d\u662f\u6211\u7684\u7236\u4eb2\uff0c\u540e\u6765\u5ffd\u7136\u53c8\u4e0d\u662f\u4e86\u3002\u83b2\u59d0\u59d0\uff0c\u5982\u4eca\u6211\u56de\u5230\u6545\u5c45\u4e86\uff0c\u6211\u7684\u4eb2\u4eba\u5374\u5728 \u54ea\u513f\uff1f\u4f60\u8d76\u5feb\u7ed9\u6211\u627e\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u4ee5\u4e3a\u4ed6\u5230\u4e86\u751f\u957f\u7684\u5730\u65b9\uff0c\u5c31\u4f1a\u5b8c\u5168\u6e05\u9192\uff0c\u90a3\u6599\u8fd8\u662f\u8fd9\u4e2a\u6837\u5b50\uff0c\u6b63\u5728\u8e0c\u8e87\uff0c\u5ffd\u7136\u51cc \u672a\u98ce\u8d70\u4e86\u4e0a\u6765\uff0c\u5411\u5e7d\u8c37\u4e00\u6307\u2026\u2026\u3002 \u3000\u3000\u5e7d\u8c37\u8fdc\u5904\uff0c\u6709\u661f\u661f\u6e34\u706b\uff0c\u4e0d\u662f\u76ee\u529b\u6781\u597d\u7684\u4eba\uff0c\u6839\u672c\u5c31\u770b\u4e0d\u5230\uff0c\u51cc\u672a\u98ce\u5fc3\u60f3\u65e2\u6709\u6e34\u706b\uff0c\u4fbf \u5f53\u6709\u4eba\u5bb6\uff0c\u4ed6\u7ad9\u5728\u5ced\u58c1\u8fb9\u7f18\uff0c\u4fef\u89c6\u9ed1\u9edd\u9edd\u7684\u6df1\u8c37\uff0c\u8111\u5b50\u91cc\u7a81\u7136\u95ea\u8fc7\u81ea\u5df1\u548c\u695a\u662d\u5357\u5728\u4e91\u5c97\u6076\u6597 \u7684\u4e00\u5e55\uff0c\u4e24\u4eba\u4e5f\u66fe\u6eda\u4e86\u5ced\u58c1\uff0c\u4f46\u5374\u90fd\u6ca1\u6709\u6bd9\u547d\u3002\u5251\u9601\u6808\u9053\u867d\u6bd4\u4e91\u5c97\u5cfb\u9669\u5f97\u591a\uff0c\u4f46\u82e5\u6b66\u529f\u6781\u597d \u7684\u4eba\uff0c\u53c8\u5047\u4f7f\u6709\u4eba\u63a5\u5e94\u7684\u8bdd\uff0c\u6eda\u4e0b\u53bb\u4e5f\u672a\u5fc5\u6bd9\u547d\u3002 \u3000\u3000\u4ed6\u5fc3\u5ff5\u4e00\u52a8\uff0c\u56de\u5934\u770b\u9ec4\u886b\u5c11\u5e74\u8fd8\u662f\u5446\u5446\u54ed\u6ce3\uff0c\u795e\u5fd7\u8ff7\u7cca\u3002\u4ed6\u5bf9\u5192\u6d63\u83b2\u62db\u547c\u4e00\u58f0\u9053\uff1a\u201c\u4f60 \u4f34\u7740\u4ed6\uff0c\u6211\u4e0b\u53bb\u770b\u770b\u3002\u201d\u53cc\u81c2\u4e00\u632f\uff0c\u5411\u5e7d\u8c37\u4e0b\u9762\u8dc3\u53bb\u3002 \u3000\u3000\u51cc\u672a\u98ce\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u5728\u8dc3\u4e0b\u4e4b\u65f6\uff0c\u5df2\u770b\u51c6\u5c71\u8170\u7a81\u51fa\u7684\u4e00\u5757\u5ca9\u77f3\uff0c\u8db3\u5c16\u4e00\u70b9\uff0c\u6362\u52bf\u518d \u8dc3\uff0c\u5ffd\u843d\u5728\u7b2c\u4e8c\u5757\u77f3\u4e0a\uff0c\u4f3c\u8fd9\u6837\uff0c\u8fde\u6362\u4e86\u5341\u51e0\u6b21\u8eab\u5f62\uff0c\u624d\u811a\u8e0f\u5b9e\u5730\uff0c\u5230\u4e86\u8c37\u5e95\u3002 \u3000\u3000\u5e7d\u8c37\u4e0b\u602a\u77f3\u78f7\u60af\uff0c\u51f9\u51f8\u4e0d\u5e73\u3002\u51cc\u672a\u98ce\u70b9\u71c3\u4e86\u706b\u6298\u5b50\uff0c\u56db\u56f4\u5bdf\u770b\uff0c\u5e76\u65e0\u5f02\u72b6\uff0c\u6b63\u5f85\u5411\u721d\u706b \u6240\u5728\u8d70\u53bb\uff0c\u731b\u7136\u95f4\uff0c\u4e00\u80a1\u9510\u98ce\uff0c\u659c\u523a\u6251\u6765\u3002\u51cc\u672a\u98ce\u60ef\u7ecf\u5927\u654c\uff0c\u8f7b\u8f7b\u4e00\u8dc3\uff0c\u5c31\u907f\u5f00\u4e86\u6765\u88ad\u7684\u6697 \u5668\uff0c\u4f46\u624b\u4e0a\u7684\u706b\u6298\u5374\u7ed9\u6765\u4eba\u6253\u7184\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5927\u5403\u4e00\u60ca\uff0c\u5c06\u706b\u6298\u62c2\u5728\u5730\u4e0b\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53c8\u662f\u9510\u98ce\u659c\u5439\uff0c\u5e26\u7740\u5578\u58f0\uff0c\u52b2\u800c \u4e14\u9510\uff0c\u51cc\u672a\u98ce\u542c\u98ce\u8fa8\u5668\uff0c\u8170\u80a2\u4e00\u626d\uff0c\u4e00\u679a\u6697\u5668\uff0c\u8d34\u7740\u8eab\u65c1\uff0c\u500f\u7136\u7a7f\u8fc7\uff0c\u51cc\u672a\u98ce\u56de\u8eab\u501f\u52bf\uff0c\u4e00 \u638c\u5288\u51fa\uff0c\u5c06\u7b2c\u4e8c\u679a\u6697\u5668\u6253\u843d\uff0c\u518d\u4f38\u624b\u5411\u4e0a\u4e00\u635e\uff0c\u628a\u7b2c\u4e09\u679a\u6697\u5668\uff0c\u63a5\u5728\u624b\u4e2d\u3002 \u3000\u3000\u8fd9\u4e8c\u679a\u6697\u5668\u6253\u7684\u90fd\u662f\u51cc\u672a\u98ce\u81f4\u547d\u7a74\u9053\uff0c\u5728\u9ed1\u591c\u4e4b\u4e2d\u8ba4\u7a74\u5947\u51c6\uff0c\u51cc\u672a\u98ce\u53cc\u6307\u4e00\u637b\uff0c\u53ea\u89c9\u63a5 \u7740\u7684\u6697\u5668\uff0c\u5f62\u72b6\u751a\u5c0f\uff0c\u5185\u90e8\u4e2d\u7a7a\u6709\u5982\u8033\u73af\u3002\u51cc\u672a\u98ce\u559d\u9053\uff1a\u201c\u6765\u8005\u4f55\u4eba\uff1f\u660f\u591c\u4e4b\u4e2d\uff0c\u5077\u88ad\u6697 \u7b97\uff0c\u8fd9\u5c82\u662f\u597d\u6c49\u6240\u4e3a\uff1f\u201d \u3000\u3000\u4e00\u4e2a\u4f4e\u6c89\u9634\u607b\u7684\u58f0\u97f3\u8fdc\u8fdc\u63a5\u7740\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u4e9b\u8d3c\u5b50\uff0c\u660f\u591c\u4e4b\u4e2d\uff0c\u65e0\u803b\u4f24\u4eba\uff0c\u8fd8\u6562\u548c\u6211\u558a \u8bdd\uff0c\u8bb2\u9053\u4e49\u3001\u8bba\u89c4\u77e9\uff0c\u5478\uff01\u4f60\u518d\u63a5\u4e09\u679a\u3002\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53c8\u662f\u4e09\u679a\u6697\u5668\uff0c\u8054\u7fe9\u98de\u6765\uff0c\u51cc\u672a\u98ce\u4ecd \u7528\u542c\u98ce\u8fa8\u5668\u4e4b\u672f\u8eb2\u907f\uff0c\u4e0d\u6599\u8fd9\u6b21\u6765\u4eba\u4e0d\u77e5\u7528\u4e86\u4ec0\u4e48\u624b\u6cd5\uff0c\u7adf\u662f\u540e\u53d1\u7684\u5148\u5230\uff0c\u800c\u4e14\u5176\u58f0\u5728\u5de6\uff0c \u5ffd\u7684\u5954\u53f3\uff0c\u51cc\u672a\u98ce\u4e0a\u4e86\u5927\u5f53\uff0c\u53ea\u907f\u8fc7\u4e00\u679a\uff0c\u5176\u4ed6\u4e24\u679a\u90fd\u6253\u4e2d\u4e86\u7a74\u9053\u3002 \u3000\u3000\u6df1\u6797\u8302\u8349\u4e4b\u4e2d\uff0c\u4e00\u4e2a\u9ed1\u8863\u5987\u4eba\u957f\u8eab\u800c\u51fa\uff0c\u5979\u4ee5\u4e3a\u51cc\u672a\u98ce\u7ed9\u6253\u4e2d\u7a74\u9053\uff0c\u5389\u58f0\u9a82\u9053\uff1a\u201c\u5c0f \u8d3c\uff0c\u53eb\u4f60\u77e5\u9053\u59d1\u5976\u5976\u7684\u5389\u5bb3\uff01\u201d\u90a3\u77e5\u8bdd\u58f0\u672a\u4e86\uff0c\u51cc\u672a\u98ce\u5df2\u662f\u5728\u5979\u9762\u524d\u73b0\u51fa\u8eab\u5f62\uff0c\u4e09\u679d\u72ec\u95e8\u6697 \u5668\u4ea6\u5df2\u7535\u5c04\u800c\u51fa\uff0c\u559d\u9053\uff1a\u201c\u53eb\u4f60\u8fd9\u8d3c\u5a46\u4e5f\u5c1d\u5c1d\u6211\u5929\u5c71\u795e\u8292\u7684\u5389\u5bb3\uff01\u201d \u3000\u3000\u90a3\u8001\u5987\u4eba\u731b\u89c1\u4e09\u9053\u4e4c\u4f59\u5149\u8292\uff0c\u5288\u9762\u626b\u6765\uff0c\u8eab\u5b50\u4e00\u6447\uff0c\u624b\u4e2d\u5251\u75be\u7684\u5411\u524d\u4e00\u8361\uff0c\u53ea\u542c\u5f97 \u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u706b\u661f\u98de\u6e85\uff0c\u5979\u987a\u52bf\u53f3\u8db3\u6491\u5730\uff0c\u5de6\u8db3\u8e6c\u7a7a\uff0c\u5934\u5411\u540e\u4ef0\uff0c\u60f3\u7528\u201c\u94c1\u677f\u6865\u201d\u8eab\u6cd5\u95ea \u8fc7\u7b2c\u4e8c\u679d\u795e\u8292\u3002\u4e0d\u6599\u51cc\u672a\u98ce\u7684\u624b\u6cd5\u4e5f\u602a\u5f02\u4e4b\u6781\uff0c\u7b2c\u4e00\u679d\u795e\u8292\u98de\u6765\u5c1a\u65e0\u5f02\u72b6\uff0c\u7b2c\u4e8c\u679d\u901f\u5ea6\u7a0d \u7f13\uff0c\u521a\u5230\u5934\u4e0a\u65f6\uff0c\u7b2c\u4e09\u679d\u7535\u4e5f\u4f3c\u7684\u8ffd\u4e0a\uff0c\u4e24\u679d\u4e00\u649e\uff0c\u659c\u98de\u51fa\u53bb\uff0c\u8001\u5987\u4eba\u65bd\u5c55\u60ca\u4eba\u6b66\u529f\uff0c\u534a\u8eab \u60ac\u7a7a\uff0c\u5934\u9885\u4e00\u65cb\uff0c\u5355\u8db3\u4ecd\u70b9\u5730\u9762\uff0c\u8eab\u5b50\u5df2\u8f6c\u4e86\u4e00\u4e2a\u5927\u5708\uff0c\u65b9\u4f4d\u7acb\u53d8\u3002\u9976\u662f\u5982\u6b64\uff0c\u8fd8\u662f\u7ed9\u7b2c\u4e09 \u679d\u795e\u8292\uff0c\u98de\u63a0\u800c\u8fc7\uff0c\u6253\u98de\u4e86\u5934\u4e0a\u7684\u5305\u5dfe\uff0c\u9732\u51fa\u6ee1\u5934\u767d\u53d1\uff01 \u3000\u3000\u8001\u5987\u4eba\u7ad9\u4e86\u8d77\u6765\uff0c\u5fc3\u91cc\u8bf4\u58f0\u201c\u597d\u9669\uff01\u201d\u518d\u4e00\u770b\u5251\u5c16\u5df2\u7ed9\u7b2c\u4e00\u652f\u795e\u8292\u6253\u7f3a\u4e86\u4e00\u4e2a\u5c0f\u53e3\u3002\u5979 \u5e73\u751f\u4ece\u672a\u9047\u5230\u5982\u6b64\u5f3a\u654c\uff0c\u53c8\u7591\u6765\u7684\u4e43\u662f\u4ec7\u5bb6\uff0c\u8eab\u5b50\u5e73\u7a7a\u98de\u63a0\uff0c\u5982\u602a\u9e1f\u4e00\u822c\uff0c\u671d\u51cc\u672a\u98ce\u6251\u53bb\uff0c \u7528\u7684\u662f\u4e94\u79bd\u5251\u6cd5\uff0c\u51cc\u7a7a\u4e0b\u51fb\uff0c\u5389\u5bb3\u5f02\u5e38\uff01 \u3000\u3000\u51cc\u672a\u98ce\u5012\u63d0\u9752\u950b\uff0c\u5411\u540e\u4e00\u7eb5\uff0c\u8eab\u5b50\u843d\u5730\uff0c\u672a\u53ca\u56de\u7738\uff0c\u53ea\u89c9\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\u5df2\u5230\u80cc\u540e\uff0c\u4ed6\u53cd \u624b\u4e00\u5251\uff0c\u7535\u5149\u77f3\u706b\u4e4b\u95f4\uff0c\u4e0e\u5bf9\u65b9\u7684\u5251\u78b0\u4e2a\u6b63\u7740\uff0c\u4e24\u4eba\u90fd\u89c9\u5f97\u5251\u5c16\u55e1\u55e1\u4f5c\u54cd\uff0c\u5251\u8eab\u98a4\u52a8\u4e0d\u4f11\uff01 \u51cc\u672a\u98ce\u5fc3\u60f3\uff0c\u53ef\u60dc\u6211\u7684\u6e38\u9f99\u5251\u5df2\u6362\u7ed9\u4e86\u5218\u90c1\u82b3\uff0c\u8981\u4e0d\u7136\u51c6\u80fd\u5c06\u5979\u7684\u5175\u5203\u622a\u65ad\uff1b\u8001\u5987\u4eba\u5fc3\u60f3\uff0c \u53ef\u60dc\u6211\u7684\u4e94\u79bd\u5251\u6cd5\u51fb\u4e0b\u65f6\u672a\u52a0\u53d8\u5316\uff0c\u5426\u5219\u51c6\u80fd\u53eb\u8fd9\u5c0f\u5b50\u6302\u5f69\u3002 \u3000\u3000\u51cc\u672a\u98ce\u6a2a\u5251\u56de\u8eab\uff0c\u6025\u5fd9\u559d\u9053\uff1a\u201c\u5148\u522b\u52a8\u624b\uff0c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u8001\u5987\u4eba\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u6beb\u4e0d\u7406 \u4f1a\uff0c\u5530\uff01\u5530\uff01\u5530\u4e00\u8fde\u51e0\u5251\uff0c\u5251\u5251\u76f4\u6307\u8981\u5bb3\uff0c\u51cc\u672a\u98ce\u6012\u9053\uff1a\u201c\u6211\u770b\u5728\u4f60\u662f\u4e2a\u8001\u5a46\u5a46\u4efd\u4e0a\uff0c\u8ba9\u4f60 \u51e0\u5206\uff0c\u4f60\u4ee5\u4e3a\u6211\u6015\u4f60\u4e0d\u6210\uff01\u201d\u8001\u5987\u4eba\u9053\uff1a\u201c\u8c01\u8981\u4f60\u8ba9\uff1f\u201d\u624b\u4e2d\u5251\u5ffd\u5de6\u5ffd\u53f3\uff0c\u7adf\u5982\u75be\u98ce\u66b4\u96e8\uff0c \u5c06\u51cc\u672a\u98ce\u7f69\u5728\u5251\u5149\u4e4b\u4e0b\u3002 \u3000\u3000\u51cc\u672a\u98ce\u8eab\u8eaf\u4e00\u6447\uff0c\u624b\u4e2d\u5251\u5982\u98ce\u98d8\u843d\u53f6\uff0c\u5012\u5377\u800c\u4e0a\u3002\u4ed6\u8ba4\u5f97\u8001\u5987\u4eba\u7684\u4e94\u79bd\u5251\u6cd5\uff0c\u4e94\u79bd\u5251\u6cd5 \u662f\u5251\u5251\u53d6\u52bf\uff0c\u4ece\u4e0a\u7a7a\u5288\u523a\u4e0b\u6765\uff0c\u603b\u4e4b\u8981\u4f7f\u81ea\u5df1\u7684\u5251\u538b\u5728\u654c\u4eba\u7684\u5251\u4e0a\uff0c\u82e5\u654c\u4eba\u8981\u4e89\u53d6\u4f4d\u7f6e\uff0c\u5219 \u5fc5\u88ab\u4e58\u865a\u800c\u5165\uff0c\u51cc\u672a\u98ce\u5251\u6cd5\u5219\u521a\u597d\u76f8\u53cd\uff0c\u5251\u5012\u5377\u4e0a\u53bb\uff0c\u81ea\u4e0b\u800c\u4e0a\uff0c\u5bfb\u51fb\u654c\u4eba\u4e2d\u8def\uff0c\u800c\u6bcf\u53d1\u4e00 \u5251\uff0c\u90fd\u662f\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u7cbe\u5999\u62db\u6570\uff0c\u5929\u5c71\u5251\u6cd5\u672c\u662f\u96c6\u5404\u5bb6\u5251\u6cd5\u4e4b\u957f\uff0c\u4e0d\u62d8\u4e00\u683c\uff0c\u4ed6\u4f7f\u51fa\u8fd9\u8def\u4e13 \u5236\u4e94\u79bd\u5251\u6cd5\u7684\u62db\u6570\uff0c\u5374\u4ecd\u517c\u6709\u5176\u4ed6\u5251\u6cd5\u4e4b\u957f\uff0c\u7aef\u7684\u5389\u5bb3\u65e0\u6bd4\u3002 \u3000\u3000\u4f46\u8001\u5987\u4eba\u529f\u529b\u6df1\u539a\uff0c\u5251\u6cd5\u867d\u7a0d\u900a\u4e00\u7b79\uff0c\u51cc\u672a\u98ce\u8feb\u5207\u95f4\u4e5f\u4e0d\u80fd\u53d6\u80dc\uff0c\u4e24\u4eba\u653b\u5b88\u5288\u6321\uff0c\u970e\u5ffd \u95f4\u62c6\u4e86\u4e00\u767e\u6765\u62db\uff0c\u51cc\u672a\u98ce\u521a\u521a\u5316\u53bb\u654c\u4eba\u5148\u624b\u653b\u52bf\uff0c\u6b63\u60f3\u8f6c\u5165\u53cd\u653b\u3002\u5ffd\u7136\u95f4\uff0c\u53ea\u89c1\u5c71\u4e0a\u4e24\u4e2a\u9ed1 \u5f71\u4e0b\u6765\u3002\u4e00\u4e2a\u94f6\u94c3\u4f3c\u7684\u58f0\u97f3\u8fdc\u8fdc\u558a\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u4f60\u548c\u8c01\u6253\u5440\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u53eb\u9053\uff1a\u201c\u6d63\u83b2\u59d1\u5a18\uff0c\u4f60\u4eec\u4e5f\u6765\u4e86\u5417\uff1f\u8fd9\u91cc\u6709\u4e00\u4e2a\u75af\u5a46\u5b50\uff0c\u5f88\u662f\u624e\u624b\uff0c\u4f60\u4eec\u5148\u522b\u4e0b \u6765\uff0c\u5f85\u6211\u548c\u5979\u6597\u5b8c\u518d\u8bf4\u3002\u201d\u4ed6\u662f\u6050\u8001\u5a46\u5a46\u6b66\u529f\u7cbe\u5f3a\uff0c\u6697\u5668\u5389\u5bb3\uff0c\u6015\u5192\u6d63\u83b2\u649e\u4e0a\uff0c\u4f1a\u5403\u4e86\u4e8f\u3002 \u3000\u3000\u51cc\u672a\u98ce\u8bf4\u8bdd\u4e4b\u95f4\u88ab\u8001\u5a46\u5a46\u8fde\u653b\u4e86\u5341\u51e0\u62db\uff0c\u9669\u8c61\u73af\u751f\u3002\u8001\u5a46\u5a46\u5ffd\u7684\u4e00\u7ffb\u53f3\u8155\uff0c\u201c\u65cb\u98ce\u626b \u53f6\u201d\uff0c\u6539\u53d8\u51cc\u7a7a\u4e0b\u51fb\u7684\u6218\u6cd5\u3001\u4e00\u5251\u538b\u4e0b\uff0c\u987a\u52bf\u4fbf\u8d34\u5730\u5f80\u51cc\u672a\u98ce\u53f3\u8db3\u5185\u8e1d\u626b\u6765\uff0c\u8fd9\u8bb0\u9669\u62db\uff0c\u72e0 \u8fa3\u4e4b\u6781\uff0c\u51cc\u672a\u98ce\u8feb\u5f97\u56de\u5251\u9632\u5b88\u3002\u8001\u5a46\u5a46\u660e\u662f\u8fdb\u653b\u5b9e\u662f\u8d70\u52bf\uff0c\u51cc\u672a\u98ce\u56de\u5251\u4e00\u6321\uff0c\u5979\u5df2\u62d4\u8eab\u800c \u8d77\uff0c\u7eb5\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u6124\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u73ed\u8d3c\u5b50\uff0c\u6211\u4eec\u4e0e\u4f60\u4eec\u4f55\u51a4\u4f55\u4ec7\uff0c\u51e0\u6b21\u4e09\u756a\u524d\u6765\u7f20 \u7ed5\uff1f\u4f60\u60f3\u7fa4\u6bb4\uff0c\u6211\u4eec\u4e5f\u6709\u4eba\u5949\u966a\u3002\u6709\u80c6\u7684\u4f60\u8ffd\u6765\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u542c\u8bdd\u91cc\u6709\u58f0\uff0c\u98de\u8eab\u8ffd\u4e0a\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8001\u5a46\u5a46\uff0c\u6211\u4eec\u4e0d\u662f\u574f\u4eba\uff0c\u4f60\u628a\u8bdd\u8bf4\u6e05 \u695a\uff01\u201d\u8fd9\u65f6\u9ec4\u6749\u5c11\u5e74\u4e5f\u5df2\u81ea\u5c71\u811a\u884c\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8c01\u5728\u8bf4\u8bdd\uff1f\u8c01\u5728\u8bf4\u8bdd\uff0c\u6211\u6765\u4e86\u554a\uff01\u201d\u8001 \u5987\u4eba\u56de\u8eab\u4e3e\u5251\uff0c\u51cc\u672a\u98ce\u4ee5\u4e3a\u5979\u53c8\u53d1\u8fa3\u62db\uff0c\u4e00\u5251\u523a\u53bb\uff0c\u4e0d\u6599\u8001\u5987\u4eba\u7adf\u4f3c\u5446\u4e86\u4e00\u822c\uff0c\u53ea\u4e3e\u5251\u5e73\u6321 \u80f8\u524d\uff0c\u7adf\u7136\u4e0d\u77e5\u8f6c\u52a8\uff0c\u51cc\u672a\u98ce\u6025\u6025\u5c06\u5251\u63a3\u56de\uff0c\u53ea\u542c\u5f97\u8001\u5987\u4eba\u558a\u9053\uff1a\u201c\u662f\u4f60\u5417\uff1f\u6211\u7684\u513f\u554a\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u672c\u6765\u662f\u548c\u9ec4\u886b\u5c11\u5e74\u5728\u5251\u9601\u4e4b\u5dc5\u5f98\u5f8a\uff0c\u5979\u89c1\u51cc\u672a\u98ce\u4e0b\u53bb\u4e4b\u540e\uff0c\u4e45\u4e45\u4e0d\u89c1\u56de\u97f3\uff0c\u4fbf\u62c9 \u9ec4\u886b\u5c11\u886b\u4e0b\u53bb\u3002\u53ef\u662f\u5979\u6ca1\u6709\u51cc\u672a\u98ce\u7684\u529f\u529b\uff0c\u9760\u9ec4\u886b\u5c11\u5e74\u7684\u6276\u5f85\uff0c\u4e5f\u53ea\u80fd\u8fd0\u7528\u5ced\u58c1\u6362\u638c\u7684\u529f \u592b\uff0c\u4e00\u8def\u722c\u4e0b\uff0c\u4e0d\u80fd\u50cf\u51cc\u672a\u98ce\u90a3\u6837\uff0c\u5f84\u4ee5\u7edd\u9876\u8f7b\u529f\uff0c\u7247\u523b\u722c\u81f3\u8c37\u5e95\u3002\u9ec4\u886b\u5c11\u5e74\u521a\u548c\u5192\u6d63\u83b2\u5e76 \u80a9\u884c\u5165\u5e7d\u8c37\uff0c\u5ffd\u542c\u5f97\u8001\u5987\u4eba\u5927\u53eb\u201c\u513f\u554a\u201d\u5168\u8eab\u98a4\u6296\uff0c\u84e6\u7136\u6323\u8131\u5192\u6d63\u83b2\u7684\u624b\uff0c\u98de\u5954\u4e0a\u53bb\uff0c\u51cc\u672a \u98ce\u8eab\u8eaf\u4e00\u95ea\uff0c\u9ec4\u886b\u5c11\u5e74\u6574\u4e2a\u8eab\u5b50\u6251\u53bb\uff0c\u54ed\u9053\uff1a\u201c\u4f60\u600e\u4e48\u53bb\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u4e5f\u4e0d\u60f3\u5ff5\u6211\u4eec\u5417\uff1f\u201d \u3000\u3000\u6bcd\u5b50\u76f8\u9022\uff0c\u604d\u5982\u9694\u4e16\uff0c\u826f\u4e45\uff0c\u826f\u4e45\uff0c\u9ec4\u886b\u5c11\u5e74\u624d\u7ad9\u4e86\u8d77\u6765\uff0c\u5192\u6d63\u83b2\u5df2\u5728\u4ed6\u7684\u8eab\u8fb9\uff0c\u542b\u6cea \u5fae\u7b11\u3002\u9ec4\u886b\u5c11\u5e74\u5ffd\u7136\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u5192\u6d63\u83b2\u59d1\u5a18\uff0c\u5988\u5988\uff0c\u4f60\u770b\u5979\u591a\u597d\uff01\u201d\u8001\u5987\u4eba\u6267\u7740\u5192\u6d63\u83b2\u7684 \u624b\uff0c\u95ee\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u662f\u4f60\u966a\u4ed6\u6765\u7684\uff0c\u591a\u8c22\u4f60\u4e86\u3002\u201d\u6d63\u83b2\u9053\uff1a\u201c\u4f2f\u6bcd\uff0c\u4ed6\u5df2\u6e05\u9192\u4e86\uff01\u4f60\u5e26\u4ed6 \u53bb\u3002\u201d\u9ec4\u886b\u5c11\u5e74\u9053\uff1a\u201c\u662f\u554a\uff01\u4f60\u5e26\u6211\u53bb\u89c1\u7236\u4eb2\uff0c\u4f60\u4eec\u4e5f\u540c\u53bb\uff01\u554a\uff0c\u5988\u5988\uff0c\u90a3\u4e2a\u7ea2\u9762\u8001\u4eba\u662f\u6211 \u7684\u7236\u4eb2\u5417\uff1f\u6211\u90a3\u5929\u6ca1\u6709\u6740\u6b7b\u4ed6\u5417\uff1f\u201d\u8001\u5987\u4eba\u98a4\u58f0\u6025\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u6ca1\u6709\uff01\u4f60\u5148\u89c1\u7740\u4ed6\u518d\u8bf4\u3002\u201d \u3000\u3000\u201c\u554a\uff01\u4e0a\u5929\u4f5c\u5f04\u5f97\u6211\u4eec\u597d\u82e6\u554a\uff01\u201d\u5979\u63a9\u7740\u9762\uff0c\u773c\u6cea\u7c41\u7c41\u7684\u76f4\u6ef4\u51fa\u6765\u3002 \u3000\u3000\u5192\u6d63\u83b2\u5f2f\u8170\u5c06\u5979\u7684\u5251\u62fe\u8d77\uff0c\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u4f2f\u6bcd\uff0c\u4f60\u7684\u5251\uff01\u201d\u8001\u5987\u4eba\u970d\u7136\u9192\u8d77\uff0c\u6536\u6cea\u8bf4 \u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u662f\u8be5\u5e26\u4f60\u4eec\u53bb\u4e86\uff0c\u53ea\u6015\u8d3c\u5b50\u53c8\u6765\u4e86\u5462\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u4ee5\u5c0a\u957f\u4e4b\u793c\u89c1\u8fc7\u8001\u5a46\u5a46\uff0c\u8fde\u58f0\u8d54\u7f6a\u3002\u8001\u5a46\u5a46\u62cd\u62cd\u51cc\u672a\u98ce\u7684\u80a9\u818a\u9053\uff1a\u201c\u554a\uff01\u4f60\u4eec\u662f \u4e00\u540c\u6765\u7684\uff0c\u6211\u5931\u773c\u4e86\u3002\u4f60\u7684\u5251\u6cd5\u771f\u597d\uff0c\u4eca\u665a\u518d\u5e2e\u6211\u4eec\u4e00\u4e2a\u5fd9\u5427\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4f2f\u6bcd\uff0c\u6709\u4e8b\u5c0f\u8f88\u670d\u5176\u52b3\uff0c\u53ea\u7ba1\u5dee\u9063\u597d\u4e86\u3002\u201d\u8001\u5a46\u5a46\u6307\u4e86\u6307\u9ec4\u886b\u5c11\u5e74\u9053\uff1a \u201c\u4ed6\u7238\u7238\u53d7\u4e86\u91cd\u4f24\uff0c\u6211\u5728\u8fd9\u91cc\u670d\u4f8d\u4ed6\uff0c\u5df2\u4e09\u4e2a\u591a\u6708\u4e86\u3002\u8fd9\u5730\u65b9\u6781\u5176\u9690\u79d8\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u6700\u8fd1\u7adf \u5e38\u6709\u751f\u4eba\u5230\u8bbf\uff0c\u6211\u66fe\u4ee5\u91d1\u73af\u6697\u5668\uff0c\u5413\u9000\u8fc7\u51e0\u4e2a\u4eba\uff0c\u6211\u4e00\u51fa\u624b\uff0c\u8fd9\u4e9b\u4eba\u5c31\u98d8\u7136\u8fdc\u53bb\uff0c\u4e5f\u4e0d\u77e5\u662f \u53cb\u662f\u654c\u3002\u5c71\u8c37\u4e2d\u5374\u5e38\u5e38\u53d1\u73b0\u7b26\u53f7\u6807\u8bb0\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4f2f\u6bcd\u521a\u624d\u6240\u8bf4\u7684\u8d3c\u5b50\uff0c\u5c31\u662f\u6307\u8fd9\u4e9b\u4eba \u5417\uff1f\u201d\u8001\u5a46\u5a46\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u8fd9\u4e9b\u4eba\u597d\u50cf\u4e0d\u662f\u4e00\u6279\u7684\uff0c\u6bcf\u6b21\u53d1\u73b0\u7684\u90fd\u662f\u4e00\u4e24\u4f4d\u597d\u624b\u3002\u4e5f \u4e0d\u50cf\u662f\u767d\u9053\u7684\u9e70\u72ac\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u90a3\u4e48\u8d3c\u5b50\u662f\u53e6\u5916\u4e00\u6279\u4eba\u5417\uff1f\u201d\u8001\u5a46\u5a46\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u524d\u6628 \u4e24\u665a\u5c31\u4e0d\u540c\u4e86\uff0c\u7adf\u7136\u53d1\u73b0\u4e86\u6e05\u5bab\u536b\u58eb\u5149\u4e34\u8352\u8c37\uff01\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u6e05\u5bab\u536b\u58eb\uff1f\u54e6\uff0c\u4ed6\u4eec\u6216\u8005\u4ee5 \u4e3a\u6842\u8001\u524d\u8f88\u672a\u6b7b\uff0c\u518d\u6765\u5230\u8bbf\uff0c\u6216\u8005\u662f\u8bbf\u5bfb\u5f53\u65e5\u4ed6\u4eec\u7684\u56db\u4e2a\u540c\u4f34\u3002\u201d \u3000\u3000\u8001\u5a46\u5a46\u542c\u5192\u6d63\u83b2\u63d0\u8d77\u201c\u6842\u8001\u524d\u8f88\u201d\uff0c\u767d\u53d1\u98d8\u52a8\uff0c\u6ee1\u9762\u60b2\u82e6\u4e4b\u5bb9\uff0c\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u4ed6\u548c\u90a3\u56db \u4e2a\u6e05\u5bab\u536b\u58eb\u90fd\u5df2\u57cb\u9aa8\u6b64\u5730\u4e86\uff01\u201d\u8bf4\u7f62\u9ed8\u7136\u4e0d\u8bed\uff0c\u9ec4\u886b\u5c11\u5e74\u8fd9\u65f6\u5ffd\u7136\u54ed\u558a\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8bb0 \u8d77\u6765\u4e86\uff0c\u6842\u3001\u6842\u2026\u2026\u201d\u8001\u5a46\u5a46\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u4ed6\u662f\u4f60\u7684\u517b\u7236\u3002\u201d\u9ec4\u886b\u5c11\u5e74\u5446\u4e86\u4e00\u5446\uff0c\u4e24\u773c\u53d1 \u9752\uff0c\u76f4\u671b\u7740\u8001\u5a46\u5a46\uff0c\u6b63\u662f\uff1a \u3000\u3000\u5eff\u5e74\u5982\u4e00\u68a6\uff0c\u8eab\u4e16\u6700\u79bb\u5947\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5341\u7ae0\u3000\u53f1\u54a4\u6df1\u5c71\u3000\u9ec4\u8863\u85cf\u9690\u79d8\u3000\u7eb5\u6a2a\u53cc\u5251\u3000\u5e7d\u8c37\u4f1a\u7fa4\u8c6a \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u5341\u56de \u53f1\u54a4\u6df1\u5c71\u3000\u9ec4\u8863\u85cf\u9690\u79d8 \u7eb5\u6a2a\u53cc\u5251\u3000\u5e7d\u8c37\u4f1a\u7fa4\u8c6a \u3000\u3000\u8001\u5a46\u5a46\u7528\u8896\u5b50\u66ff\u9ec4\u886b\u5c11\u5e74\u62b9\u4e86\u773c\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u7b49\u4e0b\u8ba9\u4f60\u7236\u4eb2\u548c\u4f60\u8bf4\u3002\u201d\u987f\u4e86 \u4e00\u987f\uff0c\u56de\u5934\u5bf9\u51cc\u672a\u98ce\u9053\uff1a\u201c\u524d\u6628\u4e24\u665a\uff0c\u6709\u51e0\u4e2a\u6e05\u5bab\u536b\u58eb\u7adf\u81ea\u5bfb\u5230\u6211\u4eec\u77f3\u5c4b\uff0c\u7b2c\u4e00\u665a\uff0c\u6211\u548c\u4ed6 \u7236\u4eb2\u7684\u5f92\u5f1f\uff0c\u5408\u529b\u9a71\u9000\u3002\u7b2c\u4e8c\u665a\u4ed6\u4eec\u53c8\u6765\uff0c\u7af9\u541b\u4e00\u4e2a\u4e0d\u5c0f\u5fc3\uff0c\u7ed9\u4ed6\u4eec\u7528\u7529\u624b\u7bad\u4f24\u4e86\u5de6\u81c2\uff0c\u5e78 \u597d\u53ea\u662f\u8f7b\u4f24\u3002\u54e6\uff0c\u5fd8\u8bb0\u544a\u8bc9\u4f60\uff0c\u7af9\u541b\u5c31\u662f\u4ed6\u7684\u59b9\u59b9\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u6211\u8ba4\u5f97\u4ee4\u5a9b\uff0c\u5979\u957f\u5f97\u5f88 \u7f8e\u3002\u201d\u8001\u5a46\u5a46\u62cd\u62cd\u8111\u888b\u8bf4\u9053\uff1a\u201c\u6211\u8001\u7cca\u6d82\u4e86\uff0c\u521a\u624d\u59d1\u5a18\u8c08\u8d77\u5f53\u65e5\u4e4b\u4e8b\uff0c\u6211\u5c31\u8be5\u60f3\u5230\u3002\u5f53\u65e5\u6211 \u867d\u7136\u4e0d\u5728\u5251\u9601\uff0c\u4f46\u542c\u7af9\u541b\u8bf4\u8d77\uff0c\u6709\u4e00\u4f4d\u5112\u51a0\u8001\u8005\u548c\u4e00\u4f4d\u5c11\u5973\u5f53\u665a\u6295\u5bbf\uff0c\u62d4\u5200\u52a9\u6218\uff0c\u628a\u90a3\u51e0\u4e2a \u536b\u58eb\u6740\u6b7b\uff0c\u90a3\u5c11\u5973\u60f3\u5fc5\u5c31\u662f\u59d1\u5a18\u4e86\uff01\u201d\u5192\u6d63\u83b2\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u5112\u51a0\u8001\u8005\u662f\u6211\u7684\u4f2f\u7236\u5085 \u9752\u4e3b\u3002\u201d\u8001\u5a46\u5a46\u8be7\u7136\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u662f\u5f53\u4eca\u56fd\u624b\u5085\u8001\u5148\u751f\uff0c\u6c5f\u6e56\u4e0a\u7fa4\u8c6a\u656c\u4ef0\u7684\u2018\u5927\u6781\u5251\u2019\u5085 \u9752\u4e3b\uff0c\u5f53\u665a\u82e5\u4e0d\u662f\u4f60\u4eec\uff0c\u4ed6\u7684\u517b\u7236\u8bf4\u4e0d\u5b9a\u8981\u53d7\u8bb8\u591a\u51cc\u8fb1\u624d\u80fd\u6b7b\u53bb\u3002\u201d \u3000\u3000\u4e00\u884c\u4eba\u8fb9\u8d70\u8fb9\u8bf4\uff0c\u60d5\u706b\u5df2\u8d8a\u6765\u8d8a\u73b0\u3002\u731b\u7136\u95f4\uff0c\u8001\u5a46\u5a46\u98de\u8eab\u4e00\u63a0\uff0c\u8bf4\u9053\uff1a\u201c\u8d3c\u5b50\u679c\u7136\u53c8\u6765 \u4e86\uff01\u201d\u51cc\u672a\u98ce\u7d27\u8ddf\u7740\u8f6c\u8fc7\u4e00\u4e2a\u4e71\u77f3\u659c\u5761\uff0c\u8033\u8fb9\u5df2\u542c\u89c1\u53f1\u54a4\u4e4b\u58f0\uff0c\u653e\u773c\u770b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u9b41\u609f\u7684 \u9ed1\u5f71\u548c\u4e24\u4e2a\u536b\u58eb\u6597\u5f97\u975e\u5e38\u5403\u529b\uff0c\u51cc\u672a\u98ce\u5927\u559d\u4e00\u58f0\uff0c\u4e24\u679d\u795e\u8292\u62a2\u5728\u8001\u5987\u4eba\u7684\u91d1\u73af\u4e4b\u524d\uff0c\u98de\u5c04\u51fa \u53bb\uff0c\u524d\u9762\u4e24\u58f0\u60e8\u53eb\uff0c\u4e00\u4e2a\u536b\u58eb\u62d4\u6b65\u98de\u9003\uff0c\u8001\u5a46\u5a46\u91d1\u73af\u51fa\u624b\uff0c\u5df2\u81ea\u6253\u4ed6\u4e0d\u7740\u3002 \u3000\u3000\u8001\u5a46\u5a46\u5f53\u5148\u5954\u5230\uff0c\u53ea\u89c1\u4e00\u4e2a\u536b\u58eb\u5c38\u6a2a\u5730\u4e0b\uff0c\u60f3\u662f\u88ab\u795e\u8292\u6253\u6b7b\u7684\uff0c\u90a3\u9b41\u68a7\u6c49\u5b50\u4e00\u628a\u62c9\u4f4f\u8001 \u5a46\u5a46\u8bf4\u9053\uff1a\u201c\u5e08\u5a18\uff0c\u8d76\u5feb\u56de\u53bb\u770b\u770b\u5e08\u7236\u3002\u201d \u3000\u3000\u4f17\u4eba\u968f\u7740\u90a3\u9b41\u68a7\u6c49\u5b50\u8d70\u8fdb\u77f3\u5c4b\uff0c\u53ea\u8d1d\u5c4b\u5f53\u4e2d\u653e\u7740\u4e00\u5f20\u5e8a\uff0c\u5e8a\u7684\u5468\u56f4\u7ad6\u7acb\u7740\u4e2a\u591a\u6839\u67cf\u6728 \u6869\uff0c\u5f53\u7740\u6b63\u4e2d\u7684\u4e09\u6839\u67cf\u6728\u6869\u5df2\u8fde\u6839\u6298\u65ad\u3002\u5e8a\u4e0a\u7761\u7740\u4e00\u4e2a\u7ea2\u9762\u8001\u4eba\uff0c\u5e8a\u8fb9\u6709\u4e00\u4e2a\u5c11\u5973\u6301\u5251\u5b88 \u536b\uff0c\u77f3\u5c4b\u4e2d\u8fd8\u8eba\u7740\u4e00\u4e2a\u6e05\u5bab\u536b\u58eb\u7684\u5c38\u4f53\u3002 \u3000\u3000\u8001\u5a46\u5a46\u4e00\u8fdb\u53bb\u5c31\u95ee\u9053\uff1a\u201c\u4e0d\u59a8\u4e8b\uff1f\u201d\u5c11\u5973\u9053\uff1a\u201c\u54ce\uff0c\u4e0d\u59a8\u4e8b\uff0c\u7238\u7238\u628a\u8fd9\u4e2a\u8d3c\u5b50\u4e00\u811a\u8e22\u6b7b \u4e86\uff01\u201d\u8fd9\u65f6\u9ec4\u886b\u5c11\u5e74\u4e5f\u5df2\u51b2\u5165\uff0c\u5c11\u5973\u4e00\u89c1\uff0c\u60ca\u559c\u4ea4\u96c6\uff01\u62d6\u7740\u9ec4\u886b\u5c11\u5e74\u7684\u624b\uff0c\u5927\u53eb\u201c\u54e5\u54e5\uff01\u201d \u9ec4\u886b\u5c11\u5e74\u5e94\u4e86\u4e00\u58f0\uff0c\u4fbf\u9690\u5f00\u5979\u7684\u624b\uff0c\u65cb\u98ce\u822c\u7684\u5411\u5e8a\u4e0a\u6251\u53bb\uff0c\u4e00\u628a\u62b1\u7740\u7ea2\u9762\u8001\u4eba\uff0c\u54ed\u558a\u9053\uff1a \u201c\u7238\u7238\uff0c\u4f60\u6ca1\u6709\u6b7b\u5417\uff1f\u201d \u3000\u3000\u7ea2\u9762\u8001\u4eba\u521a\u624d\u7528\u529b\u8fc7\u5ea6\uff0c\u5c0f\u7761\u517b\u795e\uff0c\u8fd9\u65f6\u4e00\u542c\u53eb\u58f0\uff0c\u500f\u7684\u5f20\u5f00\u53cc\u773c\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8c01\u6253 \u5f97\u6b7b\u6211\u554a\uff01\u554a\u2026\u2026\u600e\u4e48\u662f\u4f60\u56de\u6765\u4e86\uff01\u201d\u4ed6\u53cc\u76ee\u653e\u5149\uff0c\u84e6\u7136\u8df3\u8d77\uff0c\u8dcc\u5750\u5e8a\u4e0a\uff0c\u660f\u8ff7\u8fc7\u53bb\u3002\u8001\u5a46 \u5a46\u5927\u60ca\u5931\u8272\uff0c\u5192\u6d63\u83b2\u5df2\u67aa\u5728\u524d\u5934\uff0c\u5f20\u773c\u4e00\u77a7\uff0c\u5c06\u8109\u4e00\u629a\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4f2f\u6bcd\uff0c\u4ed6\u5f88\u5feb\u5c31\u4f1a\u9192 \u6765\uff0c\u4f60\u4eec\u4e0d\u8981\u54ed\u558a\uff0c\u4ed6\u8fd9\u662f\u8fc7\u4e8e\u6fc0\u52a8\u6240\u81f4\uff0c\u5e76\u4e0d\u788d\u4e8b\u3002\u201d \u3000\u3000\u90a3\u6301\u5251\u5c11\u5973\u8fd9\u65f6\u5df2\u653e\u597d\u5b9d\u5251\uff0c\u62c9\u7740\u5192\u6d63\u83b2\u7684\u624b\u8c22\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u8fd8\u8bb0\u5f97\u6211\u5417\uff1f\u591a\u8c22\u4f60\u4e24\u6b21 \u63f4\u6551\u6211\u4eec\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u5ba2\u6c14\u8bdd\u4e0d\u5fc5\u591a\u8bf4\u4e86\uff0c\u770b\u6837\u5b50\uff0c\u8001\u4f2f\u662f\u534a\u8eab\u4e0d\u9042\uff0c\u521a\u624d\u53c8\u66fe\u4e0e\u654c\u4eba \u6fc0\u6597\uff0c\u662f\u5417\uff1f\u201d\u5c11\u5973\u6307\u4e00\u6307\u5730\u4e0a\u7684\u5c38\u4f53\u8bf4\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u600e\u6837\u6fc0\u6597\uff0c\u8fd9\u4e2a\u8d3c\u4eba\u5411\u4ed6\u6251\u53bb\u3002\u5728\u67cf \u6728\u6869\u524d\u963b\u4e86\u4e00\u963b\uff0c\u6211\u7684\u7238\u7238\u624b\u8098\u652f\u5e8a\uff0c\u6251\u5730\u817e\u8d77\u4e00\u811a\uff0c\u4e00\u8fde\u626b\u65ad\u4e86\u4e09\u6839\u67cf\u6728\u6869\uff0c\u8d3c\u4eba\u4e5f\u7ed9\u9707 \u5012\u5730\u4e0a\uff0c\u6b7b\u4e86\u3002\u201d\u51cc\u672a\u98ce\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u8001\u4eba\u7684\u4e0b\u76d8\u6b66\u529f\u771f\u9ad8\uff0c\u602a\u4e0d\u5f97\u6842\u5929\u6f9c\u5f53\u65e5\u4f24\u5728\u4ed6\u7684 \u817f\u4e0b\u3002 \u3000\u3000\u5927\u7ea6\u8fc7\u4e86\u4e00\u76cf\u8336\u65f6\u523b\uff0c\u7ea2\u9762\u8001\u4eba\u679c\u7136\u60a0\u60a0\u9192\u8f6c\uff0c\u63fd\u7740\u9ec4\u886b\u5c11\u5e74\u75f4\u75f4\u770b\u7740\uff0c\u5c4b\u4e2d\u7684\u4eba\u5c4f\u606f \u547c\u5438\uff0c\u5192\u6d63\u83b2\u773c\u89d2\u542b\u6709\u6676\u83b9\u7684\u6cea\u73e0\u3002\u826f\u4e45\uff0c\u826f\u4e45\uff0c\u9ec4\u886b\u5c11\u5e74\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7238\u7238\uff0c\u4f60\u544a\u8bc9\u6211\u6211 \u7684\u6765\u5386\u5427\uff01\u201d \u3000\u3000\u7ea2\u9762\u8001\u4eba\u9762\u8272\u500f\u5730\u8f6c\u82cd\u767d\uff0c\u62db\u4e86\u62db\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5988\u5988\u5148\u8bb2\uff0c\u5979\u9053\u6f0f\u7684\u5730\u65b9\u6211\u518d\u8bf4\u3002\u201d \u8001\u5a46\u5a46\u98a4\u5dcd\u5dcd\u5730\u6276\u7740\u9ec4\u886b\u5c11\u5e74\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u540d\u5b57\u53eb\u77f3\u4ef2\u660e\u2026\u2026\u201d\u7ea2\u9762\u8001\u4eba\u5ffd\u7136\u62a2\u7740\u9053\uff1a \u201c\u5e94\u8be5\u53eb\u6842\u4ef2\u660e\u3002\u201d\u8001\u5a46\u5a46\u5706\u7741\u53cc\u773c\uff0c\u7ea2\u9762\u8001\u4eba\u9053\uff1a\u201c\u6211\u662f\u8981\u4ed6\u5ff5\u7740\u4ed6\u7684\u517b\u7236\u3002\u201d\u8001\u5a46\u5a46\u5401 \u4e86\u53e3\u6c14\uff0c\u5e73\u9759\u4e0b\u6765\uff0c\u8fd9\u624d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u7238\u7238\u53eb\u77f3\u5929\u6210\uff0c\u4ed6\u548c\u6842\u5929\u6f9c\u90fd\u662f\u4f60\u5916\u7956\u7684\u5f92\u5f1f\u3002 \u6842\u5929\u6f9c\u662f\u5e08\u5144\uff0c\u4ed6\u662f\u5e08\u5f1f\uff0c\u4f60\u7684\u5916\u79df\u662f\u4e94\u5341\u5e74\u524d\u7684\u5ddd\u4e2d\u5927\u4fa0\u53f6\u4e91\u836a\uff0c\u6211\u662f\u4ed6\u552f\u4e00\u7684\u5973\u513f\u3002 \u3000\u3000\u201c\u4f60\u5916\u7956\u819d\u4e0b\u65e0\u513f\uff0c\u628a\u4ed6\u4eec\u4e24\u4eba\u90fd\u770b\u4f5c\u513f\u5b50\u4e00\u822c\uff0c\u6211\u548c\u4ed6\u4eec\u540c\u65f6\u4e60\u6b66\uff0c\u66f4\u6ca1\u6709\u4ec0\u4e48\u907f \u5fcc\u3002\u4ed6\u4eec\u4e24\u5e08\u5144\u5f1f\u5341\u5206\u8981\u597d\uff0c\u53ea\u662f\u5929\u6210\u813e\u6c14\u66b4\u8e81\uff0c\u5929\u6f9c\u5374\u6781\u6c89\u9759\u3002\u6211\u5bf9\u4ed6\u4eec\u90fd\u50cf\u5144\u5f1f\u4e00\u822c\uff0c \u4f46\u5929\u6210\u76f4\u7387\uff0c\u867d\u7136\u66b4\u8e81\uff0c\u5374\u548c\u6211\u66f4\u5408\u5f97\u6765\u3002 \u3000\u3000\u8fc7\u4e86\u591a\u5e74\uff0c\u6211\u4eec\u4e09\u4eba\u90fd\u957f\u6210\u4eba\u4e86\uff0c\u4e00\u5929\u4f60\u5916\u7956\u7236\u6084\u6084\u95ee\u6211\uff1a\u2018\u59ae\u5b50\uff0c\u4f60\u4e5f\u8be5\u6709\u4e2a\u5bb6\u4e86\uff0c \u4f60\u5b9e\u5728\u5bf9\u6211\u8bf4\uff0c\u4ed6\u4eec\u4e24\u4eba\u4f60\u559c\u6b22\u54ea\u4e00\u4e2a\uff1f\u201d \u3000\u3000\u7ea2\u9762\u8001\u4eba\u542c\u5f97\u51fa\u795e\uff0c\u75f4\u671b\u7740\u8001\u5a46\u5a46\u8bf4\u9053\uff1a\u201c\u8fd9\u6bb5\u6545\u4e8b\u6211\u4e5f\u6ca1\u6709\u542c\u4f60\u8bf4\u8fc7\u5462\uff1f\u201d\u8001\u5a46\u5a46\u5bf9 \u9ec4\u886b\u5c11\u5e74\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u5916\u7956\u7236\u95ee\u6211\uff0c\u90a3\u65f6\u6211\u8fd8\u53ea\u50cf\u6d63\u83b2\u59d1\u5a18\u90a3\u4e48\u5927\uff0c\u4e00\u4e2a\u5973\u5b69\u513f\u5bb6\u90a3\u91cc\u6562 \u8bf4\u3002\u4f60\u5916\u7956\u7236\u81ea\u8a00\u81ea\u8bed\u5730\u9053\uff1a\u5929\u6f9c\u4eba\u5f88\u8001\u6210\uff0c\u6211\u5fcd\u4e0d\u4f4f\u63d2\u53e3\u9053\uff1a\u5c31\u662f\u592a\u8001\u6210\u4e86\uff0c\u5e74\u7eaa\u8f7b\u8f7b\uff0c \u50cf\u4e2a\u8001\u5934\u5b50\u5566\uff01\u4ed6\u53c8\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u5927\u6210\u5374\u662f\u706b\u7206\u7206\u7684\u6027\u5b50\u3002\u6211\u9053\uff1a\u5c31\u662f\u8fd9\u4e00\u70b9\u4e0d\u59a8\uff01\u4f60\u5916\u7956 \u7236\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u4ed6\u4e24\u5e08\u5144\u5f1f\uff0c\u4e00\u5148\u4e00\u540e\uff0c\u6070\u597d\u5728\u8fd9\u51e0\u5929\uff0c\u90fd\u6258\u4eba\u5411\u6211\u6c42\u4eb2\u3002\u6211\u6b63\u81ea\u51b3\u65ad \u4e0d\u4e0b\uff0c\u73b0\u5728\u884c\u5566\uff01\u59d1\u5a18\u81ea\u5df1\u8bf4\u51fa\u6765\u3002\u6211\u7f9e\u5f97\u6025\u6025\u8dd1\u5f00\uff0c\u7b2c\u4e8c\u5929\u4f60\u5916\u7956\u7236\u5c31\u6536\u4e86\u5929\u6210\u7684\u8058 \u793c\u3002\u201d\u7ea2\u9762\u8001\u4eba\u542c\u5230\u8fd9\u91cc\uff0c\u54a7\u5f00\u53e3\u7b11\u4e86\u4e00\u7b11\uff0c\u5f88\u662f\u9ad8\u5174\uff01 \u3000\u3000\u8001\u5a46\u5a46\u9762\u8272\u5374\u5f88\u9634\u6c89\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6ca1\u591a\u4e45\uff0c\u6211\u5c31\u548c\u4f60\u7684\u7238\u7238\u7ed3\u4e86\u5a5a\uff0c\u7b2c\u4e8c\u5e74\u751f\u4e0b\u4e86 \u4f60\uff0c\u9f8b\u8774\u4ef2\u660e\u3002\u65e5\u5b50\u8fc7\u5f97\u5f88\u5feb\u6d3b\uff0c\u970e\u773c\u5c31\u662f\u516d\u5e74\uff0c\u6842\u5929\u6f9c\u5df2\u4e8c\u5341\u51fa\u5934\uff0c\u4e00\u76f4\u6ca1\u6709\u7ed3\u5a5a\u3002\u6211\u4eec \u90fd\u4f4f\u5728\u4f60\u5916\u7956\u7236\u5bb6\u91cc\uff0c\u4ecd\u7136\u50cf\u5144\u5f1f\u59d0\u59b9\u4e00\u6837\u5f80\u6765\uff0c\u975e\u5e38\u8981\u597d\u3002\u4f60\u7238\u7238\u95ee\u4ed6\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u7ed3\u5a5a\uff0c \u4ed6\u6ca1\u6709\u8bf4\u3002\u6211\u6709\u70b9\u731c\u5230\u4ed6\u7684\u5fc3\u4e8b\uff0c\u5374\u4e0d\u4fbf\u8bf4\u3002\u53ef\u662f\u4ed6\u5bf9\u6211\u5374\u4e00\u76f4\u82a5\u8482\u90fd\u6ca1\u6709\uff0c\u66f4\u4ece\u6765\u6ca1\u8bf4\u8fc7 \u534a\u53e5\u98ce\u8a00\u98ce\u8bed\u3002 \u3000\u3000\u201c\u5728\u6211\u4eec\u7ed3\u5a5a\u7684\u65f6\u5019\uff0c\u6ee1\u6d32\u5175\u65e9\u5df2\u5165\u4e86\u5173\u5185\uff0c\u53ef\u662f\u6211\u4eec\u50fb\u5904\u56db\u5ddd\uff0c\u56db\u5ddd\u8fd8\u662f\u5f20\u732e\u5fe0\u7684\u5929 \u4e0b\uff0c\u6211\u4eec\u4e5f\u4e0d\u77e5\u9053\u5916\u9762\u7684\u4e8b\u60c5\uff0c\u5f20\u732e\u5fe0\u540e\u6765\u6218\u8d25\uff0c\u4ed6\u7684\u90e8\u4e0b\u5b59\u53ef\u671b\u548c\u674e\u5b9a\u56fd\u4ecd\u7136\u5360\u7740\u56db\u5ddd\uff0c \u6ee1\u6d32\u519b\u961f\u5fd9\u7740\u6536\u62fe\u4e2d\u539f\uff0c\u4e5f\u6ca1\u6709\u6253\u6765\uff0c\u6211\u4eec\u5c31\u50cf\u4f4f\u5728\u4e16\u5916\u6843\u6e90\u4e00\u6837\u3002\u5230\u4f60\u4e94\u5c81\u7684\u65f6\u5019\uff0c\u6ee1\u6e05 \u5f00\u59cb\u653b\u6253\u56db\u5ddd\uff0c\u4f60\u7238\u7238\u7684\u8001\u5bb6\u5728\u5ddd\u5357\uff0c\u8981\u56de\u53bb\u8fce\u63a5\u5bb6\u4eba\u5230\u5ddd\u5317\u53bb\u907f\u96be\u3002\u90a3\u65f6\u6211\u53c8\u6709\u4e24\u4e2a\u6708\u8eab \u5b55\uff0c\u5f53\u7136\u4e0d\u80fd\u968f\u884c\u3002\u4ed6\u4e34\u8d70\u65f6\u5631\u6258\u5929\u6f9c\u5927\u54e5\u7167\u987e\u6211\u4eec\uff0c\u4fbf\u653e\u5fc3\u56de\u5bb6\u3002 \u3000\u3000\u201c\u4e0d\u6599\u4ed6\u53bb\u540e\u8fd8\u4e0d\u5230\u534a\u6708\uff0c\u6ee1\u6e05\u7684\u5927\u519b\u4fbf\u6d8c\u8fdb\u56db\u5ddd\uff0c\u4ea4\u901a\u65ad\u7edd\uff0c\u767e\u59d3\u6d41\u79bb\uff0c\u4f60\u5916\u7956\u66ae \u5e74\uff0c\u60e8\u906d\u5927\u53d8\uff0c\u6ee1\u6d32\u519b\u961f\u5c1a\u672a\u6253\u5230\uff0c\u4ed6\u5c31\u6b7b\u4e86\uff0c\u4e34\u6b7b\u524d\u53eb\u5929\u6f9c\u4fdd\u62a4\u6211\u4eec\u9003\u96be\u3002 \u3000\u3000\u201c\u9003\u96be\u7684\u65e5\u5b50\u53ef\u60e8\u5566\uff0c\u6ca1\u5403\u6ca1\u559d\u90a3\u662f\u5e38\u4e8b\uff0c\u4f4f\u5bbf\u66f4\u662f\u4e0d\u4fbf\uff0c\u6709\u65f6\u8bb8\u591a\u4eba\u6324\u5728\u4e00\u5904\uff0c\u6709\u65f6 \u9732\u5bbf\u8352\u91ce\uff0c\u5929\u6f9c\u53c8\u8981\u6781\u529b\u907f\u5acc\uff0c\u504f\u504f\u6211\u53c8\u6000\u7740\u8eab\u5b55\uff0c\u79bb\u4e0d\u5f00\u4ed6\uff0c\u90a3\u4e9b\u82eb\u5904\u771f\u662f\u4e00\u8a00\u96be\u5c3d\u3002\u4f60 \u7684\u59b9\u59b9\u5c31\u662f\u5728\u8352\u91ce\u7af9\u4e1b\u4e2d\u4ea7\u4e0b\u6765\u7684\uff0c\u6240\u4ee5\u53eb\u505a\u7af9\u541b\u3002 \u3000\u3000\u201c\u6ee1\u6d32\u519b\u6253\u8fdb\u56db\u5ddd\u540e\uff0c\u8fde\u5e74\u6df7\u6218\uff0c\u6211\u4eec\u9003\u96be\u4e24\u5e74\uff0c\u5f62\u9500\u9aa8\u7626\uff0c\u5230\u5904\u63a2\u8bbf\u4f60\u7238\u7238\u7684\u8e2a\u8ff9\uff0c \u90fd\u6ca1\u7740\u843d\uff0c\u540e\u6765\u542c\u5f97\u6b66\u6797\u540c\u9053\u4f20\u8a00\uff0c\u8bf4\u4ed6\u5df2\u5728\u5175\u8352\u9a6c\u4e71\u4e4b\u4e2d\u6b7b\u53bb\u3002\u6211\u4eec\u5140\u662f\u5c06\u4fe1\u5c06\u7591\u3002 \u3000\u3000\u201c\u9003\u96be\u7684\u751f\u6d3b\u8d8a\u6765\u8d8a\u82e6\uff0c\u6211\u643a\u5e26\u4f60\u4eec\u5144\u59b9\u548c\u5929\u6f9c\u540c\u884c\uff0c\u53c8\u6781\u5176\u4e0d\u4fbf\uff0c\u90a3\u65f6\u5929\u6f9c\u548c\u51e0\u767e\u4e2a \u6bd4\u8f83\u58ee\u5065\u7684\u96be\u6c11\u805a\u5728\u4e00\u8d77\uff0c\u5546\u91cf\u53bb\u6295\u5f20\u732e\u5fe0\u7684\u624b\u4e0b\u674e\u5b9a\u56fd\u3002\u5929\u6f9c\u987e\u8651\u6211\u548c\u4f60\u4eec\u5144\u59b9\uff0c\u6709\u4e9b\u96be \u6c11\u5c31\u544a\u8bc9\u4ed6\u9053\uff1a\u674e\u5b9a\u56fd\u90a3\u91cc\uff0c\u8bbe\u6709\u5973\u8425\uff0c\u53ef\u4ee5\u6536\u5bb9\u6218\u58eb\u7684\u7737\u5c5e\uff0c\u4f46\u4e5f\u53ea\u9650\u4e8e\u6218\u58eb\u7684\u7737\u5c5e\u3002\u4ed6 \u4eec\u90fd\u8bf4\u9053\uff1a\u5728\u9003\u96be\u4e2d\u54ea\u7ba1\u5f97\u8fd9\u8bb8\u591a\uff0c\u4f60\u4eec\u4e24\u4eba\u4e0d\u5982\u6210\u4e86\u5a5a\u5427\uff01\u201d \u3000\u3000\u8001\u5a46\u5a46\u8bf4\u5230\u8fd9\u91cc\uff0c\u53c8\u770b\u4e86\u7ea2\u9762\u8001\u4eba\u4e00\u773c\uff0c\u7ea2\u9762\u8001\u4eba\u9053\uff1a\u201c\u4f60\u8bf4\u4e0b\u53bb\u5427\uff0c\u6211\u73b0\u5728\u660e\u767d\u4e86\uff0c \u8fd9\u4e0d\u662f\u4f60\u7684\u9517\u3002\u201d\u8001\u5a46\u5a46\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u54b1\u4eec\u4e5f\u662f\u51e0\u5341\u5c81\u7684\u4eba\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u5fcc\u8bb3\uff0c\u5f53\u7740\u513f\u5973 \u7684\u9762\uff0c\u8bf4\u4e2a\u6e05\u695a\u4e5f\u597d\u3002\u201d\u6362\u4e86\u53e3\u6c14\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5f53\u665a\uff0c\u5929\u6f9c\u95ee\u6211\u9053\uff1a\u4f60\u7684\u610f\u601d\u600e\u6837\uff1f\u6211\u60f3 \u4e86\u597d\u4e45\uff0c\u56de\u7b54\u4ed6\u9053\uff1a\u5929\u6210\u97f3\u4fe1\u5168\u65e0\uff0c\u513f\u5973\u53c8\u90fd\u5e74\u5c0f\uff0c\u9003\u96be\u6ca1\u5403\u6ca1\u559d\uff0c\u6cb3\u5c71\u53c8\u5df2\u6b8b\u7834\uff0c\u8fd9\u65e5\u5b50 \u4e5f\u771f\u96be\u8fc7\u3002\u9664\u4e86\u6295\u5954\u674e\u5b9a\u56fd\uff0c\u6050\u6015\u4e5f\u6ca1\u6709\u7b2c\u4e8c\u6761\u8def\u597d\u8d70\u7f57\uff01\u5929\u6f9c\u9053\uff1a\u672c\u6765\u6211\u89c6\u5929\u6210\u548c\u4f60\uff0c\u5982 \u540c\u5f1f\u59b9\u3002\u5728\u5e08\u95e8\u5b66\u827a\u65f6\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u662f\u5bf9\u4f60\u6709\u5fc3\u3002\u53ef\u662f\u81ea\u4f60\u4eec\u6210\u4eb2\u540e\uff0c\u6211\u65e9\u5c31\u6b7b\u4e86\u8fd9\u6761 \u5fc3\uff0c\u4e3a\u4e86\u6015\u5929\u6210\u8d77\u7591\uff0c\u6211\u8fd8\u5904\u5904\u9632\u5fae\u675c\u6e10\u3002\u53ef\u662f\u73b0\u5728\u7684\u65e5\u5b50\u8feb\u5f97\u6211\u4eec\u975e\u5728\u4e00\u8d77\u4e0d\u53ef\u3002\u6211\u4eec\u6c5f \u6e56\u513f\u5973\uff0c\u53c8\u4e0d\u662f\u5b54\u592b\u5b50\u7684\u95e8\u5f92\uff0c\u4f60\u4e0d\u5728\u4e4e\u8d1e\u8282\u724c\u574a\uff0c\u6211\u4e5f\u4e0d\u5728\u4e4e\u5be1\u5987\u518d\u91ae\uff0c\u8fd9\u4e9b\u793c\u6cd5\uff0c\u6211\u4eec \u90fd\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u59b9\u5b50\uff0c\u6211\u4eec\u6492\u571f\u4e3a\u9999\uff0c\u7980\u544a\u5929\u6210\u8d24\u5f1f\uff0c\u6c42\u4ed6\u8c05\u89e3\u5427\uff01 \u3000\u3000\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u5f62\u52bf\u8feb\u7136\u3002\u6211\u548c\u5929\u6f9c\u90fd\u613f\u610f\u7ed3\u4e3a\u60a3\u96be\u4e2d\u7684\u4f34\u4fa3\uff0c\u867d\u5728\u9003\u96be\u4e4b\u4e2d\uff0c\u6211\u4eec\u4e5f\u4e0d \u613f\u8349\u7387\uff0c\u7b2c\u4e8c\u5929\u5bf9\u96be\u53cb\u4eec\u4e00\u8bf4\uff0c\u5927\u5bb6\u90fd\u5f88\u9ad8\u5174\u3002\u4ed6\u4eec\u6316\u4e86\u8bb8\u591a\u53ef\u98df\u7684\u8349\u6839\u6811\u76ae\uff0c\u8fd8\u5e78\u8fd0\u5730\u6253 \u5230\u4e86\u4e24\u53ea\u5c71\u732a\uff0c\u5728\u5c0f\u6751\u9547\u627e\u5230\u4e86\u5ea7\u65e0\u4eba\u4f4f\u7684\u7816\u623f\u7ed9\u6211\u4eec\u505a\u65b0\u623f\uff0c\u6709\u4eba\u8fd8\u7528\u6728\u70ad\u5728\u95e8\u4e0a\u5199\u4e0a\u4e24 \u4e2a\u5927\u559c\u5b57\u3002\u4ed6\u4eec\u8bf4\uff0c\u957f\u5e74\u90fd\u5728\u6101\u4e91\u60e8\u96fe\uff0c\u8d81\u8fd9\u4e2a\u65e5\u5b50\u6b22\u4e50\u4e00\u4e0b\u5427\u3002\u7b49\u5929\u6f9c\u5927\u54e5\u6210\u4eb2\u540e\uff0c\u7ed9\u6211 \u4eec\u9886\u5934\uff0c\u5230\u674e\u5b9a\u56fd\u90a3\u91cc\u53bb\u3002 \u3000\u3000\u201c\u8c01\u77e5\u9053\u4e8b\u60c5\u5c31\u6709\u8fd9\u6837\u5de7\uff0c\u5c31\u5728\u90a3\u5929\u665a\u4e0a\uff0c\u6211\u4eec\u5c1a\u672a\u5706\u623f\uff0c\u4f60\u7684\u7238\u7238\u5c31\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u7ea2\u9762\u8001\u4eba\u70b9\u70b9\u5934\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u90a3\u4e48\u5de7\uff0c\u5c31\u4e0d\u81f3\u6709\u4ee5\u540e\u60b2\u60e8\u7684\u4e8b\u4e86\uff0c\u6211\u548c\u4f60\u5988\u5206\u5f00\u540e\uff0c\u5230 \u5ddd\u5357\u53bb\u63a5\u5bb6\u4eba\uff0c\u5728\u8def\u4e0a\u5c31\u78b0\u5230\u6e05\u5175\uff0c\u4e00\u8def\u63d0\u5fc3\u540a\u80c6\uff0c\u4e13\u62e3\u5c0f\u8def\u884c\u8d70\uff0c\u90a3\u6599\u5230\u4e86\u5bb6\u4e61\uff0c\u6211\u7684\u5bb6 \u5df2\u6210\u4e86\u74e6\u783e\uff0c\u5bb6\u4eba\u5168\u90e8\u6b7b\u4e86\uff0c\u6211\u60b2\u6124\u4e4b\u6781\uff0c\u60f3\u6295\u5954\u4e49\u519b\uff0c\u4f46\u53c8\u5ff5\u7740\u59bb\u513f\uff0c\u4e8e\u662f\u53c8\u6298\u56de\u5934\u5bfb \u8bbf\u3002 \u3000\u3000\u201c\u53ef\u662f\u90a3\u65f6\u5904\u5904\u6218\u706b\uff0c\u5730\u65b9\u7cdc\u70c2\uff0c\u6211\u627e\u4e0d\u5230\u59bb\u513f\uff0c\u53ea\u597d\u968f\u7740\u6d41\u6c11\u9003\u96be\uff0c\u7a7f\u5dde\u8fc7\u5e9c\uff0c\u4e00\u9762 \u89c5\u98df\uff0c\u4e00\u9762\u627e\u4f60\u4eec\u3002 \u3000\u3000\u201c\u9003\u96be\u9003\u4e86\u4e24\u5e74\u3002\u4ecd\u662f\u4e00\u70b9\u4e0d\u77e5\u9053\u4f60\u4eec\u7684\u8e2a\u8ff9\uff0c\u8fd9\u4e00\u5929\u9ec4\u660f\uff0c\u6211\u548c\u5341\u591a\u4e2a\u96be\u53cb\u4e5f\u9003\u5230\u90a3 \u4e2a\u5c0f\u6751\u9547\uff0c\u89c1\u53e6\u5916\u4e00\u5e2e\u96be\u6c11\u5174\u9ad8\u91c7\u70c8\uff0c\u53c8\u5531\u53c8\u8df3\uff0c\u975e\u5e38\u5947\u602a\uff0c\u6211\u627e\u7740\u4e00\u4e2a\u4eba\u95ee\uff0c\u4ed6\u8bf4\u662f\u4ed6\u4eec \u7684\u5927\u54e5\u6842\u5929\u6f9c\u96be\u4e2d\u6210\u4eb2\u3002\u6211\u6025\u5fd9\u95ee\u65b0\u5a18\u5b50\u662f\u4ec0\u4e48\u4eba\u3002\u4ed6\u8bf4\u662f\u5e26\u6709\u4e24\u4e2a\u513f\u5973\u7684\u5be1\u5f52\uff0c\u8fd8\u542c\u8bf4\u662f \u5ddd\u4e2d\u5927\u4fa0\u53f6\u4e91\u836a\u7684\u5973\u513f\u54e9\uff01 \u3000\u3000\u201c\u6211\u4e00\u542c\u540e\u8840\u6db2\u6cb8\u817e\uff0c\u5fc3\u5934\u706b\u6eda\uff0c\u626d\u8f6c\u5934\u4fbf\u8dd1\u3002\u6211\u90a3\u65f6\u75db\u5931\u5bb6\u4eba\uff0c\u53c8\u7ecf\u4f18\u60a3\uff0c\u4e0d\u5982\u610f\u4e8b \u592a\u591a\uff0c\u672c\u6765\u66b4\u8e81\u7684\u6027\u5b50\u65b0\u52a0\u66b4\u8e81\u4e86\uff01\u4e5f\u4e0d\u6653\u5f97\u60f3\u60f3\u522b\u4eba\u7684\u5904\u5883\uff0c\u53ea\u6068\u5f97\u624d\u75d2\u75d2\u7684\uff0c\u81ea\u601d\uff1a\u6211 \u5c0a\u5929\u6f9c\u5982\u4eb2\u54e5\uff0c\u6258\u59bb\u5bc4\u5b50\uff0c\u4ed6\u7adf\u4e58\u7740\u6211\u59bb\u5b50\u5728\u96be\uff0c\u8feb\u4f7f\u6210\u5a5a\uff0c\u8d3c\u5b50\u72e0\u5fc3\uff0c\u771f\u4e0d\u53ef\u6055\uff0c\u53ea\u56e0\u6211 \u548c\u59bb\u5b50\u4e00\u5411\u6781\u4e3a\u6069\u7231\uff0c\u6240\u4ee5\u4e00\u542c\u5230\u6b64\u4e8b\uff0c\u5c31\u628a\u7f6a\u8fc7\u5168\u63a8\u5728\u5929\u6f9c\u8eab\u4e0a\u3002\u4f46\u505c\u4e0b\u4e00\u60f3\uff0c\u4e0d\u77e5\u9053\u59bb \u5b50\u53d8\u5fc3\u6ca1\u6709\uff1f\u5f53\u665a\u6211\u4e0d\u52a0\u8003\u8651\uff0c\u5c31\u591c\u63a2\u4ed6\u4eec\u7684\u6d1e\u623f\u3002\u201d \u3000\u3000\u7ea2\u9762\u8001\u4eba\u505c\u4e86\u4e00\u4e0b\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u8bb0\u5f97\u90a3\u662f\u4e2a\u6708\u9ed1\u98ce\u9ad8\u4e4b\u591c\uff0c\u6211\u6ee1\u8138\u64e6\u4e0a\u7164\u70df\uff0c\u5c31 \u53bb\u591c\u63a2\u4ed6\u4eec\u7684\u6d1e\u623f\uff0c\u63d0\u9632\u88ab\u8ba4\u51fa\u3002\u5fc3\u60f3\uff0c\u770b\u4ed6\u4eec\u5230\u5e95\u600e\u6837\uff1f\u5982\u679c\u6211\u7684\u59bb\u5b50\u662f\u88ab\u5929\u6f9c\u5f3a\u8feb\u6210\u5a5a \u7684\uff0c\u6211\u5c31\u628a\u8fd9\u4eba\u9762\u517d\u5fc3\u7684\u4e1c\u897f\u6740\u6389\uff1b\u5982\u679c\u662f\u5979\u81ea\u5df1\u613f\u610f\u7684\uff0c\u6211\u5c31\u628a\u4ed6\u4eec\u4e24\u4e2a\u90fd\u6740\u6389\u3002 \u3000\u3000\u201c\u6211\u672c\u60f3\u8fc7\u4e86\u4e09\u66f4\u53bb\uff0c\u4f46\u5165\u9ed1\u4e4b\u540e\uff0c\u81ea\u5df1\u5c31\u50cf\u70ed\u9505\u4e0a\u7684\u8682\u8681\u4e00\u6837\uff0c\u600e\u6837\u4e5f\u5fcd\u53d7\u4e0d\u4f4f\uff0c\u8fdc \u8fdc\u77a7\u89c1\u90a3\u7fa4\u96be\u6c11\u8d3a\u5ba2\u9646\u7eed\u8d70\u51fa\u65b0\u623f\u4e4b\u540e\uff0c\u6211\u5c31\u5c55\u5f00\u591c\u884c\u672f\uff0c\u5230\u4ed6\u4eec\u2018\u65b0\u623f\u2019\u5916\u9762\u5077\u542c\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u542c\uff0c\u66f4\u628a\u6211\u6c14\u5f97\u80ba\u90fd\u70b8\u4e86\u3002\u6211\u7684\u59bb\u5b50\u5728\u91cc\u9762\u5429\u5490\u5b69\u5b50\u8bf4\uff1a\u4f60\u8bb0\u5f97\u4ece\u660e\u5929\u8d77\u6539\u53eb\u6842 \u4f2f\u4f2f\u505a\u7238\u7238\u3002\u5979\u7684\u58f0\u8c03\u4e00\u5982\u5e73\u5e38\uff0c\u542c\u4e0d\u51fa\u6709\u4ec0\u4e48\u60b2\u82e6\u7684\u611f\u89c9\u3002\u6211\u6b63\u60f3\u52a8\u624b\uff0c\u5ffd\u542c\u5f97\u5929\u6f9c\u5927\u53eb \u4e00\u58f0\u6709\u8d3c\uff0c\u6211\u4e00\u6012\u5c31\u5c04\u8fdb\u51e0\u679d\u7529\u624b\u7bad\uff0c\u6211\u7684\u59bb\u5b50\uff0c\u4e5f\u4e00\u626c\u624b\u6253\u51fa\u4e86\u51e0\u679a\u8033\u73af\uff0c\u90a3\u662f\u5979\u81ea\u5c0f\u7ec3 \u5c31\u7684\u72ec\u95e8\u6697\u5668\uff01\u201d \u3000\u3000\u8001\u5a46\u5a46\u9762\u8272\u82cd\u767d\uff0c\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u90a3\u65f6\u6211\u4eec\u505a\u68a6\u4e5f\u6599\u4e0d\u5230\u662f\u4f60\u3002\u6211\u7684\u82e6\u695a\u5728\u4e24\u5e74\u9003\u96be\u4e2d\uff0c \u4ec0\u4e48\u4e5f\u6328\u8fc7\u4e86\uff0c\u8981\u6709\u773c\u6cea\u7684\u8bdd\uff0c\u6cea\u4e5f\u6d41\u5c3d\u4e86\u3002\u90a3\u65f6\u6211\u4eec\u4ee5\u4e3a\u4f60\u5df2\u6b7b\u4e86\uff0c\u5c31\u662f\u4e0d\u6b7b\uff0c\u4e5f\u96be\u4ee5\u751f \u9022\u4e86\u3002\u5929\u6f9c\u5bf9\u6211\u597d\u6781\uff0c\u6211\u65e2\u613f\u610f\u5ac1\u4ed6\uff0c\u81ea\u7136\u8be5\u53eb\u5b69\u5b50\u5524\u4ed6\u505a\u7238\u7238\uff0c\u6599\u4e0d\u5230\u4f60\u7a81\u7136\u5230\u6765\uff0c\u800c\u4e14 \u4e0d\u5206\u7682\u767d\uff0c\u4e00\u626c\u624b\u5c31\u6697\u5668\u7eb7\u98de\u3002\u6211\u4eec\u53ea\u9053\u4f60\u662f\u574f\u4eba\uff0c\u56e0\u6b64\u6211\u624d\u7528\u8033\u73af\u6253\u4f60\u7684\u7a74\u9053\u3002\u201d \u3000\u3000\u7ea2\u9762\u8001\u4eba\u51c4\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8bb2\u4e86\uff0c\u73b0\u5728\u6211\u4e00\u5207\u90fd\u5df2\u660e\u767d\uff0c\u90a3\u662f\u6211\u7684\u8fc7\u9519\u3002\u4f46\u90a3 \u65f6\u6012\u706b\u653b\u5fc3\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u77e5\u9053\uff0c\u5929\u6f9c\u7eb5\u8eab\u51fa\u6765\uff0c\u6211\u4e00\u7167\u9762\u5c31\u7ed9\u5730\u51e0\u8bb0\u8fa3\u62db\u3002\u201d \u3000\u3000\u201c\u90a3\u6599\u5929\u6f9c\u529f\u529b\u6bd4\u6211\u6df1\u539a\u5f97\u591a\uff0c\u51e0\u62db\u4e00\u8fc7\uff0c\u6211\u5c31\u77e5\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u3002\u90a3\u65f6\u4f60\u4e5f\u8dd1\u4e86\u51fa\u6765\u52a9 \u9635\uff0c\u6211\u662f\u6c14\u6124\u4e4b\u6781\uff0c\u5fc3\u60f3\uff1a\u597d\uff01\u4f60\u4eec\u4e24\u4eba\u65e2\u8054\u6210\u4e00\u6c14\uff0c\u4eca\u665a\u6211\u53ea\u597d\u5fcd\u8fb1\u9003\u8dd1\uff0c\u518d\u5728\u6c5f\u6e56\u6295\u5954 \u540d\u5e08\uff0c\u7ec3\u6210\u7edd\u6280\uff0c\u600e\u6837\u4e5f\u5f97\u62a5\u593a\u59bb\u593a\u5b50\u4e4b\u4ec7\uff01\u201d \u3000\u3000\u201c\u8fd9\u65f6\u5929\u6f9c\u907f\u8fc7\u6211\u51e0\u8bb0\u9669\u62db\uff0c\u5927\u7ea6\u5df2\u770b\u51fa\u662f\u540c\u95e8\u5bb6\u6570\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u4f60\u662f\u8c01\uff1f\u5feb\u70b9\u8bf4\u51fa \u6765\uff0c\u4ee5\u514d\u81ea\u8bef\uff01\u5728\u4ed6\u5927\u559d\u4e4b\u65f6\uff0c\u4f60\u4e00\u679a\u8033\u73af\uff0c\u53c8\u53d6\u6211\u7684\u4e09\u91cc\u7a74\uff0c\u8fd8\u6709\u672a\u8d70\u5b8c\u7684\u8d3a\u5ba2\u6253\u6765\u7684\u77f3 \u5934\u548c\u5c04\u6765\u7684\u7bad\uff0c\u6211\u95f7\u58f0\u4e0d\u7b54\uff0c\u8131\u4e0b\u4e86\u8eab\u4e0a\u7684\u9ec4\u886b\uff0c\u90a3\u662f\u4f60\u65b0\u5a5a\u540e\u7ed9\u6211\u505a\u7684\uff0c\u6211\u820d\u4e0d\u5f97\u7a7f\uff0c\u90a3 \u5929\u665a\u4e0a\uff0c\u7279\u5730\u7a7f\u4e0a\uff0c\u60f3\u6c14\u6c14\u4f60\u7684\uff0c\u53ef\u662f\u4f60\u7adf\u770b\u4e0d\u51fa\u6765\u3002\u6211\u8131\u4e0b\u9ec4\u886b\uff0c\u5c55\u5f00\u94c1\u5e03\u886b\u5de5\u592b\uff0c\u628a\u77f3 \u5934\u7fbd\u7bad\uff0c\u7eb7\u7eb7\u6253\u843d\uff0c\u4f46\u4e3a\u4e86\u907f\u4f60\u90a3\u51e0\u679a\u8033\u73af\uff0c\u7f13\u5f97\u4e00\u7f13\uff0c\u7adf\u7ed9\u4e24\u7fbd\u7bad\u5c04\u4f24\uff0c\u9c9c\u8840\u67d3\u5728\u9ec4\u886b \u4e0a\u3002\u6211\u628a\u9ec4\u886b\u5411\u5929\u6f9c\u515c\u5934\u4e00\u7f69\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u6709\u80c6\u7684\uff0c\u4f60\u628a\u6211\u6740\u4e86\u5427\uff01\u4ed6\u2018\u549a\u2019\u7684\u4e00\u58f0\uff0c\u5012\u5728 \u5730\u4e0a\u3002\u6211\u8f6c\u8fc7\u8eab\u4fbf\u8dd1\uff0c\u4ee5\u540e\u4f60\u4eec\u600e\u6837\u95f9\u6cd5\uff0c\u6211\u90fd\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u8001\u5a46\u5a46\u9053\uff1a\u201c\u90a3\u65f6\u6211\u4e5f\u542c\u51fa\u4e86\u4f60\u7684\u58f0\u97f3\uff0c\u6574\u4e2a\u90fd\u50bb\u5728\u90a3\u513f\uff0c\u7b49\u5230\u6e05\u9192\u65f6\uff0c\u54ea\u91cc\u8fd8\u77a7\u5f97\u89c1 \u4f60\u7684\u5f71\u5b50\uff1f\u6211\u53ea\u597d\u628a\u5929\u6f9c\u6551\u9192\u8fc7\u6765\u3002\u201d \u3000\u3000\u8001\u5a46\u5a46\u8bf4\u5230\u8fd9\u91cc\uff0c\u5927\u5bb6\u90fd\u611f\u5230\u5fc3\u5934\u6c89\u91cd\uff0c\u7a7a\u6c14\u90fd\u597d\u4f3c\u51dd\u7ed3\u8d77\u6765\u3002\u5192\u6d63\u83b2\u8f7b\u8f7b\u53f9\u53e3\u6c14\u9053\uff1a \u201c\u8fd9\u90fd\u662f\u56e0\u4e3a\u6218\u4e89\uff01\u201d\u8001\u5a46\u5a46\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u662f\u7684\uff0c\u8c01\u90fd\u6ca1\u6709\u9519\uff0c\u9519\u7684\u662f\u6218\u4e89\u3002\u662f\u6218\u4e89\u62c6\u6563 \u4e86\u5bb6\u5ead\uff0c\u5206\u79bb\u4e86\u597d\u53cb\uff0c\u5f15\u8d77\u4e86\u8bef\u4f1a\uff0c\u9020\u6210\u4e86\u60e8\u5267\u3002\u8fd9\u7b14\u5e10\u8981\u8bb0\u5728\u6ee1\u6d32\u9791\u5b50\u8eab\u4e0a\uff01\u201d \u3000\u3000\u8001\u5a46\u5a46\u7f13\u4e86\u53e3\u6c14\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5929\u6f9c\u9192\u6765\u540e\uff0c\u773c\u6cea\u76f4\u6d41\uff0c\u8fc7\u4e86\u8bb8\u4e45\uff0c\u624d\u5bf9\u6211\u8bf4\uff1a\u59b9\u5b50\uff0c \u5929\u6210\u8fd8\u5728\u4eba\u95f4\uff0c\u54b1\u4eec\u65e0\u8bba\u5982\u4f55\u4e5f\u5f97\u5bfb\u7740\u4ed6\uff0c\u8ba9\u4f60\u4eec\u5bb6\u5ead\u56e2\u5706\u3002\u6211\u5f53\u7136\u4e5f\u662f\u8fd9\u6837\u60f3\uff0c\u53ef\u662f\u5929\u6210 \u706b\u7206\u7684\u6027\u5b50\uff0c\u6211\u77e5\u9053\u5f97\u6700\u6e05\u695a\uff0c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6050\u6015\u4ed6\u81f3\u6b7b\u4e5f\u4e0d\u4f1a\u539f\u8c05\u6211\u4eec\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u51b7\u9759\u4e0b\u6765\u4e4b\u540e\uff0c\u518d\u4ece\u957f\u8ba1\u8bae\u3002\u5929\u6f9c\u9053\uff1a\u4e8b\u5df2\u81f3\u6b64\uff0c\u59b9\u5b50\uff0c\u8981\u59d4\u5c48\u4f60\uff0c\u54b1\u4eec\u8fd8\u662f\u505a \u6302\u540d\u592b\u59bb\u5427\uff0c\u4eba\u6d77\u832b\u832b\uff0c\u592d\u6210\u4e00\u65f6\u96be\u4e8e\u5bfb\u627e\uff0c\u9003\u96be\u7684\u65e5\u5b50\uff0c\u53c8\u5b9e\u5728\u8fc7\u4e0d\u4e0b\u53bb\uff0c\u4f55\u51b5\u4f60\u8fd8\u6709\u4e24 \u4e2a\u5c0f\u5b69\u7eca\u7740\u8eab\u5b50\uff0c\u4e5f\u53ea\u6709\u5148\u6295\u5954\u674e\u5b9a\u56fd\u518d\u8bf4\u7f62\uff01\u5c31\u8fd9\u6837\uff0c\u6211\u4eec\u5e26\u9886\u7740\u4e00\u7fa4\u96be\u6c11\uff0c\u6295\u5230\u674e\u5b9a\u56fd \u519b\u4e2d\uff0c\u6211\u4eec\u8868\u9762\u4e0a\u662f\u592b\u59bb\u79f0\u547c\uff0c\u5b9e\u9645\u4e0a\u5374\u4ee5\u5144\u59b9\u76f8\u5f85\u3002\u73b0\u5728\u6211\u4e5f\u4e0d\u6015\u8bf4\u51fa\u6765\uff0c\u51e0\u5341\u5e74\u6765\uff0c\u6211 \u548c\u5929\u6f9c\u53ef\u90fd\u662f\u7389\u6d01\u51b0\u6e05\uff0c\u6ca1\u6709\u8fc7\u534a\u70b9\u82df\u4e14\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u7ea2\u9762\u8001\u4eba\u7528\u8896\u5b50\u63e9\u4e86\u63e9\u773c\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u59b9\u5b50\uff0c\u8fd9\u4e2a\u6211\u65e9\u5df2\u77e5\u9053\u4e86\uff01\u201d\u8001\u5a46\u5a46\u770b\u4e86\u4ed6\u4e00 \u773c\uff0c\u6b63\u5f85\u53d1\u95ee\uff0c\u7ea2\u9762\u8001\u4eba\u5374\u4e0d\u505c\u53e3\u5730\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u53ef\u662f\u90a3\u65f6\u6211\u5374\u628a\u4f60\u4eec\u6068\u900f\u4e86\u3002\u6211\u4ed7\u7740\u5355\u8eab \u4e00\u4eba\uff0c\u65e0\u7275\u65e0\u6302\uff0c\u56db\u5904\u98d8\u6d41\u3002\u540e\u6765\u76f4\u8d70\u5230\u56de\u7586\uff0c\u5728\u5929\u5c71\u4e4b\u5357\uff0c\u9047\u5230\u4e86\u4e5f\u662f\u4e07\u91cc\u6295\u8352\u3001\u9690\u8eab\u6f20 \u5916\u7684\u6b66\u5f53\u540d\u5bbf\u5353\u4e00\u822a\uff0c\u8ddf\u4ed6\u5b66\u4e86\u4e5d\u5bab\u795e\u884c\u638c\u548c\u9e33\u9e2f\u8fde\u73af\u817f\u4e24\u6837\u7edd\u6280\u3002\u5f53\u65f6\u6211\u4e3a\u4e86\u6068\u4f60\u4eec\uff0c\u53d1 \u8a93\u4e0d\u518d\u7528\u4f60\u7236\u4eb2\u4f20\u6388\u7684\u529f\u592b\u3002\u6211\u4e5f\u81ea\u77e5\uff0c\u82e5\u8bba\u5230\u672c\u95e8\u529f\u592b\uff0c\u5929\u6f9c\u548c\u4f60\u90fd\u8981\u6bd4\u6211\u9ad8\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u8fd9\u65f6\u63d2\u4e86\u53e5\u8bdd\u9053\uff1a\u201c\u5353\u4e00\u822a\u6211\u5c0f\u65f6\u5019\u4e5f\u89c1\u8fc7\uff0c\u4ed6\u662f\u5e08\u7236\u6666\u660e\u7985\u5e08\u7684\u597d\u53cb\u3002\u53ef\u60dc\u6211 \u5230\u5929\u5c71\u6ca1\u591a\u4e45\uff0c\u4ed6\u5c31\u6b7b\u5566\u3002\u201d\u7ea2\u9762\u8001\u4eba\u7741\u5927\u773c\u775b\u770b\u770b\u51cc\u672a\u98ce\uff0c\u201c\u566b\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u539f\u6765\u4f60\u5c31 \u662f\u6666\u660e\u7985\u5e08\u7684\u5173\u95e8\u5f92\u5f1f\u3002\u6211\u98d8\u6d41\u5230\u56de\u7586\u65f6\uff0c\u4e5f\u542c\u5f97\u6666\u660e\u7985\u5e08\u5927\u540d\u3002\u60f3\u8ddf\u4ed6\u5b66\u5251\uff0c\u53ef\u662f\u4e09\u4e0a\u5929 \u5c71\u4ed6\u90fd\u4e0d\u80af\u6536\u6211\u3002\u540e\u6765\u7ed9\u6211\u78e8\u5f97\u592a\u591a\uff0c\u624d\u53eb\u6211\u53e6\u6295\u540d\u5e08\uff0c\u6307\u5f15\u6211\u53bb\u89c1\u5353\u4e00\u822a\u3002\u4ed6\u8001\u4eba\u5bb6\u73b0\u5728 \u6050\u6015\u5df2\u8fd1\u767e\u5c81\u5927\u5bff\u4e86\u3002\u201d\u8001\u5a46\u5a46\u4e5f\u70b9\u70b9\u5934\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u5251\u6cd5\u8fd9\u6837\u5389\u5bb3\uff01\u7b97\u8d77\u6765\u4f60\u8fd9\u5c0f\u4f19\u5b50 \u7adf\u8ddf\u6211\u4eec\u4e24\u8001\u662f\u540c\u8f88\u3002\u201d\u51cc\u672a\u98ce\u5fae\u5fae\u4e00\u7b11\uff0c\u8fde\u9053\u201c\u4e0d\u6562\uff01\u201d \u3000\u3000\u7ea2\u9762\u8001\u4eba\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5353\u4e00\u822a\u662f\u6666\u660e\u7985\u5e08\u7684\u597d\u53cb\uff0c\u6b66\u529f\u81ea\u7136\u4e5f\u662f\u9876\u5c16\u513f\u7684\u3002\u6211\u5b66\u4e86\u4e03 \u5e74\uff0c\u81ea\u4fe1\u4e24\u79cd\u7edd\u6280\u5df2\u5f97\u771f\u4f20\u3002\u5c31\u8d76\u56de\u56db\u5ddd\u5bfb\u627e\u4f60\u4eec\u62a5\u4ec7\uff0c\u8fd9\u65f6\u56db\u5ddd\u65e9\u5df2\u88ab\u6e05\u519b\u5e73\u5b9a\uff0c\u53ea\u6709\u674e \u95ef\u738b\u7684\u6b8b\u90e8\uff0c\u8fd8\u5360\u5728\u5ddd\u6ec7\u8fb9\u533a\u3002\u5927\u52ab\u4e4b\u540e\uff0c\u9762\u76ee\u5168\u975e\uff1b\u4eb2\u621a\u6545\u65e7\uff0c\u534a\u767b\u9b3c\u57df\uff0c\u6211\u600e\u6837\u4e5f\u627e\u4e0d \u5230\u4f60\u4eec\uff0c\u4e5f\u65e0\u4ece\u6253\u542c\u3002\u540e\u6765\u8f97\u8f6c\u5bfb\u8bbf\uff0c\u5076\u7136\u542c\u6b66\u6797\u540d\u624b\u8bf4\u8d77\uff0c\u5251\u9601\u7edd\u9876\uff0c\u9690\u6709\u9ad8\u4eba\uff0c\u6211\u731c\u662f \u5929\u6f9c\uff0c\u8fd9\u624d\u4e24\u756a\u5230\u6765\u5bfb\u4ec7\u6253\u6597\uff01\u201d \u3000\u3000\u8001\u5a46\u5a46\u9053\uff1a\u201c\u6211\u4eec\u6295\u5954\u4e86\u674e\u5b9a\u56fd\u540e\uff0c\u4e0d\u4e45\u4fbf\u5f97\u5230\u91cd\u7528\u3002\u5929\u6f9c\u6210\u4e86\u674e\u5b9a\u56fd\u7684\u5fc3\u8179\u7231\u5c06\uff0c\u6211 \u4e5f\u5e2e\u7740\u7ba1\u7406\u5be8\u8425\u4e8b\u52a1\uff0c\u672c\u6765\u9ad8\u7ea7\u5c06\u9886\u662f\u53ef\u4ee5\u548c\u5bb6\u5c5e\u540c\u4f4f\u7684\uff0c\u4f46\u6211\u4eec\u5374\u81ea\u613f\u5206\u5f00\u3002\u674e\u5b9a\u56fd\u6709\u4e00 \u5929\u95ee\u53ca\uff0c\u5929\u6f9c\u628a\u5168\u90e8\u4e8b\u60c5\u90fd\u544a\u8bc9\u4e86\u4ed6\uff0c\u674e\u5b9a\u56fd\u6168\u7136\u8bf4\u9053\uff1a\u6211\u5fc5\u5b9a\u5e2e\u4f60\u7684\u5fd9\uff0c\u8981\u4ee4\u4f60\u4eec\u5144\u5f1f\u548c \u597d\uff0c\u592b\u59bb\u91cd\u5706\u3002\u4ed6\u4e5f\u771f\u591f\u4e49\u6c14\uff0c\u5728\u519b\u52a1\u7e41\u5fd9\u4e2d\uff0c\u8fd8\u6d3e\u4eba\u5230\u5904\u67e5\u8bbf\u5929\u6210\u7684\u4e0b\u843d\uff0c\u8c01\u77e5\u5927\u6210\u7adf\u662f \u5230\u4e86\u56de\u7586\u5462\uff01\u201d \u3000\u3000\u201c\u90a3\u4ef6\u9ec4\u886b\uff0c\u90a3\u4ef6\u6211\u65b0\u5a5a\u540e\u4eb2\u624b\u6240\u505a\u7ed9\u5929\u6210\u7684\u674f\u9ec4\u886b\u5b50\uff0c\u6211\u628a\u5b83\u73cd\u85cf\u8d77\u6765\u3002\u886b\u4e0a\u8fd8\u67d3\u6709 \u5929\u6210\u7684\u51e0\u70b9\u9c9c\u8840\uff0c\u6211\u8981\u628a\u5b83\u7559\u7ed9\u4ef2\u660e\u3002\u4ef2\u660e\u4ece\u5c0f\u81f3\u5927\uff0c\u6211\u7ed9\u4ed6\u505a\u7684\u8863\u670d\uff0c\u4e5f\u90fd\u662f\u9ec4\u8272\u7684\uff0c\u519b \u4e2d\u53eb\u4ed6\u505a\u9ec4\u886b\u513f\u3002\u6709\u4eba\u5947\u602a\u95ee\u6211\uff0c\u4e3a\u4ec0\u4e48\u603b\u662f\u505a\u9ec4\u886b\u7ed9\u5b69\u5b50\u7a7f\uff1f\u6211\u53ea\u662f\u82e6\u7b11\u4e0d\u7b54\u3002\u8fd9\u539f\u56e0\uff0c \u6211\u4e00\u76f4\u6ca1\u6709\u5bf9\u4ef2\u660e\u8bf4\u8fc7\uff0c\u6211\u53d1\u8a93\u8981\u7b49\u4ed6\u4eec\u7236\u5b50\u89c1\u9762\u540e\uff0c\u624d\u544a\u8bc9\u4ed6\u77e5\u9053\uff0c\u5929\u53ef\u601c\u89c1\uff0c\u4eca\u5929\u4ed6\u4eec \u7236\u5b50\u5230\u5e95\u662f\u89c1\u7740\u9762\u4e86\uff01\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u542c\u5230\u8fd9\u91cc\uff0c\u6cea\u6d41\u6ee1\u9762\uff0c\u4f4e\u4f4e\u5524\u4e86\u4e00\u58f0\u201c\u5988\u5988\u201d\u8001\u5a46\u5a46\u7528\u624b\u8f7b\u8f7b\u629a\u6478\u4ed6\u7684\u5934\u53d1\uff0c \u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u674e\u5b9a\u56fd\u521d\u65f6\u5360\u636e\u5ddd\u9ed4\u529b\u6297\u6e05\u5175\uff0c\u58f0\u52bf\u4e5f\u5f88\u6d69\u5927\uff0c\u53ef\u60dc\u5915\u9633\u867d\u597d\uff0c\u5df2\u8fd1\u9ec4\u660f\uff0c\u6e05 \u519b\u5e73\u5b9a\u4e2d\u539f\u4e4b\u540e\uff0c\u5174\u5175\u4e09\u8def\uff0c\u5927\u4e3e\u6765\u653b\uff0c\u6d2a\u627f\u7574\u3001\u5434\u4e09\u6842\u7b49\u5927\u6c49\u5978\u90fd\u662f\u6ee1\u519b\u7684\u524d\u9a71\uff0c\u800c\u5f20\u732e \u5fe0\u4f59\u90e8\u7684\u53e6\u4e00\u80a1\u4e3b\u5e08\u5b59\u53ef\u671b\u5ffd\u7136\u5728\u9635\u524d\u53db\u53d8\uff0c\u6295\u964d\u4e86\u6ee1\u6e05\u3002\u674e\u5b9a\u56fd\u4e00\u8def\u8d25\u9000\uff0c\u76f4\u7ed9\u9000\u5230\u7f05 \u7538\uff0c\u5728\u5b5f\u814a\u5410\u8840\u800c\u4ea1\u3002\u4e34\u6b7b\u524d\u4ed6\u5728\u75c5\u69bb\u4e0a\u4ea4\u4ee3\u519b\u52a1\u4e4b\u540e\uff0c\u5c06\u4e00\u5c01\u4fe1\u4ea4\u7ed9\u5929\u6f9c\uff0c\u8bf4\u9053\uff1a\u82e5\u4f60\u4ed6 \u65e5\u89c1\u7740\u5929\u6210\uff0c\u5c06\u8fd9\u5c01\u4fe1\u4ea4\u7ed9\u4ed6\u770b\u5427\uff01\u5929\u6210\u65e2\u662f\u6b66\u6797\u540d\u5bb6\u5f1f\u5b50\uff0c\u4ed6\u4e0d\u76f8\u4fe1\u4f60\uff0c\u4e5f\u8be5\u76f8\u4fe1\u6211\uff01\u674e \u5b9a\u56fd\u662f\u4e00\u519b\u4e3b\u5e05\uff0c\u82f1\u98ce\u4fa0\u6c14\uff0c\u5f53\u65f6\u771f\u53ef\u8bf4\u662f\u4e07\u6d41\u666f\u4ef0\u3002\u4ed6\u7684\u8bdd\u4e00\u8a00\u4e5d\u9f0e\uff0c\u771f\u96be\u5f97\u4ed6\u5728\u4e34\u6b7b\u65f6 \u8fd8\u6ca1\u6709\u5fd8\u8bb0\u5929\u6f9c\u7684\u4e8b\uff01\u201d \u3000\u3000\u201c\u674e\u5b9a\u56fd\u6b7b\u540e\uff0c\u6211\u4eec\u4ece\u7f05\u7538\u56de\u6765\uff0c\u90a3\u65f6\u5ddd\u7701\u4e49\u519b\u5df2\u5168\u90e8\u74e6\u89e3\u3002\u5929\u6f9c\u53eb\u6211\u4e0e\u4ed6\u540c\u5230\u5251\u9601\u9690 \u5c45\u3002\u4ed6\u8bf4\u4ed6\u4ee5\u524d\u66fe\u5949\u674e\u5b9a\u56fd\u4e4b\u547d\uff0c\u5230\u8fc7\u5251\u9601\u51e0\u6b21\uff0c\u90a3\u91cc\u679c\u6728\u91ce\u517d\u5f88\u591a\uff0c\u53ef\u4ee5\u4e0d\u6101\u751f\u6d3b\u3002\u81f3\u4e8e \u4ed6\u4ee5\u524d\u53bb\u5251\u9601\u505a\u4ec0\u4e48\uff0c\u4ed6\u6ca1\u6709\u8bf4\uff0c\u6211\u4e5f\u6ca1\u6709\u95ee\u3002\u201d \u3000\u3000\u7ea2\u9762\u8001\u4eba\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u6211\u63a2\u51fa\u4ed6\u4eec\u5728\u5251\u9601\u9690\u5c45\u4e4b\u540e\uff0c\u5c31\u6500\u767b\u6808\u9053\u53bb\u5bfb\u627e\u4ed6\u4eec\uff0c\u90a3\u65f6\u6211\u4e5f \u6536\u4e86\u4e00\u4e2a\u5f92\u5f1f\uff0c\u540d\u5524\u4e8e\u4e2d\uff0c\u529f\u592b\u4e5f\u8fd8\u8fc7\u5f97\u53bb\u3002\u6211\u5e26\u4ed6\u5230\u5251\u9601\uff0c\u53eb\u4ed6\u5728\u8c37\u5e95\u7b49\u6211\uff0c\u6211\u662f\u51c6\u5907\u82e5 \u4e07\u4e00\u4e0d\u654c\uff0c\u57cb\u9aa8\u8352\u5c71\uff0c\u4e5f\u5f97\u6709\u4e2a\u4eba\u6599\u7406\u3002 \u3000\u3000\u201c\u6211\u534a\u591c\u5230\u6765\uff0c\u5927\u51fa\u5929\u6f9c\u610f\u6599\u4e4b\u5916\u3002\u4ed6\u8981\u5411\u6211\u89e3\u91ca\uff0c\u53ef\u662f\u6211\u4e8c\u5341\u5e74\u6765\u5fcd\u8fb1\u8d1f\u91cd\uff0c\u79ef\u5fff\u6781 \u6df1\uff0c\u54ea\u91cc\u80af\u542c\u4ed6\u7684\u8bdd\uff0c\u4e00\u89c1\u9762\u5c31\u7528\u4e5d\u5bab\u795e\u884c\u638c\u7684\u7edd\u62db\u6253\u4ed6\uff0c\u4ed6\u88ab\u8feb\u62db\u67b6\u3002\u6211\u81ea\u4ee5\u4e3a\u5b66\u6210\u7edd \u6280\uff0c\u80dc\u5238\u53ef\u64cd\uff0c\u4e0d\u6599\u4ed6\u7684\u529f\u592b\u4e5f\u6ca1\u6401\u4e0b\uff0c\u4e0d\u4f46\u672c\u95e8\u7684\u5927\u529b\u9e70\u722a\u529f\u5df2\u7ec3\u5f97\u7089\u706b\u7eaf\u9752\uff0c\u800c\u4e14\u5b66\u6210 \u4e86\u6b66\u6797\u4e2d\u7684\u7edd\u6280\u2018\u7ef5\u638c\u2019\uff0c\u6bd4\u6211\u7684\u4e5d\u5bab\u795e\u884c\u638c\u8fd8\u8981\u5389\u5bb3\uff01\u4ed6\u4e0e\u6211\u8fc7\u62db\u65f6\u4e00\u5473\u9000\u8ba9\uff0c\u53ef\u662f\uff0c\u6211 \u5374\u4ee5\u4e3a\u4ed6\u5185\u759a\u4e8e\u5fc3\uff0c\u6240\u4ee5\u624d\u4f1a\u5982\u6b64\uff0c\u66f4\u662f\u6c14\u6124\uff0c\u8d8a\u53d1\u7d27\u8feb\uff0c\u51c6\u5907\u4e0e\u4ed6\u540c\u5f52\u4e8e\u5c3d\u3002\u6211\u4eec\u8d8a\u6253\u8d8a \u6025\uff0c\u4ed6\u4e00\u8def\u9000\u8ba9\uff0c\u6211\u4e00\u8def\u8fdb\u903c\uff0c\u770b\u770b\u628a\u4ed6\u6324\u5230\u60ac\u5d16\u4e4b\u8fb9\uff0c\u5ffd\u7136\u6709\u4eba\u5927\u53eb\u5929\u6210\uff0c\u6211\u51dd\u7738\u4e00\u770b\uff0c \u6b63\u662f\u6211\u7684\u59bb\u5b50\u548c\u4e00\u4e2a\u9ec4\u886b\u5c11\u5e74\u6765\u5566\uff01\u6211\u60c5\u77e5\u8fd9\u5c11\u5e74\u4e00\u5b9a\u662f\u6211\u7684\u513f\u5b50\uff0c\u4ed6\u81ea\u5c0f\u4e0e\u6211\u5206\u79bb\uff0c\u6211\u4e5f \u4e0d\u77e5\u4ed6\u957f\u5f97\u600e\u6837\uff0c\u4e0d\u7981\u5446\u4e86\u4e0b\u6765\uff0c\u8fce\u4e0a\u524d\u53bb\u770b\u4ed6\u3002\u4e0d\u6599\u4ed6\u624b\u4e00\u6296\uff0c\u53d1\u51fa\u4e09\u679a\u91d1\u73af\uff0c\u4ed6\u7684\u6697\u5668 \u529f\u592b\uff0c\u5df2\u5168\u5f97\u6bcd\u4eb2\u6240\u6388\uff0c\u52b2\u9053\u66f4\u662f\u6bd4\u4ed6\u7684\u6bcd\u4eb2\u8fd8\u8981\u5389\u5bb3\uff01\u5929\u6f9c\u8dc3\u8d77\u4e00\u62cd\uff0c\u66ff\u6211\u6253\u843d\u4e00\u679a\uff0c\u6211 \u5931\u9b42\u843d\u9b44\uff0c\u4e0d\u77e5\u8eb2\u907f\uff0c\u5176\u4ed6\u4e24\u679a\uff0c\u5168\u90fd\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u6253\u4e2d\u4e86\u6211\uff0c\u6211\u95ed\u4e86\u7a74\u9053\u633a\u4f4f\uff0c\u8fd8\u662f\u5341\u5206\u75bc \u75db\uff01\u90a3\u65f6\u6211\u60b2\u6124\u4e4b\u6781\uff0c\u81ea\u601d\u59bb\u4e0d\u4ee5\u6211\u4e3a\u592b\uff0c\u5b50\u4e0d\u4ee5\u6211\u4e3a\u7236\uff0c\u8fd8\u5408\u529b\u8c0b\u6211\uff0c\u6211\u8fd8\u5728\u6b64\u505a\u751a\uff1f\u4e00 \u626d\u8eab\u5c31\u5411\u60ac\u5d16\u8dc3\u4e0b\uff01\u8033\u8fb9\u53ea\u542c\u5f97\u6211\u7684\u59bb\u5b50\u5927\u58f0\u558a\u53eb\u548c\u513f\u5b50\u7684\u54ed\u58f0\uff01\u201d \u3000\u3000\u7ea2\u9762\u8001\u4eba\u8bb2\u8ff0\u81f3\u6b64\uff0c\u8bdd\u8bed\u4e00\u505c\uff0c\u4f4e\u4f4e\u5598\u606f\u3002\u5979\u7684\u5f92\u5f1f\u5929\u4e2d\u6258\u4e86\u4e00\u76d8\u679c\u5b50\u8fc7\u6765\u3002\u5e76\u5012\u4e86\u4e00 \u676f\u5c71\u8336\uff0c\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u5403\u70b9\u4e1c\u897f\uff01\u201d\u7ea2\u9762\u8001\u4eba\u4f4e\u4f4e\u8bf4\u9053\uff1a\u201c\u597d\u5f92\u5f1f\uff0c\u5e08\u7236\u4e5f\u4e8f\u4e86 \u4f60\uff0c\u5927\u5bb6\u90fd\u5403\u70b9\u4e1c\u897f\u5427\uff01\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u4e8e\u4e2d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u5949\u5e08\u4e4b\u547d\uff0c\u5728\u4e0b\u9762\u63a5\u5e94\u5e08\u7236\uff0c\u4e8b\u5148\u4e5f\u6ca1\u544a\u8bc9\u6211\u5230\u5e95\u662f\u4e3a \u4e86\u4ec0\u4e48\uff0c\u53ea\u8bf4\u6240\u627e\u7684\u662f\u4ed6\u5e73\u751f\u552f\u4e00\u7684\u5f3a\u4ec7\u5927\u654c\uff0c\u6211\u5728\u4e0b\u9762\u9065\u542c\u5e08\u7236\u5728\u4e0a\u9762\u547c\u559d\u4e4b\u58f0\uff0c\u4e00\u9897\u5fc3 \u535c\u535c\u5730\u8df3\u4e2a\u4e0d\u4f11\uff0c\u6ca1\u591a\u4e45\uff0c\u5ffd\u89c1\u5e08\u7236\u4ece\u4e0a\u9762\u6eda\u4e0b\uff0c\u6211\u6025\u5fd9\u4e0a\u53bb\u63a5\u7740\uff0c\u5e78\u597d\u5e08\u7236\u53d7\u4f24\u4e0d\u91cd\uff0c\u4ed6 \u4e00\u8d77\u6765\uff0c\u5c31\u6325\u624b\u53eb\u6211\u5feb\u8d70\uff0c\u661f\u591c\u79bb\u5f00\u4e86\u5251\u9601\u3002\u6211\u95ee\u4ed6\uff0c\u4ed6\u4ec0\u4e48\u90fd\u4e0d\u8bf4\uff0c\u53ea\u662f\u8981\u6211\u548c\u4ed6\u4e00\u9053\uff0c \u82e6\u5b66\u7edd\u6280\uff01\u201d \u3000\u3000\u8001\u5a46\u5a46\u5477\u4e86\u53e3\u5c71\u8336\uff0c\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u90a3\u665a\u6211\u548c\u7af9\u541b\u540c\u7761\uff0c\u534a\u591c\u9192\u6765\uff0c\u5ffd\u542c\u5916\u9762\u4f3c\u6709\u6253\u6597\u4e4b \u58f0\uff0c\u6211\u672c\u610f\u662f\u8981\u6b7b\u65f6\u624d\u544a\u8bc9\u5b69\u5b50\u7684\uff0c\u56e0\u4e3a\u6211\u4e0d\u613f\u5b69\u5b50\u7eaf\u771f\u7684\u5fc3\u7075\uff0c\u8499\u4e0a\u9634\u5f71\u3002\u6240\u4ee5\u4ed6\u4e00\u76f4\u4e0d \u77e5\u4f60\u662f\u4ed6\u7684\u7236\u4eb2\u3002\u4ed6\u4e00\u51fa\u624b\uff0c\u5929\u6f9c\u5c31\u5927\u53eb\uff1a\u8fd9\u4eba\u662f\u4f60\u7684\u7238\u7238\uff0c\u53ef\u662f\u5df2\u7ecf\u8fdf\u5566\uff01\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u9053\uff1a\u201c\u6211\u5728\u5251\u9601\u957f\u5927\uff0c\u4e5f\u89c9\u5f97\u7236\u4eb2\u795e\u60c5\u6709\u70b9\u5947\u602a\uff0c\u4ed6\u4eec\u867d\u5f88\u548c\u7766\uff0c\u53ef\u662f\u665a\u4e0a\u6211 \u8ddf\u7236\u4eb2\uff0c\u59b9\u59b9\u8ddf\u6bcd\u4eb2\uff0c\u5341\u4f59\u5e74\u6765\u5982\u4e00\u65e5\uff0c\u65e5\u5e38\u76f8\u5904\uff0c\u4ed6\u4eec\u4e5f\u90fd\u5ba2\u5ba2\u6c14\u6c14\uff0c\u548c\u6211\u5c0f\u65f6\u5728\u519b\u4e2d\u6240 \u89c1\u53d4\u53d4\u5a76\u5a76\u5927\u4e0d\u76f8\u540c\uff0c\u53ef\u662f\u6211\u4e5f\u7edd\u672a\u6599\u5230\u91cc\u5934\u6709\u8fd9\u6837\u590d\u6742\u7684\u60c5\u8282\uff0c\u90a3\u665a\u517b\u7236\u548c\u5988\u5988\u6d41\u7740\u6cea\u5c06 \u4e8b\u60c5\u544a\u8bc9\u6211\uff0c\u4fe8\u5982\u6674\u5929\u8d77\u4e86\u9739\u96f3\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u8be5\u6068\u8c01\u624d\u597d\uff0c\u6211\u53ea\u80fd\u6068\u6211\u81ea\u5df1\uff01\u6211\u8ff7\u8ff7\u832b\u832b\uff0c \u624b\u63d0\u53cc\u5251\uff0c\u98de\u5954\u4e0b\u5c71\uff0c\u517b\u7236\u5728\u6211\u80cc\u540e\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u4e5f\u4e0d\u62e6\u6211\u3002\u4e0b\u5c71\u4e4b\u540e\uff0c\u6211\u4ec0\u4e48\u4e5f\u4e0d\u60f3\uff0c\u4e5f \u4e0d\u77e5\u4ece\u90a3\u91cc\u627e\u5bfb\u6211\u771f\u6b63\u7684\u7236\u4eb2\uff0c\u53ea\u662f\u767d\u5929\u9ed1\u591c\uff0c\u65e0\u65f6\u4e0d\u523b\u90fd\u597d\u50cf\u6709\u4e00\u4e2a\u58f0\u97f3\uff0c\u5728\u8033\u8fb9\u53eb\u9053\uff1a \u4f60\u6740\u6b7b\u4e86\u4f60\u7684\u7236\u4eb2\u5566\uff01\u6211\u518d\u4e5f\u5fcd\u53d7\u4e0d\u4e86\uff0c\u4e00\u5929\u665a\u4e0a\u6211\u5728\u8352\u91ce\u5230\u5904\u4e71\u8dd1\uff0c\u81ea\u5df1\u6298\u78e8\u81ea\u5df1\uff0c\u90a3\u662f \u4e00\u4e2a\u5927\u96ea\u7eb7\u98de\u7684\u51ac\u5929\uff0c\u6ca1\u591a\u4e45\u5c31\u660f\u5012\u5728\u539f\u91ce\u4e0a\uff01\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u6709\u5fae\u5fae\u58f0\u54cd\uff0c\u8001\u5a46\u5a46\u4e00\u6307\u51cc\u672a\u98ce\uff0c\u672a\u5f85\u5f00\u8a00\uff0c\u51cc\u672a\u98ce\u9752\u94a2\u5251\u5df2\u55d6 \u7684\u51fa\u624b\uff0c\u8f7b\u8f7b\u4e00\u63a0\uff0c\u4f3c\u5927\u96c1\u7a7f\u51fa\u5c4b\u5916\u3002\u8001\u5a46\u5a46\u9053\uff1a\u201c\u8fd9\u58f0\u54cd\u672a\u5fc5\u662f\u4eba\uff0c\u4f46\u6709\u9632\u5907\u603b\u597d\u70b9\u3002\u6709 \u51cc\u5927\u4fa0\u5728\u6b64\u5de1\u89c6\uff0c\u6211\u4eec\u53ef\u4e0d\u5fc5\u518d\u6015\u5c0f\u8d3c\u6765\u9a9a\u6270\u4e86\uff01\u201d \u3000\u3000\u9ec4\u886b\u5c11\u5e74\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u5728\u96ea\u5730\u4e0a\u660f\u8ff7\u4e86\u4e5f\u4e0d\u77e5\u591a\u5c11\u65f6\u5019\uff01\u540e\u6765\u624d\u7ed9\u4e94\u9f99\u5e2e\u7684\u8d3c\u4eba\u6551 \u9192\u3002\u4ee5\u540e\u5c31\u8ff7\u5931\u4e86\u8bb0\u5fc6\uff0c\u8fde\u81ea\u5df1\u7684\u540d\u5b57\u548c\u6765\u5386\u90fd\u5fd8\u8bb0\u4e86\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4ee5\u540e\u7684\u4e8b\u60c5\u6211\u66ff\u4f60\u8bf4\u5427\u3002\u201d\u5979\u5c06\u9047\u89c1\u9ec4\u886b\u5c11\u5e74\u548c\u600e\u6837\u533b\u6cbb\u5979\u7684\u7ecf\u8fc7\uff0c\u4e00\u4e00 \u544a\u8bc9\u7ed9\u8001\u5a46\u5a46\u548c\u7ea2\u9762\u8001\u4eba\uff0c\u8001\u5a46\u5a46\u53c8\u60b2\u53c8\u559c\uff0c\u62c9\u7740\u5979\u7684\u624b\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u6d63\u83b2\u59d1\u5a18\uff0c\u6211\u771f\u4e0d\u77e5 \u9053\u8981\u5982\u4f55\u611f\u8c22\u4f60\u624d\u597d\uff01\u201d \u3000\u3000\u7ea2\u9762\u8001\u4eba\u4e5f\u5b9a\u6674\u770b\u7740\u5192\u6d63\u83b2\uff0c\u53c8\u555c\u4e86\u4e00\u53e3\u8336\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u8bb0\u8d77\u4f60\u6765\u4e86\uff0c\u4f60\u5c31 \u662f\u90a3\u65e5\u5728\u5251\u9601\u89c2\u6218\u7684\u4eba\u3002\u542c\u7af9\u541b\u8bb2\uff0c\u4f60\u8fd8\u5e2e\u4e86\u6211\u4eec\u5927\u5fd9\u54e9\uff01\u201d \u3000\u3000\u201c\u4f60\u5728\u5251\u9601\u90a3\u591c\uff0c\u662f\u6211\u7b2c\u4e8c\u6b21\u6765\u627e\u5929\u6f9c\u7b97\u5e10\u3002\u4e8b\u60c5\u4e5f\u771f\u6709\u8fd9\u6837\u5de7\uff0c\u7af9\u541b\u957f\u5927\u4e86\uff0c\u4e5f\u50cf\u5979 \u7684\u54e5\u54e5\u4e00\u6837\uff0c\u7528\u6697\u5668\u4f24\u4e86\u6211\u3002\u800c\u6211\u4e3a\u4e86\u6551\u5979\uff0c\u53c8\u62b1\u7740\u6e05\u5bab\u536b\u58eb\uff0c\u6c5f\u6e56\u4ee5\u524d\u95fb\u540d\u7684\u5de8\u76d7\u2018\u516b\u81c2 \u54ea\u54a4\u2019\u7126\u9738\uff0c\u540c\u5815\u6df1\u8c37\uff0c\u6211\u867d\u7136\u628a\u4ed6\u6740\u6b7b\uff0c\u4f46\u4ed6\u4e5f\u628a\u6211\u5f04\u6210\u6b8b\u5e9f\u3002\u201d \u3000\u3000\u7af9\u541b\u4e00\u624b\u8f7b\u63a0\u5934\u53d1\uff0c\u4e00\u624b\u62c9\u7740\u5192\u6d63\u83b2\u7684\u624b\u9053\uff1a\u201c\u6211\u5f53\u665a\u6025\u75db\u653b\u5fc3\uff0c\u81ea\u60ac\u5d16\u8dc3\u4e0b\uff0c\u5e78\u597d\u6211 \u5728\u6df1\u5c71\u957f\u5927\uff0c\u957f\u671f\u4e0e\u733f\u7334\u4e3a\u4f0d\uff0c\u867d\u4e0d\u6562\u8bf4\u8f7b\u529f\u7edd\u9876\uff0c\u4f46\u8eab\u624b\u4e5f\u8fd8\u7075\u6d3b\uff0c\u6211\u7ffb\u7ffb\u6eda\u6eda\uff0c\u76f4\u4e0b\u6df1 \u8c37\uff0c\u53d1\u73b0\u4e86\u7238\u7238\u5df2\u7ed9\u4e8e\u4e2d\u5e08\u5144\u6551\u9192\uff0c\u6211\u5c31\u8fc7\u53bb\u89c1\u4ed6\u3002\u90a3\u65f6\u4ed6\u867d\u7136\u4f24\u91cd\uff0c\u89c1\u4e86\u6211\u8fd8\u662f\u9ad8\u5174\u5f97 \u5f88\uff0c\u62c9\u7740\u6211\u95ee\u957f\u95ee\u77ed\u3002\u6211\u544a\u8bc9\u4ed6\uff0c\u4e8c\u5341\u5e74\u6765\uff0c\u6211\u90fd\u662f\u548c\u5988\u5988\u7761\u5728\u4e00\u8d77\uff0c\u5988\u5988\u602a\u75bc\u6211\u7684\u3002\u4ed6\u542c \u4e86\u5583\u5583\u9053\uff1a\u201c\u90a3\u4e48\u96be\u9053\u4ed6\u4eec\u53ea\u662f\u6302\u540d\u592b\u5987\uff1f\u6211\u4e5f\u542c\u4e0d\u61c2\u4ed6\u8bf4\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u8001\u5a46\u5a46\u6697\u6697\u70b9\u9996\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u521a\u624d\u8bf4\u65e9\u5df2\u77e5\u9053\u3002\u201d\u7ea2\u9762\u8001\u4eba\u5c34\u5c2c\u4e00\u7b11\uff0c\u63a5\u7740\u8bf4 \u9053\uff1a\u201c\u8fc7\u4e86\u51e0\u5929\uff0c\u4ef2\u660e\u7684\u5988\u5988\u56de\u6765\u4e86\uff0c\u90a3\u65f6\u6211\u56e0\u4e3a\u4f24\u91cd\uff0c\u4e0d\u80fd\u52a8\u5f39\uff0c\u4e8e\u4e2d\u548c\u7af9\u541b\u53ea\u597d\u5728\u8c37\u4e2d \u670d\u4f8d\u6211\u3002\u5979\u5230\u4e86\u4e4b\u540e\uff0c\u624d\u5408\u529b\u9020\u8d77\u8fd9\u95f4\u77f3\u5c4b\u3002 \u3000\u3000\u201c\u6211\u4eec\u5927\u59bb\u91cd\u9022\uff0c\u604d\u5982\u9694\u4e16\u3002\u5979\u4e00\u8def\u5728\u6211\u75c5\u584c\u8fb9\u542b\u6cea\u8bc9\u8bf4\uff0c\u6211\u660e\u767d\u4e86\u4e00\u5207\uff0c\u706b\u6c14\u4e5f\u90fd\u6d88 \u5566\uff01\u8fc7\u540e\u5979\u8fd8\u6015\u6211\u4e0d\u76f8\u4fe1\uff0c\u62ff\u51fa\u4e86\u4e00\u5c01\u4fe1\u6765\u3002\u8fd9\u5c01\u4fe1\u662f\u674e\u5b9a\u56fd\u4e34\u6b7b\u524d\u7559\u7ed9\u5929\u6f9c\u5e08\u5144\uff0c\u53eb\u4ed6\u4ea4 \u7ed9\u6211\u7684\u3002\u8fd9\u5c01\u4fe1\u5199\u5f97\u975e\u5e38\u6073\u5207\uff0c\u4ed6\u4ee5\u4e00\u519b\u4e3b\u5e05\u8eab\u4efd\uff0c\u62c5\u4fdd\u5929\u6f9c\u4e0d\u662f\u574f\u4eba\u3002\u5e76\u8bc1\u5b9e\u5929\u6f9c\u548c\u5979\u53ea \u662f\u5bf9\u6302\u540d\u592b\u59bb\u3002 \u3000\u3000\u7ea2\u9762\u8001\u4eba\u8bf4\u81f3\u6b64\u5904\uff0c\u4f38\u51fa\u624b\u629a\u7740\u9ec4\u886b\u5c11\u5e74\u7684\u5934\u53d1\u9053\uff1a\u201c\u8981\u4e0d\u662f\u6211\u8fd8\u60f3\u7740\u89c1\u4f60\u4e00\u9762\uff0c\u90a3\u65f6 \u6211\u5c31\u76f4\u60f3\u4e86\u6b64\u6b8b\u751f\uff01\u5929\u6f9c\u5e08\u5144\u5bf9\u6211\u6069\u6df1\u4e49\u91cd\uff0c\u6211\u5374\u8feb\u6b7b\u4e86\u4ed6\uff01\u6211\u5b9e\u5728\u4e0d\u662f\u4eba\uff01\u513f\u554a\uff01\u6211\u8981\u4f60 \u4eca\u540e\u6539\u59d3\u6842\uff0c\u5c31\u662f\u4e3a\u4e86\u62a5\u7b54\u4ed6\u3002\u4f60\u5c06\u6765\u7ed3\u5a5a\u751f\u5b50\uff0c\u7b2c\u4e00\u4e2a\u7b97\u662f\u6842\u5929\u6f9c\u7684\uff0c\u627f\u7ee7\u6842\u5bb6\u9999\u706b\u3002\u7b2c \u4e8c\u4e2a\u624d\u7b97\u662f\u6211\u7684\u5b59\u5b50\uff0c\u627f\u7ee7\u77f3\u5bb6\u9999\u706b\u3002\u513f\u554a\uff01\u4f60\u8981\u4e00\u4e16\u8bb0\u7740\u4f60\u517b\u7236\u5bf9\u4f60\u7684\u6069\u5fb7\uff01\u201d \u3000\u3000\u7ea2\u9762\u8001\u4eba\u77f3\u5929\u6210\u4e0e\u6842\u5929\u6f9c\u4e4b\u95f4\u7684\u601d\u6069\u6028\u6028\uff0c\u81f3\u6b64\u5927\u767d\uff0c\u4f17\u4eba\u5747\u4e0d\u7981\u9eef\u7136\u795e\u4f24\uff0c\u70ef\u5618\u53f9 \u606f\uff01\u8001\u5a46\u5a46\u5ffd\u7136\u4e00\u624b\u53d6\u8fc7\u9ec4\u886b\u5c11\u5e74\u80cc\u4e0a\u7684\u884c\u56ca\uff0c\u89e3\u5f00\u4e00\u6296\uff0c\u6296\u51fa\u51e0\u4ef6\u9ec4\u886b\u3002\u7ea2\u9762\u8001\u4eba\u53f9\u9053\uff1a \u201c\u513f\u554a\uff01\u8fd9\u51e0\u5e74\u96be\u4e3a\u4f60\u4e86\uff0c\u4e8f\u4f60\u8fd8\u80fd\u4f53\u8c05\u4f60\u5988\u7684\u82e6\u5fc3\uff0c\u867d\u7136\u5931\u4e86\u8bb0\u5fc6\uff0c\u9ec4\u886b\u670d\u9970\u8fd8\u662f\u672a \u6539\u3002\u201d\u8001\u5a46\u5a46\u95f7\u58f0\u4e0d\u54cd\uff0c\u5ffd\u7136\u62e3\u51fa\u4e00\u4ef6\u674f\u9ec4\u886b\u5b50\uff0c\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u5927\u6210\uff0c\u4f60\u770b\u770b\u8fd9\u4ef6\u9ec4\u886b\uff0c\u53ef \u4e0d\u5c31\u662f\u5f53\u5e74\u6211\u7ed9\u4f60\u505a\u7684\u90a3\u4ef6\uff0c\u4e0a\u9762\u8fd8\u6cbe\u7740\u4f60\u51e0\u70b9\u8840\u8ff9\uff01\u201d\u7ea2\u9762\u8001\u4eba\u63a5\u8fc7\u4e00\u770b\uff0c\u6d41\u4e0b\u6cea\u6765\u3002\u8001 \u5a46\u5a46\u9053\uff1a\u201c\u6211\u4eec\u4e00\u76f4\u73cd\u85cf\u7740\u8fd9\u4ef6\u886b\uff0c\u5728\u4ef2\u660e\u5341\u516b\u5c81\u90a3\u5e74\uff0c\u624d\u4ea4\u7ed9\u4ed6\u4fdd\u5b58\uff0c\u6211\u4eec\u544a\u8bc9\u4ed6\u8fd9\u662f\u4e00 \u4ef6\u5bb6\u4f20\u4fe1\u7269\uff0c\u5c06\u6765\u51ed\u8fd9\u4ef6\u8863\u670d\u53ef\u4ee5\u627e\u5230\u4e00\u4e2a\u5931\u6563\u4e86\u7684\u4eb2\u4eba\u3002\u4ed6\u5f53\u65f6\u5f88\u662f\u7591\u60d1\uff0c\u4e5f\u66fe\u53d1\u95ee\uff0c\u6211 \u8981\u544a\u8bc9\u4ed6\u8fd8\u672a\u5230\u65f6\u5019\uff0c\u4e0d\u5fc5\u591a\u95ee\u3002\u8fd9\u4e2a\u5b69\u5b50\u5f88\u542c\u8bdd\uff0c\u679c\u7136\u73cd\u85cf\u8d77\u6765\uff0c\u4f60\u770b\u4ed6\u6d41\u6d6a\u4e86\u8fd9\u4e48\u591a \u5e74\uff0c\u8fd8\u662f\u85cf\u5f97\u597d\u597d\u7684\uff01\u201d \u3000\u3000\u7ea2\u9762\u8001\u4eba\u628a\u9ec4\u886b\u5c55\u5f00\uff0c\u4e8c\u5341\u5e74\u7684\u7684\u5f80\u4e8b\u5728\u6cea\u5149\u4e2d\u6447\u6643\uff0c\u4e00\u65f6\u53ea\u89c9\u4e07\u7bad\u7a7f\u5fc3\uff01\u8fd9\u4ef6\u9ec4\u886b\uff0c \u73b0\u5728\u5df2\u7ecf\u9648\u65e7\u4e0d\u582a\u4e86\uff0c\u53ef\u662f\u5728\u4ed6\u773c\u4e2d\uff0c\u8fd8\u50cf\u5f53\u5e74\u59bb\u5b50\u65b0\u7f1d\u597d\u4ea4\u7ed9\u4ed6\u7684\u6837\u5b50\u3002\u4ed6\u5ffd\u7136\u5429\u5490\u9ec4\u886b \u5c11\u5e74\u628a\u4e00\u6280\u70b9\u7740\u7684\u677e\u679d\u62ff\u6765\u3002\u8352\u8c37\u65e0\u706f\uff0c\u77f3\u5ba4\u4e2d\u70b9\u7740\u4e00\u624e\u677e\u679d\u7167\u660e\u3002\u9ec4\u886b\u5c11\u5e74\u5982\u8a00\u53d6\u8fc7\u4e00\u679d \u71c3\u7740\u7684\u677e\u679d\uff0c\u7ea2\u9762\u8001\u4eba\u5c06\u9ec4\u886b\u5728\u706b\u4e0a\u4e00\u7f69\uff0c\u987f\u65f6\u71c3\u70e7\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u4e00\u5bb6\u56e2\u5706\uff0c\u8fd9\u4e0d\u7965 \u4e4b\u7269\uff0c\u518d\u4e0d\u8981\u4fdd\u5b58\u5b83\u4e86\uff01\u201d \u3000\u3000\u7a81\u7136\uff0c\u9ec4\u886b\u5c11\u5e74\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u770b\uff0c\u90a3\u662f\u4ec0\u4e48\uff1f\u201d\u4f17\u4eba\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c1\u90a3\u4ef6\u71c3\u70e7\u7740\u7684\u9ec4 \u886b\uff0c\u5ffd\u7136\u5728\u706b\u5149\u4e2d\u73b0\u51fa\u4e00\u5e45\u56fe\u753b\uff0c\u56fe\u4e2d\u73b0\u51fa\u4e00\u9053\u7011\u5e03\uff0c\u5728\u7011\u5e03\u7684\u5c3d\u5934\uff0c\u6c34\u50cf\u73cd\u73e0\u5e18\u5b50\u4e00\u4f38\uff0c \u6302\u5728\u4e00\u4e2a\u5c71\u6d1e\u524d\u9762\uff0c\u5c71\u6d1e\u77f3\u95e8\u7d27\u95ed\uff0c\u706b\u5149\u4e2d\u8fd8\u73b0\u51fa\u4e03\u4e2a\u5927\u5b57\uff0c\u201c\u5de6\u4e09\u53f3\u56db\u4e2d\u5341\u4e8c\u3002\u201d\u4f17\u4eba\u8be7 \u5f02\u975e\u5e38\uff0c\u90fd\u4e0d\u61c2\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u9ec4\u886b\u71c3\u70e7\u5f97\u975e\u5e38\u8fc5\u901f\uff0c\u970e\u5ffd\u4e4b\u540e\u5316\u4e3a\u7070\u70ec\uff0c\u5192\u6d63\u83b2\u5c06\u753b\u9ed8\u8bb0 \u5fc3\u4e2d\uff0c\u51c6\u5907\u4ed6\u65e5\u91cd\u7ed8\u3002 \u3000\u3000\u7ea2\u9762\u8001\u4eba\u83ab\u660e\u6240\u4ee5\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u641e\u7684\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u6211\u542c\u5085\u4f2f\u4f2f\u8bf4\u8fc7\uff0c\u6709\u4e00\u79cd \u91ce\u8349\uff0c\u70e7\u6210\u7070\u540e\uff0c\u548c\u6c34\u8c03\u5300\uff0c\u7528\u6765\u5199\u5b57\uff0c\u5b57\u8ff9\u4e0d\u663e\uff0c\u4f46\u4e00\u7ecf\u711a\u5316\uff0c\u5c31\u9732\u51fa\u6765\u3002\u6709\u4e00\u4e9b\u79d8\u5bc6\u7684 \u5e2e\u4f1a\uff0c\u66fe\u5229\u7528\u8fc7\u8fd9\u79cd\u91ce\u8349\uff0c\u5236\u6210\u9690\u5f62\u58a8\u6c34\uff0c\u6765\u4f20\u8fbe\u6781\u79d8\u5bc6\u7684\u4fe1\u4ef6\u3002\u53ef\u662f\u8fd9\u79cd\u8349\u5f88\u96be\u627e\uff0c\u7528\u6cd5 \u4e5f\u5f88\u5c11\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u7ea2\u9762\u8001\u4eba\u9053\uff1a\u201c\u4e0a\u9762\u7684\u5b57\uff0c\u6211\u8ba4\u5f97\u662f\u5929\u6f9c\u5e08\u54e5\u7684\uff0c\u53ef\u662f\u4ed6\u8fd9\u5e45\u56fe\u5374\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u8001\u5a46 \u5a46\u4e5f\u8be7\u5f02\u9053\uff1a\u201c\u6211\u4e5f\u672a\u542c\u4ed6\u8bf4\u8fc7\u3002\u4ed6\u81ea\u4ece\u5230\u5251\u9601\u9690\u5c45\u4e4b\u540e\uff0c\u8d8a\u53d1\u6c89\u9ed8\uff0c\u5e38\u5e38\u6574\u5929\u90fd\u96be\u5f97\u8bf4\u4e00 \u53e5\u8bdd\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u65f6\u5019\u753b\u7684\uff01\u201d \u3000\u3000\u4e0d\u8bf4\u4f17\u4eba\u5728\u5c4b\u5185\u4e71\u731c\uff0c\u4e14\u8bf4\u51cc\u672a\u98ce\u53d7\u8001\u5a46\u5a46\u4e4b\u6258\uff0c\u4ed7\u5251\u5728\u5916\u9762\u5de1\u89c6\u3002\u5c71\u8c37\u4e2d\u5e7d\u6cc9\u9e23\u54bd\uff0c \u8424\u706b\u9690\u73b0\uff0c\u4ed6\u60f3\u5c4b\u4e2d\u4eba\u60b2\u60e8\u7684\u906d\u9047\uff0c\u53c8\u8054\u60f3\u5230\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u4e0d\u7981\u60b2\u4ece\u4e2d\u6765\uff0c\u65e0\u53ef\u65ad\u7edd\u3002\u6b63\u601d \u60f3\u95f4\uff0c\u5ffd\u89c1\u8fdc\u5904\u6709\u4e24\u6761\u9ed1\u5f71\u98de\u9a70\u800c\u6765\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u60f3\u5fc5\u5c31\u662f\u8001\u5a46\u5a46\u542c\u8bf4\u7684\u8d3c\u4eba\uff0c\u4e14\u770b\u770b\u4ed6\u4eec\u7684\u884c\u5f84\u3002\u201d\u8eab\u5b50\u4e00 \u4f0f\uff0c\u9690\u5728\u8349\u83bd\u4e4b\u4e2d\u3002\u8fd9\u4e24\u4eba\u8eab\u6cd5\u597d\u5feb\uff0c\u970e\u5ffd\u5230\u4e86\u9762\u524d\uff0c\u53ea\u542c\u5f97\u5176\u4e2d\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u95fb\u8bf4\u6842\u8001\u5934 \u513f\u8eb2\u5728\u5251\u9601\uff01\u4f55\u4ee5\u627e\u4e0d\u7740\u4ed6\uff0c\u53ea\u89c1\u4e00\u95f4\u6b8b\u7834\u7684\u8305\u5c4b\uff1f\u201d\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u7b49\u97e9\u5927\u54e5\u6765\u5c31\u6709\u529e\u6cd5 \u4e86\uff0c\u5c31\u662f\u6015\u4ed6\u4e0d\u6765\u3002\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u79bb\u5f00\u51cc\u672a\u98ce\u56db\u4e94\u4e08\u5730\u3002\u51cc\u672a\u98ce\u6697\u6697\u6413\u7740\u4e00\u5c0f\u5757\u6ce5\u571f\uff0c \u56e2\u6210\u5c0f\u5c0f\u7684\u6ce5\u4e38\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u6b63\u6b63\u6253\u5728\u540e\u9762\u90a3\u4eba\u7684\u80a9\u4e0a\uff0c\u90a3\u4eba\u84e6\u7136\u60ca\u8d77\uff0c\u6e38\u76ee\u56db\u987e\uff0c\u6773\u65e0\u4eba \u8ff9\u3002\u8fd9\u65f6\u6070\u503c\u4e00\u9635\u98ce\u5439\u8fc7\uff0c\u65c1\u8fb9\u4e00\u88f8\u5927\u6811\uff0c\u98d8\u4e0b\u51e0\u7247\u6811\u53f6\u3002\u90a3\u4eba\u4e5f\u662f\u5185\u5bb6\u9ad8\u624b\uff0c\u8d77\u521d\u4ee5\u4e3a\u662f \u6811\u4e0a\u843d\u4e0b\u7684\u6ce5\u571f\uff0c\u7ee7\u800c\u4e00\u60f3\uff0c\u662f\u6811\u4e0a\u843d\u4e0b\u7684\uff0c\u81ea\u5df1\u4e0d\u4f1a\u611f\u5230\u4e00\u9635\u9178\u75db\u3002\u4ed6\u62cd\u62cd\u524d\u9762\u7684\u4eba\u9053\uff1a \u201c\u5e76\u80a9\u5b50\u7ad9\u7740\uff0c\u6709\u7ebf\u4e0a\u7684\u670b\u53cb\u6765\u4e86\uff01\u201d\u524d\u9762\u90a3\u4eba\u56de\u5934\u8bf4\u9053\uff1a\u201c\u9676\u5927\u54e5\uff0c\u4f60\u89c1\u4e86\u4ec0\u4e48\u554a\uff01\u201d\u88ab \u5524\u505a\u9676\u5927\u54e5\u7684\u95f7\u58f0\u4e0d\u54cd\uff0c\u4e00\u6396\u8863\u895f\uff0c\u98de\u63a0\u4e0a\u6811\uff0c\u6b63\u5f85\u77a7\u671b\uff0c\u5ffd\u7136\u8db3\u8e0f\u7684\u90a3\u6839\u6811\u679d\uff0c\u53c8\u662f\u5580\u5693 \u4e00\u58f0\uff0c\u5f00\u6839\u6298\u65ad\u3002\u5e78\u800c\u4ed6\u7684\u8f7b\u8eab\u529f\u592b\u5f88\u4fca\uff0c\u4e00\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\uff0c\u8f7b\u98d8\u98d8\u5730\u843d\u5728\u5730\u4e0a\uff0c\u5140\u662f \u5f20\u76ee\u56db\u987e\uff0c\u51cc\u672a\u98ce\u4e0d\u7981\u7b11\u51fa\u58f0\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u56de\u58f0\u9a82\u9053\uff1a\u201c\u662f\u7ebf\u4e0a\u7684\u670b\u53cb\uff0c\u8bf7\u51fa\u6765\u6307\u6559\u4e2a\u4e09\u62db\u4e24\u5f0f\uff0c\u9b3c\u9b3c\u795f\u795f\u6697\u4e2d\u6349\u5f04\uff0c\u7b97\u4ec0 \u4e48\u82f1\u96c4\uff1f\u201d\u51cc\u672a\u98ce\u7b11\u7740\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u5728\u8fd9\u91cc\u554a\uff01\u8c01\u53eb\u4f60\u4eec\u770b\u4e0d\u89c1\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u4e2a\u540d\u53eb\u516b\u65b9\u5200\u5f20\u5143\u632f\uff0c\u4e00\u4e2a\u540d\u53eb\u9ed1\u715e\u795e\u9676\u5b8f\uff0c\u90fd\u662f\u9655\u897f\u7684\u72ec\u811a\u5927\u76d7\uff0c\u8bba\u529f\u592b\u5012 \u4e0d\u662f\u5eb8\u624b\uff0c\u53ea\u662f\u8f7b\u529f\u6697\u5668\u4e4b\u672f\uff0c\u5374\u900a\u4e8e\u51cc\u672a\u98ce\uff0c\u8fd9\u756a\u88ab\u51cc\u672a\u98ce\u6697\u4e2d\u8003\u8f83\uff0c\u90fd\u5f88\u751f\u6c14\uff0c\u4e00\u5de6\u4e00 \u53f3\uff0c\u731b\u5411\u51cc\u672a\u98ce\u6251\u6765\uff01 \u3000\u3000\u51cc\u672a\u98ce\u5355\u638c\u62a4\u80f8\uff0c\u51dd\u8eab\u4e0d\u52a8\uff0c\u5de6\u9762\u7684\u5f20\u5143\u632f\u4e00\u62f3\u6253\u5230\uff0c\u4ed6\u624d\u7a81\u5730\u6c89\u638c\u6a2a\u622a\uff0c\u5f20\u5143\u632f\u5fae\u5403 \u4e00\u60ca\uff0c\u4e00\u8bb0\u201c\u624b\u6325\u7435\u7436\u201d\uff0c\u5c06\u51cc\u672a\u98ce\u7684\u6a2a\u52b2\u5316\u5f00\u3002\u9676\u5b8f\u5728\u53f3\u9762\u9a88\u6307\u5982\u671d\uff0c\u65cb\u8eab\u6251\u8fdb\uff0c\u4f38\u6307\u4fbf \u70b9\u51cc\u672a\u98ce\u7684\u201c\u6d8c\u6cc9\u7a74\u201d\u3002 \u3000\u3000\u51cc\u672a\u98ce\u4fa7\u8eab\u95ea\u8fc7\uff0c\u53cd\u624b\u4e00\u70b9\uff0c\u4e5f\u5411\u9676\u5b8f\u8170\u95f4\u7684\u201c\u656c\u51ef\u7a74\u201d\u70b9\u6765\uff0c\u53e3\u4e2d\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u4e5f \u4f1a\u70b9\u7a74\uff1f\u201d\u51cc\u672a\u98ce\u51fa\u624b\u5982\u7535\uff0c\u9676\u5b8f\u542b\u80f8\u5438\u8179\uff0c\u867d\u672a\u7ed9\u771f\u4e2a\u70b9\u4e2d\uff0c\u8863\u88f3\u5df2\u7ed9\u51cc\u672a\u98ce\u6233\u4e86\u4e00\u4e2a\u5c0f \u6d1e\uff0c\u8d81\u52bf\u53cc\u6307\u4e00\u94a9\uff0c\u6495\u5f00\u4e86\u4e00\u5927\u7247\u3002 \u3000\u3000\u9676\u5b8f\u5f80\u65c1\u75be\u9053\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4f60\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u5f20\u5143\u632f\u8fd9\u65f6\u5df2 \u770b\u6e05\u695a\u51cc\u672a\u98ce\u9669\u4e0a\u7684\u5200\u75a4\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u540d\u5524\u5929\u5c71\u795e\u8292\u7684\u51cc\u672a\u98ce\uff1f\u201d\u51cc\u672a\u98ce \u50b2\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\u6211\u7684\u540d\u5b57\uff1f\u201d\u5f20\u5143\u632f\u9053\uff1a\u201c\u4f60\u5728\u897f\u5317\u6df7\u5f97\u597d\u597d\u7684\uff0c\u4f55\u82e6\u6765\u8d9f\u8fd9\u8d9f\u6d51 \u6c34\uff1f\u201d\u51cc\u672a\u98ce\u542c\u4e0d\u61c2\u4ed6\u7684\u8bdd\u610f\uff0c\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u505a\u6d51\u6c34\uff1f\u5929\u4e0b\u4eba\u7ba1\u5929\u4e0b\u4e8b\uff0c\u4f60\u4eec\u6562\u6765\u6b3a\u8d1f\u6b8b \u5e9f\u8001\u4eba\uff0c\u6211\u53ef\u4e0d\u80fd\u4e0d\u7ba1\uff01\u201d \u3000\u3000\u9676\u5b8f\u6025\u5fd9\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u4f60\u662f\u8bf4\u6842\u5929\u6f9c\u6b8b\u5e9f\u4e86\uff1f\u6211\u4eec\u4e0d\u662f\u4ed6\u7684\u4ec7\u4eba\uff0c\u4ed6\u5728\u54ea\u91cc\uff1f \u70e6\u4f60\u5f15\u89c1\u5f15\u89c1\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u672a\u53ca\u7b54\u8bdd\uff0c\u8fdc\u5904\u53c8\u6709\u4e09\u4eba\u98de\u5954\u800c\u6765\uff0c\u51cc\u672a\u98ce\u4e00\u770b\u5168\u662f\u4e0a\u4e86\u4e94\u5341\u5c81\u7684\u8001\u6c49\u3002\u5f20\u5143 \u632f\u3001\u9676\u5b8f\u4e8c\u4eba\u4f5c\u4e86\u4e2a\u7f57\u5708\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u7f57\u5f53\u5bb6\u3001\u8fbe\u58eb\u53f8\u548c\u5362\u8235\u4e3b\u90fd\u6765\u5566\u3002\u54b1\u4eec\u5408\u5b57\u7684\u670b\u53cb\uff0c \u4e00\u74e2\u6c34\u5927\u5bb6\u559d\u5566\uff01\u201d\u51cc\u672a\u98ce\u4e00\u542c\uff0c\u4fbf\u77e5\u662f\u7eff\u6797\u7684\u5207\u53e3\uff0c\u7eff\u6797\u4e2d\u4eba\u5728\u62a2\u52ab\u4e00\u7968\u8d22\u7269\u65f6\uff0c\u82e6\u78b0\u5230 \u53e6\u4e00\u5e2e\u7684\u4e5f\u6765\u62e6\u622a\uff0c\u5982\u4e0d\u60f3\u706b\u62fc\uff0c\u5c31\u5f97\u7b54\u5e94\u201c\u89c1\u8005\u6709\u4efd\u201d\uff0c\u5927\u5bb6\u5206\u8d43\u3002\u201c\u5408\u5b57\u201d\u662f\u6307\u201c\u540c\u9053 \u4e2d\u4eba\u201d\uff0c\u201c\u4e00\u74e2\u6c34\u201d\u662f\u6307\u201c\u8d22\u8d27\u201d\u3002\u51cc\u672a\u98ce\u5341\u5206\u8be7\u5f02\uff0c\u8fd9\u4e9b\u4eba\u5230\u8352\u8c37\u4e2d\u505a\u4ec0\u4e48\u201c\u4e70\u5356\uff1f\u201d \u3000\u3000\u5f20\u5143\u632f\u6307\u7740\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u897f\u5317\u6e38\u4fa0\u5929\u5c71\u795e\u8292\u51cc\u672a\u98ce\u3002\u201d\u90a3\u4e09\u4eba\u6f2b\u4e0d\u7ecf\u610f\u5730\u70b9\u4e86\u70b9 \u5934\uff0c\u5f20\u5143\u632f\u53c8\u5bf9\u51cc\u672a\u98ce\u4e00\u4e00\u4ecb\u7ecd\u9053\uff1a\u201c\u8fd9\u4eec\u662f\u5728\u5ddd\u5317\u7709\u5c71\u5b89\u7a96\u7acb\u67dc\u7684\u7f57\u5f53\u5bb6\u7f57\u8fbe\uff0c\u8fd9\u4f4d\u662f\u77f3 \u7825\u571f\u53f8\u8fbe\u4e09\u516c\uff1b\u8fd9\u4f4d\u662f\u9752\u9633\u5e2e\u7684\u8235\u4e3b\u5362\u5927\u695e\u5b50\u3002\u201d\u51cc\u672a\u98ce\u4e00\u542c\uff0c\u77e5\u9053\u8fd9\u4e09\u4eba\u90fd\u662f\u56db\u5ddd\u54cd\u5f53\u5f53 \u7684\u89d2\u8272\uff0c\u81ea\u5df1\u5728\u897f\u5317\u540d\u5934\u867d\u5927\uff0c\u5374\u4ece\u672a\u5230\u8fc7\u56db\u5ddd\uff0c\u602a\u4e0d\u5f97\u4ed6\u4eec\u542c\u4e86\u81ea\u5df1\u540d\u5b57\uff0c\u4e5f\u53ea\u7b49\u95f2\u89c6 \u4e4b\u3002\u4f46\u5374\u4e0d\u77e5\u4f55\u4ee5\u4e00\u591c\u4e4b\u95f4\uff0c\u7adf\u6709\u8fd9\u4e48\u591a\u4f4d\u7eff\u6797\u9ad8\u624b\u5230\u6b64\uff0c\u800c\u4e14\u5176\u4e2d\u8fd8\u6709\u4e00\u4f4d\u4ee5\u94a2\u7b4b\u94c1\u9aa8\u95fb \u540d\u6b66\u6797\u7684\u5916\u5bb6\u9ad8\u624b\u8fbe\u571f\u53f8\uff01 \u3000\u3000\u5f53\u4e0b\u5f20\u5143\u632f\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u51cc\u5927\u4fa0\uff0c\u662f\u6842\u8001\u5934\u513f\u7684\u670b\u53cb\uff0c\u4ed6\u8bf4\u6842\u8001\u5934\u513f\u6b8b\u5e9f\u5566\uff0c\u6211\u4eec\u60f3\u8bf7 \u4ed6\u5f15\u89c1\u3002\u201d\u540e\u6765\u4e09\u4eba\u9f50\u58f0\u9053\u597d\u3002\u51cc\u672a\u98ce\u672c\u60f3\u5c06\u6842\u5929\u6f9c\u5df2\u6b7b\u4e4b\u4e8b\u544a\u77e5\uff0c\u968f\u540e\u4e00\u60f3\uff0c\u5374\u53c8\u5fcd\u4f4f\u3002 \u5fc3\u60f3\u4ed6\u4eec\u65e2\u81ea\u79f0\u662f\u6842\u5929\u6f9c\u7684\u670b\u53cb\uff0c\u4e14\u5148\u5e26\u4ed6\u4eec\u89c1\u77f3\u8001\u592a\u592a\u518d\u8bf4\u3002 \u3000\u3000\u4e14\u8bf4\u7ea2\u9762\u8001\u4eba\u548c\u8001\u5a46\u5a46\u7b49\u6b63\u5728\u731c\u6d4b\u6842\u5929\u6f9c\u9057\u4e0b\u7684\u602a\u56fe\u3002\u5ffd\u95fb\u5916\u9762\u4eba\u58f0\u811a\u6b65\u58f0\u54cd\u6210\u4e00\u7247\uff0c \u8001\u5a46\u5a46\u62d4\u5251\u8bf4\u9053\uff1a\u201c\u96be\u9053\u6709\u4ec0\u4e48\u8d3c\u5b50\u5230\u6765\uff0c\u8fde\u51cc\u672a\u98ce\u4e5f\u6321\u4e0d\u4f4f\uff1f\u201d\u5979\u8fce\u51fa\u5c4b\u5916\uff0c\u53ea\u89c1\u51cc\u672a\u98ce \u4e00\u9a6c\u5f53\u524d\u3002\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u77f3\u8001\u592a\u592a\uff0c\u6709\u51e0\u4f4d\u670b\u53cb\u8981\u6765\u770b\u4f60\uff0c\u4ed6\u4eec\u8bf4\u662f\u6842\u5929\u6f9c\u524d\u8f88\u719f\u8bc6\u7684\uff01\u201d \u3000\u3000\u5f20\u5143\u632f\u548c\u8fbe\u571f\u53f8\u542c\u51cc\u672a\u98ce\u53e3\u53eb\u201c\u77f3\u8001\u592a\u592a\u201d\u90fd\u89c9\u8be7\u5f02\uff0c\u4ed6\u4eec\u5531\u4e86\u4e00\u4e2a\u80a5\u558f\uff0c\u8bf4\u9053\uff1a\u201c\u6842 \u8001\u5ac2\u5b50\uff0c\u8fd8\u8bb0\u5f97\u6211\u4eec\u5417\uff1f\u5929\u6f9c\u5144\u5728\u8fd9\u91cc\u5417\uff1f\u201d\u8001\u5a46\u5a46\u9762\u8272\u4e00\u6c89\uff0c\u968f\u5373\u8bf4\u9053\uff1a\u201c\u6842\u5929\u6f9c\u5df2\u7ed9\u6e05 \u5bab\u536b\u58eb\u5bb3\u6b7b\u5566\uff0c\u4f60\u4eec\u6765\u8fdf\u4e00\u6b65\u4e86\uff0c\u6211\u7684\u5f53\u5bb6\u65b9\u5929\u6210\u5012\u5728\u8fd9\u5c4b\u5b50\u5185\uff0c\u53ea\u662f\u4ed6\u73b0\u5728\u5df2\u662f\u5e9f\u4eba\uff0c\u53ef \u4e0d\u6562\u8bf7\u8001\u670b\u53cb\u4eec\u8fdb\u53bb\uff01\u201d\u8bf4\u7f62\u6a2a\u5251\u5728\u95e8\u53e3\u4e00\u7ad9\u3002 \u3000\u3000\u5f20\u5143\u632f\u548c\u8fbe\u571f\u53f8\uff0c\u90fd\u662f\u6842\u5929\u6f9c\u548c\u5979\u5728\u674e\u5b9a\u56fd\u519b\u4e2d\u4e4b\u65f6\uff0c\u6240\u8ba4\u8bc6\u7684\u4eba\u3002\u5f20\u5143\u632f\u662f\u4e00\u80a1\u5c71\u532a \u5934\u9886\uff0c\u5f53\u65f6\u4e5f\u542c\u674e\u5b9a\u56fd\u7684\u53f7\u4ee4\uff0c\u8fbe\u571f\u53f8\u5219\u66fe\u6709\u4e00\u6b21\u501f\u8def\u7ed9\u674e\u519b\u901a\u8fc7\uff0c\u90a3\u6b21\u63a5\u6d3d\u60dc\u8def\u7684\u4eba\u6b63\u662f \u6842\u5929\u6f9c\uff0c\u90a3\u65f6\u5979\u8fd8\u662f\u6842\u5929\u6f9c\u7684\u6302\u540d\u59bb\u5b50\u3002 \u3000\u3000\u5f20\u5143\u632f\u548c\u8fbe\u571f\u53f8\u542c\u8001\u5a46\u5a46\u8fd9\u6837\u4e00\u8bf4\uff0c\u5168\u90fd\u6014\u7740\uff01\u4ed6\u4eec\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u8001\u5a46\u5a46\u53e6\u6709\u4e00\u4f4d\u201c\u5f53 \u5bb6\u201d\uff0c\u53ea\u7591\u5979\u662f\u8bf4\u8c0e\uff0c\u53ea\u662f\u89c1\u5979\u6a2a\u5251\u6321\u5728\u95e8\u524d\uff0c\u53c8\u4e0d\u6562\u8d38\u7136\u52a8\u624b\u3002\u8981\u77e5\u9053\u8fd9\u8001\u5a46\u5a46\u5f53\u5e74\u662f\u674e \u5b9a\u56fd\u519b\u4e2d\u7b2c\u4e00\u5973\u6770\uff0c\u4e94\u79bd\u5251\u6cd5\uff0c\u9a70\u8a89\u5ddd\u4e2d\u3002\u8fbe\u571f\u53f8\u8fd8\u4e0d\u600e\u6837\uff0c\u5f20\u5143\u632f\u5df2\u662f\u6709\u70b9\u5fc3\u602f\u3002\u6b63\u8fdf\u7591 \u95f4\uff0c\u5ffd\u89c1\u8fdc\u65b9\u53c8\u662f\u4e00\u7c07\u7c07\u4eba\u5f71\u3002 \u3000\u3000\u4f17\u4eba\u6b63\u51dd\u89c6\u95f4\uff0c\u5ffd\u542c\u5f97\u9752\u9633\u5e2e\u8235\u4e3b\u5362\u4eba\u695e\u5b50\u9053\uff1a\u201c\u662f\u77f3\u8001\u5ac2\u5b50\u5417\uff1f\u6211\u53eb\u5362\u5927\u695e\u5b50\uff0c\u5f53\u5e74 \u66fe\u53d7\u8fc7\u4ee4\u5c0a\u7684\u6069\u5178\uff0c\u4e5f\u66fe\u53e8\u6270\u8fc7\u8d24\u4f09\u4fea\u7684\u4e00\u5e2d\u9152\uff0c\u77f3\u5927\u54e5\u82e5\u5728\u6b64\u5904\uff0c\u7406\u5f53\u5bb9\u5c0f\u5f1f\u8fdb\u53bb\u62dc \u89c1\u3002\u201d\u5362\u5927\u695e\u5b50\u662f\u5ce8\u5d4b\u6d3e\u7684\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u5c11\u5e74\u65f6\u9157\u9152\u4f7f\u6c14\uff0c\u5f97\u7f6a\u8fc7\u4e24\u4e2a\u6781\u5389\u5bb3\u7684\u6c5f\u6e56\u4eba\u7269\uff0c\u5e78 \u5f97\u77f3\u5927\u5a18\u7684\u7236\u4eb2\u5ddd\u4e2d\u5927\u4fa0\u53f6\u4e91\u836a\u51fa\u5934\u5316\u89e3\uff0c\u624d\u544a\u65e0\u4e8b\u3002\u7ecf\u6b64\u4e00\u6765\uff0c\u4ed6\u7684\u6c14\u8d28\u4e5f\u6539\u53d8\u4e86\u8bb8\u591a\uff0c \u56e0\u6b64\u5bf9\u53f6\u4e91\u836a\u5f88\u6709\u597d\u611f\u3002\u540e\u6765\u77f3\u5927\u6210\u7ed3\u5a5a\u65f6\uff0c\u4ed6\u4e5f\u6765\u4f5c\u8d3a\u3002\u81ea\u5403\u4e86\u90a3\u987f\u559c\u9152\u4e4b\u540e\uff0c\u4e00\u522b\u4e09\u5341 \u4f59\u5e74\uff0c\u77f3\u5927\u5a18\u548c\u6842\u5929\u6f9c\u7684\u4e8b\u60c5\uff0c\u4ed6\u5c31\u5168\u4e0d\u6653\u5f97\u4e86\u3002 \u3000\u3000\u8001\u5a46\u5a46\u91cd\u7741\u53cc\u7738\uff0c\u4ecd\u662f\u6a2a\u5251\u5f53\u95e8\uff0c\u77a7\u7740\u5362\u5927\u695e\u5b50\u9053\uff1a\u201c\u8c22\u8c22\u8fd9\u4f4d\u670b\u53cb\u597d\u610f\uff0c\u53ea\u662f\u6211\u4eec\u5f53 \u5bb6\u7684\u5df2\u88ab\u6e05\u5bab\u536b\u58eb\u5f04\u6210\u5e9f\u4eba\uff0c\u6628\u665a\u4ed6\u4eec\u8fd8\u66fe\u5230\u8352\u8c37\u641c\u67e5\uff0c\u6253\u4f24\u4e86\u6211\u7684\u5973\u513f\uff0c\u6211\u4eec\u5f53\u5bb6\u7684\u6b63\u7b49 \u5f85\u8fd9\u73ed\u9e70\u72ac\u518d\u6765\uff0c\u53ef\u4e0d\u613f\u8fde\u7d2f\u670b\u53cb\u3002\u201d\u5362\u5927\u695e\u5b50\u6c14\u51b2\u51b2\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u7684\u4e8b\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u8fdc\u5904\u7684\u90a3\u7c07\u4eba\u5f71\uff0c\u5df2\u5230\u4e86\u77f3\u5c4b\u4e4b\u524d\u3002\u8001\u5a46\u5a46\u5389\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u770b\uff0c\u8fd9\u4e0d\u662f\u536b \u58eb\u8001\u7237\u4eec\u6765\u4e86\uff01\u201d\u5362\u5927\u695e\u5b50\u626d\u5934\u4e00\u770b\uff0c\u679c\u7136\u662f\u4e94\u4e2a\u7a7f\u7740\u4e00\u8272\u9752\u8863\u670d\u9970\u7684\u536b\u58eb\uff0c\u6563\u4e86\u5f00\u6765\uff0c\u91c7 \u53d6\u5305\u56f4\u4e4b\u52bf\u3002 \u3000\u3000\u5362\u5927\u695e\u5b50\u9053\uff1a\u201c\u6211\u7ed9\u4f60\u6253\u53d1\u4ed6\u4eec\uff01\u201d\u8eab\u5f62\u65b9\u8d77\uff0c\u5374\u7ed9\u7709\u5c71\u5be8\u4e3b\u7f57\u8fbe\u62c9\u7740\u9053\uff1a\u201c\u5362\u5927\u54e5\uff0c \u4e14\u6162\uff0c\u54b1\u4eec\u522b\u5fd9\u72af\u8fd9\u8d9f\u6d51\u6c34\uff01\u201d \u3000\u3000\u8fd9\u4e94\u4e2a\u536b\u58eb\u4e2d\uff0c\u6709\u4e09\u4e2a\u662f\u5927\u5185\u9ad8\u624b\uff0c\u4e3a\u9996\u7684\u53eb\u738b\u521a\uff0c\u66fe\u4ee5\u91d1\u521a\u6563\u624b\u540d\u9707\u6b66\u6797\uff0c\u53e6\u5916\u4e24\u4eba \u4e00\u53eb\u7533\u5929\u864e\uff0c\u4e00\u53eb\u7533\u5929\u8c79\uff0c\u662f\u4e24\u5144\u5f1f\uff0c\u4ee5\u6ca7\u5dde\u6d2a\u56db\u628a\u5b50\u771f\u4f20\u7684\u5434\u94a9\u5251\u6cd5\uff0c\u79f0\u4e3a\u6b66\u6797\u4e00\u7edd\u3002\u53c8 \u53e6\u5916\u4e24\u4eba\u5219\u662f\u5ddd\u9655\u603b\u7763\u5e9c\u7684\u536b\u58eb\uff0c\u4e00\u53eb\u6d2a\u6d9b\uff0c\u4e00\u53eb\u7126\u76f4\uff0c\u4ee5\u524d\u4e5f\u662f\u5ddd\u4e2d\u7eff\u6797\u4eba\u7269\uff0c\u540e\u6765\u5ddd\u9655 \u603b\u7763\u7f51\u7f57\u4e86\u53bb\u7684\u3002\u8fd9\u4e24\u4eba\u6b64\u6765\u662f\u7ed9\u738b\u521a\u4ed6\u4eec\u5e26\u8def\u3002 \u3000\u3000\u6d2a\u6d9b\u3001\u7126\u76f4\u548c\u7f57\u5be8\u4e3b\u3001\u8fbe\u58eb\u53f8\u3001\u5f20\u5143\u632f\u7b49\u90fd\u662f\u76f8\u8bc6\uff0c\u77e5\u9053\u4ed6\u4eec\u7684\u6b66\u529f\u4e0d\u51e1\uff0c\u5f53\u4e0b\u5bf9\u738b\u521a \u8bf4\u4e86\u4e00\u58f0\uff0c\u968f\u5373\u6253\u62db\u547c\u9053\uff1a\u201c\u54b1\u4eec\u5949\u547d\u6349\u62ff\u94a6\u72af\u77f3\u5927\u6210\uff0c\u5176\u4f59\u4e0d\u76f8\u5e72\u7684\u4eba\u90fd\u6ca1\u4e8b\u3002\u670b\u53cb\u4eec\uff0c \u501f\u4e2a\u8def\uff01\u201d \u3000\u3000\u5362\u5927\u695e\u5b50\u66b4\u58f0\u559d\u9053\uff1a\u201c\u8fd9\u4e0d\u6210\uff01\u201d\u7f57\u8fbe\u5374\u9053\uff1a\u201c\u5927\u54e5\uff0c\u522b\u4eba\u6b63\u70b9\u5b50\u8fd8\u6ca1\u5f00\u8154\u5462\uff0c\u4f60\u6025\u4ec0 \u4e48\uff1f\u201d\u7f57\u8fbe\u3001\u5f20\u5143\u632f\u3001\u9676\u5b8f\u3001\u8fbe\u571f\u53f8\u7b49\uff0c\u867d\u5219\u662f\u7eff\u6797\u4eba\u7269\uff0c\u96c4\u9738\u4e00\u65b9\uff0c\u53ef\u5374\u53ea\u662f\u666e\u901a\u7684\u7eff\u6797 \u9053\uff0c\u4e0e\u674e\u81ea\u6210\u3001\u5f20\u732e\u5fe0\u4e0d\u53ef\u540c\u65e5\u800c\u8bed\u3002\u4ed6\u4eec\u53ea\u662f\u5578\u805a\u5c71\u6797\uff0c\u4f46\u6c42\u7acb\u8db3\u800c\u5df2\uff0c\u56e0\u6b64\u4e0e\u5b98\u5175\u7d20\u6765 \u6cb3\u6c34\u4e0d\u72af\u4e95\u6c34\uff0c\u6709\u65f6\u8fd8\u4e92\u76f8\u5b5d\u656c\uff0c\u5404\u4fdd\u5e73\u5b89\u3002\u82e5\u8981\u4ed6\u4eec\u4e0e\u5927\u5185\u536b\u58eb\u4f5c\u5bf9\uff0c\u5305\u5e87\u94a6\u72af\uff0c\u4ed6\u4eec\u53ef \u4e0d\u5927\u613f\u610f\uff0c\u800c\u4e14\u4ed6\u4eec\u4e0e\u6842\u5929\u6f9c\u3001\u77f3\u5929\u6210\u4e5f\u6ca1\u4ec0\u4e48\u8fc7\u547d\u7684\u4ea4\u60c5\u3002 \u3000\u3000\u8001\u5a46\u5a46\u62b1\u5251\u5f53\u80f8\uff0c\u5411\u5362\u5927\u695e\u5b50\u4e00\u63d6\u8bf4\u9053\uff1a\u201c\u6211\u8001\u5a46\u5b50\u591a\u8c22\u8fd9\u4f4d\u70ed\u5fc3\u7684\u670b\u53cb\uff0c\u53ef\u4e5f\u4e0d\u6562\u53eb \u597d\u670b\u53cb\u4e3a\u96be\uff0c\u6211\u867d\u5e74\u8001\uff0c\u8fd8\u4e0d\u542b\u7cca\uff0c\u6211\u63a5\u4e0b\u6765\u597d\u4e86\uff0c\u670b\u53cb\u4eec\uff0c\u8bf7\u95ea\u5f00\uff01\u6211\u8981\u4f1a\u4e00\u4f1a\u8fd9\u4e9b\u7687\u5e1d \u8001\u8d3c\u7684\u72d7\u722a\u5b50\uff1f\u201d \u3000\u3000\u8001\u5a46\u5a46\u4e00\u5c55\u5251\u950b\uff0c\u98de\u8eab\u6b32\u51fa\u3002\u51cc\u672a\u98ce\u62a2\u5148\u4e00\u6b65\uff0c\u62e6\u5728\u524d\u5934\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u8001\u5927\u5a18\uff0c\u8fd9\u51e0 \u4e2a\u5154\u5d3d\u5b50\u7559\u7ed9\u6211\u5427\uff0c\u6211\u6709\u8bb8\u4e45\u6ca1\u6709\u5403\u5154\u5b50\u8089\u4e86\uff0c\u4f60\u82e5\u624b\u75d2\uff0c\u6211\u5c31\u7559\u4e24\u4e2a\u7ed9\u4f60\uff01\u201d\u8bf4\u7f62\uff0c\u8db3\u5c16 \u4e00\u70b9\uff0c\u4fe8\u5982\u5de8\u9e1f\u98de\u817e\uff0c\u63a0\u8d77\u4e00\u9635\u98ce\u58f0\uff0c\u5355\u8eab\u843d\u5728\u4e94\u4e2a\u536b\u58eb\u7684\u524d\u9762\u3002\u8001\u5a46\u5a46\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff0c \u6211\u8ba9\u4f60\uff0c\u4f60\u6709\u80c3\u53e3\u5c31\u5168\u5403\u6389\u597d\u4e86\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u5355\u8db3\u70b9\u5730\uff0c\u8eab\u5b50\u4e00\u65cb\uff0c\u5bf9\u84c4\u52bf\u5f85\u53d1\u7684\u4e94\u4e2a\u536b\u58eb\uff0c\u73af\u626b\u4e00\u773c\uff0c\u51b7\u7136\u53d1\u8bdd\u9053\uff1a\u201c\u8fd9\u91cc \u7684\u4e8b\u60c5\u4e3b\u4eba\u4ea4\u6258\u7ed9\u6211\u4e86\uff0c\u4f60\u4eec\u51b2\u7740\u6211\u6765\u5427\uff01\u201d\u6d2a\u6d9b\u9762\u5411\u7fa4\u8c6a\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u53c8\u4e0d\u662f\u6b63\u70b9\uff0c \u51ed\u4ec0\u4e48\u8981\u66ff\u4eba\u6311\u5927\u6881\uff1f\u670b\u53cb\uff0c\u54b1\u4eec\u6cb3\u6c34\u4e0d\u72af\u4e95\u6c34\uff0c\u5404\u7ba1\u5404\u7684\u554a\u3002\u9752\u5c71\u5e38\u5728\uff0c\u7eff\u6c34\u957f\u6d41\uff0c\u54ea\u91cc \u4e0d\u5957\u4e2a\u4ea4\u60c5\uff0c\u6211\u4eec\u8ba4\u4f60\u662f\u4e2a\u670b\u53cb\u5978\u4eba\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u8bf4\u8bdd\u5341\u5206\u51b2\u649e\uff0c\u4f60\u9053\u4f55\u4ee5\u6d2a\u6d9b\u5bf9\u4ed6\u5982\u6b64\u5ba2\u6c14\uff1f\u539f\u6765\u521a\u624d\u5362\u5927\u695e\u5b50\u90a3\u4e48\u4e00\u56b7\uff0c\u800c\u6d2a \u6d9b\u53c8\u8ba4\u5f97\u7f57\u8fbe\u3001\u8fbe\u571f\u53f8\u7b49\u548c\u4ed6\u4e00\u8def\u3002\u53ea\u6050\u51cc\u672a\u98ce\u4e00\u51fa\u624b\uff0c\u8fd9\u4e9b\u4eba\u4f1a\u5e2e\u4ed6\u3002\u8fd9\u51e0\u4e2a\u4eba\u5168\u662f\u7eff\u6797 \u9ad8\u624b\uff0c\u51cc\u672a\u98ce\u4ed6\u867d\u4e0d\u8bc6\uff0c\u4fbf\u53ea\u770b\u4ed6\u4eae\u51fa\u7684\u8fd9\u624b\u8f7b\u8eab\u529f\u592b\uff0c\u5c31\u975e\u540c\u5c0f\u53ef\uff0c\u81ea\u5df1\u8fd9\u8fb9\u4e94\u4e2a\u4eba\uff0c\u5982 \u53ea\u5bf9\u4ed8\u77f3\u5929\u6210\u592b\u5987\uff0c\u52a0\u4e0a\u4ed6\u7684\u5973\u513f\u548c\u5f92\u5f1f\uff0c\u90a3\u662f\u7ef0\u6709\u4f59\u88d5\u3002\u4f46\u82e5\u7fa4\u8c6a\u8054\u8d77\u6765\u5408\u6597\uff0c\u53ef\u5c31\u8ba8\u4e0d \u4e86\u597d\u53bb\u3002\u56e0\u6b64\u4ed6\u867d\u95f7\u7740\u4e00\u809a\u5b50\u6c14\uff0c\u8fd8\u662f\u4e0d\u80fd\u4e0d\u5957\u4ea4\u60c5\uff0c\u8bf4\u597d\u8bdd\u3002\u4ed6\u53ea\u9053\u51cc\u672a\u98ce\u4e5f\u662f\u50cf\u7f57\u8fbe\u4e00 \u6837\uff0c\u4e43\u662f\u7eff\u6797\u4eba\u7269\uff0c\u53ef\u4ee5\u5229\u7528\u7684\u3002 \u3000\u3000\u90a3\u6599\u4ed6\u4e0d\u8bf4\u72b9\u53ef\uff0c\u4e00\u8bf4\u4e4b\u540e\uff0c\u51cc\u672a\u98ce\u731b\u7136\u559d\u9053\uff1a\u201c\u653e\u5c41\uff0c\u8c01\u662f\u4f60\u7684\u670b\u53cb\uff01\u201d\u4ed6\u89c1\u6d2a\u6d9b\u671b \u7740\u7fa4\u8c6a\uff0c\u4ea2\u547d\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u53ea\u51b2\u7740\u6211\u4e00\u4e2a\u4eba\u6765\u597d\u4e86\uff01\u201d\u8bf4\u7f62\u8f6c\u8fc7\u9762\u5bf9\u7f57\u8fbe\u7b49\u4eba\u8bf4\u9053\uff1a\u201c\u5404\u4f4d \u670b\u53cb\uff0c\u82e5\u770b\u5f97\u8d77\u6211\uff0c\u8bf7\u4e0d\u8981\u52a9\u62f3\uff0c\u514d\u5f97\u4ed6\u4eec\u8bf4\u6211\u4eec\u4ee5\u4f17\u51cc\u5be1\u3002\u201d \u3000\u3000\u8fd9\u65f6\u9ed1\u591c\u6e10\u901d\uff0c\u66d9\u8272\u521d\u5f00\uff0c\u6668\u5149\u66dd\u5fae\u4e2d\uff0c\u5927\u5185\u536b\u571f\u7684\u9996\u9886\u738b\u521a\u770b\u6e05\u695a\u4e86\u51cc\u672a\u98ce\u9762\u5bb9\uff0c\u5ffd \u7136\u8de8\u524d\u4e00\u6b65\uff0c\u9634\u4fa7\u4fa7\u5730\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u662f\u4e0d\u662f\u51cc\u672a\u98ce\uff1f\u201d\u51cc\u672a\u98ce\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff1f\u201d\u738b \u521a\u602a\u7b11\u51e0\u58f0\uff0c\u5411\u4f17\u536b\u58eb\u62db\u624b\u9053\uff1a\u201c\u4f60\u4eec\u770b\u6e05\u695a\u4e86\uff0c\u8fd9\u4f4d\u5c31\u662f\u5927\u540d\u9f0e\u9f0e\u7684\u5929\u5c71\u795e\u8292\u51cc\u672a\u98ce\uff0c\u591c \u95f9\u4e94\u53f0\u5c71\uff0c\u62a2\u8d70\u820d\u5229\u5b50\uff0c\u5168\u6709\u4ed6\u7684\u4efd\uff0c\u51cc\u672a\u98ce\uff0c\u522b\u4eba\u6015\u4f60\uff0c\u6211\u4eec\u53ef\u4e0d\u6015\u4f60\uff0c\u4f60\u4e56\u4e56\u5730\u8ddf\u6211\u4eec \u8d70\u5427\uff01\u201d \u3000\u3000\u539f\u6765\u695a\u662d\u5357\u5728\u4e91\u5c97\u9003\u8131\u4e4b\u540e\uff0c\u56de\u4eac\u62a5\u544a\uff0c\u6e05\u5ef7\u628a\u51cc\u672a\u98ce\u7ed8\u56fe\u9020\u50cf\uff0c\u5206\u53d1\u5404\u5730\uff0c\u5217\u4e3a\u5934\u7b49 \u94a6\u72af\u3002\u6bd4\u8f83\u8d77\u6765\uff0c\u4ed6\u6bd4\u77f3\u5929\u6210\u592b\u5987\u66f4\u4e3a\u91cd\u8981\uff0c\u6e05\u5ef7\u66f4\u6b32\u5f97\u800c\u7518\u5fc3\u3002\u738b\u521a\u8bf8\u4eba\u65e0\u610f\u4e4b\u4e2d\uff0c\u78b0\u7740 \u4e86\u4ed6\uff0c\u53c8\u60ca\u53c8\u559c\u3002\u738b\u521a\u81ea\u6043\u91d1\u521a\u6563\u624b\uff0c\u5e73\u751f\u65e0\u654c\uff0c\u4ed6\u672c\u60f3\u94bb\u8425\u7981\u536b\u519b\u7edf\u9886\u7684\u5730\u4f4d\uff0c\u4e0d\u6599\u695a\u662d \u5357\u56de\u4eac\u540e\uff0c\u5eb7\u7199\u5374\u628a\u8fd9\u4f4d\u7f6e\u7ed9\u4e86\u695a\u662d\u5357\uff0c\u8fde\u526f\u7edf\u9886\u5f20\u627f\u658c\u90fd\u5347\u4e0d\u4e0a\u53bb\u3002\u738b\u521a\u5927\u4e3a\u4e0d\u670d\uff0c\u65e9\u5c31 \u60f3\u627e\u673a\u4f1a\u6597\u6597\u51cc\u672a\u98ce\uff0c\u95f4\u63a5\u715e\u4f4f\u695a\u662d\u5357\u7684\u6c14\u7130\u3002 \u3000\u3000\u51cc\u672a\u98ce\u51b7\u7b11\u4e00\u58f0\uff0c\u9752\u94a2\u5251\u62d4\u5728\u624b\u4e2d\uff0c\u5251\u5c16\u4e00\u6307\uff0c\u6b63\u5f85\u53d1\u8bdd\uff0c\u731b\u542c\u5f97\u80cc\u540e\u6709\u4eba\u9ad8\u58f0\u559d\u9053\uff1a \u201c\u51cc\u5927\u54e5\uff0c\u7559\u4e0b\u6211\u7684\u4e00\u4efd\uff01\u201d\u5c4b\u4e2d\u4e00\u4eba\uff0c\u624b\u63d0\u53cc\u5251\uff0c\u65cb\u98ce\u4f3c\u7684\u98de\u5954\u51fa\u6765\uff0c\u6b64\u4eba\u6b63\u662f\u9ec4\u886b\u5c11\u5e74 \u6842\u4ef2\u660e\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5c06\u5251\u629b\u8d77\u63a5\u4e0b\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u4ed6\u662f\u77f3\u8001\u524d\u8f88\u7684\u516c\u5b50\uff0c\u4ed6\u53ef\u5c31\u662f\u4f60\u4eec\u8981\u627e\u7684\u70b9\u5b50\u4e4b \u4e00\uff0c\u4ed6\u8fd9\u4e00\u6765\uff0c\u6211\u53ef\u4e0d\u597d\u610f\u601d\u72ec\u541e\u4e86\u3002\u201d \u3000\u3000\u738b\u521a\u677f\u7740\u9762\u5b54\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u7136\u66ff\u77f3\u8001\u513f\u51fa\u5934\u63a5\u7740\uff0c\u90a3\u5c31\u5212\u51fa\u9053\u513f\u6765\u5427\uff0c\u4f60\u4e24\u4eba \u82e5\u8f93\u4e86\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u6842\u4ef2\u660e\u8bf4\u9053\uff1a\u201c\u6211\u82e5\u8f93\u4e86\uff0c\u5168\u5bb6\u8ba9\u4f60\u4eec\u62ff\u53bb\uff01\u201d\u51cc\u672a\u98ce\u7b11\u9053\u63a5\u9053\uff1a\u201c\u8fde\u6211\u4e5f\u7b97\u5728\u5185\u3002\u201d \u5362\u5927\u695e\u5b50\u5728\u65c1\u63d2\u53e3\u9053\uff1a\u201c\u8fd9\u4e0d\u516c\u5e73\uff0c\u8fd8\u6ca1\u6709\u8bf4\u4ed6\u4eec\u8f93\u4e86\u53c8\u600e\u6837\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd9\u53ef\u4e0d\u5fc5\u8bf4 \u4e86\uff0c\u53cd\u6b63\u4ed6\u4eec\u9003\u4e0d\u51fa\u53bb\u3002\u201d \u3000\u3000\u738b\u521a\u6012\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u4eec\u6709\u591a\u5927\u672c\u4e8b\uff0c\u6562\u5982\u6b64\u76ee\u4e2d\u65e0\u4eba\uff1f\u54b1\u5bb6\u4e0d\u60ef\u800d\u5634\uff0c\u5916\u9762\u89c1\u771f\u7ae0 \u53bb\uff01\u201d\u6d2a\u6d9b\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff0c\u6211\u4eec\u867d\u8bf4\u662f\u6349\u62ff\u94a6\u72af\uff0c\u5927\u5bb6\u53ef\u90fd\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u6211\u8981\u8bf7\u5728\u573a\u7684\u7f57\u5927 \u54e5\u3001\u8fbe\u571f\u53f8\u7b49\u505a\u4e2a\u8bc1\u4eba\uff0c\u8fd9\u89c4\u7ae0\u53ef\u662f\u4ed6\u4eec\u81ea\u5df1\u5b9a\u7684\uff0c\u514d\u5f97\u5404\u4f4d\u5927\u54e5\u8bf4\u6211\u4eec\u4ee5\u5f3a\u6b3a\u5f31\uff0c\u4ee5\u5927\u538b \u5c0f\u3002\u201d\u6d2a\u6d9b\u7ec8\u662f\u987e\u5fcc\u5728\u573a\u7684\u8fbe\u58eb\u53f8\u8bf8\u4eba\uff0c\u6050\u6015\u4ed6\u4eec\u4f1a\u5e2e\u51cc\u672a\u98ce\uff0c\u56e0\u6b64\u62ff\u8bdd\u5148\u538b\u7740\u4ed6\u4eec\uff0c\u65e2\u7136 \u4ed6\u4eec\u505a\u8bc1\u4eba\uff0c\u4ed6\u4eec\u5f53\u7136\u5c31\u4e0d\u80fd\u51fa\u624b\u3002 \u3000\u3000\u5362\u5927\u695e\u5b50\u54fc\u4e86\u4e00\u58f0\uff0c\u7f57\u8fbe\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff0c\u6211\u4eec\u4e5f\u60f3\u5f00\u5f00\u773c\u754c\uff01\u201d\u51cc\u672a\u98ce\u62b1\u5251\u4e00 \u63d6\u8bf4\u9053\uff1a\u201c\u627f\u5404\u4f4d\u770b\u5f97\u8d77\u6211\uff0c\u4e24\u8fb9\u90fd\u4e0d\u52a9\u62f3\uff0c\u90a3\u597d\u6781\u4e86\uff01\u77f3\u8001\u5927\u5a18\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u6765\u4e86\u3002\u201d\u8001\u5a46 \u5a46\u4ecd\u662f\u6a2a\u5251\u5f53\u95e8\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u6765\u4ec0\u4e48\uff1f\u6211\u8001\u5a46\u5b50\u4fe1\u4e0d\u8fc7\u4f60\uff0c\u8fd8\u80af\u628a\u5168\u5bb6\u5927\u5c0f\u4ed8\u5728\u4f60\u7684\u8eab \u4e0a\uff1f\u4f60\u4eec\u8981\u6253\uff0c\u53ef\u5c31\u5feb\u6253\uff0c\u8981\u79bb\u5f00\u8fdc\u4e00\u70b9\u6253\uff0c\u6211\u5f53\u5bb6\u7684\u517b\u75c5\uff0c\u4e0d\u8bb8\u4f60\u4eec\u5728\u8fd9\u91cc\u5608\u5435\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u542c\u89c1\u6ca1\u6709\uff1f\u8001\u5927\u5a18\u4e0d\u8bb8\u6211\u4eec\u5728\u8fd9\u91cc\u6253\uff0c\u5916\u9762\u5c71\u8c37\u5bbd\u9614\uff0c\u54b1\u4eec\u5916 \u9762\u6253\u53bb\u3002\u201d\u738b\u521a\u628a\u624b\u4e00\u6325\uff0c\u4e94\u4e2a\u536b\u58eb\u540c\u65f6\u5411\u5916\u9762\u8c37\u4e2d\u76c6\u5730\u8dd1\u53bb\u3002\u7533\u5929\u864e\u6084\u6084\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u4f1a \u4e0d\u4f1a\u9003\u8dd1\uff0c\u6562\u4e0d\u6562\u8ddf\u6765\uff1f\u201d\u738b\u521a\u9053\uff1a\u201c\u90a3\u4e0d\u4f1a\u3002\u201d\u7533\u5929\u8c79\u56de\u5934\u4e00\u671b\u9053\uff1a\u201c\u738b\u5927\u54e5\uff0c\u8fd9\u53ef\u8bf4\u4e0d \u5b9a\uff0c\u4ed6\u4eec\u73b0\u5728\u8fd8\u672a\u8d77\u6b65\u5462\uff01\u201d \u3000\u3000\u4e8c\u7533\u9661\u7684\u51dd\u6b65\uff0c\u6b63\u5f85\u559d\u9a82\u6fc0\u5c06\uff0c\u731b\u7136\u95f4\uff0c\u53ea\u89c1\u4e24\u6761\u9ed1\u5f71\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u76f4\u6251\u8fc7\u6765\uff0c\u8fd8\u672a\u770b \u6e05\uff0c\u5df2\u89c9\u8863\u895f\u5e26\u98ce\u4e4b\u58f0\uff0c\u62c2\u9762\u800c\u8fc7\u3002\u738b\u521a\u8eab\u5f62\u9aa4\u8d77\uff0c\u75be\u5982\u98de\u9e1f\uff0c\u5f80\u524d\u4fbf\u8ffd\uff0c\u7533\u5bb6\u5144\u5f1f\u4e5f\u731b\u7684 \u9192\u8d77\uff0c\u6025\u5fd9\u98de\u8dd1\u3002 \u3000\u3000\u4e8c\u7533\u8f6c\u8fc7\u5c71\u5773\uff0c\u521a\u5230\u76d8\u5730\uff0c\u53ea\u89c1\u90a3\u4e24\u6761\u9ed1\u5f71\u5df2\u7ad9\u5728\u5f53\u4e2d\uff0c\u51cc\u672a\u98ce\u5355\u5251\u5e73\u80f8\uff0c\u6842\u4ef2\u660e\u53cc\u5251 \u4ea4\u9519\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u536b\u58eb\u8001\u7237\u4eec\uff0c\u8fd9\u51e0\u6b65\u8def\uff0c\u4f60\u4eec\u90fd\u8d70\u5f97\u8fd9\u6837\u6162\uff01\u201d\u4e8c\u7533\u53c8\u60ca\u53c8\u607c\uff0c\u77e5\u9053\u8fd9 \u662f\u654c\u4eba\u6545\u610f\u8f83\u91cf\u4ed6\u4eec\u3002\u5fc3\u91cc\u9a82\u9053\uff1a\u201c\u4f60\u4eec\u522b\u72c2\uff0c\u8f7b\u8eab\u529f\u592b\u7b97\u5f97\u4ec0\u4e48\uff1f\u7b49\u4f1a\u53eb\u4f60\u5c1d\u5c1d\u54b1\u4eec\u7684\u5434 \u94a9\u5251\u6cd5\u7684\u6ecb\u5473\uff01\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u7f57\u8fbe\u7b49\u4eba\u4e5f\u5df1\u5230\u9f50\uff0c\u5176\u4e2d\u8fd8\u591a\u51fa\u4e00\u4f4d\u7ea2\u8863\u5c11\u5973\uff0c\u4e00\u5bf9\u79cb\u6c34\u76c8\u76c8\u7684\u773c\u775b\uff0c\u6ce8\u89c6 \u7740\u9ec4\u886b\u5c11\u5e74\u6842\u4ef2\u660e\u3002 \u3000\u3000\u8fd9\u7ea2\u8863\u5c11\u5973\u6b63\u662f\u5192\u6d63\u83b2\uff0c\u5979\u8170\u60ac\u5b9d\u5251\uff0c\u624b\u91cc\u8fd8\u63e1\u7740\u4e00\u628a\u593a\u547d\u795e\u7802\uff0c\u5979\u672c\u610f\u662f\u60f3\u51fa\u672a\u52a9 \u9635\uff0c\u4f46\u4e00\u8de8\u51fa\u95e8\uff0c\u8001\u5a46\u5a46\u5c31\u544a\u8bc9\u5979\uff0c\u5982\u975e\u654c\u4eba\u4f24\u5bb3\u5979\uff0c\u5343\u4e07\u4e0d\u80fd\u51fa\u624b\uff0c\u514d\u5f97\u635f\u4e86\u51cc\u672a\u98ce\u7684\u540d \u5934\uff0c\u56e0\u6b64\u5979\u4e5f\u6742\u5728\u7fa4\u8c6a\u4e4b\u4e2d\uff0c\u4e24\u773c\u7d27\u7d27\u76ef\u7740\u6842\u4ef2\u660e\u3002\u738b\u521a\u7a81\u89c1\u591a\u51fa\u4e00\u4e2a\u5c11\u5973\uff0c\u53c8\u89c1\u5979\u8fd9\u526f\u795e \u60c5\uff0c\u4e0d\u89c9\u77a7\u4e86\u5979\u597d\u51e0\u773c\u3002 \u3000\u3000\u8fd9\u65f6\u671d\u65e5\u521d\u5347\uff0c\u6653\u971e\u6620\u7167\uff0c\u5e7d\u8c37\u4e2d\u7684\u879f\u5ca9\u602a\u77f3\uff0c\u8c41\u7136\u663e\u9732\uff0c\u7fa4\u8c6a\u548c\u5192\u6d63\u83b2\u7b95\u8e1e\u4f5c\u58c1\u4e0a \u89c2\uff0c\u5728\u51f9\u51f8\u4e0d\u5e73\u7684\u5c71\u8c37\u76c6\u5730\u4e2d\u5219\u4e24\u9635\u5bf9\u5706\uff0c\u523d\u62d4\u5f29\u5f20\u3002\u6b63\u662f\uff1a\u8352\u5c71\u5251\u6c14\u51b2\u725b\u6597\uff0c\u4e07\u6728\u65e0\u58f0\u5f85 \u96e8\u6765\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5341\u4e00\u7ae0\u3000\u4e00\u5973\u7075\u673a\u3000\u6842\u4ef2\u660e\u65e0\u5fc3\u83b7\u5b9d\u5251\u3000\u7fa4\u8c6a\u6151\u670d\u3000\u51cc\u672a\u98ce\u8d4c\u6280\u593a\u9ec4\u91d1 \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u5341\u4e00\u56de \u4e00\u5973\u7075\u673a\u3000\u6842\u4ef2\u660e\u65e0\u5fc3\u83b7\u5b9d\u5251 \u7fa4\u8c6a\u6151\u670d\u3000\u51cc\u672a\u98ce\u8d4c\u6280\u593a\u9ec4\u91d1 \u3000\u3000\u51cc\u672a\u98ce\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u60f3\u600e\u6837\u6253\u6cd5\uff1f\u662f\u5e76\u80a9\u5b50\u4e0a\u5462\uff1f\u8fd8\u662f\u4e00\u5bf9\u4e00\u7684\u8f66\u8f6e\u6218\uff1f\u201d\u738b\u521a \u5728\u7fa4\u8c6a\u4e4b\u524d\uff0c\u4e0d\u7518\u793a\u5f31\uff0c\u9ad8\u58f0\u7b54\u9053\uff1a\u201c\u6211\u4f17\u4f60\u5be1\uff0c\u7531\u4f60\u4eec\u5148\u5212\u51fa\u9053\u6765\u5427\uff01\u201d\u51cc\u672a\u98ce\u5251\u7709\u4e00 \u626c\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u5728\u573a\u7684\u6b66\u6797\u524d\u8f88\u4e00\u8a00\uff01\u201d\u5362\u5927\u695e\u5b50\u9053\uff1a\u201c\u51cc\u5927\u4fa0\u8fd9\u8fb9\u4e24\u4e2a\u4eba\uff0c\u4ee5\u4e8c\u654c\u4e94\uff0c\u90a3 \u4e0d\u516c\u5e73\uff0c\u9876\u591a\u6bcf\u8fb9\u53ea\u80fd\u51fa\u4e24\u4e2a\u4eba\uff0c\u662f\u8054\u624b\u6216\u662f\u72ec\u6597\uff0c\u6089\u542c\u5c0a\u4fbf\u3002\u201d \u3000\u3000\u738b\u521a\u542c\u4e86\uff0c\u6b63\u60f3\u6d3e\u7533\u5bb6\u5144\u5f1f\u53eb\u9635\uff0c\u5ddd\u9655\u7763\u5e9c\u7684\u536b\u58eb\u7126\u76f4\u3001\u6d2a\u6d9b\u5df2\u62a2\u51fa\u6765\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a \u201c\u6211\u4eec\u4e45\u95fb\u77f3\u8001\u524d\u8f88\u6b66\u529f\u7cbe\u5f3a\uff0c\u60f3\u8bf7\u6559\u4ed6\u7684\u516c\u5b50\u51e0\u62db\uff0c\u51cc\u5e08\u7236\u8981\u4e0a\u6765\u4e5f\u53ef\u4ee5\u3002\u201d\u8fd9\u4e24\u4eba\u9887\u5de5 \u5fc3\u8ba1\uff0c\u4ed6\u4eec\u81ea\u77e5\u6b66\u529f\u4e0d\u53ca\u5927\u5185\u9ad8\u624b\uff0c\u53c8\u6015\u88ab\u4eba\u8f7b\u89c6\uff0c\u56e0\u6b64\u4e00\u4e0a\u6765\u5c31\u62ff\u8bdd\u6263\u7740\u51cc\u672a\u98ce\uff0c\u6307\u540d\u7d22 \u6218\u9ec4\u886b\u5c11\u5e74\u3002\u4ed6\u4eec\u662f\u8bf4\u201c\u51cc\u5e08\u7236\u4e0a\u6765\u4e5f\u53ef\u4ee5\u201d\uff0c\u4f46\u4ed6\u4eec\u77e5\u9053\u4ee5\u51cc\u672a\u98ce\u7684\u540d\u5934\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u542c\u4e86 \u8fd9\u79cd\u4f3c\u8fce\u5b9e\u62d2\u7684\u8bdd\u540e\uff0c\u8fd8\u6765\u548c\u4ed6\u4eec\u76f8\u6597\uff0c\u800c\u9ec4\u886b\u5c11\u5e74\uff0c\u4ed6\u4eec\u5374\u5e76\u672a\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u51cc\u672a\u98ce\u6de1\u6de1\u4e00\u7b11\uff0c\u679c\u7136\u6309\u5251\u4e0d\u52a8\u3002\u9ec4\u886b\u5c11\u5e74\u6842\u4ef2\u660e\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u624b\u63d0\u53cc\u5251\uff0c\u6ee1\u4e0d\u5728\u4e4e\u5730 \u5c31\u4e0a\u524d\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u53d1\u62db\u5427\uff01\u4f60\u4eec\u4e24\u4eba\u90a3\u503c\u5f97\u6211\u5927\u54e5\u52a8\u624b\uff01\u201d \u3000\u3000\u7126\u76f4\u4f7f\u7684\u662f\u4e00\u5bf9\u65b9\u5929\u753b\u621f\uff0c\u5728\u5ddd\u9655\u7763\u5e9c\u4e4b\u4e2d\uff0c\u6b66\u529f\u7b2c\u4e00\u3002\u89c1\u6842\u4ef2\u660e\u61d2\u6d0b\u6d0b\u7684\u4e0d\u7acb\u95e8\u6237\uff0c \u4e58\u4ed6\u8bf4\u8bdd\u4e4b\u9645\uff0c\u7a81\u5730\u53cc\u621f\u4e00\u5206\uff0c\u201c\u6307\u5929\u5212\u5730\u201d\uff0c\u621f\u4e0a\u767d\u68ee\u68ee\u7684\u4e94\u5bf8\u591a\u957f\u7684\u9e2d\u5634\u5c16\u950b\uff0c\u547c\u7684\u523a \u5411\u6842\u4ef2\u660e\u5de6\u81c2\u3002\u6d2a\u6d9b\u4f7f\u4e00\u67c4\u82b1\u9cde\u7d2b\u91d1\u5200\uff0c\u4e00\u4e2a\u6402\u819d\u7ed5\u6b65\uff0c\u8f6c\u5230\u6842\u4ef2\u660e\u540e\u4fa7\uff0c\u987a\u52bf\u75be\u5c55\u5200\u950b\uff0c \u6a2a\u65a9\u654c\u624b\u540e\u8170\u3002\u524d\u540e\u5939\u653b\uff0c\u60f3\u4e00\u4e0b\u5b50\u5c31\u628a\u6842\u4ef2\u660e\u7f6e\u4e8e\u6b7b\u5730\u3002 \u3000\u3000\u6842\u4ef2\u660e\u9661\u5730\u4e00\u58f0\u5927\u559d\uff0c\u5982\u6674\u5929\u8d77\u4e2a\u9739\u96f3\uff0c\u820c\u5934\u7efd\u51fa\u6625\u96f7\uff0c\u77f3\u5251\u5411\u4e0a\u4e00\u62ac\uff0c\u53ea\u542c\u5f97\u5618\u568e\u4e00 \u58f0\uff0c\u628a\u7126\u76f4\u65b9\u5929\u753b\u621f\u7684\u9e2d\u5634\u5c16\u950b\uff0c\u767b\u65f6\u622a\u65ad\uff01\u4ed6\u5934\u4e5f\u4e0d\u56de\uff0c\u5de6\u624b\u5f80\u540e\u4e00\u64a9\uff0c\u642d\u7740\u4e86\u6d2a\u6d9b\u653b\u6765 \u7684\u5200\u950b\uff0c\u987a\u52bf\u4e00\u63a8\uff0c\u6d2a\u6d9b\u53ea\u89c9\u4e00\u80a1\u5927\u529b\u538b\u6765\uff0c\u4e8c\u5341\u516b\u65a4\u91cd\u7684\u5927\u5200\u51e0\u4e4e\u8131\u624b\u98de\u53bb\u3002\u7126\u76f4\u6025\u5fd9\u53eb \u9053\uff1a\u201c\u6d2a\u4e8c\u5f1f\uff0c\u4f60\u8d70\u5de6\u9762\u504f\u950b\uff0c\u4e0a\uff01\u201d\u4ed6\u4e00\u5bf9\u65b9\u5929\u753b\u621f\uff0c\u62a1\u8f6c\u5982\u98ce\uff0c\u4f7f\u51fa\u8bb8\u591a\u82b1\u62db\uff0c\u62fc\u547d\u67b6 \u4f4f\u6842\u4ef2\u660e\u7684\u53cc\u5251\u3002 \u3000\u3000\u8fd9\u662f\u6842\u4ef2\u660e\u81ea\u7075\u667a\u6062\u590d\u4e4b\u540e\uff0c\u7b2c\u4e00\u6b21\u4e0e\u5f3a\u654c\u76f8\u6597\u3002\u4ed6\u89c1\u5192\u6d63\u83b2\u501a\u7740\u5ca9\u77f3\uff0c\u7b11\u76c8\u76c8\u5730\u671b\u7740 \u4ed6\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u53cc\u5251\u65bd\u5c55\u5f00\u6765\uff0c\u7cbe\u8292\u7535\u95ea\uff0c\u4e0d\u8fc7\u4e00\u4f1a\uff0c\u7126\u76f4\u3001\u6d2a\u6d9b\u4e8c\u4eba\u5c31\u5168\u88ab\u5251\u5149\u88f9\u7740\u3002\u7f57 \u8fbe\u7b49\u4eba\uff0c\u5728\u65c1\u8fb9\u770b\u5f97\u76ee\u7729\u5fc3\u60ca\uff0c\u6599\u4e0d\u5230\u77f3\u5929\u6210\u7684\u513f\u5b50\uff0c\u4e5f\u6709\u8fd9\u6837\u7684\u529f\u592b\uff01 \u3000\u3000\u53c8\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6842\u4ef2\u660e\u5df2\u770b\u51fa\u7126\u76f4\u7684\u621f\u6cd5\u5168\u662f\u82b1\u62db\uff0c\u4e0d\u6562\u548c\u81ea\u5df1\u786c\u78b0\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u89d1\u51c6\u6765 \u8def\uff0c\u4e00\u62db\u201c\u5de7\u5973\u7a7f\u9488\u201d\uff0c\u95ea\u7535\u822c\u5730\u523a\u5c06\u51fa\u53bb\uff0c\u7126\u76f4\u53f3\u817f\u5f80\u540e\u4e00\u64a4\uff0c\u5de6\u671d\u4e00\u6643\uff0c\u201c\u4e3e\u6b64\u64a9 \u5929\u201d\uff0c\u77f3\u81c2\u4e00\u6c89\uff0c\u201c\u767d\u9e64\u63a0\u7fc5\u201d\u53f3\u621f\u5411\u4e0b\u4e00\u515c\u4e00\u626b\u3002\u53f3\u621f\u4e3b\u653b\uff0c\u4e43\u662f\u865a\u5f0f\uff0c\u5de6\u621f\u4e3b\u5b88\uff0c\u65b9\u662f \u5b9e\u62db\uff0c\u4e0d\u6599\u6842\u4ef2\u660e\u90a3\u62db\u4e5f\u662f\u865a\u5f0f\uff0c\u7126\u76f4\u5de6\u621f\u4e00\u62ac\uff0c\u4ed6\u5c31\u75be\u5410\u75be\u6536\uff0c\u6b65\u6cd5\u4e00\u53d8\uff0c\u8eab\u5f62\u4e00\u632b\uff0c\u500f \u53d8\u4e3a\u201c\u733f\u7334\u6458\u679c\u201d\uff0c\u8fde\u6311\u5e26\u523a\uff0c\u9752\u5149\u4e00\u95ea\uff0c\u6311\u6863\u523a\u8179\uff0c\u731b\u4e0b\u6740\u624b\uff01\u7126\u76f4\u5927\u53eb\u4e00\u58f0\uff0c\u53cc\u621f\u540c\u65f6 \u56de\u6551\u3002\u6842\u4ef2\u660e\u4e00\u58f0\u5927\u559d\uff0c\u5251\u5149\u8d77\u5904\uff0c\u628a\u4e00\u679d\u65b9\u5929\u753b\u621f\u5288\u6210\u4e24\u6bb5\uff0c\u53f3\u817f\u8d77\u5904\uff0c\u53c8\u628a\u4e00\u679d\u753b\u621f\u8e22 \u4e0a\u534a\u7a7a\uff0c\u60e8\u53eb\u58f0\u4e2d\uff0c\u7126\u76f4\u7684\u4e00\u6761\u624b\u81c2\u5df2\u4e0e\u8eab\u4f53\u5206\u5bb6\uff0c\u6842\u4ef2\u660e\u4e00\u817f\u628a\u7126\u76f4\u6c34\u725b\u822c\u7684\u8eab\u8eaf\u6a2a\u626b\u51fa \u6570\u4e08\u5f00\u5916\uff0c\u521a\u597d\u649e\u7740\u5ca9\u77f3\uff0c\u773c\u89c1\u4e0d\u80fd\u6d3b\u4e86\u3002 \u3000\u3000\u8fd9\u51e0\u62db\u5feb\u5982\u7535\u5149\u77f3\u706b\uff0c\u4f8d\u6d2a\u6d9b\u770b\u5f97\u6e05\u695a\uff0c\u6025\u5fd9\u540e\u9000\uff0c\u5df2\u6765\u4e0d\u53ca\uff0c\u6842\u4ef2\u660e\u817e\u7a7a\u4e00\u8dc3\uff0c\u597d\u4f3c \u5e73\u5730\u98de\u8d77\u4e00\u5934\u5de8\u9e70\uff0c\u5411\u6d2a\u6d9b\u5f53\u5934\u7f69\u4e0b\uff0c\u6d2a\u6d9b\u7d2b\u91d1\u5200\u5f80\u4e0a\u4e00\u62db\uff0c\u54ea\u6321\u5f97\u4f4f\uff01\u53ea\u542c\u5f97\u5580\u5693\u4e00\u58f0\uff0c \u624b\u8155\u5148\u65ad\uff0c\u8eab\u5b50\u4e5f\u8ddf\u7740\u88ab\u5288\u6210\u4e24\u8fb9\u3002\u8fd9\u662f\u4e94\u79bd\u5251\u6cd5\u4e2d\u7684\u7edd\u62db\uff0c\u540d\u4e3a\u201c\u82cd\u9e70\u6251\u5154\u201d\uff0c\u90fd\u662f\u4ed6\u6bcd \u4eb2\u6240\u6388\u3002 \u3000\u3000\u738b\u521a\u7b49\u4e09\u4e2a\u5927\u5185\u9ad8\u624b\uff0c\u867d\u770b\u4e0d\u8d77\u8fd9\u4e24\u4e2a\u5ddd\u9655\u7763\u5e9c\u7684\u536b\u58eb\uff0c\u4f46\u4e5f\u6599\u60f3\u4e0d\u5230\u53ea\u4e0d\u8fc7\u4e00\u76cf\u8336\u7684 \u529f\u592b\uff0c\u4e24\u4eba\u5c31\u90fd\u4e86\u7ed3\uff0c\u800c\u6b63\u70b9\u5b50\u51cc\u672a\u98ce\u8fd8\u672a\u51fa\u573a\u3002\u738b\u521a\u7709\u5934\u4e00\u76b1\uff0c\u6b63\u5f85\u4eb2\u81ea\u51fa\u573a\uff0c\u7528\u91d1\u521a\u624b \u6cd5\u786c\u62a2\u6842\u4ef2\u660e\u7684\u53cc\u5251\u3002\u53ea\u89c1\u7533\u5bb6\u5144\u5f1f\u4e8c\u4eba\uff0c\u5df2\u8054\u8882\u800c\u51fa\u3002\u6842\u4ef2\u660e\u53cc\u5251\u4e00\u7acb\uff0c\u4e25\u9635\u4ee5\u5f85\u3002\u51cc\u672a \u98ce\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u6842\u8d24\u5f1f\uff0c\u4f60\u5df2\u591f\u672c\u6709\u8d5a\u4e86\uff0c\u8fd9\u4e24\u4e2a\u8ba9\u7ed9\u6211\u5427\uff01\u201d \u3000\u3000\u7533\u5bb6\u5144\u5f1f\u7684\u5434\u94a9\u5251\u6cd5\u662f\u6ca7\u6d32\u6d2a\u56db\u628a\u5b50\u7684\u771f\u4f20\uff0c\u4e43\u662f\u4e24\u4eba\u5408\u4f7f\u7684\u3002\u7533\u5929\u864e\u4f7f\u4e00\u5bf9\u62a4\u624b\u94a9\uff0c \u7528\u4ee5\u9501\u62ff\u654c\u4eba\u5200\u5251\uff0c\u5b88\u4e2d\u5e26\u653b\uff1b\u7533\u5929\u8c79\u4f7f\u4e00\u67c4\u957f\u5251\uff0c\u5219\u5b8c\u5168\u662f\u8fdb\u624b\u7684\u62db\u6570\u3002\u8fd9\u5bf9\u5144\u5f1f\u7684\u5434\u94a9 \u5251\u6cd5\uff0c\u6240\u4ee5\u79f0\u4e3a\u6b66\u5b66\u4e00\u7edd\uff0c\u4e43\u662f\u56e0\u4e3a\u4ed6\u4eec\u653b\u5b88\u914d\u5408\uff0c\u6070\u5230\u597d\u5904\u3002\u4e09\u5341\u5e74\u6765\uff0c\u5f1f\u5144\u51fa\u624b\uff0c\u4ece\u672a \u843d\u8fc7\u4e0b\u98ce\u3002\u5c31\u662f\u5728\u4eac\u57ce\u4e4b\u65f6\uff0c\u695a\u662d\u5357\u548c\u4ed6\u4eec\u6bd4\u8bd5\uff0c\u7528\u5c3d\u529f\u592b\uff0c\u4e5f\u53ea\u662f\u52c9\u5f3a\u6253\u4e2a\u5e73\u624b\u3002 \u3000\u3000\u51cc\u672a\u98ce\u4e45\u5386\u6c5f\u6e56\uff0c\u89c1\u591a\u8bc6\u5e7f\uff0c\u6df1\u77e5\u6ca7\u6d32\u6d2a\u5bb6\u7684\u5434\u94a9\u5251\u6cd5\u7684\u5389\u5bb3\u3002\u4e00\u89c1\u7533\u5bb6\u5144\u5f1f\u7684\u5175\u5203\u548c \u8054\u8882\u51fa\u573a\u65f6\u7684\u8eab\u5f62\uff0c\u5c31\u77e5\u662f\u6d2a\u95e8\u5f1f\u5b50\u3002\u4ed6\u6050\u6015\u9ec4\u886b\u5c11\u5e74\u6b66\u529f\u867d\u5f3a\uff0c\u4f46\u7ecf\u5386\u5c1a\u6d45\uff0c\u4e0d\u61c2\u5e94\u53d8\uff0c \u56e0\u6b64\u6025\u6025\u8d76\u4e0a\uff0c\u66ff\u56de\u4e86\u4ed6\u3002 \u3000\u3000\u7533\u5bb6\u5144\u5f1f\u7acb\u597d\u95e8\u6237\uff0c\u559d\u58f0\uff1a\u201c\u63a5\u62db\uff01\u201d\u7533\u5929\u8c79\u7684\u4e00\u957f\u5251\u4fbf\u5411\u51cc\u672a\u98ce\u80f8\u524d\u624e\u53bb\uff0c\u51cc\u672a\u98ce\u77e5 \u9053\u4ed6\u4eec\u4e00\u653b\u4e00\u5b88\uff0c\u4e13\u627e\u7834\u7efd\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u5140\u7acb\u5982\u5c71\uff0c\u5f85\u5f97\u7533\u5929\u8c79\u7684\u5251\u5c16\u521a\u4e00\u53ca\u80f8\uff0c\u8eab\u5b50\u7a81\u7136 \u9065\u52a8\uff0c\u624b\u4e2d\u7684\u9752\u94a2\u5251\u201c\u5f53\u201d\u7684\u4e00\u58f0\u4fbf\u8361\u5f00\u4e86\u7533\u5929\u8c79\u7684\u5251\u5c16\uff0c\u671b\u90fd\u4e0d\u671b\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u53c8\u6070\u6070\u628a \u7533\u5929\u864e\u653b\u6765\u7684\u53cc\u94a9\u683c\u8fc7\uff0c\u4ed6\u62ff\u634f\u65f6\u5019\uff0c\u6070\u5230\u597d\u5904\uff0c\u7533\u5bb6\u5144\u5f1f\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u4e09\u4eba\u4e00\u89e6\u5373\u5206\uff0c\u6597 \u9e21\u4f3c\u7684\u4e92\u76f8\u76ef\u7740\uff0c\u8fbe\u571f\u53f8\u4e09\u5341\u5e74\u524d\u89c1\u8fc7\u6d2a\u56db\u628a\u5b50\u5434\u94a9\u5251\u8868\u6f14\uff0c\u6084\u6084\u5bf9\u5362\u5927\u695e\u5b50\u8bf4\u9053\uff1a\u201c\u8fd9\u662f \u78b0\u5230\u6781\u5f3a\u7684\u5bf9\u624b\u65f6\uff0c\u624d\u4f1a\u5982\u6b64\u3002\u8fd9\u4e24\u5144\u5f1f\u662f\u60f3\u7b49\u51cc\u672a\u98ce\u5148\u53d1\u62db\uff0c\u624d\u627e\u4ed6\u7684\u7a7a\u95e8\u8fdb\u51fb\u3002\u770b\u6765\u8fd9 \u4e2a\u2018\u5929\u5c71\u795e\u8292\u2019\u6562\u60c5\u771f\u6709\u70b9\u672c\u4e8b\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u51cc\u672a\u98ce\u5927\u559d\u4e00\u58f0\uff0c\u9752\u94a2\u5251\u4e00\u9707\uff0c\u5411\u7533\u5929 \u8c79\u6a2a\u626b\u8fc7\u53bb\uff0c\u5251\u5c16\u98a4\u52a8\uff0c\u5bd2\u5149\u70b9\u70b9\uff0c\u5982\u6d6a\u82b1\u822c\u76f4\u6d12\u4e0b\u6765\uff0c\u7533\u5bb6\u5144\u5f1f\u5e03\u6210\u7284\u89d2\u4e4b\u52bf\uff0c\u53cc\u94a9\u4e00 \u5251\uff0c\u653b\u52bf\u4e5f\u662f\u6709\u5982\u66b4\u98ce\u9aa4\u96e8\u3002\u5251\u5149\u95ea\u95ea\uff0c\u94a9\u73af\u5c71\u54cd\uff0c\u6253\u5f97\u96be\u89e3\u96be\u5206\uff01 \u3000\u3000\u6597\u4e86\u4e00\u767e\u4f59\u62db\uff0c\u7533\u5bb6\u5144\u5f1f\u989d\u5934\u89c1\u6c57\uff0c\u51cc\u672a\u98ce\u4ecd\u662f\u795e\u8272\u81ea\u5982\uff0c\u65c1\u8fb9\u7684\u4eba\u8fd8\u672a\u770b\u51fa\u4ec0\u4e48\uff0c\u738b \u521a\u5df2\u77e5\u4e0d\u5999\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u5954\u4e86\u51fa\u6765\uff01\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u4e24\u4f4d\u5144\u5f1f\u8bf7\u9000\uff0c\u5f85\u627e\u9886\u6559\u4e00\u4e0b\u51cc\u5e08\u7236\u7684\u5251 \u62db\u3002\u201d \u3000\u3000\u7533\u5bb6\u5144\u5f1f\u62fc\u547d\u75be\u653b\u6570\u62db\uff0c\u63a9\u62a4\u64a4\u9000\u3002\u51cc\u672a\u98ce\u84e6\u5730\u4e00\u58f0\u957f\u7b11\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8981\u8ba4\u8f93\u4e5f \u4e0d\u884c\uff01\u201d\u5251\u6cd5\u4e00\u53d8\uff0c\u7ffb\u7ffb\u6eda\u6eda\uff0c\u7533\u5bb6\u5144\u5f1f\u53ea\u89c9\u51b7\u6c14\u68ee\u68ee\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u56db\u9762\u516b\u65b9\u5168\u662f\u51cc\u672a\u98ce\u7684 \u5f71\u5b50\u3002 \u3000\u3000\u738b\u521a\u5954\u51fa\u9635\u6765\uff0c\u89c1\u4e09\u4eba\u4ecd\u662f\u82e6\u6597\u4e0d\u4f11\uff0c\u5251\u5149\u6325\u970d\uff0c\u5251\u6c14\u7eb5\u6a2a\uff0c\u54ea\u91cc\u62bd\u5f97\u8fdb\u53bb\uff1f\u800c\u4e14\u4e24\u65b9 \u6709\u8a00\u5728\u5148\uff0c\u4ee5\u4e8c\u6253\u4e00\u5df2\u6709\u5931\u9762\u5b50\uff0c\u81ea\u5df1\u518d\u63d2\u8fdb\u53bb\uff0c\u7eb5\u80fd\u6253\u80dc\uff0c\u4e5f\u4ee4\u5929\u4e0b\u82f1\u96c4\u9f7f\u7b11\u3002\u4f55\u51b5\u738b\u521a \u4e43\u662f\u6210\u540d\u4eba\u7269\uff0c\u4ee5\u91d1\u521a\u6563\u624b\uff0c\u4eab\u8a89\u4e09\u5341\u4f59\u5e74\uff0c\u5728\u5404\u8def\u9ad8\u624b\u4e4b\u524d\u3002\u66f4\u4e0d\u6b32\u4e3a\u4eba\u6240\u7b11\u3002 \u3000\u3000\u738b\u521a\u6b63\u5728\u8e0f\u8e87\uff0c\u5ffd\u89c1\u5bf9\u9762\u7684\u9ec4\u886b\u5c11\u5e74\u6842\u4ef2\u660e\uff0c\u7f13\u6b65\u800c\u51fa\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u51cc\u5927\u54e5\u6ca1\u7a7a\u548c\u4f60 \u727a\uff0c\u6211\u6765\u63a5\u4f60\u51e0\u62db\u3002\u201d\u738b\u521a\u6b63\u82e6\u65e0\u6cd5\u4e0b\u53f0\uff0c\u89c1\u4ed6\u51fa\u6765\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u62d4\u5251 \u5427\uff01\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u5c0f\u7237\u4e0d\u5148\u4eae\u5175\u5203\uff0c\u4f60\u7684\u5175\u5668\u5462\uff1f\u4f60\u8981\u5355\u6253\u72ec\u6597\uff0c\u6211\u5c31\u8ba9\u4f60\u5148\u8fdb\u4e09\u62db\u3002\u201d \u738b\u521a\u54c8\u54c8\u5927\u7b11\uff0c\u5fc3\u60f3\u8fd9\u5c11\u5e74\u4e00\u5b9a\u662f\u672a\u66fe\u51fa\u9053\u7684\u96cf\u513f\uff0c\u81ea\u5df1\u4ee5\u91d1\u521a\u6563\u624b\u540d\u9707\u6b66\u6797\uff0c\u4ece\u6765\u4e0d\u7528\u6b66 \u5668\uff0c\u4ed6\u7adf\u7136\u53eb\u81ea\u5df1\u53d6\u51fa\u5175\u5203\uff0c\u771f\u662f\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\uff0c\u5f53\u4e0b\u53cc\u624b\u4e00\u644a\uff0c\u7b11\u5f97\u524d\u4fef\u540e\u4ef0\uff0c\u8bf4\u9053\uff1a \u201c\u4f60\u95ee\u5728\u573a\u7684\u53d4\u4f2f\uff0c\u51e0\u65f6\u542c\u89c1\u6211\u738b\u521a\u7528\u8fc7\u5175\u5203\uff1f\u4f60\u5c3d\u529b\u53cc\u5251\u65ab\u6765\u5427\uff0c\u770b\u6211\u63a5\u4e0d\u63a5\u5f97\u4f4f\u4f60\uff1f\u201d \u3000\u3000\u6842\u4ef2\u660e\u9762\u4e00\u6c89\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u7b11\u5f97\u65e9\u4e86\u70b9\u513f\uff0c\u89c1\u8fc7\u80dc\u8d1f\u4f60\u518d\u7b11\u5427\uff01\u90a3\u65f6\u4f60\u7b11\u5f97\u51fa\u6765\u7b97 \u4f60\u597d\u6c49\u3002\u597d\uff01\u4f60\u65e2\u7136\u4e0d\u7528\u5175\u5203\uff0c\u5c0f\u7237\u4e5f\u7a7a\u624b\u63a5\u4f60\u51e0\u62db\u3002\u201d\u8bf4\u7f62\u628a\u5251\u62d4\u51fa\uff0c\u731b\u7136\u63b7\u5411\u5c71\u5d16\uff0c\u767b \u65f6\u788e\u77f3\u7eb7\u98de\uff0c\u4e24\u53e3\u5251\u76f4\u6ca1\u5230\u5251\u67c4\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u6211\u8eab\u4e0a\u4e5f\u6ca1\u4e86\u5175\u5668\uff0c\u4f60\u653e\u5fc3\u4e86\u5427\uff1f\u5484\uff0c\u4f60\u8fd8 \u4e0d\u8fdb\u62db\u662f\u4f55\u9053\u7406\uff1f\u4f60\u5230\u5e95\u60f3\u4e0d\u60f3\u6253\uff1f\u201d \u3000\u3000\u6842\u4ef2\u660e\u4eae\u4e86\u8fd9\u624b\uff0c\u65c1\u89c2\u7684\u7fa4\u8c6a\u90fd\u5927\u5403\u4e00\u60ca\u3002\u4ed6\u4eec\u867d\u89c1\u8fc7\u6842\u4ef2\u660e\u6597\u7126\u76f4\u3001\u6d2a\u6d9b\u7684\u6b66\u529f\uff0c\u4f46 \u4ed6\u4eec\u90fd\u77e5\u9053\u738b\u521a\u7684\u5389\u5bb3\uff0c\u4ed6\u4eec\u60f3\u6842\u4ef2\u660e\u4ed7\u5251\u76f8\u6597\uff0c\u8fd8\u672a\u5fc5\u5f97\u80dc\uff0c\u5982\u4f55\u8fd9\u6837\u72c2\u5984\u81ea\u5927\uff0c\u5c0f\u5c0f\u5e74 \u7eaa\uff0c\u7adf\u8981\u8d64\u624b\u7a7a\u62f3\u5bf9\u4ed8\u6b66\u6797\u7684\u6210\u540d\u4eba\u7269\uff1f \u3000\u3000\u5192\u6d63\u83b2\u89c1\u7fa4\u8c6a\u7a83\u7a83\u79c1\u8bed\uff0c\u9762\u9732\u9a87\u5bb9\uff0c\u53c8\u89c1\u738b\u521a\u51fa\u573a\u65f6\u7684\u58f0\u52bf\u5484\u5484\u903c\u4eba\uff0c\u77e5\u9053\u6b64\u4eba\u5fc5\u662f\u4e94 \u4e2a\u536b\u58eb\u4e4b\u9996\uff0c\u6709\u7740\u975e\u5e38\u7684\u6b66\u529f\uff0c\u4e0d\u89c9\u5411\u524d\u79fb\u4e86\u51e0\u6b65\u3002\u5362\u5927\u695e\u5b50\u4ee5\u4e3a\u5979\u662f\u77f3\u5929\u6210\u7684\u5973\u513f\uff0c\u8f7b\u58f0 \u53eb\u9053\uff1a\u201c\u4f60\u628a\u4f60\u7684\u54e5\u54e5\u53eb\u56de\u6765\u5427\uff0c\u8fd9\u4eba\u5916\u5bb6\u529f\u592b\u767b\u5cf0\u9020\u6781\uff0c\u91d1\u521a\u6563\u624b\uff0c\u5929\u4e0b\u65e0\u5bf9\uff0c\u8ba9\u51cc\u5927\u4fa0 \u548c\u4ed6\u6253\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u62db\u67b6\u5f97\u4f4f\u3002\u201d\u5192\u6d63\u83b2\u542c\u4e86\uff0c\u5148\u662f\u4e00\u60ca\uff0c\u542c\u5b8c\u4e86\u5fc3\u5934\u53cd\u800c\u7a0d\u5bbd\u4e86\u3002\u5979\u60f3\uff1a\u6842 \u4ef2\u660e\u7684\u529f\u592b\u6bd4\u51cc\u672a\u98ce\u7684\u529f\u592b\u5dee\u4e0d\u4e86\u591a\u5c11\uff0c\u8fd9\u4eba\u8bf4\u51cc\u672a\u98ce\u62db\u67b6\u5f97\u4f4f\uff0c\u90a3\u4ed6\u7eb5\u6700\u4e0d\u6d4e\u4e5f\u53ef\u4ee5\u652f\u6301 \u4e00\u4e9b\u65f6\u5019\uff0c\u90a3\u65f6\u51cc\u672a\u98ce\u65e9\u5df2\u628a\u90a3\u4e24\u4e2a\u5bb6\u4f19\u6536\u62fe\u4e86\u3002\u4f46\uff0c\u867d\u7136\u5982\u6b64\uff0c\u5192\u6d63\u83b2\u8fd8\u662f\u5fc3\u5934\u9e7f\u649e\uff0c\u6b63 \u6240\u8c13\u60c5\u975e\u6cdb\u6cdb\uff0c\u4efd\u5916\u5173\u5fc3\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5730\u4ecd\u7136\u4e00\u6b65\u6b65\u79fb\u8fd1\u6597\u573a\u3002\u5362\u5927\u695e\u5b50\u867d\u7136\u53d1\u89c9\uff0c\u4f46\u60f3\uff1a\u8ba9 \u5979\u51fa\u53bb\uff0c\u5f85\u4e8b\u6025\u4e4b\u65f6\u76f8\u6551\u4e5f\u597d\u3002\u53cd\u6b63\u90a3\u8fb9\u5927\u5185\u9ad8\u624b\u90fd\u5df2\u51fa\u5f00\uff0c\u5979\u4e0a\u53bb\u5e2e\u9ec4\u886b\u5c11\u5e74\uff0c\u4e5f\u53ea\u662f\u4e09 \u5bf9\u4e09\uff0c\u4e0d\u7b97\u72af\u4e86\u89c4\u7ae0\u3002 \u3000\u3000\u738b\u521a\u542c\u5f97\u6842\u4ef2\u660e\u53eb\u4ed6\u5148\u884c\u53d1\u62db\uff0c\u6012\u4e0d\u53ef\u904f\uff0c\u5fc3\u60f3\uff1a\u6211\u4e00\u638c\u4e0b\u53bb\uff0c\u4e0d\u628a\u4f60\u6253\u6210\u8089\u9171\u624d\u602a\u3002 \u6842\u4ef2\u660e\u61d2\u6563\u6563\u5730\u53c8\u201c\u5478\u201d\u7684\u4e00\u58f0\u9053\uff1a\u201c\u8fd8\u4e0d\u52a8\u624b\uff0c\u7b49\u4f60\u4ea4\u4ee3\u540e\u4e8b\u5417\uff1f\u201d\u738b\u521a\u6012\u543c\u4e00\u58f0\uff0c\u4f38\u5f00 \u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u638c\u631f\u52b2\u98ce\uff0c\u4e00\u638c\u4fbf\u5411\u6842\u4ef2\u660e\u592a\u9633\u7a74\u6253\u53bb\u3002\u6842\u4ef2\u660e\u8eab\u8eaf\u4e00\u95ea\uff0c\u8f7b\u5815\u907f\u8fc7\uff1b\u738b\u521a \u5de6\u638c\u968f\u53d1\uff0c\u6842\u4ef2\u660e\u518d\u9000\u4e09\u6b65\uff0c\u4ecd\u7136\u95ea\u5f00\u3002\u738b\u521a\u84e6\u7136\u5411\u524d\u4e00\u8dc3\uff0c\u53cc\u638c\u5316\u62f3\uff0c\u201c\u4e8c\u9b3c\u62cd\u95e8\u201d\uff0c\u731b \u5730\u5939\u51fb\u6842\u4ef2\u660e\u53cc\u988a\uff0c\u8fd9\u62db\u84e6\u5982\u661f\u706b\uff0c\u5362\u5927\u695e\u5b50\u60ca\u53eb\u8d77\u6765\uff0c\u5192\u6d63\u83b2\u4e00\u9897\u5fc3\u7a81\u7a81\u8df3\u52a8\uff0c\u95ed\u4e86\u53cc\u5df2 \u4e0d\u6562\u518d\u770b\u3002\u5728\u573a\u7684\u5404\u8def\u9ad8\u624b\uff0c\u90fd\u4ee5\u4e3a\u6842\u4ef2\u660e\u5fc5\u906d\u6bd2\u624b\uff0c\u4e0d\u6599\u6842\u4ef2\u660e\u8eab\u6cd5\u5947\u5feb\uff0c\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645 \u5c31\u5728\u738b\u521a\u62f3\u5934\u4e4b\u4e0b\u94bb\u4e86\u8fc7\u53bb\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u8bf4\u8981\u8ba9\u4f60\u4e09\u62db\uff0c\u4f60\u770b\u662f\u4e0d\u662f\u3002\u201d \u3000\u3000\u539f\u6765\u6842\u4ef2\u660e\u81ea\u5e7c\u8ddf\u968f\u4e49\u7236\u6842\u5929\u6f9c\uff0c\u7ec3\u4e60\u5927\u529b\u9e70\u722a\u529f\u3002\u5927\u529b\u9e70\u722a\u529f\u548c\u91d1\u521a\u6563\u624b\u662f\u540c\u4e00\u8def \u6570\uff0c\u4ed6\u542c\u4e49\u7236\u8bf4\u8fc7\uff0c\u8fd9\u7c7b\u786c\u529f\u592b\u8bb2\u7a76\u7684\u662f\u4e00\u9f13\u4f5c\u6c14\uff0c\u8fde\u73af\u731b\u6251\uff0c\u6700\u6015\u662f\u5f3a\u653b\u4e0d\u4e0b\uff0c\u6d88\u4e86\u9510 \u6c14\u3002\u6842\u4ef2\u660e\u53c8\u4ed7\u7740\u81ea\u5e7c\u5728\u5251\u9601\u7edd\u9876\u4e4b\u5904\u957f\u5927\uff0c\u6574\u65e5\u4e0e\u733f\u7334\u4e3a\u4f0d\uff0c\u5929\u751f\u5c31\u4e00\u526f\u7edd\u9876\u7684\u8f7b\u8eab\u529f \u592b\u3002\u56e0\u6b64\u6545\u610f\u62ff\u8bdd\u6765\u6fc0\u738b\u521a\uff0c\u8fde\u907f\u4e09\u62db\uff0c\u632b\u6298\u4ed6\u7684\u9a84\u7130\u3002\u4f46\u80a9\u5934\u8fd8\u662f\u7ed9\u738b\u521a\u7684\u62f3\u98ce\u626b\u7740\uff0c\u611f \u5230\u4e00\u9635\u706b\u8fa3\u8fa3\u7684\u75bc\u75db\u3002 \u3000\u3000\u738b\u521a\u5374\u4e0d\u77e5\u6842\u4ef2\u660e\u4e5f\u53d7\u4e86\u632b\u6298\uff0c\u89c1\u4ed6\u8fde\u907f\u4e09\u62db\uff0c\u679c\u7136\u9510\u6c14\u5927\u6298\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u5f53\u4e0b\u518d\u4e0d\u6562 \u8f7b\u654c\uff0c\u5de6\u638c\u62a4\u80f8\uff0c\u53f3\u638c\u53c8\u662f\u201c\u547c\u201d\u7684\u4e00\u58f0\u5411\u6842\u4ef2\u660e\u80f8\u53e3\u6253\u6765\u3002\u4ed6\u7528\u7684\u662f\u91d1\u521a\u6563\u624b\u4e2d\u7684\u201c\u6392\u5c71 \u8fd0\u638c\u201d\u7684\u529f\u592b\uff0c\u6842\u4ef2\u660e\u53ea\u89c9\u4e00\u80a1\u5927\u529b\u5411\u80f8\u524d\u51fb\u6765\uff01 \u3000\u3000\u6842\u4ef2\u660e\u594b\u8d77\u795e\u5a01\uff0c\u51cc\u7a7a\u6251\u8d77\uff0c\u8fd0\u5927\u529b\u9e70\u722a\u529f\uff0c\u671d\u738b\u521a\u5288\u9762\u6293\u53bb\uff0c\u4e24\u4eba\u78b0\u4e2a\u6b63\u7740\u3002\u6842\u4ef2\u660e \u5927\u559d\u4e00\u58f0\uff0c\u5341\u6307\u5982\u94c1\u94a9\u4e00\u822c\uff0c\u6293\u7740\u4e86\u738b\u521a\u7684\u624b\u8155\u3002\u738b\u521a\u53cc\u638c\u4e00\u7ffb\uff0c\u7528\u91d1\u521a\u6563\u624b\u4e2d\u7684\u201c\u6454\u201d\u5b57 \u8bc0\uff0c\u638c\u80cc\u5411\u4e0a\u4e00\u6325\uff0c\u6842\u4ef2\u660e\u8eab\u5b50\u60ac\u7a7a\uff0c\u5728\u8fd0\u529b\u4e0a\u5148\u5403\u4e86\u4e8f\uff0c\u4ed6\u7b2c\u4e8c\u6b21\u4f7f\u51fa\u602a\u62db\uff0c\u7adf\u4ee5\u4e94\u79bd\u638c \u4e2d\u7edd\u9669\u7684\u8eab\u6cd5\uff0c\u60ac\u7a7a\u5411\u540e\u4e00\u4ef0\uff0c\u5de6\u811a\u4e00\u4e2a\u201c\u8e6c\u811a\u201d\u8e6c\u5230\u738b\u521a\u80f8\u524d\uff0c\u75be\u559d\u4e00\u58f0\u201c\u8d77\uff01\u201d\u738b\u521a\u7528 \u529b\u4e00\u6325\uff0c\u6842\u4ef2\u660e\u53cc\u624b\u4e00\u677e\uff0c\u4e00\u4e2a\u201c\u7ec6\u9600\u5de7\u7ffb\u4e91\u201d\uff0c\u5411\u540e\u5012\u7ffb\u51fa\u6570\u4e08\u4e4b\u5916\u3002\u5728\u6842\u4ef2\u660e\u4f7f\u51fa\u602a\u62db \u4e4b\u65f6\uff0c\u738b\u521a\u88ab\u8feb\u5f97\u77ee\u8eab\u8eb2\u907f\uff0c\u867d\u95ea\u8fc7\u80f8\u819b\uff0c\u5de6\u80ef\u8fd8\u662f\u7ed9\u7ed3\u7ed3\u5b9e\u5b9e\u8e22\u4e86\u4e00\u4e0b\uff0c\u540c\u6837\u5728\u5730\u4e0a\u6eda\u51fa \u6570\u4e08\u5f00\u5916\u3002 \u3000\u3000\u6842\u4ef2\u660e\u843d\u5730\u4e00\u770b\uff0c\u81ea\u5df1\u7ed9\u738b\u521a\u53cd\u638c\u4e00\u6454\uff0c\u6307\u5c16\u78b0\u7740\u7684\u5730\u65b9\uff0c\u5df2\u7ecf\u76ae\u7834\u8840\u6d41\uff1b\u738b\u521a\u7ad9\u8d77\u4e00 \u770b\uff0c\u624b\u8155\u4e0a\u4e5f\u5982\u540c\u7ed9\u706b\u7ef3\u70d9\u8fc7\u4e00\u6837\uff0c\u70d9\u8d77\u5341\u6761\u7ea2\u5370\u3002\u4e24\u4eba\u90fd\u6781\u4e3a\u9a87\u5f02\uff0c\u6599\u4e0d\u5230\u5bf9\u65b9\u529f\u529b\u5982\u6b64 \u6df1\u6e5b\uff01 \u3000\u3000\u4e24\u4eba\u867d\u5404\u5403\u4e86\u5bf9\u65b9\u7684\u4e8f\uff0c\u4f46\u5728\u65c1\u89c2\u7684\u4eba\u770b\u6765\uff0c\u6842\u4ef2\u660e\u662f\u4ee5\u7edd\u9876\u7684\u8f7b\u529f\u89e3\u5f00\u9669\u62db\uff0c\u800c\u738b\u521a \u5374\u8981\u6eda\u5730\u95ea\u8eb2\uff0c\u660e\u660e\u662f\u738b\u521a\u8f93\u4e86\u4e00\u62db\u3002\u5404\u8def\u9ad8\u624b\u90fd\u4e0d\u7981\u5567\u5567\u79f0\u5947\uff0c\u5148\u524d\u77a7\u4e0d\u8d77\u6842\u4ef2\u660e\u7684\uff0c\u800c \u4eca\u90fd\u522e\u76ee\u76f8\u770b\u3002 \u3000\u3000\u738b\u521a\u81ea\u6210\u540d\u4ee5\u6765\uff0c\u4ece\u672a\u78b0\u8fc7\u5982\u6b64\u52b2\u654c\uff0c\u7edd\u6599\u4e0d\u5230\u4f1a\u5728\u4e00\u4e2a\u201c\u540e\u751f\u5c0f\u5b50\u201d\u624b\u5e95\uff0c\u6298\u4e86\u9510 \u6c14\u3002\u4ed6\u8fd9\u65f6\u5df2\u4e0d\u6562\u6025\u4e8e\u6c42\u80dc\uff0c\u62b1\u5143\u5b88\u4e00\uff0c\u8c03\u597d\u5185\u529b\uff0c\u4ee5\u91d1\u521a\u6563\u624b\u7684\u5389\u5bb3\u62db\u6570\uff0c\u5e26\u653b\u5e26\u5b88\uff0c\u4e0e \u6842\u4ef2\u660e\u7684\u5927\u529b\u9e70\u722a\u5468\u65cb\uff01 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u5f62\u52bf\u987f\u65f6\u9006\u8f6c\u3002\u672c\u6765\u8bba\u529f\u529b\u4e24\u4eba\u90fd\u5dee\u4e0d\u591a\uff0c\u6842\u4ef2\u660e\u5929\u8d4b\u6781\u9ad8\uff0c\u738b\u521a\u5219\u706b\u5019\u8001 \u5230\u3002\u4f46\u738b\u521a\u6a2a\u884c\u6c5f\u6e56\u4e09\u5341\u4f59\u5e74\uff0c\u624b\u5e95\u4e0b\u4e0d\u77e5\u4f1a\u8fc7\u591a\u5c11\u82f1\u96c4\u597d\u6c49\uff0c\u7ecf\u9a8c\u4e4b\u4e30\uff0c\u8fdc\u975e\u6842\u4ef2\u660e\u53ef \u6bd4\u3002\u4e00\u201c\u7a33\u201d\u4e86\u4e0b\u6765\uff0c\u7acb\u523b\u4ee5\u907f\u5b9e\u51fb\u865a\u3002\u4e13\u62a2\u7a7a\u95e8\u6218\u6cd5\uff0c\u8feb\u5f97\u6842\u4ef2\u660e\u8f6c\u653b\u4e3a\u5b88\uff01\u4e24\u4eba\u90fd\u662f\u638c \u98ce\u864e\u864e\uff0c\u638c\u5230\u5373\u6536\uff0c\u4e0d\u6562\u628a\u62db\u6570\u7528\u8001\u3002\u5728\u9ad8\u624b\u770b\u6765\uff0c\u867d\u7136\u8eab\u4f53\u5e76\u672a\u63a5\u89e6\uff0c\u53ef\u662f\u5374\u6bd4\u521a\u624d\u7684\u9669 \u62db\uff0c\u8fd8\u8981\u4ee4\u4eba\u6035\u76ee\u60ca\u5fc3\u3002\u53ea\u89c1\u5730\u4e0a\u6c99\u77f3\u7eb7\u98de\uff0c\u638c\u8089\u6240\u5230\uff0c\u9644\u8fd1\u7684\u6811\u53f6\u90fd\u7c41\u7c41\u843d\u4e0b\u3002 \u3000\u3000\u6218\u5230\u5206\u9645\uff0c\u6842\u4ef2\u660e\u6e10\u611f\u5904\u5728\u4e0b\u98ce\uff0c\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u75be\u53d1\uff0c\u4e24\u4eba\u90fd\u7ed9\u5bf9\u65b9\u638c\u529b\u9707\u9000\u6570 \u6b65\u3002\u6842\u4ef2\u660e\u8d8b\u52bf\u4e00\u7f13\uff0c\u5f85\u738b\u521a\u518d\u6251\u6765\u65f6\uff0c\u638c\u6cd5\u7a81\u7136\u4e00\u53d8\uff0c\u638c\u98ce\u53d1\u51fa\u597d\u4f3c\u6ca1\u6709\u4ee5\u524d\u51cc\u5389\uff0c\u4f46\u6bcf \u62db\u6bcf\u5f0f\uff0c\u90fd\u662f\u542b\u52b2\u672a\u5410\uff0c\u738b\u521a\u5076\u5c14\u638c\u950b\u89e6\u53ca\uff0c\u53ea\u89c9\u5bf9\u65b9\u7684\u624b\u662f\u8f6f\u7ef5\u7ef5\u7684\uff0c\u7136\u800c\u5374\u53c8\u6709\u6709\u6781\u5927 \u7684\u6f5c\u529b\u5411\u81ea\u5df1\u53cd\u51fb\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u6025\u5fd9\u4fbf\u7528\u8db3\u7cbe\u795e\uff0c\u4ee5\u5e73\u751f\u7edd\u6280\u4e0e\u6842\u4ef2\u660e\u76f8\u6597\uff01 \u3000\u3000\u6842\u4ef2\u660e\u8fd9\u624b\u662f\u7ef5\u638c\u7684\u5185\u52b2\u914d\u4e0a\u9e70\u722a\u7684\u786c\u529f\u3002\u539f\u6765\u4ed6\u7684\u4e49\u7236\u6842\u5929\u6f9c\u9664\u7cbe\u4e8e\u672c\u95e8\u7684\u5927\u529b\u9e70\u722a \u529f\u5916\uff0c\u53c8\u4ee5\u4e8c\u5341\u5e74\u7684\u82e6\u529f\uff0c\u719f\u6089\u4e86\u5185\u5bb6\u7ef5\u638c\u3002\u5728\u6b66\u6797\u4e2d\u4e8c\u8005\u517c\u4fee\uff0c\u800c\u53c8\u5177\u6709\u4e0a\u4e58\u529f\u529b\u7684\uff0c\u53ea \u4ed6\u4e00\u5165\uff01 \u3000\u3000\u8fd9\u65f6\uff0c\u51cc\u672a\u98ce\u548c\u7533\u5bb6\u5144\u5f1f\uff0c\u4e5f\u6b63\u6253\u5f97\u706b\u70bd\u5f02\u5e38\u3002\u51cc\u672a\u98ce\u4e00\u5251\u5feb\u4f3c\u4e00\u5251\uff0c\u5c06\u7533\u5bb6\u5144\u5f1f\u8feb\u5f97 \u6ee1\u5934\u5927\u6c49\u3002\u4e8c\u7533\u65bd\u5c55\u51fa\u5e73\u751f\u6240\u5b66\uff0c\u6240\u4f7f\u7684\u5c3d\u662f\u5434\u94a9\u5251\u6cd5\u7684\u7cbe\u5999\u62db\u6570\uff0c\u914d\u5408\u5f97\u5929\u8863\u65e0\u7f1d\uff0c\u62db\u62db \u90fd\u662f\u6bd2\u7740\u3002\u4fbf\u9976\u662f\u5982\u6b64\uff0c\u5230\u5e95\u8fd8\u662f\u843d\u5728\u4e0b\u98ce\u3002\u51cc\u672a\u98ce\u7684\u5251\u6cd5\u662f\u6d77\u5185\u7b2c\u4e00\u540d\u624b\u6240\u6388\uff0c\u7cbe\u5fae\u8d85\u8d8a \u4e4b\u5904\uff0c\u5b9e\u51fa\u4e00\u822c\u4eba\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u51cc\u672a\u98ce\u4ee5\u5929\u5c71\u5251\u6cd5\uff0c\u6076\u6597\u7533\u5bb6\u5144\u5f1f\u53f7\u79f0\u6b66\u6797\u7edd\u5b66\u7684\u5434\u94a9\u5251\uff0c\u672c\u6765\u662f\u6b66\u6797\u4e2d\u65f7\u4e16\u96be\u9022\u7684\u6bd4 \u5251\uff0c\u4fbf\u81ea\u6842\u4ef2\u660e\u4e00\u51fa\uff0c\u7fa4\u8c6a\u53cd\u800c\u628a\u4ed6\u4eec\u51b7\u843d\u4e86\u3002\u5362\u5927\u695e\u5b50\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u6837\u7684\u6bd4\u5251\u771f\u662f\u4eba\u751f \u96be\u5f97\u513f\u56de\u770b\uff01\u53ea\u53ef\u60dc\u4eca\u65e5\u597d\u620f\u8fde\u53f0\uff0c\u90a3\u8fb9\u7684\u6bd4\u638c\uff0c\u66f4\u662f\u6b66\u6797\u7684\u5947\u8ff9\uff0c\u771f\u6068\u4e0d\u80fd\u591a\u751f\u4e00\u5bf9\u773c \u775b\uff01\u201d \u3000\u3000\u6b63\u5f53\u5404\u8def\u9ad8\u624b\u5c4f\u795e\u9759\u6c14\uff0c\u6ce8\u76ee\u6842\u3001\u738b\u6076\u6218\u4e4b\u9645\uff0c\u51cc\u672a\u98ce\u548c\u7533\u5bb6\u5144\u5f1f\uff0c\u5df2\u5230\u4e86\u5f3a\u5b58\u5f31\u4ea1\uff0c \u751f\u6b7b\u7acb\u5224\u7684\u5730\u6b65\u3002\u7533\u5929\u8c79\u6b63\u4f7f\u5230\u4e00\u62db\u201c\u6a2a\u6c5f\u622a\u6597\u201d\u62e6\u8170\u4e00\u65a9\uff0c\u60f3\u963b\u6b62\u51cc\u672a\u98ce\u8fde\u7ef5\u4e0d\u65ad\u7684\u653b \u52bf\uff0c\u90a3\u6599\u51cc\u672a\u98ce\u201c\u563f\u563f\u201d\u4e24\u58f0\uff0c\u8eab\u968f\u5251\u8d70\uff0c\u8fc5\u903e\u72c2\u98d8\uff0c\u53f3\u624b\u5251\u4e00\u7ffb\uff0c\u9752\u5149\u95ea\u5904\uff0c\u5df2\u5411\u7533\u5929\u8c79 \u9888\u9879\u52d2\u4e0b\u3002\u7533\u5929\u864e\u53cc\u94a9\u5728\u51cc\u672a\u98ce\u80cc\u540e\u75be\u4e0a\uff0c\u51cc\u672a\u98ce\u8eab\u5b50\u4e00\u62e7\u4e00\u65cb\uff0c\u7533\u5929\u864e\u53cc\u94a9\u6251\u7a7a\uff0c\u672a\u53ca\u53d8 \u62db\uff0c\u5df2\u7ed9\u51cc\u672a\u98ce\u4e00\u62ff\u51fb\u4e2d\u524d\u80f8\uff0c\u4e0e\u6b64\u540c\u65f6\uff0c\u7533\u5929\u8c79\u4ea6\u7ed9\u51cc\u672a\u98ce\u7684\u9752\u94a2\u5251\u81ea\u540e\u5fc3\u76f4\u900f\u524d\u5fc3\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5728\u8863\u895f\u4e0a\u63e9\u6389\u5251\u950b\u7684\u8840\u8ff9\uff0c\u65cb\u9996\u56db\u987e\uff0c\u5f39\u5251\u957f\u5578\uff0c\u5c71\u9e23\u8c37\u4e8c\u5e94\uff0c\u56de\u58f0\u60a0\u60a0\u3002\u7fa4\u8c6a \u76f8\u987e\u9a87\u7136\uff0c\u738b\u521a\u66f4\u662f\u5927\u60ca\u5931\u8272\u3002 \u3000\u3000\u8fd9\u65f6\u6842\u4ef2\u660e\u6108\u6597\u6108\u52c7\uff0c\u7ef5\u638c\u4e0e\u9e70\u722a\u8fde\u73af\u8fd0\u7528\uff0c\u53cc\u638c\u8d77\u5904\uff0c\u5168\u5e26\u52b2\u98ce\uff01\u738b\u521a\u5df2\u662f\u65e0\u5fc3\u604b \u6218\uff0c\u5ffd\u4f7f\u72e1\u8ba1\uff0c\u865a\u6643\u4e00\u62db\u3002\u6842\u4ef2\u660e\u638c\u5982\u5200\u524a\uff0c\u76f4\u5288\u4e0b\u53bb\uff0c\u5feb\u82e5\u6d41\u661f\u3002\u738b\u521a\u503e\u7136\u5de6\u80a9\u5411\u524d\u4e00 \u649e\uff0c\u201c\u7bf7\u201d\u7684\u4e00\u58f0\uff0c\u5403\u4e86\u6842\u4ef2\u660e\u4e00\u638c\uff01\u4ed6\u4e5f\u4e58\u52bf\u5411\u524d\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u64d2\u7740\u4e86\u6842\u4ef2\u660e\u53f3\u5a49\uff0c\u7528\u624b \u4fbf\u626d\u3002\u738b\u521a\u7adf\u662f\u62fc\u7740\u80a9\u53d7\u638c\u4f24\uff0c\u4f01\u56fe\u8d25\u4e2d\u53d6\u80dc\uff0c\u65bd\u5c55\u91d1\u521a\u624b\u4e2d\u6700\u5389\u5bb3\u7684\u64d2\u62ff\u624b\u6cd5\uff0c\u60f3\u628a\u6842\u4ef2 \u660e\u6d3b\u64d2\uff0c\u631f\u4f5c\u4eba\u8d28\u3002\u4ed6\u89c1\u7533\u5bb6\u5144\u5f1f\u4e24\u4eba\u5408\u653b\uff0c\u8fd8\u662f\u4e27\u5728\u51cc\u672a\u98ce\u5251\u4e0b\uff0c\u81ea\u77e5\u4e0d\u662f\u654c\u624b\uff0c\u56e0\u6b64\u60f3 \u62ff\u7740\u6842\u4ef2\u660e\u6765\u8981\u631f\u51cc\u672a\u98ce\u3002 \u3000\u3000\u54ea\u77e5\u6842\u4ef2\u660e\u867d\u56e0\u7ecf\u9a8c\u5c1a\u6d45\uff0c\u4e2d\u4e86\u654c\u4eba\u8bf1\u654c\u4e4b\u8ba1\uff0c\u4f46\u5230\u5e95\u529f\u529b\u6df1\u539a\uff0c\u4e34\u5371\u4e0d\u4e71\uff0c\u53f3\u81c2\u4e00 \u632f\uff0c\u786c\u5982\u94c1\u68d2\uff0c\u867d\u7136\u6323\u4e0d\u8131\u624b\uff0c\u738b\u521a\u4e5f\u626d\u4ed6\u4e0d\u52a8\uff0c\u4ed6\u5de6\u624b\u4e5f\u4e0d\u95f2\u7740\uff0c\u4e00\u4e2a\u51b2\u62f3\uff0c\u53c8\u662f\u201c\u7830\u201d \u7684\u4e00\u58f0\uff0c\u51fb\u4e2d\u4e86\u738b\u521a\u4e0b\u5df4\uff0c\u738b\u521a\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u6ee1\u53e3\u9c9c\u8840\uff0c\u76f4\u55b7\u51fa\u6765\uff0c\u4e24\u6392\u95e8\u7259\uff0c\u5168\u88ab\u9707 \u788e\uff0c\u75db\u5f7b\u5fc3\u80ba\uff0c\u53f3\u624b\u4e0d\u80fd\u4e0d\u677e\u5f00\u6765\uff0c\u5411\u540e\u5012\u7ffb\u51fa\u53bb\uff01 \u3000\u3000\u5192\u6d63\u83b2\u56e0\u5173\u5fc3\u8fc7\u751a\uff0c\u4e00\u6b65\u4e00\u6b65\uff0c\u79fb\u8fd1\u6597\u573a\uff0c\u5f53\u6842\u4ef2\u660e\u9047\u9669\u4e4b\u9645\uff0c\u5979\u7adf\u7136\u4e0d\u987e\u4e00\u5207\uff0c\u98de\u7eb5 \u4e0a\u6765\uff0c\u738b\u521a\u4e00\u4e2a\u5012\u7ffb\uff0c\u7ad9\u8d77\u6765\u65f6\uff0c\u6070\u4e0e\u5192\u6d63\u83b2\u5288\u9762\u76f8\u9022\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u53f3\u624b\u4e00\u6293\u6293\u53bb\uff0c\u5192\u6d63\u83b2 \u8fce\u9762\u5c31\u662f\u4e00\u628a\u593a\u547d\u795e\u7802\uff0c\u738b\u521a\u6beb\u4e0d\u8eb2\u907f\uff0c\u7c92\u7c92\u90fd\u5d4c\u5165\u76ae\u8089\u4e4b\u5185\uff0c\u4ed6\u51b2\u7740\u795e\u7802\uff0c\u4ecd\u662f\u98de\u8eab\u6251 \u53bb\uff0c\u4e00\u6293\u6293\u4e0b\uff0c\u5c06\u5192\u6d63\u83b2\u6574\u4e2a\u8eab\u8eaf\uff0c\u5f53\u6210\u5175\u5668\uff0c\u62a1\u4e86\u8d77\u6765\uff0c\u56db\u9762\u4e00\u8361\uff0c\u6842\u4ef2\u660e\u624b\u6263\u91d1\u73af\uff0c\u6b63 \u60f3\u53d1\u5c04\uff0c\u6295\u9f20\u5fcc\u5668\uff0c\u8feb\u5f97\u53c8\u653e\u4e86\u4e0b\u6765\uff0c\u98de\u8eab\u8ffd\u53bb\uff0c\u5728\u738b\u521a\u80cc\u540e\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u628a\u5979\u653e\u4e0b\uff0c \u6211\u9976\u4f60\u4e00\u6b7b\uff01\u201d \u3000\u3000\u738b\u521a\u8fde\u8fde\u72de\u7b11\uff0c\u53d1\u529b\u72c2\u5954\uff0c\u6842\u4ef2\u660e\u5728\u5ca9\u8fb9\u987a\u624b\u62d4\u8d77\u53cc\u5251\uff0c\u65cb\u98ce\u98de\u6251\uff0c\u51cc\u672a\u98ce\u633a\u8eab\u8ffd\u4e0a\uff0c \u5404\u8def\u9ad8\u624b\uff0c\u4e5f\u4e0d\u81ea\u89c9\u5730\u8ddf\u4e0a\u6765\uff0c\u4f46\u770b\u7740\u738b\u521a\u51f6\u72e0\u7684\u795e\u60c5\uff0c\u6ca1\u4e00\u4e2a\u4eba\u6562\u4e8e\u51fa\u624b\u3002 \u3000\u3000\u77ac\u606f\u4e4b\u95f4\uff0c\u5df2\u8ffd\u51fa\u4e24\u4e2a\u5c71\u5773\uff0c\u524d\u9762\u8c41\u7136\u5f00\u6717\u3002\u8fd9\u65f6\u671d\u9633\u666e\u7167\uff0c\u4f17\u4eba\u731b\u542c\u5f97\u6c34\u58f0\u54cd\u82e5\u90c1 \u96f7\uff0c\u5c71\u9876\u4e00\u6761\u7011\u5e03\uff0c\u5982\u767d\u7ec3\u822c\u76f4\u51b2\u800c\u4e0b\uff0c\u5728\u8c37\u5e95\u6c47\u6210\u4e00\u4e2a\u6c34\u6f6d\uff0c\u6c34\u6f6d\u8fb9\u6709\u4e00\u4e2a\u5c71\u6d1e\uff0c\u7011\u5e03\u7ed9 \u5468\u56f4\u5ca9\u77f3\uff0c\u6fc0\u8d77\u4e00\u5927\u7247\u6c34\u82b1\uff0c\u5c71\u6d1e\u4e4b\u524d\uff0c\u5c31\u4f3c\u6302\u4e86\u4e00\u5e45\u6c34\u5e18\uff0c\u671d\u9633\u8f89\u6620\uff0c\u5e7b\u6210\u4e03\u8272\u7684\u5f69\u5e26\uff0c \u5947\u4e3d\u65e0\u6bd4\uff01\u4f46\u4f17\u4eba\u8c01\u4e5f\u65e0\u5fc3\u8d4f\u73a9\u98ce\u666f\uff0c\u5927\u5bb6\u90fd\u4e0d\u53d1\u4e00\u8a00\uff0c\u53ea\u987e\u524d\u8ffd\u3002 \u3000\u3000\u51cc\u672a\u98ce\u8eab\u6cd5\u75be\u8fc5\u4e4b\u6781\uff0c\u65e9\u5df2\u8d8a\u8fc7\u7fa4\u8c6a\uff0c\u8fd9\u65f6\u5df2\u8ffd\u4e0a\u4e86\u6842\u4ef2\u660e\uff0c\u4e0e\u738b\u521a\u76f8\u8ddd\u4e0d\u8fdc\u3002\u4ed6\u62cd\u4e00 \u62cd\u6842\u4ef2\u660e\u80a9\u8180\uff0c\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4e14\u95ea\u5f00\uff0c\u5f85\u6211\u6551\u5979\uff01\u201d\u6842\u4ef2\u660e\u5982\u8a00\u5f80\u65c1\u4e00\u95ea\uff0c\u53ea\u89c1\u51cc\u672a\u98ce\u53f3 \u624b\u4e00\u626c\uff0c\u4e09\u679d\u5929\u5c71\u795e\u8292\uff0c\u7535\u5c04\u800c\u51fa\u3002\u6842\u4ef2\u660e\u5927\u9a87\u53eb\u9053\uff1a\u201c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u8981\u60f3\u963b\u6b62\u5df2\u662f\u4e0d\u53ca\uff01 \u3000\u3000\u738b\u521a\u81ea\u4ee5\u4e3a\u631f\u7740\u5192\u6d63\u83b2\u63a9\u62a4\uff0c\u4e07\u65e0\u4e00\u5931\uff0c\u90a3\u6599\u51cc\u672a\u98ce\u7684\u6697\u5668\u624b\u6cd5\uff0c\u795e\u5999\u5f02\u5e38\uff0c\u4e09\u679d\u5929\u5c71 \u795e\u8292\u5168\u662f\u865a\u53d1\uff0c\u738b\u521a\u821e\u8d77\u5192\u6d63\u83b2\u4f5c\u4e3a\u76fe\u813e\uff0c\u4e00\u6321\u4e0d\u4e2d\uff0c\u7f13\u5f97\u4e00\u7f13\uff0c\u7b2c\u56db\u679d\u795e\u8292\u53c8\u5982\u6d41\u661f\u8d76\u6708 \u822c\u5c04\u6765\uff0c\u738b\u521a\u6b63\u5f85\u62a1\u8d77\u5192\u6d63\u83b2\u518d\u6321\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u53f3\u81c2\u5df2\u7ed9\u795e\u8292\u7a7f\u8fc7\uff0c\u767b\u65f6\u5947\u75db\u5f7b\u9aa8\uff0c\u624b\u638c\u4e00 \u677e\uff0c\u5c06\u5192\u6d63\u83b2\u8dcc\u5728\u5730\u4e0a\u3002\u738b\u521a\u8033\u8fb9\u542c\u5f97\u51cc\u672a\u98ce\u53f1\u54a4\u4e4b\u58f0\uff0c\u54ea\u91cc\u8fd8\u987e\u5f97\u518d\u4f24\u5bb3\u5192\u6d63\u83b2\uff0c\u6025\u5f97\u5411 \u524d\u4e00\u63a0\u6570\u4e08\uff0c\u62fc\u547d\u72c2\u5954\uff01 \u3000\u3000\u51cc\u672a\u98ce\u4e00\u8dc3\u9762\u524d\uff0c\u5c06\u5192\u6d63\u83b2\u8f7b\u8f7b\u6276\u8d77\uff0c\u4f38\u624b\u4e00\u62cd\uff0c\u89e3\u5f00\u4e86\u5979\u7684\u7a74\u9053\uff0c\u5fae\u7b11\u7740\u5bf9\u8ffd\u4e0a\u6765\u7684 \u6842\u4ef2\u660e\u9053\uff1a\u201c\u4ea4\u56de\u7ed9\u4f60\uff0c\u5979\u6beb\u53d1\u672a\u4f24\uff0c\u4f60\u53ef\u653e\u5fc3\u4e86\u5427\uff01\u201d \u3000\u3000\u738b\u521a\u53d1\u52b2\u72c2\u5954\uff0c\u9664\u4e86\u53f3\u81c2\u5947\u75db\u4e4b\u5916\uff0c\u731b\u7136\u95f4\u53c8\u89c9\u5168\u8eab\u9ebb\u75d2\uff0c\u795e\u5fd7\u6e10\u6e10\u8ff7\u7cca\u3002\u8fd9\u4e00\u60ca\u975e\u540c \u5c0f\u53ef\uff0c\u6025\u6025\u632f\u6444\u5fc3\u795e\uff0c\u8fd9\u624d\u60f3\u8d77\uff0c\u521a\u624d\u6240\u4e2d\u90a3\u628a\u7802\u5b50\uff0c\u7adf\u7136\u90fd\u662f\u5582\u6bd2\u7684\u201c\u6697\u9752\u5b50\u201d\uff08\u6697 \u5668\uff09\uff0c\u5413\u5f97\u7075\u9b42\u51fa\u7a8d\uff0c\u800c\u540e\u9762\u51cc\u672a\u98ce\u7d27\u7d27\u8ffd\u6765\uff01\u4ed6\u51b7\u6c57\u76f4\u6d41\uff0c\u4eba\u4e5f\u9677\u5165\u72c2\u4e71\u7684\u72b6\u6001\u4e4b\u4e2d\uff0c\u6025 \u4e0d\u62e9\u8def\uff0c\u7adf\u7136\u4e00\u8dc3\u6570\u4e08\uff0c\u8df3\u8fc7\u7011\u5e03\u6c47\u6210\u7684\u6c34\u6f6d\uff0c\u51cc\u672a\u98ce\u5927\u559d\u4e00\u58f0\uff0c\u53c8\u662f\u4e00\u679d\u5929\u5c71\u795e\u8292\uff0c\u81ea\u540e \u5c04\u6765\uff0c\u738b\u521a\u907f\u65e0\u53ef\u907f\uff0c\u8feb\u5f97\u5411\u524d\u731b\u529b\u4e00\u51b2\uff0c\u8d8a\u8fc7\u4e86\u5c71\u6d1e\u7684\u6c34\u5e18\uff0c\u5168\u8eab\u529b\u91cf\uff0c\u90fd\u96c6\u4e2d\u5728\u5de6\u81c2\u4e4b \u4e0a\uff0c\u5288\u556a\u4e00\u58f0\uff0c\u201c\u5355\u638c\u5f00\u7891\u201d\u4e00\u638c\u51fb\u5728\u5c71\u6d1e\u7684\u77f3\u5934\u4e0a\u3002\u738b\u521a\u7684\u91d1\u521a\u624b\u6709\u51e0\u5341\u5e74\u529f\u529b\uff0c\u62fc\u6b7b\u4e00 \u51fb\uff0c\u529b\u91cf\u7aef\u7684\u60ca\u4eba\uff0c\u53ea\u89c1\u624b\u638c\u5288\u4e0b\uff0c\u788e\u77f3\u98de\u626c\uff0c\u8f70\u9686\u4e00\u58f0\uff0c\u77f3\u95e8\u8f67\u8f67\u7684\u5f00\u4e86\u534a\u6247\uff0c\u91cc\u9762\u539f\u6765 \u662f\u7528\u5343\u65a4\u77f3\u6761\u5f53\u95e8\u6805\u4e00\u6837\u62e6\u4f4f\uff0c\u73b0\u5728\u7ed9\u738b\u521a\u638c\u529b\u9707\u65ad\uff0c\u77f3\u95e8\u4e5f\u5c31\u5f00\u4e86\u3002\u800c\u738b\u521a\u7684\u638c\u529b\u7528\u5f97\u8fc7 \u731b\uff0c\u4e5f\u7ed9\u77f3\u95e8\u53cd\u5f39\u51fa\u6765\uff0c\u624b\u8155\u6253\u65ad\uff0c\u7ed9\u7011\u5e03\u4e00\u51b2\uff0c\u8dcc\u5165\u65e0\u5e95\u6df1\u6f6d\uff0c\u6323\u624e\u51e0\u4e0b\uff0c\u7247\u523b\u6ca1\u9876\u3002\u5230 \u51cc\u672a\u98ce\u4e0e\u5404\u8def\u9ad8\u624b\u8d76\u5230\u6f6d\u8fb9\u4e4b\u65f6\uff0c\u53ea\u89c1\u6c34\u6f6d\u4e0a\u51e0\u5708\u6ce2\u7eb9\uff0c\u56db\u5916\u8361\u5f00\u3002\u8fd9\u4e2a\u6b66\u6797\u53db\u9006\uff0c\u5916\u5bb6\u9ad8 \u624b\uff0c\u5df2\u968f\u6d6a\u82b1\u6d88\u901d\u3002 \u3000\u3000\u5404\u8def\u9ad8\u624b\uff0c\u4f2b\u7acb\u6f6d\u524d\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002\u4ed6\u4eec\u76ee\u7779\u8fd9\u4e00\u573a\u60ca\u5fc3\u52a8\u9b44\u7684\u6076\u6218\uff0c\u53c8\u76ee\u7779\u738b\u521a\u60e8\u6b7b\uff0c \u5c38\u9aa8\u65e0\u5b58\uff0c\u76ee\u77aa\u53e3\u5446\uff0c\u5404\u6709\u611f\u89e6\u3002\u826f\u4e45\uff0c\u5362\u5927\u695e\u5b50\u5410\u53e3\u6c14\u9053\uff1a\u201c\u6d3b\u8be5\uff01\u6d3b\u8be5\uff01\u8fd9\u8d3c\u5b50\u65f1\u8be5\u6709 \u4eba\u6536\u62fe\u4ed6\u4e86\uff01\u201d\u8fbe\u571f\u53f8\u5411\u51cc\u672a\u98ce\u77a7\u4e86\u4e24\u773c\uff0c\u6697\u6697\u60f3\u9053\uff1a\u201c\u6211\u867d\u672a\u4e0e\u738b\u521a\u6bd4\u8bd5\u8fc7\uff0c\u4f46\u770b\u4ed6\u91d1\u521a \u638c\u529b\uff0c\u5916\u529f\u4e4b\u5f3a\uff0c\u4f3c\u4e0d\u5728\u6211\u94c1\u5e03\u886b\u7684\u6a2a\u7ec3\u529f\u592b\u4e4b\u4e0b\uff0c\u800c\u4eca\u7adf\u7ed9\u51cc\u672a\u98ce\u51e0\u679d\u6697\u5668\u8feb\u6b7b\uff0c\u770b\u6765\u8fd9 \u4e2a\u5929\u5c71\u795e\u8292\uff0c\u771f\u662f\u540d\u4e0d\u865a\u4f20\u3002\u201d\u7f57\u8fbe\u5374\u5706\u788c\u788c\u5730\u7741\u5927\u773c\u775b\uff0c\u770b\u7740\u77f3\u6d1e\u51fa\u795e\u3002 \u3000\u3000\u5192\u6d63\u83b2\u8fd9\u65f6\u5df2\u968f\u9ec4\u886b\u5c11\u5e74\u7f13\u7f13\u884c\u6765\uff0c\u770b\u6c34\u5e18\u5982\u5f69\u5e26\u4e00\u822c\uff0c\u6620\u65e5\u751f\u8f89\uff0c\u800c\u5e95\u4e0b\u6f6d\u5f71\u60a0\u60a0\uff0c \u6ce2\u5149\u80dc\u96ea\uff0c\u731b\u7136\u60f3\u8d77\u4e00\u5e45\u56fe\u753b\uff0c\u8df3\u5c06\u8d77\u6765\u3002 \u3000\u3000\u6842\u4ef2\u660e\u5fc3\u5ff5\u4e00\u52a8\uff0c\u62c9\u7740\u5192\u6d63\u83b2\u9053\uff1a\u201c\u8fd9\u4e0d\u5c31\u662f\u6211\u4e49\u7236\u5728\u9ec4\u886b\u4e0a\u7559\u4e0b\u7684\u9690\u5f62\u56fe\u753b\uff1f\u201d\u5192\u6d63 \u83b2\u4f4e\u58f0\u8bf4\uff1a\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u6c34\u5e18\u6d1e\u5c31\u662f\u56fe\u753b\u4e2d\u7684\u6240\u5728\u3002\u201d\u8bf4\u7f62\u62db\u624b\u53eb\u51cc\u672a\u98ce\u8fc7\u6765\uff0c\u51cc\u672a\u98ce \u89c1\u4ed6\u4eec\u5581\u5581\u7ec6\u8bed\uff0c\u8f7b\u8f7b\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u505a\u725b\u76ae\u706f\u7b3c\u3002\u201d\u5192\u6d63\u83b2\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c \u6211\u8bf4\u7684\u662f\u6b63\u7ecf\u4e8b\u3002\u201d \u3000\u3000\u6628\u665a\u711a\u5316\u9ec4\u886b\uff0c\u73b0\u51fa\u56fe\u753b\u4e4b\u4e8b\uff0c\u51cc\u672a\u98ce\u5e76\u4e0d\u77e5\u9053\u3002\u90a3\u65f6\u4ed6\u6b63\u5728\u77f3\u5c4b\u5916\u4ed7\u5251\u5de1\u89c6\uff0c\u73b0\u5728\u542c \u5192\u6d63\u83b2\u7ec6\u8bf4\u4e00\u904d\uff0c\u95ed\u76ee\u51dd\u601d\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u5f00\u53e3\u8bf4\u9053\uff1a\u201c\u6842\u8001\u524d\u8f88\u7559\u4e0b\u9690\u5f62\u56fe\u753b\uff0c\u8fde\u77f3\u5927\u5a18\u4e5f \u4e0d\u7ed9\u77e5\u9053\uff0c\u5176\u4e2d\u5fc5\u5b9a\u6709\u6781\u91cd\u8981\u7684\u7269\u4e8b\uff0c\u6211\u4eec\u4f55\u4e0d\u8fdb\u53bb\u63a2\u63a2\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4e14\u6162\uff0c\u753b\u4e0a\u7684 \u2018\u5de6\u4e09\u53f3\u56db\u4e2d\u5341\u4e8c\u2019\u4e03\u4e2a\u5927\u5b57\uff0c\u5374\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u4f60\u66ff\u6211\u7aef\u8be6\u4e00\u4e0b\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u4ec0 \u4e48\u6697\u53f7\uff0c\u4e5f\u8bb8\u5c31\u662f\u6307\u6240\u853d\u7269\u4ef6\u7684\u4ef6\u6570\u548c\u4f4d\u7f6e\u3002\u201d \u3000\u3000\u8fd9\u65f6\u7fa4\u8c6a\u90fd\u5728\u9694\u6d1e\u6ce8\u89c6\uff0c\u89c1\u4ed6\u4eec\u4e09\u4eba\u7a83\u7a83\u79c1\u8bed\uff0c\u4e92\u76f8\u4ea4\u6362\u773c\u8272\uff0c\u7709\u5c71\u5be8\u4e3b\u7f57\u8fbe\u5c24\u5176\u663e\u5f97 \u5fc3\u7126\uff0c\u5ffd\u8d77\u5ffd\u5750\uff0c\u4e00\u4f1a\u513f\u770b\u770b\u6c34\u5e18\u6d1e\uff0c\u4e00\u4f1a\u513f\u770b\u770b\u51cc\u672a\u98ce\u3002 \u3000\u3000\u6b63\u5728\u4f17\u4eba\u5c4f\u795e\u6ce8\u89c6\uff0c\u5404\u6709\u6240\u601d\u4e4b\u9645\uff0c\u5ffd\u5730\u91cc\u5e7d\u8c37\u4e0a\u7a7a\u201c\u545c\u201d\u7684\u4e00\u58f0\uff0c\u63a0\u8fc7\u4e00\u679d\u54cd\u7bad\uff0c\u63a5 \u7740\u53c8\u662f\u4e24\u679d\uff0c\u7f57\u8fbe\u731b\u5730\u7ad9\u8d77\u8eab\u6765\uff0c\u64ae\u5507\u602a\u5578\u3002\u51cc\u672a\u98ce\u6b63\u89c9\u8be7\u5f02\uff0c\u534a\u76cf\u8336\u540e\uff0c\u8c37\u4e2d\u5df2\u73b0\u51fa\u4e00\u4e2a \u9a7c\u80cc\u8001\u4eba\uff0c\u4ed6\u76f8\u8c8c\u867d\u7136\u4e11\u964b\uff0c\u8eab\u6cd5\u5374\u5229\u843d\u4e4b\u6781\uff0c\u98de\u8dd1\u5954\u9a70\uff0c\u811a\u4e0b\u7adf\u662f\u7247\u5c18\u4e0d\u8d77\u3002\u970e\u5ffd\u5c31\u5230\u4e86 \u7fa4\u8c6a\u4e4b\u524d\u3002\u7f57\u8fbe\u5927\u559c\u8fc7\u671b\uff0c\u8fce\u4e0a\u53bb\u53eb\u9053\uff1a\u201c\u97e9\u5927\u54e5\uff0c\u7b49\u6b7b\u6211\u4eec\u4e86\u3002\u201d\u5362\u5927\u695e\u5b50\u548c\u8fbe\u571f\u53f8\u4e5f\u8d77 \u6765\u62db\u547c\uff0c\u9676\u5b8f\u3001\u5f20\u5143\u632f\u867d\u4e0d\u8ba4\u8bc6\u6b64\u4eba\uff0c\u89c1\u7f57\u8fbe\u7b49\u4eba\u8fd9\u6837\u5c0a\u656c\uff0c\u4e5f\u968f\u7740\u51fa\u6765\u8fce\u63a5\u3002\u51cc\u672a\u98ce\u3001\u6842 \u4ef2\u660e\u548c\u5192\u6d63\u83b2\u5374\u4ecd\u662f\u7aef\u5750\u6f6d\u8fb9\u3002\u4e0d\u52a8\u58f0\u8272\uff0c\u7ec6\u5bdf\u8fd9\u51e0\u4e2a\u7eff\u6797\u8c6a\u96c4\u548c\u9a7c\u80cc\u8001\u4eba\u7684\u6765\u610f\u3002 \u3000\u3000\u88ab\u79f0\u505a\u97e9\u5927\u54e5\u7684\u9a7c\u80cc\u8001\u4eba\uff0c\u987e\u4e0d\u53ca\u8bf7\u95ee\u51cc\u672a\u98ce\u7684\u59d3\u540d\uff0c\u4e00\u89c1\u6c34\u5e18\u98d8\u52a8\uff0c\u5c71\u6d1e\u95e8\u5f00\uff0c\u9762\u8272 \u7d27\u5f20\uff0c\u62cd\u62cd\u7f57\u8fbe\u80a9\u5934\u8bf4\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u5c31\u662f\u8fd9\u4e2a\u5730\u65b9\u4e86\uff01\u6709\u4eba\u8fdb\u53bb\u8fc7\u5417\uff1f\u201d\u7f57\u8fbe\u6447\u4e86\u6447\u5934\u3002\u8fbe \u571f\u53f8\u9053\uff1a\u201c\u6211\u4eec\u4e00\u9f50\u8fdb\u53bb\uff0c\u4e00\u74e2\u6c34\u5206\u516d\u7897\u7aef\uff0c\u5927\u5bb6\u559d\u5566\uff01\u201d\u5362\u5927\u695e\u5b50\u6307\u6307\u51cc\u672a\u98ce\u4ed6\u4eec\u9053\uff1a \u201c\u90a3\u8fb9\u8fd8\u8981\u5206\u4e09\u7897\u5462\uff01\u8fbe\u571f\u53f8\u4f4e\u58f0\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u77e5\u9053\uff0c\u6ca1\u4ed6\u4eec\u7684\u4efd\uff01\u201d\u51cc\u672a\u98ce\u8033\u6735\u6781\u7075\uff0c\u8fdc \u8fdc\u542c\u5f97\u4ed6\u4eec\u53c8\u6253\u7eff\u6797\u9ed1\u8bdd\uff0c\u8bf4\u4ec0\u4e48\u5206\u6c34\u559d\uff0c\u5fc3\u60f3\uff1a\u96be\u9053\u8fd9\u5c71\u6d1e\u91cc\u7adf\u85cf\u6709\u4ec0\u4e48\u5947\u73cd\u91cd\u5b9d\uff0c\u4ee5\u81f4 \u60ca\u52a8\u8fd9\u4e9b\u9b54\u5934\uff0c\u7fa4\u96c6\u6b64\u5730\uff0c\u5408\u8bae\u5206\u8d43\uff1f \u3000\u3000\u8fbe\u571f\u53f8\u3001\u7f57\u8fbe\u7b49\u6b63\u60f3\u9080\u9676\u3001\u5f20\u4e8c\u4eba\u8fdb\u53bb\uff0c\u9a7c\u80cc\u8001\u4eba\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u4e14\u6162\uff0c\u5148\u8ba9\u4e00\u4e2a\u4eba\u8fdb\u53bb \u770b\u770b\u3002\u8c01\u80af\u53bb\u7684\uff0c\u6211\u4eec\u8ba9\u4ed6\u591a\u559d\u4e00\u7897\uff01\u201d\u7f57\u8fbe\u4e00\u8dc3\u800c\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53bb\uff01\u201d\u632f\u81c2\u4e00\u7eb5\uff0c\u8df3\u8fc7 \u516d\u4e03\u4e08\u5bbd\u7684\u6c34\u6f6d\uff0c\u5192\u7740\u7011\u5e03\u51b2\u51fb\u7684\u6c34\u82b1\uff0c\u7a7f\u8fc7\u6c34\u5e18\uff0c\u5411\u5c71\u6d1e\u91cc\u7a9c\u53bb\u3002\u7fa4\u8c6a\u51dd\u795e\u76f8\u5f85\uff0c\u51cc\u672a\u98ce \u7b49\u4e09\u4eba\uff0c\u4e5f\u7ad9\u4e86\u8d77\u6765\u89c2\u671b\u3002\u8fd9\u6c14\u6c1b\u5c31\u4f3c\u4e07\u6728\u65e0\u58f0\uff0c\u5bc6\u4e91\u5f85\u96e8\uff0c\u7d27\u5f25\u4e4b\u6781\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u5ffd\u542c\u5f97 \u5c71\u6d1e\u91cc\u4e00\u58f0\u5389\u53eb\uff0c\u4f17\u4eba\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c1\u7f57\u8fbe\u62ab\u5934\u6563\u53d1\uff0c\u6d74\u8840\u5954\u51fa\uff0c\u5c71\u6d1e\u5185\u8fd8\u6709\u5f13\u7bad\u55d6\u55d6\u5c04 \u51fa\uff0c\u7adf\u4f3c\u9690\u9690\u4f0f\u6709\u7532\u5175\u3002\u7f57\u8fbe\u8eab\u624b\u4e5f\u4e0d\u51e1\uff0c\u53d7\u4e86\u7bad\u4f24\uff0c\u4ecd\u7136\u51b2\u5230\u6f6d\u8fb9\uff0c\u5355\u8db3\u70b9\u4ed6\uff0c\u65bd\u5c55\u201c\u4e00 \u9e64\u51b2\u5929\u201d\u7684\u8f7b\u529f\uff0c\u4fbf\u5f85\u98de\u8d8a\u6c34\u6f6d\u3002\u4f46\u6f6d\u9762\u5bbd\u8fbe\u4e03\u4e08\u6709\u591a\uff0c\u4ed6\u53d7\u4f24\u4e4b\u540e\uff0c\u529f\u529b\u5df2\u51cf\uff0c\u5230\u4e86\u534a \u7a7a\uff0c\u7a81\u7136\u8eab\u5b50\u4e00\u5815\uff0c\u98de\u5760\u6f6d\u5fc3\u3002\u5362\u5927\u695e\u5b50\u5927\u53eb\u4e00\u58f0\uff0c\u8eab\u5b50\u4e00\u5f13\uff0c\u7bad\u4e00\u822c\u7684\u76f4\u5c04\u51fa\u53bb\uff0c\u63a0\u5230\u6c34 \u6f6d\u4e2d\u592e\uff0c\u6b63\u597d\u8d76\u4e0a\uff0c\u5355\u638c\u4e00\u6258\uff0c\u7adf\u7136\u5c06\u7f57\u8fbe\u7684\u8eab\u5b50\u6258\u7740\uff0c\u540c\u767b\u5f7c\u5cb8\u3002\u4f17\u4eba\u8f70\u7136\u53eb\u597d\u3002\u51cc\u672a\u98ce \u89c1\u4e86\uff0c\u4e5f\u6697\u6697\u79f0\u8d5e\u5362\u5927\u695e\u5b50\u7684\u8f7b\u529f\uff0c\u5df2\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\u3002 \u3000\u3000\u5362\u5927\u695e\u5b50\u548c\u7f57\u8fbe\u662f\u4e09\u5341\u591a\u5e74\u7684\u670b\u53cb\u4e86\uff0c\u8d77\u521d\u4e24\u4eba\u90fd\u662f\u9157\u9152\u4f7f\u6c14\u3001\u6740\u4eba\u8d8a\u8d27\u7684\u7eff\u6797\u8c6a\u5f3a\uff0c \u540e\u6765\u5362\u5927\u695e\u5b50\u53d7\u4e86\u5ddd\u4e2d\u5927\u4fa0\u53f6\u4e91\u836a\u7684\u6559\u8bf2\uff0c\u6c14\u8d28\u6e10\u53d8\uff1b\u800c\u7f57\u8fbe\u5374\u53d8\u672c\u52a0\u5389\uff0c\u8fde\u672c\u6765\u8fd8\u6709\u7684\u51e0 \u5206\u8c6a\u4fa0\u4e4b\u6c14\uff0c\u4e5f\u6e10\u6e10\u6d88\u5931\uff0c\u8d8a\u6765\u8d8a\u8d2a\u8d22\u8d27\uff0c\u5fc3\u773c\u72ed\u7a84\uff0c\u6e10\u6e10\u548c\u5362\u5927\u695e\u5b50\u5206\u9053\u626c\u9573\uff0c\u4f46\uff0c\u867d\u7136 \u5982\u6b64\uff0c\u5362\u5927\u695e\u5b50\u8fd8\u662f\u6781\u91cd\u53cb\u60c5\uff0c\u4e34\u5371\u5c06\u4ed6\u6551\u51fa\u9669\u5883\u3002 \u3000\u3000\u5362\u5927\u695e\u5b50\u6258\u7740\u4ed6\u5230\u4e86\u5f7c\u5cb8\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u89c1\u4ed6\u8eab\u4e0a\u53d7\u4e86\u8bb8\u591a\u5904\u7bad\u4f24\uff0c\u8840\u5982\u6cc9\u6d8c\uff0c\u6c14\u606f\u5401 \u5401\uff0c\u9ed8\u7136\u8bf4\u9053\uff1a\u201c\u7f57\u5927\u54e5\uff0c\u4f60\u5b9a\u4e00\u5b9a\u795e\uff0c\u8c03\u597d\u547c\u5438\uff0c\u4e0d\u8981\u5bb3\u6015\uff01\u201d\u8bf4\u7f62\u5c06\u4ed6\u631f\u5728\u80c1\u4e0b\uff0c\u518d\u6b21 \u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u8df3\u8fc7\u6c34\u6f6d\u3002 \u3000\u3000\u8fc7\u4e86\u8fd9\u8fb9\uff0c\u7fa4\u8c6a\u90fd\u6765\u63a2\u671b\uff0c\u5362\u5927\u695e\u5b50\u5411\u8fbe\u571f\u53f8\u8981\u4e86\u4e00\u4e9b\u4e91\u5357\u767d\u836f\uff0c\u6577\u4e0a\u7bad\u4f24\uff0c\u8840\u6d41\u867d \u6b62\uff0c\u4eba\u4ecd\u660f\u8ff7\uff0c\u60f3\u662f\u53d7\u4e86\u91cd\u4f24\u4e4b\u540e\uff0c\u72c2\u51b2\u9003\u547d\uff0c\u529b\u6c14\u7528\u5c3d\uff0c\u4ee5\u81f3\u5982\u6b64\u3002\u5362\u5927\u695e\u5b50\u9ed8\u7136\u8bf4\u9053\uff1a \u201c\u7f57\u5927\u54e5\u6050\u6015\u96be\u4fdd\u6027\u547d\uff01\u201d\u51cc\u672a\u98ce\u7a81\u7136\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u7c92\u78a7\u7eff\u7684\u836f\u4e38\uff0c\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u7ed9\u4ed6\u670d \u4e0b\uff01\u201d\u5362\u5927\u695e\u5b50\u770b\u4e86\u4e00\u773c\uff0c\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd9\u662f\u7528\u5929\u5c71\u96ea\u83b2\u70bc\u6210\u7684\u78a7\u7075\u4e39\uff0c\u5c31\u662f\u4e2d\u4e86\u6bd2\u7bad\u4e5f\u53ef \u4fdd\u4f4f\u6027\u547d\u3002\u201d\u7fa4\u8c6a\u542c\u4e86\u90fd\u5403\u4e00\u60ca\uff0c\u5929\u5c71\u96ea\u83b2\u4e43\u6781\u96be\u5f97\u4e4b\u7269\uff0c\u6bd4\u4e91\u5357\u767d\u836f\uff0c\u66f4\u80dc\u8bb8\u591a\uff0c\u767d\u836f\u53ea \u6cbb\u5916\u4f24\uff0c\u5b83\u8fde\u5185\u4f24\u90fd\u53ef\u533b\u6cbb\uff0c\u6599\u4e0d\u5230\u51cc\u672a\u98ce\u840d\u6c34\u76f8\u9022\uff0c\u51fa\u624b\u4fbf\u8d60\u5947\u836f\u3002\u5362\u5927\u695e\u5b50\u5c24\u5176\u611f\u6fc0\u3002 \u3000\u3000\u4f17\u4eba\u6599\u7406\u597d\u7f57\u8fbe\u4e4b\u540e\uff0c\u53c8\u62e3\u7247\u523b\u3002\u8fbe\u58eb\u53f8\u53eb\u9053\uff1a\u201c\u674e\u5b9a\u56fd\u8fd9\u4e48\u591a\u5fc3\u773c\u513f\uff0c\u6562\u60c5\u4ed6\u7adf\u6599\u5230 \u6211\u4eec\u51e0\u5341\u5e74\u540e\u4f1a\u6765\u8981\u4ed6\u7684\u4e1c\u897f\uff1f\u201d\u5f20\u5143\u632f\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u53bb\u4e0d\u53bb\uff1f\u201d\u9a7c\u80cc\u8001\u4eba\u6c89\u541f\u534a\u664c\uff0c\u8bf4 \u9053\uff1a\u201c\u4e14\u518d\u5f85\u4e24\u4e2a\u4eba\u6765\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u542c\u4ed6\u4eec\u53fd\u53fd\u55b3\u55b3\u8c08\u8bba\uff0c\u5fc3\u91cc\u6599\u5230\u51e0\u5206\uff0c\u6b63\u601d\u7d22\u95f4\uff0c\u5ffd\u7136\u5192\u6d63\u83b2\u76c8\u76c8\u8d77\u7acb\uff0c\u62c9\u7740\u6842 \u4ef2\u660e\uff0c\u78b0\u78b0\u51cc\u672a\u98ce\uff0c\u5f00\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e09\u4e2a\u5148\u53bb\uff01\u201d\u5f20\u5143\u632f\u5fc3\u60f3\uff0c\u8ba9\u4f60\u4eec\u4e09\u4e2a\u4eba\u5148\u53bb\u201c\u6321 \u707e\u201d\u4e5f\u597d\u3002\u7fd8\u8d77\u62c7\u6307\u8bf4\u9053\uff1a\u201c\u7740\uff01\u6709\u51cc\u5927\u4fa0\u53bb\u63a2\uff0c\u4e07\u65e0\u4e00\u5931\uff01\u201d\u5362\u5927\u695e\u5b50\u5374\u53eb\u9053\uff1a\u201c\u51cc\u5927 \u4fa0\uff0c\u4f60\u8fd8\u662f\u518d\u5f85\u4e00\u4f1a\u513f\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u77a7\u4e86\u5192\u6d63\u83b2\u4e00\u773c\uff0c\u89c1\u5979\u773c\u5149\u5145\u6ee1\u81ea\u4fe1\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff01\u201d\u632f\u81c2 \u4e00\u8dc3\uff0c\u4fbf\u8df3\u8fc7\u6c34\u6f6d\u3002 \u3000\u3000\u6842\u4ef2\u660e\u548c\u5192\u6d63\u83b2\u4e5f\u8054\u8882\u8dc3\u8fc7\u6c34\u6f6d\uff0c\u7d27\u8ddf\u7740\u51cc\u672a\u98ce\uff0c\u98d8\u8eab\u7a7f\u8d8a\u6c34\u5e18\uff0c\u5230\u4e86\u5c71\u6d1e\u4e4b\u524d\u3002\u5192\u6dd9 \u83b2\u4e00\u770b\uff0c\u51cc\u672a\u98ce\u8eab\u4e0a\u53ea\u6e85\u4e86\u51e0\u70b9\u6c34\u73e0\uff0c\u6842\u4ef2\u660e\u4e5f\u53ea\u662f\u758f\u758f\u843d\u843d\u5730\u6302\u7740\u4e00\u4e9b\u6c34\u70b9\uff0c\u53ea\u662f\u81ea\u5df1\u8eab \u4e0a\u6e7f\u4e86\u4e00\u7247\u3002\u5fc3\u60f3\u81ea\u5df1\u8ddf\u968f\u5085\u4f2f\u4f2f\u5b66\u827a\uff0c\u4ee5\u8f7b\u529f\u6700\u6709\u5fc3\u5f97\uff0c\u8fde\u602a\u5934\u9640\u901a\u660e\u548c\u5c1a\u4e5f\u5bf9\u81ea\u5df1\u4f69 \u670d\uff0c\u4e0d\u6599\u4eca\u65e5\u4e00\u6bd4\u5c31\u6bd4\u4e0b\u53bb\u4e86\u3002\u602a\u4e0d\u5f97\u51cc\u672a\u98ce\u540d\u6ee1\u897f\u5317\uff0c\u4ed6\u7adf\u662f\u6bcf\u6837\u529f\u592b\uff0c\u90fd\u5230\u4e86\u51fa\u795e\u5165\u5316 \u7684\u5883\u5730\u3002 \u3000\u3000\u4eba\u5230\u4e86\u6d1e\u524d\uff0c\u505c\u4e0b\u6b65\u6765\uff0c\u51cc\u672a\u98ce\u6a2a\u5251\u5b88\u5728\u6d1e\u53e3\uff0c\u5bf9\u6842\u4ef2\u660e\u9053\uff1a\u201c\u4f60\u63a8\u5f00\u5de6\u8fb9\u90a3\u6247\u77f3\u95e8\uff0c \u8ba9\u6211\u4eec\u770b\u5f97\u4ed4\u7ec6\u4e00\u70b9\u3002\u201d\u6842\u4ef2\u660e\u5e94\u58f0\u9053\u597d\uff0c\u53cc\u638c\u8fd0\u529b\uff0c\u5728\u77f3\u95e8\u4e0a\u4e00\u63a8\uff0c\u559d\u58f0\uff1a\u201c\u5f00\uff01\u201d\u90a3\u6247 \u77f3\u95e8\u767b\u65f6\u79fb\u52a8\uff0c\u76f4\u62cd\u5230\u5899\u8fb9\u3002\u8fd9\u65f6\u6d1e\u95e8\u5927\u5f00\uff0c\u5916\u9762\u7684\u9633\u5149\uff0c\u7a7f\u8fc7\u6c34\u5e18\uff0c\u7167\u5c04\u8fdb\u6765\u3002\u4e09\u4eba\u51dd\u7738 \u63a2\u89c6\uff0c\u53ea\u89c1\u6709\u4e24\u884c\u77f3\u4eba\u5206\u5217\u77f3\u6d1e\u5de6\u53f3\uff0c\u6bcf\u4e2a\u77f3\u4eba\u4e4b\u95f4\uff0c\u76f8\u8ddd\u7ea6\u6709\u4e08\u8bb8\uff0c\u6709\u7684\u624b\u4e0a\u62ff\u7740\u5200\u5251\uff0c \u6709\u7684\u624b\u4e0a\u7740\u6208\u77db\uff0c\u90a3\u4e9b\u77f3\u4eba\u96d5\u5f97\u5947\u5f62\u602a\u72b6\uff0c\u76f8\u8c8c\u72f0\u72de\uff0c\u914d\u4e0a\u6d1e\u4e2d\u9634\u6c89\u7684\u6c14\u6c1b\uff0c\u4ee4\u4eba\u66f4\u52a0\u611f\u5230 \u795e\u79d8\u53ef\u6016\u3002 \u3000\u3000\u518d\u4ed4\u7ec6\u770b\u65f6\uff0c\u53c8\u89c1\u5730\u4e0a\u5f13\u7bad\u6563\u4e71\uff0c\u8fd8\u6709\u4e00\u4e9b\u6298\u65ad\u4e86\u7684\u77db\u5934\u7684\u5200\u5251\uff0c\u8fd9\u65f6\u624d\u770b\u6e05\u695a\u6709\u4e9b\u77f3 \u4eba\u624b\u4e0a\u7684\u5175\u5203\u53ea\u5269\u4e0b\u534a\u622a\u3002\u800c\u77f3\u6d1e\u7684\u4e2d\u95f4\u901a\u9053\u5374\u662f\u7a7a\u65f7\u65f7\u7684\u4ec0\u4e48\u5e03\u7f6e\u4e5f\u6ca1\u6709\u3002\u5916\u9762\u867d\u6709\u9633\u5149 \u7167\u5165\uff0c\u4f46\u56e0\u77f3\u6d1e\u6df1\u5e7d\uff0c\u5185\u91cc\u9ed1\u9edd\u9edd\u7684\uff0c\u518d\u4e5f\u770b\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u51cc\u672a\u98ce\u6c89\u5cad\u534a\u664c\uff0c\u5bf9\u6842\u5192\u4e8c\u4eba\u8bf4\u9053\uff1a\u201c\u6211\u770b\u8fd9\u91cc\u9762\u85cf\u6709\u673a\u5173\uff0c\u8fde\u77f3\u4eba\u90fd\u53ef\u80fd\u662f\u53d7\u64cd\u7eb5\u800c \u4f1a\u6d3b\u52a8\u7684\u3002\u5730\u4e0a\u7684\u5f13\u7bad\uff0c\u5f53\u662f\u7f57\u8fbe\u521a\u624d\u8fdb\u6765\u6240\u89e6\u53d1\u7684\uff0c\u90a3\u4e9b\u6298\u65ad\u7684\u77db\u5934\u548c\u5200\u5251\uff0c\u5219\u662f\u4ed6\u5728\u6323 \u624e\u65f6\u8fd0\u638c\u6253\u65ad\u7684\u3002\u6211\u4eec\u5e94\u8be5\u5c0f\u5fc3\u4e00\u70b9\uff0c\u4e0d\u8981\u8e48\u7f57\u8fbe\u7684\u8986\u8f99\u3002\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u6211\u4eec\u5df2\u52bf\u6210\u9a91 \u864e\uff0c\u82e5\u7136\u9053\u51fa\uff0c\u5fc5\u5b9a\u53d7\u4ed6\u4eec\u803b\u7b11\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u5fae\u5fae\u4e00\u7b11\uff0c\u968f\u624b\u5728\u5730\u4e0a\u62e3\u8d77\u51e0\u5757\u77f3\u5934\uff0c\u53eb\u51cc\u6842\u4e8c\u4eba\u9000\u540e\u51e0\u6b65\uff0c\u5c06\u77f3\u5934\u9012\u7ed9\u51cc\u672a\u98ce \u9053\uff1a\u201c\u4f60\u6697\u5668\u624b\u6cd5\u6700\u6709\u51c6\u5934\uff0c\u4f60\u8bd5\u5c06\u7b2c\u4e00\u5757\u77f3\u5934\u63b7\u5728\u6d1e\u53e3\u5de6\u8fb9\uff0c\u7b2c\u4e8c\u5757\u77f3\u5934\u63b7\u5728\u666e\u901a\u4eba\u4e00\u6b65 \u8fdc\u4e4b\u5904\uff0c\u7b2c\u4e09\u5757\u77f3\u5934\u518d\u63b7\u5728\u8ddd\u7b2c\u4e8c\u5757\u77f3\u5934\u4e00\u6b65\u8fdc\u4e4b\u5904\uff0c\u770b\u770b\u6709\u4ec0\u4e48\u53d8\u5316\u3002\u201d\u53c8\u53eb\u6842\u4ef2\u660e\u9053\uff1a \u201c\u4f60\u4ed7\u5251\u5b88\u5728\u51cc\u5927\u4fa0\u8eab\u8fb9\uff0c\u82e5\u6709\u667a\u7bad\u5c04\u51fa\uff0c\u4f60\u5c31\u7528\u5251\u62e8\u6253\u3002\u201d\u51cc\u672a\u98ce\u5982\u8a00\u63b7\u4e86\u4e09\u5757\u77f3\u5934\uff0c\u4e00 \u70b9\u4e8b\u60c5\u90fd\u6ca1\u6709\u3002\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4f60\u518d\u63b7\u7b2c\u56db\u5757\u3002\u201d\u51cc\u672a\u98ce\u4f9d\u8a00\u63b7\u51fa\u3002\u53ea\u89c1\u77f3\u843d\u5904\uff0c\u2018\u84ec\u2019\u7684\u4e00 \u58f0\uff0c\u5730\u9762\u9677\u4e0b\u5c11\u8bb8\uff0c\u7a81\u7136\u95f4\u53d1\u51fa\u4e00\u6392\u7ba9\u7bad\uff0c\u524d\u540e\u5de6\u53f3\u4e71\u5c04\uff0c\u6709\u4e24\u4e09\u679d\u4e14\u5c04\u51fa\u6d1e\u53e3\uff0c\u672a\u5f85\u6842\u4ef2 \u660e\u62e8\u6253\uff0c\u5df2\u7ed9\u51cc\u672a\u98ce\u638c\u51e4\u9707\u843d\u3002 \u3000\u3000\u51cc\u672a\u98ce\u6b23\u7136\u8bf4\u9053\uff1a\u201c\u5192\u59d1\u5a18\uff0c\u4f60\u771f\u806a\u660e\u3002\u7167\u8fd9\u6837\u7b97\u6cd5\uff0c\u82e5\u63b7\u5728\u77f3\u6d1e\u53f3\u8fb9\uff0c\u5e94\u8be5\u662f\u524d\u5934\u56db \u5757\u77f3\u5934\u90fd\u6ca1\u4e8b\uff0c\u7b2c\u4e94\u5757\u5c31\u4f1a\u89e6\u53d1\u8b66\u7bad\u4e86\u3002\u6211\u518d\u8bd5\u8bd5\u3002\u201d\u8bf4\u7f62\u53c8\u5728\u5730\u4e0a\u62e3\u8d77\u4e94\u9897\u77f3\u5757\uff0c\u5411\u6d1e\u53e3 \u4e22\u53bb\u3002\u4e0d\u6599\u7b2c\u4e00\u9897\u521a\u521a\u843d\u5730\uff0c\u5f29\u7bad\u4fbf\u98de\u8757\u4f3c\u7684\u8fce\u9762\u5c04\u6765\uff01 \u3000\u3000\u8fd9\u6392\u5f29\u7bad\u9aa4\u7136\u4e0d\u610f\u5730\u5c04\u51fa\u6765\uff0c\u76f8\u8ddd\u53c8\u8fd1\uff0c\u51cc\u672a\u98ce\u6765\u4e0d\u53ca\u8fd0\u638c\u9707\u843d\uff0c\u5f80\u65c1\u8fb9\u4e00\u7a9c\uff0c\u8fc5\u5982\u98d8 \u98ce\uff0c\u907f\u8fc7\u6b63\u8def\u3002\u6842\u4ef2\u660e\u53cc\u5251\u75be\u821e\uff0c\u5f29\u7bad\u7eb7\u7eb7\u6298\u65ad\uff0c\u8dcc\u843d\u5730\u4e0a\u3002 \u3000\u3000\u51cc\u672a\u98ce\u76b1\u7709\u82e6\u7b11\uff0c\u671b\u7740\u5192\u6d63\u83b2\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5de6\u8fb9\u7684\u7b97\u6cd5\u5bf9\u4e86\uff0c\u53f3\u8fb9\u5374\u53c8\u4e0d\u5bf9\uff0c\u600e\u4e48\u529e \u5462\uff1f\u201d\u5192\u6d63\u83b2\u5c06\u201c\u5de6\u4e09\u53f3\u56db\u4e2d\u5341\u4e8c\u201d\u5ff5\u4e86\u51e0\u904d\uff0c\u60f3\u4e86\u4e00\u9635\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u4f60\u518d\u8bd5\u3002 \u8fd9\u56de\u82e5\u8fd8\u4e0d\u5bf9\uff0c\u6211\u4eec\u53ea\u597d\u9000\u51fa\u4e86\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u600e\u6837\u8bd5\u5462\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4f60\u4ece\u77f3\u6d1e\u5de6\u8fb9 \u7b2c\u4e09\u6b65\u7b97\u8d77\uff0c\u8bbe\u60f3\u4f60\u5728\u90a3\u513f\uff0c\u6a2a\u91cc\u4e00\u8dc3\uff0c\u6b63\u6b63\u8df3\u843d\u53f3\u9762\u4e24\u4e2a\u77f3\u4eba\u4e4b\u95f4\uff0c\u7136\u540e\u518d\u8d70\u56db\u6b65\uff0c\u5047\u5982 \u56db\u6b65\u90fd\u6ca1\u4e8b\uff0c\u90a3\u5c31\u5bf9\u4e86\u3002\u4f60\u4ecd\u7528\u77f3\u5934\u6bd4\u8bd5\u3002\u201d\u51cc\u672a\u98ce\u5982\u8a00\u6bd4\u8bd5\uff0c\u7b2c\u4e00\u5757\u77f3\u5934\u63b7\u5728\u53f3\u8fb9\u8ddd\u79bb\u6d1e \u53e3\u4e09\u6b65\u8fdc\u4e4b\u5904\uff0c\u679c\u7136\u6ca1\u4e8b\u3002\u7b2c\u4e8c\u3001\u7b2c\u4e09\u3001\u7b2c\u56db\u5757\u8fde\u7eed\u63b7\u51fa\uff0c\u6bcf\u5757\u77f3\u5934\u843d\u5730\u4e4b\u5904\u90fd\u8ddd\u79bb\u4e00\u6b65\uff0c \u4ecd\u662f\u5168\u65e0\u5f02\u72b6\u53d1\u751f\uff0c\u5192\u6d63\u83b2\u5927\u559c\u53eb\u9053\uff1a\u201c\u5b8c\u5168\u5bf9\u4e86\uff0c\u4f60\u518d\u63b7\u7b2c\u4e94\u5757\u77f3\u5934\uff0c\u8fd9\u56de\u4e00\u5b9a\u53c8\u6709\u5f29\u7bad \u53d1\u51fa\u3002\u201d\u51cc\u672a\u98ce\u5982\u8a00\u63b7\u53bb\uff0c\u679c\u7136\u53c8\u662f\u84ec\u7684\u4e00\u58f0\uff0c\u53d1\u51fa\u4e00\u6392\u5f29\u7bad\uff0c\u76f8\u8ddd\u8f83\u8fdc\uff0c\u5f29\u7bad\u6ca1\u5c04\u5230\u6d1e\u53e3 \u5c31\u78b0\u843d\u4e86\u3002 \u3000\u3000\u51cc\u672a\u98ce\u9053\uff1a\u201c\u7167\u8fd9\u6837\u7b97\u6cd5\uff0c\u5728\u53f3\u8fb9\u884c\u4e86\u56db\u6b65\u4e4b\u540e\uff0c\u9a6c\u4e0a\u8981\u8dc3\u5230\u4e2d\u8def\uff0c\u518d\u8fde\u7eed\u884c\u5341\u4e8c\u6b65\uff0c \u7136\u540e\u53c8\u8f6c\u5230\u5de6\u8fb9\u884c\u4e09\u6b65\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d\u5192\u6d63\u83b2\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5e94\u8be5\u8fd9\u6837\u7b97\u6cd5\u3002\u201d\u51cc\u672a\u98ce\u5728\u5730\u4e0a\u518d \u62e3\u8d77\u4e00\u5927\u628a\u77f3\u5b50\uff0c\u7528\u91cd\u624b\u6cd5\u4e00\u63b7\u53bb\uff0c\u679c\u7136\u5728\u4e2d\u8def\u63b7\u5230\u5341\u4e09\u7c92\u5bf9\xb7\u6709\u5f2f\u7bad\u53d1\u51fa\uff0c\u51cc\u672a\u98ce\u7b11\u9053\uff1a \u201c\u6210\u4e86\uff01\u6211\u4eec\u8fdb\u53bb\u5427\u3002\u201d\u5192\u9662\u83b2\u9053\uff1a\u201c\u4e14\u6162\u3002\u6211\u4eec\u8fd8\u8981\u7b97\u4e00\u7b97\u77f3\u4eba\u7684\u4f4d\u7f6e\uff0c\u662f\u5426\u4e5f\u8981\u7b97\u6b65 \u6570\u3002\u201d\u51cc\u672a\u98ce\u5c06\u77f3\u5b50\u6f5e\u5728\u77f3\u4eba\u7684\u4fa7\u9762\uff0c\u5288\u7bad\u7eb7\u7eb7\u98de\u51fa\uff0c\u4f46\u82e5\u7b97\u51c6\u6b65\u6570\uff0c\u5219\u63b7\u5728\u77f3\u4eba\u524d\u9762\uff0c\u4e5f \u6ca1\u5f2f\u7bad\u3002\u51cc\u672a\u98ce\u62cd\u88f3\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u5b8c\u5168\u5f04\u6e05\u695a\u4e86\uff0c\u78b0\u5230\u77f3\u4eba\u4e4b\u65f6\uff0c\u4e0d\u80fd\u4ece\u4fa7\u9762\u7ed5\u8fc7\uff0c\u5e94\u5f53\u4ece \u5934\u9876\u98de\u8d8a\uff0c\u4f46\u53c8\u4e0d\u80fd\u8df3\u5f97\u592a\u8fdc\uff0c\u8981\u521a\u597d\u843d\u5728\u77f3\u4eba\u524d\u9762\u4e00\u6b65\uff0c\u624d\u5408\u539f\u6765\u7684\u7b97\u6cd5\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a \u201c\u5bf9\u4e86\u3002\u4f60\u518d\u8bd5\u7528\u77f3\u5934\u63b7\u90a3\u4e9b\u77f3\u4eba\u3002\u201d\u51cc\u672a\u98ce\u968f\u4fbf\u9009\u62e9\uff1d\u4e2a\u77f3\u4eba\uff0c\u4e00\u77f3\u8d64\u53bb\uff0c\u53ea\u89c1\u90a3\u4e2a\u77f3\u4eba \u8eab\u529e\u7a81\u5411\u524d\u503e\uff0c\u624b\u4e2d\u7684\u5927\u5200\u4e00\u5200\u65a9\u4e0b\uff0c\u65a9\u5728\u5730\u4e0a\uff0c\u6fc0\u5f97\u5c18\u4e0a\u98de\u626c\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53c8\u8f6c\u4e86\u51e0\u8f6c\uff0c \u4ecd\u590d\u539f\u72b6\uff0c\u5192\u6d63\u83b2\u9053\uff1a\u201c\u90a3\u4e9b\u77f3\u4eba\u53ef\u78b0\u4e0d\u5f97\u3002\u201d\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u78b0\u78b0\u4e5f\u4e0d\u8981\u7d27\uff0c\u90a3\u653b\u77f3\u4eba\u5c31 \u53ea\u6709\u90a3\u4e00\u4e0b\u5b50\uff0c\u53c8\u4e0d\u4f1a\u8d70\u52a8\uff0c\u78b0\u4e86\u4ea1\u907f\u5f00\u5c31\u662f\u4e86\u3002\u5f53\u7136\uff0c\u82b3\u8981\u907f\u514d\u9ebb\u70e6\uff0c\u8fd8\u662f\u4e0d\u78b0\u7684\u597d\u3002\u201d \u3000\u3000\u6842\u4ef2\u660e\u9053\uff1a\u201c\u73b0\u5728\u53ef\u4ee5\u8fdb\u53bb\u5427\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u53ef\u4ee5\u4e86\u3002\u4e8f\u5f97\u5192\u5c0f\u963b\u673a\u7075\uff0c\u5c45\u7136\u60f3\u901a\u4e86 \u9ec4\u886b\u4e0a\u7684\u9690\u8bed\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u5e78\u5f97\u4f11\u5728\u8fd9\u513f\uff0c\u8981\u4e0d\u7136\u5c31\u8bd5\u4e0d\u51fa\u6765\uff0c\u83ab\u8bf4\u60f3\u901a\u4e86\u3002\u4f60\u7684\u77f3\u5934 \u53ef\u63b7\u5f97\u51c6\u6781\u4e86\u3002\u201d\u6842\u4ef2\u660e\u7b11\u9053\uff1a\u201c\u5192\u59d0\u59d0\uff0c\u4f60\u8fd9\u53ef\u662f\u5916\u884c\u8bdd\u4e86\u3002\u77f3\u5934\u63b7\u51c6\u4e0d\u96be\uff0c\u6700\u96be\u5f97\u7684\u662f \u4ed6\u7528\u5185\u5bb6\u91cd\u624b\u6cd5\u63b7\u53bb\uff0c\u4e00\u7c92\u5c0f\u5c0f\u7684\u77f3\u5b50\uff0c\u78b0\u7740\u5730\u9762\u65f6\uff0c\u5c31\u7b49\u4e8e\u4e00\u4e2a\u5927\u4eba\u8e0f\u5728\u4e0a\u9762\u4e00\u6837\uff0c\u8fd9\u624d \u80fd\u6fc0\u53d1\u5f2f\u7bad\uff0c\u4f60\u5f53\u968f\u4fbf\u63b7\u4e00\u7c92\u77f3\u5b50\uff0c\u5c31\u8bd5\u5f97\u51fa\u6765\u5417\uff1f\u201d\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u603b\u4e4b\u6211\u4f69\u670d\u5c31\u662f\u4e86\u3002 \u6211\u4eec\u8fdb\u53bb\u5427\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u4e00\u9a6c\u5f53\u524d\uff0c\u6842\u4ef2\u660e\u4ed7\u5251\u6bbf\u540e\uff0c\u5192\u6d63\u83b2\u5939\u5728\u4e2d\u95f4\uff0c\u9c7c\u8d2f\u4ece\u5de6\u9762\u8fdb\u5165\u5c71\u6d1e\u3002\u8d70\u4e86\u51e0 \u6b65\uff0c\u51cc\u672a\u98ce\u6253\u6a2a\u4e00\u3002\u8dc3\uff0c\u8df3\u5728\u77f3\u9762\u4e24\u4e2a\u77f3\u4eba\u4e4b\u95f4\uff0c\u8fd9\u65f6\u5192\u6d63\u83b2\u5df2\u8e0f\u4e0a\u4e00\u6b65\uff0c\u7ad9\u5728\u51cc\u672a\u98ce\u539f\u5148 \u7684\u4f4d\u7f6e\uff0c\u4e0e\u51cc\u672a\u98ce\u9065\u9065\u76f8\u5bf9\uff0c\u6070\u6070\u6210\u4e00\u76f4\u7ebf\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5728\u77f3\u8fb9\u518d\u8e0f\u4e0a\u4e00\u6b65\uff0c\u62db\u624b\u9053\uff1a\u201c\u4f60\u8fc7\u6765\u3002\u201d\u6842\u4ef2\u660e\u66ae\u7136\u60f3\u8d77\uff0c\u6253\u6a2a\u8df3\u8fc7\u6765\u4e0d\u96be\uff0c \u4f46\u8981\u843d\u8db3\u4e4b\u70b9\uff0c\u6070\u5230\u597d\u5904\uff0c\u82e5\u975e\u8f7b\u529f\u5df2\u5230 \u3000\u3000210\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u5374\u662f\u4e0d\u80fd\u3002\u4ed6\u4e0d\u7981\u8f7b\u8f7b\u62c9\u7740\u5192\u6d63\u83b2\u7684\u624b\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u7559\u5b88\u5427\uff0c\u8ba9 \u6211\u548c\u51cc\u5927\u4fa0\u53bb\u63a2\u4e5f\u5c31\u884c\u4e86\u3002\u201d\u5192\u6d63\u83b2\u56de\u7738\u4e00\u7b11\uff0c\u89c1\u4ed6\u773c\u5149\u6ce8\u5b9a\u81ea\u5df1\uff0c\u53c8\u662f\u611f\u6fc0\uff0c\u53c8\u662f\u597d\u7b11\u3002 \u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u8fd9\u70b9\u529f\u592b\u6211\u8fd8\u6709\u3002\u201d\u8bf4\u7f62\uff0c\u6454\u5f00\u4e86\u6842\u4ef2\u660e\u7684\u624b\uff0c\u8f7b\u8f7b\u4e00\u8dc3\uff0c\u679c\u7136\u8e0f\u5728 \u51cc\u672a\u98ce\u8ba9\u51fa\u7684\u7a7a\u4f4d\u4e0a\uff0c\u5979\u7684\u8f7b\u529f\u867d\u6bd4\u4e0d\u4e0a\u51cc\u3001\u6842\u4e8c\u4eba\uff0c\u4f46\u5728\u6b66\u6797\u4e2d\u4e5f\u5df2\u7ecf\u7b97\u662f\u7b2c\u4e00\u6d41\u7684\u4e86\u3002 \u3000\u3000\u4e09\u4eba\u6309\u7167\u201c\u5de6\u4e09\u53f3\u56db\u4e2d\u5341\u4e8c\u201d\u7684\u6b65\u6cd5\uff0c\u8fc2\u56de\u8d70\u8fdb\uff0c\u4e0d\u4e45\u4fbf\u5230\u4e86\u5c71\u6d1e\u6df1\u5e7d\u4e4b\u5904\uff0c\u51cc\u672a\u98ce\u4eae \u8d77\u706b\u6298\u3002\u518d\u5411\u524d\u884c\uff0c\u5728\u9ed1\u6697\u4e2d\u4e09\u4eba\u8d8a\u53d1\u63d0\u5fc3\u540a\u80c6\uff0c\u53c8\u8d70\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c1\u773c\u524d\u8bb8\u591a\u4f5b\u50cf\uff0c\u51cc\u672a\u98ce \u4e3e\u8d77\u706b\u628a\u4e00\u7167\uff0c\u7ec6\u7ec6\u4e00\u6570\uff0c\u539f\u6765\u662f\u5341\u516b\u7f57\u6c49\u7684\u5851\u50cf\u3002\u6bcf\u5c0a\u7f57\u6c49\u90fd\u6709\u4e00\u4e08\u591a\u9ad8\uff0c\u8fd9\u65f6\u5df2\u7ecf\u662f\u8d70 \u5230\u77f3\u6d1e\u7684\u5c3d\u5934\u4e86\u3002 \u3000\u3000\u6309\u7167\u6b65\u6cd5\uff0c\u4e09\u4eba\u6b64\u523b\u6070\u597d\u9c7c\u8d2f\u7ad9\u5728\u51e0\u5ea7\u4f5b\u50cf\u4e4b\u524d\uff0c\u51cc\u672a\u98ce\u5411\u6842\u4ef2\u660e\u9053\uff1a\u201c\u4f60\u53d6\u51fa\u51e0\u679a\u91d1 \u73af\u5411\u5de6\u53f3\u4e24\u4fa7\u6253\u53bb\uff0c\u770b\u770b\u5982\u4f55\uff1f\u201d\u6842\u4ef2\u660e\u4f9d\u8a00\u6253\u53bb\uff0c\u51cc\u672a\u98ce\u3001\u5192\u6d63\u83b2\u90fd\u4ed7\u5251\u9632\u536b\uff0c\u6842\u4ef2\u660e\u6bcf \u8fb9\u6253\u4e86\u4e09\u679a\u91d1\u73af\uff0c\u6beb\u65e0\u5f02\u72b6\u3002\u51cc\u672a\u98ce\u9053\uff1a\u201c\u5982\u679c\u5c71\u6d1e\u85cf\u6709\u5b9d\u7269\u7684\u8bdd\uff0c\u4e00\u5b9a\u662f\u5728\u4f5b\u575b\u4e4b\u4e0a\uff0c\u6216 \u8005\u662f\u5728\u7f57\u6c49\u4e4b\u4e0b\u4e86\u3002\u6240\u4ee5\u8fd9\u4e00\u5217\u4f5b\u50cf\u4e0b\u9762\uff0c\u6beb\u65e0\u57cb\u4f0f\uff0c\u60f3\u6765\u5c31\u662f\u7559\u7ed9\u5f53\u65f6\u57cb\u5b9d\u7684\u4eba\uff0c\u5de5\u4f5c\u65b9 \u4fbf\u7684\u3002\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u90a3\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u5728\u57cb\u5b9d\u4e4b\u540e\uff0c\u518d\u8bbe\u673a\u5173\u5462\uff1f\u201d \u3000\u3000\u5192\u6d63\u83b2\u76b1\u7709\u82e6\u60f3\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e8b\u60c5\u53e4\u602a\u5f97\u5f88\uff0c\u5982\u679c\u57cb\u6709\u5b9d\u7269\u7684\u8bdd\uff0c\u5b9d\u7269\u53ef\u80fd\u662f\u5f88\u7b28\u91cd \u7684\uff0c\u8981\u8bb8\u591a\u4eba\u624d\u62ac\u5f97\u52a8\uff0c\u6240\u4ee5\u8fd9\u4e00\u5e26\u624d\u4e0d\u8bbe\u7406\u4f0f\uff0c\u4ee5\u4fbf\u51fa\u5165\uff0c\u4f46\u4f9d\u5e38\u60c5\u800c\u8bba\uff0c\u662f\u5b9d\u7269\u5c31\u4e0d\u8be5 \u7b28\u91cd\uff0c\u8fd9\u53ef\u600e\u4e48\u89e3\u91ca\uff1f\u201d\u505c\u4e86\u4e00\u505c\uff0c\u5979\u53c8\u8bf4\u9053\uff1a\u201c\u5f53\u7136\uff0c\u8fd9\u53ea\u662f\u6211\u7684\u731c\u5ea6\u4e4b\u8bcd\u3002\u8fd9\u5217\u7f57\u6c49\u7684 \u524d\u9762\uff0c\u5373\u6ca1\u6709\u673a\u5173\uff0c\u6211\u4eec\u5c31\u4e00\u4e00\u5bdf\u770b\u5427\u3002\u201d\u8bf4\u7f62\u4e0e\u51cc\u672a\u98ce\u5206\u5934\u5bdf\u770b\u3002\u6842\u4ef2\u660e\u5374\u5140\u7acb\u6b63\u4e2d\u4e0d \u52a8\uff0c\u53cc\u76ee\u6ce8\u5b9a\u7f57\u6c49\uff0c\u4e0d\u77e5\u5728\u60f3\u4ec0\u4e48\u3002 \u3000\u3000\u51cc\u672a\u98ce\u827a\u9ad8\u80c6\u5927\uff0c\u4ed6\u7ec6\u7ec6\u5bdf\u770b\u77f3\u9762\u7684\u4e5d\u5c0a\u7f57\u6c49\uff0c\u6bcf\u5c0a\u7f57\u53cc\u5916\u8868\u90fd\u662f\u9ed1\u6f06\u6f06\u7684\uff0c\u7528\u624b\u53bb \u6478\uff0c\u575a\u786c\u7ed3\u5b9e\uff0c\u4f3c\u662f\u751f\u94c1\u94f8\u6210\u3002\u4e0e\u897f\u5317\u666e\u901a\u5bfa\u9662\u7684\u7f57\u6c49\uff0c\u6beb\u65e0\u4e8c\u81f4\u3002\u4ed6\u53eb\u5192\u6d63\u83b2\u5728\u80cc\u9762\u7167\u6837 \u5bdf\u770b\uff0c\u4ea6\u5143\u5f02\u72b6\u3002\u51cc\u672a\u98ce\u6b63\u60f3\u968f\u624b\u628a\u4e00\u5c0a\u7f57\u6c49\u642c\u5f00\uff0c\u5ffd\u7136\u542c\u5f97\u5192\u6d63\u83b2\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4ef2\u660e\uff0c\u4f60 \u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u539f\u6765\u5192\u6d63\u83b2\u5728\u5bdf\u770b\u7f57\u6c49\u4e4b\u65f6\uff0c\u5076\u7136\u56de\u5934\u4e00\u671b\uff0c\u89c1\u6842\u4ef2\u660e\u75f4\u75f4\u7684\u7acb\u5728\u5f53\u4e2d\uff0c\u7aef\u8be6\u770b\u4e3b\u5ea7\u7684 \u4f5b\u50cf\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5979\u53ea\u9053\u6842\u4ef2\u660e\u65e7\u75c5\u590d\u53d1\uff0c\u53c8\u53d8\u75f4\u5446\uff0c\u56e0\u6b64\u4e0d\u7981\u60ca\u53eb\u8d77\u6765\uff01 \u3000\u3000\u4f60\u9053\u6842\u4ef2\u660e\u4e3a\u4ec0\u4e48\u4ed4\u7ec6\u7aef\u8be6\u4e3b\u5ea7\u7684\u4f5b\u50cf\uff1f\u539f\u6765\u90a3\u5c0a\u4f5b\u50cf\u7684\u76f8\u8c8c\uff0c\u7adf\u4e0d\u662f\u4e00\u822c\u7f57\u6c49\u7684\u5f62 \u8c61\uff0c\u662f\u4e00\u4e2a\u4ed6\u6240\u719f\u6089\u7684\u4eba\uff0c\u8d77\u521d\u4ed6\u60f3\u6765\u60f3\u53bb\u90fd\u60f3\u4e0d\u8d77\uff0c\u540e\u6765\u4ed4\u7ec6\u56de\u5fc6\uff0c\u624d\u60f3\u8d77\u8fd9\u5c0a\u4f5b\u50cf\u7adf\u7136 \u5c31\u662f\u5f53\u5e74\u5ddd\u6ec7\u4e49\u519b\u7684\u4e3b\u5e05\uff0c\u7edf\u9886\u5f20\u732e\u5fe0\u9057\u90e8\u8054\u660e\u6297\u6e05\u7684\u5927\u5c06\u674e\u5b9a\u56fd\u3002\u4ed6\u5e7c\u5e74\u968f\u4e49\u7236\u6842\u5929\u6f9c\u5728 \u674e\u5b9a\u56fd\u519b\u4e2d\u6709\u56db\u4e94\u5e74\u4e4b\u4e45\uff0c\u674e\u5b9a\u56fd\u8fd8\u62b1\u8fc7\u4ed6\u5462\u3002\u5192\u6d63\u83b2\u4ee5\u4e3a\u4ed6\u65e7\u75c5\u590d\u53d1\uff0c\u5176\u5b9e\u4e0d\u662f\uff0c\u6070\u6070\u76f8 \u53cd\uff0c\u4ed6\u6b63\u9010\u6e10\u6062\u590d\u7075\u667a\u4e4b\u4e2d\uff0c\u5bf9\u7ae5\u5e74\u4e8b\u60c5\uff0c\u4e5f\u90fd\u8bb0\u5f97\u8d77\u6765\u4e86\uff01 \u3000\u3000\u6842\u4ef2\u660e\u6b22\u559c\u4e4b\u6781\uff0c\u7528\u624b\u62b1\u7740\u4f5b\u50cf\u7684\u8170\uff0c\u6447\u64bc\u51e0\u4e0b\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u674e\u4f2f\u4f2f\uff0c\u8fd8\u8bb0\u5f97\u6211 \u5417\uff1f\u201d\u4ed6\u7684\u624b\u638c\u89e6\u7740\u957f\u86c7\u4e00\u6837\u7684\u6ed1\u6e9c\u6e9c\u7684\u4e1c\u897f\uff0c\u7adf\u4f1a\u6ed1\u52a8\uff0c\u4ed6\u5927\u5403\u4e00\u60ca\uff0c\u53cc\u638c\u7528\u529b\u4e00\u6309\uff0c\u4eba \u5411\u540e\u9762\u4fbf\u5012\u7eb5\u51fa\u53bb\uff0c\u521a\u521a\u8d8a\u8fc7\u7981\u533a\u7684\u8fb9\u7f18\uff0c\u84ec\u7684\u4e00\u58f0\uff0c\u4e71\u7bad\u5c04\u51fa\u3002\u5e78\u5f97\u4ed6\u8f7b\u529f\u8d85\u5353\uff0c\u811a\u8ddf\u65b9 \u89e6\u5b9e\u5730\uff0c\u5df2\u81ea\u9192\u8d77\uff0c\u6025\u53c8\u5411\u524d\u7eb5\uff0c\u51cc\u672a\u98ce\u53cc\u638c\u9f50\u53d1\uff0c\u4e00\u628a\u788e\u77f3\u5c06\u4e71\u7bad\u78b0\u843d\u5730\u4e0a\uff01 \u3000\u3000\u5728\u4ed6\u5411\u524d\u7eb5\u8dc3\u4e4b\u9645\uff0c\u53c8\u4e00\u5947\u4e8b\u53d1\u751f\uff0c\u4e3b\u5ea7\u4f5b\u50cf\u8170\u95f4\u7a81\u7136\u98de\u51fa\u4e00\u9053\u767d\u5149\uff0c\u5288\u9762\u5c04\u6765\uff0c\u51cc\u672a \u98ce\u4e00\u679d\u795e\u8292\u6253\u53bb\uff0c\u78b0\u4e2a\u6b63\u7740\uff0c\u767d\u5149\u7f13\u5f97\u4e00\u7f13\uff0c\u4ecd\u7136\u5c04\u6765\uff0c\u6842\u4ef2\u660e\u8fd9\u65f6\u5df2\u8d81\u52bf\u62d4\u51fa\u53cc\u5251\uff0c\u5411\u4e0a \u64a9\u53bb\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u81ea\u5df1\u4e24\u628a\u957f\u5251\uff0c\u5168\u7ed9\u622a\u65ad\uff0c\u800c\u90a3\u9053\u767d\u5149\u4e5f\u5df2\u5815\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u65f6\u51cc\u672a\u98ce\u548c\u5192\u6d63\u83b2\u4e00\u540c\u8d76\u5230\uff0c\u53ea\u89c1\u5730\u4e0a\u8eba\u7740\u4e00\u652f\u4f3c\u5251\u975e\u5251\u7684\u4e1c\u897f\uff0c\u86c7\u4e00\u822c\u5730\u5728\u5730\u4e0a\u98a4 \u52a8\u4e0d\u4f11\uff0c\u5251\u8eab\u5f88\u7a84\uff0c\u5251\u5c16\u949d\u5f62\uff0c\u5251\u70b3\u6781\u77ed\u3002\u6842\u4ef2\u660e\u8f7b\u8f7b\u63d0\u8d77\u5251\u67c4\uff0c\u6349\u5c06\u8d77\u6765\uff0c\u53ea\u89c9\u8f6f\u7ef5\u7ef5\u7684 \u4f3c\u6761\u8170\u5e26\uff0c\u4ed6\u8bd5\u7740\u8f7b\u8f7b\u4e00\u5377\uff0c\u5c45\u7136\u5377\u6210\u4e00\u5708\uff0c\u5927\u5931\u6240\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u7b97\u5f97\u4ec0\u4e48\u5175\u5203\uff1f\u201d\u51cc\u672a \u98ce\u53cc\u773c\u95ea\u95ea\u653e\u5149\uff0c\u5927\u559c\u53eb\u9053\uff1a\u201c\u6842\u8d24\u5f1f\uff0c\u4f60\u8bd5\u7528\u529b\u6296\u52a8\uff0c\u5c06\u5df2\u4f38\u76f4\uff0c\u7ed3\u679c\u5982\u4f55\uff1f\u201d\u6842\u4ef2\u660e\u4f9d \u8a00\u4e00\u6296\uff0c\u90a3\u56e2\u4e1c\u897f\u9aa4\u7684\u4f38\u51fa\u56db\u4e94\u5c3a\u957f\uff0c\u8bd5\u4e00\u6325\u52a8\uff0c\u53ea\u89c1\u5149\u8f89\u6d41\u52a8\uff0c\u5251\u98ce\u6251\u4eba\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u8f6f \u7ef5\u7ef5\u7684\u611f\u89c9\uff0c\u6842\u4ef2\u660e\u821e\u4e86\u4e00\u9635\uff0c\u5c06\u5251\u6536\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u628a\u5251\u5982\u6b64\u5947\u602a\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u6025\u4e0d\u53ef\u5f85\uff0c\u8d76\u5fd9\u95ee\u9053\uff1a\u201c\u5148\u522b\u7ba1\u5b83\u662f\u4e0d\u662f\u5b9d\u5251\u3002\u4f60\u73b0\u5728\u600e\u6837\uff1f\u8bb0\u5f97\u8d77\u4ee5\u524d\u7684\u4e8b \u5417\uff1f\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u6211\u73b0\u5728\u4ec0\u4e48\u90fd\u8bb0\u5f97\u8d77\u4e86\uff0c\u5c0f\u5b69\u5b50\u65f6\u5019\u7684\u4e8b\u4e5f\u8bb0\u5f97\u8d77\u3002\u201d\u4ed6\u6307\u4e00\u6307\u4e3b\u5ea7\u7684 \u4f5b\u50cf\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0a\u4f5b\u50cf\u5851\u7684\u662f\u674e\u4f2f\u4f2f\u3002\u201d\u51cc\u672a\u98ce\u95ee\u9053\uff1a\u201c\u54ea\u4e2a\u4e00\u674e\u4f2f\u4f2f\uff1f\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u8fd8 \u6709\u54ea\u4e2a\uff1f\u5c31\u662f\u674e\u5b9a\u56fd\u5c06\u519b\u561b\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u559c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u4f60\u62ff\u5251\u7ed9\u6211\u770b\u770b\u3002\u201d\u6842\u51b2\u660e\u5c06\u5251\u9012\u8fc7\uff0c\u51cc\u672a\u98ce\u773c\u775b\u4e00\u4eae\uff0c\u6307 \u7740\u5251\u67c4\u4e0a\u7684\u5c0f\u5b57\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u91cc\u5199\u7684\u662f\u4ec0\u4e48\uff1f\u201d\u6842\u4ef2\u660e\u8bfb\u9053\uff1a\u201c\u817e\u86df\u5b9d\u5251\uff0c\u4f20\u81ea\u524d\u8d24\uff0c\u7559\u8d60 \u82f1\u8c6a\uff0c\u674e\u5b9a\u56fd\u62dc\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u90a3\u4e48\u8fd9\u662f\u674e\u5b9a\u56fd\u7684\u4f69\u5251\u4e86\uff0c\u602a\u4e0d\u5f97\u5982\u6b64\u5389\u5bb3\u3002\u53ea\u662f\u4ed6\u4e3a\u4ec0 \u4e48\u8981\u7559\u4e0b\u8fd9\u884c\u5c0f\u5b57\uff1f\u8fd9\u628a\u5251\u53c8\u5982\u4f55\u4f1a\u85cf\u5728\u5c71\u6d1e\u4e4b\u4e2d\u4e86\u800c\u4e14\u66f4\u5947\u602a\u7684\u662f\uff0c\u5b83\u600e\u4f1a\u7a81\u7136\u98de\u51fa\uff1f\u96be \u9053\u4e16\u95f4\u771f\u7684\u4f1a\u6709\u4ec0\u4e48\u98de\u5251\u4e0d\u6210\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u98de\u5251\u662f\u7edd\u4e0d\u4f1a\u6709\u7684\u3002\u5b83\u4f1a\u98de\u51fa\uff0c\u90a3\u662f\u6842\u8d24\u5f1f \u7528\u529b\u89e6\u53d1\u7684\uff0c\u4f60\u82e5\u4e0d\u4fe1\uff0c\u4e14\u968f\u6211\u6765\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u5728\u5730\u4e0a\u62fe\u8d77\u90a3\u679d\u88ab\u622a\u4e3a\u4e24\u6bb5\u7684\u795e\u8292\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u5c71\u795e\u8292\uff0c\u575a\u903e\u94a2\u94c1\uff0c\u53c8\u7ecf\u6211\u7528\u91cd \u624b\u6cd5\u6253\u51fa\uff0c\u8fd8\u662f\u7ed9\u622a\u4e3a\u4e24\u6bb5\uff0c\u4f60\u8fd9\u628a\u5b9d\u5251\uff0c\u770b\u6765\u8fd8\u5728\u695a\u662d\u5357\u7684\u6e38\u9f99\u5251\u4e4b\u4e0a\u3002\u201d\u8fb9\u8bf4\u8fb9\u8d70\uff0c\u5230 \u4e86\u4e3b\u5ea7\u4f5b\u50cf\u4e4b\u524d\uff0c\u6842\u4ef2\u660e\u548c\u5192\u6d63\u83b2\u8ddf\u5728\u4ed6\u7684\u80cc\u540e\u3002\u51cc\u672a\u98ce\u6307\u4e00\u6307\u795e\u575b\u4e0a\u7684\u4e00\u6761\u4e1c\u897f\u9053\uff1a\u201c\u4f60 \u4eec\u770b\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d\u6842\u4ef2\u660e\u62ff\u8d77\u4e00\u770b\uff0c\u53ea\u89c1\u9ed1\u6f06\u6f06\u7684\u4f3c\u4e00\u6761\u8170\u5e26\u3002\u7528\u624b\u4e00\u637b\uff0c\u624d\u77e5\u9053\u662f\u5939\u5c42 \u7684\uff0c\u8bd5\u7528\u521a\u5f97\u7684\u5b9d\u5251\u5f80\u91cc\u4e00\u63d2\uff0c\u6b63\u662f\u4e00\u4e2a\u6781\u597d\u7684\u5251\u9798\u3002\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u8fd9\u5251\u9798\u662f\u53ef\u4ee5\u5377\u8d77\u6765 \u7684\uff0c\u4f60\u8bd5\u8bd5\u770b\u3002\u201d\u6842\u4ef2\u660e\u4f9d\u8a00\u4e00\u8bd5\uff0c\u679c\u7136\u4e0d\u865a\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5728\u4e3b\u5ea7\u4f5b\u50cf\u7684\u5468\u56f4\u5bdf\u770b\u4e00\u4e0b\uff0c\u5411\u6842\u4ef2\u660e\u9053\uff1a\u201c\u4f60\u8fd9\u628a\u5251\u672c\u6765\u5c31\u662f\u56f4\u5728\u8fd9\u5c0a\u4f5b\u50cf\u8170 \u95f4\u7684\u8170\u5e26\uff0c\u4f60\u521a\u624d\u7528\u529b\u4e00\u62d4\u4e4b\u65f6\uff0c\u89e6\u52a8\u5f39\u7c27\uff0c\u5251\u5c31\u79bb\u9798\u6025\u5c04\u51fa\u6765\u4e86\u3002\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u51cc\u5927 \u4fa0\uff0c\u4f60\u600e\u7684\u597d\u50cf\u5f88\u77e5\u9053\u8fd9\u628a\u5251\u7684\u6765\u5386\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u5728\u5929\u5c71\u5b66\u5251\u4e4b\u65f6\uff0c\u6666\u660e\u7985\u5e08\u66fe\u5c06\u8457 \u540d\u7684\u6b66\u6797\u4eba\u7269\u548c\u8457\u540d\u7684\u5b9d\u5251\u8bb2\u7ed9\u6211\u542c\u3002\u4ed6\u8bf4\u6709\u4e00\u628a\u2018\u817e\u86df\u5251\u2019\uff0c\u4e43\u662f\u660e\u671d\u8fbd\u4e1c\u7ecf\u7565\u718a\u5ef6\u5f3c\u7684 \u4f69\u5251\uff0c\u8fd9\u628a\u5251\u7528\u4e1c\u5317\u7684\u767d\u91d1\uff08\u94c2\uff09\u7cbe\u70bc\u800c\u6210\uff0c\u5c48\u4f38\u5982\u610f\uff0c\u53ef\u4ee5\u5f53\u4f5c\u8170\u5e26\u56f4\u5728\u8170\u95f4\u3002\u771f\u53ef\u79f0\u5f97 \u662f\u2018\u767e\u70bc\u94a2\u5982\u7ed5\u6307\u67d4\u2019\u3002\u718a\u5ef6\u5f3c\u66fe\u4ed7\u8fd9\u628a\u5251\u6740\u4e86\u8bb8\u591a\u9791\u5b50\uff0c\u540e\u6765\u718a\u5ef6\u5f3c\u7ed9\u5978\u81e3\u9b4f\u5fe0\u8d24\u5bb3\u6b7b\u3002 \u8fd9\u628a\u5251\u5c31\u4e0d\u77e5\u4e0b\u843d\u3002\u60f3\u4e0d\u5230\u73b0\u5728\u7adf\u5728\u6b64\u5904\u53d1\u73b0\u3002\u770b\u5251\u4e0a\u7684\u5b57\uff0c\u5927\u7ea6\u540e\u6765\u662f\u4e3a\u674e\u5b9a\u56fd\u6240\u83b7\uff0c\u674e \u5b9a\u56fd\u5175\u8d25\u4e4b\u540e\uff0c\u5c31\u4ea4\u7ed9\u5fc3\u8179\u7231\u5c06\u4fdd\u5b58\uff0c\u53eb\u4ed6\u7559\u8d60\u82f1\u8c6a\u7684\u3002\u7559\u5b57\u6240\u8bf4\u7684\u2018\u5f97\u81ea\u524d\u8d24\u201d\u8fd9\u524d\u8d24\u5c31 \u662f\u6307\u718a\u5ef6\u5f3c\u3002\u201d\u6842\u4ef2\u660e\u9a87\u7136\u9053\uff1a\u201c\u6211\u5e38\u542c\u4e49\u7236\u8bf4\u8d77\uff0c\u718a\u5ef6\u5f3c\u662f\u53ef\u4ee5\u5ab2\u7f8e\u5cb3\u6b66\u7a46\u7684\u7231\u56fd\u540d\u5c06\uff0c \u4ed6\u7684\u5251\u674e\u5b9a\u56fd\u914d\u7528\u90a3\u662f\u5f97\u5176\u4f20\u4eba\uff0c\u6211\u600e\u6562\u4f7f\u8fd9\u628a\u5251\uff1f\u51cc\u5927\u4fa0\uff0c\u4f60\u7684\u5251\u6cd5\u72ec\u6b65\u6d77\u5185\uff0c\u8fd8\u662f\u4f60\u8981 \u4e86\u5427\u3002\u201d\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u53d1\u73b0\u7684\uff0c\u7406\u5e94\u5f52\u4f60\u6240\u6709\u3002\u518d\u8bf4\u4e00\u53e5\u6f5c\u8d8a\u7684\u8bdd\uff0c\u6211\u548c\u4f60\u6240\u5b66\u7684 \u5251\u6cd5\u4e0d\u540c\uff0c\u6211\u6240\u5b66\u7684\u5251\u6cd5\uff0c\u968f\u4fbf\u7528\u4e00\u628a\u666e\u901a\u7684\u5251\uff0c\u90fd\u53ef\u4ee5\u654c\u5f97\u4f4f\u5bf9\u65b9\u7684\u5b9d\u5251\u3002\u6211\u8981\u4e86\u8fd9\u628a \u5251\uff0c\u5bf9\u6211\u6ca1\u591a\u5927\u5e2e\u52a9\uff0c\u800c\u5bf9\u4f60\u5374\u5f88\u6709\u597d\u5904\u3002\u82e5\u4f60\u6015\u914d\u4e0d\u4e0a\u8fd9\u628a\u5251\uff0c\u90a3\u5c31\u7559\u5728\u8eab\u8fb9\u3002\u5f85\u4ee5\u540e\u518d \u9001\u7ed9\u9002\u5f53\u7684\u4eba\u5427\u3002\u201d\u6842\u4ef2\u660e\u89c1\u4ed6\u8bf4\u5f97\u5982\u6b64\u76f4\u7387\uff0c\u4e5f\u5c31\u4e0d\u518d\u63a8\u8ba9\u3002 \u3000\u3000\u6b63\u5728\u6842\u4ef2\u660e\u548c\u51cc\u672a\u98ce\u8bba\u5251\u4e4b\u65f6\uff0c\u6d1e\u53e3\u5ffd\u7136\u53c8\u53d1\u73b0\u706b\u5149\uff0c\u51cc\u672a\u98ce\u62cd\u62cd\u6842\u4ef2\u660e\u7684\u80a9\u5934\u9053\uff1a \u201c\u4f60\u51c6\u5907\u8bd5\u8fd9\u628a\u5251\u5427\uff01\u5916\u9762\u6709\u4eba\u6765\u4e86\u3002\u201d\u4e09\u4eba\u5c4f\u606f\u4ee5\u5f85\uff0c\u53ea\u89c1\u6d1e\u4e2d\u6709\u51e0\u6761\u4eba\u5f71\uff0c\u5de6\u53f3\u8df3\u8dc3\uff0c \u4e0d\u8fc7\u4e00\u4f1a\uff0c\u5c31\u5230\u4e86\u4f5b\u50cf\u4e4b\u524d\u3002\u4e00\u4e2a\u662f\u9a7c\u80cc\u8001\u4eba\u97e9\u8346\uff1b\u4e00\u4e2a\u662f\u8fbe\u571f\u53f8\uff0c\u53e6\u4e00\u4e2a\u4eba\u4ed6\u4eec\u5374\u4e0d\u8ba4 \u5f97\u3002 \u3000\u3000\u539f\u6765\u51cc\u672a\u98ce\u7b49\u8fdb\u4e86\u6d1e\u53e3\uff0c\u5916\u9762\u7fa4\u8c6a\uff0c\u66f4\u662f\u7d27\u5f20\u3002\u8fc7\u4e86\u8bb8\u4e45\uff0c\u8fd8\u672a\u89c1\u4ed6\u4eec\u51fa\u6765\uff0c\u8fbe\u571f\u53f8\u5c31 \u60f3\u95ef\u8fdb\u6d1e\u53bb\u3002\u97e9\u8346\u542c\u5f97\u8fdc\u5904\u6709\u53e3\u54e8\u58f0\u9690\u9690\u4f20\u6765\uff0c\u63a5\u7740\u8fbe\u571f\u53f8\u9053\uff1a\u201c\u522b\u5fd9\uff0c\u8ba9\u4ed6\u4eec\u4e09\u4eba\u5f00\u8def\uff0c \u6211\u4eec\u4fdd\u8bc1\u624b\u5230\u62ff\u6765\u3002\u201d \u3000\u3000\u5f20\u5143\u632f\u5362\u5927\u695e\u5b50\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u8001\u6c49\u5df2\u548c\u97e9\u8346\u6253\u4e0a\u62db\u547c\u3002\u97e9\u8346\u4e3e\u624b\u8bf4\u9053\uff1a\u201c\u8d3a\u8001\u5144 \u6765\u4e86\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u5c31\u597d\u529e\u4e86\u3002\u8d3a\u8001\u5144\u5c31\u662f\u5f53\u5e74\u5949\u674e\u5b9a\u56fd\u6240\u6d3e\uff0c\u534f\u52a9\u6842\u5929\u6f9c\u9020\u5c71\u6d1e\u673a\u5173\u7684\u4eba\u3002\u201d \u5f53\u4e0b\u97e9\u8346\u4e24\u8fb9\u4ecb\u7ecd\uff0c\u7fa4\u8c6a\u624d\u77e5\u6b64\u4eba\u5c31\u662f\u4e09\u5341\u5e74\u524d\u6709\u540d\u201c\u6c14\u7684\u5de7\u624b\u5320\u4eba\u8d3a\u4e07\u65b9\u201d\u4ed6\u64c5\u5236\u5404\u79cd\u6697 \u5668\uff0c\u6b66\u529f\u4e5f\u5f88\u4e0d\u9519\uff0c\u8d3a\u4e07\u65b9\u4e5f\u4e45\u95fb\u7fa4\u8c6a\u5927\u540d\uff0c\u5f53\u4e0b\u5404\u81ea\u53d9\u793c\u76f8\u89c1\uff0c\u97e9\u8346\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u4e24\u4f4d \u5462\uff1f\u201d\u8d3a\u4e07\u4e07\u9053\uff1a\u201c\u5728\u8fdb\u5165\u5c71\u8c37\u65f6\uff0c\u6211\u4eec\u5206\u8def\u7684\u3002\u4ed6\u4eec\u53bb\u6253\u6842\u8001\u5934\u513f\uff0c\u6211\u5374\u8ff3\u81ea\u6765\u8fd9\u91cc\u3002\u201d \u97e9\u8346\u7b11\u9053\uff1a\u201c\u6211\u4eec\u6765\u65f6\u8fd8\u6015\u6842\u8001\u5934\u963b\u6321\uff0c\u6545\u6b64\u904d\u7ea6\u9ad8\u624b\uff0c\u8c01\u77e5\u5230\u4e86\u8fd9\u91cc\uff0c\u624d\u77e5\u9053\u4ed6\u5df2\u7ecf\u6b7b \u4e86\u3002\u201d \u3000\u3000\u8d3a\u4e07\u65b9\u9053\uff1a\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u4e0d\u7ea6\u4ed6\u4eec\u6765\uff0c\u8fd8\u53ef\u4ee5\u5c11\u5206\u4e24\u4efd\u3002\u201d\u8fbe\u571f\u53f8\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u6842\u8001\u5934 \u513f\u867d\u7136\u6b7b\u4e86\uff0c\u4f46\u6050\u6015\u8fd8\u6709\u963b\u788d\u3002\u521a\u624d\u8fdb\u5c71\u6d1e\u7684\u90a3\u4e2a\u4ec0\u4e48\u2018\u5929\u5c71\u795e\u8292\u2019\u548c\u9ec4\u886b\u5c11\u5e74\uff0c\u786c\u4efd\u6050\u6015 \u4e0d\u5728\u6842\u5929\u6f9c\u4e4b\u4e0b\u3002\u4eba\u591a\u4e00\u4e9b\uff0c\u6709\u5907\u65e0\u60a3\u3002\u201d\u5362\u5927\u695e\u5b50\u9053\uff1a\u201c\u6bcf\u4eba\u5206\u4ed6\u4e00\u4efd\u597d\u4e86\u3002\u201d \u3000\u3000\u97e9\u8346\u6765\u65f6\uff0c\u5df2\u5728\u738b\u521a\u7b49\u4f0f\u8bdb\u4e4b\u540e\uff0c\u6ca1\u6709\u89c1\u8fc7\u51cc\u3001\u6842\u4e8c\u4eba\u8eab\u624b\uff0c\u201c\u55e4\u201d\u4e00\u58f0\u7b11\u9053\uff1a\u201c\u4e8f\u4f60 \u8fd8\u662f\u5916\u5bb6\u62f3\u9876\u5c16\u513f\u7684\u4eba\u7269\uff0c\u600e\u7684\u4f1a\u6015\u8d77\u4e24\u4e2a\u665a\u751f\u540e\u8f88\u6765\uff01\u201d\u8fbe\u571f\u53f8\u6012\u9053\uff1a\u201c\u8c01\u4eba\u5bb3\u6015\uff1f\u4f46\u522b \u4eba\u662f\u9ad8\u624b\uff0c\u4e5f\u4e0d\u5bb9\u4f60\u8f7b\u89c6\u3002\u4f60\u62ff\u56fe\u6837\u8fc7\u6765\uff0c\u6211\u4e00\u4e2a\u4eba\u8fdb\u53bb\u3002\u201d\u8d3a\u4e07\u65b9\u6025\u5fd9\u8bf4\uff1a\u201c\u6211\u4eec\u6b63\u8981\u5165 \u6d1e\u63a2\u89c6\uff0c\u4eba\u591a\u53bb\u4e5f\u4e0d\u597d\uff0c\u5c31\u4e09\u4e2a\u4eba\u53bb\u5427\uff0c\u8fbe\u571f\u53f8\u662f\u4e00\u7247\u597d\u610f\uff0c\u6211\u4eec\u662f\u8be5\u5c0f\u5fc3\u4e00\u70b9\u597d\uff01\u201d\u97e9\u8346 \u51b7\u51b7\u70b9\u4e86\u70b9\u5934\uff0c\u4e0e\u8fbe\u58eb\u53f8\u3001\u8d3a\u4e07\u65b9\u8dc3\u8fc7\u6c34\u5e18\uff0c\u98d8\u8eab\u8fdb\u4e86\u5c71\u6d1e\u3002 \u3000\u3000\u8d3a\u4e07\u65b9\u6df1\u6089\u6d1e\u4e2d\u673a\u5173\uff0c\u81ea\u7136\u77e5\u9053\u8d70\u6cd5\u3002\u4e0d\u4e00\u4f1a\u513f\u4ed6\u5c31\u5e26\u9886\u4e24\u4eba\u5230\u4e86\u575b\u524d\u3002\u97e9\u8346\u4e00\u773c\u671b \u53bb\uff0c\u89c1\u6842\u4ef2\u660e\u6b63\u5728\u6469\u64ce\u4f5b\u50cf\uff0c\u5fc3\u4e2d\u4e00\u8df3\uff0c\u4ee5\u4e3a\u4ed6\u4eec\u5df2\u7ecf\u53d1\u73b0\u4e86\u79d8\u5bc6\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u594b\u529b\u4e00\u8dc3\uff0c \u4e3e\u8d77\u624b\u4e2d\u7684\u5175\u5668\u9f99\u5934\u62d0\u6756\uff0c\u5411\u6842\u4ef2\u660e\u5934\u9876\u62cd\u4e0b\uff0c\u8fd9\u6839\u62d0\u6756\u662f\u7528\u767e\u70bc\u7cbe\u94a2\u6253\u6210\uff0c\u5341\u5206\u575a\u786c\u3002\u6842 \u4ef2\u660e\u53cd\u624b\u4e00\u6296\uff0c\u817e\u86df\u5b9d\u5251\u731b\u7684\u4f38\u957f\uff0c\u53ea\u542c\u5f97\u5f53\u537f\u4e00\u58f0\uff0c\u90a3\u6839\u62d0\u6756\u767b\u65f6\u7ed9\u622a\u53bb\u4e00\u534a\u3002\u97e9\u8346\u5927\u5403 \u4e00\u60ca\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u52c3\u7136\u5927\u6012\uff0c\u534a\u6839\u62d0\u6756\u6a2a\u91cc\u4e00\u626b\uff0c\u5185\u529b\u9707\u52a8\uff0c\u6842\u4ef2\u660e\u89c1\u9762\u524d\u4f3c\u6709\u5341\u51e0\u6839\u62d0\u6756 \u6253\u6765\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u5e73\u5730\u8dc3\u8d77\uff0c\u907f\u8fc7\u62d0\u4ed7\uff0c\u817e\u86df\u5251\u4e00\u4e2a\u76d8\u65cb\uff0c\u5251\u82b1\u9519\u843d\uff0c\u5f53\u5934\u7f69\u4e0b\uff0c\u8fd9\u6b63\u662f\u4e94 \u79bd\u5251\u6cd5\u4e2d\u7684\u7edd\u62db\u201c\u5c55\u7ffc\u6469\u4e91\u201d\u3002\u97e9\u8346\u7684\u6756\u6cd5\u867d\u7136\u8fc5\u75be\u5df2\u6781\uff0c\u4ecd\u7136\u907f\u4e0d\u5f00\u4e0e\u5251\u63a5\u89e6\uff0c\u201c\u5f53\u5577\u201d \u4e00\u58f0\uff0c\u53c8\u622a\u53bb\u4e00\u6bb5\u3002\u97e9\u8346\u53cc\u773c\u8840\u7ea2\uff0c\u672a\u5f85\u6842\u4ef2\u660e\u811a\u843d\u5b9e\u5730\uff0c\u5fd9\u7528\u201c\u5929\u9b54\u6756\u6cd5\u201d\u4e2d\u7684\u7edd\u62db\uff0c \u201c\u62ab\u661f\u8d76\u6708\u201d\uff0c\u659c\u659c\u4e00\u8dc3\uff0c\u624b\u4e2d\u90a3\u622a\u77ed\u6756\u5b9b\u5982\u94f6\u86c7\u4e71\u51fb\uff0c\u7adf\u5411\u6842\u4ef2\u660e\u4e39\u7530\u7a74\u6253\u6765\u3002\u6842\u4ef2\u660e\u5251 \u62db\u672a\u6536\uff0c\u8feb\u5f97\u8fde\u8fd0\u7edd\u9876\u8f7b\u529f\uff0c\u5c06\u5251\u4e00\u65cb\uff0c\u5251\u5c16\u70b9\u7740\u6756\u5934\uff0c\u4fbf\u85c9\u7740\u8fd9\u4e00\u70b9\u4e4b\u529b\uff0c\u5411\u540e\u5012\u7eb5\u51fa \u53bb\u3002\u5192\u6d63\u83b2\u60ca\u547c\u58f0\u4e2d\uff0c\u4ed6\u5df2\u5012\u7ffb\u5728\u5de6\u4fa7\u4e00\u5c0a\u4f5b\u50cf\u4e4b\u65c1\uff0c\u6536\u52bf\u4e0d\u53ca\uff0c\u624b\u4e2d\u5251\u78b0\u7740\u4f5b\u50cf\u7684\u624b\u91cc\uff0c \u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u7adf\u628a\u4f5b\u6a61\u7684\u624b\u81c2\u5207\u4e86\u4e0b\u6765\u3002\u624b\u81c2\u8dcc\u4e0b\uff0c\u53d1\u51fa\u91d1\u5149\uff0c\u6842\u4ef2\u660e\u4f4e\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u7adf \u662f\u5916\u9762\u5305\u7740\u94c1\u76ae\u7684\u8d64\u91d1\u3002\u4e0d\u7981\u53eb\u9053\uff1a\u201c\u8fd9\u4e9b\u662f\u91d1\u7f57\u6c49\uff01\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u97e9\u8346\u54c8\u54c8\u5927\u7b11\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u7684\uff0c\u5341\u516b\u5c0a\u7f57\u6c49\u90fd\u662f\u9ec4\u91d1\u94f8\u6210\uff0c\u4f46\u8fd9\u662f\u6709\u4e3b\u4e4b \u7269\uff0c\u4f60\u4eec\u89ca\u89ce\uff0c\u90a3\u53ef\u4e0d\u6210\uff01\u201d\u51cc\u672a\u98ce\u559d\u9053\uff1a\u201c\u8c01\u662f\u4e3b\u4eba\uff1f\u201d\u97e9\u8346\u6307\u7740\u81ea\u5df1\u7684\u9f3b\u5b50\u8bf4\u9053\uff1a\u201c\u5c31 \u662f\u54b1\u5bb6\uff0c\u4f60\u4eec\u7ed9\u6211\u6eda\u51fa\u6d1e\u53bb\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u51b7\u7b11\u4e00\u58f0\uff0c\u8d70\u4e86\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u770b\u4f60\u8fd9\u9a7c\u80cc\u8001\u513f\u8d22\u8ff7\u5fc3\u7a8d\uff0c\u6211\u4eec\u53ef\u4ee5\u5206\u7ed9\u4f60\u51e0\u4e24 \u4e70\u68fa\u6750\u7684\u672c\u94b1\uff01\u201d\u97e9\u8346\u5927\u6012\uff0c\u770b\u51cc\u672a\u98ce\u8d70\u8fc7\uff0c\u7a81\u7136\u4f38\u624b\u5f80\u4e3b\u5ea7\u4f5b\u50cf\u4e00\u63a8\uff0c\u90a3\u4f5b\u50cf\u6447\u6447\u6446\u6446\uff0c \u4fbf\u5f85\u540e\u5012\u3002\u51cc\u672a\u98ce\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u4e00\u6321\uff0c\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u4f5b\u50cf\u8dcc\u843d\u5730\u4e0a\u3002\u97e9\u8346\u53c8\u662f\u5927\u5403\u4e00 \u60ca\uff0c\u4ed6\u672c\u60f3\u628a\u4f5b\u50cf\u63a8\u5012\uff0c\u8c01\u77e5\u5374\u6c14\u529b\u4e0d\u591f\uff0c\u51cc\u672a\u98ce\u8fd9\u4e00\u53cd\u63a8\u4e4b\u529b\uff0c\u6bd4\u4ed6\u5f3a\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u4f5b\u50cf\u5012\u540e\uff0c\u5ea7\u4e0b\u73b0\u51fa\u4e00\u53ea\u9526\u76d2\uff0c\u51cc\u672a\u98ce\u6253\u5f00\u9526\u76d2\uff0c\u62ff\u51fa\u4e00\u5f20\u4fe1\u7b3a\uff0c\u6842\u4ef2\u660e\u4ed7\u5251\u7eb5\u4e86\u8fc7 \u6765\uff0c\u5b88\u5728\u4ed6\u7684\u8eab\u8fb9\uff0c\u817e\u86df\u5251\u5149\u8292\u56db\u5c04\uff0c\u97e9\u8346\u62ff\u7740\u88ab\u622a\u77ed\u4e86\u7684\u62d0\u6756\uff0c\u8f7b\u8f7b\u5598\u6c14\uff0c\u4e0d\u6562\u8d70\u8fd1\u3002\u4ed6 \u770b\u770b\u8fbe\u571f\u53f8\uff0c\u8fbe\u58eb\u53f8\u5374\u51b7\u51b7\u5730\u7ad9\u5728\u5f53\u4e2d\uff0c\u5e76\u65e0\u51fa\u624b\u4e4b\u610f\u3002 \u3000\u3000\u51cc\u672a\u98ce\u62ff\u8d77\u4fe1\u7b3a\u4e00\u770b\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u9053\uff1a\u201c\u4e59\u9149\u4e4b\u5e74\uff0c\u5b5f\u79cb\u4e4b\u6708\uff0c\u5927\u76d7\u79fb\u56fd\uff0c\u5b97\u5ba4\u5357\u8fc1\uff0c \u6ec7\u8fb9\u5954\u547d\uff0c\u6709\u53bb\u65e0\u74dc\u4e2d\u5174\u4e4b\u671b\uff0c\u671f\u4e8e\u540e\u4e00\u4e16\uff0c\u5b9a\u56fd\u5949\u5927\u897f\u738b\u4e4b\u9057\u547d\u4e0e\u6c38\u5386\u5e1d\u4e4b\u5fa1\u65e8\uff0c\u4ee5\u9ec4\u91d1 \u5341\u4e07\u516b\u5343\u65a4\uff0c\u94f8\u6210\u5341\u516b\u7f57\u6c49\uff0c\u85cf\u4e8e\u6b64\u6d1e\u3002\u7559\u5f85\u8c6a\u6770\u4e4b\u58eb\uff0c\u4ee5\u4e3a\u590d\u56fd\u4e4b\u8d44\u3002\u82e5\u6709\u53d6\u4f5c\u79c1\u7528\u8005\uff0c \u4eba\u5929\u5171\u8bdb\u3002\u201d \u3000\u3000\u8fd9\u6279\u9ec4\u91d1\u6b63\u662f\u674e\u5b9a\u56fd\u9003\u5954\u7f05\u7538\u4e4b\u524d\uff0c\u9063\u6842\u5929\u6f9c\u5efa\u6d1e\u6536\u85cf\u7684\u3002\u5927\u76d7\u6307\u7684\u662f\u5434\u4e09\u6842\uff0c\u5927\u897f\u738b \u5219\u662f\u5f20\u732e\u5fe0\u7684\u738b\u53f7\uff0c\u6c38\u5386\u5e1d\u5c31\u662f\u540e\u6765\u5434\u4e09\u6842\u8ffd\u5230\u7f05\u7538\u64d2\u6740\u7684\u6842\u738b\u6731\u7531\u6994\uff08\u5d07\u5e27\u65f6\u5c01\u6c38\u660e\u738b\uff0c \u660e\u795e\u5b97\u4e4b\u5b59\uff09\u3002\u674e\u5b9a\u56fd\u539f\u662f\u5f20\u732e\u5fe0\u624b\u4e0b\u7684\u5927\u5c06\u3001\u540e\u6765\u5949\u6842\u738b\u4e3a\u5e1d\u6297\u6e05\u7684\u3002\u5f20\u732e\u5fe0\u5728\u6e83\u8d25\u4e4b \u65f6\uff0c\u4e00\u6012\u4e4b\u4e0b\uff0c\u5c06\u91d1\u94f6\u73e0\u5b9d\u6c89\u843d\u5ddd\u6c5f\uff0c\u5176\u65f6\uff0c\u5c1a\u6709\u51e0\u4e07\u65a4\u91d1\u7816\u5728\u674e\u5b9a\u56fd\u519b\u4e2d\uff0c\u5f20\u732e\u5fe0\u9a70\u4e66\u53eb \u4ed6\u5c06\u9ec4\u91d1\u6bc1\u706d\uff0c\u4ed6\u4e0d\u80af\u5949\u6b64\u4e71\u547d\uff0c\u9063\u4f7f\u56de\u62a5\uff0c\u529b\u9648\u5e94\u8be5\u4fdd\u5b58\u8fd9\u6279\u9ec4\u91d1\uff0c\u5176\u5b9e\u5f20\u732e\u5fe0\u5df2\u662f\u5175\u8d25 \u53d7\u4f24\uff0c\u5944\u5944\u4e00\u606f\uff0c\u95fb\u8a00\u5bf9\u6765\u4eba\u8bf4\u9053\uff1a\u201c\u54b1\u8001\u5b50\u672c\u8981\u5929\u4e0b\u8d22\u5bcc\u4e0e\u6211\u540c\u5f52\u4e8e\u5c3d\uff0c\u674e\u5b9a\u56fd\u8fd9\u5c0f\u5b50\u5374 \u628a\u8fd9\u70b9\u70b9\u9ec4\u91d1\uff0c\u770b\u5f97\u5982\u6b64\u91cd\u8981\uff0c\u4f60\u56de\u53bb\u544a\u8bc9\u4ed6\uff0c\u4e0d\u6bc1\u6389\u4e5f\u884c\uff0c\u4f46\u4e0d\u80fd\u8ba9\u654c\u4eba\u5f97\u53bb\u3002\u201d\u5f20\u732e\u5fe0 \u6c89\u5728\u5ddd\u6c5f\u7684\u91d1\u94f6\u73e0\u5b9d\uff0c\u6bd4\u8fd9\u6279\u9ec4\u91d1\u7684\u4ef7\u503c\uff0c\u4e0d\u77e5\u9ad8\u51fa\u591a\u5c11\u500d\u3002\u4ed6\u54ea\u91cc\u5c06\u8fd9\u70b9\u70b9\u4e1c\u897f\u770b\u5728\u773c \u5185\uff0c\u56e0\u6b64\u5bf9\u674e\u5b9a\u56fd\u7684\u201c\u6297\u547d\u201d\uff0c\u4e5f\u5c31\u7b97\u4e86\u3002\u5426\u5219\u7167\u4ed6\u7684\u6027\u683c\uff0c\u54ea\u5bb9\u5f97\u674e\u5b9a\u56fd\u4e0d\u4f9d\u3002 \u3000\u3000\u674e\u5b9a\u56fd\u62e5\u7acb\u6c38\u5386\u5e1d\u4e4b\u540e\uff0c\u53c8\u88ab\u5434\u4e09\u6842\u5927\u519b\u4e00\u8def\u8ffd\u51fb\u3002\u6c38\u5386\u81ea\u77e5\u590d\u56fd\u65e0\u671b\uff0c\u53c8\u5c06\u6240\u85cf\u7684\u9ec4 \u91d1\u51e0\u4e07\u65a4\uff0c\u4ea4\u7ed9\u674e\u5b9a\u56fd\u53eb\u4ed6\u8bbe\u6cd5\u6536\u85cf\u3002\u4e24\u9876\u4e00\u5171\u5341\u4e07\u516b\u5343\u65a4\uff0c\u674e\u5b9a\u56fd\u4e8e\u662f\u6311\u9009\u5fc3\u8179\u4e09\u767e\u4eba\uff0c \u6bcf\u4eba\u732e\u8840\u7acb\u8a93\uff0c\u8a93\u4e0d\u6cc4\u6f0f\uff0c\u8fd9\u4e09\u767e\u4eba\u5c31\u4ea4\u7531\u6842\u5929\u6f9c\u7387\u9886\uff0c\u79d8\u5bc6\u5c06\u9ec4\u91d1\u8fd0\u8fdb\u5c71\u8c37\uff0c\u5728\u6d1e\u4e2d\u94f8\u6210 \u5341\u516b\u7f57\u6c49\u3002 \u3000\u3000\u6842\u5929\u6f9c\u7cfb\u76d1\u7763\u5de5\uff0c\u4e00\u9762\u8f9f\u6d1e\uff0c\u4e00\u9762\u94f8\u50cf\uff0c\u8bb8\u591a\u5de5\u5320\u5df2\u9063\u56de\u519b\u4e2d\uff0c\u6700\u540e\u53ea\u5269\u4e0b\u516d\u4e03\u4e2a\u5de7 \u5320\uff0c\u5728\u91cc\u9762\u5e03\u7f6e\u673a\u5173\uff0c\u8d3a\u4e07\u65b9\u5c31\u662f\u53c2\u4e0e\u5176\u4e8b\u7684\u5de7\u5320\u4e4b\u4e00\uff0c\u800c\u9a7c\u80cc\u8001\u4eba\u97e9\u8346\u5219\u662f\u6842\u5929\u6f9c\u7684\u526f \u624b\u3002\u5230\u5de5\u7a0b\u63a5\u8fd1\u5b8c\u6210\u4e4b\u9645\uff0c\u6842\u5929\u6f9c\u8fde\u97e9\u8346\u90fd\u5dee\u9063\u56de\u53bb\uff0c\u4e0d\u8ba9\u4ed6\u77e5\u9053\u673a\u5173\u79d8\u5bc6\uff0c\u5f53\u65f6\u97e9\u8346\u5fc3\u91cc \u5c31\u4e0d\u5927\u8212\u670d\uff0c\u4f46\u53c8\u4e0d\u80fd\u8bf4\u51fa\u6765\uff0c\u8fd9\u6c14\u5df2\u95f7\u4e86\u4e8c\u5341\u591a\u5e74\u3002 \u3000\u3000\u5341\u4e07\u516b\u5343\u65a4\u9ec4\u91d1\u85cf\u597d\u4e4b\u540e\uff0c\u6842\u5929\u6f9c\u548c\u5de7\u5320\u4e5f\u56de\u5230\u519b\u4e2d\uff0c\u7ecf\u8fc7\u8fde\u5e74\u6fc0\u6218\uff0c\u76f4\u8ffd\u5230\u7f05\u7538\uff0c\u674e \u5b9a\u56fd\u7684\u4e09\u767e\u4eb2\u4fe1\u5269\u4e0b\u7684\u5df1\u5be5\u5be5\u65e0\u51e0\u3002\u674e\u5b9a\u56fd\u4e00\u6b7b\uff0c\u8fd9\u4e9b\u4eba\u4e5f\u5c31\u661f\u6563\u4e86\u3002 \u3000\u3000\u6842\u5929\u6f9c\u5949\u9057\u547d\uff0c\u9690\u5251\u9601\uff0c\u4e00\u4e3a\u907f\u6e05\u5ef7\u641c\u7d22\uff0c\u4e8c\u4e3a\u4fdd\u62a4\u85cf\u91d1\u3002\u56e0\u4ed6\u66fe\u732e\u8840\u7acb\u8a93\uff0c\u6240\u4ee5\u5728\u672a \u6b7b\u4e4b\u524d\uff0c\u8fde\u77f3\u5927\u5a18\u4e5f\u4e0d\u544a\u77e5\uff0c\u8fd9\u6837\u5e74\u590d\u4e00\u5e74\uff0c\u6d41\u5149\u5982\u5931\uff0c\u773c\u89c1\u6e05\u5ef7\u5df2\u62b5\u5b9a\u4e2d\u539f\uff0c\u5404\u5730\u7684\u96f6\u661f \u4e49\u519b\u53c8\u672a\u6210\u6c14\u5019\uff0c\u6842\u5929\u6f9c\u6781\u76ee\u5c71\u5ddd\uff0c\u5fc3\u4f24\u901d\u8005\uff0c\u629a\u5fa1\u5174\u53f9\uff0c\u590d\u56fd\u96be\u671f\u3002\u56e0\u6b64\u5728\u9ec4\u886b\u4e0a\u7559\u4e0b\u9690 \u5f62\u56fe\u753b\uff0c\u539f\u60f3\u4f8d\u6842\u4ef2\u660e\u957f\u5927\u4e4b\u540e\uff0c\u5c06\u79d8\u5bc6\u544a\u8bc9\u4ed6\uff0c\u8ba9\u4ed6\u53bb\u95ef\u8361\u6c5f\u6e56\uff0c\u56fe\u8c0b\u590d\u56fd\uff0c\u65e5\u540e\u597d\u6309\u56fe \u7d22\u9aa5\uff0c\u53d6\u51fa\u85cf\u91d1\uff0c\u5374\u4e0d\u6599\u5e73\u7a7a\u63d2\u8fdb\u77f3\u5929\u6210\u8fd9\u6bb5\u6069\u6028\u98ce\u6ce2\uff0c\u6842\u4ef2\u660e\u5f03\u5bb6\u8fdc\u8d70\uff0c\u6842\u5929\u6f9c\u4e5f\u60e8\u6b7b\u8352 \u5c71\u3002 \u3000\u3000\u518d\u8bf4\u97e9\u8346\uff0c\u81ea\u674e\u5b9a\u56fd\u6b7b\u540e\u9690\u5c45\u5ddd\u4e1c\uff0c\u4e8c\u5341\u591a\u5e74\uff0c\u4e5f\u7ec3\u5c31\u4e00\u8eab\u6280\u4e1a\uff0c\u9690\u9690\u6210\u4e86\u5ddd\u4e1c\u7684\u6b66\u6797 \u4e4b\u96c4\uff0c\u5404\u8def\u6b66\u6797\u9ad8\u624b\uff0c\u5bf9\u4ed6\u90fd\u5f88\u5c0a\u656c\u3002\u4ed6\u672c\u6765\u5df2\u65e0\u610f\u518d\u56fe\u5927\u4e8b\uff0c\u4e5f\u4e0d\u60f3\u5077\u53d6\u85cf\u91d1\u3002\u4e0d\u6599\u5f53\u65e5 \u53c2\u4e0e\u5176\u4e8b\u7684\u4e00\u4e2a\u5de5\u5320\u5227\u540e\u4f59\u751f\uff0c\u51e0\u7ecf\u8270\u82e6\u6295\u5230\u7709\u5c71\u5be8\u4e3b\u7f57\u8fbe\u624b\u4e0b\uff0c\u7adf\u7136\u8d77\u4e86\u8d2a\u5ff5\uff0c\u5c06\u85cf\u91d1\u4e4b \u4e8b\u544a\u8bc9\u7f57\u8fbe\uff0c\u7eb5\u607f\u4ed6\u53bb\u53d6\uff0c\u5e76\u544a\u8bc9\u4ed6\uff0c\u97e9\u8346\u5c31\u662f\u5f53\u65e5\u7684\u4e3b\u4e8b\u4eba\u4e4b\u4e00\u3002\u7f57\u8fbe\u542c\u4e86\u5927\u559c\uff0c\u4eb2\u81ea\u62dc \u95e8\uff0c\u6c42\u97e9\u8346\u76f8\u52a9\u3002\u4ed6\u7684\u8bf4\u6cd5\u975e\u5e38\u5de7\u5999\uff0c\u4e00\u9762\u6fc0\u8d77\u97e9\u8346\u82f1\u96c4\u5782\u845b\u4e4b\u5fc3\uff0c\u53eb\u4ed6\u53d6\u51fa\u91d1\u6765\uff0c\u597d\u5728\u6b66 \u6797\u79f0\u9738\uff1b\u4e00\u9762\u6311\u5506\u4ed6\u4e0e\u6842\u5929\u6f9c\u51b3\u4e00\u96cc\u96c4\uff0c\u4ee5\u589e\u4ed6\u7684\u6b66\u6797\u58f0\u671b\u3002\u97e9\u8346\u672c\u6765\u662f\u4e00\u4e2a\u5fc3\u9ad8\u6c14\u50b2\u7684 \u4eba\uff0c\u4e34\u8001\u7cca\u6d82\uff0c\u60f3\u8d77\u8fd9\u6279\u9ec4\u91d1\u53cd\u6b63\u5df2\u65e0\u4e3b\u4eba\uff0c\u81ea\u5df1\u53d6\u6765\uff0c\u7acb\u523b\u5bcc\u53ef\u654c\u56fd\uff0c\u7adf\u7136\u4e5f\u8d77\u4e86\u8d2a\u5ff5\uff0c \u548c\u7f57\u8fbe\u505a\u4e86\u4e00\u8def\uff0c\u5e76\u4e14\u53e6\u5916\u9080\u7ea6\u4e24\u4e2a\u9ad8\u624b\uff0c\u51c6\u5907\u53bb\u5bf9\u4ed8\u6842\u5929\u6f9c\u3002 \u3000\u3000\u4e8b\u60c5\u867d\u79d8\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5374\u4e5f\u6f0f\u51fa\u6765\uff0c\u56db\u5ddd\u6b66\u529f\u6700\u5f3a\u7684\u51e0\u4e2a\u6b66\u6797\u4eba\u7269\uff0c\u7adf\u4e0d\u7ea6\u800c\u540c\u5730\u5230\u4e86 \u5251\u9601\uff0c\u8fd9\u4e9b\u4eba\u548c\u7f57\u8fbe\u4e00\u6837\uff0c\u54ea\u91cc\u6709\u4ec0\u4e48\u5927\u5fd7\uff0c\u53ea\u662f\u60f3\u593a\u53d6\u91cd\u91d1\u3002 \u3000\u3000\u81f3\u4e8e\u90a3\u67c4\u817e\u86df\u5b9d\u5251\uff0c\u4e5f\u662f\u674e\u5b9a\u56fd\u4e34\u6b7b\u65f6\u4ea4\u7ed9\u6842\u5929\u6f9c\uff0c\u53eb\u4ed6\u4ee3\u4e3a\u6536\u85cf\uff0c\u7559\u8d60\u82f1\u8c6a\u7684\u3002\u6842\u5929 \u6f9c\u5c31\u628a\u5b83\u7cfb\u5728\u4e3b\u5ea7\u4f5b\u50cf\u8170\u95f4\uff0c\u4f5c\u4e3a\u8170\u5e26\u3002\u4ed6\u4e3a\u4e86\u7eaa\u5ff5\u674e\u5b9a\u56fd\uff0c\u628a\u8fd9\u5ea7\u4f5b\u50cf\u5851\u6210\u674e\u5b9a\u56fd\u7684\u76f8 \u8c8c\u3002\u90a3\u5b9d\u5251\u65e0\u5de7\u4e0d\u5de7\uff0c\u4e5f\u843d\u5728\u6842\u4ef2\u660e\u624b\u4e2d\u3002 \u3000\u3000\u51cc\u672a\u98ce\u770b\u5b8c\u674e\u5b9a\u56fd\u9057\u4e66\u4e4b\u540e\uff0c\u5bf9\u85cf\u91d1\u6765\u5386\u5df2\u662f\u660e\u4e86\uff0c\u4e8e\u662f\uff0c\u5bf9\u7740\u97e9\u8346\u563f\u7684\u4e00\u58f0\u51b7\u7b11\uff0c\u61d2 \u6d0b\u6d0b\u9053\uff1a\u201c\u5931\u656c\uff0c\u5931\u656c\uff0c\u4f60\u539f\u6765\u662f\u8fd9\u6279\u9ec4\u91d1\u7684\u4e3b\u4eba\uff1f\u90a3\u4e48\u4f60\u5c31\u662f\u674e\u5b9a\u56fd\u5c06\u519b\u4e86\uff1f\u6211\u65e9\u5c31\u542c \u8bf4\uff0c\u674e\u5b9a\u56fd\u5df2\u5ba2\u6b7b\u7f05\u7538\uff0c\u60f3\u4e0d\u5230\u4ed6\u5c45\u7136\u8fd8\u6d3b\u5728\u4eba\u95f4\uff01\u201d \u3000\u3000\u97e9\u8346\u6ee1\u9762\u5df1\u901a\u7ea2\uff0c\u6012\u9053\uff1a\u201c\u662f\u674e\u5b9a\u56fd\u7684\uff0c\u4e5f\u4e0d\u662f\u4f60\u7684\uff0c\u6211\u548c\u674e\u5b9a\u56fd\u540c\u751f\u5171\u6b7b\u7684\u65f6\u5019\uff0c\u4f60 \u8fd9\u5a03\u5a03\u8fd8\u5728\u5403\u5976\u3002\u600e\u4e48\u6837\u8bf4\uff0c\u6211\u548c\u674e\u5b9a\u56fd\u90fd\u6cbe\u4e0a\u4e00\u70b9\u8fb9\uff0c\u4f60\u7b97\u8001\u51e0\uff1f\u201d\u51cc\u672a\u98ce\u563b\u563b\u7b11\u9053\uff1a \u201c\u66fe\u548c\u674e\u5b9a\u56fd\u540c\u751f\u5171\u6b7b\u90a3\u66f4\u597d\u4e86\uff0c\u4f60\u5f53\u7136\u77e5\u9053\u4ed6\u7684\u610f\u601d\u3002\u201d\u97e9\u8346\u534a\u6839\u77ed\u6756\u5411\u51cc\u672a\u98ce\u9aa4\u7684\u63b7 \u53bb\uff0c\u75be\u559d\u9053\uff1a\u201c\u51ed\u4f60\u60f3\u4f38\u624b\u62e6\u963b\uff0c\u90a3\u53ef\u4e0d\u884c\u3002\u201d\u51cc\u672a\u98ce\u626c\u624b\u5c31\u662f\u4e00\u9053\u4e4c\u91d1\u5149\u8292\uff0c\u628a\u90a3\u6839\u77ed\u6756 \u6fc0\u5c04\u5f97\u76f4\u98de\u56de\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u662f\u8981\u62e6\u4f60\uff01\u201d\u97e9\u8346\u614c\u5fd9\u4fa7\u8eab\u4e00\u95ea\uff0c\u5c06\u77ed\u6756\u63a5\u56de\u624b\u4e2d\uff0c\u53ea\u89c1\u6756 \u5934\u5d4c\u7740\u4e94\u516d\u5bf8\u957f\u7684\u4e00\u6839\u4f3c\u7bad\u975e\u7bad\u7684\u4e1c\u897f\uff0c\u53c8\u662f\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u8fd9\u5c0f\u5b50\u5c45\u7136\u51ed\u7740\u5982\u6b64\u7ec6\u5c0f\u7684\u6697 \u5668\uff0c\u5c31\u80fd\u5c06\u6211\u7684\u534a\u622a\u9f99\u5934\u62d0\u6756\u53cd\u649e\u56de\u6765\uff0c\u8fd9\u529f\u529b\u771f\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u548c\u4ed6\u6bd4\u5212\uff0c\u8981\u8d62\u4ed6\u5927\u7ea6\u662f\u5f88 \u96be\u4e86\uff0c\u53ea\u662f\u81ea\u5df1\u4e43\u662f\u6b66\u6797\u4e2d\u9876\u5c16\u513f\u7684\u4eba\u7269\uff0c\u5982\u4f55\u54bd\u5f97\u4e0b\u8fd9\u53e3\u6c14\u3002\u51cc\u672a\u98ce\u53eb\u9053\uff1a\u201c\u4f60\u60f3\u62ff\u9ec4\u91d1 \u5c31\u8fc7\u6765\uff01\u201d\u5c06\u9752\u94a2\u5251\u5728\u624b\u4e2d\u629b\u4e86\u4e24\u629b\uff0c\u813e\u8155\u659c\u89c6\u3002\u6842\u4ef2\u660e\u4e5f\u4ed7\u817e\u86df\u5b9d\u5251\uff0c\u7acb\u5728\u51cc\u672a\u98ce\u8eab\u65c1\u3002 \u3000\u3000\u8d3a\u4e07\u65b9\u662f\u59cb\u7ec8\u53c2\u4e0e\u85cf\u91d1\u4e4b\u4e8b\u7684\u4eba\uff0c\u4ed6\u77e5\u9053\u6bcf\u5ea7\u91d1\u7f57\u6c49\u91cd\u516d\u5343\u56db\u767e\u65a4\uff0c\u516d\u5343\u65a4\u662f\u8d64\u91d1\uff0c\u56db \u767e\u65a4\u662f\u94c1\u76ae\uff0c\u97e9\u8346\u53ea\u80fd\u5c06\u7f57\u6c49\u6447\u52a8\uff0c\u51cc\u672a\u98ce\u5374\u80fd\u628a\u7f57\u6c49\u63a8\u5012\u5730\u4e0a\uff0c\u770b\u6765\u5df2\u662f\u80dc\u4e86\u4e00\u7b79\u3002\u5f53\u4e0b \u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u8981\u6bd4\u5212\u4e5f\u4e0d\u80fd\u5728\u6d1e\u4e2d\u6bd4\u5212\uff0c\u8fd9\u91cc\u9762\u904d\u662f\u673a\u5173\u3002\u8fd8\u662f\u5230\u5916\u9762\u53bb\u770b\uff0c\u6570\u6d77\u5e95\uff0c\u8bb2\u89c4 \u7ae0\uff0c\u4f5c\u4e2a\u4e86\u65ad\u5427\uff01\u201d\u201c\u6570\u6d77\u5e95\u201d\u662f\u9ed1\u9053\u4e2d\u7684\u5207\u53e3\uff0c\u6b66\u6797\u4e2d\u4eba\u7269\u6709\u7ea0\u7eb7\u4e4b\u65f6\uff0c\u5c06\u81ea\u5df1\u7684\u6765\u5386\u3001 \u76ee\u7684\u3001\u8981\u6c42\u7b49\u4e00\u8bb2\u51fa\u6765\uff0c\u53eb\u505a\u201c\u6570\u6d77\u5e95\u201d\u3002\u8d3a\u4e07\u65b9\u8fd9\u8bdd\u662f\u60f3\u8bf7\u51cc\u672a\u98ce\u4ed6\u4eec\u5230\u5916\u9762\u53bb\u597d\u597d\u5546 \u91cf\u3002\u8fbe\u571f\u53f8\u9053\uff1a\u201c\u5bf9\u5440\uff01\u4f55\u5fc5\u4e3a\u8fd9\u70b9\u9ec4\u91d1\u4f24\u4e86\u548c\u6c14\uff0c\u5230\u5916\u9762\u53bb\u8bf7\u6b66\u6797\u540c\u9053\u5171\u8bae\uff0c\u4e00\u7897\u6c34\u5927\u5bb6 \u5206\u6765\u559d\u5c31\u662f\u5566\uff01\u201d\u5176\u5b9e\u8fbe\u571f\u53f8\u4f55\u5c1d\u60f3\u5c06\u9ec4\u91d1\u5206\u7ed9\u51cc\u672a\u98ce\uff0c\u53ea\u662f\u4ed6\u89c1\u51cc\u3001\u6842\u4e8c\u4eba\uff0c\u90fd\u662f\u624e\u624b\u7684 \u52b2\u654c\uff0c\u5fc3\u60f3\uff0c\u82e5\u5728\u6d1e\u4e2d\u52a8\u624b\uff0c\u81ea\u5df1\u8fd9\u8fb9\u51c6\u5904\u4e0b\u98ce\uff0c\u4e0d\u5982\u5230\u5916\u9762\u518d\u8bf4\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5c06\u9752\u94a2\u5251\u63d2\u5165\u9798\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u7740\u5440\uff01\u8981\u6253\u67b6\u4e5f\u5f97\u627e\u4e2a\u597d\u5730\u65b9\uff0c\u5230\u5916\u8fb9\u53bb\u5427\u3002 \u8bf7\uff01\u201d\u97e9\u8346\u4e00\u8a00\u4e0d\u53d1\uff0c\u6309\u7740\u201c\u5de6\u4e09\u53f3\u56db\u4e2d\u5341\u4e8c\u201d\u7684\u6b65\u6cd5\uff0c\u5c31\u5411\u6d1e\u53e3\u5954\u51fa\uff0c\u4e00\u884c\u4eba\u8ddf\u7740\u4ed6\u4e5f\u5230 \u5916\u9762\u3002 \u3000\u3000\u516d\u4eba\u8dc3\u8fc7\u6c34\u5e18\uff0c\u8c37\u4e2d\u7fa4\u8c6a\u7eb7\u7eb7\u56f4\u4e0a\uff0c\u4e03\u53e3\u516b\u820c\u63a2\u542c\u7ed3\u679c\uff0c\u8d3a\u4e07\u65b9\u9053\uff1a\u201c\u9ec4\u91d1\u5341\u4e07\u516b\u5343\u65a4 \u5168\u5728\u91cc\u9762\uff0c\u54b1\u4eec\u662f\u8d22\u661f\u7167\u547d\u5566\uff01\u201d\u8fbe\u571f\u53f8\u9053\uff1a\u201c\u9ec4\u91d1\u662f\u6709\u4e86\uff0c\u53ea\u662f\u600e\u4e48\u5206\u6cd5\uff0c\u54b1\u4eec\u53ef\u8fd8\u5f97\u597d \u597d\u8c08\u8c08\u3002\u201d\u5f20\u5143\u632f\u9053\uff1a\u201c\u6211\u4eec\u4e03\u4e2a\u4eba\u90fd\u662f\u65e9\u5df2\u77e5\u9053\u9759\u91d1\u85cf\u5904\uff0c\u7279\u5730\u8d76\u6765\u7684\uff0c\u90a3\u5f53\u7136\u662f\u6709\u4efd \u4e86\uff0c\u4ed6\u4eec\u4e09\u4eba\u561b\u2026\u2026\u201d\u5362\u5927\u695e\u5b50\u622a\u7740\u8bf4\u9053\uff1a\u201c\u51cc\u5927\u4fa0\u7b49\u4e09\u4eba\u5f53\u7136\u4e5f\u6709\u4e00\u4efd\uff0c\u6211\u4eec\u5c31\u6309\u5341\u4efd\u6765 \u5206\u4e86\uff0c\u5927\u5bb6\u90fd\u4e0d\u8981\u4e89\u3002\u201d\u7f57\u8fbe\u7bad\u4f24\u65b9\u6b62\uff0c\u5728\u5730\u4e0a\u547b\u541f\u9053\uff1a\u201c\u6211\u6700\u5148\u8fdb\u6d1e\uff0c\u4e3a\u4e86\u5927\u5bb6\u53d7\u4f24\uff0c\u4f60 \u4eec\u6709\u8a00\u5728\u5148\uff0c\u53ef\u5f97\u7ed9\u6211\u4e24\u4efd\uff01\u201d\u97e9\u8346\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u82e5\u63a2\u51fa\u7ed3\u679c\u90a3\u5f53\u7136\u7ed9\u4f60\u4e24\u4efd\uff0c\u53ef\u662f\u4f60 \u4e00\u8fdb\u53bb\u5c31\u7ed9\u7bad\u5c04\u51fa\u6765\u5566\uff01\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u9ec4\u91d1\u53ef\u4e0d\u80fd\u8fd9\u6837\u5206\u6cd5\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u6115\u7136\u95ee\u9053\uff1a\u201c\u8be5\u600e\u4e48\u4e2a\u5206\u6cd5\uff1f\u201d\u97e9\u8346\u6307\u4e00\u6307\u8d3a\u4e07\u65b9\u9053\uff1a\u201c\u6b64\u91d1\u662f\u6211\u57cb\uff0c\u673a\u5173\u662f\u4ed6 \u8bbe\uff0c\u6211\u4eec\u6bcf\u4eba\u8be5\u5360\u4e24\u4efd\u3002\u4f60\u4eec\u4e94\u4eba\u6bcf\u4eba\u4e00\u4efd\uff0c\u53e6\u5916\u6211\u9080\u6709\u4e24\u4f4d\u597d\u53cb\u4e0e\u8d3a\u8001\u5f1f\u4e00\u8d77\u6765\u7684\uff0c\u867d\u7136 \u5c1a\u672a\u89c1\u5230\uff0c\u4e5f\u8be5\u7b97\u4ed6\u4eec\u4e00\u4efd\u3002\u81f3\u4e8e\u90a3\u8fb9\u4e09\u4f4d\u5ba2\u4eba\u2026\u2026\u201d \u3000\u3000\u4ed6\u6307\u4e00\u6307\u51cc\u672a\u98ce\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u7167\u9053\u4e2d\u89c4\u77e9\uff0c\u53ea\u80fd\u5408\u8d77\u6765\u7b97\u4e00\u4efd\u3002\u4ed6\u4eec\u53ea\u662f\u8bef\u6253\u8bef\u649e \u7684\uff0c\u4e0d\u80fd\u7167\u6211\u4eec\u8fd9\u4e2a\u5206\u6cd5\u3002\u201d \u3000\u3000\u7f57\u8fbe\u542c\u4e86\u5341\u5206\u4e0d\u670d\uff0c\u4ed6\u53d7\u4e86\u7bad\u4f24\uff0c\u53ea\u5206\u5230\u4e00\u4efd\uff0c\u800c\u97e9\u8346\u4e24\u4e2a\u5c1a\u672a\u9732\u9762\u7684\u670b\u53cb\uff0c\u5374\u4e5f\u8981\u5360 \u6709\u4e00\u4efd\u3002\u4f46\u6d41\u8840\u65b9\u6b62\uff0c\u6d51\u8eab\u65e0\u529b\uff0c\u4e0d\u6562\u5f00\u58f0\uff1b\u8fbe\u571f\u53f8\u4e5f\u4e0d\u670d\uff0c\u4ed6\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5374\u7ed9\u5362\u5927\u695e\u5b50\u62a2 \u5728\u5934\u91cc\u8bf4\u9053\uff1a\u201c\u97e9\u5927\u54e5\u548c\u8d3a\u5927\u54e5\u5404\u8981\u4e24\u4efd\uff0c\u90a3\u6211\u4eec\u6ca1\u8bf4\u7684\u3002\u53ea\u662f\u51cc\u5927\u4fa0\u4ed6\u4eec\u4e09\u4eba\uff0c\u5408\u8d77\u6765\u624d \u7b97\u4e00\u4efd\uff0c\u5374\u4e5f\u4e0d\u516c\u5e73\u3002\u4f9d\u6211\u8bf4\uff0c\u65e2\u7136\u662f\u6709\u6c34\u5927\u5bb6\u559d\uff0c\u90a3\u4ed6\u4eec\u4e5f\u8be5\u5404\u5360\u4e00\u4efd\u3002\u81f3\u4e8e\u97e9\u5927\u54e5\u9080\u4e86 \u670b\u53cb\uff0c\u6309\u8bf4\u6ca1\u6709\u9732\u9762\uff0c\u672c\u6765\u96be\u51c6\u4ed6\u4eec\u63d2\u624b\u3002\u4f46\u65e2\u7136\u97e9\u5927\u54e5\u9080\u4e86\u4ed6\u4eec\uff0c\u8fd9\u70b9\u9762\u5b50\u54b1\u4eec\u5f1f\u5144\u53ef\u8fd8 \u8981\u5356\uff0c\u6211\u8bf4\u5c31\u8ba9\u4ed6\u4eec\u5408\u8d77\u6765\u7b97\u4e00\u4efd\u5427\uff0c\u4e00\u5171\u662f\u5341\u4e09\u4efd\u5e73\u5206\u3002\u5927\u5bb6\u4ee5\u4e3a\u5982\u4f55\uff1f\u201d\u7f57\u8fbe\u611f\u6fc0\u51cc\u672a \u98ce\u6551\u547d\u4e4b\u6069\uff0c\u9996\u5148\u9053\u597d\uff1b\u8fbe\u571f\u53f8\u867d\u7136\u4e0d\u662f\u51cc\u672a\u98ce\u4ed6\u4eec\u7684\u5bf9\u624b\uff0c\u4f46\u4ed6\u4eec\u60f3\u6fc0\u6012\u97e9\u8346\u548c\u51cc\u672a\u98ce\u4f5c \u5bf9\uff0c\u5750\u6536\u6e14\u4eba\u4e4b\u5229\uff0c\u56e0\u6b64\u4e5f\u8ddf\u7740\u9053\u597d\uff0c\u97e9\u8346\u4e00\u770b\uff0c\u81ea\u5df1\u8fd9\u8fb9\u5df2\u6709\u4e09\u4e2a\u4eba\u4e3b\u5f20\u51cc\u672a\u98ce\u4ed6\u4eec\u6709\u4efd \u5e73\u5206\uff0c\u5fc3\u4e2d\u53c8\u662f\u4e00\u614c\uff0c\u6697\u60f3\u82e5\u518d\u575a\u6301\uff0c\u4ed6\u4eec\u8054\u8d77\u6863\u6765\uff0c\u81ea\u5df1\u53ef\u5403\u4e0d\u4e86\uff0c\u5f53\u4e0b\u5e72\u7b11\u51e0\u58f0\u9053\uff1a \u201c\u597d\uff0c\u54b1\u4eec\u4e0d\u6253\u4e0d\u76f8\u8bc6\u3002\u94b1\u8d22\u5c0f\u4e8b\uff0c\u4e49\u6c14\u4e3a\u5148\uff0c\u5c31\u7167\u5362\u8235\u4e3b\u8bf4\u7684\uff0c\u5341\u4e09\u4efd\u5206\u5f00\u3002\u201d\u8fbe\u571f\u53f8\u4e00 \u542c\uff0c\u4ed6\u5c45\u7136\u6254\u4e0b\u4e86\u8fd9\u51e0\u53e5\u95e8\u9762\u8bdd\uff0c\u610f\u6b32\u4e0e\u51cc\u672a\u98ce\u5316\u654c\u4e3a\u53cb\uff0c\u5341\u5206\u5931\u671b\uff01 \u3000\u3000\u7eff\u6797\u7fa4\u8c6a\u4e03\u5634\u516b\u820c\u4e89\u8bba\u5206\u91d1\u4e4b\u9645\uff0c\u51cc\u672a\u98ce\u5728\u4e00\u8fb9\u51b7\u773c\u65c1\u89c2\uff0c\u61d2\u6d0b\u6d0b\u7684\u6beb\u4e0d\u5728\u610f\uff0c\u5230\u4e86\u6b64 \u523b\uff0c\u5ffd\u7136\u53cc\u773c\u4e00\u7ffb\uff0c\u970d\u5730\u7ad9\u8d77\uff0c\u559d\u9053\uff1a\u201c\u8c01\u4e0e\u4f60\u8fd9\u6837\u5206\u6cd5\uff1f\u4f60\u4eec\u8fd9\u662f\u81ea\u8bf4\u81ea\u8bdd\u3002\u201d\u97e9\u8346\u8be7\u7136 \u95ee\u9053\uff1a\u201c\u4f9d\u4f60\u8bf4\u53c8\u662f\u600e\u4e48\u4e2a\u5206\u6cd5\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd9\u4e9b\u91d1\u5168\u662f\u6211\u7684\uff0c\u8c01\u60f3\u8981\u5c31\u7740\u6211\u6765\uff01\u201d\u6b64 \u8a00\u4e00\u51fa\uff0c\u4e0d\u4f46\u7fa4\u8c6a\u5931\u8272\uff0c\u5c31\u662f\u6842\u4ef2\u660e\u548c\u5192\u6d63\u83b2\u4e8c\u4eba\u4e5f\u611f\u8be7\u5f02\uff0c\u5fc3\u60f3\uff1a\u600e\u4e48\u51cc\u5927\u4fa0\u4e00\u53cd\u672c\u6027\uff0c \u4e5f\u7231\u8d77\u9ec4\u91d1\u6765\u4e86\u3002\u6842\u4ef2\u660e\u8f7b\u8f7b\u7684\u626f\u4e00\u4e0b\u51cc\u672a\u51e4\u8863\u8896\uff0c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8981\u8fd9\u4e48\u591a\u9ec4\u91d1\u5e72\u4ec0 \u4e48\uff1f\u201d\u51cc\u672a\u98ce\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u522b\u7ba1\u3002\u6211\u8981\u51ed\u6b64\u6279\u9ec4\u91d1\u6536\u4f0f\u8fd9\u73ed\u9b54\u5934\uff0c\u5e72\u4e00\u6869\u5927\u4e8b\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u8981\u72ec\u5360\u85cf\u91d1\uff0c\u8fd9\u771f\u5927\u51fa\u7fa4\u8c6a\u610f\u5916\uff0c\u4ed6\u4eec\u4e00\u65f6\u95f4\u90fd\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u540e\u6765\u53c8\u89c1\u51cc\u672a\u98ce\u548c\u6842 \u4ef2\u660e\u7a83\u7a83\u79c1\u8bed\uff0c\u4ee5\u4e3a\u4e24\u4eba\u662f\u5546\u8bae\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u4e2a\u4e2a\u6124\u6012\uff0c\u5c31\u662f\u5362\u5927\u695e\u5b50\u672c\u6765\u662f\u611f\u6fc0\u51cc\u672a\u98ce\u7684\uff0c \u8fd9\u4e0b\u4e5f\u5f88\u4e0d\u4ee5\u4e3a\u7136\uff0c\u5fc3\u60f3\uff1a\u201c\u5929\u5c71\u795e\u8292\u201d\u539f\u6765\u7adf\u7136\u662f\u865a\u6709\u5176\u540d\uff0c\u89c1\u5229\u5fd8\u4e49\u7684\u5bb6\u4f19\u3002\u4ed6\u4e0d\u5f85\u97e9 \u8346\u8bf4\u8bdd\uff0c\u5c31\u8fc8\u524d\u4e24\u6b65\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u51ed\u4f60\u2018\u5929\u5c71\u795e\u8292\u2019\u7684\u540d\u5934\uff0c\u8981\u9ed1\u767d\u5168\u5403\uff0c\u54b1\u4eec \u672c\u8be5\u9000\u907f\u4e09\u820d\u3002\u600e\u5948\u4f17\u5f1f\u5144\u4eec\u8fdc\u9053\u524d\u6765\uff0c\u51cc\u5927\u4fa0\u8981\u6559\u4ed6\u4eec\u7a7a\u624b\u56de\u53bb\uff0c\u8fd9\u53ef\u6709\u70b9\u8bf4\u4e0d\u8fc7\u53bb\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u8f70\u7136\u53eb\u9053\uff1a\u201c\u662f\u5440\u53ef\u662f\u90a3\u95e8\u89c4\u77e9\uff1f\u201d\u51cc\u672a\u98ce\u7ffb\u7740\u767d\u75b9\u7629\u7684\u773c\u73e0\uff0c\u201c\u54e9\u201d\u7684\u4e00\u58f0\u7b11 \u9053\uff1a\u201c\u8fd9\u662f\u4f60\u4eec\u9ed1\u9053\u7684\u89c4\u77e9\u3002\u9ec4\u91d1\u662f\u6211\u4eec\u5148\u53d1\u73b0\u7684\uff0c\u4e00\u7897\u6c34\u662f\u4e0d\u662f\u5206\u6765\u559d\uff0c\u90a3\u53ef\u5f97\u7531\u6211\u4f5c \u4e3b\uff01\u201d\u7eff\u6797\u4e2d\u62a2\u8d22\u7269\u4e4b\u65f6\uff0c\u82e5\u6709\u53e6\u5916\u7684\u540c\u9053\u4e2d\u4eba\u649e\u4e0a\uff0c\u6309\u89c4\u77e9\u4ed6\u4eec\u53ef\u8981\u6c42\u5206\u8d43\uff0c\u89c1\u8005\u6709\u4efd\u3002 \u4e0d\u8fc7\u8fd9\u53ef\u5f97\u5f81\u6c42\u5148\u5728\u573a\u8005\u7684\u540c\u610f\u3002\u82e5\u4ed6\u4eec\u4e0d\u540c\u610f\uff0c\u8981\u6c42\u5206\u8d43\u8005\u53c8\u4e0d\u80af\u7f29\u624b\u7684\u8bdd\uff0c\u90a3\u5c31\u53ea\u6709\u6b66 \u529b\u89e3\u51b3\u4e86\u3002\u6240\u4ee5\u6b66\u6797\u4e2d\u8981\u6c42\u89c1\u8005\u6709\u4efd\u548c\u539f\u5148\u5728\u573a\u8005\u7684\u62d2\u7edd\u5206\u8d43\uff0c\u90fd\u4e0d\u7b97\u4e0d\u5408\u89c4\u77e9\u3002\u51cc\u672a\u51e4\u6b64 \u8a00\uff0c\u5206\u660e\u662f\u5411\u7fa4\u8c6a\u6311\u6218\u3002 \u3000\u3000\u5362\u5927\u695e\u5b50\u7ed9\u51cc\u672a\u98ce\u7684\u8bdd\u6a2a\u91cc\u4e00\u622a\uff0c\u5012\u89c9\u96be\u4e8e\u5f00\u53e3\uff0c\u4ed6\u867d\u4e0d\u670d\u51cc\u672a\u98ce\u8981\u5f3a\u884c\u541e\u5360\uff0c\u4f46\u53c8\u4e0d \u613f\u4e0e\u51cc\u672a\u98ce\u771f\u4e2a\u53ae\u62fc\uff0c\u5f53\u4e0b\u9000\u8fc7\u4e00\u8fb9\uff0c\u9ed8\u7136\u4e0d\u8bed\uff0c\u97e9\u8346\u4e0e\u8fbe\u4e0a\u53f8\u6c14\u5f97\u53cc\u773c\u901a\u7ea2\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a \u201c\u90a3\u4e48\u54b1\u4eec\u53ea\u597d\u89c1\u4e2a\u771f\u7ae0\u4e86\uff0c\u4f60\u5212\u51fa\u9053\u6765\uff01\u201d\u51cc\u672a\u51e4\u9053\uff1a\u201c\u8fd9\u6279\u9ec4\u91d1\u73b0\u5728\u5168\u7b97\u662f\u6211\u7684\uff0c\u4f60\u4eec \u8c01\u8981\uff0c\u5c31\u6765\u548c\u6211\u6bd4\u8bd5\u3002\u4e0d\u8bba\u6bd4\u90a3\u79cd\u6280\u4e1a\uff0c\u6211\u90fd\u5949\u966a\u3002\u54b1\u4eec\u8fd9\u662f\u8d4c\u6280\u593a\u91d1\uff0c\u6bcf\u6837\u6280\u4e1a\u8d4c\u6ce8\u90fd\u662f \u4e00\u5c0a\u7f57\u6c49\uff0c\u8d62\u4e86\u7684\u5c31\u662f\u4f60\u4eec\u7684\u8d4c\u672c\uff0c\u53ef\u4ee5\u52a0\u6ce8\u518d\u8d4c\u3002\u4f60\u4eec\u82e5\u80af\u8fd9\u6837\u8d4c\u6cd5\uff0c\u6211\u5c31\u4e00\u4e2a\u4eba\u5168\u63a5\u4e0b \u6765\uff0c\u4f60\u4eec\u82e5\u8981\u7fa4\u6bb4\uff0c\u90a3\u6211\u4eec\u4e09\u4eba\u4e5f\u53ef\u5949\u966a\u3002\u201d \u3000\u3000\u97e9\u8346\u5fc3\u60f3\uff1a\u201c\u6211\u4eec\u6bcf\u4eba\u90fd\u6709\u72ec\u95e8\u6b66\u529f\uff0c\u7eb5\u4f60\u51cc\u672a\u98ce\u518d\u5f3a\uff0c\u4e5f\u4e0d\u80fd\u7cbe\u901a\u5404\u5bb6\u6280\u4e1a\u3002\u8fd9\u6837\u8d4c \u6cd5\uff0c\u5012\u6bd4\u7fa4\u6bb4\u8fd8\u4e0a\u7b97\u3002\u201d\u5728\u573a\u7684\u90fd\u662f\u6210\u540d\u4eba\u7269\uff0c\u52bf\u65e0\u4ee5\u4f17\u51cc\u5be1\u4e4b\u7406\uff0c\u800c\u4e14\u82e5\u7136\u7fa4\u6bb4\uff0c\u6842\u4ef2\u660e \u90a3\u628a\u5b9d\u5251\uff0c\u53ef\u5c31\u514b\u4f4f\u6240\u6709\u7684\u5175\u5203\uff0c\u5362\u5927\u695e\u5b50\u5fc3\u60f3\uff1a\u8fd9\u6837\u6bd4\u6cd5\uff0c\u8f6e\u5230\u6211\u65f6\uff0c\u53ef\u4ee5\u6587\u6bd4\uff0c\u53ef\u4ee5\u4fdd \u5168\u548c\u6c14\uff0c\u5f53\u4e0b\u4e5f\u8868\u8d5e\u540c\u3002 \u3000\u3000\u51cc\u672a\u98ce\u89c1\u7eff\u6797\u7fa4\u8c6a\u90fd\u5df2\u7b54\u5141\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u98de\u8eab\u843d\u4e0b\u8c37\u4e2d\u76c6\u5730\uff0c\u5728\u4e00\u5757\u5927\u5ca9\u77f3\u4e0a\u4e00\u7ad9\uff0c\u9ad8 \u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u54ea\u4f4d\u5148\u4e0a\uff1f\u201d\u8fbe\u571f\u53f8\u4e00\u4e2a\u7bad\u6b65\u8df3\u51fa\u8bf4\u9053\uff1a\u201c\u4f60\u4e0b\u6765\uff0c\u6211\u548c\u4f60\u5148\u73a9\u4e00\u6837\u628a \u620f\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u628a\u620f\uff1f\u201d\u8fbe\u571f\u53f8\u5c06\u5916\u8863\u4e00\u8131\uff0c\u9732\u51fa\u9ed1\u94dc\u8272\u7684\u808c\u80a4\uff0c\u53cc\u81c0\u4e00\u9707\uff0c \u7b4b\u9aa8\u683c\u683c\u4f5c\u54cd\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u6765\u4e00\u5957\u501f\u4e09\u8fd8\u4e94\u7684\u628a\u620f\uff01\u4f60\u5148\u7ed9\u6211\u6253\u4e09\u62f3\uff0c\u6211\u4ed8\u4f60\u5229\u606f\u8fd8 \u4f60\u4e94\u62f3\uff0c\u6253\u65f6\u5927\u5bb6\u4e0d\u8bb8\u7528\u8f7b\u529f\u95ea\u907f\uff0c\u4e5f\u4e0d\u8bb8\u8fd8\u62f3\u3002\u82e5\u6709\u6b7b\u4f24\uff0c\u7237\u5b89\u5929\u547d\uff01\u201d\u8fbe\u571f\u53f8\u662f\u5916\u5bb6\u7b2c \u4e00\u6d41\u9ad8\u624b\uff0c\u94dc\u76ae\u94c1\u9aa8\uff0c\u7ec3\u5c31\u94c1\u5e03\u886b\u7684\u7edd\u9876\u529f\u592b\uff0c\u5e73\u5e38\u8fde\u5200\u67aa\u90fd\u63d2\u4e0d\u5165\uff0c\u4f55\u51b5\u62f3\u5934\u3002\u4ed6\u60f3\u51cc\u672a \u98ce\u82e5\u53d7\u6211\u4e09\u62f3\uff0c\u4e0d\u6b7b\u4e5f\u4f24\u3002\u7eb5\u7136\u4e0d\u4f24\uff0c\u4ed6\u6253\u6211\u4e94\u62f3\u6211\u4e5f\u4e0d\u6015\u3002 \u3000\u3000\u5362\u5927\u695e\u5b50\u542c\u4e86\uff0c\u5fc3\u60f3\u8fbe\u58eb\u53f8\u8fd9\u4e2a\u7c97\u4eba\u5012\u4f1a\u5360\u4fbf\u5b9c\uff0c\u4ed6\u8981\u5148\u6253\u4e09\u62f3\uff0c\u8fd9\u51cc\u672a\u98ce\u4e00\u5b9a\u4e0d\u80af\u7b54 \u5e94\u3002\u679c\u7136\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd9\u4e0d\u516c\u5e73\u3002\u201d\u8fbe\u571f\u53f8\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u5148\u6253\u6211\u4e09\u62f3\uff0c\u6211\u6253\u4f60\u4e94\u62f3\u3002\u201d\u5c82\u77e5 \u51cc\u672a\u98ce\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u4ed6\u4e0d\u7406\u8fbe\u571f\u53f8\u63d2\u5634\uff0c\u4e0d\u505c\u5730\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u8fd9\u4e0d\u516c\u5e73\uff0c\u6211\u4f55\u5fc5\u591a\u5360\u4f60\u4e24 \u62f3\uff1f\u6211\u4e0d\u8981\u5229\u606f\uff0c\u4f60\u5148\u6253\u6211\u4e09\u62f3\uff0c\u6211\u518d\u8fd8\u4f60\u4e09\u62f3\u597d\u4e86\uff01\u201d\u8fbe\u571f\u53f8\u5927\u6012\uff0c\u5fc3\u60f3\uff1a\u4f60\u6562\u8f7b\u89c6\u4e8e \u6211\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u90a3\u4f60\u4e0b\u6765\uff0c\u54b1\u4eec\u6bd4\u8bd5\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u843d\u5728\u90a3\u5757\u5927\u77f3\u4e0a\u5355\u8db3\u72ec\u7acb\uff0c\u53cc\u62f3\u4e00\u4f38\uff0c\u4e5f\u53eb\u9053\uff1a\u201c\u4f60\u4e0a\u6765\uff0c\u5728\u8fd9\u5757\u77f3\u5934\u4e0a\u6bd4\u8bd5\u8981 \u597d\u5f97\u591a\uff0c\u8c01\u8981\u843d\u4e0b\u77f3\u5934\uff0c\u4e5f\u5c31\u7b97\u8f93\u4e86\u3002\u201d\u8fbe\u571f\u53f8\u4e00\u770b\uff0c\u90a3\u5757\u77f3\u5934\u4ec5\u80fd\u5bb9\u4e24\u4eba\u7ad9\u7acb\uff0c\u522b\u8bf4\u4e0d\u80fd \u7528\u8f7b\u529f\u8eb2\u907f\uff0c\u8fde\u56de\u8eab\u95ea\u907f\u90fd\u96be\u3002\u5fc3\u60f3\u201c\u8fd9\u4f60\u66f4\u662f\u81ea\u5df1\u8ba8\u6b7b\u201d\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u8df3\u4e0a\u77f3\u5934\uff0c\u51cc\u672a\u98ce \u4ecd\u662f\u5355\u8db3\u72ec\u7acb\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7ad9\u7a33\u4e86\uff01\u8fd9\u77f3\u5934\u4e0a\u7a84\u5f97\u5f88\u5440\uff01\u597d\uff0c\u4f60\u53d1\u62f3\u5427\uff01\u201d \u3000\u3000\u8fbe\u571f\u53f8\u89c1\u4ed6\u5355\u8db3\u72ec\u7acb\uff0c\u5206\u660e\u662f\u8ba9\u81ea\u5df1\u5728\u77f3\u5934\u4e0a\u591a\u5360\u4e00\u4e9b\u5730\u65b9\uff0c\u81ea\u5df1\u4eab\u8a89\u6b66\u6797\u4e09\u5341\u591a\u5e74\uff0c \u51e0\u66fe\u53d7\u8fc7\u5982\u6b64\u8f7b\u89c6\uff0c\u6012\u706b\u51b2\u5929\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4f60\u4e5f\u7ad9\u597d\u4e86\uff01\u201d\u547c\u7684\u4e00\u58f0\uff0c\u5288\u80f8\u4e00\u62f3\u6253\u53bb\uff0c\u51cc \u672a\u98ce\u633a\u80f8\u76f8\u8fce\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u5982\u51fb\u5de8\u6728\uff0c\u51cc\u672a\u98ce\u5355\u8db3\u6446\u8361\uff0c\u8eab\u5b50\u6447\u4e86\u51e0\u6447\uff0c\u4f3c\u6b32\u8dcc \u5012\uff0c\u6842\u4ef2\u660e\u5927\u5403\u4e00\u60ca\uff0c\u6b63\u5f85\u8fc7\u53bb\u6551\u65f6\uff0c\u51cc\u672a\u98ce\u5df2\u7ad9\u7a33\u4e86\u8eab\u5f62\uff0c\u201c\u54ce\u5440\u201d\u4e00\u58f0\u7b11\u9053\uff1a\u201c\u6ca1\u4f24 \u7740\uff01\u201d \u3000\u3000\u8fbe\u571f\u53f8\u4e00\u62f3\u6253\u51fa\uff0c\u5c31\u4f3c\u6253\u7740\u4e00\u5757\u94a2\u94c1\uff0c\u62f3\u5934\u9690\u9690\u4f5c\u75db\uff0c\u8eab\u5b50\u4e5f\u7ed9\u53cd\u78b0\u5f97\u6447\u6643\u4e0d\u5b9a\uff0c\u4f46\u662f \u6842\u4ef2\u660e\u53ea\u6ce8\u610f\u51cc\u672a\u98ce\uff0c\u6ca1\u89c1\u7740\u4ed6\u7684\u72fc\u72c8\u76f8\uff0c\u7fa4\u8c6a\u53ef\u662f\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u539f\u6765\u8fd9\u62f3\u51cc\u672a\u98ce\u6545\u610f\u786c\u78b0\u78b0\u63a5\u4e86\u4e0b\u6765\uff0c\u770b\u4ed6\u7684\u52b2\u529b\u3002\u7ed3\u679c\u51cc\u672a\u51e4\u867d\u672a\u8dcc\u5012\uff0c\u80f8\u53e3\u4e5f\u662f\u9690 \u9690\u4f5c\u75db\u3002\u6025\u8c03\u597d\u547c\u5438\uff0c\u8fd0\u6c14\u4e00\u8f6c\uff0c\u6c14\u8fbe\u56db\u80a2\uff0c\u77e5\u9053\u6ca1\u6709\u53d7\u7740\u5185\u4f24\uff0c\u5fc3\u5185\u4e00\u5bbd\uff0c\u53c8\u563b\u563b\u7b11\u9053\uff1a \u201c\u7b2c\u4e00\u62f3\u8fc7\u4e86\u3002\u5f1f\u4e8c\u62f3\u6765\u5427\uff01\u201d\u8fbe\u571f\u53f8\u4e00\u8a00\u4e0d\u53d1\uff0c\u8fd0\u8d77\u795e\u529b\uff0c\u547c\u7684\u4e00\u62f3\uff0c\u53c8\u5411\u51cc\u672a\u98ce\u5c0f\u8179\u4e39 \u7530\u4e4b\u5904\u6253\u53bb\uff0c\u51cc\u672a\u51e4\u628a\u8eab\u5b50\u5411\u5de6\u5fae\u5fae\u4e00\u4fa7\uff0c\u8fbe\u571f\u53f8\u4e00\u62f3\u8d34\u8089\u6253\u8fc7\uff0c\u6ed1\u6e9c\u6e9c\u7684\u65e0\u5904\u4f7f\u52b2\u3002\u51cc\u672a \u98ce\u7528\u2018\u5378\u2019\u5b57\u8bc0\uff0c\u628a\u4ed6\u7684\u52b2\u529b\u5316\u4e8e\u65e0\u5f62\u3002\u53c8\u662f\u563b\u563b\u7b11\u9053\uff1a\u201c\u7b2c\u4e8c\u62f3\u4e5f\u6253\u8fc7\u4e86\uff0c\u8fd8\u6709\u6700\u540e\u4e00 \u62f3\uff0c\u597d\u751f\u6253\u5427\uff01\u201d\u8fbe\u571f\u53f8\u7741\u5927\u53cc\u773c\uff0c\u6012\u543c\u4e00\u58f0\uff0c\u53cc\u62f3\u9f50\u53d1\uff0c\u51cc\u672a\u98ce\u8eab\u5b50\u7a81\u7136\u5411\u540e\u4e00\u4ef0\uff0c\u5355\u8db3 \u60ac\u7a7a\uff0c\u5934\u5411\u540e\u5f2f\uff0c\u534a\u8fb9\u8eab\u5b50\u5df2\u60ac\u5ca9\u5916\uff0c\u8fbe\u571f\u53f8\u53cc\u62f3\u4e4b\u529b\uff0c\u4f55\u6b62\u5343\u65a4\uff0c\u4f46\u51cc\u672a\u98ce\u8fd9\u5411\u540e\u4e00\u4ef0\uff0c \u8e0f\u7740\u5ca9\u77f3\u7684\u53f3\u8db3\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u8179\u90e8\u5374\u51f9\u8fdb\u4e09\u5bf8\uff0c\u8fbe\u571f\u53f8\u4e24\u62f3\u5934\u90fd\u6253\u4e2d\u4e86\uff0c\u5374\u88ab\u51cc\u672a\u98ce\u8179\u808c\u5438 \u7740\uff0c\u8fbe\u571f\u53f8\u624b\u81c0\u4ea6\u5df2\u653e\u5c3d\uff0c\u65e0\u4ece\u4f7f\u529b\uff0c\u51cc\u672a\u98ce\u8eab\u5b50\u4e00\u633a\uff0c\u559d\u58f0\uff1a\u201c\u64a4\u624b\uff01\u201d\u8fbe\u571f\u53f8\u53ea\u89c9\u4e00\u80a1 \u5927\u529b\u53cd\u51fb\u56de\u6765\uff0c\u62f3\u5934\u201c\u535c\u201d\u7684\u4e00\u58f0\u5f39\u4e86\u51fa\u6765\uff0c\u8eab\u5b50\u6447\u6447\u6b32\u5012\uff0c\u5e78\u4ed6\u529f\u529b\u4e5f\u6781\u6df1\u6e5b\uff0c\u53cc\u8db3\u4e00 \u987f\uff0c\u201c\u529b\u5760\u5343\u65a4\u201d\uff0c\u624d\u628a\u8eab\u5f62\u7a33\u4f4f\u3002\u7fa4\u96c4\u77a9\u76ee\u60ca\u5fc3\uff0c\u7adf\u7981\u4e0d\u4f4f\u8f70\u7136\u559d\u8d77\u597d\u6765\uff01 \u3000\u3000\u51cc\u672a\u98ce\u63a5\u4e86\u4e09\u62f3\uff08\u6309\uff1a\u6700\u540e\u4e00\u6b21\u867d\u662f\u53cc\u62f3\u5f00\u53d1\uff0c\u4f46\u4ecd\u7b97\u662f\u4e00\u62f3\u3002\u6b66\u5bb6\u6240\u8bb2\u7684\u201c\u4e00\u62f3\u201d\u662f \u53cc\u624b\u90fd\u7b97\u5728\u5185\u7684\uff09\uff0c\u795e\u8272\u81ea\u5982\uff0c\u53cc\u8db3\u8e0f\u5b9e\uff0c\u4e0e\u8fbe\u571f\u53f8\u9762\u9762\u76f8\u5bf9\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u73b0\u5728\u8f6e\u5230\u6211\u53d1 \u62f3\u4e86\uff0c\u4f60\u7ad9\u597d\u6ca1\u6709\uff1f\u201d\u8fbe\u571f\u53f8\u5fc3\u5185\u53d1\u6bdb\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7b49\u4e00\u4e0b\u3002\u201d\u4ed6\u8c03\u597d\u547c\u5438\uff0c\u7528\u529b\u4e00\u7ef7\uff0c\u5168 \u8eab\u9aa8\u9abc\u683c\u683c\u4f5c\u54cd\uff0c\u4ed6\u8fd9\u624d\u5b9a\u4e0b\u795e\u6765\uff0c\u5fc3\u60f3\uff1a\u4f60\u51cc\u672a\u98ce\u529f\u529b\u867d\u7136\u6df1\u6e5b\uff0c\u4e5f\u672a\u5fc5\u7834\u5f97\u6211\u94c1\u5e03\u886b\u6a2a \u7ec3\u7684\u529f\u529b\u3002\u53cc\u8db3\u7528\u529b\u9489\u5728\u77f3\u4e0a\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u6253\u5427\uff01\u201d\u51cc\u672a\u98ce\u5fae\u5fae\u4e00\u7b11\uff0c\u5de6\u638c\u4e00\u626c\uff0c\u53f3\u62f3\u5728\u638c \u4e0b\u76f4\u7a7f\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u7b2c\u4e00\u62f3\u6765\u4e86\uff01\u201d \u3000\u3000\u8fbe\u571f\u53f8\u7a81\u7684\u8eab\u5b50\u4e00\u77ee\uff0c\u80a9\u5934\u5411\u524d\u4e00\u649e\uff0c\u51cc\u672a\u98ce\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u51fb\u4e2a\u6b63\u8005\uff0c\u4e5f\u89c9\u4e00\u80a1\u5927\u529b \u53cd\u51fb\u56de\u6765\uff0c\u4ed6\u75be\u7684\u5c06\u62f3\u5934\u4e00\u6536\uff0c\u8fbe\u571f\u53f8\u54fc\u4e86\u4e00\u58f0\uff0c\u7adf\u7ed9\u4ed6\u5728\u6536\u62f3\u4e4b\u9645\uff0c\u7528\u201c\u7c98\u201d\u52b2\u5c06\u8eab\u5b50\u5e26 \u52a8\u4e24\u6b65\uff0c\u51cc\u672a\u98ce\u4ece\u65c1\u5fae\u5fae\u4e00\u95ea\uff0c\u559d\u9053\uff1a\u201c\u7ad9\u7a33\u4e86\uff01\u201d\u8fbe\u571f\u53f8\u6ee1\u8138\u901a\u7ea2\uff0c\u5f3a\u7528\u91cd\u8eab\u6cd5\u7a33\u7740\u8eab \u5f62\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u539f\u6765\u8fbe\u571f\u53f8\u63a5\u8fd9\u4e00\u62f3\uff0c\u53d6\u5de7\u5230\u6781\u3002\u672c\u6765\u201c\u501f\u62f3\u8fd8\u62f3\u201d\u662f\u89c4\u5b9a\u522b\u4eba\u53d1\u62f3\u65f6\u4e0d\u8bb8\u53cd\u51fb\u7684\uff0c\u4ed6 \u80a9\u5934\u5411\u524d\u4e00\u649e\uff0c\u5176\u5b9e\u5df2\u662f\u53cd\u51fb\uff0c\u53ea\u662f\u4ed6\u4e0d\u52a8\u624b\uff0c\u56e0\u6b64\u4e0d\u7b97\u662f\u72af\u89c4\u3002 \u3000\u3000\u51cc\u6765\u98ce\u4e00\u62f3\u6253\u4ed6\u4e0d\u5012\uff0c\u7528\u5185\u5bb6\u7c98\u529b\uff0c\u4e5f\u53ea\u628a\u4ed6\u5e26\u52a8\u4e24\u6b65\uff0c\u4ea6\u662f\u9887\u611f\u8be7\u5f02\u3002\u5fc3\u60f3\uff1a\u201c\u8fd9\u5bb6 \u4f19\u540d\u4e0d\u865a\u4f20\uff0c\u867d\u7136\u53d6\u5de7\uff0c\u529f\u529b\u4e5f\u771f\u6df1\u539a\u3002\u6211\u5012\u8981\u8bd5\u8bd5\u4ed6\u7684\u94c1\u5e03\u886b\u529f\u592b\u600e\u6837\uff1f\u201d\u53c8\u662f\u5fae\u5fae\u4e00 \u7b11\uff0c\u811a\u8ddf\u4e00\u65cb\uff0c\u62f3\u5934\u81ea\u4ef0\u9762\u5411\u4ed6\u53f3\u4e73\u6253\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u7b2c\u4e8c\u62f3\u6765\u4e86\uff01\u201d \u3000\u3000\u8fd9\u56de\u8fbe\u571f\u53f8\u4e0d\u6562\u518d\u53d6\u5de7\u53cd\u51fb\uff0c\u786c\u633a\u7740\u80f8\uff0c\u8fce\u9762\u63a5\u4e86\u8fd9\u62f3\u3002\u51cc\u672a\u51e4\u4e00\u62f3\u6253\u51fa\u5982\u4e2d\u94c1\u77f3\uff0c\u4ed6 \u62f3\u5934\u6253\u4e2d\uff0c\u518d\u7528\u529b\u4e00\u6309\uff0c\u8fbe\u571f\u53f8\u4e5f\u89c9\u5982\u5343\u65a4\u94c1\u9524\u6253\u6765\u4e00\u6837\uff0c\u53c8\u662f\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8eab\u5b50\u6447\u6643\u4e86 \u51e0\u4e0b\uff0c\u7528\u529b\u633a\u7740\u3002\u51cc\u672a\u98ce\u8fd9\u62f3\u7528\u7684\u662f\u786c\u529f\uff0c\u89c1\u8fbe\u571f\u53f8\u867d\u7136\u7ed9\u6253\u5f97\u6447\u6643\uff0c\u4ecd\u65e0\u635f\u4f24\uff0c\u4ea6\u662f\u4e0d\u7981 \u6697\u6697\u4f69\u670d\u3002\u5fc3\u60f3\uff0c\u6b64\u4eba\u7684\u94c1\u5e03\u886b\u529f\u592b\u5728\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u4e5f\u53ef\u5750\u7b2c\u4e00\u628a\u4ea4\u6905\u4e86\u3002 \u3000\u3000\u8fbe\u571f\u53f8\u63a5\u8fc7\u4e24\u62f3\uff0c\u5fc3\u795e\u7a0d\u5b9a\uff0c\u60f3\u5728\u7fa4\u96c4\u4e4b\u524d\uff0c\u635e\u56de\u9762\u5b50\u3002\u5f3a\u81ea\u4f5c\u6001\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u592b \u867d\u8001\uff0c\u8fd9\u51e0\u6839\u9aa8\u5934\u8fd8\u786c\u6717\uff0c\u4f60\u8fd8\u6709\u4e00\u62f3\uff0c\u597d\u751f\u6253\u5427\uff01\u201d\u7b11\u58f0\u672a\u6bd5\uff0c\u51cc\u672a\u98ce\u5ffd\u7136\u53cc\u62f3\u5f00\u53d1\uff0c\u671d \u4ed6\u4e24\u80c1\u6253\u6765\uff0c\u8fbe\u571f\u53f8\u867d\u6709\u4e00\u8eab\u6a2a\u7ec3\u529f\u592b\uff0c\u4e0d\u6015\u70b9\u7a74\uff0c\u600e\u5948\u201c\u6d8c\u6cc9\u7a74\u201d\u4e43\u662f\u4eba\u8eab\u4e09\u5341\u516d\u5927\u7a74\u4e4b \u4e00\uff0c\u518d\u52a0\u4e0a\u51cc\u672a\u98ce\u7684\u795e\u529b\uff0c\u5982\u4f55\u7981\u53d7\u5f97\u4f4f\uff1f\u53ea\u89c9\u5168\u8eab\u9ebb\u75f9\uff0c\u7ed9\u638c\u529b\u9707\u5f97\u65ad\u7ebf\u98ce\u7b5d\u4e00\u6837\uff0c\u98d8\u98d8 \u8361\u8361\u76f4\u8dcc\u4e0b\u53bb\u3002\u5362\u5927\u695e\u5b50\u7ad9\u5728\u5c31\u8fd1\uff0c\u62a2\u8fc7\u6765\u6276\uff0c\u8fbe\u571f\u53f8\u4e5f\u597d\u751f\u4e86\u5f97\uff0c\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u7ffb \u8d77\u8eab\u6765\uff0c\u6ee1\u8138\u901a\u7ea2\uff0c\u53eb\u9053\uff1a\u201c\u9ec4\u91d1\u6211\u4e0d\u8981\u4e86\uff01\u201d\u4e00\u626d\u5934\u4fbf\u5f80\u5916\u8d70\uff0c\u60f3\u56de\u8f6c\u6545\u4e61\uff0c\u518d\u7ec3\u7edd\u6280\u3002 \u3000\u3000\u97e9\u8346\u6025\u5fd9\u62e6\u7740\u4ed6\u9053\uff1a\u201c\u522b\u5fd9\uff0c\u8fd8\u6709\u5c0f\u5f1f\u4eec\u5462\u3002\u201d\u4ed6\u4e43\u662f\u60f3\u7559\u7740\u8fbe\u571f\u53f8\uff0c\u51c6\u5907\u4e07\u4e00\u7fa4\u6bb4\u4e4b \u7528\u3002 \u3000\u3000\u8fbe\u571f\u53f8\u9053\uff1a\u201c\u6211\u662f\u8ba4\u8f93\u4e86\uff0c\u4f55\u5fc5\u8fd8\u5728\u8fd9\u91cc\u770b\u4eba\u8138\u8272\u5462\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u4e5f\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u8fbe\u571f\u53f8\uff0c\u4f60\u7528\u94c1\u5e03\u886b\u529f\u592b\uff0c\u5176\u5b9e\u6211\u8d62\u4e0d\u4e86\u4f60\uff0c\u6211\u53ea\u662f\u4ed7\u7740\u6253\u7a74\u529f \u592b\uff0c\u5de7\u80dc\u4e00\u62db\uff0c\u5f85\u4f1a\u6211\u8fd8\u8981\u5411\u4f60\u9886\u6559\u3002\u201d\u8fbe\u571f\u53f8\u867d\u7136\u660e\u77e5\u51cc\u672a\u98ce\u662f\u7ed9\u4ed6\u9762\u5b50\uff0c\uff08\u65e2\u7136\u4e92\u76f8\u8d4c \u62f3\uff0c\u5f53\u7136\u4e0d\u80fd\u9650\u5236\u522b\u4eba\u6253\u5728\u7a74\u9053\u4e0a\u3002\uff09\u4f46\u4e5f\u4e0d\u80fd\u4e0d\u7559\u4e0b\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u4e0a\u53bb\u4e0e\u51cc\u672a\u98ce\u8d4c\u7684\u662f\u9ed1\u715e\u795e\u9676\u5b8f\uff0c\u4ed6\u7684\u4e0b\u76d8\u529f\u592b\u6700\u7a33\uff0c\u4e0e\u51cc\u672a\u98ce\u6bd4\u6454\u8de4\u3002\u4f46\u8bba\u529f \u529b\u5374\u8981\u6bd4\u8fbe\u571f\u53f8\u5dee\u5f97\u591a\uff0c\u90a3\u7981\u5f97\u51cc\u672a\u98ce\u795e\u529b\uff0c\u4e0d\u8fc7\u51e0\u4e2a\u56de\u5408\uff0c\u4fbf\u7ed9\u51cc\u672a\u98ce\u6454\u5012\u3002 \u3000\u3000\u7b2c\u4e09\u4e2a\u4e0a\u6765\uff0c\u51cc\u672a\u98ce\u5374\u4e0d\u80fd\u4e0d\u6709\u70b9\u8e0c\u8e87\u4e86\uff0c\u6765\u4eba\u4e43\u662f\u5362\u5927\u695e\u5b50\u3002\u51cc\u672a\u98ce\u5fc3\u60f3\u8fd9\u4eba\u5374\u662f\u4e2a \u8c6a\u723d\u6c49\u5b50\uff0c\u82e5\u4ed6\u4e0d\u77e5\u5206\u5bf8\uff0c\u8981\u6bd4\u5175\u5203\u62f3\u811a\uff0c\u4f24\u4e86\u4ed6\u90a3\u53ef\u4e0d\u597d\u3002 \u3000\u3000\u6b63\u8e0c\u8e87\u95f4\uff0c\u5362\u5927\u695e\u5b50\u5ba2\u5ba2\u6c14\u6c14\u5730\u62f1\u624b\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u6211\u60f3\u9886\u6559\u4f60\u7684\u8f7b\u529f\u3002\u81f3\u4e8e\u9ec4\u91d1\uff0c\u6211 \u5362\u5927\u695e\u5b50\u867d\u7a77\uff0c\u4e5f\u8fd8\u6709\u4e24\u53e3\u996d\u5403\uff0c\u51cc\u5927\u4fa0\u4f60\u65e2\u7136\u8981\u91d1\u5b50\u7528\uff0c\u90a3\u6211\u53ef\u4e0d\u6562\u63d0\u8d4c\u6280\u593a\u91d1\u7684\u8bdd\uff0c\u4e0d \u8bba\u8f93\u8d62\uff0c\u6211\u540d\u4e0b\u7684\u90a3\u5c0a\u7f57\u53cc\uff0c\u4f60\u90fd\u62ff\u53bb\u597d\u4e86\uff01\u201d\u51cc\u672a\u98ce\u5fc3\u5185\u6697\u7b11\uff0c\u60c5\u77e5\u5362\u5927\u695e\u5b50\u4e0d\u5fff\u4ed6\u8981\u72ec \u5360\u9ec4\u91d1\uff0c\u628a\u4ed6\u770b\u6210\u8d2a\u8d22\u7684\u4eba\uff0c\u5fc3\u60f3\uff1a\u5f85\u4f1a\u6211\u8bf4\u51fa\u6765\u4f60\u5c31\u660e\u767d\u4e86\uff0c\u73b0\u5728\u81ea\u7531\u4f60\u8bef\u4f1a\u3002\u628a\u62f3\u4e00 \u62f1\uff0c\u4e5f\u5ba2\u5ba2\u6c14\u6c14\u5730\u8bf4\u9053\uff1a\u201c\u5362\u8235\u4e3b\u8a00\u91cd\u4e86\uff0c\u9ec4\u91d1\u7684\u4e8b\uff0c\u6bd4\u8bd5\u4e4b\u540e\u518d\u8bf4\u5427\u3002\u8bf7\u4f60\u5212\u51fa\u9053\u6765\uff0c\u8f7b \u529f\u600e\u4e48\u6bd4\u6cd5\uff1f\u201d \u3000\u3000\u5362\u5927\u695e\u5b50\u6307\u7740\u5bf9\u9762\u4e00\u4e2a\u5c0f\u5c71\u5cf0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8dd1\u4e0a\u8fd9\u5cf0\u9876\u53bb\uff0c\u4e2d\u9014\u4e0d\u5f97\u6b47\u606f\u3002\u4e00\u4e0a\u4e00 \u4e0b\uff0c\u8f7b\u529f\u5982\u4f55\u4e5f\u5c31\u770b\u51fa\u6765\u4e86\u3002\u5728\u8fd9\u91cc\u7684\u90fd\u662f\u6210\u540d\u4eba\u7269\uff0c\u65ad\u4e0d\u81f4\u5224\u4f18\u4e3a\u52a3\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a \u201c\u597d\uff0c\u5c31\u8fd9\u6837\u5427\uff0c\u5362\u8235\u4e3b\uff0c\u4f60\u5148\u8bf7\uff01\u201d \u3000\u3000\u6bd4\u8f7b\u529f\u770b\u6765\u867d\u8f83\u7f13\u548c\uff0c\u5176\u5b9e\u5374\u4e0d\u5927\u6613\uff0c\u5251\u9601\u4e43\u51fa\u540d\u5929\u9669\u4e4b\u5730\uff0c\u6bcf\u4e2a\u5c71\u5cf0\u90fd\u662f\u5149\u6e9c\u6e9c\u7684\u5ced \u58c1\uff0c\u5c31\u662f\u733f\u7334\u722c\u4e0a\u53bb\u4e5f\u96be\uff0c\u529f\u592b\u5dee\u4e00\u70b9\u7684\u51c6\u4f1a\u8dcc\u6b7b\u3002\u5362\u5927\u695e\u5b50\u8f7b\u529f\u6709\u6781\u6df1\u9020\u8be3\uff0c\u521a\u624d\u6551\u7f57\u8fbe \u4e4b\u65f6\u5df2\u663e\u8fc7\u4e00\u624b\uff0c\u73b0\u5728\u542c\u5f97\u51cc\u672a\u98ce\u53eb\u4ed6\u5148\u4e0a\uff0c\u9053\u58f0\u201c\u6709\u8bf7\uff01\u201d\u811a\u4e00\u6491\u5730\uff0c\u4fbf\u5982\u79bb\u5f26\u5f29\u7bad\uff0c\u76f4 \u51b2\u4e0a\u56db\u4e94\u4e08\u9ad8\uff0c\u53cc\u8db3\u4e00\u70b9\u77f3\u58c1\uff0c\u4fbf\u5411\u5de6\u53f3\u76d8\u65cb\u800c\u4e0a\uff0c\u53ea\u89c1\u4ed6\u5728\u5c96\u58c1\u4e4b\u4e0a\u5982\u9640\u87ba\u4e00\u822c\uff0c\u5de6\u62e7\u53f3 \u8f6c\uff0c\u970e\u5ffd\u5230\u4e86\u5cf0\u9876\u3002\u51cc\u672a\u98ce\u77e5\u9053\u8fd9\u53eb\u201c\u76d8\u9640\u529f\u201d\uff0c\u662f\u7528\u201c\u4e4b\u201d\u5b57\u5f62\u7684\u8eab\u6cd5\u6765\u5e73\u8861\u8eab\u4f53\u7684\uff0c\u96be \u5f97\u7684\u662f\u4ed6\u5728\u5ced\u58c1\u4e4b\u4e0a\uff0c\u5c45\u7136\u56de\u65cb\u5982\u610f\uff0c\u8fd9\u529f\u592b\u53ef\u771f\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\u3002 \u3000\u3000\u5362\u5927\u695e\u5b50\u5230\u4e86\u5cf0\u9876\u4e0d\u505c\u7559\uff0c\u53c8\u4f3c\u9640\u87ba\u4e00\u822c\u76d8\u65cb\u800c\u4e0b\uff0c\u5230\u79bb\u5730\u4e94\u516d\u4e08\u5904\uff0c\u5ffd\u7136\u632f\u81c0\u4e00\u8dc3\uff0c \u4f3c\u5927\u96c1\u4e00\u822c\u98de\u843d\u4e0b\u6765\uff0c\u8eab\u6cd5\u5de7\u5999\u4e4b\u6781\u3002\u7fa4\u8c6a\u9ad8\u58f0\u559d\u5f69\uff0c\u6842\u4ef2\u660e\u5fc3\u60f3\uff0c\u6211\u5728\u5251\u9601\u957f\u5927\uff0c\u8bba\u8f7b\u8eab \u529f\u592b\u4e5f\u8fd8\u900a\u4ed6\u4e00\u7b79\uff0c\u53ef\u4e0d\u77e5\u4fca\u672a\u51e4\u600e\u6837\u80dc\u4ed6\u3002 \u3000\u3000\u51cc\u672a\u51e4\u5f85\u4ed6\u843d\u5730\uff0c\u9053\u58f0\uff1a\u201c\u524d\u8f88\u8eab\u624b\u679c\u7136\u4e0d\u51e1\uff0c\u665a\u8f88\u732e\u4e11\uff0c\u5e78\u5306\u89c1\u7b11\u3002\u201d\u8bf4\u7f62\uff0c\u8db3\u5c16\u5728 \u5730\u9762\u8f7b\u8f7b\u4e00\u70b9\uff0c\u8eab\u5b50\u5e73\u5730\u62d4\u8d77\uff0c\u201c\u4e00\u9e64\u51b2\u5929\u201d\uff0c\u7adf\u63a0\u8d77\u4e86\u5341\u4f59\u4e08\u9ad8\uff0c\u5230\u4e86\u5ced\u58c1\u4e4b\u4e0a\uff0c\u7adf\u7136\u53cc \u8db3\u4e0d\u843d\u5730\uff0c\u53ea\u7528\u624b\u638c\u5728\u77f3\u58c1\u4e0a\u8f7b\u8f7b\u4e00\u62cd\uff0c\u8eab\u5b50\u53c8\u518d\u817e\u8d77\uff0c\u8fd9\u6837\u7684\u63a5\u8fde\u6362\u638c\uff0c\u5feb\u4f3c\u6d41\u661f\uff0c\u4e0b\u8fb9 \u7684\u4eba\u770b\u4e0a\u53bb\uff0c\u53ea\u89c1\u4ed6\u5c31\u4f3c\u98de\u9e1f\u4e00\u822c\uff0c\u4e00\u76f4\u201c\u98de\u201d\u4e0a\uff0c\u5230\u4e86\u5cf0\u9876\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u4ecd\u7528\u5ced\u58c1\u6362\u638c\u4e4b \u6cd5\u4e0b\u6765\u81f3\u79bb\u5730\u5341\u4e94\u516d\u4e08\u4e4b\u5904\uff0c\u5ffd\u7136\u5934\u4e0b\u811a\u4e0a\uff0c\u50cf\u6d41\u661f\u6b92\u77f3\u4e00\u822c\u76f4\u8dcc\u4e0b\u6765\uff0c\u5728\u4f17\u4eba\u60ca\u53eb\u58f0\u4e2d\uff0c \u81f3\u79bb\u5730\u4e0d\u5230\u4e00\u4e08\u7684\u65f6\u5019\uff0c\u5ffd\u7136\u4e00\u4e2a\u7b4b\u6597\uff0c\u56db\u5e73\u516b\u7a33\u5730\u843d\u5728\u5730\u4e0a\uff0c\u7fa4\u8c6a\u867d\u7136\u548c\u51cc\u672a\u98ce\u4f5c\u5bf9\uff0c\u8fd9 \u65f6\u4e5f\u4e0d\u7981\u8f70\u5929\u4ef7\u7684\u53eb\u8d77\u597d\u6765\uff0c\u5362\u5927\u695e\u5b50\u9053\uff1a\u201c\u6211\u8f93\u4e86\u3002\u201d\u9000\u8fc7\u4e00\u8fb9\uff0c\u66f4\u4e0d\u53d1\u8bdd\u3002 \u3000\u3000\u51cc\u672a\u98ce\u8fde\u80dc\u4e09\u573a\uff0c\u97e9\u8346\u6c89\u4e0d\u4f4f\u6c14\uff0c\u534a\u622a\u62d0\u6756\u63d2\u5728\u88e4\u5934\uff0c\u62d4\u6b65\u4fbf\u51fa\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u51cc\u5927 \u4fa0\uff0c\u54b1\u4eec\u6765\u6bd4\u5212\u6bd4\u5212\uff01\u201d\u6b63\u662f\uff1a\u71d5\u96c0\u5b89\u77e5\u9e3f\u9e4a\u5fd7\uff0c\u7adf\u8f7b\u4ec1\u4e49\u91cd\u9ec4\u91d1\u3002'}),showOnHomepage:!0},{name:"\u5e7d\u8c37\u7f14\u826f\u7f18\u3000\u559c\u80b2\u91d1\u73af\u8054\u5f69\u7b14  ",content:(0,t.jsx)(t.Fragment,{children:"\u6c5f\u6e56\u5055\u4fea\u5f71\u3000\u4e89\u770b\u5b9d\u5251\u914d\u795e\u7802 \u3000\u3000\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6562\u95ee\u5982\u4f55\u6bd4\u6cd5\uff1f\u201d\u97e9\u8346\u9053\uff1a\u201c\u51cc\u5e08\u7236\u7684\u8f7b\u529f\u6697\u5668\u90fd\u89c1\u8bc6\u8fc7\u4e86\uff0c\u8001\u673d\u60f3\u518d\u89c1 \u8bc6\u4f60\u7684\u5185\u529f\u3002\u201d\u51cc\u672a\u98ce\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u4efb\u51ed\u5c0a\u4fbf\u3002\u201d\u97e9\u8346\u5728\u5730\u4e0a\u53d6\u6765\u4e00\u4e9b\u67af\u679d\uff0c\u624e\u6210\u4e94\u6346\uff0c\u7528 \u706b\u77f3\u628a\u5b83\u71c3\u70b9\u8d77\u6765\uff0c\u5206\u63d2\u5730\u4e0a\u3002\u4e94\u5806\u65fa\u706b\uff0c\u718a\u718a\u71c3\u70e7\uff0c\u97e9\u8346\u9053\uff1a\u201c\u5c31\u6bd4\u8bd5\u5288\u7a7a\u638c\u7684\u529f\u592b \u5427\u3002\u201d\u8bf4\u7f62\u53cc\u8896\u4e00\u5377\uff0c\u9a7c\u80cc\u524d\u4fef\uff0c\u53cc\u81c2\u9752\u7b4b\uff0c\u6761\u6761\u575f\u8d77\uff0c\u5168\u8eab\u9aa8\u8282\uff0c\u683c\u683c\u4f5c\u58f0\uff0c\u4e00\u770b\u5c31\u77e5\u662f \u5185\u5bb6\u9ad8\u624b\u3002 \u3000\u3000\u97e9\u8346\u8fd0\u53e3\u6c14\u540e\uff0c\u53cc\u638c\u4ea4\u52a0\uff0c\u6765\u56de\u6e38\u8d70\u51e0\u5708\uff0c\u8d8a\u8d70\u8d8a\u75be\uff0c\u731b\u7136\u95f4\u811a\u5c16\u4e00\u70b9\uff0c\u4e5f\u4e0d\u89c1\u600e\u4e48\u8038 \u8eab\u4f5c\u52bf\uff0c\u4fbf\u7a9c\u5230\u4e2d\u95f4\u90a3\u6346\u706b\u628a\u7684\u9762\u524d\uff0c\u8ddd\u79bb\u4e0d\u8db3\u4e94\u5c3a\uff0c\u4e00\u4e2a\u201c\u63a8\u7a97\u671b\u6708\u201d\u62db\u5f0f\uff0c\u638c\u98ce\u547c\u54cd\uff0c \u628a\u706b\u7130\u6253\u5f97\u5411\u540e\u5410\u51fa\u53bb\uff0c\u5c31\u5728\u706b\u7130\u6447\u6447\u6b32\u706d\u4e4b\u9645\uff0c\u97e9\u8346\u53f3\u638c\u75be\u53d1\uff0c\u53ea\u89c1\u706b\u661f\u4e71\u98de\uff0c\u706b\u5149\u5168 \u706d\u3002\u8ddf\u7740\u8eab\u5b50\u4e00\u8f6c\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u4ecd\u662f\u4e00\u62db\u4e24\u5f0f\uff0c\u5de6\u638c\u5148\u53d1\uff0c\u628a\u706b\u7130\u62c9\u957f\uff0c\u53f3\u638c\u538b\u4e0b\uff0c\u5c06\u706b\u5149 \u7184\u706d\u3002\u97e9\u8346\u6253\u706d\u4e86\u4e24\u6346\u706b\u628a\u4e4b\u540e\uff0c\u53c8\u4f5c\u52bf\u76d8\u65cb\uff0c\u75be\u7ed5\u6570\u5468\uff0c\u8fd9\u6b21\u66f4\u52a0\u5389\u5bb3\uff0c\u4e00\u4e2a\u201c\u53cc\u9f99\u51fa \u6d77\u201d\uff0c\u4e24\u80a1\u52b2\u98ce\u540c\u65f6\u53d1\u51fa\uff0c\u628a\u7b2c\u4e09\u6346\u706b\u628a\u4e00\u4e0b\u7184\u706d\uff0c\u706b\u661f\u5c04\u51fa\u4e94\u516d\u5c3a\u8fdc\uff0c\u715e\u662f\u60ca\u4eba\uff0c\u63a5\u7740\u4e00 \u4e2a\u7ffb\u8eab\uff0c\u4ecd\u662f\u53cc\u638c\u9f50\u51fa\uff0c\u8fd0\u7528\u524d\u6cd5\uff0c\u628a\u7b2c\u56db\u6346\u706b\u628a\u7184\u706d\u3002\u97e9\u8346\u8fde\u7528\u56db\u4e2a\u4e0d\u540c\u7684\u62db\u5f0f\uff0c\u6253\u706d\u4e86 \u56db\u6346\u706b\u628a\uff0c\u4ef0\u5929\u5927\u7b11\uff0c\u5f97\u610f\u4e4b\u6781\u3002\u4ed6\u8eab\u5982\u98de\u9c7c\uff0c\u6b65\u5982\u6d41\u6c34\uff0c\u5de6\u53f3\u76d8\u65cb\uff0c\u6f14\u4e86\u51e0\u8def\u62f3\u6cd5\uff0c\u624d\u7a81 \u7684\u638c\u5fc3\u5411\u5916\u4e00\u5410\uff0c\u8fd9\u56de\u7adf\u5728\u8ddd\u7b2c\u4e94\u6346\u706b\u628a\u4e03\u516b\u5c3a\u4e4b\u5904\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u706b\u7130\u4fbf\u5373\u5e94\u624b\u800c\u706d\u3002\u5404\u8def \u9ad8\u624b\uff0c\u559d\u5f69\u4e0d\u5df2\uff01\u97e9\u8346\u6253\u5b8c\u4e4b\u540e,\u813e\u7768\u659c\u89c6\uff0c\u5bf9\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8001\u673d\u5c31\u662f\u8fd9\u70b9\u70b9\u529f\u592b\uff0c\u4f60\u4e5f\u8bd5 \u8bd5\u5427\uff01\u201d \u3000\u3000\u97e9\u8346\u8fd9\u6837\u7684\u5288\u7a7a\u638c\u529f\u592b\uff0c\u4e5f\u53ef\u7b97\u662f\u5185\u5bb6\u7684\u4e00\u6d41\u9ad8\u624b\u4e86\uff0c\u53ef\u662f\u5728\u51cc\u672a\u98ce\u770b\u6765\uff0c\u529f\u592b\u5374\u5c1a\u6b20 \u7eaf\u539a\u3002\u4ed6\u8981\u501f\u884c\u62f3\u98de\u6b65\u4e4b\u52bf\uff0c\u624d\u80fd\u5c06\u706b\u7130\u7184\u706d\uff0c\u800c\u4e14\u6253\u4e94\u6346\u706b\u628a\uff0c\u8981\u5206\u4e09\u6b21\uff0c\u53ef\u89c1\u4ed6\u7684\u5185\u529b \u4e0d\u80fd\u6301\u7eed\uff0c\u56e0\u6b64\uff0c\u5f85\u4ed6\u8bf4\u5b8c\u4e4b\u540e\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u53eb\u6842\u4ef2\u660e\u4e5f\u70b9\u8d77\u4e94\u6346\u706b\u628a\uff0c\u5206\u63d2\u5730\u4e0a\uff0c\u7f13\u7f13\u8d70 \u51fa\uff0c\u8d70\u5230\u8ddd\u79bb\u706b\u628a\u4e94\u5c3a\u4e4b\u5904\uff0c\u500f\u4e00\u957f\u8eab\uff0c\u5de6\u624b\u4e00\u626c\u5411\u706b\u628a\u9065\u51fb\uff0c\u706b\u5149\u5e94\u624b\u800c\u706d\uff0c\u8fc5\u6377\u5f02\u5e38\u3002 \u7fa4\u8c6a\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u51cc\u672a\u98ce\u970d\u5730\u7ffb\u8eab\uff0c\u53f3\u624b\u4e00\u62ac\uff0c\u53c8\u628a\u7b2c\u4e8c\u6346\u706b\u628a\u6253\u706d\u3002\u51cc\u672a\u98ce\u6253\u706d\u4e8c\u6346\u706b \u628a\u4e4b\u540e\uff0c\u6f2b\u4e0d\u7ecf\u610f\u7684\u5237\u5730\u4e00\u4e2a\u65cb\u8eab\uff0c\u5de6\u53f3\u4e24\u624b\u4e00\u6325\uff0c\u4e09\u56db\u4e24\u6346\u706b\u628a\u540c\u65f6\u7184\u706d\u3002\u97e9\u8346\u5728\u6253\u7b2c\u4e09 \u6346\u706b\u628a\u65f6\uff0c\u8981\u8fde\u6362\u4e24\u638c\u7684\u529f\u592b\uff0c\u624d\u80fd\u6253\u706d\u3002\u51cc\u672a\u98ce\u5374\u80fd\u4e00\u6c14\u51fb\u706d\u56db\u6346\u706b\u628a\uff0c\u53ea\u6b64\u4e00\u7aef\uff0c\u80dc\u8d1f \u5df2\u5224\u3002\u5c1a\u6709\u6700\u540e\u4e00\u6346\uff0c\u51cc\u672a\u98ce\u5374\u5e76\u4e0d\u8feb\u8fd1\u7684\u53bb\uff0c\u5c31\u5728\u8ddd\u79bb\u4e08\u8bb8\u4e4b\u5730\uff0c\u731b\u5730\u811a\u4e0b\u4e00\u6ed1\u3002\u4e00\u4e2a \u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u53cd\u638c\u6325\u53bb\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u6700\u540e\u4e00\u6346\u706b\u628a\u7184\u706d\u4e86\u3002\u7fa4\u8c6a\u8f70\u7136\u53eb\u597d\uff0c\u51cc\u672a\u98ce\u9053\uff1a \u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u97e9\u8346\u9762\u8272\u94c1\u9752\uff0c\u6d53\u7709\u5012\u7ad6\uff0c\u72de\u7b11\u8bf4\u9053\uff1a\u201c\u5288\u7a7a\u638c\u7684\u529f\u592b\uff0c\u6211\u662f\u8f93\u4e86\u3002\u51cc\u5927\u4fa0\u521a\u624d\u8bf4\u8fc7\uff0c \u6bd4\u8bd5\u4e00\u6837\u6280\u4e1a\uff0c\u8d4c\u6ce8\u5c31\u662f\u4e00\u5c0a\u91d1\u7f57\u6c49\uff0c\u6709\u8fd9\u8bdd\u5417\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6709\u3002\u201d\u97e9\u8346\u9053\uff1a\u201c\u90a3\u4e48\u6211 \u540d\u4e0b\u6709\u4e24\u5c0a\u7f57\u6c49\uff0c\u6211\u8fd8\u8981\u518d\u8d4c\u4e00\u6837\u3002\u201d\u51cc\u672a\u51e4\u9053\uff1a\u201c\u518d\u8d4c\u4ec0\u4e48\uff1f\u201d\u97e9\u8346\u9053\uff1a\u201c\u6bd4\u8f7b\u529f\u3001\u5185 \u529f\u3001\u6697\u5668\u4e4b\u7c7b\uff0c\u90fd\u662f\u96d5\u866b\u5c0f\u6280\uff0c\u54b1\u4eec\u5e72\u8106\u5728\u5175\u5668\u4e0a\u89c1\u4e2a\u8f93\u8d62\u5427\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6089\u542c\u5c0a\u4fbf\uff0c \u4f60\u4eae\u62db\uff01\u201d\u97e9\u8346\u4f38\u624b\u5411\u8170\u95f4\u4e00\u62bd\uff0c\u628a\u88ab\u817e\u86df\u5251\u622a\u65ad\u7684\u534a\u622a\u62d0\u6756\u53d6\u4e86\u51fa\u6765\uff0c\u62a2\u7ad9\u7740\u4e0a\u9996\uff0c\u4e00\u4eae \u95e8\u6237\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u8d50\u62db\uff01\u201d \u3000\u3000\u97e9\u8346\u7684\u9f99\u5934\u62d0\u6756\uff0c\u672c\u6765\u6df1\u5f97\u897f\u85cf\u5929\u9b54\u6756\u6cd5\u7684\u771f\u4f20\uff0c\u867d\u7ed9\u622a\u77ed\uff0c\u4f46\u4ecd\u53ef\u7528\u3002\u800c\u4e14\u4ed6\u53c8\u7cbe\u4e8e \u70b9\u7a74\u529f\u592b\uff0c\u622a\u77ed\u4e4b\u540e\uff0c\u6b63\u53ef\u7528\u6765\u4f5c\u51cc\u672a\u98ce\u4e00\u4e2a\u201c\u65f1\u5730\u62d4\u8471\u201d\uff0c\u51ed\u7a7a\u8dc3\u8d77\u6570\u4e08\uff0c\u97e9\u8346\u77ed\u62d0\u4e00 \u6307\uff0c\u5728\u4ed6\u811a\u5e95\u5212\u8fc7\uff0c\u51cc\u672a\u98ce\u62a2\u4e86\u5148\u624b\uff0c\u66b4\u98ce\u9aa4\u96e8\u822c\u653b\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u65e5\u8fd1\u4e2d\u5929\uff0c\u7011\u5e03\u5728\u65e5\u5149\u7167\u5c04\u4e0b\uff0c\u6cdb\u51fa\u971e\u8f89\u4e3d\u5f69\uff0c\u51cc\u672a\u51e4\u4e00\u8fde\u5341\u51e0\u8fa3\u62db\uff0c\u628a\u97e9\u8346\u8feb\u5f97 \u5411\u65e5\u800c\u7acb\uff0c\u62a2\u5148\u5360\u4e86\u6709\u5229\u5730\u52bf\u3002\u97e9\u8346\u8000\u773c\u6b32\u82b1\uff0c\u83ab\u8bf4\u627e\u4e0d\u7740\u51cc\u672a\u98ce\u7684\u7a74\u9053\uff0c\u8fde\u62db\u67b6\u4e5f\u611f\u4e3a \u96be\u3002\u6b63\u60f3\u62fc\u547d\u6321\u8fc7\u51e0\u62db\uff0c\u62bd\u8eab\u4fbf\u9003\u3002\u51cc\u672a\u98ce\u5927\u559d\u4e00\u58f0\uff0c\u67af\u679d\u8d77\u5904\uff0c\u5df2\u662f\u4e00\u62db\u201c\u7389\u5e26\u7f20\u8170\u201d\uff0c \u5411\u97e9\u8346\u8170\u80c1\u62c2\u53bb\u3002\u97e9\u8346\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u65b9\u5f85\u95ea\u8fc7\uff0c\u51cc\u672a\u98ce\u653b\u52bf\u7ef5\u7ef5\u4e0d\u65ad\uff0c\u6a2a\u91cc\u4e00\u626b\uff0c\u65e9\u5df2\u53d8 \u62db\uff0c\u67af\u679d\u62c2\u5230\u80f8\u90e8\u3002\u97e9\u8346\u5fc3\u60f3\uff0c\u4e00\u624e\u67af\u679d\uff0c\u5176\u529b\u6709\u9650\uff0c\u62fc\u7740\u53d7\u4ed6\u62c2\u4e2d\uff0c\u7136\u540e\u62a2\u653b\uff0c\u56fe\u8c0b\u9003 \u8131\u3002\u90a3\u6599\u5fc3\u5ff5\u65b9\u52a8\uff0c\u9aa4\u611f\u80f8\u90fd\u4e00\u9635\u9178\u9ebb\uff0c\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u5168\u8eab\u762b\u8f6f\uff0c\u6251\u5730\u4fbf\u5012\u3002 \u3000\u3000\u539f\u6765\u51cc\u672a\u98ce\u9664\u4e86\u5251\u6cd5\u7cbe\u7edd\u4e4b\u5916\uff0c\u8fd8\u5f97\u4e86\u6666\u660e\u7985\u5e08\u201c\u62c2\u7a74\u201d\u7684\u771f\u4f20\u3002\u5173\u4e8e\u70b9\u7a74\u529f\u592b\uff0c\u4ece\u6765 \u53ea\u5206\u4e24\u6d3e\uff0c\u4e00\u6d3e\u662f\u7528\u5175\u5203\u6765\u201c\u6253\u7a74\u201d\uff0c\u4f8b\u5982\u97e9\u8346\u4ee5\u77ed\u62d0\u5f53\u4f5c\u70b9\u7a74\u6485\uff0c\u6765\u6253\u7a74\u9053\u4fbf\u662f\u3002\u4e00\u6d3e\u662f \u201c\u70b9\u7a74\u201d\uff0c\u4ee5\u201c\u7a7a\u624b\u5165\u767d\u5203\u201d\u7684\u529f\u592b\uff0c\u7528\u624b\u6307\u53bb\u70b9\u5bf9\u65b9\u7a74\u9053\uff0c\u800c\u6666\u660e\u7985\u5e08\u5374\u521b\u9020\u4e86\u4ee5\u62c2\u5c18 \u201c\u62c2\u7a74\u201d\u4e4b\u6cd5\uff0c\u7528\u62c2\u5c18\u626b\uff0c\u540c\u6837\u4e5f\u80fd\u5c01\u95ed\u654c\u4eba\u7a74\u9053\u3002 \u3000\u3000\u97e9\u8346\u5012\u5730\u4e0d\u8d77\uff0c\u7fa4\u8c6a\u54d7\u7136\u5927\u547c\u3002\u51cc\u672a\u98ce\u65e9\u5df2\u629b\u6389\u67af\u679d\uff0c\u62a2\u5728\u6765\u63f4\u6551\u7684\u8fbe\u58eb\u53f8\u7b49\u4eba\u4e4b\u524d\uff0c \u5c06\u97e9\u8346\u62c9\u8d77\uff0c\u8f7b\u8f7b\u5728\u4ed6\u8170\u9645\u7684\u201c\u4f0f\u5154\u7a74\u201d\u4e00\u62cd\uff0c\u5c06\u5c01\u95ed\u7684\u7a74\u9053\u89e3\u5f00\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u97e9\u8001\u524d \u8f88\uff0c\u8bf7\u6012\u65e0\u793c\uff0c\u51cc\u67d0\u5728\u8fd9\u53a2\u8d54\u7f6a\u4e86\uff01\u201d \u3000\u3000\u97e9\u8346\u9762\u5982\u8d64\u7802\uff0c\u9752\u7b4b\u6bd5\u9732\u7f9e\u60ed\u4ea4\u5e76\uff0c\u4e0d\u53d1\u4e00\u8a00\uff0c\u8ba9\u8fbe\u58eb\u53f8\u6276\u7740\u4fbf\u8d70\u3002\u51cc\u672a\u98ce\u53eb\u9053\uff1a\u201c\u97e9 \u8001\u524d\u8f88\uff0c\u8bf7\u7559\u6b65\u3002\u201d\u97e9\u8346\u505c\u4e86\u4e0b\u6765\uff0c\u6b63\u5f85\u6254\u51e0\u53e5\u95e8\u9762\u8bdd\uff0c\u51cc\u672a\u98ce\u53c8\u62db\u547c\u5176\u4ed6\u51e0\u4e2a\u672a\u4ea4\u624b\u7684\u4eba \u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u8981\u4e0d\u8981\u518d\u8d4c\uff1f\u201d \u3000\u3000\u672a\u4ea4\u624b\u7684\u4eba\u4e2d\uff0c\u7f57\u8fbe\u8eab\u53d7\u7bad\u4f24\uff0c\u81ea\u7136\u4e0d\u80fd\u6bd4\u8bd5\u3002\u8d3a\u4e07\u65b9\u662f\u4e00\u4e2a\u5de5\u5320\uff0c\u867d\u7136\u529f\u592b\u5728\u5bfb\u5e38\u6c5f \u6e56\u9053\u4e2d\uff0c\u4e5f\u7b97\u597d\u624b\uff0c\u4f46\u5982\u4f55\u6562\u4e0e\u51cc\u672a\u98ce\u6bd4\u8bd5\u3002\u5c1a\u6709\u4e00\u4e2a\u516b\u65b9\u5200\u5f20\u5143\u632f\uff0c\u6b66\u529f\u5c1a\u5728\u628a\u5f1f\u9ed1\u715e\u795e \u9676\u5b8f\u4e4b\u5bbf\uff0c\u6210\u540d\u8fdc\u5728\u51cc\u672a\u98ce\u4e4b\u524d\uff0c\u8fd9\uff0c\u4ed6\u4eec\u81ea\u7136\u77e5\u9053\u3002\u97e9\u8346\u4e0d\u77e5\u5085\u9752\u4e3b\u4e0e\u51cc\u672a\u98ce\u7684\u5173\u7cfb\uff0c\u8fd8 \u4ee5\u4e3a\u5085\u9752\u4e3b\u662f\u77e5\u9053\u9ec4\u91d1\u7684\u6d88\u606f\uff0c\u8fdc\u4ece\u6c5f\u5357\u8d76\u6765\uff0c\u8981\u72ec\u5360\u9ec4\u91d1\u7684\u3002\u4ed6\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5ffd\u7136\u5634\u89d2\u6302\u7740 \u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u53ef\u70ed\u95f9\u4e86\uff01\u8fd9\u91cc\u6709\u4e00\u4f4d\u51cc\u5927\u4fa0\u81ea\u79f0\u662f\u9ec4\u91d1\u7684\u4e3b\u4eba\uff0c\u73b0\u5728\u5085\u8001\u5148\u751f\u4e5f\u4ee3\u8868\u9ec4 \u91d1\u7684\u4e3b\u4eba\u6765\u4e86\uff01\u201d\u4ed6\u8bf4\u8fd9\u8bdd\uff0c\u5206\u660e\u662f\u60f3\u6311\u62e8\u5085\u9752\u4e3b\u548c\u51cc\u672a\u98ce\u4ea4\u624b\uff0c\u597d\u5750\u6536\u6e14\u4eba\u4e4b\u5229\u3002 \u3000\u3000\u90a3\u6599\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5085\u9752\u4e3b\u548c\u51cc\u672a\u98ce\u90fd\u54c8\u54c8\u5927\u7b11\u8d77\u6765\u3002\u5085\u9752\u4e3b\u7b11\u7f62\u95ee\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u8fd9\u4e48 \u8bf4\uff0c\u91d1\u7f57\u6c49\u4f60\u5df2\u7ecf\u627e\u5230\u4e86\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u9053\uff1a\u201c\u5168\u9760\u5192\u59d1\u5a18\u7684\u673a\u7075\uff0c\u662f\u627e\u5230\u4e86\uff01\u4f60\u53c8\u600e\u4e48\u77e5\u9053\u6d88\u606f\uff0c\u8fdc\u8fdc\u8d76\u6765\uff1f\u201d\u5085\u9752\u4e3b \u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u4f60\u5148\u62db\u547c\u8fd9\u73ed\u670b\u53cb\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u8fd9\u65f6\u4ece\u888b\u91cc\u53d6\u51fa\u4e00\u7eb8\u4fe1\u7b3a\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5404\u4f4d\u670b\u53cb\uff0c\u8fd9\u6279\u9ec4\u91d1\u4e0d\u662f\u6211\u7684\uff0c\u4e5f\u4e0d\u662f \u4f60\u4eec\u7684\uff0c\u5e94\u8be5\u662f\u5927\u5bb6\u90fd\u6709\u4efd\u3002\u9ec4\u91d1\u7684\u65e7\u4e3b\u4eba\u5728\u4fe1\u4e0a\u5df2\u7ecf\u660e\u660e\u767d\u767d\uff01\u201d\u5085\u9752\u4e3b\u95ee\u9053\uff1a\u201c\u4f60\u62ff\u7684 \u4fe1\u662f\u8c01\u4eba\u5199\u7684\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd9\u662f\u674e\u5b9a\u56fd\u5c06\u519b\u7684\u9057\u4e66\uff01\u201d\u8bf4\u7f62\u5927\u58f0\u5ff5\u8bf5\u8d77\u6765\uff01 \u3000\u3000\u51cc\u672a\u98ce\u5ff5\u5230\u201c\u7559\u5f85\u8c6a\u6770\u4e4b\u58eb\uff0c\u4ee5\u4e3a\u590d\u56fd\u4e4b\u8d44\uff0c\u82e5\u6709\u53d6\u4f5c\u79c1\u7528\u8005\uff0c\u4eba\u5929\u5171\u8bdb\u201d\u4e4b\u5904\uff0c\u505c\u987f \u4e0b\u6765\uff0c\u864e\u76ee\u73af\u626b\u5168\u573a\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u97e9\u8001\u524d\u8f88\u662f\u674e\u5c06\u519b\u65e7\u90e8\uff0c\u5e94\u8be5\u4f53\u5ff5\u5c06\u519b\u9057\u5fd7\uff0c\u8fd9\u6279\u9ec4\u91d1 \u662f\u62ff\u6765\u4f5c\u590d\u56fd\u4e4b\u7528\u7684\uff01\u201d\u8fbe\u58eb\u53f8\u53eb\u9053\uff1a\u201c\u90a3\u4f60\u53c8\u600e\u8bf4\u5927\u5bb6\u90fd\u6709\u4efd\uff1f\u201d\u51cc\u672a\u98ce\u5fae\u5fae\u4e00\u7b11\uff0c\u6307\u7740 \u5085\u9752\u4e3b\u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5085\u8001\u524d\u8f88\u662f\u4e3a\u8c01\u800c\u6765\u3002\u4ed6\u4ee3\u8868\u7684\u53ef\u4e0d\u662f\u4e00\u4e2a\u4eba\uff0c\u800c\u662f\u674e\u6765\u4ea8\u5c06\u519b\u624b\u4e0b \u7684\u5341\u4e07\u5144\u5f1f\uff01\u674e\u6765\u4ea8\u5c06\u519b\u662f\u674e\u95ef\u738b\u7684\u4f84\u5b59\uff0c\u674e\u95ef\u738b\u5f53\u5e74\u548c\u5f20\u732e\u5fe0\u662f\u7ed3\u4e49\u5144\u5f1f\u3002\u5f20\u732e\u5fe0\u548c\u674e\u5b9a \u56fd\u9057\u4e0b\u7684\u9ec4\u91d1\uff0c\u9664\u4e86\u4ed6\uff0c\u8fd8\u6709\u8c01\u6709\u8d44\u683c\u52a8\u7528\u3002\u201d\u2026\u201d\u51cc\u672a\u98ce\u5c1a\u672a\u8bf4\u5b8c\uff0c\u5085\u9752\u4e3b\u5c31\u63a5\u7740\u8bf4\u9053\uff1a \u201c\u662f\u5440\uff0c\u51cc\u5927\u4fa0\u8bf4\u5f97\u5bf9\u6781\u4e86\uff01\u8fd9\u6279\u9ec4\u91d1\uff0c\u8bf4\u8d77\u6765\u561b\uff0c\u8c01\u4e5f\u4e0d\u8be5\u89ca\u89ce\uff0c\u4f46\u8c01\u4e5f\u6709\u4efd\uff0c\u53ea\u8981\u4ed6\u53c2 \u52a0\u590d\u56fd\u7684\u5927\u4e1a\u3002\u674e\u6765\u4ea8\u5c06\u519b\u4e45\u4ef0\u5404\u4f4d\u5927\u540d\uff0c\u7279\u5730\u53eb\u6211\u6765\u9080\u8bf7\u5404\u4f4d\u5408\u4f5c\u3002\u201d\u6731\u5929\u6728\u8fc8\u524d\u4e24\u6b65\uff0c \u62c9\u7740\u97e9\u8346\u7684\u624b\u8bf4\u9053\uff1a\u201c\u97e9\u4e8c\u54e5\uff0c\u5085\u8001\u5148\u751f\u7684\u8bdd\u5168\u662f\u771f\u7684\uff01\u201d\u97e9\u8346\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u6731\u5929 \u6728\u7528\u6c89\u91cd\u7684\u58f0\u8c03\uff0c\u4e00\u5b57\u4e00\u53e5\u7684\u8bf4\u9053\uff1a\u201c\u97e9\u4e8c\u54e5\uff0c\u54b1\u4eec\u6709\u51e0\u5341\u5e74\u4ea4\u60c5\uff0c\u4f60\u522b\u602a\u6211\u3002\u662f\u6211\u4e13\u7a0b\u8d76 \u53bb\u544a\u8bc9\u674e\u5c06\u519b\u7684\uff0c\u6211\u4e3a\u7684\u4f60\u597d\uff01\u6211\u613f\u4f60\u665a\u5e74\u6709\u4e2a\u5f52\u5bbf\uff0c\u56de\u5230\u4e49\u519b\u4e4b\u4e2d\uff0c\u674e\u5c06\u519b\u4ed6\u4eec\uff0c\u53ef\u90fd\u5ff5 \u7740\u4f60\u4eec\u8fd9\u4e8c\u73ed\u524d\u8f88\u3002\u201d\u97e9\u8346\u542c\u4e86\uff0c\u4e24\u773c\u6f6e\u6e7f\uff0c\u9ed8\u4e0d\u4f5c\u58f0\u3002 \u3000\u3000\u539f\u6765\u6731\u5929\u6728\u3001\u6768\u9752\u6ce2\u3001\u6842\u5929\u6f9c\u3001\u97e9\u8346\u7b49\u56db\u4eba\uff0c\u5f53\u5e74\u5728\u674e\u5b9a\u56fd\u519b\u4e2d\uff0c\u79f0\u4e3a\u201c\u56db\u6770\u201d\uff0c\u56db\u6770 \u4e4b\u4e2d\uff0c\u53c8\u4ee5\u6842\u5929\u6f9c\u6b66\u529f\u6700\u5f3a\uff0c\u5176\u6b21\u5c31\u8981\u6570\u5230\u6731\u5929\u6728\u4e86\u3002\u6731\u5929\u6728\u548c\u97e9\u8346\u4ea4\u60c5\u6700\u597d\uff0c\u4f46\u90a3\u6b21\u85cf\u91d1 \u4e4b\u4e8b\uff0c\u674e\u5b9a\u56fd\u53ea\u6d3e\u6842\u5929\u6f9c\u548c\u97e9\u8346\u53bb\u4e3b\u6301\uff0c\u6731\u5929\u6728\u548c\u6768\u9752\u6ce2\u5374\u56e0\u53e6\u6709\u516c\u52a1\uff0c\u6ca1\u6709\u53c2\u4e0e\u5176\u4e8b\uff0c\u6240 \u4ee5\u5168\u4e0d\u77e5\u3002\u674e\u5b9a\u56fd\u4e8b\u8d25\u4e4b\u540e\u56db\u6770\u661f\u6563\uff0c\u97e9\u8346\u9690\u5728\u5ddd\u4e1c\uff0c\u6731\u5929\u6728\u9690\u5728\u5ddd\u897f\u3002\u6731\u5929\u6728\u9065\u95fb\u97e9\u8346\u8fd1 \u5e74\u548c\u7eff\u6797\u9ad8\u624b\u5f80\u6765\u9887\u5bc6\uff0c\u53c8\u4e0d\u613f\u6b63\u5f0f\u63ed\u8d77\u4e49\u65d7\uff0c\u5fc3\u4e2d\u9887\u4e3a\u62c5\u5fe7\uff0c\u5bb3\u6015\u4ed6\u8d70\u4e0a\u6b67\u9014\u3002\u5230\u97e9\u8346\u7ed9 \u7f57\u8fbe\u8bf4\u52a8\uff0c\u51c6\u5907\u593a\u53d6\u9ec4\u91d1\uff0c\u7279\u5730\u6765\u627e\u4ed6\u52a9\u62f3\u65f6\uff0c\u4ed6\u5927\u5403\u4e00\u60ca\uff0c\u4f46\u4ed6\u77e5\u9053\u97e9\u8346\u813e\u6c14\uff0c\u5f53\u65f6\u4e0d\u4fbf \u529d\u544a\uff0c\u56e0\u6b64\u4e5f\u4f6f\u5141\u76f8\u52a9\uff0c\u5e76\u548c\u97e9\u8346\u7ea6\u597d\u65e5\u671f\uff0c\u540c\u4f1a\u5e7d\u8c37\uff0c\u4ed6\u7b49\u97e9\u8346\u4e00\u51fa\u95e8\uff0c\u7d27\u8ddf\u7740\u5c31\u4fcf\u6084\u53bb \u901a\u77e5\u674e\u6765\u4ea8\u3002 \u3000\u3000\u81f3\u4e8e\u6768\u9752\u6ce2\u773c\u5149\u5374\u6ca1\u6709\u6731\u5929\u6728\u6765\u5f97\u8fdc\u5927\uff0c\u4ed6\u7b54\u5e94\u76f8\u52a9\u97e9\u8346\u4e4b\u540e\uff0c\u771f\u7684\u5982\u671f\u8d76\u5230\u5251\u9601\uff0c\u5148 \u53bb\u627e\u5bfb\u6842\u5929\u6f9c\uff0c\u51c6\u5907\u529d\u6842\u5929\u6f9c\u540c\u5206\u9ec4\u91d1\u3002\u4e0d\u6599\u5288\u5934\u5c31\u9047\u5230\u77f3\u5927\u5a18\uff0c\u4e00\u542c\u4ed6\u8bf4\u4ec0\u4e48\u8981\u5206\u9ec4\u91d1\u4e4b \u4e8b\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u4e00\u9635\u65cb\u98ce\u4e5f\u4f3c\u7684\u4e94\u79bd\u5251\u5c06\u4ed6\u8feb\u5f97\u624b\u5fd9\u811a\u4e71\u3002\u5e78\u597d\u6731\u5929\u6728\u8fd9\u65f6\u5df2\u4f1a\u9f50\u5085\u9752\u4e3b\u548c \u5f20\u9752\u539f\u7b49\u524d\u6765\uff0c\u624d\u7ed9\u4ed6\u89e3\u4e86\u56f4\uff0c\u6768\u9752\u6ce2\u542c\u8bf4\u6842\u5929\u6f9c\u4e8c\u5341\u5e74\u6765\u62a4\u536b\u85cf\u91d1\u4ee5\u53ca\u60e8\u6b7b\u4e4b\u4e8b\uff0c\u65e2\u53d7\u611f \u52a8\uff0c\u53c8\u5fc6\u65e7\u60c5\u3002\u5fc3\u4e2d\u4e5f\u81ea\u53c8\u6094\u53c8\u6068\u3002 \u3000\u3000\u6731\u5929\u6728\u5c06\u524d\u56e0\u540e\u679c\uff0c\u8bf4\u5b8c\u4e4b\u540e\uff0c\u7d27\u63e1\u7740\u97e9\u8346\u7684\u624b\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u97e9\u4e8c\u54e5\uff0c\u4f60\u542c\u6211\u4eec\u7684 \u8bdd\uff0c\u548c\u8fd9\u73ed\u82f1\u96c4\uff0c\u540c\u5230\u674e\u6765\u4ea8\u519b\u4e2d\u53bb\u5427\uff01\u201d\u97e9\u8346\u5c1a\u672a\u56de\u7b54\uff0c\u5362\u5927\u695e\u5b50\u5ffd\u5927\u58f0\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c \u4f60\u4f55\u4e0d\u65e9\u8bf4\u4e86\uff0c\u6211\u8ddf\u4f60\u4e89\u8fd9\u4e9b\u9ec4\u91d1\u5e72\u561b\uff1f\u201d\u51cc\u672a\u98ce\u559c\u9053\uff1a\u201c\u90a3\u2014\u2014\u4f60\u2026\u2026\u201d\u5362\u5927\u695e\u5b50\u6717\u58f0\u8bf4 \u9053\uff1a\u201c\u6211\u56de\u53bb\u5e26\u9752\u9633\u5e2e\u7684\u5168\u5e2e\u5144\u5f1f\u8ddf\u4f60\u4eec\u8d70\u597d\u5566\uff01\u201d\u4ed6\u8bf4\u5b8c\u540e\uff0c\u62c9\u7740\u7f57\u8fbe\u7684\u624b\u95ee\u9053\uff1a\u201c\u7f57\u5927 \u54e5\uff0c\u4f60\u5462\uff1f\u201d\u7f57\u8fbe\u5fc3\u611f\u51cc\u672a\u98ce\u8d60\u836f\u4e4b\u6069\uff0c\u8e0c\u8e87\u4e86\u4e00\u9635\uff0c\u4e5f\u6982\u7136\u8bf4\u9053\uff1a\u201c\u6211\u548c\u7709\u5c71\u5be8\u7684\u5144\u5f1f\uff0c \u542c\u4ece\u51cc\u5927\u4fa0\u7684\u5429\u5490\uff01\u201d\u51cc\u672a\u98ce\u4e0a\u524d\u628a\u4ed6\u4e00\u628a\u62b1\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u7f57\u5be8\u4e3b\uff0c\u522b\u8fd9\u6837\u8bf4\uff0c\u54b1\u4eec\u4eca\u540e\u90fd \u662f\u4e00\u5bb6\u4eba\u5566\uff01\u201d\u8fbe\u58eb\u53f8\u62cd\u638c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4e2a\u76f4\u80a0\u76f4\u809a\u7684\u4eba\uff0c\u6211\u8bf4\u5b9e\u8bdd\uff0c\u6211\u53ef\u4e0d\u80fd\u50cf\u4ed6\u4eec\u4e24\u4f4d \u90a3\u6837\u8ddf\u968f\u674e\u6765\u4ea8\u5c06\u519b\u3002\u201d\u5085\u9752\u4e3b\u5fae\u7b11\u7740\u671b\u4ed6\uff0c\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u8fbe\u58eb\u53f8\u8fbe\u4e09\u516c\u3002\u201d\u8fbe\u58eb\u53f8 \u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u662f\u4e2a\u58eb\u53f8\uff0c\u8fd9\u53ef\u628a\u6211\u7f1a\u6b7b\u4e86\u3002\u6211\u4e0d\u80fd\u79bb\u5f00\u65cf\u4eba\u3002\u4f46\uff0c\u6211\u5411\u4f60\u4eec\u7acb\u8a93\uff0c\u6211\u8fbe\u67d0 \u4eba\uff0c\u4ee5\u524d\u600e\u6837\u5bf9\u674e\u5b9a\u56fd\uff0c\u4eca\u540e\u4e00\u6837\u5bf9\u674e\u6765\u4ea8\u3002\u201d\u4ed6\u8fd9\u8bdd\u5373\u662f\u58f0\u660e\u613f\u548c\u674e\u6765\u4ea8\u5408\u4f5c\u3002\u51cc\u672a\u98ce\u9ad8 \u58f0\u53eb\u9053\uff1a\u201c\u597d\uff01\u4e00\u8a00\u4e3a\u5b9a\uff01\u201d\u8fbe\u58eb\u53f8\u4e00\u638c\u5411\u65c1\u8fb9\u4e00\u682a\u5c0f\u6811\u5288\u53bb\uff0c\u5c06\u90a3\u682a\u6811\u5288\u4e3a\u4e24\u6bb5\uff0c\u8bf4\u9053\uff1a \u201c\u82e5\u80cc\u8a93\u8a00\uff0c\u6709\u5982\u6b64\u6811\uff01\u201d \u3000\u3000\u97e9\u8346\u4e24\u773c\u6f6e\u6e7f\uff0c\u6731\u5929\u6728\u8fd8\u5728\u7d27\u63e1\u7740\u4ed6\u7684\u624b\uff0c\u4ed6\u624b\u5fc3\u611f\u7740\u4e00\u80a1\u6696\u610f\uff0c\u9762\u524d\u53c8\u6709\u90a3\u4e48\u591a\u671f\u5f85 \u7684\u773c\u5149\uff0c\u4ed6\u500f\u5730\u4e5f\u5c06\u77ed\u62d0\u62d7\u6298\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u548c\u4f60\u4eec\u5927\u5bb6\u4e00\u9f50\u8d70\uff01\u201d \u3000\u3000\u97e9\u8346\u548c\u5362\u5927\u695e\u5b50\u90fd\u613f\u5230\u674e\u6765\u4ea8\u519b\u4e2d\uff0c\u5269\u4e0b\u7684\u5f20\u5143\u632f\u3001\u9676\u5b8f\u7b49\u4eba\uff0c\u81ea\u7136\u4e5f\u65e0\u5f02\u8bae\u3002\u51cc\u672a\u98ce \u6536\u670d\u4e86\u8fd9\u73ed\u9b54\u5934\uff0c\u5fc3\u4e2d\u6781\u5176\u9ad8\u5174\u3002 \u3000\u3000\u5f53\u4e0b\u7531\u77f3\u5927\u5a18\u5e26\u8def\uff0c\u5927\u5bb6\u90fd\u56de\u5230\u90a3\u95f4\u77f3\u5c4b\uff0c\u77f3\u5927\u5a18\u7b11\u9053\uff1a\u201c\u4eca\u65e9\u6211\u4e0d\u8bb8\u4f60\u4eec\u8fdb\u53bb\uff0c\u73b0\u5728 \u6211\u5374\u8981\u8bf7\u4f60\u4eec\u8fdb\u6765\u4e86\uff01\u201d\u77f3\u5929\u6210\u548c\u7fa4\u8c6a\u76f8\u89c1\uff0c\u65e2\u6709\u65e7\u8bc6\uff0c\u4e5f\u6709\u65b0\u77e5\uff0c\u540c\u53d9\u5951\u9614\uff0c\u4e92\u9053\u4ef0\u6155\uff0c \u5fc3\u4e2d\u90c1\u95f7\uff0c\u4e0d\u89c9\u5168\u6d88\u3002\u4ed6\u4ee5\u8098\u652f\u5e8a\uff0c\u62ac\u8d77\u5934\u6765\u8bf4\u9053\uff1a\u201c\u81ea\u4ece\u6211\u660e\u767d\u4e8b\u60c5\u771f\u76f8\u4e4b\u540e\uff0c\u6211\u5fc3\u91cc\u4e00 \u76f4\u5c31\u5728\u96be\u8fc7\uff0c\u6211\u6df1\u6094\u81ea\u5df1\u8feb\u6b7b\u5e08\u5144\uff0c\u539f\u60f3\u5f85\u89c1\u8fc7\u4ef2\u660e\u4e4b\u540e\uff0c\u5c31\u81ea\u5c3d\u4ee5\u4e86\u7f6a\u5b7d\u3002\u5982\u4eca\u89c1\u4f60\u4eec\u8fd9 \u6837\u4e3a\u590d\u56fd\u5927\u4e8b\u5954\u8dd1\uff0c\u6211\u4eec\u5fc3\u60f3\u660e\u767d\u4e86\uff0c\u5fc3\u91cc\u7684\u6b7b\u7ed3\u4e5f\u89e3\u5f00\u4e86\uff0c\u539f\u6765\u6211\u9664\u4e86\u8feb\u6b7b\u5e08\u5144\u4e4b\u5916\u3002\u8fd8 \u505a\u8fc7\u4e00\u4ef6\u66f4\u5927\u7684\u9519\u4e8b\uff01\u201d\u77f3\u5927\u5a18\u5947\u602a\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u66f4\u5927\u7684\u9519\u4e8b\uff1f\u201d\u77f3\u5929\u6210\u9053\uff1a\u201c\u4e09\u5341\u5e74 \u6765\uff0c\u6211\u90fd\u662f\u4e3a\u7740\u4e2a\u4eba\u6069\u6028\uff0c\u4e1c\u98d8\u897f\u8361\uff0c\u4ece\u6765\u6ca1\u6709\u505a\u8fc7\u4e00\u4ef6\u503c\u5f97\u79f0\u9053\u7684\u4e8b\u3002\u5929\u6f9c\u548c\u4f60\u7684\u4e8b\u4e1a\uff0c \u6211\u5b8c\u5168\u4e0d\u7406\u4e0d\u776c\u3002\u8fd9\u4e09\u5341\u5e74\u7b97\u662f\u767d\u8fc7\u5566\uff01\u6211\u6b7b\u4e86\u4e5f\u5bf9\u4e0d\u4f4f\u5e08\u5144\uff0c\u4e0d\u5982\u6d3b\u4e0b\u6765\u7ee7\u627f\u4ed6\u7684\u9057\u5fd7\u8fd8 \u597d\uff0c\u6211\u4f24\u597d\u4e4b\u540e\uff0c\u4e00\u5b9a\u4e5f\u5230\u674e\u6765\u4ea8\u519b\u4e2d\uff0c\u5728\u4f24\u672a\u597d\u4e4b\u524d\uff0c\u6211\u60f3\u548c\u4f60\u7559\u5728\u8fd9\u91cc\uff0c\u5b88\u536b\u9ec4\u91d1\uff0c\u4f8d \u674e\u5c06\u519b\u6d3e\u4eba\u5b8c\u5168\u628a\u5b83\u642c\u8d70\u4e3a\u6b62\u3002\u5e08\u5144\u5b88\u536b\u4e86\u4e8c\u5341\u5e74\uff0c\u8fd9\u62c5\u5b50\u4e5f\u8be5\u6211\u4eec\u4ee3\u6311\u4e86\u3002\u201d\u77f3\u5927\u5a18\u60f3\u8d77 \u5929\u6f9c\uff0c\u6cea\u6d41\u6ee1\u9762\uff0c\u4e00\u9762\u6d41\u6cea\uff0c\u4e00\u9762\u7b11\u9053\uff1a\u201c\u662f\u8be5\u5982\u6b64\uff01\u201d\u5085\u9752\u4e3b\u6b63\u5728\u62c5\u5fc3\u4e00\u65f6\u642c\u8fd0\u4e0d\u4e86\uff0c\u7559 \u5f88\u591a\u4eba\u5b88\u536b\uff0c\u53c8\u6050\u8bef\u4e86\u5176\u4ed6\u7684\u4e8b\u3002\u542c\u4ed6\u8fd9\u6837\u4e00\u8bf4\uff0c\u6781\u4e3a\u6b22\u559c\u3002 \u3000\u3000\u8fd9\u65f6\u77f3\u5929\u6210\u7684\u5f92\u5f1f\u4e8e\u4e2d\u8d70\u4e86\u8fc7\u6765\uff0c\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u8fd8\u6709\u4e00\u4ef6\u5927\u4e8b\u5462\uff01\u201d \u3000\u3000\u77f3\u5929\u6210\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8fd9\u6837\u795e\u795e\u79d8\u79d8\u7684\uff1f\u201d\u4e8e\u4e2d\u7b11\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4ed6\u4eec\u6253\u4e86\u5927\u534a\u5929\uff0c\u90fd \u8fd8\u6ca1\u5403\u4e1c\u897f\u5462\u3002\u54b1\u4eec\u662f\u4e3b\u4eba\uff0c\u53ea\u987e\u548c\u5ba2\u4eba\u804a\u5929\uff0c\u4e0d\u987e\u4ed6\u4eec\u7684\u809a\u5b50\uff0c\u90a3\u600e\u4e48\u6210\uff1f\u4eba\u4e0d\u5403\u4e1c\u897f\u5c31 \u4f1a\u6b7b\uff0c\u4f60\u8bf4\u90a3\u4e0d\u662f\u5927\u4e8b\u4e48\uff1f\u201d\u7fa4\u8c6a\u90fd\u7b11\u4e86\u8d77\u6765\u3002\u4e00\u5ba4\u878d\u878d\u5982\u6625\uff0c\u7d27\u5f20\u7684\u6c14\u6c1b\uff0c\u4e5f\u5728\u7b11\u58f0\u4e2d\u7f13 \u548c\u4e86\u3002 \u3000\u3000\u7b11\u58f0\u4e2d\uff0c\u7af9\u541b\u6367\u7740\u4e00\u5927\u76d8\u7cdf\u7c91\u548c\u70e4\u7f8a\u8089\u8fdb\u6765\uff0c\u7cdf\u7c91\u662f\u628a\u7092\u719f\u7684\u7a1e\u9ea6\u78e8\u6210\u7c97\u7c89\uff0c\u5403\u65f6\u52a0\u5165 \u9165\u6cb9\uff0c\u7528\u624b\u62cc\u5300\u634f\u6210\u9984\u9968\u7684\u6837\u5b50\uff0c\u5012\u662f\u522b\u6709\u98ce\u5473\u3002\u90a3\u70e4\u7f8a\u8089\u5219\u662f\u77f3\u5927\u5a18\u524d\u4e24\u5929\u730e\u83b7\u7684\u5c71\u7f8a\u70e4 \u6210\u7684\u3002\u8fd9\u65f6\u4e00\u5e76\u6367\u4e86\u51fa\u6765\uff0c\u7fa4\u8c6a\u624b\u56e2\u7cdf\u7c91\uff0c\u62d4\u5200\u5272\u8089\uff0c\u5403\u5f97\u5341\u5206\u9ad8\u5174\u3002 \u3000\u3000\u8fdb\u98df\u65f6\u5085\u9752\u4e3b\u4e00\u76f4\u6ce8\u89c6\u7740\u6842\u4ef2\u660e\uff0c\u89c1\u4ed6\u795e\u60c5\u5df2\u5b8c\u5168\u6062\u590d\u6b63\u5e38\uff0c\u5fc3\u4e2d\u5927\u6170\u3002\u6084\u6084\u5730\u5bf9\u5192\u6d63 \u83b2\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u771f\u884c\uff0c\u8fd9\u4e2a\u75c5\u4eba\uff0c\u4e5f\u53ea\u6709\u4f60\u624d\u533b\u5f97\u597d\uff01\u201d\u5192\u6d63\u83b2\u9762\u4e0a\u6392\u7ea2\uff0c\u201c\u54d7\u201d\u4e86\u4e00\u58f0 \u9053\uff1a\u201c\u4f2f\u4f2f\u4f60\u53c8\u6765\u548c\u6211\u5f00\u73a9\u7b11\u3002\u201d\u5085\u9752\u4e3b\u5728\u5979\u7684\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u548c\u4f60\u5f00\u73a9\u7b11\uff0c\u7b49\u4f1a\u6211\u6709\u8bdd \u8ddf\u4f60\u8bf4\u54e9\uff01\u201d\u77f3\u5927\u5a18\u5bf9\u5192\u6d63\u83b2\u6781\u4e3a\u597d\u611f\uff0c\u4e0d\u65f6\u7684\u5207\u4e30\u8089\u7ed9\u5979\uff0c\u7af9\u541b\u9f13\u7740\u5c0f\u5634\u5df4\u9053\uff1a\u201c\u77a7\uff0c\u5988 \u5988\uff0c\u4f60\u89c1\u4e86\u5192\u59d0\u59d0\uff0c\u5c31\u53ea\u75bc\u5979\u4e0d\u75bc\u5973\u513f\u4e86\u3002\u201d\u8bf4\u5f97\u4f17\u4eba\u53c8\u90fd\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u665a\u6842\u4ef2\u660e\u5348\u591c\u9192\u6765\uff0c\u770b\u7740\u81ea\u5df1\u7684\u7236\u4eb2\u7761\u5728\u8eab\u8fb9\uff0c\u4e0d\u7981\u601d\u6f6e\u8d77\u4f0f\uff0c\u518d\u4e5f\u65e0\u6cd5\u5b89\u773c\u3002\u4ed6\u60f3 \u7740\u81ea\u5df1\u79bb\u5947\u7684\u8eab\u4e16\uff0c\u60f3\u7740\u6559\u517b\u81ea\u5df1\u6210\u4eba\u7684\u517b\u7236\u6842\u5929\u6f9c\uff0c\u4eca\u65e5\u4e00\u5bb6\u56e2\u5706\uff0c\u771f\u662f\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\uff0c \u4ed6\u53c8\u559c\u53c8\u60b2\uff0c\u770b\u7740\u719f\u7761\u7684\u7238\u7238\uff0c\u89c9\u5f97\u4ed6\u5f88\u53ef\u601c\uff0c\u4f46\u60f3\u8d77\u517b\u7236\uff0c\u5374\u66f4\u662f\u53ef\u601c\u3002\u4ed6\u5ffd\u7136\u60f3\u8d77\uff1a\u660e \u5929\u6211\u5c31\u8981\u548c\u5927\u4f19\u4e00\u9053\u5230\u674e\u6765\u4ea8\u90a3\u91cc\u4e86\uff0c\u6211\u8be5\u53bb\u62dc\u522b\u517b\u7236\u7684\u575f\u5893\u3002\u4ed6\u542c\u5192\u6d63\u83b2\u8bf4\u8fc7\uff0c\u6842\u5929\u6f9c\u662f \u5979\u548c\u5085\u9752\u4e3b\u4eb2\u624b\u57cb\u846c\u7684\uff0c\u523b\u6709\u201c\u4e49\u58eb\u6842\u5929\u6f9c\u4e4b\u5893\u201d\u51e0\u4e2a\u5927\u5b57\uff0c\u53ea\u4e0d\u77e5\u846c\u5728\u90a3\u91cc\u3002\u4ed6\u611f\u60c5\u5982\u6ce2 \u6f6e\u6fc0\u8361\uff0c\u987e\u4e0d\u4e86\u907f\u5acc\uff0c\u7ade\u5077\u5077\u5730\u8d77\u6765\uff0c\u4fcf\u6084\u5730\u5f80\u7528\u677f\u95f4\u5f00\u7684\u5185\u5ba4\u4e00\u77a7\uff0c\u53ea\u89c1\u6bcd\u4eb2\u6263\u59b9\u59b9\u7761\u5f97 \u5f88\u751c\uff0c\u5192\u6d63\u83b2\u7684\u5f71\u5b50\u5374\u4e0d\u89c1\u4e86\u3002\u4ed6\u5927\u5403\u4e00\u60ca\uff0c\u4e00\u95ea\u8eab\u5c31\u51fa\u4e86\u77f3\u5c4b\uff0c\u5728\u5fae\u5f31\u7684\u661f\u5149\u4e0b\uff0c\u5728\u5e7d\u8c37 \u4e2d\u56db\u5904\u627e\u5bfb\u3002\u53ea\u542c\u5f97\u733f\u7334\u591c\u557c\uff0c\u677e\u6d9b\u8fc7\u8033\uff0c\u79cb\u866b\u5982\u79c1\u8bed\uff0c\u5c71\u7011\u82e5\u6c89\u96f7\u3002\u5904\u5904\u79cb\u58f0\uff0c\u6c47\u6210\u5929 \u7c41\uff0c\u6842\u4ef2\u660e\u867d\u5728\u5251\u9601\u957f\u5927\uff0c\u5374\u4e0d\u66fe\u9886\u7565\u8fc7\u5982\u6b64\u5883\u754c\uff0c\u4ed6\u5728\u5e7d\u8c37\u91cc\u8e3d\u8e3d\u72ec\u884c\uff0c\u601d\u6f6e\u8d77\u4f0f\u3002\u731b\u7136 \u95f4\u80a9\u5934\u5237\u7684\u7ed9\u4eba\u6309\u4e86\u4e00\u4e0b\uff0c\u4ed6\u970d\u7136\u8df3\u8d77\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u5728\u8033\u8fb9\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u4f60\u627e\u8c01\uff1f\u201d\u6842\u4ef2\u660e \u56de\u5934\u4e00\u770b\uff0c\u539f\u6765\u662f\u51cc\u672a\u98ce\uff0c\u4e0d\u7981\u8d5e\u9053\uff1a\u201c\u51cc\u5927\u4fa0\u597d\u4fca\u8eab\u624b\uff01\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u89c1\u4f60\u4ece\u77f3\u5c4b\u91cc \u8df3\u51fa\u6765\uff0c\u5c31\u7f00\u5728\u4f60\u7684\u8eab\u540e\uff0c\u4f60\u53ea\u5411\u524d\u9762\u548c\u4e24\u8fb9\u5f20\u671b\uff0c\u663e\u5f97\u5fc3\u795e\u4e0d\u5c5e\uff0c\u6211\u731c\u4f60\u5927\u7ea6\u662f\u627e\u4ec0\u4e48\u4eba \u6765\u4e86\uff0c\u4f60\u5b8c\u5168\u6ca1\u6ce8\u610f\u5230\u6211\u8ddf\u5728\u4f60\u7684\u540e\u9762\u3002\u201d \u3000\u3000\u6842\u4ef2\u660e\u9053\uff1a\u201c\u4f60\u53ef\u89c1\u7740\u5192\u59d1\u5a18\u3002\u201d\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u6211\u731c\u4f60\u51c6\u662f\u627e\u5979\u6765\u4e86\uff0c\u4f60\u968f\u6211\u6765\u3002\u201d \u8bf4\u7f62\u9886\u7740\u4f73\u4ef2\u660e\u7ffb\u8fc7\u51e0\u5904\u5c71\u5773\uff0c\u731b\u7136\u63a8\u4ed6\u4e00\u628a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u628a\u8033\u6735\u8d34\u5728\u5730\u4e0a\u9759\u542c\u3002\u201d \u3000\u3000\u4f0f\u5730\u542c\u58f0\uff0c\u53ef\u4ee5\u542c\u5f97\u597d\u8fdc\u597d\u8fdc\u3002\u6842\u4ef2\u660e\u51dd\u795e\u9759\u542c\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u8001\u8005\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u70f7 \u83b2\uff0c\u4ed6\u7684\u795e\u667a\u65e2\u5b8c\u5168\u60ed\u590d\uff0c\u90a3\u4f60\u770b\u4ed6\u80fd\u62c5\u5f53\u5f97\u8fd9\u4ef6\u5927\u4e8b\u5417\uff1f\u201d\u6842\u4ef2\u660e\u8bb6\u7136\u5bf9\u51cc\u672a\u98ce\u9053\uff1a\u201c\u90a3 \u4e0d\u662f\u5085\u8001\u524d\u8f88\u7684\u58f0\u97f3\uff1f\u201d\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u6b63\u5728\u8bf4\u4f60\u5462\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5085\u9752\u4e3b\u5ffd\u7136\u54c8\u54c8\u5927 \u7b11\uff0c\u4f20\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u5fc5\u5077\u542c\u4e86\uff0c\u5feb\u8fc7\u6765\u5427\u3002\u201d\u51cc\u672a\u98ce\u4e00\u8dc3\u800c\u8d77\uff0c\u62c9\u7740\u6842\u4ef2\u660e\u8fc7\u53bb\uff0c\u8bf4 \u9053\uff1a\u201c\u5230\u5e95\u59dc\u662f\u8001\u7684\u8fa3\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u548c\u5192\u6d63\u83b2\u501a\u7740\u4e00\u5757\u5ca9\u77f3\u8bf4\u8bdd\uff0c\u89c1\u4ed6\u4eec\u8fc7\u6765\uff0c\u62db\u62db\u624b\u9053\uff1a\u201c\u6211\u65e9\u6599\u5230\u4f60\u4eec\u4f1a\u6765 \u7684\u3002\u201d\u6842\u4ef2\u660e\u62a2\u7740\u95ee\u9053\uff1a\u201c\u5085\u4f2f\u4f2f\uff0c\u5192\u59d0\u59d0\uff0c\u6709\u4ec0\u4e48\u8981\u7d27\u4e8b\u60c5\uff0c\u8981\u5728\u534a\u591c\u5546\u8bae\uff1f\u201d\u5085\u9752\u4e3b\u7b11 \u9053\uff1a\u201c\u4eca\u5929\u767d\u5929\u6211\u5bf9\u5979\u8bf4\u4e86\u4e00\u756a\u8bdd\u540e\uff0c\u7d2f\u5979\u7761\u4e0d\u7740\uff0c\u534a\u591c\u91cc\u8d77\u6765\u8981\u627e\u6211\u8c08\u5462\uff01\u201d\u51cc\u672a\u98ce\u8bb6\u7136 \u95ee\u9053\uff1a\u201c\u5230\u5e95\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5085\u9752\u4e3b\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5728\u8fd9\u5e7d\u8c37\u91cc\u9762\uff0c\u4e0d\u77e5\u9053\u5916\u9762\u53c8\u5df2\u6362\u4e86\u4e00\u756a\u4e16\u754c\u5462\uff01\u201d\u51cc\u672a\u98ce\u9053\uff1a \u201c\u5434\u4e09\u6842\u8fd9\u53ae\u8d77\u4e8b\u4e86\uff1f\u8fd9\u6837\u5feb\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u5c31\u662f\uff0c\u4f60\u4eec\u628a\u674e\u516c\u5b50\u6551\u51fa\u6765\uff0c\u4ed6\u6015\u98ce\u58f0\u6cc4 \u6f0f\uff0c\u63d0\u524d\u8d77\u4e8b\u4e86\u5462\uff01\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4ed6\u4e0d\u548c\u6211\u4eec\u8054\u7edc\u4e86\uff1f\u201d\u5085\u9752\u4e3b\u9012\u8fc7\u4e00\u5f20\u7eb8\u9053\uff1a\u201c\u4f60\u770b\u8fd9 \u5c31\u662f\u4ed6\u7684\u6a84\u6587\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u597d\uff0c\u6211\u5012\u8981\u770b\u4ed6\u600e\u6837\u7740\u7b14\u3002\u201d \u3000\u3000\u53ea\u89c1\u6a84\u6587\u4e0a\u5148\u53d9\u5f53\u5e74\u4e4b\u4e8b\uff0c\u9a82\u674e\u95ef\u738b\u4e3a\u8d3c\uff0c\u8bf4\u674e\u95ef\u738b\u5165\u4eac\u4e4b\u540e\uff0c\u201c\u666e\u5929\u4e4b\u4e0b\u7adf\u65e0\u4ed7\u4e49\u5174 \u5e08\u3001\u52e4\u738b\u8ba8\u8d3c\u8005\uff0c\u4f24\u54c9\u56fd\u8fd0\uff0c\u592b\u590d\u4f55\u8a00\uff1f\u672c\u9547\u72ec\u5c45\u5173\u5916\uff0c\u77e2\u5c3d\u5175\u7a77\uff0c\u6cea\u5e72\u6709\u8840\uff0c\u5fc3\u75db\u65e0\u58f0\uff1b \u4e0d\u5f97\u5df2\u6ef4\u8840\u8ba2\u76df\uff0c\u8bb8\u864f\u85e9\u5c01\u3002\u6682\u501f\u5937\u5175\u5341\u4e07\uff0c\u8eab\u4e3a\u524d\u9a71\u3002\u201d\u51cc\u672a\u98ce\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e8f\u4ed6 \u8bf4\u5f97\u51fa\u6765\uff0c\u8fd8\u60f3\u6d17\u8131\u7f6a\u540d\u3002\u201d\u518d\u5ff5\u4e0b\u53bb\u9053\uff1a\u201c\u4e0d\u610f\u72e1\u864f\u9006\u5929\u80cc\u76df\u3002\u4e58\u6211\u5185\u865a\uff0c\u96c4\u636e\u71d5\u90fd\uff0c\u7a83 \u6211\u5148\u671d\u795e\u5668\uff0c\u53d8\u6211\u4e2d\u56fd\u8863\u51a0\uff01\u65b9\u52a0\u62d2\u864e\u8fdb\u72fc\u4e4b\u975e\uff0c\u83ab\u633d\u62b1\u85aa\u6551\u706b\u4e4b\u8bef\u3002\u201d\u5e95\u4e0b\u81ea\u7136\u5c31\u662f\u5199\u56e0 \u6b64\u8981\u8d77\u5175\u4e86\u3002\u51cc\u672a\u98ce\u628a\u5434\u4e09\u6842\u6a84\u6587\u63b7\u5728\u5730\u4e0a\uff0c\u6068\u5f97\u7259\u9f7f\u54ac\u5f97\u683c\u683c\u4f5c\u58f0\uff0c\u5085\u9752\u738b\u9053\uff1a\u201c\u6b63\u662f\u56e0 \u6b64\uff0c\u6240\u4ee5\u6211\u624d\u8981\u4ef2\u660e\u548c\u6d63\u83b2\u53bb\u5e72\u4e00\u6869\u5927\u4e8b\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u9053\uff1a\u201c\u90a3\u674e\u6765\u4ea8\u5c06\u519b\u51c6\u5907\u600e\u6837\u5e94\u4ed8\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6309\u8bf4\u5434\u4e09\u6842\u548c\u6211\u4eec\u6709\u4e0d\u5171\u6234 \u5929\u4e4b\u4ec7\uff0c\u6211\u4eec\u51b3\u4e0d\u80fd\u8f7b\u8f7b\u653e\u8fc7\u4ed6\uff0c\u4f46\u4ed6\u8fd9\u6b21\u4e3e\u4e8b\uff0c\u5230\u5e95\u6253\u4e86\u6ee1\u5974\uff0c\u56e0\u6b64\u674e\u601d\u6c38\u8bf4\uff0c\u7eb5\u8bb8\u5434\u4e09 \u6842\u4e00\u9762\u53cd\u6e05\uff0c\u4e00\u9762\u53cd\u5bf9\u6211\u4eec\uff0c\u6211\u4eec\u73b0\u523b\u4e5f\u4e0d\u5b9c\u4e0e\u4ed6\u4e3a\u654c\u3002\u674e\u516c\u5b50\u5b9a\u4e0b\u7684\u7b56\u7565\u662f\uff1a\u8d81\u8fd9\u4e2a\u65f6 \u673a\uff0c\u6211\u4eec\u4e5f\u6269\u5927\u53cd\u6e05\u3002\u6211\u4eec\u548c\u5434\u4e09\u6842\u5404\u5e72\u5404\u7684\uff0c\u4ed6\u82e5\u4e0d\u72af\u6211\u4eec\uff0c\u6211\u4eec\u4e5f\u4e0d\u72af\u4ed6\u3002\u4e00\u9762\u4fdd\u6301\u5ddd \u6ec7\u8fb9\u533a\uff0c\u4e00\u9762\u53d1\u52a8\u5404\u5904\u82f1\u8c6a\uff0c\u63ed\u7aff\u8d77\u4e49\u3002\u201d\u51cc\u672a\u98ce\u9f13\u638c\u8d5e\u9053\uff1a\u201c\u674e\u516c\u5b50\u773c\u5149\u771f\u975e\u5e38\u4eba\u53ef\u53ca\uff0c \u90a3\u674e\u5c06\u519b\u662f\u4e0d\u662f\u542c\u4ed6\u5f1f\u5f1f\u7684\u8bdd\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u674e\u5c06\u519b\u5df2\u5c06\u5175\u7b26\u4ea4\u7ed9\u4ed6\u7684\u5f1f\u5f1f\uff0c\u4efb\u7531\u4ed6\u5904\u7f6e \u4e86\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4eec\u90fd\u613f\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\u3002\u4f46\u4ef2\u660e\u8d24\u5f1f\u867d\u7136\u82f1\u96c4\uff0c\u5374\u662f\u521d\u6b21\u51fa \u9053\uff0c\u4e0d\u77e5\u674e\u5c06\u519b\u8981\u6d3e\u4ed6\u5e72\u4ec0\u4e48\u5927\u4e8b\uff1f\u201d\u4ed6\u662f\u62c5\u5fc3\u6842\u4ef2\u660e\u7ecf\u9a8c\u592a\u5c11\uff0c\u4f1a\u51fa\u5c94\u5b50\u3002 \u3000\u3000\u5085\u9752\u4e3b\u7b11\u9053\uff1a\u201c\u6b63\u56e0\u4ed6\u662f\u521d\u6b21\u51fa\u9053\uff0c\u6c5f\u6e56\u4e0a\u65e0\u4eba\u8bc6\u4ed6\uff0c\u8fd9\u4ef6\u4e8b\u624d\u9002\u5408\u4ed6\u53bb\u505a\u3002\u8bf4\u7f62\u95ee\u5192 \u6d63\u83b2\u9053\uff1a\u201c\u4f60\u8fd8\u8bb0\u5f97\u6613\u5170\u73e0\u59d0\u59d0\u548c\u5f20\u534e\u662d\u516c\u5b50\u5417\uff1f\u201d\u51cc\u672a\u98ce\u5fc3\u5934\u4e00\u9707\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u6613\u5170\u73e0 \u5979\u600e\u4e48\u4e86\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u5f53\u65e5\u7fa4\u96c4\u5927\u95f9\u4e94\u53f0\u5c71\uff0c\u5f20\u534e\u662d\u5931\u624b\u5c31\u64d2\uff0c\u6613\u5170\u73e0\u81ea\u544a\u594b\u52c7\uff0c\u613f\u5165 \u4eac\u6551\u4ed6\u3002\u8c01\u77e5\u5979\u8d74\u4eac\u4e4b\u540e\uff0c\u5c31\u5982\u6ce5\u725b\u5165\u6d77\uff0c\u5168\u65e0\u6d88\u606f\u3002\u5012\u662f\u5f20\u516c\u5b50\u6709\u6d88\u606f\u4f20\u6765\u4e86\u3002\u2019\u5192\u6d63\u83b2 \u95ee\u9053\uff1a\u201c\u4ed6\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u5192\u6d63\u83b2\u521d\u4e0a\u4e94\u53f0\u5c71\u65f6\uff0c\u66fe\u7ed9\u5f20\u534e\u662d\u649e\u8fc7\u4e00\u8180\uff0c\u5370\u8c61\u751a\u4e3a\u6df1\u523b\u3002 \u3000\u3000\u5085\u9752\u4e3b\u9053\uff1a\u201c\u636e\u660e\u964d\u5b98\u4f20\u7ed9\u5728\u4eac\u7684\u9c81\u738b\u65e7\u90e8\u7684\u6d88\u606f\uff0c\u8bf4\u4ed6\u7adf\u662f\u5728\u7eb3\u5170\u76f8\u5e9c\uff01\u201d\u5192\u6d63\u83b2 \u9053\uff1a\u201c\u662f\u88ab\u76d1\u7981\u4e86\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u6709\u4e00\u4e2a\u964d\u5b98\u5230\u7eb3\u5170\u76f8\u5e9c\u4f5c\u5ba2\uff0c\u89c1\u7eb3\u5170\u516c\u5b50\u6709\u4e00\u4e2a \u4e66\u50ee\uff0c\u975e\u5e38\u50cf\u4ed6\u3002\u8fd9\u4e2a\u4eba\u4ee5\u524d\u8ddf\u8fc7\u5f20\u516c\u5b50\u7684\u7236\u4eb2\u5f20\u714c\u8a00\uff0c\u5077\u5077\u8bf4\u4e86\u51fa\u6765\u3002\u201d\u5192\u6d63\u83b2\u53c8\u9053\uff1a \u201c\u4ee5\u5f20\u516c\u5b50\u7684\u6b66\u529f\uff0c\u4ea6\u975e\u6cdb\u6cdb\uff0c\u65e2\u7136\u4e0d\u662f\u53d7\u76d1\u7981\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u9003\u51fa\u6765\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u8fd9\u5c31\u4e0d \u77e5\u9053\u4e86\uff01\u6240\u4ee5\u624d\u8981\u4f60\u548c\u4ef2\u660e\u8fdb\u4eac\u4e00\u8d9f\uff0c\u53bb\u63a2\u8bbf\u4ed6\u4eec\uff0c\u5018\u82e5\u65e0\u6cd5\u52a9\u4ed6\u51fa\u8d70\uff0c\u4f60\u5c31\u8054\u7edc\u90a3\u8fb9\u5929\u5730 \u4f1a\u548c\u9c81\u738b\u65e7\u90e8\uff0c\u628a\u4ed6\u6551\u51fa\u6765\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u95ee\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u5218\u90c1\u82b3\u7684\u610f\u601d\uff1f\u201d\u5085\u9752\u4e3b\u70b9\u70b9\u5934\u9053\uff1a\u201c\u674e\u5c06\u519b\u4e5f\u8d5e\u540c\u5979\u7684\u610f\u601d\u3002 \u5f20\u714c\u8a00\u662f\u524d\u671d\u7684\u6297\u6e05\u5927\u5c06\uff0c\u9c81\u738b\u4fbf\u662f\u4ed6\u6240\u62e5\u7acb\u7684\uff0c\u6c5f\u5357\u4e00\u5e26\uff0c\u4e0d\u5c11\u9c81\u738b\u65e7\u90e8\uff0c\u8bb8\u591a\u964d\u5b98\u4e5f\u66fe \u662f\u4ed6\u7684\u90e8\u4e0b\u3002\u5218\u90c1\u82b3\u73b0\u5728\u4e0d\u80fd\u56de\u53bb\uff0c\u56e0\u6b64\uff0c\u8bf7\u6211\u4eec\u5e2e\u5fd9\uff0c\u8bbe\u6cd5\u6551\u5f20\u516c\u5b50\u51fa\u6765\uff0c\u5185\u5730\u53f7\u53ec\u4ed6\u7236 \u4eb2\u7684\u65e7\u90e8\uff0c\u5728\u6c5f\u5357\u548c\u6211\u4eec\u4f5c\u6874\u9f13\u4e4b\u5e94\u3002\u6211\u4eec\u60f3\u6765\u60f3\u53bb\uff0c\u4eba\u9009\u53ea\u6709\u4f60\u4eec\u4e24\u4eba\u6700\u4e3a\u9002\u5408\u3002\u4ef2\u660e\u6b66 \u653b\u5f3a\uff0c\u53c8\u6ca1\u4eba\u8bc6\u4ed6\uff0c\u6df7\u8fdb\u4eac\u57ce\uff0c\u6599\u975e\u96be\u4e8b\uff0c\u6d63\u83b2\u8ddf\u6211\u8d70\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u6c5f\u6e56\u4e0a\u7684\u4e8b\u60c5\uff0c\u5927\u534a\u61c2 \u5f97\uff0c\u53ef\u4ee5\u505a\u4ed6\u7684\u52a9\u624b\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u542c\u4e86\uff0c\u4f4e\u7740\u6c89\u601d\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u9762\u6cdb\u7ea2\u6f6e\uff0c\u4f4e\u4f4e\u7684\u5411\u4f73\u4ef2\u660e\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6837\uff1f\u4f60\u8bf4 \u8bdd\u5440\uff01\u201d \u3000\u3000\u6842\u4ef2\u660e\u4ef0\u8d77\u4e86\u5934\uff0c\u5b9a\u775b\u671b\u7740\u5192\u6d63\u83b2\uff0c\u5f88\u4e45\u624d\u9053\uff1a\u201c\u6211\uff0c\u6211\u662f\u5728\u60f3\u2026\u2026\u201d\u5192\u6d63\u83b2\u561f\u8d77\u5c0f \u5634\uff0c\u4e4d\u6012\u4f6f\u54b3\uff0c\u201c\u5478\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u5931\u9b42\u843d\u9b44\u7684\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u6842\u4ef2\u660e\u4f4e\u5934\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u6211 \u662f\u5728\u60f3\u4e0e\u59d0\u59d0\u4e07\u91cc\u540c\u884c\uff0c\u4e0d\u77e5\u65b9\u4e0d\u65b9\u4fbf\uff1f\u201d\u51cc\u672a\u98ce\u4e0e\u5085\u9752\u4e3b\u201c\u6251\u55e4\u201d\u4e00\u58f0\uff0c\u7b11\u4e86\u51fa\u6765\uff0c\u5192\u70f7 \u83b2\u7ea2\u6655\u6ee1\u9762\uff0c\u76f4\u7ea2\u5230\u8116\u5b50\u3002 \u3000\u3000\u5085\u9752\u4e3b\u54b3\u4e86\u4e00\u58f0\uff0c\u6545\u610f\u7aef\u6b63\u9762\u5bb9\u8bf4\u9053\uff1a\u201c\u8fd9\u5012\u662f\u771f\u8bdd\uff0c\u6211\u4e5f\u5728\u60f3\u2026\u2026\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5ffd\u7136 \u5728\u5d16\u8fb9\u6a2a\u51fa\u7684\u4e00\u68f5\u866c\u677e\u6811\u4e0a\uff0c\u8f7b\u98d8\u98d8\u5730\u843d\u4e0b\u4e00\u6761\u4eba\u5f71\uff0c\u63a5\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u4e0d\u7528\u60f3\u4e86\uff0c\u7531\u6211 \u6765\u4f5c\u4e3b\u3002\u201d\u8fd9\u4eba\u6b63\u662f\u77f3\u5927\u5a18\u3002\u6842\u4ef2\u660e\u8d77\u8eab\u65f6\uff0c\u5979\u5df2\u9192\u89c9\uff0c\u4ed7\u7740\u5730\u5f62\u719f\u6089\uff0c\u8f7b\u529f\u8d85\u5353\uff0c\u501f\u7269\u969c \u5f62\uff0c\u8fdc\u8fdc\u5730\u8ddf\u7740\u4ed6\u4eec\uff0c\u5085\u9752\u4e3b\u4ed6\u4eec\u805a\u7cbe\u4f1a\u795e\u5730\u8c08\u8bba\u5434\u4e09\u6842\u4e4b\u4e8b\uff0c\u7adf\u7136\u6ca1\u6709\u53d1\u89c9\u3002 \u3000\u3000\u77f3\u5927\u5a18\u9053\uff1a\u201c\u5085\u8001\u5148\u751f\uff0c\u4f60\u548c\u5192\u59d1\u5a18\u60c5\u540c\u7236\u5973\uff0c\u5979\u7684\u7ec8\u8eab\u5927\u4e8b\uff0c\u4f60\u5f53\u505a\u5f97\u4e86\u4e3b\uff0c\u6211\u770b\u5c31 \u7ed9\u4ed6\u4eec\u4fe9\u5b9a\u4e86\u5a5a\u5427\uff0c\u6b63\u4e86\u540d\u4efd\uff0c\u8def\u4e0a\u540c\u884c\u4e5f\u65b9\u4fbf\u5f97\u591a\u3002\u201d\u5085\u9752\u4e3b\u7b11\u9053\uff1a\u201c\u8fd9\u8fd8\u5f97\u95ee\u95ee\u4ed6\u4eec\u7684 \u610f\u601d\uff0c\u5582\uff01\u4f60\u4eec\u8bf4\uff0c\u613f\u4e0d\u613f\u610f\uff1f\u201d\u4e24\u4eba\u90fd\u4f4e\u4e0b\u5934\u6765\uff0c\u4e0d\u6562\u8bf4\u8bdd\u3002\u51cc\u672a\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u522b\u4f5c\u5f04 \u4ed6\u4eec\u4e86\uff0c\u4ed6\u4eec\u90fd\u662f\u5c0f\u5b69\u5b50\u561b\uff0c\u4f60\u8981\u4ed6\u4eec\u9523\u5bf9\u9523\u9f13\u5bf9\u9f13\u7684\u660e\u8bf4\u51fa\u6765\uff0c\u4ed6\u4eec\u53ef\u6ca1\u6709\u4f60\u90a3\u6837\u539a\u8138 \u76ae\uff01\u201d\u8bf4\u7f62\uff0c\u4e00\u624b\u62c9\u7740\u6842\u4ef2\u660e\uff0c\u4e00\u624b\u62c9\u7740\u5192\u6d63\u83b2\uff0c\u5c06\u4ed6\u4eec\u9760\u62e2\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e3b\u5a5a\u7684\u662f\u5085\u4f2f \u4f2f\u52a0\u77f3\u5927\u5a18\uff0c\u5927\u5a92\u5c31\u7531\u6211\u505a\u4e86\u5427\uff01\u201d\u4ed6\u6084\u6084\u5730\u5728\u6842\u4ef2\u660e\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u597d\u4e1c\u897f\uff0c\u5feb\u62ff \u51fa\u6765\u7ed9\u5192\u59d1\u5a18\u5440\uff01\u201d\u6842\u4ef2\u660e\u7ed9\u4ed6\u6446\u5e03\u5f97\u660f\u5934\u660f\u8111\uff0c\u4e0d\u5047\u601d\u7d22\u5730\u53d6\u51fa\u4e86\u4e09\u679a\u91d1\u73af\uff0c\u9012\u8fc7\u53bb\u9053\uff1a \u201c\u4f60\u66ff\u6211\u7ed9\u5979\u5427\u3002\u6211\u53ef\u6ca1\u6709\u4ec0\u4e48\u597d\u4e1c\u897f\uff0c\u8eab\u4e0a\u53ea\u6709\u6bcd\u4eb2\u4f20\u7ed9\u6211\u7684\u6697\u5668\u3002\u201d\u51cc\u672a\u98ce\u5927\u58f0\u8bf4\u9053\uff1a \u201c\u6210\u4e86\uff0c\u8fd9\u4e2a\u5b9a\u5a5a\u793c\u7269\u597d\u5f97\u5f88\uff0c\u6d63\u83b2\u59d1\u5a18\uff0c\u63a5\u8fc7\u4e86\uff01\u201d\u4ed6\u5c06\u4e09\u679a\u91d1\u73af\u5411\u5192\u6d63\u83b2\u629b\u53bb\uff0c\u5192\u6d63\u83b2 \u4e0d\u7531\u81ea\u4e3b\u5730\u63a5\u4e86\u8fc7\u6765\u3002\u5085\u9752\u4e3b\u9053\uff1a\u201c\u4f60\u4e5f\u5f97\u4ea4\u56de\u4e00\u4ef6\u4e1c\u897f\u7ed9\u522b\u4eba\u5440\uff01\u201d\u5192\u6d63\u83b2\u7ea2\u7740\u8138\uff0c\u5728\u6000 \u4e2d\u638f\u51fa\u4e86\u4e00\u5e45\u753b\u6765\uff0c\u4ea4\u7ed9\u5085\u9752\u4e3b\uff0c\u9ed8\u4e0d\u4f5c\u58f0\u3002\u5085\u9752\u4e3b\u6253\u5f00\u4e00\u770b\uff0c\u53ea\u89c1\u753b\u7684\u662f\u5251\u9601\u7edd\u9876\u7684\u98ce \u666f\uff0c\u4e24\u682a\u866c\u677e\u8986\u76d6\u7740\u4e00\u95f4\u8305\u5c4b\u3002\u90a3\u6b63\u662f\u5192\u6d63\u83b2\u4e3a\u7740\u8981\u70b9\u9192\u6842\u4ef2\u660e\uff0c\u7279\u5730\u7ed9\u4ed6\u753b\u7684\u3002\u8fd9\u5e45\u753b\uff0c \u5bf9\u6842\u4ef2\u660e\u6765\u8bf4\uff0c\u53ef\u662f\u6781\u4e0d\u5bfb\u5e38\u3002\u6842\u4ef2\u660e\u4e00\u89c1\uff0c\u4e0d\u4f8d\u5085\u9752\u4e3b\u7ed9\u4ed6\uff0c\u5c31\u4f38\u624b\u62ff\u8fc7\u53bb\u4e86\u3002\u5085\u9752\u4e3b\u7b11 \u9053\uff1a\u201c\u4f60\u4eec\u4ea4\u6362\u7684\u793c\u7269\u53ef\u771f\u6709\u610f\u601d\uff0c\u4ee5\u540e\u6842\u8d24\u4f84\u53ef\u8981\u6559\u5192\u6d63\u83b2\u91d1\u73af\u6253\u7a74\u7684\u529f\u592b\uff0c\u5192\u59d1\u5a18\u4e5f\u8981 \u6559\u4ed6\u6587\u7ae0\u5b57\u753b\u3002\u201d \u3000\u3000\u6842\u4ef2\u660e\u548c\u5192\u6d63\u83b2\u867d\u7136\u7f9e\u6001\u53ef\u63ac\uff0c\u5374\u90fd\u662f\u5fc3\u82b1\u6012\u653e\uff0c\u597d\u50cf\u751f\u547d\u9661\u7684\u5145\u5b9e\u8d77\u6765\uff0c\u5f7c\u6b64\u90fd\u6709\u4e86 \u4f9d\u9760\u4f3c\u7684\uff0c\u53cc\u53cc\u62ac\u8d77\u5934\u6765\uff0c\u5e7d\u8c37\u79cb\u58f0\uff0c\u4e5f\u53d8\u6210\u4e86\u5929\u4e0a\u7684\u4ed9\u4e1c\u3002\u6b63\u662f\uff1a \u3000\u3000\u8f6c\u4e1a\u5b9d\u73af\u6210\u8058\u793c\uff0c\u613f\u5c06\u5f69\u7b14\u753b\u9e33\u9e2f\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5341\u4e09\u7ae0\u3000\u4e00\u5251\u8d25\u4e09\u9b54\u3000\u5b9d\u7389\u660e\u73e0\u85cf\u76f8\u5e9c\u3000\u6e05\u6b4c\u60ca\u8fdc\u5ba2\u3000\u6f84\u6ce2\u78a7\u6d77\u8d5e\u8bcd\u4eba \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u5341\u4e09\u56de \u4e00\u5251\u8d25\u4e09\u9b54\u3000\u5b9d\u7389\u660e\u73e0\u85cf\u76f8\u5e9c \u6e05\u6b4c\u60ca\u8fdc\u5ba2\u3000\u6f84\u6ce2\u78a7\u6d77\u8d5e\u8bcd\u4eba \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u77f3\u5929\u6210\u77e5\u9053\u4e86\u8fd9\u4e8b\uff0c\u975e\u5e38\u9ad8\u5174\uff0c\u4eb2\u81ea\u628a\u4ed6\u4eec\u7684\u5a5a\u4e8b\u4e00\u5ba3\u5e03\uff0c\u7fa4\u8c6a\u7eb7\u7eb7\u9053\u8d3a\u3002\u5085 \u9752\u4e3b\u548c\u77f3\u5927\u5a18\u5e76\u5e26\u9886\u4ed6\u4eec\uff0c\u6500\u767b\u5251\u9601\uff0c\u796d\u626b\u6842\u5929\u6f9c\u7684\u5893\uff0c\u97e9\u8346\u7b49\u4e00\u5e72\u4eba\u4f17\uff0c\u4e5f\u5728\u5893\u524d\u6d41\u6cea\u81f4 \u51e4\u4edf\u4fae\u524d\u975e\uff0c\u613f\u4ee5\u6709\u751f\u4e4b\u5e74\uff0c\u7adf\u8001\u53cb\u672a\u6210\u4e4b\u4e1a\u3002 \u3000\u3000\u626b\u5893\u4e4b\u540e\uff0c\u5085\u9752\u4e3b\u51cc\u672a\u98ce\u5e26\u9886\u7fa4\u8c6a\uff0c\u6295\u5230\u674e\u6765\u4ea8\u519b\u4e2d\u3002\u77f3\u5927\u6210\u592b\u59bb\u548c\u5f92\u5f1f\u4e8e\u4e2d\u3001\u5973\u513f\u7af9 \u541b\u4ee5\u53ca\u5f20\u9752\u539f\u7b49\u4eba\u5219\u7559\u5728\u8c37\u4e2d\uff0c\u5b88\u536b\u85cf\u91d1\uff0c\u7b49\u5019\u642c\u8fd0\u3002\u6842\u4ef2\u660e\u548c\u5192\u6d63\u83b2\u968f\u4ed6\u4eec\u51fa\u5251\u9601\u4e4b\u540e\uff0c \u4fbf\u5373\u5206\u9053\u626c\u9573\uff0c\u8ff3\u8d74\u4eac\u534e\u3002 \u3000\u3000\u5176\u65f6\u5434\u4e09\u6842\u7684\u5927\u519b\u5df2\u81ea\u4e91\u5357\u800c\u51fa\u6e56\u5317\uff0c\u6842\u5192\u4e8c\u4eba\u53ea\u597d\u53d6\u9053\u7518\u8083\uff0c\u7ecf\u9655\u897f\u8f6c\u5165\u6cb3\u5357\uff0c\u518d\u51fa \u6cb3\u5317\u3002\u5192\u6d63\u83b2\u6613\u9497\u800c\u5e76\uff0c\u4e0e\u6842\u4ef2\u660e\u5144\u5f1f\u79f0\u547c\u3002\u5728\u8fe2\u8fe2\u4e07\u91cc\u7684\u65c5\u7a0b\u4e4b\u4e2d\uff0c\u6842\u4ef2\u660e\u7075\u667a\u521d\u590d\uff0c\u6837 \u6837\u90fd\u89c9\u5f97\u65b0\u9c9c\uff0c\u65f6\u65f6\u50bb\u91cc\u50bb\u6c14\u5730\u95ee\u8fd9\u95ee\u90a3\uff0c\u5192\u6d63\u83b2\u4e00\u4e00\u8010\u5fc3\u89e3\u91ca\uff0c\u6d3b\u50cf\u4ed6\u7684\u59d0\u59d0\u4e00\u822c\uff01\u6f2b\u957f \u7684\u65c5\u7a0b\uff0c\u5728\u8f7b\u9576\u6d45\u7b11\u3001\u871c\u610f\u67d4\u60c5\u4e4b\u4e2d\uff0c\u4e00\u6bb5\u4e00\u6bb5\u7684\u8fc7\u53bb\u4e86\u3002\u6842\u4ef2\u660e\u867d\u7136\u4e0d\u89e3\u6c5f\u6e56\u9669\u6076\uff0c\u4f46\u6709 \u7ec6\u5fc3\u8c28\u614e\u7684\u5192\u6d63\u83b2\u5728\u65c1\uff0c\u603b\u7b97\u6ca1\u6709\u95f9\u8fc7\u4e71\u5b50\u3002\u6708\u7f3a\u6708\u76c8\uff0c\u51ac\u53bb\u6625\u6765\uff0c\u4ed6\u4eec\u8d70\u4e86\u56db\u4e2a\u591a\u6708\uff0c\u5728 \u7b2c\u4e8c\u5e74\u521d\u6625\u65f6\u5206\uff0c\u8e0f\u5165\u6cb3\u5317\u3002\u5192\u6d63\u83b2\u8212\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5927\u7ea6\u518d\u8d70\u5341\u591a\u5929\uff0c\u5c31\u53ef\u4ee5\u5230\u4eac\u57ce\u4e86\uff01\u201d \u6842\u4ef2\u660e\u9053\uff1a\u201c\u4e00\u5411\u542c\u8bf4\u71d5\u8d75\u81ea\u53e4\u591a\u6177\u6168\u60b2\u6b4c\u4e4b\u58eb\uff0c\u600e\u7684\u6211\u4eec\u4e00\u8def\u884c\u6765\uff0c\u90fd\u6ca1\u78b0\u8fc7\u4ec0\u4e48\u4eba \u7269\uff1f\u201d \u3000\u3000\u5192\u6d63\u83b2\u5ff5\u4e86\u4e00\u53e5\u201c\u963f\u5f25\u9640\u4f5b\uff01\u201d\u7ea4\u7ea4\u7389\u6307\u62b5\u7740\u4ed6\u7684\u9762\u988a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u5927\u7237\uff0c\u54b1\u4eec\u5e72\u4ec0 \u4e48\u6765\u7684\uff1f\u4f60\u5012\u5e0c\u671b\u78b0\u5230\u4ec0\u4e48\u6c5f\u6e56\u4eba\u7269\u6765\u4e86\uff01\u6211\u53ea\u5df4\u671b\u5b89\u5b89\u9759\u9759\u5230\u8fbe\u5317\u4eac\uff0c\u53ea\u6709\u8fd9\u4e00\u6bb5\u8def\u4e86\uff0c \u53ef\u5343\u4e07\u522b\u60f9\u51fa\u4e71\u5b50\u6765\uff01\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u6211\u53ea\u968f\u4fbf\u90a3\u6765\u8bf4\u4e00\u58f0\uff0c\u5c31\u60f9\u51fa\u4f60\u4e00\u5927\u7bc7\u6559\u8bad \u6765\uff01\u6211\u53c8\u4e0d\u662f\u4e09\u5c81\u5b69\u5b50\uff0c\u4f60\u6015\u4ec0\u4e48\uff1f\u4e24\u4eba\u53e3\u89d2\u751f\u98ce\uff0c\u8bf4\u8bf4\u7b11\u7b11\u7684\u53c8\u8e0f\u4e0a\u65c5\u9014\u3002 \u3000\u3000\u8fd9\u5929\u4ed6\u4eec\u5230\u4e86\u949c\u9e7f\uff0c\u8fd9\u662f\u4e00\u4e2a\u5927\u9547\uff0c\u4ed6\u4eec\u521a\u8fdb\u4e86\u57ce\uff0c\u5c31\u89c1\u516d\u8f86\u5927\u9aa1\u8f66\uff0c\u5728\u8857\u4e0a\u884c\u8d70\uff0c\u628a \u8857\u9053\u90fd\u585e\u6ee1\u4e86\uff0c\u8f66\u7684\u4e24\u65c1\u7ed2\u5e55\u4f4e\u5782\uff0c\u9aa1\u592b\u548c\u8ddf\u968f\u9aa1\u8f66\u7684\u4eba\u90fd\u662f\u7cbe\u58ee\u7684\u6c49\u5b50\u3002\u5192\u6d63\u83b2\u77a7\u4e86\u4e00 \u773c\uff0c\u6084\u6084\u5730\u5bf9\u6842\u4ef2\u660e\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u4e00\u5b9a\u522b\u6709\u6765\u5386\uff0c\u54b1\u4eec\u7ed5\u9053\u800c\u8fc7\uff0c\u522b\u6cbe\u60f9\u4ed6\u4eec\u3002\u201d\u5979\u66fe\u548c\u5085 \u9752\u4e3b\u5230\u8fc7\u949c\u9e7f\uff0c\u719f\u6089\u9053\u8def\uff0c\u5e26\u6842\u4ef2\u660e\u901a\u8fc7\u6a2a\u8857\uff0c\u627e\u4e86\u4e00\u95f4\u6700\u5927\u7684\u5ba2\u5e97\u6295\u5bbf\u3002 \u3000\u3000\u4e0d\u6599\u4ed6\u4eec\u521a\u6b47\u606f\u4e0b\u6765\uff0c\u5c31\u542c\u5f97\u5ba2\u5e97\u5916\u4eba\u58f0\u5608\u6742\uff0c\u9a6c\u94c3\u53ee\u5f53\uff0c\u90a3\u516d\u8f86\u5927\u8f66\uff0c\u7adf\u7136\u4e5f\u5230\u8fd9\u95f4 \u5ba2\u5e97\u6295\u5bbf\uff0c\u6842\u4ef2\u660e\u597d\u5947\u5fc3\u8d77\uff0c\u5fcd\u4e0d\u4f4f\u51fa\u6765\u5f20\u671b\uff0c\u53ea\u89c1\u516d\u8f86\u5927\u8f66\uff0c\u76f4\u63a8\u5230\u9662\u5b50\u91cc\u624d\u6b47\u4e0b\u6765\uff0c\u8f66 \u95e8\u4e00\u5f00\uff0c\u6bcf\u8f86\u5927\u8f66\u8d70\u51fa\u516d\u540d\u5982\u82b1\u4f3c\u7389\u7684\u5c11\u5973\uff0c\u5171\u662f\u4e09\u5341\u516d\u4eba\uff0c\u82b1\u679d\u62db\u5c55\uff0c\u628a\u6842\u4ef2\u660e\u770b\u5f97\u5446\u4e86 \uff03\u558a\u6d63\u83b2\u5728\u4ed6\u80cc\u540e\u8f7b\u8f7b\u4e00\u634f\uff0c\u53eb\u4ed6\u56de\u623f\uff0c\u597d\u51e0\u6761\u5927\u6c49\u7684\u76ee\u5149\u90fd\u5411\u4ed6\u4eec\u5c04\u53bb\u3002\u56de\u5230\u623f\u95f4\uff0c\u5192\u6d63 \u83b2\u4e5f\u9891\u89c9\u5947\u602a\uff0c\u8fd9\u4e09\u5341\u516d\u4e2a\u5c11\u5973\uff0c\u4e2a\u4e2a\u59ff\u8272\u90fd\u4e0d\u5bfb\u5e38\uff0c\u5192\u6d63\u83b2\u5728\u82cf\u5dde\u957f\u5927\uff0c\u82cf\u5dde\u7f8e\u5973\uff0c\u81ea\u53e4 \u6709\u540d\uff0c\u5979\u90fd\u672a\u66fe\u89c1\u8fc7\u8fd9\u4e48\u591a\u4f73\u4e3d\uff01\u6842\u4ef2\u660e\u6000\u7591\u9053\uff1a\u201c\u83ab\u4e0d\u662f\u62a2\u6765\u7684\uff1f\u201d\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u7edd\u5bf9 \u4e0d\u4f1a\uff0c\u62a2\u6765\u7684\u54ea\u4f1a\u5927\u6447\u5927\u6446\u4ece\u95f9\u5e02\u7ecf\u8fc7\uff01\u201d\u6842\u4ef2\u660e\u53c8\u9053\uff1a\u201c\u83ab\u975e\u662f\u5927\u6237\u4eba\u5bb6\u7684\u5973\u513f\uff0c\u8bf7\u4eba\u4fdd \u9001\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u5192\u6d63\u83b2\u53c8\u6447\u6447\u5934\u9053\uff1a\u201c\u867d\u7136\u5927\u6237\u4eba\u5bb6\uff0c\u5341\u623f\u516b\u623f\u540c\u4f4f\u5728\u4e00\u8d77\u7684\uff0c\u6709\u51e0\u5341\u4e2a\u5c11 \u5973\uff0c\u5e76\u975e\u5947\u4e8b\u3002\u4f46\u4e5f\u7edd\u4e0d\u53ef\u80fd\u4e2a\u4e2a\u90fd\u662f\u8fd9\u6837\u5e74\u9752\u8c8c\u7f8e\u3002\u201d\u8bf4\u7740\u201c\u5657\u54e7\u201d\u4e00\u7b11\uff0c\u4f38\u51fa\u98df\u6307\u5728\u6842 \u4ef2\u660e\u8138\u4e0a\u4e00\u522e\uff0c\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u521a\u624d\u770b\u5f97\u7075\u9b42\u513f\u90fd\u98de\u4e0a\u4e5d\u5929\uff01\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u4f60\u522b\u80e1\u8bf4\u3002\u5979 \u4eec\u4e09\u5341\u516d\u4e2a\u4eba\u52a0\u8d77\u6765\u90fd\u6ca1\u4f60\u8fd9\u6837\u7f8e\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u54ce\u5537\uff0c\u5c45\u7136\u61c2\u5f97\u8ba8\u4eba\u6b22\u559c\u4e86\uff1f\u4e0d\u8089 \u9ebb\uff1f\u201d \u3000\u3000\u5c0f\u4e24\u53e3\u5b50\u5431\u5431\u55b3\u55b3\u7684\u731c\u4e86\u4e00\u9635\uff0c\u6842\u4ef2\u660e\u53c8\u9053\uff1a\u201c\u83ab\u975e\u662f\u7687\u5e1d\u6311\u9009\u7684\u79c0\u5973\uff1f\u201d\u5192\u6d63\u83b2\u7b11 \u9053\uff1a\u201c\u4f60\u771f\u662f\u6ca1\u89c1\u8fc7\u4e16\u9762\uff0c\u5047\u5982\u662f\u7687\u5e1d\u6311\u9009\u7684\u79c0\u5973\uff0c\u7a7f\u5dde\u8fc7\u53bf\uff0c\u5927\u5c0f\u5b98\u513f\u90fd\u8981\u6765\u63a5\u5e94\uff0c\u54ea\u4f1a \u4f4f\u8fd9\u4e2a\u5ba2\u5e97\uff1f\u7687\u5e1d\u7684\u5a01\u98ce\u54ea\uff0c\u4f60\u60f3\u90fd\u60f3\u4e0d\u51fa\uff01\u201d\u6842\u4ef2\u660e\u5947\u9053\uff1a\u201c\u96be\u9053\u4f60\u89c1\u8fc7\u7687\u5e1d\u4e0d\u6210\uff0c\u8bf4\u5f97 \u8fd9\u6837\u5634\u54cd\uff1f\u201d\u5192\u6d63\u83b2\u9762\u8272\u4e00\u6c89\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u89c1\u8fc7\uff01\u201d\u6842\u4ef2\u660e\u89c1\u5979\u672c\u6765\u6709\u8bf4\u6709\u7b11\uff0c\u597d\u7aef \u7aef\u7684\u5ffd\u7136\u90c1\u95f7\u8d77\u6765\uff0c\u614c\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u600e\u4e48\u4e86\uff1f\u7ba1\u4ed6\u7687\u5e1d\u4e0d\u7687\u5e1d\uff0c\u54b1\u4eec\u8c08\u54b1\u4eec\u7684\u3002\u201d\u5192\u6d63\u83b2\u53f9 \u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u7684\u8eab\u4e16\u5df2\u7ecf\u591f\u51c4\u51c9\u4e86\uff0c\u6211\u7684\u6bd4\u4f60\u7684\u8fd8\u8981\u51c4\u51c9\u3002\u4f60\u597d\u574f\u90fd\u6709\u7236\u6bcd\uff0c\u6211\u7684\u4eb2\u4eba\u5374 \u53ea\u6709\u4e00\u4e2a\u5085\u4f2f\u4f2f\u3002\u201d\u6842\u4ef2\u660e\u6025\u5fd9\u6307\u7740\u81ea\u5df1\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u6211\u5462\uff01\u201d\u5192\u6d63\u83b2\u7ed9\u4ed6\u9017\u5f97\u5fcd\u4e0d\u4f4f\u53c8 \u7b11\u8d77\u6765\uff0c\u63a8\u4ed6\u4e00\u628a\u9053\uff1a\u201c\u4f60\u522b\u6b6a\u7f20\u4e86\uff0c\u6211\u8bf4\u89c1\u8fc7\u7687\u5e1d\uff0c\u90a3\u662f\u771f\u7684\uff0c\u65e5\u540e\u6211\u518d\u7ec6\u7ec6\u5730\u544a\u8bc9\u4f60\u3002 \u73b0\u5728\u561b\uff0c\u6211\u8981\u4f60\u65e9\u70b9\u7761\u89c9\uff0c\u660e\u65e9\u9e21\u4e00\u53eb\uff0c\u6211\u5c31\u8981\u4f60\u8d77\u6765\u8d76\u8def\u3002\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u5e72\u5417\uff1f\u201d\u5192\u6d63 \u83b2\u9053\uff1a\u201c\u54b1\u4eec\u6709\u5927\u4e8b\u5728\u8eab\uff0c\u5c11\u60f9\u95f2\u4e8b\u3002\u8fd9\u73ed\u4eba\u8def\u9065\u4e0d\u660e\uff0c\u522b\u548c\u4ed6\u4eec\u5728\u4e00\u8d77\u3002\u8001\u5b9e\u8bf4\uff0c\u548c\u4ed6\u4eec \u540c\u4f4f\u8fd9\u4e2a\u5ba2\u5e97\uff0c\u6211\u4e5f\u62c5\u5fc3\u3002\u201d\u6842\u4ef2\u660e\u62cd\u62cd\u8170\u95f4\u7684\u201c\u817e\u86df\u201d\u5b9d\u5251\u9053\uff1a\u201c\u6015\u4ec0\u4e48\uff1f\u201d\u5192\u6d63\u83b2\u4e00\u628a \u5c06\u4ed6\u63a8\u5012\u5730\u4e0a\uff0c\u9053\uff1a\u201c\u8d76\u5feb\u7761\uff0c\u6211\u4e0d\u548c\u4f60\u6597\u53e3\u4e86\u3002\u201d\u5979\u81ea\u5df1\u4e5f\u548c\u8863\u6512\u4e0a\u5e8a\u53bb\u3002\u4e24\u4eba\u540c\u884c\u4e07 \u91cc\uff0c\u51e1\u662f\u4f4f\u5e97\u90fd\u662f\u6842\u4ef2\u660e\u7761\u5728\u5730\u4e0a\uff0c\u5192\u6d63\u83b2\u72ec\u5360\u5927\u5e8a\u3002 \u3000\u3000\u6842\u4ef2\u660e\u679c\u7136\u5f88\u542c\u8bdd\uff0c\u4e56\u4e56\u5730\u7761\u4e86\uff0c\u8fd9\u665a\u4e00\u70b9\u4e8b\u60c5\u90fd\u6ca1\u6709\uff0c\u7b2c\u4e8c\u5929\u4e00\u65e9\u9e21\u9e23\uff0c\u5192\u6d63\u83b2\u5c31\u50ac \u6842\u4ef2\u660e\u8d77\u6765\uff0c\u7ed3\u4e86\u623f\u94b1\uff0c\u7ee7\u7eed\u767b\u7a0b\u3002 \u3000\u3000\u4e24\u4eba\u8d70\u4e86\u4e09\u4e8c\u5341\u91cc\uff0c\u5929\u8272\u5927\u660e\uff0c\u773c\u524d\u5ffd\u7136\u73b0\u51fa\u4e00\u7247\u4eae\u6676\u6676\u7684\u6c34\u6cca\uff0c\u6e2f\u6c4a\u4ea4\u9519\uff0c\u5c31\u5728\u5927\u8def \u7684\u65c1\u8fb9\uff0c\u800c\u8def\u7684\u53e6\u4e00\u8fb9\u53c8\u662f\u9ad8\u5c97\u5bc6\u6797\u3002\u6842\u4ef2\u660e\u9053\u4e86\uff1a\u201c\u8fd9\u5730\u65b9\u5f62\u52bf\u5012\u5f88\u4e0d\u9519\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a \u201c\u554a\uff0c\u6211\u4eec\u5df2\u5230\u4e86\u82cf\u6751\u4e86\uff0c\u8fd9\u5730\u65b9\u662f\u5180\u9c81\u8c6b\u4e09\u7701\u8fb9\u5883\u6709\u540d\u7684\u9669\u8981\u4e4b\u5730\u3002\u6211\u542c\u5085\u4f2f\u4f2f\u8bf4\uff0c\u4ee5\u524d \u6709\u4e00\u80a1\u5f3a\u4eba\u5728\u8fd9\u91cc\u843d\u8349\uff0c\u517c\u505a\u6c34\u9646\u4e24\u8def\u751f\u610f\uff0c\u4e3a\u9996\u4e4b\u4eba\u90fd\u662f\u6c5f\u5317\u5927\u76d7\uff0c\u53ea\u662f\u884c\u4e3a\u4e0d\u6b63\uff0c\u8d2a\u8d22 \u597d\u8272\uff0c\u7eff\u6797\u82f1\u96c4\u9119\u5176\u4e3a\u4eba\uff0c\u540e\u6765\u53c8\u7ed9\u5b98\u519b\u6253\u4e86\u4e00\u9635\uff0c\u6ca1\u4eba\u5e2e\u4ed6\u4eec\uff0c\u542c\u8bf4\u7ad9\u4e0d\u4f4f\u811a\u9003\u4e86\uff0c\u4e0d\u77e5 \u662f\u4e5f\u4e0d\u662f\u3002\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u5c31\u662f\u6709\u5f3a\u76d7\u4e5f\u62a2\u4e0d\u4e86\u54b1\u4eec\uff01\u201d\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u7136\u80cc\u540e\u8f66\u8f9a\u8f9a\uff0c\u9a6c\u8427 \u8427\uff0c\u56de\u5934\u4e00\u770b\uff0c\u90a3\u516d\u8f86\u5927\u5e73\u548c\u4e58\u9a6c\u62a4\u9001\u7684\u4e00\u5e72\u4eba\uff0c\u5df2\u8d76\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5192\u6d63\u83b2\u773c\u5229\uff0c\u53ea\u89c1\u7b2c\u4e00\u8f86\u5927\u8f66\u524d\u9762\u6302\u7740\u4e00\u9762\u9556\u65d7\uff0c\u4e0a\u7ee3\u201c\u6b66\u5a01\u201d\u4e8c\u5b57\uff0c\u8fce\u98ce\u98d8\u8361\u3002\u516d\u8f86 \u5927\u8f66\u8fc7\u540e\uff0c\u6bbf\u540e\u7684\u4e00\u4eba\uff0c\u5e74\u7ea6\u56db\u5341\u5c81\u5149\u666f\uff0c\u62ff\u7740\u4e00\u6746\u5927\u65f1\u70df\u888b\uff0c\u53e3\u55b7\u9752\u70df\uff0c\u659c\u7740\u773c\u775b\uff0c\u770b\u4e86 \u6842\u5192\u4e8c\u4eba\u4e00\u773c\uff0c\u4f3c\u9887\u60ca\u5f02\uff0c\u4f46\u4e5f\u4e0d\u505c\u7559\uff0c\u7b56\u9a6c\u75be\u9a70\u800c\u8fc7\u3002 \u3000\u3000\u5192\u6d63\u83b2\u5f85\u5927\u961f\u8fc7\u4e86\u5c11\u8bb8\uff0c\u7b11\u7740\u5bf9\u6842\u4ef2\u660e\u9053\uff1a\u201c\u4f60\u6210\u5929\u56b7\u7740\u8981\u89c1\u6c5f\u6e56\u4eba\u7269\uff0c\u8fd9\u4fbf\u662f\u4e00\u4e2a\u4eba \u7269\u3002\u6b66\u5a01\u9556\u5c40\u662f\u5357\u4eac\u6700\u51fa\u540d\u7684\u4e00\u95f4\u9556\u5c40\uff0c\u7f25\u5934\u5c31\u53eb\u5b5f\u6b66\u5a01\uff0c\u5e74\u7eaa\u6bd4\u6211\u7684\u5085\u4f2f\u4f2f\u8fd8\u5927\u4e00\u70b9\uff0c\u5584 \u7528\u72ec\u95e8\u5175\u5668\u65f1\u70df\u888b\u6253\u7a74\uff0c\u6211\u5341\u4e00\u4e8c\u5c81\u65f6\uff0c\u548c\u5085\u4f2f\u4f2f\u5230\u5357\u4eac\u66fe\u89c1\u8fc7\u4ed6\u3002\u542c\u8bf4\u4ed6\u7684\u7edd\u827a\u53ea\u4f20\u7ed9\u513f \u5b50\u5b5f\u575a\uff0c\u521a\u624d\u90a3\u4eba\u60f3\u5fc5\u5c31\u662f\u4ed6\u7684\u513f\u5b50\u3002\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u6628\u5929\u4e3a\u4ec0\u4e48\u6ca1\u89c1\u7740\u9556\u65d7\uff0c\u4e5f\u6ca1\u89c1\u8fd9\u625b \u65f1\u70df\u888b\u7684\u6c49\u5b50\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u6628\u665a\u4ed6\u4eec\u8fdb\u57ce\u6b47\u5bbf\uff0c\u7528\u4e0d\u7740\u6302\u51fa\u9556\u65d7\u3002\u4f60\u4e0d\u77e5\u9053\uff0c\u6210\u540d\u7684\u9556 \u5e08\u90fd\u6709\u4e00\u4e9b\u602a\u89c4\u77e9\uff0c\u6bd4\u5982\u5b5f\u6b66\u5a01\uff0c\u4ed6\u603b\u662f\u5728\u9669\u8981\u7684\u9ed1\u9053\u4e0a\uff0c\u5f53\u77e5\u6709\u5f3a\u4eba\u4f0f\u4f3a\u65f6\uff0c\u5c31\u72c2\u5438\u65f1 \u70df\uff0c\u53e3\u55b7\u5947\u5f62\u602a\u72b6\u7684\u70df\u5708\uff0c\u8868\u793a\u662f\u4ed6\u4eb2\u81ea\u538b\u9556\uff0c\u5e73\u65f6\u5012\u4e0d\u5927\u5438\u70df\u7684\u3002\u8fd9\u4eba\u5b8c\u5168\u5b66\u4e86\u4ed6\u7684\u6837 \u513f\u3002\u6211\u4e5f\u662f\u89c1\u4e86\u4ed6\u7684\u65f1\u70df\u888b\u624d\u60f3\u8d77\u4ed6\u7684\u6765\u5934\uff0c\u6628\u665a\u6839\u672c\u5c31\u6ca1\u7559\u610f\u5230\u4ed6\u662f\u8c01\u3002\u201d \u3000\u3000\u6842\u4ef2\u660e\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u770b\u8d70\u773c\u4e86\uff0c\u4f1a\u6253\u7a74\u6709\u4ec0\u4e48\u7a00\u5947\uff1f\u636e\u6211\u770b\uff0c\u508d\u7740\u5927\u8f66\u8d70\u7684\u4e24 \u4e2a\u7626\u5c0f\u6c49\u5b50\uff0c\u529f\u592b\u5c31\u8981\u6bd4\u8fd9\u4eba\u9ad8\u3002\u201d\u5192\u6d63\u83b2\u51dd\u7738\u7ec6\u770b\uff0c\u770b\u4e0d\u51fa\u4ec0\u4e48\u5f02\u6837\u3002\u6842\u4ef2\u660e\u9053\uff1a\u201c\u6211\u662f \u7ec3\u5927\u529b\u9e70\u722a\u529f\u7684\uff0c\u61c2\u5f97\u4e00\u4e9b\u8def\u9053\u3002\u4f60\u770b\u90a3\u4e24\u4eba\u8fd9\u6837\u7626\u5c0f\uff0c\u5750\u7684\u9a6c\u8fd9\u6837\u9ad8\u5927\u3002\u90a3\u9a6c\u5374\u50cf\u4e0d\u80dc\u8d1f \u8377\u4f3c\u7684\uff0c\u521a\u624d\u4ed6\u4eec\u4e0e\u6211\u64e6\u8eab\u800c\u8fc7\uff0c\u6211\u542c\u90a3\u6c89\u91cd\u7684\u9a6c\u8e44\u4e4b\u58f0\uff0c\u5c31\u77e5\u8fd9\u4e24\u4eba\u5916\u5bb6\u529f\u592b\u5df2\u6709\u76f8\u5f53\u706b \u5019\u3002\u201d\u5192\u6d63\u83b2\u5947\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u53ea\u8bf4\u76f8\u5f53\u706b\u5019\u5462\uff1f\u201d \u3000\u3000\u6842\u4ef2\u660e\u9053\uff1a\u201c\u51e1\u662f\u7ec3\u9e70\u722a\u529f\u3001\u91d1\u521a\u624b\u8fd9\u7c7b\u5185\u5916\u517c\u4fee\u7684\u529f\u592b\uff0c\u5230\u4e86\u968f\u65f6\u968f\u5730\u3001\u6216\u7ad9\u6216\u5750\u90fd \u6d51\u8eab\u662f\u529b\uff0c\u4e0d\u514b\u81ea\u5236\u7684\u65f6\u5019\uff0c\u5916\u5bb6\u529f\u592b\u5c31\u5df2\u5230\u5bb6\u4e86\u3002\u53ef\u662f\u5185\u5bb6\u529f\u592b\u8fd8\u6ca1\u5230\u5bb6\u3002\u82e5\u5185\u5bb6\u529f\u592b\u5230 \u4e86\u5bb6\uff0c\u90a3\u80a1\u52b2\u529b\u968f\u5fc3\u6240\u6b32\uff0c\u80fd\u53d1\u80fd\u6536\uff0c\u6839\u672c\u5c31\u770b\u4e0d\u51fa\u6765\u3002\u201d\u8fd9\u4e24\u4eba\u5916\u529f\u4e0d\u9519\uff0c\u5185\u529f\u53ef\u8fd8\u672a\u591f \u706b\u5019\u3002\u201d\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u6211\u8fde\u4ed6\u7684\u5916\u5bb6\u529f\u592b\u90fd\u770b\u4e0d\u51fa\u6765\uff0c\u90a3\u66f4\u5dee\u4e86\u3002\u201d\u6842\u4ef2\u660e\u6b63\u8272\u9053\uff1a\u201c\u4e0d \u7136\uff0c\u4f60\u7684\u529f\u529b\u636e\u6211\u770b\u548c\u90a3\u4e24\u4e2a\u4eba\u5dee\u4e0d\u591a\uff0c\u5374\u8981\u6bd4\u90a3\u4e2a\u5b5f\u575a\u9ad8\uff0c\u4f60\u5b66\u7684\u65e0\u6781\u5251\u6cd5\u662f\u4e0a\u4e58\u7684\u5185\u5bb6 \u5251\u6cd5\uff0c\u600e\u53ef\u5984\u81ea\u83f2\u8584\uff1f\u201d\u5192\u6d63\u83b2\u62ac\u5934\u518d\u671b\uff0c\u5927\u8f66\u5df2\u8fc7\u53bb\u7ea6\u534a\u91cc\u4e4b\u9065\uff0c\u90a3\u5438\u65f1\u70df\u888b\u7684\u6c49\u5b50\uff0c\u8fd8 \u4e0d\u65f6\u56de\u5934\u770b\u3002\u5192\u6d63\u83b2\u4e0d\u89c9\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u7591\u5fc3\u6211\u4eec\u662f\u5f3a\u76d7\u5462\uff01\u53ea\u4e0d\u77e5\u8fd9\u5357\u4eac\u7684\u540d\u9556\u5934\uff0c\u4e3a\u4ec0\u4e48 \u7ed9\u4e09\u5341\u516d\u4e2a\u5c11\u5973\u4fdd\u7f25\uff0c\u8fd9\u4e8b\u53ef\u5947\u602a\u900f\u4e86\u3002\u83ab\u975e\u8fd9\u6279\u5c11\u5973\uff0c\u771f\u662f\u4ec0\u4e48\u5927\u6237\u4eba\u5bb6\u7684\u5973\u513f\uff0c\u8bf7\u4eba\u4fdd \u9001\u7684\uff1f\u53ef\u662f\u770b\u6765\u53c8\u4e0d\u50cf\u5440\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u731b\u7136\u524d\u9762\u516d\u8f86\u5927\u8f66\uff0c\u500f\u5730\u90fd\u505c\u4e0b\u6765\u3002\u524d\u9762\u5c18\u5934\u8d77\u5904\uff0c\u4e24\u9a91\u9a8f\u9a6c\uff0c\u8fce\u9762\u9a70\u6765\uff0c \u63a0\u8fc7\u5927\u8f66\uff0c\u5feb\u8fd1\u6842\u5192\u4e8c\u4eba\u65f6\uff0c\u624d\u731b\u7684\u52d2\u9a6c\u56de\u5934\uff0c\u53c8\u72c2\u5954\u8fc7\u53bb\u3002\u5192\u6d63\u83b2\u62c9\u62c9\u6842\u4ef2\u660e\u7684\u8896\u5b50\u9053\uff1a \u201c\u662f\u90a3\u8bdd\u513f\u6765\u4e86\uff01\u201d\u6842\u4ef2\u660e\u811a\u6b65\u4e0d\u505c\uff0c\u4e00\u76f4\u5411\u524d\u8d70\u53bb\u3002 \u3000\u3000\u9aa4\u7136\u95f4\u8def\u65c1\u9ad8\u5c97\u4e0a\uff0c\u5c04\u51fa\u4e86\u51e0\u679d\u54cd\u7bad\uff0c\u5176\u58f0\u9e23\u545c\uff0c\u751a\u4e3a\u51c4\u5389\uff0c\u54cd\u7bad\u8fc7\u540e\uff0c\u5bc6\u6797\u4e2d\u6d8c\u51fa\u4e00 \u6279\u4eba\u9a6c\uff0c\u7ea6\u83ab\u6709\u4e00\u767e\u591a\u4eba\uff0c\u970e\u5ffd\u5c31\u622a\u65ad\u4e86\u5927\u8def\uff0c\u62e6\u5728\u8f66\u961f\u4e4b\u524d\u3002 \u3000\u3000\u6b66\u5a01\u9556\u5c40\u7684\u9556\u5e08\u5b5f\u575a\u672c\u6765\u662f\u62bc\u961f\u6bbf\u540e\u7684\uff0c\u8fd9\u65f6\u5df2\u50ac\u9a6c\u4e0a\u524d\uff0c\u72c2\u55b7\u70df\u5708\uff0c\u8d77\u521d\u662f\u4e00\u4e2a\u4e2a\u7684 \u5706\u5f62\u70df\u5708\uff0c\u63a5\u7740\u55b7\u51fa\u7684\u51e0\u53e3\u70df\u5176\u76f4\u5982\u77e2\uff0c\u5c04\u5165\u5148\u55b7\u51fa\u7684\u70df\u5708\u4e4b\u4e2d\uff0c\u70df\u5708\u4e5f\u6e10\u6e10\u56db\u6563\uff0c\u6f2b\u6210\u70df \u96fe\u3002\u8fd9\u662f\u5b5f\u8001\u7f25\u5934\u4f20\u4e0b\u7684\u4fe1\u53f7\uff0c\u5706\u70df\u5708\u5957\u4ea4\u60c5\uff0c\u76f4\u70df\u7ebf\u8868\u6b66\u529b\u3002\u610f\u601d\u662f\u8bf4\uff1a\u201c\u597d\u670b\u53cb\u4eec\uff0c\u7ed9 \u6211\u4eec\u5706\u5706\u9762\uff08\u5356\u4eba\u60c5\uff09\u5427\uff0c\u4e0d\u7136\u82e5\u7528\u6b66\u529b\uff0c\u843d\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u53ef\u574f\u4e86\u6c5f\u6e56\u4e49\u6c14\u3002\u201d \u3000\u3000\u5bf9\u65b9\u9635\u4e2d\u7f13\u7f13\u5730\u8d70\u51fa\u4e00\u4e2a\u4e2d\u5e74\u6c49\u5b50\uff0c\u888d\u8896\u98d8\u98d8\uff0c\u610f\u6001\u6f47\u6d12\uff0c\u7709\u76ee\u59e3\u597d\uff0c\u5f88\u50cf\u4e00\u4e2a\u5973\u4eba\uff0c \u4ed6\u5728\u8896\u4e2d\u53d6\u51fa\u4e00\u628a\u6298\u6247\uff0c\u628a\u5b5f\u575a\u55b7\u51fa\u7684\u70df\u96fe\uff0c\u6247\u5f97\u4e00\u5343\u4e8c\u51c0\uff0c\u9634\u58f0\u7ec6\u6c14\u5730\u8bf4\u9053\uff1a\u201c\u6211\u9053\u662f \u8c01\uff0c\u539f\u6765\u662f\u6b66\u5a01\u9556\u5c40\u7684\u5c11\u7f25\u5934\u4eb2\u81ea\u62bc\u8fd9\u652f\u7f25\u3002\u201d\u5b5f\u575a\u4e5f\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u662f\u90dd\u5be8\u4e3b\u8fd8\u5728 \u6b64\u95f4\u3002\u65e2\u662f\u719f\u4eba\uff0c\u8bf7\u6055\u793c\u4eea\u4e0d\u5468\uff0c\u5bb9\u65e5\u540e\u8865\u4e0a\u62dc\u5e16\u5427\uff01\u201d\u8bf4\u7f62\u53c8\u55b7\u51fa\u51e0\u53e3\u70df\u5708\uff0c\u7b49\u5f85\u5bf9\u65b9\u7b54 \u8bdd\u3002 \u3000\u3000\u5728\u4ed6\u4eec\u4e24\u4eba\u6253\u8bdd\u4e4b\u9645\uff0c\u5192\u6d63\u83b2\u548c\u6842\u4ef2\u660e\u8fdc\u8fdc\u5730\u7ad9\u5728\u8def\u8fb9\u3002\u5192\u6d63\u83b2\u9053\uff1a\u201c\u679c\u7136\u90a3\u51e0\u4e2a\u9b54\u5934 \u53c8\u56de\u65e7\u5730\u3002\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u90a3\u4e0d\u7537\u4e0d\u5973\u9634\u9633\u602a\u6c14\u7684\u662f\u8c01\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u6211\u542c\u5085\u4f2f\u4f2f\u8bf4\u8fc7\uff0c \u8fd9\u4eba\u6599\u662f\u4e09\u9b54\u4e4b\u9996\uff0c\u5341\u51e0\u5e74\u524d\u7684\u6c5f\u6e56\u8d25\u7c7b\u4eba\u5996\u90dd\u98de\u51e4\u3002\u201d\u6842\u4ef2\u660e\u5947\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u53eb\u505a\u4eba \u5996\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u56e0\u4ed6\u751f\u5f97\u7709\u76ee\u5a1f\u79c0\uff0c\u5e38\u5e38\u626e\u6210\u5973\u4eba\uff0c\u4e13\u8ff7\u60d1\u5927\u5bb6\u95fa\u79c0\uff0c\u6709\u4eba\u8fd8\u8bf4\u4ed6\u771f\u662f \u4e2a\u9634\u9633\u4eba\uff0c\u6240\u4ee5\u53eb\u4ed6\u505a\u4eba\u5996\u3002\u53ef\u662f\u4ed6\u7684\u6b66\u529f\u4e5f\u771f\u597d\uff0c\u6709\u51e0\u4e2a\u4fa0\u5ba2\u60f3\u9664\u4ed6\uff0c\u90fd\u7ed9\u4ed6\u9003\u6389\u4e86\u3002\u540e \u6765\u5927\u7ea6\u662f\u5e74\u7eaa\u5927\u4e86\uff0c\u626e\u5973\u4eba\u4e0d\u7075\u4e86\uff0c\u8fd9\u624d\u843d\u8349\u4e3a\u5bc7\u7684\u3002\u201d\u6842\u4ef2\u660e\u53c8\u597d\u5947\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u505a\u9634 \u9633\u4eba\uff1f\u201d\u5192\u6d63\u83b2\u7c89\u8138\u901a\u7ea2\uff0c\u5927\u529b\u67d1\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u522b\u95ee\u4e86\uff0c\u8d76\u5feb\u770b\u5427\uff0c\u4f60\u770b\u4ed6\u4eec\u5c31\u8981\u52a8\u624b \u4e86\u3002\u201d\u6842\u4ef2\u660e\u51fa\u5176\u4e0d\u610f\u5730\u7ed9\u5979\u67d1\u4e86\u4e00\u4e0b\uff0c\u201c\u5537\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u5e78\u5f97\u90a3\u4e24\u6279\u4eba\u90fd\u5f88\u7d27\u5f20\uff0c \u8c01\u4e5f\u6ca1\u6709\u6ce8\u610f\u4ed6\u3002 \u3000\u3000\u90dd\u98de\u51e4\u6162\u6761\u65af\u7406\u5730\u53c8\u4e3e\u8d77\u6247\u6765\uff0c\u6247\u4e86\u4e24\u6247\uff0c\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u5c11\u9556\u5934\u548c\u6211\u4eec\u642d\u4ec0\u4e48\u67b6\u5b50\uff0c\u731b \u55b7\u70df\u5708\uff1f\u54b1\u4eec\u5f00\u95e8\u89c1\u5c71\uff0c\u4f60\u8981\u6211\u4eec\u5e2e\u4f60\u5706\u8fd9\u4e2a\u9762\u5b50\uff0c\u90a3\u4e5f\u6210\uff0c\u4f46\u4f60\u4e5f\u5f97\u66ff\u6211\u4eec\u5706\u4e2a\u9762\u5b50\u3002\u201d \u3000\u3000\u5b5f\u575a\u63a5\u4e86\u8fd9\u652f\u7f25\u540e\uff0c\u4e00\u89c1\u8981\u4fdd\u9001\u7684\u7adf\u662f\u4e09\u5341\u516d\u4f4d\u7f8e\u8273\u5982\u82b1\u7684\u5c11\u5973\uff0c\u5fc3\u91cc\u5f53\u7136\u89c9\u5f97\u5341\u5206\u5947 \u602a\uff0c\u4f46\u4ed6\u6043\u7740\u7236\u4eb2\u7684\u5a01\u540d\uff0c\u63d2\u4e86\u9556\u65d7\uff0c\u4e5f\u7adf\u6311\u8d77\u5927\u6881\uff0c\u4ece\u82cf\u5dde\u76f4\u4fdd\u5230\u6b64\u5730\uff0c\u4e00\u8def\u867d\u78b0\u8fc7\u4e09\u56db \u6b21\u9ed1\u9053\u4eba\u7269\uff0c\u4f46\u53ea\u987b\u55b7\u51fa\u51e0\u53e3\u70df\u5708\uff0c\u4e5f\u5c31\u628a\u5bf9\u65b9\u5413\u9000\u4e86\u3002\u4e0d\u6599\u4e00\u8e0f\u5165\u6cb3\u5317\uff0c\u5374\u78b0\u4e0a\u8fd9\u4e09\u4e2a\u786c \u5bf9\u5934\u3002\u6b63\u5728\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u4e00\u542c\u90dd\u98de\u51e4\u7684\u8bdd\u4f3c\u6709\u5546\u91cf\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u90dd\u5be8\u4e3b\u6709\u4ec0\u4e48\u5429\u5490\uff0c\u6211\u5b5f\u575a \u505a\u5f97\u5230\u7684\uff0c\u4e00\u51c6\u529e\u5230\u3002\u201d \u3000\u3000\u90dd\u98de\u98ce\u53c8\u9634\u9633\u602a\u6c14\u5730\u7b11\u4e86\u4e00\u7b11\uff0c\u5c06\u6247\u4e00\u6307\u5927\u8f66\uff0c\u6211\u4eec\u4e0d\u52ab\u4f60\u7684\u9556\uff0c\u53ea\u662f\u8981\u4e00\u4e9b\u65e0\u4f24\u5927\u96c5 \u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u5b5f\u575a\u542c\u8d66\u98de\u98ce\u8bf4\u4e0d\u52ab\u4ed6\u7684\u9556\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u8fde\u5e95\u4e0b\u90a3\u53e5\u8bdd\u90fd\u672a\u542c\u5168\uff0c\u5c31\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u591a\u8c22 \u5be8\u4e3b\u501f\u8def\u3002\u201d\u90dd\u98de\u51e4\u51b7\u51b7\u4e00\u7b11\uff0c\u54ed\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8f66\u4e0a\u7684\u7ea2\u8d27\uff08\u91d1\uff09\u767d\u8d27\uff08\u94f6\uff09\u6211\u5168\u4e0d\u8981\uff0c\u8fd9 \u4e09\u5341\u516d\u4e2a\u5973\u5a03\u5b50\uff0c\u4f60\u5f97\u7ed9\u6211\u7559\u4e0b\uff0c\u5c11\u4e00\u4e2a\u4e5f\u4e0d\u6210\uff01\u201d\u5b5f\u575a\u5f3a\u6291\u6012\u706b\uff0c\u4e00\u6446\u70df\u888b\uff0c\u4ea2\u58f0\u95ee\u9053\uff1a \u201c\u90dd\u5be8\u4e3b\uff0c\u8fd9\u662f\u600e\u4e48\u4e2a\u8bf4\u6cd5\u3002\u201d\u8d66\u98de\u51e4\u9634\u607b\u4fa7\u7684\u8bf4\u9053\uff1a\u201c\u4ece\u6765\u4fdd\u9556\u7684\u90fd\u662f\u4fdd\u7ea2\u767d\u8d22\u8d27\uff0c\u6ca1\u6709 \u4fdd\u4eba\u7684\uff0c\u6211\u4e0d\u8981\u4f60\u7684\u8d27\uff0c\u53ea\u8981\u4f60\u7684\u4eba\uff0c\u8fd9\u600e\u80fd\u7b97\u662f\u52ab\u9556\uff1f\u201d\u5b5f\u575a\u7ed9\u4ed6\u6c14\u5f97\u9aef\u7709\u5012\u7ad6\uff0c\u9a82\u9053\uff1a \u201c\u602a\u4e0d\u5f97\u4eba\u5bb6\u9a82\u4f60\u662f\u6c5f\u6e56\u8d25\u7c7b\uff0c\u6b66\u6797\u4eba\u5996\uff0c\u51b2\u7740\u6211\u6b66\u5a01\u9556\u5c40\u7684\u7f25\u65d7\u3002\u4f60\u8981\u653e\u8086\uff0c\u90a3\u53ef\u4e0d \u6210\uff01\u201d\u90dd\u98de\u51e4\u5c06\u6298\u6247\u6247\u4e86\u4e24\u6247\uff0c\u5927\u7b11\u9053\uff1a\u201c\u5c31\u662f\u4f60\u8001\u5b50\u51fa\u9a6c\uff0c\u4e5f\u5f97\u7ed9\u6211\u7559\u4e0b\u3002\u4f60\u62db\u5b50\uff08\u773c \u775b\uff09\u653e\u4eae\u4e00\u70b9\uff0c\u51ed\u6211\u8fd9\u628a\u94c1\u6247\uff0c\u8981\u4f60\u8fd9\u4e09\u5341\u516d\u4e2a\u5973\u5a03\u5b50\u5e76\u4e0d\u8fc7\u4efd\u3002\u201d\u5b5f\u575a\u77a5\u4e86\u4e00\u773c\uff0c\u89c1\u90a3\u6247 \u5b50\u4e4c\u6f06\u6f06\u7684\u95ea\u5149\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u662f\u94c1\u6247\u5e2e\u7684\uff0c\u90a3\u66f4\u597d\u4e86\uff0c\u6211\u5c31\u51ed\u8fd9\u6746\u59fb\u888b\uff0c \u6597\u6597\u4f60\u90a3\u628a\u94c1\u6247\u3002\u201d \u3000\u3000\u94c1\u6247\u5e2e\u662f\u957f\u6c5f\u4ee5\u5357\u7684\u4e00\u4e2a\u79d8\u5bc6\u5e2e\u4f1a\uff0c\u5e2e\u4e3b\u5c1a\u4e91\u4eab\u6709\u4e00\u8eab\u60ca\u4eba\u7684\u6b66\u529f\uff0c\u53ef\u662f\u624b\u5e95\u6781\u8fa3\uff0c\u9ed1 \u767d\u4e24\u9053\u5168\u4e0d\u5356\u5e10\uff0c\u78b0\u5230\u8d22\u7269\u5c31\u8981\u62e6\u622a\u3002\u90dd\u4e07\u51e4\u7a77\u9014\u843d\u9b44\uff0c\u66fe\u53bb\u6295\u4ed6\uff0c\u4ed6\u672c\u5f85\u4e0d\u6536\uff0c\u4e0d\u77e5\u600e \u7684\uff0c\u5374\u7ed9\u90dd\u98de\u51e4\u8ff7\u60d1\u5f80\u4e86\uff0c\u7ec8\u4e8e\u8ba9\u4ed6\u505a\u4e86\u5e2e\u4e2d\u7684\u4e00\u4e2a\u9999\u4e3b\u3002\u90dd\u98de\u51e4\u4e5f\u5c31\u662f\u9760\u4e86\u94c1\u6247\u5e2e\u7684\u540d \u5934\uff0c\u624d\u80fd\u91cd\u56de\u65e7\u5730\uff0c\u518d\u7acb\u95e8\u6237\u7684\u3002 \u3000\u3000\u5b5f\u575a\u5e74\u867d\u56db\u5341\uff0c\u53ef\u662f\u4e00\u5411\u9760\u7740\u4e43\u7236\u58f0\u5a01\uff0c\u4fdd\u9556\u4ee5\u6765\uff0c\u4ece\u672a\u4e0e\u786c\u624b\u52a8\u8fc7\u771f\u529b\u771f\u67aa\u3002\u800c\u4ed6\u90a3 \u94c1\u70df\u6746\u6253\u7a74\u7684\u529f\u592b\uff0c\u4e5f\u7684\u786e\u7b97\u662f\u4e00\u95e8\u7edd\u6280\uff0c\u56e0\u6b64\u4e45\u800c\u4e45\u4e4b\uff0c\u4ed6\u4e5f\u81ea\u4ee5\u4e3a\u53ef\u4ee5\u79f0\u96c4\u4e00\u65f6\u4e86\uff0c\u4eca \u65e5\u89c1\u7740\u8fd9\u4e09\u4e2a\u9b54\u5934\uff0c\u867d\u7136\u4e0d\u65e0\u987e\u5fcc\uff0c\u4f46\u4e00\u7ed9\u4ed6\u4eec\u6324\u5f97\u4e0b\u4e0d\u4e86\u53f0\uff0c\u4e5f\u81ea\u52a8\u4e86\u771f\u6c14\uff0c\u70df\u6746\u4e00\u6307\uff0c \u4fbf\u5f85\u6251\u4e0a\u3002 \u3000\u3000\u90dd\u98de\u51e4\u8f7b\u8f7b\u4e00\u95ea\uff0c\u5e76\u4e0d\u63a5\u62db\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8981\u548c\u6211\u52a8\u624b\u5440\uff0c\u90a3\u53ef\u8fd8\u5dee\u7740\u70b9\u51e0\uff0c\u4e09\u5f1f\u6765\u628a\u4ed6 \u62ff\u4e0b\uff0c\u80cc\u540e\u4e00\u4e2a\u7c97\u8c6a\u6c49\u5b50\uff0c\u5e94\u58f0\u800c\u51fa\uff0c\u53f3\u624b\u5355\u5200\uff0c\u5de6\u624b\u94c1\u76fe\uff0c\u62e6\u795d\u5408\u575a\u559d\u9053\uff1a\u201c\u6211\u5012\u8981\u770b\u4f60 \u5b5f\u5bb6\u7684\u6253\u7a74\u529f\u592b\uff01\u201d\u8fd9\u6c49\u5b50\u6b63\u662f\u4e09\u9b54\u67f3\u5927\u96c4\u3002 \u3000\u3000\u5b5f\u575a\u5fc3\u5934\u706b\u8d77\uff0c\u66f4\u4e0d\u6253\u8bdd\uff0c\u94c1\u70df\u888b\u5f53\u80f8\u6253\u53bb\uff0c\u67f3\u5927\u96c4\u4e3e\u76fe\u4e00\u8fb9\uff0c\u70df\u9505\u5f53\u7684\u4e00\u58f0\u6253\u5728\u76fe \u4e0a\uff0c\u672a\u70e7\u5b8c\u7684\u70df\u4e1d\uff0c\u7ed9\u78b0\u5f97\u76f4\u98de\u51fa\u6765\u70b9\u70b9\u706b\u661f\uff0c\u5012\u6e85\u56de\u53bb\u3002\u67f3\u5927\u96c4\u5355\u5200\u5728\u76fe\u4e0b\u500f\u5730\u653b\u51fa\uff0c\u65a9 \u5b5f\u575a\u624b\u8155\u3002\u5b5f\u575a\u6b66\u529f\u4e5f\u975e\u6cdb\u6cdb\uff0c\u624b\u8155\u4e00\u987f\uff0c\u94c1\u70df\u6746\u6a2a\u91cc\u4e00\u8361\uff0c\u628a\u5355\u5200\u8361\u4e86\u5f00\u53bb\uff0c\u5927\u559d\u4e00\u58f0\uff0c \u659c\u8eab\u6ed1\u6b65\uff0c\u70df\u9505\u5df2\u81ea\u5411\u67f3\u5927\u96c4\u80cc\u540e\u201c\u9b42\u95e8\u7a74\u201d\u6253\u53bb\u3002\u67f3\u5927\u96c4\u53cd\u624b\u4e00\u8fce\uff0c\u70df\u9505\u78b0\u5728\u76fe\u4e0a\uff0c\u4ed6\u987a \u7740\u8fd9\u62e7\u8eab\u4e4b\u52bf\uff0c\u5200\u5149\u4e00\u8f6c\u3002\u53cd\u53d6\u4e2d\u76d8\u3002\u76c2\u575a\u8fde\u8df3\u4e24\u8df3\uff0c\u624d\u907f\u5f00\u8fd9\u62db\u3002 \u3000\u3000\u6842\u4ef2\u660e\u548c\u5192\u6d63\u83b2\u4f0f\u5728\u8def\u65c1\uff0c\u770b\u8fd9\u4e24\u4eba\u53ae\u62fc\uff0c\u53ea\u89c1\u5b5f\u575a\u5982\u6012\u72ee\u731b\u640f\uff0c\u94c1\u70df\u888b\u70b9\u6253\u6572\u5288\uff0c\u53ef \u603b\u6253\u4e0d\u7740\u654c\u4eba\u7684\u7a74\u9053\uff0c\u67f3\u5927\u96c4\u4ee5\u94c1\u76fe\u63a9\u62a4\u5355\u5200\uff0c\u5e26\u653b\u5e26\u5b88\uff0c\u6253\u5f97\u5341\u5206\u6fc0\u70c8\uff0c\u518d\u6253\u4e86\u4e00\u4f1a\uff0c\u5b5f \u575a\u6e10\u6e10\u843d\u5728\u4e0b\u98ce\u3002\u672c\u6765\u8bba\u529f\u592b\u6280\u4e1a\uff0c\u4ed6\u548c\u67f3\u5927\u96c4\u539f\u4e0d\u76f8\u4e0a\u4e0b\u3002\u53ea\u662f\u67f3\u5927\u96c4\u662f\u4e2a\u5267\u76d7\uff0c\u89c1\u8fc7\u8bb8 \u591a\u9635\u4ed7\uff0c\u5b5f\u575a\u548c\u4ed6\u4e00\u6bd4\uff0c\u53ef\u5c31\u5dee\u5f97\u591a\u4e86\u3002\u6253\u5230\u5206\u9645\uff0c\u67f3\u5927\u96c4\u5de6\u624b\u76fe\u724c\u865a\u5e4c\u4e00\u62db\uff0c\u8eab\u5f62\u5411\u4e0b\u4e00 \u6251\uff0c\u5355\u5200\u7ed5\u5904\uff0c\u76f4\u5411\u4ed6\u4e0b\u4e09\u8def\u65ab\u53bb\u3002\u5b5f\u575a\u970d\u5730\u9053\u6b65\uff0c\u94c1\u70df\u6746\u201c\u5012\u6253\u91d1\u949f\u201d\uff0c\u6307\u5411\u654c\u4eba\u80cc\u810a \u201c\u5929\u67a2\u7a74\u201d\uff0c\u67f3\u5927\u96c4\u5927\u543c\u4e00\u58f0\uff0c\u8eab\u5f62\u66b4\u8d77\uff0c\u94c1\u76fe\u201c\u6a2a\u6258\u91d1\u7cb1\u201d\uff0c\u7528\u529b\u4e00\u78d5\uff0c\u77f3\u624b\u5355\u5200\uff0c\u987a\u7740 \u70df\u6746\uff0c\u5411\u4e0a\u731b\u524a\uff0c\u5b5f\u575a\u82e5\u4e0d\u64a4\u624b\uff0c\u624b\u6307\u975e\u7ed9\u524a\u65ad\u4e0d\u53ef\u3002\u6842\u4ef2\u660e\u4f0f\u5728\u8def\u65c1\uff0c\u89c1\u5230\u5b5f\u575a\u5371\u6025\uff0c\u5077 \u5077\u5730\u5bf9\u5192\u6d63\u83b2\u8bf4\uff1a\u201c\u4e14\u5f85\u6211\u52a9\u4ed6\u4e00\u4e0b\uff0c\u5192\u6d63\u83b2\u672a\u53ca\u62e6\u963b\uff0c\u6842\u4ef2\u660e\u5df2\u500f\u7136\u51fa\u624b\uff0c\u4e00\u679a\u91d1\u73af\uff0c\u8ff3 \u81ea\u98de\u53bb\u3002\u8fd9\u679a\u91d1\u73af\uff0c\u6253\u5f97\u6b63\u662f\u65f6\u5019\uff0c\u67f3\u5927\u96c4\u770b\u770b\u5f97\u624b\uff0c\u5ffd\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u5355\u5200\u5df2\u7ed9\u91d1\u73af \u8361\u5f00\u3002\u6536\u5200\u4e00\u770b\uff0c\u53ea\u89c1\u5200\u950b\u4e5f\u88ab\u78b0\u635f\uff0c\u7f3a\u4e86\u4e00\u4e2a\u5c0f\u53e3\u3002\u5b5f\u575a\u83ab\u540d\u6240\u4ee5\uff0c\u62d6\u7740\u70df\u6746\uff0c\u8e09\u8e09\u8dc4\u8dc4 \u7684\u9053\u4e86\u51e0\u6b65\u3002 \u3000\u3000\u6842\u4ef2\u660e\u6697\u5668\u6253\u5f97\u5341\u5206\u795e\u5999\uff0c\u4e24\u8fb9\u7684\u4eba\u53c8\u5168\u90fd\u6ce8\u610f\u5b5f\u575a\u548c\u677e\u5927\u96c4\u7684\u53ae\u6597\uff0c\u7adf\u7136\u6ca1\u4eba\u77e5\u9053\u6697 \u5668\u4ece\u4f55\u800c\u6765\u3002\u67f3\u5927\u96c4\u6a2a\u5200\u4e3e\u76fe\uff0c\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u54ea\u4e2a\u4e0d\u8981\u8138\u5077\u88ad\u5927\u7237\u7684\u7ad9\u51fa\u6765\uff0c\u54b1\u4eec\u660e\u5200\u660e\u67aa \u51b3\u4e2a\u80dc\u8d1f\u3002\u201d \u3000\u3000\u5b5f\u575a\u5e78\u5f97\u8fd9\u4e00\u679a\u91d1\u73af\uff0c\u4fdd\u4e86\u6b66\u5a01\u9556\u5c40\u7684\u58f0\u5a01\uff0c\u60c5\u77e5\u81ea\u5df1\u4e0d\u662f\u4eba\u5bb6\u5bf9\u624b\uff1b\u62d6\u7740\u70df\u6746\u75be\u9000\u3002 \u90dd\u98de\u51e4\u64ae\u5507\u6253\u4e86\u4e2a\u80e1\u54e8\uff0c\u53ea\u89c1\u4e00\u9a91\u5065\u9a6c\uff0c\u500f\u5730\u51b2\u53bb\uff0c\u9a6c\u4e0a\u4eba\u5f80\u4e0b\u4e00\u8df3\uff0c\u62e6\u7740\u5b5f\u575a\uff0c\u7b11\u563b\u563b\u5730 \u9053\uff1a\u201c\u5b5f\u5c11\u9556\u5934\uff0c\u4f60\u522b\u8d70\uff01\u201d\u8fd9\u4eba\u662f\u6c5f\u5317\u4e09\u9b54\u4e2d\u7684\u7b2c\u4e8c\u9b54\u6c99\u65e0\u5b9a\uff0c\u4e5f\u662f\u521a\u624d\u7b56\u9a6c\u63a2\u9556\u7684\u4eba\u3002 \u3000\u3000\u624d\u89e3\u56f0\u5384\uff0c\u53c8\u9047\u5f3a\u654c\uff1b\u5b5f\u575a\u6b63\u5728\u5fc3\u614c\uff0c\u731b\u7136\u95f4\u5927\u8f66\u961f\u4e2d\uff0c\u4e5f\u98de\u51b2\u51fa\u4e24\u9a91\u5065\u9a6c\uff0c\u5b5f\u575a\u4e00 \u770b\uff0c\u5374\u662f\u90a3\u4e24\u4e2a\u9ed1\u7626\u6c49\u5b50\uff0c\u8fd9\u4e24\u4e2a\u6c49\u5b50\u4e0b\u9a6c\u53eb\u9053\uff1a\u201c\u5b5f\u7237\u8bf7\u9053\uff01\u201d\u5176\u4e2d\u4e00\u4eba\u8d64\u624b\u7a7a\u62f3\u4fbf\u53bb\u5f3a \u62a2\u6c99\u65e0\u5b9a\u624b\u4e2d\u7684\u5927\u67aa\u3002\u53e6\u4e00\u4eba\u4e5f\u4ee5\u8d64\u624b\u7a7a\u62f3\uff0c\u8fce\u4e0a\u4e86\u9053\u6765\u7684\u67f3\u5927\u96c4\u3002 \u3000\u3000\u5b5f\u575a\u60ca\u5f02\u5f97\u51e0\u4e4e\u558a\u51fa\u58f0\u6765\uff0c\u8fd9\u4e24\u6c49\u5b50\u5c31\u662f\u5f53\u65e5\u8bf7\u4ed6\u6765\u4fdd\u9556\u7684\u4eba\uff0c\u5f53\u65f6\u4ed6\u4eec\u81ea\u79f0\u662f\u4e00\u4e2a\u5bcc \u6237\u7684\u7ba1\u5bb6\uff0c\u540d\u53eb\u9646\u660e\u9646\u4eae\uff0c\u662f\u4e24\u5144\u5f1f\uff0c\u501a\u9760\u5357\u4eac\u53e6\u4e00\u4e2a\u6b66\u6797\u5d29\u8f88\u7684\u9762\u5b50\uff0c\u6765\u592e\u6c42\u6b66\u5a01\u9556\u5c40\u4fdd \u9556\u7684\u3002\u5b5f\u575a\u770b\u4ed6\u4eec\u9aa8\u7626\u5982\u67f4\uff0c\u5f53\u65f6\u8fd8\u6697\u7b11\u600e\u7684\u8fd9\u4e2a\u5bcc\u6237\u5982\u7528\u201c\u70df\u7cbe\u201d\u6765\u4f5c\u7ba1\u5bb6\uff0c\u6839\u672c\u5c31\u6599\u4e0d \u5230\u4ed6\u4eec\u8eab\u6000\u7edd\u6280\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u51fa\u624b\u7adf\u662f\u5317\u6d3e\u7684\u9e70\u722a\u529f\u5939\u4ee5\u64d2\u62ff\u624b\uff0c\u5341\u6570\u62db\u4e00\u8fc7\uff0c\u770b\u5f97\u5b5f\u575a\u76ee\u5b9a\u53e3\u5446\u3002\u6c99\u65e0\u5b9a \u7684\u5927\u62a2\uff0c\u957f\u4e03\u5c3a\u6709\u4f59\uff0c\u4e00\u7c07\u8840\u6321\u56db\u9762\u88c1\u5f20\uff0c\u8db3\u6709\u6597\u7bf7\u5927\u5c0f\uff0c\u6311\u624e\u6251\u6253\uff0c\u864e\u864e\u751f\u98ce\uff0c\u8bba\u529f\u529b\u6bd4 \u67f3\u5927\u96c4\u8fd8\u5f3a\u8bb8\u591a\uff0c\u4f46\u9646\u660e\u53ea\u51ed\u4e00\u53cc\u8089\u638c\uff0c\u5df2\u662f\u8db3\u4ee5\u62b5\u654c\u3002\u6c99\u65e0\u5b9a\u4e00\u62a2\u7d27\u4f3c\u4e00\u67aa\uff0c\u5140\u662f\u523a\u4ed6\u4e0d \u7740\u3002\u90a3\u8fb9\u7684\u9646\u4eae\u72ec\u6218\u67f3\u5927\u96c4\uff0c\u7adf\u7136\u6b3a\u8eab\u76f4\u8fdb\uff0c\u786c\u7528\u7a7a\u624b\u4eba\u767d\u5203\u7684\u529f\u592b\uff0c\u53bb\u62a2\u67f3\u5927\u96c4\u7684\u4e32\u5200\uff0c \u4e0d\u8fc7\u7247\u523b\u5c31\u5360\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u5b5f\u575a\u5728\u4e00\u65c1\u770b\u5f97\u5012\u5438\u51c9\u6c14\uff0c\u5fc3\u4e2d\u53f9\u9053\uff1a\u201c\u4f11\u4e86\uff0c\u4f11\u4e86\uff01\u8fd9\u4e24\u4eba\u8eab\u6000\u7edd\u6280\uff0c\u6211\u5374\u4e00\u70b9\u4e5f\u770b \u4e0d\u51fa\u6765\uff0c\u8fd8\u5938\u5927\u53e3\uff0c\u4f5c\u4fdd\u7f25\uff0c\u4f20\u51fa\u5c82\u4e0d\u7b11\u6298\u522b\u4eba\u7259\u9f7f\u3002\u4eca\u756a\u7eb5\u4fdd\u5f97\u7740\u8fd9\u652f\u7f25\uff0c\u4e5f\u6298\u4e86\u540d \u5934\uff01\u201d\u770b\u4e24\u4eba\u8d8a\u6253\u8d8a\u70c8\uff0c\u9e70\u722a\u529f\u64d2\u62ff\u624b\uff0c\u62db\u6570\u7cbe\u5947\uff0c\u81ea\u5df1\u89c1\u6240\u672a\u89c1\uff0c\u8d8a\u770b\u8d8a\u602a\uff0c\u4e0d\u7981\u76b1\u7709\u60f3 \u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u529f\u592b\u8fdc\u5728\u6211\u4e0a\uff0c\u600e\u7684\u98a0\u5012\u8bf7\u6211\u6765\u505a\u4fdd\u9556\uff0c\u82e5\u4e0d\u662f\u5b58\u5fc3\u620f\u5f04\uff0c\u4e00\u5b9a\u5185\u6709\u9690\u60c5\u3002\u201d \u3000\u3000\u8fd9\u65f6\u523b\uff0c\u4e24\u5bf9\u53ae\u6740\uff0c\u529f\u592b\u4e5f\u5df2\u5206\u51fa\u5f3a\u5f31\u3002\u6c99\u65e0\u5b9a\u62db\u719f\u529b\u6c89\uff0c\u8fd8\u81ea\u62b5\u6321\u5f97\u4f4f\uff0c\u67f3\u5927\u96c4\u7684\u5355 \u5200\u5728\u9163\u6218\u58f0\u4e2d\uff0c\u5374\u7ade\u7ed9\u9646\u4eae\u4e00\u628a\u62a1\u53bb\uff0c\u53ea\u5269\u4e0b\u4e00\u9762\u94c1\u76fe\uff0c\u4e14\u6218\u4e14\u9000\u3002\u8d66\u98de\u51e4\u76f8\u8c8c\u50cf\u4e2a\u5973\u4eba\uff0c \u529f\u592b\u5374\u6781\u5229\u843d\uff0c\u8f7b\u8f7b\u4e00\u7eb5\uff0c\u62e6\u5728\u9646\u4eae\u9762\u524d\uff0c\u94c1\u6247\u4e00\u6307\uff0c\u76f4\u70b9\u9646\u4eae\u9762\u95e8\uff0c\u5de6\u8fb9\u4e00\u7acb\uff0c\u8f7b\u8f7b\u5411\u4e0a \u4e00\u6258\uff0c\u9646\u4eae\u53cc\u80a9\u4e00\u6643\uff0c\u6025\u5fd9\u5012\u7eb5\u51fa\u53bb\uff0c\u90dd\u98de\u51e4\u8fd9\u62db\u540d\u53eb\u201c\u98a0\u5012\u9634\u9633\u201d\uff0c\u4e0e\u64d2\u62ff\u624b\u6709\u5f02\u66f2\u540c\u5de5 \u4e4b\u5999\uff0c\u80f3\u818a\u82d4\u7ed9\u4ed6\u4e00\u6258\u4e00\u62d7\uff0c\u8fd9\u6761\u624b\u81c2\u5c31\u7b97\u5356\u7ed9\u4ed6\u4e86\u3002 \u3000\u3000\u90dd\u98de\u51e4\u6551\u51fa\u4e86\u67f3\u5927\u96c4\uff0c\u5c16\u58f0\u602a\u6c14\u5730\u53eb\u9053\uff1a\u201c\u4e8c\u5f1f\u8bf7\u9000\u4e0b\u3002\u201d\u6c99\u65e0\u5b9a\u529b\u523a\u4e09\u67aa\uff0c\u628a\u9646\u660e\u8feb \u8fc7\u4e00\u4fa7\uff0c\u64a4\u67aa\u75be\u9000\uff0c\u6c14\u5598\u5401\u5401\uff0c\u7ad9\u5728\u90dd\u98de\u51e4\u8eab\u8fb9\u3002 \u3000\u3000\u9646\u660e\u9646\u4eae\u5e76\u80a9\u7ad9\u7acb\uff0c\u90dd\u98de\u51e4\u5c55\u5f00\u94c1\u6247\uff0c\u6247\u4e86\u4e24\u6247\uff0c\u602a\u58f0\u7b11\u9053\uff1a\u201c\u9646\u5bb6\u5144\u5f1f\u771f\u597d\u529f\u592b\uff0c\u6211 \u4e0d\u81ea\u91cf\u529b\uff0c\u8981\u8bf7\u4e24\u4f4d\u4e00\u540c\u6307\u6559\uff01\u201d\u9646\u660e\u9646\u4eae\u90fd\u662f\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u4eba\u5996\u201d\u771f\u4e2a\u201c\u795e\u901a\u5e7f \u5927\u201d\uff0c\u6211\u4e24\u5144\u5f1f\u65e9\u5df2\u9000\u51fa\u6c5f\u6e56\uff0c\u4ed6\u7adf\u4e00\u53e3\u5c31\u80fd\u559d\u7834\u6765\u5386\u3002 \u3000\u3000\u90dd\u98de\u51e4\u94c1\u6247\u4e00\u6307\uff0c\u53c8\u6d31\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u4e24\u4f4d\u9646\u5e08\u7236\u4e0d\u80af\u8d50\u6559\u4e48\uff1f\u201d\u9646\u660e\u3001\u9646\u4eae\u5927\u6012\uff0c\u5de6\u53f3 \u4e00\u5206\uff0c\u53cc\u53cc\u6251\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u4eca\u65e5\u5b9a\u8981\u64d2\u4f60\u8fd9\u4e2a\u4eba\u5996\uff01\u201d\u90dd\u98de\u51e4\u563b\u563b\u4e00\u7b11\uff0c\u6ed1\u4f3c\u6e38\u9c7c\uff0c\u5728\u4e24\u4eba \u638c\u5e95\u94bb\u4e86\u51fa\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u6709\u8fd9\u80fd\u8010\uff1f\u201d\u53cd\u624b\u4e00\u6247\uff0c\u5c31\u548c\u4e24\u4eba\u6597\u4e0a\u4e86\u3002\u90dd\u98de\u98ce\u6247\u5b50\u4f7f\u5f00\uff0c \u4e5f\u662f\u4e00\u6d3e\u70b9\u7a74\u5bb6\u6570\uff0c\u4f46\u5374\u6bd4\u5b5f\u575a\u7684\u6253\u7a74\u5389\u5bb3\u8bb8\u591a\uff0c\u4ed6\u8eab\u6cd5\u53c8\u6781\u5176\u8f7b\u7075\uff0c\u4e00\u628a\u6247\u5b50\u6307\u4e1c\u6253\u897f\uff0c \u6307\u5357\u6253\u5317\uff0c\u5168\u662f\u6307\u5411\u4e24\u4eba\u7684\u81f4\u547d\u7a74\u9053\uff0c\u4ed6\u5de6\u624b\u4e5f\u4e0d\u95f2\u7740\uff0c\u53f3\u624b\u6247\u5b50\u6253\u51fa\uff0c\u5de6\u624b\u8ddf\u7740\u5c31\u662f\u4e00 \u638c\uff0c\u7528\u7684\u7adf\u662f\u5200\u5251\u62db\u6570\uff0c\u8fd9\u79cd\u602a\u62db\uff0c\u9646\u5bb6\u5144\u5f1f\u8fd8\u662f\u521d\u6b21\u9047\u4e0a\u3002\u5e78\u5f97\u4ed6\u4eec\u7684\u9e70\u722a\u529f\u64d2\u62ff\u624b\u4e5f\u6709 \u4e86\u76f8\u5f53\u706b\u5019\uff0c\u800c\u4e14\u76f8\u4e92\u914d\u5408\uff0c\u5a01\u529b\u66f4\u589e\uff0c\u90dd\u98de\u51e4\u8fd9\u624d\u4e0d\u6562\u8fc7\u4efd\u8feb\u8fd1\u3002 \u3000\u3000\u4e09\u4eba\u8d70\u9a6c\u706f\u4f3c\u7684\u53ae\u6740\u4e86\u4e00\u767e\u6765\u62db\uff0c\u8d66\u98de\u51e4\u602a\u62db\u5c42\u51fa\u4e0d\u7a77\uff0c\u9646\u5bb6\u5144\u5f1f\u62fc\u547d\u652f\u6301\uff0c\u5140\u662f\u5b88\u591a \u653b\u5c11\u3002\u6842\u4ef2\u660e\u770b\u4e86\u8bb8\u4e45\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u6c49\u5b50\u8981\u7cdf\u3002\u9e70\u722a\u529f\u64d2\u62ff\u624b\u539f\u662f\u5229\u4e8e\u653b\u800c\u4e0d\u5229\u4e8e \u5b88\uff0c\u4ed6\u4eec\u7ed9\u654c\u4eba\u8feb\u5f97\u8981\u64a4\u638c\u9632\u5b88\uff0c\u53ea\u6015\u6ca1\u591a\u4e45\u5c31\u8981\u843d\u8d25\u3002\u201d \u3000\u3000\u679c\u7136\u518d\u6253\u4e00\u9635\uff0c\u4e24\u5144\u5f1f\u6bc5\u7136\u72c2\u53eb\uff0c\u5f80\u540e\u4fbf\u8dd1\u3002\u4f46\u90dd\u98de\u51e4\u62db\u6cd5\u6bd4\u4ed6\u4eec\u66f4\u5feb\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u53c8 \u7eca\u7740\u4ed6\u4eec\u3002\u53e3\u4e2d\u53eb\u9053\uff0c\u201c\u4e8c\u5f1f\u4e09\u5f1f\uff0c\u4f60\u4eec\u53bb\u62a2\u5927\u8f66\uff01\u201d \u3000\u3000\u6c99\u65e0\u5b9a\u3001\u67f3\u5927\u96c4\u4e00\u58f0\u5450\u558a\uff0c\u7387\u9886\u767e\u4f59\u5e2e\u532a\uff0c\u72c2\u98ce\u4e00\u822c\u5377\u5c06\u8fc7\u6765\u3002\u90dd\u98de\u51e4\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u53ea \u8981\u4eba\uff0c\u4e0d\u8981\u8d27\uff0c\u7b97\u7559\u7ed9\u76c2\u8001\u5934\u5b50\u4e00\u70b9\u9762\u5b50\u3002\u201d\u5b5f\u575a\u6c14\u5f97\u7126\u9ec4\u4e86\u8138\uff0c\u62a1\u94c1\u70df\u888b\u62fc\u547d\u6572\u51fb\uff0c\u6df7\u6218 \u4e2d\u6c99\u65e0\u5b9a\u4e00\u67aa\u5c06\u4ed6\u7684\u70df\u6746\u6311\u4e0a\u534a\u7a7a\uff0c\u65c1\u8fb9\u7684\u5e2e\u532a\u629b\u51fa\u7eca\u9a6c\u7d22\uff0c\u5c06\u4ed6\u7eca\u5012\uff0c\u67f3\u5927\u96c4\u53cc\u624b\u6263\u4f4f\u4ed6 \u7684\u8109\u95e8\uff0c\u5c06\u4ed6\u7f1a\u5728\u8def\u65c1\u7684\u6811\u4e0a\u3002\u5176\u4ed6\u62a4\u8f66\u7684\u58ee\u6c49\uff0c\u867d\u7136\u4e5f\u6709\u6b66\u529f\uff0c\u600e\u7981\u5f97\u5e2e\u532a\u4eba\u591a\u52bf\u4f17\uff0c\u8f6c \u77ac\u4e4b\u95f4\u5c31\u7ed9\u8feb\u5230\u4e00\u9685\uff0c\u773c\u7741\u7741\u5730\u770b\u7740\u6c99\u65e0\u5b9a\u3001\u67f3\u5927\u96c4\u9886\u7740\u5e2e\u532a\uff0c\u6251\u5954\u5927\u8f66\u3002 \u3000\u3000\u6842\u4ef2\u660e\u548c\u5192\u6d63\u83b2\u4f0f\u5728\u8def\u65c1\uff0c\u79bb\u5927\u8f66\u7ea6\u6709\u5341\u6765\u4e08\u8fdc\u3002\u5192\u6d63\u83b2\u672c\u6765\u5c61\u6b21\u7981\u6b62\u6842\u4ef2\u660e\u51fa\u624b\uff0c\u8fd9 \u65f6\u89c1\u5e2e\u532a\u62c9\u5f00\u5927\u8f66\u7ed2\u5cf0\uff0c\u91cc\u9762\u5c11\u5973\u5c16\u58f0\u54ed\u53eb\uff0c\u4e0d\u7981\u67f3\u7709\u5012\u7ad6\u3002\u6842\u4ef2\u660e\u9053\uff1a\u201c\u8fd9\u5e2e\u8d3c\u4eba\u6b3a\u4fae\u5a18 \u513f\uff0c\u54b1\u4eec\u63cd\u4ed6\uff01\u201d\u5192\u6d63\u83b2\u4e00\u8dc3\u800c\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5bf9\u4ed8\u90a3\u4e24\u4e2a\u5934\u9886\uff0c\u6211\u53bb\u8d76\u5f00\u532a\u5f92\u3002\u201d \u3000\u3000\u6842\u4ef2\u660e\u89e3\u4e0b\u817e\u86df\u5b9d\u5251\uff0c\u5982\u5de8\u9e1f\u817e\u7a7a\uff0c\u51e0\u4e2a\u8d77\u843d\uff0c\u5df2\u662f\u843d\u5728\u8f66\u961f\u4e4b\u524d\u3002\u5341\u591a\u4e2a\u5e2e\u532a\u821e\u52a8\u5200 \u67aa\uff0c\u4e0a\u524d\u62e6\u963b\uff0c\u6842\u4ef2\u660e\u5706\u7741\u53cc\u773c\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u817e\u86df\u5251\u5411\u524d\u4e00\u6296\uff0c\u94f6\u8679\u75be\u5410\uff0c\u628a\u5341\u591a\u628a\u5200\u67aa\u5168 \u90fd\u524a\u65ad\uff0c\u6c99\u65e0\u5b9a\u89c1\u72b6\u5927\u63a0\uff0c\u659c\u523a\u91cc\u4e00\u67aa\u523a\u51fa\uff0c\u6842\u4ef2\u660e\u4e00\u4e2a\u65cb\u8eab\uff0c\u53c8\u662f\u4e00\u58f0\u5927\u559d\uff0c\u5b9d\u5251\u8d77\u5904\uff0c \u53ea\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u6c99\u65e0\u5b9a\u56db\u5341\u4e8c\u65a4\u91cd\u7684\u5927\u67aa\uff0c\u4e5f\u7ed9\u6298\u65ad\u4e86\uff0c\u9707\u5f97\u4ed6\u864e\u53e3\u6d41\u8840\uff0c\u62d6\u7740\u534a\u622a \u67aa\u6025\u5fd9\u5954\u547d\u3002 \u3000\u3000\u5728\u6842\u4ef2\u660e\u5927\u663e\u795e\u5a01\u4e4b\u9645\uff0c\u5192\u6d63\u83b2\u4e5f\u5df2\u8d76\u5230\u73b0\u573a\uff0c\u90a3\u4e9b\u5e2e\u532a\u6b63\u5728\u6495\u7ed2\u5e44\u3001\u7838\u8f66\u95e8\uff0c\u5192\u6d63\u83b2 \u626c\u624b\u5c31\u662f\u4e00\u5927\u628a\u593a\u547d\u795e\u7802\uff0c\u5b9b\u5982\u6d12\u4e0b\u6ee1\u5929\u82b1\u96e8\u3002\u90a3\u4e9b\u5e2e\u532a\u4e5f\u90fd\u662f\u8001\u4e8e\u6c5f\u6e56\u7684\u4e86\uff0c\u4e00\u4e2d\u6697\u5668\uff0c \u53ea\u89c9\u53c8\u9ebb\u53c8\u75d2\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u6bd2\u7802\u4e8e\uff01\u201d\u5192\u6d63\u83b2\u4e00\u58f0\u51b7\u7b11\uff0c\u7389\u624b\u8fde\u626c\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u662f\u6bd2 \u7802\u5b50\u4f60\u4eec\u4e5f\u4e0d\u77e5\u9053\u5389\u5bb3\uff01\u201d\u5e2e\u532a\u53d1\u4e00\u58f0\u558a\uff0c\u56db\u4e0b\u5954\u9003\u3002\u5192\u6d63\u83b2\u53cc\u773c\u6ef4\u6e9c\u6e9c\u7684\u4e00\u8f6c\uff0c\u53ea\u89c1\u7b2c\u4e09 \u8f86\u8f66\u4e0a\uff0c\u8fd8\u6709\u51e0\u4e2a\u5e2e\u532a\uff0c\u7ad9\u5728\u8f66\u9876\uff0c\u4ed6\u4eec\u5df2\u62a2\u51fa\u51e0\u540d\u5c11\u5973\uff0c\u7528\u4f5c\u63a9\u62a4\u3002\u5192\u9662\u83b2\u5927\u6012\uff0c\u653e\u4e0b\u795e \u7802\uff0c\u62d4\u51fa\u4f69\u5251\uff0c\u4e00\u8dc3\u800c\u4e0a\uff0c\u5251\u8d70\u504f\u950b\uff0c\u6377\u4f3c\u7075\u732b\uff0c\u5a07\u53f1\u4e24\u58f0\uff0c\u4e24\u540d\u5e2e\u532a\u4e2d\u5251\u6251\u5012\uff0c\u5192\u6d63\u83b2\u4e00 \u817f\u5c06\u4ed6\u4eec\u4ece\u8f66\u9876\u626b\u4e0b\uff0c\u633a\u5251\u4fbf\u5954\u7b2c\u4e09\u540d\u5e2e\u532a\uff0c\u90a3\u540d\u5e2e\u532a\u5c06\u631f\u6301\u7740\u7684\u5c11\u5973\u5411\u524d\u4e00\u63a8\uff0c\u5192\u6d63\u83b2\u624b \u8155\u500f\u7ffb\uff0c\u5251\u950b\u5de6\u503e\uff0c\u5411\u7a7a\u6863\u5954\u53bb\uff0c\u5251\u6cd5\u8fc5\u75be\u5f02\u5e38\uff0c\u672c\u610f\u8fd9\u540d\u5e2e\u532a\u4e5f\u6613\u4e86\u7ed3\uff0c\u4e0d\u6599\u4e00\u5251\u523a\u53bb\uff0c \u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u78b0\u4e86\u56de\u6765\uff0c\u539f\u6765\u662f\u523a\u5728\u4e0a\u9762\u76fe\u724c\u4e0a\u3002 \u3000\u3000\u8fd9\u540d\u5e2e\u532a\u662f\u67f3\u5927\u96c4\uff0c\u4ed6\u9886\u5934\u62a2\u4e0a\u4e2d\u95f4\u7684\u5927\u8f66\uff0c\u7838\u5f00\u8f66\u95e8\uff0c\u53ea\u89c1\u516d\u540d\u5c11\u5973\u7f8e\u8273\u5982\u82b1\uff0c\u773c\u90fd \u5446\u4e86\u3002\u4ed6\u770b\u4e86\u4e00\u9635\uff0c\u5c06\u5176\u4e2d\u6700\u7f8e\u7684\u5c11\u5973\u631f\u51fa\uff0c\u5192\u6d63\u83b2\u5df2\u62a2\u4e86\u4e0a\u6765\u3002\u4ed6\u820d\u4e0d\u5f97\u653e\u5f00\uff0c\u7adf\u7136\u5728\u8f66 \u4e0a\u8d1f\u9685\u987d\u6297\u3002 \u3000\u3000\u5192\u6d63\u83b2\u8fde\u523a\u6570\u5251\uff0c\u90fd\u88ab\u67f3\u5927\u96c4\u5de7\u5999\u6321\u5f00\u3002\u4ed6\u631f\u5c11\u5973\u4e3a\u8d28\uff0c\u4ee5\u94c1\u76fe\u63a9\u62a4\uff0c\u5192\u6d63\u83b2\u6b66\u529f\u867d\u6bd4 \u4ed6\u5f3a\uff0c\u6295\u9f20\u5fcc\u5668\uff0c\u6025\u5207\u95f4\u5374\u662f\u5948\u4f55\u4e0d\u5f97\u3002\u67f3\u5927\u96c4\u89c1\u5192\u6d63\u83b2\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u5e94\u4ed8\u4e5f\u611f\u4e3a\u96be\u3002\u84e6 \u7136\u95f4\u4ed6\u6293\u8d77\u5c11\u5973\u62c4\u5916\u4e00\u62a2\uff0c\u4ee5\u8fdb\u4e3a\u9000\uff0c\u5f15\u5f00\u5192\u6d63\u83b2\u7684\u5251\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u5f80\u540e\u4e00\u8dc3\u4fbf\u4f8d\u7ffb\u4e0b\u5927 \u8f66\uff0c\u90a3\u6599\u7b11\u58f0\u672a\u7edd\uff0c\u540e\u5fc3\u5ffd\u7136\u4e00\u9635\u5267\u75db\uff0c\u4e0d\u7531\u5f97\u53cc\u624b\u677e\u5f00\uff0c\u4eba\u4e5f\u50cf\u65ad\u7ebf\u98ce\u7b5d\u4e00\u6837\u8dcc\u4e86\u4e0b\u53bb\uff0c \u539f\u6765\u6842\u4ef2\u660e\u5728\u8ffd\u8d76\u6c99\u65e0\u5b9a\u65f6\uff0c\u767e\u5fd9\u4e2d\u56de\u5934\u4e00\u77a5\uff0c\u89c1\u5192\u6d63\u83b2\u5c1a\u5728\u5927\u8f66\u4e0a\u4e0e\u4eba\u62fc\u6597\uff0c\u968f\u624b\u53d1\u51fa\u4e00 \u679a\u91d1\u73af\uff0c\u6253\u4e2d\u4e86\u67f3\u5927\u96c4\u540e\u5fc3\u7a74\u9053\u3002 \u3000\u3000\u5192\u6d63\u83b2\u6b63\u81ea\u6c14\u7ea2\u4e86\u9650\uff0c\u4e5f\u5f85\u633a\u5251\u8dc3\u4e0b\u5927\u8f66\uff0c\u90a3\u5c11\u5973\u521a\u597d\u843d\u4e0b\uff0c\u5979\u53ea\u597d\u63d2\u5251\u5f52\u9798\uff0c\u4ee5\u624b\u63a5 \u4e0b\uff0c\u8f7b\u8f7b\u629a\u62cd\u5c11\u5973\uff0c\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\u53d7\u60ca\u4e86\uff01\u201d\u90a3\u5c11\u5973\u60ca\u9b42\u7a0d\u5b9a\uff0c\u53d1\u89c9\u81ea\u5df1\u5728\u7537\u5b50\u6000\u4e2d\uff0c\u6025\u5fd9 \u53cc\u624b\u4e00\u63a8\uff0c\u90a3\u6599\u624b\u6240\u89e6\u5904\uff0c\u5374\u662f\u8f6f\u7ef5\u7ef5\u7684\u4e00\u56e2\u4e1c\u897f\u3002 \u3000\u3000\u5192\u6d63\u83b2\u626c\u7802\u62d2\u654c\uff0c\u62d4\u5251\u6551\u4eba\uff0c\u7d27\u5f20\u4e2d\u7adf\u81ea\u5fd8\u8bb0\u4e86\u81ea\u5df1\u6613\u94a6\u800c\u5e76\uff0c\u662f\u4e2a\u201c\u7537\u513f\u201d\uff0c\u7ed9\u5c11\u5973 \u4e00\u89e6\uff0c\u624d\u731b\u7684\u9192\u8d77\uff0c\u6025\u5fd9\u653e\u5f00\u4e86\u624b\uff0c\u5728\u5c11\u5973\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u522b\u58f0\u5f20\uff0c\u6211\u548c\u4f60\u4e00 \u6837\uff0c\u662f\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u88e3\u887d\u81f4\u8c22\u9053\uff1a\u201c\u591a\u8c22\u59d0\u59d0\u6551\u547d\u4e4b\u6069\u3002\u201d\u5192\u6d63\u83b2\u7ea2\u7740\u8138\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u53eb\u6211\u59d0\u59d0\uff0c\u6211 \u5c31\u9886\u4f60\u7684\u60c5\u4e86\u3002\u201d\u90a3\u5c11\u5973\u4e5f\u7b97\u673a\u7075\uff0c\u6025\u5fd9\u6362\u8fc7\u53e3\u9053\uff1a\u201c\u591a\u8c22\u516c\u5b50\uff01\u201d\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u4f60\u53eb\u4ec0 \u4e48\u540d\u5b57\uff1f\u600e\u6837\u6765\u7684\uff1f\u8fd9\u4e9b\u59d1\u5a18\u662f\u4f60\u7684\u59d0\u59b9\u5417\uff1f\u201d\u90a3\u5c11\u5973\u773c\u5708\u4e00\u7ea2\uff0c\u7b54\u9053\u201c\u6211\u53eb\u7d2b\u83ca\uff0c\u662f\u82cf\u5dde \u57ce\u7684\u6b4c\u5973\uff0c\u7ed9\u4eba\u4e70\u6765\u7684\uff0c\u8fd9\u4e9b\u59d1\u5a18\uff0c\u6211\u65e9\u5148\u90fd\u4e0d\u8ba4\u8bc6\uff0c\u542c\u8bf4\u4e5f\u662f\u4e70\u6765\u7684\u3002\u201d\u5192\u6d63\u83b2\u8fd8\u5f85\u518d \u95ee\uff0c\u5ffd\u89c1\u4e0b\u9762\u4e71\u6210\u4e00\u7247\uff0c\u5e2e\u532a\u56db\u4e0b\u5954\u9003\uff0c\u6842\u4ef2\u660e\u5411\u5979\u5927\u58f0\u547c\u5524\u3002 \u3000\u3000\u90a3\u8fb9\uff0c\u6842\u4ef2\u660e\u5728\u53d1\u51fa\u91d1\u73af\uff0c\u6253\u5012\u67f3\u5927\u96c4\u4e4b\u540e\uff0c\u518d\u5411\u524d\u8ffd\uff0c\u5e2e\u532a\u754f\u60e7\u5b9d\u5251\uff0c\u7eb7\u7eb7\u8eb2\u907f\uff0c\u90dd \u98de\u51e4\u653e\u5f00\u9646\u5bb6\u5144\u5f1f\uff0c\u8d76\u4e86\u8fc7\u6765\uff0c\u4e5f\u5140\u81ea\u9547\u538b\u4e0d\u4f4f\u3002 \u3000\u3000\u90dd\u98de\u51e4\u672a\u89c1\u654c\u4eba\uff0c\u9661\u89c1\u5251\u5149\uff0c\u5fc3\u91cc\u4e00\u60ca\uff0c\u5df2\u89c9\u51b7\u6c14\u68ee\u68ee\uff0c\u5bd2\u5149\u5288\u9762\u3002\u4ed6\u4ed7\u7740\u8eab\u6cd5\u8f7b\u7075\uff0c \u8fde\u907f\u4e09\u5251\uff0c\u81ea\u77e5\u4e0d\u662f\u5bf9\u624b\uff0c\u5f85\u7b2c\u56db\u5251\u65a9\u6765\u65f6\uff0c\u6025\u5fd9\u5411\u540e\u4e00\u8dc3\uff0c\u94c1\u6247\u5b50\u5506\u5730\u51fa\u624b\uff0c\u8fce\u7740\u5251\u950b\u626b \u53bb\u3002 \u3000\u3000\u6842\u4ef2\u660e\u6b63\u6740\u5f97\u6027\u8d77\uff0c\u5ffd\u542c\u5f97\u5251\u5c16\u55e1\u55e1\u4f5c\u664c\uff0c\u706b\u661f\u4e71\u98de\uff0c\u5341\u51e0\u679d\u77ed\u7bad\u5411\u81ea\u5df1\u98de\u6765\uff0c\u4ed6\u53cc\u8db3 \u4e00\u70b9\uff0c\u5e73\u5730\u62d4\u8d77\u4e09\u4e08\u6765\u9ad8\uff0c\u5b9d\u5251\u5728\u534a\u7a7a\u5212\u4e86\u4e00\u9053\u5f27\u5f62\uff0c\u628a\u90a3\u4e9b\u77ed\u7bad\u626b\u65ad\uff0c\u8fd9\u624d\u8f7b\u98d8\u98d8\u843d\u5728\u5730 \u4e0a\u3002\u53ea\u8fd9\u6837\u88ab\u6321\u4e86\u4e00\u6321\uff0c\u90dd\u98de\u51e4\u5df2\u5230\u6cb3\u8fb9\uff0c\u6251\u901a\u4e00\u58f0\uff0c\u501f\u6c34\u800c\u9003\u3002\u539f\u6765\u8fd9\u624b\u662f\u90dd\u98de\u51e4\u6551\u547d\u7684 \u7edd\u62db\uff0c\u90a3\u628a\u94c1\u6247\u5b50\u85cf\u6709\u673a\u5173\uff0c\u7ed9\u5b9d\u5251\u622a\u65ad\u540e\uff0c\u5341\u51e0\u6761\u94c1\u6247\u9aa8\uff0c\u90fd\u5316\u6210\u5229\u7bad\uff0c\u5411\u654c\u4eba\u53d1\u5c04\u3002\u4ed6 \u4ee5\u5f80\u66fe\u6709\u51e0\u5439\u88ab\u4fa0\u4e49\u9053\u8ffd\u6740\uff0c\u5c31\u662f\u4ed7\u7740\u8fd9\u624b\u7edd\u6280\uff0c\u5f97\u4ee5\u6b7b\u91cc\u9003\u751f\u7684\u3002\u5e78\u597d\u6842\u4ef2\u660e\u6b66\u529f\u6df1\u6e5b\uff0c \u8981\u4e0d\u7136\u8fd8\u771f\u907f\u4e0d\u5f00\u8fd9\u7a81\u5982\u5176\u6765\u7684\u6697\u5668\u3002 \u3000\u3000\u6c99\u65e0\u5b9a\u6700\u5148\u9003\u8dd1\uff0c\u5374\u53ca\u4e0d\u4e0a\u90dd\u98de\u51e4\u8fc5\u6377\uff0c\u521a\u521a\u5954\u81f3\u6cb3\u8fb9\uff0c\u6842\u4ef2\u660e\u626c\u624b\u4e00\u5708\u91d1\u73af\uff0c\u5c06\u4ed6\u540e \u8111\u6253\u88c2\uff0c\u767b\u65f6\u6bd9\u547d\uff0c\u5e2e\u532a\u547c\u5578\uff0c\u6ca1\u547d\u5954\u9003\uff0c\u6842\u4ef2\u660e\u987e\u4e0d\u5f97\u8ffd\u8d76\uff0c\u5148\u81ea\u56de\u6765\u5bfb\u89c5\u5192\u6d63\u83b2\u3002 \u3000\u3000\u5192\u6dc0\u83b2\u542c\u5f97\u547c\u5524\uff0c\u8df3\u4e0b\u5927\u8f66\uff0c\u987a\u624b\u4e00\u5251\uff0c\u6311\u5f00\u5b5f\u575a\u7684\u7f1a\u7ef3\uff0c\u76c2\u575a\u6de4\u7ea2\u4e86\u8138\uff0c\u5728\u9053\u65c1\u62fe\u8d77 \u90a3\u6839\u94c1\u70df\u888b\uff0c\u4f4e\u58f0\u9053\u8c22\uff0c\u6572\u71c3\u706b\u77f3\uff0c\u72c2\u5438\u65f1\u70df\uff0c\u63a9\u9970\u7a98\u6001\u3002 \u3000\u3000\u9646\u5bb6\u5144\u5f1f\u5468\u56f4\u68c0\u89c6\u4e00\u756a\uff0c\u53ea\u6709\u4e24\u8f86\u5927\u8f66\uff0c\u88ab\u7838\u70c2\u8f66\u95e8\uff0c\u6495\u7834\u7ed2\u5e54\uff0c\u5176\u4ed6\u5168\u65e0\u635f\u5931\u3002\u6025\u5fd9 \u62f1\u624b\u5411\u6842\u3001\u5192\u4e8c\u4eba\u79f0\u8c22\uff0c\u8bf7\u95ee\u59d3\u540d\uff0c\u4ed6\u4eec\u5fc3\u4e2d\u6781\u5176\u9a87\u5f02\uff0c\u5c24\u5176\u5bf9\u4e8e\u6842\u4ef2\u660e\u7684\u6b66\u529f\uff0c\u66f4\u662f\u4f69\u670d \u5f97\u4e94\u4f53\u6295\u5730\u3002\u770b\u6842\u4ef2\u660e\u5e74\u7eaa\u4e0d\u8fc7\u4e8c\u5341\u6765\u5c81\uff0c\u4f46\u5251\u6cd5\u548c\u6697\u5668\u7cbe\u5999\uff0c\u7b80\u76f4\u662f\u95fb\u6240\u672a\u95fb\uff0c\u89c1\u6240\u672a \u89c1\u3002 \u3000\u3000\u6842\u5192\u4e8c\u4eba\u672a\u53ca\u7b54\u8bdd\uff0c\u5b5f\u575a\u5ffd\u5728\u80cc\u540e\u51b7\u51b0\u51b0\u4ed6\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u9646\u5927\u7237\uff0c\u8fd9\u8d9f\u9556\u6211\u4eec\u9000\u4e86\u3002\u6b64 \u53bb\u5317\u4eac\u5df2\u662f\u5766\u9014\uff0c\u7528\u4e0d\u7740\u6211\u6765\u4fdd\uff0c\u4e5f\u4e0d\u9700\u8981\u6211\u6765\u4fdd\u3002\u201d\u9646\u660e\u5c06\u4ed6\u4e00\u628a\u62c9\u4f4f\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u5b5f \u9556\u5934\uff0c\u8fd9\u662f\u600e\u4e48\u8bf4\u7684\uff1f\u5168\u4ed7\u8d35\u9556\u5c40\u5a01\u540d\uff0c\u6211\u4eec\u624d\u80fd\u4ece\u82cf\u5dde\u4e00\u76f4\u5e73\u5b89\u81f3\u6b64\u3002\u5728\u8fd9\u4e2a\u5730\u65b9\uff0c\u867d\u7136 \u906d\u4e86\u4e00\u70b9\u632b\u6298\uff0c\u80dc\u8d25\u4e5f\u662f\u5175\u5bb6\u5e38\u4e8b\u561b\u3002\u54b3\uff0c\u83ab\u975e\u4f60\u602a\u6211\u4eec\u5144\u5f1f\u4e24\u4eba\uff0c\u6211\u4eec\u66ff\u4f60\u8d54\u7f6a\u3002\u201d\u8bf4\u7f62 \u5144\u5f1f\u4e24\u4eba\u53cc\u53cc\u4f5c\u63d6\u3002\u5b5f\u575a\u5c34\u5c2c\u5f97\u5f88\uff0c\u53ef\u53c8\u4e0d\u80fd\u518d\u53d1\u813e\u6c14\uff0c\u6842\u4ef2\u660e\u4e5f\u4e0a\u524d\u6765\u529d\uff0c\u5b5f\u575a\u53f9\u53e3\u6c14 \u9053\uff1a\u201c\u4e24\u4f4d\u9646\u5927\u7237\u6b66\u529f\u771f\u9ad8\uff0c\u8fd9\u4e24\u4f4d\u8fbe\u5b98\u6b66\u529f\u66f4\u9ad8\uff0c\u6b66\u5a01\u9556\u5c40\u5f97\u4fdd\u58f0\u540d\uff0c\u5168\u9760\u4f60\u4eec\uff0c\u56de\u53bb\u6211 \u5c31\u7980\u544a\u5bb6\u7236\uff0c\u628a\u9556\u5c40\u6b47\u4e86\u3002\u7136\u540e\u518d\u916c\u8c22\u5404\u4f4d\u3002\u201d\u4ed6\u8fd9\u8bf4\u7684\u53ef\u662f\u771f\u8bdd\uff0c\u4ed6\u773c\u89c1\u4eca\u65e5\u8bf8\u4eba\uff0c\u6b66\u529f \u4e00\u4e2a\u6bd4\u4e00\u4e2a\u9ad8\uff0c\u4e0d\u7981\u5fc3\u7070\u610f\u51b7\uff0c\u518d\u4e0d\u60f3\u5403\u8fd9\u53e3\u6c5f\u6e56\u996d\u4e86\u3002 \u3000\u3000\u4e24\u9646\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u4e8b\u63ed\u8fc7\uff0c\u6842\u5192\u4e8c\u4eba\uff0c\u968f\u4fbf\u634f\u4e86\u4e2a\u5047\u540d\uff0c\u5bd2\u6684\u51e0\u53e5\uff0c\u4e5f\u5f85\u544a\u8f9e\u8d70\u5c0f\u8def\u3002 \u9646\u5bb6\u5144\u5f1f\u62d6\u7740\u4e0d\u653e\uff0c\u529b\u529d\u4ed6\u4eec\u4e00\u9053\uff0c\u540c\u8def\u8fdb\u4eac\uff0c\u6842\u4ef2\u660e\u77a7\u4e86\u5192\u6d63\u83b2\u4e00\u773c\uff0c\u5192\u6d63\u83b2\u5ffd\u6168\u7136\u8bf4 \u9053\uff1a\u201c\u65e2\u7136\u4e24\u4f4d\u8fd9\u6837\u70ed\u5fc3\uff0c\u54b1\u4eec\u5c31\u53e8\u5149\u6258\u836b\u5427\u3002\u201d\u4e24\u9646\u5927\u559c\uff0c\u7acb\u523b\u8ba9\u51fa\u4e24\u5339\u9a6c\uff0c\u4fee\u597d\u5927\u8f66\uff0c \u5c31\u8bf7\u6842\u5192\u4e8c\u4eba\u4e00\u540c\u4e0a\u8def\u3002 \u3000\u3000\u4e00\u8def\u4e0a\u4e24\u9646\u62ff\u8bdd\u5957\u95ee\u6842\u5192\u4e8c\u4eba\uff0c\u5192\u6d63\u83b2\u673a\u7075\u5f97\u5f88\uff0c\u542b\u7cca\u5e94\u8fc7\u3002\u5979\u62ff\u8bdd\u5957\u95ee\u4e24\u9646\uff0c\u4e24\u9646\u4e5f \u542b\u7cca\u5e94\u8fc7\uff0c\u95ee\u5f97\u7d27\u65f6\uff0c\u53ea\u662f\u7b54\u9053\uff1a\u201c\u5230\u4e86\u4eac\u57ce\uff0c\u6211\u4e24\u5144\u5f1f\u81ea\u5f53\u8bf7\u5c0a\u9a7e\u5230\u6211\u4e3b\u4eba\u5bb6\u4e2d\uff0c\u8d54\u7f6a\u9053 \u8c22\u3002\u201d\u5192\u6d63\u83b2\u77e5\u9053\u201c\u4ea4\u6d45\u8a00\u6df1\u201d\uff0c\u4e43\u662f\u6c5f\u6e56\u5927\u5fcc\uff0c\u4e5f\u5c31\u4e0d\u518d\u8ffd\u95ee\u4e0b\u53bb\u3002\u81f3\u4e8e\u5b5f\u575a\uff0c\u5219\u4e00\u8def\u9ed8 \u4e0d\u4f5c\u58f0\uff0c\u5174\u8da3\u7d2f\u7136\uff0c\u867d\u7136\u6ee1\u8179\u7591\u56e2\uff0c\u5374\u4e0d\u613f\u5f00\u53e3\u8bf4\u8bdd\u3002 \u3000\u3000\u8d70\u4e86\u5341\u591a\u5929\uff0c\u5230\u4e86\u5317\u4eac\uff0c\u6842\u4ef2\u660e\u89c1\u57ce\u5899\u9ad8\u5cfb\uff0c\u897f\u5c71\u5dcd\u5ce8\uff0c\u2018\u8425\u6bbf\u8fde\u4e91\uff0c\u5c4b\u5b87\u76f8\u6bd4\uff0c\u7aef\u7684 \u662f\u96c4\u4f1f\u58ee\u4e3d\uff0c\u2019\u6c14\u8c61\u4e07\u5343\u3002\u4ed6\u4e45\u5904\u6df1\u5c71\uff0c\u51e0\u66fe\u89c1\u8fc7\u5982\u6b64\u666f\u8c61\u3002\u6b63\u81ea\u5fc3\u80f8\u8212\u7545\uff0c\u773c\u82b1\u64a9\u4e71\u4e4b \u9645\u3002\u5ffd\u542c\u5f97\u5b5f\u575a\u51b7\u51b7\u95ee\u9053\uff1a\u201c\u9646\u5927\u7237\uff0c\u9556\u5df2\u62bc\u5230\u4eac\u57ce\u4e86\uff0c\u8bf7\u95ee\u5728\u54ea\u91cc\u4ea4\u5378\uff1f\u201d\u9646\u660e\u626c\u97ad\u4e00 \u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u7eb3\u5170\u76f8\u5e9c\uff01\u201d \u3000\u3000\u5b5f\u575a\u5403\u4e86\u4e00\u60ca\uff0c\u53cd\u95ee\u9053\uff1a\u201c\u7eb3\u5170\u76f8\u5e9c\uff1f\u201d\u9646\u660e\u53c8\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u6b63\u662f\u7eb3\u5170\u76f8\u5e9c\u3002\u201d\u5b5f\u575a\u6c89 \u7740\u773c\u9053\uff1a\u201c\u90a3\u4e48\u4e24\u4f4d\u662f\u76f8\u5e9c\u7684\u6559\u5e08\u7237\u4e86\u3002\u201d\u9646\u660e\u9646\u4eae\u540c\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\uff01\u201d\u5b5f\u575a\u5fc3\u4e2d\u6124\u6012\uff0c \u53e3\u91cc\u53ef\u4e0d\u6562\u8bf4\u51fa\u6765\u3002\u9646\u660e\u4f55\u7b49\u8001\u7ec3\uff0c\u65e9\u5df2\u770b\u51fa\uff0c\u6025\u5fd9\u966a\u8bdd\u9053\uff1a\u201c\u4e0d\u662f\u6211\u5144\u5f1f\u4fe9\u6545\u610f\u620f\u800d\u8001 \u54e5\u3002\u8fd9\u662f\u6211\u4eec\u76f8\u5e9c\u5e08\u7237\u7684\u4e3b\u610f\uff0c\u6211\u4eec\u53ea\u662f\u4f9d\u4ee4\u800c\u884c\u3002\u201d\u5192\u6d63\u83b2\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u8fd9\u4e09\u5341\u516d\u4f4d\u5c11 \u5973\uff0c\u4e5f\u662f\u76f8\u7237\u4e70\u7684\u4e86\uff1f\u201d\u9646\u660e\u9053\uff1a\u201c\u6b63\u662f\u201d\u76f8\u5e9c\u7684\u5e08\u7237\u53eb\u6211\u4eec\u51fa\u9762\uff0c\u592e\u6c42\u5357\u4eac\u7684\u7ae5\u9556\u5934\uff0c\u8f6c \u8bf7\u8d35\u9556\u5c40\u4fdd\u62a4\uff0c\u5c31\u662f\u6015\u8def\u4e0a\u51fa\u9ebb\u70e6\uff0c\u6240\u4ee5\u501f\u4f60\u4eec\u7684\u9556\u65d7\u9547\u538b\u4e00\u4e9b\u4e0d\u4e09\u4e0d\u56db\u7684\u5c0f\u5f3a\u76d7\u3002\u201d\u5b5f\u575a \u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u53ea\u662f\u628a\u6211\u4eec\u770b\u505a\u7eb8\u7cca\u7684\u59dc\u592a\u516c\uff0c\u9876\u770b\u4e0d\u9876\u7528\uff0c\u53ea\u53ef\u7528\u6765\u5413 \u5c0f\u9b3c\u7684\uff0c\u771f\u6b63\u78b0\u5230\u786c\u628a\u5b50\uff0c\u8fd8\u5f97\u4f60\u4eec\u4e24\u5144\u5f1f\u51fa\u9635\uff0c\u6240\u4ee5\u4f60\u4eec\u4e0d\u52a8\u58f0\u8272\u5730\u8ddf\u5728\u8f66\u65c1\u3002\u53ea\u53ef\u60dc\u771f \u78b0\u5230\u786c\u628a\u5b50\u65f6\uff0c\u8fde\u4f60\u4eec\u4fe9\u4e5f\u62b5\u6321\u4e0d\u4f4f\u3002\u201d\u4ed6\u62e8\u8f6c\u9a6c\u5934\uff0c\u62f1\u62f1\u624b\u9053\uff1a\u201c\u6309\u89c4\u77e9\uff0c\u6211\u4eec\u8be5\u5230\u9556\u4e3b \u5bb6\u91cc\u4ea4\u5378\uff0c\u4f46\u76f8\u5e9c\u95e8\u9ad8\uff0c\u6211\u8f88\u6821\u539a\u53ef\u4e0d\u6562\u8fdb\u53bb\u3002\u4e24\u4f4d\u6559\u5e08\u66ff\u6211\u4eec\u7f8e\u8a00\u4e00\u53e5\uff0c\u8fd9\u9556\u4f60\u4eec\u81ea\u5df1\u53bb \u62bc\u56de\u5427\uff0c\u6211\u5b5f\u575a\u9886\u60c5\u3002\u8bf4\u7f62\uff0c\u5bf9\u6842\u5192\u4e8c\u4eba\uff0c\u518d\u6df1\u6df1\u4e00\u63d6\uff0c\u8868\u793a\u8c22\u610f\u3002\u4e0d\u542c\u529d\u963b\uff0c\u62e8\u9a6c\u4fbf\u8d70\u3002 \u4ed6\u5fc3\u4e2d\u5bf9\u4e8c\u9646\u548c\u7ae5\u9556\u5934\u90fd\u5f88\u4e0d\u6ee1\uff0c\u53ea\u662f\u6df1\u6df1\u611f\u6fc0\u6842\u5192\u4e8c\u4eba\u3002 \u3000\u3000\u6842\u4ef2\u660e\u89c1\u4ed6\u8d1f\u6c14\u800c\u884c\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u4eba\u5012\u4e5f\u662f\u4e2a\u8840\u6c14\u7537\u5b50\u3002\u201d\u4ed6\u62c9\u7740\u5192\u6d63\u83b2\u6b63\u60f3\u544a \u8f9e\uff0c\u9646\u660e\u5374\u53c8\u4e0a\u524d\u62e6\u963b\u9053\uff1a\u201c\u8fd9\u6b21\u591a\u5f97\u4e24\u4f4d\u5144\u53f0\u51fa\u624b\u3001\u5c0f\u5f1f\u4ea4\u6d45\u8a00\u6df1\uff0c\u5982\u4e24\u4f4d\u5144\u53f0\u5c1a\u672a\u6709\u843d \u8db3\u4e4b\u5904\uff0c\u5c31\u8bf7\u5230\u76f8\u5e9c\u91cc\u53bb\u8c0b\u4e2a\u5dee\u4e8b\u5982\u4f55\uff1f\u201d\u6842\u4ef2\u660e\u602b\u7136\u4e0d\u60a6\uff0c\u51e0\u4e4e\u5c31\u8981\u53d1\u4f5c\uff0c\u4e0d\u6599\u5192\u70f7\u84ec\u5374 \u662f\u559c\u5f62\u4e8e\u8272\uff0c\u8fde\u58f0\u7b11\u9053\uff1a\u201c\u591a\u8c22\u4e24\u4f4d\u6559\u5e08\u7237\u5173\u7167\uff0c\u6211\u4eec\u4e5f\u4e0d\u5ba2\u5957\u63a8\u8f9e\u4e86\uff0c\u82e5\u7136\u5f97\u5728\u76f8\u5e9c\u5b89 \u8eab\uff0c\u90a3\u53ef\u662f\u6c42\u4e4b\u4e0d\u5f97\uff01\u201d\u6842\u4ef2\u660e\u731b\u7136\u4f1a\u610f\uff0c\u7acb\u523b\u88c5\u51fa\u7b11\u5bb9\uff0c\u8fde\u58f0\u9053\u8c22\u3002 \u3000\u3000\u5927\u8f66\u5728\u4eac\u57ce\u8857\u9053\u4e0a\u957f\u9a70\u800c\u8fc7\uff0c\u5411\u76f8\u5e9c\u524d\u884c\u3002\u8def\u4e0a\u5192\u6d63\u83b2\u518d\u95ee\u76f8\u5e9c\u4e70\u8fd9\u4e09\u5341\u516d\u4e2a\u5c11\u5973\u5e72 \u561b\uff1f\u9646\u5bb6\u5144\u5f1f\u8fd9\u65f6\u5df2\u628a\u4e24\u4eba\u5f53\u505a\u81ea\u5df1\u4eba\uff0c\u4e0d\u518d\u9690\u7792\uff0c\u544a\u8bc9\u4ed6\u4eec\u9053\uff1a\u201c\u8fd9\u4e09\u5341\u516d\u4e2a\u5c11\u5973\u90fd\u662f\u76f8 \u7237\u6697\u4e2d\u8bf7\u4eba\u5728\u82cf\u676d\u4e24\u5730\u641c\u4e70\u7684\uff0c\u6709\u4e9b\u662f\u51fa\u540d\u6b4c\u5973\uff0c\u4f46\u5927\u591a\u6570\u662f\u8d2b\u5bd2\u4eba\u5bb6\u7684\u6807\u81f4\u5973\u513f\u3002\u4e5f\u96be\u4e3a \u4e70\u7684\u4eba\u9009\u5f97\u4e2a\u4e2a\u90fd\u662f\u8fd9\u6837\u5982\u82b1\u4f3c\u7389\u3002\u81f3\u4e8e\u4e3a\u4ec0\u4e48\u4e70\u7684\uff0c\u90a3\u6211\u4eec\u53ef\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5217\u4f4d\u770b\u5b98\uff0c\u4f60\u9053\u662f\u4e3a\u4ec0\u4e48\u4e70\u7684\uff0c\u8bf4\u8d77\u6765\u5374\u6709\u4e00\u6bb5\u6545\u4e8b\u3002\u539f\u6765\u7eb3\u5170\u5bb9\u82e5\u867d\u662f\u5f53\u65f6\u7b2c\u4e00\u624d \u5b50\uff0c\u5c24\u4ee5\u5411\u540d\u51a0\u4e8e\u5168\u56fd\uff0c\u4ed6\u7684\u7236\u4eb2\u7eb3\u5170\u660e\u73e0\uff0c\u5374\u662f\u4e2a\u4e0d\u901a\u6587\u58a8\uff0c\u5eb8\u4fd7\u4e0d\u582a\u7684\u4eba\u3002\u4ed6\u4ed7\u7740\u662f\u5b97 \u5ba4\u5185\u4eb2\uff0c\u53c8\u5584\u5949\u627f\uff0c\u4ece\u90e8\u66f9\u5fae\u804c\u4e00\u76f4\u5347\u5230\u5f53\u671d\u7684\u5927\u5b66\u58eb\uff08\u5bb0\u76f8\uff09\u3002\u4ed6\u89c1\u987a\u6cbb\u548c\u5eb7\u7199\u4e24\u4e2a\u7687\u5e1d \u90fd\u5f88\u6ce8\u91cd\u6587\u5b66\uff0c\u4fbf\u6697\u5730\u91cc\u62db\u7eb3\u4e86\u8bb8\u591a\u6587\u4eba\u4f9b\u517b\u5728\u5bb6\uff0c\u505a\u4e86\u8bb8\u591a\u6587\u7ae0\uff0c\u5192\u5145\u662f\u81ea\u5df1\u505a\u7684\uff0c\u732e\u8fdb \u5bab\u53bb\uff0c\u535a\u53d6\u7687\u5e1d\u6b22\u5fc3\u3002\u7eb3\u5170\u5bb9\u82e5\u81ea\u5e7c\u5728\u8bb8\u591a\u4eba\u624d\u718f\u9676\u4e4b\u4e0b\uff0c\u52a0\u4ee5\u5929\u8d44\u806a\u654f\uff0c\u56e0\u6b64\u5e74\u7eaa\u8f7b\u8f7b\uff0c \u4fbf\u6210\u4e00\u4ee3\u624d\u5b50\u3002\u5eb7\u7199\u7687\u5e1d\u548c\u4ed6\u5e74\u9f84\u76f8\u5dee\u4e0d\u8fdc\uff0c\u89c1\u4ed6\u5982\u6b64\u624d\u5b66\uff0c\u5ba0\u7231\u5f02\u5e38\u3002\u56e0\u6b64\u6709\u4eba\u8bf4\uff0c\u660e\u73e0 \u4e4b\u80fd\u505a\u5230\u5927\u5b66\u58eb\uff0c\u5f97\u4ed6\u513f\u5b50\u4e4b\u529b\u4e0d\u5c11\uff0c\u53ef\u7b97\u662f\u5b98\u573a\u4e00\u4ef6\u5f02\u4e8b\u3002 \u3000\u3000\u6709\u4e00\u5929\u7eb3\u5170\u660e\u73e0\u966a\u7740\u5eb7\u7199\u5728\u897f\u4e66\u623f\u95f2\u8bdd\uff0c\u8bf4\u8d77\u5728\u5e84\u5b50\u5357\u534e\u7ecf\u91cc\u7684\u4e00\u6bb5\u6545\u4e8b\uff0c\u8bb0\u4e0d\u6e05\u695a\uff0c \u53eb\u5185\u76d1\u53d6\u4e66\u6765\u67e5\uff0c\u90a3\u5185\u76d1\u9519\u62ff\u4e86\u8001\u5b50\u7684\u9053\u5fb7\u7ecf\uff0c\u5eb7\u7199\u8dfa\u7740\u811a\u9a82\u9053\uff1a\u201c\u8822\u866b\uff01\u201d\u53c8\u53f9\u53e3\u6c14\u5bf9\u660e \u73e0\u9053\uff1a\u201c\u8fd9\u73ed\u8822\u7269\u771f\u662f\u8ba8\u538c\uff0c\u4ece\u6765\u8bf4\u7684\u2018\u7ea2\u8896\u6dfb\u9999\u591c\u8bfb\u4e66\u2019\u591a\u4e48\u6709\u8da3\u3002\u6715\u5bcc\u6709\u56db\u6d77\uff0c\u5c31\u662f\u7f3a \u4e4f\u90a3\u4e48\u51e0\u4e2a\u51b0\u96ea\u806a\u660e\u7684\u5973\u5b69\u66ff\u6715\u6dfb\u9999\u591c\u8bfb\u3002\u60f3\u90a3\u5357\u5510\u674e\u540e\u4e3b\uff0c\u867d\u662f\u4ea1\u56fd\u4e4b\u541b\uff0c\u5374\u6709\u5927\u5c0f\u5468 \u540e\uff0c\u5a34\u719f\u540c\u7ae0\uff0c\u7cbe\u901a\u97f3\u5f8b\uff0c\u98ce\u6d41\u97f5\u4e8b\uff0c\u4e07\u53e4\u6d41\u4f20\uff0c\u6715\u53cd\u800c\u6bd4\u4e0d\u4e0a\u4ed6\u5462\uff01\u201d\u660e\u73e0\u542c\u4e86\uff0c\u56e0\u4e8b\u6d89 \u5185\u5ef7\uff0c\u4e0d\u6562\u4f5c\u58f0\uff0c\u4f46\u5fc3\u4e2d\u5374\u6709\u4e86\u4e00\u4e2a\u6253\u7b97\u3002 \u3000\u3000\u660e\u73e0\u56de\u5e9c\u4e4b\u540e\uff0c\u60f3\u8d77\u82cf\u676d\u5dde\uff0c\u5c71\u5ddd\u79c0\u7f8e\uff0c\u7075\u6c14\u6240\u85b0\uff0c\u7d20\u591a\u7f8e\u5973\uff0c\u7acb\u523b\u6253\u53d1\u5bb6\u4eba\u5230\u82cf\u676d\u4e00 \u5e26\u6311\u9009\u90a3\u4e9b\u4f53\u6001\u82d7\u6761\uff0c\u9762\u8c8c\u6e05\u79c0\u7684\u6807\u81f4\u5973\u5b69\u513f\uff0c\u51c6\u5907\u6536\u5728\u5e9c\u4e2d\uff0c\u8bf7\u6587\u4eba\u5b66\u58eb\u6559\u4f1a\u8bd7\u4e66\uff0c\u7434\u5e08 \u821e\u5a18\u8bad\u7ec3\u6b4c\u821e\u3002\u8bad\u7ec3\u6210\u529f\u4e4b\u540e\uff0c\u518d\u5077\u5077\u732e\u7ed9\u7687\u4e0a\u3002\u4f46\u660e\u73e0\u4e3a\u4e86\u6cbd\u540d\u9493\u8a89\uff0c\u4e0d\u6562\u516c\u7136\u4ee5\u76f8\u5e9c\u4e4b \u540d\uff0c\u8bf7\u5730\u65b9\u5b98\u6d3e\u5175\u62a4\u9001\u3002\u56e0\u6b64\uff0c\u624d\u7531\u76f8\u5e9c\u7684\u5e08\u7237\u5b9a\u4e0b\u8ba1\u7b56\uff0c\u53eb\u9646\u660e\u3001\u9646\u4eae\u4e24\u4e2a\u6b66\u571f\u51fa\u9762\uff0c\u8f6c \u8bf7\u6b66\u5a01\u9556\u5c40\uff0c\u62a4\u9001\u6765\u4eac\u3002 \u3000\u3000\u9646\u660e\u9646\u4eae\u5c06\u4e09\u5341\u516d\u540d\u5c11\u5973\uff0c\u9001\u5230\u76f8\u5e9c\u4e4b\u540e\uff0c\u660e\u73e0\u81ea\u7136\u5341\u5206\u9ad8\u5174\u3002\u4f46\u56e0\u4ed6\u4e00\u5fc3\u76d8\u7b97\u600e\u6837\u8bad \u7ec3\u7684\u4e8b\u60c5\uff0c\u5bf9\u9646\u660e\u9646\u4eae\u4fdd\u8350\u6842\u5192\u4e8c\u4eba\uff0c\u5374\u4e0d\u8010\u7ec6\u542c\u4e0b\u53bb\uff0c\u968f\u4fbf\u628a\u624b\u4e00\u6325\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u4f60\u6709 \u4e24\u4e2a\u670b\u53cb\u8981\u8fdb\u6765\uff0c\u5c31\u5b89\u63d2\u4ed6\u4eec\u5728\u56ed\u5b50\u91cc\u770b\u56ed\u5427\u3002\u201d\u8fd9\u4e2a\u5dee\u4f7f\uff0c\u7b49\u4e8e\u4ec6\u5f79\uff0c\u4e24\u9646\u5bf9\u6842\u5192\u8bf4\u53ca\uff0c \u90fd\u89c9\u4e0d\u597d\u610f\u601d\uff0c\u5374\u4e0d\u6599\u4e8c\u4eba\u4e00\u53e3\u5c31\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u6842\u5192\u4e8c\u4eba\u8fdb\u4e86\u76f8\u5e9c\u4e4b\u540e\uff0c\u4e00\u5fc3\u60f3\u89c1\u7eb3\u5170\u5bb9\u82e5\uff0c\u597d\u63a2\u542c\u5f20\u534e\u662d\u7684\u6d88\u606f\uff0c\u4e0d\u6599\u4e00\u8fde\u4e24\u4e09\u4e2a \u6708\uff0c\u90fd\u6ca1\u89c1\u7740\u3002\u770b\u5b88\u82b1\u56ed\uff0c\u53c8\u4e0d\u80fd\u968f\u4fbf\u51fa\u53bb\uff0c\u95f7\u5f97\u6842\u4ef2\u660e\u4ec0\u4e48\u4f3c\u7684\u3002\u5192\u6d63\u83b2\u867d\u7136\u4e0d\u65f6\u5b89\u6170 \u4ed6\uff0c\u4f46\u60f3\u8d77\u5434\u4e09\u6842\u4e3e\u4e8b\u4e4b\u540e\uff0c\u5916\u5934\u5927\u5c40\u4e0d\u77e5\u5982\u4f55\uff0c\u4ea6\u662f\u4e0d\u7981\u5fc3\u7126\u3002 \u3000\u3000\u6625\u6765\u6625\u53bb\uff0c\u8f6c\u77ac\u5230\u4e86\u69b4\u82b1\u7167\u773c\u7684\u4e94\u6708\uff0c\u4e00\u65e5\u6e05\u6668\u65f6\u5206\uff0c\u6842\u4ef2\u660e\u88ab\u9063\u53bb\u76d1\u7763\u4fee\u7406\u56ed\u5b50\u7684\u5de5 \u4eba\uff0c\u5192\u6d63\u83b2\u4e00\u4eba\u72ec\u81ea\u5728\u82b1\u5f84\u5f98\u5f8a\u3002\u4e0d\u77e5\u4e0d\u89c9\uff0c\u901a\u8fc7\u5047\u5c71\u77f3\u6d1e\uff0c\u6765\u5230\u4e86\u56ed\u5b50\u6df1\u5e7d\u4e4b\u5904\uff0c\u53ea\u89c1\u6797 \u6728\u8471\u90c1\uff0c\u5947\u82b1\u70c2\u6f2b\uff0c\u4e00\u5e26\u6e05\u6d41\uff0c\u4ece\u82b1\u6728\u6df1\u5904\u6cfb\u4e8e\u77f3\u9699\u4e4b\u4e0b\uff0c\u4e24\u8fb9\u98de\u697c\u63d2\u7a7a\uff0c\u96d5\u680f\u7ee3\u69db\uff0c\u7686\u9690 \u4e8e\u5c71\u5773\u6811\u68a2\u4e4b\u95f4\uff0c\u666f\u8272\u7f8e\u4e3d\u6781\u4e86\uff0c\u4e5f\u5e7d\u96c5\u6781\u4e86\uff03\u558a\u6d63\u83b2\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u5929\u4e0a\u795e\u4ed9\u5e9c\uff0c\u4eba\u95f4\u5bb0\u76f8 \u5bb6\u3002\u8fd9\u8bdd\u8bf4\u5f97\u679c\u662f\u4e0d\u9519\uff01\u201d\u6b63\u5446\u60f3\u95f4\uff0c\u5ffd\u542c\u5f97\u6709\u97f3\u4e50\u4e4b\u58f0\u8fdc\u8fdc\u98d8\u6765\u3002\u5979\u4e0d\u89c9\u5faa\u7740\u4e50\u58f0\u5bfb\u53bb\uff0c \u7ed5\u8fc7\u51e0\u5904\u5047\u5c71\uff0c\u53ea\u89c1\u9762\u524d\u8c41\u7136\u5f00\u6717\uff0c\u4e00\u9762\u6c34\u5e73\u5982\u955c\u7684\u8377\u5858\u6a2a\u5728\u9762\u524d\uff0c\u6c60\u642a\u4e0a\u5343\u767e\u6735\u7ea2\u83b2\uff0c\u90fd \u5df2\u5f00\u653e\u3002\u56db\u9762\u7ea2\u83b2\u56f4\u7ed5\u4e2d\uff0c\u6c60\u4e2d\u5fc3\u53c8\u6709\u51e0\u5341\u6735\u7279\u522b\u76db\u5f00\u7684\u767d\u83b2\uff0c\u5b9b\u5982\u7d2f\u8863\u4ed9\u5973\uff0c\u7acb\u5728\u6c34\u4e2d \u592e\uff0c\u6c60\u5858\u5468\u56f4\u6709\u767d\u77f3\u4e3a\u680f\uff0c\u6c60\u4e0a\u6709\u5c0f\u6865\u4e5d\u66f2\uff0c\u86fe\u8712\u5982\u5e26\uff0c\u6c60\u4e2d\u7684\u4e00\u4e2a\u5c0f\u4eab\u4e0a\u9762\u6709\u51e0\u4e2a\u821e\u5a18\u7fe9 \u7fe9\u8d77\u821e\uff0c\u4ead\u4e2d\u6709\u4e00\u4e2a\u5c11\u5e74\u516c\u5b50\uff0c\u72ec\u81ea\u5f39\u7434\u3002\u90a3\u51e0\u4e2a\u821e\u5a18\uff0c\u5c31\u968f\u7740\u7434\u58f0\uff0c\u4e14\u6b4c\u4e14\u821e\u3002 \u3000\u3000\u5192\u6d63\u83b2\u5999\u89e3\u97f3\u5f8b\uff0c\u8fdc\u542c\u7434\u58f0\uff0c\u53ea\u89c9\u4e00\u7247\u51c4\u82e6\u60c5\u8c03\uff0c\u4e0d\u7981\u5446\u4e86\u5fc3\u60f3\uff1a\u7eb3\u5170\u5bb9\u82e5\u5bcc\u8d35\u8363\u534e\u5df2 \u5230\u4e86\u9876\u70b9\uff0c\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u624d\u540d\u7edd\u4ee3\u4eba\u66f4\u662f\u53e4\u4eca\u7f55\u89c1\uff0c\u4ed6\u8fd8\u6709\u4ec0\u4e48\u4e0d\u6ee1\u8db3\u7684\uff0c\u5979\u4e0d\u89c9\u6b65\u4e0a\u5c0f\u6865\uff0c \u5411\u6c60\u5858\u4e2d\u592e\u7684\u4eab\u5b50\u8d70\u53bb\u3002\u8d70\u5230\u4e00\u534a\uff0c\u4ead\u4e0a\u6b4c\u58f0\u560e\u7136\u800c\u6b62\u3002\u53ea\u542c\u5f97\u7eb3\u5170\u5bb9\u82e5\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u9996\u4e0d \u5b9c\u5408\u5531\uff0c\u53ea\u5b9c\u6e05\u6b4c\uff0c\u7d2b\u83ca\u4f60\u7ed9\u6211\u6309\u8c31\u5531\u5427\u3002\u201d\u8bf4\u7f62\uff0c\u53c8\u5f39\u8d77\u7434\u6765\uff0c\u6839\u672c\u6ca1\u6ce8\u610f\u5230\u6709\u4eba\u8d70\u4e0b\u5c0f \u6865\u3002 \u3000\u3000\u5192\u6d63\u83b2\u542c\u5f97\u201c\u7d2b\u83ca\u201d\u4e8c\u5b57\uff0c\u89c9\u5f97\u8fd9\u540d\u5b57\u597d\u719f\uff0c\u6b63\u601d\u7d22\u95f4\uff0c\u7434\u58f0\u5df2\u8d77\uff0c\u5176\u58f0\u51c4\u82e6\uff0c\u6bd4\u524d\u66f4 \u751a\uff0c\u5b9b\u5982\u4e09\u5ce1\u733f\u557c\uff0c\u9a73\u4eba\u591c\u6ce3\u3002\u4e00\u4e2a\u5c11\u5973\uff0c\u9762\u5411\u7eb3\u5170\uff0c\u80cc\u5411\u6d63\u83b2\uff0c\u6309\u8c31\u6e05\u6b4c\u3002\u6b4c\u9053\uff1a \u3000\u3000\u201c\u77ac\u606f\u6d6e\u751f\uff0c\u4fdd\u72d0\u5982\u65af\uff0c\u4f4e\u5f8a\u600e\u5fd8\uff1f\u8bb0\u7ee3\u584c\u95f2\u65f6\uff0c\u5e76\u5439\u7ea2\u5703\uff1b\u96d5\u680f\u66f2\u5904\uff0c\u540c\u501a\u659c\u9633\u3002\u68a6 \u597d\u96be\u7559\uff0c\u8bd7\u6b8b\u83ab\u7eed\uff0c\u8d62\u5f97\u66f4\u6df1\u54ed\u4e00\u573a\u3002\u9057\u5bb9\u5728\uff0c\u53ea\u7075\u98d8\u4e00\u8f6c\uff0c\u672a\u8bb8\u7aef\u8be6\u3002 \u3000\u3000\u91cd\u5bfb\u78a7\u843d\u832b\u832b\uff0c\u6599\u77ed\u53d1\u671d\u6765\u5b9a\u6709\u971c\uff0c\u4fbf\u4eba\u95f4\u5929\u4e0a\uff0c\u5c18\u7f18\u672a\u65ad\uff0c\u6625\u82b1\u79cb\u6708\uff0c\u89e6\u7eea\u8fd8\u4f24\uff01\u6b32 \u7ed3\u7ef8\u7f00\uff0c\u7ffb\u60ca\u6447\u843d\uff0c\u4e24\u5904\u9e33\u9e2f\u5404\u81ea\u51c9\uff01\u771f\u65e0\u5948\uff0c\u628a\u58f0\u58f0\u6a90\u96e8\uff0c\u8c31\u51fa\u56de\u80a0\u3002\u201d \u3000\u3000\u6b4c\u58f0\u65b9\u505c\uff0c\u4e00\u58f0\u88c2\u5e1b\uff0c\u7434\u5f26\u5df2\u65ad\u4e86\u51e0\u6839\u3002\u7eb3\u5170\u5bb9\u82e5\u63a8\u7434\u800c\u8d77\uff0c\u53f9\u4e86\u53e3\u6c14\u3002\u5192\u6d63\u83b2\u542c\u5f97\u5982 \u9189\u5982\u75f4\uff0c\u5fc3\u60f3\uff1a\u201c\u602a\u4e0d\u5f97\u6211\u4e00\u8fdb\u56ed\u5b50\u91cc\u6765\uff0c\u5c31\u542c\u5f97\u4eba\u8bf4\uff0c\u7eb3\u5170\u516c\u5b50\u662f\u4e2a\u75f4\u60c5\u79cd\u5b50\uff0c\u4ed6\u592b\u4eba\u5df2 \u6b7b\u4e86\u4e00\u5e74\uff0c\u4ed6\u8fd8\u662f\u8fd9\u6837\u54c0\u75db\u3002\u8fd9\u9996\u60bc\u4ea1\u8bcd\u771f\u662f\u5343\u53e4\u81f3\u6027\u81f3\u60c5\u7684\u6587\u5b57\uff01\u201d\u5979\u5480\u56bc\u201c\u68a6\u597d\u96be\u7559\uff0c \u8bd7\u6b8b\u83ab\u7eed\u3002\u201d\u51e0\u53e5\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u5e74\u5c11\u592b\u59bb\uff0c\u6069\u6df1\u4e49\u91cd\uff0c\u771f\u662f\u6613\u62db\u5929\u5992\u5417\uff1f\u201d\u60f3\u5230\u8fd9\u91cc\uff0c\u4e0d \u7981\u5fc3\u91cc\u7b11\u9053\uff1a\u201c\u600e\u7684\u8fd9\u6837\u5bb9\u6613\u4f24\u611f\uff0c\u6211\u548c\u4ef2\u660e\u5c31\u662f\u4e00\u5bf9\u65e0\u751f\u7231\u4fa3\u3002\u201d\u5979\u60f3\u7740\u60f3\u7740\uff0c\u81ea\u89c9\u6bd4\u7eb3 \u5170\u5bb9\u82e5\u201c\u5e78\u798f\u201d\u591a\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u90a3\u4e2a\u6b4c\u5973\u56de\u8f6c\u5934\u6765\uff0c\u89c1\u5192\u6d63\u83b2\u7ad9\u5728\u4eab\u524d\uff0c\u5ffd\u7136\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u4f4e\u4f4e\u53eb\u4e86\u51fa\u6765\u3002\u5192\u6d63 \u83b2\u4e00\u770b\uff0c\u8ba4\u5f97\u5979\u5c31\u662f\u5f53\u65e5\u81ea\u5df1\u5728\u5927\u8f66\u4e0a\u6551\u51fa\u7684\u5c11\u5973\uff0c\u602a\u4e0d\u5f97\u540d\u5b57\u8fd9\u6837\u719f\u3002\u5192\u6d63\u83b2\u6025\u5fd9\u5411\u5979\u6253 \u4e2a\u773c\u8272\uff0c\u8de8\u8fdb\u4eab\u6765\u3002 \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u542c\u5f97\u7d2b\u83ca\u4f4e\u53eb\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u89c1\u4e00\u4e2a\u4fca\u4fcf\u5c11\u5e74\uff0c\u536b\u58eb\u88c5\u675f\uff0c\u4e0d\u89c9\u4e5f\u6709\u70b9\u60ca\u8be7\uff0c\u95ee \u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u559c\u6b22\u542c\u7434\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u6211\u662f\u770b\u56ed\u7684\u3002\u516c\u5b50\uff0c\u4f60\u8fd9\u9996\u2018\u6c81\u56ed\u6625\u2019\u505a\u5f97\u597d \u6781\u4e86\uff0c\u53ea\u662f\u592a\u51c4\u82e6\u4e86\u4e9b\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u5947\u9053\uff1a\u201c\u4f60\u61c2\u5f97\u8bcd\uff1f\u201d\u5192\u6d63\u83b2\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u7a0d\u5fae \u61c2\u5f97\u4e00\u70b9\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u8bf7\u5979\u5750\u4e0b\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u89c9\u5f97\u8fd9\u8bcd\u5f88\u597d\uff0c\u6211\u5374\u89c9\u5f97\u6709\u51e0\u4e2a\u5b57\u97f3\u597d\u50cf\u8fc7\u4e8e \u9ad8\u4ea2\uff0c\u4e0d\u5207\u97f3\u5f8b\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u516c\u5b50\u96c5\u4eba\uff0c\u6599\u4e0d\u4f1a\u62d8\u6ce5\u4e8e\u6b64\uff0c\u4e3b\u4ee3\u4e4b\u5411\uff0c\u5148\u884c\u97f3\u4e50\uff0c\u800c\u540e \u6309\u58f0\u586b\u8bcd\uff0c\u5c24\u4ee5\u5468\u7f8e\u57ce\u3001\u59dc\u767d\u77f3\u4e24\u5927\u8bcd\u5bb6\u66f4\u4e3a\u8bb2\u7a76\uff1f\u4f46\u5176\u8fab\u75c5\u5374\u5728\u524a\u8db3\u9002\u5c65\uff0c\u7f3a\u4e4f\u6027\u7075\uff0c\u6240 \u4ee5\u82cf\uff08\u4e1c\u5761\uff09\u8f9b\uff08\u5f03\u75be\uff09\u51fa\uff0c\u968f\u610f\u6325\u6d12\uff0c\u544a\u6210\u8bcd\u7ae0\uff0c\u501a\u58f0\u4e00\u9053\uff0c\u5927\u589e\u5149\u5f69\u3002\u4f46\u6709\u65f6\u5374\u53c8\u4f24\u4e8e \u8fc7\u7c97\u3002\u516c\u5b50\u4e4b\u8bcd\uff0c\u4e0a\u8ffd\u5357\u5510\u540e\u4e3b\uff0c\u5177\u771f\u6027\u60c5\uff0c\u8bfb\u4e4b\u5982\u540d\u82b1\u7f8e\u9526\uff0c\u90c1\u7136\u800c\u65b0\u3002\u53c8\u5982\u78a7\u6d77\u6f84\u6ce2\uff0c \u660e\u661f\u768e\u6d01\u3002\u4f55\u5fc5\u62d8\u6ce5\u4e8e\u4e00\u5b57\u4e00\u97f3\uff1f\u201d\u7eb3\u5170\u5bb9\u82e5\u542c\u5f97\u94ee\u5706\u4e86\u773c\uff01 \u3000\u3000\u5192\u6d63\u83b2\u5bf9\u8bcd\u5b66\u7684\u89c1\u89e3\u548c\u7eb3\u5170\u5bb9\u82e5\u5b8c\u5168\u4e00\u6837\uff0c\u4ee4\u7eb3\u5170\u5bb9\u82e5\u60ca\u5947\u7684\u662f\uff1a\u4ee5\u5192\u6d63\u83b2\u8fd9\u6837\u4e00\u4e2a \u201c\u770b\u56ed\u4eba\u201d\u7684\u8eab\u4efd\uff0c\u5c45\u7136\u8bb2\u5f97\u51fa\u8fd9\u756a\u8bdd\u6765\u3002\u4ed6\u4e0d\u7981\u559c\u5b5c\u5b5c\u5730\u62c9\u8d77\u5192\u6d63\u83b2\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6bd4 \u90a3\u4e9b\u8150\u5112\u5f3a\u5f97\u591a\u4e86\uff01\u600e\u7684\u5374\u59d4\u5c48\u5728\u8fd9\u91cc\u770b\u56ed\uff1f\u201d\u5192\u6d63\u83b2\u9762\u4e0a\u53d1\u70ed\uff0c\u7d2b\u83ca\u5728\u65c1\u8fb9\u201c\u55e4\u201d\u7684\u4e00\u58f0 \u7b11\u4e86\u51fa\u6765\uff0c\u5192\u6d63\u83b2\u4e0d\u81ea\u89c9\u5730\u628a\u624b\u4e00\u6454\uff0c\u7eb3\u5170\u5bb9\u82e5\u53ea\u89c9\u4e00\u80a1\u5927\u529b\u63a8\u6765\uff0c\u8e6c\uff01\u8e6c\uff01\u8e6c\uff01\u8fde\u9000\u4e09 \u6b65\uff0c\u8fde\u5fd9\u6276\u7740\u680f\u6746\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u6709\u8fd9\u6837\u4fca\u7684\u529f\u592b\uff01\u201d\u4ed6\u8fd8\u4ee5\u4e3a\u5192\u6d63\u83b2\u6000\u624d \u4e0d\u9047\uff0c\u6240\u4ee5\u6545\u610f\u70ab\u9732\uff0c\u6587\u7684\u6b66\u7684\u90fd\u663e\u51fa\u4e00\u624b\u3002 \u3000\u3000\u5192\u6d63\u83b2\u4e00\u6454\u4e4b\u540e\uff0c\u731b\u7684\u9192\u8d77\uff0c\u81ea\u5df1\u5df2\u626e\u6210\u7537\u5b50\uff0c\u5374\u8fd8\u4e0d\u81ea\u89c9\u7684\u9732\u51fa\u5973\u513f\u672c\u76f8\uff0c\u5c82\u4e0d\u53ef \u7b11\uff1f\u7eb3\u5170\u5bb9\u82e5\u53c8\u9053\uff1a\u201c\u6211\u6709\u4e00\u4f4d\u4e66\u50ee\uff0c\u4e5f\u50cf\u4f60\u4e00\u6837\uff0c\u65e2\u89e3\u8bcd\u7ae0\uff0c\u4ea6\u901a\u6b66\u827a\u3002\u4f60\u6709\u6ca1\u6709\u529f\u592b\uff1f \u6211\u5012\u60f3\u53eb\u4f60\u548c\u4ed6\u89c1\u4e00\u89c1\u9762\u3002\u201d\u5192\u6d63\u83b2\u5927\u559c\uff0c\u8fde\u5fd9\u7b54\u5e94\u3002\u7eb3\u5170\u5bb9\u82e5\u6d12\u8131\u5f02\u5e38\uff0c\u643a\u7740\u5979\u7684\u624b\uff0c\u6b65 \u4e0b\u5c0f\u6865\u3002\u4ed6\u662f\u628a\u5192\u6d63\u83b2\u5f53\u670b\u53cb\u770b\u5f85\uff0c\u4ee5\u76f8\u56fd\u516c\u5b50\u548c\u201c\u770b\u56ed\u4eba\u201d\u643a\u624b\u540c\u884c\uff0c\u5728\u5f53\u65f6\u53ef\u662f\u4e2a\u9707\u4e16 \u9a87\u4fd7\u4e4b\u4e8b\u3002 \u3000\u3000\u5192\u6d63\u83b2\u89c1\u4ed6\u7eaf\u51fa\u81ea\u7136\uff0c\u5c31\u8ba9\u4ed6\u7275\u7740\u81ea\u5df1\u7684\u624b\uff0c\u8d70\u51fa\u4eab\u5b50\u3002 \u3000\u3000\u4e24\u4eba\u8d70\u51fa\u4ead\u5b50\uff0c\u8f6c\u8fc7\u5c71\u5761\uff0c\u7a7f\u82b1\u62c2\u67f3\uff0c\u76d8\u65cb\u66f2\u6298\uff0c\u5ffd\u89c1\u8fce\u9762\u7a81\u51fa\u63d2\u5929\u7684\u5927\u73b2\u73d1\u5c71\u77f3\u6765\uff0c \u4e0a\u9762\u5f02\u8349\u7eb7\u5782\uff0c\u628a\u65c1\u8fb9\u623f\u5c4b\u6089\u7686\u906e\u4f4f\u3002\u90a3\u4e9b\u5f02\u8349\u6709\u7275\u85e4\u7684\uff0c\u6709\u5f15\u8513\u7684\uff0c\u6216\u5782\u5c71\u5cad\uff0c\u6216\u7a7f\u77f3 \u811a\uff0c\u751a\u81f3\u5782\u6a90\u6302\u67f1\uff0c\u7d22\u780c\u76d8\u9636\uff0c\u6216\u5982\u7fe0\u5e26\u98d8\u6447\uff1b\u6216\u5982\u91d1\u7ef3\u5e61\u5c48\uff0c\u5e7d\u9999\u9635\u9635\uff0c\u6251\u5165\u9f3b\u89c2\uff0c\u6bd4\u521a \u624d\u7684\u8377\u5858\u80dc\u5730\uff0c\u66f4\u663e\u5f97\u6e05\u96c5\u7edd\u4fd7\uff0c\u5192\u6d63\u83b2\u8d5e\u53f9\u9053\uff1a\u201c\u8fd9\u6837\u7684\u5730\u65b9\uff0c\u4e5f\u53ea\u6709\u50cf\u516c\u5b50\u8fd9\u6837\u7684\u4eba\u624d \u914d\u4f4f\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u9aa4\u9047\u89e3\u4eba\uff0c\u6101\u6000\u987f\u89e3\uff0c\u5174\u81f4\u52c3\u52c3\u5730\u66ff\u5979\u89e3\u91ca\uff1a\u90a3\u7275\u85e4\u9644\u845b\u7684\u53eb\u201c\u85e4\u841d\u859b \u8354\u201d\uff0c\u90a3\u5f02\u9999\u6251\u9f3b\u7684\u662f\u201c\u675c\u82e5\u8861\u829c\u201d\uff0c\u90a3\u6de1\u7ea2\u5e26\u8f6f\u7684\u53eb\u201c\u7d2b\u4f1a\u9752\u82b7\u201d\u8fd9\u4e9b\u5f02\u8349\u4e4b\u540d\uff0c\u90fd\u662f\u5192 \u6d63\u83b2\u5728\u201c\u79bb\u9a9a\u201d\u201c\u6587\u9009\u201d\u91cc\u8bfb\u8fc7\u7684\uff0c\u5374\u4e00\u6837\u4e5f\u6ca1\u89c1\u8fc7\uff0c\u8fd9\u65f6\u542c\u7eb3\u5170\u5bb9\u82e5\u4e00\u4e00\u89e3\u91ca\uff0c\u589e\u4e86\u4e0d\u5c11 \u77e5\u8bc6\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u8def\u6e05\u8c08\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7a7f\u8fc7\u85e4\u8513\u8986\u7ed5\u7684\u6e38\u5eca\uff0c\u6b65\u5165\u4e00\u5ea7\u7cbe\u96c5\u7684\u6e05\u53a6\u3002\u8fd9\u95f4\u5927\u53a6\uff0c\u8fde\u7740 \u7b7e\u68da\uff0c\u56db\u9762\u56de\u5eca\uff0c\u7eff\u7a97\u6cb9\u58c1\uff0c\u7fa4\u5899\u4e0b\u9762\u662f\u767d\u77f3\u53f0\u9636\uff0c\u51ff\u6210\u6735\u6735\u83b2\u82b1\u6a21\u6837\uff0c\u5c4b\u5b50\u91cc\u662f\u5927\u7406\u77f3\u780c \u6210\u7eb9\u7406\uff0c\u95e8\u680f\u7a97\u6237\uff0c\u4e5f\u90fd\u7ec6\u96d5\u6210\u65f6\u65b0\u82b1\u6837\uff0c\u4e0d\u843d\u5bcc\u4e3d\u4fd7\u5957\u3002\u56db\u9762\u9999\u98ce\uff0c\u7a7f\u7a97\u5165\u6237\u3002\u7eb3\u5170\u5bb9\u82e5 \u8bf4\u9053\uff1a\u201c\u5728\u8fd9\u671b\u716e\u8317\u64cd\u7434\uff0c\u711a\u9999\u5bf9\u5955\uff0c\u5f53\u662f\u4eba\u751f\u4e00\u4e50\u3002\u201d\u8bf4\u7f62\u62cd\u4e86\u51e0\u4e0b\u624b\u638c\uff0c\u5524\u51fa\u51e0\u4e2a\u4e66 \u50bb\uff0c\u8bf4\u9053\uff1a\u201c\u4e0a\u53bb\u8bf7\u662d\u90ce\u6765\u3002\u201d\u4e0d\u4e00\u4f1a\u4e0a\u9762\u4e0b\u6765\u4e00\u4e2a\u82f1\u4fca\u5c11\u5e74\uff0c\u5192\u6d63\u83b2\u4e00\u773c\u77a7\u53bb\uff0c\u6b63\u662f\u5f53\u65e5 \u5728\u4e94\u53f0\u5c71\u76f8\u9047\u7684\u5f20\u534e\u662d\uff0c\u53ea\u662f\u4ed6\u6bd4\u524d\u7565\u4e3a\u6e05\u7626\uff0c\u4ece\u6291\u90c1\u7684\u76ee\u5149\u4e2d\u770b\u51fa\uff0c\u4f3c\u53e6\u6709\u5fc3\u4e8b\u3002\u5f20\u534e\u662d \u89c1\u7740\u5192\u6d63\u83b2\u4e5f\u662f\u4e00\u5446\uff0c\u5fc3\u60f3\uff1a\u8fd9\u4eba\u9762\u8c8c\u597d\u4f3c\u5728\u54ea\u91cc\u89c1\u8fc7\uff0c\u5374\u4e00\u65f6\u60f3\u4e0d\u8d77\u5979\u662f\u8c01\u6765\u3002 \u3000\u3000\u4e09\u4eba\u5728\u5ead\u9662\u4e2d\u8336\u9761\u67b6\u4e0b\uff0c\u56f4\u7740\u4e00\u5f20\u5927\u7406\u77f3\u507b\u82b1\u684c\u5b50\uff0c\u76d8\u819d\u800c\u5750\uff0c\u65c1\u8fb9\u6c34\u58f0\u6df7\u6dc6\uff0c\u51fa\u4e8e\u77f3 \u6d1e\uff0c\u4e0a\u5219\u85e4\u841d\u5012\u5782\uff0c\u4e0b\u5219\u843d\u82b1\u6d6e\u8361\uff0c\u9662\u5b50\u5916\u6709\u4e00\u4e1b\u4fee\u7af9\uff0c\u9ad8\u8d8a\u77ed\u5899\u3002\u8749\u58f0\u6447\u66f3\u5176\u95f4\uff0c\u5b9b\u5982\u97f3 \u4e50\uff0c\u6d63\u83b2\u9053\uff1a\u201c\u771f\u597d\u666f\u81f4\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u89c1\u684c\u4e0a\u6709\u68cb\u62a8\u4e00\u5c40\uff0c\u672a\u655b\u6b8b\u68cb\uff0c\u5ffd\u7136\u8d77\u4e86\u68cb\u5174\uff0c\u5bf9\u5192 \u6d63\u83b2\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u4e0b\u4e00\u5c40\u5982\u4f55\uff1f\u6211\u505a\u88c1\u5224\u3002\u201d\u5f20\u534e\u662d\u9053\uff1a\u201c\u516c\u5b50\u65e2\u6709\u68cb\u5174\uff0c\u4f55\u4e0d\u548c\u8fd9\u4f4d\u5144 \u53f0\u5bf9\u4e0b\uff0c\u8ba9\u6211\u5f00\u5f00\u773c\u754c\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u7b11\u9053\uff1a\u201c\u5c40\u5916\u89c2\u68cb\uff0c\u66f4\u9976\u4f73\u8da3\u3002\u201d\u8bf4\u7740\u5df2\u628a\u68cb\u5b50\u6446\u4e86\u8d77 \u6765\u3002\u5f20\u534e\u662d\u77a7\u4e86\u5192\u6d63\u83b2\u51e0\u773c\uff0c\u8d8a\u770b\u8d8a\u89c9\u9762\u719f\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u62c8\u7740\u68cb\u5b50\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f8d\u6211\u8f93\u4e86\uff0c \u516c\u5b50\u518d\u7ed9\u6211\u62a5\u4ec7\u3002\u201d\u4ed6\u7b2c\u4e00\u6b65\u5c31\u884c\u4e86\u4e2a\u5f53\u5934\u70ae\u3002 \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u5728\u65c1\u4e00\u9762\u770b\u4e00\u9762\u7b11\uff0c\u5f20\u534e\u662d\u4e00\u5f00\u5c40\u4fbf\u7740\u7740\u8fdb\u653b\uff0c\u8fdb\u4e2d\u5175\u8d77\u8fde\u73af\u7532\u518d\u51fa\u53cc\u6a2a\u8f66\uff0c \u4e03\u53ea\u68cb\u5b50\uff0c\u5411\u5bf9\u65b9\u4e2d\u8def\u731b\u88ad\u3002\u5192\u6d63\u83b2\u6c89\u7740\u5e94\u6218\uff0c\u7528\u5c4f\u98ce\u9a6c\u53cc\u76f4\u8f66\u575a\u5b88\u9635\u5730\uff0c\u7740\u6cd5\u9634\u67d4\u4e4b\u6781\uff0c \u884c\u81f3\u4e2d\u53d8\uff0c\u5df2\u5e26\u653b\u5e26\u5b88\uff0c\u53cd\u593a\u4e86\u5148\u624b\u3002\u7eb3\u5170\u5bb9\u82e6\u7b11\u9053\uff1a\u201c\u662d\u90ce\uff0c\u4f60\u8fd9\u662f\u5434\u4e09\u6842\u7684\u6218\u6cd5\uff01\u201d\u5f20 \u534e\u662d\u6115\u7136\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5bb9\u82e5\u9053\uff1a\u201c\u5434\u4e09\u6842\u8fd9\u6b21\u4e3e\u4e8b\uff0c\u58f0\u52bf\u6c79\u6d8c\uff0c\u738b\u8f85\u81e3\u5728\u897f\u5317\u8d77\u5175\uff0c\u5c1a \u803f\u4e24\u85e9\u53c8\u5728\u5357\u65b9\u9065\u4e3a\u547c\u5e94\uff0c\u5434\u4e09\u6842\u4eb2\u81ea\u7387\u9886\u5927\u5175\uff0c\u653b\u51fa\u6e56\u5317\uff0c\u60f3\u6cbf\u6c5f\u800c\u4e0b\uff0c\u653b\u5360\u5168\u56fd\u5fc3\u810f\u3002 \u653b\u52bf\u662f\u731b\u70c8\u6781\u4e86\uff0c\u4f46\u4f9d\u6211\u770b\u6765\uff0c\u975e\u8d25\u4e0d\u53ef\uff01\u5f20\u534e\u662d\u9053\uff1a\u201c\u90a3\u4f60\u662f\u8bf4\uff0c\u6211\u8fd9\u5c40\u68cb\u4e5f\u548c\u4ed6\u4e00\u6837\uff0c \u8f93\u5b9a\u4e86\uff1f\u201d\u7eb3\u5170\u5bb9\u82e5\u7b11\u9053\uff1a\u201c\u90a3\u8fd8\u9700\u8bf4\uff1f\u201d\u8bf4\u4e0d\u591a\u4e45\uff0c\u5192\u6d63\u83b2\u5927\u519b\u8fc7\u6cb3\uff0c\u5f20\u534e\u662d\u5b50\u529b\u5206\u6563\uff0c \u679c\u7136\u5df2\u5448\u8d25\u76f8\u3002\u7eb3\u5170\u5bb9\u82e5\u5ffd\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u6309\u8bf4\u6211\u4eec\u5ae1\u6d32\u4eba\uff0c\u5165\u5173\u5360\u4f60\u4eec\u7684\u5730\u65b9\uff0c\u6211\u4e5f\u5f88\u4e0d\u8d5e \u540c\u3002\u53ea\u662f\u5434\u4e09\u6842\u8981\u9a71\u81c3\u590d\u660e\uff0c\u90a3\u5374\u662f\u4e0d\u914d\uff01\u201d\u5192\u6d63\u83b2\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u4e0d\u50cf\u662f\u7687\u5ba4\u5185\u4eb2\u8bf4\u7684 \u8bdd\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u8e59\u7709\u8bf4\u9053\uff1a\u201c\u770b\u4f60\u8d85\u8fc8\u4fd7\u6d41\uff0c\u600e\u7684\u4e5f\u5b58\u79cd\u65cf\u4e4b\u89c1\uff1f\u6ee1\u6c49\u4e24\u65cf\uff0c\u6d41\u51fa\u7684\u8840\u53ef\u90fd \u662f\u7ea2\u7684\uff0c\u4ed6\u4eec\u539f\u5e94\u8be5\u662f\u5144\u5f1f\u3002\u6ee1\u6d32\u8d35\u65cf\uff0c\u81ea\u6709\u7f6a\u5b7d\uff0c\u53ef\u662f\u4e0d\u89c1\u5f97\u5728\u8d35\u65cf\u4e2d\u5c31\u6ca1\u6709\u6e05\u9192\u7684 \u4eba\uff01\u201d\u5192\u6d63\u83b2\u6697\u6697\u53f9\u9053\uff1a\u201c\u4ed6\u7684\u7236\u4eb2\u662f\u90a3\u6837\u6c61\u6d4a\u53ef\u9119\uff0c\u4ed6\u5374\u662f\u5982\u6b64\u6e05\u96c5\u8d85\u62d4\uff0c\u770b\u6765\u2018\u6709\u5176\u7236 \u5fc5\u6709\u5176\u5b50\u2019\u8fd9\u53e5\u8bdd\uff0c\u771f\u662f\u8352\u8c2c\u7684\u4e86\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u53c8\u9053\uff1a\u201c\u5176\u5b9e\uff0c\u671d\u5ef7\u6015\u7684\u4e0d\u662f\u5434\u4e09\u6842\uff0c\u800c\u662f \u853d\u5728\u6df1\u5c71\u4e2d\u7684\u674e\u6765\u4ea8\uff0c\u4ed6\u5175\u529b\u867d\u5c0f\uff0c\u5a01\u80c1\u5374\u5927\u3002\u201c\u8fd9\u6b21\u671d\u5ef7\u6d3e\u5175\u53bb\u6253\u5434\u4e09\u6842\uff0c\u5206\u4e86\u4e00\u8def\u5175\u6251 \u674e\u6765\u4ea8\uff0c\u5728\u4e09\u5ce1\u9669\u8981\u4e4b\u5730\uff0c\u7ed9\u674e\u6765\u4ea8\u4f0f\u5175\u51fa\u51fb\uff0c\u5168\u519b\u8986\u6ca1\u3002\u201d\u5192\u6d63\u83b2\u5927\u559c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u6253\u80dc \u4e86\uff01\u201d\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u7ed9\u5f20\u534e\u662d\u5403\u4e86\u4e00\u53ea\u9a6c\uff0c\u7eb3\u5170\u5bb9\u82e5\u60ca\u5f02\u5730\u671b\u5979\uff0c\u5192\u6d63\u83b2\u81ea\u89c9\u9732\u8ff9\uff0c\u6025\u5fd9\u4f4e\u4e0b \u5934\u6765\u7528\u5fc3\u4e0b\u68cb\uff0c\u7ed3\u679c\u56e0\u5b50\u529b\u5c11\u4e86\u4e00\u9a6c\uff0c\u7ed9\u5f20\u534e\u662d\u4ee5\u4e0b\u98ce\u62a2\u6210\u548c\u5c40\u3002 \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u7b11\u9053\uff1a\u201c\u4f60\u7684\u68cb\u4e0b\u5f97\u5f88\u597d\uff0c\u73b0\u5728\u8f6e\u5230\u6211\u6765\u9886\u6559\u4e86\u3002\u201d\u6b63\u6446\u68cb\u5b50\uff0c\u5ffd\u7136\u4e2b\u9b1f\u4f20 \u62a5\uff0c\u592b\u4eba\u6709\u8bf7\uff0c\u800c\u4e14\u6307\u5b9a\u8981\u662d\u90ce\u540c\u53bb\u3002\u5bb9\u82e5\u95ee\u4e86\u5192\u6d63\u83b2\u7684\u59d3\u540d\uff08\u5047\u540d\uff09\uff0c\u62f1\u62f1\u624b\u9053\uff1a\u201c\u6211\u660e \u65e5\u518d\u6d3e\u4eba\u627e\u4f60\u3002\u201d\u5f20\u534e\u662d\u8ddf\u7740\u51fa\u53bb\uff0c\u5192\u6d63\u83b2\u8d70\u5728\u540e\u9762\u3002\u5ffd\u7136\u5f20\u534e\u662d\u56de\u624b\u4e00\u626c\uff0c\u5192\u70f7\u63a5\u6025\u5fd9\u4f38 \u624b\u63a5\u7740\uff0c\u624b\u6307\u4e00\u634f\uff0c\u662f\u4e00\u4e2a\u5c0f\u5c0f\u7684\u7eb8\u56e2\u3002 \u3000\u3000\u5192\u6d63\u83b2\u628a\u7eb8\u6253\u5f00\uff0c\u53ea\u89c9\u4e00\u9635\u5e7d\u9999\u6251\u9f3b\uff0c\u4e0a\u9762\u5199\u7740\u201c\u4eca\u591c\u8bf7\u5230\u5929\u51e4\u697c\u201d\u51e0\u4e2a\u5c0f\u5b57\uff0c\u8272\u6cfd\u6de1 \u7ea2\uff0c\u7eb8\u4e0a\u8fd8\u6709\u4e00\u4e24\u7247\u63c9\u788e\u4e86\u7684\u82b1\u74e3\u3002\u4e0d\u89c9\u5fc3\u4e2d\u81ea\u7b11\uff1a\u201c\u5f20\u534e\u662d\u548c\u7eb3\u5170\u516c\u5f00\u540c\u5728\u4e00\u8d77\uff0c\u5c45\u7136\u6c89 \u8ff7\u5f97\u5982\u6b64\u98ce\u96c5\uff0c\u4ee5\u6307\u7532\u4f5c\u7b14\uff0c\u4ee5\u82b1\u6c41\u4f5c\u58a8\uff0c\u548c\u6211\u6697\u901a\u6d88\u606f\u4e86\u3002\u201d\u5979\u4e00\u9762\u7b11\uff0c\u4e00\u9762\u4f69\u670d\u5f20\u534e\u662d \u5fc3\u601d\u7075\u654f\u3002\u5bf9\u5955\u4e4b\u65f6\uff0c\u65f6\u6709\u843d\u82b1\u98d8\u4e0b\uff0c\u5f53\u65f6\u89c1\u4ed6\u7528\u82b1\u74e3\u73a9\u800d\uff0c\u6beb\u4e0d\u5728\u610f\uff0c\u5374\u6599\u4e0d\u5230\u4ed6\u5df2\u770b\u51fa \u81ea\u5df1\u662f\u540c\u9053\u4e2d\u4eba\uff0c\u7528\u6b64\u6765\u4e66\u5199\u6587\u5b57\uff0c\u51fa\u624b\u4e4b\u5feb\uff0c\u4ee4\u4eba\u5403\u60ca\uff0c\u4e0d\u4f46\u7792\u8fc7\u4e86\u7eb3\u5170\u516c\u5b50\uff0c\u8fde\u81ea\u5df1\u4e5f \u4e0d\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u65f6\u5019\u5199\u7684\u3002 \u3000\u3000\u5192\u6d63\u83b2\u76ee\u9001\u7eb3\u5170\u5bb9\u82e5\u548c\u5f20\u534e\u662d\u4e8c\u4eba\uff0c\u5728\u5bb6\u4e01\u548c\u4e2b\u9b1f\u7c07\u62e5\u4e4b\u4e2d\uff0c\u4ece\u4fa7\u95e8\u8d70\u56de\u5927\u9662\u3002\u5979\u4e5f\u7f13 \u7f13\u800c\u884c\uff0c\u4ece\u539f\u8def\u8d70\u56de\uff0c\u53bb\u627e\u6842\u4ef2\u660e\u3002\u53ea\u89c9\u8def\u4e0a\u78b0\u89c1\u7684\u4eba\uff0c\u4f3c\u4e4e\u90fd\u5728\u7528\u7740\u60ca\u5f02\u7684\u76ee\u5149\u6ce8\u89c6\u81ea \u5df1\u3002 \u3000\u3000\u7ed5\u8fc7\u5047\u5c71\uff0c\u7a7f\u8fc7\u82b1\u5f84\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u89c1\u6842\u4ef2\u660e\u548c\u56ed\u4e2d\u7684\u82b1\u5de5\u8fce\u9762\u8d70\u6765\uff0c\u5192\u6d63\u83b2\u53eb\u4ed6\u4e00\u58f0\uff0c \u6842\u4ef2\u660e\u5374\u628a\u5934\u522b\u8fc7\u4e00\u8fb9\uff0c\u4e0d\u7406\u4e0d\u776c\u3002\u82b1\u5de5\u6beb\u4e0d\u77e5\u8da3\uff0c\u5728\u65c1\u8fb9\u55e6\u55e6\u53e8\u53e8\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u540c\u4f34 \u8981\u53d1\u8ff9\u4e86\uff0c\u6211\u4eec\u7684\u516c\u5b50\u5440\uff0c\u4ec0\u4e48\u5927\u5b98\u6765\u62dc\u8bbf\u4ed6\uff0c\u4ed6\u90fd\u61d2\u5f97\u53bb\u89c1\uff0c\u504f\u504f\u5bf9\u4f60\u7684\u540c\u4f34\u8981\u597d\u5f97\u7d27\uff0c \u62c9\u4ed6\u7684\u624b\u5728\u56ed\u5b50\u91cc\u8d70\u4e86\u597d\u5927\u4e00\u6bb5\u8def\u3002\u8001\u54e5\u6211\u770b\u4f60\u4e5f\u8981\u8ddf\u7740\u5f97\u610f\u4e86\uff0c\u6709\u4ec0\u4e48\u597d\u5904\uff0c\u53ef\u522b\u5fd8\u4e86\u8001 \u670b\u53cb\u554a\uff01\u201d\u6842\u4ef2\u660e\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u80a9\u5934\u4e00\u8038\uff0c\u82b1\u5de5\u6b63\u642d\u624b\u4e0a\u6765\uff0c\u5ffd\u7136\uff0c\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u8dcc\u5012 \u5730\u4e0a\u3002\u6842\u4ef2\u660e\u8f6c\u8eab\u4fbf\u8dd1\uff0c\u5192\u6d63\u83b2\u98de\u6b65\u6025\u8d76\uff0c\u5c16\u58f0\u547c\u5524\u3002 \u3000\u3000\u6842\u4ef2\u660e\u53f9\u4e86\u53e3\u6c14\uff0c\u56de\u5934\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u8ffd\u6211\u4f5c\u4ec0\u4e48\uff1f\u201d\u5192\u6d63\u83b2\u53c8\u6c14\u53c8\u607c\u53c8\u597d\u7b11\uff0c\u62c9\u7740\u4ed6\u7684 \u624b\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u5440\uff0c\u5c31\u50cf\u4f60\u7684\u7236\u4eb2\uff0c\u4f60\u5fd8\u8bb0\u6211\u662f\u7537\u5b50\u6253\u626e\u4e86\u5417\uff1f\u4ed6\u8981\u62c9\u6211\u7684\u624b\uff0c\u96be\u9053\u6211\u4e5f \u8981\u50cf\u4f60\u6454\u82b1\u5de5\u4e00\u6837\uff0c\u628a\u4ed6\u6454\u4e2a\u534a\u6b7b\uff1f\u201d\u6842\u4ef2\u660e\u542c\u5979\u8bf4\u5230\u201c\u5c31\u50cf\u4f60\u7684\u7236\u4eb2\u201d\u8fd9\u53e5\u8bdd\u65f6\uff0c\u5982\u4e2d\u5de8 \u68d2\uff0c\u60f3\u8d77\u81ea\u5df1\u7236\u4eb2\u56e0\u8bef\u4f1a\u800c\u8feb\u6b7b\u517b\u7236\u3001\u62c6\u6563\u5bb6\u5ead\u7684\u4e8b\uff0c\u7acb\u65f6\u6124\u706b\u5168\u6d88\uff0c\u4f46\u4ecd\u7ef7\u7740\u8138\u8bf4\u9053\uff1a \u201c\u6211\u5c31\u662f\u4e0d\u9ad8\u5174\u4f60\u548c\u8fd9\u79cd\u5c11\u7237\u4eb2\u70ed\uff01\u201d\u5192\u6d63\u83b2\u76c8\u76c8\u4e00\u7b11\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u4ed6\u662f\u54ea\u4e00\u79cd\u5c11 \u7237\uff1f\u4ed6\u8fd9\u79cd\u5c11\u7237\u53ef\u4e0e\u522b\u7684\u5c11\u7237\u4e0d\u540c\u3002\u201d\u8bf4\u7f62\u628a\u7eb3\u5170\u5bb9\u82e5\u7684\u884c\u5f84\u80f8\u895f\uff0c\u7ec6\u7ec6\u5bf9\u6842\u4ef2\u660e\u5256\u89e3\u3002\u6842 \u4ef2\u660e\u542c\u5f97\u8fde\u8fde\u70b9\u5934\uff0c\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u5192\u6d63\u83b2\u5f85\u6842\u4ef2\u660e\u5b8c\u5168\u5e73\u9759\u4e4b\u540e\uff0c\u95ee\u4ed6\u9053\uff1a\u201c\u4f60\u662f\u7279\u5730\u6765\u627e\u6211\u7684\u5417\uff1f\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u9646\u660e \u9646\u4eae\u4eca\u65e5\u4ece\u76f8\u5e9c\u90a3\u8fb9\u8fc7\u6765\uff0c\u6211\u6b63\u5728\u76d1\u5de5\uff0c\u4ed6\u62c9\u7740\u6211\u5bf9\u6211\u8bf4\uff0c\u6628\u665a\u4ed6\u4eec\u8f6e\u503c\uff0c\u5ffd\u7136\u53d1\u73b0\u6b66\u6797\u9ad8 \u624b\u4ece\u56db\u5e9c\u4e00\u5ea7\u697c\u9876\u4e00\u63a0\u800c\u8fc7\uff0c\u53ea\u770b\u90a3\u8eab\u8f7b\u529f\uff0c\u5c31\u6bd4\u4ed6\u4eec\u9ad8\u660e\u5f97\u4e0d\u5374\u591a\u5c11\u500d\uff0c\u4ed6\u4eec\u4e0d\u6562\u8ffd\u8d76\uff0c \u60f3\u8bf7\u6211\u4eec\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\uff0c\u8fd9\u51e0\u665a\u7ed9\u4ed6\u4eec\u5de1\u89c6\u95e8\u6237\u3002\u4f60\u4e0d\u5728\u8eab\u8fb9\uff0c\u6211\u62ff\u4e0d\u5b9a\u4e3b\u610f\u3002\u4f60\u8bf4\u6211\u4eec\u72af \u4e0d\u72af\u5f97\u7740\u771f\u7684\u7ed9\u4ed6\u4eec\u505a\u770b\u95e8\u3002\u201d\u5192\u6d63\u83b2\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u7b54\u5e94\u4ed6\u4eec\u5427\u3002\u6211\u4eec\u867d\u4e0d\u662f\u66ff\u76f8\u5e9c \u770b\u95e8\uff0c\u4e5f\u8981\u4f1a\u4f1a\u8fd9\u4f4d\u6b66\u6797\u9ad8\u624b\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u90a3\u4e2a\u82b1\u5de5\u5df2\u4ece\u5730\u4e0a\u722c\u8d77\uff0c\u8d70\u4e86\u8fc7\u6765\u3002\u5192\u6d63\u83b2\u9053\u4e2a\u6b49\u8fce\u4e0a\u53bb\u95ee\u9053\uff1a\u201c\u5929\u51e4\u697c\u662f \u4e0d\u662f\u5728\u897f\u9662\u3002\u201d \u3000\u3000\u82b1\u5de5\u70b9\u5934\u9053\uff1a\u201c\u6b63\u662f\u5728\u897f\u9662\uff0c\u90a3\u662f\u7eb3\u5170\u516c\u5b50\u7684\u4e66\u623f\u3002\u201d\u4ed6\u7741\u5927\u773c\u775b\uff0c\u77a7\u4e86\u77a7\u5192\u6d63\u83b2\uff0c\u5ffd \u7136\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u516c\u5b50\u53eb\u4f60\u5230\u5929\u51e4\u697c\u5f53\u5dee\uff1f\u90a3\u53ef\u662f\u6700\u597d\u7684\u5dee\u4e8b\uff01\u201d\u5192\u6d63\u83b2\u7b11\u800c\u4e0d\u7b54\uff0c\u8c22 \u8fc7\u82b1\u7389\u62c9\u7740\u6842\u4ef2\u660e\u5404\u81ea\u56de\u623f\u4f11\u606f\uff0c\u51c6\u5907\u517b\u597d\u7cbe\u795e\uff0c\u591c\u63a2\u5929\u51e4\u697c\uff0c\u8bbf\u5bfb\u5f20\u534e\u662d\u3002 \u3000\u3000\u4e24\u4eba\u7761\u4e86\u4e2a\u5348\u89c9\uff0c\u518d\u51fa\u6765\u65f6\uff0c\u53ea\u89c1\u56ed\u4e2d\u9999\u54bd\u6f66\u7ed5\uff0c\u82b1\u5f71\u7f24\u7eb7\uff0c\u6240\u6709\u4e0d\u662f\u5e94\u8282\u5f00\u82b1\u7684\u6811\uff0c \u867d\u65e0\u82b1\u53f6\uff0c\u4e5f\u7528\u5404\u8272\u7ef8\u7f15\u7eb8\u7ee2\u53ca\u901a\u8349\u4e3a\u82b1\uff0c\u7c98\u4e8e\u679d\u4e0a\uff0c\u771f\u662f\u4e2a\u82b1\u56e2\u9526\u7c07\u3001\u5bcc\u4e3d\u5f02\u5e38\u3002\u5192\u6d63\u83b2 \u62c9\u7740\u4e00\u4e2a\u5c0f\u53ae\u95ee\u9053\uff1a\u201c\u600e\u7684\u4eca\u5929\u56ed\u5b50\u91cc\u5e03\u7f6e\u5f97\u8fd9\u6837\u534e\u7f8e\uff1f\u201d\u90a3\u5c0f\u53ae\u4f38\u4f38\u820c\u5934\u9053\uff1a\u201c\u4e2d\u5348\u65f6 \u5206\uff0c\u4e09\u516c\u4e3b\u9a7e\u5230\uff0c\u4f60\u90fd\u4e0d\u5982\u9053\u5417\uff1f\u4f60\u51fa\u56ed\u770b\u770b\uff0c\u90a3\u92ae\u8206\u8f66\u4ed7\uff0c\u6392\u5f97\u591a\u957f\uff1f\u4e09\u516c\u4e3b\u548c\u6211\u4eec\u7684\u76f8 \u56fd\u592b\u4eba\uff0c\u4ea4\u60c5\u6700\u597d\uff0c\u4ee5\u524d\u6bcf\u4e2a\u6708\u90fd\u8981\u6765\u4e00\u4e24\u6b21\uff0c\u4e00\u4f4f\u5c31\u662f\u51e0\u5929\u3002\u8fd9\u6b21\u4e0d\u77e5\u600e\u7684\uff0c\u9694\u4e86\u597d\u51e0\u4e2a \u6708\u624d\u6765\u3002\u201d\u5192\u6d63\u83b2\u542c\u540e\uff0c\u60f3\u8d77\u65e9\u4e0a\u7eb3\u5170\u516c\u5b50\u88ab\u592b\u4eba\u5306\u5306\u53ec\u53bb\u4e4b\u4e8b\uff0c\u5927\u7ea6\u662f\u548c\u4e09\u516c\u4e3b\u4e4b\u6765\u6709\u5173 \u4e86\u3002 \u3000\u3000\u5230\u4e86\u665a\u4e0a\uff0c\u56ed\u5b50\u91cc\u7684\u666f\u8272\u66f4\u7f8e\uff0c\u5c0f\u6cb3\u4e24\u5cb8\u7684\u77f3\u680f\uff0c\u6302\u6ee1\u8bb8\u591a\u6c34\u6676\u73bb\u7483\u7684\u5404\u8272\u98ce\u706f\uff0c\u70b9\u5f97 \u5982\u94f6\u82b1\u96ea\u6d6a\uff1b\u7eff\u6811\u679d\u5934\uff0c\u53c8\u904d\u7f00\u6c34\u6676\u8461\u8404\uff0c\u4f5c\u4e3a\u88c5\u9970\uff0c\u4e0a\u4e0b\u4e89\u8f89\uff0c\u6c34\u65e0\u7115\u5f69\uff0c\u628a\u56ed\u5b50\u88c5\u70b9\u5f97 \u4f3c\u73bb\u7483\u4e16\u754c\uff0c\u73e0\u5b9d\u4e7e\u5764\u3002\u6842\u5192\u4e8c\u4eba\uff0c\u5374\u662f\u65e0\u5fc3\u9274\u8d4f\uff0c\u542c\u5f97\u6253\u8fc7\u4e09\u66f4\uff0c\u5404\u5904\u6c89\u5bc2\u4e4b\u540e\uff0c\u4e24\u4eba\u6362 \u8fc7\u4e86\u4e00\u8eab\u9ed1\u8272\u7684\u591c\u884c\u8863\uff0c\u5c55\u5f00\u7edd\u9876\u8f7b\u529f\uff0c\u8ff3\u81ea\u6251\u5954\u897f\u9662\uff0c\u627e\u4e86\u8bb8\u4e45\uff0c\u624d\u5728\u79bb\u96d5\u680f\u7389\u780c\u7684\u91cd\u91cd \u9662\u843d\u4e4b\u95f4\uff0c\u770b\u5230\u53e4\u69d0\u6811\u836b\u4e0b\uff0c\u7ea2\u697c\u63a9\u6620\uff0c\u4e0a\u9762\u5f69\u7eb1\u5bab\u706f\uff0c\u7f00\u6210\u201c\u5927\u98ce\u6a31\u201d\u4e09\u5b57\u3002\u5192\u6d63\u83b2\u5927 \u559c\uff0c\u5bf9\u6842\u4ef2\u660e\u9053\uff1a\u201c\u4f60\u5728\u5916\u9762\u5de1\u903b\uff0c\u6211\u8fdb\u53bb\u63a2\u5f20\u516c\u5b50\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u98d8\u8eab\u800c\u4e0a\uff0c\u5728\u6bcf\u4e00\u5c42\u697c\u7fd8\u51fa\u6765\u7684\u6a90\u89d2\uff0c\u90fd\u505c\u4e86\u4e00\u4e0b\uff0c\u5f20\u671b\u8fdb\u53bb\uff0c\u5374\u662f\u5947\u602a\uff0c\u697c\u623f \u90fd\u662f\u7a7a\u65e0\u4e00\u4eba\uff0c\u76f4\u4e0a\u5230\u9876\u697c\uff0c\u65b9\u59cb\u542c\u89c1\u5973\u5b50\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u58f0\u8c03\u5341\u5206\u5e7d\u6028\u3002 \u3000\u3000\u5192\u6d63\u83b2\u8d34\u8033\u5728\u7eb1\u7a97\u4e0a\uff0c\u53ea\u542c\u90a3\u5973\u5b50\u8bf4\u9053\uff1a\u201c\u4eba\u4eec\u90fd\u7fa1\u6155\u8363\u534e\uff0c\u5e1d\u738b\u4e4b\u5bb6\u662f\u8363\u534e\u6781\u81f4\u3002\u6211 \u5374\u53ea\u77e5\u9053\uff1a\u6df1\u5bab\u5982\u9b3c\u57df\uff0c\u5ea6\u65e5\u4f3c\u957f\u5e74\u3002\u6211\u8fd8\u7b97\u8f83\u597d\u7684\u4e86\uff0c\u5bb9\u82e5\u81ea\u5c0f\u548c\u6211\u73a9\u5f97\u6765\uff0c\u540e\u6765\u53c8\u548c\u4f60 \u8ba4\u8bc6\uff0c\u4f60\u4eec\u50cf\u4e00\u80a1\u6e05\u98ce\uff0c\u7ed9\u6211\u63ed\u5f00\u6df1\u5bab\u7684\u5e18\u5e55\uff0c\u770b\u5230\u4e00\u70b9\u70b9\u5916\u5728\u7684\u9633\u5149\u3002\u6211\u7684\u59d0\u59b9\uff0c\u5979\u4eec\u66f4 \u60e8\u3002\u540d\u4e3a\u516c\u4e3b\uff0c\u5982\u53d7\u5236\u4e8e\u4fdd\u59c6\uff0c\u83ab\u8bf4\u7236\u738b\u4e0d\u6613\u89c1\uff0c\u5c31\u662f\u5ac1\u51fa\u4e4b\u540e\uff0c\u4e00\u751f\u89c1\u4e0d\u7740\u9644\u9a6c\uff0c\u4e5f\u5c5e\u5bfb \u5e38\u3002\u5f20\u516c\u5b50\uff0c\u4f60\u5c31\u4e00\u70b9\u4e5f\u4e0d\u53ef\u601c\u6211\u5417\uff1f\u201d\u5192\u6d63\u83b2\u542c\u5f97\u5927\u60ca\uff0c\u6084\u6084\u7528\u6307\u5728\u7eb1\u7a97\u6316\u4e86\u4e00\u4e2a\u5c0f\u6d1e\uff0c \u5f20\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u58c1\u9762\u5750\u7740\u4e00\u4f4d\u65d7\u88c5\u5c11\u5973\uff0c\u7f8e\u8273\u7edd\u4fd7\uff0c\u6c14\u5ea6\u9ad8\u534e\u3002\u5bf9\u9762\u7ad9\u7740\u7684\u82f1\u4fca\u5c11\u5e74\uff0c\u6b63\u662f \u65e5\u95f4\u6240\u89c1\u7684\u5f20\u534e\u662d\u3002\u5fc3\u60f3\uff1a\u83ab\u975e\u6b64\u5973\u5c31\u662f\u4ec0\u4e48\u738b\u516c\uff1f\u600e\u7684\u5979\u4f1a\u548c\u5f20\u534e\u662d\u8fd9\u6837\u53ae\u719f\uff0c\u6df1\u66f4\u65f6 \u5206\uff0c\u5728\u9ad8\u697c\u4e4b\u4e0a\u8c08\u5fc3\uff1f\u6b63\u7591\u60d1\u95f4\uff0c\u5f20\u534e\u662d\u4f4e\u4f4e\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d\u505c\u4e86\u4e00\u4e0b\uff0c \u5ffd\u7136\u80cc\u7740\u516c\u4e3b\u628a\u624b\u4e00\u626c\uff0c\u4e00\u4e2a\u5c0f\u7eb8\u56e2\uff0c\u6070\u6070\u7a7f\u8fc7\u7eb1\u7a97\u4e0a\u7684\u5c0f\u5b54\u98de\u51fa\u3002\u5192\u6d63\u83b2\u63a5\u8fc7\uff0c\u6253\u5f00\u4e00 \u770b\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u9053\uff1a\u201c\u8fc7\u4e00\u4f1a\u518d\u6765\uff01\u201d\u6b63\u5f53\u6b64\u9645\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u4e00\u58f0\u6e05\u5578\u3002\u6b63\u662f\uff1a \u3000\u3000\u6df1\u9662\u95fb\u79c1\u8bed\uff0c\u4e2d\u5bb5\u4f20\u602a\u58f0\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5341\u56db\u7ae0\u3000\u57cb\u6068\u6df1\u5bab\u3000\u82b1\u8fce\u5251\u4f69\u661f\u521d\u843d\u3000\u626c\u5a01\u4e09\u5ce1\u3000\u67f3\u62c2\u65cc\u65d7\u9732\u672a\u5e72 \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u5341\u56db\u56de \u57cb\u6068\u6df1\u5bab\u3000\u82b1\u8fce\u5251\u4f69\u661f\u521d\u843d \u626c\u5a01\u4e09\u5ce1\u3000\u67f3\u62c2\u65cc\u65d7\u9732\u672a\u5e72 \u3000\u3000\u5578\u58f0\u4e2d\uff0c\u53ea\u89c1\u524d\u9762\u7684\u4e00\u5ea7\u77f3\u5c71\u4e0a\uff0c\u6709\u4e2a\u4eba\u5f71\u4e00\u95ea\uff0c\u6ca1\u5165\u85e4\u841d\u5f02\u8349\u4e4b\u95f4\u3002\u6842\u4ef2\u660e\u5927\u5403\u4e00 \u60ca\uff0c\u8fd9\u4eba\u8eab\u6cd5\u597d\u5feb\uff01\u4ed6\u6043\u7740\u827a\u9ad8\u80c6\u5927\uff0c\u4e0d\u987e\u654c\u660e\u5df1\u6697\uff0c\u5237\u5237\u5237\uff0c\u4e09\u8d77\u4e09\u843d\uff0c\u5f84\u4ee5\u98de\u9e1f\u6295\u6797\u4e4b \u52bf\uff0c\u8dc3\u4e0a\u77f3\u5c71\uff0c\u5de6\u638c\u62a4\u80f8\uff0c\u53f3\u638c\u5e94\u654c\uff0c\u55d6\u7684\u4e00\u58f0\uff0c\u63a2\u8eab\u5165\u85e4\u841d\u4e4b\u4e2d\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u85e4\u841d\u4e2d\u4e00\u58f0\u51b7\u7b11\uff0c\u5bd2\u98ce\u6251\u9762\uff0c\u6842\u4ef2\u660e\u4f55\u7b49\u673a\u7075\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u556a\u7684\u4e00\u638c \u6253\u53bb\uff0c\u90a3\u4eba\u4e00\u51fb\u4e0d\u4e2d\uff0c\u77ed\u5251\u987a\u52bf\u4e00\u65cb\uff0c\u5411\u4e0a\u622a\u65a9\uff0c\u6842\u4ef2\u660e\u8fd9\u4e00\u638c\u539f\u53ef\u51fb\u4e2d\u5bf9\u65b9\uff0c\u4f46\u5bf9\u65b9\u5251\u62db \u4e5f\u662f\u8fc5\u901f\u4e4b\u6781\uff0c\u82e5\u4e0d\u8eb2\u907f\uff0c\u7eb5\u51fb\u4f24\u5bf9\u65b9\uff0c\u81ea\u5df1\u624b\u8155\u4e5f\u5b9a\u88ab\u622a\u65ad\u3002\u6842\u4ef2\u660e\u6025\u7528\u53f3\u638c\u4e00\u6321\uff0c\u62a2\u5148 \u4e00\u6b65\u8fc7\u53bb\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u8863\u8896\u4e2d\u4e86\u4e00\u5251\uff0c\u6842\u4ef2\u660e\u5927\u6012\uff0c\u8fd0\u5927\u529b\u9e70\u722a\u795e\u529f\uff0c\u4f38\u5f00\u5341\u6307\uff0c\u5f53\u5934 \u6293\u53bb\uff0c\u8fde\u53d1\u4e09\u62db\u8fa3\u62db\u3002\u5bf9\u65b9\u95ea\u5c55\u817e\u632a\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u8fde\u653b\u4e0b\u4e94\u5251\uff0c\u6bcf\u4e00\u5251\u90fd\u662f\u523a\u5411\u6842\u4ef2\u660e\u8981 \u5bb3\uff0c\u6842\u4ef2\u660e\u7a7a\u624b\u535a\u5251\uff0c\u867d\u7136\u672a\u81f3\u5403\u4e8f\uff0c\u5374\u4e5f\u5360\u4e0d\u4e86\u4fbf\u5b9c\u3002 \u3000\u3000\u90a3\u4eba\u4f3c\u4e0d\u604b\u6218\uff0c\u4e0d\u5230\u5341\u62db\uff0c\u4fbf\u594b\u8eab\u4e00\u8dc3\uff0c\u8dc3\u51fa\u8349\u4e1b\uff0c\u8dc3\u4e0a\u77f3\u5c71\uff0c\u6842\u4ef2\u660e\u54ea\u91cc\u80af\u820d\uff0c\u6d41\u661f \u63a3\u7535\u822c\u8854\u5c3e\u76f4\u8ffd\u3002\u8ffd\u5230\u5929\u51e4\u697c\u65f6\uff0c\u90a3\u4eba\u500f\u5730\u8f6c\u8eab\uff0c\u77ed\u5251\u4e00\u7acb\u3002\u706f\u5149\u95ea\u9655\u4e0b\uff0c\u6842\u4ef2\u660e\u53ea\u89c1\u5bf9\u65b9 \u8eab\u6750\u7626\u5c0f\uff0c\u8499\u7740\u9762\u5e55\uff0c\u53ea\u9732\u51fa\u4e24\u9897\u6ef4\u6e9c\u6e9c\u7684\u773c\u73e0\uff0c\u4f3c\u4e4e\u662f\u4e2a\u5973\u5b50\u3002\u4ed6\u5fc3\u91cc\u6b63\u5728\u6000\u7591\uff0c\u90a3\u4eba\u4f4e \u9a82\u4e00\u58f0\uff1a\u201c\u4e8f\u4f60\u8fd9\u6837\u8eab\u624b\uff0c\u7adf\u7136\u662f\u4e2a\u9e70\u722a\u5b59\u3002\u201d\u77ed\u5251\u4e00\u6296\uff0c\u6d51\u8eab\u4e0a\u4e0b\uff0c\u5377\u8d77\u51e0\u9053\u5251\u5149\uff0c\u7cbe\u8292 \u51b7\u7535\uff0c\u7f24\u7eb7\u98de\u821e\uff0c\u75be\u653b\u800c\u4e0a\u3002 \u3000\u3000\u6842\u4ef2\u660e\u542c\u5979\u58f0\u97f3\u6e05\u8106\uff0c\u751a\u4f3c\u5973\u58f0\uff0c\u65b9\u6b32\u559d\u95ee\uff0c\u5df2\u88ab\u731b\u653b\u3002\u8fd9\u56de\u4ed6\u4e0d\u6562\u7a7a\u624b\u5e94\u654c\uff0c\u6258\u5730\u5f80 \u540e\u4e00\u8dc3\uff0c\u624b\u5728\u8170\u95f4\u4e00\u6309\uff0c\u817e\u86df\u5251\u4f3c\u98de\u86c7\u822c\u76f4\u5410\u51fa\u53bb\uff0c\u90a3\u4eba\u731b\u89c1\u4e00\u9053\u94f6\u8679\u75be\u5c04\u9762\u95e8\uff0c\u5fae\u201c\u54a6\u201d \u4e00\u58f0\uff0c\u8eab\u968f\u5251\u8f6c\uff0c\u6025\u8d70\u504f\u950b\uff0c\u5c55\u5f00\u7cbe\u5947\u62db\u6570\uff0c\u8f6c\u653b\u6842\u4ef2\u660e\u4e24\u80c1\u3002 \u3000\u3000\u6842\u4ef2\u660e\u7684\u4e94\u79bd\u5251\u6cd5\uff0c\u672c\u4ee5\u8fc5\u6377\u89c1\u957f\uff0c\u4e0d\u6599\u5bf9\u65b9\u7684\u5251\u6cd5\u66f4\u4e3a\u8fc5\u6377\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u6253\u4e86 \u4e09\u4e94\u5341\u62db\uff0c\u90fd\u662f\u4e00\u6cbe\u5373\u8d70\uff0c\u4e24\u5251\u4ece\u4e0d\u76f8\u4ea4\u3002\u6842\u4ef2\u660e\u8d8a\u6253\u8d8a\u5947\uff0c\u8fd9\u4eba\u7684\u5251\u6cd5\u975e\u5e38\u4e4b\u4f3c\u51cc\u672a\u98ce\u7684 \u5929\u5c71\u5251\u6cd5\uff0c\u53d8\u5316\u7e41\u590d\uff0c\u63ba\u6742\u6709\u5404\u79cd\u5bb6\u6570\uff0c\u82e5\u4e0d\u662f\u4ed6\u89c1\u8fc7\u51cc\u672a\u98ce\u5251\u6cd5\uff0c\u51e0\u4e4e\u62b5\u6321\u4e0d\u4f4f\uff01\u4f46\u4ed6\u4e5f \u66fe\u542c\u5f97\u51cc\u672a\u98ce\u8bf4\u8fc7\uff1a\u6666\u660e\u7985\u5e08\u7684\u5929\u5c71\u5251\u6cd5\uff0c\u751f\u5e73\u53ea\u4f20\u8fc7\u4e09\u4e2a\u4eba\uff0c\u4e00\u4e2a\u662f\u4e8c\u5341\u591a\u5e74\u7684\u540d\u9707\u6c5f\u6e56 \u7684\u6768\u4e91\u9a86\uff0c\u6b64\u4eba\u5341\u516b\u5e74\u524d\u5728\u676d\u5dde\u79bb\u5947\u6bd9\u547d\u3002\u5c1a\u6709\u4e24\u4eba\uff0c\u4e00\u4e2a\u662f\u5df2\u6295\u4e86\u6e05\u5ef7\u7684\u6e38\u9f99\u5251\u695a\u662d\u5357\uff0c \u4e00\u4e2a\u5c31\u662f\u4ed6\uff0c\u90a3\u4e48\u8fd9\u4e2a\u7626\u524a\u8eab\u6750\u7684\u4eba\uff0c\u7a76\u7adf\u662f\u4ece\u4f55\u5904\u5b66\u6765\u7684\u5929\u5c71\u5251\u6cd5\uff1f \u3000\u3000\u6b64\u4eba\u5251\u6cd5\u662f\u7cbe\u5947\u6781\u4e86\uff0c\u53ea\u662f\u529f\u592b\u5374\u900a\u6842\u4ef2\u660e\u4e00\u7b79\uff0c\u6597\u4e86\u7247\u523b\uff0c\u989d\u4e0a\u89c1\u6c57\uff0c\u6842\u4ef2\u660e\u89d1\u4e2a\u771f \u5207\uff0c\u624b\u8155\u500f\u7ffb\uff0c\u786c\u78d5\u5bf9\u65b9\u7684\u5251\uff0c\u53ea\u542c\u5f97\u5f53\u7684\u4e00\u58f0\uff0c\u90a3\u4eba\u7684\u5251\u7ed9\u78d5\u4e0a\u534a\u7a7a\uff0c\u6025\u5fd9\u5012\u7eb5\u51fa\u53bb\uff0c\u8ffd \u63a5\u90a3\u88ab\u78d5\u98de\u7684\u77ed\u5251\u3002\u6842\u4ef2\u660e\u5c06\u817e\u86df\u5251\u5377\u6210\u4e00\u56e2\uff0c\u4e5f\u4e0d\u8feb\u8d76\u3002\u53ea\u89c1\u90a3\u4eba\u63a5\u5230\u88ab\u78d5\u98de\u7684\u77ed\u5251\uff0c\u5728 \u706f\u5149\u4e0b\u7ec6\u770b\uff0c\u6ee1\u9762\u7591\u60d1\u4e4b\u5bb9\u3002\u539f\u6765\u90a3\u4eba\u7684\u77ed\u5251\u4e5f\u662f\u628a\u5b9d\u5251\uff0c\u5979\u63a5\u4e86\u4e00\u770b\uff0c\u53ea\u89c1\u5251\u950b\u6709\u4e00\u4e2a\u5c0f \u5c0f\u7684\u7f3a\u53e3\uff0c\u5206\u660e\u662f\u7ed9\u6842\u4ef2\u660e\u7684\u5251\u6240\u635f\u4f24\u7684\uff0c\u54ea\u5f97\u4e0d\u60ca\u3002\u800c\u6842\u4ef2\u660e\u7684\u817e\u86df\u5251\uff0c\u81ea\u4f7f\u7528\u4ee5\u6765\uff0c\u5df2 \u4e0d\u77e5\u622a\u65ad\u8fc7\u591a\u5c11\u5175\u5668\uff0c\u5982\u4eca\u7528\u4e86\u5341\u6210\u529b\u91cf\uff0c\u6ee1\u62df\u628a\u5b83\u622a\u4e3a\u4e24\u6bb5\uff0c\u4e0d\u6599\u89c1\u5bf9\u65b9\u63a5\u4e86\u4e0b\u6765\uff0c\u7ec6\u7ec6 \u628a\u73a9\uff0c\u7adf\u4f3c\u6beb\u65e0\u4f24\u635f\uff0c\u4e5f\u662f\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u6842\u4ef2\u660e\u6ee1\u8179\u72d0\u7591\uff0c\u4e0a\u524d\u95ee\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u4ec0\u4e48\u4eba\uff1f\u4f60\u8ba4\u8bc6\u51cc\u672a\u98ce\u5417\uff1f\u201d\u90a3\u4eba\u84e6\u5730\u56de\u5934\uff0c \u8be7\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u51cc\u672a\u98ce\uff1f\u201d\u2026\u201d\u5c1a\u672a\u8bf4\u5b8c\uff0c\u5ffd\u7136\u5c71\u5773\u5904\u75be\u7684\u53c8\u98de\u63a0\u51fa\u4e24\u6761\u4eba\u5f71\uff0c\u5f53\u524d\u4e00 \u4eba\uff0c\u624b\u6301\u7740\u4e00\u628a\u5bd2\u5149\u95ea\u95ea\u7684\u957f\u5251\uff0c\u521a\u4e00\u73b0\u8eab\uff0c\u4fbf\u8fde\u58f0\u72de\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u597d\u5927\u80c6\u7684\u5973\u98de\u8d3c\uff0c\u7adf\u7136 \u95ef\u8fdb\u76f8\u5e9c\u6765\u4e86\uff01\u201d\u6842\u4ef2\u660e\u5fc3\u60f3\uff1a\u201c\u679c\u7136\u662f\u4e2a\u5973\u7684\u3002\u201d \u3000\u3000\u90a3\u4eba\u957f\u5251\u4e00\u62e6\uff0c\u5c01\u7740\u4e86\u201c\u5973\u8d3c\u201d\u7684\u53bb\u8def\uff0c\u53e6\u4e00\u4eba\u4fa7\u8fb9\u7a9c\u4e0a\uff0c\u62db\u547c\u6842\u4ef2\u660e\u9053\uff1a\u201c\u4f60\u662f\u76f8\u5e9c \u7684\u536b\u58eb\uff1f\u597d\u529f\u592b\uff0c\u4f60\u5e2e\u6211\u4eec\u628a\u5973\u8d3c\u64d2\u4f4f\uff0c\u8fd9\u662f\u5947\u529f\u4e00\u4ef6\u3002\u201d\u6842\u4ef2\u660e\u4e0d\u7406\u4e0d\u776c\uff0c\u53cc\u76ee\u6ce8\u5b9a\u90a3\u4e2a \u201c\u5973\u8d3c\u201d\u3002\u201c\u5973\u8d3c\u201d\u5df2\u548c\u90a3\u4eba\u4ea4\u4e0a\u4e86\u624b\uff0c\u53ea\u542c\u5f97\u53ee\u5f53\u51e0\u58f0\uff0c\u4e24\u4eba\u5404\u81ea\u9000\u540e\u51e0\u6b65\u3002\u4f7f\u957f\u5251\u7684\u51fa \u58f0\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5973\u8d3c\u4ece\u54ea\u91cc\u5077\u5f97\u6211\u5e08\u5144\u9057\u4e0b\u7684\u5b9d\u5251\uff1f\u201d\u201c\u5973\u8d3c\u201d\u4e5f\u9a82\u9053\uff1a\u201c\u4f60\u8fd8\u8bb0\u5f97\u4f60\u7684\u5e08 \u5144\uff1f\u201d\u77ed\u5251\u4e00\u4e3e\uff0c\u4e24\u4eba\u53c8\u6597\u5728\u4e00\u8d77\u3002 \u3000\u3000\u90a3\u4eba\u7684\u957f\u5251\u5207\u4e86\u4e09\u9053\u7f3a\u53e3\u3002\u8fd9\u8fd8\u662f\u4ed6\u5185\u529f\u6df1\u6e5b\uff0c\u4e00\u89c1\u52bf\u5934\u4e0d\u5bf9\uff0c\u4fbf\u7528\u5929\u5c71\u5251\u6cd5\u7684\u201c\u5378\u201d \u5b57\u8bc0\uff0c\u5316\u53bb\u5b9d\u5251\u786c\u524a\u4e4b\u529b\uff0c\u4e0d\u7136\u8fd9\u67c4\u957f\u5251\u771f\u4f1a\u7ed9\u77ed\u5251\u622a\u65ad\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u9000\u590d\u4e0a\uff0c\u518d\u5ea6\u4ea4\u950b\u3002\u90a3\u4f7f\u957f\u5251\u7684\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u6709\u5b9d\u5251\u4e5f\u96be\u5948\u6211\u4f55\u3002\u201d\u5c55\u5f00\u957f \u5251\uff0c\u7fe9\u5982\u60ca\u9e3f\uff0c\u731b\u5982\u96c4\u72ee\uff01\u5251\u6cd5\u548c\u90a3\u201c\u5973\u8d3c\u201d\u867d\u662f\u540c\u4e00\u8def\u6570\uff0c\u5374\u662f\u4e0d\u8fc7\u5341\u62db\uff0c\u4fbf\u628a\u201c\u5973\u8d3c\u201d \u8feb\u5f97\u8fde\u8fde\u540e\u9000\u3002\u6842\u4ef2\u660e\u5927\u5403\u4e00\u60ca\uff0c\u600e\u7684\u4eca\u665a\u78b0\u5230\u7684\u4eba\uff0c\u4e00\u4e2a\u5f3a\u4f3c\u4e00\u4e2a\uff0c\u8fd9\u4eba\u7684\u5251\u6cd5\uff0c\u4e0d\u4f46\u548c \u51cc\u672a\u98ce\u4e00\u6a21\u4e00\u6837\uff0c\u8fde\u529f\u529b\u4e5f\u597d\u4f3c\u5dee\u4e0d\u591a\uff01 \u3000\u3000\u5728\u5929\u51e4\u697c\u4e0a\u7684\u5192\u6d63\u83b2\uff0c\u542c\u5f97\u4e0b\u9762\u7684\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u8fde\u5fd9\u624b\u8db3\u5e76\u7528\uff0c\u843d\u5230\u5730\u4e0a\u3002\u4e00\u770b\u4e4b \u4e0b\uff0c\u5403\u60ca\u975e\u5c0f\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5feb\u4e0a\u53bb\u6551\u90a3\u4e2a\u5973\u5b50\uff0c\u5979\u662f\u6613\u59d0\u59d0\uff01\u201d \u3000\u3000\u8fd9\u201c\u5973\u8d3c\u201d\u6b63\u662f\u6613\u5170\u73e0\uff0c\u6765\u6349\u5979\u7684\u4eba\u5374\u662f\u695a\u662d\u5357\u3002\u5979\u7684\u77ed\u5251\u540d\u4e3a\u201c\u65ad\u7389\u5251\u201d\uff0c\u548c\u695a\u662d\u5357 \u7684\u6e38\u9f99\u5251\u540c\u662f\u6666\u660e\u7985\u5e08\u7684\u9547\u5c71\u4e4b\u5b9d\uff0c\u5f53\u5e74\u6666\u660e\u7985\u5e08\u5c06\u77ed\u5251\u4f20\u7ed9\u6768\u4e91\u9a86\uff0c\u957f\u5251\u4f20\u7ed9\u695a\u662d\u5357\uff0c\u6768 \u4e91\u9a86\u5728\u4e34\u6b7b\u65f6\u5199\u4e0b\u8840\u4e66\uff0c\u5c06\u77ed\u5251\u4e0e\u5973\u5b69\u4ea4\u4e0e\u4e00\u4e2a\u5c11\u5e74\uff0c\u53eb\u4ed6\u5230\u5929\u5c71\u4ee5\u8840\u4e66\u77ed\u5251\u4e3a\u51ed\uff0c\u62dc\u5728\u6666 \u660e\u7985\u5e08\u95e8\u4e0b\uff0c\u90a3\u5c11\u5e74\u662f\u51cc\u672a\u98ce\uff0c\u800c\u90a3\u5973\u7684\u5219\u662f\u4eca\u65e5\u7684\u6613\u5170\u73e0\u3002\u5979\u7ed9\u51cc\u672a\u98ce\u62b1\u4e0a\u5929\u5c71\u65f6\uff0c\u624d\u662f \u4e09\u5c81\u591a\u4e00\u70b9\uff0c\u5979\u7684\u4e00\u8eab\u6b66\u827a\uff0c\u90fd\u662f\u51cc\u672a\u98ce\u4ee3\u6666\u660e\u7985\u5e08\u4f20\u6388\u7684\uff0c\u56e0\u4e3a\u662f\u81ea\u5e7c\u5c31\u5f97\u4e0a\u4e58\u5251\u6cd5\u7684\u771f \u4f20\uff0c\u529f\u592b\u81ea\u662f\u4e0d\u5f31\u3002\u53ea\u662f\u548c\u695a\u662d\u5357\u6842\u4ef2\u660e\u7b49\u4eba\u6bd4\u8d77\u6765\uff0c\u529f\u529b\u5f53\u7136\u8fd8\u662f\u6709\u6240\u4e0d\u5982\u3002 \u3000\u3000\u6613\u5170\u73e0\u654c\u4e0d\u4f4f\u695a\u662d\u5357\u7684\u8fde\u73af\u653b\u51fb\uff0c\u6b63\u5728\u5371\u6025\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u695a\u662d\u5357\u5927\u53eb\u4e00\u58f0\uff0c\u5f80\u540e\u75be\u9000\uff0c \u6613\u5170\u73e0\u53ea\u89c9\u8111\u540e\u751f\u98ce\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u695a\u662d\u5357\u84e6\u5730\u53cc\u624b\u4e00\u626c\uff0c\u4e24\u9053\u94f6\u5149\uff0c\u5df2\u662f\u5411\u5979\u5c04\u6765\uff0c\u6613\u5170\u73e0 \u4e3e\u5251\u6a2a\u524a\uff0c\u201c\u5f53\u5577\u201d\u4e00\u58f0\uff0c\u6389\u5728\u5730\u4e0a\uff0c\u4e00\u770b\u5374\u662f\u4e00\u6bb5\u65ad\u5251\u3002\u8fd9\u51e0\u4e0b\uff0c\u5feb\u5f97\u51fa\u5947\uff0c\u8fde\u6613\u5170\u73e0\u4e5f \u770b\u4e0d\u6e05\u695a\u3002\u62ac\u8d77\u5934\u65f6\uff0c\u5df2\u89c1\u695a\u662d\u5357\u53cc\u624b\u7a7a\u7a7a\uff0c\u548c\u4e00\u4e2a\u6301\u5251\u5c11\u5e74\uff0c\u4e92\u76f8\u6251\u6597\uff0c\u8fd9\u5c11\u5e74\u6b63\u662f\u521a\u624d \u7528\u5b9d\u5251\u6253\u8d25\u81ea\u5df1\u7684\u4eba\u3002 \u3000\u3000\u539f\u6765\u6842\u4ef2\u660e\u6551\u4eba\u5fc3\u5207\uff0c\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u7528\u4e94\u79bd\u5251\u6cd5\u4e2d\u7684\u201c\u4fca\u9e58\u6469\u4e91\u201d\u7edd\u6280\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c \u5728\u534a\u7a7a\u4e00\u4e2a\u5012\u7ffb\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u4fbf\u5411\u695a\u662d\u5357\u51b2\u6765\u3002\u6613\u5170\u73e0\u80cc\u5411\u6842\u4ef2\u660e\uff0c\u56e0\u6b64\u53ea\u89c9\u8111\u540e\u98ce\u751f\uff0c\u770b \u4e0d\u6e05\u4eba\u5f71\u3002\u695a\u662d\u5357\u773c\u89c2\u516d\u8def\uff0c\u8033\u542c\u516b\u65b9\uff0c\u84e6\u89c1\u4e00\u4eba\u4f3c\u5f2f\u7bad\u822c\u75be\u51b2\u800c\u4e0a\uff0c\u5374\u662f\u53cc\u624b\u63e1\u62f3\uff0c\u4e0d\u5e26 \u5175\u5668\uff0c\u867d\u7136\u5bf9\u6765\u4eba\u7684\u8f7b\u529f\u9887\u611f\u60ca\u5947\uff0c\u4f46\u4e5f\u4e0d\u4ee5\u4e3a\u610f\uff0c\u4ed6\u60f3\uff1a\u6211\u5929\u5c71\u5251\u6cd5\uff0c\u795e\u5999\u65e0\u5339\uff0c\u4f60\u8fd9\u6837 \u51b2\u6765\uff0c\u6211\u53ea\u4e00\u5251\uff0c\u5c31\u53ef\u4ee5\u523a\u4f60\u4e00\u4e2a\u900f\u660e\u7a9f\u7abf\uff01\u90a3\u6599\u6842\u4ef2\u660e\u7684\u817e\u86df\u5251\uff0c\u5374\u662f\u4e00\u4ef6\u5f02\u5b9d\uff0c\u7528\u65f6\u5982 \u767e\u70bc\u94a2\uff0c\u4e0d\u7528\u65f6\u5982\u7ed5\u6307\u67d4\uff0c\u8fd9\u65f6\u7ed9\u6842\u4ef2\u660e\u5377\u6210\u4e00\u56e2\uff0c\u85cf\u4e8e\u624b\u5fc3\uff0c\u695a\u662d\u5357\u89c1\u4ed6\u7fe9\u5982\u98de\u9e1f\uff0c\u75be\u51b2 \u800c\u6765\uff0c\u628a\u5251\u4e00\u5f15\uff0c\u5148\u7c98\u5f00\u6613\u5170\u73e0\u7684\u77ed\u5251\uff0c\u53cd\u624b\u5411\u4e0a\u4e00\u64a9\uff0c\u5feb\u5982\u95ea\u7535\u3002\u4e0d\u6599\u6842\u4ef2\u660e\u5de6\u638c\u5f80\u5916\u4e00 \u7ffb\uff0c\u817e\u86df\u5251\u5f80\u5916\u7535\u5c04\u800c\u51fa\uff0c\u53ea\u542c\u5f97\u201c\u548b\u5693\u201d\u4e00\u58f0\uff0c\u695a\u662d\u5357\u7684\u5251\u7ed9\u622a\u4e3a\u4e24\u6bb5\uff0c\u6842\u4ef2\u660e\u4e5f\u85c9\u7740\u8fd9 \u4e00\u6321\u4e4b\u52bf\uff0c\u5012\u7ffb\u8fc7\u6765\uff0c\u8f7b\u98d8\u98d8\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u695a\u662d\u6d77\u529f\u592b\u4e5f\u771f\u8001\u5230\uff0c\u4e34\u5371\u4e0d\u4e71\uff0c\u4ed6\u75be\u9000\u51e0\u6b65\uff0c\u4fbf\u4ee5\u65ad\u5251\u4f5c\u4e3a\u6697\u5668\uff0c\u4e24\u8def\u53d1\u51fa\uff0c\u4e00\u53d6\u6613 \u5170\u73e0\uff0c\u4e00\u53d6\u6842\u4ef2\u660e\uff0c\u8fd9\u6837\u7f13\u5f97\u4e00\u7f13\uff0c\u4ed6\u5df2\u900f\u8fc7\u6c14\u6765\uff0c\u91cd\u6574\u8eab\u5f62\uff0c\u63a5\u4e0a\u4e86\u6842\u4ef2\u660e\u7684\u653b\u52bf\u3002 \u3000\u3000\u6842\u4ef2\u660e\u817e\u86df\u5251\u4f55\u7b49\u5389\u5bb3\uff0c\u5bd2\u5149\u4e00\u95ea\uff0c\u5df2\u5f53\u80f8\u51fb\u5230\uff0c\u695a\u662d\u5357\u8eab\u5b50\u4e00\u7ffb\uff0c\u65cb\u8f6c\u8fc7\u6765\uff0c\u53f3\u638c\u4e00 \u62c2\uff0c\u53cd\u622a\u6842\u4ef2\u660e\u6301\u5251\u7684\u624b\u8155\u3002\u6842\u4ef2\u660e\u89c1\u4ed6\u4e00\u7167\u9762\u5c31\u65bd\u5c55\u51fa\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u4e0d\u7531\u5413\u4e86\u4e00\u8df3\uff0c\u867d\u6709 \u5b9d\u5251\uff0c\u4e5f\u4e0d\u6562\u5927\u610f\uff0c\u5f53\u4e0b\u65bd\u5c55\u51fa\u4e94\u79bd\u5251\u6cd5\u4e2d\u7684\u7cbe\u5999\u62db\u6570\uff0c\u5982\u79cb\u98ce\u626b\u53f6\uff0c\u6a2a\u626b\u4e0b\u538b\u3002\u695a\u662d\u5357\u4ee5 \u5929\u5c71\u638c\u6cd5\u5bf9\u4ed8\uff0c\u751a\u611f\u5403\u529b\uff0c\u5c61\u9047\u9669\u62db\u3002 \u3000\u3000\u4ed6\u5bf9\u6842\u4ef2\u660e\u8fd9\u628a\u5251\u53c8\u6068\u53c8\u7231\uff0c\u5fc3\u60f3\uff1a\u6211\u7684\u6e38\u9f99\u5251\u7ed9\u51cc\u672a\u98ce\u593a\u4e86\u53bb\uff0c\u8fd9\u53e3\u9e1f\u6c14\uff0c\u8fc4\u4eca\u672a \u51fa\u3002\u770b\u4ed6\u8fd9\u53e3\u5251\uff0c\u597d\u50cf\u5251\u8d28\u8fd8\u5728\u6e38\u9f99\u5251\u4e4b\u4e0a\uff0c\u8981\u662f\u593a\u5f97\u8fc7\u6765\uff0c\u5c31\u4e0d\u6015\u51cc\u672a\u98ce\u4e86\uff0c\u53ef\u662f\uff0c\u6842\u4ef2 \u660e\u653b\u52bf\u5f3a\u52b2\u4e4b\u6781\uff0c\u4f11\u8bf4\u593a\u4e0d\u4e86\u4ed6\u7684\u5251\uff0c\u5076\u4e00\u4e0d\u614e\uff0c\u53ea\u6015\u7acb\u6709\u4e27\u8eab\u4e4b\u5371\u3002 \u3000\u3000\u8fd9\u65f6\u548c\u695a\u662d\u5357\u540c\u6765\u7684\u52a9\u624b\uff0c\u89c1\u6842\u4ef2\u660e\u53cd\u52a9\u201c\u5973\u8d3c\u201d\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u6025\u8df3\u4e0a\u524d\uff0c\u695a\u662d\u5357\u5927\u53eb \u9053\uff1a\u201c\u628a\u4f60\u7684\u5251\u7ed9\u6211\uff01\u201d\u4ed6\u731b\u5730\u4f7f\u51fa\u51e0\u62db\u82b1\u62db\uff0c\u4eba\u4f3c\u7a7f\u82b1\u8774\u8776\uff0c\u6643\u4e86\u51e0\u6643\uff0c\u6258\u5730\u8df3\u51fa\u6842\u4ef2\u660e \u5251\u5149\u7b3c\u7f69\u4e4b\u5916\uff0c\u4e00\u4f38\u624b\u5c31\u63a5\u4e86\u52a9\u624b\u629b\u8fc7\u6765\u7684\u957f\u5251\u3002\u6842\u4ef2\u660e\u4e00\u5251\u653b\u5230\uff0c\u5ffd\u89c9\u624b\u4e0a\u4e00\u9707\uff0c\u817e\u86df\u5251 \u7adf\u7ed9\u654c\u4eba\u5175\u5200\u7c98\u4f4f\uff0c\u5e26\u8fc7\u4e00\u8fb9\u3002\u4ed6\u6025\u5411\u524d\u987a\u52bf\u4e00\u9001\uff0c\u89e3\u53bb\u8fd9\u80a1\u5185\u5bb6\u7c98\u52b2\uff0c\u628a\u5251\u4e00\u6325\uff0c\u6325\u8d77\u4e00 \u56e2\u94f6\u8679\uff0c\u53c8\u628a\u695a\u662d\u5357\u8feb\u9000\u51e0\u6b65\uff01 \u3000\u3000\u8fd9\u65f6\u5192\u6d63\u83b2\u6b63\u8d76\u4e0a\u53bb\u62c9\u7740\u6613\u5170\u73e0\uff0c\u8fd8\u672a\u8c08\u5f97\u51e0\u53e5\uff0c\u56ed\u5b50\u91cc\u5df2\u662f\u4e00\u7247\u4eba\u58f0\uff0c\u6cb8\u6cb8\u626c\u626c\u3002 \u3000\u3000\u6613\u5170\u73e0\u76c8\u76c8\u4e00\u63d6\uff0c\u8bf4\uff1a\u201c\u5192\u59d0\u59d0\uff0c\u6211\u8981\u8d70\u4e86\u3002\u82e5\u89c1\u7740\u5f20\u516c\u5b50\uff0c\u8bf7\u4ee3\u6211\u8bf4\u4e00\u58f0\uff0c\u53eb\u4ed6\u65e9\u65e5 \u8bbe\u6cd5\u79bb\u5f00\u76f8\u5e9c\uff01\u201d\u8bf4\u7f62\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u5206\u82b1\u62c2\u67f3\uff0c\u4e00\u6e9c\u70df\u822c\u8dd1\u4e86\u3002\u695a\u662d\u5357\u7684\u52a9\u624b\u4e0a\u524d\u8ffd\u8d76\uff0c\u7ed9 \u5192\u6d63\u83b2\u5728\u80cc\u540e\u4e00\u9897\u94c1\u83b2\u5b50\u6253\u4e2d\u80a9\u80db\uff0c\u788e\u4e86\u8f6f\u7b4b\uff0c\u75db\u5f97\u5012\u5728\u5730\u4e0a\u76f4\u56b7\uff01 \u3000\u3000\u5192\u9662\u83b2\u76ee\u7779\u6613\u5170\u73e0\u98d8\u7136\u800c\u6765\uff0c\u98d8\u7136\u800c\u53bb\uff0c\u4e0d\u7981\u832b\u7136\u3002\u5979\u60f3\uff1a\u5085\u4f2f\u4f2f\u4ee5\u524d\u8bf4\u8fc7\uff0c\u770b\u6b64\u5973\u795e \u60c5\uff0c\u5979\u8eab\u4e16\u5b9a\u6709\u96be\u8a00\u4e4b\u9690\u3002\u5979\u4e07\u91cc\u6765\u4eac\uff0c\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff1f\u82e5\u771f\u662f\u4e3a\u4e86\u5f20\u534e\u662d\uff0c\u53ea\u6050\u5f20\u534e\u662d\u53c8 \u53e6\u6709\u6240\u5c5e\u3002\u518d\u770b\u4eca\u665a\u7684\u4e8b\uff0c\u51fa\u52a8\u5230\u695a\u662d\u5357\u8fd9\u53ae\u6765\u6349\u5979\uff0c\u53c8\u4e0d\u77e5\u5979\u95ef\u4e86\u4ec0\u4e48\u5927\u7978\uff1f\u53ea\u53ef\u60dc\u521a\u624d \u5306\u5306\u5fd9\u5fd9\uff0c\u6ca1\u6709\u548c\u5979\u8ba2\u4e0b\u540e\u4f1a\u4e4b\u671f\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u76f8\u5e9c\u91cc\u7684\u536b\u58eb\u5bb6\u4e01\uff0c\u5df1\u81ea\u56db\u9762\u6d8c\u6765\uff0c\u6842\u4ef2\u660e\u548c\u695a\u662d\u5357\u4e5f\u6b63\u6253\u5f97\u5341\u5206\u70bd\u70c8\u3002\u5192\u6d63\u83b2 \u65e0\u6687\u518d\u60f3\u6613\u5170\u73e0\u4e4b\u4e8b\uff0c\u638f\u51fa\u4e00\u628a\u593a\u547d\u795e\u7802\uff0c\u7741\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u695a\u662d\u5357\u5251\u4f3c\u5929\u77eb\uff0c\u5982\u6bd2\u9f99\u602a\u87d2\uff0c \u62ff\u7740\u7684\u867d\u662f\u4e00\u628a\u666e\u901a\u5200\u5251\uff0c\u4ecd\u7136\u5168\u662f\u8fdb\u624b\u62db\u6570\u3002\u518d\u770b\u6842\u4ef2\u660e\uff0c\u867d\u7136\u88ab\u8feb\u540e\u9000\uff0c\u4f46\u817e\u86df\u5b9d\u5251\u5251 \u98ce\u970d\u970d\uff0c\u5251\u6c14\u7eb5\u6a2a\uff0c\u5c01\u95ed\u906e\u6321\u4e4b\u95f4\uff0c\u5076\u800c\u4e5f\u6709\u51e0\u62db\u8f9b\u8fa3\u7684\u53cd\u51fb\u62db\u6570\uff0c\u5e26\u5b88\u5e26\u653b\uff0c\u4e5f\u5c3d\u81ea\u62b5\u6321 \u5f97\u4f4f\u3002 \u3000\u3000\u539f\u6765\u8bba\u5251\u6cd5\u4e0e\u8bba\u529f\u529b\uff0c\u90fd\u662f\u695a\u662d\u5357\u8f83\u9ad8\u4e00\u7b79\uff0c\u53ea\u662f\u6842\u4ef2\u660e\u5374\u80dc\u5728\u6709\u4e00\u628a\u5b9d\u5251\u4e0e\u6c14\u529b\u60a0 \u957f\u3002\u4ed6\u8d77\u521d\u65bd\u5c55\u4e94\u79bd\u5251\u6cd5\u7684\u201c\u538b\u201d\u5b57\u8bc0\uff0c\u5251\u62db\u81ea\u4e0a\u538b\u4e0b\uff0c\u60f3\u4ed7\u7740\u5b9d\u5251\u4e4b\u529b\uff0c\u4ee5\u6700\u51cc\u5389\u7684\u653b \u52bf\uff0c\u4e00\u4e3e\u51fb\u5012\u654c\u4eba\u3002\u4e0d\u6599\u5251\u62db\u4e00\u53d1\uff0c\u6bcf\u6bcf\u7ed9\u695a\u662d\u5357\u7528\u7c98\u3001\u5378\u4e24\u5b57\u8bc0\u5316\u53bb\u3002\u6842\u4ef2\u660e\u7684\u5251\u52bf\uff0c\u867d \u52b2\u9053\u5341\u8db3\uff0c\u65e0\u5948\u5bf9\u65b9\u7684\u5251\uff0c\u7adf\u597d\u4f3c\u8f7b\u98d8\u98d8\u7684\u6728\u7247\u4e00\u6837\uff0c\u8d34\u5728\u81ea\u5df1\u7684\u5251\u4e0a\uff0c\u987a\u7740\u5251\u98ce\uff0c\u5de6\u53f3\u6447 \u6643\uff0c\u81ea\u5df1\u7adf\u65e0\u6cd5\u7528\u529b\u524a\u65ad\u4ed6\u7684\u5175\u5203\uff01\u800c\u4e14\u5bf9\u65b9\u7684\u5251\u6cd5\u867d\u67d4\u5982\u67f3\u7d6e\uff0c\u82e5\u81ea\u5df1\u7a0d\u4e00\u758f\u795e\uff0c\u5b83\u53c8\u5ffd \u800c\u731b\u82e5\u6d2a\u6d9b\uff0c\u9aa4\u7136\u538b\u8f70\uff0c\u597d\u51e0\u6b21\u9669\u4e9b\u7ed9\u4ed6\u501f\u529b\u6253\u529b\uff0c\u593a\u53bb\u81ea\u5df1\u7684\u5b9d\u5251\uff01\u8fd9\u624d\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c \u731b\u7684\u60f3\u8d77\u4e86\u51cc\u672a\u98ce\u4e4b\u8a00\uff0c\u51cc\u672a\u98ce\u5728\u81ea\u5df1\u5f97\u4e86\u5b9d\u5251\u4e4b\u540e\uff0c\u66fe\u8bf4\uff1a\u201c\u8bba\u5251\u6cd5\uff0c\u4f60\u5c31\u662f\u6ca1\u6709\u5b9d\u5251\uff0c \u5728\u6c5f\u6e56\u4e0a\u4e5f\u7b97\u662f\u9876\u513f\u5c16\u513f\u7684\u4e86\uff0c\u80fd\u654c\u5f97\u4f4f\u4f60\u4e94\u79bd\u5251\u6cd5\u7684\uff0c\u6211\u5c48\u6307\u4e00\u6570\uff0c\u4e5f\u53ea\u662f\u6709\u9650\u51e0\u4eba\uff1b\u5f97 \u4e86\u5b9d\u5251\uff0c\u5982\u864e\u6dfb\u7ffc\uff0c\u5f53\u7136\u662f\u66f4\u5389\u5bb3\uff0c\u9664\u4e86\u5085\u8001\u524d\u8f88\u7684\u65e0\u6781\u5251\u6cd5\u548c\u6211\u7684\u5929\u5c71\u5251\u6cd5\u4e4b\u5916\uff0c\u5927\u6982\u8c01 \u90fd\u4e0d\u80fd\u6253\u8d25\u4f60\u4e86\u3002\u53ea\u662f\u8fd8\u8981\u63d0\u9632\u4e00\u4e2a\u4eba\uff0c\u4ed6\u5c31\u662f\u6211\u7684\u5e08\u5144\u695a\u662d\u5357\uff0c\u4ed6\u7684\u5251\u6cd5\u4e0d\u4e9a\u4e8e\u6211\uff0c\u529f\u529b \u5219\u4f3c\u4e4e\u8fd8\u7a0d\u5dee\u4e00\u70b9\uff0c\u4f60\u82e6\u78b0\u5230\u4ed6\uff0c\u4e0d\u8981\u548c\u4ed6\u5bf9\u653b\uff0c\u5229\u7528\u5b9d\u5251\u4e4b\u957f\uff0c\u7aed\u529b\u9632\u5b88\uff0c\u5728\u4ed6\u653b\u5f97\u6781\u6025 \u4e4b\u65f6\uff0c\u5c31\u4ee5\u4e94\u79bd\u5251\u6cd5\u4e2d\u7684\u51b2\u523a\u4e09\u5341\u516d\u5f0f\uff0c\u5ffd\u7136\u53cd\u51fb\u51fa\u53bb\uff0c\u4ed6\u975e\u64a4\u5251\u9632\u5b88\u4e0d\u53ef\u3002\u4ee5\u4ed6\u7684\u529f\u529b\uff0c \u4f60\u82e5\u9632\u5fa1\u7ef5\u5bc6\uff0c\u4ed6\u5c31\u593a\u4e0d\u4e86\u4f60\u7684\u5b9d\u5251\u3002\u8fd9\u6837\u603b\u53ef\u4ee5\u6253\u4e2a\u5e73\u624b\u3002\u201d\u6842\u4ef2\u660e\u867d\u6ca1\u89c1\u8fc7\u695a\u662d\u5357\uff0c\u4f46 \u4eca\u665a\u770b\u654c\u4eba\u51fa\u4e8e\uff0c\u548c\u51cc\u672a\u98ce\u7684\u5251\u6cd5\u4e00\u6837\uff0c\u4e0d\u662f\u695a\u662d\u5357\u8fd8\u662f\u8c01\uff1f\u4e8e\u662f\u4ed6\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u4f9d\u7740\u51cc\u672a\u51e4 \u6240\u6559\uff0c\u679c\u7136\u695a\u662d\u5357\u62ff\u4ed6\u6beb\u65e0\u529e\u6cd5\u3002\u6709\u65f6\u695a\u662d\u5357\u6025\u4e8e\u8fdb\u653b\uff0c\u5076\u6709\u7a7a\u9699\uff0c\u8fd8\u51e0\u4e4e\u7ed9\u4ed6\u8f9b\u8fa3\u7684\u53cd\u51fb \u632b\u6298\u4e0b\u6765\u3002 \u3000\u3000\u695a\u662d\u5357\u4e0d\u7531\u5f97\u5012\u62bd\u4e00\u53e3\u51c9\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\u8fd9\u4e2a\u5c11\u5e74\uff1f\u5728\u6c5f\u6e56\u9053\u4e0a\uff0c\u53ef\u4ece\u6ca1\u6709\u542c\u4eba \u8bf4\u8fc7\uff01\u201d\u8981\u77e5\u81ea\u695a\u662d\u5357\u4e0b\u5c71\u4ee5\u6765\uff0c\u9664\u4e86\u66fe\u8d25\u7ed9\u4ed6\u7684\u5e08\u5144\u6768\u4e91\u9a86\u548c\u5e08\u5f1f\u51cc\u672a\u98ce\u4e4b\u5916\uff0c\u53ef\u8bf4\u4ece\u65e0 \u654c\u624b\u3002\u5373\u7b97\u65e0\u6781\u5251\u7684\u540d\u5bbf\u5085\u9752\u4e3b\uff0c\u4e5f\u4e0d\u8fc7\u548c\u4ed6\u6253\u6210\u5e73\u624b\uff0c\u60f3\u4e0d\u5230\u5982\u4eca\u7adf\u7136\u5948\u4f55\u4e0d\u4e86\u4e00\u4e2a\u65e0\u540d \u5c11\u5e74\uff0c\u4ed6\u9a84\u72c2\u4e4b\u6c14\uff0c\u4e0d\u7531\u5f97\u6536\u655b\u4e0b\u6765\uff0c\u5251\u6cd5\u4e00\u53d8\uff0c\u5fd9\u6539\u7528\u9634\u67d4\u7684\u62db\u6570\uff0c\u60f3\u4e58\u6842\u4ef2\u660e\u7ecf\u9a8c\u4e0d\u8db3 \u7684\u5f31\u70b9\uff0c\u4e58\u9699\u593a\u5251\u3002 \u3000\u3000\u4e24\u4eba\u8f97\u8f6c\u653b\u62d2\uff0c\u6597\u4e86\u4e00\u767e\u591a\u62db\uff0c\u76f8\u5e9c\u7684\u536b\u58eb\u5bb6\u53ee\u5475\u5df2\u8702\u62e5\u800c\u5230\uff0c\u5192\u6d63\u83b2\u770b\u5f97\u5927\u4e3a\u7740\u6025\uff0c \u770b\u4ed6\u4eec\u4e24\u4eba\u6597\u5251\uff0c\u6842\u4ef2\u660e\u867d\u62b5\u6321\u5f97\u4f4f\uff0c\u5374\u8fd8\u662f\u7565\u5904\u4e0b\u98ce\uff0c\u8fd9\u4e9b\u4eba\u4e00\u6765\uff0c\u4ed6\u600e\u80fd\u9003\u8131\uff1f\u201d \u3000\u3000\u5192\u6d63\u83b2\u54ac\u7740\u7259\u6839\uff0c\u6b63\u6253\u7b97\u82e5\u90a3\u4e9b\u4eba\u56f4\u653b\u7684\u8bdd\uff0c\u5c31\u4e71\u6d12\u593a\u547d\u795e\u7802\u3002\u5ffd\u7136\u5929\u51e4\u697c\u60ac\u51fa\u767e\u4f59\u76cf \u5f69\u706f\uff0c\u4e94\u8272\u706f\u5149\u4e4b\u4e0b\uff0c\u6709\u4e00\u5c11\u5e74\u516c\u5b50\uff0c\u624b\u6447\u7ea8\u6247\uff0c\u5112\u51a0\u7d20\u670d\uff0c\u98d8\u98d8\u82e5\u4ed9\uff0c\u5728\u7b2c\u4e09\u5c42\u697c\u5934\uff0c\u659c \u501a\u680f\u6746\uff0c\u7ea8\u6247\u4e00\u6307\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u5c31\u5728\u6b64\u697c\uff0c\u8c01\u4eba\u8fd9\u6837\u653e\u8086\uff1f\u60ca\u52a8\u83b2\u9a7e\uff0c\u8be5\u5f53\u4f55\u7f6a\uff1f\u201d \u536b\u58eb\u5bb6\u4e01\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u89c1\u662f\u7eb3\u5170\u516c\u5b50\uff0c\u5413\u5f97\u5782\u4e0b\u624b\u6765\uff0c\u4e0d\u6562\u4e71\u52a8\uff0c\u695a\u662d\u5357\u8fde\u53d1\u6cfc\u98ce\u4e09\u62db\uff0c\u628a \u6842\u4ef2\u660e\u8feb\u9000\u51e0\u6b65\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u63a0\u5230\u5927\u98ce\u697c\u524d\uff0c\u62b1\u5251\u5f53\u80f8\uff0c\u884c\u793c\u8bf4\u9053\uff1a\u201c\u5351\u804c\u7981\u536b\u519b\u7edf\u9886\u695a\u662d \u5357\uff0c\u53c2\u89c1\u516c\u5b50\uff0c\u4e8b\u7f18\u4eca\u665a\u6709\u5973\u98de\u8d3c\u95ef\u5165\u76f8\u5e9c\uff0c\u5351\u804c\u524d\u6765\u64d2\u62ff\uff0c\u672a\u6687\u7980\u660e\u3002\u73b0\u5979\u8fd8\u6709\u4e24\u4e2a\u540c\u515a \u5728\u6b64\uff0c\u4e5e\u516c\u5b50\u996c\u4ee4\u5bb6\u4e01\u534f\u52a9\uff0c\u5c06\u4ed6\u4eec\u64d2\u4e0b\uff01\u201d\u7eb3\u5170\u5bb9\u82e5\u8bf4\u9053\uff1a\u201c\u8c01\u662f\u5979\u7684\u540c\u515a\uff1f\u201d\u695a\u662d\u5357\u56de \u8eab\u4e00\u6307\u6842\u4ef2\u660e\uff0c\u518d\u659c\u7a9c\u51e0\u6b65\uff0c\u627e\u5230\u4e86\u5192\u6d63\u83b2\uff0c\u521a\u521a\u4e3e\u624b\uff0c\u5192\u6d63\u83b2\u5ffd\u7136\u8863\u8896\u4e00\u62c2\uff0c\u82e5\u4e0d\u7ecf\u610f\u5730 \u906e\u7740\u8138\u90e8\uff0c\u626d\u5934\u4fbf\u8dd1\uff0c\u53eb\u9053\uff1a\u201c\u516c\u5b50\u6551\u6211\uff0c\u6b64\u4eba\u8bec\u826f\u4e3a\u76d7\uff0c\u7adf\u628a\u6211\u5f53\u5973\u8d3c\u540c\u515a\uff01\u201d\u7eb3\u5170\u516c\u5b50 \u62db\u624b\u8bf4\u9053\uff1a\u201c\u4f60\u4e0a\u6765\uff01\u201d\u5192\u6d63\u83b2\u5927\u6447\u5927\u6446\uff0c\u767b\u4e0a\u5929\u51e4\u697c\u3002\u539f\u6765\u5192\u6d63\u83b2\u5728\u4e94\u53f0\u5c71\u66fe\u548c\u695a\u662d\u5357\u671d \u8fc7\u76f8\uff0c\u6df1\u6015\u4ed6\u770b\u51fa\u81ea\u5df1\u8eab\u4efd\uff0c\u6240\u4ee5\u6025\u6025\u8eb2\u907f\u3002 \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u695a\u7edf\u9886\u6b64\u8a00\u5dee\u77e3\uff01\u8fd9\u4e24\u4eba\u90fd\u662f\u6211\u7684\u5bb6\u4e01\uff0c\u4e14\u8fd8\u662f\u6211\u6240\u719f\u6089\u7684\u4eba\uff0c \u4f60\u600e\u4e48\u8bf4\u4ed6\u4eec\u662f\u5973\u98de\u8d3c\u540c\u515a\uff1f\u4f60\u8d76\u5feb\u9000\u51fa\u53bb\u5427\uff01\u201d\u8fd9\u8fd8\u662f\u7eb3\u5170\u5bb9\u82e5\u591a\u5c11\u7ed9\u695a\u662d\u5357\u7559\u70b9\u9762\u5b50\uff0c \u8981\u4e0d\u7136\u771f\u4f1a\u8f70\u4ed6\u51fa\u53bb\uff01 \u3000\u3000\u695a\u662d\u5357\u8fdb\u4eac\u591a\u65f6\uff0c\u6df1\u77e5\u7eb3\u5170\u5bb9\u4e43\u5f53\u4eca\u7687\u4e0a\u6700\u5ba0\u7231\u4e4b\u4eba\uff0c\u66f4\u4f55\u51b5\u6709\u4e2a\u516c\u4e3b\u5728\u6b64\u3002\u5fc3\u5934\u6697 \u6068\uff0c\u6ca1\u5948\u4f55\u6253\u4e86\u51e0\u4e2a\u63d6\uff0c\u8fde\u9053\uff1a\u201c\u6055\u7f6a\uff01\u201d\u98d8\u8eab\u51fa\u4e86\u56ed\u5b50\u3002\u536b\u58eb\u5bb6\u53ee\u5475\u4e5f\u6084\u6084\u6563\u5f00\uff0c\u53ea\u5269\u4e0b \u6842\u4ef2\u660e\u7ad9\u5728\u5929\u98ce\u697c\u524d\u3002 \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u7b11\u5bf9\u6842\u4ef2\u660e\u9053\uff1a\u201c\u4f60\u7684\u6b66\u529f\u5f88\u597d\u5440\uff0c\u5c45\u7136\u80fd\u548c\u695a\u662d\u5357\u6253\u5e73\u624b\uff0c\u4f60\u662f\u8c01\u5440\uff1f\u201d\u6842 \u4ef2\u660e\u7ef7\u7740\u8138\u9053\uff1a\u201c\u6211\u662f\u4e2a\u770b\u56ed\u4eba\uff01\u201d\u7eb3\u5170\u5bb9\u82e5\u542c\u4e86\uff0c\u5927\u4e3a\u5947\u602a\uff1a\u600e\u7684\u4e00\u65e5\u4e4b\u95f4\uff0c\u63a5\u8fde\u78b0\u7740\u4e24 \u4e2a\u51fa\u7c7b\u62d4\u8403\u7684\u201c\u770b\u56ed\u4eba\u201d\uff1f\u5192\u6d63\u83b2\u5999\u89e3\u8bcd\u7ae0\uff0c\u7cbe\u901a\u97f3\u5f8b\uff0c\u7edd\u4e0d\u8f93\u4e8e\u65f6\u4e0b\u540d\u58eb\uff0c\u5df2\u4ee4\u4ed6\u5403\u60ca\u4e0d \u5c0f\uff1b\u800c\u6842\u4ef2\u660e\u7684\u6b66\u529f\uff0c\u6bd4\u8d77\u5192\u6d63\u83b2\u7684\u6587\u5b66\uff0c\u8fd8\u66f4\u4ee4\u4ed6\u60ca\u820c\u3002\u7eb3\u5170\u5bb9\u82e5\u867d\u4e0d\u7cbe\u4e8e\u6b66\u827a\uff0c\u5374\u66fe\u542c \u5f97\u5eb7\u7199\u8bf4\u8fc7\uff0c\u695a\u662d\u5357\u5728\u7981\u536b\u519b\u4e2d\uff0c\u9996\u5c48\u4e00\u6307\uff0c\u8fde\u5927\u5185\u536b\u58eb\u90fd\u7b97\u5728\u5185\uff0c\u4ed6\u4e5f\u662f\u6570\u4e00\u6570\u4e8c\u7684\u597d \u6c49\uff0c\u800c\u8fd9\u4e2a\u5e74\u9752\u7684\u201c\u770b\u56ed\u4eba\u201d\u7ade\u548c\u4ed6\u6253\u4e2a\u5e73\u624b\uff0c\u8fd9\u4eba\u7684\u6b66\u529f\uff0c\u4e5f\u5c31\u53ef\u60f3\u800c\u77e5\u4e86\u3002\u7eb3\u5170\u5bb9\u82e5\u4e0d \u7981\u8d70\u4e0b\u697c\u6765\uff0c\u62c9\u7740\u4ed6\u7684\u624b\u9053\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\u5440\uff1f\u548c\u6211\u8fdb\u697c\u5185\u5750\u5750\u5427\u3002\u201d\u6842\u4ef2\u660e\u8f7b\u8f7b\u4e00\u6454\uff0c \u8131\u51fa\u624b\u6765\uff0c\u53eb\u9053\uff1a\u201c\u6211\u6ca1\u6709\u529f\u592b\uff01\u201d\u7eb3\u5170\u5bb9\u82e5\u53c8\u662f\u4e0d\u7531\u81ea\u4e3b\u5730\u7ed9\u9707\u5f97\u9000\u540e\u513f\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u600e \u7684\u4f60\u548c\u4f60\u7684\u540c\u4f34\u90fd\u662f\u4e00\u4e2a\u6837\u513f\uff1f\u201d\u4ed6\u4e00\u62ac\u5934\uff0c\u5ffd\u8d1d\u6842\u4ef2\u660e\u4e00\u8138\u51db\u7136\u795e\u8272\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u867d\u7136 \u8d85\u8131\u5f02\u5e38\uff0c\u4e0d\u540c\u6d41\u4fd7\uff0c\u53ef\u662f\u5230\u5e95\u662f\u4e2a\u76f8\u5e9c\u516c\u5b50\uff0c\u51e0\u66fe\u53d7\u8fc7\u4eba\u8fd9\u6837\u51b7\u6f20\uff1f\u5fc3\u4e2d\u5f88\u662f\u4e0d\u5feb\uff0c\u8bf4 \u9053\uff1a\u201c\u58ee\u571f\u65e2\u4e0d\u613f\u4e0e\u6211\u8f88\u4fd7\u4eba\u4e3a\u4f0d\uff0c\u90a3\u4e5f\u5c31\u8bf7\u4fbf\u5427\u3002\u201d \u3000\u3000\u54ea\u6599\u6842\u4ef2\u660e\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u5374\u53c8\u4e0d\u8d70\uff0c\u518d\u53d1\u95ee\u9053\uff1a\u201c\u6211\u7684\u540c\u4f34\u5462\uff1f\u201d\u7eb3\u5170\u5bb9\u82e5\u9053\uff1a\u201c\u6211\u8fdb \u53bb\u7ed9\u5524\u4ed6\u4e0b\u6765\u5427\u3002\u201d\u6842\u4ef2\u660e\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u7528\u4f60\u53bb\uff0c\u6211\u81ea\u5df1\u4f1a\u627e\uff01\u201d\u8eab\u5f62\u4e00\u7eb5\uff0c\u98de\u63a0\u4e0a\u697c\uff0c \u7eb3\u5170\u5bb9\u82e5\u6014\u6014\u5730\u7ad9\u5728\u697c\u524d\uff0c\u4e0d\u77e5\u81ea\u5df1\u5230\u5e95\u662f\u54ea\u4e00\u70b9\u5f97\u7f6a\u4e86\u4ed6\u3002 \u3000\u3000\u65b9\u7acb\u4e86\u4e00\u4f1a\uff0c\u6842\u4ef2\u660e\u81ea\u5929\u51e4\u697c\u7684\u9876\u5c42\uff0c\u4e00\u8dc3\u800c\u4e0b\uff0c\u53c8\u628a\u7eb3\u5170\u5bb9\u82e5\u5413\u4e86\u4e00\u8df3\uff0c\u53ea\u89c1\u4ed6\u677f\u7740 \u9762\u5b54\u8bf4\u9053\uff1a\u201c\u4f60\u628a\u6211\u7684\u540c\u4f34\u85cf\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u7eb3\u5170\u8be7\u5f02\u5230\u6781\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u6697\u9053\uff1a\u201c\u83ab\u975e\u662f\u5f20 \u534e\u662d\u8bf7\u4ed6\u5165\u5bc6\u5ba4\uff1f\u4f46\u516c\u4e3b\u4e5f\u5728\u91cc\u9762\uff0c\u5f20\u534e\u662d\u53c8\u5982\u4f55\u80af\u8bf7\u4e00\u4e2a\u964c\u751f\u7537\u5b50\u8fdb\u53bb\uff1f\u201d\u731c\u7591\u4e0d\u5b9a\uff0c\u8d1d \u6842\u4ef2\u660e\u72b9\u81ea\u77aa\u773c\u8feb\u89c6\u7740\u4ed6\uff0c\u9887\u4e3a\u751f\u6c14\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u540c\u4f34\u53c8\u4e0d\u662f\u5c0f\u5b69\u5b50\u4e86\uff0c\u8c01\u80fd\u591f\u628a\u4ed6 \u85cf\u8d77\u6765\uff1f\u4f60\u4e0d\u89c1\u4ed6\u4e0a\u697c\u65f6\uff0c\u6211\u6b63\u5728\u697c\u5916\u548c\u695a\u662d\u5357\u8bf4\u8bdd\u5417\uff1f\u540e\u6765\u53c8\u4e0b\u6765\u548c\u4f60\u8bf4\u8bdd\uff0c\u6211\u90fd\u672a\u6709\u7a7a \u8ddf\u4ed6\u4ea4\u8c08\uff0c\u600e\u7684\u8bf4\u662f\u6211\u85cf\u4ed6\uff1f\u201d\u6842\u4ef2\u660e\u60f3\u4e86\u4e00\u60f3\uff1b\u4e5f\u662f\u9053\u7406\u3002\u6b63\u60f3\u518d\u8bf4\uff0c\u7eb3\u5170\u5bb9\u82e5\u5df2\u62c2\u8896\u4e0a \u697c\u53bb\u4e86\u3002 \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u731c\u5bf9\u4e86\uff0c\u5192\u6d63\u83b2\u679c\u7136\u662f\u88ab\u5f20\u534e\u662d\u8bf7\u5165\u5185\u5ba4\u53bb\u7684\u3002\u5979\u4e0a\u4e86\u5929\u51e4\u697c\uff0c\u8d70\u5230\u4e86\u7b2c\u4e09 \u5c42\uff0c\u5ffd\u89c1\u5f20\u534e\u662d\u4ece\u4e00\u9762\u5927\u94dc\u955c\u4fa7\u8fb9\u51fa\u6765\uff0c\u51b2\u7740\u5979\u54a7\u5634\u4e00\u7b11\uff0c\uff0c\u8bf4\u9053\uff1a\u201c\u5192\u59d1\u5a18\uff0c\u8bf7\u968f\u6211\u6765\u3002 \u5916\u9762\u7684\u4e8b\uff0c\u6709\u7eb3\u5170\u516c\u5b50\u51fa\u9762\uff0c\u4e00\u5b9a\u53ef\u4e86\u3002\u201d\u5192\u6d63\u83b2\u62bf\u5634\u4e00\u7b11\uff0c\u8ddf\u5728\u4ed6\u7684\u80cc\u540e\uff0c\u53ea\u89c1\u4ed6\u628a\u94dc\u955c \u4e00\u8f6c\uff0c\u80cc\u540e\u73b0\u51fa\u4e00\u6247\u6d3b\u95e8\uff0c\u8d70\u4e86\u8fdb\u53bb\uff0c\u95e8\u5185\u590d\u9053\u7f26\u56de\uff0c\u5176\u4e2d\u7adf\u522b\u6709\u5929\u5730\u3002\u539f\u6765\u5929\u51e4\u697c\u5efa\u7b51\u5f97 \u5341\u5206\u7cbe\u5de7\uff0c\u7adf\u662f\u5185\u4e00\u5c42\uff0c\u5916\u4e00\u5c42\uff0c\u65c1\u4eba\u600e\u6837\u4e5f\u770b\u4e0d\u51fa\u6765\uff0c\u4e00\u8d70\u4e86\u8fdb\u53bb\uff0c\u5192\u6d63\u83b2\u95ee\u9053\uff1a\u201c\u4f60\u600e \u4e48\u8ba4\u5f97\u51fa\u6211\uff1f\u201d\u5f20\u534e\u662d\u9053\uff1a\u201c\u521a\u624d\u6211\u5077\u770b\u4f60\u5e94\u654c\u65f6\u7684\u8eab\u6cd5\uff0c\u6b63\u662f\u65e0\u6781\u6d3e\u7684\uff0c\u6211\u4e00\u4e0b\u5b50\u5c31\u9192\u8d77 \u6765\u4e86\uff0c\u4f60\u968f\u5085\u9752\u4e3b\u4e0a\u4e94\u53f0\u5c71\u65f6\uff0c\u6211\u8fd8\u649e\u8fc7\u4f60\u4e00\u8180\u54e9\uff01\u201d\u8bf4\u7740\u5df2\u5230\u4e86\u4e00\u95f4\u7cbe\u5ba4\uff0c\u5192\u6d63\u83b2\u968f\u4ed6\u8fdb \u53bb\uff0c\u53ea\u89c1\u4e00\u4f4d\u65d7\u88c5\u5c11\u5973\uff0c\u5750\u5728\u5f53\u4e2d\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u7f8e\u8273\u7edd\u4fd7\uff0c\u6c14\u5ea6\u9ad8\u534e\uff0c\u7709\u5b57\u95f4\u6709\u9690\u9690\u54c0\u6028\uff0c\u5979\u9aa4\u89c1\u5f20\u534e\u662d\u548c\u4e00\u4e2a\u964c\u751f\u201c\u7537\u5b50\u201d\u8fdb \u6765\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u6b63\u60f3\u53d1\u95ee\uff0c\u5192\u6d63\u83b2\u5df2\u7b11\u76c8\u76c8\u5730\u62c9\u7740\u5979\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u6211\u4e5f\u662f\u5973\u7684\u3002\u201d\u628a\u624b\u4e00 \u62b9\uff0c\u73b0\u51fa\u5934\u4e0a\u9752\u4e1d\u3002\u516c\u4e3b\u51fa\u5947\u5730\u770b\u7740\u5979\uff0c\u5ffd\u7136\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5440\uff0c\u4f60\u771f\u50cf\u8463\u9102\u5983\uff0c\u6211\u5c0f\u7684\u65f6 \u5019\uff0c\u5f88\u559c\u6b22\u8ddf\u5979\u73a9\u3002\u5979\u8fd8\u6559\u8fc7\u6211\u505a\u8bd7\u586b\u8bcd\u5462\u3002\u201d\u5192\u6d63\u83b2\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5979\u662f\u6211\u7684\u6bcd\u4eb2\u3002\u6211\u4e09\u5c81 \u5927\u7684\u65f6\u5019\uff0c\u5979\u5c31\u88ab\u4f60\u7684\u7236\u4eb2\u62a2\u8fdb\u5bab\u53bb\u3002\u201d\u516c\u4e3b\u7b11\u5bb9\u987f\u655b\uff0c\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u6211\u5bb6\u5bf9\u4e0d\u8d77\u4f60\uff01\u201d \u5192\u6d63\u83b2\u53f9\u9053\uff1a\u201c\u4e8b\u60c5\u90fd\u8fc7\u53bb\u4e86\uff0c\u8fd8\u63d0\u5b83\u5e72\u561b\uff1f\u201d \u3000\u3000\u5f20\u534e\u662d\u7b2c\u4e00\u6b21\u77e5\u9053\u5192\u6d63\u83b2\u8eab\u4e16\uff0c\u4e5f\u9887\u60ca\u5f02\uff0c\u6c89\u9ed8\u534a\u54cd\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u5979\u662f\u6211\u4eec\u7684 \u670b\u53cb\uff0c\u6709\u4ec0\u4e48\u8bdd\u53ef\u4ee5\u8ddf\u5979\u8bf4\u3002\u201d\u516c\u4e3b\u8f7b\u63a0\u4e91\u9b13\uff0c\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u5192\u59d1\u5a18\uff0c\u6211\u771f\u6068\u6211\u751f\u5728\u5e1d\u738b\u4e4b \u5bb6\uff0c\u79cd\u4e0b\u8bb8\u591a\u7f6a\u5b7d\u3002\u4f60\u597d\u597d\u4e00\u5bb6\uff0c\u5982\u6b64\u62c6\u6563\uff0c\u4e00\u5b9a\u5f88\u6068\u6211\u4eec\u3002\u53ef\u662f\uff0c\u6211\u8981\u8bf4\u7ed9\u4f60\u542c\uff0c\u6211\u4e5f\u4e0d \u5f88\u5feb\u6d3b\u3002\u201d \u3000\u3000\u201c\u6211\u5728\u6df1\u5bab\u4e2d\u6ca1\u6709\u4e00\u4e2a\u670b\u53cb\uff0c\u59d0\u59d0\uff0c\u5982\u679c\u4f60\u8010\u70e6\u542c\u7684\u8bdd\uff0c\u6211\u60f3\u544a\u8bc9\u4f60\uff0c\u6211\u4eec\u505a\u516c\u4e3b\u7684\u662f \u600e\u6837\u8fc7\u65e5\u5b50\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u77a7\u8fd9\u516c\u4e3b\u7709\u76ee\u542b\u63a3\uff0c\u79c0\u76ee\u4f3c\u8e69\uff0c\u72b9\u5982\u4e00\u679d\u5e7d\u8c37\u767e\u5408\uff0c\u60f9\u4eba\u7231\u601c\u3002\u5750\u8fd1\u5979\u9053\uff1a\u201c\u516c \u4e3b\uff0c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u516c\u4e3b\u8f7b\u5f04\u88d9\u9497\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u77a7\u6211\u4eec\u505a\u516c\u4e3b\u7684\u8363\u534e\u6781\u81f4\uff0c\u5b9e\u5728\u5374\u6bd4\u4e0d\u4e0a\u666e\u901a\u4eba\u5bb6\uff0c \u6211\u4eec\u4e00\u51fa\u4e16\u5c31\u6709\u4e8c\u5341\u4e2a\u5b98\u5973\u3001\u516b\u4e2a\u4fdd\u59c6\u670d\u5f85\uff0c\u5bab\u5973\u4eec\u6709\u65f6\u8fd8\u53ef\u8c08\u8c08\uff0c\u90a3\u516b\u4e2a\u4fdd\u59c6\uff0c\u53ef\u51f6\u5f97\u5f88 \u54e9\uff01\u52a8\u4e0d\u52a8\u5c31\u642c\u51fa\u4ec0\u4e48\u7956\u8bad\u5bb6\u89c4\uff0c\u7687\u5bb6\u793c\u793a\uff0c\u628a\u6211\u4eec\u5173\u5728\u6df1\u5bab\u3002\u5047\u82e5\u5f97\u5230\u7236\u7687\u5ba0\u7231\u7684\uff0c\u90a3\u8fd8 \u597d\u4e00\u70b9\uff0c\u82e5\u662f\u4e0d\u7136\uff0c\u4e00\u5207\u90fd\u5f97\u542c\u4fdd\u59c6\u6446\u5e03\u3002\u6211\u7684\u5927\u59d0\u59d0\u597d\u4e0d\u5bb9\u6613\u71ac\u5230\u51fa\u5ac1\uff0c\u53ea\u548c\u9644\u9a6c\u884c\u8fc7\u5927 \u793c\uff0c\u4fdd\u59c6\u4fbf\u628a\u5979\u51b7\u6e05\u6e05\u5730\u5173\u5728\u5185\u9662\u91cc\uff0c\u4e0d\u8bb8\u548c\u9644\u9a6c\u89c1\u9762\u3002\u8fc7\u4e86\u534a\u5e74\uff0c\u5927\u516c\u4e3b\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u4fbf\u5429 \u5490\u5bab\u5973\uff0c\u628a\u9644\u9a6c\u5ba3\u53ec\u8fdb\u6765\uff0c\u8c01\u77e5\u88ab\u4fdd\u59c6\u4e0a\u6765\u62e6\u4f4f\u4e86\uff0c\u8bf4\u9053\uff1a\u2018\u8fd9\u662f\u4f7f\u4e0d\u5f97\u7684\uff0c\u88ab\u5916\u4eba\u4f20\u51fa \u53bb\uff0c\u8bf4\u516c\u4e3b\u4e0d\u8981\u5ec9\u803b\u3002\u2019\u5927\u516c\u4e3b\u6ca1\u6cd5\uff0c\u53ea\u597d\u8010\u4f4f\u4e86\u3002\u53c8\u8fc7\u4e86\u51e0\u4e2a\u6708\uff0c\u5927\u516c\u4e3b\u53c8\u8981\u53bb\u5ba3\u53ec\u9644 \u9a6c\uff0c\u53c8\u88ab\u4fdd\u59c6\u62e6\u4f4f\u4e86\uff0c\u9053\uff1a\u2018\u516c\u4e3b\u5018\u4e00\u5b9a\u8981\u5ba3\u53ec\u9644\u9a6c\uff0c\u987b\u5f97\u82b1\u51e0\u4e2a\u906e\u7f9e\u94b1\u3002\u2019\u5927\u516c\u4e3b\u62ff\u51fa\u4e00 \u767e\u4e24\u91d1\u5b50\u6765\uff0c\u4fdd\u59c6\u8bf4\u4e0d\u591f\uff0c\u53c8\u6dfb\u4e86\u4e00\u767e\u4e24\uff0c\u4e5f\u8bf4\u4e0d\u591f\uff0c\u76f4\u6dfb\u5230\u4e94\u767e\u4e24\uff0c\u8fd8\u662f\u8bf4\u4e0d\u591f\u3002\u5927\u516c\u4e3b \u4e00\u6c14\uff0c\u4e0d\u5ba3\u53ec\u4e86\u3002\u76f4\u5230\u6b63\u6708\u521d\u4e00\uff0c\u8fdb\u5bab\u62dc\u89c1\u7236\u4eb2\uff0c\u95ee\u9053\uff1a\u2018\u7236\u7687\u7a76\u7adf\u5c06\u81e3\u5973\u5ac1\u4e0e\u4f55\u4eba\uff1f\u7236\u7687 \u542c\u4e86\uff0c\u5341\u5206\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u2018\u742a\u5e27\u4e0d\u662f\u4f60\u7684\u4e08\u592b\u5417\uff1f\u2019\u5927\u516c\u4e3b\u9053\uff1a\u2018\u4ec0\u4e48\u742a\u5e27\uff1f\u4ed6\u662f\u4ec0\u4e48\u6837\u5b50 \u7684\uff1f\u81e3\u5973\u5ac1\u4e86\u4e00\u5e74\uff0c\u90fd\u672a\u89c1\u8fc7\u4ed6\u9762\uff01\u2019\u7236\u7687\u95ee\u9053\uff1a\u2018\u4f60\u4e24\u4eba\u4e3a\u4ec0\u4e48\u4e0d\u89c1\u9762\uff1f\u201d\u5927\u516c\u4e3b\u9053\uff1a \u2018\u4fdd\u59c6\u4e0d\u8bb8\uff01\u2019\u7236\u7687\u7b11\u9053\uff1a\u2018\u4f60\u592b\u59bb\u4eec\u7684\u4e8b\u4f53\uff0c\u4fdd\u59c6\u5982\u4f55\u7ba1\u5f97\uff1f\u2019\u5927\u516c\u4e3b\u542c\u4e86\uff0c\u56de\u5230\u5e9c\u53bb\u628a \u4fdd\u59c6\u5524\u5230\u8ddf\u524d\uff0c\u8bad\u65a5\u4e00\u987f\uff0c\u5f84\u81ea\u5c31\u628a\u9644\u9a6c\u5524\u6765\u4e86\u3002\u6211\u5927\u59d0\u59d0\u662f\u591f\u80c6\u91cf\uff0c\u624d\u6562\u5982\u6b64\u3002\u5176\u4ed6\u5386\u4ee3 \u516c\u4e3b\uff0c\u8fde\u5728\u5173\u5916\u79f0\u7687\u7684\u4e09\u4ee3\u90fd\u7b97\u5728\u5185\uff0c\u6ca1\u6709\u4e0d\u53d7\u4fdd\u59c6\u6b3a\u8d1f\u7684\uff01\u201d\u5192\u6d63\u83b2\u542c\u4e86\uff0c\u771f\u662f\u95fb\u6240\u672a \u95fb\uff0c\u5927\u611f\u5947\u5f02\uff0c\u516c\u4e3b\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5bab\u91cc\u7684\u89c4\u77e9\uff0c\u516c\u4e3b\u6b7b\u4e86\uff0c\u516c\u4e3b\u7684\u5668\u7528\u8863\u9970\uff0c\u5c31\u5168\u5f52\u4fdd \u59c6\u6240\u5f97\u3002\u56e0\u6b64\u4fdd\u59c6\u4eec\u5bf9\u516c\u4e3b\u5c31\u8d8a\u53d1\u7ba1\u5f97\u4e25\u5389\uff0c\u4e0d\u8bb8\u505a\u8fd9\uff0c\u4e0d\u8bb8\u505a\u90a3\uff0c\u8fde\u884c\u52a8\u90fd\u6ca1\u6709\u81ea\u7531\uff0c\u597d \u4e9b\u516c\u4e3b\u5c31\u56e0\u957f\u5904\u6df1\u5bab\uff0c\u90c1\u90c1\u800c\u6b7b\u3002\u7b97\u6765\uff0c\u6211\u8fd8\u7b97\u597d\u7684\u4e86\u3002\u201d\u5192\u6d63\u83b2\u6697\u60f3\uff1a\u201c\u8fd9\u6837\u770b\u6765\uff0c\u4fdd\u59c6 \u8650\u5f85\u516c\u4e3b\uff0c\u548c\u9e28\u6bcd\u7684\u8650\u5f85\u5993\u5973\uff0c\u5012\u5dee\u4e0d\u591a\uff01\u201d\u516c\u4e3b\u4f4e\u5401\u4e86\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u5bfb\u5e38\u767e\u59d3\u4eba\u5bb6 \u7684\u5973\u513f\uff0c\u53ef\u6709\u8fd9\u6837\u53d7\u7ba1\u675f\u7684\u5417\uff1f\u201d \u3000\u3000\u5f20\u534e\u662d\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u90a3\u4e9b\u53f7\u79f0\u8bd7\u793c\u4f20\u5bb6\u7684\u540d\u95e8\u6dd1\u5973\uff0c\u4e5f\u4e00\u6837\u88ab\u7ba1\u675f\u5f97\u5f88\u4e25\uff0c \u53ea\u4e0d\u8fc7\u6ca1\u4f60\u4eec\u90a3\u4e48\u591a\u4fdd\u59c6\uff0c\u4e0d\u662f\u53d7\u4fdd\u59c6\u7684\u7ba1\u675f\u800c\u5df2\u3002\u5927\u7ea6\u4f60\u4eec\u7687\u5bb6\u662f\u540d\u95e8\u4e2d\u7684\u540d\u95e8\uff0c\u6240\u4ee5\u5c3d \u7ba1\u505a\u7687\u5e1d\u7684\u600e\u6837\u8352\u6deb\u90fd\u53ef\u4ee5\uff0c\u4f46\u505a\u516c\u4e3b\u7684\u5374\u8981\u5b88\u7956\u8bad\u793c\u6cd5\u4e86\u3002\u201d\u5192\u6d63\u83b2\u70b9\u5934\u6697\u9053\uff1a\u201c\u4ed6\u5012\u770b \u5f97\u6bd4\u6211\u6e05\u695a\uff0c\u4e0d\u80fd\u4e13\u602a\u4fdd\u59c6\uff0c\u4fdd\u59c6\u53ea\u662f\u66ff\u7687\u5e1d\u6267\u884c\u5bb6\u89c4\u793c\u6cd5\u7684\u4eba\u7f62\u4e86\u3002\u201d \u3000\u3000\u516c\u4e3b\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5149\u5e1d\uff08\u987a\u6cbb\uff09\u7b2c\u4e09\u4e2a\u5973\u513f\uff0c\u4e94\u516d\u5c81\u7684\u4e00\u65f6\u5019\uff0c\u7236\u7687\u53bb\u4e16\uff08\u5176\u5b9e\u662f \u5230\u4e94\u53f0\u5c71\u51fa\u4e86\u5bb6\uff09\uff0c\u7687\u5144\u7ee7\u4f4d\uff0c\u6bd4\u8d77\u5176\u4ed6\u7684\u516c\u4e3b\u6765\uff0c\u53d7\u4fdd\u59c6\u7684\u7ba1\u675f\uff0c\u8fd8\u7b97\u662f\u8f83\u677e\u7684\u4e86\uff0c\u4f46\u5904 \u5728\u6df1\u5bab\uff0c\u4e5f\u662f\u5ea6\u65e5\u5982\u5e74\uff0c\u51e0\u4e4e\u95f7\u6b7b\u3002\u540e\u6765\u5bb9\u82e5\u6765\u4e86\uff0c\u4ed6\u662f\u6211\u4eec\u7684\u5185\u4eb2\uff0c\u548c\u7687\u5144\u4eb2\u5982\u624b\u8db3\uff0c\u5e38 \u5230\u5185\u5ef7\u6e38\u73a9\uff0c\u4ed6\u89c1\u6211\u90c1\u90c1\u4e0d\u4e50\uff0c\u5c31\u5e26\u6211\u51fa\u5bab\u5230\u4ed6\u7684\u5bb6\u91cc\u73a9\uff0c\u4ed6\u7684\u6bcd\u4eb2\u4e5f\u559c\u6b22\u6211\uff0c\u4ee5\u540e\u6211\u5c31\u5e38 \u5e38\u85c9\u53e3\u5230\u76f8\u5e9c\u53bb\u4f4f\uff0c\u6e9c\u51fa\u5bab\u6765\u3002 \u3000\u3000\u201c\u76f4\u5230\u53bb\u5e74\u7684\u590f\u5929\uff0c\u6709\u4e00\u65e5\uff0c\u5bb9\u82e5\u7a81\u7136\u6765\u627e\u6211\uff0c\u6084\u6084\u5730\u95ee\u6211\uff0c\u6709\u6ca1\u6709\u4e13\u6cbb\u5185\u4f24\u75c7\u7684\u5927\u5185 \u5723\u836f\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u6709\u597d\u4e9b\u5723\u836f\u662f\u6bcf\u4e2a\u516c\u4e3b\u90fd\u8d50\u4e00\u4efd\u7684\u3002\u6211\u95ee\u4ed6\u8981\u6765\u505a\u4ec0\u4e48\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5411\u7687\u5e1d \u8981\uff0c\u5374\u5411\u6211\u8981\uff1f\u4ed6\u7b11\u563b\u563b\u7684\u4e0d\u80af\u8bf4\uff0c\u6211\u53d1\u5c0f\u5b69\u5b50\u813e\u6027\uff0c\u4ed6\u4e0d\u8bf4\u6211\u5c31\u4e0d\u7ed9\uff0c\u4ed6\u71ac\u4e0d\u8fc7\uff0c\u624d\u544a\u8bc9 \u6211\u8bf4\uff0c\u662f\u7ed9\u4e00\u4e2a\u6c5f\u6e56\u5927\u76d7\u6cbb\u4f24\u7684\u3002\u6211\u975e\u5e38\u597d\u5947\uff0c\u89c9\u5f97\u8fd9\u4ef6\u4e8b\u60c5\u5f88\u591f\u523a\u6fc0\uff0c\u5c31\u8981\u6c42\u4ed6\u8ba9\u6211\u770b\u770b \u6c5f\u6e56\u5927\u76d7\u5230\u5e95\u662f\u4ec0\u4e48\u6837\u5b50\u7684\uff0c\u6211\u4eec\u7ea6\u5b9a\u5f7c\u6b64\u90fd\u4e0d\u51c6\u5bf9\u522b\u4eba\u8bf4\uff0c\u7ed3\u679c\u4ed6\u8ba9\u6211\u53bb\u770b\u4e86\uff0c\u6211\u8d77\u521d\u4ee5 \u4e3a\u6c5f\u6e56\u5927\u76d7\u4e0d\u77e5\u662f\u751f\u5f97\u591a\u51f6\u6076\u7684\u6837\u513f\u5462\uff0c\u54ea\u6599\u5374\u662f\u4e00\u4e2a\u5e74\u8f7b\u7684\u5c0f\u4f19\u5b50\uff01\u201d\u5192\u6d63\u83b2\u63d2\u53e3\u9053\uff1a \u201c\u4e00\u4e2a\u602a\u6d5a\u8c6a\u7684\u5c11\u5e74\uff01\u201d \u3000\u3000\u5f20\u534e\u662d\u9762\u4e0a\u4e00\u70ed\uff0c\u8bf4\u9053\uff1a\u201c\u5192\u59d0\u59d0\u5f00\u73a9\u7b11\uff0c\u6211\u5728\u4e94\u53f0\u5c71\u65f6\uff0c\u53d7\u4e86\u5bb9\u82e5\u59d1\u6bcd\u591a\u94ce\u738b\u5983\u7684\u98de \u9556\u6253\u4f24\uff0c\u540e\u6765\u591c\u95ef\u6e05\u51c9\u5bfa\uff0c\u53c8\u53d7\u4e86\u7981\u536b\u519b\u7684\u56f4\u653b\uff0c\u8eab\u53d7\u91cd\u4f24\uff0c\u6d41\u8840\u8fc7\u591a\uff0c\u6210\u4e86\u75e8\u75c7\u3002\u8981\u4e0d\u662f \u516c\u4e3b\u8d50\u836f\uff0c\u6211\u5df2\u6d3b\u4e0d\u5230\u73b0\u5728\u4e86\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u542c\u540e\uff0c\u5fc3\u4e2d\u4e86\u4e86\u3002\u5979\u60f3\uff1a\u50cf\u516c\u4e3b\u8fd9\u6837\u6df1\u611f\u5bc2\u5bde\u90c1\u95f7\u7684\u4eba\uff0c\u4e00\u5b9a\u6709\u8bb8\u591a\u53e4\u53e4\u602a\u602a\u7684 \u5e7b\u60f3\uff0c\u5979\u53d1\u73b0\u4e86\u201c\u6c5f\u6e56\u5927\u76d7\u201d\u8fd9\u6837\u4fca\u5dee\uff0c\u4e00\u5b9a\u5e38\u5e38\u6e9c\u51fa\u5bab\u6765\u627e\u4ed6\u8c08\u8bdd\u89e3\u95f7\uff0c\u4e45\u800c\u4e45\u4e4b\uff0c\u5c31\u751f \u4e86\u60c5\u612b\u3002\u53ea\u4e0d\u77e5\u5f20\u534e\u662d\u5bf9\u5979\u5982\u4f55\uff1f \u3000\u3000\u516c\u4e3b\u5c0f\u5634\u513f\u4e00\u5476\uff0c\u53c8\u9053\uff1a\u201c\u6211\u5f88\u4efb\u6027\uff0c\u6211\u60f3\u8981\u7684\u4e1c\u897f\uff0c\u603b\u8981\u5230\u624b\u65b9\u4f11\u3002\u6211\u5728\u5bab\u91cc\u95f7\u6b7b \u5566\uff0c\u5bb9\u82e5\u8bf4\u662d\u90ce\u5c31\u8981\u79bb\u5f00\u4e86\uff0c\u5192\u59d0\u59d0\uff0c\u4f60\u662f\u6765\u63a5\u4ed6\u51fa\u53bb\u7684\u5417\uff1f\u4f60\u4eec\u80fd\u4e0d\u80fd\u5e26\u6211\u5230\u5916\u9762\u53bb\u73a9\uff1f \u6696\uff0c\u4f60\u4eec\u4e0d\u77e5\u9053\uff0c\u6709\u65f6\u5019\u6211\u771f\u60f3\u5b89\u4e0a\u4e00\u5bf9\u7fc5\u8180\uff0c\u98de\u51fa\u6df1\u5bab\uff01\u201d\u8fd9\u65f6\u7684\u516c\u4e3b\uff0c\u6027\u60c5\u6d41\u9732\uff0c\u5c31\u50cf \u4e00\u4e2a\u6dd8\u6c14\u7684\u5c0f\u59d1\u5a18\uff01 \u3000\u3000\u5192\u6d63\u83b2\u5fc3\u60f3\uff1a\u4f60\u8981\u5b8c\u6210\u8fd9\u6837\u7684\u5fc3\u613f\uff0c\u90a3\u53ef\u6bd4\u8981\u6458\u4e0b\u5929\u4e0a\u7684\u6708\u4eae\u8fd8\u96be\uff01 \u3000\u3000\u6b63\u601d\u91cf\u95f4\uff0c\u5ffd\u7136\u590d\u9053\u671b\u6765\u4e86\u201c\u9601\u9601\u201d\u7684\u811a\u6b65\u58f0\uff0c\u5192\u6d63\u83b2\u5fd9\u628a\u5934\u5dfe\u6574\u597d\uff0c\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1 \u7eb3\u5170\u5bb9\u82e5\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u7eb3\u5170\u89c1\u516c\u4e3b\u548c\u5192\u6d63\u83b2\u8d34\u8eab\u800c\u5750\uff0c\u6b3e\u6b3e\u800c\u8c08\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u5fd9\u9053\uff1a\u201c\u4e09\u516c\u4e3b\uff0c\u65f6\u5019\u4e0d\u65e9\uff0c\u4f60 \u5e94\u8be5\u56de\u623f\u5b89\u6b47\u4e86\u3002\u201d\u516c\u4e3b\u55d4\u9053\uff1a\u201c\u5bb9\u82e5\u54e5\u54e5\uff0c\u4f60\u4e5f\u8981\u50cf\u4fdd\u59c6\u4e00\u6837\u7ba1\u6211\uff1f\u201d\u5192\u6d63\u83b2\u54a7\u5634\u4e00\u7b11\uff0c \u7ad9\u8d77\u6765\u9053\uff1a\u201c\u6211\u4e5f\u8981\u8d70\u4e86\uff01\u201d\u7eb3\u5170\u5bb9\u82e5\u6ee1\u8179\u72d0\u7591\uff0c\u62e6\u7740\u5979\u9053\uff1a\u201c\u4f60\u548c\u662d\u90ce\u662f\u4ee5\u524d\u76f8\u8bc6\u7684\u5417\uff1f \u4f60\u662f\u4ec0\u4e48\u65f6\u5019\u6765\u5230\u5e9c\u4e2d\u7684\uff1f\u201d\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u540c\u5728\u5f02\u4e61\u4e3a\u5f02\u5ba2\uff0c\u76f8\u9022\u503e\u76d6\u4fbf\u76f8\u4eb2\u3002\u201d\u7eb3\u5170\u5bb9 \u82e5\u89c1\u5979\u96c6\u5510\u4eba\u8bd7\u53e5\u4f5c\u7b54\uff0c\u610f\u601d\u662f\u8bf4\uff0c\u53ea\u56e0\u6027\u60c5\u76f8\u6295\uff0c\u4e4d\u89c1\u9762\uff08\u503e\u76d6\uff09\u4fbf\u53ef\u6210\u4e3a\u597d\u53cb\u3002\u8fd9\u6837\u8bf4 \u4f3c\u4e4e\u5979\u548c\u5f20\u534e\u662d\u4ee5\u524d\u5e76\u4e0d\u76f8\u8bc6\u3002\u4f46\u7ec6\u5473\u8bd7\u610f\uff0c\u4ea6\u53ef\u80fd\u662f\u6697\u6307\u5979\u548c\u81ea\u5df1\u4ee5\u53ca\u516c\u4e3b\uff0c\u90fd\u662f\u201c\u503e\u76d6 \u5982\u6545\u201d\u7684\u610f\u601d\uff0c\u77e5\u9053\u5979\u4e0d\u613f\u4f5c\u7b54\uff0c\u6545\u610f\u96c6\u6210\u8bd7\u53e5\uff0c\u597d\u50cf\u7985\u8bed\u4e00\u6837\u3002\u7eb3\u5170\u5bb9\u82e5\u4e0d\u89c9\u7709\u5934\u4e00\u76b1\uff0c \u4f46\u89c1\u5979\u624d\u601d\u654f\u6377\uff0c\u4e5f\u5c31\u4e0d\u518d\u7559\u96be\uff0c\u7531\u5979\u81ea\u53bb\u3002 \u3000\u3000\u5192\u6d63\u83b2\u4e0b\u4e86\u5929\u51e4\u697c\uff0c\u89c1\u6842\u4ef2\u660e\u8e3d\u8e3d\u524d\u884c\uff0c\u5982\u75f4\u5982\u50bb\uff0c\u5fd9\u4e0a\u524d\u62c9\u7740\u4ed6\u3002\u6842\u4ef2\u660e\u628a\u624b\u4e00\u6454\uff0c \u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u53bb\u966a\u90a3\u4ec0\u4e48\u516c\u5b50\uff0c\u56de\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4f60\u53c8\u6765\u4e86\uff01\u6211\u662f\u5f20\u534e\u662d\u8bf7\u53bb\u8c08 \u7684\uff0c\u5e72\u7eb3\u5170\u516c\u5b50\u4ec0\u4e48\u4e8b\uff1f\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u662f\u5417\uff1f\u6211\u770b\u7eb3\u5170\u516c\u5b50\u5f88\u559c\u6b22\u4f60\uff0c\u8981\u4e0d\u7136\uff0c\u600e\u4f60\u8bf4\u4ed6 \u5f85\u4eba\u5f88\u597d\uff0c\u5bf9\u6211\u5374\u662f\u90a3\u4e48\u51b7\u51b0\u51b0\u7684\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4f60\u628a\u7ecf\u8fc7\u7ec6\u7ec6\u8bf4\u6765\uff0c\u5f85\u6211\u8bc4\u8bc4\u7406\uff0c\u770b\u662f \u4f60\u4e0d\u5bf9\uff0c\u8fd8\u662f\u4ed6\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u6842\u4ef2\u660e\u7ec6\u7ec6\u8bf4\u4e86\uff0c\u5192\u6d63\u83b2\u7b11\u5f97\u6253\u8dcc\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u8fd9\u6837\u83bd\u649e\uff0c\u4e00\u89c1\u9762\u5c31\u5411\u4eba\u5bb6\u8981 \u4eba\uff0c\u8fd9\u600e\u602a\u5f97\u4ed6\uff0c\u8bd5\u60f3\uff0c\u5047\u5982\u662f\u4e00\u4e2a\u666e\u901a\u7684\u5bb0\u76f8\u516c\u5b50\uff0c\u4f60\uff0c\u4e00\u4e2a\u770b\u56ed\u4eba\u8fd9\u6837\u9876\u649e\u4ed6\uff0c\u4ed6\u4e0d\u628a \u4f60\u6293\u8d77\u6765\u624d\u602a\uff01\u201d\u6842\u4ef2\u660e\u542c\u4e86\uff0c\u4e5f\u662f\u9053\u7406\uff0c\u4e0d\u518d\u8a00\u8bed\u3002\u5192\u6d63\u83b2\u53c8\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u636e\u6211\u770b \u6765\uff0c\u7eb3\u5170\u516c\u5b50\u4e5f\u5df2\u8d77\u4e86\u7591\u5fc3\u4e86\u3002\u4ed6\u867d\u7136\u8d85\u8131\u7edd\u4fd7\uff0c\u4f46\u5230\u5e95\u4e0d\u80fd\u7b97\u6211\u4eec\u8fd9\u8fb9\u7684\u4eba\u3002\u4ed6\u4e00\u8d77\u4e86\u7591 \u5fc3\uff0c\u6211\u4eec\u5728\u8fd9\u671b\u5446\u4e0d\u4e0b\u53bb\u4e86\uff0c\u800c\u4e14\u5c31\u7b97\u4ed6\u4e0d\u6000\u7591\uff0c\u4f60\u4eca\u665a\u4eae\u4e86\u8fd9\u4e48\u4e00\u624b\uff0c\u628a\u695a\u662d\u5357\u7684\u5251\u524a \u65ad\uff0c\u548c\u4ed6\u6253\u6210\u5e73\u624b\uff0c\u76f8\u5e9c\u91cc\uff0c\u53ea\u8981\u662f\u61c2\u5f97\u6b66\u529f\u7684\uff0c\u6ca1\u6709\u4e0d\u6000\u7591\u4f60\u7684\u4e86\u3002\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u90a3\u6211 \u4eec\u5728\u8def\u4e0a\u4e5f\u66fe\u6253\u8d62\u4e86\u6c5f\u5317\u4e09\u9b54\uff0c\u9646\u660e\u9646\u4eae\u600e\u4e48\u8fd8\u8bf7\u6211\u4eec\u6765\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e0d\u89e3\u4e8b\uff0c \u6c5f\u5317\u4e09\u9b54\u600e\u80fd\u548c\u695a\u662d\u5357\u76f8\u63d0\u5e76\u8bba\uff1f\u5728\u8fd9\u91cc\uff0c\u8c01\u8981\u662f\u6321\u5f97\u4f4f\u695a\u662d\u5357\u4e09\u62db\uff0c\u6050\u6015\u5c31\u4f1a\u9707\u52a8\u4eac\u5e08 \u4e86\u3002\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4eec\u662f\u4e0d\u662f\u8981\u9a6c\u4e0a\u9003\u8dd1\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u6211\u867d\u7136\u89c1\u7740\u4e86\u5f20\u516c\u5b50\uff0c\u8fd8 \u6ca1\u6709\u628a\u6211\u4eec\u7684\u6765\u610f\u544a\u8bc9\u4ed6\uff0c\u6211\u4eec\u8981\u4e0d\u8981\u9a6c\u4e0a\u8d70\uff0c\u4f60\u4e14\u5f85\u6211\u4eca\u665a\u597d\u597d\u60f3\u4e00\u60f3\u3002\u201d\u6842\u4ef2\u660e\u5947\u9053\uff1a \u201c\u4f60\u5728\u5929\u51e4\u697c\u803d\u4e86\u8fd9\u4e48\u4e9b\u65f6\u5019\uff0c\u89c1\u4e86\u5f20\u516c\u5b50\u8fd8\u4e0d\u548c\u4ed6\u8bf4\u660e\u6765\u610f\uff0c\u4f60\u4eec\u5230\u5e95\u8c08\u4e9b\u4ec0\u4e48\uff1f\u201d\u5192\u6d63 \u83b2\u4e00\u7b11\u4e0d\u7b54\uff0c\u53ea\u662f\u63a8\u4ed6\u56de\u623f\u7761\u89c9\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u5192\u6d63\u83b2\u5c31\u62c9\u8d77\u6842\u4ef2\u660e\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5411\u603b\u7ba1\u544a\u5047\uff0c\u4f60\u968f\u6211\u5230\u5916\u9762\u53bb\u627e\u4e00\u4f4d \u670b\u53cb\u3002\u201d\u6842\u4ef2\u660e\u4ece\u672a\u542c\u5192\u6d63\u83b2\u8bf4\u8fc7\u5728\u5317\u4eac\u6709\u670b\u53cb\uff0c\u5927\u611f\u5947\u602a\u3002\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4e0d\u662f\u6211\u7684\u670b\u53cb\uff0c \u662f\u6211\u5085\u4f2f\u4f2f\u7684\u77e5\u4ea4\uff0c\u5317\u4e94\u7701\u7684\u540d\u7f25\u5934\u77f3\u632f\u98de\uff0c\u4ed6\u72ec\u521b\u7684\u8e51\u4e91\u5341\u4e8c\u5251\u5728\u6c5f\u6e56\u4e0a\u4e45\u8d1f\u76db\u540d\u3002\u6b64\u4eba \u6700\u91cd\u6c5f\u6e56\u9053\u4e49\uff0c\u4e09\u5341\u5e74\u6765\uff0c\u53ea\u51ed\u4e00\u9762\u9556\u65d7\u5c31\u8d70\u904d\u5927\u6c5f\u5357\u5317\uff0c\u4ece\u672a\u5931\u624b\u3002\u636e\u5085\u4f2f\u4f2f\u8bf4\uff0c\u4ed6\u7684\u5251 \u6cd5\u867d\u597d\uff0c\u4f46\u80fd\u591f\u8fd9\u6837\uff0c\u5982\u5e76\u4e0d\u662f\u5168\u9760\u6b66\u529f\uff0c\u800c\u662f\u56e0\u4e3a\u5fb7\u9ad8\u671b\u91cd\uff0c\u6c5f\u6e56\u670b\u53cb\u90fd\u7ed9\u4ed6\u4e2a\u9762\u5b50\uff01\u201d \u6842\u4ef2\u660e\u559c\u9053\uff1a\u201c\u4f60\u4f55\u4e0d\u65e9\u8bf4\uff0c\u65e2\u6709\u8fd9\u6837\u7684\u8001\u524d\u8f88\u5728\u6b64\uff0c\u6211\u4eec\u7406\u5e94\u65e9\u53bb\u62dc\u8bbf\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a \u201c\u6211\u5c0f\u65f6\u968f\u5085\u4f2f\u4f2f\u89c1\u8fc7\u4ed6\uff0c\u524d\u51e0\u5e74\u542c\u8bf4\u4ed6\u5df2\u95ed\u95e8\u5c01\u5200\uff0c\u5728\u5bb6\u7eb3\u798f\uff0c\u4e0d\u7ba1\u95f2\u4e8b\u4e86\u3002\u53ea\u662f\u4ee5\u5085\u4f2f \u4f2f\u548c\u4ed6\u7684\u4ea4\u60c5\uff0c\u4ed6\u5bf9\u6211\u4eec\u7684\u4e8b\uff0c\u603b\u4e0d\u80fd\u4e0d\u7406\u3002\u6211\u4eec\u5c06\u6765\u82e5\u8981\u5e26\u5f20\u516c\u5b50\u9003\u51fa\u76f8\u5e9c\uff0c\u6050\u6015\u8fd8\u8981\u501a \u91cd\u4e8e\u4ed6\u3002\u201d \u3000\u3000\u4e24\u4eba\u5411\u603b\u7ba1\u8bf7\u5047\uff0c\u603b\u7ba1\u89c1\u4ed6\u4eec\u6628\u5929\u90a3\u6837\u7684\u5a01\u52bf\uff0c\u5c82\u6562\u4e0d\u51c6\uff1f\u4e24\u4eba\u8d70\u51fa\u76f8\u5e9c\uff0c\u5192\u6d63\u83b2\u9053\uff1a \u201c\u6211\u53ea\u8bb0\u5f97\u4ed6\u7684\u5bb6\u5728\u5949\u5723\u80e1\u540c\uff0c\u8be6\u7ec6\u5730\u5740\u5374\u4e0d\u8bb0\u5f97\uff0c\u53ea\u662f\u8d70\u5230\u90a3\u91cc\u4e00\u95ee\uff0c\u603b\u53ef\u77e5\u9053\u3002\u201d\u4e24\u4eba \u8d70\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5230\u4e86\u80e1\u540c\u53e3\uff0c\u6b63\u60f3\u627e\u4eba\u6765\u95ee\uff0c\u5ffd\u89c1\u6709\u4eba\u62ac\u7740\u9152\u5e2d\uff0c\u8d70\u5165\u80e1\u540c\u3002\u5176\u4e2d\u4e00\u4eba\u9053\uff1a \u201c\u77f3\u8001\u9556\u5934\u8fd9\u51e0\u5929\u5929\u5929\u8bf7\u5ba2\uff0c\u4eca\u5929\u53c8\u4e0d\u77e5\u8bf7\u7684\u662f\u54ea\u4e00\u4e9b\u4eba\u3002\u201d\u5192\u6d63\u83b2\u5927\u559c\u95ee\u9053\uff1a\u201c\u662f\u77f3\u632f\u98de \u8001\u9556\u5934\u8bf7\u5ba2\u5417\uff1f\u201d\u90a3\u4eba\u7768\u4e86\u5192\u6d63\u83b2\u4e00\u773c\u9053\uff1a\u201c\u8be5\u4e0d\u662f\u8bf7\u4f60\u5427\uff1f\u201d\u5192\u6d63\u83b2\u4e00\u7b11\u4e0d\u8bed\uff0c\u8ddf\u7740\u4ed6 \u8d70\u3002\u5230\u4e86\u4e00\u5ea7\u5927\u5b85\uff0c\u62ac\u9152\u5e2d\u7684\u81ea\u6709\u7ba1\u95e8\u7684\u957f\u5de5\u63a5\u4e86\u8fdb\u53bb\u3002\u5192\u6d63\u83b2\u4e0a\u524d\u5531\u4e86\u4e2a\u558f\uff0c\u5f84\u9053\u6765\u610f\u3002 \u3000\u3000\u90a3\u7ba1\u95e8\u7684\u957f\u5de5\u53c8\u6253\u91cf\u4e86\u6842\u5192\u4e8c\u4eba\u4e00\u756a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u6709\u6ca1\u6709\u540d\u5e16\u5e26\u6765\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a \u201c\u4e00\u65f6\u672a\u6687\u5907\u529e\uff0c\u4f60\u8bf4\u662f\u6c5f\u5357\u5085\u9752\u4e3b\u6c42\u89c1\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u7ba1\u95e8\u7684\u957f\u5de5\u5600\u5600\u5495\u5495\u8d70\u4e86\u8fdb\u53bb\uff0c\u6842\u4ef2\u660e\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u8fd9\u8001\u9556\u5934\u5982\u6b64\u4e49\u6c14\uff0c\u6211\u770b\u672a\u5fc5\u5c3d \u7136\u3002\u4ed6\u53c8\u4e0d\u662f\u4ec0\u4e48\u5b98\u5e9c\u8c6a\u7ec5\uff0c\u600e\u7684\u9012\u540d\u5e16\u6c42\u89c1\uff0c\u5174\u8fd9\u4e00\u5957\u4fd7\u793c\u5bdf\u6587\uff1f\u201d\u5192\u6d63\u83b2\u4e5f\u76b1\u4e86\u76b1\u7709\uff0c \u611f\u5230\u6709\u70b9\u610f\u5916\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u7ba1\u95e8\u7684\u957f\u5de5\u51fa\u6765\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8001\u7237\u5b50\u4e0d\u5728\u5bb6\u3002\u201d\u6842\u4ef2\u660e\u5927\u6012\u3002\u56b7\u9053\uff1a \u201c\u660e\u660e\u770b\u5230\u4f60\u4eec\u8bf7\u5ba2\uff0c\u600e\u4e48\u8bf4\u4e0d\u5728\u5bb6\uff01\u54fc\uff0c\u4f60\u4e0d\u63a5\u5f85\u5ba2\u4eba\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u8c0e\u8a00\u76f8\u9a97\uff0c\u8fd8\u7b97\u5f97\u4ec0 \u4e48\u6c5f\u6e56\u4eba\u7269\uff1f\u201d\u6842\u4ef2\u660e\u7adf\u7136\u7834\u53e3\u9a82\u8d77\u77f3\u632f\u98de\u6765\uff0c\u5192\u6d63\u83b2\u60f3\u529d\u89e3\u4e5f\u6765\u4e0d\u53ca\u3002 \u3000\u3000\u95f9\u4e86\u4e00\u9635\uff0c\u5185\u91cc\u7684\u95e8\u5ffd\u7136\u6253\u5f00\uff0c\u4e00\u4e2a\u83bd\u5934\u9640\u5927\u58f0\u5406\u559d\uff0c\u98de\u8dd1\u51fa\u6765\uff0c\u671d\u6302\u4ef2\u660e\u4e00\u63a8\uff0c\u559d \u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u5728\u8fd9\u91cc\u95f9\u4ec0\u4e48\uff1f\u201d\u6842\u4ef2\u660e\u5927\u6012\uff0c\u53cd\u8fce\u4e0a\u53bb\uff0c\u7528\u9e70\u722a\u529f\u4e2d\u7684\u64d2\u62ff\u624b\u6cd5\uff0c\u4e00\u638c\u5411 \u83bd\u5934\u9640\u80a9\u5934\u6309\u53bb\u3002\u90a3\u5934\u9640\u539f\u4e0d\u6253\u7b97\u4f24\u4eba\uff0c\u53ea\u662f\u60f3\u5413\u8d70\u4ed6\u7684\uff0c\u90a3\u6599\u6842\u4ef2\u660e\u53d1\u62db\u5947\u901f\uff0c\u4e00\u4e0b\u5b50\u5df2 \u662f\u638c\u7f18\u642d\u4e86\u4e0a\u6765\uff0c\u53ea\u8981\u5f80\u4e0b\u4e00\u62ff\uff0c\u591a\u597d\u6b66\u529f\u4e5f\u4e0d\u80fd\u52a8\u5f39\u3002\u83bd\u5934\u9640\u5927\u5403\u4e00\u60ca\uff0c\u6025\u6ed1\u8eab\u5378\u6b65\uff0c\u53cc \u81c2\u4e00\u62b1\uff0c\u53f3\u8098\u5fae\u62ac\uff0c\u4e39\u7530\u4e00\u642d\uff0c\u6c14\u8fbe\u56db\u80a2\uff0c\u89e3\u62c6\u4e86\u6842\u4ef2\u660e\u7684\u64d2\u62ff\u624b\uff0c\u6012\u543c\u4e00\u58f0\uff0c\u53cd\u624b\u56de\u62f3\uff0c \u5411\u6842\u4ef2\u660e\u9762\u95e8\u6363\u6765\uff01\u6842\u4ef2\u660e\u8eab\u5f62\u4e00\u95ea\uff0c\u8fd0\u5927\u529b\u9e70\u722a\u795e\u529f\uff0c\u556a\u7684\u4e00\u638c\u6253\u53bb\uff0c\u90a3\u5934\u9640\u8eab\u6cd5\u4e5f\u5feb\uff0c \u811a\u8ddf\u4e00\u65cb\uff0c\u62f3\u5934\u5728\u534a\u7a7a\u5212\u4e86\u534a\u4e2a\u5706\u5708\uff0c\u53d8\u6210\u4e00\u8bb0\u201c\u52fe\u62f3\u201d\uff0c\u72e0\u72e0\u6253\u5230\uff01 \u3000\u3000\u6842\u4ef2\u660e\u4e00\u6293\u6293\u53bb\uff0c\u6b63\u597d\u5c06\u83bd\u5934\u9640\u7684\u201c\u52fe\u62f3\u201d\u63a5\u7740\uff0c\u6842\u4ef2\u660e\u8fd0\u8d77\u795e\u529b\uff0c\u6293\u7740\u4ed6\u7684\u624b\u8155\uff0c\u5f80 \u4e0b\u4e00\u62d7\uff0c\u90a3\u5934\u9640\u4e5f\u6012\u543c\u4e00\u58f0\uff0c\u62f3\u5934\u62b5\u5728\u638c\u5fc3\uff0c\u4ecd\u7136\u7528\u529b\u649e\u53bb\uff01\u6842\u4ef2\u660e\u4f7f\u51fa\u64d2\u62ff\u624b\u6cd5\uff0c\u8fd8\u672a\u80fd \u5c06\u4ed6\u6253\u5012\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u4e0d\u77e5\u90a3\u5934\u9640\u66f4\u662f\u6709\u82e6\u8bf4\u4e0d\u51fa\uff0c\u4ed6\u662f\u6c5f\u6e56\u4e0a\u6210\u540d\u7684\u4eba\u7269\uff0c\u7ade\u6597\u4e00\u4e2a \u5c11\u5e74\u4e0d\u8fc7\uff0c\u624b\u8155\u53c8\u75db\u53c8\u9ebb\uff0c\u4e5f\u8981\u5f3a\u884c\u5fcd\u4f4f\uff0c\u4e0d\u6562\u558a\u51fa\u58f0\u6765\u3002 \u3000\u3000\u6842\u4ef2\u660e\u77e5\u9053\u9047\u5230\u4e86\u52b2\u654c\uff0c\u6b63\u60f3\u518d\u51fa\u8fa3\u62db\uff0c\u5192\u6d63\u83b2\u5ffd\u7136\u51b2\u4e86\u4e0a\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f \u901a\u660e\u53d4\u53d4\uff1f\u201d\u83bd\u5934\u9640\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u62f3\u5934\u5f80\u540e\u4e00\u62c9\uff0c\u6842\u4ef2\u660e\u8d81\u52bf\u5411\u524d\u4e00\u9001\uff0c\u83bd\u5934\u9640\u8e09\u8e09\u8dc4 \u8dc4\uff0c\u8dcc\u51fa\u51e0\u6b65\uff0c\u4e00\u4e2a\u65cb\u8eab\uff0c\u53cc\u62f3\u7d27\u63e1\uff0c\u4ecd\u7136\u76ef\u7740\u6842\u4ef2\u660e\u3002 \u3000\u3000\u5192\u6d63\u83b2\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u6c34\u51b2\u5230\u9f99\u738b\u5e99\uff0c\u81ea\u5bb6\u4eba\u8ba4\u4e0d\u5f97\u81ea\u5bb6\u4eba\uff0c\u4ef2\u660e\uff0c\u4f60\u5feb\u8fc7\u6765\u8d54 \u7f6a\uff01\u8fd9\u4f4d\u5927\u548c\u5c1a\u662f\u51cc\u672a\u98ce\u7684\u670b\u53cb\uff0c\u6c5f\u6e56\u4e0a\u4eba\u79f0\u602a\u5934\u9640\u901a\u660e\u548c\u5c1a\u3002\u201d \u3000\u3000\u901a\u660e\u548c\u5c1a\u653e\u4e0b\u62f3\u5934\uff0c\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\uff0c\u4e00\u628a\u62b1\u7740\u6842\u4ef2\u660e\u9053\uff1a\u201c\u771f\u662f\u82f1\u96c4\u51fa\u5c11\u5e74\uff0c\u6211\u4eec\u8001\u4e00 \u8f88\u7684\u5feb\u8981\u6210\u4e86\u5e9f\u7269\u4e86\u3002\u201d\u4ed6\u6027\u60c5\u867d\u7136\u9c81\u83bd\uff0c\u4e3a\u4eba\u5374\u6781\u5766\u7387\uff0c\u4ed6\u5bf9\u6842\u4ef2\u660e\u7684\u6b66\u529f\uff0c\u53ef\u662f\u771f\u5fc3\u8d5e \u53f9\u3002 \u3000\u3000\u8fd9\u65f6\u5c4b\u91cc\u9762\u53c8\u6709\u4e09\u4e2a\u4eba\u95fb\u58f0\u800c\u51fa\uff0c\u5f53\u524d\u4e24\u4e2a\u4eba\uff0c\u4e00\u4e2a\u9ad8\u9ad8\u7626\u7626\uff0c\u773c\u73e0\u767d\u6e17\u6e17\u7684\uff0c\u6d3b\u50cf\u4e2a \u540a\u6b7b\u9b3c\uff1b\u4e00\u4e2a\u80a5\u80a5\u77ee\u77ee\uff0c\u5934\u9876\u5149\u79c3\u79c3\u7684\uff0c\u5374\u50cf\u4e00\u4e2a\u5927\u9a6c\u6876\u3002\u6842\u4ef2\u660e\u4e4d\u89c1\u602a\u76f8\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u5192 \u6d63\u83b2\u6b23\u7136\u53eb\u9053\uff1a\u201c\u5e38\u53d4\u53d4\u3001\u7a0b\u53d4\u53d4\u4f60\u4eec\u4e5f\u5728\u8fd9\u513f\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u662f\u4e27\u95e8\u795e\u5e38\u82f1\u548c\u94c1\u5854\u7a0b\u901a\uff0c\u90fd\u662f\u5929\u5730\u4f1a\u7684\u9996\u9886\uff0c\u5f53\u5e74\u66fe\u8ddf\u968f\u5218\u90c1\u82b3\u5927\u95f9\u4e94\u53f0\u5c71 \u7684\u3002\u4e24\u4eba\u5e94\u4e86\u4e00\u58f0\uff0c\u770b\u6e05\u695a\u5192\u6d63\u83b2\u9762\u76f8\uff0c\u5927\u7b11\u9053\uff1a\u201c\u4f60\u626e\u6210\u8fd9\u6837\u7684\u4fca\u4fcf\u5c0f\u5b50\uff0c\u53ef\u4e0d\u66f4\u628a\u6211\u4eec \u4e24\u4e2a\u6620\u5f97\u4e11\u602a\u4e86\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u6b63\u5f85\u53eb\u6842\u4ef2\u660e\u4e0a\u524d\u76f8\u89c1\uff0c\u5e38\u82f1\u80cc\u540e\u5ffd\u7136\u95ea\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u8eab\u6cd5\u5feb\u6781\uff0c\u62a2\u4e0a\u53bb\u62c9\u7740\u5192 \u6d63\u83b2\u7684\u624b\u9053\uff1a\u201c\u4f60\u53ea\u987e\u62db\u547c\u53d4\u4f2f\uff0c\u8fde\u6211\u4e5f\u770b\u4e0d\u89c1\uff01\u201d\u5192\u6d63\u83b2\u56e0\u548c\u901a\u660e\u7b49\u4e09\u4eba\u84e6\u7136\u91cd\u9022\uff0c\u800c\u5e38 \u82f1\u53c8\u662f\u8eab\u957f\u4e03\u5c3a\u6709\u4f59\uff0c\u867d\u7136\u770b\u51fa\u4ed6\u80cc\u540e\u8ddf\u7740\u4e00\u4e2a\u4eba\uff0c\u5374\u6ca1\u6ce8\u610f\u662f\u8c01\u3002\u8fd9\u65f6\u4e00\u542c\u58f0\u97f3\uff0c\u559c\u6781\u53eb \u9053\uff1a\u201c\u6613\u59d0\u59d0\uff0c\u4f60\u4e5f\u6765\u4e86\uff01\u201d \u3000\u3000\u901a\u660e\u548c\u5c1a\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u4e0d\u662f\u53d9\u8bf4\u4e4b\u5730\uff0c\u4f60\u4eec\u968f\u6211\u8fdb\u53bb\uff0c\u5148\u8c12\u89c1\u77f3\u8001\u82f1\u96c4\u3002\u201d\u4ed6\u4e00\u9a6c\u5f53 \u5148\uff0c\u5e26\u9886\u6842\u5192\u4e8c\u4eba\u7a7f\u5165\u5185\u9662\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u77f3\u8001\u5144\uff0c\u4f60\u5f85\u6162\u8d35\u5ba2\u4e86\uff0c\u4f60\u8bf4\u8be5\u7f5a\u591a\u5c11\u76c5\u9152\uff1f\u201d \u3000\u3000\u5192\u6d63\u83b2\u7741\u773c\u4e00\u770b\uff0c\u53ea\u5c4b\u5b50\u91cc\u5750\u7740\u9ad8\u9ad8\u77ee\u77ee\u7684\u4e09\u5c71\u4e94\u5cb3\u597d\u6c49\uff0c\u603b\u6709\u5341\u6765\u4e2a\u4eba\uff0c\u5979\u8ba4\u5f97\u5f53\u4e2d \u90a3\u4e2a\u7626\u524a\u7684\u8001\u5934\u513f\u662f\u77f3\u632f\u98de\uff0c\u5176\u4ed6\u5c31\u53ea\u8ba4\u5f97\u4e00\u4e2a\u674e\u6765\u4eab\u624b\u4e0b\u7684\u5c06\u9886\u5f20\u9752\u539f\u3002 \u3000\u3000\u77f3\u632f\u98de\u5927\u6b65\u8d70\u51fa\uff0c\u671d\u6842\u5192\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u6055\u7f6a\uff0c\u6055\u7f6a\uff01\u201d\u518d\u8f6c\u95ee\u901a\u660e\u548c\u5c1a\u9053\uff1a\u201c\u4ed6\u4eec\u4e24 \u4f4d\u662f\u8c01\uff0c\u4f60\u600e\u4e0d\u7ed9\u6211\u4ecb\u7ecd\u4ecb\u7ecd\uff1f\u201d\u901a\u660e\u548c\u5c1a\u6293\u7740\u5934\u76ae\u554a\u5440\u4e00\u58f0\u53eb\u9053\uff1a\u201c\u90a3\u4f4d\u662f\u5192\u6d63\u83b2\u59d1\u5a18\uff0c \u8fd9\u4f4d\u5440\uff0c\u53eb\u505a\u4ec0\u4e48\uff1f\u5582\uff0c\u5192\u59d1\u5a18\uff0c\u4f60\u521a\u624d\u53eb\u4ed6\u540d\u5b57\uff0c\u6211\u542c\u4e0d\u6e05\u695a\uff0c\u4f60\u518d\u53eb\u4e00\u58f0\u6211\u542c\uff01\u201d \u3000\u3000\u77f3\u632f\u98de\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u83bd\u548c\u5c1a\uff01\u201d\u5192\u6d63\u83b2\u62c9\u7740\u6842\u4ef2\u660e\u606d\u606d\u656c\u656c\u65bd\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u77f3\u8001\u4f2f\u8fd8 \u8bb0\u5f97\u6211\u5417\uff1f\u6211\u662f\u5085\u9752\u4e3b\u4f2f\u4f2f\u6536\u517b\u7684\u90a3\u4e2a\u5973\u5a03\u5b50\u3002\u201d \u3000\u3000\u77f3\u632f\u98de\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u53eb\u4e86\u8d77\u6765\uff0c\u7aef\u8be6\u4e86\u4e00\u56de\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u5927\u4e86\uff0c\u4f60\u7684\u5085\u4f2f\u4f2f\u8fd8 \u597d\uff1f\u55ef\uff0c\u8fd9\u4f4d\u662f\u2014\u2014\u201d\u4ed6\u4e00\u9762\u95ee\u5192\u6d63\u83b2\uff0c\u4e00\u9762\u95ee\u4f73\u4ef2\u660e\uff0c\u5192\u6d63\u83b2\u9053\uff0c\u201c\u4ed6\u53eb\u6842\u4ef2\u660e\uff0c\u662f\u5085\u4f2f \u4f2f\u53eb\u4ed6\u548c\u6211\u4e00\u9053\u6765\u62dc\u89c1\u4f60\u7684\u3002\u201d\u77f3\u632f\u98de\u637b\u987b\u5fae\u7b11\uff0c\u8fde\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u201d\u5192\u6d63\u83b2\u8138\u4e0a\u53d1\u70e7\uff0c\u9762 \u7ea2\u8fc7\u8033\u3002\u901a\u660e\u548c\u5c1a\u56b7\u9053\uff1a\u201c\u4f60\u8fd8\u8bf4\u4ec0\u4e48\u597d\u597d\uff1f\u4ed6\u624b\u5e95\u597d\u8fa3\uff0c\u6211\u548c\u5c1a\u66ff\u4f60\u6321\u9a7e\uff0c\u53ef\u4e5f\u66ff\u4f60\u5403\u4e86 \u82e6\u5934\u3002\u201d \u3000\u3000\u77f3\u632f\u98de\u4e00\u5411\u597d\u5ba2\uff0c\u53ea\u662f\u8fd9\u4e24\u5929\u62db\u5f85\u6c5f\u6e56\u4e0a\u7684\u9ed1\u9053\u4eba\u7269\uff0c\u4e0d\u5f97\u4e0d\u7279\u522b\u5c0f\u5fc3\u3002\u4ed6\u542c\u5f97\u7ba1\u95e8\u7684 \u6765\u62a5\uff0c\u8bf4\u662f\u5085\u9752\u4e3b\u6c42\u89c1\uff0c\u5148\u662f\u5927\u559c\uff0c\u540e\u6765\u4e00\u95ee\u76f8\u8c8c\uff0c\u6765\u7684\u5374\u662f\u4e24\u4e2a\u5c11\u5e74\uff0c\u4ed6\u77e5\u9053\u5085\u9752\u4e3b\u5e76\u65e0 \u5f92\u5f1f\uff0c\u4e0d\u7981\u5927\u7591\uff0c\u901a\u660e\u6263\u5c1a\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u6562\u4e71\u6253\u5085\u9752\u4e3b\u540d\u5934\uff0c\u5f85\u6211\u53bb\u770b\u3002\u201d\u4e0d\u6599\u8fd9\u4e00\u770b\u5c31 \u770b\u51fa\u4e86\u4e8b\uff0c\u624b\u51e0\u4e4e\u7ed9\u6842\u4ef2\u660e\u62d7\u6298\u3002 \u3000\u3000\u77f3\u632f\u98de\u5927\u7b11\uff0c\u5e26\u6842\u5192\u4e8c\u4eba\u5165\u5e2d\uff0c\u4e00\u4e00\u7ed9\u4ed6\u4eec\u4ecb\u7ecd\uff0c\u5728\u5ea7\u5ba2\u4eba\u5360\u4e86\u4e00\u534a\u662f\u5929\u5730\u4f1a\u7684\u3002\u539f\u6765 \u901a\u660e\u548c\u5c1a\u4e0e\u5e38\u82f1\u3001\u7a0b\u901a\u4e8c\u4eba\uff0c\u5728\u4e94\u53f0\u5c71\u4e0b\u6b66\u5bb6\u5e84\u7684\u7fa4\u96c4\u5927\u4f1a\u4e4b\u540e\uff0c\u5949\u6d3e\u8d74\u7ca4\uff0c\u770b\u5e73\u5357\u738b\u5c1a\u4e4b \u4fe1\u7684\u52a8\u9759\uff0c\u5e76\u8054\u7edc\u90a3\u8fb9\u7684\u8c6a\u6770\u3002\u4e0d\u6599\u4e00\u5230\u5e7f\u4e1c\uff0c\u5434\u4e09\u6842\u5df2\u7ecf\u53d1\u96be\uff0c\u5c1a\u4e4b\u4fe1\u8d77\u5175\u54cd\u5e94\uff0c\u901a\u660e\u7b49 \u4eba\u548c\u6c5f\u5357\u7684\u5929\u5730\u4f1a\u9996\u9886\uff0c\u4ee5\u53ca\u9c81\u738b\u4f59\u90e8\u4e5f\u90fd\u642d\u4e0a\u4e86\u7ebf\u3002\u5c1a\u4e4b\u4fe1\u53cd\u590d\u65e0\u5e38\uff0c\u8d77\u4e8b\u5c1a\u672a\u6ee1\u4e00\u5e74\uff0c \u53c8\u518d\u6295\u964d\u6ee1\u6e05\uff0c\u6e05\u5ef7\u8d81\u52bf\u5927\u6355\u957f\u6c5f\u4ee5\u5357\u7684\u5e2e\u4f1a\u4eba\u7269\uff0c\u901a\u660e\u548c\u5c1a\u7b49\u4eba\u7ad9\u4e0d\u4f4f\u811a\uff0c\u7d22\u6027\u6df7\u5165\u4eac \u5e08\uff0c\u4ed7\u7740\u77f3\u632f\u98de\u7684\u63a9\u62a4\uff0c\u8eb2\u5728\u4ed6\u7684\u5bb6\u91cc\uff0c\u800c\u5f20\u9752\u539f\u5219\u662f\u5949\u674e\u6765\u4ea8\u4e4b\u547d\uff0c\u79d8\u5bc6\u8fdb\u4eac\u7684\u3002 \u3000\u3000\u81f3\u4e8e\u6613\u5170\u73e0\uff0c\u5219\u95f9\u5f97\u66f4\u51f6\uff0c\u5979\u6700\u65e9\u5165\u4eac\uff0c\u66fe\u4e24\u6b21\u591c\u63a2\u591a\u94ce\u7684\u738b\u5e9c\uff0c\u6709\u4e00\u6b21\u7ed9\u591a\u94ce\u649e\u89c1\uff0c \u6076\u6597\u8d77\u6765\uff0c\u738b\u5e9c\u7684\u9ad8\u624b\uff0c\u4e5f\u7eb7\u7eb7\u8d76\u5230\uff0c\u5e78\u5728\u6613\u5170\u73e0\u8f7b\u529f\u751a\u9ad8\uff0c\u8981\u4e0d\u7136\u51e0\u906d\u4e0d\u6d4b\u3002\u6613\u5170\u73e0\u7ed9\u8ffd \u6355\u5f97\u7d27\uff0c\u4e00\u65e5\u78b0\u7740\u901a\u660e\u548c\u5c1a\uff0c\u8c08\u8d77\u77f3\u632f\u98de\u4e49\u8584\u4e91\u5929\uff0c\u9042\u4e5f\u6765\u6295\u9760\uff0c\u6613\u5170\u73e0\u5728\u77f3\u5e9c\u6ce8\u4e86\u5c06\u8fd1\u4e24 \u4e2a\u6708\uff0c\u95ed\u95e8\u4e0d\u51fa\uff0c\u7cbe\u7814\u5929\u5c71\u5251\u6cd5\uff0c\u65e5\u524d\u56e0\u5f97\u77e5\u5f20\u534e\u662d\u4e0b\u843d\u624d\u518d\u5230\u76f8\u5e9c\u67e5\u63a2\uff0c\u7b2c\u4e00\u6b21\u78b0\u5230\u9646\u660e \u9646\u4eae\uff0c\u4e00\u63a0\u5373\u8fc7\uff0c\u7b2c\u4e8c\u6b21\u78b0\u5230\u695a\u662d\u5357\uff0c\u5374\u51e0\u4e4e\u88ab\u64d2\u3002 \u3000\u3000\u4f17\u4eba\u8fd9\u6b21\u5728\u77f3\u5e9c\u91cd\u4f1a\uff0c\u5341\u5206\u9ad8\u5174\u3002\u5e2d\u4e0a\u8c08\u8d77\u6842\u4ef2\u660e\u7684\u4e94\u79bd\u5251\u6cd5\u662f\u4ee5\u524d\u5ddd\u4e2d\u5927\u4fa0\u53f6\u4e91\u836a\u7684 \u5ae1\u4f20\uff0c\u77f3\u632f\u98de\u987f\u611f\u5174\u8da3\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6240\u521b\u7684\u8e51\u4e91\u5341\u4e09\u5251\uff0c\u636e\u6c5f\u6e56\u670b\u53cb\u6240\u8a00\uff0c\u4e0e\u4e94\u79bd\u5251\u5341\u5206\u76f8 \u4f3c\uff0c\u53ea\u662f\u53f6\u5927\u4fa0\u50fb\u5904\u56db\u5ddd\uff0c\u6211\u65e0\u7f18\u62dc\u89c1\uff0c\u4ed6\u7684\u5f1f\u5b50\u6842\u5929\u6f9c\uff0c\u4e09\u5341\u5e74\u524d\u867d\u66fe\u89c1\u8fc7\u4e00\u9762\uff0c\u6211\u8981\u4ed6 \u6307\u6559\uff0c\u4ed6\u53c8\u5fd9\u4e8e\u519b\u65c5\u4e4b\u4e8b\uff0c\u4e0d\u80af\u9732\u62db\u3002\u6842\u8d24\u4f84\u662f\u53f6\u5927\u4fa0\u7684\u5916\u5b59\uff0c\u8fd9\u56de\u76f8\u89c1\uff0c\u53ef\u4e0d\u80fd\u9519\u8fc7 \u4e86\uff01\u201d\u5f53\u4e0b\u8981\u6842\u4ef2\u660e\u8868\u6f14\u5251\u6cd5\u3002\u6842\u4ef2\u660e\u8d81\u770b\u9152\u5174\uff0c\u4e5f\u4e0d\u63a8\u8f9e\uff0c\u94ee\u7684\u4e00\u58f0\uff0c\u62bd\u51fa\u5b9d\u5251\uff0c\u4fbf\u89c1\u4e00 \u9053\u5bd2\u5149\uff0c\u7167\u8000\u6ee1\u5ea7\uff0c\u77f3\u632f\u98de\u559d\u58f0\u201c\u597d\u5251\uff01\u201d\u6842\u4ef2\u660e\u62b1\u5251\u4f5c\u63d6\uff0c\u9053\u58f0\u201c\u732e\u4e11\u201d\uff01\u6ef4\u6e9c\u6e9c\u4e00\u8f6c \u8eab\uff0c\u987f\u65f6\u94f6\u5149\u904d\u4f53\uff0c\u7d2b\u7535\u98de\u7a7a\uff0c\u6ee1\u8eab\u5251\u82b1\u9519\u843d\uff0c\u54ea\u8fd8\u5206\u5f97\u51fa\u5251\u5f71\u4eba\u5f71\uff1f\u6108\u821e\u6108\u6025\uff0c\u5251\u98ce\u6307 \u5904\uff0c\u56db\u9762\u7a97\u68c2\u90fd\u98d2\u98d2\u4f5c\u54cd\uff0c\u5e2d\u4e0a\u7fa4\u96c4\u7ed9\u5251\u98ce\u8feb\u5f97\u8863\u8882\u98d8\u4e3e\uff0c\u53cc\u773c\u76f4\u7741\uff0c\u77f3\u632f\u98de\u8d5e\u9053\uff1a\u201c\u597d\u5251 \u6cd5\uff01\u201d\u659f\u6ee1\u4e00\u676f\u9152\uff0c\u7a81\u5411\u6842\u4ef2\u660e\u6cfc\u53bb\uff0c\u901a\u660e\u548c\u5c1a\u5148\u662f\u4e00\u6014\uff0c\u968f\u5373\u9192\u609f\u7528\u610f\uff0c\u5e38\u82f1\uff0c\u7a0b\u901a\u7b49\u4e5f \u90fd\u659f\u4e86\u9152\uff0c\u7eb7\u7eb7\u6cfc\u51fa\u3002 \u3000\u3000\u9152\u65b9\u6cfc\u5b8c\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u6e05\u5578\uff0c\u98ce\u5b9a\u58f0\u5bc2\uff0c\u6842\u4ef2\u660e\u5b9d\u5251\u56f4\u8170\uff0c\u53cc\u624b\u7a7a\u7a7a\uff0c\u7acb\u5728\u5f53\u4e2d\uff0c\u5468\u56f4 \u4e08\u8bb8\u4e4b\u5730\uff0c\u9152\u6e7f\u5730\u9762\uff0c\u5708\u6210\u4e00\u4e2a\u5706\u5708\uff0c\u5708\u5b50\u5185\u4e00\u70b9\u9152\u75d5\u90fd\u6ca1\u6709\u3002\u4f17\u4eba\u7eb7\u7eb7\u62cd\u638c\uff0c\u77f3\u632f\u98de\u9053\uff1a \u201c\u6cfc\u6c34\u96be\u5165\uff0c\u786e\u662f\u4e0a\u4e58\u5251\u6cd5\u3002\u201d\u6842\u4ef2\u660e\u6025\u5fd9\u65bd\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u8981\u8bf7\u8001\u524d\u8f88\u6307\u6559\u3002\u201d \u3000\u3000\u77f3\u632f\u98de\u4e5f\u4e0d\u8c26\u8f9e\uff0c\u63d0\u5251\u79bb\u5e2d\uff0c\u6162\u6162\u79fb\u6b65\u5230\u6842\u4ef2\u660e\u821e\u5251\u6240\u5728\uff0c\u5353\u7136\u7acb\u5b9a\uff0c\u76ee\u5149\u76f4\u6ce8\u5251\u950b\uff0c \u7565\u4e00\u76d8\u65cb\uff0c\u4fbf\u89c9\u5251\u5c16\u4f3c\u5c71\uff0c\u5251\u5149\u5982\u7ec3\uff0c\u76f4\u8361\u51fa\u5468\u56f4\u4e08\u8bb8\u8fdc\u8fd1\u3002\u4ed6\u5f00\u5934\u51e0\u62db\uff0c\u5e76\u4e0d\u8fc5\u6377\uff0c\u6842\u4ef2 \u660e\u7ec6\u770b\u51fa\u624b\u5bb6\u6570\uff0c\u679c\u4e0e\u4e94\u79bd\u5251\u6cd5\u6709\u4e9b\u76f8\u4f3c\uff0c\u6697\u6697\u7559\u795e\u3002\u731b\u7136\u95f4\uff0c\u77f3\u632f\u98de\u8eab\u5f62\u4e00\u6643\uff0c\u5251\u5149\u7f2d\u7ed5 \u4e2d\u53ea\u89c1\u56db\u9762\u516b\u65b9\u90fd\u662f\u77f3\u632f\u98de\u7684\u8eab\u5f71\uff0c\u6ee1\u5ba4\u5251\u5149\uff0c\u5ffd\u4e1c\u5ffd\u897f\uff0c\u5ffd\u805a\u5ffd\u6563\uff0c\u7fe9\u82e5\u60ca\u9e3f\uff0c\u5b9b\u5982\u6e38 \u9f99\uff0c\u821e\u5230\u540e\u6765\uff0c\u53ea\u89c1\u4e00\u56e2\u7535\u5149\uff0c\u6eda\u6765\u6eda\u53bb\uff0c\u5b9b\u5982\u6c34\u94f6\u6cfb\u5730\uff0c\u82b1\u96e8\u7f24\u7eb7\uff01\u5e2d\u4e2d\u7684\u4e00\u4f4d\u8001\u9556\u5934 \u8bf4\uff1a\u201c\u5251\u821e\u5f97\u5feb\u4e0d\u8db3\u4e3a\u5947\uff0c\u8bf7\u5404\u4f4d\u770b\u770b\u6211\u4eec\u8fd9\u4f4d\u5927\u54e5\u7684\u529f\u529b\u3002\u201d\u968f\u624b\u6293\u8d77\u4e00\u628a\u74dc\u5b50\uff0c\u7528\u201c\u6ee1 \u5929\u82b1\u96e8\u201d\u7684\u6253\u6697\u5668\u624b\u6cd5\uff0c\u8fdc\u8fdc\u6492\u53bb\uff0c\u4f17\u4eba\u4e5f\u90fd\u8ddf\u7740\u53bb\u505a\u3002\u5192\u6d63\u83b2\u60f3\uff1a\u201c\u74dc\u5b50\u8fd9\u6837\u5fae\u5c0f\uff0c\u4f17\u4eba \u53c8\u90fd\u7528\u52b2\u6563\u53bb\uff0c\u6050\u6015\u6bd4\u6321\u4f4f\u6cfc\u6c34\u66f4\u96be\u3002\u201d\u54ea\u77e5\u5251\u98ce\u6fc0\u8361\u4e2d\uff0c\u74dc\u5b50\u7eb7\u7eb7\u53cd\u5c04\u56de\u53bb\uff0c\u6709\u4e24\u7c92\u5f39\u5728 \u5192\u6d63\u83b2\u7684\u9762\u4e0a\uff0c\u7adf\u7136\u4f3c\u7ed9\u866b\u8681\u53ee\u4e86\u4f3c\u7684\uff0c\u9690\u9690\u4f5c\u75db\uff0c\u8fd9\u624d\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u77f3\u632f\u98de\u54c8\u54c8\u4e00\u7b11\uff0c\u505c\u8eab\u62b1\u5251\uff0c\u56db\u65b9\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8001\u4e86\uff0c\u4e0d\u4e2d\u7528\u4e86\u3002\u201d\u4f17\u4eba\u770b\u90a3\u5730 \u9762\uff0c\u4e5f\u50cf\u6842\u4ef2\u660e\u6321\u4f4f\u6cfc\u6c34\u4e00\u6837\uff0c\u74dc\u5b50\u5728\u5916\u9762\u5e03\u4e86\u4e00\u5927\u5708\u3002\u8f70\u96f7\u4e00\u6837\u7684\u53eb\u597d\u3002\u59dc\u662f\u8001\u7684\u8fa3\uff0c\u77f3 \u632f\u98de\u7684\u529f\u529b\u6bd4\u6842\u4ef2\u660e\u786e\u662f\u9ad8\u4e86\u4e00\u7b79\u3002 \u3000\u3000\u77f3\u632f\u98de\u56de\u5e2d\uff0c\u6842\u4ef2\u660e\u4e00\u63d6\u5230\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u77f3\u8001\u524d\u8f88\u7684\u6307\u70b9\u3002\u201d\u6613\u5170\u73e0\u4e5f\u62bf\u7740\u5634\u7b11 \u9053\uff1a\u201c\u8fd9\u4efd\u793c\u7269\u53ef\u4e0d\u8f7b\uff01\u201d\u77f3\u632f\u98de\u7b11\u9053\uff1a\u201c\u8001\u673d\u4e09\u5341\u5e74\u5fc3\u613f\uff0c\u4e00\u65e6\u5f97\u507f\uff0c\u5f7c\u6b64\u90fd\u6709\u76ca\u5904\uff0c\u54ea \u6562\u8bf4\u662f\u6307\u70b9\uff1f\u201d\u539f\u6765\u4e94\u79bd\u5251\u6cd5\u4e0e\u8e51\u4e91\u5341\u4e09\u5251\uff0c\u540c\u4ee5\u8fc5\u6377\u89c1\u957f\uff0c\u4f46\u4e94\u79bd\u5251\u7cbe\u5fae\u4e4b\u5904\uff0c\u5728\u4e8e\u51b2 \u523a\uff0c\u8e51\u4e91\u5251\u7cbe\u5fae\u4e4b\u5904\uff0c\u5728\u4e8e\u58f0\u4e1c\u51fb\u897f\uff0c\u907f\u5b9e\u5c31\u865a\u3002\u4e24\u4eba\u8fd9\u4e00\u4e92\u76f8\u89c2\u6469\u5f7c\u6b64\u5251\u6cd5\uff0c\u90fd\u6709\u5927\u8fdb\uff0c \u8fd9\u662f\u540e\u8bdd\u3002 \u3000\u3000\u77f3\u632f\u98de\u9152\u9163\u8033\u70ed\uff0c\u610f\u5174\u751a\u8c6a\u3002\u7ad9\u4e86\u8d77\u6765\uff0c\u9080\u8bf7\u4f17\u4eba\u5230\u4ed6\u7684\u540e\u56ed\u73a9\u73a9\uff0c\u90a3\u91cc\u6709\u4e2a\u7ec3\u6b66\u573a \u5b50\uff0c\u4ed6\u8fd8\u60f3\u8bf7\u5ba2\u4eba\u8bd5\u6f14\u672c\u95e8\u7edd\u6280\u3002\u4ed6\u5bf9\u5192\u6d63\u83b2\u7531\u5176\u949f\u7231\uff0c\u8fde\u58f0\u5730\u53eb\u5979\u8d76\u5feb\u548c\u6842\u4ef2\u660e\u642c\u6765\u4f4f\u3002 \u3000\u3000\u5192\u6d63\u83b2\u6b63\u5f85\u7b54\u8bdd\u3002\u5ffd\u7136\u6613\u5170\u73e0\u62a2\u7740\u8d77\u6765\uff0c\u622a\u4e86\u8bdd\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5192\u59d0\u59d0\u4eca\u5929\u8fd8\u6709\u70b9\u4e8b\uff0c\u5979 \u8bf4\u8981\u8fc7\u4e24\u5929\u624d\u80fd\u642c\u6765\u3002\u201d\u5192\u6d63\u83b2\u5fc3\u4e2d\u4e00\u8be7\uff0c\u81ea\u5df1\u54ea\u66fe\u5bf9\u5979\u8bf4\u8fc7\u8fd9\u6837\u7684\u8bdd\uff1f\u6613\u5170\u73e0\u5728\u5979\u8eab\u8fb9\uff0c \u8f7b\u8f7b\u5730\u63e1\u5979\u7684\u624b\uff0c\u4e00\u4e2a\u7eb8\u56e2\uff0c\u5df1\u79fb\u8fc7\u5192\u6d63\u83b2\u624b\u5fc3\uff0c\u5192\u6d63\u83b2\u4fbf\u9053\uff1a\u201c\u77f3\u4f2f\u4eec\uff0c\u8fc7\u4e24\u5929\u6211\u51c6\u6765\u6253 \u6405\u3002\u201d\u77f3\u632f\u98de\u8001\u4e8e\u6c5f\u6e56\uff0c\u77a7\u5728\u773c\u91cc\uff0c\u867d\u6709\u70b9\u626b\u5174\uff0c\u4e5f\u4e0d\u4fbf\u633d\u7559\uff0c\u5f53\u4e0b\u7aef\u8336\u9001\u5ba2\uff0c\u6bb7\u6bb7\u5631\u5490\uff0c \u4e0d\u5fc5\u7ec6\u8868\u3002 \u3000\u3000\u6842\u5192\u4e8c\u4eba\u56de\u5230\u76f8\u5e9c\uff0c\u53ea\u89c1\u95e8\u524d\u9f99\u65cc\u51e4\u92ae\uff0c\u5bab\u6247\u9999\u8f66\uff0c\u90fd\u5df1\u65e0\u8e2a\uff0c\u56ed\u5b50\u91cc\u7684\u5f69\u706f\uff0c\u4e5f\u5df2\u9664 \u4e0b\u3002\u95ee\u8d77\u6765\u65f6\uff0c\u624d\u77e5\u4e09\u516c\u4e3b\u5df2\u7ecf\u56de\u5bab\uff0c\u8fde\u7eb3\u5170\u5bb9\u82e5\u4e5f\u7ed9\u7687\u4e0a\u5ba3\u53ec\u53bb\u4e86\u3002\u5192\u6d63\u83b2\u9887\u611f\u4e0d\u5b89\uff0c\u597d \u50cf\u6709\u4ec0\u4e48\u51f6\u5146\u4f3c\u7684\uff0c\u6253\u5f00\u7eb8\u56e2\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u9053\uff1a\u201c\u4eca\u665a\u901f\u4e0e\u5f20\u516c\u5b50\u9003\u51fa\u76f8\u5e9c\uff0c\u8fdf\u5219\u6709\u53d8\uff01\u201d \u5192\u6d63\u83b2\u4e0d\u7531\u5f97\u4e00\u9635\u5fc3\u60ca\u3002\u6b63\u662f\uff1a \u3000\u3000\u81ea\u60ca\u6b64\u5915\u884c\u85cf\u9732\uff0c\u5251\u6d77\u5200\u5c71\u8d70\u4e00\u906d\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5341\u4e94\u7ae0\u3000\u4fa0\u9aa8\u7ed3\u540c\u5fc3\u3000\u767e\u5c3a\u697c\u5934\u98de\u5251\u5f71\u3000\u5e7d\u5170\u6258\u77e5\u5df2\u3000\u4e00\u751f\u6068\u4e8b\u5411\u8c01\u8a00 \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u5341\u4e94\u56de \u4fa0\u9aa8\u7ed3\u540c\u5fc3\u3000\u767e\u5c3a\u697c\u5934\u98de\u5251\u5f71 \u5e7d\u5170\u6258\u77e5\u5df2\u3000\u4e00\u751f\u6068\u4e8b\u5411\u8c01\u8a00 \u3000\u3000\u8fd9\u5219\u5df2\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u65b0\u6708\u521d\u4e0a\uff0c\u82b1\u5f71\u7f24\u7eb7\uff0c\u56ed\u5b50\u91cc\u522b\u6709\u4e00\u756a\u5e7d\u96c5\u666f\u8272\u3002\u5192\u6d63\u83b2\u54ea\u6709\u5fc3\u60c5 \u8d4f\u73a9\uff1f\u6084\u6084\u5bf9\u6842\u4ef2\u660e\u9053\uff1a\u201c\u6211\u4eec\u5148\u517b\u4e00\u56de\u795e\uff0c\u5f85\u4e09\u66f4\u65f6\u5206\uff0c\u4fbf\u5230\u5929\u51e4\u697c\uff0c\u5524\u51fa\u5f20\u516c\u5b50\u3002\u201d \u3000\u3000\u54ea\u6599\u672a\u5230\u4e09\u66f4\uff0c\u5df2\u751f\u53d8\u6545\u3002\u6842\u5192\u4e8c\u4eba\uff0c\u521a\u521a\u6536\u62fe\u505c\u5f53\uff0c\u6b63\u5728\u9685\u9685\u7ec6\u8bed\uff0c\u5546\u8bae\u5982\u4f55\u53bb\u63a5\u5e94 \u5f20\u534e\u662d\u7684\u65f6\u5019\uff0c\u5ffd\u542c\u5230\u5916\u9762\u4e52\u4e53\u5de8\u54cd\uff0c\u4ece\u7a97\u5b50\u77a7\u53bb\uff0c\u53ea\u89c1\u5f69\u7130\u6d6e\u7a7a\uff0c\u6709\u4eba\u5927\u653e\u6d41\u661f\u82b1\u70ae\u3002\u5192 \u6d63\u83b2\u5fc3\u60f3\uff1a\u65e2\u975e\u5143\u5bb5\uff0c\u53c8\u65e0\u559c\u5e86\uff0c\u653e\u82b1\u70ae\u5e72\u561b\uff1f\u5fc3\u5ff5\u65b9\u52a8\uff0c\u56ed\u5b50\u91cc\u5047\u5c71\u82b1\u77f3\uff0c\u6811\u836b\u3001\u6865\u8fb9\uff0c \u6697\u5773\u5904\u7eb7\u7eb7\u94bb\u51fa\u4eba\u6765\uff0c\u6709\u7981\u536b\u519b\uff0c\u4e5f\u6709\u76f8\u5e9c\u7684\u6b66\u58eb\u3002\u5192\u6d63\u83b2\u5927\u60ca\uff0c\u6025\u62c9\u7740\u6842\u4ef2\u660e\u9053\uff1a\u201c\u6211\u4eec \u53d7\u5305\u56f4\u4e86\uff0c\u5feb\u95ef\u51fa\u53bb\uff01\u201d\u6842\u4ef2\u660e\u864e\u543c\u4e00\u58f0\uff0c\u817e\u86df\u5b9d\u5251\u75be\u7684\u8361\u8d77\u4e00\u5708\u94f6\u8679\uff0c\u201c\u7830\u201d\u7684\u4e00\u62f3\u628a\u7a97 \u6237\u6253\u788e\uff0c\u5e26\u5192\u6d63\u83b2\u95ef\u51fa\u5916\u9762\u3002 \u3000\u3000\u539f\u6765\u695a\u662d\u5357\u6628\u665a\u88ab\u7eb3\u5170\u5bb9\u82e5\u559d\u9000\u540e\uff0c\u529f\u8d25\u5782\u6210\uff0c\u6781\u4e3a\u6c14\u6124\u3002\u6613\u5170\u73e0\u4ee5\u524d\u5728\u4e94\u53f0\u5c71\u884c\u523a\u591a \u94ce\u4e4b\u65f6\uff0c\u4ed6\u4e5f\u66fe\u76ee\u51fb\uff0c\u6628\u665a\u4e00\u4eae\u4e86\u76f8\uff0c\u695a\u662d\u5357\u4fbf\u8ba4\u5f97\u662f\u5979\u3002\u540e\u6765\u518d\u4e00\u4ea4\u624b\uff0c\u89c1\u5979\u62ff\u7684\u5b9d\u5251\uff0c \u7adf\u662f\u81ea\u5df1\u5e08\u5144\u6768\u4e91\u9a86\u7684\u9057\u7269\uff0c\u4f7f\u7684\u53c8\u662f\u5929\u5c71\u5251\u6cd5\uff0c\u66f4\u662f\u60ca\u7591\u3002\u8fd9\u201c\u5973\u8d3c\u201d\u4e09\u756a\u4e24\u6b21\u884c\u523a\u9102\u4eb2 \u738b\u591a\u94ce\uff0c\u9102\u4eb2\u738b\u4e0b\u4ee4\u8981\u695a\u662d\u5357\u8d1f\u8d23\u6349\u5230\u5979\u3002\u695a\u662d\u5357\u662f\u6666\u660e\u7985\u5e08\u7684\u53db\u5f92\uff0c\u6700\u6015\u540c\u95e8\u4e2d\u4eba\u4e0e\u4ed6\u4f5c \u5bf9\uff0c\u4ed6\u649e\u5230\u4e86\u6613\u5170\u73e0\uff0c\u5c31\u662f\u6ca1\u6709\u591a\u94ce\u547d\u4ee4\u4e5f\u4e0d\u80af\u653e\u8fc7\u3002 \u3000\u3000\u5f53\u665a\uff0c\u4ed6\u5c31\u8d76\u56de\u5bab\u4e2d\uff0c\u6c42\u89c1\u5eb7\u7199\u7687\u5e1d\uff0c\u628a\u7eb3\u5170\u516c\u5b50\u5305\u5e87\u201c\u5973\u8d3c\u201d\u7684\u4e8b\u8bf4\u4e86\u3002\u5eb7\u7199\u7b11\u9053\uff1a \u201c\u5bb9\u82e5\u5c0f\u5b69\u813e\u6c14\uff0c\u4efb\u6027\u5219\u6709\u4e4b\uff0c\u5305\u5e87\u5f53\u4e0d\u81f3\u4e8e\uff0c\u6211\u770b\u4ed6\u4e5f\u4e0d\u77e5\u9053\u6709\u53db\u9006\u6f5c\u4f0f\u5728\u4ed6\u7684\u5e9c\u4e2d\uff0c\u6240 \u4ee5\u4e0d\u9ad8\u5174\u4f60\u5230\u4ed6\u90a3\u91cc\u95f9\u4e8b\uff0c\u8fd9\u6837\u5427\uff0c\u6211\u660e\u5929\u53ec\u4ed6\u5230\u5357\u4e66\u623f\u4f34\u8bfb\uff0c\u516c\u4e3b\u4e5f\u8981\u5979\u56de\u5bab\u4fbf\u662f\u4e86\u3002\u660e \u665a\u4f60\u5e26\u7981\u536b\u519b\uff0c\u77e5\u4f1a\u7eb3\u5170\u76f8\u7237\u5171\u540c\u56f4\u6355\u3002\u201d\u695a\u662d\u5357\u5927\u559c\uff0c\u7acb\u523b\u9000\u4e0b\u53bb\u5e03\u7f6e\u3002\u8fd9\u665a\u4ed6\u5e26\u6765\u4e86\u4e09 \u767e\u7981\u536b\u519b\uff0c\u5176\u4e2d\u6709\u597d\u51e0\u4e2a\u7edf\u9886\u90fd\u662f\u9ad8\u624b\u3002 \u3000\u3000\u518d\u8bf4\u6842\u4ef2\u660e\u5251\u968f\u8eab\u8fb9\uff0c\u7a7f\u51fa\u7a97\u6237\uff0c\u94f6\u8679\u4e00\u5377\uff0c\u524a\u65ad\u653b\u5230\u4ed6\u7684\u9762\u524d\u7684\u51e0\u822c\u5175\u5668\uff0c\u5192\u6d63\u83b2\u62a2 \u4e86\u4e0a\u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u968f\u6211\u6765\u3002\u5979\u624b\u6325\u795e\u7802\uff0c\u4e13\u5bfb\u50fb\u5f84\uff0c\u4e14\u6218\u4e14\u8d70\uff0c\u6842\u4ef2\u660e\u6a2a\u5251\u65ad\u540e\uff0c\u6321\u4f4f\u4e24 \u4fa7\u653b\u6765\u7684\u7981\u536b\u519b\u7684\u5175\u5668\u3002 \u3000\u3000\u539f\u6765\u76f8\u5e9c\u82b1\u56ed\uff0c\u5e7f\u9614\u4e4b\u6781\uff0c\u4ead\u53f0\u697c\u9601\uff0c\u5047\u5c71\u82b1\u6728\uff0c\u8fd8\u6709\u6c60\u6cbc\u5c0f\u6cb3\uff0c\u957f\u6865\u4e5d\u66f2\uff0c\u628a\u56ed\u5b50\u53d8 \u5f97\u50cf\u8ff7\u5bab\u4e00\u6837\u3002\u90a3\u4e9b\u66f2\u5f84\u5e7d\uff0c\u5de6\u7ed5\u53f3\u7ed5\uff0c\u5c31\u7b97\u957f\u4f4f\u5728\u91cc\u9762\u7684\u4eba\uff0c\u6709\u65f6\u4e5f\u4f1a\u8ff7\u8def\uff0c\u5192\u6d63\u83b2\u6df1\u8c0b \u8fdc\u8651\uff0c\u4e00\u8fdb\u4e86\u56ed\uff0c\u5c31\u9ed8\u8bb0\u9053\u8def\uff0c\u6709\u4e9b\u6b67\u8def\u6781\u591a\u4e4b\u5904\uff0c\u66f4\u753b\u4e86\u51fa\u6765\uff0c\u968f\u65f6\u5c55\u9605\u3002\u5979\u8fdb\u6765\u4e09\u56db\u4e2a \u6708\uff0c\u56ed\u5b50\u91cc\u7684\u5730\u5f62\u9053\u8def\uff0c\u5df2\u5168\u90e8\u4e86\u7136\u80f8\u4e2d\u3002\u6b64\u523b\u56ed\u5b50\u91cc\u867d\u7136\u904d\u5e03\u7981\u536b\u519b\u548c\u76f8\u5e9c\u6b66\u58eb\uff0c\u7ed9\u5979\u5de6 \u9762\u4e00\u515c\uff0c\u53f3\u9762\u4e00\u7ed5\uff0c\u4e13\u5bfb\u5c0f\u8def\uff0c\u501f\u7269\u969c\u5f62\uff0c\u7adf\u7136\u907f\u8fc7\u4e86\u56f4\u653b\uff0c\u867d\u7136\u5728\u50fb\u5f84\u5c0f\u8def\uff0c\u4e5f\u65f6\u65f6\u4f1a\u78b0 \u5230\u57cb\u4f0f\u7684\u6216\u5728\u90a3\u91cc\u7ad9\u5c97\u7684\u6b66\u571f\uff0c\u4f46\u6bcf\u5904\u6700\u591a\u4e0d\u8fc7\u4e09\u4e94\u4e2a\u4eba\uff0c\u4e0d\u7ed9\u795e\u7802\u6253\u4f24\uff0c\u4e5f\u7ed9\u6842\u4ef2\u660e\u5b9d\u5251 \u51fb\u9000\u3002\u800c\u654c\u4eba\u4e00\u9000\uff0c\u4ed6\u4eec\u53c8\u53e6\u6284\u5c0f\u8def\u8d70\u4e86\u3002 \u3000\u3000\u5192\u6d63\u83b2\u5c31\u8fd9\u6837\uff0c\u4ed7\u7740\u719f\u6089\u5730\u5f62\uff0c\u4e14\u6218\u4e14\u8d70\uff0c\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u4fbf\u5e26\u6842\u4ef2\u660e\u884c\u8fd1\u4e86\u5929\u51e4\u697c\u3002 \u4ed6\u4eec\u5728\u5047\u5c71\u6697\u62d7\u5904\u4e00\u4f0f\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53c8\u662f\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u5929\u51e4\u68f1\u9ad8\u4e03\u5c42\uff0c\u767d\u7389\u4e3a\u680f\uff0c\u98de\u6a90\u7fd8\u89d2\uff0c\u5192\u6d63\u83b2\u4e00\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u5728\u7b2c\u4e09\u5c42\u7684\u6a90\u89d2\u4e0a\uff0c\u6709\u4e24 \u4e2a\u4eba\u5728\u72e0\u72e0\u6597\u5251\uff0c\u4e00\u4e2a\u662f\u695a\u662d\u5357\uff0c\u4e00\u4e2a\u662f\u5f20\u534e\u662d\u3002\u5929\u51e4\u697c\u4e0b\u56f4\u7740\u767e\u591a\u540d\u7981\u536b\u519b\uff0c\u63a7\u5f26\u5f85\u53d1\u3002 \u695a\u662d\u5357\u5251\u62db\u51f6\u8fa3\u4e4b\u6781\uff0c\u5f20\u534e\u662d\u8fde\u8fde\u95ea\u907f\uff0c\u9669\u8c61\u73af\u751f\uff0c\u89e3\u4e86\u51e0\u62db\uff0c\u695a\u662d\u5357\u76f4\u8e0f\u4e2d\u5bab\uff0c\u4e00\u5251\u523a \u53bb\uff0c\u5f20\u534e\u662d\u7a81\u7136\u7f29\u8eab\u4e00\u8dc3\uff0c\u8df3\u4e0a\u4e86\u7b2c\u56db\u5c42\u3002\u695a\u662d\u5357\u5251\u62db\u5982\u7535\uff0c\u672c\u6765\u987a\u624b\u4e00\u6325\uff0c\u5c31\u53ef\u628a\u5f20\u534e\u662d \u53cc\u8db3\u65a9\u65ad\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u4ed6\u5374\u659c\u91cc\u4e00\u70b9\uff0c\u957f\u5251\u5728\u74e6\u77bb\u4e0a\u4e00\u78b0\uff0c\u8eab\u5b50\u50cf\u5f39\u5f13\u4e00\u6837\u5f39\u4e0a\u53bb\uff0c\u51e0\u4e4e\u548c \u5f20\u534e\u662d\u540c\u65f6\u843d\u5728\u7b2c\u56db\u5c42\u7684\u98de\u6a90\u4e4b\u4e0a\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u9e70\u7fd4\u96bc\u523a\uff0c\u53c8\u628a\u5f20\u534e\u662d\u7eca\u4f4f\u3002 \u3000\u3000\u695a\u662d\u5357\u4e3a\u4f55\u4e0d\u4e0b\u6740\u624b\uff1f\u539f\u6765\u4ed6\u7387\u4f17\u5927\u641c\u5929\u51e4\u697c\u65f6\uff0c\u9760\u9646\u660e\u547c\u58f0\u6307\u70b9\uff0c\u7a7f\u5165\u5185\u58c1\u590d\u9053\uff0c\u53d1 \u73b0\u4e86\u5f20\u534e\u662d\uff0c\u8ba4\u51fa\u4ed6\u662f\u5927\u95f9\u4e94\u53f0\u5c71\u65f6\uff0c\u884c\u523a\u591a\u94ce\u7684\u51f6\u624b\u4e4b\u4e00\uff0c\u4e5f\u662f\u5728\u540e\u6765\u6e05\u51c9\u5bfa\u65f6\u548c\u6613\u5170\u73e0 \u540c\u8def\u7684\u90a3\u4e2a\u4eba\u3002\u5fc3\u4e2d\u5927\u559c\uff0c\u60f3\u9053\uff1a\u201c\u5373\u7b97\u6293\u4e0d\u7740\u5973\u8d3c\uff0c\u6293\u7740\u6b64\u4eba\u4e5f\u662f\u4e00\u5927\u529f\u52b3\u3002\u201d\u56e0\u6b64\u53ea\u60f3 \u751f\u64d2\uff0c\u4e0d\u613f\u5c06\u4ed6\u6bd9\u547d\u3002 \u3000\u3000\u5f20\u534e\u662d\u6b66\u529f\u4e0d\u5f31\uff0c\u5251\u6cd5\u5df2\u5f97\u201c\u65e0\u6781\u5251\u201d\u7cbe\u9ad3\uff0c\u867d\u7136\u4e0d\u662f\u695a\u662d\u5357\u5bf9\u624b\uff0c\u4f46\u695a\u662d\u5357\u60f3\u628a\u4ed6\u751f \u64d2\uff0c\u5374\u4e5f\u4e0d\u6613\uff0c\u695a\u662d\u5357\u8fde\u7528\u7c98\u3001\u7ede\u3001\u514b\u5236\u51e0\u79cd\u624b\u6cd5\uff0c\u60f3\u628a\u5f20\u534e\u662d\u7684\u5251\u51fb\u51fa\u624b\u53bb\uff0c\u5f20\u534e\u662d\u5c01\u95ed \u4e25\u5bc6\uff0c\u5728\u7b2c\u56db\u5c42\u7684\u98de\u6a90\u4e0a\uff0c\u53c8\u62c6\u4e86\u4e8c\u4e09\u5341\u62db\u3002\u695a\u662d\u5357\u52c3\u7136\u5927\u6012\uff0c\u5251\u6cd5\u7a81\u53d8\uff0c\u5982\u75be\u98ce\u66b4\u96e8\uff0c\u5251 \u5149\u98d8\u5ffd\uff0c\u6fc0\u6218\u4e2d\u4e00\u67c4\u5251\u5c31\u4f3c\u5316\u6210\u5341\u51e0\u67c4\u4e00\u6837\uff0c\u5f20\u534e\u662d\u53ea\u89c1\u5230\u5904\u5251\u82b1\u9519\u843d\uff0c\u4e71\u6d12\u4e0b\u6765\uff0c\u4e00\u4e2a\u63aa \u624b\u4e0d\u53ca\uff0c\u5de6\u81c2\u4e2d\u4e86\u4e00\u5251\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u53c8\u5012\u7ffb\u4e0a\u7b2c\u4e94\u5c42\u7684\u98de\u6a90\u4e4b\u4e0a\u3002 \u3000\u3000\u695a\u662d\u5357\u89c1\u751f\u64d2\u4e0d\u6613\uff0c\u6076\u5ff5\u987f\u751f\uff0c\u4ed6\u60f3\u5148\u628a\u5f20\u534e\u662d\u523a\u4f24\uff0c\u7136\u540e\u518d\u6d3b\u6349\u4ed6\u3002\u54ea\u6599\u5f20\u534e\u662d\u9a81\u52c7 \u5f02\u5e38\uff0c\u4e2d\u4e86\u5251\uff0c\u7adf\u7136\u80fd\u98de\u8eab\u4e0a\u5c4b\u3002\u695a\u662d\u5357\u5982\u4f55\u80af\u653e\u4ed6\u8d70\uff0c\u8f7b\u8f7b\u4e00\u7eb5\uff0c\u4e5f\u98de\u63a0\u4e0a\u7b2c\u4e94\u5c42\uff0c\u800c\u4e14 \u62a2\u5148\u4e00\u6b65\uff0c\u622a\u7740\u4e86\u4ed6\u7684\u9000\u8def\uff0c\u8981\u4ed6\u80cc\u5411\u697c\u5916\uff0c\u66f4\u96be\u9632\u5b88\u3002 \u3000\u3000\u6842\u5192\u4e8c\u4eba\uff0c\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u6b63\u5f85\u51fa\u624b\uff0c\u5ffd\u7136\u5728\u7b2c\u516d\u5c42\u697c\u4e2d\uff0c\u51b2\u51fa\u4e00\u4e2a\u5c11\u5973\uff0c\u53cc\u8db3\u4e00\u70b9\u767d \u7389\u680f\u6746\uff0c\u5982\u71d5\u5b50\u822c\u659c\u63a0\u4e0b\u6765\uff0c\u4e00\u53e3\u77ed\u5251\u5f80\u695a\u662d\u5357\u5251\u4e0a\u4e00\u78b0\uff0c\u53ea\u89c1\u706b\u661f\u7eb7\u98de\uff0c\u695a\u662d\u5357\u7684\u5251\u7ed9\u65ab \u4e86\u4e00\u9053\u7f3a\u53e3\uff0c\u8fd9\u5c11\u5973\u6b63\u662f\u4ed6\u8981\u8ffd\u6355\u7684\u6613\u5170\u73e0\u3002\u6613\u5170\u73e0\u9010\u697c\u641c\u7d22\uff0c\u627e\u4e0d\u7740\u5f20\u534e\u662d\uff0c\u5f85\u4e0a\u5230\u5929\u51e4 \u697c\u7b2c\u516d\u5c42\u65f6\uff0c\u695a\u662d\u5357\u5df2\u7387\u4f17\u56f4\u5230\u3002 \u3000\u3000\u6613\u5170\u73e0\u4f0f\u5728\u516d\u697c\uff0c\u8eb2\u5728\u51e0\u76c6\u76c6\u666f\u4e4b\u540e\uff0c\u51ed\u680f\u4e0b\u671b\uff0c\u89c1\u5f20\u534e\u662d\u88ab\u695a\u662d\u5357\u9010\u5c42\u8ffd\u9010\uff0c\u5f62\u52bf\u5371 \u6b86\uff0c\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u80fd\u5192\u9669\u51fa\u51fb\u4e86\u3002 \u3000\u3000\u695a\u662d\u5357\u4e00\u89c1\u6613\u5170\u73e0\u73b0\u8eab\uff0c\u987f\u65f6\u79fb\u8f6c\u6708\u6807\uff0c\u957f\u5251\u4e00\u6454\uff0c\u5530\uff01\u5530\u5530\uff01\u4e00\u8fde\u51e0\u5251\uff0c\u76f4\u6307\u6613\u5170\u73e0 \u8981\u5bb3\uff0c\u8fd9\u65f6\u5f20\u534e\u662d\u53c8\u5df2\u7ffb\u4e0a\u7b2c\u516d\u5c42\u53bb\u4e86\u3002 \u3000\u3000\u6613\u5170\u73e0\u6b66\u529f\u8981\u6bd4\u5f20\u534e\u662d\u7a0d\u597d\u4e00\u70b9\uff0c\u4f46\u695a\u662d\u5357\u7acb\u5fc3\u628a\u5979\u64d2\u62ff\uff0c\u62db\u62db\u51f6\u8fa3\uff0c\u5341\u6570\u62db\u8fc7\u540e\uff0c\u6613 \u5170\u73e0\u62b5\u654c\u4e0d\u4f4f\uff0c\u98de\u8eab\u4e0a\u4e86\u7b2c\u516d\u5c42\uff0c\u53ea\u89c1\u5f20\u534e\u662d\u6b63\u5728\u5305\u624e\u4f24\u53e3\u3002 \u3000\u3000\u6613\u5170\u73e0\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u600e\u4e48\u4e86\uff1f\u201d\u5f20\u534e\u662d\u89c1\u5979\u4ed3\u60f6\u4e4b\u60c5\uff0c\u6ea2\u4e8e\u8a00\u8868\uff0c\u5fc3\u4e2d\u611f\u52a8\uff0c\u75db\u695a\u5168 \u6d88\uff0c\u957f\u5251\u4e00\u6446\uff0c\u9053\uff1a\u201c\u4e0d\u59a8\u4e8b\uff01\u201d\u4e24\u4eba\u8fd8\u672a\u8c08\u5f97\u4e24\u53e5\uff0c\u695a\u662d\u5357\u53c8\u5df2\u7a9c\u4e86\u4e0a\u6765\uff0c\u5251\u52bf\u4f38\u5f00\u52bf\u5982 \u6d6a\u6d8c\uff0c\u6613\u5170\u73e0\u77ed\u5251\u4e00\u622a\uff0c\u5f20\u534e\u662d\u500f\u5730\u4e00\u77ee\u8eab\u8eaf\uff0c\u4e00\u62db\u201c\u94fa\u5730\u9526\u201d\uff0c\u731d\u65a9\u695a\u662d\u5357\u53cc\u8db3\u3002\u695a\u662d\u5357 \u597d\u751f\u4e86\u5f97\uff0c\u659c\u91cc\u4e00\u5251\uff0c\u8f7b\u70b9\u6613\u5170\u73e0\u8109\u95e8\uff0c\u8feb\u5f97\u6613\u5170\u73e0\u8f6c\u8eab\u8eb2\u5f00\uff0c\u4ed6\u7acb\u65f6\u715e\u8eab\u6b62\u6b65\uff0c\u5251\u62db\u4e00 \u53d8\uff0c\u201c\u5012\u679d\u5782\u67f3\u201d\u5411\u4e0b\u4e00\u65cb\u4e00\u64a9\uff0c\u5f20\u534e\u662d\u7684\u5251\u7ed9\u64a9\u4e0a\u5929\u51e4\u697c\u7684\u9876\u5c42\u3002\u6613\u5170\u73e0\u56de\u5251\u62fc\u547d\u6321\u4f4f\uff0c \u5f20\u534e\u662d\u98de\u8eab\u4e0a\u4e86\u9876\u697c\uff0c\u6613\u5170\u73e0\u4e0e\u695a\u662d\u5357\u4e5f\u7d27\u8ddf\u7740\u7a9c\u4e86\u4e0a\u7c73\u3002 \u3000\u3000\u5f20\u534e\u662d\u8fd9\u6b21\u4e0d\u6562\u518d\u5192\u9669\u8fdb\u62db\uff0c\u4ed7\u7740\u6613\u5170\u73e0\u7684\u5b9d\u5251\u5728\u6b63\u9762\u906e\u62e6\uff0c\u5c55\u5f00\u201c\u65e0\u6781\u5251\u201d\u7684\u7cbe\u5999\u62db \u6570\uff0c\u914d\u5408\u4fa7\u8f70\u3002\u695a\u662d\u5357\u4ee5\u4e00\u654c\u4e8c\uff0c\u5140\u662f\u653b\u591a\u5b88\u5c11\u3002 \u3000\u3000\u4e09\u4eba\u8d70\u9a6c\u706f\u4f3c\u7684\u5728\u5929\u51e4\u697c\u9876\u5927\u6218\uff0c\u695a\u662d\u5357\u867d\u5360\u4e0a\u98ce\uff0c\u4e00\u65f6\u95f4\u5374\u4e5f\u5948\u4f55\u4ed6\u4eec\u4e24\u4eba\u4e0d\u5f97\uff01\u8fd9 \u65f6\u5728\u7b2c\u4e09\u5c42\u697c\u98de\u63a0\u51fa\u56db\u6761\u4eba\u5f71\uff0c\u4e24\u4e2a\u662f\u9646\u660e\u9646\u4eae\uff0c\u53e6\u5916\u4e24\u4e2a\u662f\u7981\u536b\u519b\u4e2d\u6570\u4e00\u6570\u4e8c\u7684\u9ad8\u624b\u3002\u4ed6 \u4eec\u521a\u624d\u7559\u5728\u4e09\u697c\u7684\u590d\u58c1\u91cc\u641c\u7d22\u5f20\u534e\u662d\u201c\u4f59\u515a\u201d\uff0c\u641c\u4e86\u534a\u5929\uff0c\u557b\u65e0\u4eba\u8ff9\uff0c\u662f\u4ee5\u8d76\u4e0a\u6765\u5e2e\u624b\u3002 \u3000\u3000\u6842\u5192\u4e8c\u4eba\u4f0f\u5728\u5c71\u77f3\u6697\u52a9\u4e4b\u5904\uff0c\u89c1\u5929\u51e4\u697c\u9876\u695a\u662d\u5357\u8d8a\u6218\u8d8a\u51f6\uff0c\u5192\u6d63\u83b2\u4e00\u63a8\u6842\u4ef2\u660e\u9053\uff1a\u201c\u4f60 \u5feb\u4e0a\u53bb\uff0c\u82e5\u6551\u5f97\u4ed6\u4eec\u4e0b\u6765\uff0c\u5c31\u8d76\u5feb\u5954\u56de\u6b64\u5904\uff0c\u968f\u6211\u95ef\u51fa\u56ed\u5b50\u3002\u201d \u3000\u3000\u697c\u4e0b\u7684\u7981\u536b\u519b\u5f15\u9888\u4e0a\u671b\uff0c\u7ed9\u5929\u51e4\u697c\u9876\u7684\u6076\u6218\uff0c\u5413\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u4e2a\u4e2a\u5c4f\u606f\u4ee5\u89c2\uff0c\u6839\u672c\u5c31\u6ca1 \u6ce8\u610f\u5230\u9644\u8fd1\u5047\u5c71\uff0c\u8fd8\u4f0f\u6709\u4e24\u540d\u201c\u654c\u4eba\u201d\uff0c\u6842\u4ef2\u660e\u731b\u5730\u51b2\u4e86\u51fa\u6765\uff0c\u5728\u7981\u536b\u519b\u5934\u4e0a\uff0c\u98de\u63a0\u800c\u8fc7\u3002 \u8eab\u6cd5\u8fc5\u75be\u5230\u6781\uff0c\u597d\u51e0\u4e2a\u7981\u536b\u519b\u53ea\u89c9\u5934\u9876\u4e00\u75db\u3002\u62ac\u5934\u671b\u65f6\uff0c\u6842\u4ef2\u660e\u5df2\u501f\u4ed6\u4eec\u7684\u5934\u9885\uff0c\u4f5c\u4e3a\u201c\u8df3 \u677f\u201d\uff0c\u8dc3\u4e0a\u5929\u51e4\u697c\u53bb\u4e86\u3002 \u3000\u3000\u7981\u536b\u519b\u54d7\u7136\u5927\u547c\uff0c\u7bad\u5982\u96e8\u53d1\uff0c\u6842\u4ef2\u660e\u53f3\u624b\u6325\u52a8\u817e\u86df\u5b9d\u5251\uff0c\u4e00\u9053\u957f\u8679\uff0c\u62a4\u5b9a\u8eab\u8eaf\uff0c\u7bad\u4e00\u89e6 \u53ca\uff0c\u4fbf\u7ed9\u622a\u65ad\u98de\u5c04\u51fa\u53bb\uff1b\u5de6\u624b\u6263\u7740\u4e09\u679a\u91d1\u73af\uff0c\u811a\u6b65\u4e0d\u505c\uff0c\u4ecd\u7136\u4e00\u5c42\u5c42\u5730\u98de\u8dc3\u4e0a\u53bb\uff0c\u7247\u523b\u4e4b \u95f4\uff0c\u63a0\u4e0a\u7b2c\u56db\u5c42\u7684\u98de\u6a90\uff0c\u5f13\u7bad\u4e4b\u529b\uff0c\u5df2\u5f31\u5f97\u591a\uff0c\u4f73\u4ef2\u660e\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u7981\u536b\u519b\u7edf\u9886\uff0c\u521a \u521a\u98de\u8eab\u5230\u8fbe\u9876\u5c42\u3002\u6842\u4ef2\u660e\u5de6\u624b\u4e00\u626c\uff0c\u90a3\u5458\u7edf\u9886\u6b63\u60f3\u633a\u5251\u524d\u6251\uff0c\u731b\u7136\u540e\u5fc3\u4e00\u9635\u5267\u75db\uff0c\u4e00\u4e2a\u5012\u683d \u8471\u4ece\u5929\u51e4\u697c\u9876\u8dcc\u4e86\u4e0b\u6765\uff0c\u7981\u536b\u519b\u53c8\u662f\u4e00\u9635\u54d7\u7136\u5927\u547c\uff0c\u63a5\u5230\u624b\u65f6\uff0c\u90a3\u5458\u7edf\u9886\u65e9\u5df2\u6c14\u7edd\u3002 \u3000\u3000\u9646\u660e\u9646\u4eae\u521a\u521a\u8d76\u4e0a\u4e94\u5c42\uff0c\u731b\u89c1\u6842\u4ef2\u660e\u98de\u8eab\u4e0a\u6765\uff0c\u5fc3\u4e2d\u5927\u60ca\uff0c\u4e00\u7f29\u8eab\u8eb2\u8fdb\u697c\u53bb\uff0c\u6842\u4ef2\u660e\u7ffb \u4e0a\u4e94\u697c\uff0c\u4e5f\u4e0d\u7406\u4ed6\u4eec\uff0c\u5de6\u624b\u4e00\u626c\uff0c\u53c8\u662f\u4e00\u679a\u91d1\u73af\uff0c\u5411\u521a\u4e0a\u9876\u697c\u7684\u53e6\u4e00\u4e2a\u7981\u536b\u519b\u7edf\u9886\u6253\u53bb\uff0c\u4e0d \u6599\u8fd9\u4eba\u5374\u662f\u4e00\u6d41\u9ad8\u624b\uff0c\u540d\u53eb\u80e1\u5929\u67f1\uff0c\u5728\u7981\u536b\u519b\u4e2d\uff0c\u9664\u6389\u695a\u662d\u5357\u548c\u5f20\u627f\u658c\u5916\uff0c\u5c31\u6570\u5230\u4ed6\u3002\u4ed6\u4f7f \u7684\u662f\u4e00\u6761\u8f6f\u97ad\uff0c\u8f6f\u97ad\u4e00\u6325\uff0c\u5c31\u628a\u91d1\u73af\u5377\u53bb\u3002\u6842\u4ef2\u660e\u864e\u543c\u4e00\u58f0\uff0c\u8eab\u5f62\u5e76\u4e0d\u505c\u7559\uff0c\u50cf\u5f29\u7bad\u4e00\u822c\u76f4 \u51b2\u4e0a\u9876\u5c42\uff0c\u80e1\u5929\u67f1\u4e0d\u77e5\u4ed6\u4f7f\u7684\u4e43\u662f\u5b9d\u5251\uff0c\u6dae\u7684\u4e00\u97ad\u731b\u626b\u8fc7\u53bb\uff0c\u5251\u5149\u97ad\u5f71\u4e2d\u80e1\u5929\u67f1\u60ca\u53eb\u4e00\u58f0\uff0c \u8fde\u9000\u4e09\u6b65\uff0c\u97ad\u68a2\u4e00\u6bb5\u5df2\u7ed9\u524a\u65ad\u3002\u6842\u4ef2\u660e\u8de8\u8fdb\u4e00\u6b65\uff0c\u9884\u6263\u5728\u5de6\u624b\u624b\u5fc3\u7684\u7b2c\u4e09\u679a\u91d1\u73af\uff0c\u731b\u5730\u5c04\u5411 \u695a\u662d\u5357\u540e\u5fc3\u7a74\u9053\u3002 \u3000\u3000\u695a\u662d\u5357\u6fc0\u6218\u5f20\u534e\u662d\u6613\u5170\u73e0\u4e8c\u4eba\uff0c\u6b63\u81ea\u62a2\u5f97\u5148\u624b\uff0c\u5251\u5149\u970d\u970d\uff0c\u653b\u52bf\u51cc\u5389\uff0c\u5ffd\u542c\u8111\u540e\u98ce\u751f\uff0c \u53cd\u624b\u4e00\u6284\uff0c\u5c06\u91d1\u73af\u63a5\u5728\u624b\u4e2d\uff0c\u5251\u52bf\u4e00\u7f13\uff0c\u6613\u5170\u73e0\u5df2\u62a2\u51fa\u5708\u5b50\uff0c\u89e3\u4e86\u695a\u662d\u5357\u7684\u653b\u52bf\u3002 \u3000\u3000\u6842\u4ef2\u660e\u91d1\u73af\u6253\u51fa\uff0c\u548c\u8eab\u4ec6\u4e0a\uff0c\u5ffd\u89c1\u695a\u662d\u5357\u53cd\u624b\u4e00\u63b7\uff0c\u4e00\u5708\u91d1\u5149\u6289\u7740\u5578\u58f0\u8fce\u9762\u98de\u6765\uff0c\u52b2\u9053 \u751a\u5927\u3002\u6842\u4ef2\u660e\u77e5\u9053\u662f\u4ed6\u63a5\u4e86\u81ea\u5df1\u7684\u91d1\u73af\uff0c\u53cd\u6253\u81ea\u5df1\uff0c\u53ea\u662f\u542c\u98ce\u8fa8\u5668\uff0c\u695a\u7167\u5357\u7684\u6697\u5668\u529f\u529b\u6bd4\u81ea \u5df1\u9ad8\u51fa\u8bb8\u591a\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u5b9d\u5251\u4e00\u6325\uff0c\u5c06\u91d1\u73af\u5288\u6210\u4e24\u7247\u3002 \u3000\u3000\u6613\u5170\u73e0\u4e00\u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\uff0c\u695a\u662d\u5357\u56de\u5251\u6a2a\u524a\uff0c\u6613\u5170\u73e0\u8d81\u52bf\u7a7f\u51fa\u5de6\u4fa7\uff0c\u62a2\u4e86\u6709\u5229\u65b9\u4f4d\uff0c\u5927 \u58f0\u53eb\u9053\uff1a\u201c\u4ef2\u660e\uff0c\u5de6\u53f3\u5939\u51fb\uff0c\u5feb\uff01\u5feb\uff01\u6842\u4ef2\u660e\u53cc\u8db3\u4e00\u8df3\uff0c\u907f\u8fc7\u8f6f\u97ad\u7f20\u6253\uff0c\u8eab\u5b50\u817e\u7a7a\uff0c\u624b\u4e2d\u957f \u5251\u4fef\u51b2\u800c\u4e0b\uff0c\u8fd9\u4e00\u5251\u6b63\u662f\u201c\u653b\u654c\u4e4b\u6240\u5fc5\u6551\u201d\uff0c\u89e3\u4e86\u5f20\u534e\u662d\u56f0\u5371\u3002\u695a\u662d\u5357\u4e00\u4e2a\u65cb\u98ce\u75be\u8f6c\uff0c\u5de6\u5de6 \u53f3\u53f3\uff0c\u5404\u523a\u4e24\u5251\uff0c\u75be\u5982\u95ea\u7535\uff0c\u6321\u4f4f\u4e86\u4e24\u7ffc\u7684\u8fdb\u653b\u3002\u8fd9\u65f6\u6842\u4ef2\u660e\u5df2\u8865\u4e0a\u4e86\u5f20\u534e\u662d\u7684\u7a7a\u6863\uff0c\u5f20\u534e \u662d\u62bd\u51fa\u8eab\u6765\uff0c\u62e6\u963b\u80e1\u5929\u67f1\u7684\u653b\u6251\u3002 \u3000\u3000\u4e00\u5251\u98de\u6765\uff0c\u5f62\u52bf\u7acb\u53d8\uff1b\u521a\u624d\u662f\u695a\u662d\u5357\u5360\u4e0a\u98ce\uff0c\u73b0\u5728\u5374\u662f\u611f\u5230\u5e94\u4ed8\u8270\u96be\u4e86\u3002\u6842\u4ef2\u660e\u6613\u5170\u73e0 \u4e8c\u4eba\uff0c\u5251\u6cd5\u90fd\u6709\u9ad8\u6df1\u9020\u8be3\uff0c\u4e0e\u695a\u662d\u5357\u76f8\u5dee\u4e0d\u8fdc\uff0c\u66f4\u52a0\u4e0a\u4e24\u4eba\u6240\u4f7f\u7684\u90fd\u662f\u5b9d\u5251\uff0c\u8fd9\u4e00\u5de6\u53f3\u5939 \u51fb\uff0c\u5389\u5bb3\u975e\u5e38\u3002\u695a\u662d\u5357\u51fa\u5c3d\u5168\u529b\uff0c\u5c61\u9047\u9669\u62db\uff0c\u5e78\u4ed6\u529f\u529b\u6781\u9ad8\uff0c\u706b\u5019\u8001\u5230\uff0c\u4f7f\u7684\u5c3d\u662f\u9634\u9669\u6bd2\u8fa3 \u7684\u62db\u6570\uff0c\u4e92\u76f8\u7275\u5236\uff0c\u4ee5\u4e00\u654c\u4e8c\uff0c\u5c1a\u81ea\u652f\u6491\u5f97\u4f4f\u3002 \u3000\u3000\u5f20\u534e\u662d\u72ec\u6218\u80e1\u5929\u67f1\uff0c\u5374\u662f\u5904\u5728\u4e0b\u98ce\uff0c\u80e1\u5929\u67f1\u8fd9\u6761\u8f6f\u97ad\uff0c\u4f7f\u5f97\u5f97\u5fc3\u5e94\u624b\uff0c\u864e\u864e\u751f\u98ce\uff0c\u97ad\u5f71 \u7ffb\u98de\uff0c\u6781\u4e3a\u51f6\u731b\u3002\u5f20\u534e\u662d\u7684\u5185\u5bb6\u5251\u6cd5\uff0c\u867d\u7136\u4e5f\u5df1\u6709\u4e86\u76f8\u5f53\u706b\u5019\uff0c\u65e0\u5948\u8fde\u756a\u6076\u6218\u4e4b\u540e\uff0c\u52a0\u4e0a\u5de6 \u81c2\u53d7\u4f24\uff0c\u7adf\u662f\u62b5\u6321\u4e0d\u4f4f\uff0c\u7ed9\u4ed6\u4e00\u6b65\u6b65\u8feb\u51fa\u5916\u9762\uff0c\u518d\u9000\u51e0\u6b65\uff0c\u5c31\u8981\u8dcc\u843d\u697c\u4e0b\u3002 \u3000\u3000\u6613\u5170\u73e0\u89c1\u72b6\u5927\u6025\uff0c\u8fd9\u65f6\u697c\u4e0b\u53c8\u6709\u51e0\u540d\u9ad8\u624b\uff0c\u4e00\u5c42\u5c42\u5730\u8df3\u7eb5\u4e0a\u6765\uff0c\u6842\u4ef2\u660e\u5927\u559d\u4e00\u58f0\u201c\u8d70\u201d \u817e\u86df\u5251\u500f\u5730\u4e00\u7ffb\uff0c\u628a\u695a\u662d\u5357\u8feb\u9000\u4e00\u6b65\uff0c\u8fc5\u5982\u5de8\u9e70\uff0c\u5728\u53f3\u4fa7\u75be\u51b2\u800c\u51fa\uff0c\u624b\u8d77\u4e00\u5251\uff0c\u76f4\u671d\u80e1\u5927\u67f1 \u80cc\u540e\u201c\u98ce\u5e9c\u7a74\u201d\u523a\u53bb\u3002\u80e1\u5929\u67f1\u5927\u5f2f\u8170\uff0c\u6025\u65cb\u8eab\uff0c\u907f\u8fc7\u8fd9\u5251\uff0c\u6842\u4ef2\u660e\u5df2\u62d6\u7740\u5f20\u534e\u662d\u75be\u51b2\u800c\u4e0b\uff0c \u957f\u5251\u4e00\u70b9\u7b2c\u516d\u5c42\u7684\u6a90\u89d2\uff0c\u75be\u7684\u7ffb\u4e0b\u4e86\u7b2c\u4e94\u5c42\u3002\u4e24\u540d\u7981\u536b\u519b\u7edf\u9886\u521a\u81ea\u56db\u697c\u8df3\u4e0a\uff0c\u6842\u4ef2\u660e\u5de6\u624b\u4e00 \u653e\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4ece\u90a3\u8fb9\u8df3\u4e0b\uff01\u201d\u4ed6\u5934\u4e0b\u811a\u4e0a\uff0c\u81ea\u7b2c\u4e94\u5c42\u697c\u76f4\u8df3\u4e0b\u53bb\uff0c\u534a\u7a7a\u4e2d\u4e0e\u90a3\u4e24\u4eba\u8fce\u4e2a\u6b63 \u7740\uff0c\u53f3\u624b\u5251\u523a\uff0c\u5de6\u624b\u638c\u5288\uff0c\u5251\u662f\u7a00\u4e16\u4e4b\u5b9d\uff0c\u638c\u662f\u9e70\u722a\u795e\u529f\uff0c\u90a3\u4e24\u540d\u7edf\u9886\u5982\u4f55\u62b5\u6321\u5f97\u4f4f\uff1f\u4e00\u4e2a \u88ab\u5b9d\u5251\u5bf9\u80f8\u7a7f\u8fc7\uff0c\u4e00\u4e2a\u88ab\u4e94\u6307\u6293\u7834\u4e86\u5929\u7075\u76d6\uff0c\u4e24\u5177\u5c38\u8eab\uff0c\u970e\u65f6\u8dcc\u843d\u697c\u4e0b\uff01 \u3000\u3000\u6842\u4ef2\u660e\u4e00\u8dc3\u800c\u4e0b\uff0c\u5b9d\u5251\u4e00\u6325\uff0c\u6740\u5f00\u8840\u8def\uff0c\u5f20\u534e\u662d\u8ddf\u5728\u80cc\u540e\uff0c\u5ffd\u542c\u5f97\u6613\u5170\u73e0\u5c16\u53eb\u4e4b\u58f0\uff0c\u5979 \u662f\u521a\u521a\u8eab\u5f62\u7740\u5730\uff0c\u5c31\u7ed9\u695a\u662d\u5357\u8ffd\u4e0a\u4e86\u3002 \u3000\u3000\u6613\u5170\u73e0\u77ed\u5251\u4e00\u8361\uff0c\u201c\u8fce\u98ce\u626b\u5c18\u201d\uff0c\u4f46\u542c\u5f97\u5251\u5c16\u4e0a\u201c\u55e1\u55e1\u201d\u4e00\u9635\u5578\u58f0\uff0c\u51e0\u6761\u5175\u5203\uff0c\u6216\u7ed9\u524a \u65ad\uff0c\u6216\u7ed9\u8361\u5f00\u3002\u77ed\u5251\u4e00\u65cb\uff0c\u84e6\u89c9\u9510\u98ce\u659c\u5439\uff0c\u695a\u662d\u5357\u957f\u5251\u5df2\u662f\u582a\u582a\u523a\u5230\uff01 \u3000\u3000\u6613\u5170\u73e0\u4e00\u58f0\u5c16\u53eb\uff0c\u6842\u4ef2\u660e\u62fc\u547d\u51b2\u6765\u3002\u5ffd\u5730\u91cc\uff0c\u5047\u5c71\u77f3\u4e0a\uff0c\u75be\u7684\u53c8\u51b2\u51fa\u4e00\u6761\u4eba\u5f71\uff0c\u53cc\u624b\u8fde \u626c\uff0c\u7981\u536b\u519b\u201c\u54ce\u54df\u201d\u8fde\u58f0\uff0c\u7eb7\u7eb7\u95ea\u907f\uff0c\u8fd9\u4eba\u6b63\u662f\u5192\u6d63\u83b2\u3002\u5979\u4ee5\u593a\u547d\u795e\u7802\uff0c\u4e13\u6253\u7981\u536b\u519b\u9762\u76ee\uff0c \u597d\u4e0d\u5389\u5bb3\uff01\u795e\u7802\u4e00\u6d12\u5c31\u662f\u4e00\u628a\uff0c\u867d\u7136\u4e0d\u80fd\u53ca\u8fdc\uff0c\u53ef\u662f\u7528\u6765\u6551\u4eba\uff0c\u4ee5\u5be1\u654c\u4f17\uff0c\u5374\u6709\u5947\u6548\u3002 \u3000\u3000\u695a\u662d\u5357\u4e00\u5251\u628a\u6613\u5170\u73e0\u903c\u5f00\uff0c\u5de6\u624b\u4e94\u6307\u5982\u94a9\uff0c\u4fbf\u6765\u786c\u62a2\u6613\u5170\u73e0\u7684\u5b9d\u5251\u3002\u5192\u6d63\u83b2\u5288\u9762\u4e00\u628a\u795e \u7802\uff0c\u695a\u662d\u5357\u8f7b\u8f7b\u4e00\u95ea\uff0c\u6492\u638c\u6253\u51fa\uff0c\u638c\u98ce\u5c06\u795e\u7802\u9707\u843d\u5730\u9762\u3002\u8fd9\u65f6\u53ea\u542c\u5f97\u80cc\u540e\u4e00\u58f0\u5927\u543c\uff0c\u6842\u4ef2\u660e \u7684\u817e\u86df\u5b9d\u5251\u5982\u4e00\u9053\u91d1\u86c7\uff0c\u659c\u91cc\u98de\u6765\uff0c\u695a\u662d\u5357\u5012\u63d0\u9752\u950b\uff0c\u5f80\u4e0a\u4e00\u6302\u89e3\u5f00\u4e86\u6842\u4ef2\u660e\u653b\u52bf\uff0c\u6613\u5170\u73e0 \u5530\u7684\u4e00\u5251\uff0c\u53c8\u731b\u5411\u524d\u5fc3\u63b7\u6765\uff0c\u695a\u662d\u5357\u811a\u5c16\u70b9\u5730\uff0c\u63a0\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u6842\u4ef2\u660e\u3001\u6613\u5170\u73e0\u3001\u5f20\u534e\u662d\u4e09 \u4eba\uff0c\u5df2\u968f\u7740\u5192\u6d63\u83b2\u51b2\u51fa\u53bb\u4e86\uff01 \u3000\u3000\u695a\u662d\u5357\u5927\u6012\uff0c\u5fd9\u559d\u4eca\u9646\u660e\u9646\u4eae\u968f\u540c\u8ffd\u8d76\uff0c\u8fd8\u6709\u51e0\u4e2a\u7981\u536b\u519b\u7684\u9ad8\u624b\uff0c\u4e5f\u7eb7\u633a\u5175\u5203\uff0c\u4e0a\u524d\u64d2 \u62ff\u3002\u5192\u6d63\u83b2\u5bf9\u56ed\u4e2d\u9053\u8def\uff0c\u975e\u5e38\u719f\u6089\uff0c\u53ea\u89c1\u5979\u8eab\u5982\u5f69\u8776\u7a7f\u82b1\uff0c\u65f6\u800c\u7eb5\u9ad8\uff0c\u65f6\u800c\u8dc3\u4f4e\uff0c\u7a7f\u8fc7\u5047\u5c71 \u5ca9\u6d1e\uff0c\u7ed5\u8fc7\u7f8a\u80a0\u5c0f\u5f84\uff0c\u7a7f\u82b1\u62c2\u67f3\uff0c\u66f2\u6298\u8fc2\u56de\uff0c\u5e26\u9886\u4f17\u4eba\uff0c\u76f4\u5954\u56ed\u5916\uff0c\u7981\u536b\u519b\u7ed9\u5979\u629b\u5728\u80cc\u540e\uff0c \u53ea\u6709\u695a\u662d\u5357\u7b49\u51e0\u4e2a\u9ad8\u624b\uff0c\u8fd8\u80fd\u7d27\u7d27\u7f00\u7740\u3002\u5192\u6d63\u83b2\u4e00\u89c1\u695a\u662d\u5357\u8feb\u8fd1\uff0c\u5c31\u662f\u4e00\u628a\u795e\u7802\uff0c\u867d\u7136\u6253\u4e0d \u7740\u4ed6\uff0c\u53ef\u4e5f\u963b\u6ede\u4e86\u4ed6\u7684\u811a\u6b65\u3002 \u3000\u3000\u6b64\u8ffd\u5f7c\u9010\uff0c\u9e4a\u8d77\u5154\u843d\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u4ed6\u4eec\u5df2\u6740\u5230\u56ed\u5b50\u7684\u897f\u95e8\uff0c\u5b88\u95e8\u7684\u6b66\u58eb\uff0c\u89c1\u4ed6\u4eec\u4f3c\u75af\u864e \u4e00\u822c\uff0c\u54ea\u6562\u963b\u6321\u3002\u6842\u4ef2\u660e\u201c\u6392\u5c71\u8fd0\u638c\u201d\uff0c\u731b\u51fb\u56ed\u95e8\uff0c\u53ea\u4e00\u4e0b\u5c31\u628a\u56ed\u95e8\u9707\u5f00\uff0c\u98de\u5954\u51fa\u53bb\u3002 \u3000\u3000\u695a\u662d\u5357\u7d27\u8ddf\u4e0d\u820d\uff0c\u5176\u65f6\u5df2\u8fd1\u4e94\u66f4\uff0c\u5343\u8857\u5bc2\u9759\uff0c\u4e07\u6237\u65e0\u58f0\uff0c\u8ffd\u8fc7\u597d\u51e0\u6761\u8857\u9053\uff0c\u8ffd\u8fdb\u4e86\u4e00\u6761 \u6398\u5934\u5c0f\u5df7\uff0c\u5df7\u7684\u4fa7\u8fb9\u662f\u4e00\u6761\u81ed\u6c9f\uff0c\u695a\u662d\u5357\u731b\u7684\u5927\u559d\u4e00\u58f0\uff0c\u63d0\u8eab\u4e0a\u5c4b\uff0c\u5c55\u5f00\u7edd\u9876\u8f7b\u529f\uff0c\u62a2\u8fc7\u4e86 \u5192\u6d63\u83b2\u7684\u524d\u5934\uff0c\u6a2a\u5251\u4e00\u7acb\uff0c\u62e6\u4f4f\u4ed6\u4eec\u3002\u80e1\u5929\u67f1\u7b49\u4e03\u516b\u540d\u9ad8\u624b\uff0c\u5219\u5835\u5728\u5df7\u5b50\u7684\u8fdb\u53e3\u3002\u5192\u6d63\u83b2\u795e \u7802\u5df2\u7ecf\u53d1\u5b8c\uff0c\u5411\u6842\u4ef2\u660e\u6253\u4e2a\u773c\u8272\uff0c\u53cc\u53cc\u633a\u5251\uff0c\u62fc\u7740\u548c\u695a\u662d\u5357\u4f5c\u4e00\u6b7b\u6218\uff0c\u80e1\u5929\u67f1\u9646\u660e\u9646\u4eae\u4e09\u4eba \u4e5f\u6251\u4e86\u4e0a\u6765\uff0c\u770b\u770b\u5c31\u8981\u6df7\u6218\u3002\u6b63\u5728\u6b64\u9645\uff0c\u5ffd\u7136\u4e00\u5bb6\u5c45\u6c11\uff0c\u5927\u95e8\u500f\u5730\u6253\u5f00\u3002 \u3000\u3000\u5c4b\u5185\u8d70\u51fa\u4e00\u8001\u4e00\u58ee\uff0c\u8001\u7684\u957f\u987b\u98d8\u62c2\uff0c\u624b\u91cc\u62ff\u7740\u4e00\u6839\u65f1\u70df\u888b\uff0c\u5438\u4e86\u51e0\u53e3\uff0c\u731b\u7684\u4e00\u5439\uff0c\u70df\u9505 \u91cc\u706b\u661f\u70b9\u70b9\uff0c\u98de\u6e85\u51fa\u6765\uff0c\u4ed6\u7adf\u62e6\u5728\u695a\u662d\u5357\u4e0e\u6842\u4ef2\u660e\u4e4b\u95f4\u3002\u53e6\u4e00\u4e2a\u662f\u5c06\u8fd1\u56db\u65ec\u7684\u4e2d\u5e74\u53cc\u5b50\uff0c\u4e5f \u62ff\u7740\u4e00\u6839\u65f1\u70df\u888b\uff0c\u53ea\u662f\u6bd4\u90a3\u8001\u7684\u5c0f\u4e86\u8bb8\u591a\u3002\u4ed6\u4e00\u51fa\u6765\uff0c\u5c31\u6307\u7740\u9646\u660e\u9646\u4eae\u9053\uff1a\u201c\u7239\uff0c\u8bbe\u9677\u9644\u5bb3 \u6211\u4eec\u7684\u662f\u8fd9\u4e24\u4e2a\u4eba\u3002\u201d\u695a\u662d\u5357\u7741\u76ee\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c\u897f\u6562\u6765\u6df7\u6270\uff1f\u201d\u4fa7\u8eab\u4e00\u5251\uff0c\u8d8a\u8fc7\u8001\u5934\uff0c\u5411 \u6842\u4ef2\u660e\u523a\u53bb\u3002\u695a\u662d\u5357\u5fc3\u9ad8\u6c14\u50b2\uff0c\u81ea\u547d\u82f1\u96c4\uff0c\u867d\u89c1\u8fd9\u4e24\u4eba\u8ff9\u72b6\u602a\u5f02\uff0c\u4f46\u5728\u672a\u77e5\u4ed6\u4eec\u7684\u6765\u5934\u865a\u5b9e \u4e4b\u524d\uff0c\u5374\u4e0d\u5c51\u5148\u4e0b\u624b\u653b\u51fb\u4ed6\u4eec\u3002 \u3000\u3000\u6842\u4ef2\u660e\u817e\u86df\u5251\u786c\u67b6\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u7237\u6015\u4f60\u4e0d\u6210\uff01\u201d\u695a\u662d\u5357\u5251\u5149\u53f6\u5200\uff0c\u907f\u5f00\u5b9d\u5251\uff0c\u970e\u773c\u4e4b \u95f4\uff0c\u8fde\u53d1\u4e09\u62db\uff0c\u6842\u4ef2\u660e\u9000\u540e\u4e24\u6b65\uff0c\u6613\u5170\u73e0\u5192\u6d63\u83b2\u53cc\u53cc\u62a2\u8fc7\u6765\uff0c\u7981\u536b\u519b\u7684\u9ad8\u624b\uff0c\u4e5f\u4ece\u90a3\u8fb9\u5df7\u53e3 \u6d8c\u4e0a\u3002 \u3000\u3000\u4e2d\u5e74\u6c49\u5b50\u53c8\u6307\u7740\u6842\u4ef2\u660e\u9053\uff1a\u201c\u7239\u7239\uff0c\u4ed6\u662f\u6211\u4eec\u7684\u6069\u4eba\u3002\u201d\u8001\u5934\u4e00\u626c\u70df\u888b\uff0c\u559d\u9053\uff1a\u201c\u6211\u4eec \u6069\u6028\u5206\u660e\uff0c\u5148\u62a5\u6069\uff0c\u540e\u62a5\u6028\u3002\u201d\u659c\u91cc\u4e00\u8dc3\uff0c\u94c1\u70df\u888b\u75be\u7684\u70b9\u6253\u695a\u662d\u5357\u7684\u201c\u9b42\u53f0\u7a74\u201d\uff0c\u695a\u662d\u5357\u5927 \u6012\uff0c\u6a2a\u5251\u4e00\u5c01\uff0c\u53ea\u89c9\u6765\u4eba\u8155\u529b\u751a\u4e3a\u6c89\u96c4\uff0c\u5251\u7ed9\u8361\u5f00\uff0c\u864e\u53e3\u4e5f\u7ed9\u9707\u5f97\u53d1\u70ed\uff01 \u3000\u3000\u8fd9\u4e00\u8001\u4e00\u58ee\uff0c\u8001\u7684\u5c31\u662f\u5357\u4eac\u9556\u884c\u7684\u9886\u8896\u5b5f\u6b66\u5a01\uff0c\u58ee\u6c49\u662f\u4ed6\u7684\u513f\u4e8e\u5b5f\u575a\u3002\u5b5f\u6b66\u5a01\u548c\u77f3\u632f\u98de \u5e76\u79f0\u5357\u5317\u4e8c\u540d\u9556\u5934\uff0c\u4fdd\u9556\u4ece\u672a\u5931\u624b\u3002\u8fd9\u6b21\u5b5f\u575a\u7ed9\u9646\u660e\u9646\u4eae\u8bf1\u53bb\u66ff\u7eb3\u5170\u76f8\u5e9c\u4fdd\u4e09\u5341\u516d\u540d\u5c11\u5973\uff0c \u51e0\u4e4e\u6298\u5728\u6c5f\u5317\u4e09\u9b54\u624b\u4e0a\u3002\u56de\u6765\u4e00\u8bf4\uff0c\u5b5f\u6b66\u5a01\u5e74\u7eaa\u867d\u8001\uff0c\u706b\u6c14\u6781\u5927\uff0c\u867d\u4e0d\u6562\u62db\u60f9\u76f8\u5e9c\uff0c\u5374\u6068\u900f \u4e86\u9646\u660e\u9646\u4eae\u3002\u4ed6\u8bf4\u4e0d\u7ba1\u9646\u5bb6\u5144\u5f1f\u662f\u4ec0\u4e48\u76f8\u5e9c\u6b66\u5e08\uff0c\u4ed6\u4eec\u603b\u7b97\u662f\u6c5f\u6e56\u4eba\u7269\uff0c\u8fd9\u6b21\u85cf\u5978\u8bf1\u9556\uff0c\u4ee4 \u6b66\u5a01\u9556\u5c40\u51fa\u4e11\u3002\u975e\u627e\u4ed6\u4eec\u7406\u8bba\u4e0d\u53ef\uff0c\u4ed6\u5c01\u4e86\u9556\u5c40\uff0c\u5e26\u5b50\u8fdb\u4eac\uff0c\u6cbf\u9014\u627e\u5bfb\u4eba\u5996\u5927\u9b54\u90dd\u98de\u51e4\u4e0d \u7740\uff0c\u6b63\u662f\u4e00\u809a\u76ae\u6ca1\u597d\u6c14\u3002\u5230\u4e86\u4eac\u5e08\uff0c\u5c31\u60f3\u53bb\u627e\u4e8c\u9646\u3002\u5012\u662f\u4ed6\u7684\u513f\u5b50\u628a\u7ec6\uff0c\u529d\u9053\uff1a\u201c\u76f8\u5e9c\u95e8\u9ad8 \u72d7\u5927\uff0c\u4f60\u8001\u4eba\u5bb6\u53bb\u627e\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e0d\u89c1\u4f60\u4e5f\u6ca1\u6cd5\u3002\u4f55\u51b5\u4ed6\u4eec\u662f\u6b66\u6797\u5c0f\u8f88\uff0c\u4f60\u53bb\u627e\u4ed6\u4eec\uff0c\u5148\u5c31\u6298 \u4e86\u8eab\u4efd\u3002\u201d\u5b5f\u6b66\u5a01\u4e00\u60f3\uff0c\u4e5f\u662f\u9053\u7406\u3002\u5f53\u4e0b\u548c\u513f\u5b50\u76f8\u5546\uff0c\u51b3\u5b9a\u7b2c\u4e00\u6b65\u5148\u53bb\u627e\u77f3\u632f\u98de\uff0c\u7531\u4ed6\u51fa \u5934\uff0c\u67ec\u9080\u9556\u884c\u540c\u9053\u548c\u4e8c\u9646\u5230\u4f1a\u8d74\u5bb4\u3002\u77f3\u632f\u98de\u662f\u4eac\u57ce\u7684\u6b66\u6797\u9886\u8896\uff0c\u4e8c\u9646\u867d\u662f\u76f8\u5e9c\u6559\u5934\uff0c\u4f46\u5e76\u65e0 \u5b98\u804c\uff0c\u540c\u662f\u201c\u6df7\u6c5f\u6e56\u996d\u201d\u7684\uff0c\u4e0d\u5bb9\u4ed6\u4e0d\u8d74\u4f1a\u3002\u5230\u65f6\uff0c\u5b5f\u6b66\u5a01\u5c31\u8981\u4e8c\u9646\u78d5\u5934\u966a\u7f6a\uff0c\u5426\u5219\u5c31\u8981\u628a \u4ed6\u4eec\u8d76\u51fa\u4eac\u57ce\u3002 \u3000\u3000\u695a\u662d\u5357\u5927\u641c\u5929\u51e4\u697c\u4e4b\u591c\uff0c\u6b63\u662f\u5b5f\u5bb6\u7236\u5b50\u521a\u5230\u4eac\u57ce\u4e4b\u65f6\u3002\u4ed6\u4eec\u662f\u4e2d\u5348\u65f6\u5206\u5230\u4eac\u7684\uff0c\u793c\u7269\u672a \u529e\uff0c\u56e0\u6b64\u51c6\u5907\u5230\u7b2c\u4e8c\u5929\u624d\u53bb\u62dc\u4f1a\u77f3\u632f\u98de\u3002\u5f53\u665a\u5148\u4f4f\u5728\u9556\u5c40\u4e00\u4f4d\u65e7\u4f19\u8ba1\u7684\u5bb6\u91cc\uff0c\u534a\u591c\u91cc\u5ffd\u95fb\u8ffd \u9010\u4e4b\u58f0\uff0c\u5b5f\u8001\u5934\u548c\u513f\u5b50\u62ab\u8863\u8d77\u89c6\uff0c\u6b63\u662f\u964c\u8def\u76f8\u9022\uff0c\u4ec7\u4eba\u6069\u4eba\u90fd\u78b0\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u5b5f\u6b66\u5a01\u7ed9\u695a\u662d\u5357\u6a2a\u5251\u4e00\u5c01\uff0c\u94c1\u70df\u888b\u4e5f\u51e0\u4e4e\u7529\u624b\uff0c\u4ed6\u4eec\u4e24\u4eba\u529f\u529b\u6089\u654c\uff0c\u5f7c\u6b64\u90fd\u5403\u4e86\u4e00\u60ca\u3002 \u5b5f\u8001\u5934\u5b50\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u94c1\u70df\u888b\u201c\u4e91\u9e70\u4e09\u821e\u201d\uff0c\u4e00\u62db\u4e09\u5f0f\uff0c\u4e8c\u6b21\u8fdb\u6251\uff01 \u3000\u3000\u695a\u662d\u5357\u4e00\u6b65\u4e0d\u8ba9\uff0c\u638c\u4e2d\u5251\u5411\u4e0a\u4e00\u7ffb\uff0c\u201c\u62e8\u8349\u5bfb\u86c7\u201d\uff0c\u5251\u5c16\u7ade\u5411\u5b5f\u6b66\u5a01\u7684\u624b\u8155\u5212\u53bb\uff0c\u5b5f\u6b66 \u5a01\u94c1\u70df\u888b\u78fa\u91cc\u4e00\u78d5\uff0c\u201c\u5012\u6253\u91d1\u949f\u201d\uff1b\u695a\u662d\u5357\u5927\u559d\u4e00\u58f0\u201c\u6492\u624b\u201d\uff01\u8eab\u5f62\u4e00\u4fa7\uff0c\u5251\u62db\u5982\u7535\uff0c\u500f\u5730 \u6539\u5212\u4e3a\u622a\uff0c\u201c\u987a\u624b\u63a8\u821f\u201d\uff0c\u6a2a\u622a\u8fc7\u53bb\uff03\u5408\u6b66\u5a01\u7a81\u7684\u53f3\u8db3\u6491\u5730\uff0c\u5de6\u8db3\u8e6c\u7a7a\uff0c\u5934\u5411\u540e\u4ef0\uff0c\u4f7f\u51fa \u201c\u94c1\u677f\u6865\u201d\u7edd\u6280\uff0c\u5251\u98ce\u62c2\u9762\u800c\u8fc7\uff0c\u968f\u5373\u5411\u53f3\u4e00\u503e\uff0c\u8eab\u5f62\u66b4\u8d77\uff0c\u8fd9\u624d\u51b7\u7b11\u4e00\u58f0\u7b54\u9053\uff1a\u201c\u4e0d\u89c1 \u5f97\uff01\u201d\u5de6\u8db3\u8d81\u52bf\u8e22\u51fa\uff0c\u695a\u662d\u5357\u5251\u62db\u4f7f\u8001\uff0c\u5de6\u624b\u6a2a\u638c\u5982\u5200\uff0c\u5411\u4e0b\u6025\u5288\uff0c\u5b5f\u6b66\u5a01\u53f3\u8db3\u53c8\u8d77\uff0c\u8fde\u73af \u98de\u817f\uff0c\u5feb\u75be\u5f02\u5e38\u3002\u695a\u662d\u5357\u65e0\u6cd5\u8eb2\u95ea\uff0c\u5530\u5730\u5411\u4e0a\u4e00\u7a9c\uff0c\u5e73\u5730\u62d4\u8d77\u4e24\u4e08\u591a\u9ad8\u3002\u8fd9\u65f6\u6842\u4ef2\u660e\u6613\u5170\u73e0 \u7b49\u4eba\u5df2\u548c\u7981\u536b\u519b\u9ad8\u624b\u6253\u5728\u4e00\u56e2\uff0c\u6842\u4ef2\u660e\u767e\u5fd9\u4e2d\u817e\u624b\u6253\u51fa\u4e00\u679a\u91d1\u73af\uff0c\u54ea\u6599\u695a\u662d\u5357\u672c\u9886\u5b9e\u5728\u9ad8 \u5f3a\uff0c\u534a\u7a7a\u4e2d\u4f38\u624b\u4e00\u63a5\uff0c\u5c31\u628a\u91d1\u73af\u63a5\u8fc7\uff0c\u53cd\u624b\u6253\u51fa\u3002 \u3000\u3000\u5b5f\u6b66\u5a01\u521a\u62a2\u4e0a\u4e00\u6b65\uff0c\u84e6\u89c1\u6697\u5668\u98de\u6765\uff0c\u94c1\u70df\u888b\u5f80\u5916\u4e00\u7529\uff0c\u628a\u91d1\u73af\u6253\u6210\u788e\u7247\u3002 \u3000\u3000\u695a\u662d\u5357\u89d1\u51c6\u65b9\u4f4d\uff0c\u5f80\u4e0b\u4e00\u843d\uff0c\u6b63\u597d\u843d\u5728\u5b5f\u6b66\u5a01\u80cc\u540e\uff0c\u4e3e\u624b\u4e00\u5251\uff0c\u201c\u7389\u87d2\u7ffb\u8eab\u201d\uff0c\u76f4\u5954\u5b5f \u6b66\u54b8\u53f3\u80a9\u523a\u53bb\uff0c\u559d\u9053\uff1a\u201c\u518d\u63a5\u8fd9\u4e00\u62db\uff01\u201d\u5b5f\u6b66\u5a01\u559d\u9053\uff1a\u201c\u8c01\u4eba\u6015\u4f60\uff01\u201d\u94c1\u70df\u888b\u5f80\u540e\u4e00\u78d5\uff0c\u53c8 \u628a\u695a\u662d\u5357\u7684\u5251\u8361\u5f00\uff0c\u8eab\u8eaf\u534a\u8f6c\uff0c\u201c\u4ed9\u59d1\u9001\u5b50\u201d\uff0c\u659c\u51fb\u695a\u662d\u5357\u7684\u201c\u5206\u6c34\u7a74\u201d\u3002\u695a\u662d\u5357\u5927\u6012\uff0c\u95ea \u8eab\u8fdb\u5251\uff0c\u5251\u8d70\u8fde\u73af\uff0c\u70b9\u3001\u523a\u3001\u5288\u3001\u64a9\uff0c\u7fe9\u5982\u60ca\u9e3f\uff0c\u77eb\u82e5\u6e38\u9f99\uff0c\u5929\u5c71\u5251\u6cd5\u4f7f\u5f97\u51fa\u795e\u5165\u5316\uff03\u5408\u6b66 \u5a01\u4e00\u6746\u70df\u888b\uff0c\u70b9\u6253\u4e09\u5341\u516d\u9053\u5927\u7a74\uff0c\u53f3\u638c\u4e5f\u637b\u7740\u5251\u8bc0\uff0c\u5e26\u5b88\u5e26\u653b\u3002\u4ed6\u51e0\u5341\u5e74\u529f\u529b\u975e\u540c\u5c0f\u53ef\uff0c\u62db \u6570\u6c89\u7a33\u4e4b\u6781\uff0c\u695a\u662d\u5357\u867d\u5360\u4e86\u516b\u6210\u653b\u52bf\uff0c\u5982\u4e5f\u65e0\u6cd5\u653b\u5165\uff01 \u3000\u3000\u6842\u5192\u4e8c\u4eba\u7528\u7684\u90fd\u662f\u5b9d\u5251\uff0c\u5f53\u8005\u62ab\u9761\uff0c\u5b5f\u575a\u5f97\u5230\u4ed6\u4eec\u89e3\u56f0\uff0c\u89c1\u7236\u4eb2\u53ea\u6709\u62db\u67b6\u7684\u529f\u592b\uff0c\u5fc3\u4e2d \u5927\u6025\uff0c\u6df1\u6015\u8001\u7236\u5e74\u8fc8\uff0c\u654c\u4eba\u592a\u5f3a\uff0c\u62b5\u6321\u4e0d\u4f4f\u3002\u6842\u4ef2\u660e\u89c1\u5b5f\u575a\u7126\u6025\u4e4b\u60c5\uff0c\u5b9d\u5251\u4e00\u64a4\uff0c\u5fae\u7b11\u8bf4 \u9053\uff1a\u201c\u6211\u53bb\u66ff\u56de\u5b5f\u8001\u82f1\u96c4\uff01\u201d \u3000\u3000\u6842\u4ef2\u660e\u662f\u4e2a\u8bc6\u8d27\u7684\u4eba\uff0c\u5b5f\u6b66\u5a01\u66ff\u4ed6\u6321\u4f4f\u695a\u662d\u5357\u65f6\uff0c\u4ed6\u53ea\u770b\u4e86\u51e0\u62db\uff0c\u5c31\u77e5\u6b64\u8001\u529f\u529b\u975e\u540c\u5c0f \u53ef\uff0c\u7eb5\u4e0d\u80fd\u80dc\uff0c\u4e5f\u4e0d\u4f1a\u843d\u8d25\u3002\u56e0\u6b64\u653e\u5fc3\u8ba9\u5b5f\u6b66\u5a01\u548c\u695a\u662d\u5357\u62fc\u6597\u3002\u6b64\u523b\u89c1\u5b5f\u575a\u7126\u6025\uff0c\u867d\u7136\u6697\u7b11 \u4ed6\u505a\u513f\u5b50\u7684\u4e5f\u4e0d\u77e5\u9053\u7236\u4eb2\u7684\u771f\u5b9e\u672c\u9886\uff0c\u4f46\u4e8e\u7406\u4e8e\u60c5\uff0c\u90fd\u8981\u53bb\u66ff\u56de\u4ed6\u4e86\u3002 \u3000\u3000\u695a\u662d\u5357\u867d\u7136\u62a2\u4e86\u653b\u52bf\uff0c\u989d\u4e0a\u5df2\u5fae\u5fae\u89c1\u6c57\uff0c\u4e00\u89c1\u6302\u4ef2\u660e\u633a\u5251\u91cd\u6765\uff0c\u6b63\u81ea\u7740\u6025\uff0c\u5b5f\u6b66\u5a01\u5ffd\u5730 \u4e00\u58f0\u957f\u5578\uff0c\u70df\u6746\u865a\u70b9\uff0c\u9000\u51fa\u5708\u5b50\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u8001\u5934\u5b50\u4ece\u4e0d\u4ee5\u4e8c\u6253\u4e00\uff0c\u4f60\u82e5\u4e0d\u670d\uff0c\u53ef\u5230\u5357 \u4eac\u6b66\u5a01\u9556\u5c40\u627e\u6211\uff01\u201d\u8fd9\u65f6\u6842\u4ef2\u660e\u5df2\u548c\u695a\u662d\u5357\u4ea4\u4e0a\u4e86\u624b\uff0c\u53cc\u65b9\u5251\u62db\u90fd\u8f9b\u8fa3\u4e4b\u6781\u3002\u695a\u662d\u5357\u51dd\u795e\u5bf9 \u654c\uff0c\u6839\u672c\u5c31\u4e0d\u53bb\u542c\u8fd9\u8001\u5934\u5b50\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u695a\u662d\u5357\u7ecf\u8fc7\u4e00\u8f6e\u6076\u6597\uff0c\u6b64\u6d88\u5f7c\u957f\uff0c\u518d\u6218\u6842\u4ef2\u660e\uff0c\u53ea\u80fd\u582a\u582a\u6253\u6210\u5e73\u624b\u3002\u6842\u4ef2\u660e\u8d81\u6b64\u673a\u4f1a\uff0c \u6539\u5b88\u4e3a\u653b\uff0c\u5fc3\u4e2d\u7545\u5feb\u4e4b\u6781\u3002 \u3000\u3000\u5b5f\u6b66\u5a01\u8f6c\u4e2a\u65b9\u5411\uff0c\u604d\u5982\u9e70\u7275\u7a7f\u6797\uff0c\u98de\u63a0\u8fc7\u53bb\uff0c\u843d\u5728\u9646\u660e\u9646\u4eae\u8eab\u8fb9\uff0c\u70df\u6746\u5012\u6301\uff0c\u53cc\u638c\u9f50 \u8d77\uff0c\u811a\u8e0f\u4e2d\u5bab\u6b3a\u8eab\u76f4\u8fdb\uff0c\u9646\u670b\u6325\u81c2\u4e00\u683c\uff0c\u5b5f\u6b66\u5a01\u5de6\u638c\u659c\u5288\u80f8\u524d\uff0c\u53f3\u638c\u4e94\u6307\u5982\u94a9\uff0c\u76f4\u6293\u80c1\u4e0b\u3002 \u9646\u660e\u8eab\u5f62\u4e00\u4f4e\uff0c\u6b63\u5f85\u907f\u62db\u8fdb\u62db\uff0c\u5df1\u7ed9\u4e00\u628a\u6293\u4f4f\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002\u5b5f\u6b66\u5a01\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u5df2\u62a2 \u5230\u9646\u4eae\u8eab\u8fb9\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u5288\u4ed6\u4e0b\u76d8\uff0c\u9646\u4eae\u65bd\u5c55\u9e70\u722a\u529f\u592b\uff0c\u5f80\u5916\u4e00\u62ff\uff0c\u54ea\u77e5\u5b5f\u6b66\u5a01\u8fd9\u4e00\u624b\uff0c\u6697 \u85cf\u5c0f\u5929\u661f\u638c\u529b\uff0c\u5c31\u662f\u91d1\u949f\u7f69\u94c1\u5e03\u886b\u7684\u6a2a\u7ec3\u529f\u592b\uff0c\u4e00\u51fb\u4e4b\u4e0b\uff0c\u4e5f\u8981\u62c6\u6563\uff0c\u4f55\u51b5\u9646\u4eae\u7684\u9e70\u722a\u529f\u5e76 \u672a\u5230\u5bb6\uff0c\u53cc\u638c\u4e00\u4ea4\uff0c\u864e\u53e3\u9178\u9ebb\uff0c\u767b\u65f6\u5c31\u7ed9\u5b5f\u6b66\u5a01\u6263\u4f4f\u4ed6\u7684\u8109\u95e8\u3002\u5b5f\u6b66\u5a01\u4e24\u624b\u4e00\u6325\uff0c\u9646\u5bb6\u5144\u5f1f \u63a5\u8fde\u629b\u51fa\uff0c\u63b7\u4e0b\u4e86\u81ed\u6c34\u6c9f\u4e2d\u3002 \u3000\u3000\u5b5f\u6b66\u5a01\u5feb\u610f\u4e4b\u6781\uff0c\u624b\u628a\u70df\u6746\uff0c\u70b9\u70df\u72c2\u62bd\uff0c\u4e00\u53e3\u53e3\u9752\u70df\u55b7\u5c06\u51fa\u53bb\u3002\u7981\u536b\u519b\u89c1\u4ed6\u5982\u6b64\u5a01\u6b66\uff0c \u5fc3\u91cc\u6253\u7a81\u3002\u80e1\u5929\u67f1\u6296\u624b\u4e00\u97ad\uff0c\u628a\u5192\u6d63\u83b2\u8feb\u9000\u4e00\u6b65\uff0c\u60f3\u51b2\u8fc7\u53bb\u548c\u695a\u662d\u5357\u6c47\u5408\uff0c\u5b5f\u6b66\u5a01\u5927\u559d\u4e00 \u58f0\uff0c\u4e00\u53e3\u6d53\u70df\u5288\u9762\u55b7\u53bb\uff0c\u80e1\u5929\u67f1\u545b\u51fa\u58f0\u6765\uff0c\u6613\u5170\u73e0\u4fa7\u9762\u5530\u7684\u4e00\u5251\u523a\u51fa\uff0c\u80e1\u5929\u67f1\u53cd\u624b\u4e00\u97ad\uff0c\u53c8 \u7ed9\u5b9d\u5251\u65a9\u53bb\u4e00\u622a\uff0c\u5f20\u534e\u662d\u5728\u80cc\u540e\u4e00\u811a\u98de\u8d77\uff0c\u80e1\u5929\u67f1\u8fde\u53d7\u632b\u6298\uff0c\u731d\u4e0d\u53ca\u9632\uff0c\u540e\u5fc3\u7ed9\u72e0\u72e0\u8e22\u4e86\u4e00 \u811a\uff0c\u8eab\u5b50\u6251\u524d\uff0c\u5b5f\u6b66\u5a01\u8d76\u4e0a\u4e00\u6b65\uff0c\u5355\u638c\u4e00\u6258\uff0c\u559d\u58f0\u201c\u8d77\uff01\u201d\u80e1\u5929\u67f1\u817e\u4e91\u9a7e\u96fe\u822c\u7684\uff0c\u8eab\u5b50\u76f4\u98de \u51fa\u6765\uff0c\u7ee7\u9646\u5bb6\u5144\u5f1f\u4e4b\u540e\uff0c\u8dcc\u8fdb\u4e86\u81ed\u6c34\u6c9f\u4e2d\u3002 \u3000\u3000\u695a\u662d\u5357\u4eca\u665a\u8fde\u9047\u52b2\u654c\uff0c\u53c8\u60ca\u53c8\u6012\u3002\u6842\u4ef2\u660e\u5982\u521d\u751f\u4e4b\u728a\uff0c\u4e58\u7740\u4ed6\u6c14\u529b\u4e0d\u52a0\uff0c\u4e00\u53e3\u817e\u86df\u5b9d\u5251 \u6a2a\u626b\u76f4\u5317\uff0c\u51cc\u5389\u65e0\u80fd\u3002\u4ed6\u7684\u4e94\u79bd\u5251\u6cd5\uff0c\u672c\u662f\u4ee5\u653b\u52bf\u64c5\u957f\uff0c\u5f80\u65f6\u53ea\u56e0\u529f\u529b\u4e0d\u5982\u695a\u662d\u5357\uff0c\u6240\u8c13 \u201c\u68cb\u9ad8\u4e00\u7740\uff0c\u7f1a\u624b\u7f1a\u811a\u3002\u201d\u8feb\u5f97\u4f9d\u51cc\u672a\u98ce\u6240\u6559\uff0c\u4ed7\u5b9d\u5251\u4e4b\u529b\uff0c\u575a\u5b88\u8c0b\u548c\u3002\u800c\u4eca\u695a\u662d\u5357\u4e45\u6218\u529b \u75b2\uff0c\u6842\u4ef2\u660e\u5fc3\u96c4\u80c6\u58ee\uff0c\u7740\u7740\u548c\u4ed6\u62a2\u653b\uff0c\u628a\u695a\u662d\u5357\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff01 \u3000\u3000\u695a\u662d\u5357\u773c\u89c2\u56db\u9762\uff0c\u89c1\u6700\u5f97\u529b\u7684\u52a9\u624b\u80e1\u5929\u67f1\uff0c\u4e5f\u7ed9\u629b\u5165\u81ed\u6c34\u6c9f\u4e2d\u3002\u7981\u536b\u519b\u53ea\u5269\u4e0b\u56db\u4e94\u4e2a \u4eba\uff0c\u8d8a\u53d1\u62b5\u6321\u4e0d\u4f4f\u3002\u4ed6\u957f\u5251\u4e00\u6325\uff0c\u731b\u7684\u559d\u58f0\uff1a\u201c\u6d51\u5c0f\u5b50\uff0c\u4f60\u522b\u7316\u72c2\u3002\u201d\u731b\u4e0b\u8fa3\u624b\uff0c\u865a\u6643\u4e00 \u62db\uff0c\u5f15\u5f97\u6842\u4ef2\u660e\u6a2a\u5251\u62db\u67b6\uff0c\u5530\u7684\u4e00\u5251\uff0c\u75be\u5982\u95ea\u7535\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u4fbf\u4ece\u4fa7\u9762\u62a2\u4e86\u8fdb\u6765\uff0c\u76f4\u523a\u6842\u4ef2 \u660e\u80a9\u540e\u7684\u98ce\u5e9c\u7a74\u3002\u6842\u4ef2\u660e\u56de\u5251\u4e0d\u53ca\uff0c\u770b\u770b\u8981\u906d\u6bd2\u624b\u3002\u53ea\u542c\u5f97\u4e00\u58f0\u65ad\u559d\uff1a\u201c\u4f60\u4e5f\u522b\u72c2\u3002\u201d\u539f\u6765 \u5b5f\u6b66\u5a01\u65e9\u5df2\u62a2\u6b65\u8fc7\u6765\uff0c\u6765\u5f97\u6070\u662f\u65f6\u5019\uff0c\u94c1\u70df\u79c6\u201c\u6a2a\u67b6\u91d1\u6881\u201d\uff0c\u786c\u78d5\u695a\u662d\u5357\u7684\u5251\uff0c\u695a\u662d\u5357\u77e5\u4ed6 \u6c14\u529b\u6c89\u96c4\uff0c\u4e0d\u613f\u548c\u4ed6\u5bf9\u8017\uff0c\u970d\u5730\u4e00\u4e2a\u77ee\u8eab\uff0c\u98ce\u8f66\u822c\u8f6c\u5c06\u51fa\u53bb\uff0c\u957f\u5251\u8d77\u5904\uff0c\u5411\u6613\u5170\u73e0\u5192\u6d63\u83b2\u5404 \u523a\u4e24\u5251\uff0c\u4e24\u4eba\u88ab\u8feb\u95ea\u907f\uff0c\u695a\u662d\u5357\u5df2\u8131\u51fa\u91cd\u56f4\uff0c\u4e3e\u5251\u53eb\u9053\uff1a\u201c\u70b9\u5b50\u68d8\u624b\uff0c\u6682\u4e14\u6536\u5175\uff01\u201d\u5e26\u9886\u7981 \u536b\u519b\u9ad8\u624b\uff0c\u8ffd\u51fa\u5df7\u53e3\u3002\u5b5f\u6b66\u5a01\u6740\u5f97\u6027\u8d77\uff0c\u7d27\u8ffd\u4e0d\u820d\uff0c\u4ed6\u68cb\u9022\u5bf9\u624b\uff0c\u6280\u75d2\u5f02\u5e38\uff0c\u53eb\u9053\uff1a\u201c\u6211\u548c \u4f60\u5355\u6253\u72ec\u6597\u4e00\u573a\u5982\u4f55\uff1f\u201d\u695a\u662d\u5357\u6012\u9053\uff1a\u201c\u6211\u695a\u662d\u5357\u8fd8\u80fd\u6015\u4f60\u8fd9\u7cdf\u8001\u5934\u5b50\uff1f\u4f60\u8981\u5355\u6253\u72ec\u6597\uff0c\u8fc7 \u4e24\u5929\u54b1\u4eec\u7ea6\u4e2a\u573a\u6240\uff0c\u6253\u4e2a\u75db\u5feb\u3002\u201d\u5b5f\u6b66\u5a01\u4e00\u542c\u695a\u662d\u5357\u81ea\u62a5\u540d\u5934\uff0c\u4e0d\u89c9\u5446\u4f4f\u3002 \u3000\u3000\u5b5f\u6b66\u5a01\u3001\u695a\u662d\u5357\u90fd\u662f\u6c5f\u6e56\u4e0a\u7684\u6210\u540d\u4eba\u7269\uff0c\u867d\u7136\u4ee5\u5f80\u672a\u78b0\u8fc7\u9762\uff0c\u5374\u662f\u5f7c\u6b64\u90fd\u77e5\u9053\u5bf9\u65b9\u7684\u58f0 \u540d\u3002\u5982\u4eca\u695a\u662d\u5357\u81ea\u62a5\u540d\u5934\uff0c\u5b5f\u6b66\u5a01\u5fc3\u60f3\uff1a\u771f\u662f\u8001\u7cca\u6d82\u4e86\uff0c\u6b64\u4eba\u5251\u6cd5\u5982\u6b64\u795e\u5999\uff0c\u600e\u7684\u60f3\u4e0d\u8d77\u662f \u4ed6\uff1f\u6c5f\u6e56\u4e0a\u4f7f\u5251\u7684\u4eba\u867d\u591a\uff0c\u6700\u8d1f\u76db\u540d\u7684\u5374\u53ea\u6709\u4e09\u4e2a\uff0c\u4e00\u4e2a\u662f\u5085\u9752\u4e3b\uff0c\u4e00\u4e2a\u662f\u77f3\u632f\u98de\uff0c\u53e6\u4e00\u4e2a \u5c31\u662f\u4ed6\uff08\u51cc\u672a\u98ce\u662f\u540e\u8d77\u4e4b\u79c0\uff0c\u5728\u897f\u5317\u867d\u4eab\u5927\u540d\uff0c\u5b5f\u6b66\u5a01\u5374\u4e0d\u6df1\u77e5\uff09\u3002\u5085\u3001\u77f3\u4e8c\u4eba\u7684\u5251\u6cd5\uff0c\u81ea \u5df1\u65e9\u5df2\u89c1\u8fc7\u3002\u5982\u4eca\u770b\u6765\uff0c\u6b64\u4eba\u5251\u6cd5\u7edd\u4e0d\u5728\u5085\u3001\u77f3\u4e8c\u4eba\u4e4b\u4e0b\u3002\u53ea\u662f\u524d\u4e9b\u65f6\u542c\u8bf4\uff0c\u4ed6\u65e9\u5f53\u4e86\u7687\u5e1d \u7684\u7981\u536b\u519b\u7edf\u9886\uff0c\u96be\u9053\u81ea\u5df1\u5e2e\u52a9\u7684\u8fd9\u4e00\u4f19\u4eba\uff0c\u5c31\u662f\u4ed6\u8981\u8ffd\u6355\u7684\u94a6\u72af\uff1f \u3000\u3000\u5b5f\u6b66\u5a01\u867d\u7136\u662f\u8001\u5f53\u76ca\u58ee\uff0c\u4fa0\u9aa8\u8c6a\u60c5\uff0c\u4f46\u56e0\u6709\u5bb6\u6709\u4e1a\uff0c\u82e5\u8981\u4ed6\u771f\u4e2a\u548c\u671d\u5ef7\u4f5c\u5bf9\uff0c\u4ed6\u53ef\u662f\u987e \u8651\u751a\u591a\u3002\u6b64\u65f6\u542c\u695a\u662d\u5357\u9a82\u6218\uff0c\u4e0d\u89c9\u70df\u6746\u5012\u6302\uff0c\u505c\u4e86\u811a\u6b65\u3002\u5192\u6d63\u83b2\u5219\u53e6\u6709\u60f3\u6cd5\uff0c\u5979\u89c1\u695a\u662d\u5357\u867d \u8d25\uff0c\u4f46\u6025\u5207\u95f4\u8981\u632b\u6298\u4ed6\uff0c\u5374\u662f\u751a\u96be\u3002\u81ea\u5df1\u8fd9\u5e2e\u4eba\uff0c\u80fd\u9003\u8131\u5df2\u662f\u5927\u5e78\uff0c\u4f55\u5fc5\u518d\u53bb\u8ffd\u51fb\u3002\u800c\u4e14\u4eca \u665a\u7981\u536b\u519b\u7cbe\u9510\u5df2\u7ecf\u51fa\u52a8\uff0c\u7f20\u6597\u4e0b\u53bb\uff0c\u5371\u9669\u66f4\u591a\u3002\u5979\u78b0\u4e86\u78b0\u6842\u4ef2\u660e\uff0c\u8de8\u524d\u51e0\u6b65\uff0c\u5bf9\u5b5f\u6b66\u5a01\u9053\uff1a \u201c\u5b5f\u8001\u7237\u5b50\uff0c\u54b1\u4eec\u4e0d\u6253\u843d\u6c34\u72d7\uff0c\u8ba9\u4ed6\u8d70\u5427\u3002\u201d\u6842\u4ef2\u660e\u817e\u86df\u5251\u5411\u524d\u4e00\u6307\uff0c\u559d\u9053\uff1a\u201c\u5272\u9e21\u7109\u7528\u725b \u5200\uff0c\u4f60\u8981\u6bd4\u8bd5\uff0c\u5c0f\u7237\u968f\u65f6\u5949\u966a\u3002\u201d\u695a\u662d\u5357\u7b4b\u75b2\u529b\u5026\uff0c\u751f\u6015\u4ed6\u4eec\u8ffd\u51fb\u3002\u4ed6\u53ea\u662f\u4e3a\u4e86\u9762\u5b50\uff0c\u4e0d\u5f97 \u4e0d\u6545\u4f5c\u58ee\u8a00\u3002 \u3000\u3000\u800c\u4eca\u89c1\u5b5f\u6b66\u5a01\u895f\u58f0\u4e0d\u7b54\uff0c\u54ea\u6562\u9017\u7559\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u914d\uff01\u201d\u9886\u90e8\u4e0b\u98de\u8eab\u6025\u9000\uff0c\u5176\u5b9e\u4ed6 \u8fd8\u771f\u7684\u6015\u6842\u4ef2\u660e\u8ffd\u6765\uff0c\u8fde\u8dcc\u5728\u81ed\u6c34\u6c9f\u4e2d\u7684\u9646\u660e\u9646\u4eae\u7b49\u4eba\u4e5f\u987e\u4e0d\u5f97\u6551\u4e86\u3002 \u3000\u3000\u5b5f\u6b66\u5a01\u6c89\u7740\u8138\u8d76\u56de\u5c4b\u5185\uff0c\u5c4b\u4e3b\u4eba\u6b63\u63d0\u5fc3\u540a\u8138\uff0c\u501a\u95e8\u76f8\u5f85\u3002\u5b5f\u6b66\u5a01\u53eb\u4ed6\u8fde\u591c\u9003\u8d70\u3002\u5f20\u534e\u662d \u597d\u751f\u8fc7\u610f\u4e0d\u53bb\uff0c\u4e0a\u524d\u8c22\u7f6a\u3002\u5b5f\u6b66\u5a01\u9053\uff1a\u201c\u73b0\u5728\u4e5f\u4e0d\u80fd\u7406\u8fd9\u4e48\u591a\u4e86\uff0c\u4ffa\u8001\u5934\u5b50\u5192\u6627\u8bf7\u95ee\uff1a\u4f60\u4eec \u5230\u5e95\u662f\u54ea\u8def\u4eba\u7269\uff1f\u8981\u4e0a\u4f55\u65b9\uff1f\u201d\u6842\u4ef2\u660e\u62f1\u624b\u7b54\u9053\uff1a\u201c\u6211\u4eec\u662f\u674e\u6765\u4ea8\u7684\u90e8\u4e0b\uff0c\u51c6\u5907\u53bb\u6295\u5954\u77f3\u632f \u98de\u8001\u9556\u5934\u7684\u3002\u201d\u5b5f\u6b66\u5a01\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u53eb\u4e86\u51fa\u6765\uff1a\u201c\u539f\u6765\u8bf8\u4f4d\u662f\u77f3\u9556\u5934\u7684\u670b\u53cb\uff0c\u53c8\u662f\u674e\u5c06\u519b \u90e8\u4e0b\uff0c\u4ffa\u8001\u5934\u513f\u820d\u4e86\u8eab\u5bb6\u6027\u547d\uff0c\u4e5f\u503c\u5f97\u4e86\uff01\u201d\u6842\u4ef2\u660e\u5411\u4ed6\u9053\u8c22\u51fa\u624b\u76f8\u52a9\u4e4b\u6069\u3002\u5b5f\u6b66\u5a01\u62c8\u987b\u7b11 \u9053\uff1a\u201c\u4f60\u66ff\u6211\u4eec\u4fdd\u5168\u4e86\u9556\u5c40\u7684\u5a01\u540d\uff0c\u6211\u8fd8\u672a\u66fe\u5411\u4f60\u9053\u8c22\u5462\uff01\u201d \u3000\u3000\u4e00\u5e2e\u4eba\u5728\u62c2\u6653\u4e4b\u524d\u8d76\u5230\u77f3\u5bb6\u3002\u77f3\u632f\u98de\u77e5\u9053\u4ed6\u4eec\u95f9\u4e86\u8fd9\u4ef6\u5927\u4e8b\uff0c\u4e8b\u5148\u5e76\u672a\u4e0e\u4ed6\u5546\u91cf\uff0c\u9887\u4e3a \u4e0d\u5feb\u3002\u6613\u5170\u73e0\u8c22\u7f6a\u8bf4\u9053\uff1a\u201c\u6211\u662f\u6015\u7275\u7d2f\u8001\u4f2f\u3002\u201d\u77f3\u632f\u98de\u602b\u7136\u8bf4\u9053\uff1a\u201c\u6211\u548c\u5085\u9752\u4e3b\u662f\u8fc7\u547d\u7684\u4ea4 \u60c5\uff0c\u4ed6\u7684\u670b\u53cb\u95e8\u4eba\uff0c\u6211\u6562\u6536\u7559\u7684\uff0c\u5c31\u662f\u5929\u5927\u4e4b\u4e8b\uff0c\u6211\u4e5f\u6562\u62c5\u627f\uff01\u201d\u5b5f\u6b66\u5a01\u89c1\u4ed6\u5982\u6b64\u8c6a\u60c5\uff0c\u6697 \u9053\u60ed\u6127\u3002\u4e24\u8001\u5934\u6b22\u6b23\u76f8\u89c1\uff0c\u5c11\u4e0d\u5f97\u53c8\u662f\u4fc3\u819d\u957f\u8c08\u3002 \u3000\u3000\u4e14\u8bf4\u6613\u5170\u73e0\u773c\u73e0\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u4eec\u4e3a\u4ec0\u4e48\u8981\u628a\u4f60\u63a5\u51fa\u6765\u5417\uff1f\u201d\u5f20\u534e\u662d \u9762\u4e0a\u4e00\u7ea2\uff0c\u4ee5\u4e3a\u5979\u662f\u6697\u8bbd\u81ea\u5df1\u820d\u4e0d\u5f97\u516c\u4e3b\uff0c\u6240\u4ee5\u8981\u62c9\u4ed6\u51fa\u6765\u3002\u6b63\u60f3\u89e3\u91ca\uff0c\u6613\u5170\u73e0\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u6842\u5192\u4e24\u4eba\uff0c\u4e07\u91cc\u6765\u4eac\uff0c\u539f\u662f\u5949\u674e\u5c06\u519b\u548c\u5218\u5927\u59d0\u4e4b\u547d\uff0c\u60f3\u8981\u4f60\u51fa\u6765\uff0c\u7ea0\u96c6\u6c5f\u5357\u4e00\u5e26\u7684\u9c81\u738b\u65e7 \u90e8\u3002\u201d\u5f20\u534e\u662d\u9053\uff1a\u201c\u6211\u662f\u4e0a\u6708\u521a\u521a\u590d\u5143\u7684\uff0c\u4e0d\u662f\u7559\u604b\u76f8\u5e9c\u3002\u201d\u6613\u5170\u73e0\u62bf\u5634\u7b11\u9053\uff1a\u201c\u8c01\u8bf4\u4f60\u7559 \u604b\u76f8\u5e9c\u6765\u4e86\uff1f\u201d \u3000\u3000\u6696\u8272\u6b32\u5f00\uff0c\u5929\u5c06\u62c2\u6653\u3002\u6613\u5170\u73e0\u8863\u8882\u8fce\u98ce\uff0c\u795e\u60c5\u9887\u4f3c\u6709\u70b9\u6fc0\u52a8\u3002\u5f20\u534e\u662d\u671b\u7740\u8fd9\u4f4d\u795e\u79d8\u7684\u5c11 \u5973\uff08\u76f4\u5230\u73b0\u5728\u4ed6\u8fd8\u672a\u77e5\u9053\u5979\u7684\u6765\u5386\uff0c\uff09\u60f3\u8d77\u5979\u591c\u63a2\u4e94\u53f0\u5c71\u6e05\u51c9\u5bfa\u3002\u820d\u4e86\u6027\u547d\u6765\u6551\u81ea\u5df1\u7684\u5f80 \u4e8b\uff0c\u4e0d\u89c9\u795e\u601d\u604d\u998f\uff0c\u5fc3\u4e2d\u4e00\u8361\u3002\u53ea\u89c1\u6613\u5170\u73e0\u4e00\u672c\u6b63\u7ecf\u5730\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u6700\u8fd1\u7684\u60c5\u5f62\u53c8\u5df2\u53d1 \u751f\u53d8\u5316\uff0c\u9c81\u738b\u5728\u6c5f\u6e56\u7684\u65e7\u90e8\uff0c\u56e0\u4e3a\u8d81\u4e09\u85e9\u4e4b\u53d8\uff0c\u6d6e\u8d77\u5934\u6765\uff0c\u7adf\u7ed9\u6e05\u5ef7\u5927\u519b\u6253\u5f97\u4e03\u96f6\u516b\u843d\u3002\u82e5 \u60f3\u5728\u6c5f\u5357\u5927\u4e3e\uff0c\u5df2\u975e\u5bb9\u6613\u3002\u6240\u4ee5\u674e\u5c06\u519b\u7684\u90e8\u5c06\u6765\u4f20\u8fbe\u4ed6\u7684\u610f\u601d\uff0c\u8bf4\u662f\u5f53\u52a1\u4e4b\u6025\uff0c\u9996\u5728\u4fdd\u5168\u56db \u5ddd\u65b9\u9762\u7684\u5b9e\u529b\u3002\u4ed6\u60f3\u6211\u4eec\u5728\u4eac\u4e2d\u7684\u4eba\uff0c\u9009\u51fa\u4e00\u540d\u6562\u6b7b\u4e4b\u58eb\uff0c\u5e72\u4e00\u4ef6\u8f70\u8f70\u70c8\u70c8\u7684\u5927\u4e8b\uff01\u201d\u5f20\u534e \u662d\u9053\uff1a\u201c\u8981\u627e\u6562\u6b7b\u4e4b\u58eb\uff0c\u90a3\u592a\u5bb9\u6613\u4e86\uff0c\u662f\u4ec0\u4e48\u4e8b\u5462\uff1f\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u542c\u8bf4\u6e05\u5ef7\u5df2\u5185\u5b9a\u591a\u94ce\u4e3a \u5f81\u897f\u7edf\u5e05\uff0c\u7387\u9886\u516b\u65d7\u7cbe\u9510\uff0c\u5c31\u5c06\u5f00\u8d74\u897f\u5357\uff0c\u51c6\u5907\u5728\u6d88\u706d\u5434\u4e09\u6842\u7684\u540c\u65f6\uff0c\u4e5f\u628a\u674e\u5c06\u519b\u6d88\u706d\u3002\u56e0 \u6b64\u674e\u5c06\u519b\u5e0c\u671b\u6211\u4eec\u5728\u4eac\u4e2d\uff0c\u5c31\u5c06\u591a\u94ce\u8fd9\u8d3c\u5b50\u523a\u6740\uff01\u201d \u3000\u3000\u5f20\u534e\u662d\u8840\u8109\u6124\u5f20\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e8b\u5e94\u8be5\u7531\u6211\u505a\uff01\u201d\u6613\u5170\u73e0\u51c4\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u548c\u6211\u4e89 \u4e86\uff0c\u6211\u5df2\u7ecf\u5bf9\u4f17\u4eba\u8bf4\u8fc7\uff0c\u6211\u5fc5\u5b9a\u8981\u624b\u5203\u591a\u94ce\uff0c\u4e0d\u7136\u6211\u6b7b\u4e0d\u7791\u76ee\uff0c\u5728\u5165\u76f8\u5e9c\u6551\u4f60\u4e4b\u524d\uff0c\u6211\u5df2\u7ecf \u4e24\u63a2\u738b\u5e9c\uff0c\u8fd8\u548c\u591a\u94ce\u4ea4\u8fc7\u624b\u3002\u53ea\u662f\u542c\u8bf4\u4ed6\u7ecf\u8fc7\u6211\u90a3\u4e48\u4e00\u95f9\u4e4b\u540e\uff0c\u5df2\u52a0\u610f\u9632\u5907\uff0c\u4e00\u9762\u8d23\u6210\u695a\u662d \u5357\u6765\u6349\u6211\uff0c\u4e00\u9762\u7cbe\u9009\u6b66\u58eb\uff0c\u5728\u738b\u5e9c\u4e2d\u5e03\u4e0b\u5929\u7f57\u5730\u7f51\uff0c\u7b49\u6211\u4eec\u53bb\u4e0a\u94a9\u3002\u73b0\u5728\u8981\u53bb\u523a\u6740\u4ed6\uff0c\u90a3\u53ef \u662f\u6781\u4e0d\u5bb9\u6613\uff01\u201d\u5f20\u534e\u662d\u9053\uff1a\u201c\u6240\u4ee5\u8fd9\u4e8b\u60c5\u4e0d\u80fd\u5355\u72ec\u7531\u4f60\u53bb\u5e72\uff01\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u662f\u8fd9\u4e48 \u8bf4\u3002\u4f46\u674e\u5c06\u519b\u7684\u610f\u601d\u662f\uff1a\u523a\u6740\u591a\u94ce\u7684\u4eba\u5f53\u7136\u662f\u51c6\u5907\u4e0e\u4ed6\u540c\u5f52\u4e8e\u5c3d\uff0c\u727a\u7272\u8d8a\u5c11\u8d8a\u597d\u3002\u6211\u4eec\u72af\u4e0d \u7740\u727a\u7272\u8bb8\u591a\u4eba\u53bb\u6362\u4ed6\u4e00\u6761\u6027\u547d\u3002\u674e\u5c06\u519b\u8fd8\u8bf4\uff0c\u4ed6\u672c\u6765\u4e0d\u4e3b\u5f20\u6697\u6740\uff0c\u4f46\u4e3a\u4e86\u4e8b\u60c5\u7d27\u6025\uff0c\u523a\u6740\u591a \u94ce\u4e4b\u540e\uff0c\u867d\u4e0d\u80fd\u963b\u6b62\u6e05\u5ef7\u53e6\u9009\u7edf\u5e05\uff0c\u8fdb\u653b\u6211\u4eec\uff0c\u4f46\u6700\u5c11\u53ef\u62d6\u5ef6\u4e00\u4e9b\u65f6\u65e5\uff0c\u5ef6\u8fdf\u5b83\u8fdb\u519b\u7684\u65e5 \u7a0b\uff0c\u8ba9\u6211\u4eec\u53ef\u4ee5\u597d\u597d\u5e03\u7f6e\u3002\u201d\u5f20\u534e\u662d\u9053\uff1a\u201c\u5143\u8bba\u5982\u4f55\u6211\u4eec\u4e0d\u80fd\u8ba9\u4f60\u5355\u72ec\u5192\u9669\uff0c\u59d0\u59d0\uff0c\u8fd9\u4e8b\u60c5 \u8ba9\u6211\u66ff\u4f60\u505a\u4e86\u5427\uff0c\u4f60\u820d\u547d\u6551\u8fc7\u6211\uff0c\u6211\u5374\u8fd8\u672a\u66ff\u4f60\u505a\u8fc7\u534a\u70b9\u4e8b\u60c5\u3002\u201d \u3000\u3000\u5f20\u534e\u662d\u8bf4\u8fd9\u8bdd\u65f6\u5145\u6ee1\u67d4\u60c5\uff0c\u6613\u5170\u73e0\u773c\u7736\u4e00\u7ea2\uff0c\u5f3a\u5fcd\u773c\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u660e\u767d\u7684\uff0c\u8c01\u90fd\u53ef \u4ee5\u51c6\u5907\u53bb\u6b7b\uff0c\u5c31\u662f\u4f60\u4e0d\u80fd\u591f\uff01\u4f60\u662f\u5f20\u5927\u5c06\u519b\u7684\u516c\u5b50\uff0c\u4ee4\u5148\u5c0a\u7684\u90e8\u5c5e\uff0c\u73b0\u5728\u867d\u8bf4\u5df2\u4e03\u96f6\u516b\u843d\uff0c \u4f46\u6211\u4eec\u603b\u5e0c\u8fc2\u671b\u8fd8\u80fd\u7ea0\u96c6\u8d77\u6765\u3002\u8fd9\u4e00\u4ef6\u66f4\u5927\u7684\u4e8b\u60c5\u9700\u8981\u4f60\u5e72\u3002\u6240\u4ee5\u6211\u4eec\u51c6\u5907\u5728\u4eac\u57ce\u5927\u5e72\uff0c\u6740 \u6389\u591a\u94ce\u4e4b\u524d\uff0c\u5148\u8981\u628a\u4f60\u6551\u51fa\uff0c\u4f60\u5e94\u8be5\u77e5\u9053\u7eb3\u5170\u738b\u5983\uff0c\u5c31\u662f\u7eb3\u5170\u5bb0\u76f8\u7684\u5802\u59b9\uff0c\u7eb3\u5170\u5bb9\u82e5\u7684\u59d1 \u6bcd\u3002\u867d\u8bf4\u7eb3\u5170\u5bb9\u82e5\u5bf9\u4f60\u5f88\u597d\uff0c\u6211\u4eec\u603b\u4e0d\u80fd\u4e0d\u63d0\u9632\u3002\u201d\u5f20\u534e\u662d\u76ee\u4e0d\u8f6c\u775b\u5730\u6ce8\u89c6\u7740\u5979\uff0c\u89c1\u5979\u5728\u8bf4 \u5230\u201c\u7eb3\u5170\u738b\u5983\u201d\u65f6\uff0c\u58f0\u8c03\u4e00\u987f\uff0c\u5ffd\u7136\u4e00\u9897\u6cea\u73e0\uff0c\u6eda\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5f20\u534e\u662d\u84e6\u89c9\u4e00\u9635\u5bd2\u610f\uff0c\u900f\u8fc7\u5fc3\u5934\uff0c\u7a81\u7136\u60f3\u8d77\u5927\u95f9\u4e94\u53f0\u5c71\u90a3\u665a\uff0c\u88ab\u64d2\u4e4b\u540e\uff0c\u7eb3\u5170\u738b\u5983\u7adf\u7136 \u4eb2\u5230\u56da\u623f\u5c06\u4ed6\u91ca\u653e\uff0c\u8fd8\u9001\u4e86\u4ed6\u4e00\u679d\u7fe1\u7fe0\u4ee4\u7bad\u3002\u5f53\u65f6\u4ed6\u89c1\u6613\u5170\u73e0\u548c\u7eb3\u5170\u738b\u5983\u534e\u5802\u5e76\u5750\uff0c\u76ee\u8574\u6cea \u5149\uff0c\u90a3\u5947\u5f02\u7684\u795e\u60c5\u5c31\u5982\u4eca\u665a\u4e00\u6837\u3002\u4ed6\u611f\u89c9\u5230\u8fd9\u91cc\u9762\u4e00\u5b9a\u6709\u4e0d\u5bfb\u5e38\u7684\u4e8b\u60c5\uff0c\u4e0d\u7981\u8f7b\u8f7b\u62c9\u7740\u6613\u5170 \u73e0\u7684\u624b\uff0c\u51dd\u671b\u7740\u5979\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u50cf\u5929\u4e0a\u7684\u4e91\u96fe\u4e00\u822c\uff0c\u6211\u4e00\u70b9\u4e5f\u4e0d\u61c2\u5f97\u4f60\uff0c\u4f46\u6211\u5f88\u611f\u6fc0\u4f60\uff0c \u4e5f\u5f88\u4fe1\u4efb\u4f60\u3002\u4f60\u65e2\u7136\u8981\u4eb2\u81ea\u624b\u5203\u591a\u94ce\uff0c\u4e00\u5b9a\u6709\u4f60\u7684\u7f18\u6545\uff0c\u6211\u4e0d\u62e6\u963b\u4f60\uff0c\u4f46\u6211\u4e00\u5b9a\u7aed\u529b\u4fdd\u62a4 \u4f60\u3002\u201d \u3000\u3000\u6613\u5170\u73e0\u542b\u7740\u6cea\u73e0\u9053\uff1a\u201c\u4f60\u771f\u597d\uff01\u5982\u679c\u6211\u4e0d\u662f\u7a81\u7136\u6b7b\u53bb\u7684\u8bdd\uff0c\u5c06\u6765\u6211\u4f1a\u4e3a\u4f60\u628a\u4e91\u96fe\u62e8\u5f00 \u7684\u3002\u5982\u679c\u6211\u662f\u7a81\u7136\u6b7b\u53bb\u7684\u8bdd\uff0c\u90a3\u5c31\u8bf7\u4f60\u53bb\u627e\u51cc\u672a\u98ce\uff0c\u53eb\u4ed6\u5728\u6211\u7236\u4eb2\u7684\u575f\u524d\u4e0a\u9999\uff0c\u544a\u8bc9\u4ed6\uff1a\u4ed6 \u7684\u5973\u513f\u5df2\u7aed\u529b\u66ff\u4ed6\u62a5\u4ec7\u4e86\u3002\u201d\u5979\u8bf4\u5230\u6b64\u5904\uff0c\u5ffd\u53c8\u51c4\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u6211\u6700\u7231\u5170\u82b1\uff0c\u4f60 \u4e5f\u522b\u5fd8\u8bb0\u8981\u6298\u4e00\u675f\u5170\u82b1\u63d2\u5728\u6211\u7684\u5893\u524d\u3002\u201d \u3000\u3000\u8fd9\u4e00\u665a\uff0c\u5f20\u534e\u662d\u4e00\u76f4\u505a\u7740\u6076\u68a6\uff0c\u7b2c\u4e8c\u5929\u5f20\u9752\u539f\u96c6\u5408\u4f17\u4eba\u5728\u5bc6\u5ba4\u4f1a\u5546\uff0c\u4f20\u8fbe\u7684\u679c\u7136\u662f\u8981\u523a \u6740\u591a\u94ce\u7684\u547d\u4ee4\u3002\u77f3\u8001\u9556\u5934\u5728\u5317\u4eac\u7684\u540d\u6c14\u5f88\u5927\uff0c\u548c\u5b98\u65b9\u4e5f\u6709\u6765\u5f80\uff0c\u6355\u5934\u5b98\u5dee\u7b49\u95f2\u4e0d\u6562\u6765\u9a9a\u6270 \u4ed6\uff0c\u96be\u5f97\u4ed6\u8c6a\u4fa0\u5f02\u5e38\uff0c\u4e0d\u60dc\u8eab\u5bb6\u6027\u547d\uff0c\u613f\u5c3d\u63a9\u62a4\u4e4b\u8d23\u3002\u81f3\u4e8e\u5b5f\u6b66\u5a01\u7236\u5b50\uff0c\u7fa4\u96c4\u4e0d\u613f\u4ed6\u4eec\u5377\u5165 \u6f29\u6da1\uff0c\u7531\u77f3\u8001\u9556\u5934\u8bbe\u6cd5\uff0c\u5c06\u4ed6\u4eec\u5077\u5077\u9001\u51fa\u5317\u4eac\uff0c\u7531\u4ed6\u4eec\u8ff3\u81ea\u53bb\u627e\u4eba\u5996\u90dd\u98de\u51e4\uff0c\u4ee5\u62a5\u593a\u9556\u4e4b \u4ec7\u3002 \u3000\u3000\u8bdd\u5206\u4e24\u5934\uff0c\u4e14\u8bf4\u695a\u662d\u5357\u5f53\u665a\u8fde\u53d7\u632b\u6298\uff0c\u7b2c\u4e8c\u5929\u8d76\u5feb\u53bb\u89c1\u9102\u4eb2\u738b\u591a\u94ce\uff0c\u62a5\u544a\u591c\u641c\u5929\u51e4\u697c\u4e4b \u4e8b\u3002\u591a\u94ce\u542c\u8bf4\u5728\u5929\u98ce\u697c\u4e2d\uff0c\u641c\u51fa\u5973\u8d3c\u7684\u540c\u515a\uff0c\u662f\u4e2a\u5c11\u5e74\u516c\u5b50\uff0c\u5927\u4e3a\u6ce8\u610f\uff0c\u7ec6\u95ee\u76f8\u8c8c\uff0c\u5ffd\u7136\u62cd \u6848\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u5728\u4e94\u53f0\u5c71\u65f6\u66fe\u4e3a\u6211\u6240\u64d2\uff0c\u540e\u6765\u5c31\u662f\u90a3\u4e2a\u5973\u8d3c\u6551\u53bb\u7684\u3002\u201d\u695a\u662d\u5357\u544a\u8f9e\u4e4b\u540e\uff0c \u591a\u94ce\u6ee1\u6000\u7591\u8651\uff0c\u6b65\u5165\u540e\u5802\u53bb\u89c1\u592b\u4eba\u3002\u7eb3\u5170\u738b\u5983\u81ea\u5e9c\u4e2d\u5927\u95f9\u5973\u8d3c\u4e4b\u540e\uff0c\u7cbe\u795e\u4e00\u76f4\u5f88\u574f\uff0c\u597d\u4f3c\u6079 \u6079\u6b32\u75c5\u7684\u6a21\u6837\u3002\u8bf7\u5fa1\u533b\u6765\u8bca\u65ad\uff0c\u4e5f\u8bf4\u4e0d\u51fa\u4ec0\u4e48\u9053\u7406\u6765\u3002 \u3000\u3000\u7eb3\u5170\u738b\u5983\u4e00\u89c1\u591a\u94ce\u8fdb\u6765\uff0c\u5f3a\u7b11\u95ee\u9053\uff1a\u201c\u90a3\u5973\u8d3c\u6349\u5230\u4e86\u5417\uff1f\u201d\u591a\u94ce\u9053\uff1a\u201c\u8fde\u695a\u662d\u5357\u4e5f\u7ed9\u522b \u4eba\u6253\u8d25\u4e86\uff0c\u90a3\u5973\u8d3c\u539f\u6765\u8fd8\u6709\u4e00\u4e2a\u515a\u7fbd\uff0c\u5c31\u662f\u4ee5\u524d\u5728\u4e94\u53f0\u5c71\u88ab\u6211\u64d2\u4f4f\uff0c\u540e\u6765\u7a81\u7136\u88ab\u4eba\u6551\u8d70\u7684 \u4eba\u3002\u201d\u7eb3\u5170\u738b\u5983\u201c\u554a\u5440\u201d\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u8fd9\u5973\u8d3c\u771f\u662f\u5979\u4e86\uff01\u201d\u591a\u94ce\u9053\uff1a\u201c\u54ea\u4e2a \u5979\u5440\uff1f\u201d\u7eb3\u5170\u738b\u5983\u9053\uff1a\u201c\u5c31\u662f\u5f53\u665a\u6765\u6551\u90a3\u5c11\u5e74\u7684\u62ab\u7eb1\u5c11\u5973\u3002\u201d\u591a\u94ce\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u5973\u8d3c\u548c\u6211\u6709 \u4ec0\u4e48\u6df1\u4ec7\uff1f\u51e0\u6b21\u4e09\u756a\u524d\u6765\u884c\u523a\uff01\u201d\u4ed6\u4f3c\u60f3\u8d77\u4e86\u4ec0\u4e48\u4f3c\u7684\uff0c\u7a81\u7136\u7b11\u9053\uff1a\u201c\u8fd9\u5973\u8d3c\u524d\u4e24\u6b21\u6765\u65f6\uff0c \u4f60\u90fd\u6ca1\u6709\u78b0\u7740\uff0c\u6211\u5012\u548c\u5979\u4ea4\u8fc7\u624b\u3002\u8fd9\u6b21\u5728\u706f\u5149\u706b\u628a\u4e0b\u770b\u6e05\u695a\uff0c\u5979\u7684\u795e\u60c5\u4f53\u6001\uff0c\u5c45\u7136\u6709\u70b9\u4f3c \u4f60\uff0c\u4f60\u8bf4\u602a\u4e0d\u602a\uff1f\u201d\u7eb3\u5170\u738b\u5983\u624b\u4e0a\u6b63\u6367\u7740\u4e00\u676f\u8336\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u8336\u676f\u8dcc\u788e\uff0c\u5fd9\u5f3a\u6444\u5fc3\u795e\uff0c \u7b11\u9053\uff1a\u201c\u662f\u5417\uff1f\u201d \u3000\u3000\u591a\u94ce\u5403\u4e86\u4e00\u60ca\uff0c\u671b\u770b\u4ed6\u7684\u738b\u5983\uff0c\u89c1\u5979\u75c5\u5bb9\u6ee1\u9762\uff0c\u695a\u695a\u53ef\u601c\uff0c\u53ea\u9053\u5979\u662f\u75c5\u4e2d\u53d7\u60ca\u3002\u5fc3\u4e2d\u5ffd \u7136\u8d77\u4e86\u4e00\u80a1\u5ff5\u5934\uff0c\u597d\u50cf\u662f\u4ec0\u4e48\u529b\u91cf\u50ac\u7740\u4ed6\uff0c\u8981\u4ed6\u5c06\u5fc3\u4e2d\u6240\u60f3\u7684\u544a\u8bc9\u5979\u3002\u4e8e\u662f\u4ed6\u8f7b\u8f7b\u66ff\u7eb3\u5170\u738b \u5983\u6574\u7406\u4e91\u9b13\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u592b\u4eba\uff0c\u6211\u5bf9\u4e0d\u8d77\u4f60\uff01\u201d\u7eb3\u5170\u738b\u5983\u5403\u4e86\u4e00\u60ca\u4e0d\u6562\u7b54\u8bdd\uff0c\u6b63\u662f\uff1a \u3000\u3000\u5982\u6f6e\u7231\u6068\u96be\u5206\u8bf4\uff0c\u5fc3\u4e8b\u6df1\u85cf\u5341\u516b\u5e74\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5341\u516d\u7ae0\u3000\u4e91\u6d77\u5bc4\u9050\u3000\u601d\u585e\u5916\u5947\u5cf0\u66fe\u5165\u68a6\u3000\u8840\u5149\u6d88\u7f6a\u3000\u5b7d\u4eac\u534e\u5b64\u5973\u62a5\u6df1\u4ec7 \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u5341\u516d\u56de \u4e91\u6d77\u5bc4\u9050\u3000\u601d\u585e\u5916\u5947\u5cf0\u66fe\u5165\u68a6 \u8840\u5149\u6d88\u7f6a\u3000\u5b7d\u4eac\u534e\u5b64\u5973\u62a5\u6df1\u4ec7 \u3000\u3000\u7eb3\u5170\u738b\u5983\u4e00\u9635\u5fc3\u8df3\uff0c\u53ea\u542c\u5f97\u591a\u94ce\u4f4e\u58f0\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u6211\u4eec\u7ed3\u5a5a\u5df2\u5341\u516b\u5e74\u4e86\uff0c\u5341\u516b\u5e74\u6765\uff0c\u4f60 \u603b\u662f\u90c1\u90c1\u4e0d\u6b22\uff0c\u5f88\u5c11\u89c1\u4f60\u7b11\u8fc7\uff0c\u4f60\u4e0d\u8bf4\uff0c\u6211\u4e5f\u77e5\u9053\uff01\u201d\u7eb3\u5170\u738b\u5983\u79c0\u7709\u4e00\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u77e5\u9053\u4ec0 \u4e48\uff1f\u201d\u591a\u94ce\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u662f\u6211\u4eec\u65d7\u4eba\u4e2d\u7684\u7b2c\u4e00\u7f8e\u5973\uff0c\u624d\u8c8c\u53cc\u5168\uff0c\u6211\u53ea\u662f\u4e00\u4e2a\u6b66\u592b\uff0c\u5c31\u662f\u4f60 \u4e0d\u8bf4\u51fa\u6765\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u4e0d\u559c\u6b22\u6211\uff01\u201d\u7eb3\u5170\u738b\u5983\u6291\u6cea\u8bf4\u9053\uff1a\u201c\u738b\u7237\uff0c\u8fd9\u662f\u54ea\u91cc\u8bdd\u6765\uff1f\u4f60\u662f\u671d\u5ef7 \u64ce\u5929\u4e00\u67f1\uff0c\u662f\u65d7\u4eba\u4e2d\u9996\u5c48\u4e00\u6307\u7684\u82f1\u96c4\uff0c\u6211\u5ac1\u7ed9\u4f60\u5df2\u7ecf\u662f\u9ad8\u6500\u4e86\u3002\u201d\u591a\u94ce\u9053\uff1a\u201c\u592b\u4eba\uff0c\u5341\u516b\u5e74 \u592b\u59bb\uff0c\u4f60\u5c31\u4e00\u53e5\u771f\u8bdd\u4e5f\u4e0d\u80af\u5bf9\u6211\u8bf4\u5417\uff1f\u6211\u77e5\u9053\u6211\u914d\u4e0d\u4e0a\u4f60\uff0c\u53ef\u662f\u6211\u628a\u4f60\u770b\u5f97\u6bd4\u6211\u7684\u751f\u547d\u8fd8\u91cd \u8981\uff0c\u6211\u60f3\u5c3d\u4e00\u5207\u529e\u6cd5\uff0c\u8981\u4f7f\u4f60\u6b22\u5a31\uff0c\u4f46\u90a3\u5374\u8981\u6bd4\u6458\u4e0b\u5929\u4e0a\u7684\u6708\u4eae\u8fd8\u96be\u3002\u201d \u3000\u3000\u7eb3\u5170\u738b\u5983\u518d\u4e5f\u5fcd\u53d7\u4e0d\u4f4f\uff0c\u6cea\u5149\u83b9\u7136\uff0c\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u738b\u7237\uff0c\u522b\u90a3\u4e48\u8bf4\u4e86\uff0c\u4f60\u4e0d\u61c2\u5f97\uff0c\u6211\u4eec \u76f8\u89c1\u6068\u8fdf\u2026\u2026\u201d\u591a\u94ce\u6115\u7136\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u7eb3\u5170\u5de5\u5983\u84e6\u7136\u9192\u8d77\uff0c\u5fc3\u5e95\u7684\u79d8\u5bc6\u8fd8\u4e0d\u80fd\u5728\u8fd9\u4e2a\u65f6 \u5019\u6cc4\u9732\uff0c\u8863\u8896\u63a9\u9762\uff0c\u8f7b\u63e9\u6cea\u75d5\uff0c\u559f\u7136\u8bf4\u9053\uff1a\u201c\u800c\u4e14\u6211\u4eec\u53c8\u6ca1\u6709\u4e00\u513f\u534a\u5973\u3002\u201d \u3000\u3000\u591a\u94ce\u5ffd\u7136\u6ee1\u9762\u901a\u7ea2\uff0c\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u4e0d\u597d\uff0c\u6211\u4e00\u76f4\u7792\u7740\u4f60\uff0c\u90a3\u5e74\u6211\u5e26\u5175\u6253\u5927\u5c0f\u91d1 \u5ddd\uff0c\u7ed9\u2018\u751f\u756a\u2019\u7bad\u4f24\u80be\u810f\uff0c\u5fa1\u533b\u8bf4\uff0c\u6211\u547d\u4e2d\u6ce8\u5b9a\u6ca1\u6709\u513f\u5973\u4e86\u3002\u53ea\u662f\u6211\u8fd8\u4e0d\u6b7b\u5fc3\uff0c\u8fd9\u4e9b\u5e74\u6765\u6211 \u603b\u5728\u641c\u96c6\u5929\u4e0b\u7684\u5947\u73cd\u5f02\u836f\uff0c\u6709\u4eba\u8bf4\u8fd8\u672a\u7edd\u671b\uff0c\u6240\u4ee5\u6211\u4e00\u76f4\u4e0d\u544a\u8bc9\u4f60\u3002\u8fd9\u4e5f\u662f\u6211\u7684\u79c1\u5fc3\uff0c\u6211\u6015 \u8bf4\u51fa\u6765\u540e\uff0c\u4f60\u66f4\u4e0d\u559c\u6b22\u6211\u3002\u201d \u3000\u3000\u7eb3\u5170\u738b\u5983\u5927\u51fa\u610f\u5916\uff0c\u60f3\u4e0d\u5230\u6ca1\u6709\u513f\u5973\uff0c\u539f\u6765\u8fd8\u6709\u8fd9\u4e00\u6bb5\u9690\u60c5\u3002\u5979\u672c\u6765\u662f\u60f3\u8d77\u5979\u81ea\u5df1\u7684\u5973 \u513f\uff0c\u8fd9\u624d\u7a81\u7136\u611f\u559f\u7684\u3002\u6b64\u9645\uff0c\u5f88\u4e0d\u597d\u610f\u601d\u5730\u5782\u4e0b\u4e86\u5934\uff0c\u591a\u94ce\u53c8\u65ad\u65ad\u7eed\u7eed\u5730\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u5982\u679c \u4f60\u559c\u6b22\u513f\u5973\u7684\u8bdd\uff0c\u6211\u4eec\u62b1\u4e00\u4e2a\u56de\u6765\u517b\u5982\u4f55\uff1f\u4f60\u770b\u662f\u56db\u8d1d\u52d2\u7684\u5c0f\u513f\u5b50\u597d\uff1f\u8fd8\u662f\u4e03\u8d1d\u52d2\u7684\u5927\u683c\u683c \uff08\u6ee1\u5dde\u8d35\u65cf\u7684\u5973\u513f\u79f0\u683c\u683c\uff09\u597d\uff1f\u201d \u3000\u3000\u7eb3\u5170\u738b\u5983\u60c5\u6000\u7d0a\u4e71\uff0c\u7231\u6068\u5982\u6f6e\uff0c\u5979\u60f3\u8d77\u4e86\u5f53\u5e74\u548c\u6768\u4e91\u9aa2\u7684\u6c99\u6f20\u5947\u9022\uff0c\u8349\u539f\u8ba2\u76df\uff0c\u676d\u5dde\u6b7b \u522b\u7b49\u7b49\u5f80\u4e8b\uff08\u8be6\u89c1\u62d9\u8457\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\u4e00\u4e66\uff09\u3002\u8fd9\u4e9b\u5f80\u4e8b\uff0c\u94ed\u5fc3\u523b\u9aa8\uff0c\u6c38\u4e0d\u80fd\u5fd8\uff01\u591a\u94ce\u89c1\u5979 \u4f4e\u5782\u7c89\u9888\uff0c\u8f7b\u63a9\u7389\u5bb9\uff0c\u53c8\u8ffd\u95ee\u4e00\u53e5\u9053\uff1a\u201c\u4f60\u8bf4\u8bdd\u5440\uff01\u4f60\u8bf4\u54ea\u4e00\u4e2a\u597d\uff1f\u201d \u3000\u3000\u7eb3\u5170\u738b\u5983\u62ac\u8d77\u5934\u6765\uff0c\u89c1\u4e08\u592b\u76ee\u5149\u4e2d\u5145\u6ee1\u7740\u81ea\u8d23\u548c\u54c0\u4f24\uff0c\u60f3\u8d77\u4e86\u4ed6\u8fd9\u5341\u516b\u5e74\u6765\uff0c\u5bf9\u81ea\u5df1\u786e \u662f\u771f\u5fc3\u76f8\u7231\uff0c\u7a81\u7136\u89c9\u5f97\u4ed6\u4e5f\u5f88\u53ef\u601c\u3002\u62ed\u5e72\u6cea\u73e0\uff0c\u5ae3\u7136\u4e00\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u2014\u2014\u201d\u591a\u94ce\u9053\uff1a \u201c\u62b1\u4e00\u4e2a\u7537\u5b69\u5b50\u6216\u5973\u5b69\u5b50\u56de\u6765\u517b\u5440\uff01\u4f60\u8bf4\u54ea\u4e00\u4e2a\u597d\uff1f\u201d \u3000\u3000\u7eb3\u5170\u738b\u5983\u82b3\u5fc3\u6b32\u788e\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u54ea\u4e00\u4e2a\u90fd\u4e0d\u597d\uff0c\u6211\u8981\u2014\u2014\u201d\u591a\u94ce\u9053\uff1a\u201c\u4f60\u8981\u4ec0\u4e48\uff1f\u201d \u7eb3\u5170\u738b\u5983\u6e29\u67d4\u5730\u629a\u7740\u4ed6\u7684\u5934\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6c42\u4f60\u4e00\u4ef6\u4e8b\uff0c\u4f60\u80fd\u7b54\u5e94\u5417\uff1f\u201d\u591a\u94ce\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b \u90fd\u53ef\u7b54\u5e94\uff01\u201d\u7eb3\u5170\u738b\u5983\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u90a3\u4e2a\u3001\u90a3\u4e2a\u2018\u5973\u8d3c\u2019\uff0c\u4f60\u7b54\u5e94\u6211\u4e0d\u8981\u4f24\u5bb3\u5979\uff0c\u53ef\u4ee5 \u5417\uff1f\u201d\u591a\u94ce\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u7741\u5927\u773c\uff0c\u8be7\u6781\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u7eb3\u5170\u738b\u5983\u9053\uff1a\u201c\u4f60\u5148\u8bf4\u80fd\u4e0d \u80fd\u7b54\u5e94\uff1f\u201d\u591a\u94ce\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u7b54\u5e94\u4f60\uff01\u6211\u53eb\u695a\u662d\u5357\u505c\u6b62\u8ffd\u6355\uff0c\u800c\u4e14\u9664\u975e\u5979\u518d\u7528\u5251\u523a\u5230 \u6211\u7684\u8eab\u4e0a\uff0c\u5426\u5219\u6211\u51b3\u4e0d\u8ddf\u5979\u52a8\u624b\uff01\u201d\u7eb3\u5170\u738b\u5983\u9053\uff1a\u201c\u5979\u7528\u5251\u7684\uff1f\u201d\u591a\u94ce\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u5b50\u7684\u5251 \u6cd5\u597d\u6781\u5566\uff01\u53ea\u662f\u6c14\u529b\u4e0d\u884c\uff0c\u5426\u5219\u6211\u4e00\u5b9a\u4e0d\u662f\u5979\u7684\u5bf9\u624b\u3002\u695a\u662d\u5357\u8bf4\uff0c\u8fd9\u5973\u5a03\u5b50\u7684\u5251\u6cd5\u662f\u4ec0\u4e48\u5929 \u5c71\u5251\u8fed\uff0c\u548c\u4ed6\u540c\u4e00\u5e08\u95e8\u3002\u201d \u3000\u3000\u7eb3\u5170\u738b\u5983\u659c\u501a\u680f\u6746\uff0c\u51dd\u671b\u4e91\u6d77\uff0c\u4f3c\u4e4e\u90a3\u4e91\u6d77\u4e2d\u7684\u7f25\u7f08\u5947\u5cf0\uff0c\u5c31\u662f\u6f20\u5916\u7684\u5929\u5c71\u3002\u5979\u60f3\u8d77\u5979 \u7684\u5973\u513f\uff0c\u5728\u4e24\u5468\u5c81\u65f6\uff0c\u5c31\u7ed9\u6768\u4e91\u9aa2\u62a2\u53bb\uff0c\u5982\u679c\u8fd9\u5973\u5a03\u771f\u662f\u5979\u7684\u8bdd\uff0c\u90a3\u4e48\u5979\u4eca\u5e74\u8be5\u662f\u4e8c\u5341\u5c81\u7684 \u5c11\u5973\u4e86\u3002\u8fd9\u5341\u516b\u5e74\u6765\u5979\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u662f\u4ec0\u4e48\u4eba\u628a\u5979\u629a\u517b\u957f\u5927\uff1f\u5979\u975e\u5e38\u6e34\u671b\u77e5\u9053\u591a\u4e00\u4e9b\u4e1c\u897f\uff0c \u5173\u4e8e\u5979\u5973\u513f\u7684\u4e1c\u897f\uff0c\u662f\u4ec0\u4e48\u90fd\u597d\uff0c\u53ea\u4e00\u70b9\u70b9\u4e5f\u884c\uff01\u4f46\u4e00\u542c\u5230\u5979\u5b66\u7684\u662f\u5929\u5c71\u5251\u6cd5\uff0c\u5fc3\u91cc\u5374\u84e6\u7136 \u6cdb\u8d77\u4e00\u9635\u5bd2\u610f\u3002\u201c\u6768\u4e91\u9aa2\u554a\uff01\u4f60\u771f\u662f\u8fd9\u6837\u7684\u6b7b\u4e0d\u7791\u76ee\uff0c\u8981\u4f60\u7684\u5973\u513f\u5b66\u597d\u5251\u6cd5\u66ff\u4f60\u62a5\u4ec7\uff1f\u201d \u3000\u3000\u5979\u60f3\u7740\uff0c\u60f3\u7740\uff0c\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u7a81\u7136\u60f3\u8d77\u5728\u5927\u6f20\u8349\u539f\u7684\u90a3\u4e00\u4e2a\u5947\u5f02\u7684\u665a\u4e0a\uff0c\u6768\u4e91\u9aa2\u5bf9\u5979 \u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7684\u65cf\u4eba\u76f8\u4e92\u4ea4\u6218\uff0c\u4f46\u4f60\u4e0d\u662f\u6211\u7684\u4ec7\u4eba\uff0c\u6211\u7b54\u5e94\u6c38\u4e0d\u4f24\u5bb3\u4f60\u3002\u53ea\u662f\u4f60\u5047\u82e5\u6295\u5165\u522b \u4eba\u7684\u6000\u4e2d\uff0c\u90a3\u4e48\u4f60\u4e5f\u5c06\u628a\u7978\u5bb3\u5e26\u7ed9\u4ed6\uff0c\u90a3\u7ed3\u679c\u5c31\u662f\uff1a\u6b7b\uff01\u201d\u5979\u60f3\uff1a\u8fd9\u771f\u662f\u4e00\u79cd\u56fa\u6267\u5230\u65e0\u53ef\u7406 \u55bb\u7684\u7231\u60c5\uff1a\u6768\u4e91\u9aa2\u7684\u6b7b\uff0c\u4ee4\u5979\u4f24\u5fc3\u4e86\u5341\u516b\u5e74\uff0c\u5341\u516b\u5e74\u7684\u9752\u6625\u5c81\u6708\u90fd\u5728\u9eef\u6de1\u7684\u65f6\u65e5\u4e2d\u5ea6\u8fc7\uff0c\u8fd9 \u4e5f\u53ef\u4ee5\u62b5\u507f\u81ea\u5df1\u7684\u201c\u80cc\u76df\u201d\u4e86\u5427\uff1f\u5979\u60f3\uff0c\u5979\u6709\u65f6\u6068\u591a\u94ce\uff0c\u4f46\u6709\u65f6\u7231\u591a\u94ce\u2014\u2014\u5230\u5e95\u662f\u5341\u516b\u5e74\u7684 \u592b\u59bb\u4e86\u554a\uff01\u5979\u5e38\u60f3\uff1a\u6768\u4e91\u9aa2\u5e76\u4e0d\u662f\u591a\u94ce\u5bb3\u6b7b\u7684\uff0c\u591a\u94ce\u8fde\u77e5\u9053\u4e5f\u4e0d\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u867d\u7136\u4ed6\u4eec\u662f \u52bf\u4e0d\u4e24\u7acb\u7684\u654c\u4eba\uff01\u5979\u8fc7\u53bb\u5c31\u66fe\u4ee5\u8fd9\u6837\u7684\u60f3\u6cd5\u6765\u6170\u89e3\u81ea\u5df1\u3002\u53ef\u662f\u73b0\u5728\uff0c\u5979\u7684\u5973\u513f\u6765\u4e86\uff0c\u5979\u5b66\u597d \u7684\u5251\u6cd5\uff0c\u5c31\u8981\u65bd\u5c55\u5728\u81ea\u5df1\u4e08\u592b\u7684\u8eab\u4e0a\uff01\u5979\u84e6\u7136\u63a9\u4f4f\u4e86\u9762\uff0c\u5979\u4e0d\u613f\u610f\u591a\u94ce\u4f24\u5bb3\u5979\u7684\u5973\u513f\uff0c\u4f46\u4e5f \u4e0d\u613f\u610f\u5979\u7684\u5973\u513f\u4f24\u5bb3\u591a\u94ce\u3002\u591a\u94ce\u5fc3\u4e2d\u5145\u6ee1\u4e86\u7591\u95ee\uff0c\u89c1\u4ed6\u7684\u738b\u5983\u501a\u7740\u680f\u6746\u60f3\u5f97\u51fa\u795e\uff0c\u4e0d\u6562\u53bb\u60ca \u52a8\u5979\u3002\u8fd9\u65f6\u84e6\u7136\u542c\u5f97\u4e00\u58f0\u8f7b\u5524\uff0c\u6025\u5fd9\u8fc7\u53bb\uff0c\u624b\u6309\u9999\u80a9\uff0c\u4f4e\u95ee\u5979\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e86\uff1f\u201d\u7eb3\u5170\u738b\u5983 \u56de\u8fc7\u5934\u6765\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u51c6\u5979\u4f24\u5bb3\u4f60\uff01\u201d \u3000\u3000\u591a\u94ce\u8fd9\u4e00\u60ca\u6bd4\u521a\u624d\u8fd8\u8981\u5389\u5bb3\uff0c\u9000\u540e\u4e24\u6b65\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u5979\u4f1a\u542c\u4f60\u7684\u8bdd\uff1f\u201d\u7eb3\u5170\u738b\u5983\u904d\u4f53 \u6d41\u6c57\uff0c\u5b9a\u4e86\u4e0b\u795e\uff0c\u6545\u610f\u7b11\u51fa\u58f0\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u4f60\u5413\u6210\u8fd9\u4e2a\u6837\u5b50\uff01\u6211\u662f\u542c\u4f60\u8bf4\uff0c\u90a3\u5973\u5a03\u5b50\u5f88 \u50cf\u6211\uff0c\u6211\u5fc3\u91cc\u5c31\u6709\u4e00\u4e2a\u5947\u602a\u7684\u5ff5\u5934\uff0c\u5982\u679c\u5979\u662f\u6211\u4eec\u7684\u5973\u513f\u591a\u597d\u3002\u4f60\u5f88\u7231\u6211\uff0c\u6211\u60f3\u4f60\u4e00\u5b9a\u4e0d\u4f1a \u4f24\u5bb3\u50cf\u6211\u7684\u4eba\uff0c\u6240\u4ee5\u6211\u624d\u6562\u5927\u80c6\u5730\u8bf7\u6c42\u4f60\u3002\u6211\u53c8\u60f3\uff1a\u65e2\u7136\u6211\u6697\u4e2d\u5bf9\u5979\u8fd9\u6837\u7231\u60dc\uff0c\u5982\u679c\u5979\u77e5\u9053 \u7684\u8bdd\uff0c\u5979\u53ef\u80fd\u4e5f\u4f1a\u542c\u6211\u7684\u8bdd\u3002\u201d\u591a\u94ce\u53f9\u9053\uff1a\u201c\u660e\u6167\uff08\u738b\u5983\u7684\u6821\u8774\uff09\uff0c\u4f60\u771f\u50cf\u4e00\u4e2a\u5927\u5b69\u5b50\uff0c\u60f3 \u5f97\u8fd9\u6837\u5929\u771f\uff0c\u8fd9\u6837\u65e0\u90aa\uff01\u201d \u3000\u3000\u8fd9\u6b21\u8c08\u8bdd\u540e\uff0c\u7eb3\u5170\u738b\u5983\u5bf9\u591a\u94ce\u6bd4\u5e73\u65f6\u597d\u4e86\u8bb8\u591a\uff0c\u5979\u597d\u50cf\u6709\u4e00\u79cd\u9884\u611f\uff1a\u6b7b\u4ea1\u4e4b\u795e\u5df2\u7ecf\u5c55\u5f00 \u53cc\u7ffc\u98de\u5728\u4ed6\u4eec\u7684\u5934\u4e0a\u3002\u773c\u524d\u7684\u5b81\u9759\uff0c\u53ea\u662f\u66b4\u98ce\u96e8\u7684\u524d\u5915\u3002\u4e8e\u662f\u7ec8\u4e8e\u6765\u5230\u4e86\u8fd9\u4e48\u4e00\u5929\u2014\u2014\u2014\u2014 \u3000\u3000\u8fd9\u4e00\u5929\uff0c\u591a\u94ce\u6b63\u5f0f\u63a5\u5230\u201c\u5723\u65e8\u201d\uff0c\u8981\u4ed6\u7edf\u7387\u4e09\u519b\uff0c\u8282\u5236\u8bf8\u8def\u5175\u9a6c\uff0c\u53bb\u8ba8\u4f10\u5434\u4e09\u6842\u5e76\u527f\u706d \u674e\u6765\u4ea8\u3002\u672c\u6765\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u7687\u5e1d\u65e9\u5c31\u548c\u4ed6\u63d0\u8fc7\uff0c\u53ea\u662f\u4ed6\u4e0d\u613f\u610f\u544a\u8bc9\u738b\u5983\uff0c\u4ed6\u4e5f\u6709\u4e00\u79cd\u9884\u611f\uff0c\u611f \u5230\u81ea\u5df1\u7684\u751f\u547d\u597d\u50cf\u5df2\u8d70\u5230\u4e86\u5c3d\u5934\uff0c\u8fd9\u79cd\u611f\u89c9\u662f\u4ece\u672a\u6709\u8fc7\u7684\u3002\u4ed6\u5e76\u4e0d\u60e7\u6015\u5434\u4e09\u6842\uff0c\u5434\u4e09\u6842\u5df2\u5982 \u98ce\u4e2d\u4e4b\u70db\uff0c\u53ea\u8981\u4ed6\u8d76\u4e0a\u53bb\u5439\u4e00\u53e3\u6c14\uff0c\u8fd9\u70db\u5149\u5c31\u4f1a\u7184\u706d\u4e86\u3002\u4ed6\u66f4\u4e0d\u662f\u60e7\u6015\u6253\u4ed7\uff0c\u6253\u4ed7\u5bf9\u4e8e\u4ed6\uff0c \u90a3\u662f\u592a\u5e73\u5e38\u7684\u4e8b\u60c5\u3002\u53ef\u662f\u4ed6\u6709\u4e00\u79cd\u83ab\u540d\u5176\u5999\u7684\u60e7\u6015\uff0c\u8fd9\u79cd\u60e7\u6015\u662f\u7531\u4e8e\u738b\u5983\u7684\u53cd\u5e38\u6240\u5f15\u8d77\u7684\uff0c \u4ed6\u597d\u50cf\u4ece\u738b\u5983\u5947\u5f02\u7684\u773c\u795e\u4e2d\uff0c\u611f\u5230\u4e00\u79cd\u201c\u51f6\u5146\u201d\u3002\u6709\u65f6\u4ed6\u534a\u591c\u9192\u6765\uff0c\u89c1\u7740\u738b\u5983\u4e00\u53cc\u5b9d\u77f3\u822c\u7684 \u773c\u73e0\uff0c\u5728\u9ed1\u6697\u4e2d\u900f\u51fa\u5149\u4eae\uff0c\u4ed6\u5c31\u5413\u5f97\u5168\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u8fd9\u5929\u4ed6\u63a5\u5230\u201c\u5723\u65e8\u201d\u4e4b\u540e\uff0c\u56de\u53bb\u544a\u8bc9\u738b\u5983\u3002\u738b\u5983\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u738b\u7237\uff0c\u6211\u771f\u6015 \u4f60\u79bb\u5f00\u6211\uff01\u201d\u591a\u94ce\u9053\uff1a\u201c\u6211\u5f88\u5feb\u5c31\u4f1a\u56de\u6765\u7684\u3002\u201d\u738b\u5983\u9ed8\u7136\u4e0d\u8bed\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u4f60 \u53bb\u4e86\u4e5f\u597d\uff0c\u7701\u5f97\u90a3\u5973\u5a03\u5b50\u5728\u4eac\u57ce\u91cc\u548c\u4f60\u78b0\u5934\uff01\u201d\u591a\u94ce\u8e59\u7709\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8001\u662f\u63d0\u90a3\u4e2a\u5973\u5a03 \u5b50\uff1f\u201d \u3000\u3000\u738b\u5983\u5e76\u4e0d\u7b54\u4ed6\u7684\u8bdd\uff0c\u53c8\u8fc7\u4e86\u4e00\u4f1a\uff0c\u624d\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u52a8\u8eab\uff1f\u201d\u591a\u94ce\u9053\uff1a\u201c\u660e\u5929\u9605 \u5175\uff0c\u540e\u5929\u5f00\u62d4\uff01\u201d\u738b\u5983\u9053\uff1a\u201c\u6211\u660e\u5929\u66ff\u4f60\u5728\u5367\u4f5b\u5bfa\u70b9\u5934\u4e00\u70b7\u9999\u3002\u201d\u591a\u94ce\u8fd9\u4e00\u665a\u6574\u591c\u65e0\u7720\u3002 \u3000\u3000\u53e6\u4e00\u9762\uff0c\u6613\u5170\u73e0\u4e5f\u6709\u7740\u5947\u602a\u7684\u9884\u611f\uff0c\u5979\u8fd9\u4e9b\u5929\u6765\uff0c\u6f5c\u5fc3\u7cbe\u7a76\u5929\u5c71\u5251\u6cd5\uff0c\u7aed\u529b\u4e0d\u60f3\u4efb\u4f55\u4e1c \u897f\u3002\u4f46\u4e00\u5230\u9759\u4e0d\u6765\u65f6\uff0c\u5fc3\u4e2d\u5f3a\u7b51\u8d77\u6765\u7684\u5824\u9632\uff0c\u5374\u6291\u4e0d\u4f4f\u601d\u60f3\u7684\u6ce2\u6d6a\uff01\u5979\u611f\u5230\u559c\u60a6\uff0c\u4e5f\u611f\u5230\u54c0 \u4f24\u3002\u5979\u975e\u5e38\u7231\u5979\u7684\u7236\u4eb2\uff0c\u867d\u7136\u5979\u6839\u672c\u8bb0\u4e0d\u8d77\u7236\u4eb2\u7684\u989c\u5bb9\uff08\u5979\u7236\u4eb2\u6b7b\u7684\u65f6\u5019\uff0c\u5979\u624d\u53ea\u6709\u4e24\u5c81 \u54e9\uff09\u3002\u4f46\u5979\u7236\u4eb2\u7684\u4e8b\u8ff9\u5728\u5927\u8349\u539f\u4e0a\u6d41\u4f20\uff1a\u5979\u4e00\u8def\u957f\u5927\uff0c\u4e00\u8def\u542c\u5230\u7267\u6c11\u4eec\u5bf9\u5979\u7236\u4eb2\u7684\u9882\u8d5e\u3002\u5979 \u7684\u7236\u4eb2\u5e2e\u54c8\u8428\u514b\u4eba\u62b5\u6297\u6e05\u5175\uff0c\u7267\u6c11\u4eec\u63d0\u8d77\u201c\u5927\u4fa0\u6768\u4e91\u9aa2\u201d\u65f6\uff0c\u5c31\u50cf\u8bf4\u8d77\u81ea\u5df1\u7684\u4eb2\u4eba\u4e00\u6837\uff0c\u5979 \u4e3a\u6709\u8fd9\u6837\u4e00\u4e2a\u82f1\u96c4\u7684\u7236\u4eb2\u800c\u9a84\u50b2\uff0c\u56e0\u6b64\u5979\u7236\u4eb2\u7ed9\u5979\u7684\u8840\u4e66\uff0c\u51cc\u672a\u98ce\u5728\u5979\u5341\u516d\u5c81\u90a3\u5e74\u4ea4\u7ed9\u5979 \u7684\uff0c\u4e00\u76f4\u85cf\u5728\u6000\u91cc\u7684\u90a3\u5c01\u8840\u4e66\uff0c\u5c31\u50cf\u5343\u65a4\u91cd\u62c5\u538b\u5728\u5979\u7684\u5fc3\u5934\uff01\u5982\u679c\u4e0d\u80fd\u5b8c\u6210\u7236\u4eb2\u7684\u5631\u5490\uff0c\u5979 \u7684\u5fc3\u6c38\u8fdc\u4e0d\u4f1a\u8f7b\u677e\uff01\u73b0\u5728\u5979\u5df2\u51b3\u5b9a\u53bb\u6b7b\uff0c\u62fc\u7740\u6027\u547d\u53bb\u5b8c\u6210\u7236\u4eb2\u7684\u5631\u5490\u3002\u8fd9\u4e2a\u51b3\u5b9a\u4f7f\u5979\u7684\u5fc3\u5934 \u91cd\u538b\u7a81\u7136\u51cf\u8f7b\u4e86\u3002\u56e0\u6b64\u5979\u611f\u5230\u4e00\u79cd\u5947\u5f02\u7684\u559c\u60a6\uff01\u4f46\u5979\u53c8\u6709\u96be\u4ee5\u8bf4\u660e\u7684\u54c0\u4f24\u3002\u5979\u7231\u5979\u7684\u6bcd\u4eb2 \u5417\uff1f\u5979\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u3002\u5979\u5728\u5b64\u72ec\u4e2d\u957f\u5927\uff0c\u201c\u4eb2\u4eba\u201d\u53ea\u6709\u4e00\u4e2a\u51cc\u672a\u98ce\uff0c\u5979\u975e\u5e38\u6e34\u671b\u6bcd\u7231\uff0c\u4f46\u8fd9 \u79cd\u7231\u5374\u53c8\u6400\u6742\u7740\u618e\u6068\u3002\u5979\u5f88\u60f3\u89c1\u5979\u7684\u6bcd\u4eb2\uff0c\u95ee\u95ee\u5979\u4e24\u5c81\u4ee5\u524d\u662f\u600e\u6837\u7684\u3002\u5979\u9884\u611f\u5230\u8fd9\u6b21\u53bb\u6b7b\uff0c \u662f\u6c38\u8fdc\u89c1\u4e0d\u5230\u6bcd\u4eb2\u4e86\uff0c\u4e5f\u8bb8\u6bcd\u4eb2\u8fd8\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u5979\u7684\u5973\u513f\u3002\u53e6\u4e00\u65b9\u9762\uff0c\u6700\u8fd1\u8fd9\u4e00\u5e74\uff0c\u5979\u5bc2\u5bde \u7684\u5fc3\u4e2d\uff0c\u5ffd\u53c8\u95ef\u8fdb\u4e00\u4e2a\u5f71\u5b50\uff0c\u90a3\u662f\u5f20\u534e\u662d\u7684\u5f71\u5b50\uff0c\u5979\u81ea\u5df1\u4e5f\u5f04\u4e0d\u6e05\u695a\uff0c\u4ece\u4ec0\u4e48\u65f6\u5019\u8d77\uff0c\u5bf9\u4ed6 \u53d1\u751f\u4e86\u8fd9\u6837\u7684\u611f\u60c5\u3002\u6613\u5170\u73e0\u7684\u60c5\u7eea\u5728\u6df7\u4e71\u4e2d\uff0c\u5ffd\u7136\uff0c\u8fd9\u6df7\u4e71\u7684\u60c5\u7eea\u51dd\u7ed3\u4e0b\u6765\uff0c\u56e0\u4e3a\uff0c\u8fd9\u4e00\u5929 \u7ec8\u4e8e\u6765\u5230\u4e86\u2014\u2014\u2014\u2014 \u3000\u3000\u8fd9\u4e00\u5929\uff0c\u5f20\u9752\u539f\u7b49\u4eba\u4e0d\u4f46\u77e5\u9053\u4e86\u591a\u94ce\u9605\u5175\u7684\u6d88\u606f\uff0c\u800c\u4e14\u4e5f\u77e5\u9053\u4e86\u7eb3\u5170\u738b\u5983\u8981\u5230\u5367\u4f5b\u5bfa\u8fdb \u9999\u7684\u6d88\u606f\uff0c\u77f3\u632f\u98de\u5728\u5317\u4eac\u5730\u9762\u5f88\u719f\uff0c\u6697\u5730\u91cc\u7ed9\u4ed6\u4eec\u5b89\u6392\u4e86\u8bb8\u591a\u201c\u7ebf\u4eba\u201d\u3002\u9102\u738b\u5983\u5934\u4e00\u5929\u901a\u77e5 \u5367\u4f5b\u5bfa\u7684\u4e3b\u6301\uff0c\u4ed6\u4eec\u7b2c\u4e8c\u5929\u4e00\u65e9\u5c31\u77e5\u9053\u4e86\u3002\u56e0\u4e3a\u738b\u5983\u8981\u6765\u8fdb\u9999\uff0c\u4f4f\u6301\u81ea\u7136\u8981\u901a\u77e5\u548c\u5c1a\u4eec\u51c6 \u5907\uff0c\u800c\u548c\u5c1a\u4e2d\u5c31\u6709\u77f3\u632f\u98de\u7684\u201c\u7ebf\u4eba\u201d\u3002 \u3000\u3000\u8fd9\u662f\u884c\u523a\u591a\u94ce\u7684\u6700\u540e\u4e00\u4e2a\u673a\u4f1a\u4e86\uff0c\u4f46\u8fd9\u6700\u540e\u7684\u673a\u4f1a\uff0c\u5374\u771f\u662f\u975e\u5e38\u96be\u4e8e\u4e0b\u624b\uff01\u5728\u9605\u5175\u65f6\u5019 \u884c\u523a\uff0c\u90a3\u662f\u7edd\u4e0d\u53ef\u80fd\u7684\u4e8b\uff01\u83ab\u8bf4\u5728\u5341\u4e07\u5927\u519b\u4e4b\u524d\uff0c\u884c\u523a\u53ea\u4f1a\u9001\u6b7b\uff0c\u800c\u4e14\u5927\u6821\u573a\u4e2d\uff0c\u95f2\u4eba\u6839\u672c \u65e0\u6cd5\u6df7\u5f97\u8fdb\u53bb\uff01 \u3000\u3000\u5728\u8bae\u8bba\u7eb7\u7ecf\u4e2d\uff0c\u6613\u5170\u73e0\u4fdd\u6301\u7740\u5f02\u5e38\u7684\u6c89\u9ed8\uff0c\u5f20\u534e\u662d\u51dd\u671b\u7740\u5979\uff0c\u5fc3\u4e2d\u5ffd\u7136\u611f\u5230\uff0c\u5bf9\u5979\u6709\u96be \u4ee5\u5272\u820d\u7684\u611f\u60c5\u3002\u4ed6\u4e86\u89e3\u523a\u6740\u591a\u94ce\u5bf9\u4e8e\u4ed6\u4eec\u7684\u4e8b\u4e1a\u662f\u4f55\u7b49\u91cd\u8981\uff0c\u4f46\u4ed6\u5b9e\u5728\u4e0d\u5fcd\u89c1\u8fd9\u6837\u4e00\u4f4d\u5728\u5bc2 \u5bde\u4e0e\u75db\u82e6\u4e2d\u957f\u5927\u7684\u5c11\u5973\uff0c\u6b63\u5f53\u5979\u9752\u6625\u7eda\u70c2\u7684\u65f6\u5019\uff0c\u8d70\u5411\u6b7b\u4ea1\u7684\u5e7d\u8c37\uff01\u4ed6\u6392\u5f00\u4f17\u4eba\uff0c\u51fa\u6765\u8bf4 \u9053\uff1a\u201c\u65e2\u7136\u662f\u65e0\u6cd5\u4e0b\u624b\uff0c\u90a3\u5c31\u7b97\u4e86\u5427\uff01\u201d\u6613\u5170\u73e0\u5ffd\u7136\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u8c01\u8bf4\u6ca1\u6cd5\u4e0b\u624b\uff1f\u6211\u4eec\u5230 \u897f\u5c71\u7684\u5367\u4f5b\u5bfa\u53bb\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u9053\uff1a\u201c\u591a\u94ce\u9605\u5175\u4e4b\u540e\uff0c\u6709\u591a\u5c11\u5927\u4e8b\u5904\u7406\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u8981\u8fdb\u5bab\u965b\u89c1\uff0c\u4f60\u6562\u51c6\u4fdd\u4ed6\u4f1a\u5230 \u5367\u4f5b\u5bfa\u5417\uff1f\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u6211\u770b\u4ed6\u4f1a\u53bb\u7684\u3002\u800c\u4e14\u4e0d\u8bba\u4ed6\u53bb\u4e0d\u53bb\uff0c\u6211\u4eec\u4e5f\u53ea\u6709\u8fd9\u4e2a\u673a\u4f1a\u53ef\u4ee5\u5c1d \u8bd5\u4e86\uff0c\u4f60\u4eec\u4e0d\u53bb\uff0c\u6211\u5355\u72ec\u4e00\u4eba\u53bb\uff01\u201d\u901a\u660e\u548c\u5c1a\u56b7\u9053\uff1a\u201c\u4f60\u8fd9\u5973\u5a03\u5b50\u80c6\u5927\uff0c\u6211\u4eec\u4e5f\u4e0d\u80c6\u5c0f\uff0c\u8981 \u53bb\u5c31\u5927\u5bb6\u53bb\uff0c\u6211\u66ff\u4f60\u6321\u7740\u536b\u58eb\uff0c\u8ba9\u4f60\u7b2c\u4e00\u4e2a\u4e0b\u624b\uff01\u201d\u6613\u5170\u73e0\u5fae\u5fae\u4e00\u7b11\uff0c\u5f20\u534e\u662d\u9ed8\u9ed8\u4e0d\u8bed\uff0c\u5e38 \u82f1\u7a0b\u901a\u62cd\u624b\u8d5e\u6210\uff0c\u4e8b\u60c5\u5c31\u8fd9\u6837\u51b3\u5b9a\u4e86\u3002\u3001 \u3000\u3000\u4e14\u8bf4\u591a\u94ce\u8fd9\u5929\u5728\u5927\u6821\u573a\u4e2d\u9605\u5175\uff0c\u53ea\u89c1\u5341\u4e07\u96c4\u5e08\uff0c\u5200\u67aa\u80dc\u96ea\uff0c\u65c1\u8fb9\u7684\u53c2\u5c06\u8bf4\u9053\uff1a\u201c\u5927\u5e05\uff0c \u4ee5\u8fd9\u6837\u7684\u519b\u5bb9\uff0c\u5434\u4e09\u6842\u674e\u6765\u4ea8\u5fc5\u662f\u4e0d\u582a\u4e00\u51fb\uff01\u201d\u591a\u94ce\u201d\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u7b56\u9a6c\u7f13\u7f13\u68c0\u9605\u5927\u519b\uff0c\u7cbe \u795e\u4f3c\u4e4e\u5f88\u662f\u843d\u6f20\u3002\u9ad8\u7ea7\u5c06\u9886\u4e00\u4e2a\u4e2a\u4e0a\u6765\u8c12\u89c1\uff0c\u4ed6\u4e5f\u53ea\u662f\u70b9\u4e86\u70b9\u5934\u3002\u4f17\u5c06\u5b98\u90fd\u89c9\u5f97\u7edf\u5e05\u7684\u795e\u60c5 \u592a\u8fc7\u5947\u5f02\uff0c\u4e1d\u6beb\u6ca1\u6709\u5e73\u65e5\u7684\u52c7\u6b66\u96c4\u98ce\uff0c\u548c\u5927\u9605\u5175\u5e94\u6709\u7684\u6c14\u6c1b\u66f4\u662f\u6beb\u4e0d\u76f8\u79f0\uff0c\u5fc3\u91cc\u4e0d\u7981\u6697\u6697\u5600 \u5495\uff1a\u8fd9\u4f3c\u4e4e\u662f\u4e0d\u7965\u4e4b\u5146\u3002 \u3000\u3000\u591a\u94ce\u8349\u8349\u9605\u5175\uff0c\u4e0d\u5230\u6b63\u5348\uff0c\u5c31\u7ed3\u675f\u4e86\u3002\u53c2\u5c06\u56b7\u9053\uff1a\u201c\u5927\u5e05\u662f\u5426\u8981\u53ec\u96c6\u5c06\u9886\u4eec\u8bb2\u8bdd\uff1f\u201d\u591a \u94ce\u6446\u6446\u624b\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff01\u201d\u53c2\u5c06\u5341\u5206\u60ca\u5947\u3002\u8eac\u8170\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u51e0\u65f6\u70b9\u5c06\uff1f\u201d\u7167\u4f8b\u5728\u51fa\u5f81\u4e4b \u524d\uff0c\u5fc5\u5b9a\u8981\u8fdb\u884c\u201c\u70b9\u5c06\u2019\u5927\u5178\uff08\u201c\u70b9\u5c06\u201d\u5c31\u662f\u5206\u914d\u5c06\u9886\u7684\u4efb\u52a1\uff0c\u4f8b\u5982\u70b9\u5148\u950b\uff0c\u70b9\u8fd0\u7cae\u5b98\uff0c\u70b9 \u5404\u8def\u7edf\u5e05\u7b49\uff09\uff0c\u90a3\u6599\u591a\u94ce\u4e5f\u6446\u6446\u624b\u9053\uff1a\u201c\u5fd9\u4ec0\u4e48\uff1f\u51fa\u4e86\u4eac\u5e08\u518d\u70b9\uff01\u201d\u53c2\u5c06\u95ee\u9053\uff1a\u201c\u5927\u5e05\u662f\u8981 \u8d77\u5230\u5b98\u4e2d\u965b\u89c1\uff0c\u5411\u7687\u4e0a\u8f9e\u884c\u4e48\uff1f\u201d\u591a\u94ce\u8e69\u7709\u9053\uff1a\u201c\u660e\u65e9\u8fd8\u6709\u65e9\u671d\uff0c\u4e0d\u5fc5\u53e6\u5916\u965b\u89c1\u4e86\u3002\u201d\u53c2\u5c06 \u6b63\u60f3\u518d\u95ee\uff0c\u591a\u94ce\u559d\u9053\uff1a\u201c\u8981\u4f60\u7f57\u5506\u4ec0\u4e48\uff0c\u672c\u5e05\u6709\u4e8b\uff01\u201d\u53c2\u5c06\u5634\u4e0d\u4f5c\u58f0\uff0c\u66f4\u662f\u5947\u5f02\u3002\u672c\u6765\u7ed9\u7edf \u5e05\u5b89\u6392\u70b9\u5c06\u7b49\u6742\u52a1\u5de5\u4f5c\uff0c\u662f\u53c2\u5c06\u7684\u8d23\u4efb\uff0c\u60f3\u4e0d\u5230\u53ea\u8fd9\u4e48\u4e00\u63d0\uff0c\u5c31\u53d7\u5230\u65a5\u8d23\u3002\u591a\u94ce\u9063\u6563\u4e09\u519b\uff0c \u5411\u53c2\u5c06\u8bf4\u9053\uff1a\u201c\u4f60\u548c\u4eb2\u5175\u4eec\u966a\u6211\u53bb\u5367\u4f5b\u5bfa\u8fdb\u9999\uff01\u201d\u53c2\u5c06\u8be7\u6781\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4e2a\u65f6\u5019\u53bb\u8ff8\u9999\uff1f\u201d \u591a\u94ce\u65a5\u9053\uff1a\u201c\u4e0d\u80fd\u53bb\u4e48\uff1f\u201d\u53c2\u5c06\u4e0d\u6562\u4f5c\u58f0\uff0c\u552f\u552f\u800c\u9000\u3002\u7247\u523b\u4e4b\u540e\uff0c\u4e09\u767e\u7cbe\u9510\u4eb2\u5175\uff0c\u548c\u5341\u591a\u4e2a \u7279\u9009\u536b\u58eb\uff0c\u56f4\u62e5\u7740\u591a\u94ce\uff0c\u5411\u897f\u5c71\u9a70\u53bb\u3002 \u3000\u3000\u591a\u94ce\u795e\u601d\u604d\u60da\uff0c\u8111\u4e2d\u7a7a\u8361\u8361\u7684\uff0c\u4f3c\u4e4e\u4ec0\u4e48\u90fd\u6ca1\u6709\u3002\u4ed6\u53ea\u8bb0\u6302\u7740\u4e00\u4ef6\u4e8b\u60c5\uff1a\u8981\u89c1\u4ed6\u7684\u738b \u5983\u3002\u6b64\u523b\uff0c\u5728\u4ed6\u7684\u5fc3\u4e2d\uff0c\u4ed6\u7684\u738b\u5983\u8981\u6bd4\u5f53\u4eca\u5929\u5b50\u3001\u7edf\u5175\u5927\u5c06\uff0c\u90fd\u6765\u5f97\u91cd\u8981\uff01\u8fd9\u51e0\u5929\u6765\uff0c\u4ed6\u4f3c \u4e4e\u5df2\u83b7\u5f97\u4e86\u5979\uff0c\u4f46\u53c8\u4f3c\u4e4e\u8981\u5931\u53bb\u5979\u3002\u5979\u4f1a\u66ff\u4ed6\u53bb\u70b9\u5934\u4e00\u67f1\u9999\uff0c\u795d\u4ed6\u51fa\u5f81\u80dc\u5229\uff0c\u5e73\u5b89\u51ef\u65cb\uff0c\u8fd9 \u662f\u4ece\u672a\u6709\u8fc7\u7684\u4e8b\uff01\u4ed6\u73b0\u5728\u53ea\u6709\u4e00\u4e2a\u613f\u671b\uff0c\u5feb\u70b9\u5230\u5979\u7684\u8ddf\u524d\uff0c\u8bf4\u51fa\u4ed6\u7684\u8c22\u610f\u3002 \u3000\u3000\u79cb\u5929\u7684\u897f\u5c71\uff0c\u5206\u5916\u53ef\u7231\uff0c\u7fa4\u5cf0\u6ef4\u7fe0\uff0c\u67ab\u53f6\u971e\u7ea2\uff0c\u7389\u6cc9\u5c71\u7684\u6cc9\u6c34\uff0c\u4f3c\u5929\u8679\u5012\u6302\uff0c\u8272\u5982\u7d2f \u7ec3\uff0c\u5999\u5cf0\u5c71\u7684\u4e91\u6c14\uff0c\u4f3c\u5927\u6d77\u817e\u6ce2\uff0c\u6eda\u6eda\u7ffb\u7ffb\uff0c\u4f46\u8fd9\u4e00\u5207\u666f\u8272\uff0c\u591a\u94ce\u90fd\u5df2\u65e0\u5fc3\u6b23\u8d4f\uff0c\u4ed6\u4e0b\u9a6c\u4e0a \u5c71\uff0c\u8fdc\u8fdc\u4fbf\u89c1\u9999\u70df\u64a9\u7ed5\uff0c\u6ee1\u6000\u559c\u60a6\u5730\u5411\u5367\u4f5b\u5bfa\u884c\u53bb\u3002\u4eb2\u5175\u4eec\u5219\u5728\u4e24\u65c1\u5f00\u9053\uff0c\u9a71\u9010\u95f2\u4eba\u3002 \u3000\u3000\u4e0a\u5230\u534a\u5c71\uff0c\u5367\u4f5b\u5bfa\u5df2\u7ecf\u5728\u671b\uff0c\u5ffd\u7136\u9053\u65c1\u8f6c\u51fa\u4e00\u4e2a\u767d\u53d1\u82cd\u82cd\u7684\u8001\u5987\uff0c\u4f4e\u5934\u5782\u6ce3\uff0c\u4eb2\u5175\u4eec\u65a5 \u559d\u9a71\u9010\uff0c\u5979\u5140\u662f\u4e0d\u80af\u907f\u5f00\u3002\u53c2\u5c06\u626c\u97ad\u559d\u9053\uff1a\u201c\u628a\u5979\u8d76\u51fa\u53bb\uff01\u201d\u90a3\u8001\u5987\u4eba\u58f0\u54ed\u9053\uff1a\u201c\u592b\u5440\uff01\u592b \u5440\uff01\u201d\u591a\u94ce\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u8d76\u5979\uff01\u201d\u4e0a\u524d\u95ee\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd9\u6837\u54ed\uff1f\u201d\u8001\u5987\u9053\uff1a \u201c\u6211\u7684\u4e08\u592b\u5341\u516b\u5e74\u51fa\u5916\u672a\u5f52\uff0c\u524d\u5929\u4e00\u56de\u6765\uff0c\u5c31\u751f\u4e86\u91cd\u75c5\uff0c\u6211\u8981\u66ff\u5979\u70b9\u4e00\u67f1\u9999\uff01\u53eb\u83e9\u8428\u4fdd\u4ed6\u5e73 \u5b89\uff01\u201d \u3000\u3000\u591a\u94ce\u5fc3\u5934\u9707\u52a8\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u5341\u516b\u5e74\u2026\u2026\u201d\u90a3\u8001\u5987\u62ff\u7740\u62d0\u6756\u7684\u624b\uff0c\u98a4\u6296\u4e0d\u4f11\uff0c\u5e94 \u58f0\u8bf4\u9053\uff1a\u201c\u662f\u7684\uff0c\u5341\u516b\uff0c\u5341\u516b\u5e74\u7684\u7f6a\u5b7d\uff01\u201d\u90a3\u8001\u5987\u54ed\u8bc9\u9053\uff1a\u201c\u4ed6\u672c\u6765\u4e0d\u559c\u6b22\u6211\uff0c\u8feb\u4e8e\u7236\u6bcd\u4e4b \u547d\u624d\u5a36\u4e86\u6211\uff0c\u6210\u5a5a\u4e4b\u540e\uff0c\u4ed6\u4e00\u9003\u5c31\u9003\u5230\u8fdc\u65b9\uff0c\u4e00\u53bb\u5c31\u53bb\u4e86\u5341\u516b\u5e74\uff0c\u73b0\u5728\u56de\u5fc3\u8f6c\u610f\u4e86\uff0c\u5374\u53c8\u5f97 \u4e86\u91cd\u75c5\uff0c\u5927\u4eba\u554a\uff01\u8fd9\u4e0d\u662f\u7f6a\u5b7d\u662f\u4ec0\u4e48\uff1f\u201d\u591a\u94ce\u8d8a\u542c\u8d8a\u4e0d\u662f\u5473\u9053\uff0c\u731b\u7136\u89c9\u5f97\u8fd9\u58f0\u97f3\u867d\u7136\u82cd\u8001\uff0c \u58f0\u8c03\u5374\u597d\u50cf\u662f\u4ee5\u524d\u542c\u8fc7\u7684\uff0c\u4ed6\u62db\u62db\u624b\u9053\uff1a\u201c\u4f60\u8fc7\u6765\uff01\u201d\u8001\u5987\u767d\u53d1\u98d8\u98d8\uff0c\u6301\u7740\u62d0\u6756\u7684\u624b\uff0c\u6296\u5f97 \u66f4\u662f\u5389\u5bb3\uff0c\u4e00\u6b65\u4e00\u6b65\uff0c\u8e52\u8dda\u8d70\u8fd1\u3002\u4eb2\u5175\u536b\u58eb\u4eec\u90fd\u5f88\u60ca\u5f02\u5730\u6ce8\u89c6\u770b\u5979\u3002\u738b\u7237\u80af\u8ba9\u4e00\u4e2a\u8001\u5987\u8fd1\u524d \u548c\u4ed6\u8bf4\u8bdd\uff0c\u8fd9\u53ef\u771f\u662f\u602a\u4e8b\u3002\u591a\u94ce\u53c8\u6325\u6325\u624b\u9053\uff1a\u201c\u4f60\u4eec\u8ba9\u5f00\u4e00\u4e9b\uff0c\u7531\u5979\u8fc7\u6765\uff01\u201d \u3000\u3000\u4e0d\u8bf4\u4eb2\u5175\u536b\u58eb\u4eec\u60ca\u5f02\uff0c\u6697\u4f0f\u5728\u5c71\u5d16\u6811\u836b\u4e4b\u4e0b\uff0c\u5047\u88c5\u6210\u9999\u5ba2\u7684\u7fa4\u8c6a\u4e5f\u65e0\u4e0d\u9a87\u5f02\uff0c\u4e2a\u4e2a\u5fc3\u4e2d \u8d5e\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u5b50\u771f\u6709\u4e24\u624b\uff0c\u6f14\u5f97\u8fd9\u4e48\u50cf\uff01\u201d \u3000\u3000\u8001\u5987\u4eba\u4e00\u6b65\u4e00\u6b65\u8d70\u5230\u4e86\u591a\u94ce\u7684\u9762\u524d\uff0c\u5401\u5401\u5598\u6c14\u3002\u591a\u94ce\u9053\uff1a\u201c\u4f60\u62ac\u8d77\u5934\u6765\uff01\u201d\u8001\u5987\u4eba\u624b\u81c2 \u4e00\u6296\uff0c\u62d0\u6756\u7a81\u7684\u65ad\u6210\u4e24\u6bb5\uff0c\u62d0\u6756\u4e2d\u85cf\u7740\u4e00\u67c4\u7cbe\u8292\u593a\u76ee\u7684\u5229\u5251\uff01\u75be\u5982\u95ea\u7535\u7684\u4e00\u5251\u5411\u591a\u94ce\u523a\u6765\uff0c \u591a\u8bd1\u9aa4\u51fa\u4e0d\u610f\uff0c\u95ea\u907f\u4e2d\u5de6\u81c2\u4e2d\u4e86\u4e00\u5251\uff0c\u4f46\u4ed6\u7684\u957f\u5251\u4e5f\u5df2\u62d4\u4e86\u51fa\u6765\uff0c\u547c\u7684\u4e00\u5251\u626b\u53bb\uff0c\u8001\u5987\u4eba\u4f4e \u5934\u8eb2\u907f\uff0c\u5251\u98ce\u9707\u8361\u4e2d\uff0c\u6ee1\u5934\u5047\u53d1\u90fd\u843d\u5728\u5730\u4e0a\uff0c\u8fd9\u54ea\u91cc\u662f\u4ec0\u4e48\u8001\u5987\u4eba\uff0c\u7adf\u662f\u4e00\u4e2a\u5999\u9f84\u5c11\u5973\uff01 \u3000\u3000\u5c31\u5728\u6b64\u9645\uff0c\u57cb\u4f0f\u5728\u5c71\u4e0a\u7684\u7fa4\u96c4\u7eb7\u7eb7\u6740\u51fa\u3002\u5916\u56f4\u7684\u4eb2\u5175\u4f8d\u536b\uff0c\u62fc\u529b\u6321\u4f4f\uff0c\u6709\u51e0\u4e2a\u7279\u9009\u536b \u58eb\uff0c\u60f3\u8fc7\u6765\u5e2e\u5fd9\u591a\u94ce\u3002\u591a\u94ce\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8d76\u5feb\u6321\u4f4f\u5916\u654c\uff0c\u4e0d\u5fc5\u8fc7\u6765\uff01\u201d\u536b\u58eb\u4eec\u90fd\u77e5\u9053\u591a\u94ce\u52c7 \u6b66\u975e\u51e1\uff0c\u672c\u9886\u7edd\u4e0d\u4f1a\u5728\u4ed6\u4eec\u4e4b\u4e0b\uff0c\u60f3\u6765\u64d2\u4e00\u4e2a\u5973\u5a03\u5b50\u5c1a\u4e0d\u8d39\u529b\uff0c\u800c\u5c71\u4e0a\u8dc3\u4e0b\u6765\u7684\u90a3\u73ed\u4eba\uff0c\u5374 \u662f\u51f6\u731b\u5341\u5206\uff0c\u56e0\u6b64\u4e5f\u5c31\u542c\u591a\u94ce\u4e4b\u8a00\uff0c\u56de\u8eab\u8d77\u4e0a\u524d\u53bb\uff0c\u548c\u7fa4\u96c4\u6df7\u6218\u3002 \u3000\u3000\u591a\u94ce\u5de6\u81c2\u53d7\u4f24\uff0c\u6124\u6012\u5f02\u5e38\uff0c\u4e00\u67c4\u957f\u5251\u4f7f\u5f97\u547c\u547c\u98ce\u54cd\uff01\u8fd9\u4f2a\u88c5\u8001\u5987\u7684\u5c11\u5973\u6b63\u662f\u6613\u5170\u73e0\uff0c\u5979 \u4e00\u51fb\u5f97\u624b\uff0c\u8eab\u5f62\u9aa4\u8d77\uff0c\u77ed\u5251\u8f7b\u7075\u8fc5\u6377\uff0c\u5de6\u51fb\u53f3\u523a\uff0c\u7247\u523b\u4e4b\u95f4\u5c31\u62c6\u4e86\u4e00\u4e8c\u5341\u62db\uff0c\u591a\u8bd1\u529b\u5927\u5982 \u725b\uff0c\u8155\u529b\u6c89\u96c4\u4e4b\u6781\uff0c\u6613\u5170\u73e0\u6c57\u6c34\u76f4\u6d41\uff0c\u9762\u4e0a\u7684\u6cb9\u5f69\u548c\u6c57\u6c34\u7c98\u5728\u4e00\u8d77\uff0c\u5341\u5206\u96be\u53d7\u3002\u5979\u767e\u5fd9\u4e2d\u7528 \u8896\u5b50\u4e00\u63e9\uff0c\u7528\u529b\u4e00\u62b9\uff0c\u9762\u4e0a\u7528\u6cb9\u5f69\u5316\u88c5\u6210\u7684\u76b1\u7eb9\uff0c\u62b9\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u9732\u51fa\u5e90\u5c71\u9762\u76ee\u3002\u554a\uff0c\u5e74\u9752 \u65f6\u5019\u7684\u738b\u5983\u597d\u50cf\u51fa\u73b0\u5728\u591a\u94ce\u9762\u524d\uff0c\u591a\u94ce\u60ca\u53eb\u4e00\u58f0\uff0c\u5c31\u5728\u4ed6\u60ca\u53eb\u7684\u540c\u65f6\uff0c\u5367\u4f5b\u5bfa\u5bfa\u95e8\u5927\u5f00\uff0c\u91cc \u9762\u62ac\u51fa\u4e00\u4e58\u7fe1\u7fe0\u5c0f\u8f7f\u3002 \u3000\u3000\u738b\u5983\u90a3\u665a\u7684\u58f0\u97f3\uff0c\u5ffd\u7136\u5728\u591a\u94ce\u5fc3\u5934\u91cd\u54cd\u8d77\u6765\uff1a\u201c\u4f60\u7b54\u5e94\u6211\uff0c\u4e0d\u8981\u4f24\u5bb3\u5979\uff0c\u53ef\u4ee5\u5417\uff1f\u201d\u591a \u94ce\u84e6\u7136\u773c\u524d\u53d1\u9ed1\uff0c\u4e00\u9635\u8ff7\u832b\uff0c\u6613\u5170\u73e0\u5237\uff01\u5237\uff01\u4e00\u8fde\u51e0\u5251\uff0c\u76f4\u8ffd\u8fc7\u6765\uff0c\u591a\u94ce\u8eab\u4e0a\u53c8\u53d7\u4e86\u51e0\u5904\u5251 \u4f24\uff0c\u591a\u94ce\u5706\u7741\u773c\u775b\uff0c\u5f85\u8981\u53d1\u529b\u8fd8\u51fb\u65f6\uff0c\u5251\u5149\u7ee6\u7ed5\u4e2d\uff0c\u53ea\u89c1\u8feb\u8fd1\u8eab\u524d\u7684\u5c11\u5973\u9177\u4f3c\u4ed6\u65b0\u5a5a\u4e4b\u591c\u7684 \u59bb\u5b50\u3002\u970e\u7684\u4e00\u9635\u5bd2\u610f\uff0c\u900f\u8fc7\u5fc3\u5934\uff0c\u80f8\u53e3\u53c8\u4e2d\u4e86\u4e00\u5251\u3002\u591a\u94ce\u5927\u58f0\u4e00\u53eb\uff0c\u957f\u5251\u8131\u624b\u63b7\u51fa\uff0c\u6613\u5170\u73e0 \u5f15\u8eab\u4e00\u907f\uff0c\u957f\u5251\u63b7\u4e2d\u4e00\u4e2a\u8d76\u6765\u62a2\u6551\u7684\u536b\u571f\uff0c\u81ea\u524d\u5fc3\u76f4\u900f\u8fc7\u540e\u5fc3\uff01 \u3000\u3000\u6613\u5170\u73e0\u5251\u6cd5\u4f55\u7b49\u5389\u5bb3\uff0c\u4e00\u95ea\u5373\u8fdb\uff0c\u591a\u94ce\u53cd\u638c\u4e00\u51fb\uff0c\u5494\u5693\u4e00\u58f0\uff0c\u4e94\u6307\u9f50\u65ad\uff0c\u6613\u5170\u73e0\u5237\u7684\u4e00 \u5251\uff0c\u5411\u54bd\u5589\u76f4\u63d2\u8fdb\u53bb\uff0c\u4f46\u56e0\u53d7\u4e86\u638c\u51fb\u4e4b\u529b\uff0c\u5251\u950b\u5fae\u504f\uff0c\u4e00\u5251\u81ea\u54bd\u5589\u7a7f\u8fc7\uff0c\u98df\u9053\u5589\u7ba1\u5374\u672a\u5272 \u65ad\uff0c\u591a\u94ce\u4e00\u58f0\u60e8\u53eb\uff0c\u9c9c\u8840\u98de\u6d8c\uff0c\u5012\u5728\u5f53\u573a\uff0c\u4eba\u5374\u5e76\u672a\u5373\u65f6\u6bd9\u547d\u3002 \u3000\u3000\u6613\u5170\u73e0\u6b63\u60f3\u5f2f\u8170\u8865\u4ed6\u4e00\u5251\uff0c\u90a3\u4e58\u5c0f\u8f7f\u5df2\u5230\u8ddf\u524d\uff0c\u8f7f\u4e2d\u8d70\u4e86\u4e00\u4e2a\u534e\u88c5\u8d35\u5987\uff0c\u53f3\u624b\u8f7b\u62ac\uff0c\u628a \u6613\u5170\u73e0\u624b\u8155\u6258\u4f4f\uff0c\u8fd9\u4e00\u5239\u90a3\uff0c\u6613\u5170\u73e0\u8eab\u5b50\u7a81\u7136\u6447\u6643\u8d77\u6765\uff0c\u77ed\u5251\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u6389\u5728\u5730\u4e0a\uff0c\u4e24 \u8fb9\u7684\u4eb2\u5175\u5305\u56f4\u8fc7\u6765\uff0c\u7acb\u5373\u628a\u5979\u53cd\u624b\u64d2\u4f4f\u3002\u6613\u5170\u73e0\u4e00\u70b9\u4e5f\u4e0d\u53cd\u6297\uff0c\u9762\u8272\u60e8\u767d\uff0c\u76ef\u7740\u90a3\u534e\u88c5\u8d35 \u5987\uff0c\u4f4e\u58f0\u60e8\u7b11\u9053\uff1a\u201c\u5c0a\u8d35\u7684\u738b\u5983\uff0c\u6211\uff0c\u6211\u5192\u72af\u4f60\u5566\uff01\u201d \u3000\u3000\u7eb3\u5170\u738b\u5983\u9762\u8272\u6b7b\u767d\uff0c\u4ec0\u4e48\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\uff03\u548c\u7136\u95f4\uff0c\u5979\u53d1\u89c9\u6709\u4eba\u5728\u5730\u4e0a\u7528\u529b\u62b1\u7740\u81ea\u5df1\u7684\u53cc \u811a\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u591a\u94ce\u9c9c\u8840\u6dcb\u6f13\uff0c\u62ac\u5934\u671b\u7740\u81ea\u5df1\uff0c\u738b\u5983\u4fef\u8170\u62c9\u770b\uff0c\u53ea\u542c\u5f97\u4ed6\u4f4e\u58f0\u8bf4\uff1a\u201c\u6211 \u8c22\u8c22\u4f60\uff01\u201d\u7eb3\u5170\u738b\u5983\u60e8\u53eb\u4e00\u58f0\uff0c\u6655\u5728\u5730\u4e0a\uff01 \u3000\u3000\u7fa4\u96c4\u5206\u5934\u6076\u6218\uff0c\u901a\u660e\u548c\u5c1a\u6700\u4e3a\u9a81\u52c7\uff0c\u5e26\u9886\u5e38\u82f1\u7a0b\u901a\u4e8c\u4eba\uff0c\u8d8a\u6740\u8d8a\u8fd1\u3002\u4ed6\u89c1\u6613\u5170\u73e0\u5df2\u662f\u5f97 \u624b\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u5ffd\u89c1\u738b\u5983\u51fa\u6765\uff0c\u6613\u5170\u73e0\u675f\u624b\u5c31\u64d2\uff0c\u53c8\u60ca\u53c8\u6025\uff0c\u62fc\u547d\u8d76\u53bb\uff0c\u89c1\u90a3\u4e9b\u8dd1\u6765\u63f4\u6551\u591a \u94ce\u7684\u536b\u58eb\uff0c\u4ea6\u5df2\u81ea\u8d76\u5230\uff0c\u901a\u660e\u548c\u5c1a\u773c\u7741\u7741\u5730\u770b\u7740\u6613\u5170\u73e0\u7ed9\u4e94\u82b1\u5927\u7ed1\uff0c\u62d6\u5165\u5bfa\u4e2d\uff0c\u591a\u94ce\u548c\u4ed6\u7684 \u738b\u5983\uff0c\u4e5f\u7ed9\u62ac\u8fdb\u53bb\u4e86\uff01 \u3000\u3000\u901a\u660e\u548c\u5c1a\u62a1\u5f00\u6212\u5200\uff0c\u864e\u864e\u98ce\u751f\uff0c\u5e26\u9886\u5e38\u82f1\u7a0b\u901a\u4e8c\u4eba\u8fd8\u5f85\u6740\u8fdb\u5bfa\u53bb\uff0c\u4f46\u4eca\u65e5\u62a4\u9001\u591a\u94ce\u7684\u536b \u58eb\u90fd\u662f\u9ad8\u624b\uff0c\u9163\u6218\u4e2d\u5e38\u82f1\u5927\u53eb\u4e00\u58f0\uff0c\u80a9\u5934\u4e2d\u4e86\u4e00\u628a\u67f3\u53f6\u98de\u5200\uff0c\u8840\u6d41\u5982\u6ce8\u3002\u901a\u660e\u4e5f\u53d7\u4e86\u4e24\u5904\u7bad \u4f24\u3002\u5f20\u534e\u662d\u6ee1\u8eab\u8840\u6c61\uff0c\u957f\u5251\u8fd0\u8f6c\u5982\u98ce\uff0c\u76f4\u4f3c\u4e00\u5934\u75af\u864e\uff0c\u9510\u4e0d\u53ef\u5f53\uff0c\u65ab\u6740\u8fdb\u6765\u3002\u901a\u660e\u548c\u5c1a\u594b\u529b \u6325\u5200\uff0c\u8fdb\u53bb\u548c\u4ed6\u4f1a\u5408\uff0c\u5f20\u534e\u662d\u5237\u7684\u4e00\u5251\u523a\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u4eec\u62fc\u4e86\uff01\u201d\u901a\u660e\u4fa7\u8eab\u4e00\u907f\uff0c\u53eb \u9053\uff1a\u201c\u662f\u6211\uff01\u201d\u5f20\u534e\u662d\u4e24\u773c\u5706\u7741\uff0c\u6447\u6447\u6b32\u5012\u3002\u901a\u660e\u548c\u5c1a\u6697\u53eb\u4e00\u58f0\u201c\u82e6\u4e5f\uff01\u201d\u51e0\u4e2a\u4eba\u5168\u90e8\u53d7 \u4f24\uff0c\u5982\u4f55\u6740\u5f97\u51fa\u53bb\uff1f \u3000\u3000\u6b63\u5371\u6025\u95f4\uff0c\u5ffd\u89c1\u4eb2\u5175\u4e24\u8fb9\u95ea\u5f00\uff0c\u6842\u4ef2\u660e\u6325\u52a8\u5b9d\u5251\uff0c\u4e00\u7247\u94f6\u6d9b\uff0c\u547c\u547c\u4e71\u821e\uff0c\u62fc\u6b7b\u6740\u8fdb\uff0c\u5f53 \u8005\u8f9f\u6613\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5feb\u95ef\u51fa\u53bb\uff01\u201d\u901a\u660e\u548c\u5c1a\u4e00\u628a\u62c9\u7740\u5f20\u534e\u662d\uff0c\u7d27\u8ddf\u7740\u6842\u4ef2\u660e\u95ef\u8def\u3002\u5192\u6d63\u83b2 \u5728\u5f20\u9752\u539f\u7b49\u4eba\u63a9\u62a4\u4e0b\uff0c\u5927\u6d12\u593a\u547d\u795e\u7802\uff0c\u4eb2\u5175\u536b\u58eb\u4eec\u6015\u4ed6\u4eec\u6740\u8fdb\u4f5b\u5bfa\uff0c\u7eb7\u7eb7\u8d76\u56de\u9632\u62a4\uff0c\u66f4\u517c\u89c1 \u4ed6\u4eec\u62fc\u6b7b\u593a\u8def\uff0c\u4e5f\u4e0d\u6562\u600e\u6837\u62e6\u622a\u3002\u7247\u523b\u4e4b\u95f4\uff0c\u95ef\u51fa\u91cd\u56f4\u3002\u7ffb\u5c71\u9003\u8d70\u3002 \u3000\u3000\u7eb3\u5170\u738b\u5983\u88ab\u62ac\u8fdb\u4f5b\u5bfa\u4e4b\u540e\uff0c\u60a0\u60a0\u9192\u8f6c\uff0c\u7741\u773c\u4e00\u770b\uff0c\u6613\u5170\u73e0\u5df2\u7ecf\u4e0d\u89c1\u3002\u4e00\u4e2a\u53c2\u5c06\u4e0a\u7684\u7980 \u9053\uff1a\u201c\u5973\u8d3c\u5df2\u6709\u4eba\u62bc\u5b88\uff0c\u51b3\u9003\u4e0d\u4e86\uff0c\u73b0\u5728\u98de\u9a6c\u53bb\u8bf7\u5fa1\u533b\uff0c\u8bf7\u738b\u5983\u5bbd\u5fc3\uff01\u201d\u7eb3\u5170\u738b\u5983\u6325\u6325\u624b \u9053\uff1a\u201c\u4f60\u4eec\u51fa\u53bb\uff01\u201d\u53c2\u5c06\u8e0c\u8e87\u4e0d\u8d70\uff0c\u591a\u94ce\u5ffd\u7136\u7741\u5f00\u773c\u775b\uff0c\u5636\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u51fa\u53bb\uff01\u201d\u53c2\u5c06\u4eb2 \u5175\u89c1\u738b\u7237\u529b\u7aed\u58f0\u5636\uff0c\u6ee1\u8eab\u659f\u8840\uff0c\u60c5\u77e5\u5c31\u662f\u5fa1\u533b\u9a6c\u4e0a\u5230\u6765\u4e5f\u5df2\u6551\u6cbb\u4e0d\u4e86\uff0c\u4ee5\u4e3a\u738b\u7237\u6709\u4ec0\u4e48\u4e34\u7ec8 \u9057\u8a00\uff0c\u8981\u5bf9\u738b\u5983\u5631\u5490\uff0c\u4e00\u58f0\u5e94\u8bfa\uff0c\u9000\u51fa\u7985\u623f\u3002 \u3000\u3000\u7eb3\u5170\u738b\u5983\u62ab\u5934\u6563\u53d1\uff0c\u9762\u8272\u6b7b\u767d\uff0c\u53cc\u81c2\u73af\u62b1\u591a\u94ce\uff0c\u5782\u6cea\u8bf4\u9053\uff1a\u201c\u738b\u7237\uff0c\u6709\u4e00\u4ef6\u4e8b\u6211\u7792\u4e86\u4f60 \u5f88\u4e45\uff0c\u8fd9\u4e2a\u5973\u523a\u5ba2\uff0c\u662f\u3001\u662f\u6211\u7684\u5973\u513f\u2026\u2026\u201d\u591a\u94ce\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u6211\uff0c\u6211\u65e9\u5df2\u77e5\u9053\uff01\u201d\u7eb3 \u5170\u738b\u5983\u653e\u58f0\u5927\u54ed\uff0c\u591a\u94ce\u624b\u8098\u652f\u5e8a\uff0c\u5ffd\u7136\u5750\u4e86\u8d77\u6765\uff0c\u6478\u7d22\u738b\u5983\u7684\u624b\uff0c\u4e00\u628a\u63e1\u4f4f\uff0c\u5636\u54d1\u8bf4\u9053\uff1a \u201c\u660e\u6167\uff0c\u6211\u5f88\u6ee1\u610f\uff0c\u4eca\u5929\u6211\u77e5\u9053\uff0c\u539f\u6765\u4f60\u4e5f\u7231\u6211\uff01\u201d\u738b\u5983\u4e00\u542c\uff0c\u5b9b\u5982\u4e07\u7bad\u7a7f\u5fc3\uff0c\u5979\u771f\u7684\u7231\u591a \u8bd1\uff1f\u8fd9\u53ea\u662f\u4e00\u79cd\u53ef\u601c\u7684\u7231\uff0c\u7136\u800c\u5728\u6b64\u523b\u4e2d\u4ed6\u4e34\u6b7b\u4e4b\u524d\uff0c\u5979\u5ffd\u800c\u89c9\u5f97\u597d\u50cf\u662f\u6709\u540d\u7231\u4e86\uff0c\u5979\u5782\u4e0b \u4e86\u5934\uff0c\u53e3\u5507\u8f7b\u8f7b\u5370\u4e0b\u591a\u94ce\u7684\u9762\u5b54\uff0c\u9c9c\u8840\u6d82\u6ee1\u5979\u7684\u5634\u5507\uff0c\u5979\u7684\u957f\u53d1\u3002\u591a\u94ce\u6162\u6162\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5973 \u513f\uff0c\u968f\u4f60\u5904\u7f6e\u5979\u5427\uff0c\u660e\u6167\uff0c\u6211\u5f88\u6ee1\u610f\u3002\u201d\u8d8a\u8bf4\u8d8a\u6162\uff0c\u58f0\u8c03\u4e5f\u8d8a\u6765\u8d8a\u4f4e\uff0c\u624b\u6307\u7f13\u7f13\u677e\u5f00\u3002\u7eb3\u5170 \u738b\u5983\u53ea\u89c9\u5634\u5507\u4e00\u7247\u51b0\u51b7\uff0c\u591a\u94ce\u5df2\u65ad\u4e86\u6c14\uff0c\u53cc\u773c\u7d27\u778c\uff0c\u4e00\u7791\u4e0d\u89c6\u3002 \u3000\u3000\u7eb3\u5170\u738b\u5983\u6050\u6016\u5f02\u5e38\uff0c\u6253\u5f00\u7985\u623f\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6765\u4eba\u5440\uff01\u201d\u4eb2\u5175\u4f8d\u536b\u4e00\u6d8c\u800c\u5165\uff0c\u970e\u90a3\u95f4\uff0c \u54ed\u58f0\u53eb\u58f0\uff0c\u6742\u5728\u4e00\u8d77\u3002\u7eb3\u5170\u738b\u5983\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u738b\u7237\u53bb\u4e16\u4e86\uff0c\u90a3\u5973\u8d3c\uff0c\u90a3\u5973\u8d3c\uff0c\u653e\u8d70\u5979\u5427\uff01\u201d \u53c2\u5c06\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u738b\u5983\uff0c\u4f60\u6b47\u6b47\uff01\u201d\u8d34\u8eab\u4e2b\u9ae6\uff0c\u8d76\u5feb\u6765\u6276\uff0c\u738b\u5983\u60e8\u53eb\u4e00\u58f0\uff0c\u53c8\u6655\u5728\u5730\u4e0a\u3002\u591a \u94ce\u7684\u968f\u8eab\u5c06\u9886\uff0c\u90fd\u4ee5\u4e3a\u738b\u5983\u5df2\u662f\u795e\u667a\u660f\u8ff7\uff0c\u201c\u653e\u5973\u8d3c\u201d\u4e4b\u8a00\uff0c\u5f53\u7136\u53ea\u662f\u201c\u4e71\u547d\u201d\uff0c\u5927\u5bb6\u53ea\u89c9 \u5979\u75c5\u51b5\u4e25\u91cd\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u771f\u7684\u653e\u8d70\u201c\u5973\u8d3c\u201d\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5404\u8def\u7edf\u5175\u5927\u5c06\uff0c\u5f97\u4e86\u4fe1\u606f\uff0c\u7eb7\u7eb7\u8d76 \u6765\u3002\u6613\u5170\u73e0\u4e5f\u7ed9\u6253\u8fdb\u5929\u7262\u53bb\u4e86\u3002 \u3000\u3000\u201c\u5973\u8d3c\u201d\u523a\u6740\u591a\u94ce\u4e4b\u540e\uff0c\u6ee1\u671d\u6587\u6b66\uff0c\u9f50\u90fd\u9707\u60ca\uff0c\u53ef\u662f\uff0c\u5947\u602a\u4e4b\u6781\uff0c\u534a\u4e2a\u6708\u8fc7\u53bb\u4e86\uff0c\u5973\u8d3c \u8fd8\u672a\u63d0\u5ba1\u3002\u8fd9\u6837\u7684\u5927\u6848\uff0c\u636e\u7406\u7687\u4e0a\u603b\u8981\u7279\u6d3e\u738b\u516c\u5927\u81e3\u5f00\u5b66\u5927\u5ba1\uff0c\u53ef\u662f\u8fd1\u652f\u4eb2\u738b\uff0c\u6587\u6b66\u5927\u81e3\uff0c \u8c01\u90fd\u6ca1\u6709\u63a5\u5230\u7687\u4e0a\u7684\u5fa1\u65e8\u3002\u987a\u5929\u5e9c\u91cc\uff0c\u4e5f\u6beb\u4e0d\u77e5\u60c5\u3002\u6709\u51e0\u4e2a\u4eb2\u738b\uff0c\u5927\u80c6\u53bb\u95ee\u7687\u5e1d\uff0c\u7687\u5e1d\u76b1\u76b1 \u7709\u5934\uff0c\u53ea\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u201c\u6715\u77e5\u9053\u4e86\uff01\u201d\u4eb2\u738b\u4eec\u9762\u9762\u76f8\u89d1\uff0c\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u77e5\uff0c\u5eb7\u7199\u7687\u5e1d\u4e5f\u7740\u5b9e\u4e0d\u5927\u9ad8\u5174\uff0c\u7eb3\u5170\u738b\u5983\u4eb2\u81ea\u53bb\u6c42\u592a\u540e\uff0c\u8bf7\u592a\u540e\u4ee3\u5979\u8f6c\u5411\u7687\u4e0a\u6c42 \u60c5\uff0c\u60f3\u7687\u4e0a\u7b49\u5979\u75c5\u597d\u4e4b\u540e\uff0c\u518d\u5ba1\u5973\u8d3c\u3002\u5eb7\u7199\u542c\u8bf4\u7eb3\u5170\u738b\u5983\u62b1\u75c5\u6c42\u60c5\uff0c\u4ee5\u4e3a\u5979\u5fc3\u75db\u4e08\u592b\uff0c\u523a\u6fc0 \u8fc7\u6df1\uff0c\u4ee5\u81f4\u917f\u6210\u5fc3\u75c5\u3002\u53c8\u4ee5\u4e3a\u5979\u60f3\u7b49\u75c5\u597d\u4e4b\u540e\uff0c\u4eb2\u81ea\u53bb\u5ba1\u5973\u8d3c\uff0c\u66ff\u592b\u62a5\u4ec7\u3002\u56e0\u6b64\u5c31\u7b54\u5141\u4e86\uff0c \u8c01\u77e5\u8fc7\u4e86\u534a\u6708\uff0c\u7eb3\u5170\u738b\u5983\u4ecd\u672a\u8fdb\u5bab\uff0c\u5fa1\u533b\u4f1a\u8bca\uff0c\u4e5f\u53ea\u662f\u8bf4\u6291\u90c1\u6210\u75c5\uff0c\u5e76\u65e0\u6027\u547d\u4e4b\u5fe7\u3002\u5eb7\u7199\u7687 \u5e1d\u5fc3\u91cc\u5df2\u6709\u70b9\u4e0d\u5927\u9ad8\u5174\u3002\u53ea\u662f\u9102\u4eb2\u738b\u529f\u52b3\u6781\u5927\uff0c\u4ed6\u7684\u738b\u5983\u53c8\u662f\u7eb3\u5170\u5bb9\u82e5\u7684\u59d1\u6bcd\uff0c\u7687\u5e1d\u867d\u7136\u4e0d \u5927\u9ad8\u5174\uff0c\u4e00\u65f6\u4e5f\u672a\u4fbf\u53d1\u4f5c\u51fa\u6765\u3002 \u3000\u3000\u7eb3\u5170\u738b\u5983\u8fd9\u4e2a\u534a\u6708\u6765\uff0c\u6bcf\u65e5\u6bcf\u591c\uff0c\u90fd\u5728\u75db\u82e6\u7684\u71ac\u714e\u4e0b\uff0c\u5979\u628a\u81ea\u5df1\u5173\u5728\u6df1\u95fa\uff0c\u9664\u4e86\u5949\u547d\u800c \u6765\u7684\u5fa1\u533b\uff0c\u4ec0\u4e48\u4eba\u4e5f\u4e0d\u89c1\u3002\u5979\u60f3\u8fc7\u6b7b\uff0c\u53ef\u662f\u5979\u8fd8\u6709\u672a\u4e86\u7684\u5fc3\u613f\uff0c\u5979\u8fd8\u60f3\u89c1\u89c1\u5979\u7684\u5973\u513f\u3002\u53ef\u662f \u600e\u6837\u53bb\u89c1\u5979\u7684\u5973\u513f\u5462\uff1f\u9664\u975e\u5979\u80fd\u628a\u5979\u653e\u8d70\uff0c\u5426\u5219\u65e9\u4e00\u5929\u89c1\u5979\uff0c\u5c31\u662f\u53eb\u5979\u65e9\u4e8c\u5929\u6b7b\u3002\u7687\u5e1d\u662f\u4ee5 \u4e3a\u5979\u8981\u4eb2\u81ea\u5ba1\u95f4\u7684\uff0c\u53ea\u4f8d\u5979\u89c1\u8fc7\u201c\u5973\u8d3c\u201d\u4e4b\u540e\uff0c\u90a3\u5973\u8d3c\u5c31\u8981\u53d7\u51cc\u8fdf\u5904\u6b7b\u4e86\u3002 \u3000\u3000\u4f46\u662f\u5979\u80fd\u628a\u5979\u7684\u5973\u513f\u653e\u8d70\u5417\uff1f\u5979\u6ca1\u6709\u8fd9\u4e2a\u6743\u529b\uff01\u4e0a\u81f3\u7687\u5e1d\uff0c\u4e0b\u81f3\u591a\u94ce\u5e10\u4e0b\u7684\u5404\u8def\u5c06\u519b\uff0c \u90fd\u4e0d\u80fd\u8ba9\u591a\u94ce\u767d\u767d\u6b7b\u6389\u7684\uff0c\u5979\u53ea\u597d\u4e00\u5929\u5929\u7684\u62d6\u4e0b\u6765\uff0c\u62d6\u5f97\u4e00\u5929\u5c31\u662f\u4e00\u5929\u3002 \u3000\u3000\u4e0d\u8bf4\u6ee1\u671d\u8bae\u8bba\u7eb7\u7eb7\uff0c\u8be7\u5f02\u4e4b\u6781\u3002\u7fa4\u96c4\u4e5f\u662f\u83ab\u540d\u5176\u5999\uff0c\u731c\u7591\u4e0d\u5b9a\u3002\u7fa4\u96c4\u5f53\u65e5\u9003\u56de\u4e4b\u540e\uff0c\u901a \u660e\u548c\u5c1a\u5c31\u5927\u53d1\u813e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8bd1\u7684\u738b\u5983\u771f\u662f\u4e2a\u5996\u5987\uff0c\u8fd9\u5973\u5a03\u5b50\u5df2\u6740\u4e86\u591a\u94ce\uff0c\u5468\u56f4\u53c8\u6ca1\u6709\u4ec0 \u4e48\u9ad8\u624b\u536b\u58eb\uff0c\u518d\u51b2\u51fa\u5341\u6b65\u516b\u6b65\uff0c\u5c31\u53ef\u4ee5\u548c\u6211\u4f1a\u5408\u4e86\u3002\u504f\u504f\u90a3\u4e2a\u65f6\u5019\uff0c\u738b\u5983\u51fa\u6765\uff0c\u6309\u8bf4\u8fd9\u5973\u5a03 \u5b50\u624b\u4e2d\u6709\u5b9d\u5251\uff0c\u738b\u5983\u53cc\u624b\u7a7a\u7a7a\uff0c\u96be\u9053\u8fd8\u80fd\u8d5b\u8fc7\u591a\u94ce\uff0c\u4e00\u5251\u523a\u53bb\uff0c\u4ec0\u4e48\u8fd8\u4e0d\u4e86\u7ed3\uff1f\u738b\u5983\u633a\u80f8\u6321 \u4f4f\u5b9d\u5251\uff0c\u90a3\u5973\u5a03\u5b50\u5c31\u4f3c\u4e2d\u4e86\u90aa\u4e00\u822c\uff0c\u53cc\u624b\u4f4e\u5782\uff0c\u5b9d\u5251\u8dcc\u843d\uff0c\u675f\u624b\u53d7\u64d2\uff0c\u771f\u662f\u6709\u9b3c\uff01\u201d\u77f3\u632f\u98de \u8fde\u9053\uff1a\u201c\u51a4\u5b7d\uff01\u201d\u5192\u6d63\u83b2\u5fc3\u4e2d\u731c\u5230\u51e0\u5206\uff0c\u5374\u4e0d\u6562\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u7fa4\u96c4\u4e5f\u672a\u5c1d\u4e0d\u60f3\u8425\u6551\uff0c\u53ef\u662f\u98ce\u58f0\u7d27\u6781\uff0c\u5168\u57ce\u5927\u641c\uff01\u77f3\u632f\u98de\u5c06\u7fa4\u96c4\u85cf\u5728\u5730\u4e0b\u5bc6\u5ba4\u4e4b\u4e2d\uff0c\u4ed7 \u7740\u4eac\u4e2d\u6355\u5feb\uff0c\u8bb8\u591a\u662f\u81ea\u5df1\u7684\u95e8\u751f\u540e\u8f88\uff0c\u7aed\u529b\u906e\u63a9\uff0c\u5dee\u5e78\u6ca1\u6709\u51fa\u4e8b\u3002\u53ef\u662f\u7fa4\u96c4\u4e5f\u4e0d\u80fd\u9732\u9762\u6551 \u4eba\uff0c\u7126\u6025\u4e4b\u6781\u3002\u77f3\u632f\u98de\u9053\uff1a\u201c\u5c31\u662f\u98ce\u58f0\u677e\u4e86\u4e0b\u6765\uff0c\u6050\u6015\u4e5f\u96be\u8425\u6551\u3002\u6211\u542c\u8bf4\u5927\u5185\u9ad8\u624b\uff0c\u51e0\u6709\u4e00 \u534a\u8c03\u53bb\u770b\u5b88\u5929\u7262\uff01\u6700\u6015\u6551\u4e0d\u51fa\u6765\uff0c\u81ea\u5df1\u8fd8\u8981\u635f\u6298\uff01\u201d\u5f20\u9752\u539f\u9053\uff1a\u201c\u6613\u5170\u73e0\u8fd9\u6b21\u820d\u8eab\u884c\u523a\uff0c\u867d \u9677\u5929\u7262\uff0c\u53ef\u662f\u5230\u5e95\u628a\u591a\u94ce\u9664\u53bb\u4e86\u3002\u8fd9\u6d88\u606f\u82e5\u4f20\u5230\u5ddd\u4e2d\uff0c\u674e\u5c06\u519b\u542c\u4e86\u4e0d\u77e5\u8981\u591a\u9ad8\u5174\u5462\uff01\u201d\u5192\u6d63 \u83b2\u5ffd\u7136\u7d27\u5f20\u95ee\u9053\uff1a\u201c\u5f20\u5927\u54e5\uff0c\u8fd9\u6d88\u606f\u6709\u6ca1\u6709\u98de\u62a5\u5ddd\u4e2d\uff1f\u201d\u5f20\u9752\u539f\u9053\uff1a\u201c\u591a\u8c22\u77f3\u8001\u9556\u5e08\u7684\u5e2e \u5fd9\uff0c\u5f53\u65e5\u5c31\u5df2\u6d3e\u4eba\u98de\u9a91\u51fa\u4eac\uff0c\u4e00\u7ad9\u7ad9\u7684\u5c06\u6d88\u606f\u4f20\u9012\u51fa\u53bb\u4e86\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u6211\u5012\u6709\u4e00\u4e2a\u7b28\u4e3b \u610f\uff0c\u53ea\u662f\u8981\u4e00\u4e2a\u6b66\u529f\u5353\u7edd\uff0c\u80c6\u5927\u5fc3\u7ec6\u7684\u4eba\u6765\u505a\u624d\u884c\u3002\u4ef2\u660e\u6b66\u529f\u867d\u8fc7\u5f97\u53bb\uff0c\u4f46\u4e0d\u591f\u673a\u7075\u3002\u6700\u597d \u662f\u51cc\u672a\u98ce\u6216\u8005\u5085\u9752\u4e3b\u80fd\u6765\u3002\u201d\u5f20\u9752\u539f\u9053\uff1a\u201c\u4ece\u56db\u5ddd\u5230\u5317\u4eac\uff0c\u6700\u5c11\u8981\u8d70\u4e00\u4e2a\u591a\u6708\uff0c\u5982\u4f55\u7b49\u5f97 \u53ca\uff01\u201d\u901a\u660e\u548c\u5c1a\u9053\uff1a\u201c\u4f60\u4e14\u628a\u4f60\u7684\u4e3b\u610f\u8bf4\u8bf4\u770b\u3002\u201d\u5192\u6d63\u83b2\u8e59\u7709\u8bf4\u9053\uff1a\u201c\u529e\u4e0d\u5230\u4e86\uff0c\u8bf4\u51fa\u6765\u5f92 \u4e71\u4eba\u610f\u3002\u201d\u901a\u660e\u548c\u5c1a\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u5b50\u602a\u60f9\u4eba\u75bc\u7684\uff0c\u60f3\u4e0d\u5230\u6211\u4eec\u773c\u7741\u7741\u5730\u770b\u5979\u53bb\u6b7b\u3002\u201d \u5f20\u534e\u662d\u9762\u8272\u82cd\u767d\u3002\u4e0d\u53d1\u4e00\u58f0\u3002\u77f3\u632f\u98de\u76ef\u4e86\u901a\u660e\u548c\u5c1a\u4e00\u773c\uff0c\u793a\u610f\u53eb\u4ed6\u4e0d\u8981\u591a\u8bf4\u3002 \u3000\u3000\u518d\u8bf4\u591a\u94ce\u88ab\u523a\u4e4b\u540e\uff0c\u7eb3\u5170\u5bb9\u82e5\u4e5f\u66fe\u53bb\u6170\u95ee\u4ed6\u7684\u59d1\u59d1\uff0c\u738b\u5983\u867d\u62d2\u7edd\u4f17\u4eba\u63a2\u95ee\uff0c\u5bf9\u5bb9\u82e5\u5374\u63a5 \u89c1\u4e86\uff0c\u53ea\u662f\u795e\u60c5\u6291\u90c1\uff0c\u4e0d\u80af\u8bf4\u8bdd\u3002\u7eb3\u5170\u5bb9\u82e5\u77e5\u9053\u8fd9\u5973\u8d3c\u5c31\u662f\u4ee5\u524d\u5728\u6e05\u51c9\u5bfa\u542c\u4ed6\u5f39\u7434\u7684\u4eba\uff0c\u5341 \u5206\u60ca\u8be7\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u73b0\u5728\u8fd8\u8bb0\u5f97\u5979\u7684\u76ee\u5149\uff0c\u90a3\u50cf\u5bd2\u6c34\u4e00\u6837\u4ee4\u4eba\u98a4\u6144\u7684\u76ee\u5149\uff0c\u53ea\u4e0d\u77e5\u5979\u4f55\u6545\u8981 \u523a\u6740\u59d1\u4e08\uff0c\u6709\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\uff01\u201d\u7eb3\u5170\u738b\u5983\u9ed8\u8a00\u4e0d\u8bed\uff0c\u826f\u4e45\u826f\u4e45\u624d\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5979\u4e5f\u602a\u53ef\u601c \u7684\uff01\u201d\u7eb3\u5170\u5bb9\u82e5\u84e6\u7136\u8bb0\u8d77\u8fd9\u5973\u8d3c\u7684\u5f62\u5bb9\u4f53\u6001\uff0c\u5f88\u50cf\u59d1\u59d1\uff0c\u6253\u4e86\u4e00\u4e2a\u5bd2\u895f\uff0c\u5f53\u4e0b\u4fbf\u5373\u544a\u9000\u3002 \u3000\u3000\u4e00\u665a\uff0c\u7eb3\u5170\u5bb9\u82e5\u72ec\u5750\u5929\u51e4\u697c\u4e2d\uff0c\u601d\u6f6e\u8d77\u4f0f\uff0c\u4e0d\u80fd\u81ea\u5df1\u3002\u4ed6\u662f\u6ee1\u6d32\u8d35\u65cf\uff0c\u53ef\u662f\u5374\u6709\u4e00\u9897\u5584 \u826f\u7684\u5fc3\u3002\u4ed6\u770b\u4e0d\u8d77\u8d35\u65cf\u4eec\u7684\u8d2a\u9119\u65e0\u80fd\uff0c\u4f46\u5bf9\u591a\u7ece\u5374\u8fd8\u6709\u4e00\u4e9b\u656c\u610f\u3002\u591a\u94ce\u5927\u5c06\u98ce\u5ea6\uff0c\u5728\u65d7\u4eba\u4e2d \u7b97\u5f97\u662f\u94c1\u94ee\u94ee\u7684\u6c49\u5b50\uff0c\u548c\u53e6\u5916\u90a3\u4e9b\u7687\u516c\u5927\u81e3\u6bd4\u8f83\uff0c\u76f8\u53bb\u4e0d\u53ef\u4ee5\u9053\u91cc\u8ba1\uff01\u4ed6\u5bf9\u591a\u94ce\u7684\u6b7b\uff0c\u611f\u5230 \u6709\u70b9\u60cb\u60dc\uff0c\u4f46\u5bf9\u90a3\u884c\u523a\u7684\u5973\u8d3c\uff0c\u5374\u4e5f\u4f3c\u6709\u70b9\u540c\u60c5\u3002\u4ed6\u60f3\uff1a\u4e00\u4e2a\u5e74\u9752\u7684\u5973\u5b69\u5b50\uff0c\u5982\u6b64\u5904\u5fc3\u79ef \u8651\u3001\u5192\u9669\u72af\u96be\uff0c\u8981\u53bb\u523a\u6740\u4e00\u4e2a\u4eba\uff0c\u90a3\u5979\u4e00\u5b9a\u6709\u975e\u5e38\u75db\u5fc3\u7684\u4e8b\uff0c\u4e0d\u80fd\u4e0d\u8fd9\u6837\u505a\u4e86\u3002\u4f46\u59d1\u59d1\u4e3a\u4ec0 \u4e48\u4e0d\u6068\u5979\u5462\uff1f\u4ed6\u60f3\u6765\u60f3\u53bb\uff0c\u90fd\u60f3\u4e0d\u51fa\u6240\u4ee5\u7136\u6765\u3002\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u96be\u9053\u771f\u7684\u51fa\u8eab\u7687\u5bb6\u5c31\u662f\u4e00\u79cd \u7f6a\u5b7d\uff01\u201d \u3000\u3000\u6b63\u5728\u7eb3\u5170\u5bb9\u82e5\u72ec\u81ea\u601d\u91cf\uff0c\u6c89\u541f\u81ea\u8bed\u4e4b\u9645\uff0c\u5ffd\u7136\u5c4b\u5185\u70db\u5149\u4e00\u95ea\uff0c\u7a97\u95e8\u5f00\u5904\uff0c\u8df3\u8fdb\u4e24\u4e2a\u4eba \u6765\uff0c\u4e00\u4e2a\u662f\u5f20\u534e\u662d\uff0c\u53e6\u4e00\u4e2a\u662f\u5999\u9f84\u5973\u5b50\uff0c\u76f8\u8c8c\u6781\u719f\uff0c\u6b63\u5f85\u53d1\u95ee\uff0c\u90a3\u5c11\u5973\u76c8\u76c8\u65bd\u793c\uff0c\u8bf4\u9053\uff1a \u201c\u516c\u5b50\uff0c\u8fd8\u8bb0\u5f97\u90a3\u4e2a\u770b\u56ed\u4eba\u5417\uff1f\u201d\u7eb3\u5170\u516c\u5b50\u54c8\u54c8\u4e00\u7b11\uff0c\u5f20\u534e\u662d\u9053\uff1a\u201c\u5979\u53eb\u5192\u6d63\u83b2\uff0c\u662f\u5192\u8f9f\u7586 \u5148\u751f\u7684\u5973\u516c\u5b50\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u9053\uff1a\u201c\u5192\u5148\u751f\u8bcd\u575b\u4fca\u5f66\uff0c\u524d\u8f88\u98ce\u6d41\uff0c\u6211\u662f\u5341\u5206\u94a6\u4f69\uff0c\u602a\u4e0d\u5f97\u5192\u59d1 \u5a18\u5999\u89e3\u8bcd\u7ae0\uff0c\u7cbe\u901a\u97f3\u5f8b\u3002\u53ea\u662f\u4e0d\u77e5\u5f53\u65e5\u4f55\u6545\u4e54\u88c5\uff0c\u5c48\u8eab\u5bd2\u820d\uff1f\u201d \u3000\u3000\u5192\u6d63\u83b2\u5ae3\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e9b\u4e8b\u60c5\uff0c\u5bb9\u540e\u5949\u544a\u3002\u6211\u4eec\u4eca\u65e5\u5230\u6b64\uff0c\u6709\u6025\u4e8b\u76f8\u6c42\uff0c\u6b64\u4e8b\u53ea \u6709\u516c\u5b50\u624d\u80fd\u63f4\u624b\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u9053\uff1a\u201c\u8bf7\u8bf4\uff01\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u6211\u4eec\u60f3\u89c1\u4e09\u516c\u4e3b\uff01\u201d\u7eb3\u5170\u5bb9\u82e5 \u9053\uff1a\u201c\u6b64\u523b\u4e0d\u6bd4\u4ece\u524d\uff0c\u81ea\u76f8\u5e9c\u90a3\u6b21\u95f9\u4e8b\u4e4b\u540e\uff0c\u516c\u4e3b\u5df2\u4e0d\u8bb8\u51fa\u5bab\u4e86\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u628a \u6211\u4eec\u5e26\u8fdb\u5bab\u53bb\uff01\u201d\u7eb3\u5170\u5bb9\u82e5\u9762\u8272\u4e00\u53d8\uff0c\u5192\u6d63\u83b2\u9053\uff1a\u201c\u662f\u4e0d\u662f\u6211\u4eec\u7684\u8981\u6c42\u592a\u8fc7\u5206\u4e86\uff1f\u201d\u7eb3\u5170\u5bb9 \u82e5\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u8981\u89c1\u4e09\u516c\u4e3b\uff0c\u4e3a\u7684\u662f\u4ec0\u4e48\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u6211\u4eec\u60f3\u6551\u4e00\u4e2a\u4eba\u3002\u201d\u7eb3\u5170\u5bb9 \u82e5\u9053\uff1a\u201c\u5c31\u662f\u523a\u6740\u9102\u4eb2\u738b\u7684\u90a3\u4e2a\u5c11\u5973\uff1f\u201d \u3000\u3000\u5f20\u534e\u662d\u4e0d\u987e\u4e00\u5207\uff0c\u8bf4\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u6211\u4eec\u5c31\u662f\u8981\u6551\u5979\uff01\u201d\u7eb3\u5170\u5bb9\u82e5\u6120\u9053\uff1a\u201c\u9102\u4eb2\u738b \u662f\u6211\u7684\u59d1\u4e08\uff0c\u96be\u9053\u4f60\u4eec\u4e0d\u77e5\u9053\u5417\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4f60\u7684\u59d1\u4e08\u6740\u4e86\u8bb8\u591a\u5584\u826f\u7684\u4eba\uff0c\u96be\u9053\u4f60\u4e0d\u77e5 \u9053\u5417\uff1f\u201d\u7eb3\u5170\u5bb9\u82e5\u9053\uff1a\u201c\u4ed6\u662f\u671d\u5ef7\u7684\u5927\u5c06\uff0c\u5949\u547d\u5f81\u8ba8\uff0c\u5927\u519b\u8fc7\u5904\uff0c\u5fc5\u6709\u4f24\u6b8b\uff0c\u8fd9\u4e5f\u4e0d\u80fd\u7b97\u5168 \u662f\u4ed6\u7684\u9519\u3002\u201d\u5192\u6d63\u83b2\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u662f\u8001\u767e\u59d3\u9519\u4e86\uff1f\u201d\u7eb3\u5170\u5bb9\u82e5\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u3002\u201d\u5192\u6d63\u83b2 \u9053\uff1a\u201c\u4ed6\u53ef\u4ee5\u6740\u522b\u4eba\uff0c\u96be\u9053\u522b\u4eba\u5c31\u4e0d\u80fd\u6740\u4ed6\uff1f\u201d\u7eb3\u5170\u5bb9\u82e5\u53f9\u9053\uff1a\u201c\u8fd9\u6837\u51a4\u51a4\u76f8\u62a5\uff0c\u4ee5\u8840\u8fd8 \u8840\uff0c\u5982\u4f55\u5f97\u4e86\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4eec\u5e76\u4e0d\u662f\u548c\u6ee1\u6d32\u4eba\u6709\u4ec7\uff0c\u4f46\u50cf\u591a\u94ce\u90a3\u6837\uff0c\u5e26\u6ee1\u6d32\u4eba\u6765 \u6253\u6c49\u4eba\u7684\uff0c\u6211\u4eec\u5374\u96be\u653e\u8fc7\u3002\u201d \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u9ed8\u7136\u4e0d\u8bed\u3002\u5192\u6d63\u83b2\u53c8\u9053\uff0c\u201c\u4f60\u4eec\u82e5\u518d\u628a\u8fd9\u65e0\u8f9c\u7684\u5c11\u5973\u6740\u4e86\uff0c\u90a3\u662f\u8840\u4e0a\u52a0\u8840\uff01\u201d \u7eb3\u5170\u4ecd\u7136\u4e0d\u8bed\uff0c\u5192\u9662\u83b2\u4e00\u9635\u72c2\u7b11\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u53ea\u9053\u516c\u5b50\u4eba\u5982\u5176\u8bcd\uff0c\u660e\u6717\u768e\u6d01\u5982\u78a7\u6d77\u6f84 \u6ce2\uff0c\u4e0d\u6599\u5374\u662f\u6211\u4eec\u770b\u9519\u4e86\uff03\u745a\u544a\u516c\u5b50\uff0c\u6211\u4eec\u5c31\u662f\u2018\u5973\u8d3c\u2019\u7684\u540c\u515a\uff0c\u516c\u5b50\u82e5\u4e0d\u662f\u7559\u6211\u4eec\uff0c\u6211\u4eec \u5c31\u6b64\u544a\u8f9e\uff01\u201d\u7eb3\u5170\u5bb9\u82e5\u8863\u8896\u4e00\u62c2\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u6307\u7740\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4f60\u660e\u65e5\u968f\u6211\u8fdb\u5bab\uff01\u201d\u5192\u6d63\u83b2 \u559c\u9053\uff1a\u201c\u5c31\u8bf7\u501f\u7b14\u781a\u4e00\u7528\u3002\u201d\u5f20\u534e\u662d\u5373\u5e2d\u6325\u6beb\uff0c\u5199\u4e86\u6ee1\u6ee1\u4e00\u5f20\u4fe1\u7b3a\uff0c\u5c01\u597d\u4ea4\u7ed9\u5192\u6d63\u83b2\u3002\u5411\u7eb3 \u5170\u5bb9\u82e5\u4e00\u63d6\u5230\u5730\uff0c\u98de\u8eab\u4fbf\u51fa\uff01 \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u6700\u559c\u7ed3\u4ea4\u624d\u4eba\u5f02\u58eb\uff0c\u66f4\u4f55\u51b5\u5192\u6d63\u83b2\u8fd9\u6837\u6587\u6b66\u5168\u6750\uff0c\u6e05\u4e3d\u7edd\u4fd7\u7684\u59d1\u5a18\u3002\u4ed6\u89c1\u5192\u9662 \u83b2\u7b11\u8bed\u76c8\u76c8\uff0c\u795e\u601d\u4e00\u8361\uff0c\u5ffd\u7136\u60f3\u8d77\u90a3\u4e2a\u201c\u7c97\u7c97\u9c81\u9c81\u201d\u7684\u53e6\u4e00\u4e2a\u201c\u56ed\u4e01\u201d\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u90a3\u4e2a\u540c \u4f34\u5462\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4ed6\u5728\u5916\u9762\u63a5\u5e94\u662d\u90ce\uff0c\u4e0d\u8fdb\u6765\u4e86\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u9053\uff1a\u201c\u4ed6\u653e\u5fc3\u4f60\u4e00\u4e2a\u4eba\u548c \u6211\u8fdb\u5bab\uff1f\u201d\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u4ed6\u867d\u7c97\u9c81\uff0c\u4eba\u5374\u723d\u76f4\u3002\u6211\u6781\u9053\u516c\u5b50\u8d85\u8131\u7edd\u4fd7\uff0c\u4ed6\u5c06\u6765\u8fd8\u8981\u5411\u516c\u5b50 \u81f4\u8c22\u5462\uff01\u201d\u7eb3\u5170\u5bb9\u82e5\u7ec6\u4e00\u7422\u78e8\uff0c\u5fc3\u4e2d\u4e86\u4e86\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u82f1\u96c4\u513f\u5973\uff0c\u771f\u662f\u4e00\u5bf9\u4f73\u5076\uff01\u201d \u5176\u5b9e\u4ed6\u5fc3\u91cc\u7684\u8bdd\u5374\u662f\u201c\u4f60\u8fd9\u53ef\u662f\u5f69\u51e4\u968f\u9e26\uff01\u201d\u5192\u6d63\u83b2\u6ee1\u6000\u559c\u60a6\uff0c\u542b\u7b11\u7b54\u9053\uff1a\u201c\u591a\u627f\u516c\u5b50\u79f0 \u8d5e\uff0c\u53ea\u662f\u6211\u7684\u672c\u9886\u53ef\u6bd4\u4ed6\u5dee\u5f97\u8fdc\u5462\uff01\u201d\u7eb3\u5170\u516c\u5b50\u77e5\u9053\u5979\u5bf9\u90a3\u4e2a\u201c\u7c97\u9c81\u201d\u56ed\u4e01\uff0c\u76f8\u7231\u6781\u6df1\uff0c\u5fc3 \u5185\u6697\u6697\u53f9\u9053\uff1a\u201c\u7f18\u4e4b\u4e00\u5b57\uff0c\u771f\u662f\u5947\u5999\u3002\u6bcf\u4eba\u90fd\u6709\u4ed6\u7684\u7f18\u4efd\uff0c\u4e00\u682a\u8349\u6709\u4e00\u6ef4\u9732\u73e0\uff0c\u8fd9\u771f\u662f\u6ca1\u6709 \u4ec0\u4e48\u53ef\u8bf4\u7684\uff01\u201d\u4ed6\u795e\u90ce\u6c14\u6e05\uff0c\u60c5\u6000\u987f\u8c41\u3002\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u6210\u4eb2\u4e86\u6ca1\u6709\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u5c1a \u672a\uff01\u201d\u7eb3\u5170\u516c\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5f02\u65e5\u6210\u4eb2\uff0c\u6211\u5fc5\u4e0d\u80fd\u4eb2\u4e34\u9053\u559c\uff0c\u4eca\u65e5\u6211\u5c31\u9001\u4f60\u4e00\u4ef6\u8584\u793c\u5427\u3002\u201d \u8bf4\u7f62\u5728\u5899\u4e0a\u53d6\u51fa\u4e00\u67c4\u77ed\u5251\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u6b64\u5251\u540d\u4e3a\u5927\u8679\uff0c\u662f\u4e00\u4e2a\u603b\u7763\u9001\u7ed9\u6211\u7236\u4eb2\u7684\uff0c\u542c\u8bf4\u662f\u664b \u671d\u6853\u6e29\u7684\u4f69\u5251\uff0c\u4ed6\u4eec\u8bf4\u662f\u4e00\u628a\u5b9d\u5251\u3002\u4f60\u62ff\u53bb\u7528\u5427\u3002\u201d\u5192\u6d63\u83b2\u62d4\u5251\u4e00\u770b\uff0c\u53ea\u89c1\u53e4\u8272\u6591\u8c30\uff0c\u4f46\u7565 \u4e00\u6325\u52a8\uff0c\u5374\u662f\u5bd2\u5149\u8000\u76ee\u3002\u5fc3\u4e2d\u5927\u559c\uff0c\u6b63\u60f3\u9053\u8c22\uff0c\u7eb3\u5170\u516c\u5b50\u888d\u8896\u4e00\u62c2\uff0c\u7b11\u9053\uff1a\u201c\u82e5\u518d\u5ba2\u5957\uff0c\u4fbf \u662f\u4fd7\u4eba\uff01\u201d\u81ea\u8fdb\u5185\u623f\u6b47\u606f\u53bb\u4e86\u3002\u5192\u6d63\u83b2\u89c1\u7eb3\u5170\u516c\u5b50\u5982\u6b64\u6d12\u8131\uff0c\u4e5f\u4e0d\u7981\u6697\u6697\u8d5e\u53f9\u3002 \u3000\u3000\u591a\u94ce\u7684\u6b7b\u8baf\u4e5f\u4f20\u8fdb\u4e86\u5bab\u4e2d\uff0c\u53ef\u662f\u5374\u8fdc\u4e0d\u5982\u5916\u95f4\u5f15\u8d77\u90a3\u4e48\u5927\u7684\u6ce2\u52a8\u3002\u90a3\u4e9b\u5bab\u5a25\u5ad4\u5983\uff0c\u6101\u9501 \u6df1\u5bab\uff0c\u5916\u95f4\u7684\u4e8b\u60c5\uff0c\u51e0\u4e0e\u5979\u4eec\u6f20\u4e0d\u76f8\u5173\uff0c\u591a\u94ce\u7684\u6b7b\uff0c\u4e0d\u8fc7\u662f\u7ed9\u5979\u4eec\u6dfb\u4e86\u4e00\u4e9b\u8336\u4f59\u996d\u540e\u7684\u95f2\u8c08 \u8d44\u6599\uff0c\u8c08\u8fc7\u4e5f\u5c31\u7b97\u4e86\u3002 \u3000\u3000\u591a\u94ce\u662f\u4e09\u516c\u4e3b\u6240\u719f\u6089\u7684\u4eba\uff0c\u5979\u521d\u542c\u5230\u65f6\uff0c\u5012\u662f\u5fae\u5fae\u4e00\u9707\uff0c\u53ef\u662f\u5979\u7684\u5fc3\u4e2d\uff0c\u6b63\u4e5f\u5145\u6ee1\u6101 \u601d\uff0c\u591a\u94ce\u5728\u5979\u5fc3\u4e2d\uff0c\u5e76\u6ca1\u6709\u5360\u4ec0\u4e48\u4f4d\u7f6e\u3002\u585e\u6ee1\u5979\u5fc3\u4e2d\u7684\u662f\u5f20\u534e\u662d\u7684\u5f71\u5b50\uff0c\u8d77\u521d\u662f\u65b0\u5947\u548c\u523a \u6fc0\uff0c\u6e10\u6e10\uff0c\u5f20\u534e\u662d\u7684\u4e00\u8a00\u4e00\u7b11\uff0c\u4e00\u4e3e\u4e00\u52a8\uff0c\u90fd\u5728\u56de\u5fc6\u4e2d\u91cd\u73b0\u51fa\u6765\uff0c\u7d27\u7d27\u5730\u5438\u7740\u4e86\u5979\u7684\u5fc3\u7075\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u4f4f\u5728\u201c\u94a6\u5b89\u6bbf\u201d\uff0c\u4f4d\u5c45\u5fa1\u82b1\u56ed\u7684\u4e2d\u592e\uff0c\u79cb\u6df1\u65f6\u5206\uff0c\u67ab\u53f6\u98d8\u96f6\uff0c\u6b8b\u8377\u7247\u7247\uff0c\u5bd2\u9e26\u566a \u6811\uff0c\u8749\u66f3\u6b8b\u58f0\uff0c\u4e00\u65e5\u9ec4\u660f\uff0c\u4e09\u516c\u4e3b\u63ed\u5e18\u51dd\u671b\uff0c\u89c1\u504c\u5927\u4e00\u4e2a\u56ed\u5b50\u9759\u6084\u4fcf\u7684\uff0c\u8fdc\u5904\u6709\u51e0\u540d\u592a\u76d1\u5728 \u626b\u6b8b\u82b1\u8d25\u53f6\uff0c\u5fa1\u82b1\u56ed\u867d\u7136\u662f\u5efa\u7b51\u534e\u7f8e\uff0c\u6c14\u8c61\u4e07\u5343\uff0c\u5374\u6df9\u4e0d\u4e86\u90a3\u8870\u853d\u4e4b\u611f\u3002\u4e09\u516c\u4e3b\u6291\u90c1\u60c5\u6000\uff0c \u65e0\u7531\u6392\u9063\uff0c\u767e\u5143\u804a\u8d56\uff0c\u5728\u4e66\u6848\u4e0a\u62c8\u8d77\u4e00\u5e45\u8bcd\u7b3a\uff0c\u4f4e\u58f0\u541f\u8bf5\uff1a \u3000\u3000\u201c\u96fe\u7a97\u5bd2\u5bf9\u9065\u5929\u66ae\uff0c\u66ae\u5929\u9065\u5bf9\u5bd2\u7a97\u96fe\uff0c\u82b1\u843d\u6b63\u557c\u9e26\uff0c\u557c\u6b63\u843d\u82b1\u3002\u8896\u7f57\u5782\u5f71\u7626\uff0c\u7626\u5f71\u5782\u7f57 \u8896\uff0c\u98ce\u526a\u4e00\u4e1d\u7ea2\uff0c\u7ea2\u4e1d\u4e00\u526a\u98ce\u3002\u201d \u3000\u3000\u8fd9\u9996\u8bcd\u540d\u4e3a\u201c\u83e9\u8428\u86ee\u201d\uff0c\u662f\u4e00\u9996\u201c\u56de\u6587\u8bcd\u201d\uff0c\u6bcf\u4e00\u53e5\u90fd\u53ef\u98a0\u5012\u6765\u8bfb\uff0c\u5168\u9996\u8bcd\u867d\u6709\u516b\u53e5\uff0c \u5b9e\u9645\u53ea\u662f\u56db\u53e5\u3002\u7eb3\u5170\u5bb9\u82e5\u524d\u4e9b\u65f6\u5019\uff0c\u4e00\u65f6\u9ad8\u5174\uff0c\u586b\u4e86\u4e09\u9996\u201c\u56de\u6587\u201d\u7684\u201c\u83e9\u8428\u86ee\u201d\u8bcd\uff0c\u6284\u4e86\u4e00 \u4efd\u9001\u7ed9\u4e09\u516c\u4e3b\uff0c\u8fd9\u9996\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u4e09\u516c\u4e3b\u53f9\u4e86\u53e3\u6c14\uff0c\u60f3\u9053\uff1a\u8fd9\u9996\u8bcd\u5c31\u597d\u50cf\u5199\u6211\u7684\u5fc3\u4e8b\u4f3c \u7684\u3002\u6211\u73b0\u5728\u6000\u5ff5\u4f0a\u4eba\uff0c\u6005\u671b\u9065\u5929\uff0c\u4e5f\u662f\u7626\u635f\u8170\u56f4\uff0c\u6cea\u6cbe\u7f57\u8896\u5462\uff01\u5979\u65e2\u7231\u8bcd\u7684\u5de7\u601d\uff0c\u66f4\u7231\u8bcd\u7684 \u60c5\u8c03\uff0c\u4e8e\u662f\u53c8\u5c55\u5f00\u7b2c\u4e8c\u9996\u201c\u56de\u6587\u201d\u7684\u201c\u83e9\u8428\u86ee\u201d\u8bfb\u9053\uff1a \u3000\u3000\u201c\u5ba2\u4e2d\u6101\u635f\u50ac\u5bd2\u5915\uff0c\u5915\u5bd2\u50ac\u635f\u6101\u4e2d\u5ba2\u3002\u95e8\u63a9\u6708\u9ec4\u660f\uff0c\u660f\u9ec4\u6708\u63a9\u95e8\u3002\u7fe0\u84d1\u5b64\u62e5\u9189\uff0c\u9189\u62e5\u5b64 \u84d1\u7fe0\u3002\u9192\u83ab\u66f4\u591a\u60c5\uff0c\u60c5\u591a\u66f4\u83ab\u9192\u3002\u201d \u3000\u3000\u8fd9\u9996\u8bcd\u6bd4\u524d\u4e00\u9996\u66f4\u4e3a\u5e7d\u6028\uff0c\u4e09\u516c\u4e3b\u5480\u56bc\u201c\u9192\u83ab\u66f4\u591a\u60c5\uff0c\u60c5\u591a\u66f4\u83ab\u9192\u201d\u4e24\u53e5\uff0c\u5fc3\u5934\u4e0a\u5c31\u597d \u4f3c\u6709\u5343\u65a4\u91cd\u538b\u4e00\u6837\uff0c\u5979\u660e\u77e5\u548c\u5f20\u534e\u662d\u7684\u8eab\u4efd\u60ac\u6b8a\uff0c\u53ea\u8981\u662f\u795e\u5fd7\u6e05\u9192\u7684\u4eba\uff0c\u90fd\u77e5\u9053\u8fd9\u662f\u7edd\u4e0d\u53ef \u80fd\u7684\u4e8b\u3002\u53ef\u662f\u4e3a\u4ec0\u4e48\u8981\u9192\u6765\u5462\uff1f\u9192\u4e86\u5c31\u83ab\u66f4\u591a\u60c5\uff0c\u60c5\u591a\u5c31\u522b\u8981\u9192\u6765\u554a\uff01 \u3000\u3000\u4e09\u516c\u4e3b\u795e\u601d\u8ff7\u60af\uff0c\u6b63\u60f3\u5c55\u8bfb\u7b2c\u4e09\u9996\uff0c\u5ffd\u542c\u5f97\u5bab\u5a25\u4e0a\u524d\u62a5\u9053\uff1a\u201c\u7eb3\u5170\u516c\u5b50\u6765\u4e86\uff01\u201d\u4e09\u516c\u4e3b \u6697\u7b11\u81ea\u5df1\u8bfb\u8bcd\u8bfb\u5f97\u51fa\u795e\uff0c\u8fde\u8bcd\u7684\u4f5c\u8005\u4ece\u7a97\u5916\u8d70\u8fc7\u4e5f\u6ca1\u6ce8\u610f\u3002 \u3000\u3000\u7ee3\u5e18\u5f00\u5904\uff0c\u7eb3\u5170\u5bb9\u82e5\u8f7b\u8f7b\u8d70\u8fdb\uff0c\u7b11\u9053\uff1a\u201c\u4e09\u59b9\u59b9\uff0c\u4f60\u597d\u7528\u529f\uff01\u201d\u4e09\u516c\u4e3b\u4e00\u770b\uff0c\u7eb3\u5170\u5bb9\u82e5 \u540e\u9762\uff0c\u8fd8\u6709\u4e00\u4f4d\u5999\u9f84\u5c11\u5973\uff0c\u9762\u8c8c\u597d\u719f\uff0c\u7ec6\u7ec6\u4e00\u60f3\uff0c\u4e00\u9897\u5fc3\u4e0d\u7981\u535c\u535c\u8df3\u4e86\u8d77\u6765\u3002\u8fd9\u5c11\u5973\u6b63\u5f53\u65e5 \u5728\u5929\u51e4\u697c\u89c1\u8fc7\u7684\uff0c\u5f53\u65f6\u662f\u5973\u626e\u7537\u88c5\u7684\u5192\u6d63\u83b2\uff01\u4e09\u516c\u4e3b\u89c1\u5bab\u5a25\u4f8d\u5019\u5728\u65c1\uff0c\u5411\u7eb3\u5170\u5bb9\u82e5\u6253\u4e86\u4e00\u4e2a \u773c\u8272\uff0c\u7eb3\u5170\u5bb9\u82e5\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u7687\u4e0a\u8981\u6211\u5728\u5357\u4e66\u623f\u4f34\u8bfb\uff0c\u4eca\u665a\u6211\u4e0d\u56de\u53bb\u4e86\uff0c\u8fd9\u4e2a\u4e2b\u74b0\uff0c\u5c31\u7559\u5728 \u4f60\u8fd9\u91cc\u5427\uff01\u201d \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u53bb\u540e\uff0c\u4e09\u516c\u4e3b\u628a\u5bab\u5a25\u4f8d\u5973\u652f\u5f00\uff0c\u643a\u5192\u6d63\u83b2\u8d70\u5165\u5185\u5ba4\uff0c\u4e00\u628a\u697c\u7740\u9053\uff1a\u201c\u5192\u59d0\u59d0\uff0c \u6211\u60f3\u5f97\u4f60\u4eec\u597d\u82e6\uff01\u201d\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u60f3\u6211\u5427\u3002\u201d\u4e09\u516c\u4e3b\u561f\u7740\u5c0f\u5634\uff0c\u4f6f\u55d4\u9053\uff1a\u201c\u4e0d\u662f\u60f3\u4f60 \u60f3\u8c01\uff1f\u201d\u5192\u6d63\u83b2\u5fae\u5fae\u4e00\u7b11\uff0c\u5728\u6000\u91cc\u638f\u51fa\u4fe1\u6765\uff0c\u7389\u624b\u4e00\u626c\uff0c\u4e09\u516c\u4e3b\u4e00\u89c1\u5927\u559c\uff0c\u987e\u4e0d\u5f97\u5192\u6d63\u83b2\u5632 \u7b11\uff0c\u4e00\u628a\u62a2\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u4fe1\u5c01\u4fe1\u6b63\u662f\u5f20\u534e\u662d\u6258\u5192\u6d63\u83b2\u8f6c\u4ea4\u7ed9\u4e09\u516c\u4e3b\u7684\u4fe1\uff0c\u5192\u6d63\u83b2\u89c1\u4e09\u516c\u4e3b\u5c55\u5f00\u4fe1\u7b3a\uff0c\u4e00\u9762\u8bfb\u4e00 \u9762\u5fae\u7b11\uff0c\u5ffd\u7136\u9762\u8272\u5927\u53d8\uff0c\u624b\u6307\u98a4\u6296\u3002\u90a3\u5f20\u4fe1\u7b3a\u50cf\u7ed9\u5fae\u98ce\u5439\u62c2\u4e00\u6837\uff0c\u5728\u624b\u4e2d\u9707\u52a8\u4e0d\u5df2\uff0c\u90a3\u5c01\u4fe1 \u5f00\u5934\u5199\u9053\uff1a\u201c\u843d\u62d3\u6c5f\u6e56\uff0c\u98d8\u96f6\u84ec\u6897\uff0c\u6258\u5e87\u76f8\u5e9c\uff0c\u5e78\u63a5\u6731\u989c\u3002\u627f\u8499\u8d60\u836f\u4e4b\u6069\uff0c\u4e43\u7ed3\u6b8a\u65b9\u4e4b\u53cb\uff0c \u65b9\u6068\u62a5\u7b54\u4e4b\u65e0\u7531\uff0c\u53c8\u6709\u4e0d\u8bf7\u4e4b\u8bf7\u6258\u3002\u201d\u516c\u4e3b\u8bfb\u65f6\uff0c\u89c1\u5f20\u534e\u662d\u5199\u5f97\u8fd9\u6837\u8bda\u631a\uff0c\u4e0d\u4f46\u611f\u8c22\u81ea\u5df1\uff0c \u800c\u4e14\u627f\u8ba4\u81ea\u5df1\u662f\u4ed6\u7684\u53cb\u4eba\uff0c\u5fc3\u5934\u611f\u5230\u751c\u4e1d\u4e1d\u7684\uff0c\u597d\u4e0d\u8212\u670d\u3002\u5979\u60f3\uff1a\u201c\u53ea\u8981\u662f\u4f60\u5f00\u53e3\u7684\uff0c\u4ec0\u4e48 \u8bf7\u6258\uff0c\u6211\u90fd\u53ef\u4ee5\u5e94\u627f\u3002\u201d\u54ea\u6599\u518d\u8bfb\u4e0b\u53bb\uff0c\u8bb2\u7684\u5374\u662f\u523a\u6740\u591a\u94ce\u7684\u90a3\u4e2a\u5973\u8d3c\u4e4b\u4e8b\u3002\u4fe1\u4e0a\u5199\u9053\uff1a \u2018\u6b64\u5973\u8d3c\u867d\u541b\u5bb6\u4e4b\u5927\u4ec7\uff0c\u5b9e\u534e\u662d\u4e4b\u631a\u53cb\u3002\u671d\u5ef7\u6b32\u5176\u6b7b\uff0c\u534e\u662d\u6b32\u5176\u751f\uff0c\u5f7c\u82e6\u4f24\u6298\uff0c\u662d\u96be\u72ec\u6d3b\u3002 \u516c\u4e3b\u82e5\u80fd\u63f4\u624b\uff0c\u5219\u662d\u6709\u751f\u4e4b\u5e74\uff0c\u7686\u5f53\u94ed\u611f\u3002\u201d\u7ec6\u54c1\u5473\u4fe1\u4e2d\u8bed\u6c14\uff0c\u5f20\u534e\u662d\u5bf9\u90a3\u4e2a\u5973\u8d3c\uff0c\u5b9e\u662f\u60c5 \u6df1\u4e00\u7247\uff0c\u6bd4\u5bf9\u81ea\u5df1\uff0c\u7adf\u662f\u6df1\u539a\u5f97\u591a\u3002\u4e09\u516c\u4e3b\u773c\u524d\u4e00\u7247\u6a21\u7cca\uff0c\u6cea\u73e0\u8f7b\u8f7b\u6eda\u4e86\u4e0b\u6765\uff0c\u4fe1\u7b3a\u8dcc\u5728\u5730 \u4e0a\u3002 \u3000\u3000\u5192\u6d63\u83b2\u867d\u7136\u4e0d\u77e5\u9053\u4fe1\u4e2d\u5199\u7684\u4ec0\u4e48\uff0c\u770b\u6b64\u60c5\u5f62\uff0c\u5df2\u731c\u5230\u51e0\u5206\uff0c\u5979\u629a\u7740\u516c\u4e3b\u7684\u957f\u53d1\uff0c\u7231\u601c\u5730 \u53eb\u9053\uff1a\u201c\u516c\u4e3b\uff01\u201d \u3000\u3000\u516c\u4e3b\u62fe\u8d77\u4fe1\u7b3a\uff0c\u9893\u7136\u5750\u4e0b\uff0c\u826f\u4e45\uff0c\u826f\u4e45\uff0c\u5ffd\u7136\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u6211\u4e0d\u80fd\u7ba1\uff0c\u4e5f\u6ca1\u6709\u529e \u6cd5\u7ba1\uff01\u201d\u5192\u6d63\u83b2\u76ee\u4e0d\u8f6c\u775b\u5730\u770b\u7740\u516c\u4e3b\uff0c\u95ee\u9053\uff0c\u201c\u662f\u5417\uff1f\u201d\u516c\u4e3b\u8fd9\u65f6\u601d\u6f6e\u8d77\u4f0f\uff0c\u8111\u4e2d\u73b0\u51fa\u4e00\u5e45 \u56fe\u753b\uff0c\u5979\u628a\u90a3\u201c\u5973\u8d3c\u201d\u6551\u51fa\u4e4b\u540e\uff0c\u5f20\u534e\u662d\u643a\u7740\u201c\u5973\u8d3c\u201d\u7684\u624b\uff0c\u7b11\u76c8\u76c8\u5730\u5e76\u8f94\u98de\u9a70\uff0c\u8fde\u770b\u4e5f\u4e0d \u770b\u81ea\u5df1\u4e00\u773c\uff0c\u5979\u4e0d\u7981\u53c8\u72e0\u72e0\u5730\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u6551\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u5750\u5728\u516c\u4e3b\u65c1\u8fb9\uff0c\u5ffd\u7136\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u771f\u66ff\u516c\u4e3b\u53ef\u60dc\uff01\u201d\u516c\u4e3b\u62ac\u5934\u95ee\u9053\uff1a\u201c\u53ef\u60dc\u4ec0 \u4e48\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u516c\u4e3b\u672c\u6765\u5c31\u5bf9\u662d\u90ce\u6709\u6069\uff0c\u82e5\u518d\u5e2e\u4ed6\u5b8c\u6210\u5fc3\u613f\uff0c\u4ed6\u4f1a\u611f\u6fc0\u4f60\u4e00\u8f88\u5b50\u3002\u516c\u4e3b \u4e0d\u7ba1\u6b64\u4e8b\uff0c\u4e0e\u662d\u90ce\u5f80\u65e5\u4ea4\u60c5\uff0c\u4ed8\u4e4b\u6d41\u6c34\uff0c\u8fd9\u8fd8\u4e0d\u53ef\u60dc\u4e48\uff1f\u201d\u516c\u4e3b\u9ed8\u7136\u4e0d\u8bed\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u5ffd\u7136 \u95ee\u9053\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u5fc3\u4e0a\u7684\u4eba\u513f\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u6709\u7684\uff01\u201d\u516c\u4e3b\u9053\uff1a\u201c\u5982\u679c\u4ed6\u7231\u4e0a\u53e6\u4e00\u4e2a\u4eba\uff0c \u4f60\u600e\u4e48\u6837\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4e00\u6837\u7231\u4ed6\u5e2e\u4ed6\uff01\u201d\u516c\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d\u5192\u6d63\u83b2\u4ea2\u58f0\u8bf4\u9053\uff1a \u201c\u4e3a\u4ec0\u4e48\u4e0d\u771f\uff1f\u6211\u7231\u4ed6\u5f53\u7136\u5b8c\u5168\u4e3a\u4ed6\u8bbe\u60f3\uff0c\u6211\u53ea\u8981\u60f3\u5230\u4ed6\u80fd\u5e78\u798f\uff0c\u6211\u4e5f\u5c31\u4f1a\u89c9\u5f97\u5e78\u798f\u3002\u6211\u66fe \u5192\u8fc7\u751f\u547d\u7684\u5371\u9669\uff0c\u7528\u6700\u5927\u7684\u8010\u5fc3\uff0c\u5c06\u6211\u6240\u559c\u6b22\u7684\u4eba\u6551\u79bb\u9669\u5883\u3002\u90a3\u65f6\u4ed6\u968f\u65f6\u4f1a\u628a\u6211\u6740\u6b7b\uff0c\u4f46\u6211 \u6beb\u4e0d\u5bb3\u6015\uff01\u201d\u516c\u4e3b\u5947\u9053\uff1a\u201c\u771f\u662f\u8fd9\u6837\uff1f\u4eca\u665a\u4f60\u548c\u6211\u8054\u5e8a\u591c\u8bdd\uff0c\u8bb2\u8bb2\u4f60\u7684\u6545\u4e8b\u5427\uff01\u201d \u3000\u3000\u8fd9\u4e00\u665a\uff0c\u5192\u6d63\u83b2\u628a\u5979\u548c\u6842\u4ef2\u660e\u7684\u6545\u4e8b\u7ec6\u7ec6\u8bb2\u4e86\uff0c\u516c\u4e3b\u4e0d\u8a00\u4e0d\u8bed\uff0c\u53ea\u662f\u53f9\u6c14\u3002\u7b2c\u4e8c\u5929\u4e00\u65e9 \u8d77\u6765\uff0c\u516c\u4e3b\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u7b49\u6211\uff0c\u6211\u53bb\u53bb\u5c31\u6765\uff01\u201d\u5192\u6d63\u83b2\u5ffd\u89c9\u5979\u7684\u773c\u5149\uff0c\u575a\u5b9a\u660e\u6f88\uff0c \u5c31\u597d\u50cf\u7acb\u4e86\u91cd\u8a93\uff0c\u51b3\u5fc3\u8981\u53bb\u505a\u4e00\u4ef6\u4e8b\u60c5\u90a3\u6837\u3002 \u3000\u3000\u6e05\u9732\u6668\u51dd\uff0c\u6653\u8377\u6ef4\u7fe0\uff0c\u4e09\u516c\u4e3b\u8d70\u540e\uff0c\u5192\u6d63\u83b2\u95f7\u5750\u65e0\u804a\uff0c\u8f7b\u63ed\u7ee3\u5e18\uff0c\u5077\u8d4f\u5fa1\u82b1\u56ed\u7684\u666f\u8272\u3002 \u6b63\u81ea\u51fa\u795e\uff0c\u5ffd\u542c\u5f97\u9601\u9601\u4e4b\u58f0\uff0c\u6709\u4eba\u6b65\u4e0a\u697c\u68af\u3002\u5192\u6d63\u83b2\u4fa7\u8033\u4e00\u542c\uff0c\u53ea\u542c\u5f97\u6709\u4e00\u4e2a\u5c16\u9510\u7684\u58f0\u97f3\u8bf4 \u9053\uff1a\u201c\u516c\u4e3b\u8fd9\u6837\u65e9\u5c31\u51fa\u53bb\u4e86\uff1f\u201d\u53e6\u4e00\u4e2a\u5973\u58f0\u7b54\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4eec\u4e5f\u4e0d\u77e5\u9053\u5979\u53bb\u54ea\u91cc\uff0c\u5927\u7ea6\u4e0d \u662f\u53bb\u8c12\u592a\u540e\uff0c\u5c31\u662f\u53bb\u627e\u4e8c\u516c\u4e3b\u4e86\u3002\u201d\u5148\u5934\u90a3\u4e2a\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u592a\u540e\u771f\u559c\u6b22\u4f60\u4eec\u7684\u516c\u4e3b\uff0c\u5979\u524d\u65e5 \u6765\u8fc7\uff0c\u8bf4\u4e09\u516c\u4e3b\u7684\u623f\uff0c\u592a\u6734\u7d20\u4e86\u3002\u5979\u6628\u5929\u4eb2\u81ea\u627e\u51fa\u4e00\u767e\u6302\u7329\u7329\u6be1\u5e18\uff0c\u8fd8\u6709\u4e94\u5f69\u7ebf\u7edc\uff0c\u5404\u5f0f\u7ef8 \u7f0e\u5e54\u5b50\uff0c\u6795\u5957\u5e8a\u88d9\uff0c\u897f\u6d0b\u65f6\u8fb0\u949f\uff0c\u5efa\u660c\u5b9d\u955c\u7b49\u7b49\u6446\u8bbe\uff0c\u8981\u6211\u4eec\u66ff\u4e09\u516c\u4e3b\u53e6\u5916\u5e03\u7f6e\uff0c\u5168\u90e8\u6362 \u8fc7\uff0c\u65e2\u7136\u4e09\u516c\u4e3b\u4e0d\u5728\u623f\u4e2d\uff0c\u90a3\u5c31\u4e0d\u65b9\u4fbf\u4e86\u3002\u201d\u8fd9\u4eba\u7d6e\u7d6e\u53e8\u53e8\u5730\u8bf4\u4e86\u4e00\u5927\u7bc7\u540e\uff0c\u811a\u6b65\u58f0\u5df2\u505c\u5728 \u95e8\u524d\u3002\u5e95\u4e0b\u8fd8\u6709\u597d\u51e0\u4e2a\u4eba\u7684\u811a\u6b65\u58f0\uff0c\u8d70\u4e0a\u697c\u6765\uff0c\u8e0f\u5f97\u5f88\u54cd\uff0c\u5927\u7ea6\u662f\u62ac\u7740\u4e1c\u897f\u3002 \u3000\u3000\u5192\u6d63\u83b2\u773c\u775b\u8d34\u7740\u95e8\u7f1d\uff0c\u5411\u5916\u5f20\u671b\uff0c\u53ea\u89c1\u95e8\u5916\u4e24\u4eba\uff0c\u4e00\u4e2a\u592a\u76d1\uff0c\u4e00\u4e2a\u5bab\u5a25\uff0c\u8fd9\u5bab\u5a25\u60f3\u662f\u670d \u4f8d\u516c\u4e3b\u7684\uff0c\u800c\u592a\u76d1\u5219\u662f\u592a\u540e\u6240\u5dee\u3002\u5bab\u5a25\u53d6\u51fa\u9501\u5319\uff0c\u6b63\u60f3\u5f00\u95e8\uff0c\u5192\u6d63\u83b2\u5ffd\u7136\u5413\u4e86\u4e00\u8df3\uff0c\u8fd9\u592a\u76d1 \u9762\u8c8c\u597d\u719f\uff0c\u9759\u5fc3\u4e00\u60f3\uff0c\u539f\u6765\u662f\u5f53\u5e74\u591c\u63a2\u6e05\u51c9\u5bfa\uff0c\u6f5c\u5165\u94dc\u5854\u65f6\uff0c\u7ed9\u5085\u9752\u4e3b\u6349\u4f4f\u7684\u90a3\u4e2a\u592a\u76d1\u3002\u5192 \u6d63\u83b2\u6025\u5fd9\u85cf\u8eab\u5e10\u540e\uff0c\u623f\u95e8\u7f13\u7f13\u5f00\u542f\uff0c\u5192\u6d63\u83b2\u53cc\u6307\u5939\u7740\u51e0\u7c92\u795e\u7802\uff0c\u8f7b\u8f7b\u5411\u5916\u4e00\u8749\uff0c\u90a3\u592a\u76d1\u53eb\u4e86 \u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u4f60\u4eec\u8fd9\u6837\u61d2\uff0c\u5c18\u6328\u90fd\u4e0d\u626b\uff01\u201d\u4ed6\u7ed9\u51e0\u7c92\u795e\u7802\u8f7b\u62c2\u773c\u5e18\uff0c\u4ee5\u4e3a\u662f\u5c18\u57c3\u5165 \u773c\uff0c\u6025\u5fd9\u63c9\u64e6\u3002\u90a3\u5bab\u5a25\u521a\u8bf4\u5f97\u4e00\u53e5\u201c\u54ea\u4f1a\u6709\u5c18\u57c3\uff1f\u201d\u5ffd\u7136\u4e5f\u53eb\u4e86\u4e00\u58f0\uff0c\u6025\u6025\u638f\u51fa\u624b\u5e15\u63e9\u62b9\uff0c \u5583\u5583\u8bf4\u9053\uff1a\u201c\u771f\u602a\uff0c\u8fd9\u91cc\u5929\u5929\u90fd\u6253\u626b\u7684\u561b\uff01\u201d\u5192\u6d63\u83b2\u6293\u7740\u65f6\u673a\uff0c\u63ed\u5f00\u7a97\u5e18\uff0c\u4e00\u8dc3\u800c\u4e0b\u3002\u90a3\u592a \u76d1\u5bab\u5a25\uff0c\u6839\u672c\u5c31\u4e0d\u77e5\u9053\uff0c\u5192\u6d63\u83b2\u811a\u65b9\u843d\u5730\uff0c\u5ffd\u542c\u5f97\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u82b1\u67b6\u4e0b\u7a81\u7136\u5954\u51fa\u4e24\u540d\u592a \u76d1\uff0c\u811a\u6b65\u77eb\u5065\uff0c\u6b66\u529f\u7adf\u4f3c\u4e0d\u9519\uff0c\u5192\u6d63\u83b2\u81ea\u5fd6\u884c\u85cf\u8d25\u9732\uff0c\u626c\u624b\u5c31\u662f\u4e00\u628a\u795e\u7802\uff0c\u4e24\u4eba\u731d\u4e0d\u53ca\u9632\uff0c \u4e00\u4eba\u7ed9\u6253\u778e\u53cc\u773c\uff0c\u4e00\u4eba\u9762\u4e0a\u5219\u5d4c\u4e86\u5341\u591a\u9897\u7802\u5b50\uff0c\u5f53\u573a\u53d8\u4e86\u4e00\u4e2a\u5927\u9ebb\u5b50\u3002\u4e24\u4eba\u75db\u5f97\u5471\u5471\u5927\u53eb\uff0c \u9ad8\u558a\uff1a\u201c\u6709\u98de\u8d3c\uff0c\u6765\u4eba\u5440\uff01\u201d\u5192\u6d63\u83b2\u7ed5\u5047\u5c71\u7a7f\u5c0f\u5f84\uff0c\u6025\u6025\u5954\u9003\u3002\u5fa1\u82b1\u56ed\u6bd4\u76f8\u5e9c\u82b1\u56ed\uff0c\u90a3\u53ef\u8981 \u5927\u5f97\u591a\uff01\u5bab\u5a25\u4e0d\u6562\u51fa\u6765\uff0c\u592a\u76d1\u5728\u5404\u4e2a\u5bab\u6bbf\u4e4b\u4e2d\uff0c\u8d76\u51fa\u6765\u65f6\uff0c\u54ea\u91cc\u8fd8\u627e\u5f97\u5230\u5192\u6d63\u83b2\u7684\u5f71\u5b50\u3002\u4f46 \u5192\u6d63\u83b2\u4e43\u662f\u60ca\u5f13\u4e4b\u9e1f\uff0c\u5979\u542c\u5f97\u56db\u9762\u516b\u65b9\u7684\u811a\u6b65\u58f0\uff0c\u53c8\u614c\u53c8\u6025\uff0c\u8dc3\u8fc7\u4e00\u5757\u73b2\u73d1\u5c71\u77f3\uff0c\u5ffd\u7136\u524d\u9762 \u73b0\u51fa\u4e00\u5ea7\u6781\u96c5\u7684\u623f\u5b50\uff0c\u4e0a\u9762\u4e00\u4e2a\u6a2a\u989d\uff0c\u9898\u662f\u201c\u5170\u98ce\u7cbe\u820d\u201d\u56db\u4e2a\u5b57\u3002\u8fd9\u5ea7\u5c4b\u5b50\u597d\u602a\uff0c\u5899\u58c1\u5265 \u843d\uff0c\u6731\u95e8\u5c18\u5c01\uff0c\u6a90\u89d2\u8fd8\u7ed3\u7740\u86db\u7f51\u3002\u5fa1\u82b1\u56ed\u91cc\u5230\u5904\u90fd\u662f\u91d1\u78a7\u8f89\u714c\u7684\u5bab\u6bbf\uff1b\u5355\u72ec\u8fd9\u4e00\u5ea7\uff0c\u540d\u4e3a \u201c\u7cbe\u820d\u201d\uff0c\u5374\u5982\u7834\u5e99\u4e00\u822c\uff0c\u6ca1\u4eba\u6253\u626b\u3002\u5192\u6d63\u83b2\u5927\u5947\uff0c\u5fc3\u60f3\uff1a\u8fd9\u5ea7\u623f\u5b50\uff0c\u5927\u7ea6\u662f\u6ca1\u4eba\u4f4f\u7684\u4e86\u3002 \u5979\u4e00\u98d8\u8eab\uff0c\u8de8\u8fc7\u5899\u5934\uff0c\u8fdb\u5165\u5185\u9662\u3002\u5ffd\u7136\u4e00\u9635\u5e7d\u9999\uff0c\u5982\u5170\u4f3c\u5eb8\uff0c\u8d8a\u8d70\u8fdb\u53bb\uff0c\u9999\u6c14\u8d8a\u6d53\u3002\u5979\u5faa\u7740 \u9999\u6c14\u8d70\u53bb\uff0c\u8d70\u8fdb\u4e86\u4e00\u95f4\u5367\u5ba4\u3002 \u3000\u3000\u8fd9\u95f4\u5367\u5ba4\uff0c\u867d\u7136\u5c18\u57c3\u672a\u626b\uff0c\u56db\u58c1\u65e0\u5149\uff0c\u5374\u5e03\u7f6e\u5f97\u6781\u4e3a\u7cbe\u96c5\uff0c\u623f\u95f4\u56db\u9762\u90fd\u662f\u96d5\u7a7a\u7684\u73b2\u73d1\u6728 \u677f\uff0c\u4e94\u5f69\u7f15\u91d1\u5d4c\u7389\u7684\uff0c\u4e00\u683c\u4e00\u683c\uff0c\u6216\u8d2e\u4e66\uff0c\u6216\u8bbe\u9f0e\uff0c\u6216\u5b89\u7f6e\u7b14\u781a\uff0c\u6216\u4f9b\u8bbe\u74f6\u82b1\uff0c\u6216\u5b89\u653e\u76c6 \u666f\uff0c\u95f4\u683c\u5f0f\u6837\uff0c\u6216\u5706\u6216\u65b9\uff0c\u6216\u8475\u82b1\u8549\u53f6\uff0c\u6216\u8fde\u73af\u534a\u58c1\uff0c\u771f\u662f\u6e05\u96c5\u7edd\u4fd7\uff0c\u5254\u900f\u73b2\u73d1\uff0c\u90a3\u7f15\u7f15\u5e7d \u9999\uff0c\u5c31\u662f\u4ece\u4e66\u67b6\u4e0a\u53d1\u51fa\u6765\u7684\u3002\u5192\u6d63\u83b2\u8f7b\u62c2\u5c18\u57c3\uff0c\u770b\u90a3\u4e9b\u88c5\u4e66\u8d2e\u7269\u7684\u6728\u67b6\uff0c\u9edd\u9ed1\u53d1\u5149\uff0c\u5728\u4e00\u683c \u73b2\u73d1\u6728\u677f\u4e4b\u65c1\uff0c\u8d34\u7740\u5c0f\u7b7e\uff0c\u4e0a\u6709\uff1a\u201c\u8fdc\u53e4\u6c89\u9999\uff0c\u635e\u81ea\u5357\u6d77\u3002\u201d\u516b\u4e2a\u7c2a\u82b1\u5c0f\u5b57\u3002\u5192\u6d63\u83b2\u535a\u89c8\u7fa4 \u4e66\uff0c\u867d\u672a\u89c1\u8fc7\uff0c\u4e5f\u77e5\u9053\u8fd9\u79cd\u9999\u6728\uff0c\u4e43\u662f\u6700\u96be\u5f97\u7684\u9999\u6728\uff0c\u751f\u957f\u4e8e\u53e4\u4ee3\u7684\u5357\u65b9\uff0c\u540e\u6765\u5927\u7ea6\u662f\u5730\u5f62 \u53d8\u6362\uff0c\u9646\u5730\u6c89\u964d\uff0c\u6c89\u9999\u6728\u57cb\u5728\u6d77\u5e95\uff0c\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u5e74\u6708\uff0c\u624d\u7ed9\u4eba\u635e\u4e86\u51fa\u6765\u3002\u8fd9\u79cd\u6c89\u9999\u4e43\u662f\u65e0 \u4ef7\u4e4b\u5b9d\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e9b\u4e66\u67b6\u8d2e\u7269\u67b6\uff0c\u7adf\u90fd\u662f\u8fdc\u53e4\u6c89\u9999\u505a\u7684\u3002 \u3000\u3000\u5192\u6d63\u83b2\u518d\u7ec6\u770b\u5ba4\u4e2d\u5e03\u7f6e\uff0c\u9760\u4e66\u67b6\u5de6\u8fb9\u662f\u4e00\u5f20\u5b9d\u584c\uff0c\u73e0\u5e10\u4f4e\u5782\uff0c\u5e8a\u524d\u653e\u7740\u4e00\u5bf9\u5973\u978b\uff1b\u9760\u7a97 \u662f\u4e00\u5f20\u5927\u4e66\u53f0\uff0c\u517c\u4f5c\u5986\u53f0\u4e4b\u7528\uff0c\u684c\u4e0a\u96f6\u96f6\u6563\u6563\u5730\u5806\u7740\u51e0\u672c\u4e66\u3002\u77f3\u9762\u5899\u58c1\u6302\u7740\u4e00\u5f20\u753b\u50cf\uff0c\u5192\u6d63 \u83b2\u5728\u4e66\u53f0\u4e0a\u53d6\u8fc7\u4e00\u679d\u62c2\u5c18\uff0c\u628a\u753b\u50cf\u4e0a\u7684\u5c18\u57c3\u62c2\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u76db\u88c5\u5c11\u5973\uff0c\u7b11\u76c8\u76c8\u5730\u5bf9\u7740\u81ea\u5df1\u3002 \u5192\u6dc0\u83b2\u4e00\u9897\u5fc3\u535c\u535c\u8df3\u52a8\uff0c\u81ea\u5df1\u5bf9\u955c\u5b50\u4e00\u7167\uff0c\u518d\u770b\u770b\u753b\u56fe\uff0c\u8fd9\u753b\u56fe\u7adf\u4f3c\u7167\u7740\u81ea\u5df1\u7684\u5f62\u76f8\u753b\u7684\u3002 \u5192\u6d63\u83b2\u4e0a\u524d\u4e00\u770b\uff0c\u753b\u50cf\u5de6\u89d2\u6709\u4e00\u884c\u5c0f\u5b57\u662f\uff1a\u7532\u7533\u540e\u4e94\u5e74\uff0c\u4e3a\u7231\u59ec\u9020\u50cf\uff0c\u5de2\u6c11\u3002\u5192\u6d63\u83b2\u4e24\u884c\u6e05 \u6cea\uff0c\u593a\u7736\u800c\u51fa\uff0c\u4f4e\u4f4e\u5524\u4e86\u4e00\u58f0\u201c\u5988\u5988\u201d\uff01\u5979\u5c48\u6307\u4e00\u7b97\uff0c\u7532\u7533\u4e43\u662f\u660e\u5d07\u5e27\u7687\u5e1d\u6700\u540e\u4e00\u5e74\uff0c\u201c\u5de2 \u6c11\u201d\u662f\u5979\u7236\u4eb2\u7684\u540d\u5b57\uff0c\u60f3\u6765\u662f\u7236\u4eb2\u4e0d\u5fd8\u660e\u5ba4\u7684\u8868\u793a\uff0c\u7532\u7533\u540e\u7684\u7b2c\u4e94\u5e74\uff0c\u5979\u6bcd\u4eb2\u521a\u5165\u5192\u95e8\uff0c\u81ea \u5df1\u8fd8\u6ca1\u51fa\u4e16\u3002\u6bcd\u4eb2\u7adf\u6562\u5e26\u8fd9\u5e45\u753b\u8fdb\u5bab\uff0c\u53ef\u89c1\u5979\u5bf9\u7236\u4eb2\u662f\u5982\u4f55\u6df1\u60c5\u7737\u604b\uff01 \u3000\u3000\u5192\u6d63\u83b2\u68c0\u89c6\u4e66\u53f0\uff0c\u90a3\u6563\u5728\u684c\u9762\u7684\u51e0\u672c\u4e66\uff0c\u4e00\u672c\u662f\u300a\u5e84\u5b50\u300b\uff0c\u4e00\u672c\u662f\u300a\u5de2\u56ed\u8bcd\u8349\u300b\uff0c\u4e00\u672c \u662f\u300a\u7ef4\u6469\u7ecf\u300b\u3002\u300a\u5de2\u56ed\u8bcd\u8349\u300b\u662f\u624b\u6284\u672c\uff0c\u4e66\u672c\u63ed\u5f00\uff0c\u7528\u7aef\u781a\u538b\u4f4f\uff0c\u5192\u6d63\u83b2\u62c2\u53bb\u4fef\u9875\u4e0a\u7684\u5c18 \u57c3\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7740\u4e00\u9996\u8bcd\uff0c\u5192\u6d63\u83b2\u8bfb\u9053\uff1a \u3000\u3000\u201c\u5f15\u79bb\u676f\uff0c\u6b4c\u79bb\u6028\uff0c\u8bc9\u79bb\u60c5\u3002\u662f\u8c01\u8c31\u63a0\u6c34\u9e3f\u60ca\uff0c\u79cb\u5a18\u91d1\u7f15\uff0c\u66f2\u7ec8\u4eba\u6563\u6570\u5cf0\u9752\uff1f\u60a0\u60a0\u4e0d\u5411 \u8c22\u6865\u53bb\uff0c\u68a6\u7ed5\u71d5\u4eac\u3002\u6625\u7a7a\u8fd1\uff0c\u676f\u7a7a\u6ee1\uff0c\u7434\u7a7a\u5999\uff0c\u6708\u7a7a\u660e\uff01\u6015\u5170\u82d1\uff0c\u4eba\u53bb\u5c18\u751f\uff0c\u6c5f\u5357\u51ac\u66ae\uff0c\u6005 \u5e74\u5e74\u96ea\u51b7\u98ce\u6e05\uff0c\u6545\u4eba\u5929\u9645\uff0c\u95ee\u8c01\u6765\u540c\u6170\u98d8\u96f6\uff1f\u201d \u3000\u3000\u8bcd\u724c\u540d\u662f\u201c\u91d1\u4eba\u6367\u9732\u76d8\u201d\uff0c\u5e95\u4e0b\u51e0\u884c\u5c0f\u5b57\u662f\uff1a\u201c\u68a6\u5e7b\u5c18\u7f18\uff0c\u4f24\u5fc3\u60c5\u52ab\uff0c\u9e2f\u9e33\u8fdc\u53bb\uff0c\u76fc\u76fc \u697c\u7a7a\u3002\u5029\u5973\u79bb\u9b42\uff0c\u840d\u8e2a\u83ab\u95ee\u3002\u626c\u94a9\u6d77\u7554\uff0c\u8c01\u8bc1\u524d\u76df\uff1b\u628a\u81c2\u6797\u8fb9\uff0c\u96be\u5fd8\u5f80\u4e8b\u3002\u91d1\u83b2\u821e\u540e\uff0c\u7389\u6811 \u6b4c\u4f59\uff0c\u6843\u53f6\u65e0\u8e2a\uff0c\u67f3\u679d\u4f55\u5904\u4e86\u55df\u55df\uff0c\u840d\u968f\u6c34\uff0c\u6c34\u968f\u98ce\uff0c\u840d\u67af\u6c34\u5c3d\uff1b\u5e7b\u5373\u7a7a\uff0c\u7a7a\u5373\u8272\uff0c\u5e7b\u706d\u7a7a \u7075\u3002\u80fd\u6240\u53cc\u5fd8\uff0c\u8272\u7a7a\u5e76\u9063\uff1b\u957f\u6b4c\u5bc4\u610f\uff0c\u7f3a\u6708\u96be\u5706\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u5fc3\u9178\u6cea\u6d8c\uff0c\u60f3\u9053\uff1a\u539f\u6765\u8fd9\u9996\u8bcd\u4e43\u662f\u7236\u4eb2\u4e0e\u6bcd\u4eb2\u751f\u79bb\u6b7b\u522b\u7684\u524d\u5915\u6240\u586b\u7684\u3002\u602a\u4e0d\u5f97\u5988 \u5988\u5e38\u5e38\u628a\u5b83\u63ed\u5f00\u6765\u770b\u3002 \u3000\u3000\u5192\u6d63\u83b2\u5fc3\u60f3\uff1a\u300a\u5de2\u56ed\u8bcd\u8349\u300b\u662f\u5979\u7236\u4eb2\u4e00\u751f\u7684\u5fc3\u8840\uff0c\u4e0d\u8be5\u8ba9\u5b83\u57cb\u846c\u6df1\u5bab\u3002\u5979\u8f7b\u8f7b\u63ed\u8d77\u3002\u85cf \u5728\u6000\u4e2d\u3002\u6b63\u60f3\u518d\u53d6\u90a3\u5f20\u753b\u50cf\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u63a8\u95e8\u58f0\uff0c\u811a\u6b65\u58f0\uff0c\u54cd\u6210\u4e00\u7247\u3002\u5192\u6d63\u83b2\u5927\u5403\u4e00\u60ca\uff0c\u6025 \u95ea\u5728\u4e66\u6a71\u4e4b\u540e\uff0c\u7247\u523b\u95f4\uff0c\u8d70\u8fdb\u4e86\u4e24\u4e2a\u6c49\u5b50\u3002 \u3000\u3000\u5192\u6d63\u83b2\u5728\u4e66\u6a71\u540e\u770b\u5f97\u5206\u660e\uff0c\u8fd9\u4e00\u60ca\u66f4\u975e\u540c\u5c0f\u53ef\uff01\u8fd9\u4e24\u4eba\u4e2d\uff0c\u4e00\u4e2a\u7adf\u662f\u5eb7\u7199\u7687\u5e1d\uff0c\u53e6\u4e00\u4e2a \u7709\u68f1\u8038\u7acb\uff0c\u98a7\u9aa8\u9ad8\u524a\uff0c\u76ee\u7736\u6df1\u9677\uff0c\u51f8\u51fa\u4e00\u5bf9\u9ec4\u773c\u775b\uff0c\u4e00\u770b\u4fbf\u77e5\u662f\u5185\u5bb6\u9ad8\u624b\uff0c\u60f3\u6765\u5b9a\u662f\u5eb7\u7199\u7684 \u8d34\u8eab\u4f8d\u536b\u3002\u5192\u6d63\u83b2\u54bd\u4e86\u53e3\u6c14\uff0c\u5b9a\u4e00\u5b9a\u5fc3\uff0c\u8f7b\u8f7b\u62d4\u51fa\u7eb3\u5170\u5bb9\u82e5\u6240\u8d60\u7684\u5b9d\u5251\u3002 \u3000\u3000\u90a3\u4e2a\u4f8d\u536b\u66ff\u5eb7\u7199\u62c2\u53bb\u684c\u6905\u4e0a\u7684\u7070\u5c18\uff0c\u5eb7\u7199\u5750\u5728\u68b3\u5986\u53f0\u524d\u7684\u4e00\u5f20\u6447\u6905\u4e0a\uff0c\u5bf9\u7740\u58c1\u4e0a\u7684\u753b \u50cf\uff0c\u53d1\u4e86\u51e0\u58f0\u51b7\u7b11\uff0c\u53c8\u4ed4\u7ec6\u770b\u4e86\u4e00\u56de\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u95f4\u623f\u5b50\u5c01\u95ed\u4e86\u8fd1\u4e8c\u5341\u5e74\uff0c\u600e\u4e48\u8fd9\u5f20\u753b \u5982\u6b64\u5e72\u51c0\uff0c\u5c45\u7136\u6ca1\u6709\u4e00\u70b9\u5c18\u57c3\uff1f\u201d\u90a3\u540d\u4f8d\u536b\u53cc\u773c\u4e00\u626b\uff0c\u73af\u987e\u5168\u5ba4\uff0c\u5192\u6d63\u83b2\u7f29\u5728\u4e00\u89d2\uff0c\u4e0d\u6562\u900f \u6c14\uff0c\u53ea\u542c\u5f97\u90a3\u4f8d\u536b\u9053\uff1a\u201c\u7687\u4e0a\uff0c\u8fd9\u95f4\u623f\u5b50\u6050\u6015\u6709\u4eba\u6765\u8fc7\uff01\u201d\u5eb7\u7199\u7b11\u9053\uff1a\u201c\u8c01\u6562\u8fd9\u6837\u5927\u80c6\uff0c\u8fd9 \u95f4\u623f\u5b50\u81ea\u90a3\u8d31\u5a62\u88ab\u592a\u540e\u6253\u6740\u540e\uff0c\u5148\u5e1d\u7acb\u5373\u5c31\u5c01\u95ed\u8d77\u6765\uff0c\u4e0d\u8bb8\u4eba\u8fdb\u53bb\uff0c\u4e8c\u5341\u5e74\u6765\uff0c\u60ac\u4e3a\u5389\u7981\u3002 \u5c31\u662f\u6211\u6b64\u6b21\u6765\uff0c\u4e5f\u662f\u8bf7\u51c6\u4e86\u5927\u540e\u7684\uff01\u201d\u8bf4\u7f62\uff0c\u53c8\u51b7\u7b11\u4e00\u9635\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u7eed\u9053\uff1a\u201c\u5148\u5e1d\u4e5f\u771f\u662f \u7684\uff0c\u628a\u5979\u5ba0\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u636e\u592a\u540e\u8bf4\uff0c\u5c01\u95ed\u7684\u65f6\u5019\uff0c\u5ba4\u4e2d\u7684\u5e03\u7f6e\uff0c\u5b8c\u5168\u4e0d\u51c6\u79fb\u4e71\uff0c\u5b9d\u8d1d\u4e1c\u897f\uff0c \u4e5f\u4e0d\u51c6\u53d6\u51fa\u3002\u201d\u5192\u6d63\u83b2\u542c\u4e86\uff0c\u66f4\u662f\u5fc3\u4f24\u3002\u6697\u9053\uff1a\u539f\u6765\u5988\u5988\u7ed9\u592a\u540e\u62c9\u53bb\u6253\u6b7b\u7684\u524d\u4e00\u523b\uff0c\u6b63\u7ffb\u8bfb \u6211\u7238\u7238\u7684\u8bcd\u7ae0\uff0c\u800c\u90a3\u4e00\u9996\u8bcd\u53c8\u6b63\u662f\u4ed6\u4eec\u751f\u79bb\u7684\u524d\u5915\u4f5c\u7684\u3002\u8981\u662f\u7ed9\u6211\u7238\u7238\u77e5\u9053\uff0c\u4ed6\u771f\u4f1a\u6b7b\u4e0d\u7791 \u76ee\u3002 \u3000\u3000\u90a3\u540d\u4f8d\u536b\u5782\u624b\u7acb\u5728\u5eb7\u7199\u8eab\u65c1\uff0c\u8eac\u8170\u95ee\u9053\uff1a\u201c\u7687\u4e0a\u53ef\u8981\u53d6\u4ec0\u4e48\u4e1c\u897f\u51fa\u53bb\uff1f\u201d\u5eb7\u7199\u9053\uff1a\u201c\u5b9d \u8d1d\u6211\u5012\u4e0d\u7a00\u7f55\uff0c\u6211\u6b64\u6765\u4e00\u662f\u8981\u770b\u7236\u7687\u6709\u4ec0\u4e48\u9057\u7269\u653e\u5728\u8fd9\u91cc\uff0c\u4e00\u662f\u60f3\u89c1\u8bc6\u89c1\u8bc6\u90a3\u53e4\u6c89\u9999\u6240\u505a\u7684 \u4e66\u67b6\uff0c\u8fd8\u60f3\u770b\u770b\u6709\u4ec0\u4e48\u7edd\u7248\u7684\u4e66\u7c4d\u3002\u201d\u539f\u6765\u5eb7\u7199\u867d\u7136\u6b8b\u5fcd\u523b\u6bd2\uff0c\u5374\u597d\u8bfb\u4e66\u3002\u4ed6\u6740\u7236\u4e4b\u540e\uff0c\u6000 \u6709\u5fc3\u75c5\uff0c\u672c\u6765\u4e0d\u6562\u5230\u8463\u9102\u5983\uff08\u5c0f\u5b9b\uff09\u7684\u623f\u5b50\u6765\u7684\uff0c\u540e\u6765\u542c\u8001\u5bab\u4eba\u8bf4\u8d77\u8463\u9102\u5983\u85cf\u4e66\u9887\u591a\uff0c\u4e66\u6a71 \u58c1\u67b6\u5c24\u5176\u73cd\u8d35\uff0c\u5fc3\u4e2d\u8dc3\u8dc3\u6b32\u52a8\u3002\u8fd9\u51e0\u5929\uff0c\u56e0\u591a\u94ce\u6b7b\u540e\uff0c\u5fc3\u4e2d\u70e6\u95f7\uff0c\u60f3\u627e\u4e9b\u4e66\u6d88\u9063\uff0c\u5c31\u8fdb\u6765 \u4e86\u3002\u53e6\u5916\u8fd8\u6709\u4e00\u5c42\uff0c\u4ed6\u6015\u5148\u5e1d\u6709\u4ec0\u4e48\u9057\u8bcf\u7559\u5728\u8fd9\u91cc\uff08\u6e05\u5ba4\u7684\u7687\u4f4d\u7ee7\u627f\uff0c\u4e0d\u4f9d\u957f\u5e7c\u6b21\u5e8f\uff0c\u7531\u7687 \u5e1d\u7559\u4e0b\u9057\u8bcf\uff0c\u6307\u5b9a\u4e00\u4e2a\uff0c\u5e73\u5e38\u662f\u653e\u5728\u5927\u5149\u660e\u6bbf\u7684\u6b63\u6881\uff0c\u4f46\u8fd9\u6837\u7684\u9057\u8bcf\u591a\u662f\u7687\u5e1d\u665a\u5e74\uff0c\u6216\u81ea\u77e5 \u75c5\u5c06\u4e0d\u8d77\u65f6\uff0c\u624d\u9884\u5907\u7684\u3002\u987a\u6cbb\u7a81\u7136\u51fa\u5bb6\uff0c\u5eb7\u7199\u5949\u592a\u540e\u547d\u7ee7\u7acb\uff0c\u6240\u4ee5\u5fc3\u4e2d\u6709\u75c5\uff0c\u6050\u9632\u987a\u6cbb\u5199\u6709 \u9057\u8bcf\uff0c\u672a\u653e\u5728\u5927\u5149\u660e\u6bbf\uff0c\u800c\u7559\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u5176\u5b9e\u662f\u6ca1\u6709\u7684\uff09\uff0c\u56e0\u6b64\u987a\u4fbf\u6765\u641c\u4e00\u4e0b\uff0c\u867d\u7136\u4ed6\u73b0 \u5728\u5df2\u5750\u7a33\u6c5f\u5c71\uff0c\u7eb5\u6709\u9057\u8bcf\u4f20\u7ed9\u522b\u4eba\uff0c\u4ed6\u4e5f\u4e0d\u6015\uff0c\u4f46\u603b\u9632\u4f1a\u7559\u6709\u628a\u67c4\uff0c\u5bf9\u81ea\u5df1\u4e0d\u5229\u3002 \u3000\u3000\u5eb7\u7199\u6253\u5f00\u4e66\u684c\u62bd\u5c49\uff0c\u4e71\u7ffb\u4e00\u904d\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4e14\u770b\u770b\u8fd9\u4e9b\u4e66\u6a71\u58c1\u67b6\uff0c\u770b\u5230\u5e95\u662f \u600e\u4e48\u4e2a\u597d\u6cd5\uff1f\u201d\u5192\u6d63\u83b2\u7d27\u634f\u5b9d\u5251\uff0c\u51b7\u6c57\u76f4\u6d41\uff0c\u5fc3\u60f3\uff1a\u4ed6\u82e5\u8fc7\u6765\uff0c\u6211\u5c31\u7ed9\u4ed6\u4e00\u5251\uff0c\u6b63\u662f\uff1a \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000  \u7779\u7269\u601d\u4ea1\u6bcd\uff0c\u6df1\u5bab\u85cf\u6740\u673a\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000  \u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5341\u4e03\u7ae0\u3000\u7779\u753b\u601d\u4eba\u3000\u5192\u6d63\u83b2\u5fc3\u4f24\u5185\u82d1\u3000\u631f\u7b26\u95ef\u72f1\u3000\u51cc\u672a\u98ce\u591c\u63a2\u5929\u7262 \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u5341\u4e03\u56de \u7779\u753b\u601d\u4eba\u3000\u5192\u6d63\u83b2\u5fc3\u4f24\u5185\u82d1 \u631f\u7b26\u95ef\u72f1\u3000\u51cc\u672a\u98ce\u591c\u63a2\u5929\u7262 \u3000\u3000\u5eb7\u7199\u7ad9\u4e86\u8d77\u6765\uff0c\u6b63\u60f3\u53bb\u68c0\u9605\u8463\u5c0f\u5b9b\u7684\u85cf\u4e66\uff0c\u9762\u5bf9\u7740\u5899\u4e0a\u7684\u753b\u50cf\uff0c\u5ffd\u89c9\u753b\u4e0a\u7684\u8463\u5c0f\u5b9b\uff0c\u5634 \u89d2\u542b\u7740\u51b7\u7b11\uff0c\u4e00\u53cc\u773c\u73e0\uff0c\u4f3c\u4f1a\u8f6c\u52a8\u4f3c\u7684\u3002\u4ed6\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u505c\u4e0b\u6b65\u6765\uff0c\u5bf9\u5f85\u536b\u9053\uff1a\u201c\u4f60\u628a\u90a3 \u5f20\u753b\u7ed9\u6211\u6495\u4e0b\u6765\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u8eb2\u5728\u6a71\u540e\uff0c\u70ed\u8840\u5954\u6d8c\u3002\u773c\u89c1\u90a3\u4f8d\u536b\u6162\u6162\u8d70\u8fd1\u4ea1\u6bcd\u7684\u9057\u50cf\u4e4b\u524d\uff0c\u4e94\u722a\u5982\u94a9\uff0c\u5411\u753b\u50cf \u6293\u53bb\uff0c\u5192\u6d63\u83b2\u5927\u53eb\u4e00\u58f0\uff0c\u731b\u5730\u8df3\u4e86\u51fa\u6765\uff0c\u5530\u7684\u4e00\u5251\u5411\u90a3\u540d\u4f8d\u536b\u523a\u53bb\u3002 \u3000\u3000\u90a3\u540d\u4f8d\u536b\u529f\u592b\u4e5f\u7740\u5b9e\u4e86\u5f97\uff0c\u84e6\u89c9\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u6765\u81ea\u8111\u540e\uff0c\u4e00\u4e2a\u65cb\u8eab\uff0c\u4e00\u5f20\u6905\u5b50\u5df2\u62ff\u5728 \u624b\u4e2d\uff0c\u201c\u547c\u201d\u7684\u4e00\u4e0b\u6a2a\u626b\u8fc7\u53bb\u3002\u5192\u6d63\u83b2\u5b9d\u5251\u4e00\u6325\uff0c\u7d2b\u8679\u98de\u5c04\uff0c\u6905\u5b50\u7684\u56db\u6761\u817f\u5148\u81ea\u65ad\u4e86\uff01\u90a3\u540d \u5f85\u536b\u5927\u559d\u4e00\u58f0\uff0c\u6905\u5b50\u731b\u5730\u63b7\u51fa\uff0c\u5192\u6d63\u83b2\u6a2a\u5251\u4e00\u5288\uff0c\u628a\u90a3\u5f20\u6905\u5288\u4e3a\u4e24\u534a\uff0c\u4e00\u4f4e\u5934\uff0c\u907f\u5f00\u788e\u7247\uff0c \u5251\u950b\u4e00\u9886\uff0c\u5251\u5c16\u5916\u5410\uff0c\u4e00\u4e2a\u201c\u76d8\u8098\u523a\u624e\u201d\uff0c\u5530\u7684\u4e00\u5251\uff0c\u671d\u7740\u5954\u6765\u7684\u654c\u4eba\u624b\u8155\u526a\u53bb\uff0c\u90a3\u540d\u4f8d\u536b \u75be\u626d\u8eab\u8eaf\uff0c\u624b\u8155\u5df2\u88ab\u5251\u5c16\u523a\u4e86\u4e00\u4e0b\u3002\u4ed6\u66b4\u559d\u5982\u96f7\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u53cc\u62f3\u4ea4\u51fb\uff0c\u5411\u5192\u6d63\u83b2\u4e24\u9762\u8033\u95e8 \u64c2\u6253\u3002\u5192\u6d63\u83b2\u89c1\u4ed6\u6765\u52bf\u51f6\u731b\uff0c\u5fc3\u751f\u4e00\u8ba1\uff0c\u5ffd\u7136\u659c\u63a0\u6a2a\u8dc3\uff0c\u5251\u62db\u5982\u4e32\uff0c\u7a81\u5411\u5eb7\u7199\u523a\u53bb\uff01\u5eb7\u7199\u5c16 \u53eb\u4e00\u58f0\uff0c\u6251\u5012\u5728\u5730\uff0c\u8d81\u52bf\u4e00\u6eda\uff0c\u8eb2\u5728\u68b3\u5986\u53f0\u4e0b\u9762\u3002\u90a3\u540d\u4f8d\u536b\u5728\u5192\u6d63\u83b2\u63a0\u8eab\u659c\u8dc3\u65f6\uff0c\u5df2\u77e5\u4e0d \u5999\uff0c\u6025\u7eb5\u8fc7\u8eab\u6765\uff0c\u8033\u542c\u5f97\u7687\u5e1d\u5c16\u53eb\u4e4b\u58f0\uff0c\u4ee5\u4e3a\u5df2\u53d7\u4e86\u523a\u5ba2\u7684\u6697\u7b97\u3002\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u62fc\u4e86\u6027 \u547d\uff0c\u53cc\u624b\u5f20\u5f00\uff0c\u548c\u8eab\u6251\u53bb\u3002\u5192\u6d63\u83b2\u8f7b\u8f7b\u4e00\u95ea\uff0c\u90a3\u540d\u4f8d\u536b\u53ea\u987e\u6551\u4eba\uff0c\u53f3\u638c\u524d\u635e\uff0c\u5de6\u638c\u5e94\u654c\uff0c\u5c82 \u6599\u635e\u4e86\u4e2a\u7a7a\uff0c\u53ea\u89c9\u4e00\u9635\u5947\u75db\u5f7b\u9aa8\uff0c\u5de6\u638c\u5df2\u7ed9\u5192\u6d63\u83b2\u5b9d\u5251\u5207\u4e86\u4e0b\u6765\uff01 \u3000\u3000\u90a3\u540d\u4f8d\u536b\u7cbe\u901a\u5173\u5916\u5341\u516b\u8def\u957f\u62f3\uff0c\u82e5\u8bba\u6b66\u529f\uff0c\u5f53\u5728\u5192\u6d63\u83b2\u4e4b\u4e0a\u3002\u53ea\u662f\u5192\u6d63\u83b2\u6301\u6709\u5b9d\u5251\uff0c\u800c \u4ed6\u53c8\u8981\u517c\u987e\u7687\u4e0a\uff0c\u5de6\u638c\u4e00\u65ad\uff0c\u867d\u4ecd\u62fc\u6b7b\u62e6\u622a\uff0c\u5df2\u662f\u654c\u4e0d\u4f4f\u4e86\uff0c\u4e0d\u8fc7\u51e0\u62db\uff0c\u5192\u6d63\u83b2\u4e58\u4ed6\u53d1\u72c2\u731b \u51b2\u7684\u65f6\u5019\uff0c\u4e00\u4e2a\u7ed5\u6b65\uff0c\u95ea\u5230\u8eab\u540e\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u81ea\u540e\u5fc3\u7a7f\u8fc7\u524d\u5fc3\uff0c\u5c06\u4ed6\u6233\u4e86\u4e00\u4e2a\u900f\u660e\u7684\u7a9f\u7abf\u3002 \u3000\u3000\u5192\u6d63\u83b2\u53d6\u8fc7\u4e00\u5f20\u6905\u57ab\uff0c\u62b9\u4e86\u5251\u4e0a\u8840\u8ff9\uff0c\u5c06\u4ea1\u6bcd\u9057\u50cf\uff0c\u5c0f\u5fc3\u53d6\u4e0b\uff0c\u5377\u4e86\u8d77\u6765\uff0c\u5b9d\u5251\u4e00\u6307\uff0c \u559d\u9053\uff1a\u201c\u51fa\u6765\uff01\u201d \u3000\u3000\u5eb7\u7199\u5728\u68b3\u5986\u53f0\u4e0b\uff0c\u542c\u89c1\u4f8d\u536b\u88ab\u6740\uff0c\u5168\u8eab\u51b0\u51b7\uff0c\u6599\u60f3\u4eca\u65e5\u4e0d\u514d\u4e00\u6b7b\uff0c\u628a\u5fc3\u4e00\u6a2a\uff0c\u53cd\u800c\u6bd4\u524d \u9547\u5b9a\uff0c\u94bb\u4e86\u51fa\u6765\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u6562\u5f11\u541b\uff1f\u201d \u3000\u3000\u5192\u6d63\u83b2\u51b7\u51b7\u4e00\u7b11\uff0c\u5b9d\u5251\u5728\u5eb7\u7199\u9762\u524d\u4e00\u6643\uff0c\u8bf4\u9053\uff1a\u201c\u5bb0\u4e86\u4f60\u7b49\u4e8e\u5bb0\u4e00\u53e3\u732a\uff0c\u6709\u4ec0\u4e48\u8d39 \u52b2\uff1f\u201d\u5eb7\u7199\u54fc\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u522b\u60f3\u6d3b\u7740\u51fa\u5bab\u4e86\uff01\u201d\u5192\u6d63\u83b2\u60f3\u5230\u72f1\u4e2d\u7684\u6613\u5170\u73e0\uff0c\u5251\u5c16\u4e00 \u6307\uff0c\u5374\u5e76\u4e0d\u523a\u4e0b\uff0c\u4f4e\u58f0\u9a82\u9053\uff1a\u201c\u4f60\u60f3\u9976\u547d\u5417\uff1f\u201d\u5eb7\u7199\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4f60\u5f97\u5148 \u628a\u5929\u7262\u4e2d\u90a3\u4e2a\u5973\u8d3c\u653e\u51fa\u6765\uff01\u7136\u540e\u628a\u6211\u9001\u51fa\u5bab\u53bb\uff01\u201d\u5eb7\u7199\u4e00\u60f3\uff0c\u5fc3\u5185\u6697\u7b11\uff1a\u8fd9\u5973\u8d3c\u771f\u662f\u96cf\u513f\uff0c \u6211\u7b54\u5e94\u653e\u5979\uff0c\u4f60\u96be\u9053\u80fd\u51fa\u53bb\u76d1\u89c6\uff1f\u53ea\u8981\u6211\u4e00\u8131\u51fa\u638c\u63e1\uff0c\u5927\u5185\u9ad8\u624b\u9a6c\u4e0a\u8981\u628a\u4f60\u6d3b\u5bb0\u3002\u6545\u610f\u60f3\u4e86 \u4e00\u9635\u8bf4\u9053\uff1a\u201c\u5929\u5b50\u65e0\u620f\u8a00\uff0c\u6211\u9a6c\u4e0a\u5199\u4e0b\u5fa1\u65e8\uff0c\u53eb\u4eba\u653e\u5979\uff0c\u4f60\u53ef\u653e\u5fc3\u4e86\u5427\uff1f\u201d \u3000\u3000\u5192\u6d63\u83b2\u5b9d\u5251\u4e00\u6307\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5728\u6253\u4ec0\u4e48\u9b3c\u4e3b\u610f\uff0c\u4f55\u662f\u6211\u82e5\u6b7b\u5728\u5bab\u91cc\uff0c\u90a3\u6e05\u51c9 \u5bfa\u7684\u8001\u548c\u5c1a\u4f1a\u66ff\u6211\u5ff5\u7ecf\u3002\u201d\u5eb7\u7199\u9762\u8272\u500f\u53d8\uff0c\u65a5\u9053\uff1a\u201c\u4ec0\u4e48\u8001\u548c\u5c1a\uff1f\u201d\u5192\u6d63\u83b2\u51b7\u7b11\u9053\uff1a\u201c\u662f \u5440\uff0c\u4ec0\u4e48\u8001\u548c\u5c1a\uff1f\u6211\u771f\u7cca\u6d82\uff0c\u8001\u548c\u5c1a\u65e9\u6b7b\u6389\u4e86\uff0c\u4e0d\u80fd\u5ff5\u7ecf\u5566\uff01\u201d\u5ffd\u7136\u5728\u6000\u91cc\u638f\u51fa\u4e00\u4e32\u73cd\u73e0\uff0c \u5b9d\u5149\u5916\u6620\uff0c\u626c\u4e86\u4e00\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e32\u73cd\u73e0\u662f\u8fd9\u5c4b\u5b50\u7684\u4e3b\u4eba\u7684\uff0c\u8001\u548c\u5c1a\u8fd8\u7b97\u597d\u5fc3\uff0c\u4e34\u6b7b\u524d\u5c06\u5b83 \u4ea4\u56de\u7ed9\u6211\u3002\u54b3\uff0c\u4ed6\u53ef\u6b7b\u5f97\u771f\u60e8\uff01\u201d\u5192\u70f7\u83b2\u4ee5\u524d\u591c\u63a2\u6e05\u51c9\u5bfa\u65f6\uff0c\u78b0\u5230\u505a\u4e86\u548c\u5c1a\u7684\u987a\u6cbb\u7687\u5e1d\uff0c\u987a \u6cbb\u66fe\u4e00\u624b\u643a\u7740\u5979\uff0c\u4e00\u624b\u643a\u7740\u5eb7\u7199\uff0c\u53bb\u796d\u8463\u5c0f\u5b9b\u7684\u8863\u51a0\u51a2\uff0c\u8fd9\u4e32\u5b9d\u73e0\uff0c\u5c31\u662f\u8001\u548c\u5c1a\u90a3\u65f6\u4ea4\u7ed9\u5979 \u7684\uff08\u89c1\u7b2c\u4e8c\u56de\uff09\u3002\u5eb7\u7199\u8fd9\u65f6\u65e9\u5df2\u8ba4\u51fa\u5192\u6d63\u83b2\u662f\u8c01\uff0c\u505a\u58f0\u4e0d\u5f97\u3002\u5192\u6d63\u83b2\u53c8\u6307\u7740\u5730\u4e0a\u7684\u5c38\u4f53\u9053\uff1a \u201c\u4ed6\u53ef\u6b7b\u5f97\u4e0d\u503c\uff0c\u6bd4\u960e\u4e2d\u5929\u5dee\u591a\u4e86\u3002\u201d\u5eb7\u7199\u9762\u8272\u82cd\u767d\uff0c\u8eab\u5b50\u53d1\u6296\u3002\u5192\u6d63\u83b2\u563b\u563b\u7b11\u9053\uff1a\u201c\u4f60\u82e5 \u6562\u4f24\u6211\u6beb\u53d1\uff0c\u6211\u7acb\u523b\u5c31\u5728\u5bab\u91cc\u628a\u8fd9\u4ef6\u4e8b\u60c5\u6296\u51fa\u6765\uff01\u201d\u5eb7\u7199\u5fc3\u91cc\u6253\u7a81\uff0c\u60f3\u9053\uff1a\u201c\u82e5\u5979\u5728\u5916\u9762\u6cc4 \u9694\uff0c\u6211\u8fd8\u4e0d\u6015\u3002\u5728\u5bab\u91cc\u56b7\u51fa\u6765\uff0c\u592a\u540e\u77e5\u9053\u4e86\uff0c\u53ef\u4e0d\u662f\u5f53\u800d\u7684\u3002\u201d\u5f53\u4e0b\u6ee1\u8138\u5806\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60 \u8fd9\u5973\u5a03\u5b50\u771f\u662f\uff0c\u6211\u7b54\u5e94\u9001\u4f60\u51fa\u5bab\uff0c\u4f60\u778e\u7591\u5fc3\u4f5c\u751a\uff1f\u201d\u5192\u6d63\u83b2\u773c\u5149\u8d5b\u5982\u5bd2\u51b0\u5229\u526a\uff0c\u8feb\u89c6\u5eb7\u7199\uff0c \u50ac\u9053\uff1a\u201c\u5feb\u5199\uff0c\u5feb\u5199\uff01\u628a\u90a3\u5973\u8d3c\u653e\u51fa\u6765\uff01\u201d \u3000\u3000\u5eb7\u7199\u542e\u7b14\u6325\u6beb\uff0c\u6b63\u601d\u8131\u8eab\u4e4b\u8ba1\u3002\u5ffd\u542c\u5f97\u5c4b\u5916\u811a\u6b65\u58f0\u5927\u4f5c\uff0c\u695a\u662d\u5357\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u7687\u4e0a\u5728\u8fd9 \u91cc\u5417\uff1f\u201d\u5eb7\u7199\u5e94\u9053\uff1a\u201c\u5728\u8fd9\u91cc\uff01\u201d\u5192\u6d63\u83b2\u5229\u5251\u5728\u4ed6\u8116\u5b50\u4e00\u67b6\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u8bb8\u4ed6\u8fdb\u6765\uff01\u201d \u695a\u662d\u5357\u811a\u6b65\u58f0\u5df2\u5230\u95e8\u524d\uff0c\u5eb7\u7199\u9053\uff1a\u201c\u4f60\u4e14\u7a0d\u5019\uff0c\u6715\u5c31\u51fa\u6765\uff01\u201d\u695a\u662d\u5357\u7980\u9053\uff1a\u201c\u9102\u738b\u5983\u8fdb\u5bab\uff0c \u73b0\u5728\u5916\u9762\u5019\u89c1\uff01\u201d\u5eb7\u7199\u5c06\u672a\u5199\u5b8c\u7684\u7eb8\u63c9\u6210\u4e00\u56e2\uff0c\u968f\u624b\u4e00\u6254\uff0c\u5192\u6d63\u83b2\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u505a\u4ec0\u4e48\uff1f\u201d \u5eb7\u7199\u9053\uff1a\u201c\u60f3\u4e0d\u51fa\u4e86\uff01\u201d\u5192\u6d63\u83b2\u60f3\u8feb\u4ed6\u518d\u5199\uff0c\u53ea\u542c\u5f97\u5916\u9762\u53c8\u6709\u592a\u76d1\u5bdf\u9053\uff1a\u201c\u592a\u540e\u83b2\u9a7e\u5230\uff01\u201d \u5eb7\u7199\u82e6\u7b11\u9053\uff1a\u201c\u592a\u540e\u6765\u4e86\uff0c\u6211\u53ef\u4e0d\u80fd\u963b\u5979\u8fdb\u6765\uff01\u201d\u5192\u6d63\u83b2\u7709\u5934\u4e00\u76b1\uff0c\u85cf\u597d\u5b9d\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u51fa \u53bb\uff01\u201d\u5eb7\u7199\u4e00\u628a\u63a8\u5f00\u623f\u95e8\uff0c\u695a\u662d\u5357\u66ae\u89c1\u7687\u5e1d\u80cc\u540e\uff0c\u8ddf\u7740\u4e00\u4e2a\u5bab\u5a25\uff0c\u9762\u8c8c\u597d\u719f\uff01\u4e0d\u6562\u7ec6\u770b\uff0c\u5192 \u6d63\u83b2\u8fc5\u5373\u628a\u623f\u95e8\u63a9\u4e0a\uff0c\u4f4e\u4f4e\u5728\u5eb7\u7199\u8033\u8fb9\u8bf4\u4e86\u53e5\uff1a\u201c\u8bb0\u7740\u8001\u548c\u5c1a\uff01\u201d\u5eb7\u7199\u6325\u624b\u9053\uff1a\u201c\u4f60\u4eec\u8fdb\u6765 \u4f5c\u751a\uff1f\u90fd\u968f\u6211\u51fa\u53bb\uff01\u201d\u695a\u662d\u5357\u5e94\u58f0\u201c\u662f\u201d\uff0c\u968f\u53c8\u7980\u9053\uff1a\u201c\u662f\u592a\u540e\u53eb\u6211\u4eec\u5230\u8fd9\u91cc\u627e\u7684\u3002\u201d\u5eb7\u7199 \u54e6\u4e86\u4e00\u58f0\uff0c\u5927\u8e0f\u6b65\u8d70\u51fa\uff0c\u5192\u6d63\u83b2\u7d27\u7d27\u8ddf\u7740\u3002\u695a\u662d\u5357\u8fd9\u65f6\u5df2\u770b\u51fa\u5192\u6d63\u83b2\u662f\u8c01\uff0c\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u4e00\u884c\u4eba\u8d70\u51fa\u201c\u5170\u98ce\u7cbe\u820d\u201d\uff0c\u592a\u540e\u8fce\u9762\u95ee\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u5eb7\u7199\u9053\uff1a\u201c\u60f3\u6765\u53d6\u4e00 \u4e9b\u4e66\u3002\u201d\u592a\u540e\u770b\u7740\u5192\u6d63\u83b2\u624b\u4e0a\u7684\u753b\u5377\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4ece\u91cc\u9762\u62ff\u51fa\u6765\u7684\u5417\uff1f\u201d\u5eb7\u7199\u70b9\u4e86\u70b9 \u5934\uff0c\u592a\u540e\u6b63\u60f3\u53eb\u5979\u6253\u5f00\u6765\u770b\u3002\u9102\u738b\u5983\u8d70\u8fc7\u6765\uff0c\u592a\u76d1\u5c06\u9ec4\u7eeb\u94fa\u5728\u5730\u4e0a\uff0c\u9102\u738b\u5983\u8dea\u4e0b\u53e9\u5934\u3002\u592a\u540e \u9053\uff1a\u201c\u5979\u5df2\u7b49\u4e0d\u53ca\u965b\u89c1\u4e86\u3002\u201d\u5eb7\u7199\u95ee\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u7d27\u8981\u4e8b\u4e48\uff1f\u201d\u592a\u540e\u9053\uff1a\u201c\u5979\u8bf4\uff0c\u75c5\u5df2\u7a0d\u5fae \u597d\u4e86\uff0c\u60f3\u5230\u5929\u7262\u5ba1\u5973\u8d3c\uff01\u201d\u5eb7\u7199\u9053\uff1a\u201c\u90a3\u5979\u5c31\u53bb\u597d\u5566\uff01\u201d\u9102\u738b\u5983\u53e9\u7740\u5934\u8c22\u6069\u3002\u592a\u540e\u5f88\u7231\u60dc \u5979\uff0c\u62c9\u5979\u8d77\u6765\uff0c\u5192\u6d63\u83b2\u8d81\u6b62\u5df2\u65f6\u673a\uff0c\u5ffd\u7136\u5728\u7687\u5e1d\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u6211\u8981\u8ddf\u9102\u738b\u5983\u51fa\u53bb\uff01\u201d\uff09 \u3000\u3000\u539f\u6765\u5192\u6d63\u83b2\u5fc3\u60f3\uff1a\u867d\u7136\u81ea\u5df1\u63e1\u6709\u7687\u5e1d\u628a\u67c4\uff0c\u8981\u60f3\u5b89\u5168\u51fa\u5bab\uff0c\u90a3\u4e5f\u5f88\u96be\u3002\u5728\u5bab\u4e2d\u7687\u5e1d\u6015\u81ea \u5df1\u8bf4\u51fa\u6740\u7236\u4e4b\u4e8b\uff0c\u4e0d\u6562\u52a0\u5bb3\uff0c\u82e5\u4ed6\u6d3e\u4eba\u9001\u81ea\u5df1\u51fa\u5b98\uff0c\u90a3\u4ed6\u51c6\u4f1a\u6697\u4e0b\u6bd2\u624b\u3002\u800c\u4e14\u6050\u6015\u82e5\u518d\u803d\u6401 \u4e0b\u53bb\uff0c\u4f1a\u6709\u4eba\u8ba4\u51fa\u81ea\u5df1\u662f\u7eb3\u5170\u516c\u5b50\u5e26\u5165\u7981\u82d1\uff0c\u5e76\u66fe\u5728\u4e09\u516c\u4e3b\u5bab\u5185\u4f4f\u8fc7\u7684\uff0c\u90a3\u5c82\u4e0d\u8fde\u7d2f\u4ed6\u4eec\u3002 \u5979\u5bf9\u9102\u738b\u5983\u867d\u7136\u4e5f\u4e0d\u6562\u76f8\u4fe1\uff0c\u4f46\u603b\u89c9\u5f97\u5728\u9102\u738b\u5983\u8eab\u8fb9\u4f1a\u5b89\u5168\u5f97\u591a\u3002 \u3000\u3000\u5eb7\u7199\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u592a\u540e\u5df1\u5c06\u9102\u738b\u5983\u62c9\u8d77\u3002\u5eb7\u7199\u9053\uff1a\u201c\u9102\u4eb2\u738b\u4e0d\u5e78\u60e8\u6b7b\uff0c\u6715\u751a\u60bc\u5ff5\u3002\u5c1a \u671b\u738b\u5983\u8282\u54c0\u3002\u6715\u6709\u5bab\u5a25\u4e00\u540d\uff0c\u901a\u6653\u7434\u68cb\uff0c\u4f36\u4fd0\u89e3\u4e8b\uff0c\u7279\u8d50\u4e0e\u738b\u5983\uff0c\u4ee5\u89e3\u70e6\u95f7\u3002\u201d\u5192\u6d63\u83b2\u76c8\u76c8 \u4e0b\u62dc\u3002\u9102\u738b\u5983\u518d\u8c22\u8fc7\u6069\u540e\uff0c\u6276\u8d77\u5192\u6d63\u83b2\uff0c\u5fc3\u60f3\uff1a\u201c\u600e\u7684\u7687\u4e0a\u4eca\u5929\u4f1a\u7a81\u7136\u5c06\u5bab\u5a25\u8d50\u7ed9\u6211\uff1f\u201d\u672c \u6765\u7687\u5e1d\u5c06\u5bab\u5a25\u8d4f\u8d50\u4eb2\u738b\u738b\u5983\uff0c\u4e5f\u662f\u5bfb\u5e38\u7684\u4e8b\uff0c\u53ea\u4e0d\u662f\u8fd9\u6837\u5f53\u9762\u8d4f\u8d50\uff0c\u800c\u662f\u4ee4\u5bab\u4e2d\u592a\u76d1\uff0c\u4ee5\u9999 \u8f66\u5b9d\u8f87\uff0c\u9001\u5230\u5e9c\u7b2c\u7f62\u4e86\u3002\u738b\u5983\u867d\u89c9\u4e0d\u5927\u5bfb\u5e38\uff0c\u4f46\u4e5f\u4e0d\u7279\u522b\u5947\u602a\u3002 \u3000\u3000\u592a\u540e\u4e00\u5fc3\u5ff5\u7740\u8463\u5c0f\u5b9b\u7684\u4e8b\u60c5\uff0c\u60f3\u95ee\u7687\u5e1d\u5728\u5979\u623f\u4e2d\u89c1\u5230\u4ec0\u4e48\uff0c\u5e76\u4e0d\u5728\u610f\u5192\u6d63\u83b2\u548c\u9102\u738b\u5983\uff0c \u5f53\u4e0b\u5c31\u50ac\u7687\u5e1d\u56de\u8f6c\u666f\u9633\u5bab\u3002\u5eb7\u7199\u5ffd\u7136\u5411\u524d\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u4e09\u59b9\u59b9\u4e5f\u6765\u4e86\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u521a\u968f\u9102\u738b\u5983\u8d70\u4e86\u51e0\u6b65\uff0c\u5ffd\u89c1\u4e09\u516c\u4e3b\u8fce\u9762\u8d70\u6765\uff0c\u6025\u5fd9\u4f7f\u4e2a\u773c\u8272\u3002\u4e09\u516c\u4e3b\u95ee\u9053\uff1a\u201c\u738b \u5983\u8fd9\u4e48\u65e9\u8fdb\u5bab\uff1f\u201d\u4e00\u9762\u77a7\u7740\u5192\u6d63\u83b2\u3002\u9102\u738b\u5983\u70b9\u4e86\u70b9\u5934\uff0c\u6307\u7740\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u53ef\u8ba4\u8bc6\u5979 \u5417\uff1f\u7687\u4e0a\u8bf4\u5979\u901a\u6653\u7434\u68cb\uff0c\u4ee5\u540e\u6211\u4e5f\u6709\u4e2a\u4eba\u6307\u70b9\u4e86\u3002\u201d\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u54e6\uff0c\u90a3\u4e48\u662f\u7687\u4e0a\u5c06\u5979\u8d4f\u8d50 \u7ed9\u4f60\u4e86\uff1f\u201d\u9102\u738b\u5983\u9053\uff1a\u201c\u4e0d\u6562\uff01\u201d\u4e09\u516c\u4e3b\u62c9\u7740\u5192\u6d63\u83b2\u7684\u624b\uff0c\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u5f85\u6211\u770b\u770b\uff0c\u957f\u5f97\u771f \u4fca\u554a\uff01\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u600e\u4e48\u6211\u4ee5\u80fd\u6ca1\u89c1\u8fc7\u4f60\u5462\uff1f\u201d\u5979\u88c5\u7740\u548c\u5192\u6d63\u83b2\u8bf4\u8bdd\uff0c\u624b\u4e2d\u4e00\u4ef6\u4e1c\u897f\u65e9\u9012 \u4e86\u8fc7\u53bb\uff0c\u5192\u6d63\u83b2\u4f55\u7b49\u673a\u7075\uff0c\u62e2\u8896\u4e00\u63d6\uff0c\u4e1c\u897f\u65e9\u5df2\u85cf\u4eba\u8896\u4e2d\u3002\u592a\u540e\u5728\u90a3\u8fb9\u7b49\u5f97\u4e0d\u8010\u70e6\uff0c\u62db\u624b\u53eb \u4e09\u516c\u4e3b\u8fc7\u53bb\u3002\u4e09\u516c\u4e3b\u7b11\u76c8\u76c8\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u6709\u4ec0\u4e48\u4e0d\u61c2\u7684\uff0c\u53ef\u4ee5\u8bf7\u95ee\u9102\u738b\u5983\u3002\u201d\u5192\u6d63\u83b2\u5fc3\u9886 \u795e\u4f1a\uff0c\u968f\u9102\u738b\u5983\u767b\u4e0a\u5b9d\u8f87\uff0c\u8f7b\u8f7b\u6613\u6613\u5730\u51fa\u4e86\u7981\u5bab\u3002 \u3000\u3000\u5192\u6d63\u83b2\u5728\u8f87\u4e2d\u4e0e\u738b\u5983\u540c\u5ea7\uff0c\u8d8a\u53d1\u770b\u5f97\u6e05\u695a\uff0c\u53ea\u89c9\u738b\u5983\u4e0e\u6613\u5170\u73e0\u975e\u4f46\u76f8\u8c8c\u76f8\u540c\uff0c\u8fde\u8bf4\u8bdd\u795e \u60c5\u4e0e\u7709\u5b87\u95f4\u90a3\u80a1\u54c0\u6028\u4e4b\u6c14\uff0c\u4e5f\u4e00\u6a21\u4e00\u6837\u3002\u518d\u56de\u60f3\u6613\u5170\u73e0\u5728\u4e94\u53f0\u5c71\u884c\u523a\u591a\u94ce\u65f6\uff0c\u66ff\u738b\u5983\u6321\u4f4f\u98de \u9556\u7684\u5f80\u4e8b\uff0c\u5fc3\u4e2d\u900f\u660e\u96ea\u4eae\u3002\u9102\u738b\u5983\u89c1\u5192\u6d63\u83b2\u5c3d\u770b\u7740\u81ea\u5df1\uff0c\u6beb\u65e0\u666e\u901a\u5bab\u5a25\u90a3\u79cd\u754f\u7f29\u795e\u60c5\uff0c\u5fc3\u4e2d \u4e5f\u662f\u5947\u602a\u3002 \u3000\u3000\u56de\u5230\u738b\u5e9c\uff0c\u738b\u5983\u5c4f\u9000\u4f8d\u5973\uff0c\u7559\u5192\u6d63\u83b2\u72ec\u81ea\u966a\u7740\u81ea\u5df1\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u5728\u5bab\u4e2d\u591a\u5c11\u5e74\u4e86\uff1f\u662f\u4f3a \u5019\u7687\u4e0a\u8fd8\u662f\u670d\u4f8d\u7687\u540e\uff1f\u201d\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u6211\u8fdb\u5bab\u4e2d\u603b\u5171\u8fd8\u4e0d\u5230\u4e24\u5929\uff01\u201d\u738b\u5983\u60ca\u95ee\u9053\uff1a\u201c\u4f60\u4e0d \u662f\u5bab\u5a25\uff1f\u201d\u5192\u70f7\u83b2\u70b9\u4e86\u70b9\u5934\u3002\u738b\u5983\u9053\uff1a\u201c\u90a3\u4f60\u8fdb\u5bab\u505a\u4ec0\u4e48\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u548c\u4f60\u4e00\u6837\uff01\u201d\u738b \u5983\u9762\u8272\u9661\u53d8\uff0c\u5192\u6d63\u83b2\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u90a3\u662f\u4e3a\u7740\u6551\u4e00\u4e2a\u4eba\uff01\u201d\u738b\u5983\u53cc\u773c\u5706\u7741\uff0c\u98a4\u58f0\u559d\u9053\uff1a\u201c\u4f60\u5230 \u5e95\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5192\u6d63\u83b2\u903c\u524d\u4e00\u6b65\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u662f\u6613\u5170\u73e0\u7684\u53cb\u4eba\u3002\u201d\u9102\u738b\u5983\u9762\u8272\u60e8\u767d\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u5979\u628a\u4ec0\u4e48\u90fd\u544a\u8bc9\u4f60\u4e86\uff1f\u201d\u5192\u6d63\u83b2\u907f\u800c\u4e0d\u7b54\uff0c\u53cd\u95ee\u9053\uff1a\u201c\u738b\u5983\uff0c\u4f60\u771f\u8981\u5c06\u5979\u6740\u6b7b\u66ff\u4f60\u7684\u4e08\u592b \u62a5\u4ec7\uff1f\u201d\u738b\u5983\u63a9\u9762\u53eb\u9053\uff1a\u201c\u4f60\u522b\u8fd9\u6837\u903c\u6211\u884c\u4e0d\u884c\uff1f\u201d\u5192\u6d63\u83b2\u6df1\u6df1\u4e00\u63d6\uff0c\u53c8\u9053\uff1a\u201c\u738b\u5983\uff0c\u662f\u6211 \u8bf4\u9519\u4e86\uff01\u5979\u7ed9\u6253\u4e0b\u5929\u7262\uff0c\u4f60\u4e00\u5b9a\u6bd4\u6211\u4eec\u66f4\u7126\u6025\uff0c\u66f4\u8981\u6551\u5979\uff01\u201d\u738b\u5983\u54ed\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u529e \u6cd5\uff1f\u201d\u5192\u6d63\u83b2\u53cc\u8896\u4e00\u6296\uff0c\u5c06\u4e09\u516c\u4e3b\u7ed9\u5979\u7684\u4e1c\u897f\u62ff\u51fa\uff0c\u89e3\u5f00\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u5757\u900f\u660e\u78a7\u7389\u96d5\u6210\u4e00\u5bf9 \u76f8\u8fde\u7684\u6731\u679c\uff0c\u4e0a\u6709\u9f99\u7eb9\u56fe\u6848\uff0c\u523b\u5f97\u5341\u5206\u7cbe\u81f4\u3002\u5192\u6d63\u83b2\u5927\u60d1\u4e0d\u89e3\uff0c\u738b\u5983\u4e00\u89c1\uff0c\u53cc\u773c\u653e\u5149\uff0c\u6025\u5fd9 \u95ee\u9053\uff1a\u201c\u8fd9\u662f\u7687\u4e0a\u7ed9\u4f60\u7684\u5417\uff1f\u201d\u5192\u6d63\u83b2\u6447\u4e86\u6447\u5934\uff0c\u738b\u5983\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u662f\u7687\u4e0a\u7684\u610f \u601d\uff0c\u8c01\u77e5\u662f\u4f60\u5077\u6765\u7684\uff01\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4f60\u522b\u7ba1\u6211\u662f\u600e\u6837\u5f97\u6765\u7684\uff0c\u4f60\u5feb\u7ed9\u6211\u8bf4\u8bf4\u8fd9\u662f\u4ec0\u4e48\u4e1c \u897f\uff1f\u201d \u3000\u3000\u9102\u738b\u5983\u5c06\u6765\u679c\u63a5\u8fc7\uff0c\u53c8\u4ed4\u7ec6\u770b\u4e86\u4e00\u9635\uff0c\u7528\u4e24\u53ea\u62c7\u6307\u5728\u6731\u679c\u4e0a\u4e00\u6309\uff0c\u6731\u679c\u5ffd\u5730\u88c2\u5f00\uff0c\u679c\u6838 \u7a81\u51fa\uff0c\u9102\u738b\u5983\u5c06\u679c\u6838\u5c16\u7aef\u5728\u7eb8\u4e0a\u4e00\u523a\uff0c\u7eb8\u4e0a\u7acb\u523b\u73b0\u51fa\u4e24\u4e2a\u6781\u7ea4\u7ec6\u7684\u6ee1\u6d32\u6587\u5b57\uff0c\u5192\u6d63\u83b2\u4e00\u4e2a\u4e5f \u4e0d\u8ba4\u5f97\u3002 \u3000\u3000\u9102\u738b\u5983\u62c7\u6307\u653e\u677e\uff0c\u6731\u679c\u590d\u5408\uff0c\u8bf4\u9053\uff1a\u201c\u679c\u7136\u662f\u4e86\uff0c\u53ef\u60dc\u62ff\u5230\u4e86\u624b\u4e5f\u6ca1\u6709\u7528\u3002\u8fd9\u4e2a\u53eb\u6731\u679c \u91d1\u7b26\uff0c\u6211\u4eec\u7684\u592a\u7956\u636e\u8bf4\u662f\u541e\u4e0b\u795e\u4eba\u6731\u679c\u800c\u8bde\u751f\u7684\uff0c\u6240\u4ee5\u6731\u679c\u91d1\u7b26\uff0c\u4e00\u5411\u662f\u5185\u5ef7\u4fe1\u7269\u3002\u7687\u5e1d\u6709 \u4ec0\u4e48\u5bc6\u4ee4\uff0c\u5e38\u5c06\u6731\u679c\u91d1\u7b26\u4ea4\u7ed9\u5927\u81e3\u6216\u536b\u58eb\u53bb\u529e\u3002\u201d\u5192\u6d63\u83b2\u559c\u9053\uff1a\u201c\u90a3\u6211\u4eec\u6709\u4e86\u8fd9\u4e2a\uff0c\u5c82\u4e0d\u5c31 \u53ef\u4ee5\u6551\u51fa\u6613\u5170\u73e0\u59d0\u59d0\uff1f\u201d\u9102\u738b\u5983\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4f60\u542c\u6211\u8bf4\uff0c\u6731\u679c\u91d1\u7b26\u53ea\u80fd\u4ea4\u7ed9\u5927\u81e3\u6216\u5185 \u5ef7\u4f8d\u536b\u505a\u4fe1\u7269\uff0c\u800c\u4e14\u5018\u975e\u4e00\u54c1\u5927\u5458\u548c\u4e00\u7b49\u5f85\u536b\uff0c\u7687\u5e1d\u82e5\u8981\u4ed6\u6301\u91d1\u7b26\u529e\u4e8b\uff0c\u8fd8\u9700\u8d50\u4ee5\u5bc6\u8bcf\uff0c\u4e0a \u5199\u6731\u679c\u91d1\u7b26\uff0c\u4ea4\u4e0e\u67d0\u67d0\u7b49\u5b57\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u82e5\u6709\u5bc6\u8bcf\u53c8\u4f55\u5fc5\u66f4\u8d50\u91d1\u7b26\uff1f\u201d\u9102\u738b\u5983\u7b11\u9053\uff1a \u201c\u5bab\u5ef7\u4e4b\u4e8b\uff0c\u4f60\u6709\u6240\u4e0d\u77e5\u3002\u7687\u5e1d\u6709\u4e9b\u4e8b\u60c5\uff0c\u662f\u4e0d\u80fd\u5728\u8bcf\u4e66\u4e0a\u5199\u660e\u7684\uff0c\u5bc6\u8bcf\u53ea\u5199\u660e\u91d1\u7b26\u7531\u8c01\u6267 \u638c\uff0c\u90a3\u4e48\u624b\u5f85\u91d1\u7b26\u7684\u4eba\uff0c\u5c31\u662f\u7687\u4e0a\u7684\u94a6\u4f7f\uff0c\u53ef\u4ee5\u6743\u5b9c\u884c\u4e8b\uff0c\u4f46\u5374\u53c8\u4e0d\u843d\u75d5\u8ff9\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u60f3\u4e86\u4e00\u9635\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u6211\u660e\u767d\u4e86\uff0c\u4f60\u662f\u8bf4\u6211\u4eec\u65e2\u975e\u4e00\u54c1\u5927\u81e3\u53c8\u975e\u4e00\u7b49\u5f85 \u536b\uff0c\u624b\u4e0a\u53c8\u6ca1\u6709\u91d1\u7b26\u7684\u8bcf\u4e66\uff0c\u6240\u4ee5\u6b64\u7269\u5c31\u6beb\u65e0\u7528\u5904\u3002\u201d\u9102\u738b\u5983\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u8fd9\u6837\uff01\u201d\u5192 \u6d63\u83b2\u7b11\u9053\uff1a\u201c\u4e00\u54c1\u5927\u81e3\u6211\u4eec\u4e0d\u80fd\u5047\u5192\uff0c\u96be\u9053\u4e00\u7b49\u5f85\u536b\u6211\u4eec\u4e5f\u4e0d\u80fd\u5047\u5192\u5417\uff1f\u201d\u9102\u738b\u5983\u8df3\u8d77\u8bf4 \u9053\uff1a\u201c\u4f60\u771f\u806a\u660e\uff0c\u4e00\u54c1\u5927\u5458\uff0c\u671d\u4e2d\u53ea\u6709\u9650\u51e0\u4eba\uff0c\u81ea\u7136\u4e0d\u80fd\u5047\u5192\u3002\u53ef\u662f\u5185\u5ef7\u7684\u4e00\u7b49\u5f85\u536b\uff0c\u5f80\u5f80 \u4e0d\u4e3a\u5916\u5ef7\u6240\u77e5\uff0c\u5047\u5192\u90a3\u662f\u5bb9\u6613\u5f97\u591a\uff01\u201d\u5979\u6c89\u541f\u534a\u664c\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u8c01\u6709\u8fd9\u6837\u5927\u80c6\uff1f\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u6709\u4eba\u53eb\u9053\uff1a\u201c\u8c01\u6562\u8fd9\u6837\u5927\u80c6\uff01\u201d\u9102\u738b\u5983\u4e0e\u5192\u6d63\u83b2\u63a8\u7a97\u4e00\u770b\uff0c\u53ea\u89c1 \u4e00\u4e2a\u9752\u8863\u5987\u4eba\u8fd0\u5251\u5982\u98ce\uff0c\u628a\u5728\u697c\u4e0b\u5b88\u536b\u7684\u56db\u540d\u738b\u5e9c\u4f8d\u536b\uff0c\u8feb\u5f97\u2026\u7ea7\u7ea7\u5730\u5f80\u4e0a\u76f4\u9000\u3002\u56db\u540d\u4f8d\u536b \u8fde\u8fde\u547c\u559d\uff0c\u90a3\u9752\u8863\u5987\u4eba\u5374\u662f\u4e1d\u6beb\u4e0d\u776c\uff0c\u5251\u6cd5\u8fc5\u75be\u4e4b\u6781\uff01 \u3000\u3000\u559d\u6597\u58f0\u4e2d\uff0c\u4e00\u540d\u4f8d\u536b\u7a81\u7136\u201c\u54df\u5537\u201d\u4e00\u58f0\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u7ffb\u4e0b\u697c\u53bb\uff0c\u8fde\u5192\u6d63\u83b2\u4e5f\u770b\u4e0d\u6e05\u695a\uff0c \u9752\u8863\u5987\u4eba\u662f\u7528\u4ec0\u4e48\u624b\u6cd5\u628a\u4ed6\u523a\u4f24\u7684\uff0c\u6b63\u60ca\u7591\u95ee\uff0c\u53ea\u89c1\u9752\u8863\u5987\u4eba\u7adf\u5728\u5175\u5203\u98de\u821e\u4e4b\u4e2d\uff0c\u6b3a\u8eab\u76f4 \u8fdb\uff0c\u4e00\u540d\u4f7f\u6746\u68d2\u7684\u4f8d\u536b\uff0c\u5f80\u4e0b\u6251\u8eab\uff0c\u6746\u68d2\u5530\u7684\u5954\u4e0b\u76d8\u7f20\u6253\uff0c\u90a3\u9752\u8863\u5f52\u4eba\u817e\u8eab\u7a9c\u8d77\uff0c\u4e00\u62db\u201c\u98ce \u5df7\u843d\u82b1\u201d\uff0c\u628a\u5176\u4ed6\u4e24\u540d\u4f8d\u536b\u9f50\u5f00\u903c\u9000\uff0c\u53f3\u811a\u5f80\u4e0b\u4e00\u63e3\uff0c\u90a3\u540d\u4f8d\u536b\u6746\u6367\u521a\u521a\u8d34\u7740\u697c\u677f\u626b\u51fa\uff0c\u5c1a \u672a\u957f\u8eab\uff0c\u5df2\u7ed9\u8e22\u4e0b\u697c\u53bb\u3002\u56db\u540d\u4f8d\u536b\uff0c\u6b7b\u4f24\u4e00\u534a\uff0c\u5269\u4e0b\u7684\u4e24\u540d\u4f8d\u536b\uff0c\u98de\u8eab\u8dc3\u4e0a\u6a90\u89d2\uff0c\u9ad8\u53eb\u201c\u738b \u5983\uff0c\u5feb\u8eb2\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u9752\u8863\u5f52\u4eba\u5982\u5927\u96c1\u822c\u817e\u7a7a\u63a0\u6765\uff0c\u4e00\u624b\u6293\u7740\u4e00\u4e2a\uff0c\u6d3b\u751f\u751f\u5730\u4ece\u9ad8\u697c\u4e0a\u76f4 \u6454\u4e0b\u53bb\u3002 \u3000\u3000\u5192\u6d63\u83b2\u968f\u535a\u9752\u4e3b\u51fa\u9053\u4ee5\u6765\uff0c\u4e0d\u77e5\u89c1\u8fc7\u591a\u5c11\u9ad8\u624b\uff0c\u6b64\u65f6\u4e5f\u4e0d\u7531\u5f97\u6697\u81ea\u5fc3\u60ca\u3002\u8fd9\u5987\u4eba\u7684\u5251\u6cd5 \u7adf\u4f3c\u4e0d\u5728\u51cc\u672a\u98ce\u4e4b\u4e0b\uff0c\u800c\u5728\u6842\u4ef2\u660e\u4e4b\u4e0a\uff0c\u662f\u4f55\u8def\u9053\uff0c\u5979\u5374\u6beb\u65e0\u6240\u77e5\u3002\u552f\u6709\u628a\u5927\u8679\u5b9d\u5251\u51fa\u9798\uff0c \u6697\u52a0\u6212\u5907\u3002 \u3000\u3000\u9752\u8863\u5987\u4eba\u529b\u6740\u56db\u540d\u738b\u5e9c\u4f8d\u536b\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u7eb5\u8eab\u8dc3\u8fdb\u623f\u5185\uff0c\u5192\u6d63\u83b2\u62c9\u738b\u5983\u9000\u540e\u51e0\u6b65\uff0c\u6a2a\u5251 \u5c01\u7740\u95e8\u6237\uff0c\u9ad8\u58f0\u95ee\u9053\uff1a\u201c\u662f\u54ea\u4f4d\u524d\u8f88\uff1f\u201d\u9752\u8863\u5987\u4eba\u7406\u4e5f\u4e0d\u7406\uff0c\u5f84\u81ea\u559d\u95ee\u738b\u5983\uff1a\u201c\u4f60\u5c31\u662f\u7eb3\u5170 \u660e\u6167\uff1f\u201d\u738b\u5983\u604d\u60da\u8bb0\u5f97\u597d\u50cf\u662f\u8bb8\u591a\u8bb8\u591a\u5e74\u524d\u89c1\u8fc7\u7684\uff0c\u5e94\u4e86\u4e00\u58f0\uff0c\u9752\u8863\u5987\u4eba\u6597\u624b\u4e00\u626c\uff0c\u4e00\u6761\u8f6f \u97ad\u817e\u7a7a\u98de\u51fa\uff0c\u5377\u5730\u626b\u6765\uff0c\u5192\u6d63\u83b2\u5b9d\u5251\u75be\u7684\u4e00\u64a9\uff0c\u8f6f\u97ad\u7ed9\u65a9\u65ad\u4e00\u622a\uff0c\u800c\u81ea\u5df1\u4e5f\u7ed9\u626f\u52a8\u51e0\u6b65\uff0c\u6574 \u4e2a\u8eab\u8eaf\uff0c\u5411\u524d\u6251\u5012\u3002 \u3000\u3000\u90a3\u9752\u8863\u5987\u4eba\u628a\u5192\u6d63\u83b2\u626f\u8fc7\u4e00\u8fb9\uff0c\u5530\u7684\u4e00\u5251\uff0c\u75be\u5411\u738b\u5983\u523a\u53bb\uff0c\u738b\u5983\u8eab\u5f62\u6025\u95ea\uff0c\u5de6\u638c\u4e0b\u642d\uff0c \u53f3\u638c\u4e0a\u51fb\uff0c\u65bd\u5c55\u5927\u64d2\u62ff\u624b\u4e2d\u7684\u201c\u9f99\u817e\u864e\u8dc3\u201d\u4e00\u62db\uff0c\u53cd\u593a\u654c\u4eba\u5b9d\u5251\uff0c\u9752\u8863\u5987\u4eba\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c \u5251\u5149\u4e00\u95ea\uff0c\u907f\u62db\u8fdb\u62db\uff0c\u5de6\u624b\u957f\u97ad\uff0c\u75be\u98ce\u66b4\u96e8\u822c\u6a2a\u626b\u76f4\u5377\uff0c\u738b\u5983\u8fde\u8fde\u540e\u9053\uff0c\u5f62\u52bf\u5341\u5206\u5371\u9669\u3002\u5192 \u6d63\u83b2\u6025\u633a\u5929\u8679\u5b9d\u5251\uff0c\u5f80\u80cc\u540e\u5939\u653b\uff0c\u9752\u8863\u5987\u4eba\u65a5\u9053\uff1a\u201c\u4f60\u8fd9\u5973\u5a03\u5b50\u627e\u6b7b\uff01\u201d\u4e00\u65cb\u8eab\uff0c\u77ed\u5251\u6a2a \u622a\uff0c\u957f\u97ad\u5939\u51fb\uff0c\u5c06\u5192\u6d63\u83b2\u548c\u738b\u5983\u4e24\u4eba\u90fd\u7f69\u5728\u5251\u5149\u97ad\u5f71\u4e4b\u4e0b\uff0c\u5192\u9662\u83b2\u867d\u6709\u5b9d\u5251\uff0c\u53ea\u662f\u5bf9\u65b9\u6b66\u529f \u6781\u5f3a\uff0c\u8fde\u81ea\u4fdd\u4e5f\u6781\u8270\u96be\uff0c\u66f4\u8c08\u4e0d\u5230\u51fa\u51fb\u3002\u5012\u662f\u7eb3\u5170\u738b\u5983\u638c\u6cd5\u66fe\u5f97\u8fc7\u6768\u4e91\u9aa2\u6307\u70b9\uff0c\u52c9\u5f3a\u8fd8\u53ef\u652f \u6301\u3002 \u3000\u3000\u7eb3\u5170\u738b\u5983\u8fde\u8fde\u558a\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u6709\u8bdd\u597d\u8bb2\uff01\u201d\u9752\u8863\u5987\u4eba\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8d35\u4e3a \u738b\u59d1\uff0c\u54ea\u91cc\u8fd8\u8bb0\u5f97\u8d77\u6211\uff1f\u201d\u53f3\u624b\u5251\u6beb\u4e0d\u653e\u677e\uff0c\u201c\u91d1\u9488\u5ea6\u7ebf\u201d\u201c\u62bd\u6492\u8fde\u73af\u201d\uff0c\u70b9\u54bd\u5589\uff0c\u523a\u5de6 \u808b\uff0c\u626b\u80a9\u80f8\uff0c\u6302\u4e24\u81c2\uff0c\u4e00\u62db\u7d27\u4f3c\u4e00\u62db\uff0c\u5192\u6d63\u83b2\u7ed9\u957f\u97ad\u62e6\u5728\u4e00\u8fb9\uff0c\u6551\u63f4\u4e0d\u5f97\uff0c\u773c\u7741\u7741\u5730\u770b\u7740\u738b \u5983\u5c31\u8981\u4e27\u547d\u5728\u4e09\u5c3a\u9752\u950b\u4e4b\u4e0b\u3002 \u3000\u3000\u9163\u6218\u4e2d\uff0c\u738b\u5983\u53cc\u624b\u5f80\u4e0a\u4e00\u62c9\uff0c\u786c\u5c06\u8eab\u5f62\u62d4\u8d77\uff0c\u4f7f\u51fa\u9669\u62db\u201c\u91d1\u8d61\u620f\u6d6a\u201d\uff0c\u5728\u534a\u7a7a\u4e2d\u4f38\u624b\u5411 \u9752\u8863\u5987\u4eba\u53cc\u76ee\u4fbf\u6293\u3002 \u3000\u3000\u9752\u8863\u5987\u4eba\u51b7\u7b11\u4e00\u58f0\uff1a\u201c\u4f60\u627e\u6b7b\uff01\u201d\u5de6\u624b\u547c\u7684\u4e00\u97ad\uff0c\u5c06\u5192\u6d63\u83b2\u8feb\u5230\u5899\u8fb9\uff0c\u53f3\u81c2\u4e00\u62ac\uff0c\u6321\u5f00 \u4e86\u738b\u5983\u53cc\u6293\uff0c\u77ed\u5251\u53cd\u624b\u4e00\u5708\uff0c\u671d\u7740\u738b\u5983\u9888\u9879\u65a9\u622a\u3002\u5c31\u5728\u8fd9\u6027\u547d\u4ea4\u5173\u4e4b\u9645\uff0c\u738b\u5983\u5ffd\u7136\u89c9\u4e00\u80a1\u5927 \u529b\u5c06\u81ea\u5df1\u4e00\u6258\uff0c\u8d81\u52bf\u6253\u4e2a\u7b4b\u6597\uff0c\u7ffb\u8eab\u843d\u5728\u697c\u4e0a\uff0c\u540c\u65f6\u8033\u8fb9\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u9752\u8863\u5987\u4eba\u7834\u53e3 \u5927\u9a82\uff01 \u3000\u3000\u5192\u6d63\u83b2\u8eb2\u5728\u5899\u89d2\u770b\u5f97\u5206\u660e\uff0c\u89e3\u6551\u738b\u5983\u7684\u4eba\uff0c\u7adf\u662f\u4ece\u697c\u4e2d\u4e00\u5757\u5927\u533e\u989d\u7684\u540e\u9762\u98de\u8eab\u51fa\u6765\u7684\uff0c \u5192\u6d63\u83b2\u6697\u6697\u5fc3\u60ca\uff0c\u6709\u4eba\u85cf\u5728\u8eab\u8fb9\u4e5f\u4e0d\u77e5\u9053\uff0c\u5047\u5982\u662f\u654c\u4eba\u7684\u8bdd\uff0c\u5c82\u4e0d\u7cdf\u7cd5\uff1f \u3000\u3000\u5192\u6d63\u83b2\u518d\u4ed4\u7ec6\u770b\u65f6\uff0c\u5ffd\u7136\u4e00\u9635\u5fc3\u8df3\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u6765\u4eba\u867d\u7136\u4ee5\u5dfe\u8499\u9762\uff0c\u53ef\u662f\u4ece\u8eab\u6750\u5251\u6cd5\u5374 \u770b\u5f97\u51fa\u6765\uff0c\u4e0d\u662f\u51cc\u672a\u98ce\u662f\u8c01\uff1f\uff01\u6d63\u83b2\u4e0d\u81ea\u7981\u5730\u8dd1\u4e86\u4e0a\u53bb\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff01\u201d\u9752\u8863\u5987\u4eba \u53cd\u624b\u4e00\u97ad\u53c8\u628a\u5192\u6d63\u83b2\u8feb\u8fdb\u5899\u89d2\uff0c\u90a3\u8499\u9762\u4eba\u5e94\u58f0\u53eb\u9053\uff1a\u201c\u6d63\u83b2\uff0c\u4f60\u4e0d\u8981\u4e0a\u6765\uff01\u201d\u6b63\u662f\u51cc\u672a\u98ce\u7684 \u58f0\u97f3\u3002 \u3000\u3000\u51cc\u672a\u98ce\u548c\u9752\u8863\u5987\u4eba\u5404\u4ee5\u4e0a\u4e58\u6b66\u529f\u76f8\u640f\uff0c\u5947\u5feb\u65e0\u6bd4\uff0c\u5192\u6d63\u83b2\u770b\u5f97\u773c\u90fd\u82b1\u4e86\uff01\u9752\u8863\u5987\u4eba\u957f\u97ad \u547c\u5730\u4e00\u4e2a\u65cb\u626b\uff0c\u89e3\u5f00\u51cc\u672a\u98ce\u7684\u5251\u62db\uff0c\u77ed\u5251\u80f8\u80fd\u4e00\u7acb\uff0c\u5c01\u95ed\u95e8\u6237\uff0c\u9000\u540e\u4e00\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u662f\u5929 \u5c71\u795e\u8292\uff1f\u201d\u51cc\u672a\u98ce\u63a3\u56de\u9752\u94a2\u5251\u7b54\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6562\u95ee\u524d\u8f88\u4f55\u4eba\uff1f\u201d\u51cc\u672a\u98ce\u4ee5\u4e3a\u5979\u542c\u4e86\u81ea\u5df1\u7684\u540d \u5934\uff0c\u5fc5\u7136\u505c\u4e0b\u5175\u5203\uff0c\u4e0d\u6599\u90a3\u9752\u8863\u5987\u4eba\u70b9\u5934\u7b11\u9053\uff1a\u201c\u5929\u5c71\u795e\u8292\uff0c\u540d\u4e0d\u865a\u4f20\uff0c\u518d\u8bd5\u4f60\u51e0\u62db\u3002\u201d\u957f \u97ad\u5530\u5730\u626b\u51fa\uff0c\u53f3\u624b\u77ed\u5251\u4e5f\u5c55\u5f00\u4e86\u4e00\u6d3e\u8fdb\u624b\u7684\u62db\u6570\u3002\u51cc\u672a\u98ce\u5fc3\u60f3\uff1a\u600e\u7684\u8fd9\u5987\u4eba\u5982\u6b64\u6ca1\u793c\u8c8c\uff01\u8eab \u5f62\u4e00\u6643\uff0c\u9752\u94a2\u5251\u5149\u534e\u95ea\u5904\uff0c\u5c55\u5f00\u4e86\u72c2\u98ce\u66b4\u96e8\u822c\u7684\u5bf9\u653b\u3002 \u3000\u3000\u90a3\u9752\u8863\u5987\u4eba\u6b66\u529f\u975e\u540c\u5c0f\u53ef\uff0c\u4e24\u624b\u540c\u65f6\u4f7f\u7528\u4e24\u822c\u5175\u5668\uff0c\u7adf\u7136\u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\u3002\u540c\u65f6\u4f7f\u4e24\u79cd \u5175\u5203\u7684\u4eba\uff0c\u51cc\u672a\u98ce\u4ee5\u524d\u53ea\u78b0\u8fc7\u4e00\u4e2a\u4e18\u4e1c\u6d1b\uff0c\u5de6\u5200\u53f3\u5251\uff0c\u5df2\u662f\u4e0d\u51e1\u3002\u4f46\u73b0\u5728\u548c\u8fd9\u9752\u8863\u5987\u4eba\u4e00 \u6bd4\uff0c\u90a3\u4e18\u4e1c\u6d1b\u7b80\u76f4\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u51cc\u672a\u98ce\u5929\u5c71\u5251\u6cd5\u795e\u5999\u65e0\u6bd4\uff0c\u4e5f\u53ea\u80fd\u582a\u582a\u6253\u4e2a\u5e73\u624b\uff0c\u4e0d\u7531\u4ed6\u4e0d \u6697\u6697\u60ca\u5947\uff01\u4ed6\u6740\u5f97\u5174\u8d77\uff0c\u5b9d\u5251\u4e00\u6296\uff0c\u94f6\u661f\u70b9\u70b9\uff0c\u970e\u65f6\u95f4\u53ea\u89c9\u4e00\u5ba4\u4e4b\u5185\uff0c\u5251\u5149\u7ee6\u7ed5\uff0c\u5230\u5904\u90fd\u662f \u51cc\u672a\u98ce\u7684\u5f71\u5b50\u3002\u9752\u8863\u5987\u4eba\u559d\u58f0\u201c\u597d\uff01\u201d\u5de6\u97ad\u53f3\u5251\uff0c\u89c1\u62db\u62c6\u62db\uff0c\u8eab\u5f62\u4e5f\u662f\u56db\u9762\u6e38\u8d70\uff0c\u6e9c\u6ed1\u975e \u5e38\uff0c\u51cc\u672a\u98ce\u81ea\u51fa\u9053\u4ee5\u6765\uff0c\u4ece\u672a\u78b0\u8fc7\u5982\u6b64\u529f\u529b\u6df1\u539a\u7684\u4eba\uff0c\u84e6\u5730\u7701\u8d77\uff1a\u201c\u83ab\u975e\u5979\u8fd8\u5728\u4eba\u95f4\uff1f\u201d\u624b \u4e2d\u5251\u4e00\u7d27\uff0c\u9163\u6597\u4e2d\u5de6\u638c\u731b\u5730\u659c\u51fb\uff0c\u638c\u98ce\u5230\u5904\uff0c\u9752\u8863\u5987\u4eba\u671d\u9752\u5e03\u5305\u5934\u98d8\u7136\u7ffb\u8d77\uff0c\u5192\u6d63\u83b2\u53c8\u662f\u4e00 \u60ca\uff0c\u9752\u8863\u5987\u4eba\u989c\u5bb9\u7f8e\u8273\uff0c\u5374\u662f\u767d\u53d1\u8427\u7136\uff0c\u5305\u5934\u91cc\u8fd8\u7f20\u7740\u4e00\u6761\u7ea2\u5dfe\uff0c\u968f\u7740\u638c\u98ce\u98d8\u52a8\u3002\u51cc\u672a\u98ce\u500f \u5730\u8df3\u51fa\u5708\u5b50\uff0c\u62b1\u5251\u5f53\u80f8\uff0c\u957f\u63d6\u5230\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u5931\u656c\uff01\u5931\u656c\uff01\u539f\u6765\u662f\u98de\u7ea2\u5dfe\u5973\u4fa0\uff01\u201d\u9752\u8863\u5987\u4eba \u5927\u7b11\u58f0\u4e2d\uff0c\u957f\u5251\u500f\u5730\u6536\u56de\uff0c\u77ed\u5251\u63b7\u5728\u684c\u4e0a\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u6127\u662f\u6768\u5927\u4fa0\u7684\u5e08\u5f1f\uff01\u770b\u5230\u4e86\u4f60\uff0c\u5c31 \u5982\u540c\u518d\u89c1\u5230\u4ed6\u4e00\u6837\u3002\u201d\u8bf4\u7f62\uff0c\u7b11\u5bb9\u987f\u655b\uff0c\u795e\u8272\u9eef\u7136\uff01 \u3000\u3000\u98de\u7ea2\u5dfe\u5728\u4e8c\u5341\u591a\u5e74\u524d\uff0c\u9a70\u540d\u5929\u5c71\u5357\u5317\uff0c\u662f\u8349\u539f\u4e0a\u8001\u5e7c\u7686\u77e5\u7684\u5973\u82f1\u96c4\uff0c\u548c\u6768\u4e91\u9aa2\u5e76\u9a7e\u5f00 \u9a71\uff0c\u4e00\u7537\u4e00\u5973\uff0c\u540c\u79f0\u585e\u5916\u5947\u4fa0\uff0c\uff08\u8be6\u89c1\u62d9\u8457\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\uff09\u4e24\u4eba\u66fe\u7ecf\u6709\u8fc7\u6781\u6df1\u539a\u7684\u4ea4\u60c5\u3002 \u540e\u6765\u56de\u7586\u5404\u65cf\u7684\u62b5\u6297\u88ab\u6e05\u5175\u5404\u4e2a\u51fb\u7834\uff0c\u6768\u4e91\u9aa2\u4e3a\u8ffd\u5bfb\u7eb3\u5170\u660e\u6167\uff0c\u98d8\u7136\u4ece\u585e\u5916\u6765\u5230\u6c5f\u5357\uff0c\u60e8\u6b7b \u5728\u94b1\u5858\u6c5f\u8fb9\u3002\u98de\u7ea2\u5dfe\u4e5f\u7a81\u7136\u5728\u8349\u539f\u4e0a\u5931\u8e2a\uff0c\u6ca1\u6709\u4eba\u77e5\u9053\u5979\u53bb\u4e86\u54ea\u91cc\u3002\u4e8c\u5341\u5e74\u6765\uff0c\u8349\u539f\u4e0a\u5230\u5904 \u6d41\u4f20\u7740\u5979\u7684\u82f1\u96c4\u4e8b\u8ff9\uff0c\u51cc\u672a\u98ce\u662f\u5728\u5979\u5931\u8e2a\u4e24\u5e74\u4e4b\u540e\u6765\u5230\u56de\u7586\u7684\uff0c\u65e9\u5c31\u542c\u5f97\u522b\u4eba\u8bf4\u8fc7\u5979\u7684\u540d\u5b57 \u4e86\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u53cc\u638c\u4e00\u62cd\uff0c\u51b2\u7740\u7eb3\u5170\u738b\u5983\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u597d\u5440\uff01\u201d\u7eb3\u5170\u738b\u5983\u53cc\u773c\u65e0\u795e\uff0c\u51c4\u7136\u8bf4\u9053\uff1a \u201c\u6768\u4e91\u9aa2\u5df2\u6b7b\u4e86\u5341\u516b\u5e74\u4e86\uff0c\u4f60\u8fd8\u8981\u600e\u6837\uff1f\u4f60\u6740\u4e86\u6211\u5427\uff0c\u6211\u4e5f\u4e0d\u613f\u6d3b\u4e86\uff01\u201d\u98de\u7ea2\u4eca\u6284\u8d77\u77ed\u5251\uff0c \u6012\u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f\u548c\u4f60\u4e89\u6c49\u5b50\u5417\uff1f\u5478\uff01\u6211\u5c31\u662f\u8981\u6740\u4f60\uff01\u201d\u51cc\u672a\u98ce\u62e6\u9053\uff1a\u201c\u738b\u5983\u4e0e\u6211\u4eec\u5e76\u65e0\u4ec7 \u6028\uff01\u201d\u98de\u7ea2\u5dfe\u4e0d\u7406\u51cc\u672a\u98ce\uff0c\u8feb\u5411\u738b\u5983\u53d1\u8bdd\u9053\uff1a\u201c\u6768\u4e91\u9aa2\u7684\u5973\u513f\u5462\uff1f\u62ff\u6765\u7ed9\u6211\uff01\u201d\u738b\u5983\u79c0\u7709\u4e00 \u6311\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5173\u4f60\u4ec0\u4e48\u4e8b\uff1f\u5e72\u561b\u8981\u4ea4\u7ed9\u4f60\uff01\u201d\u98de\u7ea2\u5dfe\u6012\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u5979\u7684\u6bcd\u4eb2\uff0c\u53ef\u662f \u4f60\u8fd9\u4e2a\u6bcd\u4eb2\u5374\u4e00\u70b9\u4e0d\u7406\u5973\u513f\u3002\u54fc\uff0c\u4f60\u5f53\u6211\u4e0d\u77e5\u9053\u5417\uff1f\u5979\u6740\u4e86\u4f60\u7684\u5b9d\u8d1d\u4e08\u592b\uff0c\u4f60\u5c31\u628a\u5979\u6253\u4e0b\u5929 \u7262\uff0c\u8fd8\u8981\u6162\u6162\u5730\u6298\u78e8\u5979\uff01\u201d\u7eb3\u5170\u738b\u5983\u653e\u58f0\u5927\u54ed\uff0c\u4e00\u5934\u649e\u5411\u5899\u58c1\u3002\u51cc\u672a\u98ce\u8f7b\u8f7b\u4e00\u62c9\uff0c\u628a\u5979\u626f \u5f00\uff0c\u5bf9\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u5973\u4fa0\uff0c\u4f60\u4ece\u54ea\u91cc\u542c\u6765\u7684\u8bdd\uff1f\u738b\u5983\u4e0d\u662f\u4e0d\u60f3\u6551\u5979\uff0c\u53ea\u662f\u6ca1\u6709\u529e\u6cd5\uff01\u201d\u98de\u7ea2 \u4e2d\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u5f53\u771f\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u90a3\u5973\u5a03\u5b50\u662f\u6211\u629a\u517b\u6210\u4eba\u7684\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u9a97\u4f60\u3002\u201d\u98de\u7ea2 \u4e2a\u77ed\u5251\u5f52\u9798\uff0c\u7f13\u7f13\u8d70\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u660e\u6167\uff0c\u662f\u6211\u602a\u9519\u4f60\u4e86\uff01\u201d\u884c\u4e86\u51e0\u6b65\uff0c\u5ffd\u7136\u505c\u4e0b\uff0c\u53eb \u9053\uff1a\u201c\u5916\u9762\u6709\u4eba\u6765\uff01\u201d\u51cc\u672a\u98ce\u8eab\u5f62\u4e00\u8d77\uff0c\u7a7f\u51fa\u7a97\u5916\u3002 \u3000\u3000\u539f\u6765\u5eb7\u7199\u88ab\u5192\u6d63\u83b2\u8981\u631f\uff0c\u8feb\u5f97\u653e\u5979\u8d70\u51fa\u5bab\u7981\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u8f9e\u522b\u592a\u540e\u4e4b\u540e\uff0c\u5373\u53ec\u96c6\u5927\u5185\u9ad8 \u624b\uff0c\u6311\u51fa\u516b\u540d\u4e00\u7b49\u5f85\u536b\uff0c\u53eb\u4ed6\u4eec\u5230\u9102\u738b\u5e9c\u53bb\u5c06\u5192\u6d63\u83b2\u6740\u6b7b\uff0c\u5272\u5934\u56de\u62a5\u3002\u8fd9\u516b\u540d\u4f8d\u536b\u5230\u4e86\u738b\u5983 \u697c\u4e0b\uff0c\u731b\u89c1\u56db\u5177\u6b66\u58eb\u5c38\u8eab\uff0c\u65ad\u5934\u6298\u8db3\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u542c\u5f97\u697c\u4e0a\u4e00\u58f0\u5927\u559d\uff0c \u4e00\u4e2a\u8499\u9762\u602a\u4eba\uff0c\u5df2\u4f3c\u6d41\u661f\u98de\u5760\uff0c\u51ed\u7a7a\u8dc3\u4e0b\u3002\u4eba\u672a\u5230\u5730\uff0c\u6697\u5668\u5148\u53d1\uff0c\u4e24\u9053\u4e4c\u91d1\u5149\u8292\uff0c\u75be\u5982\u7535 \u5c04\uff0c\u8fd1\u8eab\u5904\u4e24\u540d\u4f8d\u536b\uff0c\u7adf\u88ab\u5929\u5c71\u795e\u8292\uff0c\u5bf9\u80f8\u7a7f\u8fc7\u3002 \u3000\u3000\u4f17\u4f8d\u536b\u54d7\u7136\u5927\u547c\uff0c\u6025\u5fd9\u56f4\u4e0a\u3002\u697c\u4e0a\u9752\u5149\u4e00\u95ea\uff0c\u98de\u7ea2\u5dfe\u7d27\u8ddf\u7740\u53c8\u8dc3\u4e0b\u6765\uff0c\u77ed\u5251\u4e00\u6325\uff0c\u5c06\u8fc7 \u6765\u8fce\u622a\u7684\u4f8d\u536b\u624b\u8155\u65a9\u65ad\uff0c\u53eb\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u6211\u548c\u4f60\u6bd4\u8d5b\u6740\u654c\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u53eb\u9053\uff1a\u201c\u597d\uff01\u201d\u9752\u94a2\u5251\u4e00\u62db\u56de\u98ce\u626b\u67f3\uff0c\u628a\u56db\u9762\u653b\u6765\u7684\u5175\u5668\u6321\u5f00\uff0c\u5de6\u638c\u53cd\u624b\u4e00\u6325\uff0c \u5411\u6b3a\u8fd1\u8eab\u8fb9\u7684\u4e00\u540d\u654c\u4eba\u5288\u53bb\uff0c\u4e0d\u6599\u4e00\u80a1\u5927\u529b\u53cd\u649e\u8fc7\u6765\uff0c\u90a3\u4eba\u7adf\u7136\u5e76\u672a\u7ed9\u4ed6\u51fb\u5012\uff0c\u51cc\u672a\u98ce \u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u7ffb\u8eab\u8fdb\u5251\uff0c\u90a3\u4eba\u53eb\u9053\uff1a\u201c\u5206\u51fa\u4e09\u4e2a\u4eba\u53bb\u6321\u4f4f\u90a3\u8d3c\u5a46\u5a18\uff0c\u6211\u548c\u90d1\u94c1\u724c\u5bf9\u4ed8\u8fd9 \u53ae\u3002\u201d\u51cc\u672a\u98ce\u4e00\u5251\u523a\u53bb\uff0c\u72e0\u75be\u5f02\u5e38\uff0c\u90a3\u4eba\u7adf\u6beb\u4e0d\u9000\u8ba9\uff0c\u4e00\u679d\u94c1\u7b14\u201c\u6a2a\u67b6\u91d1\u6881\u201d\uff0c\u8fde\u5b88\u5e26\u653b\uff0c \u8fd8\u4e86\u4e00\u62db\u3002 \u3000\u3000\u8fd9\u4eba\u662f\u5185\u5ef7\u4f8d\u536b\u4e2d\u7b2c\u4e8c\u9ad8\u624b\uff0c\u540d\u53eb\u6210\u5929\u633a\uff0c\u5916\u53f7\u201c\u94c1\u7b14\u5224\u5b98\u201d\uff0c\u5584\u4f1a\u6253\u7a74\u3002\u695a\u662d\u5357\u5219\u662f \u7981\u536b\u519b\u4e2d\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u4e24\u4eba\u66fe\u5728\u5185\u5ef7\u6253\u4e86\u4e00\u65e5\u4e00\u591c\uff0c\u6bd4\u4e86\u5341\u9879\u529f\u592b\uff0c\u5bf9\u6bd4\u6253\u6210\u5e73\u624b\u3002\u4ed6\u521d\u4ee5 \u4e3a\u5c0f\u5c0f\u4e00\u540d\u5973\u8d3c\uff0c\u81ea\u5fc5\u624b\u5230\u64d2\u6765\uff0c\u5fc3\u671b\u8fd8\u6697\u7b11\u7687\u5e1d\u5c0f\u9898\u5927\u4f5c\u3002\u54ea\u6599\u5c1a\u672a\u89c1\u5973\u8d3c\u5f71\u5b50\uff0c\u4e24\u540d\u4e00 \u7b49\u5f85\u536b\u5c31\u7ed9\u5929\u5c71\u795e\u8292\u6253\u6b7b\uff01\u6210\u5929\u633a\u89c1\u4e86\u51cc\u672a\u98ce\u7684\u6697\u5668\uff0c\u8fd9\u624d\u77e5\u9053\u662f\u78b0\u89c1\u4e86\u6c5f\u6e56\u4e0a\u95fb\u540d\u4e27\u80c6\u7684 \u201c\u5929\u5c71\u795e\u8292\u201d\u51cc\u672a\u98ce\uff01 \u3000\u3000\u6210\u5929\u633a\u5fc3\u5934\u4e00\u9707\uff0c\u62fc\u547d\u67b6\u4f4f\uff0c\u9661\u89c1\u98de\u7ea2\u5dfe\u4e00\u8dc3\u800c\u4e0b\uff0c\u53ea\u4e00\u62db\u5c31\u628a\u4e00\u540d\u5927\u5185\u9ad8\u624b\u7684\u624b\u8155\u65a9 \u65ad\uff0c\u66f4\u662f\u53d1\u614c\u3002\u4f46\u4ed6\u6bd5\u7adf\u662f\u5927\u5185\u7b2c\u4e00\u9ad8\u624b\uff0c\u867d\u60ca\u4e0d\u4e71\u3002\u51cc\u672a\u98ce\u7684\u540d\u5934\u6fc0\u8d77\u4ed6\u7684\u597d\u80dc\u4e4b\u5fc3\uff0c\u4ed6 \u7684\u624b\u5e95\u4e5f\u662f\u62db\u62db\u72e0\u8fa3\uff0c\u4e0d\u80af\u9000\u8ba9\u3002\u51cc\u672a\u98ce\u8fde\u53d1\u4e09\u5251\uff0c\u672a\u66fe\u628a\u6210\u5929\u633a\u8feb\u9000\uff0c\u5fc3\u4e2d\u5927\u6012\uff0c\u5de6\u638c\u4e00 \u626c\uff0c\u5728\u654c\u4eba\u653b\u6765\u7684\u94c1\u7b14\u4e0a\u4e00\u62cd\uff0c\u628a\u94c1\u7b14\u62cd\u5f97\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u968f\u5373\u4e00\u62db\u201c\u9f99\u9876\u6458\u73e0\u201d\uff0c\u5251\u5149\u4e00\u95ea\uff0c \u76f4\u5954\u6210\u5929\u633a\u7684\u54bd\u5589\u523a\u53bb\u3002\u8fd9\u4e00\u62db\u72e0\u8fa3\u4e4b\u6781\uff0c\u6210\u5929\u633a\u6025\u5fd9\u6ed1\u6b65\u65c1\u7a9c\uff0c\u94c1\u7b14\u4e00\u62a1\uff0c\u5f53\u6210\u864e\u5c3e\u68cd \u7528\uff0c\u201c\u6a2a\u626b\u5343\u519b\u201d\uff0c\u683c\u5f00\u9752\u94a2\u5251\u3002\u51cc\u672a\u98ce\u624b\u8155\u4e00\u7ffb\uff0c\u5251\u5149\u5982\u767d\u7ec3\u822c\u4e00\u95ea\uff0c\u201c\u9f99\u5f52\u5927\u6d77\u201d\uff0c\u53c8 \u671d\u6210\u5929\u633a\u4e0b\u4e09\u8def\u523a\u5230\u3002\u8fd9\u4e24\u62db\u8fc5\u6377\u65e0\u4f26\uff0c\u662f\u5929\u5c71\u5251\u6cd5\u4e2d\u6700\u7cbe\u5999\u7684\u62db\u6570\uff0c\u9976\u662f\u6210\u5929\u633a\u5982\u4f55\u4e86 \u5f97\uff0c\u4e5f\u7ed9\u8feb\u5f97\u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u90a3\u59d3\u90d1\u7684\u536b\u58eb\u4f7f\u4e24\u9762\u94c1\u813e\uff0c\u5728\u5bab\u4e2d\u4e5f\u662f\u4e94\u540d\u5185\u7684\u9ad8\u624b\uff0c\u6210\u5929\u633a\u7559\u4e0b\u4ed6\u548c\u81ea\u5df1\u8054\u624b\uff0c\u539f\u5c31 \u662f\u60f3\u85c9\u4ed6\u7684\u94c1\u724c\uff0c\u6765\u514b\u51cc\u672a\u98ce\u7684\u5b9d\u5251\uff0c\u60f3\u4ee5\u201c\u4e00\u529b\u964d\u5341\u4f1a\u201d\uff0c\u4f7f\u51cc\u672a\u98ce\u96be\u4e8e\u517c\u987e\u3002\u4e0d\u6599\u51cc\u672a \u98ce\u8eab\u6cd5\u6b65\u6cd5\uff0c\u53d8\u5e7b\u65e0\u7a77\uff0c\u6839\u672c\u4e0d\u7406\u94c1\u724c\u7684\u5939\u51fb\uff0c\u53ea\u72e0\u72e0\u8ffd\u6740\u6210\u5929\u633a\uff0c\u90a3\u540d\u536b\u58eb\uff0c\u94c1\u724c\u731b\u7838\uff0c \u597d\u51e0\u6b21\u773c\u770b\u8981\u7838\u4e2d\u654c\u4eba\uff0c\u53ea\u662f\u5bf9\u65b9\u4e0d\u77e5\u7528\u4ec0\u4e48\u8eab\u6cd5\uff0c\u968f\u4fbf\u4e00\u95ea\uff0c\u4fbf\u95ea\u5f00\u4e86\uff0c\u7adf\u4f3c\u80cc\u540e\u957f\u7740\u773c \u775b\u4e00\u6837\uff0c\u624b\u4e2d\u5251\u4ecd\u7136\u7d27\u7d27\u8feb\u7740\u6210\u5927\u633a\u3002 \u3000\u3000\u6210\u5929\u633a\u94c1\u7b14\u659c\u98de\uff0c\u53c8\u6321\u4e86\u5341\u4f59\u62db\uff0c\u9669\u8c61\u73af\u751f\uff0c\u6025\u5fd9\u558a\u9053\uff1a\u201c\u90d1\u94c1\u724c\uff0c\u4f60\u8fc7\u6765\uff0c\u6b63\u9762\uff01\u201d \u4ed6\u662f\u53ea\u6c42\u4e24\u4eba\u5408\u5b88\uff0c\u4e0d\u6c42\u5939\u653b\u4e86\u3002\u6210\u5929\u633a\u548c\u90d1\u94c1\u724c\u5e76\u80a9\u4e00\u7ad9\uff0c\u5c55\u5f00\u94c1\u7b14\u70b9\u7a74\u7684\u62db\u6570\uff0c\u548c\u51cc\u672a \u98ce\u518d\u5ea6\u6076\u6597\uff0c\u8fd9\u4e00\u6765\u5f62\u52bf\u679c\u7136\u597d\u4e86\u8bb8\u591a\uff01\u51cc\u672a\u98ce\u5251\u62db\u867d\u8fc5\u6377\u65e0\u4f26\uff0c\u4f46\u6210\u5929\u633a\u6709\u4e86\u5e2e\u624b\uff0c\u4f0a\u5982 \u8eab\u8fb9\u6dfb\u4e86\u4e00\u9762\u6d3b\u52a8\u7684\u76fe\u724c\uff0c\u94c1\u7b14\u70b9\u523a\u6572\u51fb\uff0c\u5c45\u7136\u548c\u51cc\u672a\u98ce\u4e92\u6709\u653b\u5b88\u3002 \u3000\u3000\u6210\u5929\u633a\u8eab\u5f62\u8f7b\u5feb\uff0c\u62db\u6570\u5706\u719f\uff0c\u66f4\u52a0\u4e0a\u90a3\u540d\u536b\u58eb\uff0c\u53cc\u724c\u8fd0\u7528\u5f97\u970d\u970d\u751f\u98ce\uff0c\u51cc\u672a\u98ce\u5251\u6cd5\u4e00\u62db \u7d27\u4f3c\u4e00\u62db\uff0c\u5140\u662f\u627e\u4e0d\u5230\u5bf9\u65b9\u7834\u7efd\uff0c\u8033\u542c\u5f97\u8fdc\u5904\u547c\u559d\u58f0\uff0c\u811a\u6b65\u58f0\uff0c\u54cd\u6210\u4e00\u7247\uff0c\u60f3\u662f\u738b\u5e9c\u4e2d\u7684\u6b66 \u58eb\uff0c\u53d1\u73b0\u8fd9\u91cc\u6076\u6218\uff0c\u7ea0\u96c6\u540c\u4f34\uff0c\u8fdb\u6765\u536b\u62a4\u738b\u5983\uff0c\u51cc\u672a\u98ce\u5fc3\u4e2d\u6025\u8e81\uff0c\u5251\u8d70\u7075\u86c7\uff0c\u95ea\u7535\u822c\u75be\u523a\u4e24 \u5251\uff0c\u628a\u6210\u5929\u633a\u518d\u8feb\u9000\u51e0\u6b65\uff0c\u628a\u5168\u8eab\u529f\u529b\u8fd0\u5728\u5de6\u638c\u4e4b\u4e0a\uff0c\u90d1\u94c1\u724c\u53cc\u724c\u7ffb\u98de\uff0c\u9f50\u9f50\u6253\u5230\u3002\u51cc\u672a\u98ce \u5927\u559d\u4e00\u58f0\uff0c\u4e00\u638c\u51fb\u53bb\uff0c\u4e24\u9762\u94c1\u724c\u90fd\u7ed9\u9707\u4e0a\u534a\u7a7a\uff0c\u51cc\u672a\u98ce\u6b3a\u8eab\u75be\u8fdb\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u628a\u90d1\u94c1\u724c\u7684\u5934 \u9885\u6253\u5f97\u7c89\u788e\u3002\u53ea\u542c\u5f97\u98de\u7ea2\u5dfe\u957f\u7b11\u53eb\u9053\uff1a\u201c\u51cc\u672a\u98ce\uff0c\u4f60\u624d\u6253\u6b7b\u4e00\u4e2a\u5417\uff1f\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u5f53\u5e74\u5a01\u9707\u585e\u5916\uff0c\u9041\u8ff9\u4e8c\u5341\u5e74\uff0c\u4ecd\u662f\u82f1\u6c14\u8feb\u4eba\uff0c\u4e09\u540d\u4e00\u7b49\u5f85\u536b\u6b3a\u5979\u662f\u4e2a\u5973\u6d41\uff0c\u4e00\u5f00 \u9996\u5c31\u5206\u4e09\u9762\u51b2\u53bb\u3002\u98de\u7ea2\u5dfe\u5140\u7acb\u5982\u5c71\uff0c\u5f85\u5230\u8fd1\u65f6\uff0c\u7a81\u7136\u4e00\u6296\u957f\u97ad\uff0c\u4e00\u540d\u4f8d\u536b\u7adf\u7ed9\u5377\u4e86\u8d77\u6765\uff0c\u98de \u7ea2\u5dfe\u5de6\u624b\u4e00\u6325\uff0c\u628a\u90a3\u540d\u4f8d\u536b\u6454\u51fa\u51e0\u4e08\u4e4b\u5916\uff0c\u649e\u7740\u77f3\u5757\uff0c\u8111\u6d46\u8ff8\u6d41\uff01 \u3000\u3000\u4f59\u4e0b\u7684\u4e24\u4eba\u867d\u7136\u662f\u4e00\u7b49\u5f85\u536b\uff0c\u529f\u529b\u5374\u6bd4\u6210\u5929\u633a\u5dee\u5f97\u591a\uff0c\u90a3\u91cc\u6321\u5f97\u4f4f\u98de\u7ea2\u5dfe\u8fd9\u79cd\u5de6\u97ad\u53f3 \u5251\uff0c\u7cbe\u5999\u7e41\u590d\u7684\u62db\u6570\u3002\u9163\u6597\u58f0\u4e2d\uff0c\u98de\u7ea2\u5dfe\u77ed\u5251\u4e00\u65cb\uff0c\u4e00\u540d\u4f7f\u9b3c\u5934\u5200\u7684\u4f8d\u536b\uff0c\u5175\u5203\u5df2\u7ed9\u51fb\u98de\uff0c \u98de\u7ea2\u5dfe\u957f\u97ad\u4e00\u62e6\uff0c\u6321\u7740\u4ed6\u7684\u540c\u4f34\uff0c\u77ed\u5251\u6a2a\u626b\uff0c\u5bd2\u5149\u95ea\u5904\uff0c\u4e00\u9897\u5934\u9885\u5df2\u7ed9\u5207\u4e0b\uff0c\u98de\u7ea2\u5dfe\u53eb\u9053\uff1a \u201c\u8fd9\u662f\u7b2c\u4e8c\u4e2a\uff01\u201d\u7b2c\u4e8c\u540d\u536b\u58eb\u9b42\u98de\u9b44\u6563\uff0c\u8f6c\u8eab\u4fbf\u9003\uff0c\u98de\u7ea2\u5dfe\u4e00\u97ad\u6253\u51fa\uff0c\u53c8\u628a\u4ed6\u5377\u4e86\u8fc7\u6765\uff0c\u77ed \u5251\u4e00\u52d2\uff0c\u53c8\u5c06\u4e00\u9897\u5934\u9885\u5272\u4e0b\u6765\uff0c\u53eb\u9053\uff1a\u201c\u7b2c\u4e09\u4e2a\u4e5f\u5f00\u9500\u4e86\uff01\u201d\u77ed\u5251\u8fc5\u90a3\u5f52\u9798\uff0c\u957f\u97ad\u6325\u821e\uff0c\u7eb5 \u58f0\u957f\u7b11\uff0c\u8fd9\u65f6\u51cc\u672a\u98ce\u624d\u51fb\u6bd9\u90d1\u94c1\u724c\u3002 \u3000\u3000\u51cc\u672a\u98ce\u89c1\u98de\u7ea2\u5dfe\u624b\u633d\u4e24\u9897\u5934\u9885\uff0c\u5982\u98de\u63a0\u81f3\uff0c\u7b11\u7740\u62db\u547c\u9053\uff1a\u201c\u5973\u4fa0\u8eab\u624b\uff0c\u679c\u662f\u4e0d\u51e1\uff0c\u4f60\u8d62 \u4e86\uff01\u201d\u6210\u5929\u633a\u8d81\u4ed6\u7a0d\u7f13\uff0c\u8654\u70b9\u4e00\u7b14\uff0c\u4e00\u9e64\u51b2\u5929\uff0c\u817e\u8eab\u4fbf\u8d70\u3002\u98de\u7ea2\u5dfe\u5341\u5206\u597d\u80dc\uff0c\u8eab\u5f62\u4e00\u63a0\uff0c\u957f \u97ad\u75be\u5377\u3002\u6210\u5929\u633a\u5728\u534a\u7a7a\u6253\u4e2a\u7b4b\u6597\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u75be\u51b2\u4e0b\u6765\uff0c\u5de6\u624b\u63e1\u7740\u97ad\u68a2\uff0c\u98de\u7ea2\u5dfe\u7adf\u6ca1\u5c06\u4ed6\u5377 \u7740\u3002\u6210\u5929\u633a\u501f\u529b\u4e00\u7ffb\uff0c\u7ffb\u5230\u98de\u7ea2\u5dfe\u8ddf\u524d\uff0c\u94c1\u7b14\u4e00\u626c\uff0c\u7535\u5149\u77f3\u706b\u822c\u75be\u70b9\u98de\u7ea2\u5dfe\u201c\u80a9\u4e95\u7a74\u201d\u3002\u98de \u7ea2\u5dfe\u4e00\u811a\u8e22\u53bb\uff0c\u6210\u5929\u633a\u624b\u8155\u4e00\u504f\uff0c\u7ed9\u5251\u5c16\u6302\u7740\u4e00\u70b9\uff0c\u76ae\u7834\u8840\u6d41\uff0c\u800c\u98de\u7ea2\u5dfe\u4e5f\u89c9\u94c1\u7b14\u631f\u98ce\uff0c\u5939 \u8033\u800c\u8fc7\uff0c\u8fde\u5fd9\u6a2a\u8dc3\u4e24\u6b65\uff0c\u6210\u5929\u633a\u5df2\u63a0\u8fc7\u4e00\u5ea7\u5047\u5c71\uff0c\u548c\u738b\u5e9c\u4e2d\u5faa\u58f0\u8d76\u6765\u7684\u6b66\u58eb\u4f1a\u5408\u4e86\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u8fd8\u5f85\u8ffd\u51fb\uff0c\u51cc\u672a\u98ce\u559d\u58f0\uff1a\u201c\u8d70\uff01\u201d\u5192\u6d63\u83b2\u65e9\u5df2\u8dc3\u4e0b\uff0c\u5728\u65c1\u8fb9\u89c2\u6218\uff0c\u8fd9\u65f6\uff0c\u638f\u51fa\u4e00 \u628a\u593a\u547d\u795e\u7802\uff0c\u5bf9\u7740\u8d76\u6765\u7684\u738b\u5e9c\u6b66\u58eb\uff0c\u8fce\u5934\u4e00\u6d12\uff0c\u51cc\u672a\u98ce\u8fde\u53d1\u4e09\u652f\u5929\u5c71\u795e\u8292\uff0c\u679d\u679d\u90fd\u662f\u7a7f\u5589\u800c \u8fc7\uff0c\u5c04\u6bd9\u4e09\u540d\u6b66\u58eb\u3002\u6b66\u58eb\u4eec\u53d1\u4e00\u58f0\u558a\uff0c\u56db\u4e0b\u5206\u5f00\uff0c\u98de\u7ea2\u5dfe\u63b7\u51fa\u4eba\u5934\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u4e0e\u51cc\u672a\u98ce\u5192 \u6d63\u83b2\u98de\u8eab\u8d70\u51fa\u738b\u5e9c\uff0c \u3000\u3000\u5230\u4e86\u50fb\u9759\u4e4b\u5904\uff0c\u98de\u7ea2\u5dfe\u9661\u7684\u505c\u4e0b\u811a\u6b65\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u540e\u4f1a\u6709\u671f\uff01\u201d\u51cc\u672a\u98ce\u6025\u5fd9 \u53eb\u9053\uff1a\u201c\u8bf7\u7559\u6b65\uff01\u201d\u98de\u7ea2\u5dfe\u626d\u5934\u95ee\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u524d\u8f88\u4e3a\u6551\u5927\u4fa0\u9057 \u5b64\uff0c\u4e0d\u8fdc\u4e07\u91cc\u800c\u6765\uff0c\u4f55\u4e0d\u4e0e\u6211\u4eec\u4e00\u8def\uff1f\u201d\u98de\u7ea2\u5dfe\u9762\u8272\u4e00\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u6768\u4e91\u9aa2\u5e08\u5f1f\uff0c\u4f55\u4ee5 \u660e\u77e5\u6545\u95ee\uff1f\u4f60\u6551\u4f60\u7684\uff0c\u6211\u6551\u6211\u7684\uff0c\u4e0d\u5fc5\u591a\u8a00\uff01\u201d\u4e00\u98d8\u8eab\uff0c\u75be\u4f3c\u65cb\u98ce\uff0c\u970e\u5ffd\u4e0d\u89c1\u4eba\u5f71\uff01\u51cc\u672a\u98ce \u7ed9\u5979\u6ca1\u5934\u6ca1\u8111\u8bf4\u4e86\u4e00\u987f\uff0c\u83ab\u540d\u5176\u5999\u3002\u8981\u77e5\u51cc\u672a\u98ce\u867d\u662f\u6768\u4e91\u9aa2\u5e08\u5f1f\uff0c\u53ef\u662f\u4e24\u4eba\u76f8\u89c1\u4e4b\u65e5\uff0c\u6b63\u662f \u6768\u4e91\u9aa2\u6bd9\u547d\u4e4b\u65f6\u3002\u6768\u4e91\u9aa2\u4e0e\u98de\u7ea2\u5dfe\u4e4b\u95f4\u7684\u6069\u6069\u6028\u6028\uff0c\u51cc\u672a\u98ce\u5982\u4f55\u77e5\u9053\uff1f \u3000\u3000\u51cc\u672a\u98ce\u53f9\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\u7684\u6b66\u529f\u771f\u662f\u51fa\u795e\u5165\u5316\uff0c\u5dfe\u5e3c\u65e0\u53cc\uff0c\u53ea\u662f\u813e\u6c14\u5982\u6041\u822c\u602a\u50fb\uff01\u201d\u5192\u6d63 \u83b2\u6839\u672c\u4e0d\u77e5\u98de\u7ea2\u5dfe\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u4e0d\u6562\u7f6e\u7b54\u3002\u51cc\u672a\u98ce\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u7684\u6731\u679c\u91d1\u7b26\u5462\uff1f\u62ff\u6765\u7ed9 \u6211\uff01\u201d\u5192\u6d63\u83b2\u6025\u5fd9\u9001\u4e0a\uff0c\u51cc\u672a\u98ce\u85cf\u5165\u6000\u4e2d\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u4eca\u665a\u6211\u8981\u591c\u63a2\u5929\u7262\uff01\u201d\u5192\u6d63\u83b2\u9053\uff1a \u201c\u51cc\u5927\u4fa0\u8981\u4e0d\u8981\u4eba\u63a5\u5e94\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4e0d\u5fc5\uff0c\u4eba\u591a\u4e86\u53cd\u800c\u4e0d\u597d\uff01\u201d\u4e24\u4eba\u8c08\u8d77\u522b\u540e\u60c5\u51b5\uff0c\u59cb \u77e5\u674e\u6765\u4ea8\u662f\u56e0\u4e3a\u6842\u5192\u4e8c\u4eba\u5165\u4eac\u6570\u6708\uff0c\u6beb\u65e0\u6d88\u606f\uff0c\u8fd9\u624d\u8bf7\u51cc\u672a\u98ce\u5165\u4eac\u4e00\u770b\u7684\u3002\u51cc\u672a\u98ce\u4e3a\u4e86\u540d\u5934 \u592a\u5927\uff0c\u9762\u6709\u5200\u75a4\uff0c\u6240\u4ee5\u603b\u662f\u663c\u4f0f\u591c\u884c\uff0c\u4e00\u8def\u4e0a\u63a2\u542c\u4e0d\u51fa\u4ec0\u4e48\u6d88\u606f\u3002\u5230\u4e86\u4eac\u57ce\uff0c\u8fd9\u624d\u77e5\u6613\u5170\u73e0 \u5df2\u523a\u6740\u591a\u94ce\uff0c\u88ab\u6253\u4e0b\u5929\u7262\u3002 \u3000\u3000\u6613\u5170\u73e0\u662f\u51cc\u672a\u98ce\u629a\u517b\u5927\u7684\uff0c\u60c5\u5982\u5144\u59b9\uff0c\u53c8\u5982\u7236\u5973\uff0c\u51cc\u672a\u98ce\u77e5\u9053\u4e4b\u540e\uff0c\u72b9\u5982\u4e07\u7bad\u94bb\u5fc3\uff0c\u5341 \u5206\u96be\u8fc7\u3002\u5fc3\u60f3\u5e08\u5144\u60e8\u6b7b\uff0c\u53ea\u6b64\u9057\u5b64\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u5979\u547d\u4e27\u4eac\u534e\uff0c\u88c2\u5c38\u897f\u5e02\u3002\u6613\u5170\u73e0\u548c \u7eb3\u5170\u738b\u5983\u7684\u5173\u7cfb\uff0c\u51cc\u672a\u98ce\u5f53\u7136\u77e5\u9053\u3002\u56e0\u6b64\u4ed6\u628a\u5bfb\u627e\u6842\u3001\u5192\u4e8c\u4eba\u7684\u4e8b\uff0c\u6682\u6401\u5728\u4e00\u8fb9\uff0c\u5148\u5230\u9102\u738b \u5e9c\u8e29\u67e5\uff0c\u4ed7\u7740\u8f7b\u529f\u8d85\u5353\uff0c\u5c45\u7136\u7ed9\u4ed6\u95ef\u5230\u4e86\u738b\u5983\u7684\u5367\u697c\uff0c\u6070\u597d\u78b0\u5230\u4e86\u5192\u6d63\u83b2\u548c\u98de\u7ea2\u5dfe\u3002 \u3000\u3000\u5192\u6d63\u83b2\u95ee\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\u662f\u600e\u6837\u7684\u4eba\uff1f\u770b\u6765\u5979\u5bf9\u6613\u5170\u73e0\u7684\u5173\u5fc3\uff0c\u4e0d\u5728\u4f60\u6211\u4e4b\u4e0b\u3002\u201d\u51cc\u672a\u98ce \u53f9\u9053\uff1a\u201c\u8fd9\u662f\u60c5\u5b7d\uff01\u6211\u4e5f\u4e0d\u5f88\u6e05\u695a\u3002\u53ea\u662f\u5728\u56de\u7586\u65f6\uff0c\u542c\u5f97\u8349\u539f\u4e0a\u7267\u6c11\u7684\u8c08\u8bba\uff0c\u7ea6\u7565\u77e5\u9053\u4e00 \u4e8c\u3002\u98de\u7ea2\u5dfe\u539f\u53eb\u54c8\u739b\u96c5\uff0c\u4e8c\u5341\u591a\u5e74\u524d\uff0c\u540d\u9707\u5357\u7586\uff0c\u662f\u7f57\u5e03\u65cf\u5510\u52aa\u8001\u82f1\u96c4\u7684\u72ec\u751f\u5973\u3002\u542c\u8bf4\u695a\u662d \u5357\u521d\u4e0b\u5929\u5c71\u65f6\uff0c\u5c31\u66fe\u5728\u5510\u52aa\u8001\u82f1\u96c4\u5e10\u4e0b\uff0c\u5e2e\u52a9\u4ed6\u4eec\u62b5\u6297\u8fc7\u6e05\u5175\u7684\uff0c\u53ea\u662f\u6ca1\u591a\u4e45\u5c31\u80cc\u53db\u4e86\u5510 \u52aa\uff0c\u6295\u964d\u4e86\u6e05\u519b\u3002\u201d\u5192\u70f7\u83b2\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u90a3\u65f6\u6211\u7684\u5927\u5e08\u5144\u6768\u4e91\u9aa2\u5728\u5317 \u7586\u9f0e\u9f0e\u6709\u540d\uff0c\u4ed6\u5e2e\u52a9\u54c8\u8428\u514b\u4eba\u6253\u4ed7\uff0c\u540e\u6765\u8fd8\u6210\u4e86\u54c8\u8428\u514b\u519b\u4e2d\u7684\u7075\u9b42\u3002\u540e\u6765\u54c8\u8428\u514b\u5728\u5317\u7586\u5403\u4e86 \u8d25\u4ed7\uff0c\u6768\u5e08\u5144\u6a2a\u8d8a\u5854\u514b\u62c9\u9a6c\u5e72\u5927\u6c99\u6f20\uff0c\u6765\u5230\u5357\u7586\uff0c\u548c\u98de\u7ea2\u5dfe\u8054\u5408\u8d77\u6765\uff0c\u4e00\u65f6\u58f0\u52bf\u5927\u76db\u3002\u201d\u5192 \u6d63\u83b2\u542c\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4eba\u540c\u6297\u6e05\u5175\uff0c\u53c8\u90fd\u662f\u4eba\u4e2d\u9f99\u51e4\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u7ed3\u6210\u8c6a\u4fa0 \u59fb\u7f18\uff0c\u795e\u4ed9\u7737\u5c5e\uff1f\u201d\u51cc\u672a\u98ce\u53f9\u9053\uff1a\u201c\u6d63\u83b2\uff0c\u5e76\u4e0d\u662f\u4eba\u4eba\u90fd\u80fd\u50cf\u4f60\u548c\u4ef2\u660e\u90a3\u6837\u7684\uff0c\u60c5\u4e4b\u4e00\u5b57\uff0c \u5fae\u5999\u4e07\u5206\uff0c\u4e00\u65e6\u9519\u8fc7\u673a\u7f18\uff0c\u4fbf\u53ea\u6709\u7ec8\u8eab\u9057\u61be\u3002\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u7ed3\u6210\u7737\u5c5e\uff0c\u6211\u662f\u6beb\u4e0d\u77e5\u60c5\u3002\u53ea \u662f\u542c\u8bf4\uff0c\u98de\u7ea2\u5dfe\u5728\u9047\u5230\u5927\u5e08\u5144\u4e4b\u524d\uff0c\u66fe\u7231\u8fc7\u4e00\u4e2a\u540d\u53eb\u62bc\u4e0d\u5362\u7684\u8349\u539f\u6b4c\u624b\u3002\u62bc\u4e0d\u5362\u7684\u6b4c\u58f0\u975e\u5e38 \u7f8e\u5999\uff0c\u53ef\u4ee5\u6253\u52a8\u4efb\u4f55\u5c11\u5973\u7684\u5fc3\uff0c\u4f46\u4e0d\u5e78\u7684\u662f\uff0c\u8fd9\u6837\u7684\u6b4c\u624b\uff0c\u5374\u6709\u4e00\u4e2a\u5351\u8d31\u7684\u7075\u9b42\uff0c\u4ed6\u52fe\u7ed3\u6e05 \u5175\uff0c\u5bb3\u6b7b\u4e86\u5510\u52aa\u8001\u82f1\u96c4\u3002\u540e\u6765\u98de\u7ea2\u5dfe\u4eb2\u81ea\u628a\u4ed6\u64d2\u6765\uff0c\u6316\u51fa\u4ed6\u7684\u5fc3\u809d\u796d\u5960\u4ea1\u7236\uff0c\u90a3\u4e00\u5e55\u2018\u8349\u539f \u591c\u796d\u2019\uff0c\u4e8c\u5341\u5e74\u6765\u7ed9\u7267\u6c11\u4eec\u7f16\u6210\u4e86\u8bb8\u591a\u6b4c\u66f2\uff0c\u5728\u8349\u539f\u4e0a\u6d41\u4f20\uff01\u201d\u5192\u6d63\u83b2\u53f9\u4e86\u53e3\u6c14\uff0c\u95ee\u9053\uff1a \u201c\u636e\u4f60\u731c\u60f3\uff0c\u662f\u4e0d\u662f\u6768\u5927\u4fa0\u5acc\u5979\u7231\u8fc7\u62bc\u4e0d\u5362\u5462\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u4f1a\uff0c\u53ef\u80fd\u662f\u5927\u5e08\u5144\u4e4b \u60c5\u53e6\u6709\u6240\u949f\uff0c\u5728\u78b0\u5230\u98de\u7ea2\u5dfe\u4e4b\u524d\u5df2\u7231\u4e0a\u73b0\u5728\u7684\u9102\u738b\u5983\u4e86\u3002\u201d\u5192\u6d63\u83b2\u6447\u5934\u53f9\u606f\uff0c\u5ffd\u89c1\u51cc\u672a\u98ce\u53cc \u76ee\u4f3c\u6709\u6cea\u5149\uff0c\u609a\u7136\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u96be\u9053\u51cc\u672a\u98ce\u4e5f\u6709\u4ec0\u4e48\u4f24\u5fc3\u4e4b\u4e8b\uff1f\u5f53\u4e0b\u4e0d\u6562\u591a\u95ee\u3002 \u3000\u3000\u51cc\u672a\u98ce\u8981\u8fc7\u4e86\u6731\u679c\u91d1\u7b26\uff0c\u95ee\u6e05\u695a\u4e86\u5192\u6d63\u83b2\u73b0\u5728\u7684\u5730\u5740\u3002\u77e5\u9053\u6842\u4ef2\u660e\u5f20\u534e\u662d\u7b49\u4e00\u73ed\u4eba\u90fd\u5728 \u201c\u8e51\u4e91\u5251\u201d\u77f3\u8001\u9556\u5934\u5bb6\u91cc\uff0c\u5f88\u662f\u9ad8\u5174\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eca\u665a\u591c\u63a2\u5929\u7262\uff0c\u82e5\u7136\u5f97\u624b\uff0c\u7acb\u523b\u5e26\u6613\u5170\u73e0 \u6765\u627e\u4f60\u4eec\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u5728\u601d\u5ff5\u7740\u6613\u5170\u73e0\uff0c\u6613\u5170\u73e0\u5728\u5929\u7262\u91cc\u4e5f\u601d\u5ff5\u7740\u51cc\u672a\u98ce\u3002 \u3000\u3000\u5929\u7262\u91cc\u9ed1\u6c89\u6c89\u7684\uff0c\u53ea\u6709\u5899\u89d2\u4e24\u76cf\u8c46\u5927\u7684\u957f\u660e\u706f\u53d1\u7740\u9eef\u6de1\u7684\u5fae\u5149\u3002\u592a\u9633\u7167\u4e0d\u8fdb\u6765\uff0c\u6708\u4eae\u7167 \u4e0d\u8fdb\u6765\uff0c\u661f\u5149\u4e5f\u900f\u4e0d\u8fc7\u90a3\u5bc6\u4e0d\u901a\u98ce\u7684\u94c1\u7a97\uff0c\u6613\u5170\u73e0\u5173\u5728\u5929\u7262\u91cc\uff0c\u604d\u604d\u60da\u60da\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11 \u4e2a\u767d\u5929\u548c\u9ed1\u591c\u3002\u5979\u611f\u5230\u5f02\u6837\u7684\u5b81\u9759\uff0c\u201c\u6211\u662f\u6211\u7236\u4eb2\u7684\u5973\u513f\u554a\uff01\u201d\u5979\u89c9\u5f97\u5979\u5e76\u6ca1\u6709\u8fb1\u6ca1\u5979\u7684\u7236 \u4eb2\uff0c\u7236\u4eb2\u7684\u8840\u4e66\u5728\u5979\u5fc3\u7075\u4e0a\u6240\u9020\u6210\u7684\u91cd\u538b\uff0c\u662f\u5df2\u7ecf\u5b8c\u5168\u6d88\u5931\u4e86\u3002\u5979\u60f3\u821e\u8e48\uff0c\u5979\u60f3\u5531\u6b4c\uff0c\u5979\u60f3 \u9762\u5bf9\u7740\u9690\u5728\u9ed1\u6697\u4e2d\u7684\u6b7b\u795e\u8bf4\u9053\uff1a\u201c\u6765\u5427\uff0c\u6211\u5e76\u4e0d\u6015\u4f60\uff01\u201d\u4e00 \u3000\u3000\u5979\u771f\u7684\u4e00\u70b9\u4e0d\u6015\u6b7b\u5417\uff1f\u53ef\u80fd\u662f\u7684\uff0c\u4f46\u5979\u5728\u6f2b\u6f2b\u7684\u957f\u591c\u91cc\uff0c\u6709\u65f6\u5374\u4e5f\u4e0d\u7981\u98a4\u6144\u8d77\u6765\uff0c\u5979\u4e0d \u662f\u6015\u6b7b\uff0c\u800c\u662f\u60cb\u60dc\u81ea\u5df1\u9752\u6625\u7684\u751f\u547d\uff0c\u8fd8\u53ea\u6709\u4e8c\u5341\u5c81\u7684\u5c11\u5973\u54ea\uff01\u5c31\u8981\u548c\u4eb2\u4eba\u4eec\u6c38\u522b\u4e86\uff01\u5979\u6ca1\u6709 \u4eb2\u4eba\uff0c\u4f46\u5979\u5374\u6000\u5ff5\u5979\u7684\u201c\u4eb2\u4eba\u201d\u3002\u738b\u5983\u662f\u5979\u7684\u6bcd\u4eb2\uff0c\u5728\u957f\u8fdc\u7684\u5c81\u6708\u91cc\uff0c\u5979\u5bf9\u5979\u7684\u611f\u60c5\u4ea4\u7ec7\u7740 \u7231\u548c\u6068\uff0c\u5728\u5979\u8f6f\u5f31\u7684\u800c\u53c8\u575a\u5f3a\u7684\u5c11\u5973\u7684\u5fc3\u4e2d\uff0c\u5979\u5e76\u6ca1\u6709\u628a\u5979\u7684\u6bcd\u4eb2\u5f53\u6210\u201c\u4eb2\u4eba\u201d\u770b\u5f85\uff0c\u7136\u800c \u6b64\u9645\uff0c\u5728\u81ea\u5df1\u751f\u547d\u5373\u5c06\u7ed3\u675f\u7684\u524d\u5915\uff0c\u5979\u60f3\u8d77\u5979\u7684\u6bcd\u4eb2\u6765\u4e86\uff01\u5979\u6709\u4e00\u4e2a\u6b32\u671b\uff0c\u8981\u628a\u81ea\u5df1\u79ef\u538b\u4e86 \u591a\u5e74\u7684\u773c\u6cea\uff0c\u5728\u5979\u6bcd\u4eb2\u7684\u9762\u524d\u75db\u75db\u5feb\u5feb\u5730\u6d41\u51fa\u6765\u3002\u5bf9\u5979\u8bc9\u8bf4\u5979\u662f\u600e\u6837\u7684\u7231\u5979\u53c8\u662f\u600e\u6837\u7684\u6068 \u5979\uff01 \u3000\u3000\u7b2c\u4e8c\u4e2a\u201c\u4eb2\u4eba\u201d\uff0c\u5979\u6df1\u6df1\u6000\u5ff5\u7740\u7684\u662f\u51cc\u672a\u98ce\uff0c\u51cc\u672a\u98ce\u5e76\u4e0d\u662f\u5979\u7684\u4eb2\u4eba\uff0c\u4f46\u5374\u8981\u6bd4\u4ec0\u4e48\u4eb2 \u4eba\u90fd\u8fd8\u8981\u4eb2\uff0c\u5979\u60f3\u8d77\u51cc\u672a\u98ce\u5728\u5979\u521a\u521a\u5b66\u4f1a\u8bb2\u8bdd\u7684\u65f6\u5019\uff0c\u5c31\u628a\u5979\u4ece\u6c5f\u5357\u5e26\u5230\u6f20\u5317\uff0c\u5e26\u5230\u5bd2\u5916\uff0c \u62b1\u4e0a\u5929\u5c71\u3002\u201c\u6211\u4e0d\u77e5\u7ed9\u4ed6\u6dfb\u4e86\u591a\u5c11\u9ebb\u70e6\uff01\u201d\u8fd9\u79cd\u60c5\u4efd\uff0c\u7b80\u76f4\u662f\u8d85\u8fc7\u4e00\u822c\u7236\u5973\u4e4b\u4e0a\u7684\uff0c\u201c\u6709\u54ea \u4e00\u4e2a\u7236\u4eb2\u4e3a\u5979\u7684\u5973\u513f\u5403\u8fc7\u8fd9\u4e48\u591a\u82e6\u5462\uff1f\u201d\u5979\u60f3\u3002\u5979\u6068\u4e0d\u5f97\u80fd\u518d\u89c1\u5230\u51cc\u672a\u98ce\uff0c\u62b1\u7740\u4ed6\u7684\u817f\uff0c\u53eb \u4ed6\u4e00\u58f0\u201c\u7238\u7238\uff01\u201d\u201c\u4f46\u51cc\u5927\u4fa0\u8fd8\u8fd9\u6837\u5e74\u9752\uff0c\u6bd4\u6211\u53ea\u5927\u5341\u591a\u5e74\uff0c\u53eb\u4ed6\u505a\u7238\u7238\uff0c\u4ed6\u9ad8\u5174\u5417\uff1f\u201d\u6613 \u5170\u73e0\u4e1c\u60f3\u897f\u60f3\uff0c\u65f6\u5e38\u5ffd\u7136\u5728\u9ed1\u6697\u4e2d\u5657\u54e7\u4e00\u58f0\u7b11\u4e86\u51fa\u6765\uff01 \u3000\u3000\u7b2c\u4e09\u4e2a\u5979\u6240\u6000\u5ff5\u7684\u201c\u4eb2\u4eba\u201d\u662f\u5f20\u534e\u662d\uff0c\u5979\u8ba4\u8bc6\u4ed6\u8fd8\u4e0d\u5230\u4e24\u5e74\uff0c\u53ef\u662f\u5979\u5df2\u5bf9\u4ed6\u6709\u4e86\u5f88\u6df1\u7684 \u60c5\u611f\uff0c\u8fd9\u79cd\u60c5\u611f\u5b8c\u5168\u4e0d\u540c\u4e8e\u5bf9\u51cc\u672a\u98ce\u7684\u60c5\u611f\u3002\u5728\u4ee5\u524d\uff0c\u5979\u662f\u5168\u4e0d\u4e86\u89e3\u7537\u5973\u4e4b\u95f4\u4f1a\u6709\u8fd9\u6837\u4e00\u79cd \u60c5\u611f\u7684\uff0c\u800c\u73b0\u5728\u5979\u5374\u628a\u4ed6\u5f53\u6210\u4eb2\u4eba\u770b\u5f85\u4e86\u3002\u5979\u60f3\u8d77\u5728\u6e05\u51c9\u5bfa\u628a\u4ed6\u6551\u51fa\u6765\u65f6\uff0c\u4ed6\u90a3\u611f\u6fc0\u7684\u800c\u53c8 \u662f\u5173\u5207\u7684\u773c\u5149\u3002\u5979\u60f3\u8d77\u5728\u77f3\u8001\u9556\u5e08\u5bb6\u4e2d\uff0c\u5979\u548c\u4ed6\u8bc0\u522b\u7684\u60c5\u666f\uff0c\u201c\u6211\u6b7b\u4e86\u4e4b\u540e\uff0c\u4ed6\u771f\u4f1a\u6298\u4e00\u675f \u5170\u82b1\u63d2\u5728\u6211\u7684\u5893\u524d\u5417\uff1f\u201d\u201c\u54e6\uff0c\u8fd9\u771f\u662f\u592a\u5962\u4f88\u7684\u5e7b\u60f3\uff0c\u6211\u6b7b\u4e86\u662f\u8fde\u575f\u5893\u4e5f\u4e0d\u4f1a\u6709\u7684\u554a\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u5728\u9ed1\u6697\u4e2d\u6d41\u4e0b\u773c\u6cea\u6765\uff0c\u5ffd\u7136\u5979\u81ea\u5df1\u8d23\u5907\u81ea\u5df1\u9053\uff1a\u201c\u6768\u5927\u4fa0\u7684\u5973\u513f\u662f\u4e0d\u6d41\u6cea\u7684\uff01\u201d \u5979\u6df1\u6df1\u5730\u60f3\u5ff5\u8fd9\u4e09\u4e2a\u4eb2\u4eba\uff0c\u4f46\u628a\u8fd9\u4e9b\u601d\u5ff5\u90fd\u52a0\u8d77\u6765\uff0c\u4e5f\u53ca\u4e0d\u4e0a\u5979\u5bf9\u5979\u7236\u4eb2\u7684\u7231\u3002\u201c\u6211\u662f\u4e3a\u6211 \u7236\u4eb2\u5b8c\u6210\u4e86\u5fc3\u613f\u800c\u6b7b\u7684\uff01\u201d\u8fd9\u6837\u4e00\u60f3\uff0c\u5979\u5c31\u4e00\u70b9\u4e5f\u4e0d\u60cb\u60dc\u81ea\u5df1\u7684\u6b7b\u4e86\uff0c\u5979\u53cc\u624b\u5f20\u5f00\uff0c\u8fce\u7740\u65e0 \u8fb9\u7684\u9ed1\u6697\uff0c\u597d\u50cf\u770b\u89c1\u6b7b\u795e\u5f20\u7ffc\u98de\u6765\uff0c\u5979\u7a81\u7136\u53eb\u9053\uff1a\u201c\u6765\u5427\uff0c\u6211\u4e0d\u6015\u4f60\uff01\u201d \u3000\u3000\u5c31\u5728\u6b64\u9645\uff0c\u7262\u95e8\u5ffd\u7684\u6253\u5f00\uff0c\u4e00\u6761\u9ed1\u5f71\u5411\u5979\u884c\u6765\uff01 \u3000\u3000\u6613\u5170\u73e0\u5fc3\u7075\u9707\u8361\uff0c\u95ed\u4e0a\u773c\u775b\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u7238\u7238\u554a\uff01\u4f60\u7b49\u7740\u6211\u5427\uff0c\u4f60\u7684\u5973\u513f\u6765\u89c1\u4f60 \u4e86\uff01\u201d\u81ea\u4ece\u5979\u88ab\u5173\u8fdb\u8fd9\u95f4\u7262\u72f1\u4e4b\u540e\uff0c\u4ece\u672a\u6709\u4eba\u6765\u8fc7\uff0c\u5c31\u662f\u6bcf\u5929\u4e24\u987f\u996d\uff0c\u4e5f\u53ea\u662f\u72f1\u5352\u4ece\u5916\u9762\u9012 \u8fdb\u6765\uff0c\u8fd9\u9ed1\u5f71\u4e0d\u662f\u6b7b\u795e\u4e5f\u662f\u523d\u5b50\u624b\u4e86\uff1f\u5979\u4e00\u9635\u660f\u8ff7\uff0c\u5ffd\u7136\u53c8\u4f3c\u5fc3\u4e2d\u7a7a\u8361\u8361\uff0c\u4ec0\u4e48\u611f\u89c9\u4e5f\u6ca1\u6709 \u4e86\uff01 \u3000\u3000\u8ff7\u8361\u4e2d\uff0c\u6709\u4e00\u53ea\u624b\u8f7b\u8f7b\u629a\u6478\u7740\u5979\u7684\u5934\u53d1\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5170\u73e0\uff0c\u662f\u6211\uff01\u201d\u6613\u5170\u73e0\u53eb\u9053\uff1a \u201c\u771f\u7684\u662f\u7238\u7238\u5417\uff1f\u201d\u90a3\u4eba\u53f9\u606f\u4e00\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u5170\u73e0\uff0c\u4f60\u9192\u9192\uff01\u6211\u6765\u5e26\u4f60\u51fa\u53bb\uff01\u201d \u3000\u3000\u90a3\u4eba\u4f3c\u4e4e\u7528\u624b\u62c2\u4e86\u51e0\u62c2\uff0c\u84e6\u7136\u95f4\u6613\u5170\u73e0\u611f\u5230\u4e00\u9635\u8f7b\u677e\uff0c\u9888\u4e0a\u7684\u94c1\u67b7\u548c\u811a\u4e0b\u7684\u9563\u94d0\u90fd\u7ed9\u90a3 \u4eba\u5f04\u65ad\u4e86\u3002\u6613\u5170\u73e0\u6251\u4e86\u4e0a\u53bb\uff0c\u62d6\u7740\u90a3\u4eba\u7684\u624b\u9053\uff1a\u201c\u4f60\u662f\u7238\u7238\u8fd8\u662f\u523d\u5b50\u624b\uff1f\u201d\u6709\u4e00\u6ef4\u70ed\u6cea\u6ef4\u5728 \u5979\u7684\u9762\u4e0a\uff0c\u6709\u4e00\u4e2a\u719f\u6089\u7684\u58f0\u97f3\u5728\u5979\u7684\u8033\u8fb9\u547c\u5524\u7740\uff1a\u201c\u5170\u73e0\uff0c\u4f60\u9192\u9192\uff01\u4f60\u8ba4\u4e0d\u51fa\u6211\u5417\uff1f\u201d\u6613\u5170 \u73e0\u773c\u6cea\u593a\u7736\u800c\u51fa\uff0c\u6251\u5012\u5730\u4e0a\uff0c\u62b1\u7740\u90a3\u4eba\u7684\u53cc\u8db3\uff0c\u558a\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u8fd9\u4e0d\u662f\u68a6\u5427\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u95ef\u8fdb\u5929\u7262\u7684\u4eba\u6b63\u662f\u51cc\u672a\u98ce\u3002\u4ed6\u53d6\u4e86\u6731\u679c\u91d1\u7b26\u4e4b\u540e\uff0c\u6362\u4e86\u4e00\u8eab\u5927\u5185\u4f8d\u536b\u7684\u670d\u9970\uff0c\u5f53\u665a \u5c31\u8499\u9762\u6765\u89c1\u72f1\u5bab\uff0c\u638c\u7ba1\u5929\u7262\u7684\u662f\u5b97\u5ba4\u4e2d\u7684\u4e00\u4e2a\u8d1d\u52d2\uff0c\u4e00\u89c1\u6765\u4eba\u53d6\u51fa\u6731\u679c\u91d1\u7b26\uff0c\u5728\u767d\u7eb8\u4e0a\u5370\u51fa \u201c\u5927\u6e05\u201d\u4e24\u4e2a\u6ee1\u6587\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u662f\u5bab\u4e2d\u7684\u4f8d\u536b\uff1f\u201d\u51cc\u672a\u98ce\u70b9\u5934\u201c\u54fc\u201d\u4e86\u4e00 \u58f0\uff0c\u8d1d\u52d2\u95ee\u9053\uff1a\u201c\u7687\u4e0a\u53ef\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u7687\u4e0a\u8981\u6211\u5373\u523b\u628a\u523a\u6740\u591a\u94ce\u7684\u90a3\u540d\u5973\u8d3c \u5e26\u8fdb\u5bab\u53bb\uff0c\u4e0d\u8bb8\u65c1\u4eba\u77e5\u9053\uff01\u4f60\u5feb\u628a\u76d1\u89c6\u5979\u7684\u4f8d\u536b\u9063\u5f00\uff01\u201d\u8d1d\u52d2\u53c8\u662f\u4e00\u60ca\uff01\u65e5\u95f4\u7687\u4e0a\u7279\u522b\u4f20\u4e0b \u5fa1\u65e8\uff0c\u53eb\u4e25\u5bc6\u770b\u5b88\u90a3\u540d\u5973\u8d3c\uff0c\u63d0\u9632\u6709\u4eba\u52ab\u72f1\uff0c\u600e\u7684\u5ffd\u7136\u53c8\u63d0\u8fdb\u5bab\u53bb\uff1f\u53ef\u662f\u8fd9\u6731\u679c\u91d1\u7b26\u975e\u540c\u5c0f \u53ef\uff0c\u6301\u6709\u7684\u4eba\u7b49\u4e8e\u7687\u5e1d\u94a6\u4f7f\uff0c\u8bf4\u8bdd\u8fdd\u6297\u4e0d\u5f97\u3002\u8d1d\u52d2\u5fc3\u6709\u7591\u56e2\uff0c\u5ffd\u7136\u7075\u673a\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f \u5fa1\u524d\u5e26\u5200\u4f8d\u536b\u5417\uff1f\u5728\u54ea\u4e00\u4f4d\u603b\u7ba1\u9762\u524d\u529e\u4e8b\uff1f\u201d\u539f\u6765\u9664\u7279\u8bb8\u5916\uff0c\u53ea\u6709\u4e00\u7b49\u4f8d\u536b\u624d\u53ef\u5728\u9f99\u4f4d\u4e4b \u65c1\uff0c\u5fa1\u524d\u5e26\u5200\uff1b\u800c\u5bab\u4e2d\u5f85\u536b\u7531\u4e24\u4f4d\u603b\u7ba1\u7ba1\u7406\uff0c\u4e00\u7b49\u5f85\u536b\u7684\u603b\u7ba1\u53eb\u683c\u94a6\u52aa\u662f\u6ee1\u4eba\uff0c\u5176\u4ed6\u4f8d\u536b\u7684 \u603b\u7ba1\u5374\u662f\u4e00\u4e2a\u59d3\u8bb8\u7684\u6c49\u4eba\u592a\u76d1\uff0c\u51cc\u672a\u98ce\u4e00\u542c\u4fbf\u77e5\u662f\u4ed6\u8003\u95ee\u81ea\u5df1\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8981\u7cdf\uff01\u201d\u90a3\u8d1d \u52d2\u53cc\u624b\u636e\u6848\uff0c\u7d27\u76ef\u7740\u4ed6\uff0c\u51cc\u672a\u98ce\u673a\u7075\u4e4b\u6781\uff0c\u5ffd\u7136\u51b7\u7b11\u4e00\u58f0\uff0c\u53cd\u624b\u4e00\u638c\u6253\u5728\u684c\u4e0a\uff0c\u767b\u65f6\u628a\u4e00\u89d2 \u6253\u584c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u914d\u95ee\u6211\uff1f\u201d\u8d1d\u52d2\u901a\u4f53\u6d41\u6c57\uff0c\u89c1\u4ed6\u663e\u51fa\u8fd9\u624b\u529f\u592b\uff0c\u6df1\u4fe1\u4ed6\u662f\u4e00\u7b49\u5f85\u536b\uff0c \u54ea\u6562\u518d\u95ee\u3002\u7247\u523b\u4e4b\u540e\uff0c\u76d1\u89c6\u6613\u5170\u73e0\u7684\u4f8d\u536b\u90fd\u7ed9\u8c03\u56de\uff0c\u51cc\u672a\u98ce\u8f7b\u8f7b\u6613\u6613\u5730\u53d6\u4e86\u9501\u5319\uff0c\u5f00\u4e86\u7262 \u95e8\uff0c\u89e3\u5f00\u6613\u5170\u73e0\u7684\u9563\u94d0\u3002 \u3000\u3000\u6613\u5170\u73e0\u6cea\u6d41\u6ee1\u9762\uff0c\u7f13\u7f13\u7ad9\u4e86\u8d77\u6765\uff0c\u518d\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u771f\u7684\u4e0d\u662f\u68a6\u5417\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4f60 \u522b\u614c\uff0c\u8ddf\u7740\u6211\u51fa\u6765\u5c31\u884c\u4e86\uff0c\u4ed6\u4eec\u90fd\u5f88\u6302\u5ff5\u4f60\u5462\uff01\u201d\u6613\u5170\u73e0\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u51fa\u53bb\uff01\u201d\u51cc\u672a\u98ce \u8be7\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u6ca1\u6709\u6c14\u529b\u5566\uff0c\u7b49\u4f1a\u51fa\u53bb\uff0c\u90a3\u4e9b\u536b\u5352\u4eec\u4e00\u5b9a\u62e6\u622a\uff0c\u6211 \u4e0d\u80fd\u50cf\u4f60\u4e00\u6837\u767b\u9ad8\u8dc3\u4f4e\uff0c\u53c8\u4e0d\u80fd\u5e2e\u4f60\u62b5\u5fa1\uff0c\u5c82\u4e0d\u6210\u4e86\u4f60\u7684\u7d2f\u8d58\uff0c\u5230\u5934\u6765\u6211\u4eec\u90fd\u8981\u7ed9\u4ed6\u4eec\u6253\u56de \u5929\u7262\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u6478\u4e00\u6478\u6000\u4e2d\u7684\u6731\u679c\u91d1\u7b26\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5170\u73e0\uff0c\u6211\u6709\u7687\u5e1d\u7684\u91d1\u7b26\uff0c\u536b\u5352\u4e0d\u4f1a\u62e6\u622a \u7684\uff0c\u4f60\u653e\u5fc3\u8ddf\u6211\u51fa\u53bb\u5427\uff01\u201d\u6613\u5170\u73e0\u5927\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u6211\u771f\u4e0d\u77e5\u8981\u600e\u6837\u611f\u6fc0\u4f60\u624d\u597d\uff01\u201d \u51cc\u672a\u98ce\u62d6\u7740\u5979\u7684\u624b\uff0c\u7f13\u7f13\u8d70\u51fa\u7262\u623f\u3002 \u3000\u3000\u638c\u7ba1\u5927\u7262\u7684\u8d1d\u52d2\uff0c\u7ed9\u51cc\u672a\u98ce\u7684\u91d1\u7b26\u548c\u6b66\u529f\u9707\u4f4f\uff0c\u679c\u7136\u9063\u5f00\u4e86\u76d1\u89c6\u6613\u5170\u73e0\u7684\u4f8d\u536b\u3002\u547d\u4ee4\u4ed6 \u4eec\uff0c\u82e5\u89c1\u6709\u4eba\u5c06\u6613\u5170\u73e0\u5e26\u51fa\u5929\u7262\uff0c\u4e0d\u8bb8\u622a\u51fb\uff0c\u8fd9\u4e00\u6765\uff0c\u53ef\u6025\u715e\u4e86\u695a\u662d\u5357\u3002 \u3000\u3000\u539f\u6765\u5eb7\u7199\u7ed9\u5192\u6d63\u83b2\u9003\u51fa\u5bab\u7981\u4e4b\u540e\uff0c\u4e00\u9762\u6d3e\u6210\u5929\u633a\u7b49\u516b\u540d\u597d\u624b\uff0c\u5230\u9102\u738b\u5e9c\u53bb\u6349\u201c\u5973\u8d3c\u201d\uff1b \u4e00\u9762\u6d3e\u695a\u662d\u5357\u8d76\u5230\u5929\u7262\uff0c\u5929\u7262\u672c\u6765\u5c31\u9ad8\u624b\u5982\u4e91\uff0c\u5bab\u4e2d\u7684\u4f8d\u536b\u5df2\u6709\u4e00\u534a\u8c03\u5230\u90a3\u91cc\uff0c\u4f46\u5eb7\u7199\u7ecf\u8fc7 \u8fd9\u4e48\u4e00\u95f9\uff0c\u5f88\u4e0d\u653e\u5fc3\uff0c\u6240\u4ee5\u518d\u9063\u695a\u662d\u5357\u524d\u53bb\u534f\u52a9\uff0c\u5e76\u4f20\u65e8\u638c\u7ba1\u5929\u7262\u7684\u8d1d\u52d2\uff0c\u52a0\u610f\u63d0\u9632\u3002 \u3000\u3000\u695a\u662d\u5357\u542c\u4e86\u8d1d\u52d2\u7684\u547d\u4ee4\uff0c\u5927\u6709\u5947\u602a\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u65e5\u95f4\u7684\u5fa1\u65e8\uff0c\u8d1d\u52d2\u96be\u9053\u8fd8\u672a\u770b\u6e05 \u695a\uff1f\u201d\u6e05\u5bab\u89c4\u77e9\uff0c\u6731\u679c\u91d1\u7b26\u4f20\u9012\u7684\u662f\u6700\u673a\u5bc6\u7684\u524d\u4eca\uff0c\u7edd\u5bf9\u4e0d\u80fd\u6cc4\u6f0f\uff0c\u8d1d\u52d2\u867d\u660e\u77e5\u695a\u662d\u5357\u662f\u7981 \u536b\u519b\u7edf\u988c\uff0c\u4e5f\u4e0d\u6562\u8bf4\u51fa\u6765\u3002\u5f53\u4e0b\u53ea\u597d\u677f\u7740\u8138\u8bf4\u9053\uff1a\u201c\u82e5\u6709\u5dee\u9519\uff0c\u7531\u6211\u62c5\u627f\u597d\u4e86\uff01\u201d\u695a\u662d\u5357\u9762 \u4e0a\u65e0\u5149\uff0c\u4e00\u58f0\u4e0d\u54cd\uff0c\u8d70\u4e86\u51fa\u53bb\u3002\u7709\u5934\u4e00\u76b1\uff0c\u6084\u6084\u5730\u7ea0\u96c6\u5bab\u4e2d\u6d3e\u6765\u7684\u9ad8\u624b\uff0c\u89c1\u673a\u884c\u4e8b\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5e26\u7740\u6613\u5170\u73e0\u8d70\u51fa\u7262\u623f\uff0c\u89c1\u752c\u9053\u4e0a\u7a7a\u8361\u8361\u7684\uff0c\u679c\u7136\u6ca1\u4eba\u76d1\u89c6\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u6602\u9996\u9614 \u6b65\uff0c\u66f4\u662f\u88c5\u5f97\u795e\u6c14\u975e\u5e38\uff0c\u7aef\u51fa\u4e86\u7687\u5e1d\u5bc6\u4f7f\u7684\u8eab\u4efd\u3002 \u3000\u3000\u695a\u662d\u5357\u8eb2\u5728\u752c\u9053\u8f6c\u5f2f\u7684\u6697\u9edd\u4e4b\u5904\uff0c\u4e09\u66f4\u54cd\u8fc7\uff0c\u89c1\u7262\u95e8\u5f00\u5904\uff0c\u4e00\u4e2a\u8499\u9762\u4eba\u62d6\u7740\u6613\u5170\u73e0\u51fa \u6765\u3002\u4ed6\u5fc3\u4e2d\u4e03\u4e0a\u516b\u843d\uff01\u4e0d\u77e5\u662f\u62e6\u622a\u597d\u8fd8\u662f\u8ba9\u4ed6\u4eec\u8d70\u597d\uff1f\u731b\u7136\u95f4\uff0c\u5fc3\u4e2d\u4e00\u9707\uff0c\u8fd9\u8499\u9762\u4eba\u7684\u8eab\u6750 \u597d\u719f\uff01\u695a\u662d\u5357\u4e0d\u7531\u5f97\u60f3\u8d77\u4e00\u4e2a\u4eba\u6765\uff0c\u53c8\u60ca\u53c8\u6025\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u82e5\u771f\u662f\u6b64\u4eba\uff0c\u4ed6\u600e\u6562\u516c\u7136\u8fdb\u5165 \u5929\u7262\uff0c\u6765\u89c1\u8d1d\u52d2\uff0c\u8d1d\u52d2\u53c8\u600e\u4f1a\u4fe1\u4ed6\u7684\u8bdd\uff1f\u6b63\u8e0c\u8e87\u95f4\uff0c\u8499\u9762\u4eba\u5df2\u8d70\u5230\u4e86\u51fd\u9053\u7684\u8f6c\u5f2f\u4e4b\u5904\u3002\u695a\u662d \u5357\u7075\u673a\u4e00\u52a8\uff0c\u500f\u5730\u81ea\u6697\u9edd\u5904\u4e00\u63a0\u800c\u51fa\uff01 \u3000\u3000\u51cc\u672a\u98ce\u670d\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u4ed6\u4f55\u5c1d\u4e0d\u77e5\u6697\u9edd\u5904\u85cf\u6709\u4eba\u5f71\u3002\u4f46\u4ed6\u6301\u6709\u6731\u679c\u91d1\u7b26\uff0c\u4e00\u9762\u63d0 \u795e\u51c6\u5907\uff0c\u4e00\u9762\u88c5\u5f97\u66f4\u82e5\u65e0\u5176\u4e8b\u3002\u731b\u7136\u95f4\uff0c\u5ffd\u89c1\u695a\u662d\u5357\u6251\u5230\u9762\u524d\uff0c\u4e00\u62db\u201c\u96ea\u62e5\u84dd\u5173\u201d\uff0c\u5de6\u638c\u638c \u51fb\u81ea\u5df1\u4e0a\u76d8\u3002\u53f3\u638c\u4e94\u6307\u5982\u94a9\uff0c\u53cd\u6263\u81ea\u5df1\u8109\u95e8\uff0c\u51cc\u672a\u98ce\u8eab\u5f62\u4e00\u95ea\uff0c\u5de6\u638c\u62a4\u7740\u6613\u5170\u73e0\uff0c\u53f3\u638c\u547c\u7684 \u4e00\u58f0\u4ece\u695a\u662d\u5357\u53cc\u638c\u4ea4\u51fb\u56f4\u6210\u7684\u534a\u5f27\u5f62\u4e2d\u76f4\u7a7f\u8fdb\u53bb\uff0c\u624b\u8098\u4e00\u649e\uff0c\u5373\u5c06\u695a\u662d\u5357\u7684\u5de6\u638c\u8361\u5f00\uff0c\u4f38\u6307 \u4fbf\u70b9\u4ed6\u80f8\u53e3\u7684\u201c\u7384\u673a\u7a74\u201d\u3002\u4e0d\u6599\u695a\u662d\u5357\u8fd9\u4e24\u62db\u5168\u662f\u865a\u62db\uff0c\u4ed6\u77e5\u9053\u51cc\u672a\u98ce\u6b66\u529f\u7edd\u9876\uff0c\u65e9\u6709\u9632 \u5907\uff0c\u4e00\u53d1\u5373\u6536\uff0c\u8eab\u5b50\u7bad\u822c\u7684\u5012\u7eb5\u51fa\u53bb\uff0c\u5927\u53eb\uff1a\u201c\u8fd9\u4eba\u662f\u94a6\u72af\uff0c\u8d76\u5feb\u6349\u4ed6\uff0c\u683c\u6740\u4e0d\u8bba\uff01\u201d\u8bdd\u58f0 \u672a\u4e86\uff0c\u6697\u9edd\u5904\uff0c\u5c4b\u9876\u4e0a\uff0c\u89d2\u95e8\u4e2d\uff0c\u6e05\u5ef7\u7684\u9ad8\u624b\u5c3d\u51fa\uff01\u6b63\u662f\uff1a\u8fc7\u4e86\u4e00\u5173\u53c8\u4e00\u5173\uff0c\u95ef\u51fa\u5927\u7262\u96be\u4e0a \u96be\u3002\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5341\u516b\u7ae0\u3000\u5b7d\u503a\u60c5\u7f18\u3000\u516c\u4e3b\u60c5\u591a\u5f92\u6005\u6005\u3000\u6cea\u75d5\u5251\u5f71\u3000\u738b\u5983\u68a6\u65ad\u6068\u7ef5\u7ef5 \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u5341\u516b\u56de \u5b7d\u503a\u60c5\u7f18\u3000\u516c\u4e3b\u60c5\u591a\u5f92\u6005\u6005 \u6cea\u75d5\u5251\u5f71\u3000\u738b\u5983\u68a6\u65ad\u6068\u7ef5\u7ef5 \u3000\u3000\u539f\u6765\u695a\u662d\u5357\u4e43\u662f\u7acb\u5fc3\u8bd5\u62db\uff0c\u6545\u610f\u7528\u5929\u5c71\u638c\u6cd5\u4e2d\u7684\u7cbe\u5999\u62db\u6570\u731d\u51fb\u51cc\u672a\u98ce\u3002\u6b66\u6797\u9ad8\u624b\uff0c\u5fc3\u827a \u5408\u4e00\uff0c\u9aa4\u9047\u9669\u62db\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u5373\u51fa\u672c\u95e8\u7edd\u6280\u3002\u695a\u662d\u5357\u672c\u6765\u8fd8\u672a\u6562\u65ad\u5b9a\u8499\u9762\u4eba\u662f\u8c01\uff0c\u4e00\u89c1\u51cc\u672a \u98ce\u51fa\u624b\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u4e00\u58f0\u5927\u53eb\uff0c\u57cb\u4f0f\u7740\u7684\u6e05\u5ef7\u9ad8\u624b\uff0c\u56db\u9762\u6740\u51fa\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5927\u559d\u4e00\u58f0\uff0c\u8eab\u8eaf\u4e00\u8f6c\uff0c\u556a\u556a\u4e24\u58f0\uff0c\u5355\u638c\u51fb\u6bd9\u4e24\u540d\u536b\u58eb\uff0c\u9752\u94a2\u5251\u500f\u5730\u51fa\u9798\uff0c\u75be\u5982\u95ea \u7535\uff0c\u628a\u4e00\u540d\u6b3a\u8fd1\u8eab\u8fb9\u7684\u536b\u58eb\u523a\u6b7b\uff0c\u4e00\u624b\u62d6\u7740\u6613\u5170\u73e0\uff0c\u4fbf\u5411\u5916\u95ef\uff01 \u3000\u3000\u695a\u662d\u752b\u4e00\u9000\u5373\u4e0a\uff0c\u957f\u5251\u4ea6\u5df2\u62d4\u5728\u624b\u4e2d\uff0c\u5530\u5530\u4e24\u5251\uff0c\u5206\u523a\u51cc\u672a\u98ce\u5de6\u53f3\u8981\u7a74\u4e0b\uff0c\u695a\u662d\u5357\u5251\u6cd5 \u4e0e\u51cc\u672a\u98ce\u76f8\u5dee\u65e0\u51e0\uff0c\u4ec5\u662f\u529f\u529b\u7a0d\u900a\uff0c\u8fd9\u4e24\u5251\u72e0\u8fa3\u4e4b\u6781\uff0c\u51cc\u672a\u98ce\u8eab\u8eaf\u534a\u65cb\uff0c\u6a2a\u5251\u4e00\u5c01\uff0c\u80cc\u540e\u547c \u547c\u98ce\u54cd\uff01\u53c8\u662f\u4e00\u6761\u94c1\u97ad\u6253\u5230\u3002\u51cc\u672a\u98ce\u632f\u5251\u4e00\u683c\uff0c\u8361\u5f00\u695a\u662d\u5357\u957f\u5251\uff0c\u5de6\u638c\u4e00\u6293\uff0c\u628a\u94c1\u97ad\u6293\u4f4f\uff0c \u559d\u58f0\u201c\u8d77\u201d\uff01\u594b\u529b\u4e00\u6325\uff0c\u90a3\u540d\u536b\u58eb\u672a\u53ca\u653e\u624b\uff0c\u7adf\u7ed9\u51cc\u672a\u98ce\u6325\u4e86\u8d77\u6765\uff0c\u556a\u5566\u7684\u4e00\u58f0\uff0c\u6454\u51fa\u4e24\u4e08 \u5f00\u5916\uff01 \u3000\u3000\u51cc\u672a\u98ce\u53f3\u624b\u4f7f\u5251\uff0c\u5de6\u624b\u8fd0\u638c\uff0c\u867d\u7136\u51fb\u9000\u654c\u4eba\uff0c\u6613\u5170\u73e0\u5374\u7ed9\u4ed6\u4eec\u622a\u5728\u4e00\u8fb9\uff0c\u51cc\u672a\u98ce\u864e\u543c\u4e00 \u58f0\uff0c\u56de\u8eab\u6765\u6551\uff0c\u91d1\u80cc\u5200\u3001\u94c1\u5c3a\u3001\u9f50\u7709\u68cd\u3002\u94fe\u5b50\u9524\u3001\u864e\u5934\u94a9\u2026\u2026\u51e0\u79cd\u4e13\u514b\u5200\u5251\u7684\u91cd\u5175\u5668\uff0c\u7eb7\u7eb7 \u6253\u5230\u3002 \u3000\u3000\u51cc\u672a\u98ce\u7ffb\u8eab\u8fdb\u5251\uff0c\u98d8\u5ffd\u5982\u98ce\uff0c\u4ece\u5175\u5668\u7684\u5939\u7f1d\u4e2d\u7a7f\u8fc7\u8eab\u53bb\u3002\u4e00\u770b\u6613\u5170\u73e0\u5df2\u88ab\u64d2\u4f4f\uff0c\u6b63\u5728\u5927 \u58f0\u53eb\u9053\uff1a\u201c\u51cc\u53d4\u53d4\uff0c\u4e0d\u5fc5\u987e\u6211\uff0c\u5148\u95ef\u51fa\u53bb\uff01\u201d\u8fd9\u5239\u90a3\u95f4\uff0c\u56db\u9762\u536b\u58eb\uff0c\u7eb7\u7eb7\u62e6\u622a\u3002 \u3000\u3000\u51cc\u672a\u98ce\u594b\u8d77\u795e\u5a01\uff0c\u638c\u5288\u5251\u622a\uff0c\u53c8\u6740\u4f24\u4e86\u51e0\u540d\u536b\u58eb\uff0c\u695a\u662d\u5357\u62fc\u5251\u6251\u4e0a\uff0c\u4e00\u62db\u201c\u767d\u8679\u8d2f \u65e5\u201d\uff0c\u523a\u5411\u51cc\u672a\u98ce\u80a9\u540e\u201c\u98ce\u5e9c\u7a74\u201d\uff0c\u51cc\u5927\u98ce\u5947\u5f62\u4e00\u95ea\u3002\u5de6\u9762\u4e00\u540d\u536b\u4e0a\u6b63\u6251\u8fc7\u6765\uff0c\u7ed9\u51cc\u672a\u51e4\u987a \u52bf\u4e00\u62d6\uff0c\u500f\u5730\u6325\u8d77\u3002\u53e4\u624b\u9752\u94a2\u5251\u4e00\u62db\u201c\u98de\u9e70\u56de\u65cb\u201d\uff0c\u8361\u5f00\u653b\u6765\u7684\u5175\u5668\uff0c\u540c\u65f6\uff0c\u5de6\u4e0b\u631f\u7740\u90a3\u540d \u536b\u58eb\uff0c\u5f80\u540e\u4e00\u626b\uff0c\u8fd9\u51e0\u4e0b\u5feb\u5f97\u51fa\u5947\uff0c\u695a\u662d\u5357\u957f\u5251\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u7a7f\u5165\u4e86\u90a3\u540d\u536b\u58eb\u7684\u540e\u5fc3\uff0c\u5c1a \u672a\u62d4\u51fa\uff0c\u51cc\u672a\u51e4\u5de6\u624b\u4e00\u63a8\uff0c\u90a3\u540d\u536b\u58eb\u7684\u8eab\u8eaf\u5e73\u5e73\u649e\u53bb\uff0c\u695a\u662d\u5357\u8fde\u9000\u51e0\u6b65\uff0c\u51cc\u672a\u98ce\u75be\u5411\u659c\u5bf9\u65b9 \u5411\u6740\u51fa\uff0c\u4f46\u6613\u5170\u73e0\u5df2\u7ed9\u4eba\u6349\u56de\u5929\u7262\u53bb\u4e86\u3002 \u3000\u3000\u695a\u662d\u5357\u7ea2\u4e86\u53cc\u773c\uff0c\u201c\u9f99\u5f62\u98de\u6b65\u201d\uff0c\u518d\u5ea6\u731b\u6251\uff0c\u51cc\u672a\u98ce\u56e0\u654c\u4eba\u592a\u591a\uff0c\u4e0d\u613f\u4e0e\u4ed6\u62fc\u6597\uff0c\u8eab\u5f62 \u8d77\u5904\uff0c\u76f4\u5982\u5de8\u9e1f\u7a7f\u6797\uff0c\u8fd0\u7528\u5927\u64d2\u62ff\u624b\uff0c\u75be\u7684\u6293\u7740\u4e00\u540d\u536b\u58eb\u540e\u5fc3\uff0c\u5411\u540e\u4fbf\u7529\uff0c\u4e09\u8d77\u4e09\u4f0f\uff0c\u8fde\u6454 \u4e09\u540d\u536b\u58eb\uff0c\u695a\u662d\u5357\u653b\u52bf\u53d7\u963b\uff0c\u5176\u4ed6\u536b\u58eb\uff0c\u89c1\u5982\u6b64\u58f0\u52bf\uff0c\u4e00\u65f6\u7a92\u4f4f\uff0c\u51cc\u672a\u98ce\u5df2\u9000\u81f3\u5899\u8fb9\u3002\u5899\u9ad8 \u4e94\u4e08\u6709\u4f59\uff0c\u65e0\u6cd5\u4e00\u8dc3\u800c\u4e0a\uff0c\u9664\u975e\u7528\u201c\u5ced\u58c1\u6362\u638c\u201d\u6216\u201c\u58c1\u864e\u6e38\u5899\u201d\u7684\u529f\u592b\uff0c\u5426\u5219\u4e07\u96be\u8131\u9669\u3002\u4f46 \u654c\u4eba\u73af\u4f3a\uff0c\u82e5\u7528\u90a3\u4e24\u79cd\u529f\u5927\uff0c\u53c8\u52bf\u96be\u517c\u987e\u53d1\u6765\u7684\u6697\u5668\u3002\u51cc\u672a\u98ce\u521a\u4e00\u72b9\u8c6b\uff0c\u679c\u7136\u6697\u5668\u5982\u8757\u98de \u81f3\uff0c\u4e2d\u95f4\u8fd8\u6742\u6709\u786b\u78fa\u5f39\u3002\u51cc\u672a\u98ce\u8eab\u5f62\u95ea\u52a8\uff0c\u638c\u5288\u8896\u62c2\uff0c\u6697\u5668\u6216\u7ed9\u5012\u62cd\u56de\u53bb\uff0c\u6216\u7ed9\u8f7b\u8f7b\u907f\u5f00\uff0c \u7adf\u7136\u6beb\u53d1\u4e0d\u635f\u3002 \u3000\u3000\u695a\u662d\u5357\u632f\u81c2\u5927\u547c\uff1a\u201c\u56f4\u7740\u4ed6\uff0c\u7d2f\u6b7b\u4ed6\uff0c\u4ed6\u8dd1\u4e0d\u4e86\uff01\u201d\u7387\u9886\u6e05\u5ef7\u9ad8\u624b\uff0c\u4e00\u9f50\u6d8c\u4e0a\uff0c\u51cc\u672a\u98ce \u8feb\u5f97\u80cc\u8d34\u94c1\u5899\uff0c\u62fc\u6b7b\u529b\u6218\u3002\u6e05\u5b98\u536b\u58eb\u867d\u591a\uff0c\u5374\u4e0d\u80fd\u56db\u9762\u5305\u56f4\uff0c\u695a\u662d\u5357\u7387\u56db\u540d\u4e00\u7b49\u597d\u624b\uff0c\u6392\u6210 \u4e00\u4e2a\u534a\u5f27\u5f62\uff0c\u72e0\u72e0\u653b\u51fb\u3002\u51cc\u672a\u98ce\u5c55\u5f00\u5929\u5c71\u5251\u6cd5\uff0c\u5de6\u653b\u53f3\u62d2\uff0c\u95ea\u7535\u60ca\u98d9\uff0c\u9163\u6597\u58f0\u4e2d\uff0c\u4e24\u540d\u536b \u58eb\uff0c\u4e2d\u5251\u5012\u4ed6\uff0c\u53e6\u5916\u4e24\u540d\u8fc5\u53c8\u8865\u4e0a\u3002\u695a\u662d\u5357\u559d\u9053\uff1a\u201c\u51cc\u672a\u98ce\uff0c\u4f60\u82e5\u4e0d\u63b7\u5251\u6295\u964d\uff0c\u4eca\u65e5\u5c31\u662f\u4f60 \u7684\u6b7b\u671f\uff01\u201d\u51cc\u672a\u98ce\u5530\u5530\u8fd8\u4e86\u4e24\u5251\uff0c\u51b7\u7b11\u559d\u9053\uff1a\u201c\u65e0\u803b\u53db\u5f92\uff0c\u4f60\u8981\u53d6\u6211\u7684\u5934\u9885\uff0c\u5148\u62ff\u5341\u4e2a\u5934\u9885 \u6765\u6362\uff01\u201d\u695a\u662d\u5357\u628a\u624b\u4e00\u6325\uff0c\u56db\u540d\u9ad8\u624b\u4e00\u9f50\u731b\u653b\uff0c\u695a\u662d\u5357\u66f4\u662f\u8e0f\u6b63\u4e2d\u5bab\uff0c\u5bfb\u6687\u62b5\u9699\uff0c\u5251\u5251\u8f9b \u8fa3\u3002 \u3000\u3000\u8981\u77e5\u695a\u662d\u5357\u6b66\u529f\u539f\u5c31\u4e0e\u51cc\u672a\u98ce\u76f8\u5dee\u65e0\u51e0\uff0c\u66f4\u52a0\u4e0a\u56db\u540d\u6e05\u5bab\u4e00\u7b49\u597d\u624b\uff0c\u9976\u662f\u51cc\u672a\u98ce\u5251\u6cd5\u5982 \u4f55\u795e\u5999\uff0c\u4e5f\u611f\u5e94\u4ed8\u8270\u96be\u3002\u800c\u4e14\u695a\u662d\u5357\u5b8c\u5168\u4e0d\u987b\u9632\u5b88\uff0c\u53ea\u662f\u8fdb\u653b\uff0c\u5a01\u529b\u53c8\u52a0\u4e86\u4e00\u500d\u3002\u53ea\u89c1\u695a\u662d \u5357\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u770b\u770b\u5c31\u8981\u628a\u51cc\u672a\u98ce\u9489\u5728\u5899\u4e0a\uff0c\u5ffd\u7136\u6709\u4e00\u540d\u536b\u58eb\u8d2a\u529f\u8e81\u8fdb\uff0c\u4e00\u5bf9\u62a4\u624b\u94a9\u659c\u91cc \u5288\u8fdb\uff0c\u51cc\u672a\u98ce\u5927\u559d\u4e00\u58f0\uff0c\u5288\u624b\u628a\u94a7\u593a\u8fc7\uff0c\u968f\u624b\u4e00\u94a7\u5c31\u628a\u90a3\u4eba\u94a9\u4e86\u8fc7\u6765\uff0c\u9752\u94a2\u5251\u4e00\u62db\u201c\u795e\u9f99\u6389 \u5c3e\u201d\u6697\u8fd0\u5185\u529f\uff0c\u7c98\u5f00\u695a\u662d\u5357\u7684\u957f\u5251\uff0c\u5de6\u624b\u5c06\u90a3\u540d\u536b\u58eb\u62a1\u4e86\u8d77\u6765\uff0c\u628a\u51e0\u540d\u9ad8\u624b\u4e00\u9f50\u8feb\u9000\uff01 \u3000\u3000\u695a\u662d\u5357\u66b4\u6012\u5982\u96f7\uff0c\u4e00\u638c\u6253\u53bb\uff0c\u5c06\u90a3\u540d\u4eba\u8d28\u6253\u98de\uff0c\u633a\u5251\u53c8\u4e0e\u51cc\u672a\u98ce\u76f8\u6597\uff0c\u6e05\u5bab\u90a3\u73ed\u4f8d\u536b\uff0c \u89c1\u695a\u662d\u5357\u5982\u6b64\u6b8b\u9177\uff0c\u53ea\u987e\u64d2\u6740\u654c\u4eba\uff0c\u4e0d\u987e\u540c\u50da\u4e4b\u60c5\uff0c\u628a\u90a3\u540d\u4eba\u8d28\u6d3b\u6d3b\u6253\u6b7b\uff0c\u9f50\u90fd\u5fc3\u5bd2\u3002\u4e00\u65f6 \u95f4\uff0c\u7adf\u6ca1\u4eba\u4e0a\u6765\u52a9\u9635\uff0c\u51cc\u672a\u98ce\u8d81\u52bf\u653b\u4e86\u51e0\u5251\uff0c\u628a\u695a\u662d\u5357\u6740\u5f97\u624b\u5fd9\u811a\u4e71\u3002\u695a\u662d\u5357\u6025\u5fd9\u559d\u9053\uff1a \u201c\u4f60\u4eec\u600e\u4e48\u8fd8\u4e0d\u4e0a\u6765\uff1f\u8981\u5f85\u7687\u4e0a\u4e0b\u65e8\u5417\uff1f\u201d\u536b\u58eb\u4eec\u731b\u7136\u9192\u8d77\uff0c\u82e5\u5728\u6b64\u523b\u663e\u5f97\u754f\u7f29\uff0c\u7ed9\u695a\u662d\u5357 \u594f\u4e0a\uff0c\u5c31\u662f\u4e00\u4e2a\u6b7b\u7f6a\u3002\u8fc5\u5373\u6709\u51e0\u540d\u9ad8\u624b\uff0c\u8865\u4e0a\u7a7a\u7f3a\uff0c\u518d\u628a\u51cc\u672a\u98ce\u8feb\u81f3\u5899\u811a\u3002\u53ea\u662f\u8fd9\u51e0\u540d\u9ad8\u624b \u6035\u76ee\u60ca\u5fc3\uff0c\u5374\u4e0d\u6562\u62fc\u6b7b\u5192\u8fdb\u4e86\uff01 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u51cc\u672a\u98ce\u867d\u7136\u4e0d\u80fd\u8131\u9669\uff0c\u5f62\u52bf\u53cd\u800c\u6bd4\u524d\u7a0d\u597d\u4e86\u4e9b\uff0c\u695a\u662d\u5357\u5411\u540e\u6307\u4e86\u4e24\u6307\uff0c\u62db\u6765 \u53e6\u4e24\u540d\u9ad8\u624b\uff0c\u4ea6\u662f\u4ed6\u7684\u6b7b\u515a\uff0c\u66ff\u4e0b\u5fc3\u5b58\u754f\u7f29\u7684\u4e24\u4eba\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u8bba\u628a\u6b64\u4eba\u751f\u64d2\u6216\u683c\u6740\uff0c \u90fd\u662f\u4e00\u4ef6\u5947\u529f\uff0c\u8c01\u80af\u51fa\u529b\uff0c\u6211\u695a\u662d\u5357\u5b9a\u5411\u7687\u4e0a\u4fdd\u4e3e\u4ed6\uff01\u201d\u4f17\u536b\u58eb\u5450\u558a\u52a9\u5a01\uff0c\u524d\u5217\u4e94\u4eba\u62fc\u547d\u653b \u51fb\uff0c\u51cc\u672a\u98ce\u957f\u591c\u6076\u6597\uff0c\u989d\u4e0a\u89c1\u6c57\uff0c\u4f53\u529b\u5df2\u6e10\u611f\u4e0d\u652f\u3002 \u3000\u3000\u82e6\u6218\u6076\u6597\u4e2d\uff0c\u5ffd\u7136\u6709\u4e00\u540d\u536b\u58eb\u53eb\u9053\uff1a\u201c\u897f\u9662\u8d77\u706b\u3002\u201d\u695a\u662d\u5357\u9000\u540e\u4e00\u6b65\uff0c\u4e3e\u76ee\u4e00\u770b\uff0c\u679c\u89c1 \u897f\u8fb9\u706b\u7130\u5347\u8d77\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u4e0d\u51c6\u614c\u4e71\uff0c\u5c31\u662f\u6709\u654c\u4eba\u6765\u5230\uff0c\u90a3\u8fb9\u4e5f\u6709\u4eba\u6321\u4f4f\u3002\u5feb\u628a\u8fd9\u540d\u8d3c\u5b50 \u6bd9\u6389\uff01\u201d\u558a\u58f0\u672a\u4e86\uff0c\u5899\u5934\u4e0a\u5ffd\u7136\u73b0\u51fa\u4e00\u540d\u9752\u8863\u5987\u4eba\uff0c\u5305\u5934\u4e0a\u7cfb\u7740\u4e00\u6761\u7ea2\u5dfe\uff0c\u80cc\u540e\u6709\u51e0\u540d\u536b\u58eb \u7d27\u7d27\u8ffd\u6765\u3002\u9752\u8863\u5987\u4eba\u5de6\u624b\u63d0\u97ad\uff0c\u53f3\u624b\u4ed7\u5251\uff0c\u5411\u4e0b\u4e00\u770b\uff0c\u4e00\u58f0\u53eb\u9053\uff1a\u201c\u51cc\u672a\u98ce\uff0c\u4f60\u522b\u614c\uff0c\u6211\u6765 \u6551\u4f60\u3002\u201d\u56de\u624b\u4e00\u97ad\uff0c\u628a\u8ffd\u81f3\u8eab\u540e\u7684\u90a3\u540d\u536b\u58eb\uff0c\u4e00\u97ad\u6253\u4e0b\u9ad8\u5899\uff0c\u8d81\u52bf\u4e00\u8dc3\u800c\u4e0b\uff0c\u957f\u97ad\u547c\u547c\u98ce \u54cd\uff0c\u901e\u5411\u695a\u662d\u5357\u4e0b\u4e09\u8def\u626b\u53bb\uff0c\u559d\u9053\uff1a\u201c\u5978\u8d3c\uff0c\u8fd8\u8ba4\u5f97\u6211\u5417\uff1f\u201d\u695a\u662d\u5357\u5fc3\u5934\u4e00\u9707\uff0c\u8fde\u9000\u4e09\u6b65\uff01 \u98a4\u58f0\u53eb\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u662f\u4f60\u3001\u4f60\u2026\u2026\u201d\u51cc\u672a\u98ce\u5587\u7684\u4e00\u5251\u523a\u51fa\uff0c\u8d81\u52bf\u53c8\u4f24\u4e86\u4e00\u540d\u5927\u5185\u9ad8\u624b\u3002 \u3000\u3000\u82e5\u53ea\u8bba\u672c\u8eab\u6b66\u827a\uff0c\u695a\u662d\u5357\u867d\u80dc\u4e0d\u4e86\u98de\u7ea2\u5dfe\uff0c\u5374\u4e5f\u4e0d\u4f1a\u843d\u8d25\uff0c\u4f60\u9053\u4ed6\u4e3a\u4f55\u5982\u6b64\u60e7\u6015\uff1f\u8bf4\u8d77 \u6709\u4e00\u6bb5\u56e0\u7531\u3002\u539f\u6765\u5728\u4e8c\u5341\u591a\u5e74\u524d\uff0c\u695a\u662d\u5357\u521a\u521a\u6280\u6210\u4e0b\u5c71\u4e4b\u65f6\uff0c\u542c\u8bf4\u7f57\u5e03\u65cf\u957f\uff0c\u5510\u52aa\u8001\u82f1\u96c4\u6709 \u4e00\u4e2a\u72ec\u751f\u5973\uff0c\u540d\u5524\u54c8\u739b\u96c5\uff0c\u5916\u53f7\u98de\u7ea2\u5dfe\uff0c\u4e0d\u4f46\u6b66\u827a\u5341\u5206\u9ad8\u5f3a\uff0c\u800c\u4e14\u662f\u8349\u539f\u4e0a\u6700\u7f8e\u4e3d\u7684\u5c11\u5973\uff0c \u4e0d\u7981\u8d77\u4e86\u6c42\u5076\u4e4b\u5fc3\uff0c\u5343\u91cc\u8fe2\u8fe2\uff0c\u627e\u5230\u4e86\u5979\u7684\u90e8\u843d\u3002\u695a\u662d\u5357\u4ee5\u4e3a\u81ea\u5df1\u82f1\u96c4\u5e74\u5c11\uff0c\u5b9a\u4f1a\u83b7\u5f97\u7f8e\u4eba \u9752\u7750\u3002\u4e0d\u6599\u76f8\u5904\u6e10\u4e45\uff0c\u98de\u7ea2\u5dfe\u53d1\u73b0\u4e86\u695a\u662d\u5357\u6b66\u827a\u867d\u9ad8\uff0c\u5374\u662f\u4eba\u54c1\u4f4e\u4e0b\u3002\u90a3\u65f6\u7f57\u5e03\u65cf\u6b63\u4e0e\u6e05\u5175 \u82e6\u6218\uff0c\u695a\u662d\u5357\u5374\u53ea\u662f\u60f3\u529e\u6cd5\u4eb2\u8fd1\u98de\u7ea2\u5dfe\uff0c\u800c\u4e0d\u80af\u5c3d\u5fc3\u7aed\u667a\u62b5\u6297\u5916\u654c\u3002\u56e0\u6b64\u98de\u7ea2\u5dfe\u5bf9\u695a\u662d\u5357\u7531 \u656c\u91cd\u800c\u53d8\u4e3a\u618e\u6076\uff0c\u7ec8\u4e8e\u7ed9\u4e00\u4e2a\u8349\u539f\u4e0a\u9a70\u540d\u7684\u6b4c\u624b\uff0c\u4e58\u865a\u800c\u5165\uff0c\u83b7\u5f97\u4e86\u98de\u7ea2\u4e2a\u7684\u82b3\u5fc3\uff0c\u695a\u662d\u5357 \u4e5f\u5c31\u53db\u53d8\u6295\u964d\u4e86\u654c\u4eba\uff0c\u540e\u6765\uff0c\u5e76\u52fe\u5f15\u4e86\u90a3\u540d\u6b4c\u624b\uff0c\u6697\u5bb3\u4e86\u98de\u7ea2\u5dfe\u7684\u7236\u4eb2\uff08\u8be6\u60c5\u89c1\u62d9\u8457\u300a\u585e\u5916 \u5947\u4fa0\u4f20\u300b\uff09\uff0c\u98de\u7ea2\u5dfe\u60b2\u6124\u83ab\u540d\uff0c\u4eb2\u624b\u6349\u4e86\u81ea\u5df1\u7684\u7231\u4eba\uff0c\u6b63\u5728\u90a3\u65f6\u5019\uff0c\u4e0e\u6a2a\u8d8a\u5927\u6c99\u6f20\u7684\u6768\u4e91\u9aa2 \u4f1a\u9762\uff0c\u6210\u4e3a\u597d\u53cb\u3002\u4e24\u4eba\u66fe\u4e24\u6b21\u6d3b\u6349\u4e86\u695a\u662d\u5357\uff0c\u4f46\u90fd\u7ed9\u4ed6\u8be1\u8c0b\u9003\u8131\u3002 \u3000\u3000\u6b63\u662f\u56e0\u6b64\uff0c\u695a\u662d\u5357\u5bf9\u98de\u7ea2\u5dfe\u9887\u4e3a\u5fcc\u60ee\u3002\u6b64\u9645\uff0c\u4e8b\u9694\u4e8c\u5341\u5e74\uff0c\u7a81\u7136\u89c1\u5979\u51fa\u73b0\uff0c\u72b9\u5982\u89c1\u4e86\u9b3c \u9b45\u4e00\u822c\uff0c\u81ea\u5df1\u4e5f\u4e0d\u77e5\u600e\u7684\uff0c\u6709\u8bf4\u4e0d\u51fa\u7684\u5bb3\u6015\u3002\u8fde\u53d7\u4e86\u98de\u7ea2\u5dfe\u51e0\u6b21\u9669\u62db\uff0c\u8fd9\u624d\u795e\u667a\u6062\u590d\u3002 \u3000\u3000\u5929\u7262\u4e2d\u7684\u6e05\u5ef7\u9ad8\u624b\uff0c\u603b\u6709\u4e09\u4e94\u5341\u4eba\uff0c\u98de\u7ea2\u5dfe\u97ad\u626b\u5251\u5288\uff0c\u867d\u4f24\u4e86\u51e0\u4eba\uff0c\u81ea\u5df1\u4ea6\u5df2\u9677\u5165\u91cd \u56f4\u3002\u5899\u5934\u4e0a\uff0c\u8fd8\u6709\u597d\u591a\u540d\u539f\u6765\u5728\u897f\u9662\u770b\u5b88\u7684\u536b\u58eb\uff0c\u662f\u4e3a\u8ffd\u51fb\u98de\u7ea2\u5dfe\u800c\u6765\u7684\uff0c\u6b64\u9645\u5c55\u9ad8\u4e34\u4e0b\uff0c \u4e5f\u4e0d\u65f6\u5077\u53d1\u6697\u5668\u3002 \u3000\u3000\u51cc\u672a\u98ce\u4e00\u89c1\u673a\u4e0d\u53ef\u5931\uff0c\u731b\u559d\u4e00\u58f0\uff0c\u5251\u62db\u5982\u98ce\u7ffb\u4e91\u6d8c\uff0c\u500f\u5730\u53c8\u523a\u4f24\u4e24\u540d\u536b\u58eb\uff0c\u51b2\u5f00\u4e00\u6761\u8840 \u8def\uff0c\u628a\u98de\u7ea2\u5dfe\u63a5\u4e86\u51fa\u6765\uff0c\u4e24\u4eba\u4e00\u540c\u9000\u5230\u5899\u8fb9\u3002\u51cc\u672a\u98ce\u5251\u4ea4\u5de6\u624b\uff0c\u683c\u5f00\u6765\u88ad\u6697\u5668\u3002\u53f3\u624b\u65e9\u53d6\u51fa \u4e09\u679d\u5929\u5c71\u795e\u8292\uff0c\u5411\u5899\u5934\u4e0a\u4e00\u626c\uff0c\u559d\u58f0\uff1a\u201c\u7740\uff01\u201d\u4e09\u9053\u4e4c\u91d1\u5149\u8292\uff0c\u75be\u5982\u7535\u5c04\uff0c\u53ea\u542c\u5f97\u8fde\u58f0\u60e8 \u53eb\uff0c\u5899\u5934\u4e0a\u4e09\u540d\u536b\u58eb\uff0c\u90fd\u7ed9\u5c04\u900f\u524d\u5fc3\uff0c\u5012\u7ffb\u4e0b\u6765\u3002\u51cc\u672a\u98ce\u9053\uff1a\u201c\u98de\u7ea2\u4e2a\uff0c\u4f60\u66ff\u6211\u6682\u6321\u4e00\u4e0b\uff0c \u6211\u4e0a\u53bb\u63a9\u62a4\u4f60\u9003\uff01\u201d\u80cc\u9760\u7740\u5899\uff0c\u8eab\u5b50\u6025\u5347\u4e0a\u53bb\u3002\u6e05\u5ef7\u536b\u58eb\uff0c\u6697\u5668\u75be\u53d1\uff0c\u98de\u7ea2\u5dfe\u4e00\u8dc3\u4e08\u4f59\uff0c\u957f \u97ad\u4e00\u5377\uff0c\u628a\u51e0\u679a\u5389\u5bb3\u7684\u6697\u5668\u626b\u98de\uff0c\u53e6\u5916\u4e24\u679d\u5f2f\u7bad\uff0c\u5c04\u5230\u51cc\u672a\u98ce\u524d\u80f8\uff0c\u7ed9\u51cc\u672a\u98ce\u63a5\u4e86\u53cd\u6253\u51fa \u6765\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u51cc\u672a\u98ce\u5df2\u4ee5\u201c\u58c1\u864e\u6e38\u5899\u201d\u7684\u7edd\u6280\uff0c\u5347\u5230\u5899\u5934\uff0c\u5530\u3001\u5530\u4e24\u5251\uff0c\u53c8\u628a\u4e0a\u9762 \u8fd8\u5269\u4e0b\u7684\u4e24\u540d\u536b\u58eb\u523a\u6b7b\uff1b\u800c\u98de\u7ea2\u5dfe\u4e5f\u843d\u5230\u5730\u9762\uff0c\u53c8\u88ab\u5305\u56f4\u8d77\u6765\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5927\u58f0\u53eb\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u4e0a\u6765\uff01\u201d\u4ed6\u5728\u5899\u5934\u8fde\u6325\u51e0\u6325\uff0c\u5929\u5c71\u795e\u8292\u63a5\u8fde\u4e09\u53d1\u51fa\uff0c\u56f4 \u7740\u98de\u7ea2\u5dfe\u7684\u9ad8\u624b\uff0c\u6216\u7ed9\u5c04\u6b7b\uff0c\u6216\u7ed9\u5c04\u4f24\uff0c\u6216\u5f15\u8eab\u8eb2\u95ea\uff0c\u970e\u65f6\u95f4\uff0c\u95f9\u5f97\u4e2a\u624b\u5fd9\u811a\u4e71\u3002\u98de\u7ea2\u4e2a\u4e00 \u58f0\u957f\u5578\uff0c\u4e00\u8dc3\u4e09\u4e08\uff0c\u957f\u97ad\u5411\u4e0a\u4e00\u4e3e\uff0c\u51cc\u672a\u98ce\u63e1\u7740\u97ad\u68a2\uff0c\u7528\u529b\u4e00\u6325\uff0c\u98de\u7ea2\u5dfe\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u4e0a \u4e86\u5899\u5934\uff0c\u5730\u4e0a\u5f2f\u7bad\u9f50\u53d1\uff0c\u6697\u5668\u7eb7\u98de\uff0c\u51cc\u672a\u51e4\u4e0e\u98de\u7ea2\u5dfe\u5251\u62e8\u97ad\u51fb\uff0c\u5c55\u5f00\u7edd\u9876\u8f7b\u529f\uff0c\u500f\u5ffd\u51fa\u4e86\u5929 \u7262\u3002\u5230\u695a\u662d\u5357\u7b49\u8ffd\u51fa\u6765\u65f6\uff0c\u53ea\u89c1\u661f\u6cb3\u803f\u803f\uff0c\u660e\u6708\u5728\u5929\uff0c\u54ea\u91cc\u8fd8\u6709\u51cc\u672a\u98ce\u4e0e\u98de\u7ea2\u5dfe\u7684\u5f71\u5b50\u3002 \u3000\u3000\u8fd9\u4e00\u5f79\u6e05\u5ef7\u536b\u58eb\u635f\u5931\u60e8\u91cd\uff0c\u654c\u4eba\u4e0d\u8fc7\u6765\u4e86\u4e24\u540d\uff0c\u800c\u5927\u5185\u7684\u4e00\u7b49\u9ad8\u624b\uff0c\u7adf\u7136\u4f24\u4ea1\u4e86\u5341\u4e94\u516d \u4eba\u4e4b\u591a\uff01\u695a\u662d\u5357\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u5374\u662f\u53d1\u4f5c\u4e0d\u5f97\u3002\u5e78\u597d\u6613\u5170\u73e0\u4ecd\u88ab\u622a\u56de\uff0c\u5426\u5219\u66f4\u4e0d\u5f97\u4e86\u3002\u51cc\u672a \u98ce\u4e0e\u98de\u7ea2\u4e2d\u90fd\u662f\u695a\u662d\u5357\u7684\u514b\u661f\uff0c\u4ed6\u54ea\u91cc\u8fd8\u6562\u6258\u5927\uff0c\u5f53\u4e0b\u5165\u5bab\u8bf7\u7f6a\uff0c\u5e76\u8bf7\u518d\u8c03\u9ad8\u624b\u589e\u63f4\uff0c\u5eb7\u7199 \u542c\u4e86\uff0c\u9762\u8272\u5927\u53d8\uff0c\u534a\u664c\u4e0d\u8bed\u3002\u695a\u662d\u5357\u4f0f\u5728\u5730\u4e0a\uff0c\u4e0d\u6562\u8d77\u6765\u3002\u5eb7\u7199\u5fc3\u60f3\uff1a\u600e\u7684\u5927\u5185\u9ad8\u624b\u5982\u6b64\u65e0 \u7528\uff0c\u4e0d\u89c9\u9635\u9635\u5fc3\u5bd2\uff0c\u4f46\u4ed6\u4eec\u4e3a\u770b\u5b88\u94a6\u72af\uff0c\u6b7b\u4f24\u7d2f\u7d2f\uff0c\u82e5\u518d\u602a\u8d23\uff0c\u66f4\u6050\u79bb\u5fc3\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8fd9\u624d \u65a5\u695a\u662d\u5357\u9053\uff1a\u201c\u6715\u77e5\u9053\u4e86\uff0c\u4ee5\u540e\u4f60\u53ef\u8981\u5c0f\u5fc3\u70b9\uff01\u201d\u5f53\u4e0b\uff0c\u53e6\u5916\u4f20\u4ee4\uff0c\u53eb\u5c0f\u9ec4\u95e8\u8bf7\u9102\u738b\u5983\u8ff8 \u5bab\u3002 \u3000\u3000\u4e14\u8bf4\uff0c\u5728\u51cc\u672a\u98ce\u7b49\u5927\u95f9\u5929\u7262\u4e4b\u540e\uff0c\u9102\u738b\u5e9c\u4e5f\u5df2\u63a5\u5230\u4e86\u6d88\u606f\uff0c\u738b\u5983\u542c\u4e86\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u6b63\u4e0d \u77e5\u6613\u5170\u73e0\u662f\u5426\u5df2\u88ab\u6551\u51fa\uff0c\u5ffd\u7136\u7687\u4e0a\u5ba3\u53ec\uff0c\u6025\u5fd9\u8fdb\u5bab\u3002\u5eb7\u7199\u89c1\u4e86\u9102\u738b\u5983\u540e\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u95ee\u9053\uff1a \u201c\u4f60\u7684\u75c5\u597d\u4e86\u5417\uff1f\u201d\u738b\u5983\u51b7\u6c57\u76f4\u6d41\uff0c\u594f\u9053\uff1a\u201c\u591a\u8c22\u7687\u4e0a\u5173\u6ce8\uff0c\u597d\u4e00\u70b9\u4e86\uff01\u201d\u5eb7\u7199\u9053\u3002\u201c\u9102\u4eb2 \u738b\u529f\u5728\u56fd\u5bb6\uff0c\u60e8\u906d\u523a\u6740\uff0c\u60f3\u4f60\u5bf9\u90a3\u5973\u8d3c\u4e5f\u662f\u6781\u75db\u6068\u7684\u4e86\uff01\u201d\u9102\u738b\u5983\u6cea\u6d41\u6ee1\u9762\uff0c\u78d5\u5934\u8bf4\u9053\uff1a \u201c\u81e3\u59be\u75db\u4e0d\u6b32\u751f\u3002\u201d\u8fd9\u53e5\u8bdd\u5012\u662f\u771f\u60c5\uff0c\u5eb7\u7199\u89c1\u5979\u5982\u6b64\uff0c\u4ee5\u4e3a\u5979\u662f\u60bc\u5ff5\u4ea1\u592b\uff0c\u4e0d\u518d\u8ffd\u95ee\uff0c\u53ea\u662f \u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u524d\u5bf9\u592a\u540e\u8bf4\uff0c\u60f3\u4eb2\u5ba1\u5973\u8d3c\uff0c\u73b0\u5728\u65e2\u7136\u75c5\u4f53\u65e0\u788d\uff0c\u90a3\u5c31\u660e\u65e5\u4eb2\u81ea\u53bb\u5929\u7262\uff0c\u4e86 \u6b64\u5fc3\u613f\u5427\u3002\u201d\u738b\u5983\u542c\u4e86\u6b64\u8a00\uff0c\u72b9\u5982\u4e94\u96f7\u51fb\u9876\uff0c\u773c\u524d\u91d1\u661f\u4e71\u5192\u3002\u5eb7\u7199\u53c8\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e0d\u80fd\u518d\u8ba9 \u8fd9\u540d\u5973\u8d3c\u4e45\u62bc\u4e0d\u51b3\u4e86\uff0c\u5979\u7684\u540c\u515a\u5f88\u591a\uff0c\u518d\u4e0d\u5904\u51b3\uff0c\u88ab\u6551\u51fa\u53bb\uff0c\u4f60\u7684\u5927\u4ec7\u5c31\u4e0d\u80fd\u62a5\u4e86\u3002\u201d\u9102\u738b \u5983\u5931\u58f0\u60e8\u53eb\uff0c\u6655\u5728\u5730\u4e0a\u3002\u5eb7\u7199\u53eb\u5bab\u5a25\u6276\u5979\u5230\u592a\u540e\u5904\u6b47\u606f\uff0c\u4e34\u884c\u8fd8\u5429\u5490\u8fd1\u8eab\u7684\u4f8d\u536b\u8bf4\uff1a\u201c\u82e5\u738b \u5983\u795e\u667a\u4e0d\u9192\uff0c\u660e\u65e5\u4e0d\u80fd\u4eb2\u5ba1\uff0c\u4f60\u5c31\u4f20\u65e8\u8d1d\u52d2\uff0c\u53eb\u4ed6\u79fb\u4ea4\u4e09\u5802\u4f1a\u5ba1\uff0c\u5373\u65e5\u5904\u51b3\u3002\u201d\u738b\u597d\u521a\u521a\u9192 \u8f6c\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u53c8\u6655\u8fc7\u53bb\u3002 \u3000\u3000\u518d\u8bf4\u6613\u5170\u73e0\u88ab\u622a\u56de\u5929\u7262\u4e4b\u540e\uff0c\u9003\u751f\u7edd\u671b\uff0c\u53cd\u800c\u5b81\u9759\u4e0b\u6765\uff0c\u5728\u9ed1\u6c89\u6c89\u7684\u7262\u623f\u4e2d\uff0c\u9759\u5f85\u7740\u6b7b \u795e\u7684\u5ba3\u5224\uff0c\u9ed1\u6697\u4e2d\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5ffd\u7136\u7262\u95e8\u8f7b\u8f7b\u6253\u5f00\uff0c\u4e00\u6761\u9ed1\u5f71\u98d8\u4e86\u8fdb\u6765\uff0c\u6613\u5170\u73e0\u52a8 \u4e5f\u4e0d\u52a8\uff0c\u5389\u58f0\u53eb\u9053\uff1a\u201c\u597d\u5427\uff01\u628a\u6211\u5e26\u51fa\u53bb\uff0c\u6740\u6b7b\uff0c\u7ede\u6b7b\uff0c\u8f66\u88c2\uff0c\u5206\u5c38\uff0c\u968f\u4f60\u4eec\u7684\u4fbf\uff0c\u53ea\u662f\u6211 \u4eec\u6c49\u65cf\u7684\u4eba\u4f60\u53ef\u6740\u4e0d\u5b8c\u554a\uff01\u201d \u3000\u3000\u90a3\u6761\u9ed1\u5f71\u201c\u7830\u201d\u7684\u4e00\u58f0\u628a\u7262\u95e8\u5173\u4e0a\uff0c\u5ffd\u7136\u95f4\uff0c\u6613\u5170\u73e0\u773c\u775b\u4e00\u4eae\uff0c\u90a3\u4eba\u4eae\u8d77\u706b\u6298\uff0c\u70b9\u71c3\u4e86 \u4e00\u679d\u725b\u6cb9\u70db\uff0c\u6367\u7740\u70db\u76d8\uff0c\u7f13\u7f13\u884c\u6765\uff0c\u4f4e\u58f0\u5524\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u4f60\u4e0d\u8ba4\u5f97\u6211\u5417\uff1f\u4f60\u62ac\u5934\u770b\u770b\uff0c\u770b\u6211 \u662f\u8c01\uff1f\u201d \u3000\u3000\u6613\u5170\u73e0\u5934\u4e5f\u4e0d\u62ac\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u8c01\u662f\u5b9d\u73e0\uff1f\u5c0a\u8d35\u7684\u738b\u5983\uff0c\u6211\u662f\u6740\u6b7b\u4f60\u4e08\u592b\u7684\u51f6\u624b\uff01\u201d \u8fd9\u970e\u90a3\u95f4\uff0c\u4e00\u53ea\u6e29\u6696\u7684\u624b\uff0c\u629a\u6478\u7740\u5979\u7684\u9762\u5e9e\uff0c\u629a\u6478\u7740\u5979\u7684\u5934\u53d1\uff0c\u6613\u5170\u73e0\u60f3\u53eb\u56b7\uff0c\u60f3\u6323\u624e\uff0c\u53ef \u662f\u4e00\u70b9\u529b\u6c14\u90fd\u6ca1\u6709\uff01 \u3000\u3000\u9102\u738b\u5983\u6cea\u6d41\u6ee1\u9762\uff0c\u54ed\u7740\u53eb\u9053\uff1a\u201c\u554a\uff01\u4ed6\u4eec\u628a\u4f60\u6298\u78e8\u5f97\u597d\u82e6\uff01\u201d\u6613\u5170\u73e0\u7684\u8116\u5b50\u7ed9\u5927\u67b7\u78e8\u4f24 \u4e86\uff1b\u5468\u56f4\u8d77\u4e86\u6de4\u9ed1\u7684\u8840\u75d5\uff0c\u4e24\u53ea\u811a\u8e1d\u4e5f \u3000\u3000\u201d\u6d41\u7740\u8113\u8840\uff0c\u738b\u5983\u53d6\u51fa\u4e1d\u7ee2\uff0c\u7ed9\u6613\u5170\u73e0\u6162\u6162\u63e9\u62ed\uff0c\u8113\u8840\u6e7f\u900f\u4e86\u4e09\u6761\u4e1d\u7ee2\uff0c\u738b\u5983\u6162\u6162\u6298 \u8d77\uff0c\u85cf\u5728\u6000\u4e2d\u3002\u6613\u5170\u73e0\u5ffd\u7136\u7741\u5f00\u773c\u775b\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u738b\u5983\uff0c\u4f60\u4e0d\u8981\u5047\u6148\u60b2\uff0c\u62c6\u78e8\u6211\u7684\u4e0d\u662f\u4ed6 \u4eec\uff0c\u662f\u4f60\uff01\u201d \u3000\u3000\u738b\u5983\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u832b\u7136\u5730\u632a\u5f00\u534a\u6b65\u3002\u6613\u5170\u73e0\u659c\u7740\u773c\u775b\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5341\u516b\u5e74\u524d\u4f60\u629b\u5f03 \u4e86\u6211\uff0c\u73b0\u5728\u53c8\u8981\u6765\u6740\u6b7b\u6211\u4e86\uff01\u201d\u738b\u5983\u5931\u58f0\u75db\u54ed\uff0c\u7d27\u7d27\u5730\u6402\u7740\u6613\u5170\u73e0\uff0c\u53eb\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u4f60\u4e00\u70b9 \u4e5f\u4e0d\u77e5\u9053\u6211\u662f\u600e\u6837\u7684\u7231\u4f60\uff01\u201d\u6613\u5170\u73e0\u7528\u624b\u8098\u8f7b\u8f7b\u63a8\u5f00\u4e86\u5979\uff0c\u53eb\u9053\uff1a\u201c\u7231\u6211\uff1f\u54c8\u54c8\uff0c\u4f60\u7231\u6211\uff1f \u4f60\u4e3a\u4e86\u8981\u505a\u738b\u5983\uff0c\u8ba9\u6211\u7684\u7236\u4eb2\u7ed9\u4f60\u7684\u4e08\u592b\u6740\u6b7b\uff1b\u4f60\u4e3a\u4e86\u8981\u505a\u738b\u5983\uff0c\u5fcd\u5fc3\u628a\u6211\u629b\u5f03\uff0c\u8ba9\u6211\u5728\u5bd2 \u51b7\u7684\u5f02\u4e61\u98d8\u6cca\u4e86\u5341\u516b\u5e74\u3002\u201d\u738b\u5983\u53eb\u9053\uff1a\u201c\u5b9d\u73e0\u4f60\u9a82\u6211\uff1f\u9a82\u4e0b\u53bb\u5427\uff01\u6211\u5f88\u559c\u6b21\uff0c\u4f60\u5df2\u7ecf\u77e5\u9053\u6211 \u662f\u4f60\u7684\u6bcd\u4eb2\u4e86\uff01\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u6211\u6ca1\u6709\u6bcd\u4eb2\uff0c\u6211\u7684\u6bcd\u4eb2\u5728\u5341\u516b\u5e74\u524d\u5df2\u7ecf\u6b7b\u4e86\uff01\u201d\u738b\u5983\u62b1\u7740\u6613 \u5170\u73e0\u5750\u5728\u5730\u4e0a\uff0c\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u4f60\u7684\u6bcd\u4eb2\u505a\u9519\u4e86\u4e8b\uff0c\u53ef\u662f\u5979\u5e76\u4e0d\u662f\u90a3\u6837\u7684\u5973\u4eba\uff01\u4f60\u76f8\u4fe1 \u4e5f\u597d\uff0c\u4e0d\u76f8\u4fe1\u4e5f\u597d\uff0c\u603b\u4e4b\uff0c\u5979\u4e0d\u662f\u90a3\u6837\u7684\u4eba\uff0c\u6211\u60f3\u8bf4\u7ed9\u4f60\u542c\uff0c\u4f46\u4e00\u5b9a\u8bf4\u4e0d\u6e05\u695a\u3002\u6211\u53ea\u8bf7\u4f60\u6a21 \u6a21\u6211\u7684\u5fc3\u5427\uff01\u4ece\u6211\u8df3\u52a8\u7684\u5fc3\uff0c\u4f60\u5e94\u8be5\u77e5\u9053\u6211\u662f\u600e\u6837\u7231\u4f60\uff0c\u5341\u516b\u5e74\u6765\uff0c\u767d\u5929\u9ed1\u591c\uff0c\u6211\u90fd\u60e6\u8bb0\u7740 \u4f60\uff0c\u6211\u8bb0\u5f97\u4f60\u5f00\u59cb\u5b66\u884c\u65f6\u5019\u7684\u795e\u60c5\uff0c\u53eb\u51fa\u7b2c\u4e00\u58f0\u2018\u5988\u5988\u2019\u65f6\u5019\u7684\u559c\u60a6\uff1b\u6211\u60f3\u7740\u4f60\u4e0d\u77e5\u5728\u4ec0\u4e48 \u5730\u65b9\u957f\u5927\u4e86\uff0c\u4e0d\u77e5\u4f60\u957f\u5f97\u50cf\u7238\u7238\u8fd8\u662f\u50cf\u5988\u5988\uff0c\u73b0\u5728\u770b\u6765\uff0c\u4f60\u662f\u957f\u5f97\u8ddf\u4f60\u7684\u7238\u7238\u4e00\u6a21\u4e00\u6837\uff0c \u563f\uff01\u50cf\u4ed6\u90a3\u6837\u7684\u5014\u5f3a\uff01\u201d\u6613\u5170\u73e0\u7684\u5934\u8d34\u7740\u738b\u5983\u7684\u80f8\uff0c\u4e24\u9897\u5fc3\u90fd\u5728\u5267\u70c8\u7684\u8df3\u52a8\uff01\u5ffd\u7136\u6613\u5170\u73e0\u5012 \u5728\u738b\u5983\u6000\u4e2d\uff0c\u8f7b\u8f7b\u555c\u6ce3\uff0c\u53eb\u9053\uff1a\u201c\u8bf4\u771f\u7684\uff0c\u5988\u5988\uff0c\u6211\u4e5f\u7231\u4f60\u554a\u3002\u201d \u3000\u3000\u70db\u5149\u9a71\u6563\u4e86\u9ed1\u6697\uff0c\u5206\u522b\u4e86\u5341\u516b\u5e74\u7684\u6bcd\u5973\u4e92\u76f8\u5730\u6402\u7740\uff0c\u6bcd\u4eb2\u7684\u773c\u6cea\u6ef4\u5728\u5973\u513f\u7684\u9762\u4e0a\uff0c\u5973\u513f \u7684\u773c\u6cea\u6ef4\u5728\u6bcd\u4eb2\u7684\u80f8\u524d\uff0c\u8fc7\u4e86\u8bb8\u4e45\u8bb8\u4e45\uff0c\u8c01\u90fd\u6ca1\u6709\u8bf4\u4e00\u53e5\u8bdd\uff0c\u5ffd\u7136\u5916\u9762\u4f20\u6765\u4e86\u9601\u9601\u7684\u811a\u6b65 \u58f0\uff0c\u4f3c\u6709\u4eba\u5728\u7262\u623f\u5916\u8d70\u6765\u8d70\u53bb\uff01 \u3000\u3000\u738b\u5983\u76b1\u4e86\u76b1\u7709\uff0c\u77bf\u7136\u4e00\u9192\uff0c\u63e9\u5e72\u773c\u6cea\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u811a\u6b65\u653e\u8f7b\u4e00\u70b9\uff0c\u522b\u5435\u6211\u5ba1\u95ee\uff01\u201d\u738b \u5983\u8fdb\u5165\u5929\u7262\u65f6\uff0c\u638c\u7ba1\u5929\u7262\u7684\u8d1d\u52d2\u518d\u4e09\u95ee\u5979\u8981\u4e0d\u8981\u4eba\u966a\u4f34\uff0c\u738b\u5983\u6447\u5934\u8bf4\u4e0d\u8981\u3002\u8d1d\u52d2\u9053\uff1a\u201c\u90a3\u5973 \u8d3c\u7684\u6b66\u529f\u5f88\u5389\u5bb3\uff0c\u867d\u7136\u80cc\u4e86\u5927\u67b7\uff0c\u6263\u4e0a\u811a\u94d0\uff0c\u53ea\u6015\u8fd8\u8981\u9884\u9632\u4e07\u4e00\u3002\u738b\u5983\u4e07\u91d1\u4e4b\u4f53\uff0c\u51fa\u4e86\u5dee \u9519\uff0c\u90a3\u53ef\u4e0d\u503c\u3002\u201d\u5de5\u5983\u6012\u9053\uff1a\u201c\u522b\u7f57\u55e6\uff0c\u6211\u8981\u4eb2\u81ea\u5ba1\u95ee\uff0c\u4e0d\u8bb8\u4e00\u4e2a\u4eba\u5728\u65c1\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d\u968f \u624b\u4e00\u6293\uff0c\u5728\u6a80\u6728\u684c\u6293\u4e86\u4e94\u9053\u88c2\u75d5\uff0c\u8d1d\u52d2\u5927\u9a87\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4eba\u8bf4\u9102\u738b\u5983\u6587\u6b66\u5168\u6750\uff0c\u662f\u54b1\u4eec \u65d7\u4eba\u4e2d\u7b2c\u4e00\u7f8e\u4eba\uff0c\u53c8\u662f\u4e00\u4f4d\u5973\u82f1\u96c4\uff0c\u770b\u6765\u771f\u662f\u4e0d\u9519\uff01\u201d\u5f53\u4e0b\u4e0d\u6562\u518d\u8bf4\u3002\u4f46\u867d\u7136\u5982\u6b64\uff0c\u8d1d\u52d2\u8fd8 \u662f\u5f88\u4e0d\u653e\u5fc3\uff0c\u56e0\u6b64\u52a0\u6d3e\u536b\u58eb\u5728\u5916\u9762\u5de1\u903b\u3002 \u3000\u3000\u738b\u5983\u65a5\u9000\u4e86\u5916\u9762\u7684\u536b\u58eb\u4e4b\u540e\uff0c\u7d27\u7d27\u697c\u7740\u6613\u5170\u73e0\uff0c\u8f7b\u8f7b\u5730\u5728\u5979\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u5973\u513f\u554a\uff0c\u73b0\u5728 \u4f60\u662f\u6211\u7684\u4e86\uff01\u201d\u542c\u4e86\u5916\u9762\u536b\u571f\u7684\u811a\u6b65\u58f0\uff0c\u6613\u5170\u73e0\u5fc3\u5934\u9661\u7136\u8d77\u4e86\u4e00\u79cd\u618e\u6068\u7684\u60c5\u7eea\uff1a\u201c\u6211\u7684\u6bcd\u4eb2 \u548c\u4ed6\u4eec\u662f\u4e00\u5bb6\u4eba\uff0c\u4ed6\u4eec\u8981\u542c\u6211\u6bcd\u4eb2\u7684\u8bdd\uff01\u201d\u8fd9\u4e2a\u5ff5\u5934\u50cf\u706b\u7130\u4e00\u6837\u70e7\u75db\u4e86\u5979\u7684\u5fc3\uff0c\u5979\u6323\u624e\u7740\u4ece \u6bcd\u4eb2\u7684\u6000\u62b1\u91cc\u8131\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u738b\u5983\uff0c\u4f60\u8bf4\u8981\u5ba1\u95ee\u6211\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5ba1\u95ee\u5462\uff1f\u201d\u738b\u5983\u5fc3\u75db\u5982\u5272\uff0c \u98a4\u58f0\u8bf4\u9053\uff1d\u5b9d\u73e0\uff0c\u4f60\u8981\u600e\u6837\u624d\u76f8\u4fe1\u6211\uff1f\u76f8\u4fe1\u4f60\u7684\u6bcd\u4eb2\uff1f\u4f60\u8bf4\u7f62\uff0c\u53ea\u8981\u662f\u6211\u505a\u5f97\u5230\u7684\u6211\u90fd\u4f1a \u505a\uff01\u201d\u6613\u5170\u73e0\u51b7\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u660e\u5929\uff0c\u4e5f\u8bb8\u7b49\u4e0d\u5230\u660e\u5929\uff0c\u4ed6\u4eec\u5c31\u4f1a\u628a\u6211\u7684\u5934\u60ac\u5728\u5348\u95e8\u4e4b\u5916\uff0c \u628a\u6211\u7684\u5fc3\u809d\u796d\u5960\u4f60\u7684\u4e08\u592b\uff0c\u6211\u8fd8\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u4f60\u53bb\u505a\uff1f\u201d \u3000\u3000\u738b\u5983\u4eb2\u4e86\u4e00\u4e0b\u5979\u7684\u5973\u513f\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u5b9d\u73e0\uff0c\u6211\u5e26\u4f60\u8d70\u51fa\u5929\u7262\uff0c\u5c06\u4f60\u5077\u5077\u653e\u8d70\uff0c \u7136\u540e\u6211\u5c31\u5403\u6700\u5389\u5bb3\u7684\u6bd2\u836f\uff0c\u53bb\u89c1\u4f60\u7684\u7238\u7238\uff0c\u8fd9\u6837\u4f60\u603b\u53ef\u4ee5\u6ee1\u610f\u4e86\u5427\uff1f\u201d \u3000\u3000\u6613\u5170\u73e0\u5c16\u53eb\u4e00\u58f0\uff0c\u6402\u7740\u5979\u7684\u5988\u5988\uff0c\u53eb\u9053\uff1a\u201c\u554a\uff01\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u8bf4\u5462\uff1f\u4f60\u662f\u628a\u6211\u5f53\u6210\u4f60 \u7684\u5973\u513f\uff0c\u8fd8\u662f\u628a\u6211\u5f53\u6210\u4f60\u7684\u654c\u4eba\uff1f\u8bf4\u5f97\u597d\u50cf\u6211\u8981\u5411\u4f60\u62a5\u4ec7\uff0c\u8ba9\u4f60\u53bb\u6b7b\uff01\u201d\u738b\u5983\u76ee\u4e0d\u8f6c\u775b\u5730\u671b \u7740\u5973\u513f\uff0c\u5ffd\u7136\u558a\u9053\uff1a\u201c\u4f60\u7684\u773c\u775b\uff0c\u8ddf\u4f60\u7684\u7238\u7238\u5b8c\u5168\u4e00\u6837\u54df\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u63a2\u624b\u5165\u6000\uff0c\u628a\u5185\u8863\u6495\u7834\uff0c\u53d6\u51fa\u90a3\u5c01\u85cf\u4e86\u8bb8\u591a\u5e74\u7684\u8840\u4e66\uff0c\u63b7\u7ed9\u738b\u5983\u9053\uff1a\u201c\u8fd9\u662f\u7238\u7238 \u7ed9\u6211\u548c\u4f60\u7684\u4fe1\uff0c\u7238\u7238\u672c\u6765\u5c31\u662f\u8981\u6211\u50cf\u4ed6\u4e00\u6837\u554a\uff01\u201d \u3000\u3000\u738b\u5983\u8eab\u8eaf\u98a4\u6296\uff0c\u4f3c\u6ce2\u6d6a\u822c\u8d77\u4f0f\u4e0d\u4f11\uff0c\u5c55\u5f00\u8840\u4e66\uff0c\u53ea\u89c1\u4fe1\u4e0a\u5199\u9053\uff1a\u201c\u5b9d\u73e0\u543e\u5973\uff0c\u5f53\u4f60\u9605\u6b64 \u4e66\u65f6\uff0c\u5f53\u5df2\u957f\u5927\u6210\u4eba\u3002\u4f60\u7236\u540d\u6768\u4e91\u9aa2\uff0c\u4f60\u6bcd\u540d\u7eb3\u5170\u660e\u6167\uff0c\u4f60\u7236\u662f\u6297\u6e05\u4e49\u58eb\uff0c\u4f60\u6bcd\u662f\u6e05\u5ba4\u738b \u5983\uff0c\u4f60\u7236\u4e27\u547d\u4e4b\u65e5\uff0c\u6b63\u662f\u4f60\u6bcd\u6539\u5ac1\u4e4b\u671f\u3002\u4f60\u6bcd\u662f\u7687\u5ba4\u4e2d\u4eba\uff0c\u6539\u5ac1\u8feb\u4e8e\u7236\u547d\uff0c\u4e0d\u5fc5\u8d23\u602a\u3002\u60df\u5f7c \u6240\u5ac1\u8005\u4e43\u56fd\u4eba\u4e4b\u654c\uff0c\u80e1\u864f\u5143\u51f6\uff0c\u4f60\u5b66\u6210\u5251\u6cd5\uff0c\u5b9a\u987b\u624b\u5203\u6b64\u7360\uff0c\u4ee5\u62a5\u7236\u4ec7\uff0c\u5e76\u9664\u516c\u654c\uff0c\u82e5\u89c1\u4f60 \u6bcd\uff0c\u53ef\u4ee5\u6b64\u4e66\u4ea4\u4e4b\uff0c\u4ee4\u4f0a\u77e5\u4f60\u7236\u975e\u4e0d\u6b32\u4f0a\u665a\u5e74\u5b89\u4e50\uff0c\u800c\u5b9e\u4e3a\u56fd\u5bb6\u4e4b\u4ec7\u4e0d\u80fd\u4e0d\u62a5\u4e5f\uff0c\u5176\u4f59\u4f60\u672a \u660e\u4e86\u4e4b\u4e8b\uff0c\u53ef\u95ee\u4f60\u4e4b\u7956\u5e08\u4e0e\u643a\u4f60\u4e0a\u5c71\u4e4b\u53d4\u53d4\uff0c\u7236\u7edd\u7b14\u3002\u201d \u3000\u3000\u738b\u5983\u8bfb\u540e\uff0c\u75db\u54ed\u8bf4\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u6211\u5e76\u6ca1\u6709\u602a\u4f60\u7684\u7238\u7238\u53eb\u4f60\u6740\u4ed6\u554a\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u7684\u773c\u775b\u653e\u51fa\u95ea\u95ea\u5149\u8292\uff0c\u518d\u8ffd\u95ee\u9053\uff1a\u201c\u5988\u5988\uff0c\u4f60\u771f\u7684\u4e0d\u602a\u6211\u5417\uff1f\u201d\u738b\u5983\u6253\u4e86\u4e00\u4e2a\u5bd2 \u5664\uff0c\u6cea\u5149\u4e2d\u84e6\u7136\u73b0\u51fa\u591a\u94ce\u4e34\u6b7b\u65f6\u7684\u60c5\u666f\uff0c\u9c9c\u8840\u6dcb\u6f13\uff0c\u60e8\u7b11\u5f85\u6b7b\u7684\u60c5\u666f\uff0c\u5979\u53c8\u60f3\u8d77\u5979\u66fe\u5bf9\u591a\u94ce \u5e94\u8bfa\u7684\u8bdd\uff1a\u201c\u4f60\u4e0d\u8981\u4f24\u5bb3\u5979\uff0c\u6211\u4e5f\u53eb\u5979\u4e0d\u8981\u4f24\u5bb3\u4f60\uff01\u201d\u662f\u7684\uff0c\u5979\u5e76\u4e0d\u602a\u5979\u7684\u5973\u513f\uff0c\u7136\u800c\u77e5\u53c8 \u6709\u70b9\u4e3a\u4ed6\u4eec\u7684\u4e92\u76f8\u4f24\u5bb3\u800c\u60cb\u60dc\u3002\u5979\u5e7d\u5e7d\u5730\u7b54\u9053\uff1a\u201c\u5973\u513f\uff0c\u6211\u600e\u4f1a\u602a\u4f60\u5462\uff1f\u4f46\u8840\u5df2\u7ecf\u6d41\u5f97\u591f \u4e86\uff0c\u6211\u4e0d\u613f\u518d\u770b\u89c1\u6d41\u8840\u4e86\uff01\u201d \u3000\u3000\u201c\u8840\u5df2\u7ecf\u6d41\u5f97\u591f\u4e86\uff1f\u201d\u6613\u5170\u73e0\u51b7\u7b11\u63a5\u9053\uff1a\u201c\u6211\u4eec\u6c49\u65cf\u4eba\u6d41\u4e86\u591a\u5c11\u8840\uff1f\u4f60\u4eec\u7687\u5e1d\u548c\u5c06\u519b\u8fd8 \u8981\u4f7f\u6211\u4eec\u7ee7\u7eed\u6d41\uff01\u4f46\u6211\u4eec\u7684\u8840\u4e5f\u4e0d\u4f1a\u767d\u6d41\u7684\uff0c\u6211\u7684\u7236\u4eb2\u8840\u6d12\u676d\u5dde\uff0c\u4f60\u7684\u4e08\u592b\u5c31\u8981\u8840\u6d12\u897f\u5c71\uff1b \u660e\u5929\uff0c\u6211\u7684\u8840\u67d3\u7ea2\u5929\u7262\uff0c\u540e\u5929\uff0c\u66f4\u591a\u6ee1\u6d32\u4eba\u7684\u8840\u5c31\u8981\u67d3\u7ea2\u4eac\u57ce\u7684\u6ce5\u571f\uff01\u201d \u3000\u3000\u738b\u5983\u50cf\u6328\u4e86\u6253\u4e00\u6837\u60ca\u8df3\u8d77\u6765\uff0c\u60ca\u6050\u5730\u6ce8\u89c6\u7740\u5979\u7684\u5973\u513f\u3002\u5979\u65e5\u65e5\u591c\u591c\u68a6\u60f3\u7740\u7684\u5973\u513f\uff0c\u5982\u4eca \u5728\u5979\u7684\u9762\u524d\uff0c\u662f\u5982\u6b64\u4eb2\u5bc6\uff0c\u5374\u53c8\u662f\u5982\u6b64\u964c\u751f\uff01\u5979\u548c\u5979\u597d\u50cf\u662f\u5904\u5728\u4e24\u4e2a\u4e16\u754c\u91cc\uff0c\u5979\u4e0d\u4e86\u89e3\u5979\uff0c \u5979\u4eec\u7684\u5fc3\u7075\u4e4b\u95f4\u597d\u50cf\u9694\u7740\u4e00\u5c42\u5e37\u5e55\uff01\u5979\u542c\u7740\u5979\u7684\u5973\u513f\u628a\u90a3\u6ee1\u8154\u6028\u6068\u50cf\u7011\u5e03\u4f3c\u7684\u503e\u6cfb\u51fa\u6765\uff0c\u5979 \u53c8\u662f\u60ca\u6050\u53c8\u662f\u54c0\u75db\uff0c\u5979\u660f\u7729\u5730\u98a4\u6296\u7740\uff0c\u5ffd\u7136\u53c8\u7d27\u7d27\u5730\u697c\u7740\u5973\u513f\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u7684\u6211\u7684\u5973\u513f\uff0c\u4f60 \u4e3a\u4ec0\u4e48\u8981\u5206\u51fa\u2018\u6211\u4eec\u2019\u548c\u2018\u4f60\u4eec\u2019\uff1f\u4f60\u662f\u6211\u8840\u4e2d\u7684\u8840\uff0c\u8089\u4e2d\u7684\u8089\uff0c\u4f60\u548c\u6211\u662f\u4e00\u4e2a\u8eab\u4f53\u7684 \u554a\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u5ffd\u7136\u7b11\u4e86\u8d77\u6765\uff0c\u4e0d\u662f\u51b7\u7b11\uff0c\u800c\u662f\u4e00\u79cd\u559c\u60a6\u7684\u7b11\uff0c\u5979\u628a\u8138\u6251\u5728\u6bcd\u4eb2\u7684\u80f8\u812f\u4e0a\uff0c\u8bf4 \u9053\uff1a\u201c\u5988\u5988\uff0c\u4f60\u771f\u7684\u8fd9\u6837\u7231\u6211\uff0c\u613f\u610f\u662f\u6211\u4eec\u7684\u4eba\u5417\uff1f\u201d\u738b\u5983\u8fd8\u6765\u4e0d\u53ca\u5f04\u6e05\u695a\u5979\u7684\u610f\u601d\uff0c\u8d76\u5fd9 \u8bf4\u9053\uff1a\u201c\u5f53\u7136\u662f\u8fd9\u6837\u7684\u554a\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u4e0d\u76f8\u4fe1\u6211\u5462\uff1f\u201d\u6613\u5170\u73e0\u6025\u4fc3\u5730\u53eb\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u5c31\u8ddf \u6211\u4e00\u9053\u8d70\u5427\uff01\u6bcd\u4eb2\uff0c\u4e0d\u662f\u4f60\u5e26\u6211\u8d70\uff0c\u662f\u4f60\u8ddf\u6211\u8d70\uff0c\u660e\u767d\u5417\uff1f\u5988\u5988\uff0c\u51cc\u5927\u4fa0\u4ed6\u4eec\u4e00\u5b9a\u8fd8\u5728\u60f3\u529e \u6cd5\u6551\u6211\uff0c\u4f60\u9a6c\u4e0a\u51fa\u53bb\uff0c\u6211\u544a\u8bc9\u4f60\u4ed6\u4eec\u7684\u5730\u5740\uff0c\u4ed6\u4eec\u6709\u4f60\u7684\u5e2e\u52a9\uff0c\u4e00\u5b9a\u4f1a\u6551\u51fa\u6211\u3002\u9664\u975e\u6211\u8fc7\u4e0d \u4e86\u660e\u5929\uff0c\u5426\u5219\u4f60\u8fd8\u6709\u673a\u4f1a\u6551\u6211\u51fa\u53bb\u7684\uff01\u201d \u3000\u3000\u738b\u5983\u4e00\u9635\u9635\u6655\u7729\uff0c\u201c\u8ddf\u4f60\u4e00\u9053\u8d70\uff1f\u201d\u5979\u5583\u5583\u95ee\u9053\u3002\u8fd9\u662f\u5979\u4ece\u6ca1\u60f3\u8fc7\u7684\u4e8b\uff0c\u5979\u662f\u4e00\u4e2a\u738b \u5983\uff0c\u600e\u4e48\u80fd\u591f\u548c\u964c\u751f\u7684\u6c49\u65cf\u4eba\u4e00\u9053\uff0c\u53cd\u5bf9\u81ea\u5df1\u7684\u65cf\u4eba\u5462\uff1f\u5979\u8fd9\u6837\u7684\u4e00\u9635\u72b9\u7591\uff0c\u6613\u5170\u73e0\u65e9\u5df2\u53d8 \u4e86\u989c\u8272\uff0c\u53eb\u903c\uff1a\u201c\u5988\u5988\uff0c\u6211\u4e00\u4e1d\u4e00\u6beb\u90fd\u4e0d\u613f\u52c9\u5f3a\u4f60\uff0c\u662f\u6211\u592a\u8fc7\u4efd\u4e86\uff0c\u662f\u6211\u60f3\u5f97\u592a\u5b69\u5b50\u6c14\u4e86\u3002 \u5982\u679c\u4f60\u613f\u610f\u8ddf\u6211\u8d70\u7684\u8bdd\uff0c\u5341\u516b\u5e74\u524d\u4f60\u5df2\u8ddf\u6211\u7684\u7236\u4eb2\u8d70\u4e86\u3002\u6211\u4e0d\u602a\u4f60\uff0c\u5988\u5988\uff01\u4f60\u4e5f\u522b\u602a\u6211\u554a\uff01 \u73b0\u5728\u6211\u4e00\u70b9\u4e00\u6ef4\u4e5f\u4e0d\u613f\u53d7\u4f60\u5e2e\u52a9\uff0c\u4f60\u8d76\u5feb\u8d70\u5427\uff01\u8fd9\u4e2a\u7262\u623f\u6c61\u79fd\u5f97\u5f88\u3002\u201d \u3000\u3000\u738b\u5983\u4f4e\u58f0\u5730\u62bd\u54bd\uff0c\u8bf4\u4e86\u8bb8\u591a\u8bdd\uff0c\u751a\u81f3\u8bf4\u613f\u610f\u8ddf\u5979\u4e00\u9053\u8d70\uff0c\u53ef\u662f\u5979\u7684\u5973\u513f\u50cf\u54d1\u4e86\u4e00\u6837\uff0c\u4e00 \u53e5\u8bdd\u4e5f\u4e0d\u7b54\u5979\u4e86\uff01\u738b\u5983\u8fd9\u65f6\u6bd4\u6b7b\u4e86\u8fd8\u96be\u53d7\uff0c\u5979\u6599\u4e0d\u5230\u5979\u7684\u5973\u513f\u7adf\u6bd4\u5979\u7684\u7238\u7238\u8fd8\u575a\u5f3a\u3002\u5ffd\u7136\uff0c \u5979\u7684\u624b\u89e6\u5230\u4e00\u6837\u4e1c\u897f\uff0c\u5979\u84e6\u5730\u53eb\u9053\uff1a\u201c\u5b9d\u73e0\uff0c\u6211\u6709\u4e00\u6837\u4e1c\u897f\u8981\u7ed9\u4f60\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u4ecd\u662f\u90a3\u4e2a\u6837\u5b50\uff0c\u628a\u8138\u85cf\u5728\u638c\u4e2d\uff0c\u5ffd\u7136\u95f4\uff0c\u5979\u7684\u773c\u775b\u4ece\u624b\u6307\u7f1d\u4e2d\u770b\u5230\u4e00\u7f15\u8840\u7ea2\u7684\u5149 \u8292\uff0c\u738b\u5983\u624b\u4e0a\u62ff\u7740\u4e00\u628a\u4eae\u6676\u6676\u7684\u77ed\u5251\uff0c\u591a\u94ce\u7684\u8840\u51dd\u7ed3\u5728\u5251\u5203\u4e0a\uff0c\u8fd8\u6ca1\u6709\u63e9\u53bb\uff0c\u6613\u5170\u73e0\u8df3\u8d77\u6765 \u9053\uff1a\u201c\u8fd9\u662f\u7238\u7238\u7684\u5b9d\u5251\u3002\u201d \u3000\u3000\u738b\u5983\u9053\uff1a\u201c\u662f\u7684\uff0c\u8fd9\u662f\u4ed6\u7684\u5b9d\u5251\uff0c\u6211\u7b2c\u4e00\u6b21\u78b0\u5230\u4ed6\u65f6\uff0c\u4ed6\u7ed9\u6c99\u6f20\u7684\u98ce\u66b4\u51fb\u5012\uff0c\u6655\u5012\u5728\u6211 \u7684\u5e10\u7bf7\u5916\uff0c\u6211\u5c31\u662f\u770b\u89c1\u4ed6\u8fd9\u628a\u5b9d\u5251\u624d\u6551\u3002\u4ed6\u7684\u3002\u4f60\u5728\u4e94\u53f0\u5c71\u884c\u523a\u7684\u65f6\u5019\uff0c\u4e00\u5251\u63d2\u5165\u6211\u7684\u8f7f \u4e2d\uff0c\u6211\u4e00\u770b\u89c1\uff0c\u5c31\u77e5\u9053\u4f60\u662f\u6211\u7684\u5973\u513f\u4e86\u3002\u201d \u3000\u3000\u8fd9\u628a\u5251\u50cf\u662f\u4e00\u4e2a\u8bc1\u4eba\uff0c\u6613\u5170\u73e0\u4e00\u5bb6\u4eba\u7684\u60b2\u6b21\u79bb\u5408\u3001\u751f\u6b7b\u5b58\u4ea1\u90fd\u548c\u5b83\u6709\u7740\u5173\u8054\u3002\u5b83\u4f34\u7740\u6768 \u4e91\u9aa2\u548c\u7eb3\u5170\u660e\u6167\u5728\u8349\u539f\u5b9a\u76df\uff1b\u5b83\u4fdd\u536b\u6768\u4e91\u9aa2\u5230\u6700\u540e\u7684\u4e00\u523b\uff1b\u51cc\u672a\u98ce\u62ff\u5b83\u4f5c\u4fe1\u7269\uff0c\u62b1\u6613\u5170\u73e0\u4e0a \u5929\u5c71\uff1b\u6700\u540e\u6613\u5170\u73e0\u5c06\u5b83\u63d2\u8fdb\u4e86\u591a\u94ce\u7684\u80f8\u819b\u3002 \u3000\u3000\u4e5f\u5c31\u662f\u5728\u523a\u6740\u591a\u94ce\u90a3\u5929\uff0c\u6613\u5170\u73e0\u56e0\u4e3a\u89c1\u7740\u6bcd\u4eb2\uff0c\u5b9d\u5251\u9707\u843d\u5728\u5730\u4e0a\uff0c\u5979\u5728\u5929\u7262\u91cc\u60f3\u8d77\u201c\u4eb2 \u4eba\u201d\u65f6\uff0c\u4e5f\u66fe\u7ecf\u60f3\u5ff5\u8fc7\u8fd9\u628a\u5b9d\u5251\u7684\u3002\u4f46\u73b0\u5728\uff0c\u5979\u7684\u6bcd\u4eb2\u5c06\u5b83\u4ea4\u8fd8\u7ed9\u5979\uff0c\u5979\u5374\u611f\u5230\u4e00\u9635\u9635\u7684\u8ff7 \u60d1\u3002 \u3000\u3000\u738b\u5983\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u7559\u7740\u8fd9\u628a\u5251\u5427\uff0c\u4e5f\u8bb8\u5bf9\u4f60\u6709\u7528\u7684\u3002\u5982\u679c\u51cc\u5927\u4fa0\u4ed6\u4eec\u518d\u6765\u6551\u4f60\uff0c\u6709\u8fd9 \u628a\u5251\uff0c\u4e5f\u6bd4\u8f83\u5bb9\u6613\u8131\u8eab\u3002\u201d \u3000\u3000\u6613\u5170\u73e0\u6700\u7231\u5979\u7684\u7236\u4eb2\uff0c\u56e0\u6b64\u4e5f\u975e\u5e38\u7231\u8fd9\u628a\u77ed\u5251\u3002\u53ef\u662f\u6b64\u523b\uff0c\u5979\u5374\u5ffd\u7136\u95f4\u611f\u5230\u618e\u6068\uff0c\u4e0d\u662f \u6068\u8fd9\u628a\u5251\uff0c\u800c\u662f\u6068\u5979\u7684\u6bcd\u4eb2\u3002\u5979\u53eb\u6211\u7559\u7740\u8fd9\u628a\u5251\u7b49\u51cc\u5927\u4fa0\u4ed6\u4eec\u6765\u6551\uff0c\u90a3\u4e48\u5c31\u662f\u8bf4\uff0c\u5979\u975e\u4f46\u4e0d \u80af\u8ddf\u6211\u4e00\u9053\u8d70\uff0c\u800c\u4e14\u4e0d\u613f\u518d\u60f3\u529e\u6cd5\u6551\u6211\u4e86\u3002\u201d\u5979\u5e76\u4e0d\u5e0c\u671b\u6bcd\u4eb2\u6551\u5979\uff0c\u53ef\u662f\u5979\u7684\u5fc3\u7075\u6df1\u5904\uff0c\u5374 \u662f\u6e34\u671b\u6bcd\u4eb2\u7684\u7231\u7684\u3002\u5979\u89c9\u5f97\u5341\u516b\u5e74\u7684\u75db\u82e6\uff0c\u5c31\u8be5\u8d62\u5f97\u6bcd\u4eb2\u5168\u90e8\u7684\u7231\u3002\u8981\u6c42\u592a\u9ad8\u4e86\uff0c\u5931\u671b\u4e5f\u5c31 \u5bb9\u6613\u3002\u8fd9\u662f\u4e00\u79cd\u975e\u5e38\u9519\u7efc\u590d\u6742\u7684\u60c5\u7eea\uff0c\u4f46\u5979\u5374\u4e0d\u77e5\u9053\uff0c\u5979\u7684\u6bcd\u4eb2\u5728\u8bf4\u8fd9\u8bdd\u65f6\uff0c\u5fc3\u91cc\u5df2\u7ecf\u4f5c\u4e86 \u4e00\u4e2a\u51b3\u5b9a\u3002 \u3000\u3000\u6613\u5170\u73e0\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u8981\u5b83\uff0c\u6211\u4eec\u6bcf\u4e2a\u4eba\u90fd\u662f\u4e00\u628a\u77ed\u5251\uff01\u4ee4\u4f60\u4eec\u6ee1\u6d32\u4eba\u98a4\u6296\u7684\u77ed\u5251\u3002\u8fd9\u628a \u5251\u8fd8\u662f\u7559\u7ed9\u4f60\u5427\uff0c\u4f60\u89c1\u7740\u5b83\u4f1a\u66f4\u8bb0\u5f97\u7238\u7238\u3002\u201d\u6613\u5170\u73e0\u53cc\u624b\u62b1\u7740\u5934\uff0c\u4f4e\u4f4e\u5730\u55d3\u4f4d\uff0c\u53c8\u4e0d\u7406\u5979\u7684 \u6bcd\u4eb2\u4e86\uff01 \u3000\u3000\u5916\u9762\u7684\u811a\u6b65\u58f0\u53c8\u54cd\u8d77\u6765\uff0c\u6709\u4eba\u50ac\u9053\uff1a\u201c\u8d1d\u52d2\u95ee\u5019\u738b\u5983\uff0c\u7687\u4e0a\u4e5f\u6d3e\u4eba\u6765\u63a2\u95ee\uff0c\u738b\u5983\u5ba1\u5b8c\u6ca1 \u6709\uff1f\u201d\u9102\u738b\u5983\u5e94\u4e86\u4e00\u58f0\uff0c\u53d6\u51fa\u4e00\u6761\u5e72\u51c0\u7684\u4e1d\u5e15\uff0c\u7ed9\u5973\u513f\u6162\u6162\u5730\u63e9\u62b9\u773c\u6cea\u3002\u5f53\u5979\u7ad9\u8d77\u6765\u65f6\uff0c\u832b \u7136\u5730\u5c06\u624b\u5e15\u6389\u843d\u5730\u4e0a\u3002 \u3000\u3000\u201c\u5b9d\u73e0\uff0c\u4f60\u597d\u597d\u4fdd\u62a4\u81ea\u5df1\uff0c\u201d\u738b\u5983\u8bf4\uff1a\u201c\u4f60\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u6613\u5170\u73e0\u7684\u5fc3\u50cf\u7ed9\u5343\u4e07\u628a\u5c16\u5200\u5272\u6210\u65e0\u6570\u788e\u7247\uff01 \u3000\u3000\u70ac\u5149\u6e10\u6e10\u6d88\u901d\u4e86\uff0c\u90a3\u679d\u738b\u5983\u5e26\u6765\u7684\u725b\u6cb9\u70db\uff0c\u53ea\u5269\u4e0b\u77ed\u77ed\u7684\u534a\u5bf8\uff0c\u5728\u5410\u7740\u5fae\u5f31\u7684\u5149\u8292\uff0c\u70db \u6cea\u51dd\u7ed3\u5728\u5730\u4e0a\uff0c\u6784\u6210\u4e0d\u89c4\u5219\u7684\u82b1\u7eb9\u56fe\u6848\u3002\u201c\u8721\u70ac\u6210\u7070\u6cea\u59cb\u5e72\uff01\u201d\u738b\u5983\u505c\u6b62\u54ed\u6ce3\uff0c\u6700\u540e\u77a7\u4e86\u6613 \u5170\u73e0\u4e00\u773c\uff0c\u6728\u7136\u5730\u8f6c\u8fc7\u4e86\u8eab\uff0c\u5411\u7740\u7262\u95e8\u8d70\u53bb\u3002 \u3000\u3000\u201c\u6211\u660e\u767d\u4e86\uff01\u201d\u6613\u5170\u73e0\u6e29\u67d4\u5730\u53f9\u9053\uff1a\u201c\u5988\u5988\uff0c\u8fd9\u4e0d\u662f\u4f60\u7684\u9519\uff01\u201d\u4f46\u5979\u8bf4\u5f97\u592a\u5c0f\u58f0\u4e86\uff0c\u4ee5 \u81f3\u738b\u5983\u6839\u672c\u6ca1\u6709\u542c\u89c1\u3002 \u3000\u3000\u8721\u70db\u70e7\u5b8c\u4e86\uff0c\u70db\u5149\u5ffd\u7684\u7184\u706d\uff0c\u5c31\u5728\u8fd9\u4e00\u523b\uff0c\u738b\u5983\u8d70\u51fa\u4e86\u7262\u95e8\uff0c\u5929\u7262\u5185\u5269\u4e0b\u865a\u7a7a\u7684\u9ed1\u7779\uff01 \u6613\u5170\u73e0\u9661\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u558a\u9053\uff1a \u3000\u3000\u201c\u5988\u5988\uff01\u6211\u4eec\u5f7c\u6b64\u539f\u8c05\u5427\uff01\u5988\u5988\uff0c\u56de\u6765\uff01\u56de\u6765\uff01\u201d \u3000\u3000\u7262\u95e8\u5df2\u7ecf\u5173\u4e0a\u4e86\u3002\u5988\u5988\u4e0d\u4f1a\u518d\u56de\u6765\u4e86\uff01\u6613\u5170\u73e0\u832b\u7136\u5730\u5411\u56db\u56f4\u5f20\u671b\uff0c\u9ed1\u6697\u4e2d\u4f3c\u6709\u65e0\u6570\u9b3c\u9b41 \u5f20\u7259\u821e\u722a\u5411\u5979\u6251\u6765\uff0c\u5979\u5c16\u53eb\u4e00\u58f0\uff0c\u6251\u5728\u5730\u4e0a\uff0c\u5fc3\u91cc\u660e\u767d\uff0c\u4ec0\u4e48\u90fd\u5b8c\u4e86\uff01 \u3000\u3000\u201c\u4ec0\u4e48\u90fd\u5b8c\u4e86\uff01\u201d\u738b\u5983\u558a\u4e86\u51fa\u6765\uff0c\u6b64\u523b\uff0c\u5979\u5df2\u7ecf\u56de\u5230\u5bb6\u4e2d\uff0c\u5728\u623f\u95f4\u8e31\u6765\u8e31\u53bb\uff0c\u53d1\u51fa\u7edd\u671b \u7684\u53eb\u558a\u3002 \u3000\u3000\u623f\u95f4\u7684\u6b63\u4e2d\u6302\u6709\u591a\u94ce\u7684\u753b\u50cf\uff0c\u591a\u94ce\u90a3\u53cc\u773c\u775b\u4f3c\u4e4e\u5728\u7262\u7262\u5730\u76ef\u7740\u5979\uff0c\u5979\u62d4\u51fa\u90a3\u67c4\u77ed\u5251\uff0c\u6768 \u4e91\u9aa2\u7684\u5f71\u5b50\u5728\u5251\u5149\u4e2d\u73b0\u51fa\u6765\uff0c\u4e5f\u4f3c\u4e4e\u5728\u7262\u7262\u5730\u76ef\u7740\u5979\u3002\u5979\u5c16\u53eb\u4e00\u58f0\uff0c\u63a9\u4e86\u9762\u5b54\u3002\u6f06\u9ed1\u4e2d\uff0c\u5979 \u5973\u513f\u7684\u5f71\u5b50\u53c8\u5728\u773c\u524d\u51fa\u73b0\uff0c\u4e5f\u4f3c\u4e4e\u5728\u7262\u7262\u5730\u76ef\u7740\u5979\uff01 \u3000\u3000\u5979\u5f20\u5f00\u4e86\u53cc\u624b\uff0c\u6162\u6162\u5730\u62ff\u8d77\u4e86\u90a3\u67c4\u77ed\u5251\u3002 \u3000\u3000\u7a81\u7136\u4e00\u9635\u6572\u95e8\u58f0\uff0c\u4f8d\u5973\u5728\u5916\u9762\u62a5\u9053\uff1a\u201c\u7eb3\u5170\u516c\u5b50\u6c42\u89c1\uff01\u201d \u3000\u3000\u201c\u662f\u4ed6\uff1f\u600e\u4e48\u8fd9\u4e2a\u65f6\u5019\u8981\u6c42\u89c1\u6211\uff1f\u201d\u7eb3\u5170\u5bb9\u82e5\u662f\u738b\u5983\u6700\u75bc\u7231\u7684\u4f84\u513f\uff0c\u4e5f\u662f\u5979\u5e73\u65e5\u552f\u4e00\u53ef \u4ee5\u8c08\u5f97\u6765\u7684\u4eba\u3002\u5979\u672c\u6765\u662f\u4e0d\u60f3\u89c1\u4efb\u4f55\u4eba\u7684\u4e86\uff0c\u53ef\u662f\u7eb3\u5170\u5bb9\u82e5\u662f\u4f8b\u5916\uff0c\u5979\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u597d\u5427\uff0c \u5c31\u548c\u4ed6\u89c1\u4e00\u9762\u5427\uff01\u201d\u5979\u6253\u5f00\u4e86\u623f\u95e8\uff0c\u7eb3\u5170\u5bb9\u82e5\u6b63\u7f13\u7f13\u5730\u8d70\u4e0a\u697c\u6765\uff0c\u4ed6\u7684\u4e66\u50ee\u5728\u697c\u4e0b\u7b49\u5019\u3002 \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u548c\u738b\u5983\u5bf9\u9762\u800c\u5750\uff0c\u5f7c\u6b64\u90fd\u5927\u5403\u4e00\u60ca\u3002\u7eb3\u5170\u5bb9\u82e5\u5403\u60ca\u7684\u662f\uff1a\u59d1\u59d1\u672c\u6765\u662f\u65d7\u4e2d\u6700\u7f8e \u7684\u7f8e\u4eba\uff0c\u73b0\u5728\u5374\u4f3c\u84e6\u7136\u8001\u4e86\u51e0\u5341\u5e74\uff0c\u800c\u4e14\u53cc\u773c\u80bf\u5f97\u50cf\u80e1\u6843\u4e00\u6837\uff0c\u663e\u7136\u662f\u6d41\u4e86\u8fc7\u591a\u7684\u773c\u6cea\uff01\u738b \u5983\u5403\u60ca\u7684\u662f\uff1a\u5979\u8fd9\u4f4d\u624d\u540d\u503e\u56fd\u7684\u4f84\u513f\uff0c\u7adf\u6d88\u5931\u4e86\u4e00\u5411\u6f47\u6d12\u7684\u98ce\u5ea6\uff0c\u9762\u8272\u60e8\u767d\uff0c\u6367\u7740\u8336\u676f\u65f6\uff0c \u624b\u6307\u4e5f\u5728\u5fae\u5fae\u5730\u98a4\u6296\u3002 \u3000\u3000\u201c\u5bb9\u82e5\uff0c\u4f60\u597d\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\u5417\uff1f\u201d\u738b\u5983\u95ee\u3002 \u3000\u3000\u201c\u4e09\u59b9\u59b9\u5df2\u7ecf\u6b7b\u4e86\uff01\u201d\u7eb3\u5170\u5bb9\u82e5\u7a81\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u8336\u6c34\u6cfc\u6e85\u5730\u4e0a\uff0c\u4ee5\u6fc0\u52a8\u7684\u58f0\u8c03\u62a5\u544a\u4e86\u8fd9 \u4e2a\u5669\u8017\uff01 \u3000\u3000\u201c\u4e09\u516c\u4e3b\u6b7b\u4e86\uff1f\u201d\u738b\u5983\u6728\u7136\u5730\u53cd\u95ee\u4e86\u4e00\u53e5\uff0c\u53d1\u5446\u7684\u773c\u775b\u770b\u7740\u7a97\u5916\u3002\u8fd9\u4e2a\u6d88\u606f\u6765\u5f97\u7a81\u7136\uff0c \u53ef\u662f\u6b64\u523b\u5979\u7684\u5fc3\u5934\u662f\u5df2\u7ecf\u591f\u6c89\u91cd\u7684\u4e86\uff0c\u518d\u589e\u591a\u4e00\u4efd\u6c89\u91cd\uff0c\u4e5f\u4e0d\u600e\u6837\u663e\u5f97\u51fa\u6765\u4e86\u3002 \u3000\u3000\u201c\u4e09\u59b9\u59b9\u662f\u81ea\u6ea2\u6b7b\u7684\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u4f4e\u6c89\u5730\u8bf4\u9053\u3002 \u3000\u3000\u201c\u81ea\u7f22\u6b7b\u7684\uff1f\u201d\u738b\u5983\u53d1\u7740\u6296\u91cd\u590d\u5730\u8bf4\uff1a\u201c\u4e09\u516c\u4e3b\u4e3a\u4ec0\u4e48\u8981\u81ea\u6740\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u81ea\u6740\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u9053\uff1a\u201c\u662f\u7ed9\u7687\u4e0a\u903c\u6b7b\u7684\uff01\u6211\u731c\uff0c\u4e8b\u60c5\u548c\u5929\u5c71\u90a3\u4e2a\u2018\u5973\u98de\u8d3c\u2019\u6709 \u5173\uff01\u201d\u8bf4\u5230\u201c\u5973\u98de\u8d3c\u201d\u65f6\uff0c\u738b\u5983\u5c16\u53eb\u4e00\u58f0\uff0c\u7eb3\u5170\u5bb9\u82e5\u60ca\u5f02\u5730\u770b\u7740\u5979\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053 \u5417\uff1f\u5c31\u5728\u4f60\u5165\u5bab\u89c1\u7687\u4e0a\u90a3\u5929\uff0c\u5bab\u4e2d\u7ed9\u4e00\u4e2a\u5973\u4fa0\u95f9\u5f97\u4e0d\u4ea6\u4e50\u4e4e\uff0c\u7687\u4e0a\u4e00\u4e2a\u4eb2\u4fe1\u536b\u58eb\u7ed9\u6740\u6b7b\u4e86\uff0c \u8fd8\u6709\u4e24\u4eba\u7ed9\u6bd2\u7802\u5b50\u6253\u6655\u4e86\uff0c\u6551\u6cbb\u4e0d\u53ca\uff0c\u540e\u6765\u4e5f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u738b\u5983\u5fc3\u4e2d\u4e86\u7136\uff0c\u77e5\u9053\u8fd9\u4e2a\u201c\u5973\u4fa0\u201d\u4e00\u5b9a\u662f\u968f\u81ea\u5df1\u51fa\u5bab\u7684\u90a3\u4e2a\u201c\u5bab\u5a25\u201d\uff0c\u81ea\u5df1\u7684\u5973\u513f\u7684\u597d \u53cb\u3002\u5979\u5f88\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u7eb3\u5170\u5bb9\u82e5\u79f0\u5979\u4e3a\u201c\u5973\u4fa0\u201d\uff0c\u5374\u79f0\u81ea\u5df1\u7684\u5973\u513f\u201c\u5973\u98de\u8d3c\u201d\uff0c\u63d2\u53e3\u95ee\u9053\uff1a \u201c\u4f60\u600e\u4e48\u77e5\u9053\u5979\u662f\u5973\u4fa0\uff1f\u201d \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u51c4\u7136\u5730\u671b\u7740\u738b\u5983\uff0c\u7a81\u7136\u7528\u4e00\u79cd\u6025\u4fc3\u7684\u58f0\u8c03\u8bf4\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u54b1\u4eec\u59d1\u4f84\u662f\u65e0\u8bdd\u4e0d \u8c08\uff0c\u90a3\u4e2a\u5973\u4fa0\u662f\u6211\u628a\u5979\u5e26\u8fdb\u5bab\u7684\uff0c\u5979\u53eb\u505a\u5192\u6d63\u83b2\uff0c\u8fd8\u662f\u8463\u9102\u5983\u4ee5\u524d\u7684\u5973\u513f\u5462\uff0c\u60f3\u4e0d\u5230\u6211\u5e26\u5979 \u8fdb\u5bab\uff0c\u5374\u5bb3\u4e86\u4e09\u59b9\u59b9\uff01\u201d \u3000\u3000\u201c\u59d1\u59d1\uff0c\u8bf7\u6055\u6211\u83bd\u649e\u95ee\u4f60\uff0c\u90a3\u5173\u5728\u5929\u7262\u4e2d\u7684\u2018\u5973\u98de\u8d3c\u2019\uff0c\u662f\u4e0d\u662f\u4f60\u4e00\u4e2a\u81f3\u4eb2\u81f3\u8fd1\u7684 \u4eba\uff1f\u201d \u3000\u3000\u738b\u5983\u4e00\u9635\u75c9\u631b\uff0c\u8bb8\u4e45\u8bb8\u4e45\uff0c\u624d\u62ac\u8d77\u5934\u6765\uff0c\u4f4e\u58f0\u7684\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u6211\u4e0d\u7528\u7792\u4f60\u4e86\uff0c\u5979\u662f\u6211\u7684 \u5973\u513f\uff01\u201d \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u770b\u5f97\u51fa\u6765\uff01\u59d1\u59d1\uff0c\u6211\u4eec\u751f\u5728\u7687\u5bb6\uff0c\u771f\u662f\u4e00\u79cd\u7f6a\u5b7d\uff01\u4e09\u59b9\u59b9\u7684\u6b7b \u4e5f\u662f\u4e00\u79cd\u60c5\u5b7d\uff01\u201d \u3000\u3000\u738b\u5987\u8138\u4e0a\u7684\u808c\u8089\u53ef\u6015\u5730\u62bd\u6410\u8d77\u6765\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u60c5\u5b7d\uff01\u60c5\u5b7d\uff1f\u201d \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u907f\u5f00\u4e86\u59d1\u59d1\u7684\u76ee\u5149\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u7684\uff0c\u60c5\u5b7d\u3002\u90a3\u4e2a\u5973\u98de\u8d3c\uff0c\u4e0d\uff0c\u5979\u4e0d\u662f\u5973\u98de\u8d3c\uff0c \u5979\u662f\u4f60\u7684\u5973\u513f\uff0c\u6211\u7684\u8868\u59b9\u3002\u8868\u59b9\u6709\u4e00\u4e2a\u610f\u4e2d\u4eba\u53eb\u5f20\u534e\u662d\uff0c\u60f3\u628a\u5979\u6551\u51fa\u6765\u3002\u800c\u4e09\u59b9\u59b9\u504f\u504f\u5c31\u7231 \u4e0a\u8868\u59b9\u7684\u610f\u4e2d\u4eba\uff01\u201d \u3000\u3000\u8fd9\u4ef6\u4e8b\u5728\u738b\u5983\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u5230\uff0c\u867d\u7136\u5979\u81ea\u89c9\u5df2\u8d70\u5230\u751f\u547d\u7684\u5c3d\u5934\uff0c\u4f46\u5bf9\u4e8e\u5973\u513f\u7684\u4e8b\u60c5\u8fd8\u662f \u6e34\u671b\u77e5\u9053\uff0c\u5979\u7a81\u7136\u53d8\u5f97\u5174\u594b\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\uff0c\u4f60\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u4f4e\u4f4e\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u95ee\u4e86\uff0c\u4e00\u4e0b\u5b50\u4e5f\u8bf4\u4e0d\u6e05\u695a\u3002\u6211\u5148\u544a\u8bc9\u4f60\u4e09\u59b9\u59b9 \u662f\u600e\u6837\u6b7b\u7684\u5427\u3002\u201d \u3000\u3000\u201c\u5192\u6d63\u83b2\u59d1\u5a18\u5927\u95f9\u7687\u5bab\u4e4b\u540e\uff0c\u7687\u4e0a\u53d1\u73b0\u5931\u4e86\u6731\u679c\u91d1\u7b26\u3002\u8fd9\u91d1\u7b26\u53ef\u7edd\u4e0d\u662f\u5916\u4eba\u5077\u5f97\u4e86\u7684\uff0c \u7687\u4e0a\u7a81\u7136\u60f3\u8d77\u6d63\u83b2\u59d1\u5a18\u4f2a\u88c5\u5bab\u5a25\u968f\u4f60\u51fa\u5bab\u65f6\uff0c\u4e09\u59b9\u59b9\u66fe\u62c9\u7740\u5979\u7684\u624b\u548c\u5979\u4eb2\u4eb2\u70ed\u70ed\u5730\u8bf4\u4e86\u51e0\u53e5 \u8bdd\uff0c\u5927\u8d77\u7591\u5fc3\uff0c\u5c31\u53eb\u592a\u76d1\u4f20\u5979\u6765\u95ee\u8bdd\u3002\u4e09\u59b9\u59b9\u5bf9\u6765\u4f20\u5979\u7684\u592a\u76d1\u8bf4\uff1a\u2018\u4f60\u4eec\u4e14\u7a0d\u7b49\u4e00\u4f1a\u513f\uff0c\u5f85 \u6211\u6362\u8fc7\u5986\u5c31\u6765\u3002\u2019\u60f3\u4e0d\u5230\u5979\u5c31\u8fd9\u6837\u5728\u5bdd\u5bab\u81ea\u7f22\u6b7b\u4e86\u3002\u201d \u3000\u3000\u738b\u5983\u53eb\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u90a3\u6731\u679c\u91d1\u7b26\u662f\u4e09\u516c\u4e3b\u5077\u7684\uff01\u201d \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u9053\uff1a\u201c\u662f\u7684\uff0c\u5979\u4e3a\u4e86\u81ea\u5df1\u6240\u7231\u7684\u4eba\uff0c\u727a\u7272\u4e86\u81ea\u5df1\uff01\u201d \u3000\u3000\u738b\u5983\u70ed\u6cea\u76c8\u7736\uff0c\u5782\u4e0b\u5934\u53bb\uff0c\u6376\u80f8\u8bf4\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u867d\u662f\u6df1\u5b98\u5f31\u8d28\uff0c\u5374\u751f\u5c31\u4fa0\u9aa8\u67d4\u80a0\uff0c\u6bd4\u6211 \u90a3\u53ef\u662f\u8981\u5f3a\u5343\u500d\u4e07\u500d\uff01\u201d \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u6cdb\u7136\u800c\u4f4d\uff0c\u54d1\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u966a\u7687\u4e0a\u5728\u5357\u4e66\u623f\u8bfb\u4e66\uff0c\u5185\u76d1\u6765\u62a5\uff0c\u8bf4\u662f\u4e09\u516c\u4e3b\u81ea\u7f22 \u6b7b\u4e86\uff0c\u7687\u4e0a\u9762\u8272\u9752\u767d\uff0c\u2018\u54fc\u2019\u4e86\u4e00\u58f0\u51b7\u7b11\u8bf4\u9053\uff1a\u2018\u6d3b\u8be5\uff01\u2019\u6211\u5413\u5f97\u6655\u4e86\uff0c\u60f3\u54ed\u54ed\u4e0d\u51fa\u6765\uff01\u7687 \u4e0a\u5ffd\u7136\u8bf4\u9053\uff1a\u2018\u4f60\u77e5\u9053\u4e09\u4e2b\u5934\u548c\u5916\u81e3\u6709\u4ec0\u4e48\u52fe\u7ed3\uff1f\u2019\u6211\u83ab\u540d\u5176\u5999\uff0c\u5fc3\u53c8\u60b2\u75db\uff0c\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u53ea \u662f\u6447\u4e86\u6447\u5934\u3002\u7687\u4e0a\u9053\uff1a\u2018\u8fd9\u4e2b\u5934\u597d\u5927\u80c6\uff0c\u5077\u4e86\u6211\u7684\u6731\u679c\u91d1\u7b26\uff0c\u6211\u53ea\u9053\u5979\u60f3\u505a\u592a\u5e73\u516c\u4e3b\u5462\uff01\u2019 \u592a\u5e73\u516c\u4e3b\u662f\u5510\u671d\u5973\u7687\u5e1d\u6b66\u5219\u5929\u7684\u5973\u513f\uff0c\u66fe\u52fe\u7ed3\u5916\u81e3\uff0c\u62a2\u593a\u7687\u5144\u7684\u6743\u67c4\u3002\u7687\u4e0a\u5f15\u592a\u5e73\u516c\u4e3b\u7684\u6545 \u4e8b\uff0c\u5927\u7ea6\u662f\u4ee5\u4e3a\u4e09\u59b9\u59b9\u5077\u4ed6\u7684\u6731\u679c\u91d1\u7b26\uff0c\u4e00\u5b9a\u5305\u853d\u6709\u62a2\u593a\u671d\u653f\u7684\u91ce\u5fc3\uff0c\u4ed6\u53c8\u54ea\u91cc\u77e5\u9053\u5176\u4e2d\u6709 \u8fd9\u6837\u590d\u6742\u7684\u4e8b\uff1f\u5927\u62b5\u505a\u7687\u5e1d\u7684\u4eba\uff0c\u51e1\u4e8b\u90fd\u4f1a\u731c\u7591\uff0c\u4ee5\u81f3\u60f3\u5f97\u5b8c\u5168\u4e0d\u8fd1\u60c5\u7406\u3002\u6211\u9053\uff1a\u2018\u4e09\u516c\u4e3b \u548c\u6211\u7d20\u6765\u53cb\u597d\uff0c\u6211\u77e5\u9053\u5979\u4ece\u6765\u4e0d\u7ba1\u5916\u4e8b\uff0c\u54ea\u4f1a\u52fe\u7ed3\u5ef7\u81e3\uff1f\u2019\u7687\u4e0a\u51b2\u7740\u6211\u7b11\u9053\uff1a\u2018\u5bb9\u82e5\uff0c\u6211\u76f8 \u4fe1\u4f60\u4e0d\u4f1a\u9a97\u6211\uff01\u2019\u6c89\u541f\u4e86\u534a\u664c\uff0c\u53c8\u9053\uff1a\u2018\u4e5f\u7f62\uff0c\u5bb6\u4e11\u4e0d\u5b9c\u5916\u626c\uff0c\u4f60\u5c31\u66ff\u6211\u53bb\u7ea6\u675f\u5185\u5ef7\uff0c\u4efb\u4f55 \u4eba\u90fd\u4e0d\u51c6\u628a\u6d88\u606f\u6cc4\u6f0f\uff0c\u5e76\u4ee3\u6211\u4e3b\u6301\uff0c\u628a\u8fd9\u4e2b\u5934\u6536\u6b93\u4e86\u5427\u3002\u2019\u6211\u5230\u4e86\u4e09\u59b9\u59b9\u4f4f\u7684\u666f\u9633\u5bab\uff0c\u628a\u4e09 \u59b9\u59b9\u89e3\u4e86\u4e0b\u6765\uff0c\u53ea\u89c1\u5979\u4e66\u6848\u4e0a\u8fd8\u6709\u4e00\u7eb8\u8bcd\u7b3a\uff0c\u4e00\u4e0a\u9762\u5199\u6709\u4e24\u53e5\u8bcd\uff1a\u2018\u98ce\u7d6e\u98d8\u6b8b\u5df2\u5316\u840d\uff0c\u6ce5\u83b2 \u521a\u5029\u85d5\u4e1d\u8426\u3002\u2019\u5979\u6700\u8fd1\u8ddf\u6211\u5b66\u8bcd\uff0c\u5927\u7ea6\u662f\u8fd8\u672a\u586b\u5b8c\uff0c\u5c31\u81ea\u7f22\u6b7b\u4e86\u3002\u201d \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u5477\u4e86\u4e00\u53e3\u9999\u8336\uff0c\u53c8\u9053\uff1a\u201c\u7687\u4e0a\u53c8\u95ee\u6211\uff0c\u77e5\u4e0d\u77e5\u9053\u6709\u4eba\u62ff\u6731\u679c\u91d1\u7b26\u53bb\u6551\u5929\u7262\u5973\u8d3c \u7684\u4e8b\uff0c\u6211\u8bf4\u4e0d\u77e5\u9053\u3002\u7687\u4e0a\u9053\uff1a\u2018\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u592a\u8fc7\u79bb\u5947\u4e86\uff0c\u81ea\u5df1\u4eba\u4e5f\u9760\u4e0d\u4f4f\uff0c\u6211\u5e94\u8be5\u597d\u597d\u67e5\u4e00 \u67e5\uff01\u2019\u59d1\u59d1\uff0c\u4f60\u7684\u884c\u900a\u53ef\u5f97\u68c0\u70b9\u4e00\u4e9b\uff0c\u7ed9\u7687\u4e0a\u770b\u51fa\uff0c\u90a3\u5c31\u4e0d\u597d\u4e86\uff01\u201d \u3000\u3000\u738b\u5983\u51c4\u7136\u7b11\u9053\uff1a\u201c\u6211\u73b0\u5728\u8fd8\u6015\u4ec0\u4e48\uff1f\u5bb9\u82e5\uff0c\u4f60\u56de\u5bab\u53bb\u5427\uff0c\u7687\u4e0a\u82e5\u95ee\u8d77\u6211\uff0c\u4f60\u5c31\u8bf4\u4e0d\u77e5\u9053 \u597d\u4e86\uff01\u201d\u7eb3\u5170\u5bb9\u82e5\u671b\u7740\u738b\u5983\uff0c\u5fc3\u5934\u611f\u5230\u4e00\u9635\u9635\u5bd2\u51b7\uff0c\u6325\u6cea\u8bf4\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u90a3\u4e48\u6211\u53bb\u4e86\uff01\u201d\u738b \u5983\u5ffd\u7136\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u4ee5\u524d\u6bcf\u6b21\u6765\uff0c\u90fd\u4f1a\u7ed9\u6211\u5e26\u6765\u4e00\u4e24\u9996\u65b0\u95fb\uff0c\u53ea\u6015\u6211\u4ee5\u540e\u518d\u4e0d\u80fd\u8bfb \u4e86\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u60ca\u95ee\u9053\uff1a\u201c\u59d1\u59d1\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u738b\u5983\u65ad\u65ad\u7eed\u7eed\u5730\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u563f\uff0c\u751f\u5728\u7687\u5bb6\u5c31 \u662f\u4e00\u79cd\u7f6a\u5b7d\uff01\u5bb9\u82e5\uff0c\u4f60\u518d\u66ff\u6211\u7559\u4e00\u4e24\u9996\u8bcd\uff0c\u5c31\u5199\u5199\u6211\u4eec\u7684\u60b2\u75db\u5427\uff01\u201d \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u6cea\u54bd\u5fc3\u9178\uff0c\u9ed8\u7136\u4e0d\u8bed\uff0c\u84e6\u5730\u6293\u8d77\u4e86\u7b14\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u5c31\u66ff\u4e09\u59b9\u59b9\u7eed\u6210\u90a3\u9996 \u8bcd\uff0c\u53e6\u5916\u518d\u9001\u4e00\u9996\u7ed9\u5979\uff01\u201d\u4ed6\u7684\u773c\u6cea\u70b9\u70b9\u6ef4\u5728\u8bcd\u7b3a\u4e0a\uff0c\u970e\u5ffd\u5199\u6210\u4e24\u9996\uff0c\u6cea\u75d5\u6df7\u7740\u58a8\u8ff9\uff0c\u5b57\u4f53 \u6f66\u8349\u6a21\u7cca\u3002\u738b\u5983\u8270\u8f9b\u5730\u8bfb\u9053\uff1a \u3000\u3000\u201c\u98ce\u7d6e\u98d8\u6b8b\u5df2\u5316\u840d\uff0c\u6ce5\u83b2\u521a\u5029\u85d5\u4e1d\u8426\u3002\u73cd\u91cd\u522b\u62c8\u9999\u4e00\u74e3\uff0c\u8bb0\u524d\u751f\uff01\u4eba\u5230\u60c5\u591a\u60c5\u8f6c\u8584\uff0c\u800c \u4eca\u771f\u4e2a\u6094\u591a\u60c5\u3002\u53c8\u5230\u65ad\u80a0\u56de\u9996\u5904\uff0c\u6cea\u5077\u96f6\uff01\u201d \u3000\u3000\u201c\u66f2\u5f84\u6df1\u5bab\u5e1d\u5b50\u5bb6\uff0c\u5267\u601c\u7389\u9aa8\u59d4\u5c18\u6c99\u3002\u6101\u5411\u98ce\u524d\u65e0\u5904\u8bf4\uff0c\u6570\u5f52\u9e26\u3002\u534a\u4e16\u6d6e\u840d\u968f\u901d\u6c34\uff0c\u4e00 \u5bb5\u51b7\u96e8\u4e27\u540d\u82b1\uff0c\u9b42\u662f\u67f3\u7ef5\u5439\u6b32\u788e\uff0c\u7ed5\u5929\u6daf\uff01\u201d \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u63b7\u7b14\u51c4\u7b11\uff0c\u738b\u5983\u76ee\u9001\u7740\u4ed6\u7684\u80cc\u5f71\u8d70\u4e0b\u697c\u68af\uff0c\u597d\u50cf\u4ec0\u4e48\u77e5\u89c9\u90fd\u6ca1\u6709\u4e86\uff01 \u3000\u3000\u518d\u8bf4\u90a3\u665a\u5927\u95f9\u5929\u7262\u4e4b\u540e\uff0c\u51cc\u672a\u98ce\u4e0e\u98de\u7ea2\u5dfe\u4ed7\u7edd\u9876\u8f7b\u529f\uff0c\u9003\u51fa\u9669\u5730\u3002\u51cc\u672a\u98ce\u518d\u7533\u524d\u8bf7\uff0c\u8bf7 \u98de\u7ea2\u5dfe\u548c\u4ed6\u4e00\u9053\uff0c\u53bb\u89c1\u6613\u5170\u73e0\u90a3\u5e2e\u670b\u53cb\u3002\u98de\u7ea2\u5dfe\u4ecd\u662f\u6447\u5934\uff0c\u51cc\u672a\u98ce\u518d\u95ee\u98de\u7ea2\u5dfe\u4f4f\u5728\u4f55\u5730\uff0c\u98de \u7ea2\u5dfe\u53c8\u662f\u4e0d\u7b54\u3002\u51cc\u672a\u98ce\u5fc3\u5185\u751f\u6c14\uff0c\u60f3\u9053\uff1a\u6211\u656c\u91cd\u4f60\u662f\u524d\u8f88\u5973\u4fa0\uff0c\u53c8\u662f\u5e08\u5144\u7684\u597d\u53cb\uff0c\u4f60\u5374\u8fd9\u4e48 \u4e0d\u8fd1\u4eba\u60c5\uff01\u98de\u7ea2\u5dfe\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u51cc\u672a\u98ce\uff0c\u6211\u4f4f\u7684\u5730\u65b9\u4e0d\u80fd\u544a\u8bc9\u4f60\uff0c\u4f60\u6709\u672c\u4e8b\u5c31\u81ea\u5df1\u5bfb\u6765\uff0c\u6211 \u5931\u966a\u4e86\uff01\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u5b9b\u5982\u6d77\u71d5\u63a0\u6ce2\uff0c\u6d41\u661f\u98de\u6e21\uff0c\u4e00\u56e2\u767d\u5f71\uff0c\u8863\u8882\u5fbd\u98d8\uff0c\u500f\u5ffd\u8fc7\u4e86\u51e0\u6761\u8857\u3002 \u51cc\u672a\u98ce\u7ec6\u5473\u8bed\u6c14\uff0c\u597d\u50cf\u98de\u7ea2\u5dfe\u662f\u6709\u610f\u53eb\u4ed6\u8ddf\u8e2a\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u6211\u5c31\u8ffd\u4e0d\u4e0a\u4f60\uff01\u201d\u4e00\u63d0\u6c14\uff0c\u4e5f \u5c55\u5f00\u4e86\u201c\u516b\u6b65\u8d76\u8749\u201d\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u7d27\u7d27\u8ddf\u5728\u98de\u7ea2\u5dfe\u8eab\u540e\uff0c\u98de\u7ea2\u5dfe\u6545\u610f\u5f53\u4f5c\u4e0d\u77e5\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c \u53ea\u662f\u4e00\u5473\u5954\u8dd1\u3002 \u3000\u3000\u9010\u7535\u5954\u96f7\uff0c\u98ce\u751f\u4e24\u814b\uff0c\u4e8c\u4eba\u529f\u592b\uff0c\u7adf\u662f\u534a\u65a4\u516b\u4e24\uff0c\u98de\u7ea2\u5dfe\u5360\u4e86\u5148\u8d77\u6b65\u7684\u4fbf\u5b9c\uff0c\u59cb\u7ec8\u9886\u5148 \u5341\u4e08\u516b\u4e08\u3002\u51cc\u672a\u98ce\u7edd\u9876\u529f\u592b\uff0c\u4e5f\u4e0d\u7531\u5f97\u4e0d\u6697\u6697\u4f69\u670d\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5979\u548c\u5927\u5e08\u5144\u5f53\u5e74\u5e76\u79f0\u585e \u5916\u5947\u4fa0\uff01\u201d\u7ea6\u83ab\u8fc7\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u4e24\u4eba\u5df2\u51fa\u5230\u90ca\u5916\uff0c\u51cc\u672a\u98ce\u770b\u7740\u98de\u7ea2\u5dfe\u5f84\u671d\u897f\u5c71\u5954\u8d70\uff0c\u5c71\u9053\u8fc2 \u56de\u76d8\u66f2\uff0c\u8f6c\u4e86\u51e0\u8f6c\uff0c\u7adf\u7136\u5931\u4e86\u98de\u7ea2\u5dfe\u7684\u5f71\u5b50\u3002 \u3000\u3000\u51cc\u672a\u98ce\u505c\u6b65\u56db\u53a9\uff0c\u53ea\u89c1\u5c71\u5cf0\u56f4\u7ed5\uff0c\u96fe\u9501\u4e91\u5c01\uff0c\u4eba\u5df2\u5728\u534a\u5c71\u4e4b\u4e0a\uff0c\u5fc3\u60f3\uff1a\u5979\u5f15\u6211\u6765\u8fd9\u91cc\u505a \u751a\uff1f\u96be\u9053\u5979\u771f\u662f\u4f4f\u5728\u897f\u5c71\u4e4b\u4e0a\uff1f\u6b63\u601d\u7591\u95ee\uff0c\u5de6\u4e0a\u65b9\u4e00\u9635\u6e05\u8106\u7684\u7b11\u58f0\uff0c\u968f\u98ce\u98d8\u4e0b\uff0c\u51cc\u672a\u98ce\u8eab\u5f62 \u4e00\u62d4\uff01\u811a\u70b9\u82cd\u82d4\uff0c\u624b\u6500\u7edd\u58c1\uff0c\u6377\u4f3c\u7075\u733f\uff0c\u970e\u5ffd\u5230\u4e86\u4e0a\u9762\uff0c\u5ffd\u89c9\u638c\u98ce\u98d2\u7136\uff0c\u4e0a\u9762\u65e9\u4f0f\u6709\u4e00\u6761\u8499 \u9762\u5927\u6c49\uff0c\u53cc\u638c\u98de\u626c\uff0c\u7a81\u65bd\u6251\u51fb\u3002\u51cc\u672a\u98ce\u5927\u6012\uff0c\u4e00\u51fa\u624b\u201c\u98ce\u5377\u843d\u82b1\u201d\u5de6\u638c\u4e00\u62d4\uff0c\u77f3\u638c\u659c\u5288\uff0c\u90a3 \u4eba\u5fae\u5fae\u4e00\u4fa7\uff0c\u4fbf\u95ea\u5f00\u4e86\u3002\u51cc\u672a\u98ce\u609a\u7136\u4e00\u60ca\uff1a\u8fd9\u4eba\u8eab\u6cd5\u597d\u5feb\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u4e00\u632b\u8eab\u4e00\u7ffb\u638c\uff0c\u53cd\u624b \u5288\u53bb\uff0c\u90a3\u4eba\u53cc\u638c\u4e00\u5408\uff0c\u5f80\u5916\u4e00\u5206\uff0c\u53c8\u628a\u653b\u52bf\u89e3\u5f00\uff0c\u8eab\u5f62\u6b6a\u6b6a\u659c\u659c\uff0c\u5ffd\u7136\u638c\u5288\u6307\u6233\uff0c\u62a2\u653b\u8fc7 \u6765\uff0c\u8eab\u6cd5\u624b\u6cd5\u6b65\u6cd5\u65e0\u4e00\u4e0d\u602a\uff0c\u51cc\u672a\u98ce\u7adf\u662f\u524d\u6240\u672a\u89c1\u3002 \u3000\u3000\u90a3\u4eba\u8fde\u53d1\u516d\u8bb0\u602a\u62db\uff0c\u9976\u662f\u51cc\u672a\u98ce\u6b66\u529f\u6df1\u6e5b\uff0c\u638c\u6cd5\u7cbe\u5999\uff0c\u4e5f\u53ea\u597d\u56de\u62f3\u81ea\u536b\u3002\u51cc\u672a\u98ce\u4e00\u58f0\u4e0d \u542d\uff0c\u6697\u6697\u7eb3\u95f7\uff0c\u53ea\u662f\u90a3\u4eba\u62db\u6570\u751a\u602a\uff0c\u529f\u529b\u5374\u5dee\uff0c\u5341\u6570\u62db\u4e00\u8fc7\uff0c\u51cc\u672a\u98ce\u5df2\u770b\u51fa\u4ed6\u7684\u7f3a\u70b9\uff0c\u638c\u8fed \u4e00\u53d8\uff0c\u5ffd\u62f3\u5ffd\u638c\uff0c\u547c\u547c\u5e26\u98ce\uff0c\u76f4\u5982\u5de8\u65a7\u5f00\u5c71\uff0c\u94c1\u9524\u51ff\u77f3\uff0c\u90a3\u4eba\u4e0d\u6562\u786c\u63a5\uff0c\u8fde\u8fde\u540e\u9000\u3002\u800c\u66f4\u5947 \u7684\u662f\uff0c\u90a3\u4eba\u5f00\u9996\u7684\u638c\u6cd5\u795e\u5999\u5f02\u5e38\uff0c\u4f46\u5341\u6570\u62db\u4e4b\u540e\u6253\u4e0d\u5230\u654c\u4eba\uff0c\u4fbf\u7834\u7efd\u9891\u751f\uff0c\u7adf\u662f\u864e\u5934\u86c7\u5c3e\u3002 \u51cc\u672a\u98ce\u54c8\u54c8\u5927\u7b11\uff0c\u632f\u81c2\u4e00\u63a0\uff0c\u4ece\u4ed6\u5934\u9876\u8df3\u8fc7\uff0c\u56de\u8eab\u5c01\u4f4f\u4e86\u4ed6\u7684\u9000\u8def\uff0c\u6b63\u60f3\u628a\u4ed6\u51fb\u5012\uff1b\u5176\u65f6\u4e24 \u4eba\u5df2\u6253\u5230\u7a0d\u4e3a\u5f00\u65f7\u4e4b\u5730\uff0c\u6708\u5149\u7167\u5f71\uff0c\u51cc\u672a\u98ce\u4e00\u638c\u6253\u51fa\uff0c\u5ffd\u5730\u6536\u56de\uff0c\u8fd9\u4eba\u7684\u8eab\u6750\u7adf\u50cf\u81ea\u5df1\u7684\u719f \u4eba\uff01\u6b63\u5f85\u559d\u95f4\uff0c\u90a3\u4eba\u4e00\u63d6\u5230\u5730\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u5230\u5e95\u8fd8\u662f\u4f60\u529f\u592b\u9ad8\uff01\u201d\u9762\u4e2d\u4e00\u63ed\uff0c\u51cc \u672a\u98ce\u559c\u5f97\u53eb\u51fa\u58f0\u6765\uff0c\u8fd9\u4eba\u7adf\u662f\u5f53\u5e74\u8d1f\u6c14\u51fa\u8d70\uff0c\u81ea\u5df1\u548c\u5218\u90c1\u82b3\u56db\u89c5\u65e0\u8e2a\u7684\u97e9\u5fd7\u90a6\u3002 \u3000\u3000\u6811\u6797\u91cc\u4e00\u58f0\u957f\u5578\uff0c\u98de\u7ea2\u5dfe\u84e6\u73b0\u8eab\u5f62\uff0c\u7b11\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u4f60\u8fd8\u607c\u6211\u4e48\uff1f\u8981\u4e0d\u662f\u97e9\u5927\u54e5\u8bf4\u4f60 \u662f\u4ed6\u7684\u597d\u53cb\uff0c\u6211\u8fd8\u4e0d\u6562\u5f15\u4f60\u6765\u3002\u201d\u97e9\u5fd7\u90a6\u633d\u7740\u51cc\u672a\u98ce\uff0c\u8bf4\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u8fd8\u6709\u51e0\u4f4d\u670b\u53cb\u7b49\u770b \u89c1\u4f60\u3002\u201d\u5e26\u7740\u51cc\u672a\u98ce\u7a7f\u4eba\u5bc6\u6797\uff0c\u5bc6\u6797\u4e2d\u6709\u4e00\u95f4\u5c0f\u5c0f\u7684\u5bfa\u9662\uff0c\u97e9\u5fd7\u90a6\u62cd\u4e86\u4e09\u4e0b\u5bfa\u95e8\uff0c\u53eb\u9053\uff1a \u201c\u8001\u670b\u53cb\u6765\u4e86\uff01\u201d\u5bfa\u95e8\u500f\u5730\u6253\u5f00\uff0c\u91cc\u9762\u6709\u4e03\u516b\u4e2a\u5587\u561b\u548c\u5341\u591a\u4e2a\u54c8\u8428\u514b\u4eba\uff0c\u9ad8\u9ad8\u77ee\u77ee\u7684\u6324\u6ee1\u4e00 \u5730\u3002\u5587\u561b\u4e2d\u51cc\u672a\u98ce\u8ba4\u5f97\u4e00\u4e2a\u5b97\u8fbe\xb7\u5b8c\u771f\uff0c\u4e43\u662f\u5f53\u65e5\u62a4\u9001\u820d\u5229\u5b50\u5165\u85cf\u7684\u4eba\uff1b\u800c\u54c8\u8428\u514b\u4eba\u4e2d\uff0c\u66f4 \u6709\u4e00\u534a\u4ee5\u4e0a\u662f\u4ed6\u65e7\u65e5\u7684\u6218\u53cb\uff0c\u5927\u5bb6\u76f8\u89c1\uff0c\u6b22\u559c\u4e4b\u60c5\uff0c\u6ea2\u4e8e\u8a00\u8868\u3002\u51cc\u672a\u98ce\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u4e07 \u91cc\u8fe2\u8fe2\u4ece\u585e\u5916\u6765\u5230\u4eac\u5e08\uff1f\u201d\u97e9\u5fd7\u90a6\u6c89\u541f\u534a\u664c\uff0c\u7b11\u9053\uff1a\u201c\u51cc\u5927\u9655\uff0c\u4f60\u4e0d\u662f\u5916\u4eba\uff0c\u4e0d\u59a8\u5bf9\u4f60\u76f4 \u8bf4\u3002\u201d\u7528\u773c\u4e00\u8198\u5b97\u8fbe\xb7\u5b8c\u771f\uff0c\u5b97\u8fbe\xb7\u5b8c\u771f\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u5f53\u65e5\u62a2\u6551\u820d\u5229\u5b50\uff0c\u51cc\u5927\u4fa0\u820d\u547d\u76f8\u52a9\uff0c \u6b64\u6069\u6b64\u5fb7\uff0c\u6211\u4eec\u662f\u6c38\u4e16\u4e0d\u5fd8\uff0c\u97e9\u5927\u4fa0\u4f46\u8bf4\u5143\u59a8\u3002\u201d\u51cc\u672a\u98ce\u89c1\u6b64\u60c5\u5f62\uff0c\u5fc3\u60f3\uff1a\u83ab\u975e\u662f\u4ed6\u4eec\u673a\u5bc6 \u4e4b\u4e8b\uff0c\u81ea\u5df1\u5012\u4e0d\u4fbf\u63d2\u8db3\u5176\u95f4\u3002\u6b63\u60f3\u8bf4\u8bdd\uff0c\u97e9\u5fd7\u90a6\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4eec\u6545\u4f5c\u795e\u79d8\uff0c\u800c\u662f\u4e8b\u5173\u897f\u85cf\u7684 \u5927\u4e8b\u3002\u51cc\u5927\u4fa0\u53ef\u77e5\u8fbe\u8d56\u6d3b\u4f5b\u6d3e\u4e86\u7279\u4f7f\u6765\u4eac\u4e4b\u4e8b\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u524d\u65e5\u521a\u5230\u6740\u5e08\uff0c\u5fd9\u4e8e\u6551 \u4eba\uff0c\u6839\u672c\u4e0d\u95fb\u5916\u4e8b\u3002\u201d\u97e9\u5fd7\u90a6\u9053\uff1a\u201c\u5434\u4e09\u6302\u4e3e\u5175\u4e4b\u524d\uff0c\u5df2\u5411\u8fbe\u8d56\u6d3b\u4f5b\u758f\u901a\uff0c\u82e5\u5904\u4e0b\u98ce\uff0c\u4fbf\u8bf7 \u6d3b\u4f5b\u4ee3\u4e3a\u6c42\u548c\uff0c\u6b64\u6b21\u8fbe\u8d56\u7279\u4f7f\u6765\u4eac\uff0c\u4fbf\u662f\u4e3a\u5434\u4e09\u6842\u6c42\u548c\u6765\u7684\u3002\u201d\u51cc\u672a\u98ce\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4 \u9053\uff1a\u201c\u6c42\u548c\u4e4b\u4e8b\uff0c\u6211\u4ee5\u524d\u5728\u4e94\u53f0\u5c71\u8c37\u6551\u51fa\u7ea2\u8863\u5587\u561b\u65f6\uff0c\u4e5f\u66fe\u542c\u4ed6\u9053\u8fc7\u3002\u201d\u97e9\u5fd7\u90a6\u9053\uff1a\u201c\u7ea2\u8863 \u5587\u561b\u6b63\u662f\u6b64\u6b21\u7279\u4f7f\uff0c\u9664\u4e86\u66ff\u5434\u4e09\u6842\u6c42\u548c\u4e4b\u5916\uff0c\u6050\u6015\u8fd8\u4f1a\u8c08\u897f\u85cf\u5185\u9644\u4e4b\u4e8b\u3002\u201d\u51cc\u672a\u98ce\u4e0d\u77e5\u97e9\u5fd7 \u90a6\u540e\u6765\u593a\u83b7\u820d\u5229\u5b50\uff0c\u7ed9\u5587\u561b\u8fce\u5165\u897f\u85cf\u7b49\u60c5\u4e8b\uff0c\u5fc3\u91cc\u6697\u6697\u5947\u602a\uff1a\u4e0d\u77e5\u97e9\u5fd7\u90a6\u4f55\u4ee5\u548c\u4ed6\u4eec\u76f8\u5904\u5f97 \u5982\u6b64\u4e4b\u597d\u3002\u97e9\u5fd7\u90a6\u53c8\u9053\uff1a\u201c\u7ea2\u8863\u5587\u561b\u7387\u9886\u4e86\u4e8c\u4e09\u5341\u4eba\u5165\u4eac\uff0c\u5b97\u8fbe\xb7\u5b8c\u771f\u548c\u54c8\u8428\u514b\u7684\u51e0\u4f4d\u670b \u53cb\uff0c\u968f\u540e\u4e5f\u8ddf\u7740\u6765\u4e86\u3002\u4e0d\u8fc7\uff0c\u6211\u4eec\u4e0d\u613f\u548c\u7ea2\u8863\u5587\u561b\u540c\u4f4f\u5bbe\u9986\u3002\u201d\u98de\u7ea2\u4e2a\u9053\uff1a\u201c\u6211\u662f\u95fb\u77e5\u4eac\u5e08 \u64d2\u4e86\u2018\u5973\u8d3c\u2019\u4e4b\u540e\uff0c\u98de\u7a0b\u8d76\u6765\u7684\u3002\u201d\u51cc\u672a\u98ce\u542c\u4e86\uff0c\u8fd9\u624d\u77e5\u9053\u98de\u7ea2\u5dfe\u8d77\u521d\u4e3a\u4ec0\u4e48\u4e0d\u80af\u5c06\u5730\u5740\u544a \u77e5\uff0c\u6562\u60c5\u5979\u4e0d\u77e5\u9053\u81ea\u5df1\u4e0e\u97e9\u5fd7\u90a6\u7b49\u90fd\u662f\u540c\u751f\u5171\u6b7b\u7684\u670b\u53cb\u3002 \u3000\u3000\u5f53\u65f6\uff0c\u4f17\u4eba\u5c31\u5bdd\u4e4b\u540e\uff0c\u97e9\u5fd7\u90a6\u4e0e\u51cc\u672a\u98ce\u643a\u624b\u5728\u6797\u4e2d\u8e0f\u6708\u540c\u6e38\uff0c\u97e9\u5fd7\u90a6\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u51cc\u5927 \u4fa0\uff0c\u4e24\u5e74\u524d\u6211\u4e0d\u8f9e\u800c\u884c\uff0c\u4f60\u4eec\u4e00\u5b9a\u5f88\u607c\u6211\u5427\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u4eec\u5f53\u65f6\u786e\u662f\u5f88\u9057\u61be\uff0c\u4f46\u4e0d\u662f \u607c\u4f60\u3002\u201d\u97e9\u5fd7\u90a6\u6b49\u7136\u8bf4\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u6709\u4e00\u4ef6\u4e8b\u6211\u5f88\u5bf9\u4e0d\u8d77\u4f60\uff0c\u6211\u66fe\u7ecf\u5ac9\u5992\u8fc7\u4f60\u3002\u201d\u51cc\u672a\u98ce \u7b11\u9053\uff1a\u201c\u90a3\u662f\u4f60\u7684\u8bef\u4f1a\uff0c\u6211\u548c\u5218\u5927\u59d0\u672c\u6765\u5c31\u6ca1\u6709\u4ec0\u4e48\u3002\u201d\u97e9\u5fd7\u90a6\u6447\u6447\u624b\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u7ecf\u8fc7 \u8fd9\u4e24\u5e74\u7684\u78e8\u70bc\uff0c\u6211\u597d\u50cf\u6bd4\u4ece\u524d\u61c2\u4e86\u8bb8\u591a\uff0c\u4e00\u5207\u7f18\u4efd\uff0c\u90fd\u662f\u52c9\u5f3a\u4e0d\u6765\u7684\u3002\u4f60\u548c\u5218\u5927\u59d0\u90fd\u662f\u6211\u6700 \u656c\u7231\u7684\u4eba\uff0c\u5982\u679c\u770b\u5230\u4f60\u4eec\u5728\u4e00\u8d77\uff0c\u6211\u5c31\u4f1a\u611f\u89c9\u5e78\u798f\u4e86\uff01\u201d\u51cc\u672a\u98ce\u5ffd\u7136\u75db\u82e6\u5730\u53eb\u9053\uff1a\u201c\u97e9\u5927 \u54e5\uff0c\u522b\u63d0\u8fd9\u4e2a\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u97e9\u5fd7\u90a6\u60ca\u5f02\u5730\u770b\u7740\u4ed6\uff0c\u8fd9\u65f6\u6708\u4eae\u897f\u6c89\uff0c\u5929\u8272\u5df2\u5c06\u7834\u6653\u4e86\u3002 \u3000\u3000\u51cc\u672a\u98ce\u7761\u4e86\u4e00\u4f1a\uff0c\u7b2c\u4e8c\u65e5\u4e00\u65e9\u8d77\u6765\uff0c\u5374\u4e0d\u89c1\u4e86\u98de\u7ea2\u5dfe\uff0c\u95ee\u8d77\u97e9\u5fd7\u90a6\uff0c\u97e9\u5fd7\u90a6\u4e5f\u4e0d\u77e5\u9053\uff0c \u53ea\u8bf4\uff1a\u201c\u8fd9\u4f4d\u5973\u4fa0\uff0c\u72ec\u6765\u72ec\u5f80\uff0c\u6b66\u529f\u6781\u9ad8\uff0c\u4eba\u53c8\u51b7\u50fb\uff0c\u8c01\u4e5f\u4e0d\u6562\u95ee\u5979\uff0c\u53ea\u6015\u662f\u53c8\u60f3\u6cd5\u6551\u90a3\u5973 \u5b69\u5b50\u4e86\u3002\u201d\u51cc\u672a\u98ce\u6697\u6697\u62c5\u5fc3\uff0c\u5374\u662f\u65e0\u6cd5\u3002\u5f53\u4e0b\u8f9e\u522b\u97e9\u5fd7\u90a6\uff0c\u53bb\u627e\u5192\u6d63\u83b2\u3002\u97e9\u5fd7\u90a6\u542c\u8bf4\u5f53\u65e5\u5927 \u95f9\u4e94\u53f0\u5c71\u7684\u4e00\u73ed\u670b\u53cb\u4e5f\u5230\u4eac\u5e08\uff0c\u5f88\u4e3a\u9ad8\u5174\u3002\u53ea\u662f\u4ecd\u53ee\u5631\u51cc\u672a\u98ce\u6682\u65f6\u4e0d\u8981\u5c06\u4ed6\u7684\u8e2a\u8ff9\u6296\u9732\u51fa \u6765\uff0c\u51cc\u672a\u98ce\u5e94\u5141\u4e86\u3002\uff0c \u3000\u3000\u97e9\u5fd7\u90a6\u6599\u5f97\u4e0d\u9519\uff0c\u98de\u7ea2\u5dfe\u679c\u7136\u662f\u60f3\u6cd5\u6551\u6613\u5170\u73e0\u53bb\u4e86\u3002\u5979\u6e05\u65e9\u8d77\u6765\uff0c\u5728\u897f\u5c71\u4e4b\u5dc5\uff0c\u7ec3\u4e86\u4e00 \u56de\u5251\u6cd5\uff0c\u7ec3\u675f\u505c\u5f53\uff0c\u4e0b\u5c71\u8fdb\u57ce\u3002\u5fc3\u4e2d\u60b2\u6124\uff0c\u90c1\u95f7\u96be\u6d88\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u60f3\u4e0d\u51fa\u6551\u6613\u5170\u73e0\u4e4b\u6cd5\uff0c\u4e00 \u65f6\u95f4\u524d\u5c18\u5f80\u4e8b\u6d8c\u4e0a\u5fc3\u5934\uff0c\u5ffd\u7136\u54ac\u7259\u60f3\u9053\uff1a\u7eb3\u5170\u660e\u6167\u662f\u5979\u7684\u6bcd\u4eb2\uff0c\u82e5\u5979\u4e0d\u80af\u6551\u51fa\u5973\u513f\uff0c\u6211\u5c31\u548c \u5979\u62fc\u4e86\u3002\u4e3b\u610f\u6253\u5b9a\uff0c\u9ec4\u660f\u65f6\u5206\uff0c\u4e00\u4e2a\u4eba\u5077\u5077\u8fdb\u4e86\u738b\u5e9c\u3002 \u3000\u3000\u518d\u8bf4\u738b\u5983\u81ea\u7eb3\u5170\u5bb9\u82e5\u53bb\u540e\uff0c\u5fc3\u4f3c\u6b7b\u7070\uff0c\u4eba\u5982\u69c1\u6728\uff0c\u72ec\u5750\u697c\u4e2d\uff0c\u773c\u524d\u53ea\u89c9\u4e00\u7247\u7070\u6697\u3002\u8fc7\u4e86 \u8bb8\u4e45\u3001\u8bb8\u4e45\uff0c\u624d\u7f13\u7f13\u7ad9\u4e86\u8d77\u6765\uff0c\u7528\u98a4\u6296\u7684\u624b\uff0c\u6293\u8d77\u4e86\u90a3\u67c4\u77ed\u5251\u3002 \u3000\u3000\u201c\u5b9d\u73e0\uff0c\u4e0d\u8981\u602a\u6211\uff01\u4e91\u9aa2\uff0c\u4f60\u7b49\u7740\u6211\uff01\u201d\u738b\u5983\u66ae\u7136\u53eb\u4e86\u51fa\u6765\uff0c\u5012\u8f6c\u5251\u950b\u3002\u5251\u5c16\u5530\u7684\u63d2\u8fdb \u5fc3\u623f\uff0c\u5ffd\u7136\uff0c\u7a97\u95e8\u500f\u5730\u6253\u5f00\uff0c\u4e00\u6761\u4eba\u5f71\uff0c\u75be\u903e\u9e70\u96bc\uff0c\u98de\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u201c\u660e\u6167\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u201d\u4e00\u53cc\u6709\u529b\u7684\u624b\uff0c\u7d27\u7d27\u5730\u6276\u7740\u5979\u3002\u65b0\u6708\u521a\u521a\u722c\u4e0a\u679d\u5934\uff0c\u900f\u8fc7\u78a7\u7eb1\u7a97 \u6237\uff0c\u7167\u7740\u4e24\u4e2a\u7231\u6068\u7ea0\u7ed3\u7684\u5973\u4eba\uff0c\u8fd9\u4e24\u4e2a\u5973\u4eba\uff0c\u9762\u8272\u90fd\u662f\u4e00\u6837\u60e8\u767d\uff01 \u3000\u3000\u201c\u98de\u7ea2\u5dfe\uff0c\u4e0d\u8981\u6068\u6211\uff01\u201d\u738b\u5983\u5583\u5583\u5730\u8bf4\u9053\u3002\u8fd9\u970e\u90a3\u95f4\uff0c\u4e00\u5207\u4ec7\u6068\u5168\u90e8\u5316\u89e3\uff0c\u53f1\u54a4\u8349\u539f\uff0c \u7eb5\u6a2a\u585e\u5916\u7684\u5973\u4fa0\uff0c\u7c41\u7c07\u5730\u843d\u4e0b\u6cea\u6765\uff01 \u3000\u3000\u201c\u98de\u7ea2\u5dfe\uff0c\u6211\u4eec\u90fd\u662f\u6768\u5927\u4fa0\u6700\u4eb2\u5bc6\u7684\u4eba\uff0c\u8ba9\u6211\u4eec\u548c\u89e3\u4e86\u5427\uff01\u59d0\u59d0\uff0c\u4f60\u4e0d\u8ba8\u538c\u6211\u53eb\u4f60\u505a\u59d0 \u59d0\u5427\uff1f\u201d\u738b\u5983\u9762\u8272\u7a81\u8f6c\u6655\u7ea2\uff0c\u5fc3\u623f\u5267\u70c8\u5730\u8df3\u52a8\uff0c\u4e34\u6b7b\u524d\u6781\u5ea6\u7684\u5174\u594b\uff0c\u4f7f\u5979\u89c9\u5f97\u8840\u6db2\u4f3c\u4e4e\u50cf\u98de \u6cc9\u4e00\u6837\u5728\u4f53\u5185\u6d41\u8f6c\u3002 \u3000\u3000\u201c\u660e\u6167\uff0c\u6211\u7684\u59b9\u59b9\uff0c\u6211\u4eec\u4e0d\u662f\u4ec7\u4eba\uff0c\u6211\u4e00\u5b9a\u4f1a\u597d\u597d\u5730\u770b\u5f85\u4f60\u7684\u5973\u513f\uff0c\u820d\u4e86\u6211\u7684\u6027\u547d\uff0c\u6211 \u4e5f\u8981\u6551\u51fa\u5979\uff01\u201d \u3000\u3000\u738b\u5983\u7528\u611f\u6fc0\u7684\u773c\u5149\u770b\u7740\u98de\u7ea2\u5dfe\uff0c\u957f\u957f\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u6c14\u529b\u6e10\u6e10\u6d88\u5931\uff0c\u6323\u624e\u7740\u8bf4\u9053\uff1a\u201c\u59d0 \u59d0\uff0c\u628a\u90a3\u67c4\u77ed\u5251\u62d4\u51fa\u6765\uff0c\u9001\u7ed9\u6211\u7684\u5973\u513f\uff0c\u90a3\u662f\u5979\u7236\u4eb2\u7684\u4e1c\u897f\uff01\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u5168\u8eab\u98a4\u6296\u8d77\u6765\uff0c\u8fd9\u6837\u575a\u5f3a\u7684\u98de\u7ea2\u5dfe\uff0c\u6b64\u523b\u4f53\u9a8c\u4e86\u751f\u5e73\u6700\u6df1\u523b\u7684\u6050\u6016\uff01\u8fd9\u628a\u5251\u63d2\u5f97 \u76f4\u6df1\u5165\u5251\u67c4\uff0c\u7eb5\u6709\u4ed9\u4e39\u5999\u836f\u4e5f\u6551\u4e0d\u4e86\uff0c\u4e00\u62d4\u51fa\u6765\uff0c\u6b7b\u5f97\u66f4\u5feb\u3002\u53ef\u662f\u600e\u80fd\u591f\u4e0d\u62d4\u51fa\u6765\u5462\uff1f\u5979\u6709 \u8d23\u4efb\u8981\u628a\u8fd9\u67c4\u77ed\u5251\u9001\u7ed9\u6768\u4e91\u9aa2\u7684\u5973\u513f\u554a\uff01 \u3000\u3000\u98de\u7ea2\u5dfe\u4eb2\u4e86\u4e00\u4e0b\u738b\u5983\uff0c\u8f7b\u8f7b\u5730\u5728\u5979\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60\u653e\u5fc3\u53bb\u5427\uff01\u201d\u95ed\u4e86\u773c\u775b\uff0c\u6293\u7740 \u5251\u67c4\uff0c\u500f\u7684\u62d4\u4e86\u51fa\u6765\u3002\u6b63\u662f\uff1a\u6069\u6028\u5df2\u968f\u5fc3\u8840\u5c3d\uff0c\u6b7b\u751f\u4e00\u4f8b\u4ed8\u6d6e\u840d\u3002\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de \u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5341\u4e5d\u7ae0\u3000\u751f\u6b7b\u4e24\u96be\u5fd8\u3000\u534a\u4e16\u6d6e\u840d\u968f\u901d\u6c34\u3000\u6069\u4ec7\u7ec8\u89e3\u8131\u3000\u4e00\u5bb5\u51b7\u96e8\u4e27\u540d\u82b1 \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u5341\u4e5d\u56de \u751f\u6b7b\u4e24\u96be\u5fd8\u3000\u534a\u4e16\u6d6e\u840d\u968f\u901d\u6c34 \u6069\u4ec7\u7ec8\u89e3\u8131\u3000\u4e00\u5bb5\u51b7\u96e8\u4e27\u540d\u82b1 \u3000\u3000\u9c9c\u8840\u50cf\u55b7\u6cc9\u4e00\u6837\u98de\u6e85\u51fa\u6765\uff0c\u7eb3\u5170\u738b\u5983\u9893\u7136\u5012\u5728\u5730\u4e0a\uff0c\u4e00\u4ef6\u4e8b\u60c5\u84e6\u5730\u515c\u4e0a\u5fc3\u5934\uff0c\u5728\u8fd9\u5fc3\u810f \u5373\u5c06\u505c\u6b62\u8df3\u52a8\u7684\u65f6\u523b\uff0c\u5979\u62fc\u7740\u6700\u540e\u4e00\u53e3\u6c14\uff0c\u65ad\u65ad\u7eed\u7eed\u6c60\u8bf4\u9053\uff1a\u201c\u660e\u5929\uff03\u745a\u5929\u9ec4\u660f\u65f6\u5206\u2026\u2026\u4ed6 \u4eec\u8981\u62bc\u5b9d\u73e0\uff0c\u62bc\u5b9d\u73e0\u2026\u2026\u5230\u2026\u2026\u5230\u5211\u90e8\u5927\u5802\u4f1a\u5ba1\u3002\u201d\u8bf4\u5b8c\u4e4b\u540e\uff0c\u4e24\u773c\u4e00\u7ffb\uff0c\u5c31\u6b64\u4e00\u7791\u4e0d\u89c6\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u63e1\u7740\u90a3\u67c4\u77ed\u5251\uff0c\u5446\u5446\u5730\u7ad9\u5728\u738b\u5983\u5c38\u65c1\uff0c\u5ffd\u7136\u7a97\u5916\u4e00\u58f0\u72de\u7b11\uff0c\u98de\u7ea2\u5dfe\u77ed\u5251\u5f53\u80f8\u4e00 \u7acb\uff0c\u65cb\u8fc7\u8eab\u6765\uff0c\u53ea\u89c1\u4e09\u4e2a\u591c\u884c\u4eba\uff01\u5df2\u7834\u7a97\u800c\u5165\u3002\u6708\u5149\u4e0b\u770b\u5f97\u5206\u660e\uff0c\u5934\u4e00\u4e2a\u957f\u987b\u5982\u94f6\uff0c\u8eab\u6750\u7626 \u5c0f\uff0c\u4e24\u65c1\u8ddf\u7740\u4e24\u4e2a\u7ea6\u6478\u56db\u4e94\u5341\u5c81\u7684\u6c49\u5b50\uff0c\u4e00\u8fdb\u6765\u89c1\u7740\u6ee1\u5730\u9c9c\u8840\uff0c\u9f50\u58f0\u60ca\u53eb\uff0c\u90a3\u767d\u987b\u8001\u8005\u559d \u9053\uff1a\u201c\u54fc\uff0c\u597d\u5927\u80c6\u7684\u5973\u8d3c\uff0c\u6562\u4f24\u5bb3\u738b\u5983\uff01\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u6ee1\u8154\u90c1\u6012\u6b63\u81ea\u65e0\u5904\u53d1\u6cc4\uff0c\u62d4\u8eab\u4e00\u8038\uff0c\u77ed\u5251\u98de\u5904\uff0c\u4e00\u7f15\u8840\u7ea2\u7684\u5149\u6cfd\uff0c\u5f84\u5411\u8001\u4eba\u5241 \u53bb\uff0c\u90a3\u8001\u4eba\u9971\u8896\u4e00\u62c2\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u7ed9\u523a\u7a7f\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u4f46\u98de\u7ea2\u5dfe\u7684\u5251\u950b\u4e5f\u7ed9\u62c2\u5f97\u6b6a\u8fc7\u4e00\u8fb9\u3002 \u98de\u7ea2\u5dfe\u624b\u5e95\u72e0\u8fa3\u5f02\u5e38\uff0c\u5de6\u638c\u968f\u7740\u5251\u950b\u523a\u51fa\u4e4b\u52bf\uff0c\u500f\u7136\u5288\u51fa\uff0c\u90a3\u8001\u8005\u54a6\u4e86\u4e00\u58f0\uff0c\u53cd\u624b\u4e00\u63a8\uff0c\u98de \u7ea2\u5dfe\u53ea\u89c9\u4e00\u80a1\u5927\u529b\u88ad\u6765\uff0c\u8d81\u52bf\u5411\u524d\u4e00\u51b2\uff0c\u4e24\u6761\u6c49\u5b50\u5200\u5251\u9f50\u4e0b\uff0c\u98de\u7ea2\u5dfe\u77ed\u5251\u6a2a\u6325\uff0c\u53ea\u542c\u5f97\u788e\u91d1 \u5207\u7389\u4e4b\u58f0\uff0c\u632b\u94ec\u4e0d\u7edd\u3002\u98de\u7ea2\u4e2a\u75be\u5982\u95ea\u7535\uff0c\u7a7f\u51fa\u7a97\u6237\uff0c\u81ea\u516d\u5c42\u697c\u98de\u8dc3\u4e0b\u5730\uff0c\u5237\u5237\u4e24\u5251\uff0c\u53c8\u523a\u4f24 \u4e86\u4e24\u540d\u738b\u5e9c\u536b\u58eb\uff0c\u6b63\u8981\u9003\u8d70\uff0c\u5ffd\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u767d\u987b\u8001\u8005\u4ea6\u5df2\u8df3\u4e86\u4e0b\u6765\uff0c\u624b\u6267\u53cc\u5251\uff0c \u62e6\u4f4f\u5979\u7684\u53bb\u8def\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u4e24\u6761\u6c49\u5b50\u4ea6\u5df2\u8dc3\u4e0b\uff0c\u548c\u738b\u5e9c\u7684\u536b\u58eb\u6563\u5728\u56db\u9762\uff0c\u9065\u9065\u91c7\u53d6 \u5305\u56f4\u4e4b\u52bf\uff0c\u4f46\u5374\u5e76\u4e0d\u4e0a\u524d\u3002\u767d\u987b\u8001\u4eba\u7765\u7768\u4f5c\u6001\uff0c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u8d62\u5f97\u6211\u624b\u4e2d\u53cc\u5251\uff0c\u6211\u5c31\u653e\u4f60 \u8fc7\u53bb\u3002\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u51e0\u66fe\u53d7\u8fc7\u5982\u6b64\u8f7b\u89c6\uff0c\u957f\u97ad\u201c\u547c\u201d\u7684\u4e00\u58f0\u6a2a\u626b\u51fa\u53bb\uff0c\u4e25\u4f3c\u7075\u86c7\uff0c\u95ea\u52a8\u4e0d\u5b9a\u3002\u767d\u987b\u8001 \u8005\u559d\u58f0\u201c\u597d\uff01\u201d\u4e00\u4e2a\u76d8\u65cb\uff0c\u62a2\u5230\u98de\u7ea2\u5dfe\u4fa7\u7ffc\uff0c\u53f3\u624b\u5251\u201c\u91d1\u96d5\u5c55\u7fc5\u201d\uff0c\u5f80\u5916\u75be\u5c55\uff0c\u51b7\u68ee\u68ee\u7684\u5251 \u950b\u731b\u524a\u654c\u4eba\u80a9\u81c2\u3002\u98de\u7ea2\u5dfe\u8eab\u6cd5\u5feb\u6781\uff0c\u4e00\u97ad\u53d1\u51fa\uff0c\u65b9\u4f4d\u7acb\u53d8\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u5e94\u62db\u53d1\u62db\uff0c\u53ea\u542c\u5f97\u5f53 \u7684\u4e00\u58f0\uff0c\u53cc\u65b9\u90fd\u9000\u51fa\u51e0\u6b65\u3002\u98de\u7ea2\u5dfe\u53ea\u89c9\u864e\u53e3\u53d1\u70ed\uff0c\u6697\u6697\u5fc3\u60ca\uff0c\u90a3\u8001\u8005\u7684\u5251\u5203\u7ed9\u65a9\u4e86\u4e00\u9053\u7f3a \u53e3\uff0c\u4e5f\u662f\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u53eb\u4e86\u51fa\u6765\uff01 \u3000\u3000\u4e24\u4eba\u518d\u5ea6\u4ea4\u950b\uff0c\u5927\u5bb6\u90fd\u4e0d\u6562\u8f7b\u654c\u3002\u98de\u7ea2\u5dfe\u5c55\u51fa\u5e08\u95e8\u7edd\u6280\uff0c\u5de6\u97ad\u53f3\u5251\uff0c\u653b\u5b88\u76f8\u8fde\u3002\u957f\u97ad\u8d77 \u5904\u5982\u9f99\u86c7\u75be\u821e\uff0c\u77ed\u5251\u76d8\u65cb\u5982\u9e70\u9e64\u56de\u7fd4\uff0c\u62db\u6570\u53d8\u5316\u7e41\u590d\uff0c\u653b\u5b88\u96be\u4ee5\u6349\u6478\u3002\u90a3\u8001\u8005\u5728\u5251\u5149\u97ad\u5f71\u4e2d \u5140\u7136\u4e0d\u60e7\uff0c\u4e24\u67c4\u957f\u5251\uff0c\u970d\u970d\u5c55\u5f00\uff0c\u7adf\u4f3c\u9690\u9690\u5e26\u6709\u98ce\u96f7\u4e4b\u58f0\uff01\u800c\u4e14\u66f4\u602a\u7684\u662f\uff1a\u4ed6\u5de6\u624b\u5251\u548c\u53f3\u624b \u522b\u7684\u8def\u6570\u5168\u7136\u4e0d\u540c\uff0c\u50cf\u98de\u7ea2\u5dfe\u4e00\u6837\uff0c\u62db\u6570\u4e5f\u662f\u53d8\u5316\u7e41\u590d\u4e4b\u6781\uff0c\u4e24\u4eba\u970e\u5ffd\u4e4b\u95f4\uff0c\u5df2\u6597\u4e86\u4e09\u4e94\u5341 \u62db\uff0c\u90a3\u8001\u8005\u5ffd\u5730\u8df3\u51fa\u5708\u5b50\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u5929\u5c71\u8001\u5996\u5a46\u7684\u5f92\u5f1f\uff1f\u201d\u98de\u7ea2\u4e2a\u5927\u6012\uff0c\u5237\u5237\u5237\uff0c \u4e09\u97ad\u8fde\u73af\u731b\u626b\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u6562\u9a82\u6211\u5e08\u7236\uff01\u201d\u8fd9\u65f6\u5979\u4ea6\u5df2\u77e5\u9053\u8fd9\u8001\u8005\u7684\u8eab\u4efd\u4e86\u3002 \u3000\u3000\u8fd9\u767d\u987b\u8001\u8005\u8f88\u4efd\u6781\u9ad8\uff0c\u4ed6\u662f\u957f\u767d\u5c71\u6d3e\u5f00\u5c71\u7956\u5e08\uff0c\u72ec\u521b\u201c\u98ce\u96f7\u5251\u201d\u6cd5\u7684\u9f50\u771f\u541b\uff0c\u95e8\u4e0b\u5f1f\u5b50 \u5f88\u591a\uff0c\u591a\u8bd1\u7684\u5e08\u53d4\u7ebd\u795c\u5362\u548c\u5341\u516b\u5e74\u524d\u5200\u4f24\u51cc\u672a\u98ce\u7684\u90b1\u4e1c\u6d1b\uff0c\u90fd\u662f\u4ed6\u7684\u5f1f\u5b50\u3002\u4e94\u5341\u5e74\u524d\u4ed6\u5230\u56de \u7586\u4e91\u6e38\uff0c\u90a3\u65f6\u4ed6\u4e09\u5341\u5c81\u672a\u5230\uff0c\u98ce\u96f7\u5251\u6cd5\u521a\u521a\u7ec3\u6210\uff0c\u5fc3\u9ad8\u6c14\u50b2\uff0c\u72ec\u4e0a\u5929\u5c71\u53bb\u6253\u6666\u660e\u7985\u5e08\uff0c\u6666\u660e \u7985\u5e08\u5ff5\u4ed6\u4e0d\u8fdc\u4e07\u91cc\u800c\u6765\uff0c\u73b0\u8eab\u76f8\u89c1\uff0c\u548c\u4ed6\u5728\u65e0\u5c71\u7edd\u9876\u8bba\u5251\uff0c\u6666\u660e\u7985\u5e08\u6700\u559c\u6709\u8654\u5fc3\u6bc5\u529b\u7684\u540e \u8f88\uff0c\u8d77\u521d\u5bf9\u4ed6\u975e\u5e38\u4e4b\u597d\uff0c\u79f0\u8d5e\u4ed6\u9053\uff1a\u201c\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u6709\u6b64\u6210\u5c31\uff0c\u5b9e\u5728\u96be\u5f97\u3002\u4f60\u7684\u5251\u6cd5\uff0c\u867d\u6709 \u7f3a\u70b9\uff0c\u5728\u5173\u5916\u60f3\u4e5f\u65e0\u4eba\u80fd\u654c\u4e86\uff01\u201d\u5f53\u65f6\u9f50\u771f\u541b\u5982\u679c\u673a\u7075\u7684\u8bdd\uff0c\u8c26\u865a\u6c42\u6559\uff0c\u751a\u6216\u7acb\u5373\u62dc\u5e08\uff0c\u6666 \u660e\u90fd\u4f1a\u5e94\u5141\u3002\u4e0d\u6599\u9f50\u771f\u541b\u7adf\u4e0d\u80af\u4ee5\u540e\u8f88\u81ea\u5c45\uff0c\u575a\u6301\u8981\u548c\u6666\u660e\u7985\u5e08\u6bd4\u8bd5\u3002\u6666\u660e\u7985\u5e08\u5fae\u5fae\u4e00\u7b11\uff0c \u8bf4\u9053\uff1a\u201c\u6211\u5c01\u5251\u591a\u5e74\uff0c\u5251\u6cd5\u65e9\u5df2\u751f\u758f\uff0c\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\u3002\u6211\u521a\u624d\u6240\u8bf4\uff0c\u53ea\u662f\u59d1\u5984\u544a\u4e4b\uff0c\u4f60\u4e0d\u5fc5 \u653e\u5728\u5fc3\u4e0a\u3002\u201d\u8bf4\u7f62\u8eab\u5f62\u4e00\u6643\uff0c\u970e\u5ffd\u4e0d\u89c1\u8e2a\u8ff9\u3002\u9f50\u771f\u541b\u867d\u7136\u60ca\u5947\u4e8e\u6666\u660e\u7985\u5e08\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u4f46\u8fd8 \u4ee5\u4e3a\u4ed6\u7684\u5251\u6cd5\u7684\u786e\u4e0d\u5982\u81ea\u5df1\uff0c\u6cbe\u6cbe\u81ea\u559c\uff0c\u4e5f\u5c31\u4e0d\u518d\u53bb\u627e\u6666\u660e\u7985\u5e08\uff0c\u5f84\u81ea\u5728\u5929\u5c71\u6f2b\u6e38\u3002 \u3000\u3000\u5929\u5c71\u6a2a\u4e98\u4e09\u5343\u591a\u91cc\uff0c\u6666\u660e\u7985\u5e08\u4f4f\u5728\u5929\u5c71\u5317\u5cf0\uff0c\u5929\u5c71\u5357\u9762\u9ad8\u5cf0\uff0c\u5374\u53e6\u5916\u4f4f\u6709\u4e00\u4e2a\u5947\u4eba\uff0c\u8e2a \u8ff9\u6bd4\u6666\u660e\u7985\u5e08\u8fd8\u8981\u8be1\u79d8\uff0c\u662f\u4e2a\u767d\u53d1\u6ee1\u5934\u4f46\u5374\u5bb9\u989c\u7f8e\u8273\u7684\u5973\u5b50\uff0c\u4eba\u79f0\u201c\u767d\u53d1\u9b54\u5973\u201d\u3002\u636e\u8bf4\u5979\u66fe \u7ecf\u505a\u8fc7\u5f3a\u76d7\u5934\u5b50\uff0c\u4e3a\u4e86\u60c5\u573a\u5931\u610f\uff0c\u4e00\u591c\u767d\u5934\uff0c\u8fd9\u624d\u7edd\u8ff9\u6c5f\u6e56\uff0c\u9690\u5c45\u585e\u5916\u7684\u3002 \u3000\u3000\u9f50\u771f\u541b\u53ea\u77e5\u6709\u4e00\u4e2a\u6666\u660e\u7985\u5e08\uff0c\u5374\u4e0d\u77e5\u6709\u4e00\u4e2a\u767d\u53d1\u9b54\u5973\uff0c\u4ed6\u81ea\u5317\u9ad8\u5cf0\u6765\u5230\u5357\u9ad8\u5cf0\uff0c\u5f39\u5251\u957f \u5578\uff0c\u610f\u6c14\u751a\u8c6a\uff0c\u5728\u5cf0\u9876\u7ec3\u4e86\u4e00\u56de\u5251\u6cd5\uff0c\u9ad8\u58f0\u53f9\u9053\uff1a\u201c\u53ef\u60dc\u4e16\u95f4\u6ca1\u6709\u4eba\u80fd\u548c\u6211\u5e73\u624b\u8fc7\u62db\uff01\u201d\u4ed6 \u771f\u4ee5\u4e3a\u81ea\u5df1\u7684\u5251\u6cd5\u72ec\u6b65\u5929\u4e0b\uff0c\u4e3a\u627e\u4e0d\u5230\u5bf9\u624b\u611f\u5230\u6ca1\u8da3\u3002\u4e0d\u6599\u8bdd\u58f0\u65b9\u4e86\uff0c\u4e00\u9635\u51b7\u7b11\u5df2\u4f20\u5230\u8033 \u8fb9\u3002 \u3000\u3000\u51ed\u9f50\u771f\u541b\u90a3\u4e48\u9ad8\u7684\u6b66\u529f\uff0c\u7adf\u7136\u4e0d\u77e5\u9053\u767d\u53d1\u9b54\u5973\u662f\u4ece\u54ea\u91cc\u94bb\u51fa\u6765\u7684\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u53cc \u5251\u6025\u5fd9\u633d\u4e2a\u5251\u82b1\uff0c\u4e00\u5251\u62a4\u80f8\uff0c\u4e00\u5251\u5e94\u654c\uff0c\u559d\u9053\uff1a\u201c\u90a3\u91cc\u6765\u7684\u5996\u5987\uff0c\u4e3a\u4f55\u51b7\u7b11\uff1f\u201d\u767d\u53d1\u9b54\u5973\u6ee1 \u8138\u9119\u5937\u4e4b\u8272\u3002\u8bf4\u9053\uff1a\u201c\u51ed\u4f60\u8fd9\u70b9\u4e0d\u6210\u6837\u7684\u73a9\u610f\uff0c\u5c45\u7136\u6562\u5728\u8fd9\u91cc\u4f7f\u5251\u3002\u201d\u9f50\u771f\u541b\u6c14\u5f97\u9762\u8272\u53d1 \u9752\uff0c\u53cc\u5251\u4e00\u6296\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u60f3\u6765\u5251\u6cd5\u9ad8\u660e\u6781\u4e86\uff0c\u597d\u5427\uff0c\u54b1\u4eec\u5c31\u6bd4\u5212\u6bd4\u5212\uff01\u201d\u767d\u53d1\u9b54 \u5973\u51b7\u7b11\u4e00\u58f0\uff0c\u968f\u624b\u6298\u4e0b\u4e00\u6839\u6811\u679d\uff0c\u8fce\u98ce\u4e00\u8361\uff0c\u77a7\u4e86\u9f50\u771f\u541b\u4e00\u773c\uff0c\u53c8\u89e3\u4e0b\u4e00\u6761\u8170\u5e26\u3002\u201c\u54fc\u201d\u4e86 \u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u867d\u7136\u4e0d\u884c\uff0c\u53ef\u8fd8\u7528\u4e0d\u7740\u62d4\u5251\u6765\u6559\u8bad\u4f60\uff01\u201d\u9f50\u771f\u541b\u5927\u6012\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u75be\u5982\u95ea \u7535\uff0c\u559d\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u5c31\u7528\u6811\u679d\u6765\u6321\u5427\uff01\u201d\u767d\u53d1\u9b54\u5973\u4e00\u4e2a\u95ea\u8eab\uff0c\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u6811\u679d\u62c2\u5904\uff0c \u7adf\u7136\u5e26\u8d77\u98ce\u58f0\uff0c\u8fde\u679d\u5e26\u53f6\uff0c\u5411\u9f50\u771f\u541b\u624b\u8155\u5212\u5230\u3002\u5979\u53ea\u7528\u4e00\u6761\u8170\u5e26\u548c\u4e00\u679d\u6811\u679d\uff0c\u4e0d\u8fc7\u4e09\u5341\u62db\uff0c \u5c31\u7834\u4e86\u9f50\u771f\u541b\u72ec\u521b\u7684\u98ce\u96f7\u5251\u6cd5\uff0c\u628a\u4ed6\u9010\u4e0b\u5929\u5c71\u3002 \u3000\u3000\u767d\u53d1\u9b54\u5973\u5c31\u662f\u98de\u7ea2\u5dfe\u540e\u6765\u7684\u5e08\u7236\u3002\u56e0\u6b64\u9f50\u771f\u541b\u4e00\u89c1\u98de\u7ea2\u5dfe\u5de6\u97ad\u53f3\u5251\u7684\u62db\u6570\uff0c\u4fbf\u731c\u51fa\u5979\u662f \u767d\u53d1\u9b54\u5973\u7684\u95e8\u4e0b\u3002 \u3000\u3000\u9f50\u771f\u541b\u81ea\u5403\u767d\u53d1\u9b54\u5973\u7684\u5927\u4e8f\u540e\uff0c\u56de\u8f6c\u957f\u767d\u5c71\u4e2d\u82e6\u7ec3\u5251\u6cd5\uff0c\u679c\u7136\u6210\u4e86\u5173\u5916\u5251\u672f\u7684\u5927\u5e08\uff0c\u6e05 \u5175\u5165\u5173\uff0c\u4e5f\u66fe\u8bf7\u4ed6\u76f8\u52a9\uff0c\u53ef\u662f\u90a3\u65f6\u4ed6\u81ea\u95ee\u8fd8\u4e0d\u662f\u767d\u53d1\u9b54\u5973\u7684\u5bf9\u624b\uff0c\u4e0d\u613f\u5165\u5173\u3002\u76f4\u5230\u90b1\u4e1c\u6d1b\u5728 \u4e91\u5357\u629a\u4ed9\u6e56\u88ab\u51cc\u672a\u98ce\u5272\u4e86\u4e00\u53ea\u8033\u6735\u4e4b\u540e\uff0c\u56de\u5230\u957f\u767d\u5c71\u54ed\u8bc9\uff0c\u4ed6\u5c48\u6307\u4e00\u7b97\uff0c\u8ddd\u79bb\u5929\u5c71\u53d7\u632b\uff0c\u970e \u5ffd\u5df2\u8fd1\u4e94\u5341\u5e74\uff0c\u4ed6\u60f3\u6666\u660e\u7985\u5e08\u548c\u767d\u53d1\u9b54\u5973\uff0c\u4e00\u5b9a\u65e9\u5df2\u901d\u4e16\uff0c\u53c8\u542c\u8bf4\u51cc\u672a\u98ce\u662f\u6666\u660e\u7985\u5e08\u7684\u5f1f \u5b50\uff0c\u4ee5\u5929\u5c71\u5251\u6cd5\uff0c\u538b\u5f97\u5173\u5916\u6b66\u5e08\u95fb\u98ce\u80c6\u843d\uff0c\u4e0d\u7981\u64a9\u8d77\u96c4\u5fc3\u3002\u8fd9\u65f6\u4ed6\u867d\u7136\u5df2\u662f\u5e74\u8fd1\u516b\u65ec\uff0c\u4f46\u529f \u529b\u6df1\u539a\uff0c\u7cbe\u795e\u77cd\u9510\u8fd8\u4f3c\u58ee\u5e74\uff0c\u4e8e\u662f\u4ed7\u5251\u51fa\u5c71\uff0c\u5728\u4e94\u5341\u5e74\u540e\u91cd\u6765\u4e2d\u571f\u3002 \u3000\u3000\u4ed6\u4e00\u5230\u5317\u4eac\uff0c\u6070\u5de7\u5728\u51cc\u672a\u98ce\u5927\u95f9\u5929\u7262\u4e4b\u540e\u3002\u4ed6\u8fdb\u5bab\u53e9\u89c1\u7687\u5e1d\uff0c\u7687\u5e1d\u5927\u559c\uff0c\u4fbf\u53eb\u4ed6\u5e26\u4e24\u4e2a \u5f92\u5f1f\uff0c\u5230\u738b\u5983\u5e9c\u4e2d\u4fa6\u5bdf\u201c\u5973\u8d3c\u201d\u8e2a\u8ff9\u3002\u539f\u6765\u7687\u5e1d\u56e0\u5192\u6d63\u83b2\u5c3d\u77e5\u4ed6\u7684\u9690\u79d8\uff0c\u6700\u4e3a\u5fcc\u60ee\uff0c\u628a\u5979\u5f53 \u6210\u5fc3\u4e4b\u523a\uff0c\u975e\u62d4\u53bb\u4e0d\u80fd\u5b89\u6795\u3002\u4ed6\u5e26\u6765\u4e86\u4e24\u4e2a\u5f92\u5f1f\uff0c\u6765\u5230\u738b\u5e9c\uff0c\u65e0\u5de7\u4e0d\u5de7\uff0c\u4e00\u5230\u738b\u5e9c\u5c31\u78b0\u5230\u98de \u7ea2\u5dfe\u3002 \u3000\u3000\u9f50\u771f\u541b\u4e00\u751f\u6700\u6068\u767d\u53d1\u9b54\u5973\uff0c\u8fd9\u56de\u78b0\u5230\u5979\u7684\u5f92\u5f1f\uff0c\u7acb\u5fc3\u5148\u628a\u5979\u796d\u5251\u3002\u4ed6\u7684\u98ce\u96f7\u5251\u6cd5\u7ecf\u8fc7\u4e94 \u5341\u5e74\u82e6\u7ec3\uff0c\u786e\u5df2\u5230\u4e86\u51fa\u795e\u5165\u5316\u4e4b\u5883\uff01\u9f50\u771f\u541b\u53cc\u5251\u5c55\u5f00\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u6d51\u8eab\u4e0a\u4e0b\uff0c\u4e00\u7247\u6e05\u5149\uff0c\u679c \u7136\u5a01\u529b\u60ca\u4eba\uff01\u4f46\u98de\u7ea2\u5dfe\u662f\u767d\u53d1\u9b54\u5973\u7684\u4f8d\u4eba\uff0c\u957f\u97ad\u77ed\u5251\uff0c\u5de6\u653b\u53f3\u62d2\uff0c\u53f3\u653b\u5de6\u62d2\uff0c\u4e5f\u662f\u914d\u5408\u5f97\u5999 \u5230\u6beb\u5dc5\uff01\u9f50\u771f\u541b\u6700\u521d\u81ea\u6043\u4e94\u5341\u5e74\u529f\u529b\uff0c\u4ee5\u4e3a\u5bf9\u4ed8\u4e00\u4e2a\u5c0f\u8f88\uff0c\u8fd8\u4e0d\u662f\u624b\u5230\u64d2\u6765\uff1f\u5fc3\u9ad8\u6c14\u50b2\uff0c\u8fed \u8d70\u9669\u62db\uff0c\u4e0d\u6599\u98de\u7ea2\u5dfe\u62db\u6570\u72e0\u8fa3\u4e4b\u6781\uff0c\u95e8\u6237\u53c8\u5c01\u5f97\u6781\u4e25\uff0c\u6597\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u975e\u4f46\u8ba8\u4e0d\u4e86\u534a\u70b9\u4fbf \u5b9c\uff0c\u800c\u4e14\u6709\u597d\u51e0\u6b21\u8fc7\u4e8e\u6025\u8e81\uff0c\u8fd8\u51e0\u4e4e\u7ed9\u98de\u7ea2\u5dfe\u7684\u957f\u97ad\u626b\u4e2d\uff0c\u8fd9\u624d\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u60f3\uff1a\u81ea\u5df1\u82e6\u7ec3 \u98ce\u96f7\u5251\u6cd5\uff0c\u539f\u662f\u60f3\u627e\u767d\u53d1\u9b54\u5973\u62a5\u4ec7\u7684\uff0c\u5982\u679c\u8fde\u5979\u7684\u5f92\u5f1f\u90fd\u6597\u4e0d\u8fc7\uff0c\u90a3\u4e94\u5341\u5e74\u5fc3\u8840\uff0c\u5c82\u4e0d\u662f\u767d \u82b1\uff1f \u3000\u3000\u5176\u5b9e\u9f50\u771f\u541b\u4e0d\u77e5\u9053\uff0c\u98de\u7ea2\u5dfe\u6bd4\u4ed6\u66f4\u611f\u5403\u529b\uff0c\u5979\u62db\u6570\u867d\u7136\u7cbe\u5947\uff0c\u529f\u529b\u5230\u5e95\u7a0d\u900a\uff0c\u7528\u5c3d\u5168 \u529b\uff0c\u624d\u80fd\u6253\u4e2a\u5e73\u624b\uff0c\u800c\u4e14\u6bcf\u6b21\u5175\u5203\u76f8\u4ea4\uff0c\u81ea\u5df1\u90fd\u611f\u5230\u4e00\u80a1\u6f5c\u529b\uff0c\u4f3c\u94c1\u9524\u631f\u98ce\uff0c\u5f53\u80f8\u538b\u4e0b\u3002\u98de \u7ea2\u5dfe\u8fd0\u6c14\u51dd\u795e\uff0c\u62fc\u547d\u652f\u6491\uff0c\u53c8\u62c6\u4e86\u4e8c\u4e09\u5341\u62db\u3002\u9f50\u771f\u541b\u8fd9\u65f6\u4e5f\u5df2\u770b\u51fa\u98de\u7ea2\u5dfe\u6b66\u827a\u867d\u9ad8\uff0c\u529f\u529b\u7a76 \u7adf\u6bd4\u4e0d\u4e0a\u4ed6\u3002\u98ce\u96f7\u5251\u6cd5\u4e00\u53d8\uff0c\u4e0d\u6c42\u6025\u653b\uff0c\u628a\u5185\u529b\u90fd\u8fd0\u5230\u5251\u4e0a\uff0c\u5251\u98ce\u8361\u5904\uff0c\u8fde\u56db\u9762\u679d\u53f6\u90fd\u7c41\u7c0c \u4f5c\u54cd\uff01\u8fd9\u56de\u8f6e\u5230\u98de\u7ea2\u5dfe\u6025\u8e81\u4e86\uff0c\u5979\u60f3\u5f3a\u654c\u5f53\u524d\uff0c\u536b\u58eb\u73af\u4f3a\uff0c\u82e5\u4e0d\u6025\u6c42\u8131\u8eab\uff0c\u53ea\u6084\u82f1\u540d\u96be\u4fdd\u3002 \u5f53\u4e0b\u4f7f\u51fa\u9669\u62db\uff0c\u4e00\u62db\u201c\u7389\u5e26\u56f4\u8170\u201d\uff0c\u8feb\u5f97\u9f50\u771f\u541b\u98de\u8eab\u8dc3\u907f\u3002\u4ed6\u51cc\u7a7a\u51fb\u523a\uff0c\u907f\u62db\u8ff8\u62db\uff0c\u5251\u6cd5\u6781 \u4e3a\u51cc\u5389\uff0c\u4f46\u98de\u7ea2\u5dfe\u6bd4\u4ed6\u66f4\u4e3a\u608d\u731b\uff0c\u811a\u8e0f\u539f\u5730\uff0c\u5de6\u80a9\u6643\u5904\uff0c\u8f6c\u8fc7\u8eab\u5f62\uff0c\u7528\u529b\u4e00\u6296\uff0c\u5de6\u624b\u90a3\u8336\u957f \u97ad\uff0c\u7ade\u7b14\u76f4\u5730\u7ad6\u8d77\u6765\uff0c\u76f4\u5411\u9f50\u771f\u541b\u201c\u4e39\u7530\u7a74\u201d\u624e\u53bb\uff0c\u97ad\u5251\u76f8\u4ea4\uff0c\u591c\u7a7a\u4e2d\u970e\u7684\u706b\u82b1\u98de\u6e85\uff0c\u4e24\u4eba \u90fd\u5411\u540e\u9762\u5012\u7ffb\u51fa\u53bb\uff01\u9f50\u771f\u541b\u529f\u529b\u867d\u6bd4\u98de\u7ea2\u5dfe\u4e3a\u9ad8\uff0c\u4f46\u9ad8\u5f97\u4e5f\u662f\u6709\u9650\uff0c\u4ed6\u8eab\u5b50\u60ac\u7a7a\uff0c\u4e0d\u6bd4\u5e73\u5730 \u6613\u4e8e\u4f7f\u529b\uff0c\u6b64\u6d88\u5f7c\u957f\uff0c\u529f\u529b\u6070\u6070\u62c9\u5e73\uff0c\u97ad\u5251\u76f8\u4ea4\uff0c\u4e24\u4eba\u90fd\u7ed9\u5bf9\u65b9\u7684\u6f5c\u529b\u9707\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u8d81\u52bf\u4e00\u4e2a\u5012\u7ffb\uff0c\u4ee5\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\u7684\u8f7b\u529f\u7edd\u6280\uff0c\u7ffb\u51fa\u516d\u4e03\u4e08\u5916\uff0c\u957f\u97ad\u5728\u534a\u7a7a\u4e2d\u53cd \u624b\u6253\u51fa\uff0c\u4e24\u540d\u536b\u58eb\uff0c\u5175\u5203\u65b9\u626c\uff0c\u5df2\u7ed9\u957f\u97ad\u5377\u7740\uff0c\u98de\u7ea2\u5dfe\u811a\u5c16\u7740\u5730\uff0c\u529b\u8d2f\u97ad\u68a2\uff0c\u4e24\u822c\u5175\u5203\uff0c\u90fd \u7ed9\u5979\u5377\u53bb\uff01 \u3000\u3000\u98de\u7ea2\u5dfe\u4e00\u58f0\u957f\u5578\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u59d1\u5976\u5976\u5c11\u966a\u4e86\uff01\u201d\u6b63\u60f3\u786c\u95ef\uff0c\u5ffd\u7136\u4e00\u6761\u5927\u6c49\uff0c\u8fce\u9762\u6734\u6765\u3002 \u5de6\u5200\u53f3\u5251\uff0c\u5f53\u5934\u5241\u4e0b\uff0c\u559d\u9053\uff1a\u201c\u4f60\u60f3\u8d70\uff0c\u90a3\u53ef\u4e0d\u6210\uff01\u201d\u98de\u7ea2\u4e2a\u4e00\u5251\u626b\u53bb\uff0c\u90a3\u4eba\u5237\u5730\u8df3\u5f00\uff0c\u5200 \u62a2\u4e2d\u76d8\uff0c\u5251\u8d70\u504f\u950b\uff0c\u5c45\u7136\u4e5f\u662f\u98ce\u96f7\u5251\u7684\u62db\u6570\uff0c\u4e0d\u8fc7\u628a\u53cc\u5251\u6539\u4e3a\u5200\u5251\u7f62\u4e86\uff0c\u8fd9\u4eba\u662f\u9f50\u771f\u541b\u7684\u5f97 \u610f\u5f1f\u5b50\u90b1\u4e1c\u6d1b\u3002\u90b1\u4e1c\u6d1b\u7684\u6b66\u529f\u867d\u6bd4\u98de\u7ea2\u5dfe\u5f31\u8bb8\u591a\uff0c\u53ef\u662f\u5341\u62db\u516b\u62db\u8fd8\u6321\u5f97\u4f4f\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c \u9f50\u771f\u541b\u53c8\u5df2\u8d76\u4e0a\u6765\u4e86\uff01 \u3000\u3000\u9f50\u771f\u541b\u8d76\u6765\uff0c\u53eb\u9053\uff1a\u201c\u4e1c\u6d1b\uff0c\u9000\u4e0b\uff01\u201d\u53cc\u5251\u547c\u5730\u5377\u6765\uff0c\u53c8\u628a\u98de\u7ea2\u4e2a\u56f4\u4f4f\uff01\u4ed6\u521a\u624d\u7ed9\u98de\u7ea2 \u5dfe\u957f\u97ad\u9707\u9000\uff0c\u5728\u4f17\u76ee\u77aa\u77aa\u4e4b\u4e0b\uff0c\u6c14\u5f97\u6ee1\u9762\u901a\u7ea2\uff01\u8fd9\u756a\u518d\u5ea6\u6251\u6765\uff0c\u51fa\u624b\u66f4\u89c1\u8f9b\u8fa3\uff0c\u98de\u7ea2\u5dfe\u77e5\u9053 \u95ef\u4e0d\u51fa\u53bb\uff0c\u4e5f\u6a2a\u4e86\u5fc3\u80a0\u62fc\u6b7b\u76f8\u6597\uff0c\u53ea\u89c1\u5251\u5149\u97ad\u5f71\uff0c\u98de\u6c99\u8d70\u77f3\uff0c\u7aef\u7684\u60ca\u9669\u4e07\u5206\uff0c\u6fc0\u70c8\u5f02\u5e38\uff01 \u3000\u3000\u53c8\u8fc7\u4e00\u9635\uff0c\u98de\u7ea2\u5dfe\u6c57\u6e7f\u8863\u88f3\uff0c\u5979\u5230\u5e95\u662f\u5973\u6d41\uff0c\u6c14\u529b\u6e10\u6e10\u4e0d\u7ee7\u3002\u6b63\u60f3\u65bd\u5c55\u5e08\u95e8\u7684\u201c\u795e\u9b54\u593a \u547d\u201d\u7edd\u62db\uff0c\u548c\u654c\u4eba\u540c\u5f52\u4e8e\u5c3d\u3002\u5ffd\u7136\u542c\u5f97\u6709\u4eba\u558a\u9053\uff1a\u201c\u97e9\u5927\u54e5\uff0c\u4f60\u53bb\u62d4\u90a3\u8001\u8d3c\u7684\u987b\uff0c\u6211\u8981\u8ffd \u503a\uff01\u201d\u98de\u7ea2\u5dfe\u4e00\u542c\u5927\u559c\uff0c\u53ea\u89c1\u9644\u8fd1\u4e00\u68f5\u5927\u6811\u4e4b\u4e0a\uff0c\u4f3c\u98de\u9e1f\u822c\u5730\u843d\u4e0b\u4e09\u6761\u9ed1\u5f71\u3002\u4e3a\u9996\u7684\u662f\u97e9\u5fd7 \u90a6\uff0c\u5f53\u4e2d\u7684\u662f\u51cc\u672a\u98ce\uff0c\u800c\u62bc\u540e\u7684\u4e00\u4e2a\u9ec4\u886b\u5c11\u5e74\uff0c\u5979\u5c31\u4e0d\u8ba4\u5f97\u4e86\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u65cb\u98ce\u822c\u5730\u6251\u5165\u6218\u56f4\uff0c\u6b65\u5b50\u6b6a\u6b6a\u659c\u659c\uff0c\u9f50\u771f\u541b\u547c\u7684\u4e00\u5251\u626b\u53bb\uff0c\u4ee5\u4e3a\u5b9a\u53ef\u628a\u654c\u4eba\u62e6\u8170 \u4e24\u622a\uff0c\u54ea\u6599\u7adf\u63b7\u4e2a\u7a7a\uff0c\u97e9\u5fd7\u90a6\u8eab\u6cd5\u602a\u6781\uff0c\u4e5f\u4e0d\u77e5\u662f\u600e\u4e48\u7ed9\u4ed6\u907f\u8fc7\u3002\u9f50\u771f\u541b\u6014\u5f97\u4e00\u6014\uff0c\u97e9\u5fd7\u90a6 \u5df2\u62a2\u653b\u4e86\u4e24\u62db\u602a\u62db\uff0c\u9f50\u771f\u541b\u89c1\u6240\u672a\u89c1\uff0c\u8981\u60f3\u56de\u5251\u62e6\u622a\uff0c\u53c8\u7ed9\u98de\u7ea2\u5dfe\u7eca\u7740\uff0c\u556a\u556a\u8fde\u58f0\uff0c\u5de6\u53f3\u4e24 \u988a\uff0c\u90fd\u4e2d\u4e86\u4e00\u638c\uff0c\u9f50\u771f\u541b\u5de6\u65f6\u4e00\u649e\uff0c\u6ca1\u6709\u649e\u4e2d\uff0c\u4e0b\u5df4\u4e00\u9635\u5267\u75db\uff0c\u96ea\u767d\u7684\u80e1\u5b50\uff0c\u7adf\u7136\u771f\u7684\u7ed9\u654c \u4eba\u62d4\u53bb\u4e00\u7efa\uff01\u8fd9\u65f6\u51cc\u672a\u98ce\u6b63\u5728\u548c\u90a3\u4e2a\u5de6\u624b\u62a1\u5200\u53f3\u624b\u4f7f\u5251\u7684\u4eba\u76f8\u6597\uff0c\u773c\u89d2\u4ecd\u540a\u7740\u97e9\u5fd7\u90a6\uff0c\u53eb \u9053\uff1a\u201c\u884c\u4e86\uff0c\u5feb\u9000\uff01\u201d\u97e9\u5fd7\u90a6\u610f\u72b9\u672a\u8db3\uff0c\u201c\u556a\u201d\u7684\u4e00\u638c\uff0c\u53c8\u51fb\u4e2d\u4e86\u9f50\u771f\u541b\u7684\u80cc\u5fc3\uff0c\u4e0d\u6599\u8fd9\u4e00 \u51fb\u5982\u4e2d\u94a2\u677f\uff0c\u9707\u5f97\u624b\u638c\u9ebb\u6728\uff0c\u864e\u53e3\u6d41\u8840\u3002\u4ed6\u4ed7\u7740\u8eab\u6cd5\u602a\u5f02\uff0c\u6025\u5fd9\u9000\u51fa\u5708\u5b50\uff0c\u98de\u7ea2\u4e2a\u865a\u6643\u4e00 \u5251\uff0c\u7acb\u5373\u8f6c\u8eab\u63a9\u62a4\uff0c\u9f50\u771f\u541b\u867d\u7136\u6c14\u6124\u5f02\u5e38\uff0c\u5374\u662f\u4e0d\u6562\u8ffd\u8d76\uff01 \u3000\u3000\u97e9\u5fd7\u90a6\u5728\u90a3\u77f3\u7a9f\u5b66\u5230\u51e0\u624b\u602a\u62db\uff0c\u5f97\u51cc\u672a\u98ce\u6240\u6559\uff0c\u51fa\u654c\u4e0d\u610f\u5730\u6b3a\u8eab\u8fdb\u51fb\uff0c\u679c\u7136\u628a\u9f50\u771f\u541b\u7684 \u80e1\u5b50\u62d4\u4e86\u4e0b\u6765\u3002\u4ed6\u4e0d\u77e5\u5389\u5bb3\uff0c\u8fd8\u60f3\u8d2a\u529f\uff01\u518d\u51fb\u9f50\u771f\u541b\u4e00\u638c\uff0c\u5374\u53cd\u7ed9\u9707\u75db\u4e86\u624b\u638c\uff0c\u6025\u5fd9\u9000\u51fa\u3002 \u8981\u77e5\u97e9\u5fd7\u90a6\u7684\u529f\u529b\u4e0e\u9f50\u771f\u541b\u76f8\u5dee\u5f88\u8fdc\uff0c\u5168\u4ed7\u5f00\u9996\u90a3\u51e0\u4e0b\u602a\u62db\u4e0e\u98de\u7ea2\u5dfe\u7275\u5236\u4e4b\u529b\uff0c\u624d\u80fd\u6210\u529f\uff0c \u5982\u4f55\u53ef\u4ee5\u4e45\u6218\u4e0b\u53bb\uff1f\u4f46\u9f50\u771f\u541b\u5374\u4e0d\u77e5\u4e2a\u4e2d\u5965\u5999\uff0c\u7ed9\u97e9\u5fd7\u90a6\u6253\u4e86\u4e24\u4e2a\u8033\u5149\uff0c\u53c8\u7ed9\u4ed6\u62d4\u4e86\u80e1\u5b50\uff0c \u8fd9\u4e00\u573a\u7f9e\u8fb1\uff0c\u6bd4\u5403\u767d\u53d1\u9b54\u5973\u7684\u4e8f\uff0c\u66f4\u91cd\u66f4\u5927\u3002\u4ed6\u53ea\u9053\u97e9\u5fd7\u90a6\u6bd4\u98de\u7ea2\u5348\u8fd8\u8981\u5389\u5bb3\uff0c\u81ea\u662f\u96be\u514d\u80c6 \u602f\u4e86\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u4e0e\u98de\u7ea2\u5dfe\u4e00\u9053\u51fa\u6765\uff0c\u6842\u4ef2\u660e\u7acb\u5373\u8d76\u4e0a\u63a5\u5e94\uff0c\u4ed6\u7684\u90a3\u53e3\u817e\u86df\u5b9d\u5251\uff0c\u821e\u5c06\u8d77\u6765\uff0c\u5b9b\u5982 \u4e00\u9053\u94f6\u8679\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u524a\u65ad\u4e86\u5341\u51e0\u4e2a\u536b\u58eb\u7684\u5175\u5203\u3002 \u3000\u3000\u6842\u4ef2\u660e\u53eb\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u6211\u4eec\u95ef\u51fa\u53bb\u5427\uff01\u201d\u51cc\u672a\u98ce\u5e94\u9053\uff1a\u201c\u5f85\u6211\u8ba8\u4e86\u6b20\u503a\uff0c\u9a6c\u4e0a\u5c31 \u6765\u3002\u201d\u4ed6\u5728\u6811\u4e0a\u7eb5\u4e0b\u6765\u65f6\uff0c\u5df2\u8ba4\u5b9a\u4e86\u90b1\u4e1c\u6d1b\uff0c\u4e00\u5c55\u9752\u94a2\u5251\uff0c\u5c31\u628a\u4ed6\u9489\u7740\uff0c\u53ea\u662f\u5f53\u65f6\u4e3a\u4e86\u5173\u5fc3 \u97e9\u5fd7\u90a6\uff0c\u6240\u4ee5\u672a\u53d1\u51fa\u8fa3\u62db\uff0c\u6b64\u9645\uff0c\u97e9\u5fd7\u90a6\u4e0e\u98de\u7ea2\u5dfe\u90fd\u5df2\u8131\u9669\uff0c\u4ed6\u8fd8\u6709\u4ec0\u4e48\u987e\u5fcc\uff1f \u3000\u3000\u51cc\u672a\u98ce\u4e00\u58f0\u957f\u7b11\uff0c\u9752\u94a2\u5251\u970d\u5730\u8fdb\u62db\uff0c\u6025\u5982\u7535\u706b\uff0c\u90b1\u4e1c\u6cbc\u5de6\u81c2\u9178\u9ebb\uff0c\u624b\u4e2d\u5200\u98de\u4e0a\u534a\u7a7a\uff0c\u53f3 \u5251\u4e00\u683c\uff0c\u7ed9\u51cc\u672a\u98ce\u53cd\u624b\u4e00\u7ede\uff0c\u5251\u53c8\u8131\u624b\u98de\u53bb\u3002\u90b1\u4e1c\u6d1b\u62d4\u6b65\u4fbf\u8dd1\uff0c\u90a3\u91cc\u8fd8\u8dd1\u5f97\u4e86\u3002\u51cc\u672a\u98ce\u5de6\u81c2 \u4e00\u63a2\uff0c\u6293\u7740\u4e86\u4ed6\u7684\u540e\u5fc3\uff0c\u50cf\u6293\u5c0f\u9e21\u4f3c\u7684\u63d0\u5c06\u8d77\u6765\uff0c\u6ef4\u6e9c\u6e9c\u5730\u6253\u4e86\u4e2a\u8f6c\uff0c\u624b\u81c2\u4e00\u5f2f\uff0c\u5c06\u4ed6\u7684\u5934 \u626d\u8f6c\u8fc7\u6765\uff0c\u4e3e\u5251\u5728\u4ed6\u7684\u9762\u95e8\u4e00\u5212\uff0c\u5413\u5f97\u4ed6\u534a\u6b7b\uff0c\u53ea\u89c9\u4e00\u7247\u6c81\u51c9\uff0c\u51cc\u672a\u98ce\u5df2\u662f\u628a\u4ed6\u7684\u53f3\u8fb9\u8033\u6735 \u5272\u4e86\u4e0b\u6765\uff0c\u5927\u7b11\u8bf4\uff1a\u201c\u672c\u606f\u4ed8\u6e05\uff0c\u9976\u4f60\u4e0d\u6b7b\uff01\u201d\u5355\u638c\u5f80\u5916\u4e00\u7529\uff0c\u5c06\u90b1\u4e1c\u6d1b\u629b\u51fa\u4e09\u4e08\u5f00\u5916\u3002 \u3000\u3000\u9f50\u771f\u541b\u6c14\u7ea2\u4e86\u773c\uff0c\u773c\u770b\u7740\u4e09\u4e2a\u201c\u53db\u8d3c\u201d\u5c31\u8981\u786c\u95ef\u51fa\u53bb\uff0c\u888d\u8896\u4e00\u6296\uff0c\u7fe9\u5982\u5927\u9e1f\u817e\u7a7a\uff0c\u843d\u5728 \u6842\u4ef2\u660e\u4e0e\u51cc\u672a\u98ce\u4e4b\u95f4\uff0c\u53cc\u5251\u5411\u51cc\u672a\u98ce\u5288\u53bb\u3002\u8fd9\u65f6\u98de\u7ea2\u65ec\u4e0e\u97e9\u5fd7\u90a6\u8dd1\u5728\u524d\u5934\uff0c\u51cc\u672a\u98ce\u6700\u540e\u3002\u9f50 \u771f\u541b\u6700\u60e7\u97e9\u5fd7\u90a6\uff0c\u5bf9\u51cc\u672a\u98ce\u5374\u5e76\u672a\u653e\u5728\u773c\u5185\u3002 \u3000\u3000\u9f50\u771f\u541b\u8ba4\u4e0d\u5f97\u51cc\u672a\u51e4\uff0c\u51cc\u672a\u98ce\u5374\u8ba4\u5f97\u9f50\u771f\u541b\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u8d3c\u8fd8\u6709\u51e0\u628a\u80e1\u5b50\uff1f\u201d \u53ea\u4e00\u6643\u8eab\uff0c\u9752\u94a2\u5251\u75be\u5982\u95ea\u7535\u822c\u7684\u5411\u4e24\u5251\u4ea4\u526a\u7684\u9699\u7f1d\u4ea7\u523a\u8fdb\uff0c\u9f50\u771f\u541b\u5927\u54c8\u4e00\u60ca\uff0c\u5411\u540e\u4e00\u4ef0\uff0c\u5de6 \u5251\u4e00\u62e8\uff0c\u907f\u5f00\u8fd9\u5251\uff0c\u51cc\u672a\u98ce\u8de8\u80fd\u4e00\u6b65\uff0c\u6beb\u4e0d\u653e\u677e\uff0c\u5251\u62db\u6539\u4e3a\u201c\u94c1\u9501\u6a2a\u821f\u201d\uff0c\u5411\u5de6\u4e00\u5c01\uff0c\u8d81\u7740 \u9f50\u771f\u541b\u907f\u62db\u540e\u4ef0\uff0c\u91cd\u5fc3\u4e0d\u7a33\u4e4b\u9645\uff0c\u9752\u94a2\u5251\u75be\u7684\u6325\u53bb\uff0c\u4e01\u5f53\u4e00\u58f0\uff0c\u628a\u9f50\u771f\u541b\u53f3\u624b\u957f\u5251\u8361\u5f00\u3002\u5251 \u62db\u4e09\u53d8\uff0c\u75be\u53d1\u75be\u6536\uff0c\u9f50\u771f\u541b\u4e00\u5ff5\u8f7b\u654c\uff0c\u51e0\u4e4e\u4e27\u547d\u5728\u51cc\u672a\u98ce\u5251\u950b\u4e4b\u4e0b\uff01 \u3000\u3000\u4f46\u9f50\u771f\u541b\u662f\u4e00\u6d3e\u5b97\u5e08\uff0c\u4e94\u5341\u5e74\u529f\u529b\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u4e34\u5371\u4e0d\u4e71\uff0c\u594b\u529b\u4e00\u632f\uff0c\u529b\u900f\u5251\u5c16\uff0c\u8eab\u5b50 \u98ce\u8f66\u822c\u5411\u5de6\u4e00\u65cb\uff0c\u53cc\u5251\u672a\u6536\uff0c\u5fae\u4e00\u70b9\u5730\uff0c\u7adf\u7136\u53cd\u5f39\u8d77\u6765\uff0c\u53f3\u5251\u64e6\u7740\u51cc\u5728\u98ce\u7684\u5251\u8eab\uff0c\u8d81\u52bf\u5f15 \u5f00\uff0c\u89e3\u4e86\u9669\u62db\uff0c\u5de6\u5251\u4e0a\u64a9\uff0c\u523a\u5411\u51cc\u672a\u98ce\u6301\u5251\u7684\u624b\u8155\uff0c\u51cc\u672a\u98ce\u4e5f\u4e0d\u7981\u5fc3\u5934\u4e00\u51db\uff0c\u98de\u8eab\u81ea\u5f00\u771f\u541b \u5de6\u4fa7\u63a0\u8fc7\uff0c\u201c\u795e\u9f99\u6389\u5c3e\u201d\uff0c\u56de\u624b\u4e00\u5251\uff0c\u671d\u9f50\u771f\u541b\u7684\u592a\u9633\u7a74\u75be\u523a\u3002\u9f50\u771f\u541b\u970d\u5730\u7ffb\u8eab\uff0c\u6a2a\u5251\u4e00 \u5288\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u91d1\u5203\u540f\u9e23\u4e4b\u58f0\uff0c\u706b\u661f\u56db\u6e85\uff0c\u4e24\u4eba\u90fd\u7ed9\u9707\u9000\u51e0\u6b65\uff0c\u624b\u4e2d\u7684\u5251\u90fd\u7ed9\u5bf9\u65b9\u780d\u4e86\u4e00\u4e2a \u7f3a\u53e3\uff01\u51cc\u672a\u98ce\u8fd9\u62db\u8bd5\u51fa\u4e86\u9f50\u771f\u541b\u7684\u529f\u529b\u548c\u81ea\u5df1\u7adf\u662f\u534a\u65a4\u516b\u4e24\uff0c\u65d7\u9f13\u76f8\u5f53\uff01\u5fc3\u60f3\uff1a\u8981\u5236\u670d\u8fd9\u8001 \u5934\u513f\uff0c\u53ef\u4e0d\u662f\u4e09\u4e94\u5341\u62db\u7684\u4e8b\uff0c\u61d2\u5f97\u4e0e\u4ed6\u7ea0\u7f20\uff0c\u559d\u9053\uff1a\u201c\u5ff5\u4f60\u4e00\u628a\u5e74\u7eaa\uff0c\u9976\u4f60\u56de\u53bb\u517b\u8001\u5427\uff01\u201d \u9752\u94a2\u5251\u5de6\u53f3\u75be\u6325\uff0c\u5251\u62db\u53d1\u5904\uff0c\u76f4\u5982\u98ce\u7ffb\u4e91\u6d8c\uff0c\u738b\u5e9c\u7684\u536b\u58eb\u4eec\u54ea\u91cc\u62e6\u622a\u5f97\u4f4f\uff1f\u970e\u5ffd\u4e4b\u95f4\uff0c\u5df2\u7ed9 \u4ed6\u548c\u6302\u4ef2\u660e\u4f1a\u5408\u4e00\u5904\u3002 \u3000\u3000\u9f50\u771f\u541b\u82e6\u7ec3\u4e86\u4e94\u5341\u5e74\uff0c\u81ea\u4ee5\u4e3a\u53ef\u4ee5\u79f0\u9738\u5929\u4e0b\uff0c\u4e0d\u6599\u4e00\u51fa\u624b\u5c31\u8fde\u8fde\u5403\u4e8f\uff0c\u4e0e\u98de\u7ea2\u5dfe\u6253\u6210\u5e73 \u624b\uff0c\u7ed9\u97e9\u5fd7\u90a6\u6253\u4e86\u8033\u5149\uff0c\u9047\u51cc\u672a\u98ce\u66f4\u51e0\u4e4e\u4e27\u547d\uff01\u800c\u8fd9\u4e09\u4e2a\u4eba\u8fd8\u90fd\u662f\u81ea\u5df1\u7684\u5c0f\u8f88\u3002\u51cc\u672a\u98ce\u770b\u6765 \u53ea\u662f\u4e09\u5341\u5c81\u591a\u70b9\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u7684\u5251\u6cd5\u662f\u4ece\u54ea\u91cc\u5b66\u6765\u7684\uff0c\u5982\u6b64\u795e\u5999\uff0c\u89c1\u9762\u56db\u62db\uff0c\u62db\u62db\u72e0\u8fa3\uff01\u4e0d\u7531 \u5f97\u4e00\u7247\u96c4\u5fc3\u90fd\u51b7\u4e86\u4e0b\u6765\uff0c\u54ea\u91cc\u8fd8\u6562\u8ffd\u8d76\uff1f \u3000\u3000\u9f50\u771f\u541b\u7684\u53e6\u4e00\u4e2a\u5f92\u5f1f\u67f3\u897f\u5ca9\uff0c\u624b\u4f7f\u4e00\u6839\u82b1\u67aa\uff0c\u7ed9\u6842\u4ef2\u660e\u7684\u5b9d\u5251\u65a9\u53bb\u534a\u622a\uff0c\u5927\u817f\u53c8\u7ed9\u98de\u7ea2 \u5dfe\u7684\u957f\u97ad\u626b\u53bb\u4e00\u5927\u7247\u76ae\u8089\uff0c\u62ff\u7740\u534a\u622a\u67aa\u6746\uff0c\u4f5c\u62d0\u6756\u7528\uff0c\u90b1\u4e1c\u6d1b\u5931\u53bb\u4e24\u53ea\u8033\u6735\uff0c\u6ee1\u9762\u6d41\u8840\uff0c\u770b \u7740\u5e08\u5f1f\uff0c\u4e00\u62d0\u4e00\u62d0\u5730\u8d70\u5230\u9f50\u771f\u541b\u9762\u524d\uff0c\u54ed\u8bf7\u5e08\u7236\u62a5\u4ec7\u3002\u90b1\u4e1c\u6d1b\u9053\uff1a\u201c\u90a3\u5343\u6740\u7684\u5c31\u662f\u51cc\u672a \u98ce\uff01\u201d\u9f50\u771f\u541b\u9762\u8272\u5927\u53d8\uff0c\u4e60\u60ef\u5730\u634b\u634b\u80e1\u5b50\uff0c\u4e00\u6478\u4e4b\u4e0b\uff0c\u624d\u9192\u8d77\u4e00\u5927\u7edc\u80e1\u5b50\u5df2\u7ed9\u62d4\u53bb\uff0c\u770b\u8005\u4e24 \u4e2a\u5f92\u5f1f\u7684\u7cdf\u6837\u5b50\uff0c\u60f3\u7740\u81ea\u5df1\u4e5f\u662f\u4e00\u6837\u7684\u72fc\u72c8\uff0c\u53c8\u7f9e\u53c8\u6012\u53c8\u662f\u5fc3\u60ca\uff0c\u8bb0\u8d77\u4e94\u5341\u5e74\u524d\u6666\u660e\u7985\u5e08\u7684 \u8bdd\uff0c\u6697\u9053\uff1a\u602a\u4e0d\u5f97\u4ed6\u8bf4\u81ea\u5df1\u7684\u5251\u6cd5\u6709\u7f3a\u70b9\uff0c\u679c\u7136\u8fde\u4ed6\u7684\u5173\u95e8\u5f92\u5f1f\uff0c\u5251\u672f\u90fd\u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c\u9762\u4e0a \u65e0\u5149\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u5f84\u81ea\u53bb\u627e\u695a\u662d\u5357\u3002 \u3000\u3000\u518d\u8bf4\u97e9\u5fd7\u90a6\u4e00\u884c\u4eba\u56de\u81f3\u897f\u5c71\uff0c\u98de\u7ea2\u5348\u9893\u7136\u5750\u4e0b\uff0c\u53f9\u9053\uff1a\u201c\u738b\u5983\u6b7b\u4e86\uff0c\u8fd9\u5973\u5a03\u5b50\u4e5f\u5b8c \u4e86\uff01\u201d\u51cc\u672a\u98ce\u9ed8\u7136\u95ee\u9053\uff1a\u201c\u738b\u5983\u600e\u4e48\u6b7b\u7684\uff1f\u201d\u98de\u7ea2\u5dfe\u628a\u5f53\u65f6\u7684\u60c5\u5f62\u8bf4\u4e86\uff0c\u51cc\u672a\u98ce\u4e5f\u7981\u4e0d\u4f4f\u6cea \u54bd\u5fc3\u9178\u3002\u5927\u5bb6\u9ed8\u5750\u65e0\u8a00\uff0c\u826f\u4e45\uff0c\u826f\u4e45\uff0c\u98de\u7ea2\u5dfe\u5ffd\u7136\u8df3\u8d77\u6765\u9053\uff1a\u201c\u6211\u51e0\u4e4e\u5fd8\u4e86\u5979\u4e34\u6b7b\u7559\u4e0b\u4e86\u4e00 \u53e5\u8bdd\uff01\u201d\u51cc\u672a\u98ce\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u5979\u8bf4\u660e\u5929\u9ec4\u660f\u65f6\u5206\uff0c\u4ed6\u4eec\u8981\u89e3\u6613\u5170\u73e0 \u5230\u5211\u90e8\u5927\u5802\u4f1a\u5ba1\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\uff1a\u6211\u4eec\u4e2d\u9014\u62e6\u52ab\uff1f\u201d\u98de\u7ea2\u5dfe\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e5f\u53ea \u597d\u8fd9\u6837\u4e86\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u6c89\u601d\u6709\u9877\uff0c\u62ac\u5934\u8bf4\u9053\uff1a\u201c\u6050\u6015\u4e0d\u884c\uff0c\u4ed6\u4eec\u5728\u628a\u2018\u94a6\u72af\u2019\u89e3\u51fa\u4e4b\u524d\uff0c\u5929\u7262\u901a\u5211\u90e8\u7684 \u8857\u9053\uff0c\u4e00\u5b9a\u65e9\u5df2\u6212\u4e25\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u6709\u5fa1\u6797\u519b\u9632\u5b88\uff0c\u6211\u4eec\u600e\u80fd\u805a\u96c6\uff1f\u7eb5\u4f7f\u6211\u4eec\u6043\u7740\u6b66\u529f\uff0c\u786c\u95ef\u8fdb \u53bb\uff0c\u4e5f\u53ea\u662f\u6253\u8349\u60ca\u86c7\uff0c\u5230\u6740\u6563\u5fa1\u6797\u519b\u65f6\uff0c\u6613\u5170\u73e0\u65e9\u88ab\u62bc\u56de\u5929\u7262\u4e86\u3002\u201d\u98de\u7ea2\u5dfe\u6012\u9053\uff1a\u201c\u96be\u9053\u6211 \u4eec\u5c31\u773c\u7741\u7741\u770b\u7740\u5979\u88ab\u51cc\u8fdf\u5904\u6b7b\u4e0d\u6210\uff1f\u6709\u4ec0\u4e48\u5371\u96be\u4e5f\u5f97\u8bd5\u5b83\u4e00\u8bd5\uff01\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8c01\u8bf4\u4e0d\u6551\u5979 \u4e86\uff1f\u6211\u53ea\u662f\u76d8\u7b97\u4e00\u6761\u5b89\u5168\u4e4b\u7b56\u3002\u201d\u8fc7\u4e86\u534a\u664c\uff0c\u53cc\u76ee\u95ea\u95ea\u653e\u5149\uff0c\u5bf9\u97e9\u5fd7\u90a6\u4e00\u63fd\u5230\u5730\uff0c\u8bf4\u9053\uff1a \u201c\u770b\u6765\u8fd9\u4e8b\u53ea\u6709\u97e9\u5927\u54e5\u80fd\u5e2e\u6211\u5fd9\uff01\u201d\u97e9\u5fd7\u90a6\u614c\u5fd9\u907f\u5f00\uff0c\u8fd8\u4e86\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u51cc\u5927\u4fa0\u4f60\u53ef\u522b\u8c03 \u4f83\u6211\u4e86\u3002\u6211\u7684\u672c\u9886\u5728\u4f60\u4e24\u4eba\u4e4b\u4e0b\uff0c\u4f60\u4eec\u90fd\u6551\u4e0d\u4e86\uff0c\u6211\u600e\u4e48\u6210\uff1f\u201d\u51cc\u672a\u98ce\u7b11\u9053\uff0c\u201c\u6551\u4eba\u53ef\u5e76\u4e0d \u5168\u662f\u8bb2\u771f\u5200\u771f\u67aa\u7684\uff0c\u4f55\u51b5\u97e9\u5927\u54e5\u7684\u672c\u9886\u4e5f\u9ad8\u5f97\u5f88\u5440\uff01\u90a3\u8001\u5934\u513f\u7684\u80e1\u5b50\u4e0d\u662f\u4e5f\u7ed9\u4f60\u62d4\u4e0b\u4e86 \u4e48\uff1f\u201d\u5f53\u4e0b\u4e00\u624b\u62c9\u97e9\u5fd7\u90a6\uff0c\u4e00\u624b\u62c9\u98de\u7ea2\u5dfe\uff0c\u98d8\u7136\u51fa\u5c4b\uff0c\u5728\u591c\u6797\u4e2d\u6f2b\u6b65\uff0c\u628a\u6240\u76d8\u7b97\u7684\u8ba1\u7b56\u8be6\u7ec6 \u8bf4\u4e86\uff0c\u95ee\u9053\uff1a\u201c\u97e9\u5927\u54e5\uff0c\u4f60\u770b\u6210\u4e0d\u6210\uff1f\u8fd9\u53ef\u5168\u8981\u770b\u4f60\u548c\u4ed6\u4eec\u7684\u4ea4\u60c5\u3002\u201d\u97e9\u5fd7\u90a6\u70b9\u70b9\u5934\u9053\uff1a \u201c\u522b\u6837\u6211\u4e0d\u6562\u8bf4\uff0c\u4ed6\u4eec\u53ef\u5bf9\u6211\u50cf\u81ea\u5df1\u4eba\u4e00\u6837\uff0c\u5bf9\u4f60\u4e5f\u5f88\u611f\u6fc0\uff01\u201d\u98de\u7ea2\u5dfe\u5ffd\u7136\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u5982 \u679c\u6551\u51fa\u6765\u4e86\uff0c\u90a3\u5973\u5a03\u5b50\u53ef\u662f\u6211\u7684\uff0c\u4f60\u4e0d\u8bb8\u548c\u6211\u4e89\uff01\u201d\u51cc\u672a\u98ce\u968f\u53e3\u7b11\u7740\u7b54\u5e94\uff1a\u201c\u6211\u548c\u4f60\u4e89\u5e72 \u561b\uff1f\u4f60\u82e5\u628a\u5979\u6536\u505a\u5973\u513f\uff0c\u6211\u66f4\u6b22\u559c\u3002\u201d\u4e09\u4eba\u5546\u8bae\u5b8c\u4e86\uff0c\u5404\u81ea\u5206\u5934\u5e03\u7f6e\u3002 \u3000\u3000\u518d\u8bf4\u6613\u5170\u73e0\u5728\u6bcd\u4eb2\u53bb\u540e\uff0c\u5fc3\u5982\u6b7b\u7070\u3002\u8fd9\u4e00\u65e5\u4e5f\u4e0d\u77e5\u662f\u767d\u5929\u8fd8\u662f\u9ed1\u591c\uff0c\u72f1\u5352\u628a\u7262\u95e8\u6253\u5f00\uff0c \u628a\u5979\u53cc\u773c\u7528\u539a\u5e03\u8499\u4e0a\uff0c\u63a5\u7740\u542c\u5230\u597d\u591a\u4eba\u7684\u811a\u6b65\u58f0\uff0c\u6709\u4eba\u628a\u81ea\u5df1\u63a8\u5230\u4e00\u8f86\u8f66\u4e0a\u3002 \u3000\u3000\u8f66\u8f9a\u8f9a\uff0c\u9a6c\u8427\u8427\uff0c\u6613\u5170\u73e0\u88ab\u8499\u7740\u53cc\u773c\uff0c\u7f1a\u5728\u8f66\u4e2d\uff0c\u7ecf\u4e86\u4e00\u4e2a\u591a\u6708\u7684\u6298\u78e8\uff0c\u53d7\u4e86\u4e00\u6b21\u5fc3\u7075 \u7684\u91cd\u521b\uff0c\u5979\u7684\u8089\u4f53\u548c\u7cbe\u795e\u90fd\u652f\u4f8d\u4e0d\u4f4f\u4e86\u3002\u5979\u7684\u8eab\u5b50\u968f\u7740\u8f66\u8f86\u7684\u98a0\u7c38\u8d77\u4f0f\u4e0d\u4f11\uff0c\u80a0\u80c3\u975e\u5e38\u4e0d\u597d \u53d7\uff0c\u4e00\u53e3\u82e6\u6c34\u5455\u4e86\u51fa\u6765\u3002\u65c1\u8fb9\u7684\u4eba\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5403\u5230\u82e6\u5934\u4e86\u5427\uff0c\u4f60\u7684\u7236\u4eb2\u4f5c\u5b7d\uff0c\u4f60\u66ff\u4ed6\u8fd8 \u503a\uff0c\u6d3b\u8be5\uff01\u201d\u6613\u5170\u73e0\u8eab\u5b50\u672c\u6765\u5df2\u975e\u5e38\u865a\u5f31\uff0c\u8fd9\u65f6\u5ffd\u7136\u633a\u8d77\u8170\u6765\uff0c\u9a82\u9053\uff1a\u201c\u695a\u662d\u5357\u4f60\u8fd9\u5978\u8d3c\uff0c \u4f60\u914d\u63d0\u8d77\u6211\u7684\u7236\u4eb2\u5417\uff1f\u4ed6\u867d\u6b7b\u4e86\u6bd4\u4f60\u6d3b\u7740\u8fd8\u8981\u5f3a\u4e00\u4e07\u500d\uff01\u201d\u695a\u662d\u5357\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u4e56\u4f84\u5973\uff0c\u4f60 \u5e94\u8be5\u653e\u8f6f\u4e00\u70b9\uff0c\u4f60\u8fd8\u8981\u4f60\u7684\u53d4\u53d4\u66ff\u4f60\u6536\u5c38\u5462\uff01\u201d\u6613\u5170\u73e0\u65a5\u9053\uff1a\u201c\u4e0d\u8981\u8138\uff0c\u4f60\u662f\u8c01\u7684\u53d4\u53d4\uff1f\u4f60 \u8fd9\u6ee1\u6d32\u9774\u5b50\u7684\u8d70\u72d7\uff01\u201d\u695a\u662d\u5357\u6b63\u60f3\u7528\u523b\u6bd2\u7684\u8bdd\u6298\u78e8\u5979\uff0c\u5ffd\u7136\u524d\u9762\u7684\u8f66\u8f86\u9aa4\u7136\u505c\u4e0b\uff0c\u695a\u662d\u5357\u63ed \u5f00\u8f66\u76d6\u4e00\u77a7\uff0c\u53ea\u89c1\u524d\u9762\u6765\u4e86\u4e24\u8f86\u5927\u8f66\uff0c\u5406\u559d\u7740\u8ba9\u9053\u3002\u695a\u662d\u5357\u5927\u4e3a\u5947\u602a\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff0c\u4e3a \u4ec0\u4e48\u8ba9\u4ed6\u95ef\u9053\uff1f\u201d \u3000\u3000\u695a\u662d\u5357\u548c\u9f50\u771f\u541b\u5949\u547d\u5e26\u7740\u4e8c\u5341\u56db\u540d\u5927\u5185\u9ad8\u624b\uff0c\u5206\u4e58\u516d\u8f86\u5927\u8f66\uff0c\u628a\u6613\u5170\u73e0\u4ece\u5929\u7262\u62bc\u5230\u5211 \u90e8\u3002\u4e0d\u51fa\u51cc\u672a\u98ce\u6240\u6599\uff0c\u4ed6\u4eec\u524d\u4e00\u665a\u5df2\u5e03\u7f6e\u4e86\u4e24\u5343\u540d\u5fa1\u6797\u519b\uff0c\u5b88\u7740\u7ecf\u8fc7\u7684\u8857\u9053\uff0c\u4efb\u4f55\u4eba\u90fd\u4e0d\u8bb8 \u901a\u8fc7\u3002\u4ed6\u4eec\u5927\u6e05\u65e9\u5c31\u4ece\u5929\u7262\u51fa\u53d1\uff0c\u6ee1\u4ee5\u4e3a\u6709\u4e86\u8fd9\u6837\u4e25\u5bc6\u7684\u9632\u5907\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u51fa\u4e8b\u3002 \u3000\u3000\u8f66\u9876\u4e0a\u7684\u536b\u58eb\u7b54\u9053\uff1a\u201c\u662f\u897f\u85cf\u6d3b\u4f5b\u7684\u8f66\u4ed7\u3002\u201d\u695a\u662d\u5357\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u9053\u662f \u8c01\uff0c\u539f\u6765\u8fd9\u73ed\u5b9d\u8d1d\uff01\u201d\u897f\u85cf\u6d3b\u4f5b\u7684\u7279\u4f7f\uff0c\u5728\u4eac\u5e08\u91cc\u751a\u53d7\u4f18\u793c\uff0c\u597d\u50cf\u5bf9\u5f85\u5916\u56fd\u4f7f\u8005\u4e00\u6837\u3002\u6212\u4e25 \u4ee4\u53ea\u80fd\u65bd\u7528\u4e8e\u4e00\u822c\u5b98\u6c11\uff0c\u6d3b\u4f5b\u4f7f\u8282\u7684\u8f66\u4ed7\uff0c\u5fa1\u6797\u519b\u53ef\u4e0d\u6562\u62e6\u963b\uff01 \u3000\u3000\u695a\u662d\u5357\u76ee\u529b\u6781\u597d\uff0c\u9065\u9065\u770b\u89c1\u524d\u9762\u8f66\u4ed7\u4e0a\u7ad9\u7740\u5341\u591a\u4e2a\u5587\u561b\uff0c\u5176\u4e2d\u4e24\u4eba\u76f8\u8c8c\u9887\u719f\uff0c\u4e00\u4eba\u8bb0\u5f97 \u662f\u4ee5\u524d\u968f\u5f20\u5929\u8499\u62a4\u9001\u820d\u5229\u5b50\u7684\u5587\u561b\u3002\u8fd9\u8fd8\u7f62\u4e86\uff0c\u53e6\u4e00\u4eba\u867d\u7a7f\u770b\u5927\u7ea2\u50e7\u888d\uff0c\u795e\u6001\u4e3e\u6b62\u5374\u4e0e\u4e00\u822c \u5587\u561b\u6709\u522b\u3002\u695a\u662d\u5357\u770b\u4e86\u4e24\u773c\uff0c\u731b\u5730\u9192\u8d77\u8fd9\u4eba\u5c31\u662f\u5929\u5730\u4f1a\u7684\u603b\u8235\u4e3b\u97e9\u5fd7\u90a6\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u6b63\u60f3\u63ed \u7834\uff0c\u5ffd\u7136\u524d\u9762\u5df2\u6709\u4eba\u53eb\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u662f\u5047\u5192\u7684\uff01\u201d\u970e\u65f6\u95f4\uff0c\u90a3\u4e24\u8f86\u5927\u8f66\uff0c\u8df3\u51fa\u8bb8\u591a\u4eba\uff0c\u6697\u5668 \u4e71\u98de\uff0c\u5200\u5251\u9f50\u4e3e\uff0c\u50cf\u4e00\u7fa4\u75af\u864e\u4f3c\u7684\uff0c\u6df7\u6740\u8fc7\u6765\u3002\u695a\u662d\u5357\u5949\u547d\u4e13\u5b88\u5973\u72af\uff0c\u6050\u6015\u6709\u5931\uff0c\u4e0d\u6562\u79bb \u5f00\u3002 \u3000\u3000\u8f66\u91cc\u8df3\u51fa\u7684\u90a3\u7fa4\u4eba\u6b63\u662f\u51cc\u672a\u98ce\u4ed6\u4eec\uff0c\u4ed6\u4eec\u662f\u5047\u5192\u7684\uff0c\u53ef\u662f\u6d3b\u4f5b\u4f7f\u8282\u7684\u8f66\u4ed7\u548c\u8f66\u524d\u9762\u7684\u4e03 \u516b\u4e2a\u5587\u561b\u5374\u662f\u771f\u7684\u3002\u539f\u6765\u97e9\u5fd7\u90a6\u7ed9\u897f\u85cf\u5587\u561b\u62a2\u56de\u820d\u5229\u5b50\uff0c\u4ed6\u4eec\u628a\u4ed6\u8fce\u5165\u897f\u85cf\uff0c\u5f85\u5982\u4e0a\u5bbe\u3002\u8fd9 \u6b21\u540c\u5728\u4eac\u5e08\uff0c\u97e9\u5fd7\u90a6\u5055\u51cc\u672a\u98ce\u53bb\u627e\u7ea2\u8863\u5587\u561b\u5546\u501f\u5173\u6587\u8f66\u4ed7\uff0c\u7ea2\u8863\u5587\u561b\u597d\u751f\u4e3a\u96be\uff0c\u51cc\u672a\u98ce\u9053\uff1a \u201c\u4e8b\u8d25\u4e4b\u540e\uff0c\u4f60\u5f53\u662f\u6211\u4eec\u5077\u53bb\u7684\u597d\u4e86\u3002\u7687\u5bab\u91cc\u7684\u6731\u679c\u91d1\u7b26\u6211\u4eec\u90fd\u6709\u672c\u9886\u5077\uff0c\u4f55\u51b5\u8fd9\u4e9b\u5173\u6587\u8f66 \u4ed7\uff1f\u7687\u4e0a\u89c1\u8fc7\u6211\u4eec\u7684\u624b\u6bb5\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u76f8\u4fe1\u7684\uff01\u201d\u7ea2\u8863\u5587\u561b\u4e00\u60f3\uff1a\u97e9\u5fd7\u90a6\u662f\u897f\u85cf\u50e7\u4f17\u7684\u6069\u4eba\uff0c \u51cc\u672a\u98ce\u662f\u81ea\u5df1\u7684\u6069\u4eba\uff08\u5728\u4e94\u53f0\u5c71\u8c37\u65f6\uff0c\u51cc\u672a\u98ce\u66fe\u6551\u8fc7\u4ed6\u3002\u89c1\u672c\u4e66\u7b2c\u4e09\u56de\uff09\uff0c\u867d\u7136\u6709\u70b9\u5192\u9669\uff0c \u53ef\u4e5f\u4e0d\u80fd\u4e0d\u501f\uff01 \u3000\u3000\u51cc\u672a\u98ce\u7b49\u501f\u4e86\u6d3b\u4f5b\u4f7f\u8282\u7684\u8f66\u4ed7\uff0c\u7531\u5b97\u8fbe\xb7\u5b8c\u771f\u5e26\u9886\u4e03\u516b\u4e2a\u5587\u561b\u5f53\u5934\uff0c\u7b97\u51c6\u65f6\u95f4\uff0c\u679c\u7136\u95ef \u8fdb\u4e86\u6212\u4e25\u5730\u5e26\uff0c\u62e6\u622a\u4e86\u62bc\u89e3\u6613\u5170\u73e0\u7684\u56da\u8f66\uff0c\u7acb\u523b\u5f15\u8d77\u4e00\u573a\u6df7\u6218\u3002 \u3000\u3000\u51cc\u672a\u98ce\u4e3a\u8c0b\u4e00\u51fb\u6210\u529f\uff0c\u5c06\u8eb2\u5728\u77f3\u9556\u5934\u5bb6\u4e2d\u7684\u4e00\u4f17\u82f1\u96c4\u90fd\u5e26\u4e86\u51fa\u6765\uff0c\u6842\u4ef2\u660e\u3001\u5192\u6d63\u83b2\u3001\u5f20 \u534e\u662d\u3001\u901a\u660e\u548c\u5c1a\u7b49\u4eba\u4e2a\u4e2a\u90fd\u6709\u60ca\u4eba\u7684\u6280\u4e1a\uff0c\u4f46\u6e05\u5ef7\u8fd9\u9762\u6709\u9f50\u771f\u541b\u7387\u9886\u4e8c\u5341\u56db\u540d\u5927\u5185\u9ad8\u624b\u6321 \u7740\uff0c\u58f0\u52bf\u4e5f\u81ea\u4e0d\u5f31\u3002 \u3000\u3000\u695a\u662d\u5357\u5c61\u7ecf\u5927\u654c\uff0c\u9547\u5b9a\u5982\u5e38\uff0c\u6309\u5251\u5b88\u5728\u6613\u5170\u73e0\u8eab\u8fb9\uff0c\u5fc3\u60f3\uff1a\u53ea\u8981\u9f50\u771f\u541b\u6321\u5f97\u4f4f\u51cc\u672a\u98ce\uff0c \u5176\u4ed6\u7684\u4eba\u6765\u62a2\u6211\u90fd\u4e0d\u6015\uff0c\u800c\u4e14\uff0c\u82e5\u4e07\u4e00\u654c\u4e0d\u4f4f\u65f6\uff0c\u6613\u5170\u73e0\u5728\u6211\u624b\u4e2d\uff0c\u4ed6\u4eec\u4e5f\u987b\u6295\u9f20\u5fcc\u5668\uff01 \u3000\u3000\u695a\u662d\u5357\u5c4f\u606f\u4ee5\u5f85\uff0c\u53ea\u89c1\u524d\u9762\u5200\u5149\u5251\u5f71\uff0c\u6253\u5f97\u5341\u5206\u6fc0\u70c8\uff0c\u51cc\u672a\u98ce\u867d\u5df2\u73b0\u8eab\uff0c\u4f46\u4e00\u65f6\u5374\u653b\u4e0d \u8fc7\u6765\uff0c\u695a\u662d\u5357\u6697\u81ea\u5fc3\u559c\uff0c\u6b63\u81ea\u76d8\u7b97\u628a\u56da\u8f66\u9a7e\u56de\u5929\u7262\uff0c\u5ffd\u7136\u95f4\uff0c\u7a81\u89c1\u524d\u9762\u98de\u8d77\u4e00\u6761\u4eba\u5f71\uff0c\u8fc5\u903e \u98de\u9e1f\uff0c\u5de6\u9762\u4e00\u515c\uff0c\u53f3\u9762\u4e00\u7ed5\uff0c\u970e\u5ffd\u5411\u4e1c\uff0c\u970e\u5ffd\u5411\u897f\uff0c\u9f50\u771f\u541b\u3001\u6210\u5929\u633a\u8fd9\u4e24\u4e2a\u6700\u9ad8\u7684\u597d\u624b\uff0c\u6b63 \u548c\u51cc\u672a\u98ce\u3001\u6842\u4ef2\u660e\u7f20\u6597\uff0c\u65e0\u6cd5\u62bd\u8eab\uff0c\u5176\u4ed6\u7684\u5927\u5185\u9ad8\u624b\uff0c\u7adf\u81ea\u62e6\u622a\u4e0d\u4f4f\uff0c\u7ed9\u5979\u5c55\u5f00\u8f7b\u7075\u8fdb\u6377\u7684 \u8eab\u6cd5\uff0c\u970e\u5ffd\u5c31\u51b2\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u695a\u662d\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u5b9a\u775b\u770b\u65f6\uff0c\u90a3\u6761\u4eba\u5f71\u5df2\u6251\u4e0a\u8f66\u9876\uff0c\u97ad\u98ce\u547c\u547c\uff0c\u4e24\u540d\u536b\u58eb\u5e94\u58f0\u5012\u5730\uff0c\u8fd9 \u4eba\u6b63\u662f\u4e8c\u5341\u591a\u5e74\u7684\u5a01\u9707\u585e\u5916\u7684\u98de\u7ea2\u5dfe\u3002 \u3000\u3000\u695a\u662d\u5357\u5bf9\u98de\u7ea2\u5dfe\u672c\u81ea\u6709\u4e9b\u5fc3\u602f\uff0c\u8fd9\u65f6\u4e5f\u987e\u4e0d\u5f97\u4e86\uff0c\u624b\u4e2d\u5251\u4e00\u63d0\u4e00\u7ffb\uff0c\u9752\u5149\u95ea\u5904\uff0c\u201c\u6a35\u592b \u95ee\u8def\u201d\uff0c\u5237\u7684\u4e00\u5251\uff0c\u76f4\u5954\u98de\u7ea2\u5dfe\u534e\u76d6\u7a74\u624e\u53bb\u3002\u98de\u7ea2\u5dfe\u80a9\u5934\u4e00\u6643\uff0c\u957f\u97ad\u77ed\u5251\uff0c\u5de6\u53f3\u4e00\u5206\uff0c\u97ad\u5377 \u9752\u950b\uff0c\u5251\u523a\u80c1\u4e0b\uff0c\u4e24\u822c\u5175\u5668\uff0c\u4e24\u79cd\u653b\u6cd5\uff0c\u4e00\u6d3e\u8fdb\u624b\u62db\u6570\uff0c\u51cc\u5389\u4e4b\u6781\u3002\u695a\u662d\u5357\u624b\u4e2d\u5251\u4e00\u62bd\uff0c\u987a \u7740\u97ad\u52bf\uff0c\u5411\u4e0a\u4e00\u62d6\uff0c\u628a\u957f\u97ad\u5f15\u5f00\uff0c\u500f\u5730\u6a2a\u8eab\uff0c\u5de6\u624b\u634f\u7740\u5251\u8bc0\u5411\u5916\u4e00\u63a8\uff0c\u53f3\u624b\u5251\u5411\u4e0b\u4e00\u6c89\uff0c\u5f80 \u5916\u4e00\u5c55\uff0c\u4e0a\u523a\u5c0f\u8179\uff0c\u4e0b\u65a9\u53cc\u817f\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u8fde\u4f7f\u51fa\u4e09\u62db\u6781\u5389\u5bb3\u7684\u62db\u6570\uff0c\u4ece\u201c\u5f15\u864e\u5f52\u5c71\u201d\u5316\u4e3a \u201c\u91d1\u96d5\u5c55\u7fc5\u201d\uff0c\u62db\u6570\u5c1a\u672a\u4f7f\u5b8c\uff0c\u53c8\u518d\u53d8\u4e3a\u201c\u79fb\u661f\u6458\u6597\u201d\uff0c\u5316\u5b88\u4e3a\u653b\uff0c\u5251\u5982\u62bd\u4e1d\uff0c\u7ef5\u7ef5\u4e0d\u7edd\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u8fd0\u7edd\u9876\u8f7b\u529f\uff0c\u548c\u4ed6\u4e00\u6837\uff0c\u540c\u65f6\u8fd0\u7528\u4e09\u79cd\u8eab\u6cd5\uff0c\u907f\u62db\u8fdb\uff0c\u77ed\u5251\u659c\u98de\uff0c\u957f\u97ad\u6a2a\u626b\uff0c\u4e00 \u6b65\u4e5f\u4e0d\u9000\u8ba9\uff01 \u3000\u3000\u4e24\u4eba\u62db\u6570\u90fd\u662f\u5feb\u901f\u4e4b\u6781\uff0c\u7535\u5149\u77f3\u706d\u4e4b\u95f4\uff0c\u5c31\u62c6\u4e86\u5341\u591a\u62db\u3002\u8bba\u6b66\u827a\uff0c\u4e24\u4eba\u6b63\u662f\u534a\u65a4\u516b\u4e24\uff0c \u8c01\u4e5f\u80dc\u4e0d\u4e86\u8c01\uff1b\u8bba\u6c14\u529b\uff0c\u5374\u8fd8\u662f\u695a\u662d\u5357\u66f4\u80fd\u6301\u4e45\u3002\u98de\u7ea2\u5dfe\u5df2\u77a5\u89c1\u6613\u5170\u73e0\u88ab\u7f1a\u5728\u8f66\u4e2d\uff0c\u5374\u662f\u65e0 \u6cd5\u5c06\u695a\u662d\u5357\u6253\u9000\uff0c\u800c\u4e24\u9762\u5df2\u6709\u51e0\u540d\u536b\u58eb\uff0c\u6251\u56de\u63f4\u52a9\uff0c\u53c8\u6025\u53c8\u607c\uff0c\u84e6\u5730\u4e00\u58f0\u957f\u5578\uff0c\u559d\u9053\uff1a\u201c\u695a \u662d\u5357\uff0c\u4f60\u6562\u62e6\u6211\uff01\u201d\u594b\u81c2\u4e00\u6296\uff0c\u957f\u97ad\u81ea\u5de6\u5411\u5728\uff0c\u626b\u4e86\u4e2a\u5706\u5708\uff0c\u8eab\u5f62\u731b\u5730\u4e00\u7eb5\uff0c\u4e0d\u987e\u6027\u547d\u5730\u786c \u51b2\u8fc7\u53bb\u3002\u695a\u662d\u5357\u7edd\u6599\u4e0d\u5230\u5979\u5982\u6b64\u62fc\u547d\uff0c\u7adf\u6562\u8eab\u5b50\u51cc\u7a7a\uff0c\u98de\u95ef\u8fc7\u6765\uff0c\u8fd9\u65f6\u695a\u662d\u5357\u82e5\u4e0b\u6740\u624b\uff0c\u5fc5 \u7136\u662f\u4e24\u8d25\u4ff1\u4f24\uff0c\u800c\u98de\u7ea2\u5dfe\u4e5f\u5fc5\u5b9a\u4f24\u5f97\u66f4\u91cd\uff01 \u3000\u3000\u8fd9\u4e00\u77ac\u95f4\uff0c\u5c11\u5e74\u60c5\u4e8b\u95ea\u7535\u4f3c\u7684\u5728\u695a\u662d\u5357\u5fc3\u5934\u63a0\u8fc7\u3002\u98de\u7ea2\u5dfe\u867d\u7136\u4ece\u672a\u7231\u8fc7\u695a\u662d\u5357\uff0c\u800c\u4e14\u8fd8 \u548c\u6768\u4e91\u9aa2\u4e00\u9053\u6349\u62ff\u4ed6\uff0c\u97ad\u6253\u8fc7\u4ed6\uff0c\u4f46\u98de\u7ea2\u725b\u5230\u5e95\u662f\u695a\u662d\u5357\u552f\u4e00\u559c\u7231\u8fc7\u7684\u4eba\u3002\u98de\u7ea2\u5dfe\u62fc\u4e86\u6027 \u547d\uff0c\u75be\u51b2\u8fc7\u6765\uff0c\u695a\u662d\u5357\u65e0\u6687\u8003\u8651\uff0c\u672c\u80fd\u5730\u5c06\u8eab\u5b50\u4e00\u95ea\uff0c\u98de\u7ea2\u5dfe\u5df2\u5982\u98de\u9e70\u63a0\u8fc7\uff0c\u4e00\u4e0b\u5b50\u5c31\u6293\u8d77 \u4e86\u6613\u5170\u73e0\uff0c\u7ffb\u4e0a\u56da\u8f66\u53bb\u4e86\uff01 \u3000\u3000\u5f85\u695a\u662d\u5357\u6e05\u9192\u4e4b\u540e\uff0c\u98de\u7ea2\u5dfe\u5df2\u63a0\u51fa\u5341\u4f59\u4e08\u5916\uff0c\u8fd9\u65f6\uff0c\u4e24\u65b9\u6df7\u6218\uff0c\u6b63\u6253\u5f97\u7ffb\u7ffb\u6eda\u6eda\uff0c\u8feb\u8fd1 \u4e86\u6765\u3002\u695a\u662d\u5357\u77e5\u9053\u98de\u7ea2\u5dfe\u8f7b\u529f\u8d85\u5353\uff0c\u8fd8\u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c\u53ea\u89c1\u51cc\u672a\u98ce\u4e0e\u9f50\u771f\u541b\u6076\u6218\uff0c\u6740\u5f97\u96be\u5206\u96be \u89e3\uff0c\u6ee1\u8154\u6012\u6c14\uff0c\u90fd\u8f6c\u79fb\u5230\u51cc\u672a\u98ce\u8eab\u4e0a\uff0c\u7d22\u6027\u653e\u8fc7\u98de\u7ea2\u5dfe\uff0c\u957f\u5251\u4e00\u6296\uff0c\u8d70\u504f\u950b\u6025\u4e0a\uff0c\u548c\u9f50\u771f\u541b \u5408\u529b\u5939\u653b\uff0c\u60f3\u628a\u51cc\u672a\u98ce\u6740\u6389\u3002 \u3000\u3000\u9f50\u771f\u541b\u6628\u65e5\u5728\u738b\u5e9c\u4e00\u6218\uff0c\u672c\u6765\u5df2\u7ed9\u97e9\u5fd7\u90a6\u4e0e\u51cc\u672a\u98ce\u5148\u58f0\u9707\u4f4f\uff0c\u4f60\u9053\u4ed6\u4eca\u65e5\u5982\u4f55\u8fd8\u6562\u786c \u62fc\uff1f\u8bf4\u6765\u6709\u6bb5\u8da3\u4e8b\u3002\u539f\u6765\u9f50\u771f\u541b\u4e00\u5230\u4eac\u5e08\uff0c\u671d\u89c1\u4e86\u5eb7\u7199\u4e4b\u540e\uff0c\u4fbf\u4e0e\u695a\u662d\u5357\u76f8\u89c1\uff0c\u4e24\u4eba\u5404\u6f14\u4e86 \u4e00\u8def\u5251\u6cd5\uff0c\u695a\u662d\u5357\u4fbf\u9053\uff1a\u201c\u524d\u8f88\u82e5\u80af\u51fa\u9a6c\uff0c\u51cc\u672a\u98ce\u90a3\u65f6\u78b0\u7740\u5bf9\u624b\u4e86\uff0c\u53ea\u8981\u6211\u4eec\u4e24\u4eba\u8054\u624b\uff0c\u51c6 \u53ef\u628a\u4ed6\u6bc1\u6389\u3002\u201d\u5f53\u65f6\u9f50\u771f\u541b\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u60f3\uff1a\u9664\u4e86\u767d\u53d1\u9b54\u5973\uff0c\u6211\u662f\u5929\u4e0b\u65e0\u654c\u3002\u6666\u660e\u7985\u5e08 \u4e94\u5341\u5e74\u524d\u8fd8\u4e0d\u6562\u548c\u6211\u6bd4\u5251\uff0c\u4f55\u51b5\u4ed6\u7684\u5173\u95e8\u5f92\u5f1f\u3002\u8fd8\u4ee5\u4e3a\u695a\u662d\u5357\u62ac\u9ad8\u8eab\u4ef7\uff0c\u5c06\u4ed6\u7684\u540c\u95e8\u5e08\u5f1f\u6545 \u610f\u5938\u5927\uff0c\u4e0d\u6599\u5728\u738b\u5e9c\u78b0\u5934\uff0c\u7ed9\u51cc\u672a\u98ce\u8fce\u9762\u56db\u62db\uff0c\u6740\u5f97\u5fc3\u60ca\u80c6\u6218\uff0c\u8fc7\u540e\uff0c\u53cd\u800c\u602a\u8d77\u695a\u662d\u5357\u6765\uff0c \u6012\u6c14\u51b2\u51b2\u8dd1\u53bb\u627e\u695a\u662d\u5357\uff0c\u8d23\u4ed6\u85cf\u5978\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5b9e\u8bf4\uff0c\u6559\u6211\u5403\u4e86\u5927\u4e8f\uff1f\u4f60\u6628\u5929\u6f14\u7684 \u90a3\u8def\u5929\u5c71\u5251\u6cd5\u548c\u51cc\u672a\u98ce\u7684\u4e3a\u4f55\u4e0d\u540c\uff1f\u54b1\u4eec\u90fd\u4e3a\u7687\u4e0a\u6548\u529b\uff0c\u5bf9\u52b2\u654c\u5e94\u6c42\u77e5\u5df2\u77e5\u5f7c\uff0c\u4f60\u5374\u85cf\u597d\uff0c \u4e0d\u628a\u4f60\u5e08\u95e8\u5251\u6cd5\u6296\u9732\u51fa\u6765\uff0c\u8ba9\u6211\u6709\u4e2a\u51c6\u5907\u3002\u54fc\uff01\u54fc\uff01\u201d\u8fd9\u8001\u5934\u513f\u5012\u5f88\u76f4\u723d\uff0c\u4ee5\u524d\u602a\u4ed6\u628a\u51cc\u672a \u98ce\u5938\u5927\uff0c\u73b0\u5728\u53cd\u800c\u6697\u602a\u4ed6\u6545\u610f\u5949\u627f\uff0c\u4e0d\u5c06\u51cc\u672a\u98ce\u7684\u771f\u5b9e\u672c\u9886\u544a\u77e5\u3002\u4ed6\u60f3\uff1a\u4f60\u8bf4\u6211\u548c\u51cc\u672a\u98ce\u53ef \u6253\u6210\u5e73\u624b\uff0c\u4e3a\u4f55\u6211\u8fde\u51e0\u62db\u90fd\u6321\u4e0d\u4e86\uff0c\u83ab\u975e\u60f3\u501f\u5200\u6740\u4eba\uff1f \u3000\u3000\u695a\u662d\u5357\u95ee\u4ed6\u600e\u6837\u8f93\u7ed9\u97e9\u5fd7\u90a6\u548c\u51cc\u672a\u98ce\uff0c\u4ed6\u4e00\u8bf4\u4e86\uff0c\u53ea\u662f\u9690\u7792\u7740\u7ed9\u97e9\u5fd7\u90a6\u6253\u8033\u5149\u62d4\u80e1\u5b50\u7684 \u4e8b\u3002\u695a\u662d\u752b\u542c\u4e86\uff0c\u5927\u4e3a\u5947\u602a\uff0c\u9f50\u771f\u541b\u7ad9\u7740\u8bf4\u8bdd\uff0c\u695a\u662d\u5357\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u7a81\u7136\u8fd0\u638c\u5411\u4ed6\u80a9\u5934\u4e00\u6309\uff0c \u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u8bf7\u5750\u4e0b\u6765\u8bf4\u3002\u201d\u9f50\u771f\u541b\u5927\u6012\uff0c\u672c\u80fd\u5730\u8fd0\u8d77\u5185\u529b\uff0c\u80a9\u5934\u5f80\u5916\u4e00\u649e\uff0c\u81ea\u5df1\u867d\u7136 \u7ed9\u6309\u5f97\u7a33\u4e0d\u4f4f\u8eab\u5f62\uff0c\u695a\u662d\u5357\u4e5f\u7ed9\u649e\u5f97\u5012\u9000\u6570\u6b65\u3002\u9f50\u771f\u541b\u6012\u9053\uff1a\u201c\u695a\u662d\u5357\uff0c\u4f60\u4e5f\u8981\u6765\u8003\u6211\uff1f\u201d \u695a\u662d\u5357\u6ee1\u8138\u5806\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u524d\u8f88\u606f\u6012\uff0c\u6211\u73b0\u5728\u5f04\u6e05\u695a\u4f60\u4e3a\u4f55\u8f93\u7ed9\u51cc\u672a\u98ce\u4e86\uff0c\u4f60\u4e0d\u662f\u771f\u8f93\uff0c\u662f \u7ed9\u4ed6\u5413\u9000\u7684\u3002\u201d\u695a\u662d\u5357\u8bd5\u51fa\u9f50\u771f\u541b\u529f\u529b\u9ad8\u8fc7\u81ea\u5df1\uff0c\u62ff\u51cc\u672a\u98ce\u76f8\u6bd4\uff0c\u6700\u5c11\u4e5f\u53ef\u529f\u529b\u6089\u654c\uff0c\u4fbf \u9053\uff1a\u201c\u4ee5\u4f60\u7684\u5251\u6cd5\u529f\u529b\uff0c\u7edd\u4e0d\u4f1a\u51e0\u62db\u5c31\u8f93\u7ed9\u51cc\u672a\u98ce\u3002\u6211\u548c\u51cc\u672a\u98ce\u3001\u98de\u7ea2\u5dfe\u4e24\u4eba\uff0c\u90fd\u66fe\u4ea4\u8fc7\u51e0 \u6b21\u624b\uff0c\u5bf9\u4f60\u4e0d\u59a8\u8bf4\u5b9e\u8bdd\uff0c\u6211\u548c\u98de\u7ea2\u5dfe\u662f\u534a\u65a4\u516b\u4e24\uff0c\u5bf9\u51cc\u672a\u98ce\u5219\u8981\u7565\u5904\u4e0b\u98ce\uff0c\u4f46\u4e5f\u76f8\u5dee\u6709\u9650\uff0c \u4f60\u6253\u5f97\u8d62\u98de\u7ea2\u5dfe\uff0c\u5c31\u4e0d\u5e94\u8f93\u7ed9\u51cc\u672a\u98ce\uff01\u201d \u3000\u3000\u5f53\u4e0b\u695a\u662d\u5357\u628a\u8fd9\u9053\u7406\u8bf4\u7ed9\u9f50\u771f\u541b\u77e5\u9053\uff0c\u4ed6\u8bf4\uff1a\u201c\u6628\u5929\u6211\u770b\u4e86\u4f60\u7684\u5251\u6cd5\uff0c\u8bba\u62db\u6570\u7684\u53d8\u5316\u590d \u6742\uff0c\u548c\u5929\u5c71\u5251\u6cd5\u53ef\u4ee5\u5339\u654c\uff1b\u8bba\u7cbe\u5fae\u5965\u5999\u4e4b\u5904\uff0c\u5374\u8981\u7a0d\u900a\u4e00\u7b79\u3002\u4f46\u6211\u770b\u4f60\u8fd0\u5251\u7684\u529f\u529b\uff0c\u90a3\u5982\u662f \u6df1\u6e5b\u4e4b\u6781\uff0c\u6700\u5c11\u4e0d\u5728\u51cc\u672a\u98ce\u4e4b\u4e0b\u3002\u521a\u624d\u6211\u8fd8\u4e0d\u6562\u76f8\u4fe1\uff0c\u518d\u8bd5\u4e00\u8bd5\uff0c\u6211\u66f4\u76f8\u4fe1\u6211\u7684\u770b\u6cd5\u4e0d\u5dee\uff0c \u7ecf\u4f60\u7684\u529f\u529b\u914d\u4e0a\u5251\u6cd5\uff0c\u548c\u51cc\u672a\u98ce\u6253\u4e2a\u5e73\u624b\u4e0d\u662f\u96be\u4e8b\uff0c\u4ed6\u80fd\u8d62\u4f60\u662f\u56e0\u4e3a\u4ed6\u5728\u660e\uff0c\u4f60\u5728\u6697\uff0c\u6211\u5f88 \u65e9\u5c31\u542c\u5e08\u7236\u8bf4\u8fc7\u4f60\u7684\u98ce\u96f7\u5251\u6cd5\uff0c\u4f60\u5374\u662f\u6628\u5929\u624d\u7b2c\u4e00\u6b21\u89c1\u5230\u5929\u5c71\u5251\u6cd5\u3002\u5929\u5c71\u5251\u6cd5\u8fc5\u901f\u5f02\u5e38\uff0c\u89c1 \u9699\u5373\u5165\uff0c\u4f60\u82e5\u5c01\u95ed\u95e8\u6237\uff0c\u4ee5\u98ce\u96f7\u5251\u6cd5\u7684\u6c89\u7a33\uff0c\u5c3d\u53ef\u5b88\u5f97\u8bb8\u4e45\uff0c\u914d\u4e0a\u4f60\u7684\u529f\u529b\uff0c\u5c31\u7b97\u4e0d\u5c06\u4ed6\u51fb \u5012\uff0c\u4e5f\u53ef\u7d2f\u4ed6\u534a\u6b7b\u3002\u201d\u695a\u662d\u5357\u6068\u6781\u51cc\u672a\u98ce\uff0c\u4e0d\u60dc\u82b1\u534a\u5929\u529f\u592b\uff0c\u628a\u5168\u90e8\u5929\u5c71\u5251\u6cd5\u90fd\u6f14\u7ed9\u9f50\u771f\u541b \u770b\uff0c\u9f50\u771f\u541b\u89c1\u6628\u65e5\u51cc\u672a\u98ce\u6240\u4f7f\u90a3\u56db\u62db\u679c\u7136\u5728\u5185\uff0c\u8fd9\u624d\u4e0d\u8bf4\u695a\u662d\u5357\u85cf\u5978\uff0c\u518d\u9f13\u96c4\u5fc3\uff0c\u613f\u4e0e\u695a\u662d \u5357\u540c\u5fc3\u5408\u529b\u94f2\u9664\u51cc\u672a\u98ce\u3002 \u3000\u3000\u518d\u8bf4\u51cc\u672a\u98ce\u7387\u9886\u7fa4\u96c4\u622a\u52ab\u56da\u8f66\uff0c\u4e0e\u9f50\u771f\u541b\u518d\u5ea6\u76f8\u9022\uff0c\u51cc\u672a\u98ce\u5982\u96c4\u72ee\u731b\u6251\uff0c\u770b\u770b\u62a2\u653b\uff0c\u9f50 \u771f\u541b\u6c89\u7a33\u6bd4\u89e3\uff0c\u4e00\u8fde\u89e3\u62c6\u4e86\u5341\u51e0\u7740\u72e0\u62db\uff0c\u51cc\u672a\u98ce\u6697\u6697\u7eb3\u7f55\uff0c\u5251\u6cd5\u9aa4\u53d8\uff0c\u610f\u5728\u62a2\u5148\uff0c\u589f\u865a\u5b9e \u5b9e\uff0c\u6bcf\u4e00\u62db\u90fd\u672a\u7528\u5c3d\uff0c\u90fd\u85cf\u53d8\u5316\uff0c\u6559\u9f50\u771f\u541b\u6839\u672c\u770b\u4e0d\u51fa\u653b\u5b88\u6765\u8def\uff0c\u628a\u5929\u5c71\u5251\u6cd5\u4f7f\u5f97\u7cbe\u5999\u7edd \u4f26\uff0c\u9f50\u771f\u541b\u53ea\u89c9\u5468\u56f4\u5251\u98ce\u98d2\u7136\uff0c\u4eba\u5f71\u6643\u52a8\uff0c\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u4ed7\u51e0\u5341\u5e74\u529f\u529b\uff0c\u7d27\u7d27\u5c01\u95ed\u95e8\u6237\uff0c \u53ea\u89c1\u4ed6\u5251\u5c16\u597d\u50cf\u633d\u7740\u5343\u65a4\u91cd\u7269\u4e00\u6837\uff0c\u5de6\u653b\u53f3\u5b88\uff0c\u53f3\u653b\u5de6\u62d2\uff0c\u5251\u62db\u867d\u6162\uff0c\u5374\u4e5f\u662f\u4e00\u7247\u9752\u5149\u9563 \u7ed5\uff0c\u7d27\u62a4\u8eab\u8eaf\uff0c\u4e24\u4eba\u5251\u98ce\u76f8\u8361\uff0c\u58f0\u5982\u88c2\u5e1b\uff0c\u5251\u5149\u4e92\u7f20\uff0c\u5ffd\u5408\u5ffd\u5206\uff0c\u53c8\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u4ecd\u662f\u672a \u5206\u80dc\u8d1f\u3002 \u3000\u3000\u51cc\u672a\u98ce\u6740\u5f97\u6027\u8d77\uff0c\u5251\u62db\u518d\u53d8\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u5de6\u624b\u9a88\u6307\u5982\u621f\uff0c\u7adf\u5728\u5251\u6279\u98de\u821e\u4e2d\uff0c\u5bfb\u6687\u62b5\u9699\uff0c \u627e\u5bfb\u9f50\u771f\u541b\u7a74\u9053\uff0c\u800c\u53f3\u624b\u7684\u9752\u94a2\u5251\u5251\u62db\u8d8a\u53d1\u8fc5\u6377\uff0c\u7ffb\u7ffb\u6eda\u6eda\uff0c\u65f6\u800c\u51cc\u7a7a\u9ad8\u8e48\uff0c\u5b9b\u5982\u9e70\u96bc\u98de \u5929\uff0c\u65f6\u800c\u8d34\u5730\u5e73\u94fa\uff0c\u5b9b\u5982\u8776\u821e\u82b1\u5f71\uff0c\u9f50\u771f\u541b\u6321\u5f97\u5251\u523a\uff0c\u8fd8\u8981\u9632\u5907\u70b9\u7a74\uff0c\u82e6\u6597\u4e4b\u4e0b\uff0c\u989d\u5934\u5df2\u662f \u89c1\u6c57\u3002\u51cc\u672a\u98ce\u53f3\u5251\u5de6\u638c\uff0c\u7adf\u597d\u4f3c\u540c\u4f7f\u4e09\u822c\u5175\u5668\u4e00\u6837\uff0c\u4ed6\u7684\u5de6\u638c\u638c\u5288\u6307\u6233\uff0c\u4f3c\u634f\u7740\u4e00\u652f\u70b9\u7a74 \u6485\uff0c\u53c8\u4f3c\u63e1\u7740\u4e00\u628a\u5355\u5200\uff0c\u53d8\u5316\u7684\u7e41\u590d\u7cbe\u5947\uff0c\u8fdc\u5728\u98ce\u96f7\u5251\u6cd5\u4e4b\u4e0a\u3002\u9f50\u771f\u541b\u521d\u521b\u98ce\u96f7\u5251\u6cd5\u4e4b\u65f6\uff0c \u4ee5\u5de6\u53f3\u624b\u7684\u5251\u6cd5\u62db\u6570\u4e0d\u540c\uff0c\u81ea\u4ee5\u4e3a\u521b\u6b66\u6797\u7edd\u5b66\u3001\u5251\u6cd5\u65b0\u7bc7\uff0c\u5e38\u5e38\u5938\u53e3\u8bf4\uff1a\u53e4\u8bed\u4e91\u5fc3\u96be\u4e24\u7528\uff0c \u6211\u5374\u504f\u504f\u80fd\u591f\u4e24\u7528\u3002\u5fd7\u5f97\u610f\u6ee1\uff0c\u4e0d\u77e5\u5929\u4e0b\u4e4b\u5927\uff01\u5982\u4eca\u4e00\u89c1\u51cc\u672a\u98ce\u53f3\u5251\u5de6\u638c\uff0c\u4e24\u624b\u4f7f\u51fa\u4e09\u79cd\u5175 \u5668\u7684\u53d8\u5316\uff0c\u8def\u6570\u6bd4\u8d77\u98ce\u96f7\u5251\u6cd5\u4e24\u624b\u540c\u662f\u4f7f\u5251\u7684\uff0c\u76f8\u8ddd\u4e0d\u77e5\u8981\u8fdc\u8bb8\u591a\uff01\u8fd9\u65f6\u4e0d\u7531\u4ed6\u53c8\u662f\u5fc3\u60ca\uff0c \u53c8\u662f\u5fc3\u670d\uff01\u4ed6\u867d\u7136\u6709\u4e94\u5341\u51e0\u5e74\u529f\u529b\uff0c\u4e5f\u53ea\u80fd\u52c9\u5f3a\u652f\u6301\uff0c\u7ed9\u51cc\u672a\u98ce\u8d8a\u6765\u8d8a\u51cc\u5389\u7684\u653b\u52bf\u8feb\u5f97\u8fde\u8fde \u540e\u9000\uff01 \u3000\u3000\u4e24\u4eba\u6253\u5f97\u7ffb\u7ffb\u6eda\u6eda\uff0c\u98de\u8eab\u8ffd\u9010\uff0c\u8fc7\u4e86\u51e0\u8f86\u5927\u8f66\uff0c\u51cc\u672a\u98ce\u6b63\u6253\u5f97\u6781\u5ea6\u7d27\u5f20\u4e4b\u65f6\uff0c\u5ffd\u89c1\u98de\u7ea2 \u5dfe\u5df2\u544a\u5f97\u624b\uff0c\u63d0\u8d77\u6613\u5170\u73e0\u5411\u53cd\u65b9\u5411\u9003\u53bb\u3002\u51cc\u672a\u98ce\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u4f55\u4ee5\u98de\u7ea2\u4e2a\u5355\u72ec\u9003\u9038\uff0c\u4e0d \u548c\u5927\u4f19\u4f1a\u5408\u4e00\u8d77\uff1f\u53c8\u60f3\u8d77\u5fa1\u6797\u519b\u5df2\u5c01\u95ed\u9644\u8fd1\u8857\u9053\uff0c\u62c5\u5fc3\u5b64\u638c\u96be\u9e23\uff0c\u9003\u4e0d\u51fa\u53bb\uff0c\u7acb\u5373\u5439\u4e86\u4e00\u58f0 \u80e1\u54e8\uff0c\u62db\u547c\u4f17\u4eba\u6740\u51fa\u3002\u4e0d\u6599\u9f50\u771f\u541b\u867d\u5904\u4e0b\u98ce\uff0c\u5c1a\u672a\u843d\u8d25\uff0c\u53cc\u5251\u76d8\u65cb\uff0c\u7d27\u7d27\u7f20\u6597\uff0c\u51cc\u672a\u98ce\u7adf\u4e0d \u80fd\u62bd\u51fa\u8eab\u6765\uff01 \u3000\u3000\u695a\u662d\u5357\u653e\u8d70\u4e86\u98de\u7ea2\u5dfe\uff0c\u957f\u5251\u4e00\u9886\uff0c\u72de\u7b11\u6251\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u51cc\u672a\u98ce\u653e\u4e0b\u5251\u6765\uff0c\u5ff5\u4f60\u540c\u95e8\uff0c\u9976 \u4f60\u4e0d\u6b7b\uff01\u201d\u51cc\u672a\u98ce\u5fae\u8f6c\u8eab\u8eaf\uff0c\u5237\u7684\u4e00\u5251\u523a\u51fa\uff0c\u9a82\u9053\uff1a\u201c\u4e0d\u8981\u8138\uff01\u201d\u695a\u662d\u5357\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6b7b\u5230 \u4e34\u5934\uff0c\u8fd8\u4e0d\u9192\u609f\uff0c\u6211\u53ea\u597d\u66ff\u5e08\u7236\u7ba1\u6559\u4f60\u4e86\uff01\u201d\u4ed6\u7adf\u7136\u4e0d\u987e\u6c5f\u6e56\u7684\u89c4\u77e9\uff01\u4ee5\u4e24\u4e2a\u6210\u540d\u4eba\u7269\uff0c\u8054 \u624b\u6765\u6597\u51cc\u672a\u98ce\uff01 \u3000\u3000\u8fd9\u4e00\u6765\u5f62\u52bf\u9661\u53d8\uff0c\u695a\u662d\u5357\u4ed7\u7740\u9f50\u771f\u541b\u6b63\u9762\u7f20\u7740\u51cc\u672a\u98ce\uff0c\u4e00\u53e3\u957f\u5251\uff0c\u771f\u662f\u77eb\u5982\u6e38\u9f99\uff0c\u5c06\u5929 \u5c71\u5251\u6cd5\u4e2d\u7684\u4e03\u5341\u4e8c\u624b\u201c\u8ffd\u98ce\u5251\u201d\u8fde\u73af\u8fd0\u7528\u3002\u5929\u5c71\u5251\u6cd5\u91c7\u96c6\u5404\u5bb6\u4e4b\u957f\uff0c\u5171\u6709\u4e09\u767e\u516d\u5341\u4e00\u624b\uff0c\u5176 \u4e2d\u6709\u653b\u6709\u5b88\uff0c\u4ea6\u6709\u653b\u5b88\u517c\u5907\u7684\uff0c\u5251\u6cd5\u7684\u7e41\u590d\uff0c\u5251\u62db\u7684\u5947\u591a\uff0c\u90fd\u5728\u5404\u6d3e\u4e4b\u4e0a\uff0c\u5176\u4e2d\u7684\u4e03\u5341\u4e8c\u624b \u8ffd\u98ce\u5251\uff0c\u53c8\u662f\u5168\u91c7\u653b\u52bf\u7684\uff0c\u8981\u78b0\u7740\u8f83\u81ea\u5df1\u5f31\u7684\u654c\u624b\uff0c\u624d\u597d\u8fd0\u7528\u3002\u51cc\u672a\u98ce\u867d\u7136\u8f83\u695a\u662d\u5357\u5f3a\uff0c\u4f46 \u56e0\u4e3a\u529b\u654c\u4e8c\u4eba\uff0c\u653b\u52bf\u5df2\u7ed9\u9f50\u771f\u541b\u6321\u4f4f\uff0c\u695a\u662d\u5357\u4e0d\u5fc5\u62c5\u5fc3\u4ed6\u7684\u51f6\u72e0\u53cd\u51fb\uff0c\u56e0\u6b64\u624d\u6562\u91c7\u53d6\u6700\u51cc\u5389 \u7684\u653b\u52bf\u3002 \u3000\u3000\u51cc\u672a\u98ce\u8fd9\u4e00\u6c14\u975e\u540c\u5c0f\u53ef\uff0c\u53ef\u662f\u4ed6\u77e5\u9053\u540d\u5bb6\u5bf9\u5251\uff0c\u7edd\u5bf9\u4e0d\u80fd\u52a8\u6012\uff0c\u6321\u4e86\u51e0\u62db\uff0c\u5b9a\u4e0b\u5fc3\u6765\uff0c \u805a\u6c14\u51dd\u795e\uff0c\u4ee5\u5929\u5c71\u5251\u6cd5\u4e2d\u653b\u5b88\u517c\u5907\u7684\u516d\u5341\u56db\u8def\u201c\u5bd2\u6d9b\u5251\u201d\u65bd\u5c55\u51fa\u6765\uff0c\u53ea\u89c1\u5251\u5c16\u98a4\u52a8\uff0c\u4e07\u70b9\u94f6 \u5149\uff0c\u771f\u5982\u5bd2\u6d9b\u5377\u5730\uff0c\u6d6a\u82b1\u98de\u7a7a\uff0c\u5343\u70b9\u4e07\u70b9\u98de\u6d12\u4e0b\u6765\uff0c\u4e00\u53e3\u5251\u529b\u654c\u4e24\u540d\u5177\u6709\u7edd\u9876\u529f\u592b\u7684\u9ad8\u624b\uff0c \u5140\u662f\u6beb\u4e0d\u9053\u8ba9\uff01\u695a\u662d\u5357\u89c1\u4ed6\u628a\u5929\u5c71\u5251\u6cd5\u4f7f\u5f97\u5982\u6b64\u795e\u5999\uff0c\u6697\u6697\u5fc3\u60ca\uff0c\u60f3\u8d77\u81ea\u5df1\u8fd9\u5341\u591a\u5e74\u6765\uff0c\u867d \u6709\u8fdb\u6b65\uff0c\u4f46\u5374\u662f\u76f8\u5f62\u89c1\u62d9\u4e86\u3002 \u3000\u3000\u4f46\u867d\u7136\u5982\u6b64\uff0c\u4ed6\u7684\u8ffd\u98ce\u5251\u6cd5\u4ecd\u662f\u51dd\u5389\u65e0\u524d\uff0c\u5251\u5251\u8f9b\u8fa3\u3002\u4ed6\u548c\u9f50\u771f\u541b\u8054\u624b\uff0c\u5a01\u529b\u8fdc\u5728\u51cc\u672a \u98ce\u4e4b\u4e0a\u3002\u4e09\u53e3\u957f\u5251\uff0c\u4f7f\u5230\u75be\u5904\uff0c\u7adf\u5982\u7ec7\u4e86\u4e00\u9762\u5149\u7f51\uff0c\u7f69\u7740\u51cc\u672a\u98ce\u7684\u4e07\u70b9\u94f6\u6d9b\uff0c\u800c\u4e14\u5728\u7d27\u7d27\u6536 \u67ec\uff0c\u628a\u51cc\u672a\u98ce\u7684\u5251\u5149\u538b\u7f29\u4e0b\u6765\u3002\u4e09\u4eba\u8d8a\u6597\u8d8a\u72e0\uff0c\u6709\u4e24\u540d\u536b\u58eb\uff0c\u60f3\u8981\u63d2\u624b\uff0c\u7ed9\u5251\u98ce\u8feb\u8361\uff0c\u94f6\u5149 \u98de\u6d12\uff0c\u7adf\u76f4\u8dcc\u51fa\u53bb\uff0c\u8eab\u4e0a\u53d7\u4e86\u51e0\u5904\u5251\u4eff\uff0c\u4e5f\u4e0d\u77e5\u662f\u7ed9\u51cc\u672a\u98ce\u6240\u521b\uff0c\u8fd8\u662f\u7ed9\u81ea\u5df1\u8bef\u4f24\uff1f\u5176\u4f59\u7684 \u536b\u58eb\uff0c\u54ea\u91cc\u8fd8\u6562\u81ea\u8ba8\u82e6\u5403\uff1f \u3000\u3000\u8fd9\u4e00\u573a\u6076\u6597\uff0c\u6bd4\u8d77\u5929\u7262\u5927\u6218\u4e4b\u5f79\u8fd8\u66f4\u60ca\u9669\uff01\u9f50\u771f\u541b\u4e94\u5341\u4f59\u5e74\u529f\u529b\uff0c\u8db3\u53ef\u5f53\u8e72\u4e94\u540d\u4e00\u7b49\u536b \u58eb\uff0c\u66f4\u52a0\u4e0a\u7cbe\u901a\u5929\u5c71\u5251\u6cd5\u7684\u695a\u662d\u5357\uff0c\u9976\u662f\u51cc\u672a\u98ce\u7edd\u4e16\u6b66\u529f\uff0c\u4e5f\u6321\u4e0d\u4f4f\u8fd9\u4e24\u4eba\u8fde\u73af\u8fdb\u51fb\uff0c\u51cc\u672a \u98ce\u53eb\u9053\uff1a\u201c\u4ef2\u660e\uff0c\u5feb\u6765\uff01\u201d\u4e45\u4e45\u4e0d\u89c1\u56de\u5e94\uff0c\u767e\u5fd9\u4e2d\u4fa7\u76ee\u659c\u89c4\uff0c\u53ea\u89c1\u6842\u4ef2\u660e\u7b49\u4e00\u73ed\u597d\u624b\u90fd\u7ed9\u5927 \u5185\u536b\u58eb\u7eca\u4f4f\uff0c\u5404\u81ea\u82e6\u6597\uff0c\u654c\u4f17\u6211\u5be1\uff0c\u90fd\u62bd\u4e0d\u51fa\u8eab\u6765\uff01\u201d \u3000\u3000\u95ef\u738b\u5e9c\uff0c\u95f9\u5929\u7262\uff0c\u5165\u6df1\u5bab\uff0c\u4e09\u4ef6\u5927\u4e8b\uff0c\u6842\u4ef2\u660e\u90fd\u7ed9\u5192\u6d63\u83b2\u7ba1\u7740\uff0c\u6ca1\u6709\u53c2\u52a0\u3002\u4e00\u4e2a\u591a\u6708 \u6765\uff0c\u5173\u5728\u77f3\u8001\u9556\u5934\u5bb6\u4e2d\uff0c\u6b63\u81ea\u6c14\u95f7\uff0c\u8fd9\u756a\u548c\u5192\u6d63\u83b2\u968f\u7740\u51cc\u672a\u98ce\u622a\u52a8\u56da\u8f66\uff0c\u72b9\u5982\u731b\u864e\u51fa\u62bc\uff0c\u817e \u86df\u5b9d\u5251\u970d\u5730\u5c55\u5f00\uff0c\u500f\u5982\u94f6\u86c7\u75be\u98de\uff0c\u8131\u624b\u800c\u51fa\uff0c\u4e00\u9635\u65ad\u91d1\u81ed\u7389\u4e4b\u58f0\uff0c\u8fce\u9762\u51e0\u4e2a\u536b\u58eb\u7684\u5175\u5203\u5168\u88ab \u622a\u65ad\uff01\u6b63\u60f3\u62db\u547c\u5192\u6d63\u83b2\u540c\u4e0a\uff0c\u53ea\u89c1\u5192\u6d63\u83b2\u6325\u821e\u7740\u4e00\u9053\u91d1\u5149\uff0c\u4e5f\u628a\u653b\u6765\u7684\u5175\u5200\uff0c\u7eb7\u7eb7\u622a\u65ad\uff0c\u6842 \u4ef2\u660e\u5927\u559c\u53eb\u9053\uff1a\u201c\u6d63\u83b2\u59d0\u59d0\uff0c\u4f60\u4ece\u54ea\u91cc\u4e5f\u5f97\u4e86\u4e00\u628a\u5b9d\u5251\uff1f\u201d\u5192\u6d63\u83b2\u7b11\u800c\u4e0d\u7b54\uff0c\u548c\u4ed6\u5e76\u80a9\u4e00 \u7acb\uff0c\u4e24\u70b3\u5b9d\u5251\u5de6\u53f3\u5c55\u5f00\uff0c\u786c\u653b\u786c\u95ef\uff0c\u5341\u5206\u5f97\u610f\uff01 \u3000\u3000\u4e0d\u6599\u4e22\u5251\u53ea\u53ef\u626c\u5a01\u4e8e\u4e00\u65f6\uff0c\u8fd9\u756a\u695a\u662d\u5357\u6311\u9009\u7684\u5927\u5185\u4e8c\u5341\u56db\u4e2a\u9ad8\u624b\uff0c\u4e2a\u4e2a\u6b66\u529f\u7cbe\u5f3a\uff0c\u6709\u51e0 \u4e2a\u5175\u5203\u7ed9\u622a\u65ad\u4e4b\u540e\uff0c\u6362\u4e0a\u6765\u7684\u536b\u58eb\uff0c\u6216\u4f7f\u864e\u5934\u94a9\uff0c\u6216\u4f7f\u5224\u5b98\u7b14\uff0c\u6216\u4f7f\u6df7\u5143\u724c\uff0c\u6216\u4f7f\u86fe\u7709\u523a\uff0c \u6216\u7528\u8f6f\u97ad\uff0c\u6216\u7528\u94dc\u9524\uff0c\u4e0d\u662f\u5b9d\u5251\u96be\u524a\u7684\u91cd\u5175\u5668\uff0c\u5c31\u662f\u8f7b\u7075\u5c0f\u5de7\u7684\u5175\u5203\uff0c\u518d\u4e0d\u7136\u5c31\u662f\u4e13\u514b\u5b9d\u5251 \u7684\u94a9\u523a\u4e4b\u7c7b\uff0c\u800c\u4e14\u5b9d\u5251\u663e\u9732\u4e4b\u540e\uff0c\u654c\u4eba\u5168\u90e8\u7559\u5fc3\u5728\u610f\uff0c\u8f7b\u6613\u4e5f\u4e0d\u5bb9\u6613\u518d\u7ed9\u622a\u65ad\u4e86\u3002 \u3000\u3000\u53ef\u662f\u6842\u4ef2\u660e\u7684\u529f\u529b\uff0c\u4e5f\u975e\u6bd4\u5bfb\u5e38\uff0c\u800c\u4e14\u4ed7\u7740\u5b9d\u5251\uff0c\u5230\u5e95\u5360\u4e86\u4fbf\u5b9c\uff0c\u4ed6\u89c1\u4e00\u73ed\u536b\u58eb\u56f4\u653b\u4e0a \u6765\uff0c\u864e\u543c\u4e00\u58f0\uff0c\u8fd0\u8d77\u795e\u529b\uff0c\u5355\u638c\u53cd\u624b\u4e00\u51fb\uff0c\u628a\u4e00\u9762\u94c1\u724c\u51fb\u98de\uff0c\u817e\u86df\u5b9d\u5251\u821e\u5f97\u864e\u864e\u751f\u98ce\uff0c\u51e0\u4e08 \u4e4b\u5185\uff0c\u5168\u662f\u51b7\u7535\u7cbe\u8292\uff0c\u7aef\u7684\u662f\u6cfc\u6c34\u96be\u8fdb\uff0c\u536b\u58eb\u4eec\u89c1\u5982\u6b64\u5a01\u529b\uff0c\u90fd\u4e0d\u6562\u8fc7\u4efd\u903c\u8fd1\u3002 \u3000\u3000\u6b63\u50f5\u6301\u95f4\uff0c\u5ffd\u7136\u56f4\u7740\u6842\u4ef2\u660e\u7684\u51e0\u4e2a\u536b\u58eb\u500f\u5730\u9000\u4e0b\uff0c\u53e6\u4e00\u540d\u7626\u5c0f\u7684\u9752\u8863\u4f8d\u536b\uff0c\u98de\u63a0\u8fc7\u6765\uff0c \u6842\u4ef2\u660e\u4e00\u5251\u5288\u53bb\uff0c\u53ea\u542c\u5f97\u5f53\u4e00\u58f0\uff0c\u654c\u4eba\u7684\u5175\u5668\u7adf\u642d\u5728\u81ea\u5df1\u7684\u5251\u8eab\u4e0a\uff0c\u4e00\u652f\u9ed1\u5ffd\u5ffd\u7684\u4e1c\u897f\uff0c\u9012 \u5230\u9762\u524d\uff0c\u6842\u4ef2\u660e\u4f38\u624b\u4e00\u6293\uff0c\u6ca1\u6709\u6293\u7740\uff0c\u654c\u4eba\u5df2\u662f\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u62a2\u5230\u4fa7\u9996\uff0c\u518d\u5ea6\u53d1\u62db\uff0c\u6842\u4ef2 \u660e\u8fd9\u65f6\u65b9\u624d\u770b\u5f97\u6e05\u695a\uff0c\u654c\u4eba\u4f7f\u7684\u662f\u4e00\u5bf9\u5224\u5b98\u7b14\u3002\u7cbe\u4e8e\u6253\u7a74\u7684\u4eba\uff0c\u591a\u534a\u662f\u957f\u4e8e\u5c0f\u5de7\u529f\u592b\uff0c\u800c\u62d9 \u4e8e\u5200\u6c14\uff0c\u8ffd\u8fd9\u4e2a\u7626\u5c0f\u7684\u536b\u58eb\uff0c\u529f\u529b\u5374\u4e0d\u5728\u6842\u4ef2\u660e\u4e4b\u4e0b\u7cfb\u662f\u5185\u5916\u517c\u4fee\u7684\u4e00\u4e2a\u52b2\u654c\uff01 \u3000\u3000\u539f\u6765\u6b64\u4eba\u5c31\u662f\u5728\u9102\u738b\u5e9c\u4e2d\u4e0e\u51cc\u672a\u98ce\u3001\u98de\u7ea2\u5dfe\u6076\u6218\u8fc7\u7684\u6210\u5929\u633a\uff0c\u5916\u53f7\u201c\u94c1\u7b14\u5224\u5b98\u201d\uff0c\u4e43\u662f \u5185\u5ef7\u4f8d\u536b\u4e2d\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u548c\u5fa1\u6797\u519b\u7684\u7b2c\u4e00\u9ad8\u624b\u695a\u662d\u5357\u53ef\u8bf4\u5f97\u662f\u5e76\u9a7e\u9f50\u9a71\u3002\u4ed6\u5bf9\u51cc\u672a\u98ce\u4e0e\u98de\u7ea2 \u5dfe\uff0c\u6216\u8bb8\u8981\u7565\u5904\u4e0b\u98ce\uff0c\u5bf9\u6842\u4ef2\u660e\u5219\u662f\u529f\u529b\u6089\u654c\u3002\u6842\u4ef2\u660e\u80dc\u5728\u6709\u628a\u5b9d\u5251\uff0c\u6210\u5929\u633a\u5219\u80dc\u5728\u706b\u5019\u8001 \u5230\uff0c\u6b63\u662f\u534a\u65a4\u516b\u4e24\uff0c\u5404\u64c5\u80dc\u573a\uff01 \u3000\u3000\u6842\u4ef2\u660e\u548c\u6210\u5929\u633a\u68cb\u9022\u5bf9\u624b\uff0c\u4e00\u4e2a\u662f\u631f\u5b9d\u5251\u4e4b\u5a01\uff0c\u5f3a\u653b\u731b\u6251\uff1b\u4e00\u4e2a\u662f\u4ed7\u591a\u5e74\u706b\u5019\uff0c\u6253\u7a74\u795e \u5947\uff1b\u8f97\u8f6c\u6076\u6597\uff0c\u5907\u4e0d\u76f8\u8ba9\uff0c\u805a\u7cbe\u4f1a\u795e\uff0c\u65e0\u6687\u65c1\u987e\u3002\u56e0\u6b64\u51cc\u672a\u98ce\u53eb\u63f4\uff0c\u6842\u4ed9\u660e\u7adf\u662f\u542c\u800c\u4e0d\u95fb\u3002 \u5192\u6d63\u83b2\u6b66\u529f\u867d\u7136\u8f83\u4f4e\uff0c\u4f46\u654c\u65b9\u4e09\u540d\u6b66\u529f\u6700\u9ad8\u7684\u4eba\uff0c\u90fd\u5df2\u5bf9\u4ed8\u51cc\u5728\u98ce\u4e0e\u6842\u4ef2\u660e\u53bb\u4e86\uff0c\u5979\u4ed7\u7740\u5929 \u8679\u5b9d\u5251\u548c\u593a\u547d\u795e\u7802\uff0c\u654c\u4eba\u4e0d\u6562\u8fc7\u4efd\u8feb\u8fd1\uff0c\u5012\u8fd8\u652f\u6301\u5f97\u4f4f\uff0c\u542c\u5f97\u51cc\u672a\u98ce\u547c\u558a\uff0c\u51e4\u773c\u4e00\u77a5\uff0c\u53ea\u89c1 \u90a3\u8fb9\u94f6\u8679\u98de\u821e\uff0c\u8fdc\u671b\u7adf\u4f3c\u4e00\u5ea7\u5251\u5c71\uff0c\u53d1\u51fa\u547c\u547c\u8f70\u8f70\u7684\u58f0\u54cd\uff0c\u4e09\u6761\u4eba\u5f71\uff0c\u5c31\u5982\u4e09\u6761\u9ed1\u7ebf\u4e00\u6837\uff0c \u5728\u94f6\u5149\u6ce2\u6d9b\u4e4b\u4e2d\u4e0a\u4e0b\u5f80\u6765\u3002\u5192\u6d63\u83b2\u76ee\u7729\u5fc3\u60ca\uff0c\u77e5\u9053\u4ee5\u81ea\u5df1\u8fd9\u70b9\u80fd\u529b\uff0c\u7edd\u63d2\u4e0d\u8fdb\u53bb\uff0c\u6025\u5fd9\u53eb \u9053\uff1a\u201c\u4ef2\u660e\uff0c\u51cc\u5927\u4fa0\u53eb\u4f60\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u4e0e\u6842\u4ef2\u660e\u76f8\u8ddd\u751a\u8fd1\uff0c\u4ed6\u5bf9\u5192\u6d63\u83b2\u7684\u58f0\u97f3\uff0c\u6709\u4e00\u79cd\u7279\u522b\u611f\u5e94\uff0c\u4e00\u58f0\u5165\u8033\uff0c\u7acb\u523b\u8dc3 \u8d77\uff0c\u6210\u5929\u633a\u559d\u9053\uff1a\u201c\u54ea\u91cc\u8d70\uff01\u201d\u5224\u5b98\u7b14\u5de6\u53f3\u4e00\u5206\uff0c\u5206\u624e\u6842\u4ef2\u660e\u5de6\u53f3\u7684\u201c\u5206\u6c34\u7a74\u201d\uff0c\u6842\u4ef2\u660e\u4e00 \u8f6c\u8eab\u5f62\uff0c\u4e00\u8bb0\u201c\u9965\u9e70\u63a0\u7fbd\u201d\u5b9d\u5251\u6a2a\u626b\u4e0b\u6765\uff0c\u6210\u5929\u633a\u597d\u751f\u5389\u5bb3\uff0c\u53ea\u89c1\u6842\u4ef2\u660e\u4e00\u95ea\uff0c\u7acb\u523b\u77e5\u9053\u4ed6 \u662f\u4ee5\u8fdb\u4e3a\u9053\uff0c\u987f\u65f6\u624b\u4e00\u7ffb\uff0c\u5224\u5b98\u7b14\u6a2a\u67b6\u91d1\u6881\uff0c\u53c8\u628a\u6842\u4ef2\u660e\u7684\u5251\u8361\u5f00\uff0c\u970d\u5730\u4e00\u4e2a\u65cb\u8eab\uff0c\u65b9\u4f4d\u518d \u53d8\uff0c\u94c1\u7b14\u4e00\u9012\uff0c\u53c8\u70b9\u6842\u4ef2\u660e\u5de6\u808b\u540e\u7684\u201c\u9b42\u95e8\u7a74\u201d\u3002\u6842\u4ef2\u660e\u52c3\u7136\u5927\u6012\uff0c\u7528\u4e94\u79bd\u5251\u6cd5\u4e2d\u7684\u62fc\u547d\u62db \u6570\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u659c\u5288\u4e0b\u6765\uff0c\u521a\u4f7f\u5230\u4e00\u534a\uff0c\u500f\u53c8\u6539\u5288\u4e3a\u626b\uff0c\u4e00\u62db\u201c\u94c1\u9501\u6a2a\u821f\u201d\uff0c\u5411\u654c\u4eba\u4ea9\u80a9\u731b \u524a\uff0c\u8fd9\u4e24\u62db\u8fc5\u5982\u7535\u5fd7\uff0c\u53d8\u5316\u6781\u901f\u3002\u6210\u5929\u633a\u85cf\u5934\u7f29\u9888\uff0c\u5411\u4e0b\u4e00\uff0c\u77ee\u8eab\u8eaf\uff0c\u817e\u86df\u5251\u5251\u547c\u7684\u4e00\u58f0\u4ece \u5934\u9876\u524a\u8fc7\uff0c\u6210\u5929\u633a\u559d\u58f0\u201c\u6253\u201d\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u53cc\u7b14\u76f4\u7ad6\u8d77\u6765\uff0c\u6307\u5411\u6842\u4ef2\u660e\u7684\u4e39\u7530\u7a74\u3002\u6842\u4ef2\u660e\u7ed9 \u4ed6\u51a4\u9b42\u4f3c\u7684\u82e6\u82e6\u7f20\u6597\uff0c\u65e0\u6cd5\u8131\u8eab\uff0c\u5fc3\u5ff5\u51cc\u672a\u98ce\u5904\u5883\uff0c\u6781\u4e3a\u7126\u6025\u3002 \u3000\u3000\u9f50\u771f\u541b\u4e0e\u695a\u662d\u5357\u4e8c\u4eba\u5404\u5c55\u72ec\u95e8\u5251\u6cd5\uff0c\u5927\u6218\u51cc\u672a\u98ce\uff0c\u95ea\u7535\u60ca\u98d8\uff0c\u8d8a\u6597\u8d8a\u70c8\uff0c\u8d8a\u6253\u8d8a\u5feb\uff0c\u4e09 \u53e3\u5251\u8054\u6210\u4e00\u9762\u5149\u7f51\uff0c\u5df2\u628a\u51cc\u672a\u98ce\u7684\u5251\u5149\u538b\u7f29\u4e0b\u53bb\uff0c\u770b\u770b\u5f97\u624b\uff0c\u5ffd\u7136\u4e00\u6761\u4eba\u5f71\uff0c\u8eab\u6cd5\u53e4\u602a\u4e4b \u6781\uff0c\u8d8a\u8fc7\u4f17\u536b\u58eb\u7684\u515c\u622a\uff0c\u76f4\u6251\u8fc7\u6765\uff0c\u9f50\u771f\u541b\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u6162\u5f97\u4e00\u6162\uff0c\u51cc\u672a\u98ce\u4e58\u52bf\u5237\u7684\u4e00\u5251 \u523a\u51fa\uff0c\u628a\u4ed6\u7684\u888d\u8896\u523a\u7a7f\uff01\u9f50\u771f\u541b\u9000\u540e\u4e00\u6b65\uff0c\u51cc\u672a\u98ce\u7684\u5bd2\u6d9b\u5251\u6cd5\u7cbe\u5999\u7edd\u4f26\uff0c\u4e58\u9699\u5373\u5165\uff0c\u94f6\u5149\u98de \u6d12\uff0c\u4e00\u4e0b\u628a\u654c\u4eba\u622a\u5f00\uff0c\u8054\u624b\u5408\u6597\u7684\u9635\u52bf\u6682\u89e3\uff0c\u51cc\u672a\u98ce\u62a2\u5230\u4e0a\u9996\uff0c\u9752\u94a2\u5251\u75be\u7684\u4e00\u51b2\uff0c\u695a\u662d\u5357\u6025 \u56de\u6321\u4f4f\u3002 \u3000\u3000\u695a\u662d\u5357\u5fc3\u91cc\u6697\u6697\u751f\u6c14\uff0c\u5fc3\u60f3\u8fd9\u9f50\u771f\u541b\u5728\u6d3b\u4e86\u8fd9\u4e48\u4e00\u5927\u628a\u5e74\u7eaa\uff0c\u600e\u7684\u7ecf\u9a8c\u5982\u6b64\u4e4b\u5dee\uff0c\u8054\u624b \u5408\u6597\uff0c\u5206\u660e\u5df2\u5360\u4e86\u7edd\u5bf9\u4e0a\u98ce\uff0c\u770b\u770b\u5c31\u53ef\u81f4\u51cc\u672a\u98ce\u4e8e\u6b7b\u5730\uff0c\u4ed6\u5374\u65e0\u7aef\u7aef\u7684\u8fd9\u4e48\u4e00\u9053\uff0c\u7ed9\u51cc\u672a\u98ce \u7f13\u4e86\u53e3\u6c14\uff0c\u8981\u52c7\u597d\u5230\u5148\u524d\u90a3\u79cd\u4f18\u52bf\uff1a\u53c8\u5f97\u8d39\u4e00\u756a\u624b\u811a\u4e86\u3002 \u3000\u3000\u695a\u662d\u5357\u6b63\u81ea\u751f\u6c14\uff0c\u90a3\u6761\u4eba\u5f71\u5df2\u6b3a\u8eab\u75be\u8fdb\uff0c\u695a\u662d\u5357\u773c\u89c2\u516d\u8def\uff0c\u4ed6\u7565\u8f6c\u8eab\u8eaf\uff0c\u4e00\u4e2a\u201c\u8e6c \u811a\u201d\uff0c\u5012\u8e22\u51fa\u53bb\uff0c\u4e0d\u6599\u654c\u4eba\u7684\u638c\u98ce\u5ffd\u7136\u5288\u5230\u9762\u95e8\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f\u7ed5\u54ea\u4e2a\u65b9\u4f4d\u8fc7\u6765\u7684\uff01 \u3000\u3000\u695a\u662d\u5357\u5927\u5403\u4e00\u60ca\uff0c\u4f46\u4ed6\u5230\u5e95\u662f\u540d\u5bb6\u8eab\u624b\uff0c\u4e34\u5371\u4e0d\u4e71\uff0c\u5de6\u8155\u4e00\u62ac\u201c\u91d1\u9f99\u63a2\u722a\u201d\uff0c\u7528\u622a\u624b\u6cd5 \u53bb\u64d2\u5bf9\u65b9\u7684\u8109\u95e8\uff0c\u90a3\u4eba\u6e9c\u6ed1\u4e4b\u6781\uff0c\u5ffd\u7136\u7f29\u624b\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5200\u5149\u95ea\u95ea\uff0c\u7adf\u81ea\u540e\u9762\u5288\u6765\uff0c\u695a\u662d\u5357 \u4e07\u6599\u4e0d\u5230\u654c\u4eba\u7684\u5200\u7adf\u7136\u4f1a\u8dd1\u5230\u80cc\u540e\uff0c\u8eab\u5f62\u6025\u8d77\uff0c\u63a0\u51fa\u4e09\u4e08\uff0c\u56de\u9996\u4e00\u671b\uff0c\u4e0d\u7981\u5927\u5947\u3002 \u3000\u3000\u8fd9\u4eba\u6b63\u662f\u97e9\u5fd7\u90a6\uff0c\u4ed6\u4ed7\u7740\u4ece\u4e91\u5c97\u77f3\u7a9f\u5b66\u6765\u7684\u602a\u62db\u5413\u9000\u8bb8\u591a\u5927\u5185\u9ad8\u624b\uff0c\u5192\u9669\u6765\u89e3\u51cc\u672a\u98ce\u4e4b \u5371\uff0c\u9f50\u771f\u541b\u5148\u77a7\u89c1\u4ed6\uff0c\u5fc3\u91cc\u4e00\u7a92\uff0c\u6240\u4ee5\u53eb\u51fa\u58f0\u6765\uff0c\u4f46\u4ed6\u4e0d\u77e5\u97e9\u5fd7\u90a6\u4e5f\u5f88\u60e7\u6015\u4ed6\uff0c\u97e9\u5fd7\u90a6\u524d\u5929 \u5728\u4ed6\u80cc\u5fc3\u6253\u4e86\u4e00\u638c\uff0c\u53d7\u4e86\u53cd\u5f39\u4e4b\u529b\uff0c\u73b0\u5728\u8fd8\u9690\u9690\u4f5c\u75db\u3002 \u3000\u3000\u6b63\u56e0\u5f7c\u6b64\u6709\u6240\u987e\u5fcc\uff0c\u6240\u4ee5\u97e9\u5fd7\u90a6\u4e5f\u4e0d\u6562\u78b0\u4ed6\uff0c\u8f6c\u800c\u6697\u88ad\u695a\u662d\u5357\uff0c\u679c\u7136\u8fce\u9762\u4e09\u62db\uff0c\u5200\u638c\u5e76 \u7528\uff0c\u628a\u695a\u662d\u5357\u8feb\u51fa\u5708\u5b50\u3002\u51cc\u672a\u98ce\u538b\u529b\u5927\u51cf\uff0c\u81ea\u7136\u662f\u9a6c\u4e0a\u53c8\u62a2\u4e0a\u98ce\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u672c\u662f\u695a\u662d\u5357\u624b\u4e0b\u8d25\u5c06\uff0c\u5728\u4e91\u5c97\u77f3\u7a9f\uff0c\u62a2\u820d\u5229\u5b50\u4e4b\u65f6\uff0c\u695a\u662d\u5357\u66fe\u4ee5\u4e00\u53cc\u8089\u638c\uff0c\u6253\u8d25 \u97e9\u5fd7\u90a6\u7684\u516b\u5366\u7d2b\u91d1\u5200\uff0c\u80dc\u6765\u6beb\u4e0d\u5403\u529b\uff01\u73b0\u5728\u89c1\u4ed6\u62db\u6570\u53e4\u602a\u4e4b\u6781\uff0c\u4e0d\u7981\u5927\u5947\uff1a\u600e\u4e48\u8fd9\u4e2a\u571f\u5305 \u5b50\uff0c\u4e0d\u5230\u4e24\u5e74\uff0c\u5c31\u5b66\u4e86\u8fd9\u8eab\u4e0a\u4e58\u6b66\u529f\uff01 \u3000\u3000\u97e9\u5fd7\u90a6\u62a2\u9ad8\u5bab\u8d70\u574e\u4f4d\uff0c\u53c8\u53d1\u4e86\u51e0\u624b\u602a\u62db\uff0c\u695a\u662d\u5357\u56de\u5251\u81ea\u4fdd\uff0c\u8d8a\u53d1\u7eb3\u95f7\uff0c\u800c\u90a3\u8fb9\u51cc\u672a\u98ce\u628a \u9f50\u771f\u541b\u8feb\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u5728\u8ffd\u9010\u4e4b\u9645\uff0c\u7ecf\u8fc7\u6842\u4ef2\u660e\u8eab\u65c1\uff0c\u8fd8\u5077\u51fa\u624b\u6765\uff0c\u5411\u6210\u5929\u633a\u53d1\u4e86\u4e00\u638c\uff0c\u867d \u672a\u6253\u7740\uff0c\u53ef\u662f\u638c\u98ce\u6240\u81f3\u5df2\u628a\u6210\u5929\u633a\u7684\u53cc\u7b14\u8361\u5f00\uff0c\u6842\u4ef2\u660e\u4e58\u52bf\u4e5f\u8df3\u51fa\u4e86\u5708\u5b50\uff0c\u628a\u5192\u6d63\u83b2\u548c\u5f20\u534e \u662d\u63a5\u5e94\u51fa\u6765\u3002 \u3000\u3000\u695a\u662d\u5357\u867d\u7ed9\u97e9\u5fd7\u90a6\u51e0\u624b\u602a\u62db\u5f04\u5f97\u7eb3\u95f7\u4e0d\u5df2\uff0c\u4f46\u4ed6\u5230\u5e95\u662f\u4e2a\u673a\u7075\u7684\u4eba\uff0c\u4e00\u60f3\uff0c\u4ec5\u4ec5\u4e24\u5e74\uff0c \u97e9\u5fd7\u90a6\u7eb5\u5b66\u5f97\u6781\u4e0a\u4e58\u6b66\u529f\uff0c\u529f\u529b\u4e5f\u662f\u4e0d\u591f\uff0c\u4f55\u5fc5\u6015\u4ed6\uff01\u5f53\u4e0b\u628a\u5168\u8eab\u7a74\u9053\u95ed\u4f4f\uff0c\u62fc\u53d7\u4ed6\u602a\u62db\u638c \u51fb\u4e4b\u5371\uff0c\u8fd0\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u5341\u4e09\u8def\u201c\u987b\u5f25\u5251\u201d\u6cd5\uff0c\u4fdd\u536b\u81ea\u5df1\uff0c\u786c\u51b2\u8fc7\u53bb\u3002\u4f5b\u7ecf\u4e2d\u6709\u201c\u987b\u5f25\u82a5 \u5b50\u201d\u4e4b\u8bf4\uff0c\u201c\u987b\u5f25\u201d\u662f\u5ea7\u5927\u5c71\uff0c\u636e\u4e91\u4f5b\u6cd5\u53ef\u5c06\u4e4b\u85cf\u4e8e\u82a5\u5b50\u4e4b\u5185\u3002\u4f5b\u7ecf\u5e38\u501f\u795e\u5947\u7684\u8bf4\u6cd5\uff0c\u6765\u8c08 \u4eba\u751f\u54f2\u7406\uff0c\u8fd9\u91cc\u4e0d\u5fc5\u6df1\u7a76\u3002\u53ea\u8bf4\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u201c\u987b\u5f25\u5251\u6cd5\u201d\uff0c\u5c31\u662f\u501f\u4f5b\u7ecf\u6b64\u8bed\u4f5c\u55bb\uff0c\u5373\u653e\u4e4b \u53ef\u5f25\u516d\u5408\uff0c\u5377\u4e4b\u53ef\u85cf\u4e8e\u5bc6\u7684\u610f\u601d\uff0c\u8fd0\u5251\u4fdd\u536b\u81ea\u5df1\uff0c\u4e0d\u8bba\u7a7a\u65f7\u4e4b\u5730\u6216\u6597\u5ba4\u4e4b\u4e2d\uff0c\u90fd\u53ef\u4f38\u7f29\u81ea \u5982\uff0c\u63d2\u9488\u96be\u8fdb\u3002\u695a\u662d\u5357\u7528\u51fa\u8fd9\u8def\u5251\u6cd5\uff0c\u66f4\u517c\u95ed\u4e86\u7a74\u9053\uff0c\u5bf9\u4ed8\u97e9\u5fd7\u90a6\u90a3\u53ef\u662f\u4e07\u65e0\u4e00\u5931\uff0c\u5f53\u4e0b\u5f3a \u51b2\u8fc7\u53bb\uff0c\u97e9\u5fd7\u90a6\u65e0\u6cd5\u53ef\u65bd\uff0c\u53ea\u597d\u4ed7\u7740\u602a\u5f02\u7684\u8eab\u6cd5\uff0c\u8fde\u8fde\u95ea\u907f\u3002 \u3000\u3000\u90a3\u4e00\u8fb9\u51cc\u672a\u98ce\u5c06\u9f50\u771f\u541b\u51fb\u9000\u4e4b\u540e\uff0c\u5374\u4e0d\u7a77\u8ffd\uff0c\u7a81\u5730\u7ffb\u8eab\u6740\u5165\uff0c\u628a\u901a\u660e\u548c\u5c1a\u3001\u5e38\u82f1\u3001\u7a0b\u901a \u7b49\u4eba\u90fd\u6551\u51fa\u91cd\u56f4\uff0c\u695a\u662d\u5357\u5927\u6025\uff0c\u6025\u5fd9\u8fc7\u6765\u5835\u622a\uff0c\u51cc\u672a\u98ce\u5237\uff01\u5237\uff01\u5237\uff01\u8fde\u73af\u4e09\u5251\uff0c\u6025\u52b2\u5f02\u5e38\uff0c \u695a\u662d\u5357\u529f\u529b\u7a0d\u900a\uff0c\u867d\u7136\u4ed7\u7740\u7edd\u5999\u7684\u987b\u5f25\u5251\u6cd5\uff0c\u4e5f\u7ed9\u8361\u5f00\uff0c\u6025\u5fd9\u62db\u547c\u9f50\u771f\u541b\u8fc7\u6765\u3002\u6842\u4ef2\u660e\u5374\u62a2 \u5728\u524d\u5934\uff0c\u5c06\u817e\u86df\u5251\u5377\u5728\u624b\u4e2d\uff0c\u500f\u5730\u53d1\u51fa\uff0c\u9f50\u771f\u541b\u89c1\u4e00\u4eba\u7a7a\u624b\u8fc7\u6765\uff0c\u4e0d\u52a0\u9632\u5907\uff0c\u5ffd\u7136\u767d\u5149\u4e00 \u9053\uff0c\u98de\u626b\u8fc7\u6765\uff0c\u53f3\u624b\u7684\u957f\u5251\uff0c\u5251\u5c16\u7adf\u7ed9\u524a\u6389\uff01 \u3000\u3000\u9f50\u771f\u541b\u5927\u5403\u4e00\u60ca\uff0c\u5de6\u624b\u957f\u5251\u5f80\u4e0b\u4e00\u6c89\uff0c\u6842\u4ef2\u660e\u987f\u89c9\u4f3c\u5343\u65a4\u91cd\u7269\uff0c\u76f4\u538b\u5251\u8eab\uff0c\u7adf\u7136\u62bd\u4e0d\u51fa \u6765\uff0c\u6025\u8fd0\u5927\u529b\u9e70\u722a\u795e\u529f\uff0c\u500f\u5730\u5411\u654c\u4eba\u624b\u8155\u6293\u53bb\uff0c\u9f50\u771f\u541b\u53f3\u5251\u4e00\u6321\uff0c\u5206\u4e86\u5206\u5fc3\uff0c\u5de6\u5251\u7684\u538b\u529b\u51cf \u5f31\u4e86\u4e9b\uff0c\u6842\u4ef2\u660e\u8d81\u52bf\u75be\u7684\u628a\u5251\u62d4\u51fa\uff0c\u4e24\u4eba\u90fd\u5411\u4fa7\u9762\u9000\u51fa\u51e0\u6b65\u3002 \u3000\u3000\u9f50\u771f\u541b\u5fc3\u91cc\u6697\u6697\u5600\u5495\uff1a\u81ea\u5df1\u5728\u957f\u767d\u5c71\u82e6\u7ec3\u4e86\u4e94\u5341\u591a\u5e74\u7684\u5251\u672f\uff0c\u672c\u4ee5\u4e3a\u53ef\u4ee5\u65e0\u654c\u4e8e\u5929\u4e0b\uff0c \u4e0d\u6599\u4e00\u5230\u4eac\u5e08\uff0c\u5c31\u8fde\u756a\u53d7\u632b\uff0c\u73b0\u5728\u7adf\u7136\u8fde\u8fd9\u6bdb\u5934\u5c0f\u4f19\u5b50\uff0c\u4e5f\u80fd\u628a\u81ea\u5df1\u7684\u5251\u5c16\u524a\u65ad\uff0c\u5230\u5e95\u4e2d\u539f \u6709\u591a\u5c11\u80fd\u4eba\uff1f\u6842\u4ef2\u660e\u4e5f\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u5982\u4e0d\u662f\u8fd9\u628a\u5b9d\u5251\u4e43\u662f\u81f3\u67d4\u81f3\u521a\u4e4b\u7269\uff0c\u7ed9\u4ed6\u4e00\u538b\uff0c\u51c6\u4f1a \u538b\u788e\uff0c\u8fd9\u4efd\u529f\u529b\uff0c\u771f\u662f\u81ea\u5df1\u51fa\u9053\u4ee5\u6765\u6240\u4ec5\u89c1\u3002 \u3000\u3000\u9f50\u771f\u541b\u7f13\u5f97\u4e00\u7f13\uff0c\u51cc\u672a\u98ce\u5df2\u548c\u6842\u4ef2\u660e\u4f1a\u5408\uff0c\u4e00\u4f17\u82f1\u96c4\u731b\u6740\u51fa\u53bb\u3002\u695a\u662d\u5357\u6025\u6025\u5927\u58f0\u547c\u53eb\uff0c \u524d\u9762\u5c01\u9501\u8857\u9053\u7684\u5fa1\u6797\u519b\uff0c\u5df2\u805a\u62e2\u5e03\u6210\u9635\u52bf\uff0c\u957f\u67aa\u5927\u621f\uff0c\u585e\u7740\u53bb\u8def\uff0c\u6c11\u623f\u4e0a\u4e5f\u90fd\u904d\u5e03\u4e86\u5f13\u7bad\u624b \u4e86\u3002 \u3000\u3000\u695a\u662d\u5357\u8fd9\u65f6\u770b\u6e05\u4e86\u654c\u65b9\u7684\u5b9e\u529b\uff0c\u80dc\u5238\u5728\u63e1\uff0c\u6307\u6325\u82e5\u5b9a\u3002\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5929\u633a\uff0c\u4f60\u53bb\u622a\u90a3\u9ec4 \u886b\u5c11\u5e74\uff1b\u9f50\u8001\u524d\u8f88\uff0c\u6211\u4eec\u518d\u8054\u624b\u6597\u51cc\u672a\u98ce\uff1b\u90a3\u571f\u5305\u5b50\u4e0d\u7528\u6015\u4ed6\uff0c\u4ed6\u53ea\u662f\u4e09\u677f\u65a7\uff0c\u5201\u56db\u798f\uff0c\u4f60 \u53bb\u622a\u4ed6\u3002\u5176\u4f59\u7684\u4eba\uff0c\u5144\u5f1f\u4eec\u5e76\u80a9\u5b50\u98df\u6389\u7b97\u5566\uff01\u201d\u695a\u662d\u5357\u7b97\u5b9a\uff1a\u654c\u65b9\u6700\u5389\u5bb3\u7684\u662f\u51cc\u672a\u98ce\u3001\u6842\u4ef2 \u660e\u3001\u97e9\u5fd7\u90a6\u4eba\uff0c\u7167\u8fd9\u6837\u90e8\u7f72\uff0c\u5fc5\u80dc\u65e0\u7591\u3002\u5201\u56db\u798f\u8f7b\u529f\u6781\u597d\uff0c\u529f\u529b\u867d\u5dee\uff0c\u4f46\u6bd4\u8d77\u97e9\u5fd7\u90a6\u5374\u8981\u597d \u4e00\u70b9\uff0c\u695a\u662d\u5357\u53eb\u4ed6\u53bb\u7eca\u97e9\u5fd7\u90a6\uff0c\u90a3\u6b63\u662f\u6700\u9002\u5f53\u7684\u4eba\u9009\u3002 \u3000\u3000\u51cc\u672a\u98ce\u51db\u7136\u4e00\u60ca\uff0c\u5fc3\u60f3\u695a\u662d\u5357\u8fd9\u53ae\u679c\u7136\u5389\u5bb3\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u90a3\u8fd8\u7f62\u4e86\uff0c\u4ed6\u8fd8\u662f\u4e2a\u6559\u7ec3\u4eba \u6750\uff0c\u4e00\u4ea4\u8fc7\u624b\uff0c\u5c31\u77e5\u9053\u5bf9\u65b9\u7684\u4f18\u52bf\u6240\u5728\uff0c\u53ef\u60dc\u98de\u7ea2\u5dfe\u5148\u9003\u51fa\u53bb\uff0c\u8981\u4e0d\u7136\u5012\u53ef\u628a\u4ed6\u7684\u5e03\u9635\u51fb \u7834\u3002\u4f46\u51cc\u672a\u98ce\u4e5f\u662f\u6781\u8001\u7ec3\u7684\u4eba\u7269\uff0c\u8d81\u7740\u4ed6\u4eec\u5c1a\u672a\u5408\u56f4\uff0c\u6253\u4e2a\u80e1\u54e8\uff0c\u628a\u81ea\u5df1\u7684\u4eba\u7d27\u805a\u4e00\u5904\uff0c\u4e00 \u53e3\u9752\u94a2\u5251\uff0c\u5929\u77eb\u5982\u9f99\uff0c\u5de6\u8361\u5728\u51b3\uff0c\u5c55\u5f00\u6700\u8fc5\u75be\u7684\u8eab\u6cd5\uff0c\u4e1c\u523a\u4e00\u5251\uff0c\u897f\u5288\u4e00\u638c\uff0c\u4e13\u89e3\u6551\u5904\u5883\u5371 \u6025\u7684\u4eba\uff0c\u5c3d\u91cf\u907f\u5f00\u695a\u3001\u9f50\u4e8c\u4eba\u4e4b\u5408\u51fb\uff0c\u82e6\u4ed6\u4eec\u5206\u5934\u60f3\u4f24\u5bb3\u81ea\u5df1\u8fd9\u65b9\u7684\u4eba\u65f6\uff0c\u4ed6\u53c8\u795e\u51fa\u9b3c\u6ca1\u5730 \u7a81\u6765\u9a9a\u6270\uff0c\u97e9\u5fd7\u90a6\u672c\u5df2\u7ed9\u5201\u56db\u798f\u8feb\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u4f46\u7ed9\u51cc\u672a\u98ce\u5ffd\u7136\u63a9\u5230\uff0c\u4e00\u638c\u6253\u65ad\u5201\u56db\u798f\u5de6 \u81c2\uff0c\u97e9\u5fd7\u90a6\u4e5f\u767b\u65f6\u8131\u51fa\u8eab\u6765\uff0c\u548c\u51cc\u672a\u98ce\u4e00\u8d77\uff0c\u4e13\u4ee5\u602a\u5f02\u7684\u8eab\u6cd5\uff0c\u63f4\u52a9\u540c\u4f34\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u695a\u662d\u5357\u7684\u90e8\u7f72\u5168\u843d\u4e86\u7a7a\uff0c\u9677\u5165\u6df7\u6218\u4e4b\u5c40\u3002\u4f46\u8bba\u5b9e\u529b\u695a\u662d\u5357\u90a3\u8fb9\u6709\u4e09\u4e2a\u9876\u5c16\u513f \u7684\u4eba\u7269\uff0c\u66f4\u517c\u5927\u5185\u8fd1\u4e8c\u5341\u4e2a\u9ad8\u624b\uff08\u672c\u6765\u662f\u4e8c\u5341\u56db\u540d\u7684\uff0c\u5df2\u6b7b\u4f24\u4e86\u516d\u4e03\u4eba\uff09\uff0c\u6bd4\u8d77\u51cc\u672a\u98ce\u8fd9 \u8fb9\uff0c\u4ecd\u7136\u5f3a\u5f97\u591a\uff01\u5927\u6df7\u6218\u4e2d\uff0c\u867d\u7136\u6682\u65f6\u662f\u50f5\u6301\u4e4b\u5c40\uff0c\u4f46\u6253\u4e0b\u53bb\u5374\u7a33\u5360\u4e0a\u98ce\uff0c\u9644\u8fd1\u8857\u9053\u7684\u5fa1\u6797 \u519b\uff0c\u7ecf\u8fc7\u4e86\u8fd9\u4e48\u4e9b\u65f6\u95f4\uff0c\u4e5f\u7eb7\u7eb7\u8d76\u6765\uff0c\u5f62\u52bf\u7aef\u7684\u5341\u5206\u5371\u9669\u3002 \u3000\u3000\u6df7\u6218\u4e2d\u5f20\u534e\u662d\u4e2d\u4e86\u4e00\u5200\uff0c\u8840\u6d41\u5982\u6ce8\uff0c\u4ecd\u662f\u6325\u5251\u529b\u640f\uff0c\u52c7\u731b\u5f02\u5e38\uff0c\u51cc\u672a\u98ce\u63a0\u8fc7\u8eab\u8fb9\uff0c\u53cd\u624b \u4e00\u638c\uff0c\u628a\u4ed6\u9762\u524d\u7684\u4e00\u540d\u5927\u5185\u9ad8\u624b\u5288\u5f97\u8111\u6d46\u8ff8\u6d41\uff0c\u5c06\u4ed6\u62d6\u5165\u5185\u56f4\uff0c\u53ea\u542c\u4ed6\u5583\u5583\u53eb\u9053\uff1a\u201c\u5170\u73e0\uff0c \u5170\u73e0\uff0c\u6211\u8981\u89c1\u4f60\u3002\u201d\u51cc\u672a\u98ce\u77e5\u4ed6\u795e\u667a\u5df2\u6e10\u660f\u4e71\uff0c\u8d8a\u53d1\u5fc3\u7126\u3002\u53f3\u5251\u62d2\u654c\uff0c\u5de6\u624b\u6495\u4e0b\u8863\u8896\uff0c\u7ed9\u4ed6 \u5305\u624e\uff0c\u5728\u4ed6\u8033\u8fb9\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u51b2\u51fa\u4e4b\u540e\uff0c\u6211\u5e26\u4f60\u53bb\u627e\u5979\u3002\uff0c\u4f60\u8ddf\u5728\u6842\u4ef2\u660e\u8d24\u5f1f\u4e4b\u540e\uff0c\u53ea\u51c6\u62d2 \u654c\uff0c\u77e5\u9053\u5417\uff1f\u201d\u5f20\u534e\u662d\u70b9\u4e86\u70b9\u5934\uff0c\u51cc\u672a\u98ce\u5251\u8d70\u8fde\u73af\uff0c\u53c8\u66ff\u5192\u6d63\u83b2\u51fb\u9000\u4e86\u4e24\u540d\u56f4\u653b\u536b\u58eb\u3002 \u3000\u3000\u51cc\u672a\u51e4\u89c1\u654c\u52bf\u8d8a\u5927\uff0c\u5fc3\u5185\u6697\u9053\uff1a\u201c\u600e\u7684\u4ed6\u4eec\u8fd8\u672a\u89c1\u6765\uff1f\u201d\u6b63\u7126\u6025\u95f4\uff0c\u5ffd\u89c1\u524d\u9762\u6e05\u5175\u4f3c\u6ce2 \u6d6a\u822c\u4e24\u8fb9\u5206\u5f00\uff0c\u524d\u5934\u6740\u6765\u4e00\u5f6a\u4eba\u9a6c\uff0c\u4e2a\u4e2a\u9762\u4e0a\u753b\u5f97\u5947\u5f62\u602a\u72b6\uff0c\u5c31\u50cf\u620f\u53f0\u4e0a\u7684\u5927\u82b1\u8138\u4e00\u6837\u3002\u51cc \u672a\u98ce\u6697\u6697\u7b11\u9053\uff1a\u201c\u5f20\u9752\u539f\u8fd9\u5bb6\u4f19\u4e5f\u6709\u4e24\u624b\uff0c\u753b\u4e86\u82b1\u8138\u679c\u7136\u6bd4\u8499\u9762\u66f4\u4e0d\u6613\u8ba4\u51fa\u5e90\u5c71\u771f\u76f8\u3002\u201d\u4f46 \u4ecd\u4e0d\u514d\u5fe7\u8651\uff1a\u201c\u5f20\u9752\u539f\u80fd\u529b\u5e73\u5e73\uff0c\u4ed6\u5e26\u6765\u7684\u5f1f\u5144\uff0c\u7eb5\u80fd\u6321\u5f97\u4f4f\u5fa1\u6797\u519b\uff0c\u4e5f\u6740\u4e0d\u9000\u8fd9\u4e9b\u5927\u5185\u9ad8 \u624b\u3002\u201d\u6b63\u76d8\u7b97\u95f4\uff0c\u5ffd\u89c1\u4e00\u4e2a\u6e05\u766f\u8001\u8005\uff0c\u4e00\u9a6c\u5f53\u5148\uff0c\u5251\u523a\u638c\u5288\uff0c\u75be\u5982\u96f7\u9706\uff0c\u62e6\u963b\u7684\u5fa1\u6797\u519b\u7eb7\u7eb7 \u5012\u5730\uff0c\u51cc\u672a\u98ce\u5927\u559c\uff0c\u5bf9\u5192\u6d63\u83b2\u9053\uff1a\u201c\u600e\u4e48\u4ed6\u8001\u4eba\u5bb6\u4e5f\u51fa\u6765\u4e86\uff01\u201d \u3000\u3000\u9f50\u771f\u541b\u62a2\u53bb\u62e6\u963b\u90a3\u6e05\u766f\u8001\u8005\uff0c\u5251\u950b\u4e00\u6296\uff0c\u53ea\u89c1\u767d\u5149\u4e00\u95ea\uff0c\u76f4\u6307\u54bd\u5589\uff0c\u9f50\u771f\u541b\u53eb\u58f0\u201c\u597d \u5feb\u201d\uff0c\u53cc\u5251\u4e00\u526a\uff0c\u653b\u5b88\u4e24\u62db\u540c\u65f6\u53d1\u51fa\uff0c\u90a3\u8001\u8005\u4e00\u4e5f\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8eab\u5f62\u970d\u5730\u4e00\u8f6c\uff0c\u5251\u5149\u95ea \u5904\uff0c\u907f\u5f00\u9f50\u771f\u541b\u7684\u201c\u98ce\u96f7\u4ea4\u51fb\u201d\u8fa3\u62db\uff0c\u8fde\u80a9\u5e26\u80cc\u523a\u5c06\u8fc7\u53bb\uff0c\u9f50\u771f\u541b\u6c89\u8170\u7ffb\u8155\uff0c\u786c\u78d5\u654c\u4eba\u5b9d \u5251\uff0c\u54ea\u77e5\u8fd9\u8001\u8005\u5251\u6cd5\u5feb\u5f97\u60ca\u4eba\uff0c\u970e\u5ffd\u4e4b\u95f4\u5df2\u653b\u4e86\u4e94\u5251\uff0c\u9f50\u771f\u541b\u8981\u8fd0\u5251\u81ea\u4fdd\uff0c\u7adf\u51fb\u4e0d\u7740\u654c\u4eba\u7684 \u5251\uff0c\u695a\u662d\u5357\u89c1\u72b6\u5927\u60ca\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u821e\u6210\u4e00\u9053\u767d\u5149\uff0c\u5411\u8001\u8005\u98de\u63a0\u8fc7\u53bb\u3002\u90a3\u8001\u8005\u75be\u523a\u4e24\u62db\uff0c\u5ffd\u7136 \u62d4\u8eab\u4e00\u8038\uff0c\u63a0\u8d77\u4e09\u4e08\u591a\u9ad8\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u98de\u4e91\u63a3\u7535\uff0c\u5411\u695a\u662d\u5357\u8fce\u9762\u523a\u53bb\uff0c\u4e24\u5251\u5728\u534a\u7a7a\u76f8\u4ea4\uff0c\u4e24 \u4eba\u90fd\u7ed9\u9707\u5f97\u5411\u540e\u5012\u98de\uff01\u90a3\u8001\u8005\u5728\u534a\u7a7a\u4e2d\u8fde\u4eba\u5e26\u5251\u8f6c\u4e86\u4e2a\u5927\u5706\u5708\uff0c\u843d\u4e0b\u6765\u65f6\uff0c\u5c31\u5b9b\u5982\u5e26\u7740\u4e00\u9053 \u5149\u73af\u98de\u964d\uff0c\u62a2\u8fc7\u6765\u7684\u5fa1\u6797\u519b\uff0c\u6298\u81c2\u65ad\u8db3\uff0c\u90fd\u7ed9\u5251\u5149\u626b\u4f24\u3002\u695a\u662d\u5357\u7ffb\u8eab\u4e0b\u6765\u65f6\uff0c\u5374\u7ed9\u6842\u4ef2\u660e\u8d81 \u52bf\u4e00\u5251\u524a\u53bb\uff0c\u817e\u86df\u5b9d\u5251\u821e\u8d77\u4e08\u4f59\u5149\u8292\uff0c\u5a01\u52bf\u7aef\u7684\u60ca\u4eba\u3002\u695a\u662d\u5357\u8eab\u5b50\u60ac\u7a7a\uff0c\u65e0\u6cd5\u8eb2\u95ea\uff0c\u53ea\u5f97\u6697 \u8fd0\u5185\u529b\u4f38\u5251\u4e00\u70b9\uff0c\u5251\u5c16\u867d\u7ed9\u622a\u65ad\uff0c\u4ed6\u4e5f\u8d81\u7740\u8fd9\u4e00\u70b9\u4e4b\u529b\uff0c\u659c\u523a\u843d\u4e0b\uff0c\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u9f50\u771f\u541b\u53c8\u662f\u4e00\u60ca\uff0c\u7779\u9053\uff1a\u600e\u4e48\u4e24\u65e5\u4e4b\u95f4\uff0c\u7adf\u78b0\u7740\u8fd9\u4e48\u591a\u80fd\u4eba\uff1f\u8fd9\u6e05\u766f\u8001\u8005\uff0c\u5251\u6cd5\u8fc5\u6377\uff0c \u7adf\u4f3c\u4e0d\u5728\u51cc\u672a\u98ce\u4e4b\u4e0b\uff0c\u695a\u662d\u5357\u8fd9\u65f6\u5df2\u731c\u51fa\u8001\u8005\u662f\u8c01\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u77f3\u8001\u5934\u5b50\uff0c\u4f60\u8fd8\u8981\u4e0d\u8981\u5728 \u4eac\u5e08\u7684\u4ea7\u4e1a\uff0c\u4f60\u8fd8\u987e\u4e0d\u987e\u4f60\u7684\u95e8\u751f\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u8fd9\u6e05\u766f\u8001\u8005\u4e43\u662f\u201c\u8e51\u4e91\u5251\u201d\u77f3\u632f\u98de\uff0c\u5728\u51cc\u672a\u98ce\u51fa\u9053\u4e4b\u524d\uff0c\u548c\u201c\u6e38\u9f99\u5251\u201d\u695a\u662d\u5357\u3001\u201c\u5143\u6781 \u5251\u201d\u5085\u9752\u4e3b\u5e76\u79f0\u5f53\u4e16\u4e09\u5927\u5251\u672f\u540d\u5bb6\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u5251\u6cd5\u7cbe\u5999\uff0c\u5728\u4eac\u5e08\u662f\u4e00\u6d3e\u5b97\u5e08\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u5f62\u52bf\u53c8\u53d8\uff0c\u51cc\u672a\u98ce\u8d81\u7740\u9f50\u771f\u541b\u695a\u662d\u5357\u4e0e\u77f3\u632f\u98de\u6076\u6597\u4e4b\u9645\uff0c\u7115\u5730\u51b2\u51fa\uff0c\u5927\u5185\u9ad8\u624b\uff0c \u62e6\u622a\u4e0d\u4f4f\uff0c\u7adf\u7ed9\u4ed6\u51b2\u5f00\u4e00\u9053\u7f3a\u53e3\u3002\u6210\u5929\u633a\u94c1\u7b14\u659c\u98de\uff0c\u62fc\u547d\u51b2\u6765\uff0c\u901a\u660e\u548c\u5c1a\u4e00\u5200\u65a9\u53bb\uff0c\u201c\u5f53\u201d \u7684\u4e00\u58f0\uff0c\u5200\u950b\u5c3d\u5377\uff0c\u4f46\u6210\u5929\u633a\u7684\u4e00\u679d\u5224\u5b98\u7b14\uff0c\u4e5f\u7ed9\u65a9\u4e86\u4e00\u9053\u7f3a\u53e3\uff0c\u6014\u5f97\u4e00\u6014\uff0c\u62bc\u540e\u7684\u6842\u4ef2\u660e \u5df2\u63a0\u4e86\u8fc7\u6765\uff0c\u817e\u86df\u5b9d\u5251\u821e\u8d77\u4e08\u4f59\u5149\u8292\uff0c\u6210\u5929\u633a\u4e0d\u6562\u786c\u63a5\uff0c\u4fa7\u8eab\u4e00\u95ea\uff0c\u6842\u4ef2\u660e\u5b9d\u5251\u6a2a\u626b\uff0c\u53c8\u4f24 \u4e86\u4e24\u540d\u536b\u571f\uff0c\u524d\u5934\u7684\u51cc\u672a\u98ce\uff0c\u5df2\u8d76\u4e0a\u53bb\u548c\u77f3\u632f\u98de\u4f1a\u5408\u4e86\uff01 \u3000\u3000\u695a\u662d\u5357\u548c\u9f50\u771f\u541b\u8fd8\u5f85\u5835\u622a\uff0c\u90a3\u91cc\u5835\u622a\u5f97\u4f4f\uff1f\u51cc\u672a\u98ce\u628a\u695a\u662d\u5357\u6740\u9000\uff0c\u77f3\u632f\u98de\u4e5f\u6321\u4f4f\u9f50\u771f \u541b\uff0c\u6842\u4ef2\u660e\u548c\u5192\u6d63\u83b2\u4e24\u53e3\u5b9d\u5251\uff0c\u970d\u970d\u5c55\u5f00\uff0c\u4ece\u65c1\u63a9\u62a4\uff0c\u4e0d\u4e00\u523b\u4fbf\u6740\u51fa\u91cd\u56f4\uff0c\u548c\u5f20\u9752\u539f\u5e26\u6765\u7684 \u4eba\u6a2a\u51b2\u76f4\u95ef\uff0c\u628a\u5fa1\u6797\u519b\u6740\u5f97\u4f24\u4ea1\u6795\u85c9\u3002\u77f3\u632f\u98de\u53eb\u9053\uff1a\u201c\u5411\u4e1c\u76f4\u95e8\u51b2\u51fa\uff01\u201d\u51cc\u672a\u98ce\u5e94\u4e86\u4e00\u58f0\uff0c \u8ba9\u77f3\u632f\u98de\u9886\u5148\uff0c\u81ea\u5df1\u6539\u4e0e\u6842\u4ef2\u660e\u6bbf\u540e\uff0c\u6478\u51fa\u4e09\u679d\u5929\u5c71\u795e\u8292\uff0c\u731b\u559d\u4e00\u58f0\uff0c\u9f50\u771f\u541b\u5ffd\u89c1\u4e00\u9053\u4e4c\u91d1 \u5149\u8292\uff0c\u5288\u9762\u5c04\u6765\uff0c\u4e3e\u5251\u4e00\u64a9\uff0c\u53ea\u89c9\u81c2\u818a\u4e00\u9635\u9178\u9ebb\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u5251\u8eab\u7adf\u7ed9\u5c04\u7a7f\uff0c\u695a\u662d\u6d77\u5374\u662f\u673a \u7075\uff0c\u8fd0\u7edd\u9876\u8f7b\u529f\uff0c\u8fde\u907f\u4e24\u652f\u795e\u8292\uff0c\u53ea\u7d2f\u4e86\u8eab\u540e\u7684\u4e24\u540d\u536b\u58eb\uff0c\u505a\u4e86\u66ff\u6b7b\u9b3c\uff0c\u7ed9\u795e\u8292\u5bf9\u80f8\u7a7f\u8fc7\uff0c \u60e8\u6b7b\u5f53\u573a\uff01 \u3000\u3000\u695a\u662d\u5357\u548c\u9f50\u771f\u541b\u77e5\u4eca\u65e5\u5df2\u4e0d\u80fd\u53d6\u80dc\uff0c\u53ea\u597d\u805a\u96c6\u6e05\u519b\uff0c\u8854\u5c3e\u75be\u8ffd\uff0c\u4e0d\u6562\u5355\u8eab\u5339\u9a6c\u95ef\u53bb\u548c\u7fa4 \u96c4\u6df7\u6218\u4e86\u3002 \u3000\u3000\u4e24\u65b9\u4eba\u9a6c\u5728\u4eac\u57ce\u7684\u5927\u8857\u8ffd\u9010\uff0c\u5413\u5f97\u6237\u6237\u5173\u95e8\uff0c\u4eba\u4eba\u8eb2\u907f\uff0c\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u5df2\u95ef\u81f3\u4e1c\u76f4 \u95e8\uff0c\u53ea\u89c1\u57ce\u95e8\u5927\u5f00\uff0c\u6709\u4e8c\u4e09\u5341\u4e2a\u82b1\u9762\u7684\u5927\u6c49\uff0c\u6b63\u4e0e\u4e00\u961f\u6e05\u5175\u53ae\u6740\uff0c\u77f3\u632f\u98de\u7b49\u4e00\u62e5\u800c\u4e0a\uff0c\u628a\u90a3 \u961f\u6e05\u5175\u5168\u6570\u6d88\u706d\uff0c\u4e00\u5927\u7fa4\u4eba\uff0c\u98de\u901f\u51fa\u57ce\uff0c\u77f3\u632f\u98de\u5bf9\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u4eec\u628a\u57ce\u95e8\u5173\u4e0a\u3002\u201d\u4e24\u4eba\u594b \u8d77\u795e\u529b\uff0c\u628a\u5927\u94c1\u95e8\u5173\u95ed\uff0c\u4ece\u5916\u9762\u628a\u4e00\u6761\u94c1\u6805\u95f8\u4e0a\uff0c\u5fa1\u676f\u519b\u8d76\u5230\uff0c\u5168\u7ed9\u5173\u5728\u57ce\u5185\u3002\u51cc\u672a\u98ce\u5927\u4e3a \u5947\u602a\u3002 \u3000\u3000\u77f3\u632f\u98de\u9053\uff1a\u201c\u8fd9\u662f\u98de\u7ea2\u5dfe\u7684\u624b\u811a\uff01\u201d\u51cc\u672a\u98ce\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff1f\u8001\u524d\u8f88\u89c1\u7740\u5979\u4e86\uff1f\u201d \u77f3\u632f\u98de\u9053\uff1a\u201c\u6211\u4eec\u57cb\u4f0f\u7684\u4eba\uff0c\u4ece\u5929\u7262\u9644\u8fd1\u6740\u51fa\uff0c\u6b63\u628a\u9644\u8fd1\u7684\u5fa1\u6797\u519b\u51b2\u6563\uff0c\u4fbf\u89c1\u98de\u7ea2\u5dfe\u5e26\u7740\u4e00 \u4eba\uff0c\u5de6\u97ad\u53f3\u5251\uff0c\u5728\u6c11\u623f\u4e0a\u5982\u98de\u63a0\u8fc7\uff0c\u6211\u8d76\u4e0a\u53bb\u95ee\u5979\uff0c\u5979\u53ea\u7b11\u7740\u8bf4\uff1a\u2018\u4f60\u82e5\u6551\u51fa\u51cc\u672a\u98ce\u4ed6\u4eec\uff0c \u53eb\u4ed6\u4eec\u81ea\u4e1c\u76f4\u95e8\u51b2\u51fa\u4fbf\u884c\u4e86\uff0c\u6211\u5e26\u6765\u7684\u4eba\uff0c\u5df2\u5728\u57ce\u95e8\u5916\u5b89\u4e0a\u94c1\u6805\uff0c\u628a\u57ce\u95e8\u5173\u4e0a\uff0c\u603b\u53ef\u4ee5\u628a\u8ffd \u5175\u963b\u6321\u5f97\u4e00\u65f6\u534a\u523b\uff01\u2019\u8bf4\u7f62\u5979\u5c31\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u98de\u9a70\u800c\u53bb\uff0c\u8fdc\u8fdc\u4f3c\u6709\u4eba\u5f71\u9690\u73b0\uff0c\u4e0d\u77e5\u662f\u5426\u63a5 \u5e94\u5979\u7684\u3002\u201d\u51cc\u672a\u98ce\u542c\u4e86\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u98de\u7ea2\u5dfe\u65e9\u6709\u5e03\u7f6e\uff0c\u90a3\u4e9b\u63a5\u5e94\u5979\u7684\uff0c\u60f3\u5c31\u662f\u5979\u5e26\u6765\u7684\u54c8 \u8428\u514b\u4eba\u3002\u201d \u3000\u3000\u5f20\u9752\u539f\u9053\uff1a\u201c\u6211\u6309\u6628\u65e5\u6240\u5b9a\u7684\u8ba1\u5212\uff0c\u6697\u4e2d\u805a\u96c6\u4e86\u5929\u5730\u4f1a\u53ca\u9c81\u738b\u65e7\u90e8\u5728\u4eac\u5e08\u7684\u670b\u53cb\uff0c\u57cb\u4f0f \u51c6\u5907\u3002\u6211\u4eec\u672c\u6765\u529d\u77f3\u8001\u524d\u8f88\u4e0d\u8981\u51fa\u9762\u7684\uff0c\u4ed6\u4e49\u8584\u4e91\u5929\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u90fd\u8981\u52a9\u6211\u4eec\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u77f3\u632f\u98de\u62c8\u987b\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u6211\u4e0d\u51fa\u9762\u5c31\u80fd\u4fdd\u5168\u4e86\u5417\uff1f\u524d\u4e24\u5929\uff0c\u5fa1\u6797\u519b\u4e2d\u5df2\u6709\u4eba\u901a\u77e5\u6211\uff0c\u8bf4 \u662f\u4ed6\u4eec\u7684\u4eba\u5df2\u5bf9\u6211\u6ce8\u610f\u4e86\uff0c\u53ea\u662f\u8fd8\u62ff\u4e0d\u5b9a\u6211\u662f\u5426\u6536\u85cf\u53db\u9006\uff0c\u53c8\u6015\u6253\u8349\u60ca\u86c7\uff0c\u6240\u4ee5\u6682\u65f6\u4e0d\u6562\u6765 \u52a8\u6211\u3002\u6211\u5c31\u662f\u4e0d\u51fa\u9762\uff0c\u4f60\u4eec\u95f9\u51fa\u8fd9\u4ef6\u5927\u4e8b\u4e4b\u540e\uff0c\u4ed6\u4eec\u4e5f\u4e00\u5b9a\u4f1a\u8e29\u67e5\u5230\u6211\u8fd9\u91cc\u6765\u7684\uff0c\u6211\u5012\u4e0d\u5982 \u5148\u8c41\u51fa\u53bb\uff0c\u7ed9\u4ed6\u4eec\u70b9\u5389\u5bb3\u77a7\u77a7\uff01\u201d\u51cc\u672a\u98ce\u95ee\u9053\uff1a\u201c\u77f3\u8001\u524d\u8f88\u4eca\u540e\u6253\u7b97\u5982\u4f55\uff1f\u4e0d\u5982\u968f\u6211\u4eec\u4e00\u8def \u5230\u56db\u5ddd\u53bb\u5427\u3002\u201d\u77f3\u632f\u98de\u9053\uff1a\u201c\u6211\u7684\u95e8\u751f\u5f1f\u5b50\u5f88\u591a\uff0c\u6211\u4e0d\u80fd\u8d70\u5f97\u8fd9\u6837\u8fdc\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4ed6\u4eec \u7559\u5728\u4eac\u4e2d\u4f1a\u788d\u4e8b\u5417\uff1f\u201d\u77f3\u632f\u98de\u83ab\u9053\uff1a\u201c\u5b98\u5e9c\u4e2d\u4eba\u53ea\u662f\u6015\u6211\u786c\u51fa\u5934\uff0c\u6211\u8d70\u4e86\uff0c\u8c05\u4ed6\u4eec\u8fd8\u4e0d\u6562\u5927 \u5174\u98ce\u6d6a\uff0c\u5fa1\u6797\u519b\u7981\u536b\u519b\u4e2d\u4e5f\u6709\u4e0d\u5c11\u662f\u6211\u7684\u6302\u540d\u5f92\u5b50\u5f92\u5b59\u5462\uff0c\u4ed6\u4eec\u54ea\u91cc\u6349\u5f97\u8fd9\u6837\u591a\uff1f\u6211\u6253\u7b97\u5c31 \u5230\u6c5f\u5357\u4e00\u5e26\u6e9c\u6e9c\uff0c\u627e\u6211\u7684\u5b5f\u575a\u8001\u5f1f\u53bb\uff0c\u548c\u4ed6\u53bb\u6349\u4eba\u5996\u90dd\u98de\u51e4\u3002\u201d\u5f20\u9752\u539f\u9053\uff1a\u201c\u8fd9\u51e0\u767e\u5929\u5730\u4f1a \u7684\u5f1f\u5144\u548c\u9c81\u738b\u7684\u65e7\u90e8\u4e5f\u4e0d\u80fd\u968f\u6211\u8d70\uff0c\u5c31\u62dc\u6258\u77f3\u8001\u524d\u8f88\u7167\u987e\u5427\u3002\u201d\u77f3\u632f\u98de\u4e50\u9053\uff1a\u201c\u7740\u5440\uff01\u6211\u82e5 \u7ed9\u5b98\u5e9c\u8feb\u5f97\u6ca1\u6cd5\u65f6\uff0c\u5c31\u5e26\u90a3\u4e9b\u5144\u5f1f\u5360\u5c71\u4e3a\u738b\uff0c\u4ece\u9556\u5934\u6539\u4e3a\u5be8\u4e3b\uff0c\u54c8\u54c8\uff01\u201d\u8bf4\u7f62\uff0c\u53c8\u5bf9\u5192\u6d63\u83b2 \u9053\uff1a\u201c\u5192\u59d1\u5a18\uff0c\u8bb0\u5f97\u7ed9\u6211\u95ee\u5019\u4f60\u7684\u5085\u4f2f\u4f2f\uff01\u201d\u51cc\u672a\u98ce\u548c\u5192\u6d63\u83b2\u53cc\u53cc\u62dc\u8c22\uff0c\u77f3\u632f\u98de\u6478\u51fa\u4e00\u5927\u5305 \u4e1c\u897f\u9012\u7ed9\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd9\u91cc\u9762\u662f\u4e00\u767e\u4e8c\u5341\u4e07\u4e24\u94f6\u5b50\u7684\u94b1\u7968\uff0c\u90fd\u662f\u5317\u4e94\u7701\u5404\u5927\u94b1\u5e84\u53d1\u51fa\u7684\uff0c\u5230 \u5904\u901a\u7528\u3002\u6211\u8981\u9003\u4ea1\u5566\uff0c\u6211\u7684\u5f92\u5f1f\u4eec\u4e00\u591c\u4e4b\u95f4\u5c31\u66ff\u6211\u5c06\u5bb6\u4ea7\u5168\u53d8\u5356\u4e86\u3002\u4ed6\u4eec\u8bf4\u4f60\u8001\u5e26\u7740\u603b\u6bd4\u7ed9 \u5b98\u5e9c\u6284\u53bb\u7684\u597d\u3002\u73b0\u5728\u6211\u4e5f\u8981\u8bf4\uff0c\u4f60\u5e26\u53bb\u7ed9\u674e\u5c06\u519b\u505a\u519b\u664c\u603b\u6bd4\u6211\u8fd9\u5149\u68cd\u8001\u5934\u513f\u5e26\u7740\u7684\u597d\uff01\u201d\u51cc \u672a\u98ce\u89c1\u4ed6\u8bf4\u5f97\u8fd9\u6837\u723d\u5feb\uff0c\u4e5f\u4e0d\u63a8\u8f9e\u3002\u77f3\u632f\u98de\u5e26\u9886\u7740\u51e0\u767e\u4eba\uff0c\u4e00\u7b11\u800c\u53bb\u3002\u7fa4\u96c4\u5bf9\u4ed6\u7684\u5982\u4e91\u9ad8 \u4e49\uff0c\u65e0\u4e0d\u8d5e\u53f9\uff01 \u3000\u3000\u5f20\u9752\u539f\u7b49\u77f3\u632f\u98de\u53bb\u540e\uff0c\u6084\u58f0\u5730\u5bf9\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u80fd\u56de\u56db\u5ddd\u4e86\uff01\u201d\u51cc\u672a\u98ce\u60ca\u9053\uff1a\u201c\u4ec0 \u4e48\uff1f\u201d\u5f20\u9752\u539f\u9053\uff1a\u201c\u803f\u7cbe\u5fe0\u3001\u5c1a\u4e4b\u4fe1\u5168\u53cd\u4e86\uff0c\u5434\u4e09\u6302\u5728\u897f\u5317\u7684\u5927\u5c06\u738b\u8f85\u81e3\u4e5f\u5728\u7518\u8083\u53cd\u4e86\uff0c\u73b0 \u5728\u5434\u4e09\u6842\u5b64\u519b\u5728\u6e56\u5357\uff0c\u867d\u7136\u5728\u8861\u5dde\u5f00\u5e9c\uff0c\u8981\u505a\u7687\u5e1d\uff0c\u90a3\u5df2\u662f\u91dc\u5e95\u6e38\u9b42\uff0c\u53bb\u65e5\u65e0\u591a\u4e86\uff01\u5434\u4e09\u6842 \u6b7b\u4e0d\u8db3\u60dc\uff0c\u53ef\u662f\u90a3\u6279\u5bb6\u4f19\u4e00\u53cd\uff0c\u53ef\u7d2f\u4e86\u6211\u4eec\u5566\uff0c\u5c24\u5176\u662f\u738b\u8f85\u81e3\u4e00\u53cd\uff0c\u6e05\u5ef7\u5728\u897f\u5317\u7684\u5927\u519b\u5168\u5165 \u56db\u5ddd\uff0c\u674e\u5c06\u519b\u6d3e\u4eba\u4f20\u9012\u4e86\u6d88\u606f\u6765\u8bf4\uff0c\u53eb\u6211\u4eec\u4e0d\u8981\u56de\u5ddd\uff0c\u4ed6\u8bf4\u4ed6\u4e5f\u8981\u5c06\u90e8\u4e0b\u5206\u6563\uff0c\u5316\u6574\u4e3a\u96f6\uff0c \u5fc5\u8981\u65f6\u8fd8\u51c6\u5907\u53d6\u9053\u7518\u8083\uff0c\u5077\u5165\u56de\u7586\u5462\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u9ed8\u7136\u4e0d\u8bed\uff0c\u826f\u4e45\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4eec\u5c31\u5230\u56de\u7586\u53bb\u4e5f\u597d\uff01\u201d\u97e9\u5fd7\u90a6\u9053\uff1a\u201c\u6211\u4eec\u73b0\u6709\u5927 \u5587\u561b\u7684\u5173\u6587\uff0c\u4ee5\u6211\u4eec\u7684\u811a\u7a0b\uff0c\u5b98\u5e9c\u7684\u6355\u5934\u4e5f\u8d76\u4e0d\u4e0a\uff0c\u4e0d\u5982\u5148\u5230\u897f\u85cf\u53bb\u5427\u3002\u201d\u5b97\u8fbe\xb7\u5b8c\u771f\u7b49\u5587 \u561b\u4e5f\u4e00\u9f50\u9080\u8bf7\uff0c\u51cc\u672a\u98ce\u6168\u7136\u9053\uff1a\u201c\u597d\uff01\u5929\u5357\u5730\u5317\uff0c\u5904\u5904\u4e3a\u5bb6\uff0c\u56de\u7586\u897f\u85cf\u90fd\u662f\u4e00\u6837\u3002\u201d\u5192\u6d63\u83b2 \u56de\u671b\u4eac\u57ce\uff0c\u60f3\u8d77\u7eb3\u5170\u5bb9\uff0c\u9887\u89c9\u4eac\u534e\u4e91\u70df\uff0c\u6709\u5982\u4e00\u68a6\u3002 \u3000\u3000\u5f20\u534e\u662d\u8fd9\u65f6\u795e\u667a\u5df2\u6e05\uff0c\u95ee\u51cc\u672a\u98ce\u9053\uff1a\u201c\u90a3\u4f4d\u98de\u7ea2\u5dfe\u662f\u4ec0\u4e48\u4eba\uff0c\u5979\u4e3a\u4ec0\u751f\u8981\u5c06\u6613\u5170\u73e0\u5e26 \u53bb\uff1f\u201d\u51cc\u672a\u98ce\u60e8\u7136\u7b11\u9053\uff1a\u201c\u603b\u662f\u60c5\u5b7d\uff0c\u4f60\u4e0d\u5fc5\u95ee\u4e86\uff0c\u6211\u5e26\u4f60\u627e\u5230\u5979\u4fbf\u662f\u3002\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u4e0d\u60dc\u6295\u8352\u5343\u4e07\u91cc\uff0c\u5eff\u5e74\u60c5\u5b7d\u89e3\u96be\u5f00\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u7ae0\u3000\u6709\u610f\u62a4\u4ed9\u82b1\u3000\u67af\u6d1e\u7a9f\u4e2d\u85cf\u5f02\u58eb\u3000\u65e0\u5fc3\u9632\u9a97\u5b50\u3000\u5587\u561b\u5bfa\u5185\u5931\u5bc4\u4e66 \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u4e8c\u5341\u56de \u6709\u610f\u62a4\u4ed9\u82b1\u3000\u67af\u6d1e\u7a9f\u4e2d\u85cf\u5f02\u58eb \u65e0\u5fc3\u9632\u9a97\u5b50\u3000\u5587\u561b\u5bfa\u5185\u5931\u5bc4\u4e66 \u3000\u3000\u4e94\u4e2a\u6708\u4e4b\u540e\uff0c\u5317\u5929\u5c71\u811a\u4e0b\uff0c\u6709\u56db\u4e2a\u9752\u5e74\u7537\u5973\uff0c\u6ee1\u9762\u98ce\u5c18\uff0c\u51dd\u671b\u7740\u5929\u5c71\u4e0a\u7a7a\u7684\u4e91\u6d77\u3002\u8fd9\u56db \u4e2a\u9752\u5e74\u7537\u5973\u5c31\u662f\u51cc\u672a\u98ce\u3001\u5f20\u534e\u662d\u3001\u5192\u70f7\u83b2\u548c\u6842\u4ef2\u660e\u3002 \u3000\u3000\u4ed6\u4eec\u968f\u671d\u5fd7\u90a6\u5230\u4e86\u897f\u85cf\u62c9\u8428\u4e4b\u540e\uff0c\u4f4f\u4e86\u534a\u4e2a\u591a\u6708\uff0c\u8fbe\u8d56\u6d3b\u4f5b\u7684\u4f7f\u8282\u7ea2\u8863\u5587\u561b\u4e5f\u56de\u6765\u4e86\u3002 \u4ed6\u8bf4\u8d77\u4eac\u5e08\u4e2d\u88ab\u51cc\u672a\u98ce\u5927\u95f9\u6570\u573a\u4e4b\u540e\uff0c\u6ee1\u671d\u6587\u6b66\u90fd\u53d1\u4e86\u614c\uff0c\u7687\u5e1d\u5bf9\u51cc\u672a\u98ce\u7b49\u5047\u501f\u6d3b\u4f5b\u8f66\u4ed7\uff0c \u6551\u51fa\u6613\u5170\u73e0\u4e4b\u4e8b\uff0c\u5927\u4e3a\u9707\u6012\uff0c\u5e78\u800c\u7687\u5e1d\u4e5f\u89c1\u8fc7\u8fd9\u4e9b\u4fa0\u5ba2\u7684\u672c\u9886\uff0c\u6df1\u4fe1\u8f66\u4ed7\u5173\u6587\u662f\u7ed9\u76d7\u53bb\u7684\uff0c \u8fd9\u624d\u4e0d\u8d23\u602a\u4e8e\u4ed6\uff0c\u53ea\u662f\u7687\u5e1d\u5374\u5bf9\u4ed6\u8bf4\uff0c\u6050\u6015\u90a3\u4e9b\u201d\u53db\u8d3c\u201d\u5165\u85cf\uff0c\u8981\u6d3e\u5175\u6765\u66ff\u4ed6\u4eec\u641c\u6355\u3002\u7ea2\u8863 \u5587\u561b\u53ea\u597d\u63a8\u8bf4\uff0c\u8981\u95ee\u8fc7\u6d3b\u4f5b\u7684\u4e3b\u610f\uff0c\u624d\u80fd\u7b54\u590d\u3002\u90a3\u65f6\u897f\u85cf\u867d\u5c5e\u4e2d\u56fd\u7248\u56fe\uff0c\u5374\u5f62\u540c\u72ec\u7acb\uff0c\u653f\u6559 \u90fd\u5728\u8fbe\u8d56\u73ed\u7985\u4e24\u4e2a\u6d3b\u4f5b\u7684\u624b\u4e2d\uff0c\u6ee1\u6e05\u7687\u5e1d\u672a\u5f97\u540c\u610f\u4e4b\u524d\uff0c\u4e5f\u4e0d\u6562\u8d38\u7136\u51fa\u5175\uff0c\u8fdc\u5230\u7a77\u8fb9\uff0c\u8fd9\u4e8b \u5c31\u6682\u65f6\u6321\u8fc7\u53bb\u4e86\u3002\u7ea2\u8863\u5587\u561b\u53e6\u5916\u8fd8\u5e26\u6765\u4e86\u4e24\u4e2a\u6d88\u606f\uff0c\u4e00\u4e2a\u662f\u5434\u4e09\u6842\u65e5\u66ae\u9014\u7a77\uff0c\u5df2\u5728\u8861\u9633\u5f00\u5e9c \u79f0\u5e1d\uff0c\u6ee1\u6e05\u5927\u519b\u56e0\u6b64\u52a0\u7d27\u8fdb\u88ad\uff0c\u4ed6\u79bb\u4eac\u65f6\uff0c\u542c\u8bf4\u5927\u519b\u5df2\u8fdb\u6e56\u5357\uff0c\u770b\u6765\u5f88\u5feb\u5c31\u4f1a\u5e73\u5b9a\u3002\u5434\u4e09\u6302 \u4e4b\u4e0d\u80fd\u6210\u4e8b\uff0c\u65e9\u5728\u6ee1\u6c49\u5927\u81e3\u7684\u610f\u6599\u4e4b\u4e2d\uff0c\u6240\u4ee5\u6e05\u5175\u5927\u6377\uff0c\u5e76\u4e0d\u600e\u6837\u5f15\u8d77\u6ce8\u610f\uff1b\u53ef\u662f\u968f\u7740\u5434\u4e09 \u6842\u7684\u632b\u8d25\uff0c\u6ee1\u6e05\u5728\u56db\u5ddd\u5374\u6709\u4e86\u610f\u5916\u7684\u6536\u83b7\uff0c\u6e05\u519b\u914d\u5408\u4e86\u5434\u4e09\u6842\u7684\u53db\u519b\uff0c\u7adf\u628a\u5ddd\u6ec7\u8fb9\u533a\u674e\u6765\u4ea8 \u7684\u90e8\u961f\u51fb\u7834\u4e86\uff0c\u542c\u8bf4\u674e\u6765\u4ea8\u56e0\u9677\u5165\u91cd\u56f4\uff0c\u4e0d\u80af\u6295\u964d\uff0c\u81ea\u7f22\u800c\u6b7b\u3002\u4ed6\u7684\u5f1f\u5f1f\u674e\u601d\u6c38\u5374\u4e0d\u77e5\u4e0b \u843d\u3002\u53e6\u4e00\u4e2a\u6d88\u606f\u662f\uff1a\u542c\u8bf4\u7687\u4e0a\u5728\u5404\u7701\u9009\u62d4\u6b66\u571f\uff0c\u5e76\u6574\u987f\u5927\u519b\uff0c\u6709\u653b\u7565\u56de\u7586\u897f\u85cf\u4e4b\u610f\u3002 \u3000\u3000\u51cc\u672a\u98ce\u542c\u4e86\u7ea2\u8863\u5587\u561b\u5e26\u6765\u7684\u6d88\u606f\u540e\uff0c\u5fc3\u4e2d\u5f88\u662f\u4e0d\u5b89\uff0c\u4ed6\u65e2\u60cb\u60dc\u674e\u6765\u4ea8\u7ecf\u8425\u4e86\u8fd9\u4e48\u591a\u5e74\u7684 \u57fa\u4e1a\uff0c\u88ab\u6bc1\u4e8e\u4e00\u65e6\uff1b\u53c8\u60ac\u5ff5\u7740\u5218\u90c1\u82b3\uff0c\u5c3d\u7ba1\u4ed6\u5bf9\u5218\u90c1\u82b3\u4e0d\u80af\u63ed\u51fa\u672c\u6765\u9762\u76ee\uff0c\u53ef\u662f\u5728\u4ed6\u5fc3\u7075\u6700 \u9690\u79d8\u7684\u5730\u65b9\uff0c\u8fd8\u662f\u6df1\u85cf\u7740\u5218\u90c1\u82b3\u7684\u5f71\u5b50\uff0c\u5730\u8001\u5929\u8352\uff0c\u600e\u6837\u90fd\u5fd8\u6000\u4e0d\u4e86\u7684\u3002 \u3000\u3000\u5f20\u534e\u662d\u5bf9\u6613\u5170\u73e0\u7684\u601d\u5ff5\u4e5f\u4e0d\u4e9a\u4e8e\u51cc\u672a\u98ce\uff0c\u800c\u4e14\u56e0\u4e3a\u4ed6\u5e74\u8f7b\uff0c\u8fd9\u4efd\u70ed\u60c5\u5c31\u66f4\u50cf\u706b\u7130\u4e00\u6837\uff0c \u71c3\u70e7\u8d77\u6765\uff0c\u663e\u793a\u51fa\u6765\u3002\u6bd4\u51cc\u672a\u98ce\u90a3\u79cd\u6df1\u85cf\u7684\u611f\u60c5\uff0c\u66f4\u4ee4\u4eba\u5bb9\u6613\u89e6\u89c9\uff0c\u4ee4\u4eba\u66ff\u4ed6\u96be\u8fc7\u3002 \u3000\u3000\u51cc\u672a\u98ce\u773c\u89c1\u7740\u5f20\u534e\u662d\u4e00\u5929\u5929\u6194\u60b4\u4e0b\u53bb\uff0c\u60f3\u8d77\u5bf9\u4ed6\u7684\u8bfa\u8a00\uff0c\u52a0\u4e0a\u4ed6\u5bf9\u6613\u5170\u73e0\u7684\u90a3\u4efd\u5982\u540c\u7236 \u5973\u7684\u611f\u60c5\uff0c\u4e5f\u50ac\u4ed6\u8d76\u5feb\u5bfb\u627e\u3002\u4e8e\u662f\u4ed6\u5411\u7ea2\u8863\u5587\u561b\u544a\u8f9e\uff0c\u8981\u5e26\u5f20\u534e\u662d\u5230\u56de\u7586\u53bb\uff0c\u7ea2\u8863\u5587\u561b\u77e5\u9053 \u4ed6\u5728\u56de\u7586\uff0c\u662f\u81ea\u6768\u4e91\u9aa2\u6b7b\u540e\uff0c\u6700\u5f97\u7267\u6c11\u7231\u6234\u7684\u4eba\u7269\uff0c\u5c24\u5176\u548c\u54c8\u8428\u514b\u4eba\u6709\u6781\u6df1\u7684\u5173\u7cfb\uff0c\u56e0\u6b64\u4e5f \u5c31\u987a\u4fbf\u6258\u4ed6\u4ee3\u4e3a\u8054\u7edc\uff0c\u51c6\u5907\u6e05\u519b\u4e07\u4e00\u6765\u653b\u65f6\uff0c\u6709\u6240\u5e94\u4ed8\u3002 \u3000\u3000\u6842\u4ef2\u660e\u8fd9\u4e24\u5e74\u6765\uff0c\u5bf9\u51cc\u672a\u98ce\u5982\u540c\u5bf9\u5927\u54e5\u4e00\u6837\uff0c\u53ef\u4ee5\u8bf4\u51cc\u672a\u98ce\u662f\u9664\u4e86\u5192\u6d63\u83b2\u4e4b\u5916\uff0c\u4ed6\u6700\u60c5 \u670d\u7684\u4eba\uff0c\u51cc\u672a\u98ce\u53bb\u56de\u7586\uff0c\u4ed6\u4e5f\u4e00\u5b9a\u8981\u540c\u53bb\u3002\u51cc\u672a\u98ce\u60f3\u5e26\u4ed6\u4eec\u53bb\u5386\u7ec3\u4e00\u4e0b\u4e5f\u597d\uff0c\u4e8e\u662f\u4e00\u884c\u56db \u4eba\uff0c\u7a7f\u8fc7\u5927\u6208\u58c1\uff0c\u8d8a\u8fc7\u5927\u8349\u539f\uff0c\u7ecf\u8fc7\u4e00\u4e2a\u591a\u6708\u7684\u8270\u9669\u65c5\u7a0b\uff0c\u7ec8\u4e8e\u6765\u5230\u4e86\u5929\u5c71\u811a\u4e0b\u3002 \u3000\u3000\u96c4\u4f1f\u58ee\u4e3d\u7684\u5929\u5c71\u7389\u7acb\u7740\uff0c\u7edd\u4e16\u7684\u82f1\u96c4\u5728\u5b83\u7684\u524d\u9762\uff0c\u4e5f\u4f1a\u89c9\u5f97\u81ea\u5df1\u7684\u6e3a\u5c0f\u3002\u51cc\u672a\u98ce\u7b49\u7ad9\u5728 \u5c71\u811a\uff0c\u53ea\u89c1\u84dd\u8499\u8499\u7684\u4e91\u5f25\u6f2b\u5929\u9645\uff0c\u96ea\u5c71\u51b0\u5cf0\u77d7\u7acb\u5728\u6df1\u84dd\u8272\u7684\u7a7a\u4e2d\uff0c\u50cf\u6c34\u6676\u4e00\u6837\uff0c\u95ea\u95ea\u53d1\u5149\uff0c \u8fd9\u65f6\u671d\u9633\u521d\u51fa\uff0c\u79ef\u96ea\u7684\u9ad8\u5cf0\u53d7\u5230\u4e86\u9633\u5149\u7684\u7167\u5c04\uff0c\u5148\u662f\u7d2b\u8272\u7684\uff0c\u6162\u6162\u5730\u53d8\u6210\u7ea2\u8272\uff0c\u6620\u5f97\u5ce1\u8c37\u91cc \u4e94\u5149\u5341\u8272\uff0c\u58ee\u4e3d\u6591\u6593\uff0c\u4efb\u662f\u6700\u5947\u5999\u7684\u753b\u5de5\uff0c\u4e5f\u753b\u4e0d\u51fa\u8fd9\u5e45\u201c\u5929\u5c71\u65e5\u51fa\u201d\u7684\u666f\u8272\u3002\u6842\u4ef2\u660e\u770b\u5f97 \u76ee\u593a\u795e\u9a70\uff0c\u8fde\u8fde\u8d5e\u53f9\u9053\uff1a\u201c\u6211\u53ea\u9053\u5251\u9601\u7edd\u9876\uff0c\u5df2\u7ecf\u662f\u4e16\u4e0a\u6700\u9669\u8981\u7684\u5730\u65b9\u4e86\uff0c\u5982\u4eca\u770b\u5230\u5929\u5c71\uff0c \u9ad8\u51fa\u4e91\u8868\uff0c\u4e07\u5cf0\u9519\u6742\uff0c\u8fd9\u624d\u771f\u662f\u96c4\u5947\u9669\u8981\u5462\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u7684\u5e08\u7236\u5c31\u4f4f\u5728\u5317\u5929\u5c71\u7684\u6700\u9ad8\u5cf0\u4e0a\u3002\u98de\u7ea2\u5dfe\u7684\u5e08\u7236\u4f4f\u5728\u5357\u9ad8\u5cf0\u4e0a\uff0c\u4e24\u5cf0\u76f8 \u8ddd\u5927\u7ea6\u6709\u4e03\u516b\u767e\u91cc\u3002\u6211\u60f3\u5148\u8c12\u89c1\u6211\u7684\u5e08\u7236\u3002\u201d\u6842\u4ef2\u660e\u7b49\u4e45\u4ef0\u6666\u660e\u7985\u5e08\u7684\u5927\u540d\uff0c\u81ea\u7136\u662f\u6b23\u559c\u83ab \u540d\u3002\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u4ee5\u6211\u4eec\u7684\u811a\u7a0b\uff0c\u8981\u6500\u767b\u81f3\u5929\u5c71\u4e4b\u5dc5\uff0c\u5927\u7ea6\u8981\u4e09\u65e5\u65f6\u5149\u3002\u6d63\u83b2\u59d1\u5a18\uff0c\u4f60\u8fd8 \u8981\u591a\u7740\u4e00\u4ef6\u76ae\u8884\u3002\u201d\u5f20\u534e\u662d\u5947\u9053\uff1a\u201c\u90a3\u65f6\u4f60\u62b1\u7740\u6613\u5170\u73e0\u4e0a\u5929\u5c71\uff0c\u5979\u8fd8\u53ea\u662f\u4e24\u4e09\u5c81\u7684\u5e74\u7eaa\uff0c\u5982 \u4f55\u8010\u5f97\u5bd2\u51b7\uff1f\u201d\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u5929\u5c71\u4e4b\u9e93\uff0c\u6709\u4e00\u79cd\u9ed1\u6cc9\u6c34\uff08\u6309\uff1a\u5373\u77f3\u6cb9\u539f\u6cb9\uff0c\u4ee5\u524d\u7684\u4eba\u4e0d \u77e5\uff0c\u79f0\u4e3a\u9ed1\u6cc9\u6c34\uff09\uff0c\u53ef\u4ee5\u70b9\u706b\uff0c\u6211\u5230\u5929\u5c71\u4e4b\u65f6\uff0c\u65f6\u5f53\u76db\u590f\uff0c\u6211\u7528\u5927\u76ae\u8884\u5305\u7740\u5979\uff0c\u6bcf\u665a\u5c31\u70b9\u9ed1 \u6cc9\u6c34\u7ed9\u5979\u53d6\u6696\u3002\u540e\u6765\u6666\u660e\u7985\u5e08\u53d1\u73b0\u4e86\uff0c\u5c06\u6211\u4eec\u63a5\u5f15\u4e0a\u53bb\u3002\u201d\u51cc\u672a\u98ce\u53c8\u8bb2\u4e86\u4e00\u4e9b\u548c\u6613\u5170\u73e0\u4e0a\u5929 \u5c71\u7684\u60c5\u5f62\u548c\u5b66\u5251\u7684\u7ecf\u8fc7\uff0c\u5927\u5bb6\u90fd\u542c\u5f97\u6d25\u6d25\u6709\u5473\u3002 \u3000\u3000\u767b\u5c71\u7684\u7b2c\u4e00\u65e5\u5927\u5bb6\u8fd8\u6ca1\u6709\u89c9\u5f97\u600e\u6837\uff0c\u7b2c\u4e8c\u5929\u5df2\u662f\u5728\u5ced\u58c1\u9669\u5cf0\u4e4b\u95f4\u884c\u8d70\u4e86\uff0c\u9ad8\u5cf0\u4e0a\u7ecf\u5e38\u6709 \u96ea\u6c34\u6c47\u6210\u7684\u6025\u6d41\u51b2\u6cfb\u4e0b\u6765\uff0c\u8d8a\u5f80\u4e0a\u8d70\uff0c\u5bd2\u6c14\u8d8a\u6d53\uff0c\u6025\u6d41\u91cc\u7684\u51b0\u5757\u4e5f\u8d8a\u6765\u8d8a\u591a\uff0c\u5192\u6d63\u83b2\u7259\u9f7f\u9707 \u5f97\u683c\u683c\u4f5c\u54cd\uff0c\u5e78\u5f97\u51cc\u672a\u98ce\u65e9\u6709\u51c6\u5907\uff0c\u5c06\u6666\u660e\u7985\u5e08\u91c7\u5929\u5c71\u96ea\u83b2\u914d\u6210\u7684\u201c\u78a7\u7075\u4e39\u201d\u9001\u4e00\u7c92\u7ed9\u5979\u54bd \u4e0b\uff0c\u53c8\u6559\u5979\u8c03\u6c14\u547c\u5438\u4e4b\u6cd5\uff0c\u8fd9\u624d\u4e0d\u611f\u5bd2\u51b7\u3002\u6842\u4ef2\u660e\u548c\u5f20\u534e\u662d\u529f\u529b\u8f83\u9ad8\uff0c\u5012\u8fd8\u9876\u53d7\u5f97\u4f4f\u3002 \u3000\u3000\u884c\u4e86\u534a\u5929\uff0c\u5ffd\u89c1\u4e00\u5ea7\u767d\u96ea\u7691\u7691\u7684\u5c71\u5cf0\uff0c\u6321\u5728\u9762\u524d\u3002\u8fd9\u5ea7\u5c71\u5cf0\uff0c\u597d\u50cf\u4e00\u5934\u5927\u9a86\u9a7c\uff0c\u5934\u4e1c\u5c3e \u897f\uff0c\u62ab\u7740\u6ee1\u8eab\u767d\u8272\u7684\u7ed2\u6bdb\u3002\u5192\u6d63\u83b2\u4ece\u672a\u89c1\u8fc7\u51b0\u5cf0\uff0c\u62cd\u638c\u53eb\u9053\uff1a\u201c\u597d\u73a9\u5440\uff01\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u53ef \u60dc\u6211\u4eec\u4e3a\u4e86\u8d76\u8def\uff0c\u53ea\u80fd\u4ece\u8fd9\u5ea7\u51b0\u5c71\u7684\u65c1\u8fb9\u7ed5\u8fc7\uff0c\u8fd9\u5c71\u5cf0\u4e0a\u7684\u666f\u8272\u624d\u7f8e\u5462\uff0c\u4e0a\u9762\u6709\u4e00\u4e2a\u51b0\u6e56\uff0c \u8fd8\u53ef\u80fd\u6709\u96ea\u83b2\uff0c\u636e\u8bf4\u662f\u6728\u4ec0\u5854\u514b\u7684\u4e3b\u5cf0\u79fb\u690d\u4e0b\u6765\u7684\u3002\u201d\u5192\u6d63\u83b2\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u505a\u6728\u4ec0\u5854\u514b\uff1f \u662f\u5c71\u540d\u5417\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u53ef\u4ee5\u8bf4\u662f\u5c71\u540d\uff0c\u4f46\u672c\u6765\u5374\u5e76\u4e0d\u662f\u7279\u6b8a\u7684\u5c71\u540d\uff0c\u2018\u6728\u4ec0\u5854\u514b\u2019\u662f\u4e00 \u53e5\u7ef4\u65cf\u8bdd\uff0c\u2018\u6728\u4ec0\u2019\u662f\u5c71\uff0c\u6728\u4ec0\u5854\u514b\uff0c\u4fbf\u662f\u51b0\u5c71\uff0c\u672c\u6765\u56de\u7586\u9ad8\u539f\u4e0a\u6240\u6709\u7684\u51b0\u5c71\uff0c\u90fd\u53ef\u4ee5\u79f0\u505a \u6728\u4ec0\u642d\u514b\uff0c\u4f46\u56e0\u6211\u5300\u76ee\u8fd9\u524d\u8fd9\u5ea7\u51b0\u5c71\uff0c\u5b83\u7684\u4e3b\u5cf0\u6700\u9ad8\uff0c\u6bd4\u6211\u5e08\u7236\u6240\u5c45\u7684\u5317\u9ad8\u5cf0\uff0c\u636e\u8bf4\u53ea\u4f4e\u4e00 \u5343\u591a\u5c3a\uff0c\u6240\u4ee5\u2018\u6728\u4ec0\u5854\u514b\u2019\u4fbf\u6210\u4e86\u5b83\u7684\u4e13\u540d\uff0c\u4f60\u770b\u8fd9\u5ea7\u659c\u63d2\u51fa\u6765\u7684\u9a86\u9a7c\u5cf0\u4e5f\u5f88\u9ad8\u4e86\u3002\u201d\u51cc\u672a \u98ce\u521a\u521a\u8bf4\u5b8c\uff0c\u5ffd\u7136\u5cf0\u9876\u4e0a\u96ea\u5757\u6eda\u6eda\u800c\u4e0b\uff0c\u6709\u5982\u5de8\u77f3\uff0c\u53d1\u51fa\u8f70\u8f70\u4e4b\u58f0\uff0c\u51cc\u672a\u98ce\u7b49\u5de6\u53f3\u8d8b\u907f\uff0c\u8fc7 \u4e86\u597d\u4e00\u4f1a\uff0c\u58f0\u52bf\u624d\u51cf\u5f31\u4e0b\u6765\u3002\u51cc\u672a\u98ce\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u5192\u6d63\u83b2\u95ee\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d \u51cc\u672a\u98ce\u6447\u4e86\u6447\u5934\uff0c\u5192\u6d63\u83b2\u62ac\u5934\u671b\u4e0a\u5cf0\u9876\uff0c\u5ffd\u89c1\u6709\u4e00\u4e1b\u7ea2\u82b1\uff0c\u4e00\u4e1b\u767d\u82b1\uff0c\u5728\u79ef\u96ea\u4e2d\u633a\u9732\u51fa\u6765\uff0c \u6781\u4e3a\u53ef\u7231\u3002\u5192\u6d63\u83b2\u9053\uff1a\u201c\u554a\uff0c\u6211\u771f\u60f3\u4e0a\u53bb\uff0c\u6458\u4e24\u6735\u4e0b\u6765\uff01\u201d\u51cc\u672a\u98ce\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6211\u7ed9\u4f60\u4eec\u8bf4 \u4e00\u4e2a\u6545\u4e8b\u597d\u4e0d\u597d\uff1f\u5c31\u662f\u5173\u4e8e\u8fd9\u4e2a\u9a86\u9a7c\u5cf0\u7684\u6545\u4e8b\u3002\u201d\u5192\u6d63\u83b2\u62cd\u624b\u9053\uff1a\u201c\u597d\u5440\uff0c\u6545\u4e8b\u91cc\u4e5f\u6709\u8fd9\u96ea \u4e2d\u7684\u9c9c\u82b1\u5417\uff1f\u201d\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u6709\u7684\u3002\u201d\u4ed6\u6307\u7740\u5c71\u5cf0\u8bf4\u9053\uff1a \u3000\u3000\u201c\u76f8\u4f20\u5728\u597d\u51e0\u767e\u5e74\u4e4b\u524d\uff0c\u5c71\u4e0a\u6ca1\u6709\u51b0\uff0c\u4e5f\u6ca1\u6709\u96ea\uff0c\u6ee1\u5c71\u662f\u7eff\u8335\u8335\u7684\u8349\u5730\u548c\u95ea\u7740\u5149\u8292\u7684\u5b9d \u77f3\uff0c\u5728\u5c71\u9876\u4e0a\u6709\u80a1\u6e05\u6cc9\uff0c\u900f\u660e\u7684\u6cc9\u6c34\u91cc\u6eda\u52a8\u7740\u4e94\u5149\u5341\u8272\u7684\u73cd\u73e0\uff0c\u6cc9\u8fb9\u4e1b\u751f\u7740\u5947\u5f02\u7684\u82b1\u8349\uff0c\u6709 \u4e00\u4e1b\u50cf\u671d\u971e\u4e00\u6837\u7684\u7ea2\u82b1\uff0c\u6709\u4e00\u4e1b\u50cf\u6708\u5149\u4e00\u6837\u7684\u767d\u82b1\uff0c\u5c31\u662f\u5c71\u811a\u4e0b\u7684\u884c\u4eba\u4e5f\u53ef\u4ee5\u95fb\u5230\u82b1\u9999\u3002\u636e \u8bf4\u62ff\u8fd9\u4e24\u79cd\u82b1\u8c03\u51b0\u56bc\u4e0b\uff0c\u5e74\u8001\u7684\u53ef\u4ee5\u53d8\u6210\u5e74\u9752\uff0c\u5e74\u9752\u7684\u4f1a\u53d8\u5f97\u66f4\u7f8e\u3002\u90a3\u65f6\u5c71\u4e0b\u4f4f\u7740\u4e00\u4e2a\u52c7\u6562 \u7684\u5854\u5409\u514b\u7684\u9752\u5e74\uff0c\u4ed6\u5c06\u8981\u548c\u4e00\u4e2a\u6f02\u4eae\u7684\u7267\u7f8a\u59d1\u5a18\u7ed3\u5a5a\uff0c\u9752\u5e74\u60f3\u6458\u51e0\u679d\u795e\u4ed9\u7684\u82b1\u6735\u8d60\u7ed9\u4ed6\u6240\u7231 \u7684\u4eba\uff0c\u4e8e\u662f\u5e26\u7740\u8db3\u591f\u7684\u7cae\u98df\u548c\u9a6c\u5976\u722c\u4e0a\u5c71\u53bb\uff0c\u722c\u4e86\u4e03\u5929\u53c8\u4e03\u591c\uff0c\u7ec8\u4e8e\u6765\u5230\u4e86\u5c71\u9876\u7684\u6cc9\u8fb9\u3002\u6b63 \u5de7\u5b88\u62a4\u82b1\u8349\u7684\u4ed9\u5973\u7761\u7740\u4e86\uff0c\u4ed6\u4fbf\u6458\u4e0b\u4e00\u675f\u7ea2\u82b1\uff0c\u4e00\u675f\u767d\u82b1\uff0c\u5f53\u4ed6\u8d70\u5230\u5c71\u8170\u7684\u65f6\u5019\uff0c\u770b\u82b1\u7684\u4ed9 \u5973\u9192\u4e86\uff0c\u4ed9\u5973\u770b\u89c1\u9752\u5e74\u624b\u91cc\u62ff\u7740\u653e\u51fa\u5f69\u971e\u7684\u82b1\u6735\uff0c\u4fbf\u547d\u8001\u96d5\u6765\u593a\uff0c\u8001\u96d5\u88ab\u9752\u5e74\u6253\u8d25\u4e86\u3002\u4ed9\u5973 \u53c8\u547d\u4eba\u718a\u6765\u593a\uff0c\u4eba\u718a\u53c8\u88ab\u9752\u5e74\u63a8\u5230\u60ac\u5d16\u5e95\u4e0b\u53bb\u4e86\u3002\u6700\u540e\uff0c\u4ed9\u5973\u81ea\u5df1\u53d8\u6210\u4e86\u4e00\u4e2a\u72f0\u72de\u7684\u5de8\u4eba\uff0c \u62e6\u4f4f\u9752\u5e74\u7684\u53bb\u8def\uff0c\u9752\u5e74\u77e5\u9053\u6218\u4e0d\u8fc7\u5979\uff0c\u4fbf\u548c\u5979\u8bf4\uff1a\u2018\u6211\u8981\u628a\u8fd9\u4e24\u675f\u82b1\u5e26\u7ed9\u6211\u6240\u7231\u7684\u4eba\uff0c\u5982\u679c \u4f60\u4e0d\u653e\u6211\u8fc7\u53bb\uff0c\u6211\u4fbf\u62b1\u7740\u8fd9\u4e24\u675f\u82b1\u8df3\u4e0b\u60ac\u5d16\u2026\u2026\u2019\u4ed9\u5973\u7684\u5fc3\u8f6f\u4e86\uff0c\u5c31\u5141\u8bb8\u9752\u5e74\u628a\u8fd9\u5e78\u798f\u7684\u82b1 \u6735\u5e26\u5230\u4eba\u95f4\uff0c\u4f46\u662f\u4ed9\u5973\u5374\u56e0\u4e3a\u8ba9\u4ed9\u82b1\u843d\u5230\u51e1\u4eba\u624b\u91cc\u800c\u72af\u4e86\u5929\u6761\uff0c\u88ab\u6c38\u8fdc\u56f0\u9501\u5728\u5c71\u9876\u4e0a\u3002\u5979\u6d41 \u4e0b\u7684\u773c\u6cea\u7ed3\u6210\u51b0\uff0c\u8986\u76d6\u5728\u5dcd\u5ce8\u7684\u5c71\u5cad\uff0c\u5c71\u4e0a\u7684\u79ef\u96ea\uff0c\u5c31\u662f\u5979\u5728\u82e6\u96be\u4e2d\u71ac\u767d\u4e86\u7684\u5934\u53d1\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u8bf4\u5b8c\u4e86\uff0c\u5192\u6d63\u83b2\u8d5e\u53f9\u9053\uff1a\u201c\u8fd9\u6545\u4e8b\u771f\u7f8e\uff01\u201d\u5f20\u534e\u662d\u9053\uff1a\u201c\u90a3\u4e2a\u9752\u5e74\u771f\u52c7\u6562\uff0c\u4e3a\u4e86 \u4ed6\u6240\u7231\u7684\u4eba\uff0c\u4ed6\u4ec0\u4e48\u5371\u9669\u90fd\u4e0d\u6015\u3002\u201d\u8fd9\u65f6\uff0c\u4e00\u9635\u98ce\u6765\uff0c\u5439\u6765\u4e00\u80a1\u6e05\u9999\uff0c\u5192\u6d63\u83b2\u770b\u7740\u90a3\u4e24\u4e1b\u9c9c \u82b1\u51fa\u795e\uff0c\u6842\u4ef2\u660e\u5ffd\u9053\uff1a\u201c\u4f60\u559c\u6b22\u90a3\u7ea2\u82b1\u548c\u767d\u82b1\u5417\uff1f\u6211\u66ff\u4f60\u53bb\u6458\uff1f\u201d\u5f20\u534e\u662d\u4e5f\u9053\uff1a\u201c\u6613\u5170\u73e0\u6700 \u7231\u82b1\uff0c\u53ef\u60dc\u5979\u4e0d\u5728\u8fd9\u513f\uff0c\u8981\u4e0d\u7136\u6211\u4e5f\u966a\u4f60\u4e0a\u53bb\uff01\u201d\u5192\u6d47\u83b2\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u771f\u5b69\u5b50\u6c14\uff0c\u8d76\u8def\u8fd8 \u6765\u4e0d\u53ca\uff0c\u4f60\u4eec\u5374\u56b7\u7740\u8981\u53bb\u6458\u82b1\u3002\u201d\u51cc\u672a\u98ce\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u541b\u5b50\u5750\u8a00\u8d77\u884c\uff0c\u4f60\u4eec\u65e2\u7136\u90fd\u60f3\u4e0a\u53bb\u6458 \u82b1\uff0c\u5c31\u4e0a\u53bb\u5427\uff0c\u6211\u548c\u5192\u6d63\u83b2\u59d1\u5a18\u5728\u8fd9\u91cc\u7b49\u4f60\u4eec\u3002\u201d\u6842\u4ef2\u660e\u6781\u7231\u90a3\u4e9b\u82b1\uff0c\u95ee\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u4f60 \u4e0d\u662f\u8bf4\u7b11\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u51e0\u65f6\u548c\u4f60\u8bf4\u8fc7\u7b11\u6765\uff1f\u201d\u6842\u4ef2\u660e\u5927\u559c\uff0c\u62c9\u7740\u5f20\u534e\u662d\u5f80\u5c71\u4e0a\u4fbf\u8dd1\uff0c \u5192\u6d63\u83b2\u5947\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u4f60\u600e\u4e48\u4e5f\u8fd9\u6837\u5b69\u5b50\u6c14\u4e86\uff1f\u201d\u51cc\u672a\u98ce\u7b11\u800c\u4e0d\u7b54\uff0c\u53cc\u773c\u6ce8\u5b9a\u5c71\u9876\uff0c\u76ee\u5149 \u4e2d\u4f3c\u542b\u6709\u6df1\u610f\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u9a86\u9a7c\u5cf0\u4e0a\u5ffd\u7136\u4f20\u51fa\u51e0\u58f0\u602a\u5578\uff0c\u6447\u66f3\u957f\u7a7a\uff0c\u9a87\u4eba\u5fc3\u9b44\uff0c\u8ddf\u7740\u662f\u6842\u4ef2\u660e\u547c\u559d\u4e4b \u58f0\uff0c\u78e8\u76d8\u5927\u7684\u96ea\u5757\u53c8\u6eda\u6eda\u800c\u4e0b\uff0c\u5192\u6d63\u83b2\u60ca\u9053\uff1a\u201c\u90a3\u4e0a\u9762\u8fd8\u4f4f\u5f97\u6709\u4eba\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u5feb\u4e0a\u53bb \u770b\uff01\u201d\u62c9\u7740\u5192\u6d63\u83b2\u817e\u8eab\u4fbf\u8d77\uff0c\u6500\u4e0a\u5c71\u9876\u3002\u8fd9\u5ea7\u51b0\u5c71\u6781\u9ad8\uff0c\u4f46\u659c\u63d2\u51fa\u6765\u7684\u9a86\u9a7c\u5cf0\uff0c\u79bb\u51cc\u672a\u98ce\u7acb \u8db3\u4e4b\u70b9\uff0c\u5374\u4e0d\u5230\u767e\u4e08\uff0c\u4e24\u4eba\u624b\u8db3\u5e76\u7528\uff0c\u6ca1\u591a\u4e45\u4fbf\u4e0a\u5230\u5c71\u9876\u3002 \u3000\u3000\u4e14\u8bf4\u6842\u4ef2\u660e\u548c\u5f20\u534e\u662d\u6500\u767b\u4e0a\u53bb\u6458\u82b1\uff0c\u4e24\u4eba\u4e24\u6837\u5fc3\u60c5\uff0c\u6842\u4ef2\u660e\u50cf\u4e2a\u5b69\u5b50\u4f3c\u7684\uff0c\u8fdc\u8fdc\u671b\u7740 \u201c\u4ed9\u82b1\u201d\u53c8\u7b11\u53c8\u56b7\uff0c\u5fc3\u60f3\uff1a\u6458\u4e86\u4e0b\u6765\u7ed9\u70f7\u83b2\uff0c\u5979\u4e0d\u77e5\u8981\u591a\u9ad8\u5174\u5462\uff01\u5f20\u534e\u662d\u5374\u662f\u9ed8\u9ed8\u65e0\u8a00\uff0c\u8033 \u8fb9\u54cd\u8d77\u6613\u5170\u73e0\u4ee5\u524d\u7684\u8bdd\uff1a\u201c\u6211\u6b7b\u4e86\u4e4b\u540e\uff0c\u4f60\u613f\u610f\u6458\u4e00\u6735\u5170\u82b1\u63d2\u5728\u6211\u7684\u5893\u65c1\u5417\uff1f\u201d\u6613\u5170\u73e0\u73b0\u5728 \u662f\u6551\u51fa\u6765\u4e86\uff0c\u4f46\u5374\u6a2a\u91cc\u6740\u6765\u4e00\u4e2a\u98de\u7ea2\u5dfe\uff0c\u628a\u5979\u62a2\u53bb\uff0c\u8fd9\u56de\u82e5\u627e\u4e0d\u5230\u5979\uff0c\u5979\u4e0d\u4f1a\u6b7b\uff0c\u5374\u8981\u8f6e\u5230 \u81ea\u5df1\u6194\u60b4\u800c\u6b7b\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u6500\u5230\u4e0a\u9762\uff0c\u5ffd\u89c9\u773c\u524d\u4e00\u4eae\uff0c\u5c71\u9876\u679c\u7136\u6709\u4e00\u80a1\u6e05\u6cc9\uff0c\u900f\u660e\u7684\u6cc9\u6c34\u4e2d\u6709\u95ea\u5149\u7684\u51b0\u5757\u548c\u96f6 \u843d\u7684\u82b1\u74e3\u3002\u6842\u4ef2\u660e\u62cd\u624b\u7b11\u9053\uff1a\u201c\u597d\u7f8e\u5440\uff01\u90a3\u4f20\u8bf4\u4e2d\u7684\u4ed9\u5883\u83ab\u975e\u7adf\u662f\u771f\u7684\uff1f\u201d\u90a3\u4e24\u4e1b\u201c\u4ed9\u82b1\u201d \u5f00\u5728\u6cc9\u6c34\u4e4b\u65c1\uff0c\u5f20\u534e\u662d\u8dd1\u53bb\u6458\u82b1\uff0c\u5ffd\u89c1\u82b1\u4e1b\u4e2d\u6709\u4e00\u6735\u6781\u5927\u7684\u7ea2\u82b1\uff0c\u7adf\u6709\u6d77\u7897\u90a3\u6837\u5927\u3002\u5f20\u534e\u662d \u7528\u5251\u62e8\u5f00\u82b1\u4e1b\u4e2d\u7684\u8346\u68d8\uff0c\u5ffd\u7136\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u4ef2\u660e\uff0c\u4f60\u5feb\u6765\uff01\u201d\u6842\u4ef2\u660e\u5b66\u4ed6\u7684\u6837\uff0c \u7528\u817e\u86df\u5b9d\u5251\u62e8\u5f00\u8346\u68d8\uff0c\u8d70\u8fdb\u53bb\u4e00\u671b\uff0c\u4e5f\u60ca\u5947\u5730\u53eb\u51fa\u58f0\u6765\uff01 \u3000\u3000\u82b1\u4e1b\u7684\u540e\u9762\u662f\u4e00\u9762\u77f3\u58c1\uff0c\u77f3\u58c1\u4e0a\u51ff\u6709\u4e00\u4e2a\u7a84\u7a84\u7684\u6d1e\u7a9f\uff0c\u6d1e\u7a9f\u91cc\u6709\u4e00\u4e2a\u4eba\u76d8\u819d\u800c\u5750\uff0c\u9762\u5bb9 \u67af\u524a\uff0c\u5168\u65e0\u8840\u8272\uff0c\u5c31\u5982\u4e00\u5177\u9ab7\u9ac5\u4e00\u6837\uff01 \u3000\u3000\u5f20\u534e\u662d\u8d70\u4e86\u8d70\u795e\uff0c\u5411\u77f3\u7a9f\u6df1\u6df1\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u65e0\u77e5\u51b2\u95ef\uff0c\u60ca\u52a8\u524d\u8f88\uff0c\u5c1a\u671b\u6055\u7f6a\uff01\u201d \u90a3\u9ab7\u9ac5\u4f3c\u7684\u602a\u4eba\u4ecd\u662f\u76d8\u819d\u95ed\u76ee\uff0c\u4e0d\u8a00\u4e0d\u8bed\u3002\u6302\u4ef2\u660e\u6709\u70b9\u5fc3\u602f\uff0c\u4e5f\u6709\u70b9\u751f\u6c14\u3002\u62c9\u5f20\u534e\u662d\u9053\uff1a \u201c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u90a3\u602a\u4eba\u5ffd\u7136\u5f20\u5f00\u53cc\u76ee\uff0c\u51b7\u68ee\u68ee\u7684\u76ee\u5149\u76f4\u5c04\u5230\u4e24\u4eba\u9762\u4e0a\uff0c\u5f20\u534e\u662d\u505c\u4e0b\u6b65\u6765\uff0c\u53ea\u542c\u5f97\u90a3\u4e2a\u602a \u4eba\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u5a03\u5b50\u65e2\u7136\u77e5\u7f6a\uff0c\u6211\u4e5f\u53ef\u653e\u4f60\u4eec\u51fa\u53bb\uff0c\u53ea\u662f\u4f60\u4eec\u5f97\u7559\u4e0b\u70b9\u4e1c\u897f\uff01\u201d\u6842\u4ef2\u660e \u6012\u9053\uff1a\u201c\u4f60\u8981\u4ec0\u4e48\uff1f\u201d\u602a\u4eba\u9053\uff1a\u201c\u628a\u4f60\u7684\u5251\u7559\u4e0b\u6765\uff01\u201d\u5ffd\u5730\u4e00\u58f0\u602a\u5578\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u600e\u4f6f\u4f5c\u52bf\uff0c \u4eba\u5df2\u98de\u63a0\u5230\u6842\u4ef2\u660e\u65c1\u8fb9\uff0c\u4f38\u51fa\u9e21\u722a\u822c\u7684\u602a\u624b\uff0c\u671d\u6842\u4ef2\u660e\u5f53\u5934\u4fbf\u6293\uff01 \u3000\u3000\u6842\u4ef2\u660e\u5927\u5403\u4e00\u60ca\uff0c\u6a2a\u91cc\u4e00\u8dc3\uff0c\u817e\u86df\u5b9d\u5251\u5237\u5730\u5f80\u4e0a\u64a9\u53bb\uff0c\u90a3\u4eba\u8eab\u6cd5\u53e4\u602a\u4e4b\u6781\uff0c\u5728\u65b9\u5bf8\u4e4b \u5730\uff0c\u7ade\u81ea\u76d8\u65cb\u5982\u610f\uff0c\u6842\u4ef2\u660e\u5251\u65b9\u523a\u51fa\uff0c\u624b\u8155\u5ffd\u5730\u4e00\u9635\u8fa3\u75db\uff0c\u5b9d\u5251\u51e0\u4e4e\u6389\u5730\uff0c\u6025\u5f97\u5927\u543c\u4e00\u58f0\uff0c \u5de6\u638c\u731b\u7684\u53d1\u51fa\uff0c\u90a3\u602a\u4eba\u8eab\u5f71\u4e00\u6643\u4e0d\u89c1\uff0c\u63a5\u7740\u662f\u5f20\u534e\u662d\u5927\u53eb\u4e00\u58f0\uff0c\u6574\u4e2a\u8eab\u5b50\u8dcc\u5165\u4e86\u82b1\u4e1b\u4e4b\u4e2d\u3002 \u3000\u3000\u539f\u6765\u5f20\u534e\u662d\u89c1\u6842\u4ef2\u660e\u731d\u88ab\u653b\u51fb\uff0c\u957f\u5251\u4e00\u62db\u201c\u795e\u9f99\u5165\u6d77\u201d\uff0c\u659c\u4fa7\u523a\u53bb\uff0c\u90a3\u602a\u4eba\u672c\u5c06\u5f97\u624b\uff0c \u4e5f\u987e\u4e0d\u5f97\u518d\u593a\u6842\u4ef2\u660e\u7684\u5b9d\u5251\uff0c\u8eab\u5f62\u7565\u95ea\uff0c\u95ea\u5230\u5f20\u534e\u662d\u80cc\u540e\uff0c\u4e00\u638c\u628a\u4ed6\u63a8\u5012\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u6842\u4ef2 \u660e\u5df2\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u8df3\u8fc7\u4e86\u82b1\u4e1b\u3002\u602a\u4eba\u53c8\u662f\u4e00\u58f0\u602a\u5578\uff0c\u8ddf\u7740\u98de\u8dc3\u51fa\u6765\u3002 \u3000\u3000\u6842\u4ef2\u660e\u8fd9\u56de\u5b66\u4e56\u4e86\uff0c\u817e\u86df\u5b9d\u5251\u4e00\u4e2a\u65cb\u98ce\u75be\u821e\uff0c\u62a4\u7740\u8eab\u8eaf\uff0c\u5c55\u5f00\u4e94\u79bd\u5251\u6cd5\u4e2d\u7684\u7cbe\u5999\u62db\u6570\uff0c \u7d27\u7d27\u5c01\u95ed\u95e8\u6237\uff0c\u90a3\u602a\u4eba\u5728\u5251\u5149\u4e2d\u7a7f\u6765\u63d2\u53bb\uff0c\u65e0\u6cd5\u593a\u5230\u5b9d\u5251\u3002\u4f46\u6842\u4ef2\u660e\u4e5f\u611f\u5230\u638c\u98ce\u5288\u9762\uff0c\u8fed\u9047 \u9669\u62db\uff0c\u8d8a\u6253\u8d8a\u5947\uff0c\u7adf\u4e0d\u77e5\u8fd9\u4eba\u7684\u638c\u6cd5\u662f\u4ec0\u4e48\u5bb6\u6570\u3002 \u3000\u3000\u518d\u8bf4\u5f20\u534e\u662d\u731d\u4e0d\u53ca\u9632\uff0c\u88ab\u602a\u4eba\u4e00\u638c\u63a8\u4eba\u82b1\u4e1b\u4e4b\u4e2d\uff0c\u5ffd\u95fb\u5947\u9999\u6251\u9f3b\uff0c\u7cbe\u795e\u987f\u723d\uff0c\u4e00\u770b\u90a3\u6735 \u5927\u7ea2\u82b1\u6b63\u5728\u9f3b\u5c16\uff0c\u6025\u5fd9\u6458\u4e0b\uff0c\u6536\u8fdb\u6000\u4e2d\uff0c\u62e8\u5f00\u82b1\u679d\uff0c\u8df3\u51fa\u5916\u9762\uff0c\u53ea\u89c1\u5251\u5149\u95ea\u70c1\uff0c\u638c\u98ce\u547c\u547c\u3002 \u6842\u4ef2\u660e\u548c\u90a3\u4e2a\u602a\u4eba\u6253\u5f97\u5341\u5206\u6fc0\u70c8\u3002 \u3000\u3000\u90a3\u602a\u4eba\u6253\u5230\u5206\u9645\uff0c\u5ffd\u7136\u53cc\u817f\u9f50\u98de\uff0c\u8fde\u73af\u8e22\u51fa\uff0c\u6842\u4ef2\u660e\u9000\u540e\u51e0\u6b65\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u9e33\u9e2f \u8fde\u73af\u817f\u662f\u90a3\u91cc\u5b66\u6765\u7684\uff1f\u201d\u602a\u4eba\u4e00\u624b\u6293\u53bb\uff0c\u6842\u4ef2\u660e\u4fa7\u8eab\u95ea\u8fc7\uff0c\u90a3\u602a\u4eba\u78d4\u78d4\u602a\u7b11\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a \u201c\u4f60\u8fd9\u5a03\u513f\u662f\u77f3\u5929\u6210\u7684\u4ec0\u4e48\u4eba\uff1f\u201d\u6842\u4ef2\u660e\u6a2a\u5251\u5f53\u6d1e\uff0c\u7d27\u5bc6\u9632\u5907\uff0c\u95ee\u9053\uff1a\u201c\u524d\u8f88\u83ab\u975e\u662f\u5bb6\u7236\u7684 \u540c\u95e8\uff1f\u201d\u602a\u4eba\u53c8\u662f\u4e00\u58f0\u957f\u5578\uff0c\u8bf4\u9053\uff1a\u201c\u554a\uff01\u539f\u6765\u4f60\u662f\u77f3\u5929\u6210\u7684\u513f\u5b50\uff01\u4f60\u7684\u773c\u529b\u4e0d\u9519\uff0c\u4f60\u7684\u7236 \u4eb2\u6b63\u662f\u6211\u7684\u5e08\u5144\uff01\u201d\u6842\u4ef0\u660e\u6025\u5fd9\u62b1\u5251\u4f5c\u63d6\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u6211\u7684\u5e08\u53d4\u4e86\uff1f\u53ef\u5426\u8bf7\u793a\u59d3\u540d\uff0c\u5bb9\u665a \u8f88\u8bf7\u6559\uff1f\u201d\u90a3\u602a\u4eba\u5ffd\u7136\u53c8\u662f\u4e00\u638c\u5288\u51fa\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u5c0a\u6211\u4e3a\u5e08\u53d4\uff0c\u628a\u4f60\u7684\u5b9d\u5251\u62ff\u6765\uff0c\u4f60\u5e08\u53d4 \u8981\u7528\u3002\u201d\u6842\u4ef2\u660e\u4e00\u4e2a\u7b4b\u6597\u5012\u7eb5\u7ffb\u51fa\u53bb\uff0c\u6717\u58f0\u7b54\u9053\uff1a\u201c\u4f60\u867d\u662f\u6211\u7684\u957f\u8f88\uff0c\u8981\u5f3a\u62a2\u90a3\u53ef\u4e0d\u6210\uff01\u201d \u817e\u86df\u5b9d\u5251\u970d\u970d\u5c55\u5f00\uff0c\u53c8\u548c\u602a\u4eba\u6076\u6597\u3002 \u3000\u3000\u6842\u4ef2\u660e\u7236\u4eb2\u77f3\u5929\u6210\uff0c\u4e8c\u5341\u591a\u5e74\u524d\uff0c\u66fe\u4e09\u4e0a\u5929\u5c71\uff0c\u8ddf\u6666\u660e\u7985\u5e08\u5b66\u5251\uff0c\u6666\u660e\u7985\u5e08\u4e0d\u80af\u6536\u7559\uff0c \u628a\u4ed6\u8350\u7ed9\u81ea\u5df1\u7684\u597d\u53cb\uff0c\u6b66\u5f53\u540d\u5bbf\u5353\u4e00\u822a\uff0c\u5b66\u4e86\u4e5d\u5bab\u795e\u884c\u638c\u548c\u9e33\u9e2f\u8fde\u73af\u817f\u4e24\u6837\u7edd\u679d\uff0c\u6842\u4ef2\u660e\u867d \u6ca1\u5b66\u8fc7\uff0c\u4f46\u5374\u77e5\u9053\u3002\u53ea\u662f\u8fd9\u602a\u4eba\u7684\u638c\u6cd5\u5374\u53c8\u4e0d\u662f\u4e5d\u5bab\u795e\u884c\u638c\uff0c\u4ed6\u867d\u7136\u81ea\u79f0\u662f\u6842\u4ef2\u660e\u7684\u5e08\u53d4\uff0c \u6842\u4ef2\u660e\u5374\u8fd8\u662f\u4e0d\u65e0\u7591\u60d1\u3002 \u3000\u3000\u5f20\u534e\u662d\u89c1\u602a\u4eba\u5982\u6b64\u4e0d\u8bb2\u9053\u7406\uff0c\u751a\u4e3a\u6124\u6068\uff0c\u53c8\u89c1\u6842\u4ef2\u660e\u6253\u5f97\u5403\u7d27\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u5237\u7684\u4e00\u5251\u523a \u51fa\uff0c\u602a\u4eba\u5ffd\u5730\u65cb\u8eab\uff0c\u53cc\u624b\u8fce\u7740\u5251\u950b\u4fbf\u6293\uff0c\u5f20\u534e\u662d\u5251\u950b\u659c\u5212\uff0c\u5f80\u540e\u4e00\u62d6\uff0c\u7528\u7684\u662f\u65e0\u6781\u5251\u4e2d\u653b\u5b88 \u517c\u5907\u7684\u7cbe\u5999\u62db\u6570\uff0c\u602a\u4eba\u5fae\u566b\u4e00\u58f0\uff0c\u8eab\u5b50\u4e00\u632b\uff0c\u4e0d\u6562\u786c\u6293\uff0c\u8f6c\u8eab\u53c8\u63a5\u4e0a\u4e86\u6842\u4ef2\u660e\u7684\u62db\u6570\u3002 \u3000\u3000\u5f20\u534e\u662d\u662f\u5085\u9752\u4e3b\u5e08\u4f84\uff0c\u65e0\u6781\u5251\u6cd5\u4e5f\u9887\u6709\u9020\u8be3\uff0c\u53ea\u662f\u521a\u624d\u731d\u51fa\u4e0d\u610f\uff0c\u624d\u7ed9\u602a\u4eba\u4e00\u638c\u51fb\u5012\uff0c \u5982\u4eca\u52a0\u610f\u9632\u5907\uff0c\u867d\u7136\u523a\u4e0d\u7740\u602a\u4eba\uff0c\u5374\u4e5f\u52a9\u4e86\u6842\u4ef2\u660e\u4e00\u81c2\u4e4b\u5200\u3002\u5f20\u534e\u662d\u6b66\u529f\u4ec5\u5728\u6842\u4ef2\u660e\u4e4b\u4e0b\uff0c \u4e24\u4eba\u8054\u624b\u5408\u6597\uff0c\u90a3\u602a\u4eba\u987e\u6b64\u5931\u5f7c\uff0c\u4e00\u65f6\u95f4\u5012\u5948\u4f55\u4ed6\u4eec\u4e24\u4eba\u4e0d\u5f97\uff01 \u3000\u3000\u53ea\u662f\u602a\u4eba\u7684\u8eab\u6cd5\u5b9e\u5728\u53e4\u602a\uff0c\u653b\u52bf\u8fde\u7ef5\u4e0d\u7edd\uff0c\u9669\u62db\u8fed\u51fa\u4e0d\u7a77\u3002\u6842\u4ef2\u660e\u548c\u5f20\u534e\u662d\u4ec5\u80fd\u81ea\u4fdd\uff0c \u65e0\u6cd5\u8fdb\u653b\uff0c\u65f6\u95f4\u4e00\u957f\uff0c\u52bf\u5fc5\u843d\u8d25\u3002\u6b63\u5728\u5403\u7d27\uff0c\u90a3\u602a\u4eba\u8fde\u8fde\u602a\u5578\uff0c\u638c\u6cd5\u66f4\u89c1\u51cc\u5389\uff0c\u53eb\u9053\uff1a\u201c\u8d24 \u4f84\u8d24\u4f84\uff01\u4e3a\u5e08\u53d4\u7684\u4e0d\u5fcd\u4f24\u4f60\uff0c\u8fd8\u662f\u628a\u5b9d\u5251\u4e56\u4e56\u5730\u732e\u7ed9\u6211\u5427\uff01\u201d \u3000\u3000\u6842\u4ef2\u660e\u5341\u5206\u6c14\u6124\uff0c\u4e00\u62db\u201c\u9e70\u51fb\u957f\u7a7a\u201d\uff0c\u5b9d\u5251\u53cd\u6311\u51fa\u53bb\uff0c\u90a3\u77e5\u6b63\u4e2d\u4e86\u602a\u4eba\u8bf1\u654c\u4e4b\u8ba1\uff0c\u5251\u4e00 \u523a\u51fa\uff0c\u9732\u51fa\u7a7a\u95e8\uff0c\u602a\u4eba\u4e00\u6293\u4fbf\u6293\u5230\u4ed6\u7684\u80c1\u4e0b\uff01 \u3000\u3000\u6842\u4ef2\u660e\u4e00\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\uff0c\u5012\u7ffb\u51fa\u53bb\uff0c\u8fd9\u4e00\u62db\u8f7b\u529f\u4e43\u662f\u5ddd\u4e2d\u5927\u4fa0\u53f6\u4e91\u82cf\u5f53\u5e74\u6a21\u62df\u7a7a\u4e2d \u98de\u79bd\u7ffb\u817e\u4e4b\u52bf\u6240\u521b\u51fa\u6765\u7684\uff0c\u602a\u4eba\u4e00\u51fb\u4e0d\u4e2d\uff0c\u548c\u8eab\u6251\u53bb\uff0c\u6842\u4ef2\u660e\u5df2\u5148\u843d\u5730\uff0c\u817e\u86df\u5b9d\u5251\u4e00\u4e2a\u76d8 \u65cb\uff0c\u6b63\u5f85\u51fa\u8d70\uff0c\u5ffd\u89c9\u80cc\u540e\u6709\u4eba\u4e00\u626f\uff0c\u6842\u4ef2\u660e\u56de\u65f6\u4e00\u649e\uff0c\u6ca1\u6709\u649e\u7740\uff0c\u5df2\u7ed9\u90a3\u4eba\u626f\u8fc7\u4e00\u8fb9\u3002\u90a3\u602a \u4eba\u51dd\u8eab\u6b62\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u51e0\u4e2a\u5e2e\u624b\uff1f\u201d\u6842\u4ef2\u660e\u8fd9\u65f6\u624d\u8ba4\u6e05\u80cc\u540e\u6765\u7684\u4eba\u4e43\u662f\u51cc\u672a\u98ce\u3002\u518d\u4e00 \u770b\u65f6\uff0c\u5192\u6d63\u83b2\u4e5f\u5373\u5c06\u722c\u81f3\u5c71\u4e0a\uff0c\u72c2\u559c\u4e4b\u4f59\uff0c\u53c8\u4e0d\u7981\u9762\u7ea2\u8fc7\u8033\u3002\u8981\u77e5\u9ad8\u624b\u640f\u6597\uff0c\u8bb2\u7a76\u7684\u662f\u773c\u89c2 \u516d\u8def\uff0c\u8033\u542c\u516b\u65b9\uff0c\u80cc\u540e\u6709\u4eba\u6765\u5230\uff0c\u81ea\u5df1\u5c1a\u672a\u77e5\u9053\uff0c\u5c82\u4e0d\u8981\u7cdf\uff1f\u4e0d\u8fc7\u8fd9\u4e5f\u602a\u4e0d\u5f97\u6842\u4ef2\u660e\uff0c\u8fd9\u602a \u4eba\u662f\u4ed6\u751f\u5e73\u7b2c\u4e00\u6b21\u78b0\u5230\u7684\u5f3a\u654c\uff0c\u6bd4\u695a\u662d\u5357\u597d\u50cf\u8fd8\u8981\u5389\u5bb3\uff0c\u4ed6\u5168\u795e\u8d2f\u6ce8\u5728\u602a\u4eba\u8eab\u4e0a\uff0c\u800c\u51cc\u672a\u98ce \u8f7b\u529f\u53c8\u6bd4\u4ed6\u9ad8\uff0c\u4ed6\u8eab\u7136\u89c9\u5bdf\u4e0d\u5230\u3002 \u3000\u3000\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u4ed6\u81ea\u79f0\u662f\u6211\u7684\u5e08\u53d4\uff0c\u5374\u53c8\u8981\u62a2\u6211\u7684\u5b9d \u5251\u3002\u201d\u51cc\u672a\u98ce\u6307\u7740\u90a3\u602a\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u505a\u957f\u8f88\u7684\u4e0d\u7ed9\u89c1\u9762\u793c\u4e5f\u8fd8\u7f62\u4e86\uff0c\u600e\u4e48\u53cd\u5411\u5c0f\u8f88\u8981\u4e1c \u897f\uff1f\u201d\u90a3\u602a\u4eba\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u66ff\u4ed6\u51fa\u5934\uff1f\u201d\u4e0d\u7531\u5206\u8bf4\uff0c\u4e00\u697c\u5934\u6293\u4e0b\uff0c\u51cc\u672a\u98ce\u5f15\u8eab\u907f\u8fc7\uff0c \u53eb\u9053\uff1a\u201c\u5929\u5c71\u4e4b\u4e0a\uff0c\u5c82\u5bb9\u4f60\u8fd9\u91ce\u4eba\u6492\u91ce\uff01\u201d\u5de6\u624b\u4e00\u638c\uff0c\u5f3a\u529b\u8fd8\u51fb\u3002\u90a3\u602a\u4eba\u8eab\u5f62\u4e00\u77ee\uff0c\u4ece\u51cc\u672a \u98ce\u638c\u4e0b\u94bb\u8fc7\uff0c\u4f38\u51fa\u4e09\u6307\uff0c\u53cd\u6263\u51cc\u672a\u98ce\u8109\u95e8\u3002\u51cc\u672a\u98ce\u9aa4\u9047\u602a\u62db\uff0c\u5374\u4e0d\u614c\u4e71\uff0c\u6c89\u8155\u4e00\u622a\uff0c\u5de6\u638c\u5411 \u4e0a\u4e00\u6311\uff0c\u8fde\u6d88\u5e26\u6253\uff0c\u602a\u4eba\u8eab\u5f62\u4e00\u6643\uff0c\u4e24\u4eba\u90fd\u4e0d\u7ea6\u800c\u540c\u7684\u500f\u5730\u5206\u5f00\uff0c\u5728\u8fd9\u7535\u5149\u77f3\u706b\u4e4b\u95f4\uff0c\u51cc\u672a \u98ce\u5df2\u6492\u62db\u6362\u62db\uff0c\u4f7f\u51fa\u201c\u6392\u5c71\u8fd0\u638c\u201d\u4e4b\u5f0f\uff0c\u602a\u4eba\u53eb\u58f0\uff1a\u201c\u597d\u5389\u5bb3\uff01\u201d\u4e0d\u6562\u786c\u63a5\u51cc\u672a\u98ce\u7684\u638c\u529b\uff0c \u53cc\u81c2\u4e00\u6296\uff0c\u5e73\u5730\u62d4\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u659c\u659c\u5411\u897f\u9996\u4e00\u843d\u3002\u8fd9\u4e2a\u8eab\u6cd5\u540d\u4e3a\u201c\u9ec4\u9e4a\u51b2\u9704\u201d\uff0c\u5341\u5206\u96be\u7ec3\uff0c \u51cc\u672a\u98ce\u89c1\u4ed6\u7528\u5f97\u5982\u6b64\u7cbe\u7eaf\uff0c\u4e0d\u7981\u4e5f\u6697\u6697\u4f69\u670d\u3002 \u3000\u3000\u6842\u4ef2\u660e\u5728\u65c1\u89c2\u6218\uff0c\u770b\u5f97\u76ee\u7729\u795e\u6447\uff0c\u66f4\u662f\u6697\u6697\u53f9\u670d\uff0c\u5fc3\u60f3\uff0c\u51cc\u672a\u98ce\u6bd5\u7adf\u662f\u4e2a\u5927\u884c\u5bb6\uff0c\u81ea\u5df1 \u9aa4\u9047\u602a\u62db\u65f6\uff0c\u51e0\u4e4e\u54c8\u4e86\u5927\u4e8f\uff0c\u800c\u4ed6\u5374\u4ece\u5bb9\u5316\u89e3\uff0c\u53ea\u8fd9\u4e00\u4efd\u9547\u5b9a\u7684\u4e34\u9635\u529f\u592b\uff0c\u5c31\u975e\u81ea\u5df1\u53ef\u53ca\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5927\u4e3a\u5947\u602a\uff0c\u8fd9\u4eba\u7684\u8eab\u6cd5\u638c\u6cd5\uff0c\u4ece\u672a\u89c1\u8fc7\uff0c\u5230\u5e95\u662f\u54ea\u4e00\u6d3e\u7684\uff1f\u800c\u4e14\u5929\u5c71\u4e4b\u5317\uff0c\u6709\u81ea \u5df1\u7684\u5e08\u7236\uff0c\u5929\u5c71\u4e4b\u5357\u6709\u767d\u53d1\u9b54\u5973\uff0c\u8fd9\u4e24\u4eba\u6b66\u529f\u76d6\u4e16\uff0c\u4ed6\u82e5\u4e0e\u8fd9\u4e24\u4eba\u6ca1\u6709\u6e0a\u6e90\uff0c\u53c8\u600e\u6562\u5728\u5929\u5c71 \u4e4b\u9e93\u7684\u9a86\u9a7c\u5cf0\u4e0a\u505c\u7559\uff1f\u82e5\u4ed6\u662f\u6842\u4ef2\u660e\u7684\u5e08\u53d4\uff0c\u90a3\u4e48\u5f53\u662f\u5ddd\u4e2d\u5927\u4fa0\u53f6\u4e91\u82cf\u7684\u95e8\u4e0b\uff0c\u4f46\u53f6\u5927\u4fa0\u548c \u81ea\u5df1\u5e08\u7236\u53ef\u7d20\u65e0\u5f80\u6765\uff0c\u96be\u9053\u662f\u767d\u53d1\u9b54\u5973\u7684\u540e\u8f88\uff1f\u8fd9\u6837\u4e00\u60f3\uff0c\u51cc\u672a\u98ce\u5012\u4e0d\u6562\u5192\u6627\u8fdb\u62db\u4e86\uff0c\u626c\u58f0 \u559d\u9053\uff1a\u201c\u4f60\u662f\u767d\u53d1\u9b54\u5973\u7684\u4ec0\u4e48\u4eba\uff1f\u201d\u602a\u4eba\u6012\u559d\u9053\uff1a\u201c\u201c\u4ec0\u4e48\u767d\u53d1\u9b54\u5973\uff0c\u770b\u638c\uff01\u201d\u5ffd\u7136\u624b\u821e\u8db3 \u8e48\uff0c\u5982\u9189\u5982\u72c2\uff0c\u53cc\u638c\u4e71\u6253\u8fc7\u6765\uff0c\u770b\u6765\u4f3c\u4e0d\u6210\u7ae0\u6cd5\uff0c\u5176\u5b9e\u6bcf\u4e00\u62db\u5f0f\uff0c\u90fd\u542b\u6709\u6781\u590d\u6742\u7684\u53d8\u5316\uff01\u51cc \u672a\u98ce\u51dd\u795e\u8fd0\u638c\uff0c\u7247\u523b\u62c6\u4e86\u5341\u6570\u62db\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u604d\u7136\u5927\u609f\uff0c\u731b\u7136\u559d\u9053\uff1a\u201c\u4f60\u597d\u4e0d\u8981\u8138\uff0c\u628a\u97e9\u5fd7 \u90a6\u7684\u4e66\u9a97\u4e86\uff0c\u5728\u8fd9\u91cc\u73b0\u4e16\uff01\u6211\u8981\u6349\u4f60\u8fd9\u4e2a\u9a97\u4e66\u7684\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u4ee5\u524d\u66fe\u542c\u97e9\u5fd7\u90a6\u8bf4\u8fc7\u5931\u4e66\u4e4b\u4e8b\uff0c\u8fd9\u65f6\u84e6\u7136\u60f3\u4e86\u8d77\u6765\u3002\u539f\u6765\u97e9\u5fd7\u90a6\u5728\u5165\u85cf\u4e4b\u540e\uff0c\u4e00 \u65e5\u4e0e\u51e0\u4e2a\u5587\u561b\u5230\u65e5\u5580\u5219\u6e38\u89c8\uff0c\u5f53\u665a\u5728\u897f\u85cf\u8457\u540d\u7684\u624e\u5e03\u4f26\u5bfa\u6295\u5bbf\uff0c\u97e9\u5fd7\u90a6\u5348\u591c\u7ec3\u62f3\uff0c\u628a\u4ece\u77f3\u7a9f \u4e2d\u5b66\u5f97\u7684\u638c\u6cd5\uff0c\u4e00\u4e00\u64cd\u7ec3\u3002\u64cd\u7ec3\u5b8c\u6bd5\uff0c\u5ffd\u95fb\u65c1\u8fb9\u6709\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u7684\u638c\u6cd5\u5f88\u597d\uff0c\u53ef\u60dc\u6ca1\u6709\u5b66 \u5168\uff01\u201d\u97e9\u5fd7\u90a6\u6115\u7136\u56de\u987e\uff0c\u53ea\u89c1\u4e00\u4e2a\u6e05\u7626\u8001\u8005\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u6765\u5230\u4e86\u81ea\u5df1\u7684\u8eab\u8fb9\u3002\u97e9\u5fd7\u90a6\u5728\u4e91 \u5c97\u77f3\u7a9f\u5c0f\uff0c\u56e0\u753b\u50cf\u5265\u843d\uff0c\u4e00\u767e\u96f6\u516b\u4e2a\u62db\u5f0f\uff0c\u53ea\u5b66\u5230\u5341\u516d\u5f0f\uff0c\u803f\u803f\u4e8e\u5fc3\uff0c\u603b\u60f3\u80fd\u591f\u5b66\u5168\u624d\u597d\u3002 \u542c\u5f97\u8fd9\u4e2a\u8001\u8005\u5982\u6b64\u4e00\u8bf4\uff0c\u4e0d\u7981\u72c2\u559c\uff0c\u65e0\u6687\u95ee\u4ed6\u662f\u4ec0\u4e48\u6765\u5386\uff0c\u4fbf\u9053\uff1a\u201c\u524d\u8f88\u6562\u60c5\u662f\u719f\u8bc6\u8fd9\u5957\u638c \u6cd5\uff1f\u5982\u8499\u4e0d\u5f03\uff0c\u5f1f\u5b50\u613f\u5217\u95e8\u5899\uff01\u201d\u90a3\u8001\u8005\u7b11\u9053\uff1a\u201c\u4f60\u4f55\u5fc5\u6c42\u6559\u4e8e\u6211\uff0c\u4f60\u6000\u4e2d\u4e0d\u662f\u8fd8\u6709\u4e00\u672c\u602a \u4e66\u5417\uff1f\u5269\u4e0b\u7684\u638c\u6cd5\u3001\u5251\u6cd5\uff0c\u5168\u8bb2\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4f60\u8ba4\u4e0d\u5f97\u5b57\u5417\uff1f\u201d\u97e9\u5fd7\u90a6\u5927\u5947\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e \u4e48\u77e5\u9053\u6211\u6709\u8fd9\u4e00\u672c\u4e66\uff1f\u201d\u8001\u4eba\u9053\uff1a\u201c\u6211\u4e0d\u4f46\u77e5\u9053\u4f60\u6709\u90a3\u672c\u4e66\uff0c\u6211\u8fd8\u77e5\u9053\u90a3\u672c\u4e66\u662f\u5510\u671d\u7684\u65e0\u4f4f \u7985\u5e08\u4f20\u4e0b\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u97e9\u5fd7\u90a6\u8bb0\u8d77\u90a3\u672c\u4e66\u540e\u9762\u7684\u6c49\u5b57\u5c0f\u6ce8\uff0c\u70b9\u4e86\u70b9\u5934\u3002\u8001\u4eba\u7ee7\u7eed\u8bf4\u9053\uff1a \u201c\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u6211\u5c31\u662f\u5143\u4f4f\u7985\u5e08\u8fd9\u4e00\u7cfb\u4f20\u4e0b\u7684\u7b2c\u56db\u5341\u4e8c\u4ee3\u4f20\u4eba\u3002\u201d\u97e9\u5fd7\u90a6\u6025\u5fd9\u62dc\u4e0b\u53bb\u9053\uff1a \u201c\u5f1f\u5b50\u5b66\u827a\u4e0d\u5168\uff0c\u4e07\u671b\u524d\u8f88\u6307\u70b9\uff01\u201d\u8001\u4eba\u9053\uff1a\u201c\u6211\u6ca1\u6709\u90a3\u4e48\u591a\u5de5\u592b\uff0c\u4f46\u6211\u53ef\u4ee5\u6559\u4f60\u7167\u4e66\u4e0a\u7684 \u65b9\u6cd5\u7ec3\u4e60\u3002\u201d\u97e9\u5fd7\u90a6\u9053\uff1a\u201c\u90a3\u4e66\u4e0a\u7684\u6587\u5b57\u53e4\u602a\u81f3\u6781\uff0c\u5f1f\u5b50\u4e00\u4e2a\u4e5f\u4e0d\u8ba4\u5f97\uff0c\u5982\u4f55\u7ec3\u4e60\uff1f\u201d\u8001\u4eba \u9053\uff1a\u201c\u4f60\u628a\u4e66\u62ff\u51fa\u6765\uff0c\u6211\u6559\u4f60\u597d\u4e86\uff01\u201d\u97e9\u5fd7\u90a6\u662f\u4e2a\u6781\u8001\u5b9e\u7684\u4eba\uff0c\u5982\u4f55\u6599\u5f97\u90a3\u8001\u4eba\u4f7f\u8bc8\uff0c\u5f53\u4e0b \u628a\u4e66\u53d6\u51fa\uff0c\u8001\u4eba\u63ed\u5f00\u51e0\u9875\uff0c\u53cc\u76ee\u653e\u5149\uff0c\u5927\u559c\u53eb\u9053\uff1a\u201c\u662f\u4e86\uff01\u662f\u4e86\uff01\u201d\u5ffd\u7136\u51b7\u7b11\u4e00\u58f0\uff0c\u4f38\u624b\u5728 \u97e9\u5fd7\u90a6\u80c1\u4e0b\u4e00\u70b9\uff0c\u70b9\u4e86\u4ed6\u7684\u9ebb\u7729\u7a74\uff0c\u643a\u4e66\u957f\u5578\uff0c\u626c\u957f\u800c\u53bb\u3002\u97e9\u5fd7\u90a6\u540e\u6765\u9760\u5587\u561b\u89e3\u4e86\u7a74\u9053\uff0c\u95ee \u8d77\u6b64\u4eba\uff0c\u5168\u90fd\u4e0d\u8bc6\uff0c\u53ea\u77e5\u9053\u4ed6\u662f\u524d\u5929\u6765\u7684\u4e00\u4e2a\u9999\u5ba2\u3002 \u3000\u3000\u90a3\u8001\u8005\u4fbf\u662f\u6b64\u523b\u4e0e\u51cc\u672a\u98ce\u5bf9\u638c\u7684\u602a\u4eba\uff0c\u4ed6\u8bf4\u662f\u5143\u4f4f\u7985\u5e08\u7684\u7b2c\u56db\u5341\u4e8c\u4ee3\u4f20\u4eba\uff0c\u5012\u662f\u4e0d\u5047\u3002 \u539f\u6765\u4ed6\u540d\u53eb\u8f9b\u9f99\u5b50\uff0c\u4e43\u662f\u6666\u660e\u7985\u5e08\u597d\u53cb\u3001\u6b66\u5f53\u540d\u5bbf\u5353\u4e00\u822a\u7684\u5f1f\u5b50\uff0c\u4ed6\u5165\u95e8\u5728\u6842\u4ef2\u660e\u7684\u7236\u4eb2\u77f3 \u5929\u6210\u4e4b\u524d\uff0c\u4f46\u77f3\u5929\u6210\u662f\u5e26\u827a\u6295\u5e08\uff0c\u5e74\u9f84\u4e5f\u6bd4\u4ed6\u5927\uff0c\u56e0\u6b64\u5353\u4e00\u822a\u8981\u4ed6\u53eb\u77f3\u5929\u6210\u4e3a\u5e08\u5144\uff0c\u6b66\u5f53\u6d3e \u662f\u4ece\u5c11\u6797\u5206\u51fa\u6765\u7684\uff0c\u5c11\u6797\u6d3e\u7684\u7956\u5e08\u662f\u5357\u5317\u671d\u6881\u6b66\u5e1d\u7684\u65f6\u5019\uff0c\u81ea\u5370\u6765\u534e\u7684\u9ad8\u618e\u8fbe\u6469\u7985\u5e08\uff0c\u97e9\u5fd7 \u90a6\u6240\u83b7\u7684\u90a3\u672c\u4e66\u4fbf\u662f\u6b66\u5b66\u4e2d\u8457\u540d\u7684\u201c\u8fbe\u6469\u4e00\u767e\u96f6\u516b\u5f0f\u201d\u771f\u672c\uff08\u4f5c\u8005\u6ce8\uff1a\u6839\u636e\u6b63\u53f2\uff0c\u8fbe\u6469\u672c\u6765 \u4e0d\u4f1a\u6b66\u529f\uff0c\u76f8\u4f20\u662f\u8fbe\u6469\u6240\u8457\u7684\u201c\u6613\u7b4b\u7ecf\u201d\u548c\u201c\u6d17\u9ad3\u7ecf\u4e5f\u90fd\u662f\u540e\u4eba\u4f2a\u4f5c\u3002\u4f46\u6b66\u4fa0\u5c0f\u8bf4\u4f3c\u4e4e\u7528\u4e0d \u7740\u90a3\u4e48\u8ba4\u771f\u8003\u8bc1\uff0c\u5f53\u88e8\u5b98\u91ce\u53f2\u770b\u53ef\u4e5f\uff09\u3002\u8fd9\u90e8\u771f\u672c\u81ea\u5143\u4ee3\u4e2d\u53f6\u8d77\u5ffd\u7136\u4e0d\u89c1\uff0c\u5c11\u6797\u6b66\u5f53\u4e24\u6d3e\u95e8 \u4eba\u56db\u89c5\u65e0\u8e2a\uff0c\u4e8e\u662f\u4ee3\u4ee3\u4f20\u4e0b\u9057\u8a00\uff0c\u8981\u540e\u4e16\u5f1f\u5b50\u5bfb\u89c5\u6b64\u4e66\uff0c\u540c\u65f6\u8fd9\u4e00\u767e\u96f6\u516b\u5f0f\u771f\u672c\u867d\u7136\u5931\u8e2a\uff0c \u4f46\u5c11\u6797\u6b66\u5f53\u4e24\u6d3e\u5357\u5317\u5206\u652f\u540d\u5bbf\uff0c\u56e0\u6545\u8001\u76f8\u4f20\uff0c\u8fd8\u5927\u7565\u8bb0\u5f97\u51e0\u4e2a\u62db\u5f0f\u3002\u5353\u4e00\u822a\u81ea\u8fbe\u6469\u7985\u5e08\u7b97\u8d77 \u7b2c\u56db\u5341\u4e00\u4ee3\uff0c\u77f3\u5929\u6210\u5f53\u5e74\u6295\u4ed6\u95e8\u4e0b\uff0c\u56e0\u4e3a\u6025\u4e8e\u62a5\u4ec7\uff0c\u53ea\u5b66\u4e86\u201c\u4e5d\u5bab\u795e\u884c\u638c\u201d\u548c\u201c\u9e33\u9e2f\u8fde\u73af \u817f\u201d\u4e24\u6837\u7edd\u6280\uff0c\u4fbf\u8dd1\u56de\u56db\u5ddd\u53bb\u4e86\u3002\u56e0\u6b64\u8f9b\u9f99\u5b50\u867d\u662f\u4e8c\u5f92\u5f1f\uff0c\u5374\u662f\u5353\u4e00\u822a\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u77e5\u9053 \u201c\u602a\u4e66\u201d\u7684\u6765\u5386\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u90a3\u5e74\u4ece\u56de\u7586\u6765\u897f\u85cf\uff0c\u626e\u6210\u9999\u5ba2\uff0c\u53bb\u624e\u5e03\u4f26\u5bfa\u8fdb\u9999\uff0c\u672c\u6765\u4ed6\u542c\u8bf4\u624e\u5e03\u4f26\u5bfa\u7684\u5927\u5587\u561b \u7cbe\u4e8e\u897f\u85cf\u7684\u5929\u9f99\u638c\u6cd5\uff0c\u62db\u6570\u751a\u602a\uff0c\u56e0\u6b64\u60f3\u53bb\u7aa5\u63a2\u4e00\u4e0b\uff0c\u770b\u662f\u5426\u548c\u8fbe\u6469\u7684\u638c\u6cd5\u6709\u76f8\u901a\u4e4b\u5904\uff0c\u4e0d \u6599\u5374\u78b0\u89c1\u97e9\u5fd7\u90a6\u5348\u591c\u7ec3\u638c\uff0c\u4ed6\u8ba4\u5f97\u6709\u4e09\u4e2a\u62db\u5f0f\uff0c\u6b63\u662f\u81ea\u5df1\u7684\u5e08\u7236\u5353\u4e00\u822a\u7559\u4e0b\u7684\u8fbe\u6469\u638c\u6cd5\uff0c\u5e08 \u7236\u4e34\u7ec8\u65f6\u8bf4\u8fc7\uff1a\u201c\u8fbe\u6469\u4e00\u767e\u96f6\u516b\u5f0f\u201d\uff0c\u5728\u6b66\u5f53\u5317\u652f\u4f20\u4e0b\u6765\u7684\u53ea\u6709\u4e94\u5f0f\uff0c\u867d\u7136\u8fd9\u51e0\u4e2a\u62db\u5f0f\u65e0\u6cd5 \u8fde\u7eed\u8fd0\u7528\uff0c\u4f46\u8fd8\u662f\u8981\u4ed6\u7cbe\u5fc3\u7814\u4e60\u3002\u56e0\u6b64\u4ed6\u4e00\u89c1\u97e9\u5fd7\u90a6\u64cd\u7ec3\u7684\u638c\u6cd5\uff0c\u7acb\u523b\u731c\u5230\u5c31\u662f\u8fbe\u6469\u7684\u771f \u4f20\uff0c\u5f53\u4e0b\u4fbf\u4ee5\u5feb\u5200\u65a9\u4e71\u9ebb\u7684\u624b\u6bb5\uff0c\u628a\u4e66\u9a97\u5230\u624b\u4e0a\u3002 \u3000\u3000\u518d\u8bf4\u8f9b\u9f99\u5b50\u88ab\u51cc\u672a\u98ce\u4e00\u53e3\u559d\u7834\u8fbe\u6469\u638c\u6cd5\u7684\u6765\u5386\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u731b\u7136\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u61c2\u5f97 \u4ec0\u4e48\uff1f\u90a3\u4e66\u672c\u6765\u5c31\u662f\u6211\u4eec\u9057\u5931\u7684\u4e1c\u897f\uff0c\u600e\u5bb9\u5916\u4eba\u62ff\u53bb\uff1f\u201d\u547c\u547c\u7684\u63a5\u8fde\u51e0\u8bb0\u602a\u62db\uff0c\u697c\u5934\u76d6\u9876\uff0c \u6376\u808b\u6363\u80f8\uff0c\u5207\u8109\u95e8\uff0c\u6309\u7a74\u9053\uff0c\u5ffd\u62f3\u5ffd\u638c\uff0c\u5ffd\u5288\u5ffd\u6233\uff0c\u62f3\u6cd5\u638c\u6cd5\u70b9\u7a74\u6cd5\uff0c\u7eb7\u7136\u6742\u9648\uff0c\u770b\u6765\u4f3c\u5168 \u65e0\u7ae0\u6cd5\uff0c\u4f46\u5982\u6781\u96be\u5e94\u4ed8\u3002\u51cc\u672a\u98ce\u4ed7\u7740\u529f\u529b\u6df1\u6e5b\uff0c\u95ed\u4e86\u5168\u8eab\u7a74\u9053\uff0c\u7528\u5929\u5c71\u638c\u6cd5\u4e2d\u7684\u201c\u987b\u5f25 \u638c\u201d\uff0c\u5e26\u653b\u5e26\u5b88\uff0c\u53c8\u6321\u4e86\u4ed6\u4e8c\u4e09\u5341\u62db\uff0c\u5140\u662f\u65e0\u6cd5\u5360\u5230\u4fbf\u5b9c\u3002\u51cc\u672a\u98ce\u5fc3\u60f3\uff1a\u5929\u5c71\u638c\u6cd5\u5251\u6cd5\uff0c\u662f \u5e08\u7236\u91c7\u96c6\u5404\u5bb6\u4e4b\u957f\u6240\u521b\u51fa\u6765\u7684\uff0c\u4ed6\u8fd9\u638c\u6cd5\u867d\u7136\u8def\u9053\u751a\u602a\uff0c\u4f46\u603b\u4e0d\u81f3\u4e8e\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\u5176\u4e2d\u7684\u53d8 \u5316\u8d8b\u52bf\uff0c\u4ed6\u7709\u5934\u4e00\u76b1\uff0c\u5ffd\u5730\u638c\u6cd5\u4e00\u6162\uff0c\u53ea\u6c42\u81ea\u4fdd\uff0c\u4e0d\u6c42\u8fdb\u653b\uff0c\u8f9b\u9f99\u5b50\u5927\u559c\uff0c\u5982\u9189\u5982\u72c2\u4e00\u6837\u4e71 \u6253\u8fc7\u6765\uff0c\u4f46\u51cc\u672a\u98ce\u638c\u6cd5\u867d\u6162\uff0c\u6bcf\u4e00\u62db\u90fd\u8fd0\u8db3\u529f\u529b\uff0c\u638c\u98ce\u6fc0\u8361\uff0c\u6709\u5982\u91d1\u5203\u631f\u98ce\u3002\u8f9b\u9f99\u5b50\u548c\u4ed6\u78b0 \u4e86\u51e0\u6b21\uff0c\u53cc\u65b9\u90fd\u7ed9\u638c\u529b\u9707\u5f00\uff0c\u867d\u7136\u6ca1\u6709\u53d7\u4f24\uff0c\u4e5f\u5404\u81ea\u60ca\u5f02\u3002\u8f9b\u9f99\u5b50\u6709\u4e09\u5341\u591a\u5e74\u529f\u529b\uff0c\u548c\u51cc\u672a \u98ce\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u4f46\u529f\u529b\u6089\u654c\u7684\u5bf9\u624b\uff0c\u653b\u65b9\u6240\u7528\u7684\u529b\u5ea6\uff0c\u8981\u6bd4\u5b88\u65b9\u4e3a\u5927\uff0c\u51cc\u672a\u98ce\u53ea\u5b88\u4e0d\u653b\uff0c\u65e0 \u5f62\u4e2d\u5728\u6c14\u529b\u4e0a\u5360\u4e86\u4fbf\u5b9c\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u4e45\u6218\u4e0d\u4e0b\uff0c\u628a\u5fc3\u4e00\u6a2a\uff0c\u5c06\u8fbe\u6469\u4e00\u767e\u96f6\u516b\u5f0f\u5168\u90e8\u65bd\u5c55\u51fa\u6765\uff0c\u602a\u62db\u8fde\u63a5\u4e0d\u65ad\uff0c\u5982\u6ce2\u7ffb \u6d9b\u6d8c\uff0c\u5484\u5484\u8feb\u4eba\uff0c\u51cc\u672a\u98ce\u4ecd\u662f\u795e\u8272\u4e0d\u53d8\uff0c\u51b7\u9759\u5e94\u4ed8\uff0c\u62c6\u5230\u4e94\u516d\u5341\u62db\u5de6\u53f3\uff0c\u51cc\u672a\u98ce\u7adf\u7ed9\u4ed6\u70b9\u4e86 \u4e24\u5904\u7a74\u9053\uff0c\u5e78\u597d\u65e9\u6709\u9632\u5907\uff0c\u65e9\u5df2\u95ed\u7a74\uff0c\u5f97\u4ee5\u4e0d\u4f24\u3002\u6842\u4ef2\u660e\u5728\u65c1\u770b\u5f97\u5927\u4e3a\u7126\u6025\uff0c\u817e\u86df\u5251\u5237\u7684\u4e00 \u6307\uff0c\u6b63\u5f85\u4e0a\u524d\uff0c\u51cc\u672a\u98ce\u5ffd\u7136\u559d\u9053\uff1a\u201c\u4ef2\u660e\uff0c\u4f60\u4e0d\u8981\u6765\uff0c\u4ed6\u4e0d\u662f\u6211\u7684\u5bf9\u624b\uff01\u201d\u8bf4\u7f62\u638c\u6cd5\u66f4\u6162\uff0c \u4f46\u95e8\u6237\u5c01\u5f97\u66f4\u4e25\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u8fde\u8fde\u51b7\u7b11\uff0c\u638c\u6cd5\u4e4b\u4e2d\u53c8\u6742\u7740\u5200\u5251\u8def\u6570\uff0c\u628a\u4e00\u767e\u96f6\u516b\u5f0f\u51e0\u4e4e\u5168\u7528\u4e0a\u4e86\uff0c\u4ecd\u6253\u4e0d\u5012\u51cc \u672a\u98ce\u3002\u4f46\u89c1\u51cc\u672a\u98ce\u7684\u811a\u6b65\u5374\u662f\u6e10\u663e\u8fdf\u6ede\u3002\u8f9b\u9f99\u5b50\u5927\u559c\uff0c\u5fc3\u9053\uff1a\u6211\u7b2c\u4e00\u904d\u626b\u4f60\u4e0d\u5012\uff0c\u518d\u4f7f\u4e00 \u904d\uff0c\u8c05\u4f60\u62b5\u654c\u4e0d\u4f4f\u3002\u638c\u6cd5\u8d8a\u4f7f\u8d8a\u75af\u72c2\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u628a\u4e00\u767e\u96f6\u516b\u5f0f\u4f7f\u5b8c\u5168\uff0c\u6b63\u7b49\u4ece\u5934\u6765\u8fc7\uff0c\u51cc \u672a\u98ce\u5ffd\u7136\u5927\u559d\u4e00\u58f0\uff1a\u201c\u770b\u6211\u7684\uff01\u201d\u98d5\u98d5\u98d5\uff0c\u53cc\u638c\u7ffb\u98de\uff0c\u500f\u5730\u6492\u5f00\u52bf\u5b50\uff0c\u731b\u5982\u96c4\u72ee\uff0c\u6377\u82e5\u7075 \u733f\uff0c\u4e00\u6d3e\u51f6\u731b\u72b7\u5389\uff0c\u624b\u811a\u8d77\u5904\uff0c\u91d1\u5e26\u52b2\u98ce\uff0c\u8f9b\u9f99\u5b50\u521a\u60f3\u4ece\u5934\u6362\u638c\uff0c\u7ed9\u4ed6\u4e00\u9635\u5f3a\u653b\uff0c\u88ab\u8feb\u5012\u9000 \u51e0\u6b65\u3002\u8f9b\u9f99\u5b50\u5927\u4e3a\u60ca\u5f02\uff0c\u8d81\u51cc\u672a\u98ce\u62a2\u653b\u4e4b\u9645\uff0c\u5c55\u5f00\u602a\u5f02\u8eab\u6cd5\uff0c\u53cd\u6251\u4ed6\u7684\u7a7a\u95e8\u3002\u62f3\u5bb6\u6709\u4e91\uff1a \u201c\u654c\u4e0d\u52a8\uff0c\u5df1\u4e0d\u52a8\uff0c\u654c\u4e00\u52a8\uff0c\u5df1\u5148\u52a8\u3002\u201d\u8bb2\u7a76\u7684\u4fbf\u662f\u201c\u5236\u654c\u673a\u5148\u201d\u7684\u5965\u5999\uff0c\u56e0\u4e3a\u654c\u4e00\u52a8\uff0c\u5fc5 \u662f\u5411\u5df1\u65b9\u67d0\u4e00\u70b9\u8fdb\u653b\uff0c\u4ed6\u7684\u5168\u90e8\u7cbe\u795e\uff0c\u5c31\u96c6\u4e2d\u5728\u4e00\u70b9\u4e0a\uff0c\u82e5\u81ea\u5df1\u6bd4\u4ed6\u51fa\u624b\u66f4\u5feb\uff0c\u907f\u5f00\u4e86\u4ed6\u7684 \u653b\u51fb\u70b9\uff0c\u4fbf\u53ef\u4ee5\u653b\u4eba\u4ed6\u7684\u7a7a\u95e8\uff0c\u201c\u8fbe\u6469\u4e00\u767e\u96f6\u516b\u5f0f\u201d\u5168\u90e8\u7684\u7cbe\u534e\uff0c\u5c31\u662f\u6559\u4eba\u600e\u6837\u653b\u51fb\u654c\u4eba\u7684 \u5f31\u70b9\uff0c\u4ee5\u53d8\u5316\u590d\u6742\u7684\u6b65\u6cd5\u624b\u6cd5\u3002\u4f7f\u654c\u4eba\u4e0d\u77e5\u4ece\u4f55\u65b9\u9632\u5fa1\u3002\u6240\u4ee5\u5e38\u80fd\u4ee5\u5f31\u80dc\u5f3a\uff0c\u751a\u81f3\u5982\u97e9\u5fd7\u90a6 \u8fd9\u6837\u529f\u529b\u751a\u4f4e\u7684\u4eba\uff0c\u4e5f\u53ef\u62d4\u6389\u9f50\u771f\u541b\u7684\u80e1\u5b50\u3002\u56e0\u6b64\u8f9b\u9f99\u5b50\u89c1\u51cc\u672a\u98ce\u731b\u70c8\u653b\u51fb\uff0c\u867d\u7136\u5403\u4e86\u4e00 \u60ca\uff0c\u53ef\u662f\u968f\u5373\u5c31\u9547\u5b9a\u4e0b\u6765\uff0c\u60f3\u9053\uff1a\u4f60\u8fd9\u4e00\u653b\uff0c\u7a7a\u95e8\u56db\u9732\uff0c\u5982\u4f55\u6321\u5f97\u6211\u7684\u602a\u62db\uff1f \u3000\u3000\u4e0d\u6599\u51cc\u672a\u98ce\u4e0d\u4f46\u6321\u5f97\u4e86\u4ed6\u7684\u602a\u62db\uff0c\u800c\u4e14\u8f9b\u9f99\u5b50\u6bcf\u4e00\u51fa\u624b\uff0c\u90fd\u611f\u53d7\u5230\u7275\u5236\uff0c\u4e0e\u4ee5\u524d\u5927\u5927\u4e0d \u540c\u3002\u51cc\u672a\u98ce\u8eab\u6cd5\u5c55\u5f00\uff0c\u500f\u8fdb\u500f\u9000\uff0c\u5ffd\u5b88\u5ffd\u653b\uff0c\u6070\u5982\u884c\u4e91\u6d41\u6c34\uff0c\u6325\u6d12\u81ea\u5982\uff0c\u771f\u4e2a\u662f\u9759\u5982\u5c71\u5cb3\uff0c \u52a8\u82e5\u6c5f\u6cb3\uff01\u8f9b\u9f99\u5b50\u60f3\u653b\u4ed6\u7684\u7a7a\u95e8\uff0c\u638c\u672a\u5230\uff0c\u800c\u4ed6\u5df2\u5148\u8fce\u4e0a\u6765\uff0c\u7adf\u597d\u50cf\u719f\u6089\u4e86\u4ed6\u7684\u602a\u62db\uff0c\u9884\u77e5 \u4ed6\u7684\u51fa\u624b\u4e00\u6837\uff01 \u3000\u3000\u4f60\u9053\u51cc\u672a\u98ce\u4f55\u4ee5\u4e00\u4e0b\u5b50\u4f1a\u53cd\u5f31\u4e3a\u5f3a\uff0c\u626d\u8f6c\u5f62\u52bf\uff1f\u539f\u6765\u4ed6\u521a\u624d\u7684\u6b7b\u5b88\uff0c\u6b63\u662f\u5b58\u5fc3\u8981\u770b\u8f9b\u9f99 \u5b50\u7684\u5168\u90e8\u62db\u6570\u3002\u6f5c\u5fc3\u7ec6\u5bdf\u4e4b\u4e0b\uff0c\u53d1\u73b0\u8f9b\u9f99\u5b50\u7684\u57fa\u672c\u6b65\u6cd5\u662f\u6b66\u5f53\u6d3e\u7684\uff0c\u53c8\u53d1\u73b0\u4ed6\u7684\u602a\u62db\uff0c\u867d\u7136 \u6781\u4e3a\u5389\u5bb3\uff0c\u4f46\u5374\u597d\u4f3c\u5e76\u4e0d\u5341\u5206\u7eaf\u719f\uff0c\u5728\u7ec6\u5fae\u4e4b\u5904\uff0c\u53d8\u5316\u5e76\u4e0d\u81ea\u5982\uff0c\u6599\u77e5\u4ed6\u5077\u4e66\u4e4b\u540e\u3001\u53ea\u6709\u4e00 \u5e74\u591a\u5de5\u592b\uff0c\u638c\u6cd5\u521a\u521a\u7ec3\u6210\uff0c\u8fd8\u4e0d\u80fd\u5fc3\u638c\u5408\u4e00\uff0c\u56e0\u6b64\u5728\u51fa\u624b\u653b\u51fb\u4e4b\u65f6\uff0c\u603b\u9732\u51fa\u4e00\u70b9\u75d5\u8ff9\uff0c\u4f8b\u5982 \u4ed6\u60f3\u8d70\u53f3\u7ffc\u504f\u950b\u6251\u653b\uff0c\u80a9\u5934\u5fc5\u5148\u5fae\u5fae\u53f3\u503e\uff0c\u5411\u5de6\u653b\u65f6\uff0c\u4e5f\u662f\u5982\u6b64\uff0c\u51cc\u672a\u98ce\u4e43\u662f\u4e00\u4e2a\u5927\u884c\u5bb6\uff0c \u628a\u4ed6\u7684\u8def\u9053\u6478\u719f\u4e4b\u540e\uff0c\u4e8e\u662f\u7740\u7740\u53cd\u5236\u673a\u5148\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u8f9b\u9f99\u5b50\u8fd8\u6709\u4e00\u70b9\u5403\u4e8f\u7684\u5730\u65b9\uff0c\u51cc\u672a\u98ce\u5e76\u4e0d\u77e5\u9053\u3002\u539f\u6765\u201c\u8fbe\u6469\u4e00\u767e\u96f6\u516b\u5f0f\u201d\uff0c\u624e\u6839 \u57fa\u7684\u529f\u592b\u662f\u201c\u4e5d\u56fe\u516d\u5750\u50cf\u201d\uff0c\u5373\u97e9\u5fd7\u90a6\u5728\u9f99\u5c97\u77f3\u7a9f\u4e2d\u6240\u89c1\u7684\uff0c\u753b\u56fe\u4e2d\u6700\u524d\u9762\u7684\u516d\u79cd\u6253\u5750\u6cd5\uff0c \u5f53\u65f6\u97e9\u5fd7\u90a6\u6ca1\u6709\u5b66\uff0c\u800c\u8f9b\u9f99\u5b50\u5374\u65e0\u6cd5\u5b66\uff08\u56e0\u602a\u4e66\u4e2d\u53ea\u6709\u8bf4\u660e\u800c\u65e0\u753b\u56fe\uff0c\u624e\u6839\u57fa\u7684\u529f\u592b\u662f\u6700\u7cbe \u5fae\u7684\u529f\u592b\uff0c\u65e0\u6cd5\u610f\u4f1a\uff09\uff0c\u56e0\u6b64\u8fbe\u6469\u4e00\u767e\u96f6\u516b\u5f0f\u867d\u7136\u7ec3\u6210\uff0c\u5374\u603b\u6b20\u7f3a\u4e00\u70b9\u706b\u5019\uff0c\u78b0\u5230\u6b66\u529f\u6781\u9ad8 \u7684\u4eba\uff0c\u5c31\u88ab\u770b\u51fa\u6765\u4e86\u3002 \u3000\u3000\u653b\u5b88\u52bf\u6613\uff0c\u4e24\u4eba\u53c8\u62c6\u4e86\u4e00\u767e\u6765\u62db\uff0c\u65c1\u89c2\u7684\u4eba\u770b\u5f97\u773c\u82b1\u64a9\u4e71\uff0c\u53ea\u89c1\u4e24\u4eba\u5ffd\u5206\u5ffd\u5408\uff0c\u6253\u5230\u75be \u5904\uff0c\u72b9\u5982\u4e24\u56e2\u767d\u5f71\uff0c\u6253\u5230\u6162\u5904\uff0c\u5374\u53c8\u50cf\u540c\u95e8\u62c6\u62db\uff0c\u8fde\u6842\u4ef2\u660e\u6b66\u529f\u90a3\u4e48\u9ad8\u7684\u4eba\uff0c\u4e5f\u4e0d\u77e5\u9053\u51cc\u672a \u98ce\u5df2\u7a0d\u5fae\u5360\u4e86\u4f18\u52bf\u3002\u5ffd\u7136\u95f4\uff0c\u731b\u542c\u5f97\u51cc\u672a\u98ce\u5927\u559d\u4e00\u58f0\uff0c\u8f9b\u9f99\u5b50\u8eab\u5b50\u98de\u63a0\u51fa\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u4e00\u638c \u6362\u4e24\u6307\uff0c\u5f7c\u6b64\u90fd\u6ca1\u5403\u4e8f\uff01\u9752\u5c71\u5e38\u5728\uff0c\u7eff\u6c34\u5e38\u6d41\uff0c\u540e\u4f1a\u6709\u671f\uff0c\u6b20\u966a\u6b20\u966a\uff01\u201d\u8eab\u5f62\u518d\u8d77\uff0c\u7fe9\u5982\u5de8 \u9e64\uff0c\u4ece\u82b1\u4e1b\u4e0a\u63a0\u8fc7\uff0c\u51cc\u672a\u98ce\u53f1\u54a4\u4e00\u58f0\uff0c\u5929\u5c71\u795e\u8292\u7535\u5c04\u800c\u51fa\uff0c\u8f9b\u9f99\u5b50\u534a\u7a7a\u6253\u4e2a\u7b4b\u6597\uff0c\u8eab\u5b50\u4f3c\u6d41 \u661f\u6b92\u77f3\u822c\u5411\u5c71\u4e0b\u843d\u53bb\u3002 \u3000\u3000\u51cc\u672a\u98ce\u4e00\u63a0\u800c\u524d\uff0c\u5728\u82b1\u4e1b\u4e2d\u6765\u4e0b\u4e00\u6735\u7897\u5927\u7684\u767d\u82b1\uff0c\u4ea4\u7ed9\u5f20\u534e\u8bcf\u9053\uff1a\u201c\u4f60\u597d\u597d\u6536\u85cf\uff0c\u5bf9\u4f60 \u4e5f\u8bb8\u5f88\u6709\u7528\u5904\u3002\u201d\u5f20\u534e\u662d\u5c06\u521a\u624d\u6240\u6458\u7684\u5927\u7ea2\u82b1\u53d6\u51fa\uff0c\u4e0e\u767d\u82b1\u653e\u5728\u4e00\u5904\uff0c\u7ea2\u82b1\u5982\u706b\uff0c\u767d\u82b1\u80dc \u96ea\uff0c\u6e05\u9999\u6c81\u4eba\uff0c\u5c3d\u6da4\u70e6\u8651\uff0c\u5f20\u534e\u662d\u7b11\u9053\uff1a\u201c\u8fd9\u4e24\u6735\u82b1\u53ef\u7231\u6781\u4e86\uff0c\u4f46\u4e0d\u77e5\u8fd8\u6709\u4ec0\u4e48\u7528\u5904\uff0c\u8981\u8bf7 \u51cc\u5927\u4fa0\u6307\u6559\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u73b0\u5728\u8fd8\u5f88\u96be\u8bf4\uff0c\u7b49\u6211\u89c1\u5e08\u7236\u4e4b\u540e\u518d\u95ee\uff0c\u6211\u4e5f\u62ff\u4e0d\u5b9a\u662f\u5426\u5c31\u662f\u8fd9 \u4e24\u6735\u82b1\u3002\u201d\u5f20\u534e\u662d\u542c\u5f97\u8bdd\u4e2d\u6709\u8bdd\uff0c\u751a\u4e3a\u7591\u60d1\uff0c\u4f46\u51cc\u672a\u98ce\u4e0d\u8bf4\uff0c\u4ed6\u4e5f\u4e0d\u4fbf\u518d\u95ee\uff0c\u5fc3\u60f3\uff1a\u201c\u4e0d\u7ba1 \u5b83\u6709\u6ca1\u6709\u7528\u5904\uff0c\u62ff\u7ed9\u6613\u5170\u73e0\u770b\uff0c\u5979\u4e00\u5b9a\u975e\u5e38\u559c\u6b22\u3002\u201d \u3000\u3000\u6842\u4ef2\u660e\u72ec\u81ea\u7ad9\u5728\u5c71\u8fb9\u51dd\u671b\uff0c\u8f9b\u9f99\u5b50\u7684\u8eab\u5f71\u5df2\u6c93\u7136\u4e0d\u89c1\u3002\u6842\u4ef2\u660e\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u6562 \u60c5\u4ed6\u771f\u662f\u6211\u7684\u5e08\u53d4\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8c01\u8bf4\u4e0d\u662f\uff1f\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u4ed6\u5230\u5e95\u662f\u574f\u4eba\u8fd8\u662f\u597d\u4eba\uff1f\u201d \u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u5440\uff01\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u90a3\u4f60\u5728\u4ed6\u8d25\u9003\u4e4b\u65f6\uff0c\u8fd8\u7528\u795e\u8292\u6253\u4ed6\u505a\u4ec0 \u4e48\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u4e0d\u8bb8\u4ed6\u91c7\u8fd9\u6735\u767d\u82b1\uff01\u201d\u987f\u4e86\u4e00\u987f\u53c8\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u66ff\u4ed6\u62c5\u5fc3\uff0c\u4ed6\u7684\u6b66\u529f \u6781\u9ad8\uff0c\u4e0d\u4f1a\u8dcc\u6b7b\u7684\uff0c\u6211\u7684\u795e\u8292\u4e5f\u5e76\u672a\u6253\u4e2d\u4ed6\uff0c\u53ea\u662f\u628a\u4ed6\u5413\u8d70\u800c\u5df2\u3002\u8fd9\u6b21\u5bf9\u638c\uff0c\u5e78\u5728\u4ed6\u5077\u6765\u7684 \u602a\u62db\uff0c\u8fd8\u672a\u7ec3\u5230\u7089\u706b\u7eaf\u9752\uff0c\u5426\u5219\u6211\u4e5f\u96be\u4e8e\u5bf9\u4ed8\u3002\u201d\u5192\u6d63\u83b2\u53c8\u95ee\u9053\uff1a\u201c\u4ed6\u8bf4\u7684\u4e24\u6307\u6362\u4e00\u638c\u662f\u4ec0 \u4e48\u610f\u601d\uff1f\u201d\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u6211\u88ab\u70b9\u4e2d\u4e24\u5904\u7a74\u9053\uff0c\u4ed6\u4e5f\u7ed9\u6211\u7528\u5927\u6454\u7891\u624b\u5288\u4e86\u4e00\u638c\uff0c\u4f60\u4eec\u770b\u4e0d\u51fa \u6765\u4e48\uff1f\u8fd9\u6b21\u662f\u6253\u4e2a\u5e73\u624b\uff0c\u4e0b\u6b21\u518d\u6253\uff0c\u4ed6\u5c31\u6ca1\u6709\u4fbf\u5b9c\u53ef\u5360\u4e86\uff01\u201d \u3000\u3000\u4e00\u884c\u4eba\u8bf4\u8bf4\u7b11\u7b11\uff0c\u7ffb\u8fc7\u9a86\u9a7c\u5cf0\u53c8\u5411\u5929\u5c71\u7edd\u9876\u884c\u8fdb\u3002\u5230\u4e86\u7b2c\u4e09\u5929\uff0c\u5317\u9ad8\u5cf0\u5df2\u9b4f\u7136\u5728\u671b\uff0c\u53ea \u89c1\u90a3\u5ea7\u9ad8\u5cf0\u5982\u5de8\u7b14\u822c\u77d7\u7acb\u5728\u4e91\u6d77\u4e2d\uff0c\u6735\u6735\u767d\u4e91\u5728\u5c71\u9876\u5ce1\u8c37\u95ee\u98d8\u6d6e\uff0c\u771f\u50cf\u6210\u7fa4\u7684\u7f8a\u5728\u8349\u5730\u4e0a\u5403 \u8349\u3002\u56db\u4eba\u518d\u884c\u534a\u65e5\uff0c\u9ec4\u660f\u65f6\u5206\uff0c\u6500\u4e0a\u5cf0\u9876\u3002 \u3000\u3000\u5c71\u9876\u4e0a\u8c41\u7136\u5f00\u6717\uff0c\u5947\u82b1\u5f02\u8349\uff0c\u904d\u5730\u90fd\u662f\uff0c\u5192\u6d63\u83b2\u5947\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5728\u5929\u5c71\u7edd\u9876\uff0c\u8fd8\u6709\u82b1 \u8349\uff01\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd9\u4e9b\u82b1\u8349\u90fd\u662f\u60ef\u8010\u971c\u96ea\u7684\u4e86\uff0c\u5728\u4e94\u516d\u6708\u95f4\uff0c\u96ea\u4e2d\u8fd8\u5f00\u51fa\u82b1\u6765\u5462\uff01\u5929\u5c71\u7edd \u9876\uff0c\u82b1\u8349\u53cd\u800c\u5bb9\u6613\u751f\u957f\uff0c\u4f60\u77e5\u662f\u4ec0\u4e48\u9053\u7406\u5417\uff1f\u201d\u8bf4\u7f62\u5411\u4e0b\u4e00\u6307\uff0c\u5728\u5317\u9ad8\u5cf0\u7a0d\u4f4e\u5904\uff0c\u6709\u4e00\u4e2a\u5c0f \u6e56\uff0c\u6e56\u5149\u4e91\u5f71\uff0c\u666f\u8272\u6e05\u7edd\u3002\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd9\u4fbf\u662f\u8457\u540d\u7684\u5929\u6c60\u4e86\uff01\u542c\u5e08\u7236\u8bf4\uff0c\u90a3\u91cc\u539f\u662f\u4e2a\u706b\u5c71 \u53e3\uff0c\u706b\u5c71\u6b7b\u4e86\uff0c\u5316\u4e3a\u6e56\u6cca\uff0c\u5927\u6c14\u5374\u662f\u6696\u7684\uff0c\u82b1\u8349\u5728\u6b7b\u706b\u5c71\u53e3\u65c1\u8fb9\uff0c\u53c8\u6709\u6e56\u6c34\u6ecb\u6da6\uff0c\u81ea\u7136\u5bb9\u6613 \u751f\u957f\u4e86\u3002\u201d\u56db\u4eba\u8fb9\u8bf4\u8fb9\u884c\uff0c\u51cc\u672a\u98ce\u53c8\u5411\u524d\u6307\u9053\uff1a\u201c\u8fd9\u95f4\u77f3\u5c4b\uff0c\u4fbf\u662f\u6211\u5e08\u7236\u7684\u4f4f\u6240\u4e86\uff01\u201d\u6842\u4ef2 \u660e\u3001\u5f20\u534e\u662d\u7b49\u4e00\u9f50\u5782\u624b\u8083\u7acb\uff0c\u51cc\u672a\u98ce\u9053\uff1a\u201c\u65e6\u5f85\u6211\u5148\u8fdb\u53bb\u66ff\u4f60\u4eec\u901a\u62a5\u3002\u201d\u4e0a\u524d\u6572\u4e86\u51e0\u4e0b\u77f3 \u95e8\uff0c\u5165\u95e8\u5f00\u5904\uff0c\u8d70\u51fa\u4e00\u4e2a\u50e7\u4eba\uff0c\u559c\u9053\uff1a\u201c\u672a\u98ce\uff0c\u4f60\u56de\u6765\u4e86\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u609f\u6027\u5e08\u5144\uff0c\u4f60 \u597d\uff0c\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u597d\u5417\uff1f\u201d\u609f\u6027\u662f\u670d\u4f8d\u6666\u660e\u7985\u5e08\u7684\u9999\u706b\u50e7\u4eba\uff0c\u5374\u5e76\u975e\u5165\u5ba4\u5f1f\u5b50\uff0c\u51cc\u672a\u98ce\u56e0\u4ed6 \u5148\u81ea\u5df1\u4e0a\u5c71\uff0c\u6240\u4ee5\u5c0a\u4ed6\u4e3a\u5e08\u5144\u3002\u609f\u6027\u6447\u4e86\u6447\u5934\uff0c\u51cc\u672a\u98ce\u5927\u6025\uff0c\u95ee\u9053\uff1a\u201c\u5e08\u7236\u4e91\u6e38\u8d70\u4e86\uff01\u201d\u609f \u6027\u9053\uff1a\u201c\u5e08\u7236\u6b63\u5750\u5173\u5462\uff01\u201d\u201c\u5750\u5173\u201d\u5c31\u662f\u8f83\u957f\u65f6\u95f4\u7684\u6253\u5750\u3002\u6666\u660e\u7985\u5e08\u5df2\u6709\u4e00\u767e\u4e00\u5341\u4e8c\u5c81\uff0c\u4ed6 \u8fc7\u4e86\u767e\u5c81\u4e4b\u540e\uff0c\u7ecf\u5e38\u4e00\u6253\u5750\u5c31\u662f\u4e24\u4e09\u5929\uff0c\u5728\u6253\u5750\u7684\u65f6\u95f4\uff0c\u5bf9\u4e00\u5207\u90fd\u662f\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\uff0c \u5f53\u7136\u66f4\u4e0d\u80fd\u63a5\u89c1\u5916\u4eba\u3002 \u3000\u3000\u51cc\u672a\u98ce\u95ee\u9053\uff1a\u201c\u5e08\u7236\u5750\u5173\u591a\u4e45\u4e86\uff1f\u201d\u609f\u6027\u9053\uff1a\u201c\u5927\u7ea6\u6709\u4e24\u5929\u4e86\u5427\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u5148 \u5230\u9759\u5ba4\u5916\u9762\u9065\u53c2\u3002\u4f60\u66ff\u6211\u62db\u5f85\u51e0\u4f4d\u670b\u53cb\u3002\u201d\u8bf4\u7f62\u8d70\u8fc7\u5f39\u5802\uff0c\u5230\u4e86\u897f\u9996\u4e00\u95f4\u9759\u5ba4\uff0c\u5ffd\u7136\u773c\u775b\u4e00 \u4eae\uff0c\u90a3\u5ba4\u95e8\u5e76\u4e0d\u5173\u95ed\uff0c\u5e08\u7236\u7aef\u5750\u5728\u6b63\u5ea7\u84b2\u56e2\u4e4b\u4e0a\uff0c\u5782\u9996\u95ed\u76ee\uff0c\u6148\u7965\u5982\u65e7\u3002\u84b2\u56e2\u4e0b\u5374\u8dea\u7740\u4e2a\u7ea2 \u8863\u5c11\u5973\uff0c\u4f3c\u5728\u4f4e\u58f0\u7980\u544a\uff0c\u51cc\u672a\u98ce\u5927\u4e3a\u5947\u602a\uff0c\u90a3\u5c11\u5973\u5ffd\u7136\u56de\u8fc7\u5934\u6765\uff0c\u9762\u8c8c\u7adf\u4f3c\u66fe\u76f8\u8bc6\uff0c\u4f46\u600e\u6837 \u4e5f\u60f3\u4e0d\u8d77\u662f\u54ea\u513f\u89c1\u8fc7\u7684\u3002\u5c11\u5973\u624b\u4e0a\u6301\u6709\u4e00\u5377\u4e1c\u897f\uff0c\u51cc\u672a\u98ce\u60f3\u8d77\u8f9b\u9f99\u5b50\u5077\u4e66\u4e4b\u4e8b\uff0c\u60f3\u9053\uff0c\u96be\u9053 \u5979\u8d81\u6211\u5e08\u7236\u5750\u5173\u4eba\u8d70\u4e4b\u65f6\uff0c\u6765\u8fd9\u91cc\u5077\u76d7\u62f3\u7ecf\u5251\u6cd5\uff1f\u4e8e\u662f\u53cc\u7738\u70af\u70af\uff0c\u770b\u5979\u600e\u6837\u3002\u90a3\u5c11\u5973\u89c1\u4e86\u51cc \u672a\u98ce\uff0c\u76c8\u76c8\u4e00\u7b11\uff0c\u884c\u4e86\u51fa\u6765\uff0c\u51cc\u672a\u98ce\u4e0d\u6562\u60ca\u52a8\u6666\u660e\u7985\u5e08\uff0c\u9000\u540e\u51e0\u6b65\u62e6\u5728\u752c\u9053\u4e0a\uff0c\u90a3\u5c11\u5973\u6084\u7136 \u5230\u4e86\u8eab\u8fb9\uff0c\u5ffd\u7136\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u8ba4\u6211\u8fc7\u53bb\u3002\u201d\u51cc\u672a\u98ce\u4e00\u6014\uff0c\u90a3\u5c11\u5973\u8eab\u5f62\u4e00\u62d4\uff0c\u4e5f\u4e0d\u89c1 \u5979\u600e\u4f6f\u4f5c\u52bf\uff0c\u8eab\u5b50\u5df2\u7ecf\u98d8\u98d8\u5730\u98de\u51fa\u5899\uff0c\u8fd9\u4efd\u8f7b\u8eab\u529f\u592b\uff0c\u7adf\u4f3c\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\u3002\u51cc\u672a\u98ce\u51db\u7136\u4e00 \u60ca\uff0c\u5ffd\u542c\u5f97\u6666\u660e\u7985\u5e08\u53eb\u9053\uff1a\u201c\u5f92\u513f\uff0c\u4f60\u8fdb\u6765\uff01\u201d \u3000\u3000\u8fd9\u7ea2\u8863\u5c11\u5973\uff0c\u4e0d\u4f46\u51cc\u672a\u98ce\u4e0d\u77e5\u5979\u662f\u8c01\uff0c\u8fde\u609f\u6027\u4e5f\u4e0d\u77e5\u9053\u5979\u5077\u5165\u7985\u5ba4\u3002\u5979\u6765\u5386\u5982\u4f55\uff0c\u540e\u6587 \u5f53\u518d\u4ea4\u4ee3\u3002\u4e14\u8bf4\u609f\u6027\u51fa\u4e86\u5bfa\u95e8\uff0c\u548c\u6842\u4ef2\u660e\u7b49\u89c1\u9762\uff0c\u7b49\u5f85\u51cc\u672a\u98ce\u53c2\u62dc\u56de\u6765\uff0c\u518d\u4f5c\u9053\u7406\uff08\u672a\u5f97\u6666 \u660e\u7985\u5e08\u5141\u8bb8\uff0c\u609f\u6027\u4e0d\u6562\u62db\u5f85\u5916\u4eba\u5165\u5bfa\uff09\u3002\u5176\u65f6\u9ec4\u660f\u65e5\u848b\uff0c\u665a\u971e\u4f59\u7eee\uff0c\u5929\u5c71\u7edd\u9876\uff0c\u9ad8\u5904\u4e0d\u80dc \u5bd2\u3002\u5192\u6d63\u83b2\u6709\u4e9b\u62b5\u53d7\u4e0d\u4f4f\u3002\u6842\u4ef2\u660e\u6b63\u5728\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u51fa\u6765\u5462\uff1f\u201d\u5ffd\u7136\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u95ee \u9053\uff1a\u201c\u6666\u660e\u8001\u7985\u5e08\u6536\u5973\u5f92\u5f1f\u7684\u4e48\uff1f\u201d\u609f\u6027\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u4e00\u4e2a\u7ea2\u8863\u5c11\u5973\u7684\u5f71\u5b50\u98d8\u7136\u7ecf\u8fc7 \u8eab\u65c1\uff0c\u609f\u6027\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d\u4ed6\u7edd\u60f3\u4e0d\u5230\u6709\u4eba\u8fd9\u6837\u5927\u80c6\uff0c\u6666\u660e\u7985\u5e08\u65b9\u5728\u5165\u5b9a\uff0c\u81ea\u5df1\u7adf\u653e\u5916\u4eba \u5165\u5185\uff0c\u8fd9\u628a\u5b88\u95e8\u6237\u4e0d\u4e25\u4e4b\u7f6a\uff0c\u53ef\u662f\u4e0d\u5c0f\u3002\u6842\u4ef2\u660e\u542c\u4ed6\u5927\u53eb\u201c\u4e0d\u597d\uff01\u201d\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u574f\u4eba \u5417\uff1f\u201d\u609f\u6027\u4e5f\u6709\u70b9\u50cf\u6842\u4ef2\u660e\uff0c\u90fd\u662f\u6206\u76f4\u7684\u6c49\u5b50\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u70b9\u4e86\u70b9\u5934\u3002\u6842\u4ef2\u660e\u628a\u624b\u4e00\u626c\uff0c\u4e09 \u679a\u91d1\u73af\u5206\u6253\u7ea2\u8863\u5c11\u5973\u7684\u7a74\u9053\u3002 \u3000\u3000\u7ea2\u8863\u5c11\u5973\u6b63\u5728\u4e0b\u5c71\uff0c\u8eab\u5f62\u98de\u5815\uff0c\u5176\u52bf\u751a\u5feb\uff0c\u542c\u5f97\u8111\u540e\u98ce\u58f0\uff0c\u53cd\u624b\u4e00\u6284\uff0c\u5f80\u659c\u4fa7\u4e00\u8dc3\uff0c\u811a \u6b65\u4e0d\u505c\uff0c\u5df2\u907f\u5f00\u4e24\u679a\uff0c\u63a5\u4e0b\u4e00\u679a\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u54ce\u54df\uff01\u8fd9\u6837\u9614\u6c14\uff0c\u9ec4\u6f84\u6f84\u7684\u91d1\u5b50\u90fd\u9001\u7ed9\u964c\u751f\u7684 \u4eba\uff0c\u5192\u59d0\u59d0\uff0c\u66ff\u6211\u591a\u8c22\u7f62\uff01\u201d\u5c71\u98ce\u5439\u9001\uff0c\u8bed\u58f0\u6e05\u6670\u3002\u5192\u6d63\u83b2\u5927\u53eb\u4e00\u58f0\uff0c\u4e5f\u60f3\u4e0d\u8d77\u5979\u662f\u8c01\u4eba\u3002 \u5f85\u53d1\u58f0\u76f8\u95ee\u65f6\uff0c\u5c71\u8170\u53ea\u89c1\u4e00\u4e2a\u7ea2\u70b9\uff0c\u518d\u8fc7\u7247\u523b\uff0c\u8fde\u7ea2\u70b9\u4e5f\u4e0d\u89c1\u4e86\uff0c\u5192\u6d63\u83b2\u9053\uff1a\u201c\u771f\u662f\u602a\u4e8b\uff0c \u5979\u600e\u8ba4\u8bc6\u6211\u5462\uff1f\u201d \u3000\u3000\u6b63\u662f\uff1a\u51b0\u96ea\u806a\u660e\u96be\u8bc6\u900f\uff0c\u7ea2\u8863\u5c11\u5973\u9690\u5929\u5c71\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5eff\u4e00\u7ae0\u3000\u60c5\u5b7d\u96be\u6d88\u3000\u72ec\u4e0a\u5929\u5c71\u62dc\u9b54\u5973\u3000\u5c18\u7f18\u672a\u65ad\u3000\u6a2a\u7a7f\u701a\u6d77\u89c5\u4f0a\u4eba \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u4e8c\u5341\u4e00\u56de \u60c5\u5b7d\u96be\u6d88\u3000\u72ec\u4e0a\u5929\u5c71\u62dc\u9b54\u5973 \u5c18\u7f18\u672a\u65ad\u3000\u6a2a\u7a7f\u701a\u6d77\u89c5\u4f0a\u4eba \u3000\u3000\u201c\u771f\u662f\u602a\u4e8b\uff0c\u5979\u600e\u8ba4\u8bc6\u6211\u5462\uff1f\u201d\u51cc\u672a\u98ce\u4e5f\u662f\u8fd9\u6837\u5730\u60f3\u3002\u4ed6\u8fdb\u4e86\u9759\u5ba4\uff0c\u53c2\u89c1\u5e08\u7236\u4e4b\u540e\uff0c\u7b80 \u7565\u5730\u62a5\u544a\u4e86\u4e0b\u5c71\u4e4b\u540e\u7684\u7ecf\u5386\u3002 \u3000\u3000\u6666\u660e\u7985\u5e08\u624b\u634b\u94f6\u987b\uff0c\u70b9\u5934\u8bf4\u9053\uff1a\u201c\u4f60\u5f88\u597d\uff0c\u4e0d\u8d1f\u6211\u4e00\u756a\u5fc3\u8840\uff01\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd8\u671b\u5e08\u7236 \u6559\u8bf2\u3002\u201d\u6666\u660e\u7985\u5e08\u95ee\u9053\uff1a\u201c\u4f60\u5df2\u89c1\u7740\u90a3\u7ea2\u8863\u5c11\u5973\u4e86\uff1f\u201d\u51cc\u672a\u98ce\u5e94\u4e86\u4e00\u58f0\u3002\u6666\u660e\u7985\u5e08\u9053\uff1a\u201c\u5979 \u662f\u767d\u53d1\u9b54\u5973\u7684\u5173\u95e8\u5f1f\u5b50\uff0c\u82e5\u5979\u5728\u5185\uff0c\u540c\u4f60\u4e00\u8f88\u5171\u6709\u4e03\u4eba\uff0c\u53ea\u4f59\u4e86\u77f3\u5929\u6210\u4e00\u4eba\u6ca1\u6709\u5b66\u5251\u3002\u5176\u4f59 \u516d\u4eba\u518d\u52a0\u4e0a\u6613\u5170\u73e0\uff0c\u4f60\u4eec\u4e03\u4eba\u5012\u53ef\u4ee5\u79f0\u4e3a\u5929\u5c71\u4e03\u5251\u5462\uff0c\u53ea\u53ef\u60dc\u4f60\u7684\u5e08\u5144\u65e9\u6b7b\uff0c\u9ab8\u9aa8\u4e5f\u6ca1\u6709\u8fd0 \u56de\uff01\u201d\u201c\u5929\u5c71\u4e03\u5251\u201d\u4e4b\u540d\u8fde\u51cc\u672a\u98ce\u4e5f\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u5230\uff0c\u6b63\u5c48\u6307\u7ec6\u6570\uff0c\u6666\u660e\u7985\u5e08\u9053\uff1a\u201c\u6211\u548c\u767d \u53d1\u9b54\u5973\u5206\u5c45\u5929\u5c71\u5357\u5317\u4e24\u9ad8\u5cf0\uff0c\u5353\u4e00\u822a\u5219\u5728\u5929\u5c71\u4e00\u5e26\u6e38\u4fa0\uff0c\u5c45\u65e0\u5b9a\u6240\u3002\u6211\u4eec\u4e09\u4eba\uff0c\u4f20\u4e0b\u7684\u5929\u5c71 \u4e03\u5251\uff0c\u53ea\u4f60\u5168\u90e8\u89c1\u8fc7\uff0c\u5176\u4ed6\u7684\u53ef\u6ca1\u8fd9\u798f\u4efd\u4e86\u3002\u201d\u51cc\u672a\u98ce\u4e00\u7b97\uff1a\u201c\u4e24\u4e2a\u5e08\u5144\u6768\u4e91\u9aa2\u548c\u695a\u662d\u5357\uff0c \u518d\u52a0\u4e0a\u81ea\u5df1\u53ca\u81ea\u5df1\u66ff\u5e08\u6388\u827a\u7684\u6613\u5170\u73e0\uff0c\u540c\u95e8\u7684\u5171\u662f\u56db\u4eba\uff0c\u767d\u53d1\u9b54\u5973\u4f20\u4e0b\u4e24\u4e2a\u5f92\u5f1f\uff1a\u98de\u7ea2\u5dfe\u4e0e \u9002\u624d\u6240\u89c1\u7684\u7ea2\u8863\u5c11\u5973\uff1b\u5353\u4e00\u822a\u4e5f\u4f20\u4e0b\u4e24\u4e2a\u5f92\u5f1f\uff0c\u77f3\u5929\u6210\u548c\u9a86\u9a7c\u5cf0\u7684\u90a3\u4e2a\u602a\u4eba\uff1b\u9664\u4e86\u77f3\u5929\u6210\u4e4b \u5916\uff0c\u679c\u7136\u662f\u4e03\u4e2a\u4eba\u3002\u201d\u4ed6\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6b63\u60f3\u5e08\u7236\u4f55\u4ee5\u77e5\u9053\u81ea\u5df1\u89c1\u8fc7\u5353\u4e00\u822a\u7684\u4e8c\u5f92\u5f1f\uff1f\uff08\u4ed6\u89c1\u8fc7 \u77f3\u5929\u6210\u4e4b\u4e8b\uff0c\u5728\u62a5\u544a\u4e0b\u5c71\u51e0\u5e74\u7684\u7ecf\u5386\u65f6\u5df2\u8bb2\u4e86\u51fa\u6765\u3002\uff09\u6666\u660e\u7985\u5e08\u5df2\u5148\u81ea\u7b11\u9053\uff1a\u201c\u95fb\u4f60\u8eab\u4e0a\u7684 \u9999\u6c14\uff0c\u60f3\u4f60\u5df2\u5230\u8fc7\u9a86\u9a7c\u5cf0\u4e86\uff0c\u8f9b\u9f99\u5b50\u813e\u6c14\u53e4\u602a\uff0c\u4f60\u4eec\u5927\u7ea6\u4ea4\u8fc7\u624b\u4e86\uff1f\u201d\u51cc\u672a\u98ce\u8fd9\u624d\u77e5\u9053\u90a3\u4e2a \u602a\u4eba\u53eb\u8f9b\u9f99\u5b50\uff0c\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8d77\u5148\u4e0d\u77e5\u9053\u4ed6\u5c31\u662f\u5353\u5e08\u53d4\u7684\u5f92\u5f1f\uff0c\u540e\u6765\u867d\u7136\u731c \u5230\uff0c\u4f46\u5df2\u6253\u5230\u9a91\u864e\u96be\u4e0b\u2026\u2026\u201d\u6666\u660e\u7985\u5e08\u622a\u65ad\u4ed6\u7684\u8bdd\u9053\uff1a\u201c\u4f60\u5e94\u4ed8\u5f97\u4e86\u4ed6\u7684\u602a\u62db\uff1f\u201d\u51cc\u672a\u98ce \u9053\uff1a\u201c\u4fa5\u5e78\u6253\u4e2a\u5e73\u624b\u3002\u201d\u6666\u660e\u7985\u5e08\u6c89\u541f\u534a\u664c\uff0c\u6168\u7136\u8bf4\u9053\uff1a\u201c\u4e03\u5251\u4e4b\u4e2d\uff0c\u6b63\u90aa\u90fd\u6709\uff0c\u4f60\u7684\u5927\u5e08 \u5144\u6700\u5f97\u6211\u5fc3\uff0c\u53ef\u60dc\u65e9\u6b7b\uff0c\u4f60\u7684\u4e8c\u5e08\u5144\u4e2d\u9014\u53d8\u8282\uff0c\u53ea\u6709\u671b\u4f60\u5c06\u6765\u6e05\u7406\u5e08\u95e8\u4e86\u3002\u8f9b\u9f99\u5b50\u4ecb\u4e4e\u90aa\u6b63 \u4e4b\u95f4\uff0c\u6211\u65e9\u5df2\u95ed\u95e8\u5c01\u5251\uff0c\u81ea\u53d1\u9b54\u5973\u4e0d\u613f\u7ba1\u4ed6\uff0c\u4e5f\u53ea\u6709\u671b\u4f60\u5c06\u6765\u628a\u4ed6\u6536\u670d\u4e86\u3002\u201d\u51cc\u672a\u98ce\u5fc3\u60f3\uff1a \u767d\u53d1\u9b54\u5973\u5ac9\u6076\u5982\u4ec7\uff0c\u4eba\u53c8\u597d\u80dc\uff0c\u8fde\u5e08\u7236\u5979\u4e5f\u8981\u4e24\u6b21\u627e\u6765\u6bd4\u8bd5\uff0c\u4e3a\u4f55\u5374\u5bb9\u5f97\u8f9b\u9f99\u5b50\u5728\u5929\u5c71\u6492 \u91ce\uff1f\u4f46\u4ed6\u77e5\u767d\u53d1\u9b54\u5973\u4e0e\u5e08\u7236\u9887\u6709\u82a5\u8482\uff0c\u4e0d\u6562\u53d1\u95ee\u3002 \u3000\u3000\u6666\u660e\u7985\u5e08\u5543\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u627f\u7ee7\u4f60\u5927\u5e08\u5144\u7684\u9057\u5fd7\uff0c\u603b\u7b97\u4e0d\u8fb1\u5e08\u95e8\u3002\u5929\u5c71\u5251\u6cd5\uff0c\u5168\u4ed7\u4f60\u628a\u5b83 \u53d1\u626c\u5149\u5927\u4e86\uff01\u201d\u51cc\u672a\u98ce\u5782\u624b\u542c\u8bad\uff0c\u6666\u660e\u7985\u5e08\u53c8\u9053\uff1a\u201c\u767d\u53d1\u9b54\u5973\u4e0e\u6211\u867d\u6709\u8fc7\u8282\uff0c\u6211\u5374\u5f88\u63a8\u91cd\u5979 \u7684\u6b66\u529f\u3002\u5979\u8fd9\u6b21\u6d3e\u5173\u95e8\u5f1f\u5b50\u6765\u89c1\u6211\uff0c\u5927\u7ea6\u8fd9\u6bb5\u8fc7\u8282\u4e5f\u53ef\u63ed\u8fc7\u4e86\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u539f\u6765\u90a3\u7ea2\u8863 \u5c11\u5973\u662f\u5979\u6d3e\u6765\u7684\uff0c\u4e0d\u77e5\u600e\u7684\u5374\u77e5\u9053\u5f1f\u5b50\u540d\u5b57\uff1f\u201d\u6666\u660e\u7985\u5e08\u9053\uff1a\u201c\u90a3\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d\u53f9\u4e86\u4e00 \u4e00\u58f0\u53c8\u9053\uff1a\u201c\u8272\u7a7a\u4e24\u5b57\uff0c\u771f\u96be\u52d8\u7834\uff0c\u6211\u4e5f\u6599\u4e0d\u5230\u767d\u53d1\u9b54\u5973\u5e74\u5c06\u8fd1\u767e\uff0c\u8fd8\u8bb0\u5f97\u5c11\u5e74\u4e8b\u60c5\uff0c\u5979\u6d3e \u4eba\u89c1\u6211\uff0c\u8981\u95ee\u4f60\u5353\u5e08\u53d4\u7684\u9057\u4e66\u3002\u201d\u51cc\u672a\u98ce\u6697\u6697\u79f0\u5947\uff0c\u5fc3\u60f3\uff1a\u83ab\u975e\u5979\u548c\u5353\u5e08\u53d4\u662f\u4e00\u5bf9\u5c11\u5e74\u60c5 \u4fa3\uff1f\u6666\u660e\u7985\u5e08\u53c8\u9053\uff1a\u201c\u4f60\u5353\u5e08\u53d4\u813e\u6c14\u4e5f\u5f88\u53e4\u602a\uff0c\u4ed6\u5230\u5929\u5c71\u51e0\u5341\u5e74\uff0c\u4ece\u672a\u5bf9\u6211\u8c08\u8fc7\u5c11\u5e74\u4e4b\u4e8b\uff0c \u4e34\u6b7b\u4e4b\u524d\uff0c\u5374\u5ffd\u7136\u7559\u4e0b\u4e00\u4e2a\u9526\u5323\u7ed9\u6211\uff0c\u8bf4\u9053\uff1a\u82e5\u6709\u4eba\u53d6\u5f97\u9a86\u9a7c\u5cf0\u4e0a\u90a3\u4e24\u6735\u2018\u4f18\u6619\u82b1\u2019\u524d\u6765\u89c1 \u4f60\uff0c\u4f60\u53ef\u5c06\u8fd9\u9526\u5323\u4ea4\u4ed6\u62ff\u53bb\u89c1\u767d\u53d1\u9b54\u5973\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u5fc3\u5ff5\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4e24\u6735\u4f18\u6619\u82b1\u662f\u4e0d\u662f\u4e00\u7ea2\u4e00\u767d\uff0c\u5927\u5982\u5de8\u7897\uff1f\u4f20\u8bf4\u516d\u5341\u5e74\u5f00\u82b1 \u4e00\u6b21\uff0c\u53ef\u4ee4\u767d\u53d1\u53d8\u9ed1\uff0c\u8fd4\u8001\u8fd8\u7ae5\uff1f\u201d\u6666\u660e\u7985\u5e08\u9053\uff1a\u201c\u6709\u6b64\u4e00\u8bf4\uff0c\u4e0d\u8fc7\u672a\u5fc5\u5982\u6b64\u7075\u6548\uff0c\u5927\u7ea6\u662f \u6bd4\u4f55\u9996\u4e4c\u66f4\u73cd\u8d35\u7684\u836f\u6750\u7f62\u4e86\u3002\u8fd9\u79cd\u82b1\u516d\u5341\u5e74\u624d\u5f00\u4e00\u6b21\uff0c\u6709\u8c01\u6709\u6b64\u8010\u5fc3\u5b88\u5019\uff1f\u800c\u4e14\u53c8\u4e0d\u662f\u4ec0\u4e48 \u4ed9\u4e39\uff0c\u7eb5\u6709\u5947\u4eba\u5f02\u58eb\uff0c\u4e5f\u4e0d\u613f\u82b1\u5982\u8bb8\u5fc3\u673a\uff0c\u53bb\u53d6\u8fd9\u52b3\u4ec0\u5b50\u3002\u201d\u51cc\u672a\u98ce\u7980\u9053\uff1a\u201c\u5f1f\u5b50\u6709\u4f4d\u53cb \u4eba\uff0c\u6b64\u6b21\u673a\u7f18\u51d1\u5de7\uff0c\u5012\u53d6\u6765\u4e86\uff01\u201d\u5f53\u4e0b\u8bf4\u4e86\u5f20\u534e\u662d\u5728\u9a86\u9a7c\u5cf0\u4e0a\u83b7\u5f97\u201c\u4f18\u6619\u82b1\u201d\u7684\u7ecf\u8fc7\uff0c\u5e76\u4ee3 \u4ed6\u4eec\u6c42\u89c1\u3002 \u3000\u3000\u6666\u660e\u7985\u5e08\u6c89\u601d\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u95ed\u95e8\u5c01\u5251\uff0c\u5df2\u516d\u5341\u591a\u5e74\uff0c\u672c\u4e0d\u613f\u518d\u89c1\u5916\u4eba\uff0c\u4f46\u6211\u4e0e\u4f60\u6b64 \u6b21\u6050\u662f\u6700\u540e\u4e00\u9762\u4e86\uff0c\u89c1\u89c1\u4f60\u4eec\u5e74\u8f7b\u4e00\u8f88\u4e5f\u597d\u3002\u4f60\u5c31\u628a\u4ed6\u4eec\u5f15\u6765\u5427\uff01\u201d \u3000\u3000\u6666\u660e\u7985\u5e08\u6b65\u51fa\u7985\u5802\uff0c\u51cc\u672a\u98ce\u5df2\u628a\u6842\u4ef2\u660e\u4ed6\u4eec\u5f15\u8fdb\u3002\u6842\u4ef2\u660e\u7b49\u4eba\u5f97\u89c1\u6b64\u4e00\u4ee3\u5251\u6cd5\u7684\u5927\u5b97 \u5e08\uff0c\u65e2\u5174\u594b\uff0c\u53c8\u81ea\u602f\uff0c\u5012\u662f\u6666\u660e\u7985\u5e08\u6781\u559c\u6709\u4e3a\u7684\u540e\u8f88\uff0c\u53eb\u4ed6\u4eec\u4e0d\u5fc5\u62d8\u675f\uff0c\u5404\u7ec3\u4e86\u4e00\u5957\u672c\u95e8\u5251 \u6cd5\uff0c\u6842\u4ef2\u660e\u7684\u662f\u201c\u4e94\u79bd\u5251\u201d\uff0c\u5f20\u534e\u662d\u548c\u5192\u6d63\u83b2\u7ec3\u7684\u662f\u201c\u65e0\u6781\u5251\u201d\u3002\u6666\u660e\u7985\u5e08\u7b11\u9053\uff1a\u201c\u5728\u540e\u8f88 \u4e4b\u4e2d\uff0c\u4f60\u4eec\u7684\u5251\u6cd5\u4e5f\u7b97\u662f\u96be\u5f97\u7684\u4e86\uff0c\u4e94\u79bd\u5251\u4ee5\u521a\u52b2\u89c1\u957f\uff0c\u65e0\u6781\u5251\u4ee5\u67d4\u53d6\u80dc\uff0c\u5404\u64c5\u80dc\u573a\u3002\u82e5\u80fd \u521a\u67d4\u4e92\u6d4e\uff0c\u5728\u53d8\u5316\u4e4b\u95f4\u518d\u7cbe\u76ca\u6c42\u7cbe\uff0c\u90a3\u4fbf\u66f4\u597d\u3002\u201d\u5f53\u4e0b\u6307\u70b9\u51e0\u5904\u7a8d\u8981\uff0c\u6842\u4ef2\u660e\u7b49\u4e09\u4eba\u4e00\u9f50\u62dc \u8c22\u3002 \u3000\u3000\u6666\u660e\u7985\u5e08\u53d6\u8fc7\u6842\u4ef2\u660e\u7684\u5b9d\u5251\uff0c\u5f39\u4e86\u51e0\u4e0b\uff0c\u559f\u7136\u53f9\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4eca\u65e5\u590d\u89c1\u6b64\u5251\uff01\u201d\u5bf9\u51cc\u672a \u98ce\u9053\uff1a\u201c\u6211\u5e74\u8f7b\u65f6\u66fe\u662f\u80fd\u7ecf\u7565\u7684\u5e55\u5ba2\uff0c\u4ed6\u53d6\u9ed1\u9f99\u6c5f\u7684\u767d\u91d1\u7ec3\u5251\u4e4b\u65f6\uff0c\u6211\u4e5f\u5728\u573a\u3002\u201d\u5f53\u4e0b\u53c8\u6307 \u70b9\u4e86\u6842\u4ef2\u660e\u51e0\u624b\u4f7f\u5251\u4e4b\u6cd5\u3002\u51cc\u672a\u98ce\u5ffd\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u4ed6\u8fd9\u53e3\u5b9d\u5251\u51e0\u4e4e\u7ed9\u4ed6\u7684\u5e08\u53d4\u593a\u53bb\u5462\uff01\u201d\u6666 \u660e\u7985\u5e08\u9053\uff1a\u201c\u662f\u5417\uff1f\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u4ed6\u4e00\u89c1\u6211\u5c31\u8981\u62a2\u8fd9\u628a\u5b9d\u5251\uff0c\u540e\u6765\u660e\u660e\u77e5\u9053\u6211\u662f\u4ed6\u7684\u5e08 \u4f84\uff0c\u4ed6\u8fd8\u8981\u62a2\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u9053\u7406\uff1f\u201d\u6666\u660e\u7985\u5e08\u53f9\u9053\uff1a\u201c\u8f9b\u9f99\u5b50\u6b64\u4eba\u4e5f\u662f\u88ab\u4f60\u7684\u5353\u5e08\u53d4\u7eb5\u574f \u4e86\uff0c\u53ea\u662f\u4ed6\u7684\u8654\u5fc3\u6bc5\u529b\uff0c\u5012\u662f\u4e0d\u9519\u3002\u2018\u8fbe\u6469\u4e00\u767e\u96f6\u516b\u5f0f\u2019\u6211\u867d\u672a\u89c1\u8fc7\uff0c\u4f46\u636e\u53e4\u8001\u76f8\u4f20\uff0c\u91cc\u9762 \u6709\u638c\u6cd5\u4e0e\u5200\u5251\u7b49\u7528\u793a\uff0c\u5176\u4e2d\u7684\u5251\u6cd5\u5c24\u5176\u7cbe\u5999\uff0c\u542c\u8bf4\u53ea\u6709\u4e09\u5341\u4e09\u4e2a\u62db\u5f0f\uff0c\u4f46\u5374\u53ef\u56de\u73af\u8fd0\u7528\uff0c\u53d8 \u5316\u5947\u7edd\uff0c\u5f80\u5f80\u4e00\u4e2a\u62db\u5f0f\u5c31\u53ef\u53d8\u51fa\u8bb8\u591a\u62db\u5f0f\u6765\uff0c\u8f9b\u9f99\u5b50\u60f3\u662f\u7ec3\u6210\u4e86\u8fbe\u6469\u5251\u6cd5\uff0c\u4f46\u5374\u6ca1\u6709\u5b9d\u5251\uff0c \u6240\u4ee5\u8fde\u5e08\u4f84\u7684\u5251\u4e5f\u8981\u62a2\u4e86\u3002\u201d \u3000\u3000\u6842\u4ef2\u660e\u7b49\u4eba\u5403\u8fc7\u658b\u996d\uff0c\u53c8\u548c\u6666\u660e\u7985\u5e08\u8c08\u4e86\u4e00\u4f1a\uff0c\u4e00\u8f6e\u660e\u6708\uff0c\u5df2\u5230\u4e2d\u5929\uff0c\u6666\u660e\u7985\u5e08\u5ffd\u7136\u643a \u4e86\u51cc\u672a\u98ce\uff0c\u5e26\u9886\u4f17\u4eba\u51fa\u5916\u3002\u5929\u5c71\u6708\u8272\u662f\u5927\u81ea\u7136\u7684\u5947\u666f\u4e4b\u4e00\uff0c\u5510\u671d\u7684\u5927\u8bd7\u4eba\u674e\u767d\u5c31\u5199\u8fc7\u201c\u660e\u6708 \u51fa\u5929\u5c71\uff0c\u82cd\u832b\u4e91\u6d77\u95f4\u201d\u8fd9\u6837\u7684\u7edd\u53e5\u3002\u8fd9\u65f6\u773a\u671b\u5927\u5c71\u7fa4\u5cf0\uff0c\u5728\u4e91\u96fe\u5c01\u7410\u4e4b\u4e2d\uff0c\u7ed9\u6708\u5149\u8feb\u65f6\uff0c\u597d \u50cf\u8499\u4e0a\u4e00\u5c42\u51b0\u96ea\uff0c\u6708\u4eae\u53c8\u5927\u53c8\u5706\uff0c\u597d\u50cf\u6b63\u6b63\u60ac\u5728\u5934\u9876\uff0c\u4f38\u624b\u53ef\u6458\u3002\u4f17\u4eba\u6c90\u5728\u6708\u5149\u4e2d\u6c89\u9189\u8d5e \u53f9\uff0c\u51cc\u672a\u98ce\u5ffd\u7136\u89c9\u5f97\u6666\u660e\u7985\u5e08\u7684\u624b\u5fae\u5fae\u53d1\u6296\u3002 \u3000\u3000\u51cc\u672a\u98ce\u609a\u7136\u4e00\u60ca\uff0c\u6666\u660e\u7985\u5e08\u5ffd\u9053\uff1a\u201c\u4eba\u751f\u767e\u5e74\uff0c\u7535\u5149\u77f3\u706b\uff1b\u672c\u65e0\u4e00\u7269\uff0c\u4f55\u67d3\u5c18\u57c3\uff1f\u968f\u5fc3 \u5230\u5904\uff0c\u4fbf\u662f\u697c\u53f0\uff0c\u9010\u610f\u884c\u65f6\uff0c\u81ea\u6210\u5b9d\u76f8\u3002\u4f60\u82e5\u5fc3\u4e2d\u6709\u6211\uff0c\u4e0d\u5fc5\u8fdc\u4e0a\u5929\u5c71\u3002\u201d\u51cc\u672a\u98ce\u4f3c\u61c2\u975e \u61c2\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u611a\u9c81\uff0c\u672a\u89e3\u7985\u4e49\uff0c\u8fd8\u671b\u5e08\u7236\u6559\u8bf2\u3002\u201d\u6666\u660e\u7985\u5e08\u9053\uff1a\u201c\u4e00\u843d\u8a00\u8be0\uff0c\u4fbf\u975e \u7cbe\u4e49\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u5fc3\u5934\u4e00\u9707\uff0c\u7ec6\u5473\u7985\u8bed\uff0c\u4f3c\u662f\u6666\u660e\u7985\u5e08\u4e34\u522b\u8bf4\u6cd5\uff0c\u70b9\u6bd4\u611a\u987d\uff0c\u4e8e\u662f\u5408\u638c\u8bf4\u9053\uff1a\u201c\u4f5b \u8bf4\u6211\u4e0d\u5165\u5730\u72f1\u8c01\u5165\u5730\u72f1\uff0c\u4eba\u95f4\u9b54\u969c\u672a\u9664\uff0c\u53c8\u4f55\u5fcd\u81ea\u5bfb\u6781\u4e50\uff1f\u201d\u6666\u660e\u7985\u5e08\u53e3\u5ba3\u4f5b\u53f7\uff0c\u8d5e\u9053\uff1a \u201c\u5584\u54c9\uff0c\u5584\u54c9\uff01\u5192\u59d1\u5a18\u5999\u89e3\u7985\u7406\uff0c\u8001\u7eb3\u627f\u6559\u4e86\u3002\u53ea\u662f\u4f5b\u4ee5\u5343\u4e07\u5316\u8eab\u666e\u6e21\u4f17\u751f\uff0c\u8001\u7eb3\u62cd\u638c\u6765 \u53bb\uff0c\u867d\u65e0\u5316\u8eab\u5374\u4e5f\u8fd8\u5e78\u6709\u51e0\u4e2a\u5f1f\u5b50\u3002\u201d\u5192\u6d63\u83b2\u6025\u5fd9\u8dea\u4e0b\u793c\u62dc\uff0c\u6842\u4ef2\u660e\u4e00\u70b9\u4e5f\u4e0d\u61c2\u4ed6\u4eec\u8bf4\u4e9b\u4ec0 \u4e48\uff0c\u77aa\u5927\u7740\u773c\uff0c\u770b\u5192\u6d63\u83b2\u3002\u51cc\u672a\u98ce\u548c\u5f20\u534e\u662d\u4e5f\u8ddf\u7740\u8dea\u4e0b\uff0c\u6842\u4ef2\u660e\u5374\u6115\u7136\u4e0d\u77e5\u6240\u4ee5\u3002 \u3000\u3000\u539f\u6765\u5192\u6d63\u83b2\u7ec6\u53c2\u7985\u610f\uff0c\u731c\u5ea6\u6666\u660e\u7985\u5e08\u4e0d\u4e45\u5c06\u5750\u5316\u3002\u56e0\u6b64\u5979\u8bf4\u201c\u4eba\u95f4\u9b54\u969c\u672a\u9664\u201d\uff0c\u529d\u6666\u660e \u7985\u5e08\u591a\u6d3b\u51e0\u5e74\uff0c\u4e3a\u4eba\u95f4\u9664\u6076\u626c\u5584\u3002\u6666\u660e\u7985\u5e05\u5374\u4ee5\u201c\u4f5b\u4ee5\u5343\u4e07\u5316\u8eab\u666e\u6e21\u4f17\u751f\u201d\u4e3a\u7b54\uff0c\u610f\u601d\u8bf4\u5373 \u4ee5\u4f5b\u7956\u90a3\u6837\u7684\u5927\u667a\uff0c\u4e5f\u8981\u5706\u5bc2\uff0c\u53ea\u80fd\u4ee5\u4f5b\u7ecf\u771f\u7406\uff0c\u904d\u4f20\u4e16\u95f4\uff0c\u7b49\u4e8e\u4ee5\u5343\u4e07\u5316\u8eab\uff0c\u666e\u6e21\u4f17\u751f\uff0c \u6211\u5df2\u8fc7\u767e\u5c81\uff0c\u4eba\u65e0\u4e0d\u6b7b\u4e4b\u7406\uff0c\u7559\u4e0b\u7684\u5f1f\u5b50\uff0c\u5982\u80fd\u7167\u6211\u7684\u8bdd\u53bb\u505a\uff0c\u751f\u751f\u4e0d\u706d\uff0c\u90a3\u4e5f\u7b49\u4e8e\u6211\u7684\u65e0 \u6570\u5316\u8eab\u4e86\u3002\u4f5b\u7ecf\u867d\u662f\u4e00\u79cd\u552f\u5fc3\u7684\u54f2\u5b66\uff0c\u4f46\u4e5f\u6709\u53ef\u91c7\u7684\u54f2\u7406\u3002\u51cc\u672a\u98ce\u8ddf\u7740\u4e5f\u609f\u51fa\u6666\u660e\u7985\u5e08\u7684\u610f \u601d\uff0c\u5fc3\u4e2d\u4e0d\u80dc\u60f6\u6050\u3002 \u3000\u3000\u6666\u9634\u7985\u5e08\u7b11\u7740\u5c06\u4ed6\u4eec\u62c9\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u4f55\u5fc5\u5982\u6b64\uff1f\u201d\u53c8\u5bf9\u51cc\u672a\u98ce\u9053\uff1a\u201c\u5929\u5c71\u7edd\u9876\u82e6\u5bd2\uff0c\u4f60 \u5c06\u6765\u613f\u5426\u7559\u6b64\uff0c\u542c\u4f60\u81ea\u4fbf\uff0c\u53ea\u662f\u85cf\u7ecf\u9601\u91cc\u7684\u4e66\uff0c\u6709\u6211\u7684\u6ce8\u89e3\uff0c\u8fd8\u6709\u4e00\u672c\u62f3\u7ecf\u548c\u4e00\u672c\u5251\u8bc0\uff0c\u4f60 \u5fc5\u987b\u66ff\u6211\u4fdd\u5168\u3002\u65f6\u5019\u4e0d\u65e9\uff0c\u8fd8\u662f\u65e9\u70b9\u5b89\u6b47\u5427\u3002\u201d \u3000\u3000\u8fd9\u4e00\u665a\uff0c\u5927\u5bb6\u90fd\u6ca1\u597d\u7761\uff0c\u51cc\u672a\u98ce\u5fc3\u60f3\u5e08\u7236\u786c\u6717\u5982\u5e38\uff0c\u4ed6\u867d\u7136\u7559\u4e0b\u9057\u5631\u822c\u7684\u5048\u8bed\uff0c\u60f3\u4e5f\u662f \u4e00\u822c\u8001\u4eba\u7684\u5e38\u60c5\uff0c\u672a\u5fc5\u5728\u77ed\u671f\u5185\u5c31\u4f1a\u5706\u5bc2\u3002\u60f3\u4e0d\u5230\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u609f\u6027\u5c31\u5306\u5306\u8d76\u6765\u9053\uff1a\u201c\u672a \u98ce\uff0c\u4e0d\u597d\u4e86\uff0c\u5e08\u7236\u5df2\u7ecf\u5750\u5316\u4e86\uff01\u201d\u51cc\u672a\u98ce\u6025\u5fd9\u8d76\u5230\u9759\u5ba4\uff0c\u53ea\u89c1\u6666\u660e\u7985\u5e08\u7aef\u5750\u84b2\u56e2\uff0c\u5782\u7709\u95ed \u76ee\uff0c\u4e00\u5982\u5e73\u65f6\u6253\u5750\u6a21\u6837\uff0c\u4e0d\u89c9\u75db\u54ed\u3002\u609f\u6027\u5728\u65c1\u9053\uff1a\u201c\u84b2\u56e2\u8fb9\u7559\u6709\u4e24\u672c\u4e66\u548c\u4e00\u4e2a\u9526\u5323\uff0c\u60f3\u662f\u5e08 \u7236\u7279\u522b\u62e3\u51fa\u6765\u4ea4\u7ed9\u4f60\u7684\uff0c\u4f60\u62dc\u9886\u4e86\u5427\u3002\u201d\u51cc\u672a\u98ce\u53d6\u8fc7\u4e24\u672c\u4e66\u6765\u770b\uff0c\u4e00\u672c\u5199\u770b\u201c\u5929\u5c71\u5251\u8bc0\u201d\uff0c \u4e00\u672c\u5199\u7740\u201c\u6666\u660e\u62f3\u7ecf\u201d\uff0c\u77e5\u662f\u5e08\u7236\u767e\u5e74\u5fc3\u8840\uff0c\u6025\u5fd9\u53e9\u5934\u8c22\u6069\u3002\u53c8\u53d6\u8fc7\u9526\u5323\u4e00\u770b\uff0c\u4e0a\u9762\u5199\u9053\uff1a \u201c\u4f18\u6619\u4ed9\u82b1\uff0c\u4e00\u767d\u4e00\u7ea2\uff0c\u643a\u540c\u6b64\u5323\uff0c\u4e0a\u5357\u9ad8\u5cf0\u3002\u201d\u53c8\u6709\u5c0f\u5b57\u6ce8\u7740\uff1a\u201c\u9886\u6211\u9057\u547d\u8005\uff0c\u662f\u6211\u9694\u4e16 \u5f1f\u5b50\uff0c\u53ef\u5411\u8f9b\u9f99\u5b50\u53d6\u6211\u62f3\u7ecf\u5251\u8bc0\uff0c\u7531\u8f9b\u9f99\u5b50\u4ee3\u5e08\u4f20\u6280\u3002\u4e00\u822a\u3002\u201d\u51cc\u672a\u98ce\u77e5\u662f\u5353\u4e00\u822a\u9057\u7269\uff0c\u8981 \u53d6\u5f97\u4f18\u6619\u82b1\u7684\u4eba\uff0c\u643a\u540c\u6b64\u5323\uff0c\u4e0a\u5357\u9ad8\u5cf0\u53bb\u89c1\u767d\u53d1\u9b54\u5973\u3002\u4ed6\u4e00\u60f3\uff1a\u8fd9\u5323\u6211\u53ef\u4e0d\u80fd\u643a\u5e26\u3002\u6b63\u60f3\u53eb \u609f\u6027\u53bb\u8bf7\u5f20\u534e\u662d\uff0c\u56de\u9996\u4e00\u770b\uff0c\u5f20\u534e\u662d\u548c\u6842\u4ef2\u660e\u7b49\u4eba\u5df2\u5728\u9759\u5ba4\u5916\u4e0b\u8dea\u53c2\u62dc\u3002 \u3000\u3000\u51cc\u672a\u98ce\u4f9d\u793c\u7b54\u62dc\uff0c\u5192\u6d63\u83b2\u9053\uff1a\u201c\u8001\u7985\u5e08\u5e74\u903e\u767e\u5c81\uff0c\u52d8\u7834\u7ea2\u5c18\uff0c\u4e00\u7b11\u897f\u884c\uff0c\u4fee\u6210\u6b63\u679c\uff0c\u51cc \u5927\u4fa0\u4e0d\u5fc5\u8fc7\u4efd\u60b2\u4f24\u3002\u201d\u51cc\u672a\u98ce\u6536\u6cea\u4e0e\u609f\u6027\u5c06\u5e08\u7236\u88c5\u655b\uff0c\u5f53\u65e5\u4e0b\u5348\u5c31\u5728\u5929\u5c71\u7edd\u9876\u4e0a\u4e3a\u6666\u660e\u7985\u5e08 \u5efa\u8d77\u575f\u5893\u3002\u4e27\u4e8b\u5b8c\u4e86\uff0c\u5c06\u94dc\u5323\u4ea4\u7ed9\u5f20\u534e\u662d\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u7684\u4e8b\u4e86\uff0c\u5c06\u9526\u5323\u4e0e\u4ed9\u82b1\u4ea4\u7ed9\u767d\u53d1\u9b54\u5973 \u4e4b\u540e\uff0c\u518d\u5411\u98de\u7ea2\u5dfe\u8ba8\u56de\u6613\u5170\u73e0\uff0c\u529f\u5fb7\u5b8c\u6ee1\u3002\u90a3\u65f6\u4f60\u82e5\u613f\u5b66\u6b66\u5f53\u62f3\u5251\uff0c\u5c31\u53bb\u62dc\u90a3\u8f9b\u9f99\u5b50\u4e3a\u5e08 \u5427\uff0c\u6709\u5353\u4e00\u822a\u7684\u9057\u547d\uff0c\u4ed6\u4e0d\u80fd\u4e0d\u6536\u4f60\u3002\u201d\u5f20\u534e\u662d\u9053\uff1a\u201c\u6211\u53ea\u6c42\u80fd\u89c1\u5f97\u7740\u6613\u5170\u73e0\uff0c\u5fc3\u613f\u5df2\u8db3\uff0c \u6211\u5012\u4e0d\u5e0c\u7f55\u90a3\u8f9b\u9f99\u5b50\u7684\u6280\u827a\u3002\u201d\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u5b66\u5b66\u602a\u62db\uff0c\u5012\u4e0d\u9519\u5440\uff01\u201d\u51cc\u672a\u98ce\u5fc3\u5ff5\u4e00\u52a8\uff0c \u60f3\u9053\uff1a\u201c\u90a3\u4e66\u662f\u5c11\u6797\u6b66\u5f53\u4e24\u6d3e\u4f20\u5bb6\u4e4b\u5b9d\uff0c\u8f9b\u9f99\u5b50\u62ff\u53bb\u5012\u8fd8\u8bf4\u5f97\u8fc7\u53bb\uff0c\u53ea\u662f\u4ed6\u4e0d\u8be5\u7528\u8be1\u8ba1\u53bb\u9a97 \u97e9\u5fd7\u90a6\uff0c\u5c06\u6765\u6211\u5012\u8981\u66ff\u97e9\u5927\u54e5\u51fa\u4e00\u53e3\u6c14\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u5b88\u575f\u4e09\u65e5\uff0c\u5c3d\u4e86\u5f92\u5f1f\u4e4b\u793c\uff0c\u5e76\u5c06\u6666\u660e\u7985\u5e08\u7559\u4e0b\u7684\u62f3\u7ecf\u5251\u8bc0\uff0c\u518d\u7ec3\u4e00\u904d\u3002\u7b2c\u56db\u65e5\u8f9e \u7075\u4e0b\u5c71\uff0c\u5e76\u4e0e\u609f\u6027\u63e1\u522b\u3002\u609f\u6027\u9053\uff1a\u201c\u767d\u53d1\u9b54\u5973\u813e\u6c14\u6781\u602a\uff0c\u4f60\u4eec\u53ef\u5f97\u5f53\u5fc3\u3002\u201d\u4ed6\u53c8\u8bf4\u8d77\u98de\u7ea2\u5dfe \u5e76\u4e0d\u4e0e\u5e08\u7236\u540c\u4f4f\uff0c\u800c\u662f\u4f4f\u5728\u5357\u9ad8\u5cf0\u4fa7\u9762\u7684\u5929\u90fd\u5cf0\uff0c\u5728\u62dc\u89c1\u767d\u53d1\u9b54\u5973\u4e4b\u524d\uff0c\u53ef\u4ee5\u5148\u89c1\u98de\u7ea2\u5dfe\uff0c \u4e5f\u53ef\u4ee5\u4e0d\u7ecf\u8fc7\u5929\u90fd\u5cf0\u800c\u76f4\u4e0a\u5357\u9ad8\u5cf0\u3002 \u3000\u3000\u6797\u6728\u8fe4\u9026\uff0c\u6c34\u5ddd\u7eb5\u6a2a\uff0c\u6c14\u5019\u53d8\u5316\u6781\u5927\uff0c\u5728\u6258\u514b\u900a\u4e00\u5e26\uff0c\u58c1\u4e0a\u53ef\u4ee5\u70d8\u997c\uff0c\u9e21\u86cb\u53ef\u4ee5\u6652\u719f\uff0c \u518d\u8d70\u534a\u65e5\uff0c\u767b\u4e0a\u4fc4\u970d\u5e03\u62c9\u5c71\u53e3\uff0c\u53c8\u662f\u4e25\u5bd2\u8feb\u4eba\u4e86\u3002\u5192\u6d63\u83b2\u53f9\u9053\uff1a\u201c\u8bfb\u4e07\u5377\u4e66\u4e0d\u5982\u884c\u4e07\u91cc\u8def\uff0c \u4e0d\u5230\u5929\u5c71\u4e0d\u77e5\u4e16\u754c\u4e4b\u5947\uff01\u201d\u56db\u4eba\u884c\u4e86\u4e03\u65e5\uff0c\u89c1\u96ea\u5c71\u63d2\u4e91\uff0c\u5341\u591a\u6761\u51b0\u6cb3\uff0c\u9576\u5728\u96ea\u5c71\u8c37\u4e2d\uff0c\u5c31\u50cf \u661f\u5149\u4e00\u6837\uff0c\u4ece\u5c71\u4e0a\u5411\u56db\u9762\u653e\u5c04\u3002\u51cc\u672a\u98ce\u6307\u70b9\u7740\u4e1c\u4fa7\u7684\u4e00\u5ea7\u5c71\u5cf0\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u5929\u90fd\u5cf0\u4e86\uff0c\u98de\u7ea2 \u5dfe\u548c\u6613\u5170\u73e0\u5c31\u4f4f\u5728\u90a3\u513f\uff1f\u201d\u5f20\u534e\u662d\u5ffd\u9053\uff1a\u201c\u6211\u4eec\u5148\u4e0a\u5929\u90fd\u5cf0\u597d\u4e0d\u597d\uff1f\u201d\u51cc\u672a\u98ce\u6c89\u601d\u672a\u7b54\uff0c\u6842 \u4ef2\u660e\u9053\uff1a\u201c\u5bf9\u5440\uff0c\u5148\u627e\u7740\u6613\u5170\u73e0\u59d0\u59d0\uff0c\u7136\u540e\u518d\u9001\u82b1\u7ed9\u767d\u53d1\u9b54\u5973\uff0c\u4e0d\u4e5f\u4e00\u6837\uff1f\u201d\u51cc\u672a\u98ce\u601c\u5f20\u534e \u662d\u7684\u82e6\u604b\uff0c\u6168\u7136\u7b54\u5141\u3002 \u3000\u3000\u5929\u90fd\u5cf0\u867d\u6bd4\u5357\u9ad8\u5cf0\u4e3a\u4f4e\uff0c\u4f46\u5df2\u662f\u539f\u59cb\u68ee\u6797\u3001\u6e3a\u65e0\u4eba\u8ff9\u4e4b\u5730\u3002\u56db\u4eba\u82b1\u4e86\u4e09\u5929\u529f\u592b\uff0c\u6500\u767b\u4e0a \u53bb\uff0c\u65f6\u89c1\u5140\u9e70\u76d8\u65cb\uff0c\u96ea\u7f8a\u7adf\u8d70\uff0c\u8fd9\u4e9b\u79bd\u517d\u89c1\u4e86\u4eba\u4e5f\u4e0d\u5bb3\u6015\u3002\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u5927\u7ea6\u5b83\u4eec\u89c1\u4e86\u6211 \u4eec\uff0c\u4e5f\u89c9\u5f97\u5f88\u5947\u602a\uff0c\u5f88\u6709\u5174\u8da3\u5427\u3002\u201d\u8d70\u4e0a\u5cf0\u9876\uff0c\u8fce\u9762\u662f\u56db\u5341\u51e0\u4e08\u9ad8\u7684\u51b0\u5d16\uff0c\u5c31\u597d\u50cf\u62c9\u8428\u7684\u5927 \u5efa\u7b51\u4e00\u6837\uff0c\u51c0\u660e\u6e9c\u4eae\uff0c\u6b63\u770b\u5f97\u5165\u795e\uff0c\u7a81\u7136\u4ece\u9644\u8fd1\u4f20\u6765\uff1a\u201c\u54d2\u2026\u2026\u54d2\u2026\u2026\u201d\u7684\u8db3\u97f3\u3002 \u3000\u3000\u6842\u4ef2\u660e\u7b49\u56db\u4e0b\u5bdf\u770b\uff0c\u5374\u627e\u4e0d\u7740\u8e2a\u8ff9\uff0c\u518d\u5f80\u524d\u8d70\u51e0\u6b65\uff0c\u8db3\u97f3\u53c8\u54cd\u4e86\uff0c\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u4eec \u4e0d\u5fc5\u778e\u627e\u4e86\uff0c\u54ea\u91cc\u6709\u4eba\uff1f\u201d\u8bdd\u72b9\u672a\u5b8c\uff0c\u201c\u54d2\uff0c\u54d2\u2026\u2026\u201d\u7684\u8db3\u97f3\u53c8\u5728\u8eab\u65c1\u4f20\u51fa\uff0c\u975e\u5e38\u54cd\u4eae\u3002\u6842 \u4ef2\u660e\u7741\u5927\u773c\u775b\uff0c\u6ee1\u8138\u7591\u60d1\u7684\u795e\u60c5\uff0c\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4f60\u4eec\u542c\u542c\u58f0\u97f3\u662f\u4ece\u54ea\u91cc\u4f20\u6765\u7684\uff1f\u201d\u5192\u6d63\u83b2 \u9053\uff1a\u201c\u5440\uff01\u600e\u7684\u8fd9\u58f0\u97f3\u5c31\u597d\u50cf\u5728\u6211\u4eec\u811a\u8e0f\u7684\u77f3\u5934\u5e95\u4e0b\u3002\u201d\u6842\u4ef2\u660e\u628a\u8033\u6735\u8d34\u5728\u77f3\u9699\u4e0a\uff0c\u53ea\u542c\u89c1 \u77f3\u4e0b\u6c34\u6d41\u5982\u6ce8\uff0c\u53ee\u53ee\u5f53 \u3000\u3000\u201d\u5f53\uff0c\u7c7b\u4f3c\u97f3\u4e50\uff0c\u95f4\u6742\u7740\u6c89\u91cd\u7684\u201c\u54d2\u2026\u2026\u54d2\u201d\u7684\u58f0\u97f3\uff0c\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u6211\u521d\u6765\u65f6\u4e5f\u66fe\u4e3a \u8fd9\u79cd\u58f0\u97f3\u7591\u60d1\u8fc7\uff0c\u540e\u6765\u624d\u77e5\u9053\u5929\u5c71\u5c71\u8109\u4e00\u5e26\uff0c\u6709\u8bb8\u591a\u5de8\u5927\u7684\u51b0\u5c71\uff0c\u7531\u4e8e\u5730\u9707\uff0c\u540e\u9762\u9ad8\u5c71\u7684\u5ca9 \u77f3\u584c\u4e0b\u6765\uff0c\u628a\u51b0\u5c71\u538b\u5728\u4e0b\u9762\u3002\u51b0\u5c71\u4e00\u5929\u5929\u878d\u5316\uff0c\u5ca9\u77f3\u5c31\u4e00\u5929\u5929\u67b6\u7a7a\u3002\u5ca9\u77f3\u4e2d\u7a7a\u5904\uff0c\u51b0\u6cb3\u6d41 \u52a8\uff0c\u548c\u4eba\u884c\u7684\u811a\u6b65\u58f0\u5341\u5206\u76f8\u4f3c\u3002\u201d\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u771f\u628a\u6211\u5413\u6b7b\u4e86\u3002\u6211\u4eec\u4ece\u6c5f\u5357\u6765 \u7684\u4eba\uff0c\u51b0\u96ea\u90fd\u5c11\u89c1\uff0c\u54ea\u6599\u5230\u5927\u5c71\u5e95\u4e0b\uff0c\u8fd8\u57cb\u85cf\u6709\u8fdc\u53e4\u7684\u51b0\u5c71\u3002\u201d\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u5f97\u5c0f\u5fc3\uff0c \u6211\u4eec\u811a\u4e0b\u5c31\u662f\u5de8\u5927\u7684\u51b0\u5c71\u5462\uff01\u53ea\u8981\u5ca9\u77f3\u54d7\u5566\u5566\u4e00\u6563\u67b6\u5b50\uff0c\u6211\u4eec\u5c31\u522b\u60f3\u751f\u8fd8\u4e86\uff1f\u201d \u3000\u3000\u5f20\u534e\u662d\u5374\u72ec\u81ea\u51fa\u795e\u8046\u542c\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff0c\u600e\u7684\u4f1a\u4e0d\u662f\u4eba\uff1f\u201d\u811a\u5c16\u4e00\u70b9\uff0c\u5982\u7bad\u79bb \u5f26\uff0c\u75be\u8dd1\u51fa\u53bb\u3002 \u3000\u3000\u5f20\u534e\u662d\u5728\u5c71\u5d16\u5ced\u58c1\u4e0a\u7ed5\u4e86\u4e2a\u5708\u5b50\uff0c\u5f84\u81ea\u6500\u4e0a\u4e86\u4e00\u4e2a\u5c71\u5934\u3001\u6ca1\u5165\u6797\u6728\u4e4b\u4e2d\u3002\u51cc\u672a\u98ce\u7b11\u9053\uff1a \u201c\u4ed6\u60f3\u5f97\u53d1\u75f4\u4e86\uff0c\u8ba9\u4ed6\u81ea\u5df1\u53bb\u770b\u770b\u5427\u3002\u201d\u4ed6\u8bdd\u867d\u5982\u6b64\u8bf4\uff0c\u4ecd\u7136\u5e26\u5934\u4e0a\u5c71\uff0c\u8fdc\u8fdc\u8ddf\u7740\u5f20\u534e\u662d\u3002 \u3000\u3000\u5f20\u534e\u662d\u8fd9\u56de\u731c\u5bf9\u4e86\uff0c\u4e0a\u9762\u771f\u6709\u4eba\u7684\u8db3\u97f3\uff0c\u4ed6\u6500\u4e0a\u5c71\u5934\uff0c\u6797\u4e2d\u5ffd\u4f20\u51fa\u4e00\u9635\u6e05\u8106\u7684\u6b4c\u58f0\uff0c\u6b4c \u9053\uff1a\u201c\u6015\u9022\u79cb\uff0c\u6015\u9022\u79cb\uff0c\u4e00\u5165\u79cb\u6765\u6ee1\u662f\u6101\uff0c\u7ec6\u96e8\u513f\u9635\u9635\u98d8\uff0c\u9ec4\u53f6\u513f\u770b\u770b\u76b1\u3002\u6253\u7740\u5fc3\u5934\uff0c\u9501\u4e86 \u7709\u5934\uff0c\u9e4a\u6865\u867d\u662f\u4e0d\u957f\u7559\uff0c\u4ed6\u4e00\u5e74\u4e00\u5ea6\u4eb2\uff0c\u5f3a\u5982\u6211\u4e0d\u6210\u5c31\u3002\u201d\u8fd9\u662f\u5317\u4eac\u9644\u8fd1\u6d41\u884c\u7684\u6c11\u6b4c\uff0c\u6613\u5170 \u73e0\u5728\u77f3\u632f\u98de\u5bb6\u4e2d\u4f4f\u7684\u65f6\u5019\u5b66\u4f1a\u7684\uff0c\u5f20\u534e\u662d\u4e5f\u66fe\u542c\u5979\u5531\u8fc7\uff0c\u8fd9\u65f6\u4e00\u542c\uff0c\u5982\u83b7\u81f3\u5b9d\uff0c\u5927\u58f0\u53eb\u9053\uff1a \u201c\u5170\u73e0\uff01\u5170\u73e0\uff01\u201d\u6811\u6797\u4e2d\u4eba\u5f62\u4e00\u89c1\uff0c\u5f20\u534e\u662d\u98de\u6b65\u8d76\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u5c11\u5973\u5de6\u8eb2\u53f3\u95ea\uff0c\u6025\u6025\u5954\u9003\uff0c \u5f20\u534e\u662d\u53c8\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5170\u73e0\uff0c\u4f60\u4e0d\u80fd\u8fd9\u6837\u5fcd\u5fc3\u5440\uff01\u201d\u65c1\u8fb9\u4e00\u4e2a\u4eba\u5ffd\u7684\u4ece\u4e00\u68f5\u6811\u540e\u8f6c\u51fa\u8eab\u6765\uff0c \u65a5\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u4e0d\u51c6\u4f60\u5728\u8fd9\u91cc\u4e71\u53eb\u4e71\u56b7\uff01\u201d\u8fd9\u4eba\u5bb9\u989c\u7f8e\u8273\uff0c\u5374\u767d\u53d1\u76c8\u5934\uff0c \u5f20\u534e\u662d\u4e00\u89c1\uff0c\u53c8\u53eb\u51fa\u58f0\u6765\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u4e0d\u51c6\u6211\u89c1\u5979\uff0c\u4f60\u5c31\u6740\u4e86\u6211\u5427\uff01\u201d\u53d1\u529b\u4e00\u8dc3\uff0c\u5ffd\u7136\u5168 \u8eab\u9ebb\u8f6f\uff0c\u5012\u5728\u5730\u4e0a\uff0c\u98de\u7ea2\u5dfe\u8eab\u5f62\u4e00\u6643\uff0c\u970e\u5ffd\u4e0d\u89c1\uff0c\u90a3\u5c11\u5973\u7684\u6b4c\u58f0\uff0c\u4f59\u97f3\u64a9\u7ed5\uff0c\u5c1a\u81ea\u8361\u6f3e\u5728\u539f \u59cb\u7684\u5927\u68ee\u6797\u4e2d\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u51cc\u672a\u98ce\u7b49\u4eba\u8d76\u5230\uff0c\u89c1\u72b6\u5927\u60ca\uff0c\u6025\u5fd9\u66ff\u5f20\u534e\u662d\u89e3\u4e86\u7a74\u9053\uff0c\u5f20\u534e\u662d\u9053\uff1a\u201c\u6211\u89c1\u7740 \u5979\u4e86\uff0c\u98de\u7ea2\u5dfe\u4e0d\u51c6\u6211\u548c\u5979\u8c08\u8bdd\u3002\u201d\u51cc\u672a\u98ce\u95ee\u77e5\u7ecf\u8fc7\uff0c\u53f9\u9053\uff1a\u201c\u7cbe\u8bda\u6240\u81f3\uff0c\u91d1\u77f3\u4e3a\u5f00\uff0c\u4f60\u80fd\u95fb \u6211\u7b49\u6240\u4e0d\u80fd\u95fb\u4e4b\u97f3,\u4e5f\u5fc5\u80fd\u4e3a\u6211\u7b49\u6240\u4e0d\u80fd\u4e3a\u4e4b\u4e8b\u3002\u6211\u4eec\u529d\u4e0d\u52a8\u98de\u7ea2\u5dfe,\u4f60\u4e00\u5b9a\u80fd\u6210\u3002\u201d \u3000\u3000\u56db\u4eba\u7a7f\u5165\u6797\u4e2d\uff0c\u679c\u7136\u89c1\u7740\u4e00\u95f4\u77f3\u5c4b\uff0c\u51cc\u672a\u98ce\u4e0a\u524d\u62cd\u95e8\u53eb\u9053\uff1a\u201c\u665a\u8f88\u51cc\u672a\u98ce\u7279\u6765\u664b\u8c12\uff01\u201d \u901a\u540d\u4e4b\u540e\uff0c\u4e45\u4e45\u4e0d\u89c1\u5f00\u95e8\u3002 \u3000\u3000\u4e14\u8bf4\u90a3\u65e5\u98de\u7ea2\u5dfe\u62fc\u6b7b\u6253\u9000\u695a\u662d\u5357\uff0c\u62a2\u5230\u6613\u5170\u73e0\u4e4b\u540e\uff0c\u628a\u5979\u643a\u56de\u5929\u90fd\u5cf0\uff0c\u6089\u5fc3\u66ff\u5979\u533b\u6cbb\u3002 \u6613\u5170\u73e0\u5728\u5929\u7262\u6570\u6708\uff0c\u7cbe\u795e\u8089\u4f53\u90fd\u7ed9\u6298\u78e8\u5f97\u75db\u82e6\u4e0d\u582a\uff0c\u96be\u5f97\u98de\u7ea2\u5dfe\u50cf\u6148\u6bcd\u4e00\u6837\u7231\u62a4\u5979\uff0c\u7167\u987e\u5f97 \u65e0\u5fae\u4e0d\u81f3\uff0c\u4e0d\u4e45\u5c31\u7ed9\u8c03\u6cbb\u597d\u4e86\u3002\u98de\u7ea2\u5dfe\u4e00\u5929\u665a\u4e0a\u544a\u8bc9\u5979\uff0c\u5979\u7684\u6bcd\u4eb2\u738b\u5983\u5df2\u6b7b\u3002\u6613\u5170\u73e0\u6728\u7136\u65e0 \u8bed\uff0c\u521a\u521a\u5e73\u590d\u7684\u5fc3\u7075\u521b\u75db\u53c8\u53d1\u4f5c\u8d77\u6765\uff0c\u98de\u7ea2\u5dfe\u7d27\u7d27\u5730\u62e5\u62b1\u7740\u5979\uff0c\u773c\u6cea\u6ef4\u5728\u5979\u7684\u9762\u4e0a\uff0c\u8bf4\u9053\uff1a \u201c\u6211\u4ee5\u524d\u5f88\u6068\u4f60\u7684\u6bcd\u4eb2\uff0c\u8fd9\u6b21\u5979\u4e34\u7ec8\u65f6\u6211\u5728\u5979\u7684\u8eab\u65c1\uff0c\u6211\u624d\u77e5\u9053\u6211\u4ee5\u524d\u6068\u9519\u4e86\uff0c\u4f60\u7684\u6bcd\u4eb2\u5b9e \u5728\u662f\u4e00\u4e2a\u7075\u9b42\u5584\u826f\u7684\u597d\u5973\u4eba\uff0c\u6211\u4eec\u7684\u51a4\u4ec7\u5728\u5979\u4e34\u7ec8\u524d\u7684\u4e00\u77ac\u5b8c\u5168\u5316\u89e3\u4e86\uff0c\u6211\u4eec\u7ed3\u6210\u4e86\u59d0\u59b9\uff0c \u5979\u7684\u5973\u513f\u5c31\u662f\u6211\u7684\u5973\u513f\u3002\u201d\u6613\u5170\u73e0\u5012\u5728\u98de\u7ea2\u5dfe\u6000\u4e2d\uff0c\u53eb\u4e86\u58f0\u201c\u5988\u5988\uff0c\u4f60\u4e0d\u5acc\u5f03\u6211\uff0c\u6211\u5c31\u505a\u4f60 \u7684\u5973\u513f\uff01\u201d\u98de\u7ea2\u5dfe\u542c\u4e86\u8fd9\u58f0\u201c\u5988\u5988\u201d\uff0c\u5fc3\u4e2d\u5982\u4e00\u80a1\u6696\u6d41\u6d41\u8fc7\uff0c\u628a\u6613\u5170\u73e0\u6402\u5f97\u66f4\u7d27\uff0c\u5728\u5979\u8033\u8fb9 \u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5170\u73e0\uff0c\u6211\u662f\u4f60\u7238\u7238\u751f\u524d\u6700\u597d\u7684\u670b\u53cb\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d\u6613\u5170\u73e0\u201c\u55ef\u201d\u4e86\u4e00\u58f0\u9053\uff1a \u201c\u90a3\u6211\u89c1\u7740\u4f60\u5c31\u5982\u89c1\u7740\u7238\u5988\u4e00\u6837\u3002\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u5fc3\u4e2d\u4e00\u9635\u60b2\u82eb\uff0c\u5c18\u5c01\u4e86\u7684\u8bb0\u5fc6\u50cf\u6bd2\u86c7\u4e00\u6837\u54ac\u7740\u5979\u7684\u5fc3\u3002\u4e8c\u5341\u4f59\u5e74\u524d\u5979\u662f\u5357\u7586\u5404\u65cf \u7684\u76df\u4e3b\uff0c\u7387\u9886\u65cf\u4eba\u62b5\u6297\u6e05\u5175\uff0c\u7267\u6c11\u4eec\u8fd8\u7279\u522b\u4e3a\u5979\u7f16\u8fc7\u4e00\u9996\u6b4c\uff0c\u201c\u6211\u4eec\u7684\u82f1\u96c4\u54c8\u739b\u96c5\uff0c\u5979\u5728\u8349 \u539f\u4e4b\u4e0a\u58f0\u540d\u5927\u201d\u5c31\u662f\u90a3\u9996\u6b4c\u7684\u5f00\u9996\u4e24\u53e5\u3002\u53ef\u662f\u8fd9\u4f4d\u53f1\u54a4\u8349\u539f\u7684\u5973\u82f1\u96c4\uff0c\u5374\u4e00\u518d\u53d7\u7740\u611f\u60c5\u7684\u6298 \u78e8\uff0c\u5979\u548c\u6768\u4e91\u9aa2\u5fd7\u540c\u9053\u5408\uff0c\u672c\u6765\u53ef\u4ee5\u6210\u4e3a\u6781\u597d\u7684\u7231\u4eba\uff0c\u4e0d\u6599\u5728\u4e00\u573a\u5927\u6218\u4e89\u4e2d\u5931\u6563\u4e4b\u540e\uff0c\u518d\u78b0 \u5934\u65f6\uff0c\u6768\u4e91\u9aa2\u548c\u7eb3\u5170\u660e\u6167\u5df2\u8ba2\u9e33\u76df\uff0c\u96be\u5206\u96be\u820d\u4e86\u3002\u98de\u7ea2\u5dfe\u7b2c\u4e00\u4e2a\u7231\u4eba\u662f\u4e2a\u6b4c\u624b\uff0c\u4e3a\u4e86\u4ed6\u6697\u901a \u654c\u4eba\uff0c\u5979\u4eb2\u624b\u628a\u4ed6\u6740\u6389\uff0c\u78b0\u5230\u6768\u4e91\u9aa2\u540e\uff0c\u5979\u4ee5\u5168\u526f\u7684\u751f\u547d\u7231\u4e0a\u4e86\u4ed6\uff0c\u4e0d\u6599\u4ed6\u5374\u53c8\u7231\u4e0a\u654c\u4eba\u7684 \u5973\u513f\uff0c\u4f46\u4ed6\u548c\u90a3\u4e2a\u6b4c\u624b\u662f\u5b8c\u5168\u4e0d\u540c\u7684\u4eba\uff0c\u5979\u4e0d\u80fd\u6740\u4ed6\uff0c\u53c8\u7981\u4e0d\u4f4f\u4e0d\u7231\u4ed6\uff0c\u540e\u6765\u5979\u542c\u5f97\u7eb3\u5170\u660e \u6167\u548c\u591a\u94ce\u6210\u5a5a\uff0c\u518d\u60f3\u53bb\u627e\u6768\u4e91\u9aa2\uff0c\u800c\u6768\u4e91\u9aa2\u7684\u6b7b\u8baf\u5df2\u4f20\u6765\u4e86\uff0c\u8fd9\u79cd\u611f\u60c5\u7684\u6298\u78e8\uff0c\u4f7f\u5979\u4e00\u591c\u4e4b \u95f4\u5934\u53d1\u5c3d\u767d\uff01\u5357\u7586\u5404\u65cf\u6297\u6e05\u5931\u8d25\u4e4b\u540e\uff0c\u5979\u9690\u5c45\u5929\u90fd\u5cf0\u4e8c\u5341\u5e74\uff0c\u5728\u5bc2\u5bde\u7684\u5c81\u6708\u4e2d\uff0c\u5bf9\u6768\u4e91\u9aa2\u7684 \u601d\u5ff5\u6108\u751a\u3002\u53ea\u8981\u5c5e\u4e8e\u6768\u4e91\u9aa2\u7684\u4e1c\u897f\uff0c\u5979\u90fd\u6709\u6df1\u6c89\u7684\u611f\u60c5\uff0c\u5982\u4eca\u5f97\u5230\u4e86\u6768\u4e91\u9aa2\u7684\u5973\u513f\uff0c\u5979\u662f\u518d \u4e5f\u4e0d\u80af\u8ba9\u5979\u5931\u6389\u4e86\u3002 \u3000\u3000\u5979\u7ed9\u6613\u5170\u73e0\u8bb2\u5979\u7236\u4eb2\u7684\u4e8b\u8ff9\uff0c\u8bb2\u4ed6\u4eec\u4e24\u4eba\u5f53\u5e74\u5e76\u80a9\u4f5c\u6218\u7684\u82f1\u96c4\u6545\u4e8b\uff0c\u8bb2\u5979\u81ea\u5df1\u7684\u60b2\u4f24\u548c \u5bc2\u5bde\uff0c\u5979\u8bf4\uff1a\u201c\u5973\u513f\u554a\uff01\u6211\u518d\u4e5f\u4e0d\u80fd\u5931\u6389\u4f60\u4e86\uff0c\u4f60\u7b54\u5e94\u6c38\u8fdc\u5728\u6211\u7684\u8eab\u8fb9\uff0c\u4ec0\u4e48\u4eba\u6765\u53eb\u4f60\u4f60\u90fd \u4e0d\u8d70\u5417\uff1f\u201d\u6613\u5170\u73e0\u52ab\u540e\u4f59\u751f\uff0c\u5fc3\u5982\u69c1\u6728\uff0c\u5f20\u534e\u662d\u7684\u5f71\u5b50\u867d\u63a0\u8fc7\u5979\u7684\u5fc3\u5934\uff0c\u4f46\u5bf9\u7740\u98de\u7ea2\u4e2a\u7684\u6cea \u5149\uff0c\u8fd9\u5f71\u5b50\u4e5f\u500f\u5730\u6d88\u5931\u4e86\uff0c\u5979\u5fcd\u4e0d\u4f4f\uff0c\u62b1\u7740\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u5988\u5988\uff0c\u6211\u7b54\u5e94\u6c38\u8fdc\u4e0d\u79bb\u5f00\u4f60\uff01\u201d \u3000\u3000\u5f20\u534e\u662d\u54ea\u91cc\u77e5\u9053\u98de\u7ea2\u5dfe\u5df2\u7528\u611f\u60c5\u63a7\u5236\u4e86\u6613\u5170\u73e0\uff0c\u4ed6\u968f\u7740\u51cc\u672a\u98ce\u5927\u529b\u62cd\u95e8\uff0c\u4e45\u4e45\u4e0d\u89c1\u4eba \u5e94\uff0c\u4e0d\u7981\u6012\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\u5230\u5e95\u662f\u4ec0\u4e48\u5c42\u5fc3\uff0c\u8fd9\u6837\u4e0d\u8bb2\u60c5\u7406\uff1f\u518d\u4e0d\u5f00\u95e8\u6211\u5c31\u6253\u8fdb\u53bb\uff01\u201d \u3000\u3000\u5f20\u534e\u662d\u8bdd\u58f0\u672a\u4e86\uff0c\u77f3\u95e8\u500f\u5730\u6253\u5f00\uff0c\u98de\u7ea2\u5dfe\u73b0\u51fa\u8eab\u6765\uff0c\u51b7\u51b7\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u51cc\u672a\u98ce \u8d76\u5fd9\u7b54\u9053\uff1a\u201c\u6211\u4eec\u7279\u6765\u62dc\u8c12\u524d\u8f88\u3002\u201d\u98de\u7ea2\u5dfe\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u53ea\u6015\u4f60\u4eec\u8981\u6765\u62dc\u8c12\u7684\u4e0d\u662f \u6211\uff01\u201d\u6842\u4ef2\u660e\u5e94\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u77e5\u9053\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8bb8\u5170\u73e0\u59d0\u59d0\u51fa\u6765\uff1f\u201d\u5192\u6d63\u83b2\u6025\u5fd9\u626f\u4ed6\u4e00 \u4e0b\u3002\u98de\u7ea2\u5dfe\u50b2\u7136\u5bf9\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u8fd9\u6837\u6ca1\u89c4\u77e9\uff01\u201d\u6842\u4ef2\u660e\u8fd8\u60f3\u8bf4\u8bdd\uff0c\u5374\u7ed9\u5192\u6d63\u83b2 \u6b62\u4f4f\u3002\u5192\u6d63\u83b2\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u5170\u73e0\u59d0\u59d0\u548c\u6211\u4eec\u60c5\u540c\u624b\u8db3\uff0c\u6211\u4eec\u4e0d\u8fdc\u4e07\u91cc\u800c\u6765\uff0c\u8fd8\u6c42\u524d\u8f88\u51c6\u8bb8\u6211 \u4eec\u89c1\u5979\u4e00\u9762\u3002\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u4e0d\u63a5\u5192\u6d63\u83b2\u7684\u8bdd\uff0c\u5374\u8f6c\u8fc7\u5934\u5bf9\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4f60\u8fd8\u8bb0\u5f97\u4f60\u8bf4\u8fc7\u7684\u8bdd\u5417\uff1f\u201d\u51cc\u672a\u98ce\u6115 \u7136\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u4ec0\u4e48\u8bdd\uff1f\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u5728\u4eac\u4e2d\u6211\u548c\u4f60\u8bf4\u8fc7\uff0c\u6211\u82e5\u6551\u5f97\u6613\u5170\u73e0\u5c31\u4e0d\u51c6\u4f60\u7ba1\uff0c \u6709\u8fd9\u53e5\u8bdd\u5417\uff1f\u201d\u51cc\u672a\u98ce\u60f3\u4e0d\u5230\u5979\u628a\u5f00\u73a9\u7b11\u7684\u8bdd\u5f53\u771f\uff0c\u6842\u4ef2\u660e\u5ffd\u7136\u9a82\u9053\uff1a\u201c\u597d\u4e0d\u5bb3\u7f9e\uff0c\u662f\u4f60\u4e00 \u4e2a\u4eba\u6551\u7684\u5417\uff1f\u4f60\u51ed\u4ec0\u4e48\u628a\u5979\u7ba1\u4f4f\uff0c\u5979\u53c8\u4e0d\u662f\u4f60\u7684\u5973\u513f\uff01\u201d\u98de\u7ea2\u5dfe\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u5979\u5c31\u662f\u6211\u7684\u5973 \u513f\uff01\u201d\u51cc\u672a\u98ce\u77aa\u4e86\u6842\u4ef2\u660e\u4e00\u773c\uff0c\u793a\u610f\u4ed6\u4e0d\u8981\u591a\u8bdd\u3002 \u3000\u3000\u5f20\u534e\u662d\u60b2\u6124\u586b\u80f8\uff0c\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u4f60\u7684\u5973\u513f\u6211\u4e5f\u8981\u89c1\uff0c\u6211\u6709\u8bdd\u8981\u548c\u5979\u8bf4\u3002\u201d\u98de\u7ea2\u5dfe\u559d \u9053\uff1a\u201c\u4f60\u662f\u5979\u4ec0\u4e48\u4eba\uff1f\u4e0d\u51c6\u4f60\u89c1\u4f60\u5c31\u4e0d\u80fd\u89c1\u3002\u201d\u51cc\u672a\u98ce\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u5ffd\u7136\u8fc8\u524d\u4e00\u6b65\uff0c\u7528\u4f4e\u6c89 \u7684\u58f0\u8c03\u95ee\u9053\uff1a\u201c\u6613\u5170\u73e0\u662f\u6211\u4ece\u5c0f\u628a\u5979\u629a\u517b\u5927\u7684\uff0c\u6211\u867d\u7136\u4e0d\u6562\u505a\u5979\u7684\u7236\u4eb2\uff0c\u4f46\u6211\u5bf9\u5979\u5982\u5b9e\u6709\u4e86 \u7236\u5973\u4e4b\u60c5\uff0c\u4f60\u51c6\u4e0d\u51c6\u6211\u89c1\u5979\u5462\uff1f\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u6014\u4e86\u4e00\u6014\uff0c\u4e5f\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u9000\u540e\u5341\u6b65\uff0c\u6211\u53eb\u6613\u5170\u73e0\u5728\u95e8\u53e3\u89c1\u89c1\u4f60\u4eec\uff0c\u8ba9 \u5979\u81ea\u5df1\u8bf4\uff0c\u5979\u613f\u7559\u5728\u8fd9\u91cc\u8fd8\u662f\u613f\u968f\u4f60\u4eec\u53bb\u3002\u201d\u51cc\u672a\u98ce\u65e0\u5948\uff0c\u548c\u540c\u6765\u4e09\u4eba\u4f9d\u8a00\u9000\u4e86\u5341\u6b65\uff0c\u98de\u7ea2 \u5dfe\u624b\u638c\u62cd\u4e86\u4e09\u4e0b\uff0c\u4e00\u4e2a\u5c11\u5973\u8f7b\u8f7b\u5730\u8d70\u5230\u95e8\u524d\u3002\u5f20\u534e\u662d\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5170\u73e0\u59d0\u59d0\uff0c\u6211\u6765\u4e86\uff01\u201d\u98de \u7ea2\u5dfe\u62bd\u51fa\u957f\u97ad\uff0c\u6307\u7740\u5f20\u534e\u662d\u9053\uff1a\u201c\u4e0d\u51c6\u4e0a\u6765\u3002\u201d \u3000\u3000\u6613\u5170\u73e0\u76ee\u5149\u5446\u6ede\uff0c\u53eb\u4e86\u58f0\u201c\u51cc\u53d4\u53d4\uff01\u201d\u4e24\u884c\u6e05\u6cea\u7c41\u7c41\u843d\u4e0b\u3002\u98de\u7ea2\u5dfe\u8d76\u5fd9\u62c9\u7740\u6613\u5170\u73e0\u95ee \u9053\uff1a\u201c\u4ed6\u4eec\u8981\u63a5\u4f60\u51fa\u53bb\uff0c\u4f60\u613f\u610f\u53bb\u4e48\uff1f\u201d\u6613\u5170\u73e0\u4f4e\u7f13\u5730\u8bf4\u9053\uff1a\u201c\u6211\u613f\u610f\u5728\u8fd9\u91cc\u966a\u4f60\uff01\u201d\u98de\u7ea2 \u5dfe\u63a8\u5979\u4e0b\u53bb\u9053\uff1a\u201c\u597d\u4e86\uff0c\u8fd9\u5c31\u884c\u4e86\uff0c\u4f60\u56de\u53bb\u6b47\u6b47\u5427\uff0c\u4f60\u7684\u795e\u8272\u5f88\u4e0d\u597d\u5462\uff01\u201d\u6613\u5170\u73e0\u5982\u4e2d\u9b54 \u5492\uff0c\u7adf\u7136\u8f6c\u8eab\u5165\u5185\uff0c\u5f20\u534e\u662d\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5170\u73e0\uff0c\u5170\u73e0\uff0c\u4e0d\u8981\u56de\u53bb\u3002\u201d\u51cc\u672a\u98ce\u4e5f\u5927\u58f0\u53eb\u9053\uff0c \u201c\u5170\u73e0\uff0c\u4f60\u7684\u7238\u5988\u867d\u7136\u90fd\u6b7b\u4e86\uff0c\u4f46\u4f60\u7238\u7238\u7684\u5fd7\u613f\u4f60\u8fd8\u6ca1\u6709\u66ff\u5730\u5b8c\u6210\u5462\uff01\u4f60\u662f\u4f60\u7238\u7238\u7684\u5973\u513f\uff01 \u53ea\u6740\u4e86\u591a\u94ce\u8fd8\u4e0d\u80fd\u7b97\u662f\u66ff\u7238\u7238\u62a5\u4ec7\u3002\u201d\u98de\u7ea2\u5dfe\u7830\u7684\u4e00\u58f0\u628a\u95e8\u5173\u4e0a\uff0c\u628a\u6613\u5170\u73e0\u5173\u5728\u91cc\u9762\uff0c\u5979\u81ea \u5df1\u5374\u7ad9\u5728\u5899\u5934\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u51cc\u672a\u98ce\uff0c\u4f60\u53ef\u4ee5\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u6842\u4ef2\u660e\u6012\u6c14\u51b2\u51b2\uff0c\u53f3\u624b\u4e00\u632f\uff0c\u500f\u7684\u6253\u51fa\u4e09\u679a\u91d1\u73af\uff0c\u5206\u6253\u98de\u7ea2\u4e2d\u4e09\u5904\u5927\u7a74\uff0c\u60f3\u628a\u98de\u7ea2\u5dfe\u6253 \u5012\uff0c\u7834\u95e8\u800c\u5165\u3002\u98de\u7ea2\u4e2d\u957f\u97ad\u4e00\u5377\uff0c\u628a\u4e09\u679a\u91d1\u73af\u5168\u90fd\u5377\u53bb\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u5ff5\u5728\u4f60\u662f\u665a\u8f88\uff0c\u4e0d \u548c\u4f60\u8ba1\u8f83\uff0c\u4f60\u518d\u80e1\u6765\uff0c\u6211\u5c31\u8981\u8fd8\u656c\u4f60\u4e86\uff01\u201d\u5192\u6d63\u83b2\u7528\u529b\u62c9\u7740\u6842\u4ef2\u660e\uff0c\u51cc\u672a\u98ce\u4e0a\u524d\u4e09\u6b65\uff0c\u8981\u4e0e \u98de\u7ea2\u5dfe\u7406\u8bba\uff0c\u6b63\u95f9\u5f97\u4e0d\u53ef\u5f00\u4ea4\u4e4b\u9645\uff0c\u5ffd\u7136\u6709\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u8d77\u81ea\u8eab\u65c1\u3002 \u3000\u3000\u90a3\u82cd\u8001\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u8c01\u6562\u5728\u5929\u5c71\u6492\u91ce\uff1f\u201d\u51cc\u672a\u98ce\u5413\u4e86\u4e00\u8df3\uff0c\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u6ee1\u5934 \u767d\u53d1\u7684\u8001\u5a46\u5a46\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u65f6\u5019\uff0c\u7adf\u7136\u6765\u5230\u4e86\u4ed6\u4eec\u4e2d\u95f4\uff0c\u51cc\u672a\u98ce\u606d\u606d\u656c\u656c\u5730\u884c\u4e86\u4e2a\u793c\uff0c\u8bf4 \u9053\uff1a\u201c\u5bb6\u5e08\u6666\u660e\u7985\u5e08\u9053\u5f1f\u5b50\u53c2\u89c1\u8001\u524d\u8f88\u3002\u201d\u767d\u53d1\u9b54\u5973\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u7684\u5e08\u7236 \u597d\uff1f\u201d\u51cc\u672a\u98ce\u5ea6\u7136\u9053\uff1a\u201c\u5bb6\u5e08\u65e5\u524d\u5706\u5bc2\uff0c\u7279\u6765\u62a5\u77e5\u3002\u201d\u767d\u53d1\u9b54\u5973\u4e00\u9635\u5fc3\u9178\uff0c\u53f9\u9053\uff1a\u201c\u4ece\u4eca\u800c \u540e\uff0c\u518d\u4e5f\u627e\u4e0d\u5230\u5bf9\u624b\u7814\u4e60\u5251\u6cd5\u4e86\u3002\u201d\u51cc\u672a\u98ce\u4e0d\u6562\u4f5c\u58f0\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u767d\u53d1\u9b54\u5973\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u4eec \u771f\u662f\u7279\u610f\u6765\u89c1\u6211\u7684\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u662f\u554a\uff01\u8fd8\u6709\u5353\u5e08\u53d4\u7559\u4e0b\u7684\u9526\u5323\uff0c\u8981\u732e\u4e0e\u4f60\u8001\u4eba\u5bb6\u3002\u201d\u81ea \u53d1\u9b54\u5973\u9762\u8272\u5927\u53d8\uff0c\u53f1\u9053\uff1a\u201c\u4f60\u6562\u5728\u6211\u9762\u524d\u8bf4\u8c0e\uff0c\u6211\u4f4f\u5728\u5357\u9ad8\u5cf0\uff0c\u4f60\u53c8\u4e0d\u662f\u4e0d\u77e5\uff0c\u4f60\u6765\u5929\u90fd\u5cf0 \u4f5c\u751a\uff1f\u5353\u4e00\u822a\u6709\u4e1c\u897f\u7ed9\u6211\uff0c\u4e5f\u4e0d\u4f1a\u53eb\u4f60\u4eec\u62ff\u6765\uff0c\u54fc\uff0c\u4f60\u6562\u620f\u5f04\u4e8e\u6211\uff1f\u201d\u51cc\u672a\u98ce\u6b63\u60f3\u8fa9\u89e3\uff0c\u98de \u7ea2\u5dfe\u62a2\u7740\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4ed6\u4eec\u8054\u540c\u6765\u6b3a\u8d1f\u6211\uff0c\u8981\u62a2\u6211\u65b0\u6536\u7684\u5f92\u5f1f\u3002\u201d\u767d\u53d1\u9b54\u5973\u5ffd\u5730\u51b7\u7b11\u4e00\u58f0\uff0c \u51cc\u672a\u98ce\u3001\u6842\u4ef2\u660e\u3001\u5192\u6dc0\u83b2\u3001\u5f20\u534e\u662d\u56db\u4eba\uff0c\u540c\u65f6\u89c9\u5f97\u4e00\u9635\u773c\u82b1\uff0c\u4f3c\u6709\u4eba\u5f71\u75be\u5728\u8eab\u65c1\u7a7f\u8fc7\uff0c\u51cc\u672a \u98ce\u8eab\u5b50\u9661\u7136\u4e00\u7f29\uff0c\u95ea\u4e86\u5f00\u53bb\uff0c\u8033\u4e2d\u4f9d\u7a00\u542c\u5f97\u6709\u4eba\u53eb\u4e00\u58f0\u201c\u597d\uff01\u201d\u8f6c\u77ac\u95f4\u5fae\u98ce\u98d2\u7136\uff0c\u767d\u53d1\u9b54\u5973 \u53c8\u5df2\u5728\u573a\u4e2d\u7ad9\u5b9a\u3002\u767d\u53d1\u9b54\u5973\u4e24\u624b\u62ff\u7740\u4e09\u53e3\u5b9d\u5251\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u51cc\u672a\u98ce\uff0c\u4f60\u670b\u53cb\u7684\u5175\u5203\u6211\u62ff\u4e0b \u4e86\uff0c\u5ff5\u4f60\u662f\u6666\u660e\u7985\u5e08\u7684\u5f1f\u5b50\uff0c\u6211\u4e0d\u518d\u60e9\u6212\u4f60\u4eec\u4e86\u3002\u4f60\u4eec\u7ed9\u6211\u6eda\u4e0b\u5c71\u53bb\uff01\u201d\u8bf4\u7f62\u643a\u98de\u7ea2\u5dfe\u5165 \u5185\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u518d\u7406\u4ed6\u4eec\u3002\u201d\u7830\u7684\u4e00\u58f0\uff0c\u628a\u77f3\u95e8\u5173\u4e0a\u3002 \u3000\u3000\u51cc\u672a\u98ce\u8fd9\u4e00\u60ca\u9a87\u975e\u540c\u5c0f\u53ef\uff0c\u767d\u53d1\u9b54\u5973\u7adf\u4e8e\u77ac\u606f\u4e4b\u95f4\uff0c\u8fde\u88ad\u4ed6\u4eec\u56db\u4eba\uff0c\u9664\u4e86\u81ea\u5df1\u4e4b\u5916\uff0c\u6842 \u4ef2\u660e\u7b49\u4e09\u4eba\u7684\u5175\u5203\u7adf\u5168\u90e8\u7ed9\u5979\u6536\u53bb\u3002\u8fd9\u771f\u662f\u6b66\u6797\u7edd\u9876\u529f\u592b\uff0c\u602a\u4e0d\u5f97\u5979\u6562\u4e24\u6b21\u53bb\u627e\u6666\u660e\u7985\u5e08\u6bd4 \u8bd5\u3002 \u3000\u3000\u51cc\u672a\u98ce\u6df1\u77e5\u767d\u53d1\u9b54\u5973\u813e\u6c14\u53e4\u602a\uff0c\u4e0d\u6562\u9017\u7559\uff0c\u5e26\u9886\u4e09\u4eba\u4e0b\u4e86\u5929\u90fd\u5cf0\uff0c\u5750\u5728\u5c71\u811a\u53f9\u9053\uff1a\u201c\u89e6 \u72af\u4e86\u8fd9\u5973\u9b54\u5934\uff0c\u6613\u5170\u73e0\u53ea\u6084\u4e0d\u80fd\u518d\u89c1\u7740\u4e86\u3002\u201d\u5f20\u534e\u662d\u795e\u60c5\u9893\u4e27\uff0c\u5982\u75f4\u5982\u679c\u3002\u6842\u4ef2\u660e\u5fc3\u75db\u5931\u4e86 \u5b9d\u5251\uff0c\u4e5f\u8bf4\u51fa\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u5192\u6d63\u83b2\u5ffd\u7136\u62cd\u638c\u8bf4\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u4e0d\u5fc5\u7070\u5fc3\uff0c\u5170\u73e0\u59d0\u59d0\u548c\u6211\u4eec\u7684\u5175\u5203\u8fd8\u53ef\u4ee5 \u56de\u6765\uff0c\u53ea\u662f\u8981\u5f20\u5927\u54e5\u5192\u4e00\u5192\u9669\u3002\u201d\u5f20\u534e\u662d\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u7528\uff1f\u6253\u53c8\u6253\u4e0d\u8fc7\u4eba\u5bb6\uff0c\u6c42\u60c5\u5979\u4eec\u53c8 \u4e0d\u7406\u776c\u3002\u201d\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u96be\u9053\u6211\u8fd8\u4f1a\u53eb\u4f60\u548c\u767d\u53d1\u9b54\u5973\u6253\u67b6\uff1f\u4f60\u4ecd\u7136\u6367\u9526\u5323\uff0c\u643a\u540c\u4ed9\u82b1\uff0c\u5f53 \u4f5c\u6ca1\u6709\u8fd9\u56de\u4e8b\u4f3c\u7684\uff0c\u4e09\u6b65\u4e00\u62dc\uff0c\u72ec\u81ea\u62dc\u4e0a\u5357\u9ad8\u5cf0\u53bb\uff0c\u767d\u53d1\u9b54\u5973\u5305\u7ba1\u53eb\u98de\u7ea2\u5dfe\u5c06\u6613\u5170\u73e0\u653e\u56de\u7ed9 \u4f60\u3002\u201d\u5f20\u534e\u662d\u6115\u7136\u9053\uff1a\u201c\u4f60\u771f\u884c\u628a\u63e1\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u6211\u620f\u5f04\u4f60\u4f5c\u4ec0\u4e48\uff1f\u800c\u4e14\u9664\u4e86\u5982\u6b64\uff0c\u4e5f \u65e0\u5176\u5b83\u6cd5\u5b50\u3002\u201d\u51cc\u672a\u98ce\u4e00\u60f3\uff0c\u61c2\u5f97\u4e86\u5192\u6d63\u83b2\u7684\u610f\u601d\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd8\u662f\u4f60\u673a\u7075\uff0c\u521a\u624d\u6211\u4eec\u90fd \u83bd\u649e\u4e86\u3002\u201d\u6842\u4ef2\u660e\u5927\u60d1\u4e0d\u89e3\uff0c\u77a7\u7740\u5192\u6d63\u83b2\u51fa\u795e\u3002\u5192\u6d63\u83b2\u201c\u55e4\u201d\u7684\u7b11\u51fa\u58f0\u6765\uff0c\u7528\u624b\u6307\u6233\u4ed6\u4e00 \u4e0b\uff0c\u5728\u4ed6\u8033\u8fb9\u6084\u6084\u8bf4\u9053\uff1a\u201c\u50bb\u74dc\uff0c\u6bd4\u5982\u6211\u6709\u4e9b\u4f53\u5df1\u8bdd\u8981\u548c\u4f60\u8bf4\uff0c\u6211\u4f1a\u8bf4\u7ed9\u8bb8\u591a\u4eba\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u5192\u6d63\u83b2\u673a\u7075\u7edd\u9876\uff0c\u767d\u53d1\u9b54\u5973\u7684\u5fc3\u601d\u5979\u4e00\u731c\u5c31\u5bf9\u4e86\u3002\u767d\u53d1\u9b54\u5973\u4e0e\u5353\u4e00\u822a\u5c11\u5e74\u60c5\u4fa3\uff0c\u540e\u6765\u56e0 \u4e8b\u95f9\u7ffb\uff0c\u4ed6\u4eec\u66fe\u7ecf\u6709\u8fc7\u4e00\u4e2a\u5bc6\u7ea6\uff0c\u767d\u53d1\u9b54\u5973\u542c\u8bf4\u5353\u4e00\u822a\u6709\u9057\u7269\u7ed9\u5979\uff0c\u9762\u8272\u5927\u53d8\u3002\u4f46\u60f3\u8d77\u90a3\u4e2a \u5bc6\u7ea6\uff0c\u5353\u4e00\u822a\u7edd\u65e0\u540c\u65f6\u6d3e\u51e0\u4e2a\u4eba\u6765\u7684\u9053\u7406\uff0c\u56e0\u6b64\u53c8\u4ee5\u4e3a\u662f\u51cc\u672a\u98ce\u6545\u610f\u8c03\u4f83\u5979\u3002 \u3000\u3000\u4e14\u8bf4\u51cc\u672a\u98ce\u7b49\u56db\u4eba\u79bb\u4e86\u5929\u90fd\u5cf0\u884c\u53bb\uff0c\u5230\u4e86\u5c71\u9e93\uff0c\u5192\u6d63\u83b2\u9053\uff1a\u201c\u597d\u4e86\uff0c\u4f60\u4e00\u4e2a\u4eba\u4e0a\u53bb\u5427\u3002 \u6211\u4eec\u5728\u8fd9\u91cc\u7b49\u4f60\uff0c\u4f60\u4e0b\u6765\u65f6\u53d1\u54cd\u7bad\u4e3a\u53f7\u5c31\u884c\u4e86\u3002\u201d\u5f20\u534e\u662d\u9053\uff1a\u201c\u767d\u53d1\u9b54\u5973\u53ea\u6015\u8fd8\u672a\u56de\u5c71\u3002\u201d \u5192\u6d63\u83b2\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u7ba1\u5979\u56de\u4e0d\u56de\u5c71\uff0c\u4e0a\u53bb\u627e\u5979\uff0c\u603b\u6709\u597d\u5904\u3002\u201d \u3000\u3000\u5f20\u534e\u662d\u4e00\u4eba\u6500\u85e4\u9644\u845b\uff0c\u72ec\u4e0a\u9ad8\u5cf0\uff0c\u8fd8\u8981\u4e09\u6b65\u4e00\u62dc\uff0c\u8f9b\u82e6\u975e\u5e38\u3002\u5357\u9ad8\u5cf0\u666f\u81f4\u53c8\u548c\u5317\u9ad8\u5cf0\u4e0d \u540c\uff0c\u5c71\u4e0a\u51b0\u6cb3\u751a\u591a\uff0c\u5f20\u534e\u662d\u884c\u4e86\u4e24\u5929\uff0c\u5df2\u63a5\u8fd1\u539f\u59cb\u51b0\u6cb3\uff0c\u51b0\u6cb3\u8fdc\u671b\u5982\u767d\u8272\u7684\u5927\u6d77\u6d6a\uff0c\u4ece\u5e7d\u8c37 \u91cc\u6d41\u6cfb\u800c\u4e0b\uff0c\u884c\u81f3\u8fd1\u5904\u770b\u6e05\u695a\u90a3\u4e9b\u201c\u6d6a\u5934\u201d\u90fd\u662f\u9ad8\u53ef\u4e94\u516d\u4e08\u7684\u5927\u51b0\u67f1\uff0c\u8d77\u4f0f\u5c42\u53e0\uff0c\u6709\u7684\u4f3c\u900f \u660e\u7684\u5b9d\u5854\u5e02\u7684\u4f3c\u5de8\u5927\u7684\u624b\u638c\uff0c\u5f62\u5f62\u8272\u8272\uff0c\u5343\u5947\u4e07\u72b6\u3002\u5f20\u534e\u662d\u4e00\u6765\u6709\u51cc\u672a\u98ce\u6240\u7ed9\u7684\u78a7\u7075\u4e39\uff0c\u4e8c \u6765\u5165\u5929\u5c71\u591a\u65e5\uff0c\u4e5f\u6e10\u6e10\u4e60\u60ef\u5c71\u4e2d\u6c14\u5019\uff0c\u867d\u7136\u5947\u51b7\u5f7b\u9aa8\uff0c\u8fd8\u80fd\u62b5\u53d7\u5f97\u4f4f\u3002 \u3000\u3000\u6cbf\u51b0\u6cb3\u4e0a\u884c\uff0c\u8fc7\u4e00\u5982\u7011\u5e03\u72b6\u7684\u51b0\u574e\uff0c\u9762\u524d\u8c41\u7136\u5f00\u6717\uff0c\u6709\u4e00\u7247\u957f\u8fbe\u51e0\u767e\u4e08\u7684\u5927\u51b0\u5742\uff0c\u51b0\u5742 \u5c3d\u5934\u77d7\u7acb\u4e00\u5ea7\u9ad8\u7ea6\u767e\u4e08\u7684\u51b0\u950b\uff0c\u72ec\u51fa\u4e8e\u7fa4\u5cf0\u4e4b\u65c1\uff0c\u6709\u7528\u575a\u51b0\u6240\u9020\u7684\u5c4b\u5b50\uff0c\u5149\u5f69\u79bb\u5e7b\uff0c\u5185\u4e2d\u9690 \u6709\u4eba\u5f71\u3002 \u3000\u3000\u5f20\u534e\u662d\u6b64\u9645\u5df2\u5728\u5357\u9ad8\u5cf0\u4e4b\u4e0a\uff0c\u90a3\u51b0\u5cf0\u4e43\u662f\u5cf0\u9876\u7684\u79ef\u96ea\u5806\u6210\u3002\u5f20\u534e\u662d\u5fc3\u60f3\u8fd9\u51b0\u5c4b\u60f3\u6765\u5c31\u662f \u767d\u53d1\u9b54\u5973\u6240\u9020\u7684\u4e86\u3002\u4ed6\u8dea\u4e0b\u884c\u4e86\u5927\u793c\uff0c\u53ea\u542c\u5f97\u82cd\u8001\u7684\u58f0\u97f3\u9053\uff1a\u201c\u6211\u9976\u6055\u4f60\u4e86\uff0c\u4f60\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u5f20\u534e\u662d\u5fc3\u60f3\uff1a\u767d\u53d1\u9b54\u5973\u771f\u662f\u602a\u7269\uff0c\u4f4f\u5728\u8fd9\u6837\u7684\u5730\u65b9\u3002\u53ea\u89c1\u5c4b\u4e2d\u70b9\u7740\u65e0\u6570\u8721\u70db\uff0c\u70db\u5149\u4e0e\u51b0 \u5899\u8f89\u6620\uff0c\u8000\u773c\u6b32\u82b1\uff0c\u5750\u5728\u5f53\u4e2d\u7684\u6b63\u662f\u767d\u53d1\u9b54\u5973\uff0c\u5f20\u534e\u662d\u6b63\u60f3\u53c2\u62dc\uff0c\u5ffd\u89c9\u4e00\u80a1\u5927\u529b\u5c06\u81ea\u5df1\u6258 \u8d77\uff0c\u767d\u53d1\u9b54\u5973\u5c06\u81ea\u5df1\u63a5\u4f4f\uff0c\u5f00\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u771f\u662f\u5353\u4e00\u822a\u9063\u6765\u7684\u4e48\uff1f\u201d \u3000\u3000\u5f20\u534e\u662d\u53d6\u51fa\u9526\u5323\uff0c\u9526\u5323\u4e0a\u7528\u4e1d\u5e26\u7cfb\u7740\u4e24\u6735\u82b1\uff0c\u4e00\u767d\u4e00\u7ea2\uff0c\u5468\u56f4\u867d\u7528\u5f69\u7ef8\u7f69\u7740\uff0c\u5f02\u9999\u4ecd\u662f \u900f\u4eba\u9f3b\u89c2\u3002\u767d\u53d1\u9b54\u5973\u53cc\u76ee\u653e\u5149\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4e24\u6735\u82b1\u662f\u6458\u6765\u7684\u5417\uff1f\u201d\u5f20\u534e\u662d\u606d\u606d\u656c\u656c\u7b54\u9053\uff1a \u201c\u662f\u5f1f\u5b50\u6240\u6458\uff0c\u5949\u5353\u8001\u524d\u8f88\u4e4b\u547d\uff0c\u9001\u7ed9\u4f60\u8001\u4eba\u5bb6\u3002\u201d\u767d\u53d1\u9b54\u5973\u5c06\u4e24\u6735\u82b1\u53d6\u4e0b\uff0c\u5374\u4ecd\u653e\u5728\u4e1d\u56ca \u4e2d\uff0c\u5e76\u4e0d\u62ff\u51fa\uff0c\u559f\u7136\u53f9\u9053\uff1a\u201c\u4e03\u5341\u5e74\u524d\u7684\u4e00\u53e5\u620f\u8a00\uff0c\u96be\u4e3a\u4ed6\u8fd8\u8bb0\u5f97\u5982\u6b64\u6e05\u695a\u3002\u6211\u4eca\u65e5\u521a\u597d\u6ee1 \u4e00\u767e\u5c81\uff0c\u8fd8\u8981\u8fd9\u4f18\u6619\u82b1\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u5f20\u534e\u662d\u77a0\u7136\u4e0d\u77e5\u6240\u7b54\uff0c\u770b\u7740\u90a3\u6ee1\u5c4b\u5b50\u7684\u70db\u5149\uff0c\u5fc3\u60f3\uff0c\u539f \u6765\u4eca\u5929\u662f\u5979\u767e\u5c81\u5927\u6625\u3002\u6b63\u60f3\u63aa\u8bcd\u9053\u8d3a\uff0c\u5374\u89c1\u767d\u53d1\u9b54\u5973\u95ed\u76ee\u9759\u5750\uff0c\u9762\u8272\u6c89\u6697\uff0c\u4fbf\u4e0d\u6562\u63d2\u8a00\u3002 \u3000\u3000\u767d\u53d1\u9b54\u5973\u60a0\u7136\u9047\u601d\uff0c\u832b\u7136\u82e5\u68a6\uff0c\u4e03\u5341\u5e74\u524d\u65e7\u4e8b\uff0c\u90fd\u4e0a\u5fc3\u5934\u3002 \u3000\u3000\u4e03\u5341\u5e74\u524d\uff0c\u767d\u53d1\u9b54\u5973\u8fd8\u53ea\u662f\u4e8c\u5341\u591a\u5c81\u7684\u5c11\u5973\uff0c\u53ef\u662f\u5374\u5df2\u540d\u9707\u6c5f\u6e56\uff0c\u662f\u897f\u5317\u7684\u5267\u76d7\uff1b\u5353\u4e00 \u822a\u5219\u662f\u4e2a\u8d35\u5bb6\u516c\u5b50\uff0c\u4ed6\u7684\u7956\u7236\u662f\u4e2a\u5378\u4efb\u603b\u7763\uff0c\u544a\u8001\u8fd8\u4e61\u65f6\u66fe\u88ab\u767d\u53d1\u9b54\u5973\u62e6\u9014\u622a\u52ab\uff0c\u5e76\u4f24\u4e86\u5353 \u4e00\u822a\u7684\u4e00\u4f4d\u540c\u95e8\u3002\u4e5f\u662f\u5408\u5f53\u6709\u6b64\u201c\u60c5\u5b7d\u201d\uff0c\u540e\u6765\u4ed6\u4eec\u7adf\u56e0\u201c\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\u201d\uff0c\u800c\u81f3\u5f7c\u6b64\u503e \u5fc3\u3002\u53ef\u662f\u5353\u4e00\u822a\u5230\u5e95\u662f\u663e\u8d35\u4e4b\u540e\uff0c\u7231\u610f\u53ea\u662f\u5b58\u5728\u5fc3\u4e2d\uff0c\u4e0d\u6562\u8868\u9732\uff0c\u66f4\u4e0d\u80af\u5165\u4f19\u505a\u5f3a\u76d7\uff0c\u767d\u53d1 \u9b54\u5973\u4e00\u6012\u800c\u53bb\uff0c\u518d\u8fc7\u51e0\u5e74\uff0c\u5353\u4e00\u822a\u5df2\u7ecf\u6210\u4e3a\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u5f1f\u5b50\uff0c\u90a3\u5c31\u66f4\u52a0\u963b\u96be\u91cd\u91cd\u4e86\u3002\u4ed6\u4eec \u7ecf\u8fc7\u51e0\u5ea6\u60b2\u6b22\uff0c\u51e0\u756a\u79bb\u5408\uff0c\u6700\u540e\u4e00\u6b21\uff0c\u767d\u53d1\u9b54\u5973\u4e0a\u6b66\u5f53\u5c71\u627e\u4ed6\uff0c\u6b66\u5f53\u6d3e\u7684\u957f\u8001\u56ff\u4e8e\u5b97\u6d3e\u4e4b\u89c1 \u4e0e\u95e8\u6237\u4e4b\u5ff5\uff0c\u8981\u628a\u767d\u53d1\u9b54\u5973\u9a71\u9010\u4e0b\u5c71\uff0c\u767d\u53d1\u9b54\u5973\u6027\u70c8\u5982\u706b\uff0c\u52a8\u624b\u4f24\u4e86\u5353\u4e00\u822a\u4e00\u4e2a\u5e08\u53d4\uff0c\u5353\u4e00 \u822a\u8feb\u4e8e\u65e0\u5948\uff0c\u4e5f\u51fa\u624b\u4f24\u4e86\u767d\u53d1\u9b54\u5973\u3002\u7ecf\u8fc7\u8fd9\u573a\u5927\u53d8\uff0c\u5353\u4e00\u822a\u4f24\u5fc3\u6b32\u7edd\uff0c\u51e0\u4e4e\u53d1\u75af\uff0c\u7ec8\u4e8e\u8f9e\u6389 \u638c\u95e8\uff0c\u8fdc\u8d76\u56de\u7586\uff0c\u8ffd\u8e2a\u767d\u53d1\u9b54\u5973\uff08\u4ed6\u4eec\u4e24\u4eba\u4e4b\u95f4\u7684\u6069\u6069\u6028\u6028\uff0c\u8be6\u89c1\u62d9\u8457\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\uff09\u3002 \u3000\u3000\u4f46\u5353\u4e00\u822a\u867d\u7ecf\u5927\u53d8\uff0c\u8fd8\u662f\u989c\u5bb9\u672a\u6539\uff0c\u767d\u53d1\u9b54\u5973\u5374\u4e0d\u7136\u4e86\uff0c\u90a3\u665a\u52a8\u624b\u4e4b\u540e\uff0c\u5fc3\u5ff5\u5168\u7070\uff0c\u4e00 \u591c\u4e4b\u95f4\uff0c\u5934\u53d1\u5c3d\u767d\u3002\u5979\u662f\u6700\u7231\u81ea\u5df1\u7684\u5bb9\u8c8c\u7684\uff0c\u767d\u53d1\u4e4b\u540e\u4f24\u5fc3\u4e0d\u5df2\uff0c\u7d22\u6027\u5230\u5929\u5c71\u9690\u5c45\uff0c\u4ec0\u4e48\u4eba \u90fd\u4e0d\u613f\u89c1\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u5c31\u662f\u56e0\u8fd9\u6837\u4e00\u518d\u8bef\u4f1a\uff0c\u4ee5\u81f4\u540e\u6765\u867d\u540c\u5728\u5929\u5c71\u6570\u5341\u5e74\uff0c\u5374\u603b\u662f\u907f\u4e0d\u89c1\u9762\uff0c\u6700\u540e\u5206\u624b \u65f6\uff0c\u5353\u4e00\u822a\u66fe\u5bf9\u5979\u8bf4\u9053\uff1a\u201c\u4f60\u4e3a\u6211\u767d\u4e86\u5934\u53d1\uff0c\u6211\u4e00\u5b9a\u8981\u5c3d\u6211\u7684\u529b\uff0c\u4e3a\u4f60\u5bfb\u627e\u7075\u4e39\u5999\u836f\uff0c\u8ba9\u4f60 \u6062\u590d\u9752\u6625\u3002\u201d\u4ed6\u77e5\u9053\u767d\u53d1\u9b54\u5973\u6700\u7231\u81ea\u5df1\u7684\u5bb9\u8c8c\uff0c\u8fdc\u5728\u4ed6\u4eec\u7b2c\u4e00\u6b21\u89c1\u9762\u65f6\uff0c\u767d\u53d1\u9b54\u5973\u5c31\u8bf4\u8fc7 \u201c\u7ea2\u989c\u6613\u8001\u201d\u8bdd\uff0c\u90a3\u65f6\u5353\u4e00\u822a\u5c31\u5f00\u73a9\u7b11\u5730\u5bf9\u5979\u8bf4\u8fc7\uff0c\u613f\u66ff\u5979\u627e\u5bfb\u5934\u53d1\u4e0d\u767d\u7684\u5999\u836f\uff0c\u60f3\u4e0d\u5230\u7adf \u6210\u8c36\u8bed\uff0c\u5982\u4eca\u5979\u5f90\u5a18\u672a\u8001\uff0c\u7adf\u5df2\u767d\u53d1\u6ee1\u5934\uff0c\u6240\u4ee5\u6700\u540e\u5206\u624b\u65f6\uff0c\u4ed6\u53c8\u65e7\u8bdd\u91cd\u63d0\uff0c\u53c8\u8c01\u6599\u5f97\u5230\u8fd9 \u4e2a\u8bb8\u8bfa\uff0c\u7adf\u7136\u6210\u4e86\u4ed6\u6570\u5341\u5e74\u6765\u672a\u4e86\u7684\u5fc3\u613f\uff01 \u3000\u3000\u6b64\u9645\u767d\u53d1\u9b54\u5973\u5bf9\u7740\u4e24\u6735\u4f18\u6619\u82b1\u75f4\u75f4\u51fa\u795e\uff0c\u51e0\u5341\u5e74\u95f4\u4e8b\u60c5\uff0c\u7535\u5149\u77f3\u706b\u822c\u5728\u5fc3\u5934\u95ea\u8fc7\uff0c\u5979\u771f \u60f3\u4e0d\u5230\u5353\u4e00\u822a\u5bf9\u5979\u5982\u6b64\u60c5\u6df1\uff0c\u751f\u524d\u4e00\u53e5\u620f\u8a00\uff0c\u6b7b\u540e\u4ecd\u7136\u529e\u5230\uff0c\u5979\u7741\u5f00\u773c\u775b\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd9 \u4e24\u6735\u82b1\u4f60\u8fd8\u662f\u62ff\u56de\u53bb\u5427\uff01\u201c\u968f\u8bf4\u968f\u6253\u5f00\u9526\u5323\uff0c\u62bd\u51fa\u4e00\u5f20\u9526\u7b3a\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7740\u4e00\u9996\u4e03\u5f8b\uff1a \u3000\u3000\u201c\u522b\u540e\u97f3\u4e66\u4e24\u4e0d\u95fb\uff0c \u3000\u3000\u9884\u77e5\u8c23\u7422\u5fc5\u7eb7\u575b\uff0c \u3000\u3000\u53ea\u7f18\u6d77\u5185\u5b58\u77e5\u5df1\uff0c \u3000\u3000\u59cb\u4fe1\u5929\u6daf\u82e5\u6bd4\u90bb\uff1b \u3000\u3000\u5386\u52ab\u4e86\u65e0\u751f\u6b7b\u5ff5\uff0c \u3000\u3000\u7ecf\u971c\u65b9\u663e\u505a\u5bd2\u5fc3\uff0c \u3000\u3000\u51ac\u98ce\u5c3d\u6298\u82b1\u5343\u6811\uff0c \u3000\u3000\u5c1a\u6709\u5e7d\u9999\u653e\u4e0a\u6797\u3002\u201d \u3000\u3000\u8fd9\u9996\u8bd7\u6b63\u662f\u5353\u4e00\u822a\u5f53\u5e74\u53d7\u5979\u8bef\u4f1a\u4e4b\u540e\uff0c\u6258\u4eba\u5e26\u7ed9\u5979\u7684\u3002\u5f53\u65f6\u5979\u706b\u6c14\u6b63\u76db\uff0c\u8fd8\u5480\u56bc\u4e0d\u51fa\u5176 \u4e2d\u6ecb\u5473\uff0c\u5982\u4eca\u91cd\u8bfb\uff0c\u53ea\u89c9\u4e00\u7247\u871c\u610f\u67d4\u60c5\uff0c\u663e\u793a\u51fa\u4ed6\u7684\u6df1\u5fc3\u76f8\u7231\u3002\u8fd9\u9996\u8bd7\u9996\u4e24\u53e5\u662f\u8bf4\u5206\u522b\u4e4b\u540e \u4e0d\u901a\u559c\u8baf\uff0c\u4ed6\u5df2\u9884\u6d4b\u5230\u4e00\u5b9a\u6709\u5f88\u591a\u8c23\u8a00\u4e86\uff1b\u4e09\u56db\u4e24\u53e5\u8bf4\uff0c\u53ea\u8981\u5f7c\u6b64\u771f\u5fc3\u76f8\u7231\uff0c\u53ea\u8981\u662f\u77e5\u5df1\u5c1a \u5b58\u5728\u4e16\u95f4\uff0c\u90a3\u5c31\u7b97\u4eba\u5728\u5929\u6daf\uff0c\u4e5f\u4e0d\u8fc7\u5982\u9694\u5899\u90bb\u820d\u4e00\u6837\uff1b\u4e94\u516d\u4e24\u53e5\u5219\u8868\u793a\u4ed6\u751f\u6b7b\u4e0d\u6e1d\u7684\u771f\u60c5\uff0c \u8bf4\u662f\u8d8a\u7ecf\u8fc7\u52ab\u96be\uff0c\u8d8a\u7ecf\u5386\u98ce\u971c\uff0c\u76f8\u7231\u7684\u5fc3\u5c31\u8d8a\u53d1\u663e\u73b0\u51fa\u6765\uff1b\u6700\u540e\u4e24\u53e5\u8bf4\u7eb5\u7136\u52ab\u96be\u50cf\u51ac\u98ce\u4e00 \u6837\uff0c\u5439\u6298\u4e86\u5343\u6811\u4e07\u6811\u7231\u60c5\u7684\u82b1\u6735\uff0c\u53ef\u662f\u7f8e\u4e3d\u7684\u7231\u60c5\u82b1\u6735\uff0c\u4ecd\u7136\u662f\u653e\u7740\u4e0d\u6563\u7684\u5e7d\u9999\uff01\u8fd9\u4e9b\u8bdd\u5f53 \u65f6\u8bfb\u8fd8\u4e0d\u89c9\u600e\u4e48\uff0c\u73b0\u5728\u51e0\u5341\u5e74\u8fc7\u53bb\u4e86\uff0c\u5353\u4e00\u822a\u6b7b\u4e86\uff0c\u5979\u4e5f\u6ee1\u4e00\u767e\u5c81\u4e86\uff0c\u5353\u4e00\u822a\u7684\u8bd7\u6070\u6070\u505a\u4e86 \u65f6\u95f4\u7684\u8bc1\u4eba\uff0c\u8bc1\u660e\u5728\u8fd9\u51e0\u5341\u5e74\u95f4\uff0c\u5353\u4e00\u80ae\u7684\u5fc3\u4e8b\u6b63\u5982\u4ed6\u6240\u5199\u7684\u8bd7\u4e00\u6837\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u53d8\u3002 \u3000\u3000\u767d\u53d1\u9b54\u5973\u5c06\u9526\u7b3a\u6298\u8d77\uff0c\u653e\u5165\u6000\u4e2d\uff0c\u9759\u5750\u51b0\u5ba4\u4e4b\u4e2d\uff0c\u51dd\u671b\u5929\u5c71\u5916\u9762\u7684\u4e91\u6d77\uff0c\u4e45\u4e45\uff0c\u4e45\u4e45\uff0c \u4e0d\u53d1\u4e00\u8a00\u3002\u5f20\u534e\u662d\u7980\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d\u767d\u53d1\u9b54\u5973\u5982\u68a6\u521d\u9192\uff0c\u5401\u53e3\u6c14\u9053\uff1a\u201c\u8f9b \u82e6\u4f60\u4e86\uff0c\u4f60\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u6211\u529e\u7684\u4e48\uff1f\u6211\u80fd\u505a\u5f97\u5230\u7684\uff0c\u4e00\u5b9a\u66ff\u4f60\u505a\u3002\u201d\u5f20\u534e\u662d\u9053\uff1a\u201c\u6211\u60f3\u8bf7\u8001 \u524d\u8f88\u5e2e\u5fd9\uff0c\u53eb\u98de\u7ea2\u5dfe\u628a\u6211\u7684\u5170\u73e0\u59d0\u59d0\u653e\u51fa\u6765\u3002\u201d\u767d\u53d1\u9b54\u5973\u9053\uff1a\u201c\u54ea\u4e2a\u5170\u73e0\u59d0\u59d0\uff1f\u554a\uff01\u662f\u90a3\u4e2a \u5973\u5a03\u5b50\u662f\u4e0d\u662f\uff1f\u201d\u5f20\u534e\u662d\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u548c\u5979\u5df2\u7ed3\u540c\u5fc3\uff0c\u4e0d\u613f\u5982\u6b64\u751f\u5206\uff01\u201d\u767d\u53d1\u9b54\u5973\u60f3\u8d77\u81ea \u5df1\u4e00\u751f\uff0c\u70b9\u5934\u53f9\u9053\uff1a\u201c\u6211\u4eec\u4e0a\u4e00\u8f88\u6240\u9519\u8fc7\u7684\u4e1c\u897f\uff0c\u4f60\u4eec\u5c0f\u8f88\u7684\u662f\u4e0d\u5e94\u8be5\u518d\u9519\u8fc7\u4e86\u3002\u98de\u7ea2\u5dfe\u82e5 \u8981\u6536\u5f92\u5f1f\uff0c\u5929\u4e0b\u6709\u7684\u662f\u806a\u6167\u7684\u5973\u513f\uff0c\u5979\u4e0d\u5e94\u8be5\u8981\u4f60\u7684\u5170\u73e0\u59d0\u59d0\u3002\u201d\u8bf4\u7740\u81ea\u7b11\u8d77\u6765\uff0c\u5728\u5934\u4e0a\u62d4 \u4e0b\u4e00\u6839\u78a7\u7389\u7c2a\uff0c\u4ea4\u7ed9\u5f20\u534e\u662d\u9053\uff1a\u201c\u6211\u8fd9\u51e0\u5929\u4e0d\u60f3\u4e0b\u5c71\uff0c\u4f60\u62ff\u8fd9\u6839\u7389\u7c2a\u53bb\u89c1\u98de\u7ea2\u5dfe\uff0c\u5c31\u8bf4\u662f\u6211 \u8981\u5979\u653e\u7684\u597d\u4e86\u3002\u201d\u5f20\u534e\u662d\u5927\u559c\u53e9\u8c22\u3002\u767d\u53d1\u9b54\u5973\u53c8\u5c06\u90a3\u65e5\u6240\u6536\u53bb\u7684\u4e09\u53e3\u5b9d\u5251\u62ff\u51fa\u6765\uff0c\u53eb\u4ed6\u5e26\u56de \u53bb\u4ea4\u8fd8\u6842\u4ef2\u660e\u4ed6\u4eec\uff0c\u4ea4\u6258\u5b8c\u6bd5\uff0c\u767d\u53d1\u9b54\u5973\u9053\uff1a\u201c\u4f60\u8fdc\u9053\u800c\u6765\uff0c\u6211\u6ca1\u6709\u793c\u7269\u7ed9\u4f60\uff0c\u4f20\u4f60\u4e00\u5957\u8f7b \u529f\u5427\u3002\u201d\u8bf4\u7f62\u968f\u624b\u4e00\u5e26\uff0c\u5f20\u534e\u662d\u53ea\u89c9\u817e\u4e91\u9a7e\u96fe\u822c\u5730\u7ed9\u5979\u4e00\u624b\u5e26\u51fa\u77f3\u5c4b\u4e4b\u5916\uff0c\u7b80\u76f4\u8fde\u5979\u8eab\u5f62\u600e \u6837\u65bd\u5c55\u4e5f\u770b\u4e0d\u6e05\u695a\u3002\u5f20\u534e\u662d\u5927\u559c\uff0c\u6025\u5fd9\u8c22\u6069\u3002\u767d\u53d1\u9b54\u5973\u6f14\u4e86\u4e00\u5957\u72ec\u521b\u7684\u8f7b\u529f\uff0c\u653e\u6162\u62db\u5f0f\uff0c\u53eb \u4ed6\u4ed4\u7ec6\u770b\u6e05\uff0c\u518d\u4f20\u6388\u4e86\u53e3\u8bc0\uff0c\u5f20\u534e\u662d\u7ec3\u4e86\u534a\u5929\uff0c\u719f\u8bb0\u5fc3\u5934\uff0c\u767d\u53d1\u9b54\u5973\u9053\uff1a\u201c\u884c\u4e86\uff0c\u4f60\u4ee5\u540e\u81ea \u5df1\u7ec3\u4e60\u5427\uff01\u201d\u6b63\u662f\uff1a\u516b\u5341\u5e74\u6765\u5982\u4e00\u68a6\uff0c\u5929\u5c71\u7edd\u9876\u6388\u8f7b\u529f\u3002\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5eff\u4e8c\u7ae0\u3000\u8fb9\u585e\u9003\u4ea1\u3000\u8352\u6f20\u5947\u7f18\u9022\u5973\u4fa0\u3000\u8349\u539f\u6076\u6218\u3000\u6b66\u6797\u7edd\u5b66\u9a87\u7fa4\u96c4 \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u4e8c\u5341\u4e8c\u56de \u8fb9\u585e\u9003\u4ea1\u3000\u8352\u6f20\u5947\u7f18\u9022\u5973\u4fa0 \u8349\u539f\u6076\u6218\u3000\u6b66\u6797\u7edd\u5b66\u9a87\u7fa4\u96c4 \u3000\u3000\u767d\u53d1\u9b54\u5973\u82e5\u6709\u6240\u601d\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u6735\u82b1\u6211\u7528\u4e0d\u7740\u4e86\uff0c\u4f60\u4e0d\u5982\u62ff\u53bb\u9001\u7ed9\u98de\u7ea2\u5dfe\u5427\u3002\u201d \u5f20\u534e\u662d\u60f3\u8d77\u98de\u7ea2\u5dfe\u4e5f\u662f\u767d\u53d1\u76c8\u5934\uff0c\u8fd9\u4e24\u6735\u82b1\u5979\u6b63\u5408\u7528\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u5f20\u534e\u662d\u62dc\u522b\u767d\u53d1\u9b54\u5973\u4e0b\u5c71\uff0c\u8d70\u4e86\u4e24\u65e5\u5230\u4e86\u5c71\u9e93\uff0c\u653e\u8d77\u54cd\u7bad\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u51cc \u672a\u98ce\u4e0e\u6842\u4ef2\u660e\u3001\u5192\u6d63\u83b2\u4ece\u5c71\u5773\u8f6c\u51fa\uff0c\u5192\u6d63\u83b2\u4e00\u89c1\u5c31\u5927\u58f0\u558a\u9053\uff1a\u201c\u600e\u4e48\u6837\uff0c\u6211\u4e0d\u9a97\u4f60\u5427\uff1f\u201d\u5f20 \u534e\u662d\u559c\u5b5c\u5b5c\u5730\u5c06\u7ecf\u8fc7\u8bf4\u4e86\uff0c\u4f17\u4eba\u9f50\u90fd\u5927\u559c\uff0c\u51cc\u672a\u98ce\u624b\u4e0a\u62ff\u7740\u4e00\u6839\u9ed1\u9edd\u9edd\u7684\u62d0\u6756\uff0c\u5728\u5ca9\u77f3\u4e0a\u6572 \u51fb\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u8d9f\u518d\u53bb\u627e\u98de\u7ea2\u5dfe\uff0c\u770b\u5979\u6562\u4e0d\u6562\u7559\u96be\uff1f\u201d\u5f20\u534e\u662d\u8fd9\u624d\u6ce8\u610f\u5230\u4ed6\u624b\u4e0a\u7684\u62d0 \u6756\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u62d0\u6756\u771f\u597d\u73a9\uff0c\u662f\u6728\u5934\u7684\u5417\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4f60\u8bf4\u597d\u73a9\u5c31\u9001\u7ed9\u4f60\u597d\u4e86\uff0c\u5b83\u6bd4\u94a2 \u94c1\u8fd8\u786c\u5462\uff01\u6211\u8fd9\u51e0\u5929\u91c7\u96c6\u4e86\u8bb8\u591a\u5929\u5c71\u795e\u8292\uff0c\u987a\u4fbf\u524a\u4e0b\u4e86\u5929\u5c71\u7279\u6709\u7684\u964d\u9f99\u6728\uff0c\u5f04\u6210\u4e86\u8fd9\u6839\u62d0 \u6756\u3002\u201d\u5f20\u534e\u662d\u9053\uff1a\u201c\u6211\u53ea\u5b66\u8fc7\u5251\u6cd5\uff0c\u53ef\u6ca1\u5b66\u8fc7\u7528\u68cd\u68d2\u97ad\u6756\u7b49\u5175\u5668\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4f60\u5c31\u4f9d\u65e0 \u6781\u5251\u6cd5\u6765\u4f7f\u8fd9\u6839\u6756\u597d\u4e86\uff0c\u53ea\u6015\u5b83\u6bd4\u4f60\u624b\u4e2d\u7684\u9752\u94a2\u5251\u8fd8\u66f4\u597d\u5462\uff01\u53e6\u5916\u6211\u518d\u6559\u4f60\u51e0\u8def\u62d0\u6756\u70b9\u7a74 \u6cd5\u3002\u201d\u5f20\u534e\u662d\u8fd9\u4e24\u65e5\u673a\u7f18\u51d1\u5de7\uff0c\u5b66\u4e86\u767d\u53d1\u9b54\u5973\u7684\u72ec\u95e8\u8f7b\u529f\uff0c\u53c8\u5f97\u4e86\u964d\u9f99\u5b9d\u6756\uff0c\u5341\u5206\u9ad8\u5174\u3002 \u3000\u3000\u51cc\u672a\u98ce\u7b49\u4e00\u884c\u56db\u4eba\u518d\u56de\u5230\u5929\u90fd\u5cf0\uff0c\u51cc\u672a\u98ce\u4e0a\u524d\u62cd\u95e8\uff0c\u53c8\u662f\u4e45\u4e45\u672a\u6709\u4eba\u5e94\u3002\u51cc\u672a\u98ce\u76b1\u7709 \u9053\uff1a\u201c\u98de\u7ea2\u5dfe\u600e\u4e48\u8fd9\u6837\u4e0d\u8bb2\u6e05\u7406\u4e0d\u7406\u4e0d \u3000\u3000420\u776c\u3002\u201d\u5f20\u534e\u662d\u9053\uff1a\u201c\u6211\u624b\u4e0a\u6709\u5979\u5e08\u7236\u7684\u7389\u7c2a\uff0c\u5c31\u95ef\u8fdb\u53bb\u89c1\u5979\u5427\uff01\u201d\u51cc\u672a\u98ce\u53c8\u53eb\u4e86\u51e0 \u58f0\uff0c\u4ecd\u7136\u672a\u89c1\u7b54\u5e94\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u514d\u6709\u70b9\u607c\u6012\uff0c\u6325\u624b\u8bf4\u9053\uff1a\u201c\u4e5f\u53ea\u6709\u95ef\u8fdb\u53bb\u4e86\uff01\u201d\u6842\u4ef2\u660e\u5df4\u4e0d\u5f97 \u51cc\u672a\u98ce\u8bf4\u51fa\u8fd9\u8bdd\uff0c\u53cc\u638c\u7528\u529b\uff0c\u5728\u77f3\u95e8\u4e0a\u4e00\u63a8\uff0c\u767b\u65f6\u628a\u77f3\u95e8\u63a8\u5f00\uff0c\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6842\u8d24\u5f1f\u4e0d\u53ef\u83bd \u649e\uff0c\u6211\u4eec\u867d\u662f\u7834\u95e8\u800c\u5165\uff0c\u8fd8\u5f97\u4ee5\u793c\u6c42\u89c1\u3002\u201d\u5e26\u9886\u4f17\u4eba\u8d70\u4eba\u5c4b\u5185\uff0c\u53ea\u89c1\u98de\u7ea2\u5dfe\u76d8\u819d\u5750\u5728\u84b2\u56e2\u4e4b \u4e0a\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5c31\u5b9b\u5982\u53e4\u4ee3\u9057\u7559\u4e0b\u7684\u4e00\u5c0a\u77f3\u50cf\u3002\u5979\u5bf9\u5916\u9762\u7684\u7eb7\u6270\uff0c\u7adf\u4f3c\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d \u95fb\u3002 \u3000\u3000\u51cc\u672a\u98ce\u653e\u8f7b\u811a\u6b65\uff0c\u8d70\u8fd1\u84b2\u56e2\uff0c\u4f4e\u58f0\u5524\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u6211\u4eec\u5949\u4ee4\u5e08\u4e4b\u547d\u6765\u770b\u4f60\u3002\u201d\u8fc7\u4e86\u8bb8 \u4e45\u98de\u7ea2\u5dfe\u624d\u8f7b\u542f\u53cc\u76ee\uff0c\u5401\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u6765\u4e86\uff1f\u6613\u5170\u73e0\u8d70\u4e86\uff01\u4e16\u4e8b\u5982\u68a6\uff0c\u4e00\u5207\u7a7a\u65e0\uff0c\u4f60\u4eec\u8fd8 \u8981\u4ec0\u4e48\uff1f\u201d\u8fd9\u5a01\u9707\u8349\u539f\u7684\u5973\u82f1\u96c4\uff0c\u5982\u4eca\u7adf\u4f3c\u4e00\u4e2a\u5782\u5371\u7684\u75c5\u4eba\uff0c\u773c\u775b\u6d88\u5931\u4e86\u5149\u5f69\uff0c\u8bdd\u8bed\u8f6f\u5f31\u65e0 \u529b\uff0c\u767d\u53d1\u98d8\u62c2\uff0c\u8eab\u8eaf\u98a4\u6296\uff0c\u51cc\u672a\u98ce\u6253\u4e86\u4e2a\u5bd2\u895f\uff0c\u5f20\u534e\u662d\u53eb\u9053\uff1a\u201c\u5170\u73e0\u59d0\u59d0\u771f\u7684\u8d70\u4e86\uff01\u201d\u98de\u7ea2 \u4e2a\u9053\uff1a\u201c\u662f\u7684\uff0c\u4f60\u8d62\u4e86\uff0c\u5979\u4e0d\u613f\u4f34\u6211\u540c\u53d7\u7a7a\u5c71\u5c91\u5bc2\uff0c\u5979\u8981\u53bb\u627e\u5bfb\u4f60\u4eec\uff0c\u5979\u5077\u5077\u5730\u8d70\u4e86\uff0c\u55ef\uff0c \u5077\u5077\u5730\u8d70\u4e86\uff01\u201d\u5979\u6307\u4e00\u6307\u53f3\u8fb9\u7684\u5899\u58c1\u9053\uff1a\u201c\u4f60\u770b\uff01\u201d\u90a3\u4e0a\u9762\u7528\u5b9d\u5251\u5212\u4e86\u51e0\u884c\u5b57\u3002\u201d\u5f20\u534e\u662d\u8bfb \u9053\uff1a\u201c\u6069\u4ec7\u672a\u4e86\uff0c\u5fc3\u4e8b\u96be\u6d88\uff0c\u613f\u5a18\u73cd\u91cd\uff0c\u540e\u4f1a\u975e\u9065\uff01\u201d\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5979\u771f\u7684\u8d70\u4e86\uff01\u201d\u98de\u7ea2\u5dfe \u53c8\u95ed\u4e0a\u53cc\u76ee\uff0c\u6325\u6325\u624b\u9053\uff1a\u201c\u4f60\u4eec\u8d70\u5427\uff0c\u8c01\u4e5f\u522b\u7406\u6211\u4e86\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u51dd\u671b\u7740\u98de\u7ea2\u5dfe\uff0c\u5fc3\u4e2d\u65e0\u9650\u96be\u8fc7\uff0c\u5ffd\u7136\u4ed6\u5927\u58f0\u53eb\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u770b\u770b\uff0c\u8fd9\u662f\u4ec0 \u4e48\uff1f\u201d\u98de\u7ea2\u5dfe\u4e0d\u7531\u5f97\u7741\u5f00\u773c\u775b\uff0c\u51cc\u672a\u98ce\u500f\u5730\u4ece\u5f20\u534e\u662d\u624b\u4e2d\uff0c\u62a2\u8fc7\u4e86\u90a3\u6839\u964d\u9f99\u5b9d\u6756\uff0c\u9012\u5230\u98de\u7ea2 \u5dfe\u9762\u524d\uff0c\u53eb\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u8981\u7528\u62d0\u6756\u4e86\uff01\u8fd9\u6839\u7ed9\u4f60\uff01\u201d\u98de\u7ea2\u5dfe\u8bb6\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u51cc\u672a\u98ce\u5927 \u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u884c\u4e86\uff0c\u4f60\u4e0d\u4e2d\u7528\u4e86\uff0c\u6ca1\u6709\u62d0\u6756\uff0c\u4f60\u8def\u4e5f\u8d70\u4e0d\u52a8\u4e86\uff01\u201d\u98de\u7ea2\u5dfe\u52c3\u7136\u5927\u6012\uff0c\u81ea\u84b2\u56e2 \u4e0a\u4e00\u8dc3\u800c\u8d77\uff0c\u9a88\u6307\u9a82\u9053\uff1a\u201c\u51cc\u672a\u98ce\u4f60\u6709\u591a\u5927\u672c\u9886\uff0c\u80c6\u6562\u5c0f\u89d1\u6211\uff1f\u5212\u51fa\u9053\u513f\u6765\uff0c\u6211\u548c\u4f60\u5927\u6218\u4e09 \u767e\u56de\u5408\uff0c\u770b\u5230\u5e95\u662f\u8c01\u884c\u8c01\u4e0d\u884c\u4e86\uff1f\u201d \u3000\u3000\u5f20\u534e\u662d\u7b49\u9a87\u7136\u9707\u60ca\uff0c\u51cc\u672a\u98ce\u795e\u8272\u81ea\u82e5\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\u4f60\u522b\u52a8\u6012\uff0c\u4f60\u81ea\u5df1\u60f3\u60f3\u6211\u6709 \u6ca1\u6709\u8bf4\u9519\u4f60\uff0c\u4f60\u4e3a\u4ec0\u4e48\u795e\u5fd7\u9893\u4e27\uff1f\u5c31\u662f\u56e0\u4e3a\u4f60\u5931\u6389\u4e86\u4f60\u7684\u62d0\u6756\uff01\u201d\u98de\u7ea2\u5dfe\u77aa\u5927\u773c\u775b\uff0c\u559d\u9053\uff1a \u201c\u80e1\u8bf4\u516b\u9053\uff0c\u4f60\u75af\u4e86\u4e48\uff1f\u201d\u51cc\u672a\u98ce\u6fc0\u52a8\u5730\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u75af\uff0c\u75af\u7684\u662f\u4f60\uff01\u4f60\u8981\u628a\u6613\u5170\u73e0\u5f53\u505a\u4f60 \u7684\u62d0\u6756\uff0c\u6ca1\u6709\u5979\u4f60\u5c31\u8fde\u8d70\u4e5f\u4e0d\u80fd\u8d70\u5566\uff01\u6211\u771f\u66ff\u4f60\u7f9e\u803b\uff0c\u4f60\u8fd9\u8349\u539f\u4e0a\u7684\u5973\u82f1\u96c4\uff0c\u8981\u501a\u9760\u4e00\u4e2a\u5973 \u5b69\u5b50\u4f5c\u4f60\u7684\u62d0\u6756\uff01\u4f60\u662f\u8fd9\u6837\u8106\u5f31\uff0c\u8106\u5f31\u5230\u81ea\u5df1\u6ca1\u6709\u52c7\u6c14\u751f\u6d3b\u4e0b\u53bb\uff1f\u53ef\u662f\u6613\u5170\u73e0\u4e0d\u662f\u6728\u5934\uff0c\u5979 \u6709\u751f\u547d\uff0c\u5979\u61c2\u5f97\u601d\u7d22\uff0c\u5979\u6709\u611f\u60c5\uff0c\u5979\u4e0d\u80fd\u591f\u505a\u4f60\u7684\u62d0\u6756\uff01\u4f60\u660e\u767d\u5417\uff1f\u98de\u7ea2\u5dfe\uff0c\u4f60\u4e5f\u5f97\u8bd5\u8bd5\u81ea \u5df1\u7ad9\u8d77\u6765\uff0c\u4e0d\u9760\u62d0\u6756\u6765\u8d70\u8def\u5566\uff01\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u7ed9\u51cc\u672a\u98ce\u4e00\u9635\u6570\u8bf4\uff0c\u9762\u8272\u9893\u8d25\uff0c\u7ea2\u4e86\u53c8\u9752\uff0c\u9752\u4e86\u53c8\u7ea2\u3002\u5192\u6d63\u83b2\u5fc3\u4e2d\u6697\u6697\u8d5e\u53f9\u9053\uff1a \u51cc\u5927\u4fa0\u771f\u884c\uff0c\u4e0d\u662f\u8fd9\u6837\u4e00\u9488\u89c1\u8840\u5730\u9053\u7834\u5979\uff0c\u4e5f\u533b\u4e0d\u4e86\u5979\u7684\u5fc3\u75c5\uff01 \u3000\u3000\u4e8c\u5341\u5e74\u524d\u7684\u82f1\u6c14\u96c4\u98ce\uff0c\u84e6\u7136\u56de\u6765\u4e86\uff0c\u98de\u7ea2\u5dfe\u70ed\u8840\u6cb8\u817e\uff0c\u4f3c\u4e4e\u8981\u7a81\u7834\u8eab\u4f53\u7684\u8eaf\u58f3\u3002\u81ea\u5931\u6389 \u6768\u4e91\u9aa2\u4e4b\u540e\uff0c\u5979\u7684\u786e\u611f\u5230\u975e\u5e38\u7a7a\u865a\uff0c\u597d\u50cf\u5931\u6389\u4e86\u751f\u6d3b\u7684\u652f\u67f1\uff0c\u5979\u7684\u6b66\u827a\u662f\u8d8a\u6765\u8d8a\u9ad8\uff0c\u53ef\u662f\u5979 \u7684\u7cbe\u795e\u529b\u91cf\u5374\u8d8a\u6765\u8d8a\u5f31\uff0c\u8fc7\u53bb\u90a3\u79cd\u6562\u4e8e\u72ec\u5f80\u72ec\u6765\uff0c\u62ab\u8346\u65a9\u68d8\u7684\u96c4\u98ce\u5ffd\u7136\u6d88\u901d\uff0c\u5979\u628a\u81ea\u5df1\u56da\u5728 \u5929\u90fd\u5cf0\u4e0a\uff0c\u72ec\u81ea\u5fcd\u53d7\u75db\u82e6\u7684\u714e\u71ac\uff0c\u5230\u5fcd\u53d7\u4e0d\u6765\u65f6\uff0c\u5c31\u628a\u6613\u5170\u73e0\u62a2\u8fc7\u6765\uff0c\u7528\u6613\u5170\u73e0\u6765\u66ff\u4ee3\u6768\u4e91 \u9aa2\u5728\u5979\u5fc3\u5934\u7684\u5730\u4f4d\uff0c\u7ed9\u5979\u4ee5\u751f\u6d3b\u7684\u52c7\u6c14\uff0c\u5979\u4ec0\u4e48\u4e5f\u4e0d\u7406\uff0c\u53ea\u60f3\u8981\u6613\u5170\u73e0\u966a\u7740\u5979\uff0c\u5728\u7cbe\u795e\u4e0a\u6276 \u6301\u5979\uff0c\u201c\u662f\u554a\uff01\u6211\u7684\u786e\u662f\u628a\u6613\u5170\u73e0\u770b\u6210\u6211\u7684\u624b\u6756\u4e86\uff01\u201d\u98de\u7ea2\u5dfe\u5fc3\u7075\u6fc0\u8361\uff0c\u5185\u5fc3\u7684\u58f0\u97f3\u5728\u8d23\u5907 \u5979\u3002\u5979\u5ffd\u7136\u5927\u58f0\u53eb\u9053\uff1a\u201c\u51cc\u672a\u98ce\uff0c\u4f60\u8bf4\u5f97\u5bf9\uff01\u4f46\u8981\u62d0\u6756\u7684\u98de\u7ea2\u5dfe\u6b7b\u4e86\uff0c\u73b0\u5728\u7ad9\u5728\u4f60\u9762\u524d\u7684\u662f \u4e0d\u8981\u62d0\u6756\u7684\u98de\u7ea2\u5dfe\u3002\u8d70\uff01\u6211\u966a\u4f60\u4eec\u4e0b\u5c71\u53bb\uff0c\u6211\u66ff\u4f60\u4eec\u628a\u6613\u5170\u73e0\u627e\u56de\u6765\uff01\u6211\u8981\u5230\u6211\u7684\u65cf\u4eba\u4e2d \u53bb\uff0c\u8ba9\u4ed6\u4eec\u77e5\u9053\u4e8c\u5341\u5e74\u524d\u7684\u98de\u7ea2\u5dfe\u590d\u6d3b\u4e86\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u628a\u62d0\u6756\u63b7\u7ed9\u5f20\u534e\u662d\uff0c\u9f13\u638c\u6b22\u547c\uff0c\u5f20\u534e\u662d\u4ece\u6000\u4e2d\u53d6\u51fa\u90a3\u4e24\u6735\u4f18\u6619\u82b1\u732e\u4e0a\u53bb\u9053\uff1a\u201c\u8fd9 \u4e24\u6735\u82b1\u662f\u5353\u8001\u524d\u8f88\u7559\u7ed9\u4ee4\u5e08\u7684\uff0c\u4ee4\u5e08\u4e0d\u8981\uff0c\u8bf4\u53eb\u6211\u9001\u7ed9\u4f60\u3002\u201d\u98de\u7ea2\u5dfe\u95fb\u5f97\u4e00\u7f15\u5e7d\u9999\uff0c\u66f4\u662f\u795e \u6e05\u6c14\u723d\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u82b1\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd9\u662f\u4f18\u6619\u82b1\uff0c\u636e\u8bf4\u53ef\u4ee4\u767d\u53d1\u53d8\u9ed1\uff0c\u529f\u903e\u9996 \u4e4c\u3002\u201d\u98de\u7ea2\u5dfe\u6447\u5934\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u8981\u5b83\u3002\u6211\u7684\u5fc3\u5e74\u8f7b\u5c31\u884c\u5566\uff0c\u4f55\u5fc5\u8981\u628a\u767d\u53d1\u53d8\u9ed1\uff1f\u6211\u8981\u7559\u7740\u8fd9 \u6ee1\u5934\u767d\u53d1\uff0c\u505a\u4e00\u4e2a\u7eaa\u5ff5\uff0c\u8fd9\u767d\u53d1\u4f1a\u63d0\u9192\u6211\uff0c\u6211\u66fe\u7ecf\u8870\u8001\u8fc7\uff0c\u4e00\u4e2a\u9700\u8981\u62d0\u6756\u7684\u5973\u4eba\uff01\u201d\u5979\u7b11\u5f97 \u975e\u5e38\u723d\u6717\uff0c\u5fc3\u6e56\u660e\u51c0\u5982\u5929\u5c71\u7684\u51b0\u6cb3\uff01 \u3000\u3000\u518d\u8bf4\u6613\u5170\u73e0\u90a3\u65e5\u81ea\u51cc\u672a\u98ce\u4e0e\u5f20\u534e\u662d\u7b49\u53bb\u540e\uff0c\u601d\u6f6e\u6d6a\u6d8c\uff0c\u5f7b\u591c\u65e0\u7720\uff0c\u5f20\u534e\u662d\u5bf9\u5979\u7684\u871c\u610f\u67d4 \u60c5\uff0c\u56fa\u7136\u4ee4\u5979\u5f98\u5f8a\u4e0d\u5df2\uff0c\u800c\u51cc\u672a\u98ce\u90a3\u756a\u8bf4\u8bdd\uff0c\u529d\u5979\u7ee7\u627f\u7236\u4eb2\u7684\u9057\u5fd7\uff0c\u66f4\u5982\u5f53\u5934\u68d2\u559d\u3001\u66ae\u9f13\u6668 \u949f\uff0c\u5979\u60f3\u6765\u60f3\u53bb\uff0c\u89c9\u5f97\u98de\u7ea2\u5dfe\u867d\u7136\u53ef\u601c\uff0c\u4f46\u81ea\u5df1\u8fd9\u6837\u966a\u5979\u5728\u7a7a\u5c71\u4e2d\u5ea6\u65e0\u804a\u7684\u5c81\u6708\uff0c\u4e5f\u4e0d\u8fc7\u662f \u4e24\u4e2a\u53ef\u601c\u4eba\u76f8\u805a\u4e00\u5904\u800c\u5df2\u3002\u201c\u6211\u8fd8\u5e74\u8f7b\uff0c\u6211\u7684\u751f\u547d\u5c31\u8ba9\u5b83\u50cf\u8721\u70db\u4e00\u6837\uff0c\u5728\u7a7a\u5c71\u4e2d\u70e7\u706d\u4e86\u5417\uff1f \u4e0d\uff0c\u6211\u4e0d\u613f\u610f\uff01\u201d\u6613\u5170\u73e0\u7a81\u7136\u4ece\u5fc3\u5185\u558a\u51fa\u6765\uff0c\u51e0\u4e2a\u6708\u6765\u5fc3\u5934\u4e0a\u90a3\u4e2a\u6b7b\u7ed3\u89e3\u5f00\u4e86\uff0c\u5979\u8fc5\u901f\u4f5c\u4e86 \u51b3\u5b9a\uff0c\u79bb\u5f00\u98de\u7ea2\u5dfe\uff0c\u53bb\u627e\u51cc\u672a\u98ce\u548c\u5f20\u534e\u662d\uff0c\u5979\u6084\u6084\u5730\u5728\u58c1\u4e0a\u9898\u4e86\u51e0\u884c\u5b57\uff0c\u5c31\u4e0b\u5c71\u53bb\u4e86\u3002 \u3000\u3000\u6613\u5170\u73e0\u5728\u5929\u5c71\u957f\u5927\uff0c\u719f\u8bc6\u9053\u8def\uff0c\u5979\u53d6\u9053\u8fbe\u6273\u57ce\u6cbf\u767d\u6768\u6cb3\u5cb8\u524d\u5f80\u5357\u7586\uff0c\u8d70\u4e86\u4e8c\u5341\u591a\u5929\uff0c\u5ffd \u89c9\u6c14\u5019\u6e10\u70ed\uff0c\u4e00\u7247\u6c99\u6f20\u6a2a\u4e98\u9762\u524d\uff0c\u5979\u77e5\u9053\u518d\u5f80\u524d\u8d70\uff0c\u5c31\u662f\u56de\u7586\u8457\u540d\u7684\u201c\u706b\u6d32\u201d\u5410\u9c81\u756a\u4e86\uff0c \u201c\u897f\u6e38\u8bb0\u201d\u4e2d\u7684\u706b\u7130\u5c71\uff0c\u5c31\u662f\u5728\u8fd9\u4e2a\u5730\u65b9\u3002\u6613\u5170\u73e0\u907f\u5f00\u6b63\u9762\uff0c\u4ece\u5410\u9c81\u756a\u897f\u9762\u7ed5\u8fc7\u3002\u4e00\u65e5\u6b63\u884c \u8def\u95f4\uff0c\u5ffd\u7136\u9635\u9635\u70ed\u98ce\uff0c\u522e\u5730\u800c\u6765\uff0c\u970e\u5ffd\u9ec4\u6c99\u6eda\u6eda\uff0c\u4e00\u7247\u70df\u96fe\uff0c\u50cf\u6c99\u6f20\u4e0a\u7a81\u7136\u5377\u8d77\u4e00\u5f20\u906e\u5929\u853d \u5730\u7684\u9ec4\u7ed2\u6be1\u5e55\u3002\u6613\u5170\u73e0\u6025\u5fd9\u8eb2\u5728\u4e00\u4e2a\u5c0f\u4e18\u540e\u9762\uff0c\u5c4f\u606f\u547c\u5438\uff0c\u65f6\u4e0d\u65f6\u7528\u624b\u62e8\u5f00\u5806\u79ef\u7684\u6d6e\u6c99\uff0c\u8fc7 \u4e86\u8bb8\u4e45\uff0c\u98ce\u6c99\u624d\u606f\uff01\u6613\u5170\u73e0\u63a2\u51fa\u5934\u6765\uff0c\u5ffd\u89c1\u5c0f\u4e18\u7684\u90a3\u4e00\u8fb9\uff0c\u7ad9\u7740\u56db\u6761\u5927\u6c49\uff0c\u90fd\u662f\u6ee1\u8eab\u9ec4\u571f\uff0c \u72fc\u72c8\u5f02\u5e38\uff01\u4e00\u4e2a\u7626\u5c0f\u7684\u6c49\u5b50\u6b63\u5411\u4ed6\u7684\u540c\u4f34\u95ee\u9053\uff1a\u201c\u4e1c\u6d1b\uff0c\u6211\u4eec\u8ff7\u4e86\u8def\uff0c\u4f60\u53ef\u8ba4\u5f97\u8def\u5417\uff1f\u201d\u90a3 \u4e2a\u53eb\u505a\u201c\u4e1c\u6d1b\u201d\u7684\u4eba\u62ab\u7740\u4e00\u4ef6\u5927\u6597\u7bf7\uff0c\u628a\u4e24\u53ea\u8033\u6735\u4e0e\u534a\u8fb9\u9762\u5b54\u5168\u90fd\u906e\u7740\u3002\u4ed6\u62ac\u8d77\u5934\u6765\uff0c\u671b\u4e86 \u4e00\u9635\uff0c\u53eb\u9053\uff1a\u201c\u82e6\u4e5f\uff01\u6c99\u6f20\u98ce\u66b4\uff0c\u5730\u5f62\u53d8\u6362\uff0c\u6211\u4e5f\u8ba4\u4e0d\u51fa\u8def\u4e86\uff0c\u597d\u5728\u6211\u4eec\u7684\u6c34\u56ca\u6ca1\u6709\u4e22\uff0c\u53ea \u597d\u62fc\u547d\u671d\u6700\u70ed\u7684\u5730\u65b9\u8d70\u53bb\uff0c\u8d70\u5230\u5410\u9c81\u756a\uff0c\u6211\u5c31\u8bc6\u8def\u4e86\u3002\u201d\u53e6\u4e00\u4e2a\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u9b3c\u5929\u6c14\uff0c\u4e00 \u65f6\u9177\u51b7\uff0c\u4e00\u65f6\u9177\u70ed\uff0c\u50cf\u8fd9\u822c\u708e\u70ed\uff0c\u6211\u4eec\u90a3\u70b9\u6c34\u53ea\u6015\u4e0d\u5230\u4e24\u5929\u5c31\u4f1a\u559d\u5b8c\uff0c\u5982\u4f55\u8fc7\u5f97\u706b\u7130\u5c71\uff1f\u201d \u6613\u5170\u73e0\u95fb\u58f0\u60f3\u8d77\uff0c\u4e00\u6478\u81ea\u5df1\u88c5\u76db\u5929\u5c71\u96ea\u6c34\u7684\u6c34\u56ca\uff0c\u5374\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u88ab\u6c99\u77f3\u522e\u4e86\u4e00\u4e2a\u5c0f\u6d1e\uff0c\u6c34 \u5168\u90fd\u6f0f\u5e72\u4e86\u3002 \u3000\u3000\u6613\u5170\u73e0\u8fd9\u4e00\u6025\u975e\u540c\u5c0f\u53ef\uff0c\u5728\u571f\u4e18\u540e\u4e00\u8dc3\u800c\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u8fc7\u8def\u7684\u5927\u54e5\uff0c\u4f60\u4eec\u8981\u53bb\u54ea\u91cc\uff1f\u6211 \u8ba4\u5f97\u8def\uff01\u201d\u6613\u5170\u73e0\u867d\u6ee1\u8eab\u9ec4\u571f\uff0c\u4f46\u5374\u63a9\u4e0d\u4f4f\u6e05\u4e3d\u7684\u5bb9\u989c\u3002\u56db\u6761\u5927\u6c49\u9661\u89c1\u6c99\u6f20\u4e4b\u4e2d\u51fa\u73b0\u5982\u6b64\u7f8e \u4e3d\u7684\u5c11\u5973\uff0c\u5168\u90fd\u5446\u4e86\uff0c\u90a3\u7626\u5c0f\u7684\u6c49\u5b50\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u4e3a\u4ec0\u4e48\u5355\u8eab\u5728\u5927\u6f20\u4e0a\u884c\u8d70\uff1f\u201d\u6613 \u5170\u73e0\u5fc3\u4e2d\u751f\u6c14\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u7ba1\u6211\u505a\u4ec0\u4e48\uff1f\u6211\u66ff\u4f60\u5e26\u8def\uff0c\u4f60\u628a\u6c34\u56ca\u7684\u6c34\u5206\u4e00\u70b9\u7ed9\u6211\uff0c\u5927\u5bb6 \u90fd\u6709\u597d\u5904\uff0c\u4f60\u4eec\u82e5\u4e0d\u613f\u610f\u5c31\u62c9\u5012\u3002\u6211\u81ea\u5df1\u4f1a\u53bb\u627e\u6c34\uff0c\u4f60\u4eec\u4e5f\u5c3d\u7ba1\u8d70\u4f60\u4eec\u7684\u8def\u3002\u201d\u4e00\u4e2a\u80a5\u5934\u5927 \u8033\u7684\u6c49\u5b50\u53eb\u9053\uff1a\u201c\u7740\u5440\uff0c\u6709\u8fd9\u6837\u6f02\u4eae\u7684\u59d1\u5a18\u5e26\u8def\u8fd8\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u59d1\u5a18\u4f60\u6e34\u4e86\u5417\uff0c\u6765\uff0c\u6765\uff01\u6211 \u8fd9\u5c31\u7ed9\u4f60\u559d\u6c34\u3002\u201d\u6613\u5170\u73e0\u77aa\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c\u5fc3\u60f3\u8fd9\u56db\u4eba\u4f3c\u4e4e\u4e0d\u662f\u4ec0\u4e48\u597d\u4eba\uff0c\u4f46\u81ea\u5df1\u4e00\u8eab\u6b66\u827a\uff0c \u5374\u4e5f\u4e0d\u6015\u4ed6\u4eec\u3002\u5f53\u4e0b\u6717\u7136\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5f7c\u6b64\u60a3\u96be\u76f8\u52a9\uff0c\u4f60\u522b\u4e71\u56bc\u820c\u5934\uff01\u201d\u5979\u5927\u5927\u65b9\u65b9\u5730\u628a\u80d6 \u5b50\u9012\u8fc7\u6765\u7684\u6c34\u559d\u4e86\u4e24\u53e3\uff0c\u6325\u6d51\u624b\u9053\uff1a\u201c\u597d\u4e86\uff01\u8d70\u5427\uff01\u201d \u3000\u3000\u8fd9\u56db\u4e2a\u4eba\u5168\u662f\u5927\u5185\u9ad8\u624b\uff0c\u90a3\u7626\u5c0f\u7684\u6c49\u5b50\u662f\u201c\u94c1\u7b14\u5224\u5b98\u201d\u6210\u5929\u633a\uff0c\u90a3\u4e2a\u88ab\u7740\u5927\u6597\u7bf7\u7684\u5374\u662f \u90b1\u4e1c\u6d1b\u3002\u90b1\u4e1c\u6d1b\u7ed9\u51cc\u672a\u98ce\u524a\u4e86\u4e24\u53ea\u8033\u6735\uff0c\u6015\u88ab\u65c1\u4eba\u770b\u89c1\u803b\u7b11\uff0c\u6240\u4ee5\u957f\u5e74\u56db\u5b63\u90fd\u62ab\u7740\u6597\u7bf7\u3002\u53e6 \u5916\u4e24\u4e2a\u662f\u6210\u5929\u633a\u7684\u526f\u624b\uff0c\u4e00\u4e2a\u53eb\u505a\u90d1\u5927\u951f\uff0c\u4e00\u4e2a\u53eb\u505a\u8fde\u4e09\u864e\u3002 \u3000\u3000\u5eb7\u7199\u662f\u4e00\u4e2a\u597d\u5927\u559c\u529f\u7684\u7687\u5e1d\uff0c\u5e73\u5b9a\u4e86\u5434\u4e09\u6842\u4e0e\u674e\u6765\u4ea8\u4e4b\u540e\uff0c\u4fbf\u60f3\u62d3\u571f\u5f00\u7586\uff0c\u7edf\u4e00\u8499\u85cf\u3002 \u4ed6\u53c8\u542c\u8bf4\u674e\u6765\u4ea8\u867d\u7136\u6b7b\u4e86\uff0c\u4ed6\u7684\u5f1f\u5f1f\u674e\u601d\u6c38\u5374\u4e0d\u5982\u4e0b\u843d\uff0c\u6709\u53ef\u80fd\u9003\u5165\u56de\u7586\uff0c\u56e0\u6b64\u4ed6\u56fe\u8c0b\u56de\u7586 \u8499\u85cf\u4e4b\u5fc3\u66f4\u6025\uff0c\u6210\u5929\u633a\u7b49\u56db\u4eba\u4fbf\u662f\u4ed6\u6d3e\u5165\u56de\u7586\u7684\u6b66\u58eb\uff0c\u4efb\u52a1\u662f\u63a2\u542c\u8fb9\u60c5\u4e0e\u4fa6\u67e5\u674e\u601d\u6c38\u7684\u4e0b \u843d\u3002 \u3000\u3000\u6613\u5170\u73e0\u4e00\u70b9\u4e5f\u4e0d\u77e5\u4ed6\u4eec\u7684\u6765\u5386\uff0c\u6cf0\u7136\u81ea\u82e5\u5730\u4e0e\u4ed6\u4eec\u540c\u884c\uff0c\u60ed\u4e1c\u6d1b\u4e00\u8def\u77a7\u7740\u5979\uff0c\u795e\u60c5\u9887\u4e3a \u602a\u5f02\uff0c\u80d6\u5b50\u90d1\u5927\u951f\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u90b1\u5927\u54e5\uff0c\u4f60\u4e0d\u662f\u7d2f\u6765\u4e0d\u559c\u6b22\u5a18\u513f\u7684\u5417\uff1f\u600e\u7684\u4eca\u5929\u7ed9\u5c0f\u72d0\u72f8\u8ff7 \u7740\u4e86\uff01\u201d\u6613\u5170\u73e0\u52c3\u7136\u5927\u6012\uff0c\u5ffd\u7136\u524d\u9762\u9ec4\u5c18\u6eda\u6eda\uff0c\u6709\u4e24\u9a91\u9a6c\u98de\u9a70\u800c\u6765\u3002\u6210\u5929\u633a\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4e24 \u4eba\u9a91\u672f\u600e\u5982\u6b64\u4e86\u5f97\uff1f\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u90a3\u4e24\u9a91\u9a6c\u5df1\u5230\u9762\u524d\u3002\u9a6c\u4e0a\u4eba\u4e00\u8dc3\u800c\u4e0b\uff0c\u4e00\u4e2a\u662f\u767d\u8863\u4e66\u751f\uff0c \u4e00\u4e2a\u662f\u7ea2\u886b\u5c11\u5973\uff0c\u4e00\u7ea2\u4e00\u767d\u76f8\u6620\u6210\u8da3\u3002\u6613\u5170\u73e0\u53c8\u60ca\u53c8\u559c\u3002\u8fd9\u7ea2\u8863\u5c11\u5973\u4e43\u662f\u767d\u53d1\u9b54\u5973\u7684\u5173\u95e8\u5f1f \u5b50\u6b66\u743c\u7476\uff0c\u51cc\u672a\u98ce\u4e0e\u6842\u4ef2\u660e\u5728\u62dc\u8c12\u6666\u660e\u7985\u5e08\u4e4b\u65f6\uff0c\u6240\u89c1\u7684\u5c31\u662f\u5979\u3002 \u3000\u3000\u6b66\u743c\u7476\u539f\u662f\u7ec8\u5357\u6d3e\u540d\u5bbf\u6b66\u5143\u82f1\u7684\u638c\u73e0\uff0c\u51cc\u672a\u98ce\u3001\u5218\u90c1\u82b3\u7b49\u4eba\u5927\u95f9\u4e94\u53f0\u5c71\u4e4b\u65f6\uff0c\u5c31\u662f\u5728\u6b66 \u5143\u82f1\u7684\u5bb6\u671b\u96c6\u4f1a\uff0c\u56e0\u6b64\u6b66\u743c\u7476\u8ba4\u5f97\u51cc\u672a\u98ce\u4e0e\u5192\u6d63\u83b2\u3002\u800c\u51cc\uff0c\u5192\u4e8c\u4eba\u5374\u7edd\u60f3\u4e0d\u5230\u5979\u4e5f\u4f1a\u5728\u5929 \u5c71\uff0c\u4ed3\u5352\u4e4b\u4e2d\uff0c\u4e24\u4eba\u90fd\u8ba4\u4e0d\u51fa\u7ea2\u8863\u5c11\u5973\u5c31\u662f\u5979\u3002 \u3000\u3000\u5f53\u65e5\u5927\u95f9\u4e94\u53f0\u5c71\u4e4b\u540e\uff0c\u7fa4\u96c4\u5206\u6563\uff0c\u6b66\u5143\u82f1\u7236\u5973\u539f\u662f\u7559\u5728\u5c71\u897f\u7684\uff0c\u540e\u6765\u56e0\u4e3a\u98ce\u58f0\u65e5\u7d27\uff0c\u5728 \u5c71\u897f\u7ad9\u4e0d\u4f4f\u811a\uff0c\u8f97\u8f6c\u5230\u4e86\u56de\u7586\u3002\u6b66\u5143\u82f1\u5e26\u6b66\u743c\u7476\u4e0a\u5929\u5c71\u8c12\u89c1\u6666\u660e\u7985\u5e08\uff0c\u4e0d\u6599\u521a\u5230\u534a\u5c71\uff0c\u5c31\u78b0 \u89c1\u767d\u53d1\u9b54\u5973\uff0c\u767d\u53d1\u9b54\u5973\u4e00\u89c1\u6b66\u743c\u7476\u5c31\u559c\u6b22\u4e86\u5979\uff0c\u4f38\u624b\u4fbf\u8981\u6b66\u5143\u82f1\u628a\u5973\u513f\u9001\u7ed9\u5979\u505a\u5f92\u5f1f\u3002\u6b66\u5143 \u82f1\u4e0d\u77e5\u5979\u7684\u6765\u5386\uff0c\u5979\u5fae\u5fae\u4e00\u7b11\uff0c\u628a\u5d16\u77f3\u968f\u624b\u6293\u4e0b\u4e00\u5757\uff0c\u634f\u6210\u7c89\u788e\uff0c\u7b11\u9053\uff1a\u201c\u7ec8\u5357\u6d3e\u4e0e\u6b66\u5f53\u6d3e \u751a\u6709\u6e0a\u6e90\uff0c\u4f60\u96be\u9053\u8fde\u767d\u53d1\u9b54\u5973\u7684\u540d\u5934\u4e5f\u6ca1\u542c\u8fc7\u5417\uff1f\u201d\u6b66\u5143\u82f1\u4e00\u542c\u624d\u77e5\u9762\u524d\u7684\u8001\u5a46\u5a46\uff0c\u4fbf\u662f\u4e0e \u5df1\u7684\u4e00\u8f88\u6b66\u5f53\u6d3e\u638c\u95e8\u4eba\u5353\u4e00\u822a\u6709\u8fc7\u7ea0\u7eb7\u7684\u767d\u53d1\u9b54\u5973\uff0c\u4ed6\u542c\u5e08\u957f\u8bf4\u8d77\uff0c\u767d\u53d1\u9b54\u5973\u5f53\u5e74\u4e3a\u4e86\u5353\u4e00 \u822a\uff0c\u66fe\u6253\u8d25\u6b66\u5f53\u4e94\u8001\u7684\u56f4\u653b\uff0c\u8fde\u5353\u4e00\u822a\u7684\u5e08\u53d4\u90fd\u7ed9\u5979\u4f24\u4e86\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u4e16\u6240\u7f55\u89c1\uff01\u53ea\u662f\u63a8\u7b97 \u5e74\u4ee3\uff0c\u5979\u5df2\u662f\u767e\u5c81\u4e4b\u4eba\uff0c\u6b66\u5143\u82f1\u771f\u6599\u4e0d\u5230\u5979\u8fd8\u6d3b\u5728\u4e16\u4e0a\u3002 \u3000\u3000\u6b66\u743c\u7476\u5e73\u65e5\u4e5f\u542c\u7236\u4eb2\u8bf4\u8fc7\u767d\u53d1\u9b54\u5973\u7684\u6545\u4e8b\uff0c\u5982\u4eca\u4e00\u542c\u8fd9\u5dfe\u5e3c\u4e2d\u5e76\u4e16\u5143\u4e8c\u7684\u5973\u4eba\uff0c\u613f\u6536\u5979 \u505a\u5f92\u5f1f\uff0c\u5927\u559c\u53e9\u8c22\uff0c\u5148\u81ea\u80af\u4e86\uff0c\u53ea\u662f\u6b66\u5143\u82f1\u4f9d\u4f9d\u4e0d\u820d\u3002\u767d\u53d1\u9b54\u5973\u9053\uff1a\u201c\u6211\u53ea\u8981\u5979\u8ddf\u6211\u4e09\u5e74\u5c31 \u884c\u4e86\uff0c\u6211\u6559\u5f92\u5f1f\u4e0e\u522b\u4eba\u4e0d\u540c\uff0c\u6211\u6559\u4e09\u5e74\u5f53\u5f97\u522b\u4eba\u6559\u4e09\u5341\u5e74\uff0c\u8fc7\u4e86\u4e09\u5e74\uff0c\u6211\u5c31\u653e\u5979\u56de\u6765\u8ddf \u4f60\u3002\u201d \u3000\u3000\u767d\u53d1\u9b54\u5973\u66ae\u5e74\u6536\u5f92\uff0c\u6b66\u743c\u7476\u53c8\u806a\u660e\u53c8\u6dd8\u6c14\uff0c\u975e\u5e38\u61c2\u5f97\u54c4\u5979\u6b22\u559c\uff0c\u767d\u53d1\u9b54\u5973\u628a\u5979\u5b9d\u8d1d\u5f97\u4e86 \u4e0d\u5f97\uff0c\u628a\u72ec\u95e8\u5251\u6cd5\u6089\u5fc3\u4f20\u6388\u4e8e\u5979\uff0c\u7528\u836f\u7269\u4e4b\u529b\uff0c\u7ed9\u5979\u51b6\u5143\u56fa\u672c\uff0c\u679c\u7136\u5728\u4e09\u5e74\u4e4b\u4e2d\uff0c\u628a\u5979\u8c03\u6559 \u5f97\u975e\u5e38\u51fa\u8272\u3002\u9664\u4e86\u529f\u529b\u7a0d\u5dee\u4e4b\u5916\uff0c\u8bba\u5251\u6cd5\u4e0d\u5728\u98de\u7ea2\u5dfe\u4e4b\u4e0b\u3002\u6b66\u743c\u7476\u4e5f\u5e38\u5230\u5929\u90fd\u5cf0\u627e\u98de\u7ea2\u5dfe\u6e38 \u73a9\uff0c\u56e0\u6b64\u8ba4\u5f97\u6613\u5170\u73e0\u3002 \u3000\u3000\u90a3\u767d\u9762\u4e66\u751f\u6b63\u662f\u674e\u601d\u6c38\uff0c\u4ed6\u5728\u6e05\u5175\u56f4\u527f\u4e4b\u4e0b\uff0c\u62fc\u6b7b\u51b2\u51fa\uff0c\u5085\u9752\u4e3b\u3001\u5218\u90c1\u82b3\u3001\u77f3\u5929\u6210\u7236 \u5973\u3001\u97e9\u8346\u7b49\u4eba\u4ed7\u7740\u4e00\u8eab\u6b66\u827a\uff0c\u4e5f\u90fd\u8131\u51fa\u8eab\u6765\u3002\u53ea\u6709\u97e9\u8346\u7684\u76df\u5144\u5f1f\u6731\u5929\u6728\u6768\u9752\u6ce2\u5374\u4e0d\u5e78\u6218\u6b7b\u3002 \u674e\u601d\u6c38\u548c\u5085\u9752\u4e3b\u7b49\u5341\u591a\u9a91\uff0c\u81ea\u56db\u5ddd\u897f\u8d70\uff0c\u8f97\u8f6c\u5230\u4e86\u56de\u7586\uff0c\u8fd9\u4e00\u65e5\u9aa4\u9047\u6c99\u6f20\u98ce\u66b4\uff0c\u674e\u601d\u6c38\u9a91\u7684 \u662f\u4e00\u5339\u9ec4\u9a8f\u9a6c\uff0c\u672a\u66fe\u8d70\u8fc7\u6c99\u6f20\uff0c\u7ed9\u98ce\u6c99\u6240\u5413\uff0c\u957f\u5636\u72c2\u5954\uff0c\u75be\u903e\u95ea\u7535\uff0c\u79bb\u7fa4\u8d70\u6563\u3002\u674e\u601d\u6c38\u867d\u7136 \u662f\u4e00\u8eab\u6b66\u827a\uff0c\u5374\u4e0d\u61c2\u5f97\u5e94\u4ed8\u98ce\u6c99\u4e4b\u6cd5\uff0c\u7126\u6025\u95f4\uff0c\u5ffd\u7136\u659c\u523a\u91cc\u4e00\u9a91\u9a6c\u51b2\u6765\uff0c\u4e00\u4e2a\u7ea2\u8863\u5c11\u5973\u4e0e\u4ed6 \u64e6\u8eab\u800c\u8fc7\uff0c\u7275\u7740\u674e\u601d\u6c38\u7684\u8863\u8896\u9053\uff1a\u201c\u5feb\u8eb2\u5728\u9a6c\u8179\u4e4b\u4e0b\uff0c\u987a\u7740\u98ce\u8dd1\uff01\u201d\u674e\u601d\u6c38\u6b63\u611f\u98ce\u6c99\u522e\u9762\uff0c \u4e24\u773c\u96be\u7741\uff0c\u6d51\u8eab\u6c14\u529b\u4e5f\u6e10\u6d88\u5931\uff0c\u88ab\u5c11\u5973\u63d0\u9192\uff0c\u4e00\u7ffb\u8eab\u5012\u60ac\u9a6c\u8179\uff0c\u548c\u5c11\u5973\u5e76\u8f94\u98de\u9a70\uff0c\u8fc7\u4e86\u8bb8 \u4e45\uff0c\u98ce\u6c99\u624d\u606f\uff0c\u4e24\u4eba\u7ffb\u4e0a\u9a6c\u80cc\uff0c\u674e\u601d\u6c38\u5411\u5979\u9053\u8c22\uff0c\u95ee\u9053\uff1a\u201c\u59d1\u5a18\u5e08\u95e8\uff0c\u53ef\u80af\u8d50\u6559\uff1f\u201d\u7ea2\u8863\u5c11 \u5973\u5a07\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u5e08\u95e8\u5440\u4e0d\u5e08\u95e8\uff0c\u6211\u4e00\u70b9\u4e5f\u4e0d\u61c2\u3002\u201d\u674e\u601d\u6c38\u9053\uff1a\u201c\u59d1\u5a18\u9a91\u672f\u7cbe\u7edd\uff0c\u90a3\u4e00\u5b9a\u662f \u61c2\u6b66\u827a\u7684\u4e86\uff01\u201d\u7ea2\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u6211\u4eec\u5728\u8349\u539f\u4e0a\u8ba8\u751f\u6d3b\uff0c\u4e0d\u61c2\u9a91\u9a6c\u8fd8\u884c\u4e48\uff1f\u81f3\u4e8e\u4ec0\u4e48\u6b66\u827a\uff0c \u90a3\u6211\u53ef\u5168\u4e0d\u61c2\u4e86\u3002\u201d\u7ea2\u8863\u5c11\u5973\u5a07\u5c0f\u73b2\u73d1\uff0c\u660e\u8273\u7167\u4eba\uff0c\u5410\u6c14\u5982\u5170\uff0c\u5a40\u5a1c\u4f5c\u6001\uff0c\u674e\u601d\u6c38\u4e0d\u89c9\u5fc3 \u9189\uff0c\u4ee5\u4e3a\u5979\u771f\u662f\u8349\u539f\u7267\u6c11\u7684\u5973\u513f\uff0c\u7adf\u77a7\u4e0d\u51fa\u5979\u8eab\u6000\u7edd\u6280\u3002\u90a3\u7ea2\u8863\u5c11\u5973\u95ee\u9053\uff1a\u201c\u516c\u5b50\u8fd9\u822c\u53d1 \u95ee\uff0c\u60f3\u5fc5\u662f\u7cbe\u901a\u6b66\u827a\u7684\u4e86\uff01\u201d\u674e\u601d\u6c38\u9053\uff1a\u201c\u5b66\u8fc7\u51e0\u624b\u7c97\u6d45\u7684\u529f\u592b\u3002\u201d\u7ea2\u8863\u5c11\u5973\u9053\uff1a\u201c\u6211\u8981\u5230 \u5410\u9c81\u756a\u9644\u8fd1\u7684\u53f6\u5c14\u7f8c\u53bb\uff0c\u516c\u5b50\u61c2\u5f97\u6b66\u827a\u90a3\u597d\u6781\u4e86\uff0c\u80fd\u4e0d\u80fd\u966a\u6211\u8d70\u4e00\u7a0b\u5462\uff1f\u6211\u771f\u5bb3\u6015\uff01\u201d\u674e\u601d \u6c38\u5947\u9053\uff0c\u201c\u6015\u4ec0\u4e48\u5462\uff1f\u8349\u539f\u4e0a\u6709\u5f3a\u76d7\u5417\uff1f\u201d\u7ea2\u8863\u5c11\u5973\u9053\uff1a\u201c\u5f3a\u76d7\u5012\u662f\u6ca1\u6709\u3002\u53ea\u662f\u6700\u8fd1\u6709\u8bb8\u591a \u6ee1\u6d32\u6b66\u58eb\u8dd1\u5230\u54b1\u4eec\u7684\u8349\u539f\u6765\u4e71\u95ef\uff0c\u4e3a\u975e\u4f5c\u6b79\uff0c\u6bd4\u5f3a\u76d7\u8fd8\u51f6\u3002\u201d\u674e\u601d\u6c38\u6012\u9053\uff1a\u201c\u82e5\u6211\u78b0\u7740\u4ed6 \u4eec\uff0c\u4e00\u5b9a\u628a\u4ed6\u4eec\u7684\u72d7\u817f\u6253\u6298\uff01\u201d\u7ea2\u8863\u5c11\u5973\u9053\uff1a\u201c\u4ed6\u4eec\u5f88\u5389\u5bb3\u554a\uff0c\u516c\u5b50\u6210\u5417\uff1f\u201d\u674e\u601d\u6c38\u9053\uff1a \u201c\u8fd9\u4e9b\u6b66\u58eb\u5341\u4e2a\u516b\u4e2a\u6211\u8fd8\u5bf9\u4ed8\u5f97\u4e86\u3002\u59d1\u5a18\u4e0d\u8981\u5bb3\u6015\uff0c\u6211\u548c\u670b\u53cb\u4eec\u51c6\u5907\u5230\u5357\u7586\u7684\u838e\u8f66\uff0c\u8981\u7ecf\u8fc7 \u53f6\u5c14\u7f8c\uff0c\u6211\u5c31\u966a\u59d1\u5a18\u5230\u90a3\u91cc\u53bb\u597d\u4e86\u3002\u201d\u674e\u601d\u6c38\u4e0d\u77e5\u6e05\u5ef7\u6d3e\u5230\u56de\u7586\u7684\u90fd\u662f\u4e00\u6d41\u597d\u624b\uff0c\u4ed6\u53ea\u4ee5\u4e3a \u662f\u4e00\u822c\u6b66\u58eb\uff0c\u6240\u4ee5\u6beb\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u90a3\u7ea2\u8863\u5c11\u5973\u6b63\u662f\u6b66\u743c\u7476\uff0c\u5979\u6cbf\u8def\u53d1\u73b0\u6210\u5929\u633a\u4ed6\u4eec\u7684\u8e2a\u8ff9\uff0c \u5df2\u6697\u81ea\u8ddf\u4e86\u4e00\u7a0b\uff0c\u77e5\u9053\u4ed6\u4eec\u6b66\u529f\u9887\u9ad8\uff0c\u4e0d\u6562\u5355\u72ec\u52a8\u624b\u3002\u542c\u4e86\u674e\u601d\u6c38\u7684\u8bdd\uff0c\u5fae\u5fae\u4e00\u7b11\u3002\u5979\u4e0b\u5c71 \u4e4b\u540e\uff0c\u5148\u89c1\u8fc7\u8001\u7236\uff0c\u8fd9\u6b21\u4fbf\u662f\u5949\u8001\u7236\u6b66\u5143\u82f1\u4e4b\u547d\u53bb\u8fce\u63a5\u674e\u601d\u6c38\u3001\u5085\u9752\u4e3b\u4ed6\u4eec\u7684\uff0c\u5979\u867d\u6ca1\u89c1\u8fc7 \u674e\u601d\u6c38\uff0c\u53ef\u662f\u4e34\u884c\u524d\u66fe\u95ee\u6e05\u76f8\u8c8c\uff0c\u4e5d\u6210\u6599\u5230\u8fd9\u767d\u9762\u4e66\u751f\u662f\u674e\u601d\u6c38\uff0c\u5fc3\u60f3\u6b66\u6797\u4e2d\u4eba\u90fd\u79f0\u8d5e\u674e\u516c \u5b50\u6587\u6b66\u5168\u6750\uff0c\u6211\u5012\u8981\u9017\u4ed6\u4e00\u4e0b\u3002 \u3000\u3000\u6b66\u743c\u7476\u6709\u4e00\u642d\u6ca1\u4e00\u642d\u5730\u9017\u674e\u601d\u6c38\u95f2\u8bdd\uff0c\u95ee\u9053\uff1a\u201c\u6211\u4eec\u5929\u5c71\u4e00\u5e26\uff0c\u4ee5\u524d\u6709\u4e00\u4e2a\u6768\u4e91\u9aa2\u5927\u4fa0 \u5e2e\u6211\u4eec\u6253\u8fc7\u6e05\u5175\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d\u674e\u601d\u6c38\u7b11\u9053\uff1a\u201c\u6768\u5927\u4fa0\u65e9\u5c31\u6b7b\u4e86\uff0c\u6211\u8ba4\u8bc6\u4ed6\u7684\u5e08\u5f1f\u51cc\u672a \u98ce\u3002\u201d\u6b66\u743c\u7476\u9053\uff1a\u201c\u674e\u516c\u5b50\u7684\u6b66\u827a\u6bd4\u4ed6\u4eec\u5982\u4f55\uff1f\u201d\u674e\u601d\u6c38\u53c8\u7b11\u9053\uff1a\u201c\u51cc\u672a\u98ce\u7684\u5251\u6cd5\u72ec\u6b65\u6d77 \u5185\uff0c\u6211\u5982\u4f55\u6bd4\u5f97\u4e0a\uff1f\u59d1\u5a18\uff0c\u6b66\u529f\u8fd9\u4e1c\u897f\u5965\u5999\u5f97\u5f88\uff0c\u6211\u4e5f\u8bf4\u4e0d\u6e05\u695a\u3002\u201d\u6b66\u743c\u7476\u6545\u610f\u8bf4\u4e9b\u5b69\u5b50\u6c14 \u7684\u8bdd\uff0c\u9017\u674e\u601d\u6c38\u8c08\u8bba\u6b66\u827a\uff0c\u674e\u601d\u6c38\u771f\u7684\u628a\u5979\u5f53\u6210\u4e0d\u61c2\u4e8b\u7684\u5973\u5b69\u5b50\uff0c\u548c\u5979\u8c08\u5f97\u5f88\u5f00\u5fc3\u3002\u4e24\u4eba\u4e0d \u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u8d70\u4e86\u4e00\u5927\u6bb5\u8def\uff0c\u548c\u90b1\u4e1c\u6d1b\u7b49\u4eba\u5728\u6c99\u6f20\u66ae\u7136\u76f8\u9022\u3002 \u3000\u3000\u6613\u5170\u73e0\u9aa4\u89c1\u6b66\u743c\u7476\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u6b63\u60f3\u62db\u547c\uff0c\u6b66\u743c\u7476\u5ffd\u7136\u6253\u4e2a\u62db\u547c\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u54ce\u54df\uff01 \u6c99\u6f20\u4e0a\u51fa\u73b0\u5929\u4ed9\u4e86\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u600e\u957f\u5f97\u8fd9\u6837\u7f8e\u554a\uff01\u201d\u8fb9\u8bf4\u8fb9\u53bb\u62c9\u6613\u5170\u73e0\u7684\u624b\u3002\u6613\u5170\u73e0\u4e5f \u662f\u673a\u7075\u7684\u4eba\uff0c\u867d\u7136\u4e0d\u77e5\u5979\u846b\u82a6\u91cc\u5356\u4ec0\u4e48\u836f\uff0c\u4f46\u5374\u61c2\u5f97\u5979\u7684\u610f\u601d\uff0c\u4e0d\u613f\u5728\u964c\u751f\u4eba\u524d\u76f8\u8ba4\u3002\u4e8e\u662f \u4e5f\u62c9\u5979\u7684\u624b\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u771f\u53eb\u6211\u5f00\u4e86\u773c\u754c\u4e86\uff0c\u597d\u5728\u8fd9\u91cc\u4e0d\u662f\u5f00\u2018\u53fc\u7f8a\u2019\u5927\u4f1a\uff0c\u5426\u5219\u7537\u5b69\u5b50 \u4eec\u90fd\u8981\u9a91\u9a6c\u8ffd\u4f60\u4e86\u3002\u201d\u201c\u53fc\u7f8a\u201d\u662f\u56de\u7586\u5404\u65cf\u6d41\u884c\u7684\u4e00\u79cd\u6e38\u620f\uff0c\u7537\u5973\u4e92\u76f8\u9a91\u9a6c\u8ffd\u9010\uff0c\u5973\u7684\u9053\u5230 \u7537\u7684\uff0c\u53ef\u4ee5\u7528\u97ad\u62bd\u6253\u4ed6\uff0c\u6709\u4e24\u53e5\u8bd7\u9053\uff1a\u201c\u59d1\u5a18\u9a91\u9a8f\u9a6c\uff0c\u957f\u97ad\u6253\u6240\u6b22\u201d\u6240\u8bf4\u7684\u5c31\u662f\u8fd9\u79cd\u201c\u53fc \u7f8a\u201d\u6e38\u620f\u3002\u6b66\u743c\u7476\u548c\u674e\u601d\u6c38\u5e76\u8f94\u5954\u9a70\uff0c\u72b6\u82e5\u8ffd\u9010\uff0c\u6240\u4ee5\u6613\u5170\u73e0\u6545\u610f\u7528\u8bdd\u53d6\u7b11\u5979\u3002\u6b66\u743c\u7476\u5012\u4e0d \u5728\u4e4e\uff0c\u674e\u601d\u6c38\u5219\u6ee1\u9762\u901a\u7ea2\u4e86\uff0c\u4ed6\u8fdb\u5165\u56de\u7586\uff0c\u61c2\u5f97\u201c\u53fc\u7f8a\u201d\u7684\u610f\u601d\uff0c\u5fc3\u60f3\uff1a\u201c\u600e\u4e48\u8349\u539f\u4e0a\u7684\u5973 \u5b69\u5b50\uff0c\u53e3\u8fd9\u6837\u6ca1\u906e\u62e6\uff0c\u80e1\u4e71\u62ff\u4eba\u53d6\u7b11\u3002\u201d\u674e\u601d\u6c38\u672c\u6765\u662f\u4e2a\u5149\u660e\u78ca\u843d\u7684\u82f1\u96c4\uff0c\u4e00\u5411\u6ca1\u6709\u7537\u5973\u4e4b \u89c1\uff0c\u53ef\u662f\u4ed6\u5bf9\u6b66\u743c\u7476\u6697\u91cc\u52a8\u60c5\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u672a\u89c9\u5bdf\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u5c31\u663e\u5f97\u6bd4\u5e73\u65f6\u654f\u611f\u8bb8 \u591a\u3002 \u3000\u3000\u518d\u8bf4\u90b1\u4e1c\u6d1b\u4ee5\u524d\u5728\u4e91\u5357\u629a\u4ed9\u6e56\u6ee8\uff0c\u66fe\u548c\u674e\u601d\u6c38\u89c1\u8fc7\u4e00\u9762\uff0c\u4ed6\u5de6\u8fb9\u90a3\u53ea\u8033\u6735\u5c31\u662f\u90a3\u6b21\u7ed9\u51cc \u672a\u98ce\u5272\u4e0b\u6765\u7684\u3002\u4e09\u5e74\u4e0d\u89c1\uff0c\u674e\u601d\u6c38\u5e76\u6ca1\u6709\u4ec0\u4e48\u6539\u53d8\uff0c\u90b1\u4e1c\u6d1b\u4e24\u53ea\u8033\u6735\u88ab\u5272\uff0c\u9762\u4e0a\u53c8\u88ab\u51cc\u672a\u98ce \u5212\u4e86\u4e24\u5200\uff0c\u957f\u5e74\u62ab\u7740\u6597\u7bf7\uff0c\u674e\u601d\u6c38\u4e00\u773c\u5374\u770b\u4e0d\u51fa\u4ed6\u662f\u8c01\u6765\u3002 \u3000\u3000\u90b1\u4e1c\u6d1b\u8ba4\u51fa\u674e\u601d\u6c38\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u60f3\uff1a\u771f\u662f\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\uff01\u4ed6\u771f\u7684 \u9003\u5230\u56de\u7586\u6765\uff0c\u53c8\u649e\u5728\u6211\u7684\u624b\u4e0a\uff0c\u771f\u662f\u4e0a\u5929\u4fdd\u4f51\uff0c\u53eb\u6211\u7acb\u6b64\u5927\u529f\u3002\u4f46\u4ed6\u77e5\u9053\u674e\u601d\u6c38\u6b66\u827a\u4e0d\u6bd4\u5bfb \u5e38\uff0c\u5355\u6253\u72ec\u6597\uff0c\u8fd8\u4e0d\u60e7\u4ed6\uff0c\u53ea\u662f\u4e00\u6253\u8d77\u6765\uff0c\u5fc5\u5b9a\u662f\u6027\u547d\u76f8\u640f\uff0c\u8981\u64d2\u4ed6\u5374\u4e0d\u5bb9\u6613\u3002\u5f53\u4e0b\u7528\u4e86\u4ed6 \u4eec\u5708\u5185\u7684\u6697\u8bed\uff0c\u544a\u8bc9\u6210\u5929\u633a\u7b49\u4eba\u77e5\u9053\uff1a\u8fd9\u767d\u9762\u4e66\u751f\u5c31\u662f\u674e\u601d\u6c38\uff0c\u53eb\u4ed6\u4eec\u6697\u4e2d\u51c6\u5907\uff0c\u4e25\u5bc6\u6212 \u5907\uff0c\u4e00\u58f0\u4eca\u4e0b\uff0c\u5c31\u8981\u628a\u4ed6\u6d3b\u6349\u3002\u6b66\u743c\u7476\u542c\u4ed6\u4eec\u8bf4\u9ed1\u8bdd\uff0c\u53ea\u662f\u563b\u563b\u5730\u7b20\u7b11\u3002 \u3000\u3000\u6613\u5170\u73e0\u89c1\u90b1\u4e1c\u6d1b\u5077\u5077\u76ef\u7740\u674e\u601d\u6c38\uff0c\u5fc3\u60f3\u8fd9\u4eba\u771f\u602a\uff0c\u770b\u4eba\u5982\u6b64\u6ca1\u6709\u793c\u8c8c\uff0c\u4e5f\u7741\u5927\u773c\u775b\u770b \u4ed6\u3002\u90b1\u4e1c\u6d1b\u76ee\u5149\u548c\u5979\u78b0\u4e2a\u6b63\u7740\uff0c\u5ffd\u7136\u8bb0\u8d77\u4e00\u4eba\uff0c\u5927\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u662f\u6768\u4e91\u9aa2\u7684\u4ec0\u4e48\u4eba\uff1f\u201d\u6613\u5170 \u73e0\u50b2\u7136\u7b54\u9053\uff1a\u201c\u5173\u4f60\u4ec0\u4e48\u4e8b\uff1f\u201d\u674e\u601d\u6c38\u7a81\u7136\u8df3\u8d77\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u8fd9\u53ae\u5728\u51cc\u672a\u98ce\u5251\u4e0b\u4fa5\u5e78\u9003 \u751f\uff0c\u8fd8\u6562\u5728\u6b64\u4f5c\u6076\u3002\u201d\u674e\u601d\u6c38\u806a\u660e\u8fc7\u4eba\uff0c\u8bb0\u6027\u6781\u597d\uff0c\u4ed6\u867d\u56e0\u90b1\u4e1c\u6d1b\u9762\u8c8c\u53d8\u5f02\u8ba4\u4e0d\u51fa\u6765\uff0c\u4f46\u4e00 \u542c\u58f0\u97f3\uff0c\u5374\u66ae\u7136\u8bb0\u8d77\u3002\u90b1\u4e1c\u6d1b\u5728\u629a\u4ed9\u6e56\u8fb9\u5411\u51cc\u672a\u98ce\u6311\u6218\u65f6\uff0c\u8bdd\u8bf4\u5f97\u5f88\u96be\u542c\uff0c\u674e\u601d\u6c38\u5f53\u65f6\u5728\u65c1 \u7ec6\u542c\uff0c\u5bf9\u4ed6\u7684\u53e3\u97f3\u6709\u5f88\u6df1\u523b\u7684\u5370\u8c61\u3002 \u3000\u3000\u90b1\u4e1c\u6d1b\u8fd8\u672a\u7b54\u8bdd\uff0c\u6210\u5929\u633a\u53cc\u7b14\u5df2\u55d6\u5730\u62d4\u51fa\uff0c\u5728\u674e\u601d\u6c38\u9762\u524d\u4e00\u7ad9\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u674e\u516c\u5b50\u5e78 \u4f1a\u5e78\u4f1a\uff01\u516c\u5b50\u5341\u4e07\u5927\u519b\uff0c\u4e00\u671d\u74e6\u89e3\uff0c\u8f97\u8f6c\u4e07\u91cc\uff0c\u6c99\u6f20\u9003\u8352\uff0c\u8fd9\u771f\u662f\u4f55\u82e6\u6765\u54c9\uff01\u4e0d\u5982\u968f\u6211\u4eec\u8fdb \u4eac\uff0c\u5f52\u987a\u4eca\u5723\uff0c\u7687\u4e0a\u5b9a\u4f1a\u5f00\u6069\uff0c\u7ed9\u516c\u5b50\u4e00\u5b98\u534a\u804c\u3002\u201d\u674e\u601d\u6c38\u9762\u8272\u500f\u53d8\uff0c\u4e24\u67c4\u6d41\u661f\u9524\u4e5f\u81ea\u8170\u95f4 \u89e3\u51fa\uff0c\u6309\u4ed6\u7684\u6027\u683c\uff0c\u672c\u5c31\u4e0d\u8010\u70e6\u542c\u5b8c\u6210\u5929\u633a\u7684\u8bf4\u8bdd\uff0c\u4f46\u4ed6\u987e\u7740\u65c1\u8fb9\u201c\u4e0d\u61c2\u6b66\u827a\u201d\u7684\u6b66\u743c\u7476\uff0c \u62c5\u5fc3\u6df7\u6218\uff0c\u4f1a\u4ee4\u5979\u65e0\u8f9c\u53d7\u4f24\uff0c\u5f53\u4e0b\u7709\u5934\u4e00\u76b1\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u51b2\u7740\u6211\u6765\u7684\uff0c\u662f\u4e0d \u662f\uff1f\u201d\u6210\u5929\u633a\u563b\u563b\u7b11\u9053\uff1a\u201c\u674e\u516c\u5b50\u6599\u5f97\u4e0d\u9519\u3002\u201d\u674e\u601d\u6c38\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4e0d\u5fc5\u591a\u8d39\u5507 \u820c\uff0c\u4f60\u4eec\u5c31\u90fd\u4e0a\u6765\u52a8\u624b\u5427\u3002\u8bdd\u8bf4\u660e\u5728\u5148\uff0c\u8fd9\u4e24\u4f4d\u59d1\u5a18\u90fd\u4e0d\u662f\u548c\u6211\u4e00\u8def\uff0c\u4f60\u4eec\u65e2\u53ea\u662f\u51b2\u7740\u6211 \u6765\uff0c\u5c31\u4e0d\u5e94\u4e3a\u96be\u5979\u4eec\uff0c\u6211\u82e5\u8f93\u7ed9\u4f60\u4eec\uff0c\u7518\u613f\u675f\u624b\u5c31\u7f1a\uff01\u201d\u6210\u5929\u633a\u7fd8\u8d77\u62c7\u6307\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u674e \u516c\u5b50\u5feb\u4eba\u5feb\u8bed\uff0c\u4e0d\u5f97\u53cd\u4fae\uff01\u201d\u5f53\u4e0b\u62db\u547c\u90b1\u4e1c\u6d1b\u9053\uff1a\u201c\u5582\uff0c\u4f60\u548c\u90a3\u4f4d\u59d1\u5a18\u8bf4\u4e9b\u4ec0\u4e48\u5440\uff0c\u6709\u8fd9\u4e48 \u591a\u8bdd\u8bf4\uff1f\u8fc7\u6765\u505a\u4e2a\u8bc1\u4eba\u5427\u3002\u201d\u4e5f\u4e0d\u77e5\u90b1\u4e1c\u6d1b\u521a\u5bf9\u624d\u8bf4\u4e86\u4ec0\u4e48\uff0c\u6613\u5170\u73e0\u6012\u9053\uff1a\u201c\u4f60\u6562\u8fb1\u9a82\u6211\u7238 \u7238\uff01\u201d\u5b9d\u5251\u51fa\u624b\uff0c\u5587\u7684\u4e00\u5251\u523a\u53bb\uff0c\u90b1\u4e1c\u6d1b\u4e00\u8dc3\u907f\u5f00\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5929\u633a\u5144\uff0c\u6211\u4eec\u53e6\u6709\u8fc7\u8282\uff0c \u5979\u662f\u6211\u4ec7\u4eba\u7684\u5973\u513f\uff01\u201d\u6613\u5170\u73e0\u4e5f\u53eb\u9053\uff1a\u201c\u4f7f\u6d41\u661f\u9524\u7684\u90a3\u4f4d\u5927\u54e5\uff0c\u6211\u9886\u4f60\u7684\u60c5\u4e86\uff01\u4f60\u6253\u4f60\u7684\uff0c \u6211\u6253\u6211\u7684\uff01\u201d \u3000\u3000\u6210\u5929\u633a\u89c1\u6613\u5170\u73e0\u90a3\u4e00\u5251\u51fa\u624b\u5f88\u5feb\uff0c\u9887\u611f\u8be7\u5f02\uff0c\u9065\u5bf9\u6b66\u743c\u7476\u6253\u4e2a\u62db\u547c\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4e5f\u8981 \u52a8\u624b\uff0c\u4f60\u4eec\u4e09\u4eba\uff0c\u6211\u4eec\u4e5f\u51fa\u4e09\u4eba\u597d\u4e86\uff01\u201d\u6b66\u743c\u7476\u6447\u5934\u9053\uff1a\u201c\u54ce\u54df\uff0c\u6211\u4e0d\u61c2\u6253\u67b6\u7684\uff01\u201d\u674e\u601d\u6c38 \u9053\uff1a\u201c\u4f60\u5feb\u8d70\u5427\uff0c\u54b1\u4eec\u540e\u4f1a\u6709\u671f\u3002\u201d\u6b66\u743c\u7476\u5a07\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u6253\u67b6\uff0c\u6211\u5374\u559c\u6b22\u770b\u6253\u67b6\uff0c\u53c8\u6709 \u5200\u53c8\u6709\u5251\u8fd8\u6709\u94dc\u9524\uff0c\u54c8\uff0c\u4e00\u5b9a\u5f88\u597d\u770b\u5440\uff01\u201d\u5979\u4e0d\u4f46\u4e0d\u8d70\uff0c\u53cd\u800c\u5b89\u5b89\u7a33\u7a33\u5730\u5750\u4e86\u4e0b\u6765\uff0c\u6258\u7740\u9999 \u816e\u89c2\u6218\uff0c\u7b11\u9053\uff1a\u201c\u8c01\u6405\u4e71\u6211\u770b\u6253\u67b6\uff0c\u6211\u5c31\u628a\u4ed6\u7684\u8138\u6293\u7834\uff01\u201d\u674e\u601d\u6c38\u5fc3\u91cc\u9a82\u9053\uff1a\u201c\u771f\u662f\u4e2a\u50bb\u5927 \u59d0\u3002\u201d\u4f46\u6b64\u65f6\u60c5\u52bf\u5371\u6025\uff0c\u6027\u547d\u76f8\u640f\uff0c\u4e5f\u987e\u4e0d\u5f97\u5979\u4e86\u3002\u6210\u5929\u633a\u53cc\u7b14\u4e00\u7acb\uff0c\u5927\u58f0\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8bf7 \u8d50\u62db\uff01\u201d\u5de6\u7b14\u659c\u98de\uff0c\u53f3\u7b14\u76f4\u70b9\uff0c\u5206\u70b9\u674e\u601d\u6c38\u7684\u547d\u95e8\u8981\u7a74\uff0c\u674e\u601d\u6c38\u5927\u5403\u4e00\u60ca\uff0c\u60f3\u4e0d\u5230\u6c99\u6f20\u4e4b \u4e2d\uff0c\u7adf\u7136\u78b0\u7740\u6e05\u5ef7\u4f8d\u536b\u4e2d\u7684\u4e00\u6d41\u9ad8\u624b\uff01 \u3000\u3000\u90a3\u4e00\u8fb9\uff0c\u6613\u5170\u73e0\u3001\u90b1\u4e1c\u6d1b\u52a8\u4e86\u5175\u5203\uff0c\u4e5f\u662f\u5404\u81ea\u5403\u60ca\uff0c\u90b1\u4e1c\u6d1b\u5de6\u5200\u53f3\u5251\uff0c\u62db\u6570\u7e41\u590d\u53e4\u602a\uff0c \u7247\u523b\u4e4b\u95f4\uff0c\u8fde\u653b\u4e86\u5341\u591a\u62db\u3002\u6613\u5170\u73e0\u54fc\u4e86\u4e00\u58f0\uff0c\u6697\u9053\uff1a\u77a7\u4e0d\u51fa\u72d7\u817f\u5b50\u5012\u6709\u51e0\u5206\u672c\u9886\uff0c\u65ad\u7389\u5251\u626c \u7a7a\u4e00\u95ea\uff0c\u84e6\u5730\u8fdb\u62db\u3002\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u628a\u90b1\u4e1c\u6cbc\u7684\u5200\u5c16\u622a\u65ad\uff0c\u90b1\u4e1c\u6d1b\u77e5\u9053\u78b0\u5230\u4e86\u5b9d\u5251\uff0c\u8fde\u9000\u51e0 \u6b65\uff0c\u500f\u5730\u51b7\u7b11\u4e00\u58f0\uff0c\u5200\u950b\u4e00\u8f6c\uff0c\u5251\u5c16\u659c\u6311\uff0c\u81ea\u4fa7\u9762\u6b3a\u8eab\u800c\u8fdb\uff0c\u98ce\u96f7\u5200\u5251\uff0c\u62db\u62db\u72e0\u6bd2\u3002\u6613\u5170\u73e0 \u5140\u7136\u4e0d\u60e7\uff0c\u5929\u5c71\u5251\u6cd5\uff0c\u970d\u970d\u5c55\u5f00\uff0c\u94f6\u5149\u88f9\u4f53\uff0c\u95ea\u7535\u60ca\u98d8\uff0c\u5728\u5200\u5251\u5939\u51fb\u4e2d\uff0c\u8fde\u5b88\u5e26\u653b\uff0c\u4e8c\u5c3a\u516b \u5bf8\u7684\u77ed\u5251\uff0c\u5251\u5251\u4e0d\u79bb\u654c\u4eba\u8981\u5bb3\u3002\u6613\u5170\u73e0\u5e74\u7eaa\u867d\u8f7b\uff0c\u5df2\u5f97\u5929\u5c71\u5251\u6cd5\u7684\u795e\u9ad3\uff0c\u66f4\u52a0\u4e0a\u98de\u7ea2\u5dfe\u53c8\u4ee5 \u767d\u53d1\u9b54\u5973\u7684\u72ec\u95e8\u5251\u6cd5\u76f8\u6388\uff0c\u5728\u201c\u5929\u5c71\u4e03\u5251\u201d\u4e4b\u4e2d\uff0c\u53ea\u6709\u5979\u662f\u72ec\u5177\u4e24\u5bb6\u4e4b\u957f\uff0c\u53ef\u60dc\u7684\u662f\u706b\u5019\u672a \u591f\uff0c\u6c14\u529b\u4e5f\u8f83\u5dee\uff0c\u8981\u4e0d\u7136\u4e24\u4e2a\u90b1\u4e1c\u6d1b\u4e5f\u62b5\u6321\u4e0d\u4f4f\u3002 \u3000\u3000\u6b66\u743c\u7476\u5750\u5728\u65c1\u8fb9\u89c2\u6218\uff0c\u6697\u6697\u70b9\u5934\u8d5e\u53f9\uff0c\u6613\u5170\u73e0\u548c\u5979\u5e74\u7eaa\u5dee\u4e0d\u591a\uff0c\u8bba\u8f88\u4efd\u6bd4\u5979\u4f4e\u534a\u8f88\uff0c\u4f46 \u5251\u6cd5\u7cbe\u5999\uff0c\u5374\u662f\u5404\u64c5\u80dc\u573a\u3002\u90b1\u4e1c\u6cbc\u4e24\u624b\u4f7f\u4e24\u822c\u5175\u5668\uff0c\u4ed7\u7740\u602a\u5f02\u62db\u6570\u5300\u7ecf\u9a8c\u8001\u5230\uff0c\u867d\u6682\u65f6\u652f\u6491 \u5f97\u4f4f\uff0c\u4f46\u770b\u6765\u6613\u5170\u73e0\u5fc5\u53ef\u5f97\u80dc\u3002 \u3000\u3000\u674e\u601d\u6c38\u90a3\u8fb9\uff0c\u5f62\u52bf\u5374\u5927\u4e0d\u76f8\u540c\u3002\u6210\u5929\u633a\u7684\u6b66\u529f\u4e0e\u695a\u662d\u5357\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u4e24\u679d\u5224\u5b98\u7b14\u795e\u51fa\u9b3c \u6ca1\uff0c\u4e13\u70b9\u654c\u4eba\u4e09\u5341\u516d\u9053\u5927\u7a74\uff0c\u500f\u800c\u53c8\u5f53\u4e94\u884c\u5251\u4f7f\uff0c\u70b9\u6253\u6233\u51fb\u624e\u523a\uff0c\u53d8\u5316\u65e0\u7a77\uff0c\u674e\u601d\u6c38\u6b66\u529f\u867d \u9ad8\uff0c\u6bd4\u8d77\u6765\u5374\u7a0d\u6709\u900a\u8272\u3002\u5e78\u800c\u4ed6\u7684\u6d41\u661f\u9524\u7075\u6d3b\u975e\u5e38\uff0c\u5229\u4e8e\u8fdc\u653b\uff0c\u53c8\u80fd\u8fd1\u6321\uff0c\u6536\u53d1\u8fc5\u75be\uff0c\u5c31\u5982 \u6d3b\u52a8\u7684\u6697\u5668\u4e00\u822c\uff0c\u6210\u5929\u633a\u4e5f\u6709\u51e0\u5206\u754f\u60e7\u3002\u4e24\u4eba\u5404\u5c55\u5947\u95e8\u5175\u5668\uff0c\u4e4d\u8fdb\u4e4d\u9000\uff0c\u500f\u5408\u500f\u5206\uff0c\u4e0d\u591a\u4e00 \u4f1a\uff0c\u5df1\u62c6\u4e86\u767e\u591a\u62db\uff0c\u6210\u5929\u633a\u6740\u5f97\u6027\u8d77\uff0c\u53cc\u7b14\u7ffb\u98de\uff0c\u674e\u601d\u6c38\u88ab\u4ed6\u8feb\u5f97\u6536\u7d27\u6d41\u661f\u9524\u7684\u94c1\u7d22\uff0c\u820d\u6389 \u8fdc\u653b\u4e4b\u5229\uff0c\u6539\u4e3a\u9632\u5b88\u3002\u6b66\u743c\u7476\u5927\u4e3a\u7126\u6025\uff0c\u60f3\u51fa\u624b\u76f8\u6551\uff0c\u53c8\u4ee5\u8bf4\u8bdd\u5728\u5148\uff0c\u4e14\u674e\u601d\u6c38\u662f\u4e2a\u6210\u540d\u4eba \u7269\uff0c\u82e5\u81ea\u5df1\u52a9\u4ed6\u4ee5\u4e8c\u654c\u4e00\uff0c\u8fd8\u6015\u4ed6\u771f\u4e2a\u4e0d\u60a6\u3002 \u3000\u3000\u6210\u5929\u633a\u90a3\u4e24\u4e2a\u526f\u624b\uff0c\u89c1\u6210\u5929\u633a\u5360\u4e86\u4e0a\u98ce\uff0c\u9ad8\u5174\u975e\u5e38\uff0c\u4ed6\u4eec\u5374\u770b\u4e0d\u51fa\u90b1\u4e1c\u6d1b\u5904\u5728\u4e0b\u98ce\uff0c\u53ea \u9053\u8fd9\u573a\u53ae\u6740\u7a33\u80dc\u65e0\u7591\uff0c\u770b\u89c1\u6b66\u743c\u7476\u7126\u6025\u795e\u60c5\uff0c\u7adf\u7136\u62ff\u5979\u53d6\u7b11\uff1a\u90d1\u5927\u951f\u548c\u8fde\u4e09\u864e\u90fd\u662f\u597d\u8272\u4e4b \u5f92\uff0c\u4e24\u4eba\u4e00\u5531\u4e00\u548c\uff0c\u4e00\u4e2a\u8bf4\uff1a\u201c\u5582\uff0c\u7ea2\u8863\u5c0f\u59d1\u5a18\uff0c\u4ed6\u662f\u4f60\u7684\u60c5\u90ce\u5417\uff1f\u4f60\u8fd9\u4e2a\u60c5\u90ce\u4e0d\u884c\uff0c\u8fd8\u662f \u518d\u62e3\u8fc7\u4e00\u4e2a\u5427\uff01\u201d\u4e00\u4e2a\u8bf4\uff1a\u201c\u4f60\u771f\u4e0d\u61c2\u60dc\u7389\u601c\u9999\uff0c\u5979\u6b63\u5fc3\u75db\u7740\u5462\uff01\u5c0f\u59d1\u5a18\uff0c\u6211\u6765\u5b89\u6170\u4f60\u3002\u201d \u8fde\u4e09\u864e\u4e0d\u77e5\u6b7b\u6d3b\uff0c\u524d\u6765\u8c03\u7b11\uff0c\u6b66\u743c\u7476\u51b7\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6709\u8bdd\u5728\u5148\uff0c\u8c01\u6405\u4e71\u6211\u770b\u6253\u67b6\uff0c\u6211 \u5c31\u6293\u7834\u4ed6\u7684\u8138\uff01\u4f60\u518d\u8d70\u8fd1\u4e00\u6b65\uff0c\u6211\u5c31\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d\u8fde\u4e09\u864e\u5b09\u76ae\u7b11\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u4f60\u8fd9\u6837 \u51f6\u3002\u201d\u8fc8\u524d\u4e00\u6b65\uff0c\u8bdd\u58f0\u672a\u4e86\uff0c\u5ffd\u7136\u4e00\u80a1\u52b2\u98ce\uff0c\u76f4\u6251\u9762\u95e8\u3002\u5c1a\u672a\u770b\u6e05\uff0c\u4e24\u773c\u5df2\u7ed9\u6293\u778e\u3002\u6b66\u743c\u7476 \u8eab\u6cd5\u5feb\u6781\uff0c\u4e00\u6293\u6293\u4e0b\uff0c\u4e24\u9897\u773c\u73e0\u53d6\u5230\u624b\u4e2d\uff0c\u628a\u624b\u4e00\u626c\uff0c\u5c06\u8fde\u4e09\u864e\u7684\u773c\u73e0\u5f53\u6210\u94c1\u84ec\u5b50\u6253\u51fa\uff0c\u90d1 \u5927\u951f\u60ca\u53eb\u4e00\u58f0\uff0c\u672a\u66fe\u5408\u53e3\uff0c\u5df2\u7ed9\u773c\u73e0\u6253\u8fdb\u53e3\u4e2d\uff0c\u4e00\u80a1\u8840\u8165\u5473\u9053\u597d\u4e0d\u96be\u53d7\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c \u6b66\u743c\u7476\u53c8\u5df2\u5230\u4e86\u4ed6\u7684\u9762\u524d\uff01\u6b63\u662f\uff1a \u3000\u3000\u8349\u539f\u5947\u5973\u5b50\uff0c\u8c08\u7b11\u620f\u51f6\u987d\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5eff\u4e09\u7ae0\u3000\u8be1\u8ba1\u591a\u7aef\u3000\u6bd2\u9152\u751c\u8a00\u6c42\u79d8\u7b08\u3000\u8270\u96be\u51e0\u5ea6\u3000\u75f4\u60c5\u871c\u610f\u83b7\u82b3\u5fc3 \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u4e8c\u5341\u4e09\u56de \u8be1\u8ba1\u591a\u7aef\u3000\u6bd2\u9152\u751c\u8a00\u6c42\u79d8\u7b08 \u8270\u96be\u51e0\u5ea6\u3000\u75f4\u60c5\u871c\u610f\u83b7\u82b3\u5fc3 \u3000\u3000\u90d1\u5927\u951f\u53cd\u624b\u4e00\u638c\uff0c\u6b66\u743c\u7476\u5df2\u6293\u5230\u4ed6\u7684\u9762\u4e0a\uff0c\u90d1\u5927\u951f\u626d\u5934\u4fa7\u9762\uff0c\u4fdd\u5168\u4e86\u773c\u73e0\uff0c\u9762\u76ae\u5374\u88ab\u6293 \u7834\u4e86\u3002\u4ed6\u90a3\u4e00\u638c\u7528\u7684\u662f\u6392\u5c71\u8fd0\u638c\u7684\u529f\u592b\uff0c\u521a\u52b2\u975e\u5e38\uff0c\u8c01\u77e5\u672a\u4e2d\u654c\u4eba\uff0c\u5148\u53d7\u4e86\u4e00\u6293\uff0c\u6240\u53d1\u7684\u638c \u529b\u81ea\u7136\u51cf\u5f31\u8bb8\u591a\uff0c\u6b66\u743c\u7476\u5de6\u624b\u4e00\u6293\uff0c\u53f3\u638c\u548c\u4ed6\u78b0\u4e2a\u6b63\u7740\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7136\u4e00\u58f0\uff0c\u90d1\u5927\u951f\u76f4\u7ed9 \u6454\u51fa\u4e24\u4e09\u4e08\u5916\u3002\u5e78\u4ed6\u529f\u529b\u6bd4\u8fde\u4e09\u864e\u9ad8\u5f97\u591a\uff0c\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u7ffb\u4e86\u8d77\u6765\uff0c\u72ec\u95e8\u5175\u5668\u866c\u9f99\u97ad \u4e5f\u5df2\u89e3\u51fa\uff0c\u5fcd\u7740\u75bc\u75db\uff0c\u4f3c\u75af\u864e\u822c\u6251\u4e0a\u62e6\u622a\uff01 \u3000\u3000\u6b66\u743c\u7476\u8eab\u6cd5\u4f55\u7b49\u5feb\u75be\uff0c\u90d1\u5927\u951f\u7ad9\u8d77\u8eab\u65f6\uff0c\u5979\u5df2\u62a2\u5230\u6210\u5929\u633a\u4e0e\u674e\u601d\u6c38\u4e4b\u95f4\uff0c\u9752\u94a2\u5251\u9aa4\u7136\u51fa \u624b\uff0c\u4e00\u62db\u201c\u4e58\u9f99\u5f15\u51e4\u201d\uff0c\u628a\u6210\u5929\u633a\u7684\u5224\u5b98\u7b14\u7c98\u81f3\u5916\u9762\uff0c\u89e3\u4e86\u674e\u601d\u6c38\u4e4b\u5371\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u6211\u8bf4 \u8fc7\u4e0d\u8bb8\u4f60\u4eec\u6270\u6211\u770b\u6253\u67b6\uff0c\u4f60\u7684\u624b\u4e0b\u504f\u4e0d\u542c\u8bdd\uff0c\u6211\u867d\u4e0d\u61c2\u6253\u67b6\uff0c\u4e5f\u8981\u548c\u4f60\u6253\u4e86\u3002\u674e\u516c\u5b50\u4f60\u66ff\u6211 \u53bb\u6536\u62fe\u90a3\u4e2a\u80d6\u5b50\uff0c\u8fd9\u4e2a\u75c5\u592b\u4f60\u7559\u7ed9\u6211\u3002\u6211\u6c14\u529b\u5c0f\uff0c\u6b63\u597d\u6253\u4ed6\u3002\u201d \u3000\u3000\u90d1\u5927\u951f\u751f\u5f97\u65b9\u9762\u5927\u8033\uff0c\u80a5\u80a5\u80d6\u80d6\uff0c\u6210\u5929\u633a\u5219\u751f\u5f97\u53c8\u77ee\u53c8\u7626\uff0c\u4f46\u6210\u5929\u633a\u7684\u6b66\u529f\u6bd4\u90d1\u5927\u951f\u90a3 \u5374\u4e0d\u77e5\u8981\u9ad8\u660e\u591a\u5c11\u3002\u6b66\u743c\u7476\u4e43\u662f\u8ba9\u674e\u601d\u6c38\u85c9\u6b64\u4e0b\u53f0\u3002 \u3000\u3000\u6210\u5929\u633a\u7ed9\u79f0\u4e3a\u201c\u75c5\u592b\u201d\uff0c\u7eb5\u58f0\u72c2\u7b11\uff0c\u53cc\u7b14\u5982\u51e4\u4f3c\u7684\uff0c\u201c\u5012\u8f6c\u4e7e\u5764\u201d\uff0c\u731b\u5954\u6b66\u743c\u7476\u4e39\u7530\u7a74 \u624e\u53bb\uff0c\u9a82\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\u6709\u591a\u5927\u672c\u9886\uff1f\u53eb\u4f60\u89c1\u8bc6\u75c5\u592b\u624b\u6bb5\uff01\u201d\u6b66\u743c\u7476\u89c1\u654c\u62db\u6765\u5f97\u7eb7\u5feb\uff0c\u628a\u5251\u4e00 \u6321\uff0c\u7ed9\u9707\u5f97\u864e\u53e3\u53d1\u70ed\uff0c\u6025\u5fd9\u811a\u5c16\u4e00\u70b9\uff0c\u5e73\u5730\u98de\u8eab\uff0c\u8f7b\u5982\u63a0\u71d5\uff0c\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u6210\u5929\u633a\u5fd9\u7528 \u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u85cf\u5934\u7f29\u9888\uff0c\u8eab\u5f62\u4e00\u77ee\uff0c\u9640\u87ba\u822c\u75be\u8f6c\u8fc7\u6765\uff0c\u4e00\u62db\u201c\u4e3e\u706b\u71ce\u5929\u201d\uff0c\u53cc\u7b14\u53c8\u8fce\u7740\u9752 \u94a2\u5251\u622a\u53bb\uff0c\u6b66\u743c\u7476\u5530\u5530\u5530\u4e00\u8fde\u51e0\u5251\uff0c\u5de6\u53f3\u5206\u523a\uff0c\u5251\u82b1\u9519\u843d\uff0c\u94f6\u5149\u98d8\u5ffd\uff0c\u6210\u5929\u633a\u7ed9\u5979\u6c14\u5f97\u8bf4\u4e0d \u51fa\u8bdd\uff0c\u4f46\u52b2\u654c\u5f53\u524d\uff0c\u4e0d\u80fd\u4e0d\u6c89\u4e0b\u6c14\u6765\uff0c\u4e00\u9762\u5c01\u95ed\u95e8\u6237\uff0c\u4e00\u9762\u4f3a\u673a\u53cd\u51fb\u3002 \u3000\u3000\u6210\u5929\u633a\u4e43\u662f\u6e05\u5bab\u4e2d\u6570\u4e00\u6570\u4e8c\u7684\u597d\u624b\uff0c\u8f7b\u529f\u867d\u6bd4\u4e0d\u4e0a\u6b66\u743c\u7476\uff0c\u5b9e\u529b\u5374\u8981\u6bd4\u5979\u9ad8\u4e00\u7b79\uff0c\u4e00\u5bf9 \u5224\u5b98\u7b14\u53c8\u7a33\u53c8\u72e0\uff0c\u6b66\u743c\u7476\u8fd8\u4e0d\u6562\u771f\u4e2a\u548c\u4ed6\u76f8\u78b0\u3002\u5979\u4ed7\u7740\u767d\u53d1\u9b54\u5973\u7684\u72ec\u95e8\u5251\u6cd5\uff0c\u5ffd\u865a\u5ffd\u5b9e\uff0c\u58f0 \u4e1c\u51fb\u897f\uff0c\u53ea\u662f\u5728\u6d88\u8017\u6210\u5929\u633a\u7684\u6c14\u529b\u3002\u4e24\u4eba\u6076\u6218\uff0c\u4e00\u4e2a\u662f\u52c7\u5982\u731b\u72ee\uff0c\u4e00\u4e2a\u5219\u6377\u82e5\u7075\u732b\uff0c\u5404\u65bd\u7edd \u6280\uff0c\u5907\u64c5\u80dc\u573a\uff0c\u6253\u5f97\u4e2a\u96be\u5206\u96be\u89e3\u3002\u6210\u5929\u633a\u8fd9\u624d\u6697\u6697\u5403\u60ca\uff0c\u60f3\u4e0d\u5230\u4e00\u4e2a\u5e74\u8f7b\u7684\u5c11\u5973\uff0c\u5251\u6cd5\u5982\u6b64 \u5389\u5bb3\uff01 \u3000\u3000\u6613\u5170\u73e0\u4e00\u89c1\u6b66\u743c\u7476\u51fa\u624b\uff0c\u5206\u5916\u7cbe\u795e\uff0c\u5979\u672c\u6765\u5df2\u5360\u4e86\u4e0a\u98ce\uff0c\u5251\u62db\u4e00\u7d27\uff0c\u8d8a\u53d1\u5982\u957f\u6c5f\u5927\u6cb3\uff0c \u6eda\u6eda\u800c\u4e0a\uff0c\u4e0d\u53ef\u62b5\u5fa1\u3002\u5251\u5149\u970d\u970d\u3001\u5251\u6c14\u7eb5\u6a2a\u4e4b\u4e2d\uff0c\u90b1\u4e1c\u6d1b\u60ca\u53eb\u4e00\u58f0\uff0c\u8499\u7740\u8033\u6735\u7684\u6597\u7bf7\u5df2\u7ed9\u524a \u843d\uff0c\u6b66\u743c\u7476\u4e00\u9762\u62b5\u5fa1\u6210\u5929\u633a\uff0c\u4e00\u9762\u6ce8\u89c6\u674e\u601d\u6c38\u548c\u6613\u5170\u73e0\uff0c\u4e00\u89c1\u90b1\u4e1c\u6d1b\u62ab\u7684\u6597\u7bf7\u8dcc\u843d\uff0c\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u770b\u5440\uff0c\u6709\u4e2a\u6ca1\u8033\u6735\u7684\u4e11\u516b\u602a\uff01\u201d\u90b1\u4e1c\u6d1b\u53c8\u6c14\u53c8\u607c\u53c8\u6ca1\u529e\u6cd5\uff0c\u865a\u8fdb\u4e00\u62db\uff0c\u98de\u8eab\u4fbf\u9000\uff01 \u3000\u3000\u6613\u5170\u73e0\u542c\u51cc\u672a\u98ce\u8bf4\u8fc7\u90b1\u4e1c\u6d1b\u7684\u4e8b\uff0c\u51b7\u7b11\u4e00\u58f0\uff1a\u201c\u54ea\u91cc\u8d70\uff01\u201d\u98de\u8eab\u6251\u4e0a\uff0c\u624b\u4e2d\u5251\u4e00\u63d0\u4e00 \u7ffb\uff0c\u9752\u5149\u95ea\u5904\uff0c\u5df2\u5230\u80cc\u540e\uff0c\u90b1\u4e1c\u6d1b\u53cd\u624b\u4e00\u5200\uff0c\u6ca1\u6709\u6321\u7740\uff0c\u4e94\u53ea\u624b\u6307\uff0c\u5df2\u7ed9\u524a\u65ad\uff0c\u6613\u5170\u73e0\u987a\u52bf \u4e00\u63a8\uff0c\u5251\u950b\u5411\u4e0b\u4e00\u5212\uff0c\u90b1\u4e1c\u6d1b\u53f3\u817f\u53c8\u7ed9\u65a9\u6389\uff0c\u6613\u5170\u73e0\u8fd9\u4e24\u62db\u5feb\u5982\u95ea\u7535\uff0c\u5979\u81ea\u5df1\u4e5f\u6599\u4e0d\u5230\u767d\u53d1 \u9b54\u5973\u7684\u72ec\u95e8\u5251\u6cd5\u5982\u6b64\u51f6\u72e0\uff0c\u5f97\u624b\u4e4b\u540e\uff0c\u53d1\u73b0\u654c\u4eba\u75db\u5f97\u5728\u5730\u4e0a\u6253\u6eda\uff0c\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u6025\u8865\u4e00\u5251\uff0c\u5c06 \u4ed6\u4e86\u7ed3\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5728\u8941\u8913\u4e4b\u4e2d\uff0c\u4f60\u5c31\u60f3\u5bb3\u6211\u3002\u51cc\u53d4\u53d4\u4e3a\u4e86\u4fdd\u62a4\u6211\uff0c\u51e0\u4e4e\u7ed9\u4f60\u780d\u6b7b\u3002\u73b0\u5728\u4f60 \u5403\u6211\u4e00\u5251\uff0c\u987b\u602a\u6211\u4e0d\u5f97\u3002\u201d\u4e00\u811a\u628a\u654c\u4eba\u5c38\u9996\u8e22\u5f00\uff0c\u63d0\u5251\u4e0a\u6765\u89c2\u6218\u3002 \u3000\u3000\u90a3\u90d1\u5927\u951f\u867d\u7136\u4e5f\u662f\u6e05\u5bab\u4f8d\u536b\u4e2d\u7684\u9ad8\u624b\uff0c\u5374\u654c\u4e0d\u4f4f\u674e\u601d\u6c38\u7684\u4e24\u67c4\u6d41\u661f\u9524\uff0c\u8033\u542c\u90b1\u4e1c\u6d1b\u54c0\u53f7 \u4e4b\u58f0\uff0c\u66f4\u662f\u5fc3\u60ca\u80c6\u98a4\uff0c\u866c\u9f99\u97ad\u8d77\u5904\uff0c\u201c\u7389\u5e26\u7f20\u8170\u201d\u547c\u7684\u4e00\u58f0\uff0c\u5411\u674e\u601d\u6c38\u62e6\u8170\u626b\u53bb\uff0c\u4ee5\u8fdb\u4e3a \u9000\uff0c\u660e\u662f\u62a2\u653b\uff0c\u5b9e\u6b32\u64a4\u9000\uff0c\u674e\u601d\u6c38\u6599\u77e5\u654c\u610f\uff0c\u6d41\u661f\u9524\u8fce\u7740\u866c\u9f99\u97ad\u4e00\u515c\uff0c\u4e24\u822c\u5916\u95e8\u5175\u5668\u649e\u4e2a\u6b63 \u7740\uff0c\u6d41\u661f\u9524\u7684\u94c1\u7d22\u5c06\u866c\u9f99\u97ad\u7ed5\u4e86\u51e0\u531d\u3002\u674e\u601d\u6c38\u5927\u559d\u4e00\u58f0\u201c\u8d77\u201c\uff0c\u594b\u529b\u4e00\u6325\uff0c\u5c06\u90d1\u5927\u951f\u6454\u4e0a\u534a \u7a7a\u3002 \u3000\u3000\u6210\u5929\u633a\u6076\u6597\u6b66\u743c\u7476\uff0c\u5140\u662f\u4e0d\u5206\u9ad8\u4e0b\u3002\u674e\u601d\u6c38\u4e0e\u6613\u5170\u73e0\u56f4\u4e0a\u6765\u770b\uff0c\u6210\u5929\u633a\u51b7\u7b11\u558a\u9053\uff1a\u201c\u4f60 \u4eec\u90fd\u4e0a\u6765\u5427\uff0c\u6211\u6b7b\u4e5f\u6b7b\u5f97\u82f1\u96c4\uff01\u201d\u6b66\u743c\u7476\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fde\u6211\u90fd\u6597\u4e0d\u8fc7\uff0c\u8fd8\u5439\u4ec0 \u4e48\u5927\u725b\u3002\u201d\u5530\uff01\u5530\uff01\u5530\uff01\u8fde\u73af\u4e09\u5251\uff0c\u65a9\u8170\u622a\u808b\u70b9\u80f8\u819b\uff0c\u5389\u5bb3\u975e\u5e38\uff0c\u6210\u5929\u633a\u51dd\u795e\u62b5\u654c\uff0c\u4e00\u53cc\u94c1 \u7b14\uff0c\u4f7f\u5f97\u9f99\u98de\u51e4\u821e\uff0c\u6beb\u65e0\u7834\u7efd\uff0c\u674e\u601d\u6c38\u770b\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u4ed6\u9886\u6559\u8fc7\u6210\u5929\u633a\u7684\u672c\u9886\uff0c\u4e0d\u7531\u5f97\u4e0d\u7531 \u8877\u4f69\u670d\u6b66\u743c\u7476\u4e86\u3002\u6210\u5929\u633a\u6253\u4e86\u4e00\u4f1a\uff0c\u89c1\u674e\u601d\u6c38\u548c\u6613\u5170\u73e0\u5e76\u4e0d\u5e2e\u624b\uff0c\u5fc3\u60c5\u7a0d\u5b9a\uff0c\u53cc\u7b14\u659c\u98de\uff0c\u4e00 \u62db\u201c\u5927\u9e4f\u5c55\u7fc5\u201d\u731b\u5730\u653b\u51fa\uff0c\u6b66\u743c\u7476\u5251\u8d70\u4e2d\u5bab\uff0c\u5206\u5fc3\u523a\u8fdb\uff0c\u90a3\u77e5\u6210\u5929\u633a\u7ecf\u9a8c\u8001\u5230\uff0c\u8fd9\u7adf\u662f\u8bf1 \u62db\uff0c\u53cc\u7b14\u65b9\u51fa\uff0c\u7acb\u5373\u5708\u4e86\u56de\u6765\uff0c\u53ea\u542c\u5f97\u201c\u4e01\u5f53\u201d\u4e00\u58f0\uff0c\u706b\u661f\u56db\u6e85\uff0c\u6b66\u743c\u7476\u6b63\u5f85\u6362\u5251\u8fdb\u62db\uff0c\u6210 \u5929\u633a\u5df2\u8131\u51fa\u5708\u5b50\uff0c\u731b\u5730\u5411\u674e\u601d\u6c38\u6251\u53bb\uff0c\u6b66\u743c\u7476\u6012\u559d\u4e00\u58f0\uff1a\u201c\u54ea\u91cc\u8d70\uff01\u201d\u5251\u968f\u8eab\u8d70\uff0c\u548c\u6613\u5170\u73e0 \u4e24\u7ffc\u6251\u4e0a\u3002 \u3000\u3000\u6210\u5929\u633a\u8fd9\u4e00\u7740\u4e43\u662f\u653b\u51fb\u654c\u65b9\u8f83\u5f31\u7684\u4e00\u70b9\uff0c\u674e\u601d\u6c38\u9aa4\u4e0d\u53ca\u9632\uff0c\u5df2\u7ed9\u6210\u5929\u633a\u51b2\u5230\uff0c\u6d41\u661f\u9524\u521a \u521a\u51fa\u624b\uff0c\u654c\u4eba\u7684\u94c1\u7b14\u5df2\u5230\u80f8\u524d\uff0c\u674e\u601d\u6c38\u970d\u5730\u5411\u53f3\u6643\u8eab\uff0c\u6210\u5929\u633a\u5df2\u5148\u62a2\u81f3\u53f3\u65b9\u4e0a\u9996\uff0c\u4f38\u624b\u4e00 \u63a8\uff0c\u515c\u4e2a\u6b63\u7740\uff0c\u559d\u58f0\u201c\u53bb\u201d\uff01\u674e\u601d\u6c38\u817e\u4e91\u9a7e\u96fe\u822c\u7ed9\u4ed6\u629b\u4e86\u51fa\u53bb\uff0c\u6b63\u6b63\u5bf9\u7740\u6b66\u743c\u7476\uff0c\u6b66\u743c\u7476\u614c \u4e0d\u8fed\u5730\u63b7\u5251\u843d\u5730\uff0c\u53cc\u624b\u6765\u63a5\uff0c\u674e\u601d\u6c38\u5ffd\u7ed9\u4eba\u62b1\u4f4f\uff0c\u80f8\u524d\u4e00\u5806\u8f6f\u7ef5\u7ef5\u7684\uff0c\u8fd8\u6709\u7f15\u7f15\u751c\u9999\uff0c\u6c81\u4eba \u5fc3\u80ba\uff0c\u6025\u5fd9\u6323\u8131\u4e0b\u5730\uff0c\u6210\u5929\u633a\u5df2\u8d81\u673a\u98de\u8dd1\u4e86\u3002 \u3000\u3000\u6613\u5170\u73e0\u987f\u8db3\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u674e\u601d\u6c38\u6ee1\u9762\u901a\u7ea2\uff0c\u5411\u6b66\u743c\u7476\u9053\u6b49\u9053\uff1a\u201c\u6211\u672c\u4e8b\u4e0d\u6d4e\uff0c \u53cd\u6210\u4e86\u4f60\u7684\u7d2f\u8d58\uff0c\u59d1\u5a18\u4e0d\u8981\u751f\u6c14\uff01\u201d\u6b66\u743c\u7476\u5657\u5473\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u516c\u5b50\u4f60\u592a\u8c26\u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u6c38\u60f3\u8d77\u5728\u8def\u4e0a\u8bf4\u7684\u8bdd\uff0c\u5341\u5206\u7f9e\u6127\uff0c\u642d\u8baa\u8bf4\u9053\uff1a\u201c\u6211\u771f\u662f\u6709\u773c\u65e0\u73e0\uff0c\u6599\u4e0d\u5230\u59d1\u5a18\u4e00\u8eab \u7edd\u6280\uff01\u201d\u6b66\u743c\u7476\u62bf\u5634\u4e00\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u540c\u884c\u4e86\u5927\u534a\u5929\uff0c\u4f60\u8fd8\u672a\u5c06\u540d\u5b57\u544a\u8bc9\u6211\u5462\uff01\u201d\u674e\u601d\u6c38\u89c1\u5979 \u529b\u6bd9\u6e05\u5bab\u536b\u58eb\uff0c\u6599\u5979\u5fc5\u662f\u540c\u9053\u4e2d\u4eba\uff0c\u4e5f\u5c31\u4e0d\u518d\u9690\u7792\uff0c\u5c06\u540d\u5b57\u8bf4\u4e86\u3002\u6613\u5170\u73e0\u53eb\u51fa\u58f0\u6765\uff0c\u9053\uff1a \u201c\u554a\uff0c\u539f\u6765\u662f\u674e\u516c\u5b50\uff0c\u51cc\u53d4\u53d4\u65f6\u65f6\u63d0\u5230\u4f60\uff01\u201d\u674e\u601d\u6c38\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u59d1\u5a18\u5251\u6cd5\u4f3c\u4e4e\u548c\u51cc\u672a\u98ce\u540c \u51fa\u4e00\u95e8\uff0c\u4e0d\u77e5\u59d1\u5a18\u548c\u4ed6\u600e\u6837\u79f0\u547c\uff1f\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u5979\u662f\u6211\u7238\u7238\u7684\u5e08\u5f1f\uff01\u201d\u674e\u601d\u6c38\u53c8\u60ca\u53c8\u559c\uff0c \u8bf4\u9053\uff1a\u201c\u4ee4\u5c0a\u662f\u6211\u751f\u5e73\u6700\u656c\u4f69\u7684\u4eba\uff0c\u6211\u5728\u56db\u5ddd\uff0c\u63a5\u5f20\u9752\u539f\u98de\u9a91\u4f20\u62a5\uff0c\u77e5\u9053\u59d1\u5a18\u88ab\u56f0\u5929\u7262\uff0c\u975e \u5e38\u7740\u6025\uff0c\u606d\u559c\u59d1\u5a18\u8131\u9669\uff0c\u4e0d\u77e5\u51cc\u5927\u4fa0\u5728\u6b64\u5730\u5426\uff1f\u201d\u6613\u5170\u73e0\u9762\u8272\u6c89\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u5728\u627e \u4ed6\uff01\u201d \u3000\u3000\u6b66\u743c\u7476\u62cd\u638c\u7b11\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u6211\u65e9\u6599\u5230\u662f\u4f60\uff0c\u679c\u7136\u4e0d\u9519\u3002\u6211\u7684\u7238\u7238\u5429\u5490\u6211\u6765\u63a5\u4f60\u4eec\uff0c\u679c \u7136\u4e00\u63a5\u5c31\u63a5\u7740\u4e86\uff01\u201d\u674e\u601d\u6c38\u201c\u554a\u5440\u201d\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u8bf4\u903c\uff1a\u201c\u4ee4\u5c0a\u60f3\u662f\u2018\u5a01\u9547\u4e09\u8fb9\u2019\u7684\u6b66\u5143 \u82f1\uff0c\u6b66\u5e84\u4e3b\uff1f\u201d\u6b66\u743c\u7476\u9053\uff1a\u201c\u4f60\u731c\u5f97\u4e0d\u9519\uff01\u201d\u6b66\u5143\u82f1\u548c\u5085\u9752\u4e3b\u662f\u751f\u6b7b\u4e4b\u4ea4\uff0c\u5085\u9752\u4e3b\u548c\u674e\u601d\u6c38 \u5728\u8fdb\u5165\u56de\u7586\u4e4b\u524d\uff0c\u5df2\u6d3e\u4eba\u9884\u5148\u4f20\u62a5\uff0c\u8bf7\u6b66\u5143\u82f1\u96c6\u5408\u897f\u5317\u5404\u5730\u5165\u7586\u7684\u5927\u5730\u4f1a\u53cb\uff0c\u4e3a\u674e\u601d\u6c38\u5e03\u7f6e \u4e00\u4e2a\u843d\u8db3\u4e4b\u70b9\uff0c\u91cd\u521b\u57fa\u4e1a\u3002\u674e\u601d\u6c38\u4e45\u95fb\u6b66\u5143\u82f1\u4e49\u8584\u4e91\u5929\uff0c\u6068\u4e0d\u5f97\u65e9\u65e5\u76f8\u89c1\u3002 \u3000\u3000\u6b66\u743c\u7476\u9053\uff1a\u201c\u5085\u4f2f\u4f2f\u4e3a\u4f55\u5c1a\u672a\u89c1\u5230\uff1f\u201d\u674e\u601d\u6c38\u767b\u9ad8\u4e00\u671b\uff0c\u89c1\u56de\u5934\u8def\u4e0a\uff0c\u8fdc\u8fdc\u9690\u6709\u708a\u70df\uff0c \u6b63\u5728\u60ca\u7591\uff0c\u5ffd\u89c1\u6709\u51e0\u9053\u5fae\u5f31\u7684\u84dd\u706b\uff0c\u5728\u9ad8\u7a7a\u4e00\u95ea\u5373\u706d\uff0c\u6025\u5fd9\u8df3\u4e0b\u6765\u9053\uff1a\u201c\u4e0d\u597d\u4e86\uff0c\u4ed6\u4eec\u4e00\u5b9a \u662f\u53d7\u4eba\u5305\u56f4\u4e86\uff01\u90a3\u84dd\u8272\u706b\u7130\u662f\u5218\u90c1\u82b3\u7684\u86c7\u7130\u7bad\uff01\u201d\u6b66\u743c\u7476\u5728\u6e05\u5bab\u536b\u58eb\u9057\u4e0b\u7684\u9a6c\u5339\u4e2d\uff0c\u9009\u4e86\u4e00 \u5339\u9a6c\u7ed9\u6613\u5170\u73e0\uff0c\u4e09\u4eba\u8fde\u9a91\u5411\u708a\u70df\u8d77\u5904\u75be\u9a70\u800c\u53bb\u3002 \u3000\u3000\u4e14\u8bf4\u5085\u9752\u4e3b\u5218\u90c1\u82b3\u7b49\u4eba\uff0c\u5728\u98ce\u6c99\u8fc7\u540e\uff0c\u4e0d\u89c1\u4e86\u674e\u601d\u6c38\uff0c\u751a\u4e3a\u7126\u6025\u3002\u77f3\u5929\u6210\u9053\uff1a\u201c\u6211\u5728\u56de \u7586\u591a\u5e74\uff0c\u8fd8\u8ba4\u5f97\u8def\uff0c\u9644\u8fd1\u7684\u5927\u57ce\u662f\u7109\u8006\uff0c\u6211\u4eec\u4e14\u5148\u5230\u7109\u8006\uff0c\u7b49\u5019\u674e\u516c\u5b50\u3002\u82e5\u8fd8\u7b49\u4e0d\u89c1\uff0c\u6211\u4eec \u5c31\u5f84\u5230\u6b66\u5143\u82f1\u6240\u4f4f\u4e4b\u5904\uff0c\u53eb\u4ed6\u6d3e\u4eba\u5e2e\u5fd9\u5bfb\u627e\u3002\u201d \u3000\u3000\u4e00\u884c\u5341\u591a\u9a91\uff0c\u7531\u77f3\u5929\u6210\u5e26\u8def\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u5ffd\u89c1\u540e\u9762\u5c18\u5934\u5927\u8d77\uff0c\u77f3\u5929\u6210\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u6837\u665a \u4e86\uff0c\u8fd8\u6709\u4eba\u8981\u901a\u8fc7\u6c99\u6f20\u53bb\u6253\u730e\uff1f\u201d\u8349\u539f\u4e0a\u7684\u6e38\u7267\u90e8\u843d\uff0c\u5e38\u5e38\u7ed3\u961f\u800c\u51fa\uff0c\u6216\u730e\u53d6\u91ce\u517d\uff0c\u6216\u627e\u5bfb \u8349\u5730\u653e\u9a6c\uff0c\u6240\u4ee5\u77f3\u5929\u6210\u8fd9\u6837\u731c\u6d4b\u3002\u5085\u9752\u4e3b\u51dd\u795e\u773a\u671b\uff0c\u53eb\u9053\uff1a\u201c\u4f3c\u4e4e\u662f\u6e05\u5175\uff01\u54b1\u4eec\u5feb\u8d70\uff01\u201d\u8bdd \u72b9\u672a\u4e86\uff0c\u90a3\u5f6a\u4eba\u9a6c\u7684\u5148\u5934\u51e0\u9a91\u5df2\u5982\u98de\u51b2\u81f3\uff0c\u4e3a\u9996\u7684\u4eba\u7adf\u662f\u695a\u662d\u5357\u3002\u5085\u9752\u4e3b\u5927\u5403\u4e00\u60ca\uff0c\u9752\u94a2\u5251 \u500f\u7684\u51fa\u624b\u3002\u695a\u662d\u5357\u5ffd\u7136\u5411\u5218\u90c1\u82b3\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u628a\u5979\u7684\u5251\u62a2\u6765\uff01\u201d\u4e00\u4e2a\u6e05\u766f\u8001\u8005\uff0c\u9ebb\u8863\u5927 \u8896\uff0c\u5f62\u72b6\u53e4\u602a\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u4f5c\u52bf\u817e\u8dc3\uff0c\u811a\u6b65\u4e00\u8f6c\uff0c\u75be\u7684\u4fbf\u5230\u5218\u90c1\u82b3\u9762\u524d\uff0c\u53cc\u624b\u6293\u4e0b\u3002\u5085\u9752\u4e3b\u5927 \u559d\u4e00\u58f0\uff0c\u4e00\u5251\u523a\u53bb\uff0c\u53c8\u51c6\u53c8\u75be\uff0c\u4e0d\u6599\u4e00\u5251\u523a\u7a7a\u3002\u90a3\u602a\u4eba\u5df2\u7ed5\u5230\u5218\u90c1\u82b3\u8eab\u540e\uff0c\u5085\u9752\u4e3b\u7b2c\u4e8c\u5251\u5377 \u5730\u626b\u53bb\uff0c\u5df2\u7ed9\u695a\u662d\u96e8\u6a2a\u5251\u6321\u4f4f\u3002\u8fd9\u65f6\u53ea\u542c\u5f97\u5218\u90c1\u82b3\u548c\u90a3\u602a\u4eba\u90fd\u5927\u53eb\u4e00\u58f0\uff01 \u3000\u3000\u77f3\u5929\u6210\u559d\u9053\uff1a\u201c\u8f9b\u9f99\u5b50\u4f60\u597d\u5927\u80c6\uff01\u201d\u5085\u9752\u4e3b\u8033\u542c\u5218\u90c1\u82b3\u53eb\u58f0\uff0c\u731b\u5730\u6487\u5f00\u695a\u662d\u5357\uff0c\u5927\u8896\u4e00 \u5c55\uff0c\u7167\u90a3\u602a\u4eba\u5934\u9762\u4e00\u62cd\uff0c\u624b\u4e2d\u5251\u75be\u5982\u95ea\u7535\uff0c\u5728\u8896\u5e95\u5237\u5730\u523a\u51fa\uff0c\u8fd9\u4e43\u662f\u5085\u9752\u4e3b\u7684\u5e73\u751f\u7edd\u6280\uff0c\u540d \u201c\u98de\u4e91\u8896\u5e95\u5251\u201d\uff0c\u957f\u8896\u548c\u5251\u90fd\u662f\u6b66\u5668\u3002\u90a3\u602a\u4eba\u4ed7\u7740\u602a\u5f02\u7684\u8eab\u6cd5\uff0c\u5f2f\u8eab\u5728\u8896\u5e95\u94bb\u8fc7\uff0c\u77f3\u5929\u6210\u548c \u77f3\u5927\u5a18\u53cc\u53cc\u6251\u5230\uff0c\u77f3\u5929\u6210\u53cc\u811a\u9f50\u8d77\uff0c\u8fde\u73af\u8e22\u51fa\uff0c\u77f3\u5927\u5a18\u4e94\u79bd\u5251\u6cd5\uff0c\u515c\u5934\u5288\u4e0b\uff0c\u90a3\u602a\u4eba\u4e00\u77ee\u8eab \u8eaf\uff0c\u9661\u7136\u5411\u540e\u7eb5\u53bb\uff0c\u5ffd\u89c9\u624b\u8155\u9ebb\u75bc\uff0c\u535a\u9752\u4e3b\u7684\u957f\u8896\u576a\u82e5\u7075\u86c7\uff0c\u4e58\u4ed6\u907f\u77f3\u5927\u6210\u592b\u5987\u7684\u7edd\u62db\u4e4b \u65f6\uff0c\u547c\u5730\u5377\u6765\uff0c\u90a3\u602a\u4eba\u867d\u7136\u6b66\u529f\u6781\u5f3a\uff0c\u4e5f\u6321\u4e0d\u4f4f\u4e09\u4e2a\u4e00\u6d41\u597d\u624b\u7684\u5939\u51fb\uff0c\u624b\u8155\u7ed9\u8863\u8896\u4e00\u5377\uff0c\u4e00 \u53e3\u5251\u7adf\u7ed9\u593a\u51fa\u4e86\u624b\uff0c\u695a\u662d\u5357\u731b\u5730\u4e00\u7eb5\uff0c\u5c06\u5251\u62a2\u5728\u624b\u4e2d\uff0c\u77f3\u5927\u5a18\u4e00\u5251\u4e0a\u523a\uff0c\u695a\u662d\u5357\u5728\u534a\u7a7a\u6253\u4e2a \u7b4b\u6597\uff0c\u659c\u4fa7\u843d\u4e0b\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u4e3e\u624b\u4e00\u62db\uff0c\u80cc\u540e\u90a3\u5f6a\u4eba\u9a6c\uff0c\u5982\u6f6e\u6d8c\u81f3\uff0c\u7eb7\u7eb7\u51b2\u6740\u8fc7\u6765\uff01 \u3000\u3000\u8fd9\u602a\u4eba\u6b63\u662f\u77f3\u5929\u6210\u7684\u5e08\u5f1f\uff0c\u5353\u4e00\u822a\u7684\u8863\u94b5\u4f20\u4eba\u8f9b\u9f99\u5b50\uff0c\u4ed6\u5f97\u4e86\u8fbe\u6469\u4e00\u767e\u96f6\u516b\u5f0f\u7684\u771f\u4f20\u4e4b \u540e\uff0c\u4e00\u5fc3\u60f3\u89c5\u5b9d\u5251\uff1b\u695a\u662d\u5357\u8fd9\u65f6\u6b63\u5949\u7687\u547d\u968f\u5927\u5c06\u547c\u56fe\u52aa\u514b\u9886\u5175\u5165\u7586\uff0c\u695a\u662d\u5357\u5728\u5929\u5c71\u4e4b\u65f6\u548c\u8f9b \u9f99\u5b50\u539f\u662f\u597d\u53cb\uff0c\u8f9b\u9f99\u5b50\u8dd1\u6765\u627e\u4ed6\uff0c\u8bf7\u4ed6\u4ee3\u4e3a\u7269\u8272\u4e00\u628a\u597d\u5251\uff0c\u695a\u662d\u5357\u7075\u673a\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u90a3 \u67c4\u6e38\u9f99\u5251\u4e43\u662f\u6666\u660e\u7985\u5e08\u9547\u5c71\u4e4b\u5b9d\uff0c\u5929\u5c71\u5b9d\u5251\u4e4b\u4e00\uff0c\u4f60\u662f\u89c1\u8fc7\u7684\u4e86\u3002\u6211\u53ef\u4ee5\u9001\u7ed9\u4f60\uff0c\u4f46\u4f60\u8981\u9760 \u81ea\u5df1\u672c\u9886\u53bb\u53d6\u3002\u201d\u8f9b\u9f99\u5b50\u602a\u773c\u4e00\u7ffb\uff0c\u8bf4\u9053\uff1a\u201c\u695a\u662d\u5357\uff0c\u4f60\u60f3\u8003\u8f83\u6211\u4e48\uff1f\u6e38\u9f99\u5251\u662f\u4f60\u7684\u547d\u6839\uff0c \u6211\u5e76\u6ca1\u95ee\u4f60\u8981\u5440\uff0c\u6211\u8981\u62a2\u53ea\u62a2\u522b\u4eba\u7684\u3002\u4f60\u83ab\u975e\u7591\u5fc3\u6211\u5411\u4f60\u6253\u4e3b\u610f\uff1f\u597d\u54c7\uff0c\u4f60\u65e2\u8fd9\u6837\u51fa\u8a00\u8fb1 \u6211\uff0c\u6211\u5012\u771f\u8981\u548c\u4f60\u6bd4\u8bd5\u4e00\u4e0b\u4e86\uff0c\u770b\u6211\u6709\u6ca1\u6709\u672c\u9886\u62a2\u4f60\u7684\u5251\uff1f\u201d\u695a\u662d\u5357\u6ee1\u9762\u5806\u6b22\uff0c\u8d76\u5fd9\u7b11\u9053\uff1a \u201c\u8f9b\u5927\u54e5\uff0c\u4f60\u4e0d\u77e5\u539f\u56e0\uff0c\u4e14\u6162\u53d1\u6012\uff0c\u6211\u90a3\u67c4\u6e38\u9f99\u5251\u7ed9\u4eba\u62a2\u53bb\u4e86\u3002\u4f60\u82e5\u6709\u672c\u9886\u62a2\u56de\uff0c\u6211\u81ea\u4e50\u5f97 \u9001\u4f60\u4f7f\u7528\u3002\u201d\u8f9b\u9f99\u5b50\u5947\u9053\uff1a\u201c\u8c01\u4eba\u6562\u62a2\u4f60\u7684\u5b9d\u5251\uff1f\u201d\u695a\u662d\u5357\u9053\uff1a\u201c\u51cc\u672a\u98ce\uff01\u201d\u8f9b\u9f99\u5b50\u9762\u8272\u4e00 \u6697\uff0c\u9ed8\u7136\u4e0d\u8bed\uff0c\u4ed6\u9886\u6559\u8fc7\u51cc\u672a\u98ce\u7684\u5389\u5bb3\uff0c\u81ea\u95ee\u6ca1\u6709\u628a\u63e1\u5728\u51cc\u672a\u98ce\u624b\u4e2d\u628a\u5b9d\u5251\u62a2\u8fc7\u6765\u3002\u695a\u662d\u5357 \u53c8\u7b11\u9053\uff1a\u201c\u6211\u5df2\u67e5\u5f97\u6e05\u695a\uff0c\u90a3\u67c4\u5251\u51cc\u672a\u98ce\u53c8\u8f6c\u9001\u7ed9\u4e00\u4e2a\u5973\u4eba\uff0c\u90a3\u5973\u4eba\u5c31\u662f\u4ee5\u524d\u6d59\u5357\u7684\u5973\u532a\u9996 \u5218\u90b5\u82b3\u3002\u201d\u8f9b\u9f99\u5b50\u6447\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u542c\u8fc7\u8fd9\u4e2a\u540d\u5b57\u3002\u6b66\u529f\u5f3a\u4e0d\u5f3a\u5440\uff1f\u201d\u695a\u662d\u5357\u9053\uff1a\u201c\u4f60\u4e09\u5341 \u5e74\u672a\u5165\u5173\u5185\uff0c\u81ea\u7136\u4e0d\u77e5\u9053\u4e86\u3002\u5218\u90c1\u82b3\u672c\u9886\u867d\u7136\u4e0d\u5f31\uff0c\u4f46\u5374\u4e0d\u662f\u4f60\u6211\u5bf9\u624b\u3002\u201d\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u90a3 \u4f60\u4e3a\u4ec0\u4e48\u4e0d\u81ea\u5df1\u53bb\u62a2\u56de\u6765\uff1f\u201d\u695a\u662d\u5357\u9053\uff1a\u201c\u6211\u7684\u624b\u4e0b\u5df2\u7ecf\u67e5\u63a2\u6e05\u695a\uff0c\u5218\u90c1\u82b3\u548c\u4e00\u5e2e\u4eba\u4ece\u56db\u5ddd \u5230\u56de\u7586\u6765\uff0c\u6211\u6b63\u8981\u5e26\u4eba\u53bb\u515c\u622a\u4ed6\u4eec\u3002\u8fd9\u5e2e\u4eba\u4e2d\u5374\u6709\u51e0\u4e2a\u597d\u624b\u3002\u201d\u8f9b\u9f99\u5b50\u5927\u7b11\u9053\uff1a\u201c\u6211\u867d\u4e09\u5341 \u5e74\u672a\u5165\u5173\u5185\uff0c\u5374\u4e0d\u4fe1\u4e16\u4e0a\u8fd8\u6709\u7b2c\u4e8c\u4e2a\u51cc\u672a\u98ce\uff0c\u7ba1\u4ed6\u6709\u591a\u5c11\u597d\u624b\uff0c\u4f60\u6211\u4e8c\u4eba\u603b\u4e0d\u4f1a\u754f\u60e7\uff0c\u597d\uff0c \u4e00\u8a00\u4e3a\u5b9a\uff0c\u6211\u628a\u5b9d\u5251\u62a2\u6765\u4e4b\u540e\uff0c\u6211\u5c31\u53bb\u627e\u51cc\u672a\u98ce\u518d\u51b3\u9ad8\u4e0b\u3002\u201d\u8f9b\u9f99\u5b50\u90a3\u65e5\u5728\u5929\u5c71\u548c\u51cc\u672a\u98ce\u6bd4 \u8bd5\u8fc7\u540e\uff0c\u81ea\u77e5\u638c\u6cd5\u65e0\u6cd5\u80dc\u4ed6\uff0c\u7acb\u5fc3\u60f3\u7528\u8fbe\u6469\u5251\u6cd5\uff0c\u518d\u548c\u51cc\u672a\u98ce\u6bd4\u8bd5\u3002 \u3000\u3000\u695a\u662d\u5357\u4ee5\u5251\u4e3a\u9975\uff0c\u628a\u8f9b\u9f99\u5b50\u6536\u5f52\u5df1\u7528\u4e4b\u540e\uff0c\u4e00\u65e5\u63a2\u542c\u5f97\u674e\u601d\u6c38\u7b49\u6b63\u5411\u5410\u9c81\u756a\u884c\u6765\uff0c\u6025\u9009 \u4e00\u5343\u7cbe\u9a91\uff0c\u5e26\u4e86\u51e0\u540d\u5927\u5185\u536b\u58eb\uff0c\u4e0e\u8f9b\u9f99\u5b50\u7b49\u534a\u9014\u62e6\u622a\uff0c\u6070\u5de7\u78b0\u5230\u5927\u98ce\u6c99\uff0c\u5085\u9752\u4e3b\u7b49\u4eba\u5230\u4e86\u4e34 \u8fd1\u4e4b\u65f6\uff0c\u65b9\u624d\u53d1\u73b0\uff0c\u4e8e\u662f\u5c55\u5f00\u4e86\u4e00\u573a\u6c99\u6f20\u6076\u6218\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u8eab\u6cd5\u5feb\u6781\uff0c\u4e00\u51fa\u624b\u4fbf\u593a\u5218\u90c1\u82b3\u7684\u5b9d\u5251\uff0c\u5218\u90c1\u82b3\u662f\u65e0\u6781\u5251\u9ad8\u624b\uff0c\u6b66\u529f\u539f\u81ea\u4e0d\u5f31\uff0c\u672c \u6765\u4e0d\u81f3\u4e8e\u4e09\u62db\u4e24\u5f0f\uff0c\u4fbf\u7ed9\u4eba\u62a2\u53bb\u5175\u5203\uff0c\u4f46\u4e0d\u6599\u5979\u53cd\u624b\u4e00\u638c\u6ca1\u6709\u6253\u7740\uff0c\u77f3\u5929\u6210\u5df2\u662f\u8ba4\u51fa\u5e08\u5f1f\uff0c \u60ca\u53eb\u8d77\u6765\uff0c\u5218\u90c1\u82b3\u5f81\u4e86\u4e00\u6014\uff0c\u5b9d\u5251\u5df2\u5230\u654c\u4eba\u624b\u4e2d\u3002 \u3000\u3000\u695a\u662d\u5357\u53ec\u96c6\u7cbe\u9a91\uff0c\u5feb\u9a6c\u51b2\u6765\uff0c\u5085\u9752\u4e3b\u5927\u8896\u4e00\u6325\uff0c\u7387\u4f17\u4eba\u98de\u9a91\u9003\u8dd1\uff0c\u8f9b\u9f99\u5b50\u51dd\u8eab\u4e0d\u52a8\u3002\u695a \u662d\u5357\u53eb\u9053\uff1a\u201c\u4ed6\u4eec\u90a3\u91cc\u8fd8\u6709\u5b9d\u5251\u5440\uff0c\u518d\u62a2\u4e00\u628a\u5427\uff01\u201d\u77f3\u5929\u6210\u6027\u70c8\u5982\u706b\uff0c\u5728\u9a6c\u80cc\u4e0a\u56de\u5934\u9a82\u9053\uff1a \u201c\u8f9b\u9f99\u5b50\uff0c\u4f60\u662f\u4e0d\u662f\u60f3\u53db\u5e08\u5356\u53cb\uff1f\u54b1\u4eec\u6b66\u5f53\u6d3e\u7684\u6212\u6761\u4f60\u90fd\u5fd8\u4e86\u5417\uff1f\u201d\u8f9b\u9f99\u5b50\u5165\u95e8\u5728\u77f3\u5929\u6210\u4e4b \u5148\uff0c\u53ea\u56e0\u77f3\u5929\u6210\u5e74\u7eaa\u6bd4\u4ed6\u5927\uff0c\u800c\u4e14\u662f\u5e26\u827a\u6295\u5e08\uff08\u4ed6\u672c\u662f\u5ddd\u4e2d\u5927\u4fa0\u53f6\u4e91\u82cf\u7684\u5f97\u610f\u5f1f\u5b50\uff09\uff0c\u56e0\u6b64 \u5353\u4e00\u822a\u4e0d\u4f9d\u5165\u95e8\u5148\u540e\u4e3a\u5e8f\uff0c\u8981\u8f9b\u9f99\u5b50\u5c0a\u77f3\u5929\u6210\u4e3a\u5144\u3002\u8f9b\u9f99\u5b50\u672c\u6765\u5c31\u5e76\u4e0d\u628a\u8fd9\u4e2a\u5e08\u5144\u653e\u5728\u773c \u5185\uff0c\u800c\u4e14\u77f3\u5929\u6210\u5728\u5353\u4e00\u822a\u95e8\u4e0b\uff0c\u4e0d\u8fc7\u4e5d\u5e74\uff0c\u5b66\u5230\u7684\u53ea\u662f\u201c\u4e5d\u5b98\u795e\u884c\u638c\u201d\u548c\u201c\u9e33\u9e2f\u8fde\u73af\u817f\u201d\u4e24 \u79cd\u7edd\u6280\uff0c\u800c\u4ed6\u5374\u5728\u5353\u4e00\u822a\u95e8\u4e0b\u4e09\u5341\u591a\u5e74\uff0c\u5c3d\u5f97\u5e08\u95e8\u5fc3\u6cd5\uff0c\u6700\u8fd1\u53c8\u5b66\u4f1a\u4e86\u8fbe\u6469\u4e00\u767e\u96f6\u516b\u5f0f\uff0c\u4e0d \u4f46\u4ee5\u5353\u4e00\u822a\u7684\u8863\u94b5\u4f20\u4eba\u81ea\u5c45\uff0c\u800c\u4e14\u4ee5\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u4eba\u81ea\u547d\uff0c\u8fd8\u68a6\u60f3\u6210\u4e3a\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\uff0c\u4ed6\u5982 \u4f55\u80af\u542c\u77f3\u5929\u6210\u7684\u201c\u6559\u8bad\u201d\uff1f\u77f3\u5929\u6210\u4e0d\u8bf4\u8fd8\u7f62\u4e86\uff0c\u4e00\u8bf4\u4ed6\u5c31\u98de\u63a0\u8fc7\u6765\uff0c\u4e24\u773c\u4e00\u7ffb\uff0c\u602a\u58f0\u7b11\u9053\uff1a \u201c\u4f60\u5728\u5e08\u7236\u95e8\u4e0b\u5b66\u4e86\u4e24\u624b\u529f\u592b\uff0c\u5c31\u6562\u5984\u81ea\u5c0a\u5927\uff1f\u4f60\u51fa\u4e86\u5e08\u95e8\u4e4b\u540e\uff0c\u4e8c\u5341\u591a\u5e74\u6765\u4e0d\u66fe\u56de\u8fc7\u5929 \u5c71\uff0c\u662f\u8c01\u7ec8\u751f\u670d\u5f85\u5e08\u7236\uff1f\u4f60\u6562\u62ac\u51fa\u5e08\u7236\u6765\u6559\u8bad\u6211\uff1f\u201d \u3000\u3000\u4ed6\u53e3\u4e2d\u53d1\u8bdd\uff0c\u624b\u5e95\u4e5f\u4e0d\u7f13\u6162\uff0c\u53cc\u638c\u7ffb\u7ffb\u6eda\u6eda\u76f4\u6253\u8fc7\u6765\uff0c\u77f3\u5929\u6210\u52c3\u7136\u5927\u6012\uff0c\u5728\u9a6c\u80cc\u4e0a\u4e00\u8dc3 \u800c\u4e0b\uff0c\u53f3\u638c\u5411\u5916\u4e00\u6325\uff0c\u5de6\u62f3\u4e00\u4e2a\u201c\u51b2\u5929\u70ae\u201d\u4e0a\u51fb\u4e0b\u989a\uff0c\u8f9b\u9f99\u5b50\u54c8\u54c8\u4e00\u7b11\uff0c\u8eab\u5f62\u5fae\u6643\uff0c\u53cc\u6307\u5ffd \u7136\u5411\u77f3\u5929\u6210\u53f3\u80c1\u70b9\u6765\uff0c\u60f3\u628a\u5e08\u5144\u51fb\u5012\uff0c\u5f00\u4e2a\u5927\u5927\u7684\u73a9\u7b11\u3002\u77f3\u5927\u5a18\u6551\u592b\u5fc3\u6025\uff0c\u9a6c\u80cc\u4e0a\u817e\u8eab\u98de \u4e0b\uff0c\u4e00\u62db\u201c\u9f99\u95e8\u9f13\u6d6a\u201d\uff0c\u9752\u94a2\u5251\u75be\u5982\u98ce\u53d1\uff0c\u76f4\u523a\u8f9b\u9f99\u5b50\u80cc\u5fc3\uff0c\u77f3\u5927\u5a18\u4e43\u662f\u53f6\u4e91\u82cf\u7684\u7231\u5973\uff0c\u6570 \u5341\u5e74\u6765\u4e13\u5b66\u672c\u95e8\u7684\u4e94\u79bd\u5251\u6cd5\uff0c\u6781\u4e3a\u7cbe\u7eaf\uff0c\u8fdc\u5728\u77f3\u5929\u6210\u4e4b\u4e0a\uff0c\u8f9b\u9f99\u5b50\u4e00\u542c\u5251\u98ce\uff0c\u4fbf\u77e5\u6765\u52bf\u751a \u52b2\uff0c\u8eac\u8170\u5411\u524d\u4e00\u7a9c\uff0c\u5218\u90c1\u82b3\u7684\u5947\u95e8\u6697\u5668\u9526\u4e91\u515c\u4e5f\u547c\u5730\u5411\u4ed6\u629b\u53bb\uff0c\u8f9b\u9f99\u5b50\u6a2a\u51fb\u4e00\u638c\uff0c\u7528\u638c\u98ce\u5c06 \u9526\u4e91\u515c\u9707\u6b6a\uff0c\u8eab\u5f62\u53ea\u662f\u7a0d\u5fae\u7f13\u4e86\u4e00\u7f13\uff0c\u53ea\u542c\u5f97\u55e4\u7684\u4e00\u58f0\uff0c\u8863\u8896\u5df2\u7ed9\u77f3\u5927\u5a18\u5229\u5251\u523a\u7a7f\u3002\u8f9b\u9f99\u5b50 \u6025\u5fd9\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u6ed1\u4e86\u5f00\u53bb\uff0c\u7834\u53e3\u5927\u9a7e\uff0c\u77f3\u5927\u5a18\u8fd8\u5f85\u524d\u8ffd\uff0c\u695a\u662d\u5357\u7684\u4eba\u53c8\u5df2\u56f4\u4e0a\uff0c\u5085 \u9752\u4e3b\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u5feb\u9000\uff01\u201d\u957f\u5251\u8d77\u5904\uff0c\u65a9\u4e86\u51e0\u540d\u5175\u58eb\uff0c\u7387\u4f17\u4eba\u51b2\u51fa\u7f3a\u53e3\uff0c\u4e24\u65b9\u90fd\u662f\u9a6c\u5feb\u4eba \u5f3a\uff0c\u5728\u6c99\u6f20\u4e0a\u98ce\u9a70\u7535\u9010\uff0c\u5085\u9752\u4e3b\u3001\u97e9\u8346\u3001\u77f3\u5929\u6210\u592b\u5987\u7b49\u4e00\u6d41\u9ad8\u624b\uff0c\u4e00\u9762\u62e8\u6253\u654c\u4eba\u7684\u51b7\u7bad\uff0c\u65f6 \u4e0d\u65f6\u4e5f\u53d1\u6697\u5668\u62d2\u654c\u3002 \u3000\u3000\u6c99\u6f20\u4e4b\u4e0a\uff0c\u98ce\u9a70\u7535\u9010\uff0c\u77f3\u5929\u6210\u5411\u524d\u4e00\u6307\uff0c\u5bf9\u5085\u9752\u4e3b\u9053\uff1a\u90a3\u8fb9\u6709\u4e2a\u70fd\u706b\u53f0\uff0c\u6211\u4eec\u8fdb\u53bb\u6682\u907f \u4e00\u4f1a\uff0c\u517b\u597d\u7cbe\u795e\uff0c\u665a\u4e0a\u518d\u6740\u51fa\u6765\uff01\u201d\u4f17\u4eba\u5728\u98ce\u66b4\u4e4b\u540e\uff0c\u5927\u591a\u56f0\u987f\uff0c\u8981\u6321\u4e00\u5343\u7cbe\u9a91\uff0c\u5b9e\u4e0d\u53ef \u80fd\uff0c\u5149\u662f\u9003\u8dd1\uff0c\u4e45\u4e86\u4e5f\u5fc5\u88ab\u8ffd\u4e0a\u3002\u5085\u9752\u4e3b\u9053\uff1a\u201c\u53ea\u597d\u5982\u6b64\uff01\u201d\u4f17\u4eba\u53d1\u4e00\u58f0\u558a\uff0c\u62a2\u5165\u5821\u5792\u3002\u70fd \u706b\u53f0\u662f\u50cf\u91d1\u5b57\u5854\u5f62\u7684\u5821\u5792\uff0c\u4e3a\u5386\u4ee3\u9a7b\u519b\u6240\u7b51\uff0c\u6709\u4e8b\u4e4b\u65f6\uff0c\u5728\u4e0a\u9762\u7684\u620d\u5352\uff0c\u70b9\u8d77\u70fd\u706b\uff0c\u53ef\u4ee5\u4e92 \u76f8\u7167\u5e94\u3002\u90a3\u5ea7\u5821\u820d\uff0c\u53ea\u6709\u4e03\u516b\u540d\u620d\u5352\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u5168\u88ab\u6454\u51fa\u5821\u5916\u3002\u4f17\u4eba\u5173\u597d\u77f3\u95e8\uff0c\u5728\u70fd\u706b\u53f0 \u7684\u4e0a\u5c42\u636e\u5b88\u3002 \u3000\u3000\u695a\u662d\u5357\u7b49\u7387\u4f17\u8d76\u5230\uff0c\u628a\u70fd\u706b\u53f0\u56e2\u56e2\u56f4\u4f4f\uff0c\u70fd\u706b\u53f0\u9ad8\u4e94\u4e08\u6709\u4f59\uff0c\u4e0d\u662f\u8f7b\u529f\u6781\u597d\u7684\uff0c\u7eb5\u8dc3\u4e0d \u4e0a\u3002\u695a\u662d\u5357\u548c\u8f9b\u9f99\u5b50\u867d\u7136\u53ef\u4ee5\uff0c\u4f46\u4e0a\u9762\u6709\u5085\u9752\u4e3b\u3001\u77f3\u5929\u6210\u592b\u5987\u548c\u97e9\u8346\u7b49\u4eba\uff0c\u90fd\u662f\u4e00\u6d41\u9ad8\u624b\uff0c \u4e24\u4eba\u4e0a\u53bb\uff0c\u529b\u5fc5\u4e0d\u654c\uff0c\u56e0\u6b64\u6682\u65f6\u6210\u4e86\u50f5\u6301\u4e4b\u5c40\u3002\u695a\u662d\u5357\u7b11\u9053\uff1a\u201c\u56f4\u5b83\u4e09\u5929\uff0c\u4ed6\u4eec\u4e0d\u7d2f\u6b7b\uff0c\u4e5f \u997f\u6b7b\u3002\u201d\u628a\u4e00\u5343\u7cbe\u5175\u5206\u4e3a\u4e09\u6279\u76d1\u89c6\uff0c\u642d\u597d\u5e10\u5e55\uff0c\u81ea\u53bb\u4f11\u606f\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u8ddf\u4e86\u8fdb\u6765\uff0c\u7ffb\u7740\u602a\u773c\uff0c\u5411\u695a\u662d\u5357\u8ba8\u5251\u3002\u695a\u662d\u5357\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u8bf4\u597d\u7684\uff0c\u662f\u4f60\u62a2\u6765\u624d \u80fd\u7ed9\u4f60\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u4e0d\u662f\u6211\u62a2\u6765\u7684\uff0c\u96be\u9053\u662f\u4f60\u62a2\u6765\u7684\u5417\uff1f\u201d\u695a\u662d\u5357\u9053\uff1a\u201c\u4f60\u867d \u7136\u4ece\u5218\u90c1\u82b3\u5904\u67aa\u6765\uff0c\u4f46\u5374\u7ed9\u654c\u4eba\u53cd\u593a\u51fa\u624b\uff0c\u4e0d\u662f\u6211\u65bd\u5c55\u8f7b\u529f\uff0c\u62a2\u5148\u63a5\u7740\uff0c\u8fd8\u4e0d\u662f\u843d\u4eba\u654c\u4eba\u624b \u4e2d\uff1f\u8f9b\u5927\u54e5\uff0c\u8fd9\u628a\u5251\u600e\u4e48\u8bf4\u4e5f\u662f\u6211\u5e08\u7236\u8d50\u7ed9\u6211\u7684\uff0c\u54b1\u4eec\u591a\u5e74\u8001\u53cb\uff0c\u81ea\u5c0f\u5c31\u5728\u5929\u5c71\u4e00\u540c\u73a9\u800d\uff0c \u7b97\u6211\u9886\u4f60\u7684\u60c5\uff0c\u4f60\u5c31\u8ba9\u6211\u6536\u56de\u4e86\u8fd9\u628a\u5251\u5427\u3002\u4f60\u8981\u5b9d\u5251\uff0c\u5305\u5728\u6211\u8eab\u4e0a\uff0c\u6211\u77e5\u9053\u6709\u597d\u591a\u5b9d\u5251\uff0c\u5c06 \u6765\u6211\u5e2e\u4f60\u4e00\u540c\u53bb\u62a2\u3002\u201d\u8f9b\u9f99\u5b50\u65e0\u6cd5\uff0c\u53ea\u597d\u7b54\u5e94\u3002 \u3000\u3000\u518d\u8bf4\u674e\u601d\u6c38\u3001\u6613\u5170\u73e0\u3001\u6b66\u743c\u7476\u4e09\u4eba\u5411\u708a\u70df\u8d77\u5904\u75be\u9a70\u800c\u53bb\uff0c\u7ea6\u4e00\u4e2a\u65f6\u8fb0\uff0c\u8d76\u5230\u5821\u5792\u5916\u9762\uff0c \u4e09\u4eba\u89c1\u6e05\u519b\u628a\u5821\u5792\u56e2\u56e2\u56f4\u4f4f\uff0c\u8bf4\u58f0\u201c\u82e6\u4e5f\uff01\u201d\u6b66\u743c\u7476\u9053\uff1a\u201c\u6740\u8fdb\u53bb\u628a\u4ed6\u4eec\u6551\u51fa\u6765\u5982\u4f55\uff1f\u201d\u674e \u601d\u6c38\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u5085\u9752\u4e3b\u7b49\u82e5\u4e0d\u8d70\u6563\uff0c\u4e00\u5343\u6570\u767e\u6e05\u5175\u4e5f\u56f4\u4ed6\u4eec\u4e0d\u4f4f\uff0c\u53ea\u6015\u5176\u4e2d\u8fd8\u6709\u9ad8 \u624b\u3002\u201d\u8ba1\u8bae\u672a\u5b9a\uff0c\u5de1\u903b\u5175\u65e9\u5df2\u53d1\u73b0\uff0c\u6570\u5341\u540d\u6e05\u519b\uff0c\u9a91\u9a6c\u51b2\u6765\uff0c\u6b66\u63a0\u7476\u53d1\u6697\u5668\u201c\u6233\u9b42\u9489\u201d\u6253\u4f24 \u4e86\u4e94\u516d\u4eba\uff0c\u6613\u5170\u73e0\u5b9d\u5251\u8d77\u5904\u4e16\u65a9\u4e86\u6570\u540d\uff0c\u53ef\u662f\u6e05\u519b\u8d8a\u6765\u8d8a\u591a\uff0c\u7ec8\u4e8e\u628a\u4e09\u4eba\u56f4\u5728\u4e00\u4e2a\u5c0f\u4e18\u4e4b \u4e0a\u3002\u674e\u601d\u6c38\u821e\u8d77\u6d41\u661f\u9524\uff0c\u6e05\u5175\u4e00\u8fd1\uff0c\u4fbf\u88ab\u6253\u5f97\u5934\u5d29\u989d\u88c2\uff1b\u6b66\u743c\u7476\u7684\u201c\u6233\u9b42\u9489\u201d\u4e5f\u5f02\u5e38\u5389\u5bb3\uff0c \u4e13\u6253\u5165\u8eab\u7a74\u9053\uff0c\u53ea\u53ef\u60dc\u4e0d\u80fd\u53ca\u8fdc\u3002\u6e05\u5175\u5728\u79bb\u5f00\u5341\u591a\u4e08\u5904\u56f4\u4f4f\uff0c\u7528\u5f13\u7bad\u731b\u5c04\uff0c\u674e\u601d\u6c38\u548c\u6b66\u743c\u7476 \u98de\u9524\u821e\u5251\uff0c\u626b\u8361\u98de\u7bad\uff0c\u6613\u5170\u73e0\u7528\u5b9d\u5251\u5212\u5f00\u6c99\u77f3\uff0c\u6316\u6210\u4e00\u9053\u7a84\u7a84\u7684\u58d5\u6c9f\uff0c\u4e09\u4eba\u8eb2\u5728\u91cc\u9762\uff0c\u4e0d\u65f6 \u7528\u63a5\u5230\u7684\u6d41\u77e2\u53cd\u51fb\uff0c\u6e05\u519b\u89c1\u4e09\u4eba\u8fd9\u6837\u5389\u5bb3\uff0c\u4e00\u9762\u56f4\u4f4f\uff0c\u4e00\u9762\u56de\u53bb\u7980\u62a5\u3002 \u3000\u3000\u8349\u539f\u65e5\u843d\uff0c\u65b0\u6708\u4e4d\u5347\uff0c\u6b66\u743c\u7476\u5ffd\u7136\u60ca\u53eb\u9053\uff1a\u201c\u4e0d\u597d\u4e86\uff0c\u6e05\u5175\u4e4b\u4e2d\uff0c\u679c\u6709\u9ad8\u624b\uff01\u201d \u3000\u3000\u674e\u601d\u6c38\u63a2\u5934\u770b\u671b\uff0c\u53ea\u89c1\u4e00\u4e2a\u6e05\u766f\u8001\u8005\uff0c\u5982\u559d\u9189\u9152\u4e00\u822c\uff0c\u8eab\u5f62\u6b6a\u6b6a\u659c\u659c\uff0c\u811a\u6b65\u8e09\u8e09\u8dc4\u8dc4\uff0c \u8dcc\u8dcc\u649e\u649e\uff0c\u76f4\u5954\u8fc7\u6765\u3002\u674e\u601d\u6c38\u6014\u4e86\u4e00\u6014\uff0c\u7ade\u4e0d\u77e5\u662f\u54ea\u4e00\u95e8\u7684\u8eab\u6cd5\u3002\u7728\u773c\u4e4b\u95f4\uff0c\u8fd9\u4eba\u5df2\u51b2\u4e0a\u5c0f \u4e18\uff0c\u6b66\u743c\u7476\u4e00\u6296\u624b\uff0c\u4e09\u679a\u201c\u6233\u9b42\u9489\u201d\uff0c\u5982\u6d41\u661f\u98de\u51fa\uff0c\u90a3\u4eba\u5927\u8896\u4e00\u62c2\uff0c\u53ea\u542c\u5f97\u94ee\u94ee\u51e0\u58f0\uff0c\u4e09\u679a \u98de\u9489\uff0c\u7ed9\u4ed6\u62cd\u5f97\u4e92\u76f8\u6fc0\u8361\uff0c\u98de\u5815\u5730\u4e0a\u3002\u674e\u601d\u6c38\u7684\u6d41\u661f\u9524\u547c\u5730\u629b\u51fa\uff0c\u90a3\u4eba\u4e00\u4fa7\u8eab\u8eaf\uff0c\u4f38\u51fa\u53cc\u6307 \u4e00\u5939\uff0c\u72c2\u7b11\u58f0\u4e2d\uff0c\u674e\u601d\u6c38\u7a81\u611f\u624b\u4e0a\u4e00\u8f7b\uff0c\u6d41\u661f\u9524\u7684\u94c1\u7d22\u5df2\u7ed9\u5939\u65ad\u3002 \u3000\u3000\u5954\u6765\u7684\u4eba\u6b63\u662f\u8f9b\u9f99\u5b50\uff0c\u4ed6\u4ee5\u534a\u622a\u6d41\u661f\u9524\u4f5c\u5175\u5668\uff0c\u6a2a\u626b\u8fc7\u53bb\uff0c\u6613\u5170\u73e0\u5a07\u53f1\u4e00\u58f0\uff0c\u77ed\u5251\u4e00 \u626c\uff0c\u628a\u94c1\u7d20\u518d\u65a9\u65ad\u4e00\u622a\uff0c\u9524\u5934\u8dcc\u843d\u5730\u4e0a\u3002\u8eab\u5f62\u75be\u8fdb\uff0c\u201c\u4e91\u9f99\u4e09\u73b0\u201d\uff0c\u4e00\u62db\u4e09\u5f0f\uff0c\u9752\u5149\u5982\u7ec3\uff0c \u5251\u82b1\u9519\u843d\uff0c\u95ea\u7535\u822c\u8fce\u9762\u5c04\u6765\uff0c\u8f9b\u9f99\u5b50\u559d\u58f0\uff1a\u201c\u597d\uff01\u201d\u8eab\u5b50\u51ed\u7a7a\u8dcb\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u659c\u4fa7\u4e00\u843d\uff0c\u6b66 \u743c\u7476\u624b\u8d77\u4e00\u5251\u5206\u5fc3\u523a\u53bb\uff0c\u90a3\u6599\u5251\u950b\u582a\u582a\u523a\u5230\uff0c\u4eba\u5f71\u5ffd\u7136\u4e0d\u89c1\uff01\u597d\u4e2a\u6b66\u743c\u7476\uff0c\u89c1\u5371\u4e0d\u4e71\uff0c\u8170\u5982 \u67f3\u679d\uff0c\u6298\u5730\u4e00\u5f2f\uff0c\u9752\u94a2\u5251\u5212\u4e86\u4e00\u9053\u5706\u5708\uff0c\u94f6\u8679\u73af\u626b\uff0c\u5251\u5149\u638c\u98ce\u4e2d\uff0c\u8f9b\u9f99\u5b50\u75be\u9000\u6570\u6b65\uff0c\u6613\u5170\u73e0 \u5df2\u662f\u62d4\u51fa\u5b9d\u5251\uff0c\u4e0a\u6765\u52a9\u653b\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u72c2\u7b11\u9053\uff1a\u201c\u54c8\uff01\u54c8\uff01\u53c8\u662f\u4e00\u628a\u5b9d\u5251\uff01\u201d\u5408\u7740\u53cc\u638c\uff0c\u5728\u5251\u5149\u4e2d\u6b3a\u8eab\u75be\u8fdb\uff0c\u7167\u6613\u5170\u73e0 \u534e\u76d6\u7a74\u5288\u53bb\uff0c\u6613\u5170\u73e0\u5411\u540e\u4e00\u9000\uff0c\u5168\u8eab\u81ea\u5de6\u5411\u53f3\u4e00\u65cb\uff0c\u4e00\u62db\u201c\u767d\u9e64\u68b3\u7fce\u201d\uff0c\u5b9d\u5251\u731b\u5411\u654c\u4eba\u8155\u65f6 \u75be\u5288\uff0c\u4ee5\u653b\u5bf9\u653b\uff0c\u5341\u5206\u51f6\u9669\uff0c\u8f9b\u9f99\u5b50\u5fae\u201c\u566b\u201d\u4e00\u58f0\uff0c\u8eab\u5f62\u4e00\u632b\uff0c\u811a\u5e95\u4e0b\u6697\u4e00\u6362\u6b65\uff0c\u8eab\u8eaf\u970d\u5730 \u4e00\u7ffb\uff0c\u95ea\u5230\u6613\u5170\u73e0\u80cc\u540e\uff0c\u53cc\u62f3\u9f50\u51fa\uff0c\u7528\u4e86\u8fbe\u6469\u62f3\u4e2d\u6700\u51f6\u6269\u7684\u201c\u8fde\u73af\u4e03\u661f\u9524\u201d\uff0c\u7167\u6613\u5170\u73e0\u7684\u540e \u5fc3\u731b\u51fb\u3002\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u4e4b\u95f4\uff0c\u6b66\u743c\u7476\u6447\u7684\u9752\u94a2\u5251\u5ffd\u5982\u98de\u9e70\u76d8\u7a7a\uff0c\u6402\u5934\u65cb\u626b\uff0c\u8f9b\u9f99\u5b50\u970d\u5730\u56de \u8f6c\u8eab\u6765\uff0c\u53cc\u81c2\u5de6\u53f3\u4e00\u5206\uff0c\u638c\u98ce\u53d1\u51fa\uff0c\u628a\u6b66\u743c\u7476\u7684\u5251\u9707\u6b6a\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4ece\u54ea\u91cc\u5077\u5b66\u6765\u767d\u53d1\u9b54\u5973 \u7684\u5251\u6cd5\uff1f\u201d\u6b66\u743c\u7476\u968f\u767d\u53d1\u9b54\u5973\u4e0d\u8fc7\u4e09\u5e74\uff0c\u5176\u65f6\u8f9b\u9f99\u5b50\u65e9\u5df2\u5728\u9a86\u9a7c\u5cf0\u5750\u5173\uff0c\u5f7c\u6b64\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u6b66\u743c\u7476\u9053\uff1a\u201c\u4f60\u7ba1\u4e0d\u7740\uff01\u201d\u5530\uff01\u5530\uff01\u5530\uff01\u8fde\u73af\u4e09\u5251\uff0c\u8fc5\u75be\u5f02\u5e38\uff0c\u4e00\u62db\u7d27\u4f3c\u4e00\u62db\uff0c\u8f9b\u9f99\u5b50 \u8eab\u5f62\u6ef4\u6e9c\u6e9c\u7684\u968f\u7740\u5251\u950b\u4e71\u8f6c\uff0c\u6b66\u743c\u7476\u7adf\u81ea\u8fde\u4ed6\u7684\u8863\u89d2\u4e5f\u626b\u4e0d\u7740\uff01\u4f46\u4ed6\u89c1\u6b66\u743c\u7476\u5251\u62db\u5982\u7535\uff0c\u4e5f \u7740\u5b9e\u60ca\u5fc3\uff0c\u4e0d\u6562\u5192\u8fdb\u3002\u6613\u5170\u73e0\u8eab\u8f7b\u5982\u71d5\uff0c\u98de\u63a0\u8fc7\u53bb\uff0c\u8f9b\u9f99\u5b50\u8eac\u8170\u75be\u95ea\uff0c\u6613\u5170\u73e0\u56de\u624b\u4e00\u5251\uff0c \u201c\u795e\u9f99\u6389\u5c3e\u201d\uff01\u5411\u4ed6\u8111\u540e\u5241\u5230\u3002\u8f9b\u9f99\u5b50\u907f\u5f97\u5f00\u65f6\uff0c\u6613\u5170\u73e0\u548c\u6b66\u743c\u7476\u5df2\u4e24\u5251\u76f8\u8054\uff0c\u9996\u5c3e\u547c\u5e94\uff0c \u628a\u8f9b\u9f99\u5b50\u8feb\u843d\u58d5\u6c9f\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u7a84\u7a84\u7684\u58d5\u6c9f\u4e2d\uff0c\u8f9b\u9f99\u5b50\u5c55\u5f00\u4e86\u6b66\u6797\u4e2d\u4ec5\u89c1\u7684\u602a\u5f02\u8eab\u6cd5\uff0c\u4e5f\u5c31\u662f\u5931\u4f20\u4e86\u6570\u767e\u5e74\u7684\u8fbe \u6469\u79d8\u6280\uff0c\u95ea\u5c55\u817e\u632a\uff0c\u5728\u65b9\u5bf8\u4e4b\u5730\u76d8\u65cb\u5982\u610f\uff0c\u6613\u5170\u73e0\u6b66\u743c\u7476\u53cc\u5251\u4ea4\u51fb\uff0c\u7adf\u81ea\u4f24\u4ed6\u4e0d\u7740\uff0c\u4f46\u4ed6\u6570 \u5ea6\u60f3\u53cd\u6251\u4e0a\u6765\uff0c\u4e5f\u4e0d\u80fd\u591f\uff01\u6b66\u3001\u6613\u4e8c\u4eba\uff0c\u4e00\u5f97\u767d\u53d1\u9b54\u5973\u771f\u4f20\uff0c\u4e00\u5f97\u5929\u5c71\u5251\u6cd5\u7cbe\u9ad3\uff0c\u9664\u4e86\u529f\u529b \u7a0d\u5dee\u4e4b\u5916\uff0c\u5168\u90fd\u662f\u6700\u4e0a\u4e58\u7684\u5251\u672f\uff0c\u8f9b\u9f99\u5b50\u4e5f\u4ec5\u80fd\u95ea\u907f\uff0c\u65e0\u6cd5\u53cd\u51fb\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6e05\u5175\u5df2\u8d81\u52bf\u6251\u4e0a\u5c0f\u4e18\uff0c\u674e\u601d\u6c38\u4e00\u4eba\u6321\u5f97\u4e1c\u6765\u987e\u4e0d\u4e86\u897f\uff0c\u6b63\u81ea\u624b\u5fd9\u811a \u4e71\uff0c\u6b66\u743c\u7476\u89c1\u72b6\uff0c\u56de\u8eab\u75be\u626b\u4e24\u5251\uff0c\u628a\u4e24\u540d\u8feb\u8fd1\u7684\u6e05\u5175\u65a9\u4f24\uff0c\u674e\u601d\u6c38\u62a2\u4e86\u4e00\u6746\u5927\u67aa\uff0c\u8fdc\u6311\u8fd1 \u6253\u3002\u53ef\u662f\u5c31\u5728\u6b66\u743c\u7476\u5206\u8eab\u5e94\u4ed8\u6e05\u5175\u4e4b\u9645\uff0c\u8f9b\u9f99\u5b50\u5df2\u8dc3\u4e86\u4e0a\u6765\uff0c\u638c\u98ce\u970d\u970d\uff0c\u51cc\u5389\u65e0\u524d\uff0c\u6613\u5170\u73e0 \u7684\u5b9d\u5251\u7adf\u81ea\u5c01\u95ed\u4e0d\u4f4f\uff01 \u3000\u3000\u518d\u8bf4\u5085\u9752\u4e3b\u7b49\u4eba\u5728\u5821\u5792\u4e4b\u4e2d\u8fc7\u4e86\u534a\u591c\uff0c\u517b\u597d\u7cbe\u795e\uff0c\u77f3\u5929\u6210\u9886\u5934\u51b2\u51fa\uff0c\u6e05\u519b\u5206\u73ed\u76d1\u89c6\uff0c\u65e9 \u6709\u9632\u5907\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u7bad\u5982\u96e8\u843d\uff0c\u628a\u4f17\u4eba\u5c04\u9000\uff0c\u5085\u9752\u4e3b\u4e0e\u97e9\u8346\u6253\u4e2a\u62db\u547c\uff0c\u8131\u4e0b\u957f\u886b\uff0c\u84e6\u5730\u5c55\u5f00 \u201c\u94c1\u5e03\u886b\u201d\u529f\u592b\uff0c\u4e0a\u4e0b\u7ffb\u98de\uff0c\u5c31\u5982\u897f\u9762\u76fe\u724c\u4e00\u6837\uff0c\u5c06\u5f2f\u7bad\u6fc0\u8361\u5f97\u56db\u9762\u98de\u5c04\uff0c\u77f3\u5927\u5a18\u7fe9\u7136\u743c \u51fa\uff0c\u5251\u62db\u75be\u53d1\uff0c\u4e00\u4e0b\u5b50\u6251\u4eba\u6e05\u519b\u9635\u4e2d\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u547c\u53eb\u4e4b\u58f0\uff0c\u5f53\u8005\u8f9f\u6613\u3002\u53ef\u662f\u6e05\u519b\u90fd\u662f\u7cbe\u9009 \u7684\u52b2\u5352\uff0c\u5e76\u4e0d\u6e83\u4e71\uff0c\u51e0\u540d\u5927\u5185\u536b\u58eb\uff0c\u75be\u5fd9\u8d76\u6765\u622a\u51fb\uff0c\u6df7\u6218\u4e2d\uff0c\u7fa4\u96c4\u628a\u6e05\u519b\u8282\u8282\u6740\u9000\uff0c\u4f46\u8fd8\u662f \u672a\u80fd\u51b2\u51fa\u5305\u56f4\u3002 \u3000\u3000\u77f3\u5929\u6210\u6740\u5f97\u6027\u8d77\uff0c\u53cc\u638c\u7ffb\u98de\uff0c\u628a\u4e00\u540d\u5927\u5185\u536b\u58eb\u51fb\u5f97\u6a2a\u98de\u51fa\u53bb\uff0c\u968f\u624b\u4e00\u635e\uff0c\u5c06\u4e00\u540d\u6e05\u519b\u6293 \u5728\u624b\u4e2d\uff0c\u6a2a\u626b\u76f4\u51fb\uff0c\u8fd1\u8eab\u7684\u5175\u58eb\uff0c\u5fc3\u5185\u53d1\u614c\u3002\u5085\u9752\u4e3b\u4e0e\u77f3\u5927\u5a18\u4e00\u5de6\u4e00\u53f3\uff0c\u594b\u529b\u51b2\u5f00\u4e00\u6761\u8840 \u8def\uff0c\u6b63\u81ea\u6740\u5f97\u6c99\u5c18\u6eda\u6eda\uff0c\u547c\u53eb\u6684\u5929\u4e4b\u9645\uff0c\u695a\u662d\u5357\u4ed7\u5251\u6740\u6765\uff0c\u77f3\u5927\u5a18\u52c3\u7136\u5927\u6012\uff0c\u8fce\u9762\u4e00\u5251\uff0c\u695a \u662d\u5357\u6a2a\u5251\u4e0a\u5c01\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u77f3\u5927\u5a18\u5c31\u4e00\u8fde\u653b\u4e86\u4e09\u5251\uff0c\u695a\u662d\u5357\u6697\u6697\u60ca\u5947\uff0c\u6599\u4e0d\u5230\u8fd9\u8001\u5a46\u5b50\u7684\u5251 \u6cd5\u5982\u6b64\u5389\u5bb3\uff0c\u4e00\u4e2a\u201c\u697c\u819d\u7ed5\u6b65\u201d\uff0c\u53cd\u5708\u5230\u77f3\u5929\u6210\u80cc\u540e\uff0c\u5bd2\u5149\u4e00\u95ea\uff0c\u6e38\u9f99\u5251\u201c\u7389\u5973\u7a7f\u9489\u201d\uff0c\u671d \u80a9\u540e\u201c\u98ce\u5e9c\u7a74\u201d\u4fbf\u523a\uff0c\u77f3\u5929\u6210\u632b\u8170\u4e00\u8f6c\uff0c\u53cc\u8db3\u75be\u53d1\uff0c\u695a\u662d\u5357\u53e3\u51fb\u4e0d\u4e2d\uff0c\u7fe9\u7136\u5982\u9e70\u96bc\u7a7f\u6797\uff0c\u4ece \u77f3\u5929\u6210\u53f3\u4fa7\u7ed5\u51fa\uff0c\u8eab\u968f\u5251\u8d70\uff0c\u5251\u968f\u8eab\u8f6c\uff0c\u731b\u5730\u7ffb\u8eab\u633a\u5251\uff0c\u53c8\u671d\u97e9\u8346\u7684\u9762\u95e8\u523a\u6765\uff0c\u97e9\u8346\u4e3e\u9f99\u5934 \u62d0\u6756\u594b\u529b\u4e00\u6321\uff0c\u4e01\u5f53\u4e00\u58f0\uff0c\u6756\u5934\u7ed9\u65a9\u53bb\u4e00\u622a\uff0c\u695a\u662d\u5357\u4e5f\u7ed9\u9707\u5f97\u864e\u53e3\u53d1\u70ed\u3002 \u3000\u3000\u695a\u662d\u5357\u7247\u523b\u4e4b\u95f4\uff0c\u8fde\u88ad\u4e09\u540d\u597d\u624b\uff0c\u5085\u9752\u4e3b\u5927\u6012\uff0c\u731b\u7136\u559d\u9053\uff1a\u201c\u9489\u7740\u4ed6\uff01\u201d\u8fd0\u5251\u5982\u98ce\uff0c\u8ffd \u8e2a\u6025\u4e0a\uff0c\u77f3\u5927\u5a18\u3001\u97e9\u8346\u5de6\u53f3\u5305\u6284\uff0c\u695a\u662d\u5357\u5927\u5403\u4e00\u60ca\uff0c\u75be\u5fd9\u540e\u9000\uff0c\u6e05\u5175\u4e3a\u8981\u536b\u62a4\u4e3b\u5e05\uff0c\u53ea\u5f97\u8ddf \u7740\u540e\u9000\uff0c\u7fa4\u96c4\u4ee5\u64d2\u8d3c\u64d2\u738b\u7684\u6218\u6cd5\uff0c\u7d27\u7d27\u8feb\u7740\u695a\u662d\u5357\uff0c\u51b2\u5f00\u4e86\u4e00\u6761\u8840\u8def\uff01 \u3000\u3000\u5085\u9752\u4e3b\u7b49\u4e14\u6218\u4e14\u8d70\uff0c\u5ffd\u95fb\u9644\u8fd1\u53c8\u6709\u5450\u558a\u53ae\u6740\u4e4b\u58f0\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u6b63\u597d\u542c\u5f97\u4e00\u58f0\u5a07\u558a\uff1a\u201c\u5085 \u4f2f\u4f2f\uff0c\u5feb\u6765\uff0c\u5feb\u6765\uff01\u201d\u7adf\u662f\u597d\u53cb\u6b66\u5143\u82f1\u7684\u5973\u513f\u6b66\u743c\u7476\uff0c\u518d\u4ed4\u7ec6\u4e00\u770b\uff0c\u674e\u601d\u6c38\u548c\u6613\u5170\u73e0\u4e5f\u5728\u90a3 \u91cc\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u62fc\u547d\u51b2\u51fa\uff0c\u695a\u662d\u5357\u7387\u4f17\u56de\u5934\u622a\u51fb\uff0c\u987f\u65f6\u53c8\u6210\u80f6\u7740\u72b6\u6001\u3002\u6b66\u743c\u7476\u7b49\u4e09\u4eba\uff0c\u7ed9\u8f9b \u9f99\u5b50\u548c\u6e05\u519b\u56f4\u5728\u5c0f\u4e18\uff0c\u5f62\u52bf\u5341\u5206\u4e0d\u5229\u3002 \u3000\u3000\u6df7\u4e71\u4e2d\uff0c\u97e9\u8346\u5ffd\u7136\u594b\u4e0d\u987e\u8eab\uff0c\u4e00\u6280\u9f99\u5934\u62d0\u6756\u4f7f\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u62fc\u547d\u5411\u695a\u662d\u5357\u6233\u53bb\u3002\u97e9\u8346\u81ea \u6295\u5411\u4e49\u519b\u4e4b\u540e\uff0c\u674e\u6765\u4ea8\u5144\u5f1f\u56e0\u4ed6\u662f\u674e\u5b9a\u56fd\u7684\u65e7\u4eba\uff0c\u4ee5\u8001\u524d\u8f88\u5f85\u4ed6\uff0c\u975e\u5e38\u656c\u91cd\uff0c\u97e9\u8346\u60f3\u8d77\u81ea\u5df1 \u51e0\u4e4e\u8bef\u5165\u7566\u9014\uff0c\u53c8\u662f\u60ed\u6127\uff0c\u53c8\u662f\u611f\u6fc0\uff0c\u6b64\u523b\u89c1\u674e\u601d\u6c38\u9677\u5165\u91cd\u56f4\uff0c\u5b81\u6b7b\u4e5f\u8981\u6551\u51fa\u674e\u601d\u6c38\u3002 \u3000\u3000\u97e9\u8346\u7684\u5929\u9f99\u6756\u6cd5\uff0c\u62db\u62db\u90fd\u662f\u6740\u624b\uff0c\u4e24\u540d\u536b\u58eb\uff0c\u8d76\u6765\u62e6\u622a\uff0c\u4ed6\u7adf\u7136\u5168\u4e0d\u9632\u536b\uff0c\u80a9\u5934\u4e2d\u4e86\u4e00 \u5200\uff0c\u524d\u80f8\u4e2d\u4e86\u4e00\u7bad\uff0c\u90fd\u7f6e\u4e4b\u4e0d\u7406\uff0c\u62d0\u6756\u4e00\u6307\uff0c\u4e00\u540d\u536b\u58eb\u7ed9\u70b9\u4e2d\u7a74\u9053\uff0c\u5012\u5730\u4e0d\u8d77\uff0c\u624b\u8155\u4e00\u7ffb\uff0c \u53c8\u628a\u53e6\u4e00\u540d\u536b\u58eb\u7684\u5929\u7075\u76d6\u51fb\u788e\uff0c\u76f4\u5982\u4e00\u5934\u53d7\u4f24\u7684\u75af\u864e\uff0c\u6d74\u8840\u524d\u51b2\u3002\u695a\u662d\u5357\u5927\u6012\uff0c\u6e38\u9f99\u5251\u75be\u5982 \u98ce\u53d1\uff0c\u94f6\u5149\u531d\u5730\uff0c\u65a9\u8db3\u622a\u8170\uff0c\u97e9\u8346\u5140\u7136\u4e0d\u60e7\uff0c\u9f99\u5934\u62d0\u6756\u5728\u5251\u5149\u4e2d\u76f4\u622a\u8fdb\u53bb\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u91d1\u94c1 \u4ea4\u545c\u4e4b\u58f0\uff0c\u9f99\u5934\u62d0\u6756\u65ad\u4e3a\u51e0\u622a\uff0c\u8170\u80c1\u4e5f\u7ed9\u5251\u5c16\u5212\u7834\u76ae\u8089\uff0c\u4f46\u695a\u662d\u5357\u4e5f\u7ed9\u4ed6\u51fb\u4e2d\u4e00\u638c\uff0c\u53eb\u51fa\u58f0 \u6765\u3002\u7fa4\u96c4\u89c1\u97e9\u8346\u5982\u6b64\u62fc\u547d\uff0c\u4e2a\u4e2a\u594b\u529b\u6740\u4e0a\u3002\u695a\u662d\u5357\u8eab\u5f62\u4e00\u7f29\uff0c\u5feb\u4f3c\u98ce\u8f66\uff0c\u7528\u5929\u5c71\u5251\u6cd5\u72e0\u8fa3\u62db \u6570\uff0c\u659c\u91cc\u4e00\u626b\uff0c\u559d\u9053\uff1a\u201c\u4f60\u60f3\u9001\u6b7b\uff01\u201d\u90a3\u77e5\u97e9\u8346\u7adf\u7136\u4e0d\u907f\u4e0d\u95ea\uff0c\u53cd\u8fce\u4e0a\u53bb\uff0c\u53ea\u542c\u5f97\u6ce2\u7684\u4e00 \u58f0\uff0c\u695a\u662d\u5357\u7684\u5251\u63d2\u5165\u4e86\u4ed6\u7684\u80f8\u819b\uff0c\u800c\u4ed6\u4e5f\u4e00\u6756\u6253\u4e2d\u695a\u662d\u5357\u80eb\u9aa8\uff0c\u695a\u662d\u5357\u5916\u5730\u4e00\u6eda\uff0c\u7ffb\u4e86\u51fa \u53bb\uff0c\u97e9\u8346\u8840\u5982\u6cc9\u6d8c\uff0c\u5012\u5728\u5730\u4e0a\u3002\u5085\u9752\u4e3b\u5c06\u4ed6\u62b1\u8d77\uff0c\u97e9\u8346\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u53bb\u6551\u674e\u516c\u5b50\uff01\u201d\u7adf\u7136 \u6b7b\u5728\u5085\u9752\u4e3b\u6000\u4e2d\u3002 \u3000\u3000\u5085\u9752\u4e3b\u76ee\u4e2d\u8574\u6cea\uff0c\u4e00\u53e3\u5251\u4f7f\u5f97\u51cc\u5389\u65e0\u524d\uff0c\u695a\u662d\u5357\u53d7\u4e86\u97e9\u8346\u4e00\u638c\u4e00\u6756\uff0c\u5143\u6c14\u5927\u4f24\uff0c\u6b63\u81ea\u8c03 \u5300\u547c\u5438\uff0c\u4e0d\u6562\u62e6\u622a\u3002\u7fa4\u96c4\u4e00\u4f1a\u513f\u4fbf\u51b2\u4e0a\u5c0f\u4e18\uff0c\u8f9b\u9f99\u5b50\u8fce\u9762\u4e00\u6293\uff0c\u5085\u9752\u4e3b\u8eab\u79fb\u6b65\u6362\uff0c\u4e00\u5251\u659c \u5288\uff0c\u6b66\u743c\u7476\u3001\u6613\u5170\u73e0\u5de6\u53f3\u6025\u653b\uff0c\u77f3\u5927\u5a18\u4e00\u62db\u201c\u638c\u51fb\u957f\u7a7a\u201d\u66f4\u662f\u8fc5\u6377\u975e\u51e0\uff0c\u540e\u53d1\u5148\u81f3\uff01\u8f9b\u9f99\u5b50 \u8eab\u5f62\u75be\u8f6c\uff0c\u5ffd\u7136\u60e8\u53eb\u4e00\u58f0\uff0c\u8eab\u5f62\u75be\u8d77\uff0c\u4fe8\u5982\u743c\u6ce2\u5de8\u9e1f\uff0c\u4ece\u6613\u5170\u73e0\u5934\u9876\u98de\u51fa\uff0c\u5085\u9752\u4e3b\u7b49\u4e5f\u4e0d\u8ffd \u8d76\uff0c\u548c\u674e\u601d\u6c38\u4f1a\u5728\u4e00\u5904\uff0c\u89c1\u4ed6\u4eec\u4e09\u4eba\u90fd\u6beb\u53d1\u65e0\u4f24\uff0c\u8fd9\u624d\u653e\u4e0b\u5fc3\u6765\u3002 \u3000\u3000\u77f3\u5927\u5a18\u53f9\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u6b66\u529f\u771f\u662f\u6211\u751f\u5e73\u7f55\u89c1\uff0c\u4ed6\u80a9\u5934\u5df2\u7ed9\u6211\u626b\u4e86\u4e00\u5251\uff0c\u8fd8\u80fd\u591f\u98de\u8eab\u9003 \u51fa\uff0c\u786e\u662f\u52b2\u654c\u3002\u53ea\u53ef\u60dc\u4ed6\u8bef\u5165\u6b67\u9014\u3002\u201d \u3000\u3000\u77f3\u65e0\u6210\u6697\u6697\u8be7\u5f02\uff0c\u4ed6\u867d\u7136\u672a\u5f97\u5e08\u95e8\u771f\u4f20\uff0c\u4f46\u770b\u8f9b\u9f99\u5b50\u7684\u8eab\u6cd5\uff0c\u5374\u5b8c\u5168\u4e0d\u662f\u5e08\u7236\u6240\u6559\uff0c\u4f17 \u4eba\u90fd\u4e0d\u77e5\u4ed6\u662f\u4ec0\u4e48\u8def\u6570\u3002 \u3000\u3000\u5085\u9752\u4e3b\u5c06\u97e9\u8346\u653e\u4e0b\u58d5\u6c9f\uff0c\u5c06\u4ed6\u57cb\u4e86\u3002\u674e\u601d\u6c38\u606d\u606d\u656c\u656c\u5730\u53e9\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u6293\u8d77\u957f\u67aa\uff0c\u8bf4 \u9053\uff1a\u201c\u6211\u4eec\u51b2\u51fa\u53bb\u3002\u201d\u5ffd\u89c1\u6e05\u5175\u4e24\u8fb9\u5206\u5f00\uff0c\u53c8\u662f\u4e00\u961f\u4eba\u9a6c\u8d76\u6765\u3002\u4e3a\u9996\u4e00\u4e2a\u8001\u8005\uff0c\u987b\u7709\u5982\u96ea\uff0c \u624b\u4f7f\u4e24\u67c4\u957f\u5251\uff0c\u8eab\u6cd5\u6781\u5feb\uff0c\u6210\u5929\u633a\u8ddf\u5728\u4ed6\u7684\u8eab\u540e\uff0c\u867d\u7136\u75be\u8dd1\uff0c\u5374\u603b\u662f\u6709\u4e03\u516b\u6b65\u8ddd\u79bb\u3002\u5085\u9752\u4e3b \u6035\u7136\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u6b66\u529f\u770b\u6765\u8fd8\u5728\u695a\u662d\u5357\u4e4b\u4e0a\u3002\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u90a3\u8001\u8005\u5df2\u51b2\u4e0a\u6765\uff0c \u53cc\u5251\u5de6\u53f3\u4e00\u526a\uff0c\u628a\u5085\u9752\u4e3b\u7684\u5251\u51e0\u4e4e\u7ede\u5f97\u8131\u624b\u98de\u51fa\uff0c\u4f46\u5085\u9752\u4e3b\u662f\u4e00\u6d3e\u5b97\u5e08\uff0c\u5251\u6cd5\u975e\u540c\u5c0f\u53ef\uff0c\u8d81 \u52bf\u4e00\u9001\uff0c\u89e3\u4e86\u6765\u52bf\uff0c\u5587\u5730\u4e00\u5251\u523a\u51fa\uff0c\u4e5f\u662f\u8fc5\u6377\u5f02\u5e38\u3002\u90a3\u8001\u8005\u6b63\u662f\u957f\u767d\u5c71\u6d3e\u7684\u7956\u5e08\u98ce\u96f7\u5251\u9f50\u771f \u541b\uff0c\u5085\u9752\u4e3b\u63a5\u4e86\u4e00\u62db\uff0c\u77e5\u9053\u5bf9\u65b9\u529f\u529b\u6781\u9ad8\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u65e0\u6781\u5251\u4e00\u62db\u201c\u8fce\u98ce\u626b\u67f3\u201d\uff0c\u5c06\u9f50\u771f\u541b \u53f3\u624b\u957f\u5251\u7c98\u7740\uff0c\u5927\u8896\u4e00\u62c2\uff0c\u65bd\u5c55\u5e73\u751f\u7edd\u6280\uff0c\u53c8\u5c06\u9f50\u771f\u541b\u5de6\u5251\u88f9\u7740\uff0c\u77f3\u5927\u5a18\u6dae\u7684\u4e00\u5251\u523a\u6765\uff0c\u9f50 \u771f\u541b\u624b\u8155\u4e00\u6c89\uff0c\u4f7f\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u8ba9\u8fc7\u77f3\u5927\u5a18\u7684\u5251\uff0c\u53cc\u5251\u521a\u521a\u64a4\u56de\uff0c\u90a3\u6599\u77f3\u5927\u5a18\u5de6\u4e00\u5251\u53f3\u4e00 \u5251\uff0c\u5251\u62db\u8d8a\u5c55\u8d8a\u5feb\uff0c\u9f50\u771f\u541b\u7ed9\u8feb\u5f97\u56e2\u56e2\u4e71\u8f6c\uff0c\u5f85\u81f3\u817e\u5251\u683c\u6321\u65f6\uff0c\u5df2\u7ed9\u5979\u4e00\u8fde\u653b\u4e86\u4e03\u516b\u5251\u3002 \u3000\u3000\u6210\u5929\u633a\u5982\u98de\u8d76\u81f3\uff0c\u6b63\u78b0\u7740\u5085\u9752\u4e3b\u4e00\u5251\u523a\u51fa\uff0c\u4ed6\u53cc\u7b14\u201c\u6a2a\u67b6\u91d1\u6881\u201d\uff0c\u5411\u4e0a\u4e00\u6321\uff0c\u53ea\u542c\u5f97\u4e86 \u5f53\u4e00\u58f0\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u53cc\u7b14\u7adf\u7ed9\u8361\u5f00\uff0c\u4f46\u4ed6\u8eab\u5f62\u7adf\u662f\u7eb9\u4e1d\u4e0d\u52a8\u3002\u5085\u9752\u4e3b\u6697\u8d5e\u201c\u597d\u529f\u529b\u201d\uff0c\u65e0\u6781 \u5251\u5212\u4e86\u534a\u4e2a\u5f27\u5f62\uff0c\u7528\u4e86\u5341\u6210\u6c14\u529f\uff0c\u6162\u6162\u5212\u53bb\uff0c\u6210\u5929\u633a\u53ea\u89c9\u4e00\u80a1\u6781\u5927\u538b\u529b\u63a8\u6765\uff0c\u7acb\u8db3\u4e0d\u7a33\uff0c\u8fde \u9053\u51e0\u6b65\uff0c\u4f46\u53cc\u7b14\u4ecd\u662f\u53d1\u62db\uff0c\u867d\u8d25\u672a\u4e71\u3002 \u3000\u3000\u90a3\u8fb9\u53a2\u9f50\u771f\u541b\u7a33\u4e86\u8eab\u5f62\uff0c\u53cc\u5251\u547c\u547c\u5c55\u5f00\uff0c\u9690\u9690\u5e26\u7740\u98ce\u96f7\u4e4b\u58f0\uff0c\u62db\u6570\u53c8\u53d8\u5316\u7e41\u590d\uff0c\u865a\u5b9e\u83ab \u6d4b\uff0c\u77f3\u5927\u5a18\u529f\u529b\u5230\u5e95\u7a0d\u900a\u4e00\u7b79\uff0c\u4e94\u79bd\u5251\u6cd5\u867d\u7136\u8fc5\u6377\u65e0\u4f26\uff0c\u5374\u5982\u78b0\u7740\u4e86\u94dc\u5899\u94c1\u58c1\uff0c\u65e0\u6cd5\u8fdb\u653b\uff0c \u4f46\u9f50\u771f\u541b\u7528\u8db3\u4e86\u6c14\u529b\uff0c\u624d\u963b\u904f\u5f97\u5979\u7684\u653b\u52bf\uff0c\u4ea6\u662f\u4e0d\u89c9\u6697\u6697\u60ca\u5947\uff0c\u60f3\u4e0d\u5230\u5728\u53d7\u632b\u4e8e\u51cc\u672a\u98ce\u4e4b \u540e\uff0c\u53c8\u4e00\u8fde\u78b0\u7740\u4e24\u4e2a\u597d\u624b\u3002 \u3000\u3000\u77f3\u5927\u5a18\u8fed\u9047\u9669\u62db\uff0c\u77e5\u9053\u4e45\u6218\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u8fd9\u65f6\u6e05\u5175\u56f4\u4e86\u4e0a\u6765\uff0c\u8fd8\u6742\u6709\u8bb8\u591a\u7ef4\u4eba\uff0c\u77f3\u5929 \u6210\u3001\u6613\u5170\u73e0\u7b49\u4eba\u6b63\u636e\u5c0f\u4e18\u4f5c\u6218\uff0c\u6b66\u743c\u8c23\u770b\u89c1\u77f3\u5927\u5a18\u5904\u5728\u4e0b\u98ce\uff0c\u4e00\u5251\u98de\u6765\u52a9\u5979\u4e00\u81c2\u3002\u6b66\u743c\u7476\u4f7f \u7684\u662f\u767d\u53d1\u9b54\u5973\u7684\u72ec\u95e8\u5251\u6cd5\uff0c\u4e00\u62db\u201c\u51b0\u5ddd\u5012\u6cfb\u201d\u5251\u950b\u81ea\u4e0a\u800c\u4e0b\uff0c\u7a0d\u4e00\u98a4\u52a8\uff0c\u4fbf\u662f\u5bd2\u5149\u70b9\u70b9\uff0c\u51b7 \u6c14\u68ee\u68ee\uff0c\u5f84\u81ea\u903c\u6765\u3002\u9f50\u771f\u541b\u53cc\u5251\u4e00\u5c01\uff0c\u88ab\u8feb\u9000\u5b88\uff0c\u77f3\u5927\u5a18\u5251\u6cd5\u4f55\u7b49\u5feb\u6377\uff0c\u8d81\u52bf\u4e00\u5251\uff0c\u4ece\u9f50\u771f \u541b\u80a9\u5934\u64e6\u8fc7\uff0c\u9f50\u771f\u541b\u4e00\u5251\u6321\u4f4f\u6b66\u743c\u7476\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u518d\u628a\u77f3\u5927\u5a18\u8feb\u9000\u3002\u4f46\u5979\u4eec\u4e8c\u4eba\u8054\u624b\uff0c\u5df2\u662f \u628a\u9f50\u771f\u541b\u56f4\u5728\u5251\u5149\u4e4b\u4e2d\u3002 \u3000\u3000\u4e14\u8bf4\u51cc\u672a\u98ce\u4e0e\u98de\u7ea2\u5dfe\u4e0b\u5c71\u4e4b\u540e\uff0c\u4e00\u76f4\u5bfb\u627e\uff0c\u51cc\u672a\u98ce\u3001\u98de\u7ea2\u5dfe\u548c\u8bb8\u591a\u7267\u6c11\u76f8\u719f\uff0c\u90a3\u65e5\u542c\u8bf4 \u4e00\u4e2a\u5c11\u5973\u5411\u5410\u9c81\u756a\u524d\u8fdb\uff0c\u4e00\u95ee\u76f8\u8c8c\uff0c\u6b63\u662f\u6613\u5170\u73e0\u3002\u5f20\u534e\u662d\u5fc3\u4e2d\u5927\u559c\uff0c\u5411\u98de\u7ea2\u5dfe\u518d\u4e09\u9053\u8c22\u3002\u98de \u7ea2\u5dfe\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u518d\u62e6\u963b\u4f60\u4e86\uff0c\u4f60\u5e94\u8be5\u591a\u8c22\u4f60\u7684\u51cc\u53d4\u53d4\u3002\u201d\u4e24\u4eba\u4e00\u7b11\uff0c\u52a0\u5feb\u811a\u7a0b\uff0c\u671d\u5410\u9c81\u756a \u884c\u53bb\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u9635\uff0c\u5ffd\u7136\u78b0\u7740\u5927\u98ce\u6c99\uff0c\u98de\u7ea2\u5dfe\u5728\u8349\u539f\u957f\u5927\uff0c\u77e5\u9053\u5389\u5bb3\uff0c\u653e\u773c\u627e\u5bfb\u63a9\u853d\u4e4b\u5730\uff0c\u5ffd\u89c1 \u4e0d\u8fdc\u4e4b\u5904\uff0c\u6709\u4e00\u5ea7\u5927\u5e10\u5e55\uff0c\u98de\u7ea2\u5dfe\u5e26\u4f17\u4eba\u53e9\u5e10\u76f4\u5165\uff0c\u53ea\u89c1\u5e10\u4e2d\u70b9\u7740\u4e00\u652f\u5927\u725b\u6cb9\u70db\uff0c\u5730\u4e0a\u8eba\u7740 \u4e00\u4e2a\u4e2d\u5e74\u7537\u5b50\uff0c\u65c1\u8fb9\u6709\u4e00\u7537\u4e00\u5973\u5b88\u62a4\uff0c\u98de\u7ea2\u5dfe\u770b\u4e86\u4e00\u773c\uff0c\u5ffd\u7136\u53eb\u8d77\u6765\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u4e0d\u662f\u9ea6 \u76d6\u63d0\u548c\u66fc\u94c3\u5a1c\uff1f\u201d\u90a3\u5973\u7684\u51dd\u795e\u7ec6\u770b\uff0c\u4e5f\u53eb\u8d77\u6765\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u600e\u4e48\u53d8\u6210\u8fd9\u4e2a\u6837\u5b50\uff1f\u201d\u4e09\u4eba \u72c2\u559c\u6d41\u6cea\uff0c\u4e92\u76f8\u62e5\u62b1\u3002\u5730\u4e0a\u8eba\u7f6e\u7684\u90a3\u4e2a\u7537\u4eba\uff0c\u7741\u5f00\u53cc\u773c\uff0c\u5636\u58f0\u8bf4\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u662f\u4f60\u5417\uff1f\u4f60 \u8981\u66ff\u6211\u62a5\u4ec7\uff01\u201d\u98de\u7ea2\u5dfe\u8df3\u8d77\u6765\u9053\uff1a\u201c\u5440\uff0c\u4f0a\u58eb\u8fbe\uff0c\u4f60\u4e5f\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u62db\u624b\u53eb\u51cc\u672a\u98ce\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u662f\u4f60\u6768\u5e08\u5144\u7684\u76df\u5f1f\uff0c\u5f53\u5e74\u4ed6\u4eec\u4e09\u4eba\u66fe\u6a2a\u8d8a\u5854 \u514b\u62c9\u9a6c\u5e72\u5927\u6c99\u6f20\uff0c\u4ece\u5317\u7586\u6765\u5230\u5357\u7586\u3002\u201d\uff08\u8be6\u89c1\u62d9\u4f5c\u300a\u585e\u5916\u5947\u4fa0\u4f20\u300b\uff09\u9ea6\u76d6\u63d0\u9053\uff1a\u201c\u4f60\u5c31\u662f\u6768 \u5927\u4fa0\u7684\u5e08\u5f1f\u51cc\u672a\u98ce\u5417\uff1f\u201d\u51cc\u672a\u98ce\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4f60\u4eec\u548c\u6768\u5e08\u5144\u662f\u516b\u62dc\u4e4b\u4ea4\uff0c\u90a3\u4e5f\u5c31\u662f\u6211\u7684\u5144 \u957f\u3002\u201d\u8bf4\u7f62\u62dc\u5c06\u4e0b\u53bb\uff0c\u9ea6\u76d6\u63d0\u6025\u5fd9\u8fd8\u793c\uff0c\u4f0a\u58eb\u8fbe\u7a81\u7136\u4ee5\u8098\u652f\u5730\uff0c\u6323\u624e\u8d77\u6765\uff0c\u65ad\u65ad\u7eed\u7eed\u5730\u8bf4 \u9053\uff1a\u201c\u51cc\u672a\u98ce\uff0c\u6211\u60f3\u89c1\u4f60\u8bb8\u4e45\u4e86\uff0c\u73b0\u5728\u624d\u89c1\u7740\uff0c\u53ef\u60dc\u5df2\u7ecf\u8fdf\u4e86\u3002\u6211\u8fd9\u91cc\u6709\u628a\u5b9d\u5251\uff0c\u662f\u4f60\u5e08\u5144 \u5f53\u5e74\u7ed9\u6211\u7684\uff0c\u73b0\u5728\u6211\u7528\u4e0d\u7740\u4e86\uff0c\u4f60\u62ff\u53bb\u66ff\u6211\u62a5\u4ec7\u5427\u3002\u201d\u8bf4\u7f62\u53cc\u773c\u4e00\u7ffb\uff0c\u5c31\u6b64\u4e00\u7791\u4e0d\u89c6\u3002 \u3000\u3000\u6768\u4e91\u9aa2\u98de\u7ea2\u5dfe\u548c\u9ea6\u76d6\u63d0\u4f0a\u58eb\u8fbe\u56db\u4eba\uff0c\u5f53\u5e74\u90fd\u662f\u751f\u6b7b\u7684\u4ea4\u60c5\uff0c\u9ea6\u4f0a\u4e8c\u4eba\u4e43\u662f\u54c8\u8428\u514b\u65cf\u6709\u540d \u7684\u52c7\u571f\uff0c\u6768\u4e91\u9aa2\u6218\u6b7b\uff0c\u98de\u7ea2\u5dfe\u9690\u5c45\uff0c\u9ea6\u76d6\u63d0\u548c\u4f0a\u58eb\u8fbe\u5728\u8349\u539f\u6d41\u6d6a\u3002\u66fc\u94c3\u5a1c\u662f\u4e00\u4f4d\u7267\u7f8a\u59d1\u5a18\uff0c \u548c\u9ea6\u76d6\u63d0\u662f\u9752\u6885\u7af9\u9a6c\u7684\u53cb\u4eba\uff0c\u540e\u6765\u548c\u9ea6\u76d6\u63d0\u7ed3\u5a5a\uff0c\u4e09\u4eba\u5e38\u5728\u4e00\u8d77\u3002 \u3000\u3000\u98de\u7ea2\u5dfe\u5fcd\u7740\u773c\u6cea\uff0c\u5bf9\u9ea6\u76d6\u63d0\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u6765\uff0c\u6211\u79bb\u5f00\u4f60\u4eec\uff0c\u5b9e\u5728\u611f\u5230\u60ed\u6127\u3002\u201d\u9ea6\u76d6\u63d0 \u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u56de\u6765\u4e86\uff0c\u90a3\u5c31\u597d\u4e86\uff0c\u4f60\u7ed9\u6211\u4eec\u589e\u6dfb\u4e0d\u5c11\u52c7\u6c14\u3002\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u662f\u7684\uff0c\u548c\u5927 \u4f19\u513f\u5728\u4e00\u8d77\uff0c\u4ec0\u4e48\u82e6\u96be\u90fd\u5fcd\u53d7\u5f97\u4f4f\u3002\u4f0a\u58eb\u8fbe\u6b7b\u4e86\uff0c\u6211\u4eec\u4f1a\u8e0f\u8fc7\u4ed6\u9c9c\u8840\u67d3\u7ea2\u7684\u6ce5\u58eb\uff0c\u66ff\u4ed6\u62a5\u4ec7 \u7684\u3002\u201d \u3000\u3000\u5e10\u5e55\u5916\u5927\u98ce\u4e2d\u9ea6\u76d6\u63d0\u7528\u4f4e\u6c89\u7684\u58f0\u8c03\u8bc9\u8bf4\u4f0a\u58eb\u8fbe\u6b7b\u7684\u4e8b\u3002\u9ea6\u76d6\u63d0\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u4f60\u8fd8\u8bb0\u5f97 \u90a3\u4e2a\u5580\u8fbe\u5c14\u65cf\u7684\u914b\u957f\u5b5f\u7984\u5417\uff1f\u5f53\u5e74\u4ed6\u4e3a\u4e86\u6768\u5927\u4fa0\u548c\u7eb3\u5170\u79c0\u5409\u5973\u513f\u7684\u4e8b\uff0c\u66fe\u8bec\u8511\u6768\u5927\u4fa0\u662f\u5978 \u7ec6\uff0c\u8c01\u77e5\u4ed6\u624d\u662f\u5978\u7ec6\u3002\u6e05\u5ef7\u6700\u8fd1\u6d3e\u4eba\u548c\u4ed6\u8054\u7edc\uff0c\u53eb\u4ed6\u6e38\u8bf4\u5357\u7586\u5404\u65cf\uff0c\u6295\u987a\u671d\u5ef7\u3002\u6211\u4eec\u4e09\u4eba\u4e00 \u70b9\u4e5f\u4e0d\u77e5\u9053\u6b64\u4e8b\uff0c\u5230\u4e86\u5357\u7586\u7684\u5580\u5c14\u6c81\u8349\u539f\uff0c\u4ecd\u7136\u5230\u4ed6\u90a3\u91cc\u4f5c\u5ba2\u3002\u6b63\u5de7\u6e05\u5ef7\u6d3e\u4e86\u4e00\u4e2a\u4f7f\u8005\u6765\uff0c \u90a3\u4f7f\u8005\u662f\u4e2a\u9ae6\u7709\u7686\u767d\u7684\u8001\u8005\uff0c\u636e\u8bf4\u662f\u4ec0\u4e48\u957f\u767d\u5c71\u6d3e\u7684\u7956\u5e08\u3002\u5b5f\u7984\u805a\u96c6\u4e00\u5411\u542c\u4ed6\u8bdd\u7684\u4e09\u65cf\u5341\u4e8c \u90e8\u843d\u7684\u914b\u957f\u4f1a\u8c08\uff0c\u4e0d\u6599\u5176\u4e2d\u5374\u6709\u4e03\u4e2a\u90e8\u843d\u4e0d\u613f\u6295\u987a\uff0c\u4f0a\u58eb\u8fbe\u5c24\u5176\u4e49\u6124\u586b\u80f8\uff0c\u5927\u58f0\u65a5\u8d23\u5b5f\u7984\uff0c \u56e0\u6b64\u53c8\u6709\u4e24\u4e2a\u90e8\u843d\u8131\u79bb\u4e86\u5b5f\u7984\uff0c\u4e5d\u4e2a\u90e8\u843d\u7684\u914b\u957f\u548c\u4ed6\u4eec\u5e26\u6765\u7684\u4eba\u4e00\u8d77\u79bb\u5f00\uff0c\u4f0a\u58eb\u8fbe\u8fd8\u60f3\u518d\u529d \u5b5f\u7984\u56de\u5934\uff0c\u5b5f\u7984\u7a81\u7136\u53d8\u8138\uff0c\u628a\u4f0a\u58eb\u8fbe\u65a9\u4e86\u4e00\u5200\uff0c\u6211\u4eec\u4e24\u4eba\u62fc\u547d\u6551\u4ed6\u8131\u9669\uff0c\u5b5f\u7984\u6015\u5176\u4ed6\u7684\u4eba\u62b1 \u4e0d\u5e73\uff0c\u4e0d\u6562\u8ffd\u8d76\u3002\u6211\u4eec\u5c06\u4f0a\u58eb\u8fbe\u6551\u51fa\u4e4b\u540e\uff0c\u4e0d\u6599\u53c8\u9047\u7740\u4e86\u98ce\u6c99\uff0c\u60f3\u4e0d\u5230\u4ed6\u8eab\u7ecf\u767e\u6218\uff0c\u4e0d\u6b7b\u5728 \u654c\u4eba\u624b\u4e2d\uff0c\u5374\u6b7b\u5728\u2018\u81ea\u5df1\u4eba\u2019\u7684\u5200\u4e0b\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u9ed8\u9ed8\u5411\u4f0a\u58eb\u8fbe\u81f4\u656c\uff0c\u5c31\u7528\u4f0a\u58eb\u8fbe\u7ed9\u4ed6\u7684\u5251\u6316\u5f00\u6c99\u571f\uff0c\u5c06\u4f0a\u58eb\u8fbe\u57cb\u846c\u3002\u9ea6\u76d6\u63d0\u9053\uff1a \u201c\u8fd9\u628a\u5251\u662f\u6768\u5927\u4fa0\u5f53\u5e74\u5728\u897f\u85cf\u5929\u9f99\u6d3e\u624b\u4e2d\u62a2\u8fc7\u6765\u7684\u3002\u5929\u9f99\u6d3e\u7684\u5929\u8499\u7985\u5e08\u5e26\u5341\u516b\u540d\u5f1f\u5b50\u5305\u56f4 \u4ed6\uff0c\u7ed9\u4ed6\u7f34\u4e86\u5341\u4e5d\u628a\u5175\u5203\u3002\u201d\u51cc\u672a\u98ce\u89c1\u8fd9\u628a\u5251\u5bd2\u5149\u593a\u76ee\uff0c\u770b\u6765\u4e0d\u5728\u6e38\u9f99\u5251\u4e4b\u4e0b\uff0c\u672c\u6765\u60f3\u8fd8\u7ed9 \u9ea6\u76d6\u63d0\u7684\uff0c\u7a81\u7136\u5fc3\u4e2d\u60f3\u8d77\u4e00\u4e8b\uff0c\u6539\u53d8\u4e3b\u610f\uff0c\u628a\u5251\u7559\u4e0b\u3002\u8fd9\u65f6\u98ce\u6c99\u5df2\u606f\uff0c\u51cc\u672a\u98ce\u970d\u7136\u8d77\u7acb\uff0c\u8bf4 \u9053\uff1a\u201c\u98ce\u66b4\u8fc7\u53bb\u4e86\uff0c\u6211\u4eec\u5411\u524d\u8d70\u5427\uff01\u201d \u3000\u3000\u65e0\u5de7\u4e0d\u5de7\uff0c\u4ed6\u4eec\u6240\u8d70\u7684\u65b9\u5411\uff0c\u53ef\u6b63\u662f\u674e\u601d\u6c38\u3001\u6613\u5170\u73e0\u7b49\u4eba\u88ab\u56f4\u56f0\u7684\u5730\u65b9\u3002\u800c\u6b64\u9645\uff0c\u5728\u6e05 \u5175\u7684\u9635\u8425\u91cc\uff0c\u4e5f\u6b63\u53d1\u751f\u7740\u4e00\u4ef6\u610f\u60f3\u4e0d\u5230\u7684\u4e8b\u3002 \u3000\u3000\u695a\u662d\u5357\u5403\u4e86\u97e9\u8346\u4e00\u6756\u4e00\u638c\uff0c\u4f24\u52bf\u4e0d\u8f7b\uff0c\u4ed7\u7740\u5185\u529f\u6df1\u6e5b\uff0c\u8c03\u5300\u547c\u5438\uff0c\u53c8\u670d\u4e86\u7528\u5929\u5c71\u96ea\u83b2\u6240 \u5236\u70bc\u7684\u78a7\u7075\u4e39\uff0c\u8fd0\u6c14\u4e00\u8f6c\uff0c\u4e00\u80a1\u6696\u6c14\uff0c\u4ece\u4e39\u7530\u76f4\u5347\u4e0a\u6765\uff0c\u81ea\u89c9\u529f\u529b\u6bd4\u524d\u9ad8\u4e86\u8bb8\u591a\uff0c\u6697\u81ea\u6b23 \u6170\uff0c\u4f46\u4e00\u60f3\u8d77\u51cc\u672a\u98ce\u5374\u6bd4\u81ea\u5df1\u8fd8\u9ad8\uff0c\u53c8\u4e0d\u7981\u6697\u6697\u4e27\u6c14\uff0c\u6b63\u60f3\u518d\u53bb\u89c6\u5bdf\u6218\u60c5\uff0c\u5ffd\u89c1\u8f9b\u9f99\u5b50\u6c14\u6025 \u8d25\u574f\u5730\u9003\u4e0b\u6765\uff0c\u53f3\u80a9\u4e00\u7247\u9c9c\u8840\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e86\uff1f\u201d\u8f9b\u9f99\u5b50\u6012\u9053\uff1a\u201c\u4f60\u8fd8 \u95ee\u54e9\uff1f\u90fd\u662f\u4f60\u53eb\u6211\u53bb\u62a2\u4ec0\u4e48\u5b9d\u5251\uff0c\u54ea\u77e5\u654c\u4eba\u4e2a\u4e2a\u90fd\u662f\u9ad8\u624b\uff0c\u6211\u7adf\u7136\u7ed9\u4e00\u4e2a\u8001\u4e5e\u5a46\u523a\u4e86\u4e00\u5251\uff0c \u597d\u53ea\u662f\u8f7b\u4f24\uff0c\u8981\u4e0d\u7136\u771f\u4f1a\u628a\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\u57cb\u5728\u6c99\u6f20\u3002\u54fc\uff0c\u6211\u518d\u4e5f\u4e0d\u7406\u4f60\u4e86\uff01\u201d\u8fb9\u8bf4\u8fb9\u6495\u5f00\u80a9 \u4e0a\u9ebb\u8863\uff0c\u6577\u4e0a\u4e86\u91d1\u521b\u5723\u836f\u3002\u695a\u662d\u5357\u9053\uff1a\u201c\u6211\u4eec\u51e0\u5341\u5e74\u670b\u53cb\uff0c\u4f60\u5c31\u4e0d\u5e2e\u6211\u4e00\u70b9\u5fd9\uff0c\u771f\u7684\u8981 \u8d70\uff1f\u201d\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u6211\u8981\u56de\u5929\u5c71\u7ec3\u5251\uff0c\u8c01\u8010\u70e6\u8ddf\u4f60\u505a\u5b98\u3002\u201d\u8bf4\u7f62\u4e00\u4f5b\u9ebb\u8863\u5927\u5730\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002 \u3000\u3000\u695a\u662d\u5357\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u8f9b\u5927\u54e5\uff0c\u4e14\u6162\uff01\u201d\u8f9b\u9f99\u5b50\u56de\u5934\u9053\uff1a\u201c\u4f60\u522b\u60f3\u518d\u7559\u6211\u4e86\uff01\u201d\u695a\u662d\u5357 \u9053\uff1a\u201c\u6211\u4e0d\u662f\u60f3\u7559\u4f60\uff0c\u53ea\u662f\u4f60\u5403\u90a3\u8001\u4e5e\u5a46\u523a\u4e86\u4e00\u5251\uff0c\u4f60\u77e5\u9053\u90a3\u8001\u4e5e\u5a46\u662f\u4ec0\u4e48\u4eba\u5417\uff1f\u5979\u662f\u4f60\u7684 \u5e08\u5ac2\uff0c\u5979\u7684\u5251\u7528\u6bd2\u836f\u6d78\u8fc7\uff0c\u5251\u4f24\u867d\u4e0d\u5389\u5bb3\uff0c\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u4f60\u5fc5\u6bd2\u53d1\u65e0\u6551\uff01\u201d\u695a\u662d\u5357\u5168\u662f \u80e1\u8bf4\u516b\u9053\uff0c\u4f46\u8f9b\u9f99\u5b50\u5374\u4fe1\u4ee5\u4e3a\u771f\uff0c\u679c\u7136\u4f3c\u89c9\u80a9\u5934\u6709\u70b9\u9ebb\u75d2\uff0c\u9762\u8272\u5927\u53d8\uff0c\u614c\u5f20\u8bf4\u9053\uff1a\u201c\u8fd9\u600e\u4e48 \u597d\uff1f\u201d\u695a\u662d\u5357\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8981\u8bf7\u4f60\u591a\u7559\u4e00\u4f1a\uff0c\u6211\u6709\u89e3\u836f\uff0c\u4f46\u8981\u7528\u70ed\u9152\u9001\u670d\uff0c\u6211\u5c31\u53eb\u4eba\u7ed9\u4f60 \u53d6\u70ed\u9152\u6765\u3002\u201d\u8bf4\u7f62\u50ac\u4e00\u4e2a\u968f\u8eab\u536b\u58eb\uff0c\u8d76\u53bb\u70eb\u4e00\u58f6\u9152\u3002 \u3000\u3000\u4f60\u9053\u695a\u662d\u5357\u6253\u4ec0\u4e48\u9b3c\u4e3b\u610f\uff01\u539f\u6765\u4ed6\u89c1\u8f9b\u9f99\u5b50\u51fa\u624b\uff0c\u602a\u5f02\u975e\u5e38\uff0c\u8fdc\u975e\u5728\u5929\u5c71\u4e4b\u65f6\u53ef\u6bd4\uff0c\u5c31 \u8fde\u4ed6\u7684\u5e08\u7236\u5353\u4e00\u822a\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u53ca\u4ed6\uff0c\u800c\u4ed6\u7684\u638c\u6cd5\u8eab\u6cd5\uff0c\u66f4\u4e0d\u50cf\u6b66\u5f53\u6d3e\u7684\uff0c\u5fc3\u4e2d\u5927\u7591\uff0c\u6240\u4ee5\u60f3 \u5957\u95ee\u4ed6\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u8f9b\u5927\u54e5\uff0c\u6211\u7684\u89e3\u836f\u867d\u7136\u53ef\u4ee5\u7ed9\u4f60\u89e3\u6bd2\u7684\uff0c\u4f46\u4f60\u8fd9\u8eab\u6b66\u529f\uff0c\u662f\u4e0d\u662f\u8fd8\u80fd \u4fdd\u5168\uff0c\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u5440\uff0c\u90a3\u8001\u4e5e\u5a46\u4e5f\u771f\u6bd2\uff0c\u53d7\u4e86\u5979\u7684\u6bd2\u5251\u6240\u4f24\uff0c\u6050\u6015\u4e5f\u4f1a\u6162\u6162\u8870\u5f31\u3002\u8f9b\u5927 \u54e5\u5440\u8f9b\u5927\u54e5\uff01\u82e5\u662f\u4f60\u6210\u4e86\u5e9f\u4eba\uff0c\u505a\u5144\u5f1f\u7684\u5251\u6cd5\u4e0d\u662f\u4ed6\u4eec\u5bf9\u624b\uff0c\u53ea\u6015\u60f3\u66ff\u4f60\u62a5\u4ec7\u4e5f\u4e0d\u80fd\u591f\uff01\u201d \u3000\u3000\u8f9b\u9f99\u5b50\u4e00\u542c\uff0c\u604d\u5982\u6674\u5929\u9739\u96f7\uff0c\u542b\u6068\u8bf4\u9053\uff1a\u201c\u6211\u82e5\u771f\u7684\u6210\u4e86\u5e9f\u4eba\uff0c\u5c31\u628a\u5251\u6cd5\u4f20\u4f60\uff0c\u6559\u4f60\u6210 \u4e3a\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\uff0c\u6bd4\u4f60\u7684\u5e08\u7236\u8fd8\u5389\u5bb3\uff01\u201d\u695a\u662d\u5357\u5fc3\u4e2d\u5927\u559c\uff0c\u9762\u4e0a\u5374\u4e0d\u9732\u51fa\u75d5\u8ff9\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a \u201c\u505a\u5144\u5f1f\u7684\u4e00\u5b9a\u5c3d\u5fc3\u66ff\u4f60\u533b\u6cbb\uff0c\u539f\u4e0d\u671b\u4f60\u6709\u4ec0\u4e48\u62a5\u7b54\u3002\u53ea\u662f\u6055\u6211\u95ee\u4f60\u4e00\u53e5\uff0c\u5728\u5929\u5c71\u4e4b\u65f6\uff0c\u4f60 \u7684\u5251\u6cd5\u597d\u50cf\u597d\u50cf\u2026\u2026\u5e76\u4e0d\uff0c\u5e76\u4e0d\u600e\u6837\u2026\u2026\u8fd9\u56de\u53c8\u672a\u89c1\u4f60\u4f7f\u5251\uff0c\u96be\u9053\u4f60\u662f\u65b0\u8fd1\u7ec3\u6210\u5251\u6cd5\uff0c\u8fd8\u6ca1 \u673a\u4f1a\u65bd\u5c55\u5417\uff1f\u201d\u8f9b\u9f99\u5b50\u7ffb\u7740\u602a\u773c\u9053\uff1a\u201c\u600e\u4e48\u4f60\u4e0d\u4fe1\u6211\uff1f\u6211\u8fd9\u4e24\u5e74\u5f97\u4e86\u8fbe\u6469\u4e00\u767e\u96f6\u516b\u5f0f\u7684\u771f \u4f20\uff0c\u8fbe\u6469\u5251\u6cd5\u4e5f\u672a\u5fc5\u5728\u4f60\u7684\u5929\u5c71\u5251\u6cd5\u4e4b\u4e0b\uff01\u201d\u695a\u662d\u5357\u662f\u6b66\u6797\u52a0\u9876\u513f\u5c16\u513f\u7684\u597d\u624b\uff0c\u81ea\u7136\u77e5\u9053\u8fbe \u6469\u5251\u6cd5\u5931\u4f20\u7684\u6545\u4e8b\uff0c\u8fd9\u4e00\u559c\u975e\u540c\u5c0f\u53ef\uff0c\u81ea\u601d\u82e5\u5b66\u4e86\u8fbe\u6469\u5251\u6cd5\uff0c\u878d\u4e24\u6d3e\u5251\u6cd5\u4e4b\u957f\uff0c\u90a3\u771f\u662f\u5929\u4e0b \u65e0\u654c\u4e86\u3002 \u3000\u3000\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u536b\u58eb\u5df2\u5c06\u70ed\u9152\u53d6\u5230\uff0c\u695a\u662d\u5357\u5c06\u4e00\u5305\u836f\u7c89\uff0c\u5f39\u5728\u9152\u4e2d\uff0c\u53eb\u8f9b\u9f99\u5b50\u996e\u4e0b\uff0c\u8f9b\u9f99\u5b50 \u4e0d\u7591\u6709\u4ed6\uff0c\u4e00\u53e3\u5c31\u541e\u5b8c\u4e86\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u89c9\u773c\u524d\u91d1\u661f\u4e71\u5192\uff0c\u8179\u75db\u5982\u7ede\uff0c\u695a\u662d\u5357\u5927\u53eb\u4e00\u58f0\u201c\u5012 \u4e5f\uff01\u201d\u4e00\u628a\u5c31\u6293\u8fc7\u6765\u3002\u8f9b\u9f99\u5b50\u5403\u4e00\u60ca\uff0c\u5ffd\u7136\u4e00\u58f0\u5927\u543c\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u53cc\u638c\u547c\u7684\u4e00\u58f0\uff0c\u628a\u695a\u662d\u5357 \u6253\u5012\u5730\u4e0a\uff0c\u695a\u662d\u5357\u5728\u5730\u4e0a\u6253\u4e2a\u76d8\u65cb\uff0c\u6e38\u9f99\u5251\u5377\u5730\u626b\u6765\uff0c\u8f9b\u9f99\u5b50\u53eb\u9053\uff1a\u201c\u695a\u662d\u5357\uff0c\u4f60\u597d\u72e0\uff01\u201d \u4e00\u7eb5\u8eab\uff0c\u51fa\u4e86\u5e10\u5e55\uff0c\u98de\u5954\u800c\u53bb\uff01 \u3000\u3000\u695a\u662d\u5357\u5728\u70ed\u9152\u4e2d\u4e0b\u4e86\u6bd2\u836f\uff0c\u4ee5\u4e3a\u8f9b\u9f99\u5b50\u5fc5\u88ab\u6bd2\u6bd9\uff0c\u6025\u4e8e\u8981\u62a2\u4ed6\u7684\u8fbe\u6469\u79d8\u7b08\uff0c\u90a3\u6599\u8f9b\u9f99\u5b50 \u529f\u529b\u6781\u9ad8\uff0c\u867d\u4e2d\u4e86\u6bd2\uff0c\u5374\u80fd\u5fcd\u4f4f\uff0c\u731b\u7136\u9192\u89c9\uff0c\u95ea\u7535\u822c\u7684\u53cd\u51fb\u8fc7\u53bb\uff0c\u695a\u662d\u5357\u731d\u4e0d\u53ca\u9632\uff0c\u7adf\u7136\u8ba9 \u4ed6\u6253\u5012\u3002\u4f46\u8f9b\u9f99\u5b50\u4e5f\u77e5\u9053\u695a\u662d\u5357\u6b66\u529f\u548c\u81ea\u5df1\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u8fd9\u756a\u4e00\u51fb\u800c\u4e2d\uff0c\u539f\u662f\u9080\u5929\u4e4b\u5e78\uff0c\u54ea\u6562 \u604b\u6218\uff0c\u56e0\u6b64\u6025\u6025\u843d\u8352\u800c\u9003\u3002\u6e05\u5175\u89c1\u4ed6\u662f\u4e3b\u5e05\u597d\u53cb\uff0c\u81ea\u662f\u4e0d\u6562\u963b\u6321\u3002 \u3000\u3000\u51cc\u672a\u98ce\u7b49\u4eba\u884c\u4e86\u534a\u65e5\uff0c\u5ffd\u95fb\u8fdc\u5904\u6709\u53ae\u6740\u4e4b\u58f0\uff0c\u6b63\u5f85\u62cd\u9a6c\u8ffd\u8d76\uff0c\u5ffd\u89c1\u8f9b\u9f99\u5b50\u8863\u88f3\u7834\u88c2\uff0c\u5982 \u98de\u5954\u6765\uff0c\u51cc\u672a\u98ce\u5728\u9a6c\u80cc\u4e0a\u4e00\u8dc3\u800c\u8d77\uff0c\u62e6\u5728\u8f9b\u9f99\u5b50\u9762\u524d\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e0d\u627e\u4f60\uff0c\u4f60\u5012\u6562\u6765\u627e \u6211\uff0c\u6211\u4eec\u518d\u6218\u4e09\u767e\u5408\uff01\u201d\u51cc\u672a\u98ce\u53ea\u9053\u4ed6\u8981\u5e26\u9886\u6e05\u519b\u6765\u6349\u62ff\u81ea\u5df1\u3002\u8f9b\u9f99\u5b50\u5982\u75af\u864e\u4e00\u822c\u8fde\u5288\u6570 \u638c\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u5e08\u5144\u5f1f\u90fd\u4e0d\u662f\u597d\u4eba\uff0c\u6211\u8f9b\u9f99\u5b50\u547d\u4e27\u4f60\u4eec\u624b\u4e2d\uff0c\u5929\u4e0b\u82f1\u96c4\u4e5f\u8981\u7b11\u8bdd\u4f60 \u4eec\uff01\u201d\u51cc\u672a\u98ce\u51dd\u795e\u8fd0\u6c14\uff0c\u62c6\u4e86\u51e0\u62db\uff0c\u8f9b\u9f99\u5b50\u5ffd\u7136\u5495\u54af\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\uff0c\u6bd2\u836f\u53d1\u4f5c\uff0c\u4ed6\u7684\u6c14\u529b \u4e5f\u5df2\u8017\u5c3d\uff0c\u51cc\u672a\u98ce\u7684\u638c\u5e76\u672a\u6253\u4e2d\u4ed6\uff0c\u4ed6\u5df2\u81ea\u5df1\u5012\u4e0b\u4e86\u3002 \u3000\u3000\u51cc\u672a\u98ce\u4e00\u542c\u8bdd\u4e2d\u6709\u8bdd\uff0c\u6025\u5fd9\u5c06\u4ed6\u6276\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u6211\u6709\u4ec0\u4e48\u89c1\u4e0d\u5f97\u4eba\u4e4b\u5904\uff1f\u201d\u8f9b \u9f99\u5b50\u6323\u624e\u8bf4\u9053\uff1a\u201c\u54fc\uff0c\u695a\u662d\u5357\u7528\u6bd2\u836f\u6697\u7b97\u6211\uff0c\u4f60\u53c8\u4e58\u6211\u4e34\u5371\u6765\u8feb\u6211\uff0c\u6211\u504f\u504f\u4e0d\u53eb\u4f60\u4eec\u79f0\u5fc3\u5982 \u610f\uff01\u201d\u53d6\u51fa\u8fbe\u6469\u79d8\u7b08\uff0c\u53cc\u624b\u4fbf\u6495\u3002\u51cc\u672a\u98ce\u4f38\u638c\u4e00\u62cd\uff0c\u5c06\u79d8\u7b08\u62cd\u843d\uff0c\u4e00\u770b\u4ed6\u5df2\u9762\u8272\u6de4\u9ed1\uff0c\u6025\u5fd9 \u53d6\u4e86\u4e00\u7c92\u78a7\u7075\u4e39\uff0c\u585e\u5165\u4ed6\u7684\u53e3\u4e2d\uff0c\u8f9b\u9f99\u5b50\u8fd8\u5f85\u6323\u624e\uff0c\u7ed9\u51cc\u672a\u98ce\u5728\u4e0b\u5df4\u4e00\u634f\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u5f20\u5f00 \u5634\u5df4\uff0c\u628a\u90a3\u9897\u836f\u4e38\u9aa8\u788c\u788c\u5730\u541e\u8fdb\u53bb\u3002\u8fc7\u4e86\u8bb8\u4e45\uff0c\u8f9b\u9f99\u5b50\u653e\u4e86\u51e0\u4e2a\u81ed\u5c41\uff0c\u80f8\u4e2d\u8212\u5766\u8bb8\u591a\uff0c\u9762\u8272 \u6e10\u6e10\u597d\u8f6c\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u7741\u5927\u773c\u775b\uff0c\u6014\u6014\u5730\u770b\u7740\u51cc\u672a\u98ce\u3002\u51cc\u672a\u98ce\u9053\uff1a\u201c\u597d\u4e86\uff0c\u4f60\u6240\u53d7\u7684\u6bd2\u5df2\u7ed9\u89e3\u4e86\u3002\u201d\u8f9b \u9f99\u5b50\u5185\u5fc3\u611f\u6fc0\uff0c\u5374\u4e0d\u9053\u8c22\u3002\u7ffb\u7740\u602a\u773c\u8bf4\u9053\uff1a\u201c\u4f60\u679c\u7136\u548c\u4f60\u7684\u5e08\u5144\u4e0d\u540c\uff0c\u53ea\u662f\u6211\u8fd8\u8981\u4e0e\u4f60\u6bd4 \u5251\u3002\u201d\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u4e0d\u5fd9\uff0c\u5f85\u4f60\u5b8c\u5168\u5eb7\u590d\u4e4b\u540e\uff0c\u6211\u4e00\u5b9a\u5949\u966a\u3002\u4f60\u4e14\u5e26\u6211\u53bb\u627e\u695a\u662d\u5357\u90a3 \u53ae\u3002\u201d\u6842\u4ef2\u660e\u4e0a\u524d\u53eb\u58f0\u201c\u5e08\u53d4\u3002\u201d\u8f9b\u9f99\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u5988\u5988\u7684\u5251\u6cd5\u5f88\u597d\uff0c\u4f60\u8fd9\u4e2a\u5e08\u4f84\u4e5f\u8fd8 \u4e0d\u4e22\u5e08\u53d4\u7684\u8138\u3002\u597d\uff0c\u77a7\u4f60\u51cc\u53d4\u53d4\u7684\u4efd\u4e0a\uff0c\u6211\u8ba4\u4f60\u4e86\u3002\u4f60\u7684\u7239\u5988\u73b0\u5728\u7ed9\u4eba\u56f4\u7740\uff0c\u6211\u4eec\u5148\u53bb\u6551\u4ed6 \u4eec\u51fa\u6765\uff01\u201d \u3000\u3000\u674e\u601d\u6c38\u548c\u5085\u9752\u4e3b\u7b49\u4f1a\u5728\u4e00\u8d77\uff0c\u5b9e\u529b\u5927\u589e\u3002\u9f50\u771f\u541b\u7ed9\u77f3\u5927\u5a18\u6b66\u743c\u7476\u7f20\u4f4f\uff0c\u98ce\u96f7\u53cc\u5251\uff0c\u867d\u7136 \u5389\u5bb3\uff0c\u5374\u4e5f\u5360\u4e0d\u4e86\u4fbf\u5b9c\u3002\u6210\u5929\u633a\u7ed9\u5085\u9752\u4e3b\u7684\u65e0\u6781\u5251\u6cd5\u6740\u9053\uff0c\u53ea\u662f\u6e05\u5175\u548c\u7ef4\u4eba\u91cd\u91cd\u5305\u56f4\uff0c\u53c8\u6709 \u4e09\u4e2a\u4e00\u6d41\u9ad8\u624b\u538b\u9635\uff0c\u7fa4\u96c4\u4e5f\u662f\u51b2\u4e0d\u51fa\u6765\uff0c\u53ea\u80fd\u636e\u5b88\u5c0f\u4e18\uff0c\u8fd1\u7528\u5251\u523a\uff0c\u8fdc\u7528\u7bad\u5c04\u3002 \u3000\u3000\u708e\u65e5\u897f\u901d\uff0c\u51c9\u6708\u4e1c\u5347\uff0c\u6c99\u6f20\u6c14\u5019\u53d8\u5e7b\u6781\u5927\uff0c\u9976\u662f\u5728\u201c\u706b\u6d32\u201d\u5410\u9c81\u756a\u7684\u9644\u8fd1\uff0c\u665a\u4e0a\u4e5f\u662f\u82e6 \u5bd2\u88ad\u4eba\u3002\u6e05\u5175\u5728\u6c99\u6f20\u4e0a\u70e7\u8d77\u91ce\u706b\uff0c\u7167\u8000\u5f97\u660e\u5982\u767d\u663c\u3002\u5218\u90c1\u82b3\u671b\u7740\u9065\u8fdc\u7684\u5929\u5c71\uff0c\u9690\u9690\u770b\u89c1\u96ea\u5c71 \u51b0\u5cf0\uff0c\u9ad8\u51fa\u4e91\u8868\uff0c\u5728\u591c\u7a7a\u4e2d\u95ea\u95ea\u53d1\u5149\u3002 \u3000\u3000\u5218\u90c1\u82b3\u5fae\u611f\u51c9\u610f\uff0c\u6402\u7740\u6613\u5170\u73e0\u9053\uff1a\u201c\u706b\u6d32\u9644\u8fd1\uff0c\u665a\u4e0a\u8fd8\u662f\u8fd9\u6837\u5bd2\u51b7\uff0c\u5929\u5c71\u4e4b\u4e0a\uff0c\u66f4\u4e0d\u77e5 \u662f\u4f55\u7b49\u9177\u5bd2\u5462\uff01\u201d\u6613\u5170\u73e0\u7b11\u9053\uff1a\u201c\u6211\u662f\u81ea\u5c0f\u5728\u5929\u5c71\u957f\u5927\u7684\uff0c\u59d0\u59d0\u662f\u6c5f\u5357\u4eba\u4e00\u5b9a\u8fc7\u4e0d\u60ef\u7684\u3002\u201d \u5218\u90c1\u82b3\u60f3\u8d77\u4e86\u51cc\u672a\u98ce\uff0c\u5fc3\u60f3\u4ed6\u82e5\u771f\u662f\u81ea\u5df1\u5c11\u5e74\u65f6\u5019\u7684\u90a3\u4e2a\u670b\u53cb\uff0c\u5219\u4ed6\u4e3a\u4e86\u81ea\u5df1\uff0c\u8fdc\u8d70\u5f02\u4e61\uff0c \u6328\u53d7\u5929\u5c71\u7684\u9177\u5bd2\uff0c\u6c5f\u6e56\u7684\u9669\u6076\uff0c\u5219\u4ed6\u6c14\u6068\u81ea\u5df1\uff0c\u4e5f\u771f\u602a\u4e0d\u5f97\u4ed6\uff0c\u5fc3\u91cc\u4e00\u9178\uff0c\u559f\u7136\u53f9\u9053\uff1a\u201c\u82e5 \u6709\u4e00\u65e5\u6211\u4e5f\u80fd\u4e0a\u5929\u5c71\u770b\u770b\u5c31\u597d\u4e86\u3002\u201d\u6b66\u743c\u7476\u508d\u7740\u674e\u601d\u6c38\uff0c\u6309\u5251\u76d1\u89c6\u6e05\u5175\uff0c\u5ffd\u89c1\u5218\u90c1\u82b3\u82e5\u6709\u6240 \u601d\uff0c\u8be7\u7136\u95ee\u9053\uff1a\u201c\u5218\u5927\u59d0\uff0c\u4f60\u60f3\u4e9b\u4ec0\u5440\uff1f\u201d\u5218\u90c1\u82b3\u9ed8\u7136\u4e0d\u7b54\uff0c\u674e\u601d\u6c38\u5ffd\u7136\u5927\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5feb \u770b\u53c8\u662f\u4ec0\u4e48\u4eba\u6765\uff01\u201d\u53ea\u89c1\u6e05\u5175\u9635\u811a\u5927\u4e71\uff0c\u9f50\u771f\u541b\u5e26\u9886\u7ef4\u4eba\u4e0a\u53bb\u963b\u622a\u3002 \u3000\u3000\u706b\u5149\u4e2d\u5218\u90c1\u82b3\u770b\u5f97\u5206\u660e\uff0c\u4e3a\u9996\u7684\u4eba\u7adf\u4f3c\u51cc\u672a\u98ce\u6a21\u6837\uff0c\u5085\u9752\u4e3b\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u5947\u4e86\uff0c\u600e\u7684\u8fd9 \u6837\u51d1\u5de7\uff0c\u51cc\u672a\u98ce\u771f\u7684\u6765\u4e86\u3002\u201d\u51dd\u795e\u770b\u65f6\uff0c\u53ea\u89c1\u51cc\u672a\u98ce\u53ea\u5e26\u7740\u51e0\u4e2a\u4eba\uff0c\u5df2\u548c\u9f50\u771f\u541b\u4ea4\u4e0a\u4e86\u624b\uff0c \u674e\u601d\u6c38\u9053\uff1a\u201c\u6e05\u5175\u4eba\u591a\uff0c\u51cc\u672a\u98ce\u867d\u7136\u6b66\u827a\u9ad8\u5f3a\uff0c\u53ea\u6015\u4e5f\u51b2\u4e0d\u8fdb\u6765\u3002\u4e0d\u5982\u54b1\u4eec\u51b2\u4e0b\u53bb\u548c\u4ed6\u4f1a\u5408 \u5427\uff01\u201d\u7fa4\u96c4\u6b63\u60f3\u884c\u52a8\uff0c\u5ffd\u7136\u9f50\u771f\u541b\u62d4\u6b65\u98de\u9003\uff0c\u4ed6\u6240\u5e26\u7684\u7ef4\u4eba\u5927\u58f0\u547c\u53eb\uff0c\u62e5\u7740\u51cc\u672a\u98ce\uff0c\u7adf\u7136\u5012 \u6208\u53cd\u6740\u8fc7\u6765\uff0c\u6e05\u519b\u767b\u65f6\u5927\u4e71\uff01 \u3000\u3000\u539f\u6765\u51cc\u672a\u98ce\u548c\u98de\u7ea2\u5dfe\u8d76\u5230\u6218\u573a\uff0c\u9f50\u771f\u541b\u4e00\u5251\u98de\u524d\uff0c\u624b\u4e0b\u51e0\u767e\u7ef4\u4eba\u5377\u5c06\u8fc7\u6765\uff0c\u51cc\u672a\u98ce\u957f\u5251 \u4e00\u6325\uff0c\u5c06\u9f50\u771f\u541b\u53cc\u5251\u683c\u5f00\uff0c\u98de\u7ea2\u5dfe\u5ffd\u7136\u4e00\u62cd\u51cc\u672a\u98ce\u80a9\u5934\uff0c\u53eb\u9053\uff1a\u201c\u9000\u4e0b\uff01\u201d\u957f\u97ad\u4e00\u6307\uff0c\u5927\u58f0 \u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u8ba4\u5f97\u6211\u5417\uff1f\u6211\u662f\u98de\u7ea2\u5dfe\uff01\u201d\u9f50\u771f\u541b\u75be\u523a\u4e24\u5251\uff0c\u98de\u7ea2\u5dfe\u8eab\u5f62\u95ea\u52a8\uff0c\u5e76\u4e0d\u8fd8\u62db\uff0c \u7ee7\u7eed\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u542c\u6211\u547d\u4ee4\uff0c\u628a\u8fd9\u8001\u8d3c\u6740\u6389\uff01\u201d\u5e74\u8001\u7684\u7ef4\u65cf\u6218\u58eb\u4eec\u72c2\u559c\u53eb\u9053\uff1a\u201c\u662f\u98de\u7ea2 \u5dfe\uff01\u201d\u5e74\u9752\u7684\u6218\u58eb\u4eec\u867d\u7136\u4e0d\u8ba4\u5f97\uff0c\u5374\u90fd\u542c\u8fc7\u98de\u7ea2\u5dfe\u7684\u5927\u540d\uff0c\u970e\u65f6\u95f4\u6b22\u58f0\u52a8\u5730\uff0c\u5200\u67aa\u5251\u621f\u9f50\u5411 \u9f50\u771f\u541b\u8eab\u4e0a\u6233\u6765\uff0c\u9f50\u771f\u541b\u4e00\u5251\u5288\u7ffb\u4e24\u4eba\uff0c\u98de\u7ea2\u5dfe\u7684\u957f\u97ad\u5df2\u556a\u7684\u4e00\u58f0\uff0c\u6253\u5230\u4ed6\u7684\u80cc\u540e\uff0c\u9f50\u771f\u541b \u62d4\u6b65\u98de\u9003\uff0c\u51cc\u672a\u98ce\u6325\u5251\u6025\u4e0a\u3002 \u3000\u3000\u7ef4\u4eba\u7684\u9996\u9886\u662f\u5b5f\u7984\u7684\u513f\u5b50\u5b5f\u5c71\uff0c\u5b5f\u7984\u5f52\u987a\u6e05\u5ef7\uff0c\u9009\u4e86\u4e00\u5343\u9a91\u5175\uff0c\u7531\u4ed6\u7387\u9886\uff0c\u8ddf\u968f\u6e05\u5ef7\u7684 \u7279\u4f7f\u9f50\u771f\u541b\u56de\u53bb\u8fce\u63a5\u6e05\u5175\uff0c\u8d70\u5230\u4e2d\u9014\uff0c\u548c\u695a\u662d\u5357\u5e26\u6765\u7684\u7981\u536b\u519b\u4f1a\u5408\u7684\u3002\u6b64\u65f6\u5c0f\u4e18\u4e0a\u7fa4\u96c4\u7eb7\u7eb7 \u51b2\u4e0b\uff0c\u5b5f\u5c71\u9886\u5175\u53bb\u5835\u622a\u98de\u7ea2\u5dfe\uff0c\u5927\u58f0\u5f39\u538b\u3002\u4e0d\u6599\u7ef4\u4eba\u89c1\u662f\u98de\u7ea2\u5dfe\uff0c\u5927\u534a\u4e0d\u542c\u4ed6\u7684\u8bf4\u8bdd\uff0c\u4ed6\u53ea \u5f97\u5e26\u7740\u5fc3\u8179\u9003\u547d\uff0c\u6218\u573a\u5f62\u52bf\uff0c\u987f\u65f6\u6539\u89c2\uff0c\u7ef4\u65cf\u9a91\u5175\u548c\u6e05\u519b\u52b2\u5352\u4e92\u76f8\u640f\u6740\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u62a2\u5165\u4e71\u519b\u4e4b\u4e2d\uff0c\u6b63\u78b0\u7740\u695a\u662d\u5357\u843d\u8352\u800c\u8d70\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u54ea\u91cc\u8d70\uff01\u201d\u695a\u662d\u5357\u7a81\u89c9\u52b2 \u98ce\u659c\u5439\uff0c\u8f9b\u9f99\u5b50\u53cc\u638c\u547c\u7684\u6253\u5230\u3002\u695a\u662d\u5357\u4fa7\u8eab\u4e00\u95ea\uff0c\u5587\u7684\u4e00\u5251\u523a\u51fa\uff0c\u8f9b\u9f99\u5b50\u4e00\u62f3\u6251\u7a7a\uff0c\u518d\u5ea6\u8fdb \u62db\uff0c\u695a\u662d\u5357\u8eab\u968f\u52bf\u8f6c\uff0c\u5251\u64a9\u638c\u5288\uff0c\u72e0\u8fa3\u5f02\u5e38\uff0c\u8f9b\u9f99\u5b50\u7a7a\u624b\u62a2\u8fdb\uff0c\u7a76\u6709\u987e\u5fcc\uff0c\u4e24\u4eba\u95ea\u7535\u822c\u5730\u62c6 \u4e86\u51e0\u62db\uff0c\u6210\u5929\u633a\u548c\u4f17\u536b\u58eb\u5df2\u8d76\u5230\uff0c\u51cc\u672a\u98ce\u6025\u5fd9\u4ed7\u5251\u8d76\u6765\uff0c\u8f9b\u9f99\u5b50\u5728\u56f4\u653b\u4e4b\u4e0b\uff0c\u80a9\u5934\u53c8\u7ed9\u695a\u662d \u5357\u523a\u4e86\u4e00\u5251\uff0c\u51cc\u672a\u98ce\u5c55\u5f00\u5929\u5c71\u5251\u6cd5\uff0c\u94f6\u5149\u70b9\u70b9\uff0c\u98de\u6d12\u800c\u6765\uff0c\u695a\u662d\u5357\u521a\u6321\u5f97\u4e00\u5251\uff0c\u80cc\u5fc3\u5374\u4e2d\u4e86 \u8f9b\u9f99\u5b50\u4e00\u638c\uff0c\u6025\u5fd9\u62d4\u8db3\u98de\u9003\uff0c\u51cc\u672a\u98ce\u957f\u5251\u7ffb\u98de\uff0c\u62a4\u4f4f\u4e86\u8f9b\u9f99\u5b50\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u7684\u4f24\u52bf\u600e\u6837\uff1f\u201d \u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u4e0d\u8981\u7ba1\u6211\uff0c\u4f60\u53bb\u8ffd\u90a3\u53ae\u5427\uff01\u201d\u51cc\u672a\u98ce\u89c1\u4ed6\u80a9\u5934\u8840\u67d3\uff0c\u77e5\u662f\u4f24\u5f97\u4e0d\u8f7b\uff0c\u8bf4\u9053\uff1a \u201c\u6709\u98de\u7ea2\u5dfe\u4ed6\u4eec\u8ffd\u51fb\uff0c\u4e00\u5b9a\u4f1a\u6253\u8d62\u7684\u3002\u201d\u5f3a\u62d6\u7740\u4ed6\u9000\u4e0b\u3002\u8fd9\u65f6\u5ffd\u7136\u542c\u5f97\u6613\u5170\u73e0\u547c\u53eb\u4e4b\u58f0\uff0c\u6842 \u4ef2\u660e\u6b63\u8dd1\u8fc7\u6765\uff0c\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4f60\u7167\u987e\u5e08\u53d4\u3002\u201d\u63d0\u5251\u52c7\u95ef\uff0c\u8f9b\u9f99\u5b50\u4e5f\u60f3\u8ddf\u53bb\uff0c\u53ea\u662f\u5468\u8eab\u9aa8\u75db\uff0c \u6842\u4ef2\u660e\u6301\u5251\u7ed9\u4ed6\u5f00\u8def\uff0c\u5374\u4e0d\u8bb8\u4ed6\u53ae\u6740\u3002 \u3000\u3000\u539f\u6765\u5f20\u534e\u662d\u77a7\u89c1\u6613\u5170\u73e0\u5728\u4e71\u519b\u4e4b\u4e2d\u51b2\u6740\uff0c\u5fc3\u5934\u72c2\u559c\uff0c\u62fc\u547d\u51b2\u53bb\u3002\u695a\u662d\u5357\u548c\u6210\u5929\u633a\u7b49\u98de \u9003\uff0c\u8fce\u9762\u6b63\u78b0\u7740\u5085\u9752\u4e3b\u6613\u5170\u73e0\u548c\u6b66\u743c\u7476\uff0c\u4e09\u53e3\u5bd2\u5149\u95ea\u95ea\u7684\u5229\u5251\uff0c\u622a\u7740\u53bb\u8def\uff0c\u695a\u662d\u5357\u77e5\u9053\u5389 \u5bb3\uff0c\u659c\u523a\u4e00\u51b2\uff0c\u4fa7\u9762\u53c8\u662f\u77f3\u5929\u6210\u592b\u5987\u62e6\u4f4f\uff0c\u695a\u662d\u5357\u6697\u53eb\u4e00\u58f0\u82e6\u4e5f\uff0c\u5ffd\u89c1\u5f20\u534e\u662d\u8dd1\u6765\uff0c\u5fc3\u4e2d\u5927 \u559c\uff0c\u626d\u8f6c\u4e86\u5934\uff0c\u4e00\u62db\u201c\u6781\u76ee\u6ca7\u6ce2\u201d\u53cd\u624b\u4e00\u5251\uff0c\u95ea\u7535\u822c\u5730\u523a\u5230\u5f20\u534e\u662d\u80c1\u4e0b\uff0c\u5f20\u534e\u662d\u5168\u795e\u8d2f\u6ce8\u6613 \u5170\u73e0\uff0c\u731d\u4e0d\u53ca\u9632\uff0c\u8eab\u5f62\u4e00\u7f29\uff0c\u624b\u8155\u5df2\u7ed9\u4ed6\u5de6\u624b\u4e09\u6307\u6263\u7740\u8109\u95e8\uff0c\u4e00\u628a\u7529\u5c06\u8d77\u6765\uff0c\u77f3\u5927\u5a18\u5530\u7684\u4e00 \u5251\u523a\u5230\uff0c\u695a\u662d\u5357\u72de\u7b11\u9053\uff1a\u201c\u53eb\u4f60\u4eec\u523a\uff01\u201d\u628a\u5f20\u534e\u662d\u5de6\u53f3\u4e00\u8361\uff0c\u6613\u5170\u73e0\u5927\u53eb\u8d77\u6765\uff0c\u77f3\u5927\u5a18\u6025\u5fd9 \u6536\u5251\uff0c\u695a\u662d\u5357\u7b49\u9886\u4f17\u4eba\u5df2\u51b2\u8fc7\u53bb\u4e86\uff01 \u3000\u3000\u51cc\u672a\u98ce\u7eb5\u8dc3\u5982\u98de\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u628a\u4eba\u653e\u4e0b\uff01\u201d\u5218\u90c1\u82b3\u4ece\u4fa7\u9762\u6740\u51fa\uff0c\u5947\u95e8\u6697\u5668\u9526\u4e91\u515c\u7a81\u7136 \u5f53\u5934\u4e00\u7f69\uff0c\u695a\u662d\u5357\u970d\u5730\u907f\u5f00\uff0c\u5ffd\u89c9\u624b\u8155\u4e00\u9635\u9ebb\u75db\uff0c\u51cc\u672a\u98ce\u624b\u81c2\u4e00\u4f38\uff0c\u53cc\u6307\u76f4\u70b9\u4ed6\u7684\u9762\u95e8\uff0c\u624b \u638c\u4e00\u677e\uff0c\u5f20\u534e\u662d\u500f\u5730\u5012\u843d\u5730\u4e0a\u3002\u51cc\u672a\u98ce\u6025\u5fd9\u6276\u8d77\uff0c\u5218\u90c1\u82b3\u4e0e\u6613\u5170\u73e0\u53cc\u53cc\u8fc7\u6765\u3002\u6b66\u743c\u7476\u629a\u5251\u5927 \u7b11\uff0c\u695a\u662d\u5357\u5374\u5df2\u9003\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u6613\u5170\u73e0\u6115\u7136\u95ee\u9053\uff1a\u201c\u6b66\u59d0\u59d0\uff0c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u6b66\u743c\u7476\u9053\uff1a\u201c\u4ed6\u4e2d\u4e86\u6211\u7684\u767d\u7709\u9488\uff0c\u6709\u4ed6\u4e00\u751f \u597d\u53d7\u7684\u4e86\u3002\u201d\u767d\u7709\u9488\u662f\u767d\u53d1\u9b54\u5973\u7684\u72ec\u95e8\u6697\u5668\uff0c\u7ec6\u5982\u725b\u6bdb\uff0c\u6240\u4ee5\u79f0\u4e3a\u767d\u7709\u9488\u3002\u8fd9\u79cd\u6697\u5668\u867d\u4e0d\u8db3 \u5236\u654c\u4eba\u6b7b\u547d\uff0c\u5374\u662f\u72e0\u8fa3\u975e\u5e38\uff0c\u5165\u4e86\u4eba\u4f53\uff0c\u6781\u4e0d\u5bb9\u6613\u53d6\u51fa\uff0c\u771f\u662f\u6709\u5982\u9644\u9aa8\u4e4b\u75bd\u3002\u695a\u662d\u5357\u6240\u4e2d\u7684 \u4e24\u679a\u767d\u7709\u9488\uff0c\u90fd\u9690\u5165\u9aa8\u5934\u5173\u8282\u4e4b\u4e2d\uff0c\u4ee5\u81f3\u529f\u529b\u6e10\u51cf\uff0c\u8fd9\u662f\u540e\u8bdd\u3002 \u3000\u3000\u695a\u662d\u5357\u4e0e\u6210\u5929\u633a\u7b49\u4e00\u9003\uff0c\u6e05\u5175\u5168\u90e8\u6e83\u9000\uff0c\u98de\u7ea2\u5dfe\u52d2\u9a6c\u4e0d\u8ffd\uff0c\u56de\u5934\u4e00\u671b\uff0c\u89c1\u5f20\u534e\u662d\u6267\u7740\u6613 \u5170\u73e0\u7684\u624b\uff0c\u4e92\u76f8\u51dd\u89c6\uff0c\u6218\u573a\u4e0a\u7684\u4e00\u5207\u7eb7\u6270\uff0c\u4ed6\u4eec\u90fd\u597d\u50cf\u662f\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\u3002\u98de\u7ea2\u5dfe\u7b11\u76c8 \u76c8\u5730\u8d70\u4e86\u8fc7\u6765\uff0c\u6613\u5170\u73e0\u5ffd\u89c1\u98de\u7ea2\u5dfe\u51fa\u73b0\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u59c6\u5988\uff0c\u4e0d\u662f\u6211\u60f3\u79bb\u5f00 \u4f60\u2026\u2026\u201d\u98de\u7ea2\u5dfe\u63a5\u58f0\u7b11\u9053\uff1a\u201c\u5170\u73e0\uff0c\u6211\u4e5f\u4e0d\u60f3\u79bb\u5f00\u4f60\uff0c\u6240\u4ee5\u6211\u4e5f\u51fa\u6765\u4e86\uff0c\u8ba9\u6211\u4eec\u5927\u5bb6\u90fd\u5728\u4e00 \u8d77\uff0c\u50cf\u4e00\u5bb6\u4eba\u90a3\u6837\u5feb\u5feb\u6d3b\u6d3b\u8fc7\u65e5\u5b50\u3002\u201d\u6613\u5170\u73e0\u773c\u6cea\u593a\u7736\u800c\u51fa\uff0c\u62b1\u7740\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u59c6\u5988\uff0c\u6211\u771f \u7684\u611f\u6fc0\u4f60\uff0c\u4f60\u5f85\u6211\u6bd4\u4eb2\u751f\u7684\u5973\u513f\u8fd8\u8981\u4eb2\u3002\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u4f60\u4e0d\u53ea\u662f\u6211\u4e00\u4e2a\u4eba\u7684\u5973\u513f\uff0c\u4e5f\u662f\u51cc \u53d4\u53d4\u7684\u597d\u4f84\u5973\u548c\u4ed6\u4eec\u7684\u597d\u670b\u53cb\u3002\u201d\u8bf4\u7740\u7279\u522b\u6307\u4e86\u5f20\u534e\u662d\u4e00\u4e0b\uff0c\u6613\u5170\u73e0\u7f9e\u5f97\u5782\u4e0b\u5934\u6765\u3002\u5f20\u534e\u662d \u5ffd\u7136\u60ca\u547c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6709\u8fd9\u4e48\u591a\u7684\u767d\u5934\u53d1\u4e86\uff01\u201d\u4e00\u9635\u98ce\u8fc7\uff0c\u6613\u5170\u73e0\u7684\u5934\u53d1\u7ed9\u98ce\u5439\u5f00\uff0c\u767d\u53d1\u6df7 \u5728\u9ed1\u53d1\u4e4b\u4e2d\uff0c\u6709\u5982\u7e41\u971c\u5806\u9b13\uff0c\u98de\u7ea2\u5dfe\u54bd\u7136\u53f9\u9053\uff1a\u201c\u6211\u4eec\u5e08\u5f92\u4e09\u4ee3\uff0c\u7adf\u7136\u90fd\u662f\u672a\u8001\u767d\u5934\uff01\u201d\u5f20 \u534e\u662d\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6267\u7740\u6613\u5170\u73e0\u7684\u624b\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u6211\u7ed9\u4f60\u533b\uff01\u201d\u4ece\u6000\u4e2d\u53d6\u51fa\u9526\u5323\uff0c\u7f15\u7f15\u6e05\u9999\uff0c \u6c81\u4eba\u5fc3\u80ba\u3002 \u3000\u3000\u6613\u5170\u73e0\u6027\u6700\u7231\u82b1\uff0c\u4e00\u89c1\u4e24\u6735\u4f18\u6619\u4ed9\u82b1\uff0c\u4e00\u7ea2\u4e00\u767d\uff0c\u4e0d\u89c9\u5fc3\u9189\u3002\u5f20\u534e\u662d\u53c8\u89e3\u4e0b\u76db\u6c34\u7684\u846b \u82a6\uff0c\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u5170\u73e0\u59d0\u59d0\uff0c\u6211\u8981\u4f60\u628a\u8fd9\u4e24\u6735\u82b1\u5403\u4e86\u3002\u201d\u6613\u5170\u73e0\u7b11\u5f97\u5982\u82b1\u679d\u4e71\u98a4\uff0c\u7ea4\u6307\u6233\u5411 \u5f20\u534e\u662d\u9762\u988a\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u771f\u5b69\u5b50\u6c14\uff01\u8fd9\u6837\u597d\u82b1\uff0c\u5403\u4e86\u4e0d\u7cdf\u8e4b\u5417\uff1f\u201d\u5f20\u534e\u662d\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d \u5b69\u5b50\u6c14\uff0c\u6211\u6c42\u4f60\u628a\u5b83\u5403\u4e0b\u3002\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u4f60\u5c31\u628a\u5b83\u5403\u4e0b\u5427\uff0c\u5728\u5929\u5c71\u65f6\uff0c\u4f60\u4e0d\u662f\u4e5f\u559c\u6b22\u5f04\u4e9b \u96ea\u83b2\u6765\u6ce1\u8336\u5417\uff1f\u201d\u6613\u5170\u73e0\u89c1\u4ed6\u4eec\u90fd\u8bf4\u5f97\u90a3\u4e48\u201c\u6b63\u7ecf\u201d\uff0c\u9887\u4e3a\u5947\u602a\uff0c\u5979\u672c\u6765\u7231\u6781\u8fd9\u4e24\u6735\u82b1\uff0c\u4e5f \u559c\u6b22\u5403\u9c9c\u82b1\u82b1\u854a\uff0c\u629a\u5f04\u4e00\u56de\uff0c\u628a\u4e24\u6735\u82b1\u90fd\u56bc\u788e\u4e0b\u54bd\uff0c\u53ea\u89c9\u9f7f\u988a\u7559\u82b3\uff0c\u5979\u8210\u8210\u820c\u5934\u9053\uff1a\u201c\u771f\u597d \u5403\uff01\u8fd8\u6709\u5417\uff1f\u201d\u5f20\u534e\u662d\u7b11\u9053\uff1a\u201c\u4f60\u5403\u4e0a\u4e86\u763e\u6765\u4e86\u3002\u6211\u53ef\u6ca1\u6709\u82b1\u518d\u7ed9\u4f60\u5403\u4e86\u3002\u201d\u98de\u7ea2\u5dfe\u7b11\u9053\uff1a \u201c\u60f3\u518d\u8981\u8fd9\u4e24\u6735\u82b1\uff0c\u53ef\u8981\u7b49\u516d\u5341\u5e74\u540e\u4e86\u3002\u201d\u6613\u5170\u73e0\u6115\u7136\u4e0d\u89e3\uff0c\u98de\u7ea2\u5dfe\u4e5f\u4e0d\u5411\u5979\u8bf4\u660e\u3002 \u3000\u3000\u674e\u601d\u6c38\u770b\u7740\u5f20\u534e\u662d\u5582\u82b1\u7ed9\u6613\u5170\u73e0\u5403\uff0c\u4f4e\u58f0\u541f\u9053\uff1a\u201c\u5341\u516b\u5e74\u6765\u5815\u4e16\u95f4\uff0c\u5439\u82b1\u56bc\u854a\u5f04\u51b0\u5f26\uff0c \u591a\u60c5\u60c5\u5bc4\u963f\u8c01\u8fb9\uff1f\u201d\u8fd9\u662f\u7eb3\u5170\u5bb9\u82e5\u7684\u540d\u53e5\uff0c\u7eb3\u5170\u8bcd\u90a3\u65f6\u6d41\u884c\u5168\u56fd\uff0c\u51e0\u4e4e\u5987\u5b7a\u80fd\u8bf5\u3002\u6b66\u743c\u7476\u8d66 \u7136\u4e00\u7b11\uff0c\u77a7\u4e86\u4ed6\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u600e\u4e48\u6837\uff1f\u662f\u7fa1\u6155\u522b\u4eba\u5440\uff1f\u8fd8\u662f\u5992\u5fcc\u522b\u4eba\u5440\uff1f\u201d\u674e \u601d\u6c38\u9762\u4e0a\u5f98\u7ea2\uff0c\u89c1\u6b66\u743c\u7476\u773c\u4e2d\u4f3c\u542b\u6709\u65e0\u9650\u60c5\u610f\u3002\u4ed6\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6709\u4f60\u5728\u65c1\uff0c\u6211\u7528\u4e0d\u7740\u7fa1\u6155\uff0c \u66f4\u7528\u4e0d\u7740\u5992\u5fcc\u5440\uff01\u201d\u8fd9\u970e\u90a3\u95f4\uff0c\u6b66\u743c\u7476\u9762\u4e5f\u7ea2\u4e86\uff01 \u3000\u3000\u8fd9\u4e2a\u65f6\u5149\uff0c\u5218\u90c1\u82b3\u4e5f\u6b63\u548c\u51cc\u672a\u98ce\u4e92\u53d9\u5951\u9614\u3002\u51cc\u672a\u98ce\u89c1\u5218\u90c1\u82b3\u6e05\u7626\u8bb8\u591a\uff0c\u9eef\u7136\u65e0\u8bed\u3002\u5218\u90c1 \u82b3\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u4e0d\u80fd\u518d\u89c1\u7740\u4f60\u4e86\uff01\u201d\u51cc\u672a\u98ce\u5f3a\u7b11\u9053\uff1a\u201c\u6211\u7b54\u5e94\u8fc7\u4f60\u548c\u4f60\u540c\u4e0a\u5929\u5c71\uff0c\u6b64\u613f\u672a \u507f\uff0c\u6211\u4eec\u5982\u4f55\u4f1a\u4e0d\u518d\u76f8\u89c1\uff1f\u201d \u3000\u3000\u7fa4\u96c4\u4f1a\u96c6\u4e4b\u540e\uff0c\u6b66\u743c\u7476\u5e26\u8def\u524d\u884c\uff0c\u5085\u9752\u4e3b\u95ee\u9053\uff1a\u201c\u4f60\u7684\u7238\u7238\u53ef\u597d\uff1f\u201d\u6b66\u743c\u7476\u9053\uff1a\u201c\u5c31\u662f \u4ed6\u53eb\u6211\u6765\u63a5\u4f2f\u4f2f\u7684\u5440\uff01\u201d\u5085\u9752\u4e3b\u548c\u6b66\u5143\u82f1\u662f\u751f\u6b7b\u4e4b\u4ea4\uff0c\u548c\u6545\u4eba\u76f8\u89c1\u5728\u5373\uff0c\u5341\u5206\u559c\u60a6\u3002\u6b63\u8bf4\u8bdd \u95f4\uff0c\u5ffd\u89c1\u524d\u9762\u5c18\u5934\u5927\u8d77\uff0c\u53c8\u6709\u767e\u4f59\u5065\u9a6c\u51b2\u6765\uff0c\u5085\u9752\u4e3b\u8e69\u7709\u9053\uff1a\u201c\u96be\u9053\u695a\u662d\u5357\u90a3\u53ae\u8fd8\u6562\u56de \u6765\uff1f\u201d\u7eb5\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u9886\u7740\u8fd9\u961f\u4eba\u9a6c\u7684\u7adf\u662f\u4e00\u4e2a\u5b69\u5b50\uff0c\u5085\u9752\u4e3b\u751a\u4e3a\u5947\u602a\uff0c\u6b66\u743c\u7476\u5df2\u5927\u58f0\u53eb \u9053\uff1a\u201c\u5f1f\u5f1f\uff0c\u5f1f\u5f1f\uff01\u201d\u90a3\u4e2a\u5b69\u5b50\u4e00\u4e2a\u7b4b\u6597\u4ece\u9a6c\u80cc\u7ffb\u4e0b\uff0c\u626f\u7740\u5085\u9752\u4e3b\u7684\u8896\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u5085\u4f2f \u4f2f\uff0c\u4f60\u4e0d\u8ba4\u5f97\u6211\u4e86\u5417\uff1f\u201d\u5085\u9752\u4e3b\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6210\u5316\uff0c\u4f60\u957f\u5f97\u8fd9\u4e48\u5927\u4e86\uff0c\u4f60\u5e26\u8fd9\u4e48\u591a\u4eba\u6765\u505a \u4ec0\u4e48\uff1f\u201d\u6b66\u6210\u5316\u662f\u6b66\u5143\u82f1\u7684\u513f\u5b50\uff0c\u66fe\u8ddf\u5085\u9752\u4e3b\u5b66\u8fc7\u6c34\u8896\u63a5\u6697\u5668\u7684\u529f\u592b\uff0c\u90a3\u65f6\u4ed6\u53ea\u6709\u5341\u4e00\u4e8c \u5c81\uff0c\u73b0\u5728\u5df2\u7ecf\u662f\u5341\u56db\u4e94\u5c81\u7684\u5927\u5b69\u5b50\u4e86\u3002 \u3000\u3000\u6b66\u6210\u5316\u53cc\u773c\u7ea2\u80bf\uff0c\u8fde\u8fde\u626f\u7740\u5085\u9752\u4e3b\u9053\uff1a\u201c\u5085\u4f2f\u4f2f\uff0c\u4f60\u5feb\u53bb\u770b\u6211\u7684\u7238\u7238\uff0c\u4ed6\u6628\u665a\u53d7\u4eba\u6697\u7b97 \u4e86\uff01\u201d\u5085\u9752\u4e3b\u8df3\u8d77\u6765\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\uff1f\u201d\u6b66\u5143\u82f1\u662f\u7ec8\u5357\u6d3e\u7684\u540d\u5bbf\uff0c\u6b66\u529f\u751a\u5f3a\uff0c\u60f3\u4e0d\u5230\u5728\u897f \u5317\u8fb9\u8352\u4e4b\u5730\uff0c\u7adf\u6709\u4eba\u80fd\u6697\u7b97\u4ed6\u3002\u6b66\u743c\u7476\u975e\u5e38\u7740\u6025\uff0c\u8fde\u5fd9\u50ac\u5f1f\u5f1f\u5feb\u8bf4\u3002\u6b66\u6210\u5316\u9053\uff1a\u201c\u6628\u665a\u4e09\u66f4 \u65f6\u5206\u6211\u6b63\u719f\u7761\uff0c\u5ffd\u7136\u542c\u5f97\u7238\u7238\u5927\u58f0\u547c\u559d\uff0c\u6211\u8df3\u8d77\u6765\uff0c\u53ea\u89c1\u4e24\u4e2a\u8d3c\u4eba\u4ece\u4f60\u7684\u623f\u95f4\u91cc\u94bb\u51fa \u6765\u2026\u2026\u201d\u6b66\u743c\u7476\u9053\uff1a\u201c\u5728\u6211\u7684\u623f\u95f4\u91cc\uff1f\u201d\u6b66\u6210\u5316\u9053\uff1a\u201c\u662f\u5440\uff0c\u4ece\u4f60\u7684\u623f\u95f4\u91cc\u51fa\u6765\uff0c\u7238\u7238\u5927 \u6012\uff0c\u5c55\u5f00\u91d1\u80cc\u65ab\u5c71\u5200\uff0c\u5c31\u548c\u4ed6\u4eec\u52a8\u4e0a\u624b\u5566\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u4eba\u8bf4\u8bdd\u9634\u58f0\u602a\u6c14\u7684\uff0c\u5f62\u8c8c\u4f53\u6001\u90fd\u50cf\u5973 \u4eba\uff0c\u4f60\u8bf4\u602a\u4e0d\u602a\uff1f\u53e6\u4e00\u4e2a\u5374\u662f\u8001\u5934\u5b50\uff0c\u6211\u4e00\u628a\u68cb\u5b50\u6492\u53bb\uff0c\u6ca1\u6709\u6253\u7740\uff0c\u5ffd\u7136\u7238\u7238\u5927\u53eb\u4e00\u58f0\uff0c\u8df3 \u51fa\u5708\u5b50\uff0c\u8fd9\u65f6\u6768\u53d4\u53d4\u4e5f\u6765\u4e86\uff0c\u90a3\u4e24\u4e2a\u8d3c\u4eba\u4e5f\u8dd1\u4e86\uff0c\u7238\u7238\u626f\u5f00\u8863\u670d\uff0c\u80f8\u819b\u9ed1\u4e86\u4e00\u5927\u5757\uff0c\u4eca\u5929\u8fd8 \u4e0d\u80fd\u8d77\u5e8a\uff0c\u4ed6\u542c\u5f97\u5929\u5730\u4f1a\u5144\u5f1f\u7684\u62a5\u544a\uff0c\u77e5\u9053\u767e\u591a\u91cc\u5916\u7684\u6c99\u6f20\u6709\u5927\u961f\u4eba\u9a6c\u53ae\u6740\uff0c\u6240\u4ee5\u6d3e\u6211\u5e26\u4eba \u6765\u770b\uff0c\u770b\u5085\u4f2f\u4f2f\u4eec\u662f\u5426\u88ab\u56f4\u4f4f\u4e86\u3002\u201d\u6b66\u5143\u82f1\u5728\u4e09\u5e74\u4e4b\u524d\uff0c\u548c\u5929\u5730\u4f1a\u7684\u4e24\u4e2a\u9996\u9886\u534e\u7d2b\u5c71\uff0c\u6768\u4e00 \u7ef4\u8f97\u8f6c\u5165\u7586\uff0c\u5728\u8349\u539f\u4e0a\u5efa\u7acb\u6751\u843d\uff0c\u6b66\u6210\u5316\u53e3\u4e2d\u8bf4\u7684\u4e24\u4f4d\u53d4\u53d4\u5c31\u662f\u4ed6\u4eec\uff0c\u6b66\u6210\u5316\u8bf4\u7f62\uff0c\u8fd9\u4e24\u4e2a \u4eba\u4fbf\u5373\u4e0a\u6765\u8c12\u89c1\u4ed6\u4eec\u7684\u603b\u8235\u4e3b\u5218\u90c1\u82b3\uff0c\u518d\u62dc\u89c1\u5085\u9752\u4e3b\u3002\u6842\u4ef2\u660e\u62c9\u7740\u5192\u6d63\u83b2\u9053\uff1a\u201c\u5192\u59d0\u59d0\uff0c\u542c \u8fd9\u4f4d\u5c0f\u5f1f\u5f1f\u6240\u8bf4\uff0c\u4f3c\u4e4e\u662f\u4eba\u5996\u90dd\u98de\u51e4\u4e5f\u6765\u5230\u56de\u7586\u4e86\u3002\u4ed6\u7684\u6b66\u529f\u5982\u4f55\u4f24\u5f97\u4e86\u6b66\u5e84\u4e3b\uff1f\u201d\u5085\u9752\u4e3b \u70b9\u70b9\u5934\u9053\uff1a\u201c\u8bf4\u8bdd\u9634\u58f0\u602a\u6c14\uff0c\u5f62\u8c8c\u4f53\u6001\u90fd\u4f3c\u5973\u4eba\u7684\u602a\u7269\uff0c\u90a3\u4e00\u5b9a\u662f\u90dd\u98de\u51e4\u4e86\uff0c\u5c0f\u5f1f\u5f1f\uff0c\u4ed6\u624b \u4e2d\u4f7f\u7684\u662f\u4e0d\u662f\u4e00\u628a\u94c1\u6247\u5b50\uff1f\u201d\u6b66\u6210\u5316\u9053\uff1a\u201c\u662f\u5440\uff01\u4e24\u4e2a\u4eba\u4f7f\u7684\u90fd\u662f\u94c1\u6247\u5b50\uff01\u201d\u5085\u9752\u4e3b\u50ac\u9a6c\u5feb \u8d70\uff0c\u5bf9\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6562\u662f\u90a3\u4e2a\u8001\u602a\u7269\u4e5f\u6765\u4e86\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u6c5f\u5357\u6765\u8001\u602a\uff0c\u585e\u5916\u73b0\u4eba\u5996\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5eff\u56db\u7ae0\u3000\u6f20\u5916\u64d2\u51f6\u3000\u77f3\u7a9f\u7edd\u62db\u8bdb\u602a\u7269\u3000\u8349\u539f\u8f83\u6280\u3000\u5929\u5c71\u795e\u5251\u4f0f\u5947\u4eba \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u4e8c\u5341\u56db\u56de \u6f20\u5916\u64d2\u51f6\u3000\u77f3\u7a9f\u7edd\u62db\u8bdb\u602a\u7269 \u8349\u539f\u8f83\u6280\u3000\u5929\u5c71\u795e\u5251\u4f0f\u5947\u4eba \u3000\u3000\u51cc\u672a\u98ce\u731c\u5230\u51e0\u5206\uff0c\u5fc3\u5934\u4e00\u51db\uff0c\u95ee\u9053\uff1a\u201c\u54ea\u4e2a\u8001\u602a\u7269\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u94c1\u6247\u5e2e\u7684\u5e2e\u4e3b\u5c1a\u4e91 \u4ead\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u95fb\u8bf4\u8fd9\u8001\u602a\u7269\u9887\u6709\u72ec\u95e8\u6b66\u529f\uff0c\u8f6f\u7684\u786c\u7684\u5168\u90fd\u4e0d\u5403\uff0c\u9ed1\u9053\u767d\u9053\u5168\u4e0d\u5356\u5e10\uff0c \u867d\u7136\u6df7\u8d26\uff0c\u5374\u8fd8\u4e0d\u662f\u9876\u574f\u7684\u4eba\uff0c\u5982\u4f55\u4f1a\u540c\u4eba\u5996\u90dd\u98de\u51e4\u5728\u4e00\u8d77\uff1f\u53c8\u5982\u4f55\u4f1a\u53bb\u627e\u6b66\u5143\u82f1\u7684\u6666\u6c14\uff0c \u8fd9\u5374\u771f\u662f\u51fa\u5947\uff01\u201d \u3000\u3000\u4f17\u4eba\u5feb\u9a6c\u52a0\u97ad\uff0c\u767e\u591a\u91cc\u8def\uff0c\u4e0d\u8fc7\u534a\u5929\u5c31\u8d76\u5230\u4e86\uff0c\u6751\u5e84\u4e0a\u7684\u4eba\u6025\u5fd9\u8fce\u63a5\uff0c\u6b66\u5927\u5a18\u559c\u9053\uff1a \u201c\u5085\u4f2f\u4f2f\u6765\u4e86\uff0c\u6210\u5316\u7684\u7239\u6709\u6551\u4e86\uff01\u201d\u5085\u9752\u4e3b\u4e0e\u6b66\u743c\u7476\u8fdb\u5165\u5185\u5ba4\uff0c\u53ea\u89c1\u6b66\u5143\u82f1\u9762\u8272\u6de4\u9ed1\uff0c\u6c14\u82e5 \u6e38\u4e1d\uff0c\u89c1\u4e86\u6545\u4eba\uff0c\u5634\u5507\u5fae\u52a8\uff0c\u5374\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u5085\u9752\u4e3b\u4ed4\u7ec6\u9a8c\u89c6\uff0c\u66ff\u4ed6\u628a\u8109\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u788d \u4e8b\uff0c\u4e0d\u788d\u4e8b\u3002\u201d\u6025\u5fd9\u66ff\u4ed6\u653e\u8840\uff0c\u5e76\u63a8\u62ff\u6709\u5173\u7684\u7a74\u9053\uff0c\u7136\u540e\u53d6\u51fa\u4e00\u5757\u836f\u997c\uff0c\u7ed9\u4ed6\u56bc\u788e\u541e\u4e86\u3002\u8fc7 \u4e86\u7247\u523b\uff0c\u6b66\u5143\u82f1\u9762\u8272\u597d\u8f6c\uff0c\u53eb\u9053\uff1a\u201c\u597d\u72e0\u6bd2\u7684\u8001\u4e1c\u897f\uff01\u201d\u5728\u5e8a\u5934\u4e0b\u53d6\u51fa\u4e00\u652f\u9ed1\u8272\u7684\u6bd2\u7bad\uff0c\u8bf4 \u9053\uff1a\u201c\u4e0d\u662f\u6211\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\u8fd8\u71ac\u5f97\u4f4f\uff0c\u53ef\u89c1\u4e0d\u7740\u4f60\u4e86\uff01\u201d\u6b66\u5143\u82f1\u4e00\u5411\u5728\u897f\u5317\uff0c\u800c\u5c1a\u4e91\u4ead\u5219\u5728 \u6c5f\u5357\uff0c\u4e24\u4eba\u4ece\u672a\u89c1\u8fc7\u9762\uff0c\u6b66\u5143\u82f1\u9053\uff1a\u201c\u6628\u665a\u6211\u6597\u90a3\u4e24\u4e2a\u8d3c\u4eba\uff0c\u8001\u8d3c\u7684\u6b66\u529f\u867d\u5f3a\uff0c\u6211\u8fd8\u6321\u5f97\u4f4f \u4ed6\u3002\u4ed6\u90a3\u628a\u94c1\u6247\u8d77\u521d\u65bd\u5c55\u7684\u4e5f\u4e0d\u8fc7\u662f\u70b9\u7a74\u529f\u592b\uff0c\u4e0d\u6599\u5230\u4e86\u540e\u6765\uff0c\u8d8a\u6597\u8d8a\u6025\uff0c\u6211\u7684\u5200\u5c16\u78b0\u5728\u4ed6 \u7684\u6247\u4e0a\uff0c\u84ec\u7684\u4e00\u58f0\uff0c\u5c31\u98de\u51fa\u4e86\uff0c\u51e0\u679d\u6bd2\u7bad\uff0c\u6697\u5668\u539f\u6765\u662f\u85cf\u5728\u6247\u5b50\u5185\u7684\u3002\u201d\u5c1a\u4e91\u4ead\u7684\u6bd2\u7bad\uff0c\u672c \u6765\u89c1\u8840\u5c01\u5589\uff0c\u5e78\u5728\u6b66\u5143\u82f1\u51e0\u5341\u5e74\u529f\u592b\uff0c\u975e\u6bd4\u5bfb\u5e38\uff0c\u8fd9\u624d\u71ac\u5f97\u5230\u5085\u9752\u4e3b\u5230\u6765\u3002\u5085\u9752\u4e3b\u5fc3\u4e2d\u6697\u53eb \u201c\u597d\u9669\u201d\uff01\u521a\u624d\u4ed6\u8bf4\u7684\u201c\u4e0d\u788d\u4e8b\u201d\uff0c\u53ea\u662f\u5b89\u6170\u6b66\u8c05\u7476\u7684\uff0c\u73b0\u5728\u89c1\u6b66\u5143\u82f1\u5df2\u771f\u7684\u4e0d\u788d\u4e8b\u4e86\uff0c\u8fd9 \u624d\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u5085\u9752\u4e3b\u4e0d\u8bb8\u6b66\u5143\u82f1\u591a\u8bf4\u8bdd\uff0c\u53eb\u6b66\u743c\u7476\u4f8d\u5019\u4ed6\u4f11\u606f\uff0c\u81ea\u51fa\u5916\u5802\u3002\u6b66\u5927\u5a18\u548c\u5929\u5730\u4f1a\u7684\u5f1f\u5144\u65e9 \u5bb0\u4e86\u51e0\u53ea\u80a5\u7f8a\uff0c\u5907\u597d\u6c34\u9152\u6b3e\u5f85\u3002\u4f17\u4eba\u7b49\u4e00\u8def\u4e0a\u5403\u7684\u90fd\u662f\u5e72\u7cae\uff0c\u5634\u91cc\u65e9\u6de1\u51fa\u9e1f\u6765\uff0c\u5927\u5757\u8089\uff0c\u5927 \u5757\u9152\uff0c\u5403\u5f97\u5f88\u662f\u9ad8\u5174\u3002\u6b66\u5927\u5a18\u6084\u6084\u5730\u5bf9\u5085\u9752\u4e3b\u9053\uff1a\u201c\u5085\u4f2f\u4f2f\uff0c\u4f60\u77a7\u90a3\u4e24\u4e2a\u8d3c\u4eba\u8fd8\u4f1a\u4e0d\u4f1a \u6765\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6211\u5c31\u62c5\u5fc3\u4ed6\u4e0d\u6765\uff01\u201d\u60f3\u4e86\u4e00\u4f1a\uff0c\u53eb\u6b66\u5927\u5a18\u5524\u6b66\u743c\u7476\u51fa\u6765\uff0c\u53eb\u5979\u548c\u6613\u5170\u73e0 \u4e0d\u8981\u643a\u5e26\u6b66\u5668\uff0c\u5230\u6751\u91cc\u6751\u5916\u8d70\u4e86\u4e00\u8f6c\uff0c\u53c8\u5bf9\u6b66\u5927\u5a18\u9053\uff1a\u201c\u5ac2\u5b50\uff0c\u8bf7\u6055\u6211\u65e0\u793c\uff0c\u6211\u60f3\u8bf7\u5ac2\u5b50\u5f00 \u8bbe\u7075\u5802\uff0c\u95e8\u53e3\u6302\u767d\uff0c\u5047\u88c5\u529e\u4e27\u4e8b\u3002\u201d\u6b66\u5927\u5a18\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5085\u9752\u4e3b\u8f7b\u58f0\u9053\uff1a\u201c\u5f15\u654c\u4eba\u6765 \u5440\uff01\u8fd9\u4e24\u4e2a\u602a\u7269\uff0c\u5c24\u5176\u662f\u90a3\u4e2a\u4eba\u5996\uff0c\u6211\u65e9\u5c31\u60f3\u628a\u4ed6\u9664\u4e86\uff01\u201d\u6b66\u5927\u5a18\u548c\u4e08\u592b\u4e00\u5411\u8c41\u8fbe\uff0c\u8fdb\u53bb\u548c \u4ed6\u8bf4\u4e86\uff0c\u6b66\u5143\u82f1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u6761\u547d\u4e5f\u662f\u5085\u8001\u54e5\u5b50\u6551\u7684\uff0c\u6211\u8fd8\u6709\u4ec0\u4e48\u5fcc\u8bb3\uff1f\u8981\u88c5\u5047\u5c31\u8981\u88c5 \u5f97\u50cf\u4e00\u70b9\uff0c\u53eb\u743c\u513f\u9010\u6237\u53bb\u62a5\u4e27\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u66ff\u6b66\u5927\u5a18\u5b89\u6392\u5b8c\u6bd5\uff0c\u53eb\u6b66\u743c\u7476\u548c\u6613\u5170\u73e0\u5728\u539f\u6765\u7684\u623f\u95f4\u7761\u89c9\uff0c\u81ea\u5df1\u548c\u77f3\u5929\u6210\u5219\u5728\u90bb \u623f\uff0c\u77f3\u5927\u5a18\u548c\u6b66\u5927\u5a18\u540c\u4f4f\uff0c\u51cc\u672a\u98ce\u5728\u5916\u9762\u5de1\u89c6\u3002\u5e03\u7f6e\u5f97\u975e\u5e38\u5468\u5bc6\uff0c\u4e0d\u6599\u4e00\u8fde\u4e24\u665a\uff0c\u654c\u4eba\u90fd\u4e0d \u6765\u3002\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6211\u770b\u654c\u4eba\u4e00\u5b9a\u4f1a\u6765\u7684\u3002\u4e0d\u80fd\u677e\u61c8\u3002\u201d\u679c\u7136\u7b2c\u4e09\u665a\u7684\u4e0b\u534a\u591c\uff0c\u654c\u4eba\u771f\u4e2a\u6765 \u4e86\uff0c\u6b66\u743c\u7476\u51e0\u4e4e\u7740\u4e86\u9053\u513f\u3002 \u3000\u3000\u94c1\u6247\u5e2e\u7684\u5e2e\u4e3b\u5c1a\u4e91\u4ead\u548c\u4eba\u5996\u90dd\u98de\u51e4\u8fdc\u6765\u56de\u7586\uff0c\u5176\u4e2d\u5374\u6709\u4e00\u6bb5\u7f18\u6545\u3002\u4ed6\u4eec\u662f\u7ed9\u5b5f\u6b66\u5a01\u548c\u77f3 \u632f\u98de\u8feb\u5f97\u8fdc\u8d70\u9ad8\u98de\u7684\uff0c\u5b5f\u6b66\u5a01\u7684\u513f\u5b50\u5b5f\u575a\u90a3\u6b21\u7ed9\u7eb3\u5170\u76f8\u5e9c\u4fdd\u7f25\uff0c\u51e0\u4e4e\u632b\u6298\u5728\u90dd\u98de\u51e4\u624b\u4e0a\uff0c\u56e0 \u6b64\u81ea\u5317\u4eac\u5927\u52ab\u5929\u7262\u4e4b\u540e\uff0c\u5b5f\u6b66\u6210\u5c31\u643a\u5b50\u4e0b\u6c5f\u5357\uff0c\u5e76\u7ea6\u5f97\u77f3\u632f\u98de\u76f8\u52a9\uff0c\u628a\u94c1\u6247\u5e2e\u7684\u579b\u5b50\u7a91\u6311 \u4e0b\uff0c\u5c1a\u4e91\u4ead\u8d25\u7ed9\u77f3\u632f\u98de\u7684\u8e51\u4e91\u5341\u4e09\u5251\uff0c\u90dd\u98de\u51e4\u4e5f\u51e0\u4e4e\u7ed9\u5b5f\u6b66\u5a01\u7684\u94c1\u70df\u6746\u6253\u6b7b\uff0c\u5c1a\u4e91\u4ead\u4ed7\u7740\u4e00 \u8eab\u7cbe\u7eaf\u7684\u6b66\u529f\uff0c\u8f93\u4e86\u4e00\u62db\uff0c\u5c31\u8131\u51fa\u8eab\u6765\uff0c\u63a9\u62a4\u90dd\u98de\u51e4\u9003\u8d70\uff0c\u540e\u6765\u59d4\u5b9e\u5728\u6c5f\u5357\u7ad9\u4e0d\u4f4f\u4e86\uff0c\u8fd9\u624d \u9041\u5230\u6f20\u5916\u3002 \u3000\u3000\u5374\u8bf4\u51cc\u672a\u98ce\u5728\u5916\u9762\u628a\u98ce\uff0c\u4e09\u66f4\u8fc7\u540e\uff0c\u6beb\u65e0\u52a8\u9759\uff0c\u65e0\u804a\u5f97\u5f88\uff0c\u62bd\u51fa\u4f0a\u58eb\u8fbe\u4e34\u7ec8\u65f6\u9001\u7ed9\u4ed6\u7684 \u90a3\u4e00\u628a\u5b9d\u5251\u6765\uff0c\u8fd9\u628a\u5251\u53e4\u8272\u6591\u6593\uff0c\u5bd2\u5149\u900f\u5c04\uff0c\u5f0f\u6837\u548c\u4e2d\u58eb\u7684\u5251\u53c8\u6709\u4e0d\u540c\uff0c\u4ed6\u628a\u73a9\u4e86\u4e00\u4f1a\uff0c\u5ffd \u89c1\u6751\u5934\u4eba\u5f71\u4e00\u95ea\uff0c\u628a\u5251\u4e00\u6a2a\uff0c\u5c31\u5954\u4e0a\u524d\u53bb\u3002\u524d\u9762\u6765\u7684\u4e43\u662f\u4e09\u4e2a\u756a\u50e7\uff0c\u51cc\u672a\u98ce\u5f81\u4e86\u4e00\u6014\uff0c\u5fc3\u60f3 \u5c1a\u4e91\u4ead\u548c\u90dd\u98de\u51e4\u81ea\u5df1\u867d\u7136\u6ca1\u89c1\u8fc7\uff0c\u4f46\u603b\u4e0d\u4f1a\u662f\u756a\u50e7\u5427\uff1f\u6b63\u60f3\u53d1\u95ee\uff0c\u4e3a\u9996\u7684\u756a\u50e7\u5ffd\u7136\u54a6\u4e86\u4e00 \u58f0\uff0c\u8d70\u4e86\u4e0a\u6765\uff0c\u7ffb\u7740\u602a\u773c\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u4ece\u4f55\u5f97\u5230\u8fd9\u628a\u5b9d\u5251\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd9\u628a\u5251\u4e0e\u4f60\u6709 \u4f55\u5173\u7cfb\uff1f\u201d\u756a\u50e7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u8fd9\u628a\u5251\u7684\u6765\u5386\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4ec0\u4e48\u6765\u5386\u6211\u53ef\u4e0d\u7ba1\uff0c\u6211\u53ea \u77e5\u9053\u5b83\u662f\u6768\u4e91\u9aa2\u7684\u4e1c\u897f\uff01\u201d\u756a\u50e7\u53eb\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6768\u4e91\u9aa2\u7684\u4e1c\u897f\uff1f\u6768\u4e91\u9aa2\u662f\u4e2a\u5f3a\u76d7\uff0c\u4ed6 \u82e5\u4e0d\u662f\u6b7b\u5728\u6c5f\u5357\uff0c\u6211\u4f1a\u628a\u4ed6\u7684\u9aa8\u5934\u6316\u51fa\u6765\u6253\u4e09\u767e\u97ad\uff01\u201d\u51cc\u672a\u98ce\u6700\u656c\u7231\u81ea\u5df1\u7684\u5927\u5e08\u5144\uff0c\u95fb\u8a00\u5fcd \u7740\u4e00\u80a1\u6012\u6c14\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u83ab\u975e\u5c31\u662f\u5929\u8499\u7985\u5e08\uff1f\u201d\u756a\u50e7\u5f97\u610f\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u77e5\u9053\u8001\u4f5b\u7237\u7684\u540d \u5b57\uff0c\u90a3\u4e48\u4f60\u4e5f\u8be5\u77e5\u9053\u8fd9\u628a\u5251\u662f\u6211\u7684\u4e1c\u897f\u4e86\u3002\u4f60\u4e56\u4e56\u9001\u4e0a\uff0c\u8001\u4f5b\u7237\u53ef\u9976\u4f60\u4e00\u6761\u6821\u72d0\uff0c\u8981\u4e0d\u7136\uff0c \u54fc\uff0c\u6559\u4f60\u627e\u6768\u4e91\u9aa2\u53bb\uff01\u201d\u51cc\u672a\u98ce\u5fc3\u60f3\uff1a\u5929\u8499\u7985\u5e08\u5f53\u65e5\u7387\u95e8\u4e0b\u5f1f\u5b50\u56f4\u653b\u81ea\u5df1\u7684\u5e08\u5144\uff0c\u7ed9\u5e08\u5144\u7f34 \u53bb\u4ed6\u7684\u5b9d\u5251\uff0c\u9001\u7ed9\u4f0a\u571f\u8fbe\uff0c\u8bf4\u8d77\u6765\u8fd9\u756a\u50e7\u602a\u4e0d\u5f97\u8c01\u3002\u53ea\u662f\u73b0\u5728\u5df1\u8fc7\u4e86\u4e8c\u5341\u591a\u5e74\uff0c\u4e0d\u77e5\u4ed6\u662f\u597d \u662f\u574f\uff0c\u82e5\u7136\u4ed6\u5df2\u6539\u8fc7\uff0c\u90a3\u4e48\u6e05\u5175\u5165\u4fb5\u5728\u5373\uff0c\u8499\u85cf\u56de\u7586\u7684\u4eba\u90fd\u5e94\u9f50\u5fc3\u6297\u654c\u624d\u662f\uff0c\u4e0d\u503c\u5f97\u4e3a\u4e86\u4e00 \u628a\u5251\u800c\u5f97\u7f6a\u4ed6\u3002\u6b63\u8e0c\u8e87\u95f4\uff0c\u90a3\u756a\u50e7\u53c8\u559d\u9053\uff1a\u201c\u4f60\u7ed9\u4e0d\u7ed9\uff1f\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u6562\u6297\u8001\u4f5b\u7237\u4e4b\u547d\uff01\u201d \u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u5c31\u662f\u6768\u4e91\u9aa2\u7684\u5e08\u5f1f\uff01\u201d\u756a\u618e\u677f\u7740\u8138\u5b54\u95ee\u9053\uff1a\u201c\u6211\u53ea\u77e5\u6768\u4e91\u9aa2\u6709\u4e00\u4e2a\u5e08\u5f1f\u695a\u662d \u5357\uff0c\u600e\u4e48\u73b0\u5728\u53c8\u94bb\u51fa\u4e00\u4e2a\u6765\u4e86\uff1f\u4f60\u82e5\u662f\u6768\u4e91\u9aa2\u7684\u5e08\u5f1f\uff0c\u90a3\u4e48\u4f60\u4e5f\u5f97\u542c\u4f60\u73b0\u5728\u7684\u5e08\u5144\u7684\u8bf4 \u8bdd\u3002\u201d\u51cc\u672a\u98ce\u626c\u7709\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u5929\u8499\u7985\u5e08\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u90a3\u4f60\u51c6\u662f\u5047 \u5192\u7684\u4e86\uff01\u695a\u662d\u5357\u5e26\u5b98\u5175\u5230\u4e86\u56de\u7586\uff0c\u6d3e\u4eba\u5165\u85cf\u5411\u6211\u8d54\u7f6a\uff0c\u66ff\u4ed6\u6b7b\u53bb\u7684\u5e08\u5144\u6c42\u9976\uff0c\u53eb\u6211\u5e2e\u4ed6\u5e73\u5b9a \u8499\u85cf\uff01\u4ed6\u7b54\u5e94\u7ed9\u6211\u627e\u56de\u5b9d\u5251\uff0c\u82e5\u627e\u4e0d\u56de\uff0c\u5c31\u628a\u4ed6\u7684\u6e38\u9f99\u5251\u9001\u6211\u54e9\uff01\u8fd9\u628a\u5251\u65e2\u5728\u4f60\u624b\u4e2d\uff0c\u90a3\u8fd8 \u6709\u4ec0\u4e48\u53ef\u8bf4\uff01\u201d\u51cc\u672a\u98ce\u5ffd\u7136\u5706\u7741\u53cc\u773c\uff0c\u559d\u9053\uff1a\u201c\u6211\u672c\u4e0d\u60f3\u8981\u8fd9\u628a\u5251\u7684\uff0c\u73b0\u5728\u5374\u504f\u4e0d\u7ed9\u4f60\uff0c\u6709 \u672c\u4e8b\u4f60\u5c31\u6765\u53d6\uff01\u201d \u3000\u3000\u5929\u8499\u7985\u5e08\u559d\u9053\uff1a\u201c\u5f92\u513f\uff0c\u66ff\u6211\u628a\u8fd9\u72c2\u5f92\u62ff\u4e0b\uff01\u201d\u4e24\u4e2a\u5c11\u5e74\u756a\u50e7\u5de6\u53f3\u6251\u4e0a\uff0c\u51cc\u672a\u98ce\u5140\u7acb\u5982 \u5c71\uff0c\u56db\u53ea\u62f3\u5934\u540c\u65f6\u6253\u5230\u8eab\u4e0a\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u84ec\u201d\u4e24\u58f0\uff0c\u8dcc\u5012\u7684\u4e0d\u662f\u51cc\u672a\u98ce\uff0c\u5374\u662f\u90a3\u4e24\u4e2a\u5c11\u5e74\u756a \u50e7\uff01\u5929\u8499\u7985\u5e08\u864e\u543c\u4e00\u58f0\uff0c\u5ffd\u7136\u8131\u4e0b\u5927\u7ea2\u50e7\u888d\uff0c\u8fce\u98ce\u4e00\u6296\uff0c\u4f3c\u4e00\u7247\u7ea2\u4e91\u76f4\u7f69\u4e0b\u6765\u3002\u51cc\u672a\u98ce\u89c1\u6765 \u52bf\u51f6\u731b\uff0c\u8eab\u79fb\u6b65\u6362\uff0c\u907f\u8fc7\u6765\u52bf\uff0c\u4e00\u624b\u6293\u7740\u888d\u89d2\uff0c\u53ea\u89c9\u5982\u6293\u7740\u4e00\u5757\u94c1\u677f\u4e00\u822c\uff0c\u77e5\u9053\u5929\u8499\u7684\u6b66\u529f \u4e5f\u5df2\u767b\u5cf0\u9020\u6781\uff0c\u6697\u8fd0\u5185\u529b\uff0c\u4e00\u58f0\u88c2\u5e1b\uff0c\u6495\u4e0b\u4e86\u534a\u8fb9\u50e7\u888d\uff0c\u5929\u8499\u7985\u5e08\u90a3\u534a\u622a\u50e7\u888d\u5df2\u6a2a\u626b\u8fc7\u6765\uff0c \u5de6\u638c\u547c\u7684\u4e00\u58f0\u4e5f\u4ece\u888d\u5e95\u653b\u51fa\uff0c\u51cc\u672a\u98ce\u8eab\u5b50\u9661\u7136\u4e00\u7f29\uff0c\u53ea\u5dee\u534a\u5bf8\uff0c\u6ca1\u7ed9\u6253\u7740\uff0c\u5929\u8499\u7985\u5e08\u9aa4\u5931\u91cd \u5fc3\uff0c\u6643\u4e86\u4e00\u6643\uff0c\u51cc\u672a\u98ce\u817e\u5730\u98de\u8d77\u4e00\u811a\uff0c\u5929\u8499\u7985\u5e08\u5c45\u7136\u5e73\u5730\u62d4\u8d77\u4e24\u4e08\u591a\u9ad8\uff0c\u624b\u4e2d\u50e7\u888d\uff0c\u518d\u5ea6\u51cc \u7a7a\u6251\u51fb\uff01 \u3000\u3000\u5929\u8499\u662f\u897f\u85cf\u5929\u9f99\u6d3e\u5f00\u5c71\u7956\u5e08\u5929\u9f99\u7985\u5e08\u7684\u5e08\u5f1f\uff0c\u81ea\u4e8c\u5341\u591a\u5e74\u7684\u8f93\u7ed9\u6768\u4e91\u9aa2\u4e4b\u540e\uff0c\u56de\u5230\u897f \u85cf\uff0c\u6f5c\u5fc3\u518d\u82e6\u7ec3\u4e86\u4e8c\u5341\u5e74\uff0c\u529f\u529b\u8fdc\u975e\u4ee5\u524d\u53ef\u6bd4\uff0c\u7adf\u7136\u548c\u51cc\u672a\u98ce\u6253\u4e86\u8bb8\u4e45\uff0c\u672a\u9732\u8d25\u8c61\u3002 \u3000\u3000\u518d\u8bf4\u6b66\u743c\u7476\u548c\u6613\u5170\u73e0\u540c\u4f4f\u4e00\u5ba4\uff0c\u5348\u591c\u8fc7\u540e\uff0c\u5c1a\u672a\u89c1\u52a8\u9759\uff0c\u6b66\u743c\u7476\u9053\uff1a\u201c\u535a\u4f2f\u4f2f\u8fd9\u4e2a\u8ba1\u7b56 \u53c8\u6015\u4e0d\u884c\uff0c\u654c\u4eba\u672a\u5fc5\u4f1a\u6765\u3002\u201d\u6613\u5170\u73e0\u903c\uff1a\u201c\u8fd8\u662f\u5c0f\u5fc3\u9632\u5907\u7684\u597d\u3002\u201d\u6b66\u743c\u7476\u9053\uff1a\u201c\u5916\u9762\u6709\u51cc\u5927 \u4fa0\u628a\u98ce\uff0c\u654c\u4eba\u82e5\u6765\uff0c\u53ea\u6084\u672a\u8fdb\u5165\u5e84\u5185\uff0c\u5c31\u7ed9\u4ed6\u6536\u62fe\u4e86\uff0c\u8fd8\u8f6e\u5230\u6211\u548c\u4f60\u52a8\u624b\u5417\uff1f\u201d\u5979\u7d2f\u4e86\u4e09 \u665a\uff0c\u4e0d\u89c9\u6253\u8d77\u778c\u7761\u3002\u6613\u5170\u73e0\u5374\u4ecd\u6253\u70b9\u7cbe\u795e\uff0c\u4ed7\u5251\u9632\u5b88\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u5ffd\u7136\u6709\u80a1\u9999\u6c14\u4ece\u7a97\u5916\u5439\u8fdb \u6765\uff0c\u4ee4\u4eba\u660f\u660f\u6b32\u9189\uff0c\u6613\u5170\u73e0\u5927\u53eb\u4e00\u58f0\u4e0d\u597d\uff0c\u7a97\u5916\u5df2\u98de\u8fdb\u4e24\u4e2a\u4eba\u6765\uff0c\u4e3a\u9996\u7684\u4eba\u9634\u58f0\u602a\u6c14\u7b11\u9053\uff1a \u201c\u54c8\uff0c\u54c8\uff0c\u4e24\u4e2a\u82b1\u59d1\u5a18\u90fd\u5728\u8fd9\u91cc\uff01\u201d\u6613\u5170\u73e0\u4fa7\u7684\u4e00\u5251\u523a\u51fa\uff0c\u90dd\u98de\u51e4\u4e3e\u6247\u4e00\u6321\uff0c\u94ff\u9535\u4e00\u58f0\uff0c\u94c1 \u6247\u5df2\u7ed9\u65a9\u65ad\uff0c\u51e0\u5341\u679d\u6885\u82b1\u9488\u98de\u5c04\u51fa\u6765\uff0c\u6613\u5170\u73e0\u821e\u8d77\u5b9d\u5251\uff0c\u4e00\u7247\u94ee\u94ee\u58f0\u54cd\uff0c\u628a\u6885\u82b1\u9488\u90fd\u6fc0\u5f97\u53cd \u5c04\u56de\u53bb\uff0c\u90dd\u98de\u51e4\u7edd\u672a\u6599\u5230\u6613\u5170\u73e0\u5982\u6b64\u5389\u5bb3\uff0c\u624b\u5fd9\u811a\u4e71\uff0c\u5c1a\u4e91\u4ead\u5927\u8896\u4e00\u6325\uff0c\u6885\u82b1\u9488\u5168\u7ed9\u9707\u843d\uff0c \u8eab\u5f62\u8d77\u5904\uff0c\u7adf\u5982\u82cd\u9e70\u6251\u5154\uff0c\u5411\u6b66\u743c\u7476\u6293\u53bb\u3002 \u3000\u3000\u7ec3\u6b66\u7684\u4eba\uff0c\u6700\u4e3a\u8b66\u9192\uff0c\u6b66\u743c\u7476\u521a\u521a\u5165\u7761\uff0c\u4e00\u95f9\u5c31\u9192\u8fc7\u6765\uff0c\u53ea\u662f\u8ff7\u8ff7\u7cca\u7cca\uff0c\u7adf\u6ca1\u6c14\u529b\uff0c\u5c1a \u4e91\u4ead\u6251\u5730\u6293\u5230\uff0c\u5371\u6025\u4e2d\u6b66\u743c\u7476\u5ffd\u60f3\u8d77\u767d\u53d1\u9b54\u5973\u7684\u72ec\u95e8\u7edd\u62db\u201c\u65e0\u5e38\u593a\u547d\u201d\uff0c\u5c31\u5730\u4e00\u6eda\uff0c\u7ea4\u8db3\u98de \u8d77\uff0c\u8e22\u5c1a\u4e91\u4ead\u817f\u5f2f\u7684\u201c\u767d\u5e02\u7a74\u201d\uff0c\u5c1a\u4e91\u4ead\u8eab\u5b50\u4e00\u7f29\uff0c\u6b66\u743c\u7476\u5df2\u6eda\u8fc7\u4e00\u8fb9\uff0c\u6613\u5170\u73e0\u4e00\u5251\u81ea\u540e\u523a \u5230\uff0c\u5c1a\u4e91\u4ead\u53cd\u624b\u4e00\u62ff\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u5411\u6613\u5170\u73e0\u7684\u624b\u8155\u6293\u5230\uff0c\u6613\u5170\u73e0\u5251\u5982\u98de\u51e4\uff0c\u4e00\u8f6c\u624b\u8155\u659c\u523a\u51fa \u53bb\uff0c\u5c1a\u4e91\u4ead\u6b65\u4f3c\u733f\u7334\uff0c\u94c1\u6247\u8d77\u5904\uff0c\u53c8\u5df2\u6307\u5230\u6613\u5170\u73e0\u80c1\u4e0b\uff0c\u6613\u5170\u73e0\u53ea\u89c9\u8111\u75db\u6b32\u88c2\uff0c\u5251\u6cd5\u867d\u7136\u7cbe \u5999\uff0c\u5374\u654c\u4e0d\u4f4f\u5c1a\u4e91\u4ead\uff0c\u53ea\u597d\u8fde\u8fde\u95ea\u8eb2\u3002\u5c1a\u4e91\u4ead\u89c1\u6613\u5170\u73e0\u5438\u4e86\u8ff7\u9999\uff0c\u6b66\u529f\u8fd8\u662f\u5982\u6b64\u4e86\u5f97\uff0c\u4e0d\u7981 \u9a87\u7136\u3002\u90dd\u98de\u51e4\u4e58\u673a\u53bb\u6293\u6b66\u743c\u7476\uff0c\u5ffd\u7136\u7a97\u5916\u4e00\u58f0\u51b7\u7b11\uff0c\u90dd\u98de\u51e4\u5495\u549a\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\uff0c\u5c1a\u4e91\u4ead\u626c \u624b\u4e00\u6325\uff0c\u4e00\u5708\u91d1\u5149\u53cd\u5c04\u51fa\u53bb\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8d3c\u5a46\u5a18\u6562\u65bd\u6697\u7b97\uff1f\u201d \u3000\u3000\u77f3\u5927\u5a18\u56de\u8eab\u4e00\u95ea\uff0c\u5c1a\u4e91\u4ead\u98de\u7bad\u822c\u5730\u7a7f\u51fa\u7a97\u53bb\uff0c\u77f3\u5927\u5a18\u7684\u4e94\u79bd\u5251\u5f53\u5934\u538b\u4e0b\uff0c\u5c1a\u4e91\u4ead\u559d\u58f0 \u201c\u6253\uff01\u201d\u94c1\u6247\u4e00\u70b9\u77f3\u5927\u5a18\u624b\u8155\uff0c\u77f3\u5927\u5a18\u51b7\u7b11\u4e00\u58f0\uff0c\u56de\u5251\u6a2a\u626b\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u8fdb\u4e86\u56db\u62db\uff0c\u5c1a\u4e91\u4ead \u5927\u5403\u4e00\u60ca\uff0c\u98de\u8eab\u4fbf\u9003\u3002\u6697\u89d2\u5904\uff0c\u84e6\u7136\u53c8\u8f6c\u51fa\u4e00\u4e2a\u5112\u51a0\u8001\u8005\uff0c\u957f\u987b\u98d8\u98d8\uff0c\u5c1a\u4e91\u4ead\u4e3e\u6247\u6a2a\u62e8\uff0c\u90a3 \u8001\u8005\u5251\u62db\u6781\u6162\uff0c\u4f46\u5374\u6709\u6781\u5927\u6f5c\u529b\uff0c\u5c1a\u4e91\u4ead\u6247\u642d\u5251\u8eab\uff0c\u6b63\u60f3\u6765\u4e2a\u201c\u987a\u6c34\u63a8\u821f\u201d\uff0c\u4e0a\u524a\u654c\u4eba\u63e1\u5251 \u7684\u624b\u6307\uff0c\u4e0d\u6599\u94c1\u6247\u7adf\u7ed9\u654c\u4eba\u7684\u5251\u7c98\u4f4f\uff0c\u4f11\u8bf4\u4e0a\u524a\uff0c\u8fde\u79fb\u52a8\u90fd\u96be\uff0c\u5c1a\u4e91\u4ead\u6025\u8fd0\u8db3\u5341\u6210\u5185\u529b\uff0c\u5411 \u5916\u4e00\u63a2\uff0c\u5de6\u638c\u4e5f\u4f7f\u4e86\u4e00\u62db\u64d2\u62ff\u624b\uff0c\u624d\u89e3\u4e86\u654c\u52bf\uff0c\u4e00\u6643\u8eab\uff0c\u659c\u8dc3\u4e0b\u843d\u3002\u8fd9\u5112\u51a0\u8001\u8005\u4e43\u662f\u5085\u9752 \u4e3b\uff0c\u548c\u77f3\u5927\u5a18\u8054\u8882\u9000\u4e0b\u3002 \u3000\u3000\u5c1a\u4e91\u4ead\u811a\u65b9\u70b9\u5730\uff0c\u98de\u7ea2\u4e2d\u65e9\u5df2\u5728\u697c\u4e0b\u7b49\u5019\uff0c\u957f\u97ad\u547c\u547c\uff0c\u5411\u94c1\u6247\u5377\u6765\u3002\u5c1a\u4e91\u4ead\u4ed7\u7740\u7cbe\u7eaf\u7684 \u6b66\u529f\uff0c\u62c6\u4e86\u51e0\u62db\uff0c\u5140\u662f\u89c9\u5f97\u5403\u529b\uff0c\u624b\u6307\u4e00\u6309\u94c1\u6247\u4e0a\u673a\u62ec\uff0c\u51e0\u679d\u6bd2\u7bad\uff0c\u6d41\u661f\u95ea\u7535\u822c\u5730\u98de\u51fa\uff0c\u98de \u7ea2\u4e2d\u56de\u97ad\u4e00\u626b\uff0c\u77ed\u5251\u4e00\u8361\uff0c\u628a\u6bd2\u7bad\u5168\u90e8\u6253\u843d\uff0c\u5c1a\u4e91\u4ead\u53c8\u8df3\u51fa\u573a\u5b50\uff0c\u6b63\u60f3\u593a\u95e8\u800c\u51fa\uff0c\u5ffd\u7136\u4e00\u58f0 \u5927\u559d\uff0c\u4e00\u4e2a\u7ea2\u9762\u8001\u4eba\uff0c\u4eba\u672a\u5230\uff0c\u811a\u5148\u5230\uff0c\u53cc\u8db3\u8fde\u98de\uff0c\u4e00\u987f\u9e33\u9e2f\u8fde\u73af\u817f\uff0c\u628a\u5c1a\u4e91\u4ead\u53c8\u9000\u56de\u6765\uff0c \u8fd9\u4eba\u4e43\u662f\u77f3\u5929\u6210\u3002 \u3000\u3000\u5c1a\u4e91\u4ead\u4e00\u770b\u56db\u9762\u516b\u65b9\uff0c\u5168\u662f\u751f\u5e73\u7f55\u9047\u7684\u9ad8\u624b\uff0c\u6a2a\u6247\u5f53\u80f8\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4ee5\u591a\u4e3a\u80dc\uff0c \u6211\u5c1a\u4e91\u4ead\u5934\u9885\u53ea\u6709\u4e00\u9897\uff0c\u4f60\u4eec\u8981\u53d6\uff0c\u6211\u7edd\u4e0d\u76b1\u7709\u3002\u201d\u5085\u9752\u4e3b\u3001\u77f3\u5927\u5a18\u3001\u98de\u7ea2\u5dfe\u3001\u77f3\u5929\u6210\u56db\u8fb9 \u7ad9\u5b9a\uff0c\u4e0d\u7406\u4e0d\u776c\u3002\u4e00\u4e2a\u9634\u607b\u607b\u7684\u58f0\u97f3\u7a81\u7136\u54cd\u81ea\u8033\u8fb9\uff0c\u201c\u4f60\u522b\u5356\u72c2\uff0c\u4f60\u53ea\u8981\u80fd\u63a5\u6211\u4e09\u62db\uff0c\u6211\u5c31 \u653e\u4f60\u51fa\u53bb\uff0c\u51b3\u4e0d\u7559\u8c01\uff01\u201d\u58f0\u97f3\u5f88\u5c0f\uff0c\u5374\u662f\u5b57\u5b57\u6e05\u695a\uff0c\u5c1a\u4e91\u4ead\u7eb5\u773c\u4e00\u770b\uff0c\u53ea\u95fb\u58f0\u800c\u4e0d\u89c1\u4eba\uff0c\u65b9 \u81ea\u60ca\u8be7\uff0c\u5ffd\u7136\u8033\u8fb9\u53c8\u542c\u5f97\u602a\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u53cc\u72d7\u773c\uff0c\u8fde\u6211\u90fd\u770b\u4e0d\u89c1\u3002\u201d\u8bed\u58f0\u65b9\u505c\uff0c\u573a\u4e2d\u5fc3\u5df2 \u591a\u4e86\u4e00\u4e2a\u7626\u5c0f\u7684\u8001\u4eba\u3002\u8fd9\u8001\u4eba\u6b63\u662f\u8f9b\u9f99\u5b50\uff0c\u4ed6\u4eba\u65e2\u77ee\u5c0f\uff0c\u53c8\u4ed7\u7740\u602a\u5f02\u7684\u8eab\u6cd5\uff0c\u7a81\u7136\u94bb\u51fa\uff0c\u4ee4 \u5c1a\u4e91\u4ead\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u5c1a\u4e91\u4ead\u6a2a\u884c\u6c5f\u5357\u51e0\u5341\u5e74\uff0c\u81ea\u7136\u662f\u4e2a\u8bc6\u8d27\u7684\u5927\u884c\u5bb6\uff0c\u77e5\u9053\u8f9b\u9f99\u5b50\u5185\u529f\u6df1\u6e5b\uff0c\u5c31\u53ea\u90a3\u624b\u201c\u4f20 \u97f3\u5165\u5bc6\u201d\u7684\u529f\u592b\uff0c\u4eba\u5728\u8fdc\u5904\uff0c\u800c\u58f0\u5374\u76f4\u8fbe\u522b\u4eba\u8033\u8fb9\uff0c\u8fd9\u6837\u7cbe\u7eaf\u7684\u529f\u529b\uff0c\u8fd8\u771f\u662f\u89c1\u6240\u672a\u89c1\u3002\u53ea \u662f\u5c1a\u4e91\u4ead\u4e5f\u6709\u51e0\u5341\u5e74\u529f\u529b\uff0c\u867d\u7136\u81ea\u77e5\u6bd4\u4e0d\u4e0a\u8f9b\u9f99\u5b50\uff0c\u4f46\u5fc3\u60f3\uff1a\u53ea\u8fc7\u4e09\u62db\uff0c\u4f60\u65e0\u8bba\u5982\u4f55\u4e5f\u6253\u4e0d \u5012\u6211\u3002\u5f53\u4e0b\u6717\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u5f53\u771f\uff1f\u201d\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u8c01\u548c\u4f60\u5f00\u73a9\u7b11\uff1f\u4f60\u6570\u7740\uff0c\u7b2c\u4e00\u62db\u5c31\u8981 \u6253\u5f97\u4f60\u6251\u5730\uff01\u201d\u5c1a\u4e91\u4ead\u7a81\u89c9\u773c\u524d\u4eba\u5f71\u4e00\u6643\uff0c\u8f9b\u9f99\u5b50\u957f\u8896\u98de\u626c\uff0c\u5b9b\u5982\u534a\u7a7a\u4f38\u51fa\u6765\u7684\u602a\u624b\uff0c\u76f4\u6251 \u4ed6\u7684\u9762\u95e8\uff0c\u8098\u53c8\u649e\u4ed6\u80f8\u819b\uff0c\u811a\u5c16\u53c8\u8e22\u4ed6\u819d\u76d6\u3002\u8fd9\u4e00\u602a\u62db\uff0c\u540c\u65f6\u8fde\u653b\u5bf9\u65b9\u4e0a\u4e2d\u4e0b\u4e09\u5904\u65b9\u4f4d\uff0c\u5bf9 \u65b9\u9664\u4e86\u4f7f\u201c\u71d5\u9752\u5341\u516b\u7ffb\u201d\u7684\u201c\u6eda\u5730\u5802\u201d\u529f\u592b\u5916\uff0c\u5b9e\u5728\u65e0\u53ef\u9003\u907f\u3002\u5c1a\u4e91\u4ead\u65e0\u6687\u601d\u7d22\uff0c\u6eda\u5730\u4e00 \u7ffb\uff0c\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u53c8\u7ffb\u8d77\u6765\uff0c\u53ea\u542c\u5f97\u90a3\u9634\u607b\u607b\u7684\u602a\u58f0\uff0c\u53c8\u5728\u8033\u8fb9\u54cd\u9053\uff1a\u201c\u7b2c\u4e8c\u62db\u8981\u6253\u5f97\u4f60 \u56e2\u56e2\u4e71\u8f6c\uff01\u201d \u3000\u3000\u5c1a\u4e91\u4ead\u5c1a\u672a\u5b9a\u795e\uff0c\u5ffd\u89c1\u8f9b\u9f99\u5b50\u5de6\u624b\u63e1\u62f3\uff0c\u53f3\u624b\u4f38\u6307\uff0c\u5de6\u8db3\u8db3\u5c16\u5fae\u8d77\uff0c\u4ee5\u91d1\u9e21\u72ec\u7acb\u4e4b\u52bf\uff0c \u7acb\u5728\u81ea\u5df1\u7684\u4fa7\u9762\uff0c\u62f3\u5bf9\u80f8\u819b\uff0c\u6307\u5411\u80c1\u4e0b\uff0c\u8db3\u5c16\u53c8\u6210\u201c\u5341\u5b57\u6446\u83b2\u201d\u4e4b\u52bf\uff0c\u53ef\u4ee5\u8e22\u6863\u6311\u8179\uff0c\u53ea\u8981 \u4e00\u52a8\uff0c\u654c\u7acb\u53ef\u5236\u81ea\u5df1\u6b7b\u547d\uff0c\u53ea\u597d\u51dd\u7acb\u4e0d\u52a8\uff0c\u5904\u5904\u65e0\u5907\u800c\u5904\u5904\u6709\u5907\uff0c\u4ee5\u4e0a\u4e58\u6b66\u529f\u62a4\u7740\u5168\u8eab\u3002\u8f9b \u9f99\u5b50\u5ffd\u7136\u51b7\u7b11\u4e00\u58f0\uff0c\u80f8\u819b\u4e00\u633a\uff0c\u4f5c\u52bf\u6b32\u6251\uff0c\u5c1a\u4e91\u4ead\u53ea\u9053\u4ed6\u8981\u53d1\u52a8\u653b\u52bf\uff0c\u6025\u5fd9\u8db3\u5c16\u4e00\u65cb\uff0c\u56e2\u56e2 \u4e71\u8f6c\uff0c\u4ee5\u516b\u5c01\u6e38\u8eab\u638c\u6cd5\uff0c\u5e94\u4ed8\u654c\u4eba\u7684\u5168\u9762\u653b\u52bf\u3002\u9664\u4e86\u8fd9\u4e00\u6cd5\u5b50\uff0c\u5b9e\u5728\u4e5f\u65e0\u6cd5\u62b5\u5fa1\u3002\u54ea\u6599\u8f9b\u9f99 \u5b50\u53ea\u662f\u4f5c\u52bf\uff0c\u5e76\u672a\u524d\u6251\uff0c\u5f85\u4ed6\u65cb\u8f6c\u4e4b\u52bf\u7a0d\u7f13\uff0c\u731b\u7136\u559d\u9053\uff1a\u201c\u7b2c\u4e09\u62db\u8981\u4f60\u6454\u51fa\u95e8\u53bb\uff01\u201d\u53cc\u638c\u4e00 \u64a4\uff0c\u8fc5\u5982\u5954\u96f7\uff0c\u638c\u98ce\u4eba\u5f71\u4e2d\uff0c\u5c1a\u4e91\u4ead\u5927\u53eb\u4e00\u58f0\uff0c\u5e73\u5730\u98de\u51fa\u6570\u4e08\uff0c\u4f46\u4ed6\u4e5f\u4e34\u5371\u663e\u4e86\u4e00\u624b\u7edd\u62db\uff0c \u6697\u8fd0\u5185\u529b\u5c06\u94c1\u6247\u9707\u88c2\uff0c\u6570\u5341\u679d\u6bd2\u7bad\uff0c\u9f50\u5411\u8f9b\u9f99\u5b50\u98de\u6765\uff0c\u8f9b\u9f99\u5b50\u731d\u4e0d\u53ca\u9632\uff0c\u4e0d\u7531\u5f97\u4e5f\u662f\u4e00\u60ca\uff0c \u6025\u5fd9\u4f7f\u4e2a\u201c\u4e00\u9e64\u51b2\u5929\u201d\u4e4b\u52bf\uff0c\u98de\u8eab\u6500\u4e0a\u5c4b\u6881\uff0c\u5c1a\u4e91\u4ead\u593a\u95e8\u72c2\u5954\uff0c\u5085\u9752\u4e3b\u98de\u7ea2\u5dfe\u7d27\u7d27\u8ddf\u8e2a\u8ffd \u51fa\u3002 \u3000\u3000\u518d\u8bf4\u51cc\u672a\u98ce\u548c\u5929\u8499\u6076\u6597\uff0c\u529f\u529b\u6089\u654c\uff0c\u65d7\u9f13\u76f8\u5f53\uff0c\u6597\u4e86\u8bb8\u4e45\uff0c\u5140\u662f\u672a\u5206\u80dc\u8d1f\u3002\u51cc\u672a\u98ce\u8eab\u6cd5 \u4e00\u53d8\uff0c\u628a\u534a\u622a\u618e\u888d\u7d27\u7d27\u6536\u675f\uff0c\u821e\u6210\u4e00\u6839\u6746\u68d2\uff0c\u5c06\u6700\u8fd1\u8fd9\u6b21\u91cd\u4e0a\u5929\u5c71\u6240\u5b66\u5f97\u7684\u5251\u6cd5\uff0c\u65bd\u5c55\u51fa \u6765\uff0c\u5c45\u7136\u662f\u5288\u523a\u64a9\u62b9\uff0c\u6089\u4f9d\u5200\u5251\u8def\u6570\uff0c\u90a3\u50e7\u888d\u675f\u6210\u7684\u6746\u68d2\uff0c\u62ff\u5728\u4ed6\u7684\u624b\u91cc\uff0c\u771f\u5982\u62ff\u7740\u4e00\u67c4\u5b9d \u5251\u3002\u6218\u5230\u5206\u9645\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u88c2\u5e1b\uff0c\u51cc\u672a\u98ce\u7684\u534a\u622a\u50e7\u888d\uff0c\u5c06\u5929\u8499\u624b\u4e2d\u7684\u534a\u622a\u50e7\u888d\u5377\u7740\uff0c\u7528\u529b\u4e00 \u7ede\uff0c\u5929\u8499\u7684\u50e7\u888d\uff0c\u53d8\u6210\u7247\u7247\u788e\u5e03\uff0c\u51cc\u672a\u98ce\u4e00\u638c\u5288\u53bb\uff0c\u5929\u8499\u60e8\u53eb\u4e00\u58f0\uff0c\u56de\u8eab\u4fbf\u9003\uff0c\u51cc\u672a\u98ce\u6b63\u5f85 \u8ffd\u51fb\uff0c\u5ffd\u89c9\u80cc\u540e\u98ce\u58f0\u98d2\u7136\uff0c\u65e0\u6687\u8ffd\u654c\uff0c\u53cd\u624b\u4fbf\u662f\u4e00\u638c\uff0c\u80cc\u540e\u7684\u4eba\u201c\u54ce\u54df\u201d\u53eb\u4e86\u4e00\u58f0\uff0c\u800c\u51cc\u672a \u98ce\u4e5f\u89c9\u6765\u4eba\u529f\u529b\u751a\u4e3a\u7eaf\u539a\u3002 \u3000\u3000\u8fd9\u4eba\u6b63\u662f\u820d\u547d\u6c42\u751f\u7684\u5c1a\u4e91\u4ead\uff0c\u4ed6\u53d7\u4e86\u51cc\u672a\u98ce\u4e00\u638c\uff0c\u5168\u8eab\u9ebb\u8f6f\uff0c\u9003\u51fa\u51e0\u6b65\uff0c\u5085\u9752\u4e3b\u5df2\u7136\u8d76 \u5230\uff0c\u9a88\u6307\u4e00\u6233\uff0c\u5c06\u4ed6\u70b9\u5012\u5730\u4e0a\uff0c\u800c\u5929\u8499\u7985\u5e08\u5df2\u5e26\u4e86\u4e24\u4e2a\u5f92\u5f1f\u98de\u9003\u4e86\uff01 \u3000\u3000\u51cc\u672a\u98ce\u5411\u5085\u9752\u4e3b\u9053\u58f0\u201c\u60ed\u6127\u201d\uff0c\u4ed6\u56e0\u6076\u6218\u5929\u8499\uff0c\u7adf\u653e\u4e86\u5c1a\u4e91\u4ead\u6df7\u5165\u5e84\u5185\uff0c\u751a\u89c9\u5c34\u5c2c\u3002\u5085 \u9752\u4e3b\u7b11\u9053\uff1a\u201c\u4e24\u4e2a\u8d3c\u4eba\u90fd\u64d2\u7740\u4e86\uff0c\u51cc\u5927\u4fa0\u4f55\u5fc5\u803f\u803f\u4e8e\u6000\u3002\u201d\u8bf4\u7f62\u628a\u5c1a\u4e91\u4ead\u62bc\u56de\u5e84\u5185\u3002 \u3000\u3000\u77f3\u5927\u5a18\u7b49\u5750\u5728\u5802\u4e2d\uff0c\u6b63\u5728\u5ba1\u95ee\u4eba\u5996\u90dd\u98de\u51e4\uff0c\u5085\u9752\u4e3b\u53cc\u638c\u6309\u5728\u5c1a\u4e91\u4ead\u80a9\u4e0a\uff0c\u5389\u58f0\u559d\u9053\uff1a \u201c\u4f60\u5230\u897f\u5317\u60f3\u5e72\u4e9b\u4ec0\u4e48\uff1f\u4e3a\u4f55\u6df7\u5165\u6b66\u5bb6\u5e84\uff1f\u4ece\u5b9e\u62db\u6765\uff0c\u5426\u5219\u6211\u53cc\u638c\u7528\u529b\uff0c\u628a\u4f60\u7684\u7435\u7436\u9aa8\u634f \u788e\uff0c\u518d\u628a\u4f60\u7684\u6b66\u529f\u5e9f\u4e86\uff01\u201d \u3000\u3000\u5c1a\u4e91\u4ead\u8ba4\u5f97\u5085\u9752\u4e3b\u662f\u65e0\u6781\u5251\u7684\u5927\u5e08\uff0c\u53eb\u9053\uff1a\u201c\u5085\u9752\u4e3b\uff0c\u4f60\u4e0d\u5fc5\u8feb\u6211\uff01\u201d\u53c8\u770b\u4e86\u8eab\u53d7\u4e94\u82b1 \u5927\u7ed1\u7684\u90dd\u98de\u51e4\u4e00\u773c\uff0c\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u603b\u662f\u8fd9\u4e2a\u5b7d\u969c\u5bb3\u6211\uff01\u201d\u7528\u529b\u4e00\u56bc\u820c\u5934\uff0c\u72c2\u53eb\u51e0\u58f0\uff0c\u55b7\u51fa \u4e00\u53e3\u9c9c\u8840\uff0c\u5728\u5730\u4e0a\u7ffb\u817e\u4e00\u9635\uff0c\u7adf\u81ea\u6b7b\u4e86\uff01 \u3000\u3000\u5085\u9752\u4e3b\u5fae\u5fae\u53f9\u606f\uff0c\u6025\u5fd9\u4f38\u624b\u4e00\u634f\u90dd\u98de\u51e4\u7684\u4e0b\u5df4\uff0c\u90dd\u98de\u51e4\u54c7\u54c7\u5927\u53eb\uff0c\u7259\u9f7f\u5168\u7ed9\u634f\u788e\uff0c\u548c\u8840 \u5410\u51fa\uff0c\u5085\u9752\u4e3b\u4f7f\u4e86\u8fd9\u624b\u8fa3\u5211\uff0c\u4e3a\u7684\u662f\u9632\u6b62\u90dd\u98de\u51e4\u4e5f\u5b66\u5c1a\u4e91\u4ead\u7684\u6837\u5b50\u81ea\u6740\u3002 \u3000\u3000\u90dd\u98de\u51e4\u75db\u6781\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u628a\u6211\u6740\u4e86\u5427\uff01\u201d\u5085\u9752\u4e3b\u5728\u4ed6\u9888\u9879\u4e00\u62cd\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u8bf4\uff1f\u201d \u90dd\u98de\u51e4\u60e8\u53eb\u4e00\u58f0\uff0c\u8bed\u97f3\u542b\u7cca\uff0c\u53ef\u662f\u8fd8\u5206\u8fa8\u5f97\u51fa\u4ed6\u8bf4\u4ec0\u4e48\uff0c\u4ed6\u8bf4\uff1a\u201c\u6211\u7ed9\u77f3\u632f\u98de\u548c\u5b5f\u6b66\u5a01\u903c\u5230 \u585e\u5916\uff0c\u662f\u5929\u8499\u7985\u5e08\u53eb\u6211\u4eec\u6765\u7684\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u662f\u5929\u8499\u7985\u5e08\u53eb\u4f60\u6765\u7684\uff1f\u53eb\u4f60\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u90dd \u98de\u51e4\u770b\u4e86\u6b66\u743c\u7476\u4e00\u773c\uff0c\u5782\u9996\u4e0d\u8bed\uff0c\u6b66\u743c\u7476\u7c89\u9762\u901a\u7ea2\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u62cd\u7684\u4e00\u638c\uff0c\u628a\u90dd\u98de\u51e4\u7684\u5927\u7075 \u76d6\u9707\u5f97\u7c89\u788e\u3002 \u3000\u3000\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u6b66\u59d1\u5a18\uff0c\u4e5f\u96be\u602a\u4f60\u53d1\u813e\u6c14\uff0c\u53ea\u662f\u592a\u4fbf\u5b9c\u4e86\u8fd9\u53ae\u3002\u201d\u5728\u5c38\u8eab\u4e0a\u4e00\u641c\uff0c\u679c\u7136 \u641c\u51fa\u5929\u8499\u7ed9\u4ed6\u7684\u4e00\u5c01\u4fe1\uff0c\u53eb\u4ed6\u5f97\u624b\u4e4b\u540e\uff0c\u6301\u4fe1\u53bb\u89c1\u695a\u662d\u5357\uff0c\u539f\u6765\u695a\u662d\u5357\u4e5f\u77e5\u9053\u6b66\u5143\u82f1\u5728\u8349\u539f \u4e0a\u5efa\u8d77\u6751\u5e84\uff0c\u53ea\u4ee5\u201c\u7663\u75a5\u4e4b\u60a3\u201d\uff0c\u4e0d\u60f3\u4eb2\u81ea\u6599\u7406\uff0c\u6240\u4ee5\u53eb\u5929\u8499\u7985\u5e08\u987a\u9053\u53bb\u6bc1\u706d\u6b66\u5bb6\u5e84\uff0c\u800c\u5929 \u8499\u7985\u5e08\u53c8\u548c\u9003\u5230\u585e\u5916\u7684\u5c1a\u4e91\u4ead\u52fe\u7ed3\u4e0a\u4e86\uff0c\u8981\u4ed6\u4eec\u5148\u63a2\u865a\u5b9e\u3002\u90dd\u98de\u51e4\u8272\u80c6\u5305\u5929\uff0c\u7b2c\u4e00\u5929\u5728\u6b66\u5bb6 \u5e84\u5916\u63a2\u89c6\uff0c\u89c1\u7740\u6b66\u743c\u7476\uff0c\u4e0d\u7b49\u5929\u8499\u7985\u5e08\u5230\u6765\uff0c\u5c31\u548c\u5c1a\u4e91\u4ead\u6251\u5165\u5e84\u5185\u91c7\u82b1\uff0c\u51e0\u4e4e\u7ed9\u6b66\u5143\u82f1\u780d \u6b7b\uff0c\u4ed7\u7740\u5c1a\u4e91\u4ead\u7684\u6bd2\u7bad\uff0c\u624d\u80fd\u9003\u8131\uff0c\u7b2c\u4e8c\u6b21\u548c\u5929\u8499\u4f1a\u5408\u4e4b\u540e\uff0c\u518d\u5206\u6279\u6765\u72af\uff0c\u4e0d\u6599\u53c8\u9047\u5230\u8bb8\u591a \u9ad8\u624b\uff0c\u7ec8\u4e8e\u4e27\u547d\u3002 \u3000\u3000\u51cc\u672a\u98ce\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u695a\u662d\u5357\u56db\u5904\u9080\u4eba\uff0c\u770b\u6765\u6e05\u5175\u5927\u4e3e\u5165\u4fb5\u4e4b\u671f\u4e0d\u8fdc\uff0c\u6211\u4eec\u987b\u5f97\u597d \u597d\u51c6\u5907\u3002\u201d\u98de\u7ea2\u4e2a\u6602\u5934\u7b11\u9053\uff1a\u201c\u6211\u660e\u5929\u5c31\u9063\u4eba\u9080\u7ea6\u5357\u7586\u5404\u65cf\u914b\u957f\uff0c\u542c\u674e\u516c\u5b50\u7684\u8c03\u9063\u3002\u201d\u674e\u601d \u6c38\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5973\u82f1\u96c4\u4e1c\u5c71\u590d\u51fa\uff0c\u90a3\u597d\u6781\u4e86\uff0c\u6211\u613f\u8346\u4ea8\u529b\uff0c\u4ee5\u4f5c\u524d\u9a71\u3002\u201d\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u4f60 \u4eec\u4e0d\u5fc5\u4e92\u76f8\u63a8\u8ba9\u4e86\u3002\u5927\u5bb6\u7d2f\u4e86\u8fd9\u4e48\u591a\u5929\uff0c\u8fd8\u662f\u660e\u65e5\u518d\u8bf4\u5427\u3002\u201d\u8f9b\u9f99\u5b50\u7ffb\u7740\u602a\u773c\u9053\uff1a\u201c\u4f60\u4eec\u90fd \u662f\u5fd9\u4eba\uff0c\u5fd9\u7740\u4ec0\u4e48\u52b3\u4ec0\u5b50\u7684\u56fd\u5bb6\u5927\u4e8b\uff0c\u6211\u5374\u662f\u95f2\u4e91\u91ce\u9e64\uff0c\u5bf9\u4f60\u4eec\u7684\u4e8b\u60c5\u6beb\u65e0\u5174\u8da3\u3002\u6211\u8981\u56de\u5929 \u5c71\u91c7\u91d1\u70bc\u5251\uff0c\u6055\u4e0d\u5e78\u966a\u4e86\u3002\u201d\u51cc\u672a\u98ce\u5c06\u4ed6\u4e00\u628a\u62c9\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u8f9b\u5927\u54e5\uff0c\u4f60\u8981\u56de\u53bb\uff0c\u4e5f\u4e0d\u5fd9\u5728 \u4eca\u5bb5\uff0c\u660e\u65e5\u5144\u5f1f\u8fd8\u6709\u8981\u4e8b\u5949\u544a\uff01\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u5ff5\u5728\u4f60\u66fe\u6551\u8fc7\u6211\u7684\u547d\uff0c\u6211\u4f9d\u4f60\u7684\u8bdd\uff0c\u8981\u6211\u591a\u7ba1 \u5c18\u4e16\u4fd7\u4e8b\uff0c\u90a3\u6211\u53ef\u4e0d\u5e72\u3002\u201d \u3000\u3000\u4e00\u5bbf\u6613\u8fc7\u3002\u7b2c\u4e8c\u65e5\u6668\u66dd\u7a00\u5fae\uff0c\u6613\u5170\u73e0\u5c31\u5728\u6751\u5e84\u5916\u7684\u8349\u5730\u5f98\u5f8a\u3002\u5979\u4e0b\u5c71\u4e4b\u540e\uff0c\u5185\u5fc3\u5145\u6ee1\u6fc0 \u60c5\uff0c\u56de\u7586\u7684\u5927\u8349\u539f\u662f\u5979\u7236\u4eb2\u5f53\u5e74\u9a70\u9a8b\u4e4b\u5730\uff0c\u5979\u7236\u4eb2\u7684\u4e00\u751f\u5c31\u662f\u5728\u8349\u539f\u4e0a\u5ea6\u8fc7\u7684\uff0c\u56e0\u4e4b\u5979\u5bf9\u56de \u7586\u7684\u5927\u8349\u539f\u4e5f\u6709\u7740\u8bf4\u4e0d\u51fa\u7684\u4e00\u79cd\u6df1\u539a\u611f\u60c5\uff0c\u5c31\u597d\u50cf\u5bf9\u5979\u7684\u7236\u4eb2\u4e00\u6837\u3002\u5979\u4e00\u65e9\u8d77\u6765\uff0c\u5c31\u662f\u60f3\u7b49 \u5f85\u51cc\u672a\u98ce\uff0c\u5411\u4ed6\u503e\u8bc9\u5979\u5bf9\u7236\u4eb2\u7684\u6000\u5ff5\uff0c\u52a0\u5bf9\u8349\u539f\u7684\u611f\u60c5\u3002 \u3000\u3000\u6613\u5170\u73e0\u6b63\u5728\u51dd\u601d\uff0c\u5ffd\u7136\u53d1\u73b0\u8349\u539f\u4e0a\u8fd8\u6709\u53e6\u5916\u7684\u4e00\u4e2a\u4eba\u5728\u72ec\u81ea\u5f98\u5f8a\uff0c\u5979\u8dd1\u4e86\u8fc7\u53bb\uff0c\u90a3\u4e2a\u4eba \u62ac\u5934\u53eb\u9053\uff1a\u201c\u5170\u73e0\uff0c\u4f60\u8fd9\u6837\u65e9\uff01\u201d\u8fd9\u4eba\u4e43\u662f\u5f20\u534e\u662d\uff0c\u98de\u5954\u7740\u8fce\u9762\u800c\u6765\uff0c\u5230\u4e86\u6613\u5170\u73e0\u8ddf\u524d\uff0c\u5ffd \u7136\u505c\u4e86\u4e0b\u6765\uff0c\u5446\u5446\u6ce8\u89c6\uff0c\u6613\u5170\u73e0\u5947\u9053\uff1a\u201c\u4f60\u50bb\u4e86\u4e48\uff1f\u770b\u4e9b\u4ec0\u4e48\uff1f\u201d\u5f20\u534e\u662d\u53eb\u9053\uff1a\u201c\u5170\u73e0\uff0c\u4f60 \u7684\u5934\u53d1\uff0c\u4f60\u7684\u5934\u53d1\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u624b\u629a\u9752\u4e1d\uff0c\u6115\u7136\u95ee\u9053\uff1a\u201c\u6211\u7684\u5934\u53d1\u600e\u6837\u4e86\uff1f\u201d\u5f20\u534e\u662d\u559c\u5f97\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4e00\u6839\u767d\u5934 \u53d1\u90fd\u6ca1\u6709\u4e86\uff01\u201d\u62c9\u7740\u6613\u5170\u73e0\u5230\u6cc9\u6c34\u8fb9\u4e00\u7167\uff0c\u53ea\u89c1\u6ee1\u5934\u4e4c\u9ed1\uff0c\u53d1\u5149\u9274\u4eba\uff0c\u6613\u5170\u73e0\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd \u6765\u3002\u5f20\u534e\u662d\u62c9\u7740\u5979\u7684\u624b\u8d5e\u9053\uff1a\u201c\u5170\u59b9\u59b9\uff0c\u4f60\u771f\u7f8e\uff01\u201d\u6613\u5170\u73e0\u5ffd\u60a0\u7136\u53f9\u9053\uff1a\u201c\u7ba1\u5b83\u767d\u53d1\u9ed1\u53d1\u90fd \u4e0e\u6211\u65e0\u5173\uff0c\u767d\u53d1\u4e0d\u8db3\u5fe7\uff0c\u9ed1\u53d1\u4ea6\u4e0d\u8db3\u559c\uff0c\u6211\u662f\u8ddf\u5b9a\u98de\u7ea2\u5dfe\u7684\u4e86\uff01\u201d \u3000\u3000\u5f20\u534e\u662d\u5947\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u66fe\u9003\u51fa\u6df1\u5c71\uff0c\u4e0d\u613f\u53d7\u5979\u62d8\u675f\u7684\u5417\uff1f\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u4f60\u4e00\u70b9\u4e5f\u4e0d\u61c2 \u5f97\u6211\uff0c\u4e5f\u4e0d\u61c2\u5f97\u98de\u7ea2\u5dfe\u3002\u73b0\u5728\u7684\u5979\u5df2\u7ecf\u4e0d\u662f\u4ee5\u524d\u7684\u5979\u4e86\uff0c\u6211\u548c\u5979\u73b0\u5728\u90fd\u4e0d\u662f\u5728\u6df1\u5c71\u4e4b\u4e2d\uff0c\u800c \u662f\u5728\u8349\u539f\u4e4b\u4e0a\u5440\uff01\u6211\u73b0\u5728\u5c0a\u656c\u5979\uff0c\u5c31\u5982\u5c0a\u656c\u6211\u7684\u51cc\u53d4\u53d4\u4e00\u6837\u3002\u201d\u6613\u5170\u73e0\u7ecf\u8fc7\u4e86\u8fd9\u573a\u5927\u53d8\uff0c\u53c8 \u53d7\u4e86\u51cc\u672a\u98ce\u7684\u6fc0\u52b1\u51fa\u5c71\uff0c\u5bf9\u5f20\u534e\u662d\u7684\u7231\u5fc3\u867d\u7136\u6ca1\u6709\u6b7b\u6389\uff0c\u53ef\u662f\u5979\u7684\u7231\u60c5\u5df2\u7ecf\u88ab\u53e6\u5916\u4e00\u79cd\u5f3a\u70c8 \u7684\u611f\u60c5\u76d6\u8fc7\u4e86\uff0c\u8fd9\u611f\u60c5\u5c31\u662f\u5bf9\u4e8e\u8349\u539f\u7684\u611f\u60c5\uff0c\u5979\u8981\u7ee7\u627f\u5979\u7236\u4eb2\u7684\u5fd7\u613f\uff0c\u4e3a\u8349\u539f\u4e0a\u7684\u7267\u6c11\u89e3\u6551 \u82e6\u96be\u3002\u7406\u60f3\u71c3\u70e7\u7740\u5979\u7684\u5fc3\uff0c\u5bf9\u6b7b\u53bb\u7684\u7236\u4eb2\u90a3\u79cd\u6df1\u6c89\u7684\u6000\u5ff5\u5360\u6398\u4e86\u5979\u7684\u5fc3\uff0c\u7231\u60c5\u53cd\u800c\u9000\u5230\u6b21\u8981 \u7684\u4f4d\u7f6e\uff0c\u6b64\u523b\u5979\u8fd8\u6ca1\u6709\u5fc3\u60c5\u8c08\u60c5\u8bf4\u7231\uff0c\u5bf9\u767d\u53d1\u9ed1\u53d1\u7684\u4e8b\u60c5\uff0c\u66f4\u4e0d\u653e\u5728\u5fc3\u4e0a\u4e86\uff01 \u3000\u3000\u5f20\u534e\u662d\u9ed8\u7136\u65e0\u8bed\uff0c\u6162\u6162\u5730\u7406\u89e3\u4e86\u5979\u7684\u5fc3\u60c5\uff0c\u62c9\u7740\u5979\u7684\u624b\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u5170\u59b9\u59b9\uff0c\u6211\u61c2\u5f97 \u7684\uff0c\u6211\u7684\u7236\u4eb2\u7ed9\u6e05\u5175\u6740\u6b7b\u7684\u65f6\u5019\uff0c\u6211\u7684\u5fc3\u4e2d\u4e5f\u662f\u5145\u6ee1\u7740\u590d\u4ec7\u7684\u706b\u7130\uff0c\u4e00\u70b9\u4e5f\u4e0d\u60f3\u5230\u5176\u4ed6\u3002\u4f46 \u662f\uff0c\u6211\u4eec\u6c38\u8fdc\u5728\u4e00\u8d77\uff0c\u4e5f\u5e76\u4e0d\u59a8\u788d\u6211\u4eec\u7684\u4e8b\u4e1a\u5440\uff01\u201d\u6613\u5170\u73e0\u9762\u73b0\u7ea2\u971e\uff0c\u6323\u8131\u4ed6\u7684\u624b\u8bf4\u9053\uff1a \u201c\u522b\u95f9\u4e86\uff0c\u4f60\u770b\u51cc\u53d4\u53d4\u4ed6\u4eec\u6765\u4e86\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u548c\u8f9b\u9f99\u5b50\u5e76\u80a9\u8d70\u5230\u8349\u539f\uff0c\u4e0d\u4e00\u4f1a\u5085\u9752\u4e3b\u77f3\u5929\u6210\u4ed6\u4eec\u4e5f\u6765\u4e86\uff0c\u51cc\u672a\u98ce\u70b9\u70b9\u5934\u9053\uff1a \u201c\u5170\u73e0\uff0c\u4f60\u65e9\uff01\u201d\u770b\u7740\u5f20\u534e\u662d\u7b11\u4e86\u4e00\u7b11\uff0c\u5ffd\u89c1\u5f20\u534e\u7167\u9eef\u7136\u65e0\u8bed\uff0c\u89c9\u5f97\u5f88\u662f\u5947\u602a\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u51cc\u672a\u98ce\uff0c\u4f60\u7ea6\u6211\u51fa\u6765\u6709\u4ec0\u4e48\u4e8b\uff1f\u8bf7\u5feb\u8bf4\u7f62\u3002\u201d\u51cc\u672a\u98ce\u7a81\u7136\u4ece\u8170\u95f4\u89e3\u4e0b\u4e00\u628a \u5b9d\u5251\uff0c\u9012\u8fc7\u53bb\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u628a\u5251\u5982\u4f55\uff1f\u201d\u8f9b\u9f99\u5b50\u7ec6\u7ec6\u8d4f\u73a9\uff0c\u5f39\u5251\u957f\u5578\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u897f\u85cf\u5929 \u9f99\u6d3e\u7684\u9547\u5c71\u5b9d\u5251\u5440\uff0c\u4f60\u5982\u4f55\u5f97\u5230\uff1f\u201d\u51cc\u672a\u98ce\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u77e5\u9053\u8fd9\u628a\u5251\u7684\u6765\u5386\uff0c\u4f60\u559c\u6b22\u8fd9 \u628a\u5251\u5417\uff1f\u201d\u8f9b\u9f99\u5b50\u6de1\u7136\u8bf4\u9053\uff1a\u201c\u82e5\u679c\u5728\u5929\u8499\u8d3c\u79c3\u7684\u624b\u4e2d\uff0c\u4e5f\u8bb8\u6211\u4f1a\u62a2\u4ed6\u7684\u3002\u5728\u4f60\u7684\u624b\u4e2d\uff0c\u6211 \u4e0d\u4f1a\u5f3a\u62a2\u7684\u3002\u201d\u51cc\u672a\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u7136\u559c\u6b22\uff0c\u6211\u5c31\u9001\u7ed9\u4f60\uff01\u201d\u8f9b\u9f99\u5b50\u6115\u7136\u9053\uff1a\u201c\u771f \u7684\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4e00\u628a\u5b9d\u5251\u6709\u4ec0\u4e48\u7a00\u5947\uff0c\u6211\u751f\u5e73\u4ece\u4e0d\u7528\u5b9d\u5251\uff0c\u4e5f\u672a\u5c1d\u53d7\u8fc7\u632b\u8d25\uff01\u201d\u8f9b\u9f99\u5b50 \u602a\u773c\u4e00\u7ffb\uff0c\u5c06\u5b9d\u5251\u6325\u52a8\u51e0\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u54c8\uff0c\u51cc\u672a\u98ce\uff0c\u4f60\u6015\u6211\u4e0d\u53d7\u5b9d\u5251\uff0c\u6545\u610f\u6fc0\u6211\uff0c\u597d\uff0c\u6211\u63a5 \u53d7\u4f60\u7684\u597d\u610f\uff0c\u4f46\u8fd8\u662f\u8981\u548c\u4f60\u6bd4\u5251\uff01\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u597d\u5440\uff01\u54b1\u4eec\u70b9\u5230\u4e3a\u6b62\uff0c\u80dc\u8d25\u4e0d\u8bba\u3002\u201d \u3000\u3000\u6842\u4ef2\u660e\u62ff\u6765\u4e00\u6876\u77f3\u7070\uff0c\u51cc\u672a\u98ce\u53d6\u51fa\u4ed6\u5e73\u5e38\u60ef\u7528\u7684\u9752\u94a2\u5251\uff0c\u5728\u77f3\u7070\u4e2d\u4e00\u63d2\uff0c\u53cd\u8eab\u8dc3\u51fa\uff0c\u8bf4 \u9053\uff1a\u201c\u6765\u5427\uff01\u201d\u6613\u5170\u73e0\u6b66\u743c\u7476\u5341\u5206\u5947\u602a\uff0c\u53ea\u6709\u5085\u9752\u4e3b\u6301\u987b\u5fae\u7b11\u3002 \u3000\u3000\u51cc\u672a\u98ce\u77e5\u9053\u8f9b\u9f99\u5b50\u6b66\u529f\u6781\u9ad8\uff0c\u65b0\u8fd1\u53c8\u5b66\u4e86\u8fbe\u6469\u5251\u6cd5\uff0c\u82e5\u90a3\u6069\u5a01\u5e76\u65bd\uff0c\u4e0d\u80fd\u5c06\u4ed6\u6536\u670d\uff0c\u56e0 \u6b64\u9001\u4ed6\u5b9d\u5251\u4e4b\u540e\uff0c\u4ecd\u8df5\u524d\u8a00\uff0c\u8981\u548c\u4ed6\u6bd4\u5251\u3002\u5085\u9752\u4e3b\u8001\u4e8e\u9605\u5386\uff0c\u81ea\u7136\u731c\u5230\u51cc\u672a\u98ce\u5fc3\u610f\u3002\u6613\u5170\u73e0 \u548c\u6b66\u743c\u7476\u5374\u5728\u6697\u6697\u7740\u6025\uff0c\u5979\u4eec\u89c1\u8bc6\u8fc7\u8f9b\u9f99\u5b50\u7684\u6b66\u529f\uff0c\u4ee5\u5979\u4eec\u4e24\u4eba\u8054\u5251\u5408\u653b\u4e4b\u529b\uff0c\u5140\u81ea\u654c\u4e0d\u8fc7 \u8f9b\u9f99\u5b50\u7684\uff0c\u5982\u4eca\u8f9b\u9f99\u5b50\u5b9d\u5251\u5728\u624b\uff0c\u5982\u864e\u6dfb\u7ffc\uff0c\u53ea\u6015\u51cc\u672a\u98ce\u62b5\u6321\u4e0d\u4e86\uff0c\u4e24\u4eba\u6697\u634f\u4e00\u628a\u6c57\uff0c\u7ad9\u5728 \u6597\u573a\u7684\u5916\u56f4\uff0c\u51c6\u5907\u4e00\u6709\u5371\u9669\u4e4b\u65f6\uff0c\u7acb\u523b\u62a2\u6551\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u6a2a\u5251\u5f53\u80f8\uff0c\u4e0e\u51cc\u672a\u98ce\u76f8\u5bf9\u800c\u7acb\uff0c\u53cc\u76ee\u51dd\u89c6\uff0c\u4e45\u4e45\u4e0d\u52a8\u3002\u4f17\u4eba\u65b9\u89c9\u5947\u602a\uff0c\u5ffd\u7136\u8f9b\u9f99 \u5b50\u5f80\u5730\u4e0a\u4e00\u5750\uff0c\u5251\u5c16\u500f\u5730\u4e0a\u6311\uff0c\u51cc\u672a\u98ce\u6c89\u5251\u4e00\u5f15\uff0c\u8f9b\u9f99\u5b50\u95ea\u7535\u822c\u5730\u5728\u5730\u4e0a\u6253\u4e86\u51e0\u4e2a\u76d8\u65cb\uff0c\u9664 \u4e86\u6709\u9650\u51e0\u4eba\uff0c\u522b\u4eba\u6839\u672c\u4e0d\u77e5\u9053\u4ed6\u4ec0\u4e48\u65f6\u5019\u53c8\u7ad9\u4e86\u8d77\u6765\uff0c\u5085\u9752\u4e3b\u4f38\u51fa\u820c\u5934\u5bf9\u77f3\u5927\u5a18\u9053\uff0c\u8fbe\u6469\u5251 \u6cd5\u771f\u4e2a\u795e\u5999\uff0c\u53ea\u8fd9\u4e00\u4f0f\u4e00\u8d77\u7684\u65f6\u95f4\uff0c\u4ed6\u5df2\u63a5\u8fde\u4f7f\u4e86\u5341\u51e0\u624b\u602a\u62db\uff0c\u82e5\u975e\u51cc\u672a\u98ce\uff0c\u4e5f\u771f\u96be\u62b5\u6321\u5f97 \u4f4f\u3002 \u3000\u3000\u518d\u770b\u6597\u573a\u65f6\uff0c\u5f62\u52bf\u53c8\u53d8\uff0c\u8f9b\u9f99\u5b50\u6d3b\u50cf\u4e00\u4e2a\u9189\u6c49\uff0c\u811a\u6b65\u8e09\u8dc4\uff0c\u65f6\u800c\u7eb5\u9ad8\uff0c\u5b9b\u5982\u9e70\u96bc\u51cc\u7a7a\uff1b \u65f6\u800c\u6251\u4f4e\uff1a\u5b9b\u5982\u8776\u821e\u82b1\u5f71\uff0c\u4e00\u628a\u5b9d\u5251\u4e1c\u6307\u897f\u5212\uff0c\u770b\u6765\u4e0d\u6210\u7ae0\u6cd5\uff0c\u5176\u5b9e\u6bcf\u4e00\u62db\u90fd\u6697\u85cf\u597d\u51e0\u4e2a\u53d8 \u5316\uff0c\u51cc\u672a\u98ce\u65bd\u5c55\u51fa\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u201c\u987b\u5f25\u5251\u6cd5\u201d\uff0c\u653b\u5b88\u517c\u5907\uff0c\u4e00\u67c4\u9752\u94a2\u5251\u98d8\u5ffd\u5982\u98ce\uff0c\u610f\u5728\u5251 \u5148\uff0c\u60a0\u7136\u800c\u6765\uff0c\u5bc2\u7136\u800c\u53bb\uff0c\u4f7f\u5230\u7d27\u5904\uff0c\u771f\u662f\u653b\u5982\u96f7\u9706\u75be\u53d1\uff0c\u5b88\u5982\u6c5f\u6d77\u51dd\u5149\u3002\u8fbe\u6469\u5251\u6cd5\u867d\u7136\u602a \u7edd\uff0c\u5374\u662f\u4f24\u4e0d\u4e86\u51cc\u672a\u98ce\u5206\u6beb\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u6597\u5230\u9163\u5904\uff0c\u5ffd\u7136\u4e00\u58f0\u602a\u53eb\uff0c\u5251\u6cd5\u518d\u53d8\uff0c\u6597\u573a\u4e2d\u56db\u9762\u516b\u65b9\u90fd\u662f\u8f9b\u9f99\u5b50\u7684\u8eab\u5f71\uff0c\u90a3\u67c4 \u5b9d\u5251\u5bd2\u5149\u7535\u5c04\uff0c\u5251\u82b1\u9519\u843d\uff0c\u5c31\u5982\u9ed1\u591c\u7e41\u661f\uff0c\u5343\u70b9\u4e07\u70b9\uff0c\u6d12\u843d\u4e0b\u6765\uff0c\u51cc\u672a\u98ce\u7684\u8eab\u5f62\uff0c\u5df2\u88ab\u5251\u5149 \u88f9\u4f4f\uff0c\u8fde\u5085\u9752\u4e3b\u4e5f\u770b\u5f97\u4e0d\u5927\u6e05\u695a\uff0c\u4e0d\u77e5\u9053\u4ed6\u662f\u5982\u4f55\u9632\u5fa1\u7684\u4e86\uff01 \u3000\u3000\u4e0d\u8bf4\u65c1\u4eba\u66ff\u51cc\u672a\u98ce\u62c5\u5fc3\uff0c\u8f9b\u9f99\u5b50\u5374\u662f\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u51cc\u672a\u98ce\u770b\u6765\u4f3c\u662f\u88ab\u56f0\u7740\uff0c\u5176\u5b9e\u5374\u662f \u7528\u6700\u4e0a\u4e58\u7684\u5251\u6cd5\uff0c\u7740\u7740\u53cd\u51fb\uff01\u8f9b\u9f99\u5b50\u53ea\u89c9\u9762\u524d\u5982\u5e03\u4e86\u4e00\u9762\u94c1\u58c1\u94dc\u5899\u51fa\u4e0d\u8fdb\u53bb\uff0c\u5b9d\u5251\u6307\u5904\uff0c\u90fd \u88ab\u4e00\u80a1\u6781\u5927\u7684\u6f5c\u529b\u6321\u4e86\u56de\u6765\uff0c\u8fd8\u4e0d\u65f6\u8981\u7528\u4e0a\u4e58\u6b66\u529f\uff0c\u89e3\u53bb\u51cc\u672a\u98ce\u9752\u94a2\u5251\u7684\u7c98\u529b\u3002\u4f3c\u8fd9\u6837\u6597\u4e86 \u4e00\u767e\u591a\u62db\uff0c\u628a\u65c1\u4eba\u770b\u5f97\u773c\u82b1\u64a9\u4e71\uff0c\u5ffd\u7136\u51cc\u672a\u51e4\u5728\u5251\u5149\u4e2d\u5982\u661f\u4e38\u8df3\u8dc3\uff0c\u8f9b\u9f99\u5b50\u731b\u7eb5\u8d77\u6765\uff0c\u4e00\u5708 \u94f6\u8679\uff0c\u73af\u8170\u75be\u626b\uff0c\u6613\u5170\u73e0\u6b66\u743c\u7476\u60ca\u53eb\u4e00\u58f0\u53cc\u53cc\u67aa\u51fa\uff0c\u77f3\u5929\u6210\u6bd4\u5979\u4eec\u66f4\u5feb\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u5df2\u62a2\u5728 \u524d\u5934\uff0c\u5927\u53eb\uff1a\u201c\u8f9b\u9f99\u5b50\uff0c\u4f60\u8fd9\u5b7d\u969c\uff0c\u80c6\u6562\u4f24\u5bb3\u51cc\u5927\u4fa0\uff01\u201d\u8bed\u58f0\u672a\u505c\uff0c\u5ffd\u89c1\u51cc\u672a\u98ce\u7b11\u541f\u541f\u5730\u7ad9 \u5728\u9762\u524d\uff0c\u8f9b\u9f99\u5b50\u5374\u5982\u6597\u8d25\u7684\u516c\u9e21\u4e00\u6837\uff0c\u659c\u7acb\u5728\u51cc\u672a\u98ce\u4e09\u4e08\u4e4b\u5916\uff0c\u62b1\u5251\u8bf4\u9053\uff1a\u201c\u51cc\u5927\u4fa0\u771f\u597d\u5251 \u6cd5\uff0c\u6211\u8f93\u4e86\uff01\u201d\u77f3\u5929\u6210\u60ca\u6115\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u4ed4\u7ec6\u770b\u65f6\uff0c\u53ea\u89c1\u8f9b\u9f99\u5b50\u7684\u8863\u670d\u4e0a\uff0c\u6709\u8bb8\u591a\u767d\u70b9\uff0c \u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u8fd9\u4e9b\u767d\u70b9\uff0c\u5168\u662f\u51cc\u672a\u98ce\u7528\u5251\u5c16\u4e0a\u7684\u77f3\u7070\u70b9\u4e0a\u53bb\u7684\uff0c\u82e5\u7136\u51cc\u672a\u98ce\u771f\u4e2a\u628a\u8f9b\u9f99\u5b50 \u5f53\u4e3a\u654c\u4eba\uff0c\u8f9b\u9f99\u5b50\u65e9\u5df2\u4e27\u547d\u5728\u4e09\u5c3a\u9752\u950b\u4e4b\u4e0b\u4e86\u3002 \u3000\u3000\u51cc\u672a\u98ce\u4e5f\u888d\u5251\u5f53\u80f8\uff0c\u7b11\u65f6\u541f\u5730\u8bf4\u9053\uff1a\u201c\u8f9b\u5927\u54e5\u771f\u597d\u5251\u6cd5\uff0c\u6597\u4e86\u4e09\u767e\u591a\u62db\uff0c\u624d\u5076\u7136\u5931\u4e86\u4e00 \u62db\uff0c\u505a\u5144\u5f1f\u7684\u5341\u5206\u4f69\u670d\u3002\u201d\u6613\u5170\u73e0\u7684\u5929\u5c71\u5251\u6cd5\u5df2\u6709\u516b\u6210\u706b\u5019\uff0c\u89c1\u51cc\u672a\u98ce\u53ea\u4e0d\u8fc7\u8d62\u4e86\u4e00\u62db\uff0c\u5728 \u8fd9\u6837\u77ed\u4fc3\u7684\u65f6\u95f4\u5185\uff0c\u5c31\u80fd\u591f\u5728\u8f9b\u9f99\u5b50\u8eab\u4e0a\u7559\u4e0b\u51e0\u5341\u5904\u8bb0\u53f7\uff0c\u4e5f\u662f\u9a87\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u60f3\u4e0d\u5230\u672c \u95e8\u5251\u6cd5\u7684\u795e\u5999\u81f3\u5982\u65af\uff01 \u3000\u3000\u8f9b\u9f99\u5b50\u65e2\u662f\u4f69\u670d\u53c8\u662f\u5c34\u5c2c\uff0c\u6b63\u5728\u4e0b\u4e0d\u4e86\u53f0\uff0c\u77f3\u5929\u6210\u559d\u9053\uff1a\u201c\u5927\u4e08\u592b\u6069\u6028\u5206\u660e\uff0c\u4f60\u6709\u6069\u4e0d \u62a5\uff0c\u6709\u4ec7\u4e0d\u62a5\uff0c\u7b97\u54ea\u4e00\u95e8\u4fa0\u4e49\u9053\uff01\u201d\u8f9b\u9f99\u5b50\u9661\u7136\u8f6c\u8eab\uff0c\u5c06\u5251\u5411\u4e0a\u4e00\u4e3e\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff0c \u6211\u627f\u6559\u4e86\uff01\u51cc\u5927\u4fa0\u6b66\u827a\u65e0\u53cc\uff0c\u6211\u8981\u62a5\u6069\u4e5f\u65e0\u4ece\u62a5\u8d77\uff0c\u6211\u53ea\u6709\u968f\u7740\u51cc\u5927\u4fa0\uff0c\u4f46\u613f\u4ed7\u4ed6\u4e4b\u5185\uff0c\u62a5 \u4e86\u695a\u662d\u5357\u7684\u6697\u7b97\u4e4b\u4ec7\uff0c\u6211\u5c31\u56de\u8f6c\u6df1\u5c71\u3002\u201d\u77f3\u5929\u6210\u4ecd\u662f\u602b\u7136\u4e0d\u60a6\uff0c\u8f93\u6068\u8f9b\u9f99\u5b50\u592a\u8fc7\u7cca\u6d82\uff0c\u6b63\u60f3 \u53d1\u8bdd\uff0c\u5ffd\u7136\u8349\u539f\u4e0a\u6570\u9a91\u98de\u6765\uff0c\u5230\u6b66\u5143\u82f1\u8ddf\u524d\uff0c\u500f\u5730\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u62a5\u9053\uff1a\u201c\u6e05\u519b\u5df2\u5927\u4e3e\u5165\u7586 \u4e86\uff01\u201d \u3000\u3000\u8fd9\u51e0\u4e2a\u4eba\u90fd\u662f\u6b66\u5143\u82f1\u5dee\u5230\u8fb9\u754c\u63a2\u542c\u6d88\u606f\u7684\uff0c\u4ed6\u4eec\u5728\u8fb9\u5883\u7684\u70fd\u706b\u53f0\u4e0a\u9065\u89c1\u6e05\u5175\u5927\u961f\u5f00\u6765\uff0c \u8fde\u5fd9\u98de\u9a91\u56de\u62a5\uff0c\u5085\u9752\u4e3b\u6c89\u541f\u9053\uff1a\u201c\u5927\u519b\u884c\u7a0b\u8fdf\u7f13\uff0c\u6cbf\u9014\u53c8\u5b9a\u6709\u7267\u6c11\u961f\u4f0d\uff0c\u5411\u4ed6\u4eec\u88ad\u51fb\uff0c\u6700\u5c11 \u8fd8\u8981\u5341\u5929\u534a\u6708\uff0c\u4ed6\u4eec\u624d\u80fd\u653b\u5230\u8fd9\u91cc\u3002\u201d\u98de\u7ea2\u4e2a\u9053\uff1a\u201c\u5341\u5929\u4e4b\u5185\uff0c\u6211\u4fdd\u7ba1\u80fd\u628a\u5357\u7586\u5404\u65cf\uff0c\u805a\u96c6 \u8d77\u6765\u3002\u201d\u6b66\u5143\u82f1\u9053\uff1a\u201c\u53ea\u662f\u5b5f\u7984\u90a3\u8fb9\uff0c\u5374\u662f\u5fc3\u8179\u5927\u60a3\uff0c\u5b5f\u7984\u662f\u5580\u8fbe\u5c14\u65cf\u7684\u8001\u914b\u957f\uff0c\u548c\u5357\u7586\u7684 \u54c8\u8428\u514b\u65cf\u90fd\u5b9a\u5c45\u5728\u5580\u6c38\u6c81\u8349\u539f\uff0c\u5728\u90a3\u8349\u539f\u4e0a\u8fd8\u6709\u5341\u591a\u4e2a\u90e8\u843d\uff0c\u800c\u4ee5\u5580\u8fbe\u5c14\u548c\u54c8\u8428\u514b\u4e24\u65cf\u7684\u4eba \u6700\u591a\u3002\u867d\u7136\u5b5f\u7984\u53ea\u5f97\u4e09\u56db\u4e2a\u90e8\u843d\u62e5\u62a4\uff0c\u4f46\u4ed6\u52bf\u529b\u6700\u5927\uff0c\u6e05\u519b\u4e00\u65e6\u8fdb\u6765\uff0c\u4ed6\u4f1a\u88f9\u631f\u5176\u4ed6\u5404\u65cf\uff0c \u670d\u4ece\u4ed6\u7684\u3002\u201d\u51cc\u672a\u98ce\u6168\u7136\u8bf4\u9053\u3002\u201c\u6211\u548c\u54c8\u8428\u514b\u4eba\u6700\u719f\uff0c\u6211\u4eec\u5e08\u5144\u5f1f\u4e24\u4ee3\uff0c\u90fd\u5e2e\u54c8\u8428\u514b\u4eba\u6253\u8fc7 \u4ed7\uff0c\u6211\u613f\u5230\u5580\u5c14\u6c81\u8349\u539f\u8d70\u4e00\u8d9f\u3002\u5148\u548c\u54c8\u8428\u514b\u4eba\u8054\u7edc\uff0c\u7136\u540e\u628a\u5b5f\u7984\u6536\u670d\u8fc7\u6765\u3002\u201d\u4f17\u4eba\u542c\u4e86\uff0c\u90fd \u8bf4\u592a\u8fc7\u5371\u9669\uff0c\u6b66\u5143\u82f1\u9053\uff1a\u201c\u90a3\u8fb9\u662f\u5b5f\u7984\u7684\u52bf\u529b\uff0c\u4f60\u5355\u67aa\u5339\u9a6c\uff0c\u6050\u6015\u4f1a\u53d7\u6697\u7b97\u3002\u201d\u51cc\u672a\u98ce\u7b11 \u9053\uff1a\u201c\u6211\u4e00\u751f\u7ecf\u5386\u8fc7\u65e0\u6570\u5371\u96be\uff0c\u4f55\u60e7\u4e00\u4e2a\u5b5f\u7984\u3002\u4f55\u51b5\u6211\u8fd8\u6709\u54c8\u8428\u514b\u65cf\u7684\u670b\u53cb\u3002\u201d\u8f9b\u9f99\u5b50\u5e94\u58f0 \u8bf4\u9053\uff1a\u201c\u6211\u662f\u54c8\u8428\u514b\u4eba\uff0c\u4e8c\u5341\u591a\u524d\uff0c\u6211\u66fe\u505a\u8fc7\u4e00\u4ef6\u5f88\u5bf9\u4e0d\u8d77\u672c\u65cf\u7684\u4e8b\uff0c\u5f53\u65f6\u4e0d\u77e5\u9053\u9519\uff0c\u73b0\u5728 \u662f\u77e5\u9053\u4e86\u3002\u6211\u613f\u968f\u51cc\u5927\u4fa0\u524d\u5f80\uff0c\u4e00\u6765\u53ef\u62a5\u51cc\u5927\u4fa0\u6069\u5fb7\uff1b\u4e8c\u6765\u4e5f\u53ef\u7a0d\u8d4e\u6106\u3002\u201d\u4f17\u4eba\u89c1\u8f9b\u9f99\u5b50\u613f \u5f80\uff0c\u9f50\u90fd\u5927\u559c\uff0c\u5fc3\u60f3\u4e24\u4e2a\u90fd\u662f\u7edd\u4e16\u6b66\u529f\uff0c\u5e94\u8be5\u4e0d\u81f3\u4e8e\u51fa\u4e8b\uff0c\u4e8b\u60c5\u5c31\u8fd9\u6837\u51b3\u5b9a\u4e86\u3002 \u3000\u3000\u5f53\u665a\uff0c\u51cc\u672a\u98ce\u548c\u5218\u90c1\u82b3\u9759\u9759\u5728\u8349\u539f\u6f2b\u6b65\uff0c\u5218\u90c1\u82b3\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u624d\u4e00\u89c1\u9762\uff0c\u4f60\u53c8\u8d70\u4e86\uff01\u201d \u51cc\u672a\u98ce\u5f3a\u7b11\u9053\uff1a\u201c\u6211\u603b\u4f1a\u56de\u6765\u7684\u3002\u201d\u5218\u90c1\u82b3\u9053\uff1a\u201c\u4f46\u4f60\u5374\u4e00\u76f4\u4e0d\u613f\u8bf4\u771f\u8bdd\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a \u201c\u6211\u7684\u8fc7\u53bb\u5df2\u7ecf\u57cb\u846c\u4e86\uff0c\u4f60\u4e3a\u4f55\u4e00\u5b9a\u8981\u77e5\u9053\u6211\u7684\u8fc7\u53bb\uff1f\u201d\u5218\u90c1\u82b3\u9053\uff1a\u201c\u53ef\u662f\u6211\u5fc3\u5934\u4e0a\u7684\u90a3\u4e2a \u7ae5\u5e74\u670b\u53cb\uff0c\u5374\u8fd8\u6ca1\u6709\u6b7b\u6389\uff01\u51cc\u672a\u98ce\uff0c\u4f60\u771f\u7684\u8fd9\u6837\u6b8b\u9177\uff0c\u4e0d\u613f\u628a\u5f53\u5e74\u771f\u76f8\u544a\u8bc9\u6211\u5417\uff1f\u201d\u8349\u539f\u4e0a \u997f\u72fc\u591c\u66dd\uff0c\u80e1\u7b33\u8fdc\u95fb\uff0c\u51cc\u672a\u98ce\u8f7b\u8f7b\u5730\u63a8\u5f00\u5218\u90c1\u82b3\u7684\u624b\uff0c\u6084\u6084\u5730\u9053\uff1a\u201c\u6211\u518d\u91cd\u590d\u6211\u8fc7\u53bb\u8bf4\u8fc7\u7684 \u4e00\u53e5\u8bdd\uff0c\u5728\u4e34\u6b7b\u4e4b\u524d\uff0c\u6211\u4e00\u5b9a\u4f1a\u628a\u771f\u76f8\u544a\u8bc9\u4f60\u7684\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u5386\u5c3d\u6ca7\u6851\u5fc3\u672a\u6362\uff0c\u7591\u771f\u7591\u5e7b\u8d39\u7591\u731c\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5eff\u4e94\u7ae0\u3000\u7267\u91ce\u98de\u971c\u3000\u78a7\u8840\u91d1\u6208\u5343\u53e4\u6068\u3000\u51b0\u6cb3\u6d17\u5251\u3000\u91d1\u84d1\u94c1\u9a6c\u4e00\u751f\u6101 \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u4e8c\u5341\u4e94\u56de \u7267\u91ce\u98de\u971c\u3000\u78a7\u8840\u91d1\u6208\u5343\u53e4\u6068 \u51b0\u6cb3\u6d17\u5251\u3000\u91d1\u84d1\u94c1\u9a6c\u4e00\u751f\u6101 \u3000\u3000\u6e05\u5175\u5165\u4fb5\u7684\u6d88\u606f\uff0c\u4f3c\u65cb\u98ce\u4e00\u6837\u743c\u8fc7\u8349\u539f\uff0c\u8349\u539f\u4e0a\u7684\u4eba\u4eec\uff0c\u7279\u522b\u662f\u8349\u539f\u4e0a\u7684\u9752\u5e74\u4eec\uff0c\u4e00\u89c1 \u9762\u5c31\u8c08\u8bba\u8fd9\u4e2a\u6d88\u606f\uff0c\u6124\u6012\u7684\u706b\u7130\uff0c\u5728\u4ed6\u4eec\u7684\u5fc3\u5934\u71c3\u8d77\uff0c\u8c01\u60f3\u538b\u7184\u8fd9\u4e2a\u706b\u7130\uff0c\u8c01\u5c31\u5c06\u88ab\u706b\u7130\u70e7 \u6b7b\u3002 \u3000\u3000\u5728\u5580\u5c14\u6c81\u8349\u539f\uff0c\u4eba\u4eec\u4e0d\u6562\u516c\u5f00\u8c08\u8bba\uff0c\u53ef\u662f\u6bcf\u5f53\u8349\u539f\u65e5\u843d\uff0c\u665a\u971e\u4f59\u7f14\uff0c\u7f8a\u7fa4\u4f11\u606f\u4e4b\u540e\uff0c\u9752 \u5e74\u7267\u6c11\u5728\u8349\u539f\u4e0a\u6f2b\u6b65\u95f2\u6e38\uff0c\u4fbf\u65f6\u4e0d\u65f6\u4e09\u4e94\u6210\u7fa4\uff0c\u8d70\u5230\u50fb\u9759\u4e4b\u5904\uff0c\u7a83\u7a83\u79c1\u8bae\u3002\u8fd9\u4e9b\u4eba\u4e4b\u4e2d\uff0c\u7adf \u6709\u7740\u5b5f\u7984\u7684\u5973\u513f\u5b5f\u66fc\u53a2\u4e1d\u548c\u54c8\u8428\u514b\u7684\u9752\u5e74\u914b\u957f\u547c\u514b\u6d4e\u3002 \u3000\u3000\u547c\u514b\u6d4e\u7684\u7236\u4eb2\u662f\u6768\u4e91\u9aa2\u7684\u670b\u53cb\uff0c\u5f53\u547c\u514b\u6d4e\u8fd8\u662f\u5c0f\u5b69\u5b50\u7684\u65f6\u5019\uff0c\u66fe\u7ed9\u695a\u662d\u5357\u6349\u53bb\u4f5c\u4e3a\u4eba \u8d28\uff0c\u540e\u6765\u5168\u9760\u98de\u7ea2\u5dfe\u548c\u6768\u4e91\u9aa2\u5c06\u4ed6\u6551\u56de\uff08\u4e8b\u8be6\u62d9\u8457\u300a\u585e\u5916\u4ea5\u4e8c\u4fa0\u4f20\u300b\uff09\uff0c\u56e0\u6b64\u5728\u5580\u5c14\u6c81\u8349\u539f \u4e0a\u7684\u5404\u65cf\u90e8\u843d\u4e2d\uff0c\u547c\u514b\u6d4e\u662f\u4e3b\u6218\u6700\u529b\u7684\u4eba\u3002\u53ef\u662f\u5b5f\u7984\u7684\u52bf\u529b\u592a\u5927\u4e86\uff0c\u8fd8\u6709\u6e05\u5ef7\u6d3e\u6765\u7684\u6b66\u58eb\u5e2e \u52a9\u4ed6\uff0c\u56e0\u6b64\u547c\u514b\u6d4e\u4e5f\u53ea\u597d\u628a\u590d\u4ec7\u7684\u706b\u7130\u538b\u5728\u5fc3\u4e2d\uff0c\u4e0d\u613f\u5411\u5b5f\u7984\u5f53\u9762\u900f\u9732\u3002 \u3000\u3000\u8fd9\u65e5\u9ec4\u660f\uff0c\u547c\u514b\u6d4e\u548c\u5b5f\u66fc\u4e3d\u4e1d\u5728\u8349\u539f\u4e0a\u7684\u4e00\u6761\u5c0f\u6cb3\u8fb9\u6f2b\u6b65\uff0c\u5b5f\u66fc\u4e3d\u4e1d\u7684\u8138\u7ed9\u665a\u971e\u67d3\u4e0a\u4e00 \u5c42\u7ea2\u6655\uff0c\u4e24\u53ea\u5927\u773c\u775b\u50cf\u5929\u4e0a\u7684\u661f\u661f\u95ea\u52a8\uff0c\u773c\u5149\u4e2d\u6709\u5174\u594b\u4e5f\u6709\u5fe7\u90c1\uff0c\u547c\u514b\u6d4e\u9053\uff1a\u201c\u4f60\u7236\u4eb2\u6628\u665a \u6d3e\u957f\u8001\u6765\u63d0\u4eb2\u5566\uff0c\u4eca\u5929\u665a\u4e0a\uff0c\u4ed6\u5c31\u8981\u5982\u5f00\u5404\u90e8\u843d\u914b\u957f\u7684\u5927\u4f1a\u5566\uff01\u201d\u5b5f\u66fc\u4e3d\u4e1d\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u6211 \u77e5\u9053\u5566\uff01\u8fd9\u4e24\u4ef6\u4e8b\u60c5\u8054\u5728\u4e00\u8d77\uff0c\u4e0d\u662f\u597d\u4e8b\uff01\u201d\u547c\u514b\u6d4e\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u50bb\u5b50\uff0c\u6211\u4e5f\u77e5\u9053\u4ed6\u7684\u7528 \u610f\u3002\u4ed6\u77e5\u9053\u6211\u559c\u6b22\u4f60\uff0c\u4ee5\u524d\u6697\u4e2d\u963b\u6320\uff0c\u73b0\u5728\u5374\u6d3e\u4eba\u63d0\u4eb2\uff0c\u8fd8\u4e0d\u662f\u60f3\u6211\u4eca\u665a\u8d5e\u540c\u4ed6\u7684\u4e3b\u5f20 \u5417\uff1f\u201d\u5b5f\u66fc\u4e3d\u4e1d\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4e00\u70b9\u4e5f\u4e0d\u559c\u6b22\uff0c\u6211\u7684\u7236\u4eb2\u8d8a\u8001\u8d8a\u7cca\u6d82\uff0c\u7adf\u7136\u8981\u505a\u5f15\u72fc\u5165 \u5ba4\u7684\u5927\u7f6a\u4eba\uff0c\u6211\u770b\u4ed6\u5c06\u6765\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff01\u6211\u505a\u5973\u513f\u7684\u4e5f\u4e0d\u77e5\u9053\u600e\u6837\u6551\u4ed6\u3002\u201d\u547c\u514b\u6d4e\u7d27\u7d27\u63e1\u7740 \u5979\u7684\u624b\uff0c\u6b22\u7136\u8bf4\u9053\uff1a\u201c\u5b5f\u66fc\u4e3d\u4e1d\uff0c\u4f60\u771f\u662f\u6211\u7684\u597d\u59b9\u5b50\uff0c\u4eca\u665a\u4f60\u7684\u7236\u4eb2\u4e0d\u4f1a\u6210\u529f\u7684\uff0c\u4ed6\u6709\u6e05\u5ef7 \u7684\u6b66\u58eb\uff0c\u6211\u4eec\u8fd9\u91cc\u4e5f\u6765\u4e86\u4e24\u4e2a\u5f02\u4eba\u3002\u201d\u5b5f\u66fc\u4e3d\u4e1d\u8be7\u7136\u8bf4\u9053\uff1a\u201c\u5f02\u4eba\uff1f\u4e3a\u4ec0\u4e48\u6211\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053 \u662f\u4ec0\u4e48\u5f02\u4eba\uff1f\u6211\u5c31\u6015\u5404\u90e8\u843d\u7684\u914b\u957f\uff0c\u4eca\u665a\u4f1a\u5728\u4ed6\u52bf\u529b\u4e4b\u4e0b\u4f4e\u5934\uff0c\u66f4\u52a0\u91cd\u4e86\u4ed6\u7684\u7f6a\u5b7d\uff01\u201d\u547c\u514b \u6d4e\u9053\uff1a\u201c\u4ec0\u4e48\u5f02\u4eba\uff0c\u4eca\u665a\u4f60\u5c31\u77e5\u9053\u4e86\u3002\u201d\u5b5f\u66fc\u4e3d\u4e1d\u5a07\u55d4\u9053\uff1a\u201c\u8fd9\u6837\u795e\u79d8\uff1f\u8fde\u6211\u4e5f\u4e0d\u544a\u8bc9\u3002\u201d \u547c\u514b\u6d4e\u7b11\u9053\uff1a\u201c\u8ba9\u4f60\u4e5f\u60ca\u8bb6\u4e00\u4e0b\u561b\uff01\u201d\u5b5f\u66fc\u4e3d\u4e1d\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u667a\u73e0\u5728\u63e1\uff0c\u7a33\u64cd\u80dc\u7b97\u4e86\uff01\u201d \u547c\u514b\u6d4e\u9053\uff1a\u201c\u5168\u662f\u90a3\u4e24\u4f4d\u5f02\u4eba\u7ed9\u6211\u51fa\u7684\u4e3b\u610f\u3002\u201d\u5b5f\u66fc\u4e3d\u4e1d\u9053\uff1a\u201c\u90a3\u4e48\u600e\u6837\u5904\u7f6e\u6211\u7684\u7236\u4eb2\uff1f\u201d \u547c\u514b\u6d4e\u9012\u8fc7\u4e00\u5305\u836f\u7c89\uff0c\u5728\u5b5f\u66fc\u4e3d\u4e1d\u8033\u8fb9\u8f7b\u8f7b\u8bf4\u4e86\u51e0\u53e5\uff0c\u5b5f\u66fc\u4e3d\u4e1d\u9053\uff1a\u201c\u4e5f\u53ea\u597d\u8fd9\u6837\u4e86\u3002\u201d \u3000\u3000\u665a\u971e\u6d88\u901d\uff0c\u8349\u539f\u4e0a\u65b0\u6708\u5347\u8d77\uff0c\u5404\u90e8\u843d\u7684\u914b\u957f\u3001\u957f\u8001\u548c\u6709\u5730\u4f4d\u7684\u4eba\u90fd\u805a\u96c6\u5728\u5e10\u6155\u73af\u7ed5\u7684\u4e00 \u7247\u8349\u573a\u4e0a\uff0c\u5b5f\u7984\u5e26\u7740\u7532\u5175\uff0c\u8eab\u65c1\u8fd8\u6709\u4e24\u4e2a\u8001\u8005\u548c\u56db\u540d\u6e05\u5ef7\u6b66\u58eb\uff0c\u5927\u8349\u539f\u4e0a\u9e26\u96c0\u65e0\u58f0\uff0c\u76c2\u7984\u7765 \u7768\u4f5c\u6001\uff0c\u73af\u987e\u5168\u573a\uff0c\u5341\u5206\u5f97\u610f\uff01\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u671d\u5ef7\u5927\u519b\uff0c\u5df2\u7834\u5173\u76f4\u5165\uff0c\u6240\u81f3\u4e4b\u5904\uff0c\u5982\u6c64\u6cfc \u96ea\uff0c\u4e0d\u65e5\u4fbf\u5c06\u5230\u6b64\uff0c\u8bf8\u541b\u4f5c\u4f55\u6253\u7b97\uff1f\u201d\u5404\u90e8\u843d\u914b\u957f\u4e0d\u53d1\u4e00\u8a00\uff0c\u89c6\u7ebf\u7eb7\u96c6\u5728\u54c8\u8428\u514b\u5e74\u8f7b\u914b\u957f\u547c \u514b\u6d4e\u8eab\u4e0a\u3002\u547c\u514b\u6d4e\u652f\u5934\u5fae\u7b11\uff0c\u6709\u4eba\u77e5\u9053\u5b5f\u7984\u5bf9\u547c\u514b\u6d4e\u63d0\u4eb2\u7684\u6d88\u606f\u7684\uff0c\u66f4\u662f\u731c\u7591\uff0c\u5854\u5c71\u65cf\u7684\u5e74 \u8f7b\u914b\u957f\u5fcd\u4e0d\u4f4f\u8d77\u6765\u9053\uff1a\u201c\u6e05\u5175\u5165\u5173\u540e\u4e09\u5341\u4f59\u5e74\uff0c\u5bf9\u56de\u7586\u4ea6\u66fe\u5c61\u6b21\u7528\u5175\uff0c\u7aef\u8d56\u5404\u65cf\u4e00\u5fc3\uff0c\u77e2\u5fd7 \u62b5\u6297\uff0c\u6e05\u5175\u53ea\u6562\u5360\u4f0a\u7281\u7b49\u51e0\u4e2a\u5927\u57ce\uff0c\u6211\u4eec\u5728\u8349\u539f\u4e0a\u8fd8\u53ef\u7267\u7f8a\u653e\u9a6c\u3002\u5982\u679c\u4e0d\u6218\u800c\u5c48\uff0c\u7518\u53d7\u5974 \u5f79\uff0c\u5bf9\u6211\u4eec\u7684\u7956\u5148\u4e5f\u5bf9\u4e0d\u8d77\uff01\u201d\u5b5f\u7984\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6709\u591a\u5927\u5e74\u7eaa\uff0c\u5984\u6562\u8c08\u6218\uff01\u4e8c\u5341\u591a\u5e74\u524d\uff0c\u8349 \u539f\u4e0a\u7684\u5973\u82f1\u96c4\u98de\u7ea2\u5dfe\u96c6\u5357\u7586\u5404\u65cf\u4e4b\u4f17\uff0c\u8fd8\u654c\u4e0d\u8fc7\u6e05\u519b\uff0c\u5979\u7684\u519b\u961f\u74e6\u89e3,\u5979\u81ea\u5df1\u4fa7\u9003\u5165\u6df1\u5c71\uff0c \u518d\u4e0d\u6562\u51fa\u6765\u73b0\u4e16\uff0c\u4eca\u65e5\u5165\u5173\u7684\u6e05\u5175\uff0c\u5341\u500d\u4e8e\u6614\uff0c\u800c\u6211\u4eec\u7684\u4eba\u624d\uff0c\u8fd8\u6ca1\u6709\u4eba\u6bd4\u5f97\u4e0a\u6614\u65e5\u7684\u98de\u7ea2 \u5dfe\u3002\u8bd5\u95ee\u4ee5\u6b64\u8fb9\u9119\u4e00\u5730,\u5c06\u5be1\u5175\u5fae\uff0c\u5982\u4f55\u53bb\u62b5\u6297\u738b\u5e08\uff01\u201d\u5854\u5c71\u65cf\u7684\u914b\u957f\u70ed\u8840\u6cb8\u817e\uff0c\u5927\u58f0\u8bf4 \u9053\uff1a\u201c\u6211\u4eec\u662f\u4e86\u4e3a\u7389\u788e\uff0c\u4e0d\u4e3a\u74e6\u5168\uff01\u201d\u5b5f\u7984\u563b\u563b\u51b7\u7b11\uff0c\u8eab\u8fb9\u4e24\u4e2a\u6e05\u5ef7\u6b66\u58eb\uff0c\u8d70\u8fc7\u6765\u9053\uff1a\u201c\u8fd9 \u4f4d\u82f1\u96c4\u7740\u5b9e\u4ee4\u4eba\u4f69\u670d\uff0c\u54b1\u4eec\u4ea4\u4ea4\u3002\u201d\u5854\u5c71\u65cf\u7684\u914b\u957f\u7d27\u63e1\u62f3\u5934\uff0c\u51c6\u5907\u53cd\u51fb\u3002\u547c\u514b\u6d4e\u4e00\u7b11\u8d77\u7acb\uff0c \u906e\u5728\u642d\u5c71\u65cf\u914b\u957f\u524d\u9762\uff0c\u4e3e\u676f\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u6765\u8fd9\u91cc\u5546\u8ba8\u5927\u6c41\uff0c\u4e0d\u662f\u6253\u67b6\u6765\u7684\u3002\u597d\u597d\u559d\u9152\uff0c\u518d\u542c \u5b5f\u8001\u914b\u957f\u7684\u9ad8\u89c1\u3002\u201d\u5854\u5c71\u65cf\u7684\u914b\u957f\u77aa\u4e86\u547c\u514b\u6d4e\u4e00\u773c\uff0c\u5b5f\u7984\u7709\u5f00\u773c\u7b11\uff0c\u62db\u56de\u4e24\u4e2a\u6e05\u5bab\u6b66\u58eb\uff0c\u8bf4 \u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u6709\u4ec0\u4e48\u9ad8\u89c1\uff0c\u53e4\u8bed\u8bf4\u5f97\u597d\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u83ab\u975e\u738b\u571f\uff0c\u7387\u4e0a\u4e4b\u6ee8\uff0c\u83ab\u975e\u738b\u81e3\u3002\u6e05\u519b \u5165\u5173\uff0c\u4e2d\u539f\u534e\u590f\u4e4b\u533a\uff0c\u5c1a\u671b\u98ce\u6295\u987a\uff0c\u6211\u4eec\u8fb9\u7586\u50fb\u5730\uff0c\u5c82\u53ef\u4e0e\u4e4b\u6297\u4e89\uff1f\u6211\u4eec\u8fd8\u662f\u732e\u8840\u4e3a\u76df\uff0c\u4fdd \u571f\u5b89\u6c11\uff0c\u7b49\u5019\u8fce\u63a5\u738b\u5e08\u5427\u3002\u518d\u8bf4\u671d\u5ef7\u4e5f\u7279\u522b\u5c0a\u91cd\u6211\u4eec\uff0c\u6d3e\u4e86\u4e24\u4f4d\u540d\u6ee1\u5929\u4e0b\u7684\u4f7f\u8005\uff0c\u6765\u5230\u6211\u4eec \u8fd9\u8352\u91ce\u4e4b\u533a\uff0c\u5404\u4f4d\u8fd8\u6709\u4ec0\u4e48\u8bf4\u7684\uff01\u201d\u8bf4\u7f62\uff0c\u65bd\u4e86\u4e00\u793c\uff0c\u8bf7\u8eab\u65c1\u4e24\u4f4d\u4f7f\u8005\u7ad9\u8d77\uff0c\u4e24\u4f4d\u4f7f\u8005\u90fd\u662f \u987b\u7709\u5982\u96ea\u7684\u8001\u4eba\uff0c\u5b5f\u7984\u606d\u606d\u656c\u656c\u5730\u4ecb\u7ecd\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4fbf\u662f\u957f\u767d\u5c71\u6d3e\u7684\u6559\u7956\uff0c\u540d\u95fb\u5929\u4e0b\u7684\u98ce\u96f7 \u5251\u9f50\u771f\u541b\uff01\u8fd9\u8fb9\u8fd9\u4f4d\u662f\u897f\u85cf\u5929\u8499\u7985\u5e08\u7684\u5e08\u5f1f\u5929\u96c4\u4e0a\u4eba\uff0c\u4e5f\u662f\u585e\u5916\u6570\u4e00\u6570\u4e8c\u7684\u6b66\u6797\u4eba\u7269\uff0c\u5404\u4f4d \u4e00\u5b9a\u77e5\u9053\u4ed6\u7684\u540d\u5b57\uff01\u201d \u3000\u3000\u5929\u96c4\u5fc3\u9ad8\u6c14\u505a\uff0c\u6027\u5b50\u6700\u6025\uff0c\u626b\u4e86\u5404\u914b\u957f\u4e00\u773c\uff0c\u8d70\u81f3\u8349\u573a\u4e2d\u592e\uff0c\u8349\u573a\u4e2d\u592e\u6709\u4e00\u4e2a\u5927\u77f3\u9f13\uff0c \u7528\u7c97\u85e4\u7cfb\u7740\u4e00\u5934\u897f\u85cf\u91ce\u725b\uff0c\u662f\u5b5f\u7984\u51c6\u5907\u62ff\u6765\u732e\u8840\u4e3a\u76df\u7528\u7684\u3002\u5929\u96c4\u53f1\u54a4\u4e00\u58f0\uff0c\u817e\u8d77\u4e00\u811a\uff0c\u77f3\u9f13 \u5411\u5929\u98de\u53bb\uff0c\u5728\u534a\u7a7a\u4e2d\u88c2\u4e3a\u51e0\u5757\uff01\u91ce\u725b\u8131\u4e86\u7f81\u7eca\uff0c\u91ce\u6027\u5927\u53d1\uff0c\u500f\u5730\u5411\u4eba\u591a\u5904\u51b2\u6765\uff0c\u5404\u914b\u957f\u731b\u4e0d \u53ca\u9632\uff0c\u7eb7\u7eb7\u9a87\u53eb\uff0c\u9f50\u771f\u541b\u5fae\u5fae\u4e00\u7b11\uff0c\u4f38\u51fa\u4e24\u53ea\u6307\u5934\uff0c\u5728\u91ce\u725b\u9888\u4e0a\u4e00\u642d\uff0c\u90a3\u91ce\u725b\u75db\u6781\u72c2\u55e5\uff0c\u56db \u819d\u8dea\u4e0b\uff0c\u9f50\u771f\u541b\u9a88\u6307\u4e00\u6233\uff0c\u725b\u8179\u5f53\u5802\u6d1e\u7a7f\uff0c\u9c9c\u8840\u55b7\u51fa\uff0c\u5b5f\u7984\u6301\u5927\u94b5\u88c5\u76db\uff0c\u88c5\u4e86\u6ee1\u6ee1\u4e09\u94b5\uff0c\u8981 \u77e5\u897f\u85cf\u91ce\u725b\uff0c\u76ae\u8d28\u575a\u539a\uff0c\u53ef\u5fa1\u5f13\u7bad\uff0c\u9f50\u771f\u541b\u53ea\u8f7b\u8f7b\u4e00\u63d2\uff0c\u4fbf\u544a\u6d1e\u7a7f\uff0c\u8fd9\u771f\u6bd4\u5200\u5251\u8fd8\u5389\u5bb3\u5341 \u500d\uff01 \u3000\u3000\u5404\u90e8\u843d\u914b\u957f\u51e0\u66fe\u89c1\u8fc7\u5982\u6b64\u795e\u529b\uff0c\u5927\u90fd\u77a0\u76ee\u7ed3\u820c\uff03\u5408\u7984\u5f97\u610f\u6d0b\u6d0b\uff0c\u5c06\u725b\u8840\u548c\u9152\uff0c\u5728\u6bcf\u4e2a\u914b \u957f\u4e4b\u524d\uff0c\u653e\u4e86\u4e00\u76ce\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8bf7\u5c3d\u6b64\u76c5\uff0c\u5171\u56fe\u5927\u4e8b\u3002\u201d\u5404\u914b\u957f\u867d\u662f\u9707\u9a87\uff0c\u5374\u4ecd\u7aef\u5750\u4e0d \u52a8\uff0c\u5b5f\u7984\u5927\u6012\uff0c\u6b63\u60f3\u53d1\u4f5c\uff0c\u5b5f\u66fc\u4e3d\u4e1d\u5ffd\u81ea\u65c1\u63a9\u51fa\uff0c\u7b11\u76c8\u76c8\u5730\u5bf9\u5b5f\u7984\u8bf4\u9053\uff1a\u201c\u7238\u7238\uff0c\u4f60\u597d\u7cca \u6d82\uff0c\u8be5\u662f\u505a\u4e3b\u4eba\u7684\u5148\u559d\u5440\uff01\u4f60\u559d\u4e86\uff0c\u5973\u513f\u518d\u529d\u4f2f\u4f2f\u53d4\u53d4\u4eec\u559d\u3002\u201d \u3000\u3000\u5979\u6367\u8d77\u4e00\u76c5\u8840\u9152\uff0c\u5728\u718a\u718a\u7684\u91ce\u706b\u4e0a\u4e00\u6696\uff0c\u9012\u7ed9\u7236\u4eb2\u3002\u5b5f\u7984\u4e00\u996e\u800c\u5c3d\uff0c\u5c06\u9152\u76c5\u4e00\u6454\uff0c\u54c8\u54c8 \u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e3d\u513f\uff0c\u529d\u9152\uff01\u201d\u5854\u5c71\u65cf\u7684\u914b\u957f\u773c\u55b7\u6012\u706b\uff0c\u77aa\u4e86\u54c8\u8428\u514b\u7684\u5e74\u8f7b\u914b\u957f\u547c\u514b\u6d4e\u4e00 \u773c\uff0c\u5550\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u7231\u7684\u597d\u59d1\u5a18\uff01\u201d \u3000\u3000\u547c\u514b\u6d4e\u4ecd\u662f\u5fae\u7b11\u4e0d\u8bed\uff0c\u5b5f\u7984\u5411\u4ed6\u4e00\u6307\uff0c\u53eb\u9053\uff1a\u201c\u547c\u514b\u6d4e\uff0c\u4f60\u5148\u559d\uff01\u201d\u547c\u514b\u6d4e\u500f\u5730\u8d77\u7acb\uff0c \u8bf4\u9053\uff1a\u201c\u5b5f\u8001\u9149\u957f\uff0c\u6211\u6709\u4e24\u4f4d\u5ba2\u4eba\uff0c\u60f3\u89c1\u8bc6\u6ee1\u6d32\u82f1\u96c4\u7684\u795e\u6280\uff01\u201d\u5929\u96c4\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60 \u4eec\u8fd9\u4e9b\u86ee\u5b50\uff0c\u4e0d\u6328\u4e00\u987f\u597d\u6253\uff0c\u4e5f\u4e0d\u5fc3\u670d\uff01\u201d\u5b5f\u7984\u542b\u55d4\u8bf4\u9053\uff1a\u201c\u547c\u514b\u6d4e\uff0c\u4f60\u8fd8\u662f\u4e0d\u80af\u559d\u9152\uff1f\u201d \u547c\u514b\u6d4e\u7b11\u9053\uff1a\u201c\u559d\u5be1\u9152\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u8fd8\u662f\u770b\u70ed\u95f9\u4e4b\u540e\u518d\u559d\u5427\uff01\u201d\u5929\u96c4\u65e9\u8131\u4e0b\u5927\u7ea2\u8888\u88df\uff0c\u8df3\u51fa \u573a\u5fc3\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u90a3\u5ba2\u4eba\u4f55\u5728\uff1f\u201d \u3000\u3000\u547c\u514b\u6d4e\u5fae\u4e00\u62db\u624b\uff0c\u8eab\u8fb9\u500f\u5730\u7ad9\u8d77\u4e24\u4eba\uff0c\u4e5f\u4e0d\u89c1\u600e\u4f6f\u4f5c\u52bf\uff0c\u5374\u5df2\u5230\u4e86\u573a\u5fc3\u3002\u4e00\u4eba\u89e3\u4e0b\u906e\u9762 \u7684\u201c\u6597\u7bf7\u201d\uff0c\u9762\u4e0a\u6709\u4e00\u9053\u5200\u75d5\uff0c\u5341\u5206\u9192\u76ee\uff0c\u53e6\u4e00\u5219\u662f\u77ee\u5c0f\u6e05\u7626\u7684\u8001\u5934\u513f\uff0c\u6beb\u4e0d\u5f53\u773c\u3002\u4e24\u4eba\u521a \u624d\u9ed8\u9ed8\u5730\u6742\u5728\u4eba\u5806\u4e4b\u4e2d\uff0c\u5b5f\u7984\u53ea\u9053\u4ed6\u4eec\u662f\u547c\u514b\u6d4e\u7684\u4ece\u4eba\uff0c\u6beb\u4e0d\u5728\u610f\uff0c\u8fd9\u4e00\u4eae\u76f8\uff0c\u4ee4\u4ed6\u731b\u5403\u4e00 \u60ca\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u51cc\u672a\u98ce\uff01\u201d\u573a\u4e2d\u6709\u8fc7\u534a\u914b\u957f\u4e5f\u8ba4\u5f97\u51cc\u672a\u98ce\uff0c\u9f50\u90fd\u6b22\u547c\u8d77\u6765\uff0c\u9f50\u771f\u541b\u9762 \u8272\u9752\u767d\uff0c\u53ea\u6709\u5929\u96c4\u8fd8\u672a\u89c1\u8bc6\u8fc7\u51cc\u672a\u98ce\u7684\u672c\u9886\uff0c\u4ecd\u7136\u7765\u7768\u4f5c\u6001\uff0c\u7acb\u638c\u80f8\u524d\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u5c31 \u662f\u4ec0\u4e48\u51cc\u672a\u98ce\u5417\uff1f\u4f60\u60f3\u548c\u6d12\u5bb6\u5355\u6253\u72ec\u6597\uff0c\u8fd8\u662f\u60f3\u4e24\u4eba\u9f50\u4e0a\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e24\u4eba\uff0c\u60f3\u89c1\u8bc6\u4f60\u4eec\u516d\u4e2a\u4eba\u7684\u672c\u9886\uff0c\u770b\u4f60\u4eec\u6709\u591a\u5927\u672c\u4e8b\uff0c\u6562\u5728\u8fd9 \u91cc\u98de\u626c\u62d4\u6248\uff0c\u79f0\u5f3a\u9053\u9738\uff1f\u4f60\u4eec\u516d\u4eba\u4e00\u9f50\u4e0a\uff0c\u6211\u4eec\u5c31\u4e24\u4eba\u63a5\u4f4f\uff0c\u4f60\u82e5\u4e00\u4e2a\u6765\uff0c\u5c31\u968f\u4f60\u5728\u6211\u4eec\u4e24 \u4eba\u4e4b\u4e2d\uff0c\u6311\u4e00\u4e2a\u5bf9\u624b\uff0c\u5582\uff0c\u9f50\u771f\u541b\uff0c\u4f60\u4e5f\u6765\u5440\uff0c\u4f60\u9ad8\u5174\u6311\u54ea\u4e00\u4e2a\uff1f\u201d\u9f50\u771f\u541b\u786c\u7740\u5934\u76ae\u9053\uff1a \u201c\u4f60\u4f55\u5fc5\u5728\u8fd9\u91cc\u591a\u4e8b\uff1f\u522b\u4eba\u6015\u4f60\uff0c\u8001\u592b\u5374\u6015\u4f60\u3002\u201d\u5176\u5b9e\u4ed6\u6b63\u662f\u5bb3\u6015\u5f97\u7d27\u3002\u51cc\u672a\u98ce\u51b7\u4e00\u7b11\u9053\uff1a \u201c\u4e0d\u6015\u5c31\u6765\u5427\uff01\u201d\u9f50\u771f\u541b\u8fdf\u8fdf\u7591\u7591\uff0c\u6b63\u60f3\u8131\u8eab\u4e4b\u8ba1\uff0c\u6709\u4e24\u4e2a\u6e05\u5bab\u65b0\u62db\u7eb3\u6765\u7684\u4e00\u7b49\u536b\u58eb\uff0c\u6765\u81ea \u6c5f\u5357\uff0c\u5c1a\u672a\u77e5\u51cc\u672a\u98ce\u7684\u540d\u5934\uff0c\u6697\u607c\u9f50\u771f\u541b\u90a3\u4e48\u5927\u7684\u5a01\u540d\uff0c\u5728\u51cc\u672a\u98ce\u9762\u524d\uff0c\u5374\u663e\u5f97\u90a3\u4e48\u754f\u7f29\u3002 \u8fd9\u65f6\u9f50\u771f\u541b\u5df2\u662f\u6e05\u5bab\u4f8d\u536b\u7684\u7edf\u9886\uff0c\u5929\u96c4\u4e0a\u4eba\u5c1a\u662f\u65b0\u8fd1\u62c9\u6765\u7684\u5ba2\u537f\uff0c\u4e24\u4e2a\u536b\u58eb\u5fc3\u60f3\uff0c\u82e5\u4e0d\u628a\u8fd9 \u4ec0\u4e48\u51cc\u672a\u98ce\u5f53\u573a\u964d\u4f0f\uff0c\u4e0d\u4f46\u6298\u5c3d\u5927\u5185\u536b\u58eb\u7684\u5a01\u98ce\uff0c\u53ea\u6050\u8fde\u5929\u96c4\u90fd\u77a7\u4ed6\u4eec\u4e0d\u8d77\u3002\u4e24\u4eba\u4e00\u6837\u5fc3 \u601d\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u98de\u7eb5\u51fa\u573a\uff0c\u51b2\u8fc7\u6765\u9053\uff1a\u201c\u597d\uff0c\u8ba9\u6211\u4eec\u5144\u5f1f\u5148\u63a5\u8fd9\u573a\uff01\u201d\u51dd\u672a\u98ce\u51b7\u51b7\u95ee\u9053\uff1a \u201c\u4f60\u4eec\u60f3\u9009\u54ea\u4e00\u4e2a\u5bf9\u624b\uff1f\u201d \u3000\u3000\u4e24\u540d\u6e05\u5bab\u536b\u58eb\uff0c\u51b2\u5411\u51cc\u672a\u98ce\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u9f50\u58f0\u559d\u9053\uff1a\u201c\u627e\u4f60\uff01\u201d\u957f\u7b11\u58f0\u4e2d\uff0c\u51cc\u672a\u98ce\u8eab\u5f62 \u9aa4\u8d77\uff0c\u53cc\u81c2\u7b95\u5f20\uff0c\u5411\u5916\u4e00\u5c55\uff0c\u4e00\u62db\u201c\u94c1\u9501\u6a2a\u821f\u201d\uff0c\u7b2c\u4e00\u540d\u654c\u624b\u8fde\u8eab\u5f62\u5c1a\u672a\u770b\u6e05\uff0c\u624b\u8155\u5df2\u7ed9\u62ff \u4f4f\uff0c\u51cc\u672a\u98ce\u6b65\u6cd5\u8f7b\u7075\uff0c\u500f\u7136\u8f6c\u8eab\uff0c\u5c06\u64d2\u4f4f\u7684\u536b\u58eb\u731b\u7136\u6454\u51fa\uff0c\u7b2c\u4e8c\u540d\u654c\u624b\u521a\u521a\u653b\u5230\uff0c\u554a\u5440\u4e00 \u58f0\uff0c\u95ea\u907f\u5f00\u65f6\uff0c\u51cc\u672a\u98ce\u65e9\u5df2\u548c\u8eab\u6251\u4e0a\uff0c\u90a3\u540d\u536b\u4e0a\u7a81\u89c9\u52b2\u98ce\u8d2f\u80f8\uff0c\u5982\u4e2d\u5de8\u65a7\uff0c\u7ed9\u51cc\u672a\u98ce\u7528\u91d1\u94a2 \u5927\u529b\u624b\u6cd5\u6298\u788e\u80f8\u9aa8\uff0c\u767b\u65f6\u60e8\u53eb\u4e00\u58f0\uff0c\u8840\u67d3\u8349\u573a\uff01 \u3000\u3000\u51cc\u672a\u98ce\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u5929\u5c71\u638c\u6cd5\uff0c\u4e3e\u624b\u6295\u8db3\u4e4b\u95f4\uff0c\u8fde\u6bd9\u4e24\u540d\u5927\u5185\u536b\u58eb\uff0c\u5728\u573a\u4eba\u7b49\uff0c\u5168 \u90fd\u5446\u4e86\u3002\u5929\u96c4\u4e0a\u4eba\u8fde\u9000\u51e0\u6b65\uff0c\u51cc\u672a\u98ce\u53c8\u9661\u7136\u559d\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u4f60\u8981\u548c\u8c01\u5bf9\u624b\uff1f\u201d \u3000\u3000\u5929\u96c4\u60ca\u9b42\u7a0d\u5b9a\uff0c\u5f3a\u6444\u5fc3\u795e\uff0c\u5fc3\u60f3\uff1a\u51cc\u672a\u98ce\u7684\u6b66\u529f\uff0c\u770b\u6765\u5df2\u81fb\u5316\u5883\uff0c\u786e\u662f\u4e0d\u6613\u62b5\u6321\uff0c\u4f46\u4e0d \u4fe1\u4e16\u95f4\u5c1a\u6709\u7b2c\u4e8c\u4e2a\u4f3c\u51cc\u672a\u98ce\u7684\u4eba\u3002\u81ea\u5df1\u6df1\u5f97\u5929\u9f99\u638c\u6cd5\u7cbe\u9ad3\uff0c\u5728\u6b66\u6797\u4e2d\u4e5f\u662f\u6709\u6570\u4eba\u7269\uff0c\u4e0d\u5982\u907f \u5f00\u51cc\u672a\u98ce\uff0c\u6311\u6218\u90a3\u4e2a\u7626\u5c0f\u7684\u8001\u5934\u513f\u3002 \u3000\u3000\u51cc\u672a\u98ce\u53c8\u50ac\u9053\uff1a\u201c\u60f3\u597d\u4e86\u6ca1\u6709\uff1f\u201d\u5929\u96c4\u9053\uff1a\u201c\u51cc\u672a\u98ce\u4f60\u521a\u6597\u8fc7\u4e00\u573a\uff0c\u6211\u518d\u6597\u4f60\uff0c\u80dc\u4e4b\u4e0d \u6b66\u3002\u6211\u5148\u548c\u4f60\u7684\u670b\u53cb\u6bd4\u8bd5\u4e00\u573a\uff0c\u5f85\u4f60\u6b47\u606f\u8fc7\u540e\uff0c\u6211\u518d\u5949\u966a\u3002\u201d\u51cc\u672a\u98ce\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u548c\u4f60\u4eec\u5bf9 \u624b\uff0c\u7b49\u4e8e\u548c\u5c0f\u5b69\u5b50\u73a9\u800d\uff0c\u6709\u4ec0\u4e48\u7d2f\u7684\uff1f\u4e0d\u8fc7\uff0c\u4f60\u8981\u89c1\u8bc6\u6211\u8fd9\u4f4d\u670b\u53cb\u7684\u672c\u9886\uff0c\u90a3\u53ea\u597d\u7531\u4ed6\u6765\u6559 \u8bad\u4f60\u4e86\uff01\u4ed6\u7684\u624b\u5e95\u6bd4\u6211\u66f4\u8fa3\uff0c\u4f60\u7b49\u7740\u77a7\u597d\u4e86\uff01\u201d \u3000\u3000\u5929\u96c4\u6b63\u60f3\u53d1\u8bdd\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u6709\u4eba\u9634\u4fa7\u4fa7\u5730\u8bf4\u9053\uff1a\u201c\u81ed\u548c\u5c1a\uff0c\u4f60\u5439\u4ec0\u4e48\u5927\u6c14\uff1f\u4f60\u8981\u600e\u6837\u52a8 \u624b\u5440\uff1f\u201d\u5929\u96c4\u5413\u4e86\u4e00\u8df3\uff0c\u56de\u5934\u770b\u65f6\uff0c\u4e0d\u5c31\u6b63\u662f\u81ea\u5df1\u770b\u7740\u4e0d\u8d77\u773c\u7684\u90a3\u4e2a\u8001\u5934\u513f\uff01\u51cc\u672a\u98ce\u4e00\u7b11\u9000 \u4e0b\uff0c\u8f9b\u9f99\u5b50\u559d\u9053\uff1a\u201c\u7559\u795e\uff0c\u63a5\u62db\uff01\u201d\u5929\u96c4\u773c\u795e\u4e00\u82b1\uff0c\u62f3\u98ce\u5df2\u5230\u9762\u95e8\uff0c\u5929\u96c4\u542b\u80f8\u5438\u8179\uff0c\u4e00\u62db \u201c\u795e\u9f99\u6389\u5c3e\u201d\uff0c\u5de6\u638c\u8d77\u5904\uff0c\u52bf\u5982\u5377\u74e6\uff0c\u6a2a\u62d4\u654c\u4eba\u624b\u8155\uff0c\u8fd9\u672c\u662f\u5929\u9f99\u638c\u6cd5\u4e2d\u7684\u6740\u624b\uff0c\u4e0d\u6599\u8f9b\u9f99 \u5b50\u6ed1\u4f3c\u6e38\u9c7c\uff0c\u77ee\u5c0f\u8eab\u8eaf\u7adf\u4ece\u4ed6\u638c\u5e95\u94bb\u8fc7\uff0c\u547c\u7684\u4e00\u638c\uff0c\u63b4\u5728\u4ed6\u7684\u9762\u4e0a\uff0c\u5929\u96c4\u5927\u53eb\u4e00\u58f0\uff0c\u55b7\u51fa\u4e00 \u53e3\u9c9c\u8840\uff0c\u5410\u51fa\u4e24\u9897\u95e8\u7259\uff01 \u3000\u3000\u5929\u96c4\u51e0\u5341\u5e74\u529f\u592b\uff0c\u4e5f\u81ea\u6709\u76f8\u5f53\u9020\u8be3\uff0c\u8f93\u4e86\u4e00\u62db\uff0c\u731b\u7136\u9192\u8d77\uff0c\u8db3\u8ddf\u4e00\u8f6c\uff0c\u53cc\u638c\u7ffb\u98de\uff0c\u8eab\u968f \u52bf\u8f6c\uff0c\u7aef\u7684\u662f\u628a\u5468\u8eab\u5c01\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u201c\u5929\u9f99\u5341\u516b\u638c\u201d\u5171\u5341\u516b\u8def\uff0c\u6bcf\u8def\u5305\u62ec\u4e5d\u4e2a\u53d8\u5316\uff0c\u603b\u5171\u662f \u4e00\u767e\u516d\u5341\u4e8c\u624b\uff0c\u4e00\u6b63\u4e00\u53cd\uff0c\u76f8\u751f\u76f8\u514b\uff0c\u53d8\u5316\u5faa\u73af\uff0c\u6089\u4eff\u9f99\u5f62\uff0c\u6492\u5f00\u52bf\u5b50\uff0c\u4e5f\u662f\u4e00\u6d3e\u72b7\u5389\uff0c\u624b \u811a\u8d77\u5904\uff0c\u5168\u5e26\u52b2\u98ce\u3002\u4e24\u4eba\u8d70\u9a6c\u706f\u4f3c\u7684\u4e71\u8f6c\uff0c\u628a\u4f17\u4eba\u770b\u5f97\u773c\u82b1\u64a9\u4e71\uff01\u5929\u96c4\u7985\u5e08\u6597\u4e86\u4e00\u4f1a\uff0c\u6b63\u60f3 \u62bd\u7a7a\u8fdb\u62db\uff0c\u8f9b\u9f99\u5b50\u5df2\u628a\u4ed6\u7684\u638c\u6d3b\u8def\u6570\u6478\u719f\uff0c\u800c\u4ed6\u5374\u8fd8\u4e0d\u77e5\u9053\u8f9b\u9f99\u5b50\u7684\u638c\u6cd5\u662f\u4f55\u6d3e\u4f55\u5bb6\uff0c\u731b\u653b \u51e0\u62db\uff0c\u62db\u62db\u843d\u7a7a\uff0c\u5ffd\u7136\u80c1\u4e0b\u88ab\u4eba\u638f\u4e86\u4e00\u628a\uff0c\u53c8\u9178\u53c8\u75d2\uff0c\u8f6c\u5f97\u8eab\u6765\uff0c\u9888\u80cc\u53c8\u88ab\u4eba\u634f\u4e86\u4e00\u628a\uff0c\u53cd \u624b\u4e00\u638c\u3002\u5374\u8fde\u654c\u4eba\u7684\u886b\u89d2\u90fd\u635e\u4e0d\u7740\u3002\u8f9b\u9f99\u5b50\u4ed7\u7740\u602a\u5f02\u7684\u8eab\u6d3b\uff0c\u628a\u4ed6\u620f\u5f04\u5f97\u557c\u7b11\u7686\u975e\uff0c\u4e0b\u53f0\u4e0d \u5f97\u3002\u4f17\u4eba\u53ea\u89c1\u8f9b\u9f99\u5b50\u5728\u638c\u98ce\u4e2d\u500f\u8fdb\u500f\u9000\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u800c\u5929\u96c4\u7985\u5e08\u5219\u8fde\u8fde\u602a\u53eb\uff0c\u72b9\u5982\u4e00\u5934\u8d1f\u4f24 \u7684\u86ee\u725b\uff01 \u3000\u3000\u8f9b\u9f99\u5b50\u65bd\u5c55\u6b66\u6797\u602a\u6280\uff0c\u50cf\u9017\u5f04\u5c0f\u5b69\u5b50\u4e00\u6837\u5730\u620f\u800d\u5929\u96c4\u7985\u5e08\uff0c\u9f50\u771f\u541b\u4e00\u65c1\u51dd\u795e\u6ce8\u89c6\uff0c\u53c8\u559c \u53c8\u60ca\uff0c\u559c\u8005\u662f\u4ed6\u65e0\u610f\u4e4b\u4e2d\uff0c\u5f97\u7779\u6b66\u6797\u7edd\u5b66\uff0c\u5fc3\u5185\u7684\u7591\u56e2\u6e10\u89e3\u3002\u539f\u6765\u4ed6\u4ee5\u524d\u5403\u97e9\u5fd7\u90a6\u51e0\u8bb0\u602a \u62db\uff0c\u62d4\u53bb\u80e1\u5b50\uff0c\u5f15\u4e3a\u5e73\u751f\u7684\u5947\u803b\u5927\u8fb1\uff0c\u4f46\u56e0\u97e9\u5fd7\u90a6\u90a3\u51e0\u624b\u53ea\u662f\u96f6\u788e\u7684\u7247\u6bb5\u529f\u592b\uff0c\u4ed6\u600e\u6837\u63e3\u6469 \u4e5f\u63e3\u6469\u4e0d\u51fa\u9053\u7406\u6765\u3002\u5982\u4eca\u770b\u4e86\u8f9b\u9f99\u5b50\u7684\u602a\u62db\uff0c\u60f3\u8d77\u4ee5\u524d\u97e9\u5fd7\u90a6\u7684\u624b\u6cd5\uff0c\u5fc3\u4e2d\u65b9\u59cb\u8c41\u7136\u8d2f\u901a\uff0c \u77e5\u9053\u4ed6\u4eec\u4e24\u4eba\u90fd\u662f\u51fa\u81ea\u540c\u4e00\u5bb6\u6570\u3002\u60ca\u8005\u662f\u867d\u7136\u770b\u51fa\u4e00\u4e9b\u9053\u7406\uff0c\u4f46\u8d8a\u770b\u8d8a\u89c9\u51fa\u5b83\u7684\u590d\u6742\u6df1\u5965\uff0c \u771f\u662f\u6b66\u6797\u4e2d\u4ec5\u89c1\u7684\u529f\u592b\u3002\u81ea\u5df1\u82e5\u51fa\u5c3d\u5168\u529b\uff0c\u4e5f\u8bb8\u53ef\u62b5\u5fa1\u8fd9\u79cd\u602a\u62db\uff0c\u4f46\u5374\u7edd\u65e0\u628a\u63e1\u53d6\u80dc\uff0c\u4ed6\u60f3 \u51cc\u672a\u98ce\u7684\u529f\u592b\u5df2\u8fd9\u6837\u5389\u5bb3\uff0c\u518d\u52a0\u4e0a\u8fd9\u4e2a\u602a\u7269\uff0c\u90a3\u662f\u4e07\u4e07\u4e0d\u80fd\u62b5\u6321\u3002 \u3000\u3000\u6b63\u5f53\u4f17\u4eba\u5168\u795e\u8d2f\u6ce8\u573a\u5fc3\u4e4b\u9645\uff0c\u9f50\u771f\u541b\u5ffd\u7136\u98de\u8eab\u8dc3\u8d77\uff0c\u5176\u4ed6\u4e24\u540d\u536b\u58eb\u624d\u9192\u8d77\u9f50\u771f\u541b\u539f\u6765\u662f \u754f\u60e7\u5148\u9003\uff0c\u6025\u5fd9\u79bb\u5ea7\u98de\u5954\uff0c\u54ea\u91cc\u8fd8\u6765\u5f97\u53ca\u3002\u51cc\u672a\u98ce\u53cc\u624b\u4e00\u626c\uff0c\u4e09\u9053\u4e4c\u91d1\u5149\u8292\uff0c\u65e9\u5df2\u7535\u5c04\u800c \u51fa\uff0c\u5c04\u9f50\u771f\u541b\u90a3\u679d\uff0c\u56e0\u4e3a\u8ddd\u79bb\u8fc7\u8fdc\uff0c\u5c04\u5230\u65f6\u529b\u5ea6\u8f83\u5f31\uff0c\u7ed9\u9f50\u771f\u541b\u53cd\u5251\u62e8\u843d\uff0c\u90a3\u4e24\u540d\u536b\u58eb\uff0c\u5374 \u662f\u65e0\u6cd5\u8eb2\u907f\uff0c\u7ed9\u5929\u5c71\u795e\u8292\uff0c\u81ea\u80cc\u5fc3\u76f4\u8d2f\u524d\u5fc3\uff01\u573a\u4e2d\u5fc3\u8f9b\u9f99\u5b50\u4e5f\u5ffd\u7136\u4e00\u58f0\u602a\u5578\uff0c\u4e00\u628a\u6293\u7740\u5929\u96c4 \u7985\u5e08\u7684\u8888\u88df\uff0c\u5012\u63d0\u8d77\u6765\uff0c\u4ed6\u6025\u4e8e\u8981\u8ffd\u9f50\u771f\u541b\uff0c\u968f\u624b\u628a\u5929\u96c4\u7985\u5e08\u5f80\u5916\u4e00\u6454\uff0c\u4e0d\u7406\u4ed6\u7684\u6b7b\u6d3b\uff0c\u4fbf \u8ffd\u4e0a\u53bb\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u8f9b\u5927\u54e5\uff0c\u7a77\u5bc7\u83ab\u8ffd\uff01\u201d\u8f9b\u9f99\u5b50\u6115\u7136\u6b62\u6b65\uff0c\u53ea\u89c1\u5b5f\u7984\u624b\u821e\u8db3\u8e48\uff0c\u5982\u4e2d \u75af\u9b54\uff0c\u5927\u53eb\u5927\u53f7\uff0c\u8dd1\u51fa\u573a\u6765\uff0c\u5404\u65cf\u914b\u957f\u4e00\u62e5\u800c\u4e0a\uff0c\u628a\u4ed6\u64d2\u4f4f\u3002\u5b5f\u66fc\u4e3d\u4e1d\u54ed\u9053\uff1a\u201c\u6211\u7684\u7238\u7238\u8fd9 \u51e0\u5929\u5f97\u4e86\u5927\u70ed\u75c5\uff0c\u5fc3\u667a\u8ff7\u7cca\uff0c\u6211\u672c\u6765\u529d\u4ed6\u4eca\u665a\u4e0d\u8981\u53ec\u5f00\u4ec0\u4e48\u52b3\u4ec0\u5b50\u7684\u4f1a\u7684\uff0c\u4ed6\u504f\u4e0d\u542c\u3002\u201d\u5404 \u65cf\u914b\u957f\uff0c\u672c\u6765\u5bf9\u5b5f\u7984\u5341\u5206\u6124\u6068\uff0c\u539f\u60f3\u628a\u4ed6\u64d2\u4f4f\u4e4b\u540e\uff0c\u5c31\u8981\u516c\u8bae\u5904\u51b3\uff0c\u4f46\u4e00\u6478\u4ed6\u989d\u5934\u624b\u8db3\uff0c\u679c \u7136\u6eda\u70ed\uff0c\u4ed6\u4eec\u8349\u539f\u90e8\u843d\u7684\u89c4\u77e9\uff0c\u91cd\u75c5\u4e4b\u4eba\uff0c\u4e0d\u8bba\u4ed6\u72af\u4e86\u4ec0\u4e48\u5927\u7f6a\uff0c\u4e5f\u4e0d\u80fd\u5f53\u573a\u5ba1\u95ee\u5904\u5211\uff0c\u582a \u6070\u65cf\u7684\u914b\u957f\u53eb\u9053\uff1a\u201c\u5148\u628a\u4ed6\u770b\u7ba1\u8d77\u6765\u3002\u201d\u5b5f\u66fc\u4e3d\u4e1d\u9053\uff1a\u201c\u6211\u7238\u7238\u600e\u6837\u4e5f\u662f\u4e00\u65cf\u4e4b\u957f\uff0c\u7531\u6211\u770b \u62a4\u4ed6\u5427\u3002\u201d\u5854\u5c71\u65cf\u7684\u914b\u957f\u9053\uff1a\u201c\u54fc\uff0c\u7531\u4f60\u770b\u62a4\u3002\u4f60\u548c\u4f60\u7238\u7238\u8fd8\u4e0d\u662f\u4e00\u9f3b\u5b54\u51fa\u6c14\u3002\u201d\u547c\u514b\u6d4e\u6392 \u4f17\u800c\u51fa\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u522b\u51a4\u6789\u597d\u4eba\uff0c\u5979\u662f\u542c\u6211\u7684\u8bdd\u624d\u6765\u7684\u3002\u201d\u5404\u90e8\u843d\u914b\u957f\uff0c\u56e0\u89c1\u51cc\u5927\u4fa0\u662f\u4ed6 \u8bf7\u6765\uff0c\u521a\u624d\u7684\u5acc\u7591\u5c3d\u91ca\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u8349\u539f\u4e0a\u5ffd\u706b\u628a\u901a\u660e\uff0c\u5580\u8fbe\u5c14\u65cf\u7684\u6218\u58eb\u56db\u5904\u6d8c\u73b0\uff0c\u5927\u58f0\u53eb \u9053\uff1a\u201c\u5b5f\u7984\u91cd\u75c5\uff0c\u62e5\u5b5f\u66fc\u4e3d\u4e1d\u59d1\u5a18\u505a\u6211\u4eec\u7684\u9886\u8896\uff0c\u4e0e\u5404\u65cf\u540c\u6297\u6e05\u5175\uff01\u201d\u5b5f\u66fc\u4e3d\u4e1d\u5fae\u7b11\u63a5\u53d7\u4e86 \u6b22\u547c\uff0c\u5404\u90e8\u843d\u914b\u957f\u9f50\u90fd\u5927\u559c\u3002\u5b5f\u66fc\u4e3d\u4e1d\u9053\uff1a\u201c\u6211\u4eec\u7684\u65cf\u4eba\u548c\u4f60\u4eec\u4e00\u6837\uff0c\u90fd\u662f\u70ed\u8840\u7537\u5b50\u3002\u6211\u7238 \u7238\u7684\u4e3b\u610f\uff0c\u6211\u65e9\u5c31\u53cd\u5bf9\uff0c\u6211\u4eec\u65cf\u4eba\u8fd9\u6b21\u613f\u8054\u76df\u6297\u6e05\uff0c\u5c31\u662f\u6211\u8fd9\u51e0\u5929\u5b89\u6392\u597d\u7684\u3002\u201d\u5854\u5c71\u65cf\u7684\u9149 \u957f\u544a\u7f6a\u9053\uff1a\u201c\u90a3\u4e48\u662f\u6211\u9519\u602a\u59d1\u5a18\u4e86\u3002\u201d\u5b5f\u7984\u5ffd\u7136\u5927\u53eb\u4e00\u58f0\uff0c\u55b7\u51fa\u4e00\u5927\u53e3\u9c9c\u8840\uff0c\u5495\u549a\u5012\u5730\u3002 \u3000\u3000\u539f\u6765\u5b5f\u66fc\u4e3d\u4e1d\u521a\u624d\u6367\u8840\u9152\u7ed9\u7236\u4eb2\u559d\u65f6\uff0c\u957f\u8896\u4f4e\u5782\uff0c\u6697\u4e2d\u5f39\u4e0b\u4e00\u4e9b\u767d\u8272\u7684\u836f\u7c89\u3002\u8fd9\u836f\u7c89\u4e43 \u662f\u8349\u539f\u4e0a\u7684\u5f02\u8349\u70bc\u6210\uff0c\u6027\u6781\u71e5\u70ed\uff0c\u670d\u540e\u5168\u8eab\u53d1\u6eda\uff0c\u5c31\u5982\u60a3\u4e86\u5927\u70ed\u75c5\u4e00\u822c\u3002\u8fd9\u79cd\u914d\u836f\u4e4b\u6cd5\u4e43\u662f \u547c\u514b\u6d4e\u4ece\u65cf\u4e2d\u6700\u5e74\u8001\u7684\u7267\u4eba\u90a3\u91cc\u5b66\u6765\u7684\uff0c\u4ed6\u4f20\u7ed9\u5b5f\u66fc\u4e3d\u4e1d\uff0c\u53eb\u5979\u5982\u6b64\u8bbe\u8ba1\u6551\u7236\u3002\u5b5f\u7984\u8001\u5978\u5de8 \u6ed1\uff0c\u542c\u5973\u513f\u6307\u4ed6\u6709\u75c5\uff0c\u7acb\u523b\u5c06\u8ba1\u5c31\u8ba1\uff0c\u88c5\u5f97\u771f\u7684\u50cf\u4e2a\u70ed\u72c2\u7684\u4eba\uff0c\u5176\u5b9e\uff0c\u795e\u667a\u8fd8\u662f\u6e05\u9192\u7684\u3002\u5230 \u4e86\u540e\u6765\uff0c\u4e00\u89c1\u65cf\u4eba\u90fd\u62e5\u62a4\u5979\u7684\u5973\u513f\u6297\u6e05\uff0c\u4f17\u53db\u4eb2\u79bb\uff0c\u53c8\u6124\u53c8\u6012\uff0c\u6c14\u5f97\u5410\u8840\uff0c\u5f04\u5047\u6210\u771f\uff0c\u771f\u7684 \u53d8\u6210\u75c5\u4eba\u4e86\u3002 \u3000\u3000\u5b5f\u66fc\u4e3d\u4e1d\u5ba3\u5e03\u52a0\u76df\u4e4b\u540e\uff0c\u81ea\u6276\u5b5f\u7984\u56de\u5e10\u5e55\u4f11\u606f\u3002\u5580\u5c14\u6c81\u8349\u539f\u7684\u5404\u65cf\u5404\u90e8\u843d\u63a8\u547c\u514b\u6d4e\u505a\u76df \u4e3b\u3002\u51cc\u672a\u98ce\u5341\u5206\u9ad8\u5174\uff0c\u4e3e\u676f\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u8981\u8bb2\u4e00\u4e2a\u597d\u6d88\u606f\u7ed9\u4f60\u4eec\u77e5\u9053\uff0c\u521a\u624d\u5b5f\u7984\u8bf4\u98de\u7ea2\u5dfe\u8eb2 \u5728\u6df1\u5c71\uff0c\u4e0d\u6562\u51fa\u6765\uff0c\u8fd9\u4e0d\u662f\u771f\u7684\uff01\u98de\u7ea2\u5dfe\u73b0\u5728\u5df2\u7ecf\u590d\u51fa\uff0c\u91cd\u4f5c\u5357\u7586\u5404\u65cf\u7684\u76df\u4e3b\uff0c\u6211\u4eec\u5c31\u662f\u5979 \u6d3e\u6765\u7684\u4f7f\u8005\uff01\u201d\u4f17\u4eba\u53c8\u662f\u4e00\u9635\u6b22\u547c\u3002\u547c\u514b\u6d4e\u7d27\u63e1\u7740\u51cc\u672a\u98ce\u7684\u624b\uff0c\u611f\u6fc0\u5f97\u6d41\u4e0b\u6cea\u6765\uff0c\u9ad8\u58f0\u8bf4 \u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u4e8c\u5341\u591a\u5e74\u524d\uff0c\u4f60\u7684\u5e08\u5144\u6768\u4e91\u9aa2\u548c\u98de\u7ea2\u5dfe\u5973\u4fa0\u6551\u4e86\u6211\u7684\u547d\uff0c\u73b0\u5728\u4f60\u53c8\u6765\u6551\u51fa\u6211 \u4eec\u65cf\u4eba\u3002\u98de\u7ea2\u5dfe\u518d\u51fa\u6765\u90a3\u597d\u6781\u4e86\uff0c\u6211\u4eec\u5580\u5c14\u6c81\u8349\u539f\u7684\u5404\u65cf\u5404\u90e8\u843d\uff0c\u613f\u9065\u53d7\u5979\u7684\u8282\u5236\u3002\u201d\u5f53\u4e0b \u548c\u51cc\u672a\u98ce\u559d\u4e86\u4e00\u76cf\u8840\u9152\uff0c\u7b97\u4f5c\u6b63\u5f0f\u52a0\u76df\u3002 \u3000\u3000\u518d\u8bf4\u5b5f\u66fc\u4e3d\u4e1d\u628a\u7236\u4eb2\u6276\u5165\u5e10\u540e\uff0c\u7528\u96ea\u6c34\u7ed9\u4ed6\u89e3\u6d88\u836f\u529b\uff0c\u5b5f\u7984\u6f78\u7136\u6cea\u4e0b\u53f9\u9053\uff1a\u201c\u5973\u513f\uff0c\u4f60 \u4eba\u5927\u5fc3\u96c4\uff0c\u9e1f\u513f\u957f\u4e0a\u7fc5\u8180\uff0c\u8981\u62e3\u9ad8\u679d\u98de\u4e86\uff01\u201d\u5b5f\u66fc\u4e3d\u4e1d\u6025\u9053\uff1a\u201c\u7238\u7238\uff0c\u8fd9\u662f\u54ea\u91cc\u8bdd\u6765\uff0c\u53ea\u8981 \u4f60\u8bda\u5fc3\u6094\u6539\uff0c\u5411\u5404\u4f4d\u4f2f\u4f2f\u53d4\u53d4\u8c22\u7f6a\uff0c\u5973\u513f\u5305\u4fdd\u4ed6\u4eec\u4e0d\u4f1a\u96be\u4e3a\u4f60\u3002\u201d\u5b5f\u7984\u82e6\u7b11\u4e00\u58f0\uff0c\u5ffd\u7136\u8bf4 \u9053\uff1a\u201c\u4f60\u4eec\u6709\u51cc\u5927\u4fa0\u5e2e\u52a9\uff0c\u8fd8\u8981\u4f60\u7238\u7238\u4f5c\u751a\uff1f\u201d\u5b5f\u66fc\u4e3d\u4e1d\u9053\uff1a\u201c\u51cc\u5927\u4fa0\u8fd8\u8981\u56de\u5230\u5357\u7586\uff0c\u4ed6\u54ea \u80fd\u5728\u6211\u4eec\u8fd9\u513f\u505c\u7559\uff1f\u53ea\u6015\u4ed6\u660e\u540e\u5929\u5c31\u8981\u8d70\u4e86\u3002\u518d\u8bf4\uff0c\u591a\u4e00\u4e2a\u4eba\u5c31\u591a\u4efd\u529b\u91cf\uff0c\u4f55\u51b5\u7238\u7238\u8fd8\u662f\u4e8c \u5341\u5e74\u524d\u7684\u6297\u6e05\u82f1\u96c4\uff1f\u201d\u5b5f\u7984\u9053\uff1a\u201c\u53ea\u6050\u6015\u522b\u4eba\u4e0d\u662f\u8fd9\u4e48\u770b\u6cd5\uff01\u201d\u5b5f\u66fc\u4e3d\u4e1d\u6b63\u60f3\u53cd\u590d\u5f00\u89e3\uff0c\u5b5f \u7984\u5df2\u95ed\u4e0a\u773c\u775b\uff0c\u8bf4\u662f\u75b2\u5026\u6b32\u7720\uff0c\u53eb\u5973\u513f\u4e0d\u8981\u5435\u6270\u4ed6\u4e86\u3002 \u3000\u3000\u4e0d\u6599\u7b2c\u4e8c\u5929\u4e00\u65e9\u8d77\u6765\uff0c\u5b5f\u7984\u7adf\u7136\u79c1\u9003\u53bb\u4e86\u3002\u5b5f\u66fc\u4e3d\u4e1d\u53c8\u6025\u53c8\u6c14\uff0c\u5979\u7236\u5973\u60c5\u6df1\uff0c\u4e00\u65f6\u7cca \u6d82\uff0c\u4e0d\u6562\u5c06\u5b5f\u7984\u9003\u8dd1\u7684\u4e8b\u8bf4\u51fa\u6765\uff0c\u751a\u81f3\u8fde\u547c\u514b\u6d4e\u4e5f\u4e0d\u6562\u544a\u8bc9\u3002\u7b2c\u4e09\u5929\u51cc\u672a\u98ce\u548c\u8f9b\u9f99\u5b50\u5411\u5979\u8f9e \u884c\uff0c\u5979\u611f\u5230\u5341\u5206\u5c34\u5c2c\uff0c\u53ea\u662f\u6073\u8bf7\u51cc\u672a\u98ce\u7ed9\u5979\u95ee\u5019\u98de\u7ea2\u5dfe\u3002 \u3000\u3000\u65f6\u5e8f\u63a8\u79fb\uff0c\u8fd9\u65f6\u5df2\u662f\u6df1\u79cb\u65f6\u5206\uff0c\u8349\u539f\u4e0a\u78a7\u7a7a\u5982\u6d17\uff0c\u6c14\u5019\u867d\u7136\u5bd2\u51b7\uff0c\u5374\u662f\u4ee4\u4eba\u5fc3\u65f7\u795e\u6021\u3002 \u51cc\u672a\u98ce\u5e72\u4e86\u8fd9\u6869\u5927\u4e8b\uff0c\u66f4\u662f\u5341\u5206\u6109\u5feb\uff0c\u4e00\u8def\u4e0a\u6559\u8f9b\u9f99\u5b50\u5531\u8349\u539f\u7684\u6c11\u6b4c\u3002\u4ece\u5580\u5c14\u6c81\u8349\u539f\u56de\u5230\u5410 \u9c81\u756a\u9644\u8fd1\uff0c\u8981\u7ecf\u8fc7\u5929\u5c71\u652f\u8109\u7684\u6155\u58eb\u642d\u683c\u5c71\uff0c\u8fd9\u5c71\u867d\u6ca1\u6709\u5929\u5c71\u7684\u9ad8\u5165\u4e91\u9704\uff0c\u4f46\u4e5f\u9669\u5cfb\u5f02\u5e38\u3002\u5c71 \u8109\u662f\u8bb8\u591a\u51b0\u5c71\u96ea\u5cad\u6240\u6784\u6210\uff0c\u4ece\u8fd9\u4e1b\u51b0\u5c71\u96ea\u5cad\u4e0a\u6d41\u4e0b\u6570\u4e0d\u5c3d\u7684\u51b0\u6cb3\uff0c\u597d\u50cf\u8bb8\u591a\u59ff\u52bf\u4e0d\u540c\u7684\u94f6\u767d \u8272\u7684\u821e\u9f99\uff0c\u9576\u5728\u96ea\u5c71\u5ce1\u8c37\uff0c\u722c\u884c\u5728\u5cf0\u5ce6\u5c71\u5773\u4e4b\u95f4\uff0c\u6784\u6210\u65e0\u6bd4\u58ee\u4e3d\u7684\u666f\u8272\uff01 \u3000\u3000\u51cc\u672a\u98ce\u7eb5\u76ee\u51b0\u6cb3\u666f\u8272\uff0c\u5bf9\u8f9b\u9f99\u5b50\u8d5e\u53f9\u9053\uff1a\u201c\u5929\u5c71\u4e0a\u867d\u6709\u51b0\u6cb3\uff0c\u5374\u8fd8\u4e0d\u5982\u8fd9\u91cc\u7684\u58ee\u4e3d\u3002\u201d \u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u6211\u4eec\u54c8\u8428\u514b\u4eba\u6709\u4e00\u4e2a\u53e4\u8001\u7684\u4f20\u8bf4\uff0c\u4f20\u8bf4\u6709\u4e00\u4f4d\u7f8e\u4e3d\u7684\u5c11\u5973\uff0c\u5979\u7684\u60c5\u90ce\u5230\u5173\u5185 \u53bb\uff0c\u4e00\u53bb\u4e0d\u56de\u3002\u5979\u6500\u4e0a\u6155\u58eb\u5854\u683c\u5c71\u75f4\u75f4\u51dd\u671b\uff0c\u5934\u53d1\u53d8\u6210\u4e86\u51b0\u67f1\uff0c\u773c\u6cea\u6dcc\u6210\u4e86\u51b0\u6cb3\uff01\u201d\u51cc\u672a\u98ce \u9053\uff1a\u201c\u6211\u4eec\u6c49\u4eba\u4e5f\u6709\u671b\u592b\u6210\u5c71\u4e4b\u7c7b\u7684\u4f20\u8bf4\u3002\u53ef\u89c1\u4e0d\u5206\u79cd\u65cf\uff0c\u513f\u5973\u60c5\u6000\u90fd\u662f\u76f8\u540c\u7684\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u7ed9\u8f9b\u9f99\u5b50\u7684\u8bdd\u6311\u8d77\u6101\u601d\u3002\u6005\u60c6\u826f\u4e45\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u8f9b\u5927\u54e5\uff0c\u4f60\u4e5f\u6709\u8fc7\u7231\u6155\u4f60\u7684\u5c11 \u5973\u4e48\uff1f\u201d\u8f9b\u9f99\u5b50\u7ffb\u7740\u602a\u773c\uff0c\u6728\u7136\u4e0d\u7b54\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u624d\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u5ea7\u5c71\u6211\u4e8c\u5341\u591a\u5e74\u524d\u66fe\u5230 \u8fc7\u7684\uff0c\u90a3\u65f6\u6211\u7684\u5e08\u7236\u4e3a\u4e86\u8eb2\u907f\u767d\u53d1\u9b54\u5973\uff0c\u6709\u4e00\u56de\u5c31\u907f\u5230\u8fd9\u5c71\u4e0a\u6765\uff0c\u5bb3\u6211\u627e\u5f97\u597d\u82e6\u3002\u6211\u770b\u4e86\u5e08 \u7236\u7684\u60c5\u5f62\uff0c\u5fc3\u90fd\u51c9\u4e86\uff0c\u5c31\u7b97\u6709\u5929\u4ed9\u4f3c\u7684\u59d1\u5a18\uff0c\u6211\u4e5f\u4e0d\u6562\u62db\u60f9\u3002\u201d\u51cc\u672a\u98ce\u559f\u7136\u53f9\u9053\uff1a\u201c\u4f60\u771f\u806a \u660e\uff01\u201d\u524d\u5c18\u5f80\u4e8b\u4e00\u5e55\u5e55\u5730\u4ece\u5fc3\u5934\u7ffb\u8fc7\uff1a\u94b1\u5858\u6c5f\u5927\u6f6e\u4e4b\u591c\uff0c\u5c11\u5e74\u60c5\u4fa3\u7684\u98a4\u58f0\u547c\u5438\uff1b\u77f3\u7a9f\u4e2d\u7389\u624b \u6577\u4f24\uff0c\u91cd\u9022\u540e\u7684\u53c8\u6068\u53c8\u7231\uff1b\u6c34\u7262\u91cc\u4f24\u5fc3\u8bdd\u65e7\uff0c\u90a3\u51c4\u51c9\u5e7d\u6028\u7684\u773c\u5149\u2026\u51cc\u672a\u98ce\u84e6\u5730\u6253\u4e86\u4e00\u4e2a\u5bd2 \u5664\uff0c\u5fc3\u5e95\u91cc\u53eb\u9053\uff1a\u201c\u5218\u90c1\u82b3\uff0c\u4f60\u5728\u54ea\u91cc\u5462\uff1f\u201d \u3000\u3000\u8f9b\u9f99\u5b50\u602a\u773c\u7741\u5f97\u66f4\u5927\uff0c\u5947\u602a\u51cc\u672a\u98ce\u90a3\u4e48\u597d\u7684\u6b66\u529f\uff0c\u7adf\u4f1a\u8010\u4e0d\u4f4f\u5bd2\u51b7\uff0c\u5728\u51b0\u6cb3\u51b7\u6c14\u4fb5\u88ad\u4e0b \u6253\u8d77\u5bd2\u5664\uff0c\u4ed6\u597d\u610f\u5730\u95ee\u9053\uff1a\u201c\u600e\u4e48\u6837\uff0c\u4f60\u7740\u4e86\u51c9\u4e86\uff1f\u201d\u51cc\u672a\u98ce\u832b\u7136\u4e0d\u89c9\uff0c\u8f9b\u9f99\u5b50\u4e00\u638c\u62cd\u53bb\uff0c \u558a\u9053\uff1a\u201c\u4f60\u4e2d\u4e86\u90aa\u4e48\uff1f\u201d\u51cc\u672a\u98ce\u8df3\u4e86\u8d77\u6765\uff0c\u6115\u7136\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\uff0c\u6211\u600e\u4e48\u4f1a\u4e2d\u90aa\uff1f\u201d\u8f9b\u9f99\u5b50 \u6b63\u60f3\u518d\u95ee\uff0c\u5ffd\u7136\u811a\u4e0b\u4e00\u9635\u9707\u52a8\uff0c\u6025\u62c9\u7740\u51cc\u672a\u98ce\u5f80\u9ad8\u5904\u8dc3\u53bb\u3002\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u662f\u96ea\u5d29\u4e86\uff01\u201d\u5239 \u90a3\u95f4\uff0c\u5c71\u6c9f\u91cc\u54cd\u8d77\u5de8\u5927\u7684\u96f7\u9e23\u58f0\uff0c\u4e07\u5c71\u56de\u5e94\uff0c\u9707\u8bf4\u6b32\u804b\uff0c\u78e8\u76d8\u5927\u7684\u51b0\u96ea\u4ece\u60ac\u5ca9\u4e0a\u6eda\u584c\u800c\u4e0b\uff0c \u58f0\u52bf\u6781\u4e3a\u60ca\u4eba\uff01\u5929\u5c71\u548c\u6155\u58eb\u5854\u683c\u5c71\u7b49\u9ad8\u5c71\uff0c\u5c71\u5dc5\u79ef\u96ea\uff0c\u5e38\u6cbf\u7740\u5c71\u5761\u5411\u4e0b\u6eda\u52a8\uff0c\u662f\u4e3a\u96ea\u5d29\u3002\u82e5 \u7136\u6b63\u5f53\u5176\u950b\uff0c\u4efb\u591a\u5927\u672c\u9886\u7684\u82f1\u96c4\u597d\u6c49\u4e5f\u4f1a\u7ed9\u96ea\u5757\u51b2\u843d\u5c71\u8c37\uff0c\u6d3b\u6d3b\u57cb\u6389\u3002\u5e78\u5f97\u51cc\u672a\u98ce\u548c\u8f9b\u9f99\u5b50 \u90fd\u751a\u6709\u7ecf\u9a8c\uff0c\u53c8\u6709\u6781\u4e0a\u4e58\u7684\u8f7b\u529f\uff0c\u5728\u6ee1\u5c71\u96ea\u5757\u98de\u6eda\u4e2d\u817e\u632a\u95ea\u907f\uff0c\u5c45\u7136\u6beb\u53d1\u65e0\u4f24\u3002 \u3000\u3000\u8fc7\u4e86\u8bb8\u4e45\uff0c\u96ea\u5d29\u624d\u6e10\u6e10\u505c\u606f\uff0c\u51cc\u672a\u98ce\u65b9\u900f\u8fc7\u53e3\u6c14\uff0c\u5ffd\u53c8\u542c\u5f97\u9635\u9635\u54c0\u53f7\u58f0\uff0c\u8f9b\u9f99\u5b50\u62c9\u7740\u4ed6 \u8dc3\u51fa\u5c71\u6c8f\uff0c\u54c0\u53f7\u4e4b\u58f0\u8d8a\u6765\u8d8a\u5927\uff0c\u800c\u4e14\u6b64\u8d77\u5f7c\u843d\uff0c\u663e\u7136\u6709\u4e0d\u5c11\u4eba\u53d7\u96ea\u5d29\u538b\u9876\u4e4b\u707e\u3002\u8f9b\u9f99\u5b50\u9053\uff1a \u201c\u5947\u4e86\uff0c\u600e\u4e48\u4f1a\u6709\u8fd9\u4e48\u591a\u884c\u4eba\uff1f\u201d\u51cc\u672a\u98ce\u6025\u9053\uff0c\u201c\u54b1\u4eec\u5feb\u53bb\u770b\u770b\uff0c\u80fd\u6551\u5f97\u591a\u5c11\u5c31\u662f\u591a\u5c11\u3002\u201d \u8dd1\u51fa\u5c71\u53e3\uff0c\u5f80\u4e0b\u4e00\u671b\uff0c\u53ea\u89c1\u5c71\u8c37\u4e2d\u65e0\u6570\u6e05\u5175\uff0c\u65ad\u624b\u6298\u8db3\uff0c\u6323\u624e\u547c\u53f7\u3002\u51cc\u672a\u98ce\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f \u53ef\uff0c\u5ffd\u542c\u5f97\u5bf9\u9762\u5c71\u5cf0\u4e0a\u6709\u4eba\u53f1\u54a4\u547c\u559d\uff0c\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u770b\u5440\uff01\u90a3\u8fb9\u6709\u4eba\u6597\u5251\uff01\u201d\u51cc\u672a\u98ce\u62ac\u8d77\u5934 \u6765\uff0c\u4e00\u58f0\u6e05\u8106\u7684\u547c\u6551\u58f0\uff0c\u968f\u98ce\u98d8\u5230\uff1a\u201c\u51cc\u672a\u98ce\uff0c\u662f\u4f60\u5417\uff1f\u5feb\u6765\u5440\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u4e00\u542c\uff0c\u6bd4\u521a\u624d\u6240\u60ca\u5c24\u751a\uff01\u7741\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u5218\u90c1\u82b3\u7ad9\u5728\u4e00\u5757\u5371\u5ca9\u4e4b\u4e0a\uff0c\u695a\u662d\u5357\u6b63\u4f3c \u733f\u7334\u822c\u5730\u7eb5\u8dc3\u4e0a\u53bb\uff01 \u3000\u3000\u51cc\u672a\u98ce\u53f1\u54a4\u4e00\u58f0\uff0c\u5929\u5c71\u795e\u8292\u6296\u624b\u98de\u51fa\uff0c\u695a\u662d\u5357\u8eab\u5f62\u95ea\u5c55\uff0c\u5b9d\u5251\u64a9\u5288\uff0c\u95f9\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u597d \u5bb9\u6613\u624d\u907f\u8fc7\u5929\u5c71\u795e\u8292\u7684\u8fde\u73af\u6512\u5c04\uff0c\u51cc\u672a\u98ce\u548c\u8f9b\u9f99\u5b50\u75be\u5982\u98de\u9e1f\uff0c\u8d76\u4e86\u8fc7\u6765\u3002\u695a\u662d\u5357\u5927\u559d\u4e00\u58f0\uff1a \u201c\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u636e\u5728\u5ca9\u77f3\u4e4b\u4e0a\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u594b\u529b\u6321\u4f4f\u51cc\u672a\u98ce\uff0c\u53e6\u5916\u4e24\u540d\u536b\u58eb\uff0c\u53c8\u4ece\u5371\u5d16\u7684\u53e6 \u4e00\u8fb9\u8dd1\u4e0a\uff0c\u5218\u90c1\u82b3\u9891\u9891\u547c\u5524\uff0c\u4f46\u695a\u662d\u5357\u5360\u7740\u5730\u5229\uff0c\u51cc\u672a\u98ce\u6025\u5207\u4e4b\u95f4\u5374\u653b\u4e0d\u4e0a\uff0c\u53ea\u5f97\u5927\u58f0\u558a \u9053\uff1a\u201c\u4f60\u6321\u4f4f\u4e00\u9635\uff0c\u6211\u5c31\u6765\u4e86\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u7a0d\u5b9a\u5fc3\u795e\uff0c\u5468\u56f4\u4e00\u770b\uff0c\u53ea\u89c1\u8f9b\u9f99\u5b50\u4e5f\u8d76\u4e86\u8fc7\u6765\uff0c\u5728\u5c71\u5761\u4e0a\u6597\u5f97\u6b63\u70c8\uff01\u62e6\u7740\u4ed6\u7684\u6b63 \u662f\u957f\u767d\u5c71\u6d3e\u7684\u7956\u5e08\u3001\u98ce\u96f7\u5251\u9f50\u771f\u541b\u3002\u53e6\u4e00\u5806\u4eba\u5219\u5728\u56f4\u653b\u4e00\u4e2a\u767d\u53d1\u8001\u4eba\u548c\u4e00\u4e2a\u7ea2\u8863\u5c11\u5973\uff0c\u767d\u53d1 \u7ea2\u989c\u5728\u5200\u5149\u5251\u5f71\u4e4b\u4e2d\u5de6\u51b2\u77f3\u7a81\uff0c\u8001\u4eba\u5927\u53eb\u201c\u8f9b\u5e08\u5f1f\u201d\uff0c\u5c11\u5973\u5219\u547c\u5524\u201c\u51cc\u5927\u4fa0\u201d\u3002\u51cc\u672a\u98ce\u5fc3\u7a0d \u5bbd\u6170\uff0c\u6697\u9053\uff1a\u201c\u539f\u6765\u662f\u77f3\u5929\u6210\u548c\u6b66\u743c\u7476\uff01\u4ed6\u4eec\u4e24\u4eba\u90fd\u662f\u6b66\u827a\u9ad8\u5f3a\uff0c\u8c05\u4e0d\u4f1a\u8d25\u5728\u654c\u4eba\u4e4b\u624b\uff01\u201d \u8fd0\u5251\u5982\u98ce\uff0c\u8feb\u695a\u662d\u5357\u8ba9\u4e86\u4e00\u6b65\uff0c\u518d\u653e\u773c\u770b\u65f6\uff0c\u53c8\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u56f4\u653b\u77f3\u5929\u6210\u548c\u6b66\u743c\u7476\u7684\u7adf\u662f \u4e03\u516b\u4e2a\u5587\u561b\u50e7\uff0c\u5176\u4e2d\u5c31\u6709\u548c\u81ea\u5df1\u6076\u6597\u8fc7\u7684\u5929\u8499\u5929\u96c4\u4e24\u5e08\u5144\u5f1f\u3002\u770b\u60c5\u5f62\uff0c\u897f\u85cf\u5929\u9f99\u6d3e\u7684\u9ad8\u624b\uff0c \u9664\u4e86\u638c\u95e8\u7684\u5929\u9f99\u7985\u5e08\u5916\uff0c\u7adf\u662f\u503e\u5de2\u4fa7\u51fa\uff0c\u518d\u653e\u773c\u770b\u65f6\uff0c\u8fd8\u6709\u4e03\u516b\u4e2a\u5927\u5185\u536b\u58eb\uff0c\u6b63\u5206\u6210\u4e24\u62e8\uff0c \u4e00\u62e8\u53bb\u56f4\u653b\u8f9b\u9f99\u5b50\uff0c\u4e00\u62e8\u5374\u5411\u81ea\u5df1\u8fd9\u9762\u6251\u6765\uff01\u51cc\u672a\u98ce\u6697\u53eb\u4e00\u58f0\u201c\u82e6\u4e5f\u201d\uff01\u201c\u62bd\u6492\u8fde\u73af\u201d\u5530\uff01 \u5530\uff01\u5530\uff01\u75be\u523a\u6570\u5251\uff0c\u62a2\u4e0a\u4e86\u5ca9\u77f3\uff0c\u53cd\u8eab\u4e00\u4e2a\u65cb\u98ce\u75be\u821e\uff0c\u8fce\u4e0a\u4e86\u6765\u653b\u7684\u51e0\u95e8\u5175\u5203\uff01 \u3000\u3000\u539f\u6765\u5218\u90c1\u82b3\u6b63\u662f\u627e\u51cc\u672a\u98ce\u6765\u7684\u3002\u5979\u81ea\u51cc\u672a\u98ce\u53bb\u540e\uff0c\u5fc3\u4e2d\u60ac\u60ac\u3002\u5230\u7b2c\u4e09\u65e5\uff0c\u98de\u7ea2\u5dfe\u5df2\u548c\u5357 \u7586\u5404\u65cf\u914b\u957f\u8054\u7edc\u4e0a\u4e86\uff0c\u6e05\u5175\u5230\u4e86\u4e00\u5730\uff0c\u90fd\u662f\u5148\u7b51\u7889\u5821\uff0c\u56e0\u6b64\u884c\u519b\u8fdf\u7f13\uff0c\u8fd8\u5728\u6570\u767e\u91cc\u5916\u3002\u6e05\u519b \u6218\u7565\uff0c\u975e\u5e38\u663e\u660e\uff0c\u662f\u60f3\u4ed7\u7740\u4f18\u52bf\u5175\u529b\uff0c\u7a33\u624e\u7a33\u6253\uff0c\u8695\u98df\u5168\u7586\u3002\u98de\u7ea2\u5dfe\u5bf9\u7740\u8fd9\u79cd\u6218\u7565\uff0c\u65e0\u6cd5\u53ef \u65bd\uff0c\u5341\u5206\u5fe7\u6025\u3002\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6211\u4eec\u5175\u529b\u8584\u5f31\uff0c\u8981\u60f3\u5f3a\u73ab\uff0c\u7edd\u5bf9\u4e0d\u884c\u3002\u4f46\u4ed6\u4eec\u4ee5\u7889\u5821\u6218\u672f\uff0c\u4e5f \u672a\u5fc5\u5236\u670d\u5f97\u4e86\u6211\u4eec\uff0c\u8349\u539f\u5e7f\u5927\uff0c\u6709\u5982\u832b\u65e0\u8fb9\u9645\u7684\u6d77\u6d0b\uff0c\u6211\u4eec\u5c31\u5982\u6e38\u9c7c\u4e00\u6837\uff0c\u5728\u7889\u5821\u4e2d\u95f4\u7a7f\u6765 \u63d2\u53bb\uff0c\u8349\u539f\u4e0a\u5904\u5904\u662f\u6211\u4eec\u7684\u4eba\uff0c\u6211\u4eec\u8033\u806a\u76ee\u7075\uff0c\u4ed6\u4eec\u82e5\u6765\u8ffd\u6355\uff0c\u52bf\u5927\u6211\u4eec\u5c31\u907f\u5f00\u5b83\uff0c\u52bf\u5f31\u6211 \u4eec\u5c31\u5403\u6389\u5b83\u3002\u201d\u98de\u7ea2\u5dfe\u53f9\u606f\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u8fd9\u662f\u4e00\u79cd\u65e0\u5c3d\u671f\u7684\u4f5c\u6218\u4e86\uff01\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u4ee5\u5f31\u6297 \u5f3a\uff0c\u53ea\u80fd\u8fd9\u6837\uff0c\u6211\u4eec\u82e5\u628a\u8349\u539f\u53d8\u6210\u4e00\u4e2a\u5927\u6ce5\u6dd6\uff0c\u8ba9\u4ed6\u4eec\u8d8a\u9677\u8d8a\u6df1\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u80fd\u957f\u671f\u505c\u7559\u4e0b \u53bb\uff01\u201d\u6218\u7565\u4e00\u5b9a\uff0c\u5927\u5bb6\u5012\u4e0d\u5fc3\u6025\u4e86\uff0c\u6218\u4e8b\u4e00\u65f6\u4e5f\u7206\u53d1\u4e0d\u8d77\u6765\u3002\u5218\u90c1\u82b3\u82e6\u5ff5\u51cc\u672a\u98ce\uff0c\u6697\u91cc\u548c\u6b66 \u743c\u7476\u5546\u91cf\uff0c\u60f3\u548c\u5979\u4e00\u540c\u5230\u5580\u5c14\u6c81\u8349\u539f\u53bb\u63a5\u5e94\u51cc\u672a\u98ce\u3002\u6b66\u8c05\u7476\u6027\u5b50\u597d\u52a8\uff0c\u548c\u5218\u90c1\u82b3\u4ea4\u60c5\u53c8\u597d\uff0c \u4e00\u53e3\u7b54\u5e94\uff0c\u613f\u4e3a\u5979\u5e26\u8def\uff0c\u4e24\u4eba\u5411\u98de\u7ea2\u5dfe\u8bf7\u6c42\uff0c\u98de\u7ea2\u5dfe\u89c1\u76ee\u524d\u65e0\u4e8b\uff0c\u800c\u4e14\u5979\u4e5f\u6302\u5ff5\u51cc\u672a\u98ce\uff0c\u4e00 \u6c42\u4fbf\u5141\u3002 \u3000\u3000\u518d\u8bf4\u90a3\u77f3\u5929\u6210\uff0c\u4ed6\u81ea\u8bef\u6740\u5e08\u5144\uff0c\u5386\u5c3d\u5fe7\u60a3\u4e4b\u540e\uff0c\u5fc3\u4e2d\u81ea\u8d23\uff0c\u6bcf\u56fe\u6587\u529f\u81ea\u8d4e\u3002\u542c\u8bf4\u5218\u90c1\u82b3 \u548c\u6b66\u743c\u7476\u8981\u5230\u5580\u5c14\u6c81\u8349\u539f\uff0c\u4ed6\u4e5f\u613f\u610f\u540c\u884c\uff0c\u5218\u90c1\u82b3\u662f\u60f3\u53bb\u63a5\u5e94\u51cc\u672a\u51e4\uff0c\u800c\u4ed6\u5219\u662f\u60f3\u53bb\u627e\u8f9b\u9f99 \u5b50\u3002\u5982\u4eca\u4ed6\u53ea\u6709\u8fd9\u4e00\u4e2a\u5e08\u5f1f\u4e86\uff0c\u8fd9\u4e2a\u5e08\u5f1f\u867d\u7136\u602a\u50fb\u7cca\u6d82\uff0c\u4ed6\u4e5f\u53ea\u80fd\u628a\u5149\u5927\u672c\u95e8\u7684\u5e0c\u671b\u4f17\u90fd\u5bc4 \u6258\u5728\u4ed6\u7684\u8eab\u4e0a\u4e86\u3002\u5c24\u5176\u56e0\u4e3a\u4ed6\u6709\u8fc7\u51e0\u4e4e\u8bef\u5165\u6b67\u9014\u3001\u4ee5\u81f3\u9519\u6740\u5e08\u5144\u7684\u6c89\u75db\u7ecf\u9a8c\uff0c\u56e0\u6b64\u4ed6\u7279\u522b\u6302 \u5fc3\u8f9b\u9f99\u5b50\uff0c\u4ed6\u60f3\u4ee5\u201c\u8fc7\u6765\u4eba\u201d\u7684\u8eab\u4efd\uff0c\u73b0\u8eab\u8bf4\u6cd5\uff0c\u53eb\u8f9b\u9f99\u5b50\u9192\u89c9\u8fc7\u6765\u3002\u4e0d\u8981\u53ea\u662f\u6f5c\u5fc3\u5b66\u827a\uff0c \u800c\u4e0d\u987e\u4eba\u95f4\u7684\u5584\u6076\u662f\u975e\u3002 \u3000\u3000\u81f3\u4e8e\u695a\u662d\u5357\u5374\u662f\u968f\u7740\u5927\u519b\u8fdc\u5f81\u56de\u7586\u7684\u3002\u5927\u519b\u7684\u7edf\u5e05\u6210\u4eb2\u738b\u683c\u6d4e\u6b66\u827a\u4e0d\u5f3a\uff0c\u4f46\u4ed6\u662f\u4e2a\u7cbe\u901a \u6218\u7565\u7684\u4eba\uff0c\u4ed6\u4e00\u9762\u4ee5\u7889\u5821\u6218\u672f\uff0c\u9010\u6b65\u63a8\u8fdb\uff0c\u4e00\u9762\u53eb\u695a\u662d\u5357\u7387\u6570\u767e\u7cbe\u9a91\uff0c\u5947\u5175\u7a81\u51fa\uff0c\u4ee5\u591c\u95f4\u7684 \u6025\u884c\u519b\uff0c\u5728\u8349\u539f\u8fb9\u7f18\u8854\u679a\u75be\u8fdb\uff0c\u907f\u8fc7\u98de\u7ea2\u5dfe\u7684\u8425\u5730\uff0c\u6df1\u5165\u5580\u5c14\u6c81\u8349\u539f\uff0c\u6e05\u519b\u7684\u5982\u610f\u7b97\u76d8\uff0c\u662f \u60f3\u4ee5\u8fd9\u961f\u7cbe\u9a91\u5e2e\u52a9\u5b5f\u7984\u63a7\u5236\u8349\u539f\u5404\u65cf\uff0c\u4ee4\u56de\u7586\u5404\u65cf\u5206\u5d29\u79bb\u6790\u3002\u8fd9\u6837\u5185\u5916\u5939\u653b\uff0c\u5357\u7586\u5404\u65cf\u7684\u62b5 \u6297\u5c31\u53ef\u4ee5\u6beb\u4e0d\u8d39\u529b\u5730\u5404\u4e2a\u51fb\u7834\u3002 \u3000\u3000\u695a\u662d\u5357\u5c06\u5230\u6155\u58eb\u5854\u683c\u5c71\u4e4b\u9645\uff0c\u5ffd\u89c1\u9f50\u771f\u541b\u5e26\u7740\u5341\u51e0\u4e2a\u5587\u561b\uff0c\u8fce\u9762\u800c\u6765\u3002\u95ee\u8baf\u4e4b\u4e0b\uff0c\u59cb\u77e5 \u51cc\u672a\u98ce\u548c\u8f9b\u9f99\u5b50\u4e5f\u5230\u6b64\u5730\uff0c\u5580\u5c14\u6c81\u7684\u5404\u65cf\u5df2\u7ecf\u5949\u54c8\u8428\u514b\u7684\u914b\u957f\u505a\u9886\u8896\uff0c\u4e0d\u8981\u5b5f\u7984\u4e86\uff0c\u9f50\u771f\u541b \u8bf4\uff1a\u201c\u5e78\u5f97\u5929\u96c4\u4e0a\u4eba\uff0c\u65e9\u5df2\u9080\u96c6\u540c\u95e8\uff0c\u8d76\u6765\u6b64\u5730\u3002\u6211\u4eec\u53d7\u632b\u4e4b\u540e\uff0c\u4e00\u8fc7\u6155\u58eb\u5854\u683c\u5c71\uff0c\u5c31\u548c\u4ed6 \u4eec\u4f1a\u5408\u4e86\u3002\u53ea\u662f\u6211\u4eec\u81ea\u5fd6\u4eba\u6570\u8fd8\u5c11\uff0c\u4e0d\u60f3\u9a6c\u4e0a\u653b\u51fb\u4ed6\u4eec\u3002\u201d\u695a\u662d\u5357\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u56de\u51cc\u672a\u98ce \u63d2\u7ffc\u96be\u9003\uff01\u6211\u7b97\u4ed6\u4e8b\u6210\u4e4b\u540e\uff0c\u5fc5\u8d76\u56de\u5357\u7586\uff0c\u6211\u4eec\u57cb\u4f0f\u5728\u5e55\u58eb\u642d\u683c\u5c71\u4e2d\uff0c\u7b49\u4ed6\u4eba\u7f51\uff01\u201d \u3000\u3000\u65e0\u5de7\u4e0d\u5de7\uff0c\u521a\u5230\u6155\u58eb\u5854\u683c\u5c71\u53e3\uff0c\u77f3\u5929\u6210\u7b49\u4e09\u4eba\u4e5f\u6b63\u7b56\u9a6c\u9a70\u6765\uff0c\u4e00\u573a\u6df7\u6218\uff0c\u77f3\u5929\u6210\u7b49\u4e09\u4eba \u9669\u4e9b\u88ab\u64d2\uff0c\u5ffd\u7136\u5c71\u9876\u96ea\u5d29\uff0c\u9664\u4e86\u5341\u591a\u4e2a\u6b66\u529f\u8f83\u9ad8\u7684\u5587\u561b\uff0c\u4ee5\u53ca\u695a\u662d\u5357\u9f50\u771f\u541b\u7b49\u4e00\u73ed\u536b\u58eb\u5916\uff0c \u6570\u767e\u6e05\u519b\uff0c\u90fd\u7ed9\u6ee1\u5c71\u4e71\u6eda\u7684\u5927\u96ea\u5757\u51b2\u4e0b\u6df1\u8c37\u3002\u96ea\u5d29\u58f0\u4e2d\uff0c\u5404\u4eba\u81ea\u987e\u4e0d\u6687\uff0c\u6218\u6597\u6682\u505c\uff0c\u5218\u90c1\u82b3 \u5728\u7eb7\u4e71\u4e2d\u722c\u4e0a\u4e00\u5ea7\u5371\u5d16\uff0c\u77f3\u5929\u6210\u6b66\u743c\u7476\u4e24\u4eba\uff0c\u4e00\u9762\u51fa\u624b\u653b\u51fb\u5929\u9f99\u6d3e\u7684\u5587\u561b\uff0c\u4e00\u9762\u95ea\u907f\u90a3\u6ee1\u5c71 \u4e71\u6eda\u7684\u96ea\u5757\u3002\u4e24\u4eba\u90fd\u662f\u7edd\u9876\u6b66\u529f\uff0c\u5230\u96ea\u5d29\u505c\u6b62\u4e4b\u65f6\uff0c\u4ed6\u4eec\u5df2\u51fb\u6bd9\u4e86\u4e94\u4e2a\u5587\u561b\uff0c\u4e24\u4e2a\u536b\u58eb\u3002\u800c \u8fd9\u65f6\u51cc\u672a\u98ce\u548c\u8f9b\u9f99\u5b50\u4e5f\u5df2\u7ecf\u73b0\u8eab\u4e86\u3002 \u3000\u3000\u51b0\u6cb3\u6620\u65e5\u3001\u5251\u6c14\u817e\u9704\uff0c\u4e24\u8fb9\u4eba\u5206\u6210\u56db\u5904\u53ae\u6740\u3002\u5218\u90c1\u82b3\u9ad8\u636e\u5371\u5d16\u4e4b\u4e0a\uff0c\u5de6\u624b\u9526\u4e91\u515c\uff0c\u53f3\u624b \u9752\u94a2\u5251\uff0c\u8fdc\u653b\u8fd1\u6321\uff0c\u654c\u4f4f\u4e86\u4e09\u540d\u536b\u58eb\uff1b\u51cc\u672a\u98ce\u5728\u5c71\u8170\u5904\uff0c\u72ec\u62e2\u695a\u662d\u5357\u4e0e\u53e6\u5916\u56db\u540d\u536b\u58eb\uff0c\u8fd0\u72ec \u6b65\u6d77\u5185\u7684\u5929\u5c71\u5251\u6cd5\uff0c\u54ac\u7259\u6b7b\u6218\uff1b\u77f3\u5929\u6210\u8fde\u73af\u817f\u8d77\u53cc\u638c\u7ffb\u98de\uff0c\u5728\u4f17\u5587\u561b\u4e2d\u65bd\u5c55\u4ed6\u4e5d\u5bab\u795e\u884c\u638c\u7684 \u7edd\u6280\uff1b\u800c\u8f9b\u9f99\u5b50\u5219\u4ee5\u6b66\u6797\u7edd\u5b66\u7684\u8fbe\u6469\u5251\u6cd5\uff0c\u6076\u6218\u9f50\u771f\u541b\uff01 \u3000\u3000\u8f9b\u9f99\u5b50\u4eae\u51fa\u5b9d\u5251\uff0c\u7cbe\u795e\u6296\u64de\uff0c\u602a\u62db\u9aa4\u5c55\uff0c\u987f\u65f6\u94f6\u5149\u904d\u4f53\uff0c\u7d2b\u7535\u98de\u7a7a\uff0c\u6ee1\u8eab\u5251\u82b1\u9519\u843d\uff0c\u602a \u5578\u58f0\u4e2d\uff0c\u4e00\u540d\u536b\u58eb\u7684\u5934\u9885\u98de\u4e0a\u534a\u7a7a\uff0c\u6d12\u4e0b\u8840\u8165\u7ea2\u96e8\uff0c\u9f50\u771f\u541b\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u5251\u4e00\u5708\uff0c\u5251\u5149\u548c\u5251 \u5149\u4e00\u649e\uff0c\u91d1\u94c1\u652f\u9e23\uff0c\u76f4\u8361\u5f00\u53bb\uff0c\u8f9b\u9f99\u5b50\u53ea\u89c9\u624b\u8155\u4e00\u9635\u9178\u9ebb\uff0c\u5251\u8eab\u4e00\u6c89\uff0c\u89e3\u5f00\u6765\u52bf\uff0c\u800c\u9f50\u771f\u541b \u4e5f\u662f\u864e\u53e3\u53d1\u70ed\uff0c\u5de6\u624b\u957f\u5251\u7ed9\u622a\u4e86\u4e00\u6bb5\u3002\u4e24\u4eba\u529f\u529b\u6b63\u662f\u65d7\u9f13\u76f8\u5f53\uff0c\u9f50\u771f\u541b\u65b9\u95ea\u4e86\u4e00\u62db\uff0c\u8f9b\u9f99\u5b50 \u5df2\u662f\u5530\uff01\u5530\uff01\u5530\uff01\u4e00\u8fde\u4e09\u5251\uff0c\u5251\u98ce\u76f4\u903c\u9762\u95e8\uff01\u9f50\u771f\u541b\u4e0b\u76d8\u529f\u592b\u6781\u7a33\uff0c\u53cc\u5251\u4e00\u653b\u4e00\u5b88\uff0c\u5728\u95f4\u4e0d \u5bb9\u53d1\u4e4b\u95f4\uff0c\u6321\u5f00\u8f9b\u9f99\u5b50\u7684\u8fde\u73af\u653b\u52bf\uff0c\u8d81\u52bf\u4e5f\u8fd8\u4e86\u4e00\u62db\u3002\u8f9b\u9f99\u5b50\u602a\u53eb\u9053\uff1a\u201c\u597d\u5440\uff0c\u4e09\u62db\u6362\u4e00 \u5251\uff0c\u4e8f\u4f60\u8eab\u4e3a\u4e00\u6d3e\u7956\u5e08\uff0c\u8fd8\u6562\u604b\u6218\u4e0b\u53bb\uff1f\u201d\u6b66\u6797\u4e2d\u6210\u540d\u9ad8\u624b\u76f8\u6597\uff0c\u8f93\u4e86\u62db\uff0c\u4fbf\u8be5\u670d\u8f93\u3002\u800c\u4eca \u8f9b\u9f99\u5b50\u8fde\u53d1\u4e09\u62db\uff0c\u9f50\u771f\u541b\u624d\u8fd8\u4e86\u4e00\u5251\uff0c\u663e\u7136\u5df2\u8f93\u4e86\u62db\u3002\u53ea\u662f\u6b64\u6b21\u4e24\u8fb9\u4ea4\u950b\u3002\u4e43\u662f\u6027\u547d\u640f\u6597\uff0c \u54ea\u91cc\u8fd8\u4f1a\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u89c4\u77e9\uff1f\u9f50\u771f\u541b\u95f7\u58f0\u4e0d\u54cd\uff0c\u53cc\u5251\u970d\u970d\u5c55\u5f00\uff0c\u9690\u9690\u5e26\u7740\u98ce\u96f7\u4e4b\u58f0\uff0c\u8f9b\u9f99\u5b50\u5f3a \u653b\u731b\u6251\uff0c\u4ed6\u7adf\u7136\u5bf8\u6b65\u4e0d\u8ba9\uff0c\u811a\u8ddf\u9489\u5728\u5730\u4e0a\uff0c\u5251\u5c16\u4f3c\u5c71\uff0c\u5251\u5149\u5982\u7ec3\uff0c\u5251\u62db\u867d\u6162\uff0c\u5374\u662f\u5177\u89c1\u5185\u529b \u6df1\u539a\uff0c\u975e\u6bd4\u5bfb\u5e38\uff01 \u3000\u3000\u9f50\u771f\u541b\u672c\u6765\u65e0\u6cd5\u62b5\u6321\u8f9b\u9f99\u5b50\u7684\u602a\u62db\uff0c\u4f46\u8f9b\u9f99\u5b50\u5728\u5580\u5c14\u6c81\u8349\u539f\u620f\u5f04\u5929\u96c4\u4e4b\u65f6\uff0c\u4ed6\u5728\u65c1\u89c2 \u671b\uff0c\u6f5c\u5fc3\u63e3\u6469\uff0c\u4ed7\u7740\u4e94\u5341\u591a\u5e74\u7684\u529f\u529b\uff0c\u627f\u7136\u80fd\u5316\u9669\u4e3a\u5937\u3002\u53c8\u4ed7\u7740\u6709\u4e09\u4e2a\u5927\u5185\u9ad8\u624b\u76f8\u52a9\uff0c\u8fd9\u624d \u582a\u6253\u4e86\u4e2a\u5e73\u624b\u3002 \u3000\u3000\u9163\u6597\u58f0\u4e2d\uff0c\u56f4\u653b\u7740\u77f3\u5929\u6210\u6b66\u743c\u7476\u7684\u5587\u561b\u50e7\u5ffd\u7136\u7eb7\u7eb7\u5927\u559d\uff0c\u5929\u8499\u7985\u5e08\u6258\u5730\u8df3\u51fa\u5708\u5b50\uff0c\u5411\u540c \u95e8\u5429\u5490\u4e86\u51e0\u53e5\uff0c\u6325\u821e\u7740\u4e00\u6839\u9563\u94c1\u7985\u6756\uff0c\u6076\u72e0\u72e0\u5730\u52a0\u5165\u4e86\u9f50\u771f\u541b\u8fd9\u5806\uff0c\u559d\u903c\uff1a\u201c\u4f55\u7269\u5996\u90aa\uff1f\u5feb \u8fd8\u6211\u9547\u5c71\u5b9d\u5251\uff01\u201d\u7985\u6756\u626b\u5904\uff0c\u547c\u5401\u58f0\u54cd\u3002\u8f9b\u9f99\u5b50\u5ffd\u7136\u5411\u7740\u7985\u6756\u51b2\u53bb\uff0c\u5929\u8499\u4e00\u62db\u201c\u8001\u6811\u76d8 \u6839\u201d\uff0c\u6ee1\u62df\u628a\u8f9b\u9f99\u5b50\u53cc\u811a\u6253\u65ad\uff0c\u4e0d\u6599\u94c1\u6756\u6253\u7a7a\uff0c\u8f9b\u9f99\u5b50\u4e00\u53e3\u6d53\u75f0\uff0c\u6b63\u6b63\u553e\u5728\u5929\u8499\u9762\u4e0a\uff0c\u8033\u8fb9 \u542c\u5f97\u4e00\u58f0\u5632\u9a82\uff1a\u201c\u5478\uff0c\u4e0d\u8981\u8138\uff01\u201d\u5929\u8499\u7985\u6756\u4e00\u7ffb\uff0c\u5df2\u662f\u4e0d\u89c1\u4eba\u5f71\u3002\u8033\u8fb9\u53c8\u542c\u5f97\u9f50\u771f\u541b\u82cd\u8001\u7684 \u58f0\u97f3\u53eb\u9053\uff1a\u201c\u5b88\u79bb\u5bab\uff0c\u5927\u574e\u4f4d\uff0c\u4e0d\u8981\u614c\u4e71\uff01\u201d\u5929\u8499\u9762\u4e0a\u70ed\u8fa3\u8fa3\u7684\u4f5c\u75db\uff0c\u8896\u5b50\u4e00\u62b9\uff0c\u5df2\u89c1\u9c9c \u8840\uff0c\u4ed6\u7ed9\u8f9b\u9f99\u4e8e\u553e\u4e86\u4e00\u53e3\u6d53\u75f0\uff0c\u5c31\u5982\u4e2d\u4e86\u4e00\u9897\u94c1\u83b2\u5b50\u4e00\u822c\uff01 \u3000\u3000\u5176\u5b9e\u5929\u8499\u8fd8\u4e0d\u77e5\u9053\uff0c\u82e5\u975e\u9f50\u771f\u541b\u53ca\u65f6\u51fa\u624b\uff0c\u4ed6\u65e9\u5df2\u4e27\u547d\u4e8e\u8f9b\u9f99\u5b50\u4e09\u5c3a\u9752\u950b\u4e4b\u4e0b\uff0c\u8f9b\u9f99\u4e86 \u4e00\u89c1\u5929\u8499\u7985\u5e08\u4f7f\u4e86\u4e0a\u62db\uff0c\u5c31\u77e5\u4ed6\u7684\u6b66\u529f\u52c7\u731b\u6709\u4f59\uff0c\u7cbe\u7eaf\u4e0d\u8db3\uff0c\u4f7f\u513f\u8fbe\u6469\u4e00\u767e\u96f6\u516b\u5f0f\u7684\u6b66\u6797\u7edd \u5b66\uff0c\u4e00\u4e2a\u201c\u91d1\u8d61\u620f\u6d6a\u201d\uff0c\u5728\u5200\u5251\u7985\u6756\u6734\u51fb\u4e4b\u4e0b\uff0c\u94bb\u4e86\u8fc7\u53bb\uff0c\u4ed7\u7740\u602a\u5f02\u8eab\u6cd5\u3002\u5230\u4e86\u5929\u8499\u80cc\u540e\uff0c \u5929\u8499\u5c1a\u4e14\u61f5\u7136\u4e0d\u77e5\uff0c\u9f50\u771f\u541b\u89c1\u5f62\u52bf\u5371\u9669\uff0c\u4e00\u4e2a\u201c\u76d8\u819d\u62d7\u6b65\u201d\uff0c\u957f\u5251\u5f80\u5916\u659c\u9012\uff0c\u8eab\u5251\u76f8\u5408\uff0c\u4e00 \u7f15\u9752\u5149\uff0c\u4e5f\u81ea\u8ffd\u5230\u4e86\u8f9b\u9f99\u5b50\u8eab\u540e\u3002\u8f9b\u9f99\u5b50\u65e0\u6687\u51fb\u654c\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u89e3\u5f00\u4e86\u9f50\u771f\u541b\u6697\u88ad\u7684\u5a01\u80c1\uff0c \u5230\u5929\u8499\u7985\u5e08\u7684\u7985\u6756\u843d\u4e0b\uff0c\u4ed6\u5df2\u5708\u5230\u9f50\u771f\u541b\u7684\u53f3\u4fa7\u53bb\u4e86\u3002\u5929\u8499\u4f9d\u7740\u9f50\u771f\u541b\u6240\u6559\uff0c\u811a\u8e0f\u516b\u5366\u65b9 \u4f4d\uff0c\u5728\u574e\u4f4d\u8fdb\u62db\uff0c\u8fd9\u624d\u89c1\u7740\u8f9b\u9f99\u5b50\u7684\u8eab\u5f62\uff0c\u4ed6\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u521a\u597d\u80fd\u591f\u907f\u4e86\u5f00\u53bb\uff01 \u3000\u3000\u5929\u8499\u529f\u529b\uff0c\u5728\u6e05\u5ef7\u8fd9\u8fb9\u4ec5\u6b21\u4e8e\u9f50\u771f\u541b\u3001\u695a\u662d\u5357\u3001\u6210\u5929\u633a\u7b49\u6709\u9650\u51e0\u4eba\uff0c\u4e5f\u662f\u4e00\u7b49\u4e00\u7684\u597d \u624b\uff0c\u4f9d\u7740\u9f50\u771f\u541b\u6240\u9753\u5b88\u7a33\u95e8\u6237\uff0c\u62a1\u8d77\u7985\u6756\uff0c\u547c\u547c\u8f70\u8f70\uff0c\u524d\u540e\u5de6\u53f3\u90fd\u662f\u4e00\u7247\u6756\u5f71\uff0c\u5a01\u529b\u4ea6\u751a\u60ca \u4eba\uff0c\u8f9b\u9f99\u5b50\u7684\u5b9d\u5251\u8fd8\u771f\u4e0d\u6562\u548c\u4ed6\u76f8\u78b0\u3002\u9f50\u771f\u541b\u98ce\u96f7\u53cc\u5251\uff0c\u6321\u7740\u6b63\u9762\uff0c\u66f4\u662f\u6c89\u7a33\u96c4\u5065\u3002\u4e09\u4e2a\u5927 \u5185\u9ad8\u624b\uff0c\u5219\u4ece\u4e24\u4fa7\u914d\u5408\u94bb\u653b\u3002\u8f9b\u9f99\u5b50\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u62b5\u654c\u4e0d\u4f4f\u4e94\u540d\u4e00\u6d41\u597d\u624b\u3002\u8fd9\u4e00\u6218\u6253\u5f97\u6c99\u98de \u77f3\u8d70\uff0c\u6d41\u51b0\u6eda\u52a8\uff0c\u6076\u6597\u4e86\u4e09\u767e\u591a\u62db\uff0c\u8f9b\u9f99\u5b50\u5df2\u662f\u6c57\u6e7f\u9ebb\u8863\uff0c\u547c\u5438\u7d27\u4fc3\uff0c\u53ea\u80fd\u4ed7\u7740\u602a\u5f02\u7684\u8eab \u6cd5\uff0c\u5728\u5468\u56f4\u5175\u5203\u5939\u51fb\u4e2d\uff0c\u632a\u817e\u95ea\u907f\uff0c\u5077\u7a7a\u8fdb\u62db\u4e86\uff01 \u3000\u3000\u77f3\u5929\u6210\u6b66\u743c\u7476\u90a3\u8fb9\uff0c\u5f62\u52bf\u8f83\u597d\uff0c\u4f46\u4e5f\u5360\u4e0d\u4e86\u4fbf\u5b9c\uff0c\u77f3\u3001\u6b66\u4e8c\u4eba\u5408\u6597\u516d\u540d\u5587\u561b\uff0c\u4e24\u540d\u536b \u58eb\uff0c\u800c\u4e24\u540d\u536b\u58eb\u4e4b\u4e2d\uff0c\u6709\u4e00\u4e2a\u662f\u4ec5\u6b21\u4e8e\u695a\u662d\u5357\u7684\u6210\u5927\u633a\uff0c\u4ed6\u7684\u4e00\u53cc\u5224\u5b98\u7b14\uff0c\u5404\u957f\u4e00\u5c3a\u516b\u5bf8\uff0c \u4e13\u6253\u4eba\u8eab\u4e09\u5341\u516d\u9053\u5927\u7a74\uff0c\u77f3\u6b66\u4e8c\u4eba\uff0c\u4e0d\u80fd\u4e0d\u5c0f\u5fc3\u63d0\u9632\u3002\u597d\u5728\u77f3\u5929\u6210\u51e0\u5341\u5e74\u6765\uff0c\u4e13\u7ec3\u4e24\u95e8\u7edd \u6280\uff0c\u9e33\u9e2f\u8fde\u73af\u817f\u4e13\u653b\u654c\u4eba\u4e0b\u76d8\uff0c\u4e5d\u5bab\u795e\u884c\u638c\u5219\u4e13\u95e8\u4f3a\u9699\u64d2\u62ff\u654c\u4eba\u5175\u5203\uff0c\u4e00\u4f17\u5587\u561b\uff0c\u672a\u66fe\u89c1\u8fc7 \u8fd9\u79cd\u6218\u6cd5\uff0c\u4e0d\u6562\u8fc7\u4efd\u8feb\u8fd1\u3002\u81f3\u4e8e\u6b66\u743c\u7476\u7684\u5251\u6cd5\uff0c\u4e43\u662f\u767d\u53d1\u9b54\u5973\u7684\u771f\u4f20\uff0c\u53ea\u8bba\u8f9b\u8fa3\u9669\u72e0\u4e4b\u5904\uff0c \u6bd4\u5929\u5c71\u5251\u6cd5\u5c24\u751a\uff0c\u53ea\u6709\u6210\u5929\u633a\u6562\u548c\u5979\u6b63\u9762\u8fdb\u62db\uff0c\u5176\u4ed6\u5587\u561b\u90fd\u662f\u7a0d\u6cbe\u5373\u8d70\u3002\u4f46\u8fd9\u516d\u4e2a\u5587\u561b\uff0c\u90fd \u662f\u5929\u8499\u7684\u5e08\u5f1f\uff0c\u529f\u529b\u4e5f\u81ea\u4e0d\u5f31\uff0c\u66f4\u517c\u4ed6\u4eec\u540c\u51fa\u4e00\u95e8\uff0c\u5929\u9f99\u5251\u6cd5\u7ec3\u4e60\u6709\u7d20\uff0c\u516d\u4e2a\u4eba\u5982\u540c\u4e00\u4f53\uff0c \u6b64\u8fdb\u5f7c\u9000\u3002\u8f97\u8f6c\u653b\u62d2\uff0c\u5e03\u4e0b\u4e86\u5929\u9f99\u5251\u9635\uff0c\u9976\u662f\u77f3\u6b66\u4e8c\u4eba\uff0c\u5404\u6709\u72ec\u95e8\u6b66\u529f\uff0c\u9ad8\u5f3a\u6280\u827a\uff0c\u4e5f\u88ab\u4ed6 \u4eec\u56f0\u5728\u6838\u5fc3\u3002 \u3000\u3000\u4f46\u6076\u6597\u5f97\u6700\u6fc0\u70c8\u7684\u8fd8\u662f\u51cc\u672a\u98ce\u90a3\u4e00\u8fb9\uff0c\u534f\u52a9\u695a\u662d\u5357\u7684\u56db\u540d\u536b\u58eb\uff0c\u90fd\u662f\u5927\u5185\u5341\u540d\u5185\u7684\u5165 \u9009\uff0c\u6bd4\u534f\u52a9\u9f50\u771f\u541b\u7684\u90a3\u4e09\u540d\u536b\u58eb\uff0c\u53c8\u9ad8\u51fa\u4e00\u7b79\uff01\u695a\u662d\u5357\u7684\u6e38\u9f99\u5251\u53c8\u5df2\u53d6\u56de\uff0c\u4ed7\u7740\u5b9d\u5251\u4e4b\u529b\uff0c \u4e5f\u662f\u7740\u7740\u8ff8\u8feb\u3002\u51cc\u672a\u98ce\u5fc3\u60ac\u6218\u53cb\uff0c\u8fed\u8d70\u9669\u62db\uff0c\u51e0\u88ab\u695a\u662d\u5357\u6240\u4e58\u3002\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u5140\u662f\u672a\u80fd\u51b2 \u51fa\uff0c\u695a\u662d\u5357\u5927\u58f0\u5632\u7b11\uff0c\u53eb\u51cc\u672a\u98ce\u5f03\u5251\u6295\u964d\u3002\u4ed6\u9053\uff1a\u201c\u51cc\u672a\u98ce\uff0c\u4f60\u632b\u6298\u5728\u5e08\u5144\u624b\u5185\uff0c\u6709\u4ec0\u4e48\u8981 \u7d27\uff1f\u8d76\u5feb\u6295\u987a\uff0c\u514d\u88ab\u5200\u5251\u5206\u5c38\u3002\u201d\u51cc\u672a\u98ce\u4e00\u58f0\u864e\u543c\uff0c\u624b\u4e2d\u5251\u201c\u529b\u5212\u9e3f\u6c9f\u201d\uff0c\u5411\u4e0b\u4e00\u626b\u3002\u5251\u5149 \u95ea\u5904\uff0c\u5427\u5427\u5427\u5427\uff0c\u4e00\u7247\u8fde\u54cd\uff0c\u628a\u51e0\u540d\u536b\u58eb\u7684\u5175\u5203\u5168\u90fd\u8361\u5f00\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u51e0\u4f3c\u5316\u6210\u4e00\u9053\u767d\u5149\uff0c \u76f4\u5411\u695a\u662d\u5357\u51b2\u53bb\uff0c\u695a\u662d\u5357\u4e0d\u6562\u548c\u4ed6\u62fc\u547d\uff0c\u5411\u540e\u4e00\u4ef0\uff0c\u8fde\u9000\u51e0\u6b65\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5251\u6cd5\u51cc\u5389\u65e0\u524d\uff0c\u7d27\u7d27\u9489\u4f4f\uff0c\u5bf9\u5176\u4ed6\u56db\u540d\u536b\u58eb\u7684\u5175\u5203\uff0c\u53ea\u51ed\u7740\u542c\u98ce\u8fa8\u5668\u4e4b\u672f\uff0c\u8d8b\u95ea \u8eb2\u907f\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u8fde\u53d1\u5341\u51e0\u62db\u8fa3\u62db\uff0c\u628a\u695a\u662d\u5357\u8feb\u5230\u4e0b\u9996\uff0c\u53c8\u8df3\u4e0a\u4e00\u5757\u5ca9\u77f3\uff0c\u5c55\u9ad8\u4e34\u4e0b\uff0c\u518d\u6321 \u654c\u4eba\u7684\u56f4\u653b\u3002\u4ed6\u662f\u60f3\u8981\u62a2\u5360\u6709\u5229\u5730\u5f62\uff0c\u9010\u6b65\u79fb\u4e0a\u6073\u5ca9\uff0c\u5148\u89e3\u5218\u90c1\u82b3\u7684\u6025\u96be\uff01 \u3000\u3000\u5218\u90c1\u82b3\u90a3\u8fb9\uff0c\u5f62\u52bf\u6700\u662f\u5371\u9669\uff0c\u5979\u72ec\u636e\u5371\u5d16\u4e4b\u4e0a\uff0c\u524d\u65e0\u9053\u8def\uff0c\u4e0b\u6709\u8ffd\u5175\uff0c\u73af\u653b\u5979\u7684\u4e09\u540d\u536b \u58eb\uff0c\u5168\u975e\u5eb8\u624b\uff0c\u5e78\u5979\u7684\u5185\u5bb6\u65e0\u6781\u5251\u6cd5\uff0c\u8bb2\u7a76\u4ee5\u67d4\u514b\u521a\uff0c\u4ee5\u5de7\u964d\u529b\uff0c\u914d\u4e0a\u5979\u7684\u5947\u95e8\u6697\u5668\u9526\u4e91 \u515c\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u62fc\u6b7b\u82e6\u6597\uff0c\u654c\u4eba\u6025\u5207\u4e4b\u95f4\uff0c\u8fd8\u653b\u4e0d\u4e0a\u6765\u3002\u53ea\u662f\uff0c\u867d\u7136\u5982\u6b64\uff0c\u654c\u4eba\u4ecd\u662f\u4e00\u6b65\u6b65 \u5730\u8feb\u4e0a\u3002\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u4e09\u540d\u536b\u58eb\uff0c\u5148\u540e\u90fd\u5df2\u4e0a\u5230\u5cf0\u9876\uff0c\u628a\u5218\u90c1\u82b3\u56f0\u5728\u6838\u5fc3\uff0c\u5218\u90c1\u82b3\u5931\u4e86\u6709 \u5229\u5730\u5f62\uff0c\u66f4\u89c1\u5403\u529b\uff0c\u5251\u62db\u5c55\u5904\uff0c\u53ea\u80fd\u5728\u5468\u56f4\u516b\u5c3a\u4e4b\u5185\uff0c\u82e6\u82e6\u5c01\u95ed\u95e8\u6237\uff0c\u6beb\u65e0\u8fd8\u51fb\u4e4b\u529b\u4e86\u3002 \u3000\u3000\u51cc\u672a\u98ce\u8fde\u8fde\u62a2\u653b\u51e0\u6b21\uff0c\u9010\u6b65\u4e0a\u79fb\uff0c\u548c\u5218\u90c1\u82b3\u5df2\u7136\u76f8\u671b\uff0c\u5218\u90c1\u82b3\u5927\u58f0\u53eb\u9053\uff1a\u201c\u51cc\u672a\u98ce\uff01\u54b1 \u4eec\u5230\u5e95\u89c1\u7740\u4e86\uff01\u201d\u51cc\u672a\u98ce\u53eb\u9053\uff1a\u201c\u55ef\uff0c\u6211\u5c31\u6765\uff01\u201d\u695a\u662d\u5357\u51b7\u7b11\u9053\uff1a\u201c\u54fc\uff01\u539f\u6765\u4f60\u8fd8\u6709\u4e2a\u5fc3\u4e0a \u4eba\u5728\u8fd9\u91cc\uff01\u597d\uff0c\u5c31\u8ba9\u4f60\u505a\u9b3c\u4e5f\u98ce\u6d41\uff01\u201d\u5251\u62db\u4e00\u7d27\uff0c\u4e00\u5251\u5feb\u4f3c\u4e00\u5251\uff0c\u4ed6\u4ed7\u7740\u56db\u540d\u536b\u58eb\u534f\u52a9\uff0c\u4e0d \u987b\u9632\u5b88\uff0c\u7adf\u628a\u5929\u5c71\u5251\u6cd5\u4e2d\u6700\u51f6\u8fa3\u7684\u653b\u62db\u5168\u4f7f\u51fa\u6765\uff0c\u51cc\u672a\u98ce\u989d\u5934\u89c1\u6c57\uff0c\u51b2\u4e86\u4e24\u6b21\u6ca1\u6709\u51b2\u51fa\u3002\u628a \u5fc3\u4e00\u6a2a\uff0c\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff01\u5c55\u5f00\u4e86\u62fc\u547d\u7684\u62db\u6570\u3002\u4e00\u67c4\u9752\u94a2\u5251\u7a81\u5982\u795e\u9f99\u620f\u6c34\uff0c\u5ffd\u4f3c\u98de\u9e70\u76d8\u7a7a\uff0c\u8fdb \u5982\u731b\u864e\u51fa\u62bc\uff0c\u9053\u82e5\u72e1\u5154\u907f\u9e70\uff0c\u695a\u662d\u5357\u75be\u653b\u51e0\u5251\uff0c\u90fd\u7ed9\u4ed6\u8fde\u6d88\u5e26\u6253\uff0c\u53cd\u523a\u8fc7\u6765\uff0c\u62ff\u634f\u65f6\u5019\uff0c\u5999 \u5230\u6beb\u5dc5\uff0c\u5389\u5bb3\u4e4b\u6781\uff01\u695a\u662d\u5357\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u60f3\u4e0d\u5230\u4ed6\u7684\u5251\u6cd5\u5df2\u5230\u4e86\u51fa\u795e\u5165\u5316\u4e4b\u5883\uff0c\u6bd4\u4e0a\u6b21\u76f8 \u9047\uff0c\u53c8\u7cbe\u5999\u4e86\u8bb8\u591a\uff01\u4f46\u60f3\u51cc\u672a\u98ce\u867d\u7136\u51f6\u72b7\u7edd\u4f26\uff0c\u5230\u5e95\u4e0d\u662f\u94c1\u6253\u7684\u4eba\uff0c\u81ea\u5df1\u5408\u4e94\u4e2a\u9ad8\u624b\u4e4b\u529b\uff0c \u867d\u4e0d\u80fd\u53d6\u80dc\uff0c\u8c05\u4e5f\u4e0d\u4f1a\u843d\u8d25\uff0c\u4ed6\u8fd9\u6837\u5f3a\u653b\u731b\u6253\uff0c\u4e0d\u987b\u591a\u4e45\uff0c\u6c14\u529b\u5b9a\u8017\u5b8c\u3002\u4e3b\u610f\u6253\u5b9a\uff0c\u6253\u4e2a\u6697 \u53f7\uff0c\u5251\u62db\u4e00\u53d8\uff0c\u7528\u5927\u5c71\u5251\u6cd5\u4e2d\u653b\u5b88\u517c\u5907\u7684\u987b\u5f25\u5251\u6cd5\u548c\u56db\u540d\u536b\u58eb\uff0c\u8054\u6210\u4e00\u7ebf\uff0c\u9996\u5c3e\u547c\u5e94\uff0c\u5e03\u6210 \u4e86\u94c1\u58c1\u94dc\u5899\uff0c\u53ea\u548c\u51cc\u672a\u98ce\u6e38\u6597\uff01 \u3000\u3000\u695a\u662d\u5357\u6253\u7684\u4e3b\u610f\u4e0d\u9519\uff0c\u4f46\u4ed6\u5374\u4e0d\u77e5\u9053\u51cc\u672a\u98ce\u5f97\u4e86\u6666\u660e\u7985\u5e08\u7684\u62f3\u7ecf\u5251\u8bc0\uff0c\u53c8\u609f\u4e86\u8bb8\u591a\u6b66\u529f \u7684\u7a8d\u8981\u3002\u4ee5\u524d\u51cc\u672a\u98ce\u548c\u695a\u662d\u5357\u6240\u9886\u609f\u7684\u5251\u6cd5\uff0c\u5b8c\u5168\u4e00\u6837\uff0c\u4f46\u73b0\u5728\u4ed6\u4e00\u89c1\u695a\u662d\u5357\u4f7f\u51fa\u6700\u6df1\u5965\u7684 \u987b\u5f25\u5251\u6cd5\uff0c\u5c31\u77e5\u9053\u4ed6\u5c1a\u672a\u5230\u5bb6\uff01\u8fd9\u5012\u4e0d\u662f\u6666\u660e\u7985\u5e08\u6709\u4ec0\u4e48\u504f\u5fc3\uff0c\u4e5f\u4e0d\u662f\u5251\u8bc0\u4e0a\u7559\u4e0b\u51e0\u624b\u672a \u6559\uff0c\u800c\u662f\u56e0\u4e3a\u6700\u6df1\u5965\u7684\u5251\u6cd5\uff0c\u5e38\u4e8e\u7a8d\u8981\u4e4b\u5904\uff0c\u53ef\u610f\u4f1a\u800c\u4e0d\u53ef\u8a00\u4f20\u3002\u695a\u662d\u5357\u53ea\u662f\u5f97\u4e86\u5e08\u7236\u6240 \u6388\uff0c\u800c\u51cc\u672a\u98ce\u5219\u662f\u5bf9\u62f3\u7ecf\u5251\u8bc0\uff0c\u6f5c\u5fc3\u82e6\u5b66\uff0c\u8c41\u7136\u8d2f\u901a\uff0c\u5728\u6700\u6df1\u5965\u7684\u5730\u65b9\uff0c\u6240\u5f97\u6700\u5927\u3002\u82e5\u695a\u662d \u5357\u53e6\u7528\u5176\u4ed6\u5251\u8fed\uff0c\u51cc\u672a\u98ce\u4e00\u65f6\u8fd8\u4e0d\u80fd\u5c06\u5b83\u7834\u53bb\uff0c\u5982\u4eca\u695a\u662d\u5357\u4f7f\u51fa\u987b\u5f25\u5251\u6cd5\uff0c\u6b63\u5408\u4ed6\u610f\uff0c\u4ed6\u5ffd \u5730\u4e00\u58f0\u51b7\u7b11\uff0c\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u7a81\u5982\u94f6\u9f99\u5165\u6d77\uff0c\u4e0d\u8fc7\u6570\u62db\uff0c\u5c31\u628a\u695a\u662d\u5357\u7684\u5251\u6cd5\u7834\u53bb\u3002\u695a\u662d\u5357 \u6b63\u60f3\u6362\u62db\uff0c\u80a9\u5934\u5df2\u4e2d\u4e86\u4e00\u5251\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u8df3\u51fa\u5708\u5b50\u3002\u51cc\u672a\u98ce\u53cd\u81c2\u523a\u624e\uff0c\u75be\u5982\u95ea\u7535\uff0c\u201c\u6ce2\u201d\u7684 \u4e00\u58f0\uff0c\u628a\u8eab\u540e\u4e00\u540d\u536b\u58eb\uff0c\u523a\u4e86\u4e2a\u900f\u660e\u7a9f\u7abf\uff01\u4ed6\u51b2\u51fa\u7f3a\u53e3\uff0c\u548c\u5218\u90c1\u82b3\u7684\u8ddd\u79bb\u8d8a\u6765\u8d8a\u8fd1\u4e86\uff01 \u3000\u3000\u695a\u662d\u5357\u7709\u5934\u4e00\u76b1\uff0c\u4e00\u62db\u201c\u4e1c\u98ce\u6298\u67f3\u201d\uff0c\u5b9d\u5251\u5377\u5730\u626b\u53bb\uff0c\u51cc\u672a\u98ce\u7eb5\u8eab\u4e00\u8dc3\uff0c\u5229\u5251\u659c\u6311\uff0c\u53c8 \u523a\u4f24\u4e86\u4e00\u540d\u536b\u58eb\uff0c\u695a\u662d\u5357\u84e6\u5730\u957f\u8eab\uff0c\u624b\u4e0a\u5df2\u63e1\u4e86\u4e00\u628a\u788e\u77f3\uff0c\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u6563\u5f00\u3002\u201d\u7adf\u4ee5\u201c\u53cd \u81c2\u9634\u9556\u201d\u7684\u624b\u6cd5\uff0c\u5411\u5218\u90c1\u82b3\u6d12\u53bb\u3002\u5218\u90c1\u82b3\u7684\u9526\u4e91\u515c\u8fce\u95e8\u4e00\u6321\uff0c\u4e00\u5927\u628a\u788e\u77f3\uff0c\u7ed9\u5979\u8361\u5f97\u56db\u9762\u7eb7 \u98de\uff0c\u4f46\u695a\u662d\u5357\u53d1\u6697\u5668\u7684\u52b2\u9053\u5947\u5927\uff0c\u9526\u4e91\u515c\u7684\u788e\u91d1\u94a2\u4e1d\u7f51\u4e5f\u7ed9\u9707\u7834\u4e86\u5341\u51e0\u4e2a\u5c0f\u6d1e\uff0c\u4e0d\u80fd\u518d\u7528\u6765 \u52fe\u9501\u5175\u5668\u4e86\uff0c\u8fd9\u4e00\u6765\u5218\u90c1\u82b3\u7684\u5a01\u529b\u5927\u51cf\uff0c\u7ed9\u53f3\u7ffc\u7684\u536b\u571f\u4e00\u5251\u628a\u5305\u5934\u9752\u4e2d\u524a\u843d\uff0c\u51e0\u906d\u4e0d\u6d4b\uff0c\u51cc \u672a\u98ce\u5927\u5403\u4e00\u60ca\uff0c\u90a3\u4e00\u5251\u867d\u672a\u523a\u4e2d\u5218\u90c1\u82b3\uff0c\u5374\u201c\u523a\u4e2d\u201d\u4e86\u4ed6\u7684\u5fc3\u5934\u3002\u4ed6\u8eab\u5b50\u9661\u7136\u4e00\u9707\uff0c\u695a\u662d\u5357 \u4e00\u5251\u81ea\u540e\u523a\u6765\uff0c\u4ed6\u95ea\u8eb2\u7a0d\u6162\uff0c\u7ed9\u5251\u5c16\u5212\u4f24\u4e86\u4e00\u5904\u76ae\u8089\u3002\u51cc\u672a\u98ce\u820c\u7efd\u6625\u96f7\uff0c\u4e00\u58f0\u66b4\u559d\uff0c\u53cd\u624b\u4e00 \u5251\uff0c\u628a\u4e00\u540d\u536b\u58eb\u62e6\u8170\u65a9\u65ad\uff0c\u8fd9\u65f6\u5ffd\u542c\u5f97\u8f9b\u9f99\u5b50\u8fde\u58f0\u602a\u5578\uff0c\u60e8\u5389\u4e4b\u6781\uff01 \u3000\u3000\u8f9b\u9f99\u5b50\u72ec\u6218\u9f50\u771f\u541b\u3001\u5929\u8499\u7985\u5e08\u548c\u53e6\u5916\u4e09\u540d\u5927\u5185\u536b\u58eb\uff0c\u4ee5\u4e00\u4eba\u4e4b\u529b\u548c\u4e94\u540d\u4e00\u6d41\u9ad8\u624b\u53ae\u62fc\uff0c \u800c\u4e14\u9f50\u771f\u541b\u7684\u529f\u529b\u548c\u8f9b\u9f99\u5b50\u53c8\u6b63\u662f\u534a\u65a4\u516b\u4e24\uff0c\u65d7\u9f13\u76f8\u5f53\uff01\u8f9b\u9f99\u5b50\u4ed7\u7740\u8fbe\u6469\u79d8\u6280\uff0c\u602a\u5f02\u62db\u6570\uff0c \u82e6\u6597\u4e86\u4e09\u4e94\u767e\u62db\uff0c\u6c57\u5982\u96e8\u4e0b\uff0c\u8eab\u6cd5\u6e10\u6e10\u8fdf\u6ede\uff0c\u9f50\u771f\u541b\u98ce\u96f7\u53cc\u5251\u864e\u864e\u8feb\u6765\uff0c\u8f9b\u9f99\u5b50\u4e8c\u8fde\u53d7\u4e09\u5904 \u5251\u4f24\uff0c\u6012\u6781\u72c2\u5659\uff0c\u5929\u8499\u7985\u5e08\u4ee5\u4e3a\u6709\u673a\u53ef\u4e58\uff0c\u547c\u7684\u4e00\u6756\uff0c\u201c\u8fc5\u96f7\u51fb\u9876\u201d\u5411\u4ed6\u5934\u9885\u6253\u843d\u3002\u54ea\u6599\u8f9b \u9f99\u5b50\u867d\u662f\u5f3a\u5f29\u4e4b\u672a\uff0c\u4f59\u52bf\u672a\u8870\uff0c\u5de6\u624b\u634f\u7740\u5251\u8bc0\uff0c\u659c\u659c\u5411\u5916\u4e00\u63a8\uff0c\u53f3\u624b\u5251\u201c\u767d\u9e64\u5544\u9c7c\u201d\u76f4\u70b9\u5929 \u8499\u80f8\u819b\uff0c\u5929\u8499\u7acb\u8d77\u7985\u6756\uff0c\u4e00\u4e2a\u7ffb\u8eab\uff0c\u201c\u4e4c\u9f99\u76d8\u6811\u201d\uff0c\u6a2a\u626b\u8f9b\u9f99\u5b50\u4e2d\u8def\uff0c\u6756\u98ce\u4eba\u5f71\u4e2d\uff0c\u602a\u5578\u4e0e \u72c2\u547c\u6742\u4f5c\uff0c\u8f9b\u9f99\u5b50\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\uff0c\u4e00\u6293\u6293\u5728\u5929\u8499\u7684\u80f8\u819b\u4e0a\uff0c\u7acb\u65f6\u4e94\u6307\u6d1e\u7a7f\uff0c\u7985\u6756\u8131 \u624b\u98de\u53bb\uff01\u9f50\u771f\u541b\u53cc\u5251\u5288\u6765\uff0c\u8f9b\u9f99\u5b50\u5df1\u662f\u8dc4\u8dc4\u8e09\u8e09\u5730\u4ece\u53cc\u5251\u7f1d\u4e2d\u94bb\u4e86\u8fc7\u53bb\uff01 \u3000\u3000\u77f3\u5929\u6210\u95fb\u539a\u8f9b\u9f99\u5b50\u602a\u5578\u4e4b\u58f0\uff0c\u5173\u5fc3\u8fc7\u751a\uff0c\u5728\u8f9b\u9f99\u5b50\u8089\u640f\u5929\u8499\u4e4b\u65f6\uff0c\u4ed6\u4e5f\u6267\u6b7b\u8089\u640f\u4e00\u4f17\u5587 \u561b\uff0c\u7a81\u7136\u8dc3\u51fa\uff0c\u4e00\u638c\u6253\u5728\u4fa7\u7ffc\u5587\u561b\u7684\u624b\u8155\u4e0a\uff0c\u7b2c\u4e8c\u4e2a\u5587\u561b\u4e00\u5251\u523a\u6765\uff0c\u5c06\u4ed6\u7684\u80a9\u80db\u7a7f\u6d1e\uff0c\u4ed6\u7adf \u4e0d\u95ea\u907f\uff0c\u4e5d\u5bab\u795e\u884c\u638c\u62db\u6570\u4e1d\u6beb\u4e0d\u7f13\uff0c\u4e94\u6307\u64d2\u62ff\uff0c\u628a\u4fa7\u7ffc\u7684\u5587\u561b\u6325\u821e\u8d77\u6765\uff0c\u53cd\u624b\u4e00\u811a\uff0c\u53c8\u628a\u523a \u4f24\u4ed6\u7684\u90a3\u4e2a\u5587\u561b\u6d3b\u6d3b\u8e22\u6bd9\uff0c\u8fd9\u4e00\u6765\u65e0\u9f99\u5251\u9635\u767b\u65f6\u5927\u4e71\uff0c\u77f3\u5929\u6210\u9ad8\u547c\u9163\u6597\uff0c\u51b2\u8fc7\u6210\u5929\u633a\u7684\u5c01 \u9501\uff0c\u53bb\u63f4\u6551\u5e08\u5f1f\u8f9b\u9f99\u5b50\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u672c\u5df2\u529b\u7aed\u7b4b\u75b2\uff0c\u4e00\u89c1\u5e08\u5144\u62fc\u6b7b\u6765\u63f4\uff0c\u5927\u4e3a\u611f\u52a8\uff0c\u594b\u8d77\u7cbe\u795e\uff0c\u4e00\u4e2a\u602a\u62db\u628a\u9f50\u771f\u541b\u8feb \u9000\u51e0\u6b65\uff0c\u56de\u8eab\u4e8c\u5251\uff0c\u7528\u4e2a\u201c\u56de\u9f99\u5f52\u6d1e\u201d\uff0c\u4e00\u7ffb\u4e00\u5377\uff0c\u53f3\u9762\u653b\u4e0a\u7684\u90a3\u540d\u536b\u58eb\uff0c\u767b\u65f6\u60e8\u53eb\u4e00\u58f0\uff0c \u5de6\u624b\u4e94\u4e2a\u6307\u5934\uff0c\u5168\u7ed9\u5251\u950b\u5272\u65ad\uff0c\u75db\u5f7b\u5fc3\u813e\uff0c\u6251\u901a\u5012\u5730\uff0c\u4e00\u76f4\u6eda\u4e0b\u51b0\u6cb3\u3002\u77f3\u5929\u6210\u548c\u8eab\u6251\u4e0a\uff0c\u53cc \u638c\u4e00\u5206\uff0c\u201c\u5927\u6454\u7891\u624b\u201d\u7167\u51c6\u4e00\u540d\u536b\u58eb\u7684\u201c\u592a\u9633\u7a74\u201d\u5288\u53bb\uff0c\u90a3\u540d\u536b\u58eb\u4f7f\u4e2a\u201c\u91ce\u9a6c\u5206\u9b03\u201d\uff0c\u8eab\u8eaf \u521a\u8f6c\u5f97\u4e00\u534a\uff0c\u5df2\u7ed9\u77f3\u5929\u6210\u4e00\u811a\u8e22\u7ffb\uff0c\u4e5f\u6eda\u4e0b\u4e86\u51b0\u6cb3\u3002\u9f50\u771f\u541b\u6012\u6781\u6c14\u6781\uff0c\u53f3\u5251\u4e00\u62db\u201c\u98ce\u5377\u6b8b \u4e91\u201d\uff0c\u654c\u7740\u8f9b\u9f99\u5b50\u7684\u602a\u62db\uff0c\u5de6\u5251\u5530\u5730\u76f4\u523a\u5230\u77f3\u5929\u6210\u808b\u4e0b\uff0c\u72e0\u75be\u5f02\u5e38\uff01 \u3000\u3000\u77f3\u5929\u6210\u56de\u8eab\u62d7\u6b65\uff0c\u9f50\u771f\u541b\u7684\u957f\u5251\u8d34\u808b\u800c\u8fc7\uff0c\u77f3\u5929\u6210\u53cd\u624b\u4e00\u638c\u51fb\u53bb\uff0c\u9f50\u771f\u541b\u4e5f\u7f29\u8170\u56de\u8098\u649e \u6765\uff0c\u4e24\u4eba\u90fd\u5927\u543c\u4e00\u58f0\uff0c\u6258\u5730\u540e\u9000\uff0c\u8f9b\u9f99\u5b50\u4e58\u52bf\u8865\u4e0a\u4e00\u5251\uff0c\u628a\u9f50\u771f\u541b\u80a9\u5934\u523a\u4f24\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u77f3\u5929\u6210\u51c4\u5389\u53eb\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u6211\u4e0d\u884c\u4e86\uff0c\u4f60\u8981\u597d\u597d\u5149\u5927\u672c\u95e8\uff01\u201d\u8f9b\u9f99\u5b50\u9a87\u7136\u56de\u987e\uff0c \u53ea\u89c1\u77f3\u5929\u6210\u56de\u8272\u60e8\u767d\uff0c\u7476\u7476\u6b32\u5815\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u8f9b\u9f99\u5b50\u5fc3\u5934\u65e0\u9650\u96be\u8fc7\uff0c\u60f3\u8d77\u81ea\u5df1\u81ea\u6043\u5f97\u4e86\u5e08\u7236 \u8863\u94b5\u771f\u4f20\uff0c\u4e0d\u628a\u5e08\u5144\u653e\u5728\u773c\u5185\uff0c\u751a\u81f3\u8fde\u5e08\u5144\u4e5f\u4e0d\u60f3\u8ba4\uff0c\u800c\u4eca\u5e08\u5144\u5374\u820d\u4e86\u6027\u547d\u6765\u6551\u81ea\u5df1\uff01\u8f9b\u9f99 \u5b50\u987e\u4e0d\u5f97\u8ffd\u51fb\u9f50\u771f\u541b\uff0c\u56de\u8eab\u6765\u6551\u5e08\u5144\uff0c\u4e0d\u60f3\u5269\u4e0b\u7684\u90a3\u540d\u5927\u5185\u536b\u58eb\uff0c\u624b\u821e\u6df7\u5143\u94c1\u813e\uff0c\u53c8\u4ece\u65c1\u8fb9 \u72e0\u72e0\u6251\u4e0a\uff0c\u8f9b\u9f99\u5b50\u6124\u6012\u975e\u5e38\uff0c\u731b\u543c\u4e00\u58f0\uff0c\u4e00\u5251\u5288\u53bb\uff0c\u628a\u536b\u571f\u7684\u94c1\u724c\u51fb\u5f97\u98de\u4e0a\u534a\u7a7a\uff0c\u4f38\u81c2\u4e00 \u6293\uff0c\u628a\u90a3\u540d\u536b\u58eb\u6293\u4e86\u8fc7\u6765\uff0c\u6d3b\u6d3b\u6454\u6b7b\uff0c\u518d\u60f3\u56de\u8eab\uff0c\u5ffd\u7136\u89c9\u5f97\u53cc\u81c2\u9178\u9ebb\uff0c\u811a\u6b65\u865a\u6d6e\uff0c\u773c\u524d\u91d1\u661f \u4e71\u5192\uff0c\u539f\u6765\u521a\u624d\u81ea\u5df1\u52a8\u4e86\u771f\u6c14\uff0c\u62fc\u547d\u4e00\u51fb\uff0c\u6c14\u529b\u7adf\u5df2\u8017\u5c3d\uff0c\u8f9b\u9f99\u5b50\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u4e0d\u60f3\u6211\u4eca \u65e5\u547d\u6bd5\u4e8e\u6b64\uff01\u201d\u4ed6\u5bb3\u6015\u9f50\u771f\u541b\u4e58\u52bf\u53cd\u51fb\uff0c\u5c06\u4ed6\u51cc\u8fb1\uff0c\u6b63\u62d4\u81ea\u5c3d\uff0c\u5ffd\u89c1\u9f50\u771f\u541b\u4e5f\u7ad9\u5728\u4e00\u8fb9\u51dd\u8eab \u4e0d\u52a8\uff0c\u4f3c\u5728\u5598\u606f\u8fd0\u6c14\uff0c\u8f9b\u9f99\u5b50\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6025\u5fd9\u53cc\u811a\u9489\u5730\uff0c\u4e5f\u8c03\u606f\u547c\u5438\uff0c\u8fd0\u6b66\u5f53\u79d8\u4f20\u7684\u5410\u7eb3\u4e4b \u6d01\uff0c\u8fd9\u65f6\u8f9b\u9f99\u5b50\u548c\u9f50\u771f\u541b\u9762\u5bf9\u9762\u5730\u7ad9\u7740\uff0c\u76f8\u8ddd\u4e0d\u8fc7\u6570\u6b65\uff0c\u4f46\u4e24\u4eba\u90fd\u4f3c\u6597\u8d25\u4e86\u7684\u516c\u9e21\uff0c\u4e92\u76f8\u7741 \u7740\u4e00\u53cc\u602a\u773c\u76ef\u4f4f\uff0c\u9762\u4e0a\u795e\u8272\uff0c\u975e\u5e38\u6050\u6016\uff01 \u3000\u3000\u539f\u6765\u521a\u624d\u77f3\u5929\u6210\u5403\u9f50\u771f\u541b\u649e\u4e2d\u80f8\u819b\uff0c\u800c\u9f50\u771f\u541b\u7684\u808b\u4e0b\u4e5f\u7ed9\u77f3\u5929\u6210\u51fb\u4e86\u4e00\u638c\uff0c\u7adf\u662f\u4e24\u8d25\u4ff1 \u4f24\uff01\u4f46\u9f50\u771f\u541b\u7684\u529f\u529b\u8981\u6bd4\u77f3\u65e0\u6210\u9ad8\u51fa\u4e00\u7b79\uff0c\u5403\u4e86\u4e00\u638c\uff0c\u867d\u7136\u6298\u65ad\u4e86\u4e24\u6839\u808b\u9aa8\uff0c\u5374\u8fd8\u80fd\u591f\u54ac\u7259 \u82e6\u62b5\uff0c\u77f3\u5929\u6210\u7ed9\u4ed6\u6376\u8098\u4e00\u649e\uff0c\u767b\u65f6\u628a\u6a2a\u7ec3\u7684\u94c1\u5e03\u886b\u529f\u592b\u4e5f\u649e\u7834\u4e86\u3002\u5f53\u4ed6\u5631\u5490\u8f9b\u9f99\u5b50\u8981\u5149\u5927\u672c \u95e8\u4e4b\u540e\uff0c\u5df2\u662f\u767e\u9ab8\u6b32\u6563\uff0c\u5012\u5728\u5730\u4e0a\uff0c\u4e0d\u80fd\u52a8\u5f39\uff0c\u800c\u9f50\u771f\u541b\u867d\u7136\u7a0d\u597d\uff0c\u4f46\u4e8b\u4f24\u4e4b\u540e\uff0c\u53c8\u7ed9\u8f9b\u9f99 \u4e8e\u8865\u4e0a\u4e00\u5251\uff0c\u4e5f\u662f\u7cbe\u6c14\u6da3\u6563\uff0c\u50cf\u8f9b\u9f99\u5b50\u4e00\u6837\u90fd\u5df2\u65e0\u529b\u7ee7\u7eed\u62fc\u6597\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u76f8\u6301\u4e86\u4e00\u4f1a\uff0c\u8f9b\u9f99\u5b50\u6c14\u529b\u7a0d\u7a0d\u6062\u590d\uff0c\u9f50\u771f\u541b\u4e5f\u6162\u6162\u4e3e\u8d77\u957f\u5251\u4e00\u6ee1\u9762\u72f0\u72de\u4e4b\u8272\uff0c\u767d\u53d1 \u5982\u9488\uff0c\u6839\u6839\u76f4\u575a\u3002\u8f9b\u9f99\u5b50\u602a\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4f24\u4e86\u6211\u7684\u5e08\u5144\uff0c\u6211\u7eb5\u6b7b\u4e5f\u4e0d\u80fd\u7ed9\u4f60\u9003\u51fa\u6211\u7684\u5251 \u4e0b\uff01\u201d\u5b9d\u5251\u4e00\u6a2a\uff0c\u4e5f\u662f\u7f13\u7f13\u5730\u79fb\u52a8\u811a\u6b65\uff0c\u8fce\u4e0a\u524d\u53bb\u3002\u6b63\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u8fdc\u65b9\u4e00\u58f0\u6e05\u8106\u7684\u53eb \u58f0\uff0c\u63a5\u7740\u4f3c\u662f\u51cc\u672a\u98ce\u7684\u5927\u58f0\u53f1\u54a4\uff0c\u800c\u8fd1\u5904\u6b66\u743c\u7476\u5ffd\u7136\u9510\u58f0\u53eb\u558a\uff0c\u4f3c\u4e00\u53ea\u767d\u9e64\u98de\u4e0b\u51b0\u5d16\uff01 \u3000\u3000\u539f\u6765\u5728\u521a\u624d\u8f9b\u9f99\u5b50\u8fde\u4e2d\u4e09\u5904\u5251\u4f24\uff0c\u602a\u58f0\u547c\u5524\u7684\u65f6\u5019\uff0c\u51cc\u672a\u98ce\u6b63\u5728\u548c\u695a\u662d\u5357\u6b7b\u6218\uff0c\u95fb\u58f0\u4e00 \u9707\uff0c\u6df1\u6015\u8f9b\u9f99\u5b50\u60e8\u906d\u4e0d\u6d4b\uff0c\u6298\u4e86\u6700\u6709\u529b\u7684\u5e2e\u624b\uff0c\u56de\u5934\u4e00\u671b\uff0c\u4e0d\u89c9\u5251\u62db\u7a0d\u7f13\u3002\u9ad8\u624b\u6bd4\u5251\uff0c\u54ea\u80fd \u5206\u795e\uff0c\u695a\u662d\u5357\u4e00\u62db\u201c\u5012\u7737\u661f\u6cb3\u201d\uff0c\u5b9d\u5251\u4ece\u51cc\u672a\u98ce\u5934\u9876\u524a\u8fc7\uff0c\u51cc\u672a\u98ce\u8eab\u8eaf\u4e00\u77ee\uff0c\u4e3e\u5251\u4e0a\u8fce\uff0c\u80cc \u5fc3\u5df2\u4e2d\u4e86\u4e00\u4e2a\u536b\u58eb\u7684\u94dc\u9524\uff01\u5e78\u4ed6\u529f\u529b\u975e\u51e1\uff0c\u4e2d\u4e86\u4e00\u9524\uff0c\u8e09\u8e09\u8dc4\u8dc4\u5730\u5954\u51fa\u51e0\u6b65\uff0c\u8fd8\u80fd\u8d81\u52bf\u4e00 \u5251\uff0c\u5251\u950b\u76f4\u53d6\u695a\u662d\u5357\u7684\u9b42\u95e8\u7a74\u3002 \u3000\u3000\u695a\u662d\u5357\u201c\u602a\u83bd\u7ffb\u8eab\u201d\uff0c\u5f80\u56de\u4e00\u8f6c\uff0c\u6e38\u9f99\u5251\u201c\u91d1\u96d5\u5c55\u7fc5\u201d\uff0c\u9aa4\u5f80\u51cc\u672a\u98ce\u7684\u5251\u8eab\u4e0a\u5d29\u7838\uff0c\u559d \u9053\uff1a\u201c\u6492\u624b\uff01\u201d\u7528\u8db3\u5341\u6210\u529b\u91cf\uff0c\u51cc\u672a\u98ce\u9752\u94a2\u5251\u75be\u5f80\u4e0b\u6c89\uff0c\u968f\u5373\u5f80\u5916\u7528\u8155\uff0c\u4e00\u62db\u201c\u6c9b\u516c\u65a9 \u86c7\u201d\uff0c\u5251\u950b\u4e0b\u65a9\u695a\u662d\u5357\u53cc\u8db3\uff0c\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u53db\u8d3c\u770b\u62db\uff01\u201d\u695a\u662d\u5357\u7684\u53cd\u81c2\u5c3d\u7ba1\u8fc5\u5982\u7535\u706b\uff0c\u5230\u5e95 \u672a\u80fd\u78b0\u7740\u51cc\u672a\u98ce\u7684\u5175\u5203\u3002\u51cc\u672a\u98ce\u7684\u9752\u94a2\u5251\u75be\u6536\u75be\u53d1\uff0c\u695a\u662d\u5357\u5251\u62db\u4f7f\u8001\uff0c\u65e0\u6cd5\u5229\u7528\u5b9d\u5251\u6240\u957f\uff0c \u80a9\u5934\u4e00\u52a8\uff0c\u817e\u8eab\u8dc3\u8d77\uff0c\u51cc\u672a\u98ce\u7fe9\u5982\u5de8\u9e70\uff0c\u4e5f\u4ece\u659c\u523a\u51b2\u51fa\uff0c\u8fd9\u65f6\u8ddd\u79bb\u5218\u90c1\u82b3\u5df2\u4e0d\u5230\u5341\u6b65\u4e86\u3002 \u3000\u3000\u695a\u662d\u5357\u62a2\u5148\u4e00\u6b65\uff0c\u53c8\u636e\u4e86\u4e00\u5757\u5ca9\u77f3\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u6321\u7740\u51cc\u672a\u98ce\u7684\u53bb\u8def\uff0c\u6e38\u9f99\u5251\u5288\u5241\u64a9\u6321\uff0c \u5149\u8292\u56db\u5c04\uff0c\u4e24\u4e2a\u536b\u58eb\u53c8\u6765\u62a1\u9524\u821e\u621f\uff0c\u524d\u540e\u5939\u653b\u3002\u51cc\u672a\u98ce\u5df2\u6e05\u6e05\u695a\u695a\u770b\u5230\u5218\u90c1\u82b3\u90a3\u53c8\u60ca\u60f6\u53c8\u559c \u60a6\u7684\u795e\u60c5\uff0c\u53ea\u5c31\u662f\u8fd9\u6570\u6b65\u4e4b\u9694\u65e0\u6cd5\u51b2\u8fc7\u3002 \u3000\u3000\u5218\u90c1\u82b3\u89c1\u51cc\u672a\u98ce\u5c31\u5c06\u6765\u5230\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u4e00\u67c4\u9752\u94a2\u5251\u821e\u5f97\u6ef4\u6c34\u4e0d\u5165\uff0c\u628a\u4e09\u540d\u536b\u58eb\u62e6\u5728\u5468\u56f4 \u516b\u5c3a\u4e4b\u5916\u3002\u51cc\u672a\u98ce\u633a\u5251\u4e00\u51b2\uff0c\u695a\u662d\u5357\u659c\u8eab\u8fdb\u5251\uff0c\u51cc\u672a\u98ce\u6b63\u60f3\u5192\u9669\u51b2\u8fc7\uff0c\u80cc\u540e\u547c\u547c\u98ce\u54cd\uff0c\u90a3\u540d \u536b\u58eb\u7684\u94dc\u9524\u582a\u582a\u7838\u5230\u540e\u5fc3\uff0c\u51cc\u672a\u98ce\u52c3\u7136\u5927\u6012\u53cd\u624b\u4e00\u635e\uff0c\u635e\u7740\u9524\u5934\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u53bb\uff01\u201d\u628a\u90a3 \u536b\u58eb\u9aa4\u7136\u626f\u4e86\u8d77\u6765\uff0c\u63b7\u4e0b\u51b0\u6cb3\uff01\u4f46\u695a\u662d\u5357\u4e5f\u8d81\u6b64\u65f6\u673a\uff0c\u4fef\u8eab\u53c8\u6293\u8d77\u4e00\u5757\u77f3\u5757\uff0c\u7528\u529b\u4e00\u634f\uff0c\u53d8 \u6210\u65e0\u6570\u77f3\u5f39\uff0c\u6253\u4e2a\u62db\u547c\uff0c\u56f4\u653b\u5218\u90c1\u82b3\u7684\u4e09\u540d\u536b\u58eb\u970e\u5730\u6563\u5f00\uff0c\u695a\u662d\u5357\u7528\u201c\u6ee1\u5929\u82b1\u96e8\u201d\u7684\u91d1\u94b1\u9556 \u624b\u6cd5\uff0c\u4e00\u628a\u77f3\u5f39\u6d12\u5c06\u8fc7\u53bb\uff0c\u8ddd\u79bb\u65e2\u8fd1\uff0c\u529b\u9053\u53c8\u5927\uff0c\u5218\u90c1\u82b3\u7684\u9752\u94a2\u5251\u6321\u683c\u4e0d\u4f4f\uff0c\u8eab\u4e0a\u4e2d\u4e86\u51e0\u9897 \u77f3\u5f39\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u811a\u6b65\u4e00\u677e\uff0c\u7adf\u7136\u4ece\u5371\u5d16\u4e0a\u8dcc\u4e86\u4e0b\u53bb\uff0c\u4eba\u5728\u534a\u7a7a\uff0c\u72b9\u81ea\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u51cc\u672a \u98ce\uff0c\u4f60\u73b0\u5728\u8fd8\u4e0d\u8bf4\u5b9e\u8bdd\u5417\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u6454\u6b7b\u90a3\u540d\u536b\u571f\u4e4b\u540e\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u521a\u597d\u89c1\u7740\u8fd9\u60e8\u70c8\u7684\u753b\u9762\uff0c\u5218\u90c1\u82b3\u7684\u8bed\u97f3\u7476\u66f3\u957f \u7a7a\uff0c\u9707\u8361\u5fc3\u9b44\uff01\u51cc\u672a\u98ce\u6025\u6781\u9a87\u6781\uff0c\u4e0d\u7406\u751f\u6b7b\uff0c\u4e00\u4e2a\u201c\u51cc\u9e4a\u6469\u4e91\u201d\uff0c\u51ed\u7a7a\u8dc3\u8d77\u6570\u4e08\uff0c\u4ece\u695a\u662d\u5357 \u5934\u9876\u98de\u743c\u800c\u8fc7\uff0c\u4ed6\u7684\u9752\u94a2\u5251\u5728\u534a\u7a7a\u4e0a\u5c1a\u4f7f\u4e86\u4e00\u8bb0\u8fa3\u62db\uff0c\u5251\u5c16\u5728\u695a\u662d\u5357\u5934\u9876\u4e09\u5bf8\u4e4b\u5904\uff0c\u659c\u659c\u62d6 \u523a\uff0c\u695a\u662d\u5357\u5fd9\u4e8e\u8eb2\u907f\uff0c\u7adf\u7136\u65e0\u6687\u4f24\u4ed6\uff01 \u3000\u3000\u51cc\u672a\u98ce\u4e00\u60ca\u800c\u524d\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u5c31\u662f\u90a3\u4e2a\u5b69\u5b50\uff0c\u5728\u676d\u5dde\u957f\u5927\u7684\u90a3\u4e2a\u5b69\u5b50\u5440\uff01\u201d\u53ef\u662f\u5218 \u90c1\u82b3\u5df2\u542c\u4e0d\u89c1\u4e86\uff0c\u4ed6\u51b2\u5230\u5ca9\u8fb9\uff0c\u4f9d\u7a00\u89c1\u7740\u5218\u90c1\u82b3\u7684\u8863\u88d9\u5728\u534a\u7a7a\u98d8\u8361\uff01\u51cc\u672a\u98ce\u6b63\u60f3\u8ddf\u7740\u8dc3\u4e0b\uff0c \u524d\u540e\u5de6\u53f3\u51e0\u822c\u5175\u5668\uff0c\u5df2\u540c\u65f6\u523a\u6765\uff01\u56f4\u653b\u5218\u90c1\u82b3\u7684\u90a3\u4e09\u540d\u536b\u58eb\u548c\u695a\u662d\u5357\u5df2\u7136\u4f1a\u5408\u4e00\u5904\uff0c\u8981\u628a\u8fd9 \u7edd\u4e16\u6b66\u529f\u7684\u5927\u4fa0\uff0c\u8feb\u4e0b\u60ac\u5d16\uff0c\u5b66\u6b66\u7684\u4eba\uff0c\u78b0\u5728\u6781\u5ea6\u5371\u9669\u4e4b\u65f6\uff0c\u672c\u80fd\u5730\u4f1a\u8eb2\u95ea\u53cd\u51fb\uff0c\u51cc\u672a\u98ce\u7a81 \u4f7f\u51fa\u5929\u5c71\u5251\u6cd5\u7684\u795e\u6280\uff0c\u201c\u5927\u6f20\u6d41\u6c99\u201d\uff0c\u9752\u94a2\u5251\u500f\u5730\u98de\u626c\uff0c\u5bd2\u5149\u4e07\u70b9\uff0c\u771f\u5982\u53f0\u98ce\u626c\u6c99\uff0c\u8feb\u5f97\u536b \u58eb\u4eec\u7741\u4e0d\u5f00\u773c\uff0c\u4e00\u540d\u536b\u58eb\u53d7\u4e86\u5251\u4f24\uff0c\u695a\u662d\u5357\u4e5f\u8feb\u9000\u4e24\u6b65\uff0c\u51cc\u672a\u98ce\u53cd\u8eab\u8df3\u51fa\u573a\u5fc3\uff01 \u3000\u3000\u548c\u695a\u662d\u5357\u5939\u653b\u4ed6\u7684\u90a3\u540d\u4f7f\u53cc\u621f\u7684\u536b\u58eb\uff0c\u521a\u521a\u8d76\u5230\uff0c\u53cc\u621f\u4e00\u63a2\uff0c\u201c\u6fc0\u8361\u98ce\u96f7\u201d\uff0c\u5411\u51cc\u672a\u98ce \u8fce\u9762\u63d2\u53bb\uff0c\u51cc\u672a\u98ce\u9aa4\u89c9\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u731b\u7136\u628a\u524d\u51b2\u4e4b\u52bf\u715e\u4f4f\uff0c\u9640\u87ba\u4f3c\u7684\uff0c\u4e00\u4e2a\u201c\u9760\u5c71\u80cc\u201d\u95ea \u4e86\u56de\u6765\uff0c\u63a5\u7740\u201c\u62e8\u4e91\u89c1\u65e5\u201d\u5de6\u624b\u5411\u540e\u4e00\u6325\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u638c\u7f18\u7adf\u9707\u5728\u65b9\u5929\u753b\u621f\u7684\u719f\u94dc\u541e\u53e3\u4e0a\uff0c \u90a3\u540d\u536b\u58eb\uff0c\u5403\u4ed6\u8fd9\u4e00\u638c\uff0c\u9707\u5f97\u864e\u53e3\u70ed\u8fa3\u8fa3\u7684\uff0c\u8fde\u53f3\u81c2\u4e5f\u4e00\u9635\u9ebb\u6728\uff0c\u6b6a\u6b6a\u659c\u659c\uff0c\u76f4\u8dcc\u51fa\u53bb\uff0c\u6536 \u52bf\u4e0d\u4f4f\uff0c\u7adf\u7136\u4e5f\u4ece\u60ac\u5ca9\u4e4b\u4e0a\uff0c\u4f3c\u65ad\u7ebf\u98ce\u7b5d\u7684\u76f4\u8dcc\u4e0b\u53bb\uff01 \u3000\u3000\u51cc\u672a\u98ce\u638c\u5288\u5251\u6233\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u53c8\u63a5\u4e0a\u695a\u662d\u5357\u548c\u53e6\u5916\u4e09\u540d\u536b\u58eb\u3002\u4ed6\u5fc3\u75db\u5982\u5272\uff0c\u672c\u60f3\u8df3\u4e0b\u60ac \u5ca9\uff0c\u53bb\u5bfb\u5218\u90c1\u82b3\u7684\u5c38\u4f53\uff0c\u4f46\u4e00\u60f3\u6b7b\u8005\u5df2\u77e3\uff0c\u4e0d\u5982\u66ff\u5979\u62a5\u6b64\u8840\u4ec7\u3002\u695a\u662d\u5357\u626c\u624b\u53c8\u662f\u4e00\u628a\u77f3\u5f39\uff0c \u8feb\u5f97\u751a\u7d27\uff0c\u51cc\u672a\u98ce\u75db\u6012\u6210\u72c2\uff0c\u5ffd\u7136\u4ef0\u5929\u957f\u5578\uff0c\u9752\u94a2\u5251\u6bd4\u6210\u4e00\u9053\u94f6\u8679\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u56de\u8eab\u51b2\u53bb\uff0c \u5251\u98ce\u6fc0\u8361\uff0c\u77f3\u5f39\u4e71\u98de\uff0c\u54ea\u6709\u4e00\u9897\u6253\u5f97\u5230\u4ed6\u8eab\u4e0a\uff1f\u695a\u662d\u5357\u4e0d\u7531\u5927\u60ca\uff0c\u5fd9\u547d\u4e09\u540d\u536b\u58eb\uff0c\u534f\u540c\u81ea \u5df1\uff0c\u8054\u5251\u9632\u5b88\uff0c\u514d\u5f97\u88ab\u4ed6\u51b2\u4e0b\u60ac\u5d16\u3002 \u3000\u3000\u5218\u90c1\u82b3\u8dcc\u4e0b\u60ac\u5d16\u4e4b\u9645\uff0c\u6b66\u743c\u7476\u6b63\u81ea\u628a\u90a3\u73ed\u756a\u50e7\u6740\u5f97\u624b\u5fd9\u811a\u4e71\u3002\u5929\u9f99\u5251\u9635\uff0c\u7ed9\u77f3\u5929\u6210\u51fb\u6bd9 \u4e24\u4eba\u4e4b\u540e\uff0c\u9635\u52bf\u5df2\u7834\uff0c\u6b66\u743c\u7476\u5251\u62db\u50ac\u7d27\uff0c\u65bd\u5c55\u767d\u53d1\u9b54\u5973\u79d8\u4f20\u7684\u6740\u624b\uff0c\u4e00\u7247\u5bd2\u5149\uff0c\u4e0a\u4e0b\u7ffb\u98de\uff0c \u6709\u5982\u5954\u9706\u9a87\u7535\uff0c\u5269\u4e0b\u7684\u90a3\u516d\u540d\u756a\u50e7\uff0c\u672a\u53ca\u8054\u9632\u5df2\u7ed9\u6b66\u743c\u8c23\u6740\u5f97\u5934\u660f\u773c\u82b1\uff0c\u7740\u7740\u9000\u540e\u3002\u516d\u540d\u756a \u618e\u4e4b\u4e2d\uff0c\u5929\u96c4\u7985\u5e08\u662f\u5929\u8499\u5e08\u5f1f\uff0c\u8f88\u4efd\u6700\u9ad8\uff0c\u5728\u4e00\u73ed\u5e08\u4f84\u4e4b\u524d\uff0c\u4e0d\u7518\u88ab\u4e00\u4e2a\u5e74\u9752\u5c11\u5973\uff0c\u6740\u5f97\u5982 \u6b64\u72fc\u72c8\uff0c\u4ed7\u7740\u81ea\u5df1\u7ec3\u8fc7\u5927\u529b\u91d1\u94a2\u624b\u7684\u529f\u529b\uff0c\u53f3\u5251\u201c\u767d\u9e64\u68b3\u7fce\u201d\uff0c\u659c\u5207\u51fa\u53bb\uff0c\u5de6\u638c\u968f\u540e\uff0c\u5728\u957f \u5251\u63a9\u62a4\u4e4b\u4e0b\uff0c\u4e00\u62db\u201c\u91d1\u8c79\u63a2\u722a\u201d\uff0c\u76f4\u9012\u51fa\u6765\uff0c\u8981\u6293\u6b66\u743c\u7476\u80f8\u90e8\uff0c\u54ea\u6599\u4e00\u6293\u6293\u7a7a\uff0c\u6b66\u743c\u7476\u8eab\u5f62 \u5ffd\u7136\u4e0d\u89c1\uff0c\u4fa7\u9762\u7830\u7830\u4e24\u58f0\uff0c\u6b66\u743c\u7476\u5df2\u6293\u8d77\u4e00\u540d\u5587\u561b\uff0c\u5f80\u524d\u4e00\u8361\uff0c\u6b63\u6b63\u649e\u5728\u53e6\u4e00\u540d\u5587\u561b\u8eab\u4e0a\uff0c \u4e24\u4eba\u4e00\u9f50\u4ef0\u7ffb\u5012\u5730\uff0c\u6eda\u5728\u5929\u96c4\u7985\u5e08\u7684\u8db3\u65c1\uff0c\u72c2\u568e\u547c\u75db\uff0c\u5929\u96c4\u4e00\u811a\u8e0f\u53bb\uff0c\u6b63\u6b63\u8e0f\u5728\u4e00\u540d\u756a\u50e7\u7684 \u5934\u9885\u4e4b\u4e0a\uff0c\u4e5f\u5176\u4e0d\u610f\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u6b66\u743c\u7476\u5c31\u8d81\u4ed6\u4e00\u7a92\u7684\u5f53\u513f\uff0c\u5251\u82b1\u4e00\u7ed5\uff0c\u5929\u96c4\u731b\u89c9\u9888\u9645\u4e00 \u51c9\uff0c\u5de6\u8fb9\u4e00\u53ea\u8033\u6735\uff0c\u5df2\u548c\u8eab\u4f53\u5206\u5bb6\uff0c\u75db\u5f97\u4ed6\u4e00\u58f0\u602a\u53eb\uff0c\u6258\u5730\u5411\u540e\u53e3\u8df3\uff0c\u6070\u6070\u548c\u53e6\u4e00\u4e2a\u5e08\u4f84\u649e \u4e2a\u6b63\u770b\uff0c\u53cc\u53cc\u5815\u4e0b\u51b0\u6cb3\u3002\u6210\u5929\u633a\u5728\u6c99\u6f20\u4e0a\u66fe\u9886\u6559\u8fc7\u6b66\u743c\u7476\u672c\u4e8b\uff0c\u6b64\u9645\u53ea\u6c42\u81ea\u4fdd\uff0c\u53cc\u7b14\u5e26\u653b\u5e26 \u5b88\uff0c\u5c01\u7740\u95e8\u6237\u3002\u6b66\u743c\u7476\u6b63\u5408\u5fc3\u610f\uff0c\u4e0d\u7406\u6210\u5929\u633a\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u628a\u5269\u4e0b\u7684\u4e09\u540d\u756a\u50e7\uff0c\u5168\u90e8\u4e86\u7ed3\uff0c \u6b63\u60f3\u5bf9\u4ed8\u6700\u5f3a\u7684\u6210\u5929\u633a\uff0c\u731b\u89c1\u5bf9\u9762\u5c71\u5cf0\uff0c\u5218\u90c1\u82b3\u8dcc\u4e86\u4e0b\u6765\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u5979\u548c\u5218\u90c1\u82b3\u867d\u7136\u76f8\u5904 \u7684\u65f6\u65e5\u4e0d\u591a\uff0c\u77e5\u662f\u76f8\u4ea4\u9887\u539a\u3002\u5979\u4ed7\u7740\u767d\u53d1\u9b54\u5973\u7684\u72ec\u95e8\u8f7b\u529f\uff0c\u8ff3\u81ea\u5192\u9669\u8dc3\u4e0b\uff0c\u8dc3\u4e0b\u4e4b\u9645\uff0c\u8fd8\u53cd \u624b\u6253\u51fa\u94f6\u9488\u6697\u5668\uff0c\u5c06\u6210\u5929\u633a\u624b\u8155\u6253\u4f24\u3002\u6210\u5929\u633a\u89c1\u591a\u8bc6\u5e7f\uff0c\u77e5\u9053\u8fd9\u79cd\u6bd2\u9488\u7684\u5389\u5bb3\u3002\u6025\u5fd9\u95ed\u7740\u7a74 \u9053\uff0c\u9759\u5750\u5730\u4e0a\uff0c\u62e3\u8d77\u4e00\u628a\u5229\u5251\uff0c\u525c\u8089\u53d6\u9489,\u8fde\u9f50\u771f\u541b\u548c\u8f9b\u9f99\u5b50\u5728\u65c1\u8fb9\u62fc\u6b7b\u6076\u6218\uff0c\u4e5f\u987e\u4e0d\u5f97 \u4e86\uff01 \u3000\u3000\u8f9b\u9f99\u5b50\u542c\u5f97\u51cc\u672a\u98ce\u5927\u53eb\u4e4b\u540e\uff0c\u8ddf\u7740\u53c8\u770b\u89c1\u6b66\u743c\u7476\u4ece\u5c71\u5934\u98de\u4e0b\uff0c\u4e0d\u7981\u5927\u9a87\u3002\u5fae\u4e00\u758f\u795e\uff0c\u9f50 \u771f\u541b\u98ce\u96f7\u53cc\u5251\u5df2\u5206\u5fc3\u523a\u5230\uff0c\u8f9b\u9f99\u5b50\u54ac\u7259\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d\u4e00\u6643\u8eab\u8fde\u907f \u4e24\u62db\uff0c\u7136\u540e\u7528\u4e2a\u201c\u79cb\u6c34\u6a2a\u821f\u201d\u4e4b\u52bf\uff0c\u5411\u5de6\u4e00\u5c01\uff0c\u518d\u548c\u9f50\u771f\u541b\u62fc\u6b7b\u6076\u6218\u3002\u4e24\u4eba\u90fd\u5df2\u7b4b\u75b2\u529b\u7aed\uff0c \u597d\u4e0d\u5bb9\u6613\uff0c\u4f11\u606f\u5c11\u8bb8\uff0c\u624d\u7a0d\u7a0d\u6062\u590d\u5143\u6c14\uff0c\u8fd9\u756a\u82e6\u6218\uff0c\u53cc\u65b9\u90fd\u662f\u9669\u8c61\u73af\u751f\uff0c\u6740\u5f97\u795e\u667a\u660f\u8ff7\u3002\u8f9b \u9f99\u5b50\u53ea\u89c9\u8111\u80c0\u6b32\u88c2\uff0c\u81ea\u77e5\u65e0\u6cd5\u518d\u6218\uff0c\u4f46\u53c8\u4e0d\u80fd\u4e0d\u6218\uff0c\u731b\u5730\u54ac\u7259\uff0c\u60f3\u9053\uff1a\u6211\u7eb5\u6b7b\u4e5f\u4e0d\u80fd\u8ba9\u4ed6\u82df \u6d3b\uff0c\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u632f\u8d77\u7cbe\u795e\uff0c\u4e24\u81c2\u4e00\u6296\uff0c\u4f7f\u4e2a\u201c\u767d\u9e64\u51b2\u5929\u201d\uff0c\u62d4\u8d77\u4e24\u4e08\u591a\u9ad8\uff0c\u5728\u534a\u7a7a\u91cc\u500f\u5730 \u4e00\u58f0\u602a\u53eb\uff0c\u821e\u8d77\u4e08\u4f59\u957f\u77ed\u4e00\u6735\u5251\u82b1\uff1b\u9f50\u771f\u541b\u4e07\u6599\u4e0d\u5230\u8f9b\u9f99\u5b50\u5728\u4e45\u6218\u4e4b\u540e\uff0c\u5c1a\u80fd\u7528\u6b64\u6076\u62db\uff0c\u6b63 \u8981\u53f3\u624b\u56de\u5251\uff0c\u4e00\u4e2a\u201c\u7389\u5e26\u56f4\u8170\u201d\uff0c\u5411\u540e\u622a\u53bb\uff0c\u54ea\u77e5\u773c\u524d\u4e00\u6697\uff0c\u4eba\u5f71\u5df2\u7ecf\u98de\u6765\uff0c\u731b\u89c9\u5de6\u80a9\u5934 \u4e0a\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u4e2d\u4e86\u4eba\u5bb6\u4e00\u811a\uff0c\u75db\u4eba\u5fc3\u80ba\uff0c\u8fde\u201c\u54ce\u54df\u201d\u4e24\u5b57\u672a\u558a\u51fa\uff0c\u53f3\u80c1\u4e0b\u9661\u7684\u4e00\u9ebb\uff0c\u201c\u767d \u6d77\u7a74\u201d\u53c8\u7740\u4e86\u654c\u4eba\u6307\u6233\uff0c\u539f\u6765\u8f9b\u9f99\u5b50\u77e5\u9053\u9f50\u771f\u541b\u5251\u6cd5\u7cbe\u5999\uff0c\u98de\u8eab\u6251\u4e0b\u6765\u65f6\uff0c\u7528\u5251\u4f6f\u653b\uff0c\u51b7\u4e0d \u9632\u4e00\u811a\u8e6c\u5728\u4ed6\u7684\u80a9\u5934\u4e0a\uff0c\u8d81\u4ed6\u8eab\u8eaf\u4e00\u6643\u4e4b\u95f4\uff0c\u9a88\u4e2d\u98df\u6307\uff0c\u5411\u4ed6\u201c\u767d\u6d77\u7a74\u201d\u4e00\u6233\uff0c\u6233\u4e2a\u6b63\u7740\u3002 \u9f50\u771f\u541b\u6251\u5730\u4fbf\u5012\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u5f97\u610f\u72c2\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u5144\uff0c\u6211\u66ff\u4f60\u62a5\u4ec7\u4e86\uff01\u201d\u4e00\u5251\u5288\u4e0b\u3002\u54ea\u77e5\u9f50\u771f\u541b\u5341\u5c81\u5b66\u5251\uff0c \u81f3\u4eca\u5df2\u4e03\u5341\u591a\u5e74\uff0c\u4e03\u5341\u591a\u5e74\u529f\u592b\uff0c\u975e\u6bd4\u5bfb\u5e38\uff0c\u867d\u7136\u529b\u7aed\u7b4b\u75b2\u4e4b\u540e\uff0c\u53c8\u53d7\u91cd\u4f24\uff0c\u4f46\u4e34\u6b7b\u6323\u624e\uff0c \u72b9\u81ea\u5341\u5206\u5389\u5bb3\uff01\u7adf\u7528\u201c\u5367\u864e\u7ffb\u8eab\u201d\u4e4b\u52bf\uff0c\u817e\u5730\u4e00\u817f\uff0c\u76f4\u5411\u8f9b\u9f99\u5b50\u6863\u4e0b\u8e22\u53bb\uff0c\u8f9b\u9f99\u5b50\u62d4\u8eab\u6b32 \u8d77\uff0c\u5df2\u6765\u4e0d\u53ca\uff0c\u9f50\u771f\u541b\u5de6\u817f\u76f4\u8e6c\uff0c\u53f3\u817f\u6a2a\u626b\uff0c\u562d\u562d\u4e24\u58f0\u5c06\u4ed6\u8e22\u5012\u3002\u8f9b\u9f99\u5b50\u5b9d\u5251\u4e00\u63b7\uff0c\u4f7f\u51fa\u8fbe \u6469\u5251\u6cd5\u4e2d\u7684\u6700\u540e\u7edd\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u5b9d\u5251\u201c\u6ce2\u201d\u5f97\u4e00\u58f0\u63d2\u5165\u9f50\u771f\u541b\u80f8\u819b\uff0c\u81ea\u5df1\u4e5f\u7ffb\u7ffb\u6eda\u6eda\uff0c \u4e00\u5927\u53e3\u6de4\u8840\u5410\u4e86\u51fa\u6765\uff0c\u628a\u96ea\u5730\u90fd\u67d3\u7ea2\u4e86\uff01 \u3000\u3000\u90a3\u8fb9\u53a2\uff0c\u51cc\u672a\u98ce\u4e0e\u695a\u662d\u5357\u4e5f\u5230\u4e86\u751f\u6b7b\u7acb\u5224\u3001\u5f3a\u5b58\u5f31\u4ea1\u7684\u5730\u6b65\u3002\u90a3\u4e09\u540d\u539f\u5148\u56f4\u653b\u5218\u90c1\u82b3\u7684 \u536b\u58eb\uff0c\u6321\u4e0d\u4f4f\u51cc\u672a\u98ce\u51cc\u5389\u7684\u5251\u6cd5\uff0c\u8fde\u8fde\u540e\u9000\uff0c\u695a\u662d\u5357\u5927\u58f0\u559d\u9053\uff1a\u201c\u56f4\u7740\u4ed6\uff0c\u7f20\u6b7b\u4ed6\uff01\u4e0d\u8981\u677e \u52b2\uff01\u201d\u4ed6\u89c1\u51cc\u672a\u98ce\u9762\u4e0a\uff0c\u5df2\u6ef4\u4e0b\u9ec4\u8c46\u5927\u7684\u6c57\u73e0\uff0c\u77e5\u9053\u4ed6\u4e5f\u5230\u4e86\u5f3a\u5f29\u4e4b\u672b\u7684\u65f6\u5019\u4e86\u3002\u90a3\u4e09\u540d\u536b \u58eb\u88ab\u695a\u662d\u5357\u4e00\u518d\u50ac\u8feb\uff0c\u4e0d\u6562\u9003\u8dd1\uff0c\u53ea\u597d\u518d\u7ffb\u8eab\u62fc\u547d\u3002\u51cc\u672a\u98ce\u60f3\u8d77\u5218\u90c1\u82b3\u5c31\u662f\u88ab\u4ed6\u4eec\u4e09\u4eba\u8feb\u4e0b \u60ac\u5ca9\u7684\uff0c\u4e00\u89c1\u4ed6\u4eec\u56de\u8eab\u518d\u6218\uff0c\u987f\u65f6\u53cc\u77b3\u55b7\u706b\uff0c\u594b\u8d77\u795e\u5a01\uff0c\u9752\u94a2\u5251\u4e00\u5f15\uff0c\u5c06\u695a\u662d\u5357\u7684\u5b9d\u5251\u5f15\u8fc7 \u4e00\u8fb9\uff0c\u8eab\u5b50\u4e00\u8eac\uff0c\u5de6\u638c\u4e00\u4e2a\u201c\u5355\u638c\u5f00\u7891\u201d\u5411\u4e00\u540d\u536b\u58eb\u5288\u53bb\uff0c\u5494\u5693\u4e00\u58f0\uff0c\u628a\u4ed6\u7684\u9888\u9879\u6253\u6298\uff0c\u695a \u662d\u5357\u4e00\u4e2a\u65cb\u8eab\uff0c\u75be\u53d1\u4e24\u5251\uff0c\u51cc\u672a\u98ce\u8db3\u8ddf\u4e00\u65cb\uff0c\u8ba9\u695a\u662d\u5357\u7684\u5251\u950b\u5728\u8033\u8fb9\u524a\u8fc7\uff0c\u4ed6\u4e00\u8f6c\u8eab\uff0c\u4e00\u4e2a \u201c\u9f99\u5f62\u98de\u6b65\u201d\uff0c\u53c8\u7ed5\u5230\u53e6\u4e00\u540d\u536b\u58eb\u8eab\u65c1\uff0c\u5de6\u638c\u5411\u5916\u4e00\u6325\uff0c\u4ed6\u8fd9\u4e00\u638c\u542b\u7740\u767e\u6b65\u795e\u62f3\u7684\u771f\u529b\uff0c\u90a3 \u540d\u536b\u571f\u6025\u5fd9\u7528\u4e2a\u201c\u9e5e\u5b50\u94bb\u5929\u201d\u5411\u4e0a\u4e00\u5347\uff0c\u53ef\u662f\u54ea\u5f00\u91cc\u8fd8\u6765\u5f97\u53ca\uff1f\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u5df2\u7ed9\u638c\u950b\u626b \u4e2d\u53f3\u80ef\uff0c\u5728\u7a7a\u4e2d\u6253\u4e86\u4e2a\u6eda\uff0c\u5760\u4e0b\u4e86\u5c24\u5e95\u7684\u51b0\u6cb3\uff01 \u3000\u3000\u8fd8\u5269\u4e0b\u4e00\u4e2a\u536b\u58eb\uff0c\u9b42\u9b44\u4e0d\u9f50\uff0c\u4e0d\u7406\u695a\u662d\u5357\u7684\u5403\u559d\uff0c\u62bd\u8eab\u4fbf\u9000\uff0c\u51cc\u672a\u98ce\u731b\u5438\u4e00\u53e3\u4e39\u7530\u4e4b \u6c14\uff0c\u8fde\u4eba\u5e26\u5251\u821e\u6210\u4e00\u9053\u767d\u5149\uff0c\u98de\u743c\u8fc7\u53bb\uff0c\u8fd9\u4e00\u624b\u6b63\u662f\u5929\u5c71\u5251\u6cd5\u4e2d\u767b\u5cf0\u9020\u6781\u7684\u529f\u592b\uff0c\u540d\u5524\u201c\u6d41 \u661f\u8d76\u6708\u201d\uff0c\u53ea\u89c1\u767d\u5149\u4e00\u95ea\uff0c\u5982\u7bad\u79bb\u5f26\uff0c\u90a3\u540d\u536b\u58eb\uff0c\u5982\u4f55\u6321\u5f97\uff1f\u767b\u65f6\u7ed9\u51cc\u672a\u98ce\u4e00\u5251\u81ea\u540e\u5fc3\u76f4\u900f \u9a79\u5fc3\uff01 \u3000\u3000\u4e00\u573a\u6076\u6218\uff0c\u51cc\u672a\u98ce\u8fde\u6bd9\u4e03\u540d\u5927\u5185\u9ad8\u624b\uff0c\u547c\u5438\u7d27\u4fc3\uff0c\u5168\u8eab\u6eda\u70ed\uff0c\u51b0\u6cb3\u51b7\u6c14\uff0c\u9635\u9635\u88ad\u6765\uff0c\u4e0d \u89c9\u4e00\u8fde\u6253\u4e86\u51e0\u4e2a\u5bd2\u5664\uff0c\u5934\u8111\u80c0\u95f7\uff0c\u695a\u662d\u5357\u5530\uff01\u5530\uff01\u5530\uff01\u8fde\u523a\u6570\u5251\uff0c\u51cc\u672a\u98ce\u7740\u7740\u9000\u540e\uff0c\u7adf\u7ed9\u4ed6 \u8feb\u81f3\u6073\u5d16\u8fb9\u7f18\uff01\u695a\u662d\u5357\u6599\u4ed6\u6cb9\u5c3d\u706f\u67af\uff0c\u5fc3\u4e2d\u72c2\u559c\uff0c\u7eb5\u58f0\u72de\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u51cc\u672a\u98ce\uff0c\u4f60\u4e5f\u6709\u4e86\u4eca \u65e5\uff01\u201d\u6e38\u9f99\u5251\u5251\u950b\u4e00\u6307\uff0c\u76f4\u53d6\u51cc\u672a\u98ce\u54bd\u5589\uff01 \u3000\u3000\u4e0d\u6599\uff0c\u51cc\u672a\u98ce\u95fb\u8a00\u77bf\u7136\u9192\u8d77\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u53db\u8d3c\uff0c\u4f60\u60f3\u5728\u6211\u624b\u4e0a\u8ba8\u5f97\u597d\u53bb\uff1f\u201d\u5251\u628a\u731b \u7ffb\uff0c\u547c\u5730\u5708\u8f6c\u8eab\u6765\uff0c\u9752\u94a2\u5251\u75be\u53d1\u5982\u98ce\uff0c\u53cd\u64a9\u654c\u4eba\u8155\u5e95\uff0c\u5e26\u6302\u8170\u80c1\uff0c\u4e00\u62db\u4e24\u5f0f\uff0c\u865a\u4e2d\u5957\u5b9e\uff0c\u628a \u695a\u662d\u5357\u653b\u52bf\u8f7b\u8f7b\u89e3\u4e86\u3002\u695a\u662d\u5357\u5927\u5403\u4e00\u60ca\uff0c\u7ed9\u4ed6\u53cd\u8f6c\u6765\u8feb\u9000\u51e0\u6b65\uff0c\u4ed7\u5b9d\u5251\u7684\u5a01\u529b\uff0c\u633d\u8d77\u4e00\u4e2a\u5251 \u82b1\uff0c\u62a4\u7740\u80f8\u8179\uff0c\u5251\u62db\u4e00\u53d8\uff0c\u4f7f\u51fa\u5929\u5c71\u5251\u6cd5\u7684\u9632\u8eab\u5251\u672f\uff0c\u7d27\u7d27\u5c01\u95ed\u95e8\u6237\u3002 \u3000\u3000\u51cc\u672a\u98ce\u672c\u5c06\u7cbe\u6c14\u7115\u6563\uff0c\u7ed9\u695a\u662d\u5357\u4e00\u6fc0\uff0c\u60f3\u8d77\u5218\u90c1\u82b3\u7ed9\u4ed6\u8feb\u6b7b\uff0c\u4e0d\u77e5\u54ea\u91cc\u6765\u7684\u6c14\u529b\uff0c\u7cbe\u795e \u9661\u632f\uff0c\u4e00\u62db\u7d27\u4f3c\u4e00\u62db\uff0c\u5251\u5149\u970d\u970d\uff0c\u5251\u5251\u76f4\u6307\u695a\u662d\u5357\u8981\u5bb3\uff01 \u3000\u3000\u8fd9\u65f6\uff0c\u6155\u58eb\u5854\u683c\u5c71\u4e0a\uff0c\u552f\u95fb\u6714\u98ce\u6012\u53f7\uff0c\u6d41\u51b0\u88c2\u54cd\uff0c\u695a\u662d\u5357\u5e26\u6765\u7684\u5341\u51e0\u540d\u5927\u5185\u536b\u58eb\uff0c\u548c\u5929 \u8499\u7985\u5e08\u5e26\u6765\u7684\u516b\u4e2a\u5929\u9f99\u6d3e\u9ad8\u624b\uff0c\u51e0\u4e4e\u5168\u90e8\u6b7b\u4ea1\uff01\u53ea\u5269\u4e0b\u6210\u5929\u633a\u4e00\u4eba\u5728\u51b0\u6cb3\u4e4b\u8fb9\u6253\u5750\uff0c\u8c03\u5300\u547c \u5438\uff0c\u7597\u51b6\u6bd2\u9488\u4e4b\u4f24\uff0c\u51cc\u672a\u98ce\u548c\u695a\u662d\u5357\u90fd\u4e0d\u77e5\u81ea\u5df1\u7684\u4eba\u6253\u5f97\u600e\u6837\u3002\u53ea\u89c9\u7a7a\u5c71\u5c91\u5bc2\uff0c\u6c93\u65e0\u4eba\u58f0\uff0c \u5fc3\u4e2d\u90fd\u6697\u6697\u53d1\u614c\uff0c\u51cc\u672a\u98ce\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u867d\u7136\u5fc3\u60ac\u6218\u53cb\u5b89\u5371\uff0c\u5251\u62db\u5374\u662f\u4e1d\u6beb\u4e0d\u7f13\uff0c\u695a\u662d\u5357\u5927 \u53eb\u51e0\u58f0\uff0c\u6beb\u65e0\u56de\u5e94\uff0c\u51b7\u6c57\u6c81\u808c\uff0c\u5b9d\u5251\u4e00\u5c01\uff0c\u731b\u5730\u5411\u540e\u8dc3\u53bb\uff0c\u54ea\u6599\u4ed6\u8eab\u5f62\u4e00\u52a8\uff0c\u5934\u9876\u5251\u98ce\u98d2 \u7136\uff0c\u4ed6\u4f38\u5251\u4e00\u683c\uff0c\u53ea\u89c1\u51cc\u672a\u98ce\u5df2\u8d76\u8fc7\u524d\u5934\uff0c\u633a\u5251\u622a\u7740\u4e86\u4ed6\u7684\u9053\u8def\uff01\u695a\u662d\u5357\u6c57\u6bdb\u5012\u575a\uff0c\u5927\u58f0\u53eb \u9053\uff1a\u201c\u51cc\u672a\u98ce\uff0c\u54b1\u4eec\u4e0d\u8bba\u5982\u4f55\uff0c\u90fd\u662f\u540c\u95e8\u4e00\u8109\uff0c\u4eca\u65e5\u51b0\u6cb3\u4e4b\u6218\uff0c\u6240\u6709\u7684\u4eba\u90fd\u5df2\u7684\u4ea1\uff0c\u53ea\u6709\u4f60 \u6211\u5e78\u5b58\uff0c\u4f55\u5fc5\u8fd8\u8981\u82e6\u62fc\u4e0b\u53bb\uff1f\u4e0d\u5982\u5404\u8d70\u5404\u7684\uff0c\u514d\u81f4\u4e24\u8d25\u4ff1\u4f24\uff01\u201d\u51cc\u672a\u98ce\u4e0d\u7406\u4e0d\u776c\uff0c\u9752\u94a2\u5251\u8fc5 \u5982\u63a3\u5c6f\uff0c\u626c\u7a7a\u4e00\u5212\uff0c\u76f4\u70b9\u654c\u624b\u8109\u95e8\uff0c\u695a\u662d\u5357\u4e00\u4e2a\u76d8\u65cb\uff0c\u6e38\u9f99\u5251\u4e00\u8361\u4e00\u5708\uff0c\u8d25\u91cc\u53cd\u51fb\uff0c\u51cc\u672a\u98ce \u53f1\u54a4\u4e00\u58f0\uff0c\u6b3a\u8eab\u76f4\u8fdb\uff0c\u5251\u950b\u5df2\u5728\u695a\u662d\u5357\u624b\u8155\u4e0a\u5212\u4e86\u4e00\u9053\u53e3\u5b50\uff0c\u695a\u662d\u5357\u8d1f\u75db\u72c2\u66dd\uff0c\u9ec4\u8c46\u5927\u7684\u6c57 \u73e0\u70b9\u70b9\u6ef4\u4e0b\uff0c\u72c2\u53eb\u9053\uff1a\u201c\u51cc\u672a\u98ce\uff0c\u4f60\u771f\u4e0d\u5ff5\u540c\u95e8\u4e4b\u60c5\uff1f\u201d\u51cc\u672a\u98ce\u624b\u8155\u4e00\u7ffb\uff0c\u559d\u9053\uff1a\u201c\u53db\u8d3c\u770b \u5251\uff01\u201d\u5530\u7684\u53c8\u662f\u4e00\u5251\u523a\u53bb\uff0c\u695a\u662d\u5357\u5251\u4ea4\u5de6\u624b\uff0c\u4e00\u62db\u201c\u4e58\u9f99\u5f15\u51e4\u201d\uff0c\u594b\u529b\u6321\u5f00\uff0c\u51cc\u672a\u98ce\u5251\u8d70\u8fde \u73af\uff0c\u653b\u52bf\u7ef5\u7ef5\u4e0d\u7edd\uff01\u695a\u662d\u5357\u53c8\u7ed9\u4ed6\u8feb\u9000\u51e0\u6b65\uff0c\u9669\u8c61\u73af\u751f\uff0c\u5934\u9762\u9752\u7b4b\u6bd5\u73b0\u3002 \u3000\u3000\u51cc\u672a\u98ce\u8fdb\u4e00\u6b65\uff0c\u695a\u662d\u5357\u9000\u4e00\u6b65\uff0c\u6e10\u6e10\u53c8\u8feb\u5230\u4e86\u6073\u5d16\u4e4b\u8fb9\u3002\u8bba\u8fd9\u65f6\u7684\u5f62\u52bf\uff0c\u51cc\u672a\u98ce\u539f\u53ef\u65e9 \u628a\u695a\u662d\u5357\u6740\u6389\uff0c\u4f46\u51cc\u672a\u98ce\u60f3\u8981\u4e3a\u5218\u90c1\u82b3\u62a5\u4ec7\uff0c\u60f3\u7167\u6837\u628a\u695a\u662d\u5357\u8feb\u4e0b\u60ac\u5ca9\uff0c\u56e0\u6b64\u4fbf\u5982\u7075\u732b\u620f \u9f20\uff0c\u6b65\u6b65\u8ffd\u8feb\uff0c\u695a\u662d\u5357\u5927\u6025\uff0c\u6e38\u9f99\u5251\u8fde\u8d70\u9669\u62db\uff0c\u62fc\u547d\u62a2\u653b\uff0c\u51cc\u672a\u98ce\u51b7\u7b11\u4e00\u58f0\uff0c\u55d6\u5730\u4e00\u4f0f\u8eab\uff0c \u5229\u5251\u75be\u5982\u95ea\u7535\uff0c\u5bf9\u51c6\u54bd\u5589\uff0c\u76f4\u523a\u8fc7\u6765\uff0c\u8fd9\u5251\u53c8\u51c6\u53c8\u6df1\uff0c\u695a\u662d\u5357\u867d\u660e\u77e5\u518d\u51e0\u6b65\uff0c\u5c31\u8981\u8dcc\u4e0b\u60ac \u5ca9\uff0c\u4f46\u82e5\u4e0d\u9000\uff0c\u5f53\u573a\u5c31\u88ab\u5229\u5251\u7a7f\u5589\uff0c\u8feb\u5f97\u9000\u540e\u4e00\u6b65\uff0c\u7528\u5251\u4e00\u5c01\u3002\u51cc\u672a\u98ce\u970d\u5730\u6536\u62db\uff0c\u864e\u773c\u4e00 \u7741\uff0c\u5251\u8bc0\u4e00\u9886\uff0c\u5530\u5730\u53c8\u662f\u4e00\u5251\uff0c\u63a2\u8eab\u76f4\u53d6\uff0c\u5251\u624e\u80f8\u819b\uff0c\u695a\u662d\u5357\u5f80\u540e\u53c8\u9000\u4e86\u4e00\u6b65\uff0c\u7528\u5251\u4e00\u67b6\u3002 \u51cc\u672a\u98ce\u8fd9\u4e00\u56de\u5374\u4e0d\u6536\u62db\uff0c\u5251\u5c16\u4e00\u6c89\uff0c\u53cd\u624b\u4e00\u53d8\u62db\uff0c\u65cb\u8eab\u523a\u624e\uff0c\u501f\u8fd9\u7529\u81c2\u56de\u8eab\u4e4b\u529b\uff0c\u7b2c\u4e09\u62db\u659c \u80a9\u5e26\u81c2\uff0c\u72e0\u72e0\u626b\u6765\uff0c\u695a\u662d\u5357\u4e0d\u6562\u786c\u63a5\uff0c\u4f0f\u8eab\u4e00\u65cb\uff0c\u7a9c\u540e\u6570\u6b65\uff0c\u731b\u89c9\u5de6\u8db3\u8db3\u8ddf\u8e0f\u7a7a\uff0c\u534a\u8eab\u5df2\u6302 \u5728\u60ac\u5ca9\u4e4b\u5916\uff0c\u6025\u6025\u51dd\u8eab\uff0c\u51cc\u672a\u98ce\u9752\u94a2\u5251\u500f\u5730\u4e00\u6307\uff0c\u5251\u5c16\u95ea\u95ea\uff0c\u770b\u770b\u70b9\u5230\u695a\u662d\u5357\u7684\u5fc3\u7a9d\uff01 \u3000\u3000\u695a\u662d\u5357\u95ed\u76ee\u5f85\u6b7b\uff0c\u5ffd\u542c\u51cc\u672a\u98ce\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u5229\u5251\u5815\u5730\uff0c\u695a\u662d\u5357\u7741\u773c\u4e00\u770b\uff1a\u53ea\u89c1\u51cc\u672a\u98ce \u8eab\u5b50\u6296\u4e2a\u4e0d\u4f4f\uff0c\u8138\u4e0a\u808c\u8089\u6536\u7f29\uff0c\u73b0\u51fa\u6781\u75db\u82e6\u7684\u795e\u60c5\u3002\u695a\u662d\u5357\u72b9\u81ea\u4e0d\u6562\u5984\u52a8\uff0c\u518d\u770b\u51cc\u672a\u98ce\u6296\u5f97 \u66f4\u751a\uff0c\u819d\u76d6\u4e0b\u5f2f\uff0c\u770b\u770b\u5c31\u8981\u503e\u5012\uff0c\u695a\u662d\u5357\u5927\u559c\u8fc7\u671b\uff0c\u53cd\u8eab\u8dc3\u51fa\uff0c\u4e00\u638c\u51fb\u53bb\uff01\u51cc\u672a\u98ce\u7adf\u6beb\u65e0\u62b5 \u6297\uff0c\u7ed9\u638c\u529b\u9707\u5012\u5730\u4e0a\uff01 \u3000\u3000\u539f\u6765\u51cc\u672a\u98ce\u56e0\u5c11\u5e74\u65f6\u5019\uff0c\u72ec\u4e0a\u5929\u5c71\uff0c\u5728\u51b0\u65e0\u96ea\u5730\u4e4b\u4e2d\uff0c\u53d7\u5bd2\u6c14\u4fb5\u8680\uff0c\u5f97\u4e86\u4e00\u79cd\u602a\u75c5\uff0c\u5e38 \u5e38\u7a81\u7136\u4f1a\u53d1\u751f\u75c9\u631b\uff08\u62bd\u7b4b\uff09\uff0c\u540e\u6765\u6b66\u529f\u65e5\u76ca\u6df1\u6e5b\uff0c\u75c9\u631b\u75c7\u5df2\u4e0d\u5e38\u53d1\u4e86\uff0c\u53ef\u662f\u5076\u7136\u8fd8\u4f1a\u7a81\u5982\u5176 \u6765\u5730\u53d1\u4f5c\uff0c\u50cf\u4ee5\u524d\u4ed6\u5728\u5434\u4e09\u6302\u7684\u6c34\u7262\u4e2d\u5c31\u66fe\u53d1\u4f5c\u8fc7\u4e00\u6b21\uff0c\u8fd9\u6b21\u5728\u51b0\u6cb3\u4e4b\u65c1\uff0c\u82e6\u6218\u4e00\u65e5\uff0c\u7528\u529b \u592a\u751a\uff0c\u51fa\u6c57\u8fc7\u591a\uff0c\u5bd2\u6c14\u53c8\u6d53\uff0c\u7adf\u7136\u5728\u6700\u540e\u5173\u5934\uff0c\u75c9\u631b\u75c7\u7a81\u7136\u53d1\u4f5c\uff0c\u7edd\u4e16\u6b66\u529f\uff0c\u7adf\u81ea\u65e0\u80fd\u4e3a \u529b\uff01 \u3000\u3000\u695a\u662d\u5357\u6251\u8eab\u4e0a\u524d\uff0c\u7528\u91cd\u624b\u6cd5\u628a\u51cc\u672a\u98ce\u7684\u201c\u6655\u7729\u7a74\u201d\u5c01\u4f4f\uff0c\u7eb5\u58f0\u72c2\u7b11\uff0c\u968f\u624b\u5728\u51b0\u5d16\u4e4b\u8fb9\u6298 \u4e0b\u5c71\u85e4\uff0c\u5c06\u51cc\u672a\u98ce\u6346\u5f97\u7ed3\u7ed3\u5b9e\u5b9e\uff0c\u8fd9\u79cd\u5c71\u85e4\u575a\u97e7\u5f02\u5e38\uff0c\u7eb5\u8bb8\u51cc\u672a\u98ce\u9192\u6765\uff0c\u4e5f\u8981\u7ecf\u8fc7\u4e00\u9635\u6323 \u624e\uff0c\u800c\u4e00\u6323\u624e\u4e00\u5b9a\u53c8\u4f1a\u88ab\u695a\u662d\u5357\u53d1\u73b0\u3002\u518d\u65bd\u8fa3\u624b\uff0c\u6240\u4ee5\u695a\u662d\u5357\u662f\u6709\u6043\u65e0\u6050\u3002 \u3000\u3000\u8fd9\u65f6\u695a\u662d\u5357\u4e5f\u5df2\u8170\u9178\u9aa8\u8f6f\uff0c\u773c\u775b\u53d1\u9ed1\uff0c\u4f11\u606f\u4e86\u4e00\u4f1a\uff0c\u5ffd\u542c\u5f97\u6210\u5929\u633a\u5c16\u58f0\u53eb\u5524\uff0c\u695a\u662d\u5357\u631f \u7740\u51cc\u672a\u98ce\u8d70\u53bb\uff0c\u53ea\u89c1\u6210\u5929\u633a\u4e5f\u662f\u9762\u8272\u60e8\u767d\uff0c\u795e\u60c5\u72fc\u72c8\u3002\u695a\u662d\u5357\u60ca\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6837\u4e86\uff1f\u201d\u6210 \u5929\u633a\u4e00\u89c1\u695a\u662d\u5357\u6349\u4e86\u51cc\u672a\u98ce\uff0c\u4e0d\u7981\u5927\u559c\uff0c\u7cbe\u795e\u4e00\u632f\uff1a\u7b54\u9053\uff1a\u201c\u6211\u4e2d\u4e86\u5973\u8d3c\u7684\u4e00\u53e3\u6bd2\u9488\uff0c\u5e78\u5f97 \u6211\u5185\u529f\u5c1a\u6df1\uff0c\u8fd0\u6c14\u884c\u8840\uff0c\u73b0\u5728\u5df1\u65e0\u4e8b\u4e86\u3002\u4f60\u5462\uff1f\u600e\u4e48\u5c45\u7136\u6349\u7740\u4e86\u51cc\u672a\u98ce\uff1f\u201d\u695a\u662d\u5357\u5f97\u610f\u6d0b \u6d0b\uff0c\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u662f\u4ed6\u7684\u5e08\u5144\u561b\uff0c\u4ed6\u7684\u90a3\u5957\u5251\u6cd5\uff0c\u5982\u4f55\u6597\u5f97\u8fc7\u6211\uff1f\u201d\u6210\u5929\u633a\u5c06\u4fe1\u5c06 \u7591\uff0c\u8fde\u58f0\u9053\u8d3a\uff0c\u695a\u662d\u5357\u7b11\u9053\uff1a\u201c\u6211\u4eec\u867d\u6298\u4e86\u6570\u767e\u7cbe\u9a91\uff0c\u5341\u4f59\u9ad8\u624b\uff0c\u6349\u5230\u4e86\u4ed6\uff0c\u4e5f\u62b5\u5f97\u8fc7 \u4e86\uff01\u201d \u3000\u3000\u695a\u662d\u5357\u4e0e\u6210\u5929\u633a\u6e38\u76ee\u56db\u987e\uff0c\u53ea\u89c1\u6d41\u51b0\u6bb7\u7ea2\uff0c\u5c38\u6a2a\u904d\u5730\uff0c\u95f4\u6709\u65ad\u65ad\u7eed\u7eed\u7684\u5fae\u5f31\u547b\u541f\u58f0\u4f20\u4eba \u8033\u9f13\u3002\u695a\u662d\u5357\u6b63\u60f3\u53eb\u6210\u5929\u633a\u641c\u7d22\u4e00\u4e0b\uff0c\u770b\u654c\u6211\u53cc\u65b9\u6b7b\u4f24\u4e86\u591a\u5c11\u4eba\uff0c\u82e5\u53d1\u73b0\u6709\u8d1f\u4f24\u672a\u6b7b\u7684\u654c \u4eba\uff0c\u8fd8\u53ef\u518d\u8865\u4ed6\u4e00\u5251\u3002\u5ffd\u542c\u5f97\u5c71\u8c37\u4e0b\u9690\u9690\u6709\u9a6c\u8e44\u58f0\uff0c\u6210\u5929\u633a\u8df3\u8d77\u6765\u9053\uff1a\u201c\u6076\u6597\u4e00\u65e5\uff0c\u6211\u5df2\u7d2f \u5f97\u8981\u6b7b\u4e86\uff0c\u82e5\u6765\u7684\u662f\u654c\u4eba\uff0c\u6211\u4eec\u5982\u4f55\u5403\u5f97\u6d88\uff1f\u8fd8\u662f\u5feb\u70b9\u8d70\u5427\uff01\u201d\u695a\u662d\u5357\u867d\u7136\u5634\u786c\uff0c\u5176\u5b9e\u4e5f\u662f \u7b4b\u75b2\u529b\u5026\uff0c\u65e0\u80fd\u518d\u6218\u3002\u5f20\u671b\u4e00\u4e0b\uff0c\u89c1\u51b0\u6cb3\u4e4b\u8fb9\uff0c\u8f9b\u9f99\u5b50\u77f3\u5929\u6210\u9f50\u771f\u541b\u4e09\u4eba\u6ee1\u8eab\u6d74\u76bf\uff0c\u4ed6\u8dd1\u53bb \u6bcf\u4eba\u8e22\u4e86\u4e24\u811a\uff0c\u4e09\u4eba\u54fc\u90fd\u4e0d\u54fc\u4e00\u58f0\uff0c\u663e\u89c1\u6b7b\u4e86\uff0c\u695a\u662d\u5357\u5728\u8f9b\u9f99\u5b50\u8eab\u4e0a\u641c\u4e86\u4e00\u9635\uff0c\u7a7a\u624b\u62bd\u51fa\uff0c \u5ffd\u7136\u628a\u51cc\u672a\u98ce\u70b9\u9192\uff0c\u55d6\u7684\u62d4\u51fa\u5251\u6765\uff0c\u5251\u950b\u4e00\u6325\uff0c\u628a\u51cc\u672a\u98ce\u53f3\u624b\u7684\u62c7\u6307\u524a\u6389\uff0c\u75af\u72c2\u53eb\u9053\uff1a\u201c\u53eb \u4f60\u7ec8\u751f\u4e0d\u80fd\u4f7f\u5251\uff01\u201d\u6210\u5929\u633a\u9a87\u7136\u76f8\u89c6\uff0c\u695a\u662d\u5357\u6602\u5934\u72c2\u7b11\uff0c\u5bf9\u6210\u5929\u633a\u9053\uff1a\u201c\u8f9b\u3001\u9f50\u4e8c\u4eba\u6b7b\u6389\uff0c \u51cc\u672a\u98ce\u53c8\u6210\u6b8b\u5e9f\uff0c\u4ece\u4eca\u800c\u540e\uff0c\u5f53\u4eca\u5929\u4e0b\uff0c\u6ca1\u6709\u4eba\u7684\u5251\u6cd5\u518d\u6bd4\u5f97\u4e0a\u6211\u4e86\uff01\u201d\u6210\u5929\u633a\u4e0d\u89c9\u5fc3\u5bd2\uff0c \u60f3\u9053\uff1a\u51cc\u672a\u98ce\u3001\u8f9b\u9f99\u5b50\u4e5f\u8fd8\u7f62\u4e86\uff0c\u9f50\u771f\u541b\u662f\u81ea\u5df1\u4eba\uff0c\u4ed6\u5c45\u7136\u4e5f\u5e78\u707e\u4e50\u7978\uff01\u51cc\u672a\u98ce\u75db\u5f7b\u5fc3\u813e\uff0c \u5374\u54fc\u4e5f\u4e0d\u54fc\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u51ed\u4f60\u7684\u5251\u6cd5\uff0c\u4fbf\u60f3\u6a2a\u884c\u5929\u4e0b\uff1f\u54fc\uff0c\u90a3\u662f\u505a\u68a6\uff01\u201d\u695a\u662d\u5357\u7791\u76ee\u53eb \u9053\uff1a\u201c\u4f60\u8bf4\u8bf4\u770b\u8fd8\u6709\u8c01\u6bd4\u5f97\u4e0a\u6211\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u5e08\u7236\u7684\u62f3\u7ecf\u5251\u8bc0\uff0c\u6211\u65e9\u6536\u85cf\u597d\u4e86\uff0c\u6211\u4f20\u7ed9 \u8c01\uff0c\u8c01\u4fbf\u8981\u80dc\u8fc7\u4f60\uff01\u201d\u695a\u662d\u5357\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u8d77\u8f9b\u9f99\u5b50\u4ee5\u524d\u5bf9\u4ed6\u8bf4\u8fc7\u5728\u5929\u5c71\u9a86\u9a7c\u5cf0\u9047\u89c1\u51cc\u672a\u98ce \u7684\u4e8b\uff0c\u60f3\u9053\uff1a\u201c\u54fc\uff0c\u539f\u6765\u4ed6\u4e00\u5230\u56de\u7586\uff0c\u4fbf\u4e0a\u5929\u5c71\uff0c\u53d6\u5230\u4e86\u5e08\u7236\u7684\u9057\u4e66\u3002\u201d\u4ed6\u4f38\u624b\u8981\u641c\u51cc\u672a \u98ce\uff0c\u51cc\u672a\u98ce\u201c\u5478\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u53e3\u6d53\u75f0\u7a81\u7136\u55b7\u51fa\uff0c\u695a\u662d\u5357\u4e00\u58f0\u72c2\u547c\uff0c\u5de6\u773c\u773c\u73e0\uff0c\u7adf\u7ed9\u6d53\u75f0\u5c04 \u788e\uff0c\u8840\u6d41\u6ee1\u9762\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5728\u91cd\u4f24\u5927\u75c5\u4e4b\u4e2d\uff0c\u5185\u529f\u5c42\u7136\u8fd8\u662f\u5982\u6b64\u6df1\u6e5b\uff01\u695a\u662d\u5357\u6124\u6781\u4e00\u6233\uff0c\u53c8\u628a\u51cc\u672a\u98ce\u7684\u6655\u7729 \u7a74\u5c01\u7740\u3002\u6210\u5929\u633a\u9053\uff1a\u201c\u4f55\u4e0d\u628a\u4ed6\u6740\u6389\uff01\u201d\u695a\u662d\u5357\u4e00\u9762\u624e\u4f24\uff0c\u4e00\u9762\u6447\u4e86\u6447\u5934\u3002\u8fd9\u65f6\u5c71\u8c37\u4e0b\u5df2\u6709 \u9a6c\u5636\u4e4b\u58f0\u3002\u695a\u662d\u5357\u631f\u7740\u51cc\u672a\u98ce\u817e\u8eab\u4fbf\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u5feb\u8d70\uff01\u201d\u4e0e\u6210\u5929\u633a\u4e8c\u4eba\u65bd\u5c55\u8f7b\u529f\uff0c\u7ffb\u5c71\u9003 \u8dd1\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u77f3\u5929\u6210\u4e8c\u4eba\u4f24\u91cd\u660f\u8ff7\uff0c\u5176\u5b9e\u672a\u6b7b\uff0c\u7ed9\u695a\u662d\u5357\u8e22\u4e86\u4e24\u811a\uff0c\u60a0\u60a0\u9192\u8f6c\uff0c\u5f7c\u6b64\u76f8\u671b\uff0c\u4e0d \u89c9\u54ed\u51fa\u58f0\u6765\uff0c\u8f9b\u9f99\u5b50\u5728\u5730\u4e0a\u6162\u6162\u79fb\u52a8\uff0c\u6328\u8fd1\u5e08\u5144\uff01\u4f38\u624b\u5c06\u4ed6\u62b1\u7740\uff0c\u65ad\u65ad\u7eed\u7eed\u5730\u8bf4\u9053\uff1a\u201c\u5e08 \u5144\uff0c\u6211\u77e5\u9519\u4e86\uff01\u201d\u77f3\u65e0\u6210\u9053\uff1a\u201c\u77e5\u9519\u4fbf\u597d\u3002\u201d\u4ed6\u4eec\u5e08\u5144\u5f1f\u4fe9\u4e00\u5411\u9694\u819c\uff0c\u800c\u4eca\u4e34\u6b7b\u62e5\u62b1\uff0c\u53c8\u662f \u8f9b\u9178\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u77f3\u5929\u6210\u9053\uff1a\u201c\u6211\u662f\u65e0\u8bba\u5982\u4f55\u4e0d\u80fd\u6d3b\u4e86\uff0c\u4f60\u82e5\u80fd\u4fa5\u5e78\u9003\u751f\uff0c\u8bf7\u4ee3\u6211\u8fd8\u4e24\u4e2a\u5fc3 \u613f\uff0c\u4e00\u4e2a\u662f\u5c06\u6211\u7684\u9ab8\u9aa8\u62fe\u53bb\u846c\u5728\u5251\u9601\u4e4b\u4e0a\uff0c\u548c\u6211\u5e08\u5144\u6842\u5929\u6f9c\uff0c\u846c\u5728\u4e00\u5904\u3002\u53e6\u4e00\u4e2a\u662f\u671b\u4f60\u6307\u70b9 \u4e00\u4e0b\u6842\u4ef2\u660e\u3002\u201d\u8f9b\u9f99\u5b50\u5185\u529f\u6df1\u6e5b\uff0c\u4e00\u65f6\u5c1a\u6b7b\u4e0d\u6389\uff0c\u4fa7\u8033\u56db\u542c\uff0c\u53ea\u542c\u4e00\u9635\u9a6c\u5636\u4e4b\u58f0\uff0c\u4e0d\u4e45\u53c8\u6e10 \u6e10\u9759\u5bc2\uff0c\u8f9b\u9f99\u5b50\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5373\u4f7f\u662f\u8349\u539f\u9a6c\u5e2e\uff0c\u4e5f\u53ea\u80fd\u5728\u8c37\u4e2d\u884c\u8d70\uff0c\u7edd\u4e0a\u4e0d\u6765\u3002\u800c\u4e14\u6211\u5982\u6b64 \u91cd\u4f24\u3002\u4fbf\u6709\u7075\u829d\u4ed9\u8349\uff0c\u4e5f\u96be\u6551\u6cbb\u3002\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d\u4ed6\u5267\u75db\u653b\u5fc3\uff0c\u5ffd\u7136\u773c\u775b\u4e00\u4eae\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u95ee\u541b\u4f55\u4e8b\u7d22\u6000\u62b1\uff0c\u6709\u613f\u96be\u507f\u76ee\u672a\u7791\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5eff\u516d\u7ae0\u3000\u54c1\u8317\u8c08\u5fc3\u3000\u559c\u6709\u826f\u670b\u6c38\u8ba4\u591c\u3000\u56e0\u8bcd\u5bc4\u610f\u3000\u6c38\u7559\u77e5\u5df2\u5728\u4eba\u95ea \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u4e8c\u5341\u516d\u56de \u54c1\u8317\u8c08\u5fc3\u3000\u559c\u6709\u826f\u670b\u6c38\u8ba4\u591c \u56e0\u8bcd\u5bc4\u610f\u3000\u6c38\u7559\u77e5\u5df2\u5728\u4eba\u95ea \u3000\u3000\u8f9b\u9f99\u5b50\u773c\u775b\u4e00\u4eae\uff0c\u539f\u6765\u662f\u770b\u89c1\u9f50\u771f\u541b\u7684\u5c38\u4f53\u5c31\u6a2a\u8eba\u5728\u81ea\u5df1\u8eab\u8fb9\uff0c\u81ea\u5df1\u90a3\u67c4\u5b9d\u5251\uff0c\u5c1a\u63d2\u5728 \u4ed6\u7684\u80f8\u819b\uff0c\u9732\u51fa\u534a\u622a\uff0c\u8000\u773c\u751f\u8f89\u3002\u8f9b\u9f99\u5b50\u7231\u5251\u5982\u547d\uff0c\u4e00\u751f\u5bfb\u6c42\u5b9d\u5251\uff0c\u4e0d\u60f3\u4e00\u5f97\u5b9d\u5251\uff0c\u672a\u6ee1\u4e00 \u6708\uff0c\u4fbf\u906d\u5927\u52ab\uff0c\u6b64\u9645\uff0c\u4ed6\u89c1\u4e86\u81ea\u5df1\u7684\u5b9d\u5251\uff0c\u4e0d\u89c9\u82e6\u82e6\u6323\u624e\uff0c\u5728\u96ea\u5730\u4e0a\u53c8\u6162\u6162\u5730\u79fb\u52a8\u81ea\u5df1\u7684\u8eaf \u4f53\uff0c\u6eda\u5230\u9f50\u771f\u541b\u7684\u65c1\u8fb9\uff0c\u6293\u7740\u5251\u67c4\uff0c\u6162\u6162\u5730\u628a\u5b83\u62d4\u4e86\u51fa\u6765\uff0c\u6df1\u60c5\u5730\u770b\u4e86\u4e00\u773c\uff0c\u957f\u53f9\u53eb\u9053\uff1a \u201c\u51cc\u672a\u98ce\u5440\uff0c\u6211\u8f9c\u8d1f\u4e86\u4f60\u6240\u8d60\u7684\u5b9d\u5251\u4e86\uff01\u201d\u628a\u5251\u5c16\u8d34\u7740\u80f8\u819b\uff0c\u6b63\u60f3\u81ea\u5c3d\uff0c\u5ffd\u7136\u6709\u4eba\u53eb\u9053\uff1a \u201c\u51cc\u5927\u4fa0\u3001\u51cc\u5927\u4fa0\uff01\u201d\u8f9b\u9f99\u5b50\u624b\u6307\u4e00\u677e\uff0c\u5b9d\u5251\u843d\u5730\uff0c\u51b0\u5d16\u65c1\u8fb9\u95ea\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u8f9b\u9f99\u5b50\u60ca\u559c\u53eb \u9053\uff1a\u201c\u97e9\u5fd7\u90a6\uff0c\u539f\u6765\u662f\u4f60\uff01\u201d \u3000\u3000\u97e9\u5fd7\u90a6\u662f\u4ece\u897f\u85cf\u6765\u7684\u3002\u5f53\u6e05\u519b\u4fb5\u5165\u56de\u7586\u4e4b\u540e\uff0c\u8499\u85cf\u672c\u5df2\u4e25\u5bc6\u6212\u5907\uff0c\u540e\u6765\u89c1\u6e05\u519b\u5728\u56de\u7586\u63a8 \u8fdb\uff0c\u6781\u4e3a\u7f13\u6162\uff0c\u4e24\u4e2a\u591a\u6708\uff0c\u5c1a\u672a\u8fdb\u81f3\u4f0a\u7281\uff0c\u4e0d\u89c9\u677e\u61c8\u4e0b\u6765\u3002\u4e0d\u6599\u6e05\u519b\u5728\u4fb5\u5165\u56de\u7586\u4e4b\u65f6\uff0c\u5df2\u6697 \u4e2d\u5206\u51fa\u4e00\u652f\u5947\u5175\uff0c\u7531\u7687\u5b50\u5141\u9898\u7387\u9886\uff0c\u7a81\u7136\u653b\u5165\u5357\u85cf\uff0c\u628a\u8fbe\u8d56\u6d3b\u4f5b\u4fd8\u864f\u4e86\uff0c\u53e6\u7acb\u65b0\u7684\u8fbe\u8d56\u3002\u97e9 \u5fd7\u90a6\u548c\u897f\u85cf\u5587\u561b\u7684\u611f\u60c5\u6781\u597d\uff0c\u5728\u6e05\u519b\u8feb\u8fd1\u62c9\u8428\u4e4b\u65f6\uff0c\u5192\u9669\u9003\u51fa\uff0c\u5230\u56de\u7586\u53bb\u8ba8\u6551\u5175\u3002\u8fd9\u65e5\uff0c\u9ec4 \u660f\u65f6\u5206\uff0c\u7ecf\u8fc7\u6155\u58eb\u5854\u683c\u5c71\uff0c\u89c1\u5c71\u8c37\u4e2d\u6ee1\u5751\u6ee1\u8c37\u90fd\u662f\u6e05\u519b\u7684\u5c38\u4f53\uff0c\u6709\u4e9b\u672a\u6b7b\u7684\u8fd8\u5728\u60b2\u60e8\u547b\u541f\uff0c \u4e0d\u89c9\u6bdb\u9aa8\u609a\u7136\uff0c\u722c\u5230\u5c71\u8170\uff0c\u84e6\u7136\u542c\u5f97\u8f9b\u9f99\u5b50\u5728\u5927\u53eb\u51cc\u672a\u98ce\uff0c\u4e24\u4eba\u76f8\u89c1\uff0c\u51e0\u4e4e\u7591\u662f\u6076\u68a6\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u89c1\u8f9b\u9f99\u5b50\u901a\u8eab\u8840\u7ea2\uff0c\u5944\u5944\u4e00\u606f\uff0c\u9a87\u7136\u95ee\u9053\uff1a\u201c\u8f9b\u9f99\u5b50\uff0c\u4f60\u600e\u6837\u4e86\uff1f\u201d\u53d6\u51fa\u968f\u8eab\u643a \u5e26\u7684\u91d1\u521b\u836f\uff0c\u4fbf\u5f85\u7ed9\u4ed6\u63e9\u8840\u6577\u4f24\uff0c\u8f9b\u9f99\u5b50\u547b\u541f\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u7406\u6211\uff0c\u628a\u90a3\u67c4\u5b9d\u5251\u6361\u8d77\u6765\uff01\u201d\u97e9 \u5fd7\u90a6\u54ea\u91cc\u80af\u4f9d\uff0c\u4e00\u5b9a\u8981\u66ff\u8f9b\u9f99\u5b50\u6cbb\u4f24\uff0c\u8f9b\u9f99\u5b50\u7741\u7740\u602a\u773c\u9a82\u9053\uff1a\u201c\u6211\u4e34\u6b7b\u4f60\u8fd8\u4e0d\u542c\u6211\u7684\u8bdd\uff0c \u5feb\u3001\u5feb\u3001\u628a\u90a3\u67c4\u5b9d\u5251\u62ff\u8fc7\u6765\uff0c\u8d81\u6211\u8fd8\u6709\u4e09\u5206\u6c14\u5728\uff0c\u5982\u8fdf\u5c31\u4e0d\u53ca\u4e86\u3002\u201d\u97e9\u5fd7\u90a6\u65e0\u5948\uff0c\u5c06\u5251\u6361\u8d77 \u9012\u53bb\uff0c\u8f9b\u9f99\u5b50\u5e76\u4e0d\u63a5\u5251\uff0c\u53c8\u5429\u5490\u9053\uff1a\u201c\u4f60\u53cc\u624b\u6367\u5251\uff0c\u5e73\u653e\u5934\u9876\uff0c\u8dea\u4e0b\u6765\uff0c\u8dea\u4e0b\u6765\uff01\u201d\u97e9\u5fd7\u90a6 \u8be7\u6781\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u6211\u8981\u4f60\u5ba3\u8a93\u5f52\u5165\u6b66\u5f53\u95e8\u4e0b\uff0c\u6211\u4eca\u65e5\u66ff\u53bb\u4e16\u7684\u5e08\u5c0a\u6536 \u5f92\uff01\u201d\u97e9\u5fd7\u90a6\u89c1\u8f9b\u9f99\u5b50\u53cc\u773c\u5706\u7741\uff0c\u76f4\u53ee\u7740\u81ea\u5df1\uff0c\u77e5\u9053\u82e5\u4e0d\u7b54\u5e94\uff0c\u4ed6\u6b7b\u4e0d\u7791\u76ee\uff0c\u53ea\u597d\u8dea\u4e0b\u3002\u8f9b \u9f99\u5b50\u7cbe\u795e\u4e00\u632f\uff0c\u542c\u4e86\u97e9\u5fd7\u90a6\u5ba3\u8a93\u7688\u4f9d\u4e4b\u540e\uff0c\u5401\u53e3\u6c14\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u4e3a\u4eba\u6734\u8bb7\u8bda\u5b9e\uff0c\u672c\u95e8\u6212\u5f8b \u6211\u4e0d\u5fc5\u8bf4\u4e86\uff0c\u4ee5\u540e\u81ea\u6709\u4eba\u544a\u8bc9\u4f60\u3002\u73b0\u5728\u4f60\u628a\u5b9d\u5251\u7ed9\u6211\u3002\u201d\u63a5\u8fc7\u5b9d\u5251\uff0c\u5728\u5251\u9798\u4e2d\u62bd\u51fa\u4e00\u5f20\u4e1d \u7ee2\uff0c\u4e0a\u9762\u5199\u6ee1\u6587\u5b57\uff0c\u8fd8\u753b\u6709\u56fe\u5f0f\uff0c\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u8fd9\u662f\u6211\u624b\u6284\u7684\u8fbe\u6469\u4e00\u767e\u96f6\u516b\u5f0f\u7684\u526f\u672c\uff0c\u8fd8\u6709 \u6211\u7684\u4f53\u4f1a\u5fc3\u5f97\uff0c\u90fd\u5199\u4e0a\u53bb\u4e86\u3002\u6b63\u672c\u6211\u57cb\u5728\u9a86\u9a7c\u5cf0\u7684\u77f3\u7a9f\u4e2d\uff0c\u8fd9\u672c\u526f\u672c\u6211\u5df2\u8bd1\u6210\u6c49\u6587\uff0c\u8fbe\u6469\u79d8 \u590d\u672c\u6765\u662f\u4f60\u53d1\u73b0\u7684\uff0c\u4f46\u4f60\u4ee5\u524d\u4e0d\u662f\u672c\u95e8\u4e2d\u4eba\uff0c\u6240\u4ee5\u6211\u6682\u501f\u53bb\u3002\u201d\u97e9\u5fd7\u90a6\u8fd9\u624d\u604d\u7136\u8f9b\u9f99\u5b50\u8981\u81ea \u5df1\u5165\u6b66\u5f53\u95e8\u7684\u7528\u610f\uff0c\u5fd9\u518d\u8dea\u4e0b\u53e9\u8c22\u3002\u8f9b\u9f99\u5b50\u8fd0\u4e00\u53e3\u6c14\uff0c\u5f3a\u81ea\u652f\u6301\uff0c\u53eb\u97e9\u5fd7\u90a6\u5728\u51b0\u5d16\u4e4b\u4e0b\u3001\u51b0 \u6cb3\u4e4b\u8fb9\uff0c\u501f\u7740\u51b0\u96ea\u7684\u5149\u8f89\uff0c\u770b\u6e05\u6587\u5b57\uff0c\u4ed6\u53e3\u8bb2\u6307\u5212\uff0c\u7ed9\u97e9\u5fd7\u90a6\u8bb2\u89e3\u8fd9\u6b66\u6797\u4e0d\u4f20\u4e4b\u79d8\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u8bb2\u5b8c\u4e4b\u540e\uff0c\u5df2\u662f\u6c14\u82e5\u6e38\u4e1d\uff0c\u72b9\u81ea\u6323\u624e\u95ee\u9053\uff1a\u201c\u4f60\u61c2\u4e86\u4e48\uff1f\u201d\u97e9\u5fd7\u90a6\u5176\u5b9e\u5e76\u4e0d\u5f88 \u61c2\uff0c\u4f46\u89c1\u8f9b\u9f99\u5b50\u5982\u6b64\u82e6\u695a\uff0c\u4e0d\u5fcd\u53eb\u4ed6\u518d\u8bb2\u4e0b\u53bb\uff0c\u7565\u4e00\u8e0c\u8e87\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u591a\u8c22\u5e08\u5144\uff0c\u6211\u5168\u61c2 \u4e86\u3002\u201d\u8f9b\u9f99\u5b50\u6447\u4e86\u6447\u5934\uff0c\u7ee7\u7eed\u8bf4\u9053\uff0c\u201c\u4f60\u82e5\u4e0d\u61c2\uff0c\u6211\u7279\u51c6\u4f60\u62ff\u79d8\u672c\u53bb\u8bf7\u6559\u51cc\u672a\u98ce\uff0c\u53ea\u662f\u4ed6\u4eca \u65e5\u751f\u6b7b\u5982\u4f55\uff0c\u6211\u4e5f\u6beb\u4e0d\u77e5\u9053\uff01\u201d\u97e9\u5fd7\u90a6\u9a87\u6781\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u51cc\u5927\u4fa0\u548c\u4f60\u90fd\u4e2d\u4e86\u654c\u4eba\u6697\u7b97 \u4e86\uff1f\u201d\u8f9b\u9f99\u5b50\u53ea\u5269\u6700\u540e\u4e00\u53e3\u6c14\uff0c\u4e0d\u7b54\u97e9\u5fd7\u90a6\u7684\u95ee\u8bdd\uff0c\u8fde\u7740\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u6842\u4ef2\u660e\u548c\u5f20\u534e\u662d \u4e8c\u4eba\uff0c\u4e5f\u5e94\u5f53\u4eba\u6211\u6b66\u5f53\u4e4b\u95e8\uff0c\u4ed6\u4eec\u5c31\u7b97\u4f60\u7684\u5f92\u5f1f\u5427\uff01\u201d\u6842\u4ef2\u660e\u662f\u77f3\u5929\u6210\u4e34\u7ec8\u62dc\u6258\u8f9b\u9f99\u5b50\u6307\u70b9 \u7684\uff0c\u81f3\u4e8e\u5f20\u534e\u662d\u5219\u662f\u56e0\u4e3a\u53d6\u5f97\u4e86\u4f18\u6619\u4ed9\u82b1\uff0c\u7531\u5353\u4e00\u822a\u9057\u547d\u8981\u8f9b\u9f99\u5b50\u6559\u7684\uff0c\u97e9\u5fd7\u90a6\u8fd8\u5f85\u95ee\u65f6\uff0c \u8f9b\u9f99\u5b50\u5bf9\u5b9d\u5251\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u7ed9\u4f60\uff01\u201d\u602a\u773c\u4e00\u7ffb\uff0c\u6ea2\u7136\u957f\u901d\uff01 \u3000\u3000\u97e9\u5fd7\u90a6\u53d6\u4e86\u5b9d\u5251\uff0c\u5728\u51b0\u6cb3\u4e2d\u6d17\u62b9\u5e72\u51c0\uff0c\u6b63\u60f3\u6316\u4e00\u5893\u7a74\uff0c\u5c06\u8f9b\u9f99\u5b50\u57cb\u846c\uff0c\u5ffd\u89c1\u5e7d\u8c37\u4e0b\u706b\u628a \u5b9b\u82e5\u957f\u9f99\uff0c\u6162\u6162\u5411\u4e0a\u79fb\u52a8\u3002\u97e9\u5fd7\u90a6\u5fc3\u60f3\uff0c\u81ea\u5df1\u662f\u8ba8\u6551\u5175\u6765\u7684\uff0c\u8fd9\u961f\u4eba\u9a6c\uff0c\u82e5\u662f\u654c\u4eba\uff0c\u88ab\u4ed6\u4eec \u4e0a\u5f97\u5c71\u6765\uff0c\u81ea\u5df1\u63d2\u7ffc\u96be\u9003\uff0c\u770b\u6765\u516c\u8c0a\u79c1\u60c5\u4e0d\u80fd\u517c\u9890\uff0c\u53ea\u597d\u8ba9\u8f9b\u9f99\u5b50\u5f7c\u6d41\u51b0\u6240\u57cb\u4e86\u3002\u4ed6\u6ef4\u4e86\u51e0 \u6ef4\u773c\u6cea\uff0c\u6005\u89e6\u4e00\u4ee3\u602a\u4fa0\uff0c\u5982\u6b64\u6536\u573a\uff0c\u7ffb\u8fc7\u5c71\u5761\uff0c\u6025\u6025\u5411\u5357\u8fdb\u53d1\u3002 \u3000\u3000\u8c01\u77e5\u8fd9\u961f\u4eba\u9a6c\uff0c\u65e2\u4e0d\u662f\u8349\u539f\u9a6c\u5e2e\uff0c\u4e5f\u4e0d\u662f\u6e05\u519b\u5175\u58eb\uff0c\u4e43\u662f\u54c8\u8428\u514b\u5e74\u8f7b\u914b\u957f\u547c\u514b\u6d4e\u6240\u5e26\u7684 \u4eba\u3002\u5b5f\u7984\u9003\u8d70\u4e4b\u540e\uff0c\u5b5f\u66fc\u4e3d\u4e1d\u8d77\u5934\u7792\u4ed6\uff0c\u5f53\u665a\u5979\u6574\u591c\u5931\u7720\uff0c\u5fc3\u4e2d\u603b\u50cf\u88ab\u4e00\u6761\u5c0f\u6bd2\u86c7\u541e\u556e\u4f3c\u7684 \u5341\u5206\u96be\u8fc7\u3002 \u3000\u3000\u5b5f\u66fc\u4e3d\u4e1d\u5ffd\u7136\u9192\u8fc7\u6765\u9053\uff1a\u201c\u6211\u4eec\u8349\u539f\u4e0a\u6709\u53e5\u6210\u8bed\uff1a\u5bf9\u6240\u7231\u7684\u4eba\u9690\u7792\uff0c\u5c31\u50cf\u628a\u6c61\u6ce5\u6492\u4e0b\u7518 \u6cc9\uff0c\u5929\u4e0b\u6700\u7f8e\u7684\u4e1c\u897f\u4e5f\u53d8\u4e86\u6627\uff0c\u8fd9\u6210\u8bed\u8bf4\u5f97\u5bf9\u5440\uff01\u6211\u4e3a\u4ec0\u4e48\u8981\u7792\u7740\u6240\u7231\u7684\u4eba\uff1f\u82e5\u544a\u8bc9\u4e86\u4ed6\uff0c \u80fd\u628a\u6211\u7684\u7238\u7238\u8ffd\u56de\u6765\uff0c\u4e5f\u662f\u4e00\u4ef6\u597d\u4e8b\u3002\u201d\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u5979\u5c31\u53bb\u544a\u8bc9\u547c\u514b\u6d4e\uff0c\u547c\u514b\u6d4e\u5e26\u4eba\u641c \u7d22\uff0c\u8fdb\u5165\u6155\u58eb\u5854\u683c\u5c71\uff0c\u53ea\u89c1\u5c71\u8c37\u4e2d\u6a2a\u4e03\u7ad6\u516b\u5806\u7740\u65e0\u6570\u6e05\u5175\u5c38\u4f53\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u6b63\u5f85\u7ec6\u770b\uff0c\u5ffd\u542c \u5f97\u94f6\u94c3\u4f3c\u7684\u5c11\u5973\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u662f\u9a6c\u5e2e\u5417\uff1f\u201d\u51b0\u6cb3\u811a\u4e0b\uff0c\u4e00\u4e2a\u7ea2\u8863\u5c11\u5973\uff0c\u6000 \u62b1\u4e00\u4eba\uff0c\u4f3c\u7cbe\u7075\u822c\u7684\u5189\u5189\u5347\u8d77\uff0c\u547c\u514b\u6d4e\u548c\u5b5f\u66fc\u4e3d\u4e1d\u90fd\u770b\u5f97\u5446\u4e86\u3002 \u3000\u3000\u5b5f\u66fc\u4e3d\u4e1d\u8fce\u4e0a\u4e91\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u4eec\u662f\u54c8\u8428\u514b\u7684\u6218\u58eb\uff0c\u4f60\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u8fd9\u4e48\u591a\u6e05\u5175\u662f\u8c01\u6740 \u7684\uff01\u201d\u90a3\u4e2a\u7ea2\u8863\u5c11\u5973\u5927\u559c\u8df3\u8dc3\uff0c\u53eb\u9053\uff1a\u201c\u54e6\uff0c\u54c8\u8428\u514b\u7684\u6218\u58eb\uff01\u90a3\u4f60\u4eec\u4e00\u5b9a\u77e5\u9053\u51cc\u672a\u98ce\u7684 \u4e86\uff1f\u201d\u547c\u514b\u6d4e\u9053\uff1a\u201c\u51cc\u672a\u98ce\uff0c\u90a3\u600e\u80fd\u4e0d\u77e5\uff1f\u4ed6\u662f\u6211\u4eec\u4e00\u65cf\u7684\u6069\u4eba\uff01\u6562\u95ee\u5973\u631f\u548c\u51cc\u5927\u4fa0\u53ef\u662f\u76f8 \u8bc6\uff1f\u201d\u7ea2\u8863\u5c11\u5973\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u6211\u4eec\u90fd\u662f\u51cc\u5927\u4fa0\u7684\u597d\u670b\u53cb\uff0c\u6211\u53eb\u6b66\u743c\u7476\uff0c\u6211\u624b\u4e2d\u62b1\u7740\u7684\u53eb\u5218\u90c1 \u82b3\uff0c\u2026\u2026\u201d\u6b66\u743c\u7476\u751f\u6027\u987d\u76ae\uff0c\u89c1\u547c\u514b\u6d4e\u548c\u5b5f\u66fc\u4e3d\u4e1d\u6001\u5ea6\u4eb2\u70ed\uff0c\u7b11\u7740\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u5979\u548c\u51cc\u672a\u98ce \u5c31\u50cf\u4f60\u4eec\u4e24\u4eba\u4e00\u6837\u8981\u597d\uff01\u201d\u5b5f\u66fc\u4e3d\u4e1d\u674f\u8138\u98de\u971e\uff0c\u547c\u514b\u6d4e\u5219\u522e\u76ee\u76f8\u770b\uff0c\u6025\u5fd9\u95ee\u5218\u90c1\u82b3\u4f24\u5f97\u600e \u6837\uff1f \u3000\u3000\u5218\u90c1\u82b3\u53ef\u771f\u4f24\u5f97\u4e0d\u8f7b\uff0c\u5979\u88ab\u695a\u662d\u5357\u548c\u536b\u58eb\u4eec\u8feb\u4e0b\u60ac\u5d16\uff0c\u672c\u6765\u4e07\u96be\u9003\u547d\uff0c\u5e78\u5979\u624b\u4e0a\u6709\u5947\u95e8 \u6697\u5668\u9526\u4e91\u515c\uff0c\u5f20\u5728\u7a7a\u4e2d\uff0c\u98d8\u98d8\u8361\u8361\uff0c\u51cf\u4f4e\u4e86\u4e0b\u5815\u7684\u901f\u5ea6\uff0c\u6070\u597d\u90a3\u9526\u4e91\u515c\u53c8\u521a\u53d7\u695a\u662d\u5357\u77f3\u5f39\u9707 \u88c2\uff0c\u94a2\u987b\u6b6a\u659c\u51cc\u4e71\uff0c\u5815\u5230\u534a\u5c71\uff0c\u52fe\u7740\u4e00\u682a\u866c\u677e\uff0c\u767b\u65f6\u6b62\u4e86\u4e0b\u5815\u4e4b\u52bf\uff0c\u4f46\u4eba\u5df1\u660f\u8ff7\u4e0d\u9192\u4e86\u3002 \u3000\u3000\u6b66\u743c\u7476\u8fd0\u767d\u53d1\u9b54\u5973\u7684\u72ec\u95e8\u8f7b\u529f\uff0c\u5148\u89d1\u51c6\u4e00\u70b9\uff0c\u843d\u4e0b\u5341\u4f59\u4e08\u3001\u811a\u4e0d\u6cbe\u5c18\uff0c\u7528\u811a\u5c16\u4e00\u70b9\u5b9e \u5730\uff0c\u6362\u52bf\u53c8\u8dc3\u4e0b\u5341\u4f59\u4e08\uff0c\u8fd9\u6837\u770b\u6765\uff0c\u4e5f\u548c\u534a\u7a7a\u98de\u5815\u4e00\u6837\u3002\u5218\u90c1\u82b3\u5728\u534a\u7a7a\u98d8\u98d8\u8361\u8361\u5730\u964d\u843d\uff0c\u6b66 \u743c\u7476\u770b\u5f97\u5206\u660e\uff0c\u7d27\u7d27\u8ffd\u8e51\uff0c\u7ec8\u4e8e\u6551\u4e86\u5218\u90c1\u82b3\u4e00\u547d\u3002 \u3000\u3000\u5f53\u4e0b\u6b66\u743c\u7476\u5c06\u5f53\u65e5\u6076\u6218\u7684\u60c5\u5f62\uff0c\u544a\u8bc9\u4e86\u547c\u514b\u6d4e\u3002\u8fd9\u4f4d\u5e74\u8f7b\u7684\u914b\u957f\u70ed\u5fc3\u5f97\u5f88\uff0c\u4e00\u9762\u6d3e\u4eba\u722c \u4e0a\u5c71\u53bb\u627e\u5bfb\u51cc\u672a\u98ce\uff0c\u4e00\u9762\u9080\u8bf7\u6b66\u743c\u7476\u4f4f\u5230\u4ed6\u7684\u8425\u5730\u53bb\uff0c\u597d\u66ff\u5218\u90c1\u82b3\u6cbb\u4f24\u3002\u6b66\u743c\u7476\u81ea\u7136\u662f\u6c42\u4e4b \u4e0d\u5f97\u3002 \u3000\u3000\u518d\u8bf4\u98de\u7ea2\u5dfe\u548c\u5085\u9752\u4e3b\u4ed6\u4eec\uff0c\u81ea\u51cc\u672a\u98ce\u53bb\u540e\uff0c\u5fc3\u4e2d\u60ac\u60ac\uff0c\u4f46\u6218\u60c5\u4e00\u5929\u5929\u7d27\u5f20\u8d77\u6765\uff0c\u6e05\u519b\u7a81 \u7136\u6025\u901f\u63a8\u8fdb\uff0c\u5927\u519b\u50cf\u98ce\u66b4\u822c\u6a2a\u626b\u8fc7\u8349\u539f\uff0c\u98de\u7ea2\u5dfe\u6267\u884c\u65e2\u5b9a\u7684\u7b56\u7565\uff0c\u5316\u6574\u4e3a\u96f6\uff0c\u6d41\u6563\u5728\u5e7f\u9614\u65e0 \u8fb9\u7684\u8349\u539f\uff0c\u5f53\u5927\u519b\u7ecf\u8fc7\u7684\u65f6\u5019\uff0c\u5085\u9752\u4e3b\u548c\u98de\u7ea2\u5dfe\u5728\u4e00\u5ea7\u9ad8\u5c71\u4e4b\u4e0a\u89c2\u671b\uff0c\u53ea\u89c1\u80dc\u65d7\u853d\u7a7a\uff0c\u4e07\u9a6c \u5954\u817e\uff0c\u519b\u5bb9\u751a\u76db\uff0c\u5085\u9752\u4e3b\u8e69\u7709\u8bf4\u9053\uff1a\u201c\u6e05\u519b\u4e2d\u5927\u6709\u5c06\u624d\uff0c\u4eca\u56de\u7684\u7edf\u5e05\u7edd\u4e0d\u5728\u591a\u94ce\u4e4b\u4e0b\u3002\u201d\u98de \u7ea2\u5dfe\u626c\u97ad\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u4e0d\u8f93\u4ed6\uff0c\u4e14\u5148\u628a\u6761\u957f\u86c7\u7684\u5c3e\u5df4\u5207\u4e86\uff01\u201d\u5f85\u5927\u519b\u8fc7\u4e86\u5341\u4e4b\u4e03\u516b\uff0c\u7a81\u7136 \u96c6\u4e2d\u5175\u529b\u5c06\u5b83\u5207\u65ad\uff0c\u6253\u4e86\u4e2a\u6f02\u4eae\u7684\u80dc\u4ed7\u3002\u4f46\u90a3\u80a1\u6e05\u5175\u5f3a\u5f97\u5f88\uff0c\u867d\u8d25\u4e0d\u4e71\uff0c\u575a\u5b88\u5f85\u63f4\u3002\u78e8\u4e86\u597d \u51e0\u5929\uff0c\u6e05\u519b\u540e\u63f4\u7eed\u5230\uff0c\u53c8\u53ea\u597d\u653e\u8d70\u4ed6\u4eec\u3002\u4e0d\u8fc7\u4ea6\u5df2\u628a\u4ed6\u4eec\u6d88\u706d\u4e86\u5927\u534a\u3002 \u3000\u3000\u5927\u519b\u8fc7\u540e\uff0c\u6d88\u606f\u4f20\u6765\uff0c\u62a5\u9053\u6e05\u5175\u7a81\u5206\u4e24\u8def\uff0c\u4e00\u5165\u8499\u53e4\uff0c\u4e00\u5165\u897f\u85cf\uff0c\u5165\u897f\u85cf\u7684\u4e14\u662f\u7687\u5b50\u5141 \u9898\u7387\u9886\u3002\u5085\u9752\u4e3b\u559f\u7136\u5bf9\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u6b21\u6253\u4e2a\u80dc\u4ed7\uff0c\u4f46\u4ed6\u4eec\u8fd9\u6b21\u5374\u6253\u4e86\u4e2a\u5927\u80dc\u4ed7\uff0c\u4ed6\u4eec \u660e\u660e\u77e5\u9053\u8fd9\u4e00\u5e26\u662f\u5357\u7586\u5404\u65cf\u96c6\u7ed3\u4e4b\u5730\uff0c\u7ecf\u8fc7\u65f6\u7406\u4e5f\u4e0d\u7406\uff0c\u6545\u610f\u8ba9\u957f\u86c7\u7684\u5c3e\u5df4\u7ed9\u6211\u4eec\u622a\u65ad\uff0c\u548c \u6211\u4eec\u7f20\u6253\uff0c\u86c7\u5934\u4ecd\u75be\u9a70\u53bb\u4e86\uff01\u201d\u98de\u7ea2\u5dfe\u4e00\u60f3\uff0c\u679c\u7136\u4e2d\u4e86\u654c\u4eba\u7684\u5708\u5957\uff0c\u6709\u70b9\u61ca\u607c\uff0c\u5085\u9752\u4e3b\u5374\u7b11 \u9053\uff1a\u201c\u4ed6\u4eec\u7eb5\u6709\u5c06\u624d\uff0c\u5c31\u5168\u5c40\u6765\u8bf4\uff0c\u5374\u65e0\u6cd5\u633d\u56de\u8d25\u4ea1\u547d\u8fd0\u3002\u201d\u98de\u7ea2\u5dfe\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6ca1\u8001\u767e\u59d3 \u5e2e\u52a9\u7684\u519b\u961f\uff0c\u8fdf\u65e9\u90fd\u4f1a\u5931\u8d25\uff0c\u6211\u61c2\u5f97\u4f60\u7684\u8bdd\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u6b63\u5728\u95f2\u8bdd\uff0c\u5ffd\u89c1\u5192\u6d63\u83b2\u548c\u6842\u4ef2\u660e\u5e76\u8f94\u9a70\u6765\uff0c\u5192\u6d63\u83b2\u5728\u9a6c\u80cc\u4e0a\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5085\u4f2f\u4f2f\uff0c \u5085\u4f2f\u4f2f\uff0c\u4f60\u731c\u8fd9\u6b21\u6e05\u519b\u7684\u7edf\u5e05\u662f\u8c01\uff1f\u201d\u5085\u9752\u4e3b\u8bb6\u9053\uff1a\u201c\u6211\u600e\u4e48\u4f1a\u731c\u5f97\u7740\uff1f\u4f60\u8fd9\u5c0f\u9b3c\u5934\u8fd9\u6837 \u8bf4\uff0c\u4e00\u5b9a\u662f\u5f97\u5230\u4ec0\u4e48\u98ce\u58f0\u4e86\uff01\u201d\u6842\u3001\u5192\u4e8c\u4eba\u662f\u98de\u7ea2\u4e2a\u5dee\u53bb\u5bdf\u770b\u4e00\u4e2a\u6e05\u519b\u9a7b\u624e\u8fc7\u7684\u8425\u5730\u7684\uff0c\u56e0 \u6b64\uff0c\u98de\u7ea2\u5dfe\u4e5f\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u5728\u6e05\u519b\u7684\u8425\u5730\u91cc\u53d1\u73b0\u4ec0\u4e48\u4e1c\u897f\u4e86\uff1f\u201d \u3000\u3000\u5192\u6d63\u83b2\u62c9\u7740\u98de\u7ea2\u5dfe\u4fbf\u8d70\uff0c\u5e76\u5bf9\u5085\u9752\u4e3b\u9053\uff1a\u201c\u5085\u4f2f\u4f2f\uff0c\u4f60\u4e5f\u6765\u770b\u770b\uff0c\u770b\u6211\u7684\u731c\u6d4b\u5bf9\u4e0d \u5bf9\uff1f\u201d\u56db\u4eba\u7b56\u9a6c\u767b\u5c71\uff0c\u770b\u5c71\u8170\u4e0a\u6e05\u519b\u9a7b\u8fc7\u7684\u8425\u5730\uff0c\u53ea\u89c1\u622a\u58c1\u8fde\u8425\uff0c\u7284\u89d2\u76f8\u4f9d\uff0c\u72ac\u7259\u4ea4\u9519\uff0c\u6df1 \u6709\u6cd5\u5ea6\u3002\u5085\u9752\u4e3b\u9053\uff1a\u201c\u8c03\u5ea6\u5927\u519b\uff0c\u5982\u81c2\u4f7f\u6307\uff0c\u5b89\u8425\u884c\u519b\uff0c\u4e2d\u89c4\u4e2d\u77e9\uff0c\u8fd9\u4f4d\u7edf\u5e05\u79f0\u5f97\u4e0a\u662f\u5927\u5c06 \u4e4b\u624d\u4e86\uff01\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u53ea\u6015\u7edf\u5175\u7684\u4e0d\u662f\u5c06\u519b\uff01\u201d\u4f38\u624b\u4e00\u6307\u5bf9\u9762\u77f3\u58c1\uff0c\u5085\u9752\u4e3b\u7b49\u51d1\u8fc7\u53bb\u770b\uff0c \u53ea\u89c1\u4e0a\u9762\u523b\u7740\u51e0\u884c\u64d8\u7aa0\u5927\u5b57\uff0c\u5f53\u662f\u5199\u4e86\u4e4b\u540e\uff0c\u53eb\u77f3\u5de5\u523b\u7684\uff0c\u90a3\u51e0\u884c\u5b57\u5199\u5f97\u9f99\u98de\u51e4\u821e\u53c8\u6709\u6e05\u9038 \u4e4b\u6c14\uff0c\u5085\u9752\u4e3b\u662f\u4e66\u6cd5\u540d\u5bb6\uff0c\u4e5f\u4e0d\u7981\u8d5e\u51fa\u58f0\u6765\uff0c\u5192\u6d63\u83b2\u8bfb\u9053\uff1a \u3000\u3000\u201c\u8bd5\u671b\u9634\u5c71\uff0c\u9ed8\u7136\u9500\u9b42\uff0c\u65e0\u8a00\u5f98\u5f8a\u3002\u89c1\u9752\u5cf0\u51e0\u7c07\uff0c\u53bb\u5929\u624d\u5c3a\uff0c\u9ec4\u6c99\u4e00\u7247\uff0c\u531d\u5730\u65e0\u57c3\u3002\u788e \u53f6\u57ce\u8352\uff0c\u62c2\u4e91\u5806\u8fdc\uff0c\u96d5\u5916\u5bd2\u70df\u60e8\u4e0d\u5f00\uff0c\u8718\u8e70\u4e45\uff0c\u5ffd\u51b0\u5d16\u8f6c\u77f3\uff0c\u4e07\u58d1\u60ca\u96f7\uff01\u7a77\u8fb9\u81ea\u8db3\u6101\u6000\uff0c\u53c8 \u4f55\u5fc5\u5e73\u751f\u591a\u6068\u54c9\uff1f\u53ea\u51c4\u51c9\u7edd\u585e\uff0c\u86fe\u7709\u9057\u51a2\uff0c\u9500\u6c89\u8150\u8349\uff0c\u9a8f\u9aa8\u7a7a\u53f0\uff0c\u5317\u8f6c\u6cb3\u6d41\uff0c\u5357\u6a2a\u6597\u67c4\uff0c\u7565 \u70b9\u5fae\u971c\u9b13\u65e9\u8870\uff0c\u541b\u4e0d\u4fe1\uff0c\u5411\u897f\u98ce\u56de\u9996\uff0c\u767e\u4e8b\u582a\u54c0\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u8bfb\u5b8c\u4e4b\u540e\u8bf4\u9053\uff1a\u201c\u5085\u4f2f\u4f2f\uff0c\u4f60\u770b\u8fd9\u9996\u6c81\u56ed\u6625\u8bcd\uff0c\u662f\u4e0d\u662f\u7eb3\u5170\u5bb9\u82e5\u7684\u98ce\u683c\uff1f\u201d\u5085\u9752 \u4e3b\u9053\uff1a\u201c\u54c0\u611f\u987d\u8273\uff0c\u51c4\u60cb\u4e4b\u4e2d\u53c8\u6709\u8c6a\u60c5\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u4e5f\u53ea\u6709\u7eb3\u5170\u5bb9\u82e5\u624d\u80fd\u5199\u5f97\u5982\u6b64\u597d\u8bcd\u3002\u201d \u5192\u6d63\u83b2\u9053\uff1a\u201c\u6211\u4e5f\u6df1\u6709\u540c\u611f\uff01\u6b64\u8bcd\u7edd\u585e\u751f\u60c5\uff0c\u8fb9\u57ce\u5bc4\u611f\uff0c\u968f\u519b\u5f81\u6218\u4e2d\u9690\u9690\u6709\u53cd\u6218\u4e4b\u601d\uff0c\u4e0d\u662f \u7eb3\u5170\uff0c\u8c01\u6562\u586b\u6b64\uff1f\u201d\u5085\u9752\u4e3b\u62cd\u638c\u8d5e\u9053\uff1a\u201c\u4f60\u771f\u806a\u660e\uff0c\u731c\u5f97\u5bf9\u4e86\uff0c\u7edf\u5175\u7684\u4e0d\u662f\u5c06\u519b\uff0c\u800c\u662f\u7687 \u5e1d\uff01\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u4f60\u4eec\u8c08\u8bd7\u8bba\u8bcd\uff0c\u6211\u662f\u4e00\u7a8d\u4e0d\u901a\uff0c\u600e\u4e48\u4f60\u4eec\u4f1a\u4ece\u8fd9\u4e00\u9996\u8bcd\u800c\u731c\u5230\u7edf\u5175\u7684\u662f \u7687\u5e1d\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u7eb3\u5170\u5bb9\u82e5\u662f\u76f8\u56fd\u516c\u5b50\uff0c\u53c8\u662f\u4e00\u7b49\u5f85\u536b\uff0c\u82e5\u975e\u5eb7\u7199\u5fa1\u9a7e\u4eb2\u5f81\uff0c\u4ed6\u600e\u4f1a\u968f \u519b\u5230\u6b64\u8fb9\u8352\u4e4b\u5730\uff1f\u201d\u98de\u7ea2\u5dfe\u54fc\u9053\uff1a\u201c\u5c31\u662f\u7687\u5e1d\u8001\u513f\u4eb2\u6765\uff0c\u6211\u4eec\u4e5f\u4e0d\u6015\u4ed6\uff01\u201d\u5085\u9752\u4e3b\u9053\uff1a \u201c\u6015\uff0c\u6211\u4eec\u5f53\u7136\u4e0d\u6015\uff0c\u53ea\u662f\u5eb7\u7199\u4eb2\u7387\u5927\u519b\uff0c\u53ef\u89c1\u4ed6\u5bf9\u8fb9\u7586\u7684\u91cd\u89c6\uff0c\u6211\u4eec\u60f3\u6b63\u9762\u5bf9\u6297\uff0c\u90a3\u662f\u7edd \u4e0d\u53ef\u80fd\u7684\u4e86\u3002\u201d\u6842\u4ef2\u660e\u548c\u98de\u7ea2\u5dfe\u4e00\u6837\uff0c\u4e5f\u662f\u4e0d\u89e3\u8bd7\u8bcd\uff0c\u89c1\u5192\u6d63\u83b2\u5bf9\u58c1\u51dd\u601d\uff0c\u5ffd\u7136\u60f3\u8d77\u7eb3\u5170\u5bb9 \u82e5\u62c9\u5979\u7684\u624b\u7684\u5f80\u4e8b\uff0c\u5fc3\u4e2d\u9887\u4e3a\u4e0d\u5feb\u3002 \u3000\u3000\u56db\u4eba\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u89c1\u8349\u539f\u8fdc\u5904\uff0c\u98de\u6765\u4e24\u9a91\u5feb\u9a6c\uff0c\u7d27\u7d27\u8ffd\u9010\uff0c\u4e24\u9a6c\u4e00\u4ea4\uff0c\u524d\u9762\u7684\u4eba\u5c31\u56de\u8eab\u62fc \u547d\uff0c\u518d\u8fc7\u4e00\u9635\uff0c\u770b\u5f97\u66f4\u662f\u5206\u660e\uff0c\u53ea\u89c1\u540e\u9762\u90a3\u9a91\uff0c\u4e43\u662f\u4e2a\u7ea2\u8863\u5c11\u5973\uff0c\u5251\u5149\u95ea\u52a8\uff0c\u4e0d\u79bb\u524d\u9762\u90a3\u540d \u9a91\u58eb\u7684\u80cc\u5fc3\uff0c\u4e24\u4eba\u5927\u58f0\u53eb\u56b7\uff0c\u4f3c\u662f\u4e92\u76f8\u65a5\u8d23\uff0c\u5ffd\u7136\u53cc\u53cc\u843d\u9a6c\uff0c\u5728\u8349\u539f\u4e0a\u6597\u8d77\u5251\u6765\uff0c\u90a3\u7ea2\u8863\u5c11 \u5973\u5251\u6cd5\u7cbe\u7edd\uff0c\u75be\u4f3c\u72f8\u732b\uff0c\u77eb\u82e6\u733f\u7334\uff0c\u5251\u5149\u8d77\u5904\uff0c\u8d77\u4e00\u7247\u7cbe\u8292\u51b7\u7535\uff0c\u524d\u9762\u90a3\u540d\u9a91\u58eb\u662f\u4e2a\u4e2d\u5e74\u6c49 \u5b50\uff0c\u5251\u6cd5\u751a\u602a\uff0c\u811a\u6b65\u8dc4\u8dc4\u8e09\u8e09\uff0c\u5982\u9189\u6c49\u72c2\u821e\uff0c\u7adf\u662f\u8f9b\u9f99\u5b50\u7684\u602a\u62db\u5bb6\u6570\uff0c\u98de\u7ea2\u4e2a\u4e00\u58f0\u5927\u558a\uff0c\u7b56 \u9a6c\u51b2\u4e0b\u5c71\u53bb\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4f4f\u624b\uff0c\u90fd\u662f\u81ea\u5df1\u4eba\uff01\u201d\u5085\u9752\u4e3b\u4e5f\u7d27\u968f\u7740\u53eb\u9053\uff1a\u201c\u97e9\u5927\u54e5\u4f4f \u624b\uff0c\u6211\u4eec\u90fd\u5728\u8fd9\u513f\uff01\u201d \u3000\u3000\u90a3\u4e24\u4eba\u6b63\u662f\u6b66\u743c\u7476\u548c\u97e9\u5fd7\u90a6\u3002\u539f\u6765\u6b66\u743c\u7476\u548c\u547c\u514b\u6d4e\u722c\u4e0a\u5c71\u53bb\u641c\u7d22\uff0c\u53ea\u89c1\u6a2a\u5c38\u904d\u5730\uff0c\u8f9b\u9f99 \u5b50\u548c\u77f3\u5929\u6210\u7684\u5c38\u4f53\u4e5f\u5728\u5176\u5185\uff0c\u4e0d\u7981\u5927\u62d7\uff0c\u5f53\u4e0b\u5c06\u4e24\u4eba\u7684\u9ab8\u9aa8\u6536\u62fe\u597d\u4e86\uff0c\u548c\u547c\u514b\u6d4e\u56de\u5230\u5580\u5c14\u6c81 \u8349\u539f\u7684\u8425\u5730\uff0c\u5218\u90c1\u82b3\u60a0\u60a0\u9192\u8f6c\uff0c\u6267\u7740\u6b66\u743c\u7476\u7684\u624b\u6d41\u4e0b\u6cea\u6765\uff0c\u7b2c\u4e00\u53e5\u8bdd\u5c31\u95ee\u51cc\u672a\u98ce\u600e\u4e48\u6837\u4e86\uff0c \u6b66\u743c\u7476\u544a\u8bc9\u5979\u5e76\u6ca1\u53d1\u73b0\u51cc\u672a\u98ce\u7684\u5c38\u4f53\uff0c\u5979\u624d\u7a0d\u7a0d\u5b89\u5fc3\uff0c\u4f46\u542c\u4e86\u77f3\u5929\u6210\u548c\u8f9b\u9f99\u5b50\u7684\u6b7b\u8baf\uff0c\u53c8\u89c9 \u5341\u5206\u96be\u8fc7\u3002\u6b66\u743c\u7476\u5b89\u6170\u4e86\u5979\u4e00\u9635\uff0c\u770b\u5979\u5916\u4f24\u867d\u91cd\uff0c\u4f46\u8fd8\u4e0d\u81f3\u4e8e\u6b7b\uff0c\u4e8e\u751a\u62dc\u6258\u547c\u514b\u6d4e\u548c\u5b5f\u66fc\u4e3d \u4e1d\u597d\u597d\u7167\u6599\u5979\uff0c\u7acb\u5373\u544a\u8f9e\u4e86\uff0c\u5feb\u9a6c\u8d76\u56de\uff0c\u4e00\u6765\u662f\u8981\u5411\u98de\u7ea2\u5dfe\u62a5\u544a\u6d88\u606f\uff0c\u4e8c\u6765\u662f\u8981\u8bf7\u5085\u9752\u4e3b\u53bb \u65bd\u6551\u3002 \u3000\u3000\u5176\u65f6\u97e9\u5fd7\u90a6\u5df2\u5148\u8d70\u4e86\u4e00\u7a0b\uff0c\u4f46\u4ed6\u7684\u9a91\u672f\u4e0d\u53ca\u6b66\u743c\u7476\u9ad8\u660e\uff0c\u8def\u9014\u4e5f\u6ca1\u6b66\u743c\u7476\u719f\u6089\uff0c\u4e2d\u9014\u4e3a \u4e86\u8981\u8eb2\u907f\u6e05\u519b\uff0c\u5bfb\u89c5\u5c0f\u8def\uff0c\u53c8\u803d\u6401\u4e86\u4e00\u4e9b\u65f6\u5019\uff0c\u5c06\u8981\u56de\u5230\u98de\u7ea2\u5dfe\u7684\u9a7b\u5730\u65f6\uff0c\u4fbf\u88ab\u6b66\u743c\u7476\u8ffd \u4e0a\uff0c\u6b66\u743c\u7476\u89c1\u4ed6\u624b\u4e0a\u7684\u90a3\u628a\u5b9d\u5251\uff0c\u6b63\u662f\u51cc\u672a\u98ce\u9001\u7ed9\u8f9b\u9f99\u5b50\u90a3\u4e00\u628a\uff0c\u4e0d\u7981\u5927\u7591\uff0c\u53ea\u9053\u97e9\u5fd7\u90a6\u4e43 \u662f\u8d70\u8131\u7684\u6e05\u5ef7\u536b\u58eb\uff0c\u6740\u5bb3\u8f9b\u9f99\u5b50\u7684\u51f6\u624b\uff0c\u4e0a\u524d\u559d\u95ee\uff0c\u97e9\u5fd7\u90a6\u7ed3\u7ed3\u5df4\u5df4\uff0c\u4e0d\u5584\u8bf4\u8bdd\uff0c\u6b66\u8c05\u7476\u6027 \u5b50\u6025\u8e81\uff0c\u4e00\u8a00\u4e0d\u5408\uff0c\u5c31\u52a8\u8d77\u624b\u672a\uff0c\u97e9\u5fd7\u90a6\u65b0\u5b66\u602a\u62db\uff0c\u5c1a\u672a\u6210\u719f\uff0c\u6321\u4e0d\u4f4f\u6b66\u743c\u7476\u8f9b\u8fa3\u7684\u5251\u6cd5\uff0c \u4e00\u8fb9\u6253\u4e00\u8fb9\u9003\uff0c\u82e5\u4e0d\u662f\u5e78\u597d\u78b0\u4e0a\u98de\u7ea2\u725b\uff0c\u9669\u4e9b\u5c31\u8981\u4f24\u5728\u6b66\u743c\u7476\u7684\u5229\u5251\u4e4b\u4e0b\u3002 \u3000\u3000\u6b66\u743c\u7476\u548c\u97e9\u5fd7\u90a6\u5404\u5c06\u5f53\u65e5\u7684\u60c5\u5f62\u8bf4\u4e86\uff0c\u98de\u7ea2\u5dfe\u548c\u5085\u9752\u4e3b\u90fd\u4e0d\u89c9\u6f78\u7136\u6cea\u4e0b\uff0c\u6842\u4ef2\u660e\u66f4\u662f\u75db \u54ed\u5c16\u58f0\uff0c\u4e0d\u4e45\u77f3\u5927\u5a18\u4e5f\u77e5\u9053\u5669\u8017\uff0c\u60f3\u7740\u8fd9\u4e00\u751f\u574e\u5777\u906d\u9047\uff0c\u6069\u7231\u592b\u59bb\uff0c\u4e8c\u5341\u5e74\u79bb\u6563\uff0c\u597d\u5bb9\u6613\u51b0 \u6d88\u8bef\u89e3\uff0c\u800c\u4eca\u53c8\u5206\u9694\u5e7d\u660e\uff0c\u90a3\u4efd\u4f24\u5fc3\u5c31\u66f4\u4e0d\u5fc5\u63d0\u4e86\u3002\u5979\u6b32\u54ed\u65e0\u6cea\uff0c\u9065\u671b\u8fdc\u65b9\uff0c\u826f\u4e45\uff0c\u5ffd\u7136\u629a \u5251\u53f9\u9053\uff1a\u201c\u4ed6\u8fd9\u6837\u7684\u6b7b\uff0c\u4e5f\u8fd8\u503c\u5f97\uff01\u4ed6\u7684\u5e08\u5144\u4e5d\u6cc9\u6709\u77e5\uff0c\u4e5f\u8be5\u8c05\u89e3\u4ed6\u4e86\uff01\u201d\u97e9\u5fd7\u90a6\u518d\u8bf4\u51fa\u77f3 \u5929\u6210\u4e34\u6b7b\u62dc\u6258\u8f9b\u9f99\u5b50\u7684\u8bf4\u8bdd\uff0c\u97e9\u5fd7\u90a6\u9053\uff1a\u201c\u6211\u7684\u6b66\u529f\u8fdc\u4e0d\u5982\u6842\u8d24\u5f1f\uff0c\u4f46\u8f9b\u9f99\u5b50\u65e2\u8f6c\u6258\u4e86\u6211\uff0c \u6211\u5c31\u66ff\u4ed6\u6536\u5f92\uff0c\u4e92\u76f8\u7814\u4e60\u8fbe\u6469\u79d8\u6280\u5427\u3002\u81f3\u4e8e\u77f3\u8001\u80fd\u8f88\u7684\u9ab8\u9aa8\uff0c\u5c06\u6765\u6842\u8d24\u5f1f\u518d\u5e26\u5230\u5251\u9601\u53bb\u548c\u6842 \u8001\u524d\u8f88\u5408\u846c\u3002\u201d \u3000\u3000\u5f53\u4e0b\u5085\u9752\u4e3b\u7565\u4f5c\u5b89\u6392\uff0c\u5c31\u548c\u97e9\u5fd7\u90a6\u3001\u6b66\u743c\u7476\u3001\u6613\u5170\u73e0\u3001\u6842\u4ef2\u660e\u3001\u5192\u6d63\u83b2\u3001\u77f3\u5927\u5a18\u7b49\u516d\u4eba \u4e00\u540c\u51fa\u53d1\uff0c\u7559\u4e0b\u674e\u601d\u6c38\u3001\u6b66\u5143\u82f1\u3001\u6768\u4e00\u7ef4\u3001\u534e\u7d2b\u5c71\u3001\u5f20\u534e\u662d\u7b49\u4eba\u5e2e\u52a9\u98de\u7ea2\u5dfe\u3002 \u3000\u3000\u5085\u9752\u4e3b\u7b49\u5feb\u9a6c\u8d76\u5230\u5580\u5c14\u6c81\u8349\u539f\uff0c\u5218\u90c1\u82b3\u517b\u606f\u51e0\u5929\uff0c\u4f24\u52bf\u5df2\u6e10\u597d\u8f6c\u3002\u5f97\u5085\u9752\u4e3b\u7ed9\u5979\u533b\u6cbb\uff0c \u679c\u7136\u836f\u5230\u56de\u6625\uff0c\u4e0d\u6d88\u51e0\u5929\uff0c\u5218\u90c1\u82b3\u8eab\u4f53\u4e0a\u7684\u521b\u4f24\u5df2\u5b8c\u5168\u533b\u597d\uff0c\u53ef\u662f\u5fc3\u7075\u4e0a\u7684\u521b\u4f24\u5374\u53cd\u52a0\u91cd\u8d77 \u6765\u3002\u56e0\u4e3a\u51cc\u672a\u98ce\u4e0b\u843d\u672a\u660e\uff0c\u81f3\u4eca\u4ecd\u662f\u6beb\u65e0\u6d88\u606f\uff0c\u6613\u5170\u73e0\u4e5f\u56e0\u6b64\u7cbe\u795e\u6194\u60b4\uff0c\u90c1\u95f7\u96be\u4ee5\u8a00\u5ba3\u3002\u4f46 \u89c1\u5218\u90c1\u82b3\u4f24\u5fc3\uff0c\u5979\u53ea\u80fd\u6291\u7740\u54c0\u4f24\uff0c\u4e3a\u5979\u5f00\u89e3\u3002\u6613\u5170\u73e0\u8bf4\uff1a\u201c\u6211\u7684\u53d4\u53d4\u7edd\u4e16\u6b66\u529f\uff0c\u6599\u60f3\u6709\u60ca\u65e0 \u9669\u3002\u201d\u5218\u90c1\u82b3\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u654c\u4eba\u592a\u591a\uff0c\u5c06\u4ed6\u5bb3\u4e86\u3002\u201d\u53c8\u9053\uff1a\u201c\u82e5\u4ed6\u672a\u6b7b\uff0c\u4e3a\u4f55\u8fd8\u4e0d\u56de \u6765\uff1f\u201d\u6613\u5170\u73e0\u767e\u822c\u5b89\u6170\uff0c\u5979\u603b\u662f\u90c1\u90c1\u4e0d\u6b22\u3002\u5192\u6d63\u83b2\u773c\u73e0\u4e00\u8f6c\uff0c\u5ffd\u7136\u62cd\u638c\u8bf4\u9053\u3002\u201d\u6211\u4eec\u4f55\u4e0d\u53bb \u627e\u7eb3\u5170\u516c\u5b50\uff0c\u8bf7\u4ed6\u6253\u63a2\u4e00\u4e0b\u51cc\u5927\u4fa0\u7684\u6d88\u606f\uff1f\u82e5\u679c\u51cc\u5927\u4fa0\u662f\u88ab\u6e05\u519b\u4fd8\u864f\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u77e5\u9053\u7684\u3002\u201d \u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u767e\u4e07\u519b\u4e2d\uff0c\u4f60\u5982\u4f55\u80fd\u591f\u8fdb\u53bb\uff1f\u4f55\u51b5\u4ed6\u662f\u6e05\u5e1d\u5ba0\u81e3\uff0c\u53c8\u5982\u4f55\u80af\u544a\u8bc9\u4f60\uff1f\u201d\u5192\u6d63\u83b2 \u9053\uff1a\u201c\u6211\u6539\u88c5\u4f5c\u7267\u7f8a\u59d1\u5a18\uff0c\u5085\u4f2f\u4f2f\u966a\u6211\u53bb\u3002\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u7eb3\u5170\u516c\u5b50\u4e0d\u662f\u5e38\u4eba\uff0c\u82e5\u89c1\u7740\u4e86\uff0c \u4e5f\u8bb8\u53ef\u4ee5\u5f97\u5230\u4e00\u4e9b\u6d88\u606f\u3002\u201d\u6842\u4ef2\u660e\u6ee1\u6000\u4e0d\u60a6\uff0c\u4f46\u4e00\u8f6c\u5ff5\u8fd9\u662f\u4e3a\u4e86\u51cc\u672a\u98ce\u7684\u4e8b\uff0c\u4e5f\u4fbf\u4e0d\u4f5c\u58f0 \u4e86\u3002 \u3000\u3000\u5085\u9752\u4e3b\u533b\u672f\u7cbe\u6e5b\uff0c\u4ed6\u81ea\u5236\u6709\u201c\u6613\u5bb9\u4e39\u201d\uff0c\u80fd\u6539\u53d8\u4eba\u7684\u8138\u578b\u9762\u8c8c\uff08\u8fd9\u5176\u5b9e\u4e5f\u6ca1\u6709\u4ec0\u4e48\u795e \u79d8\uff0c\u53ea\u662f\u4e00\u79cd\u9ad8\u660e\u7684\u5316\u88c5\u672f\u800c\u5df2\uff0c\u4e0d\u8fc7\u5728\u4ed6\u4eec\u90a3\u4e2a\u65f6\u4ee3\uff0c\u8fd8\u662f\u88ab\u4eba\u79f0\u4e3a\u795e\u5947\u7684\uff09\u3002\u4e24\u4eba\u64e6\u4e86 \u201c\u6613\u5bb9\u4e39\u201d\uff0c\u5f62\u8c8c\u4ecd\u7136\u4fdd\u6301\u539f\u6765\u7684\u8f6e\u5ed3\uff0c\u4f46\u4e0d\u662f\u5f88\u719f\u7684\u4eba\u5df2\u770b\u4e0d\u51fa\u6765\u4e86\u3002\u5218\u90c1\u82b3\u63e1\u7740\u5192\u6d63\u83b2 \u7684\u624b\uff0c\u611f\u6fc0\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u97e9\u5fd7\u90a6\u770b\u5728\u773c\u4e2d\uff0c\u5fc3\u4e2d\u4e5f\u6709\u8bb8\u591a\u611f\u89e6\u3002 \u3000\u3000\u4e14\u8bf4\u7eb3\u5170\u5bb9\u82e5\u8fd9\u6b21\u51fa\u5f81\uff0c\u539f\u975e\u6240\u613f\u3002\u4ed6\u8fd9\u4e9b\u5e74\u6765\u4e13\u5fc3\u7814\u7a76\u6613\u7ecf\u548c\u5510\u4ee3\u4ee5\u4e0b\u7684\u7ecf\u5b66\u4e66\u7c4d\uff0c \u6b63\u5728\u7f16\u4e00\u90e8\u5927\u4e66\uff0c\u5df2\u5b9a\u540d\u4e3a\u300a\u901a\u5fd7\u5802\u7ecf\u89e3\u300b\uff0c\u4ed6\u662f\u60f3\u4ee5\u6b64\u4e3a\u201c\u540d\u5c71\u4e8b\u4e1a\u201d\u7684\uff0c\u4e0d\u6599\u5eb7\u7199\u5374\u62c9 \u4ed6\u5230\u7edd\u585e\u7a77\u8fb9\uff0c\u53bb\u6253\u56de\u4eba\u85cf\u4eba\u3002\u4ed6\u773c\u89c1\u6e05\u519b\u6a2a\u8d8a\u8349\u539f\uff0c\u6740\u5bb3\u4e86\u65e0\u6570\u725b\u7f8a\uff0c\u5e26\u7ed9\u8349\u539f\u4e0a\u7684\u7267\u6c11 \u65e0\u7a77\u707e\u96be\uff0c\u5fc3\u4e2d\u5f88\u662f\u4e0d\u5fcd\uff0c\u53ef\u662f\u4ed6\u8eab\u4e3a\u8d35\u65cf\u3002\u53c8\u4e0d\u80fd\u516c\u7136\u53db\u9006\uff0c\u7cbe\u795e\u4e0a\u82e5\u95f7\u5f02\u5e38\uff0c\u8fd9\u65e5\u4ed6\u5df2 \u968f\u5927\u519b\u8fdb\u5230\u675f\u52d2\uff0c\u8ddd\u79bb\u85cf\u8fb9\u4e0d\u8fdc\u4e86\uff0c\u7acb\u9a6c\u9ad8\u539f\uff0c\u53ea\u89c1\u6f2b\u5929\u98de\u96ea\uff0c\u5927\u5730\u5982\u5806\u743c\u780c\u7389\uff0c\u5c71\u5934\u5982\u5012 \u6302\u94f6\u86c7\uff0c\u4e0d\u89c9\u4e00\u7247\u82cd\u51c9\u4e4b\u611f\uff0c\u60f3\u8d77\u81ea\u5df1\u7231\u59bb\u6b7b\u540e\uff0c\u5df2\u65e0\u77e5\u5fc3\u4e4b\u4eba\uff0c\u6b32\u767d\u9996\u7a77\u7ecf\uff0c\u53c8\u88ab\u8feb\u968f\u519b \u5f81\u6218\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u56de\u5230\u8425\u4e2d\uff0c\u63d0\u8d77\u72fc\u6beb\uff0c\u968f\u624b\u5728\u9526\u7b3a\u4e0a\u5199\u9053\uff1a \u3000\u3000\u201c\u975e\u5173\u7656\u7231\u8f7b\u6a21\u6837\uff0c\u51b7\u5904\u504f\u6302\uff1b\u522b\u6709\u6839\u82bd\uff0c\u4e0d\u662f\u4eba\u95f4\u5bcc\u8d35\u82b1\u3002 \u3000\u3000\u8c22\u5a18\u522b\u540e\u8c01\u80fd\u60dc\uff0c\u98d8\u6cca\u5929\u6daf\uff0c\u5bd2\u6708\u60b2\u524d\uff0c\u4e07\u91cc\u897f\u98ce\u701a\u6d77\u6c99\uff01\u201d \u3000\u3000\u518d\u586b\u4e0a\u8bcd\u724c\u540d\u201c\u91c7\u6851\u5b50\u201d\uff0c\u5728\u8bcd\u540d\u4e0b\u6ce8\u9053\uff1a\u201c\u585e\u4e0a\u548f\u96ea\u82b1\u201d\u3002\u60f3\u9053\uff1a\u201c\u6211\u4e5f\u50cf\u585e\u4e0a\u7684\u96ea \u82b1\u4e00\u6837\uff0c\u504f\u7231\u51b7\u5904\u3002\u4e0d\u559c\u7e41\u534e\u3002\u53ef\u662f\u6211\u867d\u522b\u6709\u6839\u82bd\uff0c\u5374\u504f\u504f\u751f\u4f5c\u4eba\u95f4\u5bcc\u8d35\u82b1\u3002\u8fd9\u4e5f\u771f\u662f\u9020\u5316 \u5f04\u4eba\u4e86\uff01\u201d\u4ed6\u586b\u597d\u65b0\u8bcd\uff0c\u60f3\u627e\u4eba\u6b23\u8d4f\uff0c\u5374\u53c8\u4e0d\u7981\u56db\u987e\u832b\u7136\u5fc3\u4e2d\u81ea\u53f9\uff1a\u201c\u7231\u59bb\u548c\u59d1\u59d1\u6b7b\u540e\uff0c\u60f3 \u627e\u4e2a\u4eba\u8c08\u5fc3\u4e5f\u96be\u4e86\u3002\u201d\u4e0d\u77e5\u600e\u7684\uff0c\u5ffd\u7136\u60f3\u8d77\u5192\u6d63\u83b2\u6765\uff0c\u201c\u4e0d\u77e5\u8fd9\u4f4d\u7cbe\u901a\u97f3\u5f8b\uff0c\u5999\u89e3\u8bd7\u8bcd\u7684\u6c5f \u6e56\u5947\u5973\u5b50\uff0c\u5982\u4eca\u6d41\u6d6a\u4f55\u65b9\uff1f\u201d\u4e0d\u89c9\u53c8\u63d0\u8d77\u7b14\u6765\uff0c\u586b\u4e86\u4e00\u9996\u201c\u70f7\u6eaa\u6c99\u201d\u9053\uff1a \u3000\u3000\u201c\u8c01\u9053\u98d8\u96f6\u4e0d\u53ef\u601c\uff0c\u65e7\u6e38\u65f6\u8282\u597d\u82b1\u5929\uff0c\u65ad\u80a0\u4eba\u53bb\u81ea\u7ecf\u5e74\u3002 \u3000\u3000\u4e00\u7247\u6655\u7ea2\u7591\u7740\u96e8\u3001\u665a\u98ce\u5439\u743c\u9b13\u4e91\u504f\uff0c\u60c5\u9b42\u9500\u5c3d\u5915\u9633\u524d\uff01\u201d \u3000\u3000\u63b7\u7b14\u957f\u53f9\uff0c\u60f3\u8d77\u53bb\u5e74\u590f\u79cb\u4e4b\u4ea4\uff0c\u548c\u5192\u6d63\u83b2\u540c\u8d4f\u8377\u82b1\u7684\u60c5\u666f\uff0c\u4e0d\u89c9\u795e\u9a70\uff01\u6b63\u5728\u6b64\u65f6\uff0c\u5ffd\u542c \u5f97\u8425\u95e8\u5916\u4e00\u9635\u55a7\u54d7\u9f13\u566a\u2026\u2026 \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u51fa\u6765\u89c2\u770b\uff0c\u89c1\u5175\u58eb\u56f4\u7740\u4e00\u4e2a\u8001\u4eba\u548c\u4e00\u4e2a\u5c11\u5973\uff0c\u5728\u90a3\u91cc\u4e89\u5435\uff0c\u8425\u5e10\u8fdc\u5904\u7f8a\u7fa4\u6b63\u5728 \u9003\u6563\uff0c\u90a3\u8001\u4eba\u548c\u5c11\u5973\uff0c\u90fd\u662f\u54c8\u8428\u514b\u4eba\u6253\u626e\uff0c\u8001\u7684\u77ed\u9b03\u5982\u621f\uff0c\u72b6\u9887\u7c97\u8c6a\uff0c\u4f46\u7ec6\u770b\u4e4b\u4e0b\uff0c\u7c97\u8c6a\u4e2d \u5374\u53c8\u9690\u6709\u61e6\u96c5\u4e4b\u6c14\uff0c\u90a3\u5c11\u5973\u957f\u7709\u5982\u753b\uff0c\u74dc\u5b50\u8138\u578b\uff0c\u7709\u6e05\u76ee\u79c0\uff0c\u6709\u6c5f\u5357\u5c11\u5973\u7684\u98ce\u97f5\u3002\u5175\u58eb\u4eec\u563b \u76ae\u7b11\u8138\u5730\u5411\u90a3\u5c11\u5973\u8c03\u7b11\uff0c\u7eb3\u5170\u5bb9\u82e5\u4e0a\u524d\u559d\u6b62\uff0c\u7a76\u95ee\u60c5\u7531\uff0c\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6211\u4eec\u7684\u7f8a\u7fa4\u7ed9\u4f60\u4eec\u5175 \u7237\u7684\u6218\u9a6c\u51b2\u6563\u4e86\uff0c\u6211\u8fd8\u6ca1\u5411\u4ed6\u4eec\u7d22\u8d54\uff0c\u4ed6\u4eec\u53cd\u800c\u628a\u6211\u62c9\u5230\u8fd9\u91cc\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u76b1\u76b1\u7709\u5934\uff0c\u6599\u60f3 \u5fc5\u662f\u58eb\u5175\u89c1\u5979\u8c8c\u7f8e\uff0c\u6545\u610f\u6270\u5f04\u5979\u7684\uff0c\u6e05\u519b\u52ab\u743c\u725b\u4e30\uff0c\u6b8b\u5bb3\u767e\u59d3\u90fd\u662f\u5e38\u4e8b\uff0c\u4f55\u51b5\u51b2\u6563\u7f8a\u7fa4\u3002\u7eb3 \u5170\u5bb9\u82e5\u5bf9\u6e05\u519b\u7eaa\u5f8b\u4e4b\u574f\uff0c\u751a\u611f\u75db\u5fc3\uff0c\u6b63\u60f3\u53f1\u8d23\uff0c\u4f46\u89c1\u90a3\u5c11\u5973\u4f83\u4f83\u800c\u8c08\uff0c\u7591\u5fc3\u5927\u8d77\u3002\u8349\u539f\u4e0a\u7684 \u5987\u513f\u89c1\u5230\u6e05\u519b\uff0c\u5982\u7f8a\u9047\u72fc\u7fa4\uff0c\u907f\u4e4b\u552f\u6050\u4e0d\u53ca\uff0c\u5982\u4f55\u6562\u8fd9\u6837\u4e0e\u4eba\u7406\u8bba\uff1f\u56e0\u6b64\u6b32\u8a00\u53c8\u6b62\uff0c\u53cd\u8bd8\u95ee \u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u662f\u54ea\u91cc\u7684\u4eba\uff1f\u5927\u519b\u9a7b\u624e\u4e4b\u5730\uff0c\u5982\u4f55\u5bb9\u5f97\u4f60\u5728\u6b64\u653e\u7f8a\uff1f\u201d\u90a3\u5c11\u5973\u201c\u54ce\u54df\u201d\u4e00\u58f0 \u53eb\u8d77\u6765\u9053\uff1a\u201c\u504c\u5927\u4e00\u4e2a\u8349\u539f\uff0c\u4e0d\u8bb8\u653e\u7f8a\uff0c\u96be\u9053\u53eb\u6211\u4eec\u559d\u897f\u5317\u98ce\uff1f\u201d\u7eb3\u5170\u5bb9\u82e5\u9762\u8272\u4e00\u6c89\uff0c\u90a3\u5e74 \u8001\u7684\u7267\u4eba\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u6211\u7684\u95fa\u5973\u4e0d\u61c2\u8bf4\u8bdd\uff0c\u5c06\u519b\u4f60\u591a\u5305\u6db5\u5219\u4e2a\u3002\u7f8a\u7fa4\u6211\u4eec\u4e5f\u4e0d\u613f\u8981\u4e86\uff0c\u4f60\u653e \u6211\u4eec\u8d70\u5427\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u6545\u610f\u677f\u8d77\u8138\u5b54\u8bf4\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u975e\u7f5a\u4e0d\u53ef\uff01\u201d\u519b\u58eb\u4eec\u89c1\u7eb3\u5170\u516c\u5b50\u975e\u4f46\u4e0d \u52a0\u8d23\u5907\uff0c\u53cd\u800c\u8892\u62a4\u4ed6\u4eec\uff0c\u5927\u4e3a\u9ad8\u5174\uff0c\u4f46\u53c8\u6015\u7eb3\u5170\u516c\u5b50\u771f\u7684\u8d23\u7f5a\u90a3\u4e2a\u5c11\u5973\uff0c\u4e8e\u662f\u4e03\u5634\u516b\u820c\u5730\u53eb \u9053\uff1a\u201c\u7f5a\u5979\u5439\u6bb5\u7b1b\u5b50\u5427\uff0c\u5979\u5439\u5f97\u771f\u597d\u542c\uff01\u201d\u7eb3\u5170\u5bb9\u82e5\u89c1\u5c11\u5973\u624b\u4e2d\u62ff\u7740\u4e00\u652f\u77ed\u7b1b\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a \u201c\u662f\u5417\uff1f\u201d\u5175\u58eb\u4eec\u9053\uff1a\u201c\u521a\u624d\u6211\u4eec\u8fd8\u770b\u89c1\u5979\u4e00\u9762\u653e\u7f8a\uff0c\u4e00\u9762\u5439\u7740\u7b1b\u5b50\u5531\u6b4c\u5462\uff01\u201d\u7eb3\u5170\u5bb9\u82e5\u9762 \u8272\u4e00\u7aef\uff0c\u715e\u6709\u4ecb\u4e8b\u5730\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u6b21\u4ece\u8f7b\u5904\u7f5a\uff0c\u5c31\u7f5a\u4f60\u5439\u4e00\u6bb5\u7b1b\u5b50\uff01\u201d\u7267\u7f8a\u5c11\u5973\u5658\u7740\u5634\u513f\uff0c \u8001\u4eba\u9053\uff1a\u201c\u513f\u554a\uff0c\u4f60\u5c31\u5439\u4e00\u6bb5\u5427\uff01\u201d\u5c11\u5973\u62c8\u8d77\u7b1b\u5b50\u8d4c\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff01\u5439\u5c31\u5439\uff01\u201d\u624b\u6307\u4e00 \u6309\uff0c\u5439\u51fa\u4e00\u6bb5\u6fc0\u6124\u6e05\u8d8a\u7684\u8c03\u5b50\u6765\uff0c\u8001\u4eba\u5531\u8bcd\u76f8\u548c\uff0c\u7eb3\u5170\u5bb9\u82e5\u4e00\u542c\uff0c\u542c\u5f97\u5446\u4e86\uff0c\u5979\u5439\u7684\u7adf\u662f\u81ea \u5df1\u65e5\u524d\u5199\u5728\u77f3\u58c1\u4e0a\u90a3\u9996\u201c\u6c81\u56ed\u6625\u201d\uff0c\u4ece\u201c\u8bd5\u671b\u9634\u5c71\uff0c\u9eef\u7136\u6d88\u9b42\uff0c\u65e0\u8a00\u5f98\u5f8a\u3002\u201d\u4e00\u76f4\u5439\u5230\u201c\u5411 \u897f\u98ce\u56de\u9996\uff0c\u767e\u4e8b\u582a\u54c0\uff01\u201d \u3000\u3000\u8fd9\u9996\u8bcd\u662f\u7eb3\u5170\u5bb9\u82e5\u534a\u6708\u524d\u9a7b\u519b\u5357\u7586\u65f6\u5199\u5728\u77f3\u58c1\u4e0a\u7684\uff0c\u4ed6\u4e0d\u89e3\u5c11\u5973\u5982\u4f55\u80fd\u591f\u770b\u5230\uff1f\u5373\u7b97\u770b \u5230\uff0c\u600e\u4e48\u8fd9\u6837\u5feb\u5c31\u5230\u6b64\u5730\uff1f\u96be\u9053\u662f\u4e13\u8bda\u6765\u627e\u81ea\u5df1\uff1f\u5fc3\u4e2d\u6ee1\u5e03\u7591\u4e91\uff0c\u5b58\u5fc3\u8bd5\u4e00\u8bd5\u5979\uff0c\u6447\u6447\u5934 \u9053\uff1a\u201c\u8fd9\u652f\u5439\u5f97\u4e0d\u597d\uff0c\u7f5a\u4f60\u53e6\u5916\u6e05\u5531\u4e00\u652f\u3002\u201d\u5175\u58eb\u4eec\u8f70\u7136\u9053\u597d\uff0c\u5c11\u5973\u626d\u4e0d\u8fc7\uff0c\u773c\u6ce2\u6d41\u8f6c\uff0c\u655b \u895f\u6905\u659c\u9633\u4e00\u798f\uff0c\u5531\u8d77\u6765\u9053\uff1a\u201c\u77ac\u606f\u6d6e\u751f\uff0c\u4fdd\u72d0\u5982\u65af\uff0c\u4f4e\u5f8a\u600e\u5fd8\uff1f\u8bb0\u7ee3\u69bb\u95f2\u65f6\uff0c\u5e76\u5439\u7ea2\u96e8\uff0c\u96d5 \u680f\u66f2\u5904\uff0c\u540c\u501a\u659c\u9633\uff0c\u68a6\u597d\u96be\u7559\uff0c\u8bd7\u6210\u83ab\u7eed\uff0c\u8d62\u5f97\u66f4\u6df1\u54ed\u4e00\u573a\u2026\u2026\u201d\u7eb3\u5170\u4e00\u542c\uff0c\u66f4\u662f\u60ca\u5947\uff0c\u8fd9 \u9996\u8bcd\u4e43\u662f\u4ed6\u60bc\u4ea1\u8bcd\u4e2d\u5455\u5fc3\u6ca5\u8840\u4e4b\u4f5c\uff0c\u4e5f\u6b63\u662f\u53bb\u5e74\u5728\u76f8\u5e9c\u7684\u5927\u82b1\u56ed\u4e2d\uff0c\u521d\u89c1\u5192\u6d63\u83b2\u65f6\uff0c\u81ea\u5df1\u53eb \u6b4c\u5973\u6240\u5531\u7684\u90a3\u9996\uff0c\u5f53\u65f6\u5192\u6d63\u83b2\u8fd8\u662f\u7537\u5b50\u6253\u626e\uff0c\u542c\u6b4c\u4e4b\u540e\uff0c\u5c31\u548c\u81ea\u5df1\u501a\u680f\u8c08\u8bcd\uff0c\u4e34\u6d41\u8d4f\u8377\uff0c\u7eb3 \u5170\u5bb9\u82e5\u5fc3\u9b42\u4e00\u8361\uff0c\u76ef\u4e86\u8fd9\u5c11\u5973\u4e00\u773c\uff0c\u8eab\u6750\u679c\u4f3c\u5192\u6d63\u83b2\u8f6e\u5ed3\uff0c\u53ef\u662f\u8138\u578b\u76f8\u8c8c\uff0c\u5374\u53c8\u4e0d\u540c\uff0c\u6b63\u5728 \u60ca\u5947\uff0c\u5c11\u5973\u773c\u73e0\u6ef4\u6e9c\u6e9c\u5730\u5411\u81ea\u5df1\u4e00\u8f6c \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u66ae\u7136\u60f3\u8d77\u5192\u6d63\u83b2\u90a3\u65f6\u660e\u5982\u79cb\u6c34\u7684\u773c\u775b\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u518d\u4ed4\u7ec6\u770b\u65f6\uff0c\u89c9\u5f97\u90a3\u5c11\u5973\u8eab \u6750\u597d\u719f\uff0c\u7adf\u9690\u9690\u4f3c\u5192\u6d63\u83b2\u7684\u8f6e\u5ed3\u3002\u4ed6\u5927\u611f\u60ca\u5947\uff0c\u4e8e\u662f\u65a5\u6563\u58eb\u5175\uff0c\u5e26\u8fd9\u4e24\u201c\u7236\u5973\u201d\u8fdb\u5165\u5e10\u5185\u3002 \u3000\u3000\u5192\u6d63\u83b2\u6602\u7136\u4e0d\u60e7\uff0c\u968f\u7eb3\u5170\u8d70\u8fdb\u6e05\u8425\u3002\u7eb3\u5170\u5bb9\u82e5\u72ec\u6398\u4e00\u4e2a\u5e10\u7bf7\uff0c\u867d\u5728\u884c\u519b\u4e4b\u4e2d\uff0c\u4e5f\u5e03\u7f6e\u5f97 \u975e\u5e38\u96c5\u6d01\u3002\u4ed6\u5c4f\u9000\u536b\u5352\uff0c\u8bf7\u5085\u9752\u4e3b\u548c\u5192\u6d63\u83b2\u5750\u4e0b\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5927\u539a\u7a77\u8352\uff0c\u77e5\u97f3\u96be\u89c5\uff0c\u4eca\u65e5 \u4e00\u4f1a\uff0c\u4ee4\u4eba\u5fc3\u6298\uff0c\u4f46\u62d9\u8bcd\u6d45\u964b\uff0c\u4e0d\u503c\u4e00\u6b4c\u518d\u6b4c\uff0c\u8bf7\u59d1\u5a18\u5b50\u996e\u6c34\u8bcd\u5916\u518d\u8c31\u4e00\u8c03\u5982\u4f55\uff1f\u201d\u5192\u6d63\u83b2 \u76c8\u76c8\u4e00\u7b11\u9053\uff1a\u201c\u516c\u5b50\u4f55\u524d\u5014\u800c\u540e\u606d\uff1f\u201d\u5c06\u77ed\u7b1b\u9012\u7ed9\u5085\u9752\u4e3b\u5439\u548c\uff0c\u8f7b\u542f\u6731\u5589\uff0c\u6b4c\u9053\uff1a \u3000\u3000\u201c\u5b63\u5b50\u5e73\u5b89\u5426\uff1f\u4fbf\u5f52\u6765\uff0c\u5e73\u751f\u4e07\u4e8b\uff0c\u90a3\u582a\u56de\u9996\uff1f\u884c\u8def\u60a0\u60a0\u8c01\u6170\u85c9\uff1f\u6bcd\u8001\u5bb6\u8d2b\u5b50\u5e7c\u3002\u8bb0\u4e0d \u8d77\u4ece\u524d\u676f\u9152\u3002\u9b51\u9b45\u640f\u4eba\u5e94\u89c1\u60ef\uff0c\u603b\u8f93\u4ed6\u8986\u96e8\u7ffb\u4e91\u624b\uff0c\u51b0\u4e0e\u96ea\uff0c\u5468\u65cb\u4e45\u3002\u6cea\u75d5\u83ab\u6ef4\u725b\u8863\u900f\uff0c\u6570 \u5929\u6daf\u4f9d\u7136\u9aa8\u8089\uff0c\u51e0\u5bb6\u80fd\u591f\uff1f\u6bd4\u4f3c\u7ea2\u989c\u591a\u547d\u7c3f\uff0c\u66f4\u4e0d\u5982\u4eca\u8fd8\u6709\u3002\u53ea\u7edd\u585e\u82e6\u5bd2\u96be\u53d7\u3002\u7518\u8f7d\u5305\u80e5\u627f \u4e00\u8bfa\uff0c\u76fc\u4e4c\u5934\u9a6c\u89d2\u7ec8\u76f8\u6551\u3002\u7f6e\u76f8\u672d\uff0c\u541b\u6000\u3002\u8896\u3002\u201d \u3000\u3000\u8fd9\u65e8\u201c\u91d1\u697c\u66f2\u201d\u662f\u7eb3\u5170\u597d\u53cb\u987e\u6881\u6c7e\u6240\u4f5c\uff0c\u5176\u4e2d\u542b\u6709\u4e00\u6bb5\u52a8\u4eba\u7684\u6545\u4e8b\u3002\u5eb7\u7199\u521d\u5e74\uff0c\u7eb3\u5170\u7684 \u53e6\u4e00\u4f4d\u670b\u53cb\u5434\u6c49\u69ce\u88ab\u5145\u519b\u5230\u5173\u5916\u7684\u5b81\u53e4\u5854\uff0c\u987e\u6881\u6c7e\u4e43\u662f\u4ed6\u7684\u77e5\u4ea4\uff0c\u7279\u4e3a\u6b64\u586b\u4e86\u4e24\u9996\u201c\u91d1\u7f15 \u66f2\u201d\u5bc4\u7ed9\u7eb3\u5170\u5bb9\u82e5\uff0c\u671b\u4ed6\u63f4\u6551\uff0c\u5192\u6d63\u83b2\u6b4c\u7684\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u8fd9\u4e24\u9996\u8bcd\u60b2\u6df1\u611f\u5207\uff0c\u7eb3\u5170\u5bb9\u82e5\u770b \u4e86\u5927\u4e3a\u611f\u52a8\uff0c\u5c31\u4ee3\u5411\u7236\u4eb2\u6c42\u60c5\uff0c\u628a\u5434\u6c49\u69ce\u6551\u4e86\u56de\u6765\uff0c\u5192\u6d63\u83b2\u800c\u4eca\u6b4c\u6b64\uff0c\u5176\u4e2d\u5927\u6709\u6df1\u610f\u3002 \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u806a\u660e\u7edd\u9876\uff0c\u95fb\u6b4c\u4f1a\u610f\uff0c\u6168\u7136\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4ec0\u4e48\u4eb2\u670b\uff0c\u65e0\u8f9c\u88ab\u6355\u4e86\u4e48\uff1f\u201d\u5192\u6d63 \u83b2\u9053\uff1a\u201c\u516c\u5b50\u53ef\u613f\u63f4\u624b\uff1f\u201d\u7eb3\u5170\u9053\uff1a\u201c\u8981\u770b\u4ed6\u662f\u4f55\u7b49\u6837\u4eba\uff1f\u82e5\u662f\u50cf\u5434\u6c49\u69ce\u90a3\u6837\u7684\u540d\u58eb\uff0c\u6211\u4e5f \u613f\u2018\u4e4c\u5934\u9a6c\u89d2\u7ec8\u76f8\u6551\u2019\u7684\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u5434\u6c49\u69ce\u662f\u72c2\u50b2\u4e66\u751f\uff0c\u6211\u7684\u670b\u53cb\u5374\u662f\u4e00\u4ee3\u5947\u4fa0\u3002\u201d \u7eb3\u5170\u52a8\u5bb9\u95ee\u9053\uff1a\u201c\u8c01\uff1f\u201d\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u66fe\u4ee4\u5f53\u4eca\u7687\u4e0a\u5bdd\u98df\u4e0d\u5b89\u7684\u51cc\u672a\u98ce\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u609a\u7136 \u4e00\u60ca\uff0c\u5b9a\u4e86\u773c\u775b\uff0c\u8feb\u89c6\u5192\u6d63\u83b2\u548c\u5085\u9752\u4e3b\uff0c\u5192\u6d63\u83b2\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u8001\u670b\u53cb\u90fd\u8ba4\u4e0d\u5f97\u4e86\u4e48\uff1f\u201d\u7eb3\u5170 \u5bb9\u82e5\u60ca\u559c\u4ea4\u96c6\uff0c\u4e0d\u89c9\u63e1\u7740\u5192\u6d63\u83b2\u7684\u53cc\u624b\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u5192\u6d63\u83b2\u59d1\u5a18\u4e48\uff1f\u600e\u4e48\u76f8\u8c8c\u90fd\u53d8\u4e86\uff1f\u8fd9 \u4f4d\u53c8\u662f\u8c01\u4eba\uff1f\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u8fd9\u4f4d\u4fbf\u662f\u5f53\u4eca\u7684\u795e\u533b\u56fd\u624b\u5085\u9752\u4e3b\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u653e\u5f00\u4e86\u5192\u6d63\u83b2\uff0c \u53c8\u7d27\u63e1\u5085\u9752\u4e3b\u7684\u624b\uff0c\u8fde\u9053\u4ef0\u5e55\u3002\u5085\u9752\u4e3b\u9664\u4e86\u533b\u9053\u9ad8\u660e\uff0c\u53c8\u662f\u4e66\u753b\u540d\u5bb6\uff0c\u8bd7\u6587\u4e5f\u597d\uff0c\u7b97\u6765\u8fd8\u662f \u7eb3\u5170\u7684\u524d\u8f88\u3002\u7eb3\u5170\u6ce8\u89c6\u8bb8\u4e45\u9053\uff1a\u201c\u6211\u4e0e\u5085\u8001\u5148\u751f\u795e\u4ea4\u5df2\u4e45\uff0c\u5728\u5bab\u4e2d\u4e5f\u89c1\u8fc7\u524d\u8f88\u7684\u753b\u50cf\uff0c\u5bb9\u6211 \u5192\u6627\u4e00\u95ee\uff0c\u600e\u4e48\u76f8\u8c8c\u4e5f\u4e0e\u753b\u50cf\u4e0d\u5927\u76f8\u540c\uff1f\u201d\u5192\u6d63\u83b2\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u5bab\u4e2d\u4e3a\u4f55\u6709\u5085\u4f2f\u4f2f\u7684\u753b \u50cf\uff1f\u201d\u7eb3\u5170\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4f60\u7684\u5462\uff01\u4f60\u4eec\u90a3\u665a\u5728\u6e05\u51c9\u5bfa\u4e00\u95f9\uff0c\u7687\u4e0a\u7acb\u523b\u53eb\u4e39\u9752\u5999\u624b\u753b\u4e86\u4f60\u4eec\u7684 \u989c\u5bb9\uff0c\u5230\u5904\u641c\u6355\u4f60\u4eec\uff0c\u4f60\u4eec\u8fd8\u4e0d\u77e5\u4e48\uff1f\u201d \u3000\u3000\u5085\u9752\u4e3b\u7b11\u9053\uff1a\u201c\u8001\u62d9\u5c31\u662f\u9884\u6599\u6709\u6b64\uff0c\u6240\u4ee5\u7565\u65bd\u5c0f\u6280\uff0c\u5c06\u672c\u6765\u9762\u76ee\u53d8\u4e86\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u5927\u4e3a \u94a6\u4f69\uff0c\u8d5e\u9053\uff1a\u201c\u5148\u751f\u533b\u672f\uff0c\u771f\u6709\u593a\u9b3c\u795e\u9020\u5316\u4e4b\u80fd\uff0c\u5192\u6d63\u83b2\u59d1\u5a18\u7684\u76f8\u8c8c\uff0c\u60f3\u4e5f\u662f\u8001\u4f2f\u65bd\u672f\u66f4\u6613 \u7684\u4e86\u3002\u201d\u5192\u6d63\u83b2\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5982\u679c\u8981\u6062\u590d\u539f\u6765\u9762\u76ee\uff0c\u53ea\u9700\u4e00\u76c6\u6e05\u6c34\u5c31\u884c\u4e86\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u6447\u624b \u9053\uff1a\u201c\u8fd8\u662f\u4e0d\u8981\u6062\u590d\u7684\u597d\u3002\u201d\u5192\u6d63\u83b2\u518d\u95ee\u8d77\u51cc\u672a\u98ce\u4e4b\u4e8b\uff0c\u7eb3\u5170\u5bb9\u82e5\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u5440\uff0c\u5f85 \u6211\u89c1\u7740\u7687\u4e0a\u65f6\uff0c\u518d\u66ff\u4f60\u4eec\u63a2\u95ee\u5427\u3002\u4f46\u6211\u4e5f\u8981\u529d\u4f60\u4eec\uff0c\u4e0d\u8981\u518d\u5728\u56de\u7586\u95f9\u4e0b\u53bb\u4e86\u3002\u6211\u4e0e\u4f60\u4eec\u4e00\u6837 \u90fd\u8ba8\u538c\u5e72\u6208\uff0c\u6e05\u519b\u6d17\u52ab\u8349\u539f\uff0c\u6211\u4e5f\u6781\u4e3a\u5185\u759a\uff0c\u53ea\u662f\u5929\u547d\u96be\u8fdd\uff0c\u5c0f\u4eba\u4e0d\u654c\uff0c\u53c8\u4f55\u82e6\u518d\u4ee4\u751f\u7075\u6d82 \u70ad\uff1f\u201d\u5192\u6d63\u83b2\u62c2\u8896\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u6b64\u8a00\u5dee\u77e3\uff0c\u516c\u5b50\u535a\u89c8\u7fa4\u4e66\uff0c\u5c82\u4e0d\u95fb\u2018\u5b81\u4e3a\u7389\u788e\u4e0d\u4e3a\u74e6\u5168\u2019\u4e4b \u8bed\uff1f\u6e05\u519b\u65e0\u6545\u5165\u4fb5\uff0c\u8349\u539f\u4e0a\u7684\u7267\u6c11\u53c8\u5c82\u80fd\u4e0d\u8d77\u6765\u53cd\u6297\uff1f\u201d\u7eb3\u5170\u5bb9\u82e5\u9ed8\u7136\u4e0d\u8bed\uff0c\u826f\u4e45\uff0c\u624d\u5f00\u58f0 \u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u6211\u4eec\u53ea\u8bba\u53cb\u60c5\uff0c\u4e0d\u8c08\u56fd\u4e8b\uff0c\u597d\u5417\uff1f\u201d\u4ed6\u7684\u5185\u5fc3\u751a\u4e3a\u77db\u76fe\u75db\u82e6\uff0c\u4e00\u65b9\u9762\u540c\u60c5\u5192\u6d63 \u83b2\u4ed6\u4eec\uff0c\u4f46\u53e6\u4e00\u65b9\u9762\u4ed6\u53c8\u4e0d\u80fd\u53db\u79bb\u7687\u5ba4\u3002\u6240\u4ee5\u53ea\u597d\u907f\u800c\u4e0d\u8c08\u3002 \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u542c\u5f97\u5e10\u5916\u8fdc\u8fdc\u7684\u559d\u9053\u58f0\uff0c\u7eb3\u5170\u5bb9\u82e5\u60ca\u9053\uff1a\u201c\u7687\u4e0a\u6765\u4e86\uff01\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6211 \u4eec\u8981\u4e0d\u8981\u6682\u907f\uff1f\u201d\u7eb3\u5170\u5bb9\u82e5\u518d\u770b\u4e86\u4ed6\u4eec\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\uff0c\u7687\u4e0a\u4e0d\u8ba4\u5f97\u4f60\u4eec\u7684\u3002\u201d\u63ed\u5f00\u5e10 \u5e55\uff0c\u5eb7\u7199\u5e26\u7740\u51e0\u4e2a\u536b\u58eb\u7f13\u7f13\u8d70\u8fdb\u3002\u5085\u9752\u4e3b\u548c\u5192\u6d63\u83b2\u8feb\u4e8e\u65e0\u5948\uff0c\u968f\u7eb3\u5170\u5bb9\u8dea\u4e0b\u8fce\u63a5\u3002\u5077\u773c\u4e00 \u77a7\uff0c\u536b\u58eb\u4e2d\u6709\u4e00\u4e2a\u6b63\u662f\u7981\u536b\u519b\u7684\u526f\u7edf\u9886\u5f20\u627f\u658c\uff0c\u4e5f\u5c31\u662f\u5f53\u5e74\u5e26\u5175\u56f4\u6b66\u5bb6\u5e84\u7684\u4eba\u3002 \u3000\u3000\u5eb7\u7199\u89c1\u7eb3\u5170\u5e10\u4e2d\u6709\u4e24\u4e2a\u964c\u751f\u4eba\uff0c\u4e5f\u9887\u60ca\u8bb6\u3002\u7eb3\u5170\u6025\u5fd9\u594f\u9053\uff1a\u201c\u65e0\u804a\u5f97\u7d27\uff0c\u8bf7\u4e00\u4e2a\u7267\u7f8a\u59d1 \u5a18\u6765\u5531\u5531\u5979\u4eec\u585e\u5916\u7684\u66f2\u513f\u3002\u201d\u5eb7\u7199\u89c1\u5192\u6d63\u83b2\u9762\u76ee\u79c0\u4e3d\uff0c\u522b\u6709\u4f1a\u5fc3\uff0c\u7b11\u4e86\u4e00\u7b3a\uff0c\u6307\u7740\u5085\u9752\u4e3b \u9053\uff1a\u201c\u8fd9\u4eba\u53c8\u662f\u8c01\uff1f\u201d\u7eb3\u5170\u9053\uff1a\u201c\u662f\u8fd9\u4e2a\u59d1\u5a18\u7684\u7239\u7239\uff0c\u4ed6\u5728\u8349\u539f\u884c\u533b\uff0c\u9887\u61c2\u5f97\u533b\u585e\u5916\u7684\u4e00\u4e9b \u5947\u96be\u6742\u75c7\u3002\u201d\u5eb7\u7199\u9053\uff1a\u201c\u4f60\u5c31\u662f\u559c\u6b22\u7ed3\u4ea4\u8fd9\u4e9b\u4e5d\u6d41\u4e09\u6559\u7684\u5947\u4eba\uff0c\u597d\uff0c\u53ea\u8981\u4f60\u9ad8\u5174\uff0c\u6211\u4e5f\u53ef\u4ee5 \u7834\u4f8b\u51c6\u4f60\u7559\u4ed6\u4eec\u5728\u519b\u4e2d\u533b\u4f4f\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u8c22\u8fc7\u7687\u6069\uff0c\u5eb7\u7199\u53c8\u9053\uff1a\u201c\u8fd9\u4eba\u65e2\u61c2\u533b\u672f\uff0c\u6715\u5c31\u8ba9\u4ed6 \u8bd5\u8bd5\u53bb\u533b\u5341\u56db\u8d1d\u5b50\u548c\u535a\u6d4e\u5c06\u519b\uff0c\u4ed6\u4eec\u4e24\u4eba\u51bb\u75ae\u53d1\u4f5c\u5f88\u662f\u5389\u5bb3\uff0c\u5582\uff01\u4f60\u61c2\u5f97\u533b\u51bb\u75ae\u5417\uff1f\u201d\u5085\u9752 \u4e3b\u9053\uff1a\u201c\u90a3\u662f\u8349\u539f\u4e0a\u5f88\u5e73\u5e38\u7684\u75c5\uff0c\u53ea\u8981\u7528\u8349\u539f\u4e0a\u7684\u4e00\u79cd\u91ce\u8349\u71ac\u6c41\u5916\u6577\uff0c\u7528\u4e0d\u5230\u4e09\u5929\uff0c\u5c31\u53ef\u533b \u597d\u3002\u201d\u5eb7\u7199\u9053\uff1a\u201c\u597d\u5440\uff01\u90a3\u4f60\u5c31\u8fdb\u53bb\u5427\uff01\u201d\u53eb\u4e00\u4e2a\u4f8d\u536b\u5f15\u4ed6\u4e0b\u53bb\uff0c\u5728\u7eb3\u5170\u8033\u8fb9\u6084\u6084\u8bf4\u9053\uff1a \u201c\u4f60\u77a7\uff0c\u6715\u5bf9\u4f60\u597d\u4e0d\u597d\uff1f\u201d\u4ed6\u4ee5\u4e3a\u7eb3\u5170\u559c\u6b22\u8fd9\u4e2a\u7267\u7f8a\u59d1\u5a18\uff0c\u6240\u4ee5\u85c9\u6545\u628a\u5979\u7684\u7239\u7239\u8c03\u5f00\uff0c\u597d\u8ba9 \u7eb3\u5170\u5355\u72ec\u548c\u5979\u4eb2\u8fd1\u3002\u7eb3\u5170\u5bb9\u82e5\u6ee1\u9762\u901a\u7ea2\uff0c\u5374\u662f\u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u5eb7\u7199\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6715\u5fa1\u9a7e\u4eb2\u5f81\uff0c\u626b\u7a74\u7281\u5ead\uff0c\u76f4\u6363\u7a77\u8fb9\uff0c\u62d3\u571f\u5f00\u7586\uff0c\u56fd\u5a01\u8fdc\u64ad\uff0c\u4f60\u719f\u8bfb\u7ecf \u53f2\uff0c\u4f60\u8bf4\u5728\u5386\u4ee3\u660e\u541b\u4e4b\u4e2d\uff0c\u6715\u662f\u5426\u53ef\u7b97\u4e00\u4e2a\u3002\u201d\u7eb3\u5170\u9053\uff1a\u201c\u965b\u4e0b\u6b66\u529f\u4e4b\u76db\uff0c\u6bd4\u4e4b\u79e6\u7687\u6c49\u6b66\u5510 \u5b97\u5b8b\u7956\uff0c\u4e0d\u901e\u591a\u8ba9\u3002\u832c\u80fd\u4f50\u4ee5\u4ec1\u653f\uff0c\u5584\u5f85\u9ece\u5eb6\uff0c\u5fc5\u66f4\u9752\u53f2\u7559\u82b3\u3002\u201d\u5eb7\u7199\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5230\u5e95\u662f \u4e66\u751f\u4e4b\u89c1\uff0c\u54b1\u4eec\u5165\u5173\u672a\u6ee1\u4e09\u5341\u5e74\uff0c\u81ea\u5f53\u5148\u4e25\u540e\u5bbd\uff0c\u82e5\u4e0d\u4e34\u4ee5\u519b\u5a01\uff0c\u5b89\u5f97\u56db\u5937\u6151\u670d\uff1f\u201d\u8c08\u4e86\u4e00 \u9635\uff0c\u5eb7\u7199\u59cb\u7ec8\u4e0d\u63d0\u8d77\u51cc\u672a\u98ce\u4e4b\u4e8b\uff0c\u5e10\u5916\u6714\u98ce\u6012\u9e23\uff0c\u8fdc\u5904\u80e1\u7b33\u60b2\u5207\uff0c\u5929\u8272\u5df2\u6e10\u9ec4\u660f\uff0c\u5eb7\u7199\u5411\u7eb3 \u5170\u8981\u4e86\u51e0\u9996\u65b0\u8bcd\uff0c\u4fbf\u5f85\u79bb\u53bb\uff0c\u7eb3\u5170\u5bb9\u82e5\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u7559\u4e0b\u5f20\u627f\u658c\u4e0e\u6211\u5982\u4f55\uff1f\u6211\u60f3\u8bf7\u6559\u4ed6 \u51e0\u624b\u6b66\u827a\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u6587\u6b66\u5168\u6750\uff0c\u8bcd\u7ae0\u4e4b\u5916\uff0c\u9a91\u5c04\u4e5f\u751a\u4e86\u5f97\uff0c\u5eb7\u7199\u7b11\u9053\uff1a\u201c\u4f60\u4eca\u65e5\u8fd8\u6709\u5982\u6b64 \u95f2\u60c5\u4e48\uff1f\u201d\u628a\u5f20\u627f\u658c\u7559\u4e0b\uff0c\u5e26\u9886\u5176\u4ed6\u536b\u58eb\u79bb\u5f00\u4e86\u7eb3\u5170\u7684\u5e10\u5e55\u3002 \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u5176\u5b9e\u5e76\u4e0d\u662f\u60f3\u5b66\u4ec0\u4e48\u6b66\u827a\uff0c\u4ed6\u77e5\u9053\u5f20\u627f\u658c\u4e0e\u695a\u662d\u5357\u4e4b\u95f4\u9887\u6709\u5fc3\u75c5\uff0c\u6240\u4ee5\u6545\u610f\u628a \u4ed6\u7559\u4e0b\uff0c\u5eb7\u7199\u8d70\u540e\uff0c\u4ed6\u64a9\u5f20\u627f\u658c\u9053\uff1a\u201c\u4f60\u5728\u5927\u5185\u6709\u4e8c\u5341\u5e74\u4e86\u5427\uff1f\u201d\u5f20\u627f\u658c\u9053\uff1a\u201c\u4e8c\u5341\u4e03\u516b\u5e74 \u4e86\uff0c\u5148\u5e1d\u767b\u4f4d\u8fd8\u672a\u6ee1\u4e09\u5e74\uff0c\u6211\u5c31\u6765\u4e86\u3002\u201d\u7eb3\u5170\u53c8\u9053\uff1a\u201c\u4f60\u73b0\u5728\u8fd8\u662f\u7981\u536b\u519b\u7684\u526f\u7edf\u9886\uff1f\u201d\u5f20\u627f \u658c\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u505a\u526f\u7edf\u9886\u4e5f\u5feb\u8fd1\u5341\u5e74\u4e86\uff01\u201d\u7eb3\u5170\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u8bf4\u9053\uff1a\u201c\u695a\u662d\u5357\u5012\u5347\u5f97\u5f88 \u5feb\u3002\u201d\u5f20\u627f\u658c\u9053\uff1a\u201c\u90a3\u662f\u5e94\u8be5\u7684\uff0c\u4ed6\u6b66\u529f\u65e2\u5f3a\uff0c\u53c8\u5c61\u7acb\u5927\u529f\uff0c\u6211\u4eec\u8fd9\u4e9b\u5148\u5e1d\u7684\u65e7\u4eba\u90fd\u6bd4\u4e0d\u4e0a \u4ed6\u3002\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u5374\u9887\u89c1\u6fc0\u6124\u4e4b\u60c5\u3002\u7eb3\u5170\u5fae\u7b11\u9053\uff1a\u201c\u662f\u5417\uff1f\u600e\u4e48\u4e0d\u89c1\u4ed6\u5462\uff1f\u201d\u5f20\u627f\u658c\u53c8\u9053\uff1a \u201c\u4ed6\u505a\u4e86\u7edf\u9886\u4e4b\u540e\uff0c\u5f1f\u5144\u4eec\u6298\u635f\u5f88\u591a\uff0c\u4f46\u4e00\u5c06\u529f\u6210\u4e07\u9aa8\u67af\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u8bf4\u7684\u3002\u201d\u7eb3\u5170\u9053\uff1a \u201c\u695a\u662d\u5357\u6700\u559c\u4e89\u529f\uff0c\u6211\u4e0d\u559c\u6b22\u4ed6\u3002\u5176\u5b9e\u561b\uff0c\u505a\u9996\u9886\u7684\u4eba\u5e94\u8be5\u5bbd\u539a\u4e00\u70b9\uff0c\u8fd9\u70b9\uff0c\u4f60\u6bd4\u4ed6\u5f3a\u591a \u4e86\u3002\u201d\u5f20\u627f\u658c\u559c\u5f62\u4e8e\u8272\uff0c\u8dea\u4e0b\u778c\u5934\u9053\uff1a\u201c\u8fd8\u671b\u516c\u5b50\u683d\u57f9\uff01\u201d\u7eb3\u5170\u6276\u4ed6\u8d77\u6765\uff0c\u5f20\u627f\u658c\u53c8\u9053\uff1a \u201c\u6700\u8fd1\u4ed6\u548c\u6210\u5929\u633a\u5e26\u4e86\u5341\u51e0\u540d\u4e00\u7b49\u536b\u58eb\u51fa\u5dee\uff0c\u9664\u4e86\u4ed6\u4eec\u4e24\u4eba\uff0c\u5176\u4f59\u5168\u90e8\u6b7b\u5149\uff0c\u53ea\u6349\u5230\u4e00\u4e2a\u654c \u4eba\u3002\u201d\u7eb3\u5170\u9053\uff1a\u201c\u554a\uff01\u90a3\u4e48\u654c\u4eba\u4e00\u5b9a\u5f88\u5389\u5bb3\u4e86\u3002\u6349\u5230\u4e86\u8c01\u5462\uff1f\u201d\u5f20\u627f\u658c\u9053\uff1a\u201c\u5c31\u662f\u4ee5\u524d\u5927\u95f9 \u5929\u7262\u7684\u90a3\u4e2a\u51cc\u672a\u98ce\u3002\u201d\u8bf4\u7f62\uff0c\u770b\u4e86\u5192\u6d63\u83b2\u4e00\u773c\uff0c\u5192\u6d63\u83b2\u6545\u610f\u4f4e\u5934\u5377\u7740\u624b\u7ee2\u73a9\u3002\u7eb3\u5170\u5fae\u7b11\u9053\uff1a \u201c\u8fd9\u4e2a\u7267\u7f8a\u59d1\u5a18\u53ef\u4e0d\u77e5\u9053\u4f60\u4ec0\u4e48\u98ce\u98ce\u96e8\u96e8\uff0c\u4f60\u4f46\u8bf4\u65e0\u59a8\u3002\u201d\u5f20\u627f\u658c\u9053\uff1a\u201c\u6298\u635f\u4e86\u8fd9\u4e48\u591a\u4eba\uff0c \u7687\u4e0a\u8fd8\u662f\u5609\u5956\u4ed6\uff01\u201d\u7eb3\u5170\u9053\uff1a\u201c\u600e\u4e48\u6211\u4e0d\u89c1\u7687\u4e0a\u63d0\u8d77\uff0c\u90a3\u4e2a\u51cc\u672a\u98ce\u6740\u6389\u4e86\u5417\uff1f\u201d\u5f20\u627f\u658c\u9053\uff1a \u201c\u7687\u4e0a\u8fd9\u4e9b\u5929\u6765\u5fd9\u4e8e\u8c03\u52a8\u5927\u519b\uff0c\u5206\u5360\u8499\u85cf\uff0c\u4eca\u5929\u624d\u7a7a\u95f2\u4e00\u70b9\u3002\u60f3\u662f\u89c1\u516c\u5b50\u6709\u5ba2\u4eba\uff0c\u6240\u4ee5\u4e0d\u63d0 \u8d77\u4e86\u3002\u51cc\u672a\u98ce\u6709\u6ca1\u6709\u6740\u6389\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u542c\u8bf4\u7687\u4e0a\u4ea4\u7ed9\u695a\u662d\u5357\u5904\u7f6e\uff0c\u53c8\u542c\u8bf4\u695a\u662d\u5357\u8fd8\u820d\u4e0d\u5f97 \u6740\u4ed6\u3002\u201d\u7eb3\u5170\u5947\u9053\uff1a\u201c\u4ed6\u4eec\u672c\u6765\u662f\u76f8\u8bc6\u7684\u670b\u53cb\u5417\uff1f\u201d\u5f20\u627f\u658c\u9053\uff1a\u201c\u5c82\u6b62\u76f8\u8bc6\uff0c\u8fd8\u662f\u5e08\u5144\u5f1f \u5462\u3002\u542c\u8bf4\u5c31\u662f\u56e0\u6b64\uff0c\u4ed6\u8981\u8feb\u51cc\u672a\u98ce\u4ea4\u51fa\u5e08\u7236\u7684\u62f3\u7ecf\u5251\u8bc0\u3002\u201d\u7eb3\u5170\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u695a\u662d\u5357\u4e0d\u62bc\u4ed6 \u5230\u8fd9\u91cc\u6765\uff1f\u201d\u5f20\u627f\u658c\u9053\uff1a\u201c\u7687\u4e0a\u6d3e\u4ed6\u53bb\u5e2e\u4e09\u8d1d\u52d2\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u542c\u81f3\u6b64\u5904\uff0c\u968f\u4fbf\u53c8\u95ee\u4e86\u51e0\u624b\u6b66 \u529f\uff0c\u4fbf\u7aef\u8336\u9001\u5ba2\u3002 \u3000\u3000\u5f20\u627f\u658c\u53bb\u540e\uff0c\u5929\u5df2\u5165\u66ae\u3002\u7687\u4e0a\u5ffd\u7136\u6d3e\u4eba\u9001\u4e86\u897f\u85cf\u7684\u9f99\u6d8e\u9999\u548c\u5bab\u5973\u7684\u9526\u8863\u6765\u3002\u7eb3\u5170\u5bb9\u82e5\u5927 \u7a98\uff0c\u5bf9\u7740\u5192\u6d63\u83b2\uff0c\u9762\u7ea2\u76f4\u900f\u8033\u6839\u3002 \u3000\u3000\u7687\u5e1d\u9001\u6765\u8fd9\u4e9b\u4e1c\u897f\uff0c\u663e\u7136\u662f\u628a\u5192\u6d63\u83b2\u5f53\u4f5c\u7eb3\u5170\u5bb9\u82e5\u65b0\u6536\u7684\u5983\u5b50\u3002\u5192\u6d63\u83b2\u795e\u8272\u81ea\u82e5\uff0c\u4f6f\u4f5c \u4e0d\u77e5\uff0c\u5f85\u4f8d\u536b\u53bb\u540e\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u826f\u670b\u76f8\u9047\uff0c\u711a\u9999\u591c\u8c08\uff0c\u4e5f\u662f\u4eba\u751f\u4e00\u5927\u5feb\u4e8b\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u89c1 \u5192\u6d63\u83b2\u5fc3\u80f8\u5f00\u6717\uff0c\u81ea\u8d23\u5fc3\u90aa\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u7761\uff0c\u6211\u4e5f\u4e0d\u7761\u597d\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u526a\u70db\u711a\u9999\uff0c\u54c1\u8317\u591c\u8bdd\u3002\u7eb3\u5170\u5bb9\u82e5\u9053\uff1a\u201c\u59d1\u5a18\u771f\u91cd\u53cb\u9053\uff0c\u4e3a\u51cc\u672a\u98ce\u5192\u6b64\u5927\u9669\u3002\u201d\u5192\u6d63 \u83b2\u9053\uff1a\u201c\u5168\u9760\u516c\u5b50\u5e2e\u5fd9\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u9053\uff1a\u201c\u695a\u662d\u5357\u5949\u6d3e\u7ed9\u5341\u56db\u7687\u5b50\u5141\u9898\u505a\u5e2e\u624b\uff0c\u90a3\u4e48\u73b0\u5728\u662f \u5728\u897f\u85cf\u4e86\u3002\u5141\u9898\u5e10\u4e0b\u6b66\u58eb\u9887\u591a\uff0c\u53ea\u6015\u4e0d\u6613\u8425\u6551\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u5c3d\u529b\u800c\u4e3a\uff0c\u6210\u4e0d\u6210\u90a3\u53ea\u597d\u59d4 \u4e4b\u5929\u547d\u4e86\u3002\u201d\u7eb3\u5170\u53c8\u9053\uff1a\u201c\u53ef\u60dc\u6211\u4e0d\u80fd\u5e2e\u4f60\u4ec0\u4e48\u5fd9\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4f60\u66ff\u6211\u4eec\u63a2\u51fa\u6d88\u606f\uff0c\u6211 \u4eec\u5df2\u662f\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u6b63\u4e8b\u8bf4\u5b8c\u4e4b\u540e\uff0c\u4e24\u4eba\u8c08\u8bba\u8bd7\u8bcd\uff0c\u5341\u5206\u6295\u5408\uff0c\u5e10\u5916\u6714\u98ce\u6012\u53f7\uff0c\u5e10\u4e2d\u5374\u6e29\u6696\u5982\u6625\u3002\u7eb3\u5170\u5bb9\u82e5 \u542c\u5192\u6d63\u83b2\u7ec6\u8c08\u5bb6\u4e16\uff0c\u53c8\u662f\u601c\u60dc\uff0c\u53c8\u662f\u7fa1\u6155\uff0c\u8bf4\u9053\uff1a\u201c\u7236\u6b7b\u522b\uff0c\u6bcd\u751f\u79bb\uff0c\u5269\u4e0b\u4f60\u4e00\u4e2a\u5b64\u5973\uff0c\u6d6a \u8ff9\u5929\u6daf\uff0c\u4e5f\u771f\u96be\u4e3a\u4f60\u4e86\u3002\u201d\u5192\u6d63\u83b2\u9053\uff1a\u201c\u60ef\u4e86\uff0c\u4e5f\u5c31\u4e0d\u89c9\u5f97\u4e86\u3002\u5176\u5b9e\u6211\u4e5f\u5e76\u4e0d\u5bc2\u5bde\uff0c\u6709\u5085\u4f2f \u4f2f\uff0c\u8fd8\u6709\u8bb8\u591a\u670b\u53cb\u4eec\u5728\u4e00\u8d77\u3002\u201d\u7eb3\u5170\u53f9\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8bf4\u4f60\u6bd4\u6211\u6709\u798f\u3002\u201d\u4ed6\u60f3\u8d77\u6b7b\u53bb\u7684\u7231\u59bb\uff0c \u518d\u7740\u773c\u524d\u7684\u7389\u4eba\uff0c\u5fc3\u9b44\u52a8\u8361\uff0c\u66ae\u7136\u60f3\u8d77\u5192\u6d63\u83b2\u6240\u8bf4\u7684\u201c\u597d\u670b\u53cb\u201d\u4e4b\u4e2d\uff0c\u60f3\u6765\u4e5f\u6709\u90a3\u201c\u50bb\u5c0f \u5b50\u201d\u5728\uff0c\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u4f60\u90a3\u4f4d\u2026\u2026\u90a3\u4f4d\uff0c\u6211\u8bb0\u4e0d\u8d77\u540d\u5b57\u4e86\u3002\u6ca1\u6709\u4e0e\u4f60\u540c\u6765\uff1f\u201d\u5192\u6d63\u83b2\u5a07\u7b11 \u9053\uff1a\u201c\u4ed6\u53eb\u6842\u4ef2\u660e\uff0c\u4ed6\u50bb\u5f97\u5f88\uff0c\u6211\u4e0d\u653e\u5fc3\u4ed6\uff0c\u4e0d\u6562\u8981\u4ed6\u540c\u6765\u3002\u201d\u8bdd\u8bed\u4e2d\u5145\u6ee1\u65e0\u9650\u67d4\u60c5\uff0c\u7eb3\u5170 \u5bb9\u82e5\u5982\u6c90\u51b7\u6c34\uff0c\u5f3a\u7b11\u9053\uff1a\u201c\u6842\u5144\u77e5\u4f60\u8fd9\u6837\u5173\u5fc3\uff0c\u4e0d\u77e5\u5982\u4f55\u611f\u6fc0\uff1f\u201d\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u82e5\u4f7f\u4e24\u5fc3 \u4e3a\u4e00\uff0c\u90a3\u5df2\u65e0\u9700\u611f\u6fc0\u4e86\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u6572\u4e86\u4e00\u4e0b\u989d\u5934\uff0c\u7b11\u9053\uff1a\u201c\u8be5\u7f5a\uff0c\u8be5\u7f5a\uff0c\u6211\u8fd9\u53e5\u8bdd\u771f\u5982\u8bcd \u4e2d\u52a3\u7b14\uff0c\u9053\u4e0d\u51fa\u631a\u6027\u771f\u60c5\u3002\u201d\u5192\u6d63\u83b2\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u591a\u4e00\u4e2a\u77e5\u5fc3\u7684\u4eba\u5c31\u5c11\u8bb8\u591a\u5bc2\u5bde\uff0c\u4f60\u8fd8\u662f\u8be5 \u65e9\u70b9\u7eed\u5f26\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u9053\uff1a\u201c\u66fe\u7ecf\u6ca7\u6d77\uff0c\u53ea\u6015\u5f88\u96be\u518d\u52a8\u5fc3\u4e86\u3002\u201d\u5192\u6d63\u83b2\u7b11\u9053\uff1a\u201c\u6211\u867d\u672a\u7ed3 \u5a5a\uff0c\u4f46\u6211\u60f3\u592b\u5987\u4e4b\u95f4\uff0c\u53ea\u6c42\u6709\u6240\u9002\u5408\uff0c\u4fbf\u662f\u7f8e\u6ee1\u59fb\u7f18\uff0c\u4e0d\u5fc5\u5f3a\u6c42\u6837\u6837\u9002\u5408\u3002\u6bd4\u5982\u6211\u548c\u6842\u4ef2 \u660e\uff0c\u540c\u662f\u6c5f\u6e56\u513f\u5973\uff0c\u6211\u559c\u6b22\u4ed6\u7684\u6206\u76f4\u7eaf\u771f\uff0c\u4ed6\u867d\u4e0d\u89e3\u8bd7\u8bcd\uff0c\u6211\u4e5f\u5e76\u65e0\u6240\u61be\u3002\u4ee5\u4f60\u7684\u8eab\u4e16\uff0c\u5c3d \u53ef\u627e\u5f97\u6e29\u67d4\u8d24\u6dd1\u7684\u95fa\u79c0\uff0c\u4f55\u5fc5\u8fc7\u4efd\u82db\u6c42\uff1f\u201d\u7eb3\u5170\u52c9\u5f3a\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8c22\u8c22\u59d1\u5a18\u5173\u5fc3\u3002\u201d \u3000\u3000\u591c\u6e10\u6d53\uff0c\u4e24\u4eba\u8c08\u5f97\u4e5f\u8d8a\u4eb2\u5207\u3002\u7eb3\u5170\u5bb9\u82e5\u95fb\u5f97\u7f15\u7f15\u5e7d\u9999\uff0c\u9189\u9b42\u9165\u9aa8\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6211\u53bb\u5e74 \u5728\u4eac\u4e2d\u4e0e\u4f60\u540c\u8d4f\u8377\u82b1\uff0c\u8fc7\u540e\u65f6\u89c9\u5e7d\u9999\u3002\u53ea\u9053\u4eca\u751f\u4e0d\u80fd\u518d\u95fb\u4e86\u3002\u8c01\u6599\u53c8\u6709\u4eca\u665a\u5947\u9022\u3002\u201d\u5192\u6d63\u83b2 \u4f55\u7b49\u806a\u660e\uff0c\u773c\u73e0\u4e00\u8f6c\uff0c\u626d\u8f6c\u8bdd\u9898\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u662f\u5f53\u4ee3\u8bcd\u5bb6\uff0c\u6211\u6709\u5e78\u5f97\u4e0e\u516c\u5b50\u957f\u8c08\uff0c\u82e5\u4e0d\u732e\u8bcd \u6c42\u6559\uff0c\u5c82\u4e0d\u8f9c\u8d1f\u4eca\u5bb5\u4e4b\u4f1a\uff1f\u201d\u7eb3\u5170\u5bb9\u82e5\u5927\u4e3a\u9ad8\u5174\uff0c\u62cd\u638c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u51b0\u96ea\u806a\u660e\uff0c\u586b\u7684\u8bcd\u4e00\u5b9a \u662f\u597d\u7684\u4e86\u3002\u201d\u5c55\u5f00\u8bcd\u7b3a\uff0c\u63d0\u8d77\u7b14\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5ff5\u5427\uff0c\u6211\u7ed9\u4f60\u5199\u3002\u201d \u3000\u3000\u5192\u6d63\u83b2\u5ff5\u9053\uff1a \u3000\u3000\u201c\u6700\u4f24\u5fc3\u70fd\u706b\u70e7\u8fb9\u57ce\uff0c\u5bb6\u56fd\u6068\u96be\u5e73\u3002 \u3000\u3000\u542c\u5f81\u4eba\u591c\u6ce3\uff0c\u80e1\u7b33\u60b2\u594f\uff0c\u5e94\u538c\u8a00\u5175\u3002 \u3000\u3000\u4e00\u5251\u5929\u5c71\u6765\u53bb\uff0c\u98ce\u96e8\u60ef\u66fe\u7ecf\u3002 \u3000\u3000\u613f\u5f85\u6ca7\u6851\u6362\u4e86\uff0c\u5e76\u8f94\u6570\u5bd2\u661f\u3002 \u3000\u3000\u6b64\u6068\u8c01\u80fd\u89e3\uff0c\u7edd\u585e\u5bc4\u79bb\u60c5\u3002 \u3000\u3000\u83ab\u7eed\u4eac\u534e\u65e7\u68a6\uff0c \u3000\u3000\u8bf7\u770b\u9ec4\u6c99\u767d\u8349\u2014\u2014\u2014\u2014 \u3000\u3000\u78a7\u8840\u5c1a\u9634\u51dd\u3002 \u3000\u3000\u60ca\u9e3f\u743c\u6c34\u8fc7\uff0c\u6ce2\u8361\u4e86\u65e0\u58f0\u3002 \u3000\u3000\u66f4\u4f11\u95ee\u7ee6\u73e0\u79fb\u540e\uff0c \u3000\u3000\u6cea\u96be\u6d47\uff0c\u4f55\u5904\u6258\u5b64\u830e\uff0c \u3000\u3000\u5e94\u73cd\u91cd\uff1a\u743c\u697c\u6765\u53bb\uff0c\u7a33\u6cdb\u7a7a\u6eaa\u3002 \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u4e00\u9762\u5199\uff0c\u5fc3\u513f\u4e00\u9762\u535c\u535c\u5730\u8df3\uff0c\u5199\u5b8c\u4e4b\u540e\uff0c\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u9996\u8bcd\u539f\u6765\u662f\u4f60\u7279\u522b\u9001 \u7ed9\u6211\u7684\uff1f\u201d\u5192\u6d63\u83b2\u70b9\u4e86\u70b9\u5934\uff0c\u7eb3\u5170\u5bb9\u82e5\u5377\u8d77\u8bcd\u7b3a\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u7684\u597d\u610f\uff01\u201d \u3000\u3000\u5192\u6d63\u83b2\u8fd9\u9996\u8bcd\u8868\u73b0\u4e86\u771f\u631a\u7684\u53cb\u60c5\uff0c\u4f46\u5176\u4e2d\u5374\u53c8\u542b\u6709\u6df1\u610f\uff0c\u4e0a\u534a\u9600\u8868\u8fbe\u4e86\u538c\u6076\u6218\u4e89\uff0c\u4f46\u4e3a \u4e86\u56fd\u4ec7\u5bb6\u6068\uff0c\u53c8\u4e0d\u80fd\u4e0d\u5192\u7740\u66b4\u98ce\u96e8\u53bb\u6297\u4e89\u7684\u601d\u60f3\u611f\u60c5\u3002\u5230\u201c\u613f\u5f85\u6ca7\u6851\u6362\u4e86\uff0c\u5e76\u8f94\u6570\u5bd2\u661f\u201d\u4e24 \u53e5\uff0c\u4fbf\u8c08\u53ca\u81ea\u5df1\u5bf9\u7eb3\u5170\u5bb9\u82e5\u7684\u53cb\u8c0a\u6001\u5ea6\uff0c\u610f\u601d\u662f\uff1a\u6211\u4eec\u73b0\u5728\u4ecd\u662f\u5904\u5728\u4e0d\u540c\u7684\u4e24\u4e2a\u654c\u5bf9\u96c6\u56e2\uff0c \u9664\u975e\u662f\u4e16\u754c\u53d8\u4e86\uff0c\u6e05\u5175\u9000\u51fa\u5173\u4e86\uff0c\u6211\u4eec\u7684\u53cb\u8c0a\u624d\u80fd\u81ea\u7531\u751f\u957f\uff0c\u90a3\u65f6\u5019\u624d\u80fd\u548c\u4f60\u65e0\u62d8\u65e0\u675f\u5730\u5728 \u661f\u5149\u4e0b\u5e76\u8f94\u9a71\u9a70\u3002\u800c\u73b0\u5728\u5462\uff1f\u5374\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u3002\u8fd9\u79cd\u6218\u4e89\u9020\u6210\u7684\u53cb\u8c0a\u969c\u788d\uff0c\u5b9e\u5728\u662f\u4eba\u751f\u7684\u4e00 \u5927\u6068\u4e8b\u3002\u53ef\u662f\u8fd9\u79cd\u6068\u4e8b\uff0c\u53c8\u6709\u51e0\u4eba\u80fd\u591f\u4e86\u89e3\u5462\uff1f \u3000\u3000\u4e0b\u534a\u9602\u81ea\u201d\u83ab\u7eed\u4eac\u534e\u65e7\u68a6\u201d\u8d77\uff0c\u4e00\u76f4\u5230\u201c\u5e94\u73cd\u91cd\uff0c\u743c\u697c\u6765\u53bb\uff0c\u7a33\u6cdb\u7a7a\u6e9f\u201d\u6b62\uff0c\u66f4\u662f\u76f4\u63a5 \u7b54\u590d\u7eb3\u5170\u5bb9\u82e5\u521a\u624d\u7684\u8bdd\u4e86\u3002\u7eb3\u5170\u5bb9\u82e5\u7f05\u6000\u4eac\u534e\u65e7\u4e8b\uff0c\u604b\u604b\u4e8e\u6614\u65e5\u8c08\u8bcd\u8d4f\u8377\u7684\u597d\u68a6\u3002\u5192\u6d63\u83b2\u544a \u8bc9\u4ed6\u9053\uff1a\u4eac\u534e\u65e7\u68a6\u662f\u96be\u4e8e\u7eed\u4e0b\u53bb\u4e86\uff0c\u4f60\u770b\u76ee\u524d\u7684\u60c5\u51b5\u5427\uff0c\u6e05\u519b\u743c\u8fc7\u8349\u539f\uff0c\u5728\u9ec4\u6c99\u767d\u8349\u4e4b\u4e0a\uff0c \u78a7\u8840\u5c1a\u81ea\u51dd\u7ed3\uff0c\u6ca1\u6709\u6d88\u5c3d\uff0c\u5728\u8fd9\u6837\u4e24\u65b9\u4ea4\u6218\u4e4b\u4e2d\uff0c\u90a3\u79cd\u597d\u68a6\u53c8\u5982\u4f55\u80fd\u591f\u518d\u7eed\u4e0b\u53bb\uff1f\u6211\u4eec\u8fd9\u6bb5 \u53cb\u8c0a\uff0c\u53ea\u597d\u8bf7\u4f60\u6bd4\u4f5c\u201c\u60ca\u9e3f\u743c\u6c34\u201d\uff0c\u8fc7\u4e86\u4fbf\u7b97\u4e86\u3002\u81f3\u4e8e\u6211\u5462\uff1f\u4f60\u4e0d\u5fc5\u4e3a\u6211\u62c5\u5fc3\uff0c\u6211\u867d\u7136\u662f\u4e2a \u5b64\u5973\uff0c\u4f46\u5374\u5e76\u4e0d\u50cf\u795e\u8bdd\u4e2d\u7684\u7ee6\u73e0\u4ed9\u8349\uff0c\u79bb\u5f00\u4e86\u5929\u6cb3\u4e4b\u540e\uff0c\u8981\u7528\u773c\u6cea\u6765\u6d47\u624d\u80fd\u751f\u957f\u7684\u3002\u4e0d\uff0c\u6211 \u8fd8\u6ca1\u6709\u90a3\u6837\u8106\u5f31\u3002\u5012\u662f\u5bf9\u4e8e\u4f60\uff0c\u6211\u5374\u5e0c\u671b\u4f60\u81ea\u5df1\u73cd\u91cd\uff0c\u4f60\u5728\u5e1d\u7389\u4e4b\u5bb6\uff0c\u6b63\u5982\u5728\u201c\u743c\u697c\u201d\u9ad8 \u5904\uff0c\u53ef\u80fd\u4e0d\u80dc\u5bd2\u98ce\u5462\uff0c\u6211\u5012\u613f\u610f\u4f60\u80fd\u591f\u628a\u6301\u5f97\u5b9a\uff0c\u597d\u50cf\u5728\u592a\u7a7a\u4e2d\u884c\u9a76\u7684\u8239\u53ea\uff0c\u867d\u7136\u6ca1\u4ec0\u4e48\u4eba \u5e2e\u52a9\u4f60\uff0c\u4f60\u4e5f\u80fd\u628a\u7a33\u4e86\u8235\u3002 \u3000\u3000\u8fd9\u9996\u8bcd\u60c5\u8bcd\u6073\u5207\uff0c\u771f\u631a\u7eaf\u6d01\u7684\u53cb\u8c0a\u8fdc\u8d85\u4e8e\u4e00\u822c\u79c1\u60c5\u7737\u604b\u4e4b\u4e0a\u3002\u7eb3\u5170\u5bb9\u82e5\u4e24\u773c\u6f6e\u6e7f\uff0c\u5fc3\u7075 \u660e\u51c0\uff0c\u81ea\u89c9\u4eb5\u6e0e\u4e86\u5192\u6d63\u83b2\u73cd\u8d35\u7684\u611f\u60c5\u3002\u5728\u70db\u5f71\u7476\u7ea2\u4e2d\uff0c\u7d27\u63e1\u7740\u5192\u6d63\u83b2\u53cc\u624b\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u5929 \u5feb\u8981\u4eae\u4e86\uff0c\u6211\u9001\u4f60\u51fa\u53bb\u5427\uff01\u201d\u6b63\u662f\u3002 \u3000\u3000\u8109\u8109\u6b64\u60c5\u8c01\u53ef\u8bed\uff0c\u6c38\u7559\u77e5\u5df1\u5728\u4eba\u95f4\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5eff\u4e03\u7ae0\u3000\u77e2\u5fd7\u590d\u4ec7\u3000\u6613\u5170\u73e0\u8654\u5fc3\u7ec3\u5251\u3000\u5e08\u95e8\u7559\u6068\u3000\u6b66\u743c\u7476\u6709\u610f\u8bd5\u62db \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u4e8c\u5341\u4e03\u56de \u77e2\u5fd7\u590d\u4ec7\u3000\u6613\u5170\u73e0\u8654\u5fc3\u7ec3\u5251 \u5e08\u95e8\u7559\u6068\u3000\u6b66\u743c\u7476\u6709\u610f\u8bd5\u62db \u3000\u3000\u201c\u5929\u5feb\u8981\u4eae\u4e86\uff0c\u4f60\u4e5f\u8be5\u6b47\u6b47\u4e86\uff01\u201d\u5728\u5580\u5c14\u6c81\u8349\u539f\u4e0a\uff0c\u97e9\u5fd7\u90a6\u4e5f\u8fd9\u6837\u5bf9\u6842\u4ef2\u660e\u8bf4\u3002 \u3000\u3000\u5192\u6d63\u83b2\u548c\u7eb3\u5170\u5bb9\u82e5\u957f\u8c08\u5f85\u6653\u4e4b\u591c\uff0c\u6842\u4ef2\u660e\u4e5f\u662f\u5f7b\u591c\u65e0\u7720\u3002\u8fd9\u4e9b\u5929\u6765\uff0c\u97e9\u5fd7\u90a6\u5949\u8f9b\u9f99\u5b50\u7684 \u9057\u547d\uff0c\u628a\u8fbe\u6469\u4e00\u767e\u96f6\u516b\u5f0f\u7684\u526f\u672c\uff0c\u548c\u4ed6\u5171\u540c\u7814\u7a76\uff0c\u6842\u4ef2\u660e\u6839\u57fa\u5f88\u597d\uff0c\u5bf9\u6b66\u529f\u7684\u9886\u609f\u4e5f\u8fdc\u80dc\u5e38 \u4eba\u3002\u4e0d\u6d88\u51e0\u5929\uff0c\u5df2\u8d85\u51fa\u97e9\u5fd7\u90a6\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u6842\u4ef2\u660e\u628a\u8fbe\u6469\u79d8\u6280\uff0c\u5f0f\u5f0f\u6f14\u4e60\uff0c\u53cd\u590d\u63e3\u6469\uff0c\u6e10\u89c9\u5fc3\u9886\u795e\u4f1a\u3002\u97e9\u5fd7\u90a6\u5c61\u6b21\u529d\u4ed6\u53bb \u7761\uff0c\u4ed6\u90fd\u7f6e\u82e5\u7f54\u95fb\uff0c\u4e00\u5ffd\u513f\u5728\u5730\u4e0a\u6253\u5750\u51a5\u601d\uff0c\u4e00\u5ffd\u513f\u53c8\u8df3\u8d77\u624b\u821e\u8db3\u8e48\u3002\u97e9\u5fd7\u90a6\u867d\u7136\u6b66\u529f\u4e0d \u9ad8\uff0c\u4e5f\u77e5\u4ed6\u7ec3\u529f\u5df2\u5230\u4e86\u7d27\u8981\u5173\u5934\uff0c\u6b63\u5728\u63a2\u7d22\u8fbe\u6469\u79d8\u6280\u7684\u5173\u952e\u7a8d\u8981\uff0c\u4e0d\u6562\u6253\u6405\uff0c\u5728\u4e00\u65c1\u6014\u6014\u5730 \u770b\u7740\u4ed6\uff0c\u8349\u539f\u4e0a\u591c\u5bd2\u5b64\u9aa8\u3002\u97e9\u5fd7\u90a6\u6e10\u89c9\u4e0d\u8010\uff0c\u5ffd\u542c\u5f97\u8fdc\u5904\u9e21\u58f0\uff0c\u66d9\u5149\u7b56\u73b0\uff0c\u97e9\u5fd7\u90a6\u770b\u6842\u4ef2\u660e \u65f6\uff0c\u53ea\u89c1\u4ed6\u53c8\u8dcc\u5750\u5730\u4e0a\uff0c\u4fe8\u5982\u8001\u50e7\u5165\u5b9a\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u97e9\u5fd7\u90a6\u6b63\u60f3\u53eb\u4ed6\uff0c\u5ffd\u7136\u4ed6\u5927\u58f0\u53eb\u9053\uff1a \u201c\u5f97\u4e86\uff01\u5f97\u4e86\uff01\u201d\u500f\u7684\u8df3\u8d77\uff0c\u62d4\u51fa\u817e\u86df\u5b9d\u5251\uff0c\u6309\u8fbe\u6469\u5251\u6cd5\uff0c\u98de\u821e\u8d77\u6765\uff0c\u987f\u65f6\u94f6\u5149\u904d\u4f53\uff0c\u7d2b\u7535 \u98de\u7a7a\uff0c\u97e9\u5fd7\u90a6\u867d\u7136\u901a\u6653\u8fbe\u6469\u79d8\u6280\uff0c\u4e5f\u770b\u5f97\u773c\u82b1\u7ea7\u4e71\uff0c\u6842\u4ef2\u660e\u821e\u5230\u6025\u5904\uff0c\u5ffd\u7136\u4e00\u987f\uff0c\u53c8\u6162\u4e0b \u6765\uff0c\u53ea\u89c1\u4ed6\u4e1c\u4e00\u5251\uff0c\u897f\u4e00\u5251\uff0c\u597d\u50cf\u6beb\u4e0d\u7528\u529b\uff0c\u6f2b\u4e0d\u7ecf\u5fc3\uff0c\u4f46\u5185\u884c\u4eba\u770b\u6765\uff0c\u5374\u662f\u5df2\u8fbe\u5230\u201c\u5fc3\u5251 \u5408\u4e00\u201d\u7684\u4e0a\u4e58\u529f\u592b\uff0c\u771f\u6709\u6d41\u6c34\u884c\u4e91\uff0c\u6325\u6d12\u81ea\u5982\u4e4b\u5999\u3002\u97e9\u5fd7\u90a6\u6df1\u6df1\u4f69\u670d\uff0c\u4e0d\u89c9\u53f9\u9053\uff1a\u201c\u6b66\u827a\u4e00 \u9053\uff0c\u771f\u5f97\u6709\u7f18\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u63a5\u58f0\u8d5e\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff01\u201d\u6842\u4ef2\u660e\u8eab\u5b50\u4e00\u65cb\uff0c\u500f\u5730\u6536 \u5251\u51dd\u8eab\uff0c\u8bf4\u9053\uff1a\u201c\u5170\u73e0\u59b9\u59b9\uff0c\u4f60\u597d\u65e9\u554a\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u5fae\u7b11\u70b9\u5934\uff0c\u5ffd\u5730\u62d4\u51fa\u77ed\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u6842\u5927\u54e5\uff0c\u4f60\u7ed9\u6211\u5582\u5582\u62db\u3002\u201d\u6842\u4ef2\u660e\u4e00\u9635\u8e0c \u8e87\uff0c\u539f\u6765\u4ed6\u4ee5\u524d\u5728\u7eb3\u5170\u76f8\u5e9c\u7684\u82b1\u56ed\uff0c\u8bef\u6253\u8bef\u649e\uff0c\u66fe\u548c\u6613\u5170\u73e0\u6597\u8fc7\uff0c\u90a3\u65f6\u4ed6\u4e5f\u662f\u7565\u5360\u4e0a\u98ce\u3002\u73b0 \u5728\u5f97\u4e86\u8fbe\u6469\u5251\u6cd5\u7cbe\u9ad3\uff0c\u6b66\u529f\u53c8\u4e0d\u77e5\u6bd4\u4ee5\u524d\u9ad8\u4e86\u591a\u5c11\u3002\u4f46\u6b63\u56e0\u4e3a\u521a\u521a\u9886\u609f\uff0c\u53ea\u6050\u81ea\u5df1\u8fd8\u4e0d\u80fd\u5b8c \u5168\u63a7\u5236\uff0c\u800c\u8fbe\u6469\u5251\u6cd5\u53c8\u72e0\u8fa3\u5f02\u5e38\uff0c\u62c5\u5fc3\u4e00\u65f6\u5931\u624b\uff0c\u4f24\u4e86\u6613\u5170\u73e0\uff0c\u90a3\u53ef\u4e0d\u597d\u610f\u601d\u4e86\uff0c\u6240\u4ee5\u4ed6\u8fdf \u8fdf\u7591\u7591\uff0c\u4e0d\u6562\u5373\u7b54\uff0c\u6613\u5170\u73e0\u597d\u4f3c\u7aa5\u7834\u4ed6\u7684\u5fc3\u610f\uff0c\u5251\u950b\u4e00\u9886\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u59a8\u5148\u7528\u4e94\u79bd\u5251 \u6cd5\u548c\u6211\u8fc7\u62db\uff0c\u82e5\u89c9\u6211\u6bd4\u4ee5\u524d\u7a0d\u6709\u8fdb\u5883\uff0c\u90a3\u4f60\u518d\u7528\u65b0\u5b66\u6210\u7684\u6b66\u6797\u79d8\u6280\u5982\u4f55\uff1f\u201d \u3000\u3000\u6842\u4ef2\u660e\u65e0\u6cd5\u63a8\u8f9e\uff0c\u53ea\u597d\u7b54\u5e94\uff0c\u521a\u8bf4\u5f97\u58f0\uff1a\u201c\u8bf7\u8fdb\u62db\uff01\u201d\u6613\u5170\u73e0\u5df2\u5237\u7684\u4e00\u5251\uff0c\u5241\u5230\u80f8\u714e\uff0c \u6842\u4ef2\u660e\u5b9d\u5251\u659c\u538b\uff0c\u6613\u5170\u73e0\u77ac\u606f\u4e4b\u95f4\uff0c\u5df2\u8fde\u53d1\u4e09\u5251\uff0c\u6842\u4ef2\u660e\u64a4\u5251\u9632\u5b88\uff0c\u5927\u611f\u60ca\u5f02\uff0c\u8f97\u8f6c\u653b\u62d2\uff0c \u62c6\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u6842\u4ef2\u660e\u5b88\u5f97\u751a\u4e3a\u5403\u529b\u3002\u53ea\u89c9\u6bd4\u5bf9\u695a\u662d\u5357\u4e4b\u65f6\uff0c\u4f3c\u4e4e\u66f4\u611f\u56f0\u96be\u3002\u865a\u6643\u4e00\u62db\uff0c\u5251 \u6cd5\u4e00\u53d8\uff0c\u628a\u65b0\u5b66\u6210\u7684\u8fbe\u6469\u5251\u6cd5\uff0c\u65bd\u5c55\u51fa\u6765\uff0c\u970e\u65f6\u95f4\u602a\u62db\u6d6a\u6d8c\uff0c\u53d8\u5316\u65e0\u7a77\uff0c\u5982\u5265\u8327\u62bd\u4e1d\uff0c\u7ef5\u7ef5 \u4e0d\u7edd\uff0c\u6613\u5170\u73e0\u9053\u58f0\u201c\u6765\u5f97\u597d\uff01\u201d\u77ed\u5251\u4e00\u7ffb\uff0c\u5728\u5251\u5149\u4e2d\u7a7f\u6765\u63d2\u53bb\uff01 \u3000\u3000\u4e24\u4eba\u8d8a\u6597\u8d8a\u5feb\uff0c\u6842\u4ef2\u660e\u821e\u5230\u6c89\u9163\u6dcb\u6f13\u4e4b\u9645\uff0c\u817e\u86df\u5b9d\u5251\uff0c\u968f\u610f\u6240\u4e4b\uff0c\u5ffd\u75be\u5ffd\u4f59\uff0c\u4e00\u4e3e\u624b\u4e00 \u6295\u8db3\uff0c\u4fbf\u89c9\u5251\u5149\u64a9\u7ed5\uff0c\u6709\u98ce\u98d2\u7136\u3002\u6613\u5170\u73e0\u8863\u8882\u98d8\u98d8\uff0c\u968f\u7740\u6842\u4ef2\u660e\u7684\u5251\u950b\u6ef4\u6e9c\u6e9c\u5730\u8f6c\uff0c\u65e0\u8bba\u6842 \u4ef2\u660e\u7684\u5251\u62db\uff0c\u5982\u4f55\u602a\u5f02\uff0c\u5979\u603b\u80fd\u62ff\u634f\u65f6\u5019\uff0c\u4e0d\u5dee\u6beb\u53d1\uff0c\u6321\u5728\u5934\u91cc\u3002\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u6842\u4ef2\u660e\u7684 \u8fbe\u6469\u5251\u6cd5\u5feb\u5c06\u7528\u5b8c\uff0c\u8fd8\u662f\u521a\u521a\u6253\u6210\u5e73\u624b\u3002\u6613\u5170\u73e0\u5a07\u53f1\u4e00\u58f0\uff0c\u5251\u62db\u5ffd\u7d27\uff0c\u8eab\u5982\u661f\u4e38\u8df3\u63b7\uff0c\u4e00\u53e3 \u77ed\u5251\u56de\u73af\u98de\u821e\uff0c\u970d\u970d\u8feb\u6765\u3002\u6842\u4ef2\u660e\u4f9d\u7136\u4e00\u60ca\uff0c\u6599\u4e0d\u5230\u6613\u5170\u73e0\u8fdb\u5883\u5982\u6b64\u795e\u901f\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u628a\u6628 \u665a\u51a5\u601d\u9ed8\u7d22\u7684\u5fc3\u5f97\uff0c\u5168\u7528\u51fa\u6765\uff0c\u4e0d\u6309\u8fbe\u6469\u5251\u6cd5\u6b21\u5e8f\uff0c\u968f\u610f\u62c6\u656c\u5f00\u6765\uff0c\u52a0\u4e0a\u4e94\u79bd\u5251\u4e2d\u539f\u6709\u7684\u7cbe \u5999\u62db\u6570\uff0c\u521b\u6210\u4e86\u72ec\u5177\u4e00\u683c\u7684\u4e0a\u4e58\u5251\u6cd5\uff0c\u5e26\u5b88\u5e26\u653b\uff0c\u628a\u6613\u5170\u73e0\u6321\u4f4f\uff0c\u53c8\u662f\u6597\u5f97\u4e2a\u534a\u65a4\u516b\u4e24\uff0c\u94e2 \u4e24\u6089\u654c\u3002\u4e00\u53e3\u957f\u5251\uff0c\u4e00\u53e3\u77ed\u5251\uff0c\u5982\u7389\u9f99\u5929\u77eb\uff0c\u534a\u7a7a\u76f8\u6597\uff0c\u97e9\u5fd7\u90a6\u5728\u65c1\u8fb9\u770b\u6765\uff0c\u53ea\u89c1\u4e07\u70b9\u94f6\u661f \u4ece\u5251\u7aef\u98de\u821e\u800c\u51fa\uff0c\u53c8\u50cf\u4e07\u6735\u68a8\u82b1\uff0c\u4ece\u7a7a\u6492\u4e0b\uff0c\u904d\u4f53\u7b3c\u7f69\uff0c\u54ea\u91cc\u8fd8\u5206\u5f97\u51fa\u54ea\u4e2a\u662f\u6842\u4ef2\u660e\uff0c\u54ea\u4e2a \u662f\u6613\u5170\u73e0\u3002\u4f59\u52bf\u6240\u53ca\uff0c\u5468\u56f4\u7684\u767d\u8349\u9ec4\u6c99\uff0c\u90fd\u817f\u98ce\u98a4\u52a8\u98de\u626c\uff0c\u8349\u4e0a\u7684\u79ef\u96ea\uff0c\u4e5f\u7ed9\u9707\u5f97\u7eb7\u7eb7\u98de \u821e\uff0c\u76d8\u65cb\u5929\u7a7a\uff0c\u96ea\u82b1\u5251\u82b1\u6ee1\u7a7a\u4ea4\u6218\uff0c\u5e7b\u6210\u5947\u5f69\u3002\u97e9\u5fd7\u90a6\u770b\u5f97\u76ee\u5b9a\u53e3\u5446\uff0c\u5230\u4e86\u540e\u6765\uff0c\u8fde\u4e24\u4eba\u5934 \u4e0a\u7f24\u7eb7\u98de\u821e\u7684\u662f\u5251\u82b1\uff0c\u662f\u96ea\u82b1\uff0c\u4e5f\u5206\u8fa8\u4e0d\u51fa\u4e86\u3002\u521a\u53eb\u5f97\u4e00\u58f0\u201c\u597d\u201d\u5b57\uff0c\u5ffd\u542c\u5f97\u201c\u5f53\u5f53\u201d\u4e24 \u58f0\uff0c\u706b\u82b1\u4e71\u5c04\uff0c\u500f\u5730\u4e24\u9053\u767d\u5149\u8fce\u9762\u5c04\u6765\uff0c\u97e9\u5fd7\u90a6\u4e00\u77ee\u8eab\u65f6\uff0c\u5df2\u662f\u98ce\u5b9a\u58f0\u5bc2\u3002\u6842\u4ef2\u660e\u548c\u6613\u5170\u73e0 \u655b\u624b\u7ad9\u5728\u81ea\u5df1\u7684\u9762\u524d\uff0c\u7b11\u563b\u563b\u9053\uff1a\u201c\u6211\u4eec\u6597\u5f97\u5fd8\u5f62\uff0c\u5413\u7740\u4e86\u97e9\u53d4\u53d4\u4e86\u3002\u201d \u3000\u3000\u4f60\u9053\u6613\u5170\u73e0\u5251\u6cd5\u4f55\u4ee5\u5982\u6b64\u795e\u5947\uff1f\u539f\u6765\u5728\u6842\u4ef2\u660e\u6f5c\u5fc3\u7814\u4e60\u8fbe\u6469\u5251\u6cd5\u4e4b\u65f6\uff0c\u5979\u4e5f\u5728\u6f5c\u5fc3\u7814\u4e60 \u5929\u5c71\u5251\u6cd5\u3002\u51cc\u672a\u98ce\u5728\u4e0a\u6b21\u79bb\u5f00\u5979\u65f6\uff0c\u5c31\u5c06\u6666\u660e\u7985\u5e08\u7684\u62f3\u7ecf\u5251\u51b3\u4ea4\u7ed9\u4e86\u5979\u4fdd\u7ba1\u3002\u6613\u5170\u73e0\u706b\u5019\u672a \u5230\uff0c\u539f\u60f3\u5f85\u51cc\u672a\u98ce\u5f52\u6765\u4e4b\u540e\uff0c\u6709\u6687\u4e4b\u65f6\uff0c\u518d\u8bf7\u4ed6\u4f20\u6388\u5965\u5999\u7cbe\u534e\u4e4b\u5904\uff0c\u4e0d\u6599\u51cc\u672a\u98ce\u51b0\u6cb3\u9047\u9669\u3002 \u6613\u5170\u73e0\u77e2\u5fd7\u6551\u4ed6\uff0c\u7528\u7edd\u5927\u7684\u8654\u5fc3\u6bc5\u529b\u82e6\u82e6\u5b66\u5251\uff0c\u5341\u51e0\u5929\u6765\uff0c\u8fde\u5f20\u534e\u662d\u4e5f\u4e00\u9762\u4e0d\u89c1\uff0c\u771f\u6240\u8c13\u7cbe \u8bda\u6240\u81f3\uff0c\u91d1\u77f3\u4e3a\u5f00\uff0c\u8fc7\u4e86\u51e0\u4e2a\u4e0d\u7720\u4e4b\u591c\uff0c\u5c45\u7136\u7ed9\u5979\u65e0\u5e08\u81ea\u901a\uff0c\u6478\u7d22\u51fa\u5929\u5c71\u5251\u6cd5\u7684\u5965\u5999\uff0c\u81ea\u7136 \u8d2f\u901a\uff0c\u518d\u52a0\u4e0a\u98de\u7ea2\u5dfe\u4eb2\u6388\u7684\u767d\u53d1\u9b54\u5973\u72ec\u95e8\u5251\u6cd5\uff0c\u878d\u5316\u4f1a\u5408\uff0c\u987f\u89c9\u7075\u53f0\u660e\u51c0\uff0c\u4ee5\u524d\u6240\u78b0\u5230\u7684\u6b66 \u5b66\u96be\u9898\u90fd\u4e00\u4e00\u8fce\u5203\u800c\u89e3\u3002\u51cc\u672a\u98ce\u5728\u5e08\u7236\u4ea4\u7ed9\u4ed6\u7684\u62f3\u7ecf\u5251\u8bc0\u4e0a\uff0c\u53c8\u65b0\u6dfb\u4e86\u4e00\u7ae0\u4ed6\u81ea\u5df1\u7684\u5fc3\u5f97\uff0c \u4e13\u8bba\u600e\u6837\u5e94\u4ed8\u8fbe\u6469\u5251\u6cd5\u7684\u3002\u6240\u4ee5\u6613\u5170\u73e0\u548c\u6842\u4ef2\u660e\u6bd4\u5251\uff0c\u975e\u4f46\u6beb\u4e0d\u5403\u4e8f\uff0c\u800c\u4e14\u5728\u5251\u6cd5\u4e0a\u8fd8\u7565\u5360 \u4e0a\u98ce\u3002\u53ea\u662f\u4ee5\u529f\u529b\u800c\u8bba\uff0c\u6613\u5170\u73e0\u8fd8\u7a0d\u900a\u6842\u4ef2\u660e\u4e00\u7b79\uff0c\u6240\u4ee5\u6253\u6765\u6253\u53bb\uff0c\u6253\u6210\u5e73\u624b\u3002 \u3000\u3000\u6bd4\u5251\u4e4b\u540e\uff0c\u6842\u4ef2\u660e\u9887\u6709\u70b9\u6cae\u4e27\uff0c\u89c9\u5f97\u82e6\u5fc3\u5b66\u6280\uff0c\u7cbe\u901a\u4e86\u8fbe\u6469\u5251\u6cd5\u4e4b\u540e\uff0c\u4e5f\u53ea\u4e0d\u8fc7\u5982\u6b64\u3002 \u4e0d\u6599\u6613\u5170\u73e0\u5df2\u62a2\u7740\u79f0\u8d5e\u4ed6\u9053\uff1a\u201c\u6842\u5927\u54e5\uff0c\u4f60\u73b0\u5728\u5df2\u53ef\u4ee5\u505a\u4e00\u6d3e\u7684\u5b97\u5e08\u4e86\uff01\u201d \u3000\u3000\u6842\u4ef2\u660e\u60f6\u7136\u8bf4\u9053\uff1a\u201c\u5170\u73e0\u59b9\u59b9\uff0c\u4f60\u600e\u7684\u5632\u7b11\u8d77\u6211\u6765\u4e86\u3002\u201d\u6613\u5f53\u73e0\u9053\uff1a\u201c\u6211\u867d\u7136\u5e74\u8f7b\u8bc6 \u6d45\uff0c\u81ea\u5e7c\u8ddf\u968f\u51cc\u53d4\u53d4\uff0c\u5bf9\u5404\u5bb6\u5404\u6d3e\u5251\u6cd5\u7565\u77e5\u4e00\u4e8c\uff0c\u5982\u4eca\u770b\u6765\uff0c\u5c06\u6765\u80fd\u4e0e\u5929\u5c71\u5251\u6cd5\u5339\u654c\u7684\uff0c\u53ea \u6709\u4f60\u6240\u63e3\u6469\u51fa\u6765\u7684\u5251\u6cd5\u4e86\u3002\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u8fd9\u4e9b\u5929\u6765\uff0c\u5bf9\u672c\u95e8\u5251\u6cd5\uff0c\u4e5f\u8fd8\u7528\u4e86\u4e00\u4e9b\u529f\u592b\uff0c\u81ea\u4fe1 \u5df2\u6bd4\u524d\u9ad8\u4e86\u8bb8\u591a\uff0c\u4e0d\u6599\u548c\u4f60\u4e00\u6bd4\uff0c\u8fd8\u662f\u4e0d\u80fd\u53d6\u80dc\u3002\u201d\u6842\u4ef2\u660e\u8fd9\u624d\u8f6c\u6cae\u4e27\u4e3a\u559c\u60a6\uff0c\u51b2\u8bf4\u9053\uff1a \u201c\u6d63\u83b2\u59d0\u59d0\u82e5\u770b\u5230\u6211\u4eec\u4eca\u671d\u8fd9\u573a\u6bd4\u5251\uff0c\u4e00\u5b9a\u975e\u5e38\u9ad8\u5174\u3002\u201d\u6613\u5170\u73e0\u5657\u5473\u7b11\u9053\uff1a\u201c\u662f\u5440\uff0c\u5979\u770b\u5230 \u4f60\u6709\u5982\u6b64\u8fdb\u5883\uff0c\u4e00\u5b9a\u4f1a\u5938\u5956\u4f60\uff01\u201d\u6842\u4ef2\u660e\u9762\u4e0a\u4e00\u7ea2\uff0c\u8fdc\u5904\u5f20\u534e\u662d\u53eb\u9053\uff1a\u201c\u5170\u73e0\uff01\u5170\u73e0\uff01\u201d\u6613 \u5170\u73e0\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6211\u53ef\u4ee5\u89c1\u4ed6\u4e86\u3002\u201d\u626d\u5934\u4fbf\u8dd1\u3002\u6842\u4ef2\u660e\u50bb\u7b11\u7740\u5bf9\u97e9\u5fd7\u90a6\u9053\uff1a\u201c\u97e9\u53d4\u53d4\uff0c\u4e0d\u6015 \u4f60\u89c1\u7b11\uff0c\u6211\u603b\u89c9\u5f97\u914d\u4e0d\u8d77\u70f7\u83b2\u59d0\u59d0\uff0c\u6240\u4ee5\u6211\u5728\u5251\u6cd5\u4e0a\u8981\u7279\u522b\u7528\u529f\u3002\u201d\u97e9\u5fd7\u90a6\u770b\u4ed6\u4eec\u4e24\u5bf9\u5c0f\u513f \u5973\u5982\u6b64\u601d\u7231\uff0c\u4e2a\u89c9\u5fae\u611f\u8f9b\u9178\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u66fe\u82e6\u604b\u5218\u90c1\u82b3\u5341\u4f59\u5e74\uff0c\u540e\u6765\u77e5\u9053\u4e86\u5218\u90c1\u82b3\u4e4b\u60c5\u522b\u6709\u6240\u9524\uff0c\u7ecf\u8fc7\u4e86\u4e00\u6bb5\u65f6\u671f\u5fc3\u7075\u7684 \u75db\u82e6\uff0c\u8fd9\u624d\u6e10\u6e10\u5e73\u9759\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u656c\u91cd\u51cc\u672a\u98ce\uff0c\u4ed6\u751a\u81f3\u6697\u4e2d\u66fe\u4e3a\u51cc\u672a\u98ce\u5218\u90c1\u82b3\u4e8c\u4eba\u7977\u544a\u3002\u4ed6\u5e76\u4e0d\u662f\u4e0d\u7231\u5218\u90c1\u82b3\uff0c\u4ed6\u7684\u7231 \u662f\u6bd4\u4ee5\u524d\u66f4\u6df1\u4e86\u3002\u53ef\u662f\uff0c\u8fd9\u5df2\u7ecf\u4e0d\u662f\u60f3\u201c\u5360\u6709\u201d\u7684\u7231\uff0c\u800c\u662f\u631a\u671b\u6240\u7231\u7684\u4eba\u5f97\u5230\u5e78\u798f\u7684\u90a3\u79cd\u65e0 \u79c1\u4e4b\u7231 \u3000\u3000\u4ed6\u79bb\u5f00\u4e86\u6842\u4ef2\u660e\uff0c\u60d8\u60d8\u7136\u5730\u53bb\u6572\u5218\u90c1\u82b3\u7684\u623f\u95e8\uff0c\u5218\u90c1\u82b3\u5f00\u95e8\u89c1\u4ed6\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u600e\u4e48 \u6837\uff1f\u6709\u4e86\u51cc\u672a\u98ce\u7684\u6d88\u606f\u4e86\uff1f\u201d\u8fd9\u4e9b\u5929\u6765\uff0c\u5218\u90c1\u82b3\u603b\u662f\u628a\u81ea\u5df1\u5173\u5728\u6597\u5ba4\u4e4b\u5185\uff0c\u4efb\u4f55\u4eba\u90fd\u77e5\u9053\u5979 \u5fcd\u53d7\u7740\u75db\u82e6\u7684\u714e\u71ac\uff0c\u53ef\u662f\uff0c\u5374\u6ca1\u6709\u8c01\u80fd\u591f\u6170\u89e3\u5979\u3002\u97e9\u5fd7\u90a6\u770b\u7740\u5979\u82cd\u767d\u7684\u8138\u5bb9\uff0c\u9ed8\u9ed8\u5730\u4f38\u51fa\u4e86 \u4ed6\u7684\u624b\uff0c\u5218\u90c1\u82b3\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8ba1\u7b97\u65e5\u7a0b\uff0c\u5085\u9752\u4e3b\u4ed6\u4eec\u5c31\u5feb\u8981\u56de\u6765\u4e86\uff0c\u2026\u2026\u201d\u97e9\u5fd7\u90a6\u9053\uff1a\u201c\u5218 \u5927\u59d0\uff0c\u6211\u4e0d\u61c2\u5f97\u8bf4\u8bdd\uff0c\u4f46\u6211\u82e5\u4e00\u77e5\u9053\u51cc\u5927\u4fa0\u7684\u6d88\u606f\uff0c\u6211\u5411\u4f60\u53d1\u8a93\uff0c\u6211\u8981\u628a\u4ed6\u5e26\u56de\u4f60\u7684\u8eab \u8fb9\u3002\u201d\u5218\u90c1\u82b3\u4f38\u51fa\u624b\u6765\uff0c\u8ba9\u4ed6\u63e1\u4e86\u4e00\u4f1a\u513f\uff0c\u7ec8\u4e8e\u8bf4\u9053\uff1a\u201c\u5fd7\u90a6\uff0c\u4f60\u6c38\u8fdc\u662f\u60a3\u96be\u4e2d\u7684\u597d\u670b \u53cb\uff01\u201d \u3000\u3000\u8fd9\u65f6\u5ac9\uff0c\u51cc\u672a\u98ce\u4e5f\u6b63\u60f3\u5ff5\u7740\u5218\u90c1\u82b3\uff0c\u4ed6\u4e5f\u7ed3\u8bc6\u4e86\u4e00\u73ed\u65b0\u7684\u60a3\u96be\u4e2d\u7684\u670b\u53cb\uff0c\u4ed6\u88ab\u5173\u5728\u897f\u85cf \u62c9\u8428\u7684\u5e03\u8fbe\u62c9\u5bfa\u8ff7\u5bab\u3002\u5e03\u8fbe\u62c9\u5bfa\u672c\u6765\u662f\u8fbe\u8d56\u5587\u561b\u9a7b\u9521\u4e4b\u5730\uff0c\u73b0\u5728\u5374\u53d8\u6210\u4e86\u5141\u9898\u7684\u4fb5\u85cf\u519b\u603b \u90e8\u3002\u5141\u9898\u4e3a\u4e86\u5949\u884c\u5eb7\u7199\u7684\u6000\u67d4\u653f\u7b56\uff0c\u9664\u4e86\u53e6\u7acb\u65b0\u7684\u8fbe\u8d56\u4e4b\u5916\uff0c\u5176\u4f59\u5bfa\u4e2d\u7684\u5587\u561b\uff0c\u4ecd\u7136\u7559\u7740\uff0c \u4f46\u6e05\u519b\u7684\u6b66\u58eb\u5df2\u904d\u5e03\u5bfa\u5185\u3002\u5bfa\u4e2d\u7684\u8ff7\u5bab\u9053\u8def\u66f2\u66f2\u6298\u62c6\uff0c\u5141\u9898\u5230\u540e\u53c8\u547d\u5de7\u5320\u589e\u52a0\u95e8\u6237\uff0c\u53d8\u66f4\u9053 \u8def\uff0c\u5f04\u5f97\u5341\u5206\u590d\u6742\u3002\u8ff7\u5bab\u4e2d\u91cd\u95e8\u53e0\u6237\uff0c\u5168\u662f\u6e05\u519b\u7684\u7279\u9009\u6b66\u58eb\u5b88\u536b\u3002\u51cc\u672a\u98ce\u5c31\u5173\u5728\u8ff7\u5bab\u4e2d\u5fc3\u7684 \u5bc6\u7a92\u91cc\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5728\u90a3\u91cc\u6fc0\u8d77\u4e86\u6781\u5927\u7684\u6ce2\u6f9c\uff0c\u8270\u96be\u4ee4\u4eba\u76f8\u4fe1\u7684\u5947\u4e8b\u53d1\u751f\u4e86\u3002\u4ed6\u867d\u7136\u62c7\u6307\u88ab\u5272\uff0c\u9762\u6709 \u5200\u75a4\uff0c\u4f46\u5c31\u662f\u8fd9\u6837\u4e11\u964b\u7684\u4eba\uff0c\u5168\u8eab\u5374\u4f3c\u5145\u6ee1\u4e86\u4e00\u79cd\u7279\u6b8a\u7684\u9b45\u529b\u3002\u770b\u5b88\u4ed6\u7684\u536b\u58eb\u4eec\uff0c\u90fd\u88ab\u4ed6\u8fd9 \u79cd\u5947\u5f02\u7684\u9b45\u529b\u6240\u5438\u5f15\u7740\u3002\u51cc\u672a\u98ce\u7684\u82f1\u96c4\u6545\u4e8b\uff0c\u672c\u6765\u50cf\u4f20\u5947\u4e00\u6837\uff0c\u4e45\u4e45\u4ee5\u6765\uff0c\u5c31\u6df1\u5370\u5728\u4ed6\u4eec\u7684 \u8111\u6d77\u91cc\u3002\u5982\u4eca\u51cc\u672a\u98ce\u7adf\u7136\u548c\u4ed6\u4eec\u547c\u5438\u76f8\u95fb\uff0c\u671d\u5915\u4e0e\u5171\u3002\u8fd9\u81ea\u7136\u5f15\u8d77\u4e86\u4e00\u573a\u8f70\u52a8\u3002\u4ed6\u4eec\u8d77\u521d\u8fd8 \u53ea\u662f\u6000\u7740\u597d\u5947\u7684\u5fc3\u7406\uff0c\u53bb\u63a5\u8fd1\u51cc\u672a\u98ce\uff0c\u6e10\u6e10\u5c31\u88ab\u4ed6\u82f1\u96c4\u7684\u6c14\u8d28\u3001\u82f1\u96c4\u7684\u8c08\u5410\u6240\u201c\u8ff7\u201d\u7740\u4e86\u3002 \u5c24\u5176\u4e00\u4e9b\u5e74\u8f7b\u7684\u536b\u58eb\u4eec\uff0c\u66f4\u662f\u4ece\u5fc3\u5e95\u91cc\u5c0a\u656c\u4ed6\u3002 \u3000\u3000\u5728\u5e74\u8f7b\u7684\u536b\u58eb\u4e2d\uff0c\u6709\u4e24\u4e2a\u4eba\u7279\u522b\u63a5\u8fd1\u51cc\u672a\u98ce\uff0c\u4e00\u4e2a\u53eb\u505a\u5468\u9752\uff0c\u4e00\u4e2a\u53eb\u505a\u9a6c\u65b9\u3002\u5468\u9752\u662f\u4e16 \u88ad\u6b66\u58eb\uff0c\u4ed6\u7684\u7956\u7236\u8fd8\u662f\u987a\u6cbb\u521d\u5165\u5173\u65f6\uff0c\u6444\u653f\u738b\u591a\u5c14\u886e\u6240\u7f51\u7f57\u7684\u6b66\u58eb\u4e4b\u4e00\uff0c\u540e\u6765\u56e0\u4e3a\u66ff\u6444\u653f\u738b \u5e72\u4e86\u4e00\u4ef6\u79d8\u5bc6\u5dee\u4e8b\uff0c\u4e8b\u6210\u540e\u88ab\u6444\u653f\u738b\u6bd2\u6b7b\u706d\u53e3\u3002\u9a6c\u65b9\u5219\u662f\u56de\u4eba\uff0c\u6d6a\u8361\u6c5f\u6e56\uff0c\u65e0\u4ee5\u4e3a\u751f\uff0c\u94c1\u6247 \u5e2e\u7684\u5e2e\u4e3b\u5c1a\u4e91\u4ea8\uff0c\u5728\u56de\u7586\u9047\u89c1\u4ed6\uff0c\u628a\u4ed6\u8350\u7ed9\u4e86\u695a\u662d\u5357\u3002 \u3000\u3000\u51cc\u672a\u98ce\u5728\u522b\u7684\u536b\u58eb\u53e3\u4e2d\uff0c\u63a2\u51fa\u4e86\u5468\u9752\u7956\u7236\u7684\u6b7b\u4e8b\uff0c\u4e5f\u63a2\u51fa\u4e86\u9a6c\u65b9\u7684\u6765\u5386\u3002\u4e0d\u6d88\u591a\u4e45\uff0c\u4fbf \u548c\u4e24\u4eba\u6210\u4e86\u5fc3\u8179\u4e4b\u4ea4\u3002\u6709\u4e00\u665a\u8f6e\u5230\u5468\u9752\u5b88\u536b\uff0c\u51cc\u672a\u98ce\u548c\u4ed6\u8c08\u8d77\u6c5f\u6e56\u597d\u6c49\u7684\u884c\u5f84\uff0c\u5468\u9752\u542c\u5f97\u6d25 \u6d25\u6709\u5473\u3002\u51cc\u672a\u98ce\u6709\u610f\u65e0\u610f\u5730\u63d0\u8d77\u4e86\u5468\u9752\u7684\u7956\u7236\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u4ee5\u9053\u4e49\u4e3a\u5148\uff0c\u670b\u53cb\u5b81\u613f \u4e24\u80c1\u63d2\u5200\uff0c\u81ea\u5df1\u7684\u4eba\u7edd\u4e0d\u4f1a\u4e92\u76f8\u6b8b\u6740\u3002\u7ed9\u7687\u5e1d\u8001\u513f\u5f53\u5dee\uff0c\u867d\u7136\u6709\u529f\u540d\u5229\u788c\uff0c\u5374\u662f\u671d\u5915\u90fd\u5f97\u63d0 \u5fc3\u540a\u80c6\uff0c\u65e2\u6015\u7687\u5e1d\u8bdb\u622e\uff0c\u53c8\u6015\u540c\u4f34\u9677\u5bb3\u3002\u6709\u8840\u6027\u7684\u7537\u5b50\u4e5f\u771f\u96be\u957f\u505a\u4e0b\u53bb\u3002\u50cf\u4ee4\u7956\u90a3\u6837\u82f1\u96c4\uff0c \u5230\u5934\u6765\u8fd8\u4e0d\u514d\u6a2a\u6b7b\u3002\u201d\u5468\u9752\u5bf9\u7956\u7236\u7684\u4e8b\uff0c\u9690\u9690\u6709\u6240\u77e5\u95fb\uff0c\u542c\u51cc\u672a\u98ce\u90a3\u4e48\u4e00\u8bf4\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u5fd9 \u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u4f60\u7684\u6d88\u606f\u53ef\u771f\uff1f\u201d\u51cc\u672a\u98ce\u4f9d\u76f4\u8bf4\u4e86\u3002\u5468\u9752\u6d41\u6cea\u9053\uff1a\u201c\u6211\u7956\u7236\u7684\u4e8b\uff0c \u6211\u4e5f\u66fe\u5f71\u5f71\u7ef0\u7ef0\u6709\u6240\u51e4\u95fb\uff0c\u53ea\u662f\u6211\u81ea\u5c0f\u5c31\u662f\u536b\u58eb\uff0c\u4e00\u5411\u90fd\u4ee5\u4e3a\u6548\u5fe0\u7687\u4e0a\uff0c\u662f\u505a\u2018\u5974\u624d\u2019\u8005\u5929 \u7ecf\u5730\u4e49\u4e4b\u4e8b\uff0c\u4f60\u6765\u4e86\uff0c\u4ee4\u6211\u8305\u585e\u987f\u5f00\uff0c\u539f\u6765\u5728\u6c5f\u6e56\u4e0a\uff0c\u4eba\u4e0e\u4eba\u4e4b\u95f4\uff0c\u662f\u8fd9\u6837\u8d64\u8bda\u76f8\u5bf9\u7684\u3002\u201d \u8bf4\u5b8c\u4e4b\u540e\uff0c\u706b\u7206\u7206\u5730\u5c31\u60f3\u5e2e\u52a9\u51cc\u672a\u98ce\u9003\u8d70\uff0c\u51cc\u672a\u98ce\u6025\u5fd9\u529d\u6b62\uff0c\u53eb\u4ed6\u9759\u5f85\u65f6\u673a\u3002 \u3000\u3000\u53c8\u6709\u4e00\u665a\uff0c\u662f\u9a6c\u65b9\u5f53\u503c\u3002\u51cc\u672a\u98ce\u7ec6\u8c08\u56de\u4eba\u6240\u53d7\u7684\u82e6\u96be\uff0c\u53c8\u8bf4\u8d77\u5c1a\u4e91\u4ead\u548c\u4eba\u5996\u90dd\u98de\u51e4\u662f\u600e \u6837\u7684\u4e3a\u6c5f\u6e56\u6240\u4e0d\u9f7f\u3002\u9a6c\u65b9\u9762\u7ea2\u8033\u8d64\uff0c\u7f9e\u6127\u4e4b\u5ff5\u6cb9\u7136\u800c\u751f\uff0c\u81ea\u6b64\u4e5f\u88ab\u51cc\u672a\u98ce\u6536\u4e3a\u5fc3\u8179\u3002 \u3000\u3000\u695a\u662d\u5357\u5c06\u51cc\u672a\u98ce\u5173\u5230\u8ff7\u5bab\u7684\u5bc6\u5ba4\u4e4b\u540e\uff0c\u65f6\u65f6\u6765\u8feb\u4ed6\u8981\u62f3\u7ecf\u5251\u8bc0\uff0c\u5230\u77e5\u9053\u62f3\u7ecf\u5251\u8bc0\u786e\u5b9e\u4e0d \u5728\u4ed6\u7684\u8eab\u4e0a\u65f6\uff0c\u53c8\u8981\u4ed6\u91cd\u5199\u51fa\u6765\u3002\u51cc\u672a\u98ce\u7684\u820c\u5934\u5389\u5bb3\u6781\u4e86\uff0c\u695a\u662d\u5357\u6bcf\u6b21\u6765\u90fd\u7ed9\u4ed6\u9a82\u5f97\u72d7\u8840\u6dcb \u5934\uff0c\u800c\u4e14\u51cc\u672a\u98ce\u7edd\u4e0d\u80e1\u9a82\uff0c\u4e00\u4ef6\u4ef6\u4e00\u6869\u6869\uff0c\u90fd\u662f\u695a\u662d\u5357\u5e72\u8fc7\u7684\u574f\u4e8b\u3002\u628a\u4ed6\u600e\u6837\u80cc\u53db\u5e08\u95e8\uff0c\u9677 \u5bb3\u5e08\u5144\uff0c\u6697\u6740\u540c\u4f34\u7684\u4e8b\u90fd\u6296\u4e86\u51fa\u6765\u3002\u542c\u5f97\u536b\u58eb\u4eec\u60ca\u5fc3\u52a8\u9b44\uff0c\u695a\u662d\u5357\u82e6\u607c\u6781\u4e86\uff0c\u65e2\u60f3\u903c\u4ed6\u5199\u51fa \u62f3\u7ecf\u5251\u8bc0\uff0c\u53c8\u6015\u4ed6\u7684\u6bd2\u9a82\uff0c\u5230\u4e86\u540e\u6765\uff0c\u77e5\u9053\u8981\u8feb\u4ed6\u5199\u662f\u5f88\u96be\u7684\u4e86\uff0c\u6e10\u6e10\u5c31\u8d77\u4e86\u6740\u673a\u3002 \u3000\u3000\u53ef\u662f\u5f53\u695a\u662d\u5357\u6b63\u8981\u4e0b\u6740\u624b\u7684\u65f6\u5019\uff0c\u6709\u4e00\u5c0f\u961f\u4eba\u9a6c\uff0c\u5df2\u6a2a\u8fc7\u8349\u539f\uff0c\u7a7f\u4eba\u897f\u85cf\uff0c\u5077\u8fdb\u62c9\u8428\uff0c \u4f3a\u673a\u63f4\u6551\u51cc\u672a\u98ce\u4e86\u3002 \u3000\u3000\u5728\u6842\u4ef2\u660e\u548c\u6613\u5170\u73e0\u5b66\u6210\u5251\u6cd5\u540e\u7684\u7b2c\u4e03\u5929\uff0c\u5192\u6d63\u83b2\u548c\u5085\u9752\u4e3b\u56de\u6765\u4e86\u3002\u8bf4\u51fa\u51cc\u672a\u98ce\u5c1a\u5728\u4eba\u95f4 \u7684\u6d88\u606f\uff0c\u5927\u5bb6\u90fd\u975e\u5e38\u9ad8\u5174\u3002\u4f46\u542c\u8bf4\u51cc\u672a\u98ce\u88ab\u5173\u5728\u5e03\u8fbe\u62c9\u5bab\uff0c\u5468\u56f4\u6709\u5141\u9898\u7684\u91cd\u5175\u9632\u5b88\uff0c\u5927\u5bb6\u53c8 \u90fd\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u53ea\u6050\u6bd4\u5f53\u521d\u5927\u52ab\u5929\u7262\u8fd8\u8981\u56f0\u96be\u3002\u6613\u5170\u73e0\u9053\uff1a\u201c\u65e0\u8bba\u600e\u6837\u5371\u96be\uff0c\u6211\u4eec\u90fd\u8981\u53bb\u6551\u7684 \u4e86\u3002\u201d\u54c8\u8428\u514b\u7684\u9752\u5e74\u914b\u957f\u547c\u514b\u6d4e\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff0c\u51cc\u5927\u4fa0\u662f\u6211\u4eec\u4e00\u65cf\u7684\u6069\u4eba\uff0c\u4e3a\u4e86\u4ed6\uff0c\u6211\u4eec \u8d74\u6c64\u8e48\u706b\uff0c\u90fd\u4e0d\u6562\u63a8\u8f9e\u3002\u53ea\u662f\u4e5f\u5f97\u76d8\u7b97\u4e00\u6761\u6bd4\u8f83\u7a33\u59a5\u7684\u8ba1\u7b56\uff0c\u53ea\u51e0\u4e2a\u4eba\u53bb\uff0c\u6050\u6015\u65e0\u6d4e\u4e8e \u4e8b\u3002\u201d\u5085\u9752\u4e3b\u62c8\u987b\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u5c31\u70e6\u4f60\u9009\u4e09\u767e\u901a\u6653\u6280\u51fb\u7684\u6b7b\u58eb\uff0c\u968f\u6211\u4eec\u4e00\u9053\u53bb\u3002\u201d\u5218\u90c1\u82b3 \u9053\uff1a\u201c\u4eba\u591a\u6613\u4e8e\u88ab\u53d1\u89c9\uff0c\u6211\u4eec\u600e\u51b2\u5f97\u8fc7\u85cf\u8fb9\u7684\u5927\u519b\u5c01\u9501\uff1f\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u82e5\u5728\u5341\u5929\u4e4b\u5185\uff0c\u8d76 \u5230\u85cf\u8fb9\uff0c\u4e5f\u8bb8\u8fd8\u6709\u529e\u6cd5\u901a\u8fc7\u3002\u8fdf\u4e86\u6211\u5c31\u4e0d\u6562\u62c5\u4fdd\u4e86\u3002\u201d\u4f17\u4eba\u5fd9\u95ee\u7f18\u6545\uff0c\u5085\u9752\u4e3b\u7b11\u9053\uff1a\u201c\u5c71\u4eba \u6599\u5230\u4eca\u65e5\u4e4b\u4e8b\uff0c\u65e9\u5df2\u505a\u4e86\u4e00\u756a\u624b\u811a\u4e86\uff01\u201d\u539f\u6765\u5f53\u65e5\u5085\u9752\u4e3b\u5728\u5fa1\u8425\u4e4b\u4e2d\uff0c\u88ab\u5eb7\u7199\u53eb\u53bb\uff0c\u66ff\u4e00\u4e2a \u8d1d\u5b50\u8bd5\u533b\u6cbb\u51bb\u75ae\uff0c\u4e00\u8bd5\u4fbf\u597d\uff0c\u5eb7\u7199\u5341\u5206\u9ad8\u5174\uff0c\u8bf7\u4ed6\u4f20\u4e0b\u836f\u65b9\u3002\u5085\u9752\u4e3b\u5341\u5206\u201c\u5356\u529b\u201d\uff0c\u4e0d\u4f46\u5199 \u4e0b\u836f\u65b9\uff0c\u8fd8\u91c7\u96c6\u8349\u836f\uff0c\u7814\u6210\u6570\u767e\u5305\u836f\u7c89\uff0c\u7559\u7ed9\u8fb9\u5883\u7684\u620d\u5352\u3002\u533b\u6cbb\u51bb\u75ae\u7684\u836f\u65b9\u5e76\u4e0d\u5047\uff0c\u53ef\u662f\u7814 \u6210\u7684\u836f\u7c89\u4e4b\u4e2d\uff0c\u5085\u9752\u4e3b\u5374\u52a0\u591a\u4e86\u4e00\u79cd\u5389\u5bb3\u7684\u8349\u836f\uff0c\u64e6\u540e\u521d\u65f6\u5e76\u65e0\u5f02\u72b6\uff0c\u800c\u4e14\u60a3\u8005\u8fd8\u9887\u89c9\u8212 \u670d\uff0c\u53ef\u662f\u8fc7\u4e86\u51e0\u5929\u4e4b\u540e\uff0c\u51bb\u75ae\u5374\u4f1a\u590d\u53d1\uff0c\u800c\u4e14\u6bd4\u539f\u6765\u7684\u8fd8\u5389\u5bb3\u5341\u5206\u3002\u5085\u9752\u4e3b\u7b97\u4e86\u65e5\u671f\uff0c\u4f30\u8ba1 \u5728\u5341\u5929\u4e4b\u5185\u8d76\u5230\u85cf\u8fb9\uff0c\u5c31\u6b63\u662f\u90a3\u73ed\u620d\u5352\u51bb\u75ae\u5927\u53d1\u7684\u65f6\u5019\u3002 \u3000\u3000\u518d\u8bf4\u7eb3\u5170\u5bb9\u82e5\u81ea\u5192\u6d63\u83b2\u53bb\u540e\uff0c\u60c5\u601d\u60d8\u60d8\u3002\u4e00\u65e5\u542c\u5f97\u8425\u5e10\u5916\u8fdc\u8fdc\u4f20\u6765\u4e86\u6218\u9f13\u4e4b\u58f0\uff0c\u5eb7\u7199\u7687 \u5e1d\u6012\u5bb9\u6ee1\u9762\u5730\u8fdb\u6765\u8bf4\u9053\uff1a\u201c\u5bb9\u82e5\uff0c\u524d\u65e5\u6765\u7684\u90a3\u4e24\u7236\u5973\u662f\u5978\u7ec6\uff01\u201d\u7eb3\u5170\u5bb9\u82e5\u8df3\u8d77\u6765\u9053\uff1a\u201c\u600e\u4e48 \u89c1\u5f97\uff1f\u201d\u5eb7\u7199\u9053\uff1a\u201c\u9002\u624d\u524d\u536b\u7684\u6307\u6325\u6d3e\u9063\u5feb\u9a6c\u6765\u62a5\uff0c\u6709\u4e00\u80a1\u9a6c\u8d3c\u60f3\u51b2\u8fc7\u5c01\u9501\uff0c\u7ed5\u8fc7\u8349\u539f\uff0c\u4ed6 \u4eec\u51fa\u52a8\u6570\u5343\u620d\u5352\u515c\u6355\uff0c\u4e0d\u6599\u5175\u58eb\u4eec\u5341\u4e5d\u751f\u4e86\u51bb\u75ab\uff0c\u800c\u4e14\u53d1\u4f5c\u5f97\u6781\u4e3a\u5389\u5bb3\uff0c\u6570\u5343\u620d\u5352\uff0c\u82e6\u6218\u4e4b \u4e0b\uff0c\u7adf\u6321\u4e0d\u4f4f\uff0c\u8981\u6211\u4eec\u8d76\u6d3e\u4eba\u53bb\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u201c\u554a\u5440\u201d\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u60f6\u6050\u8bf4\u9053\uff1a\u201c\u5fae\u81e3\u8be5 \u6b7b\uff0c\u7adf\u7136\u7ed9\u5978\u7ec6\u6df7\u4e86\u8fdb\u6765\uff0c\u8bf7\u7687\u4e0a\u5904\u7f6a\uff0c\u201d\u5eb7\u7199\u9053\uff1a\u201c\u4e0d\u77e5\u4e0d\u7f6a\uff0c\u6211\u4e5f\u4e0d\u602a\u8d23\u4f60\uff0c\u4f60\u53d7\u4e86\u6b64 \u6b21\u6559\u8bad\uff0c\u4ee5\u540e\u5c11\u4ea4\u6765\u5386\u4e0d\u660e\u7684\u4eba\u3002\u201d\u7eb3\u5170\u5bb9\u82e5\u552f\u552f\u79f0\u662f\u3002\u5eb7\u7199\u53c8\u5f97\u610f\u7b11\u9053\uff1a\u201c\u5e78\u4e8f\u6211\u7684\u795e\u7b56 \u8425\u4fdd\u517b\u5f97\u597d\uff0c\u6839\u672c\u6ca1\u6709\u7528\u5230\u90a3\u4eba\u7684\u836f\u7c89\uff0c\u73b0\u5728\u5df1\u6d3e\u51fa\u53bb\uff0c\u6599\u90a3\u4e00\u5c0f\u80a1\u9a6c\u8d3c\uff0c\u9003\u4e0d\u51fa\u795e\u7b56\u8425\u7684 \u94c1\u638c\u3002\u6211\u5012\u770b\u770b\uff0c\u8fd9\u4e9b\u9a6c\u8d3c\u53ef\u662f\u5403\u4e86\u8001\u864e\u7684\u5fc3\u3001\u8c79\u5b50\u7684\u80c6\uff1f\u5c45\u7136\u8fd9\u6837\u80c6\u5927\u5305\u5929\uff01\u201d\u7eb3\u5170\u5bb9\u82e5 \u542c\u4e86\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u5403\u4e86\u4e00\u60ca\u3002\u795e\u7b56\u8425\u662f\u7981\u536b\u519b\u4e2d\u7684\u7cbe\u9510\uff0c\u7531\u7687\u5e1d\u4eb2\u81ea\u7edf\u7387\uff0c\u7aef\u7684\u975e\u540c\u5c0f\u53ef\u3002 \u7eb3\u5170\u5bb9\u82e5\u773c\u73e0\u4e00\u8f6c\uff0c\u8bf4\u9053\uff1a\u201c\u51fa\u52a8\u4e86\u795e\u7b56\u8425\u53bb\u56f4\u6355\u9a6c\u8d3c\uff0c\u5b9a\u80fd\u624b\u5230\u64d2\u6765\u3002\u7687\u4e0a\u82e5\u6709\u5174\u81f4\u53bb \u770b\uff0c\u6211\u4eec\u4e00\u540c\u89c2\u6218\u5982\u4f55\uff1f\u201d\u5eb7\u7199\u4e00\u65f6\u5174\u8d77\uff0c\u8fde\u58f0\u9053\u597d\uff0c\u548c\u7eb3\u5170\u9009\u4e86\u4e24\u9a91\u5fa1\u9a6c\uff0c\u5728\u4f8d\u536b\u7c07\u62e5 \u4e0b\uff0c\u5f1b\u5411\u8fb9\u5883\u3002 \u3000\u3000\u795e\u7b56\u8425\u4eba\u5f3a\u9a6c\u58ee\uff0c\u4ece\u5927\u8425\u9a70\u5230\u524d\u7ebf\uff0c\u5341\u4f59\u4e8c\u5341\u91cc\u8def\uff0c\u7528\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\u3002\u5085\u9752\u4e3b\u4ed6\u4eec\u6b63\u81ea \u7a81\u56f4\uff0c\u795e\u7b56\u8425\u4e00\u6d8c\u800c\u4e0a\uff0c\u56db\u9762\u6563\u5f00\uff0c\u72b9\u5982\u5728\u8349\u539f\u4e0a\u8f85\u4e86\u4e00\u5f20\u5927\u7f51\uff0c\u5411\u4e2d\u592e\u6162\u6162\u6536\u675f\uff0c\u5c06\u5085\u9752 \u4e3b\u7b49\u4e09\u767e\u5065\u513f\u56f4\u5728\u6838\u5fc3\u3002\u5eb7\u7199\u548c\u7eb3\u5170\u5bb9\u82e5\u8d76\u5230\u4e4b\u65f6\uff0c\u53ea\u542c\u5f97\u6740\u58f0\u9707\u5929\uff0c\u5251\u5f71\u5200\u5149\uff0c\u4ea4\u6218\u5f97\u5341 \u5206\u6fc0\u70c8\u3002 \u3000\u3000\u5eb7\u7199\u548c\u7eb3\u5170\u5bb9\u82e5\u7acb\u9a6c\u4e0a\u4e18\uff0c\u6307\u70b9\u89c2\u671b\u3002\u5eb7\u7199\u53d8\u8272\u8bf4\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u5bfb\u5e38\u7684\u9a6c\u8d3c\uff01\u201d\u795e\u7b56\u8425 \u7684\u7edf\u5e26\u4e2a\u4e2a\u90fd\u662f\u6b66\u529f\u7cbe\u6e5b\u7684\u4eba\uff0c\u6570\u5341\u7edf\u5e26\u7edf\u9886\u4e09\u5343\u94c1\u9a91\uff0c\u867d\u7136\u628a\u654c\u4eba\u91cd\u91cd\u56f0\u4f4f\uff0c\u4f46\u90a3\u5e2e\u201c\u9a6c \u8d3c\u201d\u51b2\u5230\u4e4b\u5904\uff0c\u5374\u5982\u6ce2\u5206\u6d6a\u88c2\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u5eb7\u7199\u5df2\u4eb2\u773c\u89c1\u5230\u51e0\u4e2a\u7edf\u5e26\u4e27\u547d\u5200\u5251\u4e4b\u4e0b\u3002\u770b\u4e86\u4e00 \u9635\uff0c\u5eb7\u7199\u53c8\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u628a\u624b\u4e00\u6307\uff0c\u5bf9\u7eb3\u5170\u5bb9\u82e5\u8bf4\u9053\uff1a\u201c\u4f60\u770b\uff0c\u90a3\u4e2a\u8001\u513f\uff01\u201d\u7eb3\u5170\u4f9d\u8a00\u770b \u53bb\uff0c\u53ea\u89c1\u5085\u9752\u4e3b\u4e00\u9a6c\u5f53\u524d\uff0c\u4e00\u67c4\u957f\u5251\uff0c\u98ce\u7ffb\u4e91\u6d8c\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u4fbf\u6740\u7ffb\u51e0\u4eba\u3002\u5eb7\u7199\u9053\uff1a\u201c\u8fd9\u8001 \u513f\u4e0d\u5c31\u662f\u524d\u5929\u90a3\u4e2a\u8349\u5934\u533b\u751f\uff1f\u201d\u7eb3\u5170\u4e00\u770b\uff0c\u53ea\u89c1\u5192\u6d63\u83b2\u4e5f\u6742\u5728\u4e71\u519b\u4e4b\u4e2d\u3002\u7eb3\u5170\u5fc3\u60f3\uff1a\u4ed6\u4eec\u867d \u7136\u90fd\u662f\u6b66\u6797\u9ad8\u624b\uff0c\u53ea\u662f\u5be1\u4e0d\u654c\u4f17\uff0c\u65f6\u5019\u4e00\u4e45\uff0c\u5fc5\u5b9a\u652f\u6301\u4e0d\u4f4f\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u5bf9\u5eb7\u7199\u8bf4\u9053\uff1a\u201c\u90a3 \u4e2a\u5c11\u5973\u539f\u6765\u4e5f\u662f\u9a6c\u8d3c\u3002\u201d\u5eb7\u7199\u8fd9\u65f6\u4e5f\u770b\u89c1\u5192\u6d63\u83b2\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u7eb3\u5170\u5bb9\u82e5\u5ffd\u7136\u7eb5\u9a6c\u51fa\u53bb\uff0c\u5927\u53eb \u9053\uff1a\u201c\u6c14\u715e\u6211\u4e5f\uff01\u4e0d\u5c06\u8d3c\u5b50\u751f\u64d2\uff0c\u8a93\u4e0d\u4e3a\u4eba\uff01\u201d\u5eb7\u7199\u6025\u53eb\uff1a\u201c\u522b\u5192\u9669\uff0c\u5feb\u56de\u6765\uff01\u201d\u7eb3\u5170\u5feb\u9a6c \u5636\u98ce\uff0c\u65e9\u5df2\u51b2\u8fdb\u9635\u4e2d\u53bb\u4e86\u3002 \u3000\u3000\u795e\u7b56\u8425\u5b98\u5175\u5ffd\u89c1\u7eb3\u5170\u516c\u5b50\u98de\u9a6c\u51b2\u6765\uff0c\u4e2a\u4e2a\u6115\u7136\u3002\u5f20\u534e\u662d\u508d\u7740\u6613\u5170\u73e0\uff0c\u6740\u5f97\u5934\u660f\u773c\u82b1\uff0c\u5bf9 \u7740\u7eb3\u5170\u5bb9\u82e5\u4e00\u5251\u523a\u53bb\uff0c\u7eb3\u5170\u594b\u529b\u4e00\u67b6\uff0c\u9669\u9669\u843d\u9a6c\uff0c\u6613\u5170\u73e0\u624b\u8098\u4e00\u649e\uff0c\u628a\u5f20\u534e\u662d\u649e\u8fc7\u4e00\u8fb9\uff0c\u5f20 \u534e\u662d\u8fd9\u624d\u770b\u6e05\u662f\u7eb3\u5170\u5bb9\u82e5\uff0c\u201c\u554a\u5440\uff01\u201d\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\u3002\u5192\u6d63\u83b2\u9a70\u9a6c\u8fc7\u6765\uff0c\u7eb3\u5170\u63d0\u5200\u5288\u53bb\uff0c\u5192 \u6d63\u83b2\u8f7b\u8f7b\u4e00\u95ea\uff0c\u7eb3\u5170\u5bb9\u82e5\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u628a\u6211\u64d2\u53bb\u3002\u201d\u53c8\u662f\u4e00\u5200\u5411\u5192\u6d63\u83b2\u6000\u4e2d\u62a2\u4eba\uff0c\u6842\u4ef2\u660e\u864e \u543c\u8d77\u6765\uff0c\u5192\u6d63\u83b2\u4e00\u8212\u7389\u624b\uff0c\u628a\u7eb3\u5170\u624b\u8155\u5201\u7740\uff0c\u631f\u4e86\u8fc7\u6765\uff0c\u77aa\u76ee\u6a2a\u4e86\u6842\u4ef2\u660e\u4e00\u773c\u9053\uff1a\u201c\u4f60\u8fd9\u50bb \u74dc\uff01\u7ed9\u6211\u9053\u4e0b\u3002\u201d\u6842\u4ef2\u660e\u4f9d\u7a00\u8ba4\u5f97\u7eb3\u5170\u5bb9\u82e5\uff0c\u53eb\u9053\uff1a\u201c\u54fc\uff01\u6211\u4eec\u90fd\u4ee5\u4e3a\u4f60\u662f\u597d\u4eba\uff0c\u539f\u6765\u4f60\u4e5f \u66ff\u7687\u5e1d\u8001\u513f\u5356\u547d\uff01\u201d\u5192\u6d63\u83b2\u7ed9\u4ed6\u6c14\u5f97\u557c\u7b11\u7686\u975e\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5feb\u53eb\u5085\u4f2f\u4f2f\u6765\uff01\u201d \u3000\u3000\u795e\u7b56\u8425\u58eb\u5175\u89c1\u7eb3\u5170\u516c\u5b50\u4e00\u7167\u9762\u5c31\u88ab\u654c\u4eba\u64d2\u53bb\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u7eb7\u7eb7\u6765\u6551\uff0c\u6613\u5170\u73e0\u77ed\u5251 \u98de\u821e\uff0c\u780d\u7ffb\u51e0\u4e2a\uff0c\u5085\u9752\u4e3b\u6025\u5fd9\u8d76\u6765\u3002\u5728\u5192\u6d63\u83b2\u624b\u4e2d\u63a5\u4e86\u7eb3\u5170\u5bb9\u82e5\uff0c\u957f\u5251\u67b6\u5728\u4ed6\u7684\u9888\u9879\uff0c\u5389\u58f0 \u5bf9\u6e05\u5175\u8bf4\u9053\u3002\u201d\u4f4f\u624b\uff0c\u4e0d\u7136\u6211\u5c31\u5c06\u8fd9\u4eba\u5241\u4e86\uff01\u201d \u3000\u3000\u795e\u7b56\u8425\u5c06\u58eb\u77e5\u9053\u7eb3\u5170\u516c\u5b50\u662f\u7687\u4e0a\u6700\u5ba0\u7231\u7684\u4eba\uff0c\u5982\u4f55\u8fd8\u6562\u52a8\u624b\uff1f\u7981\u536b\u519b\u7684\u526f\u7edf\u9886\u517c\u795e\u7b56\u8425 \u7684\u603b\u7ba1\u5e26\u5f20\u627f\u658c\u7eb5\u9a6c\u8fc7\u6765\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u6709\u8bdd\u597d\u8bf4\u3002\u4e14\u6162\u52a8\u624b\uff01\u201d\u5085\u9752\u4e3b\u626c\u7709\u7b11\u9053\uff1a\u201c\u5f20\u526f \u7edf\u9886\uff0c\u522b\u6765\u65e0\u6059\uff1f\u201d\u5f20\u627f\u658c\u4e00\u6115\uff0c\u5085\u9752\u4e3b\u9053\uff1a\u201c\u4e94\u53f0\u5c71\u4e0b\u6b66\u5bb6\u5e84\u4e4b\u4f1a\uff0c\u526f\u7edf\u9886\u8fd8\u8bb0\u5f97\u4e48\uff1f\u8001 \u673d\u4fbf\u662f\u6c5f\u5357\u5085\u9752\u4e3b\uff01\u201d\u5f20\u627f\u658c\u4e00\u770b\uff0c\u89c1\u5085\u9752\u4e3b\u5f62\u5bb9\u5168\u653f\uff0c\u4f46\u77e5\u4ed6\u533b\u672f\u795e\u5999\uff0c\u4e5f\u4e0d\u4ee5\u4e3a\u5f02\uff0c\u5f53 \u4e0b\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5085\u8001\u5148\u751f\u6709\u4f55\u89c1\u6559\uff1f\u201d\u5f20\u627f\u658c\u65e9\u5e74\u4e5f\u662f\u6c5f\u6e56\u4eba\u7269\uff0c\u4e3a\u4eba\u6bd4\u695a\u662d\u5357\u7a33\u91cd\u5f97\u591a\u3002 \u6240\u4ee5\u5f53\u5e74\u56f4\u6b66\u5bb6\u5e84\u65f6\uff0c\u8fd8\u548c\u6b66\u5143\u82f1\u4ee5\u793c\u76f8\u89c1\u3002\u4ed6\u77e5\u5085\u9752\u4e3b\u6349\u4e86\u7eb3\u5170\u5bb9\u82e5\u4e4b\u540e\uff0c\u5fc5\u5b9a\u6709\u6240\u8981 \u631f\uff0c\u7d22\u6027\u4e00\u5f00\u53e3\u4fbf\u628a\u8bdd\u8bf4\u660e\uff0c\u7b49\u5019\u5bf9\u65b9\u5f00\u51fa\u6761\u4ef6\u3002 \u3000\u3000\u5085\u9752\u4e3b\u53cc\u7738\u70af\u70af\uff0c\u7ad6\u8d77\u62c7\u6307\u8bf4\u9053\uff1a\u201c\u5f20\u5927\u4eba\u4e5f\u662f\u6c5f\u6e56\u7684\u5927\u884c\u5bb6\uff0c\u54b1\u4eec\u4e0d\u6562\u591a\u6c42\uff0c\u53ea\u70e6\u7eb3 \u5170\u516c\u5b50\u9001\u6211\u4eec\u767e\u91cc\u8def\uff01\u201d\u5f20\u627f\u658c\u9053\uff1a\u201c\u6b64\u4e8b\u6211\u4e0d\u6562\u4f5c\u4e3b\uff0c\u8bf7\u5404\u4f4d\u7a0d\u5f85\u987b\u81fe\uff0c\u5f85\u6211\u7980\u8fc7\u7687\u4e0a\u5982 \u4f55\uff1f\u201d\u8d70\u51fa\u6218\u5730\uff0c\u5c06\u5085\u9752\u4e3b\u7684\u8bdd\u5bf9\u5eb7\u7199\u8bf4\u4e86\u3002\u5eb7\u7199\u76b1\u7709\u9053\uff1a\u201c\u53eb\u4ed6\u628a\u5bb9\u82e5\u653e\u56de\uff0c\u6211\u4eec\u8ba9\u4ed6\u4eec \u8fc7\u53bb\u4fbf\u662f\u4e86\uff01\u201d\u5f20\u627f\u658c\u5feb\u9a6c\u56de\u62a5\uff0c\u5085\u9752\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u5047\u82e5\u5f20\u5927\u4eba\u53ef\u4ee5\u505a\u4e3b\uff0c\u90a3\u4e48\u54b1\u4eec\u4ea4\u4eba\u501f \u8def\uff0c\u5230\u4e5f\u723d\u8106\uff0c\u53ea\u662f\u6b64\u756a\u4e43\u7687\u4e0a\u505a\u4e3b\uff0c\u8bf7\u6055\u76f4\u8a00\uff0c\u54b1\u4eec\u5b9e\u5728\u4fe1\u4e0d\u8fc7\u7687\u4e0a\uff0c\u8bf7\u95ee\uff0c\u5047\u82e5\u6211\u4eec\u6b64 \u523b\u653e\u7eb3\u5170\u516c\u5b50\u56de\u53bb\uff0c\u7687\u4e0a\u4e0b\u65e8\uff0c\u8981\u4f60\u518d\u7387\u5175\u58eb\u6765\u8ffd\uff0c\u4f60\u662f\u5949\u65e8\u8fd8\u662f\u6297\u547d\uff1f\u201d\u5f20\u627f\u658c\u4e0d\u6562\u7f6e \u7b54\uff0c\u518d\u56de\u62a5\u7687\u5e1d\u3002\u5eb7\u7199\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u5374\u662f\u65e0\u6cd5\u53ef\u65bd\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u4e5f\u7f62\uff0c\u5bb9\u82e5\u5c11\u4e0d\u66f4\u4e8b\uff0c \u7b97\u4ed6\u4eec\u9020\u5316\u3002\u53ea\u662f\u82e5\u4ed6\u4eec\u5c06\u5bb9\u82e5\u5e26\u51fa\u4e8c\u767e\u91cc\u5916\u3002\u4e0d\u653e\u4ed6\u56de\u6765\u53c8\u600e\u4e48\u529e\uff1f\u201d\u5f20\u627f\u658c\u53e9\u5934\u7980\u9053\uff1a \u201c\u90a3\u8001\u513f\u540d\u5524\u5085\u9752\u4e3b\u2026\u2026\u201d\u5eb7\u7199\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u63d2\u53e3\u9053\uff1a\u201c\u54e6\uff0c\u5085\u9752\u4e3b\uff1f\u6211\u77e5\u9053\uff01\u4ed6\u4e0d\u662f\u8fd9 \u4e2a\u6837\u5b50\uff01\u201d\u5f20\u627f\u658c\u9053\uff1a\u201c\u4ed6\u6709\u53d8\u5bb9\u6613\u8c8c\u7684\u672c\u9886\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u5eb7\u7199\u65a5\u9053\uff1a\u201c\u4f60\u541e\u541e\u5410\u5410\u60f3\u8bf4 \u4ec0\u4e48\uff1f\u201d\u5f20\u627f\u658c\u9053\uff1a\u201c\u8fd9\u4eba\u5728\u6c5f\u6e56\u4e0a\u9887\u6709\u540d\u671b\uff0c\u8bf4\u4e00\u662f\u4e00\uff0c\u8bf4\u4e8c\u662f\u4e8c\uff0c\u8c05\u4ed6\u4e0d\u81f4\u5931\u4fe1\uff01\u201d\u5eb7 \u7199\u9762\u8272\u500f\u53d8\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u4fe1\u4e0d\u8fc7\u6211\uff0c\u4f60\u5012\u4fe1\u5f97\u8fc7\u4ed6\u4eec\uff01\u201d\u5f20\u627f\u658c\u4fef\u4f0f\u5728 \u5730\uff0c\u77a7\u4e0d\u89c1\u5eb7\u7199\u9762\u8272\uff0c\u53c8\u7980\u9053\uff1a\u201c\u5974\u624d\u613f\u968f\u516c\u5b50\u524d\u53bb\uff0c\u518d\u62a4\u4ed6\u56de\u6765\u3002\u201d\u5eb7\u7199\u53ea\u597d\u7b54\u5e94\uff0c\u53eb\u4ed6 \u548c\u53e6\u5916\u56db\u540d\u4f8d\u536b\u8d54\u53bb\uff0c\u5085\u9752\u4e3b\u4e5f\u7b54\u5e94\u4e86\u3002\u5eb7\u7199\u7ecf\u6b64\u4e00\u5f79\uff0c\u9887\u4e3a\u4e0d\u5feb\uff0c\u73ed\u5e08\u56de\u671d\u4e4b\u540e\uff0c\u5c31\u501f\u6545 \u5c06\u5f20\u627f\u658c\u6740\u6389\uff0c\u90a3\u662f\u540e\u8bdd\u3002 \u3000\u3000\u5f53\u4e0b\u795e\u7b56\u8425\u5065\u5352\u5c3d\u64a4\uff0c\u5f20\u627f\u658c\u548c\u53e6\u5916\u56db\u4e2a\u536b\u58eb\uff0c\u966a\u7740\u7eb3\u5170\u4f5c\u4e3a\u4eba\u8d28\u3002\u53ea\u662f\u4ed6\u4eec\u88ab\u9694\u5f00\u8ddf \u5728\u540e\u9762\uff0c\u7eb3\u5170\u5219\u6362\u8fc7\u9a8f\u9a6c\uff0c\u548c\u5085\u9752\u4e3b\u5192\u6d63\u83b2\u7b49\u8d70\u5728\u524d\u5934\u3002\u6842\u4ef2\u660e\u50bb\u4e4e\u4e4e\u5730\u5bf9\u7eb3\u5170\u9053\uff1a\u201c\u4ee5\u524d \u6211\u4eec\u505a\u4f60\u7684\u56ed\u4e01\uff0c\u73b0\u5728\u4f60\u4f5c\u6211\u4eec\u7684\u56da\u72af\uff0c\u521a\u597d\u626f\u76f4\uff0c\u54c8\u54c8\uff01\u201d\u5085\u9752\u4e3b\u62c9\u4e86\u6842\u4ef2\u660e\u4e00\u628a\uff0c\u4fcf\u58f0 \u8bf4\u9053\uff1a\u201c\u4f60\u5f53\u7eb3\u5170\u516c\u5b50\u771f\u7684\u88ab\u6211\u4eec\u64d2\u7740\u5417\uff1f\u4ed6\u662f\u60f3\u6551\u6211\u4eec\u624d\u6545\u610f\u6765\u7684\u5440\uff01\u201d\u5192\u6d63\u83b2\u4e5f\u6233\u4e86\u4ed6 \u4e00\u4e0b\uff0c\u55d4\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u51e0\u65f6\u624d\u80fd\u5b66\u5f97\u806a\u660e\uff1f\u201d \u3000\u3000\u6842\u4ef2\u660e\u5446\u4e86\u4e00\u9635\uff0c\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u7d27\u63e1\u7eb3\u5170\u5bb9\u82e5\u7684\u624b\uff0c\u50bb\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u7684\u662f\u4e2a\u597d \u4eba\uff01\u201d\u7eb3\u5170\u89c1\u4ed6\u4e00\u6d3e\u6d6a\u6f2b\u5929\u771f\uff0c\u6697\u6697\u4e3a\u5192\u70f7\u83b2\u6b22\u559c\u3002 \u3000\u3000\u8d70\u4e86\u4e24\u65e5\uff0c\u4e8c\u767e\u91cc\u8def\u7a0b\u5df2\u8fc7\uff0c\u7eb3\u5170\u6084\u7136\u8bf4\u9053\uff1a\u201c\u9001\u541b\u5343\u91cc\uff0c\u7ec8\u987b\u4e00\u522b\u3002\u6211\u4e0e\u5404\u4f4d\u76f8\u77e5\u5728 \u5fc3\uff0c\u613f\u5f7c\u6b64\u73cd\u91cd\u3002\u201d\u5085\u9752\u4e3b\u5429\u5490\u4f17\u4eba\u4e0b\u9a6c\uff0c\u5e2d\u5730\u800c\u5750\uff0c\u53d6\u51fa\u9152\u4e0e\u8089\u812f\uff0c\u66ff\u7eb3\u5170\u9001\u884c\uff0c\u6842\u4ef2\u660e \u76ee\u5192\u6d63\u83b2\u56de\u6765\u540e\uff0c\u4e00\u76f4\u672a\u6709\u673a\u4f1a\u4e3a\u5979\u8868\u6f14\u5251\u672f\uff0c\u8fd9\u65f6\u5174\u8d77\uff0c\u89e3\u4e0b\u817e\u86df\u5b9d\u5251\uff0c\u7b11\u5bf9\u7eb3\u5170\u8bf4\u9053\uff1a \u201c\u6211\u821e\u4e00\u8d9f\u5251\u4e0e\u516c\u5b50\u89e3\u9152\u3002\u201d\u5251\u82b1\u4e00\u633d\uff0c\u767b\u65f6\u5c06\u6b66\u6797\u5931\u4f20\u7684\u8fbe\u6469\u5251\u6cd5\u65bd\u5c55\u8d77\u6765\uff01 \u3000\u3000\u7fa4\u96c4\u4e2d\u9664\u97e9\u5fd7\u90a6\u4e0e\u6613\u5170\u73e0\u5916\uff0c\u5176\u4ed6\u5747\u672a\u89c1\u8fc7\uff0c\u5567\u5567\u79f0\u5947\uff01\u6b63\u821e\u5230\u9163\u7545\u4e4b\u5904\uff0c\u8fce\u9762\u4e09\u9a91\u5feb \u9a6c\uff0c\u95ea\u7535\u5954\u6765\uff0c\u5ffd\u7136\u52d2\u4f4f\uff0c\u5085\u9752\u4e3b\u9887\u611f\u60ca\u5947\uff0c\u9a6c\u4e0a\u4e09\u4eba\uff0c\u4e00\u4e2a\u662f\u4e2d\u5e74\u7f8e\u5987\uff0c\u4e00\u4e2a\u662f\u4e94\u5341\u591a\u5c81 \u7684\u6c49\u5b50\uff0c\u77ed\u987b\u5982\u621f\uff0c\u8fd8\u6709\u4e00\u4e2a\u5374\u662f\u767d\u987b\u98d8\u62c2\u7684\u8001\u9053\u3002\u8fd9\u4e09\u4eba\u76f8\u8c8c\u6e05\u5947\uff0c\u795e\u5149\u5185\u8574\uff0c\u663e\u7136\u90fd\u6709 \u7cbe\u6e5b\u7684\u6b66\u529f\uff0c\u5085\u9752\u4e3b\u6b63\u60f3\u62db\u547c\uff0c\u8fd9\u4e8c\u4eba\u770b\u4e86\u4e00\u9635\uff0c\u5ffd\u7136\u6253\u4e2a\u773c\u8272\uff0c\u8001\u9053\u4e0e\u6c49\u5b50\u53cc\u53cc\u5411\u6842\u4ef2\u660e \u51b2\u6765\uff0c\u90a3\u4e2d\u5e74\u7f8e\u5987\uff0c\u8eab\u624b\u66f4\u662f\u77eb\u6377\uff0c\u500f\u5730\u4e00\u7eb5\uff0c\u4e00\u5251\u5c31\u5411\u7eb3\u5170\u5bb9\u82e5\u63d2\u4e0b\u3002 \u3000\u3000\u5085\u9752\u4e3b\u72e9\u4e0d\u53ca\u9632\uff0c\u957f\u8896\u4e00\u626c\uff0c\u4f7f\u51fa\u6d41\u4e91\u98de\u8896\u7684\u7edd\u62db\uff0c\u5377\u5411\u654c\u4eba\u7693\u8155\uff0c\u53f3\u638c\u547c\u7684\u4e00\u58f0\uff0c\u4ece \u8896\u5e95\u51fb\u51fa\u6765\uff0c\u7f8e\u5987\u4eba\u51cc\u7a7a\u4e00\u4e2a\u7b4b\u6597\uff0c\u7ffb\u5230\u5085\u9752\u4e3b\u80cc\u540e\uff0c\u5237\u7684\u4e00\u5251\uff0c\u4e1d\u6beb\u4e0d\u7f13\uff0c\u7ee7\u7eed\u523a\u6765\u3002\u5085 \u9752\u4e3b\u8fd9\u4e00\u77ac\u95f4\uff0c\u9752\u94a2\u5251\u4e5f\u5df2\u51fa\u624b\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u5c06\u654c\u4eba\u5251\u950b\u7c98\u7740\uff0c\u62c9\u8fc7\u4e00\u8fb9\uff0c\u7f8e\u5987\u8d81\u52bf\u4e00\u9001\uff0c \u5251\u950b\u53c8\u5954\u4e0b\u76d8\u3002\u5085\u9752\u4e3b\u6697\u6697\u8be7\u5f02\uff0c\u5148\u4e0d\u559d\u95ee\uff0c\u56de\u5251\u4e0e\u5979\u76f8\u6597\uff0c\u6597\u4e86\u4e00\u9635\uff0c\u7f8e\u5987\u4eba\u566b\u4e86\u4e00\u58f0\uff0c \u8bf4\u9053\uff1a\u201c\u4f60\u662f\u65e0\u6781\u6d3e\u7684\u9ad8\u624b\uff0c\u4e3a\u4f55\u5374\u81ea\u7518\u4e0b\u6d41\uff01\u201d\u5085\u9752\u8fde\u89e3\u4e09\u5251\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u6b66\u5f53\u6d3e \u9ad8\u624b\uff0c\u4e3a\u4f55\u8bf4\u8bdd\u8fd9\u6837\u65e0\u793c\uff01\u201d\u7f8e\u5987\u4eba\u6012\u9053\uff1a\u201c\u4f60\u6234\u6c49\u4eba\u8863\u51a0\uff0c\u5374\u4fdd\u62a4\u9791\u5b50\uff0c\u7f9e\u4e5f\u4e0d\u7740\uff1f\u201d\u8f6c \u77ac\u4e4b\u95f4\uff0c\u53c8\u523a\u4e86\u51e0\u5251\u3002 \u3000\u3000\u90a3\u8fb9\u53a2\u6842\u4ef2\u660e\u4e5f\u548c\u4e24\u4e2a\u654c\u624b\uff0c\u6740\u5f97\u96be\u5206\u96be\u89e3\u3002\u90a3\u767d\u53d1\u8005\u9053\u529f\u529b\u6df1\u6e5b\uff0c\u6842\u4ef2\u660e\u5251\u5c16\u89e6\u5904\uff0c \u53ea\u89c9\u4e00\u80a1\u5927\u529b\u53cd\u51fb\u8fc7\u6765\uff0c\u90a3\u77ed\u987b\u5982\u621f\u7684\u6c49\u5b50\uff0c\u5251\u6cd5\u5730\u6781\u7cbe\u5999\u3002\u6842\u4ef2\u660e\u4ed7\u7740\u8fbe\u6469\u602a\u62db\u548c\u817e\u86df\u5b9d \u5251\uff0c\u624d\u582a\u582a\u6253\u6210\u5e73\u624b\uff0c\u90a3\u4e24\u4eba\u8f88\u5206\u5f88\u9ad8\u3002\u7ed9\u4e00\u4e2a\u540e\u751f\u5c0f\u5b50\u654c\u4f4f\uff0c\u53c8\u60ca\u53c8\u607c\uff0c\u53cc\u5251\u5de6\u53f3\u5c55\u5f00\uff0c \u7740\u7740\u8fdb\u8feb\uff0c\u6842\u4ef2\u660e\u89c9\u4e24\u4eba\u529f\u529b\uff0c\u7adf\u4f3c\u4e0d\u5728\u9f50\u771f\u541b\u4e4b\u4e0b\uff0c\u6597\u4e86\u4e00\u9635\uff0c\u989d\u5934\u5df2\u662f\u89c1\u6c57\u3002 \u3000\u3000\u6613\u5170\u73e0\u89c1\u654c\u4eba\u4e2a\u4e2a\u6b66\u529f\u9ad8\u5f3a\uff0c\u5085\u9752\u4e3b\u4ee5\u4e00\u654c\u4e00\uff0c\u8fd8\u7565\u5360\u4e0a\u98ce\uff0c\u6842\u4ef2\u660e\u4ee5\u4e00\u654c\u4e8c\uff0c\u7adf\u662f\u9732 \u51fa\u8d25\u8c61\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u77ed\u5251\u4e00\u7ffb\uff0c\u5c31\u5411\u90a3\u8001\u9053\u53bb\u3002\u8001\u9053\u957f\u5251\u4e00\u5377\uff0c\u6ca1\u6709\u5377\u7740\uff0c\u6613\u5170\u73e0\u7684\u5251\u62db\uff0c \u5df2\u5982\u957f\u6c5f\u6d6a\u6d8c\uff0c\u6eda\u6eda\u800c\u4e0a\u3002\u6597\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u90a3\u8001\u9053\u5df2\u88ab\u8feb\u540e\u9000\u3002\u6613\u5170\u73e0\u6b63\u5f85\u8ffd\u51fb\uff0c\u8001\u9053\u6a2a\u5251 \u4e00\u5c01\uff0c\u6f5c\u8fd0\u5185\u529b\u628a\u6613\u5170\u73e0\u9707\u51fa\u4e24\u6b65\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u5973\u5a03\u5b50\u662f\u767d\u53d1\u9b54\u5973\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5085\u9752\u4e3b\u957f\u8896\u4e00\u6325\uff0c\u628a\u90a3\u4e2d\u5e74\u7f8e\u5987\u4e5f\u8feb\u51fa\u4e24\u6b65\uff0c\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u4e09\u4f4d\u6b66\u5f53\u6d3e\u9ad8\u624b\u8bf7\u4e86\uff01\u6562\u95ee \u4f60\u4eec\u4e0e\u5353\u5927\u4fa0\u662f\u5982\u4f55\u79f0\u547c\uff1f\u201d\u767d\u53d1\u8001\u9053\u89c1\u5085\u9752\u4e3b\u5982\u6b64\u529f\u529b\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5353\u5927\u4fa0 \u662f\u6211\u4eec\u5e08\u5144\uff0c\u8f6c\u8bf7\u5c0a\u9a7e\u5927\u540d\u3002\u201d\u5085\u9752\u4e3b\u62a5\u4e86\u59d3\u540d\uff0c\u4e09\u4eba\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u5947\u602a\u540d\u6ee1\u5929\u4e0b\u7684\u4e00\u6d3e\u5b97 \u5e08\u3001\u795e\u533b\u5085\u9752\u4e3b\uff0c\u5374\u4e0e\u6ee1\u6d32\u8d35\u5b98\u540c\u5728\u4e00\u5904\u559d\u9152\u3002\u5085\u9752\u4e3b\u53c8\u6307\u7740\u6613\u5170\u73e0\u9053\uff1a\u201c\u5979\u662f\u6666\u660e\u7985\u5e08\u7684 \u518d\u4f20\u5f1f\u5b50\uff0c\u53c8\u662f\u5973\u82f1\u96c4\u98de\u7ea2\u5dfe\u7684\u5e72\u5973\u513f\uff0c\u6545\u6b64\u4e5f\u5f97\u4e86\u767d\u53d1\u9b54\u5973\u72ec\u95e8\u5251\u6cd5\u7684\u771f\u4f20\u3002\u201d\u8001\u9053\u8d5e \u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5251\u6cd5\u5982\u6b64\u51cc\u5389\uff0c\u6211\u4e0e\u6666\u660e\u7985\u5e08\u7f18\u60ad\u4e00\u9762\uff0c\u4eca\u65e5\u5f97\u89c1\u4ed6\u7684\u5ae1\u4f20\u5251\u6cd5\u4e5f\u7b97\u5927\u5f00\u773c \u754c\u3002\u201d \u3000\u3000\u8fd9\u4e09\u4eba\u662f\u4ece\u6e56\u5317\u6765\u7684\u3002\u90a3\u767d\u53d1\u8001\u9053\u540d\u5524\u7384\u771f\uff0c\u662f\u5353\u4e00\u822a\u5e08\u53d4\u9ec4\u53f6\u9053\u4eba\u7684\u5f1f\u5b50\uff0c\u73b0\u5728\u662f\u6b66 \u5f53\u6d3e\u7684\u638c\u95e8\uff0c\u90a3\u4e2d\u5e74\u7f8e\u5987\u540d\u5524\u4f55\u7eff\u534e\uff0c\u662f\u5353\u4e00\u822a\u53e6\u4e00\u4f4d\u5e08\u53d4\u767d\u77f3\u9053\u4eba\u672a\u51fa\u5bb6\u65f6\u751f\u4e0b\u7684\u5973\u513f\uff0c \u90a3\u4e94\u5341\u591a\u5c81\u7684\u6c49\u5b50\u4e43\u662f\u5979\u7684\u4e08\u592b\uff0c\u5979\u4eca\u5e74\u4e5f\u8fd1\u4e94\u5341\uff0c\u53ea\u56e0\u9a7b\u989c\u6709\u672f\uff0c\u6240\u4ee5\u770b\u6765\u5c1a\u662f\u7f8e\u8273\u52a8 \u4eba\u3002\u5353\u4e00\u822a\u6570\u5341\u5e74\u524d\u66fe\u662f\u6b66\u5f53\u6d3e\u638c\u95e8\uff0c\u5e74\u7eaa\u6bd4\u5e08\u53d4\u4eec\u5c0f\u4e0d\u4e86\u591a\u5c11\uff0c\u5374\u6bd4\u5e08\u5f1f\u5e74\u957f\u8bb8\u591a\u3002\u5353\u4e00 \u822a\u81ea\u629b\u5f03\u638c\u95e8\u4f4d\u5b50\uff0c\u9690\u5c45\u5929\u5c71\u4e4b\u540e\uff0c\u6b66\u5f53\u95e8\u4e0b\u8fd8\u65f6\u65f6\u60f3\u8fce\u4ed6\u56de\u6765\uff0c\u4e8c\u5341\u591a\u5e74\u524d\uff0c\u6768\u4e91\u9aa2\u8fd8\u5728 \u56de\u7586\u7684\u65f6\u5019\uff0c\u4f55\u7eff\u534e\u5c31\u66fe\u72ec\u4e0a\u5929\u5c71\u627e\u5bfb\u8fc7\u5353\u4e00\u822a\uff0c\u800c\u4e14\u66fe\u56e0\u6b64\u52a0\u91cd\u4e86\u767d\u53d1\u9b54\u5973\u7684\u8bef\u4f1a\u3002 \u3000\u3000\u5353\u4e00\u822a\u6b7b\u540e\u8bb8\u4e45\uff0c\u6b66\u5f53\u95e8\u4e0b\u624d\u77e5\u4fe1\u606f\u3002\u540e\u6765\u53c8\u542c\u897f\u85cf\u5587\u561b\u50e7\u4f20\u51fa\uff0c\u8fbe\u6469\u79d8\u7b08\u5df2\u91cd\u73b0\u4e16 \u95f4\u3002\u8fd9\u8fbe\u6469\u79d8\u7b08\u4e43\u662f\u4ed6\u4eec\u6b66\u5f53\u6d3e\u5931\u4f20\u7684\u9547\u5c71\u5b9d\u5178\uff0c\u51e1\u662f\u6b66\u5f53\u95e8\u4e0b\uff0c\u90fd\u5949\u6709\u9057\u547d\u627e\u5bfb\u3002\u56e0\u6b64\u638c \u95e8\u4eba\u7384\u771f\u4eb2\u7387\u5e08\u5f1f\u5e08\u59b9\uff0c\u8fdc\u81f3\u897f\u85cf\uff0c\u51c6\u5907\u8bbf\u5f97\u4e0b\u843d\u540e\uff0c\u518d\u4e0a\u5929\u5c71\u628a\u5353\u4e00\u822a\u7684\u9ab8\u9aa8\u8fce\u56de\u6b66\u5f53\u5c71 \u5b89\u846c\uff0c\u4e0d\u6599\u5230\u897f\u85cf\u4e0d\u4e45\uff0c\u6e05\u519b\u5927\u4e3e\u4fb5\u5165\uff0c\u5e03\u8fbe\u62c9\u5bfa\u4e5f\u88ab\u5141\u9898\u5360\u4f5c\u603b\u90e8\u3002\u4e09\u4eba\u4e0d\u77e5\u8fb9\u5883\u5df2\u88ab\u5c01 \u9501\uff0c\u9893\u7136\u5357\u8fd4\uff0c\u8c01\u77e5\u65e0\u5de7\u4e0d\u5de7\uff0c\u9014\u4e2d\u78b0\u89c1\u6842\u4ef2\u660e\u821e\u5251\uff0c\u4ed6\u4eec\u8ba4\u5f97\u4e94\u516d\u4e2a\u62db\u5f0f\uff0c\u6b63\u662f\u4ed6\u4eec\u6b66\u5f53 \u8fdc\u7956\u9760\u8bb0\u5fc6\u4f20\u4e0b\u6765\u7684\u8fbe\u6469\u5251\u5f0f\uff0c\u53c8\u89c1\u7eb3\u5170\u5bb9\u82e5\u548c\u6e05\u5ba6\u536b\u571f\u4e5f\u5728\u90a3\u513f\uff0c\u56e0\u6b64\u4e0d\u95ee\u7682\u767d\uff0c\u7acb\u523b\u52a8 \u624b\u3002\u53e6\u4e00\u65b9\u9762\uff0c\u7384\u771f\u4e5f\u662f\u60f3\u8bd5\u8bd5\u8fbe\u6469\u5251\u6cd5\u7684\u5a01\u529b\u3002 \u3000\u3000\u4e24\u9762\u628a\u8bdd\u8bf4\u5f00\uff0c\u7384\u771f\u77e5\u9053\u5085\u9752\u4e3b\u4e00\u6d3e\u5b97\u5e08\uff0c\u7d20\u6765\u4e0d\u6253\u8bf3\u8bed\u3002\u4ed6\u867d\u4e0d\u80af\u63ed\u9732\u7eb3\u5170\u8eab\u4efd\uff0c\u4f46 \u8fd9\u6837\u7ef4\u62a4\u7eb3\u5170\uff0c\u5176\u4e2d\u5fc5\u6709\u9053\u7406\uff0c\u4e5f\u4e0d\u4fbf\u518d\u52a0\u8ffd\u7a76\u3002\u7eb3\u5170\u77e5\u9053\u8fd9\u4e09\u4eba\u8981\u4e0a\u5929\u5c71\uff0c\u5fae\u5fae\u7b11\u9053\uff1a \u201c\u8fb9\u5883\u5927\u519b\u4e91\u96c6\uff0c\u9501\u5f97\u6c34\u6cc4\u4e0d\u901a\uff0c\u9053\u957f\u5251\u6cd5\u867d\u9ad8\uff0c\u53ea\u6050\u4e0d\u6613\u95ef\u8fc7\uff01\u201d\u7384\u771f\u55d4\u76ee\u6012\u9053\uff1a\u201c\u6211\u4eec \u4e09\u4eba\u62fc\u8840\u6e85\u9ec4\u6c99\uff0c\u6700\u5c11\u4e5f\u80fd\u6740\u767e\u6570\u5341\u4e2a\u80e1\u72d7\uff01\u201d\u5f20\u627f\u658c\u9762\u8272\u5927\u53d8\uff0c\u7eb3\u5170\u5982\u4e0d\u4ee5\u4e3a\u5fe4\uff0c\u4ecd\u7136\u7b11 \u9053\uff1a\u201c\u4e24\u8d25\u4ff1\u4f24\uff0c\u8fd9\u53c8\u4f55\u5fc5\uff1f\u5982\u9053\u957f\u4e0d\u4ee5\u4e3a\u5acc\uff0c\u5728\u56de\u7a0b\u65f6\uff0c\u6211\u5e26\u8bf8\u4f4d\u8fc7\u53bb\u4fbf\u7b97\u4e86\u3002\u4f60\u4eec\u8ba4\u662f \u6e38\u65b9\u9053\u58eb\uff0c\u4e0d\u4f1a\u6709\u4ec0\u4e48\u4e8b\u7684\u3002\u201d\u5085\u9752\u4e3b\u6084\u6084\u5bf9\u7384\u771f\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u597d\u670b\u53cb\uff0c\u6211\u529d\u9053\u5144\u8fd8\u662f\u9886\u4ed6 \u7684\u60c5\u5427\uff01\u201d\u7384\u771f\u5927\u611f\u60ca\u5f02\uff0c\u4ed6\u89c1\u7eb3\u5170\u4e30\u795e\u4fca\u6717\uff0c\u6c14\u5ea6\u4e0d\u51e1\uff0c\u4e0d\u89c9\u51cf\u4e86\u51e0\u5206\u654c\u610f\uff0c\u5f53\u4e0b\u4e0d\u518d\u8a00 \u8bed\u3002\u5085\u9752\u4e3b\u6b63\u60f3\u7f62\u624b\u9053\u522b\uff0c\u7384\u771f\u5ffd\u7136\u6307\u6842\u4ef2\u660e\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u54e5\uff0c\u6682\u8bf7\u7559\u4e0b\u3002\u201d\u6842\u4ef2\u660e\u6012\u9053\uff1a \u201c\u4ec0\u4e48\uff1f\u51ed\u4ec0\u4e48\u7ed9\u4f60\u7559\u4e0b\uff1f\u201d\u5192\u9662\u83b2\u5ffd\u6084\u6084\u5730\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4f60\u7684\u5e08\u53d4\uff0c\u4f11\u5f97\u65e0 \u793c\uff01\u201d\u6842\u4ef2\u660e\u4e00\u6014\uff0c\u5c34\u5c2c\u5df2\u6781\u3002\u8fd9\u624d\u60f3\u8d77\u81ea\u5df1\u5b66\u4e86\u8fbe\u6469\u79d8\u7b08\uff0c\u5df2\u7b97\u6b66\u5f53\u5f1f\u5b50\uff0c\u53ea\u597d\u8fc7\u6765\uff0c\u5411 \u7384\u771f\u7b49\u5531\u4e86\u4e00\u4e2a\u966a\uff0c\u53eb\u58f0\u201c\u5e08\u53d4\u201d\u3002\u7384\u771f\u8be7\u9053\uff1a\u201c\u4f60\u662f\u5353\u4e00\u822a\u7684\u5173\u95e8\u5f1f\u5b50\u5427\uff1f\u201d\u6842\u4ef2\u660e\u6447\u6447 \u5934\u9053\uff1a\u201c\u4e0d\u662f\uff01\u201d\u8bf4\u4e86\u4e4b\u540e\uff0c\u81ea\u89c9\u4e0d\u5b89\uff0c\u53c8\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e5f\u7b97\u5f97\u662f\uff01\u201d\u7384\u771f\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u662f \u600e\u4e48\u8bf4\u6cd5\uff1f\u201d\u97e9\u5fd7\u90a6\u5728\u65c1\u9053\uff1a\u201c\u4ed6\u662f\u8f9b\u9f99\u5b50\u9057\u547d\u8981\u6211\u4ee3\u5353\u5927\u4fa0\u6536\u5f92\u7684\uff01\u201d\u7384\u771f\u77aa\u4e86\u97e9\u5fd7\u90a6\u4e00 \u773c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u4f60\u662f\u672c\u95e8\u7684\u5f1f\u5b50\u5417\uff1f\u201d\u97e9\u5fd7\u90a6\u4e5f\u6447\u4e86\u6447\u5934\uff0c\u5192\u6d63\u83b2\u6025\u5fd9\u8fc7\u6765\u89e3 \u8bf4\uff0c\u597d\u4e0d\u5bb9\u6613\uff0c\u8bf4\u4e86\u534a\u5929\u624d\u8bf4\u6e05\u695a\uff0c\u7384\u771f\u975e\u5e38\u4e0d\u5feb\u3002\u4ed6\u4eec\u6b66\u5f53\u4e00\u6d3e\uff0c\u7d20\u91cd\u5c0a\u5351\u4e4b\u5206\uff0c\u4e0d\u6599\u4eca \u65e5\u521d\u4f1a\uff0c\u4e24\u4e2a\u5e08\u53d4\u7adf\u81ea\u5408\u6218\u5e08\u4f84\u4e0d\u4e0b\uff0c\u800c\u6842\u4ef2\u660e\u53c8\u6beb\u4e0d\u4ee5\u5c0a\u957f\u4e4b\u793c\u76f8\u89c1\uff0c\u597d\u50cf\u5e76\u4e0d\u60f3\u627f\u8ba4\u4ed6 \u662f\u5e08\u53d4\u4e00\u6837\u3002\u7384\u771f\u5f53\u7740\u5085\u9752\u4e3b\u7b49\u4eba\u4e4b\u9762\uff0c\u4e0d\u4fbf\u53d1\u4f5c\u3002\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u53e6\u6709\u8981\u4e8b\uff1f\u201d\u6842\u4ef2\u660e \u7b11\u9053\uff1a\u201c\u5f53\u7136\u6709\u8981\u4e8b\u5566\uff0c\u4e0d\u7136\u8c01\u8fd8\u5192\u9669\u8fdc\u5230\u897f\u85cf\uff1f\u201d\u7384\u771f\u7ef7\u7740\u8138\u9053\uff1a\u201c\u90a3\u4e48\u7ed9\u4f60\u4e00\u4e2a\u6708\u671f \u9650\uff0c\u4f60\u4e8b\u60c5\u5b8c\u540e\uff0c\u5c31\u5230\u5929\u5c71\u9a86\u9a7c\u5cf0\u6765\uff0c\u5c06\u4f60\u5e08\u7236\u7684\u9ab8\u9aa8\u8fc1\u846c\u3002\u201d\u6842\u4ef2\u660e\u6115\u7136\u4e0d\u77e5\u7f6e\u7b54\uff0c\u7384\u771f \u677f\u7740\u8138\u9053\uff1a\u201c\u6211\u867d\u4e0d\u624d\uff0c\u5fdd\u4efb\u6b66\u5f53\u638c\u95e8\uff0c\u4f60\u662f\u672c\u95e8\u5f1f\u5b50\uff0c\u5e94\u8be5\u61c2\u5f97\u89c4\u77e9\u3002\u201d\u5085\u9752\u4e3b\u6025\u66ff\u6842\u4ef2 \u660e\u89e3\u56f4\u9053\uff1a\u201c\u4ed6\u8fd8\u662f\u521d\u51fa\u9053\u7684\u96cf\u513f\uff0c\u5e74\u8f7b\u7387\u76f4\uff0c\u9053\u5144\u662f\u4ed6\u672c\u95e8\u5c0a\u957f\uff0c\u8c05\u4e5f\u4e0d\u4f1a\u89c1\u602a\u3002\u5230\u671f\u6211 \u53eb\u4ed6\u5230\u5929\u5c71\u53bb\u542c\u9053\u5144\u6559\u8bad\u4fbf\u662f\u4e86\uff01\u201d\u6842\u4ef2\u660e\u8fd9\u624d\u50bb\u4e4e\u4e4e\u5730\u8bf4\u9053\uff1a\u201c\u5e08\u53d4\u4f60\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u73b0\u5728\u6765 \u4e0d\u53ca\uff0c\u5c06\u6765\u4f60\u597d\u597d\u6559\u8bad\u3002\u201d\u7384\u771f\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u4e3e\u624b\u4fbf\u5411\u5085\u9752\u4e3b\u9053\u522b\u3002 \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u4e0e\u5192\u6d63\u83b2\u5206\u522b\uff0c\u5341\u5206\u4e0d\u820d\uff0c\u5f53\u7740\u4f17\u4eba\uff0c\u4e0d\u80fd\u8868\u9732\uff0c\u5f3a\u81ea\u6291\u538b\uff0c\u65e0\u9650\u60b2\u9178\u3002\u56de\u9a6c \u4e4b\u540e\uff0c\u4e00\u8def\u9eef\u7136\uff0c\u5f20\u627f\u658c\u7b49\u4e0d\u6562\u53d1\u95ee\uff0c\u4f55\u7eff\u534e\u867d\u662f\u5973\u6d41\uff0c\u751f\u6027\u8c6a\u723d\uff0c\u559c\u5f00\u73a9\u7b11\uff0c\u5f53\u4e0b\u9017\u7eb3\u5170 \u9053\uff1a\u201c\u5582\uff0c\u4f60\u8fd9\u5c0f\u54e5\u513f\u6101\u4ec0\u4e48\u5440\uff1f\u201d\u7eb3\u5170\u773c\u6cea\u8e72\u7136\u800c\u4e0b\uff0c\u5728\u9a6c\u80cc\u4e0a\u66fc\u58f0\u541f\u8fc8\uff1a \u3000\u3000\u201c\u8eab\u5411\u4e91\u5c71\u90a3\u7554\u884c\uff1f\u5317\u98ce\u5439\u65ad\u9a6c\u5636\u58f0\uff0c\u6df1\u79cb\u8fdc\u585e\u82e5\u4e3a\u60c5\u3002\u4e00\u62b9\u665a\u70df\u8352\u620d\u5792\uff0c\u534a\u7aff\u659c\u65e5\u5173 \u57ce\uff0c\u53e4\u4eca\u5e7d\u6068\u51e0\u65f6\u5e73\uff1f\u201d \u3000\u3000\u201c\u4e07\u91cc\u9634\u5c71\u4e07\u91cc\u6c99\uff0c\u8c01\u5c06\u7eff\u9b13\u6597\u971c\u534e\uff0c\u5e74\u6765\u5f3a\u534a\u5728\u5929\u6daf\u3002\u9b42\u68a6\u4e0d\u79bb\u91d1\u5c48\u620d\uff0c\u753b\u56fe\u8c61\u5c55\u7389 \u9e26\u53c9\uff0c\u751f\u601c\u7626\u51cf\u4e00\u5206\u82b1\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e2d\u4f55\u7eff\u534e\u9887\u89e3\u8bd7\u8bcd\uff0c\u4e00\u542c\u4e4b\u4e0b\uff0c\u987f\u7136\u4e00\u60ca\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u83ab\u975e\u4f60\u5c31\u662f\u6ee1\u6d32\u8bcd\u4eba\u7eb3\u5170 \u5bb9\u82e5\uff1f\u201d\u5f20\u627f\u658c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\u6211\u4eec\u516c\u5b50\u7684\u5927\u540d\uff1f\u201d\u7384\u771f\u6012\u9053\uff1a\u201c\u4f60\u4eec\u80e1\u4eba\u4e2d\uff0c\u53ea\u6709\u6b64 \u4eba\u8fd8\u52c9\u5f3a\u7b97\u662f\u597d\u4eba\uff1f\u4f60\u7b97\u4ec0\u4e48\uff1f\u201d\u624b\u8098\u4e00\u649e\uff0c\u628a\u5f20\u627f\u658c\u649e\u4e0b\u9a6c\u6765\u3002\u536b\u58eb\u4eec\u5927\u6012\uff0c\u7eb3\u5170\u5bb9\u82e5\u4e0e \u4f55\u7eff\u534e\u6025\u5fd9\u4e24\u8fb9\u529d\u6b62\u3002 \u3000\u3000\u7eb3\u5170\u5bb9\u82e5\u4e00\u884c\u4eba\u7b49\uff0c\u56de\u5230\u6e05\u519b\u9a7b\u5730\uff0c\u524d\u54e8\u620d\u5352\uff0c\u6025\u5fd9\u98de\u9a91\u56de\u62a5\uff0c\u7eb3\u5170\u5bb9\u82e5\u5bf9\u7384\u771f\u9053\uff1a \u201c\u4f60\u4eec\u53ef\u4ee5\u53bb\u4e86\uff01\u201d\u7384\u771f\u7b49\u4e09\u4eba\u4e0a\u9a6c\u53bb\u540e\uff0c\u518d\u8fc7\u7247\u5230\uff0c\u5927\u8425\u4e2d\u5df2\u6d3e\u51fa\u795e\u7b56\u8425\u5065\u5352\uff0c\u8fce\u7eb3\u5170\u56de \u8425\uff0c\u4f34\u7eb3\u5170\u56de\u6765\u7684\u56db\u4e2a\u536b\u58eb\uff0c\u6253\u4e2a\u773c\u8272\uff0c\u53e6\u7ea6\u4e86\u4e94\u516d\u4e2a\u540c\u4f34\uff0c\u8de8\u4e0a\u9a8f\u9a6c\uff0c\u5411\u5357\u9a70\u53bb\u3002\u5f20\u627f\u658c \u77e5\u9053\u4ed6\u4eec\u6c14\u90a3\u8001\u9053\u4e0d\u8fc7\uff0c\u6b64\u53bb\u5fc5\u7136\u662f\u60f3\u7559\u96be\u4ed6\u4eec\uff0c\u4e5f\u4e0d\u4f5c\u58f0\uff0c\u8fd8\u66ff\u4ed6\u4eec\u5728\u7eb3\u5170\u4e4b\u524d\u906e\u63a9\u3002 \u3000\u3000\u7384\u771f\u7b49\u9a70\u51fa\u5341\u4f59\u4e8c\u5341\u91cc\uff0c\u5df2\u51fa\u8fb9\u5883\u7684\u5c01\u9501\u7ebf\u5916\uff0c\u5ffd\u542c\u80cc\u540e\u94c1\u8e44\u5f97\u5f97\uff0c\u9a6c\u94c3\u4e86\u5f53\uff0c\u56de\u5934\u4e00 \u671b\uff0c\u53ea\u89c1\u5341\u6570\u9a91\u5065\u9a6c\uff0c\u5982\u98de\u8ffd\u5230\u3002\u7384\u771f\u51b7\u7b11\u4e00\u58f0\uff0c\u62d4\u5251\u5728\u624b\uff0c\u4e3a\u9996\u7684\u536b\u58eb\u559d\u9053\uff1a\u201c\u6076\u9053\u7559 \u4e0b\uff01\u201d\u7384\u771f\u53cd\u624b\u4e00\u5251\uff0c\u53c8\u75be\u53c8\u51c6\u3002\u767b\u65f6\u628a\u90a3\u540d\u536b\u58eb\u80f3\u818a\u523a\u4f24\u3002\u4f17\u536b\u58eb\u4e00\u6d8c\u800c\u4e0a\uff0c\u628a\u4e09\u4eba\u56f4\u4e86 \u8d77\u6765\uff0c\u8fd9\u4e9b\u536b\u58eb\uff0c\u867d\u7136\u4e5f\u662f\u5927\u5185\u9ad8\u624b\uff0c\u5374\u5982\u4f55\u654c\u5f97\u4ed6\u4eec\uff1f\u6218\u4e86\u7247\u523b\uff0c\u53c8\u6709\u4e09\u4eba\u4e2d\u5251\u843d\u9a6c\uff0c\u4f59 \u4eba\u843d\u8352\u9003\u8d70\u3002\u7384\u771f\u957f\u5578\u4e00\u58f0\uff0c\u5f97\u610f\u4e4b\u6781\uff0c\u634b\u987b\u8bf4\u9053\uff1a\u201c\u5c31\u8ba9\u4ed6\u4eec\u8d70\u5427\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5ffd\u7136\u90a3 \u4e9b\u536b\u58eb\uff0c\u81ea\u9a6c\u4e0a\u5012\u649e\u4e0b\u6765\u3002\u7384\u771f\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u8d1d\u5c71\u5c97\u4e71\u77f3\u4e1b\u4e2d\uff0c\u8d70\u51fa\u4e00\u4e2a\u7ea2\u8863\u5c11\u5973\u548c\u4e00\u4e2a\u767d \u9762\u4e66\u751f\u3002\u90a3\u5c11\u5973\u683c\u683c\u5730\u7b11\u4e2a\u4e0d\u505c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u9053\u7237\uff0c\u5251\u6cd5\u7cbe\u5f69\u6781\u5566\uff0c\u53ef\u60dc\u8fd8\u4e0d\u591f\u72e0\uff01\u201d\u7384 \u771f\u773c\u73e0\u4e00\u7ffb\uff0c\u51b7\u51b7\u95ee\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u59d1\u5a18\u4e00\u5b9a\u662f\u4e2a\u5927\u884c\u5bb6\u4e86\uff1f\u201d\u7ea2\u8863\u5c11\u5973\u4e00\u7b11\u4e0d\u7b54\uff0c\u5374\u6307 \u7740\u90a3\u51e0\u540d\u536b\u58eb\u9053\uff1a\u201c\u6211\u66ff\u4f60\u4eec\u628a\u654c\u4eba\u5168\u6b7c\u706d\u4e86\uff0c\u4f60\u4eec\u8c22\u4e5f\u4e0d\u8c22\u4e00\u58f0\uff0c\u5012\u8003\u8f83\u8d77\u6211\u7684\u5251\u6cd5\u6765 \u4e86\uff01\u201d\u7384\u771f\u662f\u4e00\u6d3e\u638c\u95e8\uff0c\u5982\u4f55\u5403\u5f97\u8fd9\u53e3\u95f7\u6c14\uff0c\u5229\u5251\u4e00\u63d0\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7684\u5251\u6cd5\u4e0d\u884c\uff0c\u4ee5 \u81f4\u654c\u4eba\u6f0f\u7f51\uff0c\u60ed\u6127\u5f97\u5f88\u3002\u65e2\u627f\u4f60\u59d1\u5a18\u6307\u70b9\uff0c\u6211\u8001\u9053\u4e0d\u77e5\u597d\u574f\uff0c\u8fd8\u60f3\u8bf7\u6559\u51e0\u62db\u3002\u201d\u90a3\u767d\u9762\u4e66\u751f \u77a7\u4e86\u7ea2\u8863\u5c11\u5973\u4e00\u773c\uff0c\u4f3c\u9887\u7591\u60d1\uff0c\u7ea2\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u7ba1\uff0c\u770b\u770b\u70ed\u95f9\u5427\u3002\u201d\u957f\u5251\u4e00\u6307\uff0c\u4e00 \u7b11\u9053\uff1a\u201c\u8bf7\u6055\u5c0f\u8f88\u65e0\u793c\u3002\u201d\u7384\u771f\u9053\uff1a\u201c\u53d1\u62db\u5427\uff01\u201d\u7384\u771f\u5fc3\u4e2d\uff0c\u867d\u56e0\u7ea2\u8863\u5c11\u5973\u9002\u624d\u6f5c\u7528\u6697\u5668\uff0c \u4e3e\u624b\u4e4b\u95f4\uff0c\u4fbf\u5c06\u4e94\u540d\u536b\u58eb\u4e00\u9053\u51fb\u843d\uff0c\u6709\u6240\u5fc3\u60ca\uff0c\u4f46\u4ed6\u81ea\u6043\u51e0\u5341\u5e74\u529f\u529b\uff0c\u53c8\u662f\u6b66\u6797\u6b63\u5b97\u7684\u638c \u95e8\uff0c\u8fd8\u771f\u4e0d\u628a\u7ea2\u8863\u5c11\u5973\u653e\u5728\u5fc3\u4e0a\u3002\u4ed6\u662f\u7acb\u5fc3\u8bd5\u62db\uff0c\u60f3\u60e9\u6212\u60e9\u6212\u8fd9\u72c2\u5984\u7684\u201c\u5c0f\u8f88\u201d\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\uff0c\u8fd9\u7ea2\u8863\u5c11\u5973\u4e5f\u662f\u7acb\u5fc3\u8bd5\u62db\u7684\u3002\u539f\u6765\u8fd9\u4e00\u7537\u4e00\u5973\uff0c\u4e43\u662f\u674e\u601d\u6c38\u548c\u6b66\u743c\u7476\u3002\u5085\u9752 \u4e3b\u7b49\u4ece\u5580\u5c14\u6c81\u8349\u539f\u52a8\u8eab\u540e\uff0c\u98de\u7ea2\u5dfe\u5728\u5410\u9c81\u756a\u5f97\u77e5\u6d88\u606f\uff0c\u751a\u4e3a\u62c5\u5fc3\u3002\u6b66\u743c\u7476\u6700\u559c\u70ed\u95f9\uff0c\u4fbf\u6c42\u51c6 \u5e08\u59d0\uff0c\u5e26\u674e\u601d\u6c38\u4e5f\u8d74\u56de\u7586\u3002\u674e\u601d\u6c38\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u53c8\u662f\u4e00\u7b49\u5c06\u624d\uff0c\u914d\u4e0a\u6b66\u743c\u7476\u719f\u6089\u585e\u5916 \u7684\u60c5\u5f62\uff0c\u4e24\u4eba\u4e00\u8def\u884c\u6765\u4e86\u5e73\u5b89\u65e0\u4e8b\u3002\u674e\u601d\u6c38\u968f\u65f6\u968f\u5730\uff0c\u89c2\u5bdf\u5c71\u5ddd\u5f62\u52bf\u548c\u6e05\u5175\u7684\u5e03\u7f6e\uff0c\u5fc3\u4e2d\u6697 \u6697\u753b\u4e0b\u5c06\u6765\u7528\u5175\u7684\u84dd\u56fe\u3002\u4e24\u4eba\u5728\u6f2b\u957f\u7684\u65c5\u7a0b\u4e2d\uff0c\u60c5\u611f\u4e5f\u65e5\u76ca\u589e\u8fdb\u3002 \u3000\u3000\u8fd9\u65e5\u5c06\u8fd1\u8fb9\u5883\uff0c\u674e\u601d\u6c38\u89c1\u8fdc\u5904\u708a\u70df\u5927\u8d77\uff0c\u6218\u9a6c\u5636\u9e23\uff0c\u609a\u7136\u60ca\u9053\uff1a\u201c\u8fb9\u5883\u5fc5\u6709\u5927\u519b\u5c01\u9501\uff0c \u5982\u4f55\u662f\u597d\uff1f\u201d\u6b66\u743c\u7476\u9053\uff1a\u201c\u8349\u539f\u5e7f\u9614\uff0c\u8fb9\u5883\u672a\u5fc5\u5904\u5904\u90fd\u6709\u5927\u519b\u9632\u5b88\u3002\u201d\u674e\u601d\u6c38\u51b5\u541f\u7247\u523b\uff0c\u548c \u6b66\u743c\u7476\u540c\u4e0a\u5c71\u5761\u773a\u671b\uff0c\u5ffd\u89c1\u5341\u4f59\u6e05\u519b\u6b66\u58eb\uff0c\u8ffd\u8d76\u4e00\u4e2a\u8001\u9053\uff0c\u674e\u601d\u6c38\u5947\u9053\uff1a\u201c\u8fd9\u8001\u9053\u4e0d\u77e5\u662f\u4f55 \u7b49\u4eba\u7269\uff0c\u7adf\u80fd\u901a\u8fc7\u8fb9\u5883\uff1f\u201d\u518d\u770b\u4e0b\u53bb\uff0c\u53c8\u89c1\u4e00\u4e2a\u4e2d\u5e74\u7f8e\u5987\u4e0e\u4e00\u4e2a\u7c97\u8c6a\u6c49\u5b50\u548c\u8001\u9053\u5e84\u4e00\u8d77\u540c\u6297 \u654c\u4eba\uff0c\u66f4\u611f\u60ca\u5f02\u3002 \u3000\u3000\u770b\u4e86\u7247\u523b\uff0c\u6b66\u743c\u7476\u6084\u6084\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u8fd9\u4e09\u4eba\u7684\u6765\u5386\u3002\u201d\u674e\u601d\u6c38\u9053\uff1a\u201c\u8fd9\u4e09\u4eba\u90fd\u662f\u4e00\u7b49 \u7684\u6b66\u6797\u9ad8\u624b\uff01\u201d\u6b66\u743c\u7476\u7b11\u9053\uff1a\u201c\u8fd8\u662f\u6b66\u5f53\u7684\u524d\u8f88\u54e9\u3002\u5f85\u6211\u52a9\u4ed6\u4eec\u4e00\u81c2\uff0c\u7136\u540e\u518d\u800d\u800d\u4ed6\u4eec\u3002\u201d \u674e\u601d\u6c38\u9053\uff1d\u4f60\u4e3a\u4ec0\u4e48\u603b\u662f\u8fd9\u6837\u987d\u76ae\uff1f\u201d\u6b66\u743c\u7476\u7b11\u800c\u4e0d\u7b54\u3002 \u3000\u3000\u8fd9\u6b21\u6b66\u743c\u7476\u5012\u4e0d\u662f\u6545\u610f\u6dd8\u6c14\uff0c\u539f\u6765\u6b66\u743c\u7476\u5728\u767d\u53d1\u9b54\u5973\u95e8\u4e0b\u4e09\u5e74\uff0c\u77e5\u9053\u5e08\u7236\u548c\u6b66\u5f53\u6d3e\u7684\u4e00 \u6bb5\u6069\u6028\u3002\u6b66\u743c\u7476\u751a\u66ff\u5e08\u7236\u4e0d\u503c\uff0c\u5fc3\u60f3\u5e08\u7236\u548c\u5353\u5927\u4fa0\u672c\u6765\u662f\u5927\u597d\u59fb\u7f18\uff0c\u504f\u504f\u4ed6\u7684\u4ec0\u4e48\u672c\u95e8\u5c0a\u957f \u8981\u51fa\u6765\u6a2a\u52a0\u5e72\u6d89\uff0c\u4ee5\u81f3\u5e08\u7236\u51e0\u5341\u5e74\u90c1\u90c1\u7a7a\u5c71\uff0c\u95f7\u6c14\u96be\u4f38\u3002\u6240\u4ee5\u522b\u4eba\u90fd\u89c9\u5f97\u767d\u53d1\u9b54\u5973\u6027\u60c5\u602a \u50fb\uff0c\u53ea\u6709\u6b66\u743c\u7476\u548c\u5979\u7684\u5e08\u59d0\u98de\u7ea2\u5dfe\u61c2\u5f97\u5e08\u7236\u7684\u771f\u60c5\u3002 \u3000\u3000\u6b66\u743c\u7476\u548c\u674e\u601d\u6c38\u534a\u5c71\u89c2\u6218\uff0c\u674e\u601d\u6c38\u9053\uff1a\u201c\u6b66\u5f53\u5251\u6cd5\u679c\u7136\u5389\u5bb3\u3002\u201d\u6b66\u743c\u7476\u7b11\u9053\uff1a\u201c\u8d4b\u5f97\u7a33 \u6377\u4e8c\u5b57\uff0c\u72e0\u8fa3\u8fd8\u5dee\u5f97\u8fdc\u54e9\uff01\u201d\u679c\u7136\u6218\u5230\u540e\u6765\uff0c\u6709\u4e94\u4e2a\u536b\u58eb\u5c45\u7136\u6f0f\u7f51\uff0c\u6b66\u743c\u7476\u4e00\u7b11\uff0c\u4e00\u628a\u4e5d\u661f \u5b9a\u5f62\u98de\u9488\uff0c\u5c06\u4e94\u4e2a\u536b\u58eb\u90fd\u6253\u4e0b\u9a6c\u6765\u3002 \u3000\u3000\u518d\u8bf4\u7384\u771f\u7ed9\u6b66\u743c\u7476\u4e00\u6fc0\uff0c\u8bf7\u5979\u53d1\u62db\uff0c\u6b66\u743c\u7476\u9053\u58f0\uff1a\u201c\u6709\u50ed\uff01\u201d\u5de6\u80a9\u4e00\u6643\uff0c\u7384\u771f\u53ea\u9053\u5979\u59ff \u653b\u81ea\u5df1\u53f3\u80c1\uff0c\u4e0a\u534a\u8eab\u5f80\u53f3\u5fae\u504f\uff0c\u4e00\u504f\u5251\u950b\uff0c\u6325\u5229\u5251\u5f80\u5916\u4e00\u5c01\uff0c\u54ea\u6599\u6b66\u743c\u7476\u4e43\u662f\u8bf1\u62db\uff0c\u5de6\u80a9\u4e00 \u6643\uff0c\u5374\u4e0d\u53d1\u62db\uff0c\u5f85\u7384\u771f\u5251\u5230\uff0c\u624d\u731b\u559d\u4e00\u58f0\uff1a\u201c\u53bb\uff01\u201d\u5de6\u624b\u5251\u8bc0\u659c\u5f80\u4e0a\u6307\uff0c\u53f3\u624b\u5251\u950b\u201c\u767d\u9e64\u4eae \u7fc5\u201d\u731b\u7136\u4e00\u64a9\uff0c\u5237\u5730\u622a\u65a9\u7384\u771f\u8109\u95e8\uff0c\u767d\u53d1\u9b54\u5973\u7684\u5251\u6cd5\u6700\u4e3a\u72e0\u8fa3\uff0c\u8fd9\u4e00\u62db\u5c24\u5176\u4f7f\u5f97\u60ca\u9669\u7edd\u4f26\uff0c \u53ea\u4e89\u77ac\u606f\u5148\u540e\uff0c\u7384\u771f\u4e07\u6599\u4e0d\u5230\u8fd9\u5973\u5a03\u5b50\u5251\u62db\u5982\u6b64\u8001\u8fa3\uff0c\u5e78\u4ed6\u4eba\u8001\u62db\u719f\uff0c\u5168\u8eab\u6512\u529b\uff0c\u5927\u5f2f\u8170\uff0c \u659c\u63d2\u67f3\uff0c\u501f\u52bf\u4e00\u8f6c\uff0c\u624d\u582a\u582a\u907f\u8fc7\u6b66\u743c\u7476\u7684\u5251\u950b\u3002\u6b66\u743c\u7476\u9752\u94a2\u5251\u95ea\u95ea\u542b\u5149\uff0c\u8ddf\u8e2a\u6025\u88ad\uff0c\u7384\u771f\u811a \u8e0f\u516b\u5366\u65b9\u4f4d\uff0c\u4e00\u53e3\u5251\u7d27\u7d27\u5c01\u95ed\u95e8\u6237\uff0c\u6b66\u8c05\u7476\u5251\u5c16\u6240\u89e6\u4e4b\u5904\uff0c\u90fd\u6709\u52b2\u98ce\u53cd\u6251\u8fc7\u6765\u3002\u6b66\u743c\u7476\u77e5\u4ed6 \u529f\u529b\u6781\u9ad8\uff0c\u5df2\u7528\u4e0a\u4e58\u5251\u6cd5\u62a4\u7740\u5168\u8eab\uff0c\u5fc3\u60f3\uff1a\u201c\u53ef\u4e0d\u80fd\u8ba9\u4ed6\u5598\u606f\uff01\u201d\u5237\uff01\u5237\uff01\u5237\uff01\u8fde\u73af\u8fdb\u5251\uff0c \u884c\u7684\u5ffd\u540e\uff0c\u653b\u5de6\u5ffd\u77f3\u3002\u5168\u662f\u8fdb\u624b\u7684\u62db\u6570\uff0c\u7384\u771f\u53ea\u8981\u7a0d\u9732\u7a7a\u9699\uff0c\u7acb\u523b\u4fbf\u6709\u8840\u6e85\u9ec4\u6c99\u4e4b\u9669\uff01\u4f55\u7eff \u534e\u592b\u5987\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u6b66\u743c\u7476\u5374\u4e5f\u6697\u6697\u53eb\u82e6\u3002\u539f\u6765\u8bba\u5251\u6cd5\u662f\u5979\u7684\u8f9b\u8fa3\uff0c\u8bba\u529f\u529b\u5374\u662f\u7384\u771f\u6df1 \u6e5b\u3002\u82e5\u7136\u4e45\u6218\u4e0d\u4e0b\uff0c\u6700\u540e\u53ea\u6015\u4ecd\u8981\u8d25\u7ed9\u8fd9\u4e2a\u8001\u9053\uff01 \u3000\u3000\u4e24\u4eba\u4e00\u5b88\u4e00\u653b\uff0c\u9669\u62db\u8fed\u89c1\uff0c\u6b66\u743c\u7476\u4e00\u62db\u5feb\u4f3c\u4e00\u62db\uff0c\u4e00\u5f0f\u7d27\u4f3c\u4e00\u5f0f\uff0c\u65c1\u89c2\u7684\u4f55\u7eff\u534e\u592b\u5987\u867d \u660e\u77e5\u7384\u771f\u4e0d\u4f1a\u843d\u8d25\uff0c\u4e5f\u7981\u4e0d\u4f4f\u6697\u6697\u60ca\u5fc3\uff01\u8fd9\u65f6\u7384\u771f\u5df2\u770b\u51fa\u6b66\u743c\u7476\u7684\u6765\u5386\uff0c\u751a\u4e3a\u6c14\u607c\uff0c\u5fc3\u60f3\uff1a \u201c\u54fc\uff0c\u539f\u6765\u53c8\u662f\u767d\u53d1\u9b54\u5973\u7684\u95e8\u4eba\uff0c\u602a\u4e0d\u5f97\u8981\u6545\u610f\u8f83\u8003\u8001\u592b\u3002\u201d\u4e3a\u4e86\u672c\u95e8\u58f0\u8a89\uff0c\u6068\u4e0d\u5f97\u4e00\u4e3e\u628a \u5979\u51fb\u8d25\u3002\u53ef\u662f\u767d\u53d1\u9b54\u5973\u7684\u72ec\u95e8\u5251\u6cd5\uff0c\u59d4\u5b9e\u72e0\u8fa3\u975e\u5e38\u3002\u7384\u771f\u54ea\u6562\u8f7b\u4e3e\u5984\u52a8\u3002\u518d\u6597\u4e86\u4e94\u516d\u5341\u56de \u5408\uff0c\u4ecd\u7136\u5360\u4e0d\u4e86\u4fbf\u5b9c\u3002\u7384\u771f\u867d\u7136\u81ea\u6043\u81ea\u5df1\u529f\u592b\u5728\u5979\u4e4b\u4e0a\uff0c\u4e45\u6218\u4e0b\u53bb\uff0c\u5fc5\u5b9a\u53ef\u5360\u4e0a\u98ce\uff0c\u53ef\u662f\u5bf9 \u65b9\u80dc\u5728\u5e74\u8f7b\uff0c\u9510\u6c14\u6b63\u76db\uff0c\u8981\u51b3\u80dc\u8d1f\uff0c\u4e0d\u77e5\u8981\u6218\u5230\u4f55\u65f6\uff1f\u800c\u6ee1\u519b\u5c31\u5728\u5341\u4f59\u91cc\u5916\uff0c\u4e07\u4e00\u8ffd\u6765\uff0c\u5c82 \u4e0d\u662f\u4e24\u8d25\u4ff1\u4f24\uff1f\u56e0\u6b64\u5fc3\u91cc\u4e5f\u6697\u6697\u53eb\u82e6\uff01 \u3000\u3000\u4f55\u7eff\u534e\u592b\u5987\u4e5f\u662f\u5982\u6b64\u60f3\u6cd5\uff0c\u4f46\u7384\u771f\u662f\u638c\u95e8\u5e08\u5144\uff0c\u82e5\u7136\u5728\u4ed6\u5c1a\u5904\u4e0b\u98ce\u4e4b\u65f6\uff0c\u5373\u529d\u4e24\u65b9\u505c \u6218\uff0c\u4ed6\u9762\u4e0a\u5fc5\u6302\u4e0d\u4e0b\uff0c\u800c\u4e14\u4e5f\u4e22\u4e86\u6b66\u5f53\u6d3e\u7684\u9762\u5b50\u3002\u6b63\u8fdf\u7591\u95ee\uff0c\u4e24\u4eba\u6597\u5f97\u5341\u5206\u6fc0\u70c8\uff0c\u6b66\u743c\u7476\u5251 \u623e\u4e00\u9886\uff0c\u4e00\u4e2a\u201c\u9f99\u5f62\u4e00\u5f0f\u201d\uff0c\u8eab\u968f\u5251\u8d70\uff0c\u5251\u968f\u81c2\u626c\uff0c\u201c\u9e37\u79bd\u6251\u5154\u201d\uff0c\u5237\u5730\u4e00\u5251\u5bf9\u654c\u4eba\u8170\u8179\u624e \u53bb\u3002\u7384\u771f\u4ed7\u51e0\u5341\u5e74\u529f\u592b\uff0c\u7a81\u4f7f\u9669\u62db\uff0c\u4e00\u63a3\u5251\u67c4\uff0c\u6a2a\u8eab\u8f6c\u6b65\uff0c\u4f3c\u5c06\u95ea\u8eb2\uff0c\u5374\u7a81\u7136\u4e0d\u540e\u9000\u800c\u53cd\u8fdb \u653b\uff0c\u7adf\u63c9\u8eab\u732e\u5251\uff0c\u5377\u5730\u4e00\u626b\uff0c \u3000\u3000\xb7\u559d\u9053\uff1a\u201c\u770b\u5251\uff01\u201d\u90a3\u6599\u6b66\u743c\u7476\u5251\u672f\u53c8\u5feb\u53c8\u72e0\uff0c\u7384\u771f\u672a\u53ca\u8fdb\u62db\uff0c\u6b66\u743c\u7476\u7684\u5251\u5df2\u631f\u4e00\u697c\u5bd2 \u5149\uff0c\u731b\u7136\u523a\u5230\uff0c\u7384\u771f\u559d\u5979\u201c\u770b\u5251\u201d\uff0c\u5979\u4e5f\u559d\u7384\u771f\u201c\u6492\u5251\u201d\uff0c\u5c31\u5728\u6b64\u9645\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u91d1\u94c1\u5300\u9e23 \u4e4b\u58f0\uff0c\u4e24\u4eba\u7684\u5251\u90fd\u8131\u624b\u98de\u51fa\uff01 \u3000\u3000\u539f\u6765\u7384\u771f\u8fd9\u5251\uff0c\u7528\u8db3\u5341\u6210\u529b\u91cf\uff0c\u4f46\u6b66\u743c\u7476\u5251\u62db\u5148\u5230\uff0c\u7384\u771f\u82e5\u4e0d\u6492\u5251\uff0c\u624b\u8155\u5fc5\u5b9a\u65a9\u65ad\uff0c\u7384 \u771f\u6c14\u7ea2\u4e86\u773c\uff0c\u628a\u5fc3\u4e00\u6a2a\uff0c\u957f\u5251\u4e00\u9707\uff0c\u731b\u5730\u63b7\u53bb\uff0c\u5176\u75be\u5982\u77e2\uff0c\u6b66\u743c\u7476\u7528\u5251\u4e00\u683c\uff0c\u7adf\u6321\u4e0d\u4f4f\u90a3\u80a1 \u52b2\u529b\uff0c\u624b\u4e2d\u7684\u9752\u94a2\u5251\u4e5f\u7ed9\u9707\u98de\uff0c\u4e24\u53e3\u5251\u5728\u534a\u7a7a\u4e2d\u8ff8\u51fa\u4e00\u6e9c\u706b\u82b1\uff0c\u9668\u77f3\u822c\u5730\u5411\u8349\u539f\u843d\u4e0b\uff01 \u3000\u3000\u8fd9\u4e24\u62db\u5feb\u5982\u95ea\u7535\uff0c\u4f55\u7eff\u534e\u674e\u601d\u6c38\u540c\u65f6\u7eb5\u51fa\uff0c\u4f55\u7eff\u534e\u62c9\u7740\u7384\u771f\uff0c\u5927\u53eb\u201c\u5e08\u5144\u4f4f\u624b\uff01\u201d\u674e\u601d \u6c38\u4e5f\u62c9\u7740\u4e86\u6b66\u743c\u7476\u5927\u53eb\u201c\u743c\u59b9\u4f4f\u624b\uff01\u201d\u7384\u771f\u6c14\u5598\u5598\u5730\u77aa\u7740\u53cc\u773c\uff0c\u4e0d\u53d1\u4e00\u8a00\u3002\u4f55\u7eff\u534e\u674e\u601d\u6c38\u540c \u65f6\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u529f\u529b\u6089\u654c\uff0c\u4e0d\u5fc5\u6bd4\u4e86\uff01\u201d\u7384\u771f\u62fe\u8d77\u957f\u5251\uff0c\u674e\u601d\u6c38\u6b63\u60f3\u529d\u6b66\u743c\u7476\u4e0a\u524d\u966a\u7f6a\uff0c\u7384 \u771f\u5df2\u8de8\u4e0a\u9a6c\u80cc\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5dfe\u5e3c\u82f1\u96c4\uff0c\u8001\u673d\u4f69\u670d\uff01\u4e00\u6708\u4e4b\u540e\uff0c\u5728\u5929\u5c71\u9a86\u9a7c\u5cf0\u76f8\u89c1\u5982\u4f55\uff1f\u201d \u4e0d\u5f85\u6b66\u743c\u7476\u56de\u7b54\uff0c\u4e24\u817f\u4e00\u631f\uff0c\u9a8f\u9a6c\u5636\u98ce\uff0c\u7edd\u5c18\u800c\u53bb\uff01\u4f55\u7eff\u534e\u592b\u5987\u9053\u58f0\u201c\u5f97\u7f6a\u201d\u4e5f\u8ddf\u7740\u5e08\u5144\u53bb \u4e86\u3002 \u3000\u3000\u6b66\u743c\u7476\u62ac\u8d77\u5229\u5251\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u725b\u9f3b\u5b50\u813e\u6c14\u771f\u5927\uff01\u201d\u5bf9\u674e\u601d\u6c38\u8bf4\u660e\u539f\u59d4\uff0c\u674e\u601d\u6c38\u4e5f\u7b11\u9053\uff1a \u201c\u4ed6\u4eec\u6b66\u5f53\u6d3e\u4eba\u867d\u5f97\u7f6a\u4f60\u7684\u5e08\u7236\uff0c\u4f46\u4f60\u4e5f\u592a\u6dd8\u6c14\u4e86\uff01\u201d\u773c\u775b\u4e00\u6e9c\uff0c\u770b\u5230\u5730\u4e0a\u7684\u536b\u58eb\u5c38\u4f53\uff0c\u53c8 \u629a\u638c\u7b11\u9053\uff1a\u201c\u6211\u60f3\u5230\u8fb9\u5883\u8131\u8eab\u4e4b\u8ba1\u4e86\u3002\u201d\u5265\u4e0b\u4e24\u4e2a\u536b\u58eb\u7684\u76d4\u7532\u961c\u8863\uff0c\u53eb\u6b66\u743c\u7476\u626e\u6210\u7537\u5b50\uff0c\u5411 \u8fb9\u5883\u9a70\u53bb\u3002\u8fd9\u4e00\u53bb\u4e5f\uff0c\u6709\u5206\u6559\uff1a \u3000\u3000\u82f1\u96c4\u5927\u96c6\u4f1a\uff0c\u8840\u6218\u5587\u561b\u5bab\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5eff\u516b\u7ae0\u3000\u5fc3\u613f\u96be\u507f\u3000\u4e00\u7eb8\u65ad\u80a0\u6101\u7edd\u585e\u3000\u60c5\u6000\u4f9d\u65e7\u3000\u5341\u5e74\u5e7d\u68a6\u7981\u8ff7\u5bab \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u4e8c\u5341\u516b\u56de \u5fc3\u613f\u96be\u507f\u3000\u4e00\u7eb8\u65ad\u80a0\u6101\u7edd\u585e \u60c5\u6000\u4f9d\u65e7\u3000\u5341\u5e74\u5e7d\u68a6\u7981\u8ff7\u5bab \u3000\u3000\u674e\u601d\u6c38\u548c\u6b66\u743c\u7476\u4e54\u88c5\u6e05\u519b\u6b66\u58eb\uff0c\u679c\u7136\u9a97\u8fc7\u4e86\u5c01\u9501\u8fb9\u5883\u7684\u524d\u54e8\u620d\u5352\uff0c\u9a6c\u4e0d\u505c\u8e44\uff0c\u8d76\u5230\u62c9 \u8428\u3002\u4e24\u4eba\u5546\u91cf\u600e\u6837\u53bb\u627e\u5085\u9752\u4e3b\u7b49\u4eba\uff0c\u6b66\u743c\u7476\u9053\uff1a\u201c\u6211\u7684\u7238\u7238\u548c\u897f\u5317\u5929\u5730\u4f1a\u6e0a\u6e90\u5f88\u6df1\uff0c\u6211\u4e5f\u77e5 \u9053\u4ed6\u4eec\u4f1a\u4e2d\u7684\u5207\u53e3\u548c\u6697\u53f7\u3002\u56db\u5e74\u524d\u6211\u4eec\u7236\u5973\u548c\u5927\u5730\u4f1a\u7684\u5927\u5934\u76ee\u6768\u4e00\u7ef4\u534e\u7d2b\u5c71\u7b49\u6765\u5230\u56de\u7586\uff0c\u6709 \u4e00\u90e8\u4efd\u5929\u5730\u4f1a\u7684\u4f1a\u53cb\u6563\u5165\u897f\u85cf\uff0c\u6599\u60f3\u62c9\u8428\u57ce\u4e2d\uff0c\u4e5f\u6709\u4ed6\u4eec\u7684\u5206\u8235\u3002\u62c9\u8428\u5730\u65b9\u4e0d\u5927\uff0c\u6211\u4eec\u591a\u5728 \u9152\u697c\u83dc\u9986\u7a7f\u63d2\uff0c\u4e5f\u8bb8\u53ef\u78b0\u89c1\u4ed6\u4eec\u3002\u5c31\u662f\u78b0\u4e0d\u7740\uff0c\u6211\u4eec\u4e5f\u53ef\u7559\u4e0b\u6697\u53f7\uff0c\u53eb\u4ed6\u4eec\u6765\u627e\u6211\u4eec\u3002\u201d \u3000\u3000\u8fd9\u65e5\uff0c\u4e24\u4eba\u5230\u62c9\u8428\u6700\u5927\u7684\u4e00\u5bb6\u9152\u9986\u559d\u9152\uff0c\u65f6\u4ea4\u6b63\u5348\uff0c\u5ba2\u4eba\u751a\u591a\uff0c\u4e24\u4eba\u627e\u5f97\u4e00\u5f20\u96c5\u5ea7\uff0c\u8981 \u4e86\u4e00\u58f6\u7af9\u53f6\u9752\uff0c\u7ec6\u659f\u6d45\u914c\u3002\u6b66\u743c\u7476\u4e00\u65f6\u5174\u8d77\uff0c\u5bf9\u674e\u601d\u6c38\u9053\uff1a\u201c\u6211\u548c\u4f60\u6bd4\u8d5b\u559d\u9152\u5982\u4f55\u201d\u674e\u601d\u6c38 \u9152\u91cf\u751a\u8c6a\uff0c\u7b11\u9053\uff1a\u201c\u6709\u4e8b\u5728\u8eab\uff0c\u4f60\u559d\u9189\u4e86\u5982\u4f55\u662f\u597d\uff1f\u201d\u6b66\u743c\u7476\u5634\u5df4\u4e00\u5476\uff0c\u8f7b\u58f0\u8bf4\u9053\uff0c\u201c\u600e\u89c1 \u5f97\u4e00\u5b9a\u662f\u6211\u559d\u9189\uff1f\u201d\u674e\u601d\u6c38\u4e00\u542c\uff0c\u6599\u5f97\u5979\u662f\u60f3\u70ab\u8000\u5185\u529f\uff0c\u4e5f\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u8033\u76ee\u4f17\u591a\uff0c\u4f60 \u53ef\u4e0d\u8981\u80e1\u4e71\u5356\u5f04\u3002\u201d \u3000\u3000\u6b66\u743c\u7476\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u4fdd\u7ba1\u4e0d\u4f1a\u7ed9\u4eba\u77a7\u7834\u5c31\u662f\u4e86\uff01\u201d\u674e\u601d\u6c38\u89c1\u8fc7\u6b66\u743c\u7476\u7cbe\u5999\u7684\u5251\u672f\uff0c \u4e5f\u60f3\u77e5\u9053\u5979\u7684\u5185\u529f\u9020\u8be3\u5982\u4f55\uff0c\u89c1\u5979\u9ad8\u5174\uff0c\u4fbf\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u5c31\u5e73\u8d4c\u5427\u3002\u201d\u6b66\u743c\u7476\u9053\uff1a\u201c\u8d4c\u4ec0 \u4e48\u5462\uff1f\u201d\u674e\u601d\u6c38\u9053\uff1a\u201c\u8c01\u8f93\u4e86\uff0c\u5c31\u5f97\u7b54\u5e94\u542c\u5bf9\u65b9\u7684\u4e00\u53e5\u8bdd\u3002\u201d\u6b66\u743c\u7476\u9053\uff1a\u201c\u597d\uff0c\u4f9d\u4f60\uff01\u201d \u3000\u3000\u4e24\u4eba\u4e00\u676f\u4e00\u676f\u5730\u8c6a\u6b22\u8d77\u6765\uff0c\u996e\u4e86\u4e00\u4f1a\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5c31\u559d\u5149\u4e86\u4e09\u58f6\u7af9\u53f6\u9752\uff0c\u674e\u601d\u6c38\u6e10\u6e10\u4e0d\u80dc \u9152\u529b\uff0c\u770b\u6b66\u743c\u7476\u65f6\uff0c\u53ea\u89c1\u5979\u5934\u4e0a\u9690\u5192\u70ed\u6c14\uff0c\u6c57\u5982\u96e8\u4e0b\uff0c\u77e5\u9053\u5979\u6b63\u7528\u4e0a\u4e58\u5185\u529f\u628a\u9152\u8feb\u53d1\u51fa\u6765\u3002 \u585e\u5916\u82e6\u5bd2\uff0c\u897f\u5317\u7267\u4eba\u7ecf\u5e38\u996e\u9152\u89e3\u5bd2\uff0c\u9152\u91cf\u8981\u6bd4\u4e2d\u539f\u7684\u9152\u5ba2\u4eba\u591a\u3002\u8fd9\u65f6\u9152\u697c\u6b63\u6709\u4e0d\u5c11\u4eba\u5728\u8c6a \u996e\uff0c\u56e0\u6b64\u674e\u601d\u6c38\u4e5f\u5c31\u4e0d\u4ee5\u4e3a\u610f\uff0c\u4f46\u6b66\u743c\u7476\u662f\u5973\u626e\u7537\u88c5\uff0c\u53ea\u6050\u5979\u996e\u5f97\u592a\u591a\uff0c\u9732\u51fa\u5973\u513f\u4f53\u6001\uff0c\u53cd \u6b63\u81ea\u5df1\u4e5f\u5df2\u6709\u4e86\u516b\u6210\u9152\u610f\uff0c\u4fbf\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u8ba4\u8f93\uff01\u201d\u6b66\u743c\u7476\u5fc3\u82b1\u6012\u653e\uff0c\u773c\u6ce2\u6d41\u8f6c\uff0c\u7b11 \u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u7ed3\u5e10\u56de\u53bb\u5427\u3002\u4f60\u5f97\u542c\u6211\u7684\u4e00\u53e5\u8bdd\u4e86\uff01\u201d\u674e\u601d\u6c38\u6b63\u60f3\u628a\u9152\u4fdd\u5524\u6765\uff0c\u5ffd\u89c1\u9694\u5ea7\u4e00 \u4eba\uff0c\u773c\u707c\u707c\u5730\u770b\u7740\u4ed6\u4eec\uff0c\u6697\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d\u6025\u5fd9\u7ed3\u5e10\u4e0b\u697c\uff0c\u8d70\u5230\u8857\u4e0a\uff0c\u5077\u5077\u56de\u987e\uff0c\u53ea\u89c1\u90a3\u4eba \u4e5f\u8ddf\u5728\u540e\u9762\u3002\u674e\u601d\u6c38\u6084\u58f0\u5bf9\u6b66\u743c\u7476\u8bf4\u4e86\uff0c\u6b66\u743c\u7476\u9053\uff1a\u201c\u597d\uff0c\u7ed9\u4ed6\u70b9\u82e6\u5934\u5403\u5403\uff01\u201d\u674e\u601d\u6c38\u9053\uff1a \u201c\u4e0d\u884c\uff0c\u6b64\u4eba\u975e\u53cb\u5373\u654c\uff0c\u4e0d\u80fd\u80e1\u4e71\u52a8\u624b\uff01\u201d\u8d70\u5165\u4e00\u6761\u50fb\u9759\u7684\u5c0f\u5df7\uff0c\u4e00\u8f86\u725b\u8f66\u8fce\u9762\u800c\u6765\uff0c\u8857\u9053 \u72ed\u7a84\uff0c\u4e24\u4eba\u4fa7\u8eab\u95ea\u907f\uff0c\u521a\u521a\u8ba9\u8fc7\u725b\u8f66\uff0c\u90a3\u4eba\u5df2\u5230\u4e86\u80cc\u540e\uff0c\u4f6f\u4f5c\u8eb2\u95ea\u725b\u8f66\uff0c\u5ffd\u7136\u8eab\u5b50\u5411\u524d\u4e00 \u6251\uff0c\u671d\u674e\u601d\u6c34\u80cc\u540e\u538b\u6765\uff0c\u674e\u601d\u6c38\u6697\u8fd0\u5185\u529b\u53cc\u81c2\u5411\u540e\u4e00\u5f20\uff0c\u60f3\u628a\u90a3\u4eba\u8feb\u9000\uff0c\u90a3\u6599\u6765\u4eba\u819d\u76d6\u4e00 \u9876\uff0c\u674e\u601d\u6c38\u817f\u53d8\u9178\u8f6f\uff0c\u51e0\u4e4e\u8dcc\u5012\u3002\u6b66\u743c\u7476\u53cd\u624b\u4e00\u70b9\uff0c\u90a3\u4eba\u5495\u54af\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\uff0c\u4e00\u4e2a\u9ca4\u9c7c\u6253 \u633a\uff0c\u53c8\u7ffb\u4e86\u8d77\u6765\uff0c\u6b66\u743c\u7476\u6b63\u60f3\u559d\u95ee\uff0c\u90a3\u4eba\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u8ba4\u5f97\u51cc\u672a\u98ce\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u6c38\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u4eba\u7126\u6025\u4e4b\u72b6\uff0c\u5f62\u4e8e\u8f9e\u8272\uff0c\u53c8\u8ffd\u95ee\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u7ba1\u6211\u662f\u8c01\uff0c\u6211 \u53ea\u95ee\u4f60\uff0c\u4f60\u53ef\u662f\u51cc\u672a\u98ce\u7684\u670b\u53cb\uff1f\u201d\u6b66\u743c\u7476\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u51cc\u672a\u98ce\u5371\u5728\u65e6\u5915\uff0c \u4f60\u4eec\u82e5\u662f\u6765\u6551\u4ed6\u7684\uff0c\u53ef\u5f97\u8d76\u5feb\uff01\u201d\u674e\u601d\u6c38\u9053\uff1a\u201c\u4f60\u5982\u4f55\u77e5\u9053\uff1f\u201d\u90a3\u4eba\u82e6\u7b11\u9053\uff1a\u201c\u6211\u5c31\u662f\u770b\u7ba1 \u4ed6\u7684\u4eba\uff0c\u5c06\u6765\u884c\u5211\u65f6\uff0c\u4e5f\u8bb8\u8fd8\u8981\u6211\u505a\u523d\u5b50\u624b\u5462\uff01\u6211\u53ef\u771f\u4e0d\u613f\u4eb2\u624b\u6740\u4ed6\uff01\u201d\u674e\u601d\u6c38\u9762\u8272\u500f\u53d8\uff0c \u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u53ef\u771f\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u9a97\u4f60\uff1f\u201d\u674e\u601d\u6c38\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u8d76\u5feb\u56de\u53bb\u89c1\u51cc\u5927 \u4fa0\uff0c\u4eca\u665a\u4ea5\u65f6\uff0c\u54b1\u4eec\u5728\u897f\u7985\u5c71\u76f8\u89c1\u3002\u201d \u3000\u3000\u90a3\u4eba\u4e43\u662f\u5141\u9898\u65b0\u6536\u7684\u56de\u65cf\u6b66\u58eb\u9a6c\u65b9\uff0c\u4ed6\u548c\u5468\u9752\u6210\u4e86\u51cc\u672a\u98ce\u7684\u5fc3\u8179\u4e4b\u540e\uff0c\u65e0\u65f6\u4e0d\u60f3\u6551\u4ed6\u3002 \u53ef\u662f\u4eba\u5c11\u5200\u5f31\uff0c\u6beb\u65e0\u529e\u6cd5\uff0c\u51cc\u672a\u98ce\u65f6\u5e38\u548c\u4ed6\u4f5c\u957f\u591c\u4e4b\u8c08\uff0c\u56e0\u6b64\u51cc\u672a\u98ce\u7684\u670b\u53cb\u4ed6\u4eec\u4e5f\u8033\u719f\u80fd \u8be6\u3002\u9a6c\u65b9\u4e45\u5728\u6c5f\u6e56\u884c\u8d70\uff0c\u9605\u5386\u751a\u591a\uff0c\u8fd9\u65e5\u5728\u9152\u697c\u4e0a\u89c1\u5230\u674e\u601d\u6c38\u548c\u6b66\u743c\u7476\u8c6a\u6b22\uff0c\u6697\u6697\u79f0\u5947\uff0c \u674e\u3001\u6b66\u4e8c\u4eba\uff0c\u76f8\u8c8c\u6587\u5f31\uff0c\u5206\u660e\u662f\u4e2d\u539f\u6765\u7684\uff0c\u4f46\u9152\u91cf\u5374\u4e0d\u5728\u4ed6\u4eec\u4e4b\u4e0b\uff0c\u8fd9\u4fbf\u5f15\u8d77\u4e86\u9a6c\u65b9\u7684\u6ce8 \u610f\u3002\u518d\u4ed4\u7ec6\u770b\u65f6\uff0c\u90a3\u767d\u9762\u4e66\u751f\u7684\u76f8\u8c8c\uff0c\u751a\u4f3c\u51cc\u672a\u98ce\u63cf\u7ed8\u7684\u674e\u601d\u6c38\uff0c\u8bd5\u4e00\u63a2\u95ee\uff0c\u679c\u7136\u4e0d\u9519\u3002 \u3000\u3000\u9a6c\u65b9\u53bb\u540e\uff0c\u6b66\u743c\u7476\u9053\uff1a\u201c\u4f60\u4f55\u4e0d\u7ea6\u5728\u4ed6\u5728\u5bd3\u6240\u76f8\u89c1\uff1f\u201d\u674e\u601d\u6c38\u9053\uff1a\u201c\u6b64\u4eba\u7684\u8bdd\uff0c\u4e0d\u53ef\u4e0d \u4fe1\uff0c\u5374\u4e5f\u4e0d\u53ef\u5168\u4fe1\u3002\u201d\u4e24\u4eba\u8fb9\u8d70\u8fb9\u8c08\uff0c\u6b66\u743c\u7476\u5ffd\u63e1\u7740\u674e\u601d\u6c38\u7684\u624b\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u4f60 \u521a\u624d\u8d4c\u6d12\u8f93\u4e86\uff0c\u53ef\u8981\u4f9d\u6211\u4e00\u4ef6\u4e8b\u4e86\uff01\u201d\u674e\u601d\u6c38\u9053\uff1a\u201c\u4f9d\u4f60\uff0c\u4f60\u8bf4\uff01\u201d\u6b66\u743c\u7476\u4f4e\u9b03\u4e00\u7b11\uff0c\u8bf4 \u9053\uff1a\u201c\u4f60\u7231\u56de\u7586\u7684\u8349\u539f\u5417\uff1f\u201d\u674e\u601d\u6c38\u9053\uff1a\u201c\u4e0d\u5230\u56de\u7586\uff0c\u4e0d\u77e5\u4e2d\u56fd\u4e4b\u5927\uff0c\u65e0\u9645\u8349\u539f\uff0c\u6781\u76ee\u96be \u5c3d\uff0c\u4eca\u4eba\u80f8\u6000\u5f00\u9614\uff0c\u6211\u559c\u6b22\u6781\u4e86\uff01\u201d\u6b66\u743c\u7476\u634f\u4e86\u674e\u601d\u6c38\u624b\u5fc3\u4e00\u4e0b\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u6211\u8981\u4f60 \u7ec8\u751f\u4f4f\u5728\u8349\u539f\uff0c\u6c38\u8fdc\u966a\u7740\u6211\uff0c\u884c\u4e48\uff1f\u201d\u674e\u601d\u6c38\u5fc3\u9b42\u52a8\u8361\uff0c\u559c\u4e0a\u7709\u68a2\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u6b63\u662f\u6c42 \u4e4b\u4e0d\u5f97\uff01\u201d\u539f\u6765\u674e\u601d\u6c38\u4e8c\u5341\u5e74\u620e\u9a6c\uff0c\u4e45\u4f5c\u4e00\u519b\u4e3b\u5e05\uff0c\u751a\u5c11\u60f3\u5230\u513f\u5973\u79c1\u60c5\uff0c\u548c\u6b66\u743c\u7476\u7ed3\u8bc6\u4e4b \u540e\uff0c\u867d\u7136\u4e24\u5fc3\u7231\u6155\uff0c\u4f46\u603b\u4e0d\u6562\u628a\u7231\u610f\u8868\u9732\u51fa\u6765\u3002\u4e24\u4eba\u540c\u884c\u534a\u6708\uff0c\u6b66\u743c\u7476\u65e9\u5df2\u671f\u5f85\u4ed6\u8bf4\u51fa\u7231 \u5b57\u3002\u4e0d\u6599\u5728\u8fd9\u65b9\u9762\uff0c\u674e\u601d\u6c38\u6bd4\u5973\u5b69\u5b50\u8fd8\u8981\u5bb3\u7f9e\uff0c\u56e0\u6b64\u4eca\u65e5\u6b66\u743c\u7476\u501f\u7740\u9152\u610f\uff0c\u9053\u51fa\u5fc3\u4e8b\u3002\u4e24\u4eba \u5728\u5e7d\u9759\u7684\u957f\u8857\u501a\u504e\u800c\u884c\uff0c\u674e\u601d\u6c38\u53ea\u89c9\u5170\u9e9d\u5e7d\u9999\uff0c\u4e2d\u4eba\u6b32\u9189\uff0c\u5343\u8a00\u4e07\u8bed\u90fd\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\u4e86\uff0c\u4e24 \u4eba\u624b\u633d\u624b\u884c\u4e86\u4e00\u4f1a\uff0c\u6b66\u743c\u7476\u62bf\u5634\u7b11\u9053\uff1a\u201c\u5230\u4e86\uff0c\u4f60\u8fd8\u5c3d\u5f80\u524d\u8d70\u4f5c\u751a\uff1f\u201d\u674e\u601d\u6c38\u62ac\u5934\u4e00\u671b\uff0c\u5bd3 \u6240\u5c31\u5728\u773c\u524d\uff0c\u4e0d\u89c9\u54d1\u7b11\u3002 \u3000\u3000\u4e24\u4eba\u8fdb\u5165\u5bd3\u6240\uff0c\u6253\u5f00\u623f\u95e8\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u4f4e\u6c89\u7684\u58f0\u97f3\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u73b0\u5728\u624d\u6765\uff1f\u201d\u674e\u601d\u6c38 \u671b\uff0c\u53ea\u89c1\u5e8a\u4e0a\u5750\u7740\u4e00\u4e2a\u8001\u4eba\uff0c\u6b63\u662f\u4ed6\u4eec\u65e5\u5915\u76fc\u671b\u7684\u5085\u9752\u4e3b\u3002\u6b66\u743c\u7476\u9053\uff1a\u201c\u5085\u4f2f\u4f2f\uff0c\u6211\u7238\u7238\u95ee \u5019\u4f60\uff0c\u4f60\u662f\u600e\u6837\u6478\u6765\u7684\u554a\uff01\u201d\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6211\u4eec\u7684\u4eba\u770b\u5230\u4f60\u4eec\u7684\u6697\u8bb0\uff0c\u6211\u5c31\u4e00\u4e2a\u4eba\u6478\u6765 \u4e86\uff01\u201d\u674e\u601d\u6c38\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u5085\u4f2f\u4f2f\u5e26\u4e86\u591a\u5c11\u4eba\u6765\uff1f\u201d\u5085\u9752\u4e3b\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4eba\u5012\u662f\u5e26\u6765 \u4e86\u4e0d\u5c11\uff0c\u4f46\u5e03\u8fbe\u62c9\u5bab\u9632\u5b88\u68ee\u4e25\uff0c\u51cc\u672a\u98ce\u53c8\u4e0d\u77e5\u5173\u5728\u4f55\u5904\uff0c\u6211\u4eec\u82e5\u662f\u5192\u9669\u591c\u88ad\uff0c\u53ea\u6050\u672a\u6253\u8fdb \u53bb\uff0c\u51cc\u672a\u98ce\u5df2\u7ed9\u6740\u6389\u4e86\u3002\u201d\u674e\u601d\u6c38\u9053\uff1a\u201c\u5982\u6709\u5185\u5e94\uff0c\u53ef\u80fd\u6210\u529f\uff01\u201d\u5085\u9752\u4e3b\u773c\u775b\u4e00\u4eae\uff0c\u6025\u5fd9\u95ee \u9053\uff1a\u201c\u4f60\u5728\u6e05\u519b\u7684\u6b66\u58eb\u4e2d\uff0c\u53ef\u6709\u719f\u4eba\uff1f\u201d\u674e\u601d\u6c38\u9053\uff1a\u201c\u719f\u4eba\u5012\u6ca1\u6709\uff0c\u4f46\u5374\u6709\u4eba\u4e0e\u6211\u4eec\u63a5\u8fc7 \u5934\u3002\u201d\u5f53\u4e0b\u628a\u9a6c\u65b9\u7684\u4e8b\u8bf4\u4e86\u3002\u5085\u9752\u4e3b\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4e0d\u59a8\u4e0e\u4ed6\u4e00\u89c1\uff0c\u4f46\u4e5f\u5f97 \u63d0\u9632\u6709\u8bc8\u3002\u4eca\u665a\u6211\u4e0e\u51e0\u4e2a\u5f1f\u5144\u5230\u897f\u7985\u5c71\u63a5\u5e94\u4f60\u4eec\u3002\u201d\u5927\u5bb6\u7ea6\u597d\u65f6\u95f4\u6697\u53f7\uff0c\u5085\u9752\u4e3b\u5148\u81ea\u53bb\u4e86\u3002 \u3000\u3000\u5085\u9752\u4e3b\u8fd9\u51e0\u767e\u4eba\u6f5c\u5165\u62c9\u8428\u4e4b\u540e\uff0c\u5206\u5c45\u5728\u5404\u5904\uff0c\u5085\u9752\u4e3b\u4f4f\u5728\u4e00\u4e2a\u85cf\u65cf\u7684\u7267\u6c11\u5bb6\u4e2d\uff0c\u521a\u521a\u8e0f \u8fdb\u5bd3\u6240\uff0c\u5218\u90c1\u82b3\u5c31\u8fce\u4e86\u51fa\u6765\uff0c\u9762\u8272\u6c89\u6697\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u97e9\u5fd7\u90a6\u8d70\u4e86\uff01\u201d\u5085\u9752\u4e3b\u5947\u9053\uff1a\u201c\u4ed6\u5230 \u54ea\u91cc\u53bb\uff1f\u6709\u4ec0\u4e48\u4e66\u4fe1\u7559\u4e0b\u5417\uff1f\u201d\u5218\u90c1\u82b3\u9053\uff1a\u201c\u4ec0\u4e48\u90fd\u6ca1\u6709\u3002\u201d\u5085\u9752\u4e3b\u76b1\u8d77\u7709\u5934\uff0c\u60f3\u4e86\u4e00\u9635\uff0c \u8bf4\u9053\uff1a\u201c\u97e9\u5fd7\u90a6\u4e0d\u662f\u8d2a\u751f\u6015\u6b7b\u4e4b\u8f88\uff0c\u4ed6\u8fd9\u4e00\u8d70\uff0c\u60f3\u662f\u53e6\u6709\u539f\u56e0\u3002\u201d\u5218\u90c1\u82b3\u9eef\u7136\u65e0\u8bed\uff0c\u97e9\u5fd7\u90a6 \u8fd9\u4e9b\u5929\u6765\uff0c\u7aed\u8bda\u5730\u6170\u89e3\u5979\uff0c\u5df2\u7ecf\u6210\u4e3a\u5979\u60a3\u96be\u4e2d\u6700\u597d\u7684\u670b\u53cb\u4e86\u3002\u5979\u60f3\u8d77\u5341\u591a\u5e74\u6765\uff0c\u5bf9\u4ed6\u7684\u51b7 \u6f20\uff0c\u4e0d\u89c9\u6709\u4e9b\u6b49\u610f\uff0c\u53ea\u6050\u4ed6\u53c8\u50cf\u4e0a\u6b21\u5728\u4e91\u5c97\u90a3\u6837\uff0c\u4e00\u65f6\u53d1\u4e86\u50bb\u52b2\uff0c\u5c31\u4e0d\u522b\u800c\u884c\uff0c\u5085\u9752\u4e3b\u89c1\u5979 \u90c1\u90c1\u4e0d\u6b22\uff0c\u6025\u5fd9\u5c06\u674e\u601d\u6c38\u4e0e\u6e05\u519b\u6b66\u571f\u63a5\u8fc7\u5934\u7684\u6d88\u606f\u544a\u8bc9\u5979\uff0c\u8fd9\u624d\u4f7f\u5979\u8f6c\u60b2\u4e3a\u559c\u3002 \u3000\u3000\u5f53\u665a\u4ea5\u65f6\uff0c\u674e\u601d\u6c38\u548c\u6b66\u743c\u7476\u4f9d\u65f6\u5728\u897f\u7985\u5c71\u76f8\u5019\uff0c\u7b49\u4e86\u8bb8\u4e45\uff0c\u8fd8\u4e0d\u89c1\u9a6c\u65b9\u7684\u8e2a\u8ff9\uff0c\u4e0d\u89c9\u5927 \u7591\uff0c\u5c06\u8fd1\u5b50\u591c\uff0c\u98ce\u96ea\u4ea4\u52a0\u3002\u6b66\u743c\u7476\u9053\uff1a\u201c\u4e0d\u5982\u56de\u53bb\u5427\uff01\u201d\u674e\u601d\u6c38\u201c\u55ef\u201d\u4e86\u4e00\u58f0\u3002\u5ffd\u89c1\u4e00\u6761\u9ed1 \u5f71\u5411\u5c71\u9876\u8dd1\u6765\uff0c\u6b66\u743c\u7476\u7ec3\u8fc7\u6885\u82b1\u9488\uff0c\u773c\u529b\u6781\u597d\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u4eba\u4e0d\u662f\u9a6c\u65b9\uff01\u201d\u674e\u601d\u6c38\u5b9a \u6674\u770b\u65f6\uff0c\u90a3\u4eba\u8d8a\u8dd1\u8d8a\u8fd1\uff0c\u9a6c\u65b9\u662f\u5e74\u8fc7\u56db\u65ec\u7684\u4e2d\u5e74\u4eba\uff0c\u90a3\u4eba\u5374\u662f\u4e00\u4e2a\u4e8c\u5341\u591a\u5c81\u7684\u5c0f\u4f19\u5b50\uff0c\u674e\u601d \u6c38\u9053\uff1a\u201c\u4ed6\u53ea\u662f\u5b64\u8eab\u4e00\u4eba\uff0c\u4f60\u5728\u65c1\u76d1\u89c6\uff0c\u5f85\u6211\u95ee\u4ed6\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u90a3\u4eba\u5df2\u5230\u8ddf\u524d\uff0c\u628a\u4ed6\u4eec\u548c \u9a6c\u65b9\u7ea6\u5b9a\u7684\u6697\u53f7\u8bf4\u4e86\uff0c\u5ffd\u7136\u644a\u5f00\u624b\u638c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u51cc\u5927\u4fa0\u7ed9\u4f60\u4eec\u7684\u4fe1\u3002\u201d\u674e\u601d\u6c38\u6050\u9632\u6709 \u8bc8\uff0c\u6697\u7528\u64d2\u62ff\u624b\u6cd5\u3002\u4e09\u6307\u6263\u4f4f\u4ed6\u7684\u8109\u95e8\uff0c\u5728\u624b\u638c\u4e0a\u4e00\u77a7\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7740\uff1a\u201c\u6765\u4eba\u662f\u6211\u597d\u53cb\uff0c \u8bf7\u4e0e\u4ed6\u7ec6\u5546\u52ab\u72f1\u4e4b\u6cd5\u3002\u201d\u6b63\u662f\u51cc\u672a\u98ce\u7684\u5b57\u8ff9\uff0c\u624b\u6307\u4e00\u677e\uff0c\u6765\u4eba\u7b11\u563b\u563b\u5730\u9053\uff1a\u201c\u6211\u4ece\u672a\u89c1\u8fc7\u6c5f \u6e56\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u5982\u4eca\u8bc6\u4e86\u51cc\u5927\u4fa0\uff0c\u53c8\u8bc6\u4e86\u4f60\u4eec\uff0c\u771f\u662f\u751f\u5e73\u5feb\u4e8b\u3002\u4f60\u8fd9\u624b\u64d2\u62ff\u6cd5\u5f88\u4e0d\u9519\uff0c\u662f\u54ea \u4e00\u6d3e\u7684\u5440\uff1f\u554a\uff01\u8bf4\u4e86\u8bb8\u591a\uff0c\u6211\u8fd8\u672a\u544a\u8bc9\u4f60\uff0c\u6211\u53eb\u5468\u9752\uff0c\u548c\u9a6c\u65b9\u662f\u6700\u597d\u7684\u670b\u53cb\u3002\u201d\u674e\u601d\u6c38\u89c1\u4ed6 \u5929\u771f\u53ef\u7231\uff0c\u751a\u4e3a\u60ca\u5947\uff1a\u8fd9\u6837\u6beb\u65e0\u6c5f\u6e56\u7ecf\u9a8c\u7684\u9752\u5e74\u4eba\uff0c\u5c45\u7136\u4e5f\u662f\u6e05\u5bab\u4e2d\u5f97\u5230\u4fe1\u4efb\u7684\u6b66\u58eb\uff0c\u4ee4\u4ed6 \u5927\u60d1\u4e0d\u89e3\u3002\u4ed6\u5374\u4e0d\u77e5\u5468\u9752\u4e43\u662f\u4e16\u88ad\u7684\u6b66\u58eb\u3002 \u3000\u3000\u5468\u9752\u53c8\u9053\uff1a\u201c\u9a6c\u5927\u54e5\u4eca\u65e5\u6070\u5de7\u5f53\u503c\uff0c\u6240\u4ee5\u7531\u6211\u66ff\u4ed6\u8df5\u7ea6\u3002\u201d\u8fb9\u8bf4\u8fb9\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u5f20\u7f8a\u76ae \u5730\u56fe\u3002\u674e\u601d\u6c38\u770b\u4e86\uff0c\u4e0d\u7531\u5f97\u5927\u559c\u8fc7\u671b\u3002 \u3000\u3000\u90a3\u7f8a\u76ae\u4e0a\u753b\u7684\u662f\u5e03\u8fbe\u62c9\u5bab\u7684\u95e8\u6237\u9053\u8def\uff0c\u5728\u51cc\u672a\u98ce\u6240\u4f4f\u4e4b\u5904\uff0c\u5708\u4e86\u4e00\u4e2a\u7ea2\u5708\u5708\u3002\u5468\u9752\u9053\uff1a \u201c\u8fd9\u662f\u9a6c\u5927\u54e5\u548c\u6211\u6697\u4e2d\u753b\u4e0b\u6765\u7684\uff0c\u8ff7\u5bab\u4e2d\u5343\u95e8\u4e07\u6237\uff0c\u9053\u8def\u7eb7\u6b67\uff0c\u6709\u4e9b\u8fde\u6211\u4eec\u4e5f\u4e0d\u6e05\u695a\uff0c\u8fd9\u56fe \u53ea\u662f\u51ed\u6211\u4eec\u8bb0\u5fc6\u6240\u53ca\u753b\u7684\u3002\u4f60\u4eec\u8bb0\u719f\u4e4b\u540e\uff0c\u540e\u5929\u665a\u4e0a\uff0c\u8bf7\u6d3e\u9ad8\u624b\u524d\u6765\uff0c\u6211\u4eec\u5f53\u5728\u91cc\u9762\u63a5 \u5e94\u3002\u201d \u3000\u3000\u5468\u9752\u53bb\u540e\uff0c\u5085\u9752\u4e3b\u54c8\u54c8\u5927\u7b11\uff0c\u4ece\u6697\u9edd\u5904\u8d70\u4e86\u51fa\u6765\uff0c\u6311\u8d77\u62c7\u6307\u9053\uff1a\u201c\u51cc\u672a\u98ce\u771f\u6210\uff01\u5c45\u7136\u8fde \u76d1\u5b88\u4ed6\u7684\u654c\u4eba\u90fd\u7ed9\u4ed6\u6536\u670d\uff01\u201d\u5f53\u665a\u5373\u62df\u597d\u4e86\u591c\u88ad\u5587\u561b\u5bab\u7684\u8ba1\u5212\uff0c\u674e\u601d\u6c38\u548c\u6b66\u743c\u7476\u7b2c\u4e8c\u5929\u4e5f\u642c \u53bb\u548c\u5085\u9752\u4e3b\u540c\u4f4f\u3002 \u3000\u3000\u518d\u8bf4\u51cc\u672a\u98ce\u88ab\u5173\u5728\u8ff7\u5bab\u4e4b\u4e2d\uff0c\u5df2\u8fd1\u4e00\u4e2a\u6708\uff0c\u4ed6\u5728\u72f1\u4e2d\u4e5f\u5e76\u4e0d\u7a7a\u95f2\uff0c\u4ed6\u5229\u7528\u6bcf\u4e00\u4e2a\u673a\u4f1a\uff0c \u548c\u76d1\u5b88\u4ed6\u7684\u6b66\u58eb\u8c08\u8bdd\uff0c\u7ed9\u4ed6\u4eec\u8bb2\u6c5f\u6e56\u4e0a\u7684\u82f1\u96c4\u4e8b\u8ff9\uff0c\u6709\u65f6\u8fd8\u6307\u70b9\u4ed6\u4eec\u7684\u6b66\u827a\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u4ed6 \u6bcf\u4e00\u4e2a\u957f\u591c\uff0c\u90fd\u6f5c\u5fc3\u63a2\u7d22\u6b66\u5b66\u4e0a\u7684\u5965\u79d8\uff0c\u975e\u4f46\u5929\u5c71\u5251\u6cd5\u878d\u4f1a\u8d2f\u901a\uff0c\u800c\u4e14\u4ed6\u8fd8\u5f52\u7eb3\u4e86\u5e73\u751f\u7684\u5fc3 \u5f97\uff0c\u521b\u9020\u4e86\u8bb8\u591a\u65b0\u5947\u7684\u62db\u6570\u3002\u4ed6\u81ea\u89c9\u6bd4\u4ee5\u524d\u6210\u719f\u4e86\u8bb8\u591a\u3002\u201c\u6211\u867d\u7136\u6ca1\u6709\u4e86\u53f3\u624b\u7684\u62c7\u6307\uff0c\u4f46\u53ea \u8981\u6211\u4e0d\u5c48\u6b7b\u4e8e\u72f1\u4e2d\uff0c\u6211\u8fd8\u4e00\u6837\u7684\u53ef\u4ee5\u6559\u4eba\u4f7f\u5251\u3002\u201d\u4ed6\u7ecf\u5f97\u8d77\u82e6\u96be\u7684\u8003\u9a8c\uff0c\u4e3a\u81ea\u5df1\u5014\u5f3a\u7684\u751f\u547d \u800c\u611f\u5230\u9a84\u50b2\u3002 \u3000\u3000\u8fd9\u4e00\u665a\uff0c\u4ed6\u548c\u5085\u9752\u4e3b\u7ea6\u5b9a\u7684\u65f6\u523b\u5230\u4e86\u3002\u5728\u9ed1\u6c89\u6c89\u7684\u6df1\u591c\u4e2d\uff0c\u7a81\u7136\u8d77\u4e86\u8f70\u5929\u7684\u5de8\u54cd\uff0c\u5468\u9752 \u5306\u5306\u5730\u8dd1\u4e86\u8fdb\u6765\uff0c\u6253\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u51cc\u672a\u98ce\u5927\u559d\u4e00\u58f0\uff0c\u8fd0\u529b\u4e00\u6323\uff0c\u8eab\u4e0a\u7684\u9563\u94d0\u5bf8\u5bf8\u788e\u88c2\uff0c\u53cd\u624b \u4e00\u638c\uff0c\u628a\u623f\u4e2d\u7684\u77f3\u684c\u6253\u5f97\u7c89\u788e\uff0c\u65c1\u8fb9\u770b\u5b88\u7684\u51e0\u4e2a\u6b66\u58eb\u60ca\u5f97\u5446\u4e86\uff0c\u5468\u9752\u5c16\u53eb\u7740\u5047\u88c5\u88ab\u51cc\u5728\u98ce\u8ffd \u9010\u800c\u60ca\u60f6\uff0c\u5047\u620f\u771f\u505a\uff0c\u65f6\u95f4\u914d\u5408\u5f97\u6070\u5230\u597d\u5904\u3001 \u3000\u3000\u5085\u9752\u4e3b\u7387\u9886\u4f17\u4eba\uff0c\u6309\u7740\u5730\u56fe\uff0c\u6740\u8fdb\u8ff7\u5bab\uff0c\u6e05\u5175\u867d\u7136\u4eba\u591a\uff0c\u53ef\u662f\u6765\u7684\u4e2a\u4e2a\u90fd\u662f\u9ad8\u624b\uff0c\u53c8\u662f \u5728\u6df1\u591c\u4e4b\u4e2d\uff0c\u7a81\u7136\u88ad\u5230\uff0c\u6e05\u519b\u4e0d\u53ef\u80fd\u90fd\u805a\u5728\u4e00\u5904\uff0c\u7adf\u7ed9\u4ed6\u4eec\u6740\u8fdb\u4e86\u5916\u4e09\u95e8\u3002\u5218\u90c1\u82b3\u5927\u53eb\u51cc\u672a \u98ce\uff0c\u5185\u4e09\u95e8\u5ffd\u7136\u500f\u5730\u6253\u5f00\uff0c\u695a\u662d\u5357\u620e\u88c5\u4f69\u5251\uff0c\u7acb\u5728\u5f53\u4e2d\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u8fdc\u5343\u91cc\u9762\u6765\uff0c \u5c31\u8bf7\u8fdb\u6765\u559d\u676f\u6c34\u9152\u5427\uff01\u201d\u6613\u5170\u73e0\u7ea4\u8170\u4e00\u7476\uff0c\u98de\u71d5\u822c\u5730\u659c\u743c\u8fc7\u53bb\uff0c\u77ed\u5251\u4e00\u523a\uff0c\u695a\u662d\u5357\u6a2a\u5251\u4e00 \u5c01\uff0c\u75be\u7684\u53c8\u9000\u5165\u4e86\u53e6\u4e00\u9053\u95e8\u6237\uff0c\u5f20\u534e\u662d\u6842\u4ef2\u660e\u53cc\u53cc\u62a2\u8fdb\uff0c\u5085\u9752\u4e3b\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u201d\u4f46\u4f17\u4eba\u5df2 \u62e5\u7740\u81ea\u5df1\u540c\u8fdb\u3002\u695a\u662d\u5357\u626c\u58f0\u53eb\u9053\uff1a\u201c\u5085\u8001\u5934\u513f\uff0c\u54b1\u4eec\u518d\u6bd4\u4e00\u6bd4\u5251\u3002\u201d\u6b66\u743c\u7476\u4e00\u628a\u94f6\u9488\u6253\u53bb\uff0c \u695a\u662d\u5357\u54c8\u54c8\u5927\u7b11\uff0c\u53cc\u8db3\u4e00\u8e6c\uff0c\u8eab\u5b50\u5411\u540e\u5c04\u51fa\uff0c\u8fdb\u5165\u4e86\u53e6\u4e00\u9053\u95e8\u6237\u3002\u674e\u601d\u6c38\u9053\uff1a\u201c\u4e0d\u8981\u5fd9\uff0c\u54b1 \u4eec\u6309\u56fe\u6740\u8fdb\uff0c\u8fd9\u4e2a\u8d3c\u5b50\u7ec8\u8d70\u4e0d\u60bc\u7684\uff0c\u73b0\u5728\u4e0d\u8981\u4e2d\u4ed6\u7684\u8be1\u8ba1\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5ffd\u7136\u5468\u56f4\u7684\u95e8\u6237\u4e00 \u9635\u65cb\u8f6c\uff0c\u4f17\u4eba\u518d\u4e5f\u8fa8\u4e0d\u6e05\u65b9\u5411\uff0c\u53ea\u89c9\u91cd\u95e8\u53e0\u6237\u4e4b\u5185\uff0c\u5982\u5904\u9690\u4f0f\u7532\u5175\u3002\u674e\u601d\u6c38\u53eb\u58f0\u82e6\u4e5f\uff0c\u6d41\u661f \u9524\u821e\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u628a\u4e00\u6247\u95e8\u677f\u6253\u788e\uff0c\u91cc\u9762\u5341\u591a\u4e2a\u536b\u58eb\u4e00\u62e5\u800c\u51fa\uff0c\u6740\u4e86\u4e00\u9635\uff0c\u500f\u53c8\u56db\u9762\u6563\u5f00\uff0c \u6216\u9690\u5165\u6821\u53f7\u590d\u58c1\uff0c\u6216\u4ece\u8718\u86db\u7f51\u822c\u7684\u96e8\u9053\u9003\u6563\u3002\u7247\u523b\u4e4b\u540e\uff0c\u53c8\u662f\u4e0d\u89c1\u4eba\u5f71\uff0c\u53ea\u542c\u5f97\u695a\u662d\u5357\u5f97\u610f \u7684\u7b11\u58f0\u3002 \u3000\u3000\u51cc\u672a\u98ce\u9000\u81f3\u5927\u5802\uff0c\u6e3a\u4e0d\u89c1\u4eba\uff0c\u6b63\u81ea\u751f\u7591\uff0c\u56db\u5468\u95e8\u6237\uff0c\u5ffd\u7136\u6253\u5f00\uff0c\u6570\u767e\u536b\u58eb\uff0c\u540c\u65f6\u6740\u51fa\u3002 \u51cc\u672a\u98ce\u795e\u5a01\u51db\u51db\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u695a\u662d\u5357\uff0c\u6709\u80c6\u7684\u6562\u6765\u4e0e\u6211\u51b3\u4e00\u6b7b\u6218\uff01\u201d\u536b\u58eb\u4eec\u8e0c\u8e87\u4e0d\u524d\uff0c\u5468 \u9752\u4e00\u65f6\u9519\u6115\uff0c\u4e5f\u6b62\u4e86\u811a\u6b65\u3002\u695a\u662d\u5357\u8d8a\u4f17\u800c\u51fa\uff0c\u5ffd\u7136\u5389\u58f0\u53eb\u9053\uff1a\u201c\u5148\u628a\u5468\u9752\u64d2\u4e0b\uff01\u201d\u4e24\u540d\u7981\u536b \u519b\u7edf\u9886\uff0c\u5206\u62a2\u4e0a\u6765\uff0c\u51cc\u672a\u98ce\u53cc\u81c2\u4e00\u632f\uff0c\u6293\u7740\u4e86\u524d\u9762\u90a3\u540d\u7edf\u9886\uff0c\u559d\u4e00\u58f0\u201c\u53bb\uff01\u201d\u594b\u529b\u6454\u51fa\uff0c\u649e \u4e2a\u6b63\u7740\uff0c\u5c06\u540e\u9762\u90a3\u540d\u7edf\u9886\u4e5f\u6253\u7ffb\u4e86\u3002\u624b\u8155\u4e00\u5e26\uff0c\u628a\u5468\u9752\u5e26\u8d77\uff0c\u5954\u5411\u5de6\u9762\u4fa7\u95e8\uff0c\u95e8\u5185\u6709\u51e0\u540d\u536b \u58eb\u9547\u5b88\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u5168\u90fd\u6563\u4e86\uff01 \u3000\u3000\u51cc\u672a\u98ce\u6258\u7740\u5468\u9752\uff0c\u5f80\u5899\u5934\u4e00\u7a9c\uff0c\u521a\u521a\u8e0f\u4e0a\uff0c\u5ffd\u89c9\u80cc\u540e\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u5237\u5730\u4eba\u5251\u4ff1\u5230\uff01\u51cc \u672a\u98ce\u79fb\u8eab\u8f6c\u6b65\uff0c\u5c06\u5468\u9752\u5f80\u5899\u5934\u5916\u4e00\u63a8\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u9003\u547d\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u695a\u662d\u5357\u7684 \u6e38\u9f99\u5251\u5df2\u523a\u5230\u4ed6\u7684\u80c1\u4e0b\u3002\u51cc\u672a\u98ce\u8eab\u5f62\u5f80\u540e\u4e00\u64a4\uff0c\u811a\u70b9\u5899\u5934\uff0c\u540e\u9000\u65e0\u8def\uff0c\u5306\u5fd9\u4e2d\u659c\u8eab\u5f80\u5de6\u4e00\u95ea \u4e09\u695a\u662d\u5357\u53d8\u62db\u5947\u5feb\uff0c\u5251\u5c16\u4e00\u98a4\uff0c\u53c8\u4ece\u53f3\u4fa7\u70b9\u5230\u3002\u51cc\u672a\u98ce\u731b\u7136\u53cd\u624b\u4e00\u638c\uff0c\u545b\u537f\u4e00\u58f0\uff0c\u695a\u662d\u5357\u7684 \u5251\u88ab\u51fb\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u4e00\u8fd9\u4e00\u638c\u6b63\u662f\u51cc\u672a\u98ce\u63c9\u5408\u5929\u5c71\u638c\u6cd5\u4e0e\u8fbe\u6469\u638c\u6cd5\u72ec\u521b\u7684\u4e00\u4e2a\u602a\u62db\uff0c\u695a\u662d\u5357 \u731d\u4e0d\u53ca\u9632\uff0c\u7740\u4e86\u9053\u513f\uff01\u53ef\u662f\u4ed6\u4e5f\u662f\u4e45\u7ecf\u5927\u654c\uff0c\u51cc\u672a\u98ce\u4e00\u638c\u51fb\u51fa\uff0c\u4ed6\u5df2\u77e5\u9053\u65e0\u6cd5\u8eb2\u95ea\uff0c\u6765\u4e0d\u53ca \u64a4\u5251\uff0c\u5374\u5148\u817e\u8d77\u4e00\u817f\uff0c\u51cc\u672a\u98ce\u5de6\u638c\u5288\u51fa\uff0c\u53f3\u638c\u8ddf\u7740\u4e00\u62c9\uff0c\u4e24\u4eba\u540c\u65f6\u8fdb\u62db\uff0c\u562d\u562d\u4e24\u58f0\uff0c\u51cc\u672a\u98ce \u7740\u4e86\u4e00\u811a\uff0c\u695a\u662d\u5357\u5403\u4e86\u4e00\u638c\uff0c\u540c\u65f6\u8dcc\u4e0b\u4e86\u5899\u5934\u3002 \u3000\u3000\u51cc\u672a\u98ce\u8eab\u672a\u8d77\uff0c\u811a\u5148\u98de\uff0c\u5750\u5728\u5730\u4e0a\u4e00\u4e2a\u201c\u5341\u5b57\u6446\u83b2\u201d\uff0c\u628a\u9644\u8fd1\u7684\u4e24\u540d\u6b66\u58eb\uff0c\u8e22\u51fa\u4e09\u4e08\u4ee5 \u5916\uff0c\u695a\u662d\u5357\u5df2\u62fe\u8d77\u4e86\u6e38\u9f99\u5b9d\u5251\uff0c\u5206\u5fc3\u523a\u5230\u3002\u51cc\u672a\u98ce\u6012\u9053\uff1a\u201c\u6211\u4e0d\u7528\u5251\u4e5f\u80fd\u6559\u8bad\u4f60\u8fd9\u4e2a\u53cd \u8d3c\uff01\u201d\u5de6\u62f3\u53f3\u638c\uff0c\u6b3a\u8eab\u76f4\u8fdb\uff0c\u695a\u662d\u5357\u7684\u6e38\u9f99\u5251\u547c\u547c\u5288\u98ce\uff0c\u7adf\u7136\u5288\u4e0d\u5230\u51cc\u672a\u98ce\u8eab\u4e0a\uff0c\u536b\u571f\u4eec\u6563 \u5728\u56db\u5468\uff0c\u5374\u4e0d\u4e0a\u524d\u3002\u539f\u6765\u695a\u662d\u5357\u81ea\u4ee5\u4e3a\u6709\u5251\u5728\u624b\uff0c\u5fc5\u5b9a\u4e0d\u4f1a\u8f93\u7ed9\u51cc\u672a\u98ce\uff0c\u6240\u4ee5\u4e8b\u5148\u53eb\u540c\u4f34\u4e0d \u8981\u5e2e\u4ed6\u3002\u800c\u8bb8\u591a\u536b\u58eb\u4e5f\u4e0d\u613f\u4e0e\u51cc\u672a\u98ce\u4e3a\u654c\uff0c\u4e50\u5f97\u8896\u624b\u65c1\u89c2\u3002 \u3000\u3000\u8f6c\u77ac\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u62fc\u6597\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u695a\u662d\u5357\u5140\u662f\u5360\u4e0d\u5230\u534a\u70b9\u4fbf\u5b9c\u3002\u51cc\u672a\u98ce\u5c55\u5f00\u4e86\u75be\u653b\u901f \u51b3\u7684\u6218\u6cd5\uff0c\u7a7a\u624b\u5165\u767d\u5203\uff0c\u786c\u62a2\u695a\u662d\u5357\u7684\u5b9d\u5251\u3002\u695a\u662d\u5357\u54ac\u5b9e\u7259\u6839\uff0c\u5251\u8bc0\u4e00\u6307\uff0c\u5237\u5237\u6570\u5251\uff0c\u529b\u731b \u62db\u95ea\uff0c\u4e0d\u6599\u51cc\u672a\u98ce\u8eab\u6cd5\u5feb\u6781\uff0c\u4e00\u95ea\u5373\u653b\uff0c\u4f0f\u8eab\u63a2\u6b65\uff0c\u53cc\u6307\u500f\u5730\u6233\u5230\u695a\u662d\u5357\u9762\u95e8\uff0c\u695a\u662d\u5357\u659c\u8eab \u65c1\u683d\uff0c\u8fde\u7a9c\u6570\u6b65\uff0c\u582a\u582a\u907f\u8fc7\u3002\u51e0\u4e2a\u5fc3\u8179\u6b7b\u58eb\u987e\u4e0d\u5f97\u4ed6\u8981\u5355\u6253\u72ec\u6597\u7684\u524d\u8a00\uff0c\u4e00\u6d8c\u800c\u4e0a\uff0c\u695a\u662d\u5357 \u9000\u5165\u89d2\u95e8\uff0c\u5ffd\u7136\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u51cc\u672a\u98ce\uff0c\u8ba9\u4f60\u9003\uff0c\u4f60\u4e5f\u9003\u4e0d\u51fa\u53bb\uff01\u201d\u628a\u624b\u4e00\u62db\uff0c\u6240\u6709\u536b \u58eb\u90fd\u8ddf\u7740\u4ed6\u9690\u5165\u91cd\u95e8\u53e0\u6237\u4e4b\u4e2d\u3002\u51cc\u672a\u98ce\u56db\u987e\u832b\u7136\uff0c\u5728\u8ff7\u5bab\u4e2d\u5de6\u7a7f\u53f3\u63d2\uff0c\u95f9\u4e86\u534a\u5929\uff0c\u59cb\u7ec8\u627e\u4e0d \u5230\u51fa\u8def\uff01 \u3000\u3000\u8fd9\u65f6\u5085\u9752\u4e3b\u7b49\u88ab\u56f4\u5728\u5916\u4e09\u95e8\uff0c\u9010\u6b65\u6df1\u5165\uff0c\u4e5f\u662f\u5de6\u7a7f\u53f3\u63d2\uff0c\u5140\u81ea\u627e\u4e0d\u5230\u51fa\u8def\uff0c\u8ff7\u5bab\u4e2d\u56db\u9762 \u57cb\u4f0f\u4e00\u9f50\u53d1\u52a8\uff0c\u5404\u5904\u8981\u51b2\uff0c\u90fd\u6709\u6e05\u519b\u4ed7\u7740\u5f13\u7bad\u6320\u94a9\uff0c\u963b\u4f4f\u8def\u53e3\uff0c\u5237\u5237\u5237\u53d1\u51fa\u7bad\u6765\uff0c\u5085\u9752\u4e3b\u5927 \u559d\u9053\uff1a\u201c\u9f20\u5b50\u6562\u5c14\uff01\u201d\u53cd\u624b\u4e00\u5251\uff0c\u5728\u77f3\u67f1\u4e0a\u5288\u4e86\u4e00\u9053\u88c2\u75d5\uff0c\u4e00\u8f6c\u8eab\uff0c\u55d6\u55d6\u55d6\uff0c\u5982\u71d5\u5b50\u743c\u7a7a\uff0c \u5411\u4eba\u591a\u5904\u53cd\u6251\u8fc7\u53bb\uff0c\u6842\u4ef2\u660e\u3001\u6613\u5170\u73e0\u4e24\u53e3\u5b9d\u5251\u5de6\u53f3\u5f00\u8def\uff0c\u5f53\u8005\u8f9f\u6613\uff01\u6e05\u519b\u53d1\u4e00\u58f0\u558a\u56db\u6563\u5954 \u9003\uff0c\u7fa4\u96c4\u8fde\u95ef\u51e0\u5904\uff0c\u53ea\u662f\u62e3\u4eba\u591a\u5904\u95ef\u53bb\uff0c\u8f6c\u4e86\u534a\u5929\uff0c\u5085\u9752\u4e3b\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u5feb\u505c\uff01\u201d\u6307\u7740\u8eab \u65c1\u77f3\u67f1\uff0c\u67f1\u4e0a\u5251\u75d5\u5b9b\u7136\uff0c\u8f6c\u4e86\u534a\u5929\uff0c\u7adf\u8f6c\u5230\u539f\u6765\u7684\u5730\u65b9\u6765\u4e86\uff01 \u3000\u3000\u5085\u9752\u4e3b\u9053\uff1a\u201c\u4e3a\u4eca\u4e4b\u8ba1\uff0c\u53ea\u597d\u6682\u65f6\u6309\u5175\u4e0d\u52a8\uff0c\u514d\u5f97\u767d\u8d39\u6c14\u529b\u3002\u201d\u7fa4\u96c4\u56f4\u6210\u4e86\u4e00\u9053\u5706\u5708\uff0c \u9996\u5c3e\u76f8\u8054\uff0c\u62b5\u5fa1\u4e71\u7bad\u3002\u53c8\u50f5\u6301\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u674e\u601d\u6c38\u53f9\u9053\u3002\u201d\u60f3\u4e0d\u5230\u4e00\u751f\u620e\u9a6c\uff0c\u5374\u4e0d\u660e\u4e0d\u767d\u6b7b \u5728\u8fd9\u91cc\uff01\u201d\u6b66\u743c\u7476\u5ffd\u9053\uff1a\u201c\u5218\u5927\u59d0\uff0c\u4f60\u6709\u6ca1\u6709\u5e26\u86c7\u7130\u7bad\uff1f\u6b64\u5730\u98ce\u9ad8\u7269\u71e5\uff0c\u653e\u706b\u70e7\u5b83\uff01\u201d\u674e\u601d \u6c38\u60f3\uff1a\u201c\u6211\u4eec\u4e0d\u77e5\u51fa\u8def\uff0c\u53ea\u6050\u6015\u653e\u706b\u4e4b\u540e\uff0c\u81ea\u5df1\u53cd\u88ab\u56f0\u5728\u706b\u6d77\u3002\u201d\u5085\u9752\u4e3b\u8001\u8c0b\u6df1\u7b97\uff0c\u4e5f\u662f\u6414 \u9996\u65e0\u7b56\uff0c\u6b63\u7126\u6025\u95f4\uff0c\u897f\u8fb9\u89d2\u95e8\uff0c\u6709\u4eba\u5927\u58f0\u545b\u559d\uff0c\u4e00\u4e2a\u9752\u5e74\u6b66\u58eb\uff0c\u5982\u98de\u8dd1\u51fa\uff0c\u6e05\u519b\u6b66\u58eb\u7eb7\u7eb7\u53eb \u9053\uff1a\u201c\u5468\u9752\uff0c\u4f60\u53d1\u75af\u4e86\u5417\uff0c\u4e71\u8dd1\u4ec0\u4e48\uff1f\u201d\u5085\u9752\u4e3b\u4e00\u58f0\u957f\u7b11\uff0c\u7a81\u7136\u62d4\u8eab\u4e00\u7eb5\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u821e\u6210 \u4e00\u9053\u94f6\u8679\uff0c\u534a\u7a7a\u98de\u4e0b\uff0c\u5de6\u624b\u4e00\u6293\uff0c\u6070\u5982\u5de8\u9e70\u6251\u5154\uff0c\u628a\u5468\u9752\u4e00\u628a\u6293\u8d77\uff0c\u53f3\u5251\u4e00\u8361\uff0c\u5c06\u8ffd\u6765\u7684\u6b66 \u58eb\uff0c\u626b\u5f97\u7ffb\u7ffb\u6eda\u6eda\uff0c\u8fd9\u4e00\u77ac\u95f4\uff0c\u6842\u4ef2\u660e\u3001\u6613\u5170\u73e0\u4e5f\u5df2\u5982\u98de\u743c\u5230\uff0c\u4e24\u9053\u5251\u5149\uff0c\u5de6\u53f3\u6a2a\u4f38\uff0c\u6709\u5982 \u65a9\u74dc\u5207\u83dc\uff0c\u987f\u65f6\u780d\u7ffb\u4e86\u5341\u51e0\u4e8c\u5341\u4eba\uff0c\u6e05\u519b\u53d1\u4e00\u58f0\u558a\uff0c\u53c8\u56db\u6563\u5954\u9003\u53bb\u4e86\uff01 \u3000\u3000\u539f\u6765\u5468\u9752\u88ab\u695a\u662d\u5357\u559d\u7834\u4e4b\u540e\uff0c\u5f97\u51cc\u672a\u98ce\u4e4b\u52a9\uff0c\u8d8a\u5899\u9003\u547d\uff0c\u5176\u4ed6\u6e05\u519b\u6b66\u58eb\uff0c\u5c1a\u672a\u77e5\u9053\u4ed6\u5df2 \u53cd\u53db\uff0c\u7adf\u7ed9\u4ed6\u6df7\u81f3\u5916\u9762\uff0c\u548c\u5085\u9752\u4e3b\u7b49\u4eba\u4f1a\u5408\u4e86\u3002 \u3000\u3000\u5085\u9752\u4e3b\u6551\u4e86\u5468\u9752\u4e4b\u540e\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u8def\uff1f\u201d\u5468\u9752\u9053\uff1a\u201c\u4e14\u8bd5\u4e00\u8bd5\u3002\u201d\u6839\u636e \u81ea\u5df1\u6240\u77e5\uff0c\u6307\u70b9\u4f17\u4eba\u5411\u751f\u95e8\u6740\u53bb\uff0c\u56db\u9762\u4e71\u7bad\u5bc6\u96c6\u5982\u96e8\uff0c\u695a\u662d\u5357\u7a81\u7136\u73b0\u8eab\uff0c\u626c\u58f0\u559d\u9053\uff1a\u201c\u5468 \u9752\uff0c\u4f60\u5c61\u53d7\u56fd\u6069\uff0c\u7adf\u6562\u53cd\u53db\uff01\u201d\u5f20\u5f13\u642d\u7bad\uff0c\u5506\u7684\u4e00\u7bad\u5c04\u6765\uff0c\u5085\u9752\u4e3b\u628a\u5468\u9752\u5f80\u5de6\u4e00\u5e26\uff0c\u957f\u5251\u4e00 \u683c,\u90a3\u679d\u7bad\u6b6a\u4e86\u51c6\u5934\uff0c\u5411\u65c1\u98de\u53bb\uff0c\u201c\u5693\u201d\u7684\u4e00\u58f0\uff0c\u7adf\u6ca1\u5165\u4e86\u77f3\u67f1\u4e4b\u4e2d\u3002\u5468\u9752\u5927\u9a87\uff0c\u695a\u662d\u5357\u7bad \u53d1\u8fde\u73e0\uff0c\u55d6\u55d6\u4e24\u7bad\uff0c\u63a5\u8fde\u7684\u51fa\uff0c\u6842\u4ef2\u660e\u626c\u624b\u4e24\u5708\u91d1\u73af\uff0c\u631f\u98ce\u547c\u5578\uff0c\u6253\u843d\u4e86\u8fde\u73e0\u7bad\uff0c\u5374\u662f\u4f59\u529b \u672a\u8870\uff0c\u5728\u7a7a\u4e2d\u547c\u547c\u65cb\u8f6c\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u624d\u8dcc\u843d\u6e05\u519b\u9635\u4e2d\u3002\u695a\u662d\u5357\u5927\u4e3a\u60ca\u5947\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u201c\u5c0f\u8f88 \u201c\uff0c\u522b\u6765\u672a\u4e45\uff0c\u529f\u529b\u7adf\u7136\u7cbe\u8fdb\u5982\u65af\uff01\u4ed6\u6309\u52a8\u673a\u5173\uff0c\u6253\u5f00\u4e00\u9053\u6697\u95e8\uff0c\u96e8\u9053\u4e0a\u7684\u5927\u95e8\u5ffd\u7136\u6253\u5f00\uff0c \u6e05\u519b\u6b66\u58eb\u5728\u8718\u86db\u7f51\u822c\u7684\u752c\u9053\u4e0a\u56db\u5904\u6e38\u8d70\uff0c\u65f6\u4e0d\u65f6\u53d1\u51fa\u51b7\u7bad\u3002\u5468\u9752\u5e26\u4f17\u4eba\u8f6c\u4e86\u51e0\u8f6c\uff0c\u5ffd\u7136\u53eb\u8d77 \u82e6\u6765\uff0c\u5bf9\u5085\u9752\u4e3b\u9053\uff1a\u201c\u95e8\u6237\u8f6c\u6362\uff0c\u9053\u8def\u7eb7\u6b67\uff0c\u6211\u8ba4\u4e0d\u51fa\u8def\u4e86\uff01\u201d\u5e03\u8fbe\u62c9\u8ff7\u5bab\uff0c\u539f\u662f\u7ea2\u8863\u5587\u561b \u6240\u9020\uff0c\u5141\u9898\u5230\u540e\uff0c\u53c8\u6309\u516b\u9635\u56fe\u5f62\uff0c\u6dfb\u8bbe\u95e8\u6237\u9053\u8def\uff0c\u5468\u9752\u6240\u8bc6\u7684\u53ea\u662f\u5176\u4e2d\u4e00\u90e8\uff0c\u5e76\u975e\u5168\u90e8\u5965 \u79d8\uff0c\u6240\u4ee5\u4ecd\u7ed9\u695a\u662d\u5357\u56f0\u4f4f\u3002 \u3000\u3000\u5085\u9752\u4e3b\u5b9a\u4e86\u5b9a\u795e\uff0c\u53ea\u542c\u5f97\u91cd\u95e8\u6df1\u6237\u4e4b\u4e2d\uff0c\u9f13\u89d2\u4e4b\u58f0\uff0c\u6b64\u8d77\u5f7c\u843d\uff0c\u60f3\u662f\u6e05\u519b\u8c03\u96c6\u7cbe\u9510\uff0c\u6765 \u548c\u81ea\u5df1\u7f20\u6597\uff0c\u6b63\u81ea\u5fc3\u6025\uff0c\u5ffd\u7136\u752c\u9053\u77f3\u9762\u4e00\u4e2a\u89d2\u95e8\uff0c\u6e05\u519b\u4e2d\u7a81\u7136\u5954\u51fa\u4e00\u4e2a\u8499\u9762\u4eba\u6765\uff0c\u695a\u662d\u5357\u5728 \u752c\u9053\u4e2d\u7684\u5927\u94c1\u95e8\u5185\u626c\u5f13\u4e00\u6307\uff0c\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u5c06\u4ed6\u64d2\u4e0b\uff0c\u683c\u6740\u4e0d\u8bba\uff01\u201d\u56db\u540d\u5fc3\u8179\u6b66\u58eb\u5982\u7bad\u79bb \u5f26\uff0c\u500f\u5730\u8ffd\u4e0a\uff0c\u5085\u9752\u4e3b\u8ddd\u79bb\u8fc7\u8fdc\uff0c\u65e0\u6cd5\u63f4\u6551\uff0c\u6115\u7136\u6ce8\u89c6\uff0c\u8fd9\u56db\u540d\u6b66\u58eb\u90fd\u662f\u7981\u536b\u519b\u4e2d\u6709\u6570\u7684\u9ad8 \u624b\uff0c\u695a\u662d\u5357\u4ee5\u4e3a\u5fc5\u7136\u624b\u5230\u64d2\u6765\u3002\u4e00\u540d\u6b66\u58eb\uff0c\u624b\u62a1\u98de\u6293\uff0c\u5f53\u5934\u6293\u5230\uff0c\u90a3\u8499\u9762\u4eba\u500f\u7136\u4f0f\u8eab\uff0c \u201c\u55d6\u201d\u7684\u4e00\u4e2a\u626b\u5802\u817f\uff0c\u4f7f\u98de\u6293\u7684\u4e00\u4e2a\u8e09\u8dc4\uff0c\u683d\u51fa\u51e0\u6b65\u4ee5\u5916\uff0c\u8ddf\u7740\u7684\u90a3\u4e2a\u6b66\u58eb\uff0c\u6446\u94a9\u9570\u67aa\u62e6 \u963b\uff0c\u4e5f\u662f\u5ffd\u5730\u201c\u6696\u5440\u201d\u4e00\u58f0\uff0c\u7ffb\u8eab\u683d\u5012\uff01\u7b2c\u4e09\u540d\u6b66\u58eb\u529f\u592b\u6700\u5f3a\uff0c\u63d0\u97ad\u5927\u53eb\uff0c\u98de\u821e\u800c\u524d\uff0c\u8499\u9762 \u4eba\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u53cd\u51b2\u8fc7\u6765\uff0c\u90a3\u540d\u6b66\u58eb\u60dc\u624b\u4e0d\u53ca\uff0c\u53cc\u97ad\u624d\u5c55\uff0c\u5df2\u662f\u7ed9\u4ed6\u70b9\u7740\u4e86\u201c\u81bb\u4e2d\u7a74\u201d\uff0c \u90a3\u540d\u6b66\u58eb\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u53cc\u97ad\u5815\u5730\uff0c\u8499\u9762\u4eba\u5c06\u4ed6\u4e3e\u8d77\uff0c\u4e00\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u5c06\u540e\u9762\u90a3\u540d\u6b66\u58eb\u4e5f\u626b \u51fa\u4e00\u5927\u5f00\u5916\u3002\u695a\u662d\u5357\u5927\u53eb\u201c\u653e\u7bad\uff01\u201d\u8499\u9762\u4eba\u5c06\u64d2\u7740\u7684\u90a3\u540d\u6b66\u58eb\uff0c\u5012\u63d0\u624b\u4e2d\uff0c\u821e\u52a8\u8d77\u6765\uff0c\u5954\u8dd1 \u5982\u98de\uff0c\u6e05\u5175\u6295\u9f20\u5fcc\u5668\uff0c\u53ea\u6709\u51e0\u4eba\u7a00\u7a00\u758f\u758f\u5730\u653e\u51fa\u51e0\u679d\u4e71\u7bad\uff0c\u8499\u9762\u4eba\u65e9\u5df2\u65cb\u98ce\u4e00\u822c\u5730\u8dd1\u5165\u4e86\u5085 \u9752\u4e3b\u90a3\u4e00\u7fa4\u4eba\u4e2d\u4e86\u3002 \u3000\u3000\u8499\u9762\u4eba\u4e3e\u624b\u6295\u8db3\u4e4b\u95f4\uff0c\u51fb\u5012\u56db\u540d\u6b66\u58eb\uff0c\u5085\u9752\u4e3b\u56fa\u7136\u6781\u611f\u8be7\u5f02\uff0c\u695a\u662d\u5357\u66f4\u662f\u6697\u6697\u5403\u60ca\uff0c\u8fd9 \u4eba\u7a7f\u7684\u662f\u7981\u536b\u519b\u670d\u9970\uff0c\u695a\u662d\u5357\u5374\u600e\u6837\u4e5f\u60f3\u4e0d\u8d77\u81ea\u5df1\u624b\u4e0b\u6709\u8fd9\u6837\u672c\u9886\u9ad8\u5f3a\u7684\u4eba\u7269\uff0c\u4e0d\u7981\u4e00\u9635\u5fc3 \u614c\u3002\u4e0d\u77e5\u81ea\u5df1\u4eba\u4e2d\uff0c\u9690\u6709\u591a\u5c11\u5978\u7ec6\uff1f \u3000\u3000\u8499\u9762\u4eba\u5c06\u624b\u4e2d\u6b66\u58eb\u5411\u752c\u9053\u4e0a\u4e00\u6454\uff0c\u5085\u9752\u4e3b\u62a2\u6b65\u8fce\u4e0a\uff0c\u90a3\u8499\u9762\u4eba\u4f4e\u58f0\u9002\uff1a\u201c\u5c0f\u5f1f\u662f\u97e9\u5fd7 \u90a6\u3002\u201d\u5085\u9752\u4e3b\u4e49\u60ca\u53c8\u559c\uff0c\u97e9\u5fd7\u90a6\u53c8\u9053\uff1a\u201c\u6211\u77e5\u9053\u65e7\u8ff7\u5bab\u7684\u51fa\u8def\uff0c\u65b0\u6dfb\u7684\u95e8\u6237\u9053\u8def\u6211\u5c31\u4e0d\u77e5\u9053 \u4e86\u3002\u201d\u5085\u9752\u4e3b\u65e0\u6687\u7ec6\u95ee\u539f\u7531\uff0c\u6025\u5fd9\u53eb\u4ed6\u548c\u5468\u9752\u89c1\u9762\uff0c\u5546\u8ba8\u8131\u8eab\u4e4b\u8ba1\u3002 \u3000\u3000\u539f\u6765\u97e9\u5fd7\u90a6\u53d1\u8a93\u8981\u6551\u51fa\u51cc\u672a\u98ce\uff0c\u6697\u4e2d\u51fa\u8d70\uff0c\u5bfb\u8bbf\u591a\u65e5\uff0c\u627e\u5230\u4e86\u88ab\u5141\u9898\u9a71\u9010\u51fa\u5bab\u7684\u4e00\u4e9b\u5587 \u561b\uff0c\u90a3\u4e9b\u5587\u561b\u548c\u5e03\u8fbe\u62c9\u5bab\u7684\u5587\u561b\uff0c\u4ecd\u662f\u4e92\u901a\u58f0\u6c14\uff0c\u800c\u88ab\u5141\u9898\u65b0\u7acb\u7684\u5927\u5587\u561b\uff0c\u6b63\u662f\u5f53\u5e74\u62a4\u9001\u820d \u5229\u5b50\u7684\u5b97\u8fbe\xb7\u5b8c\u771f\u3002\u97e9\u5fd7\u90a6\u5f53\u5e74\u673a\u7f18\u51d1\u5408\uff0c\u65e0\u610f\u4e3a\u897f\u85cf\u5587\u561b\u62a2\u56de\u5723\u7269\uff0c\u88ab\u8fce\u5230\u62c9\u8428\uff0c\u5f53\u4f5c\u6069 \u4eba\u6b3e\u5f85\uff0c\u6240\u4ee5\u82e5\u6709\u6240\u6c42\uff0c\u65e0\u4e0d\u7b54\u5e94\u3002\u97e9\u5fd7\u90a6\u60f3\u6cd5\u5077\u4f1a\u4e86\u5b97\u8fbe\xb7\u5b8c\u771f\uff0c\u9760\u4ed6\u7684\u5e2e\u52a9\uff0c\u5148\u662f\u626e\u6210 \u4e86\u5587\u561b\uff0c\u9690\u5728\u5e03\u8fbe\u62c9\u5bab\uff0c\u81f3\u5085\u9752\u4e3b\u7b49\u88ab\u56f0\u4e4b\u65f6\uff0c\u4ed6\u53c8\u5077\u4e86\u4e00\u5957\u7981\u536b\u519b\u7684\u670d\u9970\uff0c\u4e00\u76f4\u6df7\u5230\u8ff7\u5bab \u7684\u5916\u4e09\u95e8\uff0c\u4ed7\u7740\u602a\u62db\uff0c\u628a\u56db\u540d\u6b66\u58eb\u51fb\u5012\uff0c\u8fd9\u65f6\u548c\u5468\u9752\u4e92\u8bf4\u6240\u77e5\uff0c\u5192\u6d63\u83b2\u9759\u5fc3\u503e\u542c\uff0c\u5728\u5468\u9752\u539f \u6765\u753b\u7684\u7f8a\u76ae\u56fe\u4e0a\u4e1c\u753b\u897f\u753b\uff0c\u4e0d\u4e45\u7adf\u628a\u8ff7\u5bab\u7684\u51fa\u8def\u53c2\u900f\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u9053\uff1a\u201c\u6e05\u519b\u9510\u6c14\u5df2\u6298\uff0c\u6211\u4eec\u5148\u6740\u51fa\u53bb\u5427\u3002\u201d\u5085\u9752\u4e3b\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4e5f\u53ea\u597d\u5982\u6b64 \u4e86\u3002\u201d\u5192\u6d63\u83b2\u966a\u7740\u6842\u4ef2\u660e\u5f00\u8def\uff0c\u7387\u5148\u6251\u5411\u751f\u95e8\u6740\u51fa\uff0c\u695a\u662d\u5357\u4e0d\u77e5\u4ed6\u4eec\u5df2\u53c2\u900f\u8ff7\u5bab\u9053\u8def\uff0c\u52d2\u4ee4 \u6b66\u571f\uff0c\u4e0d\u51c6\u786c\u62fc\uff0c\u4f01\u56fe\u56f0\u6b7b\u4ed6\u4eec\uff0c\u6842\u4ef2\u660e\u7b49\u9f99\u86c7\u75be\u8d70\uff0c\u5982\u6c64\u6cfc\u96ea\uff0c\u8fde\u95ef\u8fc7\u51e0\u9053\u95e8\u6237\uff0c\u5230\u4e86\u5916 \u4e09\u95e8\uff0c\u6e05\u519b\u60ca\u89c9\uff0c\u5f85\u518d\u56f4\u4e0a\u6765\u65f6\uff0c\u54ea\u91cc\u8fd8\u62e6\u963b\u5f97\u4f4f\uff1f\u7fa4\u96c4\u5c31\u5982\u5341\u51e0\u5934\u731b\u864e\uff0c\u81ea\u5916\u4e09\u95e8\u4e00\u76f4\u6740 \u51fa\u4e86\u5e03\u8fbe\u62c9\u5bab\uff01 \u3000\u3000\u518d\u8bf4\u51cc\u672a\u98ce\u8f6c\u4e86\u534a\u5929\uff0c\u627e\u4e0d\u5230\u51fa\u8def\uff0c\u5916\u9762\u53c8\u6295\u4eba\u63a5\u5e94\uff0c\u53c8\u5026\u53c8\u997f\uff0c\u695a\u662d\u5357\u548c\u4e00\u7fa4\u6b66\u58eb\u500f \u5730\u51fa\u73b0\uff0c\u695a\u662d\u5357\u987e\u76fc\u81ea\u8c6a\uff0c\u5f97\u610f\u7b11\u9053\uff1a\u201c\u51cc\u672a\u98ce\u672c\u9886\u901a\u5927\uff0c\u4e5f\u8131\u4e0d\u4e86\u6211\u7684\u624b\u638c\uff0c\u770b\u4ed6\u5df2\u5448\u5026 \u5bb9\uff0c\u8c01\u66ff\u6211\u628a\u4ed6\u64d2\u4e0b\uff1f\u201d\u6b66\u58eb\u4eec\u6709\u4e9b\u662f\u9707\u60e7\u51cc\u672a\u98ce\u7684\u795e\u5a01\uff0c\u6709\u4e9b\u5219\u5bf9\u4ed6\u7531\u8877\u656c\u7231\uff0c\u4e0d\u613f\u4e0e\u4ed6 \u4ea4\u624b\uff0c\u9762\u9762\u76f8\u89d1\uff0c\u695a\u662d\u5357\u795e\u60c5\u4e0d\u60a6\uff0c\u6b63\u60f3\u53d1\u4f5c\uff0c\u6b66\u58eb\u7fa4\u4e2d\u84e6\u7136\u8dc3\u51fa\u56db\u4eba\uff0c\u4e09\u540d\u662f\u695a\u662d\u5357\u7684\u5fc3 \u8179\uff0c\u8fd8\u6709\u4e00\u4eba\u5219\u662f\u9a6c\u65b9\u3002\u51cc\u672a\u98ce\u4e00\u58f0\u957f\u5578\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u8fc5\u5982\u5954\u96f7\uff0c\u7167\u4e00\u540d\u6b66\u58eb\u624b\u8155\u5288\u4e0b\uff0c\u90a3 \u540d\u6b66\u58eb\u4e5f\u662f\u9ad8\u624b\uff0c\u9661\u5730\u95ea\u8eab\u8fdb\u62db\uff0c\u54ea\u77e5\u51cc\u672a\u98ce\u638c\u6cd5\u795e\u5999\uff0c\u4e00\u5288\u4e00\u6309\uff0c\u638c\u5fc3\u4e00\u9707\uff0c\u628a\u90a3\u540d\u6b66\u58eb \u6253\u7ffb\uff0c\u53e6\u4e24\u540d\u6b66\u58eb\u53cc\u5251\u9f50\u4e0a\uff0c\u9aa4\u7f29\u9aa4\u4f38\uff0c\u5982\u6bd2\u86c7\u5410\u4fe1\uff0c\u5206\u523a\u51cc\u672a\u98ce\u5de6\u53f3\u80a9\u80db\uff0c\u51cc\u672a\u98ce\u731b\u7136\u4e00 \u6251\u8eab\uff0c\u5f80\u4e0b\u6740\u8170\uff0c\u201c\u6241\u8e29\u86ee\u725b\u201d\uff0c\u7830\u7684\u4e00\u811a\uff0c\u63e3\u4e2d\u4e00\u540d\u6b66\u58eb\u7684\u53f3\u80ef\uff0c\u201c\u6251\u901a\u201d\u5982\u5012\u4e86\u534a\u5835 \u5899\uff0c\u6454\u5012\u5730\u4e0a\uff01\u51cc\u672a\u98ce\u8eab\u5f62\u9aa4\u957f\uff0c\u66b4\u559d\u4e00\u58f0\uff0c\u53e6\u4e00\u540d\u6b66\u58eb\u9a87\u7136\u4e00\u60ca\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u9000\u51fa\u4e24\u6b65\uff0c \u9a6c\u65b9\u53cc\u62f3\u9f50\u53d1\uff0c\u6251\u9762\u6253\u6765\uff0c\u51cc\u672a\u98ce\u89c1\u4ed6\u773c\u775b\u4e00\u970e\uff0c\u6599\u77e5\u7528\u610f\uff0c\u731b\u7136\u4e00\u7a9c\uff0c\u562d\u7684\u4e00\u638c\uff0c\u6253\u5728\u9a6c \u65b9\u80a9\u4e0a\uff0c\u8eab\u5b50\u4e00\u504f\uff0c\u524d\u80f8\u4e5f\u7ed3\u7ed3\u5b9e\u5b9e\u4e2d\u4e86\u9a6c\u65b9\u4e00\u62f3\uff0c\u6447\u6447\u6b32\u5012\uff0c\u5730\u4e0a\u7684\u4e24\u540d\u6b66\u58eb\uff0c\u8d81\u52bf\u7528\u811a \u4e00\u52fe\uff0c\u51cc\u672a\u98ce\u7ffb\u8eab\u6251\u5730\uff0c\u9a6c\u65b9\u7b49\u56db\u540d\u6b66\u58eb\u4e00\u9f50\u6251\u4e0a\uff0c\u56db\u4eba\u516b\u624b\uff0c\u5c06\u4ed6\u6309\u4f4f\uff0c\u51cc\u672a\u98ce\u53cc\u81c2\u4e00 \u632f\uff0c\u56db\u4eba\u6309\u637a\u4e0d\u4f4f\uff0c\u7ed9\u4ed6\u7ffb\u4e86\u8d77\u6765\uff0c\u6b63\u5728\u5403\u60ca\uff0c\u51cc\u672a\u98ce\u5ffd\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u53cc\u81c2\u4f4e\u5782\uff0c\u8bf4\u9053\uff1a \u201c\u62ff\u94c1\u7d22\u6765\u7f1a\u5427\uff01\u201d\u4e09\u540d\u6b66\u58eb\u5927\u559c\uff0c\u77e5\u4ed6\u8bf4\u8bdd\u7b97\u6570\uff0c\u5411\u540c\u4f34\u8981\u8fc7\u94c1\u7d22\uff0c\u5c06\u4ed6\u7f1a\u4e2a\u7ed3\u7ed3\u5b9e\u5b9e\u3002 \u3000\u3000\u695a\u662d\u5357\u89c1\u56db\u4eba\u9762\u9752\u5507\u80bf\uff0c\u9a6c\u65b9\u4f24\u5f97\u66f4\u91cd\uff0c\u5455\u51fa\u8840\u6765\uff0c\u695a\u662d\u5357\u6697\u9053\uff1a\u201c\u8fd9\u4e2a\u56de\u5b50\uff0c\u5012\u8fd8\u5356 \u529b\uff01\u201d\u5f53\u4e0b\u5c06\u51cc\u672a\u98ce\u660f\u7729\u7a74\u70b9\u4e86\uff0c\u53eb\u4e00\u4eba\u8bf7\u6210\u5929\u633a\u8fc7\u6765\uff0c\u547d\u6210\u5929\u633a\u4eb2\u81ea\u770b\u5b88\u51cc\u672a\u98ce\uff0c\u5e76\u5728\u4ed6 \u8033\u8fb9\u4f4e\u4f4e\u5730\u5429\u5490\u4e86\u51e0\u53e5\u8bdd\u3002 \u3000\u3000\u539f\u6765\u9a6c\u65b9\u548c\u5468\u9752\u662f\u5bf9\u597d\u53cb\uff0c\u5468\u9752\u53cd\u53db\uff0c\u9a6c\u65b9\u8bda\u6050\u88ab\u7591\uff0c\u6240\u4ee5\u6025\u6025\u4e0a\u524d\uff0c\u548c\u51cc\u672a\u98ce\u5bf9\u654c\uff0c \u51cc\u672a\u98ce\u4e5f\u731c\u51fa\u4ed6\u7684\u7528\u610f\uff0c\u53cd\u6b63\u81ea\u5df1\u8dd1\u4e0d\u6389\uff0c\u4e50\u5f97\u5356\u4e2a\u4eba\u60c5\uff0c\u4f46\u695a\u662d\u5357\u4e5f\u662f\u4e2a\u5927\u884c\u5bb6\uff0c\u4e0d\u80fd\u88ab \u4ed6\u770b\u7834\uff1b\u6240\u4ee5\u7528\u5916\u91cd\u5185\u8f7b\u7684\u624b\u6cd5\uff0c\u5c06\u9a6c\u65b9\u6253\u5f97\u5455\u8840\u3002 \u3000\u3000\u6210\u65e0\u633a\u628a\u51cc\u672a\u98ce\u62bc\u56de\u8ff7\u5bab\u7684\u5bc6\u5ba4\uff0c\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u5305\u836f\u7c89\uff0c\u64ac\u5f00\u51cc\u6797\u98ce\u7259\u5173\uff0c\u51b2\u5f00\u6c34\u7ed9\u4ed6 \u704c\u4e0b\uff0c\u51cc\u672a\u98ce\u60a0\u60a0\u9192\u8f6c\uff0c\u53ea\u89c9\u6d51\u8eab\u65e0\u529b\u3002 \u3000\u3000\u6210\u5143\u633a\u704c\u51cc\u672a\u98ce\u5403\u7684\u662f\u5927\u5185\u5723\u836f\uff0c\u4e13\u4e3a\u6446\u5e03\u6b66\u529f\u9ad8\u5f3a\u4e4b\u4eba\u7528\u7684\uff0c\u5403\u4e86\u4e4b\u540e\uff0c\u5982\u4e2d\u70c8\u9152\uff0c \u660f\u7729\u65e0\u529b\uff0c\u66f4\u517c\u6210\u5929\u633a\u6309\u7740\u53cc\u7b14\uff0c\u5b88\u5728\u65c1\u8fb9\uff0c\u51cc\u672a\u98ce\u7eb5\u6709\u901a\u5929\u672c\u9886\uff0c\u4e5f\u96be\u9003\u4e86\uff0c\u8981\u77e5\u6210\u5929\u633a \u7684\u672c\u9886\uff0c\u4e0e\u695a\u662d\u5357\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u5373\u5728\u5e73\u65f6\uff0c\u4ed6\u4e5f\u53ef\u4ee5\u4e0e\u51cc\u672a\u98ce\u7f20\u6597\u6570\u5341\u56de\u5408\uff0c\u4f55\u51b5\u5728\u51cc\u672a\u98ce \u670d\u836f\u4e4b\u540e\u3002 \u3000\u3000\u518d\u8bf4\u5085\u9752\u4e3b\u7b49\u56de\u5230\u5bd3\u6240\u4e4b\u540e\uff0c\u518d\u5546\u8425\u6551\u4e4b\u7b56\uff0c\u5085\u9752\u4e3b\u9053\uff1a\u201c\u5982\u4eca\u8ff7\u5bab\u9053\u8def\u5df2\u660e\uff0c\u7d22\u6027\u5e72 \u5b83\u4e00\u573a\u5927\u7684\uff0c\u628a\u62c9\u8428\u57ce\u5185\u6211\u4eec\u7684\u4eba\u90fd\u8c03\u96c6\u8d77\u6765\uff0c\u4e5f\u53ef\u6709\u4e24\u4e09\u5343\u4eba\u3002\u201d\u674e\u601d\u6c38\u9053\uff1a\u201c\u5174\u5e08\u52a8 \u4f17\uff0c\u53ea\u6050\u653b\u8fdb\u53bb\u65f6\uff0c\u51cc\u5927\u4fa0\u5df2\u7ecf\u53d7\u5bb3\u3002\u201d\u4f17\u4eba\u5546\u8bae\u672a\u5b9a\uff0c\u5218\u90c1\u82b3\u5341\u5206\u9893\u4e27\uff0c\u72ec\u81ea\u5165\u623f\u53bb\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u65e9\u6668\uff0c\u5218\u90c1\u82b3\u5c1a\u81ea\u6101\u80a0\u767e\u7ed3\uff0c\u5367\u5e8a\u672a\u8d77\u3002\u5ffd\u542c\u5f97\u6709\u4eba\u5728\u7a97\u5916\u5f39\u4e86\u51e0\u4e0b\uff0c\u6b66\u743c\u7476\u538b \u4f4e\u4e86\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5218\u5927\u59d0\uff0c\u697c\u4e0b\u6709\u4e00\u4e2a\u4eba\u8981\u770b\u4f60\u3002\u201d\u8fd9\u4e9b\u5929\u6765\uff0c\u522b\u4eba\u548c\u5218\u90c1\u82b3\u8bf4\u8bdd\u65f6\uff0c\u90fd\u4e0d \u81ea\u89c9\u5730\u91c7\u7528\u4e86\u8fd9\u79cd\u8bf4\u8bdd\u58f0\u8c03\uff0c\u6765\u8868\u793a\u4ed6\u4eec\u5fc3\u4e2d\u5171\u540c\u7684\u60b2\u75db\u3002 \u3000\u3000\u5728\u697c\u4e0a\u90a3\u95f4\u5c0f\u5c0f\u7684\u5ba2\u5ba4\u91cc\uff0c\u5218\u90c1\u82b3\u770b\u89c1\u4e00\u4e2a\u767d\u5e03\u7f20\u5934\u7684\u6c49\u5b50\u7b14\u76f4\u5730\u7ad9\u5728\u623f\u95f4\u4e2d\u592e\uff0c\u5085\u9752 \u4e3b\u5728\u65c1\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u597d\u6c49\u540d\u53eb\u9a6c\u65b9\uff0c\u662f\u76d1\u5b88\u51cc\u672a\u98ce\u7684\u536b\u58eb\u3002\u201d \u3000\u3000\u9a6c\u65b9\u5b9a\u795e\u671b\u7740\u5218\u90c1\u82b3\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u5c31\u662f\u5929\u5730\u4f1a\u7684\u603b\u8235\u4e3b\u5218\u5927\u59d0\u5427\uff1f\u6211\u7ed9\u4f60\u5e26\u6765\u4e86\u4e00\u5c01 \u4fe1\u3002\u201d \u3000\u3000\u201c\u4e00\u5c01\u2026\u2026\u4fe1\uff1f\u201d\u5218\u90c1\u82b3\u6709\u70b9\u53d1\u6296\uff0c\u628a\u624b\u653e\u5728\u684c\u4e0a\u7a33\u5b9a\u81ea\u5df1\u3002 \u3000\u3000\u9a6c\u65b9\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u51cc\u5927\u4fa0\u54ac\u7834\u6307\u5934\u5192\u9669\u5199\u7684\uff0c\u4f46\u6211\u6765\u4e86\u4e4b\u540e\uff0c\u53ef\u662f\u4e0d\u80fd\u7ed9\u4f60\u5e26\u4fe1\u56de \u53bb\u4e86\u3002\u201d \u3000\u3000\u5218\u90c1\u82b3\u62ff\u7740\u90a3\u5c01\u4fe1\uff0c\u9ed8\u9ed8\u5730\u7ad9\u4e86\u4e00\u4f1a\u513f\uff0c\u7136\u540e\u5728\u7a97\u5b50\u8fb9\u6df1\u6df1\u7684\u5438\u4e86\u4e00\u53e3\u65b0\u9c9c\u7684\u7a7a\u6c14\uff0c\u6253 \u5f00\u4e86\u4fe1\uff0c\u53ea\u89c1\u4fe1\u4e0a\u7684\u8840\u5b57\u6b6a\u6b6a\u659c\u659c\uff0c\u53ef\u4ee5\u60f3\u89c1\u5199\u65f6\u624b\u6307\u7684\u98a4\u6296\uff0c\u800c\u4e14\u6709\u51e0\u5904\u5b57\u8ff9\u4e5f\u5df2\u7ecf\u6a21\u7cca \u4e86\u3002\u5218\u90c1\u82b3\u9ed8\u9ed8\u5ff5\u9053\uff1a \u3000\u3000\u743c\u59d0\uff1a \u3000\u3000\u4eca\u591c\u4e43\u5f1f\u6bd5\u547d\u4e4b\u671f\uff0c\u6bd5\u547d\u4e4b\u524d\uff0c\u5f53\u4ee5\u4e8b\u5b9e\u544a\u8bc9\u4f60\u3002\u4e8c\u5341\u5e74\u80fd\uff0c\u4e0e\u59d0\u94b1\u5858\u89c2\u6f6e\uff0c\u59d0\u5c1d\u620f \u66f0\uff1a\u2018\u82e5\u4eba\u5982\u6f6e\u4e4b\u6709\u4fe1\uff0c\u7eb5\u5728\u5175\u8352\u9a6c\u4e71\u4e4b\u4e2d\uff0c\u6b7b\u522b\u5750\u79bb\uff0c\u5730\u8001\u5929\u8352\uff0c\u4f59\u4ea6\u5fc5\u5f85\u4f60\u5f52\u6765\u4e5f\u3002\u201d \u55df\u4e4e\uff0c\u6b64\u4e00\u620f\u8a00\uff0c\u7adf\u6210\u4e8b\u5b9e\u3002\u59d0\u59d0\u4e0d\u5fc5\u4e3a\u5f53\u5e74\u4e4b\u8bef\u4f1a\u4f24\u5fc3\uff0c\u59d0\u4e4b\u771f\u60c5\uff0c\u5df2\u5982\u94b1\u5858\u4e4b\u6f6e\uff0c\u8db3\u6da4 \u5341\u500d\u4e4b\u8bef\u4f1a\u800c\u6709\u4f59\u3002\u59d0\u4ea6\u4e0d\u5fc5\u4e3a\u5f1f\u4f24\u5fc3\uff0c\u4e00\u51cc\u672a\u98ce\u6b7b\uff0c\u5341\u51cc\u672a\u98ce\u751f\uff0c\u5fd7\u58eb\u4e49\u4eba\uff0c\u72b9\u5982\u6625\u8349\uff0c \u829f\u4e4b\u4e0d\u5c3d\uff0c\u70e7\u4e4b\u91cd\u751f\u4e5f\u3002\u6240\u60dc\u8005\u552f\u5929\u5c71\u8d4f\u96ea\u4e4b\u7ea6\uff0c\u53ea\u80fd\u671f\u4e4b\u6765\u751f\u77e3\uff01 \u3000\u3000\u7a46\u90ce\u7edd\u7b14 \u3000\u3000\u7eb8\u4e0a\u7684\u5b57\u8ff9\u7a81\u7136\u6a21\u7cca\u5f97\u50cf\u4e00\u7247\u4e91\u96fe\uff0c\u5979\u53c8\u4e00\u6b21\u5931\u53bb\u4e86\u4ed6\u2014\u2014\u53c8\u4e00\u6b21\u5931\u53bb\u4e86\u4ed6\uff01\u5979\u832b\u7136\u5730 \u4f38\u7740\u4e24\u624b\uff0c\u597d\u50cf\u5929\u5c71\u7684\u6797\u5cf0\u6b63\u538b\u5728\u5979\u7684\u5fc3\u4e0a\u4e00\u4e00\u4fe1\u7b3a\u843d\u5230\u5730\u4e0a\u4e86\u3002 \u3000\u3000\u201c\u743c\u201d\u662f\u5218\u90c1\u82b3\u7684\u6821\u8774\uff0c\u800c\u201c\u7a46\u90ce\u201d\u5219\u662f\u51cc\u672a\u98ce\u7684\u6821\u8774\uff0c\u4ed6\u7684\u771f\u540d\u53eb\u505a\u6881\u7a46\u90ce\uff0c\u7956\u5148\u662f \u897f\u5357\u6765\u7684\u79fb\u6c11\uff0c\u6240\u4ee5\u53d6\u201c\u73e0\u7a46\u90ce\u739b\u5cf0\u201d\u4e2d\u7684\u4e8c\u5b57\u7ed9\u4ed6\u547d\u540d\u3002 \u3000\u3000\u94c5\u4e00\u6837\u7684\u6c89\u91cd\u7edd\u671b\u7684\u611f\u60c5\u5c06\u5218\u90c1\u82b3\u538b\u4f4f\u4e86\uff0c\u5979\u501a\u5728\u7a97\u524d\uff0c\u5bc2\u7136\u4e0d\u52a8\uff0c\u9762\u8272\u60e8\u767d\uff0c\u6709\u5982\u5e7d \u7075\uff0c\u4f17\u4eba\u51dd\u671b\u7740\u5979\uff0c\u4e0d\u6562\u8bf4\u8bdd\uff0c\u5728\u8fd9\u65f6\u5019\uff0c\u4e00\u5207\u5b89\u6170\u7684\u8a00\u8bed\uff0c\u90fd\u662f\u591a\u4f59\u7684\u4e86\u3002\u6b66\u743c\u7476\u4e00\u53ea\u624b \u8f7b\u8f7b\u642d\u5728\u5979\u7684\u80a9\u4e0a\uff0c\u51c4\u7136\u5730\u7ed9\u5979\u6574\u7406\u51cc\u4e71\u7684\u4e91\u9b03\u3002 \u3000\u3000\u5085\u9752\u4e3b\u6084\u6084\u5730\u5c06\u9a6c\u65b9\u62c9\u8fc7\u4e00\u8fb9\uff0c\u95ee\u9053\uff1a\u201c\u51cc\u5927\u4fa0\u4eca\u665a\u53ef\u6709\u5371\u9669\u3002\u201d\u9a6c\u65b9\u4e0d\u5b89\u5730\u6414\u4e86\u6414 \u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u573a\u4e8b\u53d1\u751f\u8fd9\u540e\uff0c\u695a\u662d\u5357\u5bb3\u6015\u6781\u4e86\uff0c\u6bd4\u5728\u51b0\u6cb3\u6076\u6218\u7ed9\u51cc\u672a\u98ce\u8ffd\u8feb\u65f6\u8fd8\u8981\u5bb3\u6015\uff01 \u695a\u662d\u5357\u5728\u8fd9\u573a\u4e8b\u4e2d\u770b\u5f97\u51fa\u6765\uff0c\u8bb8\u591a\u6b66\u58eb\u4e0d\u613f\u4e0e\u51cc\u672a\u98ce\u4e3a\u654c\uff0c\u6ca1\u6709\u4ec0\u4e48\u6bd4\u5185\u90e8\u7684\u79bb\u5fc3\u66f4\u4ee4\u4eba\u53ef \u6015\u7684\u4e86\uff01\u6211\u542c\u5f97\u4ed6\u548c\u6210\u5929\u633a\u5546\u8bae\uff0c\u4e3a\u4e86\u8fd9\u4e2a\u7f18\u6545\uff0c\u4eca\u591c\u5b50\u65f6\uff0c\u5c31\u8981\u628a\u51cc\u5927\u4fa0\u6084\u6084\u5904\u51b3\uff0c\u514d\u5f97 \u4ed6\u5728\u7262\u72f1\u4e2d\u4e5f\u2018\u86ca\u60d1\u4eba\u5fc3\u3002\u201d\u5085\u9752\u4e3b\u5782\u4e0b\u4e86\u5934\uff0c\u989d\u4e0a\u7684\u76b1\u7eb9\u4e5f\u4f3c\u5728\u8f7b\u8f7b\u8df3\u52a8\uff0c\u663e\u7136\u4ed6\u662f\u9677\u5728 \u6df1\u6df1\u7684\u601d\u7d22\u4e4b\u4e2d\u53bb\u4e86\u3002 \u3000\u3000\u5728\u6b7b\u4e00\u6837\u7684\u9759\u5bc2\u4e2d\uff0c\u97e9\u5fd7\u90a6\u7a81\u7136\u8dd1\u4e86\u8fdb\u6765\uff0c\u4ed6\u5df2\u542c\u5230\u5173\u4e8e\u51cc\u672a\u98ce\u7684\u6076\u4fe1\uff0c\u6025\u5fd9\u6765\u627e\u5218\u90c1 \u82b3\uff0c\u4e00\u8fdb\u4e86\u95e8\uff0c\u9a6c\u4e0a\u4e3a\u90a3\u79cd\u9759\u7a46\u54c0\u4f24\u7684\u6c14\u6c1b\u6240\u9707\u9a87\uff0c\u7981\u4e0d\u4f4f\u5c06\u5218\u90c1\u82b3\u4e00\u628a\u62c9\u4f4f\uff0c\u7528\u6025\u4fc3\u800c\u98a4 \u6296\u7684\u58f0\u8c03\u95ee\u9053\uff1a \u3000\u3000\u201c\u5218\u5927\u59d0\uff01\u6211\u7684\u5929\uff01\u4f60\u600e\u4e48\u5566\uff1f\u55ef\uff0c\u4f60\u6d41\u4e86\u6cea\uff1f\u6211\u8bb0\u5f97\u4f60\u662f\u4ece\u6765\u4e0d\u54ed\u7684\u5440\uff01\u51cc\u5927\u4fa0\u7684 \u4e8b\uff0c\u6211\u2026\u2026\u6211\u2026\u201d \u3000\u3000\u5218\u90c1\u82b3\u84e6\u7136\u62ac\u8d77\u4e86\u65e0\u795e\u7684\u773c\u775b\uff0c\u6fc0\u8d8a\u5730\u8bf4\u9053\uff1a\u201c\u771f\u7684\u662f\u4ed6\u5440\uff0c\u662f\u4ed6\uff0c\u662f\u4ed6\uff01\u6211\u4e8c\u5341\u5e74 \u524d\uff0c\u548c\u4ed6\u5728\u94b1\u5858\u6c5f\u8fb9\u770b\u6f6e\u7684\u90a3\u4e2a\u5927\u5b69\u5b50\u5440\uff01\u201d\u5979\u6446\u8131\u4e86\u97e9\u5fd7\u90a6\u7684\u624b\uff0c\u5f2f\u4e0b\u8eab\u8eaf\uff0c\u62fe\u8d77\u90a3\u5f20\u6cbe \u6ee1\u8840\u6cea\u7684\u4fe1\u7b3a\uff0c\u5306\u5306\u585e\u8fdb\u888b\u91cc\uff0c\u67d4\u8f6f\u65e0\u529b\u5730\u8bf4\u9053\uff1a\u201c\u5fd7\u90a6\uff0c\u4f60\u53bb\u5427\uff0c\u6211\u73b0\u5728\u4ec0\u4e48\u4e5f\u4e0d\u60f3\u8bf4 \u4e86\uff01\u201d \u3000\u3000\u97e9\u5fd7\u90a6\u4e0d\u6562\u8bf4\u8bdd\uff0c\u53ea\u51c4\u7136\u5730\u54ac\u770b\u81ea\u5df1\u7684\u5634\u5507\uff0c\u4ed6\u7981\u4e0d\u4f4f\u53c8\u4e00\u6b21\u5730\u6cc4\u6f0f\u4e86\u81ea\u5df1\u7684\u771f\u60c5\uff0c\u8fd9 \u662f\u81ea\u548c\u5218\u90c1\u82b3\u91cd\u89c1\u4e4b\u540e\uff0c\u4e00\u76f4\u5c31\u538b\u5236\u7740\u7684\u771f\u60c5\u3002\u7136\u800c\u5979\u8fde\u6ce8\u610f\u90fd\u6ca1\u6709\u6cbe\u610f\u5230\uff01\u84e6\u7136\u4ed6\u53c8\u60f3\u8d77 \u51e0\u5e74\u4e4b\u524d\uff0c\u4ed6\u66fe\u6000\u7591\u8fc7\u51cc\u672a\u98ce\u4ee5\u201c\u65b0\u77e5\u201d\u800c\u95f4\u201c\u65e7\u4ea4\u201d\u4e4b\u4e8b\uff0c\u4e0d\u7981\u9762\u7ea2\u76f4\u900f\u8033\u6839\u3002\u539f\u6765\u51cc\u672a \u98ce\u7adf\u7136\u662f\u5979\u513f\u65f6\u7684\u597d\u53cb\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u6084\u6084\u5730\u53c8\u9000\u4e86\u51fa\u53bb\uff0c\u5085\u9752\u4e3b\u5728\u6c89\u601d\uff0c\u5176\u4ed6\u7684\u4eba\u56f4\u62e2\u7740\u5218\u90c1\u82b3\uff0c\u6ca1\u6709\u4eba\u6ce8\u610f\u5230\u4ed6\u3002 \u6b63\u662f\uff1a \u3000\u3000\u201c\u5fc3\u4e8b\u96be\u8a00\u8c01\u53ef\u89e3\uff0c\u5341\u5e74\u82e6\u604b\u955c\u4e2d\u82b1\u3002\u201d \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u5eff\u4e5d\u7ae0\u3000\u65e0\u9650\u6df1\u60c5\u3000\u820d\u5df1\u4e3a\u4eba\u7518\u66ff\u6b7b\u3000\u7edd\u62db\u96ea\u6068\u3000\u95ef\u5173\u7834\u654c\u52c7\u9664\u5978 \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u4e8c\u5341\u4e5d\u56de \u65e0\u9650\u6df1\u60c5\u3000\u820d\u5df1\u4e3a\u4eba\u7518\u66ff\u6b7b \u7edd\u62db\u96ea\u6068\u3000\u95ef\u5173\u7834\u654c\u52c7\u9664\u5978 \u3000\u3000\u97e9\u5fd7\u90a6\u5306\u5306\u5730\u8dd1\u5230\u4e86\u9644\u8fd1\u7684\u4e00\u95f4\u5587\u561b\u5bfa\u4e2d\uff0c\u95ee\u5587\u561b\u9053\uff1a\u201c\u4f60\u6709\u91d1\u521b\u836f\u5417\uff1f\u201d\u5927\u5587\u561b\u9053\uff1a \u201c\u6709\u7684\uff0c\u4f60\u8981\u6765\u7ed9\u670b\u53cb\u6577\u4f24\u5417\uff1f\u201d\u97e9\u5fd7\u90a6\u8fde\u58f0\u50ac\u9053\uff1a\u201c\u5feb\u70b9\u7ed9\u6211\uff01\u201d\u897f\u85cf\u5587\u561b\u7684\u91d1\u521b\u836f\u529f\u6548 \u751a\u5927\uff0c\u97e9\u5fd7\u90a6\u8981\u4e86\u8fc7\u6765\uff0c\u8dd1\u8fdb\u4ed6\u5bc4\u5bd3\u8fc7\u7684\u5c0f\u623f\u5185\uff0c\u5c06\u5c0f\u5587\u561b\u63a8\u4e86\u51fa\u53bb\uff0c\u84e6\u5730\u5173\u8d77\u623f\u95e8\uff0c\u62bd\u51fa \u8f9b\u9f99\u5b50\u9001\u7ed9\u4ed6\u7684\u90a3\u628a\u5929\u9f99\u6d3e\u7684\u9547\u5c71\u5b9d\u5251\u6765\uff01\u5bf9\u7740\u5899\u4e0a\u90a3\u9762\u53d1\u5149\u7684\u94dc\u955c\uff0c\u51dd\u89c6\u4e86\u4e00\u9635\uff0c\u5251\u950b\u5411 \u4e0a\uff0c\u500f\u5730\u55d6\u55d6\u4e24\u5251\uff0c\u5728\u9762\u4e0a\u5212\u8fc7\uff0c\u5212\u4e86\u4e24\u9053\u6df1\u6df1\u7684\u521b\u53e3\uff0c\u9c9c\u8840\u6c69\u6c69\u6d41\u4e0b\uff0c\u7981\u4e0d\u4f4f\u75db\u5f97\u53eb\u51fa\u58f0 \u6765\uff01\u5927\u5587\u561b\u5bf9\u97e9\u5fd7\u90a6\u7684\u884c\u52a8\u672c\u5c31\u89c9\u5f97\u5947\u602a\uff0c\u8fd9\u65f6\u6765\u5230\u623f\u5916\uff0c\u542c\u5230\u91cc\u9762\u547b\u541f\u4e4b\u58f0\uff0c\u6025\u5fd9\u4e00\u811a\u8e22 \u7834\u623f\u95e8\u201c\u54d7\u201d\u7684\u4e00\u58f0\u53eb\u9053\uff1a\u201c\u5fd7\u90a6\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u201d\u97e9\u5fd7\u90a6\u5b9d\u5251\u5f53\u5577\u4e00\u6837\u8dcc\u843d\u5730\u4e0a\uff0c\u5927\u5587\u561b\u8d76 \u5fd9\u4e0a\u524d\u5c06\u4ed6\u62b1\u4f4f\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u75af\u4e86\u5417\uff1f\u201d\u97e9\u5fd7\u90a6\u53d6\u51fa\u91d1\u521b\u5999\u836f\uff0c\u5927\u5587\u561b\u7ed9\u4ed6\u6577\u4e0a\uff0c\u8fc7\u4e86\u4e00 \u9635\uff0c\u97e9\u5fd7\u90a6\u8fd9\u624d\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u9a6c\u4e0a\u5e26\u6211\u53bb\u89c1\u6d3b\u4f5b\uff01\u201d\u5927\u5587\u561b\u83ab\u540d\u5176\u5999\uff0c\u97e9\u5fd7\u90a6\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u8bf7\u4f60\u770b\u5728\u820d\u5229\u5b50\u7684\u4efd\u4e0a\uff0c\u7167\u6211\u7684\u8bdd\u53bb\u505a\uff0c\u4e0d\u8981\u53d1\u95ee\u3002\u201d\u5927\u5587\u561b\u89c1\u4ed6\u795e\u667a\u6e05\u9192\uff0c\u4e0d\u662f\u75af\u72c2\uff0c \u8fdf\u7591\u4e86\u4e00\u4f1a\uff0c\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u5c45\u58eb\u662f\u6211\u4eec\u7684\u5927\u6069\u4eba\uff0c\u6562\u4e0d\u9075\u547d\uff01\u201d\u53d6\u8fc7\u4e00\u4ef6\u9ed1\u6be1\u5927\u8863\uff0c\u7ed9\u97e9\u5fd7 \u90a6\u62ab\u4e0a\uff0c\u62d6\u7740\u4ed6\u6084\u6084\u5730\u4ece\u540e\u95e8\u8d70\u51fa\u3002 \u3000\u3000\u518d\u8bf4\u51cc\u672a\u98ce\u81ea\u77e5\u6bd5\u547d\u671f\u8fd1\uff0c\u867d\u662f\u65f7\u4e16\u82f1\u96c4\uff0c\u4e5f\u7981\u4e0d\u4f4f\u6709\u6240\u7275\u5ff5\u3002\u201c\u6211\u592a\u6b8b\u9177\u4e86\uff0c\u4e0d\u5e94\u8be5 \u90a3\u4e48\u5bf9\u5f85\u743c\u59d0\u7684\uff01\u201d\u4ed6\u60f3\u8d77\u676d\u5dde\u5c11\u5e74\u65f6\u6e38\u4e50\u7684\u65e5\u5b50\uff0c\u60f3\u8d77\u94b1\u5858\u6c5f\u5927\u6f6e\u4e4b\u540e\uff0c\u60f3\u8d77\u6a2a\u8fc7\u4e91\u8d35\u9ad8 \u539f\u65f6\u5218\u90c1\u82b3\u51c4\u6028\u7684\u773c\u5149\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u84e6\u7136\u53c8\u60f3\u8d77\u97e9\u5fd7\u90a6\u90a3\u8bda\u6073\u8001\u5b9e\u7684\u6a21\u6837\uff0c\u4e00\u4e2a\u5ff5\u5934\uff0c\u7a81\u7136 \u4ece\u5fc3\u4e2a\u63a0\u8fc7\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u5728\u6b7b\u524d\u7ed9\u4ed6\u4eec\u64ae\u5408\u5462\uff1f\u201d\u4ed6\u601d\u7d22\u7740\u6709\u6ca1\u6709\u673a\u4f1a\u518d\u5199\u4e00\u5c01\u8840\u4e66\uff0c\u6258 \u77e5\u5fc3\u7684\u536b\u58eb\u5728\u4ed6\u6b7b\u540e\u5e26\u51fa\u3002\u56db\u5468\u9ed1\u9edd\u9edd\u7684\uff0c\u53ea\u6709\u56db\u4e2a\u89d2\u843d\u53d1\u51fa\u70db\u5149\u3002\u4ed6\u62ac\u8d77\u4e86\u5934\u95ee\u6210\u5929\u633a \u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u4e86\uff1f\u201d\u6210\u5929\u633a\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5c31\u662f\u5348\u591c\uff0c\u51cc\u672a\u98ce\uff0c\u4f60\u4e34\u6b7b\u524d\u6709\u4ec0\u4e48 \u9057\u8a00\u8981\u6211\u7ed9\u4f60\u5e26\u51fa\u53bb\u5417\uff1f\u201d\u6210\u5929\u633a\u662f\u6e05\u5ef7\u7684\u6b7b\u58eb\uff0c\u51cc\u672a\u98ce\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u544a\u8bc9\u695a\u662d\u5357\uff0c\u50cf\u4ed6 \u4e00\u6837\u4e3a\u80e1\u864f\u4f5c\u9e70\u72ac\u7684\u4eba\uff0c\u82e5\u4e0d\u53ca\u65e9\u56de\u5934\uff0c\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff01\u201d\u6210\u5929\u633a\u7b11\u9053\uff1a\u201c\u770b\uff0c\u4f60\u628a\u4f60\u7684\u5e08 \u5144\u6068\u5f97\u90a3\u6837\uff0c\u4f60\u7684\u5e08\u5144\u5012\u8fd8\u60e6\u8bb0\u7740\u4f60\u5462\uff01\u4ed6\u5728\u4f60\u4e34\u6b7b\u4e4b\u524d\uff0c\u8fd8\u51c6\u6d3b\u4f5b\u6765\u7ed9\u4f60\u7977\u544a\uff0c\u6309\u85cf\u6c11\u7684 \u98ce\u4fd7\uff0c\u706b\u5316\u4f60\u7684\u5c38\u9ab8\uff0c\u4f60\u542c\uff0c\u5916\u9762\u7684\u811a\u6b65\u58f0\uff0c\u4ed6\u4eec\u6b64\u523b\u5df2\u7ecf\u6765\u4e86\uff0c\u55ef\uff0c\u6bd4\u539f\u5b9a\u7684\u65f6\u95f4\u8fd8\u8981\u65e9 \u54e9\uff01\u201d \u3000\u3000\u5141\u9898\u65b0\u7acb\u7684\u6d3b\u4f5b\u5b97\u8fbe\xb7\u5b8c\u771f\uff0c\u9ec4\u660f\u65f6\u5206\u4e13\u8bda\u53bb\u62dc\u8bbf\u5141\u9898\uff0c\u4ed6\u8bf4\u5e03\u8fbe\u62c9\u5bab\u662f\u5587\u561b\u6559\u7684\u5723 \u5bfa\uff0c\u82e5\u7136\u5728\u91cc\u9762\u5904\u51b3\u4eba\u72af\uff0c\u4e00\u5b9a\u8981\u5f97\u5230\u4ed6\u4eec\u7684\u540c\u610f\uff0c\u5e76\u5e94\u51c6\u4ed6\u4eec\u53bb\u505a\u7977\u544a\u3002\u5141\u9898\u77e5\u9053\u695a\u662d\u5357 \u4eca\u665a\u8981\u5728\u8ff7\u5bab\u5c06\u51cc\u672a\u98ce\u6084\u6084\u5904\u51b3\uff0c\u9887\u4e3a\u60ca\u8bb6\u5b97\u8fbe\xb7\u5b8c\u771f\u6d88\u606f\u7684\u7075\u901a\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5728\u8fd9\u4e9b\u5c0f \u4e8b\u4e0a\u5012\u4e0d\u59a8\u5c0a\u91cd\u4ed6\u4eec\u7684\u4e60\u60ef\uff0c\u4e5f\u4fbf\u70b9\u9996\u7b54\u5e94\u4e86\u3002\u8ff7\u5bab\u4e2d\u5230\u5904\u90fd\u6709\u6b66\u827a\u9ad8\u5f3a\u7684\u536b\u58eb\u628a\uff0c\u770b\u5b88\u51cc \u672a\u98ce\u7684\u66f4\u662f\u4e00\u7b49\u4e00\u7684\u5927\u5185\u9ad8\u624b\u6210\u5929\u633a\uff0c\u8c05\u4e5f\u4e0d\u4f1a\u51fa\u4ec0\u4e48\u4e71\u5b50\u3002 \u3000\u3000\u51cc\u672a\u98ce\u542c\u6210\u5929\u633a\u8bf4\u8d77\u6709\u5587\u561b\u6765\u66ff\u4ed6\u7977\u544a\uff0c\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u5927\u4e08\u592b\u6b7b\u5219\u6b7b\u8033\uff0c\u4f55\u5fc5\u5982\u6b64\u591a \u4e8b\uff1f\u201d\u7ee7\u800c\u53c8\u60f3\uff0c\u5f53\u5e74\u62a2\u820d\u5229\u5b50\u65f6\uff0c\u81ea\u5df1\u4e5f\u66fe\u51fa\u8fc7\u4e00\u628a\u529b\uff0c\u548c\u90a3\u4e9b\u5927\u5587\u561b\u4e5f\u9887\u6709\u4ea4\u60c5\uff0c\u4ed6\u4eec \u6765\u66ff\u81ea\u5df1\u4f5c\u6b7b\u524d\u7948\u7977\uff0c\u6b63\u597d\u8d81\u6b64\u673a\u4f1a\u8bf7\u4ed6\u4eec\u628a\u8840\u4e66\u5e26\u51fa\u3002\u6b63\u601d\u91cf\u95f4\uff0c\u4e24\u4e2a\u9ed1\u5f71\u5df2\u4e00\u95ea\u800c\u5165\uff0c \u4e3a\u9996\u7684\u6b63\u662f\u5b97\u8fbe\xb7\u5b8c\u771f\u3002 \u3000\u3000\u6210\u5927\u633a\u6309\u7740\u53cc\u7b14\uff0c\u6b20\u8eab\u4f5c\u793c\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5b97\u8fbe\xb7\u5b8c\u5b97\u4fa7\u9762\u7684\u5587\u561b\uff0c\u84e6\u7136\u4e00\u8dc3\u800c \u524d\uff0c\u624b\u6307\u4e00\u6233\uff0c\u5df2\u628a\u6210\u5929\u633a\u7684\u7a74\u9053\u5c01\u95ed\uff0c\u6597\u7bf7\u4e00\u63ed\uff0c\u9732\u51fa\u9762\u76ee\uff0c\u51cc\u672a\u98ce\u60ca\u53eb\u9053\uff1a\u201c\u97e9\u5927\u54e5\u4f60 \u600e\u4e48\u53d8\u6210\u4e86\u8fd9\u4e2a\u6837\u5b50\uff01\u201d \u3000\u3000\u6210\u5929\u633a\u5728\u5730\u4e0a\u56ed\u7741\u53cc\u773c\uff0c\u53c8\u6c14\u53c8\u6012\uff0c\u5374\u662f\u52a8\u5f39\u4e0d\u5f97\u3002\u6309\u8bf4\u6210\u5929\u633a\u7684\u6b66\u529f\u6bd4\u97e9\u5fd7\u90a6\u9ad8\u51fa\u8bb8 \u591a\uff0c\u65e0\u5948\u4ed6\u5168\u65e0\u9632\u5907\uff0c\u800c\u97e9\u5fd7\u90a6\u53c8\u5b66\u6210\u4e86\u8fbe\u6469\u79d8\u7b08\uff0c\u602a\u62db\u4f7f\u51fa\uff0c\u8fde\u9f50\u771f\u541b\u521d\u9047\u65f6\u4e5f\u8981\u5403\u4e8f\uff0c \u66f4\u4f55\u51b5\u6210\u5929\u633a\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u5c06\u6210\u5929\u633a\u7f1a\u5728\u6905\u4e0a\uff0c\u4ecd\u9762\u5411\u7740\u51cc\u5728\u98ce\u3002\u62d4\u51fa\u5b9d\u5251\uff0c\u628a\u51cc\u672a\u98ce\u8eab\u4e0a\u7684\u9563\u94d0\u5168\u90e8\u65a9 \u65ad\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u51cc\u5927\u4fa0\uff0c\u4f60\u968f\u6d3b\u4f5b\u51fa\u53bb\u5427\uff01\u201d \u3000\u3000\u51cc\u672a\u98ce\u4ed4\u7ec6\u4e00\u60f3\uff0c\u4e86\u7136\u4e8e\u5fc3\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u97e9\u5927\u54e5\uff0c\u8c22\u8c22\u4f60\u3002\u6789\u8d39\u4e86\u4f60\u7684\u5fc3\u8840\u4e86\uff0c\u6211\u4e0d \u80fd\u8d70\u51fa\u53bb\uff01\u201d\u97e9\u5fd7\u90a6\u6025\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u5230\u5904\u90fd\u6709\u536b\u571f\u628a\u5b88\uff0c\u6211\u4e0d\u60f3\u8fde\u7d2f\u4f60 \u4eec\uff01\u201d\u97e9\u5fd7\u90a6\u628a\u9ed1\u6be1\u6597\u7bf7\u8131\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7559\u5728\u8fd9\u91cc\uff0c\u4f60\u51fa\u53bb\uff0c\u6234\u4e0a\u6597\u7bf7\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u77e5\u9053\u4f60 \u662f\u8c01\u7684\uff01\u201d\u51cc\u672a\u98ce\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u97e9\u5927\u54e5\uff0c\u90a3\u4e0d\u6210\uff01\u6211\u5c82\u80fd\u5bb9\u4f60\u66ff\u6211\u53bb\u6b7b\uff01\u201d\u97e9\u5fd7\u90a6 \u9053\uff1a\u201c\u4f60\u6bd4\u6211\u6709\u7528\u5f97\u591a\uff0c\u4f60\u8be5\u7559\u7740\uff0c\u8ba9\u6211\u53bb\u6b7b\uff01\u201d\u51cc\u672a\u98ce\u6012\u9053\uff1a\u201c\u4f60\u8981\u6211\u505a\u4e0d\u4e49\u4e4b\u4eba\uff0c\u81ea\u5df1 \u82df\u6d3b\uff0c\u5374\u8981\u670b\u53cb\u66ff\u6b7b\uff01\u201d\u97e9\u5fd7\u90a6\u54ac\u7740\u7259\u6839\uff0c\u4e0d\u53d1\u4e00\u8a00\uff0c\u5ffd\u7136\u53cc\u6307\u4e00\u6233\uff0c\u70b9\u4e86\u51cc\u672a\u98ce\u7684\u54d1\u7a74\uff0c \u51cc\u672a\u98ce\u836f\u529b\u672a\u89e3\uff0c\u6d51\u8eab\u65e0\u529b\uff0c\u7edd\u9876\u6b66\u529f\u4e5f\u7528\u4e0d\u51fa\u6765\uff0c\u53ea\u597d\u4efb\u4ed6\u6446\u5e03\u3002\u97e9\u5fd7\u90a6\u7ed9\u51cc\u672a\u98ce\u62ab\u4e0a\u5927 \u8863\u548c\u6597\u7bf7\uff0c\u5c06\u4ed6\u4ea4\u7ed9\u5b97\u8fbe.\u5b8c\u771f\uff0c\u4fef\u9996\u8bf4\u9053\uff1a\u201c\u6d3b\u4f5b\uff0c\u4e00\u5207\u90fd\u62dc\u6258\u4f60\u4e86\uff01\u201d\u5b97\u8fbe\xb7\u5b8c\u771f\u5f2f\u8170 \u543b\u4e86\u97e9\u5fd7\u90a6\u7684\u8db3\u8ddf\uff0c\u6ef4\u6cea\u8bf4\u9053\uff1a\u201c\u97e9\u4e49\u58eb\uff0c\u4f60\u624d\u662f\u771f\u6b63\u7684\u6d3b\u4f5b\uff01\u201d\u8f6c\u8fc7\u8eab\u9a71\uff0c\u534a\u62d6\u534a\u62c9\uff0c\u628a \u51cc\u672a\u98ce\u5e26\u51fa\u4e86\u8ff7\u5bab\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u5750\u5728\u80e1\u5e8a\u4e4b\u4e0a\uff0c\u9762\u5bf9\u7740\u6210\u5929\u633a\uff0c\u65f6\u4e0d\u65f6\u6709\u503c\u73ed\u7684\u6b66\u58eb\u7ecf\u8fc7\u5bc6\u5ba4\uff0c\u63a2\u5934\u5185\u671b\uff0c\u97e9\u5fd7 \u90a6\u8eab\u6750\u548c\u51cc\u672a\u98ce\u5dee\u4e0d\u591a\uff0c\u9762\u4e0a\u53c8\u6709\u5200\u75d5\uff0c\u5ba4\u5185\u5149\u7ebf\u53c8\u5f88\u5fae\u5f31\uff0c\u536b\u58eb\u4eec\u6beb\u4e0d\u5728\u610f\u5730\u5de1\u8fc7\u4fbf\u7b97\uff0c \u8c01\u4e5f\u6ca1\u6709\u53d1\u73b0\u3002 \u3000\u3000\u97e9\u5fd7\u90a6\u4e07\u5ff5\u4ff1\u5bc2\uff0c\u5728\u9ed1\u6697\u4e2d\u9759\u5f85\u6700\u540e\u7684\u65f6\u8fb0\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u7684\u503c\u73ed\u6b66\u58eb\u8bf4\u9053\uff1a\u201c\u695a\u7edf\u9886\uff0c \u65f6\u8fb0\u8fd8\u672a\u5230\u5440\uff0c\u4f60\u6765\u5f97\u8fd9\u6837\u65e9\uff01\u201d\u95e8\u5916\u695a\u662d\u5357\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6211\u8981\u4ed6\u51e0\u65f6\u6b7b\u4fbf\u51e0\u65f6\u6b7b\uff0c\u4f60\u7ba1 \u5f97\u7740\uff1f\u201d\u8fb9\u8bf4\u8fb9\u63a8\u5f00\u4e86\u623f\u95e8\uff0c\u53eb\u9053\uff1a\u201c\u6210\u5929\u633a\uff0c\u4f60\u51fa\u53bb\uff01\u201d\u6210\u5929\u633a\u4e0d\u8a00\u4e0d\u52a8\uff0c\u695a\u662d\u5357\u8de8\u8fdb\u4e24 \u6b65\uff0c\u6b63\u5f85\u53d1\u95ee\uff0c\u97e9\u5fd7\u90a6\u8eab\u5f62\u9aa4\u8d77\uff0c\u62f3\u98ce\u5288\u9762\uff0c\u695a\u662d\u5357\u9661\u7136\u4e00\u7f29\uff0c\u80f8\u53e3\u5df2\u7ed3\u7ed3\u5b9e\u5b9e\u53d7\u4e86\u4e00\u62f3\uff0c \u70db\u5149\u7476\u66f3\u4e2d\uff0c\u695a\u662d\u5357\u770b\u51fa\u654c\u4eba\u4e0d\u662f\u51cc\u672a\u98ce\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u51cc\u672a\u98ce\u54ea\u91cc \u53bb\u4e86\uff1f\u201d\u559d\u58f0\u672a\u505c\uff0c\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\u53c8\u81ea\u80cc\u540e\u88ad\u5230\uff0c\u97e9\u5fd7\u90a6\u8eab\u5f62\u5947\u5feb\uff0c\u62d4\u5251\u8fdb\u62db\u6362\u4f4d\uff0c\u90fd\u53ea\u662f \u4e00\u5239\u90a3\u95f4\u4e4b\u4e8b\uff0c\u695a\u662d\u5357\u8f7b\u8f7b\u4e00\u95ea\uff0c\u8170\u80c1\u53c8\u7ed9\u97e9\u5fd7\u90a6\u53cc\u6307\u6233\u4e86\u4e00\u4e0b\uff0c\u4e00\u58f0\u6012\u543c\uff0c\u6e38\u9f99\u5251\u6323\u7136\u51fa \u624b\uff0c\u542c\u98ce\u8fa8\u5668\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u6697\u5ba4\u4e2d\u706b\u82b1\u84ec\u98de\uff0c\u97e9\u5fd7\u90a6\u76f4\u7ed9\u9707\u5230\u5899\u8fb9\uff0c\u624d\u715e\u5f97\u4f4f\u8eab\u5f62\u3002\u695a\u662d\u5357 \u65cb\u8fc7\u8eab\u6765\uff0c\u770b\u5f97\u771f\u5207\uff0c\u4e00\u58f0\u72de\u7b11\uff1a\u6251\u4e0a\u524d\u9053\uff1a\u201c\u54c8\uff0c\u97e9\u5fd7\u90a6\uff0c\u4f60\u4e5f\u6562\u6765\u627e\u6b7b\uff1f\u201d\u6e38\u9f99\u5251\u4e00 \u7476\uff0c\u500f\u5730\u76f4\u5954\u97e9\u5fd7\u90a6\u54bd\u5589\u523a\u53bb\uff01 \u3000\u3000\u97e9\u5fd7\u90a6\u4ed7\u7740\u8fbe\u6469\u602a\u62db\uff0c\u6253\u4e86\u695a\u662d\u5357\u4e00\u62f3\uff0c\u53c8\u70b9\u4e2d\u4ed6\u7684\u7a74\u9053\u3002\u65e0\u5948\u529f\u529b\u76f8\u5dee\u592a\u8fdc\uff0c\u695a\u662d\u5357 \u53c8\u662f\u6b66\u6797\u7684\u5927\u884c\u5bb6\uff0c\u5165\u623f\u4e4b\u65f6\uff0c\u53d1\u89c9\u8ff9\u8c61\u6709\u5f02\uff0c\u5df2\u628a\u5168\u8eab\u7a74\u9053\u95ed\u7740\uff0c\u97e9\u5fd7\u90a6\u4e00\u62f3\u53cc\u6307\u90fd\u5982\u51fb \u8d25\u8349\uff0c\u624b\u8155\u53cd\u7ed9\u9707\u75db\u3002\u8fd9\u65f6\u89c1\u695a\u662d\u5357\u72e0\u72e0\u523a\u6765\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u547c\u7684\u4ece\u65c1\u8fb9\u62a2\u51fa\uff0c\u5b9d\u5251\u659c\u6311\uff0c\u62db \u6570\u5374\u4e0d\u7528\u8001\uff0c\u695a\u662d\u5357\u56de\u5251\u5c01\u8feb\uff0c\u4ed6\u53c8\u62a2\u5230\u53f3\u9996\u53bb\u4e86\uff01 \u3000\u3000\u695a\u662d\u5357\u4f55\u7b49\u673a\u7075\uff0c\u77e5\u9053\u97e9\u5fd7\u90a6\u662f\u60f3\u4ed7\u7740\u602a\u5f02\u7684\u8eab\u6cd5\u6765\u548c\u81ea\u5df1\u6e38\u6597\uff0c\u5fc3\u60f3\uff1a\u97e9\u5fd7\u90a6\u53ea\u662f\u7663 \u75a5\u4e4b\u60a3\uff0c\u4e0d\u5fc5\u7406\u4ed6\u3002\u770b\u6837\u5b50\u51cc\u672a\u98ce\u5927\u7ea6\u9003\u51fa\u672a\u4e45\uff0c\u82e5\u7ed9\u97e9\u5fd7\u90a6\u7f20\u7740\uff0c\u5c82\u4e0d\u8d70\u4e86\u5927\u654c\uff1f\u5f53\u4e0b\u865a \u6643\u4e00\u5251\uff0c\u5411\u95e8\u53e3\u5954\u53bb\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u51cc\u672a\u98ce\u9003\u4e86\uff0c\u8d76\u5feb\u641c\u6355\uff01\u201d\u97e9\u5fd7\u90a6\u4e00\u58f0\u4e0d\u54cd\uff0c\u5237\u7684\u53c8\u662f \u4e00\u5251\uff0c\u695a\u662d\u5357\u7a81\u89c9\u51b7\u6c14\u68ee\u68ee\uff0c\u5251\u950b\u6307\u5230\u80c1\u4e0b\uff0c\u60f3\u8d77\u97e9\u5fd7\u90a6\u4f7f\u7684\u4e5f\u662f\u5b9d\u5251\uff0c\u4e00\u8feb\u5f97\u56de\u5251\u9632\u5b88\uff0c \u5251\u950b\u4e00\u78b0\uff0c\u53c8\u662f\u4e00\u6e9c\u706b\u82b1\uff0c\u4e24\u53e3\u5b9d\u5251\uff0c\u90fd\u6ca1\u6709\u4f24\u635f\u3002 \u3000\u3000\u695a\u662d\u5357\u52c3\u7136\u5927\u6012\uff0c\u770b\u6765\u975e\u628a\u97e9\u5fd7\u90a6\u6740\u6b7b\uff0c\u5c31\u4e0d\u80fd\u51fa\u53bb\u3002\u6e38\u9f99\u5251\u4e00\u7ffb\u4e00\u5377\uff0c\u5c55\u5f00\u4e86\u5929\u5c71\u5251 \u6cd5\u4e2d\u7684\u7cbe\u5999\u62db\u6570\uff0c\u72c2\u98ce\u66b4\u96e8\u822c\u5730\u7d27\u7d27\u8ffd\u8feb\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u97e9\u5fd7\u90a6\uff0c\u4f60\u771f\u7684\u4e0d\u8981\u547d\u4e86\uff01\u201d\u97e9\u5fd7 \u5e2e\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u662f\u4e0d\u8981\u547d\uff0c\u4f60\u4e5f\u522b\u60f3\u518d\u8ffd\u7740\u51cc\u672a\u98ce\uff01\u201d\u695a\u662d\u5357\u5251\u8d70\u8fde\u73af\uff0c\u70b9\u523a\u5288\u64a9\uff0c\u771f \u662f\u7fe9\u5982\u60ca\u9e3f\u3001\u77eb\u82e5\u6e38\u9f99\uff0c\u97e9\u5fd7\u90a6\u4ed7\u7740\u602a\u62db\uff0c\u5728\u5251\u5149\u4e2d\u94bb\u6765\u94bb\u53bb\uff0c\u695a\u662d\u5357\u4e00\u65f6\u95f4\u5374\u4e5f\u5948\u4f55\u4ed6\u4e0d \u5f97\uff01\u6076\u53eb\u4e00\u58f0\uff0c\u8fd0\u8d77\u5185\u529b\uff0c\u5c06\u5251\u4e00\u6296\uff0c\u5251\u98ce\u56db\u8361\uff0c\u56db\u8fb9\u5899\u89d2\u7684\u70db\u5149\u5168\u90e8\u7184\u706d\uff0c\u4f46\u5251\u82b1\u9519\u843d\uff0c \u5149\u8292\u56db\u5c04\uff0c\u6697\u5ba4\u4e2d\u5251\u6c14\u7eb5\u6a2a\uff0c\u53cd\u6bd4\u4ee5\u524d\u660e\u4eae\uff0c\u97e9\u5fd7\u90a6\u53ea\u89c9\u56db\u9762\u516b\u65b9\uff0c\u90fd\u662f\u695a\u662d\u5357\u7684\u5f71\u5b50\uff0c\u81ea \u77e5\u65e0\u6cd5\u9003\u547d\uff0c\u53cd\u800c\u5927\u58f0\u72c2\u7b11\uff0c\u695a\u662d\u5357\u89c5\u5f97\u7834\u7efd\uff0c\u4e00\u5251\u75be\u523a\uff0c\u81ea\u97e9\u5fd7\u90a6\u524d\u5fc3\u76f4\u7a7f\u8fdb\u53bb\uff0c\u97e9\u5fd7\u90a6 \u5b9d\u5251\u843d\u5730\uff0c\u8840\u5982\u6cc9\u55b7\uff0c\u72b9\u81ea\u72c2\u7b11\u9053\uff1a\u201c\u5218\u5927\u59d0\uff0c\u6211\u5bf9\u5f97\u4f4f\u4f60\u4e86\uff01\u201d\u695a\u662d\u5357\u5b9d\u5251\u62bd\u51fa\uff0c\u98de\u811a\u628a \u97e9\u5fd7\u90a6\u5c38\u8eab\u8e22\u7ffb\uff0c\u8dc3\u51fa\u5bc6\u5ba4\uff0c\u5ffd\u542c\u5f97\u8f70\u9686\u4e00\u58f0\uff0c\u5916\u9762\u706b\u5149\u51b2\u5165\uff0c\u6b66\u58eb\u4eec\u7eb7\u7eb7\u5411\u5916\u4e09\u95e8\u6d8c\u51fa\u3002 \u3000\u3000\u695a\u662d\u5357\u5954\u51fa\u4e2d\u95e8\uff0c\u706b\u5149\u4e2d\u53ea\u89c1\u5085\u9752\u4e3b\u7b49\u6325\u5251\u6740\u4eba\uff0c\u4f17\u6b66\u58eb\u5835\u622a\u4e0d\u4f4f\uff0c\u8fde\u8fde\u540e\u9000\uff0c\u695a\u662d\u5357 \u632f\u81c2\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u614c\u4e71\uff0c\u56f0\u6b7b\u4ed6\u4eec\uff01\u201d\u9000\u5165\u89d2\u95e8\uff0c\u4e0b\u4ee4\u653e\u7bad\uff01\u4e0d\u6599\u654c\u4eba\u7adf\u4f3c\u719f\u8bc6\u8ff7\u5bab\u9053\u8def\uff0c \u5de6\u7a7f\u77f3\u63d2\uff0c\u76f4\u8ffd\u8fdb\u6765\uff0c\u695a\u662d\u5357\u62bc\u7740\u9635\u811a\uff0c\u4ea2\u58f0\u53eb\u9053\uff1a\u201c\u5927\u519b\u5c31\u8981\u5230\u6765\uff0c\u4ed6\u4eec\u4e00\u4e2a\u4e5f\u8d70\u4e0d\u6389\uff0c \u6211\u4eec\u8981\u62fc\u547d\u6321\u4f4f\uff01\u201d\u5085\u9752\u4e3b\u7eb5\u58f0\u957f\u7b11\uff0c\u628a\u624b\u4e00\u62db\uff0c\u5185\u5916\u5065\u513f\u7eb7\u7eb7\u6740\u5165\uff0c\u706b\u7bad\u4e71\u98de\uff0c\u706b\u5934\u56db \u8d77\uff0c\u695a\u662d\u5357\u653e\u773c\u671b\uff0c\u53ea\u89c1\u8ff7\u5bab\u4e2d\u5230\u5904\u90fd\u662f\u654c\u4eba\uff0c\u4e5f\u4e0d\u77e5\u5085\u9752\u4e3b\u4ece\u4f55\u5904\u8c03\u96c6\u5f97\u8fd9\u4e48\u591a\u52c7\u58eb\uff01 \u3000\u3000\u5085\u9752\u4e3b\u8fd9\u6b21\u62fc\u7740\u4f5c\u4e00\u6b7b\u6218\uff0c\u628a\u62c9\u8428\u57ce\u4e2d\u7684\u5929\u5730\u4f1a\u515a\u5f92\u3001\u54c8\u8428\u514b\u52c7\u58eb\u7b49\u53ef\u4ee5\u8c03\u96c6\u7684\u4eba\u90a3\u8c03 \u96c6\u8d77\u6765\uff0c\u603b\u8ba1\u6709\u4e09\u5343\u591a\u4eba\uff0c\u5192\u9669\u6740\u5165\u5e03\u8fbe\u62c9\u5bab\u3002\u4ed6\u4eec\u4e0d\u5355\u5355\u662f\u60f3\u63f4\u6551\u51cc\u672a\u98ce\uff0c\u800c\u4e14\u60f3\u7ed9\u5141\u9898 \u4e00\u4e2a\u6253\u51fb\u3002\u9ed1\u591c\u4e2d\u5141\u9898\u4e0d\u77e5\u654c\u4eba\u8654\u5b9e\uff0c\u4e0d\u6562\u63a5\u6218\uff0c\u5728\u536b\u58eb\u4fdd\u62a4\u4e0b\u9003\u51fa\u5e03\u8fbe\u62c9\u5bab\uff0c\u4f20\u4ee4\u5927\u519b\uff0c \u5835\u622a\u56db\u9762\u57ce\u95e8\uff0c\u8ba9\u695a\u662d\u5357\u548c\u4ed6\u7684\u4e00\u961f\u7981\u536b\u519b\u5728\u5bab\u4e2d\u548c\u654c\u4eba\u7f20\u6597\u3002 \u3000\u3000\u5141\u9898\u9003\u51fa\u4e86\u5bab\uff0c\u695a\u662d\u5357\u5374\u4e0d\u77e5\u9053\u3002\u9547\u5b88\u5e03\u8fbe\u62c9\u5bab\u7684\u7981\u536b\u519b\u53ea\u6709\u4e8c\u5343\uff0c\u5982\u4f55\u6321\u5f97\u4f4f\u5085\u9752\u4e3b \u6240\u7387\u9886\u7684\u4e09\u5343\u6b7b\u58eb\uff0c\u53ae\u6740\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u7981\u536b\u519b\u6b7b\u4ea1\u7d2f\u7d2f\uff0c\u5e03\u8fbe\u62c9\u5bab\u70df\u96fe\u5f25\u6f2b\uff0c\u6881\u6467\u680b\u6298\uff0c\u5085 \u9752\u4e3b\u5927\u58f0\u559d\u9053\uff1a\u201c\u695a\u662d\u5357\uff0c\u5feb\u628a\u51cc\u672a\u98ce\u4ea4\u51fa\uff0c\u4e0d\u7136\u53eb\u4f60\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff01\u201d\u695a\u662d\u5357\u4e00\u542c\uff0c\u6697 \u9053\uff1a\u201c\u539f\u6765\u51cc\u672a\u98ce\u4e0d\u662f\u4ed6\u4eec\u6551\u51fa\u7684\u3002\u201d\u773c\u73e0\u4e00\u8f6c\uff0c\u63a5\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5148\u9000\u51fa\u53bb\uff0c\u54b1\u4eec\u597d\u597d\u5546 \u91cf\u3002\u8981\u4e0d\u7136\u6211\u5c31\u5148\u628a\u51cc\u672a\u98ce\u6740\u4e86\uff01\u201d\u674e\u601d\u6c38\u6012\u9053\uff1a\u201c\u4f60\u6b7b\u5728\u4e34\u5934\uff0c\u8fd8\u6562\u8981\u631f\uff01\u201d\u626c\u624b\u4e00\u652f\u86c7 \u7130\u7bad\uff0c\u84ec\u7684\u4e00\u58f0\uff0c\u5728\u695a\u662d\u5357\u8eab\u4fa7\u70b8\u88c2\u5f00\u6765\u3002 \u3000\u3000\u695a\u662d\u5357\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u771f\u7684\u4e0d\u8981\u51cc\u672a\u98ce\u4e86\uff1f\u201d\u5927\u58f0\u53eb\u9053\uff1a\u201c\u738b\u680b\u3001\u5f20\u6750\uff0c\u8fdb\u53bb\u628a\u51cc\u672a\u98ce \u9996\u7ea7\u53d6\u6765\uff01\u201d\u5218\u90c1\u82b3\u9762\u8272\u5927\u53d8\uff0c\u5bf9\u5085\u9752\u4e3b\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u5c31\u628a\u51cc\u672a\u98ce\u7684\u547d\u6362\u4ed6\u7684\u547d\u5427\uff01\u201d\u5085\u9752 \u4e3b\u77e5\u9053\u695a\u662d\u5357\u8be1\u8ba1\u591a\u7aef\uff0c\u8bda\u6050\u5148\u9000\u51fa\u53bb\uff0c\u53cd\u4e2d\u4ed6\u7684\u9677\u997c\uff0c\u4e00\u9635\u8e0c\u8e87\uff0c\u695a\u662d\u5357\u53c8\u5927\u53eb\u9053\uff1a\u201c\u4f60 \u4eec\u9000\u81f3\u5916\u4e09\u95e8\uff0c\u6211\u5c31\u628a\u51cc\u672a\u98ce\u653e\u51fa\uff0c\u4e24\u8fb9\u6536\u5175\u3002\u8981\u4e0d\u7136\uff0c\u4f60\u4eec\u5c31\u53ea\u80fd\u89c1\u51cc\u672a\u98ce\u7684\u4eba\u5934\u4e86\uff0c \u597d\uff0c\u73b0\u5728\u6211\u6570\u4e09\u58f0\uff0c\u6570\u5230\u7b2c\u4e09\uff0c\u4f60\u4eec\u8fd8\u4e0d\u7b54\u5e94\uff0c\u5c31\u83ab\u602a\u6211\u4e0b\u6bd2\u624b\uff01\u4e00\uff01\u4e8c\uff01\u2026\u2026\u201d\u5218\u90c1\u82b3\u5927 \u4e3a\u7740\u6025\uff0c\u695a\u662d\u5357\u7565\u505c\u4e86\u4e00\u505c\uff0c\u201c\u4e8c\u201d\u5b57\u5c1a\u5728\u51fa\u53e3\uff0c\u5ffd\u7136\u6709\u51e0\u540d\u5587\u561b\u75be\u7684\u4ece\u4e00\u5ea7\u70e7\u584c\u7684\u623f\u4e2d\u51b2 \u51fa\uff0c\u4e3a\u9996\u7684\u7a7f\u7740\u5927\u7ea2\u618e\u888d\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u51cc\u672a\u98ce\u65e9\u5df2\u9003\u51fa\u8ff7\u5bab\u4e86\uff01\u201d\u695a\u662d\u5357\u5927\u6012\uff0c\u628a\u624b\u4e00 \u6325\uff0c\u4e71\u7bad\u5982\u96e8\uff0c\u90a3\u540d\u5587\u561b\u6b66\u529f\u9887\u4e3a\u4e86\u5f97\uff0c\u6325\u52a8\u7985\u6756\uff0c\u51b2\u51fa\u7bad\u96e8\uff0c\u5085\u9752\u4e3b\u7b49\u6025\u4e0a\u524d\u6551\u5e94\uff0c\u695a\u662d \u5357\u62a2\u8fc7\u4e00\u5f20\u4e94\u77f3\u5f3a\u5f13\uff0c\u55d6\uff0c\u55d6\uff0c\u55d6\uff01\u8fde\u53d1\u4e09\u7bad\uff0c\u90a3\u540d\u5587\u561b\uff0c\u6253\u843d\u4e00\u7bad\uff0c\u907f\u8fc7\u4e00\u7bad\uff0c\u5374\u7ed9\u7b2c\u4e09 \u7bad\u5c04\u7a7f\u5589\u5499\uff0c\u51b2\u5230\u5085\u9752\u4e3b\u8ddf\u524d\uff0c\u9893\u7136\u5012\u4e0b\u3002 \u3000\u3000\u5085\u9752\u4e3b\u4e00\u770b\uff0c\u539f\u6765\u5c31\u662f\u4ee5\u524d\u548c\u695a\u662d\u5357\u540c\u4e0a\u4e94\u53f0\u5c71\u7684\u7ea2\u8863\u5587\u561b\uff0c\u90a3\u65f6\u695a\u662d\u5357\u521d\u53db\u5434\u4e09\u6842\uff0c \u88ab\u7ea2\u8863\u5587\u561b\u8bc6\u7834\uff0c\u695a\u662d\u5357\u4e00\u4e0d\u505a\u4e8c\u4e0d\u4f11\uff0c\u5728\u4e94\u53f0\u5c71\u8c37\uff0c\u8981\u5c06\u7ea2\u8863\u5587\u561b\u51fb\u6740\uff0c\u5e78\u597d\u51cc\u672a\u98ce\u6551\u4e86 \u4ed6\u7684\u3002\u4e0d\u6599\u4ed6\u4eca\u5929\u4ecd\u662f\u4e27\u5728\u695a\u662d\u5357\u7bad\u4e0b\u3002\u7ea2\u8863\u5587\u561b\u5728\u5085\u9752\u4e3b\u8ddf\u524d\u5012\u4e0b\uff0c\u72b9\u81ea\u5636\u58f0\u8bf4\u9053\uff1a\u201c\u51cc \u5927\u4fa0\u5df2\u8131\u9669\u4e86\u3002\u4f60\u4eec\u4e0d\u8981\u653e\u8fc7\u8fd9\u4e2a\u8d3c\u5b50\uff01\u201d\u628a\u624b\u6307\u4e00\u6307\u695a\u662d\u5357\uff0c\u6e98\u7136\u957f\u901d\u3002 \u3000\u3000\u5085\u9752\u5b9d\u5251\u950b\u4e00\u6307\uff0c\u6842\u4ef2\u660e\u6613\u5170\u73e0\u53cc\u5251\u98de\u821e\uff0c\u62fc\u547d\u6740\u4e0a\uff0c\u6b66\u58eb\u4eec\u7eb7\u7eb7\u8d70\u907f\uff0c\u695a\u662d\u5357\u865a\u6643\u4e00 \u5251\uff0c\u5f80\u540e\u4fbf\u9003\uff0c\u6842\u4ef2\u660e\u594b\u529b\u4e00\u8dc3\uff0c\u817e\u86df\u5b9d\u5251\u523a\u5230\u80cc\u540e\uff0c\u695a\u662d\u5357\u6325\u52a8\u6e38\u9f99\u5251\uff0c\u5f80\u6842\u4ef2\u660e\u7684\u5251\u4e0a \u4e00\u642d\uff0c\u7528\u529b\u4e00\u63a5\uff0c\u9661\u7136\u7ffb\u4e86\u4e0a\u6765\uff0c\u6842\u4ef2\u660e\u5251\u8bc0\u4e00\u9886\uff0c\u4e86\u5f53\u4e00\u58f0\uff0c\u51b2\u5f00\u5251\u82b1\uff0c\u5237\uff01\u5237\uff01\u5237\uff01\u5916 \u4e00\u8fde\u4e09\u58f0\uff0c\u671d\u654c\u4eba\u731b\u523a\uff0c\u695a\u662d\u5357\u5403\u60ca\u4e8e\u4ed6\u7684\u5251\u6cd5\u7cbe\u8fdb\u5982\u65af\uff0c\u4f46\u4ed7\u7740\u706b\u5019\u8001\u5230\u867d\u60ca\u4e0d\u4e71\uff0c\u6e38\u9f99 \u5251\u731b\u7136\u4e00\u7ede\uff0c\u89e3\u4e86\u6842\u4ef2\u660e\u653b\u52bf\uff0c\u8f7b\u98d8\u98d8\u98de\u8eab\u4e00\u7a9c\uff0c\u51b2\u70df\u76f4\u4e0a\uff0c\u767b\u4e0a\u4e00\u5ea7\u6b63\u5728\u71c3\u70e7\u7684\u623f\u5b50\uff0c\u62fc \u547d\u5954\u9003\uff0c\u706b\u5149\u4e2d\u7a81\u7136\u4eba\u5f71\u4e00\u95ea\uff0c\u4e00\u9053\u9752\u5149\uff0c\u51b2\u5f00\u70df\u96fe\u523a\u5165\uff0c\u695a\u662d\u5357\u56de\u5251\u4e00\u683c\uff0c\u8df3\u8fc7\u7b2c\u4e8c\u95ee\u5c4b \u9762\uff0c\u5c1a\u672a\u7ad9\u8d70\uff0c\u80cc\u540e\u51b7\u6c14\u68ee\u68ee\uff0c\u4e00\u53e3\u5b9d\u5251\uff0c\u53c8\u5df2\u582a\u582a\u88ad\u5230\uff0c\u695a\u662d\u5357\u53cd\u624b\u4e00\u5251\uff0c\u817e\u8eab\u8dc3\u8d77\uff0c\u8df3 \u843d\u5730\u9762\uff0c\u90a3\u6761\u4eba\u5f71\u4e5f\u8ddf\u7740\u4e0b\u843d\uff0c\u695a\u662d\u5357\u4e00\u770b\uff0c\u539f\u6765\u5374\u662f\u6613\u5170\u73e0\uff01 \u3000\u3000\u695a\u662d\u5357\u4e00\u89c1\u662f\u5979\uff0c\u5fc3\u91cc\u7a0d\u5bbd\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u5b50\u4e0d\u662f\u6211\u7684\u5bf9\u624b\uff0c\u4f46\u8fd9\u4e2a\u65f6\u5019\uff0c\u5012\u4e0d\u597d\u548c \u5979\u7f20\u6597\uff0c\u7528\u624b\u4e00\u6309\u58c1\u4e0a\u673a\u5173\uff0c\u4e24\u8fb9\u5899\u58c1\u88c2\u5f00\uff0c\u4e2d\u95f4\u73b0\u51fa\u6697\u95e8\uff0c\u695a\u662d\u5357\u4e00\u95ea\u800c\u5165\uff0c\u6b63\u60f3\u518d\u63a5\u673a \u5173\uff0c\u66ae\u89c9\u9510\u98ce\u52b2\u6251\uff0c\u51b7\u6c14\u88ad\u4eba\uff0c\u672a\u6562\u56de\u5934\uff0c\u5148\u884c\u659c\u8dc3\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u5c06\u654c\u5251\u6302\u5f00\uff0c\u626c\u58f0\u9a82\u9053\uff1a \u201c\u6613\u5170\u73e0\uff0c\u4f60\u4fa5\u5e78\u9003\u8131\uff0c\u8fd8\u6562\u518d\u6765\u627e\u6b7b\uff01\u201d\u6613\u5170\u73e0\u7c89\u9762\u51dd\u971c\uff0c\u53e3\u89d2\u5659\u7740\u51b7\u7b11\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u65ad \u7389\u5251\u626c\u7a7a\u4e00\u5207\uff0c\u98de\u4e91\u63a3\u7535\u822c\uff0c\u6b3a\u8eab\u76f4\u8fdb\uff0c\u695a\u662d\u5357\u53cc\u80a9\u4e00\u7eb5\uff0c\u659c\u98d8\u51fa\u53bb\uff0c\u5de6\u638c\u5728\u5899\u4e0a\u4e00\u62b5\uff0c\u4e24 \u8fb9\u5899\u58c1\u53c8\u518d\u91cd\u5408\uff0c\u811a\u5c16\u7528\u529b\u4e00\u8e6c\uff0c\u53c8\u659c\u8dc3\u51fa\u6570\u4e08\uff0c\u56de\u5934\u72de\u7b11\u9053\uff1a\u201c\u6613\u5170\u73e0\uff0c\u4eca\u65e5\u4f60\u4f11\u602a\u5e08\u53d4 \u624b\u8fa3\uff01\u201d\u6613\u5170\u73e0\u84e6\u89c9\u773c\u524d\u4e00\u6697\uff0c\u695a\u662d\u5357\u7684\u5b9d\u5251\u5df2\u53cd\u5288\u8fc7\u6765\uff0c\u5fae\u5fae\u4e00\u6643\uff0c\u5251\u950b\u5411\u5916\u4e00\u5c55\uff0c\u628a\u6765 \u52bf\u5316\u5f00\uff0c\u8d81\u52bf\u8dc3\u51fa\u4e09\u6b65\uff0c\u51dd\u8eab\u5f85\u654c\u3002 \u3000\u3000\u695a\u662d\u5357\u907f\u8fdb\u7684\u5730\u65b9\uff0c\u4e43\u662f\u8ff7\u5bab\u4e2d\u7684\u6697\u9053\uff0c\u53e6\u4e00\u5934\u76f4\u901a\u5bab\u5916\uff0c\u8fd9\u6761\u4e34\u9053\u6781\u5c11\u4eba\u77e5\uff0c\u695a\u662d\u5357 \u539f\u662f\u60f3\u501f\u6b64\u9003\u547d\u7684\uff0c\u4e0d\u6599\u6613\u5170\u73e0\u8eab\u6cd5\u5947\u5feb\uff0c\u7adf\u7d27\u968f\u8eab\u540e\uff0c\u8ffd\u4e86\u8fdb\u6765\uff0c\u695a\u662d\u5357\u5fc3\u5ff5\u4e00\u52a8\uff0c\u767b\u65f6 \u6539\u53d8\u4e3b\u610f\uff0c\u60f3\u5148\u628a\u6613\u5170\u73e0\u751f\u64d2\uff0c\u4f5c\u4e3a\u4eba\u8d28\uff0c\u7136\u540e\u518d\u9003\u51fa\u5bab\u3002\u6613\u5170\u73e0\u662f\u523a\u6740\u591a\u950b\u7684\u51f6\u624b\uff0c\u64d2\u7740 \u4e86\u5979\uff0c\u5219\u867d\u8d70\u8131\u51cc\u672a\u98ce\uff0c\u7687\u4e0a\u600e\u4e48\u4e5f\u4e0d\u4f1a\u602a\u8d23\u3002\u4ed6\u5229\u7984\u85b0\u5fc3\uff0c\u5728\u6697\u9053\u4e2d\u53cd\u5411\u6613\u5170\u73e0\u8fdb\u8feb\uff01 \u3000\u3000\u8fd9\u65f6\u6697\u95e8\u5df2\u95ed\uff0c\u752c\u9053\u4e2d\u9ed1\u9edd\u9edd\u7684\u89c9\u4eba\u5f71\u5e62\u5e62\u3002\u6613\u5170\u73e0\u4ece\u672a\u8bd5\u8fc7\u5728\u9ed1\u6697\u4e4b\u4e2d\u4e0e\u4eba\u6597\u5251\uff0c\u867d \u8bf4\u5979\u4e5f\u5b66\u8fc7\u542c\u98ce\u8fa9\u5668\u7684\u529f\u592b\uff0c\u5230\u5e95\u4e0d\u53ca\u695a\u662d\u5357\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u8fde\u6321\u51e0\u5251\uff0c\u5341\u5206\u5403\u529b\uff0c\u695a\u662d\u5357\u4e00\u58f0 \u72c2\u7b11\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u7565\u8d70\u504f\u950b\uff0c\u5251\u5149\u7ed5\u5904\uff0c\u5237\u5730\u4fbf\u5954\u6613\u5170\u73e0\u5de6\u80a9\u523a\u6765\uff0c\u6613\u5170\u73e0\u8eac\u8170\u4e00\u62db\uff0c\u4e00\u62cd \u201c\u9f99\u95e8\u9f13\u6d6a\u201d\uff0c\u5b9d\u5251\u75be\u5982\u98ce\u53d1\uff0c\u907f\u62db\u8fdb\u62db\u3002\u695a\u662d\u5357\u6697\u5403\u4e00\u60ca\uff0c\u53f3\u8155\u500f\u7ffb\uff0c\u201c\u91d1\u96d5\u5c55\u7fc5\u201d\uff0c\u53cd \u624b\u4e00\u5251\uff0c\u4fbf\u5288\u6613\u5170\u73e0\u53f3\u81c2\uff0c\u4e24\u5251\u76f8\u4ea4\uff0c\u94f6\u5149\u6fc0\u5c04\uff0c\u6613\u5170\u73e0\u7ec8\u662f\u706b\u5019\u7a0d\u6b20\uff0c\u8fde\u73af\u4e09\u5251\uff0c\u523a\u4e0d\u7740 \u654c\u4eba\uff0c\u4e0d\u6562\u5192\u8fdb\uff0c\u77ed\u5251\u4e00\u5708\uff0c\u6b63\u5f85\u53d8\u62db\uff0c\u695a\u662d\u5357\u731b\u7136\u7ffb\u8eab\u73b0\u5251\uff0c\u4e00\u62db\u201c\u7389\u5e26\u56f4\u8170\u201d\uff0c\u622a\u65a9\u8170 \u808b\uff0c\u6613\u5170\u73e0\u88ab\u8feb\u5c06\u77ed\u5251\u4e00\u6321\uff0c\u94f6\u5149\u6fc0\u5c04\u4e2d\uff0c\u84e6\u89c1\u695a\u662d\u5357\u9762\u5e26\u60e7\u5bb9\uff0c\u800c\u6613\u5170\u73e0\u4e5f\u7ed9\u4ed6\u9707\u51fa\u51e0 \u6b65\uff0c\u624b\u8155\u9178\u75db\u3002 \u3000\u3000\u695a\u662d\u5357\u626c\u58f0\u53eb\u9053\uff1a\u201c\u5170\u73e0\uff0c\u8bf4\u4ec0\u4e48\u6211\u90fd\u662f\u4f60\u7684\u5c0a\u957f\uff0c\u4f60\u653e\u4e0b\u5251\u6765\uff0c\u6211\u65ad\u4e0d\u4f1a\u4f24\u4f60\uff01\u201d\u6613 \u5170\u73e0\u4ecd\u662f\u4e00\u58f0\u4e0d\u54cd\uff1b\u9ed1\u6697\u4e2d\u53ea\u89c1\u5979\u4e00\u53cc\u660e\u5982\u79cb\u6c34\u7684\u773c\u775b\uff0c\u53d1\u51fa\u51b7\u51b7\u7684\u5149\u8292\u3002\u695a\u662d\u5357\u51db\u7136\u4e00 \u60ca\uff0c\u5fc3\u60f3\uff1a\u4e0d\u8fc7\u4e00\u5e74\uff0c\u8fd9\u59ae\u5b50\u7684\u5251\u672f\u600e\u7684\u7adf\u6709\u5982\u6b64\u8fdb\u5c55\uff01\u83ab\u975e\u5e08\u5085\u7684\u62f3\u7ecf\u5251\u8bc0\uff0c\u5df2\u5230\u4e86\u5979\u7684 \u624b\u4e2d\uff1f\u6b63\u81ea\u6c89\u541f\uff0c\u6613\u5170\u73e0\u811a\u5c16\u4e00\u70b9\uff0c\u817e\u8eab\u743c\u8d77\uff0c\u5ffd\u7136\u4e00\u62db\u201c\u98de\u9e1f\u6295\u6797\u201d\uff0c\u534a\u7a7a\u6740\u4e0b\uff0c\u695a\u662d\u5357 \u633a\u8170\u4e00\u5251\uff0c\u622a\u65a9\u6613\u5170\u73e0\u53cc\u8db3\uff0c\u8fd9\u62db\u662f\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u6740\u624b\uff0c\u5341\u5206\u5389\u5bb3\uff0c\u6ee1\u4ee5\u4e3a\u6613\u5170\u73e0\u8eab\u5b50\u60ac \u7a7a\uff0c\u5b9a\u8eb2\u4e0d\u4e86\uff0c\u54ea\u77e5\u6613\u5170\u73e0\u5c31\u5728\u534a\u7a7a\u4e2d\uff0c\u8fde\u4eba\u5e26\u5251\u8f6c\u4e86\u4e2a\u5927\u5706\u5708\uff0c\u5251\u5149\u95ea\u5904\uff0c\u201c\u767d\u8679\u8d2f \u65e5\u201d\uff0c\u53c8\u5411\u695a\u662d\u5357\u523a\u6765\uff0c\u695a\u662d\u5357\u66f4\u662f\u5403\u60ca\uff0c\u6599\u4e0d\u5230\u5979\u628a\u5929\u5c71\u5251\u4e2d\u8ffd\u98ce\u5251\u6cd5\u7684\u7edd\u62db\uff0c\u4f7f\u5f97\u51fa\u795e \u5165\u5316\uff0c\u62d4\u8eab\u4e00\u8df3\uff0c\u582a\u582a\u907f\u5f00\uff0c\u800c\u6613\u5170\u73e0\u4e5f\u5df2\u98d8\u8eab\u843d\u5730\uff0c\u77ed\u5251\u4e00\u6325\uff0c\u53c8\u518d\u72e0\u6740\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u6613\u5170\u73e0\u5bf9\u9ed1\u6697\u5df2\u6e10\u4e60\u60ef\uff0c\u51ed\u85c9\u7740\u4e24\u628a\u5b9d\u5251\u53d1\u51fa\u7684\u5149\u8292\uff0c\u8ba4\u5b9a\u654c\u4eba\u8eab\u5f62\uff0c\u72e0\u72e0\u653b\u51fb\uff0c \u5979\u7684\u5251\u4f7f\u5f88\u8fc5\u6377\u65e0\u4f26\uff0c\u695a\u662d\u5357\u88ab\u8feb\u5f97\u4ee5\u5feb\u6253\u5feb\uff0c\u4e24\u53e3\u5b9d\u5251\uff0c\u98de\u4e91\u63a3\u7535\u822c\u5728\u6697\u5ba4\u4e2d\u76f8\u6597\uff0c\u53ea\u89c1 \u5251\u82b1\u9519\u843d\uff0c\u51b7\u7535\u7cbe\u8292\uff0c\u968f\u7740\u541e\u65f6\u8fdb\u9000\u7684\u5251\u5c16\u51b2\u51fb\uff0c\u6597\u5230\u6025\u5904\uff0c\u5b9b\u4f3c\u5343\u4e07\u6761\u94f6\u86c7\u4e71\u63a3\uff0c\u548c\u5728\u767d \u5929\u76f8\u6597\uff0c\u7adf\u5dee\u4e0d\u4e86\u591a\u5c11\uff0c\u695a\u662d\u5357\u778e\u4e86\u4e00\u773c\uff0c\u53cd\u7ed9\u903c\u5f97\u773c\u82b1\u7eb7\u4e71\uff0c\u770b\u4e0d\u6e05\u5251\u70b9\uff0c\u53c8\u6025\u53c8\u6012\uff0c\u518d \u626c\u58f0\u559d\u9053\uff1a\u201c\u4f60\u771f\u7684\u8981\u62fc\u547d\uff1f\u201d\u6613\u5170\u73e0\u4ecd\u662f\u4e00\u58f0\u4e0d\u54cd\uff0c\u6325\u5251\u75be\u653b\uff01\u695a\u662d\u5357\u6012\u9053\uff1a\u201c\u96be\u9053\u6211\u4f1a \u6015\u4f60\uff01\u201d\u5251\u62db\u4e00\u53d8\uff0c\u89e3\u51fa\u5929\u5c71\u5251\u4e2d\u6700\u6df1\u5965\u7684\u987b\u5f25\u5251\u6cd5\uff0c\u5e26\u5b88\u5e26\u653b\uff0c\u4e13\u627e\u6613\u5170\u73e0\u7684\u5b9d\u5251\uff0c\u6597\u4e86 \u4e09\u5341\u6765\u62db\uff0c\u6613\u5170\u73e0\u624b\u8155\u4e00\u9707\uff0c\u77ed\u5251\u53c8\u88ab\u695a\u662d\u5357\u78b0\u7740\u3002\u6613\u5170\u73e0\u7684\u65ad\u7389\u5251\u548c\u695a\u662d\u5357\u7684\u6e38\u9f99\u5251\u540c\u662f \u6666\u660e\u7985\u5e08\u91c7\u4e94\u91d1\u4e4b\u7cbe\u6240\u70bc\uff0c\u5251\u8d28\u4e00\u6837\uff0c\u53cc\u5251\u78b0\u51fb\uff0c\u4e24\u65e0\u635f\u4f24\uff0c\u53ef\u662f\u6613\u5170\u73e0\u662f\u4e2a\u5c11\u5973\uff0c\u6c14\u529b\u5374 \u8fdc\u900a\u695a\u662d\u5357\uff1b\u695a\u662d\u5357\u4e00\u62db\u5f97\u624b\uff0c\u957f\u5251\u4e00\u6296\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u5288\u9762\u5241\u6765\u3002\u6613\u5170\u73e0\u5251\u8d70\u8f7b\u7075\uff0c\u4e00\u4e2a \u201c\u62d7\u819d\u6402\u6b65\u201d\uff0c\u98d8\u98ce\u822c\u5708\u5230\u695a\u662d\u5357\u53f3\u4fa7\uff0c\u5251\u62db\u500f\u53d8\uff0c\u65ad\u7389\u5251\u5411\u4e0a\u4e00\u64a9\uff0c\u53cd\u6311\u654c\u4eba\u53f3\u81c2\uff0c\u695a\u662d \u5357\u597d\u4e0d\u72e0\u6bd2\uff0c\u4ed7\u7740\u62db\u719f\u529b\u6c89\uff0c\u62ff\u634f\u65f6\u5019\uff0c\u5f85\u6613\u5170\u73e0\u5251\u950b\u521a\u6cbe\u8863\u88f3\u4e4b\u9645\uff0c\u84e6\u7136\u8eab\u5b50\u5411\u524d\u4e00\u6251\uff0c \u201c\u5f2f\u5f13\u5c04\u864e\u201d\uff0c\u5206\u5f00\u5de6\u53f3\uff0c\u53f3\u5251\u731b\u523a\uff0c\u5de6\u638c\u5e73\u4f38\uff0c\u5251\u523a\u638c\u5288\uff0c\u540c\u65f6\u653b\u5230\uff0c\u6613\u5170\u73e0\u7684\u5251\u62db\u4f7f \u5230\uff0c\u53eb\u58f0\u4e0d\u597d\uff0c\u84e6\u5730\u4f7f\u51fa\u767d\u53d1\u9b54\u53d1\u5973\u72ec\u95e8\u5251\u672f\uff0c\u77ed\u5251\u5377\u7a7a\uff0c\u731b\u7136\u4e00\u632f\u4e4e\u8155\uff0c\u5251\u950b\u5012\u8f6c\uff0c\u7adf\u4ece \u53cd\u4fa7\u5411\u695a\u662d\u5357\u5206\u5fc3\u523a\u5230\uff0c\u695a\u662d\u5357\u4e0d\u8bc6\u8fd9\u62db\uff0c\u4e00\u5251\u63b7\u7a7a\uff0c\u6025\u5fd9\u5438\u80f8\u51f9\u8179\uff0c\u6643\u8eab\u98d8\u51fa\u3002\u5fc3\u91cc\u66f4\u4e3a \u60ca\u7591\uff0c\u6613\u5170\u73e0\u8fd9\u62db\u4e43\u5929\u5c71\u5251\u6cd5\u6240\u65e0\uff0c\u5374\u53c8\u5982\u6b64\u8f9b\u8fa3\uff01 \u3000\u3000\u6613\u5170\u73e0\u5598\u606f\u672a\u5b9a\uff0c\u695a\u662d\u5357\u6d53\u7709\u4e00\u7ad6\uff0c\u957f\u5251\u6325\u4e86\u534a\u4e2a\u5f27\u5f62\uff0c\u50fb\u556a\u6709\u58f0\uff0c\u4ecd\u7528\u987b\u5f25\u5251\u4e2d\u7684\u7cbe \u5999\u62db\u6570\uff0c\u72e0\u72e0\u6740\u4eba\u3002\u8981\u77e5\u987b\u5f25\u5251\u6cd5\u653b\u5b88\u517c\u5907\uff0c\u4e43\u6666\u660e\u7985\u5e08\u7cbe\u5fc3\u6240\u521b\uff0c\u4e13\u4e3a\u5bf9\u4ed8\u548c\u81ea\u5df1\u672c\u9886\u5dee \u4e0d\u591a\u7684\u4eba\u7528\u7684\u3002\u521a\u624d\u695a\u662d\u752b\u8fc7\u4e8e\u8d2a\u529f\uff0c\u4ee5\u81f4\u9669\u800c\u53cd\u906d\u8d25\u7ee9\uff0c\u8fd9\u756a\u518d\u6218\uff0c\u5206\u5916\u5c0f\u5fc3\uff0c\u6613\u5170\u73e0\u8bd5 \u51e0\u62db\u767d\u53d1\u9b54\u5973\u7684\u8fa3\u62db\uff0c\u5206\u6beb\u4e5f\u653b\u4e0d\u8fdb\uff0c\u695a\u662d\u5357\u626c\u58f0\u559d\u9053\uff1a\u201c\u4f60\u662f\u5929\u5c71\u95e8\u4e0b\uff0c\u672c\u95e8\u5251\u6cd5\u4e0d\u52a0\u6df1 \u7a76\uff0c\u53cd\u53bb\u5b66\u90aa\u9b54\u5916\u9053\uff0c\u597d\u4e0d\u77e5\u7f9e\uff0c\u8fd8\u4e0d\u5f03\u5251\u6295\u964d\uff01\u201d\u7728\u773c\u4e4b\u95f4\uff0c\u75be\u5288\u51e0\u5251\uff0c\u767d\u53d1\u9b54\u5973\u7684\u5251 \u6cd5\uff0c\u6700\u9002\u5b9c\u4e8e\u5947\u5175\u7a81\u88ad\uff0c\u82e5\u8bba\u5230\u7cbe\u79d8\u53d8\u5316\uff0c\u5374\u4e0d\u53ca\u5929\u5c71\u5251\u6cd5\uff0c\u6613\u5170\u73e0\u672a\u53ca\u6362\u62db\uff0c\u65ad\u7389\u5251\u53c8\u7ed9 \u695a\u662d\u5357\u7684\u5251\u683c\u4e86\u4e00\u4e0b\uff0c\u767b\u65f6\u518d\u7ed9\u9707\u9000\u6570\u6b65\uff0c\u695a\u662d\u5357\u5927\u58f0\u559d\u9053\uff1a\u201c\u4e56\u4f84\u5973\uff01\u4f60\u8fd8\u4e0d\u8ba4\u8f93\u5417\uff1f\u201d \u3000\u3000\u6613\u5170\u73e0\u7a81\u7136\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53eb\u4f60\u89c1\u8bc6\u672c\u95e8\u5251\u6cd5\u7684\u7cbe\u5999\uff01\u201d\u628a\u8654\u5fc3\u82e6\u7ec3\u3001\u5999\u609f\u901a\u7384\u7684\u5251\u6cd5\u65bd \u5c55\u51fa\u6765\uff0c\u5ffd\u865a\u5ffd\u5b9e\uff0c\u5ffd\u5f90\u5ffd\u75be\uff0c\u53d8\u5316\u500f\u5ffd\uff0c\u4e0d\u53ef\u6349\u6478\u3002\u8fd9\u65f6\u6613\u5170\u73e0\u5df2\u77e5\u9053\u654c\u6211\u53cc\u65b9\u4f18\u52a3\u6240 \u5728\uff0c\u800c\u81ea\u5df1\u62db\u719f\u529b\u6c89\uff0c\u4e00\u67c4\u77ed\u5251\u4f7f\u5f97\u51fa\u795e\u4eba\u5316\uff0c\u4ee5\u5251\u6cd5\u7684\u7cbe\u5999\uff0c\u62b5\u6d88\u529f\u529b\u7684\u4e0d\u8db3\uff0c\u695a\u662d\u5357\u65e0 \u6cd5\u9707\u98de\u5730\u7684\u5b9d\u5251\uff0c\u8feb\u5f97\u54ac\u7259\u82e6\u5b88\u3002\u6613\u5170\u73e0\u5251\u62db\u8d8a\u5c55\u8d8a\u5feb\uff0c\u653b\u5982\u96f7\u9706\u75be\u53d1\uff0c\u5b88\u5982\u6c5f\u6d77\u51dd\u5149\uff0c\u6325 \u6d12\u81ea\u5982\uff0c\u771f\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u6070\u5230\u597d\u5904\u3002\u695a\u662d\u5357\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u8fde\u8fde\u9000\u5b88\uff0c\u6613\u5170\u73e0\u559d\u9053\uff1a\u201c\u8fd9\u624d \u662f\u672c\u95e8\u7684\u5251\u6cd5\uff0c\u4f60\u61c2\u5f97\u4e86\u5417\uff1f\u201d\u695a\u662d\u5357\u53c8\u6c14\u53c8\u6012\uff0c\u5374\u4e0d\u6562\u7b54\u8bdd\uff0c\u53ea\u662f\u7d27\u7d27\u5c01\u95ed\u95e8\u6237\uff0c\u60f3\u4ed7\u7740 \u529f\u529b\u6df1\u539a\uff0c\u548c\u6613\u5170\u73e0\u5bf9\u8017\u3002\u6613\u5170\u73e0\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c\u4e8e\u6f2b\u4e0d\u7ecf\u610f\u4e4b\u95f4\uff0c\u53c8\u6742\u4ee5\u767d\u53d1\u9b54\u5973\u7684\u8f9b\u8fa3 \u5251\u6cd5\uff0c\u7a81\u65bd\u88ad\u51fb\uff0c\u5979\u628a\u4e24\u79cd\u6700\u4e0a\u4e58\u7684\u5251\u6cd5\u6df7\u5408\u6765\u7528\uff0c\u9664\u4e86\u529f\u529b\u7a0d\u4f4e\u4e4b\u5916\uff0c\u548c\u51cc\u672a\u98ce\u5df2\u5dee\u4e0d\u591a \u4e00\u6837\u3002\u695a\u662d\u5357\u5982\u4f55\u62b5\u6321\u5f97\u4e86\uff1f\u5fc3\u5185\u6697\u60f3\uff1a\u4e09\u5341\u516d\u7740\uff0c\u8d70\u4e3a\u4e0a\u7740\uff01\u6e38\u9f99\u5251\u731b\u7136\u4e00\u51b2\uff0c\u660e\u662f\u8fdb \u653b\uff0c\u5b9e\u662f\u8d70\u52bf\uff01\u6613\u5170\u73e0\u7a81\u7136\u4e00\u58f0\u6e05\u54a4\uff0c\u77ed\u5251\u4e00\u65cb\uff0c\u75be\u7684\u5012\u5377\u4e0a\u53bb\uff0c\u5251\u98ce\u9707\u8361\u4e2d\uff0c\u695a\u662d\u5357\u4e00\u6b65 \u5927\u53eb\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u5411\u4e0a\u4e00\u62d4\uff0c\u7a9c\u8d77\u4e24\u4e08\u591a\u9ad8\uff0c\u201c\u4e91\u91cc\u7ffb\u5408\u201d\uff0c\u771f\u4f3c\u71d5\u5b50\u4e00\u822c\uff0c\u5411\u524d\u76f4\u743c\u51fa \u53bb\u3002\u6613\u5170\u73e0\u628a\u8eab\u4e00\u8eac\uff0c\u4e5f\u50cf\u5f2f\u7bad\u822c\u98de\u5c04\u800c\u6765\uff0c\u5982\u5f71\u968f\u5f62\uff0c\u7d27\u63a5\u6251\u5230\uff0c\u5251\u638c\u9f50\u98de\u3002\u695a\u662d\u5357\u6b66\u529f \u7740\u5b9e\u9ad8\u5f3a\uff0c\u867d\u53d7\u632b\u8d25\uff0c\u4ecd\u80fd\u53cd\u51fb\uff0c\u8eab\u672a\u7740\u5730\uff0c\u5df2\u662f\u53cd\u624b\u4e00\u5251\uff0c\u5c06\u6613\u5170\u73e0\u77ed\u5251\u8361\u5f00\uff0c\u4f46\u867d\u7136\u5982 \u6b64\uff0c\u53f3\u80c1\u4ecd\u88ab\u6613\u5170\u73e0\u638c\u98ce\u626b\u4e2d\uff0c\u6613\u5170\u73e0\u8fd9\u638c\u662f\u501f\u7740\u695a\u662d\u5357\u53bb\u52bf\uff0c\u5411\u524d\u201c\u987a\u6c34\u63a8\u821f\u201d\u4e00\u9001\uff0c\u548c \u592a\u6781\u62f3\u4e2d\u7684\u201c\u501f\u529b\u6253\u529b\u201d\uff0c\u6709\u5f02\u66f2\u540c\u5de5\u4e4b\u5999\uff0c\u695a\u662d\u5357\u8eab\u4e0d\u7531\u5df1\uff0c\u817e\u4e91\u9a7e\u96fe\u822c\u5730\u76f4\u98de\u51fa\u53bb\uff0c\u7adf \u7136\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u6454\u5012\u5730\u4e0a\uff0c\u5e78\u4ed6\u529f\u529b\u6df1\u539a\uff0c\u8dcc\u4e0b\u65f6\u5019\uff0c\u56db\u80a2\u7528\u529b\u5411\u4e0a\u4e00\u63d0\uff0c\u201c\u91d1\u8749\u620f\u6d6a\u201d \u76f4\u8df3\u8d77\u6765\uff0c\u6613\u5170\u73e0\u6402\u5934\u4e00\u5251\uff0c\u53c8\u7ed9\u4ed6\u4e00\u5251\u683c\u5f00\u3002\u6613\u5170\u73e0\u7ed9\u4ed6\u8fde\u6321\u4e24\u5251\uff0c\u950b\u5203\u76f8\u4ea4\uff0c\u5374\u5e76\u4e0d\u611f \u5230\u5982\u524d\u5403\u529b\uff0c\u5251\u5149\u98d8\u77a5\u4e2d\uff0c\u53ea\u89c1\u695a\u662d\u5357\u895f\u4e0a\u9c9c\u8840\u70b9\u70b9\uff0c\u539f\u6765\u4ed6\u7684\u53f3\u80a9\u88ab\u523a\u4f24\uff0c\u5de6\u624b\u4e5f\u7ed9\u65a9\u53bb \u4e24\u6307\uff0c\u6613\u5170\u73e0\u81ea\u5df1\u5374\u8fd8\u672a\u77e5\u9053\u3002 \u3000\u3000\u695a\u662d\u5357\u8d1f\u4f24\u4e4b\u540e\uff0c\u53c8\u88ab\u7a77\u8ffd\uff0c\u53cd\u8eab\u518d\u6597\uff0c\u4ee5\u6b7b\u76f8\u6251\uff0c\u5251\u631f\u52b2\u98ce\uff0c\u62db\u62db\u72e0\u8fa3\uff0c\u8fd9\u4e00\u6765\u6613\u5170 \u73e0\u5012\u4e0d\u6562\u8fc7\u4efd\u8fdb\u8feb\uff0c\u695a\u662d\u5357\u72c2\u547c\u6012\u53f7\uff0c\u957f\u5251\u6325\u5288\uff0c\u4fe8\u5982\u4e00\u5934\u53d7\u4f24\u7684\u72ee\u5b50\u3002\u6613\u5170\u73e0\u51dd\u795e\u9759\u6c14\uff0c \u5728\u9ed1\u6697\u4e2d\u7ec6\u8fa8\u654c\u4eba\u8eab\u5f62\uff0c\u8fdb\u9000\u8d8b\u907f\uff0c\u8f97\u8f6c\u6597\u4e86\u4e94\u516d\u5341\u62db\uff0c\u695a\u662d\u5357\u6076\u6c14\u6e10\u6d88\uff0c\u6613\u5170\u73e0\u4e58\u673a\u8fde\u4f7f \u767d\u53d1\u9b54\u5973\u72ec\u95e8\u8fa3\u62db\uff0c\u5de6\u4e00\u5251\uff0c\u53f3\u4e00\u5251\uff0c\u4e0a\u4e00\u5251\uff0c\u4e0b\u4e00\u5251\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u695a\u662d\u5357\u53c8\u8fde\u53d7\u51e0\u5904\u5251 \u4f24\uff0c\u6012\u543c\u58f0\u58f0\uff0c\u518d\u62fc\u6b7b\u53cd\u6251\uff0c\u6613\u5170\u73e0\u6377\u4f3c\u7075\u732b\uff0c\u5341\u5206\u6e9c\u6ed1\uff0c\u695a\u662d\u5357\u6251\u5230\u4e1c\uff0c\u5979\u8eb2\u5411\u897f\uff0c\u695a\u662d \u5357\u6251\u5230\u5357\uff0c\u5979\u8eb2\u5411\u5317\uff0c\u695a\u662d\u5357\u53c8\u6c14\u53c8\u6025\uff0c\u5934\u8111\u660f\u4e71\uff0c\u5982\u4f55\u6251\u5f97\u7740\u5979\u3002\u518d\u8fc7\u4e00\u4f1a\uff0c\u695a\u662d\u5357\u5df2\u662f \u518d\u8870\u4e09\u7aed\uff0c\u6613\u5170\u73e0\u8fd0\u5251\u5982\u98ce\uff1b\u77ed\u5251\u500f\u7ffb\uff0c\u695a\u662d\u5357\u72c2\u53eb\u4e00\u58f0\uff0c\u5de6\u81c2\u5df2\u7ed9\u65a9\u65ad\uff0c\u6e38\u9f99\u5251\u7a81\u7136\u5012 \u8f6c\uff0c\u5411\u5fc3\u7a9d\u4e00\u63d2\uff0c\u5389\u58f0\u53eb\u9053\uff1a\u201c\u5927\u4e08\u592b\u5b81\u6b7b\u4e0d\u8fb1\uff0c\u4f60\u8981\u6740\u6211\uff0c\u90a3\u662f\u4f11\u60f3\uff01\u201d\u695a\u662d\u5357\u5fc3\u9ad8\u6c14 \u50b2\uff0c\u76ee\u7a7a\u4e00\u4e16\uff0c\u4e0d\u6599\u5374\u88ab\u81ea\u5df1\u7684\u665a\u8f88\u6240\u8d25\uff0c\u81ea\u77e5\u5fc5\u6b7b\uff0c\u4ecd\u7136\u6b7b\u8981\u9762\u5b50\uff0c\u6b7b\u4e0d\u8ba4\u8f93\uff0c\u81ea\u6740\u8eab \u4ea1\uff0c\u4e34\u6b7b\u5c1a\u4e0d\u6094\u609f\uff1b\u771f\u662f\u53ef\u7b11\u53ef\u601c\u3002\u6613\u5170\u73e0\u5230\u5e95\u5973\u5b69\u5b50\u5fc3\u8f6f\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5978\u8d3c\u5440\u5978\u8d3c\uff0c\u4f60 \u82e5\u65e9\u80fd\u8fa8\u6e05\u662f\u975e\uff0c\u4f55\u81f3\u5982\u6b64\uff01\u201d\u628a\u4ed6\u7684\u6e38\u9f99\u5251\u62d4\u51fa\uff0c\u63d2\u8fdb\u5251\u9798\uff0c\u4f69\u5728\u8eab\u4e0a\u3002\u4fa7\u8033\u4e00\u542c\uff0c\u5916\u9762 \u5bc2\u7136\u65e0\u58f0\uff0c\u653e\u773c\u4e00\u671b\uff0c\u9661\u957f\u7684\u752c\u9053\uff0c\u9ed1\u6c89\u6c89\u7684\u4e0d\u77e5\u901a\u5411\u4f55\u65b9\u3002\u7d22\u6027\u653e\u6b65\u5411\u524d\u8d70\u53bb\u3002 \u3000\u3000\u4e14\u8bf4\u6842\u4ef2\u660e\u88ab\u51e0\u4e2a\u6b66\u58eb\u7eca\u7740\uff0c\u8ffd\u4e0d\u4e0a\u695a\u662d\u5357\uff0c\u5927\u6012\u4e4b\u4e0b\uff0c\u817e\u86df\u5b9d\u5251\uff0c\u4e00\u9635\u4e71\u6325\uff0c\u628a\u51e0\u4e2a \u6b66\u58eb\u5168\u90fd\u6740\u6b7b\u3002\u5085\u9752\u4e3b\u7387\u7fa4\u96c4\u8ffd\u4e0a\u3002\u5df2\u4e0d\u89c1\u4e86\u6613\u5170\u73e0\u3002\u6842\u4ef2\u660e\u9053\uff1a\u201c\u5979\u5355\u8eab\u8ffd\u695a\u662d\u5357\u53bb \u4e86\u3002\u201d\u5085\u9752\u4e3b\u5341\u5206\u62c5\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u59ae\u5b50\u4e5f\u771f\u5192\u9669\uff01\u201d\u6842\u4ef2\u660e\u6325\u5251\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u628a\u6e05\u519b\u6b66\u58eb \u6740\u5c3d\uff0c\u4e0d\u6101\u627e\u4e0d\u7740\u5979\uff01\u201d\u5085\u9752\u4e3b\u5ffd\u7136\u5c06\u4ed6\u7684\u624b\u81c2\u4e00\u62c9\uff0c\u626c\u58f0\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u662f\u6c49\u4eba\uff0c\u4f55\u82e6\u4e3a \u80e1\u864f\u62fc\u547d\uff0c\u6211\u4eec\u7f51\u5f00\u4e00\u9762\uff0c\u4f60\u4eec\u5feb\u9003\uff01\u201d\u7981\u536b\u519b\u6b66\u58eb\u89c1\u4ed6\u4eec\u7684\u9996\u9886\u695a\u662d\u5357\u5c1a\u81ea\u9003\u9038\u65e0\u8e2a\uff0c\u800c \u4e14\u5e03\u8fbe\u62c9\u5bab\u5927\u706b\u718a\u718a\uff0c\u518d\u4e0d\u9003\u65f6\uff0c\u52bf\u5fc5\u9677\u8eab\u706b\u6d77\uff0c\u4e5f\u5c31\u4e0d\u518d\u604b\u6218\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u56db\u6563\u5954\u9003\u3002\u5085 \u9752\u4e3b\u9053\uff1a\u201c\u6211\u4eec\u5206\u6279\u641c\u5bab\uff0c\u8d81\u706b\u52bf\u5c1a\u672a\u71ce\u539f\uff0c\u8d76\u5feb\u628a\u6613\u5170\u73e0\u5bfb\u51fa\u6765\u3002\u4e0d\u8bba\u5bfb\u5f97\u4e0e\u5426\uff0c\u5929\u660e\u524d \u90fd\u8981\u81f3\u5bab\u5916\u4f1a\u9f50\u3002\u201d \u3000\u3000\u5218\u90c1\u82b3\u867d\u7136\u542c\u5f97\u5587\u561b\u4f20\u8bed\uff0c\u8bf4\u662f\u51cc\u672a\u98ce\u5df2\u5e73\u5b89\u8131\u79bb\uff0c\u4f46\u5fc3\u4e2d\u5230\u5e95\u4e0d\u65e0\u7275\u6842\uff0c\u5f97\u9a6c\u65b9\u6240\u7ed8 \u7684\u5730\u56fe\uff0c\u4e0e\u5f20\u534e\u662d\u5468\u9752\u7b49\u4eba\u4e00\u8def\u6309\u56fe\u7d22\u9e66\uff0c\u8fc5\u901f\u76f4\u6251\u8ff7\u5bab\u4e2d\u592e\uff0c\u66f2\u6298\u8fc2\uff0c\u8d70\u4e86\u4e00\u9635\uff0c\u5468\u9752\u5ffd \u5730\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u56da\u7981\u51cc\u672a\u98ce\u7684\u5bc6\u5ba4\uff0c\u6211\u4eec\u8fdb\u53bb\u770b\u770b\u3002\u201d\u5bc6\u5ba4\u5c1a\u672a\u7740\u706b\uff0c\u95e8\u6237\u53c8\u662f\u5927 \u5f00\uff0c\u5218\u90c1\u82b3\u968f\u7740\u5468\u9752\u95ef\u8fdb\uff0c\u5ba4\u5185\u6709\u4eba\u95ee\u9053\uff1a\u201c\u51cc\u672a\u98ce\u6349\u56de\u4e86\u5417\uff1f\u201d\u5218\u90c1\u82b3\u633a\u5251\u4e00\u51b2\uff0c\u811a\u4e0b\u5ffd \u89c9\u6709\u7269\u7eca\u7740\uff0c\u540c\u65f6\u6709\u52b2\u98ce\u6251\u5230\uff0c\u5218\u90c1\u82b3\u4f38\u5251\u4e00\u6321\uff0c\u7adf\u88ab\u9707\u51fa\u6b65\u3002\u5468\u3001\u5f20\u4e8c\u4eba\uff0c\u53cc\u4e0a\u8fce\u654c\uff0c\u5218 \u90c1\u82b3\u817e\u8eab\u8131\u5f00\uff0c\u95e8\u7a97\u5916\u706b\u7167\u5c04\u8fdb\u6765\uff0c\u53ea\u89c1\u5730\u4e0a\u4e00\u6ee9\u6d53\u8840\uff0c\u8840\u6cca\u4e2d\u8eba\u7740\u7684\u7adf\u662f\u97e9\u5fd7\u90a6\uff03\u70d8\u4e0a\u5212 \u6709\u4e24\u9053\u5200\u75d5\uff0c\u80f8\u53e3\u88ab\u5251\u523a\u7a7f\u4e00\u4e2a\u5927\u6d1e\u3002\u5218\u90c1\u82b3\u9b42\u98de\u9b44\u6563\uff0c\u60f3\u8d77\u97e9\u5fd7\u90a6\u65e5\u524d\u7684\uff0c\u5fc3\u4e2d\u4e86\u7136\u3002\u77e5 \u9053\u4ed6\u4ee5\u81ea\u5df1\u7684\u6027\u547d\u6362\u4e86\u51cc\u672a\u98ce\u7684\u6027\u547d\uff0c\u970e\u65f6\u95f4\u5267\u75db\u653b\u5fc3\uff0c\u6b32\u54ed\u65e0\u6cea\u3002\u4f46\u8033\u8fb9\u542c\u5f97\u91d1\u94c1\u4ea4\u9e23\u4e4b \u58f0\uff0c\u5374\u4e0d\u7531\u5f97\u5979\u4e0d\u970d\u7136\u4e00\u7701\uff1a\u201c\u6b64\u523b\u8fd8\u4e0d\u662f\u6211\u60b2\u4f24\u7684\u65f6\u5019\uff01\u201d\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c1\u5468\u9752\u548c\u5f20\u534e\u662d \u5df2\u662f\u7ed9\u6210\u5929\u633a\u6740\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u539f\u6765\u6210\u5929\u633a\u88ab\u97e9\u5fd7\u90a6\u70b9\u4e86\u7a74\u9053\uff0c\u4ed7\u7740\u6b66\u529f\u6df1\u6e5b\uff0c\u6697\u4e2d\u8fd0\u6c14\u884c\u8840\uff0c\u8fc7\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u65e9\u5df2\u89e3 \u5f00\u4e86\u3002\u5f20\u534e\u662d\u6325\u52a8\u51cc\u672a\u98ce\u8d60\u4ed6\u7684\u964d\u9f99\u5b9d\u6756\uff0c\u786c\u63a5\u654c\u62db\uff0c\u6210\u5929\u633a\u94c1\u7b14\u201c\u6a2a\u67b6\u91d1\u6881\u201d\uff0c\u5f80\u4e0a\u4e00 \u6258\uff0c\u5f20\u534e\u662d\u864e\u53e3\u53d1\u75db\uff0c\u6210\u5929\u633a\u4e5f\u89c9\u5bf9\u65b9\u5175\u5668\u575a\u786c\u5f02\u5e38\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5468\u9752\u5df2\u662f\u9000\u800c\u590d\u4e0a\uff0c\u5218\u90c1 \u82b3\u4ea6\u5df2\u4ece\u4fa7\u9762\u52a9\u653b\u3002\u6210\u5929\u633a\u662f\u6e05\u5bab\u5927\u5185\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u529b\u6218\u4e09\u4eba\uff0c\u7ef0\u6709\u88d5\u4f59\uff0c\u4f46\u5bab\u5185\u706b\u5149\u51b2 \u5929\uff0c\u6740\u58f0\u9707\u5730\uff0c\u4ed6\u4e0d\u77e5\u5916\u95f4\u865a\u5b9e\uff0c\u786e\u662f\u4e0d\u6562\u604b\u6218\uff0c\u53cc\u7b14\u659c\u98de\uff0c\u51b2\u5f00\u4e00\u6761\u51fa\u8def\uff0c\u62d4\u8db3\u98de\u5954\uff0c\u4e09 \u4eba\u4e2d\u5218\u90c1\u82b3\u6b66\u529f\u8f83\u9ad8\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u65bd\u5c55\u8f7b\u529f\uff0c\u968f\u540e\u6025\u9053\u3002\u5f20\u534e\u662d\u53eb\u9053\uff1a\u201c\u5218\u5927\u59d0\uff0c\u7a77\u5bc7\u83ab \u8ffd\uff01\u201d\u5218\u90c1\u82b3\u53ea\u9053\u97e9\u5fd7\u90a6\u662f\u6210\u5929\u633a\u6740\u7684\uff0c\u6ee1\u6000\u60b2\u6124\uff0c\u7adf\u6beb\u4e0d\u987e\u8651\u6210\u5929\u633a\u6b66\u529f\u6bd4\u5979\u9ad8\u51fa\u8bb8\u591a\u3002 \u4e00\u5fc3\u53ea\u4e3a\u826f\u53cb\u62a5\u4ec7\uff0c\u5bf9\u80cc\u540e\u558a\u58f0\u5145\u8033\u4e0d\u95fb\u3002 \u3000\u3000\u8dd1\u4e86\u4e00\u9635\uff0c\u6210\u5929\u633a\u94c1\u7b14\u5728\u5899\u4e0a\u4e00\u70b9\uff0c\u6697\u95e8\u51fa\u73b0\uff0c\u5218\u90c1\u82b3\u4e0d\u5047\u601d\u7d22\uff0c\u4e5f\u8ddf\u7740\u8fdb\u53bb\u3002\u6210\u5929\u633a \u54c8\u54c8\u5927\u7b11\uff0c\u968f\u624b\u8f6c\u52a8\u673a\u62ec\uff0c\u628a\u6697\u95e8\u5173\u4e0a\uff0c\u4ed6\u6b63\u662f\u60f3\u8bf1\u5218\u90c1\u82b3\u8fdb\u6765\uff0c\u597d\u64d2\u7740\u5979\u4f5c\u4e3a\u4eba\u8d28\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u6210\u5929\u633a\u94c1\u7b14\u4e00\u51b2\uff0c\u5218\u90c1\u82b3\u7528\u65e0\u6781\u5251\u4e2d\u201c\u4e58\u9f99\u5f15\u51e4\u201d\u7684\u62db\u6570\uff0c\u628a\u5224\u5b98\u7b14\u7c98\u81f3\u5916\u95e8\uff0c \u6210\u5929\u633a\u5de6\u7b14\u4e00\u62ac\uff0c\u53cc\u7b14\u4e00\u5939\uff0c\u628a\u5218\u90c1\u82b3\u7684\u9752\u94a2\u5c81\u5939\u4f4f\uff0c\u559d\u58f0\uff1a\u201c\u64a4\u624b\uff01\u201d\u5218\u90c1\u82b3\u864e\u53e3\u9178\u9ebb\uff0c \u9752\u94a2\u5251\u5e94\u58f0\u5815\u5730\uff01\u6025\u6025\u5f80\u524d\u4e00\u8dc3\uff0c\u6210\u5929\u633a\u4f38\u7b14\u4e00\u63a2\uff0c\u9ed1\u6697\u4e2d\u8ba4\u7a74\u70b9\u7a74\u3002\u5218\u90c1\u82b3\u7a81\u7136\u53cd\u624b\u4e00 \u626c\uff0c\u4e00\u9053\u84dd\u5149\u5728\u96e8\u9053\u4e0a\u7a7a\u55e4\u7684\u4e00\u58f0\u7206\u70b8\u5f00\u6765\uff0c\u6210\u5929\u633a\u5413\u4e86\u4e00\u8df3\uff0c\u6025\u5fd9\u98d8\u8eab\u95ea\u8fc7\uff0c\u5218\u90c1\u82b3\u7684\u86c7 \u7130\u7bad\u662f\u6b66\u6797\u4e2d\u4e00\u79cd\u72ec\u95e8\u6697\u5668\uff0c\u542b\u6709\u786b\u6bd2\uff0c\u7740\u7269\u5373\u71c3\uff0c\u89c1\u4f24\u5373\u94bb\uff0c\u6df1\u5165\u76ae\u80a4\uff0c\u5341\u5206\u5389\u5bb3\u3002\u752c\u9053 \u72ed\u7a84\uff0c\u8d8b\u907f\u8270\u96be\uff0c\u6210\u5929\u633a\u6b66\u529f\u867d\u9ad8\uff0c\u4e5f\u5fc3\u5b58\u6212\u60e7\u3002\u4e24\u4eba\u5728\u752c\u9053\u4e2d\u8ffd\u9010\uff0c\u5218\u90c1\u82b3\u88ab\u8feb\u5230\u6025\u65f6\uff0c \u5c31\u662f\u4e00\u652f\u706b\u7bad\uff0c\u6210\u5929\u633a\u6216\u5c55\u8f7b\u529f\u907f\u8fc7\uff0c\u6216\u8fd0\u638c\u98ce\u6253\u706d\uff0c\u4ecd\u662f\u7a77\u8ffd\u4e0d\u5df2\u3002 \u3000\u3000\u4e24\u4eba\u5728\u752c\u9053\u4e2d\u8d8a\u8fdb\u8d8a\u6df1\uff0c\u84e6\u7136\u95f4\uff0c\u5218\u90c1\u82b3\u53d1\u73b0\u86c7\u7130\u7bad\u5df2\u7ecf\u7528\u5b8c\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u6210\u5929\u633a\u53c8\u5df2 \u8ffd\u81f3\u80cc\u540e\uff0c\u5979\u53cd\u624b\u4e00\u626c\uff0c\u53eb\u58f0\u201c\u770b\u7bad\uff01\u201d\u6210\u5929\u633a\u672c\u80fd\u5730\u5f80\u65c1\u4e00\u95ea\uff0c\u5374\u4e0d\u89c1\u706b\u5149\u98de\u51fa\uff0c\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u5218\u90c1\u82b3\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u4f0e\u4fe9\uff0c\u8fd8\u4e0d\u8d76\u5feb\u6295\u964d\uff01\u201d\u9ed1\u6697\u4e2d\u84e6\u5730\u6709\u4eba\u63a5\u58f0\u53eb\u9053\uff1a\u201c\u5218\u5927\u59d0\uff0c \u662f\u4f60\u5417\uff1f\u201d\u6210\u5929\u633a\u94c1\u7b14\u5f80\u5916\u4e00\u7a7f\uff0c\u5df2\u5230\u5218\u90c1\u82b3\u80cc\u540e\uff0c\u5ffd\u7136\u624b\u8155\u4e00\u9707\uff0c\u201c\u4e01\u5f53\u201d\u4e00\u58f0\uff0c\u5224\u5b98\u7b14 \u7adf\u88ab\u8361\u5f00\uff0c\u6765\u4eba\u6301\u7740\u4e00\u628a\u5bd2\u5149\u95ea\u95ea\u7684\u5b9d\u5251\uff0c\u9ed1\u6697\u4e2d\u661f\u7738\u70af\u70af\uff0c\u4f3c\u662f\u4e00\u4e2a\u5c11\u5973\u3002 \u3000\u3000\u5218\u90c1\u82b3\u5927\u559c\u53eb\u9053\uff1a\u201c\u4f60\u662f\u5170\u73e0\u59b9\u59b9\uff1f\u201d\u6210\u5929\u633a\u6b66\u529f\u6df1\u6e5b\uff0c\u9ed1\u6697\u4e2d\u4ea6\u53ef\u8fa8\u7269\uff0c\u8fd9\u65f6\u4e5f\u8ba4\u51fa \u4e86\u8f6e\u5eca\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5973\u98de\u8d3c\u597d\u5927\u80c6\uff01\u4f60\u4e0d\u6015\u518d\u5750\u4e00\u6b21\u5929\u7262\uff1f\u201d\u4e00\u6643\u8eab\uff0c\u8eac\u8170\u63c9\u8fdb\uff0c\u5de6\u8fa8\u5224 \u5b98\u7b14\u659c\u70b9\u9762\u95e8\uff0c\u6613\u5170\u73e0\u5fae\u4e00\u4fa7\u8138\uff0c\u6210\u5929\u633a\u8fd9\u62db\u672c\u662f\u865a\u62db\uff0c\u5de6\u624b\u4e00\u64a4\uff0c\u53f3\u624b\u5224\u5b98\u7b14\u5f80\u5916\u4e00\u7a7f\uff0c \u500f\u5730\u6a2a\u8eab\uff0c\u7167\u6613\u5170\u73e0\u7684\u4e2d\u76d8\u201c\u4e91\u53f0\u7a74\u201d\u4fbf\u4e0b\u91cd\u624b\uff01\u6613\u5170\u73e0\u4e00\u95ea\u95ea\u5f00\uff0c\u77ed\u5251\u5f80\u4e0b\u4e00\u6c89\uff0c\u659c\u524a\u80a9 \u81c2\uff0c\u987a\u65a9\u8109\u95e8\uff0c\u8fd9\u662f\u767d\u53d1\u9b54\u5973\u7684\u72ec\u95e8\u8fa3\u62db\u4e4b\u4e00\uff0c\u6210\u5929\u633a\u84e6\u89c9\u51b7\u6c14\u68ee\u68ee\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u9661\u7136\u5f80\u540e \u4e00\u6ed1\uff0c\u62a1\u53cc\u7b14\u65cb\u8eab\u76d8\u6253\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u5c06\u8fd9\u62db\u5316\u5f00\uff01\u6613\u5170\u73e0\u4e00\u9762\u53d1\u62db\uff0c\u4e00\u9762\u95ee\u9053\uff1a\u201c\u5218\u5927\u59d0\uff0c \u4f60\u6ca1\u53d7\u4f24\u5427\uff1f\u201d\u5218\u90c1\u82b3\u9053\uff1a\u201c\u6ca1\u6709\u3002\u8fd9\u4eba\u662f\u6740\u6b7b\u4f60\u97e9\u53d4\u53d4\u7684\u51f6\u624b\uff0c\u4e0d\u8981\u653e\u8fc7\uff01\u201d\u6613\u5170\u73e0\u4e00\u8bb0 \u8fa3\u62db\u628a\u6210\u5929\u633a\u8feb\u5f00\uff0c\u628a\u6e38\u9f99\u5251\u89e3\u4e0b\uff0c\u626b\u7ed9\u5218\u90c1\u82b3\u9053\uff1a\u201c\u8fd9\u662f\u695a\u662d\u5357\u7684\u6e38\u9f99\u5251\uff0c\u4f60\u62ff\u53bb\uff01\u201d \u3000\u3000\u5218\u90c1\u82b3\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u695a\u662d\u5357\u90a3\u8d3c\u5b50\u600e\u4e48\u4e86\uff1f\u201d\u6613\u5170\u73e0\u6de1\u7136\u8bf4\u9053\uff1a\u201c\u6211\u628a\u4ed6\u6740\u6389\u4e86\uff01\u201d\u5979 \u8bf4\u5f97\u751a\u4e3a\u5e73\u9759\uff0c\u597d\u50cf\u5e76\u4e0d\u662f\u4ec0\u4e48\u4e86\u4e0d\u8d77\u7684\u4e8b\u60c5\u3002\u6210\u5929\u633a\u542c\u4e86\uff0c\u5374\u5982\u6674\u5929\u9739\u96f3\uff01\u7a0d\u4e00\u5b9a\u795e\uff0c\u5fc3 \u91cc\u5c06\u4fe1\u5c06\u7591\uff0c\u6697\u9053\uff1a\u8fd9\u5973\u5a03\u5b50\u5251\u6cd5\u867d\u5c5e\u4e0d\u51e1\uff0c\u5374\u5982\u4f55\u80fd\u628a\u695a\u662d\u5357\u6740\u6389\uff1f\u8fd8\u62a2\u4e86\u4ed6\u7684\u5b9d\u5251\u3002\u6613 \u5170\u73e0\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u5374\u662f\u4e1d\u6beb\u4e0d\u6162\uff0c\u9aa4\u7136\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u53cc\u81c2\u201c\u91d1\u96d5\u5c55\u7fc5\u201d\uff0c\u5b9d\u5251\u4e0b\u65a9 \u654c\u4eba\u4e2d\u76d8\uff0c\u624b\u6cd5\u8fc5\u75be\u65e0\u4f26\uff0c\u6210\u5929\u633a\u8eab\u7ecf\u767e\u6218\uff0c\u5fae\u566b\u4e00\u58f0\uff0c\u53cc\u7b14\u4e00\u5206\uff0c\u5de6\u624b\u5224\u5b98\u7b14\u62a1\u4e0b\u6765\u3002\u7167 \u77ed\u5251\u4e00\u5212\uff0c\u5c31\u624b\u5f80\u5916\u4e00\u6302\uff0c\u6a2a\u8eab\u8fdb\u6b65\uff0c\u53f3\u624b\u7b14\u201c\u4ed9\u4eba\u6307\u8def\u201d\uff0c\u5c45\u7136\u5728\u9ed1\u6697\u4e2d\u4e4b\u4e2d\uff0c\u63a2\u7a74\u4f4d\uff0c \u5bfb\u7a74\u9053\uff0c\u76f4\u5954\u6613\u5170\u73e0\u7684\u201c\u534e\u76d6\u7a74\u201d\u3002\u6613\u5170\u73e0\u634f\u5251\u8bc0\u4e00\u6307\u654c\u4eba\u8109\u95e8\uff0c\u5229\u5203\u631f\u98ce\uff0c\u4ee5\u653b\u4e3a\u5b88\uff0c\u65ad \u7389\u5251\u53cd\u51fb\u654c\u8155\uff0c\u6210\u5929\u633a\u8001\u7ec3\u5de7\u6ed1\uff0c\u5224\u5b98\u7b14\u624d\u53d1\u4fbf\u6536\uff0c\u84e6\u7136\u53d8\u62db\uff0c\u53cc\u70b9\u6613\u5170\u73e0\u4e24\u80c1\u7684\u201c\u592a\u4e59 \u7a74\u201d\uff0c\u8fd9\u4e00\u62db\u865a\u5b9e\u83ab\u6d4b\uff0c\u72e0\u6bd2\u5f02\u5e38\u3002\u4e0d\u6599\u6613\u5170\u73e0\u5251\u6cd5\uff0c\u66f4\u662f\u795e\u5999\uff0c\u811a\u4e0b\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u8eab\u4f53\u9661\u7f29 \u5c3a\u4f59\uff0c\u6070\u6070\u628a\u5224\u5b98\u7b14\u8ba9\u5f00\uff0c\u672a\u5bb9\u4ed6\u6536\u62db\u53d8\u62db\uff0c\u9053\u58f0\uff1a\u201c\u7740\uff01\u201d\u65ad\u7389\u5251\u5bd2\u5149\u4e00\u95ea\uff0c\u53cd\u5c55\u5251\u950b\uff0c \u864e\u53e3\u5411\u5916\uff0c\u75be\u5982\u9a87\u7535\uff0c\u5237\u7684\u4e00\u5251\uff0c\u523a\u5230\u9762\u95e8\uff0c\u6210\u5929\u633a\u53cc\u7b14\u62c4\u4e0a\u4e00\u5d29\uff0c\u6613\u5170\u73e0\u8155\u5b50\u5f80\u91cc\u4e00\u5408\uff0c \u77ed\u5251\u7ffb\u6210\u9634\u628a\uff0c\u9752\u5149\u518d\u95ea\uff0c\u9510\u98ce\u659c\u5439\uff0c\u4ece\u654c\u4eba\u53f3\u80a9\u7ffb\u4e0b\u6765\uff0c\u622a\u65a9\u53f3\u808b\u3002\u6210\u5929\u633a\u53cc\u7b14\u5df2\u5168\u5c01\u4e0a \u53bb\uff0c\u6025\u5207\u95f4\u54ea\u91cc\u64a4\u5f97\u56de\u6765\uff1f\u8feb\u5f97\u4e5f\u8d70\u9669\u62db\uff0c\u4ed7\u7740\u51e0\u5341\u5e74\u529f\u529b\uff0c\u4e0d\u8feb\u4e0d\u95ea\uff0c\u53cc\u7b14\u4e00\u6643\uff0c\u4ee5\u653b\u4e3a \u5b88\uff0c\u731b\u6251\u6613\u5170\u73e0\u4e2d\u76d8\uff0c\u5de6\u70b9\u201c\u671f\u95e8\u7a74\u201d\uff0c\u53f3\u70b9\u201c\u7cbe\u767d\u7a74\u201d\uff0c\u529b\u731b\u62db\u5feb\uff0c\u6613\u5170\u73e0\u4e0d\u60f3\u4e24\u8d25\u4ff1 \u4f24\uff0c\u4e3a\u52bf\u6240\u8feb\uff0c\u659c\u8eab\u4fa7\u6b65\uff0c\u907f\u654c\u6b63\u950b\uff0c\u5fae\u4e00\u8ba9\u8eab\uff0c\u6210\u5929\u633a\u501f\u52bf\u6536\u62db\uff0c\u8e0a\u8eab\u4e00\u7eb5\uff0c\u659c\u7a9c\u51fa\u4e00\u4e08 \u4ee5\u5904\uff0c\u6b63\u60f3\u5954\u9003\uff0c\u731b\u7136\u659c\u4fa7\u91cc\u9752\u5149\u4e00\u95ea\uff0c\u6210\u5929\u633a\u4e3e\u7b14\u4e00\u8fce\uff0c\u5f3a\u5f29\u4e4b\u672b\uff0c\u529b\u91cf\u5927\u51cf\uff0c\u53ea\u542c\u5f97\u5f53 \u7684\u4e00\u58f0\uff0c\u706b\u82b1\u84ec\u98de\uff0c\u7b14\u5c16\u5df2\u7ed9\u524a\u6389\uff0c\u800c\u6765\u4eba\u4e5f\u7ed9\u9707\u8dcc\u5c18\u57c3\u3002 \u3000\u3000\u6613\u5170\u73e0\u4e00\u743c\u800c\u524d\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u5218\u5927\u59d0\uff0c\u5f85\u6211\u6765\u6536\u62fe\u8fd9\u53ae\uff01\u201d\u8d81\u6210\u5929\u633a\u4e00\u6014\u4e4b\u9645\uff0c\u62a2\u5728 \u4e24\u4eba\u4e2d\u95f4\uff0c\u5b9d\u5251\u4e00\u6325\u53c8\u5c01\u4f4f\u4e86\u6210\u5929\u633a\u7684\u53bb\u8def\uff01 \u3000\u3000\u5218\u90c1\u82b3\u90a3\u5251\u7528\u4e86\u5341\u6210\u529b\u91cf\uff0c\u4e0d\u6599\u4ecd\u7ed9\u9707\u8dcc\uff0c\u53ea\u597d\u6a2a\u5251\u89c2\u6218\u3002\u6210\u5927\u633a\u7b14\u5c16\u88ab\u524a\uff0c\u8ba4\u51fa\u4e86\u5218 \u90c1\u82b3\u7684\u5b9d\u5251\u6b63\u662f\u695a\u662d\u5357\u90a3\u628a\u6e38\u9f99\u5251\uff0c\u8111\u95e8\u8f70\u7684\u4e00\u58f0\u9b42\u98de\u9b44\u6563\u3002\u770b\u6765\u6613\u5170\u73e0\u6240\u8bf4\u975e\u865a\uff0c\u695a\u662d\u5357 \u771f\u7684\u7ed9\u5979\u6740\u4e86\uff01\u53cc\u7b14\u98de\u821e\uff0c\u5de6\u53f3\u4e71\u7a9c\uff0c\u6025\u7740\u89c5\u8def\u6b32\u9003\u3002\u4ed6\u82e5\u4e0d\u614c\u9003\uff0c\u8fd8\u53ef\u4e0e\u6613\u5170\u73e0\u7f20\u6597\u8bb8 \u4e45\uff0c\u4ed6\u8fd9\u4e00\u60f3\u9003\uff0c\u5fc3\u795e\u5206\u6563\uff0c\u5982\u4f55\u6321\u5f97\u4f4f\u6613\u5170\u73e0\u5999\u609f\u901a\u7384\u7684\u5c71\u5929\u5251\u6cd5\uff1f\u518d\u6597\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u6613 \u5170\u73e0\u53c8\u559d\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u55e8\u7684\u4e00\u58f0\uff0c\u6210\u5929\u633a\u808b\u4e0b\u4e2d\u5251\uff0c\u811a\u6b65\u8e09\u8dc4\uff0c\u5f80\u65c1\u8fde\u9000\uff0c\u5218\u90c1\u82b3\u8d81\u52bf\u4e00 \u5251\u524a\u6765\uff0c\u6210\u5929\u633a\u53cc\u7b14\u7ed9\u6613\u5170\u73e0\u4e00\u5251\u5c01\u4f4f\uff0c\u65e0\u6cd5\u62b5\u6321\uff0c\u7adf\u7ed9\u5218\u90c1\u82b3\u524a\u65ad\u624b\u81c2\uff0c\u518d\u52a0\u4e00\u5251\uff0c\u9001\u4e86 \u6027\u547d\u3002 \u3000\u3000\u5218\u90c1\u82b3\u9053\uff1a\u201c\u597d\uff0c\u97e9\u5927\u54e5\u7684\u4ec7\u4e5f\u62a5\u4e86\uff0c\u54b1\u4eec\u89c5\u8def\u51fa\u53bb\uff01\u201d\u752c\u9053\u6f2b\u957f\uff0c\u9ed1\u9edd\u9edd\u7684\u4e0d\u77e5\u901a\u5411 \u4f55\u65b9\uff0c\u4e24\u4eba\u8d70\u4e86\u8bb8\u4e45\uff0c\u5140\u662f\u627e\u4e0d\u5230\u51fa\u8def\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u662f\u6210\u5927\u4eba\u5417\uff1f\u5feb\uff0c\u5feb\u6765\uff01\u51cc\u672a\u98ce\u2014\u2014\u201d\u201c\u963f\u5440\uff0c\u4e0d\u5bf9\uff01\u600e\u4e48\u662f\uff0c\u662f \u2014\u2014\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u662f\u540c\u65f6\u547c\u53eb\u7684\uff0c\u4f46\u4e5f\u4f3c\u4e4e\u662f\u5728\u540c\u4e00\u65f6\u5019\u6ce2\u4eba\u51fb\u5012\uff0c\u8ddf\u7740\u5c31\u662f\u4e24\u58f0\u6495\u5fc3\u88c2\u80ba\u7684\u60e8 \u547c\u4e86\uff01 \u3000\u3000\u539f\u6765\u5728\u8fd9\u752c\u9053\u51fa\u53e3\u4e4b\u5904\uff0c\u695a\u662d\u5357\u8fd8\u8bbe\u4e0b\u57cb\u4f0f\uff0c\u5b97\u8fbe\xb7\u5b8c\u771f\u5df2\u7ecf\u4e2d\u4e86\u6697\u7bad\u3002\u4f46\u5f53\u90a3\u4e24\u540d\u5b88 \u536b\u4e0a\u524d\u770b\u4e4b\u65f6\uff0c\u4e00\u4e2a\u88ab\u51cc\u672a\u98ce\u6253\u65ad\u8155\u9aa8\uff0c\u53e6\u4e00\u4e2a\u53d1\u73b0\u7adf\u7136\u662f\u65b0\u7acb\u7684\u201c\u6d3b\u4f5b\u201d\u4e4b\u65f6\uff0c\u5b97\u8fbe\xb7\u5b8c \u771f\u8d81\u4ed6\u5927\u60ca\u4e4b\u9645\uff0c\u4e5f\u5c06\u4ed6\u6309\u5012\u4e86\u3002 \u3000\u3000\u5218\u90c1\u82b3\u4e0e\u6613\u5170\u73e0\u65e9\u5df2\u5411\u7740\u58f0\u97f3\u6765\u5904\u98de\u5954\uff0c\u5979\u4eec\u6765\u5f97\u6b63\u5408\u65f6\uff0c\u628a\u7b2c\u4e09\u540d\u8ddf\u7740\u4e0a\u6765\u7684\u5c1a\u672a\u53d7 \u4f24\u7684\u536b\u58eb\u6740\u4e86\u3002\u5468\u9752\u968f\u540e\u6765\u5230\uff0c\u4ed6\u4e3a\u4eba\u8c28\u614e\uff0c\u5c06\u90a3\u4e24\u540d\u6323\u624e\u6b32\u8d77\u7684\u5b88\u536b\u5404\u81ea\u8865\u4e0a\u4e00\u5200\uff0c\u5168\u90e8 \u4e86\u7ed3\u3002\u752c\u9053\u91cc\u5df2\u7ecf\u6ca1\u6709\u4e86\u654c\u4eba\uff0c\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u51cc\u672a\u98ce\u662f\u88ab\u6210\u5929\u633a\u704c\u4e86\u9ebb\u836f\u7684\uff0c\u836f\u529b\u672c\u6765\u672a\u89e3\uff0c\u521a\u624d\u90a3\u4e00\u51fb\uff0c\u4e43\u662f\u51fa\u4e8e\u6c42\u751f\u7684\u672c\u80fd\uff0c\u4ed6 \u90a3\u6df1\u539a\u7684\u6b66\u529f\u5e95\u5b50\u53d1\u6325\u4e86\u5947\u8ff9\u822c\u7684\u6f5c\u80fd\uff0c\u4f46\u4e00\u51fb\u6210\u529f\uff0c\u4ed6\u4e5f\u597d\u50cf\u201c\u865a\u8131\u201d\u4e00\u822c\uff0c\u518d\u4e5f\u4f7f\u4e0d\u51fa \u534a\u70b9\u6c14\u529b\u4e86\u3002 \u3000\u3000\u5218\u90c1\u82b3\u62b1\u7740\u4ed6\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u672a\u98ce\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u597d\u50cf\u4e0d\u76f8\u4fe1\u773c\u524d\u7684\u73b0\u5b9e\uff0c\u53cc\u7738\u534a\u542f\uff0c\u54d1\u58f0\u9053\uff1a\u201c\u5218\u5927\u59d0\uff0c\u5f53\u771f\u662f\u4f60\uff1f\u6211\uff0c\u6211\u4e0d\u662f \u505a\u68a6\uff1f\u201d\u5218\u90c1\u82b3\u9053\uff1a\u201c\u5f53\u7136\u662f\u6211\uff0c\u4f60\u54ac\u54ac\u6307\u5934\uff0c\u770b\u75db\u4e0d\u75db\uff1f\u201d \u3000\u3000\u4f46\u51cc\u672a\u98ce\u5374\u662f\u8fde\u62ac\u8d77\u5934\u7684\u6c14\u529b\u90fd\u6ca1\u6709\u4e86\u3002\u4e0d\u8fc7\uff0c\u4ed6\u4e5f\u65e0\u987b\u7528\u54ac\u6307\u5934\u6765\u8bc1\u660e\u4e0d\u662f\u505a\u68a6\u4e86\u3002 \u3000\u3000\u4ed6\u770b\u6e05\u695a\u4e86\u662f\u5218\u90c1\u82b3\uff0c\u4e00\u53e3\u6c14\u677e\u4e86\u4e0b\u6765\uff0c\u767b\u65f6\u5c31\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6613\u5170\u73e0\u5927\u5403\u4e00\u60ca\u9053\uff1a\u201c\u53d4\u53d4\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u597d\u5728\u5218\u90c1\u82b3\u7ecf\u9a8c\u8001\u5230\uff0c\u867d\u60ca\u4e0d\u4e71\uff0c\u4e00\u63a2\u8109\u606f\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u53ea\u662f\u6c14\u529b\u8017\u5c3d\uff0c\u6162\u6162\u4f1a\u9192\u8fc7\u6765 \u7684\uff01 \u3000\u3000\u6b64\u65f6\u5979\u4eec\u624d\u60f3\u8d77\u4e86\u8eba\u5728\u51cc\u672a\u98ce\u65c1\u8fb9\u7684\u5b97\u8fbe\xb7\u5b8c\u771f\u3002 \u3000\u3000\u5218\u90c1\u82b3\u5145\u6ee1\u6b49\u610f\uff0c\u66ff\u4ed6\u62d4\u51fa\u5229\u7bad\uff0c\u6613\u5170\u73e0\u7ed9\u4ed6\u5728\u4f24\u53e3\u6577\u4e0a\u91d1\u521b\u836f\uff0c\u8bf4\u9053\uff1a\u201c\u6d3b\u4f5b\uff0c\u591a\u8c22 \u4f60\u6551\u4e86\u6211\u7684\u51cc\u53d4\u53d4\u3002\u201d \u3000\u3000\u5b97\u8fbe\xb7\u5b8c\u771f\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u90fd\u662f\u97e9\u5927\u9655\u7684\u529f\u52b3\uff0c\u4ed6\u624d\u662f\u771f\u6b63\u7684\u6d3b\u4f5b\u3002\u201d \u3000\u3000\u5218\u90c1\u82b3\u5185\u759a\u4e8e\u5fc3\uff0c\u6b49\u610f\u66f4\u6df1\uff0c\u773c\u6cea\u7981\u4e0d\u4f4f\u4e00\u9897\u9897\u800c\u4e0b\u3002\u5b97\u8fbe\xb7\u5b8c\u771f\u9053\uff1a\u201c\u4f60\u4eec\u8d76\u5feb\u51fa \u53bb\uff0c\u518d\u8fdf\u5c31\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u6613\u5170\u73e0\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u5b97\u8fbe.\u5b8c\u771f\u9053\uff1a\u201c\u6211\u7559\u5728\u8fd9\u513f\u3002\u201d \u3000\u3000\u5218\u90c1\u82b3\u62b9\u6389\u773c\u6cea\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u90a3\u600e\u4e48\u884c\uff1f\u201d \u3000\u3000\u5b97\u8fbe\xb7\u5b8c\u771f\u6ca1\u6709\u56de\u7b54\uff0c\u5374\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u695a\u662d\u5357\u5462\uff1f\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u5df2\u7ecf\u7ed9\u6211\u6740\u4e86\u3002\u201d\u5b97 \u8fbe\xb7\u5b8c\u771f\u518d\u95ee\uff1a\u201c\u6210\u5929\u633a\u5462\uff1f\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u4e5f\u5df2\u7ed9\u6211\u4eec\u6740\u4e86\u3002\u201d\u5b97\u8fbe\xb7\u5b8c\u771f\u547c\u4e86\u53e3\u6c14\uff0c\u8bf4 \u9053\uff1a\u201c\u90a3\u4f60\u4eec\u5c31\u4e0d\u7528\u66ff\u6211\u4eec\u62c5\u5fe7\u4e86\uff0c\u8fd9\u4e24\u4e2a\u4eba\u6b7b\u6389\uff0c\u5c31\u6ca1\u4eba\u77e5\u9053\u6211\u5728\u8fd9\u91cc\u66fe\u7ecf\u505a\u8fc7\u4e9b\u4ec0\u4e48 \u4e86\u3002\u7531\u6211\u5e26\u51cc\u5927\u4fa0\u51fa\u53bb\uff0c\u8fd9\u53ea\u662f\u6ca1\u529e\u6cd5\u4e2d\u7684\u529e\u6cd5\uff0c\u6709\u4f60\u4eec\u4ee3\u52b3\uff0c\u4e0d\u66f4\u597d\u5417\uff1f\u6211\u7684\u4f24\u5e76\u65e0\u5927 \u788d\uff0c\u4ed6\u4eec\u4e5f\u7edd\u5bf9\u4e0d\u6562\u52a0\u5bb3\u6211\u7684\uff0c\u4f60\u4eec\u5927\u53ef\u653e\u5fc3\uff0c\u5feb\u5feb\u8d70\u5427\uff01\u201d\u8981\u77e5\u5b97\u8fbe\xb7\u5b8c\u771f\u4e43\u662f\u6e05\u5ef7\u5c01\u8d50 \u7684\u6d3b\u4f5b\uff0c\u9664\u975e\u8feb\u4e0d\u5f97\u5df2\uff0c\u5426\u5219\u4ed6\u5f53\u7136\u8fd8\u662f\u60f3\u548c\u6e05\u5ef7\u7ef4\u6301\u5173\u7cfb\u3002 \u3000\u3000\u5218\u90c1\u82b3\u542c\u4ed6\u8bf4\u5f97\u6709\u7406\uff0c\u800c\u4e14\u5728\u8fd9\u6837\u7684\u60c5\u51b5\u5e95\u4e0b\uff0c\u5979\u4e5f\u7684\u786e\u662f\u5f88\u96be\u517c\u987e\uff0c\u53ea\u597d\u4f9d\u4ece\u4ed6\u4e86\u3002 \u3000\u3000\u5468\u9752\u80cc\u8d77\u51cc\u672a\u98ce\uff0c\u5e26\u4ed6\u4eec\u8d70\u51fa\u752c\u9053\u3002\u752c\u9053\u51fa\u53e3\u5904\u5df2\u662f\u8fdc\u79bb\u5e03\u8fbe\u62c9\u5bab\u7684\u4e00\u6761\u8857\u9053\u3002 \u3000\u3000\u53ef\u662f\u8fd8\u6709\u4e00\u4e2a\u96be\u9898\u51fa\u73b0\u5728\u4ed6\u4eec\u7684\u9762\u524d\u3002\u4ed6\u4eec\u539f\u6765\u6240\u4f4f\u7684\u5730\u65b9\u662f\u5728\u5e02\u90ca\uff0c\u6700\u5c11\u8fd8\u8981\u8d70\u4e00\u4e2a \u65f6\u8fb0\u3002\u4ed6\u4eec\u4e0d\u77e5\u9053\u5bab\u4e2d\u53ae\u6740\u7684\u7ed3\u679c\u5982\u4f55\uff0c\u4e5f\u4e0d\u77e5\u9053\u654c\u65b9\u8fd8\u6709\u6ca1\u6709\u63f4\u5175\u5f00\u5230\uff0c\u4ed6\u4eec\u65e2\u7136\u4e0d\u80fd\u56de \u53bb\u4e0e\u7fa4\u96c4\u4f1a\u5408\uff0c\u5018\u82e5\u8981\u56de\u5230\u539f\u6765\u7684\u4f4f\u6240\uff0c\u5728\u8fd9\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u4e2d\uff0c\u662f\u4ec0\u4e48\u610f\u5916\u7684\u5371\u9669\u90fd\u6709\u53ef\u80fd\u53d1 \u751f\u7684\u3002\u600e\u4e48\u529e\u5462\uff1f \u3000\u3000\u5468\u9752\u5ffd\u5730\u60f3\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u5927\u59d0\uff0c\u9a6c\u65b9\u6628\u5929\u7ed9\u4f60\u9001\u4fe1\u4e4b\u540e\uff0c\u662f\u4e0d\u662f\u7559\u5728\u4f60\u4eec\u90a3 \u513f\uff1f\u201d \u3000\u3000\u5218\u90c1\u82b3\u9053\uff1a\u201c\u4e0d\uff0c\u4ed6\u60e6\u8bb0\u5bb6\u4eba\uff0c\u6211\u4eec\u7ed9\u4ed6\u6577\u836f\u4e4b\u540e\uff0c\u508d\u665a\u65f6\u5206\uff0c\u4ed6\u5c31\u56de\u5bb6\u53bb\u4e86\u3002\u201d \u3000\u3000\u5468\u9752\u8bf4\u9053\uff1a\u201c\u90a3\u54b1\u4eec\u5c31\u5140\u987b\u591a\u5192\u98ce\u9669\u4e86\uff0c\u9a6c\u65b9\u7684\u5bb6\u5c31\u5728\u9644\u8fd1\uff01\u201d \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u51cc\u672a\u98ce\u6e10\u6e10\u6709\u4e86\u77e5\u89c9\uff0c\u6162\u6162\u5f20\u5f00\u773c\u775b\u3002\u4ed6\u8fd8\u672a\u770b\u6e05\u695a\u773c\u524d\u666f\u7269\uff0c\u4fbf\u542c \u5f97\u4e00\u4e2a\u719f\u6089\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8c22\u8c22\u5929\uff0c\u7a46\u54e5\uff0c\u4f60\u7ec8\u4e8e\u5f97\u6551\u4e86\uff01\u201d\u51cc\u672a\u98ce\u4f3c\u662f\u4ece\u6076\u68a6\u4e2d\u9192\u6765\uff0c\u773c \u795e\u5446\u6ede\uff0c\u4e0d\u8a00\u4e0d\u8bed\u3002 \u3000\u3000\u5218\u90c1\u82b3\u9053\uff1a\u201c\u672a\u98ce\uff0c\u4f60\u7741\u773c\u77a7\u77a7\uff0c\u7ad9\u5728\u4f60\u9762\u524d\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u7741\u5927\u773c\u775b\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u97e9\u5927\u54e5\u5462\uff1f\u201d\u5218\u90c1\u82b3\u77e5\u9053\u4e0d\u80fd\u7792\u4ed6\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6b7b \u4e86\uff01\u201d\u51cc\u672a\u98ce\u6162\u6162\u7ad9\u4e86\u8d77\u6765\uff0c\u808c\u8089\u75c9\u631b\uff0c\u597d\u50cf\u53d7\u5230\u4e86\u76ae\u97ad\u62bd\u6253\u4f3c\u7684\uff0c\u5218\u90c1\u82b3\u5413\u7740\u4e86\uff0c\u559c\u60a6\u4e0e \u54c0\u4f24\u7684\u5fc3\u60c5\u7ea0\u7ed3\u7740\uff0c\u50cf\u4e00\u56e2\u89e3\u4e0d\u5f00\u7684\u4e71\u7f57\uff0c\u5979\u4e00\u9635\u660f\u7729\uff0c\u4e0d\u77e5\u9053\u8be5\u600e\u5206\u8bf4\u624d\u597d\uff01 \u3000\u3000\u201c\u51cc\u53d4\u53d4\uff0c\u6211\u4eec\u7ec8\u4e8e\u80dc\u5229\u4e86\uff01\u201d\u6613\u5170\u73e0\u8df3\u56b7\u7740\u8fdb\u6765\u3002\u5979\u672c\u6765\u662f\u60f3\u8ba9\u5218\u90c1\u82b3\u548c\u51cc\u672a\u98ce\u53d9\u53d9 \u8877\u60c5\u7684\uff0c\u9694\u5e18\u4e00\u770b\uff0c\u795e\u60c5\u4e0d\u5bf9\uff0c\u6025\u6025\u8fdb\u6765\uff0c\u7d27\u63e1\u7740\u51cc\u672a\u98ce\u7684\u624b\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u8fd8\u8bb0\u5f97\u4f60\u7ed9\u98de \u7ea2\u5dfe\u548c\u54b1\u4eec\u4e0d\u8981\u56de\u94b1\u5858\u6c5f\u53bb\u770b\u6f6e\u5462\uff01\u5509\uff0c\u8981\u662f\u771f\u80fd\u591f\u8fd9\u6837\u7684\u8bdd\uff0c\u90a3\u53ef\u591a\u7f8e\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u53ef\u6ca1\u6709\u53d1\u89c9\u4ed6\u4eec\u7684\u58f0\u97f3\u5f02\u6837\uff0c\u5979\u8fd8\u6b63\u5728\u4e3a\u7740\u4ed6\u4eec\u9ad8\u5174\u5462\uff01\u5979\u8f6c\u8fc7\u8eab\u8d70\u51fa\u623f\u95e8\uff0c\u7b11 \u9053\uff1a\u201c\u6b64\u540e\u4f60\u4eec\u60f3\u505a\u4ec0\u4e48\u5c31\u505a\u4ec0\u4e48\uff0c\u6ca1\u6709\u4eba\u53ef\u4ee5\u963b\u62e6\u4f60\u4eec\u4e86\uff01\u201d \u3000\u3000\u5f53\u771f\u6ca1\u6709\u4e86\u4e48\uff1f\u8981\u662f\u6613\u5170\u73e0\u77e5\u9053\u4ed6\u4eec\u5728\u60f3\u4ec0\u4e48\uff0c\u5979\u4e00\u5b9a\u7b11\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u51cc\u672a\u98ce\u88ab\u8feb\u670d\u4e0b\u7684\u9ebb\u836f\u4e43\u662f\u5927\u5185\u79d8\u65b9\u5236\u70bc\uff0c\u9976\u662f\u4ed6\u529f\u529b\u6df1\u539a\u4e5f\u8fd8\u672a\u80fd\u6062\u590d\u4f53\u529b\uff0c\u53ea\u597d\u5728 \u9a6c\u65b9\u5bb6\u91cc\u518d\u4f4f\u4e24\u5929\u3002 \u3000\u3000\u6613\u5170\u73e0\u8bb0\u6302\u7740\u5f20\u534e\u662d\uff0c\u7b2c\u4e09\u5929\u4e00\u5927\u6e05\u65e9\uff0c\u5979\u56e0\u4e3a\u7761\u4e0d\u597d\uff0c\u7d22\u6027\u5c31\u8d77\u6765\u4e86\uff0c\u5979\u5728\u9662\u5728\u91cc\u6563 \u6b65\uff0c\u770b\u89c1\u51cc\u672a\u98ce\u7684\u623f\u95f4\u91cc\u8fd8\u6709\u706f\u5149\uff0c\u5c31\u8d70\u8fc7\u53bb\u6572\u7a97\u95ee\u9053\uff1a\u201c\u51cc\u53d4\u53d4\uff0c\u4f60\u4e00\u665a\u90fd\u6ca1\u7761\u89c9\u5417\uff1f\u4f60 \u662f\u8981\u548c\u5218\u5927\u59d0\u4e0a\u5929\u5c71\u770b\u96ea\u7684\uff0c\u600e\u80fd\u8fd9\u6837\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u6253\u5f00\u623f\u95e8\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u53ea\u4e0d\u8fc7\u60f3\u5199\u4e00\u5c01\u4fe1\uff0c\u6240\u4ee5\u6bd4\u4f60\u65e9\u8d77\u6765\u7f62\u4e86\u3002\u201d \u3000\u3000\u6613\u5170\u73e0\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u5199\u4fe1\uff0c\u5bc4\u7ed9\u8c01\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4fe1\u5df2\u7ecf\u5199\u597d\u4e86\uff0c\u8fd9\u5c01\u4fe1\u6211\u8fd8\u8bf7\u4f60\u7ed9\u6211\u9001\u53bb\u5462\uff0c\u4f60\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u5199\u7ed9\u5218\u5927\u59d0\u7684\uff1f\u201d\u6613\u5170\u73e0\u563b\u563b\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u540c\u4f4f\u5728 \u4e00\u4e2a\u5730\u65b9\uff0c\u6709\u8bdd\u4e0d\u597d\u8bf4\u5417\u4e86\u8fd8\u8981\u5199\u4fe1\uff1f\u201d\u84e6\u5730\u60f3\u8d77\uff0c\u7537\u5973\u4e4b\u95f4\uff0c\u6709\u4e9b\u8bdd\u7684\u786e\u662f\u4e0d\u4fbf\u5f53\u9762\u8bf4 \u7684\uff0c\u5fc3\u4e2d\u6697\u7b11\u51cc\u53d4\u53d4\u8138\u76ae\u592a\u8584\uff0c\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u6211\u61c2\u4e86\uff0c\u6211\u7ed9\u4f60\u9001\u7ed9\u5218\u5927\u59d0\u5c31\u6700\u662f\u3002\u201d \u3000\u3000\u5979\u50cf\u4e2a\u987d\u76ae\u7684\u5b69\u5b50\uff0c\u4e00\u63a8\u5f00\u5218\u90c1\u82b3\u7684\u623f\u95e8\uff0c\u4fbf\u5373\u7b11\u9053\uff1a\u201c\u5927\u59d0\uff0c\u6211\u7ed9\u4f60\u9001\u60c5\u4e66\u6765\u5566\uff0c\u4f60 \u62ff\u4ec0\u4e48\u8c22\u2014\u2014\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5ffd\u5730\u7b11\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5979\u7741\u5927\u773c\u775b\uff0c\u623f\u95f4\u91cc\u90a3\u91cc\u8fd8\u6709\u5218\u90c1\u82b3\uff1f\u4f46\u684c\u4e0a\u5374\u6709\u4e00\u5c01\u4fe1\uff0c\u65c1\u8fb9\u4e86\u6709\u4e00\u5f20\u5b57\u6761\uff1a\u201c\u5170 \u73e0\uff0c\u6211\u8d70\u4e86\uff0c\u8fd9\u5c01\u4fe1\u8bf7\u4f60\u66ff\u6211\u4ea4\u7ed9\u51cc\u53d4\u53d4\u3002\u201d \u3000\u3000\u6613\u5170\u73e0\u83ab\u540d\u5176\u5999\uff0c\u53ea\u597d\u62ff\u8d77\u90a3\u5c01\u4fe1\uff0c\u53c8\u518d\u56de\u53bb\u627e\u51cc\u672a\u98ce\u3002\u4e0d\u6599\u51cc\u672a\u98ce\u4e5f\u4e0d\u89c1\u4e86\uff0c\u89c1\u5230\u7684 \u53ea\u662f\u9a6c\u65b9\u3002\u9a6c\u65b9\u626c\u7740\u624b\u4e2d\u4e00\u5f20\u5b57\u6761\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u51cc\u5927\u4fa0\u7559\u5b57\u7ed9\u6211\uff0c\u8bf4\u662f\u751a\u4e3a\u62b1 \u6b49\uff0c\u4ed6\u4e0d\u80fd\u548c\u6211\u7ec6\u8bf4\u56e0\u7531\uff0c\u7adf\u7136\u4e0d\u8f9e\u800c\u884c\u4e86\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u95ee\u6211\uff0c\u6211\u95ee\u8c01\uff1f\u5509\uff0c\u4ed6\u4eec\u4e8c\u4eba\u4e5f\u4e0d\u77e5\u662f\u4e3a\u4ec0\u4e48\u8981\u73a9\u6349\u8ff7\u85cf\u7684\u6e38 \u620f\uff1f\u201d \u3000\u3000\u9a6c\u65b9\u9053\uff1a\u201c\u6349\u8ff7\u85cf\uff1f\u201d \u3000\u3000\u6613\u5170\u73e0\u626c\u8d77\u624b\u4e2d\u7684\u5b57\u6761\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5927\u6982\u8fd8\u672a\u77e5\u9053\u5427\uff01\u5218\u5927\u5973\u5df2\u8d70\u4e86\uff01\u201d \u3000\u3000\u4e24\u4eba\u76f8\u5bf9\u9eef\u7136\uff0c\u534a\u664c\uff0c\u9a6c\u65b9\u8bf4\u9053\uff1a\u201c\u597d\u5728\u8fd8\u6709\u4e2a\u597d\u6d88\u606f\uff0c\u6e05\u5175\u5df2\u7ecf\u8d70\u4e86\u3002\u201d \u3000\u3000\u6613\u5170\u73e0\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u4e5f\u5e94\u8be5\u8d70\u4e86\u3002\u201d\u5979\u85cf\u597d\u4e24\u5c01\u4fe1\uff0c\u8d70\u51fa\u9a6c\u5bb6\uff0c\u5fc3\u4e2d\u9690\u9690\u731c\u5230\u51e0\u5206\uff0c \u6697\u81ea\u60f3\u9053\uff1a\u201c\u4f46\u613f\u9633\u90ce\u4e0d\u8981\u8eb2\u907f\u6211\u624d\u597d\u3002\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u5fc3\u5e95\u521b\u4f24\u96be\u590d\u5408\uff0c\u6df1\u60c5\u672a\u53d8\u5374\u5bd2\u76df\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u2014\u2014\u7b2c\u4e09\u5341\u7ae0\u3000\u751f\u6b7b\u832b\u832b\u3000\u4fa0\u9aa8\u67d4\u60c5\u57cb\u701a\u6d77\u3000\u6069\u4ec7\u4e86\u4e86\u3000\u82f1\u96c4\u513f\u5973\u9690\u5929\u5c71 \u6881\u7fbd\u751f\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b \u7b2c\u4e09\u5341\u56de \u751f\u6b7b\u832b\u832b\u3000\u4fa0\u9aa8\u67d4\u60c5\u57cb\u701a\u6d77 \u6069\u4ec7\u4e86\u4e86\u3000\u82f1\u96c4\u513f\u5973\u9690\u5929\u5c71 \u3000\u3000\u56de\u5934\u518d\u8bf4\u90a3\u5929\u665a\u4e0a\u7684\u4e8b\u60c5\u3002\u7fa4\u96c4\u5206\u6279\u641c\u5bab\uff0c\u9ece\u660e\u4e4b\u524d\uff0c\u4f1a\u5408\u4e00\u8d77\uff0c\u4e0d\u4f46\u6613\u5170\u73e0\u4e0d\u89c1\u8e2a \u8ff9\uff0c\u8fde\u5218\u90c1\u82b3\u4e5f\u5931\u4e86\u8e2a\u3002\u5085\u9752\u4e3b\u9053\uff1a\u201c\u5141\u9898\u5927\u519b\u73af\u4f3a\uff0c\u9ed1\u591c\u4e4b\u4e2d\u4ed6\u4eec\u4e0d\u6562\u52a8\u624b\uff0c\u6211\u4eec\u5fc5\u987b\u5728 \u9ece\u660e\u4e4b\u524d\u51b2\u51fa\u57ce\u53bb\u3002\u6211\u7559\u4e0b\u6765\u63a5\u5e94\uff0c\u6307\u6325\u8131\u96be\u4e4b\u8d23\uff0c\u53ea\u597d\u504f\u52b3\u674e\u516c\u5b50\u4e86\u3002\u201d\u6842\u4ef2\u660e\u9053\uff1a\u201c\u6211 \u4e5f\u60f3\u7559\u4e0b\u6765\u7b49\u5019\u51cc\u5927\u4fa0\u3002\u201d\u5085\u9752\u4e3b\u7476\u7476\u5934\u9053\uff0c\u201c\u4e0d\u884c\uff01\u4f60\u5fd8\u4e86\u6211\u4eec\u4e0e\u6b66\u5f53\u6d3e\u638c\u95e8\u4eba\u7384\u771f\u4e4b\u7ea6 \u4e86\u5417\uff1f\u201d\u5f20\u534e\u662d\u9053\uff1a\u201c\u90a3\u4e48\u6211\u7559\u4e0b\u6765\u966a\u4f34\u5e08\u53d4\u5982\u4f55\uff1f\u201d\u5085\u9752\u4e3b\u5fae\u5fae\u4e00\u7b11\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4f60\u7559 \u4e0b\u6765\u8fd8\u6709\u9053\u7406\u3002\u201d\u5f53\u4e0b\u4e0e\u674e\u601d\u6c38\u6325\u624b\u4f5c\u522b\uff0c\u4e0e\u4f17\u4eba\u533f\u5c45\u4e00\u4e2a\u5c0f\u5587\u561b\u5bfa\u4e2d\u3002 \u3000\u3000\u674e\u601d\u6c38\u5f53\u5e74\u66fe\u6307\u6325\u5341\u4e07\u5927\u519b\uff0c\u81ea\u662f\u5927\u5c06\u4e4b\u624d\uff0c\u5f53\u4e0b\u5c06\u4e09\u5343\u5065\u513f\u5206\u4e3a\u4e09\u961f\uff0c\u4e00\u961f\u4f6f\u653b\u4e1c \u95e8\uff0c\u4e00\u961f\u57cb\u4f0f\u63a5\u5e94\uff0c\u4e00\u961f\u6bbf\u540e\uff0c\u5f85\u5438\u5f15\u6e05\u519b\u4e3b\u529b\u8f6c\u79fb\u540e\uff0c\u7a81\u7136\u540e\u961f\u6539\u4e3a\u524d\u961f\uff0c\u6251\u653b\u897f\u95e8\uff0c\u6e05 \u519b\u8ffd\u6765\uff0c\u4f0f\u5175\u56db\u8d77\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u4ee5\u5c11\u4f5c\u591a\uff0c\u66f4\u517c\u7fa4\u96c4\u4e2a\u4e2a\u6b66\u827a\u9ad8\u5f3a\uff0c\u6e05\u519b\u4e0d\u77e5\u865a\u5b9e\uff0c\u53c8\u8981\u5206 \u4e00\u961f\u4eba\u53bb\u5e03\u8fbe\u62c9\u5bab\u6551\u706b\uff0c\u7adf\u7ed9\u674e\u601d\u6c38\u7387\u4f17\u5b89\u5168\u64a4\u51fa\u57ce\u5916\u3002 \u3000\u3000\u8fd9\u652f\u4eba\u9a6c\uff0c\u4eba\u5f3a\u9a6c\u58ee\uff0c\u8131\u9669\u4e4b\u540e\uff0c\u75be\u9a70\u6570\u5929\uff0c\u5df2\u5230\u8fb9\u5883\u3002\u65f6\u503c\u9ec4\u660f\uff0c\u674e\u601d\u6c38\u767b\u9ad8\u4e00\u671b\uff0c \u89c1\u708a\u70df\u7a00\u8584\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6e05\u5ef7\u8fb9\u5883\u5927\u519b\u5df2\u64a4\uff0c\u4e0d\u77e5\u4f55\u6545\uff1f\u201d\u5f53\u4e0b\u8f7b\u6613\u51b2\u8fc7\u5c01\u9501\uff0c\u4e0d \u5230\u5341\u5929\u5df2\u56de\u81f3\u5580\u5c14\u6c81\u8349\u539f\uff0c\u4e00\u8def\u672a\u9047\u654c\u519b\uff0c\u95ee\u8d77\u6765\u65f6\uff0c\u624d\u77e5\u6e05\u519b\u8fdb\u9a7b\u4e86\u56de\u7586\u51e0\u4e2a\u5927\u57ce\u4e4b\u540e\uff0c \u5eb7\u7199\u56e0\u754f\u585e\u5916\u82e6\u5bd2\uff0c\u524d\u51e0\u5929\u5df2\u73ed\u5e08\u56de\u671d\u3002 \u3000\u3000\u6842\u4ef2\u660e\u5c48\u6307\u4e00\u8ba1\uff0c\u7384\u771f\u9053\u957f\u5929\u5c71\u4e4b\u7ea6\u5c06\u5c4a\u3002 \u3000\u3000\u4e8e\u662f\u548c\u5192\u6d63\u83b2\u5148\u56de\u5230\u5357\u7586\uff0c\u53bb\u8bf7\u793a\u98de\u7ea2\u5dfe\uff0c\u54ea\u77e5\u98de\u7ea2\u5dfe\u4e5f\u5728\u65e9\u4e24\u5929\u5355\u8eab\u4e0a\u5929\u5c71\u53bb\u4e86\uff0c\u5192 \u6d63\u83b2\u9053\uff1a\u201c\u6211\u770b\u8fd9\u4e8b\u6709\u70b9\u6eaa\u8df7\uff0c\u98de\u7ea2\u5dfe\u4e0d\u8fdf\u4e0d\u65e9\uff0c\u6070\u5de7\u8fd9\u4e2a\u65f6\u5019\u4e5f\u4e0a\u5929\u5c71\uff0c\u5fc5\u6709\u7f18\u6545\uff0c\u6211\u4eec \u4e0d\u5982\u7559\u4e0b\u4e66\u4fe1\uff0c\u82e5\u51cc\u5927\u4fa0\u548c\u5170\u73e0\u59b9\u59b9\u56de\u6765\uff0c\u53eb\u4ed6\u4eec\u4e5f\u4e0a\u5929\u5c71\u3002\u201d\u6842\u4ef2\u660e\u4e00\u5207\u90fd\u542c\u5192\u6d63\u83b2\u7684\u610f \u89c1\uff0c\u81ea\u7136\u7167\u529e\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u4e2a\u591a\u6708\uff0c\u4e24\u4eba\u5df2\u5230\u5929\u5c71\u7684\u9a86\u9a7c\u5cf0\u4e0b\uff0c\u5192\u6d63\u83b2\u9053\uff1a\u201c\u4f60\u4e0d\u8bb0\u5f97\u5728\u8fd9\u91cc\u9047\u89c1\u8f9b\u9f99\u5b50\u7684 \u4e8b\u5417\uff1f\u4e0d\u6599\u4eca\u65e5\u91cd\u6765\uff0c\u8fd9\u4f4d\u602a\u4fa0\u5df2\u64a4\u624b\u5c18\u5bf0\uff0c\u5cf0\u9876\u53ea\u7559\u4e0b\u4ed6\u5e08\u7236\u7684\u9ab8\u9aa8\u4e86\u3002\u201d\u6842\u4ef2\u660e\u9053\uff1a \u201c\u6211\u4e5f\u60f3\u4e0d\u5230\u7adf\u6210\u4e86\u5353\u5927\u4fa0\u7684\u9694\u4e16\u5f1f\u5b50\u3002\u53ea\u4e0d\u77e5\u638c\u95e8\u5e08\u53d4\u80af\u4e0d\u80af\u5141\u6211\u5217\u5165\u95e8\u5899\uff1f\u201d\u8bdd\u58f0\u672a \u4e86\uff0c\u5ffd\u542c\u5f97\u9a86\u9a7c\u5cf0\u4e0a\u4f20\u51fa\u602a\u5578\u4e4f\u58f0\uff0c\u8ddf\u7740\u662f\u53f1\u54a4\u8ffd\u9010\u4e4b\u58f0\uff0c\u9a87\u4eba\u5fc3\u9b44\uff0c\u5239\u90a3\u4e4b\u95f4\uff0c\u78e8\u76d8\u5927\u7684 \u96ea\u5757\u81ea\u5c71\u9876\u98de\u6eda\u4e0b\u6765\uff0c\u7ad9\u7acb\u4e4b\u5904\uff0c\u72b9\u5982\u5730\u9707\uff01\u548c\u5f53\u65e5\u521d\u5230\u9a86\u9a7c\u5cf0\u9047\u8f9b\u9f99\u5b50\u7684\u60c5\u5f62\u9887\u4e3a\u76f8\u4f3c\uff0c \u53ea\u662f\u6bd4\u5f53\u65e5\u66f4\u4e3a\u9a87\u4eba\u3002\u6842\u4ef2\u660e\u9053\uff1a\u201c\u96be\u9053\u4e0a\u9762\u8fd8\u6709\u4e00\u4e2a\u8f9b\u9f99\u5b50\uff1f\u201d\u6043\u7740\u827a\u9ad8\u80c6\u5927\uff0c\u62d6\u5192\u6d63\u83b2 \u51b2\u4e0a\u5c71\u5cf0\u3002 \u3000\u3000\u4e14\u8bf4\u7384\u771f\u4e3a\u4e86\u5b9d\u5178\uff08\u6307\u8fbe\u6469\u79d8\u590d\uff09\u5f52\u5b97\uff0c\u548c\u8fce\u846c\u524d\u8f88\u638c\u95e8\u4eba\u9ab8\u9aa8\u7684\u5927\u4e8b\uff0c\u7387\u9886\u4e86\u5e08\u5f1f\u7384 \u901a\u3001\u7384\u89c9\u3001\u5e08\u59b9\u4f55\u7eff\u534e\u592b\u5987\uff0c\u4ee5\u53ca\u540e\u4e00\u8f88\u4e2d\u6b66\u529f\u6700\u9ad8\u7684\u4e03\u5927\u5f1f\u5b50\uff0c\u767b\u4e0a\u9a86\u9a7c\u5cf0\uff0c\u7b49\u89c1\u6842\u4ef2 \u660e\u3002\u54ea\u77e5\u6842\u4ef2\u660e\u8fd8\u672a\u89c1\u5230\uff0c\u5374\u89c1\u4e86\u4e00\u4ef6\u602a\u4e8b\u3002 \u3000\u3000\u7384\u771f\u7b49\u521a\u4e0a\u5230\u5cf0\u9876\uff0c\u4fbf\u542c\u5f97\u4e00\u58f0\u602a\u5578\uff0c\u5176\u58f0\u751a\u8fdc\uff0c\u5374\u5165\u8033\u5c16\u9510\uff0c\u7384\u771f\u609a\u7136\u4e00\u60ca\u3002\u602a\u5578\u4e00 \u58f0\u63a5\u7740\u4e00\u58f0\uff0c\u6709\u7684\u5982\u7a7a\u5c71\u733f\u557c\uff0c\u6709\u7684\u5982\u5c0f\u513f\u591c\u54ed\uff0c\u6709\u7684\u5982\u72fc\u55e5\u72ee\u543c\uff0c\u6709\u7684\u5982\u591c\u9e26\u5389\u9e23\uff0c\u8bf8\u58f0 \u6742\u4f5c\uff0c\u663e\u89c1\u6765\u8005\u4e0d\u662f\u4e00\u4eba\uff0c\u4f46\u773a\u671b\u4e0b\u53bb\uff0c\u5374\u53c8\u4e0d\u89c1\u5f71\u5b50\u3002\u7384\u771f\u9a82\u9053\uff1a\u201c\u8fd9\u662f\u4f55\u65b9\u5996\u5b7d\uff1f\u80c6\u6562 \u5413\u552c\u9053\u7237\uff1f\u201d\u4ed7\u7740\u4eba\u591a\u52bf\u4f17\uff0c\u8ff3\u81ea\u6251\u5165\u8f9b\u9f99\u5b50\u6614\u65e5\u85cf\u8eab\u7684\u77f3\u7a9f\u3002 \u3000\u3000\u77f3\u7a9f\u4e2d\u55e4\u7684\u4e00\u58f0\u51b7\u7b11\uff0c\u7384\u771f\u62d4\u5251\u5728\u624b\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u7387\u4f17\u5165\u5185\uff0c\u4f55\u7eff\u534e\u4eae\u8d77\u706b\u6298\uff0c\u5ffd\u7136\u60ca \u53eb\u8d77\u6765\uff0c\u77f3\u7a9f\u5185\u6709\u77f3\u5757\u780c\u6210\u5e73\u53f0\uff0c\u5e73\u53f0\u4e0a\u4e00\u5177\u9ab7\u9ac5\uff0c\u7626\u9aa8\u78f7\u5ccb\uff0c\u5934\u9762\u5b8c\u597d\uff0c\u7adf\u662f\u5927\u4fa0\u5353\u4e00\u822a \u7684\u5c38\u4f53\uff0c\u4f46\u5353\u4e00\u822a\u751f\u524d\u8eab\u9ad8\u516d\u5c3a\uff0c\u4fca\u6717\u5f02\u5e38\uff0c\u800c\u90a3\u5177\u9ab7\u9ac5\u770b\u6765\u4e0d\u5230\u4e09\u5c3a\uff0c\u6d3b\u8c61\u4e00\u4e2a\u5c0f\u5b69\u5b50\u7684 \u5c38\u9ab8\u3002\u9ab7\u9ac5\u65c1\u76d8\u819d\u5750\u7740\u4e00\u4e2a\u767d\u53d1\u8001\u5987\uff0c\u5206\u660e\u662f\u767d\u53d1\u9b54\u5973\uff01\u4f55\u7eff\u534e\u4e8c\u5341\u4f59\u5e74\u524d\uff0c\u5230\u56de\u7586\u63a2\u5353\u4e00 \u822a\uff0c\u88ab\u767d\u53d1\u9b54\u5973\u9a71\u9010\uff0c\u81f3\u4eca\u60f3\u8d77\uff0c\u72b9\u81ea\u80c6\u5bd2\uff01\u9000\u540e\u4e00\u6b65\uff0c\u6a2a\u5251\u53eb\u9053\uff1a\u201c\u767d\u53d1\u9b54\u5973\uff0c\u4f60\u6211\u65e0\u51a4 \u65e0\u4ec7\uff0c\u5353\u5927\u4fa0\u5df2\u8eab\u6b7b\uff0c\u4eca\u65e5\u6211\u592b\u5987\u8fdc\u6765\uff0c\u4e0e\u4f60\u4e95\u6c34\u4e0d\u72af\u6cb3\u6c34\u3002\u201d\u767d\u53d1\u9b54\u5973\u5782\u624b\u95ed\u76ee\uff0c\u7eb9\u4e1d\u4e0d \u52a8\u3002\u7384\u771f\u5076\u7136\u62ac\u5934\uff0c\u53ea\u89c1\u5ced\u58c1\u4e0a\u6709\u4e09\u884c\u5927\u5b57\uff0c\u5de6\u53f3\u4e24\u884c\u662f\uff1a\u201c\u5386\u52ab\u4e86\u65e0\u751f\u6b7b\u5ff5\uff0c\u7ecf\u971c\u65b9\u663e\u64a4 \u5bd2\u5fc3\uff01\u201d\u6b63\u4e2d\u4e00\u884c\u662f\uff1a\u201c\u8c01\u6562\u79fb\u52a8\u6211\u4e8c\u4eba\u9ab8\u9aa8\uff0c\u4e0d\u5f97\u751f\u51fa\u6b64\u95e8\uff1f\u201d\u4e2a\u4e2a\u5927\u5b57\u5165\u77f3\u6570\u5206\uff0c\u8352\u5c71 \u5ced\u58c1\uff0c\u663e\u7136\u4e0d\u662f\u4eba\u5de5\u6240\u51ff\uff0c\u800c\u662f\u767d\u53d1\u9b54\u5973\u7528\u6307\u5934\u5212\u51fa\u6765\u7684\u3002\u7384\u771f\u867d\u662f\u6b66\u529f\u6df1\u6e5b\uff0c\u4e5f\u4e0d\u7981\u5413\u4e86 \u4e00\u8df3\u3002\u731b\u7136\u95f4\u6d1e\u5185\u4e00\u9635\u9634\u98ce\uff0c\u706b\u6298\u7184\u706d\uff0c\u6709\u4eba\u9634\u607b\u4fa7\u5730\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u771f\u6562\u6765\u6b64\uff1f\u201d\u4f55\u7eff\u534e \u60ca\u53eb\u4e00\u58f0\uff0c\u6258\u5730\u540e\u8df3\uff0c\u7384\u771f\u62c9\u7740\u4e24\u4e2a\u5e08\u5f1f\uff0c\u5927\u53eb\u201c\u6025\u9000\uff01\u201d\u53cd\u8eab\u8dc3\u51fa\u6d1e\u5916\u3002 \u3000\u3000\u767d\u53d1\u9b54\u5973\u6614\u65e5\u6b66\u5f53\u6597\u5251\uff0c\u529b\u632b\u56db\u5927\u957f\u8001\uff0c\u5251\u4f24\u767d\u77f3\u9053\u4eba\uff0c\u6b66\u5f53\u6d3e\u81f3\u4eca\u8ba4\u4e3a\u5947\u803b\u5927\u8fb1\uff0c\u7136 \u800c\u53c8\u4e3a\u767d\u53d1\u9b54\u5973\u58f0\u5a01\u9707\u6151\uff0c\u9634\u98ce\u4e00\u8d77\uff0c\u4e2a\u4e2a\u5fc3\u614c\uff0c\u8df3\u51fa\u6d1e\u5916\uff0c\u60ca\u9b42\u65b9\u5b9a\uff0c\u53ea\u89c1\u6d1e\u4e2d\u8d70\u51fa\u4e00\u4e2a \u5973\u4eba\uff0c\u867d\u7136\u767d\u53d1\u76c8\u5934\uff0c\u5374\u662f\u5bb9\u989c\u8273\u53a2\uff0c\u4f55\u7eff\u534e\u5618\u53e3\u6c14\u9053\uff1a\u201c\u98de\u7ea2\u5dfe\uff0c\u539f\u6765\u662f\u4f60\uff01\u201d \u3000\u3000\u98de\u7ea2\u5dfe\u5de6\u624b\u6301\u97ad\uff0c\u53f3\u624b\u4ed7\u5251\uff0c\u626c\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4f55\u7b49\u6837\u4eba\uff1f\u80c6\u6562\u7aa5\u4f3a\u6211\u5e08\u7236\u91d1\u8eab\uff01\u201d \u539f\u6765\u767d\u53d1\u9b54\u5973\u767e\u5c81\u5927\u5bff\u4e4b\u65e5\uff0c\u5f97\u5f20\u534e\u662d\u9001\u5323\u4f20\u82b1\uff0c\u5fc3\u611f\u5353\u4e00\u822a\u6b7b\u751f\u4e0d\u6e1d\u4e4b\u60c5\uff0c\u5bfb\u81f3\u9a86\u9a7c\u5cf0\u77f3 \u7a9f\uff0c\u6398\u51fa\u5353\u4e00\u822a\u9057\u4f53\u3002\u5353\u4e00\u822a\u751f\u524d\u9887\u7231\u81ea\u5df1\u7684\u5bb9\u989c\uff0c\u56e0\u6b64\u6b7b\u65f6\u547d\u8f9b\u9f99\u5b50\u7528\u602a\u836f\u70bc\u8fc7\u5c38\u8eab\uff0c\u8eab \u4f53\u7f29\u5c0f\uff0c\u9aa8\u9abc\u5b8c\u6574\uff0c\u6829\u6829\u5982\u751f\u3002\u767d\u53d1\u9b54\u5973\u6050\u81ea\u5df1\u6b7b\u540e\uff0c\u4ec7\u4eba\u6765\u52ab\u593a\u9ab8\u9aa8\uff0c\u56e0\u6b64\u624d\u53eb\u98de\u7ea2\u5dfe\u4e0a \u5c71\uff0c\u8981\u5f92\u5f1f\u5c06\u5979\u548c\u5353\u4e00\u822a\u5408\u846c\u3002\u521a\u624d\u90a3\u9635\u9634\u98ce\uff0c\u5c31\u662f\u98de\u7ea2\u5dfe\u505a\u7684\u624b\u811a\u3002 \u3000\u3000\u7384\u771f\u89c1\u6765\u4eba\u4e0d\u662f\u767d\u53d1\u9b54\u5973\uff0c\u677e\u4e86\u53e3\u6c14\uff0c\u957f\u5251\u4e00\u6307\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u6b66\u5f53\u6d3e\u524d\u6765\u8fce\u63a5\u524d \u8f88\u638c\u95e8\u4eba\u7684\u9ab8\u9aa8\u56de\u5c71\uff0c\u8c01\u7ba1\u4f60\u7684\u4ec0\u4e48\u5e08\u7236\uff01\u201d\u98de\u7ea2\u5dfe\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u957f\u97ad\u6d51\u52a8\uff0c\u50fb\u556a\u4f5c\u54cd\uff0c \u51b7\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u884c\uff01\u201d \u3000\u3000\u7384\u771f\u6012\u9053\uff1a\u201c\u6211\u4eec\u6b66\u5f53\u6d3e\u7684\u5bb6\u4e8b\uff0c\u5bb9\u4f60\u6765\u7ba1\uff1f\u201d\u98de\u7ea2\u5dfe\u51b7\u7b11\u9053\uff1a\u201c\u5bb6\u4e8b\uff0c\u5bb6\u4e8b\uff0c\u4f60\u4eec\u6b66 \u5f53\u7684\u4eba\u5c11\u7ba1\u95f2\u4e8b\uff0c\u5353\u5927\u4fa0\u548c\u6211\u7684\u5e08\u7236\u4e5f\u4e0d\u81f3\u4e8e\u8fd9\u4e2a\u6837\u5b50\uff0c\u5353\u5927\u4fa0\u8fdc\u8d70\u5929\u5c71\uff0c\u548c\u4f60\u4eec\u6b66\u5f53\u6d3e\u65e9 \u5df2\u6069\u65ad\u4e49\u7edd\u3002\u9057\u4e66\u8981\u548c\u6211\u5e08\u7236\u5408\u846c\u3002\u4f60\u4eec\u80c6\u6562\u52a8\u4ed6\u7684\u9ab8\u9aa8\uff0c\u5148\u8bf7\u5403\u6211\u4e00\u97ad\uff01\u201d\u7384\u771f\u52c3\u7136\u5927 \u6012\uff0c\u957f\u5251\u4e00\u6307\uff0c\u4e03\u5927\u5f1f\u5b50\u4e2a\u4e2a\u4e89\u5148\uff0c\u770b\u770b\u5c31\u8981\u52a8\u624b\u3002\u5ffd\u7136\u5c71\u4e0b\u602a\u58f0\u5927\u4f5c\uff0c\u98de\u7ea2\u5dfe\u53d8\u8272\u9053\uff1a \u201c\u4f60\u4eec\u8981\u547d\u7684\u5feb\u8d70\uff0c\u8fd9\u662f\u897f\u57df\u4e09\u5996\u6765\u4e86\uff01\u201d \u3000\u3000\u201c\u897f\u57df\u4e09\u5996\u201d\u5404\u6709\u72ec\u95e8\u6b66\u529f\uff0c\u5927\u5996\u6851\u4e7e\uff0c\u70bc\u7684\u662f\u4e03\u7edd\u8bdb\u9b44\u5251\uff0c\u5251\u5c16\u6709\u6bd2\uff0c\u89c1\u8840\u5c01\u5589\uff1b\u4e8c \u5996\u6851\u5f27\uff0c\u70bc\u7684\u662f\u5927\u529b\u91d1\u94a2\u6775\uff0c\u5916\u5bb6\u529f\u529b\uff0c\u767b\u5cf0\u9020\u6781\uff1b\u4e09\u5996\u6851\u4ec1\uff0c\u70bc\u7684\u662f\u9634\u9633\u5288\u98ce\u638c\uff0c\u4e2d\u4e86\u638c \u529b\uff0c\u4e94\u810f\u9707\u88c2\u3002\u4e09\u4eba\u6614\u65e5\u6a2a\u884c\u897f\u57df\uff0c\u56e0\u4e3a\u6240\u70bc\u7684\u529f\u592b\u9634\u72e0\u6bd2\u8fa3\uff0c\u6240\u4ee5\u88ab\u79f0\u4e3a\u201c\u4e09\u5996\u201d\u3002\u767d\u53d1 \u9b54\u5973\u5230\u4e86\u5929\u5c71\u4e4b\u540e\uff0c\u4e0d\u8bb8\u4e09\u5996\u5728\u56de\u7586\u7acb\u8db3\uff0c\u4e09\u5996\u4e0d\u662f\u5979\u7684\u5bf9\u624b\uff0c\u76f4\u88ab\u8d76\u5230\u897f\u85cf\u3002\u4e09\u5341\u5e74\u6765\uff0c \u9500\u58f0\u533f\u8ff9\uff0c\u5982\u4eca\u63a2\u542c\u5f97\u6666\u660e\u7985\u5e08\u548c\u5353\u4e00\u822a\u90fd\u5df2\u53bb\u4e16\uff0c\u767d\u53d1\u9b54\u5973\u4e5f\u4e45\u5df2\u4e0d\u89c1\u9732\u9762\u3002\u56e0\u6b64\u7387\u9886\u5f92 \u4f17\uff0c\u5148\u4e0a\u5317\u9ad8\u5cf0\uff0c\u60f3\u5077\u6666\u660e\u7985\u5e08\u548c\u5353\u4e00\u822a\u7684\u62f3\u7ecf\u5251\u8bc0\uff0c\u7136\u540e\u518d\u6597\u767d\u53d1\u9b54\u5973\u3002 \u3000\u3000\u7384\u771f\u662f\u4e00\u6d3e\u638c\u95e8\uff0c\u6df1\u77e5\u897f\u57df\u4e09\u5996\u6765\u5386\uff0c\u9762\u8272\u5927\u53d8\uff0c\u987e\u4e0d\u5f97\u518d\u6597\u98de\u7ea2\u5dfe\uff0c\u6025\u53eb\u4f17\u5f1f\u5b50\u9996\u5c3e \u76f8\u8054\uff0c\u56f4\u6210\u4e00\u5708\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u602a\u58f0\u7476\u66f3\u957f\u7a7a\uff0c\u500f\u5730\u505c\u6b62\uff0c\u897f\u57df\u4e09\u5996\u548c\u4ed6\u4eec\u7684\u5341\u591a\u4e2a\u515a \u5f92\uff0c\u5df2\u5230\u5c71\u9876\u3002\u89c1\u7384\u771f\u7b49\u56f4\u6210\u4e00\u5708\uff0c\u8fde\u58f0\u72de\u7b11\uff0c\u4e0d\u5206\u7682\u767d\uff0c\u51f6\u795e\u6076\u715e\u822c\u5730\u76f4\u6740\u8fc7\u6765\uff01 \u3000\u3000\u7384\u771f\u77e5\u9053\u4e09\u5996\u65e0\u53ef\u7406\u55bb\uff0c\u5c4f\u6c14\u51dd\u795e\uff0c\u54ea\u6562\u6253\u8bdd\uff0c\u957f\u5251\u5f80\u5916\u4e00\u5c01\uff0c\u5c06\u5927\u5996\u7684\u8bdb\u9b44\u5251\u6321\u7740\uff0c \u5927\u5996\u559d\u58f0\u201c\u6765\u5f97\u597d\u201d\uff0c\u6bd2\u5251\u4e00\u632f\uff0c\u53cc\u5251\u53cd\u5f39\u51fa\u53bb\uff0c\u4e09\u5996\u6851\u4ec1\u9634\u4fa7\u4fa7\u5730\u7b11\u9053\uff1a\u201c\u5353\u4e00\u822a\u54ea\u91cc\u8bf7 \u6765\u8fd9\u6279\u6742\u6bdb\u7ed9\u4ed6\u5b88\u5c38\uff01\u201d\u53cc\u638c\u75be\u53d1\uff0c\u7384\u901a\u5927\u53eb\u4e00\u58f0\uff0c\u65b9\u4fbf\u94f2\u7adf\u7ed9\u9707\u98de\uff0c\u7384\u771f\u8eab\u8eaf\u4e00\u6c89\uff0c\u5927\u5996 \u6851\u4e7e\u7684\u6bd2\u5251\u5f80\u4e0b\u4e00\u626b\uff0c\u5251\u950b\u5df2\u81ea\u6cbe\u8863\uff0c\u98de\u7ea2\u5dfe\u7a81\u5730\u957f\u97ad\u4e00\u5377\uff0c\u75be\u5982\u95ea\u7535\uff0c\u7f20\u5411\u6851\u4e7e\u624b\u8155\uff0c\u6851 \u4e7e\u8eab\u8eaf\u970d\u5730\u4e00\u7ffb\uff0c\u95ea\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u7384\u771f\u6b7b\u91cc\u9003\u751f\uff0c\u53eb\u58f0\u201c\u597d\u9669\u201d\uff01\u4e8c\u5996\u7684\u5927\u529b\u91d1\u94a2\u67de\uff0c\u4e00\u62db\u201c\u6a2a\u626b\u5343\u519b\u201d\uff0c\u96ea\u5d29\u98ce\u8d77\uff0c\u4e03 \u5927\u5f1f\u5b50\u7eb7\u7eb7\u8d70\u907f\uff0c\u6446\u597d\u7684\u5706\u9635\uff0c\u767b\u65f6\u7834\u4e86\uff01\u4f55\u7eff\u534e\u8f7b\u529f\u8d85\u5353\uff0c\u8170\u52b2\u4e00\u63d0\uff0c\u8eab\u5b50\u51ed\u7a7a\u62d4\u8d77\u4e00\u4e08 \u591a\u9ad8\uff0c\u51cc\u7a7a\u4e00\u5251\uff0c\u5237\u7684\u5411\u4e8c\u5996\u80a9\u5934\u523a\u4e0b\uff0c\u4e09\u5996\u8d76\u6765\u4e00\u6293\uff0c\u7adf\u6765\u786c\u62a2\u4f55\u7eff\u534e\u7684\u5b9d\u5251\uff0c\u98de\u7ea2\u5dfe\u5288 \u9762\u4e00\u97ad\uff0c\u77ed\u5251\u76f4\u62a2\u8fdb\u6765\uff0c\u4e09\u5996\u4e00\u6293\u6293\u7a7a\uff0c\u5927\u5996\u6025\u5fd9\u8fc7\u6765\u6321\u4f4f\u3002 \u3000\u3000\u7384\u771f\u3001\u98de\u7ea2\u5dfe\u3001\u4f55\u7eff\u534e\u548c\u4e09\u5996\u6076\u6597\u4e4b\u9645\uff0c\u4e03\u5927\u5f1f\u5b50\u548c\u7384\u771f\u7684\u4e24\u4e2a\u5e08\u5f1f\uff0c\u4e5f\u548c\u4e09\u5996\u7684\u515a\u7fbd \u52a8\u8d77\u624b\u6765\u3002\u9a86\u9a7c\u5cf0\u4e0a\u53f1\u54a4\u8ffd\u9010\uff0c\u602a\u5578\u4e0d\u7edd\u3002\u4e8c\u5996\u6851\u5f27\u7684\u5927\u529b\u91d1\u94a2\u6775\u5de6\u8361\u53f3\u51b3\uff0c\u6b66\u5f53\u6d3e\u5f1f\u5b50\u4e00 \u7ed9\u78b0\u7740\uff0c\u65e0\u4e0d\u864e\u53e3\u9ebb\u75db\uff0c\u4e24\u4e2a\u529f\u529b\u7a0d\u4f4e\u7684\uff0c\u624b\u4e2d\u957f\u5251\u5df2\u7ed9\u9707\u98de\uff01 \u3000\u3000\u6076\u6218\u4e2d\u7384\u771f\u3001\u98de\u7ea2\u5dfe\u3001\u4f55\u7eff\u534e\u4e09\u4eba\u5c1a\u53ef\u62b5\u6321\uff0c\u7384\u901a\u7384\u89c9\u548c\u4e03\u5927\u5f1f\u5b50\u5374\u9669\u8c61\u73af\u751f\uff0c\u4e8c\u5996\u6851 \u5f27\uff0c\u821e\u52a8\u91d1\u94a2\u67de\uff0c\u6253\u5f97\u96ea\u5d29\u77f3\u88c2\uff0c\u51f6\u731b\u5f02\u5e38\u3002\u7384\u771f\u865a\u6643\u4e00\u5251\uff0c\u8ba9\u98de\u7ea2\u5dfe\u586b\u4e0a\u7a7a\u4f4d\uff0c\u63a5\u6218\u5927\u5996 \u6851\u4e7e\uff0c\u81ea\u5df1\u633a\u5251\u6765\u6597\u6851\u5f27\uff0c\u8fd0\u8db3\u529f\u529b\uff0c\u582a\u582a\u62b5\u6321\u5f97\u4f4f\u3002\u4e09\u5996\u6851\u4ec1\u731b\u53d1\u6570\u638c\uff0c\u628a\u4f55\u7eff\u534e\u8feb\u9000\uff0c \u864e\u543c\u4e00\u58f0\uff0c\u51cc\u7a7a\u4e00\u8dc3\uff0c\u5ffd\u7136\u5411\u7384\u771f\u6293\u6765\uff0c\u7384\u771f\u8eab\u5f62\u6025\u95ea\uff0c\u6851\u5f27\u7684\u91d1\u94a2\u6775\uff0c\u547c\u7684\u4e0a\u58f0\uff0c\u62e6\u8170\u626b \u5230\uff0c\u7384\u771f\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u6321\u4e0d\u7740\u4e24\u5996\u73af\u51fb\uff0c\u95ea\u907f\u4e2d\u957f\u5251\u88ab\u6851\u4ec1\u4e00\u624b\u6293\u53bb\uff0c\u7384\u771f\u6697\u53eb\u201c\u6211\u547d\u4f11 \u77e3\uff01\u201d\u8fde\u8fde\u540e\u9000\uff0c\u7adf\u7ed9\u8feb\u81f3\u5ca9\u8fb9\u3002 \u3000\u3000\u4f55\u7eff\u534e\u3001\u7384\u89c9\u89c1\u72b6\u5927\u60ca\uff0c\u53cc\u62a2\u8fc7\u6765\uff0c\u628a\u6851\u4ec1\u62e6\u7740\uff0c\u6851\u5f27\u4e00\u6775\u5411\u7384\u771f\u5934\u9885\u649e\u53bb\uff0c\u7384\u771f\u4e0d\u987e \u751f\u6b7b\uff0c\u51cc\u7a7a\u8dc3\u4e0b\uff0c\u5ffd\u89c9\u8170\u9645\u88ab\u4eba\u7528\u529b\u4e00\u6258\uff0c\u53c8\u7ed9\u5e26\u4e0a\u5cf0\u9876\uff01 \u3000\u3000\u6851\u5f27\u4e00\u6837\u5c06\u7384\u771f\u8feb\u4e0b\u9a86\u9a7c\u5cf0\uff0c\u6b63\u81ea\u5f97\u610f\uff0c\u5ffd\u89c1\u4e00\u4e2a\u9ec4\u886b\u5c11\u5e74\u5e26\u7740\u7384\u771f\u518d\u8dc3\u4e0a\u6765\uff0c\u602a\u5578\u559d \u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u4e5f\u6765\u9001\u6b7b\uff01\u201d\u547c\u7684\u4e00\u58f0\uff0c\u8fd0\u5927\u529b\u91d1\u94a2\u624b\u6cd5\uff0c\u53c8\u662f\u4e00\u6775\u626b\u53bb\u3002\u90a3\u9ec4\u886b\u5c11\u5e74\u820c\u7efd \u6625\u96f7\uff0c\u731b\u7136\u4e00\u58f0\u5927\u559d\uff0c\u559d\u9053\uff1a\u201c\u4f60\u6562\u6b3a\u6211\u5e08\u53d4\u3002\u201d\u53cc\u624b\u63e1\u62f3\uff0c\u811a\u5c16\u70b9\u5730\uff0c\u75be\u5982\u98de\u7bad\uff0c\u8fce\u524d\u4e0a \u53bb\uff0c\u770b\u770b\u5c31\u8981\u7ed9\u6775\u649e\u4e0a\uff0c\u9ec4\u886b\u5c11\u5e74\u731b\u7136\u53f3\u624b\u4e00\u6296\uff0c\u4e00\u9053\u767d\u5149\uff0c\u7535\u5c04\u800c\u51fa\uff0c\u6851\u5f27\u9661\u7136\u4e00\u9707\uff0c\u91d1 \u94a2\u6775\u7adf\u7136\u65ad\u4e86\u4e00\u622a\uff01\u8fd9\u9ec4\u886b\u5c11\u5e74\u6b63\u662f\u6842\u4ef2\u660e\uff0c\u4ed6\u7684\u817e\u86df\u5b9d\u5251\u81f3\u67d4\u81f3\u521a\uff0c\u6851\u5f27\u51b7\u4e0d\u53ca\u9632\uff0c\u5403\u4e86 \u5927\u4e8f\uff0c\u6c14\u5f97\u5c06\u534a\u622a\u91d1\u94a2\u6775\u4e22\u5728\u5730\u4e0a\uff0c\u7528\u5927\u529b\u91d1\u94a2\u624b\uff0c\u7a7a\u624b\u786c\u62a2\u6842\u4ef2\u660e\u7684\u5b9d\u5251\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u5f62\u52bf\u500f\u53d8\uff0c\u6842\u4ef2\u660e\u5b9d\u5251\u5728\u624b\uff0c\u602a\u62db\u6d6a\u6d8c\uff0c\u53d8\u5316\u65e0\u65b9\uff0c\u6851\u5f27\u5408\u4e86\u51e0\u4e2a\u515a\u7fbd\u4e4b\u5200\uff0c\u624d \u582a\u582a\u62b5\u654c\u5f97\u4f4f\u3002\u7384\u771f\u62fe\u8d77\u957f\u5251\uff0c\u518d\u52a0\u5165\u6218\u56e2\uff0c\u548c\u4e09\u5996\u626b\u5f97\u5341\u5206\u6fc0\u70c8\u3002 \u3000\u3000\u4f46\u9976\u662f\u5982\u6b64\uff0c\u8fd8\u53ea\u7b97\u521a\u521a\u62c9\u5e73\uff0c\u4e09\u5996\u515a\u7fbd\u8f83\u591a\uff0c\u53c8\u5404\u6709\u72ec\u95e8\u6b66\u529f\uff0c\u82e5\u4ee5\u5355\u6253\u72ec\u6597\u6765\u8bba\uff0c \u98de\u7ea2\u5dfe\u5c1a\u80fd\u7a0d\u5360\u4e0a\u98ce\uff0c\u7384\u771f\u5219\u4ec5\u80fd\u81ea\u4fdd\uff0c\u7384\u901a\u7384\u89c9\u548c\u5927\u5f1f\u5b50\u4e0e\u4e09\u5996\u515a\u6df7\u6218\uff0c\u5219\u6709\u8fdb\u6709\u9000\uff0c\u7d27 \u5bc6\u4e92\u7f20\u3002 \u3000\u3000\u6b63\u5267\u6597\u95f4\uff0c\u5c71\u8170\u51fa\u73b0\u4e8c\u6761\u4eba\u5f71\uff0c\u6377\u4f3c\u7075\u733f\uff0c\u6500\u767b\u76f4\u4e0a\uff0c\u4e3a\u9996\u7684\u662f\u4e2a\u5c11\u5973\uff0c\u626c\u58f0\u53eb\u9053\uff1a \u201c\u5192\u59d0\u59d0\uff0c\u522b\u614c\uff0c\u6211\u4eec\u6765\u4e86\u3002\u201d\u8fd9\u5c11\u5973\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u6613\u5170\u73e0\uff0c\u8ddf\u5728\u5979\u540e\u9762\u7684\u662f\u5085\u9752\u4e3b\u548c\u5f20 \u534e\u662d\u3002\u539f\u6765\u5979\u672c\u662f\u8981\u56de\u5929\u5c71\u7684\uff0c\u5728\u627e\u5230\u5f20\u534e\u662d\u4e4b\u540e\uff0c\u4fbf\u5373\u542f\u7a0b\u3002\u5085\u9752\u4e3b\u4e0e\u5192\u6d63\u83b2\u60c5\u5982\u7236\u5973\uff0c \u4ed6\u8bb0\u6302\u5192\u6d63\u83b2\uff0c\u5927\u4e8b\u65e2\u4e86\uff0c\u9042\u4e5f\u4e0e\u4ed6\u4eec\u4e00\u9053\uff0c\u540c\u4e0a\u5929\u5c71\u3002 \u3000\u3000\u4e8c\u5996\u89c1\u5bf9\u65b9\u5f3a\u63f4\u6765\u5230\uff0c\u53d1\u52a8\u731b\u653b\uff0c\u610f\u6b32\u62a2\u5148\u6293\u4f4f\u5bf9\u65b9\u4e00\u4e24\u4e2a\u4eba\u4f5c\u4e3a\u4eba\u8d28\u3002\u5192\u6d63\u83b2\u6325\u52a8\u5929 \u8679\u5b9d\u5251\uff0c\u4e0e\u6842\u4ef2\u660e\u5e76\u80a9\u4f5c\u6218\uff0c\u6851\u5f27\u770b\u51fa\u5979\u529f\u529b\u8f83\u4f4e\uff0c\u84b2\u638c\u822c\u7684\u5927\u624b\u731b\u6293\u4e0b\u53bb\uff0c\u6842\u4ef2\u660e\u659c\u5288\u4e0a \u5251\uff0c\u6ca1\u6709\u5288\u7740\uff0c\u5ffd\u542c\u5f97\u5192\u6d63\u83b2\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u5b9d\u5251\u7adf\u7ed9\u6293\u53bb\u3002\u6842\u4ef2\u660e\u5927\u60ca\u5931\u8272\uff0c\u8eab\u5f62\u4e00\u743c\uff0c \u8fc5\u5982\u98d9\u98ce\uff0c\u817e\u86df\u5251\u5237\u5730\u523a\u5411\u654c\u4eba\u540e\u5fc3\uff0c\u5c1a\u672a\u523a\u5230\uff0c\u5ffd\u542c\u5f97\u6851\u5f27\u5389\u5578\u4e00\u58f0\uff0c\u500f\u5730\u5012\u5730\uff0c\u5192\u6d63\u83b2 \u5927\u559c\u53eb\u9053\uff1a\u201c\u51cc\u5927\u4fa0\u6765\u4e86\uff01\u201d\u6842\u4ef2\u660e\u626d\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u51cc\u672a\u98ce\u82f1\u98ce\u51db\u51db\uff0c\u73b0\u8eab\u5cf0\u9876\uff0c\u4ed6\u867d\u7136\u6765 \u8fdf\u4e00\u6b65\uff0c\u5374\u53cd\u800c\u62a2\u5728\u6613\u5085\u7b49\u4eba\u7684\u524d\u5934\u3002 \u3000\u3000\u51cc\u672a\u98ce\u88c1\u6307\u6851\u4e7e\u9a82\u9053\uff1a\u201c\u5929\u5c71\u4e4b\u4e0a\uff0c\u5c82\u5bb9\u4f60\u7b49\u5996\u5b7d\u6492\u91ce\uff1f\u5feb\u5feb\u7ed9\u6211\u6eda\u4e0b\u5c71\u53bb\uff01\u201d\u6851\u4e7e\u559d \u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u80c6\u6562\u53d1\u6b64\u5927\u8a00\uff01\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6666\u660e\u7985\u5e08\u5728\u65e5\uff0c\u5916\u65b9\u5251\u5ba2\uff0c\u65e0\u4eba\u6562\u5e26\u5251\u4e0a \u5c71\uff0c\u4f60\u4eec\u77e5\u4e0d\u77e5\u9053\uff1f\u201d\u6851\u4e7e\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u6666\u660e\u7985\u5e08\u7684\u5f1f\u5b50\u4e86\uff1f\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4f60\u4eec\u653e\u4e0b\u5175 \u5668\uff0c\u6eda\u4e0b\u5c71\u53bb\uff0c\u6211\u53ef\u4ee5\u9976\u4f60\u4e0d\u6b7b\uff01\u201d\u6851\u4e7e\u6012\u9053\uff1a\u201c\u4f60\u6709\u4f55\u5fb7\u4f55\u80fd\uff0c\u5c45\u7136\u6562\u4e0e\u6666\u660e\u76f8\u6bd4\uff1f\u201d\u51cc \u672a\u98ce\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u670d\uff0c\u5c3d\u7ba1\u6765\u6597\uff01\u201d\u4e09\u5996\u6851\u4ec1\u62b1\u8d77\u4e8c\u5996\u6851\u5f27\u7684\u5c38\u8eab\uff0c\u5927\u54ed\u53eb\u9053\uff1a\u201c\u5927 \u54e5\uff0c\u4e8c\u54e5\u5df2\u7ed9\u8fd9\u53ae\u7528\u5929\u5c71\u795e\u8292\u5c04\u6b7b\u4e86\uff01\u201d\u6851\u4e7e\u4ef0\u5929\u6012\u5578\uff0c\u559d\u9053\uff1a\u201c\u54b1\u4eec\u4e0e\u4e8c\u5f1f\u62a5\u4ec7\uff01\u201d\u6bd2\u5251 \u626c\u7a7a\u4e00\u95ea\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u76f4\u5377\u8fc7\u6765\uff01\u6851\u4ec1\u653e\u4e0b\u6851\u5f27\u5c38\u8eab\uff0c\u53cc\u62f3\u4e00\u62e2\uff0c\u5411\u4e0b\u4e00\u6c89\uff0c\u4e24\u638c\u5de6\u53f3\u4f38 \u5f00\uff0c\u8d70\u4fa7\u7ffc\uff0c\u62a2\u8fb9\u950b\uff0c\u4e5f\u6765\u52a9\u6218\u3002\u51cc\u672a\u98ce\u559d\u9053\uff1a\u201c\u597d\uff0c\u6211\u6559\u4f60\u4e24\u4eba\u6b7b\u800c\u65e0\u6028\uff01\u4ef2\u660e\u3001\u5170\u73e0\uff0c \u4f60\u628a\u90a3\u4e9b\u4eba\u7684\u5175\u5668\u7684\u5168\u7f34\u4e0b\u6765\uff0c\u628a\u4ed6\u4eec\u9010\u4e0b\u5c71\u53bb\uff01\u201d \u3000\u3000\u6851\u4ec1\u6043\u7740\u638c\u98ce\u5389\u5bb3\uff0c\u540e\u53d1\u5148\u81f3\uff0c\u76f4\u62a2\u8fc7\u6765\uff0c\u5de6\u638c\u659c\u5288\u80f8\u524d\uff0c\u53f3\u638c\u4e94\u6307\u5982\u94a9\uff0c\u76f4\u6293\u80c1\u4e0b\uff0c \u8fd9\u4e00\u62db\u540d\u53eb\u201c\u4e4c\u9f99\u63a2\u722a\u201d\uff0c\u638c\u529b\u5f88\u91cd\uff0c\u4e00\u6253\u51fa\u6765\uff0c\u8ddd\u79bb\u638c\u5fc3\u4e03\u5c3a\u4e4b\u5185\uff0c\u575a\u5982\u6728\u77f3\uff0c\u4e5f\u8981\u6d1e \u7a7f\uff0c\u82e5\u662f\u4eba\u8eab\uff0c\u4e0d\u7528\u6253\u5b9e\uff0c\u53ea\u5403\u638c\u98ce\u626b\u7740\uff0c\u4e5f\u8981\u7b4b\u65ad\u9aa8\u6298\uff0c\u7aef\u7684\u975e\u540c\u5c0f\u53ef\u3002\u51cc\u672a\u98ce\u4e45\u7ecf\u5927 \u654c\uff0c\u5982\u4f55\u4e0d\u6653\uff1f\u8eab\u5f62\u4e00\u4f4e\uff0c\u201c\u731b\u864e\u4f0f\u6869\u201d\uff0c\u53ea\u4e00\u95ea\u8eab\uff0c\u4fbf\u62a2\u5230\u6851\u4ec1\u80cc\u540e\uff0c\u5e73\u4f38\u53f3\u638c\uff0c\u53cd\u5411\u6851 \u4ec1\u4e0b\u4e09\u8def\u626b\u53bb\uff0c\u8fd9\u4e00\u638c\u6697\u85cf\u94c1\u7435\u7436\u638c\u529b\uff0c\u5c31\u662f\u91d1\u949f\u7f69\u94c1\u5e03\u886b\uff0c\u4e00\u51fb\u4e4b\u4e0b\uff0c\u4e5f\u8981\u62c6\u6563\uff01\u6851\u4ec1\u4e00 \u63a5\u638c\u98ce\uff0c\u77e5\u9053\u5389\u5bb3\uff0c\u5438\u80f8\u51f9\u8179\uff0c\u5411\u540e\u4e00\u9053\uff0c\u6851\u4e7e\u7684\u8bdb\u9b44\u5251\u4ece\u4e2d\u8def\u76f4\u523a\u524d\u80f8\uff0c\u51cc\u672a\u98ce\u201c\u5413\u201d\u7684 \u4e00\u58f0\uff0c\u53cc\u6307\u5fae\u642d\u5251\u8eab\uff0c\u5de6\u638c\u5ffd\u5316\u638c\u4e3a\u62f3\uff0c\u547c\u7684\u4e00\u62f3\u6363\u53bb\uff01\u6851\u4e7e\u4e5f\u6781\u8001\u7ec3\u77eb\u6377\uff0c\u6025\u6025\u201c\u9738\u738b\u5378 \u7532\u201d\uff0c\u5f80\u4e0b\u6251\u8eab\uff0c\u62f3\u98ce\u63a0\u9876\u800c\u8fc7\uff0c\u6851\u4ec1\u53cd\u624b\u4e00\u638c\uff0c\u518d\u5ea6\u6253\u6765\uff0c\u51cc\u672a\u98ce\u6325\u81c2\u4e00\u683c\uff0c\u8f70\u8f70\u58f0\u54cd\uff0c \u638c\u98ce\u76f8\u649e\uff0c\u4e8c\u81c2\u4ea4\u51fb\uff0c\u5982\u51fb\u8d25\u9769\uff0c\u6851\u4ec1\u864e\u543c\u4e00\u58f0\uff0c\u5012\u9000\u51fa\u53bb\uff01\u51cc\u672a\u98ce\u6697\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5996\u5b7d\uff0c \u5c45\u7136\u8fd8\u6709\u4e24\u4e0b\uff01\u201d\u5929\u5c71\u638c\u8fed\uff0c\u547c\u547c\u5c55\u5f00\uff0c\u98ce\u96e8\u4e0d\u900f\uff01 \u3000\u3000\u51cc\u672a\u98ce\u5bf9\u6666\u660e\u7985\u5e08\u7684\u62f3\u7ecf\u5251\u8bc0\uff0c\u5df2\u5168\u90e8\u878d\u4f1a\u8d2f\u901a\uff0c\u66f4\u52a0\u4ee5\u4e0b\u5c71\u4ee5\u6765\uff0c\u4f1a\u5c3d\u5404\u5bb6\u5404\u6d3e\uff0c\u6b66 \u529f\u5df2\u5230\u7089\u706b\u7eaf\u9752\uff0c\u51fa\u795e\u5165\u5316\u4e4b\u5883\uff01\u4e09\u4eba\u6597\u4e86\u4e94\u4e03\u5341\u62db\uff0c\u4e24\u5996\u53ea\u6709\u62db\u67b6\u8fd9\u529f\uff0c\u6beb\u65e0\u8fd8\u51fb\u4e4b\u529b\u3002 \u6851\u4ec1\u53c8\u614c\u53c8\u6025\uff0c\u60f3\u7528\u9669\u62db\uff0c\u8d25\u4e2d\u6c42\u80dc\uff0c\u5de6\u624b\u638c\u5fc3\u5411\u81c2\u4e0a\u4e00\u642d\uff0c\u5f80\u51cc\u672a\u98ce\u5de6\u4e73\u7f69\u95e8\u7a74\u731b\u649e\uff0c\u8fd9 \u4e00\u624b\u540d\u53eb\u201c\u91d1\u868a\u526a\u5c3e\u201d\uff0c\u53cc\u638c\u56de\u73af\u4ea4\u9519\uff0c\u5e73\u63a8\u51fa\u53bb\uff0c\u53ea\u8981\u51cc\u672a\u98ce\u6a2a\u638c\u4e00\u5c01\uff0c\u4ed6\u4fbf\u53ef\u4ee5\u4e00\u8fde\u53d8 \u5316\u201c\u4e4c\u9f99\u7a7f\u5854\u201d\u94f6\u9f99\u6296\u7532\u201d\u201c\u91d1\u9f99\u5f52\u6d77\u201d\u4e09\u4e2a\u62db\u5f0f\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u51cc\u672a\u98ce\u54ea\u4f1a\u4e2d\u8ba1\uff0c\u53f3\u80a9\u5411\u540e \u4e00\u7529\uff0c\u8eab\u5f62\u4e8c\u95ea\uff0c\u53cc\u81c2\u4e00\u5206\uff0c\u8ff3\u7528\u767e\u6b65\u795e\u62f3\u529b\uff0c\u76f4\u5411\u6851\u4ec1\u53f3\u80ef\u6253\u53bb\uff0c\u7830\u7830\u4e24\u58f0\uff0c\u6253\u4e2a\u6b63\u7740\uff0c \u6851\u4ec1\u7684\u8eab\u5b50\uff0c\u7adf\u662f\u629b\u7403\u4e00\u822c\uff0c\u98de\u8d77\u4e09\u56db\u4e08\u9ad8\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u58f0\u60e8\u53eb\uff0c\u8dcc\u4e0b\u9a86\u9a7c\u5cf0\uff01 \u3000\u3000\u6851\u4e7e\u6bd2\u5251\u4e5f\u6b63\u53cd\u524a\u8fc7\u6765\uff0c\u51cc\u672a\u98ce\u53cc\u81c2\u4e00\u6296\uff0c\u786c\u5c06\u8eab\u5f62\u62d4\u8d77\uff0c\u5f80\u4e0b\u4e00\u843d\uff0c\u6293\u7740\u6851\u4e7e\u80cc\u5fc3\uff0c \u559d\u9053\uff1a\u201c\u4f60\u4e5f\u7ed9\u6211\u6eda\u4e0b\u5c71\u53bb\u3002\u201d\u5f80\u5916\u4e00\u7529\uff0c\u6851\u4e7e\u4e5f\u7ed9\u629b\u7403\u4e00\u822c\u5730\u629b\u4e0b\u9a86\u9a7c\u5cf0\uff01 \u3000\u3000\u53e6\u4e00\u8fb9\uff0c\u6842\u4ef2\u660e\u548c\u6613\u5170\u73e0\u4e24\u628a\u5b9d\u5251\uff0c\u7eb5\u6a2a\u9a70\u9a8b\uff0c\u53ea\u89c1\u5bd2\u971c\u531d\u5730\uff0c\u7d2b\u7535\u98de\u7a7a\uff0c\u4e24\u56e2\u7535\u5149\uff0c \u6eda\u6765\u6eda\u53bb\uff0c\u5b9b\u5982\u6c34\u94f6\u6cfb\u5730\uff0c\u82b1\u96e8\u7f24\u7eb7\uff0c\u5206\u4e0d\u6e05\u5251\u5f71\u4eba\u5f71\uff0c\u5230\u51cc\u672a\u98ce\u6536\u62fe\u4e86\u6851\u4e7e\u6851\u4ec1\u4e8c\u5996\u4e4b \u540e\uff0c\u6842\u4ef2\u660e\u548c\u6613\u5170\u73e0\u4e5f\u500f\u7136\u6536\u5251\uff0c\u5730\u4e0a\u6ee1\u662f\u88ab\u6298\u65ad\u7684\u5175\u5203\uff0c\u4e09\u5996\u5e26\u6765\u7684\u515a\u7fbd\uff0c\u624b\u4e0a\u6ca1\u6709\u4e00\u628a \u5b8c\u6574\u7684\u5200\u5251\uff0c\u60ca\u9b42\u672a\u8d70\uff0c\u51cc\u672a\u98ce\u559d\u9053\uff1a\u201c\u9996\u6076\u5df2\u8bdb\uff0c\u80c1\u4ece\u4e0d\u7a76\uff0c\u4f60\u4eec\u8fd8\u4e0d\u6eda\u4e0b\u5c71\u53bb\uff01\u201d\u4e09\u5996 \u515a\u7fbd\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u8fde\u722c\u5e26\u6eda\u3001\u90fd\u9003\u4e0b\u9a86\u9a86\u5cf0\u3002 \u3000\u3000\u7384\u771f\u89c1\u6842\u4ef2\u660e\u5982\u6b64\u58f0\u52bf\uff0c\u53f9\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1d\u6211\u4e5f\u4e0d\u6562\u8ba4\u4f60\u4f5c\u5e08\u4f84\u4e86\uff0c\u4f60\u5f97\u4e86\u8fbe\u6469\u5251\u6cd5\uff0c \u662f\u4f60\u7684\u7f18\u4efd\uff01\u6211\u8fd9\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u4e5f\u4e0d\u505a\u4e86\uff0c\u8ba9\u7ed9\u4f60\u5427\uff01\u201d\u6842\u4ef2\u660e\u56b7\u9053\uff1a\u201c\u5582\uff0c\u5e08\u53d4\uff0c\u4f60\u6162\u70b9 \u8d70\uff0c\u6211\u54ea\u91cc\u61c2\u5f97\u505a\u4ec0\u4e48\u638c\u95e8\uff1f\u201d\u7384\u771f\u5934\u4e5f\u4e0d\u56de\uff0c\u548c\u4f55\u7eff\u5987\u592b\u5987\u8d70\u4e0b\u9a86\u9a7c\u5cf0\uff0c\u56de\u58f0\u5bf9\u4e03\u5927\u5f1f\u5b50 \u9053\uff1a\u201c\u4f60\u4eec\u7559\u5728\u8fd9\u91cc\u5b89\u846c\u5353\u7956\u5e08\u9ab8\u9aa8\uff0c\u8981\u5b66\u8fbe\u6469\u5251\u6cd5\uff0c\u53ef\u8ddf\u4f60\u4eec\u7684\u638c\u95e8\u5e08\u5144\u53bb\u5b66\uff01\u201d\u6842\u4ef2\u660e \u8981\u8ffd\uff0c\u5374\u7ed9\u5085\u9752\u4e3b\u62c9\u4f4f\u3002 \u3000\u3000\u6613\u5170\u73e0\u60ca\u559c\u4ea4\u96c6\uff0c\u8bf4\u9053\uff1a\u201c\u51cc\u53d4\u53d4\uff0c\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u89c1\u7740\u4f60\u3002\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u5927\u59d0\u2014\u2014\u201d \u3000\u3000\u51cc\u672a\u98ce\u8bf4\u9053\uff1a\u201c\u6211\u662f\u7279\u5730\u56de\u6765\u4e86\u5374\u4e00\u6869\u5fc3\u4e8b\u7684\u3002\u201d \u3000\u3000\u6613\u5170\u73e0\u9053\uff1a\u201c\u5fc3\u4e8b\uff1f\u90a3\u4f60\u4e3a\u4f55\u629b\u4e0b\u4e86\u5218\u5927\u59d0\u4e0d\u8f9e\u800c\u884c\uff0c\u4f60\u4ee5\u4e3a\u5979\u4f1a\u5728\u8fd9\u91cc\u7b49\u4f60\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211\u77e5\u9053\u5979\u4e0d\u4f1a\u3002\u6211\u56de\u6765\u662f\u4e3a\u4e86\u6211\u7684\u5e08\u5144\uff0c\u4f60\u7684\u7239\u7239\u7acb\u4e2a\u8863\u51a0\u51a2\uff0c\u5f53\u5e74\u662f\u4ed6 \u5e26\u6211\u4e0a\u5929\u5c71\u7684\u3002\u55ef\uff0c\u4f60\u8bf4\u8d77\u5218\u5927\u59d0\uff0c\u90a3\u5c01\u4fe1\u2014\u2014\u2014\u2014\u201d \u3000\u3000\u6613\u5170\u73e0\u9053\uff1a\u201c\u5bf9\u4e0d\u8d77\uff0c\u6211\u8bbe\u6cd5\u7ed9\u4f60\u4ea4\u5230\u5218\u5927\u59d0\u624b\u4e0a\u3002\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u6613\u5170\u73e0 \u9053\uff1a\u201c\u5979\u548c\u4f60\u4e00\u6837\uff0c\u4e5f\u662f\u5728\u90a3\u5929\u65e9\u4e0a\uff0c\u7559\u4e0b\u4e00\u5c01\u4fe1\u7ed9\u4f60\uff0c\u5c31\u79bb\u5f00\u9a6c\u5bb6\u4e86\u3002\u6211\u6839\u672c\u6ca1\u89c1\u7740\u5979\u3002 \u73b0\u5728\u4e24\u5c01\u4fe1\u90fd\u5728\u6211\u8fd9\u91cc\uff0c\u5f85\u4f1a\u513f\u6211\u627e\u51fa\u6765\u7684\u7ed9\u4f60\u3002\u201d\u51cc\u672a\u98ce\u5583\u5583\u9053\uff1a\u201c\u6211\u65e9\u77e5\u9053\u5979\u4f1a\u8fd9\u6837 \u7684\u3002\u5979\u5199\u4e9b\u4ec0\u4e48\uff0c\u6211\u60f3\u6211\u4e5f\u80fd\u731c\u5230\u51e0\u5206\u3002\u4f60\u522b\u5fd9\u7ed9\u6211\uff0c\u529e\u5b8c\u6b63\u4e8b\u518d\u8bf4\u3002\u201d\u6613\u5170\u73e0\u9053\uff1a\u201c\u6211\u771f \u731c\u4e0d\u900f\u4f60\u4eec\u7684\u5fc3\u601d\uff0c\u4f60\u4eec\u5206\u660e\u662f\u4e00\u5bf9\u6709\u60c5\u4eba\uff0c\u5374\u505a\u51fa\u65e0\u60c5\u7684\u4e8b\u3002\u201d \u3000\u3000\u51cc\u672a\u98ce\u53f9\u9053\uff1a\u201c\u5170\u73e0\uff0c\u4f60\u4e0d\u61c2\u7684\u3002\u9053\u662f\u65e0\u60c5\u5374\u6709\u60c5\uff0c\u60c5\u5230\u6df1\u65f6\u60c5\u8f6c\u8584\u2026\u2026\u201d \u3000\u3000\u6613\u5170\u73e0\u9053\uff1a\u201c\u6211\u662f\u4e0d\u61c2\uff0c\u6211\u4e5f\u4e0d\u60f3\u61c2\u4f60\u5ff5\u5728\u4ec0\u4e48\u8bd7\u8bcd\u3002\u6211\u53ea\u77e5\u9053\u4f60\u90a3\u5929\u66fe\u9080\u5218\u5927\u59d0\u53bb\u5929 \u5c71\u8d4f\u96ea\uff0c\u5982\u4eca\u5374\u53ea\u662f\u4f60\u4e00\u4e2a\u5728\u8fd9\u91cc\u81ea\u6028\u81ea\u55df\uff0c\u5218\u5927\u59d0\u4e0d\u77e5\u54ea\u91cc\u53bb\u4e86\u3002\u201d\u51cc\u672a\u98ce\u5fc3\u4e2d\u82e6\u7b11\uff1a \u201c\u4f60\u8fd8\u662f\u4e0d\u61c2\uff01\u6211\u4eec\u4e5f\u5e76\u4e0d\u662f\u53ea\u6709\u81ea\u6028\u81ea\u55df\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u9053\uff1a\u201c\u6211\u77e5\u9053\u5979\u53bb\u54ea\u513f\uff01\u5979\u662f\u56de\u8f6c\u6c5f\u5357\uff0c\u91cd\u6574\u9c81\u738b\u7684\u65e7\u90e8\u3002\u201d \u3000\u3000\u6842\u4ef2\u660e\u9053\uff1a\u201c\u5085\u4f2f\u4f2f\uff0c\u6211\u4e5f\u6ca1\u60f3\u5230\u4f60\u4f1a\u6765\u6b64\u3002\u201d \u3000\u3000\u5085\u9752\u4e3b\u7b11\u9053\uff1a\u201c\u6d63\u83b2\u662f\u8ddf\u6211\u957f\u5927\u7684\uff0c\u4f60\u4e5f\u6ca1\u4e86\u4eb2\u4eba\uff0c\u6211\u4e0d\u6765\uff0c\u8c01\u7ed9\u4f60\u4eec\u4e3b\u5a5a\uff1f\u201d\u6842\u4ef2\u660e \u50bb\u516e\u516e\u5730\u7b11\uff0c\u5192\u6d63\u83b2\u5219\u662f\u8138\u90fd\u7ea2\u4e86\u3002 \u3000\u3000\u4fa0\u9aa8\u67d4\u60c5\u57cb\u701a\u6d77\uff0c\u82f1\u96c4\u513f\u5973\u9690\u5929\u5c71\u3002\u4ed6\u4eec\u5728\u5929\u5c71\u5b89\u987f\u4e0b\u6765\uff0c\u6842\u3001\u5192\u4e8c\u4eba\u5148\u884c\u6210\u5a5a\uff0c\u6613\u5170 \u73e0\u56e0\u4e3a\u8981\u66ff\u7236\u4eb2\u5b88\u5b5d\u4e00\u5e74\uff0c\u4e0e\u5f20\u534e\u662d\u7684\u5a5a\u4e8b\u6682\u4e14\u7f13\u529e\u3002 \u3000\u3000\u5085\u9752\u4e3b\u7ed9\u4ed6\u4eec\u5907\u529e\u5a5a\u4e8b\u5f88\u662f\u5468\u5230\uff0c\u8fde\u4e00\u5bf9\u9f99\u51e4\u70db\u90fd\u7ed9\u4ed6\u4eec\u9884\u5148\u4e70\u597d\u4e86\u3002 \u3000\u3000\u6d1e\u623f\u7ea2\u70db\u559c\u6d0b\u6d0b\u3002\u6842\u4ef2\u660e\u5728\u70db\u5149\u4e0b\u770b\u65b0\u5a18\uff0c\u53ea\u89c9\u5192\u6d63\u83b2\u6bd4\u5e73\u65f6\u66f4\u52a0\u5a07\u7f8e\u3002\u4ed6\u4e0d\u61c2\u8bf4\u8c03\u60c5 \u7684\u8bdd\u513f\uff0c\u7785\u770b\u65b0\u5a18\uff0c\u53ea\u662f\u50bb\u7b11\u3002\u5192\u6d63\u83b2\u4e5f\u63a9\u4e0d\u4f4f\u5185\u5fc3\u7684\u559c\u60a6\uff0c\u867d\u6ca1\u7b11\u51fa\u58f0\uff0c\u8138\u4e0a\u7684\u5bb9\u4e5f\u50cf\u82b1 \u6735\u822c\u7efd\u5f00\u4e86\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6842\u4ef2\u660e\u5ffd\u89c1\u5979\u7684\u7b11\u5bb9\u4f3c\u4e4e\u6b63\u5728\u6536\u655b\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u6d63\u83b2\uff0c\u4f60 \u4e0d\u9ad8\u5174\u4e48\uff1f\u201d \u3000\u3000\u5192\u6d63\u83b2\u9053\uff1a\u201c\u51c6\u8bf4\u6211\u4e0d\u9ad8\u5174\uff1f\u201d \u3000\u3000\u6842\u4ef2\u660e\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u5728\u60f3\u7740\u4ec0\u4e48\u5fc3\u4e8b\uff1f\u201d \u3000\u3000\u5192\u6d63\u83b2\u55e4\u55e4\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5728\u60f3\u4f60\u8fd9\u50bb\u5c0f\u5b50\uff0c\u600e\u4e48\u5c31\u53ea\u77e5\u9053\u50bb\u7b11\uff1f\u201d \u3000\u3000\u6842\u4ef2\u660e\u6b64\u65f6\u5012\u4e0d\u7cca\u6d82\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u50bb\u4eba\u624d\u6709\u50bb\u798f\u5462\uff0c\u8981\u4e0d\u7136\u600e\u8ba8\u5f97\u4f60\u8fd9\u6837\u5929\u4ed9\u4f3c\u7684\u4eba \u513f\u3002\u201d\u4e00\u9762\u7b11\u4e00\u9762\u628a\u5192\u6d63\u83b2\u62e5\u5165\u6000\u4e2d\u3002 \u3000\u3000\u5192\u6d63\u83b2\u521a\u624d\u7684\u786e\u662f\u522b\u6709\u6240\u601d\uff0c\u4e0d\u8fc7\uff0c\u82e5\u8bf4\u201c\u5fc3\u4e8b\u201d\u5219\u5acc\u201c\u4e25\u91cd\u201d\u4e86\u4e9b\uff0c\u5979\u53ea\u662f\u60f3\u8d77\u4e86\u4e00 \u4e2a\u4eba\uff0c\u60f3\u8d77\u4e86\u8fdc\u5728\u4eac\u534e\u7684\u7eb3\u5170\u5bb9\u82e5\u3002\u60f3\u8d77\u4e86\u90a3\u5929\u665a\u4e0a\uff0c\u5728\u8fb9\u57ce\u7684\u5e10\u5e55\u91cc\uff0c\u5979\u548c\u7eb3\u5170\u5bb9\u82e5\u4e5f\u662f \u5bf9\u7740\u70db\u5149\uff0c\u54c1\u8317\u6e05\u8c08\uff0c\u85c9\u65b0\u8bcd\u800c\u8868\u5fc3\u610f\u3002 \u3000\u3000\u201c\u83ab\u7eed\u4eac\u534e\u65e7\u68a6\uff0c\u8bf7\u770b\u9ec4\u6c99\u767d\u8349\uff0c\u78a7\u8840\u5c1a\u9634\u51dd\u3002\u60ca\u9e3f\u63a0\u6c34\u8fc7\uff0c\u6ce2\u8361\u4e86\u65e0\u58f0\u3002\u66f4\u4f11\u95ee\u7edb\u73e0 \u79fb\u540e\uff0c\u6cea\u96be\u6d47\uff0c\u4f55\u5904\u6258\u5b64\u830e\uff0c\u5e94\u73cd\u91cd\uff0c\u743c\u697c\u6765\u53bb\uff0c\u7a33\u6cdb\u7a7a\u6e9f\u3002\u201d\u5979\u5fc3\u4e2d\u9ed8\u7136\u5ff5\u90a3\u665a\u5199\u7684\u8fd9\u51e0 \u53e5\u8bcd\u60f3\u9053\uff1a\u201c\u4eba\u751f\u54ea\u6709\u5341\u5168\u5341\u7f8e\uff0c\u4ef2\u660e\u7eaf\u771f\u6206\u76f4\uff0c\u5f97\u5a7f\u5982\u6b64\uff0c\u592b\u590d\u4f55\u6c42\uff01\u5982\u4eca\u6211\uff0c\u5df2\u662f\u5b64\u513f \u6709\u6258\uff0c\u4f46\u613f\u7eb3\u5170\u516c\u5b50\u4e5f\u80fd\u591f\u65e9\u65e5\u91cd\u7eed\u9e33\u80f6\u3002\u201d\u5979\u9669\u4e0a\u7684\u7b11\u5bb9\u91cd\u65b0\u7efd\u5f00\uff0c\u4e0e\u6842\u4ef2\u660e\u540c\u5165\u7f57\u5e10\u3002 \u3000\u3000\u4e07\u91cc\u4e4b\u5916\uff0c\u4eac\u57ce\u76f8\u5e9c\u7684\u767d\u7389\u697c\u4e2d\uff0c\u7eb3\u5170\u5bb9\u82e5\u6b63\u5728\u5bf9\u6708\u6000\u4eba\u3002\u4ed6\u5f53\u7136\u4e0d\u4f1a\u77e5\u9053\u8fd9\u665a\u6b63\u662f\u5192 \u6d63\u83b2\u7684\u6d1e\u623f\u82b1\u70db\u591c\uff0c\u66f4\u4e0d\u4f1a\u77e5\u9053\u5192\u6d63\u83b2\u4e5f\u66fe\u7ecf\u60f3\u5230\u4e86\u4ed6\u3002 \u3000\u3000\u4ed6\u662f\u56e0\u4e3a\u65e5\u95f4\u542c\u5230\u4e86\u5927\u519b\u5df2\u7ecf\u4ece\u56de\u7586\u64a4\u9000\u7684\u6d88\u606f\u800c\u4e3a\u5192\u70f7\u83b2\u795d\u798f\u7684\u3002\u201c\u5316\u5e72\u6208\u800c\u4e3a\u7389 \u5e1b\uff0c\u867d\u7136\u8a00\u4e4b\u5c1a\u65e9\uff0c\u4f46\u6700\u5c11\u5979\u5728\u56de\u7586\u662f\u53ef\u4ee5\u6709\u4e00\u6bb5\u5e73\u5b89\u65e5\u5b50\u597d\u8fc7\uff0c\u6211\u4e5f\u53ef\u4ee5\u653e\u4e0b\u4e00\u5757\u77f3\u5934 \u4e86\u3002\u201d\u5509\uff0c\u4f46\u53c8\u4e0d\u77e5\u8981\u5f85\u5230\u4f55\u65f6\uff0c\u65b9\u59cb\u80fd\u591f\uff0c\u6ca7\u6851\u6362\u4e86\uff0c\u5e76\u8f94\u6570\u5bd2\u661f\uff1f\u201d \u3000\u3000\u6101\u601d\u96be\u9053\uff0c\u4ed6\u4e0d\u77e5\u4e0d\u89c9\u53c8\u5ff5\u8d77\u90a3\u9996\u9898\u4e3a\u201c\u585e\u4e0a\u548f\u96ea\u82b1\u201d\u7684\u201c\u91c7\u6851\u5b50\u201d\u6765\u3002\u8fd9\u9996\u8bcd\u65e2\u662f\u4ed6 \u7684\u81ea\u9648\u62b1\u8d1f\uff0c\u4e5f\u662f\u4e3a\u4e86\u601d\u5ff5\u5192\u6d63\u83b2\u800c\u5199\u7684\u3002\u81ea\u4ece\u4e0e\u5192\u6d63\u83b2\u5206\u624b\u4e4b\u540e\uff0c\u4ed6\u5df2\u4e0d\u77e5\u5ff5\u8fc7\u591a\u5c11\u6b21 \u4e86\u3002 \u3000\u3000\u975e\u5173\u7656\u7231\u8f7b\u6a21\u6837\uff0c \u3000\u3000\u51b7\u5904\u504f\u4f73\uff0c \u3000\u3000\u522b\u6709\u6839\u82bd\uff0c \u3000\u3000\u4e0d\u662f\u4eba\u95f4\u5bcc\u8d35\u82b1\u3002 \u3000\u3000\u8c22\u5a18\u522b\u540e\u8c01\u80fd\u60dc\uff1f \u3000\u3000\u98d8\u6cca\u5929\u6daf\uff0c \u3000\u3000\u5bd2\u6708\u60b2\u7b33\uff0c \u3000\u3000\u4e07\u671b\u897f\u98ce\u701a\u6d77\u6c99\u3002 \u3000\u3000\u6768\u4e91\u9aa2\u7684\u8863\u51a0\u51a2\u5df2\u7ecf\u5efa\u597d\u4e86\uff0c\u51cc\u672a\u98ce\u62dc\u796d\u8fc7\u5e08\u5144\u7684\u8863\u51a0\u51a2\u540e\uff0c\u5c31\u51c6\u5907\u4e0b\u5c71\u4e86\uff0c\u4e0d\u8fc7\uff0c\u6b64 \u9645\u4ed6\u5374\u5e76\u4e0d\u662f\u548c\u6613\u5170\u73e0\u8bdd\u522b\uff0c\u800c\u662f\u6367\u7740\u4e00\u5c01\u4fe1\u51fa\u795e\u3002\u5218\u90c1\u82b3\u5199\u7ed9\u4ed6\u7684\u90a3\u5c01\u4fe1\u662f\u6613\u5170\u73e0\u521a\u521a\u4ea4 \u7ed9\u4ed6\u7684\uff0c\u4ed6\u5199\u7ed9\u5218\u90c1\u82b3\u90a3\u5c01\u4fe1\u5f53\u7136\u4ea6\u5df2\u56de\u5230\u4ed6\u7684\u624b\u4e0a\u3002 \u3000\u3000\u201c\u50bb\u53d4\u53d4\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u4e00\u4f1a\u513f\u53d1\u7b11\uff0c\u4e00\u4f1a\u513f\u53d1\u5446\uff0c\u5218\u5927\u59d0\u7684\u4fe1\u4e0a\u7a76\u7adf\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u9053\uff1a\u201c\u5979\u5199\u7684\u548c\u6211\u4e00\u6837\uff0c\u4e0d\u8fc7\uff0c\u5979\u8bf4\u5f97\u6bd4\u6211\u66f4\u597d\u3002\u4f60\u77a7\u8fd9\u51e0\u53e5\uff0c\u867d\u7136\u662f\u5f15\u7528\u300a\u5e84 \u5b50\u300b\uff0c\u5374\u80dc\u4e8e\u4e07\u8bed\u5343\u8a00\uff01\u201d \u3000\u3000\u6613\u5170\u73e0\u5ff5\u9053\uff1a\u201c\u6db8\u8f99\u4e4b\u9c8b\uff0c\u76f8\u6fe1\u4ee5\u6cab\uff0c\u76f8\u7166\u4ee5\u6e7f\uff0c\u66f7\u82e5\u76f8\u5fd8\u4e8e\u6c5f\u6e56\u3002\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u51cc\u672a\u98ce\u9053\uff1a\u201c\u8fd9\u662f\u8bf4\u6211\u4eec\u8981\u770b\u5230\u66f4\u5e7f\u9614\u7684\u5929\u5730\uff0c\u4e0d\u8981\u50cf\u56f0\u5728\u6db8\u8f99\u7684\u4e24\u6761\u6ce5\u9cc5\u4e00\u6837\uff0c\u53ea\u80fd \u9760\u7740\u5f7c\u6b64\u6240\u5410\u7684\u53e3\u6cab\u6ecb\u6da6\u3002\u5176\u5b9e\u8fd9\u4e5f\u6b63\u5982\u90a3\u5929\u4f60\u548c\u6211\u8bf4\u8fc7\u7684\u90a3\u756a\u8bdd\u7684\u610f\u601d\u4e00\u6837\uff0c\u6709\u8bb8\u591a\u4e8b\u60c5 \u7b49\u5f85\u6211\u4eec\u53bb\u505a\uff0c\u6211\u4eec\u662f\u4e0d\u80fd\u6127\u5bf9\u6b7b\u8005\u7684\u3002\u201d \u3000\u3000\u6613\u5170\u73e0\u9053\uff1a\u201c\u90a3\u5929\u6211\u8bf4\u7684\u8bdd\u53ea\u662f\u60f3\u529d\u4f60\u4eec\u8d70\u51fa\u5fe7\u4f24\u7684\u6df1\u8c37\uff0c\u5e76\u975e\u2014\u2014\u201d\u51cc\u672a\u98ce\u9053\uff1a\u201c\u6211 \u662f\u613f\u4f3c\u6f6e\u800c\u6709\u4fe1\uff0c\u53ea\u53ef\u60dc\u94b1\u5858\u6f6e\u6c34\uff0c\u4e5f\u51b2\u4e0d\u6de1\u97e9\u5927\u54e5\u6240\u6d41\u7684\u9c9c\u8840\u3002\u201d\u56e0\u6b64\u5218\u90c1\u82b3\u548c\u51cc\u672a\u98ce\u7684 \u201c\u5929\u5c71\u8d4f\u96ea\uff0c\u94b1\u642a\u89c2\u6f6e\u201d\u4e4b\u7ea6\uff0c\u4e5f\u53ea\u80fd\u50cf\u5bf9\u5f85\u4ed6\u7684\u611f\u60c5\u4e00\u6837\uff0c\u6700\u5c11\u5728\u76ee\u524d\u6765\u8bf4\uff0c\u662f\u53ea\u80fd\u76f8\u5fd8 \u4e8e\u6c5f\u6e56\u4e86\u3002 \u3000\u3000\u5f80\u540e\u5341\u5e74\uff0c\u6842\u4ef2\u660e\u6210\u4e86\u6b66\u5f53\u6d3e\u5317\u652f\u7684\u5f00\u5c71\u7956\u5e08\uff0c\u6309\u5353\u4e00\u822a\u9057\u547d\uff0c\u5f20\u534e\u662d\u4e5f\u5217\u5165\u6b66\u5f53\u95e8 \u4e0b\uff0c\u5b66\u4e86\u8fbe\u6469\u5251\u6cd5\uff0c\u7b97\u6842\u4ef2\u660e\u7684\u5e08\u5f1f\u3002\u51cc\u672a\u98ce\u4f20\u4e86\u6666\u660e\u7985\u5e08\u7684\u8863\u94b5\uff0c\u5149\u5927\u5929\u5c71\u5251\u6d3e\uff0c\u98de\u7ea2\u5dfe \u505a\u4e86\u56de\u7586\u5404\u65cf\u6302\u540d\u7684\u76df\u4e3b\uff0c\u5728\u5929\u5c71\u7684\u65f6\u5019\u5c11\uff0c\u5728\u8349\u539f\u9a70\u9a8b\u7684\u65f6\u5019\u591a\u3002\u6709\u4ec0\u4e48\u4e8b\u60c5\u53d1\u751f\u65f6\uff0c\u51cc \u672a\u98ce\u5c31\u4f1a\u6765\u5230\u5979\u7684\u519b\u4e2d\uff0c\u5e2e\u5979\u5e94\u4ed8\uff0c\u4e8b\u60c5\u5b8c\u4e86\uff0c\u518d\u56de\u5929\u5c71\u3002\u674e\u601d\u6c38\u540e\u6765\u5728\u5ddd\u897f\u6218\u6b7b\uff0c\u4ed6\u7684\u59bb \u5b50\u6b66\u743c\u7476\u672c\u662f\u767d\u53d1\u9b54\u5973\u7684\u5173\u95e8\u5f1f\u5b50\uff0c\u9042\u4e5f\u5e26\u4e86\u4e00\u53cc\u513f\u5973\uff0c\u56de\u5230\u5929\u5c71\u5b9a\u5c45\u3002\u6b66\u6797\u4e2d\u4eba\uff0c\u4ee5\u524d\u672c \u6709\u201c\u5929\u5c71\u4e94\u5251\u201d\u4e4b\u8bf4\uff0c\u201c\u4e94\u5251\u201d\u662f\u6307\u6768\u4e91\u9aa2\u3001\u98de\u7ea2\u5dfe\u3001\u695a\u662d\u5357\u3001\u8f9b\u9f99\u5b50\u548c\u51cc\u672a\u98ce\u3002\u6768\u3001\u695a\u3001 \u8f9b\u4e09\u4eba\u6b7b\u540e\uff0c\u6c5f\u6e56\u628a\u201c\u4e94\u5251\u201d\u6269\u5927\u800c\u79f0\u201c\u4e03\u5251\u201d\u3002\u5929\u5c71\u4e03\u5251\u9664\u4e86\u539f\u6709\u7684\u98de\u7ea2\u5dfe\u548c\u51cc\u672a\u98ce\u4e4b \u5916\uff0c\u53c8\u518d\u52a0\u4e0a\u4e86\u6842\u4ef2\u660e\u3001\u5192\u6d63\u83b2\u3001\u6613\u5170\u73e0\u3001\u5f20\u534e\u662d\u548c\u6b66\u743c\u7476\u4e94\u4eba\u3002\u5218\u90c1\u82b3\u867d\u7136\u4e0d\u5728\u5929\u5c71\uff0c\u4e5f \u88ab\u79f0\u4e3a\u201c\u5929\u5c71\u4e4b\u53cb\u201d\u3002\u201c\u4e94\u5251\u201d\u4e2d\u6709\u53db\u5f92\u695a\u662d\u5357\u548c\u4ecb\u4e8e\u6b63\u90aa\u4e4b\u95f4\u7684\u8f9b\u9f99\u5b50\uff0c\u201c\u4e03\u5251\u201d\u52a0\u4e0a \u201c\u5929\u5c71\u4e4b\u53cb\u201d\u7684\u5218\u90c1\u82b3\uff0c\u5219\u90fd\u662f\u82f1\u96c4\u513f\u5973\u3002\u201c\u4e03\u5251\u201d\u867d\u4ee5\u5929\u5c71\u4e3a\u5bb6\uff0c\u5374\u5e76\u975e\u4e0d\u95fb\u4e16\u4e8b\uff0c\u800c\u662f \u5e38\u4e0b\u5929\u5c71\u7684\u3002\u4ed6\u4eec\u7684\u4f20\u5947\u6545\u4e8b\uff0c\u7ed9\u7f16\u6210\u4e86\u8bd7\u6b4c\uff0c\u5728\u8349\u539f\u4e0a\u5230\u5904\u6b4c\u5531\u3002\u6b63\u662f\uff1a \u3000\u3000\u5df2\u60ef\u6c5f\u6e56\u4f5c\u6d6a\u6e38\uff0c\u4e14\u5c06\u6069\u6028\u8bf4\u4ece\u5934\uff0c\u5982\u6f6e\u7231\u6068\u603b\u96be\u4f11\u3002 \u3000\u3000\u701a\u6d77\u4e91\u70df\u8ff7\u671b\u773c\uff0c\u5929\u5c71\u5251\u6c14\u8361\u5bd2\u79cb\uff0c\u86fe\u7709\u7edd\u585e\u6709\u4eba\u6101\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u2014\u2014\u8c03\u5bc4\u6d63\u6eaa\u6c99 \u3000\u3000\uff08\u5168\u4e66\u5b8c\uff09"}),showOnHomepage:!0}]}}]);